INSERT INTO "rawai" VALUES(82,1,'82:1','যখন আসমান বিদীর্ণ হবে।');
INSERT INTO "rawai" VALUES(82,2,'82:2','আর যখন নক্ষত্রগুলো ঝরে পড়বে।');
INSERT INTO "rawai" VALUES(82,3,'82:3','আর যখন সমুদ্রগুলোকে একাকার করা হবে।');
INSERT INTO "rawai" VALUES(82,4,'82:4','আর যখন কবরগুলো উন্মোচিত হবে।');
