INSERT INTO "rawai" VALUES(82,6,'82:6','হে মানুষ, কিসে তোমাকে তোমার মহান রব সম্পর্কে ধোঁকা দিয়েছে?');
INSERT INTO "rawai" VALUES(82,7,'82:7','যিনি তোমাকে সৃষ্টি করেছেন অতঃপর তোমাকে সুসম করেছেন, তারপর তোমাকে সুসমঞ্জস করেছেন।');
INSERT INTO "rawai" VALUES(82,8,'82:8','যে আকৃতিতে তিনি চেয়েছেন তোমাকে গঠন করেছেন।');
INSERT INTO "rawai" VALUES(82,9,'82:9','কখনো নয়, তোমরা তো প্রতিদান দিবসকে অস্বীকার করে থাক।');
INSERT INTO "rawai" VALUES(82,10,'82:10','আর নিশ্চয় তোমাদের উপর সংরক্ষকগণ রয়েছে।');
INSERT INTO "rawai" VALUES(82,11,'82:11','সম্মানিত লেখকবৃন্দ।');
INSERT INTO "rawai" VALUES(82,12,'82:12','তারা জানে, যা তোমরা কর।');
INSERT INTO "rawai" VALUES(82,13,'82:13','নিশ্চয় সৎকর্মপরায়ণরা থাকবে সুখ- স্বাচ্ছন্দ্যে।');
