INSERT INTO "rawai" VALUES(83,1,'83:1','ধ্বংস যারা পরিমাপে কম দেয় তাদের জন্য।');
INSERT INTO "rawai" VALUES(83,2,'83:2','যারা লোকদের কাছ থেকে মেপে নেয়ার সময় পূর্ণমাত্রায় গ্রহণ করে।');
INSERT INTO "rawai" VALUES(83,3,'83:3','আর যখন তাদেরকে মেপে দেয় অথবা ওজন করে দেয় তখন কম দেয়।');
INSERT INTO "rawai" VALUES(83,4,'83:4','তারা কি দৃঢ় বিশ্বাস করে না যে, নিশ্চয় তারা পুনরুত্থিত হবে,');
INSERT INTO "rawai" VALUES(83,5,'83:5','এক মহা দিবসে ?');
INSERT INTO "rawai" VALUES(83,6,'83:6','যেদিন মানুষ সৃষ্টিকুলের রবের জন্য দাঁড়াবে।');
INSERT INTO "rawai" VALUES(83,7,'83:7','কখনো নয়, নিশ্চয় পাপাচারীদের ‘আমলনামা সিজ্জীনে।* *সপ্ত যমীনের নীচে অবস্থিত একটি স্থান। পাপীদের আমলনামা সেখানে রাখা হয়।');
INSERT INTO "rawai" VALUES(83,8,'83:8','কিসে তোমাকে জানাবে ‘সিজ্জীন’ কী?');
INSERT INTO "rawai" VALUES(83,9,'83:9','লিখিত কিতাব।');
INSERT INTO "rawai" VALUES(83,10,'83:10','সেদিন ধ্বংস অস্বীকারকারীদের জন্য ।');
INSERT INTO "rawai" VALUES(83,11,'83:11','যারা প্রতিদান দিবসকে অস্বীকার করে।');
INSERT INTO "rawai" VALUES(83,12,'83:12','আর সকল সীমালঙ্ঘনকারী পাপাচারী ছাড়া কেউ তা অস্বীকার করে না।');
INSERT INTO "rawai" VALUES(83,13,'83:13','যখন তার কাছে আমার আয়াতসমূহ তিলাওয়াত করা হয় তখন সে বলে, ‘পূর্ববর্তীদের রূপকথা।’');
INSERT INTO "rawai" VALUES(83,14,'83:14','কখনো নয়, বরং তারা যা অর্জন করত তা-ই তাদের অন্তরসমূহকে ঢেকে দিয়েছে।');
INSERT INTO "rawai" VALUES(83,15,'83:15','কখনো নয়, নিশ্চয় সেদিন তারা তাদের রব থেকে পর্দার আড়ালে থাকবে।');
INSERT INTO "rawai" VALUES(83,16,'83:16','তারপর নিশ্চয় তারা প্রজ্জ্বলিত আগুনে প্রবেশ করবে।');
INSERT INTO "rawai" VALUES(83,17,'83:17','তারপর বলা হবে, এটাই তা যা তোমরা অস্বীকার করতে।');
INSERT INTO "rawai" VALUES(83,18,'83:18','কখনো নয়, নিশ্চয় নেককার লোকদের আমলনামা থাকবে ইল্লিয়্যীনে ।* *আমল নামা। অথবা সপ্তম আকাশে জান্নাতের নিচে অবস্থিত একটি স্থান। অথবা জান্নাতের সর্বোচ্চ মর্যাদাপূর্ণ স্থান।');
INSERT INTO "rawai" VALUES(83,19,'83:19','কিসে তোমাকে জানাবে ‘ইল্লিয়্যীন’ কী?');
INSERT INTO "rawai" VALUES(83,20,'83:20','লিখিত কিতাব।');
INSERT INTO "rawai" VALUES(83,21,'83:21','নৈকট্যপ্রাপ্তরাই তা অবলোকন করে।');
INSERT INTO "rawai" VALUES(83,22,'83:22','নিশ্চয় নেককাররাই থাকবে সুখ-স্বাচ্ছন্দ্যের মধ্যে।');
INSERT INTO "rawai" VALUES(83,23,'83:23','সুসজ্জিত আসনে বসে তারা দেখতে থাকবে।');
INSERT INTO "rawai" VALUES(83,24,'83:24','তুমি তাদের চেহারাসমূহে সুখ-স্বাচ্ছন্দ্যের লাবণ্যতা দেখতে পাবে।');
INSERT INTO "rawai" VALUES(83,25,'83:25','তাদেরকে সীলমোহর করা বিশুদ্ধ পানীয় থেকে পান করানো হবে।');
INSERT INTO "rawai" VALUES(83,26,'83:26','তার মোহর হবে মিসক। আর প্রতিযোগিতাকারীদের উচিৎ এ বিষয়ে প্রতিযোগিতা করা।');
INSERT INTO "rawai" VALUES(83,27,'83:27','আর তার মিশ্রণ হবে তাসনীম থেকে।');
INSERT INTO "rawai" VALUES(83,28,'83:28','তা এক প্রস্রবণ, যা থেকে নৈকট্যপ্রাপ্তরা পান করবে।');
INSERT INTO "rawai" VALUES(83,29,'83:29','নিশ্চয় যারা অপরাধ করেছে তারা মুমিনদেরকে নিয়ে হাসত।');
INSERT INTO "rawai" VALUES(83,30,'83:30','আর যখন তারা মুমিনদের পাশ দিয়ে যেত তখন তারা তাদেরকে নিয়ে চোখ টিপে বিদ্রূপ করত।');
INSERT INTO "rawai" VALUES(83,31,'83:31','আর যখন তারা পরিবার-পরিজনের কাছে ফিরে আসত তখন তারা উৎফুল্ল হয়ে ফিরে আসত।');
INSERT INTO "rawai" VALUES(83,32,'83:32','আর যখন তারা মুমিনদেরকে দেখত তখন বলত, ‘নিশ্চয় এরা পথভ্রষ্ট’।');
INSERT INTO "rawai" VALUES(83,33,'83:33','আর তাদেরকে তো মুমিনদের হিফাযতকারী হিসেবে পাঠানো হয়নি।');
INSERT INTO "rawai" VALUES(83,34,'83:34','অতএব আজ মুমিনরাই কাফিরদেরকে নিয়ে হাসবে।');
INSERT INTO "rawai" VALUES(83,35,'83:35','উচ্চ আসনে বসে তারা দেখতে থাকবে।');
INSERT INTO "rawai" VALUES(83,36,'83:36','কাফিরদেরকে তাদের কৃতকর্মের প্রতিদান দেয়া হল তো?');
INSERT INTO "rawai" VALUES(84,1,'84:1','যখন আসমান ফেটে যাবে।');
INSERT INTO "rawai" VALUES(84,2,'84:2','আর তার রবের নির্দেশ পালন করবে এবং এটাই তার করণীয়।');
INSERT INTO "rawai" VALUES(84,3,'84:3','আর যখন যমীনকে সম্প্রসারিত করা হবে।');
INSERT INTO "rawai" VALUES(84,4,'84:4','আর তার মধ্যে যা রয়েছে তা নিক্ষেপ করবে এবং খালি হয়ে যাবে।');
INSERT INTO "rawai" VALUES(84,5,'84:5','আর তার রবের নির্দেশ পালন করবে এবং এটাই তার করণীয়।');
INSERT INTO "rawai" VALUES(84,6,'84:6','হে মানুষ, তোমার রব পর্যন্ত (পৌঁছতে) অবশ্যই তোমাকে কঠোর পরিশ্রম করতে হবে। অতঃপর তুমি তাঁর সাক্ষাৎ পাবে।');
INSERT INTO "rawai" VALUES(84,7,'84:7','অতঃপর যাকে তার আমলনামা তার ডান হাতে দেয়া হবে;');
INSERT INTO "rawai" VALUES(84,8,'84:8','অত্যন্ত সহজভাবেই তার হিসাব-নিকাশ করা হবে।');
INSERT INTO "rawai" VALUES(84,9,'84:9','আর সে তার পরিবার-পরিজনের কাছে আনন্দিত হয়ে ফিরে যাবে।');
INSERT INTO "rawai" VALUES(84,10,'84:10','আর যাকে তার আমলনামা পিঠের পেছনে দেয়া হবে,');
INSERT INTO "rawai" VALUES(84,11,'84:11','অতঃপর সে ধ্বংস আহবান করতে থাকবে।');
INSERT INTO "rawai" VALUES(84,12,'84:12','আর সে জ্বলন্ত আগুনে প্রবেশ করবে।');
INSERT INTO "rawai" VALUES(84,13,'84:13','নিশ্চয় সে তার পরিবার-পরিজনদের মধ্যে আনন্দে ছিল।');
INSERT INTO "rawai" VALUES(84,14,'84:14','নিশ্চয় সে মনে করত যে, সে কখনো ফিরে যাবে না।');
INSERT INTO "rawai" VALUES(84,15,'84:15','হ্যাঁ, নিশ্চয় তার রব তার প্রতি সম্যক দৃষ্টি দানকারী।');
INSERT INTO "rawai" VALUES(84,16,'84:16','অতঃপর আমি কসম করছি পশ্চিম আকাশের লালিমার।');
INSERT INTO "rawai" VALUES(84,17,'84:17','আর রাতের কসম এবং রাত যা কিছুর সমাবেশ ঘটায় তার।');
INSERT INTO "rawai" VALUES(84,18,'84:18','আর চাঁদের কসম, যখন তা পরিপূর্ণ হয়।');
INSERT INTO "rawai" VALUES(84,19,'84:19','অবশ্যই তোমরা এক স্তর থেকে অন্য স্তরে আরোহণ করবে।');
INSERT INTO "rawai" VALUES(84,20,'84:20','অতএব তাদের কী হল যে, তারা ঈমান আনছে না?');
INSERT INTO "rawai" VALUES(84,21,'84:21','আর যখন তাদের কাছে কুরআন তিলাওয়াত করা হয় তখন তারা সিজদা করে না।[সাজদাহ]');
INSERT INTO "rawai" VALUES(84,22,'84:22','বরং কাফিররা অস্বীকার করে।');
INSERT INTO "rawai" VALUES(84,23,'84:23','আর তারা যা অন্তরে পোষণ করে আল্লাহ তা সবিশেষ পরিজ্ঞাত।');
