INSERT INTO "rawai" VALUES(84,25,'84:25','কিন্তু যারা ঈমান আনে এবং সৎকর্ম করে তাদের জন্য রয়েছে নিরবচ্ছিন্ন প্রতিদান।');
INSERT INTO "rawai" VALUES(85,1,'85:1','কক্ষপথ বিশিষ্ট আসমানের কসম,');
INSERT INTO "rawai" VALUES(85,2,'85:2','আর ওয়াদাকৃত দিনের কসম,');
INSERT INTO "rawai" VALUES(85,3,'85:3','আর কসম সাক্ষ্যদাতার এবং যার ব্যাপারে সাক্ষ্য দেয়া হবে তার,');
INSERT INTO "rawai" VALUES(85,4,'85:4','ধ্বংস হয়েছে গর্তের অধিপতিরা,');
INSERT INTO "rawai" VALUES(85,5,'85:5','(যাতে ছিল) ইন্ধনপূর্ণ আগুন।');
INSERT INTO "rawai" VALUES(85,6,'85:6','যখন তারা তার কিনারায় উপবিষ্ট ছিল।');
INSERT INTO "rawai" VALUES(85,7,'85:7','আর তারা মুমিনদের সাথে যা করছিল তার প্রত্যক্ষদর্শী।');
INSERT INTO "rawai" VALUES(85,8,'85:8','আর তারা তাদেরকে নির্যাতন করেছিল শুধুমাত্র এ কারণে যে, তারা মহাপরাক্রমশালী প্রশংসিত আল্লাহর প্রতি ঈমান এনেছিল।');
