INSERT INTO "rawai" VALUES(85,10,'85:10','নিশ্চয় যারা মুমিন পুরুষ ও মুমিন নারীদেরকে আযাব দেয়, তারপর তাওবা করে না, তাদের জন্য রয়েছে জাহান্নামের আযাব। আর তাদের জন্য রয়েছে আগুনে দগ্ধ হওয়ার আযাব।');
INSERT INTO "rawai" VALUES(85,11,'85:11','নিশ্চয় যারা ঈমান আনে এবং সৎকর্ম করে তাদের জন্য রয়েছে জান্নাত। যার তলদেশে প্রবাহিত হবে নহরসমূহ। এটাই বিরাট সফলতা।');
INSERT INTO "rawai" VALUES(85,12,'85:12','নিশ্চয় তোমার রবের পাকড়াও বড়ই কঠিন।');
INSERT INTO "rawai" VALUES(85,13,'85:13','নিশ্চয় তিনি সৃষ্টির সূচনা করেন এবং তিনিই পুনরায় সৃষ্টি করবেন।');
INSERT INTO "rawai" VALUES(85,14,'85:14','আর তিনি অত্যন্ত ক্ষমাশীল, প্রেমময়।');
INSERT INTO "rawai" VALUES(85,15,'85:15','আরশের অধিপতি, মহান।');
INSERT INTO "rawai" VALUES(85,16,'85:16','তিনি তা-ই করেন যা চান ।');
INSERT INTO "rawai" VALUES(85,17,'85:17','তোমার কাছে কি সৈন্যবাহিনীর খবর পৌঁছেছে?');
INSERT INTO "rawai" VALUES(85,18,'85:18','ফির‘আউন ও সামূদের।');
INSERT INTO "rawai" VALUES(85,19,'85:19','বরং কাফিররা মিথ্যারোপে লিপ্ত।');
INSERT INTO "rawai" VALUES(85,20,'85:20','আর আল্লাহ তাদের অলক্ষ্যে তাদের পরিবেষ্টনকারী।');
INSERT INTO "rawai" VALUES(85,21,'85:21','বরং তা সম্মানিত কুরআন।');
INSERT INTO "rawai" VALUES(85,22,'85:22','সুরক্ষিত ফলকে (লিপিবদ্ধ)।');
INSERT INTO "rawai" VALUES(86,1,'86:1','কসম আসমানের ও রাতে আগমনকারীর।');
INSERT INTO "rawai" VALUES(86,2,'86:2','আর কিসে তোমাকে জানাবে রাতে আগমনকারী কী?');
INSERT INTO "rawai" VALUES(86,3,'86:3','উজ্জ্বল নক্ষত্র।');
INSERT INTO "rawai" VALUES(86,4,'86:4','প্রত্যেক জীবের উপরই সংরক্ষক রয়েছে।');
INSERT INTO "rawai" VALUES(86,5,'86:5','অতএব মানুষের চিন্তা করে দেখা উচিৎ, তাকে কী থেকে সৃষ্টি করা হয়েছে ?');
INSERT INTO "rawai" VALUES(86,6,'86:6','তাকে সৃষ্টি করা হয়েছে দ্রুতবেগে নির্গত পানি থেকে।');
INSERT INTO "rawai" VALUES(86,7,'86:7','যা বের হয় মেরুদন্ড ও বুকের হাঁড়ের মধ্য থেকে।');
INSERT INTO "rawai" VALUES(86,8,'86:8','নিশ্চয় তিনি তাকে ফিরিয়ে আনতে সক্ষম।');
INSERT INTO "rawai" VALUES(86,9,'86:9','যে দিন গোপন বিষয়াদি পরীক্ষা করা হবে।');
INSERT INTO "rawai" VALUES(86,10,'86:10','অতএব তার কোন শক্তি থাকবে না। আর সাহায্যকারীও না।');
INSERT INTO "rawai" VALUES(86,11,'86:11','বৃষ্টিসম্পন্ন আসমানের কসম।');
INSERT INTO "rawai" VALUES(86,12,'86:12','কসম বিদীর্ণ যমীনের।');
INSERT INTO "rawai" VALUES(86,13,'86:13','নিশ্চয় এটা ফয়সালাকারী বাণী।');
INSERT INTO "rawai" VALUES(86,14,'86:14','আর তা অনর্থক নয়।');
INSERT INTO "rawai" VALUES(86,15,'86:15','নিশ্চয় তারা ভীষণ কৌশল করছে।');
INSERT INTO "rawai" VALUES(86,16,'86:16','আর আমিও ভীষণ কৌশল করছি।');
INSERT INTO "rawai" VALUES(86,17,'86:17','অতএব কাফিরদেরকে অবকাশ দাও, তাদেরকে কিছু সময়ের অবকাশ দাও।');
INSERT INTO "rawai" VALUES(87,1,'87:1','তুমি তোমার সুমহান রবের নামের তাসবীহ পাঠ কর,');
INSERT INTO "rawai" VALUES(87,2,'87:2','যিনি সৃষ্টি করেন। অতঃপর সুসম করেন।');
INSERT INTO "rawai" VALUES(87,3,'87:3','আর যিনি নিরূপণ করেন অতঃপর পথ নির্দেশ দেন।');
INSERT INTO "rawai" VALUES(87,4,'87:4','আর যিনি তৃণ-লতা বের করেন।');
INSERT INTO "rawai" VALUES(87,5,'87:5','তারপর তা কালো খড়-কুটায় পরিণত করেন।');
INSERT INTO "rawai" VALUES(87,6,'87:6','আমি তোমাকে পড়িয়ে দেব অতঃপর তুমি ভুলবে না।');
INSERT INTO "rawai" VALUES(87,7,'87:7','আল্লাহ যা চান তা ছাড়া। নিশ্চয় তিনি জানেন, যা প্রকাশ্য এবং যা গোপন থাকে।');
INSERT INTO "rawai" VALUES(87,8,'87:8','আর আমি তোমাকে সহজ বিষয় সহজ করে দেব।');
INSERT INTO "rawai" VALUES(87,9,'87:9','অতঃপর উপদেশ দাও যদি উপদেশ ফলপ্রসু হয়।');
INSERT INTO "rawai" VALUES(87,10,'87:10','সে-ই উপদেশ গ্রহণ করে, যে ভয় করে ।');
INSERT INTO "rawai" VALUES(87,11,'87:11','আর হতভাগাই তা এড়িয়ে যায়।');
INSERT INTO "rawai" VALUES(87,12,'87:12','যে ভয়াবহ আগুনে প্রবেশ করবে।');
INSERT INTO "rawai" VALUES(87,13,'87:13','তারপর সে সেখানে মরবেও না এবং বাঁচবেও না।');
INSERT INTO "rawai" VALUES(87,14,'87:14','অবশ্যই সাফল্য লাভ করবে যে আত্মশুদ্ধি করবে,');
INSERT INTO "rawai" VALUES(87,15,'87:15','আর তার রবের নাম স্মরণ করবে, অতঃপর সালাত আদায় করবে।');
INSERT INTO "rawai" VALUES(87,16,'87:16','বরং তোমরা দুনিয়ার জীবনকে প্রাধান্য দিচ্ছ।');
INSERT INTO "rawai" VALUES(87,17,'87:17','অথচ আখিরাত সর্বোত্তম ও স্থায়ী।');
INSERT INTO "rawai" VALUES(87,18,'87:18','নিশ্চয় এটা আছে পূর্ববর্তী সহীফাসমূহে।');
INSERT INTO "rawai" VALUES(87,19,'87:19','ইবরাহীম ও মূসার সহীফাসমূহে।');
INSERT INTO "rawai" VALUES(88,1,'88:1','কিয়ামতের সংবাদ কি তোমার কাছে এসেছে?');
INSERT INTO "rawai" VALUES(88,2,'88:2','সেদিন অনেক চেহারা হবে অবনত।');
INSERT INTO "rawai" VALUES(88,3,'88:3','কর্মক্লান্ত, পরিশ্রান্ত।');
INSERT INTO "rawai" VALUES(88,4,'88:4','তারা প্রবেশ করবে জ্বলন্ত আগুনে।');
INSERT INTO "rawai" VALUES(88,5,'88:5','তাদের পান করানো হবে ফুটন্ত ঝর্ণা থেকে।');
INSERT INTO "rawai" VALUES(88,6,'88:6','তাদের জন্য কাঁটাবিশিষ্ট গুল্ম ছাড়া কোন খাদ্য থাকবে না।');
INSERT INTO "rawai" VALUES(88,7,'88:7','তা মোটা-তাজাও করবে না এবং ক্ষুধাও নিবারণ করবে না।');
INSERT INTO "rawai" VALUES(88,8,'88:8','সেদিন অনেক চেহারা হবে লাবণ্যময়।');
INSERT INTO "rawai" VALUES(88,9,'88:9','নিজদের চেষ্টা সাধনায় সন্তুষ্ট।');
INSERT INTO "rawai" VALUES(88,10,'88:10','সুউচ্চ জান্নাতে।');
INSERT INTO "rawai" VALUES(88,11,'88:11','সেখানে তারা শুনবে না কোন অসার বাক্য।');
INSERT INTO "rawai" VALUES(88,12,'88:12','সেখানে থাকবে প্রবাহমান ঝর্ণাধারা,');
INSERT INTO "rawai" VALUES(88,13,'88:13','সেখানে থাকবে সুউচ্চ আসনসমূহ।');
INSERT INTO "rawai" VALUES(88,14,'88:14','আর প্রস্তুত পানপাত্রসমূহ।');
INSERT INTO "rawai" VALUES(88,15,'88:15','আর সারি সারি বালিশসমূহ।');
INSERT INTO "rawai" VALUES(88,16,'88:16','আর বিস্তৃত বিছানো কার্পেটরাজি।');
INSERT INTO "rawai" VALUES(88,17,'88:17','তবে কি তারা উটের প্রতি দৃষ্টিপাত করে না, কীভাবে তা সৃষ্টি করা হয়েছে?');
INSERT INTO "rawai" VALUES(88,18,'88:18','আর আকাশের দিকে, কীভাবে তা ঊর্ধ্বে স্থাপন করা হয়েছে?');
INSERT INTO "rawai" VALUES(88,19,'88:19','আর পর্বতমালার দিকে, কীভাবে তা স্থাপন করা হয়েছে?');
INSERT INTO "rawai" VALUES(88,20,'88:20','আর যমীনের দিকে, কীভাবে তা বিস্তৃত করা হয়েছে?');
INSERT INTO "rawai" VALUES(88,21,'88:21','অতএব তুমি উপদেশ দাও, তুমি তো একজন উপদেশদাতা মাত্র।');
INSERT INTO "rawai" VALUES(88,22,'88:22','তুমি তাদের উপর শক্তি প্রয়োগকারী নও।');
INSERT INTO "rawai" VALUES(88,23,'88:23','তবে যে মুখ ফিরিয়ে নেয় এবং কুফরী করে,');
INSERT INTO "rawai" VALUES(88,24,'88:24','ফলে আল্লাহ তাকে কঠোর আযাব দেবেন।');
INSERT INTO "rawai" VALUES(88,25,'88:25','নিশ্চয় আমারই নিকট তাদের প্রত্যাবর্তন।');
INSERT INTO "rawai" VALUES(88,26,'88:26','তারপর নিশ্চয় তাদের হিসাব-নিকাশ আমারই দায়িত্বে।');
INSERT INTO "rawai" VALUES(89,1,'89:1','কসম ভোরবেলার।');
INSERT INTO "rawai" VALUES(89,2,'89:2','কসম দশ রাতের।');
INSERT INTO "rawai" VALUES(89,3,'89:3','কসম জোড় ও বিজোড়ের।');
INSERT INTO "rawai" VALUES(89,4,'89:4','কসম রাতের, যখন তা বিদায় নেয়।');
INSERT INTO "rawai" VALUES(89,5,'89:5','এর মধ্যে কি বোধশক্তিসম্পন্ন ব্যক্তির জন্য কসম আছে?');
INSERT INTO "rawai" VALUES(89,6,'89:6','তুমি কি দেখনি তোমার রব কিরূপ আচরণ করেছেন ‘আদ জাতির সাথে?');
INSERT INTO "rawai" VALUES(89,7,'89:7','ইরাম গোত্রের সাথে, যারা ছিল সুউচ্চ স্তম্ভের অধিকারী?');
INSERT INTO "rawai" VALUES(89,8,'89:8','যার সমতুল্য কোন দেশে সৃষ্টি করা হয়নি।');
INSERT INTO "rawai" VALUES(89,9,'89:9','আর সামূদ সম্প্রদায়, যারা উপত্যকায় পাথর কেটে বাড়ি ঘর নির্মাণ করেছিল?');
INSERT INTO "rawai" VALUES(89,10,'89:10','আর ফির‘আউন, সেনাছাউনীর অধিপতি?');
INSERT INTO "rawai" VALUES(89,11,'89:11','যারা সকল দেশে সীমা ছাড়িয়ে গিয়েছিল।');
INSERT INTO "rawai" VALUES(89,12,'89:12','অতঃপর তারা সেখানে বিপর্যয় বাড়িয়ে দিয়েছিল।');
INSERT INTO "rawai" VALUES(89,13,'89:13','ফলে তোমার রব তাদের উপর আযাবের কশাঘাত মারলেন।');
INSERT INTO "rawai" VALUES(89,14,'89:14','নিশ্চয় তোমার রব ঘাঁটিতেই।* *مرصاد অর্থ ঘাঁটি, যেখানে কোন লোক তার শত্রুর অজান্তে তার অপেক্ষায় ওঁত পেতে থাকে এবং শত্রুকে বাগে পেয়েই আক্রমণ করে। এখানে আল্লাহর ক্ষেত্রে শব্দটি সতর্ক দৃষ্টি রাখা, অর্থে ব্যবহৃত হয়েছে।');
INSERT INTO "rawai" VALUES(89,15,'89:15','আর মানুষ তো এমন যে, যখন তার রব তাকে পরীক্ষা করেন, অতঃপর তাকে সম্মান দান করেন এবং অনুগ্রহ প্রদান করেন, তখন সে বলে, ‘আমার রব আমাকে সম্মানিত করেছেন।');
INSERT INTO "rawai" VALUES(89,16,'89:16','আর যখন তিনি তাকে পরীক্ষা করেন এবং তার উপর তার রিয্ককে সঙ্কুচিত করে দেন, তখন সে বলে, ‘আমার রব আমাকে অপমানিত করেছেন’।');
INSERT INTO "rawai" VALUES(89,17,'89:17','কখনো নয়, বরং তোমরা ইয়াতীমদের দয়া-অনুগ্রহ প্রদর্শন কর না।');
INSERT INTO "rawai" VALUES(89,18,'89:18','আর তোমরা মিসকীনদের খাদ্যদানে পরষ্পরকে উৎসাহিত কর না।');
INSERT INTO "rawai" VALUES(89,19,'89:19','আর তোমরা উত্তরাধিকারের সম্পত্তি সম্পূর্ণরূপে ভক্ষণ কর।');
INSERT INTO "rawai" VALUES(89,20,'89:20','আর তোমরা ধন-সম্পদকে অতিশয় ভালবাস।');
INSERT INTO "rawai" VALUES(89,21,'89:21','কখনো নয়, যখন যমীনকে চূর্ণ-বিচূর্ণ করা হবে পরিপূর্ণভাবে।');
INSERT INTO "rawai" VALUES(89,22,'89:22','আর তোমার রব ও ফেরেশতাগণ উপস্থিত হবেন সারিবদ্ধভাবে।');
INSERT INTO "rawai" VALUES(89,23,'89:23','আর সেদিন জাহান্নামকে উপস্থিত করা হবে, সেদিন মানুষ স্মরণ করবে, কিন্তু সেই স্মরণ তার কী উপকারে আসবে?');
INSERT INTO "rawai" VALUES(89,24,'89:24','সে বলবে, ‘হায়! যদি আমি কিছু আগে পাঠাতাম আমার এ জীবনের জন্য’!');
INSERT INTO "rawai" VALUES(89,25,'89:25','অতঃপর সেদিন তাঁর আযাবের মত আযাব কেউ দিতে পারবে না।');
INSERT INTO "rawai" VALUES(89,26,'89:26','আর কেউ তাঁর বাঁধার মত বাঁধতে পারবে না।');
INSERT INTO "rawai" VALUES(89,27,'89:27','হে প্রশান্ত আত্মা!');
INSERT INTO "rawai" VALUES(89,28,'89:28','তুমি ফিরে এসো তোমার রবের প্রতি সন্তুষ্টচিত্তে, সন্তোষভাজন হয়ে।');
INSERT INTO "rawai" VALUES(89,29,'89:29','অতঃপর আমার বান্দাদের মধ্যে শামিল হয়ে যাও।');
INSERT INTO "rawai" VALUES(89,30,'89:30','আর প্রবেশ কর আমার জান্নাতে।');
INSERT INTO "rawai" VALUES(90,1,'90:1','আমি কসম করছি এই নগরীর।');
INSERT INTO "rawai" VALUES(90,2,'90:2','আর তুমি এই নগরীতে মুক্ত।');
INSERT INTO "rawai" VALUES(90,3,'90:3','কসম জনকের এবং যা সে জন্ম দেয়।');
INSERT INTO "rawai" VALUES(90,4,'90:4','নিঃসন্দেহে আমি মানুষকে সৃষ্টি করেছি কষ্ট- ক্লেশের মধ্যে।');
INSERT INTO "rawai" VALUES(90,5,'90:5','সে কি ধারণা করছে যে, কেউ কখনো তার উপর ক্ষমতাবান হবে না?');
INSERT INTO "rawai" VALUES(90,6,'90:6','সে বলে, ‘আমি প্রচুর ধন-সম্পদ নিঃশেষ করেছি’।');
INSERT INTO "rawai" VALUES(90,7,'90:7','সে কি ধারণা করছে যে, কেউ তাকে দেখেনি?');
INSERT INTO "rawai" VALUES(90,8,'90:8','আমি কি তার জন্য দু’টি চোখ বানাইনি?');
INSERT INTO "rawai" VALUES(90,9,'90:9','আর একটি জিহবা ও দু’টি ঠোঁট?');
INSERT INTO "rawai" VALUES(90,10,'90:10','আর আমি তাকে দু’টি পথ প্রদর্শন করেছি।');
INSERT INTO "rawai" VALUES(90,11,'90:11','তবে সে বন্ধুর গিরিপথটি অতিক্রম করতে সচেষ্ট হয়নি।');
INSERT INTO "rawai" VALUES(90,12,'90:12','আর কিসে তোমাকে জানাবে, বন্ধুর গিরিপথটি কী?');
INSERT INTO "rawai" VALUES(90,13,'90:13','তা হচ্ছে, দাস মুক্তকরণ।');
INSERT INTO "rawai" VALUES(90,14,'90:14','অথবা খাদ্য দান করা দুর্ভিক্ষের দিনে।');
INSERT INTO "rawai" VALUES(90,15,'90:15','ইয়াতীম আত্মীয়-স্বজনকে।');
INSERT INTO "rawai" VALUES(90,16,'90:16','অথবা ধূলি-মলিন মিসকীনকে।');
INSERT INTO "rawai" VALUES(90,17,'90:17','অতঃপর সে তাদের অন্তর্ভুক্ত হয়ে যায়, যারা ঈমান এনেছে এবং পরস্পরকে উপদেশ দেয় ধৈর্যধারণের, আর পরস্পরকে উপদেশ দেয় দয়া-অনুগ্রহের।');
INSERT INTO "rawai" VALUES(90,18,'90:18','তারাই সৌভাগ্যবান।');
INSERT INTO "rawai" VALUES(90,19,'90:19','আর যারা আমার আয়াতসমূহকে অস্বীকার করেছে তারাই দুর্ভাগা।');
INSERT INTO "rawai" VALUES(90,20,'90:20','তাদের উপর থাকবে অবরুদ্ধ আগুন।');
INSERT INTO "rawai" VALUES(91,1,'91:1','কসম সূর্যের ও তার কিরণের।');
INSERT INTO "rawai" VALUES(91,2,'91:2','কসম চাঁদের, যখন তা সূর্যের অনুগামী হয়।');
INSERT INTO "rawai" VALUES(91,3,'91:3','কসম দিবসের, যখন তা সূর্যকে প্রকাশ করে।');
INSERT INTO "rawai" VALUES(91,4,'91:4','কসম রাতের, যখন তা সূর্যকে ঢেকে দেয়।');
INSERT INTO "rawai" VALUES(91,5,'91:5','কসম আসমানের এবং যিনি তা বানিয়েছেন।');
INSERT INTO "rawai" VALUES(91,6,'91:6','কসম যমীনের এবং যিনি তা বিস্তৃত করেছেন।');
INSERT INTO "rawai" VALUES(91,7,'91:7','কসম নাফ্সের এবং যিনি তা সুসম করেছেন।');
INSERT INTO "rawai" VALUES(91,8,'91:8','অতঃপর তিনি তাকে অবহিত করেছেন তার পাপসমূহ ও তার তাকওয়া সম্পর্কে।');
INSERT INTO "rawai" VALUES(91,9,'91:9','নিঃসন্দেহে সে সফলকাম হয়েছে, যে তাকে পরিশুদ্ধ করেছে।');
INSERT INTO "rawai" VALUES(91,10,'91:10','এবং সে ব্যর্থ হয়েছে, যে তা (নাফস)-কে কলুষিত করেছে।');
INSERT INTO "rawai" VALUES(91,11,'91:11','সামূদ জাতি আপন অবাধ্যতাবশত অস্বীকার করেছিল।');
INSERT INTO "rawai" VALUES(91,12,'91:12','যখন তাদের সর্বাধিক হতভাগা ব্যক্তিটি তৎপর হয়ে উঠল।');
INSERT INTO "rawai" VALUES(91,13,'91:13','তখন আল্লাহর রাসূল তাদেরকে বলেছিল, ‘আল্লাহর উষ্ট্রী ও তার পানি পান সম্পর্কে সতর্ক হও।’');
INSERT INTO "rawai" VALUES(91,14,'91:14','কিন্তু তারা তাকে অস্বীকার করল এবং উষ্ট্রীকে যবেহ করল। ফলে তাদের রব তাদের অপরাধের কারণে তাদেরকে সমূলে ধ্বংস করে দিলেন। অতঃপর তা একাকার করে দিলেন।');
INSERT INTO "rawai" VALUES(91,15,'91:15','আর তিনি এর পরিণামকে ভয় করেন না।');
INSERT INTO "rawai" VALUES(92,1,'92:1','কসম রাতের, যখন তা ঢেকে দেয়।');
INSERT INTO "rawai" VALUES(92,2,'92:2','কসম দিনের, যখন তা আলোকিত হয়।');
INSERT INTO "rawai" VALUES(92,3,'92:3','কসম তাঁর, যিনি নর ও নারী সৃষ্টি করেছেন।');
INSERT INTO "rawai" VALUES(92,4,'92:4','নিশ্চয় তোমাদের কর্মপ্রচেষ্টা বিভিন্ন প্রকারের।');
INSERT INTO "rawai" VALUES(92,5,'92:5','সুতরাং যে দান করেছে এবং তাকওয়া অবলম্বন করেছে,');
INSERT INTO "rawai" VALUES(92,6,'92:6','আর উত্তমকে সত্য বলে বিশ্বাস করেছে,');
INSERT INTO "rawai" VALUES(92,7,'92:7','আমি তার জন্য সহজ পথে চলা সুগম করে দেব।');
INSERT INTO "rawai" VALUES(92,8,'92:8','আর যে কার্পণ্য করেছে এবং নিজকে স্বয়ংসম্পূর্ণ মনে করেছে,');
INSERT INTO "rawai" VALUES(92,9,'92:9','আর উত্তমকে মিথ্যা বলে মনে করেছে,');
INSERT INTO "rawai" VALUES(92,10,'92:10','আমি তার জন্য কঠিন পথে চলা সুগম করে দেব।');
INSERT INTO "rawai" VALUES(92,11,'92:11','আর তার সম্পদ তার কোন কাজে আসবে না, যখন সে অধঃপতিত হবে।');
INSERT INTO "rawai" VALUES(92,12,'92:12','নিশ্চয় পথ প্রদর্শন করাই আমার দায়িত্ব।');
INSERT INTO "rawai" VALUES(92,13,'92:13','আর অবশ্যই আমার অধিকারে পরকাল ও ইহকাল।');
INSERT INTO "rawai" VALUES(92,14,'92:14','অতএব আমি তোমাদের সতর্ক করে দিয়েছি লেলিহান আগুন সম্পর্কে,');
INSERT INTO "rawai" VALUES(92,15,'92:15','তাতে নিতান্ত হতভাগা ছাড়া কেউ প্রবেশ করবে না;');
INSERT INTO "rawai" VALUES(92,16,'92:16','যে অস্বীকার করেছে এবং মুখ ফিরিয়ে নিয়েছে।');
INSERT INTO "rawai" VALUES(92,17,'92:17','আর তা থেকে দূরে রাখা হবে পরম মুত্তাকীকে।');
INSERT INTO "rawai" VALUES(92,18,'92:18','যে তার সম্পদ দান করে আত্ম-শুদ্ধির উদ্দেশ্যে,');
INSERT INTO "rawai" VALUES(92,19,'92:19','আর তার প্রতি কারো এমন কোন অনুগ্রহ নেই, যার প্রতিদান দিতে হবে।');
INSERT INTO "rawai" VALUES(92,20,'92:20','কেবল তার মহান রবের সন্তুষ্টির প্রত্যাশায়।');
INSERT INTO "rawai" VALUES(92,21,'92:21','আর অচিরেই সে সন্তোষ লাভ করবে।');
INSERT INTO "rawai" VALUES(93,1,'93:1','কসম পূর্বা‎‎হ্নের,');
INSERT INTO "rawai" VALUES(93,2,'93:2','কসম রাতের যখন তা অন্ধকারাচ্ছন্ন হয়।');
INSERT INTO "rawai" VALUES(93,3,'93:3','তোমার রব তোমাকে পরিত্যাগ করেননি এবং অসন্তুষ্টও হননি।');
INSERT INTO "rawai" VALUES(93,4,'93:4','আর অবশ্যই তোমার জন্য পরবর্তী সময় পূর্ববর্তী সময়ের চেয়ে উত্তম।');
INSERT INTO "rawai" VALUES(93,5,'93:5','আর অচিরেই তোমার রব তোমাকে দান করবেন, ফলে তুমি সন্তুষ্ট হবে।');
INSERT INTO "rawai" VALUES(93,6,'93:6','তিনি কি তোমাকে ইয়াতীম অবস্থায় পাননি? অতঃপর তিনি আশ্রয় দিয়েছেন।');
INSERT INTO "rawai" VALUES(93,7,'93:7','আর তিনি তোমাকে পেয়েছেন পথ না জানা অবস্থায়। অতঃপর তিনি পথনির্দেশ দিয়েছেন।');
INSERT INTO "rawai" VALUES(93,8,'93:8','তিনি তোমাকে পেয়েছেন নিঃস্ব। অতঃপর তিনি সমৃদ্ধ করেছেন।');
INSERT INTO "rawai" VALUES(93,9,'93:9','সুতরাং তুমি ইয়াতীমের প্রতি কঠোর হয়ো না।');
INSERT INTO "rawai" VALUES(93,10,'93:10','আর ভিক্ষুককে তুমি ধমক দিওনা।');
INSERT INTO "rawai" VALUES(93,11,'93:11','আর তোমার রবের অনুগ্রহ তুমি বর্ণনা কর।');
INSERT INTO "rawai" VALUES(94,1,'94:1','আমি কি তোমার জন্য তোমার বক্ষ প্রশস্ত করিনি?');
INSERT INTO "rawai" VALUES(94,2,'94:2','আর আমি নামিয়ে দিয়েছি তোমার থেকে তোমার বোঝা,');
INSERT INTO "rawai" VALUES(94,3,'94:3','যা তোমার পিঠ ভেঙ্গে দিচ্ছিল।');
INSERT INTO "rawai" VALUES(94,4,'94:4','আর আমি তোমার (মর্যাদার) জন্য তোমার স্মরণকে সমুন্নত করেছি।');
INSERT INTO "rawai" VALUES(94,5,'94:5','সুতরাং কষ্টের সাথেই রয়েছে সুখ।');
INSERT INTO "rawai" VALUES(94,6,'94:6','নিশ্চয় কষ্টের সাথেই রয়েছে সুখ।');
INSERT INTO "rawai" VALUES(94,7,'94:7','অতএব যখনই তুমি অবসর পাবে, তখনই কঠোর ইবাদাতে রত হও।');
INSERT INTO "rawai" VALUES(94,8,'94:8','আর তোমার রবের প্রতি আকৃষ্ট হও।');
INSERT INTO "rawai" VALUES(95,1,'95:1','কসম ‘তীন ও যায়তূন’ এর।');
INSERT INTO "rawai" VALUES(95,2,'95:2','কসম ‘সিনাই’ পর্বতের,');
INSERT INTO "rawai" VALUES(95,3,'95:3','কসম এই নিরাপদ নগরীর।');
INSERT INTO "rawai" VALUES(95,4,'95:4','অবশ্যই আমি মানুষকে সৃষ্টি করেছি সর্বোত্তম গঠনে।');
INSERT INTO "rawai" VALUES(95,5,'95:5','তারপর আমি তাকে ফিরিয়ে দিয়েছি হীনদের হীনতম রূপে।');
INSERT INTO "rawai" VALUES(95,6,'95:6','তবে যারা ঈমান এনেছে এবং সৎকর্ম করেছে, তাদের জন্য রয়েছে নিরবচ্ছিন্ন পুরস্কার।');
INSERT INTO "rawai" VALUES(95,7,'95:7','সুতরাং এরপরও কিসে তোমাকে কর্মফল সম্পর্কে অবিশ্বাসী করে তোলে?');
INSERT INTO "rawai" VALUES(95,8,'95:8','আল্লাহ কি বিচারকদের শ্রেষ্ঠ বিচারক নন?');
INSERT INTO "rawai" VALUES(96,1,'96:1','পড় তোমার রবের নামে, যিনি সৃষ্টি করেছেন।');
INSERT INTO "rawai" VALUES(96,2,'96:2','তিনি সৃষ্টি করেছেন মানুষকে ''আলাক'' থেকে।');
INSERT INTO "rawai" VALUES(96,3,'96:3','পড়, আর তোমার রব মহামহিম।');
INSERT INTO "rawai" VALUES(96,4,'96:4','যিনি কলমের সাহায্যে শিক্ষা দিয়েছেন।');
INSERT INTO "rawai" VALUES(96,5,'96:5','তিনি মানুষকে তা শিক্ষা দিয়েছেন, যা সে জানত না।');
INSERT INTO "rawai" VALUES(96,6,'96:6','কখনো নয়, নিশ্চয় মানুষ সীমালঙ্ঘন করে থাকে।');
INSERT INTO "rawai" VALUES(96,7,'96:7','কেননা সে নিজকে মনে করে স্বয়ংসম্পূর্ণ।');
INSERT INTO "rawai" VALUES(96,8,'96:8','নিশ্চয় তোমার রবের দিকেই প্রত্যাবর্তন।');
INSERT INTO "rawai" VALUES(96,9,'96:9','তুমি কি তাকে দেখেছ যে নিষেধ করে।');
INSERT INTO "rawai" VALUES(96,10,'96:10','এক বান্দাকে, যখন সে সালাত আদায় করে?');
INSERT INTO "rawai" VALUES(96,11,'96:11','তুমি কি দেখেছ, যদি সে হিদায়াতের উপর থাকে,');
INSERT INTO "rawai" VALUES(96,12,'96:12','অথবা তাকওয়ার নির্দেশ দেয়?');
INSERT INTO "rawai" VALUES(96,13,'96:13','যদি সে মিথ্যারোপ করে এবং মুখ ফিরিয়ে নেয়?');
INSERT INTO "rawai" VALUES(96,14,'96:14','সে কি জানেনা যে, নিঃসন্দেহে আল্লাহ দেখেন?');
INSERT INTO "rawai" VALUES(96,15,'96:15','কখনো নয়, যদি সে বিরত না হয়, তবে আমি তাকে কপালের সম্মুখভাগের চুল ধরে টেনে- হিঁচড়ে নিয়ে যাব।');
INSERT INTO "rawai" VALUES(96,16,'96:16','মিথ্যাবাদী, পাপিষ্ঠ কপাল।');
INSERT INTO "rawai" VALUES(96,17,'96:17','অতএব, সে তার সভাসদদের আহবান করুক।');
INSERT INTO "rawai" VALUES(96,18,'96:18','অচিরেই আমি ডেকে নেব জাহান্নামের প্রহরীদেরকে।');
INSERT INTO "rawai" VALUES(96,19,'96:19','কখনো নয়, তুমি তার আনুগত্য করবে না। আর সিজদা কর এবং নৈকট্য লাভ কর।[সাজদাহ]');
INSERT INTO "rawai" VALUES(97,1,'97:1','নিশ্চয়ই আমি এটি নাযিল করেছি ‘লাইলাতুল ক্বাদরে।’');
INSERT INTO "rawai" VALUES(97,2,'97:2','তোমাকে কিসে জানাবে ‘লাইলাতুল ক্বাদর’ কী?');
INSERT INTO "rawai" VALUES(97,3,'97:3','‘লাইলাতুল কদর’ হাজার মাস অপেক্ষা উত্তম।');
INSERT INTO "rawai" VALUES(97,4,'97:4','সে রাতে ফেরেশতারা ও রূহ (জিবরাইল) তাদের রবের অনুমতিক্রমে সকল সিদ্ধান্ত নিয়ে অবতরণ করে।');
INSERT INTO "rawai" VALUES(97,5,'97:5','শান্তিময় সেই রাত, ফজরের সূচনা পর্যন্ত।');
INSERT INTO "rawai" VALUES(98,1,'98:1','কিতাবীদের মধ্যে যারা কুফরী করে তারা ও মুশরিকরা, তাদের কাছে সুস্পষ্ট প্রমাণ না আসা পর্যন্ত (নিজদের অবিশ্বাসে) অটল থাকবে।');
INSERT INTO "rawai" VALUES(98,2,'98:2','আল্লাহর পক্ষ থেকে এক রাসূল পবিত্র কিতাবসমূহ তিলাওয়াত করে।');
INSERT INTO "rawai" VALUES(98,3,'98:3','তাতে রয়েছে সঠিক বিধিবদ্ধ বিধান।');
INSERT INTO "rawai" VALUES(98,4,'98:4','আর কিতাবীরা তাদের কাছে সুস্পষ্ট প্রমাণ আসার পরই কেবল মতভেদ করেছে।');
INSERT INTO "rawai" VALUES(98,5,'98:5','আর তাদেরকে কেবল এই নির্দেশ দেয়া হয়েছিল যে, তারা যেন আল্লাহর ‘ইবাদাত করে তাঁরই জন্য দীনকে একনিষ্ঠ করে, সালাত কায়েম করে এবং যাকাত দেয়; আর এটিই হল সঠিক দীন।');
INSERT INTO "rawai" VALUES(98,6,'98:6','নিশ্চয় কিতাবীদের মধ্যে যারা কুফরী করেছে ও মুশরিকরা, জাহান্নামের আগুনে থাকবে স্থায়ীভাবে। ওরাই হল নিকৃষ্ট সৃষ্টি।');
INSERT INTO "rawai" VALUES(98,7,'98:7','নিশ্চয় যারা ঈমান এনেছে ও সৎকাজ করেছে তারাই সৃষ্টির সর্বোৎকৃষ্ট।');
