INSERT INTO "sadiq_sani" VALUES(1,1,'1:1','በአላህ ስም እጅግ በጣም ሩኅሩህ በጣም አዛኝ በኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(1,2,'1:2','ምስጋና ለአላህ ይገባው የዓለማት ጌታ ለኾነው፤');
INSERT INTO "sadiq_sani" VALUES(1,3,'1:3','እጅግ በጣም ርኅሩህ በጣም አዛኝ');
INSERT INTO "sadiq_sani" VALUES(1,4,'1:4','የፍርዱ ቀን ባለቤት ለኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(1,5,'1:5','አንተን ብቻ እንግገዛለን፤ አንተንም ብቻ እርዳታን እንለምናለን፡፡');
INSERT INTO "sadiq_sani" VALUES(1,6,'1:6','ቀጥተኛውን መንገድ ምራን፡፡');
INSERT INTO "sadiq_sani" VALUES(1,7,'1:7','የእነዚያን በነርሱ ላይ በጎ የዋልክላቸውን በነሱ ላይ ያልተቆጣህባቸውንና ያልተሳሳቱትንም ሰዎች መንገድ (ምራን፤ በሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,1,'2:1','አ.ለ.መ');
INSERT INTO "sadiq_sani" VALUES(2,2,'2:2','ይህ መጽሐፍ (ከአላህ ለመኾኑ) ጥርጥር የለበትም፤ ለፈራህያን መሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,3,'2:3','ለነዚያ በሩቁ ነገር የሚያምኑ ሶላትንም ደንቡን ጠብቀው የሚሰግዱ ከሰጠናቸውም ሲሳይ የሚቸሩ ለኾኑት፤');
INSERT INTO "sadiq_sani" VALUES(2,4,'2:4','ለእነዚያም ወደ አንተ በተወረደውና ከአንተ በፊትም በተወረደው የሚያምኑ በመጨረሻይቱም (ዓለም) እነርሱ የሚያረጋግጡ ለኾኑት (መሪ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,5,'2:5','እነዚያ ከጌታቸው በመመራት ላይ ናቸው፤ እነዚያም እነሱ ፍላጎታቸውን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,6,'2:6','እነዚያ የካዱት (ሰዎች) ብታስፈራራቸውም ባታስፈራራቸውም በነርሱ ላይ እኩል ነው፤ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,7,'2:7','አላህ በልቦቻቸው ላይ በመስሚያቸውም ላይ አትሞባቸዋል፤ በዓይኖቻቸውም ላይ መሸፈኛ አልለ፤ ለነሱም ታላቅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,8,'2:8','ከሰዎችም «በአላህና በመጨረሻው ቀን አምነናል» የሚሉ አልሉ፤ እነርሱም አማኞች አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,9,'2:9','አላህንና እነዚያን ያመኑትን (ሰዎች) ያታልላሉ፤ የማያውቁ ሲኾኑ ነፍሶቻቸውን እንጅ ሌላን አያታልሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,10,'2:10','በልቦቻቸው ውስጥ (የንፍቅና) በሽታ አለባቸው፡፡ አላህም በሽታን ጨመረባቸው፡፡ ለነርሱም ይዋሹ በነበሩት ምክንያት አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,11,'2:11','ለነርሱም «በምድር ላይ አታበላሹ» በተባሉ ጊዜ «እኛ አሳማሪዎች ብቻ ነን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,12,'2:12','ንቁ እነርሱ አጥፊዎቹ እነሱው ናቸው፤ ግን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,13,'2:13','ለነሱም «ሰዎቹ እንዳመኑ እመኑ» በተባሉ ጊዜ «ቂሎቹ እንዳመኑ እናምናለን?» ይላሉ፡፡ አዋጅ ንቁ እነርሱ ቂሎቹ እነርሱው ናቸው፤ ግን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,14,'2:14','እነዚያንም ያመኑትን በተገናኙ ጊዜ «አምነናል» ይላሉ፡፡ ወደ ሰይጣኖቻቸውም ባገለሉ ጊዜ «እኛ ከናንተ ጋር ነን፤ እኛ (በነሱ) ተሳላቂዎች ብቻ ነን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,15,'2:15','አላህ በነሱ ይሳለቅባቸዋል፤ በጥመታቸውም ውስጥ የሚዋልሉ ሲኾኑ ያዘገያቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,16,'2:16','እነዚህ እነዚያ ስህተትን በቅንነት የገዙ ናቸው፡፡ ንግዳቸውም አላተረፈችም፤ ቀጥተኞችም አልኾኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,17,'2:17','(በንፍቅና) ምሳሌያቸው እንደዚያ እሳትን እንዳነደደ ሰው በዙሪያው ያለውን ሁሉ ባበራች ጊዜም አላህ ብርሃናቸውን እንደወሰደባቸው በጨለማዎችም ውስጥ የማያዩ ኾነው እንደተዋቸው (ሰዎች) ብጤ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,18,'2:18','(እነሱ) ደንቆሮዎች፣ ዲዳዎች፣ ዕውሮች ናቸው፤ ስለዚህ እነርሱ አይመለሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,19,'2:19','ወይም (ምሳሌያቸው) ከሰማይ እንደ ወረደ ዝናም (ባለቤቶች) ነው፤ በርሱ (በደመናው) ውስጥ ጨለማዎች፣ ነጎድጓድም፣ ብልጭታም ያሉበት ሲኾን ከመብረቆቹ ሞትን ለመፍራት ጣቶቻቸውን በጆሮቻቸው ውስጥ እንደሚያደርጉ ብጤ ነው፡፡ አላህም ከሓዲዎችን ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,20,'2:20','ብልጭታው ዓይኖቻቸውን ሊነጥቅ ይቀርባል፡፡ ለነርሱ ባበራላቸው ቁጥር በርሱ ውስጥ ይኼዳሉ፡፡ በነሱም ላይ ባጨለመ ጊዜ ይቆማሉ፡፡ አላህም በሻ ኖሮ መስሚያቸውንና ማያዎቻቸውን በወሰደ ነበር፤ አላህ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,21,'2:21','እናንተ ሰዎች ሆይ! የፈጠራችሁን እነዚያንም ከናንተ በፊት የነበሩትን (የፈጠረውን) ጌታችሁን ተገዙ፤ (ቅጣትን) ልትጠነቀቁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,22,'2:22','(እርሱ) ያ ለናንተ ምድርን ምንጣፍ ሰማይንም ጣራ ያደረገ ነው፤ ከሰማይም (ከደመና) ውሃን ያወረደ በርሱም ከፍሬዎች ለናንተ ሲሳይን ያወጣ ነው፡፡ እናንተም (ፈጣሪነቱን) የምታውቁ ስትኾኑ ለአላህ ባላንጣዎችን አታድርጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,23,'2:23','በባሪያችንም ላይ ካወረድነው በመጠራጠር ውስጥ ብትኾኑ ከብጤው አንዲትን ምዕራፍ አምጡ፡፡ እውነተኞችም እንደኾናችሁ ከአላህ ሌላ መስካሪዎቻችሁን ጥሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,24,'2:24','(ይህንን) ባትሠሩ ፈጽሞም አትሠሩትምና ያችን መቀጣጠያዋ ሰዎችና ድንጋዮች የኾነችውን እሳት ተጠበቁ፤ ለከሓዲዎች ተደግሳለች፡፡');
INSERT INTO "sadiq_sani" VALUES(2,25,'2:25','እነዚያን ያመኑትንና መልካሞችንም የሠሩትን ለነርሱ ከሥሮቻቸው ወንዞች የሚፈሱባቸው ገነቶች ያሏቸው መኾኑን አብስራቸው፡፡ ከርሷ ከፍሬ (ዓይነት) ሲሳይን በተመገቡ ቁጥር (ፍሬዎችዋ ስለሚመሳሰሉ) «ይህ ያ ከአሁን በፊት የተገመብነው ነው» ይላሉ፡፡ እርሱንም ተመሳሳይ ኾኖ ተሰጡት፡፡ ለነሱም በውስጧ ንጹህ የተደረጉ ሚስቶች አሏቸው፡፡ እነሱም በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,26,'2:26','አላህ ማንኛውንም ነገር ትንኝንም ኾነ (በትንሽነት ወይም በትልቅነት) ከበላይዋ የኾነንም ምሳሌ ለማድረግ አያፍርም፡፡ እነዚያ ያመኑትማ፤ እርሱ (ምሳሌው) ከጌታቸው (የተገኘ) እውነት መኾኑን ያውቃሉ፡፡ እነዚያም የካዱትማ «አላህ በዚህ ምንን ምሳሌ ሻ» ይላሉ፡፡ በርሱ (በምሳሌው) ብዙዎችን ያሳስታል በርሱም ብዙዎችን ያቀናል፡፡ በርሱም አመፀኞችን እንጅ ሌላን አያሳስትም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,27,'2:27','እነዚያ የአላህን ቃል ኪዳን ከአጠበቀባቸው በኋላ የሚያፈርሱ እንዲቀጠልም በርሱ ያዘዘውን ነገር የሚቆርጡ በምድርም ላይ የሚያበላሹ እነዚያ እነርሱ ከሳሪዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,28,'2:28','ሙታን የነበራችሁ ስትኾኑ ሕያው ያደረጋችሁ ከዚያም የሚገድላችሁ ከዚያም ሕያው የሚያደርጋችሁ ሲኾን ከዚያም ወደርሱ የምትመለሱ ስትኾኑ በአላህ እንዴት ትክዳለችሁ!');
INSERT INTO "sadiq_sani" VALUES(2,29,'2:29','እርሱ ያ በምድር ያለውን ሁሉ ለእናንተ የፈጠረ ነው፡፡ ከዚያም ወደ ሰማይ አሰበ፤ ሰባት ሰማያትም አደረጋቸው፡፡ እርሱም በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,30,'2:30','(ሙሐመድ ሆይ) ጌታህ ለመላእክት፡- «እኔ በምድር ላይ ምትክን አድራጊ ነኝ፤» ባለ ጊዜ (የኾነውን አስታውስ፤ እነርሱም) «እኛ ከማመስገን ጋር የምናጠራህ ላንተም የምንቀድስ ስንኾን በርሷ ውስጥ የሚያጠፋንና ደሞችንም የሚያፈስን ታደርጋለህን?» አሉ፡፡ (አላህ) «እኔ የማታውቁትን ነገር አውቃለሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,31,'2:31','አደምንም ስሞችን ሁሏንም አስተማረው፡፡ ከዚያም በመላእክት ላይ (ተጠሪዎቹን) አቀረባቸው፡፡ «እውነተኞችም እንደኾናችሁ የነዚህን (ተጠሪዎች) ስሞች ንገሩኝ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,32,'2:32','«ጥራት ይገባህ፤ ከአስተማርከን ነገር በስተቀር ለኛ ዕውቀት የለንም፡፡ አንተ ዐዋቂው ጥበበኛው አንተ ብቻ ነህና» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,33,'2:33','፡-«አደም ሆይ ስሞቻቸውን ንገራቸው» አለው፡፡ ስሞቻቸውን በነገራቸውም ጊዜ «እኔ የሰማያትንና የምድርን ሩቅ ምስጢር ዐውቃለሁ፤ የምትገልጹትንና ያንንም ትደብቁት የነበራችሁትን ዐውቃለሁ አላልኳችሁምን?» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,34,'2:34','ለመላእክትም «ለአደም ስገዱ» ባልን ጊዜ (አስታውስ) ፡፡ ሁሉም ወዲያውኑ ሰገዱ፤ ኢብሊስ (ዲያብሎስ) ብቻ ሲቀር እምቢ አለ፤ ኮራም ከከሓዲዎቹም ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,35,'2:35','«አደም ሆይ! አንተ ከነሚስትህ በገነት ተቀመጥ፤ ከርሷም በፈለጋችሁት ስፍራ በሰፊው ተመገቡ፤ ግን ይህችን ዛፍ አትቅረቡ፤ ከበደለኞች ትኾናላችሁና» አልንም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,36,'2:36','ከርሷም ሰይጣን አዳለጣቸው በውስጡም ከነበሩበት (ድሎት) አወጣቸው፡፡ «ከፊላችሁም ለከፊሉ ጠላት ሲኾን ውረዱ፤ ለናንተም በምድር ላይ እስከ ጊዜ (ሞታችሁ) ድረስ መርጊያና መጣቀሚያ አላችሁ» አልናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,37,'2:37','አደምም ከጌታው ቃላትን ተቀበለ፡፡ በርሱ ላይም (ጌታው ጸጸትን በመቀበል) ተመለሰለት፤ እነሆ እርሱ ጸጸትን ተቀባይ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,38,'2:38','«ሁላችሁም ኾናችሁ ከርሷ ውረዱ፡፡ ከኔም የኾነ መመሪያ ቢመጣላችሁ ወዲያውኑ መመሪያዬን የተከተለ ሰው በነሱ ላይ ፍርሃት የለባቸውም፤ እነሱም አያዝኑም» አልናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,39,'2:39','እነዚያም (በመልክተኞቻችን) የካዱ በአንቀጾቻችንም ያስተባበሉ እነዚያ የእሳት ጓዶች ናቸው፡፡ እነርሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,40,'2:40','የእስራኤል ልጆች ሆይ! ያችን በናንተ ላይ የለገስኳትን ጸጋዬን አስታውሱ፡፡ በቃል ኪዳኔም ሙሉ፤ በቃል ኪዳናችሁ እሞላለሁና፤ እኔንም ብቻ ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,41,'2:41','ከናንተ ጋር ያለውን (መጽሐፍ) የሚያረጋግጥ ሆኖ ባወረድኩትም (ቁርኣን) እመኑ፡፡ በርሱም የመጀመሪያ ከሓዲ አትሁኑ፡፡ በአንቀጾቼም ጥቂትን ዋጋ አትለውጡ፡፡ እኔንም ብቻ ተጠንቀቁ፡');
INSERT INTO "sadiq_sani" VALUES(2,42,'2:42','እውነቱንም በውሸት አትቀላቅሉ፡፡ እናንተም የምታውቁ ስትሆኑ እውነትን አትደብቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,43,'2:43','ሶላትንም ደንቡን ጠብቃችሁ ስገዱ፡፡ ዘካንም (ግዴታ ምጽዋትን) ስጡ፡፡ (ለጌታችሁ) ከአጎንባሾች ጋርም አጎንብሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,44,'2:44','እናንተ መጽሐፉን የምታነቡ ሆናችሁ ሰዎችን በበጎ ሥራ ታዛላችሁን? ነፍሶቻችሁንም ትረሳላችሁን? (የሥራችሁን መጥፎነት) አታውቁምን?');
INSERT INTO "sadiq_sani" VALUES(2,45,'2:45','በመታገስና በሶላትም ተረዱ፡፡ እርሷም (ሶላት) በፈሪዎች ላይ እንጅ በሌላው ላይ በእርግጥ ከባድ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(2,46,'2:46','እነዚያ እነርሱ ጌታቸውን የሚገናኙ እነሱም ወደእርሱ ተመላሾች መኾናቸውን የሚያረጋግጡ በኾኑት (ላይ እንጅ ከባድ ናት)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,47,'2:47','የእስራኤል ልጆች ሆይ! ያችን በናንተ ላይ የለገስኳትን ጸጋዬንና እኔም በዓለማት ላይ ያበለጥኳችሁ (አባቶቻችሁን በጊዜያቸው ከነበሩት ዓለማት ያበለጥኩዋቸው) መኾኔን አስታውሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,48,'2:48','(ማንኛዋ) ነፍስም ከ(ሌላዋ) ነፍስ ምንንም የማትመነዳበትን፣ ከርሷም ምልጃን የማይቀበሉባትን፣ ከርሷም ቤዛ የማይያዝበትን፣ እነርሱም የማይረዱበትን ቀን ተጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,49,'2:49','ከፈርዖንም ቤተሰቦች (ከጎሶቹና ከሰራዊቱ) ብርቱን ቅጣት የሚያቀምሱዋችሁ ወንዶች ልጆቻችሁን የሚያርዱ ሴቶቻችሁንም የሚተው ሲኾኑ ባዳንናችሁ ጊዜ (የኾነውን አስታውሱ)፡፡ በዚሃችሁም ከጌታችሁ ታላቅ ፈተና አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(2,50,'2:50','በናንተም ምክንያት ባሕሩን በከፈልን ጊዜ (አስታውሱ)፡፡ ወዲያውም አዳንናችሁ፡፡ የፈርዖንንም ቤተሰቦች እናንተ የምትመለከቱ ኾናችሁ አሰጠምናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,51,'2:51','ሙሳንም አርባን ሌሊት በቀጠርነው ጊዜ (የኾነውን አስታውሱ)፡፡ ከዚያም ከርሱ (መኼድ) በኋላ እናንተ በዳዮች ስትኾኑ ወይፈንን (አምላክ አድርጋችሁ) ያዛችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,52,'2:52','ከዚያም ከዚህ በኋላ እናንተ ታመሰግኑ ዘንድ ከናንተ ምሕረት አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(2,53,'2:53','ሙሳንም መጽሐፍንና (እውነትና ውሸትን) መለያንም ትመሩ ዘንድ በሰጠነው ጊዜ (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,54,'2:54','ሙሳም ለሕዝቦቹ፡- «ሕዝቦቼ ሆይ! እናንተ ወይፈንን (አምላክ አድርጋችሁ) በመያዛችሁ ነፍሶቻችሁን በደላችሁ፡፡ ወደ ፈጣሪያችሁም ተመለሱ፤ ነፍሶቻችሁንም ግደሉ፤ ይሃችሁ በፈጣሪያችሁ ዘንድ ለናንተ በላጭ ነው» ባለ ጊዜ (አስታውስ)፡፡ በእናንተም ላይ ጸጸትን በመቀበል ተመለሰላችሁ፡፡ እነሆ እርሱ ጸጸትን ተቀባይ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,55,'2:55','«ሙሳ ሆይ! አላህን በግልጽ እስከምናይ ድረስ ላንተ በፍጹም አናምንልህም» በላችሁም ጊዜ (አስታውሱ)፡፡ እናንተም እየተመለከታችሁ መብረቅ ያዘቻችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,56,'2:56','ከዚያም ታመሰግኑ ዘንድ ከሞታችሁ በኋላ አስነሳናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,57,'2:57','በናንተም ላይ ደመናን አጠለልን፡፡ በናተም ላይ (እንደ ነጭ ማር ያለ) መንናን እና ድርጭትን አወረድን፡፡ ከሰጠናችሁም ጣፋጮች «ብሉ (አልን)፡፡» አልበደሉንምም ግን ነፍሶቻቸውን ይበድሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(2,58,'2:58','«ይህችንም ከተማ ግቡ፡፡ ከርሷም በሻችሁት ስፍራ ሰፊን (ምግብ) ተመገቡ፡፡ በሩንም ያጎነበሳችሁ ኾናችሁ ግቡ፤ (ጥያቄያችን የኃጢአታችን መርገፍ ነው) በሉም፡፡ ኃጢአቶቻችሁን ለናንተ እንምራለንና፡፡ በጎ ሠሪዎችንም (ምንዳን) እንጨምርላቸዋለን» ባልን ጊዜ (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,59,'2:59','እነዚያም የበደሉት ሰዎች ከዚያ ለነሱ ከተባሉት ሌላ ቃልን ለወጡ፡፡ በነዚያም በበደሉት ላይ ያመጹ በመሆናቸው ምክንያት መቅሠፍትን አወረድንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,60,'2:60','ሙሳም ለሕዝቦቹ መጠጥን በፈለገ ጊዜ (የኾነውን አስታውሱ)፡፡ «ድንጋዩንም በበትርህ ምታ» አልነው፡፡ (መታውም) ከርሱም ዐሥራሁለት ምንጮች ፈለቁ፡፡ ሰዎቹ ሁሉ መጠጫቸውን በእርግጥ ዐወቁ፡፡ «ከአላህ ሲሳይ ብሉ፤ ጠጡም፤ አመጠኞችም ኾናችሁ በምድር ላይ አታበላሹ» (አልናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,61,'2:61','ሙሳ ሆይ፡- «በአንድ (ዓይነት) ምግብ ላይ በፍጹም አንታገሥም፤ ስለዚህ ጌታህን ከዚያ ምድር ከምታበቅለው ሁሉ ከቅጠላቅጠል ከዱባዋም ከስንዴዋም ከምስርዋም ከሽንኩርቷም ለኛ ያወጣ ዘንድ ለኛ ለምንልን» ባላችሁም ጊዜ (የኾነውን አስታውሱ፡፡ ሙሳም፡- «ያንን እርሱ ዝቅተኛ የኾነውን በዚያ እርሱ በላጭ በኾነው ነገር ለውጥን ትፈልጋላችሁን? ወደ ከተማ ውረዱ፤ ለናንተም የጠየቃችሁት ነገር አላችሁ» አላቸው፡፡ በነርሱም ላይ ውርደትና ድኽነት ተመታባቸው፡፡ ከአላህም በኾነ ቁጣ ተመለሱ፡፡ ይህ እነርሱ በአላህ ታምራቶች ይክዱና ነቢያትንም ያለ ሕግ ይገድሉ ስለነበሩ ነው፡፡ ይህ በማመጻቸውና ወሰን የሚያልፉ በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,62,'2:62','እነዚያ ያመኑ እነዚያም ይሁዳውያን የኾኑ፣ ክርስቲያኖችም፣ ሳቢያኖችም (ከእነርሱ) በአላህና በመጨረሻው ቀን ያመነ መልካምንም ሥራ የሠራ ለነርሱ በጌታቸው ዘንድ ምንዳቸው አላቸው፡፡ በነርሱም ላይ ፍርሃት የለባቸውም፤ እነሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,63,'2:63','ከበላያችሁም የጡርን ተራራ ያነሳን ኾነን የጠበቀ ቃልኪዳናችሁን በያዝን ጊዜ (የኾነውን አስታውሱ)፡፡ «ያንን የሰጠናችሁን በኀይል ያዙ፤ በውስጡ ያለውንም ነገር (ከእሳት) ትጠበቁ ዘንድ አስታውሱ» (አልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,64,'2:64','ከዚያም ከዚህ በኋላ (ኪዳኑን) ተዋችሁ፡፡ በናንተም ላይ የአላህ ችሮታና እዝነቱ ባልነበረ ኖሮ በእርግጥ ከጠፊዎቹ በኾናችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(2,65,'2:65','እነዚያንም ከእናንተ ውስጥ በቅዳሜ ቀን (ዐሣን በማጥመድ) ወሰን ያለፉትንና ለነሱ «ወራዳዎች ስትኾኑ ዝንጀሮዎች ኹኑ» ያልናቸውን በእርግጥ ዐወቃችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,66,'2:66','(ቅጣቲቱንም) ለነዚያ በስተፊትዋ ለነበሩትና ለነዚያም ከበኋላዋ ላሉት (ሕዝቦች) መቀጣጫ ለፈራህያንም መገሰጫ አደረግናት፡፡');
INSERT INTO "sadiq_sani" VALUES(2,67,'2:67','ሙሳም ለሕዝቦቹ፡- «አላህ ላምን እንድታርዱ ያዛችኋል» ባለ ጊዜ (አስታወሱ)፡፡«መሳለቂያ አድርገህ ትይዘናለህን?» አሉት፡፡ «ከተሳላቂዎች ከመኾን በአላህ እጠበቃለሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,68,'2:68','«ለኛ ጌታህን ጠይቅልን፤ እርሷ ምን እንደኾነች (ዕድሜዋን) ያብራራልን» አሉ፡፡ «እርሱ እርሷ ያላረጀች ጥጃም ያልኾነች በዚህ መካከል ልከኛ የኾነች ጊደር ናት ይላችኋል፤ የታዘዛችሁትንም ሥሩ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,69,'2:69','«ለኛ ጌታህን ጠይቅልን፤ መልኳ ምን እንደኾነ ለኛ ይግለጽልን» አሉ፡፡ «እርሱ እርሷ መልኳ ደማቅ ተመልካቾችን የምታስደስት ዳለቻ ላም ናት ይላችኋል» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,70,'2:70','«ለኛ ጌታህን ጠይቅልን፤ እርሷ ምን እንደኾነች ይግለጽልን፤ ከብቶች በኛ ላይ ተመሳሰሉብን፡፡ እኛም አላህ የሻ እንደኾነ በእርግጥ ተመሪዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,71,'2:71','«እርሱ እርሷ ያልተገራች ምድርን (በማረስ) የማታስነሳ እርሻንም የማታጠጣ (ከነውር) የተጠበቀች ልዩ ምልክት የሌለባት ናት ይላችኋል» አላቸው፡፡ «አሁን በትክክል መጣህ» አሉ፤ ሊሠሩ ያልተቃረቡም ሲኾኑ አረዷት ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,72,'2:72','ነፍስነም በገደላችሁና በርሷም (ገዳይ) በተከራከራችሁ ጊዜ (አስታውሱ)፡፡ አላህም ትደብቁት የነበራችሁትን ሁሉ ገላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,73,'2:73','«(በድኑን) በከፊሏም ምቱት» አልን፤ እንደዚሁ አላህ ሙታንን ያስነሳል፤ ታውቁም ዘንድ ታምራቶችን ያሳያችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,74,'2:74','ከዚያም ከዚህ በኋላ ልቦቻችሁ ደረቁ፤ እርሷም እንደ ድንጋዮች ወይም በድርቅና ይበልጥ የበረታች ናት፡፡ ከድንጋዮችም ከርሱ ጂረቶች የሚፈሱለት አልለ፡፡ ከነርሱም በእርግጥ የሚሰነጠቅና ከርሱ ውሃ (ምንጭ) የሚወጣው አልለ፡፡ ከነርሱም አላህን ከመፍራት የተነሳ ወደ ታች የሚወርድ አልለ፡፡ አላህም ከምትሠሩት ነገር ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,75,'2:75','(አይሁዶች) ከነሱ የኾኑ ጭፍሮች የአላህን ቃል የሚሰሙና ከዚያም ከተረዱት በኋላ እነርሱ እያወቁ የሚለውጡት ሲኾኑ ለናንተ ማመናቸውን ትከጅላላችሁን?');
INSERT INTO "sadiq_sani" VALUES(2,76,'2:76','እነዚያንም ያመኑትን ባገኙ ጊዜ «አምነናል» ይላሉ፡፡ ከፊላቸውም ወደ ከፊሉ ባገለለ «ጊዜ አላህ በናንተ ላይ በገለጸላቸሁ ነገር እጌታችሁ ዘንድ በርሱ እንዲከራከሩዋችሁ ትነግሩዋቸዋላችሁን? አታውቁምን?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,77,'2:77','አላህ የሚደብቁትንና የሚገልጹትን የሚያውቅ መኾኑን አያውቁምን?');
INSERT INTO "sadiq_sani" VALUES(2,78,'2:78','ከነሱም መጽሐፉን የማያውቁ መሃይማናን አሉ፡፡ ግን ከንቱ ምኞቶችን (ይመኛሉ)፤ እነርሱም የሚጠራጠሩ እንጂ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,79,'2:79','ለነዚያም መጽሐፉን በእጆቻቸው ለሚጽፉና ከዚያም በርሱ ጥቂትን ዋጋ ሊገዙበት «ይህ ከአላህ ዘንድ ነው» ለሚሉ ወዮላቸው፡፡ ለነርሱም ከዚያ እጆቻቸው ከጻፉት ወዮላቸው፡፡ ለነሱም ከዚያ ከሚያፈሩት (ኃጢኣት) ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,80,'2:80','«እሳትም የተቆጠሩን ቀኖች እንጂ አትነካንም» አሉ፡፡ «አላህ ዘንድ ቃል ኪዳን ይዛችኋልን? (ይህ ከኾነ) አላህም ኪዳኑን አያፈርስም፤ በእውነቱ በአላህ ላይ የማታውቁትን ትናገራላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,81,'2:81','አይደለም (ትነካችኋለች)፤ መጥፎን (ክሕደትን) የሠራ በርሱም ኃጠኣቱ የከበበችው ሰው እነዚያ የእሳት ጓዶች ናቸው፡፡ እነርሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,82,'2:82','እነዚያም ያመኑት በጎ ሥራዎችንም የሠሩት እነዚያ የገነት ጓዶች ናቸው፡፡ እነርሱ በውስጧ ዘላለም ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,83,'2:83','የእስራኤል ልጆችንም ጥብቅ ኪዳን አላህን እንጂ ሌላን አታምልኩ፤ በወላጆችም በጎን ሥራ (አድርጉ)፤ በዝምድና ባለቤቶችም፣ በየቲሞችም (አባት የሌላቸው ልጆች) በምስኪኖችም (በጎ ዋሉ)፤ ለሰዎችም መልካምን ተናገሩ፤ ሶላትንም ደንቡን ጠብቃችሁ ስገዱ፤ ዘካንም ስጡ በማለት በያዝንባቸው ጊዜ (አስታውሱ)፡፡ ከዚያም ከናንተ ጥቂቶች ሲቀሩ ሸሻችሁ እናንተም (ኪዳንን) የምትተዉ ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,84,'2:84','ደሞቻችሁን አታፍስሱ ነፍሶቻችሁንም (ከፊላችሁን) ከአገሮቻችሁ አታውጡ የምንል ስንኾን የጠበቀ ቃል ኪዳናችሁን በያዝንባችሁ ጊዜ (አስታውሱ)፡፡ ከዚያም (በኪዳኑ) አረጋገጣችሁ፤ እናንተም ትመሰክራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,85,'2:85','ከዚያም እናንተ እነዚያ ነፍሶቻችሁን የምትገድሉ ከናንተም የኾኑ ጭፍሮችን በኃጢአትና በመበደል በነርሱ ላይ የምትረዳዱ ስትኾኑ ከአገሮቻቸው የምታወጡ ምርኮኞችም ኾነው ቢመጡዋችሁ የምትበዡ ናችሁ፡፡ እርሱ (ነገሩ) እነርሱን ማውጣት በእናንተ ላይ የተከለከለ ነው፡፡ በመጽሐፉ ከፊል ታምናላችሁን? በከፊሉም ትክዳላችሁን? ከእናንተም ይህንን የሚሠራ ሰው ቅጣት በቅርቢቱ ሕይወት ውርደት እንጂ ሌላ አይደለም፡፡ በትንሣኤ ቀንም ወደ ብርቱ ቅጣት ይመለሳሉ፡፡ አላህም ከምትሠሩት ሥራ ሁሉ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,86,'2:86','እነዚህ እነዚያ ቅርቢቱን ሕይወት በመጨረሻይቱ አገር የገዙ ናቸው፡፡ ከነሱም ቅጣቱ አይቀልላቸውም፤ እነሱም አይርረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,87,'2:87','ሙሳንም መጽሐፍን በእርግጥ ሰጠነው፡፡ ከበኋላውም መልክትኞችን አስከታተልን፡፡ የመርየምን ልጅ ዒሳንም ግልጽ ታምራቶችን ሰጠነው፡፡ በቅዱሱ መንፈስም አበረታነው፡፡ ነፍሶቻችሁ በማትወደው ነገር መልክተኛ በመጣላችሁ ቁጥር (ከመከተል) ትኮራላችሁን? ከፊሉን አስተባበላችሁ፤ ከፊሉንም ትገድላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,88,'2:88','«ልቦቻችንም ሽፍኖች ናቸው» አሉ፤ አይደለም አላህ በክሕደታቸው ምክንያት ረገማቸው ጥቂትንም ብቻ ያምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,89,'2:89','ከነሱም ጋር ያለውን (መጽሐፍ) አረጋጋጭ የኾነ መጽሐፍ ከአላህ ዘንድ በመጣላቸው ጊዜ (ከመምጣቱ) በፊት በነዚያ በካዱት ላይ ይረዱበት የነበሩ ሲኾኑ ያወቁት ነገር በመጣላቸው ጊዜ በርሱ ካዱ፡፡ የአላህም ርግማን በከሓዲዎች ላይ ይኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(2,90,'2:90','ነፍሶቻቸውን በርሱ የሸጡበት ነገር ከፋ! (እርሱም) አላህ ከባሮቹ በሚሻው ሰው ላይ ከችሮታው (ራእይን) ማውረዱን በመመቅኘት አላህ ባወረደው ነገር መካዳቸው ነው፡፡ በቁጣ ላይም ቁጣ (የተረጋገጠባቸው ሲኾኑ) ተመለሱ፡፡ ለከሓዲዎችም አዋራጅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,91,'2:91','አላህም ባወረደው (ሁሉ) ለእነርሱ «እመኑ» በተባሉ ጊዜ «በኛ ላይ በተወረደው (መጽሐፍ ብቻ) እናምናለን» ይላሉ፡፡ ከርሱ ኋላ ባለው (ቁርአን) እርሱ ከነሱ ጋር ላለው (መጽሐፍ) አረጋጋጭ እውተኛ ሲኾን ይክዳሉ፡፡ «አማኞች ከኾናችሁ ከአሁን በፊት የአላህን ነቢያት ለምን ገደላችሁ?» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,92,'2:92','ሙሳም በታምራቶች በእርግጥ መጣላችሁ፡፡ ከዚያም ከበኋላው እናንተ በዳዮች ስትኾኑ ወይፈንን (አምላክ አድርጋችሁ) ያዛችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,93,'2:93','የጡርንም ጋራ ከበላያችሁ ያነሳን ስንኾን (በኦሪት ሕግ እንድትሠሩ) ኪዳናችሁን በያዝን ጊዜ (አስታውሱ)፡፡ «የሰጠናችሁን በኀይል ያዙ፤ ስሙም፤» (አልን)፡፡«ሰማን አመጽንም» አሉ፡፡ የወይፈኑንም ውዴታ በክሕደታቸው ምክንያት በልቦቻቸው ውስጥ ተጠጡ፡፡ «አማኞች እንደኾናችሁ እምነታችሁ በርሱ የሚያዛችሁ ነገር ከፋ!» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,94,'2:94','«የመጨረሻይቱ አገር (ገነት) አላህ ዘንድ ከሰው ሁሉ ሌላ የተለየች ስትኾን ለእናንተ ብቻ እንደኾነች እውነተኞች ከኾናችሁ ሞትን ተመኙ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,95,'2:95','እጆቻቸውም ባሳለፉት (በሠሩት) ምክንያት ምን ጊዜም ፈጽሞ አይመኙትም፤ አላህም በዳዮችን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,96,'2:96','ከሰዎችም ሁሉ ከእነዚያም (ጣዖትን) ከአጋሩት ይበልጥ በሕይወት ላይ የሚጓጉ ኾነው በእርግጥ ታገኛቸዋለህ፡፡ አንዳቸው ሺሕ ዓመት ዕድሜ ቢሰጥ ይወዳል፡፡ እርሱም ዕድሜ መሰጠቱ ከቅጣት የሚያርቀው አይደለም፡፡ አላህም የሚሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,97,'2:97','ለጂብሪል (ለገብርኤል) ጠላት የኾነ ሰው (በቁጭት ይሙት) በላቸው፡፡ እርሱ (ቁርኣኑን) ከበፊቱ ለነበሩት (መጻሕፍት) አረጋጋጭ ለምእመናን መሪና ብስራት ሲኾን በአላህ ፈቃድ በልብህ ላይ አውርዶታልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,98,'2:98','ለአላህና ለመላእክቱ ለመልክተኞቹም ለጂብሪልም ለሚካልም (ሚካኤል) ጠላት የኾነ ሰው አላህ ለ(እነዚህ) ከሓዲዎች ጠላት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,99,'2:99','ወዳንተም ግልጽ የኾኑትን አንቀጾች በእርግጥ አውርደናል፡፡ በርሷም አመጸኞች እንጂ ሌላው አይክድም፤');
INSERT INTO "sadiq_sani" VALUES(2,100,'2:100','ቃል ኪዳንንም ቃል በገቡ ቁጥር ከእነርሱ ከፊሉ ይጥለዋልን? (ያፈርሰዋልን?)፤ ይልቁንም አብዛኞቻቸው አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,101,'2:101','እነርሱ ጋርም ላለው (መጽሐፍ) አረጋጋጭ የኾነ መልክተኛ ከአላህ ዘንድ በመጣላቸው ጊዜ ከነዚያ መጽሐፍን ከተሰጡት ከፊሉ እነርሱ እንደማያውቁ ኾነው የአላህን መጽሐፍ ከጀርባዎቻቸው ኋላ ጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,102,'2:102','ሰይጣናትም በሱለይማን (ሰሎሞን) ዘመነ መንግስት የሚያነቡትን (ድግምት) ተከተሉ፡፡ ሱለይማንም አልካደም፤ (ድግምተኛ አልነበረም)፤ ግን ሰይጣናት ሰዎችን ድግምትን የሚያስተምሩ ሲኾኑ ካዱ፡፡ ያንንም በባቢል በሁለቱ መላእክት በሃሩትና ማሩት ላይ የተወረደውን ነገር (ያስተምሩዋቸዋል)፡፡ «እኛ መፈተኛ ነንና አትካድ» እስከሚሉም ድረስ አንድንም አያስተምሩም፡፡ ከእነሱም በሰውየውና በሚስቱ መካከል በርሱ የሚለዩበትን ነገር ይማራሉ፡፡ እነርሱም በአላህ ፈቃድ ካልኾነ በርሱ አንድንም ጎጂዎች አይደሉም፡፡ የሚጎዳቸውንና የማይጠቅማቸውንም ይማራሉ፡፡ የገዛውም ሰው ለርሱ በመጨረሻይቱ አገር ምንም እድል የሌለው መኾኑን በእርግጥ ዐወቁ፡፡ ነፍሶቻቸውንም በርሱ የሸጡበት ዋጋ ከፋ! የሚያውቁ በኾኑ ኖሮ (ባልሠሩት ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,103,'2:103','እነርሱም (አይሁዶች) ባመኑና በተጠነቀቁ ኖሮ (በተመነዱ ነበር)፡፡ የሚያውቁ ቢኾኑ ከአላህ ዘንድ የኾነው ምንዳ (ነፍሶቻቸውን ከሚሸጡበት) በላጭ ነው፡፤');
INSERT INTO "sadiq_sani" VALUES(2,104,'2:104','እናንተ ያመናችሁ ሆይ! (ለነቢዩ) ራዒና አትበሉ፡፡ ተመለከትን በሉም፡፡ ስሙም፤ ለከሓዲዎችም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,105,'2:105','እነዚያ ከመጽሐፉ ባለቤቶችና ከአጋሪዎቹም የካዱት በናንተ ላይ ከጌታችሁ የኾነ መልካም ነገር መወረዱን አይወዱም፡፡ አላህም በችሮታው (በነቢይነት) የሚሻውን ይመርጣል፡፡ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,106,'2:106','ከአንቀጽ ብንለውጥ ወይም እርሷን ብናስረሳህ ከርሷ የሚበልጥን ወይም ብጤዋን እናመጣለን፤ አላህ በነገሩ ሁሉ ላይ ከሃሊ (ቻይ) መኾኑን አታውቅምን?');
INSERT INTO "sadiq_sani" VALUES(2,107,'2:107','አላህ የሰማያትና የምድር ንግሥና ለርሱ ብቻ መኾኑን አታውቅምን? ለእናንተም ከአላህ ሌላ ዘመድና ረዳት ምንም የላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,108,'2:108','በእውነቱ ከአሁን በፊት ሙሳ እንደተጠየቀ ብጤ መልክተኛችሁን ልትጠይቁ ትፈልጋላችሁን? በእምነትም ክህደትን የሚለውጥ ሰው ትክክለኛውን መንገድ በእርግጥ ተሳሳተ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,109,'2:109','ከመጽሐፉ ባለቤቶች ብዙዎች እውነቱ ከተገለጸላቸው በኋላ ከነፍሶቻቸው በኾነው ምቀኝነት ከእምነታችሁ በኋላ ከሓዲዎች አድርገው ሊመልሱዋችሁ ተመኙ፡፡ አላህም ትዕዛዙን እስከሚያመጣ ድረስ ይቅርታ አድርጉ፤ እለፏቸውም፡፡ አላህ በነገሩ ሁሉ ላይ ከሃሊ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,110,'2:110','ሶላትንም አስተካክላችሁ ስገዱ፡፡ ዘካንም ስጡ፡፡ ለነፍሶቻችሁም ከበጎ ሥራ የምታስቀድሙትን አላህ ዘንድ ታገኙታላችሁ፡፡ አላህ የምትሠሩትን ሁሉ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,111,'2:111','«ገነትንም አይሁድን ወይም ክርስቲያኖችን የኾነ ሰው እንጂ ሌላ አይገባትም» አሉ፡፡ ይህቺ (ከንቱ) ምኞታቸው ናት፡፡ «እውነተኞች እንደኾናችሁ አስረጃችሁን አምጡ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,112,'2:112','አይደለም (ሌላውም ይገባታል) እርሱ በጎ ሠሪ ኾኖ ፊቱን ለአላህ የሰጠ ሰው ለርሱ በጌታው ዘንድ ምንዳው አለው፡፡ በነሱም ላይ ፍርሃት የለባቸውም፤ እነሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,113,'2:113','እነርሱ መጽሐፉን የሚያነቡ ሲኾኑ አይሁዶች፡- ክርስቲያኖች በምንም ላይ አይደሉም አሉ፡፡ ክርስቲያኖችም፡- አይሁዶች በምንም ላይ አይደሉም አሉ፡፡ እንደዚሁ እነዚያ የማያውቁት (አጋሪዎች) የንግግራቸውን ብጤ አሉ፡፡ አላህም በዚያ በርሱ ይለያዩበት በነበሩት ነገር በትንሣኤ ቀን በመካከላቸው ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,114,'2:114','የአላህንም መስጊዶች በውስጣቸው ስሙ እንዳይወሳ ከከለከለና እርሷንም በማበላሸት ከሮጠ ሰው ይበልጥ በዳይ ማነው? እነዚያ ፈሪዎች ኾነው እንጂ ለእነርሱ በቅርቢቱ ዓለም ውርደት አላቸው፡፡ ለነርሱም በመጨረሻይቱ አገር ታላቅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,115,'2:115','ምሥራቁም ምዕራቡም የአላህ ነው (ፊቶቻችሁን) ወደ የትም ብታዞሩ የአላህ ፊት እዚያ ነው፡፡ አላህ ችሮታው ሰፊ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,116,'2:116','አላህም ልጅ አለው አሉ፡፡ (ከሚሉት) ጥራት ተገባው፡፡ አይደለም በሰማያትና በምድር ያለው ሁሉ የርሱ ነው፡፡ ሁሉም ለርሱ ታዛዦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,117,'2:117','ሰማያትንና ምድርን ያለብጤ ፈጣሪ ነው፤ ነገርንም (ማስገኘት) በሻ ጊዜ ለርሱ የሚለው፡- «ኹን ነው፤» ወዲያውም ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,118,'2:118','እነዚያም የማያውቁት (አንተ መልክተኛ ስለመኾንህ) አላህ አያናግረንም ኖሮአልን? ወይም (ለእውነተኛነትህ) ታምር አትመጣልንም ኖሮአልን? አሉ፡፡ እንደዚሁ እነዚያ ከነሱ በፊት የነበሩት እንደ ንግግራቸው ብጤ ብለዋል፡፡ ልቦቻቸው (በክሕደት) ተመሳሰሉ፡፡ ለሚያረጋግጡ ሕዝቦች አንቀጾችን በእርግጥ አብራርተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,119,'2:119','እኛ አብሳሪና አስፈራሪ ኾነህ በውነት ላክንህ፡፡ ከእሳትም ጓዶች አትጠየቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,120,'2:120','አይሁዶችና ክርስቲያኖችም ሃይማኖታቸውን እስከምትከተል ድረስ ካንተ ፈጽሞ አይወዱም፡፡ «የአላህ መምራት (ትክክለኛው) መምራት እርሱ ብቻ ነው» በላቸው፡፡ ከዚያም እውቀቱ ከመጣልህ በኋላ ዝንባሌያቸውን ብትከተል ለአንተ ከአላህ (የሚከለክልልህ) ዘመድና ረዳት ምንም የለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,121,'2:121','እነዚያ መጽሐፉን የሰጠናቸው ተገቢ ንባቡን ያነቡታል፡፡ እነዚያ በርሱ ያምናሉ፤ በርሱም የሚክዱ እነዚያ እነርሱ ከሳሪዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,122,'2:122','የእስራኤል ልጆች ሆይ! ያችን በናንተ ላይ የለገስኳትን ጸጋዬንና እኔም በዓለማት ላይ ያበለጥኳችሁ መኾኔን አስታውሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,123,'2:123','(አማኝ) ነፍስም ከ(ከሓዲ) ነፍስ ምንንም የማትጠቅምበትን፣ ከርሷም ቤዛ የማይወሰድበትን፣ ምልጃም ለርሷ የማትጠቅምበትን፣ እነርሱም የማይረዱበትን ቀን ተጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,124,'2:124','ኢብራሂምንም ጌታው በቃላት (በሕግጋት) በፈተነውና በፈጸማቸው ጊዜ (አስታውስ)፤ «እኔ ለሰዎች መሪ አድራጊህ ነኝ» አለው፡፡ «ከዘሮቼም (አድርግ)» አለ፡፡ ቃል ኪዳኔ በዳዮቹን አያገኝም አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,125,'2:125','ቤቱንም ለሰዎች መመለሻና ጸጥተኛ ባደረግን ጊዜ (አስታውስ)፡፡ ከኢብራሂምም መቆሚያ መስገጃን አድርጉ፡፡ ወደ ኢብራሂምና ወደ ኢስማዒልም ቤቴን ለዘዋሪዎቹና ለተቀማጮቹም ለአጎንባሾች ሰጋጆቹም አጥሩ ስንል ቃል ኪዳን ያዝን፡፡');
INSERT INTO "sadiq_sani" VALUES(2,126,'2:126','ኢብራሂም ባለ ጊዜ (አስታውስ)፡፡ ጌታዬ ሆይ! ይህንን ጸጥተኛ አገር አድርግ፡፡ ቤተሰቦቹንም ከነሱ በአላህና በመጨረሻው ቀን ያመነውን ሰው ከፍሬዎች ስጠው፡፡ (አላህም) የካደውንም ሰው፤ (እሰጠዋለሁ)፡፡ ጥቂትም እጠቅመዋለሁ፤ ከዚያም ወደ እሳት ቅጣት አስጠጋዋለሁ፤ ምን ትከፋም መመለሻ! (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,127,'2:127','ኢብራሂምና ኢስማኢልም «ጌታችን ሆይ! ከእኛ ተቀበል፡፡ አንተ ሰሚውና ዐዋቂው አንተ ነህና» የሚሉ ሲኾኑ ከቤቱ መሠረቶቹን ከፍ ባደረጉ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,128,'2:128','«ጌታችን ሆይ! ላነተ ታዛዦችም አድርገን፡፡ ከዘሮቻችንም ላንተ ታዛዦች ሕዝቦችን (አድርግ)፡፡ ሕግጋታችንንም አሳየን፤ (አሳውቀን)፡፡ በኛም ላይ ተመለስን፤ አንተ ጸጸትን ተቀባዩ ሩኅሩኅ አንተ ብቻ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(2,129,'2:129','«ጌታችን ሆይ! በውስጣቸውም ከነሱው የኾነን መልክተኛ በነርሱ ላይ አንቀጾችህን የሚያነብላቸውን መጽሐፍንና ጥበብንም የሚያስተምራቸውን (ከክህደት) የሚያጠራቸውንም ላክ፤ አንተ አሸናፊው ጥበበኛው አንተ ብቻ ነህና» (የሚሉም ሲኾኑ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,130,'2:130','ከኢብራሂምም ሕግጋት ነፍሱን ያቄለ ሰው ካልኾነ በስተቀር የሚያፈገፍግ ማነው? (የለም)፤ በቅርቢቱም ዓለም በእርግጥ መረጥነው፡፡ በመጨረሻይቱም ዓለም እርሱ ከመልካሞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,131,'2:131','ጌታው ለርሱ ታዘዝ ባለው ጊዜ (መረጠው)፡፡ ለዓለማት ጌታ ታዘዝኩ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,132,'2:132','በርሷም (በሕግጋቲቱ) ኢብራሂም ልጆቹን አዘዘ፡፡ ያዕቁብም (እንደዚሁ ልጆቹን አዘዘ)፡፡ «ልጆቼ ሆይ! አላህ ለናንተ ሃይማኖትን መረጠ፤ ስለዚህ እናንተ ሙስሊሞች ኾናችሁ እንጂ አትሙቱ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,133,'2:133','ያዕቁብን ሞት በመጣበት ጊዜ ለልጆቹ ከኔ በኋላ ማንን ትገዛላችሁ ባለ ጊዜ ቀራቢዎች ነበራችሁን? (እነርሱም) አምላክህንና የአባቶችህን የኢብራሂምን የኢስማዒልንና የኢስሐቅንም አምላክ አንድ የኾነውን አምላክ እኛ ለርሱ ፍፁም ታዛዦች ኾነን እናመልካለን አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,134,'2:134','ይህች (የተወሳችው) በእርግጥ ያለፈች ሕዝብ ናት፡፡ ለርሷ የሠራችው (ምንዳ) አላት፡፡ ለናንተም የሠራችሁት (ምንዳ) አላችሁ፡፡ ይሠሩትም ከነበሩት አትጠየቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,135,'2:135','«አይሁድን ወይም ክርስቲያኖችን ኹኑ፤ (ቅኑን መንገድ) ትመራላችሁና» አሉም፡፡ «አይደለም የኢብራሂምን ሃይማኖት ቀጥተኛ ሲኾን እንከተላለን፤ ከአጋሪዎችም አልነበረም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,136,'2:136','«በአላህና ወደኛ በተወረደው (ቁርኣን) ወደ ኢብራሂምም ወደ ኢስማዒልና ወደ ኢስሐቅም ወደ ያዕቁብና ወደ ነገዶቹም በተወረደው በዚያም ሙሳና ዒሳ በተሰጡት በዚያም ነቢያት ሁሉ ከጌታቸው በተሰጡት ከነርሱ በአንድም መካከል የማንለይ ስንኾን አመንን፤ እኛም ለርሱ (ለአላህ) ታዛዦች ነን» በሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,137,'2:137','በርሱ ባመናችሁበት ብጤ ቢያምኑ በእርግጥ ተመሩ፡፡ ቢዞሩም እነርሱ በጭቅጭቅ ውስጥ ብቻ ናቸው፡፡ እነርሱንም አላህ ይበቃሃል፤ እርሱም ሰሚው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,138,'2:138','የአላህን (የተፈጥሮ) መንክር (እምነት) ያዙ፡፡ በመንከርም ከአላህ ይበልጥ ያማረ ማነው? (ማንም የለም) እኛም ለርሱ ብቻ ተገዢዎች ነን (በሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,139,'2:139','እርሱ (አላህ) ጌታችንና ጌታችሁ ሲኾን ለኛም ሥራችን ያለን ስንኾን ለናንተም ሥራችሁ ያላችሁ ስትኾኑ እኛም ለርሱ ፍጹም ታዛዦች ስንኾን በአላህ (ሃይማኖት) ትከራከሩናላችሁን? በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,140,'2:140','ወይም ኢብራሂም ኢስማዒልም ኢስሐቅም ያዕቁብም ነገዶቹም አይሁዶች ወይም ክርስቲያኖች ነበሩ ትላላችሁን? «እናንተ ታውቃላችሁን? ወይንስ አላህ?» በላቸው፡፡ እርሱም ዘንድ ከአላህ የኾነችን ምስክርነት ከደበቀ ሰው ይበልጥ በዳይ ማነው? አላህም ከምትሠሩት ሁሉ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,141,'2:141','ይህቺ በእርግጥ ያለፈች ሕዝብ ናት፤ ለእርሷ የሠራችው አላት፤ ለናንተም የሠራችሁት አላችሁ፤ ይሠሩትም ከነበሩት አትጠየቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,142,'2:142','ከሰዎቹ ቂሎቹ «ከዚያች በርሷ ላይ ከነበሩባት ቂብላቸው ምን አዞራቸው?» ይላሉ፤ «ምሥራቁም ምዕራቡም የአላህ ነው፤ የሻውን ሰው ወደ ቀጥተኛው መንገድ ይመራል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,143,'2:143','እንደዚሁም (እንደመራናችሁ) በሰዎች ላይ መስካሪዎች ልትሆኑና መልክተኛውም በናንተ ላይ መስካሪ ይሆን ዘንድ ምርጥ ሕዝቦች አደረግናችሁ፡፡ ያችንም በርሷ ላይ የነበርክባትን አቅጣጫ መልክተኛውን የሚከተለውን ሰው ወደ ክህደት ከሚመለሰው ሰው ልናውቅ (ልንገልጽ) እንጅ ቂብላ አላደረግናትም፡፡ እርሷም በነዚያ አላህ በመራቸው ሰዎች ላይ በስተቀር በእርግጥ ከባድ ናት፡፡ አላህም እምነታችሁን (ስግደታችሁን) የሚያጠፋ አይደለም፤ አላህ ለሰዎች በጣም ርኅሩኅ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,144,'2:144','የፊትህን ወደ ሰማይ መገላበጥ በእርግጥ እናያለን፡፡ ወደምትወዳትም ቂብላ እናዞርሃለን፡፡ ስለዚህ ፊትህን ወደ ተከበረው መስጊድ (ወደ ካዕባ) አግጣጫ አዙር፡፡ የትም ስፍራ ብትኾኑም (ስትሰግዱ) ፊቶቻችሁን ወደርሱ አግጣጫ አዙሩ፡፡ እነዚያም መጽሐፉን የተሰጡት እርሱ ከጌታቸው ሲኾን እውነት መሆኑን ያውቃሉ፡፡ አላህም ከምትሠሩት ሥራ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,145,'2:145','እነዚያንም መጽሐፍን የተሰጡትን በአስረጅ ሁሉ ብትመጣቸው ቂብላህን አይከተሉም፡፡ አንተም ቂብላቸውን ተከታይ አይደለህም፡፡ ከፊላቸውም የከፊሉን ቂብላ ተከታይ አይደሉም፡፡ ከዕውቀትም (ከራዕይ) ከመጣልህ በኋላ ዝንባሌያቸውን ብትከተል አንተ ያን ጊዜ ከበዳዮች ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,146,'2:146','እነዚያ መጽሐፍን የሰጠናቸው ወንዶች ልጆቻቸውን እንደሚያውቁ (ሙሐመድን) ያውቁታል፡፡ ከነሱም የተለዩ ክፍሎች እነርሱ የሚያወቁ ሲኾኑ እውነቱን በእርግጥ ይደብቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,147,'2:147','(ይህ) ከጌታህ የኾነ እውነት ነው፤ ከተጠራጣሪዎቹም አትሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(2,148,'2:148','ለሁሉም እርሱ (በስግደት ፊቱን) የሚያዞርባት አግጣጫ አለችው፡፡ ወደ መልካም ሥራዎችም ተሽቀዳደሙ፡፡ የትም ስፍራ ብትኾኑ አላህ እናንተን የተሰበሰባችሁ ኾናችሁ ያመጣችኋል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,149,'2:149','ከየትም (ለጉዞ) ከወጣህበት ስፍራ ፊትሀን ወደ ተከበረው መስጊድ አግጣጫ አዙር፡፡ እርሱም ከጌታህ የኾነ እርግጠኛ እውነት ነው፡፡ አላህም ከምትሠሩት ሥራ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,150,'2:150','ከየትም ከወጣህበት ስፍራ ፊትህን ወደ ተከለለው መስጊድ አግጣጫ አዙር፡፡ በየትም ስፍራ ብትኾኑ ለሰዎቹ እነዚያ ከነሱ የበደሉት ሲቀሩ (ሃይማኖታችንን ይክዳሉ ቂብላችንን ይከተላሉ በማለት) በናንተ ላይ መከራከሪያ እንዳይኖራቸው ፊቶቻችሁን ወደ አግጣጫው አዙሩ፡፡ አትፍሩዋቸውም፤ ፍሩኝም፤ (በዚህም ያዘዝኳችሁ መከራከሪያ እንዳይኖራቸው) በናንተም ላይ ጸጋየን እንድሞላላችሁና (ወደ እውነትም) እንድትመሩ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,151,'2:151','በውስጣችሁ ከናንተው የኾነን በናንተ ላይ አንቀጾቻችንን የሚያነብላችሁና የሚያጠራችሁ፣ መጽሐፍንና ጥበብንም የሚያስተምራችሁ፣ ታውቁት ያልነበራችሁትንም ነገር የሚያሳውቃችሁ የኾነን መልክተኛ እንደላክን (ጸጋን ሞላንላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,152,'2:152','አስታውሱኝም፤ አስታውሳችኋለሁና ለኔም አመስግኑ፤ አትካዱኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,153,'2:153','እናንተ ያመናችሁ ሆይ! በመታገስና በሶላት ተረዱ፡፡ አላህ (በእርዳታው) ከታጋሾች ጋር ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,154,'2:154','በአላህ መንገድ (ለሃይማኖቱ) የሚገደሉትን ሰዎችም «ሙታን ናቸው» አትበሉ፡፡ በእውነቱ ሕያዋን ናቸው፤ ግን አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,155,'2:155','ከፍርሃትና ከረኃብም በጥቂት ነገር፣ ከገንዘቦችና ከነፍሶችም፣ ከፍራፍሬዎችም በመቀነስ በእርግጥ እንሞክራችኋለን፡፡ ታጋሾችንም (በገነት) አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(2,156,'2:156','እነዚያን መከራ፤ በነካቻቸው ጊዜ «እኛ ለአላህ፤ ነን እኛም ወደርሱ ተመላሾች» ነን የሚሉትን (አብስር)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,157,'2:157','እነዚያ በእነርሱ ላይ ከጌታቸው የኾኑ ምሕረቶች ችሮታም አልሉ፡፡ እነርሱም (ወደ እውነት) ተመሪዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,158,'2:158','ሶፋና መርዋ ከአላህ (ትዕዛዝ መፈጸሚያ) ምልክቶች ናቸው፡፡ ቤቱን (ካዕባን) በሐጅ ወይም በዑምራህ ሥራ የጎበኘ ሰው በሁለቱ (መካከል) በመመላለሱ በርሱ ላይ ኃጢአት የለበትም፡፡ መልካምንም ሥራ በፈቃደኛነት የሠራ ሰው (አላህ ይመነዳዋል)፤ አላህ አመስጋኝ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,159,'2:159','እነዚያ ከአንቀጾችና ከቅን መምሪያ ያወረድነውን ለሰዎች በመጽሐፉ ከገለጽነው በኋላ የሚደብቁ፤ እነዚያ አላህ ይረግማቸዋል፤ ረጋሚዎችም ሁሉ ይረግሟቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,160,'2:160','እነዚያ (ከመደበቅ) የተጸጸቱና (ሥራቸውን) ያሳመሩም (የደበቁትን) የገለጹም ብቻ ሲቀሩ፡፡ እነዚህም በነሱ ላይ (ጸጸታቸውን) እቀበላለሁ፤ እኔም ጸጸትን በጣም ተቀባይ አዛኙ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,161,'2:161','እነዚያ የካዱ እነርሱ ከሐዲዎች ኾነውም የሞቱ እነዚያ በነርሱ ላይ የአላህና የመላእክት የሰዎችም ሁሉ እርግማን አለባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,162,'2:162','በውስጧ (በርግማንዋ ውስጥ) ሁልጊዜ ዘውታሪዎች ሲኾኑ ቅጣቱ ከነሱ አይቀለልም እነርሱም አይቅቆዩም (ጊዜ አይስሰጡም)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,163,'2:163','አምላካችሁም አንድ አምላክ ብቻ ነው፤ ከርሱ በቀር ሌላ አምላክ የለም፡፡ (እርሱ) እጅግ በጣም ርኅሩህ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,164,'2:164','ሰማያትንና ምድርን በመፍጠር ሌሊትንና ቀንንም በማተካካት፣ በዚያቸም ሰዎችን በሚጠቅም ነገር (ተጭና) በባህር ላይ በምትንሻለለው ታንኳ፣ አላህም ከሰማይ ባወረደው ውሃና በርሱም ምድርን ከሞተች በኋላ ሕያው በማድረጉ፣ በርሷም ውስጥ ከተንቀሳቃሽ ሁሉ በመበተኑ፣ ነፋሶችንም (በየአግጣጫው) በማገለባበጥ፣ በሰማይና በምድር መካከል በሚነዳውም ደመና ለሚያውቁ ሕዝቦች እርግጠኛ ምልክቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,165,'2:165','ከሰዎችም ከአላህ ሌላ ባለንጣዎችን (ጣዖታትን) አላህን እንደሚወዱ የሚወዷቸው ኾነው የሚይዙ አልሉ፡፡ እነዚያ ያመኑትም አላህን በመውደድ (ከነርሱ) ይበልጥ የበረቱ ናቸው፡፡ እነዚያም የበደሉት ሰዎች ቅጣትን (በትንሣኤ ቀን) ባዩ ጊዜ ኀይል ሁሉ ለአላህ ብቻ መኾኑንና አላህም ቅጣቱ ብርቱ መኾኑን (በአዱኛ ዓለም) ቢያውቁ ኖሮ (ባላንጣዎችን በመያዛቸው በተጸጸቱ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,166,'2:166','እነዚያ አስከታዮቹ ቅጣትን ያዩ ሲኾኑ ከእነዚያ ከተከታዮች በተጥራሩና በእነርሱም (መካከል) ምክንያቶች በተቆረጡ ጊዜ (የሚኾኑትን ባወቁ ኖሮ በተጸጸቱ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,167,'2:167','እነዚያም የተከተሉት «ለእኛ (ወደ ቅርቢቱ ዓለም) አንዲት ጊዜ መመለስ በኖረችንና ከእኛ እንደተጥራሩ ከእነርሱ በተጥራራን እንመኛለን» ይላሉ፡፡ እንደዚሁ አላህ ሥራዎቻቸውን በነርሱ ላይ ጸጸቶች አድርጎ ያሳያቸዋል፡፡ እነርሱም ከእሳት ወጪዎች አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,168,'2:168','እናንተ ሰዎች ሆይ! በምድር ካለው ነገር የተፈቀደ ጣፋጭ ሲኾን ብሉ፡፡ የሰይጣንንም እርምጃዎች አትከታተሉ፡፡ እርሱ ለእናንተ ግልጽ ጠላት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,169,'2:169','(እርሱ) የሚያዛችሁ በኃጢኣትና በጸያፍ ነገር በአላህም ላይ የማታውቁትን እንድትናገሩ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,170,'2:170','ለእነርሱም «አላህ ያወረደውን ተከተሉ» በተባሉ ጊዜ «አይደለም አባቶቻችንን በርሱ ላይ ያገኘንበትን ነገር እንከተላለን» ይላሉ፡፡ አባቶቻቸው ምንም የማያውቁና (ወደ እውነት) የማይመሩም ቢኾኑ (ይከተሉዋቸዋልን?)');
INSERT INTO "sadiq_sani" VALUES(2,171,'2:171','የነዚያም የካዱት (እና ወደ ቅን መንገድ የሚጠራቸው ሰው) ምሳሌ እንደዚያ ድምጽንና ጥሪን በስተቀር ሌላን በማይሰማ (እንስሳ) ላይ እንደሚጮህ ብጤ ነው፡፡ (እነርሱ) ደንቆሮዎች፣ ዲዳዎች፣ ዕውሮች ናቸው፤ ስለዚህ እነርሱ አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,172,'2:172','እናንተ ያመናችሁ ሆይ! ከነዚያ ከሰጠናችሁ ጣፋጮች ብሉ፡፡ ለአላህም እርሱን ብቻ የምትገዙ እንደኾናችሁ አመስግኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,173,'2:173','በናንተ ላይ እርም ያደረገው በክትንና ደምን፣ የእሪያ ሥጋንም፣ በእርሱም (ማረድ) ከአላህ ስም ሌላ የተነሳበትን ነገር ብቻ ነው፡፡ ሽፍታና ወሰን አላፊ ሳይኾን (ለመብላት) የተገደደ ሰውም በርሱ ላይ ኃጢኣት የለበትም አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,174,'2:174','እነዚያ አላህ ከመጽሐፍ ያወረደውን የሚደብቁ በርሱም (በመደበቃቸው) ጥቂትን ዋጋ የሚገዙ እነዚያ በሆዶቻቸው ውስጥ እሳትን እንጂ አይበሉም፡፡ አላህም በትንሣኤ ቀን አያናግራቸውም፤ (ከኃጢኣት) አያጠራቸውምም፡፡ ለነርሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,175,'2:175','እነዚያ ጥመትን በቅንነት ቅጣትንም በምሕረት የገዙ ናቸው፡፡ በእሳት ላይም ምን ታጋሽ አደረጋቸው!');
INSERT INTO "sadiq_sani" VALUES(2,176,'2:176','ይህ (ቅጣት) አላህ መጽሐፍን በእውነት ያወረደ በመኾኑ ምክንያት (እና በርሱ በመካዳቸው) ነው፡፡ እነዚያም በመጽሐፉ የተለያዩት (ከእውነት) በራቀ ጭቅጭቅ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,177,'2:177','መልካም ሥራ ፊቶቻችሁን ወደ ምሥራቅና ምዕራብ አቅጣጫ ማዞር አይደለም፡፡ ግን መልካም ሥራ በአላህና በመጨረሻው ቀን፣ በመላእክትም፣ በመጻሕፍትም፣ በነቢያትም፣ ያመነ ሰው ገንዘብንም ከመውደዱ ጋር ለዝምድና ባለቤቶችና ለየቲሞች ለምስኪኖችም፣ ለመንገደኞችም፣ ለለማኞችም፣ ለጫንቃዎችም (ማስለቀቅ) የሰጠ ሰውና ሶላትንም ደንቡን ጠብቆ የሰገደ፣ ዘካንም የሰጠ፣ ቃል ኪዳንም በገቡ ጊዜ በኪዳናቸው የሞሉ (ሰዎች ሥራ) ነው፡፡ በችግር በበሽታና በጦር ጊዜም ታጋሾችን (እናወድሳለን)፡፡ እነዚህ እነዚያ እውነትን የያዙ ናቸው፡፡ እነዚያም ተጠንቃቂዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,178,'2:178','እናንተ ያመናችሁ ሆይ! በተገደሉ ሰዎች ማመሳሰል በናንተ ላይ ተጻፈ፡፡ ነጻ በነጻ ባሪያም በባሪያ ሴትም በሴት (ይገደላሉ)፡፡ ለእርሱም (ለገዳዩ) ከወንድሙ (ደም) ትንሽ ነገር ምሕረት የተደረገለት ሰው (በመሓሪው ላይ ጉማውን) በመልካም መከታተል ወደርሱም (ወደ መሓሪው) ገዳዩ በመልካም አኳኋን መክፈል አለባቸው፡፡ ይህ ከጌታችሁ የኾነ ማቃለልና እዝነት ነው፡፡ ከዚህም በኋላ ሕግን የተላለፈ ሰው ለርሱ አሳማሚ ቅጣት አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,179,'2:179','ለእናንተም ባለ አእምሮዎች ሆይ! በማመሳሰል (ሕግ) ውስጥ ሕይወት አላችሁ፡፡ ትጠነቀቁ ዘንድ (ገዳይን መግደል ተደነገገላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,180,'2:180','አንዳችሁን ሞት በመጣበት ጊዜ ሀብትን ቢተው ለወላጆችና ለቅርብ ዘመዶች በበጎ መናዘዝ በናንተ ላይ ተጻፈ፡፡ (ይህ) በጥንቁቆቹ ላይ እርግጠኛ ድንጋጌ ተደነገገ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,181,'2:181','(ኑዛዜውን) ከሰማውም በኋላ የለወጠው ሰው ኃጢአቱ በነዚያ በሚለውጡት ላይ ብቻ ነው፡፡ አላህ ሰሚ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,182,'2:182','ከተናዛዢም በኩል (ከውነት) መዘንበልን ወይም (ከሢሶ በመጨመር) ኃጢኣትን ያወቀና በመካከላቸው ያስታረቀ ሰው በእርሱ ላይ ኃጢኣት የለበትም፡፡ አላህ በጣም መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,183,'2:183','እናንተ ያመናችሁ ሆይ ጾም በነዚያ ከናንተ በፊት በነበሩት (ሕዝቦች) ላይ እንደ ተጻፈ በናንተም ላይ ተጻፈ (ተደነባ) ልትጠነቀቁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,184,'2:184','የተቆጠሩን ቀኖች (ጹሙ)፡፡ ከእናንተም ውሰጥ በሽተኛ ወይም በጉዞ ላይ የኾነ ሰው ከሌሎች ቀኖች ቁጥሮችን መጾም አለበት፡፡ በእነዚያም ጾምን በማይችሉት ላይ ቤዛ ድኻን ማብላት አለባቸው፡፡ (ቤዛን በመጨመር) መልካምንም ሥራ የፈቀደ ሰው እርሱ (ፈቅዶ መጨመሩ) ለርሱ በላጭ ነው፡፡ መጾማችሁም ለናንተ የበለጠ ነው፤ የምታውቁ ብትኾኑ (ትመርጡታላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,185,'2:185','(እንድትጾሙ የተጻፈባችሁ) ያ በርሱ ውስጥ ለሰዎች መሪ ከቅን መንገድና (እውነትን ከውሸት) ከሚለዩም ገላጮች (አንቀጾች) ሲኾን ቁርኣን የተወረደበት የረመዳን ወር ነው፡፡ ከእናንተም ወሩን ያገኘ ሰው ይጹመው፡፡ በሽተኛ ወይም በጉዞ ላይ የኾነም ሰው ከሌሎች ቀኖች ቁጥሮችን (በልኩ) መጾም አለበት፡፡ አላህ በእናንተ ገሩን (ነገር) ይሻል፡፡ በእናንተም ችግሩን አይሻም፡፡ ቁጥሮችንም ልትሞሉ አላህንም ቅኑን መንገድ ስለመራችሁ ታከብሩትና ታመሰግኑት ዘንድ (ይህን ደነገግንላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,186,'2:186','ባሮቼም ከእኔ በጠየቁህ ጊዜ (እንዲህ በላቸው)፡- እኔ ቅርብ ነኝ፡፡ የለማኝን ጸሎት በለመነኝ ጊዜ እቀበለዋለሁ፡፡ ስለዚህ ለኔ ይታዘዙ፤ በኔም ይመኑ፤ እነሱ ሊመሩ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,187,'2:187','በጾም ሌሊት ወደ ሴቶቻችሁ መድረስ ለናንተ ተፈቀደላችሁ፡፡ እነርሱ ለናንተ ልብሶች ናቸው፤ እናንተም ለነርሱ ልብሶች ናችሁ፡፡ አላህ እናንተ ነፍሶቻችሁን የምትበድሉ መኾናችሁን ዐወቀ፡፡ በእናንተም ላይ ተመለሰላችሁ፡፡ ከእናንተም ይቅርታ አደረገ፡፡ አሁን ተገናኙዋቸው፡፡ አላህም ለናንተ የጻፈላችሁን ነገር (ልጅን) ፈልጉ፡፡ ከጎህ የኾነው ነጩ ክርም ከጥቁሩ ክር (ከሌሊት ጨለማ) ለእናንተ እስከሚገልጽላችሁ ድረስ ብሉ፤ ጠጡም፡፡ ከዚያም ጾምን እስከ ሌሊቱ ድረስ ሙሉ፡፡ እናንተም በመስጊዶች ተቀማጮች ስትኾኑ አትገናኙዋቸው፡፡ ይህች የአላህ ሕግጋት ናትና (ለመተላለፍ) አትቅረቧት፡፡ እንደዚሁ አላህ አንቀጾቹን ለሰዎች ያብራራል፡፡ እነርሱ (የተከለከሉትን) ሊጠነቀቁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,188,'2:188','ገንዘቦቻችሁንም በመካከላችሁ በከንቱ (ያለ አግባብ) አትብሉ፡፡ እናንተም የምታውቁ ስትኾኑ ከሰዎች ገንዘቦች ከፊልን በኃጢኣት ትበሉ ዘንድ ወደ ዳኞች አትጣሏት፡፡');
INSERT INTO "sadiq_sani" VALUES(2,189,'2:189','(ሙሐመድ ሆይ!) ከለጋ ጨረቃዎች (መለዋወጥ) ይጠየቁሃል፡፡ እነርሱ ለሰዎች ጥቅም ለሐጅም (ማወቂያ) ጊዜያቶች (ምልክቶች) ናቸው በላቸው፡፡ መልካም ሥራም ቤቶችን ከጀርባዎቻቸው በመምጣታችሁ አይደለም፡፡ ግን የመልካም ሥራ ባለቤት የተጠነቀቀ ሰው ነው፡፡ ቤቶችንም ከደጃፎቻቸው በኩል ግቡ፤ አላህንም ፍሩ ልትድኑ ይከጀላልና (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,190,'2:190','እነዚያንም የሚጋደሉዋችሁን (ከሓዲዎች) በአላህ መንገድ ተጋደሉ፡፡ ወሰንንም አትለፉ፤ አላህ ወሰን አላፊዎን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,191,'2:191','ባገኛችኋቸውም ስፍራ ሁሉ ግደሉዋቸው፡፡ ከአወጧችሁም ስፍራ አውጧቸው፡፡ መከራም ከመግደል ይበልጥ የበረታች ናት፡፡ በተከበረው መስጊድም ዘንድ በርሱ ውስጥ እስከሚጋደሉዋችሁ ድረስ አትጋደሉዋቸው፡፡ ቢጋደሉዋችሁም ግደሉዋቸው፤ የከሓዲዎች ቅጣት እንደዚህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,192,'2:192','ቢከለከሉም አላህ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,193,'2:193','ሁከት እስከማይገኝና ሃይማኖት ለአላህ ብቻ እስከሚኾን ድረስ ተጋደሉዋቸው፡፡ ቢከለክሉም ወሰንን ማለፍ፤ በበዳዮች ላይ እንጂ የለም (ወሰን አትለፉባቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,194,'2:194','የተከበረው ወር በተከበረው ወር አንጻር ነው፡፡ ክብሮችም ሁሉ ተመሳሳዮች ናቸው፡፡ በእናንተም ላይ (በተከበረው ወር) ወሰን ያለፈባችሁን ሰው በናንተ ላይ ወሰን ባለፈው ብጤ በርሱ ላይ ወሰን እለፉበት፡፡ አላህንም ፍሩ አላህ ከሚፈሩት ጋር መኾኑንም ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,195,'2:195','በአላህም መንገድ ለግሱ፡፡ በእጆቻችሁም (ነፍሶቻችሁን) ወደ ጥፋት አትጣሉ፡፡ በጎ ሥራንም ሥሩ፤ አላህ በጎ ሠሪዎችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,196,'2:196','ሐጅንና ዑምራንም ለአላህ ሙሉ፡፡ ብትታገዱም ከሀድይ (ከመሥዋእት) የተገራውን (መሰዋት) አለባችሁ፡፡ ሀድዩም እስፍራው እስከሚደርስ ድረስ ራሶቻችሁን አትላጩ፡፡ ከናንተም ውስጥ በሽተኛ ወይም በራሱ ሁከት ያለበት የኾነ ሰው (ቢላጭ) ከጾም ወይም ከምጽዋት ወይም ከመሥዋዕት ቤዛ አለበት፡፡ ጸጥታም ባገኛችሁ ጊዜ እስከ ሐጅ በዑምራ የተጣቀመ ሰው ከሀድይ የተገራውን (መሠዋት) አለበት፡፡ ያላገኘም ሰው ሶስትን ቀኖች በሐጅ ወራት፤ ሰባትም በተመለሳችሁ ጊዜ መጾም አለበት፡፡ ይህች ሙሉ ዐሥር (ቀናት) ናት፡፡ ይህም (ሕግ) ቤተሰቦቹ ከቅዱሱ መስጊድ አቅራቢያ ላልኾኑ ነው፡፡ አላህንም ፍሩ፤ አላህም ቅጣተ ብርቱ መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,197,'2:197','ሐጅ (ጊዜያቱ) የታወቁ ወሮች ናቸው፡፡ በእነርሱም ውስጥ ሐጅን (እንዲሠራ) ነፍሱን ያስገደደ ሰው በሐጅ ውስጥ ሴትን መገናኘት ማመጽም ክርክርም የለም፡፡ ከበጎም ሥራ የምትሠሩትን ሁሉ አላህ ያውቀዋል፡፡ ተሰነቁም፤ ከስንቅም ሁሉ በላጩ ጥንቃቄ (አላህን መፍራት) ነው፡፡ የአእምሮዎችም ባለቤቶች ሆይ! ፍሩኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,198,'2:198','(በሐጅ ጊዜ በንግድ ሥራ) ከጌታችሁ ትርፍን በመፈለጋችሁ በናንተ ላይ ኃጢኣት የለባችሁም፡፡ ከዐረፋትም በጎረፋችሁ ጊዜ መሸዐረልሐራም ዘንድ አላህን አውሱ፡፡ (ለሐጅ) ስለመራችሁም አውሱት፡፡ ከመምራቱ በፊትም በእርግጥ ከተሳሳቾች ነበራችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,199,'2:199','ከዚያም (ቁረይሾች ሆይ!) ሰዎቹ ከጎረፉበት ስፍራ ጉረፉ፤ (ተመለሱ)፡፡ አላህንም ምሕረትን ለምኑ፤ አላህ እጅግ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,200,'2:200','የሐጅ ሥራዎቻችሁንም በፈጸማችሁ ጊዜ አባቶቻችሁን እንደምታወሱ ወይም ይበልጥ የበረታን ማውሳት አላህን አውሱ፡፡ ከሰዎችም ውስጥ፡- «ጌታችን ሆይ! በምድረዓለም መልካም ዕድልን ስጠን» የሚል ሰው አለ፡፡ ለርሱም በመጨረሻይቱ አገር ከዕድል ምንም የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,201,'2:201','ከእነርሱም ውስጥ፡- «ጌታችን ሆይ! በምድረ ዓለም ደግን ነገር (ጸጋን) በመጨረሻይቱም አገር ደግን ነገር (ገነትን) ስጠን፤ የእሳትንም ቅጣት ጠብቀን» የሚሉ ሰዎች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,202,'2:202','እነዚያከሠሩት በጎ ሥራ ለነርሱ እድል አላቸው፡፡ አላህም ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,203,'2:203','በተቆጠሩ ቀኖችም ውስጥ (በሚና ጠጠሮችን ስትወረውሩ) አላህን አውሱ፡፡ በሁለት ቀኖችም ውስጥ (በመኼድ) የተቻኮለ ሰው በርሱ ላይ ኃጢኣት የለበትም፡፡ የቆየም ሰው በርሱ ላይ ኃጢኣት የለበትም፡፡ (ይህም) አላህን ለፈራ ሰው ነው፡፡ አላህንም ፍሩ፡፡ እናንተ ወደርሱ የምትሰበሰቡ መኾናችሁንም ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,204,'2:204','ከሰዎችም ውስጥ እርሱ ክርክረ ብርቱ ሲኾን በቅርቢቱ ሕይወት ንግግሩ የሚደንቅህና በልቡ ውስጥ ባለው ነገር ላይ አላህን የሚያስመሰክር ሰው አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,205,'2:205','(ካንተ) በዞረም ጊዜ በምድር ላይ በውስጧ ሊያበላሽና አዝመራንና እንስሳዎችን ሊያጠፋ ይሮጣል፡፡ አላህም ማበላሸትን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,206,'2:206','ለእርሱ «አላህን ፍራ» በተባለም ጊዜ፤ ትዕቢቱ በኃጢኣት (ሥራ) ላይ ትገፈፋዋለች፡፡ ገሀነምም በቂው ናት፤ (እርሷም) በእርግጥ የከፋች ምንጣፍ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(2,207,'2:207','ከሰዎችም ውስጥ የአላህን ውዴታ ለመፈለግ ነፍሱን የሚሸጥ ሰው አልለ፡፡ አላህም ለባሮቹ በጣም ርኅሩህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,208,'2:208','እናንተ ያመናችሁ ሆይ! ሁላችሁም በመታዘዝ ውስጥ ግቡ፡፡ የሰይጣንንም እርምጃዎች አትከተሉ፤ እርሱ ለእናንተ ግልጽ ጠላት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,209,'2:209','ግልጽ ማስረጃዎችም ከመጡላችሁ በኋላ ብትዘነበሉ አላህ አሸናፊ ጥበበኛ መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,210,'2:210','አላህ (ቅጣቱ)ና መላእክቱ ከደመና በኾኑ ጥላዎች ውስጥ ሊመጡዋቸው እንጂ አይጠባበቁም፡፡ ነገሩም ተፈጸመ፤ ነገሮችም ሁሉ ወደ አላህ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,211,'2:211','የእስራኤልን ልጆች ከግልጽ ተዓምር ስንትን እደሰጠናቸው ጠይቃቸው፡፡ የአላህንም ጸጋ ከመጣችለት በኋላ የሚለውጥ ሰው አላህ ቅጣተ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,212,'2:212','ለእነዚያ ለካዱት ከእነዚያ ካመኑት የሚሳለቁ ሲኾኑ ቅርቢቱ ሕይወት ተሸለመችላችው፡፡ እነዚያም የተጠነቀቁት በትንሣኤ ቀን ከበላያቸው ናቸው፡፡ አላህም ለሚሻው ሰው ያለ ግምት ይሰጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,213,'2:213','ሰዎቹ ሁሉ አንድ ሕዝብ ነበሩ፤ (ተለያዩ)፡፡ አላህም ነቢያትን አብሳሪዎችና አስፈራሪዎች አድርጎ ላከ፡፡ ከእነርሱም ጋር መጻሕፍትን በሰዎቹ መካከል በዚያ በርሱ በተለያዩበት ነገር ይፈርድ ዘንድ በእውነት አወረደ፡፡ በእርሱም (በሃይማኖት) ግልጽ አስረጂዎች ከመጡላቸው በኋላ በመካከላቸው ለኾነው ምቀኝነት እነዚያው የተሰጡት እንጂ አልተለያዩበትም፡፡ አላህም እነዚያን ያመኑትን ሰዎች ለእዚያ ከእውነት በርሱ ለተለያዩበት በፍቃዱ መራቸው፡፡ አላህም የሚሻውን ሰው ወደ ቀጥተኛው መንገድ ይመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,214,'2:214','በእውነቱ የእነዚያ ከበፊታችሁ ያለፉት (ምእምናን መከራ) ብጤ ሳይመጣችሁ ገነትን ልትገቡ ታስባላችሁን? መልክተኛውና እነዚያ ከርሱ ጋር ያመኑት «የአላህ እርዳታ መቼ ነው?» እስከሚሉ ድረስ መከራና ጉዳት ነካቻቸው፤ ተርበደበዱም፡፡ ንቁ! የአላህ እርዳታ በእርግጥ ቅርብ ነው (ተባሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,215,'2:215','ምንን (ለማንም) እንደሚለግሱ ይጠይቁሃል፡፡ ከመልካም ነገር የምትለግሱት፤ ለወላጆችና ለቅርብ ዘመዶች፣ ለየቲሞችም፣ ለድሆችም፣ ለመንገደኞችም ነው፡፡ ከበጎ ነገርም ማንኛውንም ብትሠሩ አላህ እርሱን ዐዋቂ ነው በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,216,'2:216','መጋደል እርሱ ለእናንተ የተጠላ ሲሆን በእናንተ ላይ ተጻፈ፡፡ አንዳች ነገርን እርሱ ለናንተ የበለጠ ሲሆን የምትጠሉት መኾናችሁ ተረጋገጠ፡፡ አንዳችንም ነገር እርሱ ለናንተ መጥፎ ሲሆን የምትወዱት መሆናችሁ ተረጋገጠ፡፡ አላህም (የሚሻላችሁን) ያውቃል ግን እናንተ አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,217,'2:217','ከተከበረው ወር (ከረጀብ) በርሱ ውስጥ ከመጋደል ይጠይቁሃል፡፡ በላቸው «በርሱ ውስጥ መጋደል ታላቅ (ኀጢአት) ነው፡፡ ግን ከአላህ መንገድ (ሰዎችን) መከላከል፣ በርሱም መካድ፣ ከተከበረውም መስጊድ (ማገድ) ባለቤቶቹንም ከርሱ ማውጣት አላህ ዘንድ ይበልጥ ታላቅ (ወንጀል) ነው፡፡ ፈተናም (ማጋራት) ከመግደል ይበልጥ ከባድ ነው፡፡ (ከሐዲዎች) ቢችሉ ከሃይማኖታችሁ እስከሚመልሳችሁ ድረስ የሚዋጉዋችሁ ከመሆን አይቦዝኑም፡፡ ከእናንተ ውስጥም ከሃይማኖቱ የሚመለስና እርሱም ከሐዲ ሆኖ የሚሞት ሰው እነዚያ (በጎ) ሥራቸው በቅርቢቱም በመጨረሻይቱም አገር ተበላሸች፡፡ እነዚያ የእሳት ጓዶች ናቸው፡፡ እነርሱ በርሷ ውስጥ ዘውታሪዎች ናቸው፡፡»');
INSERT INTO "sadiq_sani" VALUES(2,218,'2:218','እነዚያ ያመኑትና እነዚያም (ከአገራቸው) የተሰደዱት በአላህም መንገድ ላይ የተጋደሉት እነዚያ የአላህን እዝነት ይከጅላሉ፡፡ አላህም እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,219,'2:219','አእምሮን ከሚቃወም መጠጥና ከቁማር ይጠይቁሃል፡፡ «በሁለቱም ውስጥ ታላቅ ኃጢኣትና ለሰዎች ጥቅሞች አሉባቸው፡፡ ግን ኃጢኣታቸው ከጥቅማቸው በጣም ትልቅ ነው» በላቸው፡፡ ምንን እንደሚመጸውቱም (መጠኑን) ይጠይቁሃል፡፡ «ትርፍን (መጽውቱ)» በላቸው፡፡ እንደዚሁ ታስተነትኑ ዘንድ አላህ ለናንተ አንቀጾችን ይገልጽላችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,220,'2:220','በቅርቢቱ ዓለምና በመጨረሻይቱ ታስተነትኑ ዘንድ (ይገለጽላችኋል)፡፡ ከየቲሞችም ይጠይቁሃል፡፡ «ለእነርሱ (ገንዘባቸውን በማራባት) ማሳመር በላጭ ነው፡፡ ብትቀላቀሏቸውም ወንድሞቻችሁ ናቸው፡፡ አላህም የሚያጠፋውን ከሚያበጀው (ለይቶ) ያውቃል፡፡ አላህም በሻ ኖሮ ባስቸገራችሁ ነበር፤ አላህ አሸናፊ ጥበበኛ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,221,'2:221','(በአላህ) አጋሪ የሆኑ ሴቶች እስከሚያምኑ ድረስ አታግቡዋቸው፡፡ ከአጋሪይቱ ምንም ብትደንቃችሁም እንኳ ያመነችው ባሪያ በእርግጥ በላጭ ናት፡፡ ለአጋሪዎቹም እስከሚያምኑ ድረስ አትዳሩላቸው፡፡ ከአጋሪው (ጌታ) ምንም ቢደንቃችሁ ምእምኑ ባሪያ በላጭ ነው፡፡ እነዚያ (አጋሪዎች) ወደ እሳት ይጣራሉ፡፡ አላህም በፈቃዱ ወደ ገነትና ወደ ምሕረት ይጠራል፡፡ ሕግጋቱንም ለሰዎች ይገልጻል እነርሱ ሊገሰጹ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,222,'2:222','ከወር አበባም ይጠይቁሃል፡፡ እርሱ አስጠያፊ ነው፡፡ ሴቶችንም በወር አበባ ጊዜ ራቋቸው፡፡ ንጹሕ እስከሚኾኑም ድረስ አትቅረቡዋቸው፡፡ ንጹሕ በኾኑም ጊዜ አላህ ካዘዛችሁ ስፍራ ተገናኙዋቸው፡፡ አላህ (ከኃጢኣት) ተመላሾችን ይወዳል፤ ተጥራሪዎችንም ይወዳል በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,223,'2:223','ሴቶቻችሁ ለናንተ እርሻ ናቸው፡፡ እርሻችሁንም በፈለጋችሁት ኹነታ ድረሱ፡፡ ለነፍሶቻችሁም (መልካም ሥራን) አስቀድሙ፡፡ አላህንም ፍሩ፡፡ እናንተም ተገናኝዎቹ መኾናችሁን ዕወቁ፡፡ ምእመናንንም (በገነት) አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(2,224,'2:224','መልካም እንዳትሠሩ፣ አላህንም እንዳትፈሩ፣ በሰዎችም መካከል እንዳታስታርቁ አላህን ለመሐላዎቻችሁ ግርዶ አታድርጉ፡፡ አላህም ሰሚ አዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,225,'2:225','በመሐላዎቻችሁ በውድቁ (ሳታስቡ በምትምሉት) አላህ አይዛችሁም፡፡ ግን ልቦቻችሁ ባሰቡት ይይዛችኋል፡፡ አላህም በጣም መሐሪ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,226,'2:226','ለእነዚያ ከሴቶቻቸው (ላይቀርቡ) ለሚምሉት አራትን ወሮች መጠበቅ አለባቸው፡፡ (ከመሐላቸው) ቢመለሱም አላህ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,227,'2:227','መፍታትንም ቁርጥ አሳብ ቢያደርጉ (ይፍቱ)፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,228,'2:228','የተፈቱ ሴቶችም ነፍሶቻቸውን (ከማግባት) ሦስትን ቁርእ ይጠብቁ፡፡ በአላህና በመጨረሻውም ቀን የሚያምኑ ቢኾኑ አላህ በማሕፀኖቻቸው ውስጥ የፈጠረውን ሊደብቁ ለነርሱ አይፈቀድላቸውም፡፡ ባሎቻቸውም በዚህ ውስጥ እርቅን ቢፈልጉ በመማለሳቸው ተገቢዎች ናቸው፡፡ ለእነርሱም (ለሴቶች) የዚያ በእነርሱ ላይ ያለባቸው (ግዳጅ) ብጤ በመልካም አኑዋኑዋር (በባሎቻቸው ላይ መብት) አላቸው፡፡ ለወንዶችም (ጣጣቸውን ስለሚሸከሙ) በእነሱ ላይ ብልጫ አላቸው፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,229,'2:229','ፍች ሁለት ጊዜ ነው፤ (ከዚህ በኋላ) በመልካም መያዝ ወይም በበጎ አኳኋን ማሰናበት ነው፡፡ የአላህንም ሕግጋት አለመጠበቃቸውን ካላወቁ በስተቀር ከሰጣችኃቸው ነገር ምንንም ልትወስዱ ለእናንተ (ለባሎች) አይፈቀድላችሁም፡፡ የአላህንም ሕግጋት አለመጠበቃቸውን ብታውቁ በእርሱ (ነፍሷን) በተበዠችበት ነገር በሁለቱም ላይ ኃጢኣት የለም፡፡ ይህች የአላህ ሕግጋት ናት፤ አትተላለፏትም፡፡ የአላህንም ሕግጋት የሚተላለፉ እነዚያ እነርሱ በዳዮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,230,'2:230','(ሦስተኛ) ቢፈታትም ከዚህ በኋላ ሌላን ባል እስከምታገባ ድረስ ለርሱ አትፈቀድለትም፡፡ (ሁለተኛው ባል) ቢፈታትም የአላህን ሕግጋት መጠበቃቸውን ቢያውቁ በመማለሳቸው በሁለቱም ላይ ኃጢኣት የለባቸውም፡፡ ይህችም የአላህ ሕግጋት ናት ለሚያውቁ ሕዝቦች ያብራራታል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,231,'2:231','ሴቶችን በፈታችሁና (የዒዳ) ጊዜያቸውን በደረሱ ጊዜ በመልካም ያዙዋቸው፤ (ተማለሷቸው) ወይም በመልካም ኹኔታ አሰናብቱዋቸው፡፡ ለመጉዳትም ወሰን ታልፋባቸው ዘንድ አትያዙዋቸው፤ ይህንንም የሚሠራ ሰው ነፍሱን በእርግጥ በደለ፡፡ የአላህንም አንቀጾች ማላገጫ አድርጋችሁ አትያዙ፡፡ የአላህንም ጸጋ በናንተ ላይ ያደረገውን ከመጽሐፍና ከጥበብም በርሱ የሚገስጻችሁ ሲኾን በናንተ ላይ ያወረደውን አስታውሱ፡፡ አላህንም ፍሩ፤ አላህም ነገሩን ሁሉ ዐዋቂ መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,232,'2:232','ሴቶችንም በፈታችሁና ጊዜያቸውን በደረሱ ጊዜ በመካከላቸው በሕግ በተዋደዱ ጊዜ ባሎቻቸውን ከማግባት አታስተጓጉሏቸው፡፡ ይህ (መከልከል) ከእናንተ በአላህና በመጨረሻው ቀን የሚያምን ሰው በርሱ ይገሰጽበታል፡፡ ይህ ሁኔታችሁ ለእናንተ በላጭ ነው፤ (ከመጠርጠር) አጥሪም ነው፡፡ አላህም ያውቃል፤ እናንተ ግን አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,233,'2:233','እናቶችም ልጆቻቸውን ሙሉ የኾኑን ሁለት ዓመታት ያጥቡ፡፡ (ይህም) ማጥባትን መሙላት ለሻ ሰው ነው፡፡ ለእርሱም በተወለደለት (አባት) ላይ ምግባቸውና ልብሳቸው በችሎታው ልክ አለበት፡፡ ነፍስ ችሎታዋን እንጂ አትገደድም፡፡ ወላጂት (እናት) በልጅዋ ምክንያት ለርሱ የተወለደለትም (አባት) በልጁ ምክንያት አይጎዳዱ፡፡ በወራሽም ላይ እንደዚሁ ብጤ አለበት፡፡ (ወላጆቹ) ከሁለቱም በኾነ መዋደድና መመካከር (ልጁን ከጡት) መነጠልን ቢፈልጉ በሁለቱም ላይ ኃጢኣት የለባቸውም፡፡ ልጆቻችሁንም ለሌሎች አጥቢዎች ማስጠባትን ብትፈልጉ ልትሰጡ የሻችሁትን በመልካም ኹኔታ በሰጣችሁ ጊዜ (በማስጠባታችሁ) በእናንተ ላይ ኃጢኣት የለባችሁም፡፡ አላህንም ፍሩ፤ አላህም የምትሠሩትን ሁሉ ተመልካች መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,234,'2:234','እነዚያም ከናንተ ውስጥ የሚሞቱና ሚስቶችን የሚተዉ (ሚስቶቻቸው) በነፍሶቻቸው አራት ወሮች ከዐስር (ቀናት ከጋብቻ) ይታገሱ፡፡ ጊዜያቸውንም በጨረሱ ጊዜ በነፍሶቻቸው በታወቀ ሕግ በሠሩት ነገር በእናንተ ላይ ኃጢአት የለባችሁም፡፡ አላህም በምትሠሩት ሁሉ ውስጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,235,'2:235','ሴቶችንም ከማጨት በርሱ ባሸሞራችሁበት ወይም በነፍሶቻችሁ ውስጥ (ለማግባት) በደበቃችሁት በናንተ ላይ ኃጢኣት የለባችሁም፡፡ አላህ እናንተ በእርግጥ የምታስታውሷቸው መኾናችሁን ዐወቀ፡፡ (ስለዚህ ማሸሞርንና ማሰብን ፈቀደላችሁ፡፡) ግን በሕግ የታወቀን ንግግር የምትነጋገሩ ካልኾናችሁ በስተቀር፤ ምስጢርን (ጋብቻን) አትቃጠሩዋቸው፡፡ የተጻፈውም (ዒዳህ) ጊዜውን እስከሚደርስ ድረስ ጋብቻን ለመዋዋል ቁርጥ ሐሳብ አታድርጉ፡፡ አላህም በነፍሶቻችሁ ያለውን ነገር የሚያውቅ መኾኑን ዕወቁ፤ ተጠንቀቁትም፡፡ አላህም መሓሪ ታጋሽ መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,236,'2:236','ሴቶችን ሳትነኳቸው (ሳትገናኙ)፤ ወይም ለነሱ መህርን ሳትወስኑላቸው ብትፈቷቸው በናንተ ላይ ኃጢኣት የለባችሁም፡፡ (ዳረጎት በመስጠት መፍታት ትችላላችሁ፡፡) ጥቀሟቸውም፡፡ በሀብታም ላይ ችሎታው በድኀም ላይ ችሎታው (አቅሙ የሚፈቅደውን መስጠት) አለበት፡፡ መልካም የኾነን መጥቀም በበጎ ሠሪዎች ላይ የተረጋገጠን፤ (ጥቀሟቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,237,'2:237','ለእነርሱም መወሰንን በእርግጥ የወሰናችሁላቸው ስትኾኑ ሳትነኳቸው በፊት ብተፈቷቸው ካልማሩዋችሁ ወይም ያ የጋብቻው ውል በእጁ የኾነው (ባልየው) ካልማረ በስተቀር የወሰናችሁት ግማሹ አላቸው፡፡ ምሕረትም ማድረጋችሁ ወደ አላህ ፍራቻ በጣም የቀረበ ነው፡፡ በመካከላችሁም ችሮታን አትርሱ፤ አላህ የምትሠሩትን ሁሉ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,238,'2:238','በሶላቶች (በተለይ) በመካከለኛይቱም ሶላት ላይ ተጠባበቁ፡፡ ታዛዦች ኾናችሁም ለአላህ ቁሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,239,'2:239','ብትፈሩም እግረኞች ወይም ጋላቢዎች ኾናችሁ (ስገዱ)፡፡ ጸጥተኞችም በኾናችሁ ጊዜ ታውቁት ያልነበራችሁትን እንደ አሳወቃችሁ አላህን አውሱ (ስገዱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,240,'2:240','እነዚያ ከናንተ ውስጥ የሚሞቱ ሚስቶችንም የሚተዉ፤ ለሚስቶቻቸው (ከቤታቸው) የማይወጡ ሲኾኑ ዓመት ድረስ መጠቀምን ኑዛዜን (ይናዘዙ)፡፡ በፈቃዳቸው ቢወጡም በሕግ ከታወቀው ነገር በነፍሶቻቸው በሠሩት በእናንተ (በሟቹ ዘመዶች) ላይ ኃጢኣት የለባችሁም አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,241,'2:241','ለተፈቱ ሴቶችም በችሎታ መጠን ዳረጎት አላቸው፡፡ አላህን በሚፈሩ ላይ ተደንግጓል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,242,'2:242','እንደዚሁ አላህ አንቀጾቹን ታውቁ ዘንድ ለናንተ ያብራራላችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,243,'2:243','ወደ እነዚያ እነርሱ ብዙ ሺሕ ኾነው ሞትን ለመፍራት ከሀገሮቻቸው ወደ ወጡት ሰዎች ዕውቀትህ አልደረሰምን? አላህም ለነርሱ «ሙቱ» አላቸው፤ (ሞቱም)፡፡ ከዚያም ሕያው አደረጋቸው፡፡ አላህም በሰዎች ላይ ባለችሮታ ነው፤ ግን አብዛኛዎቹ ሰዎች አያመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,244,'2:244','በአላህም መንገድ (ሃይማኖት) ተጋደሉ፡፡ አላህም ሰሚ ዐዋቂ መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,245,'2:245','ያ ለአላህ መልካም ብድርን የሚያበድርና ለእርሱ (አላህ) ብዙ እጥፎች አድርጎ የሚያነባብርለት ማነው? አላህም ይጨብጣል፤ ይዘረጋልም፤ ወደ እርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,246,'2:246','ከእስራኤል ልጆች ከሙሳ በኋላ ወደ ነበሩት ጭፍሮች ለነቢያቸው (ለሳሙኤል)፡- «ለእኛ ንጉሥን አስነሳልን በአላህ መንገድ እንዋጋለን፤» ባሉ ጊዜ አላየህምን? (አላወቅህምን?)፡-«መዋጋት ቢጻፍባችሁ አትዋጉም ይኾናል» አላቸው፡፡ «ከሀገሮቻችንና ከልጆቻችን የተባረርን ስንኾን በአላህ መንገድ የማንዋጋ ለእኛ ምን አለን?» አሉ፡፡ በእነርሱ ላይ መዋጋት በተጻፈባቸውም ጊዜ ከእነርሱ ጥቂቶች ሲቀሩ አፈገፈጉ፡፡ አላህም በዳዮቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,247,'2:247','ነቢያቸውም ለእነርሱ፡- «አላህ ጧሉትን (ሳኦልን) ንጉሥ አድርጎ በእርግጥ ላከላችሁ» አላቸው፡፡ (እነርሱም)፡- «እኛ ከእርሱ ይልቅ በንግሥና ተገቢዎች ስንኾን ከሀብትም ስፋትን ያልተሰጠ ሲኾን ለእርሱ በኛ ላይ እንዴት ንግሥና ይኖረዋል?» አሉ፡፡ (ነቢያቸውም)፡-«አላህ በእናንተ ላይ መረጠው፡፡ በዕውቀትና በአካልም ስፋትን ጨመረለት፡፡ አላህም ንግሥናውን ለሚሻው ሰው ይሰጣል፡፡ አላህም ችሮታው ሰፊ ዐዋቂ ነው» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,248,'2:248','ነቢያቸውም ለእነርሱ፡- «የንግሥናው ምልክት ከጌታችሁ የኾነ እርጋታ የሙሳ ቤተሰብና የሃሩን ቤተሰብ ከተውትም ቅርስ በውስጡ ያለበት መላእክት የሚሸከሙት ኾኖ ሳጥኑ ሊመጣላችሁ ነው፡፡ አማኞች ብትኾኑ በዚህ ለእናንተ እርግጠኛ ምልክት አልለ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,249,'2:249','ጧሉትም በሠራዊቱ (ታጅቦ) በወጣ ጊዜ፡- «አላህ በወንዝ (ውሃ) ፈታኛችሁ ነው ከርሱም የጠጣ ሰው ከእኔ አይደለም፡፡ ያልቀመሰውም ሰው በእጁ መዝገንን የዘገነ ሰው ብቻ ሲቀር እርሱ ከኔ ነው» አለ፡፡ ከእነርሱም ጥቂቶች ሲቀሩ ከርሱ ጠጡ፡፡ እርሱና እነዚያም ከእርሱ ጋር ያመኑት (ወንዙን) ባለፉት ጊዜ፡- «ጃሎትንና ሠራዊቱን በመዋጋት ለኛ ዛሬ ችሎታ የለንም» አሉት፡፡ እነዚያ እነርሱ አላህን ተገናኝዎች መኾናቸው የሚያረጋግጡት፡- «ከጥቂት ጭፍራ በአላህ ፈቃድ ብዙን ጭፍራ ያሸነፈች ብዙ ናት፤ አላህም ከታጋሾች ጋር ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,250,'2:250','ለጃሎትና ለሠራዊቱ በተሰለፉም ጊዜ፡- «ጌታችን ሆይ! በእኛ ላይ ትዕግስትን አንቧቧ፤ ጫማዎቻችንንም አደላድል፤ በከሓዲያን ሕዝቦችም ላይ እርዳን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,251,'2:251','በአላህም ፈቃድ ድል መቷቸው፡፡ ዳውድም ጃሉትን (ዳዊት ጎልያድን) ገደለ፡፡ ንግሥናንና ጥበብንም (ነቢይነትን) አላህ ሰጠው፡፡ ከሚሻውም ነገር ሁሉ አሳወቀው፡፡ አላህም ሰዎችን ከፊላቸውን በከፊሉ መመለሱ ባልነበረ ኖሮ ምድር በተበላሸች ነበር፤ ግን አላህ በዓለማት ላይ የችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,252,'2:252','እነዚህ (አንቀጾች) በውነት በአንተ ላይ የምናነባቸው ሲኾኑ የአላህ አንቀጾች ናቸው፤ አንተም በእርግጥ ከመልክተኞቹ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,253,'2:253','እነዚህን መልክተኞች ከፊላቸውን በከፊሉ ላይ አበለጥን፡፡ ከእነርሱ ውስጥ አላህ ያነጋገረው አልለ፡፡ ከፊሎቻቸውንም በደረጃዎች ከፍ አደረገ፡፡ የመርየምን ልጅ ዒሳንም ግልጽ ታምራቶችን ሰጠነው፡፡ በቅዱሱ መንፈስም አበረታነው፡፡ አላህም በሻ ኖሮ እነዚያ ከእነርሱ (ከመልክተኞቹ) በኋላ የነበሩት ግልጽ ታምራቶች ከመጡላቸው በኋላ ባልተጋደሉ ነበር፡፡ ግን ተለያዩ፡፡ ከእነርሱም ውስጥ ያመነ ሰው አልለ፡፡ ከነርሱም ውስጥ የካደ ሰው አልለ፡፡ አላህም በሻ ኖሮ ባልተዋጉ (ባልተለያዩ) ነበር፡፡ ግን አላህ የሚሻውን ይሠራል፡፡');
INSERT INTO "sadiq_sani" VALUES(2,254,'2:254','እናንተ ያመናችሁ ሆይ! በርሱ ውስጥ ሽያጭ (መበዠት)፣ ወዳጅነትም፣ ምልጃም የሌለበት ቀን ከመምጣቱ በፊት ከሰጠናችሁ ነገር ለግሱ፡፡ ከሓዲዎችም በዳዮቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,255,'2:255','አላህ ከእርሱ በቀር ሌላ አምላክ የለም፡፡ ሕያው ራሱን ቻይ ነው፡፡ ማንገላጀትም እንቅልፍም አትይዘውም፡፡ በሰማያት ውስጥና በምድር ውስጥ ያለው ሁሉ የርሱ ብቻ ነው፡፡ ያ እርሱ ዘንድ በፈቃዱ ቢኾን እንጅ የሚያማልድ ማነው? (ከፍጡሮች) በፊታቸው ያለውንና ከኋላቸው ያለውን ሁሉ ያውቃል፡፡ በሻውም ነገር እንጂ ከዕውቀቱ በምንም ነገር አያካብቡም (አያውቁም)፡፡ መንበሩ ሰማያትንና ምድርን ሰፋ፡፡ ጥበቃቸውም አያቅተውም፡፡ እርሱም የሁሉ በላይ ታላቅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,256,'2:256','በሃይማኖት ማስገደድ የለም፡፡ ቅኑ መንገድ ከጠማማው በእርግጥ ተገለጠ፡፡ በጣዖትም የሚክድና በአላህ የሚያምን ሰው ለርሷ መበጠስ የሌላትን ጠንካራ ዘለበት በእርግጥ ጨበጠ፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,257,'2:257','አላህ የእነዚያ ያመኑት ሰዎች ረዳት ነው፡፡ ከጨለማዎች ወደ ብርሃን ያወጣቸዋል፡፡ እነዚያም የካዱት ረዳቶቻቸው ጣዖታት ናቸው፡፡ ከብርሃን ወደ ጨለማዎች ያወጧቸዋል፡፡ እነዚያ የእሳት ጓዶች ናቸው፤ እነርሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,258,'2:258','ወደዚያ አላህ ንግሥናን ስለሰጠው ኢብራሂምን በጌታው ነገር ወደ ተከራከረው ሰው አላየህምን ኢብራሂም «ጌታዬ ያ ሕያው የሚያደርግና የሚገድል ነው» ባለ ጊዜ «እኔ ሕያው አደርጋለሁ እገድላለሁም» አለ፡፡ ኢብራሂም፡- «አላህ ፀሐይን ከምሥራቅ በኩል ያመጣል (አንተ) ከምዕራብ በኩል አምጣት» አለው፡፡ ያም የካደው ሰው ዋለለ፤ (መልስ አጣ) አላህም በዳዮች ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,259,'2:259','ወይም ያንን በከተማ ላይ እርሷ በጣራዎችዋ ላይ የወደቀች ስትኾን ያለፈውን ሰው ብጤ (አላየህምን)፡- «ይህችን (ከተማ) ከሞተች በኋላ አላህ እንዴት ሕያው ያደርጋታል» አለ፡፡ አላህም ገደለው መቶ ዓመትን (አቆየውም)፡፡ ከዚያም አስነሳው፡- «ምን ያህል ቆየህ» አለው፡፡ «አንድ ቀን ወይም የቀንን ከፊል ቆየሁ» አለ፡፡ «አይደለም መቶን ዓመት ቆየህ፡፡ ወደ ምግብህና ወደ መጠጥህም ያልተለወጠ ሲኾን ተመልከት፡፡ ወደ አህያህም ተመልከት፡፡ ለሰዎችም አስረጅ እናደርግህ ዘንድ (ይህንን ሠሰራን)፡፡ ወደ ዐፅሞቹም እንዴት እንደምናስነሳት ከዚያም ሥጋን እንደምናለብሳት ተመልከት» አለው፡፡ ለርሱም (በማየት) በተገለጸለት ጊዜ፡- «አላህ በነገሩ ሁሉ ላይ ቻይ መኾኑን ዐውቃለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,260,'2:260','ኢብራሂምም «ጌታዬ ሆይ! ሙታንን እንዴት ሕያው እንደምታደርግ አሳየኝ» ባለ ጊዜ (አስታውስ፡፡) አላህ ፡- «አላመንክምን» አለው፡፡ «አይደለም (አምኛለሁ)፤ ግን ልቤ እንዲረጋ» ነው አለ፡፡ (አላህም)፡- «ከበራሪዎች (ከወፎች) አራትን ያዝ፡፡ ወደ አንተም ሰብስባቸው፡፡ (ቆራርጣቸውም)፤ ከዚያም በየኮረብታው ሁሉ ላይ ከነርሱ ቁራጭን አድርግ፡፡ ከዚያም ጥራቸው፤ ፈጥነው ይመጡሃልና፡፡ አላህም አሸናፊ ጥበበኛ መኾኑን ዕወቅ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,261,'2:261','የእነዚያ ገንዘቦቻቸውን በአላህ መንገድ የሚለግሱ ሰዎች (ልግስና) ምሳሌ በየዘለላው ሁሉ መቶ ቅንጣት ያለባቸውን ሰባት ዘለላዎች እንደ አበቀለች አንዲት ቅንጣት ብጤ ነው፡፡ አላህም ለሚሻው ሰው (ከዚህ በላይ) ያነባብራል፡፡ አላህም ችሮታው ሰፊ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,262,'2:262','እነዚያ ገንዘቦቻቸውን በአላህ መንገድ የሚለግሱ ከዚያም የሰጡትን ነገር መመጻደቅንና ማስከፋትን የማያስከትሉ ለእነርሱ በጌታቸው ዘንድ ምንዳቸው አላቸው፡፡ በእነርሱም ላይ ፍርሃት የለባቸውም፤ እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,263,'2:263','መልካም ንግግርና ምሕረት ማድረግ ማስከፋት ከሚከተላት ምጽዋት በላጭ ነው፡፡ አላህም ተብቃቂ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,264,'2:264','እናንተ ያመናችሁ ሆይ! እንደዚያ ገንዘቡን ለሰዎች ማሳየት ሲል እንደሚሰጠውና በአላህና በመጨረሻውም ቀን እንደማያምነው ሰው ምጽዋቶቻችሁን በመመጻደቅና በማስከፋት አታበላሹ፡፡ ምሳሌውም በላዩ ዐፈር እንዳለበት ለንጣ ድንጋይ ኃይለኛም ዝናብ እንዳገኘውና ምልጥ አድርጎ እንደተወው ብጤ ነው፡፡ ከሠሩት ነገር በምንም ላይ (ሊጠቀሙ) አይችሉም፡፡ አላህም ከሓዲያንን ሕዝቦች አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,265,'2:265','የእነዚያም የአላህን ውዴታ ለመፈለግና ለነፍሶቻቸው (እምነትን) ለማረጋገጥ ገንዘቦቻቸውን የሚለግሱ ሰዎች ምሳሌ በተስተካከለች ከፍተኛ ስፍራ ላይ እንዳለች አትክልት፣ ብዙ ዝናብ እንደነካት ፍሬዋንም እጥፍ ኾኖ እንደሰጠች፣ ዝናብም ባይነካት ካፊያ እንደሚበቃት ብጤ ነው፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,266,'2:266','(ከእናንተ) አንዳችሁ ለርሱ ከዘምባባና ከወይኖች በስርዋ ወንዞች የሚፈሱባት አትክልት ልትኖረው፣ በውስጧም ለርሱ ከየፍሬው ሁሉ ሊኖረው እርጅናም ሊነካውና ለርሱ ደካማዎች ዝርያዎች ያሉት ሲኾን በውስጡ እሳት ያለበት ውርጭ ሊያገኛትና ልትቃጠል ይወዳልን እንደዚሁ አላህ አንቀጾችን ለእናንተ ያብራራል፤ ልታስተነትኑ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(2,267,'2:267','እናንተ ያመናችሁ ሆይ! ካፈራችሁትና ከዚያም ለእናንተ ከምድር ካወጣንላችሁ ከመልካሙ ለግሱ፡፡ መጥፎውንም (ለመስጠት) አታስቡ፡፡ በእርሱ ዓይኖቻችሁን ጨፍናችሁ እንጂ የማትወስዱት ስትኾኑ ከእርሱ (ከመጥፎው) ትሰጣላችሁን አላህም ተብቃቂ ምስጉን መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,268,'2:268','ሰይጣን (እንዳትለግሱ) ድኽነትን ያስፈራራችኋል፡፡ በመጥፎም ያዛችኋል፡፡ አላህም ከርሱ የኾነን ምሕረትና ችሮታን ይቀጥራችኋል፡፡ አላህም ችሮታው ሰፊ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,269,'2:269','ለሚሻው ሰው ጥበብን ይሰጣል፤ ጥበብንም የሚስሰጥ ሰው ብዙን መልካም ነገር በእርግጥ ተሰጠ፡፡ የአእምሮዎች ባለቤቶች እንጂ (ሌላው) አይገሰጽም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,270,'2:270','ከምጽዋትም ማንኛውንም የለገሳችሁትን ወይም ከስለት የተሳላችሁትን አላህ ያውቀዋል፡፡ ለበዳዮችም ከረዳቶች ምንም የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,271,'2:271','ምጽዋቶችን ብትገልጹ እርሷ ምንኛ መልካም ናት፡፡ ብትደብቋትና ለድኾች ብትሰጧትም እርሱ ለናንተ በላጭ ነው፡፡ ከኃጢአቶቻችሁም ከእናንተ ያብሳል፡፡ አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,272,'2:272','እነርሱን ማቅናት በአንተ ላይ የለብህም፡፡ ግን አላህ የሚሻውን ሰው ያቀናል፡፡ ከገንዘብም የምትለግሱት (ምንዳው) ለነፍሶቻችሁ ነው፡፡ የአላህንም ፊት (ውዴታውን) ለመፈለግ እንጂ አትለግሱም፡፡ ከገንዘብም የምትለግሱት ሁሉ (ምንዳው) ወደናንተ ይሞላል፡፡ እናንተም አትበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,273,'2:273','(የምትመጸውቱት) ለእነዚያ በአላህ መንገድ (ለመታገል ሲሉ) ለታገዱት ድኾች ነው፡፡ በምድር መኼድን አይችሉም፡፡ (ልመናን) ከመጥጠበቃቸው (የተነሳ) የማያውቅ ሰው ሀብታሞች ናቸው ብሎ ይጠረጥራቸዋል፡፡ በምልክታቸው ታውቃቸዋለህ፡፡ ሰዎችን በችክታ አይለምኑም፡፡ ከገንዘብም የምትለግሱት አላህ እርሱን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,274,'2:274','እነዚያ ገንዘቦቻቸውን በሌሊትና በቀን በድብቅም በግልጽም የሚለግሱ ለእነርሱ በጌታቸው ዘንድ ምንዳቸው አላቸው፡፡ በእነርሱም ላይ ፍርሃት የለባቸውም እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,275,'2:275','እነዚያ አራጣን የሚበሉ ያ ሰይጣን ከመንካቱ የተነሳ የሚጥለው ሰው (ከአውድቁ) እንደሚነሳ ብጤ እንጅ (ከመቃብራቸው) አይነሱም፡፡ ይህ እነርሱ መሸጥ የአራጣ ብጤ ብቻ ነው በማለታቸው ነው፡፡ ግን መሸጥን አላህ ፈቅዷል፡፡ አራጣንም እርም አድርጓል፡፡ ከጌታውም ግሳጼ የመጣለትና የተከለከለ ሰው ለርሱ (ከመከልከሉ በፊት) ያለፈው አለው፡፡ ነገሩም ወደ አላህ ነው፤ (አራጣን ወደ መብላት) የተመለሰም ሰው እነዚያ የእሳት ጓዶች ናቸው፡፡ እነርሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,276,'2:276','አላህ አራጣን (በረከቱን) ያጠፋል፡፡ ምጽዋቶችንም ያፋፋል፡፡ አላህም ኃጠኢተኛ ከሓዲን ሁሉ አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,277,'2:277','እነዚያ ያመኑ፡፡ መልካም ሥራዎችንም የሠሩ፡፡ ሶላትንም ያስተካከሉ፡፡ ዘካንም የሰጡ ለነርሱ በጌታቸው ዘንድ ምንዳቸው አላቸው፡፡ በእነርሱም ላይ ፍርሃት የለባቸውም እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,278,'2:278','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፡፡ ከአራጣም የቀረውን ተዉ፡፡ አማኞች እንደኾናችሁ (ተጠንቀቁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,279,'2:279','(የታዘዛችሁትን) ባትሠሩም ከአላህና ከመልክተኛው በኾነች ጦር (መወጋታችሁን) ዕወቁ፡፡ ብትጸጸቱም ለእናንተ የገንዘቦቻችሁ ዋናዎች አሏችሁ፡፡ አትበድሉም አትበደሉምም፡፡');
INSERT INTO "sadiq_sani" VALUES(2,280,'2:280','የድኽነት ባለቤት የኾነም (ባለዕዳ) ሰው ቢኖር እማግኘት ድረስ ማቆየት ነው፡፡ (በመማር) መመጽወታችሁም ለእናንተ በላጭ ነው፡፡ የምታውቁ ብትኾኑ (ትሠሩታላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(2,281,'2:281','በእርሱም ውስጥ ወደ አላህ የምትመለሱበትንና ከዚያም እነርሱ የማይበደሉ ሲሆኑ ነፍስ ሁሉ የሠራችውን ሥራ የምትሞላበትን ቀን ተጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(2,282,'2:282','እናንተ ያመናችሁ ሆይ! እስከ ተወሰነ ጊዜ ድረስ በዕዳ በተዋዋላችሁ ጊዜ ጻፉት፡፡ ጸሐፊም በመካከላችሁ በትክክል ይጻፍ፡፡ ጸሐፊም አላህ እንደ አሳወቀው መጻፍን እንቢ አይበል፡፡ ይጻፍም፡፡ ያም በርሱ ላይ ዕዳው ያለበት ሰው በቃሉ ያስጽፍ፡፡ አላህንም ጌታውን ይፍራ፡፡ ከእርሱም (ካለበት ዕዳ) ምንንም አያጉድል፡፡ ያም በርሱ ላይ ዕዳው ያለበት ቂል፣ ወይም ደካማ፣ ወይም በቃሉ ማስጻፍን የማይችል ቢኾን ዋቢው በትክክል ያስጽፍለት፡፡ ከወንዶቻችሁም ሁለትን ምስክሮች አስመስክሩ፡፡ ሁለትም ወንዶች ባይኾኑ ከምስክሮች ሲኾኑ ከምትወዱዋቸው የኾኑን አንድ ወንድና አንደኛዋ ስትረሳ አንደኛይቱ ሌላዋን ታስታውሳት ዘንድ ሁለት ሴቶች (ይመስክሩ)፡፡ ምስክሮችም በተጠሩ ጊዜ እንቢ አይበሉ፡፡ (ዕዳው) ትንሽ ወይም ትልቅ ቢኾንም እስከ ጊዜው ድረስ የምትጽፉት ከመኾን አትሰልቹ፡፡ እንዲህ ማድረጋችሁ አላህ ዘንድ በጣም ትክክል ለምስክርነትም አረጋጋጭ ላለመጠራጠራችሁም በጣም ቅርብ ነው፡፡ ግን በመካከላችሁ እጅ በጅ የምትቀባበሏት ንግድ ብትኾን ባትጽፉዋት በናንተ ላይ ኃጢኣት የለባችሁም፡፡ በተሻሻጣችሁም ጊዜ አስመስክሩ፡፡ ጸሐፊም ምስክርም (ባለ ጉዳዩ ጋር) አይጎዳዱ፡፡ (ይህንን) ብትሠሩም እርሱ በእናንተ (የሚጠጋ) አመጽ ነው፡፡ አላህንም ፍሩ፡፡ አላህም ያሳውቃችኋል፡፡ አላህም ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,283,'2:283','በጉዞም ላይ ብትኾኑና ጸሐፊን ባታገኙ የተጨበጡ መተማመኛዎችን ያዙ፡፡ ከፊላችሁም ከፊሉን ቢያምን ያ የታመነው ሰው አደራውን ያድርስ፡፡ አላህንም ጌታውን ይፍራ፡፡ ምስክርነትንም አትደብቁ፡፡ የሚደብቃትም ሰው እርሱ ልቡ ኃጢኣተኛ ነው፡፡ አላህም የምትሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,284,'2:284','በሰማያት ውስጥና በምድርም ውስጥ ያለው ሁሉ የአላህ ነው፡፡ በነፍሶቻችሁ ውስጥ ያለውን ብትገልጹ ወይም ብትደብቁት አላህ በርሱ ይቆጣጠራችኋል፡፡ ለሚሻውም ሰው ይምራል፡፡ የሚሻውንም ሰው ይቀጣል፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(2,285,'2:285','መልክተኛው ከጌታው ወደ ርሱ በተወረደው አመነ፡፡ ምእምኖቹም (እንደዚሁ)፡፡ ሁሉም በአላህ፣ በመላዕክቱም፣ በመጻሕፍቱም፣ በመልክተኞቹም ከመልክተኞቹ «በአንድም መካከል አንለይም» (የሚሉ ሲኾኑ) አመኑ፡፡ «ሰማን፤ ታዘዝንም፡፡ ጌታችን ሆይ! ምሕረትህን (እንሻለን)፡፡ መመለሻም ወዳንተ ብቻ ነው» አሉም፡:');
INSERT INTO "sadiq_sani" VALUES(2,286,'2:286','አላህ ነፍስን ከችሎታዋ በላይ አያስገድዳትም፡፡ ለርስዋ የሠራችው አላት፡፡ በርስዋም ላይ ያፈራችው (ኀጢአት) አለባት፡፡ (በሉ)፡- ጌታችን ሆይ! ብንረሳ ወይም ብንስት አትያዘን፤ (አትቅጣን)፡፡ ጌታችን ሆይ! ከባድ ሸክምን ከእኛ በፊት በነበሩት ላይ እንደጫንከው በእኛ ላይ አትጫንብን፡፡ ጌታችን ሆይ! ለኛም በርሱ ችሎታ የሌለንን ነገር አታሸክመን፡፡ ከእኛም ይቅርታ አድርግ ለእኛም ምሕረት አድርግ፡፡ እዘንልንም፤ ዋቢያችን አንተ ነህና፡፡ በከሓዲዎች ሕዝቦች ላይም እርዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,1,'3:1','አ.ለ.መ (አሊፍ ላም ሚም)፤');
INSERT INTO "sadiq_sani" VALUES(3,2,'3:2','አላህ ከርሱ በስተቀር ሌላ አምላክ የለም፡፡ (እርሱ) ሕያው ሁሉን ነገር አስተናባሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,3,'3:3','ከርሱ በፊት ያሉትን መጻሕፍት የሚያረጋግጥ ሲኾን መጽሐፉን (ቁርኣንን) ባንተ ላይ ከፋፍሎ በእውነት አወረደ፡፡ ተውራትንና ኢንጂልንም አውርዷል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,4,'3:4','(ከቁርኣን) በፊት ለሰዎች መሪ አድርጎ (አወረዳቸው)፡፡ ፉርቃንንም አወረደ፡፡ እነዚያ በአላህ ተዓምራቶች የካዱ ለእነርሱ ብርቱ ቅጣት አላቸው፡፡ አላህም አሸናፊ የመበቀል ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,5,'3:5','አላህ በምድርም በሰማይም ምንም ነገር በርሱ ላይ አይሰወርም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,6,'3:6','እርሱ ያ በማሕጸኖች ውስጥ እንደሚሻ አድርጎ የሚቀርጻችሁ ነው፡፡ ከእርሱ በስተቀር ሌላ አምላክ የለም፡፡ አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,7,'3:7','እርሱ ያ በአንተ ላይ መጽሐፉን ያወረደ ነው፡፡ ከእርሱ (ከመጽሐፉ) ግልጽ የኾኑ አንቀጾች አልሉ፡፡ እነሱ የመጽሐፉ መሠረቶች ናቸው፡፡ ሌሎችም ተመሳሳዮች አልሉ፡፡ እነዚያማ በልቦቻቸው ውስጥ ጥመት ያለባቸው ሰዎች ማሳሳትን ለመፈለግና ትችቱን ለመፈለግ ከርሱ የተመሳሰለውን ይከታተላሉ፡፡ (ትክክለኛ) ትችቱንም አላህ ብቻ እንጅ ሌላ አያውቀውም፡፡ በዕውቀትም የጠለቁት «በርሱ አምነናል፤ ሁሉም ከጌታችን ዘንድ ነው» ይላሉ፡፡ የአእምሮ ባለቤቶችም እንጅ (ሌላው) አይገሰጽም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,8,'3:8','(እነሱም ይላሉ)፡- «ጌታችን ሆይ! ቅኑን መንገድ ከመራኸን በኋላ ልቦቻችንን አታዘምብልብን፡፡ ከአንተ ዘንድ የኾነን ችሮታም ለኛ ስጠን፡፡ አንተ በጣም ለጋስ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,9,'3:9','«ጌታችን ሆይ! አንተ በርሱ (ለመምጣቱ) ጥርጥር የሌለበት በኾነ ቀን ሰውን ሁሉ ሰብሳቢ ነህ፡፡ ምንጊዜም አላህ ቀጠሮውን አያፈርስምና፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,10,'3:10','እነዚያ የካዱት ሰዎች ገንዘቦቻቸውና ልጆቻቸው ከአላህ (ቅጣት) ምንም አያስጥሉዋቸውም፡፡ እነዚያም እነርሱ የእሳት ማገዶዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,11,'3:11','(ልማዳቸው ሁሉ) እንደፈርዖን ቤተሰብና እንደእነዚያ ከበፊታቸው እንደነበሩት ሕዝቦች ልማድ ነው፡፡ በአንቀጾቻችን አስተባበሉ፡፡ አላህም በኃጢአቶቻቸው ያዛቸው፡፡ አላህም ቅጣተብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,12,'3:12','ለእነዚያ ለካዱት ሰዎች በላቸው፡- «በቅርብ ጊዜ ትሸነፉላችሁ፡፡ ወደ ገሀነምም ትሰበሰባላችሁ፡፡ ምንጣፊቱም ምን ትከፋ!»');
INSERT INTO "sadiq_sani" VALUES(3,13,'3:13','(የበድር ቀን) በተጋጠሙት ሁለት ጭፍሮች ለናንተ በእርግጥ ተዓምር አልለ፡፡ አንደኛዋ ጭፍራ በአላህ መንገድ ትጋደላለች፡፡ ሌላይቱም ከሓዲ ናት፡፡ ከሓዲዎቹ (አማኞቹን) በዓይን አስተያየት እጥፋቸውን ኾነው ያዩዋቸዋል፡፡ አላህም በርዳታው የሚሻውን ሰው ያበረታል፡፡ በዚህ ውስጥ ለማስተዋል ባለቤቶች በእርግጥ መገሰጫ አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(3,14,'3:14','ከሴቶች፣ ከወንዶች ልጆችም፣ ከወርቅና ከብር፣ ከተካመቹ ገንዘቦችም፣ ከተሰማሩ ፈረሶችም፣ ከግመል ከከብትና ከፍየልም፣ ከአዝመራም የኾኑ ፍላጎቶችን መውደድ ለሰዎች ተሸለመ፡፡ ይህ ሁሉ የቅርቢቱ ሕይወት መጠቀሚያ ነው፡፡ አላህም እርሱ ዘንድ መልካም መመለሻ (ገነት) አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,15,'3:15','«ከዚህ ሁሉ ነገር የሚበልጥን ልንገራችሁን» በላቸው፡፡ (እርሱም) «ለእነዚያ ለተጠነቀቁት ሰዎች በጌታቸው ዘንድ በሥራቸው ወንዞች የሚፈሱባቸው ገነቶች በውስጧ ሁልጊዜ ነዋሪዎች ሲኾኑ ንጹሕ የተደረጉ ሚስቶችም፣ ከአላህም የኾነ ውዴታ አላቸው፡፡ አላህም ባሮቹን ተመልካች ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,16,'3:16','(እነርሱም) እነዚያ፡- «ጌታችን ሆይ! እኛ አመንን ኀጢአቶቻችንንም ለእኛ ማር፤ የእሳትንም ቅጣት ጠብቀን» የሚሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,17,'3:17','ታጋሾች እውነተኞችም ታዛዦችም ለጋሶችና በሌሊት መጨረሻዎች ምሕረትን ለማኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,18,'3:18','አላህ በማስተካከል የቆመ (አስተናባሪ) ሲኾን ከእርሱ በስተቀር ሌላ አምላክ የሌለ መኾኑን መሰከረ፡፡ መላእክቶችና የዕውቀት ባለቤቶችም (እንደዚሁ መሰከሩ)፡፡ ከእርሱ በስተቀር ሌላ አምላክ የለም፡፡ አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,19,'3:19','አላህ ዘንድ (የተወደደ) ሃይማኖት ኢስላም ብቻ ነው፡፡ እነዚያም መጽሐፉን የተሰጡት ሰዎች በመካከላቸው ላለው ምቀኝነት ዕውቀቱ ከመጣላቸው በኋላ እንጅ አልተለያዩም፡፡ በአላህም አንቀጾች የሚክድ አላህ ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,20,'3:20','ቢከራከሩህም፡- «ፊቴን ለአላህ ሰጠሁ፤ የተከተሉኝም ሰዎች (እንደዚሁ ለአላህ ሰጡ)» በላቸው፡፡ ለእነዚያም መጽሐፍን ለተሰጡት ሰዎችና ለመሐይሞቹ፡- «ሰለማችሁን» በላቸው፡፡ ቢሰልሙም በእርግጥ ተመሩ፡፡ እምቢ ቢሉም በአንተ ላይ ያለብህ ማድረስ ብቻ ነው፡፡ አላህም ባሮቹን ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,21,'3:21','እነዚያ በአላህ አንቀጾች የሚክዱ ነቢያትንም ያለ አግባብ የሚገድሉ ከሰዎችም ውስጥ እነዚያን በትክክለኛነት የሚያዙትን የሚገድሉ በአሳማሚ ቅጣት አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,22,'3:22','እነዚያ በቅርቢቱና በመጨረሻይቱ ዓለም ሥራዎቻቸው የተበላሹ ናቸው፡፡ ለእነርሱም ምንም ረዳቶች የሏቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,23,'3:23','ወደ እነዚያ ከመጽሐፉ (ትምህርት) ዕድልን ወደ ተሰጡት ሰዎች አላየህምን ወደ አላህ መጽሐፍ በመካከላቸው ይፈርድ ዘንድ ይጠራሉ፡፡ ከዚያም ከእነሱ ገሚሶቹ እነርሱ (እውነቱን) የተዉ ኾነው ይሸሻሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,24,'3:24','ይኸ እነርሱ «እሳት የተቆጠሩ ቀኖችን እንጅ አትነካንም» በማለታቸው ነው፡፡ በሃይማኖታቸውም ይቀጣጥፉት የነበሩት ነገር አታለላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,25,'3:25','በርሱ (መምጣት) ጥርጥር የሌለበት በኾነው ቀን በሰበሰብናቸውና ነፍስ ሁሉ የሠራችውን ሥራ በተሞላች ጊዜ እንዴት ይኾናሉ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,26,'3:26','(ሙሐመድ ሆይ!) በል፡- «የንግስና ባለቤት የኾንክ አላህ ሆይ! ለምትሻው ሰው ንግሥናን ትሰጣለህ፡፡ ከምትሻውም ሰው ንግሥናን ትገፍፋለህ፡፡ የምትሻውንም ሰው ታልቃለህ፡፡ የምትሻውንም ሰው ታዋርዳለህ፡፡ መልካም ነገር ሁሉ በእጅህ (በችሎታህ) ነው፤ አንተ በነገሩ ሁሉ ላይ ቻይ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,27,'3:27','ሌሊቱን በቀን ውስጥ ታስገባለህ፡፡ ቀኑንም በሌሊቱ ውስጥ ታስገባለህ፡፡ ሕያውንም ከሙት ውስጥ ታወጣለህ፡፡ ሙትንም ከሕያው ውስጥ ታወጣለህ፡፡ ለምትሻውም ሰው ያለግምት ትሰጣለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,28,'3:28','ምእምናን ከሓዲዎችን ከምእምናን ሌላ ረዳቶች አድርገው አይያዙ፡፡ ይኼንንም የሚሠራ ሰው ከአላህ (ሃይማኖት) በምንም ውስጥ አይደለም፡፡ ከእነርሱ መጥጠበቅን ብትጠበቁ እንጂ፡፡ አላህም ነፍሱን (ቁጣውን) ያስጠነቅቃችኋል፡፡ መመለሻም ወደ አላህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,29,'3:29','«በደረቶቻችሁ ውስጥ ያለውን ነገር ብትደብቁ ወይም ብትገልፁት አላህ ያውቀዋል፡፡ በሰማያት ያለውንና በምድርም ያለውን ሁሉ ያውቃል፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው» (በል)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,30,'3:30','ነፍስ ሁሉ ከመልካም ነገር የሠራችውን የቀረበ ኾኖ የምታገኝበትን ቀን (አስታውስ)፡፡ ከመጥፎም የሠራችው በርስዋና በርሱ (በኀጢአትዋ) መካከል በጣም የሰፋ ርቀት ቢኖር ትመኛለች፡፡ አላህ ነፍሱን ያስጠነቅቃችኋል፡፡ አላህም ለባሮቹ ሩኅሩኅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,31,'3:31','በላቸው፡- «አላህን የምትወዱ እንደኾናችሁ ተከተሉኝ፤ አላህ ይወዳችኋልና፡፡ ኀጢኣቶቻችሁንም ለናንተ ይምራልና፡፡ አላህም መሓሪ አዛኝ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,32,'3:32','«አላህንና መልክተኛውን ታዘዙ፡፡ ብትሸሹም አላህ ከሓዲዎችን አይወድም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,33,'3:33','አላህ አደምን፣ ኑሕንም፣ የኢብራሂምንም ቤተሰብ፣ የዒምራንንም ቤተሰብ በዓለማት ላይ መረጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,34,'3:34','ከፊልዋ ከከፊሉ የኾነች ዝርያ አድርጎ (መረጣት)፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,35,'3:35','የዒምራን ባለቤት (ሐና) «ጌታዬ ሆይ! እኔ በሆዴ ውስጥ ያለውን (ፅንስ ከሥራ) ነጻ የተደረገ ሲኾን ለአንተ ተሳልኩ፡፡ ከእኔም ተቀበል አንተ ሰሚው ዐዋቂው ነህና» ባለች ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,36,'3:36','በወለደቻትም ጊዜ፡- «ጌታዬ ሆይ! እኔ ሴት ኾና ወለድኋት፡፡» አላህም የወለደችውን ዐዋቂ ነው፡፡ «ወንድም እንደ ሴት አይደለም፡፡ እኔም መርየም ብዬ ስም አወጣሁላት፡፡ እኔም እስዋንም ዝርያዋንም ከተረገመው ሰይጣን በአንተ እጠብቃቸዋለሁ» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(3,37,'3:37','ጌታዋም በደህና አቀባበል ተቀበላት፡፡ በመልካም አስተዳደግም አፋፋት፡፡ ዘከሪያም አሳደጋት፤ ዘከሪያ በርሷ ላይ በምኹራቧ በገባ ቁጥር እርስዋ ዘንድ ሲሳይን አገኘ፡፡ «መርየም ሆይ! ይህ ለአንቺ ከየት ነው» አላት፡፡ «እርሱ ከአላህ ዘንድ ነው፡፡ አላህ ለሚሻው ሰው ሲሳዩን ያለ ድካም ይሰጣል» አለችው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,38,'3:38','እዚያ ዘንድ ዘከሪያ ጌታውን ለመነ፡፡ «ጌታዬ ሆይ! ከአንተ ዘንድ መልካም ዘርን ለኔ ስጠኝ አንተ ጸሎትን ሰሚነህና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,39,'3:39','እርሱም በጸሎት ማድረሻው ክፍል ቆሞ ሲጸልይ መላእክት ጠራችው፡፡ «አላህ በየሕያ ከአላህ በኾነ ቃል የሚያረጋግጥ ጌታም ድንግልም ከደጋጎቹ ነቢይም ሲኾን ያበስርሃል» በማለት (መላእክት ጠራችው)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,40,'3:40','«ጌታዬ ሆይ! እኔ እርጅና በእርግጥ የደረሰብኝ ስኾን ባልተቤቴም መሐን ስትሆን ለእኔ እንዴት ልጅ ይኖረኛል አለ፡፡ (መልአኩም) እንደዚሁ አላህ የሚሻውን ነገር ይሠራል» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,41,'3:41','«ጌታዬ ሆይ! ለእኔ ምልክትን አድርግልኝ» አለ፡፡ «ምልክትህ ሦስት ቀን በጥቅሻ ቢኾን እንጅ ሰዎችን አለማናገርህ ነው፡፡ ጌታህንም በብዙ አውሳ፡፡ በማታና በጧትም አወድሰው» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,42,'3:42','መላእክትም ያሉትን (አስታውስ)፡፡ «መርየም ሆይ! አላህ በእርግጥ መረጠሸ፡፡ አነጻሽም፡፡ በዓለማት ሴቶችም ላይ መረጠሽ፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,43,'3:43','«መርየም ሆይ! ለጌታሽ ታዘዢ፡፡ ስገጂም፡፡ ከአጎንባሾቹም ጋር አጎንብሺ፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,44,'3:44','ይኸ ወደ አንተ የምናወርደው የኾነ ከሩቅ ወሬዎች ነው፡፡ መርየምንም ማን እንደሚያሳድግ ብርኦቻቸውን (ለዕጣ) በጣሉ ጊዜ እነሱ ዘንድ አልነበርክም፡፡ በሚከራከሩም ጊዜ እነሱ ዘንድ አልነበርክም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,45,'3:45','መላእክት ያሉትን (አስታውስ)፡- «መርየም ሆይ! አላህ ከርሱ በኾነው ቃል ስሙ አልመሲሕ ዒሳ የመርየም ልጅ በዚህ ዓለምና በመጨረሻውም ዓለም የተከበረ ከባለሟሎችም በኾነ (ልጅ) ያበስርሻል፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,46,'3:46','«በሕፃንነቱና በከፈኒሳነቱ ሰዎቹን ያነጋግራል፡፡ ከመልካሞቹም ነው» (አላት)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,47,'3:47','፡-ጌታዬ ሆይ! ሰው ያልነካኝ ስኾን ለኔ እንዴት ልጅ ይኖረኛል አለች፡፡ ነገሩ እንዳልሽው ነው፡፡ አላህ የሚሻውን ይፈጥራል፡፡ አንዳችን በሻ ጊዜ ለርሱ ኹን ይለዋል፤ ወዲውኑም ይኾናል አላት፡፡');
INSERT INTO "sadiq_sani" VALUES(3,48,'3:48','ጽሕፈትንና ጥበብንም ተውራትንና ኢንጂልንም ያስተምረዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,49,'3:49','ወደ እስራኤልም ልጆች መልክተኛ ያደርገዋል፡፡ (ይላልም)፡- «እኔ ከጌታዬ ዘንድ በተዓምር ወደ እናንተ መጣሁ፡፡ እኔ ለናንተ ከጭቃ እንደ ወፍ ቅርፅ እፈጥራለሁ፡፡ በርሱም እተነፍስበታለሁ፡፡ በአላህም ፈቃድ ወፍ ይኾናል፡፡ በአላህም ፈቃድ ዕውር ኾኖ የተወለደን፣ ለምጸኛንም አድናለሁ፡፡ ሙታንንም አስነሳለሁ፡፡ የምትበሉትንና በቤታችሁ የምታደልቡትንም ሁሉ እነግራችኋለሁ፡፡ የምታምኑ እንደኾናችሁ ለእናንተ በዚህ ውስጥ በእርግጥ ተዓምር አለበት፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,50,'3:50','«ከተውራትም ከእኔ በፊት ያለውን ያረጋገጥሁ ስኾን የዚያንም በእናንተ ላይ እርም የተደረገውን ከፊል ለእናንተ እፈቅድ ዘንድ (መጣኋችሁ) ከጌታችሁም በኾነ ታምር መጣሁዋችሁ፡፡ አላህንም ፍሩ፤ ታዘዙኝም፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,51,'3:51','«አላህ ጌታዬና ጌታችሁ ነው፤ ስለዚህ ተገዙት፤ ይህ ቀጥተኛ መንገድ ነው» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,52,'3:52','ዒሳ ከነርሱ ክህደት በተሰማውም ጊዜ፡- «ወደ አላህ (ተጨምረው) ረዳቶቼ እነማን ናቸው» አለ፤ ሐዋርያት፡- «እኛ የአላህ ረዳቶች ነን፤ በአላህ አምነናል፤ እኛም ትክክለኛ ታዛዦች መኾናችንን መስክር» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,53,'3:53','«ጌታችን ሆይ! ባወረድኸው አመንን፤ መልክተኛውንም ተከተልን፤ ከመስካሪዎችም ጋር መዝግበን» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,54,'3:54','(አይሁዶችም) አደሙ አላህም አድማቸውን መለሰባቸው፡፡ አላህም ከአድመኞች ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,55,'3:55','አላህም ባለ ጊዜ (አስታውስ)፡፡ ዒሳ ሆይ! እኔ ወሳጂህ ወደ እኔም አንሺህ ነኝ፡፡ ከነዚያም ከካዱት ሰዎች አጥሪህ ነኝ እነዚያንም የተከተሉህን እስከ ትንሣኤ ቀን ድረስ ከነዚያ ከካዱት በላይ አድራጊ ነኝ፡፡ ከዚያም መመለሻችሁ ወደእኔ ነው፡፡ በርሱ ትለያዩበትም በነበራችሁት ነገር በመካከላችሁ እፈርዳለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,56,'3:56','«እነዚያማ የካዱትን ሰዎች በቅርቢቱ ዓለምና በመጨረሻይቱ ዓለም ብርቱን ቅጣት እቀጣቸዋለሁ፡፡ ለእነርሱም ምንም ረዳቶች የሏቸውም፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,57,'3:57','እነዚያም ያመኑትማ መልካም ሥራዎችንም የሠሩት ምንዳዎቻቸውን ይሞላላቸዋል፡፡ አላህም በዳዮችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,58,'3:58','ይህ ከተዓምራቶችና ጥበብን ከያዘው ተግሳጽ ሲኾን በአንተ ላይ እናነበዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,59,'3:59','አላህ ዘንድ የዒሳ ምሳሌ እንደ አዳም ብጤ ነው፡፡ ከዐፈር ፈጠረው፡፡ ከዚያም ለርሱ (ሰው) «ኹን» አለው፤ ኾነም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,60,'3:60','ይህ ከጌታህ ዘንድ የኾነ እውነት ነው፡፡ ከተጠራጣሪዎቹም አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,61,'3:61','ዕውቀቱም ከመጣልህ በኋላ በርሱ (በዒሳ) የተከራከሩህን ሰዎች «ኑ፤ ልጆቻችንንና ልጆቻችሁን፣ ሴቶቻችንንና ሴቶቻችሁንም፣ ነፍሶቻችንንና ነፍሶቻችሁንም እንጥራ፡፡ ከዚያም አጥብቀን አላህን እንለምን፤ የአላህንም ቁጣ በውሸታሞቹ ላይ እናድርግ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,62,'3:62','ይህ እርሱ በእርግጥ እውነተኛ ታሪክ ነው፡፡ አምላክም ከአላህ በስተቀር ምንም የለም፡፡ አላህም እርሱ በእርግጥ አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,63,'3:63','(ከማመን) እንቢ ቢሉም አላህ አጥፊዎቹን በእርግጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,64,'3:64','የመጽሐፉ ባለቤቶች ሆይ! በእኛና በእናንተ መካከል ትክክል ወደ ኾነች ቃል ኑ፡፡ (እርሷም) አላህን እንጅ ሌላን ላንገዛ፣ በርሱም ምንንም ላናጋራ፣ ከፊላችንም ከፊሉን ከአላህ ሌላ አማልክት አድርጎ ላይይዝ ነው፤ በላቸው፡፡ እምቢ ቢሉም፡- እኛ ሙስሊሞች መኾናችንን መስክሩ በሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,65,'3:65','እናንተ የመጽሐፉ ሰዎች ሆይ! በኢብራሂም ለምን ትከራከራላችሁ ተውራትና ኢንጂልም ከርሱ በኋላ እንጅ አልተወረዱም፡፡ ልብ አታደርጉምን በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,66,'3:66','ንቁ! እናንተ እነዚያ ለእናንተ በርሱ ዕውቀት ባላችሁ ነገር የተከራከራችሁ ናችሁ፡፡ ታዲያ ለእናንተ በርሱ ዕውቀት በሌላችሁ ነገር ለምን ትከራከራላችሁ አላህም ያውቃል፡፡ እናንተ ግን አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,67,'3:67','ኢብራሂም ይሁዳዊም ክርስቲያንም አልነበረም፡፡ ግን ወደ ቀጥተኛው ሃይማኖት የተዘነበለ ሙስሊም ነበረ፡፡ ከአጋሪዎችም አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,68,'3:68','ከሰው ሁሉ በኢብራሂም ተገቢዎቹ እነዚያ የተከተሉትና ይህ ነቢይ እነዚያም ያመኑት ናቸው፡፡ አላህም የምእምናን ረዳት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,69,'3:69','ከመጽሐፉ ባለቤቶች የኾኑ ጭፍሮች ነፍሶቻቸውን እንጂ የማያሳስቱ ሲኾኑ ሊያሳስቱዋችሁ ተመኙ ግን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,70,'3:70','የመጽሐፉ ባለቤቶች ሆይ! እናንተ የምታውቁ ስትኾኑ በአላህ አንቀጾች ለምን ትክዳላችሁ');
INSERT INTO "sadiq_sani" VALUES(3,71,'3:71','«የመጽሐፉ ሰዎች ሆይ! እውነቱን በውሸት ለምን ትቀላቅላላችሁ እውነቱንም እናንተ የምታውቁ ስትኾኑ ለምን ትደብቃላችሁ»');
INSERT INTO "sadiq_sani" VALUES(3,72,'3:72','ከመጽሐፉ ባለቤቶች የኾኑ ጭፍሮችም አሉ፡- «በነዚያ በአመኑት ላይ በተወረደው (ቁርኣን) በቀኑ መጀመሪያ ላይ እመኑበት፡፡ በመጨረሻውም ካዱት፡፡ እነርሱ ሊመለሱ ይከጀላልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,73,'3:73','እናንተ የተሰጣችሁትን ብጤ አንድም ሰው መሰጠቱን ወይም እጌታችሁ ዘንድ የሚከራከሩዋችሁ መኾንን ሃይማኖታችሁን ለተከተለ ሰው ቢኾን እንጂ አትመኑ (አትግለጹ አሉ)፡፡ «መምሪያው የአላህ መምሪያ ብቻ ነው» በላቸው፡፡ «ችሮታ በአላህ እጅ ነው፡፡ ለሚሻው ይሰጠዋል፡፡ አላህም ችሮታው ሰፊ ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,74,'3:74','በችሮታው (በነቢይነት) የሚሻውን ይመርጣል፡፡ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,75,'3:75','ከመጽሐፉ ሰዎችም በብዙ ገንዘብ ብታምነው ወዳንተ የሚመልስ ሰው አልለ፡፡ ከእነሱም በአንድ ዲናር እንኳ ብታምነው፤ ሁል ጊዜ በርሱ ላይ የምትጠባበቅ ካልኾንክ በስተቀር የማይመልስ ሰው አልለ፡፡ «ይህ በመሃይምናን (በምናደርገው) በእኛ ላይ ምንም መንገድ የለብንም» ስለሚሉ ነው፡፡ እነርሱ እያወቁም በአላህ ላይ ውሸትን ይናገራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,76,'3:76','አይደለም (አለባቸው እንጅ)፡፡ በቃል ኪዳኑ የሞላና የተጠነቀቀ ሰው አላህ ጥንቁቆችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,77,'3:77','እነዚህ በአላህ ቃል ኪዳንና በመሐላዎቻቸው ጥቂትን ዋጋ የሚለውጡ እነዚያ በመጨረሻይቱ ዓለም ምንም ዕድል የላቸውም፡፡ በትንሣኤ ቀንም አላህ አያነጋግራቸውም፡፡ ወደ እነርሱም አይመለከትም አያነጻቸውምም፡፡ ለነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,78,'3:78','ከእነሱም እርሱ ከመጽሐፉ ያይደለ ሲኾን ከመጽሐፉ መኾኑን እንድታስቡ በመጽሐፉ ምላሶቻቸውን የሚያጣምሙ ክፍሎች አሉ፡፡ እርሱ ከአላህ ዘንድ ያልኾነ ሲኾን «እርሱ ከአላህ ዘንድ ነውም» ይላሉ፡፡ እነርሱ የሚያውቁ ሲኾኑም በአላህ ላይ ውሸትን ይናገራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,79,'3:79','ለማንም ሰው አላህ መጽሐፍንና ጥበብን ነቢይነትንም ሊሰጠውና ከዚያም ለሰዎች «ከአላህ ሌላ ለእኔ ባሮች ኾኑ» ሊል አይገባውም፡፡ «ግን መጽሐፍን የምታስተምሩ በነበራችሁትና የምታጠኑም በነበራችሁት በዕውቀታችሁ ሠሪዎች ኹኑ» (ይላቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,80,'3:80','መላእክትንና ነቢያትንም አማልክቶች አድርጋችሁ እንድትይዙ ሊያዛችሁ (አይገባውም)፡፡ እናንተ ሙስሊሞች ከኾናችሁ በኋላ በክህደት ያዛችኋልን');
INSERT INTO "sadiq_sani" VALUES(3,81,'3:81','አላህ የነቢያትን ቃል ኪዳን ከመጽሐፍና ከጥበብ ሰጥቻችሁ ከዚያም «ከእናንተ ጋር ላለው (መጽሐፍ) የሚያረጋግጥ መልክተኛ ቢመጣላችሁ በርሱ በእርግጥ እንደምታምኑበት በእርግጥም እንድትረዱት ሲል በያዘ ጊዜ (አስታውስ)፡፡ አረጋገጣችሁን በዚህም ላይ ኪዳኔን ያዛችሁምን» አላቸው፡፡ «አረጋገጥን» አሉ፡፡ «እንግዲያስ መስክሩ፤ እኔም ከእናንተ ጋር ከመስካሪዎቹ ነኝ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,82,'3:82','ከዚህም በኋላ የሸሹ ሰዎች እነዚያ እነሱ አመጸኞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,83,'3:83','በሰማያትና በምድር ያሉ ሁሉ፤ በውድም በግድም ለርሱ የታዘዙ ወደርሱም የሚመለሱ ሲኾኑ (ከሓዲዎች) ከአላህ ሃይማኖት ሌላን ይፈልጋሉን');
INSERT INTO "sadiq_sani" VALUES(3,84,'3:84','«በአላህ አመንን፡፡ በእኛ ላይ በተወረደውም (በቁርኣን)፣ በኢብራሂምና በኢስማዒልም፣ በኢስሓቅም፣ በያዕቆብም፣ በነገዶችም ላይ በተወረደው፤ ለሙሳና ለዒሳም ለነቢያትም ሁሉ ከጌታቸው በተሰጠው (አመንን)፡፡ ከእነርሱ መካከል አንድንም አንለይም፡፡ እኛ ለእርሱ ታዛዦች ነን» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,85,'3:85','ከኢስላም ሌላ ሃይማኖትን የሚፈልግ ሰው ፈጽሞ ከርሱ ተቀባይ የለውም፡፡ እርሱም በመጨረሻይቱ ዓለም ከከሳሪዎቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,86,'3:86','ከእምነታቸውና መልክተኛውም እውነት መኾኑን ከመሰከሩ የተብራሩ አንቀጾችም ከመጡላቸው በኋላ የካዱን ሕዝቦች አላህ እንዴት ያቀናል! አላህ በዳዮችንም ሕዝቦች አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,87,'3:87','እነዚያ ቅጣታቸው የአላህና የመላእክት የሰዎችም ሁሉ ርግማን በእነሱ ላይ መኾን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,88,'3:88','በውስጧ ዘውታሪዎች ሲኾኑ ቅጣቱም ከነሱ ላይ አይቃለልላቸውም፡፡ እነሱም ቀን አይስሰጡም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,89,'3:89','እነዚያ ከዚህ በኋላ ንስሓ የገቡና (ሥራቸውን) ያሳመሩ ሲቀሩ፡፡ (እነዚህንስ ይምራቸዋል)፤ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(3,90,'3:90','እነዚያ ከእምነታቸው በኋላ የካዱ ከዚያም ክህደትን የጨመሩ ጸጸታቸው ፈጽሞ ተቀባይ የላትም፡፡ እነዚያም የተሳሳቱ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,91,'3:91','እነዚያ የካዱና እነርሱም በክህደታቸው ላይ እንዳሉ የሞቱ ከእነሱ ከአንዳቸው በምድር ሙሉ የኾነ ወርቅ (ቢኖረውና) በርሱ ቢበዥበትም እንኳ ፈጽሞ ተቀባይን አያገኝም፡፡ እነዚያ ለእነርሱ የሚያሰቃይ ቅጣት አላቸው፡፡ ለነርሱም ምንም ረዳቶች የሏቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,92,'3:92','የምትወዱትን እስከምትለግሱ ድረስ በጎን ሥራ (ዋጋውን) አታገኙም፡፡ ከምንም ነገር ብትለግሱ አላህ ያውቀዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,93,'3:93','ተውራት ከመወረድዋ በፊት እስራኤል በራሱ ላይ እርም ካደረገው ነገር በስተቀር ምግብ ሁሉ ለእስራኤል ልጆች የተፈቀደ ነበር፡፡ እውነተኞችም እንደኾናችሁ ተውራትን አምጡ አንብቡዋትም በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,94,'3:94','ከዚህም በኋላ በአላህ ላይ ውሸትን የቀጣጠፉ ሰዎች እነዚያ እነርሱ በደለኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,95,'3:95','«አላህ እውነትን ተናገረ፡፡ የኢብራሂምንም መንገድ ወደ እውነት ያዘነበለ ሲኾን ተከተሉ፡፡ ከአጋሪዎቹም አልነበረም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,96,'3:96','ለሰዎች (መጸለያ) መጀመሪያ የተኖረው ቤት ብሩክና ለዓለማት ሁሉ መመሪያ ሲኾን ያ በበካህ (በመካ) ያለው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,97,'3:97','በውስጡ ግልጽ የኾኑ ተዓምራቶች የኢብራሒምም መቆሚያ አልለ፡፡ ወደ ውስጡ የገባ ሰው ጸጥተኛ ይኾናል፡፡ ለአላህም በሰዎች ላይ ወደርሱ መኼድን በቻለ ሁሉ ላይ ቤቱን መጎብኘት ግዴታ አለባቸው፡፡ የካደም ሰው አላህ ከዓለማት ሁሉ የተብቃቃ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,98,'3:98','«የመጽሐፉ ባለቤቶች ሆይ! አላህ በምትሠሩት ሁሉ ላይ ዐዋቂ ሲኾን በአላህ ተዓምራት ለምን ትክዳላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,99,'3:99','«የመጽሐፉ ሰዎች ሆይ! እናንተ መስካሪዎች ኾናችሁ ከአላህ መንገድ መጥመሟን የምትፈልጓት ስትኾኑ ያመነን ሰው ለምን ትከለክላላችሁ አላህም ከምትሰሩት ነገር ሁሉ ዘንጊ አይደለም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,100,'3:100','እናንተ ያመናችሁ ሆይ! ከነዚያ መጽሐፉን ከተሰጡት ሰዎች ከፊሉን ብትታዘዙ ከእምነታችሁ በኋላ ከሓዲዎች አድርገው ይመልሱዋችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,101,'3:101','የአላህም አንቀጾች በእናንተ ላይ የሚነበቡ ሲኾኑ መልክተኛውም በውስጣችሁ ያለ ሲኾን እናንተ እንዴት ትክዳላችሁ በአላህም የሚጠበቅ ሰው ወደ ቀጥተኛው መንገድ በእርግጥ ተመራ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,102,'3:102','እናንተ ያመናችሁ ሆይ! አላህን ተገቢውን መጠንቀቅ ተጠንቀቁት፡፡ እናንተም ሙስሊሞች ኾናችሁ እንጅ አትሙቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,103,'3:103','የአላህንም (የማመን) ገመድ ሁላችሁም ያዙ፡፡ አትለያዩም፡፡ ጠበኞችም በነበራችሁ ጊዜ በእናንተ ላይ (የዋለውን) የአላህን ጸጋ አስታውሱ፡፡ በልቦቻችሁም መካከል አስማማ፡፡ በጸጋውም ወንድማማቾች ኾናችሁ፡፡ በእሳት ጉድጓድ አፋፍም ላይ ነበራችሁ፡፡ ከእርስዋም አዳናችሁ፡፡ እንደዚሁ ትመሩ ዘንድ አላህ ለእናንተ አንቀጾቹን ያብራራል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,104,'3:104','ከናንተም ወደ በጎ ነገር የሚጠሩ በመልካም ሥራም የሚያዙ ከክፉ ነገርም የሚከለክሉ ሕዝቦች ይኑሩ፡፡ እነዚያም እነሱ የሚድኑ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,105,'3:105','እንደነዚያም ግልጽ ተዓምራት ከመጣላቸው በኋላ እንደተለያዩትና እንደ ተጨቃጨቁት አትኹኑ፡፡ እነዚያም ለእነርሱ ታላቅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,106,'3:106','ፊቶች የሚያበሩበትን ፊቶችም የሚጠቁሩበትን ቀን (አስታውስ)፡፡ እነዚያ ፊቶቻቸው የጠቆሩትማ «ከእምነታችሁ በኋላ ካዳችሁን ትክዱት በነበራችሁት ነገር ቅጣቱን ቅመሱ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,107,'3:107','እነዚያም ፊቶቻቸው ያበሩትማ በአላህ ችሮታ (ገነት) ውስጥ ናቸው፡፡ እነርሱ በውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,108,'3:108','ይህች በአንተ ላይ በእውነት የምናነባት ስትኾን የአላህ ተዓምራት ናት፡፡ አላህም ለዓለማት በደልን የሚሻ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,109,'3:109','በሰማያት ያለውና በምድር ያለውም ሁሉ የአላህ ነው፡፡ ነገሮችም ሁሉ ወደ አላህ ብቻ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,110,'3:110','ለሰዎች ከተገለጸች ሕዝብ ሁሉ በላጭ ኾናችሁ በጽድቅ ነገር ታዛላችሁ፤ ከመጥፎም ነገር ታዛላችሁ፤ በአላህም (አንድነት) ታምናላችሁ፡፡ የመጽሐፉም ሰዎች ባመኑ ኖሮ ለነርሱ የተሻለ በኾነ ነበር፡፡ ከነርሱ አማኞች አሉ፡፡ አብዛኞቻቸው ግን አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,111,'3:111','ማስከፋትን እንጅ ፈጽሞ አይጎዱዋችሁም፡፡ ቢዋጉዋችሁም ጀርባዎችን ያዙሩላችኋል (ይሸሻሉ)፡፡ ከዚያም አይረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,112,'3:112','የትም በተገኙበት ስፍራ ከአላህ በኾነ ቃል ኪዳን ከሰዎችም በኾነ ቃል ኪዳን (የተጠበቁ) ካልኾኑ በስተቀር በነርሱ ላይ ውርደት ተመታችባቸው፡፡ ከአላህም በኾነ ቁጣ ተመለሱ፡፡ በእነርሱም ላይ ድኽነት ተመታችባቸው፡፡ ይኸ እነርሱ በአላህ ተዓምራት ይክዱ ስለ ነበሩና ነቢያትንም ያለ ሕግ ይገድሉ ስለነበሩ ነው፡፡ ይህ በማመጻቸውና ወሰንን ይተላለፉ ስለነበሩ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,113,'3:113','(የመጽሐፉ ሰዎች ሁሉ) እኩል አይደሉም፡፡ ከመጽሐፉ ሰዎች ቀጥ ያሉ በሌሊት ሰዓቶች እነርሱ የሚሰግዱ ኾነው የአላህን አንቀጾች የሚያነቡ ሕዝቦች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,114,'3:114','በአላህና በመጨረሻው ቀን ያምናሉ፡፡ በጽድቅ ነገርም ያዛሉ፡፡ ከመጥፎ ነገርም ይከለክላሉ፡፡ በበጎ ሥራዎችም ይጣደፋሉ፡፡ እነዚያም ከመልካሞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,115,'3:115','ከበጎ ሥራ ማንኛውንም ቢሠሩ አይካዱትም፡፡ አላህም ጥንቁቆቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,116,'3:116','እነዚያ የካዱት ሰዎች ገንዘቦቻቸውና ልጆቻቸው ከአላህ (ቅጣት) እነርሱን ምንንም አያድኑዋቸውም፡፡ እነዚያም የእሳት ጓዶች ናቸው፡፡ እነርሱ በእርስዋ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,117,'3:117','የዚያ በዚህች በቅርቢቱ ሕይወት የሚለግሱት ነገር ምሳሌ በውስጧ ውርጭ ያለባት ነፋስ ነፍሶቻቸውን የበደሉን ሕዝቦች አዝመራ እንደነካችና እንዳጠፋችው ብጤ ነው፡፡ አላህም አልበደላቸውም ግን ነፍሶቻቸውን ይበድላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,118,'3:118','እናንተ ያመናችሁ ሆይ! ከእናንተ ሌላ ምስጢረኛን አትያዙ፡፡ ማበላሸትን አይገቱላችሁም፡፡ ጉዳታችሁን ይወዳሉ፡፡ ጥላቻይቱ ከአፎቻቸው በእርግጥ ተገለጸች፡፡ ልቦቻቸውም የሚደብቁት ይበልጥ ትልቅ ነው፡፡ ልብ የምታደርጉ እንደኾናችሁ ለእናንተ ማብራሪያዎችን በእርግጥ ገለጽን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,119,'3:119','እነሆ እናንተ እነዚያ የምትወዱዋቸው ናችሁ፡፡ (እነሱ) ግን አይወዱዋችሁም፡፡ በመጽሐፉም በሁሉም ታምናላችሁ፡፡ ባገኙዋችሁም ጊዜ «አምነናል» ይላሉ፡፡ ባገለሉም ጊዜ ከቁጭታቸው የተነሳ በእናንተ ላይ ዐጽቆቻቸውን ይነክሳሉ፡፡ «በቁጭታችሁ ሙቱ፤ አላህ ልቦች የቋጠሩትን ዐዋቂ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,120,'3:120','ደግ ነገር ብትነካችሁ ታስከፋቸዋለች፡፡ መጥፎም ነገር ብታገኛችሁ በእርሷ ይደሰታሉ፡፡ ብትታገሱና ብትጠነቀቁም ተንኮላቸው ምንም አይጎዳችሁም፡፡ አላህ በሚሠሩት ሁሉ (በዕውቀቱ) ከባቢ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(3,121,'3:121','ለምእምኖቹም ለውጊያ ሥራዎችን የምታዘጋጅ ኾነህ ከቤተሰብህ በማለድክ ጊዜ (አስታውስ)፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,122,'3:122','ከእናንተ ሁለት ጭፍሮች አላህ ረዳታቸው ሲኾን ለመፍራት ባሰቡ ጊዜ (አስታውስ)፡፡ በአላህም ላይ ብቻ ምእምናኖች ይመኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,123,'3:123','በበድርም እናንተ ጥቂቶች ኾናችሁ ሳለ አላህ በእርግጥ ረዳችሁ፡፡ አላህንም ታመሰግኑ ዘንድ ፍሩት፡፡');
INSERT INTO "sadiq_sani" VALUES(3,124,'3:124','ለምእምናን «ጌታችሁ በሦስት ሺህ መላእክት የተወረዱ ሲኾኑ ቢረዳችሁ አይበቃችሁምን» በምትል ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,125,'3:125','«አዎን ብትታገሱና ብትጠነቀቁ ከዚህም ፍጥነታቸው (ጠላቶቻችሁ) ቢመጡባችሁ ጌታችሁ ምልክት ባላቸው አምስት ሺህ መላእክት ይረዳችኋል፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,126,'3:126','አላህም (እርዳታውን) ለእናንተ ብስራትና ልቦቻችሁ በእርሱ እንዲረኩ እንጂ አላደረገውም፡፡ ድልም መንሳት አሸናፊ ጥበበኛ ከኾነው አላህ ዘንድ እንጅ ከሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,127,'3:127','(ድልን ያጎናጸፋችሁ) ከእነዚያ ከካዱት ከፊልን ሊቆርጥ (ሊያጠፋ) ወይም ሊያዋርዳቸውና ያፈሩ ኾነው እንዲመለሱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,128,'3:128','(አላህ) በእነርሱ ላይ ይቅርታ እስኪያደርግ ወይም እነርሱ በዳዮች ናቸውና እስኪቀጣቸው ድረስ ላንተ ከነገሩ ምንም የለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,129,'3:129','በሰማያት ያለው በምድርም ያለው ሁሉ የአላህ ነው፡፡ ለሚሻው ሰው ይምራል፤ የሚሻውንም ሰው ይቀጣል፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,130,'3:130','እናንተ ያመናችሁ ሆይ! አራጣን የተነባበሩ እጥፎች ሲኾኑ አትብሉ፡፡ ትድኑም ዘንድ አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,131,'3:131','ያችንም ለከሓዲዎች የተደገሰችውን እሳት ተጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,132,'3:132','ይታዘንላችሁም ዘንድ አላህንና መልክተኛውን ታዘዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,133,'3:133','ከጌታችሁ ወደ ኾነችም ምሕረትና ስፋትዋ እንደ ሰማያትና ምድር ወደ ኾነች ገነት አላህን ለሚፈሩ የተዘጋጀች ስትኾን ተቻኮሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,134,'3:134','ለእነዚያ በድሎትም ኾነ በችግር ለሚለግሱት፣ ቁጭትንም ገቺዎች ከሰዎችም ይቅርታ አድራጊዎች ለኾኑት (ተደግሳለች)፡፡ አላህም በጎ ሠሪዎችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,135,'3:135','ለእነዚያም መጥፎ ሥራን በሠሩ ወይም ነፍሶቻቸውን በበደሉ ጊዜ አላህን የሚያስታውሱና ለኀጢአቶቻቸው ምሕረትን የሚለምኑ ለኾኑት ከአላህም ሌላ ኀጢአቶችን የሚምር አንድም የለ፡፡ (በስሕተት) በሠሩትም ላይ እነርሱ የሚያውቁ ሲኾኑ የማይዘወትሩ ለኾኑት (ተደግሳለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,136,'3:136','እነዚያ ምንዳቸው ከጌታቸው ምሕረትና በውስጣቸው ዘውታሪዎች ሲኾኑ በሥሮቻቸው ወንዞች የሚፈሱባቸው ገነቶችም ናቸው፡፡ የሠሪዎችም ምንዳ (ገነት) ምንኛ አማረች!፡፡');
INSERT INTO "sadiq_sani" VALUES(3,137,'3:137','ከእናንተ በፊት ድርጊቶች በእርግጥ አልፈዋል፡፡ በምድርም ላይ ኺዱ፡፡ የአስተባባዮችም ፍጻሜ እንዴት እንደነበረ ተመልከቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,138,'3:138','ይህ ለሰዎች ገላጭ፤ ለጥንቁቆች መሪና ገሳጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,139,'3:139','እናንተም የበላዮች ስትኾኑ ምእመናን እንደኾናችሁ አትስነፉ፤ አትዘኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,140,'3:140','ቁስል ቢያገኛችሁ ሰዎቹንም መሰሉ ቁስል አግኝቷቸዋል፡፡ ይኽችንም ቀናት በሰዎች መካከል እናዘዋውራታለን፡፡ (እንድትገሰጹና) አላህም እነዚያን ያመኑትን እንዲያውቅ (እንዲለይ) ከእናንተም ሰማዕታትን እንዲይዝ ነው፡፡ አላህም በዳዮችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,141,'3:141','አላህም እነዚያን ያመኑትን (ከኀጢአት) ሊያነጻና ከሓዲዎችንም ሊያጠፋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,142,'3:142','በእውነቱ አላህ ከናንተ እነዚያን የታገሉትን ሳያውቅ (ሳይለይ) ታጋሾቹንም ሳያውቅ ገነትን ልትገቡ አሰባችሁን');
INSERT INTO "sadiq_sani" VALUES(3,143,'3:143','(በጦርነት ላይ) መሞትንም ሳታገኙት በፊት በርግጥ የምትመኙት ነበራችሁ፡፡ እናንተም የምትመለከቱ ስትኾኑ በርግጥ አያችሁት (ታዲያ ለምን ሸሻችሁ)');
INSERT INTO "sadiq_sani" VALUES(3,144,'3:144','ሙሐመድም ከበፊቱ መልክተኞች በእርግጥ ያለፉ የኾነ መልክተኛ እንጅ ሌላ አይደለም፡፡ ታዲያ ቢሞት ወይም ቢገደል ወደ ኋላችሁ ትገለበጣላችሁን ወደኋላው የሚገለበጥም ሰው አላህን ምንም አይጎዳም፡፡ አላህም አመስጋኞቹን በእርግጥ ይመነዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,145,'3:145','ለማንኛይቱም ነፍስ በአላህ ፍርድ ቢኾን እንጅ ልትሞት አይገባትም፡፡ (ጊዜውም) ተወስኖ ተጽፏል፡፡ የቅርቢቱንም ምንዳ የሚፈልግ ሰው ከእርስዋ እንሰጠዋለን፡፡ የመጨረሻይቱንም ምንዳ የሚፈልግ ሰው ከእርስዋ እንሰጠዋለን፤ አመስጋኞችንም በእርግጥ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,146,'3:146','ከነቢይም ብዙ ሊቃውንት ከርሱ ጋር ኾነው የተዋጉ ብዙ ናቸው፡፡ በአላህም መንገድ ለሚደርስባቸው ነገር አልፈሩም፤ አልደከሙምም፡፡ (ለጠላት) አልተዋረዱምም፡፡ አላህም ትዕግስተኞችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,147,'3:147','ንግግራቸውም «ጌታችን ሆይ! ኃጢአቶቻችንንና በነገራችን ሁሉ ወሰን ማለፋችንን ለእኛ ማር፣ ይዞታችንንም አደላድል፣ በከሓዲዎችም ሕዝቦች ላይ እርዳን» ማለታቸው እንጅ ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,148,'3:148','አላህም የቅርቢቱን ምንዳና የመጨረሻይቱን መልካም ምንዳ ሰጣቸው፡፡ አላህም በጎ አድራጊዎችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,149,'3:149','እናንተ ያመናችሁ ሆይ! እነዚያን የካዱትን ሰዎች ብትታዘዙ ወደ ኋላችሁ ይመልሱዋችኋል፡፡ ከሳሪዎች ኾናችሁም ትገለበጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,150,'3:150','(አይረዷችሁም)፤ ይልቁን አላህ ብቻ ረዳታችሁ ነው፡፡ እርሱም ከረዳቶች ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,151,'3:151','በእነዚያ በካዱት ሰዎች ልቦች ውስጥ አላህ በእርሱ አስረጅ ያላወረደበትን ነገር በአላህ በማጋራታቸው ምክንያት ፍርሃትን እንጥላለን፡፡ መኖሪያቸውም እሳት ናት፡፡ የበዳዮችም መኖሪያ ምን ትከፋ!');
INSERT INTO "sadiq_sani" VALUES(3,152,'3:152','አላህም በፈቃዱ በምትጨፈጭፏቸው ጊዜ ኪዳኑን በእርግጥ አረጋገጠላችሁ፡፡ የምትወዱትንም ነገር ካሳያችሁ በኋላ በፈራችሁ በትዕዛዙም በተጨቃጨቃችሁና ባመጻችሁ ጊዜ (እርዳታውን ከለከላችሁ)፡፡ ከእናንተ ውስጥ ቅርቢቱን ዓለም የሚሻ አለ፡፡ ከእናንተም የመጨረሻይቱን ዓለም የሚሻ አልለ፡፡ ከዚያም ሊሞክራችሁ ከእነርሱ አዞራችሁ፡፡ በእርግጥም ከእናንተ ይቅርታ አደረገላችሁ፡፡ አላህም በምእምናን ላይ የችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,153,'3:153','መልክተኛው ከኋላችሁ የሚጠራችሁ ሲኾን ርቃችሁ በምትሸሹና በአንድም ላይ የማትዞሩ በኾናችሁ ጊዜ (አስታውሱ)፡፡ ባመለጣችሁና በደረሰባችሁም ነገር እንድታዝኑ በሐሳብ ላይ ሐሳብን መነዳችሁ፡፡ አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,154,'3:154','ከዚያም ከጭንቅ በኋላ ጸጥታን ከእናንተ ከፊሎችን የሚሸፍንን እንቅልፍ በእናንተ ላይ አወረደ፡፡ ከፊሎቹም ነፍሶቻቸው በእርግጥ አሳሰቧቸው፡፡ እውነት ያልኾነውን የመሃይምነትን መጠራጠር በአላህ ይጠራጠራሉ፤ «ከነገሩ ለእኛ ምንም የለንም» ይላሉ፡፡ «ነገሩ ሁሉም ለአላህ ነው» በላቸው፡፡ ለአንተ የማይገልጹትን በነፍሶቻቸው ውስጥ ይደብቃሉ፡፡ «ከነገሩ ለእኛ አንዳች በነበረን ኖሮ እዚህ ባልተገደልን ነበር» ይላሉ፡፡ «በቤታችሁ ውስጥ በኾናችሁም ኖሮ እነዚያ በእነርሱ ላይ መገደል የተጻፈባቸው ወደ መውደቂያቸው በወጡ ነበር» በላቸው፡፡ አላህ (ፍርዱን ሊፈጽምና) በደረቶቻችሁም ውስጥ ያለውን ሊፈትን በልቦቻችሁም ውስጥ ያለውን ነገር ሊገልጽ (ይህን ሠራ)፡፡ አላህም በደረቶች ውስጥ ያለን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,155,'3:155','እነዚያ ሁለቱ ጭፍሮች በተገናኙ ቀን ከእናንተ የሸሹት ሰዎች በዚያ በሠሩት ከፊል ምክንያት ያሳሳታቸው ሰይጣን ነው፡፡ አላህም ከነሱ በእርግጥ ይቅር አለ፡፡ አላህ መሓሪ በቅጣት የማይቸኩል ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(3,156,'3:156','እናንተ ያመናችሁ ሆይ! እንደነዚያ እንደ ካዱትና ስለ ወንድሞቻቸው በምድር ላይ በተጓዙ ወይም ዘማቾች በኾኑ ጊዜ «እኛ ዘንድ በነበሩ ኖሮ ባልሞቱም ባልተገደሉም ነበር» እንደሚሉት አትኹኑ፡፡ አላህ ይህንን በልቦቻቸው ውስጥ ጸጸት ያደርግባቸው ዘንድ (ይህንን አሉ)፡፡ አላህም ሕያው ያደርጋል፤ ይገድላልም፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,157,'3:157','በአላህም መንገድ ብትገደሉ ወይም፤ ብትሞቱ ከአላህ የኾነው ምሕረትና እዝነት (እነርሱ) ከሚሰበስቡት ሀብት በእርግጥ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,158,'3:158','ብትሞቱም ወይም ብትገደሉ በእርግጥ ወደ አላህ ብቻ ትሰበሰባላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,159,'3:159','ከአላህም በኾነች ችሮታ ለዘብክላቸው፡፡ ዐመለ መጥፎ ልበ ደረቅም በኾንክ ኖሮ ከዙሪያህ በተበተኑ ነበር፡፡ ከእነርሱም ይቅር በል፡፡ ለእነርሱም ምሕረትን ለምንላቸው፡፡ በነገሩም ሁሉ አማክራቸው፡፡ ቁርጥ ሐሳብም ባደረግህ ጊዜ በአላህ ላይ ተመካ፡፡ አላህ በርሱ ላይ ተመኪዎችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(3,160,'3:160','አላህ ቢረዳችሁ ለእናንተ አሸናፊ የለም፡፡ ቢያዋርዳችሁም ያ ከርሱ (ማዋረድ) በኋላ የሚረዳችሁ ማነው በአላህም ላይ ብቻ ምእምናን ይመኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,161,'3:161','ለነቢይም ሰለባን መደበቅ አይገባውም፡፡ ሰለባንም የሚደብቅ ሰው በትንሣኤ ቀን በደበቀው ነገር (ተሸክሞ) ይመጣል፡፡ ከዚያም ነፍስ ሁሉ የሥራዋን ዋጋ ትሞላለች፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,162,'3:162','የአላህን ውዴታ የተከተለ ሰው ከአላህ በኾነ ቁጣ እንደ ተመለሰና መኖሪያው ገሀነም እንደ ኾነ ሰው ነውን መመለሻውም ምን ይከፋ!');
INSERT INTO "sadiq_sani" VALUES(3,163,'3:163','እነርሱ አላህ ዘንድ ባለ ደረጃዎች ናቸው፡፡ አላህም የሚሠሩትን ነገር ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,164,'3:164','አላህ በምእምናን ላይ ከጎሳቸው የኾነን፤ በእነርሱ ላይ አንቀጾቹን የሚያነብ፣ የሚጠራቸውም፣ መጽሐፍንና ጥበብን የሚያስተምራቸውም የኾነን መልእክተኛ በውስጣቸው በላከ ጊዜ፤ በእርግጥ ለገሰላቸው፡፡ እነርሱም ከዚያ በፊት በግልጽ ስህተት ውስጥ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,165,'3:165','ሁለት ብጤዎችዋን በእርግጥ ያገኛችሁ የኾነች መከራ (መጠቃት) ባገኘቻችሁ ጊዜ «ይህ ከየት ነው» አላችሁን «እርሱ ከነፍሶቻችሁ ዘንድ ነው፡፡» አላህ በነገሩ ሁሉ ላይ ቻይ ነውና በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,166,'3:166','ሁለቱ ጭፍሮችም በተጋጠሙ ቀን የደረሰባችሁ በአላህ ፈቃድ ነው፡፡ ምእምናንንም (ሊፈትንና) ሊገልጽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,167,'3:167','እነዚያንም የነፈቁትንና ለነርሱ «ኑ፤ በአላህ መንገድ ተጋደሉ፤ ወይም ተከላከሉ፤» የተባሉትን ሊገልጽ ነው፡፡ «መዋጋት (መኖሩን) ብናውቅ ኖሮ በተከተልናችሁ ነበር» አሉ፡፡ እነርሱ ያን ጊዜ ከእምነት ይልቅ ወደ ክህደት የቀረቡ ናቸው፡፡ በልቦቻቸው የሌለውን በአፎቻቸው ይናገራሉ፡፡ አላህም የሚደብቁትን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,168,'3:168','እነዚያ (ከትግል) የቀሩ ሲኾኑ ለወንድሞቻቸው፡- «በታዘዙን ኖሮ ባልተገደሉ ነበር» ያሉ ናቸው፡፡ «እውነተኞች እንደኾናችሁ ከነፍሶቻችሁ ላይ ሞትን ገፍትሩ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,169,'3:169','እነዚያን በአላህ መንገድ የተገደሉትን ሙታን አድርገህ አትገምታቸው፡፡ በእርግጥ እነርሱ እጌታቸው ዘንድ ሕያዋን ናቸው፤ ይመገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,170,'3:170','አላህ ከችሮታው በሰጣቸው ነገር ተደሳቾች ሲኾኑ (ይመገባሉ)፡፡ በነዚያም ከኋላቸው ገና በእነርሱ ባልተከተሉት በእነርሱ ላይ ፍርሃት ባለመኖሩና እነርሱም የማያዝኑ በመኾናቸው ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,171,'3:171','ከአላህ በኾነ ጸጋና ልግስና አላህም የምእምናንን ምንዳ የማያጠፉ በመኾኑ ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,172,'3:172','እነዚያ መቁሰል ካገኛቸው በኋላ ለአላህና ለመልክተኛው የታዘዙት ከእነሱ ለነዚያ በጎ ለሠሩትና ለተጠነቀቁት ታላቅ ምንዳ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,173,'3:173','እነዚያ ሰዎቹ ለእነርሱ፡- «ሰዎች ለእናንተ (ጦርን) አከማችተዋልና ፍሩዋቸው» ይሉዋቸውና (ይህም) እምነትን የጨመረላቸው «በቂያችንም አላህ ነው ምን ያምርም መጠጊያ!» ያሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,174,'3:174','ከአላህም በኾነ ጸጋና ችሮታ ክፉ ነገር ያልነካቸው ኾነው ተመለሱ፡፡ የአላህንም ውዴታ ተከተሉ፤ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,175,'3:175','እንዲህ የሚላችሁ ሰይጣን ብቻ ነው፡፡ ወዳጆቹን ያስፈራራችኋል፡፡ አትፍሩዋቸውም፡፡ ምእምናንም እንደኾናችሁ ፍሩኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,176,'3:176','እነዚያም በክህደት የሚቻኮሉት አያሳዝኑህ፡፡ ከነርሱ ፈጽሞ አላህን በምንም አይጎዱምና፡፡ አላህ በመጨረሻይቱ ዓለም ለእነሱ ዕድልን ላያደርግ ይሻል፡፡ ለእነርሱም ታላቅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,177,'3:177','እነዚያ ክህደትን በእምነት የለወጡ አላህን በምንም ነገር ፈጽሞ አይጎዱትም፡፡ ለእነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,178,'3:178','እነዚያ የካዱት ሰዎች እነርሱን ማዘግየታችንን ለነፍሶቻቸው ደግ ነገር ነው ብለው አያስቡ፡፡ እነርሱን የምናዘገያቸው ኃጢኣትን እንዲጨምሩ ብቻ ነው፡፡ ለነሱም አዋራጅ ስቃይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,179,'3:179','አላህ መጥፎውን ከመልካሙ እስከሚለይ ድረስ ምእምናንን እናንተ በርሱ ላይ ባላችሁበት (ኹኔታ) ላይ የሚተው አይደለም፡፡ አላህም ሩቁን ነገር የሚያሳውቃችሁ አይደለም፡፡ ግን አላህ ከመልክተኞቹ የሚሻውን ይመርጣል፡፡ በአላህና በመልክተኞቹም እመኑ፡፡ ብታምኑና ብትጠነቀቁም ለናንተ ታላቅ ምንዳ አላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,180,'3:180','እነዚያም አላህ ከችሮታው በሰጣቸው ገንዘብ የሚነፍጉ እርሱ ለእነሱ ደግ አይምሰላቸው፡፡ ይልቁንም፤ እርሱ ለነሱ መጥፎ ነው፡፡ ያንን በርሱ የነፈጉበትን በትንሣኤ ቀን (እባብ ኾኖ) ይጠለቃሉ፡፡ የሰማያትና የምድርም ውርስ ለአላህ ብቻ ነው፡፡ አላህም በሚሠሩት ሁሉ ውስጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,181,'3:181','የእነዚያን «አላህ ድኻ ነው እኛ ግን ከበርቴዎች ነን» ያሉትን ሰዎች ቃል አላህ በእርግጥ ሰማ፡፡ ያንን ያሉትንና ነቢያትን ያለ ሕግ መግደላቸውን በእርግጥ እንጽፋለን፡፡ «የእሳትንም ስቃይ ቅመሱ» እንላቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(3,182,'3:182','ያ (ስቃይ) እጆቻችሁ ባሳለፉት ሥራ ነው፡፡ አላህም ለባሮቹ በዳይ ባለመኾኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,183,'3:183','እነዚያ ለማንኛውም መልክተኛ «እሳት የምትበላው የኾነን ቁርባን እስከሚያመጣልን ድረስ ላናምን አላህ ወደኛ አዟል» ያሉ ናቸው፡፡«መልክተኞች ከእኔ በፊት በተዓምራትና በዚያም ባላችሁት (ቁርባን) በእርግጥ መጥተውላችኋል፡፡ እውነተኞች ከኾናችሁ ታዲያ ለምን ገደላችኋቸው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,184,'3:184','ቢያስተባብሉህም ከአንተ በፊት በግልጽ ተዓምራትና በጽሑፎች፣ በብሩህ መጽሐፍም የመጡት መልክተኞች በእርግጥ ተስተባብለዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(3,185,'3:185','ነፍስ ሁሉ ሞትን ቀማሽ ናት፡፡ ምንዳዎቻችሁንም የምትሞሉት በትንሣኤ ቀን ብቻ ነው፡፡ ከእሳትም የተራቀና ገነትን የተገባ ሰው በእርግጥ ምኞቱን አገኘ፡፡ ቅርቢቱም ሕይወት የመታለያ መሳሪያ እንጅ ሌላ አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(3,186,'3:186','በገንዘቦቻችሁና በነፍሶቻችሁ በእርግጥ ትፈተናላችሁ፡፡ ከእነዚያም ከእናንተ በፊት መጽሐፍን ከተሰጡትና ከእነዚያም ከአጋሩት ብዙን ማሰቃየት ትሰማላችሁ፡፡ ብትታገሱና ብትጠነቀቁ ይህ ከጥብቅ ነገሮች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,187,'3:187','አላህ የነዚያን መጽሐፉን የተሰጡትን ቃል ኪዳን ለሰዎች በእርግጥ ታብራሩታላችሁ አትደብቁትምም በማለት የያዘባቸውን (አስታውሱ)፡፡ በጀርባዎቻቸውም ኋላ ጣሉት፡፡ በርሱም ጥቂትን ዋጋ ገዙ፡፡ የሚገዙትም ነገር ከፋ!');
INSERT INTO "sadiq_sani" VALUES(3,188,'3:188','እነዚያን በሠሩት ነገር የሚደሰቱትንና ባልሠሩትም መመስገንን የሚወዱትን አታስብ፤ ከቅጣት በመዳን ላይ ናቸው ብለህ አታሰባቸው፡፡ ለእነሱ አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,189,'3:189','የሰማያትና የምድር ንግሥና ለአላህ ብቻ ነው፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,190,'3:190','ሰማያትንና ምድርን በመፍጠር ሌሊትና ቀንንም በመተካካት ለባለ አእምሮዎች በእርግጥ ምልክቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,191,'3:191','(እነርሱም) እነዚያ ቆመው፣ ተቀምጠውም፣ በጎኖቻቸው ተጋድመውም አላህን የሚያወሱ በሰማያትና በምድር አፈጣጠርም የሚያስተነትኑ፡- «ጌታችን ሆይ! ይኸን በከንቱ አልፈጠርከውም፤ ጥራት ይገባህ፤ ከእሳትም ቅጣት ጠብቀን» የሚሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,192,'3:192','«ጌታችን ሆይ! አንተ እሳት የምታገባውን ሰው በእርግጥ አዋረድከው፤ ለበደለኞችም ምንም ረዳቶች የሉዋቸውም፡፡»');
INSERT INTO "sadiq_sani" VALUES(3,193,'3:193','«ጌታችን ሆይ! እኛ በጌታችሁ እመኑ በማለት ወደ እምነት የሚጠራን ጠሪ ሰማን፡፡ አመንም፡፡ ጌታችን ሆይ! ኃጢአቶቻችንንም ለኛ ማር፡፡ ክፉ ሥራዎቻችንንም ከኛ አብስ፡፡ ከንጹሖቹም ሰዎች ጋር ግደለን፤»');
INSERT INTO "sadiq_sani" VALUES(3,194,'3:194','«ጌታችን ሆይ! በመልክተኞችህም ላይ ተስፋ ቃል ያደረግክልንን ስጠን፡፡ በትንሣኤ ቀንም አታዋርደን፡፡ አንተ ቀጠሮን አታፈርስምና» (የሚሉ ናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(3,195,'3:195','ጌታቸውም «እኔ ከእናንተ ከወንድ ወይም ከሴት የሠሪን ሥራ አላጠፋም፡፡ ከፊላችሁ ከከፊሉ ነው፤ በማለት ለነርሱ ልመናቸውን ተቀበለ፡፡ እነዚያም የተሰደዱ ከአገሮቻቸውም የተባረሩ በመንገዴም የተጠቁ የተጋደሉም የተገደሉም ክፉ ሥራዎቻቸውን ከእነርሱ በእርግጥ አብሳለሁ፡፡ በሥሮቻቸውም ወንዞች የሚፈሱባቸውን ገነቶች በእርግጥ አገባቸዋለሁ፡፡ ከአላህ ዘንድ የኾነን ምንዳ (ይመነዳሉ)፡፡ አላህም እርሱ ዘንድ መልካም ምንዳ አልለ፡፡ »');
INSERT INTO "sadiq_sani" VALUES(3,196,'3:196','የእነዚያ የካዱት ሰዎች በአገሮች መንፈላሰስ አያታልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(3,197,'3:197','አነስተኛ ጥቅም ነው፡፡ ከዚያም መኖሪያቸው ገሀነም ናት፡፡ ምን ትከፋም ምንጣፍ!');
INSERT INTO "sadiq_sani" VALUES(3,198,'3:198','ግን እነዚያ ጌታቸውን የፈሩ ለእነሱ ከሥሮቻቸው ወንዞች የሚፈሱባቸው ገነቶች በውስጧ ዘውታሪዎች ሲኾኑ ከአላህ ዘንድ (የተሰጡ) መስተንግዶዎች አሏቸው፡፡ አላህም ዘንድ ያለው (ምንዳ) ለበጎ ሠሪዎች በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,199,'3:199','ከመጽሐፉም ባለቤቶች ለአላህ ፈሪዎች በአላህ አንቀጾችም ጥቂትን ዋጋ የማይለወጡ ሲኾኑ በአላህና በዚያ ወደ እናንተ በተወረደው በዚያም ወደእነርሱ በተወረደው የሚያምኑ ሰዎች አልሉ፡፡ እነዚያ ለእነርሱ በጌታቸው ዘንድ ምንዳቸው አላቸው፡፡ አላህ ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(3,200,'3:200','እናንተ ያመናችሁ ሆይ! ታገሱ ተበራቱም፡፡ (በጦር ኬላ ላይ) ተሰለፉም፡፡ ትድኑም ዘንድ አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,1,'4:1','እናንተ ሰዎች ሆይ! ያንን ከአንዲት ነፍስ (ከአዳም) የፈጠራችሁን ከእርስዋም መቀናጆዋን (ሔዋንን) የፈጠረውን ከእነርሱም ብዙ ወንዶችንና ሴቶች የበተነውን ጌታችሁን ፍሩ፡፡ ያንንም በርሱ የምትጠያየቁበትን አላህንና ዝምድናዎችንም (ከመቁረጥ) ተጠንቀቁ፡፡ አላህ በእናንተ ላይ ተጠባባቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,2,'4:2','የቲሞችንም ገንዘቦቻቸውን ስጡ፡፡ መጥፎውንም በመልካሙ አትለውጡ፡፡ ገንዘቦቻቸውንም ወደ ገንዘቦቻችሁ (በመቀላቀል) አትብሉ እርሱ ታላቅ ኃጢአት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,3,'4:3','በየቲሞችም (ማግባት) አለማስተካከላችሁን ብትፈሩ (ዝሙትንና ከተወሰነላችሁ በላይ ማግባትንም ፍሩ)፡፡ ከሴቶች ለእናንተ የተዋበላችሁን ሁለት ሁለት ሦስት ሦስትም አራት አራትም አግቡ፡፡ አለማስተካከልንም ብትፈሩ አንዲትን ብቻ ወይም እጆቻችሁ ንብረት ያደረጉትን ያዙ፡፡ ይህ ወደ አለመበደል በጣም የቀረበ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,4,'4:4','ሴቶችንም መህሮቻቸውን በደስታ ስጡ፡፡ ለእናንተም ከእርሱ አንዳችን ነገር ነፍሶቻቸው ቢለግሱ መልካም ምስጉን ሲኾን ብሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(4,5,'4:5','ቂሎችንም ያችን አላህ ለእናንተ መተዳደሪያ ያደረጋትን ገንዘቦቻችውን (የያዛችሁላቸውን) አትስጡዋቸው፡፡ ከርሷም መግቡዋቸው አልብሱዋቸውም፡፡ ለእነሱም መልካምን ንግግር ተናገሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,6,'4:6','የቲሞችንም ጋብቻን እስከደረሱ ጊዜ ድረስ ፈትኑዋቸው፡፡ ከእነርሱም ቅንነትን ብታውቁ ገንዘቦቻቸውን ወደ እነርሱ ስጡ፡፡ በማባከንና ማደጋቸውንም በመሽቀዳደም አትብሏት፡፡ (ከዋቢዎች) ሀብታምም የኾነ ሰው ይከልከል፡፡ ድሃም የኾነ ሰው (ለድካሙ) በአግባብ ይብላ፡፡ ገንዘቦቻቸውንም ወደ እነርሱ በሰጣችሁ ጊዜ በእነርሱ ላይ አስመስክሩ፡፡ ተጠባባቂነትም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,7,'4:7','ለወንዶች ወላጆችና የቅርብ ዘመዶች ከተዉት (ንብረት) ፋንታ (ድርሻ) አላቸው፡፡ ለሴቶችም ወላጆችና የቅርብ ዘመዶች ከተዉት ከእርሱ ካነሰው ወይም ከበዛው ፋንታ አላቸው፡፡ የተወሰነ ድርሻ (ተደርጓል)፡፡');
INSERT INTO "sadiq_sani" VALUES(4,8,'4:8','ክፍያንም (ወራሽ ያልኾኑ) የዝምድና ባለቤቶችና የቲሞች፣ ምስኪኖችም በተገኙበት ጊዜ ከእርሱ ዳርጓቸው፡፡ መልካምንም ንግግር ተናገሩዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,9,'4:9','እነዚያም ከኋላቸው ደካሞችን ዝርያዎች ቢተው ኖሮ በእነርሱ ላይ የሚፈሩ (በየቲሞች ላይ) ይጠንቀቁ፡፡ አላህንም ይፍሩ፡፡ (ለተናዛዡ) ትክክለኛንም ቃል ይናገሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,10,'4:10','እነዚያ የየቲሞችን ገንዘብ በመበደል የሚበሉ በሆዶቻቸው ውስጥ የሚበሉት እሳትን ብቻ ነው፡፡ እሳትንም በእርግጥ ይገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,11,'4:11','አላህ በልጆቻችሁ (ውርስ በሚከተለው) ያዛችኋል፡፡ ለወንዱ የሁለት ሴቶች ድርሻ ብጤ አለው፡፡ (ሁለት ወይም) ከሁለት በላይም የኾኑ ሴቶች ብቻ ቢኾኑ ለእነሱ (ሟቹ) ከተወው ረጀት (ድርሻ) ከሦስት ሁለት እጁ አላቸው፡፡ አንዲትም ብትኾን ለርሷ ግማሹ አላት፡፡ ለአባትና ለእናቱም ለእርሱ ልጅ እንዳለው ከሁለቱ ለያንዳንዱ ከስድስት አንድ አላቸው፡፡ ለእርሱም ልጅ ባይኖረውና ወላጆቹ ቢወርሱት ለእናቱ ሲሶው አላት፡፡ ለእርሱም ወንድሞችና እኅቶች ቢኖሩት ለናቱ ከስድስት አንድ አላት፡፡ (ይህም የተባለው) በርሷ ከተናዘዘባት ኑዛዜ ወይም ከዕዳ በኋላ ነው፡፡ አባቶቻችሁና ልጆቻችሁ ለእናንተ በመጥቀም ማንኛቸው ይበልጥ ቅርብ እንደኾኑ አታውቁም፡፡ (ይኽም) ከአላህ የተደነገገ ነው፤ አላህ ዐዋቂ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,12,'4:12','ለእናንተም ሚስቶቻችሁ ከተዉት ንብረት ለእነርሱ ልጅ ባይኖራቸው ግማሹ አላችሁ፡፡ ለእነሱም ልጅ ቢኖራቸው ከተዉት ሀብት ከአራት አንድ አላችሁ፡፡ (ይህም) በእርሷ ከሚናዘዙባት ኑዛዜ ወይም ከዕዳ በኋላ ነው፡፡ ለናንተም ልጅ ባይኖራችሁ ከተዋችሁት ንብረት ለእነርሱ (ለሚስቶች) ከአራት አንድ አላቸው፡፡ ለእናንተም ልጅ ቢኖራችሁ ከተዋችሁት ንብረት ለእነርሱ ከስምንት አንድ አላቸው (ይህም በርሷ ከምትናዘዙባት ኑዛዜ ወይም ከዕዳ በኋላ ነው፡፡) ወላጅም ልጅም የሌለው በጎን ወራሾች የሚወርስ ወንድ ወይም ሴት ቢገኝ ለእርሱም (ለሟቹ) ወንድም ወይም እኅት (ከእናቱ በኩል ብቻ የኾነ) ቢኖር ከሁለቱ ለያንዳንዳቸው ከስድስት አንድ አላቸው፡፡ ከዚህም (ከአንድ) የበዙ ቢኾኑ እነርሱ በሲሶው ተጋሪዎች ናቸው፡፡ (ይህም ወራሾችን) የማይጎዳ ኾኖ በእርሷ ከሚናዘዙባት ኑዛዜ ወይም ከዕዳ በኋላ ነው፡፡ ከአላህም የኾነን ኑዛዜ (ያዛችኋል)፡፡ አላህም ዐዋቂ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,13,'4:13','እነዚህ የአላህ ውሳኔዎች ናቸው፡፡ አላህንና መልክተኛውንም የሚታዘዙ ሰዎች ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ ያገባቸዋል፡፡ ይህም ትልቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,14,'4:14','አላህንና መልክተኛውንም የሚያምጽ ወሰኖቹንም የሚተላለፍ በውስጧ ዘውታሪ ሲኾን እሳትን ያገባዋል፡፡ ለርሱም አዋራጅ ቅጣት አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,15,'4:15','እነዚያም ከሴቶቻችሁ መጥፎን ሥራ (ዝሙትን) የሚሠሩ በእነርሱ ላይ ከናንተ አራትን (ወንዶች) አስመስክሩባቸው፡፡ ቢመሰክሩም ሞት እስከሚደርስባቸው ወይም አላህ ለእነርሱ መንገድን እስከሚያደርግ ድረስ በቤቶች ውሰጥ ያዙዋቸው (ጠብቁዋቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(4,16,'4:16','እነዚያንም ከእናንተ ውስጥ (ዝሙትን) የሚሠሩዋትን አሰቃዩዋቸው፡፡ ቢጸጸቱና ሥራቸውንም ቢያሳምሩ ከእነርሱ ተገቱ (አታሰቃዩዋቸው)፡፡ አላህ ጸጸትን ተቀባይ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,17,'4:17','ጸጸትን መቀበል በአላህ ላይ የሚገባው ለእነዚያ ኀጢአትን በስህተት ለሚሠሩና ከዚያም ከቅርብ ጊዜ ለሚጸጸቱት ብቻ ነው፡፡ እነዚያንም አላህ በእነርሱ ላይ ጸጸትን ይቀበላል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,18,'4:18','ጸጸትንም መቀበል ለእነዚያ ኀጢአቶችን ለሚሠሩ አንዳቸውንም ሞት በመጣበት ጊዜ «እኔ አሁን ተጸጸትኩ» ለሚልና ለነዚያም እነርሱ ከሓዲዎች ኾነው ለሚሞቱ አይደለችም፡፡ እነዚያ ለእነሱ አሳማሚን ቅጣት አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,19,'4:19','እናንተ ያመናችሁ ሰዎች ሆይ! ሴቶችን ያስገደዳችሁ ኾናችሁ ልትወርሱና ግልጽን መጥፎ ሥራ ካልሠሩ በስተቀር ከሰጣችኋቸው ከፊሉን ልትወስዱ ልታጉላሏቸውም ለእናንተ አይፈቀድም፡፡ በመልካምም ተኗኗሩዋቸው፡፡ ብትጠሉዋቸውም (ታገሱ)፡፡ አንዳችን ነገር ብትጠሉ አላህም በእርሱ ብዙ ደግ ነገርን ሊያደርግ ይቻላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,20,'4:20','በሚስትም ስፍራ ሚስትን ለመለወጥ ብትፈልጉ ለአንደኛይቱ (ለምትፈታው) ብዙን ገንዘብ የሰጣችኋት ስትኾኑ ከርሱ ምንንም አትውሰዱ፡፡ በመበደልና ግልጽ በኾነ ኀጢአት ትወስዱታላችሁን');
INSERT INTO "sadiq_sani" VALUES(4,21,'4:21','ከፊላችሁ ወደ ከፊሉ በእርግጥ የደረሰ ሲኾንና ከእናንተ ላይም (ሴቶች) የጠበቀ ኪዳንን የያዙባችሁ ሲኾኑ እንዴት ትወስዱታላችሁ!');
INSERT INTO "sadiq_sani" VALUES(4,22,'4:22','ከሴቶችም አባቶቻችሁ ያገቡዋቸውን አታግቡ፡፡ (ትቀጡበታላችሁ)፡፡ ያለፈው ሲቀር፡፡ እርሱ መጥፎና የተጠላ ሥራ ነውና፡፡ መንገድነቱም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(4,23,'4:23','እናቶቻችሁ፣ ሴት ልጆቻችሁም፣ እኅቶቻችሁም፣ አክስቶቻችሁም፣ የሹሜዎቻችሁም፣ የወንድም ሴቶች ልጆችም፣ የእኅት ሴቶች ልጆችም፣ እነዚያም ያጠቡዋችሁ እናቶቻችሁ፣ ከመጥባት የኾኑትም እኅቶቻችሁ፣ የሚስቶቻችሁም እናቶች፣ እነዚያም በጉያዎቻችሁ ያሉት የእነዚያ በእነርሱ የገባችሁባቸው ሚስቶቻችሁ ሴቶች ልጆች፣ (ልታገቧቸው) በእናንተ ላይ እርም ተደረጉባችሁ፡፡ በእነርሱም (በሚስቶቻችሁ) ያልገባችሁባቸው ብትኾኑ (በፈታችኋቸው ጊዜ ሴቶች ልጆቻቸውን ብታገቡ) በእናንተ ላይ ኀጢአት የለባችሁም፡፡ የእነዚያ ከጀርባዎቻችሁ የኾኑት ወንዶች ልጆቻችሁ ሚስቶችም በሁለት እኅትማማቾችም መካከል መሰብሰብ (እንደዚሁ እርም ነው)፡፡ ግን ከዚህ በፊት ያለፈው ሲቀር (እርሱንስ ተምራችኋል) አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,24,'4:24','ከሴቶችም (በባል) ጥብቆቹ እጆቻቸሁ (በምርኮ) የያዙዋቸው ሲቀሩ በእናንተ ላይ እርም ናቸው፡፡ (ይህን) አላህ በናንተ ላይ ጻፈ፡፡ ከዚሃችሁም (ከተከለከሉት) ወዲያ ጥብቆች ኾናችሁ ዝሙተኞች ሳትኾኑ በገንዘቦቻችሁ ልትፈልጉ ለእናንተ ተፈቀደ፡፡ ከእነሱም በርሱ (በመገናኘት) የተጠቀማችሁባቸውን ሴቶች መህሮቻቸውን ግዴታ ሲኾን ስጧቸው፡፡ ከመወሰንም በኋላ በተዋደዳችሁበት ነገር በእናንተ ላይ ኀጢአት የለም፡፡ አላህ ዐዋቂ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,25,'4:25','ከናንተም ውስጥ ጥብቆች ምእምናት የኾኑትን ለማግባት ሀብትን ያልቻለ ሰው እጆቻችሁ ንብረት ካደረጓቸው ከምእምናት ወጣቶቻችሁ (ባሪያን ያግባ)፡፡ አላህም እምነታችሁን ዐዋቂ ነው፡፡ ከፊላችሁ ከከፊሉ (የተራባ) ነው፡፡ (ባሮችን) በጌቶቻቸውም ፈቃድ አግቡዋቸው፡፡ መህሮቻቸውንም በመልካም መንገድ ስጧቸው፡፡ ጥብቆች ኾነው ዝሙተኞች ያልኾኑ የስርቆሽ ወዳጆችንም ያልያዙ ሲኾኑ (አግቧቸው)፡፡ በማግባት በተጠበቁም ጊዜ መጥፎን ሥራ ቢሠሩ በእነርሱ ላይ ከቅጣት በነጻዎቹ ላይ ያለው ግማሽ አለባቸው፡፡ ይኸ (ባሪያን ማግባት) ከናንተ ዝሙትን ለፈራ ሰው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,26,'4:26','አላህ (የሃይማኖታችሁን ሕግጋት) ሊያብራራላችሁ ከእናንተ በፊትም የነበሩትን ነቢያት ደንቦች ሊመራችሁ በእናንተም ላይ ጸጸትን ሊቀበል ይሻል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,27,'4:27','አላህም በእናንተ ላይ ጸጸትን ሊቀበል ይሻል፡፡ እነዚያም ፍላጎታቸውን የሚከተሉት (ከውነት) ትልቅን መዘንበል እንድትዘነበሉ ይፈልጋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,28,'4:28','አላህ ከእናንተ ሊያቃልልላችሁ ይሻል፡፡ ሰውም ደካማ ኾኖ ተፈጠረ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,29,'4:29','እናንተ ያመናችሁ ሆይ! ገንዘቦቻችሁን በመካከላችሁ ያለ አግባብ አትብሉ፡፡ ግን ከእናንተ በመዋደድ የኾነችውን ንግድ (ብሉ)፡፡ ነፍሶቻችሁንም አትግደሉ፡፡ አላህ ለእናንተ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,30,'4:30','ወሰን በማለፍና በመበደልም ይህንን የሠራ ሰው እሳትን እናገባዋለን፡፡ ይኸም በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,31,'4:31','ከእርሱ ከተከለከላችሁት ታላላቆቹን (ኀጢአቶች) ብትርቁ (ትናንሾቹን) ኀጢአቶቻችሁን ከእናንተ እናብሳለን፡፡ የተከበረንም ስፍራ (ገነትን) እናገባችኋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,32,'4:32','አላህም ከፊላችሁን በከፊሉ ላይ በእርሱ ያበለጠበትን ጸጋ አትመኙ፡፡ ለወንዶች ከሠሩት ሥራ እድል አላቸው፡፡ ለሴቶችም ከሠሩት ሥራ ዕድል አላቸው፡፡ አላህንም ከችሮታው ለምኑት፡፡ አላህ በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,33,'4:33','ለሁሉም (ለወንዶችና ለሴቶች) ወላጆችና የቅርብ ዘመዶች ከተዉት ሀብት ጠቅላይ ወራሾችን አድርገናል፡፡ እነዚያንም (ለመረዳዳትና ለመዋረስ) በመሐላዎቻችሁ የተዋዋላችኋቸውን ድርሻቸውን (ከስድስት አንድ) ስጧቸው፡፡ አላህ በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,34,'4:34','ወንዶች በሴቶች ላይ ቋሚዎች (አሳዳሪዎች) ናቸው፡፡ አላህ ከፊላቸውን በከፊሉ ላይ በማብለጡና (ወንዶች) ከገንዘቦቻቸው (ለሴቶች) በመስጠታቸው ነው፡፡ መልካሞቹም ሴቶች (ለባሎቻቸው) ታዛዦች አላህ ባስጠበቀው ነገር ሩቅን ጠባቂዎች ናቸው፡፡ እነዚያንም ማመጸቸውን የምትፈሩትን ገስጹዋቸው፡፡ በመኝታዎችም ተለዩዋቸው፡፡ (ሳካ ሳታደርሱ) ምቱዋቸውም፡፡ ቢታዘዟችሁም (ለመጨቆን) በእነርሱ ላይ መንገድን አትፈልጉ፤ አላህ የበላይ ታላቅ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,35,'4:35','(እናንተ ዋቢዎች) የመካከላቸውንም ጭቅጭቅ ብታውቁ ከቤተሰቦቹ ሽማግሌን ከቤተሰቦችዋም ሽማግሌን ላኩ፡፡ ማስታረቅን ቢሹ አላህ በመካከላቸው ያስማማል፡፡ አላህ ግልጽን ዐዋቂ ውስጥንም ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,36,'4:36','አላህንም ተገዙ፡፡ በእርሱም ምንንም አታጋሩ፡፡ በወላጆችና በቅርብ ዝምድና ባለቤትም፣ በየቲሞችም፣ በምስኪኖችም፣ በቅርብ ጎረቤትም፣ በሩቅ ጎረቤትም፣ በጎን ባልደረባም፣ በመንገደኛም፣ እጆቻችሁም ንብረት ባደረጓቸው (ባሮች)፣ መልካምን (ሥሩ)፤ አላህ ኩራተኛ ጉረኛ የኾነን ሰው አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,37,'4:37','እነዚያ የሚሰስቱ ሰዎችንም በመሰሰት የሚያዙ አላህም ከችሮታው የሰጣቸውን ጸጋ የሚደብቁ (ብርቱን ቅጣት ይቀጥጣሉ)፡፡ ለከሓዲዎችም አዋራጅን ቅጣት አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,38,'4:38','እነዚያም ለሰዎች ይውልኝ ሲሉ ገንዘቦቻቸውን የሚሰጡ በአላህና በመጨረሻውም ቀን የማያምኑ (ይቀጥጣሉ)፡፡ ሰይጣንም ለእርሱ ጓደኛው የኾነ ሰው ጓደኛነቱ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(4,39,'4:39','በአላህና በመጨረሻውም ቀን ባመኑ አላህ ከሰጣቸውም (ሲሳይ) በለገሱ ኖሮ በነርሱ ላይ ምን (ጉዳት) ነበረ አላህም በነርሱ (ኹነታ) ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,40,'4:40','አላህ የብናኝን ክብደት ያህል አይበድልም፡፡ መልካም ሥራ ብትኾንም ይደራርባታል፡፡ ከእርሱም ዘንድ ታላቅን ምንዳ ይሰጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,41,'4:41','ከሕዝቦችም ሁሉ መስካሪን ባመጣን ጊዜ አንተንም በእነዚህ ላይ መስካሪ አድርገን በምናመጣህ ጊዜ (የከሓዲዎች ኹኔታ) እንዴት ይኾን');
INSERT INTO "sadiq_sani" VALUES(4,42,'4:42','በዚያ ቀን እነዚያ የካዱትና መልክተኛውን ያልታዘዙት በእነሱ ምድር ብትደፋባቸው ይመኛሉ፡፡ ከአላህም ወሬን አይደብቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,43,'4:43','እናንተ ያመናችሁ ሆይ! እናንተ የሰከራችሁ ኾናችሁ የምትሉትን ነገር እስከምታውቁ የረከሳችሁም ስትኾኑ መንገድን አላፊዎች ካልኾናችሁ በስተቀር (አካላታችሁን) እስከምትታጠቡ ድረስ ስግደትን አትቅረቡ፡፡ በሽተኞች ወይም በጉዞ ላይ ብትኾኑም፥ ወይንም ከእናንተ አንዱ ከዓይነምድር ቢመጣ፥ ወይንም ሴቶችን ብትነካኩና ውሃን ባታገኙ፣ ንጹሕ የኾነን የምድር ገጽ አስቡ፡፡ ፊቶቻችሁንና እጆቻችሁንም (በርሱ) አብሱ፡፡ አላህ ይቅር ባይ መሓሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,44,'4:44','ወደነዚያ ከመጽሐፉ (ዕውቀት) ዕድልን ወደ ተሰጡት አላየህምን ጥመትን (በቅንነት) ይገዛሉ፡፡ መንገድንም እንድትሳሳቱ ይፈልጋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,45,'4:45','አላህም ጠላቶቻችሁን ዐዋቂ ነው፡፡ ጠባቂነትም በአላህ በቃ፡፡ ረዳትነትም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,46,'4:46','ከነዚያ አይሁዳውያን ከኾኑት ሰዎች ንግግሮችን ከስፍራዎቹ የሚያጣምሙ አልሉ፡፡ «ሰማንም አመጽንም የማትሰማም ስትኾን ስማ» ይላሉ፡፡ በምላሶቻቸውም ለማጣመምና ሃይማኖትንም ለመዝለፍ ራዒና ይላሉ፡፡ እነሱም «ሰማን ታዘዝንም ስማም ተመልከተንም» ባሉ ኖሮ ለነሱ መልካምና ትክክለኛ በኾነ ነበር፡፡ ግን በክህደታቸው አላህ ረገማቸው፡፡ ጥቂትንም እንጅ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,47,'4:47','እናንተ መጽሐፉን የተሰጣችሁ ሆይ! ፊቶችን ሳናብስና በጀርባዎች ላይ ሳንመልሳቸው ወይም የሰንበትን ባለቤቶች (ያፈረሱትን) እንደረገምን ሳንረግማቸው በፊት ከእናንተ ጋር ያለውን የሚያረጋግጥ ኾኖ ባወረድነው (ቁርኣን) እመኑ፡፡ የአላህም ትዕዛዝ ተፈጻሚ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,48,'4:48','አላህ በእርሱ ማጋራትን በፍጹም አይምርም፡፡ ከዚህ ሌላ ያለውንም (ኀጢአት) ለሚሻው ሰው ይምራል፡፡ በአላህም የሚያጋራ ሰው ታላቅን ኀጢአት በእርግጥ ቀጠፈ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,49,'4:49','ወደእነዚያ ነፍሶቻቸውን ወደ ሚያጠሩት (የሚያወድሱት) አላየህምን አይደለም፤ አላህ የሚፈልገውን ያጠራል፡፡ የተምር ፍሬ ክር ያህልም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,50,'4:50','በአላህ ላይ ውሸትን እንዴት እንደሚቀጣጥፉ ተመልከት፡፡ ግልጽ ወንጀልም በርሱ (በመቅጠፍ) በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,51,'4:51','ወደ እነዚያ ከመጽሐፉ ዕድልን ወደ ተሰጡት አላየኸምን በድግምትና በጣዖት ያምናሉ፡፡ ለእነዚያም ለካዱት፡- እናንተ በመንገድ (በሃይማኖት) ከእነዚያ ካመኑት ይበልጥ የቀናችሁ ናችሁ ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,52,'4:52','እነዚያ አላህ የረገማቸው ናቸው፡፡ አላህም የረገመው ሰው ለእርሱ ፈጽሞ ረዳትን አታገኝለትም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,53,'4:53','በእውነቱ ለእነሱ ከንግሥናው ፋንታ አላቸውን ያን ጊዜ ለሰዎች በተምር ፍሬ ላይ ያለችውን ነጥብ ያህል አይሰጡም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,54,'4:54','ይልቁንም ሰዎችን (ሙሐመድን) አላህ ከችሮታው በሰጣቸው ነገር ላይ ይመቀኛሉን ለኢብራሂምም ቤተሰቦች መጽሐፍንና ጥበብን በእርግጥ ሰጠን ታላቅንም ንግሥና ሰጠናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,55,'4:55','ከእነሱም (ከይሁዶች) በእርሱ ያመነ አለ፡፡ ከእነርሱም ከእርሱ ያፈገፈ አለ፡፡ አቃጣይነትም በገሀነም በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,56,'4:56','እነዚያን በተአምራታችን የካዱትን በእርግጥ እሳትን እናገባቸዋለን፡፡ ስቃይን እንዲቀምሱ ቆዳዎቻቸው በተቃጠሉ ቁጥር ሌሎችን ቆዳዎች እንለውጥላቸዋለን፡፡ አላህ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,57,'4:57','እነዚያንም ያመኑትንና መልካም ሥራዎችን የሠሩትን ከስራቸው ወንዞች የሚፈሱባቸውን ገነቶች ዘልዓለም በውስጣቸው ዘውታሪዎች ሲኾኑ በእርግጥ እናገባቸዋለን፡፡ ለእነሱ በውስጥዋ ንጹሕ ሚስቶች አሉዋቸው፡፡ የምታስጠልልን ጥላም እናገባቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,58,'4:58','አላህ አደራዎችን ወደ ባለቤቶቻቸው እንድታደርሱ ያዛችኋል፡፡ በሰዎችም መካከል በፈረዳችሁ ጊዜ በትክክል እንድትፈርዱ (ያዛችኋል)፡፡ አላህ በርሱ የሚገሥጻችሁ ነገር ምን ያምር! አላህ ሰሚ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,59,'4:59','እናንተ ያመናችሁ ሆይ! አላህን ተገዙ፡፡ መልክተኛውንና ከእናንተም የስልጣን ባለቤቶችን ታዘዙ፡፡ በማንኛውም ነገር ብትከራከሩ በአላህና በመጨረሻው ቀን የምታምኑ ብትኾኑ (የተከራከራችሁበትን ነገር) ወደ አላህና ወደ መልክተኛው መልሱት፡፡ ይህ የተሻለ መጨረሻውም ያማረ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,60,'4:60','ወደ እነዚያ እነርሱ በአንተ ላይ በተወረደውና ከአንተ በፊትም በተወረደው አምነናል ወደሚሉት፤ አላየህምን በእርሱ እንዲክዱ በእርግጥ የታዘዙ ሲኾኑ ወደ ጣዖት መፋረድን ይፈልጋሉ፡፡ ሰይጣንም (ከእውነት) የራቀን መሳሳት ሊያሳስታቸው ይፈልጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,61,'4:61','ለእነርሱም፡- «አላህ ወደ አወረደው (ቁርኣን)ና ወደ መልክተኛው ኑ» በተባሉ ጊዜ መናፍቃን ከአንተ መሸሽን ሲሸሹ ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,62,'4:62','እጆቻቸውም ባስቀደሙት (ጥፋት) መከራ በደረሰችባቸውና ከዚያም «ደግን ሐሳብና ማስማማትን እንጅ ሌላ አልሻንም» በማለት በአላህ የሚምሉ ኾነው በመጡህ ጊዜ እንዴት ይኾናሉ');
INSERT INTO "sadiq_sani" VALUES(4,63,'4:63','እነዚህ እነዚያ አላህ በልቦቻቸው ውስጥ ያለውን ሁሉ የሚያውቅባቸው ናቸው፡፡ እነርሱንም ተዋቸው ገሥጻቸውም፡፡ ለእነርሱም በነፍሶቻቸው ውስጥ ስሜት ያለውን ቃል ተናገራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,64,'4:64','ማንንም መልክተኛ በአላህ ፈቃድ ሊታዘዙት እንጅ አልላክንም፡፡ እነሱም ነፍሶቻቸውን በበደሉ ጊዜ ወደ አንተ ቢመጡና አላህንም ምሕረትን በለመኑ መልክተኛውም ለነርሱ ምሕረትን በለመነላቸው ኖሮ አላህን ጸጸትን ተቀባይ አዛኝ ኾኖ ባገኙት ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,65,'4:65','በጌታህም እምላለሁ በመካከላቸው በተከራከሩበት ፍርድ እስከሚያስፈርዱህ ከዚያም ከፈረድከው ነገር በነፍሶቻቸው ውስጥ ጭንቀትን እስከማያገኙና ፍጹም መታዘዝንም እስከሚታዘዙ ድረስ አያምኑም፤ (ምእመን አይኾኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(4,66,'4:66','እኛም በእነርሱ ላይ፡- «ነፍሶቻችሁን ግደሉ ወይም ከአገሮቻችሁ ውጡ» ማለትን በጻፍን ኖሮ ከነሱ ጥቂቶቹ እንጅ ባልሠሩት ነበር፡፡ እነርሱም በእርሱ የሚገሰጹበትን ነገር በሠሩ ኖሮ ለነሱ መልካምና (በእምነታቸው ላይ) ለመርጋትም በጣም የበረታ በኾነ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,67,'4:67','ያን ጊዜም ከእኛ ዘንድ ታላቅን ምንዳ በሰጠናቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,68,'4:68','ቀጥተኛውንም መንገድ በእርግጥ በመራናቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,69,'4:69','አላህንና መልክተኛውንም የሚታዘዝ ሰው እነዚያ ከእነዚያ አላህ በእነርሱ ላይ ከለገሳቸው ከነቢያት፣ ከጻድቃንም፣ ከሰማዕታትም፣ ከመልካሞቹ ጋር ይኾናሉ፡፡ የእነዚያም ጓደኝነት አማረ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,70,'4:70','ይህ ችሮታ ከአላህ ነው፡፡ አዋቂነትም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,71,'4:71','እናንተ ያመናችሁ ሆይ! ጥንቃቄያችሁን ያዙ፡፡ ክፍልፍልም Ñድ ሆናችሁ (ለዘመቻ) ውጡ፡፡ ወይም ተሰብስባችሁ ውጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,72,'4:72','ከእናንተም ውስጥ በእርግጥ (ከዘመቻ) ወደ ኋላ የሚንጓደድ ሰው አልለ፡፡ አደጋም ብታገኛችሁ ከእነሱ ጋር ተጣጅ ባልኾንኩ ጊዜ አላህ በእኔ ላይ በእርግጥ ለገሰልኝ ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,73,'4:73','ከአላህም የኾነ ችሮታ ቢያገኛችሁ በእናንተና በእርሱ መካከል ፍቅር እንዳልነበረች ሁሉ «ታላቅ ዕድልን አገኝ ዘንድ ከነሱ ጋር በኾንኩ ወይ ምኞቴ!» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,74,'4:74','እነዚያም ቅርቧን ሕይወት በመጨረሻይቱ ሕይወት የሚለውጡ በአላህ መንገድ ይጋደሉ፡፡ በአላህም መንገድ የሚጋደል፣ የሚገደልም ወይም የሚያሸንፍ ታላቅ ምንዳን በእርግጥ እንሰጠዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,75,'4:75','በአላህ መንገድና ከወንዶች ከሴቶችና ከልጆችም የኾኑትን ደካሞች እነዚያን፡- «ጌታችን ሆይ! ከዚች ባለቤቶችዋ በዳይ ከኾኑት ከተማ አውጣን ከአንተም ዘንድ ለእኛ አሳዳሪን አድርግልን ከአንተ ዘንድም ለእኛ ረዳትን አድርግልን» የሚሉትን (ለማዳን) የማትጋደሉት ለእናንተ ምን አላችሁ');
INSERT INTO "sadiq_sani" VALUES(4,76,'4:76','እነዚያ ያመኑት ሰዎች በአላህ መንገድ ይጋደላሉ፡፡ እነዚያ የካዱትም በጣዖት መንገድ ይጋደላሉ፡፡ የሰይጣንንም ጭፍሮች ተጋደሉ፡፡ የሰይጣን ተንኮል ደካማ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,77,'4:77','ወደእነዚያ ለእነርሱ፡- እጆቻችሁን (ከመጋደል) ሰብስቡ፣ ሶላትንም ደንቡን ጠብቃችሁ ስገዱ፣ ግዴታ ምጽዋትም ስጡ ወደ ተባሉት አላየህምን በእነርሱም ላይ መጋደል በተጻፈ ጊዜ ከእነሱ ከፊሎቹ ወዲያውኑ ሰዎችን አላህን እንደሚፈሩ ወይም የበለጠ ፍራቻን ይፈራሉ፡፡ «ጌታችን ሆይ! በእኛ ላይ መጋደልን ለምን ደነገግክ እስከ ቅርብ ጊዜ ድረስ አታቆየንም ኖሮአልን» ይላሉም፡፡ «የቅርቢቱ ሕይወት ጥቅም አነስተኛ ነው፡፡ መጨረሻይቱም ዓለም አላህን ለሚፈራ ሰው በላጭ ናት፡፡ በተምር ፍሬ ውስጥ ያለን ክር ያህል እንኳ አትበደሉም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,78,'4:78','«የትም ስፍራ ብትኾኑ በጠነከሩ ሕንጻዎች ውስጥ ብትኾኑም እንኳ ሞት ያገኛችኋል፡፡ ደግም ነገር ብታገኛቸው ይህች ከአላህ ዘንድ ናት» ይላሉ፡፡ መከራም ብታገኛቸው «ይህቺ ከአንተ ዘንድ ናት» ይላሉ፡፡ «ሁሉም (ደጉም ክፉውም) ከአላህ ዘንድ ነው» በላቸው፡፡ ለእነዚህም ሰዎች ንግግርን ሊረዱ የማይቀርቡት ምን አላቸው');
INSERT INTO "sadiq_sani" VALUES(4,79,'4:79','ከደግ ነገር የሚያገኝህ ከአላህ (ችሮታ) ነው፡፡ ከመከራም የሚደርስብህ ከራስህ (ጥፋት የተነሳ) ነው፡፡ ለሰዎችም ሁሉ መልክተኛ ኾነህ ላክንህ፡፡ መስካሪም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,80,'4:80','መልክተኛውን የሚታዘዝ ሰው በእርግጥ አላህን ታዘዘ፡፡ ከትእዛዝም የሸሸ ሰው (አያሳስብህ)፡፡ በእነሱ ላይ ጠባቂ አድርገን አልላክንህምና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,81,'4:81','«(ነገራችን) መታዘዝ ነው» ይላሉም፤ ከአንተም ዘንድ በወጡ ጊዜ ከነርሱ ከፊሎቹ ከዚያ (በፊትህ) ከሚሉት ሌላን (በልቦቻቸው) ያሳድራሉ፡፡ አላህም የሚያሳድሩትን ነገር ይጽፋል፡፡ ስለዚህ ተዋቸው፡፡ በአላህም ላይ ተጠጋ፡፡ መጠጊያም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,82,'4:82','ቁርኣንን አያስተነትኑምን ከአላህ ሌላ ዘንድ በነበረ ኖሮ በርሱ ውስጥ ብዙን መለያየት ባገኙ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,83,'4:83','ከጸጥታ ወይም ከፍርሃት አንዳች (ወሬ) በመጣላቸውም ጊዜ እርሱን ያጋንናሉ፡፡ ወደ መልክተኛውና ከእነርሱ ወደ ትዕዛዙ ባለቤቶች (ወደ ዐዋቂዎቹ) በመለሱት ኖሮ እነዚያ ከእነርሱ (ነገሩን) የሚያውጣጡት ባወቁት ነበር፡፡ በእናንተም ላይ የአላህ ችሮታና እዝነቱ ባልነበረ ኖሮ ከጥቂቶች በስተቀር ሰይጣንን በተከተላችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(4,84,'4:84','በአላህም መንገድ ተጋደል፡፡ ራስህን እንጅ ሌላን አትገደድም፡፡ ምእምናንንም (በመጋደል ላይ) አደፋፍር፡፡ አላህ የእነዚያን የካዱትን ኀይል ሊከለክል ይከጀላል፡፡ አላህም በኀይል በመያዙ የበረታ ቅጣቱም የጠነከረ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,85,'4:85','መልካም መታደግን የሚታደግ ሰው ለእርሱ ከርስዋ ዕድል ይኖረዋል፡፡ መጥፎ መታደግንም የሚታደግ ሰው ለእርሱ ከርስዋ ድርሻ ይኖረዋል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,86,'4:86','በሰላምታ በተከበራችሁም ጊዜ ከእርሷ ይበልጥ ባማረ (ሰላምታ) አክብሩ፡፡ ወይም (እርሷኑ) መልሷት፡፡ አላህ በነገሩ ሁሉ ላይ ተቆጣጣሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,87,'4:87','አላህ ከእርሱ በስተቀር አምላክ የለም፡፡ ወደ ትንሣኤ ቀን በርሱ ጥርጥር የሌለበት ሲኾን በእርግጥ ይሰበስባችኋል፡፡ በንግግርም ከአላህ ይበልጥ እውነተኛ ማነው');
INSERT INTO "sadiq_sani" VALUES(4,88,'4:88','በመናፍቃንም (ነገር) አላህ በሥራዎቻቸው ወደ ክህደት የመለሳቸው ሲኾኑ ሁለት ክፍሎች የኾናችሁት ለናንተ ምን አላችሁ አላህ ያጠመመውን ሰው ልታቀኑ ታስባላችሁን አላህም ያሳሳተውን ሰው ለእርሱ መንገድን ፈጽሞ አታገኝለትም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,89,'4:89','(እነርሱ) እንደ ካዱ ብትክዱና እኩል ብትኾኑ ተመኙ! በአላህም ሃይማኖት እስከሚሰደዱ ድረስ ከነሱ ወዳጆችን አትያዙ፡፡ (ከእምነት) ቢያፈገፍጉም ያዙዋቸው፤ (ማርኩዋቸው)፡፡ ባገኛችሁበትም ስፍራ ግደሉዋቸው፡፡ ከእነሱም ወዳጅንና ረዳትን አትያዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,90,'4:90','እነዚያ በእናንተና በእነርሱ መካከል ቃል ኪዳን ወዳላቸው ሕዝቦች የሚጠጉና ወይም እናንተን ለመጋደል ወይም ወገኖቻቸውን ለመጋደል ልቦቻቸው የተጨነቁ ኾነው የመጧዋችሁ ሲቀሩ፤ (እነዚያንስ አትጋደሉዋቸው)፡፡ አላህም በሻ ኖሮ በእናንተ ላይ ኀይል በሰጣቸውና በተጋደሉዋችሁ ነበር፡፡ ቢተዋችሁና ባይጋደሉዋችሁም ወደ እናንተም እርቅን ቢያቀርቡ አላህ ለእናንተ በነሱ ላይ (የመጋደል) መንገድን አላደረገም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,91,'4:91','ሌሎች ከእናንተ ለመዳን ከወገኖቻቸውም ለመዳን የሚፈልጉን ታገኛላችሁ፡፡ ወደ እውከት እንዲመለሱ በተጠሩ ቁጥር በርሷ ውስጥ ይመለሳሉ፡፡ ባይተውዋችሁና እርቅንም ባያቀርቡላችሁ፣ እጆቻቸውንም ባይሰበስቡ፣ ባገኛችኋቸው ስፍራ ማርኩዋቸው፡፡ ግደሉዋቸውም፡፡ እነዚያንም ለእናንተ በነሱ ላይ ግልጽ ማስረጃን አድርገናል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,92,'4:92','ለምእመንም በስሕተት ካልኾነ በቀር ምእመንን መግደል አይገባውም፡፡ ምእመንንም በስህተት የገደለ ሰው ያመነችን ጫንቃ (ባሪያ) ነጻ ማውጣትና ወደ ቤተሰቦቹም የምትሰጥ ጉማ ምሕረት ካላደረጉለት በስተቀር (መክፈል) አለበት፡፡ እርሱ (ተገዳዩ) ምእመን ሲኾን ለእናንተ ጠላት ከኾኑትም ሕዝቦች ቢኾን ያመነችን ጫንቃ ነጻ ማውጣት (ብቻ) አለበት፡፡ በእናንተና በእነሱ መካከል ቃል ኪዳን ካላቸውም ሕዝቦች ቢኾን ወደ ቤተሰቦቹ በእጅ የምትሰጥ ጉማና ያመነችን ጫንቃ ነጻ ማውጣት አለበት፡፡ ያላገኘ ሰውም ተከታታዮችን ሁለት ወሮች መጾም አለበት፡፡ አላህ መጸጸትን ለመቀበል (ደነገገላችሁ)፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,93,'4:93','ምእመንንም አስቦ የሚገድል ሰው ቅጣቱ በውስጧ ዘውታሪ ሲኾን ገሀነም ናት፡፡ አላህም በርሱ ላይ ተቆጣ፡፡ ረገመውም፡፡ ለእርሱም ከባድ ቅጣትን አዘጋጀ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,94,'4:94','እናንተ ያመናችሁ ሆይ! በአላህ መንገድ (ለመጋደል) በተጓዛችሁ ጊዜ አስረግጡ፡፡ ሰላምታንም ወደናንተ ላቀረበ ሰው የቅርቢቱን ሕይወት ጥቅም የምትፈልጉ ኾናችሁ ምእመን አይደለህም አትበሉ፡፡ አላህም ዘንድ ብዙ ዘረፋዎች አሉ፡፡ ከዚህ በፊት እንደዚሁ ነበራችሁ፡፡ አላህም በእናንተ ላይ ለገሰ ስለዚህ አስተውሉ፡፡ አላህ በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,95,'4:95','ከምእመናን የጉዳት ባለቤት ከኾኑት በቀር ተቀማጮቹና በአላህ መንገድ በገንዘቦቻቸውና በነፍሶቻቸው የሚታገሉት አይተካከሉም፡፡ በገንዘቦቻቸውና በነፍሶቻቸው የሚታገሉትን አላህ በተቀማጮቹ ላይ በደረጃ አበለጠ፡፡ ለሁሉም አላህ መልካሚቱን (ገነት) ተስፋ ሰጠ፡፡ ታጋዮቹንም በተቀማጮቹ ላይ አላህ በታላቅ ምንዳ አበለጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,96,'4:96','ከእርሱ ዘንድ በኾኑ ደረጃዎች (አበለጠ)፡፡ ምሕረትንም አደረገ፡፡ እዝነትም አዘነላቸው፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,97,'4:97','እነዚያ (ለእምንት ባለመሰደድ) ነፍሶቻቸውን በዳዮች ኾነው መላእክት (በበድር) የገደሉዋቸው (መላእክት ለነርሱ) «በምን ነገር ላይ ነበራችሁ» አሏቸው፡፡ «በምድር ውስጥ ደካሞች ነበርን» አሉ፡፡ «የአላህ ምድር ትሰደዱባት ዘንድ ሰፊ አልነበረችምን» አሉዋቸው፡፡ እነዚያም መኖሪያቸው ገሀነም ናት፡፡ በመመለሻነትም ከፋች!');
INSERT INTO "sadiq_sani" VALUES(4,98,'4:98','ግን ከወንዶችና ከሴቶች ከሕፃኖችም ሲኾኑ (ለመውጣት) መላን የማይችሉና መንገድንም የማይምመሩ ደካሞች (ቅጣት የለባቸውም)');
INSERT INTO "sadiq_sani" VALUES(4,99,'4:99','እነዚያም አላህ ከእነርሱ ይቅርታ ሊያደርግ ይሻል፡፡ አላህም ይቅር ባይ መሓሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,100,'4:100','በአላህም መንገድ የሚሰደድ ሰው በምድር ውስጥ ብዙ መሰደጃንና ምቾትን ያገኛል፡፡ ወደ አላህና ወደ መልክተኛውም ስደተኛ ኾኖ ከቤቱ የሚወጣ ከዚያም (በመንገድ) ሞት የሚያገኘው ሰው ምንዳው በእርግጥ አላህ ዘንድ ተረጋገጠ፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,101,'4:101','በምድርም ላይ በተጓዛችሁ ጊዜ እነዚያ የካዱት ያውኩናል ብላችሁ ብትፈሩ ከሶላት (ባለ አራት ረከዓት የኾኑትን) ብታሳጥሩ በናንተ ላይ ኀጢአት የለም፡፡ ከሓዲዎች ለእናንተ በእርግጥ ግልጽ ጠላቶች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,102,'4:102','በውስጣቸውም በኾንክና ሶላትን ለእነርሱ ባስገደድካቸው ጊዜ ከእነሱ አንዲት ጭፍራ ካንተ ጋር ትቁም፡፡ መሣሪያዎቻቸውንም ይያዙ፡፡ በግንባራቸውም በተደፉ ጊዜ ከስተኋላችሁ ይኹኑ፡፡ (እነዚህ ይኺዱና) ያልሰገዱትም ሌሎቹ ጭፍሮች ይምጡ፡፡ ከአንተም ጋር ይስገዱ፡፡ ጥንቃቄያቸውንና መሣሪያዎቻቸውንም ይያዙ፡፡ እነዚያ የካዱት ከመሣሪያዎቻችሁና ከጓዞቻችሁ ብትዘነጉና በእናንተ ላይ አንዲትን መዘንበል ቢዘነበሉ ተመኙ፡፡ ከዝናብም የኾነ ችግር በእናንተ ቢኖር ወይም ሕመምተኞች ብትኾኑ መሣሪያዎቻችሁን ብታስቀምጡ በናንተ ላይ ኀጢአት የለም፡፡ ጥንቃቄያችሁንም ያዙ፡፡ አላህ ለከሓዲዎች አዋራጅን ቅጣት አዘጋጀ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,103,'4:103','ሶላትንም በፈጸማችሁ ጊዜ ቆማችሁም ተቀምጣችሁም በጎኖቻችሁም ላይ ተጋድማችሁ አላህን አውሱ፡፡ በረጋችሁም ጊዜ ሶላትን (አሟልታችሁ) ስገዱ፡፡ ሶላት በምእምናን ላይ በጊዜያት የተወሰነች ግዴታ ናትና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,104,'4:104','ሰዎቹንም በመፈለግ አትስነፉ፡፡ (ስትቆስሉ) የምትታመሙ ብትኾኑ እናንተ እንደምትታመሙ እነሱም ይታመማሉ፡፡ የማይከጅሉትንም ከአላህ ትከጅላላችሁ፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,105,'4:105','እኛ በሰዎች መካከል አላህ ባሳወቀህ ልትፈርድ ወደ አንተ መጽሐፉን በእውነት አወረድን፡፡ ለከዳተኞችም ተከራካሪ አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,106,'4:106','አላህንም ምሕረትን ለምን፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,107,'4:107','ከእነዚያም ነፍሶቻቸውን ከሚበድሉት ላይ አትከራከር፡፡ አላህ ከዳተኛ ኃጢአተኛ የኾነን ሰው አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,108,'4:108','ከሰው ይደበቃሉ፡፡ አላህም እርሱ (በዕውቀቱ) ከእነሱ ጋር ሲኾን ከንግግር የማይወደውን ነገር (በልቦቻቸው) በሚያሳድሩ ጊዜ (ከእርሱ) አይደበቁም፡፡ አላህም በሚሠሩት ሁሉ (በዕውቀቱ) ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,109,'4:109','እነሆ እናንተ እነዚያ በቅርቢቱ ሕይወት ከነሱ የተከራከራችሁ ናችሁ፡፡ በትንሣኤ ቀን አላህን ስለእነሱ የሚከራከር ማን ነው ወይስ ለእነሱ ኃላፊና መመኪያ የሚኾን ማን ነው');
INSERT INTO "sadiq_sani" VALUES(4,110,'4:110','መጥፎም የሚሠራ ሰው ወይም ነፍሱን የሚበድል ከዚያም (ተጸጽቶ) አላህን ምሕረት የሚለምን አላህን መሓሪ አዛኝ ኾኖ ያገኘዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,111,'4:111','ኀጢአትንም የሚሠራ ሰው የሚሠራው ጥፋት በራሱ ላይ ነው፡፡ አላህም ዐዋቂ ጥበበኛ ነው፤');
INSERT INTO "sadiq_sani" VALUES(4,112,'4:112','ኀጢአትን ወይም አበሳን የሚሠራም ሰው ከዚያም በእርሱ ንጹሕን ሰው የሚሰድብ ቅጥፈትንና ግልጽ አበሳን በቁርጥ ተሸከመ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,113,'4:113','የአላህም ችሮታና እዝነቱ ባንተ ላይ ባልነበረ ኖሮ ከእነሱ የኾኑ ጭፍሮች ሊያሳስቱህ ባሰቡ ነበር፡፡ ነፍሶቻቸውንም እንጅ ሌላን አያሳስቱም፡፡ በምንም አይጐዱህም፡፡ አላህም በአንተ ላይ መጽሐፍንና ጥበብን አወረደ፡፡ የማታውቀውንም ሁሉ አስተማረህ፡፡ የአላህም ችሮታ በአንተ ላይ ታላቅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,114,'4:114','ከውይይታቸው በምጽዋት ወይም በበጎ ሥራ ወይም በሰዎች መካከል በማስታረቅ ከሚያዙ ሰዎች ውይይት በስተቀር በብዙው ውስጥ ደግ ነገር የለበትም፡፡ ይኸንንም (የተባለውን) የአላህን ውዴታ ለመፈለግ የሚሠራ ሰው ታላቅ ምንዳን እንሰጠዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,115,'4:115','ቅኑም መንገድ ለእርሱ ከተገለጸ በኋላ መልክተኛውን የሚጨቃጨቅና ከምእምኖቹ መንገድ ሌላ የኾነን የሚከተል ሰው (በዚህ ዓለም) በተሾመበት (ጥመት) ላይ እንሾመዋለን፤ ገሀነምንም እናገባዋለን፡፡ መመለሻይቱም ከፋች!');
INSERT INTO "sadiq_sani" VALUES(4,116,'4:116','አላህ በርሱ የማጋራትን (ወንጀል) አይምርም፡፡ ከዚህም ወዲያ ያለውን ለሚሻው ሰው ይምራል፡፡ በአላህም የሚያጋራ ሰው (ከእውነት) የራቀን መሳሳት በእርግጥ ተሳሳተ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,117,'4:117','ከእርሱ ሌላ እንስታትን እንጅ አይገዙም፡፡ አመጸኛ ሰይጣንንም እንጅ ሌላን አይግገዙም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,118,'4:118','«አላህ የረገመውን ከባሮችህም የተወሰነን ድርሻ በእርግጥ እይዛለሁ» ያለውን (ሰይጣን እንጅ ሌላን አይከተሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(4,119,'4:119','በእርግጥ አጠማቸዋለሁም፡፡ (ከንቱን) አስመኛቸዋለሁም፡፡ አዛቸውምና የእንስሶችን ጆሮዎች ይተለትላሉ፡፡ አዛቸውምና የአላህን ፍጥረት ይለውጣሉ፤ (ያለውን ይከተላሉ)፡፡ ከአላህም ሌላ ሰይጣንን ረዳት አድርጎ የሚይዝ ሰው ግልጽ ክስረትን በአርግጥ ከሰረ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,120,'4:120','(የማይፈጸመውን) ተስፋ ይሰጣቸዋል፡፡ ያስመኛቸዋልም፡፡ ሰይጣንም ለማታለል እንጅ አይቀጥራቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,121,'4:121','እነዚያ መኖሪያቸው ገሀነም ናት፡፡ ከርስዋም መሸሻን አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,122,'4:122','እነዚያም ያመኑና በጎ ሥራዎችን የሠሩ በሥራቸው ወንዞች የሚፈሱባቸውን ገነቶች ዘላለም በውስጣቸው ዘውታሪዎች ሲኾኑ እናገባቸዋለን፡፡ አላህ እውነተኛን ተስፋ ሰጣቸው፡፡ በንግግርም ከአላህ ይበልጥ እውነተኛ ማን ነው');
INSERT INTO "sadiq_sani" VALUES(4,123,'4:123','(ነገሩ) በምኞታችሁና በመጽሐፉ ሰዎች ምኞት አይደለም፡፡ መጥፎን የሚሠራ ሰው በእርሱ ይቀጣል፡፡ ለእርሱም ከአላህ ሌላ ጠባቂንም ረዳትንም አያገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,124,'4:124','ከወንድ ወይም ከሴት እርሱ አማኝ ኾኖ ከበጎ ሥራዎች አንዳችን የሚሠራም ሰው እነዚያ ገነትን ይገባሉ፡፡ በተምር ፍሬ ላይ ያለችን ነጥብ ያክል እንኳ አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,125,'4:125','እርሱ መልካም ሠሪ ኾኖ ፊቱን ለአላህ ከሰጠና የኢብራሂምንም መንገድ ቀጥተኛ ሲኾን ከተከተለ ሰው ይበልጥ ሃይማኖቱ ያማረ ማን ነው አላህም ኢብራሂምን ፍጹም ወዳጅ አድርጐ ያዘው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,126,'4:126','በሰማያት ያለውና በምድር ያለው ሁሉ የአላህ ነው፡፡ አላህም በነገሩ ሁሉ በዕውቀት ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,127,'4:127','በሴቶችም ነገር ያስተቹሃል፡፡ አላህ በእነርሱ ይነግራችኋል፡፡ ያም በመጽሐፉ በእናንተ ላይ የሚነበበው በእነዚያ ለእነርሱ (ከውርስ) የተጻፈላቸውን በማትሰጧቸውና ልታገቧቸው በማትፈልጉት የቲሞች ሴቶች (ይህንን እንዳታደርጉ) ከሕፃናትም ለኾኑት ደካሞች መብታቸውን እንድትሰጡ ለየቲሞችም በትክክል አንድትቆሙ (ይነግራችኋል) በላቸው፡፡ ከበጎም ሥራ ማናቸውንም ብትሠሩ አላህ እርሱን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,128,'4:128','ሴትም ከባልዋ ጥላቻን ወይም ፊቱን ማዞርን ብታውቅ በመካከላቸው መስማማትን ቢስማሙ በሁለቱ ላይ ኀጢአት የለም፡፡ መታረቅም መልካም ነው፡፡ ነፍሶችም ንፍገት ተጣለባቸው፡፡ መልካምም ብትሠሩ ብትጠነቀቁም አላህ በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,129,'4:129','በሴቶችም መካከል ምንም እንኳ ብትጓጉ (በፍቅር) ለማስተካከል አትችሉም፡፡ እንደ ተንጠለጠለችም አድርጋችሁ ትተዋት ዘንድ (ወደምትወዷት) መዘንበልን ሁሉ አትዘንበሉ፡፡ ብታበጁም ብትጠነቀቁም አላህ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,130,'4:130','ቢለያዩም አላህ ሁሉንም በችሮታው ያብቃቃቸዋል፡፡ አላህም ችሮታው ሰፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,131,'4:131','በሰማያት ያለውና በምድርም ያለው ሁሉ የአላህ ነው፡፡ እነዚያንም ከበፊታችሁ መጽሐፍን የተሰጡትን እናንተንም አላህን ፍሩ በማለት በእርግጥ አዘዝን፡፡ ብትክዱም በሰማያትና በምድር ያለው ሁሉ የአላህ ነው፡፡ (አትጐዱትም)፡፡ አላህም ተብቃቂ ምስጉን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,132,'4:132','በሰማያት ያለውና በምድርም ያለው ሁሉ የአላህ ነው፡፡ መመከያም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,133,'4:133','ሰዎች ሆይ!(አላህ) ቢሻ ያስወግዳችኋል፡፡ ሌሎችንም ያመጣል፡፡ አላህም በዚህ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,134,'4:134','የቅርቢቱን ዓለም ምንዳ የሚፈልግ የኾነ ሰው አላህ ዘንድ የቅርቢቱና የመጨረሻይቱ ምንዳ አልለ፡፡ አላህም ሰሚ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,135,'4:135','እናንተ ያመናችሁ ሆይ! በትክክል (ፍትሕ) ቀዋሚዎች በነፈሶቻችሁ ወይም በወላጆችና በቅርብ ዘመዶች ላይ ቢኾንም እንኳ ለአላህ መስካሪዎች ኹኑ፡፡ ሀብታም ወይም ድኻ ቢኾን አላህ በእነርሱ (ከእናንተ) ይበልጥ ተገቢ ነው፡፡ እንዳታደሉም ዝንባሌን አትከተሉ፡፡ ብታጠምሙም ወይም (መመስከርን) ብትተው አላህ በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,136,'4:136','እናንተ ያመናችሁ ሆይ! በአላህና በመልክተኛው በዚያም በመልክተኛው ላይ ባወረደው መጽሐፍ በዚያም ከበፊቱ ባወረደው መጽሐፍ እመኑ፡፡ በአላህና በመላእክቱም፣ በመጽሐፎቹም፣ በመልክተኞቹም፣ በመጨረሻውም ቀን፣ የካደ ሰው (ከእውነት) የራቀን መሳሳት በእርግጥ ተሳሳተ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,137,'4:137','እነዚያ (በሙሳ) ያመኑና ከዚያም (ወይፈንን በመገዛት) የካዱ ከዚያም (በእርሱ) ያመኑ ከዚያም (በዒሳ) የካዱ ከዚያም (በሙሐመድ) ክሕደትን የጨመሩ አላህ ለእነሱ የሚምራቸውና ቅኑን መንገድ የሚመራቸው አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,138,'4:138','መናፍቃንን ለእነርሱ አሳማሚ ቅጣት ያላቸው መኾኑን አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,139,'4:139','እነዚያ ከምእምናን ሌላ ከሓዲዎችን ወዳጆች አድርገው የሚይዙ እነሱ ዘንድ ልቅናን ይፈልጋሉን ልቅናውም ሁሉ ለአላህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,140,'4:140','በእናንተም ላይ በመጽሐፉ ውስጥ የአላህን አንቀጾች በርሷ ሲካድባትና በርሷም ሲላገጥባት በሰማችሁ ጊዜ በሌላ ወሬ ውስጥ እስከሚገቡ ድረስ ከእነሱ ጋር አትቀመጡ፡፡ ማለትን በእርግጥ አወረደ፡፡ እናንተ ያንጊዜ ብጤያቸው ናችሁና፡፡ አላህ መናፍቃንን እና ከሓዲዎችን በሙሉ በገሀነም ውስጥ ሰብሳቢ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(4,141,'4:141','እነዚያ በእናንተ (የጊዜን መዘዋወር) የሚጠባበቁ ናቸው፡፡ ለእናንተም ከአላህ የኾነ አሸናፊነት «ቢኖራችሁ ከእናንተ ጋር አልነበርንምን» ይላሉ፡፡ ለከሓዲዎችም ዕድል ቢኖር «(ከአሁን በፊት) በእናንተ ላይ አልተሾምንምን (እና አልተውናችሁምን) ከምእምናንም (አደጋ) አልከለከልናችሁምን» ይላሉ፡፡ አላህም በትንሣኤ ቀን በመካከላችሁ ይፈርዳል፡፡ አላህም ለከሓዲዎች በምእምናን ላይ መንገድን በፍጹም አያደርግም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,142,'4:142','መናፍቃን አላህን ያታልላሉ፡፡ እርሱም አታላያቸው ነው፡፡ (ይቀጣቸዋል)፡፡ ወደ ሶላትም በቆሙ ጊዜ ታካቾች ስዎችን የሚያሳዩ ኾነው ይቆማሉ፡፡ አላህንም ጥቂትን እንጅ አያወሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,143,'4:143','በዚህ መካከል ወላዋዮች ኾነው (ያሳያሉ)፡፡ ወደእነዚህም ወደእነዚያም አይደሉም፡፡ አላህም ያሳሳተውን ሰው ለእርሱ (የቅንነት) መንገድን በፍፁም አታገኝለትም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,144,'4:144','እናንተ ያመናችሁ ሆይ! ከምእምናን ሌላ ከሓዲዎችን ወዳጆች አድርጋችሁ አትያዙ፤ ለአላህ በናንተ ላይ ግልጽ ማስረጃዎችን ልታደርጉ ትፈልጋላችሁን');
INSERT INTO "sadiq_sani" VALUES(4,145,'4:145','መናፍቃን በእርግጥ ከእሳት በታችኛው አዘቅት ውስጥ ናቸው፡፡ ለእነሱም ረዳትን አታገኝላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,146,'4:146','እነዚያ የተመለሱ (ሥራቸውን) ያሳመሩም፤ በአላህም የተጠበቁ፤ ሃይማኖታቸውንም ለአላህ ፍጹም ያደረጉ ሲቀሩ፡፡ እነዚያስ ከምእምናን ጋር ናቸው፡፡ ለምእምናንም አላህ ታላቅን ምንዳ በእርግጥ ይሰጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,147,'4:147','ብታመሰግኑና ብታምኑ አላህ እናንተን በመቅጣት ምን ያደርጋል አላህም ወረታን መላሽ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,148,'4:148','አላህ ከንግግር በክፉው መጮህን ከተበደለ ሰው (ጩኸት) በቀር አይወድም፡፡ አላህ ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,149,'4:149','ደግ ነገርን ብትገልጹ ወይም ብትደብቁት ወይም ከመጥፎ ነገር (ከበደል) ይቅርታ ብታደርጉ አላህ ይቅር ባይ ኃያል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,150,'4:150','እነዚያ በአላህና በመልክተኞቹ የሚክዱ፣ በአላህና በመልክተኞቹም መካከል መለየትን የሚፈልጉ፣ «በከፊሉም እናምናለን በከፊሉም እንክዳለን» የሚሉ፣ በዚህም መካከል መንገድን ሊይዙ የሚፈልጉ፤');
INSERT INTO "sadiq_sani" VALUES(4,151,'4:151','እነዚያ በውነት ከሓዲዎቹ እነርሱ ናቸው፡፡ ለከሓዲዎችም አዋራጅን ቅጣት አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,152,'4:152','እነዚያም በአላህና በመልክተኞቹ ያመኑ ከነርሱም በአንድም መካከል ያልለዩ እነዚያ ምንዳዎቻቸውን በእርግጥ ይሰጣቸዋል፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,153,'4:153','የመጽሐፉ ሰዎች በነሱ ላይ ከሰማይ መጽሐፍን እንድታወርድ ይጠይቁሃል፡፡ ከዚያም የከበደን (ነገር) ሙሳን በእርግጥ ጠይቀዋል፡፡ «አላህንም በግልጽ አሳየን» ብለዋል፡፡ በበደላቸውም መብረቅ ያዘቻቸው፡፡ ከዚያም ተዓምራቶች ከመጡላቸው በኋላ ወይፈኑን (አምላክ አድርገው) ያዙ፡፡ ከዚያም ይቅር አልን፡፡ ሙሳንም ግልጽ ስልጣንን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,154,'4:154','በቃል ኪዳናቸውም ምክንያት የጡርን ተራራ ከበላያቸው አነሳን፡፡ ለእነሱም «ደጃፉን አጎንባሾች ኾናችሁ ግቡ» አልን፡፡ ለእነርሱም «በሰንበት ቀን ትዕዛዝን አትተላልፉ» አልን፡፡ ከእነሱም ከባድን ቃል ኪዳን ያዝን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,155,'4:155','ቃል ኪዳናቸውንም በማፍረሳቸው፣ በአላህም አንቀጾች በመካዳቸው፣ ነቢያትንም ያለ አግባብ በመግደላቸውና «ልቦቻችን ሽፍኖች ናቸው» በማለታቸው ምክንያት (ረገምናቸው)፡፡ በእውነቱ በክህደታቸው ምክንያት አላህ በርሷ (በልቦቻቸወ) ላይ አተመ፡፡ ስለዚህ ጥቂትን እንጅ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,156,'4:156','በመካዳቸውም በመርየም ላይ ከባድ ቅጥፈትን በመናገራቸውም ምክንያት (ረገምናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(4,157,'4:157','«እኛ የአላህን መልክተኛ የመርየምን ልጅ አልመሲሕ ዒሳን ገደልን» በማለታቸውም (ረገምናቸው)፡፡ አልገደሉትም አልሰቀሉትምም፡፡ ግን ለእነሱ (የተገደለው ሰው በዒሳ) ተመሰለ፡፡ እነዚያም በእርሱ ነገር የተለያዩት ከእርሱ (መገደል) በመጠራጠር ውስጥ ናቸው፡፡ ጥርጣሬን ከመከተል በስተቀር በእርሱ ነገር ምንም ዕውቀት የላቸውም፡፡ በእርግጥም አልገደሉትም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,158,'4:158','ይልቁንስ አላህ (ኢሳን) ወደርሱ አነሳው፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,159,'4:159','ከመጽሐፉም ሰዎች ከመሞቱ በፊት በእርሱ (በዒሳ) በእርግጥ የሚያምን እንጅ (አንድም) የለም፡፡ በትንሣኤም ቀን በነሱ ላይ መስካሪ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,160,'4:160','ከእነዚያ ይሁዳውያን ከኾኑትም በተገኘው በደል ሰዎችንም ከአላህ መንገድ በብዙ በመከልከላቸው ምክንያት ለእነሱ ተፈቅደው የነበሩትን ጣፋጮች ምግቦች በእነርሱ ላይ እርም አደረግንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,161,'4:161','ከእርሱ በቁርጥ የተከለከሉ ሲኾኑ አራጣንም በመያዛቸውና የሰዎችን ገንዘቦች ያለ አግባብ (በጉቦ) በመብላታቸውም ምክንያት (የተፈቀደላቸውን እርም አደረግንባቸው)፡፡ ከእነሱም ለከሓዲዎቹ አሳማሚን ቅጣት አዘጋጀን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,162,'4:162','ግን ከነርሱ ውስጥ በዕውቀት የጠለቁትና ምእምናኖቹ በአንተ ላይ በተወረደውና ከአንተም በፊት በተወረደው መጽሐፍ የሚያምኑ ሲኾኑ ሶላትንም ደንቡን ጠብቀው ሰጋጆችን (እናመሰግናለን)፡፡ ዘካንም ሰጪዎቹ በአላህና በመጨረሻዎቹም ቀን አማኞቹ እነዚያ ታላቅ ምንዳን በእርግጥ እንሰጣቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,163,'4:163','እኛ ወደ ኑሕና ከርሱ በኋላ ወደ ነበሩት ነቢያት እንዳወረድን ወደ አንተም አወረድን፡፡ ወደ ኢብራሂምም፣ ወደ ኢስማዒልም፣ ወደ ኢስሓቅም፣ ወደ ያዕቆብም፣ ወደ ነገዶቹም፣ ወደ ዒሳም፣ ወደ አዩብም፣ ወደ ዩኑስም፣ ወደ ሃሩንና፣ ወደ ሱለይማንም አወረድን፡፡ ለዳውድም ዘቡርን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,164,'4:164','ከዚህም በፊት በአንተ ላይ በእርግጥ የተረክናቸውን መልክተኞች ባንተ ላይም ያልተረክናቸውን መልክተኞች (እንደላክን ላክንህ)፡፡ አላህም ሙሳን ማነጋገርን አነጋገረው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,165,'4:165','ከመልክተኞቹ በኋላ ለሰዎች በአላህ ላይ አስረጅ እንዳይኖር አብሳሪዎችና አስፈራሪዎች የኾኑን መልክተኞች (ላክን)፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,166,'4:166','ግን አላህ ባንተ ላይ ባወረደው ይመሰክራል፡፡ በዕውቀቱ አወረደው፡፡ መላእክቱም ይመሰክራሉ፡፡ መስካሪም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,167,'4:167','እነዚያ የካዱት ከአላህም መንገድ ያገዱት (ከእውነት) የራቀን መሳሳት በእርግጥ ተሳሳቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,168,'4:168','እነዚያ የካዱ የበደሉም አላህ የሚምራቸውና (ቅን) መንገድን የሚመራቸው አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,169,'4:169','ግን በውስጧ ዘለዓለም ዘውታሪዎች ሲኾኑ የገሀነምን መንገድ (ይመራቸዋል)፡፡ ይህም በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,170,'4:170','እናንተ ሰዎች ሆይ! መልክተኛው እውነትን ከጌታችሁ አመጣላችሁ፡፡ እመኑም ለናንተ የተሻለ (ይኾናል)፡፡ ብትክዱም (አትጐዱትም)፡፡ በሰማያትና በምድር ያለው ሁሉ የአላህ ነውና፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(4,171,'4:171','እናንተ የመጽሐፉ ሰዎች ሆይ! በሃይማኖታችሁ ወሰንን አትለፉ፡፡ በአላህም ላይ እውነትን እንጅ አትናገሩ፡፡ የመርየም ልጅ አልመሲሕ ዒሳ የአላህ መልክተኛ ወደ መርየም የጣላት «የኹን» ቃሉም ከእርሱ የኾነ መንፈስም ብቻ ነው፡፡ በአላህና በመልክተኞቹም እመኑ፡፡ « (አማልክት) ሦስት ናቸው» አትበሉም፡፡ ተከልከሉ፤ ለእናንተ የተሻለ ይኾናልና፡፡ አላህ አንድ አምላክ ብቻ ነው፡፡ ለእርሱ ልጅ ያለው ከመኾን የጠራ ነው፡፡ በሰማያትና በምድር ያለ ሁሉ የርሱ ነው፡፡ መመኪያም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(4,172,'4:172','አልመሲሕ (ኢየሱስ) ለአላህ ባሪያ ከመኾን ፈጽሞ አይጠየፍም፡፡ ቀራቢዎች የኾኑት መላእክትም (አይጠየፉም)፡፡ እርሱን ከመገዛት የሚጠየፍና የሚኮራም ሰው (አላህ) ሁሉንም ወደ እርሱ በእርግጥ ይሰበስባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,173,'4:173','እነዚያማ ያመኑት መልካሞችንም የሠሩት ምንዳዎቻቸውን ይሞላላቸዋል፡፡ ከችሮታውም ይጨምርላቸዋል፡፡ እነዚያን የተጠየፉትንና የኮሩትንማ አሳማሚን ቅጣት ይቀጣቸዋል፡፡ ከአላህም ሌላ ለእነሱ ዝምድንና ረዳትን አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(4,174,'4:174','እናንተ ሰዎች ሆይ! ከጌታችሁ ዘንድ በእርግጥ አስረጅ መጣላችሁ፤ ወደእናንተም ገላጭ የኾነን ብርሃን (ቁርኣንን) አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(4,175,'4:175','እነዚያማ በአላህ ያመኑ በርሱም የተጠበቁ ከሱ በኾነው እዝነትና ችሮታ ውስጥ በእርግጥ ያስገባቸዋል፡፡ ወደእርሱም ቀጥተኛ መንገድን ይመራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(4,176,'4:176','ይጠይቁሃል በላቸው፡- «ወላጅና ልጅ ሳይኖረው በሚወረስ ሰው አላህ ይነግራችኋል፡፡ ለእርሱ ልጅ የሌለው ሰው ቢሞት ለእርሱም እኅት ብትኖረው ለእርስዋ ከተወው ረጀት ግማሹ አላት፡፡ እርሱም ለርሷ ልጅ የሌላት እንደኾነች (በሙሉ) ይወርሳታል፡፡ ሁለት (እኅቶች ወይም ከሁለት በላይ) ቢኾኑም ከተወው ረጀት ከሦስት ሁለት እጁ አላቸው፡፡ ወንድሞች (ና እኅቶች) ወንዶችና ሴቶች ቢኾኑም ለወንዱ የሁለት ሴቶች ድርሻ ብጤ አለው፡፡ አላህ እንዳትሳሳቱ ለእናንተ ያብራራል፡፡ አላህም በነገሩ ሁሉ ዐዋቂ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,1,'5:1','እናንተ ያመናችሁ ሆይ! በቃል ኪዳኖች ሙሉ፡፡ በእናንተ ላይ የሚነበበው ብቻ ሲቀር፡፡ የግመል፣ የከብት፣ የበግና የፍየል እንስሳዎች እናንተ በሐጅ ሥራ ላይ ኾናችሁ ማደንን የተፈቀደ ሳታደረጉ ለእናንተ ተፈቀዱ፡፡ አላህ የሚሻውን ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,2,'5:2','እናንተ ያመናችሁ ሆይ! የአላህ (ክልክል ያደረጋቸውን) ምልክቶች የተፈቀዱ አታድርጉ፡፡ የተከበረውንም ወር በመጋደል አትድፈሩ፡፡ ወደ መካ የሚነዱትን መስዋዕትና የሚታበቱባቸውንም ገመዶች ከጌታቸው ችሮታንና ውዴታን ፈልገው ወደ ተከበረው ቤት አሳቢዎችንም ሰዎች (አትንኩ)፡፡ ከሐጅም ሥራ በወጣችሁ ጊዜ ዐድኑ፡፡ ከተከበረው መስጊድ ስለከለከሉዋችሁ ሰዎችን መጥላትም (በነሱ ላይ) ወሰን እንድታልፉ አይገፋፋችሁ፡፡ በበጎ ነገርና አላህን በመፍራትም ተረዳዱ፡፡ ግን በኃጢአትና ወሰንን በማለፍ አትረዳዱ፡፡ አላህንም ፍሩ፡፡ አላህ ቅጣተ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,3,'5:3','በክት፣ ፈሳሽ ደምም፣ የእሪያ (አሣማ) ሥጋም፣ በርሱ ከአላህ (ስም) ሌላ የተነሳበትም፣ የታነቀችም፣ ተደብድባ የተገደለችም፣ ተንከባላ የሞተችም፣ በቀንድ ተውግታ የሞተችም፣ ከርሷ አውሬ የበላላትም (ከእነዚህ በሕይወት ደርሳችሁ) ያረዳችሁት ብቻ ሲቀር ለጣዖታትም የታረደው በአዝላምም ዕድልን መፈለጋችሁ በእናንተ ላይ እርም ተደረገ፡፡ ይህ ድርጊት አመጽ ነው፡፡ ዛሬ እነዚያ የካዱት ከሃይማኖታችሁ ተስፋ ቆረጡ፡፡ ስለዚህ አትፍሩዋቸው፡፡ ፍሩኝም፡፡ ዛሬ ሃይማኖታችሁን ለናንተ ሞላሁላችሁ፡፡ ጸጋዬንም በናንተ ላይ ፈጸምኩ፡፡ ለእናንተም ኢስላምን ከሃይማኖት በኩል ወደድኩ፡፡ በረኃብ ወቅት ወደ ኃጢአት ያዘነበለ ሳይሆን (እርም የኾኑትን ለመብላት) የተገደደ ሰውም (ይብላ)፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,4,'5:4','ለእነርሱ ምን እንደተፈቀደላቸው ይጠይቁሃል፡፡ በላቸው፡- «ለእናንተ መልካሞች ሁሉና እነዚያም ከአዳኞች አሰልጣኞች ኾናችሁ ያስተማራችኋቸው (ያደኑት) ተፈቀደላችሁ፡፡ አላህ ከአስተማራችሁ ዕውቀት ታስተምሯቸዋላችሁ፡፡ ለእናንተም ከያዙላችሁ ነገር ብሉ፡፡ በርሱም ላይ የአላህን ስም አውሱ፡፡ አላህንም ፍሩ አላህ ምርመራው ፈጣን ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,5,'5:5','ዛሬ መልካሞች ሁሉ ለናንተ ተፈቀዱ፡፡ የእነዚያም መጽሐፍን የተሰጡት ሰዎች ምግብ (ያረዱት) ለእናንተ የተፈቀደ ነው፡፡ ምግባችሁም ለነሱ የተፈቀደ ነው፡፡ ከምእመናት ጥብቆቹም ከእነዚያ ከእናንተ በፊት መጽሐፍን ከተሰጡት ሴቶች ጥብቆቹም፤ ዘማዊዎችና (ዝሙተኞች) የምስጢር ወዳጅ ያዢዎች ሳትኾኑ ጥብቆች ኾናችሁ መህሮቻቸውን በሰጣችኋቸው ጊዜ (ልታገቡዋቸው የተፈቀዱ ናቸው)፡፡ ከእምነትም የሚመለስ ሰው በእርግጥ ሥራው ተበላሸ፡፡ በመጨረሻም ቀን እርሱ ከከሳሪዎቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,6,'5:6','እናንተ ያመናችሁ ሆይ! ወደ ሶላት በቆማችሁ (ለመቆም ባሰባችሁ) ጊዜ ፊቶቻችሁን፣ እጆቻችሁንም እስከ ክርኖች እጠቡ፡፡ ራሶቻችሁንም (በውሃ) አብሱ፡፡ እግሮቻችሁንም እስከ ቁርጭምጭሚቶች (እጠቡ)፡፡ (ሴት ጋር በመገናኘት ወይም በሌላ ምክንያት «ጀናባ») ብትኾኑ (ገላችሁን) ታጠቡ፡፡ በሽተኞች ወይም በጉዞ ላይ ብትኾኑም ወይም ከእናንተ አንዳችሁ ከዓይነ ምድር ቢመጣ ወይም ሴቶችን ብትነካኩና ውሃን ባታገኙ ንጹሕን የምድር ገጽ አስቡ ፡፡ ከሱም ፊቶቻችሁንና እጆቻችሁን አብሱ፡፡ አላህ በእናንተ ላይ ምንም ችግር ሊያደርግ አይሻም፡፡ ግን ታመሰግኑ ዘንድ ሊያጠራችሁና ጸጋውን በእናንተ ላይ ሊሞላ ይሻል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,7,'5:7','በእናንተም ላይ (የለገሰውን) የአላህን ጸጋ ያንንም ሰምተናል ታዘናልም ባላችሁ ጊዜ በርሱ ያጠበቀባችሁን ቃል ኪዳኑን አስታውሱ፡፡ አላህንም ፍሩ፡፡ አላህ በደረቶች ውስጥ ያለውን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,8,'5:8','እናንተ ያመናችሁ ሆይ! ለአላህ ቀጥተኞች በትክክል መስካሪዎች ኹኑ፡፡ ሕዝቦችንም መጥላት ባለማስተካከል ላይ አይገፋፋችሁ፡፡ አስተካክሉ፡፡ እርሱ (ማስተካከል) ለአላህ ፍራቻ በጣም የቀረበ ነው፡፡ አላህንም ፍሩ፡፡ አላህ በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,9,'5:9','እነዚያንም ያመኑትንና በጎ ሥራዎችን የሠሩትን አላህ (ገነትን) ተስፋ አደረገላቸው፡፡ ለእነርሱ ምሕረትና ታላቅ ምንዳ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,10,'5:10','እነዚያም የካዱና በተአምራቶቻችን ያስተባበሉ እነዚያ የእሳት ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,11,'5:11','እናንተ ያመናችሁ ሆይ! ሕዝቦች እጆቻቸውን ወደ እናንተ ሊዘረጉ ባሰቡና እጆቻቸውን ከእናንተ ላይ በከለከላችሁ ጊዜ በናንተ ላይ (የዋለላችሁን) የአላህን ጸጋ አስታውሱ፡፡ አላህንም ፍሩ፡፡ ምእመናንም በአላህ ላይ ይመኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,12,'5:12','አላህም የእስራኤልን ልጆች የጠበቀ ቃል ኪዳን በእርግጥ ያዘባቸው፡፡ ከነርሱም ዐስራ ሁለትን አለቆች አስነሳን፡፡ አላህም አላቸው፤ «እኔ ከናንተ ጋር ነኝ፤ ሶላትን ብታስተካክሉ ግዴታ ምጽዋትንም ብትሰጡ በመልክተኞቼም ብታምኑ ብትረዱዋቸውም ለአላህም መልካም ብድርን ብታበድሩ ኀጢአቶቻችሁን ከናንተ በእርግጥ አብሳለሁ፡፡ ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶችም በእርግጥ አገባችኋለሁ፡፡ ከዚህም በኋላ ከእናንተ የካደ ሰው ቀጥተኛውን መንገድ በእርግጥ ተሳሳተ፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,13,'5:13','ቃል ኪዳናቸውንም በማፍረሳቸው ረገምናቸው፡፡ ልቦቻቸውንም ደረቆች አደረግን፡፡ ቃላትን ከቦታዎቻቸው ይለውጣሉ፡፡ በርሱም ከታዘዙበት ነገር ፈንታን ተው፡፡ ከእነሱም ጥቂቶች ሲቀሩ ከነርሱ የኾነን ክዳት የምታውቅባቸው ከመኾን አትወገድም፡፡ ከእነርሱም ይቅርታ አድርግ እለፋቸውም፡፡ አላህ መልካም ሠሪዎችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,14,'5:14','ከእነዚያም እኛ ክርስቲያኖች ነን ካሉት የጠበቀ ቃል ኪዳናቸውን ያዝን፡፡ በእርሱም ከታዘዙበት ነገር ፈንታን ተውት፡፡ ስለዚህ እስከ ትንሣኤ ቀን ድረስ በመካከላቸው ጠብንና ጥላቻን ጣልንባቸው፡፡ አላህም ይሠሩት የነበሩትን ሁሉ በእርግጥ ይነግራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,15,'5:15','የመጽሐፉ ባለቤቶች ሆይ! ከመጽሐፉ ትሸሽጉት ከነበራችሁት ነገር ብዙውን ለእናንተ የሚገልጽ ከብዙውም የሚተው ሲኾን መልክተኛችን (ሙሐመድ) በእርግጥ መጣላችሁ፡፡ ከአላህ ዘንድ ብርሃንና ገላጭ መጽሐፍ በእርግጥ መጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,16,'5:16','አላህ ውዴታውን የተከተሉትን ሰዎች የሰላምን መንገዶች በእርሱ ይመራቸዋል፡፡ በፈቃዱም ከጨለማዎች ወደ ብርሃን ያወጣቸዋል፡፡ ወደ ቀጥተኛም መንገድ ይመራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,17,'5:17','እነዚያ አላህ እርሱ የመርየም ልጅ አልመሲሕ ነው ያሉ በእርግጥ ካዱ፡፡ የመርየምን ልጅ አልመሲሕን እናቱንም በምድር ያለንም ሁሉ ለማጥፋት ቢሻ ከአላህ ማዳንን የሚችል ማነው በላቸው፡፡ የሰማያትና የምድር የመካከላቸውም ንግሥና የአላህ ብቻ ነው፡፡ የሚሻውን ይፈጥራል፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,18,'5:18','አይሁዶችና ክርስቲያኖችም «እኛ የአላህ ልጆችና ወዳጆቹ ነን» አሉ፡፡ «ታዲያ በኃጢአቶቻችሁ ለምን ያሰቃያችኋል አይደላችሁም እናንተ ከፈጠራቸው ሰዎች ናችሁ» በላቸው፡፡ ለሚሻው ሰው ይምራል፡፡ የሚሻውንም ሰው ይቀጣል፡፡ የሰማያትና የምድርም በመካከላቸውም ያለው ሁሉ ንግሥና የአላህ ብቻ ነው፡፡ መመለሻም ወደርሱ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,19,'5:19','እናንተ የመጽሐፉ ሰዎች ሆይ! «አብሳሪና አስፈራሪ አልመጣልንም» እንዳትሉ ከመልክተኞች በመቋረጥ ጊዜ ላይ ሲኾን (ሕጋችንን) የሚያብራራ ኾኖ መልክተኛችን በእርግጥ መጣላችሁ፡፡ አብሳሪና አስፈራሪም በእርግጥ መጣላችሁ፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,20,'5:20','ሙሳም ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፡- «ሕዝቦቼ ሆይ የአላህን ጸጋ በውስጣችሁ ነቢያትን ባደረገና ነገሥታትንም ባደረጋችሁ ከዓለማትም ለአንድም ያልሰጠውን ችሮታ በሰጣችሁ ጊዜ በእናንተ ላይ (ያደረገውን ጸጋ) አስታውሱ፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,21,'5:21','«ሕዝቦቼ ሆይ! ያችን አላህ ለእናንተ ያደረጋትን የተቀደሰችውን መሬት ግቡ፡፡ ወደ ኋላችሁም አትመለሱ፡፡ ከሳሪዎች ኾናችሁ ትመለሳላችሁና፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,22,'5:22','«ሙሳ ሆይ! በርስዋ ውስጥ ኀያላን ሕዝቦች አሉ፡፡ ከርስዋም እስከሚወጡ ድረስ እኛ ፈጽሞ አንገባትም፡፡ ከርስዋም ቢወጡ እኛ ገቢዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,23,'5:23','ከእነዚያ (አላህን) ከሚፈሩትና አላህ በእነርሱ ላይ ከለገሰላቸው የኾኑ ሁለት ሰዎች «በእነርሱ (በኀያሎቹ) ላይ በሩን ግቡ፡፡ በገባችሁትም ጊዜ እናንተ አሸናፊዎች ናችሁ፡፡ ምእመናንም እንደኾናችሁ በአላህ ላይ ተመኩ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,24,'5:24','«ሙሳ ሆይ! በውስጧ እስካሉ ድረስ እኛ ፈጽሞ ምን ጊዜም አንገባትም፡፡ ስለዚህ ኺድ አንተና ጌታህ ተጋደሉም እኛ እዚህ ተቀማጮች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,25,'5:25','«ጌታዬ ሆይ እኔ ራሴንና ወንድሜን በቀር (ላስገድድ) አልችልም፡፡ በእኛና በአመጸኞቹም ሕዝቦች መካከል ለይ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,26,'5:26','እርስዋም (የተቀደሰችው መሬት) በእነርሱ ላይ አርባ ዓመት እርም ናት፡፡ በምድረ በዳ ይንከራተታሉ፡፡ «በአመጸኞችም ሕዝቦች ላይ አትዘን» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,27,'5:27','በነርሱም ላይ የአደምን ሁለት ልጆች ወሬ ቁርባንን ባቀረቡና (አላህ) ከአንደኛቸው ተቀብሎ ከሌላው ባልተቀበለ ጊዜ (የኾነውን) በእውነት አንብብላቸው፡፡ «በእርግጥ እገድልሃለሁ» አለው፡፡ (ተገዳዩ) «አላህ የሚቀበለው እኮ ከጥንቁቆቹ ብቻ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,28,'5:28','«ልትገድለኝ እጅህን ወደኔ ብትዘረጋ እኔ ልገድልህ እጄን ወዳንተ የምዘረጋ አይደለሁም፡፡ እኔ የዓለማትን ጌታ አላህን እፈራለሁና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,29,'5:29','«እኔ በኃጢአቴና በኃጢአትህ ልትመለስና ከእሳት ጓዶች ልትኾን እሻለሁ፤ ይኽም የበደለኞች ቅጣት ነው» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(5,30,'5:30','ነፍሱም ወንድሙን መግደልን ለርሱ ሸለመችለት፤(አነሳሳችው፤)፡፡ ገደለውም፡፡ ከከሳሪዎቹም ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,31,'5:31','የወንድሙንም ሬሳ እንዴት እንደሚሸሽግ ያሳየው ዘንድ አላህ መሬትን የሚጭር ቁራን ላከለት፡፡ «ወይ እኔ የወንድሜን ሬሳ እሸሽግ ዘንድ እንደዚህ ቁራ ብጤ መኾን አቃተኝን» አለ፡፡ ከጸጸተኞችም ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,32,'5:32','በዚህ ምክንያት በእስራኤል ልጆች ላይ እነሆ ያለ ነፍስ (መግደል) ወይም በምድር ላይ ያለማጥፋት (ካልሆነ በስተቀር) ነፍስን የገደለ ሰው ሰዎቹን ሁሉ እንደገደለ ነው፤ ሕያው ያደረጋትም ሰው ሰዎቹን ሁሉ ሕያው እንዳደረገ ነው ማለትን ጻፍን፡፡ መልክተኞቻችንም በግልጽ ተዓምራት በእርግጥ መጡዋቸው፡፡ ከዚያም ከዚህ በኋላ ከእነሱ ብዙዎቹ በምድር ላይ ወሰንን አላፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,33,'5:33','የእነዚያ አላህንና መልክተኛውን የሚዋጉ በምድርም ላይ ለማጥፋት የሚተጉ ሰዎች ቅጣት መገደል ወይም መሰቀል ወይም እጆቻቸውንና እግሮቻቸውን በማፈራረቅ መቆረጥ ወይም ከአገር መባረር ነው፡፡ ይህ በነሱ በቅርቢቱ ዓለም ውርደት ነው፡፡ በመጨረሻይቱም ለእነርሱ ከባድ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,34,'5:34','እነዚያ በእነርሱ ላይ ከመቻላችሁ (ከመወሰናችሁ) በፊት የተጸጸቱ ሲቀሩ፡፡ አላህም መሓሪ አዛኝ መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,35,'5:35','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፡፡ ወደርሱም መቃረቢያን (መልካም ሥራን) ፈልጉ፡፡ ትድኑ ዘንድም በእርሱ መንገድ ታገሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,36,'5:36','እነዚያ የካዱት ሰዎች በምድር ላይ ያለው ሁሉ ከእርሱም ጋር ብጤው ከትንሣኤ ቀን ቅጣት በእርሱ ሊበዡበት ለእነርሱ በኖራቸው ኖሮ ከእነሱ ተቀባይን ባላገኙ ነበር፡፡ ለእነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,37,'5:37','ከእሳት ሊወጡ ይፈልጋሉ፡፡ እነርሱም ከርስዋ ወጪዎች አይደሉም፡፡ ለእነርሱም ዘውታሪ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,38,'5:38','ሰራቂውንና ሰራቂይቱንም በሠሩት ነገር ለቅጣት ከአላህ የኾነን መቀጣጫ እጆቻቸውን ቁረጡ፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,39,'5:39','ከበደሉም በኋላ የተጸጸተና ሥራውን ያሳመረ አላህ ጸጸቱን ከእርሱ ይቀበለዋል፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,40,'5:40','የሰማያትና የምድር ንግሥና የሱ (የአላህ) ብቻ መኾኑን አላወቅክምን የሚሻውን ሰው ይቀጣል፡፡ ለሚሻውም ሰው ይምራል፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,41,'5:41','አንተ መልክተኛ ሆይ! እነዚያ በክሕደት የሚቻኮሉት ከእነዚያ ልቦቻቸው ያላመኑ ሲኾኑ በአፎቻቸው «አመንን» ካሉትና ከእነዚያም አይሁድ ከኾኑት ሲኾኑ አያሳዝኑህ፡፡ (እነርሱ) ውሸትን አዳማጮች ናቸው፡፡ ለሌሎች ወዳንተ ላልመጡ ሕዝቦች አዳማጮች ናቸው፡፡ ንግግሮችን ከቦታቸው ሌላ ያጣምማሉ፡፡ «ይህንን (የተጣመመውን) ብትስሰጡ ያዙት፡፡ ባትስሰጡትም ተጠንቀቁ» ይላሉ፡፡ አላህም መፈተኑን የሚሻበትን ሰው ለእርሱ ከአላህ (ለመከላከል) ምንንም አትችልም፡፡ እነዚህ እነዚያ አላህ ልቦቻቸውን ማጥራትን ያልሻላቸው ናቸው፡፡ ለእነርሱ በቅርቢቱ ዓለም ውርደት አላቸው፡፡ ለእነርሱም በመጨረሻይቱ ዓለም ከባድ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,42,'5:42','ውሸትን አዳማጮች እርምን በላተኞች ናቸው፡፡ ወደአንተ ቢመጣም በመካከላቸው ፍረድ፡፡ ወይም ተዋቸው፡፡ ብትተዋቸውም ምንም አይጎዱህም፡፡ ብትፈርድም በመካከላቸው በትክክል ፍረድ፡፡ አላህ በትክክል ፈራጆችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,43,'5:43','እነርሱም ዘንድ ተውራት እያለች በውስጧ የአላህ ፍርድ ያለባት ስትኾን እንዴት ያስፈርዱሃል! ከዚያም ከዚህ በኋላ እንዴት ይሸሻሉ! እነዚያም በፍጹም ምእምናን አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,44,'5:44','እኛ ተውራትን በውስጥዋ መምሪያና ብርሃን ያለባት ስትኾን አወረድን፡፡ እነዚያ ትዕዛዝን የተቀበሉት ነቢያት በእነዚያ አይሁዳውያን በኾኑት ላይ በርሷ ይፈርዳሉ፡፡ ሊቃውንቱና ዐዋቂዎቹም ከአላህ መጽሐፍ እንዲጠብቁ በተደረጉትና በርሱም ላይ መስካሪዎች በኾኑት (ይፈርዳሉ)፡፡ ሰዎችንም አትፍሩ፡፡ ፍሩኝም፡፡ በአንቀጾቼም አነስተኛን ዋጋ አትለውጡ፡፡ አላህም ባወረደው ነገር ያልፈረደ ሰው እነዚያ ከሓዲዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,45,'5:45','በእነርሱም ላይ በውስጧ «ነፍስ በነፍስ ዓይንም በዓይን አፍንጫም በአፍንጫ ጆሮም በጆሮ ጥርስም በጥርስ (ይያዛል)፡፡» ቁስሎችንም ማመሳሰል አለባቸው ማለትን ጻፍን፡፡ በእርሱም የመጸወተ (የማረ) ሰው እርሱ ለርሱ (ለሠራው ኃጢአት) ማስተሰሪያ ነው፡፡ አላህም ባወረደው ነገር የማይፈርድ ሰው፤ እነዚያ በደለኞቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,46,'5:46','በፈለጎቻቸውም (በነቢያቶቹ ፈለግ) ላይ የመርየምን ልጅ ዒሳን ከተውራት በስተፊቱ ያለውን አረጋጋጭ ሲኾን አስከተልን፡፡ ኢንጂልንም በውስጡ ቀጥታና ብርሃን ያለበት በስተፊቱ ያለችውን ተውራትንም የሚያረጋግጥ ለጥንቁቆችም መሪና ገሳጭ ሲኾን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,47,'5:47','የኢንጅልም ባለቤቶች በውስጡ አላህ ባወረደው ሕግ ይፍረዱ፡፡ አላህም ባወረደው የማይፈርድ ሰው እነዚያ አመጸኞች እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,48,'5:48','ወደ አንተም መጽሐፉን ከበፊቱ ያለውን መጽሐፍ አረጋጋጭና በእርሱ ላይ ተጠባባቂ ሲኾን በእውነት አወረድን፡፡ በመካከላቸውም አላህ ባወረደው ሕግ ፍረድ፡፡ እውነቱም ከመጣልህ በኋላ ዝንባሌዎቻቸውን አትከተል፡፡ ከእናንተ ለሁሉም ሕግንና መንገድን አደረግን፡፡ አላህም በሻ ኖሮ አንድ ሕዝብ ባደረጋችሁ ነበር፡፡ ግን በሰጣችሁ ሕግጋት ሊሞክራችሁ (ለያያችሁ)፡፡ በጎ ሥራዎችንም (ለመሥራት) ተሽቀዳደሙ፤ መመለሻችሁ በጠቅላላ ወደ አላህ ነው፡፡ በእርሱም ትለያዩበት የነበራችሁትን ነገር ይነግራችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,49,'5:49','በመካከላቸውም አላህ ባወረደው ሕግ ፍረድ፡፡ ፍላጎቶቻቸውንም አትከተል፡፡ አላህም ወደ አንተ ካወረደው ከከፊሉ እንዳያሳስቱህ ተጠንቀቃቸው (ማለትን አወረድን)፡፡ ቢዞሩም አላህ የሚሻው በከፊሉ ኃጢአታቸው ሊቀጣቸው መኾኑን ዕወቅ፡፡ ከሰዎቹም ብዙዎቹ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,50,'5:50','የማይምንነትን ፍርድ ይፈልጋሉን ለሚያረጋግጡም ሰዎች ከአላህ ይበልጥ ፍርዱ ያማረ ማን ነው');
INSERT INTO "sadiq_sani" VALUES(5,51,'5:51','እናንተ ያመናችሁ ሆይ! አይሁዶችንና ክርስቲያኖችን ረዳቶች አድርጋችሁ አትያዙ፡፡ ከፊላቸው ለከፊሉ ረዳቶች ናቸው፡፡ ከእናንተም ውስጥ ረዳቶች የሚያደርጋቸው ሰው እርሱ ከነርሱ ነው፡፡ አላህ አመጸኞችን ሕዝቦች አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,52,'5:52','እነዚያንም በልቦቻቸው ውስጥ (የንፍቅና) በሽታ ያለባቸውን ሰዎች «የጊዜ መለዋወጥ እንዳያገኘን እንፈራለን የሚሉ» ሲኾኑ በእነርሱ (ለመረዳት) ሲቻኮሉ ታያቸዋለህ፡፡ አላህም ድል መንሳትን ወይም ከእርሱ ዘንድ የኾነን ነገር (ማጋለጥን) ሊያመጣ በነፍሶቻቸው ውስጥ በደበቁትም ነገር ተጸጻቾች ሲሆኑ ተረጋገጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,53,'5:53','እነዚያም ያመኑት ሰዎች፡- «እነዚያ እነርሱ ከእናንተ ጋር ነን ብለው የጠነከረ መሐላቸውን በአላህ የማሉት እነዚህ ናቸውን» ይላሉ፡፡ ሥራዎቻቸው ተበላሹ፡፡ ከሳሪዎችም ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,54,'5:54','እናንተ ያመናችሁ ሆይ! ከእናንተ ውስጥ ከሃይማኖቱ የሚመለስ ሰው አላህ (በእነርሱ ስፍራ) የሚወዳቸውንና የሚወዱትን፣ በምእመናን ላይ ትሑቶች፣ በከሓዲዎች ላይ ኀያላን የኾኑን በአላህ መንገድ የሚታገሉን የወቃሽን ወቀሳ የማይፈሩን ሕዝቦች በእርግጥ ያመጣል፡፡ ይኸ የአላህ ችሮታ ነው፤ ለሚሻው ሰው ይሰጠዋል፤ አላህም ችሮታው ሰፊ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,55,'5:55','ረዳታችሁ አላህና መልክተኛው እነዚያም ያመኑት ብቻ ናቸው፡፡ (እነርሱ) እነዚያ ሶላትን በደንቡ የሚሰግዱ እነርሱም ያጎነበሱ ኾነው ምጽዋትን የሚሰጡ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,56,'5:56','አላህንና መልክተኛውን እነዚያንም ያመኑትን የሚወዳጅ ሰው (ከአላህ ጭፍሮች ይኾናል)፡፡ የአላህም ጭፍሮች እነርሱ አሸናፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,57,'5:57','እናንተ ያመናችሁ ሆይ! ከእነዚያ ከበፊታችሁ መጽሐፍን ከተሰጡት እነዚያን ሃይማኖታችሁን ማላገጫና መጫወቻ አድርገው የያዙትን ከሓዲዎችንም ረዳቶች አድርጋችሁ አትያዙ፡፡ ምእምናንም እንደኾናችሁ አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,58,'5:58','ወደ ሶላትም በጠራችሁ ጊዜ (ጥሪይቱን) መሳለቂያና መጫወቻ አድርገው ይይዟታል፡፡ ይህ እነርሱ የማያውቁ ሕዝቦች በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,59,'5:59','«የመጽሐፉ ባለቤቶች ሆይ! በአላህና ወደኛ በተወረደው በፊትም በተወረደው ለማመናችን አብዛኞቻችሁም አመጸኞች ለመኾናችሁ እንጅ (ሌላን ነገር) ከኛ ትጠላላችሁን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,60,'5:60','«አላህ ዘንድ ቅጣቱ ከዚህ የከፋ ነገር ልንገራችሁን» በላቸው፡፡ (እርሱም) «አላህ የረገመው ሰው በእርሱም ላይ ተቆጣበት፡፡ ከእነርሱም ዝንጀሮዎችንና ከርከሮዎችን ያደረገው ጣዖትንም የተገዛ ሰው (ሃይማኖት) ነው፡፡ እነዚያ በጣም በከፋ ስፍራ ናቸው፡፡» ከቀጥተኛውም መንገድ በጣም የተሳሳቱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,61,'5:61','በመጡዋችሁም ጊዜ (ወደእናንተ) ከክህደት ጋር በእርግጥ የገቡ እነርሱም ከርሱ ጋር በእርግጥ የወጡ ሲኾኑ «አምነናል» ይላሉ፡፡ አላህም ይደብቁት የነበሩትን ነገር በጣም ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,62,'5:62','ከእነርሱም ብዙዎቹን በኃጢአትና በበደል እርምንም በመብላታቸው የሚጣደፉ ሲኾኑ ታያለህ፡፡ ይሠሩት የነበሩት ነገር በጣም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(5,63,'5:63','ቀሳውስቱና ሊቃውንቱ ከኃጢአት ንግግራቸውና እርምን ከመብላታቸው አይከለክሏቸውም ኖሮአልን ይሠሩት የነበሩት ነገር በጣም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(5,64,'5:64','አይሁዶችም «የአላህ እጅ የታሰረች ናት» አሉ፡፡ እጆቻቸው ይታሰሩ ባሉትም ነገር ይረገሙ፡፡ አይደለም (የአላህ) እጆቹ የተዘረጉ ናቸው፡፡ እንደሚሻ ይለግሳል፡፡ ከጌታህም ወደ አንተ የተወረደው (ቁርኣን) ከእነሱ ብዙዎቹን ትዕቢትንና ክህደትን በእርግጥ ይጨምራቸዋል፡፡ በመካከላቸውም ጠብንና ጥላቻን እስከ ትንሣኤ ቀን ድረስ ጣልን፡፡ ለጦር እሳትን ባያያዙ (ባጫሩ) ቁጥር አላህ ያጠፋታል፡፡ በምድርም ውስጥ ለማበላሸት ይሮጣሉ፡፡ አላህም አበላሺዎችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,65,'5:65','የመጽሐፉም ባለቤቶች ባመኑና (ከክህደትም) በተጠነቀቁ ኖሮ ከእነሱ ኃጢኣቶቻቸውን በእርግጥ ባበስንና የመጠቀሚያ ገነቶችንም በእርግጥ ባገባናቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(5,66,'5:66','እነርሱም ተውራትንና ኢንጂልን ከጌታቸውም ወደእነሱ የተወረደውን መጽሐፍ ባቋቋሙ (በሠሩበት) ኖሮ ከበላያቸውና ከእግሮቻቸው ሥር በተመገቡ ነበር፡፡ ከእነሱ ውስጥ ትክክለኞች ሕዝቦች አልሉ፡፡ ከእነሱም ብዙዎቹ የሚሠሩት ነገር ከፋ!');
INSERT INTO "sadiq_sani" VALUES(5,67,'5:67','አንተ መልክተኛ ሆይ! ከጌታህ ወዳንተ የተወረደውን አድርስ፡፡ ባትሠራም መልክቱን አላደረስክም፡፡ አላህም ከሰዎች ይጠብቅሃል፡፡ አላህ ከሓዲዎችን ሕዝቦች አያቀናምና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,68,'5:68','«እናንተ የመጽሐፉ ባለቤቶች ሆይ! ተውራትንና ኢንጂልን ከጌታችሁም ወደእናንተ የተወረደውን እስከምታቆሙ (እስከምትሠሩባቸው) ድረስ በምንም ላይ አይደላችሁም» በላቸው፡፡ ከጌታህም ወደ አንተ የተወረደው ቁርኣን ከእነሱ ብዙዎቹን ትዕቢትንና ክህደትን በእርግጥ ይጨምርባቸዋል፡፡ በከሓዲያን ሕዝቦችም ላይ አትዘን፡፡');
INSERT INTO "sadiq_sani" VALUES(5,69,'5:69','እነዚያ ያመኑና እነዚያም አይሁዳውያን የኾኑ፣ ሳቢያኖችም፣ ክርስቲያኖችም (ከነርሱ) በአላህና በመጨረሻው ቀን ያመነና መልካምን ሥራ የሠራ ሰው በነርሱ ላይ ፍርሃት የለባቸውም፡፡ እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,70,'5:70','የእስራኤልን ልጆች የጠበቀ ቃል ኪዳን በእርግጥ ያዝንባቸው፡፡ ወደእነሱም መልክተኞችን ላክን፡፡ ነፍሶቻቸው በማትወደው ነገር መልእክተኛ በመጣላቸው ቁጥር ከፊሉን አስተባበሉ፤ ከፊሉንም ይገድላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,71,'5:71','ፈተናም አለመኖርዋን ጠረጠሩ፡፡ ታወሩም፣ ደነቆሩም፣ ከዚያም አላህ ከነርሱ ላይ ጸጸትን ተቀበለ፡፡ ከዚያም ከእነሱ ብዙዎቹ ታወሩ፣ ደነቆሩም፡፡ አላህም የሚሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,72,'5:72','እነዚያ «አላህ እርሱ የመርየም ልጅ አልመሲሕ ነው» ያሉ በእርግጥ ካዱ፡፡ አልመሲህም አለ፡- «የእስራኤል ልጆች ሆይ! ጌታዬንና ጌታችሁን አላህን ተገዙ፡፡» እነሆ! በአላህ የሚያጋራ ሰው አላህ በርሱ ላይ ገነትን በእርግጥ እርም አደረገ፡፡ መኖሪያውም እሳት ናት፡፡ ለበዳዮችም ምንም ረዳቶች የሏቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,73,'5:73','እነዚያ «አላህ የሦስት ሦስተኛ ነው» ያሉ በእርግጥ ካዱ፡፡ ከአምላክም አንድ አምላክ እንጅ ሌላ የለም፡፡ ከሚሉትም ነገር ባይከለከሉ ከነሱ እነዚያን የካዱትን አሳማሚ ቅጣት በእርግጥ ይነካቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,74,'5:74','ወደ አላህ አይመለሱምና ምሕረትን አይለምኑትምን አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,75,'5:75','የመርየም ልጅ አልመሲሕ ከበፊቱ መልክተኞች በእርግጥ ያለፉ የኾነ መልክተኛ እንጅ ሌላ አይደለም፡፡ እናቱም በጣም እውነተኛ ናት፡፡ (ሁለቱም) ምግብን የሚበሉ ነበሩ፤ አንቀጾችን ለነርሱ (ለከሓዲዎች) እንዴት እንደምናብራራ ተመልከት ከዚያም (ከእውነት) እንዴት እንደሚመለሱ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(5,76,'5:76','«ከአላህ ሌላ ለእናንተ መጉዳትንና መጥቀምን የማይችልን ትገዛላችሁን» በላቸው፡፡ አላህም እርሱ ሰሚው ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,77,'5:77','«እናንተ የመጽሐፉ ባለቤቶች ሆይ! በሃይማኖታችሁ ከእውነት ሌላ የኾነን (ማለፍ) ወሰንን አትለፉ፡፡ በፊትም በእርግጥ የተሳሳቱትንና ከቀጥተኛው መንገድም (አሁን) የተሳሳቱን ሕዝቦች ከንቱ ዝንባሌዎች አትከተሉ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,78,'5:78','ከእስራኤል ልጆች እነዚያ የካዱት በዳውድና በመርየም ልጅ በዒሳ ምላስ ተረገሙ፡፡ ይህ ትእዛዝን በመጣሳቸውና ወሰንን የሚያልፉ በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,79,'5:79','ከሠሩት መጥፎ ነገር አይከላከሉም ነበር፡፡ ይሠሩት የነበሩት ነገር በእርግጥ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(5,80,'5:80','ከእነሱ ብዙዎቹን እነዚያን የካዱትን ሲወዳጁ ታያለህ፡፡ በእነሱ ላይ አላህ የተቆጣባቸው በመኾኑ ለእነርሱ ነፍሶቻቸው ያስቀደሙት ሥራ በጣም ከፋ፡፡ እነሱም በቅጣት ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,81,'5:81','በአላህና በነቢዩ ወደርሱም በተወረደው ቁርኣን የሚያምኑ በኾኑ ኖሮ ወዳጆች አድርገው ባልያዙዋቸው ነበር፡፡ ግን ከነርሱ ብዙዎቹ አመጠኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,82,'5:82','አይሁዶችንና እነዚያን ያጋሩትን ለእነዚያ ለአመኑት በጠላትነት ከሰዎች ሁሉ ይበልጥ የበረቱ ኾነው በእርግጥ ታገኛለህ፡፡ እነዚያንም «እኛ ክርስቲያኖች ነን» ያሉትን ለእነዚያ ለአመኑት በወዳጅነት በእርግጥ ይበልጥ የቀረቧቸው ኾነው ታገኛለህ፡፡ ይህ ከእነሱ ውስጥ ቀሳውስትና መነኮሳት በመኖራቸውና እነሱም የማይኮሩ በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,83,'5:83','ወደ መልክተኛውም የተወረደውን (ቁርኣን) በሰሙ ጊዜ እውነቱን ከማወቃቸው የተነሳ ዓይኖቻቸው እንባን ሲያፈሱ ታያለህ፡፡ «ጌታችን ሆይ! አመንን ከመስካሪዎቹም ጋር ጻፈን » ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,84,'5:84','«በአላህና ከውነትም በመጣልን ነገር የማናምን ጌታችንም ከመልካም ሰዎች ጋር ሊያስገባን የማንከጅል ለኛ ምን አለን» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(5,85,'5:85','ባሉትም ምክንያት አላህ በስሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ መነዳቸው፡፡ ይህም የበጎ ሠሪዎች ዋጋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,86,'5:86','እነዚያም የካዱና በአንቀጾቻችን ያስተባበሉ እነዚያ የእሳት ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,87,'5:87','እናንተ ያመናችሁ ሆይ! አላህ ለናንተ የፈቀደላችሁን ጣፋጮች እርም አታድርጉ፡፡ ወሰንንም አትለፉ፡፡ አላህ ወሰን አላፊዎችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,88,'5:88','አላህም ከሰጣችሁ ሲሳይ የተፈቀደ ጣፋጭ ሲኾን ብሉ፡፡ ያንንም እናንተ በርሱ አማኞች የኾናችሁበትን አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,89,'5:89','አላህ በመሐላዎቻችሁ በውድቁ አይዛችሁም፡፡ ግን መሐላዎችን (ባሰባችሁት) ይይዛችኋል፡፡ ማስተሰሪያውም ቤተሰቦቻችሁን ከምትመግቡት ከመካከለኛው (ምግብ) ዐስርን ምስኪኖች ማብላት ወይም እነሱን ማልበስ ወይም ጫንቃን ነጻ ማውጣት ነው፤ (ከተባሉት አንዱን) ያላገኘም ሰው ሦስት ቀኖችን መጾም ነው፤ ይህ በማላችሁ ጊዜ የመሐላዎቻችሁ ማካካሻ ነው፡፡ መሐላዎቻችሁንም ጠብቁ፡፡ እንደዚሁ አላህ ለናንተ አንቀጾችን ያብራራል፡፡ እናንተ ልታመሰግኑ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,90,'5:90','እናንተ ያመናችሁ ሆይ! የሚያሰክር መጠጥ ቁማርም ጣዖታትም አዝላምም ከሰይጣን ሥራ የኾኑ እርኩስ ብቻ ናቸው፡፡ (እርኩስን) ራቁትም ልትድኑ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(5,91,'5:91','ሰይጣን የሚፈልገው በሚያሰክር መጠጥና በቁማር በመካከላችሁ ጠብንና ጥላቻን ሊጥል አላህን ከማውሳትና ከስግደትም ሊያግዳችሁ ብቻ ነው፡፡ ታዲያ እናንተ (ከእነዚህ) ተከልካዮች ናችሁን (ተከልከሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(5,92,'5:92','አላህንም ተገዙ፡፡ መልክተኛውንም ታዘዙ፡፡ ተጠንቀቁም፡፡ ብትሸሹም፤ በመልክተኛችን ላይ ያለበት ግልጽ ማድረስ ብቻ መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,93,'5:93','በእነዚያ ባመኑትና በጎ ሥራዎችን በሠሩት ላይ (ክህደትን) በተጠነቀቁና ባመኑ መልካም ሥራዎችንም በሠሩ ከዚያም (የሚያሰክርንና ቁማርን) በተጠነቀቁና ባመኑ ከዚያም (ከተከለከለው ሁሉ) በተጠነቀቁና (ሥራን) ባሳመሩ ጊዜ (ከመከልከሉ በፊት) በተመገቡት ነገር ኃጢአት የለባቸውም፡፡ አላህም በጎ ሠሪዎችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,94,'5:94','እናንተ ያመናችሁ ሆይ! (በሐጅ ጊዜ) እጆቻችሁና ጦሮቻችሁ በሚያገኙት ታዳኝ አውሬ አላህ ይሞክራችኋል፡፡ በሩቅ ኾኖ የሚፈራውን ሰው አላህ ሊገልጽ (ይሞክራችኋል)፡፡ ከዚያም በኋላ ወሰንን ያለፈና ያደነ ሰው ለርሱ አሳማሚ ቅጣት አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,95,'5:95','እናንተ ያመናችሁ ሆይ! እናንት በሐጅ ሥራ ውስጥ ስትኾኑ አውሬን አትግደሉ፡፡ ከእናንተም እያወቀ የገደለው ሰው ቅጣቱ ከእናንተ ሁለት የትክክለኛነት ባለቤቶች የሚፈርዱበት የኾነ ወደ ካዕባ ደራሽ መሥዋዕት ሲኾን ከቤት እንስሳዎች የገደለውን ነገር ብጤ መስጠት ወይም ምስኪኖችን ማብላት ወይም የዚህን ልክ መጾም ማካካሻ ነው፡፡ (ይኽም) የሥራውን ከባድ ቅጣት እንዲቀምስ ነው፡፡ (ሳይከለከል በፊት) ካለፈው ነገር አላህ ይቅርታ አደረገ፡፡ (ወደ ጥፋቱም) የተመለሰም ሰው አላህ ከርሱ ይበቀላል፡፡ አላህም አሸናፊ የመበቀል ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,96,'5:96','የባሕር ታዳኝና ምግቡ ለእናንተም ለመንገደኞችም መጠቀሚያ ይኾን ዘንድ ለናንተ ተፈቀደ፡፡ በሐጅም ላይ እስካላችሁ ድረስ የየብስ አውሬ በናንተ ላይ እርም ተደረገ፡፡ ያንንም ወደርሱ የምትሰበሰቡበትን አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,97,'5:97','ከዕባን፣ የተከበረውን ቤት፣ ክልክል የኾነውንም ወር፣ መስዋዕቱንና ባለ ምልክቶቹንም መንጋዎች አላህ ለሰዎች መቋቋሚያ አደረገ፡፡ ይህ አላህ በሰማያት ያለውን ሁሉ በምድርም ያለውን ሁሉ የሚያውቅ መኾኑን አላህም ነገሩን ሁሉ ዐዋቂ መኾኑን እንድታውቁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,98,'5:98','አላህ ቅጣተ ብርቱ መኾኑን አላህም መሓሪ አዛኝ መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,99,'5:99','በመልክተኛው ላይ ማድረስ እንጅ ሌላ የለበትም፡፡ አላህም የምትገልጹትን ነገር የምትደብቁትንም ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,100,'5:100','«የመጥፎው (ገንዘብ) ብዛት ቢያስደንቃችሁም እንኳ መጥፎውና ጥሩው አይስተካከሉም፡፡ ባለልቦች ሆይ! አላህንም ፍሩ፤ እናንተ ልትድኑ ይከጀላልና፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,101,'5:101','እናንተ ያመናችሁ ሆይ! ለእናንተ ቢገለጹ ከሚያስከፏችሁ ነገሮች አትጠይቁ፡፡ ቁርኣንም በሚወርድበት ጊዜ ከእርሷ ብትጠይቁ ለናንተ ትገለጻለች፤ ከእርሷ አላህ ይቅርታ አደረገ፡፡ አላህም መሓሪ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,102,'5:102','ከናንተም በፊት ሕዝቦች በእርግጥ ጠየቋት፡፡ ከዚያም በእርሷ (ምክንያት) ከሓዲዎች ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,103,'5:103','ከበሒራ ከሳኢባም ከወሲላም ከሓምም አላህ ምንንም (ሕግ) አላደረገም፡፡ ግን እነዚያ የካዱት በአላህ ላይ ውሸትን ይቀጣጥፋሉ፡፡ አብዛኞቻቸውም አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,104,'5:104','ለእነርሱም አላህ ወደ አወረደውና ወደ መልክተኛው ኑ በተባሉ ጊዜ «አባቶቻችንን በርሱ ላይ ያገኘንበት ነገር ይበቃናል» ይላሉ፡፡ አባቶቻቸው ምንም የማያውቁና የማይመሩ ቢኾኑም (ይኸንን ማለት ይበቃቸዋልን)');
INSERT INTO "sadiq_sani" VALUES(5,105,'5:105','እናንተ ያመናችሁ ሆይ! ነፍሶቻችሁን (ከእሳት) ያዙ፤ (ጠብቁ)፡፡ በተመራችሁ ጊዜ የተሳሳተ ሰው አይጎዳችሁም፡፡ የሁላችሁም መመለሻ ወደ አላህ ብቻ ነው፡፡ ትሠሩትም የነበራችሁትን ሁሉ ይነግራችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,106,'5:106','እናንተ ያመናችሁ ሆይ! አንዳችሁን ሞት በመጣበት ጊዜ በኑዛዜ ወቅት የመካከላችሁ ምስክርነት ከእናንተ ሁለት የትክክለኛነት ባለቤቶች የኾኑ ሰዎች ወይም በምድር ብትጓዙና የሞት አደጋ ብትነካችሁ ከሌሎቻችሁ የኾኑ ሁለት ሌሎች ሰዎች (መመስከር) ነው፡፡ ብትጠራጠሩ ከሶላት (ከዐሱር) በኋላ ታቆሟቸውና (የሚመሰክርለት ሰው) የዝምድና ባለቤት ቢኾንም እንኳ «በርሱ ዋጋን አንገዛም የአላህንም ምስክርነት አንደብቅም፤ ያን ጊዜ (ደብቀን ብንገኝ) እኛ ከኃጢአተኞች ነን» ብለው በአላህ ስም ይምላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,107,'5:107','እነርሱም ኃጢአትን (በውሸት መስክረው) የተገቡ መኾናቸው ቢታወቅ ከእነዚያ (ለሟቹ) ቅርቦች በመኾን (ውርስ) ከተገባቸው የኾኑ ሁለት ሌሎች ሰዎች በስፍራቸው (በምስክሮች ስፍራ) ይቆሙና «ምስክርነታችን ከእነሱ ምስክርነት ይልቅ እውነት ነው ወሰንም አላለፍንም ያን ጊዜ እኛ ከበዳዮች ነን» ሲሉ በአላህ ይምላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,108,'5:108','ይህ ምስክርነትን በተገቢዋ ላይ ለማምጣታቸው ወይም ከመሐላዎቻቸው በኋላ የመሐላዎችን (ወደ ወራሾች) መመለስን ለመፍራት በጣም የቀረበ ነው፡፡ አላህንም ፍሩ፡፡ ስሙም፡፡ አላህም አመጸኞች ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(5,109,'5:109','አላህ መልክተኞቹን የሚሰበስብበትንና «ምን መልስ ተሰጣችሁ» የሚልበትን ቀን (አስታውስ)፡፡ «ለእኛ ምንም ዕውቀት የለንም አንተ ሩቆችን በጣም ዐዋቂ አንተ ብቻ ነህ» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,110,'5:110','አላህ በሚል ጊዜ (አስታውስ)፡- «የመርየም ልጅ ዒሳ ሆይ! በአንተና በእናትህ ላይ (የዋልኩላችሁን) ጸጋዬን አስታውስ፡፡ በሕፃንነትና በከፈኒሳነት (የበሰለ ሰው ኾነህ) ሰዎችን የምትናገር ስትኾን በቅዱስ መንፈስ (በገብሬል) ባበረታሁህ ጊዜ፤ ጽሕፈትንና ጥበብንም ተውራትንና ኢንጂልንም ባስተማርኩህ ጊዜ፣ ከጭቃም የወፍ ቅርጽ ብጤ በፈቃዴ በምትሠራና በውስጧ በምትነፋ በፈቃዴም ወፍ በምትኾን ጊዜ፣ ዕውርም ኾኖ የተወለደንና ለምጻምንም በፈቃዴ በምታሽር ጊዜ፣ ሙታንንም በፈቃዴ (ከመቃብራቸው) በምታወጣ ጊዜ፣ የእስራኤልንም ልጆች በተአምራት በመጣህባቸውና ከእነሱ እነዚያ የካዱት ይህ ግልጽ ድግምት እንጅ ሌላ አይደለም ባሉ ጊዜ፣ (ሊገድሉህ ሲያስቡህ) ከአንተ ላይ በከለከልኩልህም ጊዜ (ያደረግሁልህን ውለታ አስታውስ)፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,111,'5:111','ወደ ሐዋርያትም «በኔና በመልክተኛዬ እመኑ» በማለት ባዘዝኩ ጊዜ (አስታውስ)፡፡ «አመንን፤ እኛ ሙስሊሞች መኾናችንን መስክር» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,112,'5:112','ሐዋርያት፡- «የመርየም ልጅ ዒሳ ሆይ! ጌታህ በእኛ ላይ ከሰማይ ማእድን ሊያወርድልን ይችላልን» ባሉ ጊዜ (አስታውስ)፡፡ «ምእመናን እንደ ኾናችሁ አላህን ፍሩ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(5,113,'5:113','«ከእርሷ ልንበላ ልቦቻችንም ሊረኩ እውነት ያልከንም መኾንህን ልናውቅና በእርሷም ላይ ከመስካሪዎች ልንኾን እንፈልጋለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,114,'5:114','የመርየም ልጅ ዒሳ አለ፡- «ጌታችን አላህ ሆይ! ለእኛ ለመጀመሪያዎቻችንና ለመጨረሻዎቻችን ባዕል (መደሰቻ) የምትኾንን ከአንተም ተአምር የኾነችን ማእድ ከሰማይ በእኛ ላይ አውርድ፡፡ ስጠንም፡፡ አንተ ከሰጪዎች ሁሉ በላጭ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,115,'5:115','አላህ፡- «እኔ (ማእድዋን) በናንተ ላይ አውራጅዋ ነኝ፡፡ በኋላም ከእናንተ የሚክድ ሰው እኔ ከዓለማት አንድንም የማልቀጣውን ቅጣት እቀጣዋለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(5,116,'5:116','አላህም፡- «የመርየም ልጅ ዒሳ ሆይ! አንተ ለሰዎቹ፡- እኔንና እናቴን ከአላህ ሌላ ሁለት አምላኮች አድርጋችሁ ያዙ ብላሃልን» በሚለው ጊዜ (አስታውስ)፡፡ «ጥራት ይገባህ፤ ለእኔ ተገቢዬ ያልኾነን ነገር ማለት ለኔ አይገባኝም፡፡ ብዬው እንደ ኾነም በእርግጥ ዐውቀኸዋል፡፡ በነፍሴ ውስጥ ያለውን ሁሉ ታውቃለህ፡፡ ግን አንተ ዘንድ ያለውን አላውቅም፡፡ አንተ ሩቆችን ሁሉ በጣም ዐዋቂ አንተ ብቻ ነህና» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(5,117,'5:117','«በእርሱ ያዘዝከኝን ቃል ጌታዬንና ጌታችሁን አላህን ተገዙ ማለትን እንጂ ለነርሱ ሌላ አላልኩም፡፡ በውስጣቸውም እስካለሁ ድረስ በነርሱ ላይ ተጣባበቂ ነበርኩ፡፡ በተሞላኸኝም ጊዜ (ባነሳኸኝ ጊዜ) አንተ በነሱ ላይ ተጠባባቂ ነበርክ፡፡ አንተም በነገሩ ሁሉ ላይ ዐዋቂ ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,118,'5:118','«ብትቀጣቸው እነርሱ ባሮችህ ናቸው፡፡ ለነርሱ ብትምርም አንተ አሸናፊው ጥበበኛው ነህ» (ይላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(5,119,'5:119','አላህ ይላል፡- «ይህ እውነተኞችን እውነታቸው የሚጠቅምበት ቀን ነው፡፡ ለእነርሱ በሥሮቻቸው ወንዞች የሚፈሱባቸው በውስጣቸው ሁልጊዜ ዘውታሪዎች ሲኾኑ የሚኖሩባቸው ገነቶች አሉዋቸው፡፡ አላህ ከእነሱ ወደደ፤ እነርሱም ከእርሱ ወደዱ፤ ይህ ታላቅ ዕድል ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(5,120,'5:120','የሰማያትና የምድር በውስጣቸውም ያለው ሁሉ ንግሥና የአላህ ብቻ ነው፡፡ እሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,1,'6:1','ምስጋና ለዚያ ሰማያትንና ምድርን ለፈጠረው ጨለማዎችንና ብርሃንንም ላደረገው ለአላህ የተገባ ነው፡፡ ከዚያም እነዚያ የካዱት (ጣዖታትን በጌታቸው) ያስተካክላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,2,'6:2','እርሱ ያ ከጭቃ የፈጠራችሁ ከዚያም (የሞት) ጊዜን የወሰነ ነው፡፡ እርሱም ዘንድ (ለትንሣኤ) የተወሰነ ጊዜ አልለ፡፡ ከዚያም እናንተ (በመቀስቀሳችሁ) ትጠራጠራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,3,'6:3','እርሱም ያ በሰማያትና በምድር (ሊግገዙት የሚገባው) አላህ ነው፡፡ ምስጢራችሁን ግልጻችሁንም ያውቃል፡፡ የምትሠሩትንም ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,4,'6:4','ከጌታቸውም አንቀጾች አንዲትም አንቀጽ አትመጣቸውም ከርሷ የሚሸሹ ቢኾኑ እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,5,'6:5','በእውነቱም (ቁርኣን) በመጣላቸው ጊዜ በእርግጥ አስተባበሉ፡፡ በእርሱም ይሳለቁበት የነበሩት ነገር ዜናዎች (ቅጣት) በእርግጥ ይመጣባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,6,'6:6','ከበፊታቸው ከክፍለ ዘመናት ሕዝቦች ብዙዎችን ማጥፋታችንን አላዩምን ለእናንተ ያላስመቸነውን (ለእነርሱ) በምድር ውስጥ አስመቸናቸው፡፡ በእነርሱም ላይ ዝናምን የተከታተለ ሲኾን ላክን፡፡ ወንዞችንም በሥራቸው እንዲፈሱ አደረግን፡፡ በኃጢአቶቻቸውም አጠፋናቸው፡፡ ከኋላቸውም ሌሎችን የክፍለ ዘመን ሰዎች አስገኘን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,7,'6:7','ባንተም ላይ በወረቀት የተጻፈን መጽሐፍ ባወረድንና በእጆቻቸው በነኩት ኖሮ እነዚያ የካዱት ሰዎች «ይህ ግልጽ ድግምት እንጅ ሌላ አይደለም» ባሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(6,8,'6:8','«በእርሱም (በሙሐመድ) ላይ መልአክ አይወረድለትም ኖሮአልን» አሉ፡፡ መልአክን ባወረድንም ኖሮ (ባያምኑ በጥፋታቸው) ነገሩ በተፈረደ ነበር፡፡ ከዚያም አይቆዩም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(6,9,'6:9','(መልክተኛውን) መልአክም ባደረግነው ኖሮ ወንድ (በሰው ምስል) ባደረግነው ነበር፡፡ በእነርሱም ላይ የሚያመሳስሉትን ነገር ባመሳሰልንባቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(6,10,'6:10','ካንተ በፊትም በመልክተኞች በእርግጥ ተላግጧል፡፡ በእነዚያም ከእነሱ ባላገጡት ላይ በእርሱ ያላግጡ የነበሩት ነገር (ቅጣት) ወረደባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,11,'6:11','«በምድር ላይ ኺዱ የአስተባባዮችም መጨረሻ እንዴት እንደነበረ ተመልከቱ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,12,'6:12','«በሰማያትና በምድር ያለው ሁሉ የማን ነው» በላቸው፡፡ (ሌላ መልስ የለምና) «የአላህ ነው» በል፡፡ «በነፍሱ ላይ እዝነትን ጻፈ፡፡ በትንሣኤ ቀን በእርሱ ጥርጥር የሌለበት ሲኾን በእርግጥ ይሰበስባችኋል፡፡ እነዚያ ነፍሶቻቸውን ያከሰሩ ናቸው፤ እነርሱም አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,13,'6:13','በሌሊትና በቀንም ጸጥ ያለው ሁሉ የርሱ ብቻ ነው፡፡ እርሱም ሰሚው ዐዋቂ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,14,'6:14','«ሰማያትንና ምድርን ፈጣሪ ከኾነው አላህ እርሱ የሚመግብ የማይመገብም ሲኾን ሌላን አምላክ እይዛለሁን» በላቸው፡፡ «እኔ መጀመሪያ ትእዛዝን ከተቀበለ ሰው ልኾን ታዘዝኩ፡፡ ከአጋሪዎችም ፈጽሞ አትኹን (ተብያለሁ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,15,'6:15','«እኔ በጌታዬ ባምጽ የታላቁን ቀን ቅጣት እፈራለሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,16,'6:16','በዚያ ቀን ከእርሱ ላይ (ቅጣት) የሚመለስለት ሰው (አላህ) በእርግጥ አዘነለት፡፡ ይህም ግልጽ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,17,'6:17','አላህም በጉዳት ቢነካህ ለእርሱ (ለጉዳቱ) ከእርሱ በቀር ሌላ አስወጋጅ የለም፡፡ በበጎም ነገር ቢነካህ እርሱ በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,18,'6:18','እርሱም ከባሮቹ በላይ ሲኾን አሸናፊ ነው፡፡ እርሱም ጥበበኛው ውስጥ ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,19,'6:19','«በምስክርነት ከነገሩ ሁሉ ይበልጥ ታላቅ ማነው» በላቸው፡፡ (ሌላ መልስ የለምና) «አላህ ነው፡፡ በእኔና በእናንተ መካከል መስካሪ ነው፡፡ ይህም ቁርኣን እናንተንና የደረሰውን ሰው ሁሉ በርሱ ላስፈራራበት ወደኔ ተወረደ፡፡ ከአላህ ጋር ሌሎች አማልክት መኖራቸውን እናንተ ትመሰክራላችሁን» በላቸው፡፡ «እኔ አልመሰክርም» በላቸው፡፡ «እርሱ አንድ አምላክ ብቻ ነው፡፡ እኔም ከምታጋሩት ነገር ንጹሕ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,20,'6:20','እነዚያ መጽሐፍን የሰጠናቸው ልጆቻቸውን እንደሚያውቁ (ሙሐመድን) ያውቁታል፡፡ እነዚያ ከእነርሱ ውስጥ ነፍሶቻቸውን ያከሰሩት እነርሱ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,21,'6:21','በአላህም ላይ ውሸትን ከቀጣጠፈ ወይም በአንቀጾቹ ካስተባበለ ሰው ይበልጥ በደለኛ ማነው እነሆ በደለኞች አይድኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,22,'6:22','ሁላቸውንም የምንሰበስብበትንና ከዚያም ለእነዚያ (ጣዖታትን) ለአጋሩት እነዚያ (ለአላህ) ተጋሪዎች ናቸው ብላችሁ ታስቧቸው የነበራችሁት ተጋሪዎቻችሁ የት ናቸው የምንልበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,23,'6:23','ከዚያም መልሳቸው «በጌታችን በአላህ ይኹንብን አጋሪዎች አልነበርንም» ማለት እንጅ ሌላ አትኾንም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,24,'6:24','በነፍሳቸው ላይ እንዴት እንደ ዋሹና ይቀጣጥፉት የነበሩት ነገር ከእነሱ እንዴት እንደ ተሰወረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(6,25,'6:25','ከእነርሱም ወደ አንተ የሚያዳምጥ ሰው አልለ፡፡ በልቦቻቸውም ላይ እንዳያውቁት ሺፋኖችን በጆሮዎቻቸውም ላይ ድንቁርናን አደረግን፡፡ ተዓምርንም ሁሉ ቢያዩ በርሷ አያምኑም፡፡ በመጡህም ጊዜ የሚከራከሩህ ሲኾኑ እነዚያ የካዱት «ይህ (ቁርኣን) የቀድሞዎቹ ሰዎች ጽሑፍ ተረቶች እንጅ ሌላ አይደለም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,26,'6:26','እነርሱም (ሰዎችን) ከእርሱ ይከለክላሉ፡፡ ከእርሱም ይርቃሉ፡፡ ነፍሶቻቸውንም እንጂ ሌላን አያጠፉም፤ ግን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,27,'6:27','በእሳትም ላይ በተቆሙ ጊዜ ምነው (ወደ ምድረ ዓለም) በተመለስን በጌታችንም አንቀጾች ባላስተባበልን ከምእምናንም በኾንን ዋ ምኞታችን! ባሉ ጊዜ ብታይ ኖሮ (የሚያሰደነግጥን ነገር ባየህ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,28,'6:28','ይልቁንም ከዚህ በፊት ይደብቁት የነበሩት ሁሉ ለእነርሱ ተገለጸ፡፡ (ወደምድረ ዓለም) በተመለሱም ኖሮ ከእርሱ ወደ ተከለከሉት ነገር በተመለሱ ነበር፡፡ እነሱም በእርግጥ ውሸታሞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,29,'6:29','እርሷም (ሕይወት) «የቅርቢቱ ሕይወታችን እንጅ ሌላ አይደለችም እኛም ተቀስቀቃሾች አይደለንም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,30,'6:30','በጌታቸውም ላይ (ለምርመራ) በተቆሙ ጊዜ ብታይ ኖሮ (አስደንጋጭን ነገር ባየህ ነበር)፡፡ «ይህ እውነት አይደለምን» ይላቸዋል፡፡ «በጌታችን ይኹንብን እውነት ነው» ይላሉ፡፡ «ትክዱት በነበራችሁት ምክንያት ቅጣቱን ቅመሱ» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,31,'6:31','እነዚያ በአላህ መገናኘት ያስተባበሉ በእርግጥ ከሰሩ፡፡ ሰዓቲቱም በድንገት በመጣቻቸው ጊዜ እነሱ ኃጢኣቶቻቸውን በጀርባዎቻቸው ላይ የሚሸከሙ ሲኾኑ «በርሷ (በምድረ ዓለም) ባጓደልነው ነገር ላይ ዋ ቁጪታችን» ይላሉ፡፡ ንቁ! የሚሸከሙት ነገር ከፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,32,'6:32','የቅርቢቱም ሕይወት ጨዋታና ላግጣ እንጅ ሌላ አይደለችም፡፡ የመጨረሻይቱም አገር (ገነት) ለእነዚያ ለሚጠነቀቁት በጣም በላጭ ናት፡፡ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(6,33,'6:33','እነሆ ያ የሚሉህ ነገር እንደሚያሳዝንህ በእርግጥ እናውቃለን፡፡ እነርሱም (በልቦቻቸው) አያስተባብሉህም፡፡ ግን በዳዮቹ በአላህ አንቀጾች ይክዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,34,'6:34','ከበፊትህም መልክተኞች በእርግጥ ተስተባበሉ፡፡ በተስተባበሉበትም ነገርና በተሰቃዩት ላይ እርዳታችን እስከመጣላቸው ድረስ ታገሱ፡፡ የአላህንም ንግግሮች ለዋጭ የለም፡፡ ከመልክተኞቹም ወሬ (የምትረጋጋበት) በእርግጥ መጣልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,35,'6:35','(ኢስላምን) መተዋቸውም ባንተ ላይ ከባድ ቢኾንብህ በምድር ውስጥ ቀዳዳን ወይም በሰማይ ውስጥ መሰላልን ልትፈልግና በተአምር ልትመጣቸው ብትችል (አድርግ)፡፡ አላህም በሻ ኖሮ በቅኑ መንገድ ላይ በሰበሳባቸው ነበር፡፡ ከተሳሳቱትም ሰዎች በፍጹም አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,36,'6:36','ጥሪህን የሚቀበሉት እነዚያ የሚሰሙት ብቻ ናቸው፡፡ ሙታንንም አላህ ይቀሰቅሳቸዋል፡፡ ከዚያም ወደርሱ ብቻ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,37,'6:37','«ከጌታውም በእርሱ ላይ ለምን ተዓምር አልተወረደም» አሉ፡፡ «አላህ ተአምርን በማውረድ ላይ ቻይ ነው» በላቸው፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,38,'6:38','ከተንቀሳቃሽም በምድር (የሚኼድ)፤ በሁለት ክንፎቹ የሚበርም በራሪ መሰሎቻችሁ ሕዝቦች እንጅ ሌላ አይደሉም፡፡ በመጽሐፉ ውስጥ ምንንም ነገር አልተውንም ከዚያም ወደ ጌታቸው ይሰበሰባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,39,'6:39','እነዚያም በአንቀጾቻችን ያስዋሹ ደንቆሮች ድዳዎችም በጨለማዎች ውስጥ ያሉ ናቸው፡፡ አላህ ማጥመሙን የሚሻውን ሰው ያጠመዋል፡፡ መምራቱን የሚሻውንም ሰው በቀጥተኛ መንገድ ላይ ያደርገዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,40,'6:40','የአላህ ቅጣት ቢመጣባችሁ ወይም ሰዓቲቱ (ትንሣኤ) ብትመጣባችሁ ከአላህ ሌላን ትጠራላችሁን እውነተኞች እንደ ኾናችሁ ንገሩኝ በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,41,'6:41','አይደለም እርሱን ብቻ ትጠራላችሁ፡፡ ቢሻም ወደርሱ የምትጠሩበትን ነገር ያስወግዳል፡፡ የምታጋሩትንም ነገር ትተዋላችሁ (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,42,'6:42','ከበፊትህም ወደ ነበሩት ሕዝቦች (መልክተኞችን) በእርግጥ ላክን፡፡ እንዲዋደቁም በድህነትና በበሽታ ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,43,'6:43','ታዲያ ቅጣታችን በመጣባቸው ጊዜ አይዋደቁም ኖሯልን ግን ልቦቻቸው ደረቁ፡፡ ይሠሩት የነበሩትንም ነገር ሰይጣን ለነሱ አሳመረላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,44,'6:44','በርሱም ይገሰጹበት የነበረውን ነገር በተው ጊዜ በእነሱ ላይ የነገርን ሁሉ ደጃፎች ከፈትን (አስመቸናቸው)፡፡ በተሰጡትም ነገር በተደሰቱ ጊዜ በድንገት ያዝናቸው፡፡ ወዲያውኑም እነርሱ ተስፋ ቆራጮች ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,45,'6:45','የእነዚያም የበደሉት ሕዝቦች መጨረሻ ተቆረጠ፡፡ ምስጋናም ለዓለማት ጌታ ለአላህ ይገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,46,'6:46','ንገሩኝ አላህ መስሚያችሁንና ማያዎቻችሁን ቢወስድ በልቦቻችሁም ላይ ቢያትም ከአላህ ሌላ እሱን የሚያመጣላችሁ አምላክ ማነው በላቸው፡፡ አንቀጾችን እንዴት እንደምናብራራ ከዚያም እነሱ እንዴት እንደሚመለሱ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(6,47,'6:47','«ንገሩኝ የአላህ ቅጣት በድንገት ወይም በግልጽ ቢመጣባችሁ አመጠኞች ሕዝቦች እንጅ ሌላ ይጠፋልን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,48,'6:48','መልክተኞችንም አብሳሪና አስፈራሪ አድርገን እንጅ አንልክም፡፡ ያመኑና መልካምን የሠሩ ሰዎችም በእነርሱ ላይ ፍርሃት የለባቸውም፤ እነሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,49,'6:49','እነዚያም ባንቀጾቻችን ያስዋሹ ያምጹ በነበሩት ምክንያት ቅጣቱ ያገኛቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,50,'6:50','«ለእናንተ የአላህ ግምጃ ቤቶች እኔ ዘንድ ናቸው አልልም፡፡ ሩቅንም አላውቅም፡፡ ለእናንተም እኔ መልአክ ነኝ አልልም፡፡ ወደእኔ የሚወርድልኝን እንጅ ሌላን አልከተልም» በላቸው፡፡ «ዕውርና የሚያይ ይስተካከላሉን አታስተነትኑምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,51,'6:51','እነዚያንም ከእርሱ ሌላ ረዳትና አማላጅ የሌላቸው ሲኾኑ ወደ ጌታቸው መስሰብሰብን የሚፈሩትን ይጠነቀቁ ዘንድ በርሱ (በቁርኣን) አስፈራራ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,52,'6:52','እነዚያን ጌታቸውን ፊቱን (ውዴታውን) የሚሹ ኾነው በጧትና በማታ የሚጠሩትን አታባር፡፡ ታባርራቸውና ከበደለኞችም ትኾን ዘንድ እነሱን መቆጣጠር ባንተ ላይ ምንም የለብህም፡፡ አንተንም መቆጣጠር በነሱ ላይ ምንም የለባቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,53,'6:53','እንደዚሁም «ከመካከላችን አላህ የለገሰላቸው እነዚህ ናቸውን» ይሉ ዘንድ ከፊላቸውን በከፊሉ ሞከርን፡፡ አላህ አመስጋኞቹን ዐዋቂ አይደለምን');
INSERT INTO "sadiq_sani" VALUES(6,54,'6:54','እነዚያም በተአምራታችን የሚያምኑት (ወደ አንተ) በመጡ ጊዜ «ሰላም በእናንተ ላይ ይኹን፡፡ ጌታችሁ በነፍሱ ላይ እዝነትን ጻፈ፡፡ እነሆ ከእናንተ ውስጥ በስሕተት ክፉን ሥራ የሠራ ሰው ከዚያም ከእርሱ በኋላ የተጸጸተና ሥራውን ያሳመረ እርሱ (አላህ) መሓሪ አዛኝ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,55,'6:55','እንደዚሁም (እውነቱ እንዲገለጽና) የወንጀለኞችም መንገድ ትብራራ ዘንድ አንቀጾችን እንገልጻለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,56,'6:56','«እኔ እነዚያን ከአላህ ሌላ የምትጠሩዋቸውን ከመገዛት ተከልክያለሁ» በላቸው፡፡ «ዝንባሌያችሁን አልከተልም፡፡ ያን ጊዜ በእርግጥ ተሳሳትኩ፡፡ እኔም ከተመሪዎቹ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,57,'6:57','«እኔ ከጌታዬ በኾነች ግልጽ ማስረጃ ላይ ነኝ፡፡ በእርሱም አስተባበላችሁ፡፡ በርሱ የምትቻኮሉበት ቅጣት እኔ ዘንድ አይደለም፡፡ ፍርዱ ለአላህ ብቻ ነው፡፡ እውነትን ይፈርዳል፡፡ እርሱም ከፈራጆች ሁሉ በላጭ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,58,'6:58','«በእርሱ የምትቻኮሉበት ቅጣት እኔ ዘንድ በኾነ ኖሮ በእኔና በእናንተ መካከል ነገሩ (ቅጣታችሁ) በተፈጸመ ነበር፡፡ አላህም በደለኞችን ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,59,'6:59','የሩቅ ነገርም መክፈቻዎች እርሱ ዘንድ ናቸው፡፡ ከእርሱ በቀር ማንም አያውቃቸውም፡፡ በየብስና በባሕር ያለውንም ሁሉ ያውቃል፡፡ ከቅጠልም አንዲትም አትረግፍም የሚያውቃት ቢኾን እንጅ፡፡ ከቅንጣትም በመሬት ጨለማዎች ውስጥ የለም ከእርጥብም ከደረቅም አንድም የለም ግልጽ በኾነው መጽሐፍ ውሰጥ (የተመዘገበ) ቢኾን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,60,'6:60','እርሱም ያ በሌሊት የሚያስተኛችሁ በቀንም የሠራችሁትን ሁሉ የሚያውቅ ከዚያም የተወሰነው ጊዜ ይፈፀም ዘንድ በእርሱ (በቀን) ውስጥ የሚቀሰቅሳችሁ ነው፡፡ ከዚያም መመለሻችሁ ወደ እርሱ ነው፡፡ ከዚያም ትሠሩት የነበራችሁትን ሁሉ ይነግራችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,61,'6:61','እርሱም ከባሮቹ በላይ ሲኾን ሁሉን አሸነፊ ነው፡፡ በናንተም ላይ ጠባቂዎችን (መላእክት) ይልካል፡፡ አንዳችሁንም ሞት በመጣበት ጊዜ (የሞት) መልእክተኞቻችን እነርሱ (ትእዛዛትን) የማያጓድሉ ሲኾኑ ይገድሉታል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,62,'6:62','ከዚያም እውነተኛ ወደ ኾነው ጌታቸው ወደ አላህ ይመለሳሉ፡፡ ንቁ! ፍርዱ ለእርሱ ብቻ ነው፡፡ እርሱም ከተቆጣጣሪዎች ሁሉ ይበልጥ ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,63,'6:63','ከየብስና ከባሕር ጨለማዎች በግልጽና በምስጢር የምትጠሩት ስትኾኑ «ከነዚህ ቢያድነን በእርግጥ ከአመስጋኞች እንኾናለን (ስትሉ) የሚያድናችሁ ማነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,64,'6:64','አላህ ከሷና ከጭንቅም ሁሉ ያድናችኋል፡፡ ከዚያም እናንተ ታጋራላችሁ በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,65,'6:65','«እርሱ (አላህ) ከበላያችሁ ወይም ከእግሮቻችሁ በታች ቅጣትን በእናንተ ላይ ሊልክ ወይም የተለያዩ ሕዝቦች ስትኾኑ ሊያደባልቃችሁና ከፊላችሁን የከፊሉን ጭካኔ ሊያቀምስ ቻይ ነው» በላቸው፡፡ ያውቁ ዘንድ አንቀጾችን እንዴት እንደምናብራራ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(6,66,'6:66','በእርሱም (በቁርኣን) እሱ እውነት ሲኾን ሕዝቦችህ አስተባበሉ፡፡ በላቸው፡- «በናንተ ላይ ጠባቂ አይደለሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,67,'6:67','ለትንቢት ሁሉ (የሚደርስበት) መርጊያ አለው፡፡ ወደፊትም ታውቁታላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,68,'6:68','እነዚያንም በአንቀጾቻችን የሚገቡትን ባየህ ጊዜ ከእርሱ ሌላ በኾነ ወሬ እስከሚገቡ ድረስ ተዋቸው፡፡ ሰይጣንም (መከልከልህን) ቢያስረሳህ ከትውስታ በኋላ ከበደለኞች ሕዝቦች ጋር አትቀመጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,69,'6:69','በእነዚያም በሚጠነቀቁት ላይ ከሒሳባቸው ምንም የለባቸውም፡፡ ግን ይጠነቀቁ ዘንድ መገሰጽ (አለባቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,70,'6:70','እነዚያንም ሃይማኖታቸውን ጨዋታና ላግጣ አድርገው የያዙትን ቅርቢቱም ሕይወት ያታለለቻቸውን ተዋቸው፡፡ በእርሱም (በቁርኣን) ነፍስ በሠራችው ሥራ እንዳትጠፋ አስታውስ፡፡ ለእርሷ ከአላህ ሌላ ረዳትና አማላጅ የላትም፡፡ በመበዢያም ሁሉ ብትበዥ ከርሷ አይወሰድም፡፡ እነዚህ እነዚያ በሠሩት ሥራ የተጠፉት ናቸው፡፡ ለነሱ ይክዱ በነበሩት ምክንያት ከፈላ ውሃ መጠጥና አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,71,'6:71','«ከአላህ ሌላ የማይጠቅመንን እና የማይጎዳንን እንገዛለን አላህም ከመራን ጊዜ በኋላ የኋሊት እንመለሳለን እንደዚያ በምድር ላይ የዋለለ ሲኾን ሰይጣናት እንዳሳሳቱት ለእርሱ ወደኛ ና እያሉ ወደ ቅን መንገድ የሚጠሩ ወዳጆች እንዳሉት (እንደማይከተላቸውም) ሰው እንኾናለን» በላቸው፡፡ «የአላህ መንገድ እርሱ ቀጥተኛው መንገድ ነው፡፡ ለዓለማት ጌታ ልንገዛም ታዘዝን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,72,'6:72','ሶላትንም በደንቡ ስገዱ ፍሩትም፤ (በማለት ታዘዝን)፡፡ እርሱም ያ ወደርሱ ብቻ የምትሰበሰቡበት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,73,'6:73','እርሱም ያ ሰማያትንና ምድርን በእውነት የፈጠረ ነው፡፡ «ኹን» የሚልበትንና ወዲያውም የሚኾንበትን ቀን (አስታውስ)፡፡ ቃሉ እውነት ነው፡፡ በቀንዱም በሚነፋ ቀን ንግሥናው የርሱ ብቻ ነው፡፡ ሩቁንና ቅርቡን ዐዋቂ ነው፡፡ እርሱም ጥበበኛው ውስጥ ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,74,'6:74','ኢብራሒምም ለአባቱ ለአዘር «ጣዖታትን አማልክት አድርገህ ትይዛለህን እኔ፤ አንተንም ሕዝቦችህንም በግልጽ መሳሳት ውስጥ ኾናችሁ አያችኋለሁ» ባለ ጊዜ (አስታውስ)');
INSERT INTO "sadiq_sani" VALUES(6,75,'6:75','እንደዚሁም ኢብራሂምን (እንዲያውቅና) ከአረጋጋጮቹም ይኾን ዘንድ የሰማያትንና የምድርን መለኮት አሳየነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,76,'6:76','ሌሊቱም በእርሱ ላይ ባጨለመ ጊዜ ኮከብን አየ፡፡ «ይህ ጌታዬ ነው» አለ፡፡ በጠለቀም ጊዜ፡- «ጠላቂዎችን አልወድም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,77,'6:77','ጨረቃንም ወጪ ኾኖ ባየ ጊዜ፡- «ይህ ጌታዬ ነው» አለ፡፡ በገባም ጊዜ፡- «ጌታዬ (ቅኑን መንገድ) ባይመራኝ በእርግጥ ከተሳሳቱት ሕዝቦች እኾናለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,78,'6:78','ፀሐይንም ጮራዋን ዘርግታ ባየ ጊዜ፡- «ይህ ጌታዬ ነው፡፡ ይህ በጣም ትልቅ ነው» አለ፡፡ በገባችም ጊዜ፡- «ወገኖቼ ሆይ! እኔ ከምታጋሩት ሁሉ ንጹሕ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,79,'6:79','«እኔ ለዚያ ሰማያትንና ምድርን ለፈጠረው (አምላክ) ቀጥተኛ ስኾን ፊቴን አዞርኩ፡፡ እኔም ከአጋሪዎቹ አይደለሁም» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,80,'6:80','ወገኖቹም ተከራከሩት፡፡ «በአላህ (አንድነት) በእርግጥ የመራኝ ሲኾን ትከራከሩኛላችሁን በእርሱም የምታጋሩትን ነገር አልፈራም፡፡ ግን ጌታዬ ነገርን ቢሻ (ያገኘኛል)፡፡ ጌታዬም ነገሩን ሁሉ ዕውቀቱ ሰፋ፡፡ አትገነዘቡምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,81,'6:81','«በእናንተ ላይ በእርሱ ማስረጃን ያላወረደበትን እናንተ ማጋራታችሁን የማትፈሩ ስትኾኑ የምታጋሩትን (ጣዖታት) እንዴት እፈራለሁ! የምታውቁም ብትኾኑ ከሁለቱ ክፍሎች በጸጥታ ይበልጥ ተገቢው ማንኛው ነው» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,82,'6:82','እነዚያ ያመኑና እምነታቸውን በበደል ያልቀላቀሉ እነዚያ ለእነርሱ ጸጥታ አላቸው፡፡ እነሱም የተመሩ ናቸው፤');
INSERT INTO "sadiq_sani" VALUES(6,83,'6:83','ይህችም ማስረጃችን ናት፡፡ ለኢብራሂም በሕዝቦቹ ላይ (አስረጅ እንድትኾን) ሰጠናት፡፡ የምንሻውን ሰው በደረጃዎች ከፍ እናደርጋለን፡፡ ጌታህ ጥበበኛ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,84,'6:84','ለርሱም ኢስሐቅን (የልጅ ልጁን) ያዕቁብንም ሰጠነው፡፡ ሁሉንም መራን፡፡ ኑሕንም በፊት መራን፡፡ ከዘሮቹም ዳውድን፣ ሱለይማንንም፣ አዩብንም፣ ዩሱፍንም፣ ሙሳንም፣ ሃሩንንም (መራን)፡፡ እንደዚሁም በጎ ሰሪዎችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,85,'6:85','ዘከሪያንም፣ የሕያንም፣ ዒሳንም፣ ኢልያስንም (መራን)፡፡ ሁሉም ከመልካሞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,86,'6:86','ኢስማዒልንም፣ አልየስዕንም፣ ዩኑስንም፣ ሉጥንም (መራን)፡፡ ሁሉንም በዓለማት ላይ አበለጥናቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,87,'6:87','ከአባቶቻቸውም፣ ከዘሮቻቸውም፣ ከወንድሞቻቸውም (መራን)፡፡ መረጥናቸውም፤ ወደ ቀጥታውም መንገድ መራናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,88,'6:88','ይህ የአላህ መምሪያ ነው፡፡ በእርሱ ከባሮቹ የሚሻውን ሰው ይመራል፡፡ ባጋሩም ኖሮ ይሠሩት የነበሩት ከነሱ በታበሰ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(6,89,'6:89','እነዚህ እነዚያ መጻሕፍትንና ጥበብን፣ ነቢይነትንም የሰጠናቸው ናቸው፡፡ እነዚህ (የመካ ከሓዲዎች) በእርሷ ቢክዱም በእርሷ የማይክዱን ሕዝቦች ለእርሷ በእርግጥ አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,90,'6:90','እነዚህ (ነቢያት)፤ እነዚያ አላህ የመራቸው ናቸው፡፡ በመንገዳቸውም ተከተል፡፡ «በእርሱ (በቁርአን) ላይ ምንም ዋጋ አልጠይቃችሁም፡፡ እርሱ ለዓለማት ግሣጼ እንጅ ሌላ አይደለም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,91,'6:91','«አላህ በሰው ላይ ምንም አላወረደም» ባሉም ጊዜ አላህን ተገቢ ክብሩን አላከበሩትም፡፡ (እንዲህ) በላቸው፡- «ያንን ብርሃንና ለሰዎች መሪ ኾኖ ሙሳ ያመጣውን መጽሐፍ ክፍልፍሎች የምታደርጉት ስትኾኑ ማን አወረደው (የወደዳችኋትን) ትገልጿታላችሁ ብዙውንም ትደብቃላችሁ፡፡ እናንተም አባቶቻችሁም ያላወቃችሁትን ተስተማራችሁ፤»');
INSERT INTO "sadiq_sani" VALUES(6,92,'6:92','«አላህ (አወረደው)» በላቸው፡፡ ከዚያም በውሸታቸው ውስጥ የሚጫወቱ ሲኾኑ ተዋቸው፡፡ ይህም ያወረድነው የኾነ ብሩክ ያንንም በፊቱ የነበረውን (መጽሐፍ) አረጋጋጭ መጽሐፍ ነው፡፡ የከተሞችን እናት (መካን) እና በዙሪያዋ ያሉትንም ሰዎች ልታስጠነቅቅበት (አወረድነው)፡፡ እነዚያም በመጨረሻይቱ ዓለም የሚያምኑት እነርሱ በሶላታቸው ላይ የሚጠባበቁ ሲኾኑ በርሱ ያምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,93,'6:93','በአላህም ላይ እብለትን ከቀጣጠፈ ወይም ወደርሱ ምንም ያልተወረደለት ሲኾን «ወደኔ ተወረደልኝ» ካለና፡-«አላህም ያወረደውን ብጤ በእርግጥ አወርዳለሁ» ካለ ሰው ይበልጥ በዳይ ማን ነው በደለኞችንም በሞት መከራዎች ውስጥ በኾኑ ጊዜ መላእክት እጆቻቸውን ዘርጊዎች ኾነው « (ለቅጥጥብ) ነፍሶቻችሁን አውጡ፤ በአላህ ላይ እውነት ያልኾነን ነገር ትናገሩ በነበራችሁትና ከአንቀጾቹም ትኮሩ በነበራችሁት ምክንያት ዛሬ የውርደትን ቅጣት ትመነዳላችሁ» (የሚሏቸው ሲኾኑ) ብታይ ኖሮ (አስደናቂን ነገር ባየህ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,94,'6:94','መጀመሪያም ጊዜ እንደ ፈጠርናችሁ ኾናችሁ የሰጠናችሁን ሁሉ በጀርባዎቻችሁ ኋላ የተዋችሁ ስትኾኑ የሰጠናችሁን ሁሉ በጀርባዎቻችሁ ኋላ የተዋችሁ ስትኾኑ ለየብቻችሁ ኾናችሁ በእርግጥ መጣችሁን እነዚያንም እነሱ በእናንተ ውስጥ (ለአላህ) ተጋሪዎች ናቸው የምትሉዋቸውን አማላጆቻችሁን ከእናንተ ጋር አናይም፡፡ ግንኙነታችሁ በእርግጥ ተቋረጠ፡፡ ከእናንተም ያ (ያማልደናል) የምትሉት ጠፋ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,95,'6:95','አላህ ቅንጣትንና የፍሬን አጥንት ፈልቃቂ ነው፡፡ ሕያውን ከሙት ያወጣል፤ ሙትንም ከሕያው አውጪ ነው፡፡ እርሱ አላህ ነው፤ ታዲያ (ከእምነት) እንዴት ትመለሳላችሁ (ትርቃላችሁ)');
INSERT INTO "sadiq_sani" VALUES(6,96,'6:96','እርሱም ጎህን (ከሌሊት ጨለማ) ፈልቃቂ ሌሊትንም ማረፊያ ፀሐይንና ጨረቃንም (ለጊዜ) መቁጠሪያ አድራጊ ነው፡፡ ይህ የአሸናፊው የዐዋቂው (አላህ) ችሎታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,97,'6:97','እርሱም ያ ከዋክብትን በየብስና በባሕር ጨለማዎች ውስጥ በእርሷ ትመሩ ዘንድ ለእናንተ ያደረገ ነው፡፡ ለሚያውቁ ሕዝቦች አንቀጾችን በእርግጥ ዘረዘርን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,98,'6:98','እርሱም ያ ከአንዲት ነፍስ ያስገኛችሁ፤ ነው፡፡ (በማሕፀን) መርጊያና (በጀርባ) መቀመጫም (አላችሁ)፡፡ ለሚያወቁ ሕዝቦች አንቀጾችን በእርግጥ ዘረዘርን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,99,'6:99','እርሱም ያ ከሰማይ ውሃን ያወረደ ነው፡፡ በርሱም የነገሩን ሁሉ በቃይ አወጣን፡፡ የተደራረበንም ቅንጣት ከርሱ የምናወጣ የኾነን ለምለም (አዝመራ) ከርሱ አወጣን፡፡ ከዘንባባም ከእንቡጧ የተቀራረቡ የኾኑ ዘለላዎች አሉ፡፡ ከወይኖችም፣ ከወይራም፣ ከሩማንም (ቅጠላቸው) ተመሳሳይና (ፍሬያቸው) የማይመሳሰል ሲኾኑ አትክልቶችን (አወጣን)፡፡ ባፈራ ጊዜ ወደ ፍሬውና ወደ መብሰሉ ተመልከቱ፡፡ በእነዚህ ተዓምራት ለሚያምኑ ሕዝቦች ምልክቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,100,'6:100','ለአላህም አጋንንትን የፈጠራቸው ሲኾን (በመታዘዝ) ተጋሪዎች አደረጉ፡፡ ወንዶች ልጆችንና ሴቶች ልጆችንም ያለ ዕውቀት ለእርሱ ቀጠፉ፡፡ (አላህ) ጥራት የተገባው ከሚሉት ነገር የላቀ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,101,'6:101','(እርሱ) ሰማያትንና ምድርን ያለ ብጤ ፈጣሪ ነው፡፡ ለእርሱ ሚስት የሌለችው ሲኾን እንዴት ለእርሱ ልጅ ይኖረዋል ነገርንም ሁሉ ፈጠረ፡፡ እርሱም ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,102,'6:102','ይህ ጌታችሁ አላህ ነው፡፡ ከእርሱ በቀር አምላክ የለም፡፡ ነገርን ሁሉ ፈጣሪ ነው፡፡ ስለዚህ ተገዙት፡፡ እርሱም በነገሩ ሁሉ ላይ ተጠባባቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,103,'6:103','ዓይኖች አያገኙትም፤ (አያዩትም)፡፡ እርሱም ዓይኖችን ያያል፡፡ እርሱም ርኅራኄው ረቂቁ ውስጠ ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,104,'6:104','«ከጌታችሁ ዘንድ ብርሃኖች በእርግጥ መጡላችሁ፡፡ የተመለከተም ሰው (ጥቅሙ) ለነፍሱ ብቻ ነው፡፡ የታወረም ሰው (ጉዳቱ) በራሱ ላይ ብቻ ነው፡፡ እኔም በእናንተ ላይ ጠባቂ አይደለሁም» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,105,'6:105','እንደዚሁም « (እንዲገመግሙና ያለፉትን መጻሕፍት) አጥንተሃልም» እንዲሉ ለሚያውቁ ሕዝቦችም (ቁርኣንን) እንድናብራራው አንቀጾችን እንገልጻለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,106,'6:106','ከጌታህ ወደ አንተ የተወረደውን ተከተል፡፡ ከእርሱ በቀር አምላክ የለም፡፡ አጋሪዎችንም ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,107,'6:107','አላህም በሻ ኖሮ ባላጋሩ ነበር፡፡ በእነሱም ላይ ጠባቂ አላደረግንህም፡፡ አንተም በእነሱ ላይ ኃላፊ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,108,'6:108','እነዚያንም ከአላህ ሌላ የሚገዙዋቸውን (ጣዖታትን) አትስደቡ፡፡ ድንበርን በማለፍ ያለዕውቀት አላህን ይሰድባሉና፡፡ እንደዚሁ ለሕዝቦች ሁሉ ሥራቸውን ደግ አስመሰልንላቸው፡፡ ከዚያም መመለሻቸው ወደ ጌታቸው ነው፡፡ ይሠሩት የነበሩትንም ሁሉ ይነግራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,109,'6:109','ተዓምርም ብትመጣላቸው በእርሷ በቁርጥ ሊያምኑ ጥብቅ መሐሎቻቸውን በአላህ ማሉ፡፡ «ተዓምራት ሁሉ አላህ ዘንድ ናት» በላቸው፡፡ እርሷ በመጣች ጊዜም አለማመናቸውን (ወይም ማመናቸውን) ምን አሳወቃችሁ');
INSERT INTO "sadiq_sani" VALUES(6,110,'6:110','በመጀመሪያም ጊዜ በእርሱ እንዳላመኑ ሁሉ ልቦቻቸውንና ዓይኖቻቸውን እናገላብጣለን፡፡ በጥመታቸውም ውስጥ የሚዋልሉ ሲኾኑ እንተዋቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,111,'6:111','እኛም ወደእነርሱ መላእክትን ባወረድን ሙታንም ባነጋገሩዋቸው ነገሩንም ሁሉ ጭፍራ ጭፍራ አድርገን በእነሱ ላይ በሰበሰብን ኖሮ አላህ ካልሻ በስተቀር የሚያምኑ ባልኾኑ ነበር፡፡ ግን አብዛኞቻቸው (ይህንን) ይስታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,112,'6:112','እንደዚሁም ለነቢያት ሁሉ ከሰውና ከጋኔን የኾኑን ሰይጣናት ጠላት አደረግን፡፡ ከፊላቸው ወደ ከፊሉ ለማታለል ልብስብስን ቃል ይጥላሉ፡፡ ጌታህም በሻ ኖሮ ባልሠሩት ነበር፡፡ ከቅጥፈታቸውም ጋር ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,113,'6:113','(የሚጥሉትም ሊያታልሉና) የእነዚያም በመጨረሻይቱ ሕይወት የማያምኑት ሰዎች ልቦች ወደእርሱ እንዲያዘነብሉ እንዲወዱትም እነርሱ ይቀጥፉ የነበሩትንም እንዲቀጣጥፉ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,114,'6:114','እርሱ ያ መጽሐፉን የተብራራ ኾኖ ወደእናንተ ያወረደ ሲኾን «ከአላህ ሌላ ዳኛን እፈልጋለሁን» (በላቸው)፡፡ እነዚያም መጽሐፍን የሰጠናቸው እርሱ ከጌታህ ዘንድ በእውነት የተወረደ መኾኑን ያውቃሉ፡፡ ከተጠራጣሪዎቹም አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,115,'6:115','የጌታህም ቃላት እውነተኛና ትክክለኛ ስትኾን ተፈጸመች፡፡ ለቃላቱ ለዋጭ የለም፡፡ እርሱም ሰሚው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,116,'6:116','በምድርም ካሉት ሰዎች አብዛኞቹን ብትከተል ከአላህ መንገድ ያሳስቱሃል፡፡ ጥርጣሬን እንጅ ሌላን አይከተሉም፡፡ እነርሱም የሚዋሹ እንጂ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,117,'6:117','ጌታህ እርሱ ከመንገዱ የሚሳሳቱትን ዐዋቂ ነው፡፡ እርሱም ተመሪዎቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,118,'6:118','በአንቀጾቹም አማኞች እንደ ኾናችሁ (ሲታረድ) የአላህ ስም በእርሱ ላይ ከተወሳበት እንስሳ ብሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,119,'6:119','ወደርሱም (መብላት) ከተገደዳችሁበት በስተቀር በእናንተ ላይ እርም የተደረገውን ለእናንተ (አላህ) በእርግጥ የዘረዘረ ሲኾን ከዚያ በእርሱ ላይ የአላህ ስም ከተጠራበት የማትበሉት ለናንተ ምን (ምክንያት) አላችሁ ብዙዎቹም ያለ ዕውቀት በዝንባሌዎቻቸው ያሳስታሉ፡፡ ጌታህ እርሱ ወሰን አላፊዎቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,120,'6:120','የኃጢአትንም ግልጹንም ድብቁንም ተውዉ፡፡ እነዚያ ኃጢአትን የሚሠሩ፤ ይሠሩት በነበሩት ነገር በእርግጥ ይቀጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,121,'6:121','በእርሱም ላይ የአላህ ስም ካልተጠራበት ነገር አትብሉ፡፡ እርሱም በእርግጥ አመጽ ነው፡፡ ሰይጣናትም ወደ ወዳጆቻቸው (በክትን በመብላት) ይከራከሯችሁ ዘንድ ያሾከሹካሉ፡፡ ብትታዘዙዋቸውም እናንተ በእርግጥ አጋሪዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,122,'6:122','ሙት የነበረና ሕያው ያደረግነው ለእርሱም በሰዎች መካከል በእርሱ የሚኼድበትን ብርሃን ያደረግንለት ሰው በጨለማዎች ውስጥ ከርሷ የማይወጣ ኾኖ እንዳልለ ሰው ብጤ ነውን እንደዚሁ ለከሓዲዎች ይሠሩት የነበሩት ነገር ተጌጠላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,123,'6:123','እንደዚሁም በየከተማይቱ በውስጧ ያሴሩ ዘንድ የተንኮለኞችዋን ታላላቆች አደረግን፡፡ በነፍሶቻቸውም ላይ እንጅ በሌላ ላይ አያሴሩም፤ ግን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,124,'6:124','ተዓምርም በመጣቻቸው ጊዜ፡- «የአላህ መልክተኞች የተሰጡትን ብጤ እስክንሰጥ ድረስ በፍጹም አናምንም» አሉ፡፡ አላህ መልክቱን የሚያደርግበትን ስፍራ ዐዋቂ ነው፡፡ እነዚያን ያመጹትን ሰዎች ይዶልቱ በነበሩት ነገር አላህ ዘንድ ውርደትና ብርቱ ቅጣት ያገኛቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,125,'6:125','አላህም ሊመራው የሚሻውን ሰው ደረቱን (ልቡን) ለኢስላም ይከፍትለታል፡፡ ሊያጠመውም የሚሻውን ሰው ደረቱን ጠባብ ቸጋራ ወደ ሰማይ ለመውጣት አንደሚታገል ያደርገዋል፡፡ እንደዚሁ አላህ በእነዚያ በማያምኑት ላይ ርክሰትን ያደርጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,126,'6:126','ይህም (ያለህበት) ቀጥተኛ ሲኾን የጌታህ መንገድ ነው፡፡ ለሚያስታውሱ ሕዝቦች አንቀጾችን በእርግጥ ዘርዝረናል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,127,'6:127','ለእነርሱ በጌታቸው ዘንድ የሰላም አገር አላቸው፡፡ እርሱም (ጌታህ) ይሠሩት በነበሩት ምክንያት ረዳታቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,128,'6:128','ሁሉንም የሚሰበስብባቸውንም ቀን (አስታውስ)፡፡፡ የአጋንንት ጭፍሮች ሆይ! ከሰዎች (ጭፍራን በማጥመም) በእርግጥ አበዛችሁ (ይባላሉ)፡፡ ከሰዎችም የኾኑት ወዳጆቻቸው፡-«ጌታችን ሆይ! ከፊላችን በከፊሉ ተጠቀመ፡፡ ያንንም ለእኛ የወሰንክልንን ጊዜያችንን ደረስን» ይላሉ፡፡ እሳት አላህ የሻው ጊዜ ሲቀር በውስጧ ዘውታሪዎች ስትኾኑ መኖሪያችሁ ናት ይላቸዋል፡፡ ጌታህ ጥበበኛ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,129,'6:129','እንደዚሁም የበደለኞችን ከፊል በከፊሉ ላይ ይሠሩት በነበሩት ጥፋት ምክንያት እንሾማለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,130,'6:130','የጋኔንና የሰዎች ጭፍሮች ሆይ!፡- «አንቀጾቼን በእናንተ ላይ የሚተርኩ የዚህንም ቀናችሁን (ቅጣት) ማግኘትን የሚያስፈራሩዋችሁ ከእናንተው የኾኑ መልክተኞች አልመጧችሁምን» (ይባላሉ)፡፡ «በነፍሶቻችን ላይ መሰከርን፤ (መጥተውልናል)» ይላሉ፡፡ የቅርቢቱም ሕይወት አታለለቻቸው፡፡ በነፍሶቻቸውም ላይ እነርሱ ከሓዲዎች የነበሩ መኾናቸውን መሰከሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,131,'6:131','ይህ ጌታህ ከተሞችን ባለቤቶችዋ ዘንጊዎች ኾነው ሳሉ በበደል ምክንያት አጥፊ ባለመኾኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,132,'6:132','ለሁሉም ከሠሩት ሥራ (የተበላለጡ) ደረጃዎች አልሏቸው፡፡ ጌታህም ከሚሠሩት ሁሉ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,133,'6:133','ጌታህም ተብቃቂ የእዝነት ባለቤት ነው፡፡ ቢሻ ያስወግዳችኋል፤ (ያጠፋችኋል)፡፡ ከሌሎች ሕዝቦችም ዘሮች እንዳስገኛችሁ ከበኋላችሁ የሚሻውን ይተካል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,134,'6:134','የምትቀጠሩት ሁሉ በእርግጥ መጪ ነው፡፡ እናንተም አምላጮች አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,135,'6:135','«ሕዝቦቼ ሆይ! በችሎታችሁ ላይ ሥሩ፤ እኔ (በችሎታዬ ላይ) ሠሪ ነኝና፡፡ ምስጉኒቱም አገር (ገነት) ለእርሱ የምትኾንለት ሰው ማን እንደኾነ ወደ ፊት ታውቃላችሁ፡፡ እነሆ በደለኞች አይድኑም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,136,'6:136','ለአላህም ከፈጠረው ከአዝመራና ከግመል፣ ከከብት፣ ከፍየልም ድርሻን አደረጉ፡፡ በሐሳባቸውም «ይህ ለአላህ ነው፡፡ ይህም ለተጋሪዎቻችን ነው፡፡» ለተጋሪዎቻቸውም «(ለጣዖታት) የኾነው ነገር ወደ አላህ አይደርስም፡፡ ለአላህም የኾነው እርሱ ወደ ተጋሪዎቻቸው ይደርሳል» አሉ፡፡ የሚፈርዱት ፍርድ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(6,137,'6:137','እንደዚሁም ከአጋሪዎቹ ለብዙዎቹ፤ ተጋሪዎቻቸው ሊያጠፉዋቸው ሃይማኖ ታቸውንም በእነሱ ላይ ሊያቀላቅሉባቸው (ሊያመሳስሉባቸው) ልጆቻቸውን መግደልን አሳመሩላቸው፡፡ አላህም በሻ ኖሮ ባልሠሩት ነበር፡፡ ከቅጥፈታቸውም ጋር ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,138,'6:138','በሐሳባቸውም ይህች እርም የኾነች ለማዳ እንስሳና አዝመራ ናት፡፡ የምንሻው ሰው እንጂ ሌላ አይበላትም፡፡ (ይህች) ጀርቦችዋ እርም የተደረገች ለማዳ እንስሳም ናት (አትጫንም)፡፡ ይህች በርሷ ላይ የአላህን ስም (ስትታረድ) የማይጠሩባትም እንስሳ ናት አሉ፡፡ በእርሱ ላይ ለመቅጠፍ (ነገሩን ወደ አላህ አስጠጉ)፡፡ ይቀጥፉት በነበሩት ነገር በእርግጥ ይቀጣቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,139,'6:139','«በነዚህም እንስሶች ሆዶች ውስጥ ያለው ለወንዶቻችን በተለይ የተፈቀደ ነው፡፡ በሚስቶቻችንም ላይ እርም የተደረገ ነው፡፡ ሙትም ቢኾን (ሙት ኾኖ ቢወለድ) እነርሱ (ወንዶቹም ሴቶቹም) በርሱ ተጋሪዎች ናቸው» አሉ፡፡ በመቅጠፋቸው አላህ በእርግጥ ይቀጣቸዋል፡፡ እርሱ ጥበበኛ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,140,'6:140','እነዚያ ያለ ዕውቀት በሞኝነት ልጆቻቸውን የገደሉና አላህም የሰጣቸውን (ሲሳይ) በአላህ ላይ በመቅጠፍ እርም ያደረጉ በእርግጥ ከሰሩ፡፡ በእርግጥ ተሳሳቱ፡፡ ተመሪዎችም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,141,'6:141','እርሱም ያ ዳስ የሚደረግላቸውንና ዳስ የማይደረግላቸውን አትክልቶች የፈጠረ ነው፡፡ ዘምባባንም (የተምርን ዛፍ) አዝመራንም ፍሬዎቹ የተለያዩ ሲኾኑ ወይራንና ሩማንንም (ቅጠላቸው) ተመሳሳይና (ጣዕማቸው) የማይመሳሰል ሲኾኑ (ፈጠረ)፡፡ ባፈራ ጊዜ ከፍሬው ብሉ፡፡ በአጨዳውም ቀን ተገቢውን (ዘካ) ስጡ፡፡ አታባክኑም፡፡ እርሱ አባካኞችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,142,'6:142','ከለማዳ እንስሳዎችም የሚጫኑትንና የማይጫኑትን (ፈጠረ)፡፡ አላህ ከሰጣችሁ ሲሳይ ብሉ፡፡ የሰይጣንንም እርምጃዎች አትከተሉ፡፡ እርሱ ለእናንተ ግልጽ ጠላት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,143,'6:143','ስምንትን ዓይነቶች ከበግ ሁለትን (ወንድና ሴትን) ከፍየልም ሁለትን (ፈጠረ)፡፡«ከሁለቱም ክፍሎች አላህ ወንዶቹን እርም አደረገን ወይስ ሴቶቹን ወይስ በርሱ ላይ የሁለቱ ሴቶች ማሕፀኖች ያጠቃለሉትን እውነተኞች እንደሆናችሁ በዕውቀት (በአስረጅ) ንገሩኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,144,'6:144','ከግመልም ሁለትን ከከብትም ሁለትን (ፈጠረ)፡፡«አላህ (ከሁለቱ) ወንዶቹን እርም አደረገን ወይስ ሴቶቹን ወይስ የሁለቱ ሴቶች ማሕፀኖች በእርሱ ላይ ያጠቃለሉትን በእውነቱ አላህ በዚህ ባዘዛችሁ ጊዜ የተጣዳችሁ ነበራችሁን ሰዎችንም ያለ ዕውቀት ሊያጠም በአላህ ላይ ውሸትንም ከቀጠፈ ሰው ይበልጥ በደለኛ ማን ነው አላህ በደለኞችን ሕዝቦች (ቅኑን መንገድ) አይመራም፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,145,'6:145','(ሙሐመድ ሆይ!) በላቸው፡- «ወደእኔ በተወረደው ውስጥ በክት ወይም ፈሳሽ ደም ወይም የአሳማ ስጋ እርሱ ርኩስ ነውና ወይም በአመጽ ከአላህ ስም ሌላ በእርሱ ላይ የተወሳበት ካልኾነ በስተቀር በሚመገበው ተመጋቢ ላይ እርም የኾነን ነገር አላገኝም፡፡ አመጸኛና ወሰን ያለፈ ሳይኾንም የተገደደ ሰው (ከተወሱት ቢበላ ኃጢአት የለበትም)፡፡ ጌታህ መሓሪ አዛኝ ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,146,'6:146','በእነዚያም አይሁዳውያን በኾኑት ላይ ባለ ጥፍርን ሁሉ እርም አደረግን፡፡ ከከብት፣ ከፍየልና ከበግም ሞራዎቻቸውን ጀርባዎቻቸው ወይም አንጀቶቻቸው የተሸከሙት ወይም በአጥንት የተቀላቀለው (ስብ) ሲቀር በነርሱ ላይ እርም አደረግን፡፡ ይህንን በአመጻቸው ምክንያት ቀጣናቸው፡፡ እኛ እውነተኞች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,147,'6:147','ቢያስተባብሉህም፡- «ጌታችሁ የሰፊ ችሮታ ባለቤት ነው፡፡ ብርቱ ቅጣቱም ከአመጸኞች ሕዝቦች ላይ አይመለስም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,148,'6:148','እነዚያ ያጋሩት «አላህ በሻ ኖሮ እኛም አባቶቻቸንም ባላገራን ነበር፡፡ አንዳችንም ነገር እርም ባላደረግን ነበር ይላሉ፡፡ እንደዚሁ (እነዚህን እንደዋሹ) እነዚያ ከበፊታቸው የነበሩት ብርቱ ቅጣታችንን እስከቀመሱ ድረስ አስተባባሉ፡፡ ለእኛ ታዘልቁልን ዘንድ (ማጋራታችሁን ለመውደዱ) እናንተ ዘንድ ዕውቀት አለን ጥርጣሬን እንጅ ሌላ አትከተሉም፡፡ እናንተ ዋሾዎች እንጂ ሌላ አይደላችሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,149,'6:149','«የተሟላው ማስረጃ የአላህ ነው፡፡ በሻም ኖሮ ሁላችሁንም ባቀናችሁ ነበር» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,150,'6:150','«እነዚያን አላህ ይህን እርም ማድረጉን የሚመሰክሩትን ምስክሮቻችሁን አምጡ» በላቸው፡፡ ቢመሰክሩም ከእነርሱ ጋር አትመስክር፡፡ የነዚያንም በአንቀጾቻችን ያስተባበሉትን የነዚያንም እነርሱ በጌታቸው (ሌላን) የሚያስተካክሉ ሲኾኑ በመጨረሻይቱ ዓለም የማያምኑትን ሰዎች ዝንባሌዎች አትከተል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,151,'6:151','«ኑ፤ ጌታችሁ በእናንተ ላይ እርም ያደርገውን ነገር (በእርሱም ያዘዛችሁን) ላንብብላችሁ» በላቸው፡፡ «በእርሱ (በአላህ) ምንንም ነገር አታጋሩ፡፡ ለወላጆችም በጎን ሥራ (ሥሩ)፡፡ ልጆቻችሁንም ከድህንት (ፍራቻ) አትግደሉ፡፡ እኛ እናንተንም እነርሱንም እንመግባችኋለንና፡፡ መጥፎ ሥራዎችንም ከእርሷ የተገለጸውንም የተደበቀውንም ሁሉ አትቅረቡ፡፡ ያችንም አላህ እርም ያደረጋትን ነፍስ በሕግ ቢኾን እንጅ አትግደሉ፡፡ ይህን ታውቁ ዘንድ (አላህ) በእርሱ አዘዛችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,152,'6:152','«የየቲምንም ገንዘብ ብርታቱን (አካለ መጠን) እስኪደርስ ድረስ በዚያች እርሷ መልካም በኾነች ኹኔታ እንጅ አትቅረቡ፡፡ ስፍርንና ሚዛንንም በትክክሉ ሙሉ፡፡ ነፍስን ችሎታዋን እንጂ አናስገድድም፡፡ በተናገራችሁም ጊዜ በዘመዶቻችሁ ላይ ቢኾንም እንኳ (እውነትን በመናገር) አስተካክሉ፡፡ በአላህም ቃል ኪዳን ሙሉ፡፡ እነሆ ትገሰጹ ዘንድ በርሱ አዘዛችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,153,'6:153','«ይህም ቀጥተኛ ሲኾን መንገዴ ነው፤ ተከተሉትም፡፡ (የጥመት) መንገዶችንም አትከተሉ፡፡ ከ(ቀጥተኛው) መንገዱ እናንተን ይለያዩዋችኋልና፡፡እነሆ ትጠነቀቁ ዘንድ በርሱ አዘዛችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,154,'6:154','ከዚያም ለሙሳ በዚያ መልካም በሠራው ላይ (ጸጋችንን) ለማሟላት ነገርንም ሁሉ ለመዘርዘር መጽሐፉን (ለእስራኤል ልጆች) መሪና እዝነት ይኾን ዘንድ ሰጠነው፡፡ እነርሱ በጌታቸው መገናኘት ሊያምኑ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(6,155,'6:155','ይህም ያወረድነው የኾነ የተባረከ መጽሐፍ ነው፡፡ ተከተሉትም፡፡ ይታዘንላችሁም ዘንድ (ከክህደት) ተጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(6,156,'6:156','«(ያወረድነውም) መጽሐፍ የተወረደው ከፊታችን በሁለቱ ጭፍሮች ላይ ብቻ ነው፡፡ እኛም ከንባባቸው ዘንጊዎች ነበርን» እንዳትሉ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,157,'6:157','ወይም «እኛ መጽሐፍ በእኛ ላይ በተወረደ ኖሮ ከእነሱ ይበልጥ የተመራን እንኾን ነበር» እንዳትሉ ነው፡፡ ስለዚህ ከጌታችሁ ግልጽ ማስረጃ፣ መምሪያም፣ እዝነትም በእርግጥ መጣላችሁ፡፡ በአላህም አንቀጾች ካስተባበለና ከእርሷም ከዞረ ሰው ይበልጥ በዳይ ማን ነው እነዚያን አንቀጾቻችንን የሚተውትን ይተውት በነበሩት ምክንያት ብርቱን ቅጣት በእርግጥ እንቀጣቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(6,158,'6:158','መላእክት ልትመጣላቸው ወይም ጌታህ (ቅጣቱ) ሊመጣ ወይም ከጌታህ ምልክቶች ከፊሉ ሊመጣ እንጂ ይጠባበቃሉን ከጌታህ ምልክቶች ከፊሉ በሚመጣ ቀን ከዚህ በፊት አማኝ ያልኾነችን ነፍስ እምነትዋ ወይም በእምነትዋ (ጊዜ) በጎ ያልሠራችን (ነፍስ ጸጸትዋ) አይጠቅማትም፡፡ ተጠባበቁ እኛ ተጠባባቂዎች ነንና በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(6,159,'6:159','እነዚያ ሃይማኖታቸውን የለያዩ አሕዛብም የኾኑ በምንም ከእነሱ አይደለህም፡፡ ነገራቸው ወደ አላህ ብቻ ነው፡፡ ከዚያም ይሠሩት የነበሩትን ሁሉ ይነገራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,160,'6:160','በመልካም ሥራ የመጣ ሰው ለእርሱ ዐሥር ብጤዎችዋ አሉት፡፡ በክፉ ሥራም የመጣ ሰው ብጤዋን እንጅ አይመነዳም፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(6,161,'6:161','«እኔ ጌታዬ ወደ ቀጥተኛው መንገድ ትክክለኛን ሃይማኖት ወደ እውነት አዘንባይ ሲኾን የአብርሃምን መንገድ መራኝ፡፡ እርሱም ከአጋሪዎቹ አልነበረም» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,162,'6:162','«ስግደቴ፣ መገዛቴም፣ ሕይወቴም፣ ሞቴም ለዓለማት ጌታ ለአላህ ነው» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(6,163,'6:163','«ለእርሱ ተጋሪ የለውም፡፡ በዚህም (በማጥራት) ታዘዝኩ፡፡ እኔም የሙስሊሞች መጀመሪያ ነኝ» (በል)፡፡');
INSERT INTO "sadiq_sani" VALUES(6,164,'6:164','በላቸው «እርሱ (አላህ) የሁሉ ጌታ ሲኾን ከአላህ በቀር ሌላን ጌታ እፈልጋለሁን ነፍስም ሁሉ በራሷ ላይ እንጅ (ክፉን) አትሠራም፡፡ ተሸካሚም (ነፍስ) የሌላይቱን ሸክም (ኃጢአት) አትሸከምም፡፡ ከዚያም መመለሻችሁ ወደ ጌታችሁ ነው፡፡ ወዲያውም በእርሱ ትለያዩበት የነበራችሁትን ሁሉ ይነገራችኋል፡፡»');
INSERT INTO "sadiq_sani" VALUES(6,165,'6:165','እርሱም ያ በምድር ምትኮች ያደረጋችሁ በሰጣችሁም ጸጋ ሊፈትናችሁ ከፊላችሁን ከከፊሉ በላይ በደረጃዎች ከፍ ያደረገ ነው፡፡ ጌታህ ቅጣቱ ፈጣን ነው፡፡ እርሱም እጅግ መሓሪ ሩኅሩኅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,1,'7:1','አ.ለ.መ.ሰ (አሊፍ፤ ላም ፤ሚም፤ ሷድ)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,2,'7:2','(ይህ) ወደ አንተ የተወረደ መጽሐፍ ነው፡፡ በደረትህም ውስጥ ከእርሱ ጭንቀት አይኑር፡፡ (የተወረደውም) በእርሱ ልታስፈራራበትና ለምእምናን መገሠጫ እንዲኾን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,3,'7:3','ከጌታችሁ ወደእናንተ የተወረደውን ተከተሉ፡፡ ከአላህም ሌላ ረዳቶችን አትከተሉ፡፡ ጥቂትን ብቻ ትገሰጻላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,4,'7:4','ከከተማም ልናጠፋት የሻነው ወዲያውም ብርቱ ቅጣታችን ሌሊት ወይም ቀን እነርሱ በቀትር አርፈው ሳሉ የመጣባቸው ብዙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,5,'7:5','ቅጣታችንም በመጣባቸው ጊዜ «ጸሎታቸው እኛ በደለኞች ነበርን» ከማለት በቀር ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,6,'7:6','እነዚያንም ወደነሱ የተላከባቸውን (ሕዝቦች) በእርግጥ መልክተኞቹንም እንጠይቃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,7,'7:7','በእነርሱም ላይ (የነበሩበትን ሁሉ) ከዕውቀት ጋር በእርግጥ እንተርክላቸዋለን፡፡ የራቅንም አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,8,'7:8','ትክክለኛውም ሚዛን የዚያን ቀን ነው፡፡ ሚዛኖቻቸው የከበዱላቸው ሰዎችም እነዚያ እነርሱ የፈለጉትን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,9,'7:9','ሚዛኖቻቸው የቀለሉባቸው ሰዎችም እነዚያ እነርሱ ተአምራቶቻችንን ይክዱ በነበሩት ምክንያት ነፍሶቻቸውን ያከሰሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,10,'7:10','በምድርም ላይ በእርግጥ አስመቸናችሁ፡፡ በእርሷም ላይ ለናንተ መኖሪያዎችን አደረግንላችሁ፤ ምስጋናችሁ በጣም ጥቂት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,11,'7:11','በእርግጥም ፈጠርናችሁ፡፡ ከዚያም ቀረጽናችሁ፡፡ ከዚያም ለመላእክቶች «ለአዳም ስገዱ» አልን፡፡ ወዲያውም ሰገዱ፡፡ ኢብሊስ (ዲያብሎስ) ሲቀር ከሰጋጆቹ አልኾነም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,12,'7:12','(አላህ) «ባዘዝኩህ ጊዜ ከመስገድ ምን ከለከለህ» አለው፡፡ «እኔ ከርሱ የበለጥኩ ነኝ፡፡ ከእሳት ፈጠርከኝ፡፡ እርሱን ግን ከጭቃ ፈጠርከው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,13,'7:13','ከእርሷ ውረድ፤ በእርሷ ውስጥ ልትኮራ አይገባህምና፡፡ ውጣም አንተ ከወራዶቹ ነህና አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,14,'7:14','«እስከሚቀሰቀሱበት ቀን ድረስ አቆየኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,15,'7:15','፡-አንተ ከሚቆዩት ነህ አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,16,'7:16','«ስለአጠመምከኝም ለእነርሱ በቀጥተኛው መንገድ ላይ በእርግጥ እቀመጥባቸዋለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,17,'7:17','«ከዚያም ከስተፊቶቻቸው ከኋላቸውም ከቀኞቻቸውም ከግራዎቻቸውም በእርግጥ እመጣባቸዋለሁ፡፡ አብዛኞቻቸውንም አመስጋኞች ኾነው አታገኛቸውም» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,18,'7:18','«የተጠላህ ብራሪ ስትኾን ከእርሷ ውጣ፡፡ ከእነርሱ የተከተለህ ከእናንተ ከመላችሁም ገሀነምን በእርግጥ እሞላታለሁ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,19,'7:19','«አዳም ሆይ! አንተም ሚስትህም በገነት ተቀመጡ፡፡ ከሻችሁትም ስፍራ ብሉ፡፡ ግን ይህችን ዛፍ አትቅረቡ፡፡ (ራሳቸውን) ከሚበድሉት ትኾናላችሁና» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,20,'7:20','ሰይጣንም ከሐፍረተ ገላቸው የተሸሸገውን ለእነርሱ ሊገልጽባቸው በድብቅ ንግግር ጎተጎታቸው፡፡ «ጌታችሁም መልኣኮች እንዳትኾኑ ወይም ከዘላለም ነዋሪዎች እንዳትኾኑ እንጂ ከዚህች ዛፍ አልከለከላችሁም» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,21,'7:21','«እኔ ለእናንተ በእርግጥ ከሚመክሩዋችሁ ነኝ» ሲልም አማለላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,22,'7:22','በማታለልም አዋረዳቸው፡፡ ከዛፊቱም በቀመሱ ጊዜ ኀፍረተ ገላቸው ለሁለቱም ተገለጠችላቸው፡፡ ከገነት ቅጠልም በላያቸው ላይ ይደርቱ ጀመር፡፡ ጌታቸውም «ከዚህ ዛፍ አልከለከልኋችሁምን ሰይጣንም ለእናንተ ግልጽ ጠላት ነው አላልኳችሁምን» ሲል ጠራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,23,'7:23','«ጌታችን ሆይ! ነፍሶቻችንን በደልን፡፡ ለእኛ ባትምር ባታዝንልንም በእርግጥ ከከሳሪዎቹ እንኾናለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,24,'7:24','(አላህ) « ከፊላችሁ ለከፊሉ ጠላት ሲኾን ውረዱ፡፡ ለእናንተም በምድር ላይ እስከ ተወሰነ ጊዜ ድረስ መርጊያና መጠቀሚያ አላችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,25,'7:25','«በእርሷ ላይ ትኖራላችሁ፡፡ በእርሷም ላይ ትሞታላችሁ፡፡ ከእርሷም ውስጥ ትወጣላችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,26,'7:26','የአዳም ልጆች ሆይ! ኀፍረተ ገላችሁን የሚሸሽግን ልብስ ጌጥንም በእርግጥ በናንተ ላይ አወረድን፡፡ አላህን የመፍራትም ልብስ ይህ የተሻለ ነው፡፡ ይህ ከአላህ ታምራቶች ነው፡፡ ይገሠጹ ዘንድ (አወረደላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,27,'7:27','የአዳም ልጆች ሆይ! ሰይጣን አባትና እናታችሁን ኀፍረተ ገላቸውን ሊያሳያቸው ልብሶቻቸውን ከነሱ የገፈፋቸው ሲኾን ከገነት እንዳወጣቸው ሁሉ እናንተን አይሞክራችሁ፡፡ እነሆ እርሱ ከነሰራዊቱ ከማታዩዋቸው ስፍራ ያዩዋችኋልና፡፡ እኛ ሰይጣናትን ለእነዚያ ለማያምኑት ወዳጆች አድርገናል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,28,'7:28','መጥፎንም ሥራ በሠሩ ጊዜ በእርሷ ላይ አባቶቻችንን አገኘን አላህም በእርሷ አዞናል ይላሉ፡፡ «አላህ በመጥፎ ነገር አያዝም፡፡ በአላህ ላይ የማታውቁትን ትናገራላችሁን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,29,'7:29','ጌታዬ በማስተካከል አዘዘ፡፡ በየመስጊዱም ዘንድ ፊቶቻችሁን (እርሱን ለመገዛት) አስተካክሉ፡፡ ሃይማኖትንም ለእርሱ ፍጹም አድርጋችሁ ተገዙት፡፡ እንደጀመራችሁ ትመለሳላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,30,'7:30','ከፊሉን ወደ ቅን መንገድ መራ፡፡ ከፊሎቹም በእነሱ ላይ ጥመት ተረጋግጦባቸዋል፡፡ እነርሱ ሰይጣናትን ከአላህ ሌላ ረዳቶች አድርገው ይዘዋልና፡፡ እነርሱም (ቅኑን መንገድ) የተመራን ነን ብለው ያስባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,31,'7:31','የአዳም ልጆች ሆይ! (ኀፍረተ ገላችሁን የሚሸፍኑትን) ጌጦቻችሁን በመስገጃው ሁሉ ዘንድ ያዙ፡፡ ብሉም፤ ጠጡም፤ አታባክኑም፡፡ እርሱ አባካኞችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,32,'7:32','«የአላህን ጌጥ ያችን ለባሮቹ የፈጠራትን ከሲሳይም ጥሩዎቹን እርም ያደረገ ማን ነው» በላቸው፡፡ «እርሷ በትንሣኤ ቀን ለእነዚያ ላመኑት ብቻ ስትኾን በቅርቢቱ ሕይወት ተገቢያቸው ናት» በላቸው፡፡ እንደዚሁ ለሚያውቁ ሕዝቦች አንቀጾችን እናብራራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,33,'7:33','«ጌታዬ እርም ያደረገው መጥፎ ሥራዎችን፣ ከርሷ የተገለጸውንና የተደበቀውን ኃጢኣትንም፣ ያላግባብ መበደልንም፣ በርሱም ማስረጃ ያላወረደበትን (ጣዖት) በአላህ ማጋራታችሁን፣ በአላህም ላይ የማታውቁትን መናገራችሁን ብቻ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,34,'7:34','ለሕዝብም ሁሉ የተወሰነ ጊዜ አላቸው፡፡ ጊዜያቸውም በመጣ ወቅት አንዲትን ሰዓት አይቆዩም፡፡ (ከጊዜያቱ) አይቀደሙምም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,35,'7:35','የአዳም ልጆች ሆይ! ከእናንተ ውስጥ በናንተ ላይ አንቀጾቼን የሚያነቡ መልከተኞች ቢመጧችሁ ከእናንተ ክሕደትን የተጠነቀቁና መልካምንም የሠሩ በነሱ ላይ ፍርሃት የለባቸውም፡፡ እነሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,36,'7:36','እነዚያም በአንቀጾቻችን ያስተባበሉ ከእርሷም የኮሩ እነዚያ የእሳት ጓዶች ናቸው፡፡ እነሱ በርሷ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,37,'7:37','በአላህ ላይ ውሸትን ከቀጠፈ ወይም በአንቀጾቹ ከአስዋሸ ሰው ይበልጥ በዳይ ማን ነው እነዚያ (ከተጻፈላቸው) ከመጽሐፉ ውስጥ ሲኾን ዕድላቸው ያገኛቸዋል፡፡ (የሞት) መልክተኞቻችንም የሚገድሏቸው ኾነው በመጡባቸው ጊዜ «ከአላህ ሌላ ትገዟቸው የነበራችሁት የት አሉ» ይሏቸዋል፡፡ «ከእኛ ተሰወሩን» ይላሉ፡፡ እነርሱም ከሃዲያን እንደነበሩ በነፍሶቻቸው ላይ ይመሰክራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,38,'7:38','«ከጋኔንም ከሰውም ከእናንተ በፊት በእርግጥ ካለፉት ሕዝቦች ጋር ኾናችሁ እሳት ውስጥ ግቡ» ይላቸዋል፡፡ አንዲቱ ሕዝብ ወደ እሳት በገባች ቁጥር (ያሳሳተቻትን) ብጤዋን ትረግማለች፡፡ መላውም በውስጧ ተሰብስበው በተገናኙም ጊዜ የኋለኛይቱ ለመጀመሪያይቱ (ተከታዮች ለአስከታዮች) «ጌታችን ሆይ! እነዚህ አሳሳቱን፡፡ ከእሳትም ስቃይ እጥፍን ስጣቸው» ትላለች፡፡ (አላህም)፡- ለሁሉም እጥፍ አለው ግን አታውቁም ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,39,'7:39','«መጀመሪያይቱም ለኋለኛይቱ ለእናንተ በእኛ ላይ ምንም ብልጫ አልነበራችሁም» ትላለች፡፡ «ትሠሩትም በነበራችሁት ቅጣቱን ቅመሱ» (ይላቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,40,'7:40','እነዚያ አንቀጾቻችንን ያስተባበሉ ከእርሷም የኮሩ ለእነርሱ የሰማይ ደጃፎች አይከፈቱላቸውም፡፡ ግመል በመርፌ ቀዳዳ እስከሚገባም ድረስ ገነትን አይገቡም፡፡ እንደዚሁም አጋሪዎችን እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,41,'7:41','ለእነርሱ ከገሀነም እሳት (በሥራቸው) ምንጣፍ ከበላያቸውም (የእሳት) መሸፈኛዎች አሏቸው፡፡ እንደዚሁም በደለኞችን እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,42,'7:42','እነዚያም ያመኑና መልካሞችን የሠሩ ነፍስን ችሎታዋን እንጂ አናስገድድምና፤ እነዚያ የገነት ሰዎች ናቸው፡፡ እነርሱ በርሷ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,43,'7:43','በደረቶቻቸው ውስጥ ያለውንም ጥላቻ እናስወግዳለን፡፡ ወንዞች በሥሮቻቸው ይፈሳሉ፡፡ «ለዚያም ወደዚህ (ላደረሰን ሥራ) ለመራን አላህ ምስጋና ይገባው፡፡ አላህ ባልመራንም ኖሮ አንመራም ነበር፡፡ የጌታችን መልክተኞች በእውነት ላይ ሲኾኑ በእርግጥ መጥተውልናል» ይላሉ፡፡ ይህች ገነት ትሠሩት በነበራችሁት ተሰጣችኋት በማለት ይጠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,44,'7:44','የገነትም ሰዎች የእሳትን ሰዎች «ጌታችን ቃል የገባልንን እውነት ኾኖ አገኘን፡፡ ጌታችሁ የዛተባችሁንስ እውነት ኾኖ አገኛችሁትን» ሲሉ ይጣራሉ፡፡ «አዎን አገኘን» ይላሉ፡፡ በመካከላቸውም «የአላህ ርግማን በበደለኞች ላይ ይኹን» ሲል ለፋፊ ይለፍፋል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,45,'7:45','(በደለኞች) እነዚያ ከአላህ መንገድ የሚከለክሉ፣ (የአላህም መንገድ) እንድትጣመምም የሚፈልጉዋት፣ እነርሱም በመጨረሻዋ ዓለም ከሓዲዎች የኾኑ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,46,'7:46','በመካከላቸውም ግርዶሽ አልለ፡፡ በአዕራፍም ላይ ሁሉንም በምልክታቸው የሚያውቁ ሰዎች አልሉ፡፡ የገነትንም ሰዎች ሰላም ለናንተ ይኹን በማለት ይጣራሉ፡፡ እነርሱም (የአዕራፍ ሰዎች) የሚከጅሉ ሲኾኑ ገና አልገቧትም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,47,'7:47','ዓይኖቻቸውም ወደ እሳት ሰዎች አቅጣጫ በተዞሩ ጊዜ፡- «ጌታችን ሆይ! ከበደለኞች ጋር አታድርገን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,48,'7:48','የአዕራፍም ሰዎች በምልክታቸው የሚያውቁዋቸውን (ታላላቅ) ሰዎች ይጣራሉ፡፡ «ስብስባችሁና በብዛታችሁ የምትኮሩ መኾናችሁም ከእናንተ ምንም አልጠቀማችሁ» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,49,'7:49','እነዚያ አላህ በችሮታው አያገኛቸውም ብላችሁ የማላችሁት (ደካሞች) እነዚህ ናቸውን «ገነትን ግቡ ፍርሃት የለባችሁም እናንተም አታዝኑም» (ተባሉ ይሏቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,50,'7:50','የእሳትም ሰዎች የገነትን ሰዎች «በእኛ ላይ ከውሃ አፍስሱብን ወይም አላህ ከሰጣችሁ ሲሳይ (ጣሉልን)» ብለው ጠሩዋቸዋል፡፡ እነርሱም «አላህ በከሓዲዎች ላይ እርም አድርጓቸዋል» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,51,'7:51','እነዚያ ሃይማኖታቸውን መሳለቂያና መጫወቻ አድርገው የያዙ የቅርቢቱም ሕይወት ያታለለቻቸው ይህንን ቀናቸውን መገናኘትን እንደረሱ በተአምራታችንም ይክዱ እንደነበሩ ዛሬ እንረሳቸዋልን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,52,'7:52','ከዕውቀት ጋርም የዘረዘርነው የኾነን መጽሐፍ ለሚያምኑ ሕዝቦች መምሪያና እዝነት ሲኾን በእርግጥ አመጣንላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,53,'7:53','(የዛቻውን) ፍጻሜውን እንጂ ሌላ አይጠባበቁም፡፡ ፍጻሜው በሚመጣበት ቀን እነዚያ ከመምጣቱ በፊት የረሱት ሰዎች «የጌታችን መልክተኞች በእርግጥ በውነት መጥተዋል፡፡ ለእኛ ያማልዱም ዘንድ አማላጆች አልሉን ወይስ ከዚያ እንሠራው ከነበርነው ሌላን ሥራ እንሠራ ዘንድ (ወደ ምድረ ዓለም) እንመለሳለን» ይላሉ፡፡ ነፍሶቻቸውን በእርግጥ አከሰሩ፡፡ ይቀጥፉት የነበሩትም ከእነርሱ ጠፋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,54,'7:54','ጌታችሁ ያ ሰማያትንና ምድርን በስድስት ቀኖች ውስጥ የፈጠረ አላህ ነው፡፡ ከዚያም (ለክብሩ በምስማማ መልኩ) በዐርሹ ላይ ተደላደለ፡፡ (አላህ) ሌሊትን በቀን ፈጥኖ የሚፈልገው ሲኾን ይሸፍናል፡፡ ፀሐይንና ጨረቃንም ከዋክብትንም በትእዛዙ የተገሩ ሲኾኑ (ፈጠራቸው)፡፡ ንቁ! መፍጠርና ማዘዝ የእርሱ ብቻ ነው፡፡ የዓለማት ጌታ አላህ (ክብሩ) ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,55,'7:55','ጌታችሁን ተዋርዳችሁ በድብቅም ለምኑት፡፡ እርሱ ወሰን አላፊዎችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,56,'7:56','በምድርም ውስጥ ከተበጀች በኋላ አታበላሹ፡፡ ፈርታችሁና ከጅላችሁም ተገዙት፡፡ የአላህ ችሮታ ከበጎ አድራጊዎች ቅርብ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,57,'7:57','እርሱም ያ ነፋሶችን ከችሮታው (ከዝናም) በፊት አብሳሪዎች አድርጎ የሚልክ ነው፡፡ ከባዳዎችንም ደመናዎች በተሸከሙ ጊዜ ሙት ወደ ኾነ አገር እንነዳዋለን፡፡ በእርሱም ውሃን እናወርዳለን፡፡ ከፍሬዎችም ሁሉ በእርሱ እናወጣለን፡፡ እንደዚሁ ትገነዘቡ ዘንድ ሙታንን (ከመቃብር) እናወጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,58,'7:58','መልካሙም አገር በጌታው ፈቃድ በቃዩ (ያማረ ኾኖ) ይወጣል፡፡ ያም መጥፎ የኾነው (በቃዩ) ደካማ ኾኖ እንጂ አይወጣም፡፡ እንደዚሁ ለሚያመሰግኑ ሕዝቦች ታምራትን እናብራራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,59,'7:59','ኑሕን ወደ ወገኖቹ በእርግጥ ላክነው፡፡ አላቸውም፡- «ወገኖቼ ሆይ! አላህን ተገዙ፡፡ ለእናንተ ከርሱ ሌላ ምንም አምላክ የላችሁም፡፡ እኔ በእናንተ ላይ የከባድ ቀንን ቅጣት እፈራላችኋለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,60,'7:60','ከሕዝቦቹ (የካዱት) መሪዎቹ፡- «እኛ በግልጽ መሳሳት ውስጥ ኾነህ በእርግጥ እናይሃለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,61,'7:61','አላቸው «ወገኖቼ ሆይ! ምንም መሳሳት የለብኝም፡፡ ግን እኔ ከዓለማት ጌታ መልእክተኛ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,62,'7:62','«የጌታዬን መልክቶች አደርስላችኋለሁ፡፡ ለእናንተም እመክራችኋለሁ፡፡ ከአላህም በኩል የማታውቁትን ዐውቃለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,63,'7:63','«አላህን ልትፈሩና ይታዘንላችሁም ዘንድ ከእናንተው (ጎሳ) በኾነ ሰው ላይ እንዲያስጠነቅቃችሁ ከጌታችሁ ግሳጼ ቢመጣላችሁ ትደነቃላችሁን» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,64,'7:64','ወዲያውም አስተባበሉት፡፡ እርሱንና እነዚያንም ከእርሱ ጋር ያሉትን (አማኞች) በታንኳዋ ውስጥ አዳንናቸው፡፡ እነዚያንም በአንቀጾቻችን ያስተባበሉትን አሰጠምን፡፡ እነርሱ (ልበ) ዕውራን ሕዝቦች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,65,'7:65','ወደ ዓድም ወንድማቸውን ሁድን ላክን፡፡ «ወገኖቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ምንም አምላክ የላችሁም፡፡ (የአላህን ቅጣት) አትፈሩምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,66,'7:66','ከሕዝቦቹ እነዚያ የካዱት መሪዎች «እኛ በሞኝነት ላይ ኾነህ በእርግጥ እናይሃለን፡፡ እኛም ከውሸተኞቹ ነህ ብለን እንጠረጥርሃለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,67,'7:67','(እርሱም) አላቸው «ወገኖቼ ሆይ! እኔ ሞኝነት የለብኝም፡፡ ግን እኔ ከዓለማት ጌታ የተላክሁ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,68,'7:68','«የጌታዬን መልክቶች አደርስላችኋለሁ፡፡ እኔም ለእናንተ ታማኝ መካሪ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,69,'7:69','«ከጌታችሁ የኾነ ግሣጼ ከእናንተው ውስጥ ባንድ ሰው ላይ ያስጠነቀቃችሁ ዘንድ ቢመጣባችሁ ትደነቃላችሁን ከኑሕም ሕዝቦች በኋላ ምትኮች ባደረጋችሁና በፍጠረትም ግዙፍነትን በጨመረላችሁ ጊዜ አስታውሱ፡፡ የሻችሁትንም ታገኙ ዘንድ የአላህን ጸጋዎች አስታውሱ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,70,'7:70','«አላህን ብቻውን እንድንገዛ አባቶቻችንም ይገዙት የነበረውን (አማልክት) እንድንተው መጣህብን ከውነተኞች ከኾንክ የምታስፈራብንን (ቅጣት) አምጣብን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,71,'7:71','«ከጌታችሁ የኾነ ቅጣትና ቁጣ በእናንተ ላይ በእውነት ተረጋገጠ፡፡ እናንተና አባቶቻችሁም (አማልክት ብላችሁ) በጠራችኋቸው ስሞች አላህ በእርሷ ምንም ማስረጃ ያላወረደባት ስትኾን ትከራከሩኛላችሁን ተጠባባቁም እኔ ከእናንተ ጋር ከተጠባባቂዎቹ ነኝና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,72,'7:72','እርሱንና እነዚያንም አብረውት የነበሩትን ከእኛ በኾነው ችሮታ (ከቅጣት) አዳንናቸው፡፡ የእነዚያንም በተአምራታችን ያስተባበሉትንና አማኞች ያልነበሩትን መሠረት ቆረጥን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,73,'7:73','ወደ ሰሙድም ወንድማቸውን ሷሊህን ላክን፡፡ አላቸው «ወገኖቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ለእናንተ ምንም አምላክ የላችሁም፡፡ (እውነተኛ ለመኾኔ) ከጌታችሁ የኾነች ምልክት በእርግጥ መጥታላችኋለች፡፡ ይህች ለእናንተ ተዓምር ስትኾን የአላህ ግመል ናትና ተዋት፡፡ በአላህ ምድር ውስጥ ትብላ፤ (ትጠጣም)፡፡ በክፉ አትንኳትም፡፡ አሳማሚ ቅጣት ይይዛችኋልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,74,'7:74','«ከዓድም በኋላ ምትኮች ባደረጋችሁና በምድሪቱም ላይ ባስቀመጣችሁ ጊዜ አስታውሱ፡፡ ከሜዳዎቿ (ሸክላ) ሕንጻዎችን ትገነባላችሁ፡፡ ከተራራዎችም ቤቶችን ትጠርባላችሁ፡፡ ስለዚህ የአላህን ጸጋዎች አስታውሱ፡፡ በምድርም ውስጥ አጥፊዎች ኾናችሁ አታበላሹ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,75,'7:75','ከወገኖቹ እነዚያ የኮሩት መሪዎች ለእነዚያ ለተሸነፉት ከነሱ ላመኑት፡- «ሷሊህ ከጌታው መላኩን ታውቃላችሁን» አሏቸው፡፡ « (አዎን)፡- እኛ እርሱ በተላከበት ነገር አማኞች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,76,'7:76','እነዚያ የኮሩት፡- «እኛ በዚያ እናንተ በርሱ ባመናችሁበት ከሓዲዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,77,'7:77','ወዲያውም ግመሊቱን ወጓት፡፡ ከጌታቸውም ትዕዛዝ ወጡ፡፡ አሉም፡- «ሷሊህ ሆይ! ከመልክተኞቹ እንደኾንክ የምታስፈራራብንን (ቅጣት) አምጣብን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,78,'7:78','ወዲያውም የምድር መንቀጥቀጥ (ጩኸትም) ያዘቻቸው፡፡ በቤቶቻቸውም ውስጥ ተንከፍርረው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,79,'7:79','(ሷሊህ) ከእነርሱም ዞረ፡፡ (እንዲህም) አለ «ወገኖቼ ሆይ! የጌታዬን መልክት በእርግጥ አደረስኩላችሁ፡፡ ለእናንተም መከርኳችሁ፡፡ ግን መካሪዎችን አትወዱም፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,80,'7:80','ሉጥንም ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፡፡ «አስቀያሚን ሥራ ትሠራላችሁን በእርሷ ከዓለማት አንድም አልቀደማችሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,81,'7:81','«እናንተ ከሴቶች ሌላ ወንዶችን በመከጀል በእርግጥ ትመጡባቸዋላችሁ፡፡ በእውነቱ እናንተ ወሰንን አላፊዎች ሕዝቦች ናችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,82,'7:82','የሕዝቦቹም መልስ « (ሎጥንና ተከታዮቹን) ከከተማችሁ አውጧቸው፡፡ እነሱ የሚጥራሩ ሰዎች ናቸውና» ማለት እንጂ ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,83,'7:83','እርሱንና ቤተሰቦቹንም አዳንናቸው ሚስቱ ብቻ ስትቀር፡፡ እርሷ (ለጥፋት) ከቀሩት ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(7,84,'7:84','በእነሱም ላይ (የእሳት) ዝናብን አዘነብንባቸው፡፡ የኃጢአተኞችም መጨረሻ እንዴት እንደነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,85,'7:85','ወደ መድየንም (ምድያን) ወንድማቸውን ሹዓይብን (ላክን)፤ አላቸው፡- «ወገኖቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ በቀር ምንም አምላክ የላችሁም፡፡ ከጌታችሁ ዘንድ ግልጽ ማስረጃ በእርግጥ መጥታላችኋለች፡፡ ስፍርንና ሚዛንን ሙሉ፡፡ ሰዎችንም አንዳቾቻቸውን (ገንዘቦቻቸውን) አታጉድሉባቸው፡፡ በምድርም ውስጥ ከተበጀች በኋላ አታበላሹ፡፡ ይህ ምእምናን እንደኾናችሁ ለእናንተ የተሻለ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,86,'7:86','« (ሰዎችን) የምታስፈራሩና ከአላህም መንገድ በእርሱ ያመኑትን የምታግዱ መጥመሟንም የምትፈልጉዋት ኾናችሁ በየመንገዱ አትቀመጡ፡፡ ጥቂቶችም በነበራችሁና ባበዛችሁ ጊዜ አስታውሱ፡፡ የአጥፊዎችም መጨረሻ እንዴት እንደነበረ ተመልከቱ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,87,'7:87','«ከእናንተም በዚያ እኔ በእርሱ በተላክሁበት ያመኑ ጭፍሮችና ያላመኑ ጭፍሮችም ቢኖሩ በመካከላችን አላህ እስከሚፈርድ ታገሱ፡፡ እርሱም ከፈራጆች ሁሉ በላጭ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,88,'7:88','ከሕዝቦቹ እነዚያ የኮሩት መሪዎች፡- «ሹዓይብ ሆይ! አንተንና እነዚያን ከአንተጋር ያመኑትን ከከተማችን በእርግጥ እናስወጣችኋለን፡፡ ወይም ወደ ሃይማኖታችን መመለስ አለባችሁ አሉ፡፡ የጠላንም ብንኾን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,89,'7:89','«አላህ ከእርሷ ከአዳነን በኋላ ወደ ሃይማኖታችሁ ብንመለስ በአላህ ላይ በእርግጥ ውሸትን ቀጠፍን፡፡ አላህ ጌታችን ካልሻም በስተቀር ለእኛ ወደእርሷ ልንመለስ አይገባንም፡፡ ጌታችን ዕውቀቱ ሁሉን ነገር ሰፋ፡፡ በአላህ ላይ ተጠጋን፡፡ ጌታችን ሆይ! በኛና በወገኖቻችን መካከል በውነት ፍረድ፡፡ አንተም ከፈራጆቹ ሁሉ በላጭ ነህ» (አለ)');
INSERT INTO "sadiq_sani" VALUES(7,90,'7:90','ከወገኖቹም እነዚያ የካዱት መሪዎች «ሹዓይብን ብትከተሉ እናንተ ያን ጊዜ ከሳሪዎች ናችሁ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,91,'7:91','ወዲያውም የምድር መንቀጥቀጥ (ጩኸትም) ያዘቻቸው፡፡ በከተማቸውም ውስጥ ተንከፍርረው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,92,'7:92','እነዚያ ሹዓይብን ያሰተባበሉት በእርሷ እንዳልነበሩባት ኾኑ፡፡ እነዚ ሹዓይብን ያስተባበሉት እነርሱ ከሳሪዎች ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,93,'7:93','ከእነርሱም (ትቷቸው) ዞረ፡- «ወገኖቼ ሆይ! የጌታዬን መልክቶች በእርግጥ አደረስኩላችሁ፡፡ ለእናንተም መከርኩ፡፡ ታዲያ በከሓዲዎች ሰዎች ላይ እንዴት አዝናለሁ» አለም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,94,'7:94','በከተማ አንድንም ነቢይ አልላክንም ሰዎችዋን ይዋደቁ ዘንድ በድህነትና በጉዳት የያዝናቸው ብንኾን እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,95,'7:95','ከዚያም እስከበዙና «አባቶቻችንን ድህነትና በሺታ በእርግጥ ነክቷቸዋል፡፡ (ይኽም የጊዜ ልማድ ነው)» እስካሉ ድረስ በመጥፎው ስፍራ በጎውን ለወጥን፡፡ ወዲያውም እነርሱ የማያውቁ ኾነው በድንገት ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,96,'7:96','የከተማዋም ሰዎች ባመኑና በተጠነቀቁ ኖሮ በእነሱ ላይ ከሰማይም ከምድርም በረከቶችን በከፈትንላቸው ነበር፡፡ ግን አስተባበሉ፡፡ ይሠሩት በነበሩትም ኃጢኣት ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,97,'7:97','የከተማዋ ሰዎችም እነሱ የተኙ ኾነው ቅጣታችን ሌሊት ሊመጣባቸው አይፈሩምን');
INSERT INTO "sadiq_sani" VALUES(7,98,'7:98','የከተማዋ ሰዎችም እነሱ የሚጫወቱ ኾነው ሳሉ ቅጣታችን ቀን በረፋድ ሊመጣባቸው አይፈሩምን');
INSERT INTO "sadiq_sani" VALUES(7,99,'7:99','የአላህንም ማዘናጋት አይፈሩምን የአላህንም ማዘናጋት ከከሳሪዎች ሕዝቦች በስተቀር የሚተማመን የለም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,100,'7:100','ለእነዚያ ምድርን ከባለቤቶችዋ (ጥፋት) በኋላ ለሚወርሱት ብንሻ ኖሮ በኃጢኣቶቻቸው የምንቀጣቸው መኾናችን አልተገለጸላቸውምን በልቦቻቸውም ላይ እናትማለን፡፡ ስለዚህ እነርሱ አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,101,'7:101','እነዚህ ከተሞች (ከኑሕ እስከ ሹዓይብ ሰዎች የተነገሩት) ከወሬዎቻቸው ባንተ ላይ እንተርካለን፡፡ መልክተኞቻቸውም በግልጽ ተዓምራት በእርግጥ መጥተዋቸዋል፡፡ ከመምጣታቸው በፊት ባስተባበሉትም ነገር የሚያምኑ አልኾኑም፡፡ እንደዚሁ አላህ በከሓዲያን ልቦች ላይ ያትማል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,102,'7:102','ለብዙዎቻቸውም በቃል ኪዳናቸው መሙላትን አላገኘንም፡፡ አብዛኞቻቸውንም በእርግጥ አመጸኞች ኾነው አገኘናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,103,'7:103','ከዚያም ከበኋላቸው ሙሳን ወደ ፈርዖንና ወደ መማክርቶቹ በተዓምራታችን ላክነው፡፡ በእርሷም ካዱ፡፡ ያጥፊዎችም መጨረሻ እንዴት እንደነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,104,'7:104','ሙሳም አለ፡- «ፈርዖን ሆይ! እኔ ከዓለማት ጌታ የተላክሁ መልክተኛ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,105,'7:105','«»በአላህ ላይ ከእውነት በቀር አለመናገር ተገቢዬ ነው፡፡ ከጌታችሁ በተዓምር በእርግጥ መጣኋችሁ፡፡ የእስራኤልንም ልጆች ከእኔ ጋር ልቀቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,106,'7:106','(ፈርዖንም «በተዓምር የመጣህ እንደኾንክ ከውነተኞቹ ከኾንክ እርሷን አምጣት» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,107,'7:107','በትሩንም ጣለ፡፡ እርስዋም ወዲያውኑ ግልጽ እባብ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(7,108,'7:108','እጁንም አወጣ፡፡ እርሷም ወዲያውኑ ለተመልካቾች ነጭ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(7,109,'7:109','ከፈርዖን ሰዎች መማክርቶቹ፡- «ይህ በእርግጥ ዐዋቂ ድግምተኛ ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,110,'7:110','«ከምድራችሁ ሊያወጣችሁ ይሻል» (አሉ)፡፡ «ታዲያ ምን ታዛላችሁ» (አለ)');
INSERT INTO "sadiq_sani" VALUES(7,111,'7:111','(እነርሱም) አሉ «እርሱንና ወንድሙን አቆይ፡፡ ወደ ከተሞቹም ሁሉ ሰብሳቢዎችን (ዘበኞች) ላክ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,112,'7:112','«ዐዋቂ ድግምተኛ የኾነን ሁሉ ያመጡልሃልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,113,'7:113','ድግምተኞቹም ወደ ፈርዖን መጡ፡፡ «እኛ አሸናፊዎች ብንኾን ለእኛ በእርግጥ ዋጋ አለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,114,'7:114','«አዎን እናንተም በእርግጥ ከባለሟሎቹ ትኾናላችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,115,'7:115','«ሙሳ ሆይ (በትርህን) ወይም (በፊት) ትጥላለህ ወይም እኛ ጣዮች እንኾናለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,116,'7:116','«ጣሉ» አላቸው፡፡ (ገመዶቻቸውን) በጣሉም ጊዜ የሰዎቹን ዓይኖች ደገሙባቸው፡፡ አስፈራሩዋቸውም፡፡ ትልቅ ድግምትንም አመጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,117,'7:117','ወደ ሙሳም፡- «በትርህን ጣል» ስንል ላክን፡፡ (ጣላትም) ወዲያውኑም የሚቀጣጥፉትን (ማታለያ) ትውጣለች፡፡');
INSERT INTO "sadiq_sani" VALUES(7,118,'7:118','እውነቱም ተገለጸ፡፡ ይሠሩት የነበሩትም (ድግምት) ተበላሸ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,119,'7:119','እዚያ ዘንድ ተሸነፉም፡፡ ወራዶችም ኾነው ተመለሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,120,'7:120','ድግምተኞቹም ሰጋጆች ኾነው ወደቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,121,'7:121','አሉ፡- «በዓለማት ጌታ አመንን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,122,'7:122','«በሙሳና በሃሩን ጌታ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,123,'7:123','ፈርዖን አለ፡- «እኔ ለእናንተ ሳልፈቅድላችሁ በፊት በእርሱ አመናችሁን ይህ በከተማይቱ ውስጥ ሰዎቿን ከእርሷ ለማውጣት በእርግጥ የተስማማችሁበት ተንኮል ነው፡፡ ወደፊትም (የሚደርስባችሁን) ታውቃላችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,124,'7:124','«እጆቻችሁንና እግሮቻችሁን በማፈራረቅ በእርግጥ እቆራርጣለሁ፡፡ ከዚያም ሁላችሁንም በእርግጥ እሰቅላችኋለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,125,'7:125','አሉ፡- «እኛ ወደ ጌታችን ተመላሾች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,126,'7:126','«የጌታችንም ተዓምራቶች በመጡልን ጊዜ ከማመናችን በቀር ከእኛ አትጠላም፡፡ ጌታችን ሆይ! በእኛ ላይ ትዕግስትን አፍስስ፡፡ ሙስሊሞች ኾነን ግደለን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,127,'7:127','ከፈርዖንም ሰዎች ታላላቆቹ (ለፈርዖን) «ሙሳንና ሰዎቹን በምድር ላይ እንዲያበላሹና (ሙሳም) አንተንና አማልክቶችህን እንዲተው ትተዋቸዋለህን» አሉ፡፡ «ወንዶች ልጆቻቸውን እንገድላለን፡፡ ሴቶቻቸውንም እናስቀራለን፡፡ እኛም ከበላያቸው ነን፡፡ አሸናፊዎች (ነን)» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,128,'7:128','ሙሳ ለሰዎቹ፡- «ለአላህ ተገዙ ታገሱም፡፡ ምድር ለአላህ ናትና፡፡ ከባሮቹ ለሚሻው ያወርሳታል፡፡ ምሰጉንዋም ፍጻሜ ለጥንቁቆቹ ናት» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,129,'7:129','«ከመምጣትህም በፊት ከመጣኽልንም በኋላ ተሰቃየን» አሉት፡፡ «ጌታችሁ ጠላቶቻችሁን ሊያጠፋ እንዴት እንደምትሠሩም ይመለከት ዘንድ በምድር ላይ ሊተካችሁ ይሻል» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,130,'7:130','የፈርዖንንም ቤተሰቦች እንዲገሰጹ በድርቅ ዓመታትና ከፍሬዎችም በመቀነስ በእርግጥ ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,131,'7:131','ደጊቱም ነገር (ምቾት) በመጣችላቸው ጊዜ «ይህች ለእኛ (ተገቢ) ናት» ይላሉ፡፡ ክፉትም ብታገኛቸው በሙሳና አብረውት ባሉት ገደቢስነት ያመካኛሉ፡፡ ንቁ! ገደቢስነታቸው አላህ ዘንድ ብቻ ነው፡፡ ግን አብዛኛዎቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,132,'7:132','(ለሙሳም) «በማንኛይቱም ተዓምር በእርሷ ልትደግምብን ብትመጣብን እኛ ለአንተ የምናምንልህ አይደለንም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,133,'7:133','ወዲያም የወሃን ማጥለቅለቅ አንበጣንም ነቀዝንም እንቁራሪቶችንም ደምንም የተለያዩ ተዓምራት ሲኾኑ በነሱ ላይ ላክን ኮሩም ተንኮለኞችም ሕዝቦች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,134,'7:134','በእነርሱም ላይ ቅጣቱ በወደቀባቸው ጊዜ «ሙሳ ሆይ! ጌታህን ላንተ ቃል ኪዳን በገባልህ ነገር ለእኛ ለምንልን፡፡ ከእኛ ቅጣቱን ብታነሳልን ለአንተ በእርግጥ እናምንልሃለን፡፡ የእስራኤልንም ልጆች ከአንተ ጋር በእርግጥ እንለቃለን፤» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,135,'7:135','እነርሱም ደራሾቹ እኾኑበት ጊዜ ድረስ ቅጣትን ከእነሱ ላይ ባነሳን ጊዜ ወዲያውኑ እነርሱ ቃላቸውን ያፈርሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,136,'7:136','እነርሱ በተዓምራታችን ስለአስተባበሉም ከእርሷ ዘንጊዎች ስለኾኑም፤ ከእነሱ ተበቀልን፡፡ በባሕርም ውስጥ አሰጠምናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,137,'7:137','እነዚያንም ደካሞች የነበሩትን ሕዝቦች ያችን በውስጧ በረከት ያደረግንባትን ምድር ምሥራቆችዋንም ምዕራቦቿንም አወረስናቸው፡፡ የጌታህም መልካሚቱ ቃል በእስራኤል ልጆች ላይ በመታገሳቸው ተፈጸመች፡፡ ፈርዖንና ሰዎቹም ይሠሩት የነበረውን (ሕንጻ) ዳስ ያደርጉትም የነበረውን አፈረስን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,138,'7:138','የእስራኤልንም ልጆች ባሕሩን አሻገርናቸው፡፡ ለእነርሱ በኾኑ ጣዖታት (መገዛት) ላይ በሚዘወትሩ ሕዝቦች ላይ አለፉም፡፡ «ሙሳ ሆይ፡- ለእነርሱ (ለሰዎቹ) አማልክት እንዷላቸው ለእኛም አምላክን አድርግልን» አሉት፡፡ «እናንተ የምትሳሳቱ ሕዝቦች ናችሁ፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,139,'7:139','«እነዚህ እነርሱ በውስጡ ያሉበት ነገር ጠፊ ነው፡፡ ይሰሩት የነበሩትም ብልሹ ነው» (አላቸው)፤');
INSERT INTO "sadiq_sani" VALUES(7,140,'7:140','«ከአላህ ሌላ እርሱ ከዓለማት ያበለጣችሁ ሲኾን (የምትገዙት) አምላክን እፈልግላችኋለሁን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,141,'7:141','ከፈርዖንም ቤተሰቦች ብርቱን ቅጣት የሚያቀምሱዋችሁ ወንዶች ልጆቻችሁን የሚገድሉ ሴቶቻችሁንም የሚተዉ ሲኾኑ ባዳናችሁ ጊዜ (አስታውሱ)፡፡ በዚህም ውስጥ ከጌታችሁ ዘንድ የኾነ ከባድ ፈተና አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(7,142,'7:142','ሙሳንም ሰላሳን ሌሊት (ሊጾምና ልናነጋግረው) ቀጠርነው፡፡ በዐስርም (ሌሊት) ሞላናት፡፡ የጌታውም ቀጠሮ አርባ ሌሊት ሲኾን ተፈጸመ፡፡ ሙሳም ለወንድሙ ሃሩን «በሕዝቦቼ ላይ ተተካኝ፡፡ አሳምርም የአጥፊዎችንም መንገድ አትከተል» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,143,'7:143','ሙሳም ለቀጠሯችን በመጣና ጌታውም ባነጋገረው ጊዜ፡- «ጌታዬ ሆይ! (ነፍስህን) አሳየኝ ወደ አንተ እመለከታለሁና» አለ፡፡ (አላህም)፡- «በፍጹም አታየኝም ግን ወደ ተራራው ተመልከት፡፡ በስፍራውም ቢረጋ በእርግጥ ታየኛለህ» አለው፡፡ ጌታው ለተራራው በተገለጸ ጊዜ እንኩትኩት አደረገው፡፡ ሙሳም ጮሆ ወደቀ፡፡ በአንሰራራም ጊዜ «ጥራት ይገባህ፡፡ ወዳንተ ተመለስኩ፡፡ እኔም (በወቅቱ) የምእምናን መጀመሪያ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,144,'7:144','(አላህም) አለው፡- «ሙሳ ሆይ! እኔ በመልክቶቼና በማናገሬ በሰዎች ላይ መረጥኩህ፡፡ የሰጡህንም ያዝ፡፡ ከአመስጋኞቹም ኹን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,145,'7:145','ለእርሱም በሰሌዳዎቹ ላይ ከነገሩ ሁሉ ግሳጼንና ለነገሩም ሁሉ ማብራራትን ጻፍንለት፡፡ (አልንም) በብርታትም ያዛት፡፡ ሕዝቦችህንም በመልካሟ እንዲይዙ እዘዛቸው፡፡ የአመጸኞቹን አገር በእርግጥ አሳያችኋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,146,'7:146','እነዚያን ያለአግባብ በምድር ላይ የሚኮሩትን ከአንቀጾቼ በእርግጥ አዞራቸዋለሁ፡፡ ተዓምርንም ሁሉ ቢያዩ በርሷ አያምኑም፡፡ ቅንንም መንገድ ቢያዩ መንገድ አድርገው አይዙትም፡፡ ግን የጥመትን መንገድ ቢያዩ መንገድ አድርገው ይይዙታል፡፡ ይህ እነርሱ በአንቀጾቻችን ስለአስተባበሉና ከእርሷም ዘንጊዎች ስለኾኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,147,'7:147','እነዚያም በአንቀጾቻችንና መጨረሻይቱን ዓለም በመገናኘት ያስተባበሉት ሥራዎቻቸው ተበላሹ፡፡ ይሠሩት የነበረውን እንጂ ይመነዳሉን');
INSERT INTO "sadiq_sani" VALUES(7,148,'7:148','የሙሳም ሕዝቦች ከእርሱ (መኼድ) በኋላ ከጌጦቻቸው ወይፈንን አካልን ለርሱ ማግሳት ያለውን (አምላክ አድርገው) ያዙ፡፡ እርሱ የማያናግራቸው መንገድንም የማይመራቸው መኾኑን አይመለከቱምን (አምላክ አድርገው) ያዙት፡፡ በዳዮችም ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,149,'7:149','በተጸጸቱና እነሱም በእርግጥ የተሳሳቱ መኾናቸውን ባዩ ጊዜ፡- «ጌታችን ባያዝንልንና ባይምረን በእርግጥ ከከሳሪዎቹ እንኾናለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,150,'7:150','ሙሳም እየተቆጣና እያዘነ ወደ ሕዝቦቹ በተመለሰ ጊዜ «ከእኔ በኋላ በእኔ ላይ የተካችሁት ነገር ከፋ! የጌታችሁን ትእዛዝ ተቻኮላችሁን» አላቸው፡፡ ሰሌዳዎቹንም ጣላቸው፡፡ የወንድሙንም ራስ (ጸጉር) ወደርሱ የሚጎትተው ሲኾን ያዘ፡፡ (ወንድሙም)፡- «የናቴ ልጅ ሆይ! ሕዝቦቹ ናቁኝ፡፡ ሊገድሉኝም ተቃራቡ፡፡ ስለዚህ በእኔ ጠላቶችን አታስደስትብኝ፡፡ ከአመጸኞች ሕዝቦችም ጋር አታድርገኝ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,151,'7:151','(ሙሳም)፡- «ጌታዬ ሆይ! ለእኔም ለወንድሜም ማር፡፡ በእዝነትህም ውስጥ አግባን፡፡ አንተም ከአዛኞች ሁሉ ይበልጥ አዛኝ ነህ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,152,'7:152','እነዚያ ወይፈኑን (አምላክ አድርገው) የያዙ ከጌታቸው ዘንድ ቁጣ፤ በቅርቢቱም ሕይወት ውርደት በእርግጥ ያገኛቸዋል፡፡ እንዲሁም ቀጣፊዎችን እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,153,'7:153','እነዚያም ኃጢአቶችን የሠሩ ከዚያም ከእርሷ በኋላ የተጸጸቱ ያመኑም ጌታህ ከእርሷ በኋላ በእርግጥ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,154,'7:154','ከሙሳም ቁጣው በበረደ ጊዜ ሰሌዳዎቹን በግልባጫቸው ውስጥ ለእነዚያ እነርሱ ጌታቸውን የሚፈሩ ለኾኑት መምሪያና እዝነት ያለባቸው ሲኾኑ ያዘ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,155,'7:155','ሙሳም ከሕዝቦቹ ውስጥ ለቀጠሮዋችን ሰባን ሰዎች መረጠ፡፡ ብርቱ የምድር እንቅጥቃጤ በያዘቻቸውም ጊዜ ሙሳ አለ «ጌታዬ ሆይ! በሻህ ኖሮ ከአሁን በፊት በአጠፋሃቸው ነበር፡፡ እኔንም (ባጠፋኸኝ ነበር)፡፡ ከእኛ ቂሎቹ በሠሩት ነገር ታጠፋናለህን እርሷ (ፈተናይቱ) ያንተ ፈተና እንጂ ሌላ አይደለችም፡፡ በእርሷ የምትሻውን ታሳስታለህ፡፡ የምትሻውንም ታቀናለህ፡፡ አንተ ረዳታችን ነህና ለእኛ ምሕረት አድርግልን፡፡ እዘንልንም፡፡ አንተም ከመሓሪዎች ሁሉ በላጭ ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,156,'7:156','«ለእኛም በዚች በቅርቢቱ ዓለም መልካሟን በመጨረሻይቱም (መልካሟን) ጻፍልን፡፡ እኛ ወደ አንተ ተመለስን፡፡» (ሲል ሙሳ ጸለየ፤ አላህም) አለ፡- «ቅጣቴ በእርሱ የምሻውን ሰው እቀጣበታለሁ፡፡ ችሮታየም ነገሩን ሁሉ ሰፋች፡፡ ለእነዚያም ለሚጠነቀቁ፣ ዘካንም ለሚሰጡ፣ ለእነዚያም እነሱ በአንቀጾቻችን ለሚያምኑ በእርግጥ» እጽፋታለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,157,'7:157','ለእነዚያ ያንን እነሱ ዘንድ በተውራትና በኢንጂል ተጽፎ የሚያገኙትን የማይጽፍና የማያነብ ነቢይ የኾነውን መልክተኛ የሚከተሉ ለኾኑት (በእርግጥ እጽፍለታለሁ)፡፡ በበጎ ሥራ ያዛቸዋል፡፡ ከክፉም ነገር ይከለክላቸዋል፡፡ መልካም ነገሮችንም ለነርሱ ይፈቅድላቸዋል፡፡ መጥፎ ነገሮችንም በእነርሱ ላይ እርም ያደርግባቸዋል፡፡ ከእነሱም ላይ ሸክማቸውንና እነዚያንም በእነርሱ ላይ የነበሩትን እንዛዝላዎች (ከባድ ሕግጋቶች) ያነሳላቸዋል፡፡ እነዚያም በእርሱ ያመኑ ያከበሩትም የረዱትም ያንንም ከእርሱ ጋር የተወረደውን ብርሃን የተከተሉ እነዚያ እነሱ የሚያድኑ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,158,'7:158','(ሙሐመድ ሆይ!) በላቸው፡- «እናንተ ሰዎች ሆይ! እኔ ወደናንተ ወደሁላችሁም የአላህ መልክተኛ ነኝ፡፡ (እርሱም) ያ የሰማያትና የምድር ንግሥና ለእርሱ ብቻ የኾነ ነው፡፡ እርሱ እንጂ ሌላ አምላክ የለም፡፡ ሕያው ያደርጋል፡፡ ይገድላልም፡፡ በአላህ በዚያም በአላህና በቃላቶቹ በሚያምነው የማይጽፍ የማያነብ ነቢይ በኾነው መልክተኛው እመኑ፡፡ ቅንንም መንገድ ትመሩ ዘንድ ተከተሉት፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,159,'7:159','ከሙሳም ሕዝቦች በእውነት የሚመሩት በእርሱም (ፍርድን) የሚያስተካክሉ ጭፍሮች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,160,'7:160','ዐሥራ ሁለት ነገዶች ሕዝቦች አድርገንም ከፋፈልናቸው፡፡ ወደ ሙሳም ወገኖቹ መጠጣትን በፈለጉበት ጊዜ «ድንጋዩን በበትርህ ምታው» ስንል ላክን፡፡ (መታውም) ከእርሱ ዐሥራ ሁለት ምንጮችም ፈለቁ፡፡ ሰዎቹ ሁሉ መጠጫቸውን በእርግጥ ዐወቁ፡፡ በእነሱም ላይ ደመናን አጠለልን፡፡ በእነሱም ላይ መናንና ድርጭትን አወረድን፡፡ «ከሰጠናችሁም መልካም ሲሳይ ብሉ» (አልን፤ ጸጋችንን በመካዳቸው) አልበደሉንምም፡፡ ግን ነፍሶቻቸውን የሚበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,161,'7:161','ለእነርሱም በተባሉ ጊዜ (አስታውስ) «በዚህች ከተማ ተቀመጡ፡፡ ከእርሷም በሻችሁት ስፍራ ብሉ፡፡ (የምንፈልገው) የኃጢኣታችንን መርገፍ ነው በሉም፡፡ የከተማይቱንም በር አጎንብሳችሁ ግቡ፡፡ ኃጢኣቶቻችሁን ለእናንተ እንምራለንና፡፡ በጎ ለሠሩት በእርግጥ እንጨምራለን፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,162,'7:162','ከእነሱም ውስጥ እነዚያ (ራሳቸውን) የበደሉት ሰዎች ከዚያ ለእነርሱ ከተባለው ሌላ የኾነን ቃል ለወጡ፡፡ በእነርሱም ላይ ይበድሉ በነበሩት በደል መዐትን ከሰማይ ላክንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,163,'7:163','ከዚያችም በባህሩ ዳርቻ ከነበረችው ከተማ በቅዳሜ (ቀን) ወሰን ባለፉ ጊዜ በሰንበታቸው ቀን ዐሳዎቻቸው የተከማቹ ኾነው በሚመጡላቸው ጊዜ (የኾነውን) ጠይቃቸው፡፡ ሰንበትንም በማያከብሩበት (በሌላው) ቀን አይመጡላቸውም፡፡ እንደዚሁ ያምጹበት በነበሩት ምክንያት እንሞክራቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,164,'7:164','ከእነሱም (ከፊሎቹ) ሕዝቦች «አላህ አጥፊያቸው ወይም በብርቱ ቅጣት ቀጪአቸው የኾኑትን ሕዝቦች ለምን ትገስፃጻላችሁ» ባሉ ጊዜ (ገሳጮቹ) «ወደ ጌታችሁ በቂ ምክንያት እንዲኾንና እነርሱም ቢከለከሉ ብለን ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,165,'7:165','በእርሱም የተገሰጹበትን ነገር በተዉ ጊዜ እነዚያን ከክፉ የሚከለከሉትን አዳንን፤ እነዚያንም የበደሉትን ያምጹ በነበሩት ምክንያት በብርቱ ቅጣት ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,166,'7:166','ከእርሱም ከተከለከሉት ነገር በኮሩ ጊዜ ለእነሱ «ወረዶች ዝንጀሮች ኹኑ አልን፤» (ኾኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,167,'7:167','ጌታህም እስከ ትንሣኤ ቀን ክፉ ቅጣትን የሚያቀምሳቸውን ሰው በነሱ ላይ በእርግጥ የሚልክ መኾኑን ባስታወቀ ጊዜ (አስታውሳቸው)፡፡ ጌታህ በእርግጥ ቅጣተ ፈጣን ነው፡፡ እርሱም በእርግጥ እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,168,'7:168','(የእስራኤልን ልጆች) በምድር ላይ የተለያዩ ሕዝቦች አድርገንም ከፋፈልናቸው፡፡ ከእነሱ መልካሞች አሉ፡፡ ከእነሱም ከዚያ ሌላ አልሉ፡፡ ይመለሱ ዘንድም በተድላዎችም በመከራዎችም ሞከርናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,169,'7:169','ከኋላቸውም፡- መጽሐፉን የወረሱ መጥፎ ምትኮች ተተኩ፡፡ የዚህን የቅርቡን ጠፊ ጥቅም ይይዛሉ፡፡ ብጤውም ጥቅም ቢመጣላቸው የሚይዙት ሲኾኑ « (በሠራነው)፡- ለኛ ምሕረት ይደረግልናል» ይላሉ፡፡ በአላህ ላይ ከእውነት በቀር እንዳይናገሩ በእነርሱ ላይ የመጽሐፉ ቃል ኪዳን አልተያዘባቸውምን በእርሱ ውስጥ ያለውንም አላነበቡምን የመጨረሻይቱም አገር ለእነዚያ ጌታቸውን ለሚፈሩት በላጭ ናት፡፡ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(7,170,'7:170','እነዚያም መጽሐፉን ጠብቀው የሚይዙ ሶላትንም በደንቡ የሰገዱ እኛ የመልካም ሠሪዎችን ዋጋ አናጠፋም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,171,'7:171','የጡርን ተራራ ነቅለን ከበላያቸው እንደ ጥላ ኾኖ በአነሳነውና እርሱም በነሱ ላይ ወዳቂ መኾኑን ባረጋገጡ ጊዜ (አስታውስ)፡፡ «የሰጠናችሁን በብርታት ያዙ፡፡ ትጠነቀቁም ዘንድ በውስጡ ያለውን ተገንዘቡ» (አልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,172,'7:172','ጌታህም ከአደም ልጆች ከጀርቦቻቸው ዘሮቻቸውን ባወጣና «ጌታችሁ አይደለሁምን» ሲል በነፍሶቻቸው ላይ ባስመሰከራቸው ጊዜ (የኾነውን አስታውስ)፡፡ «ጌታችን ነህ መሰከርን» አሉ፡፡ «በትንሣኤ ቀን ከዚህ (ኪዳን) ዘንጊዎች ነበርን እንዳትሉ፡፡»');
INSERT INTO "sadiq_sani" VALUES(7,173,'7:173','ወይም «(ጣዖታትን) ያጋሩት ከእኛ በፊት የነበሩት አባቶቻችን ብቻ ናቸው፡፡ እኛም ከእነሱ በኋላ የኾን ዘሮች ነበርን፡፡ አጥፊዎቹ በሠሩት ታጠፋናለህን» እንዳትሉ (አስመሰከርናችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,174,'7:174','እንደዚሁም (እንዲያስቡ) እንዲመለሱም አንቀጾችን እናብራራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,175,'7:175','የዚያንም ተዓምራታችንን የሰጠነውንና ከእርሷ የወጣውን ሰይጣንም ያስከተለውን ከጠማሞቹም የኾነውን ሰው ወሬ በእነርሱ ላይ አንብብላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,176,'7:176','በሻንም ኖሮ በእርሷ ከፍ ባደረግነው ነበር፡፡ እርሱ ግን ወደ ምድር ተዘነበለ፡፡ ፍላጎቱንም ተከተለ፡፡ ብጤውም ብታባርረው ምላሱን አውጥቶ የሚያለከልክ ወይም ብትተወው ምላሱን አውጥቶ የሚያለከልክ እንደ ኾነ ውሻ ነው፡፡ ይህ የእነዚያ በአንቀጾቻችን ያስተባበሉት ሕዝቦች ምሳሌ ነው፡፡ ያስተነትኑም ዘንድ ታሪኮችን ተርክላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,177,'7:177','የእነዚያ በአንቀጾቻችን ያስተባበሉትና ነፍሶቻቸውን ይበድሉ የነበሩት ሰዎች ምሳሌ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(7,178,'7:178','አላህ የሚያቀናው ሰው ቅን እርሱ ነው፡፡ የሚያጠመውም ሰው እነዚያ ከሳሪዎቹ እነሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,179,'7:179','ከጋኔንም ከሰዎችም ብዙዎችን ለገሀነም በእርግጥ ፈጠርን፡፡ ለእነርሱ በርሳቸው የማያውቁባቸው ልቦች አሏቸው፡፡ ለእነሱም በሳቸው የማያዩባቸው ዓይኖች አሉዋቸው፡፡ ለእነሱም በሳቸው የማይሰሙባቸው ጆሮዎች አሉዋቸው፡፡ እነዚያ እንደ እንስሳዎች ናቸው፡፡ ይልቁንም እነርሱ በጣም የተሳሳቱ ናቸው፡፡ እነዚያ ዘንጊዎቹ እነርሱ ናቸው፤');
INSERT INTO "sadiq_sani" VALUES(7,180,'7:180','ለአላህም መልካም ስሞች አሉት፡፡ (ስትጸልዩ) በእርሷም ጥሩት፡፡ እነዚያንም ስሞቹን የሚያጣምሙትን ተውዋቸው፡፡ ይሠሩት የነበሩትን ነገር በእርግጥ ይምመነዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,181,'7:181','ከፈጠርናቸውም ሰዎች በእውነት የሚመሩ በእርሱም (ፍርድን) የሚያስተካክሉ ሕዝቦች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,182,'7:182','እነዚያንም በአንቀጾቻችን ያስተባበሉትን ከማያውቁት ስፍራ ቀስ በቀስ (ምቾትን በመጨመር) እናዘነጋቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,183,'7:183','ለእነርሱም ጊዜ እሰጣቸዋለሁ ጥበቤ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,184,'7:184','በነቢያቸው (በሙሐመድ) ምንም ዕብደት የሌለበት መኾኑን አያስተውሉምን እርሱ ግልጽ አስጠንቃቂ እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,185,'7:185','በሰማያትና በምድር ግዛት ውስጥ ከማንኛው ነገር አላህ በፈጠረውም ሁሉ የሞት ጊዜያቸውም በእርግጥ መቅረቡ የሚፈራ መኾኑን አያስተውሉምን ከእርሱም (ከቁርኣን) ወዲያ በየትኛው ንግግር ያምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,186,'7:186','አላህ የሚያጠመው ሰው ለእርሱ ምንም አቅኝ የለውም፡፡ በጥምመታቸውም ውስጥ እየዋለሉ ይተዋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(7,187,'7:187','ከሰዓቲቱ መቼ እንደምትረጋ (እንደምትመጣ) ይጠይቁሃል፡፡ «ዕውቀቷ በጌታዬ ዘንድ ነው፡፡ በጊዜዋ እርሱ እንጂ ሌላ አይገልጣትም፡፡ በሰማያትና በምድርም ከበደች፡፡ በድንገት ቢኾን እንጂ አትመጣችሁም» በላቸው፡፡ ከእርሷ አጥብቀህ እንደ ተረዳህ አድርገው ይጠይቁሃል፡፡ «ዕውቀቷ አላህ ዘንድ ብቻ ነው፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,188,'7:188','«አላህ የሻውን በስተቀር ለእራሴ ጥቅምንም ጉዳትንም ማምጣት አልችልም፡፡ ሩቅንም (ምስጢር) የማውቅ በነበርኩ ኖሮ ከመልካም ነገር ባበዛሁና ክፉም ነገር ባልነካኝ ነበር፡፡ እኔ ለሚያምኑ ሕዝቦች አስፈራሪና አብሳሪ እንጂ ሌላ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,189,'7:189','እርሱ ያ ከአንዲት ነፍስ (ከአደም) የፈጠራችሁ ከእርሷም መቀናጆዋን ወደርሷ ይረካ ዘንድ ያደረገ ነው፡፡ በተገናኛትም ጊዜ ቀላልን እርግዝና አረገዘች፡፡ እርሱንም (ፅንሱን) ይዛው ኼደች፡፡ በከበደችም ጊዜ «ደግን ልጅ ብትሰጠን በእርግጥ ከአመስጋኞቹ እንኾናለን፤» ሲሉ ጌታቸውን አላህን ለመኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,190,'7:190','መልካም ልጅ በሰጣቸውም ጊዜ በሰጣቸው ልጅ (ስም) ለእርሱ ተጋሪዎችን አደረጉለት፡፡ አላህም ከሚያጋሩት ሁሉ ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,191,'7:191','ምንንም የማይፈጥሩትንና እነርሱም የሚፈጠሩትን (በአላህ) ያጋራሉን');
INSERT INTO "sadiq_sani" VALUES(7,192,'7:192','ለእነርሱም መርዳትን የማይችሉትን ነፍሶቻቸውንም የማይረዱትን (ያጋራሉን)');
INSERT INTO "sadiq_sani" VALUES(7,193,'7:193','(ጣዖቶቹን) ወደ ቅን መንገድ ብትጠሩዋቸውም አይከተሉዋችሁም፡፡ ብትጠሩዋቸው ወይም እናንተ ዝምተኞችም ብትኾኑ በእናንተ ላይ እኩል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,194,'7:194','እነዚያ ከአላህ ሌላ የምትገ3ቸው ብጤዎቻችሁ ተገዢዎች ናቸው፡፡ እውነተኞችም እንደኾናችሁ ጥሩዋቸውና ለእናንተ ይመልሱላችሁ፤ (መመለስ ግን አይችሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,195,'7:195','ለእነርሱ በእርሳቸው የሚኼዱባቸው እግሮች አሉዋቸውን ወይስ ለእነሱ በርሳቸው የሚጨብጡባቸው እጆች አሉዋቸውን ወይስ ለእነሱ በእርሳቸው የሚያዩባቸው ዓይኖች አሉዋቸውን ወይስ ለእነሱ በእርሳቸው የሚሰሙባቸው ጆሮዎች አሉዋቸውን «ያጋራችኋቸውን ጥሩ ከዚያም ተተናኮሉኝ፡፡ ጊዜም አትስጡኝ፤ (አልፈራችሁም)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,196,'7:196','«የእኔ ረዳቴ ያ መጽሐፉን (ቁርኣንን) ያወረደልኝ አላህ ነውና፤ እርሱም መልካም ሠሪዎችን ይረዳል» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(7,197,'7:197','እነዚያም ከእርሱ በቀር የምትገ3ቸው ሊረÇችሁ አይችሉም፡፡ ነፍሶቻቸውንም አይረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,198,'7:198','«ወደ ቅንም መንገድ (እንዲመሩዋችሁ) ብትጠሩዋቸው አይሰሙም፤» (በላቸው)፡፡ እነርሱንም የማያዩ ሲኾኑ ወደ አንተ ሲመለከቱ ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,199,'7:199','ገርን ጠባይ ያዝ፡፡ በመልካምም እዘዝ፡፡ ባለጌዎቹንም ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,200,'7:200','ከሰይጣንም (በኩል) ጉትጎታ ቢያገኝህ በአላህ ተጠበቅ፡፡ እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,201,'7:201','እነዚያ የተጠነቀቁት ከሰይጣን የኾነ ዘዋሪ በነካቸው ጊዜ (ጌታቸውን) ይገነዘባሉ፡፡ ወዲያውም እነሱ ተመልካቾች ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(7,202,'7:202','ወንድሞቻቸውም ጥመትን ይጨምሩላቸዋል፤ ከዚያም (እነርሱ) አይገቱም፡፡');
INSERT INTO "sadiq_sani" VALUES(7,203,'7:203','በተዓምርም ባልመጣሃቸው ጊዜ (በራስህ) «ለምን አትፈጥራትም» ይላሉ፡፡ ከጌታዬ ወደኔ የተወረደውን ብቻ እከተላለሁ፡፡ ይህ (ቁርኣን) ከጌታችሁ ሲኾን ለሚያምኑ ሕዝቦች መረጃዎችና መምሪያ እዝነትም ነው በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(7,204,'7:204','ቁርኣንም በተነበበ ጊዜ እርሱን አዳምጡ፡፡ ጸጥም በሉ፡፡ ይታዘንላችኋልና፡፡');
INSERT INTO "sadiq_sani" VALUES(7,205,'7:205','ጌታህንም በምስጢር ተዋድቀህና ፈርተህ ከጩኸት በታችም በኾነ ቃል በጧትም በማታም አውሳው፡፡ ከዘንጊዎቹም አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(7,206,'7:206','እነዚያ እጌታህ ዘንድ ያሉት (መላእክት) እርሱን ከመገዛት አይኮሩም፡፡ ያወድሱታልም፡፡ ለእርሱም ብቻ ይሰግዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,1,'8:1','ከጦር ዘረፋ ገንዘቦች ይጠይቁሃል፡፡ «የዘረፋ ገንዘቦች የአላህና የመልክተኛው ናቸው፡፡» ስለዚህ አላህን ፍሩ፡፡ በመካከላችሁ ያለችውንም ኹኔታ አሳምሩ፡፡ አማኞችም እንደኾናችሁ አላህንና መልክተኛውን ታዘዙ በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,2,'8:2','ፍጹም ምእምናን እነዚያ አላህ በተወሳ ጊዜ ልቦቻቸው የሚፈሩት፣ በነሱም ላይ አንቀጾቻችን በተነበቡ ጊዜ እምነትን የሚጨምሩላቸው፣ በጌታቸውም ላይ ብቻ የሚመኩት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,3,'8:3','እነዚያ ሶላትን ደንቡን አሟልተው የሚሰግዱ ከሰጠናቸውም ሲሳይ የሚለግሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,4,'8:4','እነዚያ በእውነት አማኞች እነሱ ብቻ ናቸው፡፡ ለእነሱ በጌታቸው ዘንድ ደረጃዎች ምህረትና የከበረ ሲሳይም አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,5,'8:5','(ይህ በዘረፋ ክፍያ የከፊሉ ሰው መጥላት) ከምእምናን ከፊሉ የጠሉ ሲኾኑ ጌታህ ከቤትህ በእውነት ላይ ኾነህ እንዳወጣህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,6,'8:6','እነርሱ እያዩ ወደሞት እንደሚነዱ ኾነው በእውነቱ ነገር (በመጋደል ግዴታነት) ከተገለጸላቸው በኋላ ይከራከሩሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(8,7,'8:7','አላህም ከሁለቱ ጭፍሮች አንደኛዋን እርሷ ለናንተ ናት ሲል ተስፋ በሰጣችሁ ጊዜ፣ የሀይል ባለቤት ያልኾነችውም (ነጋዴይቱ) ለናንተ ልትኾን በወደዳችሁ ጊዜ፣ አላህም በተስፋ ቃላቱ እውነትን ማረጋገጡን ሊገልጽና የከሓዲዎችንም መጨረሻ ሊቆርጥ በሻ ጊዜ (የኾነውን አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,8,'8:8','(ይህንንም ያደረገው) አጋሪዎቹ ቢጠሉም እውነቱን ሊያረጋግጥ ክህደትንም ሊያጠፋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,9,'8:9','ከጌታችሁ ርዳታን በፈለጋችሁ ጊዜ «እኔ በሺህ መላእክት ተከታታዮች ሲኾኑ እረዳችኋለሁ» ሲል ለናንተ የተቀበላችሁን (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,10,'8:10','አላህም (ይኽንን ርዳታ) ለብስራትና ልቦቻችሁ በርሱ ሊረኩበት እንጂ ለሌላ አላደረገውም፡፡ ድልም መንሳት ከአላህ ዘንድ እንጂ ከሌላ አይደለም፡፡ አላህ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,11,'8:11','ከእርሱ በኾነው ጸጥታ (በጦር ግንባር) በእንቅልፍ በሸፈናችሁና ውሃውንም በእርሱ ሊያጠራችሁ፣ የሰይጣንንም ጉትጎታ ከእናንተ ሊያስወግድላችሁ፣ ልቦቻችሁንም (በትዕግስት) ሊያጠነክርላችሁ፣ በእርሱም ጫማዎችን (በአሸዋው ላይ) ሊያደላድልላችሁ በእናንተ ላይ ከሰማይ ባወረደላችሁ ጊዜ (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,12,'8:12','ጌታህ ወደ መላእክቱ «»እኔ (በእርዳታዬ) ከእናንተ ጋር ነኝና እነዚያን ያመኑትን አጽናኑ፡፡ በእነዚያ በካዱት ልቦች ውስጥ ፍርሃትን በእርግጥ እጥላለሁ፡፡ ከአንገቶችም በላይ (ራሶችን) ምቱ፡፡ ከእነሱም የቅርንጫፎችን መለያልይ ሁሉ ምቱ፡፡ ሲል ያወረደውን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,13,'8:13','ይህ እነርሱ አላህንና መልክተኛውን ስለተቃወሙ ነው፡፡ አላህንና መልክተኛውንም የሚቃወም ሁሉ አላህ ቅጣቱ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,14,'8:14','ይህ (ቅጣታችሁ ነው) ቅመሱትም፡፡ ለከሓዲዎችም የእሳት ቅጣት በእርግጥ አለባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,15,'8:15','እናንተ ያመናችሁ ሆይ! እነዚያን የካዱትን ሰዎች (ለጦር) ሲጓዙ ባገኛችኋቸው ጊዜ ጀርባዎችን አታዙሩላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,16,'8:16','ያን ጊዜም ለግድያ ለመዘዋወር ወይም ወደ ሠራዊት ለመቀላቀል ሳይኾን ጀርባውን የሚያዞርላቸው ሰው ከአላህ በኾነ ቁጣ በእርግጥ ተመለሰ፡፡ መኖሪያውም ገሀነም ናት፡፡ መመለሻይቱም ከፋች፡፡');
INSERT INTO "sadiq_sani" VALUES(8,17,'8:17','አልገደላችኋቸውም ግን አላህ ገደላቸው፡፡ (ጭብጥን ዐፈር) በወረወርክም ጊዜ አንተ አልወረወርክም፡፡ ግን አላህ ወረወረ (ወደ ዓይኖቻቸው አደረሰው)፡፡ ለአማኞችም ከርሱ የኾነን መልካም ጸጋ ለመስጠት (ይህን አደረገ)፡፡ አላህ ሰሚ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,18,'8:18','ይህ (ዕውነት ነው)፡፡ አላህም የከሓዲዎችን ተንኮል አድካሚ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,19,'8:19','ፍትሕን (ፍርድን) ብትጠይቁ ፍትሑ በእርግጥ መጥቶላችኋል፡፡ (ክህደትንና መዋጋትን) ብትከለከሉም እርሱ ለእናንተ የተሻለ ነው፡፡ (ወደ መጋደል) ብትመለሱም እንመለሳለን፡፡ ሰራዊታችሁም ብትበዛም እንኳ ከናንተ ምንም አትጠቅማችሁም፡፡ አላህም ከምእምናን ጋር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,20,'8:20','እናንተ ያመናችሁ ሆይ! አላህንና መልክተኛውን ታዘዙ፡፡ እናንተም የምትሰሙ ስትኾኑ ከርሱ አትሽሹ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,21,'8:21','እንደነዚያም እነሱ የማይሰሙ ሲኾኑ ሰማን እንዳሉት አትሁኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,22,'8:22','ከተንቀሳቃሾች ሁሉ አላህ ዘንድ መጥፎ ተንኮለኞች እነዚያ የማያውቁት፣ ደንቆሮዎቹ፣ ዲዳዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,23,'8:23','በውስጣቸውም ደግ መኖሩን አላህ ባወቀ ኖሮ (የመረዳትን መስማት) ባሰማቸው ነበር፡፡ (ደግ የሌለባቸው መኾኑን ሲያውቅ) ባሰማቸውም ኖሮ እነሱ እውነትን የተው ኾነው በሸሹ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(8,24,'8:24','እናንተ ያመናችሁ ሆይ! (መልክተኛው) ሕያው ወደሚያደርጋችሁ እምነት በጠራችሁ ጊዜ ለአላህና ለመልክተኛው ታዘዙ፡፡ አላህም በሰውየውና በልቡ መካከል የሚጋርድ መኾኑን ወደርሱም የምትሰበሰቡ መኾናችሁን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,25,'8:25','ከናንተም ውስጥ እነዚያን የበደሉትን ብቻ ለይታ የማትነካን ፈተና ተጠንቀቁ፡፡ አላህም ቅጣቱ ብርቱ መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,26,'8:26','እናንተም በምድር ላይ የተናቃችሁ ጥቂቶች ኾናችሁ ሳላችሁ ሰዎች ሊነጥቁዋችሁ የምትፈሩ ስትኾኑ ያስጠጋችሁን በእርዳታውም ያበረታችሁን ከመልካሞች ሲሳዮችም ታመሰግኑ ዘንድ የሰጣችሁን አስታውሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,27,'8:27','እናንተ ያመናችሁ ሆይ! አላህንና መልክተኛውን አትክዱ፡፡ አደራዎቻችሁንም እናንተ እያወቃችሁ አትክዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,28,'8:28','ገንዘቦቻችሁና ልጆቻችሁም ፈተና ብቻ መኾናቸውንና አላህም እሱ ዘንድ ታላቅ ምንዳ ያለው መኾኑን እወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,29,'8:29','እናንተ ያመናችሁ ሆይ! አላህን ብትፈሩ የእውነት መለያን ብርሃን ያደርግላችኋል፡፡ ክፉ ሥራዎቻችሁንም ከእናንተ ላይ ያብስላችኋል፡፡ ለእናንተም ይምራችኋል፡፡ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,30,'8:30','እነዚያም የካዱት ሊያስሩህ ወይም ሊገድሉህ ወይም (ከመካ) ሊያወጡህ ባንተ ላይ በመከሩብህ ጊዜ (አስታውስ)፡፡ ይመክራሉም አላህም ተንኮላቸውን ይመልስባቸዋል፡፡ አላህም ከመካሪዎቹ ሁሉ በላይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,31,'8:31','አንቀጾቻችንም በነሱ ላይ በተነበቡ ጊዜ «በእርግጥ ሰምተናል፤ በሻን ኖሮ የዚህን ብጤ ባልን ነበር፤ ይህ የመጀመሪያዎቹ ሰዎች ጽሑፍ ተረቶች እንጂ ሌላ አይደለም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,32,'8:32','«ጌታችን ሆይ! ይህ እርሱ ካንተ ዘንድ (የተወረደ) እውነት እንደ ኾነ በኛ ላይ ከሰማይ ድንጋዮችን አዝንብብን ወይም አሳማሚ ቅጣትን አምጣብን» ባሉም ጊዜ (አስታውስ)');
INSERT INTO "sadiq_sani" VALUES(8,33,'8:33','አላህም አንተ በውስጣቸው እያለህ (ድንጋይ በማዝነብና ባሳማሚ ቅጣት) የሚቀጣቸው አይደለም፡፡ አላህም እነሱ ምህረትን የሚለምኑ ሲኾኑ የሚቀጣቸው አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,34,'8:34','እነሱ ከተከበረው መስጊድ የሚከለክሉ ሲኾኑም አላህ (በሰይፍ) የማይቀጣቸው ለነሱ ምን አልላቸው (የቤቱ) ጠባቂዎችም አልነበሩም፡፡ ጠባቂዎቹ (ክሕደትን) ተጠንቃቂዎቹ እንጂ ሌሎቹ አይደሉም፡፡ ግን አብዛኛዎቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,35,'8:35','በቤቱ ዘንድም (በካዕባ) ስግደታቸው ማፏጨትና ማጨብጨብ እንጂ ሌላ አልነበረም፡፡ ትክዱት በነበራችሁትም ነገር ቅጣትን ቅመሱ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,36,'8:36','እነዚያ የካዱት ከአላህ መንገድ ሊከለክሉ ገንዘቦቻቸውን ያወጣሉ፡፡ በእርግጥም ያወጡዋታል፡፡ ከዚያም በእነሱ ላይ ጸጸት ትኾንባቸዋለች፡፡ ከዚያም ይሸነፋሉ፡፡ እነዚያም የካዱት ወደ ገሀነም ይሰበሰባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,37,'8:37','አላህ መጥፎውን ከመልካሙ ሊለይ መጥፎውንም ከፊሉን በከፊሉ ላይ ሊያደርግና ባንድ ላይ ሊያነባብረው በገሀነም ውስጥም ሊያደርገው (ይሰበሰባሉ)፡፡ እነዚያ እነሱ ከሳሪዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,38,'8:38','ለነዚያ ለካዱት በላቸው፡- ቢከለከሉ ለእነሱ በእርግጥ ያለፈውን (ሥራ) ምሕረት ይደረግላቸዋል፡፡ (ወደ መጋደል) ቢመለሱም (እናጠፋቸዋለን)፡፡ የቀድሞዎቹ ሕዝቦች ልማድ በእርግጥ አልፋለችና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,39,'8:39','ሁከትም እስከማትገኝ ሃይማኖትም ሁሉ ለአላህ ብቻ እስከሚኾን ድረስ ተጋደሏቸው፡፡ ቢከለከሉም አላህ የሚሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,40,'8:40','(ከእምነት) ቢዞሩም አላህ ረዳታችሁ መኾኑን ዕወቁ፡፡ (እርሱ) ምን ያምር ጠባቂ! ምን ያምርም ረዳት!');
INSERT INTO "sadiq_sani" VALUES(8,41,'8:41','ከማንኛውም ነገር በጦር (ከከሓዲዎች) የዘረፋችሁትም አንድ አምስተኛው ለአላህና ለመልክተኛው፣ (ለነቢዩ) የዝምድና ባለቤቶችም፣ ለየቲሞችም፣ ለምስኪኖችም፣ ለመንገደኛም የተገባ መኾኑን ዕውቁ፣ በአላህና በዚያም እውነትና ውሸት በተለየበት ቀን ሁለቱ ጭፍሮች በተገናኙበት (በበድር) ቀን በባሪያችን ላይ ባወረድነው የምታምኑ እንደኾናችሁ (ይህንን ዕወቁ)፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,42,'8:42','እናንተ በቅርቢቱ ዳርቻ ኾናችሁ እነርሱም በሩቂቱ ዳርቻ ኾነው የነጋዴዎቹም ጭፍራ ከናንተ በታች ሲኾኑ በሰፈራችሁ ጊዜ (ያደረግንላችሁን አስታውሱ)፡፡ በተቃጠራችሁም ኖሮ በቀጠሮው በተለያያችሁ ነበር፡፡ ግን አላህ ሊሠራው የሚገባውን ነገር ሊፈጽም የሚጠፋ ሰው ከአስረጅ በኋላ እንዲጠፋ ሕያው የሚኾንም ሰው ከአስረጅ በኋላ ሕያው እንዲኾን (ያለቀጠሮ አጋጠማችሁ)፡፡ አላህም በእርግጥ ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,43,'8:43','አላህ በሕልምህ እነሱን ጥቂት አድርጎ ባሳየህ ጊዜ (አስታውስ)፡፡ እነሱን ብዙ አድርጎ ባሳየህም ኖሮ በፈራችሁና በነገሩ በተጨቃጨቃችሁ ነበር፡፡ ግን አላህ አዳናችሁ፡፡ እርሱ በደረቶች ውስጥ ያለን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,44,'8:44','አላህም ሊሠራው የሚገባን ነገር ይፈጽም ዘንድ በተጋጠማችሁ ጊዜ እነርሱን በዓይኖቻችሁ ጥቂት አድርጎ ያሳያችሁንና በዓይኖቻቸውም ላይ ያሳነሳችሁን (አስታውሱ)፡፡ ነገሮቹም ሁሉ ወደ አላህ ብቻ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(8,45,'8:45','እናንተ ያመናችሁ ሆይ! ሰራዊትን በገጠማችሁ ጊዜ እርጉ፤ (መክቱ)፡፡ አላህንም በብዙ አውሱ፤ በእርግጥ ትድናላችሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,46,'8:46','አላህንና መልክተኛውንም ታዘዙ፡፡ አትጨቃጨቁም፡፡ ትፈራላችሁና ኃይላችሁም ትኼዳለችና፡፡ ታገሱም፤ አላህ ከትዕግስተኞች ጋር ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,47,'8:47','እንደነዚያ ለትዕቢትና ለሰዎች ይዩልኝ ሲሉ ከአላህም መንገድ ለማገድ ከአገራቸው እንደወጡት አትኹኑ፡፡ አላህም በሚሠሩት ሁሉ (በዕውቀቱ) ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,48,'8:48','ሰይጣንም ሥራዎቻቸውን ለእነርሱ ባሳመረላቸውና «ዛሬ ለእናንተ ከሰዎች አሸናፊ የላችሁም እኔም ለእናንተ ረዳት ነኝ» ባለ ጊዜም (አስታውስ)፡፡ ሁለቱ ጭፍሮችም በተያዩ ጊዜ፤ ወደኋላው አፈገፈገ፡፡ «እኔ ከናንተ ንጹህ ነኝ፡፡ እኔ እናንተ የማታዩትን አያለሁ፡፡ እኔ አላህን እፈራለሁ፡፡ አላህም ቅጣተ ብርቱ ነው» አላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,49,'8:49','መናፍቃንና እነዚያ በልቦቻቸው ውስጥ በሽታ ያለባቸው «እነዚህን (ሙስሊሞች) ሃይማኖታቸው አታለላቸው» ባሉ ጊዜ (አስታውስ)፡፡ በአላህም ላይ የሚጠጋ (ያሸንፋል)፤ አላህ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,50,'8:50','እነዚያንም የካዱትን መላእክት ፊቶቻቸውንና ጀርባዎቻቸውን እየመቱ «የቃጠሎንም ስቃይ ቅመሱ» (እያሉ) በሚገድሏቸው ጊዜ ብታይ ኖሮ (አስደንጋጭን ነገር ታይ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,51,'8:51','ይህ እጆቻችሁ ባስቀደሙት ምክንያት አላህም ለባሮቹ በዳይ ባለመኾኑ ነው (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,52,'8:52','(የነዚያ ልማድ) እንደ ፈርዖን ቤተሰብና እንደነዚያም ከእነሱ በፊት እንደ ነበሩት ልማድ ነው፡፡ በአላህ አንቀጾች ካዱ፤ አላህም በኃጢኣቶቻቸው ያዛቸው፤ አላህ ኀይለኛ ቅጣተ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,53,'8:53','ይህ (ቅጣት) አላህ በሕዝቦች ላይ የለገሰውን ጸጋ በነፍሶቻቸው ያለውን ነገር እስከሚለውጡ ድረስ የማይለውጥ በመኾኑ ምክንያት ነው፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,54,'8:54','እንደ ፈርዖን ቤተሰቦችና እንደእነዚያም ከእነሱ በፊት እንደ ነበሩት ልማድ (እስከሚለውጡ ድረስ አይለውጥባቸውም)፡፡ በጌታቸው ተአምራት አስተባበሉና በኃጢኣቶቻቸው አጠፋናቸው፡፡ የፈርዖንንም ቤተሰቦች አሰጠምናቸው፡፡ ሁሉም በዳዮች ነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,55,'8:55','ከሚንቀሳቀሱ እንስሳዎች ሁሉ አላህ ዘንድ ይበልጥ ክፉዎቹ እነዚያ ዘወትር የካዱት ናቸው፡፡ ስለዚህ እነሱ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,56,'8:56','እነዚያ ከነሱ ቃል ኪዳን የያዝክባቸው ከዚያም በየጊዜው ቃል ኪዳናቸውን የሚያፈርሱ ናቸው፡፡ እነሱም አይጠነቀቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,57,'8:57','በጦርም ላይ ብታገኛቸው በእነርሱ ቅጣት ምክንያት (ሌሎቹ ከሓዲዎች) ይገሰጹ ዘንድ ከኋላቸው ያሉትን በትንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,58,'8:58','ከሕዝቦችም ክዳትን ብትፈራ (የኪዳኑን መፍረስ በማወቅ) በመተካከል ላይ ኾናችሁ ኪዳናቸውን ወደነሱ ጣልላቸው፡፡ አላህ ከዳተኞችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,59,'8:59','እነዚያም የካዱት ከአላህ ቅጣት ያመለጡ መኾናቸውን አያስቡ፡፡ እነሱ አያቅቱምና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,60,'8:60','ለእነሱም ከማንኛውም ኃይልና ከታጀቡ ፈረሶችም የቻላችሁትን ሁሉ በእርሱ የአላህን ጠላትና ጠላታችሁን ሌሎችንም ከእነርሱ በቀር ያሉትን የማታውቋቸውን አላህ የሚያውቃቸውን (መናፍቃን) የምታሸብሩበት ስትኾኑ አዘጋጁላቸው፡፡ ከማንኛውም ነገር በአላህ መንገድ የምትለግሱትም ምንዳው ወደናንተ በሙሉ ይሰጣል፡፡ እናንተም አትበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(8,61,'8:61','ወደ ዕርቅም ቢያዘነብሉ ወደ እርሷ አዘንብል፡፡ በአላህም ላይ ተጠጋ፡፡ እነሆ እርሱ ሰሚ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,62,'8:62','ሊያታልሉህም ቢፈልጉ አላህ በቂህ ነው፡፡ እርሱ ያ በእርዳታውና በምእምናን ያበረታህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,63,'8:63','በልቦቻቸውም መካከል ያስማማ ነው፡፡ በምድር ያለውን ሁሉ (ሀብት) በለገስክ ኖሮ በልቦቻቸው መካከል ባላስማማህ ነበር፡፡ ግን አላህ በመካከላቸው አስማማ፡፡ እርሱ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,64,'8:64','አንተ ነቢዩ ሆይ! አላህ በቂህ ነው፡፡ ለተከተሉህም ምእምናን (አላህ በቂያቸው ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(8,65,'8:65','አንተ ነቢዩ ሆይ! ምእምናንን በመዋጋት ላይ አደፋፍራቸው፡፡ ከእናንተ ውስጥ ሃያ ታጋሾች ኖሩ ሁለት መቶን ያሸንፋሉ፡፡ ከእናንተም መቶ ቢኖሩ ከእነዚያ ከካዱት እነሱ የማያውቁ ሕዝቦች ስለኾኑ ሺህን ያሸንፋሉ፤');
INSERT INTO "sadiq_sani" VALUES(8,66,'8:66','አሁን አላህ ከእናንተ ላይ አቀለለላችሁ፡፡ በእናንተ ውስጥም ድክመት መኖሩን ዐወቀ፡፡ ስለዚህ ከእናንተ መቶ ታጋሾች ቢኖሩ ሁለት መቶን ያሸንፋሉ፡፡ ከእናንተም ሺህ ቢኖሩ በአላህ ፈቃድ ሁለት ሺህን ያሸንፋሉ፡፡ አላህም ከታጋሾቹ ጋር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,67,'8:67','ለነቢይ በምድር ላይ እስቲያደክም ድረስ ለእርሱ ምርኮኞች ሊኖሩት አይገባም፡፡ የቅርቢቱን ዓለም ጠፊ ጥቅም ትፈልጋላችሁ፡፡ አላህም መጨረሻይቱን ይሻል፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,68,'8:68','ከአላህ ያለፈ ፍርድ ባልነበረ ኖሮ በወሰዳችሁት (ቤዛ) ከባድ ቅጣት በነካችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(8,69,'8:69','(ከጠላት) ከዘረፋችሁትም (ሀብት) የተፈቀደ መልካም ሲኾን ብሉ፡፡ አላህንም ፍሩ፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(8,70,'8:70','አንተ ነቢዩ ሆይ! ከምርኮኞች በእጆቻችሁ ላሉት በላቸው፡- «አላህ በልቦቻችሁ ውስጥ በጎን ነገር (እምነትን) ቢያውቅ ከእናንተ ከተወሰደባችሁ የተሻለን ይሰጣችኋል፡፡ ለእናንተም ይምራችኋል፡፡ አላህም መሓሪ አዛኝ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(8,71,'8:71','ሊከዱህም ቢፈልጉ ከዚህ በፊት አላህን በእርግጥ ከድተዋል፡፡ ከነሱም አስመችቶሃል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,72,'8:72','እነዚያ ያመኑና የተሰደዱ፡፡ በአላህም መንገድ ላይ በገንዘቦቻቸውና በነፍሶቻቸው የታገሉ፡፡ እነዚያም (ስደተኞቹን) ያስጠጉና የረዱ፡፡ እነዚያ ከፊሎቻቸው ለከፊሉ ረዳቶች ናቸው፡፡ እነዚያም ያመኑና ያልተሰደዱ እስከሚሰደዱ ድረስ ከነሱ ምንም ዝምድና የላችሁም፡፡ በሃይማኖትም እርዳታን ቢፈልጉባችሁ በእናንተና በነሱ መካከል ቃል ኪዳን ባላችሁ ሕዝቦች ላይ ካልኾነ በስተቀር በናንተ ላይ መርዳት አለባችሁ፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,73,'8:73','እነዚያም የካዱት ከፊሎቻቸው ለከፊሉ ረዳቶች ናቸው፡፡ (ከምእምናን መረዳዳትን ከከሓዲያን መቆራረጥን) ባትሠሩት በምድር ላይ ሁከትና ታላቅ ጥፋት ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(8,74,'8:74','እነዚያም ያመኑና የተሰደዱ በአላህም መንገድ ላይ የታገሉ፣ እነዚያም ያስጠጉና የረዱ እነዚያ እነሱ በእውነት አማኞች ናቸው፡፡ ለእነሱም ምሕረትና የከበረ ሲሳይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(8,75,'8:75','እነዚያም በኋላ ያመኑና የተሰደዱ፣ ከእናንተም ጋር ኾነው የታገሉ እነዚያ ከናንተው ናቸው፡፡ የዝምድናዎች ባለቤቶችም በአላህ መጽሐፍ ከፊሎቻቸው በከፊሉ (መውረስ) የተገቡ ናቸው፡፡ አላህ በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,1,'9:1','(ይህች) ከአላህና ከመልክተኛው ወደእነዚያ ቃል ኪዳን ወደ ተጋባችኋቸው አጋሪዎች የምትደርስ ንጽሕና ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(9,2,'9:2','በምድር ላይም አራት ወሮችን (ጸጥተኞች ስትኾኑ) ኺዱ፡፡ እናንተም ከአላህ (ቅጣት) የማታመልጡ መኾናችሁንና አላህም ከሓዲዎችን አዋራጅ መኾኑን ዕወቁ፤ (በሏቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,3,'9:3','(ይህ) ከአላህና ከመልክተኛው በታላቁ ሐጅ ቀን የወጣ አላህ ከአጋሪዎቹ ንጹሕ ነው መልክተኛውም (እንደዚሁ)፡፡ (ከክህደት) ብትጸጸቱም እርሱ ለእናንተ በላጭ ነው፡፡ (ከእምነት) ብትሸሹም እናንተ አላህን የማታቅቱት መኾናችሁን ዕወቁ በማለት ወደ ሰዎች የሚደርስ ማስታወቂያ ነው፡፡ እነዚያን የካዱትንም በአሳማሚ ቅጣት አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,4,'9:4','ከአጋሪዎቹ እነዚያ ቃል ኪዳን የተጋባችኋቸውና ከዚያም ምንም ያላጎደሉባችሁ በናንተ ላይም አንድንም ያልረዱባችሁ ሲቀሩ፤ (እነዚህን) ቃል ኪዳናቸውን እስከጊዜያታቸው (መጨረሻ) ሙሉላቸው፡፡ አላህ ጥንቁቆችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,5,'9:5','የተከበሩትም ወሮች ባለቁ ጊዜ አጋሪዎቹን በአገኛችሁባቸው ስፍራ ግደሉዋቸው፡፡ ያዙዋቸውም፣ ክበቡዋቸውም፣ ለእነሱም (መጠባበቅ) በየመንገዱ ተቀመጡ፡፡ ቢጸጸቱም፣ ሶላትንም በደንቡ ቢሰግዱ፣ ግዴታ ምጽዋትንም ቢሰጡ መንገዳቸውን ልቀቁላቸው፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,6,'9:6','ከአጋሪዎችም አንዱ ጥገኝነትን ቢጠይቅ የአላህን ቃል ይሰማ ዘንድ አስጠጋው፡፡ ከዚያም ወደ መጠበቂያው ስፍራ አድርሰው፡፡ ይህ እነሱ የማያውቁ ሕዝቦች በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,7,'9:7','ለአጋሪዎቹ አላህ ዘንድና እመልክተኛው ዘንድ ቃል ኪዳን እንዴት ይኖራቸዋል እነዚያ በተከበረው መስጊድ ዘንድ ቃል ኪዳን የተጋባችኋቸው ብቻ ሲቀሩ፡፡ (እነዚህ) በቃል ኪዳናቸው ለእናንተ ቀጥ እስካሉላችሁ ድረስ ለነርሱም ቀጥ በሉላቸው፡፡ አላህ ጥንቁቆችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,8,'9:8','በእናንተ ላይ ቢያይሉም በእናንተ ውስጥ ዝምድናንና ቃል ኪዳንን የማይጠብቁ ሲኾኑ እንዴት (ቃል ኪዳን ይኖራቸዋል)፡፡ በአፎቻቸው ያስወድዷችኋል፡፡ ልቦቻቸውም እንቢ ይላሉ፡፡ አብዛኞቻቸውም አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,9,'9:9','በአላህ አንቀጾች ጥቂትን ዋጋ ገዙ፡፡ ከመንገዱም አገዱ፡፡ እነርሱ ይሠሩት የነበሩት ሥራ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(9,10,'9:10','በምእምን ነገር ዝምድናንም ኪዳንንም አይጠብቁም፤ እነዚያም እነሱ ወሰን አላፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,11,'9:11','ቢጸጸቱም፣ ሶላትንም ቢሰግዱ፣ ዘካንም ቢሰጡ የሃይማኖት ወንድሞቻችሁ ናቸው፡፡ ለሚያውቁ ሕዝቦችም አንቀጾችን እናብራራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(9,12,'9:12','ከቃል ኪዳናቸው በኋላ መሓላዎቻቸውን ቢያፈርሱ፣ ሃይማኖታችሁንም ቢያነውሩ፣ የክህደት መሪዎችን (ከክህደት) ይከለከሉ ዘንድ ተዋጉዋቸው፡፡ እነርሱ ቃል ኪዳን የላቸውምና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,13,'9:13','መሓላዎቻቸውን ያፈረሱትን መልክተኛውንም ለማውጣት ያሰቡትን ሕዝቦች እነሱ ለመጀመሪያ ጊዜ የጀመሩዋችሁ ሲኾኑ ለምን አትዋጉዋቸውም ትፈሩዋቸዋላችሁን ምእምናን እንደ ኾናችሁም አላህ ይበልጥ ልትፈሩት የተገባው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,14,'9:14','ተጋደሉዋቸው፡፡ አላህ በእጆቻችሁ ያሰቃያቸዋል፡፡ ያዋርዳቸዋልም፡፡ በእነሱም ላይ ይረዳችኋል፡፡ የምእምናን ሕዝቦችንም ልቦች ያሽራል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,15,'9:15','የልቦቻችሁንም ቁጭት ያስወግዳል፡፡ አላህም ከሚሻው ሰው ላይ ጸጸትን ይቀበላል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,16,'9:16','እነዚያን ከእናንተ ውስጥ የታገሉትንና ከአላህም ከመልክተኛውም ከምእመናንም ሌላ ምስጢረኛ ወዳጅ ያልያዙትን አላህ ሳይገልጽ ልትተው ታስባላችሁን አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,17,'9:17','ለከሓዲዎች በነፍሶቻቸው ላይ በክህደት የሚመሰክሩ ሲኾኑ የአላህን መስጊዶች ሊሠሩ አይገባቸውም፡፡ እነዚያ ሥራዎቻቸው ተበላሹ፡፡ እነሱም በእሳት ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,18,'9:18','የአላህን መስጊዶች የሚሠራው በአላህና በመጨረሻው ቀን ያመነ፣ ሶላትንም በደንቡ የሰገደ፣ ግዴታ ምጽዋትንም የሰጠ ከአላህም ሌላ (ማንንም) ያልፈራ ሰው ብቻ ነው፡፡ እነዚያም ከተመሩት ጭምር መኾናቸው ተረጋገጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,19,'9:19','ካዕባን ጎብኝዎችን ማጠጣትንና የተከበረውን መስጊድ መሥራትን በአላህና በመጨረሻው ቀን እንዳመነና በአላህ መንገድ እንደታገለ ሰው (እምነትና ትግል) አደረጋችሁን አላህ ዘንድ አይተካከሉም፡፡ አላህም በዳዮች ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,20,'9:20','እነዚያ ያመኑት፣ ከአገራቸውም የተሰደዱት፣ በገንዘቦቻቸውና በነፍሶቻቸውም በአላህ መንገድ ላይ የታገሉት አላህ ዘንድ በደረጃ በጣም የላቁ ናቸው፡፡ እነዚያም እነሱ ፍላጎታቸውን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,21,'9:21','ጌታቸው ከርሱ በኾነው እዝነትና ውዴታ በገነቶችም ለነሱ በውስጥዋ የማያቋርጥ መጠቀሚያ ያለባት ስትኾን ያበስራቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,22,'9:22','በውስጧ ዘላለም ዘውታሪዎች ሲኾኑ (ያበስራቸዋል)፡፡ አላህ እርሱ ዘንድ ታላቅ ምንዳ አለና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,23,'9:23','እናንተ ያመናችሁ ሆይ! አባቶቻችሁና ወንድሞቻችሁ ክህደትን ከእምነት አብልጠው ቢወዱ ወዳጆች አድርጋችሁ አትያዙዋቸው፡፡ ከእናንተም ውስጥ ወዳጅ የሚያደርጋቸው እነዚያ እነሱ በዳዮች ናቸው፤');
INSERT INTO "sadiq_sani" VALUES(9,24,'9:24','«አባቶቻችሁና ወንዶች ልጆቻችሁ፣ ወንድሞቻችሁም፣ ሚስቶቻችሁም፣ ዘመዶቻችሁም፣ የሰበሰባችኋቸው ሀብቶችም፣ መክሰሩዋን የምትፈሩዋት ንግድም፣ የምትወዷቸው መኖሪያዎችም እናንተ ዘንድ ከአላህና ከመልክተኛው በርሱ መንገድም ከመታገል ይበልጥ የተወደዱ እንደኾኑ አላህ ትእዛዙን እስከሚያመጣ ድረስ ተጠባበቁ» በላቸው፡፡ አላህም አመጸኞች ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,25,'9:25','አላህም በብዙ ስፍራዎች በእርግጥ ረዳችሁ፤ የሑነይንም ቀን ብዛታችሁ በአስደነቀቻችሁና ከእናንተም ምንም ባልጠቀመቻችሁ ጊዜ ምድርም ከስፋቷ ጋር በጠበበቻችሁና ከዚያም ተሸናፊዎች ኾናችሁ በዞራችሁ ጊዜ (ረዳችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,26,'9:26','ከዚያም አላህ እርጋታውን በመልክተኛውና በምእምናኖቹ ላይ አወረደ፡፡ ያላያችኋቸውንም ሰራዊት አወረደ፡፡ እነዚያን የካዱትንም በመገደልና በመማረክ አሰቃየ፡፡ ይህም የከሓዲያን ፍዳ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,27,'9:27','ከዚያም ከዚህ በኋላ አላህ በሚሻው ሰው ላይ ጸጸትን ይቀበላል፡፡ አላህም እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,28,'9:28','እናንተ ያመናችሁ ሆይ! አጋሪዎች እርኩሶች ብቻ ናቸው፡፡ ከዚህም ዓመታቸው በኋላ የተከበረውን መስጊድ አይቅረቡ፡፡ ድኽነትንም ብትፈሩ አላህ ቢሻ ከችሮታው በእርግጥ ያከብራችኋል፡፡ አላህ ዐዋቂ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,29,'9:29','ከእነዚያ መጽሐፍን ከተሰጡት ሰዎች እነዚያን በአላህና በመጨረሻው ቀን የማያምኑትን፣ አላህና መልክተኛው እርም ያደረጉትንም እርም የማያደርጉትንና እውነተኛውንም ሃይማኖት የማይቀበሉትን እነርሱ የተዋረዱ ኾነው ግብርን በእጆቻቸው እስከሚሰጡ ድረስ ተዋጉዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,30,'9:30','አይሁድ ዑዘይር የአላህ ልጅ ነው አለች፡፡ ክርስቲያኖችም አልመሲሕ የአላህ ልጅ ነው አሉ፡፡ ይህ በአፎቻቸው (የሚናገሩት) ቃላቸው ነው፡፡ የእነዚያን ከነሱ በፊት የካዱትን ሰዎች ቃል ያመሳስላሉ፡፡ አላህ ያጥፋቸው፡፡ (ከእውነት) እንዴት ይመለሳሉ!');
INSERT INTO "sadiq_sani" VALUES(9,31,'9:31','ከእርሱ በቀር ሌላ አምላክ የሌለውን አንድን አምላክ ሊገዙ እንጂ ያልታዘዙ ሲኾኑ ሊቃውንቶቻቸውንና መነኮሳታቸውን፣ የመርየምን ልጅ አልመሲሕንም ከአላህ ሌላ አማልክት አድርገው ያዙ፡፡ ከሚያጋሩት ሁሉ የጠራ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,32,'9:32','የአላህን ብርሃን በአፎቻቸው ለማጥፋት ይፈልጋሉ፡፡ አላህም ከሓዲዎች ቢጠሉም እንኳ ብርሃኑን መሙላትን እንጅ ሌላን አይሻም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,33,'9:33','እርሱ ያ አጋሪዎቹ ቢጠሉም መልክተኛውን (ሙሐመድን) በቅን መንገድና በእውነተኛ ሃይማኖት ላይ ከሃይማኖት ሁሉ በላይ ይፋ ያደርገው ዘንድ የላከው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,34,'9:34','እናንተ ያመናችሁ ሆይ! ከሊቃውንትና ከመነኮሳት ብዙዎቹ የሰዎችን ገንዘቦች በውሸት በእርግጥ ይበላሉ፡፡ ከአላህም መንገድ ያግዳሉ፡፡ እነዚያንም ወርቅንና ብርን የሚያደልቡትን በአላህም መንገድ ላይ የማያወጧትን በአሳማሚ ቅጣት አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,35,'9:35','በርሷ ላይ በገሀነም እሳት ውስጥ በሚጋልባትና ግንባሮቻቸውም፣ ጎኖቻቸውም፣ ጀርባዎቻቸውም በርሷ በሚተኮሱ ቀን (አሳማሚ በኾነ ቅጣት አብስራቸው)፡፡ ይህ ለነፍሶቻችሁ ያደለባችሁት ነው፡፡ ታደልቡት የነበራችሁትንም ቅመሱ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,36,'9:36','የወሮች ቁጥር አላህ ዘንድ በአላህ መጽሐፍ ውስጥ ሰማያትንና ምድርን በፈጠረበት ቀን ዐሥራ ሁለት ወር ነው፡፡ ከእነሱ አራቱ የተከበሩ ናቸው፡፡ ይህ ቀጥተኛው ሃይማኖት ነው፡፡ በእነርሱ ውስጥም ነፍሶቻችሁን አትበድሉ፡፡ አጋሪዎችንም በአንድ ላይ ኾነው እንደሚዋጉዋችሁ በአንድ ላይ ኾናችሁ ተጋደሉዋቸው፡፡ አላህም ከሚፈሩት ጋር መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,37,'9:37','የአንዱን ወር ክብር ወደ ሌላው ማዘግየት በክህደት ላይ (ክህደትን) መጨመር ብቻ ነው፡፡ በእርሱ እነዚያ የካዱት ሰዎች ይሳሳቱበታል፡፡ በአንድ ዓመት የተፈቀደ ያደርጉታል፡፡ በሌላው ዓመትም ያወግዙታል፡፡ (ይህ) አላህ ያከበራቸውን ወሮች ቁጥር ሊያስተካክሉ፣ አላህም እርም ያደረገውን የተፈቀደ ሊያደርጉ ነው፡፡ የሥራዎቻቸው መጥፎው ለነርሱ ተዋበላቸው፡፡ አላህም ከሓዲያን ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,38,'9:38','እናንተ ያመናችሁ ሆይ! በአላህ መንገድ (ለመታገል) ውጡ በተባላችሁ ጊዜ ወደ ምድር የምትወዘፉት ለእናንተ ምን አላችሁ የቅርቢቱን ሕይወት ከመጨረሻይቱ ይበልጥ ወደዳችሁን የቅርቢቱም ሕይወት ጥቅም በመጨረሻይቱ (አንጻር) ጥቂት እንጂ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,39,'9:39','ለዘመቻ ባትወጡ (አላህ) አሳማሚን ቅጣት ይቀጣችኋል፡፡ ከእናንተ ሌላ የኾኑንም ሕዝቦች ይለውጣል፡፡ በምንም አትጎዱትምም፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,40,'9:40','(ነቢዩን) ባትረዱት እነዚያ የካዱት ሕዝቦች የሁለት (ሰዎች) ሁለተኛ ኾኖ (ከመካ) ባወጡት ጊዜ አላህ በእርግጥ ረድቶታል፡፡ ሁለቱም በዋሻው ሳሉ ለጓደኛው አትዘን አላህ ከእኛ ጋር ነውና ባለ ጊዜ አላህም መረጋጋትን በእርሱ ላይ አወረደ፡፡ ባላያችኋቸውም ሰራዊት አበረታው፡፡ የእነዚያንም የካዱትን ሰዎች ቃል ዝቅተኛ አደረገ፡፡ የአላህም ቃል ለእርሷ ከፍተኛ ናት፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,41,'9:41','ቀላሎችም ከባዶችም ኾናችሁ ዝመቱ፡፡ በአላህም መንገድ በገንዘቦቻችሁ በነፍሶቻችሁም ታገሉ፡፡ ይህ የምታውቁ ብትኾኑ ለናንተ በጣም የተሻለ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,42,'9:42','(የጠራህባቸው ነገር) ቅርብ ጥቅምና መካከለኛ ጉዞ በኾነ ኖሮ በተከተሉህ ነበር፡፡ ግን በነሱ ላይ መንገዲቱ ራቀችባቸው፡፡ «በቻልንም ኖሮ ከእናንተ ጋር በወጣን ነበር» ሲሉ በአላህ ይምላሉ፡፡ ነፍሶቻቸውን ያጠፋሉ፡፡ አላህም እነሱ ውሸታሞች መኾናቸውን በእርግጥ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,43,'9:43','አላህ ከአንተ ይቅር አለ፡፡ እነዚያ እውነተኛዎቹ ላንተ እስከሚገለጹልህና ውሸታሞቹንም እስከምታውቅ ድረስ ለነርሱ (እንዲቀሩ) ለምን ፈቀድክላቸው');
INSERT INTO "sadiq_sani" VALUES(9,44,'9:44','እነዚያ በአላህና በመጨረሻው ቀን የሚያምኑት በገንዘቦቻቸውና በነፍሶቻቸው ከመታገላቸው (ለመቅረት) ፈቃድን አይጠይቁህም፡፡ አላህም የሚፈሩትን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,45,'9:45','ፈቃድን የሚጠይቁህ እነዚያ በአላህና በመጨረሻው ቀን የማያምኑት ልቦቻቸውም የተጠራጠሩት ብቻ ናቸው፡፡ እነሱም በጥርጣሬያቸው ውስጥ ይዋልላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,46,'9:46','መውጣትንም ባሰቡ ኖሮ ለርሱ ዝግጅትን ባሰናዱ ነበር፡፡ ግን አላህ (ለመውጣት) እንቅስቃሴያቸውን ጠላ (አልሻውም)፡፡ አሰነፋቸውም፡፡ ከተቀማጮቹም ጋር ተቀመጡ ተባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,47,'9:47','ከእናንተ ጋር በወጡ ኖሮ ጥፋትን እንጂ አይጨምሩላችሁም ነበር፡፡ ሁከትንም የሚፈልጉላችሁ ሲኾኑ በመካከላችሁ (በማሳበቅ) ይቻኮሉ ነበር፡፡ በናንተም ውስጥ ለነሱ አዳማጮች አሏቸው፡፡ አላህም በዳዮችን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,48,'9:48','እነርሱ ጠይዎች ሲኾኑ እውነቱ እስከመጣና የአላህም ትዕዛዝ እስከተገለጸ ድረስ ከዚህ በፊት ሁከትን በእርግጥ ፈለጉ፡፡ ነገሮችንም ሁሉ አገላበጡልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,49,'9:49','ከነሱም ውስጥ «ለእኔ ፍቀድልኝ አትሞክረኝም» የሚል ሰው አልለ፡፡ ንቁ! በመከራ ውስጥ ወደቁ፡፡ ገሀነምም ከሓዲዎችን በእርግጥ ከባቢ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(9,50,'9:50','መልካም ነገር ብታገኝህ ታስከፋቸዋለች፡፡ መከራም ብታገኝህ «ከዚህ በፊት በእርግጥ ጥንቃቄያችንን ይዘናል» ይላሉ፡፡ እነርሱም ተደሳቾች ኾነው ይሸሻሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,51,'9:51','«አላህ ለኛ የጻፈልን (ጥቅም) እንጂ ሌላ አይነካንም፡፡ እርሱ ረዳታችን ነው፡፡ በአላህ ላይም ምእመናን ይመኩ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,52,'9:52','«በእኛ ላይ ከሁለቱ መልካሞች አንደኛዋን እንጂ ሌላን ትጠባበቃላችሁን እኛም አላህ ከእርሱ በኾነው ቅጣት ወይም በእጆቻችን የሚያጠፋችሁ መኾኑን በናንተ ላይ እንጠባበቃለን፡፡ ተጠባበቁም እኛ ከእናንተ ጋር ተጠባባቂዎች ነንና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,53,'9:53','«ወዳችሁም ኾነ ጠልታችሁ ለግሱ፡፡ ከእናንተ ተቀባይ የላችሁም፡፡ እናንተ አመጸኞች ሕዝቦች ናችሁና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,54,'9:54','ልግስናዎቻቸውን ከነሱ ተቀባይ የሚያገኙ ከመኾን እነሱ በአላህና በመልክተኛው የካዱ፣ ሶላትንም እነሱ ታካቾች ኾነው በስተቀር የማይሰግዱ፣ እነሱም ጠይዎች ኾነው በስተቀር የማይሰጡ መኾናቸው እንጂ ሌላ አልከለከላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,55,'9:55','ገንዘቦቻቸውና ልጆቻቸውም አይድነቁህ፡፡ አላህ የሚሻው በቅርቢቱ ሕይወት በነርሱ (በገንዘቦቻቸውና በልጆቻቸው) ሊቀጣቸው ነፍሶቻቸውም እነሱ ከሓዲዎች ኾነው ሊወጡ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,56,'9:56','እነሱም በእርግጥ ከእናንተ ለመኾናቸው በአላህ ይምላሉ፡፡ እነርሱም ከናንተ አይደሉም፡፡ ግን እነሱ (አጋሪዎችን ያገኘ እንዳያገኛቸው) የሚፈሩ ሕዝቦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,57,'9:57','መጠጊያን ወይም ዋሻዎችን ወይም መግቢያን (ቀዳዳ) ባገኙ ኖሮ እነርሱ እየገሰገሱ ወደርሱ በሸሹ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(9,58,'9:58','ከነሱም ውስጥ በምጽዋቶች የሚዘልፉህ ሰዎች አሉ፡፡ ከርሷም (የሚሹትን) ቢስሰጡ ይደሰታሉ፡፡ ከርሷም ባይስሰጡ እነሱ ያን ጊዜ ይጠላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,59,'9:59','እነሱም አላህና መልክተኛው የሰጣቸውን በወደዱ፣ «አላህም በቂያችን ነው፤ አላህ ከችሮታው በእርግጥ ይሰጠናል፣ መልክተኛውም (ይሰጠናል)፣ እኛ ወደ አላህ ከጃዮች ነን» ባሉ ኖሮ (ለነሱ በተሻላቸው ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,60,'9:60','ግዴታ ምጽዋቶች (የሚከፈሉት) ለድኾች፣ ለምስኪኖችም፣ በርሷም ላይ ለሚሠሩ ሠራተኞች፣ ልቦቻቸውም (በእስልምና) ለሚለማመዱት፣ ጫንቃዎችንም (በባርነት ተገዢዎችን) ነጻ በማውጣት፣ በባለ ዕዳዎችም፣ በአላህ መንገድም በሚሠሩ፣ በመንገደኛም ብቻ ነው፡፡ ከአላህ የተደነገገች ግዴታ ናት፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,61,'9:61','ከእነሱም (ከመናፍቃን) እነዚያ ነቢዩን የሚያሰቃዩ «እርሱም ጆሮ ነው» (ወሬ ሰሚ ነው) የሚሉ አልሉ በላቸው፡፡ ለእናንተ የበጎ (ወሬ) ሰሚ ነው፡፡ በአላህ ያምናል፣ ምእምናንንም ያምናቸዋል፣ ከእናንተም ውስጥ ለነዚያ ላመኑት እዝነት ነው፡፡ እነዚያም የአላህን መልክተኛ የሚያሰቃዩ ለነሱ አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,62,'9:62','አላህንና መልክተኛውን ሊያስወድዱ ተገቢያቸው ሲኾን እናንተን ያስወደዷችሁ ዘንድ በአላህ ይምሉላችኋል፡፡ ምእምናኖች ቢኾኑ (አላህንና መልክተኛውን ያስወድዱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,63,'9:63','አላህንና መልክተኛውን የሚከራከር ሰው ለእርሱ የገሀነም እሳት በውስጧ ዘውታሪ ሲኾን የተገባቺው መኾኑን አያውቁምን ይህ ታላቅ ውርደት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,64,'9:64','መናፍቃን በልቦቻቸው ውስጥ ያለውን ነገር የምትነግራቸው ሱራ በእነርሱ ላይ መውረዷን ይፈራሉ፡፡ «አላግጡ አላህ የምትፈሩትን ሁሉ ገላጭ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,65,'9:65','በእርግጥ ብትጠይቃቸው «እኛ የምንዘባርቅና የምንጫወት ብቻ ነበርን» ይላሉ፡፡ «በአላህና በአንቀጾቹ፣ በመልክተኛውም ታላግጡ ነበራችሁን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,66,'9:66','አታመካኙ፤ ከእምነታችሁ በኋላ በእርግጥ ካዳችሁ፡፡ ከእናንተ አንዷን ጭፍራ ብንምር (ሌላዋን) ጭፍራ እነሱ ኃጢአተኞች በመኾናቸው እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(9,67,'9:67','መናፍቃንና መናፍቃት ከፊሎቻቸው ከከፊሉ ናቸው፡፡ በመጥፎ ነገር ያዛሉ ከደግም ነገር ይከለክላሉ፡፡ እጆቻቸውንም (ከልግስና) ይሰበስባሉ፡፡ አላህን ረሱ፡፡፤ ስለዚህ (እርሱ) ዋቸው፡፡ መናፍቃን አመጸኞቹ እነሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,68,'9:68','መናፍቃንንና መናፍቃትን፣ ከሓዲዎችንም አላህ የገሀነምን እሳት በውስጧ ዘውታሪዎች ሲኾኑ ቃል ገብቶላቸዋል፡፡ እርሷ በቂያቸው ናት፡፡ አላህም ረግሟቸዋል፡፡ ለእነርሱም ዘውታሪ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,69,'9:69','እንደነዚያ ከናንተ በፊት እንደ ነበሩት ናችሁ፡፡ ከእናንተ ይበልጥ በኃይል የበረቱ በገንዘቦችም በልጆችም ይበልጥ የበዙ ነበሩ፡፡ በዕድላቸውም ተጠቀሙ፡፡ እነዚያም ከእናንተ በፊት የነበሩት በዕድላቸው እንደተጠቀሙ በዕድላችሁ ተጠቀማችሁ፡፡ እንደነዚያም እንደዘባረቁት ዘባረቃችሁ፡፡ እነዚያ ሥራዎቻቸው በቅርቢቱም በመጨረሻይቱም ዓለም ተበላሹ፡፡ እነዚያም ከሳሪዎቹ እነሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,70,'9:70','የእነዚያ ከእናንተ በፊት የነበሩት የኑሕ ሕዝቦች፣ የዓድና የሰሙድም፣ የኢብራሂምም ሕዝቦች የመድየን ባለቤቶችና የተገልባጮቹም ከተሞች ወሬ አልመጣላቸውምን መልክተኞቻቸው በተዓምራት መጧቸው፡፡ አላህም የሚበድላቸው አልነበረም፡፡ ግን ነፍሶቻቸውን የሚበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,71,'9:71','ምእምንና ምእምናትም ከፊሎቻቸው ለከፊሉ ረዳቶች ናቸው፡፡ በደግ ነገር ያዛሉ፤ ከክፉም ይከለክላሉ፡፡ ሶላትንም ይሰግዳሉ፡፡ ዘካንም ይሰጣሉ፡፡ አላህንና መልክተኛውንም ይታዘዛሉ፡፡ እነዚያን አላህ በእርግጥ ያዝንላቸዋል፡፡ አላህ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,72,'9:72','አላህ ምእምናንንና ምእምናትን ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ በመኖሪያ ገነቶችም ውስጥ መልካም መኖሪያ ቤቶችን ቃል ገብቶላቸዋል፡፡ ከአላህም የኾነው ውዴታ ከሁሉ የበለጠ ነው፡፡ ይህ እርሱ ታላቅ እድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,73,'9:73','አንተ ነቢዩ ሆይ! ከሓዲዎችንና መናፍቃንን ታገል፡፡ በእነሱም ላይ ጨክን፡፡ መኖሪያቸውም ገሀነም ናት፡፡ መመለሻይቱም ከፋች፡፡');
INSERT INTO "sadiq_sani" VALUES(9,74,'9:74','ምንም ያላሉ ለመኾናቸው በአላህ ይምላሉ፡፡ የክህደትንም ቃል በእርግጥ አሉ፡፡ ከእስልምናቸውም በኋላ ካዱ፡፡ ያላገኙትንም ነገር አሰቡ፡፡ አላህም ከችሮታው መልክተኛውም (እንደዚሁ) ያከበራቸው መኾኑን እንጂ ሌላን አልጠሉም፡፡ ቢጸጸቱም ለእነሱ የተሻለ ይኾናል፡፡ ቢሸሹም አላህ በቅርቢቱና በመጨረሻይቱ ዓለም አሳማሚን ቅጣት ይቀጣቸዋል፡፡ ለእነሱም በምድር ውስጥ ምንም ወዳጅና ረዳት የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,75,'9:75','ከእነሱም «አላህን ከችሮታው ቢሰጠን በእርግጥ እንመጸውታለን ከመልካሞቹም በእርግጥ እንኾናለን» ሲል ቃል የተጋባ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,76,'9:76','ከችሮታውም በሰጣቸው ጊዜ በእርሱ ሰሰቱ፡፡ እነሱ (ኪዳናቸውን) የተዉ ኾነውም ዞሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,77,'9:77','አላህንም ቃል የገቡለትን በማፍረሳቸውና ይዋሹትም በነበሩት ምክንያት እስከሚገናኙት ቀን ድረስ ንፍቅናን አስከተላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,78,'9:78','አላህ ምስጢራቸውንና ውይይታቸውን የሚያውቅ መኾኑን አላህም ሩቆችን ሁሉ በጣም ዐዋቂ መኾኑን አያውቁምን');
INSERT INTO "sadiq_sani" VALUES(9,79,'9:79','እነዚያ ከምእምናን በምጽዋቶች ፈቃደኛ የኾኑትን፣ እነዚያንም የችሎታቸውን ያክል እንጅ የማያገኙትን ሰዎች የሚያነውሩ፣ ከእነሱም የሚስቁ አላህ ከነሱ ይሳለቅባቸዋል፤ (ይቀጣቸዋል)፡፡ ለእነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,80,'9:80','ለእነርሱ ምሕረትን ለምንላቸው፤ ወይም ለእነሱ ምሕረትን አትለምንላቸው (እኩል ነው)፡፡ ለእነሱ ሰባ ጊዜ ምሕረትን ብትለምንላቸው አላህ ለነሱ በፍጹም አይምርም፡፡ ይህ እነርሱ አላህና መልክተኛውን በመካዳቸው ነው፡፡ አላህም አመጸኞች ሕዝቦችን አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,81,'9:81','እነዚያ ከዘመቻ የቀሩት ከአላህ መልክተኛ በኋላ በመቀመጣቸው ተደሰቱ፡፡ በአላህም መንገድ በገንዘቦቻቸውና በነፍሶቻቸው መታገልን ጠሉ፡፡ «በሐሩር አትኺዱ» አሉም፡፡ «የገሀነም እሳት ተኳሳነቱ በጣም የበረታ ነው» በላቸው፡፡ የሚያውቁ ቢኾኑ ኖሮ (አይቀሩም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,82,'9:82','ጥቂትንም ይሳቁ፡፡ ይሠሩት በነበሩት ዋጋ ብዙን ያለቅሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,83,'9:83','ከእነሱም ወደ ኾነችው ጭፍራ አላህ ቢመልስህ (ከአንተ ጋር) ለመውጣትም ቢያስፈቅዱህ፡-«ከኔ ጋር በፍጹም አትወጡም፡፡ ከእኔም ጋር ጠላትን አትዋጉም፡፡ እናንተ በመጀመሪያ ጊዜ መቀመጥን ወዳችኋልና፡፡ ከተቀማጮቹ ጋርም ተቀመጡ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,84,'9:84','ከእነሱም በአንድም በሞተ ሰው ላይ ፈጽሞ አትስገድ፡፡ በመቃብሩም ላይ አትቁም፡፡ እነሱ አላህንና መልክተኛውን ክደዋልና፡፡ እነሱም አመጸኞች ኾነው ሞተዋልና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,85,'9:85','ገንዘቦቻቸውና ልጆቻቸውም አይድነቁህ፡፡ አላህ የሚሻው በቅርቢቱ ዓለም በነሱ ሊቀጣቸውና ከሓዲዎችም ኾነው ነፍሶቻቸው እንዲወጡ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,86,'9:86','በአላህ እመኑ፡፡ ከመልክተኛውም ጋር ኾናችሁ ታገሉ፤ በማለት ምዕራፍ በተወረደች ጊዜ ከነሱ የሰፊ ሀብት ባለቤቶች የኾኑት (ለመቅረት) ፈቃድ ይጠይቁሃል፡፡ «ከተቀማጮቹ ጋርም እንኹን ተወን» ይሉሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,87,'9:87','በቤት ከሚቀሩት ጋር መኾናቸውን ወደዱ፡፡ በልቦቻቸውም ላይ (ዝገት) ታተመባቸው፡፡ ስለዚህ እነሱ አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,88,'9:88','ግን መልክተኛው እነዚያም ከርሱ ጋር ያመኑት በገንዘቦቻቸውም በነፍሶቻቸውም ታገሉ፡፡ እነዚያም መልካሞች ሁሉ ለነሱ ናቸው፡፡ እነዚያም እነሱ ምኞታቸውን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,89,'9:89','ለእነሱም ገነቶችን በሥራቸው ወንዞች የሚፈሱባቸውን በውስጣቸው ዘውታሪዎች ሲኾኑ አላህ አዘጋጅቶላቸዋል፡፡ ይህ ታላቅ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,90,'9:90','ከአዕራቦችም ይቅርታ ፈላጊዎቹ ለእነሱ እንዲፈቀድላቸው መጡ፡፡ እነዚያ አላህንና መልክተኛውን የዋሹትም ተቀመጡ፡፡ ከነሱ እነዚያን የካዱትን አሳማሚ ቅጣት በእርግጥ ይነካቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,91,'9:91','በደካሞች ላይ በበሽተኞችም ላይ በነዚያም የሚያወጡት ገንዘብ በማያገኙት ላይ ለአላህና ለመልክተኛው ፍጹም ታዛዦች ከኾኑ (ባይወጡም) ኃጢኣት የለባቸውም፡፡ በበጎ አድራጊዎች ላይ (የወቀሳ) መንገድ ምንም የለባቸውም፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,92,'9:92','በእነዚያም ልትጭናቸው በመጡህ ጊዜ «በርሱ ላይ የምጭናችሁ (አጋሰስ) አላገኝም፤» ያልካቸው ስትኾን የሚያወጡት ገንዘብ ባለማግኘታቸው ለማዘናቸው ዓይኖቻቸው እንባን እያፈሰሱ በዞሩት ላይ (የወቀሳ መንገድ የለባቸውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,93,'9:93','(የወቀሳ) መንገዱ በእነዚያ እነሱ ባለጸጋዎች ኾነው ሳሉ ለመቅረት ፈቃድ በሚጠይቁህ ሰዎች ላይ ብቻ ነው፡፡ ከቀሪዎቹ ጋር መኾናቸውን ወደዱ፡፡ አላህም በልቦቻቸው ላይ አተመባቸው፤ ስለዚህ እነሱ አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,94,'9:94','ወደእነሱ በተመለሳችሁ ጊዜ ምክንያታቸውን ያቀርቡላችኋል፡፡ አታመካኙ፤ እናንተን ፈጽሞ አናምንም፡፡ አላህ ከወሬዎቻችሁ በእርግጥ ነግሮናልና፡፡ አላህም ሥራችሁን በእርግጥ ያያል፡፡ መልክተኛውም (እንደዚሁ)፤ ከዚያም ሩቅንና ቅርብን ሁሉ ዐዋቂ ወደ ኾነው (አላህ) ትመለሳላችሁ ወዲያውም ትሠሩት የነበራችሁትን ሁሉ ይነግራችኋል በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,95,'9:95','ወደእነሱ በተመለሳችሁ ጊዜ እንድትተውዋቸው ለእናንተ በእርግጥ በአላህ ይምላሉ፡፡ እነሱንም ተዋቸው፡፡ እነሱ እርኩሶች ናቸውና፡፡ ይሠሩትም በነበሩት ዋጋ መኖሪያቸው ገሀነም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,96,'9:96','ከእነሱ ትወዱላቸው ዘንድ ለናንተ ይምሉላችኋል፡፡ ከእነሱ ብትወዱም አላህ አመጸኞች ሕዝቦችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,97,'9:97','አዕራቦች በክህደትና በንፍቅና በጣም የበረቱ፤ አላህም በመልክተኛው ላይ ያወረደውን ሕግጋት ባለማወቅ የተገቡ ናቸው፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,98,'9:98','ከአዕራቦችም (በአላህ መንገድ) የሚወጣውን (ገንዘብ) ዕዳ አድርጎ የሚይዝ በእናንተም ላይ የጊዜን መገለባበጥ የሚጠባበቅ ሰው አልለ፡፡ በእነሱ ላይ ጥፋቱ ይዙርባቸው፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,99,'9:99','ከአዕራቦችም በአላህና በመጨረሻው ቀን የሚያምን የሚሰጣቸውንም (ምጽዋቶች) አላህ ዘንድ መቃረቢያዎችና ወደ መልክተኛው ጸሎቶች መዳረሻ አድርጎ የሚይዝ ሰው አልለ፡፡ ንቁ! እርሷ ለእነሱ በእርግጥ አቃራቢ ናት፡፡ አላህ በችሮታው ውስጥ (በገነቱ) በእርግጥ ያስገባቸዋል፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,100,'9:100','ከስደተኞቹና ከረዳቶቹም ሲኾኑ የመጀመሪያዎቹ ቀዳሚዎች እነዚያም በበጎ ሥራ የተከተሏቸው አላህ ከእነሱ ወዷል ከእርሱም ወደዋል፡፡ በሥሮቻቸውም ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘለዓለም ነዋሪዎች ሲኾኑ ለእነርሱ አዘጋጅቶላቸዋል፡፡ ይህ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,101,'9:101','በዙሪያችሁም ካሉት ከዐረብ ዘላኖች መናፍቃን አልሉ፡፡ ከመዲና ሰዎችም በንፍቅና ላይ በማመጽ የዘወተሩ አልሉ፡፡ አታውቃቸውም፡፡ እኛ እናውቃቸዋለን፡፡ ሁለት ጊዜ እንቀጣቸዋለን፡፡ ከዚያም ወደታላቅ ቅጣት ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,102,'9:102','ሌሎችም በኃጢአቶቻቸው የተናዘዙ መልካም ሥራንና ሌላን መጥፎ ሥራ የቀላቀሉ አልሉ፡፡ አላህ ከእነሱ ጸጸታቸውን ሊቀበል ይከጀላል፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,103,'9:103','ከገንዘቦቻቸው ስትኾን በእርሷ የምታጠራቸውና የምታፋፋቸው የኾነችን ምጽዋት ያዝ፡፡ ለነሱም ጸልይላቸው፡፡ ጸሎትህ ለእነሱ እርጋታ ነውና አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,104,'9:104','አላህ እርሱ ከባሮቹ ንስሓን የሚቀበል ምጽዋቶችንም የሚወስድ መኾኑን አላህም እርሱ ጸጸትን ተቀባይ አዛኝ መኾኑን አያውቁም');
INSERT INTO "sadiq_sani" VALUES(9,105,'9:105','በላቸውም ሥሩ አላህ ሠራችሁን በእርግጥ ያያልና፡፡ መልክተኛውና ምእምናንም (እንደዚሁ ያያሉ)፡፡ ሩቁንና ቅርቡን ሁሉ ዐዋቂ ወደ ኾነውም (አላህ) በእርግጥ ትመለሳላችሁ፡፡ ትሠሩትም የነበራችሁትን ሁሉ ይነግራችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,106,'9:106','ሌሎችም ለአላህ ትዕዛዝ የተቆዩ ሕዝቦች አልሉ፡፡ ወይ ይቀጣቸዋል ወይም ከነሱ ላይ ጸጸትን ይቀበላል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,107,'9:107','እነዚያም (ምእምናንን) ለመጉዳት፣ ክህደትንም ለማበርታት፣ በምእምናንም መካከል ለመለያየት፣ ከአሁን በፊት አላህንና መልክተኛውን የተዋጋውንም ሰው ለመጠባበቅ መስጊድን የሠሩት (ከነሱ ናቸው)፡፡ መልካምን ሥራ እንጂ ሌላ አልሻንም ሲሉም በእርግጥ ይምላሉ፡፡ አላህም እነሱ በእርግጥ ውሸታሞች መኾናቸውን ይመሰክራል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,108,'9:108','በእርሱ ውስጥ በፍጹም አትስገድ፡፡ ከመጀመሪያ ቀን ይዞ በአላህ ፍራቻ ላይ የተመሠረተው (የቁባ) መስጊድ በውስጡ ልትሰግድበት ይልቅ የተገባው ነው፡፡ በእሱ ውስጥ መጥራራትን የሚወዱ ወንዶች አልሉ፡፡ አላህም ተጥራሪዎችን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(9,109,'9:109','አላህን በመፍራትና ውዴታውን በመፈለግ ላይ ግንቡን የመሠረተው ሰው ይበልጣልን ወይስ ጎርፍ በሸረሸረው ለመናድ በተቃረበ ገደል አፋፍ ላይ ግንቡን የመሠረተውና በርሱ (ይዞት) በገሀነም እሳት ውስጥ የወደቀ (ይበልጣል)፡፡ አላህም በደለኞች ሕዝቦችን አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,110,'9:110','ያ የካቡት ግንባቸው ልቦቻቸው (በሞት) ካልተቆራረጡ በቀር በልቦቻቸው ውስጥ የመጠራጠር ምክንያት ከመኾን አይወገድም፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,111,'9:111','አላህ ከምእምናን ነፍሶቻቸውንና ገንዘቦቻቸውን ገነት ለእነሱ ብቻ ያላቸው በመኾን ገዛቸው፡፡ በአላህ መንገድ ላይ ይጋደላሉ፡፡ ይገድላሉም፤ ይገደላሉም፡፡ በተውራት በኢንጅልና በቁርኣንም (የተነገረውን) ተስፋ በእርሱ ላይ አረጋገጠ፡፡ ከአላህም የበለጠ በኪዳኑ የሚሞላ ማነው በዚያም በእርሱ በተሻሻጣችሁበት ሽያጫችሁ ተደሰቱ፡፡ ይህም እርሱ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,112,'9:112','(እነርሱ) ተጸጻቺዎች፣ ተገዢዎች፣ አመስጋኞች፣ ጿሚዎች፣ አጎንባሾች፣ በግንባር ተደፊዎች፣ በበጎ ሥራ አዛዦች ከክፉም ከልካዮች፣ የአላህንም ሕግጋት ጠባቂዎች ናቸው ምእምናንንም አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(9,113,'9:113','ለነቢዩና ለነዚያ ላመኑት ለአጋሪዎቹ የዝምድና ባለቤቶች ቢኾኑም እንኳ እነሱ (ከሓዲዎቹ) የእሳት ጓዶች መኾናቸው ከተገለጸላቸው በኋላ ምሕረትን ሊለምኑ የሚገባ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,114,'9:114','የኢብራሂምም ለአባቱ ምሕረትን መለመን ለእርሱ ገብቶለት ለነበረችው ቃል (ለመሙላት) እንጂ ለሌላ አልነበረም፡፡ እርሱም የአላህ ጠላት መኾኑ ለእርሱ በተገለጸለት ጊዜ ከርሱ ራቀ፤ (ተወው)፡፡ ኢብራሂም በእርግጥ በጣም ርኅሩኅ ታጋሽ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,115,'9:115','አላህም ሕዝቦችን ከአቀናቸው በኋላ የሚጠነቀቁትን (ሥራ) ለእነሱ እስከሚገልጸላቸው (እስከሚተውትም) ድረስ ጥፋተኛ የሚያደርግ አይደለም፡፡ አላህ በነገሩ ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,116,'9:116','አላህ የሰማያትና የምድር ንግስና የርሱ ብቻ ነው፡፡ ሕያው ያደርጋል፤ ይገድላልም፡፡ ለእናንተም ከርሱ በቀር ጠባቂም ረዳትም የላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(9,117,'9:117','በነቢዩ በነዚያም በችግሪቱ ጊዜያት (በተቡክ ዘመቻ) ከእነሱ የከፊሎቹ ልቦች (ለመቅረት) ሊዘነበሉ ከተቃረቡ በኋላ በተከተሉት ስደተኞችና ረዳቶች ላይ አላህ በእርግጥ ጸጸታቸውን ተቀበለ፡፡ ከዚያም ከእነሱ ንስሓ መግባታቸውን ተቀበለ፡፡ እርሱ ለእነሱ ርኅሩኅ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,118,'9:118','በእነዚያም በሦስቱ ሰዎች ላይ ምድር ከስፋትዋ ጋር በእነሱ ላይ እስከ ጠበበች፣ ነፍሶቻቸውም በርሳቸው ላይ እስከተጠበቡ፣ ከአላህም ወደርሱ ቢኾን እንጂ ሌላ መጠጊያ አለመኖሩን እስከ አረጋገጡ ድረስ በተቆዩት ላይ (አላህ ጸጸትን ተቀበለ)፡፡ ከዚያም ይጸጸቱ ዘንድ ወደ ጸጸት መራቸው፡፡ አላህ እርሱ ጸጸትን ተቀባይ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,119,'9:119','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፤ ከእውነተኞቹም ጋር ኹኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,120,'9:120','ለመዲና ሰዎችና ከአዕራብም በዙሪያቸው ላሉት ሰዎች ከአላህ መልክተኛ ወደኋላ ሊቀሩ ነፍሶቻቸውንም ከነፍሱ አብልጠው ሊወዱ አይገባቸውም ነበር፡፡ ይህ (ከመቅረት መከልከል) ለእነርሱ መልካም ሥራ የሚጻፍላቸው ቢኾን እንጅ፡፡ በአላህ መንገድ ላይ ጥምም፣ ድካምም፣ ረኃብም፣ የማይነካቸው ከሐዲዎችንም የሚያስቆጭን ስፍራ የማይረግጡ፣ ከጠላትም የሚጎዳን ነገር (መግደልን መማረክን መዝረፍን) የማያገኙ በመኾናቸው ነው፡፡ አላህ የመልካም ሠሪዎችን ምንዳ አያጠፋምና፡፡');
INSERT INTO "sadiq_sani" VALUES(9,121,'9:121','ትንሽንም ትልቅንም ልግስና አይለግሱም ወንዝንም አያቋርጡም አላህ ይሠሩት ከነበረው የበለጠን ምንዳ ይመነዳቸው ዘንድ ለእነሱ የሚጻፍላቸው ቢሆን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,122,'9:122','ምእምናንም (ከነቢዩ ጋር ካልኾነ) በሙሉ ሊወጡ አይገባም፡፡ ከእነሱ ውስጥ ከየክፍሉ አንዲት ጭፍራ ለምን አትወጣም፡፡ (ሌሎቹ) ሃይማኖትን እንዲማሩና ወገኖቻቸው ወደነርሱ በተመለሱ ጊዜ እንዲጠነቀቁ ይገስጹዋቸው ዘንድ (ለምን አይቀሩም)፡፡');
INSERT INTO "sadiq_sani" VALUES(9,123,'9:123','እናንተ ያመናችሁ ሆይ! እነዚያን በአቅራቢያችሁ ያሉትን ከሓዲዎች ተዋጉ፡፡ ከእናንተም ብርታትን ያግኙ፡፡ አላህም ከሚጠነቀቁት ጋር መኾኑን ዕወቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,124,'9:124','ሱራም (ምዕራፍ) በተወረደች ጊዜ ከእነሱ (ከመናፍቃን) ውስጥ ማንኛችሁ ነው ይህች (ምዕራፍ) እምነትን የጨመረችለት የሚል ሰው አልለ፡፡ እነዚያ ያመኑትማ የሚደሰቱ ሲኾኑ እምነትን ጨመረችላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(9,125,'9:125','እነዚያም በልቦቻቸው ውስጥ በሽታ ያለባቸውማ በርክሰታቸው ላይ ርክሰትን ጨመረችላቸው፡፡ እነርሱም ከሓዲዎች ኾነው ሞቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,126,'9:126','በየዓመቱ አንድ ወይም ሁለት ጊዜ እነርሱ የሚሞከሩ መኾናቸውን አያዩምን ከዚያም አይጸጸቱምን እነሱም አይገሰጹምን');
INSERT INTO "sadiq_sani" VALUES(9,127,'9:127','(እነሱን የምታነሳ) ምዕራፍም በተወረደች ጊዜ አንድ ሰው ያያችኋልን እያሉ ከፊሎቻቸው ወደ ከፊሉ ይመለከታሉ፡፡ ከዚያም (ተደብቀው) ይኼዳሉ፡፡ እነርሱ የማያውቁ ሕዝቦች በመኾናቸው አላህ ልቦቻቸውን አዞረ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,128,'9:128','ከጎሳችሁ የሆነ ችግራችሁ በእርሱ ላይ ጽኑ የሆነ፣ በእናንተ (እምነት) ላይ የሚጓጓ፣ በምእምናን (ላይ) ርኅሩኅ አዛኝ የኾነ መልክተኛ በእርግጥ መጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(9,129,'9:129','ቢያፈገፍጉም አላህ በቂዬ ነው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ በእርሱ ላይ ተጠጋሁ እርሱም የታላቁ ዐርሽ ጌታ ነው በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,1,'10:1','አ.ለ.ረ (አሊፍ ላም ራ) ይህቺ (ሱራ) ጥበብ ከተመላው መጽሐፍ (ከቁርኣን) አንቀጾች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(10,2,'10:2','«ሰዎችን አስፈራራ፡፡ እነዚያንም ያመኑትን ለእነሱ ከጌታቸው ዘንድ መልካም ምንዳ ያላቸው መኾኑን አብስር» በማለት ከነርሱው ወደ ኾነ አንድ ሰው ራእይን ማውረዳችን ለሰዎች ድንቅ ኾነባቸውን ከሓዲዎቹ፡- «ይህ በእርግጥ ግልጽ ድግምተኛ ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,3,'10:3','ጌታችሁ ያ ሰማያትንና ምድርን በስድስት ቀኖች ውስጥ የፈጠረ አላህ ነው፡፡ ከዚያም ነገሩን ሁሉ የሚያስተናብር ሲኾን በዐርሹ ላይ (ለሱ ክብር በምስማማ መልኩ) ተደላደለ፡፡ ከእርሱ ፈቃድ በኋላ ቢኾን እንጂ አንድም አማላጅ የለም፡፡ እነሆ! አላህ ጌታችሁ ነውና ተገዙት፤ አትገሰጹምን');
INSERT INTO "sadiq_sani" VALUES(10,4,'10:4','ሁላችሁም ኾናችሁ መመለሻችሁ ወደ እርሱ ነው፡፡ (ይህም) የተረጋገጠ የአላህ ቃል ኪዳን ነው፤ እርሱ መፍጠርን ይጀምራል፡፡ ከዚያም እነዚያን ያመኑትንና መልካም የሠሩትን በትክክል ይመነዳ ዘንድ ይመልሰዋል፡፡ እነዚያ የካዱትም ይክዱት በነበሩት ነገር ለነርሱ ከፈላ ውሃ የኾነ መጠጥና አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,5,'10:5','እርሱ ያ ፀሐይን አንጻባራቂ ጨረቃንም አብሪ ያደረገ ነው፡፡ የዓመታትን ቁጥርና ሒሳብን ታውቁ ዘንድም ለእርሱ መስፈሪያዎችን የለካ ነው፡፡ አላህ ይህንን በእውነት እንጂ (በከንቱ) አልፈጠረውም፡፡ ለሚያውቁ ሕዝቦች አንቀጾችን ያብራራል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,6,'10:6','ሌሊትና ቀን በመተካካታቸው አላህም በሰማያትና በምድር ውስጥ በፈጠረው ሁሉ ለሚጠነቀቁ ሕዝቦች ምልክቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,7,'10:7','እነዚያ መገናኘታችንን የማይፈሩ ቅርቢቱንም ሕይወት የወደዱ በእሷም የረኩ እነዚያም እነሱ ከአንቀጾቻችን ዘንጊዎች የኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,8,'10:8','እነዚያ ይሠሩት በነበሩት ምክንያት መኖሪያቸው በእሳት ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,9,'10:9','እነዚያ ያመኑና መልካም ሥራዎችን የሠሩ ጌታቸው በእምነታቸው ምክንያት (የገነትን መንገድ) ይመራቸዋል፡፡ ከሥራቸው ወንዞች ይፈስሳሉ፡፡ በመጠቀሚያ ገነቶች ውስጥ ይኖራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,10,'10:10','በእርሷም ውስጥ ጸሎታቸው ጌታችን ሆይ! ጥራት ይገባህ (ማለት) ነው፡፡ በእርሷ ውስጥ መከባበሪያቸውም ሰላም መባባል ነው፤ የመጨረሻ ጸሎታቸውም ምስጋና ለዓለማት ጌታ ይሁን (ማለት) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,11,'10:11','አላህ ለሰዎች ደግን ነገር ለማግኘት እንደ መቻኮላቸው (አጥፋን ሲሉት) ክፉን ነገር ቢያስቸኩል ኖሮ ጊዜያቸው ወደነሱ በተፈጸመ ነበር፡፡ እነዚያንም መገናኘታችንን የማይፈሩትን በጥመታቸው ውስጥ የሚዋልሉ ሲኾኑ እንተዋቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,12,'10:12','ሰውንም ጉዳት ባገኘው ጊዜ በጎኑ ላይ ተጋድሞ ወይም ተቀምጦ ወይም ቆሞ ይለምነናል፡፡ ጉዳቱንም ከእሱ ላይ በገለጥንለት ጊዜ ወደ አገኘው ጉዳት እንዳልጠራን ሆኖ ያልፋል፡፡ እንደዚሁ ለድንበር አላፊዎች ይሠሩት የነበሩት ሥራ ተሸለመላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,13,'10:13','ከእናንተ በፊት የነበሩትንም የክፍለ ዘመናት ሰዎች መልክተኞቻቸው በማስረጃዎች የመጧቸው ሲኾኑ በበደሉና የማያምኑም በኾኑ ጊዜ በእርግጥ አጠፋናቸው፡፡ እንደዚሁ ተንኮለኞችን ሕዝቦች እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,14,'10:14','ከዚያም እንዴት እንደምትሠሩ ልንመለከት ከእነሱ በኋላ በምድር ላይ ምትኮች አደረግናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,15,'10:15','አንቀጾቻችንም ግልጽ ኾነው በእነርሱ ላይ በተነበቡላቸው ጊዜ እነዚያ መገናኘታችንን የማይፈሩት ከዚህ ሌላ የሆነን ቁርኣን አምጣልን ወይም ለውጠው ይላሉ፡፡ እኔ ከራሴ በኩል ልለውጠው አይገባኝም፡፡ ወደእኔ የሚወርደውን እንጂ አልከተልም፡፡ እኔ ከጌታዬ ባምጽ የታላቁን ቀን ቅጣት እፈራለሁ በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,16,'10:16','አላህ በሻ ኖሮ በእናንተ ላይ ባለነበብኩትም (አላህ) እርሱን ባላሳወቃችሁም ነበር፡፡ በእናንተም ውስጥ ከእርሱ በፊት (ምንም ሳልል) ብዙን ዕድሜ በእርግጥ ኖሬአለሁ፤ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(10,17,'10:17','በአላህም ላይ እብለትን ከቀጣጠፈ ወይም በአንቀጾቹ ከአስተባበለ ይበልጥ በዳይ ማን ነው እነሆ አመጸኞች አይድኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,18,'10:18','ከአላህም ሌላ የማይጎዳቸውን የማይጠቅማቸውንም ይግገዛሉ፡፡ «እነዚህም አላህ ዘንድ አማላጆቻችን ናቸው» ይላሉ፡፡ «አላህን በሰማያትና በምድር ውስጥ የማያውቀው ነገር ኖሮ ትነግሩታላችሁን» በላቸው፡፡ ከሚያጋሩት ሁሉ ጠራ፤ ላቀም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,19,'10:19','ሰዎችም አንድ ሕዝብ እንጂ ሌላ አልነበሩም፡፡ ተለያዩም፡፡ ከጌታህ ያለፈች ቃልም ባልነበረች ኖሮ በእርሱ በሚለያዩበት ነገር በመካከላቸው በእርግጥ (በቶሎ) በተፈረደ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(10,20,'10:20','በእርሱ ላይም ከጌታው የሆነች ተዓምር ለምን አትወረድለትም ይላሉ፡፡ «ሩቅ ነገርም ሁሉ የአላህ ብቻ ነው፡፡ ተጠባበቁም፤ እኔ ከናንተ ጋር ከሚጠባበቁት ነኝና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,21,'10:21','ሰዎችንም ካገኛቸው ጉዳት በኋላ እዝነትን (ዝናብና ምቾትን) ባቀመስናቸው ጊዜ ወዲያውኑ ለእነሱ በተዓምራቶቻችን (በማላገጥ) ተንኮል ይኖራቸዋል፡፡ አላህ ቅጣተ ፈጣን ነው፡፡ «መልክተኞቻችን የምትመክሩትን ነገር በእርግጥ ይጽፋሉ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,22,'10:22','እርሱ (አላህ) ያ በየብስና በባሕር የሚያስኬዳችሁ ነው፡፡ በመርከቦችም ውስጥ በሆናችሁና በእነርሱም በመልካም ነፋስ በርሷ የተደሰቱ ኾነው (መርከቦቹ) በተንሻለሉ ጊዜ ኀይለኛ ነፋስ ትመጣባታለች፡፡ ከየስፍራውም ማዕበል ይመጣባቸዋል፡፡ እነሱም (ለጥፋት) የተከበቡ መሆናቸውን ያረጋግጣሉ፡፡ (ያን ጊዜ) አላህን ከዚህች (ጭንቀት) ብታድነን በእርግጥ ከአመስጋኞቹ እንሆናለን ሲሉ ሃይማኖትን ለርሱ ብቻ ያጠሩ ሆነው ይለምኑታል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,23,'10:23','በአዳናቸውም ጊዜ እነሱ ወዲያውኑ ያለ አግባብ በምድር ላይ ወሰን ያልፋሉ፡፡ እናንተ ሰዎች ሆይ! ወሰን ማለፋችሁ (ጥፋቱ) በነፍሶቻችሁ ላይ ብቻ ነው፡፡ (እርሱም) የቅርቢቱ ሕይወት መጠቀሚያ ነው፡፡ ከዚያም መመለሻችሁ ወደኛ ነው፡፡ ትሠሩት የነበራችሁትንም ሁሉ እንነግራችኋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,24,'10:24','የቅርቢቱ ሕይወት ምሳሌ ከሰማይ እንዳወረድነው ውሃ፣ በእርሱም ሰውና እንስሳዎች ከሚበሉት የምድር በቃይ ተቀላቅሎበት (እንደ ፋፋ) ምድርም ውበትዋን በያዘችና ባጌጠች ጊዜ ባለቤቶችዋም በርሷ ላይ እነርሱ ቻዮች መኾናቸውን ባሰቡ ጊዜ ትእዛዛችን ሌሊት ወይም ቀን መጥቶባት ትላንት እንዳልነበረች የታጨደች እንዳደረግናት ብጤ ነው፡፡ እንደዚሁም ለሚያስተነትኑ ሕዝቦች አንቀጾችን እናብራራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,25,'10:25','አላህም ወደ ሰላም አገር ይጠራል፡፡ የሚሻውንም ሰው ወደ ቀጥተኛው መንገድ ይመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,26,'10:26','ለእነዚያ መልካም ለሠሩት መልካም ነገርና ጭማሪም አላቸው፡፡ ፊቶቻቸውንም ጥቁረትና ውርደት አይሸፍናቸውም፡፡ እነዚያ የገነት ሰዎች ናቸው፡፡ እነርሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,27,'10:27','ለእነዚያም ኃጢአቶችን ለሠሩት የኃጢኣቲቱ ቅጣት በብጤዋ አለቻቸው፡፡ ውርደትም ትሸፍናቸዋለች፡፡ ለእነሱ ከአላህ (ቅጣት) ጠባቂ የላቸውም፡፡ ፊቶቻቸው ከጨለመ ሌሊት ቁራጮች እንደ ተሸፈኑ ይኾናሉ፡፡ እነዚያ የእሳት ሰዎች ናቸው፡፡ እነርሱ በውስጧ ዘለዓለም ነዋሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,28,'10:28','ሁሉንም የምንሰበስባቸውን ከዚያም ለእነዚያ ለአጋሩት እናንተም ተጋሪዎችሁም ስፍራችሁን ያዙ የምንልበትን ተጋሪዎቻቸውም እኛን ትገዙ አልነበራችሁም የሚሉዋቸው ሲኾኑ በመካከላቸው የምንለይበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(10,29,'10:29','«(ጣዖታቶቹ ለእኛ) ከመገዛታችሁ በእርግጥ ዘንጊዎች ለመኾናችንም በእኛና በእናንተ መካከል የአላህ መስካሪነት በቃ» (ይሏቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(10,30,'10:30','በዚያ ቀን ነፍስ ሁሉ ያሳለፈችውን ሥራ ታውቃለች፡፡ (አጋሪዎች) ወደ አላህም እውነተኛ ወደ ሆነው ጌታቸው ይመለሳሉ፡፡ ከእነሱም ይቀጣጥፉት የነበሩት ነገር ይጠፋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,31,'10:31','«ከሰማይና ከምድር ሲሳይን የሚሰጣችሁ ማን ነው መስሚያዎችንና ማያዎችንስ የፈጠረ ማን ነው ከሙትም ሕያውን የሚያወጣ ከሕያውም ሙትን የሚያወጣስ ማን ነው ነገሩን ሁሉ የሚያስተናብርስ ማን ነው» በላቸው፡፡ «በእርግጥም አላህ ነው» ይሉሃል፡፡ «ታዲያ (ለምን ታጋራላችሁ) አትፈሩትምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,32,'10:32','እርሱ እውነተኛ ጌታችሁ አላህ ነው፡፡ ከእውነትም በኋላ ጥመት እንጂ ሌላ ምን አለ (ከውነት) እንዴት ትዞራላችሁ');
INSERT INTO "sadiq_sani" VALUES(10,33,'10:33','እንደዚሁ የጌታህ ቃል በእነዚያ ባመጹት ላይ እነርሱ የማያምኑ መሆናቸው ተረጋገጠች፡፡');
INSERT INTO "sadiq_sani" VALUES(10,34,'10:34','«ከምታጋሩዋቸው (ጣዖታት) ውስጥ መፍጠርን የሚጀምር ከዚም የሚመልሰው አለን» በላቸው፡፡ «አላህ መፍጠርን ይጀምራል፤ ከዚያም ይመልሰዋል፡፡ ታዲያ (ከእምነት) እንዴት ትዞራላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,35,'10:35','«ከምታጋሩዋቸው፤ ወደ እውነት የሚመራ አለን» በላቸው፡፡ «አላህ ወደ እውነቱ ይመራል፡፡ ወደ እውነት የሚመራው ሊከተሉት የተገባው ነውን ወይስ ካልተመራ በስተቀር የማይመራው ለእናንተም ምን (አስረጅ) አላችሁ እንዴት (በውሸት) ትፈርዳላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,36,'10:36','አብዛኞቻቸውም ጥርጣሬን እንጂ አይከተሉም፡፡ ጥርጣሬ ከእውነት ምንም አያብቃቃም፡፡ አላህ የሚሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,37,'10:37','ይህም ቁርኣን ከአላህ ሌላ (ከሆነ ፍጡር) የተቀጠፈ ሊሆን አይገባውም፡፡ ግን ያንን ከእርሱ በፊት ያለውን (መጽሐፍ) የሚያረጋግጥና በመጽሐፉ ውስጥ ያለውን የሚዘረዝር በእርሱ ጥርጣሬ የሌለበት ሲሆን ከዓለማት ጌታ (የተወረደ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,38,'10:38','በእውነትም «(ሙሐመድ) ቀጣጠፈው» ይላሉን «ይህ ከሆነ መሰሉን (አንዲት) ሱራ አምጡ፡፡ ከአላህም ሌላ የቻላችሁትን ሁሉ (ረዳት) ጥሩ፡፡ እውነተኞች እንደሆናችሁ (ተጋግዛችሁ አምጡ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,39,'10:39','ይልቁንም (ከቁርኣን) እውቀቱን ባላደረሱት ፍቹም ገና ባልመጣቸው ነገር አስተባበሉ፡፡ እንደዚሁ እነዚያ ከእነሱ በፊት የነበሩት አስተባብለዋል፡፡ የበዳዮችም መጨረሻ እንዴት እንደ ነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(10,40,'10:40','ከእነርሱም ውስጥ (ወደፊት) በርሱ የሚያምኑ አልሉ፡፡ ከእነርሱም በእርሱ የማያምኑ አልሉ፡፡ ጌታህም አጥፊዎቹን በጣም ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,41,'10:41','«ቢያስተባብሉህም ለእኔ ሥራዬ አለኝ፡፡ ለእናንተም ሠራችሁ አላችሁ፡፡ እናንተ ከምሠራው ነገር ንጹሕ ናችሁ፡፡ እኔም ከምትሠሩት ነገር ንጹሕ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,42,'10:42','ከእነሱም ወደ አንተ የሚያዳምጡ (እና የማያምኑ) አልሉ፡፡ አንተ ደንቆሮዎችን የማያውቁ ቢኾኑም ታሰማለህን');
INSERT INTO "sadiq_sani" VALUES(10,43,'10:43','ከእነሱም ወደ አንተ የሚመለከቱ አልሉ፡፡ አንተ (ልበ) ዕውራንን የማያዩ ቢኾኑም ትመራለህን');
INSERT INTO "sadiq_sani" VALUES(10,44,'10:44','አላህ ሰዎችን ምንም አይበድልም፡፡ ግን ሰዎች ነፍሶቻቸውን ይበድላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,45,'10:45','(ከሓዲዎችን) ከቀን አንዲትን ሰዓት እንጂ እንዳልቆዩ መስለው በመካከላቸው የሚተዋወቁ ሲሆኑ በምንሰበስባቸው ቀን (አስታውስ)፡፡ እነዚያ በአላህ መገናኘት ያስተባበሉት በእርግጥ ከሰሩ የተመሩም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,46,'10:46','የዚያንም የምናስፈራራቸውን ከፊሉን (በሕይወትህ) ብናሳይህ (መልካም ነው)፡፡ ወይም (ሳናሳይህ) ብንገድልህ መመለሻቸው ወደ እኛ ነው፡፡ ከዚያም አላህ በሚሠሩት ስራ ላይ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,47,'10:47','ለሕዝቦችም ሁሉ መልክተኛ አላቸው፡፡ መልክተኛውም በመጣ ጊዜ (ሲያስተባብሉ) በመካከላቸው በትክክል ይፈረዳል፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,48,'10:48','«እውነተኞችም ከሆናችሁ ይህ (የቅጣቱ) ቀጠሮ መቼ ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,49,'10:49','«ለራሴ ጉዳትንም ጥቅምንም አላህ ከሻው በስተቀር አልችልም፡፡ ለሕዝቦች ሁሉ የተወሰነ ጊዜ አልላቸው፡፡ ጊዜያቸው በመጣ ወቅት አንዲትን ሰዓት አይቆዩም፤ አይቀደሙምም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,50,'10:50','«ቅጣቱ በሌሊት ወይም በቀን ቢመጣባችሁ፤ አጋሪዎቹ ከርሱ የሚቻኮሉበት ነገር ምንድን ነው ንገሩኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,51,'10:51','«ከዚያም (ቅጣቱ) በወደቀ ጊዜ በእርሱ አመናችሁበትን በእርግጥ በእርሱ የምትቻኮሉ የነበራችሁ ስትሆኑ አሁን» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(10,52,'10:52','ከዚያም ለእነዚያ ለበደሉት፡- «ዘውታሪን ቅጣት ቅመሱ ትሠሩት የነበረችሁትን ዋጋ እንጂ አትመነዱም» ይባላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,53,'10:53','እርሱም «እውነት ነውን» (ሲሉ) ይጠይቁሃል፡፡ «አዎን፤ በጌታዬ እምላለሁ፤ እርሱ እውነት ነው፡፡ እናንተም አምላጮች አይደላችሁም፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,54,'10:54','ለበደለችም ነፍስ ሁሉ በምድር ላይ ያለው ሁሉ ቢኖራት ኖሮ በእርግጥ በተበዠችበት ነበር፡፡ ቅጣቱንም ባዩ ጊዜ ጸጸትን ይገልጻሉ፡፡ በመካከላቸውም በትክክል ይፈረዳል፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,55,'10:55','ንቁ! በሰማያትና በምድር ያለው ሁሉ የአላህ ነው፡፡ ንቁ! የአላህ ተስፋ ቃል እርግጥ ነው፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,56,'10:56','እርሱ ሕያው ያደርጋል፤ ይገድላልም ወደ እርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,57,'10:57','እናንተ ሰዎች ሆይ! ከጌታችሁ ግሳጼ በደረቶች ውስጥም ላለው (የመጠራጠር በሽታ) መድኃኒት ለምእምናንም ብርሃንና እዝነት በእርግጥ መጣችላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,58,'10:58','«በአላህ ችሮታና በእዝነቱ (ይደሰቱ)፡፡ በዚህም ምክንያት ይደሰቱ፡፡ እርሱ ከሚሰበስቡት ሀብት በላጭ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,59,'10:59','«አላህ ከሲሳይ ለእናንተ ያወረደውን ከእርሱም እርምና የተፈቀደ ያደረጋችሁትን አያችሁን» በላቸው፡፡ «አላህ (ይህንን) ለእናንተ ፈቀደላችሁን ወይስ በአላህ ላይ ትቀጣጥፋላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,60,'10:60','የእነዚያም በአላህ ላይ ውሸትን የሚቀጣጥፉ (ሰዎች) በትንሣኤ ቀን (በአላህ) ጥርጣሬያቸው ምንድን ነው (አይቀጡም ይመስላቸዋልን) አላህ በሰዎች ላይ (ቅጣትን ባለማቻኮል) የልግስና ባለቤት ነው፡፡ ግን አብዛኛዎቹ አያመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,61,'10:61','(ሙሐመድ ሆይ!) በማንኛውም ነገር ላይ አትሆንም፣ ከርሱም ከቁርኣን አታነብም፣ ማንኛውንም ሥራ (አንተም ሰዎቹም) አትሠሩም በገባችሁበት ጊዜ በናንተ ላይ ተጠባባቂዎች ብንሆን እንጂ፡፡ በምድርም ሆነ በሰማይ የብናኝ ክብደት ያክል ከጌታህ (ዕውቀት) አይርቅም፡፡ ከዚያም ያነሰ የተለቀም የለም፤ በግልጹ መጽሐፍ ውስጥ የተጻፈ ቢሆን እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,62,'10:62','ንቁ! የአላህ ወዳጆች በእነሱ ላይ ፍርሃት የለባቸውም፤ እነሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,63,'10:63','(እነሱም) እነዚያ ያመኑትና ይፈሩት የነበሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,64,'10:64','ለእነሱ በቅርቢቱ ሕይወትም በመጨረሻይቱም ብስራት አላቸው፤ የአላህ ቃላት መለወጥ የላትም፤ ይህ እርሱ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,65,'10:65','ንግግራቸውም አያሳዝንህ፡፡ ኀይል ሁሉ በሙሉ የአላህ ብቻ ነውና፡፡ እርሱ ሰሚው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,66,'10:66','ንቁ! በሰማያት ያሉትና በምድርም ያሉት ሁሉ በእርግጥ የአላህ ናቸው፤ እነዚያ ከአላህ ሌላ ተጋሪዎችን የሚጠሩ ምንን ይከተላሉ ጥርጣሬን እንጂ ሌላ አይከተሉም፡፡ እነርሱም የሚዋሹ እንጂ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,67,'10:67','እርሱ ያ ሌሊትን በውስጡ ልታርፉበት (ጨለማ)፤ ቀንንም (ልትሠሩበት) ብርሃን ያደረገላችሁ ነው፡፡ በዚህ ውስጥ ለሚሰሙ ሕዝቦች በእርግጥ ታምራቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,68,'10:68','«አላህ ልጅን ያዘ (ወለደ)» አሉ፡፡ (ከሚሉት) ጥራት ተገባው፡፡ እርሱ ተብቃቂ ነው፡፡ በሰማያትና በምድር ያለው ሁሉ የርሱ ነው፡፡ እናንተ ዘንድ በዚህ (በምትሉት) ምንም አስረጅ የላችሁም፡፡ በአላህ ላይ የማታውቁትን ትናገራላችሁን');
INSERT INTO "sadiq_sani" VALUES(10,69,'10:69','«እነዚያ በአላህ ላይ ውሸትን የሚቀጣጥፉ ፈጽሞ አይድኑም፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,70,'10:70','(እነሱ) በቅርቢቱ ዓለም መጠቀም አላቸው፡፡ ከዚያም መመለሻቸው ወደእኛ ነው፡፡ ከዚያም ይክዱ በነበሩት ምክንያት ብርቱን ቅጣት እናቀምሳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,71,'10:71','የኑሕንም ወሬ በእነሱ ላይ አንብብላቸው፡፡ ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፡፡ «ሕዝቦቼ ሆይ! በእናንተ ውስጥ ብዙ ጊዜ መቆየቴ በአላህም ተዓምራት ማስታወሴ በእናንተ ላይ የከበደ ቢሆን፤ በአላህ ላይ ተጠግቻለሁ፡፡ ነገራችሁንም ከምታጋሩዋቸው (ጣዖታት) ጋር ሆናችሁ ቁረጡ፡፡ ከዚያም ነገራችሁ በእናንተ ላይ ድብቅ አይሁን (ግለጹት)፡፡ ከዚያም (የሻችሁትን) ወደእኔ አድርሱ፡፡ ጊዜም አትስጡኝ (ከምንም አልቆጥራችሁም)፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,72,'10:72','«ብትሸሹም (አትጎዱኝም)፡፡ ከምንዳ ምንንም አልለምናችሁምና፡፡ ምንዳዬ በአላህ ላይ እንጂ በሌላ ላይ አይደለም፡፡ ከሙስሊሞች እንድሆንም ታዝዣለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,73,'10:73','አስተባበሉትም፡፡ እርሱንና ከእርሱ ጋር የነበሩትንም በታንኳይቱ ውስጥ አዳንናቸው፡፡ (ለጠፉት) ምትኮችም አደረግናቸው፡፡ እነዚያንም በአንቀጾቻችን ያስተባበሉትን አሰጠምን፡፡ የተስፈራሩትም ሕዝቦች መጨረሻ እንዴት እንደነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(10,74,'10:74','ከዚያም ከእርሱ በኋላ መልክተኞችን ወደየሕዝቦቻቸው ላክን፡፡ በግልጽ ማስረጃዎችም መጧቸው፡፡ (ከመላካቸው) በፊት በእርሱ ባስተባበሉበትም ነገር፤ የሚያምኑ አልሆኑም፡፡ እንደዚሁ በወሰን አላፊዎች ልቦች ላይ እናትማለን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,75,'10:75','ከዚያም ከእነሱ ኋላ ሙሳንና ሃሩንን ወደ ፈርዖንና ወደ መማክርቱ በተዓምራታችን ላክን፡፡ ኮሩም ትዕቢተኞች ሕዝቦችም ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,76,'10:76','ከእኛም ዘንድ እውነቱ በመጣላቸው ጊዜ «ይህ በእርግጥ ግልጽ ድግምት ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,77,'10:77','ሙሳ አለ፡- «እውነቱን በመጣላችሁ ጊዜ ድግምተኞች የማይድኑ ሲኾኑ (እርሱ ድግምት ነው) ትላላችሁን ይህ ድግምት ነውን»');
INSERT INTO "sadiq_sani" VALUES(10,78,'10:78','(እነርሱም) አሉ፡- «አባቶቻችንን በእርሱ ላይ ካገኘንበት ሃይማኖት ልታዞረን ለእናንተም በምድር ውስጥ ኩራት (ሹመት) ልትኖራችሁ መጣህብን እኛም ለእናንተ አማኞች አይደለንም፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,79,'10:79','ፈርዖንም፡- «ዐዋቂ ድግምተኛን ሁሉ አምጡልኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,80,'10:80','ድግምተኞቹም በመጡ ጊዜ ሙሳ ለእነርሱ፡- «እናንተ የምትጥሉትን ነገር ጣሉ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,81,'10:81','(ገመዶቻቸውን) በጣሉም ጊዜ ሙሳ አለ፡- «ያ በርሱ የመጣችሁበት ነገር ድግምት ነው፡፡ አላህ በእርግጥ ያፈርሰዋል፡፡ አላህ የአጥፊዎችን ሥራ ፈጽሞ አያበጅምና፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,82,'10:82','አላህም ከሓዲዎች ቢጠሉም እውነትን በቃላቱ ያረጋግጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,83,'10:83','ለሙሳም ከፈርዖንና ከሹማምንቶቻቸው ማሰቃየትን ከመፍራት ጋር ከወገኖቹ የኾኑ ጥቂቶች ትውልዶች እንጂ አላመኑለትም፡፡ ፈርዖንም በምድር ላይ በእርግጥ የኮራ ነበር፡፡ እርሱም በእርግጥ ወሰን ካለፉት ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(10,84,'10:84','ሙሳም አለ፡- «ሕዝቦቼ ሆይ! በአላህ አምናችሁ እንደ ሆነ በርሱ ላይ ተጠጉ፡፡ ታዛዦች እንደ ሆናችሁ (በአላህ ላይ ትመካላችሁ)፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,85,'10:85','አሉም፡- «በአላህ ላይ ተጠጋን፡፡ ጌታችን ሆይ! ለበደለኞች ሕዝቦች መፈተኛ አታድርገን፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,86,'10:86','በእዝነትህም ከከሓዲዎች ሕዝቦች አድነን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,87,'10:87','ወደ ሙሳና ወደ ወንድሙ፡- «ለሕዝቦቻችሁ በምስር ቤቶችን ሥሩ፡፡ ቤቶቻችሁንም መስገጃ አድርጉ፡፡ ሶላትንም በደንቡ ስገዱ፡፡ (ለሙሳ) ምእምናኖቹንም አብስር» ስንል ላክን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,88,'10:88','ሙሳም አለ፡- «ጌታችን ሆይ! አንተ ለፈርዖንና ለሹሞቹ በቅርቢቱ ሕይወት ጌጥን ብዙ ገንዘቦችንም በእርግጥ ሰጠህ፡፡ ጌታችን ሆይ! ከመንገድህ ያሳስቱ ዘንድ (ሰጠሃቸው)፡፡ ጌታችን ሆይ! ገንዘቦቻቸውን አጥፋ፡፡ በልቦቻቸውም ላይ አትም፡፡ አሳማሚን ቅጣት እስከሚያዩ አያምኑምና፡፡»');
INSERT INTO "sadiq_sani" VALUES(10,89,'10:89','(አላህም) «ጸሎታችሁ በእርግጥ ተሰምታለች፡፡ ቀጥም በሉ፡፡ የእነዚያንም የማያውቁትን ሰዎች መንገድ አትከተሉ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,90,'10:90','የእስራኤልንም ልጆች ባሕሩን አሳለፍናቸው፡፡ ፈርዖንና ሰራዊቱም ወሰን ለማለፍና ለመበደል ተከተሏቸው፡፡ መስጠምም ባገኘው ጊዜ፡- «አመንኩ፡፡ እነሆ ከዚያ የእስራኤል ልጆች በርሱ ከአመኑበት በስተቀር ሌላ አምላክ የለም፡፡ እኔም ከታዛዦቹ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,91,'10:91','ከአሁን በፊት በእርግጥ ያመጽክ ከአጥፊዎችም የነበርክ ስትሆን አሁን (አመንኩ ትላለህ)');
INSERT INTO "sadiq_sani" VALUES(10,92,'10:92','ዛሬማ ከኋላህ ላሉት ተዓምር ትሆን ዘንድ በድን ሆነህ (ከባሕሩ) እናወጣሃለን (ተባለ)፡፡ ከሰዎችም ብዙዎቹ ከተዓምራታችን በእርግጥ ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,93,'10:93','የእስራኤልንም ልጆች ምስጉንን ስፍራ በእርግጥ አሰፈርናቸው፡፡ ከመልካም ሲሳዮችም ሰጠናቸው፡፡ ዕውቀትም (ቁርኣን) እስከመጣቸው ድረስ አልተለያዩም፡፡ ጌታህ በርሱ ይለያዩበት በነበሩት ነገር በትንሣኤ ቀን በመካከላቸው ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,94,'10:94','ወደ አንተም ካወረድነው በመጠራጠር ውስጥ ብትሆን እነዚያን ከአንተ በፊት መጽሐፉን የሚያነቡትን ጠይቅ፡፡ እውነቱ ከጌታህ ዘንድ በእርግጥ መጥቶልሃል፡፡ ከተጠራጣሪዎቹም አትሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,95,'10:95','ከእነዚያም የአላህን አንቀጾች ከአስተባበሉት አትሁን፡፡ ከከሳሪዎቹ ትሆናለህና፡፡');
INSERT INTO "sadiq_sani" VALUES(10,96,'10:96','እነዚያ በእነርሱ ላይ የጌታህ ቃል የተረጋገጠባቸው አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,97,'10:97','ተዓምር ሁሉ ብትመጣላቸውም አሳማሚን ቅጣት እስከሚያዩ ድረስ (አያምኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(10,98,'10:98','(ከዩኑስ በፊት ካለፉት ከተሞች) ያመነችና እምነትዋ የጠቀማት ከተማ ለምን አልኖረችም ግን የዩኑስ ሕዝቦች ባመኑ ጊዜ በቅርቢቱ ሕይወት የውርደትን ቅጣት ከእነሱ ላይ አነሳንላቸው፡፡ እስከ ጊዜም ድረስ አጣቀምናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,99,'10:99','ጌታህም በሻ ኖሮ በምድር ያሉት ሰዎች ሁሉም የተሰበሰቡ ሆነው ባመኑ ነበር፡፡ ታዲያ አንተ ሰዎችን አማኞች እንዲኾኑ ታስገድዳለህን');
INSERT INTO "sadiq_sani" VALUES(10,100,'10:100','ለማንኛዋም ነፍስ በአላህ ፈቃድ ቢሆን እንጂ ልታምን (ችሎታ) የላትም፡፡ (አላህ ለከፊሎቹ እምነትን ይሻል)፡፡ በእነዚያም በማያውቁት ላይ ርክሰትን ያደርጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(10,101,'10:101','«በሰማያትና በምድር ያለውን (ተዓምር) ተመልከቱ» በላቸው፡፡ ተዓምራቶችና አስፈራሪዎችም ለማያምኑ ሕዝቦች አይጠቅሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(10,102,'10:102','የእነዚያን ከእነሱ በፊት ያለፉትን ሕዝቦች ቀኖች ብጤ እንጂ ሌላን ይጠባበቃሉን «ተጠባበቁ፤ እኔ ከናንተ ጋር ከሚጠባበቁት ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,103,'10:103','ከዚያም መልክተኞቻችንን እነዚያንም ያመኑትን እናድናለን፡፡ እንደዚሁም ምእምናንን (ከጭንቅ ሁሉ) እናድናለን፡፡ (ይህ) በእኛ ላይ ተረጋገጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(10,104,'10:104','«እናንተ ሰዎች ሆይ! ከእኔ ሃይማኖት በመጠራጠር ውስጥ ብትሆኑ እነዚያን ከአላህ ሌላ የምትገዟቸውን አልገዛም፡፡ ግን ያንን የሚገድላችሁን አላህን እገዛለሁ፡፡ ከምእምናን እንድሆንም ታዝዣለሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,105,'10:105','ፊትህንም (ወደ ቀጥታ) ያዘነበልክ ስትሆን ለሃይማኖት ቀጥ አድርግ፡፡ ከአጋሪዎቹም አትሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(10,106,'10:106','«ከአላህም በቀር የማይጠቅምንና የማይጎዳን አትገዛ፡፡ ብትሠራም አንተ ያንጊዜ ከበደለኞቹ ትሆናለህ» (ተብያለሁ በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(10,107,'10:107','አላህም ጉዳትን ቢያደርስብህ ከእርሱ ሌላ ለእርሱ ገላጭ የለውም፡፡ በጎንም ነገር ቢሻልህ ለችሮታው መላሽ የለም፡፡ ከባሮቹ የሚሻውን በእርሱ ይለይበታል፡፡ እርሱም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,108,'10:108','«እናንተ ሰዎች ሆይ! እውነቱ ከጌታችሁ በእርግጥ መጣላችሁ፡፡ (በእርሱ) የተመራም ሰው የሚመራው ለራሱ ነው፡፡ የተሳሳተም ሰው የሚሳሳተው (ጉዳቱ) በራሱ ላይ ነው፡፡ እኔም በእናንተ ላይ ተጠባበቂ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(10,109,'10:109','ወዳንተም የሚወረደውን ነገር ተከተል፡፡ አላህም (በነሱ ላይ) እስከሚፈርድ ድረስ ታገስ፡፡ እርሱም ከፈራጆቹ ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,1,'11:1','አ.ለ.ረ (አሊፍ ላም ራ፤ ይህ ቁርኣን) አንቀጾቹ በጥንካሬ የተሰካኩ ከዚያም የተዘረዘሩ የሆነ መጽሐፍ ነው፡፡ ጥበበኛ ውስጠ ዐዋቂ ከሆነው ዘንድ (የተወረደ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,2,'11:2','(እንዲህ በላቸው)፡- «አላህን እንጂ ሌላን አትገዙ፡፡ እኔ ለእናንተ ከርሱ (የተላክሁ) አስፈራሪና አብሳሪ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,3,'11:3','ጌታችሁንም ምሕረትን ለምኑት፡፡ ከዚያም ወደእርሱ ተመለሱ፡፡ እስከተወሰነ ጊዜ ድረስ መልካምን መጥቀም ይጠቅማችኋልና፡፡ የትሩፋትንም ባለቤት ሁሉ ችሮታውን (ምንዳውን) ይሰጠዋል፡፡ ብትሸሹም እኔ በእናንተ ላይ የታላቁን ቀን ቅጣት እፈራላችኋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,4,'11:4','መመለሻችሁ ወደ አላህ ብቻ ነው፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,5,'11:5','ንቁ! እነሱ ከእርሱ (ከአላህ) ይደበቁ ዘንድ ደረቶቻቸውን ያጥፋሉ፡፡ ንቁ! ልብሶቻቸውን በሚከናነቡ ጊዜ የሚደብቁትን የሚገልጹትንም ሁሉ ያውቃል፡፡ እርሱ በደረቶች ውስጥ ያለውን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,6,'11:6','በምድርም ላይ ምንም ተንቀሳቃሽ የለችም ምግቧ በአላህ ላይ ያለ ቢኾን እንጂ፡፡ ማረፊያዋንም መርጊያዋንም ያውቃል፡፡ ሁሉም ግልጽ በሆነው መጽሐፍ ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,7,'11:7','እርሱም ያ ሰማያትንና ምድርን በስድስት ቀኖች ውስጥ የፈጠረ ነው፡፡ ዐርሹም በውሃ ላይ ነበር፡፡ የትኛችሁ ሥራው ያማረ መሆኑን ይፈትናችሁ ዘንድ (ፈጠራቸው)፡፡ «እናንተ ከሞት በኋላ በእርግጥ ተቀስቃሾች ናችሁ» ብትልም እነዚያ የካዱት ይህ ግልጽ ድግምት እንጂ ሌላ አይደለም ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,8,'11:8','ቅጣቱንም ወደ ተቆጠሩ (ጥቂት) ጊዜያቶች ከእነርሱ ብናቆይላቸው «(ከመውረድ) የሚከለክለው ምንድን ነው» ይላሉ፡፡ ንቁ! በሚመጣባቸው ቀን ከእነሱ ላይ ተመላሽ አይደለም፡፡ በእርሱ ይሳለቁበት የነበሩትም (ቅጣት) በእነርሱ ላይ ይወርድባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(11,9,'11:9','ሰውንም ከእኛ ችሮታን ብናቀምሰው ከዚያም ከርሱ ብንወስዳት እርሱ በእርግጥ ተስፋ ቆራጭ ክህደተ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,10,'11:10','ካገኘችውም ችግር በኋላ ጸጋዎችን ብናቀምሰው «ችግሮች ከኔ ላይ በእርግጥ ተወገዱ» ይላል (አያመሰግንም)፡፡ እርሱ ተደሳች ጉረኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,11,'11:11','ግን እነዚያ የታገሱ መልካም ሥራዎችንም የሠሩ እነዚያ ለእነሱ ምሕረትና ታላቅ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,12,'11:12','በእርሱ ላይ የገንዘብ ድልብ ለምን አልተወረደም ወይም ከርሱ ጋር መልአክ ለምን አልመጣም ማለታቸውንም በመፍራት ወደ አንተ ከሚወርደው ከፊሉን ልትተው በእርሱም ልብህ ጠባብ ሊኾን ይፈራልሃል፡፡ አንተ አስፈራሪ ብቻ ነህ፡፡ አላህም በነገሩ ሁሉ ተጠባባቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,13,'11:13','ይልቁንም «(ቁርኣንን) ቀጣጠፈው» ይላሉን፡- «እውነተኞች እንደሆናችሁ ብጤው የሆኑን ዐስር የተቀጣጠፉ ሱራዎች አምጡ፡፡ ከአላህም ሌላ የቻላችሁትን (ረዳት) ጥሩ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,14,'11:14','ለእናንተም (ጥሪውን) ባይቀበሏችሁ የተወረደው በአላህ እውቀት ብቻ መሆኑንና ከእርሱ ሌላ አምላክ አለመኖሩን ዕወቁ፡፡ ታዲያ እናንተ የምትሰልሙ ናችሁን (ስለሙ በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,15,'11:15','ቅርቢቱን ሕይወትና ጌጧን የሚሹ የሆኑትን ሰዎች ሥራዎቻቸውን (ምንዳዋን) በእርሷ ውስጥ ወደነሱ እንሞላላቸዋለን፡፡ እነሱም በርሷ ውስጥ ምንም አይጎድልባቸውም፤');
INSERT INTO "sadiq_sani" VALUES(11,16,'11:16','እነዚያ እነርሱ በመጨረሻይቱ ዓለም ለእነርሱ ከእሳት በቀር የሌላቸው ናቸው፡፡ የሠሩትም ሥራ በርሷ ውስጥ ተበላሸ፡፡ (በቅርቢቱ ዓለም) ይሠሩት የነበሩትም በጎ ሥራ ብልሹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,17,'11:17','ከጌታው ከአስረጅ ጋር የኾነ ሰው፤ ከእርሱም (ከአላህ) የኾነ መስካሪ የሚከተለው፣ ከእርሱ በፊትም የሙሳ መጽሐፍ መሪና እዝነት ሲኾን (የመሰከረለት) የቅርቢቱን ሕይወት እንደሚሻው ሰው ነውን እነዚያ በእርሱ (በቁርኣን) ያምናሉ፡፡ ከአሕዛቦቹም በእርሱ የሚክድ ሰው እሳት መመለሻው ናት፡፡ ከእርሱም በመጠራጠር ውስጥ አትሁን፡፡ እርሱ ከጌታህ የሆነ እውነት ነው፡፡ ግን አብዛኞቹ ሰዎች አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,18,'11:18','በአላህ ላይ እብለትን ከሚቀጣጥፍም ይበልጥ በዳይ ማነው እነዚያ በጌታቸው ላይ ይቀርባሉ፡፡ መስካሪዎቹም «እነዚህ በጌታቸው ላይ የዋሹት ናቸው» ይላሉ፡፡ ንቁ! የአላህ ርግማን በበዳዮች ላይ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(11,19,'11:19','(እነሱም) እነዚያ ከአላህ መንገድ የሚከለክሉ መጥመሟንም የሚፈልጓት ናቸው፡፡ እነሱም መጨረሻይቱን ዓለም እነሱ ከሓዲዎች ናቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(11,20,'11:20','እነዚያ በምድር ውስጥ (ከአላህ) የሚያመልጡ አልነበሩም፡፡ ከአላህም ሌላ ለእነርሱ ረዳቶች አልነበሯቸውም፡፡ ለእነሱ ቅጣቱ ይደራረብላቸዋል፡፡ (እውነትን) መስማትን የሚችሉ አልነበሩም የሚያዩም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,21,'11:21','እነዚያ እነርሱ ነፍሶቻቸውን ያከሰሩ ከእነሱም ይቀጣጥፉት የነበሩት ነገር የጠፋቸው ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,22,'11:22','እነርሱ በመጨረሻይቱ ዓለም በጣም ከሳሪዎቹ እነሱ መሆናቸው ጥርጥር የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,23,'11:23','እነዚያ ያመኑትና በጎ ሥራዎችን የሠሩት ወደ ጌታቸውም የተዋረዱት እነዚያ የገነት ሰዎች ናቸው፡፡ እነሱ በውስጧ ዘላለም ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,24,'11:24','የሁለቱ ክፍሎች ምሳሌ እንደ ዕውርና እንደ ደንቆሮ እንደሚያይና እንደሚሰማም ብጤ ነው፡፡ በምሳሌ ይተካከላሉን አትገሰጹምን');
INSERT INTO "sadiq_sani" VALUES(11,25,'11:25','ኑሕንም ወደ ሕዝቦቹ በእርግጥ ላክን፡፡ (አላቸውም)፡- «እኔ ለእናንተ ግልጽ አስፈራሪ (አስጠንቃቂ) ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,26,'11:26','«አላህን እንጂ ሌላን አትገዙ፡፡ እኔ በእናንተ ላይ የአሳማሚን ቀን ቅጣት እፈራላችኋለሁና፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,27,'11:27','ከሕዝቦቹም እነዚያ የካዱት መሪዎቹ ብጤያችን ሰው ሆነህ እንጂ አናይህም፡፡ እነዚያም እነሱ ሐሳበ ጥሬዎች የሆኑት ወራዶቻችን እንጂ ሌላ ተከትሎህ አናይህም፡፡ ለእናንተም በእኛ ላይ ምንም ብልጫን አናይም፡፡ ከቶውንም ውሸታሞች መሆናችሁን እንጠረጥራችኋለን አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,28,'11:28','«ሕዝቦቼ ሆይ! ንገሩኝ ከጌታዬ በግልጽ ማስረጃ ላይ ብሆን ከእርሱም ዘንድ የሆነን ችሮታ (ነብይነት) ቢሰጠኝና በእናንተ ላይ ብትደበቅባችሁ እናንተ ለርቯ ጠይዎች ስትሆኑ እርቯን (በመቀበል) እናስገድዳችኋለን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,29,'11:29','«ሕዝቦቼም ሆይ! በርሱ (በተላክሁበት ማድረስ) ላይ ገንዘብን አልጠይቃችሁም፡፡ ምንዳዬ በአላህ ላይ እንጂ በሌላ ላይ አይደለም፡፡ እኔም እነዚያን ያመኑትን አባራሪ አይደለሁም፡፡ እነርሱ ጌታቸውን የሚገናኙ ናቸውና፡፡ ግን እኔም የምትሳሳቱ ሕዝቦች ሆናችሁ አያችኋለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,30,'11:30','«ሕዝቦቼም ሆይ ባባርራቸው ከአላህ (ቅጣት) የሚያድነኝ ማን ነው አትገሰጹምን»');
INSERT INTO "sadiq_sani" VALUES(11,31,'11:31','«ለእናንተም እኔ ዘንድ የአላህ ግምጃ ቤቶች አልሉ ሩቅንም ዐውቃለሁ አልላችሁም፡፡ እኔም መልአክ ነኝ አልላችሁም፡፡ ለእነዚያም ዓይኖቻችሁ ለሚያዋርዷቸው አላህ ደግ ነገርን (እምነትን) አይሰጣቸውም አልልም፡፡ አላህ በነፍሶቻቸው ውስጥ ያለውን ነገር አዋቂ ነው፡፡ እኔ ያን ጊዜ ከበደለኞች እሆናለሁና» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,32,'11:32','«ኑህ ሆይ! በእርግጥ ተከራከርከን፡፡ እኛን መከራከርህንም አበዛኸው፡፡ ከእውነተኞቹም እንደሆንክ የምታስፈራራብንን ቅጣት አምጣው» አሉ፡');
INSERT INTO "sadiq_sani" VALUES(11,33,'11:33','«እርሱን የሚያመጣባችሁ የሻ እንደ ሆነ አላህ ብቻ ነው፡፡ እናንተም የምታቅቱ አይደላችሁም» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,34,'11:34','«ለእናንተም ልመክራችሁ ብሻ አላህ ሊያጠማችሁ ሽቶ እንደ ኾነ ምክሬ አይጠቅማችሁም፡፡ እርሱ ጌታችሁ ነው፡፡ ወደ እርሱም ትመለሳላችሁ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,35,'11:35','«ይልቁንም ቀጠፈው ይላሉን ብቀጥፈው ኃጢኣቴ (ቅጣቱ) በኔ ላይ ነው፡፡ እኔም ከምታጠፉት (ጥፋት) ንጹሕ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,36,'11:36','ወደ ኑሕም እነሆ «ከሕዝቦችህ በእርግጥ ካመኑት በስተቀር (ወደፊት) አያምኑም፡፡ ይሠሩትም በነበሩት (ክህደት) አትዘን ማለት ተወረደ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,37,'11:37','(አላህም) «በጥበቃችንና በትእዛዛችንም ሆነህ መርከቢቱን ሥራ፡፡ በእነዚያም በበደሉት ሰዎች ነገር አታናግረኝ እነሱ በእርግጥ ሰማጮች ናቸውና» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,38,'11:38','ከወገኖቹም መሪዎቹ በእርሱ አጠገብ ባለፉ ቁጥር ከእርሱ እየተሳለቁ መርከቢቱን ይሠራል፡፡ «ከእኛ ብትሳለቁ እኛም እንደተሳለቃችሁብን ከናንተ እንሳለቅባችኋለን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,39,'11:39','«የሚያዋርደው ቅጣት የሚመጣበትን በእርሱም ላይ ዘውታሪ ቅጣት የሚሰፍርበት ሰው (ማን እንደ ሆነ) ወደፊት ታውቃላችሁ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,40,'11:40','ትእዛዛችንም በመጣና እቶኑም በገነፈለ ጊዜ «በእርሷ ውስጥ ከየዓይነቱ ሁሉ ሁለት ሁለት (ወንድና ሴት) ፤ ቤተሰቦችህንም ቃል ያለፈበት ብቻ ሲቀር ያመነንም ሰው ሁሉ ጫን» አልነው፡፡ ከእርሱም ጋር ጥቂቶች እንጂ አላመኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,41,'11:41','«መሄዷም መቆሟም በአላህ ስም ነው እያላችሁም በውስጧ ተሳፈሩ፡፡ ጌታ መሓሪ አዛኝ ነውና» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,42,'11:42','እርሷም እንደ ተራራዎች በሆነ ማዕበል ውስጥ በእነርሱ (ይዛቸው) የምትንሻለል ስትሆን (በአላህ ስም ተቀመጡባት)፡፡ ኑሕም ልጁን ከመርከቢቱ የራቀ ሆኖ ሳለ «ልጄ ሆይ! ከኛ ጋር ተሳፈር ከከሓዲዎቹም አትሁን» ሲል ጠራው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,43,'11:43','(ልጁም) «ከውሃው ወደሚጠብቀኝ ተራራ እጠጋለሁ» አለ፡፡ (አባቱም)፡- «ዛሬ ከአላህ ትዕዛዝ ምንም ጠባቂ የለም (እርሱ) ያዘነለት ካልሆነ በቀር» አለው፡፡ ማዕበሉም በመካከላቸው ጋረደ፡፡ ከሰጣሚዎቹም ሆነ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,44,'11:44','ተባለም፡- «ምድር ሆይ! ውሃሽን ዋጪ፡፡ ሰማይም ሆይ (ዝናብሽን) ያዢ፡፡ ውሃውም ሰረገ፡፡ ቅጣቱም ተፈጸም፡፡ ጁዲይ በሚባልም ተራራ ላይ (መርከቢቱ) ተደላደለች፡፡ ለከሓዲዎችም ሰዎች ጥፋት ተገባቸው (ጠፉ)» ተባለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,45,'11:45','ኑሕም ጌታውን ጠራ፡፡ አለም «ጌታዬ ሆይ! ልጄ ከቤተሰቤ ነው፡፡ ኪዳንህም እውነት ነው፡፡ አንተም ከፈራጆች ሁሉ ይበልጥ ዐዋቂ ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,46,'11:46','(አላህም) «ኑሕ ሆይ! እርሱ ከቤተሰብህ አይደለም፤ እርሱ መልካም ያልሆነ ሥራ ነው፤ በርሱ ዕውቀት የሌለህንም ነገር አትጠይቀኝ፤ እኔ ከሚሳሳቱት ሰዎች እንዳትሆን እገስጽሀለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,47,'11:47','«ጌታዬ ሆይ! በእርሱ ለእኔ ዕውቀት የሌለኝን ነገር የምጠይቅህ ከመሆን እኔ ባንተ እጠበቃለሁ፡፡ ለእኔም ባትምረኝና ባታዝንልኝ ከከሳሪዎቹ እሆናለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,48,'11:48','«ኑሕ ሆይ! ከእኛ በሆነ ሰላም ባንተ ላይና አንተም ጋር ባሉት ሕዝቦች (ትውልድ) ላይ በሆኑ በረከቶችም የተጎናጸፍክ ሆነህ ውረድ፡፡ (ከእነሱው ዘሮች የሆኑ) ሕዝቦችም በቅርቢቱ ዓለም በእርግጥ እናስመቻቸዋለን፡፡ ከዚያም ከኛ የሆነ አሳማሚ ቅጣት ይነካቸዋል» ተባለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,49,'11:49','ይህቺ ከሩቁ ወሬዎች ናት፡፡ ወደ አንተ እናወርዳታለን፡፡ አንተም ሕዝቦችህም ከዚህ በፊት የምታውቋት አልነበራችሁም፡፡ ታገስም፤ ምስጉን የሆነችው ፍጻሜ ለሚጠነቀቁት ናትና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,50,'11:50','ወደ ዓድም ወንድማቸውን ሁድን (ላክን)፡፡ አላቸው፡- «ወገኖቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ምንም አምላክ የላችሁም፡፡ እናንተም ቀጣፊዎች እንጂ ሌላ አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,51,'11:51','«ሕዝቦቼ ሆይ! በእርሱ (በማድረሴ) ላይ ምንዳን አልጠይቃችሁም፡፡ ምንዳዬ በዚህ በፈጠረኝ ላይ እንጂ በሌላ ላይ አይደለም፡፡ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(11,52,'11:52','«ሕዝቦቼም ሆይ! ጌታችሁን ምሕረትን ለምኑት፡፡ ከዚያም ወደርሱ ተጸጸቱ፡፡ ዝናብን በእናንተ ላይ ተከታታይ አድርጎ ይልክላችኋልና፡፡ ወደ ኃይላችሁም ኃይልን ይጨምርላችኋል፡፡ አመጸኞችም ሆናችሁ አትሽሹ፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,53,'11:53','አሉ፡- «ሁድ ሆይ! በአስረጅ አልመጣህልንም፡፡ እኛም ላንተ ንግግር ብለን አማልክቶቻችንን የምንተው አይደለንም፡፡ እኛም ለአንተ አማኞች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,54,'11:54','«ከፊሎቹ አማልክቶቻችን በክፉ ነገር (በዕብደት) ለክፈውሃል እንጂ ሌላን አንልም» (አሉ)፡፡ «እኔ አላህን አስመሰክራለሁ፡፡ ከምታጋሩትም እኔ ንጹሕ መሆኔን መስክሩ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,55,'11:55','ከእርሱ ሌላ (አማልክትን ከምታጋሩት ንጹህ ነኝ)፡፡ ሁላችሁም ሆናችሁ ተንኮልን ሥሩብኝ፤ ከዚያም አታቆዩኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,56,'11:56','«እኔ በጌታዬና በጌታችሁ በአላህ ላይ ተጠጋሁ፡፡ በምድር ላይ ምንም ተንቀሳቃሽ የለችም እርሱ አናትዋን የያዛት ብትሆን እንጂ፡፡ ጌታዬ (ቃሉም ሥራውም) በቀጥተኛው መንገድ ላይ ነው» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,57,'11:57','«ብትዞሩም በእርሱ ወደእናንተ የተላክሁበትን ነገር በእርግጥ አድርሼላችኋለሁ፡፡ ጌታዬ ከእናንተ ሌላ ሕዝብም ይተካል ምንም አትጎዱትምም፡፡ ጌታዬ በነገሩ ሁሉ ላይ ተጠባባቂ ነውና» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,58,'11:58','ትዕዛዛችንም በመጣ ጊዜ ሁድንና እነዚያን ከእሱ ጋር ያመኑትን ከእኛ በሆነው ችሮታ አዳን፡፡ ከብርቱ ቅጣትም አዳናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,59,'11:59','ይህች (ነገድ) ዓድ ናት፡፡ በጌታቸው ታምራት ካዱ፡፡ መልክተኞቹንም አመጹ፤ የኃያል ሞገደኛን ሁሉ ትዕዛዝም ተከተሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,60,'11:60','በዚህች በቅርቢቱ ዓለምም እርግማን እንዲከተላቸው ተደረጉ፡፡ በትንሣኤም ቀን (እንደዚሁ)፡፡ ንቁ! ዓዶች ጌታቸውን ካዱ፡፡ ንቁ! የሁድ ሕዝቦች ለሆኑት ዓዶች (ከእዝነት) መራቅ ይገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,61,'11:61','ወደ ሰሙዶችም ወንድማቸውን ሷሊህን (ላክን)፡፡ «ሕዝቦቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ለእናንተ አምላክ የላችሁም፡፡ እርሱ ከምድር ፈጠራችሁ፡፡ በውስጧ እንድታለሟትም አደረጋችሁ፡፡ ምሕረቱንም ለምኑት፡፡ ከዚህም ወደእርሱ ተመለሱ፡፡ ጌታዬ ቅርብ (ለለመነው) ተቀባይ ነውና» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,62,'11:62','«ሷሊህ ሆይ! ከዚህ በፊት በእኛ ውስጥ (መሪ ልትሆን) በእርግጥ የምትከጅል ነበርክ፡፡ አባቶቻችን የሚገዙትን ከመገዛት ትከለክለናለህን እኛም ወደርሱ ከምትጠራን ነገር በእርግጥ አወላዋይ በሆነ መጠራጠር ውስጥ ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,63,'11:63','«ሕዝቦቼ ሆይ! ንገሩኝ ከጌታዬ በሆነ ማስረጃ ላይ ብሆን ከእርሱም የሆነን ችሮታ (ነቢይነት) ቢሰጠኝና ባልታዘዘው ከአላህ (ቅጣት) የሚያድነኝ ማነው ከማሳሳትም በስተቀር ምንም አትጨምሩልኝም» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,64,'11:64','ሕዝቦቼም ሆይ! ይህቺ ለእናንተ ተዓምር ስትሆን የአላህ ግመል ናት፡፡ ተውዋትም፡፡ በአላህ ምድር ውስጥ ትብላ፡፡ በክፉም አትንኳት፡፡ ቅርብ የሆነ ቅጣት ይይዛችኋልና» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,65,'11:65','ወግተው ገደሏትም፡፡ (ሷሊህ) «በአገራችሁም ሶስትን ቀናት (ብቻ) ተጠቀሙ፡፡ ይህ የማይዋሽ ቀጠሮ ነው» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,66,'11:66','ትዕዛዛችንም በመጣ ጊዜ ሷሊህንና እነዚያን ከሱ ጋር ያመኑትን ከእኛ በሆነው እዝነት አዳን፡፡ ከዚያም ቀን ውርደት (አዳንናቸው)፡፡ ጌታህ እርሱ ብርቱው አሸናፊው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,67,'11:67','እነዚያንም የበደሉትን ጩኸት ያዛቸው፡፡ በቤቶቻቸውም ውስጥ የተንበረከኩ ሆነው ሞተው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,68,'11:68','በእሷ ውስጥ እንዳልኖሩባት ሆኑ፡፡ ንቁ! ሰሙዶች ጌታቸውን በእርግጥ ካዱ፡፡ ንቁ! ለሰሙዶች (ከአላህ እዝነት) መራቅ ተገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,69,'11:69','መልክተኞቻችንም ኢብራሂምን በ(ልጅ) ብስራት በእርግጥ መጡት፡፡ ሰላም አሉት፡፡ ሰላም አላቸው፡፡ ጥቂትም ሳይቆይ ወዲያውኑ የተጠበሰን የወይፈን ስጋ አመጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,70,'11:70','እጆቻቸውም ወደ እርሱ የማይደርሱ መኾነቸውን ባየ ጊዜ ሸሻቸው፡፡ ከነሱም ፍርሃት ተሰማው፡፡ «አትፍራ እኛ ወደ ሉጥ ሕዝቦች ተልከናልና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(11,71,'11:71','ሚስቱም የቆመች ስትኾን (አትፍራ አሉት) ሳቀችም፡፡ በኢስሐቅም አበሰርናት፡፡ ከኢስሐቅም በኋላ (በልጁ) በያዕቁብ (አበሰርናት)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,72,'11:72','(እርሷም) ዋልኝ! እኔ አሮጊት ይህም ባሌ ሽማግሌ ሆኖ ሳለ እወልዳለሁን ይህ በእርግጥ አስደናቂ ነገር ነው አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(11,73,'11:73','«ከአላህ ትዕዛዝ ትደነቂያለሽን የአላህ ችሮታና በረከቶቹ በእናንተ በኢብራሂም ቤተሰቦች ላይ ይሁን እርሱ ምስጉን ለጋስ ነውና» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,74,'11:74','ከኢብራሂም ፍራቻው በሌደለትና ብስራት በመጣችለትም ጊዜ በሉጥ ሕዝቦች (ነገር) ይከራከረን ጀመር፡፡');
INSERT INTO "sadiq_sani" VALUES(11,75,'11:75','ኢብራሂም በእርግጥ ታጋሽ አልቃሻ መላሳ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,76,'11:76','«ኢብራሂም ሆይ! ከዚህ (ክርክር) ተው፡፡ እነሆ የጌታህ ትዕዛዝ በእርግጥ መጥቷል፡፡ እነሱም የማይመለስ ቅጣት የሚመጣባቸው ናቸው» (አሉት)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,77,'11:77','መልክተኞቻችንም ሉጥን በመጡት ጊዜ በነሱ (ምክንያት) አዘነ፡፡ ልቡም በነሱ ተጨነቀ፡፡ «ይህ ብርቱ ቀን ነውም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,78,'11:78','ሕዝቦቹም ወደርሱ እየተጣደፉ መጡት፡፡ ከዚህም በፊት መጥፎ ሥራዎችን ይሠሩ ነበር፡፡ «ሕዝቦቼ ሆይ! እኝህ ሴቶች ልጆቼ ናቸው፡፡ እነሱ ለእናንተ ይልቅ በጣም የጸዱ ናቸው፤ (አግቧቸው)፡፡ አላህንም ፍሩ፡፡ በእንግዶቼም አታሳፍሩኝ፡፡ ከእናንተ ውስጥ ቅን ሰው የለምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,79,'11:79','«ከሴቶች ልጆችህ ለእኛ ምንም ጉዳይ የለንም፡፡ አንተም የምንሻውን በእርግጥ ታውቃለህ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(11,80,'11:80','«በእናንተ ላይ ለኔ ኀይል በኖረኝ ወይም ወደ ብርቱ ወገን ብጠጋ ኖሮ (የምሠራውን በሠራሁ ነበር)» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,81,'11:81','«ሉጥ ሆይ! እኛ የጌታህ መልክተኞች ነን፡፡ (ሕዝቦችህ) ወደ አንተ (በክፉ) አይደርሱብህም፡፡ ቤተሰብህንም ይዘህ ከሌሊቱ በከፊሉ ውስጥ ሊድ፡፡ ከእናንተም አንድም (ወደኋላው) አይገላመጥ፡፡ ሚስትህ ብቻ ስትቀር፡፡ እነሆ እርሷን (እነሱን) የሚያገኛቸው ስቃይ ያገኛታልና፡፡ ቀጠሯቸው እንጋቱ ላይ ነው፡፡ ንጋቱ ቅርብ አይደለምን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(11,82,'11:82','|ትዕዛዛችንም በመጣ ጊዜ (ከተማይቱን) ላይዋን ከታችዋ አደረግን (ገለበጥናት)፡፡ ተከታታይም የሆነን የሸክላ ድንጋይ በርሷ ላይ አዘነብን፡፡');
INSERT INTO "sadiq_sani" VALUES(11,83,'11:83','ከጌታህ ዘንድ ምልክት የተደረገባት ስትሆን (አዘነብናት)፡፡ እርሷም ከበደለኞቹ ሩቅ አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,84,'11:84','ወደ መድየንም ወንድማቸውን ሹዐይብን (ላክን)፡፡ አላቸው «ሕዝቦቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ለናንተ አምላክ የላችሁም፡፡ ስፍርንና ሚዛንንም አታጉድሉ፤ እኔ በጸጋ ላይ ሆናችሁ አያችኋለሁ፡፡ እኔም በእናንተ ላይ የከባቢን ቀን ቅጣት እፈራለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,85,'11:85','«ሕዝቦቼም ሆይ! ስፍርንና ሚዛንን በትክክል ሙሉ፡፡ ሰዎችንም ምንም ነገራቸውን አታጉድሉባቸው፡፡ በምድርም ውስጥ አጥፊዎች ሆናችሁ አታበላሹ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,86,'11:86','አላህ ያስቀረላችሁ ለእናንተ የተሻለ ነው፡፡ ምእመናን እንደሆናችሁ (አላህ በሰጣችሁ ውደዱ)፡፡ እኔም (መካሪ እንጅ) በናንተ ላይ ተጠባባቂ አይደለሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(11,87,'11:87','«ሹዐይብ ሆይ! ስግደትህ አባቶቻችን የሚገዙትን ጣዖታት እንድንተው ወይም በገንዘቦቻችን የምንሻውን መሥራትን (እንድንተው) ታዝሃለችን አንተ በእርግጥ ታጋሹ ቅኑ አንተ ነህና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(11,88,'11:88','«ሕዝቦቼ ሆይ! ንገሩኝ፤ ከጌታዬ በሆነ አስረጅ ላይ ብሆንና ከእርሱም የሆነን መልካም ሲሳይ ቢሰጠኝ (በቅጥፈት ልቀላቅለው ይገባልን) ከእርሱ ወደ ከለከልኳችሁም ነገር ልለያችሁ አልሻም፡፡ በተቻለኝ ያክል ማበጀትን እንጂ አልሻም (ለደግ ሥራ) መገጠሜም በአላህ እንጂ በሌላ አይደለም፡፡ በእርሱ ላይ ተመካሁ፡፡ ወደእርሱም እመለሳለሁ፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,89,'11:89','«ወገኖቼም ሆይ! እኔን መከራከራችሁ የኑሕን ሕዝቦች ወይም የሁድን ሕዝቦች ወይም የሷሊሕን ሕዝቦች ያገኛቸው (ቅጣት) ብጤ እንዲያገኛችሁ አይገፋፋችሁ፡፡ የሉጥም ሕዝቦች ከእናንተ ሩቅ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,90,'11:90','«ጌታችሁንም ምሕረትን ለምኑት፡፡ ከዚያም ወደርሱ ተመለሱ፡፡ ጌታዬ አዛኝ ወዳድ ነውና (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,91,'11:91','«ሹዐይብ ሆይ! ከምትለው ነገር ብዙውን አናውቀውም፡፡ እኛም አንተን በእኛ ውስጥ ደካማ ሆነህ እናይሃለን፡፡ ጎሳዎችህም ባልኖሩ ኖሮ በወገርንህ ነበር፤ አንተም በእኛ ላይ የተከበርክ አይደለህም» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(11,92,'11:92','«ሕዝቦቼ ሆይ! ጎሳዎቼ በእናንተ ላይ ከአላህ ይልቅ የከበሩ ናቸውን (አላህን) ከኋላችሁ ወደ ጀርባ አድርጋችሁም ያዛችሁት፡፡ ጌታዬ በምትሠሩት ሁሉ ከባቢ ነው» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,93,'11:93','«ሕዝቦቼም ሆይ! በችሎታችሁ ልክ ሥሩ፡፡ እኔ ሠሪ ነኝና፡፡ የሚያዋርደው ቅጣት የሚመጣበትና እርሱ ውሸታም የሆነው ማን እንደኾነ ወደ ፊት ታውቃላችሁ፡፡ ጠብቁም እኔ ከናንተ ጋር ተጠባባቂ ነኝና»(አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,94,'11:94','ትዕዛዛችንም በመጣ ጊዜ ሹዐይብንና እነዚያን ከርሱ ጋር ያመኑትን ከእኛ በሆነው ችሮታ አዳን፡፡ እነዚያን የበደሉትንም (የጂብሪል) ጩኸት ያዘቻቸው፡፡ በቤቶቻቸውም ውስጥ ተንከፍርረው አደሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,95,'11:95','በእርሷ ውስጥ እንዳልነበሩ ሆኑ፡፡ ንቁ! ሰሙድ (ከአላህ እዝነት) እንደ ራቀች መድየንም ትራቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,96,'11:96','ሙሳንም በተዓምራታችንና በግልጽ ብርሃን በእርግጥ ላክነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,97,'11:97','ወደ ፈርዖንና ወደ ሰዎቹ (ላክነው)፡፡ የፈርዖንንም ነገር (ሕዝቦቹ) ተከተሉ፡፡ የፈርዖን ነገርም ቀጥተኛ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,98,'11:98','በትንሣኤ ቀን ሕዝቦቹን ይቀድማል፡፡ ወደ እሳትም ያወርዳቸዋል፡፡ የሚገቡትም አገባብ ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(11,99,'11:99','በዚችም (በቅርቢቱ ዓለም) እርግማንን አስከተልናቸው፡፡ በትንሣኤም ቀን (እንደዚሁ)፡፡ የተሰጡት ስጦታ ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(11,100,'11:100','ይህ (የተነገረው) ከከተሞቹ ወሬዎች ነው፡፡ ባንተ ላይ እንተርከዋለን፡፡ ከእርሷ ፋናው የቀረና ሙሉ በሙሉ የተደመሰሰ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,101,'11:101','እኛም አልበደልናቸውም፡፡ ግን ነፍሶቻቸውን በደሉ፡፡ የአላህም ትእዛዝ በመጣ ጊዜ እነዚያ ከአላህ ሌላ የሚገዙዋቸው አማልክቶቻቸው በምንም አላዳኗቸውም፡፡ ከማክሰርም በቀር ምንም አልጨመሩላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,102,'11:102','የጌታህም ቅጣት የከተሞችን ሰዎች እነሱ በዳዮች ሲሆኑ በቀጣቸው ጊዜ እንደዚህ ነው፡፡ ቅጣቱ በእርግጥ አሳማሚ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,103,'11:103','በዚህ ውስጥ የመጨረሻውን ቀን ቅጣት ለሚፈሩ ሁሉ መገሰጫ አለ፡፡ ይህ (የትንሣኤ ቀን) ሰዎች በርሱ የሚሰበሰቡበት ቀን ነው፡፡ ይህም የሚጣዱት ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,104,'11:104','(ይህንን ቀን) ለተቆጠረም ጊዜ እንጂ አናቆየውም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,105,'11:105','በሚመጣ ቀን ማንኛዋም ነፍስ በእርሱ ፈቃድ ቢሆን እንጂ አትናገርም፡፡ ከነሱም መናጢና ዕድለኛም አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,106,'11:106','እነዚያ መናጢ የሆኑትማ፡ በእሳት ውስጥ ናቸው፡፡ ለእነርሱ በእርሷ ውስጥ ማናፋትና መንሰቅሰቅ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,107,'11:107','ጌታህ ከሻው (ጭማሬ) ሌላ ሰማያትና ምድር እስከ ዘወተሩ ድረስ በውስጧ ዘውተሪዎች ሲኾኑ (በእሳት ይኖራሉ)፡፡ ጌታህ የሚሻውን ሠሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,108,'11:108','እነዚያም ዕድለኞቹማ ጌታህ ከሻው (ጭማሬ) ሌላ ሰማያትና ምድር እስከ ዘወተሩ ድረስ በውስጧ ዘውታሪዎች ኾነው በገነት ውስጥ ይኖራሉ፡፡ የማይቋረጥ ስጦታን ተሰጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,109,'11:109','እነዚህ (ከሓዲዎች) ከሚገዙት ጣዖት በመጠራጠር ውስጥ አትሁን፤ አባቶቻቸው ከዚህ በፊት እንደሚግገዙት እንጂ አይግገዙም፡፡ እኛም (እነዚህን) ፈንታቸውን የማይጓደል ሲሆን የምንሞላላቸው ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(11,110,'11:110','ለሙሳም መጽሐፍን በእርግጥ ሰጠነው፡፡ በእርሱም ተለያዩበት፡፡ ከጌታህም ያለፈች ቃል ባልነበረች ኖሮ በመካከላቸው (አሁን) ይፈረድ ነበር፡፡ እነሱም ከእርሱ (ከቁርኣን) አወላዋይ በሆነ መጠራጠር ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,111,'11:111','ሁሉንም ጌታህ ሥራዎቻቸውን (ምንዳቸውን) በእርግጥ ይሞላላቸዋል፡፡ እርሱ በሚሠሩት ሁሉ ውስጠ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,112,'11:112','እንደ ታዘዝከውም ቀጥ በል፡፡ ከአንተ ጋር ያመኑትም (ቀጥ ይበሉ)፡፡ ወሰንንም አትለፉ፤ እርሱ የምትሠሩትን ሁሉ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,113,'11:113','ወደእነዚያም ወደ በደሉት አትጠጉ፡፡ እሳት ትነካችኋለችና፡፡ ከአላህም ሌላ ጠባቂዎች የሏችሁም፡፡ ከዚያም አትረድዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,114,'11:114','ሶላትንም በቀን ጫፎች ከሌሊትም ክፍሎች ፈጽም፡፡ መልካም ሥራዎች ኀጢአቶችን ያስወገድዳሉና፡፡ ይህ ለተገሳጮች ግሳጼ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,115,'11:115','ታገስም፤ አላህ የበጎ ሠሪዎችን ምንዳ አያጠፋምና፡፡');
INSERT INTO "sadiq_sani" VALUES(11,116,'11:116','ከእናንተም በፊት ከነበሩት ከክፍለ ዘመናት ሰዎች ውስጥ በምድር ላይ ከማበላሸት የሚከለክሉ የመልካም ቀሪ ሥራዎች ባለቤቶች ለምን አልነበሩም ግን ከእነሱ ያዳንናቸው ጥቂቶቹ (ከለከሉና ዳኑ)፡፡ እነዚያም የበደሉት ሰዎች (አልከለከሉም)፡፡ በእርሱ የተቀማጠሉበትን ተድላ ተከተሉ፡፡ አመጸኞችም ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(11,117,'11:117','ጌታህም ከተሞችን ባለቤቶቻቸው መልካም ሠሪዎች ሆነው ሳሉ በመበደል የሚያጠፋቸው አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,118,'11:118','ጌታህም በሻ ኖሮ ሰዎችን ሁሉ አንድ ሕዝብ ባደረጋቸው ነበር፡፡ የተለያዩም ከመሆን አይወገዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(11,119,'11:119','ጌታህ ያዘነለት ብቻ ሲቀር፤ (ከመለያየት አይወገዱም)፡፡ ለዚሁም ፈጠራቸው፡፡ የጌታህም ቃል ገሀነምን ከአጋንንትና ከሰዎች ከሁሉም በእርግጥ እሞላታለሁ በማለት ተፈጸመች፡፡');
INSERT INTO "sadiq_sani" VALUES(11,120,'11:120','ከመልክተኞቹም ዜናዎች (ተፈላጊውን) ሁሉንም ልብህን በርሱ የምናረካበትን እንተርክልሃለን፡፡ በዚህችም (ሱራ) እውነቱ ነገር ለምእምናን ግሳጼና ማስታወሻ መጥቶልሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(11,121,'11:121','ለእነዚያም ለማያምኑት ባላችሁበት ሁኔታ ላይ ሥሩ፤ እኛ ሠሪዎች ነንና በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(11,122,'11:122','ተጠባበቁም እኛ ተጠባባቂዎች ነንና (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(11,123,'11:123','በሰማያትና በምድርም ያለው ምስጢር ሁሉ የአላህ ነው፡፡ ነገሩ ሁሉም ወደርሱ ይመለሳል፡፡ ስለዚህ ተገዛው፡፡ በእርሱም ላይ ተጠጋ፡፡ ጌታህም ከሚሠሩት ሁሉ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,1,'12:1','አ.ለ.ረ (አሊፍ ላም ራ) እነዚህ የገላጩ መጽሐፍ አንቀጾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,2,'12:2','እኛ (ፍቹን) ታውቁ ዘንድ ዐረብኛ ቁርኣን ሲሆን በእርግጥ አወረድነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,3,'12:3','እኛ ይህንን ቁርኣን ወደ አንተ በማውረዳችን በጣም መልካም ዜናዎችን በአንተ ላይ እንተርክልሃለን፡፡ እነሆ ከእርሱ በፊት (ካለፉት ሕዝቦች ታሪክ) በእርግጥ ከዘንጊዎቹ ነበርክ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,4,'12:4','ዩሱፍ ለአባቱ፡- «አባቴ ሆይ! እኔ ዐስራ አንድ ከዋክብትን ፀሐይንና ጨረቃንም (በሕልሜ) አየሁ፡፡ ለእኔ ሰጋጆች ሆነው አየኋቸው» ባለ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,5,'12:5','(አባቱም) አለ «ልጄ ሆይ! ሕልምህን ለወንድሞችህ አታውራ፡፡ ላንተ ተንኮልን ይሰሩብሃልና፡፡ ሰይጣን ለሰው ግልጽ ጠላት ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,6,'12:6','«እንደዚሁም (እንዳየኸው) ጌታህ ይመርጥሃል፡፡ ከንግግሮችም ፍች ያስተምርሃል፡፡ ጸጋውንም ከአሁን በፊት በሁለቱ አባቶችህ በኢብራሂምና በኢስሐቅ ላይ እንዳሟላት በአንተ ላይና በያዕቆብ ዘሮችም ላይ ይሞላታል፡፡ ጌታህ ዐዋቂ ጥበበኛ ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,7,'12:7','በዩሱፍና በወንድሞቹ (ታሪኮች) ውስጥ ለጠያቂዎች ሁሉ በእርግጥ አስደናቂ ምልክቶች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,8,'12:8','(ወንድሞቹ) ባሉ ጊዜ (አስታውስ)፡፡ «እኛ ጭፍሮች ስንሆን ዩሱፍና ወንድሙ (ብንያም) ወደ አባታችን ከእኛ ይበልጥ የተወደዱ ናቸው፡፡ አባታችን በግልጽ ስህተት ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,9,'12:9','«ዩሱፍን ግደሉ፡፡ ወይም በ(ሩቅ) ምድር ላይ ጣሉት፡፡ ያባታችሁ ፊት ለእናንተ የግል ይኾናልና፡፡ ከእርሱም በኋላ መልካም ሕዝቦች ትሆናላችሁና» (ተባባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,10,'12:10','ከእነሱ አንድ ተናጋሪ «ዩሱፍን አትግደሉ ግን በጉድጓድ አዘቅት ጨለማ ውስጥ ጣሉት፤ ከተጓዢዎች አንዱ ያነሳዋልና፡፡ ሠሪዎች ብትኾኑ (በዚሁ ተብቃቁ) አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,11,'12:11','(እነሱም) አሉ «አባታችን ሆይ! በዩሱፍ ላይ ለምን አታምነንም እኛም ለእርሱ በእርግጥ አዛኞች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,12,'12:12','«ነገ ከእኛ ጋር ላከውና ይደሰት፤ ይጫወትም፡፡ እኛም ለእርሱ ጠባቂዎች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,13,'12:13','«እኔ እርሱን (ዩሱፍን) ይዛችሁ መሌዳችሁ በእርግጥ ያሳዝነኛል፡፡ እናንተም ከእርሱ ዘንጊዎች ስትሆኑ ተኩላ ይበላዋል ብዬ እፈራለሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,14,'12:14','«እኛ ጭፍራዎች ሆነን ሳለን ተኩላ ቢበላውማ እኛ ያን ጊዜ በእርግጥ ከሳሪዎች ነን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,15,'12:15','እርሱንም ይዘውት በሌዱና በጉድጓዱ ጨለማ አዘቅት ውስጥ እንዲያደርጉት በቆረጡ ጊዜ (ሐሳባቸውን ፈጸሙበት)፡፡ ወደእርሱም እነሱ የማያውቁ ሲሆኑ ይህንን ሥራቸውን በእርግጥ ትነግራቸዋለህ ስንል ላክንበት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,16,'12:16','አባታቸውንም በምሽት እያለቀሱ መጡት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,17,'12:17','«አባታችን ሆይ! እኛ (ለሩጫና ለውርወራ) ልንሽቀዳደም ሌድን፡፡ ዩሱፍንም ዕቃችን ዘንድ ተውነው፡፡ ወዲያውም ተኩላ በላው፡፡ አንተም እውነተኞች ብንሆንም የምታምነን አይደለህም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,18,'12:18','በቀሚሱም ላይ የውሸትን ደም አመጡ፡፡ (አባታቸው) «አይደለም፤ ነፍሶቻችሁ (ውሸት) ነገርን ለእናንተ ሸለሙላችሁ፡፡ ስለዚህ መልካም ትዕግስት (ማድረግ አለብኝ) በምትሉትም ነገር ላይ መታገዣው አላህ ብቻ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,19,'12:19','መንገደኞችም መጡ፡፡ ውሃ ቀጂያቸውንም ላኩ፡፡ አኮሊውንም (ወደ ጉድጓዱ) ሰደደ «የምስራች! ይህ ወጣት ልጅ ነው» አለ፡፡ ሸቀጥ አድርገውም ደበቁት፡፡ አላህም የሚሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,20,'12:20','በርካሽ ዋጋም በሚቆጠሩ ዲርሃሞች ሸጡት፡፡ በእርሱም ከቸልተኞቹ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,21,'12:21','ያም ከምስር የገዛው ሰው ለሚስቱ «መኖሪያውን አክብሪ፡፡ ሊጠቅመን ወይም ልጅ አድርገን ልንይዘው ይከጀላልና» አላት፡፡ እንደዚሁም ለዩሱፍ (ገዥ ልናደርገውና) የሕልሞችንም ፍች ልናስተምረው በምድር ላይ አስመቸነው፡፡ አላህም በነገሩ ላይ አሸናፊ ነው፤ ግን አብዛኛዎቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,22,'12:22','ጥንካሬውንም በደረሰ ጊዜ ጥበብንና ዕውቀትን ሰጠነው፡፡ እንደዚሁም መልካም ሠሪዎችን ሁሉ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(12,23,'12:23','ያቺም እርሱ በቤቷ የነበረባት ሴት ከነፍሱ አባበለችው፡፡ ደጃፎቹንም ዘጋች፤ «ላንተ ተዘጋጅቼልሃለሁና ቶሎ ናም» አለችው፡፡ «በአላህ እጠበቃለሁ እርሱ (የገዛኝ) ጌታዬ ኑሮዬን ያሳመረልኝ ነውና፤ (አልከዳውም)፡፡ እነሆ! በደለኞች አይድኑም» አላት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,24,'12:24','በእርሱም በእርግጥ አሰበች፡፡ በእርሷም አሰበ፤ የጌታውን ማስረጃ ባላየ ኖሮ (የተፈጥሮ ፍላጎቱን ባረካ ነበር፤)፡፡ እንደዚሁ መጥፎ ነገርንና ኀጢአትን ከእርሱ ላይ ልንመልስለት (አስረጃችንን አሳየነው)፡፡ እርሱ ከተመረጡት ባሮቻችን ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(12,25,'12:25','በሩንም ተሽቀዳደሙ ቀሚሱንም ከበስተኋላው ቀደደችው፡፡ ጌታዋንም (ባለቤቷን) እበሩ አጠገብ አገኙት፡፡ (ቀደም ብላ) «በባለቤትህ መጥፎን ያሰበ ሰው ቅጣቱ መታሰር ወይም አሳማሚ ስቃይ እንጂ ሌላ አይደለም» አለችው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,26,'12:26','(ዩሱፍም) «እርሷ ከነፍሴ አባበለችኝ» አለ፡፡ ከቤተሰቦቿም መስካሪ (እንዲህ ሲል) መሰከረ፡፡ «ቀሚሱ ከበስተፊት ተቀድዶ እንደሆነ እውነት ተናገረች፡፡ እርሱም ከውሸታሞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,27,'12:27','«ቀሚሱም ከበስተኋላ ተቀድዶ እንደሆነ ዋሸች፡፡ እርሱም ከእውነተኞቹ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,28,'12:28','ቀሚሱንም ከበስተኋላ ተቀድዶ ባየ ጊዜ «እርሱ (የተናገርሺው) ከተንኮላችሁ ነው (ሴቶች) ተንኮላችሁ በእርግጥ ብርቱ ነውና» አላት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,29,'12:29','«ዩሱፍ ሆይ! ከዚህ (ወሬ) ተከልከል፡፡ ለኃጢአትሽም ማርታን ለምኚ፡፡ አንቺ ከስህተተኞቹ ሆነሻልና» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,30,'12:30','በከተማው ያሉ ሴቶችም «የዐዚዝ ሚስት ብላቴናዋን ከነፍሱ ታባብለዋለች፡፡ በእውነቱ ፍቅሩ ልቧን መቷታል፡፡ እኛ በግልጽ ስህተት ውስጥ ሆና በእርግጥ እናያታለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,31,'12:31','ሀሜታቸውንም በሰማች ጊዜ ወደነሱ ላከችባቸው፡፡ ምግብንም ለእነርሱ አዘጋጀችላቸው፡፡ ከእነሱ ለያንዳንዳቸውም ቢላዋን ሰጠች፡፡ በእነሱም ላይ «ውጣ» አለችው፡፡ ባዩትም ጊዜ አደነቁት፡፡ እጆቻቸውንም ቆረጡ፡፡ አላህም ጥራት ይገባው፡፡ ይህ ሰው አይደለም፡፡ ይህ ያማረ መልአክ እንጅ ሌላ አይደለም አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,32,'12:32','«ታዲያ ይህ ያ በርሱ (ፍቅር) የዘለፋችሁኝ ነው፡፡ በእርግጥም ከነፍሱ አባበልኩት፡፡ እምቢ አለም፡፡ የማዘውንም ነገር ባይሠራ በእርግጥ ይታሰራል፡፡ ከወራዶቹም ይሆናል» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(12,33,'12:33','«ጌታዬ ሆይ! ወደርሱ ከሚጠሩኝ ነገር (ከዝሙት) ይልቅ መታሰር ለእኔ የተወደደ ነው፡፡ ተንኮላቸውንም ከእኔ ላይ ባትመልስልኝ ወደነሱ እዘነበላለሁ፡፡ ከስህተተኞቹም እሆናለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,34,'12:34','ጌታውም ጸሎቱን ተቀበለው፡፡ ተንኮላቸውንም ከእርሱ መለሰለት፡፡ እነሆ! እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(12,35,'12:35','ከዚያም ማስረጃዎቹን ካዩ በኋላ እስከጊዜ ድረስ እንዲያስሩት ለነሱ ታያቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,36,'12:36','ከእሱም ጋር ሁለት ጎበዞች እስር ቤቱ ገቡ፡፡ አንደኛቸው «እኔ በህልሜ የወይን ጠጅን ስጠምቅ አየሁ» አለ፡፡ ሌላውም፡- «እኔ በራሴ ላይ እንጀራን ተሸክሜ ከእሱ በራሪ (አሞራ) ስትበላ አየሁ፡፡ ፍቹን ንገረን፡፡ እኛ ከአሳማሪዎች ሆነህ እናይሃለንና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,37,'12:37','(ዩሱፍም) አለ፡- «ማንኛውም የምትስሰጡት ምግብ ለናንተ ከመምጣቱ በፊት ፍቹን የምነግራችሁ ቢሆን እንጂ አይመጣላችሁም፡፡ ይኸ ጌታዬ ካሳወቀኝ ነገር ነው፤ እኔ በአላህ የማያምኑን እነርሱም መጨረሻይቱን ዓለም እነርሱ ከሓዲዎች የሆኑትን ሕዝቦች ሃይማኖት ትቻለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,38,'12:38','«የአባቶቼንም የኢብራሂምንና የኢስሐቅን፣ የያዕቆብንም ሃይማኖት ተከትያለሁ፡፡ ለእኛ በአላህ ምንንም ማጋራት አይገባንም ያ (አለማጋራት) በእኛ ላይና በሰዎቹ ላይ የአላህ ችሮታ ነው፡፡ ግን አብዛኞቹ ሰዎች አያመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,39,'12:39','«የእስር ቤት ጓደኞቼ ሆይ! የተለያዩ አምላኮች ይሻላሉን ወይስ አሸናፊው አንዱ አላህ');
INSERT INTO "sadiq_sani" VALUES(12,40,'12:40','«ከእርሱ ሌላ እናንተና አባቶቻችሁ (አማልክት ብላችሁ) የጠራችኋቸውን ስሞች እንጅ አትገዝዙም፡፡ አላህ በእርሷ ምንም አስረጅ አላወረደም፡፡ ፍርዱ የአላህ እንጅ የሌላ አይደለም፡፡ እርሱን እንጅ ሌላን እንዳትገዙ አዟል፡፡ ይህ ትክክለኛው ሃይማኖት ነው፡፡ ግን አብዛኞቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,41,'12:41','«የወህኒ ቤት ጓደኞቼ ሆይ! አንደኛችሁማ ጌታውን (ንጉሡን) ጠጅ ያጠጣል፡፡ ሌላውማ ይሰቀላል፡፡ ከራሱም በራሪ (አሞራ) ትበላለች፡፡ ያ ፍቹን የምትጠይቁት ነገር ተፈጸመ፤» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,42,'12:42','ለዚያም ከሁለቱ እርሱ የሚድን መሆኑን ለተጠራጠረው እጌታህ ዘንድ አስታውሰኝ አለው፡፡ ጌታውንም ከማስታወስ ሰይጣን አስረሳው፡፡ ለጥቂት ዓመታትም በወህኒ ቤቱ ውስጥ ቆየ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,43,'12:43','ንጉሡም «እኔ ሰባትን የሰቡ ላሞች ሰባት ከሲታዎች ሲበሏቸው ሰባትንም ለምለም ዘለላዎች ሌሎችንም ደረቆችን (ሲጠመጠሙባቸው በሕልሜ) አያለሁ፡፡ እናንተ ታላላቅ ሰዎች ሆይ! ሕልምን የምትፈቱ እንደሆናችሁ ሕልሜን ፍቱልኝ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,44,'12:44','«የሕልሞች ቅዠቶች ናቸው፡፡ እኛም የሕልሞችን ፍች ዐዋቂዎች አይደለንም» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,45,'12:45','ያም ከሁለቱ የዳነውና ከብዙ ጊዜ በኋላ (ዩሱፍን) ያስታወሰው ሰው «እኔ ፍቹን እነግራችኋለሁና ላኩኝ» አለ፤ (ወደ ዩሱፍ ሌደም)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,46,'12:46','«አንተ እውነተኛው ዩሱፍ ሆይ! ሰባትን የሰቡ ላሞች ሰባት ከሲታዎች ላሞች ሲበሉዋቸው ሰባትን ለምለም ዘለላዎችም ሌሎችንም ደረቆች (በነሱ ላይ ሲጠመጠሙባቸው ያየን ሰው ሕልም ፍች) ተችልን፡፡ ያውቁ ዘንድ ወደ ሰዎቹ ልመለስ እከጅላለሁና» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,47,'12:47','(እርሱም) አለ፡- «ሰባትን የተከታተሉ ዓመታት ትዘራላችሁ፡፡ ያጨዳችሁትንም ሁሉ ከምትበሉት ጥቂት በስተቀር በዘለላው ውስጥ ተዉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,48,'12:48','«ከዚያም ከእነዚያ በኋላ ካደለባችሁት ጥቂት ሲቀር ለእነሱ ያደለባችሁትን ሁሉ የሚበሉ ሰባት የችግር ዓመታት ይመጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,49,'12:49','«ከዚያም ከእነዚያ በኋላ ሰዎቹ በርሱ የሚዘነቡበት በርሱም (ወይንን) የሚጨምቁበት ዓመት ይመጣል፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,50,'12:50','ንጉሡም «እርሱን አምጡልኝ» አለ፡፡ መልክተኛውም (ዩሱፍን) በመጣው ጊዜ «ወደጌታህ ተመለስ፡፡ የዚያንም እጆቻቸውን የቆረጡትን ሴቶች ሁኔታ ጠይቀው፡፡ ጌታዬ ተንኮላቸውን ዐዋቂ ነውና» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,51,'12:51','(ንጉሡም)፡- «ዩሱፍን ከነፍሱ ባባበላችሁት ጊዜ ነገራችሁ ምንድን ነው» አላቸው፡፡ «ለአላህ ጥራት ይገባው፡፡ በእርሱ ላይ ምንም መጥፎን ነገር አላወቅንም» አሉት፡፡ የዐዚዝ ሚስት፡-«አሁን እውነቱ ተገለጸ፡፡ እኔ ከነፍሱ አባበልኩት፡፡ እርሱም ከእውነተኞቹ ነው» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(12,52,'12:52','(ዩሱፍ) «ይህ (ጌታዬ) ሩቅ ሆኖ ሳለ ያልከዳሁት መሆኔን አላህም የከዳተኞችን ተንኮል የማያቃና መሆኑን እንዲያውቅ ነው፤» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,53,'12:53','«ነፍሴንም (ከስሕተት) አላጠራም፡፡ ነፍስ ሁሉ ጌታዬ የጠበቃት ካልሆነች በስተቀር በመጥፎ ነገር በእርግጥ አዛዥ ናትና፡፡ ጌታዬ በጣም መሓሪ አዛኝ ነው» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,54,'12:54','ንጉሡም «እርሱን አምጡልኝ ለራሴ ግለኛ አደርገዋለሁና» አለ፡፡ ባናገረውም ጊዜ «አንተ ዛሬ እኛ ዘንድ ባለሟል ታማኝ ነህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,55,'12:55','«በምድር ግምጃ ቤቶች ላይ (ሹም) አድርገኝ፡፡ እኔ ጠባቂ አዋቂ ነኝና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,56,'12:56','እንደዚሁም ለዩሱፍ በምድር ላይ ከእርሷ በፈለገው ስፍራ የሚሰፍር ሲሆን አስመቸነው፡፡ በችሮታችን የምንሻውን ሰው እንለያለን፡፡ የበጎ ሠሪዎችንም ዋጋ አናጠፋም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,57,'12:57','የመጨረሻይቱም ዓለም ምንዳ ለእነዚያ ላመኑትና ይጠነቀቁ ለነበሩት የበለጠ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,58,'12:58','የዩሱፍ ወንድሞችም መጡ፡፡ በእርሱም ላይ ገቡ፡፡ እነሱም እርሱን የሳቱት ሲሆኑ ዐወቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,59,'12:59','(ጉዳያቸውን ፈጽሞ) ስንቃቸውንም ባዘጋጀላቸው ጊዜ አለ፡- «ከአባታችሁ በኩል የሆነውን ወንድማችሁን አምጡልኝ፡፡ እኔ ከአስተናጋጆች ሁሉ በላጭ ስሆን፤ ስፍርን የምሞላ መሆኔን አታዩምን');
INSERT INTO "sadiq_sani" VALUES(12,60,'12:60','እርሱንም ባታመጡልኝ እኔ ዘንድ ለእናንተ ስፍር የላችሁም፤ አትቀርቡኝምም፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,61,'12:61','«ስለእርሱ አባቱን በጥብቅ እንጠይቃለን፡፡ እኛም (ይህንን) በእርግጥ ሠሪዎች ነን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,62,'12:62','ለአሽከሮቹም «ሸቀጣቸውን ወደ ቤተሰቦቻቸው በተመለሱ ጊዜ ያውቋት ዘንድ በየጓዞቻቸው ውስጥ አድርጉላቸው፡፡ ሊመለሱ ይከጀላልና» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,63,'12:63','ወደ አባታቸውም በተመሰሉ ጊዜ፡- «አባታችን ሆይ! (ወንድማችንን እስከምንወስድ) ስፍር ከእኛ ተከልክሏል፡፡ ስለዚህ ወንድማችንን ከእኛ ጋር ላከው፡፡ ይሰፍርልናልና እኛም ለርሱ ጠባቂዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,64,'12:64','«ከአሁን በፊት በወንድሙ ላይ እንዳመንኳችሁ እንጂ በእርሱ ላይ አምናችኋለሁን አላህም በጠባቂነት (ከሁሉ) የበለጠ ነው፡፡ እርሱም ከአዛኞች ሁሉ ይበልጥ አዛኝ ነው፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,65,'12:65','ዕቃቸውንም በከፈቱ ጊዜ ሸቀጣቸው ወደእነርሱ ተመልሳ አገኙ፡፡ ፡-«አባታችን ሆይ! ምን እንፈልጋለን ይህቺ ሸቀጣችን ናት፡፡ ወደኛ ተመልሳልናለች፤ (እንረዳባታለን)፡፡ ለቤተሰቦቻችንም እንሸምታለን፡፡ ወንድማችንንም እንጠብቃለን፡፡ የግመልንም ጭነት እንጨምራለን፡፡ ይህ (በንጉሡ ላይ) ቀላል ስፍር ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,66,'12:66','«ካልተከበባችሁ በስተቀር እርሱን በእርግጥ የምታመጡልኝ ለመሆናችሁ ከአላህ የሆነን ቃል ኪዳን መተማመኛ እስከምትሰጡኝ ድረስ ከእናንተ ጋር ፈጽሞ አልልከውም» አላቸው፡፡ መተማመኛቸውንም በሰጡት ጊዜ «አላህ በምንለው ሁሉ ላይ ምስክር ነው» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,67,'12:67','አለም «ልጆቼ ሆይ! በአንድ በር አትግቡ፡፡ ግን በተለዩ በሮች ግቡ፡፡ ከአላህም (ፍርድ) በምንም አልጠቅማችሁም፤ (አልመልስላችሁም)፡፡ ፍርዱ የአላህ እንጅ የሌላ አይደለም፡፡ በእርሱ ላይ ተመካሁ፡፡ ተመኪዎችም ሁሉ በእርሱ ብቻ ይመኩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,68,'12:68','አባታቸውም ካዘዛቸው ስፍራ በገቡ ጊዜ ከአላህ (ፍርድ) ምንም ነገር ከነርሱ የሚከለክልላቸው አልነበረም፡፡ ግን በያዕቆብም ነፍስ ውስጥ የነበረች ጉዳይ ናት፡፡ ፈጸማት፡፡ እርሱም ስላሳወቅነው የዕውቀት ባለቤት ነው፤ ግን አብዛኞቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,69,'12:69','በዩሱፍ ላይ በገቡ ጊዜም ወንድሙን ወደርሱ አስጠጋው፡፡ «እነሆ እኔ ወንድምህ ነኝ፡፡ ይሠሩት በነበሩትም ሁሉ አትቆላጭ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,70,'12:70','ዕቃቸውንም ባዘጋጀላቸው ጊዜ፤ ዋንጫይቱን በወንድሙ ዕቃ ውስጥ አደረገ፡፡ ከዚያም «እናንተ ባለ ግመሎች ሆይ! እናንተ በእርግጥ ሌቦች ናችሁ ሲል ጠሪ ተጣራ፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,71,'12:71','ወደነሱ ዞረውም «ምንድን ጠፋችሁ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,72,'12:72','«የንጉሡ መስፈሪያ ጠፍቶናል፡፡ እርሱንም ላመጣ ሰው የግመል ጭነት አለው፡፡ እኔም በእርሱ ተያዥ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,73,'12:73','«በአላህ እንምላለን በምድር ላይ ለማጥፋት እንዳልመጣን በእርግጥ ዐውቃችኋል፡፡ ሌቦችም አልነበርንም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,74,'12:74','«ውሸታሞች ብትሆኑ ቅጣቱ ምንድን ነው» አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,75,'12:75','«ቅጣቱ (በያዕቆብ ሕግ) ዕቃው በጓዙ ውስጥ የተገኘበት ሰው (ራሱ መወሰድ) ነው፡፡ እርሱም ቅጣቱ ነው፡፡ እንደዚሁ በደለኞችን እንቀጣለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,76,'12:76','(ምርመራውን) ከወንድሙም ዕቃ በፊት በዕቃዎቻቸው ጀመረ፡፡ ከዚያም (ዋንጫይቱን) ከወንድሙ ዕቃ ውስጥ አወጣት፡፡ እንደዚሁ ለዩሱፍ ብልሃትን አስተማርነው፡፡ አላህ ባልሻ ኖሮ በንጉሡ ሕግ ወንድሙን ሊይዝ አይገባውም ነበር፡፡ የምንሻውን ሰው በደረጃዎች ከፍ እናደርጋለን፡፡ ከዕውቀት ባለቤቶች ሁሉ በላይም ዐዋቂ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,77,'12:77','«ቢሰርቅ ከአሁን በፊት የርሱው ወንድም በእርግጥ ሰርቋል» አሉ፡፡ ዩሱፍም (ይህችን ንግግር) በነፍሱ ውስጥ ደበቃት፡፡ ለእነሱም አልገለጣትም፡፡ (በልቡ) «እናንተ ሥራችሁ የከፋ ነው፡፡ አላህም የምትሉትን ሁሉ ይበልጥ ዐዋቂ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,78,'12:78','አንተ የተከበርከው ሆይ! «ለእርሱ በእርግጥ ታላቅ ሽማግሌ አባት አለው፡፡ ስለዚህ በእርሱ ስፍራ አንደኛችንን ያዝ፡፡ እኛ ከመልካም ሠሪዎች ሆነህ እናይሃለንና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,79,'12:79','«ዕቃችንን እርሱ ዘንድ ያገኘንበትን ሰው እንጂ ሌላን ከመያዝ በአላህ እንጠብቃለን፡፡ እኛ ያን ጊዜ በእርግጥ በዳዮች ነን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,80,'12:80','ከርሱም ተስፋን በቆረጡ ጊዜ የሚመካከሩ ሆነው ገለል አሉ፡፡ ታላቃቸው አለ «አባታቸሁ በናንተ ላይ ከአላህ የሆነን መታመኛ ኪዳን በእርግጥ የያዘባችሁ መሆኑን ከዚህም በፊት በዩሱፍ ላይ ግፍ የሠራችሁትን አታውቁምን ስለዚህ አባቴ ለእኔ እስከሚፈቅድልኝ ወይም አላህ ለኔ እስከሚፈርድልኝ ድረስ የምስርን ምድር አልለይም፡፡ እርሱም ከፈራጆች ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,81,'12:81','«ወደ አባታችሁ ተመለሱ በሉትም፡- አባታችን ሆይ! ልጅህ ሰረቀ፡፡ ባወቅነውም ነገር እንጂ አልመሰከርንም፡፡ ሩቁንም ነገር (ምስጢሩን) ዐዋቂዎች አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,82,'12:82','«ያችንም በውስጧ የነበርንባትን ከተማ ያችንም በውስጧ የመጣነውን ባለ ግመል ጓድ ጠይቅ፡፡ እኛም በእርግጥ እውነተኞች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(12,83,'12:83','(ያዕቆብ) «አይደለም ነፍሶቻችሁ አንዳች ነገርን ለእናንተ ሸለሙላችሁ፡፡ (ሠራችሁትም)፡፡ መልካምም ትእግስት (ማድረግ) አለብኝ፡፡ እነርሱን (ሦስቱንም) የተሰበሰቡ ሆነው አላህ ሊያመጣልኝ ይከጀላል፡፡ እነሆ እርሱ ዐዋቂ ጥበበኛ ነውና» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,84,'12:84','ከእነሱም ዘወር አለናኣ«በዩሱፍ ላይ ዋ ሐዘኔ!» አለ፡፡ ዓይኖቹም ከሐዘን (ለቅሶ) የተነሳ ነጡ፡፡ እርሱም በትካዜ የተመላ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,85,'12:85','(እነርሱም) «በአላህ እንምላለን፡፡ ለጥፋት የቀረብክ እስከምትሆን ወይም ከጠፊዎቹ እስከምትሆን ድረስ ዩሱፍን ከማውሳት አትወገድም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,86,'12:86','«ጭንቀቴንና ሐዘኔንና የማሰሙተው ወደ አላህ ብቻ ነው፡፡ ከአላህም በኩል የማታውቁትን ነገር ዐውቃለሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,87,'12:87','«ልጆቼ ሆይ! ሊዱ፤ ከዩሱፍና ከወንድሙም (ወሬ) ተመራመሩ፡፡ ከአላህም እዝነት ተስፋ አትቁረጡ፡፡ እነሆ ከአላህ እዝነት ከሓዲዎች ሕዝቦች እንጂ ተስፋ አይቆርጥም» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,88,'12:88','በእርሱም ላይ በገቡ ጊዜ «አንተ የተከበርከው ሆይ! እኛንም ቤተሰቦቻችንንም ጉዳት ደረሰብን፡፡ ርካሽ ሸቀጥም ይዘን መጥተናል፡፡ ስፍርንም ለኛ ሙላልን፡፡ በኛም ላይ መጽውት አላህ መጽዋቾችን ይመነዳልና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,89,'12:89','«እናንተ የማታውቁ በነበራችሁ ጊዜ በዩሱፍና በወንድሙ ላይ የሰራችሁትን ግፍ ዐወቃችሁን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,90,'12:90','«አንተ በእርግጥ አንተ ዩሱፍ ነህን» አሉት፡፡ «እኔ ዩሱፍ ነኝ፡፡ ይህም ወንድሜ ነው፡፡ አላህ በኛ ላይ በእርግጥ ለገሰልን፡፡ እነሆ! የሚጠነቀቅና የሚታገስ ሰው (አላህ ይክሰዋል)፡፡ አላህ የበጎ አድራጊዎችን ዋጋ አያጠፋምና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,91,'12:91','«በአላህ እንምላለን፡፡ አላህ በኛ ላይ በእርግጥ አበለጠህ፡፡ እኛም በእርግጥ አጥፊዎች ነበርን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,92,'12:92','«ዛሬ በናንተ ላይ ወቀሳ የለባችሁም፡፡ አላህ ለእናንተ ይምራል፡፡ እርሱም ከአዛኞች ሁሉ ይበልጥ አዛኝ ነው» አላቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(12,93,'12:93','«ይህንን ቀሚሴን ይዛችሁ ሊዱ፡፡ በአባቴ ፊትም ጣሉት የሚያይ ሆኖ ይመጣልና፡፡ ቤተሰቦቻችሁንም ሰብስባችሁ አምጡልኝ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,94,'12:94','ግመል ጫኞቹም (ምስርን) በተለዩ ጊዜ አባታቸው «እኔ የዩሱፍን ሽታ በእርግጥ አገኛለሁ፡፡ ባታቄሉኝ ኖሮ (ታምኑኝ ነበር)» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,95,'12:95','«በአላህ እንምላለን፡፡ አንተ በእርግጥ በቀድሞው ስህተትህ ውስጥ ነህ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(12,96,'12:96','አብሳሪውም በመጣ ጊዜ (ቀሚሱን) በፊቱ ላይ ጣለው፡፡ ወዲውም የሚያይ ኾነ፡፡ «እኔ ለእናንተ፡- ከአላህ በኩል የማታውቁትን ዐውቃለሁ አላልኳችሁምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,97,'12:97','«አባታችን ሆይ! ለኀጢአቶቻችን ምሕረትን ለምንልን እኛ ጥፋተኞች ነበርንና» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,98,'12:98','«ወደፊት ለእናንተ ጌታዬን ምሕረትን እለምንላችኋለሁ፡፡ እነሆ! እርሱ መሓሪው አዛኙ ነውና» አላቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(12,99,'12:99','በዩሱፍ ላይም በገቡ ጊዜ ወላጆቹን ወደርሱ አስጠጋቸው፡፡ «በአላህም ፈቃድ ጥብቆች ስትኾኑ ምስርን ግቡ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(12,100,'12:100','ወላጆቹንም በዙፋኑ ላይ አወጣቸው፡፡ ለእርሱም ሰጋጆች ኾነው ወረዱለት፡፡ «አባቴም ሆይ! ይህ ፊት ያየኋት ሕልሜ ፍች ነው፡፡ ጌታ በእርግጥ እውነት አደረጋት፡፡ ከወህኒ ቤትም ባወጣኝና ሰይጣንም በእኔና በወንድሞቼ መካከል ከአበላሸ በኋላ እናንተን ከገጠር ባመጣችሁ ጊዜ በእርግጥ ለኔ መልካም ዋለልኝ፡፡ ጌታዬ ለሚሻው ነገር እዝነቱ ረቂቅ ነው፡፡ እነሆ እሱ ዐዋቂው ጥበበኛው ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,101,'12:101','«ጌታዬ ሆይ! ከንግሥና በእርግጥ ሰጠኸኝ፡፡ ከሕልሞችም ፍች አስተማርከኝ፡፡ የሰማያትና የምድር ፈጣሪ ሆይ! አንተ በቅርቢቱና በመጨረሻይቱ ዓለም ረዳቴ ነህ፡፡ ሙስሊም ሆኘ ግደለኝ፡፡ በመልካሞቹም አስጠጋኝ» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(12,102,'12:102','(ሙሐመድ ሆይ) ይህ ወዳንተ የምናወርደው ሲኾን፤ ከሩቅ ወሬዎች ነው፡፡ አንተም እነርሱ (በዩሱፍ) የሚመክሩ ኾነው ነገራቸውን በቆረጡ ጊዜ እነሱ ዘንድ አልነበርክም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,103,'12:103','አብዛኛዎቹም ሰዎች (ለማመናቸው) ብትጓጓም የሚያምኑ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,104,'12:104','በእርሱም (በቁርአን) ላይ ምንም ዋጋ አትጠይቃቸውም፡፡ እርሱ ለዓለማት ግሳጼ እንጂ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,105,'12:105','በሰማያትና በምድርም ካለችው ምልክት ብዙይቱ እነሱ ከርሷ ዘንጊዎች ኾነው በርሷ ላይ ያልፋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(12,106,'12:106','አብዛኞቻቸውም፤ እነሱ አጋሪዎች ኾነው እንጂ በአላህ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,107,'12:107','ከአላህ ቅጣት ሸፋኝ አደጋ የምትመጣባቸው ወይም ሰዓቲቱ እነሱ የማያውቁ ሲኾኑ በድንገት የምትመጣባቸው መኾኑዋን አይፈሩምን');
INSERT INTO "sadiq_sani" VALUES(12,108,'12:108','«ይህች መንገዴ ናት፡፡ ወደ አላህ እጠራለሁ፡፡ እኔም የተከተለኝም ሰው በግልጽ ማስረጃ ላይ ነን፡፡ ጥራትም ለአላህ ይገባው፡፡ እኔም ከአጋሪዎቹ አይደለሁም» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(12,109,'12:109','ካንተ በፊትም ከከተሞች ሰዎች ወደነርሱ ራእይ የምናወርድላቸው የኾኑን ወንዶችን እንጂ አልላክንም፡፡ በምድር ላይ አይኼዱምና የእነዚያን ከእነሱ በፊት የነበሩትን ሰዎች መጨረሻ አይመለከቱምን የመጨረሻይቱም አገር ለእነዚያ ለተጠነቀቁት በእርግጥ የተሻለች ናት፤ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(12,110,'12:110','መልክተኞቹም ተስፋ በቆረጡና እነርሱ በእርግጥ የተዋሹ መኾናቸውን በተጠራጠሩ ጊዜ እርዳታችን መጣላቸው፡፡ (እኛ) የምንሻውም ሰው እንዲድን ተደረገ፡፡ ቅጣታችንም ከአጋሪዎቹ ሕዝቦች ላይ አይመለስም፡፡');
INSERT INTO "sadiq_sani" VALUES(12,111,'12:111','በታሪካቸው ውስጥ ለአእምሮ ባለ ቤቶች በእርግጥ መገሰጫ ነበረ፡፡ (ይህ ቁርኣን) የሚቀጣጠፍ ወሬ አይደለም፡፡ ግን ያንን በስተፊቱ ያለውን አረጋጋጭ ለነገርም ሁሉ ገላጭ ለሚያምኑ ሕዝቦችም መሪና ችሮታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,1,'13:1','አ.ለ.መ.ረ (አሊፍ ላም ሚም ራ) ይህች (አናቅጽ) ከመጽሐፉ አንቀጾች ናት፡፡ ያም ከጌታህ ወዳንተ የተወረደው እውነት ነው፡፡ ግን አብዛኛዎቹ ሰዎች አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,2,'13:2','አላህ ያ ሰማያትን የምታዩዋት አዕማድ ሳትኖር ያነሳት ከዚያም በዐርሹ (ዙፋኑ) ላይ (ለሱ ክብር በሚስማማ መልኩ) የተደላደለ ፀሐይንና ጨረቃንም የገራ ነው፡፡ ሁሉም ለተወሰነ ጊዜ ይሮጣሉ፡፡ ነገሩን ሁሉ ያስተናብራል፡፡ በጌታችሁ መገናኘት ታረጋግጡ ዘንድ ተዓምራቶችን ይዘረዝራል፡፡');
INSERT INTO "sadiq_sani" VALUES(13,3,'13:3','እርሱም ያ ምድርን የዘረጋ በሷም ተራራዎችንና ወንዞችን ያደረገ በውስጧም ከፍሬዎች ሁሉ ሁለት ሁለት ዓይነቶችን ያደረገ ነው፡፡ ሌሊትን በቀን ይሸፍናል፡፡ በዚህም ውስጥ ለሚያስተነትኑ ሕዝቦች ምልክቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(13,4,'13:4','በምድርም የተጎራበቱ ቁርጥራጮች አልሉ፡፡ ከወይኖችም አትክልቶች አዝርዕቶችም መንታዎችና መንታዎች ያልኾኑ ዘንባባዎችም አልሉ፡፡ በአንድ ውሃ ይጠጣሉ፤ (በጣዕምም ይለያያሉ)፡፡ ከፊሉዋንም በከፊሉ ላይ በሚበላው ሰብል እናበልጣለን፡፡ በዚህም ውስጥ ለሚያውቁ ሕዝቦች ተዓምራት አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(13,5,'13:5','ብትደነቅም ዐፈር በኾንን ጊዜ እና አዲስ ፍጥረት እንኾናለን ማለታቸው (ታላቅ) ድንቅ ነው፡፡ እነዚህ እነዚያ በጌታቸው የካዱት ናቸው፡፡ እነዚህም እንዛዝላዎች በአንገቶቻቸው ላይ ያሉባቸው ናቸው፡፡ እነዚሀም የእሳት ጓዶች ናቸው፡፡ እነሱ በውስጧ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,6,'13:6','ከእነሱ በፊት የመሰሎቻቸው ቅጣቶች በእርግጥ ያለፉ ሲኾኑ ከመልካሙ በፊት በመጥፎው ያስቸኩሉሃል፡፡ ጌታህም ለሰዎች ከመበደላቸው ጋር በእርግጥ የምህረት ባለቤት ነው፡፡ ጌታህም በእርግጥ ቅጣተ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,7,'13:7','እነዚያም የካዱት በእርሱ ላይ ከጌታው ተዓምር ለምን አልተወረደለትም ይላሉ፡፡ አንተ አስፈራሪ ብቻ ነህ፡፡ ለሕዝብም ሁሉ መሪ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,8,'13:8','አላህ ሴት ሁሉ የምታረግዘውን ያውቃል፡፡ ማሕጸኖችም የሚያጎድሉትን የሚጨምሩትንም (ያውቃል)፡፡ ነገሩም ሁሉ እርሱ ዘንድ በልክ የተወሰነ ነው፡፤');
INSERT INTO "sadiq_sani" VALUES(13,9,'13:9','ሩቁንና ቅርቡን ዐዋቂ ታላቅ የላቀ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,10,'13:10','ከእናንተ ውስጥ ቃሉን ዝቅ ያደረገ ሰው በእርሱ የጮኸም ሰው እርሱ በሌሊት ተደባቂም በቀን (ተገልጾ) ኺያጅም የኾነ ሰው (እርሱ ዘንድ) እኩል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,11,'13:11','ለእርሱ (ለሰው) ከስተፊቱም ከኋላውም በአላህ ትዕዛዝ (ከክፉ) የሚጠብቁት ተተካኪዎች (መላእክት) አሉት፡፡ አላህ በሕዝቦች ዘንድ ያለውን ጸጋ በነፍሶቻቸው ያለውን (ኹኔታ) እስከሚለውጡ ድረስ አይለውጥም፡፡ አላህም በሰዎች ላይ ክፉን በሻ ጊዜ ለእርሱ መመለስ የለውም፡፡ ለእነርሱም ከእርሱ ሌላ ምንም ተከላካይ የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,12,'13:12','እርሱ ያ የምትፈሩና የምትከጅሉም ስትሆኑ ብልጭታን የሚያሳያችሁ ከባዶች ደመናዎችንም የሚያስገኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,13,'13:13','ነጎድጓድም አላህን በማመስገን ያጠራል፡፡ መላእክትም እርሱን ለመፍራት (ያጠሩታል)፡፡ መብረቆችንም ይልካል፡፡ እነርሱም (ከሓዲዎች) በአላህ የሚከራከሩ ሲሆኑ በእርሷ የሚሻውን ሰው ይመታል፡፡ እርሱም ኀይለ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,14,'13:14','ለእርሱ (ለአላህ) የእውነት መጥሪያ አለው፡፡ እነዚያም ከእርሱ ሌላ የሚገዙዋቸው (ጣዖታት) ወደ አፉ ይደርስ ዘንድ ወደ ውሃ መዳፎቹን (በሩቅ ሆኖ) እንደሚዘረጋ (ሰው መልስ) እንጂ ለነርሱ በምንም አይመልሱላቸውም፡፡ እርሱም ወደ አፉ ደራሽ አይደለም፡፡ የከሓዲዎችም ጥሪ በከንቱ እንጂ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,15,'13:15','በሰማያትና በምድርም ያሉት ሁሉ በውድም ሆነ በግድ ለአላህ ይሰግዳሉ፡ ጥላዎቻቸውም በጧቶችና በሠርኮች ይሰግዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(13,16,'13:16','«የሰማያትና የምድር ጌታ ማን ነው» በላቸው፡፡ «አላህ ነው» በል፡፡ «ለነፍሶቻቸው ጥቅምንም ጉዳትንም የማይችሉን ረዳቶች ከርሱ ሌላ ያዛችሁን» በላቸው፡፡ «ዕውርና የሚያይ ይስተካከላሉን ወይስ ጨለማዎችና ብርሃን ይስተካከላሉን ወይስ ለአላህ እንደርሱ አፈጣጠር የፈጠሩን ተጋሪዎች አደረጉለትና ፍጥረቱ በእነሱ ላይ ተመሳሰለባቸውን» በል፤ «አላህ ሁለመናውን ፈጣሪ ነው፡፡ እርሱም አንዱ አሸናፊው ነው» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(13,17,'13:17','ከሰማይ ውሃን አወረደ፡፡ ሸለቆዎቹም በመጠናቸው ፈሰሱ፡፡ ጎርፉም አሰፋፊውን ኮረፋት ተሸከመ፡፡ ለጌጥ ወይም ለዕቃ ፍላጎት በእርሱ ላይ እሳት የሚያነዱበትም (ማዕድን) ብጤው የኾነ ኮረፋት አልለው፡፡ እንደዚሁ አላህ ለእውነትና ለውሸት ምሳሌን ያደርጋል፡፡ ኮረፋቱማ ግብስብስ ኾኖ ይጠፋል፡፡ ለሰዎች የሚጠቅመውማ በምድር ላይ ይቆያል፡፡ እንደዚሁ አላህ ምሳሌዎችን ይገልፃል፡፡');
INSERT INTO "sadiq_sani" VALUES(13,18,'13:18','ለእነዚያ ለጌታቸው ለታዘዙት መልካም ነገር (ገነት) አልላቸው፡፡ እነዚያም ለእርሱ ያልታዘዙት ለእነሱ በምድር ያለው ሁሉ ከእርሱም ጋር ብጤው ቢኖራቸው ኖሮ በእርሱ በተበዡበት ነበር፡፡ እነዚያ ለእነሱ ክፉ ምርመራ አለባቸው፡፡ መኖሪያቸውም ገሀነም ናት፡፡ ፍራሻቸውም ከፋች!');
INSERT INTO "sadiq_sani" VALUES(13,19,'13:19','ከጌታህ ወዳንተ የተወረደው እውነት መኾኑን የሚያውቅ ሰው እንደዚያ እርሱ ዕውር እንደኾነው ሰው ነውን የሚገሠጹት የአእምሮ ባለቤቶች ብቻ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,20,'13:20','እነዚያ በአላህ ቃል ኪዳን የሚሞሉ የጠበቀውንም ኪዳን የማያፈርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,21,'13:21','እነዚያም አላህ እንዲቀጠል በእርሱ ያዘዘውን ነገር የሚቀጥሉ ጌታቸውንም የሚያከብሩ መጥፎንም ቁጥጥር የሚፈሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,22,'13:22','እነዚያም የጌታቸውን ውዴታ ለመፈለግ የታገሱ፣ ሶላትንም አዘውትረው የሰገዱ፣ እኛ ከሰጠናቸው ሲሳይም በምስጢርም ኾነ በግልጽ የመጸወቱ፣ ክፉውንም ነገር በበጎ የሚገፈትሩ ናቸው፡፡ እነዚያ ለእነሱ መጨረሻይቱ ምስጉን አገር አለቻቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,23,'13:23','(እርሷም) የመኖሪያ ገነቶች ናት፡፡ ይገቡባታል፡፡ ከአባቶቻቸውም፣ ከሚስቶቻቸውም፣ ከዝርያቸውም መልካም የሠራ ሰው (ይገባታል)፡፡ መላእክትም በእነርሱ ላይ ከየደጃፉ ሁሉ ይገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(13,24,'13:24','«ሰላም ለእናንተ ይኹን፡፡ (ይህ ምንዳ) በመታገሳችሁ ነው፡፡ የመጨረሻይቱም አገር ምን ታምር!» (ይሏቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(13,25,'13:25','እነዚያም የአላህን ቃል ኪዳን ከጠበቀ በኋላ የሚያፈርሱ አላህም እንዲቀጠል በእርሱ ያዘዘውን ነገር የሚቆርጡ በምድርም ላይ የሚያበላሹ እነዚያ ለነሱ እርግማን አለባቸው፡፡ ለእነሱም መጥፎ አገር (ገሀነም) አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,26,'13:26','አላህ ለሚሻው ሰው ሲሳይን ያሰፋል፤ ያጠባልም፡፡ (ከሓዲዎች) በቅርቢቱም ሕይወት ተደሰቱ፡፡ የቅርቢቱም ሕይወት በመጨረሻይቱ አንጻር (ትንሽ) መጠቀሚያ እንጂ ምንም አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,27,'13:27','እነዚያም የካዱት «በእርሱ ላይ ከጌታው ተዓምር ለምን አልተወረደም» ይላሉ፡፡ «አላህ የሚሻውን ያጠምማል፡፡ የተመለሰውንም ሰው ወደርሱ ይመራል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,28,'13:28','(እነሱም) እነዚያ ያመኑ ልቦቻቸውም አላህን በማውሳት የሚረኩ ናቸው፡፡ ንቁ! አላህን በማውሳት ልቦች ይረካሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(13,29,'13:29','እነዚያ ያመኑትና መልካም ሥራዎችን የሠሩት ለእነሱ ደግ ኑሮ መልካም መመለሻም አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,30,'13:30','እንደዚሁ ከበፊትዋ ብዙ ሕዝቦች በእርግጥ ያለፉ ወደኾነችው ሕዝብ እነርሱ በአልረሕማን የሚክዱ ሲኾኑ ያንን ወደ አንተ ያወረድነውን ቁርኣን በእነርሱ ላይ ታነብላቸው ዘንድ ላክንህ፡፡ «እርሱ (አልረሕማን) ጌታዬ ነው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ በርሱ ላይ ተጠጋሁ፡፡ መመለሻየም ወደርሱ ብቻ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,31,'13:31','ቁርኣንም በእርሱ ተራራዎች በተነዱበት ወይም በእርሱ ምድር በተቆራረጠችበት ወይም በእርሱ ሙታን እንዲናገሩ በተደረጉበት ኖሮ (የመካ ከሓዲዎች ባላመኑ ነበር)፡፡ በእውነቱ ነገሩ ሁሉ የአላህ ነው፡፡ እነዚያ ያመኑት ሰዎች እነሆ አላህ በሻ ኖሮ ሰዎችን በመላ በእርግጥ ይመራቸው እንደነበረ አያውቁምን እነዚያም የካዱት በሥራቸው ምክንያት (አጥፊ) ዐደጋ የምታገኛቸው ከመኾን ወይም የአላህ ቀጠሮ እስኪመጣ በአገራቸው አቅራቢያ (አንተ) የምትሰፍርባቸው ከመኾን አይወገዱም፡፡ አላህ ቀጠሮውን አያፈርስምና፡፡');
INSERT INTO "sadiq_sani" VALUES(13,32,'13:32','ከአንተ በፊት በነበሩትም መልክተኞች በእርግጥ ተላግጦባቸዋል፡፡ ለእነዚያም ለካዱት ጊዜን ሰጠኋቸው፡፡ ከዚያም (በቅጣት) ያዝኩዋቸው፡፡ ቅጣቴም እንዴት ነበር!');
INSERT INTO "sadiq_sani" VALUES(13,33,'13:33','እርሱ በነፍስ ሁሉ ላይ በሠራቸው ሥራ ተጠባባቂ የኾነው (አላህ እንደዚህ እንዳልኾነው ጣዖት ብጤ ነውን) ለአላህም ተጋሪዎችን አደረጉ፡፡ ጥሯቸው «አላህን በምድር ውስጥ የማያውቀው ኖሮ ትነግሩታላችሁን ወይስ ከቃል በግልጽ (ፍቺ በሌለው ከንቱ ቃል ተጋሪዎች በማለት ትጠሩዋቸዋላችሁን)» በላቸው፡፡ በእውነቱ ለእነዚያ ለካዱት ተንኮላቸው ተሸለመላቸው፡፡ ከእውነቱ መንገድም ታገዱ፡፡ አላህም ያጠመመው ሰው ለእርሱ ምንም አቅኚ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,34,'13:34','ለእነሱ በቅርቢቱ ሕይወት ቅጣት አላቸው፡፡ የመጨረሻይቱም (ዓለም) ቅጣት በጣም የበረታ ነው፡፡ ለእነሱም ከአላህ (ቅጣት) ምንም ጠባቂ የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,35,'13:35','ያች ጥንቁቆቹ ተስፋ የተሰጧት ገነት ምጣኔዋ (እንደሚነገራችሁ ነው)፡፡ ከሥርዋ ወንዞች ይፈስሳሉ፡፡ ምግቧ (ሁልጊዜ) የማይቋረጥ፤ ነው፡፡ ጥላዋም (እንደዚሁ)፡፡ ይህች የእነዚያ የተጠነቀቁት መጨረሻ ናት፡፡ የከሃዲዎችም መጨረሻ እሳት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(13,36,'13:36','እነዚያም መጽሐፉን የሰጠናቸው ወዳንተ በተወረደው ይደሰታሉ፡፡ ከአሕዛብም ከፊሉን የሚክዱ ሰዎች አልሉ፡፡ «እኔ የታዘዝኩት አላህን ብቻ እንድገዛ በእርሱም እንደዳላጋራ ነው፡፡ ወደእርሱ እጠራለሁ፤ መመለሻዬም ወደእርሱ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,37,'13:37','እንደዚሁ በዐረብኛ የተነገረ ፍትሕ ሲኾን አወረድነው፡፡ ዕውቀቱ ከመጣልህም በኋላ ዝንባሌዎቻቸውን ብትከተል ከአላህ ቅጣት ምንም ረዳትም ጠባቂም የለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(13,38,'13:38','ከአንተ በፊትም መልክተኞችን በእርግጥ ልከናል፡፡ ለእነርሱም ሚስቶችንና ልጆችን አድርገናል፡፡ ለማንኛውም መልክተኛ በአላህ ፈቃድ እንጂ ተዓምር ሊያመጣ አይገባውም፡፡ ለጊዜው ሁሉ ጽሑፍ አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,39,'13:39','አላህ የሚሻውን ያብሳል፤ ያጸድቃልም፡፡ የመጽሐፉ መሠረትም እርሱ ዘንድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,40,'13:40','የዚያንም ያስፈራራናቸውን ከፊሉን ብናሳይህ (መልካም ነው)፡፡ ወይም ብንገድልህ (ወቀሳ የለብህም)፡፡ በአንተ ላይ ያለብህ ማድረስ ብቻ ነው፡፡ ምርመራውም በኛ ላይ ነው፡፤');
INSERT INTO "sadiq_sani" VALUES(13,41,'13:41','እኛ ምድርን ከጫፎችዋ የምናጎድላት ኾነን የምንመጣባት መኾናችንን አላዩምን አላህም ይፈርዳል፡፡ ለፍርዱም ገልባጭ የለውም፡፡ እርሱም ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(13,42,'13:42','እነዚያም ከእነሱ በፊት የነበሩት በእርግጥ መከሩ፡፡ አዘንግቶም የመያዙ ዘዴ በሙሉ የአላህ ነው፡፡ ነፍስ ሁሉ የምትሠራውን ያውቃል፡፡ ከሓዲዎችም የመጨረሻይቱ አገር ለማን እንደምትኾን ወደፊት ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(13,43,'13:43','እነዚያም የካዱት ሰዎች «መልክተኛ አይደለህም» ይላሉ፡፡ «በእኔና በእናንተ መካከል መስካሪ በአላህና እርሱ ዘንድ የመጽሐፉ ዕውቀት ባለው ሰው በቃ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,1,'14:1','አ.ለ.ረ (አሊፍ ላም ራ፤ ይህ ቁርአን) ሰዎችን በጌታቸው ፈቃድ ከጨለማዎች ወደ ብርሃን አሸናፊ ምስጉን ወደ ኾነው፡፡ ጌታ መንገድ ታወጣ ዘንድ ወዳንተ ያወረድነው መጽሐፍ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,2,'14:2','አላህ ያ በሰማያት ያለው በምድርም ያለው ሁሉ የርሱ ብቻ የኾነ ነው፤ ለከሓዲዎችም ከብርቱ ቅጣት ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,3,'14:3','እነዚያ የቅርቢቱን ሕይወት ከመጨረሻይቱ ሕይወት ይበልጥ የሚወዱ ከአላህም መንገድ የሚያግዱ መጥመሟንም የሚፈልጉዋት ናቸው፡፡ እነዚያ በሩቅ ስሕተት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,4,'14:4','ከመልክተኛ ማንኛውንም ለእነርሱ ያብራራላቸው ዘንድ በወገኖቹ ቋንቋ እንጂ በሌላ አልላክንም፡፡ አላህም የሚሻውን ያጠማል፡፡ የሚሻውንም ያቀናል፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,5,'14:5','ሙሳንም ወገኖችህን ከጨለማዎች ወደ ብርሃን አውጣ አላህንም ቀኖች አስገንዝባቸው በማለት በተዓምራታችን በእርግጥ ላክነው፡፡ በዚህ ውስጥ በብዙ ታጋሽና በብዙ አመስጋኝ ለኾኑት ሁሉ በእርግጥ ተዓምራቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,6,'14:6','ሙሳም ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፤ ከፈርዖን ቤተሰቦች መጥፎን ቅጣት የሚያቀምሱዋችሁ ወንዶች ልጆቻችሁንም የሚያርዱ ሴቶቻችሁንም የሚተዉ ሲኾኑ በአዳንናችሁ ጊዜ በእናንተ ላይ (የዋለላችሁን) የአላህን ጸጋ አስታውሱ፡፡ በዚህም ከጌታችሁ የኾነ ታላቅ ፈተና አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(14,7,'14:7','ጌታችሁም «ብታመሰግኑ በእርግጥ እጨምርላችኋለሁ ብትክዱም (እቀጣችኋለሁ) ቅጣቴ በእርግጥ ብርቱ ነውና» በማለት ባስታወቀ ጊዜ (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(14,8,'14:8','ሙሳም አለ «እናንተም በምድር ያለውም ሁሉ በመላ ብትክዱ አላህ በእርግጥ ተብቃቂ ምስጉን ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,9,'14:9','የእነዚያ ከናንተ በፊት የነበሩት የኑሕ ሕዝቦች የዓድም የሰሙድም የእነዚያም ከእነሱ በኋለ የነበሩት ከአላህ በስተቀር ሌላ የማያውቃቸው (ሕዝቦች) ወሬ አልመጣላችሁምን መልክተኞቻቸው በማስረጃዎች መጧቸው፡፡ እጆቻቸውንም (በቁጭት ሊነክሱ) ወደ አፎቻቸው መለሱ፡፡ አሉም «እኛ እናንተ በርሱ በተላካችሁበት ነገር ካድን እኛም ወደርሱ ከምትጠሩን ነገር አወላዋይ በኾነ ጥርጣሬ ውስጥ ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,10,'14:10','መልክተኞቻቸው «ከኀጢአቶቻችሁ እንዲምራችሁ ወደ ተወሰነ ጊዜም (ያለ ቅጣት) እንዲያቆያችሁ የሚጠራችሁ ሲኾን ሰማያትንና ምድርን ፈጣሪ በኾነው አላህ (በመኖሩ) ጥርጣሬ አለን» አሏቸው፡፡ (ሕዝቦቹም) «እናንተ ብጤያችን ሰው እንጂ አይደላችሁም አባቶቻችን ይገዙት ከነበሩት ልትከለክሉን ትሻላችሁ፡፡ ግልጽንም አስረጅ (ካመጣችሁት ሌላ) አምጡልን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,11,'14:11','መልክተኞቻቸው ለእነርሱ አሉ «እኛ ብጤያችሁ ሰው እንጂ ሌላ አይደለንም ግን አላህ ከባሮቹ በሚሻው ሰው ላይ ይለግሳል፡፡ ለእኛም በአላህ ፈቃድ እንጂ ማስረጃን ልናመጣላችሁ አይገባንም፡፡ በአላህም ላይ ምእምናኖች ይጠጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,12,'14:12','«መንገዳችንንም በእርግጥ የመራን ሲኾን በአላህ ላይ የማንመካ ለእኛ ምን አለን በማሰቃየታችሁም ላይ በእርግጥ እንታገሳለን፡፡ በአላህም ላይ ተመኪዎች ሁሉ ይመኩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,13,'14:13','እነዚያም የካዱት ለመልክተኞቻቸው «ከምድራችን በእርግጥ እናወጣችኋለን፤ ወይም ወደ ሃይማኖታችን በእርግጥ ትመለሳላችሁ» አሉ፡፡ ወደእነርሱም ጌታቸው እንዲህ ሲል ላከ «ከሓዲዎችን በእርግጥ እናጠፋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(14,14,'14:14','«ከእነሱም በኋላ ምድሪቱን በእርግጥ እናስቀምጣችኋለን፡፡ ይኸ በፊቴ መቆሙን ለሚፈራ ዛቻዬንም ለሚፈራ ሰው ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,15,'14:15','እርዳታንም (ከአላህ) ፈለጉ፤ (ተረዱም)፡፡ ጨካኝ ሞገደኛ የኾነም ሁሉ አፈረ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,16,'14:16','ከስተፊቱ ገሀነም አለበት፡፡ እዥ ከኾነም ውሃ ይጋታል፡፡');
INSERT INTO "sadiq_sani" VALUES(14,17,'14:17','ይጎነጨዋል፤ ሊውጠውም አይቀርብም፡፡ ሞትም ከየስፍራው ሁሉ ይመጣበታል፤ እርሱም የሚሞት አይደለም፡፡ ከስተፊቱም ከባድ ቅጣት አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,18,'14:18','የእነዚያ በጌታቸው የካዱት ሰዎች ምሳሌ (መልካም) ሥራዎቻቸው በነፋሻ ቀን ነፋስ በርሱ እንደ በረታችበት አመድ ነው፡፡ በሠሩት ሥራ በምንም ላይ (ሊጠቀሙ) አይችሉም፡፡ ይህ እርሱ ሩቅ ጥፋት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,19,'14:19','አላህ ሰማያትንና ምድርን በእውነት የፈጠረ መኾኑን አታይምን ቢሻ ያጠፋችኋል፡፡ አዲስ ፍጡርንም ያመጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(14,20,'14:20','ይህም በአላህ ላይ አስቸጋሪ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(14,21,'14:21','የተሰበሰቡም ኾነው ለአላህ ይገለጻሉ፡፡ (ሐሳበ) ደካማዎቹም (ተከታዮች) ለእነዚያ ለኮሩት «እኛ ለእናንተ ተከታዮች ነበርን፤ እናንተ ከአላህ ቅጣት ከኛ ላይ አንዳችን ነገር ተከላካዮች ናችሁን» ይላሉ፡፡ (አስከታዮቹም) «አላህ ባቀናን ኖሮ በእርግጥ በመራናችሁ ነበር» ይሏቸዋል፡፡ «ብንበሳጭ ወይም ብንታገስም በእኛ ላይ እኩል ነው፡፡ ለእኛ ምንም መጠጊያ የለንም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,22,'14:22','ነገሩም በተፈጸመ ጊዜ ሰይጣን ይላቸዋል «አላህ እውነተኛውን ቃል ኪዳን ገባላችሁ፡፡ ቃል ኪዳንም ገባሁላችሁ፤ አፈረስኩባችሁም፡፡ ለእኔም በእናንተ ላይ ምንም ስልጣን አልበረኝም፡፡ ግን ጠራኋችሁ፡፡ ለእኔም ታዘዛችሁ፡፡ ስለዚህ አትውቀሱኝ፤ ነፍሶቻችሁንም ውቀሱ፡፡ እኔ የምረዳችሁ አይደለሁም፡፡ እናንተም የምትረዱኝ አይደላችሁም፡፡ እኔ ከአሁን በፊት ለአላህ ተጋሪ በአደረጋችሁት ነገር ካድኩ፡፡ በዳዮቹ ለእነርሱ በእርግጥ አሳማሚ ቅጣት አላቸው፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,23,'14:23','እነዚያም ያመኑና መልካም ሥራዎችን የሠሩ ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ በጌታቸው ፈቃድ እንዲገቡ ይደረጋሉ፡፡ በውስጣቸው መከባበሪያቸው ሰላም (ለእናንተ ይኹን መባባል) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,24,'14:24','አላህ መልካምን ቃል ሥርዋ (በምድር ውስጥ) የተደላደለች ቅርንጫፍዋም ወደ ሰማይ (የረዘመች) እንደሆነች መልካም ዛፍ አድርጎ ምሳሌን እንዴት እንደገለጸ አላየኽምን');
INSERT INTO "sadiq_sani" VALUES(14,25,'14:25','ምግቧን (ፍሬዋን) በጌታዋ ፈቃድ በየጊዜው ትሰጣለች፡፡ አላህም ለሰዎች ይገሰጹ ዘንድ ምሳሌዎችን ይገልጻል፡፡');
INSERT INTO "sadiq_sani" VALUES(14,26,'14:26','የመጥፎም ቃል ምሳሌ ከምድር በላይ የተጎለሰሰች ለእርሷ ምንም መደላደል የሌላት እንደኾነች መጥፎ ዛፍ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,27,'14:27','አላህ እነዚያን ያመኑትን በቅርቢቱም ሕይወት በመጨረሻይቱም (በመቃብር) በተረጋገጠው ቃል ላይ ያረጋቸዋል፡፡ ከሓዲዎችንም አላህ ያሳስታቸዋል፡፡ አላህም የሚሻውን ይሠራል፡፡');
INSERT INTO "sadiq_sani" VALUES(14,28,'14:28','ወደእነዚያ የአላህን ጸጋ በክህደት ወደ ለወጡት ሕዝቦቻቸውንም በጥፋት አገር ወዳሰፈሩት (ወደ ቁረይሾች) አላየህምን');
INSERT INTO "sadiq_sani" VALUES(14,29,'14:29','(አገሪቱም) የሚገቧት ስትኾን ገሀነም ናት፡፡ ምን ትከፋም መርጊያ!');
INSERT INTO "sadiq_sani" VALUES(14,30,'14:30','ለአላህም ከመንገዱ ያሳስቱ ዘንድ ባላንጣዎችን አደረጉለት፡፡ «(ጥቂትን) ተጠቀሙ መመለሻችሁም ወደ እሳት ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,31,'14:31','ለእነዚያ ላመኑት ባሮቼ በእርሱ ውስጥ ሽያጭና ወዳጂነት የሌለበት ቀን ከመምጣቱ በፊት ሶላትን በደንቡ ይሰግዳሉ፡፡ ከሰጠናቸውም ሲሳይ በምስጢርም በግልጽም ይለግሳሉ፡፡ (ስገዱ ለግሱም) በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,32,'14:32','አላህ ያ ሰማያትንና ምድርን የፈጠረ ከሰማይም ውሃን ያወረደ በእርሱም ከፍሬዎች ሲሳይን ለእናንተ ያወጣ መርከቦችንም በፈቃዱ በባሕር ላይ ይንሻለሉ ዘንድ ለናንተ የገራ ወንዞችንም ለናንተ የገራ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,33,'14:33','ፀሐይንና ጨረቃንም ዘወትር ኼያጆች ሲኾኑ ለእናንተ የገራ ሌሊትንና ቀንንም ለእናንተ የገራላችሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,34,'14:34','ከለመናችሁትም ሁሉ የሰጣችሁ ነው፡፡ የአላህንም ጻጋ ብትቆጥሩ አትዘልቋትም፡፡ ሰው በጣም በደለኛ ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,35,'14:35','ኢብራሂምም ባለ ጊዜ (አስታውስ) «ጌታዬ ሆይ! ይህንን አገር (መካን) ጸጥተኛ አድርገው፡፡ እኔንም ልጆቼንም ጣዖታትን ከመገዛት አርቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(14,36,'14:36','«ጌታዬ ሆይ! እነርሱ (ጣዖታት) ከሰዎች ብዙዎችን አሳስተዋልና፡፡ የተከተለኝም ሰው እርሱ ከኔ ነው፡፡ ትእዛዜንም የጣሰ ሰው አንተ መሓሪ አዛኝ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,37,'14:37','«ጌታችን ሆይ! እኔ አዝመራ በሌለው ሸለቆ ውስጥ በተከበረው ቤት (በካዕባ) አጠገብ ከዘሮቼ አስቀመጥኩ፡፡ ጌታችን ሆይ! ሶላትን ያቋቁሙ ዘንድ (አስቀመጥኳቸው)፡፡ ከሰዎችም ልቦችን ወደእነሱ የሚናፍቁ አድርግ፡፡ ያመሰግኑህም ዘንድ ከፍሬዎች ስጣቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,38,'14:38','«ጌታችን ሆይ! አንተ የምንደብቀውንም የምንገልጸውንም ሁሉ በእርግጥ ታውቃለህ፡፡ በአላህም ላይ በምድርም በሰማይም ምንም ነገር አይደበቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(14,39,'14:39','«ለእዚያ ከሽምግልና በኋላ ኢስማዒልን ኢስሐቅንም ለእኔ ለሰጠኝ አላህ ምሰጋና ይገባው፡፡ ጌታዬ ልመናን በእርግጥ ሰሚ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(14,40,'14:40','«ጌታዬ ሆይ! ሰላትን አዘውትሬ የምሰግድ አድርገኝ፡፡ ከዘሮቼም (አድርግ)፡፡ ጌታችን ሆይ! ጸሎቴንም ተቀበለኝ፤');
INSERT INTO "sadiq_sani" VALUES(14,41,'14:41','«ጌታችን ሆይ! ለእኔም ለወላቼም ለምእመናንም ሁሉ ምርመራ በሚደረግበት ቀን ማር፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,42,'14:42','አላህንም በደለኞች ከሚሠሩት ግፍ ዘንጊ አድርገህ አታስብ፡፡ የሚያቆያቸው ዓይኖች በእርሱ እስከሚፈጡበት ቀን ድረስ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,43,'14:43','(ወደ ጠሪው መልአክ) ቸኳዮች ራሶቻቸውን አንጋጣጮች ኾነው (ዓይኖቻቸው ይፈጣሉ)፡፡ ዓይኖቻቸው ወደነርሱ አይመለሱም፡፡ ልቦቻቸውም ባዶዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,44,'14:44','ሰዎችንም ቅጣቱ የሚመጣባቸውን ቀን አስፈራራቸው፡፡ እነዚያም የበደሉት «ጌታችን ሆይ! ወደ ቅርበ ጊዜ ድረስ አቆየን፡፡ ጥሪህን እንቀበላለንና መልክተኞቹንም እንከተላለንና» ይላሉ፡፡ «ከአሁን በፊት (በምድረ ዓለም) ለእናንተ ምንም መወገድ የላችሁም በማለት የማላችሁ አልነበራችሁምን» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(14,45,'14:45','«በእነዚያም ነፍሶቻቸውን በበደሉት ሰዎች መኖሪያ ውስጥ ተቀመጣችሁ፡፡ በእነሱም እንዴት እንደሠራንባቸው ለእናንተ ተገለጸላችሁ፡፡ ለእናንተም ምሳሌዎችን ገለጽንላችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(14,46,'14:46','(በነቢዩ ላይ) ሴራቸውንም በእርግጥ አሴሩ፡፡ ሴራቸውም (ቅጣቱ) አላህ ዘንድ ነው፡፡ ሴራቸውም ኮረብታዎች (ሕግጋቶች) በእርሱ የሚወገዱበት አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(14,47,'14:47','አላህንም መልክተኞቹን (የገባላቸውን) ቃል ኪዳኑን አፍራሽ አድርገህ አታስብ፡፡ አላህ አሸናፊ የመበቀል ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,48,'14:48','ምድር በሌላ ምድር የምትለወጥበትን ሰማያትም (እንደዚሁ) አንድ አሸናፊ ለኾነው አላህም (ፍጡራን ሁሉ) የሚገለጹበት ቀን (አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(14,49,'14:49','አመጸኞችንም በዚያ ቀን በሰንሰለቶች ተቆራኝተው ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(14,50,'14:50','ቀሚሶቻቸው ከካትራም ናቸው፡፡ ፊቶቻቸውንም እሳት ትሸፍናቸዋለች፡፡');
INSERT INTO "sadiq_sani" VALUES(14,51,'14:51','አላህ ነፍስን ሁሉ የሠራችውን ይመነዳ ዘንድ (ይህንን አደረገ)፡፡ አላህ ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(14,52,'14:52','ይህ (ቁርኣን) ለሰዎች ገላጭ ነው፡፡ (ሊመከሩበት)፣ በእርሱም ሊስፈራሩበት፣ እርሱም አንድ አምላክ ብቻ መኾኑን ሊያውቁበት፣ የአእምሮ ባለቤቶችም ሊገሰፁበት (የተወረደ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,1,'15:1','አ.ለ.ረ (አሊፍ ላም ራ) ይህች (አናቅጽ) ከመጽሐፉ አንቀጾችና ገላጭ ከኾነው ቁርኣን ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,2,'15:2','እነዚያ የካዱት (በትንሣኤ ቀን) ሙስሊሞች በኾኑ ኖሩ በብዛት ይመኛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,3,'15:3','ተዋቸው ይብሉ፤ ይጠቀሙም፡፡ ተስፋም ያዘናጋቸው፡፡ በእርግጥም (መጥፎ ፍጻሜያቸውን) ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,4,'15:4','ማንኛይቱንም ከተማ ለእርሷ የተወሰነ መጽሐፍ (ጊዜያት) ያላት ኾና እንጂ አላጠፋንም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,5,'15:5','ማንኛይቱም ሕዝብ ጊዜዋን ምንም አትቀድምም፡፡ (ከእርሱ) አይቆዩምም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,6,'15:6','«አንተ ያ በእርሱ ላይ ቁርኣን የተወረደለት ሆይ! አንተ በእርግጥ ዕብድ ነህ» አሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,7,'15:7','«ከእውነተኞቹ እንደ ኾንክ በመላእክት (መስካሪ) ለምን አትመጣንም» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,8,'15:8','መላእክትን በእውነት (በቅጣት) እንጂ አናወርድም፡፡ ያን ጊዜም የሚቆዩ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,9,'15:9','እኛ ቁርኣንን እኛው አወረድነው፡፡ እኛም ለእርሱ ጠባቂዎቹ ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,10,'15:10','ከአንተም በፊት በነበሩት በመጀመሪያዎቹ ሕዝቦች ውስጥ (መልክተኞችን) በእርግጥ ልከናል፡፡');
INSERT INTO "sadiq_sani" VALUES(15,11,'15:11','ማንኛውም መልእክተኛ በእርሱ የሚያላግጡበት ኾነው እንጅ አይመጣቸውም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(15,12,'15:12','እንደዚሁ (ማስተባበልን) በአመጸኞቹ ልቦች ውስጥ እናገባዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,13,'15:13','በእርሱ አያምኑበትም፡፡ የቀድሞዎቹም ድርጊት በእርግጥ አልፋለች፤ (እንደጠፉ ይጠፋሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,14,'15:14','በእነርሱም ላይ ከሰማይ ደጃፍን በከፈትንላቸውና በእርሱ ሲወጡ በዋሉ ኖሮ፤');
INSERT INTO "sadiq_sani" VALUES(15,15,'15:15','«የተዘጉት ዓይኖቻችን ናቸው፡፡ እንዲያውም እኛ የተደገመብን ሰዎች ነን» ባሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(15,16,'15:16','በሰማይም ላይ ቡርጆችን በእርግጥ አድርገናል፡፡ ለተመልካቾችም (በከዋክብት) አጊጠናታል፡፡');
INSERT INTO "sadiq_sani" VALUES(15,17,'15:17','ከተባረረ ሰይጣንም ሁሉ ጠብቀናታል፡፡');
INSERT INTO "sadiq_sani" VALUES(15,18,'15:18','ግን (ወሬ) መስማትን የሚሰርቅ ወዲየውኑ አብሪ ችቦ ይከተለዋል፡፡ (ያቃጥለዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,19,'15:19','ምድርንም ዘረጋናት፡፡ በውስጧም ኮረብታዎችን ጣልናባት፡፡ በውስጧም (የተለካን) በቃይ ሁሉ አበቀልንባት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,20,'15:20','በእርሷም ውስጥ ለእናንተ መኖሪያዎችንና ለእርሱ መጋቢዎቹ ያልኾናችሁትንም (እንስሳትን) አደረግንላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,21,'15:21','መካዚኖቹም (መክፈቻቸው) እኛ ዘንድ ያልሆነ ምንም ነገር የለም፡፡ በተወሰነም ልክ እንጂ አናወርደውም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,22,'15:22','ነፋሶችንም (ደመናን) ተሸካሚዎች አድርገን ላክን፡፡ ከሰማይም (ከደመና) ዝናብን አወረድን፡፡ እርሱንም አጠጣናችሁ፡፡ እናንተም ለእርሱ አድላቢዎች አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,23,'15:23','እኛም ሕያው የምናደርግና የምንገድል እኛው ብቻ ነን፡፡ እኛም (ፍጡርን ሁሉ ) የምንወርስ (ቀሪ) ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,24,'15:24','ከእናንተም ተቀዳሚዎቹን በእርግጥ ዐውቀናል፡፡ ወደ ኋላም ቀሪዎቹን በእርግጥ ዐውቀናል፡፡');
INSERT INTO "sadiq_sani" VALUES(15,25,'15:25','ጌታህም እርሱ ይሰበስባችኋል፡፡ እርሱ ጥበበኛ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(15,26,'15:26','ሰውንም ከሚቅጨለጨል ሸክላ ከሚገማ ጥቁር ጭቃ በእርግጥ ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,27,'15:27','ጃንንም (ከሰው) በፊት ከእሳት ነበልባል ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,28,'15:28','ጌታህም ለመላእክት ባለጊዜ (አስታውስ)፡፡ እኔ ሰውን ከሚቅጨለጨል ሸክላ ከሚገማ ጭቃ እፈጥራለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,29,'15:29','(ፍጥረቱን) ባስተካከልኩትና በውስጡ ከመንፈሴ በነፋሁበትም ጊዜ ለእርሱ ሰጋጆች ኾናችሁ ውደቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,30,'15:30','መላእክትም መላውም ተሰብስበው ሰገዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,31,'15:31','ኢብሊስ (ዲያብሎስ) ብቻ ሲቀር፡፡ ከሰጋጆቹ ጋር ከመኾን እንቢ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,32,'15:32','(አላህም) «ኢብሊስ ሆይ! ከሰጋጆቹ ጋር የማትኾን ላንተ ምን ምክንያት አለህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,33,'15:33','«ከሚቅጨለጨል ሸክላ ከግም ጭቃ ለፈጠርከው ሰው ልሰግድ አይገባኝም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,34,'15:34','(አላህ) አለው «ከርሷም ውጣ፡፡ አንተ የተባረርክ እርጉም ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,35,'15:35','«ባንተ ላይም እስከ ፍርዱ ቀን ድረስ እርግማን አለብህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,36,'15:36','«ጌታዬ ሆይ! እንግዲውያስ (ሰዎች) እስከሚቀሰቀሱበት ቀን ድረስ አቆየኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,37,'15:37','(አላህም) አለ «አንተ ከሚቆዩት ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,38,'15:38','«እስከታወቀው ወቅት ቀን ድረስ፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,39,'15:39','(ኢብሊስ) አለ «ጌታዬ ሆይ! እኔን በማጥመምህ ይኹንብኝ ለነርሱ በምድር ላይ (ኀጢአትን) እሸልምላቸዋለሁ፡፡ ሁሉንም አጠማቸዋለሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,40,'15:40','«ከነርሱ ፍጹሞቹ ባሮችህ ብቻ ሲቀሩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,41,'15:41','(አላህም) አለ «ይህ በእኔ ላይ (መጠበቁ የተገባ) ቀጥተኛ መንገድ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,42,'15:42','«እነሆ ባሮቼ በእነሱ ላይ ላንተ ስልጣን የለህም፡፡ ከጠማሞቹ የተከተለህ ሰው ብቻ ሲቀር፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,43,'15:43','«ገሀነምም (ለርሱና ለተከተሉት) ለመላውም በእርግጥ ቀጠሯቸው ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,44,'15:44','«ለእርሷ ሰባት ደጃፎች አሏት፡፡ ለየደጀፉም ሁሉ ከእነሱ የተከፈለ ፋንታ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,45,'15:45','«እነዚያ (ከኀጢአት) የተጠነቀቁት በአትክልቶችና በምንጮች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,46,'15:46','«ጸጥተኞች ኾናችሁ በሰላም ግቧት (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,47,'15:47','«ባልጋዎችም ላይ ፊት ለፊት የሚቅጣጩ ወንድማማቾች ኾነው በደረቶቻቸው ውስጥ ከቂም ያለውን ሁሉ እናስወግዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,48,'15:48','«በእርሷም ውስጥ ድካም አይነካቸውም፡፡ እነሱም ከእርሷ የሚወጡ አይደሉም፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,49,'15:49','ባሮቼን እኔ መሓሪው አዛኙ እኔው ብቻ መኾኔን ንገራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,50,'15:50','ቅጣቴም እርሱ አሳማሚ ቅጣት መኾኑን (ንገራቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,51,'15:51','ከኢብራሂም እንግዶችም (ወሬ) ንገራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,52,'15:52','በእርሱ ላይ በገቡና ሰላም ባሉት ጊዜ የኾነውን (አስታውስ)፡፡ (እርሱም) «እኛ ከእናንተ ፈሪዎች ነን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,53,'15:53','«አትፍራ፤ እኛ ዐዋቂ በኾነ ወንድ ልጅ እናበስርሃለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,54,'15:54','«እርጅና የደረሰብኝ ከመኾኔ ጋር አበሰራችሁኝን በምን ታበስሩኛላችሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,55,'15:55','«በእውነት አበሰርንህ፡፡ ከተሰፋ ቆራጮችም አትኹን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,56,'15:56','«ከጠማማዎችም በስተቀር ከጌታው እዝነት ተስፋን የሚቆርጥ ማነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,57,'15:57','«እናንተ መልክተኞች ሆይ! ኧረ ለመኾኑ ነገራችሁ ምንድን ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,58,'15:58','«እኛ አመጸኞች ወደ ኾኑ ሕዝቦች ተልከናል» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,59,'15:59','«የሉጥ ቤተሰቦች ብቻ ሲቀሩ፡፡ (እነርሱን) እኛ ሁላቸውንም በእርግጥ የምናድናቸው ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,60,'15:60','«ሚስቱ ብቻ ስትቀር፡፡ እርሷን (በቅጣቱ ውስጥ) ከሚቀሩት መኾኗን ወሰነናል» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,61,'15:61','መልክተኞቹም የሉጥን ቤተሰቦች በመጡባቸው ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(15,62,'15:62','(ሉጥ) «እናንተ የተሳታችሁ ሕዝቦች ናችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,63,'15:63','«አይደለም እኛ ሕዝቦችህ በርሱ ይጠራጠሩበት በነበሩት ቅጣት መጣንህ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,64,'15:64','«እውነትንም ይዘን መጣንህ፡፡ እኛም በእርግጥ እውነተኞች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,65,'15:65','ከቤተሰብህም ጋር በሌሊቱ ከፊል ኺድ፡፡ ከኋላቸውም ተከተል፡፡ ከእናንተም አንድም ሰው ወደ ኋላው አይገላመጥ፡፡ ወደ ታዘዛችሁበትም ስፍራ እለፉ» (አሉት)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,66,'15:66','ወደርሱም ያንን ትእዛዝ አወረድን፡፡ (እርሱም) የእነዚህ (ሕዝቦች) መጨረሻ ያነጉ ሲኾኑ (ሥራቸው) መቆረጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,67,'15:67','የከተማይቱም ሰዎች የሚበሳሰሩ ኾነው መጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,68,'15:68','(ሉጥም) አለ «እነዚህ እንግዶቼ ናቸውና አታጋልጡኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,69,'15:69','«አላህንም ፍሩ፤ አታሳፍሩኝም፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,70,'15:70','«ከዓለማት (ማንንም እንዳታስተናግድ) አልከለከልንህምን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,71,'15:71','(ሉጥም) «እነኝህ ሴቶች ልጆቼ ናቸው፡፡ ሠሪዎች ብትኾኑ (አግቧቸው)» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,72,'15:72','በዕድሜህ እንምላለን እነሱ በእርግጥ በስከራቸው ውስጥ ይዋልላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,73,'15:73','ጩኸቲቱም ፀሐይ የወጣችባቸው ሲኾኑ ያዘቻቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,74,'15:74','ላይዋንም ከታችዋ አደረግን፡፡ በእነሱም ላይ የሸክላ ደንጊያዎችን አዘነብንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,75,'15:75','በዚህ ውስጥ ለተመልካቾች በእርግጥ መገምገሚያዎች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,76,'15:76','እሷም (ከተማይቱ) በቀጥታ መንገድ ላይ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(15,77,'15:77','በዚህ ውስጥ በእርግጥ ለምእምናን መገሰጫ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,78,'15:78','እነሆ የአይከት ሰዎችም በእርግጥ በዳዮች ነበሩ፤');
INSERT INTO "sadiq_sani" VALUES(15,79,'15:79','ከነሱም ተበቀልን ሁለቱም (የሉጥና የአይከት ሕዝቦች ከተሞች) በእርግጥ በግልጽ መንገድ ላይ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,80,'15:80','የሒጅርም ሰዎች መልክተኞቹን በእርግጥ አስተባበሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,81,'15:81','ተዓምራታችንንም ሰጠናቸው፡፡ ከእርሷም ዘንጊዎች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,82,'15:82','ከኮረብታዎችም ቤቶችን ጸጥተኞች ኾነው ይጠርቡ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(15,83,'15:83','ያነጉም ሲኾኑ ጩኸት ያዘቻቸው፤ (ወደሙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,84,'15:84','ይሠሩትም የነበሩት ሕንጻ ምንም አልጠቀማቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(15,85,'15:85','ሰማያትንና ምድርንም በመካከላቸው ያለውንም ሁሉ በእውነት እንጂ (በላግጣ) አልፈጠርንም፡፡ ሰዓቲቱም በእርግጥ መጪ ናት፡፡ መልካምንም ይቅርታ አድርግላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,86,'15:86','ጌታህ እርሱ (ያልነበረን) ፈጣሪው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,87,'15:87','ከሚደጋገሙ የኾኑን ሰባትንና ታላቁንም ቁርኣን (በሙሉ) በእርግጥ ሰጠንህ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,88,'15:88','ከእነርሱ (ከከሓዲዎች) ብዙዎችን ወገኖች በእርሱ ወደ አጣቀምንበት ጸጋ ዓይኖችህን አትዘርጋ፡፡ በእነሱም ላይ (ባያምኑ) አትዘን፡፡ ክንፍህንም ለምእምናን አለዝብ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,89,'15:89','በልም «እኔ ግልጽ አስፈራሪው እኔ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(15,90,'15:90','(ሰዎችን ለማገድ የመካን በሮች) በተከፋፈሉት ላይ እንደ አወረድነው (በኾነ ቅጣት አሰፈራሪያችሁ ነኝ በል)፡፡');
INSERT INTO "sadiq_sani" VALUES(15,91,'15:91','(እነርሱም) እነዚያ ቁርኣንንን ክፍልፍሎች ያደረጉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,92,'15:92','በጌታህም እንምላለን፡፡ ሁላቸውንም እንጠይቃቸዋለን፤');
INSERT INTO "sadiq_sani" VALUES(15,93,'15:93','ይሠሩት ከነበሩት ነገር ሁሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,94,'15:94','የታዘዝክበትንም ነገር በይፋ ግለጽ፡፡ አጋሪዎችንም ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(15,95,'15:95','ተሳላቂዎችን ሁሉ እኛ በቅተንሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(15,96,'15:96','(እነሱም) እነዚያ ከአላህ ጋር ሌላ አምላክን ያደረጉ ናቸው፡፡ በእርግጥም (ፍጻሜያቸውን) ወደፊት ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(15,97,'15:97','አንተም በሚሉት ነገር ልብህ የሚጠብ መኾኑን በእርግጥ እናውቃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,98,'15:98','ጌታህንም ከማመስገን ጋር አጥራው ከሰጋጆቹም ኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(15,99,'15:99','እውነቱም (ሞት) እስኪመጣህ ድረስ ጌታህን ተገዛ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,1,'16:1','የአላህ ትዕዛዝ መጣ፤ ስለዚህ አታስቸኩሉት፤ ከማይገባው ሁሉ ጠራ ከሚያጋሩትም ሁሉ ላቀ፤');
INSERT INTO "sadiq_sani" VALUES(16,2,'16:2','ከባሮቹ በሚሻው ሰው ላይ መላእክትን ከራእይ ጋር በፈቃዱ ያወርዳል (ከሓዲዎችን በቅጣት) አስጠንቅቁ፤ እነሆ ከኔ በቀር ሌላ አምላክ የለም፤ ፍሩኝም ማለትን (አስታውቁ በማለት ያወርዳል)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,3,'16:3','ሰማያትንና ምድርን በእውነት ፈጠረ ከሚያጋሩት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,4,'16:4','ሰውን ከፍትወት ጠብታ ፈጠረው፡፡ ከዚያም እርሱ ወዲያውኑ (ትንሣኤን በመካድ) ግልጽ ተከራካሪ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,5,'16:5','ግመልን፣ ከብትን፣ ፍየልንም በርሷ (ብርድ መከላከያ) ሙቀት ጥቅሞችም ያሉባት ስትኾን ለእናንተ ፈጠረላችሁ፡፡ ከርሷም ትበላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,6,'16:6','ለእናንተም በእርሷ ወደ ማረፊያዋ በምትመልሷት ጊዜ በምታሰማሩዋትም ጊዜ ውበት አላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,7,'16:7','ጓዞቻችሁንም በነፍሶች ችግር እንጂ ወደማትደርሱበት አገር ትሸከማለች፡፡ ጌታችሁ በእርግጥ ሩኅሩኅ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,8,'16:8','ፈረሶችንም፣ በቅሎዎችንም፣ አህዮችንም ልትቀመጡዋቸውና ልታጌጡባቸው (ፈጠረላችሁ)፡፡ የማታውቁትንም ድንቅ ነገር ይፈጥራል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,9,'16:9','በአላህም ላይ (በችሮታው) ቀጥተኛውን መንገድ መግለጽ አለበት፡፡ ከእርሷም (ከመንገድ) ጠማማ አልለ፡፡ በሻም ኖሮ ሁላችሁንም በእርግጥ ባቀናችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(16,10,'16:10','እርሱ ያ ከሰማይ ውሃን ያወረደ ነው፡፡ ከእርሱ ለእናንተ መጠጥ አላችሁ፡፡ ከእርሱም (እንስሳዎችን) በእርሱ የምታሰማሩበት ዛፍ (ይበቅልበታል)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,11,'16:11','በእርሱ ለእናንተ አዝመራን፣ (የዘይት) ወይራንም፣ ዘምባባዎችንም (ተምርን)፣ ወይኖችንም፣ ከፍሬዎችም ሁሉ ያበቅልላችኋል፡፡ በዚህ ውስጥ ለሚያስተነትኑ ሕዝቦች በእርግጥ ተዓምርአልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,12,'16:12','ለእናንተም ሌሊትንና ቀንን፣ ፀሐይንና ጨረቃንም ገራላችሁ፡፡ ከዋክብትም በፈቃዱ የተገሩ ናቸው፡፡ በዚህ ውስጥ ለሚያውቁ ሕዝቦች በእርግጥ ተዓምራቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,13,'16:13','በምድርም ላይ መልኮቹ የተለያዩ ኾኖ ለእናንተ የፈጠረላችሁን ሁሉ (ገራላችሁ)፡፡ በዚህ ውስጥ ለሚገሰጹ ሕዝቦች በእርግጥ ተዓምር አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,14,'16:14','እርሱም ያ ባሕርን ከእርሱ እርጥብ ስጋን ልትበሉና ከእርሱም የምትለብሱትን ጌጣጌጥ ታወጡ ዘንድ የገራ ነው፡፡ መርከቦችንም በውስጡ (ውሃውን) ቀዳጆች ኾነው (ሲንሻለሉ) ታያለህ፡፡ ከችሮታው ልትፈልጉና ልታመሰግኑም (ገራላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,15,'16:15','በምድርም ውስጥ በእናንተ እንዳታረገርግ ተራራዎችን ጣለባት፡፡ ጂረቶችንም፣ መንገዶችንም ትመሩ ዘንድ (አደረገ)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,16,'16:16','ምልክቶችንም (አደረገ)፡፡ በከዋክብትም እነሱ ይምመራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,17,'16:17','የሚፈጥር እንደማይፈጥር ነውን አትገሰጹምን');
INSERT INTO "sadiq_sani" VALUES(16,18,'16:18','የአላህንም ጸጋ ብትቆጥሩ አትዘልቋትም፡፡ አላህ በእርግጥ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,19,'16:19','አላህም የምትደብቁትንና የምትገልጹትን ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,20,'16:20','እነዚያም ከአላህ ሌላ የምትገዟቸው ምንንም አይፈጥሩም፡፡ እነርሱም ይፈጠራሉ፤');
INSERT INTO "sadiq_sani" VALUES(16,21,'16:21','ሕያው ያልኾኑ ሙታን ናቸው፡፡ መቼ እንደሚቀሰቀሱም አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,22,'16:22','አምላካችሁ አንድ አምላክ ብቻ ነው፡፡ እነዚያም በመጨረሻይቱ ዓለም የማያምኑት ልቦቻቸው ከሓዲዎች ናቸው፡፡ እነሱም የኮሩ ናቸው፤ (አያምኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,23,'16:23','አላህ የሚደብቁትንና የሚገልጹትን ሁሉ የሚያውቅ ለመኾኑ ጥርጣሬ የለበትም፡፡ እርሱ ኩሩዎችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,24,'16:24','ለእነርሱም «ጌታችሁ (በሙሐመድ ላይ) ምንን አወረደ» በተባሉ ጊዜ «(እርሱ) የመጀመሪያዎቹ ሕዝቦች ተረቶች ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,25,'16:25','(ይህንንም የሚሉት) በትንሣኤ ቀን ኀጢኣቶቻቸውን በሙሉ ከእነዚያም ያለ ዕውቀት ኾነው ከሚያጠሟቸው ሰዎች ኀጢአቶች ከፊሉን ሊሸከሙ ነው፡፡ ንቁ! የሚሸከሙት ኃጢኣት ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(16,26,'16:26','እነዚያ ከእነሱ (ከቁረይሾች) በፊት የነበሩት በእርግጥ መከሩ፡፡ አላህም ሕንጻዎቻቸውን ከመሠረቶቻቸው አፈረሰ፡፡ ጣሪያውም በእነሱ ላይ ከበላያቸው ወደቀባቸው፡፡ ቅጣቱም ከማያውቁት ሥፍራ መጣባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,27,'16:27','ከዚያም በትንሣኤ ቀን ያዋርዳቸዋል፡፡ እነዚያም «በእነሱ (ጉዳይ) ትከራከሩኝ የነበራችሁት ተጋሪዎቼ የት ናቸው» ይላቸዋል፡፡ «እነዚያ ዕውቀትን የተሰጡት ዛሬ ሐፍረቱና ቅጣቱ በእርግጥ በከሓዲዎች ላይ ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,28,'16:28','(እነርሱ) «እነዚያ ነፍሶቻቸውን በዳይ ኾነው መላእክት የሚገድሏቸው ናቸው፡፡ ከክፉ ሥራ ምንም ነገር የምንሠራም አልነበርን» (ሲሉ) ታዛዥነታቸውን ይገልጻሉ፡፡ «በእውነት አላህ ትሰሩት የነበራችሁትን ነገር ዐዋቂ ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,29,'16:29','«የገሀነምንም ደጃፎች በውስጧ ዘውታሪዎች ስትኾኑ ግቡ» (ይባላሉ)፡፡ የኩሩዎችም መኖሪያ (ገሀነም) በእርግጥ ከፋች!');
INSERT INTO "sadiq_sani" VALUES(16,30,'16:30','ለእነዚያም ለተጠነቀቁት «ጌታችሁ ምንን ነገር አወረደ» ተባሉ፡፡ «መልካምን ነገር» አሉ፡፡ ለእነዚያ ደግ ለሠሩት በዚሀች በቅርቢቱ ዓለም መልካም ኑሮ አላቸው፡፡ የመጨረሻይቱም አገር በእርግጥ በላጭ ናት፡፡ የጥንቁቆቹም አገር ምን ታምር!');
INSERT INTO "sadiq_sani" VALUES(16,31,'16:31','(እርሷም) የሚገቡዋትና በሥርዋ ወንዞች የሚፈሱባት ስትኾን የመኖሪያ አትክልቶች ናት፡፡ ለእነሱም በወስጧ የሚሹት ሁሉ አላቸው፡፡ እንደዚሁ አላህ ጥንቁቆችን ይመነዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,32,'16:32','እነዚያ በመልካም ኹኔታ ላይ ኾነው መላእክት «ሰላም በእናንተ ላይ» እያሉ የሚገድሏቸው ናቸው፡፡ «ትሠሩት በነበራችሁት ሥራ ገነትን ግቡ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,33,'16:33','(ከሓዲዎች) መላእክት ወደ እነሱ መምጣትን ወይም የጌታህ ትእዛዝ መምጣቱን እንጂ አይጠባበቁም፡፡ ከእነሱ በፊት የነበሩት እንደዚህ ሠርተዋል፡፡ አላህም አልበደላቸውም፤ ግን ነፍሶቻቸውን የሚበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,34,'16:34','የሠሩዋቸውም ኀጢአቶች (ቅጣት) አገኛቸው፡፡ በእርሱም ይሳለቁበት የነበሩት ሁሉ በነሱ ላይ ወረደባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,35,'16:35','እነዚያም (ጣዖታትን) ያጋሩት «አላህ በሻ ኖሮ ከእርሱ ሌላ እኛም አባቶቻችንም ምንንም ባልተገዛን ነበር፤ ያለእርሱም (ትእዛዝ) ምንንም እርም ባላደረግን ነበር አሉ፡፡» እነዚያም ከነሱ በፊት የነበሩት እነደዚሁ ሠሩ፡፡ በመልክተኞቹም ላይ ግልፅ ማድረስ ብቻ እንጂ ሌላ የለባቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,36,'16:36','በየሕዝቡም ሁሉ ውስጥ «አላህን ተገዙ፤ ጣዖትንም ራቁ» በማለት መልክተኛን በእርግጥ ልከናል፡፡ ከእነሱም ውስጥ አላህ ያቀናው ሰው አልለ፡፡ ከእነሱም ውስጥ በእርሱ ላይ ጥመት የተረጋገጠበት ሰው አልለ፡፡ በምድርም ላይ ኺዱ፤ የአስተባባዮችም መጨረሻ እንዴት እንደ ነበረ ተመልከቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,37,'16:37','በመቅናታቸው ላይ ብትጓጓ (ምንም ልታደርግ አትችልም)፡፡ አላህ የሚጠመውን ሰው አያቀናውምና፡፡ ለእነሱም ከረዳቶች ምንም የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,38,'16:38','መሓሎቻቸውምን አላህ የሚሞትን ሰው አያስነሳም ሲሉ በአላህ ስም ማሉ፡፡ ሐሰት ነው (ያስነሳቸዋል)፡፡ በእርሱ ላይ ቃል ኪዳን ገብቷል፡፡ አረጋግጧል፤ ግን አብዛኞቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,39,'16:39','(የሚቀሰቅሳቸውም) ለእነሱ ያንን በእርሱ የሚለያዩበትን ሊገልጽላቸው፤ እነዚያ የካዱትም እነሱ ውሸታሞች የነበሩ መኾናቸውን እንዲያውቁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,40,'16:40','ለማንኛውም ነገር (መኾኑን) በሻነው ጊዜ ቃላችን ለእርሱ «ኹን» ማለት ብቻ ነው፤ ወዲውም ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,41,'16:41','እነዚያም ከተበደሉ በኋላ በአላህ (መንገድ ላይ) የተሰደዱት በቅርቢቱ ዓለም መልካሚቱን አገር (መዲናን) በእርግጥ እናሰፍራቸዋለን፡፡ የመጨረሻይቱም ዓለም ምንዳ ታላቅ ነው፡፡ (ከሓዲዎች) ቢያውቁ ኖሮ (በተከተሏቸው ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,42,'16:42','(እነርሱ) እነዚያ የታገሱት በጌታቸውም ላይ የሚጠጉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,43,'16:43','ከአንተም በፊት ወደእነሱ ወሕይን (ራእይን) የምናወርድላቸውን ሰዎችን እንጂ ሌላን አልላክንም፡፡ የማታውቁም ብትኾኑ የዕውቀትን ባለቤቶች ጠይቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,44,'16:44','በግልጽ ማስረጃዎችና በመጻሕፍት (ላክናቸው)፡፡ ወደ አንተም ለሰዎች ወደእነሱ የተወረደውን (ፍች) ልትገልጽላቸውና ያስተነትኑም ዘንድ ቁርኣንን አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,45,'16:45','እነዚያ መጥፎዎችን የዶለቱ አላህ በእነሱ ምድርን የሚገለብጥባቸው ወይም ከማያውቁት ስፍራ ቅጣት የሚመጣባቸው መኾኑን አይፈሩምን');
INSERT INTO "sadiq_sani" VALUES(16,46,'16:46','ወይም (ካገር ወደ አገር) በሚዛወሩበት ጊዜ የሚይዛቸው መኾኑን (አይፈሩምን) እነሱም አሸናፊዎች አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,47,'16:47','ወይም ቀስ በቀስ በማጉደል ላይ የሚይዛቸው መኾኑን (አይፈሩምን) ጌታችሁም በእርግጥ ርኅሩኅ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,48,'16:48','ወደዚያ ከማንኛውም ነገር አላህ ወደ ፈጠረው ጥላዎቹ እነርሱ የተዋረዱ ሲኾኑ ለአላህ ሰጋጆች ኾነው ከቀኝና ከግራዎች ወደሚዛወሩት አላዩምን');
INSERT INTO "sadiq_sani" VALUES(16,49,'16:49','ለአላህም በሰማያት ያለው ከተንቀሳቃሽም በምድር ያለው ሁሉ፣ መላእክትም ይሰግዳሉ፡፡ እነርሱም አይኮሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,50,'16:50','ጌታቸውን ከበላያቸው ሲኾን ይፈሩታል፡፡ የታዘዙትንም ሁሉ ይሠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,51,'16:51','አላህም አለ «ሁለት አማልክትን አትያዙ፡፡ እርሱ አንድ አምላክ ብቻ ነው፡፡ እኔንም ብቻ ፍሩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(16,52,'16:52','በሰማያትና በምድር ያለው ሁሉ የርሱ ብቻ ነው፡፡ መገዛትም ዘወትር ሲኾን ለርሱ ብቻ ነው፡፡ ከአላህም ሌላ ያለን ትፈራላችሁን');
INSERT INTO "sadiq_sani" VALUES(16,53,'16:53','ማንኛውም በእናንተ ላይ ያለው ጸጋ ከአላህ ነው፡፡ ከዚያም ችግር በደረሰባችሁ ጊዜ ወደርሱ ብቻ ትጮሃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,54,'16:54','ከዚያም ከእናንተ ላይ ችግርን ባነሳላችሁ ጊዜ ከእናንተው የኾኑ ጭፍሮች ወዲያውኑ በጌታቸው (ጣዖትን) ያጋራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,55,'16:55','(የሚያጋሩትም) በሰጠናቸው ሊክዱ ነው፡፡ ተጠቀሙም ወደ ፊትም (የሚደርስባችሁን) በእርግጥ ታውቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,56,'16:56','ለማያውቁትም (ጣዖታት) ከሰጠናቸው ሲሳይ ፈንታን ያደርጋሉ፡፡ በአላህ እምላለሁ፤ ትቀጣጥፉት ከነበራችሁት ሁሉ በእርግጥ ትጠየቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,57,'16:57','ለአላህም (ከመላእክት) ሴቶች ልጆችን ያደርጋሉ፡፡ ጥራት ተገባው፡፡ ለእነርሱም የሚፈልጉትን (ወንዶች ልጆችን) ያደርጋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,58,'16:58','አንዳቸውም በሴት ልጅ በተበሰረ ጊዜ እርሱ የተቆጨ ኾኖ ፊቱ ጠቁሮ ይውላል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,59,'16:59','በእርሱ ከተበሰረበት ነገር መጥፎነት በውርደት ላይ ኾኖ ይያዘውን ወይስ በዐፈር ውስጥ ይደብቀውን (በማለት እያምታታ) ከሰዎች ይደበቃል፡፡ ንቁ! የሚፈርዱት (ፍርድ) ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(16,60,'16:60','ለእነዚያ በመጨረሻይቱ ዓለም ለማያምኑት መጥፎ ጠባይ አላቸው፡፡ ለአላህም ታላቅ ባሕርይ አለው፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,61,'16:61','አላህም ሰዎችን በበደላቸው በያዛቸው ኖሮ በርሷ (በምድር ላይ) ከተንቀሳቃሽ ምንንም ባልተወ ነበር፡፡ ግን እስከ ተወሰነ ጊዜ ድረስ ያቆያቸዋል፡፡ ጊዜያቸውም በመጣ ወቅት አንዲትን ሰዓት አይቆዩም፤ አይቀደሙምም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,62,'16:62','ለአላህም የሚጠሉትን ነገር ያደረጋሉ፡፡ ለእነሱም መልካሚቱ (አገር) አለቻቸው በማለት ምላሶቸቸው ውሸትን ይናገራሉ፡፡ ለእነሱ እሳት ያለቻቸው መኾናቸውና እነሱም (ወደርሷ) በቅድሚያ የሚነዱ መኾናቸው ጥርጥር የለበትም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,63,'16:63','በአላህ እንምላለን፤ ካንተ በፊት ወደ ነበሩት ሕዝቦች በእርግጥ (መልክተኞችን) ልከናል፡፡ ሰይጣንም ለእነርሱ ሥራዎቻቸውን ሸለመላቸው፡፡ እርሱም ዛሬ ረዳታቸው ነው፡፡ ለእነሱም (ኋላ) አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,64,'16:64','ባንተም ላይ መጽሐፉን አላወረድንም፤ ያንን በርሱ የተለያዩበትን ለነርሱ ልታብራራላቸውና ለሚያምኑትም ሕዝቦች መሪና እዝነት ሊኾን እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,65,'16:65','አላህም ከሰማይ ውሃን አወረደ፡፡ በእርሱም ምድርን ከሞተች በኋላ ሕያው አደረገ፡፡ በዚህ ውስጥ ለሚሰሙ ሕዝቦች በእርግጥ ተዓምር አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,66,'16:66','ለናንተም በግመል፣ በከብት፣ በፍየል በእርግጥ መገምገሚያ አልላችሁ፡፡ በሆዶቹ ውስጥ ካለው ከፈረስና ከደም መካከል ጥሩ ወተትን ለጠጪዎች ተዋጭ ሲሆን እናጠጣችኋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,67,'16:67','ከዘምባባዎችና ከወይኖችም ፍሬዎች (እንመግባችኋለን)፡፡ ከእርሱ ጠጅንና መልካም ምግብንም ትሠራላችሁ፡፡ በዚህም ውስጥ ለሚያስቡ ሕዝቦች በእርግጥ ተዓምር አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,68,'16:68','ጌታህም ወደ ንብ እንዲህ ሲል አስታወቀ «ከተራራዎች፣ ከዛፍም፣ (ሰዎች) ከሚሠሩትም (ቀፎ) ቤቶችን ያዢ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,69,'16:69','«ከዚያም ከፍሬዎች ሁሉ ብይ፡፡ የጌታሽንም መንገዶች (ላንቺ) የተገሩ ሲኾኑ ግቢ፡፡» ከሆዶችዋ መልኮቹ የተለያዩ መጠጥ ይወጣል፡፡ በእርሱ ውስጥ ለሰዎች መድኃኒት አለበት፡፡ በዚህ ውስጥ ለሚያስተነትኑ ሕዝቦች በእርግጥ ተዓምር አልለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(16,70,'16:70','አላህም ፈጠራችሁ፤ ከዚያም ይገድላችኋል፡፡ ከእናንተም ከዕወቅት በኋላ ምንንም ነገር እንዳያውቅ ወደ ወራዳ ዕድሜ የሚመለስ ሰው አልለ፡፡ አላህ ዐዋቂ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,71,'16:71','አላህም ከፊላችሁን በከፊሉ ላይ በሲሳይ አበለጠ፡፡ እነዚያም በላጭ የተደረጉት ሲሳያቸውን እጆቻቸው በያዙዋቸው (ባሮች) ላይ እነሱ በእርሱ ይተካከሉ ዘንድ መላሾች አይደሉም፡፡ ታዲያ በአላህ ጸጋ (ለእርሱ ተጋሪ በማድረግ) ይክዳሉን');
INSERT INTO "sadiq_sani" VALUES(16,72,'16:72','አላህም ከነፍሶቻችሁ (ከጎሶቻችሁ) ለእናንተ ሚስቶችን አደረገ፡፡ ለእናንተም ከሚስቶቻችሁ ወንዶች ልጆችን፣ የልጅ ልጆችንም አደረገላችሁ፡፡ ከመልካሞችም ጸጋዎች ሰጣችሁ፡፡ ታድያ በውሸት (በጣኦት) ያምናሉን በአላህም ጸጋ እነሱ ይክዳሉን');
INSERT INTO "sadiq_sani" VALUES(16,73,'16:73','ከአላህም ሌላ ከሰማያትም ከምድርም ምንንም ሲሳይ የማይሰጧቸውን (ምንንም) የማይችሉትንም ይግገዛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,74,'16:74','ለአላህም አምሳያዎችን አታድርጉ፡፡ አላህ (መሳይ እንደሌለው) ያውቃል፡፡ እናንተ ግን አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,75,'16:75','በምንም ላይ የማይችለውን በይዞታ ያለውን ባሪያና ከእኛ መልካም ሲሳይን የሰጠነውን እርሱም ከእርሱ (ከሰጠነው) በምስጢርና በግልጽ የሚለግሰውን (ነጻ) ሰው አላህ (ለጣዖትና ለርሱ) ምሳሌ አደረገ፡፡ (ሁለቱ) ይተካከላሉን ምስጋና ለአላህ ይኹን፡፡ በእውነቱ አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,76,'16:76','አላህም ሁለትን ወንዶች (ለከሓዲና ለምእመን) ምሳሌ አደረገ፡፡ አንደኛቸው በምንም ነገር ላይ የማይችል ዲዳ ነው፡፡ እርሱም በጌታው ላይ ሸክም ነው፡፡ ወደ የትም ቢያዞረው በደግ ነገር አይመጣም፡፡ እርሱና ያ እርሱ በቀጥተናው መንገድ ላይ ኾኖ በማስተካከል የሚያዘው ሰው ይስተካከላሉን');
INSERT INTO "sadiq_sani" VALUES(16,77,'16:77','በሰማያትና በምድርም ያለው ሩቅ ምስጢር የአላህ ነው፡፡ የሰዓቲቱም ነገር (መምጣቷ) እንደዓይን ቅጽበት እንጂ አይደለም፡፡ ወይ እርሱ ይበልጥ የቀረበ ነው፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,78,'16:78','አላህም ከእናቶቻችሁ ሆዶች ምንም የማታውቁ ሆናችሁ አወጣችሁ፡፡ ታመሰግኑም ዘንድ ለእናንተ መስሚያን ማያዎችንም ልቦችንም አደረገላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,79,'16:79','ወደ በራሪዎች በሰማይ አየር ውስጥ (ለመብረር) የተገሩ ሲኾኑ (ከመውደቅ) አላህ እንጂ ሌላ የማይዛቸው ኾነው አይመለከቱምን በዚህ ውስጥ ለሚያምኑ ሕዝቦች በእርግጥ ተዓምራቶች አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,80,'16:80','አላህም ከቤቶቻችሁ ለእናንተ መርጊያን አደረገላችሁ፡፡ ከእንስሳዎችም ቆዳዎች በጉዟችሁ ቀን በመቀመጫችሁም ቀን የምታቀልላቸውን ቤቶች ለናንተ አደረገላችሁ፡፡ ከበግዋ ሱፎች፣ ከግመልዋም ጠጉሮች፣ ከፍየልዋም ጠጉሮች የቤት ዕቃዎችን እስከ ጊዜም ድረስ መጠቃቀሚያን (አደረገላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(16,81,'16:81','አላህም ከፈጠረው ነገር ለእናንተ ጥላዎችን አደረገላችሁ፡፡ ከጋራዎችም ለእናንተ መከለያዎችን አደረገላችሁ፡፡ ሐሩርንም (ብርድንም) የሚጠብቋችሁን ልብሶች የጦራችሁንም አደጋ የሚጠብቋችሁን ጥሩሮች ለእናንተ አደረገላችሁ፡፡ እንደዚሁ ትሰልሙ ዘንድ ጸጋውን በእናንተ ላይ ይሞላል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,82,'16:82','(ከኢስላም) ቢሸሹም ባንተ ላይ ያለብህ ግልጽ ማድረስ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,83,'16:83','የአላህን ጸጋ ያውቃሉ፡፡ ከዚያም ይክዷታል፡፡ አብዛኞቻቸውም ከሓዲዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,84,'16:84','ከየሕዝቡም ሁሉ መስካሪን የምንቀሰቅስበትን ቀን (አስታውስ)፡፡ ከዚያም ለእነዚያ ለካዱት (ንግግር) አይፈቀድላቸውም፡፡ እነሱም በወቀሳ አይታለፉም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,85,'16:85','እነዚያም የበደሉት ቅጣቱን ባዩ ጊዜ ከእነሱ (ቅጣቱ) አይቀለልላቸውም፡፡ እነሱም ጊዜ አይስሰጡም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,86,'16:86','እነዚያም ያጋሩት የሚያጋሩዋቸውን ባዩ ጊዜ «ጌታችን ሆይ! እነዚህ ከአንተ ሌላ እንገዛቸው የነበርነው ተጋሪዎቻችን ናቸው» ይላሉ፡፡ (አማልክቶች) «እናንተ በእርግጥ ውሸታሞች ናችሁ» የማለትንም ቃል ወደእነሱ ይጥላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,87,'16:87','(አጋሪዎቹ) በዚያ ቀንም ታዛዥነታቸውን ወደ አላህ ያቀርባሉ፡፡ ይቀጣጥፉት የነበሩትም ሁሉ ከእነርሱ ይጠፋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,88,'16:88','እነዚያ የካዱ፣ ከአላህም መንገድ የከለከሉ ያጠፉ በነበሩት ምክንያት ቅጣትን በቅጣት ላይ ጨመርንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,89,'16:89','በየሕዝቡም ሁሉ ውስጥ ከጎሳቸው በእነሱ ላይ መስካሪን የምንቀሰቅስበትን አንተንም በእነዚህ (ሕዝቦች) ላይ መስካሪ አድርገን የምናመጣህን ቀን (አስታውስ)፡፡ መጽሐፉንም ለሁሉ ነገር አብራሪ፣ መሪም፣ እዝነትም፣ ለሙስሊሞችም አብሳሪ ኾኖ ባንተ ላይ አወረድነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,90,'16:90','አላህ በማስተካከል፣ በማሳመርም፣ ለዝምድና ባለቤት በመስጠትም ያዛል፡፡ ከአስከፊም (ከማመንዘር)፣ ከሚጠላም ነገር ሁሉ፣ ከመበደልም ይከለክላል፡፡ ትገነዘቡ ዘንድ ይገስጻችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,91,'16:91','ቃል ኪዳንም በገባችሁ ጊዜ በአላህ ቃል ኪዳን ሙሉ፡፡ መሓሎቻችሁንም ከአጠነከራችኋት በኋላ አላህን በእናንተ ላይ በእርግጥ መስካሪ ያደረጋችሁ ስትኾኑ አታፍርሱ፡፡ አላህ የምትሠሩትን ሁሉ ያውቃልና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,92,'16:92','(ቃል ኪዳናችሁን በማፍረስ አንዲቱ) ሕዝብ እርሷ ከ(ሌላይቱ) ሕዝብ የበዛች ለመኾንዋ በመካከላችሁ መሓሎቻችሁን (ለክዳት) መግቢያ አድርጋችሁ የምትይዙ ስትኾኑ እንደዚያች ፈትልዋን ከጠነከረ በኋላ ትርትሮች አድርጋ እንዳፈረሰችው (ሴት) አትኹኑ፡፡ አላህ በእርሱ ይሞክራችኋል፤ በትንሣኤ ቀንም በእርሱ ትለያዩበት የነበራችሁትን ነገር ለእናንተ በእርግጥ ያብራራላችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,93,'16:93','አላህም በሻ ኖሮ አንዲት ሕዝብ ባደረጋችሁ ነበር፡፡ ግን የሚሻውን ሰው ያጠማል፡፡ የሚሻውንም ሰው ያቀናል፡፡ ትሠሩት ከነበራችሁትም ሁሉ በእርግጥ ትጠየቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,94,'16:94','ጫማዎች ከተደላደሉ በኋላ እንዳይንዳለጡ ከአላህም መንገድ በመከልከላችሁ ምክንያት ቅጣትን እንዳትቀምሱ መሓሎቻችሁን በመካከላችሁ ለክዳት መግቢያ አድርጋችሁ አትያዙ፡፡ ለእናንተም (ያን ጊዜ) ታላቅ ቅጣት አላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,95,'16:95','በአላህም ቃል ኪዳን ጥቂትን ዋጋ አትግዙ፡፡ የምታውቁ ብትኾኑ አላህ ዘንድ ያለው ምንዳ እርሱ ለእናንተ በላጭ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,96,'16:96','እናንተ ዘንድ ያለው ሁሉ ያልቃል፡፡ አላህ ዘንድ ያለው ግን (ዘለዓለም) ቀሪ ነው፡፡ እነዚያንም የታገሱትን ይሠሩት በነበሩት በመልካሙ ምንዳቸውን እንመነዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,97,'16:97','ከወንድ ወይም ከሴት እርሱ አማኝ ኾኖ በጎን የሰራ መልካም ኑሮን በእርግጥ እናኖረዋለን፡፡ ይሠሩትም ከነበሩት ነገር በመልካሙ ምንዳቸውን እንመነዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,98,'16:98','ቁርኣንንም ባነበብህ ጊዜ እርጉም ከኾነው ሰይጣን በአላህ ተጠበቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,99,'16:99','እርሱ በእነዚያ ባመኑትና በጌታቸው ላይ በሚጠጉት ላይ ለእርሱ ስልጣን የለውምና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,100,'16:100','ስልጣኑ በእነዚያ በሚታዘዙት ላይና በእነዚያም እነርሱ በእርሱ (ምክንያት) አጋሪዎች በኾኑት ላይ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,101,'16:101','በአንቀጽም ስፍራ አንቀጽን በለወጥን ጊዜ አላህም የሚያወርደውን ነገር አዋቂ ነው፡፡ «አንተ ቀጣፊ እንጂ ሌላ አይደለህም» ይላሉ፡፡ በእውነቱ አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,102,'16:102','እነዚያን ያመኑትን ለማረጋጋት ሙስሊሞቹንም ለመምራትና ለማብሰር (ቁርኣንን) ቅዱሱ መንፈስ (ጂብሪል) እውነተኛ ሲኾን ከጌታህ አወረደው በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,103,'16:103','እነርሱም «እርሱን (ቁርኣንን) የሚያስተምረው ሰው ብቻ ነው» ማለታቸውን በእርግጥ እናውቃለን፡፡ የዚያ ወደርሱ የሚያስጠጉበት ሰው ቋንቋ ዐጀም ነው፡፡ ይህ (ቁርአን) ግን ግልጽ ዐረብኛ ቋንቋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,104,'16:104','እነዚያን በአላህ አንቀጾች የማያምኑትን አላህ አይመራቸውም፤ ለነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,105,'16:105','ውሸትን የሚቀጣጥፉት እነዚያ በአላህ አንቀጾች የማያምኑት ብቻ ናቸው፡፡ እነዚያም ውሸታሞቹ እነሱ ብቻ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,106,'16:106','ከእምነቱ በኋላ በአላህ የካደ ሰው (ብርቱ ቅጣት አልለው)፡፡ ልቡ በእምነት የረጋ ኾኖ (በክህደት ቃል በመናገር) የተገደደ ሰው ብቻ ሲቀር፡፡ ግን ልባቸውን በክሕደት የከፈቱ ሰዎች በነሱ ላይ ከአላህ ቁጣ አለባቸው፡፡ ለእነሱም ታላቅ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,107,'16:107','ይህ (ቅጣት) እነሱ ቅርቢቱን ሕይወት ከመጨረሻይቱ በመምረጣቸው ምክንያትና አላህም ከሓዲዎችን ሕዝቦች የማያቀና በመኾኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,108,'16:108','እነዚያ እነርሱ በልቦቻቸው፣ በዓይኖቻቸውም፣ በጆሮዎቻቸውም ላይ አላህ ያተመባቸው ናቸው፡፡ እነዚያም ዝንጉዎቹ እነሱ ናቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(16,109,'16:109','እነርሱ በመጨረሻይቱ ዓለም ከሳሪዎቹ እነሱ ለመኾናቸው ጥርጥር የለም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,110,'16:110','ከዚያም ጌታህ ለእነዚያ ከተፈተኑ በኋላ ለተሰደዱት ከዚያም ለታገሉትና ለታገሱት (መሓሪ አዛኝ ነው)፡፡ ጌታህ ከእርሷ (ከፈተናዋ) በኋላ በእርግጥ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,111,'16:111','ነፍስ ሁሉ ከራሷ ላይ የምትከራከር ኾና የምትመጣበትን ነፍስም ሁሉ የሠራችውን (ዋጋ) የምትቀበልበትን ቀን (አስታውስ)፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,112,'16:112','አላህ ጸጥተኛ፣ የረካች፣ ሲሳይዋ ሰፊ ኾኖ ከየስፍራው ይመጣላት የነበረችውንና በአላህ ጸጋዎች የካደችውን አላህም ይሠሩት በነበሩት ኃጢኣት የረኃብንና የፍርሃትን ቅጣቶች ያቀመሳትን ከተማ (መካን) ምሳሌ አደረገ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,113,'16:113','ከእነርሱ ውስጥ የኾነ መልክተኛም በእርግጥ መጣላቸው፡፡ አስተባበሉትም፡፡ እነሱም በዳዮች ኾነው ቅጣቱ ያዛቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,114,'16:114','አላህም ከሰጣችሁ ሲሳይ የተፈቀደ ንጹሕ ሲኾን ብሉ፡፡ የአላህንም ጸጋ እርሱን የምትገዙት ብትሆኑ አመስግኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,115,'16:115','በእናንተ ላይ እርም ያደረገው በክትንና ደምን፣ የአሳማንም ስጋ፣ ያንንም (በመታረድ ጊዜ) በእርሱ ከአላህ ስም ሌላ የተነሳበትን ብቻ ነው፡፡ አመጸኛም ወሰን አላፊም ሳይኾን (ለመብላት) የተገደደ ሰው (ይፈቀድለታል)፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(16,116,'16:116','በአላህም ላይ ውሸትን ልትቀጣጥፉ ምላሶቻችሁ በሚመጥኑት ውሸት «ይህ የተፈቀደ ነው፤ ይህም እርም ነው» አትበሉ፡፡ እነዚያ በአላህ ላይ ውሸትን የሚቀጣጥፉ አይድኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,117,'16:117','ጥቂት መጣቀም አላቸው፡፡ ለእነሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,118,'16:118','በእነዚያም ይሁዳውያን በኾኑት ላይ ከአሁን በፊት በአንተ ላይ የተረክነውን ነገር እርም አድርገንባቸዋል፡፡ እኛም አልበደልናቸውም፡፡ ግን ነፍሶቻቸውን ይበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(16,119,'16:119','ከዚያም ጌታህ ለእነዚያ በስሕተት መጥፎን ለሠሩና ከዚያም ከዚህ በኋላ ለተጸጸቱ ሥራቸውንም ላበጁ ጌታህ ከእርሷ በኋላ በእርግጥ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,120,'16:120','ኢብራሂም ለአላህ ታዛዥ ወደ ቀጥተኛ ሃይማኖት ተዘንባይ ሕዝብ ነበር፡፡ ከአጋሪዎቹም አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(16,121,'16:121','ለጸጋዎቹ አመስጋኝ ነበር፤ መረጠው፡፡ ወደ ቀጥተኛውም መንገድ መራው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,122,'16:122','በቅርቢቱም ዓለም በጎን ነገር ሰጠነው፡፡ እርሱም በመጨረሻይቱ ዓለም በእርግጥ ከመልካሞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,123,'16:123','ከዚያም ወደ አንተ የኢብራሂምን ሃይማኖት ቀጥተኛ ሲኾን ተከተል፤ ከአጋሪዎቹም አልነበረም ማለት አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,124,'16:124','ሰንበት (ክልክል) የተደረገው በእነዚያ በእርሱ በተለያዩበት ሰዎች ላይ ብቻ ነው፡፡ ጌታህም በትንሣኤ ቀን ይለያዩበት በነበሩት ነገር በመካከላቸው በእርግጥ ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(16,125,'16:125','ወደ ጌታህ መንገድ በብልሃትና በመልካም ግሳጼ (በለዘብታ ቃል) ጥራ፡፡ በዚያችም እርሷ መልካም በኾነችው (ዘዴ) ተከራከራቸው፡፡ ጌታህ እርሱ ከመንገዱ የተሳሳተውን ሰው ዐዋቂ ነው፡፡ እርሱም ቅን የኾኑትን ሰዎች ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,126,'16:126','ብትበቀሉም በርሱ በተቀጣችሁበት ብጤ ተበቀሉ፡፡ ብትታገሱም እርሱ ለታጋሾች በእርግጥ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(16,127,'16:127','ታገስም፤ መታገስህም በአላህ (ማስቻል) እንጂ አይደለም፡፡ በእነርሱም ላይ (ባያምኑ) አትተክዝ፡፡ (ባንተ ላይ) ከሚመክሩትም ተንኮል በጭንቀት ውስጥ አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(16,128,'16:128','አላህ ከእነዚያ ከሚፈሩትና ከእነዚያም እነሱ መልካም ሠሪዎች ከኾኑት ጋር ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,1,'17:1','ያ ባሪያውን ከተከበረው መስጊድ ወደዚያ ዙሪያውን ወደ ባረክነው ወደ ሩቁ መስጊድ በሌሊት ውስጥ ያስኼደው (ጌታ) ጥራት ይገባው፡፡ ከተዓምራቶቻችን ልናሳየው (አስኼድነው)፡፡ እነሆ እርሱ (አላህ) ሰሚው ተመልካቺው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,2,'17:2','ሙሳንም መጽሐፉን ሰጠነው፡፡ ለእስራኤልም ልጆች መሪ አደረግነው፡፡ ከእኔ ሌላ መጠጊያን አትያዙ፤ (አልናቸውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(17,3,'17:3','እናንተ ከኑሕ ጋር (በመርከቢቱ) የጫናቸው ዘሮች ሆይ! (አላህን ተገዙ) እርሱ በብዙ አመስጋኝ ባሪያ ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,4,'17:4','ወደ እስራኤልም ልጆች በመጽሐፉ ውስጥ (እንዲህ በማለት) አወረድን፡፡ በምድር ላይ በእርግጥ ሁለት ጊዜ ታጠፋላችሁ፡፡ ትልቅንም ኩራት ትኮራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,5,'17:5','ከሁለቱ (ጊዜያቶች) የመጀመሪያይቱ ቀጠሮ በመጣም ጊዜ ለእኛ የኾኑን ባሮች የብርቱ ኃይል ባለቤቶች የኾኑትን በእናንተ ላይ እንልካለን፡፡ በቤቶችም መካከል ይመላለሳሉ፤ (ይበረብሩታል፡፡) ይህ ተፈጻሚ ቀጠሮም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(17,6,'17:6','ከዚያም (በኋላ) ለእናንተ በእነሱ ላይ ድልን መለስንላችሁ፡፡ በገንዘቦችና በወንዶች ልጆችም ጨመርንላችሁ፡፡ በወገንም የበዛችሁ አደረግናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,7,'17:7','መልካም ብትሠሩ ለነፍሶቻችሁ መልካምን ሠራችሁ፡፡ መጥፎንም ብትሠሩ በነርሱ (በነፍሶቻችሁ) ላይ ነው፤ (አልን)፡፡ የኋለኛይቱም (ጊዜ) ቀጠሮ በመጣ ጊዜ ፊቶቻችሁን ሊያስከፉ፣ መስጊዱንም በመጀመሪያ ጊዜ እንደገቡት ሊገቡ፣ ያሸነፉትንም ሁሉ (ፈጽመው) ማጥፋትን እንዲያጠፉ (እንልካቸዋልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(17,8,'17:8','(በመጽሐፉም አልን) «ብትጸጸቱ፡- ጌታችሁ ሊያዝንላችሁ ይከጀላል፡፡ (ወደ ማጥፋት) ብትመለሱም እንመለሳለን፡፡ ገሀነምንም ለከሓዲዎች ማሰሪያ አደርገናል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,9,'17:9','ይህ ቁርኣን ወደዚያች እርሷ ቀጥተኛ ወደ ኾነችው መንገድ ይመራል፡፡ እነዚያንም በጎ የሚሠሩትን ምእመናን ለእነሱ ታላቅ ምንዳ ያላቸው መኾኑን ያበስራል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,10,'17:10','እነዚያም በመጨረሻይቱ ዓለም የማያምኑት ለእነሱ አሳማሚ ቅጣትን አዘጋጅተንላቸዋል፤');
INSERT INTO "sadiq_sani" VALUES(17,11,'17:11','ሰው መልካምን ነገር እንደሚለምን ሁሉ፤ መጥፎንም ነገር ይለምናል፡፡ ሰውም ቸኳላ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,12,'17:12','ሌሊትንና ቀንንም (ለችሎታችን) ምልክቶች አደረግን፡፡ የሌሊትን ምልክትም አበስን፡፡ የቀንን ምልክትም የምታሳይ አደረግን፡፡ (ይህም የኾነበት) ከጌታችሁ ትርፍን ልትፈልጉ የዓመታትንም ቁጥርና ሒሳብንም ታውቁ ዘንድ ነው፡፡ ነገሩንም ሁሉ ለያይተን ዘረዘርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,13,'17:13','ሰውንም ሁሉ በራሪውን (ሥራውን) በአንገቱ አስያዝነው፡፡ ለእርሱም በትንሣኤ ቀን የተዘረጋ ኾኖ የሚያገኘው የኾነን መጽሐፍ እናወጣለታለን፡፡');
INSERT INTO "sadiq_sani" VALUES(17,14,'17:14','«መጽሐፍህን አንብብ፡፡ ዛሬ ባንተ ላይ ተቆጣጣሪነት በነፍስህ በቃ» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(17,15,'17:15','የተመራ ሰው የሚመራው ለራሱ ብቻ ነው፡፡ የተሳሳተም ሰው የሚሳሳተው (ጉዳቱ) በእርሷ ላይ ነው፡፡ ተሸካሚም (ነፍስ) የሌላይቱን ኃጢኣት አትሸከምም፡፡ መልዕክተኛንም እስከምንልክ ድረስ የምንቀጣ አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,16,'17:16','ከተማንም ለማጥፋት በፈለግን ጊዜ ባለ ጸጋዎችዋን እናዛለን፡፡ በውስጧም ያምጻሉ፡፡ በእርሷም ላይ ቃሉ (ቅጣቱ) ይፈጸምባታል፡፡ ማጥፋትንም እናጠፋታለን፡፡');
INSERT INTO "sadiq_sani" VALUES(17,17,'17:17','ከኑሕም በኋላ ከክፍለ ዘመናት ሰዎች ያጠፋናቸው ብዙዎች ናቸው፡፡ የባሮቹንም ኃጢኣቶች ውስጠ ዐዋቂ ተመልካች መኾን በጌታህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,18,'17:18','ቸኳይቱን ዓለም (በሥራው) የሚፈልግ ሰው ለእርሱ በርሷ ውስጥ የሻነውን (ጸጋ) ለምንሻው ሰው እናስቸኩልለታለን፡፡ ከዚያም ለእርሱ ገሀነምን (መኖሪያ) አድርገንለታል፡፡ ተወቃሽ ብራሪ ኾኖ ይገባታል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,19,'17:19','መጨረሻይቱንም ዓለም የፈለገ ሰው እርሱ አማኝ ኾኖ ለርሷ (ተገቢ) ሥራዋን የሠራም ሰው እነዚህ ሥራቸው የተመሰገነ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,20,'17:20','ሁሉንም እነዚህንና እነዚያን ከጌታህ ስጦታ (በዚህ ዓለም) እንጨምርላቸዋለን፡፡ የጌታህም ስጦታ (በዚች ዓለም) ክልክል አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,21,'17:21','ከፊላቸውን በከፊሉ ላይ እንዴት እንዳበለጥን ተመልከት፡፡ የመጨረሻያቱም አገር በማዕረጎች በጣም የከበረችና በመብለጥም የተለቀች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(17,22,'17:22','ከአላህ ጋር ሌላን አምላክ አታድርግ የተወቀስክ ረዳት የሌለህ ትኾናለህና፤');
INSERT INTO "sadiq_sani" VALUES(17,23,'17:23','ጌታህም (እንዲህ ሲል) አዘዘ፡- እርሱን እንጂ ሌላን አትገዙ፡፡ በወላጆቻችሁም መልካምን ሥሩ፡፡ በአንተ ዘንድ ኾነው አንዳቸው ወይም ሁለታቸው እርጅናን ቢደርሱ ፎህ አትበላቸው፡፡ አትገላምጣቸውም፡፡ ለእነርሱም መልካምን ቃል ተናገራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,24,'17:24','ለሁለቱም ከእዝነትህ የመዋረድን ክንፍ ዝቅ አድርግላቸው፡፡ «ጌታዬ ሆይ! በሕፃንነቴ (በርኅራኄ) እንዳሳደጉኝ እዘንልላቸውም» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,25,'17:25','ጌታችሁ በነፍሶቻችሁ ውስጥ ያለውን ሁሉ አዋቂ ነው፡፡ ታዛዦችም ብትኾኑ (በመሳሳትም ብታጠፉ) እርሱ ለተመላሾች መሓሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,26,'17:26','ለዝምድና ባለቤትም መብቱን ስጥ፡፡ ለምስኪንና ለመንገደኛም (ስጥ)፡፡ ማባከንንም አታባክን፡፡');
INSERT INTO "sadiq_sani" VALUES(17,27,'17:27','አባካኞች የሰይጣናት ወንድሞች ናቸውና፡፡ ሰይጣንም ለጌታው ብርቱ ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,28,'17:28','ከጌታህም የምትከጅላትን ጸጋ ለማጣት ከእነርሱ ብትዞር ለእነሱ ልዝብን ቃል ተናገራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,29,'17:29','እጅህንም ወደ አንገትህ የታሰረች አታድርግ፡፡ መዘርጋትንም ሁሉ አትዘርጋት፤ የተወቀስክ የተቆጨኽ ትኾናለህና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,30,'17:30','ጌታህ ሲሳይን ለሚሻው ሰው ያሰፋል፤ ያጠባልም፡፡ እርሱ በባሮቹ ኹኔታ ውስጠ ዐዋቂ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,31,'17:31','ልጆቻችሁንም ድኽነትን ለመፍራት አትግደሉ፡፡ እኛ እንመግባቸዋለን፡፡ እናንተንም (እንመግባለን)፡፡ እነርሱን መግደል ታላቅ ኃጢኣት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,32,'17:32','ዝሙትንም አትቅረቡ፡፡ እርሱ በእርግጥ መጥፎ ሥራ ነውና፡፡ መንገድነቱም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(17,33,'17:33','ያቺንም አላህ ያወገዛትን ነፍስ ያለ ሕግ አትግደሉ፡፡ የተበደለም ኾኖ የተገደለ ሰው ለዘመዱ (በገዳዩ ላይ) በእርግጥ ስልጣንን አድርገናል፡፡ በመግደልም ወሰንን አይለፍ፤ እርሱ የተረዳ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,34,'17:34','የየቲምንም ገንዘብ የብርታቱን ጊዜ እስኪደርስ ድረስ በዚያች እርሷ መልካም በኾነች ኹኔታ እንጂ አትቅረቡ፡፡ በኪዳናችሁም ሙሉ፡፡ ኪዳን የሚጠየቁበት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,35,'17:35','በሰፈራችሁም ጊዜ ስፍርን ሙሉ፡፡ በትክክለኛው ሚዛንም መዝኑ፡፡ ይህ መልካም ነገር ነው፡፡ መጨረሻውም ያማረ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,36,'17:36','ለአንተም በርሱ ዕውቀት የሌለህን ነገር አትከተል፡፡ መስሚያ፣ ማያም፣ ልብም እነዚህ ሁሉ (ባለቤታቸው) ከእነሱ ተጠያቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,37,'17:37','በምድርም ላይ የተንበጣረረክ ሆነህ፤ አትሂድ፡፡ አንተ ፈጽሞ ምድርን አትሰረጉድምና በርዝመትም ፈጽሞ ጋራዎችን አትደርስምና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,38,'17:38','ይህ ሁሉ መጥፎው (አሥራ ሁለቱ ክልክሎች) እጌታህ ዘንድ የተጠላ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,39,'17:39','ይህ ጌታህ ከጥበቡ ወደ አንተ ካወረደው ነገር ነው፡፡ ከአላህም ጋር ሌላን አምላክ አታድርግ የተወቀስክ የተባረርክ ኾነህ በገሀነም ውስጥ ትጣላለህና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,40,'17:40','ጌታችሁ በወንዶች ልጆች መረጣችሁና ከመላእክት ሴቶችን (ልጆች) ያዝን እናንተ ከባድን ቃል በእርግጥ ትናገራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,41,'17:41','ይገሰጹም ዘንድ በዚህ ቁርኣን ውስጥ ደጋግመን በእርግጥ ገለጽን፡፡ መበርገግንም እንጂ ሌላን አይጨመርላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,42,'17:42','(ሙሐመድ ሆይ) በላቸው «እንደምትሉት ከእርሱ ጋር አማልክት በነበሩ ኖሮ ያን ጊዜ ወደ ዐርሹ ባለቤት መንገድን በፈለጉ ነበር፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,43,'17:43','ጥራት ይገባው፡፡ ከሚሉትም ነገር ሁሉ ከፍ ያለን ልቅና ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,44,'17:44','ሰባቱ ሰማያትና ምድርም በውስጣቸውም ያለው ሁሉ ለእርሱ ያጠራሉ፡፡ ከነገርም ሁሉ ከማመስገን ጋር የሚያጠራው እንጂ አንድም የለም፡፡ ግን ማጥራታቸውን አታውቁትም፡፡ እርሱ ታጋሽ መሓሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,45,'17:45','ቁርኣንንም ባነበብክ ጊዜ ባንተና በእነዚያ በመጨረሻይቱ ዓለም በማያምኑት ሰዎች መካከል የሚሸሸግን ግርዶሽ አድርገናል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,46,'17:46','እንዳያውቁትም በልቦቻቸው ላይ ሽፋንን በጆሮቻቸውም ውስጥ ድንቁርናን አደረግን፡፡ ጌታህንም ብቻውን ኾኖ በቁርኣን ባወሳኸው ጊዜ የሚሸሹ ኾነው በጀርባዎቻቸው ላይ ይዞራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,47,'17:47','እኛ ወደ አንተ በሚያዳምጡ ጊዜ እነሱም በሚንሾካሾኩ ጊዜ በዳዮች (እርስ በርሳቸው) የተደገመበትን ስው እንጂ አትከተሉም በሚሉ ጌዜ በእርሱ የሚያዳምጡበትን ምክንያት ዐዋቂ ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(17,48,'17:48','ምሳሌዎችን ለአንተ እንዴት እንዳደረጉልህና እንደ ተሳሳቱ ተመልከት፡፡ (ወደ እውነቱ ለመድረስ) መንገድንም አይችሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,49,'17:49','አሉም «እንዴት አጥንቶችና ብስብሶች በኾን ጊዜ እኛ አዲስ ፍጥረት ኾነን በእርግጥ ተቀስቃሾች ነን»');
INSERT INTO "sadiq_sani" VALUES(17,50,'17:50','በላቸው «ድንጋዮችን ወይም ብረትን ሁኑ»');
INSERT INTO "sadiq_sani" VALUES(17,51,'17:51','«ወይም በልቦቻችሁ ውስጥ የሚተልቅን ፍጥረት (ኹኑ መቀስቀሳችሁ አይቀርም)፡፡» «የሚመልሰንም ማነው» ይላሉ፡፡ ያ በመጀመሪያ ጊዜ የፈጠራችሁ ነው» በላቸው፡፡ ወደ አንተም ራሶቻቸውን ይነቀንቃሉ፡፡ «እርሱም መቼ ነው» ይላሉ፡፡ «(እርሱ) ቅርብ ሊኾነን ይቻላል በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,52,'17:52','(እርሱም) የሚጠራችሁና (አላህን) አመስጋኞቹ ኾናችሁ ጥሪውን የምትቀበሉበት ጥቂትንም (ቀኖች) እንጂ ያልቆያችሁ መኾናችሁን የምትጠራጠሩበት ቀን ነው፤ (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(17,53,'17:53','ለባሮቼም በላቸው፡- ያችን እርሷ መልካም የኾነችውን (ቃል) ይናገሩ፡፡ ሰይጣን በመካከላቸው ያበላሻልና፡፡ ሰይጣን ለሰው ግልጽ ጠላት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,54,'17:54','ጌታችሁ በእናንተ (ኹኔታ) ዐዋቂ ነው፡፡ ቢሻ ያዝንላችኋል፤ ወይም ቢሻ ይቀጣችኋል፡፡ በእነሱም ላይ ኃላፊ ኾነህ አላክንህም፡፡ (አታስገድዳቸውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(17,55,'17:55','ጌታህም በሰማያትና በምድር ያለውን ሁሉ ዐዋቂ ነው፡፡ ከፊሉንም ነቢያት በከፊሉ ላይ በእርግጥ አብልጠናል፡፡ ዳውድንም ዘቡርን ሰጥተነዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,56,'17:56','«እነዚያን ከእርሱ ሌላ (አማልክት) የምትሏቸውን ጥሩ፡፡ ከእናንተም ላይ ጉዳትን ማስወገድን (ወደ ሌላ) ማዞርንም አይችሉም» በላቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(17,57,'17:57','እነዚያ እነርሱ የሚግገዟቸው ማንኛቸውም (ወደ አላህ) በጣም ቀራቢያቸው ወደ ጌታቸው መቃረቢያን (ሥራ) ይፈልጋሉ፡፡ እዝነቱንም ይከጅላሉ፡፡ ቅጣቱንም ይፈራሉ፡፡ የጌታህ ቅጣት የሚፈራ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,58,'17:58','አንዲትም ከተማ የለችም እኛ ከትንሣኤ ቀን በፊት አጥፊዎችዋ ወይም ብርቱን ቅጣት ቀጭዎችዋ ብንኾን እንጂ፡፡ ይህ በመጽሐፉ የተጻፈ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,59,'17:59','ታምራቶችንም ከመላክ የቀድሞዎቹ ሰዎች በእርሷ ማስተባበልና (መጥፋት) እንጂ ሌላ አልከለከለንም፡፡ ለሰሙድም ግመልን ግልጽ ተዓምር ኾና ሰጠናቸው፡፡ በእርሷም በደሉ፡፡ ተዓምራቶችንም ለማስፈራራት እንጂ አንንልክም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,60,'17:60','ላንተም ጌታህ (ዕውቀቱ) በሰዎቹ ከበበ ባልንህ ጊዜ (አስታውስ)፡፡ ያችንም (በሌሊቱ ጉዞ በዓይንህ) ያሳየንህን ትርዕይት ለሰዎች ፈተና እንጂ አላደረግናትም፡፡ በቁርኣንም የተረገመችውን ዛፍ (እንደዚሁ ፈተና እንጂ አላደረግንም)፡፡ እናስፈራራቸዋለንም፤ ታላቅንም ጥመት እንጂ አይጨምርላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,61,'17:61','ለመላእክትም ለአደም ስገዱ ባልናቸው ጊዜ (አስታውስ)፡፡ ወዲያውም ሰገዱ፤ ኢብሊስ ብቻ ሲቀር፡፡ «ከጭቃ ለፈጠርከው ሰው እሰግዳለሁን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,62,'17:62','«ንገረኝ ይህ ያ በእኔ ላይ ያበለጥከው ነውን እስከ ትንሣኤ ቀን ድረስ ብታቆየኝ ዘሮቹን ጥቂቶች ሲቀሩ ወደ ስህተት በእርግጥ እስባቸዋለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,63,'17:63','(አላህም) አለው «ኺድ፤ ከእነሱም የተከተለህ ገሀነም የተሟላች ቅጣት ስትኾን ፍዳችሁ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(17,64,'17:64','«ከእነሱ የቻልከውንም ሰው በድምጽህ አታል፡፡ በእነሱም ላይ በፈረሰኞችህ፣ በእግረኞችህም ኾነህ ለልብ፡፡ በገንዘቦቻቸውም፣ በልጆቻቸውም ተጋራቸው፣ ተስፋ ቃልም ግባላቸው፡፡ ሰይጣንም ማታለልን እንጂ ተስፋ ቃልን አይገባላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,65,'17:65','«ባሮቼ በእነሱ ላይ ፈጽሞ ላንተ ስልጣን የለህም፡፡ መጠጊያም በጌታህ በቃ፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,66,'17:66','ጌታችሁ ያ ከችሮታው ትፈልጉ ዘንድ መርከቦችን በባሕር ለይ ለእናንተ የሚነዳላችሁ ነው፡፡ እነሆ እርሱ ለናንተ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,67,'17:67','በባሕሩም ውስጥ ጉዳት ባገኛችሁ ጊዜ የምትጠሩዋቸው (አማልክት) ሁሉ ከእርሱ (ከአላህ) በቀር ይጠፋሉ፡፡ ወደ የብስም በማድረስ ባዳናችሁ ጊዜ (እምነትን) ትተዋላችሁ፡፡ ሰውም በጣም ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,68,'17:68','የየብሱን በኩል (ምድርን) በእናንተ መገልበጡን ወይም ጠጠርን የያዘ ንፋስን በእናንተ ላይ መላኩን ከዚያም ለእናንተ ጠባቂ አለማግኘታችሁን አትፈሩምን');
INSERT INTO "sadiq_sani" VALUES(17,69,'17:69','ወይስ ሌላ ጊዜ ወደ እርሱ (ወደ ባሕር) የሚመልሳችሁ ወዲያውም በእናንተ ላይ ብርቱን ነፋስ የሚልክ በክሕደታችሁም ምክንያት የሚያሰጥማችሁ መኾኑን ከዚያም በኛ ላይ በእርሱ ተከታይን (ረዳትን) ለእናንተ የማታገኙ መኾናችሁን አትፈሩምን');
INSERT INTO "sadiq_sani" VALUES(17,70,'17:70','የአደምንም ልጆች በእርግጥ (ከሌላው ፍጡር) አከበርናቸው፡፡ በየብስና በባህርም አሳፈርናቸው፡፡ ከመልካሞችም (ሲሳዮች) ሰጠናቸው፡፡ ከፈጠርናቸውም ፍጡሮች በብዙዎቹ ላይ ማብለጥን አበለጥናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,71,'17:71','ሰዎችን ሁሉ በመሪያቸው የምንጠራበትን ቀን (አስታውስ)፡፡ መጽሐፉንም በቀኙ የተሰጠ ሰው እነዚያ መጽሐፋቸውን ያነባሉ፡፡ በተምር ፍሬ ስንጥቅ ላይ ያለውን ክር ያክል እንኳ አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,72,'17:72','በዚህችም ዓለም (ልበ) ዕውር የኾነ ሰው እርሱ በመጨረሻይቱም (ዓለም) ይበልጥ ዕውር ነው፡፡ መንገድንም በጣም የተሳሳተ ነው፡፤');
INSERT INTO "sadiq_sani" VALUES(17,73,'17:73','እነሆ ከዚያም ወደ አንተ ካወረድነው ሌላን በእኛ ላይ ትዋሽ ዘንድ ሊፈትኑህ በእርግጥ ተቃረቡ፡፡ ያን ጊዜም ወዳጅ አድርገው በያዙህ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(17,74,'17:74','ባላረጋንህም ኖሮ ወደነሱ ጥቂትን (ዝንባሌ) ልትዘነበል በእርግጥ በተቃረብክ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(17,75,'17:75','ያን ጊዜ የሕይወትን ድርብ (ቅጣት) የሞትንም ድርብ (ቅጣት) ባቀመስንህ ነበር፡፡ ከዚያም ላንተ በኛ ላይ ረዳትን አታገኝም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(17,76,'17:76','ከምድሪቱም (ከዓረብ ምድር) ከርሷ ያወጡህ ዘንድ ሊያሸብሩህ በእርግጥ ተቃረቡ፡፡ ያን ጊዜም ከአንተ በኋላ ጥቂትን ጊዜ እንጂ አይቆዩም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(17,77,'17:77','ከመልክተኞቻችን ካንተ በፊት በእርግጥ እንደላክናቸው ሰዎች ልማድ ብጤ (ይጠፉ ነበር)፡፡ ለልማዳችንም መለወጥን አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,78,'17:78','ሶላትን ከፀሐይ መዘንበል እስከ ሌሊት ጨለማ ድረስ ስገድ፡፡ የጎህንም ሶላት ስገድ፡፡ የጎህ ሶላት (መላእክት) የሚጣዱት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(17,79,'17:79','ከሌሊትም ላንተ ተጨማሪ የኾነችን ሶላት በእርሱ (በቁርኣን) ስገድ፡፡ ጌታህ ምስጉን በኾነ ስፍራ በእርግጥ ያቆምሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,80,'17:80','በልም ጌታዬ ሆይ! የተወደደን ማግባት አግባኝ፡፡ የተወደደንም ማውጣት አውጣኝ፡፡ ለእኔም ከአንተ ዘንድ የተረዳን ስልጣን አድርግልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,81,'17:81','በልም «እውነት መጣ ውሸትም ተወገደ፤ እነሆ ውሸት ተወጋጅ ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,82,'17:82','ከቁርኣንም ለምእመናን መድኀኒትና እዝነት የኾነን እናወርዳለን፡፡ በዳዮችንም ከሳራን እንጂ ሌላ አይጨምርላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,83,'17:83','በሰውም ላይ ጸጋችንን በለገስን ጊዜ (ከምስጋና) ይዞራል፡፡ ጎኑንም (ከእውነት) ያርቃል፡፡ ችግርም በነካው ጊዜ ተስፋ ቆራጭ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,84,'17:84','«ሁሉም በሚመስለው መንገዱ ላይ ይሠራል፡፡ ጌታችሁም እርሱ መንገዱ ቀጥተኛ የኾነውን ሰው ዐዋቂ ነው በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,85,'17:85','ከሩሕም ይጠይቁሃል፡፡ «ሩሕ ከጌታዬ ነገር ነው ከዕውቀትም ጥቂትን እንጂ አልተሰጣችሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,86,'17:86','ብንሻም ያንን ወዳንተ ያወረድነውን በእርግጥ እናስወግዳለን፡፡ ከዚያም ላንተ በእኛ ላይ በርሱ (ለማስመለስ) ተያዢን አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,87,'17:87','ግን ከጌታህ በኾነው እዝነት (ጠበቅነው)፡፡ ችሮታው ባንተ ላይ ታላቅ ነውና፡፤');
INSERT INTO "sadiq_sani" VALUES(17,88,'17:88','«ሰዎችም ጋኔኖችም የዚህን ቁርኣን ብጤ በማምጣት ላይ ቢሰበሰቡ ከፊላቸው ለከፊሉ ረዳት ቢሆንም እንኳ ብጤውን አያመጡም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,89,'17:89','በዚህም ቁርኣን ውስጥ ከምሳሌው ሁሉ ለሰዎች በእርግጥ መላለስን፡፡ አብዛኞቹም ሰዎች ክህደትን እንጂ ሌላን እምቢ አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,90,'17:90','አሉም «ለኛ ከምድር ምንጭን እስከምታፈነዳን ለአንተ አናምንም፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,91,'17:91','«ወይም ከዘምባባዎችና ከወይን የኾነች አትክልት ለአንተ እስከምትኖርህና በመካከልዋም ጂረቶችን በብዛት እስከምታንቧቧ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,92,'17:92','«ወይም እንደምትለው ከሰማይ ቁራጮችን በኛ ላይ እስከምታወድቅ፤ ወይም አላህንና መላእክትን በግልጽ እስከምታመጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,93,'17:93','«ወይም ከወርቅ የኾነ ቤት ላንተ እስከሚኖርህ፤ ወይም በሰማይ እስከምትወጣ፤ ለመውጣትህም በእኛ ላይ የምናነበው የኾነን መጽሐፍ እስከምታወርድልን ድረስ ፈጽሞ አናምንልህም» (አሉ)፡፡ «ጌታዬ ጥራት ይገባው፤ እኔ ሰው መልክተኛ እንጂ ሌላ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,94,'17:94','ሰዎችንም መሪ (ቁርኣን) በመጣላቸው ጊዜ ከማመን «አላህ ሰውን መልክተኛ አድርጎ ላከን» ማለታቸው እንጂ ሌላ አልከለከላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,95,'17:95','«በምድር ላይ ረግተው የሚኼዱ መላእክት በነበሩ ኖሮ በእነሱ ላይ (ከጎሳቸው) የመልአክን መልክተኛ ባወረድን ነበር» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,96,'17:96','«በእኔና በእናንተ መካከል መስካሪ በአላህ በቃ፡፡ እርሱ በባሮቹ (ነገር) ውስጥ ዐዋቂ ተመልካች ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,97,'17:97','አላህም ያቀናው ሰው ቅኑ እርሱ ብቻ ነው፡፡ ያጠመማቸውም ሰዎች ከእርሱ ሌላ ለእነርሱ ፈጽሞ ረዳት አታገኝላቸውም፡፡ በትንሣኤ ቀንም ዕውሮች፣ ዲዳዎችም ደንቆሮዎችም ኾነው በፊቶቻቸው ላይ (እየተጎተቱ) እንሰበስባቸዋለን፡፡ መኖሪያቸው ገሀነም ናት፡፡ (ነዲድዋ) በደከመች ቁጥር መንቀልቀልን እንጨምርባቸዋለን፡፤');
INSERT INTO "sadiq_sani" VALUES(17,98,'17:98','ይህ (ቅጣት)፤ እነርሱ በአንቀጾቻችን ስለ ካዱና «አጥንቶችና ብስብሶች በኾን ጊዜ እኛ አዲስ ፍጥረት ኾነን ተቀስቃሾች ነን» ስላሉም ፍዳቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,99,'17:99','ያ ሰማያትንና ምድርን የፈጠረ አላህ ብጤያቸውን በመፍጠር ላይ ቻይ መኾኑን በእርሱ ጥርጣሬ የሌለበትም ጊዜ (ለሞትም ለትንሣኤም) ለእነርሱ የወሰነ መኾኑን አላወቁምን በደለኞችም ከክህደት በቀር እምቢ አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,100,'17:100','«እናንተ የጌታዬን የችሮታ መካዚኖች ብትይዙ ኖሮ ያን ጊዜ በማውጣታችሁ ማለቋን በመፍራት በጨበጣችሁ ነበር፡፡ ሰውም በጣም ቆጣቢ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,101,'17:101','ለሙሳም ግልጽ የኾኑን ዘጠኝ ተዓምራቶች በእርግጥ ሰጠነው፡፡ በመጣቸውም ጊዜ የእስራኤልን ልጆች (ከፈርዖን እንዲለቀቁ) ጠይቅ (አልነው)፡፡ ፈርዖንም «ሙሳ ሆይ! እኔ የተደገመብህ መኾንህን በእርግጥ እጠረጥራለሁ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,102,'17:102','(ሙሳም) «እነዚህን (ተዓምራቶች) መገሰጫዎች ሲኾኑ የሰማያትና የምድር ጌታ እንጂ ሌላ እንዳላወረዳቸው በእርግጥ ዐውቀሃል፡፡ እኔም ፈርዖን ሆይ! የምትጠፋ መኾንህን በእርግጥ እጠረጥርሃለሁ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,103,'17:103','ከምድርም ሊቀሰቅሳቸው አሰበ፡፡ እርሱንና ከእርሱ ጋር የነበሩትንም ሰዎች ሁሉንም አሰጠምናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,104,'17:104','ከእርሱም በኋላ ለእሰራኤል ልጆች «ምድሪቱን ተቀመጡባት የኋለኛይቱም (ሰዓት) ቀጠሮ በመጣ ጊዜ እናንተን የተከማቻችሁ ስትኾኑ እናመጣችኋለን» አልናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,105,'17:105','(ቁርኣንን) በውነትም አወረድነው፡፡ በእውነትም ወረደ፡፡ አንተንም አብሳሪና አስጠንቃቂ አድርገን እንጂ አልላክንህም፡፡');
INSERT INTO "sadiq_sani" VALUES(17,106,'17:106','ቁርኣንንም በሰዎች ላይ በዝግታ ላይ ኾነህ ታነበው ዘንድ ከፋፈልነው፡፡ ቀስ በቀስ ማውረድንም አወረድነው፡፡');
INSERT INTO "sadiq_sani" VALUES(17,107,'17:107','«በእርሱ እመኑ ወይም አትመኑ» በላቸው፤ እነዚያ ከእርሱ በፊት ዕውቀትን የተሰጡት በእነሱ ላይ በተነበበ ጊዜ ሰጋጆች ኾነው በሸንጎበቶቻቸው ላይ ይወድቃሉ፤');
INSERT INTO "sadiq_sani" VALUES(17,108,'17:108','ይላሉም «ጌታችን ጥራት ይገባው! እነሆ የጌታችን ተስፋ ተፈጻሚ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(17,109,'17:109','እያለቀሱም በሸንጎበቶቻቸው ላይ ይወድቃሉ (አላህን) መፍራትንም ይጨምራላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(17,110,'17:110','«አላህን ጥሩ፤ ወይም አልረሕማንን ጥሩ፤ (ከሁለቱ) ማንኛውንም ብትጠሩ (መልካም ነው)፡፡ ለእርሱ መልካም ስሞች አሉትና» በላቸው፡፡ በስግደትህም (ስታነብ) አትጩህ፡፡ በእርሷም ድምጽህን ዝቅ አታድርግ በዚህም መካከል መንገድን ፈልግ፡፡');
INSERT INTO "sadiq_sani" VALUES(17,111,'17:111','«ምስጋና ለአላህ ለዚያ ልጅን ላልያዘው፣ ለእርሱም በንግሥናው ተጋሪ ለሌለው፣ ለእርሱም ከውርደት ረዳት ለሌለው ይገባው በልም፡፡ ማክበርንም አክብረው፡፡»');
INSERT INTO "sadiq_sani" VALUES(18,1,'18:1','ምስጋና ለአላህ ለዚያ መጽሐፉን በውስጡ መጣመምን ያላደረገበት ሲሆን በባሪያው ላይ ላወረደው ይገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,2,'18:2','ቀጥተኛ ሲሆን ከእርሱ ዘንድ የኾነን ብርቱን ቅጣት ሊያስፈራራበት፣ እነዚያንም በጎ ሥራዎችን የሚሠሩትን ምእመናን ለነሱ መልካም ምንዳ ያላቸው መኾኑን ሊያበስርበት፤ (አወረደው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,3,'18:3','በእርሱ ውስጥ ዘለዓለም የሚቆዩበት ሲኾኑ (ያላቸው መኾኑን)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,4,'18:4','እነዚያንም «አላህ ልጅን ይዟል» ያሉትን ሊያስፈራራበት (አወረደው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,5,'18:5','ለእነሱም ለአባቶቻቸውም በርሱ ምንም እውቀት የላቸውም፡፡ ከአፎቻቸው የምትወጣውን ቃል ምን አከበዳት! ውሸትን እንጂ አይናገሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,6,'18:6','በዚህም ንግግር (በቁርኣን) ባያምኑ በፈለጎቻቸው ላይ በቁጭት ነፍስህን አጥፊ ልትሆን ይፈራልሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(18,7,'18:7','እኛ ማንኛቸው ሥራው ያማረ መኾኑን ልንፈትናቸው በምድር ላይ ያለን ሁሉ ለእርሷ ጌጥ አደረግን፤');
INSERT INTO "sadiq_sani" VALUES(18,8,'18:8','እኛም በእርሷ ላይ ያለውን (በመጨረሻ) በእርግጥ በቃይ የሌለበት ምልጥ ዐፈር አድራጊዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,9,'18:9','የዋሻውና የሰሌዳው ባለቤቶች ከተዓምራቶቻችን ሲሆኑ ግሩም መሆናቸውን አሰብክን');
INSERT INTO "sadiq_sani" VALUES(18,10,'18:10','ጎበዞቹ ወደ ዋሻው በተጠጉና «ጌታችን ሆይ! ከአንተ ዘንድ እዝነትን ስጠን፤ ለእኛም ከነገራችን ቅንን አዘጋጅልን» ባሉ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,11,'18:11','በዋሻውም ውስጥ የተቆጠሩን ዓመታት በጆሮዎቻቸው ላይ መታንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,12,'18:12','ከዚያም ከሁለቱ ክፍሎች ለቆዩት ጊዜ ልክ ያረጋገጠው ማንኛው መሆኑን ልናውቅ አስነሳናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,13,'18:13','እኛ ወሬያቸውን በአንተ ላይ በእውነት እንተርካለን፡፡ እነሱ በጌታቸው ያመኑ ጎበዞች ናቸው፡፡ መመራትንም ጨመርንላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,14,'18:14','(በንጉሣቸው ፊት) በቆሙና «ጌታችን የሰማያትና የምድር ጌታ ነው፡፡ ከእርሱ ሌላ አምላክን አንገዛም፡፡ ያን ጊዜ (ሌላን ብናመልክ) ወሰን ያለፈን (ንግግር) በእርግጥ ተናገርን፡፡» ባሉ ጊዜ ልቦቻቸውን አጠነከርን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,15,'18:15','«እነዚህ ሕዝቦቻችን ከእርሱ ሌላ አማልክትን ያዙ፤ (እውነተኞች ከኾኑ) በእነርሱ ላይ ግልጽ ማስረጃን ለምን አያመጡም በአላህም ላይ ውሸትን ከሚቀጣጥፍ ይበልጥ በዳይ ማነው» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,16,'18:16','«እነርሱንና ያንን ከአላህ ሌላ የሚግገዙትን በተለያችሁ ጊዜ ወደ ዋሻው ተጠጉ፡፡ ጌታችሁ ለእናንተ ከችሮታው ይዘረጋላችኋልና፤ ከነገራችሁም ለእናንተ መጠቃቀሚያን ያዘጋጅላችኋል» (ተባባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,17,'18:17','ፀሐይንም በወጣች ጊዜ ከዋሻቸው ወደ ቀኝ ጎን ስታዘነብል ታያታለህ፡፡ በገባችም ጊዜ እነርሱ ከእርሱ በሰፊው ስፍራ ውስጥ ኾነው ሳሉ ወደ ግራ በኩል ትተዋቸዋለች፡፡ ይህ ከአላህ ተዓምራቶች ነው፡፡ አላህ የሚያቀናው ሰው ቅኑ እርሱ ብቻ ነው፡፡ የሚያጠመውም ሰው ለርሱ አቅኝን ረዳት አታገኝለትም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,18,'18:18','እነሱም የተኙ ሆነው ሳሉ (ዓይኖቻቸው በመከፈታቸው) ንቁዎች ናቸው ብለህ ታስባቸዋለህ፡፡ ወደ ቀኝ ጎንና ወደ ግራ ጎንም እናገላብጣቸዋለን፡፡ ውሻቸውም ሁለት ክንዶቹን በዋሻው በር ላይ ዘርግቷል፡፡ በእነሱ ላይ ዘልቀህ ብታይ ኖሮ የምትሸሽ ኾነህ በዞርክ ነበር፡፡ ከእነሱም በእርግጥ ፍርሃትን በተመላህ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(18,19,'18:19','እንዲሁም በመካከላቸው እንዲጠያየቁ ቀሰቀስናቸው፡፡ ከእነሱ አንድ ተናጋሪ «ምን ያክል ቆያችሁ» አለ፡፡ «አንድን ቀን ወይም የቀንን ከፊል ቆየን» አሉ፡፡ «ጌታችሁ የቆያችሁትን ጊዜ ልክ ዐዋቂ ነው፡፡ ከዚህችም ብራችሁ ጋር አንዳችሁን ወደ ከተማይቱ ላኩ፡፡ ከምግቦቿ የትኛዋ ንጹሕ መሆኗንም ይመልከት፡፡ ከእርሱም (ከንጹሑ) ምግብን ያምጣላችሁ፡፡ ቀስም ይበል፡፡ በእናንተም አንድንም ሰው አያሳውቅ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,20,'18:20','«እነሱ በእናንተ ላይ ቢዘልቁ ይቀጠቅጧችኋልና፡፡ ወይም ወደ ሃይማኖታቸው ይመልሷችኋል፡፡ ያንጊዜም በፍጹም ፍላጎታችሁን አታገኙም» (ተባባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,21,'18:21','እንደዚሁም የአላህ ቀጠሮ እርግጠኛ መሆኑን ሰዓቲቱም በርሷ ጥርጣሬ የሌለ መሆኑን ያውቁ ዘንድ በእነርሱ ላይ (ሰዎችን) አሳወቅን፡፡ (አማኞቹና ከሓዲዎቹ) ነገራቸውን በመካከላቸው ሲከራከሩ (የሆነውን አስታውስ፤ ከሓዲዎቹ)፡- «በእነሱ ላይም ግንብን ገንቡ» አሉ፡፡ ጌታቸው በእነሱ ይበልጥ ዐዋቂ ነው፡፡ እነዚያ በነገራቸው ላይ ያሸነፉት (ምእመናን) «በእነሱ ላይ በእርግጥ መስጊድን እንሠራለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,22,'18:22','በሩቅ ወርዋሪዎች ሆነው (በጥርጣሬ) «ሦስት ናቸው፤ አራተኛቸው ውሻቸው ነው» ይላሉ፡፡ «አምስት ናቸው፤ ስድስተኛቸው ውሻቸው ነውም» ይላሉ፡፡ «ሰባት ናቸው፤ ስምንተኛቸውም ውሻቸው ነው» ይላሉም፡፡ «ጌታዬ ቁጥራቸውን ዐዋቂ ነው፡፡ ጥቂት (ሰው) እንጂ አያውቃቸውም» በላቸው፡፡ በእነሱም ነገር ግልጽን ክርክር እንጂ (ጠልቀህ) አትከራከር፡፡ በእነሱም ጉዳይ ከነሱ (ከመጽሐፉ ሰዎች) አንድንም አትጠይቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,23,'18:23','ለማንኛውም ነገር «እኔ ይህንን ነገ ሠሪ ነኝም» አትበል፡፡');
INSERT INTO "sadiq_sani" VALUES(18,24,'18:24','«አላህ የሻ እንደ ሆነ (እሰራዋለሁ)» ብትል እንጂ፡፡ በረሳህም ጊዜ ጌታህን አውሳ፡፡ «ጌታዬም ከዚህ ይበልጥ ለቀጥታ የቀረበን ሊመራኝ ይከጀላል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,25,'18:25','በዋሻቸውም ውስጥ ሶስት መቶ ዓመታትን ቆዩ፡፡ ዘጠኝንም ጨመሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,26,'18:26','«አላህ የቆዩትን ልክ ዐዋቂ ነው» በላቸው፡፡ የሰማያትና የምድር ምስጢር የእሱ ብቻ ነው፡፡ እርሱ ምን ያይ! ምን ይሰማም! ለእነርሱ ከእርሱ በቀር ምንም ረዳት የላቸውም፡፡ በፍርዱም አንድንም አያጋራም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,27,'18:27','ከጌታህም መጽሐፍ ወደ አንተ የተወረደውን አንብብ፡፡ ለቃላቶቹ ለዋጭ የላቸውም፡፡ ከእርሱም በቀር መጠጊያን በፍጹም አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,28,'18:28','ነፍስህንም፤ ከእነዚያ ጌታቸውን ፊቱን (ውዴታውን) የሚሹ ኾነው በጧትና በማታ ከሚግገዙት ጋር አስታግስ፡፡ የቅርቢቱንም ሕይወት ሽልማት የምትሻ ሆነህ ዓይኖችህ ከእነሱ (ወደ ሌላ) አይለፉ፡፡ ልቡንም እኛን ከማስታወስ ያዘነጋነውን ፍላጎቱንም የተከተለውን ነገሩም ሁሉ ወሰን ማለፍ የሆነውን ሰው አትታዘዝ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,29,'18:29','«እውነቱም ከጌታችሁ ነው፡፡ የሻም ሰው ይመን፤ የሻም ሰው ይካድ» በላቸው፡፡ እኛ ለበደለኞች አጥሯ በእነሱ የከበበ የኾነችውን እሳት አዘጋጅተናል፡፡ (ከጥም) እርዳታንም ቢፈልጉ እንደ ዘይት ዝቃጭ ባለ ፊቶችን በሚጠብስ ውሃ ይረዳሉ፡፡ መጠጡ ከፋ! መደገፊያይቱም ምንኛ አስከፋች!');
INSERT INTO "sadiq_sani" VALUES(18,30,'18:30','እነዚያ ያመኑና መልካም ሥራዎችንም የሠሩ እኛ ሥራን ያሳመረን ሰው ምንዳ አናጠፋም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,31,'18:31','እነዚያ ለእነሱ የመኖሪያ ገነቶች ከሥሮቻቸው ወንዞች የሚፈሱባቸው አሏቸው፡፡ በእርሷ ውስጥ ከወርቅ የኾኑን አንባሮች ይሸለማሉ፡፡ ከቀጭን ሐርና ከወፍራም ሐርም አረንጓዴን ልብሶች ይለብሳሉ፡፡ በውስጧ በባለ አጎበር አልጋዎች ላይ የተደገፉ ኾነው (ይቀመጣሉ)፡፡ ምንዳቸው ምን ያምር! መደገፊያይቱም ምንኛ አማረች!');
INSERT INTO "sadiq_sani" VALUES(18,32,'18:32','ለእነሱም የሁለትን ሰዎች ምሳሌ ግለጽላቸው፡፡ ለአንደኛቸው (ለከሓዲው) ከወይኖች የኾኑን ሁለት አትክልቶች አደረግንለት፡፡ በዘምባባም አከበብናቸው፡፡ በመካከላቸውም አዝመራን አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,33,'18:33','ሁለቱም አትክልቶች ሰብላቸውን ሰጡ፡፡ ከእርሱም ምንም አላጎደሉም፡፡ በመካከላቸውም ወንዝን አፈሰስን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,34,'18:34','ለእርሱም (ከአትክልቶቹ ሌላ ፍሬያማ) ሀብት ነበረው፡፡ ለጓደኛውም (ለአማኙ) እርሱ የሚወዳደረው ሲኾን «እኔ ካንተ በገንዘብ ይበልጥ የበዛሁ በወገንም ይበልጥ የበረታሁ ነኝ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,35,'18:35','እርሱም ነፍሱን በዳይ ኾኖ ወደ አትክልቱ ገባ፡፡ «ይህች ዘለዓለም ትጠፋለች ብዬ አልጠረጥርም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,36,'18:36','«ሰዓቲቱንም ቋሚ (ኋኝ) ናት ብዬ አልጠረጥርም፡፡ (እንደምትለው) ወደ ጌታዬም ብመለስ ከእርሷ የበለጠን መመለሻ በእርግጥ አገኛለሁ» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,37,'18:37','ጓደኛው (አማኙ) እርሱ ለእርሱ የሚመላለሰው ሲኾን «በዚያ ከዐፈር፣ ከዚያም ከፍትወት ጠብታ ከፈጠረህ፣ ከዚያም ሰው ባደረገህ (አምላክ) ካድክን» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,38,'18:38','«እኔ ግን እርሱ አላህ ጌታዬ ነው፤ (እላለሁ)፡፡ በጌታዬም አንድንም አላጋራም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,39,'18:39','«አትክልትህንም በገባህ ጊዜ አላህ የሻው ይኾናል፤ በአላህ ቢኾን እንጂ ኀይል የለም፤ አትልም ኖሯልን እኔን በገንዘብም በልጅም ካንተ በጣም ያነስኩ ሆኜ ብታየኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,40,'18:40','«ጌታዬ ከአትክልትህ የበለጠን ሊሰጠኝ በርሷም (በአትክልትህ) ላይ ከሰማይ መብረቆችን ሊልክባትና የምታንዳልጥ ምልጥ ምድር ልትኾን ይቻላል፡፡');
INSERT INTO "sadiq_sani" VALUES(18,41,'18:41','ወይም ውሃው ሠራጊ ሊኾን (ይችላል)፡፡ ያን ጊዜ ለርሱ መፈለግን ፈጽሞ አትችልም» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,42,'18:42','ሀብቱም ተጠፋ፡፡ እርሷ በዳሶቿ ላይ የወደቀች ኾና በእርሷ ባወጣው ገንዘብ ላይ (እየተጸጸተ) መዳፎቹን የሚያገላብጥና «ወይ ጸጸቴ! በጌታዬ አንድም ባላጋራሁ» የሚል ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,43,'18:43','ለእርሱም ከአላህ ሌላ የሚረዱት ጭፍሮች አልነበሩትም፡፡ ተረጂም አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,44,'18:44','እዚያ ዘንድ (በትንሣኤ ቀን) ስልጣኑ እውነተኛ ለኾነው አላህ ብቻ ነው፡፡ እርሱ በመመንዳት የበለጠ ፍጻሜንም በማሳመር የበለጠ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,45,'18:45','ለእነሱም የቅርቢቱን ሕይወት ምሳሌ አውሳላቸው፡፡ (እርሷ) ከሰማይ እንዳወረድነው ውሃ በእርሱም የምድር በቃይ እንደ ተቀላቀለበት (ከተዋበ በኋላ ደርቆ) ነፋሶችም የሚያበኑት ደቃቅ እንደ ኾነ ብጤ ናት፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,46,'18:46','ገንዘብና ወንዶች ልጆች የቅርቢቱ ሕይወት ጌጦች ናቸው፡፡ መልካሞቹም ቀሪዎች (ሥራዎች) እጌታህ ዘንድ በምንዳ በላጭ ናቸው፡፡ በተስፋም በላጭ ናቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(18,47,'18:47','ተራራዎችንም የምናስኼድበትን ቀን (አስታውስ)፡፡ ምድርንም ግልጽ ኾና ታያታለህ፡፡ እንሰበስባቸዋለንም፡፡ ከእነሱም አንድንም አንተውም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,48,'18:48','የተሰለፉም ኾነው በጌታህ ላይ ይቀረባሉ፡፡ (ይባላሉም) «በመጀመሪያ ጊዜ እንደ ፈጠርናችሁ (ራቁታችሁን) በእርግጥ መጣችሁን በእውነቱ ለእናንተ (ለመቀስቀሻ) ጊዜን አናደርግም መስሏችሁ ነበር፡፡»');
INSERT INTO "sadiq_sani" VALUES(18,49,'18:49','(ለሰው ሁሉ) መጽሐፉም ይቀርባል፡፡ ወዲያውም ከሓዲዎችን በውስጡ ካለው ነገር ፈሪዎች ኾነው ታያቸዋለህ፡፡ «ዋ ጥፋታችን! ለዚህ መጽሐፍ (ከሥራ) ትንሽንም ትልቅንም የቆጠራት ቢኾን እንጂ የማይተወው ምን አለው» ይላሉም፡፡ የሰሩትንም ነገር ሁሉ ቀራቢ ኾኖ ያገኙታል፡፡ ጌታህም አንድንም አይበድልም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,50,'18:50','ለመላእክትም ለአደም ስገዱ ባልናቸው ጊዜ (የኾነውን አስታውስ)፡፡ ወዲያውም ሰገዱ፤ ኢብሊስ ብቻ ሲቀር፡፡ ከጋኔን (ጎሳ) ነበር፡፡ ከጌታውም ትእዛዝ ወጣ፡፡ እርሱንና ዘሮቹን እነርሱ ለእናንተ ጠላቶች ሲኾኑ ከእኔ ላይ ረዳቶች አድርጋችሁ ትይዛላችሁን ለበዳዮች ልዋጭነቱ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(18,51,'18:51','የሰማያትንና የምድርን አፈጣጠር አላሳየኋቸውም፡፡ የነፍሶቻቸውንም አፈጣጠር (እንደዚሁ)፡፡ አሳሳቾችንም ረዳቶች አድርጌ የምይዝ አይደለሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,52,'18:52','«እነዚያንም (አምላክ) የምትሏቸውን ተጋሪዎቼን ጥሩ» የሚልበትን ቀን (አስታውስ)፡፡ ይጠሯቸዋልም ግን አይመልሱላቸውም፡፡ በመካከላቸውም መጥፊያን ስፍራ (ገሀነምን) አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,53,'18:53','ከሓዲዎችም እሳትን ያያሉ፡፡ እነርሱም በውስጧ ወዳቂዎች መኾናቸውን ያረጋግጣሉ፡፡ ከእርሷም መሸሻን አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,54,'18:54','በዚህም ቁርኣን ውስጥ ከየምሳሌው ሁሉ ለሰዎች መላልሰን ገለጽን፡፡ ሰውም ከነገሩ ሁሉ ይበልጥ ክርክረ ብዙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,55,'18:55','ሰዎችንም መምሪያ በመጣላቸው ጊዜ ከማመንና ጌታቸውንም ምሕረትን ከመለመን የመጀመሪዎቹ (ሕዝቦች) ልማድ (መጥፋት) ልትመጣባቸው ወይም ቅጣቱ በያይነቱ ሊመጣባቸው (መጠባበቅ) እንጂ ሌላ አልከለከላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,56,'18:56','መልክተኞችንም አብሳሪዎችና አስፈራሪዎች ኾነው እንጂ አንልክም፡፡ እነዚያ የካዱትም በውሸት እውነቱን በእሱ ሊያበላሹ ይከራከራሉ፡፡ አንቀጾቼንና በእርሱ የተስፈራሩበትንም ነገር ማላገጫ አድርገው ያዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,57,'18:57','በጌታውም አንቀጾች ከተገሰጸና ከእርሷ ከዞረ እጆቹም ያስቀደሙትን ነገር ከረሳ ሰው ይበልጥ በዳይ ማነው እኛ በልቦቻቸው ላይ እንዳያውቁት ሺፋኖችን በጆሮዎቻቸውም ውስጥ ድንቁርናን አደረግን፡፡ ወደ መመራትም ብትጠራቸው ያን ጊዜ ፈጽሞ አይምመሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,58,'18:58','ጌታህም በጣም መሃሪው የእዝነት ባለቤቱ ነው፡፡ በሠሩት ሥራ ቢይዛቸው ኖሮ ቅጣቱን ለእነሱ ባስቸኮለባቸው ነበር፡፡ ግን ለእነሱ ከእርሱ ሌላ መጠጊያን ፈጽሞ የማያገኙበት ቀጠሮ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,59,'18:59','እነዚህ ከተሞችም በበደሉ ጊዜ አጠፋናቸው፡፡ ለመጥፊያቸውም የተወሰነ ጊዜን አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,60,'18:60','ሙሳም ለወጣቱ «የሁለቱን ባሕሮች መገናኛ እስከምደርስ ወይም ብዙን ጊዜ እስከምኼድ ድረስ (ከመጓዝ) አልወገድም» ያለውን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,61,'18:61','የሁለቱን መገናኛ በደረሱም ጊዜ ዐሣቸውን ረሱ፡፡ (ዐሣው) መንገዱንም በባህሩ ውስጥ ቀዳዳ አድርጎ ያዘ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,62,'18:62','ባለፉም ጊዜ ለወጣቱ «ምሳችንን ስጠን፡፡ ከዚህ ጉዟችን በእርግጥ ድካምን አግኝተናልና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,63,'18:63','«አየህን ወደ ቋጥኝዋ በተጠጋን ጊዜ እኔ ዐሣውን ረሳሁ፡፡ ማስታወሱንም ሰይጣን እንጂ ሌላ አላስረሳኝም፡፡ በባሕሩም ውስጥ መንገዱን አስደናቂ (መንገድ) አድርጎ ያዘ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,64,'18:64','(ሙሳም) ይህ እንፈልገው የነበርነው ነው አለው፡፡ በፈለጎቻቸውም ላይ እየተከተሉ ተመለሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,65,'18:65','ከባሮቻችንም ከእኛ ዘንድ ችሮታን የሰጠነውን ከእኛም ዘንድ ዕውቀትን ያስተማርነውን አንድን ባሪያ (ኸድርን) አገኙ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,66,'18:66','ሙሳ ለእርሱ «ከተማርከው ነገር ቀጥተኛን (ዕውቀት) ታስተምረኝ ዘንድ ልከተልህን» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,67,'18:67','(ባሪያውም) አለ «አንተ ከእኔ ጋር መታገስን በጭራሽ አትችልም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,68,'18:68','«በዕውቀትም በእርሱ ባላዳረስከው ነገር ላይ እንዴት ትታገሳለህ»');
INSERT INTO "sadiq_sani" VALUES(18,69,'18:69','(ሙሳ) «አላህ የሻ እንደ ኾነ ታጋሽና ላንተ ትዕዛዝን የማልጥስ ኾኜ ታገኘኛለህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,70,'18:70','«ብትከተለኝም ለአንተ ከእርሱ ማውሳትን እስከምጀምርልህ ድረስ ከምንም ነገር አትጠይቀኝ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,71,'18:71','ኼዱም፡፡ በመርከቢቱም በተሳፈሩ ጊዜ ቀደዳት፡፡ «ባለ ቤቶቿን ልታሰጥም ቀደድካትን ትልቅ ነገርን በእርግጥ ሠራህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,72,'18:72','«አንተ ከኔ ጋር ትእግሥትን ፈጽሞ አትችልም አላልኩምን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,73,'18:73','«በረሳሁት ነገር አትያዘኝ፡፡ ከነገሬም ችግርን አታሸክመኝ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,74,'18:74','(ወርደው) ተጓዙም፡፡ ወጣትንም ልጅ ባገኙና በገደለው፤ «ጊዜ ያለ ነፍስ (መግደል) ንጹሕን ነፍስ ገደልክን በእርግጥ መጥፎን ነገር ሥራህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,75,'18:75','«አንተ ከኔ ጋር ትእግሥትን ፈጽሞ አትችልም አላልኩህምን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,76,'18:76','«ከርሷ (ከአሁኒቱ ጊዜ) በኋላ ከምንም ነገር ብጠይቅህ አትጎዳኘኝ፡፡ ከእኔ የይቅርታን መጨረሻ በእርግጥ ደርሰሃል» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,77,'18:77','ኼዱም፡፡ ወደ አንዲት ከተማ ሰዎች በመጡም ጊዜ ነዋሪዎቿን ምግብን ጠየቁ፡፡ ከማስተናገዳቸውም እምቢ አሉ፡፡ በእርሷም ውስጥ ለመውደቅ የተቃረበን ግድግዳ አገኙ፡፡ (ኸድር) አቆመውም፡፡ (ሙሳም) «በሻህ ኖሮ በእርሱ ላይ ዋጋን በተቀበልክ ነበር» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,78,'18:78','(ኸድር) አለ «ይህ በእኔና በአንተ መካከል መለያያ ነው፡፡ በእርሱ ላይ መታገስን ያልቻልክበትን ፍች እነግርሃለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,79,'18:79','«መርከቢቱማ በባሕር ለሚሠሩ ምስኪኖች ነበረች፡፡ ከኋላቸውም መርከብን ሁሉ በቅሚያ የሚይዝ ንጉሥ ነበረና፤ (እንዳይቀማቸው) ላነውራት ፈቀድኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,80,'18:80','«ወጣቱም ልጅማ ወላጆቹ ምእመናን ነበሩ፡፡ (ቢያድግ) ትእቢትንና ክህደትንም የሚያስገድዳቸው መኾኑን ፈራን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,81,'18:81','«ጌታቸውም በንጹሕነት ከእርሱ በላጭን በእዝነትም ከእርሱ በጣም ቀራቢን ልጅ ሊለውጥላቸው ፈለግን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,82,'18:82','«ግድግዳውማ በከተማይቱ ውስጥ የነበሩ የሁለት የቲሞች ወጣቶች ነበር፡፡ በሥሩም ለእነርሱ የኾነ (የተቀበረ) ድልብ ነበረ፡፡ አባታቸውም መልካም ሰው ነበር፡፡ ጌታህም ለአካለ መጠን እንዲደርሱና ከጌታህ ችሮታ ድልባቸውን እንዲያወጡ ፈቀደ፡፡ በፈቃዴም አልሠራሁትም፡፡ ይህ በእርሱ ላይ መታገስን ያልቻልከው ነገር ፍች ነው» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,83,'18:83','ከዙልቀርነይንም ይጠይቁሃል፡፡ «በእናንተ ላይ ከእርሱ ወሬን አነባለሁ፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,84,'18:84','እኛ ለእርሱ በምድር አስመቸነው፡፡ ከነገሩም ሁሉ (መዳረሻ) መንገድን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,85,'18:85','መንገድንም (ወደ ምዕራብ) ተከተለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,86,'18:86','ወደ ፀሐይ መግቢያም በደረሰ ጊዜ ጥቁር ጭቃ ባላት ምንጭ ውስጥ ስትጠልቅ አገኛት፡፡ ባጠገቧም ሕዝቦችን አገኘ፡፡ «ዙልቀርነይን ሆይ! ወይም (በመግደል) ትቀጣለህ ወይም በእነርሱ መልካም ነገርን (መማረክን) ትሠራለህ» አልነው፡፤');
INSERT INTO "sadiq_sani" VALUES(18,87,'18:87','«የበደለውን ሰውማ ወደፊት እንቀጣዋለን፡፡ ከዚያም ወደ ጌታው ይመለሳል፡፡ ብርቱንም ቅጣት ይቀጣዋል» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,88,'18:88','«ያመነም ሰውማ መልካምንም ሥራ የሠራ ለእርሱ ከምንዳ በኩል መልካሚቱ (ገነት) አለችው፡፡ ለእርሱም ከትእዛዛችን ገርን ነገር እናዘዋለን» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,89,'18:89','ከዚያም መንገድን (ወደ ምሥራቅ) ቀጠለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,90,'18:90','ወደ ፀሐይ መውጫ በደረሰም ጊዜ ለእነሱ ከበታችዋ መከለያን ባላደረግንላቸው ሕዝቦች ላይ ስትወጣ አገኛት፡፡');
INSERT INTO "sadiq_sani" VALUES(18,91,'18:91','(ነገሩ) እንደዚሁ ነው፡፡ እርሱም ዘንድ ባለው ነገር ሁሉ በእርግጥ በዕውቀት ከበብን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,92,'18:92','ከዚያም (ወደ ሰሜን አቅጣጫ) መንገድን ቀጠለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,93,'18:93','በሁለቱ ጋራዎችም መካከል በደረሰ ጊዜ በአቅራቢያቸው ንግግርን ለማወቅ የማይቃረቡ ሕዝቦችን አገኘ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,94,'18:94','«ዙልቀርነይን ሆይ! የእጁጅና መእጁጅ በምድር ላይ አበላሺዎች ናቸውና በእኛና በእነሱ መካከል ግድብን ታደርግልን ዘንድ ግብርን እናድርግልህን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,95,'18:95','አለ «ጌታዬ በእርሱ ያስመቸኝ ሀብት (ከናንተ ግብር) በላጭ ነው፡፡ ስለዚህ በጉልበት እገዙኝ፡፡ በእናንተና በእነሱ መካከል ብርቱን ግድብ አደርጋለሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(18,96,'18:96','«የብረትን ታላላቅ ቁራጮች ስጡኝ» (አላቸው)፡፡ በሁለቱ ኮረብታዎች (ጫፍ) መካከልም ባስተካከለ ጊዜ አናፉ አላቸው፡፡ (ብረቱን) እሳትም ባደረገው ጊዜ «የቀለጠውን ነሐስ ስጡኝ፤ በርሱ ላይ አፈስበታለሁና» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,97,'18:97','(የእጁጅና መእጁጅ) ሊወጡትም አልቻሉም፡፡ ለእርሱ መሸንቆርንም አልቻሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,98,'18:98','«ይህ (ግድብ) ከጌታዬ ችሮታ ነው፡፡ የጌታየም ቀጠሮ በመጣ ጊዜ ትክክል ምድር ያደርገዋል፡፡ የጌታዬም ቀጠሮ ተረጋገጠ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(18,99,'18:99','በዚያም ቀን ከፊላቸውን በከፊሉ የሚቀላቀሉ አድርገን እንተዋቸዋለን፡፡ በቀንዱም ይነፋል መሰብሰብንም እንሰበስባቸዋለን፡፤');
INSERT INTO "sadiq_sani" VALUES(18,100,'18:100','ገሀነምንም በዚያ ቀን ለከሓዲዎች በጣም ማቅረብን እናቀርባታለን፡፡');
INSERT INTO "sadiq_sani" VALUES(18,101,'18:101','ለዚያ ዓይኖቻቸው ከግሳጼዬ በሺፋን ውስጥ ለነበሩትና መስማትንም የማይችሉ ለነበሩት (እናቀርባታለን)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,102,'18:102','እነዚያ የካዱት ባሮቼን ከእኔ ሌላ አማልክት አድርገው መያዛቸውን (የማያስቆጣኝ አድርገው) አሰቡን እኛ ገሀነምን ለከሓዲዎች መስተንግዶ አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(18,103,'18:103','«በሥራዎች በጣም ከሳሪዎቹን እንንገራችሁን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,104,'18:104','እነዚያ እነሱ ሥራን የሚያሳምሩ የሚመስላቸው ሲሆኑ በቅርቢቱ ሕይወት ሥራቸው የጠፋባቸው ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,105,'18:105','እነዚያ እነርሱ በጌታቸው ማስረጃዎችና በመገናኘቱ የካዱት ናቸው፡፡ ሥራዎቻቸውም ተበላሹ፡፡ ለእነሱም በትንሣኤ ቀን (ጠቃሚ) ሚዛንን አናቆምላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(18,106,'18:106','(ነገሩ) ይህ ነው፡፡ በክህደታቸው አንቀጾቼንና መልክተኞቼንም መሳለቂያ አድርገው በመያዛቸው ፍዳቸው ገሀነም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,107,'18:107','እነዚያ ያመኑና መልካም ሥራዎችን የሠሩ የፊርደውስ ገነቶች ለእነሱ መስፈሪያ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,108,'18:108','በእርሷ ውስጥ ዘውታሪዎች ከእርሷ መዛወርን የማይፈልጉ ሲኾኑ፤ (መስፈሪያቸው ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(18,109,'18:109','«ባሕሩ ለጌታዬ ቃላት (መጻፊያ) ቀለሞችን በሆነ ኖሮ ብጤውን ጭማሪ ብናመጣም እንኳን የጌታዬ ቃላት ከማለቋ በፊት ባሕሩ ባለቀ ነበር» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(18,110,'18:110','«እኔ አምላካችሁ አንድ አምላክ ብቻ ነው ማለት ወደኔ የሚወረድልኝ ቢጤያችሁ ሰው ብቻ ነኝ፡፡ የጌታውንም መገናኘት የሚፈልግ ሰው፤ መልካም ሥራን ይሥራ፡፡ በጌታውም መገዛት አንድንም አያጋራ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,1,'19:1','ከ.ሀ.የ.ዐ.ጸ (ካፍ ሃ ያ ዓይን ሷድ)');
INSERT INTO "sadiq_sani" VALUES(19,2,'19:2','(ይህ) ጌታህ ባሪያውን ዘከሪያን በችሮታው ያወሳበት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,3,'19:3','ጌታውን የምስጢርን ጥሪ በተጣራ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(19,4,'19:4','አለ «ጌታዬ ሆይ! እኔ አጥንቴ ደከመ፤ ራሴም በሺበት ተንቀለቀለ፤ አንተም በመለመኔ ጌታዬ ሆይ! (ምንጊዜም) ዕድለ ቢስ አልሆንኩም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,5,'19:5','«እኔም ከበኋላዬ ዘመዶቼን በእርግጥ ፈራሁ፡፡ ሚስቴም መካን ነበረች ስለዚህ ከአንተ ዘንድ ለኔ ልጅን ስጠኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,6,'19:6','«የሚወርሰኝ ከያዕቆብ ቤተሰቦችም የሚወርስ የሆነን (ልጅ)፡፡ ጌታዬ ሆይ! ተወዳጅም አድርገው፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,7,'19:7','«ዘከሪያ ሆይ! እኛ በወንድ ልጅ ስሙ የሕያ በኾነ ከዚህ በፊት ሞክሼን ባላደረግንለት እናበስርሃለን» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(19,8,'19:8','«ጌታዬ ሆይ! ሚስቴ መካን የነበረች ስትኾን እኔም ከእርጅና ድርቀትን በእርግጥ የደረስኩ ስኾን እንዴት ልጅ ይኖረኛል!» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,9,'19:9','(ጅብሪል) አለ «(ነገሩ) እንደዚሁ ነው፡፡ ጌታህ፡- ከአሁን በፊት ምንም ያልነበርከውን የፈጠርኩህ ስኾን እርሱ በእኔ ላይ ቀላል ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,10,'19:10','«ጌታዬ ሆይ! (እንግዲያውስ) ለእኔ ምልክትን አድርግልኝ አለ፡፡ ምልክትህ ጤናማ ሆነህ ሳለህ ሦስት ሌሊትን (ከነቀናቸው) ሰዎችን ለማነጋገር አለመቻልህ ነው» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,11,'19:11','ከምኩራቡም በሕዝቦቹ ላይ ወጣ፡፡ በ×ትና በማታ (ጌታችሁን) አወድሱ በማለትም ወደነሱ ጠቀሰ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,12,'19:12','«የሕያ ሆይ! መጽሐፉን በጥብቅ ያዝ!» (አልነው)፡፡ ጥበብንም በሕፃንነቱ ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,13,'19:13','ከእኛም የሆነን ርኅራኄ ንጽሕናንም (ሰጠነው)፡፡ ጥንቁቅም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(19,14,'19:14','ለወላጆቹም በጎ ሠሪ ነበር፡፡ ትዕቢተኛ አመጸኛም አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,15,'19:15','በተወለደበት ቀንና በሚሞትበትም ቀን፣ ሕያው ሆኖ በሚነሳበትም ቀን ሰላም በእሱ ላይ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,16,'19:16','በመጽሐፉ ውስጥ መርየምንም ከቤተሰቧ ወደ ምሥራቃዊ ስፍራ በተለይች ጊዜ (የሆነውን ታሪኳን) አውሳ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,17,'19:17','ከእነሱም መጋረጃን አደረገች፡፡ መንፈሳችንምም (ጂብሪልን) ወደርሷ ላክን፡፡ ለእርሷም ትክክለኛ ሰው ሆኖ ተመሰለላት፡፡');
INSERT INTO "sadiq_sani" VALUES(19,18,'19:18','«እኔ ከአንተ በአልረሕማን እጠበቃለሁ፡፡ ጌታህን ፈሪ እንደ ሆንክ (አትቅረበኝ)» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(19,19,'19:19','«እኔ ንጹሕን ልጅ ለአንቺ ልሰጥሽ የጌታሽ መልክተኛ ነኝ» አላት፡፡');
INSERT INTO "sadiq_sani" VALUES(19,20,'19:20','«(በጋብቻ) ሰው ያልነካኝ ሆኜ አመንዝራም ሳልሆን ለእኔ እንዴት ልጅ ይኖረኛል!» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(19,21,'19:21','አላት «(ነገሩ) እንደዚህሽ ነው፡፡» ጌታሽ «እርሱ በእኔ ላይ ገር ነው፡፡ ለሰዎችም ታምር ከእኛም ችሮታ ልናደርገው (ይህንን ሠራን) የተፈረደም ነገር ነው» አለ፤ (ነፋባትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(19,22,'19:22','ወዲያውኑም አረገዘችው፡፡ በእርሱም (በሆዷ ይዛው) ወደ ሩቅ ስፍራ ገለል አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(19,23,'19:23','ምጡም ወደ ዘንባባይቱ ግንድ አስጠጋት፡፡ «ዋ ምኞቴ! ምነው ከዚህ በፊት በሞትኩ፡፡ ተረስቼም የቀረሁ በሆንኩ» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(19,24,'19:24','ከበታቿም እንዲህ ሲል ጠራት «አትዘኝ፡፡ ጌታሽ ከበታችሽ ትንሽን ወንዝ በእርግጥ አድርጓል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,25,'19:25','«የዘምባባይቱንም ግንድ ወዳንቺ ወዝውዣት ባንቺ ላይ የበሰለን የተምር እሸት ታረግፍልሻለችና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,26,'19:26','«ብይም፣ ጠጭም፣ ተደሰችም፡፡ እኔ ለአልረሕማን ዝምታን ተስያለሁ፤ ዛሬም ሰውን በፍጹም አላነጋግርም» በይ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,27,'19:27','በእርሱም የተሸከመቺው ሆና ወደ ዘመዶቿ መጣች፡፡ «መርየም ሆይ! ከባድን ነገር በእርግጥ ሠራሽ» አሏት፡፡');
INSERT INTO "sadiq_sani" VALUES(19,28,'19:28','«የሃሩን እኅት ሆይ! አባትሽ መጥፎ ሰው አልነበረም፡፡ እናትሽም አመንዝራ አልነበረችም» አሏት፡፡');
INSERT INTO "sadiq_sani" VALUES(19,29,'19:29','ወደርሱም ጠቀሰች፡፡ «በአንቀልባ ያለን ሕጻን እንዴት እናናግራለን!» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,30,'19:30','(ሕፃኑም) አለ «እኔ የአላህ ባሪያ ነኝ፡፡ መጽሐፍን ሰጥቶኛል ነቢይም አድርጎኛል፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,31,'19:31','«በየትም ስፍራ ብሆን ብሩክ አድርጎኛል፡፡ በሕይወትም እስካለሁ ሶላትን በመስገድ ዘካንም በመስጠት አዞኛል፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,32,'19:32','«ለእናቴም ታዛዥ (አድርጎኛል)፡፡ ትዕቢተኛ እምቢተኛም አላደረገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,33,'19:33','«ሰላምም በእኔ ላይ ነው፡፡ በተወለድሁ ቀን፣ በምሞትበትም ቀን፣ ሕያው ኾኜ በምቀሰቀስበትም ቀን፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,34,'19:34','ይህ የመርየም ልጅ ዒሳ ነው፡፡ ያ በርሱ የሚጠራጠሩበት እውነተኛ ቃል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,35,'19:35','ለአላህ ልጅን መያዝ አይገባውም፡፡ (ከጉድለት ሁሉ) ጠራ፡፡ ነገርን በሻ ጊዜ ለርሱ የሚለው «ኹን» ነው ወዲያውም ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,36,'19:36','(ዒሳ አለ) «አላህም ጌታዬ ጌታችሁም ነውና ተገዙት፡፡ ይህ ቀጥተኛ መንገድ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,37,'19:37','ከመካከላቸውም አሕዛቦቹ በእርሱ ነገር ተለያዩ፡፡ ለእነዚያም ለካዱት ሰዎች ከታላቁ ቀን መጋፈጥ ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,38,'19:38','በሚመጡን ቀን ምን ሰሚ ምንስ ተመልካች አደረጋቸው! ግን አመጸኞች ዛሬ (በዚህ ዓለም) በግልጽ ስህተት ውስጥ ናቸው፡፤');
INSERT INTO "sadiq_sani" VALUES(19,39,'19:39','እነሱም (አሁን) በዝንጋቴ ላይ ኾነው ሳሉ እነሱም የማያምኑ ሲኾኑ ነገሩ በሚፈረድበት ጊዜ የቁልጭቱን ቀን አስፈራራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,40,'19:40','እኛ ምድርን በእርሷም ላይ ያለውን ሁሉ እኛ እንወርሳለን፡፡ ወደኛም ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,41,'19:41','በመጽሐፉ ውስጥ ኢብራሂምንም አውሳ፡፡ እርሱ በጣም እውነተኛ ነቢይ ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,42,'19:42','ለአባቱ ባለ ጊዜ (አስታውስ) «አባቴ ሆይ! የማይሰማንና የማያይን ከአንተም ምንንም የማይጠቅምህን (ጣዖት) ለምን ትግገዛለህ');
INSERT INTO "sadiq_sani" VALUES(19,43,'19:43','«አባቴ ሆይ! እኔ ከዕውቀት ያልመጣልህ ነገር በእርግጥ መጥቶልኛልና ተከተለኝ፡፡ ቀጥተኛውን መንገድ እመራሃለሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,44,'19:44','«አባቴ ሆይ! ሰይጣንን አትግገዛ፡፡ ሰይጣን ለአልረሕማን አመጸኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,45,'19:45','«አባቴ ሆይ! እኔ ከአልረሕማን ቅጣት ሊያገኝህ ለሰይጣንም ጓደኛ ልትኾን እፈራለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,46,'19:46','(አባቱም) «ኢብራሂም ሆይ! አንተ አምላኮቼን ትተህ የምትዞር ነህን ባትከለከል በእርግጥ እወግርሃለሁ፡፡ ረዥም ጊዜንም ተወኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,47,'19:47','«ደህና ኹን፡፡ ወደፊት ከጌታዬ ምሕረትን እለምንልሃለሁ፡፡ እርሱ ለኔ በጣም ርኅሩኅ ነውና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,48,'19:48','«እናንተንም ከአላህ ሌላ የምትግገዙትንም እርቃለሁ፡፡ ጌታዬንም እግገዛለሁ፡፡ ጌታዬን በመገዛት የማፍር አለመኾኔን እከጅላለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(19,49,'19:49','እነርሱንም ከአላህ ሌላ የሚግገዙትንም በራቀ ጊዜ ለእርሱ ኢስሐቅንና ያዕቆብን ሰጠነው፡፡ ሁሉንም ነቢይ አደረግንም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,50,'19:50','ለእነሱም ከችሮታችን ሰጠናቸው፡፡ ለእነርሱም ከፍ ያለ ምስጉን ዝናን አደረግንላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,51,'19:51','በመጽሐፉ ውስጥ ሙሳንም አውሳ፡፡ እርሱ ምርጥ ነበርና፡፡ መልክተኛ ነቢይም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(19,52,'19:52','ከጡር ቀኝ ጎን በኩልም ጠራነው፡፡ ያነጋገርነውም ሲኾን አቀረብነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,53,'19:53','ከችሮታችንም ወንድሙን ሃሩንን ነቢይ አድርገን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,54,'19:54','በመጽሐፉ ኢስማዒልንም አውሳ፡፡ እርሱ ቀጠሮን አክባሪ ነበረና፡፡ መልክተኛ ነቢይም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(19,55,'19:55','ቤተሰቦቹንም በሶላትና በዘካ ያዝ ነበር፡፡ እጌታውም ዘንድ ተወዳጅ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(19,56,'19:56','በመጽሐፉ ኢድሪስንም (ሄኖክን) አውሳ፡፡ እርሱ እውነተኛ ነቢይ ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,57,'19:57','ወደ ከፍተኛም ስፍራ አነሳነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,58,'19:58','እነዚህ (ዐሥሩ) እነዚያ አላህ በእነሱ ላይ የለገሰላቸው ከነቢያት ከአዳም ዘር ከኑሕ ጋር (በመርከቢቱ ላይ) ከጫናቸውም (ዘሮች) ከኢብራሂምና ከእስራኤልም ዘሮች ከመራናቸውና ከመረጥናቸውም የኾኑት የአልረሕማን አንቀጾች በነሱ ላይ በተነበቡ ጊዜ ሰጋጆችና አልቃሾች ኾነው ይወድቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,59,'19:59','ከእነሱም በኋላ ሶላትን ያጓደሉ (የተዉ) ፍላጎቶችንም የተከተሉ መጥፎ ምትኮች ተተኩ! የገሀነምንም ሸለቆ በእርግጥ ያገኛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,60,'19:60','ግን የተጸጸተና ያመነም በጎንም ሥራ የሠራ ሰው እነዚህ ገነትን ይገባሉ፡፡ አንዳችንም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,61,'19:61','የመኖሪያን ገነቶች ያችን አልረሕማን ለባሮቹ በሩቅ ኾነው ሳሉ ተስፋ ቃል የገባላቸውን (ይገባሉ)፡፡ እርሱ ተስፋው ተፈጻሚ ነውና፤');
INSERT INTO "sadiq_sani" VALUES(19,62,'19:62','በእርሷ ሰላምን እንጂ ውድቅን ነገር አይሰሙም፡፡ ለእነሱም በርሷ ውስጥ ጧትም ማታም ሲሳያቸው አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,63,'19:63','ይህች ያቺ ከባሮቻችን ጥንቁቆች ለኾኑት የምናወርሳት ገነት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(19,64,'19:64','(ጂብሪል አለ) «በጌታህም ትእዛዝ እንጅ አንወርድም፡፡ በፊታችን ያለው፣ በኋላችንም ያለው በዚህም መካከል ያለው ሁሉ የርሱ ነው፡፡ ጌታህም ረሺ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,65,'19:65','(እርሱ) የሰማያትና የምድር በሁለቱም መካከል ላለው ሁሉ ጌታ ነውና ተገዛው፡፡ እርሱን በመገዛትም ላይ ታገስ፡፡ ለእርሱ ሞክሼን ታውቃለህን');
INSERT INTO "sadiq_sani" VALUES(19,66,'19:66','ሰውም «በሞትኩ ጊዜ ወደፊት ሕያው ኾኜ (ከመቃብር) እወጣለሁን» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,67,'19:67','ሰው ከአሁን በፊት ምንም ነገር ያልነበረ ሲኾን እኛ የፈጠርነው መኾኑን አያስታውስምን');
INSERT INTO "sadiq_sani" VALUES(19,68,'19:68','በጌታህ እንምላለን ሰይጣናት ጋር በእርግጥ እንሰበስባቸዋለን፡፡ ከዚያም በገሀነም ዙሪያ የተንበረከኩ ኾነው፤ በእርግጥ እናቀርባቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,69,'19:69','ከዚያም ከየጭፍሮቹ ሁሉ ከእነርሱ ያንን (እርሱ) በአልረሕማን ላይ በድፍረት በጣም ብርቱ የኾነውን እናወጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,70,'19:70','ከዚያም እኛ እነዚያን እነርሱ በእርሷ ለመግባት ተገቢ የኾኑትን እናውቃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,71,'19:71','ከእናንተም ወደ እርሷ ወራጅ እንጂ አንድም የለም፡፡ (መውረዱም) ጌታህ የፈረደው ግዴታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,72,'19:72','ከዚያም እነዚያን የተጠነቀቁትን እናድናለን፡፡ አመጸኞቹንም የተንበረከኩ ኾነው በውስጧ እንተዋቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,73,'19:73','በእነሱም ላይ አንቀጾቻችን ግልጽ መስረጃዎች ኾነው በተነበቡ ጊዜ እነዚያ የካዱት ለእነዚያ ላመኑት «ከሁለቱ ክፍሎች መኖሪያው የሚበልጠውና ሸንጎውም ይበልጥ የሚያምረው ማንኛው ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,74,'19:74','ከእነሱም በፊት ከክፍለ ዘመናት ሕዝቦች እነሱ በቁሳቁስና በትርኢትም በጣም ያማሩትን ብዙዎችን አጥፍተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,75,'19:75','«በስሕተት ውስጥ የኾነ ሰው አልረሕማን ለእርሱ ማዘግየትን ያዘገየዋል፡፡ የሚዛትባቸውንም ወይም ቅጣቱን ወይም ሰዓቲቱን ባዩ ጊዜ እርሱ ስፍራው መጥፎና ሰራዊቱ ደካማ የኾነው ሰው ማን እንደኾነ በእርግጥ ያውቃሉ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,76,'19:76','እነዚያንም የቀኑትን ሰዎች አላህ ቅንነትን ይጨምርላቸዋል፡፡ መልካሞቹ ቀሪዎች (ሥራዎች) እጌታህ ዘንድ በምንዳ በላጭ ናቸው፡፡ በመመለሻም የተሻሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,77,'19:77','ያንንም በአንቀጾቻችን የካደውን «(በትንሣኤ ቀን) ገንዘብም ልጅም በእርግጥ እስሰጣለሁ ያለውንም አየህን»');
INSERT INTO "sadiq_sani" VALUES(19,78,'19:78','ሩቁን ምስጢር ዐወቀን ወይስ አልረሕማን ዘንድ ቃል ኪዳንን ያዘ');
INSERT INTO "sadiq_sani" VALUES(19,79,'19:79','ይከልከል (አይሰጠውም)፡፡ የሚለውን ሁሉ በእርግጥ እንጽፋለን፡፡ ለእርሱም ከቅጣት ጭማሬን እንጨምርለታለን፡፡');
INSERT INTO "sadiq_sani" VALUES(19,80,'19:80','(አልለኝ) የሚለውንም ሁሉ እንወርሰዋለን፡፡ ብቻውንም ኾኖ ይመጣናል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,81,'19:81','ከአላህም ሌላ አማልክትን ለእነሱ መከበሪያ (አማላጅ) እንዲኾኑዋቸው ያዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,82,'19:82','ይከልከሉ፤ መገዛታቸውን በእርግጥ ይክዷቸዋል፡፡ በእነሱም ላይ ተቃራኒ ይኾኑባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,83,'19:83','እኛ ሰይጣናትን በከሓዲዎች ላይ (በመጥፎ ሥራ) ማወባራትን የሚያወባሩዋቸው ሲኾኑ የላክን መኾናችንን አለየህምን');
INSERT INTO "sadiq_sani" VALUES(19,84,'19:84','በእነሱም (መቀጣት) ላይ አትቻኮል፡፡ ለእነሱ (ቀንን) መቁጠርን እንቆጥርላቸዋለንና፡፡');
INSERT INTO "sadiq_sani" VALUES(19,85,'19:85','ምእምናንን የተከበሩ ጭፍሮች ኾነው ወደ አልረሕማን የምንሰበስብበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(19,86,'19:86','ከሓዲዎችንም የተጠሙ ኾነው ወደ ገሀነም የምንነዳበትን (ቀን አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(19,87,'19:87','አልረሕማን ዘንድ ቃል ኪዳንን የያዘ ሰው ቢኾን እንጂ ማማለድን አይችሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,88,'19:88','«አልረሕማንም ልጅን ያዘ (ወለደ)» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,89,'19:89','ከባድ መጥፎን ነገር በእርግጥ አመጣችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,90,'19:90','ከእርሱ (ከንግግራቸው) ሰማያት ሊቀደዱ፣ ምድርም ልትሰነጠቅ፣ ጋራዎችም ተንደው ሊወድቁ ይቃረባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,91,'19:91','ለአልረሕማን ልጅ አልለው ስለአሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(19,92,'19:92','ለአልረሕማን ልጅን መያዝ አይገባውም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,93,'19:93','በሰማያትና በምድር ያለው ሁሉ (በትንሣኤ ቀን) ለአልራሕማን ባሪያ ኾነው የሚመጡ እንጂ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(19,94,'19:94','በእርግጥ (በዕውቀቱ) ከቧቸዋል፡፡ መቁጠርንም ቆጥሯቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,95,'19:95','ሁሉም በትንሣኤ ቀን ለየብቻ ኾነው ወደርሱ መጪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,96,'19:96','እነዚያ ያመኑና በጎ ሥራዎችን የሠሩ አልረሕማን ለእነሱ ውዴታን ይሰጣቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(19,97,'19:97','በምላስህም (ቁርኣንን) ያገራነው በእርሱ ጥንቁቆቹን ልታበስርበት በእርሱም ተከራካሪዎችን ሕዝቦች ልታስፈራራበት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(19,98,'19:98','ከእነሱም በፊት ከክፍለ ዘመናት ሕዝቦች ብዙዎችን አጥፍተናል፡፡ ከእነሱ አንድን እንኳ ታያለህን ወይስ ለእነሱ ሹክሹክታን ትሰማለህን');
INSERT INTO "sadiq_sani" VALUES(20,1,'20:1','ጠ.ሀ. (ጣ ሃ)');
INSERT INTO "sadiq_sani" VALUES(20,2,'20:2','ቁርኣንን ባንተ ላይ እንድትቸገር አላወረድንም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,3,'20:3','ግን አላህን ለሚፈራ ሰው መገሰጫ ይኾን ዘንድ (አወረድነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,4,'20:4','ምድርንና የላይኛዎቹን ሰማያት ከፈጠረ አምላክ ተወረደ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,5,'20:5','(እርሱ) አልረሕማን ነው በዐርሹ ላይ (ለሱ ክብር በሚስማማ መልኩ) ተደላደለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,6,'20:6','በሰማያት ያለው፣ በምድርም ያለው፣ በመካከላቸውም ያለው፣ ከዐፈር በታችም ያለው ሁሉ የርሱ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,7,'20:7','በንግግር ብትጮህ (አላህ ከጩኸቱ የተብቃቃ ነው)፡፡ እርሱ ምስጢርን በጣም የተደበቀንም ያውቃልና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,8,'20:8','አላህ ከእርሱ ሌላ አምላክ የለም፡፡ ለእርሱ መልካሞች የኾኑ ስሞች አልሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,9,'20:9','የሙሳም ወሬ በእርግጥ መጥቶሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,10,'20:10','እሳትን ባየና ለቤተሰቦቹ፡- (እዚህ) «ቆዩ፤ እኔ እሳትን አየሁ፡፡ ከእርሷ ችቦን ላመጣላችሁ፤ ወይም እሳቲቱ ዘንድ መሪን ላገኝ እከጅላለሁ» ባለጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,11,'20:11','በመጣትም ጊዜ «ሙሳ ሆይ» በማለት ተጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,12,'20:12','«እኔ ጌታህ እኔ ነኝ መጫሚያዎችህንም አውልቅ፡፡ አንተ በተቀደሰው ሸለቆ በጡዋ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,13,'20:13','«እኔም መረጥኩህ፤ የሚወረድልህንም ነገር አዳምጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,14,'20:14','«እኔ አላህ እኔ ነኝ ያለ እኔ አምላክ የለምና ተገዛኝ፡፡ ሶላትንም (በእርሷ) እኔን ለማውሳት ስገድ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,15,'20:15','«ሰዓቲቱ በእርግጥ መጪናት፡፡ ልደብቃት እቃረባለሁ፡፡ ነፍስ ሁሉ በምትሠራው ነገር ትመነዳ ዘንድ (መጭ ናት)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,16,'20:16','«በእርሷ የማያምነውና ዝንባሌውን የተከተለውም ሰው ከእርሷ አያግድህ ትጠፋለህና፡፤');
INSERT INTO "sadiq_sani" VALUES(20,17,'20:17','«ሙሳ ሆይ! ይህችም በቀኝ እጅህ ያለችው ምንድን ናት» (ተባለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,18,'20:18','«እርሷ በትሬ ናት፡፡ በእርሷ ላይ እደገፍባታለሁ፣ በእርሷም ለፍየሎቼ ቅጠልን አረግፍባታለሁ፣ ለእኔም በእርሷ ሌሎች ጉዳዮች አሉኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,19,'20:19','(አላህም) «ሙሳ ሆይ! ጣላት» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,20,'20:20','ጣላትም፡፡ ወዲያውም እርሷ የምትሮጥ እባብ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(20,21,'20:21','«ያዛት፤ አትፍራም፡፡ ወደ መጀመሪያ ጠባይዋ እንመልሳታለን» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,22,'20:22','«እጅህንም ወደ ብብትህ አግባ፡፡ ሌላ ተዓምር ስትኾን ያለነውር ነጭ ኾና ትወጣለችና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,23,'20:23','«ከተዓምራቶቻችን ታላቋን እናሳይህ ዘንድ (ይህን ሠራን)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,24,'20:24','«ወደ ፈርዖን ኺድ፡፡ እርሱ ወሰን አልፏልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,25,'20:25','(ሙሳም) አለ «ጌታዬ ሆይ! ልቤን አስፋልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,26,'20:26','«ነገሬንም ለእኔ አግራልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,27,'20:27','«ከምላሴም መኮላተፍን ፍታልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,28,'20:28','«ንግግሬን ያውቃሉና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,29,'20:29','«ከቤተሰቦቼም ለእኔ ረዳትን አድርግልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,30,'20:30','«ሃሩንን ወንድሜን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,31,'20:31','«ኅይሌን በእርሱ አበርታልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,32,'20:32','«በነገሬም አጋራው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,33,'20:33','«በብዙ እናጠራህ ዘንድ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,34,'20:34','«በብዙም እንድናወሳህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,35,'20:35','«አንተ በእኛ ነገር ዐዋቂ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,36,'20:36','(አላህም) አለ «ሙሳ ሆይ! ልመናህን በእርግጥ ተሰጠህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,37,'20:37','«በሌላም ጊዜ ባንተ ላይ በእርግጥ ለግሰናል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,38,'20:38','«ወደናትህ በልብ የሚፈስን ነገር ባሳወቅን ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,39,'20:39','«(ሕፃኑን) በሳጥኑ ውስጥ ጣይው፡፡ እርሱንም (ሳጥኑን) በባሕር ላይ ጣይው፡፡ ባሕሩም በዳርቻው ይጣለው፡፡ ለእኔ ጠላት ለእርሱም ጠላት የኾነ ሰው ይይዘዋልና በማለት (ባሳወቅን ጊዜ ለገስንልህ)፡፡ ባንተ ላይም ከእኔ የኾነን መወደድ ጣልኩብህ፡፡ (ልትወደድና) በእኔም ጥበቃ ታድግ ዘንድ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,40,'20:40','«እኅትህም በምትኼድና የሚያሳድገውን ሰው ላመላክታችሁን በምትል ጊዜ (መወደድን ጣልኩብህ)፡፡ ወደ እናትህም ዓይንዋ እንዲረጋ (እርሷ) እንዳታዝንም መለስንህ፡፡ ነፍስንም ገደልክ፡፡ ከጭንቅም አዳንንህ፡፡ ፈተናዎችንም ፈተንህ፡፡ በመድየን ቤተሰቦችም ውስጥ (ብዙ) ዓመታትን ተቀመጥክ፡፡ ከዚያም ሙሳ ሆይ! በተወሰነ ጊዜ ላይ መጣህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,41,'20:41','«ለነፍሴም (በመልእክቴ) መረጥኩህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,42,'20:42','«አንተም ወንድምህም በተዓምራቶቼ ኺዱ፡፡ እኔንም ከማውሳት አትቦዝኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,43,'20:43','«ወደ ፈርዖን ኺዱ፡፡ እርሱ ወሰን አልፏልና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,44,'20:44','«እርሱም ይገሰጽ ወይም ይፈራ ዘንድ፤ ለእርሱ ልዝብን ቃል ተናገሩት፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,45,'20:45','«ጌታችን ሆይ! እኛ በእኛ ላይ ክፋት በመሥራት መቸኮሉን ወይም ኩራትን መጨመሩን እንፈራለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,46,'20:46','(አላህም) አለ «አትፍሩ፡፡ እኔ በእርግጥ ከእናንተ ጋር ነኝና፡፡ እሰማለሁ፤ አያለሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,47,'20:47','«ወደርሱም ኺዱ፤ በሉትም፡- ‹እኛ የጌታህ መልክተኞች ነን፡፡› የእስራኤልንም ልጆች ከኛ ጋር ልቀቅ፡፡ አታሰቃያቸውም፡፡ ከጌታህ በኾነው ተዓምር በእርግጥ መጥተንሃልና፡፡ ሰላምም ቀጥታን በተከተለ ሰው ላይ ይኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,48,'20:48','«እኛ ቅጣቱ ባስተባበለና እምቢ ባለ ሰው ላይ ነው ማለት በእርግጥ ተወረደልን፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,49,'20:49','(ፈርዖንም) «ሙሳ ሆይ! ጌታችሁ ማነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,50,'20:50','«ጌታችን ያ ለፍጥረቱ ነገርን ሁሉ (የሚያስፈልገውን) የሰጠ ከዚያም የመራው ነው» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,51,'20:51','(ፈርዖንም) «የመጀመሪያይቱ ዘመናት ሕዝቦች ኹኔታ ምንድን ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,52,'20:52','(ሙሳም) «ዕውቀትዋ እጌታዬ ዘንድ በመጽሐፍ የተመዘገበ ነው፡፡ ጌታዬ አይሳሳትም አይረሳምም» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,53,'20:53','(እርሱ) ያ ምድርን ለእናንተ ምንጣፍ ያደረገላችሁ፣ በእርሷም ውስጥ ለእናንተ መንገድን ያገራላችሁ፣ ውሃንም ከሰማይ ያወረደ ነው (አለ)፡፡ በርሱም ከተለያየ በቃይ ዓይነቶችን አወጣንላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,54,'20:54','ብሉ እንስሳዎቻችሁንም አግጡ፤ (ባዮች ኾነን አወጣንላችሁ)፡፡ በዚህ ውስጥ ለአእምሮ ባለቤቶች በእርግጥ ተዓምራት አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,55,'20:55','ከእርሷ (ከምድር) ፈጠርናችሁ፡፡ በእርሷም ውስጥ እንመልሳችኋለን፡፡ ከእርሷም በሌላ ጊዜ እናወጣችኋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,56,'20:56','ተዓምራቶቻችንንም ሁሏንም (ለፈርዖን) በእርግጥ አሳየነው፡፡ አስተባበለም፡፡ እምቢም አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,57,'20:57','«ከምድራችን በድግምትህ ልታወጣን መጣህብን ሙሳ ሆይ!» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,58,'20:58','«መሰሉንም ድግምት እናመጣብሃለን፡፡ በእኛና ባንተም መካከል እኛም አንተም የማንጥሰው የኾነ ቀጠሮን በመካከለኛ ስፍራ አድርግልን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,59,'20:59','«ቀጠሯችሁ በማጌጫው ቀን ሰዎቹም በረፋድ በሚሰበሰቡበት ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,60,'20:60','ፈርዖንም ዞረ፡፡ ተንኮሉንም ሰበሰበ፡፡ ከዚያም መጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,61,'20:61','ሙሳ ለእነሱ አላቸው «ወዮላችሁ በአላህ ላይ ውሸትን አትቅጠፉ፡፡ በቅጣት ያጠፋችኋልና፡፡ የቀጠፈም ሰው በእርግጥ አፈረ፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,62,'20:62','(ድግምተኞቹ) በመካከላቸውም ነገራቸውን ተጨቃጨቁ፡፡ ውይይትንም ደበቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,63,'20:63','«እነዚህ በእርግጥ ደጋሚዎች ናቸው፡፡ ከምድራችሁ በድግምታቸው ሊያወጧችሁ በላጪቱንም መንገዳችሁን ሊያስወግዱባችሁ ይሻሉ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,64,'20:64','«ተንኮላችሁንም አጠንክሩ፡፡ ከዚያም ተሰልፋችሁ ኑ፡፡ ዛሬ ያሸነፈም ሰው ምኞቱን አገኘ» (ተባባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,65,'20:65','«ሙሳ ሆይ! ወይ ትጥላለህ ወይም በመጀመሪያ የምንጥል እንኾናለን» (ምረጥ) አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,66,'20:66','«አይደለም ጣሉ» አላቸው፡፡ ወዲያውም ገመዶቻቸውና ዘንጎቻቸው ከድግምታቸው የተነሳ እነርሱ የሚሮጡ (እባቦች) ኾነው ወደርሱ ተመለሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,67,'20:67','ሙሳም በነፍሱ ውስጥ ፍርሃትን አሳደረ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,68,'20:68','«አትፍራ አንተ የበላዩ አንተ ነህና» አልነው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,69,'20:69','«በቀኝ እጅህ ያለቸውንም በትር ጣል፡፡ ያንን የሠሩትን ትውጣለችና፡፡ ያ የሠሩት ሁሉ የድግምተኛ ተንኮል ነውና፡፡ ድግምተኛም በመጣበት ስፍራ ሁሉ አይቀናውም» (አልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,70,'20:70','ድግምተኞቹም ሰጋጆች ኾነው ወደቁ፡፡ «በሃሩንና በሙሳ ጌታ አመንን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,71,'20:71','(ፈርዖንም) «ለእናንተ ሳልፈቅድላችሁ በፊት ለእርሱ አመናችሁን እርሱ በእርግጥ ያ ድግምትን ያስተማራችሁ ትልቃችሁ ነው፡፡ እጆቻችሁንና እግሮቻችሁን በማናጋት (ግራናቀኝን በማፈራረቅ) እቆራርጣችኋለሁ፡፡ በዘምባባም ግንዶች ለይ እሰቅላችኋለሁ፡፡ ማንኛችንም ቅጣቱ በጣም ብርቱ የሚቆይም መኾኑን ታውቃላችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,72,'20:72','ከመጡልን ታምራቶች ከዚያም ከፈጠረን(አምላክ) ፈጽሞ አንመርጥህም፤ አንተም የምትፈርደውን ፍረድ፤ የምትፈርደው በዚች በአነስተኛይቱ ሕይወት ብቻ ነው አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,73,'20:73','«ኃጢአቶቻችንንና ከድግምትም በእርሱ ላይ ያስገደድከንን ለእኛ ይምረን ዘንድ እኛ በጌታችን አምነናል፡፡ አላህም በጣም በላጭ ነው፤ (ቅጣቱም) በጣም የሚዘወትር ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,74,'20:74','እነሆ ከሓዲ ኾኖ ወደ ጌታው የሚመጣ ሰው ለእርሱ ገሀነም አለችው፡፡ በውስጧም አይሞትም ሕያውም አይኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,75,'20:75','በጎ ሥራዎችን በእርግጥ የሠራ ምእመን ኾኖ የመጣውም ሰው እነዚያ ለእነሱ ከፍተኛ ደረጃዎች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,76,'20:76','ከሥሮቻቸው ወንዞች የሚፈሱባቸው የመኖሪያ ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ (አሏቸው)፡፡ ይህም የተጥራራ ሰው ምንዳ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,77,'20:77','ወደ ሙሳም «ባሮቼን ይዘህ በሌሊት ኺድ፡፡ ለእነሱም በባሕር ውስጥ ደረቅ መንገድን አድርግላቸው፡፡ መገኘትን አትፍራ፡፡ (ከመስጠም) አትጨነቅም፡፡» ስንል በእርግጥ ላክንበት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,78,'20:78','ፈርዖንም ከሰራዊቱ ጋር ኾኖ ተከተላቸው፡፡ ከባህሩም የሚሸፍን ሸፈናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,79,'20:79','ፈርዖንም ሕዝቦቹን አሳሳታቸው፡፡ ቅኑንም መንገድ አልመራቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,80,'20:80','የእስራኤል ልጆች ሆይ! ከጠላታችሁ በእርግጥ አዳንናችሁ፡፡ በጡርም ቀኝ ጎን ቀጠሮ አደረግንላችሁ፡፡ በእናንተም ላይ መናንና ድርጭትን አወረድንላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,81,'20:81','ከሰጠናችሁ ሲሳይ፤ ከመልካሙ ብሉ፡፡ በእርሱም ወሰንን አትለፉ፡፡ ቁጣዬ በእናንተ ላይ ይወርድባችኋልና፡፡ በእርሱም ላይ ቁጣዬ የሚወርድበት ሰው በእርግጥ ይጠፋል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,82,'20:82','እኔም ለተጸጸተ፣ ላመነም፣ መልካምንም ለሠራ፣ ከዚያም ለተመራ ሰው በእርግጥ መሓሪ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,83,'20:83','«ሙሳ ሆይ! ከሕዝቦችህ ምን አስቸኮለህም» (ተባለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,84,'20:84','«እነሱ እነዚህ፤ በዱካዬ ላይ ያሉ ናቸው፡፡ ጌታዬ ሆይ! ትወድልንም ዘንድ ወደ አንተ ቸኮልኩ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,85,'20:85','(አላህ) «እኛም ከአንተ በኋላ ሰዎችህን በእርግጥ ፈትተን፡፡ ሳምራዊውም አሳሳታቸው» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,86,'20:86','ሙሳም ወደ ሰዎቹ የተቆጣ ያዘነ ኾኖ ተመለሰ፡፡ «ሕዝቦቼ ሆይ! ጌታችሁ መልካም ተስፋን አልቀጠራችሁምን ቀጠሮው በእናንተ ላይ ረዘመባችሁን» ወይስ በእናንተ ላይ ከጌታችሁ ቅጣት ሊወርድባችሁ ፈለጋችሁና ቀጠሮየን አፈረሳችሁን አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,87,'20:87','«ቀጠሮህን በፈቃዳችን አልጣስንም፡፡ ግን እኛ ከሕዝቦቹ ጌጥ ሸክሞችን ተጫን (በእሳት ላይ) ጣልናትም፡፡ ሳምራዊውም እንደዚሁ ጣለ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,88,'20:88','ለእነሱም አካል የኾነ ጥጃን ለእርሱ መጓጎር ያለውን አወጣላቸው፡፡ (ተከታዮቹ) «ይህ አምላካችሁ የሙሳም አምላክ ነው ግን ረሳው» አሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,89,'20:89','ወደእነሱ ንግግርን የማይመልስ ለእነሱም ጉዳትንና ጥቅምን የማይችል መኾኑን አያዩምን');
INSERT INTO "sadiq_sani" VALUES(20,90,'20:90','ሃሩንም ከዚህ በፊት በእርግጥ አላቸው፡- «ሕዝቦቼ ሆይ! (ይህ) በእርሱ የተሞከራችሁበት ብቻ ነው፡፡ ጌታችሁም አልረሕማን ነው፡፡ ተከተሉኝም፡፡ ትዕዛዜንም ስሙ፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,91,'20:91','«ሙሳ ወደእኛ እስከሚመለስ በእርሱ (መገዛት) ላይ ከመቆየት ፈጽሞ አንወገድም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,92,'20:92','(ሙሳ) አለ፡- «ሃሩን ሆይ! ተሳስተው ባየሃቸው ጊዜ ምን ከለከልህ');
INSERT INTO "sadiq_sani" VALUES(20,93,'20:93','«እኔን ከመከተል ትዕዛዜን ጣስክን»');
INSERT INTO "sadiq_sani" VALUES(20,94,'20:94','«የእናቴ ልጅ ሆይ! ጢሜንም ራሴንም አትያዝ፡፡ እኔ በእስራኤል ልጆች መካከል ለያየህ፤ ቃሌንም አልጠበቅህም ማለትህን ፈራሁ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,95,'20:95','(ሙሳ) «ሳምራዊው ሆይ! ነገርህም ምንድን ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,96,'20:96','ያላዩትን ነገር አየሁ፡፡ ከመልክተኛው (ከፈረሱ ኮቴ) ዱካም ጭብጥ አፈርን ዘገንኩ፡፡ (በቅርጹ ላይ) ጣልኳትም፡፡ እንደዚሁም ነፍሴ ሸለመችልኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,97,'20:97','አለው «ኺድ፤ ላንተም በሕይወትህ (ለአየኸው ሰው ሁሉ) መነካካት የለም ማለት አለህ፡፡ ለአንተም ፈጽሞ የማትጣሰው ቀጠሮ አለህ፡፡ ወደዚያም በእርሱ ላይ ተገዢው ኾነህ ወደ ቆየህበት አምላክህ ተመልከት፡፡ በእርግጥ እናቃጥለዋለን፡፡ ከዚያም በባሕሩ ውስጥ መበተንን እንበትነዋለን፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,98,'20:98','ጌታችሁ ያ ከእርሱ በቀር ሌላ አምላክ የሌለ የኾነው አላህ ብቻ ነው፡፡ እውቀቱ ነገርን ሁሉ አዳረሰ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,99,'20:99','እንደዚሁ በእርግጥ ካለፉት ወሬዎች ባንተ ላይ እንተርካለን፡፡ ከእኛም ዘንድ ቁረኣንን በእርግጥ ሰጠንህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,100,'20:100','ከእርሱ ያፈገፈገ ሰው እርሱ በትንሣኤ ቀን ከባድን ቅጣት ይሸከማል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,101,'20:101','በእርሱ ውስጥ ዘውታሪዎች ኾነው (ይሸከማሉ)፡፡ በትንሣኤም ቀን ለእነሱ የኾነው ሸክም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(20,102,'20:102','በቀንዱ በሚነፋ ቀን (ሸክማቸው ከፋ)፤ ከሓዲዎችንም በዚያ ቀን (ዓይኖቻቸው) ሰማያዊዎች ሆነው እንሰበስባቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,103,'20:103','«ዐሥርን (ቀን) እንጂ አልቆያችሁም» በመባባል በመካከላቸው ይንሾካሾካሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,104,'20:104','በሐሳብ ቀጥተኛው «አንድን ቀን እንጅ አልቆያችሁም» በሚል ጊዜ የሚሉትን ነገር እኛ ዐዋቂዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,105,'20:105','ከጋራዎችም ይጠይቁሃል፤ በላቸው «ጌታዬ መበተንን ይበትናቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,106,'20:106','«ትክክል ሜዳም ኾና ይተዋታል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,107,'20:107','«በርሷ ዝቅታና ከፍታን አታይም፡፡»');
INSERT INTO "sadiq_sani" VALUES(20,108,'20:108','በዚያ ቀን (ለመሰብሰብ) ጠሪውን ለእርሱ መጣመም የሌለውን መከተል ይከተላሉ፡፡ ድምጾችም ለአልረሕማን ጸጥ ይላሉ፡፡ ስለዚህ ሹክሹክታን እንጂ አትሰማም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,109,'20:109','በዚያ ቀን ለእርሱ አልረሕማን የፈቀደለትንና ለእርሱም ቃልን የወደደለትን ሰው ቢኾን እንጅ ምልጃ (አንድንም) አትጠቅምም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,110,'20:110','በስተፊታቸው ያለውን በስተኋላቸውም ያለውን ሁሉ ያውቃል፡፡ በእርሱም ዕውቀትን አያካብቡም፤ (አያውቁም)፡፡');
INSERT INTO "sadiq_sani" VALUES(20,111,'20:111','ፊቶችም ሁሉ ሕያው አስተናባሪ ለኾነው (አላህ) ተዋረዱ፡፡ በደልንም የተሸከመ ሰው በእርግጥ ከሰረ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,112,'20:112','እርሱ አማኝ ኾኖ ከመልካም ሥራዎችም የሠራ ሰው በደልንም መጉደልንም አይፈራም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,113,'20:113','እንደዚሁም ዐረብኛ ቁርኣን ኾኖ አወረድነው፡፡ አላህንም ይፈሩ ዘንድ ወይም ለእነሱ ግሣጼን ያድስላቸው ዘንድ ከዛቻ ደጋግመን በውስጡ ገለጽን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,114,'20:114','እውነተኛው ንጉስ አላህም (ከሓዲዎች ከሚሉት) ላቀ፡፡ ወደ አንተ መወረዱም ከመፈጸሙ በፊት በማንበብ አትቸኩል፡፡ «ጌታዬ ሆይ! ዕውቀትንም ጨምርልኝ» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,115,'20:115','ወደ አደምም ከዚህ በፊት ኪዳንን በእርግጥ አወረድን፡፡ ረሳም፡፡ ለእርሱም ቆራጥነትን አላገኘንለትም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,116,'20:116','ለመላእክትም ለአዳም ስገዱ ባልን ጊዜ (አስታውስ)፡፡ ሰገዱም፤ ኢብሊስ ብቻ ሲቀር እምቢ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,117,'20:117','አልንም «አደም ሆይ! ይህ ላንተም ለሚስትህም በእርግጥ ጠላት ነው፡፡ ስለዚህ ከገነት አያውጣችሁ፤ ትለፋለህና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,118,'20:118','ለአንተ በእርሷ ውስጥ አለመራብና አለመታረዝ አለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(20,119,'20:119','አንተም በርሷ ውስጥ አትጠማም፤ ፀሐይም አትተኮስም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,120,'20:120','ሰይጣንም ወደርሱ ጎተጎተ «አደም ሆይ! በመዘውተሪያ ዛፍ በማይጠፋ ንግሥናም ላይ ላመላክትህን አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,121,'20:121','ከእርሷም በልሉ፡፡ ለእነርሱም ኀፍረተ ገላቸው ተገለጸች፡፡ ከገነትም ቅጠል በላያቸው ላይ ይለጥፉ ጀመር፡፡ አደምም የጌታውን ትእዛዝ (ረስቶ) ጣሰ፡፡ (ከፈለገው መዘውተር) ተሳሳተም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,122,'20:122','ከዚያም ጌታው መረጠው ከእርሱም ጸጸቱን ተቀበለው መራውም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,123,'20:123','አላቸው «ከፊላችሁም ለከፊሉ ጠላት ሲኾን ሁላችሁም ከእርሷ ውረዱ ከእኔም የኾነ መሪ ቢመጣላችሁ መሪየን የተከተለ አይሳሳትም አይቸገርምም፡፡');
INSERT INTO "sadiq_sani" VALUES(20,124,'20:124','«ከግሣጼዬም የዞረ ሰው ለእርሱ ጠባብ ኑሮ አለው፡፡ በትንሣኤም ቀን ዕውር ኾኖ እንቀሰቅሰዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(20,125,'20:125','«ጌታዬ ሆይ! ለምን ዕውር አድርገህ አስነሳኸኝ በእርግጥ የማይ የነበርኩ ስኾን» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,126,'20:126','(ነገሩ) እንደዚሁ ነው፡፡ «ታምራታችን መጣችልህ፡፡ ተውካትም፡፡ እንደዚሁም ዛሬ ትተዋለህ» ይለዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(20,127,'20:127','እንደዚሁም ያጋራንና በጌታው አንቀጾች ያላመነን ሰው እንቀጣዋለን፡፡ የመጨረሻይቱም ዓለም ቅጣት በጣም ብርቱ ሁልጊዜ ዘውታሪም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,128,'20:128','(ቁረይሾች) ከእነሱ በፊት ከክፍለ ዘመናት ሕዝቦች ብዙዎችን ያጠፋን መኾናችን በመኖሪያዎቻቸው የሚኼዱ ኾነው ሳሉ ለእነርሱ አልተገለጸላቸውምን በዚህ ውስጥ ለአእምሮ ባለቤቶች በእርግጥ መልክቶች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,129,'20:129','ከጌታህ ያለፈች ቃልና የተወሰነ ጊዜ ባልነበረ ኖሮ (ቅጣቱ አሁኑኑ) የሚይዛቸው ይኾን ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(20,130,'20:130','በሚሉትም ላይ ታገስ፡፡ ጌታህንም ፀሐይ ከመውጣትዋ በፊት ከመግባትዋም በፊት የምታመሰግን ኾነህ አጥራው፤ (ስገድ)፡፡ ከሌሊት ሰዓቶችም በቀን ጫፎችም አጥራው፡፡ (በሚሰጥህ ምንዳ) ልትወድ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(20,131,'20:131','ዓይኖችህንም ከእነርሱ (ከሰዎቹ) ክፍሎችን በእርሱ ልንፈትናቸው ወደ አጣቀምንበት፤ ወደ ቅርቢቱ ሕይወት ጌጦች አትዘርጋ፡፡ የጌታህ ሲሳይም በጣም በላጭ ዘውታሪም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(20,132,'20:132','ቤተሰብህንም በስግደት እዘዝ፡፡ በእርሷም ላይ ዘውትር፤(ጽና)፡፡ ሲሳይን አንጠይቅህም፡፡ እኛ እንሰጥሃለን፡፡ መልካሚቱ መጨረሻም ለጥንቁቆቹ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(20,133,'20:133','«ከጌታውም በኾነ ተዓምር አይመጣንምን» አሉ በመጀመሪያዎቹ ጽሑፎች ውስጥ ያለችው ማስረጃ አልመጣቻቸውምን');
INSERT INTO "sadiq_sani" VALUES(20,134,'20:134','እኛም ከእርሱ (ከሙሐመድ መላክ) በፊት በቅጣት ባጠፋናቸው ኖሮ «ጌታችን ሆይ! ከመዋረዳችንና ከማፈራችን በፊት አንቀጾችህን እንከተል ዘንድ ወደኛ መልከተኛን አትልክም ነበርን» ባሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(20,135,'20:135','«ሁሉም ተጠባባቂ ነው፡፡ ተጠባበቁም፡፡ የቀጥታውም መንገድ ባለቤቶች እነማን እንደሆኑ እነማንም እንደ ተመሩ ወደ ፊት ታውቃላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,1,'21:1','ለሰዎች እነርሱ በዝንጋቴ ውስጥ (መሰናዳትን) የተው ኾነው ሳሉ ምርመራቸው ቀረበ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,2,'21:2','ከጌታቸው አዲስ የተወረደ ቁርኣን አይመጣላቸውም፡፡ እነሱ የሚያላግጡ ሆነው የሚያደምጡት ቢሆኑ እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,3,'21:3','ልቦቻቸው ዝንጉዎች ኾነው (የሚያዳምጡት ቢኾኑ እንጅ)፡፡ እነዚያም የበደሉት ሰዎች መንሾካሾክን ደበቁ፡፡ «ይህ ብጤያችሁ ሰው እንጅ ሌላ ነውን እናንተም የምታዩ ስትኾኑ ድግምትን ለመቀበል ትመጣላችሁን» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,4,'21:4','(ሙሐመድም) «ጌታዬ ቃልን ሁሉ በሰማይና በምድር ውስጥ ያለ ሲኾን ያውቃል እርሱም ሰሚው ዐዋቂው ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,5,'21:5','«በእውነቱ (ቁርኣኑ) የሕልሞች ቅዠቶች ነው፡፡ ይልቁንም ቀጠፈው እንዲያውም እርሱ ቅኔን ገጣሚ ነው፡፡ የቀድሞዎቹም እንደተላኩ (መልክተኛ ከኾነ) በተዓምር ይምጣብን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,6,'21:6','ከእነሱ በፊት ያጠፋናት ከተማ አላመነችም፡፡ ታዲያ እነሱ ያምናሉን');
INSERT INTO "sadiq_sani" VALUES(21,7,'21:7','ከአንተም በፊት ወደእነሱ የምናወርድላቸው የኾነን ሰዎችን እንጂ ሌላን አላክንም፡፡ የማታውቁም ብትኾኑ የመጽሐፉን ባለቤቶች ጠይቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,8,'21:8','ምግብን የማይበሉ አካልም አላደረግናቸውም፡፡ ዘውታሪዎችም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,9,'21:9','ከዚያም ቀጠሮን ሞላንላቸው፡፡ አዳንናቸውም፡፡ የምንሻውንም ሰው (አዳን)፡፡ ወሰን አላፊዎቹንም አጠፋን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,10,'21:10','ክብራችሁ በውስጡ ያለበትን መጽሐፍ ወደእናንተ በእርግጥ አወረድን፡፡ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(21,11,'21:11','በዳይም ከነበረች ከተማ ያጠፋናትና ከኋላዋም ሌሎችን ሕዝቦች ያስገኘነው ብዙ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(21,12,'21:12','ቅጣታችንም በተሰማቸው ጊዜ እነርሱ ወዲያውኑ ከእርሷ (ለመሸሽ) ይገሠግሣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,13,'21:13','አትገሥግሡ፤ ትለመኑም ይኾናልና፡፡ በእርሱ ወደ ተቀማጠላችሁበት ጸጋ ወደ መኖሪያዎቻችሁም ተመለሱ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,14,'21:14','«ዋ ጥፋታችን! እኛ በእርግጥ በዳዮች ነበርን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,15,'21:15','የታጨዱ ሬሳዎችም እስካደረግናቸው ድረስ ይህቺ ጠሪያቸው ከመኾን አልተወገደችም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,16,'21:16','ሰማይንና ምድርን በመካከላቸው ያለውንም ሁሉ ቀላጆች ኾነን አልፈጠርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,17,'21:17','መጫወቻን (ሚስትና ልጅን) ልንይዝ በሻን ኖሮ ከእኛ ዘንድ በያዝነው ነበር፡፡ (ግን) ሠሪዎች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,18,'21:18','በእርግጥ እውነትን በውሸት ላይ እንጥላለን፡፡ አንጎሉን ያፈርሰዋልም፡፡ ወዲያውም እርሱ ጠፊ ነው፡፡ ለእናንተም ከዚያ (ሚስትና ልጅ አለው በማለት) ከምትመጥኑት ነገር ብርቱ ቅጣት አላችሁ፤ (ወዮላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,19,'21:19','በሰማያትና በምድር ያለው ሁሉም የእርሱ ነው፡፡ እርሱ ዘንድ ያሉትም (መላእክት) እርሱን ከመገዛት አይኮሩም፡፡ አይሰለቹምም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,20,'21:20','በሌሊትና በቀንም ያጠሩታል፤ አያርፉም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,21,'21:21','ይልቁንም ከምድር የኾኑን እነርሱ ሙታንን የሚያስነሱን አማልክት ያዙን (የለም)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,22,'21:22','በሁለቱ (በሰማያትና በምድር) ውስጥ ከአላህ ሌላ አማልክት በነበሩ ኖሮ በተበላሹ ነበር፡፡ የዐርሹ ጌታ አላህም ከሚሉት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,23,'21:23','ከሚሠራው ሁሉ አይጠየቅም፡፡ እነርሱ (ፍጥረቶቹ) ግን ይጠየቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,24,'21:24','ይልቁንም ከእርሱ ሌላ አማልክትን ያዙን «አስረጃችሁን አምጡ፡፡ ይህ (ቁርኣን) እኔ ዘንድ ያለው ሕዝብ መገሰጫ ከእኔ በፊትም የነበሩት ሕዝቦች መገሰጫ ነው» በላቸው፡፡ በውነቱ አብዛኞቻቸው እውነቱን አያውቁም፡፡ ስለዚህ እነሱ እምቢተኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,25,'21:25','ከአንተ በፊትም እነሆ ከእኔ ሌላ አምላክ የለምና ተገዙኝ በማለት ወደርሱ የምናወርድለት ቢኾን እንጅ ከመልክተኛ አንድንም አልላክንም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,26,'21:26','«አልረሕማንም (ከመላእክት) ልጅን ያዘ» አሉ፡፡ ጥራት ተገባው፡፡ አይደለም (መላእክት) የተከበሩ ባሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,27,'21:27','በንግግር አይቀድሙትም፤ (ያላለውን አይሉም)፡፡ እነርሱም በትእዛዙ ይሠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,28,'21:28','በፊታቸው ያለውንና በኋላቸው ያለውንም ሁሉ ያውቃል፡፡ ለወደደውም ሰው እንጅ ለሌላው አያማልዱም፡፡ እነርሱም እርሱን ከመፍራታቸው የተነሳ ተጨናቂዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,29,'21:29','ከእነሱም «እኔ ከእርሱ ሌላ አምላክ ነኝ» የሚል ያንን ገሀነምን እንመነዳዋለን፡፡ እንደዚሁ በዳዮችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,30,'21:30','እነዚያም የካዱት ሰማያትና ምድር የተጣበቁ የነበሩና የለያየናቸው መኾናችንን አያውቁምን ሕያው የኾነንም ነገር ሁሉ ከውሃ ፈጠርን፡፡ አያምኑምን');
INSERT INTO "sadiq_sani" VALUES(21,31,'21:31','በምድርም ውስጥ በእነሱ እንዳታረገርግ ጋራዎችን አደረግን፡፡ ይመሩም ዘንድ በእርሷ ውስጥ ሰፋፊ መንገዶችን አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,32,'21:32','ሰማይንም (ከመውደቅ) የተጠበቀ ጣራ አደረግን፡፡ እነርሱም ከተዓምራቶቿ ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,33,'21:33','እርሱም ሌሊትንና ቀንን፣ ፀሐይንና ጨረቃንም የፈጠረ ነው፡፡ ሁሉም በፈለካቸው ውስጥ ይዋኛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,34,'21:34','(ሙሐመድ ሆይ!) ካንተ በፊት ለማንም ሰው ዘለዓለም መኖርን አላደረግንም፡፡ ታዲያ ብትሞት እነሱ ዘውታሪዎች ናቸውን');
INSERT INTO "sadiq_sani" VALUES(21,35,'21:35','ነፍስ ሁሉ ሞትን ቀማሽ ናት፡፡ ለመፈተንም በክፉም በበጎም እንሞክራችኋለን፡፡ ወደኛም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,36,'21:36','እነዚያም የካዱት ሰዎች ባዩህ ጊዜ መሳለቂያ አድርገው እንጂ አይይዙህም፡፡ እነሱ በአልረሕማን መወሳት እነርሱ ከሓዲዎች ሲሆኑ «ያ አማልክቶቻችሁን (በክፉ) የሚያነሳው ይህ ነውን» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,37,'21:37','ሰው ቸኳይ ሆኖ ተፈጠረ፡፡ ተዓምራቶቼን በእርግጥ አሳያችኋለሁና አታቻኩሉኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,38,'21:38','«ይህ ቀጠሮም መቼ ነው እውነተኞች ከኾናችሁ (አምጡት)» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,39,'21:39','እነዚያ የካዱት ከፊቶቻቸውና ከጀርባዎቻቸው ላይ እሳትን የማይከለክሉበትን እነሱም የማይረዳዱበትን ጊዜ ቢያውቁ ኖሮ (ይህንን አይሉም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,40,'21:40','ይልቁንም (ሰዓቲቱ) በድንገት ትመጣቸዋለች፤ ታዋልላቸዋለችም፡፡ መመለሷንም አይችሉም፡፡ እነሱም አይቆዩም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,41,'21:41','ከአንተ በፊትም የነበሩት መልክተኞች በእርግጥ ተቀለደባቸው፡፡ በእነዚያም ከእነሱ ይቀልዱ በነበሩት በርሱ ይሳለቁበት የነበሩት (ቅጣት) ሰፈረባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,42,'21:42','«ከአልረሕማን (ቅጣት) በሌሊትና በቀን የሚጠብቃችሁ ማነው» በላቸው፡፡ በእውነቱ እነሱ ከጌታቸው ግሳጼ (ከቁርአን) ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,43,'21:43','ለእነሱ ከእኛ ሌላ የምትከላከልላቸው አማልክት አለቻቸውን ነፍሶቻቸውን መርዳትን አይችሉም፡፡ እነርሱም (ከሓዲዎቹ) ከእኛ አይጠበቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,44,'21:44','በእውነት እነዚህንና አባቶቻቸውን በእነሱ ላይ ዕድሜ እስከ ረዘመባቸው ድረስ አጣቀምናቸው፡፡ (ተታለሉም)፡፡ እኛ ምድርን ከጫፎችዋ የምናጎላት ኾነን ስንመጣባት አያዩምን እነሱ አሸናፊዎች ናቸውን');
INSERT INTO "sadiq_sani" VALUES(21,45,'21:45','«የማስፈራራችሁ በተወረደልኝ ብቻ ነው፡፡ ግን ደንቆሮዎች በሚስፈራሩ ጊዜ ጥሪን አይሰሙም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,46,'21:46','ከጌታህ ቅጣትም ወላፈን ብትነካቸው «ዋ ጥፋታችን! እኛ በዳዮች ነበርን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,47,'21:47','በትንሣኤም ቀን ትክክለኛ ሚዛኖችን እናቆማለን፡፡ ማንኛይቱም ነፍስ ምንንም አትበደልም፡፡ (ሥራው) የሰናፍጭ ቅንጣት ያክል ቢኾንም እርሷን እናመጣታለን፡፡ ተቆጣጣሪዎችም በኛ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,48,'21:48','ለሙሳና ለሃሩንም እውነትንና ውሸትን የሚለይን ብርሃንንም ለጥንቁቆችም መገሰጫን በእርግጥ ሰጠናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,49,'21:49','ለእነዚያ ጌታቸውን በሩቅ ለሚፈሩት እነሱም ከሰዓቲቱ ተጨናቂዎች ለኾኑ (መገሰጫን ሰጠን)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,50,'21:50','ይህም (ቁርኣን) ያወረድነው የኾነ ብሩክ መገሰጫ ነው፡፡ ታዲያ እናንተ ለእርሱ ከሓዲዎች ናችሁን');
INSERT INTO "sadiq_sani" VALUES(21,51,'21:51','ለኢብራሂምም ከዚያ በፊት ቅን መንገዱን በእርግጥ ሰጠነው፡፡ እኛም በእርሱ (ተገቢነት) ዐዋቂዎች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,52,'21:52','ለአባቱና ለሕዝቦቹ «ይህቺ ቅርጻ ቅርጽ ያቺ እናንተ ለእርሷ ተገዢዎች የኾናችሁት ምንድን ናት» ባለ ጊዜ (መራነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,53,'21:53','«አባቶቻችንን ለእርሷ ተገዢዎች ኾነው አገኘን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(21,54,'21:54','«እናንተም አባቶቻችሁም በእርግጥ በግልጽ ስህተት ውስጥ ነበራችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,55,'21:55','«በምሩ መጣህልን ወይንስ አንተ ከሚቀልዱት ነህ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(21,56,'21:56','«አይደለም ጌታችሁ የሰማያትና የምድር ጌታ ያ የፈጠራቸው ነው፡፡ እኔም በዚህ ላይ ከመስካሪዎቹ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,57,'21:57','«በአላህም እምላለሁ ዟሪዎች ኾናችሁ ከሄዳችሁ በኋላ ጣዖቶቻችሁን ተንኮል እሠራባቸዋለሁ» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,58,'21:58','(ዘወር ሲሉ) ስብርብሮችም አደረጋቸው፡፡ ለእነሱ የኾነ አንድ ታላቅ (ጣዖት) ብቻ ሲቀር ወደርሱ ይመለሱ ዘንድ (እርሱን ተወው)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,59,'21:59','«በአማልክቶቻችን ይህንን የሠራ ማነው እርሱ በእርግጥ ከበደለኞች ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,60,'21:60','«ኢብራሂም የሚባል ጎበዝ (በመጥፎ) ሲያነሳቸው ሰምተናል» ተባባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,61,'21:61','«ይመሰክሩበት ዘንድ በሰዎቹ ዓይን (ፊት) ላይ አምጡት» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,62,'21:62','«ኢብራሂም ሆይ! በአማልክቶቻችን ይህንን የሠራህ አንተ ነህን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(21,63,'21:63','«አይደለም ይህ ታላቃቸው ሠራው፡፡ ይናገሩም እንደ ኾነ ጠይቋቸው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,64,'21:64','ወደ ነፍሶቻቸውም ተመለሱ፡፡ «እናንተ (በመጠየቃችሁ) በዳዮቹ እናንተው ናችሁም» ተባባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,65,'21:65','ከዚያም በራሶቻቸው ላይ ተገለበጡ፡፡ «እነዚህ የሚናገሩ አለመኾናቸውን በእርግጥ ዐውቀሃል፤» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,66,'21:66','«ታዲያ ለእናንተ ምንም የማይጠቅማችሁንና የማይጎዳችሁን ነገር ከአላህ ሌላ ትገዛላችሁን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,67,'21:67','«ፎህ! ለእናንተ ከአላህ ሌላ ለምትገዙትም ነገር፤ አታውቁምን» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,68,'21:68','«ሠሪዎች እንደ ኾናችሁ አቃጥሉት፡፡ አማልክቶቻችሁንም እርዱ» አሉ፡፡ (በእሳት ላይ ጣሉትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,69,'21:69','«እሳት ሆይ! በኢብራሂም ላይ ቀዝቃዛ፤ ሰላምም ሁኚ» አልን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,70,'21:70','በእርሱም ተንኮልን አሰቡ፡፡ በጣም ከሳሪዎችም አደረግናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,71,'21:71','እርሱንና ሉጥንም ወደዚያች፤ በውስጧ ለዓለማት በረከትን ወደ አደረግንባት ምድር (በመውሰድ) አዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,72,'21:72','ለእርሱም ኢስሐቅን ያዕቆብንም ተጨማሪ አድርገን ሰጠነው፡፡ ሁሉንም መልካሞች አደረግን፡፤');
INSERT INTO "sadiq_sani" VALUES(21,73,'21:73','በትዕዛዛችንም ወደ በጎ ሥራ የሚመሩ መሪዎች አደረግናቸው፡፡ ወደእነሱም መልካም ሥራዎችን መሥራትን፣ ሶላትንም መስገድን፣ ዘካንም መስጠትን አወረድን፡፡ ለእኛ ተገዢዎችም ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,74,'21:74','ሉጥንም ፍርድንና ዕውቀትን ሰጠነው፡፡ ከዚያችም መጥፎ ሥራዎችን ትሠራ ከነበረችው ከተማ አዳንነው፡፡ እነሱ ክፉ ሰዎች አመጸኞች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(21,75,'21:75','በችሮታችንም ውስጥ አገባነው፡፡ እርሱ ከመልካሞቹ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(21,76,'21:76','ኑሕንም ከዚያ በፊት (ጌታውን) በጠራ ጊዜ (አስታውስ)፡፡ ለእርሱም (ጥሪውን) ተቀበልነው፡፡ እርሱንም ቤተሰቦቹንም ከከባድ ጭንቅ አዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,77,'21:77','ከነዚያም በታምራቶቻችን ከአስተባበሉት ሕዝቦች (ተንኮል) ጠበቅነው፡፡ እነሱ ክፉ ሕዝቦች አመጸኞች ነበሩና፡፡ ሁሉንም አሰጠምናቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,78,'21:78','ዳውድንና ሱለይማንንም በአዝመራው ነገር በሚፈርዱ ጊዜ የሕዝቦቹ ፍየሎች ሌሊት በርሱ ውስጥ በተሰማሩ ጊዜ (አስታውስ)፡፡ ፍርዳቸውንም ዐዋቂዎች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,79,'21:79','ለሱለይማንም (ትክክለኛይቱን ፍርድ) አሳወቅናት፡፡ ለሁሉም ጥበብንና ዕውቀትን ሰጠን፡፡ ተራራዎችን ከዳውድ ጋር የሚያወድሱ ሲኾኑ ገራን፡፡ አእዋፍንም (እንደዚሁ ገራን)፡፡ ሠሪዎችም ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,80,'21:80','የብረት ልብስንም ሥራ ለእናንተ ከጦራችሁ ትጠብቃችሁ ዘንድ አስተማርነው፡፡ እናንተ አመስጋኞች ናችሁን');
INSERT INTO "sadiq_sani" VALUES(21,81,'21:81','ለሱለይማንም ነፋስን በኀይል የምትነፍስ በትዕዛዙ ወደዚያች በእርሷ ውስጥ በረከትን ወደ አደረግንባት ምድር (ወደ ሻም) የምትፈስ ስትኾን (ገራንለት)፡፡ በነገሩ ሁሉም ዐዋቂዎች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,82,'21:82','ከሰይጣናትም ለእርሱ (ሉልን ለማውጣት) የሚጠልሙንና ከዚያም ሌላ ያለን ሥራ የሚሠሩን (ገራንለት)፡፡ ለእነሱም ተጠባባቂዎች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,83,'21:83','አዩብንም (ኢዮብን) ጌታውን «እኔ መከራ አገኘኝ አንተም ከአዛኞች ሁሉ ይበልጥ አዛኝ ነህ» ሲል በተጣራ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,84,'21:84','ለእርሱም ጥሪውን ተቀበልነው፡፡ ከጉዳትም በእርሱ ላይ የነበረውን ሁሉ አስወገድን፡፡ ቤተሰቦቹንም ከእነሱም ጋር መሰላቸውን ከእኛ ዘንድ ለችሮታና ለተገዢዎች ለማስገንዘብ ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,85,'21:85','ኢስማዒልንም ኢድሪስንም ዙልኪፍልንም (አስታውስ)፡፡ ሁሉም ከታጋሾቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,86,'21:86','ከችሮታችንም ውስጥ አገባናቸው፡፡ እነሱ ከመልካሞቹ ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(21,87,'21:87','የዓሳውንም ባለቤት ተቆጥቶ በኼደ ጊዜ (አስታውስ)፡፡ በእርሱም ላይ ፈጽሞ የማንፈርድበት መኾናችንን ጠረጠረ፡፡ (ዓሳም ዋጠው)፡፡ በጨለማዎችም ውስጥ ኾኖ ከአንተ በቀር ሌላ አምላክ የለም፡፡ ጥራት ይገባህ፡፡ እኔ በእርግጥ ከበዳዮቹ ነበርኩ በማለት ተጣራ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,88,'21:88','ለርሱም ጥሪውን ተቀበልነው፡፡ ከጭንቅም አዳነው፡፡ እንደዚሁም ምእመናንን እናድናለን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,89,'21:89','ዘከሪያንም «ጌታዬ ሆይ! ብቻዬን አትተወኝ፡፡ አንተም ከወራሾች ሁሉ በላጭ ነህ ሲል» ጌታውን በተጣራ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,90,'21:90','ለእርሱም ጥሪውን ተቀበልነው፡፡ ለእርሱም የሕያን ሰጠነው፡፡ ለእርሱም ሚስቱን አበጀንለት፡፡ እነርሱ በበጎ ሥራዎች የሚቻኮሉ ከጃዮችና ፈሪዎች ኾነው የሚለምኑንም ነበሩ፡፡ ለእኛ ተዋራጆችም ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,91,'21:91','ያችንም ብልቷን የጠበቀቺውን በርሷም ውስጥ ከመንፈሳችን የነፋንባትን እርሷንም ልጅዋንም (እንደዚሁ) ለዓለማት ተዓምር ያደረግናትን (መርየምን አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,92,'21:92','ይህች (ሕግጋት) አንዲት መንገድ ስትሆን በእርግጥ ሃይማኖታችሁ ናት፡፡ እኔም ጌታችሁ ነኝና ተገዙኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,93,'21:93','በሃይማኖታቸውም ነገር በመካከላቸው ተለያዩ፡፡ ሁሉም ወደኛ ተመላሾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,94,'21:94','እርሱ ያመነ ኾኖ ከበጎ ሥራዎች ማንኛውንም የሚሠራም ሰው፤ ምንዳውን አይነፈግም፡፡ እኛም ለእርሱ መዝጋቢዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,95,'21:95','ባጠፋናትም ከተማ ላይ እነሱ (ወደኛ) የማይመለሱ መኾናቸው እብለት ነው፤ (ይመለሳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,96,'21:96','የእጁጅና መእጁጅም እነርሱ ከየተረተሩ የሚንደረደሩ ሲኾኑ (ግድባቸው) በተከፈተች ጊዜ፥');
INSERT INTO "sadiq_sani" VALUES(21,97,'21:97','እውነተኛውም ቀጠሮ በቀረበ ጊዜ፥ ያን ጊዜ እነሆ የእነዚያ የካዱት ሰዎች ዓይኖች ይፈጥጣሉ፡፡ «ዋ ጥፋታችን! ከዚህ (ቀን) በእርግጥ በዝንጋቴ ላይ ነበርን፤ በእውነትም በዳዮች ነበርን» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,98,'21:98','እናንተ ከአላህ ሌላ የምትግገዟቸውም (ጣዖታት) የገሀነም ማገዶዎች ናችሁ፡፡ እናንተ ለእርሷ ወራጆች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,99,'21:99','እነዚህ (ጣዖታት) አማልክት በነበሩ ኖሮ አይገቧዋትም ነበር፡፡ ግን ሁሉም በእርሷ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,100,'21:100','ለእነርሱ በእርሷ ውስጥ መንሰቅሰቅ አላቸው፡፡ እነርሱም በውስጧ (ምንንም) አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,101,'21:101','እነዚያ ከእኛ መልካሟ ቃል ለእነርሱ ያለፈችላቸው እነዚያ ከርሷ የተራቁ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,102,'21:102','ድምጽዋን አይሰሙም፡፡ እነርሱም ነፍሶቻቸው በሚሹት ነገር ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,103,'21:103','ታላቁ ድንጋጤ አያስተክዛቸውም፡፡ መላእክትም ይህ ያ ትቀጠሩ የነበራችሁት ቀናችሁ ነው፤ እያሉ ይቀበሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(21,104,'21:104','ለመጽሐፎች የኾኑ ገጾች እንደሚጠቀለሉ ሰማይን የምንጠቀልልበትን ቀን (አስታውስ)፡፡ የመጀመሪያን ፍጥረት እንደ ጀመርን እንመልሰዋለን፡፡ (መፈጸሙ) በእኛ ላይ የኾነን ቀጠሮ ቀጠርን፡፡ እኛ (የቀጠርነውን) ሠሪዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(21,105,'21:105','ምድንም መልካሞቹ ባሮቼ ይወርሷታል ማለትን ከመጽሐፉ (ከተጠበቀው ሰሌዳ) በኋላ በመጽሐፎቹ በእርግጥ ጽፈናል፡፡');
INSERT INTO "sadiq_sani" VALUES(21,106,'21:106','በዚህ (ቁርኣን) ውስጥ ለተገዢዎች ሕዝቦች በቂነት አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(21,107,'21:107','(ሙሐመድ ሆይ!) ለዓለማትም እዝነት አድርገን እንጅ አልላክንህም፡፡');
INSERT INTO "sadiq_sani" VALUES(21,108,'21:108','«ያ ወደኔ የሚወረደው አምላካችሁ አንድ አምላክ ብቻ ነው ማለት ነው፤ ታዲያ እናንተ ፍጹም ታዛዦች ናችሁን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,109,'21:109','እምቢም ቢሉ (በማወቅ) በእኩልነት ላይ ኾነን (የታዘዝኩትን) አስታውቅኋችሁ፡፡ «የምትስፈራሩበትም ነገር ቅርብ ወይም ሩቅ መኾኑን አላውቅም፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(21,110,'21:110','እርሱ ከንግግር ጩኸትን ያውቃል፡፡ የምትደብቁትንም ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(21,111,'21:111','እርሱም (ቅጣትን ማቆየት) ምናልባት ለእናንተ ፈተናና እስከ ጊዜው መጠቃቀሚያ እንደሆነም አላውቅም፤ (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(21,112,'21:112','«ጌታዬ ሆይ! በእውነት ፍረድ፤ ጌታችንም እጅግ በጣም ሩኅሩኅ በምትሉት ነገር ላይ መታገዣ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,1,'22:1','እናንተ ሰዎች ሆይ! ጌታችሁን ፍሩ የሰዓቲቱ እንቅጥቃጤ በጣም ከባድ ነገር ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,2,'22:2','በምታዩዋት ቀን አጥቢ ሁሉ ከአጠባችው ልጅ ትፈዝዛለች፡፡ የእርግዝና ባለቤት የኾነችም ሁሉ እርጉዟን ትጨነግፋለች፡፡ ሰዎቹንም (በድንጋጤ ብርታት) የሰከሩ ኾነው ታያለህ፡፡ እነርሱም (ከመጠጥ) የሰከሩ አይደሉም፡፡ ግን የአላህ ቅጣት ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,3,'22:3','ከሰዎቹም ያለ ዕውቀት በአላህ ነገር የሚከራከር፤ ሞገደኛ ሰይጣንንም ሁሉ የሚከተል ሰው አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,4,'22:4','እነሆ! የተከተለውን ሰው እርሱ ያጠመዋል፡፡ ወደ ነዳጅ እሳት ስቃይም ይመራዋል ማለት በርሱ ላይ ተጽፏል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,5,'22:5','እናንተ ሰዎች ሆይ! ከመቀስቀስ በመጠራጠር ውስጥ እንደ ሆናችሁ (አፈጣጠራችሁን ተመልከቱ)፡፡ እኛም ከዐፈር ፈጠርናችሁ፣ ከዚያም ከፍትወት ጠብታ፣ ከዚያም ከረጋ ደም፣ ከዚያም ከቁራጭ ሥጋ፣ ፍጥረትዋ ሙሉ ከሆነችና ሙሉ ካልሆነች (ችሎታችንን) ለናንተ ልንገልጽላችሁ ፈጠርናችሁ፡፡ የምንሻውንም እስከ ተወሰነ ጊዜ ድረስ በማሕፀን ውስጥ እናረጋዋለን፡፡ ከዚያም ሕፃን ሆናችሁ እናወጣችኋለን፡፡ ከዚያም ሙሉ ጥንካሪያችሁን ትደርሱ ዘንድ (እናሳድጋችኋለን)፡፡ ከእናንተም የሚሞት ሰው አልለ፡፡ ከእናንተም ከዕውቀት በኋላ ምንንም እንዳያውቅ ወደ ወራዳ ዕድሜ የሚመለስ ሰው አልለ፡፡ ምድርንም ደረቅ ሆና ታያታለህ፡፡ በእርሷም ላይ ውሃን ባወረድን ጊዜ ትላወሳለች፡፡ ትነፋፋለችም፡፡ ውበት ካለው ጎሳ ሁሉ ታበቅላለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(22,6,'22:6','ይህ አላህ እርሱ መኖሩ የተረጋገጠ፣ እርሱም ሙታንን ሕያው የሚያደርግ፣ እርሱም በነገሩ ሁሉ ላይ ቻይ በመሆኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,7,'22:7','ሰዓቲቱም መጪ ፣ በእርሷ ፈጽሞ ጥርጣሬ የሌለባት በመሆኗ፣ አላህም በመቃብሮች ውስጥ ያለን ሁሉ የሚቀሰቅስ በመሆኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,8,'22:8','ከሰዎችም ውስጥ ያለ ዕውቀት፣ ያለ ማስረጃም፣ ያለ አብራሪ መጽሐፍም በአላህ ነገር የሚከራከር ሰው አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,9,'22:9','ጎኑን ያጠፈ ሆኖ ከአላህ መንገድ ሊያሳስት (ይከራከራል)፡፡ በቅርቢቱ ዓለም ለእርሱ ውርደት አልለው፡፡ በትንሳኤ ቀንም አቃጣይን ቅጣት እናቀምሰዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(22,10,'22:10','ይህ እጆችህ ባስቀደሙት ኀጢአት አላህም ለባሮቹ ፈጽሞ በዳይ ባለመሆኑ ነው (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,11,'22:11','ከሰዎችም (ከሃይማኖት) በጫፍ ላይ ሆኖ አላህን የሚግገዛ ሰው አልለ፡፡ ታዲያ መልካም ነገር ቢያገኘው በእርሱ ይረጋል፡፡ መከራም ብታገኘው በፊቱ ላይ ይገለበጣል፡፡ የቅርቢቱን ዓለምም የመጨረሻይቱንም ከሰረ፡፡ ይህ እርሱ ግልጽ ከሳራ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,12,'22:12','ከአላህ ሌላ የማይጎዳውንና የማይጠቅመውን ይግገዛል፡፡ ይህ እርሱ (ከእውነት) የራቀ ስህተት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,13,'22:13','ያንን ከጥቅሙ ይልቅ ጉዳቱ የሚቀርብን ይግገዛል፡፡ ረዳቱ ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(22,14,'22:14','አላህ እነዚያን ያመኑትንና መልካም ሥራዎችን የሠሩትን በሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች ያገባቸዋል፡፡ አላህ የሚሻውን ነገር በእርግጥ ይሠራል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,15,'22:15','አላህ (መልክተኛውን) በቅርቢቱም በመጨረሻይቱም ዓለም አይረዳውም ብሎ የሚያስብ ሰው ገመድን ወደ ሰማይ ይዘርጋ፡፡ ከዚያም (ትንፋሹ እስኪቆረጥ) ይታነቅ፡፡ ተንኮሉ የሚያስቆጨውን ነገር ያስወግድለት እንደሆነም ይመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(22,16,'22:16','እንደዚሁም (ቁርኣንን) የተብራሩ አንቀጾች አድርገን አወረድነው፡፡ አላህም የሚሻውን ሰው ይመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,17,'22:17','እነዚያ ያመኑት፣ እነዚያም ይሁዳውያን የሆኑ፣ ሳቢያኖችም፣ ክርስቲያኖችም፣ መጁሶችም እነዚያም (ጣዖታትን በአላህ) ያጋሩ አላህ በትንሣኤ ቀን በመካከላቸው በፍርድ ይለያል፡፡ አላህ በነገሩ ሁሉ ላይ በእርግጥ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,18,'22:18','አላህ በሰማያት ያለው ሁሉ፣ በምድርም ያለው ሁሉ፣ ፀሐይና ጨረቃም፣ ከዋክብትም፣ ተራራዎችም፣ ዛፎችም፣ ተንቀሳቃሾችም ከሰዎች ብዙዎችም፣ ለርሱ የሚሰግዱለት መሆኑን አታውቁምን ብዙውም በርሱ ላይ ቅጣት ተረጋገጠበት፡፡ አላህ የሚያዋርደውም ሰው ለእርሱ ምንም አክባሪ የለውም፡፡ አላህ የሻውን ይሠራልና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,19,'22:19','እነዚህ በጌታቸው የተከራከሩ ሁለት ባላጋራዎች ናቸው፡፡ እነዚያም የካዱት ለእነርሱ የእሳት ልብሶች ተለክተውላቸዋል፡፡ ከራሶቻቸው በላይ የፈላ ውሃ ይምቧቧቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,20,'22:20','በሆዶቻቸው ውስጥ ያሉ ቆዳዎቻቸውም በእርሱ ይቀለጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,21,'22:21','ለእነሱም (መቅጫ) ከብረት የሆኑ መዶሻዎች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,22,'22:22','ከጭንቀት ብርታት የተነሳ ከእርሷ ለመውጣት በፈለጉ ቁጥር ከእርሷ ውስጥ እንዲመለሱ ይደረጋሉ፡፡ «በጣም የሚያቃጥልንም ቅጣት ቅመሱ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,23,'22:23','አላህ እነዚያን ያመኑትንና መልካም ሥራዎችን የሠሩትን ሰዎች በሥሮቻቸው ወንዞች የሚፈስሱባቸውን ገነቶች ያገባቸዋል፡፡ በእርሷ ውስጥ ከወርቅ የሆኑን አንባሮችና ሉልን ይሸለማሉ፡፡ በእርሷ ውስጥ ልብሶቻቸውም ሐር ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,24,'22:24','ከንግግርም ወደ መልካሙ ተመሩ፡፡ ወደ ምስጉንም መንገድ ተመሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,25,'22:25','እነዚያ የካዱ ከአላህም መንገድና ከዚያም ለሰዎች በውስጡ ነዋሪ ለሆኑትም፣ ከሩቅ ለሚመጡትም እኩል ካደረግነው ከተከበረው መስጊድ (ሰዎችን) የሚከለክሉ (አሳማሚን ቅጣት እናቀምሳቸዋለን)፡፡ በእርሱም ውስጥ (ከትክክለኛ መንገድ) በመዘንበል በዳይ ሆኖ (ማንኛውንም ነገር) የሚፈልግ ሰው ከአሳማሚ ቅጣት እናቀምሰዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(22,26,'22:26','ለኢብራሂምም የቤቱን (የካዕባን) ስፍራ መመለሻ ባደረግንለት ጊዜ «በእኔ ምንንም አታጋራ፣ ቤቴንም ለሚዞሩትና ለሚቆሙበት ለሚያጎነብሱትና በግንባራቸውም ለሚደፉት ንጹሕ አድርግላቸው» (ባልነው ጊዜ አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,27,'22:27','(አልነውም)፡- በሰዎችም ውስጥ በሐጅ ትዕዛዝ ጥራ፡፡ እግረኞች ከየሩቅ መንገድ በሚመጡ ከሲታ ግመሎችም ሁሉ ላይ ሆነው ይመጡሃልና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,28,'22:28','ለእነሱ የሆኑ ጥቅሞችን ይጣዱ ዘንድ በታወቁ ቀኖችም ውስጥ ከለማዳ እንስሳዎች በሰጣቸው ሲሳይ ላይ የአላህን ስም ያወሱ ዘንድ (ይመጡሃል)፡፡ ከርሷም ብሉ፡፡ ችግረኛ ድሃንም አብሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,29,'22:29','ከዚያም (እንደ ጠጉርና ጥፍር ያሉትን) ትርፍ አካላቸውንና ዕድፋቸውን ያስወግዱ፡፡ ስለቶቻቸውንም ይሙሉ፤ በጥንታዊውም ቤት ይዙሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,30,'22:30','(ነገሩ) ይህ ነው፡፡ የአላህንም ሕግጋት የሚያከብር ሰው እርሱ ጌታው ዘንድ ለእርሱ በጣም የተሻለ ነው፡፡ የቤት እንስሳትም (ግመል ከብት በግና ፍየል) በናንተ ላይ (እርም መሆኑ) ከሚነበብላችሁ በስተቀር ለእናንተ ተፈቅዳላችኋለች፡፡ ከጣዖታትም ርክሰት ተጠንቀቁ፡፡ ሐሰትንም ቃል ራቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,31,'22:31','ለአላህ ታዛዦች በእርሱ የማታገሩ ሆናችሁ (ከሐሰት ራቁ)፡፡ በአላህም የሚያጋራ ሰው፤ ከሰማይ እንደ ወደቀና በራሪ እንደምትነጥቀው፣ ወይም ነፋስ እርሱን በሩቅ ስፍራ እንደምትጥለው ብጤ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,32,'22:32','(ነገሩ) ይህ ነው፡፡ የአላህንም የሃይማኖት ምልክቶች የሚያከብር ሰው እርሷ ከልቦች የኾነች ጥንቃቄ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(22,33,'22:33','ለእናንተ በእርሷ እስከ ተወሰነ ጊዜ ድረስ ጥቅሞች አሏችሁ፡፡ ከዚያም (የማረጃ) ስፍራዋ እጥንታዊው ቤት አጠገብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,34,'22:34','ለሕዝብም ሁሉ (ወደ አላህ) መስዋዕት ማቅረብን ደነገግን፡፡ ከቤት እንስሳት በሰጣቸው ሲሳይ ላይ የአላህን ስም ብቻ ያወሱ ዘንድ (አዘዝናቸው)፡፡ አምላካችሁም አንድ አምላክ ብቻ ነው፡፡ ለእርሱም ብቻ ታዘዙ፡፡ ለአላህ ተዋራጆችንም አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,35,'22:35','እነዚያን አላህ በተወሳ ጊዜ ልቦቻቸው የሚጨነቁትን፣ በደረሰባቸውም (መከራ) ላይ ታጋሾችን፣ ሶላትንም አስተካክለው ሰጋጆችን፣ ከሰጠናቸውም ሲሳይ የሚለግሱትን (አብስር)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,36,'22:36','ግመሎችንም ለእናንተ ከአላህ ሃይማኖት ምልክቶች አደረግናቸው፡፡ በእርሷ ለእናንተ መልካም ጥቅም አላችሁ፡፡ በእርሷም ላይ (ስታርዷት) በሶስት እግሮችዋ ቆማ የተሰለፈች ሆና የአላህን ስም አውሱ፡፡ ጎኖቻቸውም በወደቁ ጊዜ ከእርሷ ብሉ፡፡ ለማኝንም ለልመና የሚያገዳድምንም አብሉ፡፡ እንደዚሁ ታመሰግኑ ዘንድ ለእናንተ ገራናት፡፡');
INSERT INTO "sadiq_sani" VALUES(22,37,'22:37','አላህን ሥጋዎቿም ደሞቿም ፈጽሞ አይደርሰውም፡፡ ግን ከእናንተ የሆነው ፍራቻ ይደርሰዋል፡፡ እንደዚሁ አላህን በመራችሁ ላይ ልታከብሩት ለእናንተ ገራት፡፡ በጎ ሠሪዎችንም አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(22,38,'22:38','አላህ ከእነዚያ ካመኑት ላይ ይከላከልላቸዋል፡፡ አላህ ከዳተኛን ውለታቢስን ሁሉ አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(22,39,'22:39','ለእነዚያ ለሚገደሉት (ምእመናን) እነርሱ የተበደሉ በመኾናቸው (መጋደል) ተፈቀደላቸው፡፡ አላህም እነርሱን በመርዳት ላይ በእርግጥ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,40,'22:40','ለእነዚያ «ጌታችን አላህ ነው» ከማለታቸው በስተቀር ያለ አግባብ ከአገራቸው የተባረሩ ለሆኑት (ተፈቀደ)፡፡ አላህ ሰዎችን ከፊላቸውን በከፊሉ መገፍተሩ ባልነበረ ኖሮ ገዳማት፣ ቤተክርስቲያኖችም፣ ምኩራቦችም በውስጣቸው የአላህ ስም በብዛት የሚወሳባቸው መስጊዶችም፤ በተፈረሱ ነበር፡፡ አላህም ሃይማኖቱን የሚረዳውን ሰው በእርግጥ ይረዳዋል፡፡ አላህ ብርቱ አሸናፊ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,41,'22:41','(እነርሱም) እነዚያ በምድር ላይ ብናስመቻቸው ሶላትን እንደሚገባ የሚያስተካክሉ፣ ዘካንም የሚሰጡ፣ በደግ ነገርም የሚያዝዙ፣ ከመጥፎ ነገርም የሚከለክሉ ናቸው፡፡ የነገሮቹም ሁሉ መጨረሻ ወደ አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,42,'22:42','ቢያስተባብሉህም ከእነሱ በፊት የኑሕ ሕዝቦች ዓድና ሰሙድም በእርግጥ አስተባብለዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,43,'22:43','የኢብራሂምም ሕዝቦች የሉጥም ሕዝቦች (አስተባብለዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,44,'22:44','የመድየንም ሰዎች (አስተባብለዋል)፡፡ ሙሳም ተስተባብሏል፡፡ ለከሓዲዎቹም ጊዜ ሰጠኋቸው፡፡ ከዚያም ያዝቸው፡፡ ጥላቻዬም እንዴት ነበረ!');
INSERT INTO "sadiq_sani" VALUES(22,45,'22:45','ከከተማም እርሷ በደለኛ ሆና ያጠፋናትና እርሷ በጣሪያዎቿ ላይ ወዳቂ የሆነችው ብዙ ናት፡፡ ከተራቆተችም የውሃ ጉድጓድ ከተገነባም ሕንፃ (ያጠፋነው ብዙ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(22,46,'22:46','ለእነርሱም በእነርሱ የሚያውቁባቸው ልቦች ወይም በእነርሱ የሚሰሙባቸው ጆሮች ይኖሯቸው ዘንድ በምድር ላይ አይሄዱምን እነሆ ዓይኖች አይታወሩም ግን እነዚያ በደረቶች ውስጥ ያሉት ልቦች ይታወራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,47,'22:47','አላህም ቀጠሮውን በፍጹም የማይጥስ ሲሆን በቅጣት ያቻኩሉሃል፡፡ እጌታህም ዘንድ አንዱ ቀን ከምትቆጥሩት (ቀን) እንደ ሺሕ ዓመት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,48,'22:48','ከከተማ እርሷ በዳይ ሆና ለእርሷ ጊዜ የሰጠኋትና ከዚያም የያዝኳት ብዙ ናት፡፡ መመለሻም ወደኔ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,49,'22:49','«እናንተ ሰዎች ሆይ! እኔ ለእናንተ ግልጽ አስጠንቃቂ ብቻ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,50,'22:50','እነዚያም ያመኑ መልካም ሥራዎችንም የሠሩ ለእነርሱ ምህረትና ያማረ ሲሳይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,51,'22:51','እነዚያም የሚያመልጡ መስሏቸው ተዓምራቶቻችንን በመንቀፍ የተጉ እነሱ የእሳት ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,52,'22:52','ከመልክተኛና ከነቢይም ከአንተ በፊት አንድንም አላክንም ባነበበ (እና ዝም ባለ) ጊዜ ሰይጣን በንባቡ ላይ (ማጥመሚያን ቃል) የሚጥል ቢሆን እንጅ፡፡ ወዲያውም አላህ ሰይጣን የሚጥለውን ያስወግዳል፡፡ ከዚያም አላህ አንቀጾቹን ያጠነክራል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,53,'22:53','ሰይጣን የሚጥለውን ነገር ለእነዚያ በልቦቻቸው ውስጥ በሽታ ላለባቸው ልቦቻቸውም ደረቆች ለሆኑት ፈተና ሊያደርግ (ይጥላል)፡፡ በዳዮችም ከእውነት በራቀ ጭቅጭቅ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,54,'22:54','እነዚያም ዕውቀትን የተሰጡት እርሱ (ቁርኣን) ከጌታህ የኾነ እውነት መኾኑን እንዲያውቁና በእርሱ እንዲያምኑ ልቦቻቸውም ለእርሱ እንዲረኩ (ያጠነክራል)፡፡ አላህም እነዚያን ያመኑትን ወደ ቀጥተኛው መንገድ በእርግጥ መሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,55,'22:55','እነዚያ የካዱት ሰዎች ሰዓቲቱ በድንገት እስከምትመጣባቸው ወይም (ከደግ ነገር) መካን የሆነው ቀን ቅጣት እስከሚመጣባቸው ድረስ ከእርሱ (ከቁርኣን) በመጠራጠር ውስጥ ከመሆን አይወገዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(22,56,'22:56','በዚያ ቀን ንግሥናው የአላህ ብቻ ነው፡፡ በመካከላቸው ይፈርዳል፡፡ እነዚያም ያመኑት መልካም ሥራዎችንም የሠሩት በመደሰቻ ገነቶች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,57,'22:57','እነዚያም የካዱት በአንቀጾቻችንም ያስተባበሉት እነዚያ ለእነርሱ አዋራጅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,58,'22:58','እነዚያም በአላህ ሃይማኖት የተሰደዱ ከዚያም የተገደሉ ወይም የሞቱ አላህ መልካምን ሲሳይ በእርግጥ ይሰጣቸዋል፡፡ አላህም እርሱ ከሰጪዎች ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,59,'22:59','የሚወዱትን መግቢያ (ገነትን) በእርግጥ ያገባቸዋል፡፡ አላህም በእርግጥ ዐዋቂ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,60,'22:60','(ነገሩ) ይህ ነው፡፡ በእሱ በተበደለበትም ብጤ የተበቀለ ሰው ከዚያም በእርሱ ላይ ግፍ የተዋለበት አላህ በእርግጥ ይረዳዋል፡፡ አላህ ይቅር ባይ መሓሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,61,'22:61','ይህ አላህ ሌሊትን በቀን ውስጥ የሚያስገባ ቀንንም በሌሊት ውስጥ የሚያስገባ (ቻይ) አላህም ሰሚ ተመልካች በመሆኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,62,'22:62','ይህ አላህ እርሱ እውነት በመሆኑ ከእርሱም ሌላ የሚግገዙት ነገር እርሱ ፍጹም ውሸት በመሆኑ አላህም እርሱ የሁሉ በላይ ታላቅ በመኾኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,63,'22:63','አላህ ከሰማይ ውሃን ማውረዱንና ምድር የምትለመልም መኾኗን አታይምን አላህ ሩኅሩኅ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,64,'22:64','በሰማያት ውስጥ ያለውና በምድር ውስጥ ያለው ሁሉ የርሱ ብቻ ነው፡፡ አላህም እርሱ ተብቃቂው ምስጉኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,65,'22:65','አላህ በምድር ውስጥ ያለን ሁሉ ለእናንተ የገራላችሁ፣ መርከቦችንም በባሕር ውስጥ በፈቃዱ የሚንሻለሉ ሲኾኑ (የገራላችሁ) መኾኑን፣ ሰማይንም በፈቃዱ ካልኾነ በምድር ላይ እንዳትወድቅ የሚይዛት መኾኑን አላየህምን አላህ ለሰዎች በእርግጥ ሩኅሩኅ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,66,'22:66','እርሱም ያ ሕያው ያደረጋችሁ ነው፡፡ ከዚያም ይገድላችኋል፡፡ ከዚያም ሕያው ያደርጋችኋል፡፡ ሰው በእርግጥ በጣም ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,67,'22:67','ለየሕዝቡ ሁሉ እነሱ የሚሠሩበት የኾነን ሥርዓተ ሃይማኖት አድርገናል፡፡ ስለዚህ በነገሩ አይከራከሩህ፡፡ ወደ ጌታህ መንገድም ጥራ፡፡ አንተ በእርግጥ በቅኑ መንገድ ላይ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,68,'22:68','ቢከራከሩህም «አላህ የምትሰሩትን ሁሉ ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,69,'22:69','አላህ በትንሣኤ ቀን በዚያ በእርሱ ትለያዩበት በነበራችሁት ሁሉ በመካከላችሁ ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(22,70,'22:70','አላህ በሰማይና በምድር ውስጥ ያለን ሁሉ በእርግጥ የሚያውቅ መኾኑን አታውቅምን ይህ በመጽሐፍ ውስጥ (የተመዘገበ) ነው፡፡ ይህ በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,71,'22:71','ከአላህ ሌላም በእርሱ ማስረጃን ያላወረደበትን ለእነርሱም በእርሱ ዕውቀት የሌላቸውን ነገር ይግገዛሉ፡፡ ለባዳዮችም ምንም ረዳት የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(22,72,'22:72','አንቀጾቻችንም የተብራሩ ኾነው በእነሱ ላይ በሚነበቡ ጊዜ በእነዚያ በካዱት ሰዎች ፊቶች ላይ ጥላቻን ታውቃለህ፡፡ በእነዚያ በእነሱ ላይ አንቀጾቻችንን በሚያነቡት ላይ በኃይል ሊዘልሉባቸው ይቃረባሉ፡፡ «ከዚህ ይልቅ የከፋን ነገር ልንገራችሁን» በላቸው፡፡ «(እርሱም) እሳት ናት፡፡ አላህ ለእነዚያ ለካዱት ሰዎች ቀጥሯታል፡፡ ምን ትከፋም መመለሻ!»');
INSERT INTO "sadiq_sani" VALUES(22,73,'22:73','እናንተ ሰዎች ሆይ! አስደናቂ ምሳሌ ተገለጸላችሁ፡፡ ለእርሱም አድምጡት፡፡ እነዚያ ከአላህ ሌላ የምትግገዟቸው (ጣዖታት) ዝንብን ፈጽሞ አይፈጥሩም፡፡ እርሱን (ለመፍጠር) ቢሰበሰቡም እንኳን (አይችሉም)፡፡ አንዳችንም ነገር ዝንቡ ቢነጥቃቸው ከእርሱ አያስጥሉትም፡፡ ፈላጊውም ተፈላጊውም ደከሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,74,'22:74','አላህን ተገቢ ክብሩን አላከበሩትም፡፡ አላህ በጣም ኃያል አሸናፊ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,75,'22:75','አላህ ከመላእክት ውስጥ መልክተኞችን ይመርጣል፡፡ ከሰዎችም (እንደዚሁ)፤ አላህ ሰሚ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(22,76,'22:76','በስተፊታቸው ያለን በስተኋላቸውም ያለን ሁሉ ያውቃል፡፡ ወደ አላህም ነገሮች ሁሉ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(22,77,'22:77','እናንተ ያመናችሁ ሰዎች ሆይ! (በስግደታችሁ) አጎንብሱ፣ በግንባራችሁም ተደፉ፣ ጌታችሁንም ተገዙ፣ በጎንም ነገር ሥሩ፣ ልትድኑ ይከጀልላችኋልና፡፡');
INSERT INTO "sadiq_sani" VALUES(22,78,'22:78','በአላህም ሃይማኖት ተገቢውን ትግል ታገሉ፡፡ እርሱ መርጧችኋል፡፡ በናንተም ላይ በሃይማኖቱ ውስጥ ምንም ችግር አላደረገባችሁም፡፡ የአባታችሁን የኢብራሂምን ሃይማኖት ተከተሉ፡፡ እርሱ ከዚህ በፊት ሙስሊሞች ብሎ ሰይሟችኋል፡፡ በዚህም (ቁርኣን)፤ መልክተኛው በእናንተ ላይ መስካሪ እንዲሆን እናንተም በሰዎቹ ላይ መስካሪዎች እንድትኾኑ (ሙስሊሞች ብሎ ሰይሟችኋል)፡፡ ሶላትንም አስተካክላችሁ ስገዱ፣ ዘካንም ስጡ፣ በአላህም ተጠበቁ፣ እርሱ ረዳታችሁ ነው፡፡ (እርሱ) ምን ያምር ጠባቂ! ምን ያምርም ረዳት!');
INSERT INTO "sadiq_sani" VALUES(23,1,'23:1','ምእምናን ፍላጎታቸውን ሁሉ በእርግጥ አገኙ (ዳኑ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,2,'23:2','እነዚያ እነርሱ በስግደታቸው ውስጥ (አላህን) ፈሪዎች፡፡');
INSERT INTO "sadiq_sani" VALUES(23,3,'23:3','እነዚያም እነርሱ ከውድቅ ንግግር ራቂዎች፡፡');
INSERT INTO "sadiq_sani" VALUES(23,4,'23:4','እነዚያም እነርሱ ዘካን ሰጭዎች፡፡');
INSERT INTO "sadiq_sani" VALUES(23,5,'23:5','እነዚያም እነርሱ ብልቶቻቸውን ጠባቂዎች የሆኑት (አገኙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,6,'23:6','በሚስቶቻቸው ወይም እጆቻቸው በያዟቸው (ባሪያዎች) ላይ ሲቀር፡፡ እነርሱ (በነዚህ) የማይወቀሱ ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(23,7,'23:7','ከዚህም ወዲያ የፈለጉ ሰዎች እነዚያ እነርሱ ወሰን አላፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,8,'23:8','እነዚያም እነርሱ ለአደራዎቻቸውና ለቃል ኪዳናቸው ጠባቂዎች፡፡');
INSERT INTO "sadiq_sani" VALUES(23,9,'23:9','እነዚያም እነሱ በስግደቶቻቸው ላይ የሚጠባበቁ የኸኑት (አገኙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,10,'23:10','እነዚያ ወራሾቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,11,'23:11','እነዚያ ፊርደውስን (ላዕላይ ገነትን)፤ የሚወርሱ ናቸው፡፡ እነርሱ በእርሷ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,12,'23:12','በእርግጥም ሰውን ከነጠረ ጭቃ ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,13,'23:13','ከዚያም በተጠበቀ መርጊያ ውስጥ የፍትወት ጠብታ አደረግነው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,14,'23:14','ከዚያም ጠብታዋን (በአርባ ቀን) የረጋ ደም አድርገን ፈጠርን፡፡ የረጋውንም ደም ቁራጭ ሥጋ አድርገን ፈጠርን፡፡ ቁራጯንም ሥጋ አጥንቶች አድርገን ፈጠርን፡፡ አጥንቶቹንም ሥጋን አለበስናቸው፡፡ ከዚያም (ነፍስን በመዝራት) ሌላ ፍጥረትን አድርገን አስገኘነው፡፡ ከሰዓሊዎችም ሁሉ በላጭ የሆነው አላህ ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,15,'23:15','ከዚያም እናንተ ከዚህ በኋላ በእርግጥ ሟቾች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,16,'23:16','ከዚያም እናንተ በትንሣኤ ቀን ትቀሰቀሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,17,'23:17','በእርግጥም ከበላያችሁ ሰባትን ሰማያት ፈጠርን፡፡ ከፍጥረቶቹም ዘንጊዎች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,18,'23:18','ከሰማይም ውሃን በልክ አወረድን፡፡ በምድርም ውስጥ አስቀመጥነው፡፡ እኛም እርሱን በማስወገድ ላይ በእርግጥ ቻዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,19,'23:19','በእርሱም ከዘምባባዎችና ከወይኖች የኾኑን አትክልቶች ለእናንተ አስገኘንላችሁ፡፡ በውስጧ ለእናንተ ብዙ ፍራፍሬዎች አሏችሁ፡፡ ከእርሷም ትበላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,20,'23:20','ከሲና ተራራም የምትወጣን ዛፍ በቅባትና ለበይዎችም መባያ በሚኾን (ዘይት) ተቀላቅላ የምትበቅልን (አስገኘንላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,21,'23:21','ለእናንተም በግመል፣ በከብት፣ በፍየልና በበግ መገምገሚያ አልላችሁ፡፡ በሆዶችዋ ውስጥ ካለው (ወተት) እናጠጣችኋለን፡፡ ለእናንተም በእርሷ ብዙ ጥቅሞች አሏችሁ፡፡ ከእርሷም ትበላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,22,'23:22','በእርሷም ላይ በመርከብም ላይ ትጫናላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,23,'23:23','ኑሕንም ወደ ሕዝቦቹ በእርግጥ ላክነው፡፡ ወዲያውም «ሕዝቦቼ ሆይ! አላህን ተገዙ፡፡ ከእርሱ ሌላ ምንም አምላክ የላችሁም፡፡ አትጠነቀቁምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,24,'23:24','ከሕዝቦቹም እነዚያ ታላላቆቹ ሰዎች አሉ «ይህ ብጤያችሁ ሰው እንጅ ሌላ አይደለም፡፡ በእናንተ ላይ መብለጥን ይፈልጋል፡፡ አላህም በሻ ኖሮ መላእክትን ባወረደ ነበር፡፡ ይህንንም (የሚለውን) በመጀመሪያዎቹ አባቶቻችን አልሰማንም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,25,'23:25','«እርሱ በእርሱ ዕብደት ያለበት ሰው እንጅ ሌላ አይደለም፡፡ በእርሱም እስከ ጊዜ (ሞቱ) ድረስ ተጠባበቁ» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,26,'23:26','(ኑሕም) «ጌታዬ ሆይ! ባስተባበሉኝ ምክንያት እርዳኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,27,'23:27','ወደርሱም (እንዲህ ስንል) ላክን «በተመልካችነታችንና በትእዛዛችንም ታንኳን ሥራ በመጣና እቶኑ በፈለቀ ጊዜ በውስጧ ከሁሉም ሁለት ዓይነቶችን ቤተሰቦችህንም ከነሱ (ጠፊ በመኾን) ቃል ያለፈበት ብቻ ሲቀር፤ አግባ፡፡ በእነዚያም በበደሉት ሰዎች ነገር አታነጋግረኝ፡፡ እነሱ ተሰጣሚዎች ነቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(23,28,'23:28','አንተም አብረውህ ካሉት ጋር በታንኳይቱ ላይ በተደላደልክ ጊዜ «ምስጋና ለዚያ ከበደለኞች ሕዝቦች ላዳነን አላህ ይገባው» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(23,29,'23:29','በልም «ጌታዬ ሆይ! ብሩክ የኾነን ማውረድ አውርደኝ፡፡ አንተም ከአውራጆች ሁሉ በላጭ ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(23,30,'23:30','በዚህ ውስጥ (ለከሃሊነታችን) ምልከቶች አልሉበት፡፡ እነሆ! (የኑሕን ሰዎች) በእርግጥም ፈትታኞች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,31,'23:31','ከዚያም ከኋላቸው ሌሎችን የክፍለ ዘመን ሕዝቦች አስገኘን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,32,'23:32','በውስጣቸውም ከእነሱ የኾነን መልክተኛ አላህን ተገዙ ከእርሱ ሌላ አምላክ የላችሁም አትጠነቀቁምን በማለት ላክን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,33,'23:33','ከሕዝቦቹም እነዚያ የካዱት በመጨረሻይቱም ቀን መገናኘት ያስተባበሉት በቅርቢቱም ሕይወት ያቀማጠልናቸው ታላላቅ ሰዎች «ይህ ብጤያችሁ ሰው እንጂ ሌላ አይደለም፡፡ ከርሱ ከምትበሉት ምግብ ይበላል፡፡ ከምትጠጡትም ውሃ ይጠጣል» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,34,'23:34','«ብጤያችሁም የኾነን ሰው ብትታዘዙ እናንተ ያን ጊዜ በእርግጥ የተሞኛችሁ ናቸሁ» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,35,'23:35','«እናንተ በሞታችሁና ዐፈርና አጥንቶችም በኾናችሁ ጊዜ እናንተ (ከመቃብር) ትወጣላችሁ በማለት ያስፈራራችኋልን');
INSERT INTO "sadiq_sani" VALUES(23,36,'23:36','«ያ የምትስፈራሩበት ነገር ራቀ፤ በጣም ራቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,37,'23:37','«እርሷ (ሕይወት) ቅርቢቱ ሕይወታችን እንጂ ሌላ አይደለችም፡፡ እንሞታለን፤ (ልጆቻችን ስለሚተኩ) ሕያውም እንኾናለን፡፡ እኛም ተቀስቃሾች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,38,'23:38','«እርሱ በአላህ ላይ ውሸትን የቀጠፈ ሰው እንጂ ሌላ አይደለም፡፡ እኛም ለእርሱ አማኞች አይደለንም» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,39,'23:39','(መልክተኛውም) «ጌታዬ ሆይ! ስላስተባበሉኝ እርዳኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,40,'23:40','(አላህም) «ከጥቂት ጊዜ በኋላ (በጥፋታቸው) በእርግጥ ተጸጻቾች ይኾናሉ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,41,'23:41','ወዲያውም (የጥፋት) ጩኸቲቱ በእውነት ያዘቻቸው፡፡ እንደ ጎርፍ ግብስባሽም አደረግናቸው፡፡ ለበደለኞች ሕዝቦችም (ከእዝነት) መራቅ ተገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,42,'23:42','ከዚያም ከእነሱ በኋላ ሌሎችን የክፍለ ዘመናት ሕዝቦች አስገኘን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,43,'23:43','ማንኛይቱም ሕዝብ ጊዜያዋን አትቀድምም፤ አይቅቆዩምም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,44,'23:44','ከዚያም መልክተኞቻችንን የሚከታተሉ ኾነው ላክን፡፡ ማንኛይቱንም ሕዝብ መልክተኛዋ በመጣላት ቁጥር አስተባበሉት፡፡ ከፊላቸውንም በከፊሉ (በጥፋት) አስከታተለን፡፡ ወሬዎችም አደረግናቸው፡፡ ለማያምኑም ሕዝቦች (ከእዝነታችን) መራቅ ተገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,45,'23:45','ከዚያም ሙሳንና ወንድሙን ሃሩንን በተዓምራታችንና በግልጽ አስረጅ ላክን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,46,'23:46','ወደ ፈርዖንና ወደ ጭፍሮቹ (ላክናቸው)፡፡ ኮሩም፡፡ የተንበጣረሩ ሕዝቦችም ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,47,'23:47','«ብጤያችንም ለኾኑ ሁለት ሰዎች ሕዝቦቻቸው ለእኛ ተገዢዎች ኾነው ሳሉ እናምናለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,48,'23:48','አስተባበሉዋቸውም፡፡ ከጠፊዎቹም ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,49,'23:49','ሙሳንም (ነገዶቹ) ይምመሩ ዘንድ መጽሐፍን በእርግጥ ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,50,'23:50','የመርየምን ልጅና እናቱንም ተዓምር አደረግናቸው፡፡ የመደላደልና የምንጭ ባለቤት ወደ ኾነች ከፍተኛ ስፍራም አስጠጋናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,51,'23:51','እናንተ መልክተኞች ሆይ! ከተፈቀዱት ምግቦች ብሉ፡፡ በጎ ሥራንም ሥሩ፡፡ እኔ የምተሠሩትን ሁሉ ዐዋቂ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,52,'23:52','ይህችም (በአንድ አምላክ የማመን ሕግጋት) አንድ መንገድ ስትኾን ሃይማኖታችሁ ናት፡፡ እኔም ጌታችሁ ነኝና ፍሩኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,53,'23:53','(ከዚያ ተከታዮቻቸው) የሃይማኖት ነገራቸውንም በመካከላቸው ክፍልፍሎች አድርገው ቆራረጡ፡፡ ሕዝብ ሁሉ እነርሱ ዘንድ ባለው ሃይማኖት ተደሳቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,54,'23:54','እስከ ጊዜያቸውም ድረስ በጥምመታቸው ውስጥ ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,55,'23:55','ከገንዘብና ከልጆች በእርሱ የምንጨምርላቸውን ያስባሉን');
INSERT INTO "sadiq_sani" VALUES(23,56,'23:56','በበጎ ነገሮች የምንቻኮልላቸው መኾንን (ያስባሉን) አይደለም፤ (ለማዘንጋት መኾኑን) አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,57,'23:57','እነዚያ እነርሱ ጌታቸውን ከመፍራት የተነሳ ተጨናቂዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(23,58,'23:58','እነዚያም እነርሱ በጌታቸው ተዓምራቶች ከሚያምኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(23,59,'23:59','እነዚያም እነርሱ በጌታቸው (ጣዖትን) የማያጋሩት፡፡');
INSERT INTO "sadiq_sani" VALUES(23,60,'23:60','እነዚያም የሚሰጡትን ነገር እነርሱ ወደ ጌታቸው ተመላሾች መኾናቸውን ልቦቻቸው የሚፈሩ ኾነው የሚሰጡት፡፡');
INSERT INTO "sadiq_sani" VALUES(23,61,'23:61','እነዚያ በመልካም ሥራዎች ይጣደፋሉ፡፡ እነሱም ለርሷ ቀዳሚዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,62,'23:62','ማንኛይቱንም ነፍስ ችሎታዋን እንጂ አናስገድዳትም፡፡ እኛም ዘንድ በእውነት የሚናገር መጽሐፍ አልለ፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,63,'23:63','በእውነቱ (ከሓዲዎች) ልቦቻቸው ከዚያ (መጽሐፍ) በዝንጋቴ ውስጥ ናቸው፡፡ ለእነሱም ከዚህ ሌላ እነሱ ለርሷ ሠሪዎችዋ የኾኑ (መጥፎ) ሥራዎች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,64,'23:64','ቅምጥሎቻቸውንም በቅጣት በያዝናቸው ጊዜ ወዲያውኑ እነርሱ ይወተውታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,65,'23:65','ዛሬ አትወትውቱ፤ እናንተ ከእኛ ዘንድ አትረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,66,'23:66','አንቀጾቹ በእናንተ ላይ በእርግጥ ይነበቡላችሁ ነበር፡፡ በተረከዞቻችሁም ላይ ወደ ኋላችሁ ትመለሱ ነበራችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,67,'23:67','በእርሱ (በክልሉ ቤት) ኩሩዎች ሌሊት ተጫዋቾች (ቁርኣንንም) የምትተው ኾናችሁ (ትመለሱ ነበራችሁ ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,68,'23:68','(የቁርኣንን) ንግግሩንም አያስተነትኑምን ወይስ ለመጀመሪያዎቹ አባቶቻቸው ያልመጣ ነገር መጣባቸውን');
INSERT INTO "sadiq_sani" VALUES(23,69,'23:69','ወይስ መልክተኛቸውን አላወቁምን ስለዚህ እነርሱ ለእርሱ ከሓዲዎች ናቸውን');
INSERT INTO "sadiq_sani" VALUES(23,70,'23:70','ወይስ በእርሱ ዕብደት አለበት ይላሉን አይደለም፡፡ እውነትን ይዞ መጣላቸው፡፡ አብዛኞቻቸውም እውነትን ጠይዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,71,'23:71','አላህም ዝንባሌዎቻቸውን በተከተለ ኖሮ ሰማያትና ምድር በውስጣቸውም ያለው ሁሉ በእርግጥ በተበላሸ ነበር፡፡ ይልቁንም ክብራቸው ያለበትን ቁርኣን አመጣንላቸው፡፡ እነርሱም ከክብራቸው ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,72,'23:72','ወይስ ግብርን ትጠይቃቸዋለህን የጌታህም ችሮታ በላጭ ነው፡፡ እርሱም ከሰጪዎች ሁሉ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,73,'23:73','አንተም ወደ ቀጥተኛው መንገድ በእርግጥ ትጠራቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,74,'23:74','እነዚያም በመጨረሻይቱ ዓለም የማያምኑት ከትክክለኛው መንገድ ተዘንባዮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,75,'23:75','ባዘንንላቸውና ከጉዳትም በእነርሱ ያለውን ባነሳንላቸው ኖሮ በጥመታቸው ውስጥ የሚዋልሉ ኾነው ይዘወትሩ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(23,76,'23:76','በእርግጥም በቅጣት ያዝናቸው፡፡ ታዲያ ለጌታቸው አልተናነሱም፡፡ አይዋደቁምም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,77,'23:77','በእነሱም ላይ የብርቱ ቅጣት ባለቤት የኾነ ደጃፍን በከፈትንባቸው ጊዜ እነርሱ ያን ጊዜ በእርሱ ተስፋ ቆራጮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,78,'23:78','እርሱም ያ መስሚያዎችንና ማያዎችን ልቦችንም ለእናንተ የፈጠረላችሁ ነው፡፡ ጥቂትን እንጅ አታመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,79,'23:79','እርሱም ያ በምድር ውስጥ የበተናችሁ ነው፡፡ ወደርሱም ትሰበሰባላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,80,'23:80','እርሱም ያ ሕያው የሚያደርግ የሚገድልም ነው፡፡ የሌሊትና የቀን መተካካትም የእርሱ ነው፡፡ አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(23,81,'23:81','ይልቁንም (የመካ ከሓዲዎች) የፊተኞቹ ሕዝቦች እንዳሉት ብጤ አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,82,'23:82','«በሞትንና ዐፈርና አጥንቶች በኾን ጊዜ እኛ ተቀስቃሾች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,83,'23:83','«ይህንን እኛም ከእኛ በፊትም የነበሩት አባቶቻችን በእርግጥ ተቀጥረናል፡፡ ይህ የመጀመሪያዎቹ ሰዎች ተረቶች እንጂ ሌላ አይደለም» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,84,'23:84','«ምድርና በውስጥዋ ያለው ሁሉ የማን ነው የምታውቁ ብትኾኑ (ንገሩኝ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,85,'23:85','«በእርግጥ የአላህ ነው» ይሉሃል፡፡ «ታዲያ አትገሰጹምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,86,'23:86','«የሰባቱ ሰማያት ጌታና የታላቁ ዐርሽ ጌታ ማን ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,87,'23:87','«በእርግጥ አላህ ነው» ይሉሃል፡፡ «እንግዲያ አትፈሩትምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,88,'23:88','«የነገሩ ሁሉ ግዛት በእጁ የኾነ እርሱ የሚጠብቅ በእርሱ ላይ የማይጠበቅ ማን ነው የምታውቁ እንደኾናችሁ (መልሱልኝ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,89,'23:89','«በእርግጥ አላህ ነው» ይሉሃል፡፡ «ታዲያ እንዴት ትታለላላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,90,'23:90','ይልቁንም እውነትን አመጣንላቸው፡፡ እነርሱም (በመካዳቸው) ውሸታሞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,91,'23:91','አላህ ምንም ልጅን አልያዘም (አልወለደም)፡፡ ከእርሱም ጋር አንድም አምላክ የለም፡፡ ያን ጊዜ (ሌላ አምላክ በነበረ) አምላክ ሁሉ በፈጠረው ነገር በተለየ ነበር፡፡ ከፊላቸውም በከፊሉ ለይ በላቀ ነበር፡፡ አላህ ከሚመጥኑት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,92,'23:92','ሩቁንና ቅርቡንም ሁሉ ዐዋቂ ነው፡፡ (በእርሱ) ከሚያጋሩትም ሁሉ ከፍ አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,93,'23:93','(ሙሐመድ ሆይ!) በል «ጌታዬ ሆይ! የሚያስፈራሩበትን (ቅጣት) ብታሳየኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,94,'23:94','«ጌታዬ ሆይ! በበደለኞች ሕዝቦች ውስጥ አታድርገኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(23,95,'23:95','እኛም የምናስፈራራቸውን ነገር ልናሳይህ በእርግጥ ቻዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,96,'23:96','በዚያች እርስዋ መልካም በኾነችው (ጠባይ) መጥፎይቱን ነገር ገፍትር፡፡ እኛ የሚሉትን ሁሉ ዐዋቂዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,97,'23:97','በልም «ጌታዬ ሆ! ከሰይጣናት ጉትተጎታዎች በአንተ እጠበቃለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,98,'23:98','«ባንተም ጌታዬ ሆይ! (በነገሮቼ) ወደእኔ ከመጣዳቸው እጠበቃለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(23,99,'23:99','አንዳቸውንም ሞት በመጣበት ጊዜ እንዲህ ይላል «ጌታዬ ሆይ! (ወደ ምድረ ዓለም) መልሱኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,100,'23:100','«በተውኩት ነገር በጎን ሥራ ልሠራ እከጅላለሁና፡፡» (ይህን ከማለት) ይከልከል፡፡ እርስዋ እርሱ ተናጋሪዋ የኾነች ከንቱ ቃል ናት፡፡ ከስተፊታቸውም እስከሚቀሰቀሱበት ቀን ድረስ ግርዶ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,101,'23:101','በቀንዱም በተነፋ ጊዜ በዚያ ቀን በመካከላቸው ዝምድና የለም፡፡ አይጠያየቁምም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,102,'23:102','ሚዛኖቹም የከበዱለት ሰው እነዚያ እነርሱ ፍላጎታቸውን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,103,'23:103','ሚዛኖቹም የቀለሉበት ሰው እነዚያ እነርሱ ነፍሶቻቸውን ያከሰሩ ናቸው፡፡ በገሀነም ውስጥ ዘመውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,104,'23:104','ፊቶቻቸውን እሳት ትገርፉቸዋለች፡፡ እነርሱም በእርሷ ውስጥ ከንፈሮቻቸው የተኮማተሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,105,'23:105','«አንቀጾቼ በእናንተ ላይ የሚነበቡላችሁና በእነርሱ የምታስተባብሉ አልነበራችሁምን» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,106,'23:106','ይላሉ «ጌታችን ሆይ! በእኛ ላይ መናጢነታችን አሸነፈችን፡፡ ጠማማዎችም ሕዝቦች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(23,107,'23:107','«ጌታችን ሆይ! ከእርሷ አውጣን፡፡ (ወደ ክህደት) ብንመለስም እኛ በደዮች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(23,108,'23:108','(አላህም) «ወራዶች ኾናችሁ በውስጥዋ እርጉ፡፡ አታናግሩኝም» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(23,109,'23:109','እነሆ ከባሮቼ «ጌታችን ሆይ! አምነናልና ማረን፡፡ እዘንልንም አንተም ከአዛኞች ሁሉ ይበልጥ አዛኝ ነህ» የሚሉ ክፍሎች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,110,'23:110','እኔንም ማውሳትን እስካስረሷችሁ ድረስም ማላገጫ አድርጋችሁ ያዛችኋቸው፡፡ በእነርሱም የምትስቁ ነበራችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,111,'23:111','እኔ ዛሬ በትዕግስታቸው ምክንያት እነሱ ፍላጎታቸውን የሚያገኙት እነርሱ ብቻ በመኾን መነዳኋቸው፤ (ይላቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(23,112,'23:112','«በምድር ውስጥ ከዓመታት ቁጥር ስንትን ቆያችሁ» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(23,113,'23:113','«አንድ ቀንን ወይም ከፊል ቀንን ቆየን፡፡ ቆጣሪዎቹንም ጠይቅ» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(23,114,'23:114','«እናንተ (የቆያችሁትን መጠን) የምታውቁ ብትኾኑ ኖሮ (በእሳት ውስጥ በምትቆዩት አንፃር) ጥቂትን ጊዜ እንጂ አልቆያችሁም» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(23,115,'23:115','«የፈጠርናችሁ ለከንቱ መኾኑን እናንተም ወደኛ የማትመለሱ መኾናችሁን ጠረጠራችሁን» (ለከንቱ የፈጠርናችሁ መሰላችሁን)');
INSERT INTO "sadiq_sani" VALUES(23,116,'23:116','የእውነቱም ንጉስ አላህ ከፍተኛነት ተገባው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ የሚያምረው ዐርሽ ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(23,117,'23:117','ከአላህም ጋር ሌላን አምላክ ለእርሱ በእርሱ ማስረጃ የሌለውን የሚገዛ ሰው ምርመራው እጌታው ዘንድ ብቻ ነው፡፡ እነሆ ከሓዲዎች አይድኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(23,118,'23:118','በልም «ጌታዬ ሆይ ማር፤ እዘንም፡፡ አንተም ከአዛኞች ሁሉ በላጭ ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(24,1,'24:1','(ይህች) ያወረድናትና የደነገግናት ምዕራፍ ናት፡፡ በእርሷም ውስጥ ትገሰፁ ዘንድ ግልጾችን አንቀጾች አውርደናል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,2,'24:2','ዝሙተኛይቱና ዝሙተኛው ከሁለቱ እያንዳንዳቸውን (ያላገቡ እንደኾኑ) መቶ ግርፋትን ግረፉዋቸው፤ በእነርሱም በአላህ ፍርድ ርኅራኄ አትያዛችሁ፡፡ በአላህና በመጨረሻው ቀን የምታምኑ እንደኾናችሁ (አትራሩ)፡፡ ቅጣታቸውንም ከምእምናን ጭፍሮች ይገኙበት፡፡');
INSERT INTO "sadiq_sani" VALUES(24,3,'24:3','ዝሙተኛው ዝሙተኛይቱን ወይም አጋሪይቱን እንጂ አያገባም፡፡ ዝሙተኛይቱንም ዝሙተኛ ወይም አጋሪ እንጂ አያገባትም፡፡ ይህም በምእምናን ላይ ተከልክሏል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,4,'24:4','እነዚያም ጥብቆችን ሴቶች (በዝሙት) የሚሰድቡ ከዚያም አራትን ምስክሮች ያላመጡ ሰማኒያን ግርፋት ግረፏቸው፡፡ ከእነርሱም ምስክርነትን ሁልጊዜ አትቀበሉ እነዚያም እነሱ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,5,'24:5','እነዚያ ከዚህ በኋላ የተጸጸቱና ሥራቸውን ያሳመሩ ሲቀሩ፡፡ አላህ በጣም መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(24,6,'24:6','እነዚያም ሚስቶቻቸውን (በዝሙት) የሚሰድቡ ለእነሱም ከነፍሶቻቸው በስተቀር ምስክሮች የሌሏቸው የኾኑ የአንዳቸው ምስክርነት እርሱ ከውነተኞች ለመኾኑ በአላህ ስም አራት ጊዜ ምሎ መመስከር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,7,'24:7','አምስተኛይቱም ከውሸታሞች ቢኾን በእርሱ ላይ የአላህ እርግማን ይኑርበት (ብሎ መመስከር) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,8,'24:8','እርሱም ከውሸታሞች ነው ብላ አራት ጊዜ መመስከሮችን በአላህ ስም መመስከርዋ ከእርስዋ ላይ ቅጣትን ይገፈትርላታል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,9,'24:9','አምስተኛይቱም እርሱ ከውነተኞች ቢኾን በእርስዋ ላይ የአላህ ቁጣ ይኑርባት (ብላ መመስከርዋ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,10,'24:10','በእናንተም ላይ የአላህ ችሮታና እዝነቱ ባልነበረ አላህም ጸጸትን ተቀባይ ጥበበኛ ባልኾነ ኖሮ (ውሸታሙን ይገልጸው ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,11,'24:11','እነዚያ መጥፎን ውሸት ያመጡ ከናንተው የኾኑ ጭፍሮች ናቸው፡፡ ለእናንተ ክፉ ነገር ነው');
INSERT INTO "sadiq_sani" VALUES(24,12,'24:12','(ውሸቱን) በሰማችሁት ጊዜ ምእምናንና ምእምናት በጎሶቻቸው ለምን ደግን ነገር አልጠረጠሩም ለምንስ ይህ ግልጽ ውሸት ነው አላሉም');
INSERT INTO "sadiq_sani" VALUES(24,13,'24:13','በእርሱ ላይ ለምን አራትን ምስክሮች አላመጡም ምስክሮቹንም ካላመጡ እነዚያ አላህ ዘንድ ውሸታሞቹ እነሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,14,'24:14','በቅርቢቱም በመጨረሻይቱም ዓለም በእናንተ ላይ የአላህ ችሮታና እዝነቱ ባልነበረ ኖሮ በውስጡ በገባችሁበት ወሬ ምክንያት ታላቅ ቅጣት በእርግጥ በደረሰባችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(24,15,'24:15','በምላሶቻችሁ በምትቅበባሉት ጊዜ ለእናንተም በእርሱ ዕውቀት በሌላችሁ ነገር በአፎቻችሁ በተናገራችሁና እርሱ አላህ ዘንድ ከባድ ኀጢአት ኾኖ ሳለ ቀላል አድርጋችሁ ባሰባችሁት ጊዜ (ከባድ ቅጣት በነካችሁ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,16,'24:16','በሰማችሁትም ጊዜ በዚህ ልንናገር ለእኛ አይገባንም፡፡ ጥራት ይገባህ፡፡ ይህ ከባድ ቅጥፈት ነው፤ አትሉም ነበርን');
INSERT INTO "sadiq_sani" VALUES(24,17,'24:17','ምእመናን እንደኾናችሁ ወደ ብጤው በፍፁም እንዳትመለሱ አላህ ይገስጻችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,18,'24:18','ለእናንተም አላህ አንቀጾችን ይገልጽላችኋል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,19,'24:19','እነዚያ በእነዚያ ባመኑት ሰዎች ውስጥ መጥፎ ወሬ እንድትስፋፋ የሚወዱ ለእነሱ በቅርቢቱም በመጨረሻይቱም ዓለም አሳማሚ ቅጣት አላቸው፡፡ አላህም ያውቃል እናንተ ግን አታውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(24,20,'24:20','በእናንተም ላይ የአላህ ችሮታውና እዝነቱ ባልነበረ አላህም ሩኅሩኅና አዛኝ ባልኾነ ኖሮ (ቶሎ ባጠፋችሁ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,21,'24:21','እናንተ ያመናችሁ ሆይ! የሰይጣንን እርምጃዎች አትከተሉ፡፡ የሰይጣንንም እርምጃዎች የሚከተል ሰው (ኀጢአትን ተሸከመ)፡፡ እርሱ በመጥፎና በሚጠላ ነገር ያዛልና፡፡ በእናንተም ላይ የአላህ ችሮታና እዝነቱ ባልነበረ ኖሮ ከናንተ አንድም ፈጽሞ ባልጠራ ነበር፡፡ ግን አላህ የሚሻውን ሰው ያጠራል፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,22,'24:22','ከእናንተም የችሮታና የክብረት ባለቤቶች የኾኑት ለቅርብ ዘመዶችና ለድኾች በአላህም መንገድ ለተሰደዱ ሰዎች ላይሰጡ አይማሉ፡፡ ይቅርታም ያድርጉ፡፡ (ጥፋተኞቹን) ይለፉም፡፡ አላህ ለእናንተ ሊምር አትወዱምን አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,23,'24:23','እነዚያ ጥብቆቹን ከዝሙት ዘንጊዎቹን ምእምናት የሚሰድቡ፤ በቅርቢቱም በመጨረሻይቱም');
INSERT INTO "sadiq_sani" VALUES(24,24,'24:24','በእነሱ ላይ ምላሶቻቸው፣ እጆቻቸውና እግሮቻቸውም ይሠሩት በነበሩት ነገር በሚመሰክሩባቸው ቀን (ከባድ ቅጣት አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,25,'24:25','በዚያ ቀን አላህ እውነተኛ ዋጋቸውን ይሞላላቸዋል፡፡ አላህም እርሱ መኖሩ የተረጋገጠ (ሁሉን ነገር) ገላጭ መኾኑን ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,26,'24:26','መጥፎዎቹ ሴቶች ለመጥፎዎች ወንዶች መጥፎዎቹ ወንዶችም ለመጥፎዎቹ ሴቶች የተገቡ ናቸው፡፡ ጥሩዎቹ ሴቶችም ለጥሩዎቹ ወንዶች ጥሩዎቹ ወንዶችም ለጥሩዎቹ ሴቶች የሚገቡ ናቸው፡፡ እነዚያ (መጥፎዎቹ) ከሚሉት ነገር ንጹሕ የተደረጉ ናቸው፡፡ ለእነርሱ ምህረትና መልካም ሲሳይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,27,'24:27','እናንተ ያመናችሁ ሆይ! እስከምታስፈቅዱና በባለቤቶቿ ላይ ሰላምታን እስከምታቀርቡ ድረስ ከቤቶቻችሁ ሌላ የኾኑን ቤቶች አትግቡ፡፡ ይህ ለእናንተ መልካም ነው፡፡ እናንተ ትገነዘቡ ዘንድ (በዚህ ታዘዛችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,28,'24:28','በውስጧም አንድንም ሰው ባታገኙ ለእናንተ እስከሚፈቀድላችሁ ድረስ አትግቧት፡፡ ለእናንተ ተመለሱ ብትባሉም ተመለሱ፡፡ እርሱ ለእናንተ የተሻለ ነው፡፡ አላህም የምትሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,29,'24:29','መኖሪያ ያልኾኑን ቤቶች በውስጣቸው ለእናንተ ጥቅም ያላችሁን (ሳታስፈቅዱ) ብትገቡ በእናንተ ላይ ኀጢአት የለባችሁም፡፡ አላህም የምትገልጹትንና የምትደብቁትን ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,30,'24:30','ለምእመናን ንገራቸው፡- ዓይኖቻቸውን (ያልተፈቀደን ከማየት) ይከልክሉ፤ ብልቶቻቸውንም ይጠብቁ፤ ይህ ለነሱ የተሻለ ነው፤ አላህ በሚሠሩት ሁሉ ውስጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,31,'24:31','ለምእምናትም ንገራቸው፡- ዓይኖቻቸውን ይከልክሉ፡፡ ብልቶቻቸውንም ይጠብቁ፡፡ ጌጣቸውንም ከእርሷ ግልጽ ከኾነው በስተቀር አይግለጡ፡፡ ጉፍታዎቻቸውንም በአንገትጌዎቻቸው ላይ ያጣፉ፡፡ (የውስጥ) ጌጣቸውንም ለባሎቻቸው ወይም ለአባቶቻቸው ወይም ለባሎቻቸው አባቶች ወይም ለወንዶች ልጆቻቸው ወይም ለባሎቻቸው ወንዶች ልጆች ወይም ለወንድሞቻቸው ወይም ለወንድሞቻቸው ወንዶች ልጆች ወይም ለእህቶቻቸው ወንዶች ልጆች ወይም ለሴቶቻቸው ወይም እጆቻቸው ለያዙት (ባሪያ) ወይም ከወንዶች ለሴት ጉዳይ የሌላቸው ለኾኑ ተከታዮች ወይም ለነዚያ በሴቶች ሐፍረተ ገላ ላይ ላላወቁ ሕፃኖች ካልኾነ በስተቀር አይግለጹ፡፡ ከጌጣቸውም የሚሸፍኑት ይታወቅ ዘንድ በእግሮቻቸው አይምቱ፡፡ ምእመናኖች ሆይ! ትድኑ ዘንድ ሁላችሁም ወደ አላህ (በመመለስ) ተጸጸቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,32,'24:32','ከናንተም ትዳር የሌላቸውን አጋቡ፡፡ ከወንዶች ባሮቻችሁና ከሴቶች ባሮቻችሁም ለጋብቻ ብቁ የኾኑትን (አጋቡ)፡፡ ድኾች ቢኾኑ አላህ ከችሮታው ያከብራቸዋል፡፡ አላህም ስጦታው ሰፊ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,33,'24:33','እነዚያም የማግቢያን ጣጣ የማያገኙ አላህ ከችሮታው እስከሚያከብራቸው ድረስ ይጠበቁ፡፡ እነዚያንም እጆቻችሁ ከጨበጧቸው አገልጋዮች (ገንዘብ ሰጥተው ነጻ ለመውጣት) መጻጻፍን የሚፈልጉትን ከእነሱ መልካምን ነገር ብታውቁ ተጻጻፉዋቸው፡፡ ከአላህም ገንዘብ ከዚያ ከሰጣችሁ ስጧቸው፡፡ ሴቶች ባሮቻችሁንም መጥጠበቅን ቢፈልጉ የቅርቢቱን ሕይወት ጥቅም ለመፈለግ ብላችሁ በዝሙት ላይ አታስገድዱዋቸው፡፡ የሚያስገድዳቸውም ሰው አላህ ከመገደዳቸው በኋላ (ለተገደዱት) መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,34,'24:34','ወደእናንተም አብራሪ የኾኑን አንቀጾች ከእነዚያ ከበፊታችሁ ካለፉትም (ምሳሌዎች ዓይነት) ምሳሌን ለጥንቁቆቹ መገሰጫንም በእርግጥ አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(24,35,'24:35','አላህ የሰማያትና የምድር አብሪ ነው፡፡ የብርሃኑ ምሳሌ በውስጧ መብራት እንዳለባት (ዝግ) መስኮት፣ መብራቱ በብርጭቆ ውስጥ የኾነ፣ ብርጭቆይቱ ፍፁም ሉላዊ ኮከብ የምትመሰል፣ ምሥራቃዊው ምዕራባዊም ካልኾነች ከተባረከች የወይራ ዛፍ ዘይቷ እሳት ባይነካውም እንኳ ሊያበራ የሚቀርብ ከኾነች (ዘይት) የሚቃጠል እንደ ሆነ (መብራት) ነው፡፡ (ይህ) በብርሃን ላይ የኾነ ብርሃን ነው፡፡ አላህ ወደ ብርሃኑ የሚሻውን ሰው ይመራል፡፡ አላህም ለሰዎች ምሳሌዎችን ያቀርባል፡፡ አላህም በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,36,'24:36','አላህ እንድትከበርና ስሙ በውስጧ እንዲወሳ ባዘዘው ቤቶች ውስጥ (አወድሱት)፡፡ በውስጧ በጧትና በማታ ለእርሱ ያጠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,37,'24:37','አላህን ከማውሳትና ሶላትን ከመስገድ ዘካንም ከመስጠት ንግድም ሽያጭም የማያታልላቸው ልቦችና ዓይኖች በእርሱ የሚገላበጡበትን ቀን የሚፈሩ የኾኑ ሰዎች (ያጠሩታል)፡፡');
INSERT INTO "sadiq_sani" VALUES(24,38,'24:38','አላህ የሠሩትን ሥራ መልካሙን ሊመነዳቸው ከችሮታውም ሊጨመርላቸው (ያጠሩታል)፡፡ አላህም ለሚሻው ሰው ያለ ግምት ይሰጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,39,'24:39','እነዚያም የካዱት ሰዎች ሥራዎቻቸው (መልካሞቹ) በበረሃ ሜዳ እንዳለ ሲሪብዱ የጠማው ሰው ውሃ ነው ብሎ እንደሚያስበው በመጣውም ጊዜ ምንም ነገር ኾኖ እንደማያገኘው ነው፡፡ (ከሓዲው) አላህንም እሠራው ዘንድ ያገኘዋል፡፡ ምርመራውንም (ቅጣቱን) ይሞላለታል፡፡ አላህም ምርመራው ፈጣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,40,'24:40','ወይም (መጥፎ ሥራዎቻቸውን) ከበላዩ ደመና ያለበት ማዕበል በሚሸፈነው ጥልቅ ባህር ውስጥ እንዳሉ ጨለማዎች ነው፡፡ (እነዚያ) ከፊላቸው ከከፊሉ በላይ ድርብርብ የኾኑ ጨለማዎች ናቸው፡፡ (በዚህች) የተሞከረ (ሰው) እጁን ባወጣ ጊዜ ሊያያት አይቀርብም፡፡ አላህም ለእርሱ ብርሃንን ያላደረገለት ሰው ለእርሱ ምንም ብርሃን የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(24,41,'24:41','አላህ በሰማያትና በምድር ያለ ሁሉ በራሪዎችም ክንፎቻቸውን (በአየር ላይ) ያንሳፈፉ ኾነው ለእርሱ የሚያጠሩ (የሚያወድሱ) መኾናቸውን አላወቅህምን ሁሉም ስግደቱንና ማጥራቱን በእርግጥ ዐወቀ፡፡ አላህም የሚሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,42,'24:42','የሰማያትና የምድርም ግዛት የአላህ ነው፡፡ መመለሻም ወደ አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,43,'24:43','አላህ ደመናን የሚነዳ መኾኑን አላየህምን ከዚያም ከፊሉን ከከፊሉ ያገናኛል፡፡ ከዚያም የተደራረበ ያደርገዋል፡፡ ዝናቡንም ከመካከሉ የሚወጣ ኾኖ ታየዋለህ፡፡ ከሰማይም (ከደመና) በውስጧ ካሉት ጋራዎች በረዶን ያወርዳል፡፡ በእርሱም የሚሻውን ሰው (በጉዳት) ይነካል፡፡ ከሚሻውም ሰው ላይ ይመልሰዋል፡፡ የብልጭታው ብርሃን ዓይኖችን ሊወስድ ይቀርባል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,44,'24:44','አላህ ሌሊትንና ቀንን ያገላብጣል፡፡ በዚህም ለባለ ውስጥ ዓይኖች በእርግጥ ማስረጃ አልለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(24,45,'24:45','አላህም ተንቀሳቃሽን ሁሉ ከውሃ ፈጠረ፡፡ ከእነሱም በሆዱ ላይ የሚኼድ አልለ፡፡ ከእነሱም በሁለት እግሮች ላይ የሚኼድ አልለ፡፡ አላህ የሚሻውን ሁሉ ይፈጥራል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(24,46,'24:46','አብራሪን አንቀጾች በእርግጥ አወረድን፡፡ አላህም የሚሻውን ሰው ወደ ቀጥተኛ መንገድ ይመራዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(24,47,'24:47','በአላህና በመልክተኛውም አምነናል ታዘናልም ይላሉ፡፡ ከዚያም ከዚህ በኋላ ከእነሱ ከፊሉ ይሸሻል፡፡ እነዚያም ምእምናን አይደሉም፤');
INSERT INTO "sadiq_sani" VALUES(24,48,'24:48','ወደ አላህና ወደ መልክተኛውም በመካከላቸው ሊፈርድ በተጠሩ ጊዜ ከእነሱ ከፊሉ ወዲያውኑ ይሸሻሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,49,'24:49','እውነቱም (ሐቁ) ለእነሱ ቢኾን ወደርሱ ታዛዦች ኾነው ይመጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,50,'24:50','በልቦቻቸው ውስጥ በሺታ አለን ወይስ (በነቢይነቱ) ተጠራጠሩን ወይስ አላህና መልክተኛው በእነሱ ላይ የሚበድሉ መኾንን ይፈራሉን ይልቁንም፤ እነዚያ እነሱ በዳዮቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,51,'24:51','የምእምናን ቃል የነበረው ወደ አላህና ወደ መልክተኛው በመካከላቸው ሊፈርድ በተጠሩ ጊዜ ሰማን፤ ታዘዝንም ማለት ብቻ ነው፡፡ እነዚህም እነሱ የፈለጉትን የሚያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,52,'24:52','አላህንና መልክተኛውን የሚታዘዝም ሰው፣ አላህንም የሚፈራና የሚጠነቀቀው ሰው፤ እነዚያ እነርሱ ፍለጎታቸውን የሚያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,53,'24:53','(ለዘመቻ) ብታዛቸውም በእርግጥ ሊወጡ የጠነከሩ መሓሎቻቸውን በአላህ ስም ይምላሉ፡፡ «አትማሉ፤ የታወቀች (የንፍቅና) መታዘዝ ናትና፡፡ አላህ በምትሠሩት ነገር ሁሉ ውስጥ ዐዋቂ');
INSERT INTO "sadiq_sani" VALUES(24,54,'24:54','«አላህን ተገዙ፡፡ መልክተኛውንም ታዘዙ፤ ብትሸሹም (አትጎዱትም)፡፡ በእርሱ ላይ ያለበት የተገደደውን ማድረስ ብቻ ነው፡፡ በእናንተም ላይ ያለባችሁ የተገደዳችሁት (መታዘዝ) ብቻ');
INSERT INTO "sadiq_sani" VALUES(24,55,'24:55','አላህ እነዚያን ከእናንተ ያመኑትንና መልካም ሥራዎችን የሠሩትን እነዚያን ከእነሱ በፊት የነበሩትን እንደተካ በምድር ለይ በእርግጥ ሊተካቸው፣ ለእነሱም ያንን የወደደላቸውን ሃይማኖታቸውን ሊያደላድልላቸው፣ ከፍርሃታቸውም በኋላ ጸጥታን ሊለውጥላቸው ተስፋን ቃል ሰጥቷቸዋል፡፡ በእኔ ምንንም የማያጋሩ ኾነው ይግገዙኛል፡፡ ከዚያም በኋላ የካደ ሰው እነዚያ እነርሱ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,56,'24:56','ሶላትንም አስተካክላችሁ ስገዱ፡፡ ምጽዋትንም ስጡ፡፡ መልክተኛውንም ታዘዙ፡፡ ለእናንተ ሊታዘንላችሁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(24,57,'24:57','እነዚያን የካዱትን በምድር ውስጥ የሚያቅቱ አድርገህ አታስብ፡፡ መኖሪያቸውም እሳት ናት፡፡ በእርግጥም የከፋች መመለሻ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(24,58,'24:58','እናንተ ያመናችሁ ሆይ! እነዚያ እጆቻችሁ የጨበጧቸው (ባሮች) እነዚያም ከእናንተ ለአካላ መጠን ያልደረሱት ከጎህ ስግደት በፊት፣ በቀትርም ልብሶቻችሁን በምታወልቁ ጊዜ ከምሽት ስግደትም በኋላ ሦስት ጊዜያት (ለመግባት ሲፈልጉ) ያስፈቅዷችሁ፡፡ (እነዚህ) ለእናንተ የኾኑ ሦስት የሐፍረተ ገላ መገለጫ ጊዜያቶች ናቸውና፡፡ ከእነዚህ በኋላ በእናንተም በእነርሱም ላይ (ያለፈቃድ በመግባት) ኀጢአት የለም፡፡ በእናንተ ላይ (ለማገልገል) ዙዋሪዎች ናቸውና፡፡ ከፊላችሁ በከፊሉ ላይ ዙዋሪ ነው፡፡ እንደዚሁ አላህ ለእናንተ ሕግጋትን ይገልጽላችኋል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,59,'24:59','ከእናንተም ሕፃናቶቹ አቅመ አዳምን በደረሱ ጊዜ እነዚያ ከእነሱ በፊት የነበሩት (ታላላቆቹ) እንዳስፈቀዱ ያስፈቅዱ፡፡ እንደዚሁ አላህ ለእናንተ አንቀጾቹን ያብራራል፤ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,60,'24:60','ከሴቶችም እነዚያ ጋብቻን የማይፈልጉት ተቀማጮች (ባልቴቶች)፤ በጌጥ የተገለፁ ሳይኾኑ የላይ ልብሶቻቸውን ቢጥሉ በእነሱ ላይ ኀጢአት የለባቸውም፡፡ ግን መጠበቃቸው ለእነርሱ የተሻለ ነው፡፡ አላህም ሰሚ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(24,61,'24:61','(ከሰዎች ጋር በመብላት) በዕውር ላይ ኀጢአት የለበትም፡፡ በአንካሳም ላይ ኀጢአት የለበትም፡፡ በበሽተኛም ላይ ኀጢአት የለበትም፡፡ በነፍሶቻችሁም ላይ ከቤቶቻችሁ ወይም ከአባቶቻችሁ ቤቶች ወይም ከእናቶቻችሁ ቤቶች ወይም ከወንድሞቻችሁ ቤቶች ወይም ከእኅቶቻችሁ ቤቶች ወይም ከአጎቶቻችሁ ቤቶች ወይም ከአክስቶቻችሁ ቤቶች ወይም ከየሹሞቻችሁ ቤቶች ወይም ከየሹሜዎቻችሁ ቤቶች ወይም መክፈቻዎቹን ከያዛችሁት ቤት ወይም ከወዳጃችሁ ቤት (ብትበሉ) ኀጢአት የለባችሁም፡፡ ተሰብስባችሁ ወይም ተለያይታችሁ ብትበሉ በእናንተ ላይ ኀጢአት የለባችሁም፡፡ ቤቶችንም በገባችሁ ጊዜ፤ ከአላህ ዘንድ የኾነችን የተባረከች መልካም ሰላምታ በነፍሶቻችሁ ላይ ሰላም በሉ፡፡ እንደዚሁ አላህ አንቀጾችን ለእናንተ ያብራራል፡፡ ልታውቁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(24,62,'24:62','ምእመናን ማለት እነዚያ በአላህና በመልክተኛው ያመኑት፤ ከእርሱ ጋር በሚሰበሰብ ጉዳይ ላይ በኾኑ ጊዜ እስከሚያስፈቅዱ ድረስ የማይኼዱት ብቻ ናቸው፡፡ እነዚያ የሚያስፈቅዱህ እነርሱ እነዚያ በአላህና በመልክተኛው የሚያምኑት ናቸው፡፡ ለግል ጉዳያቸውም ፈቃድ በጠየቁህ ጊዜ ከእነሱ ለሻኸው ሰው ፍቀድለት፡፡ ለእነሱም አላህን ምሕረትን ለምንላቸው አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(24,63,'24:63','በመካከላችሁ የመልክተኛውን ጥሪ ከፊላችሁ ከፊሉን እንደ መጥራት አታድርጉት፡፡ ከእናንተ ውስጥ እነዚያን እየተከለሉ በመስለክለክ የሚወጡትን አላህ በእርግጥ ያውቃቸዋል፡፡ እነዚያም ትዕዛዙን የሚጥሱ መከራ እንዳትደርስባቸው ወይም አሳማሚ ቅጣት እንዳያገኛቸው ይጠንቀቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(24,64,'24:64','ንቁ! በሰማያትና በምድር ያለው ሁሉ የአላህ ብቻ ነው፡፡ እናንተ በእርሱ ላይ ያላችሁበትን ኹኔታ በእርግጥ ያውቃል፡፡ ወደእርሱ የሚመለሱበትን ቀንም (ያውቃል)፡፡ የሠሩትንም ሁሉ ይነግራቸዋል፡፡ አላህም ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,1,'25:1','ያ ፉርቃንን በባሪያው ላይ ለዓለማት አስፈራሪ ይኾን ዘንድ ያወረደው (አምላክ) ክብርና ጥራት ተገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,2,'25:2','(እርሱ) ያ የሰማያትና የምድር ንግሥና የእርሱ ብቻ የኾነ፣ ልጅንም ያልያዘ፣ በንግሥናውም');
INSERT INTO "sadiq_sani" VALUES(25,3,'25:3','(ከሓዲዎች) ከእርሱም ሌላ ምንንም የማይፈጥሩን፣ እነርሱም የሚፈጠሩን፣ ለነፍሶቻቸውም ጉዳትን ለመከላከል ጥቅምንም ለማምጣት የማይችሉን ሞትንና ሕይወትንም መቀስቀስንም የማይችሉን፣ አማልክት ያዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,4,'25:4','እነዚያም የካዱት ይህ (ቁርኣን ሙሐመድ) የቀጠፈው በእርሱም ላይ ሌሎች ሕዝቦች ያገዙት የኾነ ውሸት እንጂ ሌላ አይደለም አሉ፡፡ በእርግጥም በደልንና እብለትን ሠሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,5,'25:5','አሉም «የመጀመሪያዎቹ ሰዎች ተረቶች ናት፡፡ አስጻፋት፡፡ እርሷም በእርሱ ላይ ጧትና ማታ ትነበብለታለች፡፡»');
INSERT INTO "sadiq_sani" VALUES(25,6,'25:6','«ያ በሰማያትና በምድር ውስጥ ያለን ምስጢር የሚያውቀው አወረደው፡፡ እርሱ መሓሪ አዛኝ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,7,'25:7','ለዚህም መልክተኛ ምግብን የሚበላ፣ በገበያዎችም የሚኼድ ሲኾን ምን (መልክተኛነት) አለው ከርሱ ጋር አስፈራሪ ይኾን ዘንድ ወደርሱ መልአክ (ገሃድ) አይወረድም ኖሯልን አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,8,'25:8','«ወይም ወደእርሱ ድልብ አይጣልለትምን ወይም ከእርሷ የሚበላላት አትክልት ለእርሱ አትኖረውምን» (አሉ)፡፡ በዳዮቹም (ላመኑት) «የተደገመበትን ሰው እንጂ ሌላ አትከተሉም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,9,'25:9','ላንተ ምሳሌዎችን እንዴት እንዳደረጉልህና እንደተሳሳቱ ተመልከት፡፡ (ወደ እውነት ለመድረስ) መንገድንም አይችሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(25,10,'25:10','ያ ቢሻ ከዚህ (ካሉት) የተሻለን ከሥሮቻቸው ወንዞች የሚፈሱባቸውን አትክልቶች ላንተ የሚያደርግልህ ሕንጻ ቤቶችንም ላንተ የሚያደርግልህ ጌታ ችሮታው በዛ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,11,'25:11','ይልቁንም በትንሣኤ አስተባበሉ፡፡ በትንሣኤ ላስተባበለም ሰው ሁሉ ነዳጅን እሳት አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(25,12,'25:12','ከሩቅ ስፍራ ባየቻቸው ጊዜ ለእርሷ የቁጭት መገንፈልንና ማናፋትን ይሰማሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,13,'25:13','እጅ ከፍንጅ የታሠሩ ኾነውም ከእርሷ በጠባብ ስፍራ በተጣሉ ጊዜ በዚያ ስፍራ ጥፋትን ይጠራሉ፤ (ዋ ጥፋታችን ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,14,'25:14','፡-ዛሬ አንድን ጥፋት ብቻ አትጥሩ ብዙንም ጥፋት ጥሩ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,15,'25:15','(እንዲህ) በላቸው «ይህ የተሻለ ነውን ወይስ ጥንቁቆቹ ተስፋ የተሰጧት የዘለዓለሟ ገነት» ለእነርሱ ምንዳና መመለሻ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(25,16,'25:16','ለእነሱ በውስጧ ዘውታሪዎች ኾነው የሚፈልጉት ሁሉ አልላቸው፡፡ (ይህም) ተስፋ ከጌታህ ተለማኝ ተስፋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,17,'25:17','እነርሱንና ከአላህ ሌላ የሚግገዟቸውንም የሚሰበስብባቸውንና «እናንተ እነዚህን ባሮቼን አሳሳታችሁን ወይስ እነሱው መንገድን ሳቱ» የሚልበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,18,'25:18','«ጥራት ይገባህ፤ ካንተ ሌላ ረዳቶችን ልንይዝ ለእኛ ተገቢያችን አልነበረም፡፡ ግን እነርሱንም አባቶቻቸውንም መገንዘብን እስከተዉ ድረስ አጣቀምካቸው፡፡ ጠፊ ሕዝቦችም ኾኑ» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,19,'25:19','በምትሉትም (አማልክቶቻችሁ) አስዋሹዋችሁ፡፡ ቅጣቴን መገፍተርንም መርዳትንም አትችሉም፡፡ ከእናንተም የሚበድለውን ታላቅን ቅጣት እናቀምሰዋለን፤ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,20,'25:20','ከአንተ በፊትም ከመልክተኞች እነሱ በእርግጥ ምግብን የሚበሉ በገበያዎችም የሚኼዱ ኾነው በስተቀር አልላክንም፡፡ ከፊላችሁንም ለከፊሉ ፈተና አድርገናል፡፡ ትታገሳላችሁን ጌታህም ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,21,'25:21','እነዚያም መገናኘታችንን የማይፈሩት በእኛ ላይ ለምን መላእክት አልወረደም፡፡ ወይም ጌታችንን ለምን አናይም አሉ፡ በነፍሶቻቸው ውስጥ በእርግጥ ኮሩ፡፡ ታላቅንም አመጽ አመፁ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,22,'25:22','መላእክትን በሚያዩበት ቀን በዚያ ጊዜ ለአመጸኞች ምስራች የላቸውም፡፡ «የተከለከለ ክልክልም አድርገን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,23,'25:23','ከሥራም ወደ ሠሩት እናስባለን፡፡ የተበተነ ትቢያም እናደርገዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(25,24,'25:24','የገነት ሰዎች በዚያ ቀን በመርጊያ የተሻሉ በማረፊያም በጣም ያማሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,25,'25:25','ሰማይም በደመና የምትቀደድበትንና መላእክትም መወረድን የሚወርድበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,26,'25:26','እውነተኛው ንግሥና በዚያ ቀን ለአልራሕማን ብቻ ነው፡፡ በከሓዲዎችም ላይ አስቸጋሪ ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,27,'25:27','በዳይም «ከመልክተኛው ጋር ቀጥተኛን መንገድ ይዤ በኾነ ዋ ምኞቴ!» እያለ (በጸጸት) ሁለት እጆቹን የሚነክስበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,28,'25:28','«ዋ ጥፋቴ! እገሌን ወዳጅ ባልያዝኩ ኖሮ እመኛለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,29,'25:29','(የእምነት ቃልን) ከመጣልኝ በኋላ ከማስታወስ በእርግጥ አሳሳተኝ» (ይላል)፡፡ ሰይጣንም ለሰው አጋላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,30,'25:30','መልክተኛውም «ጌታዬ ሆይ! ሕዝቦቼ ይህንን ቁርኣን የተተወ ነገር አድርገው ያዙት» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,31,'25:31','እንደዚሁም ለነቢዩ ሁሉ ከአመጸኞች የኾነ ጠላትን አድርገናል፡፡ መሪና ረዳትም በጌታህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,32,'25:32','እነዚያ የካዱትም «ቁርኣን በእርሱ ላይ ለምን በጠቅላላ አንድ ጊዜ አልተወረደም» አሉ፡፡ እንደዚሁ በእርሱ ልብህን ልናረጋ (ከፋፍለን አወረድነው)፡፡ ቀስ በቀስ መለያየትንም ለየነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,33,'25:33','በጥያቄም አይመጡብህም እውነተኛውን (መልስ) መልካምን ፍችም የምናመጣልህ ብንኾን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,34,'25:34','እነዚያ በፊቶቻቸው ለይ (እየተጎተቱ) ወደ ገሀነም የሚሰበሰቡ ናቸው፡፡ እነዚያ ስፍራቸው የከፋ መንገዳቸውም የጠመመ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,35,'25:35','በእርግጥም ለሙሳ መጽሐፍን ሰጠነው፡፡ ከእርሱም ጋር ወንድሙን ሃሩንን ረዳት አደረግንለት፡፡');
INSERT INTO "sadiq_sani" VALUES(25,36,'25:36','«ወደእነዚያም በተዓምራቶቻችን ወደ አስተባበሉት ሕዝቦች ኺዱ» አልናቸው፡፡ (አስዋሿቸውም) ማጥፋትንም አጠፋናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,37,'25:37','የኑሕን ሰዎችም መልክተኞችን ባስተባበሉ ጊዜ አሰጠምናቸው፡፡ ለሰዎችም መገሰጫ አደረግናለቸው፡፡ ለበደለኞችም አሳማሚን ቅጣት አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(25,38,'25:38','ዓድንም ሰሙድንም የረስን ሰዎችም በዚህ መካከል የነበሩትንም ብዙን የክፍለ ዘመናት ሕዝቦች (አጠፋን)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,39,'25:39','ሁሉንም (ገሠጽን) ለእነሱ ምሳሌዎችን ገለጽን፡፡ ሁሉንም ማጥፋትን አጠፋናቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(25,40,'25:40','በዚያችም ክፉ ዝናምን በተዘነመችው ከተማ ላይ (የመካ ከሐዲዎች) በእርግጥ መጥተዋል፡፡ የሚያዩዋት አልነበሩምን በእውነቱ መቀስቀስን የማይፈሩ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,41,'25:41','ባዩህም ጊዜ ያ አላህ መልእክተኛ አድርጎ የላከው ይህ ነውን እያሉ መሳለቂያ እንጂ ሌላ አያደርጉህም፡፡');
INSERT INTO "sadiq_sani" VALUES(25,42,'25:42','«እነሆ በእርሷ ላይ መታገሳችን ባልነበረ ኖሮ ከአማልክቶቻችን ሊያሳስተን ቀርቦ ነበር» (ሲሉም ይሳለቃሉ)፡፡ ወደፊትም ቅጣቱን በሚያዩ ጊዜ መንገድን በጣም ተሰሳቹ ማን እንደኾነ በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,43,'25:43','ፍላጎቱን አምላኩ አድርጎ የያዘውን ሰው አየህን አንተ በእርሱ ላይ ጠባቂ ትኾናለህን');
INSERT INTO "sadiq_sani" VALUES(25,44,'25:44','ይልቁንም አብዛኛዎቻቸው የሚሰሙ ወይም የሚያውቁ መኾናቸውን ታስባለህን እነርሱ እንደ እንስሳዎች እንጂ ሌላ አይደሉም፡፡ ከቶውንም እነሱ ይልቅ መንገድን የተሳሳቱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,45,'25:45','ወደ ጌታህ (ሥራ) ጥላን እንዴት እንደ ዘረጋ አላየህምን በሻም ኖሮ የረጋ ባደረገው ነበር፡፡ ከዚያም ፀሐይን በእርሱ ላይ ምልክት አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(25,46,'25:46','ከዚያም ቀስ በቀስ ወደእኛ ሰብሰብነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,47,'25:47','እርሱም ያ ለእናንተ ሌሊትን ልብስ እንቅልፍንም ማረፊያ ያደረገ፤ ቀንንም መበተኛ ያደረገላችሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,48,'25:48','እርሱም ያ ነፋሶችን አብሳሪዎች ሲኾኑ ከዝናሙ በስተፊት የላከ ነው፡፡ ከሰማይም አጥሪ ውሃን አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(25,49,'25:49','በእርሱ የሞተችን አገር ሕያው ልናደርግበት ከፈጠርነውም ፍጡር እንስሳዎችንና ብዙ ሰዎችንም ልናጠጣው (አወረድነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,50,'25:50','(ጸጋችንን) ይገነዘቡም ዘንድ በመካከላቸው ከፋፈልነው፡፡ ግን አብዛኛዎቹ ሰዎች ክህደትን እንጂ እንቢ አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,51,'25:51','በሻንም ኖሮ በየከተማይቱ አስፈራሪን በላክን ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(25,52,'25:52','ከሓዲዎችንም አትታዘዛቸው፡፡ በእርሱም (በቁርኣን) ታላቅን ትግል ታገላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,53,'25:53','እርሱም ያ ሁለቱን ባሕሮች አጎራብቶ የለቀቀ ነው፡፡ ይህ ጥምን የሚቆርጥ ጣፋጭ ነው፡፡ ይህም የሚመረግግ ጨው ነው፡፡ በመካከላቸውም (ከመቀላቀል) መለያንና የተከለለን ክልል ያደረገ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,54,'25:54','እርሱም ያ ከውሃ ሰውን የፈጠረ፣ የዝምድናና የአማችም ባለቤት ያደረገው ነው፡፡ ጌታህም ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,55,'25:55','ከአላህም ሌላ የማይጠቅማቸውንና የማይጎዳቸውን ይግገዛሉ፤ ከሓዲም በጌታው ላይ (በማመጽ ለሰይጣን) ረዳት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,56,'25:56','አብሳሪና አስፈራሪም አድርገን እንጂ አልላክንህም፡፡');
INSERT INTO "sadiq_sani" VALUES(25,57,'25:57','«በእርሱ ላይ ዋጋን በፍጹም አልጠይቃችሁም፡፡ ግን ወደ ጌታው (መልካም) መንገድን ለመያዝ የሻ ሰው (ይሥራ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,58,'25:58','በዚያም በማይሞተው ሕያው አምላክ ላይ ተመካ፡፡ ከማመስገንም ጋር አጥራው፡፡ በባሮቹ ኀጢኣቶችም ውስጠ ዐዋቂ በእርሱ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,59,'25:59','ያ ሰማያትና ምድርን በመካከላቸው ያለውንም ሁሉ በስድስት ቀኖች ውስጥ የፈጠረ፣ ከዚያም በዐርሹ ላይ (ለሱ ክብር በምስማማ መልኩ) የተደላደለ አልረሕማን ነው፡፡ ከእርሱም ዐዋቂን ጠይቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,60,'25:60','ለእነርሱም ለአልረሕማን ስገዱ በተባሉ ጊዜ አልረሕማን ማነው ለምታዘን (እና ለማናውቀው) እንሰግዳለን ይላሉ፡፡ (ይህ) መራቅንም ጨመራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,61,'25:61','ያ በሰማይ ቡሩጆችን ያደረገና በእርሷም አንጸባራቂን (ፀሐይ) አብሪ ጨረቃንም ያደረገ ጌታ ክብሩ በጣም ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,62,'25:62','እርሱም ያ (ያመለጠውን ሥራ) ማስታወስን ለሚፈልግ ወይም ማመስገንን ለሚፈልግ ሰው ሌሊትንና ቀንን ተተካኪ ያደረገ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,63,'25:63','የአልረሕማንም ባሮች እነዚያ በምድር ላይ በዝግታ የሚኼዱት፣ ባለጌዎችም (በክፉ) ባነጋገሩዋቸው ጊዜ ሰላም የሚሉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,64,'25:64','እነዚያም ለጌታቸው በግንባራቸው ተደፊዎችና ቋሚዎች ኾነው የሚያድሩት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,65,'25:65','እነዚያም «ጌታችን ሆይ! የገሀነምን ቅጣት ከእኛ ላይ መልስልን ቅጣቷ የማይለቅ ነውና» የሚሉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,66,'25:66','እርሷ መርጊያና መቀመጫ በመኾን ከፋች! (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(25,67,'25:67','እነዚያም በለገሱ ጊዜ የማያባክኑ የማይቆጥቡትም ናቸው፡፡ በዚህም መካከል (ልግስናቸው) ትክክለኛ የኾነ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,68,'25:68','እነዚያም ከአላህ ጋር ሌላን አምላክ የማይገዙት፣ ያችንም አላህ እርም ያደረጋትን ነፍስ ያለ ሕግ የማይገድሉት፣ የማያመነዝሩትም ናቸው፡፡ ይህንንም የሚሠራ ሰው ቅጣትን ያገኛል፡፡');
INSERT INTO "sadiq_sani" VALUES(25,69,'25:69','በትንሣኤ ቀን ቅጣቱ ለእርሱ ይደራረባል፡፡ በእርሱም ውስጥ የተዋረደ ኾኖ ይኖራል፡፡');
INSERT INTO "sadiq_sani" VALUES(25,70,'25:70','ተጸጽቶ የተመለሰና ያመነ መልካምንም ሥራ የሠራ ሰው ብቻ ሲቀር፡፡ እነዚያም አላህ መጥፎ ሥራዎቻቸውን በመልካም ሥራዎች ይለውጣል፡፡ አላህም እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,71,'25:71','ተጸጽቶ የተመለሰም ሰው መልካምንም የሠራ እርሱ የተወደደን መመለስ ወደ አላህ ይመለሳል፡፡');
INSERT INTO "sadiq_sani" VALUES(25,72,'25:72','እነዚያም እብለትን የማይመሰክሩ፤ በውድቅ ቃልም (ተናጋሪ አጠገብ) ባለፉ ጊዜ የከበሩ ኾነው የሚያልፉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,73,'25:73','እነዚያም በጌታቸው አንቀጾች በተገሰጹ ጊዜ (የተረዱ ተቀባዮች ኾነው እንጂ) ደንቆሮዎችና ዕውሮች ኾነው በእርሷ ላይ የማይደፉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,74,'25:74','እነዚያም «ጌታችን ሆይ! ከሚስቶቻችንና ከዘሮቻችን ለዓይኖች መርጊያን ለእኛ ስጠን፡፡ አላህን ለሚፈሩትም መሪ አድርገን» የሚሉት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(25,75,'25:75','እነዚያ በመታገሳቸው የገነትን ሰገነቶች ይምመነዳሉ፡፡ በእርሷም ውስጥ ውዳሴንና ሰላምታን ይስሰጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(25,76,'25:76','በእርሷ ውስጥ ዘውታሪዎች ኾነው (ይስሰጣሉ)፡፡ መርጊያና መኖሪያይቱ አማረች፡፡');
INSERT INTO "sadiq_sani" VALUES(25,77,'25:77','«ጸሎታችሁ ባልነበረ ኖሮ ጌታዬ እናንተን ከምንም አይቆጥራችሁም ነበር፡፡ በእርግጥም አስተባበላችሁ፤ ወደ ፊትም (ቅጣቱ) ያዣችሁ ይኾናል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,1,'26:1','ጠ.ሰ.መ. (ጧ ሲን ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,2,'26:2','ይህች ግልጽ ከኾነው መጽሐፍ አንቀጾች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,3,'26:3','አማኞች ባለመኾናቸው፤ (በቁጭት) ነፍስህን ገዳይ መኾን ይፈራልሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(26,4,'26:4','ብንሻ በእነሱ ላይ ከሰማይ ተዓምረን እናወርድና አንገቶቻቸው (መሪዎቻቸው) ለእርሷ ተዋራጆች ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,5,'26:5','ከአልረሕማንም ዘንድ ኣዲስ የተወረደ ቁርኣን አይመጣላቸውም፤ ከእርሱ የሚሸሹ ቢኾኑ እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,6,'26:6','በእርግጥም አስተባበሉ፡፡ የዚያም በእርሱ ይሳለቁበት የነበሩት ወሬዎች (ፍጻሜ) ይመጣባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(26,7,'26:7','ወደ ምድርም በውስጧ ከመልካም (በቃይ) ጎሳ ሁሉ ብዙን እንዳበቀልን አላዩምን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,8,'26:8','በዚህ አስደናቂ ምልክት አልለበት፡፡ አብዛኞቻቸውም አማኞች አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,9,'26:9','ጌታህም እርሱ በእርግጥ አሸናፊ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,10,'26:10','ጌታህም ሙሳን «ወደ በደለኞቹ ሕዝቦች ኺድ» በማለት በጠራው ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,11,'26:11','«ወደ ፈርዖን ሕዝቦች (ኺድ) አላህን አይፈሩምን» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,12,'26:12','(ሙሳም) አለ «ጌታዬ ሆይ! እኔ ሊያስተባብሉኝ እፈራለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,13,'26:13','«ልቤም ይጠብባል፡፡ ምላሴም አይፈታም፡፡ ስለዚህ ወደ ሃሩን ላክ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,14,'26:14','«ለእነርሱም በእኔ ላይ (የደም) ወንጀል አልለ፡፡ ስለዚህ እንዳይገድሉኝ እፈራለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,15,'26:15','(አላህ) አለ «ተው! (አይነኩህም)፡፡ በተዓምራቶቻችንም ኺዱ፡፡ እኛ ከእናንተ ጋር ሰሚዎች ነንና፡፡');
INSERT INTO "sadiq_sani" VALUES(26,16,'26:16','«ወደ ፈርዖንም ኺዱ፡፡ በሉትም፡- እኛ የዓለማት ጌታ መልክተኞች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,17,'26:17','«የእስራኤልን ልጆች ከእኛ ጋር ልቀቅ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,18,'26:18','(ፈርዖንም) አለ «ልጅ ኾነህ በእኛ ውስጥ አላሳደግንህምን በእኛ ውስጥም ከዕድሜህ ብዙ ዓመታትን አልተቀመጥክምን');
INSERT INTO "sadiq_sani" VALUES(26,19,'26:19','«ያችንም የሠራሃትን ሥራህን አልሠራህምን አንተም ከውለታ ቢሶቹ ነህ» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,20,'26:20','(ሙሳም) አለ «ያን ጊዜ እኔም ከተሳሳቱት ኾኜ ሠራኋት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,21,'26:21','«በፈራኋችሁም ጊዜ ከእንናተ ሸሸሁ፡፡ ጌታየም ለእኔ ጥበብን ሰጠኝ፡፡ ከመልክተኞቹም አደረገኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,22,'26:22','«ይህችም የእስራኤልን ልጆች ባሪያ በማድረግህ በእኔ ላይ የምትመጻደቅባት ጸጋ ነት፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,23,'26:23','ፈርዖን አለ «(ላከኝ የምትለው) የዓለማትም ጌታ ምንድነው»');
INSERT INTO "sadiq_sani" VALUES(26,24,'26:24','(ሙሳ) «የሰማያትና የምድር በመካከላቸውም ያለው ሁሉ ጌታ ነው፡፡ የምታረጋግጡ ብትኾኑ (ነገሩ ይህ ነው)፤» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,25,'26:25','(ፈርዖንም) በዙሪያው ላሉት ሰዎች «አትሰሙምን» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,26,'26:26','(ሙሳ) «ጌታችሁና የመጀመሪያዎቹ አባቶቻችሁም ጌታ ነው» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,27,'26:27','(ፈርዖን)');
INSERT INTO "sadiq_sani" VALUES(26,28,'26:28','(ሙሳ) «የምሥራቅና የምዕራብ በመካከላቸውም ያለው ነገር ሁሉ ጌታ ነው፡፡ ታውቁ እንደኾናችሁ (እመኑበት)» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,29,'26:29','(ፈርዖን) «ከእኔ ሌላ አምላክን ብትይዝ በውነቱ ከእስረኞቹ አደርግሃለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,30,'26:30','(ሙሳ) «በግልጽ አስረጅ ብመጣህም እንኳ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,31,'26:31','«እንግዲያውስ ከእውነተኞች እንደኾንክ (አስረጁን) አምጣው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,32,'26:32','በትሩንም ጣለ፡፡ እርሷም ወዲያውኑ ግልጽ እባብ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,33,'26:33','እጁንም አወጣ፡፡ ወዲያውም እርሷ ለተመልካቾች (የምታበራ) ነጭ ኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,34,'26:34','(ፈርዖን) በዙሪያው ላሉት መማክርት «ይህ በእርግጥ ዐዋቂ ድግምተኛ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,35,'26:35','«ከምድራችሁ በድግምቱ ሊያወጣችሁ ይፈልጋልና ምንን ታዛላችሁ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,36,'26:36','አሉት «እርሱን ወንድሙንም አቆይና በየከተሞቹ ሰብሳቢዎችን ላክ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,37,'26:37','«በጣም ዐዋቂ ድግምተኞችን ሁሉ ያመጡልሃልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,38,'26:38','ድግምተኞቹም በታወቀ ቀን ቀጠሮ ተሰበሰቡ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,39,'26:39','ለሰዎቹም «እናንተ ተሰብስባችኋልን» ተባለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,40,'26:40','«ድግምተኞቹን እነሱ አሸናፊዎች ቢኾኑ እንከተል ዘንድ» (ተባለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,41,'26:41','«ድግምተኞቹም በመጡ ጊዜ ለፈርዖን እኛ አሸናፊዎች ብንሆን ለእኛ በእርግጥ ዋጋ አለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,42,'26:42','«አዎን፤ እናንተም ያን ጊዜ ከባለሟሎቹ ትኾናላችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,43,'26:43','ሙሳ «ለእነርሱ እናንተ የምትጥሉትን ጣሉ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,44,'26:44','ገመዶቻቸውንና በትሮቻቸውንም ጣሉ፡፡ «በፈርዖንም ክብር ይኹንብን፡፡ እኛ በእርግጥ አሸናፊዎቹ እኛ ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,45,'26:45','ሙሳም በትሩን ጣለ፡፡ ወዲያውም እርሷ የሚያስመስሉትን ሁሉ ትውጣለች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,46,'26:46','ድግምተኞቹም ሰጋጆች ኾነው ወደቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,47,'26:47','(እነሱም) አሉ «በዓለማት ጌታ አመንን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,48,'26:48','«በሙሳና በሃሩን ጌታ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,49,'26:49','(ፈርዖንም) «ለእናንተ ሳልፈቅድላችሁ በፊት ለእርሱ አመናችሁን እርሱ ያ ድግምትን ያስተማራችሁ ታላቃችሁ ነው፡፡ ወደፊትም (የሚያገኛችሁን) በእርግጥ ታውቃላችሁ፡፡ እጆቻችሁንና እግሮቻችሁን (ቀኝና ግራን) በማናጋት እቆረርጣለሁ፡፡ ሁላችሁንም እሰቅላችኋለሁም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,50,'26:50','(እነርሱም) አሉ «ጉዳት የለብንም፡፡ እኛ ወደ ጌታችን ተመላሾች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,51,'26:51','«እኛ የምእምናን መጀመሪያ በመኾናችን ጌታችን ኀጢአቶቻችንን ለእኛ ሊምር እንከጅላለን፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,52,'26:52','ወደ ሙሳም «ባሮቼን ይዘህ ሌሊት ኺድ፡፡ እናንተ የሚከተሉዋችሁ ናችሁና» ስንል ላክን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,53,'26:53','ፈርዖንም በየከተሞቹ ሰብሳቢዎችን (እንዲህ ሲል) ላከ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,54,'26:54','«እነዚህ ጥቂቶች ጭፈሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,55,'26:55','«እነርሱም ለእኛ አስቆጪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,56,'26:56','«እኛም ብዙዎች ጥንቁቆች ነን፤» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,57,'26:57','አወጣናቸውም፡፡ ከአትክልቶችና ከምንጮች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,58,'26:58','ከድልቦችም ከመልካም መቀመጫዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,59,'26:59','እንደዚሁ ለእስራኤል ልጆች አወረስናትም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,60,'26:60','ፀሐይዋ ስትወጣም ተከተሉዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,61,'26:61','ሁለቱ ጭፍሮችም በተያዩ ጊዜ የሙሳ ጓዶች «እኛ(የፈርዖን ሰዎች) የሚደርሱብን ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,62,'26:62','(ሙሳ) «ተዉዉ! ጌታዬ ከእኔ ጋር ነው፡፡ በእርግጥ ይመራኛል» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,63,'26:63','ወደ ሙሳም «ባሕሩን በበትርህ ምታው» ስንል ላክንበት፡፡ (መታውና) ተከፈለም፡፡ ክፍሉም ሁሉ እንደ ታላቅ ጋራ ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,64,'26:64','እዚያም ዘንድ ሌሎችን አቀረብን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,65,'26:65','ሙሳንም ከእርሱ ገር ያሉትንም ሰዎች ሁሉንም አዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,66,'26:66','ከዚያም ሌሎቹን አሰጠምን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,67,'26:67','በዚህ ውስጥ ታላቅ ታምር አለበት፡፡ አብዛኞቻቸውም አማኞች አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,68,'26:68','ጌታህም እርሱ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,69,'26:69','በእነሱም (በሕዝቦችህ) ላይ የኢብራሂምን ወሬ አንብብላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,70,'26:70','ለአባቱና ለሕዝቦቹ «ምንን ትግገዛላችሁ» ባለ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,71,'26:71','«ጣዖታትን እንገዛለን፤ እርሷንም በመገዛት ላይ እንቆያለን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,72,'26:72','(እርሱም) አለ «በጠራችኋቸው ጊዜ ይሰሟችኋልን');
INSERT INTO "sadiq_sani" VALUES(26,73,'26:73','«ወይስ ይጠቅሟችኋልን ወይስ ይጎዷችኋልን»');
INSERT INTO "sadiq_sani" VALUES(26,74,'26:74','«የለም! አባቶቻችን እንደዚሁ ሲሠሩ አገኘን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,75,'26:75','«ትግገዙት የነበራችሁትን አስተዋላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,76,'26:76','«እናንተም የቀድሞዎቹ አባቶቻችሁም (የተግገዛችሁትን)፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,77,'26:77','«እነሱም (ጣዖቶቹ) ለእኔ ጠላቶች ናቸው፡፡ ግን የዓለማት ጌታ ሲቀር (እርሱ ወዳጄ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,78,'26:78','«(እርሱ) ያ የፈጠረኝ ነው፡፡ እርሱም ይመራኛል፡፡');
INSERT INTO "sadiq_sani" VALUES(26,79,'26:79','«ያም እርሱ የሚያበላኝና የሚያጠጣኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,80,'26:80','«በታመምኩም ጊዜ እርሱ ያሽረኛል፡፡');
INSERT INTO "sadiq_sani" VALUES(26,81,'26:81','«ያም የሚገድለኝ ከዚያም ሕያው የሚያደርገኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,82,'26:82','ያም በፍርዱ ቀን ኀጢአቴን ለእኔ ሊምር የምከጅለው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,83,'26:83','ጌታዬ ሆይ! ዕውቀትን ስጠኝ! በደጋጎቹም ሰዎች አስጠጋኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,84,'26:84','በኋለኞቹም ሕዝቦች ውስጥ ለእኔ መልካም ዝናን አድርግልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,85,'26:85','የጸጋይቱን ገነት ከሚወርሱትም አድርገኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,86,'26:86','ለአባቴም ማር፡፡ እርሱ ከተሳሳቱት ነበረና፡፡');
INSERT INTO "sadiq_sani" VALUES(26,87,'26:87','በሚቀሰቀሱበት ቀንም አታዋርደኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,88,'26:88','ገንዘብም ልጆችም በማይጠቅሙበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,89,'26:89','ወደ አላህ በንጹህ ልብ የመጣ ሰው ቢኾን እንጅ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,90,'26:90','ገነትም ለፈሪዎች በምትቀረብበት (ቀን)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,91,'26:91','ገሀነምም ለጠመሞች በምትገለጽበት (ቀን)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,92,'26:92','ለእነሱም በሚባል ቀን «ትግገዟቸው የነበራችሁት የት ናቸው»');
INSERT INTO "sadiq_sani" VALUES(26,93,'26:93','«ከአላህ ሌላ ሲኾኑ ይረዱዋችኋልን ወይስ (ለራሳቸው) ይርረዳሉን»');
INSERT INTO "sadiq_sani" VALUES(26,94,'26:94','በውስጧም እነሱና ጠማሞቹ በፊቶቻቸው ተጥለው ይንከባለላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,95,'26:95','የሰይጣንም ሰራዊቶች መላውም (ይጣላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,96,'26:96','እነርሱም በእርሷ ውስጥ የሚከራከሩ ኾነው ይላሉ፡-');
INSERT INTO "sadiq_sani" VALUES(26,97,'26:97','በአላህ እንምላለን፤ እኛ በግልጽ መሳሳት ውስጥ ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,98,'26:98','(ጣዖቶቹን) በዓለማት ጌታ ባስተካከልናችሁ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,99,'26:99','አመጸኞቹም እንጅ ሌላ አላሳሳተንም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,100,'26:100','ከአማላጆችም ለእኛ ምንም የለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,101,'26:101','አዛኝ ወዳጂም (የለንም)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,102,'26:102','ለእኛም አንዲት ጊዜ መመለስ በኖረችንና ከአማኞቹ በኾን እንመኛለን (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,103,'26:103','በዚህ ውስጥ አስደናቂ ግሳጼ አልለበት፡፡ አብዛኞቻቸውም አማኞች አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,104,'26:104','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,105,'26:105','የኑሕ ሕዝቦች መልክተኞችን አስተባበሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,106,'26:106','ወንድማቸው ኑሕ ለእነሱ ባላቸው ጊዜ «አትጠነቀቁምን');
INSERT INTO "sadiq_sani" VALUES(26,107,'26:107','«እኔ ለእናንተ ታማኝ መልክተኛ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,108,'26:108','«አላህንም ፍሩ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,109,'26:109','«በእርሱም ላይ ምንም ዋጋ አልጠይቃችሁም፡፡ ዋጋዬ በዓለማት ጌታ ላይ እንጅ በሌላ ላይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,110,'26:110','«አላህንም ፍሩ፤ ታዘዙኝም፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,111,'26:111','(እነርሱም) «ወራዶቹ የተከተሉህ ኾነህ ለአንተ እናምናለን» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,112,'26:112','(እርሱም) አላቸው «ይሠሩት በነበሩት ነገር ምን ዕውቀት አለኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,113,'26:113','«ምርመራቸው በጌታዬ ላይ እንጅ በሌላ ላይ አይደለም፡፡ ብታውቁ ኖሮ (ይህንን ትርረዱ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,114,'26:114','«እኔም አማኞችን አባራሪ አይደለሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,115,'26:115','«እኔ ግልጽ አስፈራሪ እንጅ ሌላ አይደለሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,116,'26:116','«ኑሕ ሆይ! (ከምትለው) ባትከለከል በእርግጥ ከሚወገሩት ትኾናለህ» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(26,117,'26:117','(እርሱም) አለ «ጌታዬ ሆይ! ሕዝቦቼ አስተባበሉኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,118,'26:118','«በእኔና በእነርሱም መካከል (ተገቢ) ፍርድን ፍረድ፡፡ አድነኝም፡፡ ከእኔ ጋር ያሉትንም ምእምናን፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,119,'26:119','እርሱንም ከእርሱ ጋር ያለውንም ሁሉ በተመላው መርከብ ውስጥ አዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,120,'26:120','ከዚያም በኋላ ቀሪዎቹን አሰጠምን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,121,'26:121','በዚህ ውስጥ ታላቅ ግሳጼ አለበት፡፡ አብዛኞቻቸውም አማኞች አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,122,'26:122','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,123,'26:123','ዓድ መልክተኞችን አስተባበለች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,124,'26:124','ወንድማቸው ሁድ ለነርሱ ባላቸው ጊዜ «አትጠነቀቁምን');
INSERT INTO "sadiq_sani" VALUES(26,125,'26:125','«እኔ ለናንተ ታማኝ መልክተኛ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,126,'26:126','«አላህንም ፍሩ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,127,'26:127','«በእርሱም ላይ ምንም ዋጋ አለምናችሁም፡፡ ዋጋዬ በዓለማት ጌታ ላይ እንጅ በሌላ ላይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,128,'26:128','«የምትጫወቱ ኾናችሁ በከፍተኛ ስፍራ ሁሉ ላይ ምልክትን ትገነባላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,129,'26:129','«የውሃ ማጠራቀሚያዎችንና ሕንፃዎችንም ዘላለም መኖርን የምትከጅሉ ኾናችሁ ትሠራላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,130,'26:130','«በቀጣችሁም ጊዜ ጨካኞች ኾናችሁ ትቀጣላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,131,'26:131','«አላህንም ፍሩ፤ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,132,'26:132','«ያንንም በምታውቁት (ጸጋ) ያጣቀማችሁን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,133,'26:133','«በእንስሳዎችና በልጆች ያጣቀማችሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,134,'26:134','«በአትክልቶችና በምንጮችም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,135,'26:135','«እኔ በእናንተ ላይ የታላቅን ቀን ቅጣት እፈራለሁና፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,136,'26:136','(እነርሱም) አሉ «ብትገስጽም ወይም ከገሳጮቹ ባትኾንም በእኛ ላይ እኩል ነው፤ (ያለንበትን አንለቅም)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,137,'26:137','«ይህ የፊተኞቹ ሰዎች ፀባይ እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,138,'26:138','«እኛም የምንቅቀጣ አይደለንም» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,139,'26:139','አስተባበሉትም፡፡ አጠፋናቸውም፡፡ በዚህም ውስጥ እርግጠኛ ግሳጼ አለበት፡፡ አብዛኞቻቸውም ምእመናን አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,140,'26:140','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,141,'26:141','ሰሙድ መልክተኞችን አስተባበለች፡፡');
INSERT INTO "sadiq_sani" VALUES(26,142,'26:142','ወንድማቸው ሷሊህ ለእነርሱ ባላቸው ጊዜ «አትጠነቀቁምን');
INSERT INTO "sadiq_sani" VALUES(26,143,'26:143','«እኔ ለእናንተ ታማኝ መልክተኛ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,144,'26:144','«አላህንም ፍሩ፤ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,145,'26:145','«በእርሱም ላይ ምንም ዋጋ አልለምናችሁም፡፡ ዋጋዬ በዓለማት ጌታ ላይ እንጅ በሌላ ላይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,146,'26:146','«በዚያ እዚህ ባለው (ጸጋ) ውስጥ የረካችሁ ኾናችሁ ትተዋላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,147,'26:147','«በአትክልቶችና በምንጮች ውስጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,148,'26:148','«በአዝመራዎችም ፍሬዋ የበሰለ በኾነች ዘንባባም፤ (ውስጥ ትተዋላችሁን)');
INSERT INTO "sadiq_sani" VALUES(26,149,'26:149','«ብልሆች ኾናችሁ ከጋራዎች ቤቶችንም ትጠርባላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,150,'26:150','«አላህንም ፍሩ፤ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,151,'26:151','«የወሰን አላፊዎችንም ትዕዛዝ አትከተሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,152,'26:152','«የእነዚያን በምድር ላይ የሚያጠፉትንና የማያበጁትን፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,153,'26:153','(እነሱም) አሉ «አንተ በብዙ ከተደገመባቸው ሰዎች ብቻ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,154,'26:154','«አንተ ብጤያችን ሰው እንጅ ሌላ አይደለህም፡፡ ከእውነተኞቹም እንደኾንክ ተዓምርን አምጣ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,155,'26:155','(እርሱም) አለ «ይህች ግመል ናት፡፡ ለእርሷ የመጠጥ ፋንታ አላት፡፡ ለእናንተም የታወቀ ቀን ፋንታ አላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,156,'26:156','«በክፉም አትንኳት፡፡ የታላቅ ቀን ቅጣት ይይዛችኋልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,157,'26:157','ወጓትም፡፡ ወዲያውም ተጸጻቾች ኾነው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,158,'26:158','ቅጣቱም ያዛቸው፡፡ በዚህም ውስጥ ግሳፄ አልለበት፡፡ አብዛኞቻቸውም ምእመናን አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,159,'26:159','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,160,'26:160','የሉጥ ሕዝቦች መልክተኞችን አስተባባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,161,'26:161','ወንድማቸው ሉጥ ለእነርሱ ባለ ጊዜ «አትጠነቀቁምን');
INSERT INTO "sadiq_sani" VALUES(26,162,'26:162','«እኔ ለእናንተ ታማኝ መልክተኛ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,163,'26:163','«አላህንም ፍሩ፤ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,164,'26:164','«በእርሱም ላይ ምንም ዋጋ አልለምናችሁም፡፡ ዋጋዬ በዓለማት ጌታ ላይ እንጅ በሌላ ላይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,165,'26:165','«ከዓለማት ሰዎች ወንዶችን ትመጣላችሁን');
INSERT INTO "sadiq_sani" VALUES(26,166,'26:166','«ከሚስቶቻችሁም ጌታችሁ ለናንተ የፈጠረላችሁን ትተዋላችሁን በእውነቱ እናንተ ወሰን አላፊዎች ሕዝቦ ናችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,167,'26:167','(እነርሱም) አሉ «ሉጥ ሆይ! ባትከለከል በእርግጥ (ከአገር) ከሚወጡት ትኾናለህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,168,'26:168','(እርሱም) አለ «እኔ ሥራችሁን ከሚጠሉት ሰዎች ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,169,'26:169','«ጌታዬ ሆይ! እኔንም ቤተሰቦቼንም ከሚሠሩት ሥራ (ቅጣት) አድነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,170,'26:170','እርሱንም ቤተሰቦቹንም በጠቅላላ አዳንናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,171,'26:171','በቀሪዎቹ ውስጥ የኾነች አሮጊት ብቻ ስትቀር (እርሷ ጠፋች)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,172,'26:172','ከዚያም ሌሎቹን አጠፋን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,173,'26:173','በእነሱም ላይ (የድንጋይን) ዝናምን አዘነምንባቸው፡፡ የተስፈራሪዎቹም ዝናም (ምንኛ) ከፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,174,'26:174','በዚህ ውስጥ አስደናቂ ተዓምር አልለበት፡፡ አብዛኞቻቸውም ምእመናን አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,175,'26:175','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,176,'26:176','የአይከት ሰዎች መልክተኞችን አስተባበሉ፤');
INSERT INTO "sadiq_sani" VALUES(26,177,'26:177','ሹዕይብ ለእነርሱ ባላቸው ጊዜ «አትጠነቀቁምን');
INSERT INTO "sadiq_sani" VALUES(26,178,'26:178','«እኔ ለእናንተ ታማኝ መልክተኛ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,179,'26:179','«አላህንም ፍሩ፤ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,180,'26:180','«በርሱም ላይ ምንም ዋጋ አልጠይቃችሁም፡፡ ዋጋዬ በዓለማት ጌታ ላይ እንጅ በሌላ ላይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,181,'26:181','«ስፍርን ሙሉ፡፡ ከአጉዳዮቹም አትኹኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,182,'26:182','«በትክክለኛ ሚዛንም መዝኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,183,'26:183','«ሰዎችንም ነገሮቻቸውን አታጉድሉባቸው፡፡ በምድርም ላይ አጥፊዎች ኾናችሁ አታበላሹ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,184,'26:184','«ያንንም የፈጠራችሁን የቀድሞዎቹንም ፍጡሮች (የፈጠረውን) ፍሩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,185,'26:185','አሉ «አንተ በብዛት ከተደገመባቸው ሰዎች ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,186,'26:186','«አንተም ብጤያችን ሰው እንጅ ሌላ አይደለህም፡፡ እነሆ ከውሸታሞች ነህ ብለን እንጠረጥርሃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(26,187,'26:187','«ከእውነተኞቹም እንደኾንክ በእኛ ላይ ከሰማይ ቁራጭን ጣልብን፡፡»');
INSERT INTO "sadiq_sani" VALUES(26,188,'26:188','«ጌታዬ የምትሠሩትን ሁሉ ዐዋቂ ነው፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,189,'26:189','አስተባበሉትም፡፡ የጥላይቷ ቀን ቅጣትም ያዛቸው፡፡ እርሱ የከባድ ቀን ቅጣት ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(26,190,'26:190','በዚህ ውስጥ አስደናቂ ተዓምር አልለበት፡፡ አብዛኞቻቸውም ምእምናን አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,191,'26:191','ጌታህም እርሱ በእርግጥ አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,192,'26:192','እርሱም (ቁርኣን) በእርግጥ ከዓለማት ጌታ የተወረደ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,193,'26:193','እርሱን ታማኙ መንፈስ (ጂብሪል) አወረደው፤');
INSERT INTO "sadiq_sani" VALUES(26,194,'26:194','ከአስፈራሪዎቹ (ነቢያት) ትኾን ዘንድ በልብህ ላይ (አወረደው)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,195,'26:195','ግልጽ በኾነ ዐረብኛ ቋንቋ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,196,'26:196','እርሱም (ቁርኣን) በቀድሞዎቹ መጻሐፍት ውስጥ (የተወሳ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,197,'26:197','የእስራኤል ልጆች ሊቃውንት የሚያውቁት መኾኑ ለእነርሱ (ለመካ ከሓዲዎች) ምልክት አይኾናቸውምን');
INSERT INTO "sadiq_sani" VALUES(26,198,'26:198','ከአዕጀሞች ባንዱ ላይ ባወረድነውም ኖሮ፤');
INSERT INTO "sadiq_sani" VALUES(26,199,'26:199','በእነርሱ ላይ ባነበበውም በእርሱ አማኞች አይኾኑም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(26,200,'26:200','እንደዚሁ (ማስተባበልን) በተንኮለኞች ልቦች ውስጥ አስገባነው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,201,'26:201','አሳማሚ ቅጣትን እስከሚያዩ ድረስ በእርሱ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,202,'26:202','እነርሱ የማያውቁ ሲኾኑ (ቅጣቱ) ድንገት እስከሚመጣባቸውም ድረስ (አያምኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,203,'26:203','(በመጣባቸው ጊዜ) «እኛ የምንቆይ ነን» እስከሚሉም (አያምኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,204,'26:204','በቅጣታችን ያቻኩላሉን');
INSERT INTO "sadiq_sani" VALUES(26,205,'26:205','አየህን ብዙ ዓመታትን ብናጣቅማቸው፤');
INSERT INTO "sadiq_sani" VALUES(26,206,'26:206','ከዚያም ያ ይስፈራሩበት የነበሩት ቢመጣባቸው፤');
INSERT INTO "sadiq_sani" VALUES(26,207,'26:207','ይጣቀሙበት የነበሩት ሁሉ ከእነሱ ምንም አያብቃቃቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,208,'26:208','አንዲትንም ከተማ ለእርሷ አስፈራሪዎች ያሏት (እና ያስተባበለች) ኾና እንጅ አላጠፋንም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,209,'26:209','(ይህች) ግሳፄ ናት፡፡ በዳዮችም አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,210,'26:210','ሰይጣናትም እርሱን (ቁርኣንን) አላወረዱትም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,211,'26:211','ለእነርሱም አይገባቸውም፤ አይችሉምም፡፡');
INSERT INTO "sadiq_sani" VALUES(26,212,'26:212','እነርሱ (የመላእክትን ንግግር) ከመስማት ተከለከሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,213,'26:213','ከአላህም ጋር ሌላን አምላክ አትግገዛ፤ ከሚቀጡት ትኾናለህና፡፡');
INSERT INTO "sadiq_sani" VALUES(26,214,'26:214','ቅርቦች ዘመዶችህንም አስጠንቅቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,215,'26:215','ከምእምናንም ለተከተሉህ ሰዎች ክንፍህን ዝቅ አድርግ፤ (ልዝብ ኹን)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,216,'26:216','«እንቢ» ቢሉህም «እኔ ከምትሠሩት ንጹሕ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,217,'26:217','አሸናፊ አዛኝ በኾነው (ጌታህ) ላይም ተጠጋ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,218,'26:218','በዚያ ለሶላት በምትቆም ጊዜ በሚያይህ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,219,'26:219','በሰጋጆች ውስጥ መዘዋወርህንም (በሚያየው ጌታህ ላይ ተጠጋ)፡፡');
INSERT INTO "sadiq_sani" VALUES(26,220,'26:220','እነሆ እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(26,221,'26:221','ሰይጣናት በማን ላይ እንደሚወርዱ ልንገራችሁን');
INSERT INTO "sadiq_sani" VALUES(26,222,'26:222','በውሸታም ኀጢአተኛ ሁሉ ላይ ይወርዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(26,223,'26:223','የሰሙትን (ወደ ጠንቋዮች) ይጥላሉ፡፡ አብዛኞቻቸውም ውሸታሞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(26,224,'26:224','ባለ ቅኔዎችንም ጠማማዎቹ ይከተሉዋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(26,225,'26:225','እነርሱ በ(ንግግር) ሸለቆ ሁሉ የሚዋልሉ መኾናቸውን አታይምን');
INSERT INTO "sadiq_sani" VALUES(26,226,'26:226','እነሱም የማይሠሩትን ነገር የሚናገሩ መኾናቸውን (አታይምን)፤');
INSERT INTO "sadiq_sani" VALUES(26,227,'26:227','እነዚያ ያመኑና መልካም ሥራዎችን የሠሩ አላህንም በብዛት ያወሱ ከተበደሉም በኋላ (በቅኔ) የተከላከሉ ሲቀሩ፤ (እነርሱ አይወቀሱም)፡፡ እነዚያም የበደሉ (ከሞቱ በኋላ) እንዴት ያለን መመለስ እንደሚመለሱ በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,1,'27:1','ጠ.ሰ (ጣ ሲን) ይህቺ ከቁርኣኑና ከገላጩ መጽሐፍ አንቀጾች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,2,'27:2','ለምእምናን መሪና ብስራት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,3,'27:3','ለእነዚያ ሶላትን ለሚሰግዱት ዘካንም ለሚሰጡት እነሱም በመጨረሻይቱ ሕይወት እነሱ የሚያረጋግጡ ለኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,4,'27:4','እነዚያ በመጨረሻይቱ ዓለም የማያምኑት ለእነሱ (ክፉ) ሥራዎቻቸውን ሸለምንላቸው፡፡ ስለዚህ እነርሱ ይዋልላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,5,'27:5','እነርሱ እነዚያ ክፉ ቅጣት ለእነርሱ ያላቸው ናቸው፡፡ እነርሱም በመጨረሻይቱ ዓለም እነርሱ በጣም ከሳሪዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,6,'27:6','አንተም ቁርኣንን ጥበበኛና ዐዋቂ ከኾነው (ጌታህ) ዘንድ በእርግጥ ትስሰጣለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,7,'27:7','ሙሳ ለቤተሰቦቹ «እኔ እሳትን አየሁ፡፡ ከእርሷ ወሬን አመጣላችኋለሁ፡፡ ወይም ትሞቁ ዘንድ የተለኮሰ ችቦን አመጣላችኋለሁ» ባለ ጊዜ (የኾነውን አውሳላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,8,'27:8','በመጣትም ጊዜ እንዲህ በማለት ተጠራ፤ «በእሳቲቱ ያለው ሰውና በዙሪያዋ ያሉም ሁሉ ተባረኩ፡፡ የዐለማትም ጌታ አላህ ጥራት ይገባው፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,9,'27:9','«ሙሳ ሆይ! እነሆ እኔ አሸናፊው ጥበበኛው አላህ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,10,'27:10','«በትርህንም ጣል» (ተባለ ጣለም)፡፡ እርሷ እንደ ትንሽ እባብ በፍጥነት ስትስለከለክ ባያትም ጊዜ፤ ወደ ኋላ ዞሮ ሸሸ፡፡ አልተመለሰምም፡፡ «ሙሳ ሆይ! አትፈራ፤ እኔ መልክተኞቹ እኔ ዘንድ አይፈሩምና፡፡');
INSERT INTO "sadiq_sani" VALUES(27,11,'27:11','«ግን የበደለ ሰው ከዚያም ከመጥፎ ሥራው በኋላ መልካምን የለወጠ እኔ መሓሪ አዛኝ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,12,'27:12','«እጅህንም በአንገትጌህ ውስጥ አግባ፡፡ ያለነውር (ያለ ለምጽ) ነጭ ኾና ትወጣለችና፡፡ በዘጠኝ ተዓምራት ወደ ፈርዖንና ወደ ሕዝቦቹ (ኺድ)፡፡ እነርሱ አመጸኞች ሕዝቦች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(27,13,'27:13','ተዓምራታችንም ግልጽ ኾና በመጣቻቸው ጊዜ «ይህ ግልጽ ድግምት ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,14,'27:14','ነፍሶቻቸውም ያረጋገጧት ሲኾኑ ለበደልና ለኩራት በእርሷ ካዱ፡፡ የአመጸኞችም ፍጻሜ እንዴት እንደ ነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,15,'27:15','ለዳውድና ለሱለይማንም ዕውቀትን በእርግጥ ሰጠናቸው፡፡ አሉም፡- «ምስጋና ለአላህ ለእዚያ ከምእምናን ባሮቹ በብዙዎቹ ላይ ላበለጠን ይገባው፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,16,'27:16','ሱለይማንም ዳውድን ወረሰ፡፡ አለም «ሰዎች ሆይ! የበራሪን ንግግር (የወፍን ቋንቋ) ተስተማርን፡፡ ከነገሩም ሁሉ ተሰጠን፡፡ ይህ እርሱ በእርግጥ ግልጽ የኾነ ችሮታ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,17,'27:17','ለሱለይማንም ሰራዊቶቹ ከጋኔን፣ ከሰውም፣ ከበራሪም የኾኑት ተሰበሰቡ፡፡ እነሱም ይከመከማሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,18,'27:18','በጉንዳንም ሸለቆ በመጡ ጊዜ አንዲት ጉንዳን «እናንተ ጉንዳኖች ሆይ! ወደ መኖሪያዎቻችሁ');
INSERT INTO "sadiq_sani" VALUES(27,19,'27:19','ከንግግርዋም እየሳቀ ጥርሱን ፈገግ አደረገ፡፡ «ጌታዬ ሆይ! ያቺን በእኔና በወላጆቼ ላይ የለገስካትን ጸጋህን እንዳመሰግን የምትወደውንም በጎ ሥራ እንድሠራ ምራኝ፡፡ በችሮታህም በመልካሞቹ ባሮችህ ውስጥ አግባኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,20,'27:20','በራሪዎቹንም ተመለከተ፤ አለም «ሁድሁድን ለምን አላየውም! በእውነቱ ከራቁት ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(27,21,'27:21','«ብርቱን ቅጣት በእርግጥ እቀጣዋለሁ፡፡ ወይም በእርግጥ ዐርደዋለሁ፤ ወይም ግልጽ በኾነ አስረጅ ይመጣኛል» (አለ)');
INSERT INTO "sadiq_sani" VALUES(27,22,'27:22','(ወፉ) ዕሩቅ ያልኾነንም ጊዜ ቆዬ፡፡ አለም «ያላወቅከውን ነገር አወቅኩ፡፡ ከሰበእም እርግጠኛን ወሬ አመጣሁልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,23,'27:23','«እኔ የምትገዛቸው የኾኑችን ሴት ከነገሩም ሁሉ የተሰጠችኝ አገኘሁ፡፡ ለእርሷም ታላቅ ዙፋን አልላት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,24,'27:24','«እርስዋንም ሕዝቦችዋንም ከአላህ ሌላ ለፀሐይ ሲሰግዱ አገኘኋቸው፡፡ ሰይጣንም ለእነሱ ሥራዎቻቸውን ሸልሞላቸዋል፡፡ ከእውነተኛውም መንገድ አግዷቸዋል፡፡ ስለዚህ እነሱ (ወደ እውነት) አይምመሩም፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,25,'27:25','ለዚያ በሰማያትና በምድር የተደበቀውን ለሚያወጣው፣ የምትሸሽጉትንና የምትገ ልጹትንም ሁሉ ለሚያውቀው አላህ እንዳይሰግዱ (ሰይጣን አግዷቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,26,'27:26','አላህ ከርሱ ሌላ አምላክ የለም፡፡ የታላቁ ዐርሽ (ዙፋን) ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,27,'27:27','(ሱለይማንም) አለ «እውነት እንደ ተናገርክ ወይም ከውሸታሞቹ እንደ ኾንክ ወደፊት እናያለን፡፡');
INSERT INTO "sadiq_sani" VALUES(27,28,'27:28','«ይህንን ደብዳቤዬን ይዘህ ኺድ፡፡ ወደእነርሱም ጣለው፡፡ ከዚያም ከእነሱ ዘወር በል፡፡ ምን አንደሚመልሱም ተመልከት፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,29,'27:29','(እርሷም በደረሳት ጊዜ) አለች «እናንተ መማክርቶች ሆይ! እኔ ክብር ያለው ጽሑፍ ወደኔ ተጣለ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,30,'27:30','«እርሱ ከሱለይማን ነው፡፡ እርሱም በአላህ ስም እጅግ በጣም ሩኅሩኅ በጣም አዛኝ በኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,31,'27:31','«በእኔ ላይ አትኩሩ፡፡ ታዛዦችም ኾናችሁ ወደኔ ኑ፤ (የሚል ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,32,'27:32','«እናንተ መማክርቶች ሆይ! በነገሬ (የሚበጀውን) ንገሩኝ፡፡ እስከምትገኙልኝ ድረስ አንድንም ነገር ቆራጭ አይደለሁምና» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(27,33,'27:33','«እኛ የኀይል ባለቤቶች የብርቱ ጦርም ባለቤቶች ነን፡፡ ግን ትዕዛዙ ወደ አንቺ ነው፡፡ ምን እንደምታዢም አስተውዪ፤» አሏት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,34,'27:34','(እርሷም) አለች «ንጉሦች ከተማን (በኀይል) በገቡባት ጊዜ ያበላሹዋታል፡፡ የተከበሩ ሰዎችዋንም ወራዶች ያደርጋሉ፡፡ እንደዚሁም (እነዚህ) ይሠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,35,'27:35','«እኔም ወደእነርሱ ገጸ በረከትን የምልክና መልክተኞቹ በምን እንደሚመለሱ የምጠባበቅ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,36,'27:36','(መልክተኛው) ሱለይማንንም በመጣው ጊዜ አለ «በገንዘብ ትረዱኛላችሁን አላህም የሰጠኝ ከሰጣችሁ የበለጠ ነው፡፡ ይልቁንም እናንተ በገጸ በረከታችሁ ትደሰታላችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,37,'27:37','«ወደእነሱ ተመለስ፡፡ ለእነርሱም በእርሷ ችሎታ በሌላቸው ሰራዊት እንመጣባቸዋለን፡፡ ከእርሷም እነርሱ የተዋረዱ ኾነው በእርግጥ እናወጣቸዋለን» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,38,'27:38','«እናንተ መኳንንቶች ሆይ! ሙስሊሞች ኾነው ሳይመጡኝ በፊት ዙፋንዋን የሚያመጣልኝ ማንኛችሁ ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,39,'27:39','ከጋኔን ኀይለኛው «ከችሎትህ ከመነሳትህ በፊት እኔ እርሱን አመጣልሃለሁ፡፡ እኔም በእርሱ ላይ ብርቱ ታማኝ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,40,'27:40','ያ እርሱ ዘንድ ከመጽሐፉ ዕውቀት ያለው ሰው (የተገለጸው) «ዓይንህ ወደ አንተ ከመመለሱ በፊት እኔ እርሱን አመጣልሃለሁ» አለ፤ (እንደዚሁም አደረገ)፡፡ እርሱ ዘንድ ረግቶ ባየውም ጊዜ «ይህ ከጌታዬ ችሮታ ነው፡፡ የማመሰግን ወይም የምክድ መኾኔን ሊሞክረኝ (ቸረልኝ)፡፡ ያመሰገነም ሰው የሚያመሰግነው ለራሱ ነው፡፡ የካደም ሰው ጌታዬ ከእርሱ ተብቃቂ ቸር ነው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,41,'27:41','«ዙፋንዋን ለእርሷ አሳስቱ፡፡ ታውቀው እንደኾነ ወይም ከእነዚያ ከማያወቁት ትኾን እንደሆነ፤ እናያለን፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,42,'27:42','በመጣች ጊዜ፡- «ዙፋንሽ እንደዚህ ነውን» ተባለች፡፡ «እርሱ ልክ እርሱ ነው መሰለኝ» አለች፡፡ (ሱለይማን) «ከእርሷ በፊትም ዕውቀትን ተሰጠን ሙስሊሞችም ነበርን፤» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,43,'27:43','ከአላህ ሌላ ትግገዛው የነበረችውንም ከለከላት፡፡ እርሷ ከከሓዲዎች ሕዝቦች ነበረችና፡፡');
INSERT INTO "sadiq_sani" VALUES(27,44,'27:44','ሕንጻውን ግቢ ተባለች፡፡ ባየቺውም ጊዜ ባሕር ነው ብላ ጠረጠረችው፡፡ ከባቶችዋም ገለጠች፡፡ «እርሱ ከመስተዋት የተለሰለሰ ሕንጻ ነው» አላት፡፡ «ጌታዬ ሆይ! እኔ ነፍሴን በደልኩ፡፡ ከሱለይማንም ጋር ሆኜ ለዓለማት ጌታ ለአላህ ታዘዝኩ» አለች፡፡');
INSERT INTO "sadiq_sani" VALUES(27,45,'27:45','ወደ ሰሙድም ወንድማቸውን ሷሊሕን አላህን ተገዙ በማለት በእርግጥ ላክነው፡፡ ወዲያውም እነሱ የሚነታረኩ ሁለት ጭፍሮች ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,46,'27:46','«ሕዝቦቼ ሆይ! ከመልካሙ በፊት በክፉው (ቅጣትን አምጣብን በማለት) ለምን ታስቸኩላላችሀ ይታዘንላችሁ ዘንድ አላህን ምሕረትን አትለምኑምን» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,47,'27:47','«ባንተና ከአንተም ጋር ባሉት ሰዎች ምክንያት ገደቢሶች ኾን» አሉት፡፡ «ገደቢስነታችሁ አላህ ዘንድ ነው፤ ይልቁንም እናንተ የምትፈተኑ ሕዝቦች ናችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,48,'27:48','በከተማይቱም ውስጥ በምድር ውስጥ የሚያበላሹና የማያሳምሩ ዘጠኝ ሰዎች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,49,'27:49','«እርሱንና ቤተሰቦቹን ሌሊት ልንገድል ከዚያም ለዘመዱ የቤተሰቦቹን ጥፋት (መገደላቸውን) አላየንም፤ እኛም እውነተኞች ነን ልንል በአላህ ተማማሉ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,50,'27:50','ተንኮልንም መከሩ፡፡ እነርሱም የማያወቁ ሲኾኑ በተንኮላቸው አጠፋናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,51,'27:51','የተንኮላቸውም መጨረሻ እንዴት እንደነበረ እኛ እነርሱንም ሕዝቦቻቸውንም በሙሉ እንዴት እንዳጠፋናቸው ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,52,'27:52','እኚህም በበደላቸው ምክንያት ባዶዎች ሲሆኑ ቤቶቻቸው ናቸው፡፡ በዚህም ውስጥ ለሚያወቁ ሕዝቦች አስደናቂ ተዓምር አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,53,'27:53','እነዚያንም ያመኑትንና ከክሕደት ይጠነቀቁ የነበሩትን አዳን፡፡');
INSERT INTO "sadiq_sani" VALUES(27,54,'27:54','ሉጥንም ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፤ «እናንተ የምታዩ ስትኾኑ ፀያፍን ነገር ትሠራላችሁን');
INSERT INTO "sadiq_sani" VALUES(27,55,'27:55','«እናንተ ከሴቶች አልፋችሁ ወንዶችን ለመከጀል ትመጣላችሁን በእውነቱ እናንተ የምትሳሳቱ ሕዝቦች ናቸሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(27,56,'27:56','የሕዝቦቹም መልስ የሉጥን ቤተሰቦች «ከከተማችሁ አውጡ፡፡ እነርሱ የሚጥራሩ ሰዎች ናቸውና» ማለት እንጅ ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,57,'27:57','እርሱንና ቤተሰቦቹንም አዳን፡፡ ሚስቱ፤ ብቻ ስትቀር፡፡ (በጥፋቱ ውስጥ) ከቀሪዎቹ አደረግናት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,58,'27:58','በእነርሱም ላይ (የድንጋይ) ዝናብን አዘነብንባቸው፡፡ የተስፈራሪዎቹም ዝናብ ከፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,59,'27:59','(ሙሐመድ ሆይ) በል «ምስጋና ለአላህ ይግባው፡፡ በእነዚያም በመረጣቸው ባሮቹ ላይ ሰላም ይውረድ፡፡ አላህ በላጭ ነውን ወይስ ያ የሚያጋሩት (ጣዖት)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,60,'27:60','ወይም ያ ሰማያትንና ምድርን የፈጠረና ከሰማይም ለእናንተ ውሃን ያወረደው (ይበልጣል ወይስ የሚያጋሩት) በእርሱም ባለውበት የኾኑትን የታጠሩ አትክልቶችን ዛፍዋን ልታበቅሉ ለናንተ ችሎታ ያልነበራችሁን አበቀልን፡፡ ከአላህ ጋር ሌላ አምላክ አለን (የለም)፡፡ ግን እነርሱ (ከእውነት) የሚያዘነብሉ ሕዝቦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,61,'27:61','ወይም ያ ምድርን መርጊያ ያደረገና በመካከሏም ወንዞችን ያደረገ፣ ለእርሷም ጋራዎችን ያደረገ፣ በሁለቱ ባሕሮችም (በጣፋጩና በጨው ባሕር) መካከል ግርዶን ያደረገ (ይበልጣልን ወይስ የሚያጋሩት) ከአላህ ጋር ሌላ አምላክ አለን ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,62,'27:62','ወይም ያ ችግረኛን በለመነው ጊዜ የሚቀበል፣ መከራንም የሚያስወግድ፣ በምድርም ላይ ምትኮች የሚያደረጋችሁ፤ (ይበልጣልን ወይስ የሚያጋሩት) ከአላህ ጋር ሌላ አምላክ አለን ጥቂትንም አትገሠጹም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,63,'27:63','ወይም ያ በየብስና በባሕር ጨለማዎች ውስጥ የሚመራችሁ ነፋሶችንም ከዝናሙ በፊት አብሳሪ ኾነው የሚልክ (ይበልጣልን ወይስ የሚያጋሩት) ከአላህ ጋር ሌላ አምላክ አለን አላህ (በእርሱ) ከሚያጋሩዋቸው ሁሉ ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,64,'27:64','ወይም ያ መፍጠርን የሚጀምር ከዚያም የሚመልሰው ከሰማይና ከምድርም ሲሳይን የሚሰጣችሁ አምላክ (ይበልጣልን ወይስ የሚያጋሩት) ከአላህ ጋር ሌላ አምላክ አለን «እውነተኞች እንደኾናችሁ፤ አስረጃችሁን አምጡ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,65,'27:65','«በሰማያትና በምድር ያለው ሁሉ ሩቅን ምስጢር አያውቅም፡፡ ግን አላህ (ያውቀዋል)፡፡ መቼ እንደሚቀሰቀሱም አያውቁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,66,'27:66','በእውነት የመጨረሻይቱን ዓለም (ኹኔታ) ማወቃቸው ተሟላን አይደለም እነርሱ ከእርሷ በመጠራጠር ውስጥ ናቸው፡፡ በእውነትም እነርሱ ከእርሷ ዕውሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,67,'27:67','እነዚያም የካዱት አሉ «እኛም አባቶቻችንም ዐፈር በኾን ጊዜ እኛ (ከመቃብር) የምንወጣ ነን');
INSERT INTO "sadiq_sani" VALUES(27,68,'27:68','«ይህንን እኛም ከፊት የነበሩት አባቶቻችንም በእርግጥ ተቀጥረንበታል፡፡ ይህ የመጀመሪያዎቹ ሰዎች ተረቶች እንጅ ሌላ አይደለም» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,69,'27:69','«በምድር ላይ ኺዱ፡፡ የአመጸኞችም መጨረሻ እንዴት እንደ ነበረ ተመልከቱ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,70,'27:70','በእነሱም ላይ አትዘን፡፡ ከሚመክሩብህም ነገር በጭንቀት ውስጥ አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(27,71,'27:71','«እውነተኞችም እንደ ኾናችሁ ይህ ቀጠሮ መቼ ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,72,'27:72','«የዚያ የምትቻኮሉበት ከፊሉ ለእናንተ የቀረበ መኾኑ ተረጋግጧል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,73,'27:73','ጌታህም በሰዎች ላይ በእርግጥ የችሮታ ባለቤት ነው፡፡ ግን አብዛኛዎቻቸው አያመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,74,'27:74','ጌታህም ልቦቻቸው የሚደብቁትንና የሚገልጹትን ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(27,75,'27:75','በሰማይና በምድር ውስጥ ምንም የተደበቀ ነገር የለም ገላጭ በኾነው መጽሐፍ ውስጥ ያለ ቢኾን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,76,'27:76','ይህ ቁርኣን በእስራኤል ልጆች ላይ የእነዚያን እነርሱ በእርሱ የሚለያዩበትን አብዛኛውን ይነግራል፡፡');
INSERT INTO "sadiq_sani" VALUES(27,77,'27:77','እርሱም ለምእምናን መምሪያና እዝነት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,78,'27:78','ጌታህ በመካከላቸው በትክክል ፍርዱ ይፈርዳል፡፡ እርሱም አሸናፊው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,79,'27:79','በአላህም ላይ ተጠጋ፡፡ አንተ ግልጽ በኾነው እውነት ላይ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(27,80,'27:80','አንተ ሙታንን አታሰማም፡፡ ደንቆሮዎችንም የሚተው ኾነው በዞሩ ጊዜ ጥሪን አታሰማም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,81,'27:81','አንተም (ልበ) ዕውሮችን ከጥመታቸው አቅኚ አይደለህም፡፡ በአንቀጾቻችን የሚያምኑትን በስተቀር አታሰማም፡፡ እነርሱ ፍጹም ታዛዦች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(27,82,'27:82','በእነርሱም ላይ (የቅጣት) ቃሉ በተረጋገጠ ጊዜ ሰዎቹ በአንቀጾቻችን የማያረጋግጡ እንደነበሩ የምትነግራቸውን እንስሳ ለእነርሱ ከምድር እናወጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(27,83,'27:83','ከሕዝቦቹም ሁሉ ባንቀፆቻችን የሚያስተባብሉትን ጭፍሮች የምንሰበስብበትን ቀን (አስታውስ)፡፡ እነሱም ይከመከማሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,84,'27:84','በመጡም ጊዜ (አላህ) ይላቸዋል «በአንቀጾቼ እርሷን በማወቅ ያላዳረሳችሁ ስትሆኑ አስተባበላችሁን ወይስ ምንን ትሠሩ ነበራችሁ»');
INSERT INTO "sadiq_sani" VALUES(27,85,'27:85','በመበደላቸውም በእነርሱ ላይ ቃሉ ይፈጸምባቸዋል፡፡ እነርሱም አይናገሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(27,86,'27:86','እኛ ሌሊትን በእርሱ ውስጥ እንዲያርፉበት ቀንንም የሚያሳይ እንዳደረግን አያዩምን ለሚያምኑ ሕዝቦች በዚህ ውስጥ ተዓምራቶች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(27,87,'27:87','በቀንዱም በሚነፋ ቀን በሰማያት ውስጥ ያሉትና በምድርም ውስጥ ያሉት ሁሉ አላህ የሻው ነገር ብቻ ሲቀር በሚደነግጡ ጊዜ (የሚኾነውን አስታውስ)፡፡ ሁሉም የተናነሱ ኾነውም ወደርሱ ይመጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(27,88,'27:88','ጋራዎችንም እርሷ እንደ ደመና አስተላለፍ የምታልፍ ስትኾን የቆመች ናት ብለህ የምታስባት ሆና ታያታለህ፡፡ የዚያን ነገሩን ሁሉ ያጠነከረውን የአላህን ጥበብ (ተመልከት)፡፡ እርሱ በምትሠሩት ሁሉ ውስጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,89,'27:89','በመልካም ሥራ የመጣ ሰው ለእርሱ ከእርስዋ የበለጠ ምንዳ አልለው፡፡ እነርሱም በዚያ ቀን ከድንጋጤ ጸጥተኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,90,'27:90','በመጥፎም የመጡ ሰዎች ፊቶቻቸው በእሳት ውስጥ ይደፋሉ፡፡ «ትሠሩት የነበራችሁትን እንጅ አትመነዱም» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,91,'27:91','የታዘዝኩት የዚህችን አገር ጌታ ያንን ክልል ያደረጋትን እንድግገዛ ብቻ ነው፡፡ ነገሩንም ሁሉ የእርሱ ነው፡፡ ከሙስሊሞችም እንድኾን ታዝዣለሁ (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(27,92,'27:92','ቁርኣንንም እንዳነብ (ታዝዣለሁ)፡፡ የተመራም ሰው የሚመራው ለእራሱ ብቻ ነው፡፡ የተሳሳተም ሰው «እኔ ከአስጠንቃቂዎች ነኝ እንጅ ሌላ አይደለሁም» በለው፡፡');
INSERT INTO "sadiq_sani" VALUES(27,93,'27:93','ምስጋናም ለአላህ ነው፡፡ ተዓምራቶቹን ወደፊት ያሳያችኋል፡፡ «ታውቋትምአላችሁ» በላቸው፡፡ ጌታህም ከምትሠሩት ሁሉ ዘንጊ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,1,'28:1','ጠ.ሰ.መ (ጣ ሲን ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,2,'28:2','ይህቺ ገላጭ ከሆነው መጽሐፍ አንቀጾች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(28,3,'28:3','ከሙሳና ከፈርዖን ዜና እውነተኞች ስንኾን ለሚያምኑ ሕዝቦች ባንተ ላይ እናነባለን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,4,'28:4','ፈርዖን በምድር ላይ ተንበጣረረ፡፡ ነዋሪዎቿንም የተከፋፈሉ ጓዶች አደረጋቸው፡፡ ከእነርሱ ጭፍሮችን ያዳክማል፡፡ ወንዶች ልጆቻቸውን በብዛት ያርዳል፡፡ ሴቶቻቸውንም ይተዋል፤ እርሱ ከሚያበላሹት ሰዎች ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(28,5,'28:5','በእነዚያም በምድር ውስጥ በተጨቆኑ ላይ ልንለግስ፣ መሪዎችም ልናደርጋቸው ወራሾችም ልናደርጋቸው እንሻለን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,6,'28:6','ለእነርሱም በምድር ላይ ልናስመች ፈርዖንንና ሃማንንም ሰራዊቶቻቸውንም ከእነሱ ይፈሩት የነበሩትን ነገር ልናሳያቸው (እንሻለን)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,7,'28:7','ወደ ሙሳም እናት «አጥቢው፣ በርሱም ላይ በፈራሽ ጊዜ በባሕሩ ውስጥ ጣይው አትፈሪም፣ አትዘኝም፣ እኛ ወዳንቺ መላሾቹና ከመልክተኞቹም አድራጊዎቹ ነንና» ማለትን አመለከትን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,8,'28:8','የፈርዖን ቤተሰቦችም (መጨረሻው) ለእነርሱ ጠላት ሐዘንም ይሆን ዘንድ አነሱት፡፡ ፈርዖንና ሃማንም ሰራዊቶቻቸውም ሀጢአተኞች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,9,'28:9','የፈርዖንም ሚስት «ለእኔ የዓይኔ መርጊያ ነው፡፡ ለአንተም፡፡ አትግደሉት፡፡ ሊጠቅመን ወይም ልጅ አድርገን ልንይዘው ይከጀላልና» አለች፡፡ እነርሱም (ፍጻሜውን) የማያውቁ ሆነው (አነሱት)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,10,'28:10','የሙሳም እናት ልብ ባዶ ሆነ፡፡ ከምእምናን ትሆን ዘንድ በልቧ ላይ ባላጠነከርን ኖሮ ልትገልጸው ቀርባ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(28,11,'28:11','ለእኅቱም ተከታተይው አለቻት፡፡ እርሱንም እነሱ የማያውቁ ሲሆኑ በሩቅ ሆና አየችው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,12,'28:12','(ወደናቱ ከመመለሱ) በፊትም አጥቢዎችን (መጥባትን) በእርሱ ላይ እርም አደረግን፡፡ (እኅቱ) «ለእናንተ የሚያሳድጉላችሁን እነርሱም ለእርሱ ቅን አገልጋዮች የሆኑን ቤተሰቦች ላመልክታችሁን» አለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,13,'28:13','ወደእናቱም ዓይንዋ እንድትረጋ፣ እንዳታዝንም፣ የአላህም የተስፋ ቃል እርግጥ መሆኑን እንድታውቅ መለስነው፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,14,'28:14','ብርታቱንም በደረሰና በተስተካከለ ጊዜ ጥበብንና ዕውቀትን ሰጠነው፡፡ እንዲሁም መልካም ሠሪዎችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,15,'28:15','ከተማይቱንም ሰዎቿ በዝንጋቴ ጊዜ ላይ ሆነው ሳሉ ገባ፡፡ በእርሷም ውስጥ የሚጋደሉን ሁለት ሰዎችን አገኘ፡፡ ይህ ከወገኑ ነው፤ ይህም ከጠላቱ ነው፡፡ ያም ከወገኑ የሆነው ሰው በዚያ ከጠላቱ በሆነው ሰው ላይ እርዳታን ጠየቀው፡፡ ሙሳም በጡጫ መታው፡፡ ገደለውም፡፡ ይህ ከሰይጣን ሥራ ነው፡፡ እርሱ ግልጽ አሳሳች ጠላት ነውና አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,16,'28:16','«ጌታዬ ሆይ! እኔ ነፍሴን በደልኩ፡፡ ለእኔም ማር አለ፡፡» ለእርሱም ምሕረት አደረገለት እርሱ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(28,17,'28:17','«ጌታዬ ሆይ! በእኔ ላይ በመለገስህ ይሁንብኝ (ከስህተቴ እጸጸታለሁ)፤ ለአመጸኞችም ፈጽሞ ረዳት አልሆንም አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,18,'28:18','በከተማይቱም ውስጥ ፈሪ የሚጠባበቅ ሆኖ አደረ፡፡ በድንገትም ያ ትላንት እርዳታን የጠየቀው ሰው በጩኸት እርዳታን ይጠይቀዋል፡፡ ሙሳ ለርሱ «አንተ በእርግጥ ግልጽ ጠማማ ነህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,19,'28:19','(ሙሳ) ያንን እርሱ ለሁለቱም ጠላት የሆነውን ሰው በኃይል ሊይዘው በፈለገም ጊዜ «ሙሳ');
INSERT INTO "sadiq_sani" VALUES(28,20,'28:20','ከከተማይቱም ዳርቻ አንድ ሰው እየሮጠ መጣ፡፡ «ሙሳ ሆይ! ሹማምንቶቹ ሊገድሉህ ባንተ ይመካከራሉና ውጣ፡፡ እኔ ላንተ ከመካሪዎች ነኝና» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,21,'28:21','የፈራና የሚጠባበቅ ሆኖም ከእርሷ ወጣ፡፡ «ጌታዬ ሆይ! ከበደለኞቹ ሕዝቦች አድነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,22,'28:22','ወደ መድየን አቅጣጫ ፊቱን ባዞረ ጊዜም «ጌታዬ ቀጥተኛውን መንገድ ሊመራኝ እከጅላለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,23,'28:23','ወደ መድየንም ውሃ በመጣ ጊዜ በእርሱ ላይ ከሰዎች ጭፍሮችን (መንጋዎቻቸውን) የሚያጠጡ ሆነው አገኘ፡፡ ከእነርሱ በታችም (ከውሃው መንጎቻቸውን) የሚከለክሉን ሁለት ሴቶች አገኘ፡፡ «ነገራችሁ ምንድን ነው» አላቸው፡፡ «እረኞቹ ሁሉ (መንጋዎቻቸውን) እስከሚመልሱ አናጠጣም፡፡ አባታችንም ትልቅ ሽማግሌ ነው» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(28,24,'28:24','ለሁለቱም አጠጣላቸው፡፡ ከዚያም ወደ ጥላው ዘወር አለ፡፡ «ጌታዬ ሆይ! እኔ ከመልካም ነገር ወደኔ ለምታወርደው ፈላጊ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,25,'28:25','ከሁለቱ አንደኛይቱም ከሐፍረት ጋር የምትኼድ ሆና መጣችው፡፡ «አባቴ ለእኛ ያጠጣህልንን ዋጋ ሊሰጥህ ይጠራሃል» አለችው፡፡ ወደእርሱ በመጣና ወሬውን በእርሱ ላይ በተረከለትም ጊዜ «አትፍራ ከበደለኞቹ ሕዝቦች ድነሃል» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,26,'28:26','ከሁለቱ አንደኛይቱም «አባቴ ሆይ ቅጠረው፡፡ ከቀጠርከው ሰው ሁሉ በላጩ ብርቱው ታማኙ ነውና» አለችው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,27,'28:27','«ስምንትን ዓመታት ልታገለግለኝ እኔ ከእነዚህ ሁለት ሴቶቸ ልጆቼ አንዲቱን ላጋባህ እሻለሁ፡፡ ዐስርን ብትሞላም ከአንተ ነው፡፡ ባንተ ላይ ማስቸገርንም አልሻም፡፡ አላህ የሻ እንደ ኾነ ከመልካሞቹ ሰዎች ኾኜ ታገኘኛለህ» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,28,'28:28','(ሙሳም) «ይህ (ውለታ) በእኔና ባንተ መካከል (ረጊ) ነው፡፡ ከሁለቱ ጊዜያቶች ማንኛውንም ብፈጽም በእኔ ላይ ወሰን ማለፍ የለም፡፡ አላህም በምንለነው ላይ ምስክር» ነው አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,29,'28:29','ሙሳም ጊዜውን በጨረሰና ከቤተሰቦቹ ጋር በኼደ ጊዜ ከጡር ተራራ ጎን እሳትን አየ፡፡ ለቤተሰቡ (እዚህ) «ቆዩ፡፡ እኔ እሳትን አየሁ፡፡ ከእርሷ ወሬን ወይም ትሞቁ ዘንድ ከእሳት ትንታግን አመጣላችኋለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,30,'28:30','በመጣትም ጊዜ ከሸለቆው ቀኝ ዳርቻ ከዛፊቱ በኩል በተባረከችው ስፍራ ውስጥ «ሙሳ ሆይ! እኔ የዓለማት ጌታ አላህ እኔ ነኝ» በማለት ተጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,31,'28:31','«በትርህንም ጣል» (ተባለ)፡፡ እንደ ትንሽ እባብ ስትስለከለክ ባያትም ጊዜ ወደ ኋላ ዞሮ ሸሸ፤ አልተመለሰምም፡፡ «ሙሳ ሆይ! ተመለስ አትፍራም፤ አንተ ከጥብቆቹ ነህና» (ተባለም)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,32,'28:32','«እጅህን በአንገትጌህ ውስጥ አግባ፡፡ ያለ ነውር ነጭ ሆና ትወጣለችና፡፡ ክንፍህንም ከፍርሃት (ለመዳን) ወዳንተ አጣብቅ፡፡ እነዚህም ከጌታህ የሆኑ ወደ ፈርዖንና ወደ መኳንንቶቹ የሚደርሱ ሁለት አስረጅዎች ናቸው፡፡ እነርሱ አመጸኞች ሕዝቦች ነበሩና፡፡»');
INSERT INTO "sadiq_sani" VALUES(28,33,'28:33','(ሙሳ) አለ «ጌታዬ ሆይ! እኔ ከእነርሱ ነፍስን ገድያለሁ፡፡ ስለዚህ እንዳይገድሉኝ እፈራለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,34,'28:34','«ወንድሜም ሃሩን እርሱ ምላሱ ከእኔ የተባ ነው፡፡ እውነተኛነቴንም የሚያረጋግጥልኝ ረዳት ኾኖ ከእኔ ጋር ላከው፡፡ እኔ ሊያስተባብሉኝ እፈራለሁና፡፡»');
INSERT INTO "sadiq_sani" VALUES(28,35,'28:35','(አላህም) «ጡንቻህን በወንድምህ በእርግጥ እናበረታለን፡፡ ለሁለታችሁም ስልጣንን እናደርጋለን፡፡ ወደእናንተም (በመጥፎ) አይደርሱም፡፡ በተዓምራቶቻችን (ኺዱ)፡፡ እናንተና የተከተላችሁም ሁሉ አሸናፊዎች ናችሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,36,'28:36','ሙሳም በታምራቶቻችን ግልጽ ኾነው በመጣባቸው ጊዜ «ይህ የተቀጠፈ ድግምት እንጅ ሌላ አይደለም፡፡ ይህንንም በመጀመሪያዎቹ አባቶቻችን ውስጥ አልሰማንም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,37,'28:37','ሙሳም «ጌታዬ ከእርሱ ዘንድ በቀጥታው መንገድ የመጣውን ሰው ምስጉኒቱም አገር ለእርሱ የምትኾንለትን ሰው ዐዋቂ ነው፡፡ እነሆ በደለኞቹ አይድኑም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,38,'28:38','ፈርዖንም «እናንተ ጭፍሮች ሆይ! ከእኔ ሌላ ለእናንተ አምላክን ምንም አላወቅሁም፡፡ ሃማንም ሆይ! ጭቃን ለእኔ አቃጥልልኝ፤ (ጡብ ሥራልኝ)፡፡ ለእኔም ከፍተኛ ሕንጻን ሥራልኝ፡፡ ወደ ሙሳ አምላክ ልወጣ እከጅላለሁና፡፡ እኔም ከውሸታሞቹ መኾኑን እጠረጥረዋለሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,39,'28:39','እርሱም ሰራዊቶቹም በምድር ላይ ያለ አግባብ ኮሩ፡፡ እነርሱም ወደእኛ የማይመለሱ መኾናቸውን ጠረጠሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,40,'28:40','እርሱንም ሰራዊቱንም በጥብቅ ያዝናቸው፡፡ በባሕርም ውስጥ ጣልናቸው፡፡ የበደለኞች መጨረሻም፤ እንዴት እንደ ነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(28,41,'28:41','ወደ እሳት የሚጠሩ መሪዎችም አደረግናቸው፡፡ በትንሣኤም ቀን አይርረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,42,'28:42','በይህችም በቅርቢቱም ዓለም ውስጥ እርግማንን አስከተልናቸው፡፡ በትንሣኤም ቀን እነሱ ከሚባረሩት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,43,'28:43','የፊተኞቹን የክፍለ ዘመናት ሕዝቦችም ከአጠፋን በኋላ ለሰዎች የልብ ብርሃን መሪም እዝነትም ሲኾን ይገሰጹ ዘንድ ለሙሳ መጽሐፉን በእርግጥ ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,44,'28:44','ወደ ሙሳም ትዕዛዝን ባወረድንለት ጊዜ (ከተራራው) በምዕራባዊው ጎን አልነበርክም ከተጣዱትም አልነበርክም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,45,'28:45','ግን እኛ (ከእርሱ በኋላ) የክፍለ ዘመናትን ሰዎች አስገኘን፡፡ በእነሱም ላይ ዕድሜዎች ተራዘሙ፡፡ በመድየንም ሰዎች ውስጥ ተቀማጭ በእነሱ ላይ አንቀጾቻችን የምታነብ አልነበርክም፡፡ ግን እኛ ላኪዎችህ ኾንን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,46,'28:46','በጠራነውም ጊዜ በጡር ጎን አልነበርክም፡፡ ግን ከአንተ በፊት ምንም አስፈራሪ ያልመጣቸውን ሕዝቦች ታስፈራራ ዘንድ ከጌታህ በኾነ ችሮታ (ላክንህ) ሊገሰጹ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(28,47,'28:47','እጆቻቸውም ባሳለፉት ምክንያት መከራ የምትነካቸውና «ጌታችን ሆይ! አንቀጾችህን እንድንከተል ከምእምናንም እንድንኾን ወደኛ መልክተኛን አትልክም ኖሯልን» የሚሉ ባልኾኑ ኖሮ (አንልክም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,48,'28:48','እውነቱም ከእኛ ዘንድ በመጣላቸው ጊዜ «ለሙሳ የተሰጠው ብጤ አይስሰጠውም ኖሯልን» አሉ፡፡ «ከዚህ በፊት ሙሳ በተሰጠው አልካዱምን የተረዳዱ ሁለት ድግምቶች ናቸው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,49,'28:49','«ከአላህ ዘንድ የኾነን መጽሐፍ እርሱ ከሁለቱም ይበልጥ ቅን የኾነን አምጡ፡፡ እውነተኞች እንደ ኾናችሁ (ብታመጡት) እከተለዋለሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,50,'28:50','እሺ ባይሉህም፤ የሚከተሉት፤ ዝንባሌዎቻቸውን ብቻ መኾኑን ዕወቅ፡፡ ከአላህም የኾነ መመሪያ ሳይኖረው ዝንባሌውን ከተከተለ ሰው ይበልጥ የጠመመ አንድም የለም፡፡ አላህ በደለኞች ሕዝቦችን አይመራምና፡፡');
INSERT INTO "sadiq_sani" VALUES(28,51,'28:51','ይገሰጹም ዘንድ ቃልን (ቁርኣንን በያይነቱ) ለእነርሱ በእርግጥ አስከታተልን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,52,'28:52','እነዚያ ከእርሱ በፊት መጽሐፍን የሰጠናቸው እነርሱ በእርሱ ያምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,53,'28:53','በእነሱ ላይ በሚነበብላቸውም ጊዜ «በእርሱ አምነናል፡፡ እርሱ ከጌታችን የኾነ እውነት ነው፡፡ እኛ ከርሱ በፊት ሙስሊሞች ነበርን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,54,'28:54','እነዚያ በመታገሳቸው ዋጋቸውን ሁለት ጊዜ ይስሰጣሉ፡፡ ክፉውንም ነገር በበጎ ምግባር ይገፈትራሉ፡፡ ከሰጠናቸውም ሲሳይ ይለግሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,55,'28:55','ውድቅንም ቃል በሰሙ ጊዜ ከእርሱ ይርቃሉ፡፡ «ለእኛ ሥራዎቻችን አሉን፡፡ ለናንተም ሥራዎቻችሁ አሏችሁ፡፡ ሰላም በእናንተ ላይ (ይኹን) ባለጌዎችን አንፈልግም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,56,'28:56','አንተ የወደድከውን ሰው ፈጽሞ አታቀናም፡፡ ግን አላህ የሚሻውን ሰው ያቀናል እርሱም ቅኖቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,57,'28:57','«ከአንተ ጋር ቅንን መንገድ ብንከተል ከምድራችን እንባረራለን» አሉም፡፡ ከእኛ ዘንድ (የተሰጠ) ሲሳይ ሲሆን የነገሩ ሁሉ ፍሬዎች ወደእርሱ የሚነዱበትን ጸጥተኛ ክልል (መካን) ለእነሱ አላስመቸናቸውምን ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,58,'28:58','ከከተማም ኑሮዋን (ምቾቷን) የካደችን (ከተማ) ያጠፋናት ብዙ ናት፡፡ እነዚህም ከእነሱ በኋላ ጥቂት ጊዜ እንጅ ያልተኖረባቸው ሲኾኑ መኖሪያዎቻቸው ናቸው፡፡ እኛም (ከእነርሱ) ወራሾች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,59,'28:59','ጌታህም ከተሞችን በዋናዋ (ከተማ) ውስጥ አንቀጾቻችንን በእነሱ ላይ የሚያነብ መልክተኛን እስከሚልክ ድረስ አጥፊ አልነበረም፡፡ ከተሞችንም ባለቤቶቿ በዳዮች ሆነው እንጅ አጥፊዎች አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,60,'28:60','ከማንኛውም ነገር የተሰጣችሁት፤ የቅርቢቱ ሕይወት ጥቅምና ጌጧ ነው፡፡ አላህ ዘንድ ያለውም (ምንዳ) በጣም በላጭና ዘውታሪ ነው አታውቁምን');
INSERT INTO "sadiq_sani" VALUES(28,61,'28:61','መልካምን ተስፋ ቃል የገባንለትና እርሱም አግኚው የሆነ ሰው የቅርቢቱን ሕይወት ጥቅም እንዳስመቸነውና ከዚያም እርሱ በትንሣኤ ቀን (ለእሳት) ከሚቀረቡት እንደ ኾነው ሰው ነውን');
INSERT INTO "sadiq_sani" VALUES(28,62,'28:62','(አላህ) የሚጠራባቸውንና እነዚያ ተጋሪዎቼ ትሏቸው የነበራችሁት የት ናቸው የሚልበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,63,'28:63','እነዚያ በእነርሱ ላይ ቃሉ የተረጋገጠባቸው ይላሉ «ጌታችን ሆይ! እነዚህ እነዚያ ያጠመምናቸው ናቸው፡፡ እንደጠመምን አጠመምናቸው፡፡ (ከእነሱ) ወዳንተ ተጥራራን፡፡ እኛን ይግገዙ አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,64,'28:64','«(ለአላህ) የምታጋሯቸውንም ጥሩ» ይባላሉ፤ ይጠሯቸዋልም፡፡ ግን ለእነርሱ አይመልሱላቸውም፡፡ ቅጣቱንም ያያሉ፡፡ እነርሱ ይመሩ በነበሩ ኖሮ (አያዩትም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,65,'28:65','«የሚጠራባቸውንና ለመልክተኞቹም ምንን መለሳችሁ» የሚልበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,66,'28:66','በዚያም ቀን ወሬዎቹ ሁሉ በእነርሱ ላይ ይሰውሩባቸዋል፡፡ እነርሱም አይጠያየቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,67,'28:67','የተጸጸተና ያመነ ሰውማ መልካምንም የሠራ ከሚድኑት ሊኾን ይከጀላል፡፡');
INSERT INTO "sadiq_sani" VALUES(28,68,'28:68','ጌታህም የሚሻውን ይፈጥራል፤ ይመርጣልም፡፡ ለእነርሱ (ለሰዎቹ) ምርጫ የላቸውም፡፡ አላህ ከማይግገባው ሁሉ ጠራ፡፡ ከሚያጋሯቸውም ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,69,'28:69','ጌታህም ልቦቻቸው የሚደብቁትንና የሚገልጹትን ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(28,70,'28:70','እርሱም አላህ ነው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ ምስጋና በመጀመሪያይቱም በመጨረሻይቱም ዓለም ለእርሱ ብቻ ነው፡፡ ፍርዱም የእርሱ ብቻ ነው፡፡ ወደእርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,71,'28:71','አያችሁን (ንገሩኝ) «አላህ በእናንተ ላይ ሌሊትን እስከ ትንሣኤ ቀን ድረስ ዘውታሪ ቢያደርገው ከአላህ ሌላ ብርሃንን የሚያመጣላችሁ አምላክ ማነው አትሰሙምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,72,'28:72','አያችሁን «አላህ በእናንተ ላይ ቀንን እስከ ትንሣኤ ቀን ድረስ ዘውታሪ ቢያደርገው ከአላህ ሌላ በውስጡ የምታርፉበትን ሌሊት የሚያመጣላችሁ አምላክ ማን ነው (ንገሩኝ) አታስተውሉምን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,73,'28:73','ከችሮታውም ለእናንተ ሌሊትንና ቀንን በውስጡ ልታርፉበት፣ ከትሩፋቱም ልትፈልጉበት፣ አመስገኞችም ልትኾኑ አደረገላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,74,'28:74','የሚጠራባቸውንም ቀንና «እነዚያ ተጋሪዎቼ ትሏቸው የነበራችሁት የት ናቸው» የሚልበትን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,75,'28:75','ከሕዝቦችም ሁሉ መስካሪን እናወጣለን፡፡ (ያን ጊዜ) እውነተኛውም አምላክነት ለአላህ ብቻ መኾኑን ያውቃሉ፡፡ ይቀጣጥፉት የነበሩትም ከእነርሱ ይጠፋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(28,76,'28:76','ቃሩን ከሙሳ ነገዶች ነበር፡፡ በእነርሱም ላይ አመጸ፡፡ ከድልቦችም ያንን መከፈቻዎቹ ብቻ የኃይል ባለቤቶች የኾኑ ጭፍሮችን (ሸክሙ) የሚከብድን ሰጠነው፡፡ ወገኖቹ ለእርሱ አትኩራ፤ አላህ ኩራተኞችን አይወድምና ባሉት ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,77,'28:77','«አላህም በሰጠህ ሀብት የመጨረሻይቱን አገር ፈልግ፡፡ ከቅርቢቱም ዓለም ፋንታህን አትርሳ፡፡ አላህም ወደ አንተ መልካምን እንዳደረገልህ (ለሰዎች) መልካምን አድርግ፡፡ በምድርም ውስጥ ማጥፋትን አትፈልግ፡፡ አላህ አጥፊዎችን አይወድምና» (አሉት)፡፡');
INSERT INTO "sadiq_sani" VALUES(28,78,'28:78','(ሀብቱን) «የተሰጠሁት እኔ ዘንድ ባለው እውቀት ብቻ ነው አለ፡፡ አላህ ከእርሱ በፊት ከክፍለ ዘመናት ሰዎች በኀይል ከእርሱ ይበልጥ የበረቱትን (ሀብትን) በመሰብሰብም ይበልጥ የበዙትን በእርግጥ ያጠፋ መኾኑን አያውቅምን አመጸኞችም ከኀጢኣቶቻቸው (ምሕረት የሚከተለውን ጥያቄ) አይጠየቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,79,'28:79','በሕዝቦቹም ላይ በጌጡ ውስጥ ኾኖ ወጣ፡፡ እነዚያ ቅርቢቱን ሕይወት የሚፈልጉት «ወይ ምኞታችን! ቃሩን የተሰጠው ብጤ ምነው ለእኛ በኖረን፤ እርሱ የታላቅ ዕድል ባለቤት ነውና» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,80,'28:80','እነዚያም ዕውቀትን የተሰጡት «ወዮላችሁ! የአላህ ምንዳ ለአመነ ሰው መልካምንም ለሠራ በላጭ ነው፡፡ ታጋሾችም እንጅ ሌላው አያገኛትም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,81,'28:81','በእርሱም በቤቱም ምድርን ደረባን፡፡ ለእርሱም ከአላህ ሌላ የሚረዱት ጭፍሮች ምንም አልነበሩትም፡፡ ከሚርረዱትም አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,82,'28:82','እነዚያም በትላንትናው ቀን ስፍራውን ይመኙ የነበሩት «ወይ ጉድ! ለካ አላህ ከባሮቹ ለሚሻው ሰው ሲሳይን ያሰፋል፤ ያጠባልም፡፡ አላህ በእኛ ላይ (በእምነት) ባልለገሰልን ኖሮ ምድርን በእኛ ላይ በገለበጠብን ነበር፡፡ ወይ ጉድ! ከሓዲዎች አይድኑም» የሚሉ ኾነው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(28,83,'28:83','ይህች የመጨረሻይቱ አገር ለእነዚያ በምድር ውስጥ ኩራትንና ማመፅን ለማይፈልጉት እናደርጋታለን፡፡ ምስጉኒቱም መጨረሻ ለጥንቁቆቹ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(28,84,'28:84','በደግ ሥራ የመጣ ሰው ለእርሱ ከእርሷ በላጭ ምንዳ አልለው፡፡ በክፉም ሥራ የመጡ ሰዎች እነዚህ መጥፎዎችን የሠሩ ይሠሩት የነበሩትን እንጅ አይመነዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(28,85,'28:85','ያ ቁርኣንን የተከፋፈለ ኾኖ ባንተ ላይ ያወረደው (አምላክ) ወደ መመለሻ (ወደመካ) በእርግጥ መላሽህ ነው፡፡ «ጌታዬ በቅን መንገድ የመጣውን ሰውና እርሱም በግልጽ ስሕተት ውስጥ የኾነውን ሰው ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(28,86,'28:86','መጽሐፉ ወዳንተ መወረዱን ተስፋ የምታደርግ አልነበርክም፡፡ ግን ከጌታህ ችሮታ (ተወረደልህ)፡፡ ለከሓዲዎችም በፍጹም ረዳት አትኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(28,87,'28:87','ከአላህም አንቀጾች ወዳንተ ከተወረዱ ጊዜ በኋላ አያግዱህ፡፡ ወደ ጌታህም (ሰዎችን) ጥራ፡፡ ከአጋሪዎቹም አትኹን፤');
INSERT INTO "sadiq_sani" VALUES(28,88,'28:88','ከአላህም ጋር ሌላን አምላክ አትግገዛ፡፡ ከእርሱ በቀር አምላክ የለም፡፡ ነገሩ ሁሉ ከአላህ በቀር ጠፊ ነው፡፡ ፍርዱ የርሱ፤ ብቻ ነው፡፡ ወደእርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,1,'29:1','አ.ለ.መ (አሊፍ ላም ሚም)');
INSERT INTO "sadiq_sani" VALUES(29,2,'29:2','ሰዎቹ አምነናል በማለታቸው ብቻ እነሱ ሳይፈተኑ የሚተው መኾናቸውን ጠረጠሩን');
INSERT INTO "sadiq_sani" VALUES(29,3,'29:3','እነዚያንም ከእነሱ በፊት የነበሩትን በእርግጥ ፈትነናል፡፡ እነዚያንም እውነት የተናገሩትን አላህ በእርግጥ ያውቃል፡፡ ውሸታሞቹንም ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(29,4,'29:4','ይልቁንም እነዚያ መጥፎዎችን ሥራዎች የሚሠሩት ሊያመልጡን ይጠረጥራሉን ያ የሚፈርዱት (ፍርድ) ከፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,5,'29:5','የአላህን መገናኘት ተስፋ የሚያደርግ ሰው (ይዘጋጅ)፡፡ የአላህ ቀጠሮ በእርግጥ መጪ ነውና፡፡ እርሱም ሰሚው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,6,'29:6','የታገለ ሰውም የሚታገለው ለነፍሱ ነው፡፡ አላህ ከዓለማት በእርግጥ ተብቃቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(29,7,'29:7','እነዚያም ያመኑ፤ መልካሞችንም የሠሩ መጥፎዎቹን ሥራዎቻቸውን ከእነሱ ላይ እናብስላቸዋለን፡፡ በዚያም ይሠሩት በነበሩት መልካም ሥራ በእርግጥ እንመነዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(29,8,'29:8','ሰውንም በወላጆቹ መልካም አድራጎትን አዘዝነው፡፡ ላንተም በእርሱ ዕውቀት የሌለህን (ጣዖት) በእኔ እንድታጋራ ቢታገሉህ አትታዘዛቸው፡፡ መመለሻችሁ ወደኔ ነው፡፡ ትሠሩት የነበራችሁትንም ሁሉ እነግራችኋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,9,'29:9','እነዚያንም ያመኑ፤ መልካም ሥራዎችንም የሠሩ በደጋጎቹ ውስጥ በእርግጥ እናገባቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(29,10,'29:10','ከሰዎችም በአላህ አመንን የሚል ሰው አልለ፡፡ በአላህም (በማመኑ) በተሰቃየ ጊዜ የሰዎችን ማሰቃየት እንደ አላህ ቅጣት ያደርጋል፡፡ ከጌታህም እርዳታ ቢመጣ እኛ በእርግጥ ከእናንተ ጋር ነበርን ይላሉ፡፡ አላህ በዓለማት ሕዝብ ልቦች ውስጥ ያለውን ሁሉ ዐዋቂ አይደለምን');
INSERT INTO "sadiq_sani" VALUES(29,11,'29:11','አላህም እነዚያን ያመኑትን በእርግጥ ያውቃል፡፡ መናፍቆቹንም ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(29,12,'29:12','እነዚያም የካዱት ሰዎች ለእነዚያ ላመኑት «መንገዳችንን ተከተሉ፤ ኀጢአቶቻችሁንም እንሸከማለን» አሉ፡፡ እነርሱም ከኀጢአቶቻቸው ምንንም ተሸካሚዎች አይደሉም፡፡ እነርሱ በእርግጥ ውሸታሞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,13,'29:13','ሸክሞቻቸውንና ከሸክሞቻቸው ጋር (ሌሎች) ሸክሞችንም በእርግጥ ይሸከማሉ፡፡ በትንሣኤም ቀን ይቀጥፉት ከነበሩት በእርግጥ ይጠየቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,14,'29:14','ኑሕንም ወደ ሕዝቦቹ በእርግጥ ላክነው፡፡ በውስጣቸውም ሺህ ዓመትን አምሳ ዓመት ሲቀር ተቀመጠ፡፡ እነርሱ በዳዮች ኾነውም የውሃው ማጥለቅለቅ ያዛቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,15,'29:15','ኑሕንና የመርከቢቱንም ጓዶች አዳናቸው፡፡ እርሷንም ለዓለማት ተዓምር አደረግናት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,16,'29:16','ኢብራሂምንም ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፡፡ «አላህን ተገዙ ፍሩትም፡፡ ይህ የምታውቁ ብትሆኑ ለእናንተ በላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,17,'29:17','«ከአላህ ሌላ የምትገዝዙት ጣዖታትን ብቻ ነው፡፡ ውሸትንም ትቀጣጥፋላችሁ፡፡ እነዚያ ከአላህ ሌላ የምትግገዙዋቸው ለእናንተ ሲሳይን ሊሰጧችሁ አይችሉም፡፡ ስለዚህ ሲሳይን ከአላህ ዘንድ ብቻ ፈልጉ፡፡ ተገዙትም፡፡ ለእርሱም አመሰግኑ፡፡ ወደእርሱ ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,18,'29:18','«ብታስተባብሉም ከፊታችሁ የነበሩት ሕዝቦች በእርግጥ አስተባብለዋል፡፡ በመልክተኛውም ላይ ግልጽ ማድረስ እንጂ ሌላ የለበትም፡፡»');
INSERT INTO "sadiq_sani" VALUES(29,19,'29:19','አላህ መፍጠርን እንዴት እንደሚጀመር ከዚያም እንዴት እንደሚመልሰው አይመለከቱምን ይህ በአላህ ላይ ቀላል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,20,'29:20','«፡--በምድር ላይ ኺዱ፤ መፍጠርንም እንዴት እንደ ጀመረ ተመልከቱ፡፡ ከዚያም፤ አላህ የመጨረሻይቱን መነሳት ያስነሳል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,21,'29:21','የሚሻውን ሰው ይቀጣል፡፡ የሚሻውንም ሰው ያዝንለታል፡፡ ወደእርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,22,'29:22','እናንተም በምድርም ኾነ በሰማይ የምታቅቱ አይደላችሁም፡፡ ከአላህም ሌላ ረዳትም ተከላካይም ምንም የላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,23,'29:23','እነዚያም በአላህ አንቀጾችና በመገናኘቱ የካዱ እነዚያ ከእዝነቴ ተስፋን ቆረጡ፡፡ እነዚያም ለእነሱ አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,24,'29:24','የሕዝቦቹም መልስ፡- «ግደሉት፤ ወይም አቃጥሉት» ማለት እንጂ ሌላ አልነበረም፡፡ ከእሳቲቱም አላህ አዳነው፡፡ በዚህ ውስጥ ለሚያምኑ ሕዝቦች ተዓምራቶች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,25,'29:25','(ኢብራሂም) አለም «ከአላህ ሌላ ጣዖታትን (አማልክት) የያዛችሁት በቅርቢቱ ሕይወት በመካከላችሁ ለመዋደድ ብቻ ነው፡፡ ከዚያም በትንሣኤ ቀን ከፊላቸሁ በከፊሉ ይክዳል፡፡ ከፊላችሁም ከፊሉን ይረግማል፡፡ መኖሪያችሁም እሳት ናት፡፡ ለእናንተም ከረዳቶች ምንም የላችሁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(29,26,'29:26','ሉጥም ለእርሱ አመነ፤ (ኢብራሂም) «እኔ ወደ ጌታዬ ተሰዳጅ ነኝ፡፡ እነሆ እርሱ አሸናፊው ጥበበኛው ነውና» አለም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,27,'29:27','ለእርሱም ኢስሐቅንና ያዕቆብን ሰጠነው፡፡ በዘሩም ውስጥ ነብይነትንና መጽሐፍን አደረግን፡፡ ምንዳውንም በቅርቢቱ ዓለም ሰጠነው፡፡ እርሱም በመጨረሻይቱ ዓለም ከደጋጎቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,28,'29:28','ሉጥንም ለሕዝቦቹ ባለ ጊዜ (አስታውስ) «እናንተ ጠያፍን ስራ ትሠራላችሁን በእርሷ ከዓለማት አንድም አልቀደማችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,29,'29:29','«እናንተ በእርግጥ ወንዶችን ትመጣላችሁን መንገድንም ትቆርጣላችሁን በሸንጓችሁም የሚነወርን ነገር ትሠራላችሁን» (አላቸው)፡፡ የሕዝቦቹም መልስ «ከእውነተኞቹ እንደ ሆንክ የአላህን ቅጣት አምጣብን» ማለት እንጂ ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,30,'29:30','«ጌታዬ ሆይ! በአመጸኞች ሕዝቦች ላይ እርዳኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,31,'29:31','መልክተኞቻችንም ኢብራሂምን በብስራት በመጡት ጊዜ «እኛ የዚህችን ከተማ ሰዎች አጥፊዎች ነን፡፡ ሰዎቿ በዳዮች ነበሩና» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,32,'29:32','«በእርሷ ውስጥ ሉጥ አለ እኮ» አላቸው፡፡ «እኛ በውስጧ ያለውን ይበልጥ ዐዋቂ ነን፡፡ (እርሱን) በእርግጥ እናድነዋለን፡፡ ቤተሰቦቹንም ጭምር፡፡ ሚስቱ ብቻ ስትቀር፡፡ እርሷ በጥፋቱ ውስጥ ከሚቀሩት ናት» አሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,33,'29:33','መልክተኞቻችንም ሉጥን በመጡት ጊዜ በእነሱ ምክንያት አዘነ፡፡ በእነርሱም ልቡ ተጨነቀ፡፡ «አትፍራ፤ አትዘንም፡፡ እኛ አዳኞችህ ነን፡፡ ቤተሰቦችህንም፤ ሚስትህ ብቻ ስትቀር፡፡ እርሷ በጥፋቱ ውስጥ ከቀሪዎቹ ናት» አሉትም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,34,'29:34','«እኛ በዚህች ከተማ ሰዎች ላይ ያምጹ በነበሩት ምክንያት ከሰማይ ቅጣትን አውራጆች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(29,35,'29:35','በእርግጥም ለሚያስቡ ሰዎች ከእርሷ ግልጽ ምልክትን ተውን (አስቀረን)፡፡');
INSERT INTO "sadiq_sani" VALUES(29,36,'29:36','ወደ መድየንም ወንድማቸውን ሹዐይብን (ላክን)፡፡ «ሕዝቦቼ ሆይ! አላህን ተገዙ፣ የመጨረሻውንም ቀን ፍሩ፣ በምድርም ውስጥ የምታጠፉ ሆናችሁ አታበላሹ አላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,37,'29:37','አስተባባሉትም፡፡ የምድር መንቀጥቀጥ ያዘቻቸው፡፡ በሃገራቸውም ውስጥ ተንከፍርረው አነጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,38,'29:38','ዓድንና ሰሙድንም (አጠፋን)፡፡ ከመኖሪያዎቻቸውም (ፍርስራሾች በኩል መጥፋታቸው) ለእናንተ በእውነት ተገለጠላችሁ፡፡ ሰይጣንም ሥራዎቻቸውን ለእነርሱ ሸለመላቸው፡፡ ከመንገድም አገዳቸው፡፡ የማስተዋልም ባለቤቶች ነበሩ፤ (ግን አላስተዋሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(29,39,'29:39','ቃሩንንም ፈርዖንንም ሃማንንም (አጠፋን)፡፡ ሙሳም በተዓምራቶች በእርግጥ መጣባቸው፡፡ በምድርም ላይ ኮሩ፡፡ አምላጪዎችም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,40,'29:40','ሁሉንም በኅጢኣቱ ያዝነው፡፡ ከእነሱም ውስጥ ጠጠርን ያዘለ ነፋስን የላክንበት አልለ፡፡ ከእነሱም ውስጥ ጩኸት የያዘችው አልለ፡፡ ከእነርሱም ውስጥ በእርሱ ምድርን የደረባንበት አልለ፡፡ ከእነሱም ውስጥ ያሰጠምንው አልለ፡፡ አላህም የሚበድላቸው አልነበረም፡፡ ግን ነፍሶቻቸውን የሚበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,41,'29:41','የእነዚያ ከአላህ ሌላ ረዳቶችን (ጣዖታትን) የያዙ ሰዎች ምሳሌ ቤትን እንደ ሠራች ሸረሪት ብጤ ነው፡፡ ከቤቶችም ሁሉ በጣም ደካማው የሸረሪት ቤት ነው፡፡ ቢያውቁ ኖሮ (አማልክት አድርገው አይግገዟቸውም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(29,42,'29:42','አላህ ያንን ከእርሱ ሌላ የሚግገዙትን ማንኛውንም ያውቃል፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,43,'29:43','እነዚህንም ምሳሌዎች ለሰዎች እንገልጻቸዋለን፡፡ ከሊቃውንቶቹም በስተቀር ሌሎቹ አያውቋትም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,44,'29:44','«አላህ ሰማያትንና ምድርን እውነተኛ ሆኖ ፈጠረ፡፡» በዚህ ውስጥ ለምእምናን ተዓምር አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,45,'29:45','ከመጽሐፍ ወደ አንተ የተወረደውን አንብብ፡፡ ሶላትንም ደንቡን ጠብቀህ ስገድ፡፡ ሶላት ከመጥፎና ከሚጠላ ነገር ሁሉ ትከለክላለችና፡፡ አላህንም ማውሳት ከሁሉ ነገር በላጭ ነው፤ አላህም የምትሠሩትን ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(29,46,'29:46','የመጽሐፉን ባለቤቶችም በዚያች እርሷ መልካም በኾነችው ክርክር እንጂ አትከራከሩ፡፡ ከእነርሱ እነዚያን የበደሉትን ሲቀር፡፡ በሉም «በዚያ ወደኛ በተወረደው ወደናንተም በተወረደው አመንን፡፡ አምላካችንም አምላካችሁም አንድ ነው፡፡ እኛም ለእርሱ ታዛዦች ነን፡፡»');
INSERT INTO "sadiq_sani" VALUES(29,47,'29:47','እንደዚሁም ወዳንተ መጽሐፍን አወረድን፡፡ እነዚያም መጽሐፍን የሰጠናቸው፤ በእርሱ ያምናሉ፡፡ ከእነዚህም (ከመካ ሰዎች) በእርሱ የሚያምኑ አልሉ፡፡ በተዓምራቶቻችንም ከሓዲዎቹ እንጂ ማንም አይክድም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,48,'29:48','ከእርሱ በፊትም መጽሐፍን የምታነብ በቀኝህም የምትጽፈው አልነበርክም፡፡ ያን ጊዜ አጥፊዎቹ በእርግጥ በተጠራጠሩ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(29,49,'29:49','አይደለም እርሱ (ቁርኣን) በእነዚያ ዕውቀትን በተሰጡት ሰዎች ልቦች ውስጥ (የጠለቀ) ግልጾች አንቀጾች ነው፡፡ በአንቀጾቻችንም በዳዮቹ እንጂ ሌላው አይክድም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,50,'29:50','«በእርሱ ላይም ከጌታህ ዘንድ ተዓምራቶች ለምን አልተወረዱም» አሉ፡፡ «ተዓምራቶች አላህ ዘንድ ብቻ ናቸው፡፡ እኔም ግልጽ አስፈራሪ ብቻ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,51,'29:51','እኛ መጽሐፉን በእነሱ ላይ የሚነበብ ኾኖ ባንተ ላይ ማውረዳችን አይበቃቸውምን በዚህ ውስጥ ለሚያምኑ ሕዝቦች ችሮታና ግሳጼ አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,52,'29:52','«በእኔና በእናንተ መካከል መስካሪ በአላህ በቃ፡፡ በሰማያትና በምድርም ያለውን ሁሉ ያውቃል» በላቸው፡፡ እነዚያም በብላሽ ነገር ያመኑ በአላህም የካዱ እነዚያ እነሱ ከሳሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,53,'29:53','በቅጣትም ያስቸኩሉሃል፡፡ የተወሰነም ጊዜ ባልነበረ ኖሮ ቅጣቱ በመጣባቸው ነበር፡፡ እነርሱ የማያውቁ ሲኾኑም በድንገት በእርግጥ ይመጣባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(29,54,'29:54','በቅጣት ያስቸኩሉሃል፡፡ ገሀነምም ከሓዲዎችን በእርግጥ ከባቢ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(29,55,'29:55','ቅጣቱ ከበላያቸው፣ ከእግሮቻቸውም ሥር በሚሸፍናቸው ቀን ትሠሩት የነበራችሁትን ቅመሱ በሚላቸውም (ቀን ትከባቸዋለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(29,56,'29:56','እናንተ ያመናችሁ ባሮቼ ሆይ! ምድሬ በእርግጥ ሰፊ ናት፤ (ብትቸገሩ ተሰደዱ)፡፡ እኔንም ብቻ ተገዙኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,57,'29:57','ነፍስ ሁሉ ሞትን ቀማሽ ናት፡፡ ከዚያም ወደኛ ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,58,'29:58','እነዚያም ያመኑና መልካም ሥራዎችን የሠሩ ከገነት በሥሮቻቸው ወንዞች የሚፈሱባቸውን ሰገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ በእርግጥ እናሰፍራቸዋለን፡፡ የሠራተኞች ምንዳ ምንኛ አማረ!');
INSERT INTO "sadiq_sani" VALUES(29,59,'29:59','(እነሱ) እነዚያ የታገሱ በጌታቸውም ላይ የሚመኩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(29,60,'29:60','ከተንቀሳቃሽም ምግቧን ለመሸከም የማትችለው ብዙ ናት፡፡ አላህ ይመግባታል፡፡ እናንተንም (ይመግባል)፡፡ እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(29,61,'29:61','ሰማያትንና ምድርን የፈጠረ ፀሐይንና ጨረቃንም የገራ ማን እንደኾነ ብትጠይቃቸው «በእርግጥ አላህ ነው» ይሉሃል፡፡ ታድያ እንዴት ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,62,'29:62','አላህ ከባሮቹ ለሚሻው ሲሳይን ያሰፋል፡፡ ለእርሱም ያጠብባል፡፡ አላህ በነገሩ ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(29,63,'29:63','ከሰማይም ውሃን ያወረደና በእርሱም ምድርን ከሞተች በኋላ ሕያው ያደረጋት ማን እንደኾነ ብትጠይቃቸው «አላህ ነው» ይሉሃል፡፡ «ምስጋና ለአላህ ነው» በላቸው፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(29,64,'29:64','ይህችም የቅርቢቱ ሕይወት መታለያና ጨዋታ እንጂ ሌላ አይደለችም፡፡ የመጨረሻይቱም አገር እርሷ በእርግጥ የዘላለም ሕይወት አገር ናት፡፡ የሚያውቁ ቢኾኑ ኖሮ፤ (ጠፊይቱን ሕይወት አይመርጡም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(29,65,'29:65','በመርከቦች ውስጥ በተሳፈሩ ጊዜም አላህን መገዛትን ለእርሱ ብቻ ያጠሩ ኾነው ይጠሩታል፡፡ ወደ የብስ (በማውጣት) ባዳናቸው ጊዜም ወዲያውኑ እነርሱ (ጣዖትን) ያጋራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,66,'29:66','በሰጠናቸው ጸጋ ሊክዱና ሊጠቃቀሙ (ያጋራሉ)፡፡ ወደፊትም (የሚጠብቃቸውን) ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(29,67,'29:67','ሰዎቹ ከዙሪያቸው ሲነጠቁ እኛ (አገራቸውን ) ጸጥተኛ ክልል ማድረጋችንን አያዩምን በውድቅ ነገር ያምናሉን በአላህም ጸጋ ይክዳሉን');
INSERT INTO "sadiq_sani" VALUES(29,68,'29:68','በአላህ ላይም ውሸትን ከቀጣጠፈ ሰው ወይም እውነቱ በመጣለት ጊዜ ከአስዋሸ ይበልጥ በዳይ ማነው በገሀነም ውስጥ ለከሓዲዎች መኖሪያ የለምን');
INSERT INTO "sadiq_sani" VALUES(29,69,'29:69','እነዚያም በእኛ መንገድ የታገሉ መንገዳችንን በእርግጥ እንመራቸዋለን፡፡ አላህም በእርግጥ ከበጎ ሠሪዎች ጋር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,1,'30:1','አ.ለ.መ (አሊፍ ላም ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(30,2,'30:2','ሩም ተሸነፈች፡፡');
INSERT INTO "sadiq_sani" VALUES(30,3,'30:3','በጣም ቅርብ በሆነችው ምድር፡፡ እነርሱም ከመሸነፋቸው በኋላ በእርግጥ ያሸንፋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,4,'30:4','በጥቂት ዓመታት ውስጥ (ያሸንፋሉ)፡፡ ትዕዛዙ በፊትም በኋላም የአላህ ነው፡፡ በዚያ ቀንም ምእመናን ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,5,'30:5','በአላህ እርዳታ (ይደሰታሉ)፡፡ የሚሻውን ሰው ይረዳል፤ እርሱም አሸናፊው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,6,'30:6','አላህ እርዳታን ቀጠረ፡፡ አላህ ቀጠሮውን አያፈርስም፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(30,7,'30:7','ከቅርቢቱ ህይወት ግልጹን ብቻ ያውቃሉ፡፡ እነርሱም ከኋለኛይቱ ዓለም እነርሱ ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,8,'30:8','በነፍሶቻቸው (ሁኔታ) አያስተውሉምን? ሰማያትንና ምድርን በመካከላቸው ያለውንም ሁሉ አላህ በእውነትና በተወሰነ ጊዜ እንጂ አልፈጠራቸውም፡፡ ከሰዎቹም ብዙዎቹ በጌታቸው መገናኘት ከሓዲዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,9,'30:9','በምድር ላይ አይኼዱምና የእነዚያን ከበፊታቸው የነበሩትን ሰዎች ፍጻሜ እንዴት እንደነበረ አይመለከቱምን? በኀይል ከእነርሱም ይበልጥ የጠነከሩ ነበሩ፡፡ ምድርንም አረሱ፡፡ (እነዚህ) ከአለሟትም የበዛ አለሟት፡፡ መልዕክተ ኞቻቸውም በተዓምራቶች መጡባቸው፤ (አስተባበሉምና ጠፉ)፡፡ አላህም የሚበድላቸው አልነበረም፤ ግን ነፍሶቻቸውን ይበድሉ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,10,'30:10','ከዚያም የእነዚያ ያጠፉት ሰዎች መጨረሻ በአላህ አንቀጾች በማስተባበላቸውና በእርሷ የሚሳለቁ በመኾናቸው ምክንያት መጥፎ ቅጣት ሆነች፡፡');
INSERT INTO "sadiq_sani" VALUES(30,11,'30:11','አላህ መፍጠርን ይጀምራል፡፡ ከዚያም ይመልሰዋል፤ ከዚያም ወደርሱ ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,12,'30:12','ሰዓቲቱም በምትቆምበት ቀን አመጸኞች ጭጭ ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,13,'30:13','ለእነርሱም ከሚያጋሩዋቸው (ጣዖታት) አማላጆች አይኖሯቸውም፤ በሚያጋሯቸውም (ጣዖታት) ከሓዲዎች ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,14,'30:14','ሰዓቲቱ በምትቆምበት ቀንም በዚያ ቀን ይለያያሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,15,'30:15','እነዚያ ያመኑትና መልካሞችን ሥራዎች የሠሩትማ እነርሱ በገነት ጨፌ ውስጥ ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,16,'30:16','እነዚያም የካዱትማ በአንቀጾቻችንና በኋለኛይቱም ዓለም መገናኘት ያስተባበሉት እነዚያ በቅጣቱ ውስጥ የሚጣዱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,17,'30:17','አላህንም በምታመሹ ጊዜ በምታነጉም ጊዜ አጥሩት፤ (ስገዱለት)፡፡');
INSERT INTO "sadiq_sani" VALUES(30,18,'30:18','ምስጋናም በሰማያትና በምድር ውስጥ ለርሱ ብቻ (የተገባው) ነው፡፡ በሰርክም፤ በቀትር ውስጥ በምትገቡም ጊዜ፤ (አጥሩት)፡፡');
INSERT INTO "sadiq_sani" VALUES(30,19,'30:19','ሕያውን ከሙት ያወጣል፡፡ ሙታንም ከሕያው ያወጣል፡፡ ምድርንም ከሞተች በኋላ ህያው ያደርጋታል፡፡ እንደዚሁም (ከመቃብር) ትወጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,20,'30:20','እናንተንም ከዐፈር መፍጠሩ ከዚያም እናንተ ወዲያውኑ የምትበታተኑ ሰዎች መኾናችሁ ከአስደናቂ ምልክቶቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,21,'30:21','ለእናንተም ከነፍሶቻችሁ (ከጎሶቻችሁ) ሚስቶችን ወደእነርሱ ትረኩ ዘንድ መፍጠሩ፣ በመካከላችሁም ፍቅርንና እዝነትን ማድረጉ ከአስደናቂ ምልክቶቹ ነው፡፡ በዚህ ውስጥ ለሚያስተውሉ ሕዝቦች ተዓምራቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,22,'30:22','ሰማያትንና ምድርንም መፍጠሩ፣ የቋንቋዎቻችሁና የመልኮቻችሁም መለያየት፣ ከአስደናቂ ምልክቶቹ ነው፡፡ በዚህ ውስጥ ለዐዋቂዎች ተዓምራቶች አልሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(30,23,'30:23','በሌሊትና በቀንም መተኛታችሁ፣ ከችሮታውም መፈለጋችሁ ከምልክቶቹ ነው፡፡ በዚህ ውስጥ ለሚሰሙ ሕዝቦች ተዓምራቶች አልሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(30,24,'30:24','ብልጭታንም ፈሪዎችና ከጃዮች ስትሆኑ ለእናንተ ማሳየቱ፣ ከሰማይም ውሃን ማውረዱ፣ በእርሱም ምድርን ከሞተች በኋላ ሕያው ማድረጉ ከአስደናቂ ምልክቶቹ ነው፡፡ በዚህ ውስጥ ለሚያስቡ ሕዝቦች ተዓምራቶች አልሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(30,25,'30:25','ሰማይና ምድርም (ያለምሰሶ) በትዕዛዙ መቆማቸው፣ ከዚያም (መልአኩ ለትንሣኤ) ከምድር ጥሪን በጠራችሁ ጊዜ እናንተ ወዲያውኑ የምትወጡ መሆናችሁ ከአስደናቂ ምልክቶቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,26,'30:26','በሰማያትና በምድር ያለው ሁሉ የእርሱ ብቻ ነው፡፡ ሁሉም ለእርሱ ታዛዦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,27,'30:27','እርሱም ያ መፍጠርን የሚጀምር ከዚያም የሚመልሰው ነው፡፡ እርሱም (መመለሱ) በእርሱ ላይ በጣም ገር ነው፡፡ ለእርሱም በሰማያትም በምድርም ከፍተኛ ባሕርይ (አንድነትና ለእርሱ ብጤ የሌለው መኾን) አልለው፡፡ እርሱም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,28,'30:28','ለእናንተ ከነፍሶቻችሁ የሆነን ምሳሌ አደረገላችሁ፡፡ (እርሱም) እጆቻችሁ ከያዟቸው (ባሮች) ውስጥ በሰጠናችሁ ጸጋ ለእናንተ ተጋሪዎች አሏችሁን? ታዲያ እናንተና እነርሱ በእርሱ ትክክል ናችሁን? ነፍሶቻችሁን (ብጤዎቻችሁን) እንደምትፈሩ ትፈሯቸዋላችሁን? እንደዚሁ ለሚያስተውሉ ሕዝቦች፤ አንቀጾችን እንገልጻለን፡፡');
INSERT INTO "sadiq_sani" VALUES(30,29,'30:29','ይልቁንም እነዚያ የበደሉ ሰዎች ያለ ዕውቀት ዝንባሌዎቻቸውን ተከተሉ፡፡ አላህም ያጠመመውን ሰው የሚያቀናው ማነው? ለእነርሱም ከረዳቶች ምንም የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(30,30,'30:30','ወደ እውነት ተዘንባይ ኾነህም ፊትህን ለሃይማኖት ቀጥ አድርግ፡፡ የአላህን ፍጥረት ያችን አላህ ሰዎችን በእርስዋ ላይ የፈጠረባትን (ሃይማኖት ያዟት)፡፡ የአላህን ፍጥረት መለወጥ የለም፡፡ ይህ ቀጥተኛ ሃይማኖት ነው፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(30,31,'30:31','ወደእርሱ ተመላሾች ሆናቸሁ (የአላህን ሃይማኖት ያዙ)፡፡ ፍሩትም፡፡ ሶላትንም አስተካክላችሁ ስገዱ፡፡ ከአጋሪዎቹም አትሁኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,32,'30:32','ከእነዚያ ሃይማኖታቸውን ከለያዩትና ክፍልፍሎችም ከሆኑት (አትሁኑ)፡፡ ሕዝብ ሁሉ እነሱ ዘንድ ባለው ነገር ተደሳቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,33,'30:33','ሰዎችንም ችግር ባገኛቸው ጊዜ ጌታቸውን ወደርሱ ተመላሾች ሆነው ይጠሩታል፡፡ ከዚያም ከእርሱ ችሮታን ባቀመሳቸው ጊዜ ከእነርሱ ከፊሎቹ ወዲያውኑ በጌታቸው (ጣዖትን) ያጋራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,34,'30:34','በሰጠናቸው ጸጋ ሊክዱ (ያጋራሉ)፡፡ ተጣቀሙም፤ በእርግጥም (መጨረሻችሁን) ወደፊት ታውቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,35,'30:35','በእነርሱ ላይ አስረጅ አወረድን? ታዲያ እርሱ በዚያ በእርሱ ያጋሩ በነበሩት ነገር ይናገራልን;(የለም)፡፡');
INSERT INTO "sadiq_sani" VALUES(30,36,'30:36','ሰዎችንም ችሮታን ባቀመስናቸው ገዜ በእርሷ ይደሰታሉ፡፡ እጆቻቸውም ባሳለፉት ምክንያት መከራ ብታገኛቸው ወዲያውኑ እነርሱ ተስፋ ይቆርጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,37,'30:37','አላህ ለሚሻው ሰው ሲሳይን የሚያሰፋ የሚያጠብም መሆኑን አያዩምን በዚህ ውስጥ ለሚያምኑ ሕዝቦች አስደናቂ ምልክቶች አልሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(30,38,'30:38','የዝምድናን ባለቤት ተገቢውን ስጠው፡፡ ለድኻም ለመንገደኛም (እርዳ)፡፡ ይህ ለእነዚያ የአላህን ፊት (ውዴታውን) ለሚሹ መልካም ነው፡፡ እነዚያም እነርሱ የፈለጉትን የሚያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,39,'30:39','ከበረከትም በሰዎች ገንዘቦች ውስጥ ይጨመር ዘንድ የምትሰጡት አላህ ዘንድ አይጨምርም፡፡ ከምጽዋትም የአላህን ፊት የምትሹ ሆናችሁ የምትሰጡት እነዚያ (ሰጪዎች) አበርካቾች እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,40,'30:40','አላህ ያ የፈጠራችሁ፣ ከዚያም ሲሳይን የሰጣችሁ፣ ከዚያም የሚገድላችሁ ከዚያም ሕያው የሚያደርጋችሁ ነው፡፡ ከምታጋሯቸው (ጣዖታት) ውስጥ ከዚህ ነገር አንዳችን የሚሠራ አለን ለአላህ ጥራት ይገባው፡፡ (በእርሱ) ከሚያጋሩትም ሁሉ ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,41,'30:41','የሰዎች እጆች በሠሩት ኃጢኣት ምክንያት የዚያን የሠሩትን ከፊሉን ያቀምሳቸው ዘንድ መከራው በየብስና በባሕር ተገለጠ፤ (ተሰራጨ)፡፡ እነርሱ ሊመለሱ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(30,42,'30:42','«በምድር ላይ ኺዱ የእነዚያንም በፊት የነበሩትን ሰዎች መጨረሻ እንዴት እንደነበረ ተመልከቱ» በላቸው፡፡ አብዛኞቻቸው አጋሪዎች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,43,'30:43','ለርሱ መመለስ የሌለው ቀን (የትንሣኤ ቀን) ከአላህ ሳይመጣ በፊት ፊትህን ወደ ቀጥተኛው');
INSERT INTO "sadiq_sani" VALUES(30,44,'30:44','የካደ ሰው ክህደቱ (ጠንቁ) በእርሱው ላይ ነው፡፡ መልካምም የሠሩ ለነፍሶቻቸው (ማረፊያዎችን) ያዘጋጃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,45,'30:45','እነዚያን ያመኑትንና መልካሞችንም የሠሩትን ከችሮታው ይመነዳ ዘንድ (ይለያያሉ)፡፡ እርሱ ከሓዲዎችን አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(30,46,'30:46','ነፋሶችንም (በዝናብ) ልታበስራችሁ፣ ከችሮታውም (በእርሷ) ሊያቀምሳችሁ፣ መርከቦችም በትዕዛዙ እንዲንሻለሉ፣ ከችሮታውም እንድትፈልጉ፣ ታመሰግኑትም ዘንድ መላኩ ከአስደናቂ ምልክቶቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,47,'30:47','ከአንተ በፊትም መልክተኞችን ወደየሕዝቦቻቸው በእርግጥ ላክን፡፡ በግልጽ ማስረጃዎችም መጡባቸው፡፡ ከእነዚያ ካመጹትም ተበቀልን፡፡ ምእመኖቹንም መርዳት በእኛ ላይ ተገቢ ሆነ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,48,'30:48','አላህ ያ ነፋሶችን የሚልክ ነው፡፡ ደመናንም ይቀሰቅሳሉ፡፡ በሰማይ ላይም እንደሚሻ ይዘረጋዋል፡፡ ቁርጥራጮችም ያደርገዋል፡፡ ዝናቡንም ከደመናው መካከል ሲወጣ ታያለህ፡፡ በእርሱም ከባሮቹ የሚሻውን በለየ ጊዜ ወዲያውኑ እነሱ ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,49,'30:49','በእነርሱም ላይ ከመወረዱ በፊት ከእርሱ በፊት በእርግጥ ተስፋ ቆራጮች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,50,'30:50','ምድርንም ከሞተች በኋላ እንዴት ሕያው እንደሚያደርጋት ወደ አላህ ችሮታ ፈለጎች ተመልከት፡፡ ይህ (አድራጊ) ሙታንንም በእርግጥ ሕያው አድራጊ ነው፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,51,'30:51','ነፋስንም (በአዝመራዎች ላይ) ብንልክና ገርጥቶ ቢያዩት ከእርሱ በኋላ በእርግጥ (ችሮታውን) የሚክዱ ይሆናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,52,'30:52','አንተም ሙታንን አታሰማም፡፡ ደንቆሮዎችንም ዟሪዎች ሆነው በሸሹ ጊዜ ጥሪን አታሰማም፡፡');
INSERT INTO "sadiq_sani" VALUES(30,53,'30:53','አንተ ዕውሮችንም ከጥመታቸው የምታቀና አይደለህም፡፡ በአንቀጾቻችን የሚያምኑትን ሰዎች እንጂ ሌላን አታሰማም፡፡ እነርሱም ታዛዦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,54,'30:54','አላህ ያ ከደካማ (ፍትወት ጠብታ) የፈጠራችሁ ነው፡፡ ከዚያም ከደካማነት በኋላ ኀይልን አደረገ፡፡ ከዚያም ከብርቱነት በኋላ ደካማነትንና ሽበትን አደረገ፡፡ የሚሻውን ይፈጥራል፡፡ እርሱም ዐዋቂው ቻዩ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(30,55,'30:55','ሰዓቲቱ በምትሆንበት ቀን ከሓዲዎች «ከአንዲት ሰዓት በስተቀር (በመቃብር) አልቆየንም» ብለው ይምላሉ፡፡ እንደዚሁ (ከእውነት) ይመለሱ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,56,'30:56','እነዚያንም ዕውቀትንና እምነትን የተሰጡት «በአላህ መጽሐፍ (ፍርድ) እስከ ትንሣኤ ቀን በእርግጥ ቆያችሁ፡፡ ይህም (የካዳችሁት) የትንሣኤ ቀን ነው፡፡ ግን እናንተ የማታውቁ ነበራችሁ» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(30,57,'30:57','በዚያም ቀን እነዚያን የበደሉትን ማመካኘታቸው አይጠቅማቸውም፡፡ እነርሱም በወቀሳ አይታለፉም፡፡');
INSERT INTO "sadiq_sani" VALUES(30,58,'30:58','በዚያም ቁርኣን ውስጥ ከምሳሌ ሁሉ ለሰዎች በእርግጥ ገለጽን፡፡ በተዓምርም ብትመጣባቸው እነዚያ የካዱት ሰዎች «እናንተ አበላሺዎች እንጂ ሌላ አይደላችሁም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(30,59,'30:59','እንደዚሁ አላህ በእነዚያ በማያውቁት ሰዎች ልቦች ላይ ያትማል፡፡');
INSERT INTO "sadiq_sani" VALUES(30,60,'30:60','ስለዚህ ታገስ፡፡ የአላህ ቀጠሮ እውነት ነውና፡፡ እነዚያም በትንሣኤ የሚያረጋግጡት አያቅልሉህ፡፡');
INSERT INTO "sadiq_sani" VALUES(31,1,'31:1','አ.ለ.መ. (አሊፍ ላም ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(31,2,'31:2','ይህች ጥበብ ከተመላው መጽሐፍ አንቀጾች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(31,3,'31:3','ለበጎ አድራጊዎች መሪና እዝነት ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(31,4,'31:4','ለእነዚያ ሶላትን አስተካክለው ለሚያደርሱት፣ ዘካንም ለሚሰጡት፣ እነርሱም በመጨረሻይቱ ዓለም እነርሱ የሚያረጋግጡ ለሆኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(31,5,'31:5','እነዚያ ከጌታቸው በሆነ ቅን መንገድ ላይ ናቸው፡፡ እነዚያም እነርሱ ምኞታቸውን ያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,6,'31:6','ከሰዎችም ያለ ዕውቀት ሆኖ ከአላህ መንገድ ሊያሳስትና ማላገጫ አድርጎ ሊይዛት አታላይ ወሬን የሚገዛ አልለ፡፡ እነዚያ ለእነርሱ አዋራጅ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,7,'31:7','አንቀጾቻችንም በእርሱ ላይ በሚነበቡ ጊዜ እንዳልሰማትና በጆሮቹ ድንቁርና እንዳለበት የኮራ ሆኖ ይዞራል፡፡ በአሳማሚ ቅጣትም አብስረው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,8,'31:8','እነዚያ ያመኑና መልካሞችንም የሠሩ ለእነርሱ የጸጋ ገነቶች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,9,'31:9','በእርሷ ውስጥ ዘውታሪዎች ሲሆኑ፤ አላህም እውነተኛን ተስፋ ቃል ገባላቸው፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,10,'31:10','ሰማያትን ያለምታዩዋት አዕማድ (ምሰሶዎች) ፈጠረ፡፡ በምድርም ውስጥ በእናንተ እንዳታረገርግ ተራራዎችን ጣለ፡፡ በእርሷም ላይ ከተንቀሳቃሽ ሁሉ በተነ፡፡ ከሰማይም ውሃን አወረድን፡፡ በእርሷም ውስጥ ከመልካም ዓይነት ሁሉ አበቀልን፡፡');
INSERT INTO "sadiq_sani" VALUES(31,11,'31:11','ይህ የአላህ ፍጡር ነው፡፡ እነዚያ ከእርሱ ሌላ ያሉት ምንን እንደፈጠሩ እስኪ አሳዩኝ፡፡ በእውነቱ በዳዮቹ በግልጽ ጥመት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,12,'31:12','ለሉቅማንም ጥበብን በእርግጥ ሰጠነው፡፡ (አልነውም)፡- «አላህን አመስግን፡፡» ያመሰገነም ሰው የሚያመሰግነው ለራሱ ብቻ ነው፡፡ የካደም ሰው (በራሱ ላይ ነው)፡፡ አላህ ተብቃቂ ምስጉን ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,13,'31:13','ሉቅማንም ለልጁ እርሱ የሚገሥጸው ሲኾን «ልጄ ሆይ! በአላህ (ጣዖትን) አታጋራ፡፡ ማጋራት ታላቅ በደል ነውና» ያለውን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(31,14,'31:14','ሰውንም በወላጆቹ (በጎ እንዲያደርግ) በጥብቅ አዘዝነው፡፡ እናቱ ከድካም በላይ በሆነ ድካም');
INSERT INTO "sadiq_sani" VALUES(31,15,'31:15','ለአንተ በእርሱ ዕውቀት የሌለህን ነገር በእኔ እንድታጋራ ቢታገሉህም አትታዘዛቸው፡፡ በቅርቢቱም ዓለም፤ በመልካም ሥራ ተወዳጃቸው፡፡ ወደእኔም የተመለሰን ሰው መንገድ ተከተል፡፡ ከዚያም መመለሻችሁ ወደእኔ ነው፡፡ ትሠሩት የነበራችሁትንም ሁሉ እነግራችኋለሁ፤ (አልነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(31,16,'31:16','(ሉቅማንም አለ) «ልጄ ሆይ! እርሷ የሰናፍጭ ቅንጣት ክብደት ያህል ብትሆንና በቋጥኝ ውስጥ ወይም በሰማያት ውስጥ ወይም በምድር ውስጥ ብትሆን አላህ ያመጣታል፡፡ አላህ ሩኅሩኅ ውስጥ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,17,'31:17','«ልጄ ሆይ! ሶላትን አስተካክለህ ስገድ፡፡ በበጎ ነገርም እዘዝ፡፡ ከሚጠላም ሁሉ ከልክል፡፡ በሚያገኝህም መከራ ላይ ታገስ፡፡ ይህ በምር ከሚያዙ ነገሮች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,18,'31:18','«ጉንጭህንም (በኩራት) ከሰዎች አታዙር፡፡ በምድርም ላይ ተንበጥርረህ አትሂድ፡፡ አላህ ተንበጥራሪን ጉረኛን ሁሉ አይወድምና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,19,'31:19','«በአካኼድህም መካከለኛ ኹን፡፡ ከድምጽህም ዝቅ አድርግ፡፡ ከድምጾች ሁሉ አስከፊው የአህዮች ድምጽ ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(31,20,'31:20','አላህ በሰማያት ያለውንና በምድርም ያለውን ሁሉ ለእናንተ ያገራላችሁ ጸጋዎቹንም ግልጽም ድብቅም ሲኾኑ የሞላላችሁ መኾኑን አታዩምን? ከሰዎችም ያለ ዕውቀትና ያለ መሪ፣ ያለግልጽ መጽሐፍም በአላህ የሚከራከር አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(31,21,'31:21','ለእነርሱም አላህ ያወረደውን ተከተሉ በተባሉ ጊዜ አይደለም በእርሱ ላይ አባቶቻችንን ያገኘንበትን እንከተላለን ይላሉ፡፡ ሰይጣን ወደ እሳት ቅጣት የሚጠራቸው ቢሆንም (ይከተሉዋቸዋልን?)');
INSERT INTO "sadiq_sani" VALUES(31,22,'31:22','እርሱ መልካም ሠሪ ሆኖ ፊቱን ወደ አላህ የሚሰጥም ሰው ጠንካራን ገመድ በእርግጥ ጨበጠ፡፡ የነገሩም ሁሉ ፍጻሜ ወደ አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,23,'31:23','የካደም ሰው ክህደቱ አያሳዝንህ፡፡ መመለሻቸው ወደኛ ነው፤ የሠሩትንም ሁሉ እንነግራቸዋለን፡፡ አላህ በልቦች ውስጥ ያለን ሁሉ ያውቃልና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,24,'31:24','ጥቂትን እናጣቅማቸዋለን፡፡ ከዚያም ወደ ብርቱ ቅጣት እናስገድዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(31,25,'31:25','ሰማያትንና ምድርንም የፈጠረ ማን እንደሆነ ብትጠይቃቸው በእርግጥ አላህ ነው ይላሉ፡፡ ምስጋና ለአላህ ይገባው በላቸው፡፡ ይልቁንም አብዛኛዎቹ አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(31,26,'31:26','በሰማያትና በምድር ያለው ሁሉ የአላህ ነው፡፡ አላህ እርሱ ተብቃቂ ምስጉን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,27,'31:27','ከዛፍም በምድር ያለው ሁሉ ብእሮች ቢሆኑ ኖሮ ባሕሩም ከእርሱ (ማለቅ) በኋላ ሰባት ባህሮች የሚጨመሩለት ሆኖ (ቀለም ቢሆንና ቢጻፍባቸው) የአላህ ቃላት ባላለቀች ነበር፡፡ አላህ አሸናፊ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,28,'31:28','እናንተን መፍጠርና እናንተን መቀስቀስ እንደ አንዲት ነፍስ እንጂ ሌላ አይደለም፡፡ አላህ ሰሚ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(31,29,'31:29','አላህ ሌሊትን በቀን ውስጥ የሚያስገባ፣ ቀንንም በሌሊት ውስጥ የሚያስገባ፣ ፀሐይንና ጨረቃንም የገራ መሆኑን አታይምን? ሁሉም እስከ ተወሰነ ጊዜ ድረስ ይሮጣሉ፡፡ አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,30,'31:30','ይህ አላህ እርሱ እውነት ከእርሱም ሌላ የሚግገዙት (ጣዖት) ውሸት በመሆኑና አላህም እርሱ የበላይ ታላቅ በመሆኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(31,31,'31:31','ከአስደናቂ ምልክቶቹ ሊያሳያችሁ መርከቦች በአላህ ችሮታ በባህር ውስጥ የሚንሻለሉ መሆናቸውን አታይምን? በዚህ ውስጥ በጣም ታጋሽ አመስጋኝ ለሆኑ ሁሉ ተዓምራቶች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(31,32,'31:32','እንደ ጥላዎችም የሆነ ማዕበል በሸፈናቸው ጊዜ አላህን ሃይማኖትን ለእርሱ ብቻ ያጠሩ ሆነው ይጠሩታል፡፡ ወደ የብስም ባዳናቸው ጊዜ ከእነሱ ትክክለኛም አልለ፡፡ (ከእነርሱም የሚክድ አለ)፡፡ በአንቀጾቻችንም አታላይ ከዳተኛ ሁሉ እንጂ ሌላው አይክድም፡፡');
INSERT INTO "sadiq_sani" VALUES(31,33,'31:33','እናንተ ሰዎች ሆይ! ጌታችሁን ፍሩ፡፡ ወላጅም ከልጁ (በምንም) የማይጠቅምበትን፤ ተወላጅም እርሱ ወላጅን በምንም ጠቃሚ የማይሆንበትን ቀን ፍሩ፡፡ የአላህ ቀጠሮ እውነት ነውና፡፡ ቅርቢቱም ሕይወት አትሸንግላችሁ፡፡ በአላህም (መታገስ) አታላዩ (ሰይጣን) አያታልላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(31,34,'31:34','አላህ የሰዓቲቱ ዕውቀት እርሱ ዘንድ ብቻ ነው፡፡ ዝናብንም ያወርዳል፡፡ በማሕፀኖችም ውስጥ ያለን ሁሉ ያውቃል፡፡ ማንኛይቱ ነፍስም ነገ የምትሠራውን አታውቅም፡፡ ማንኛይቱ ነፍስም በየትኛው ምድር እንደምትሞት አታውቅም፡፡ አላህ ዐዋቂ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,1,'32:1','አ.ለ.መ (አሊፍ ላም ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(32,2,'32:2','የመጽሐፉ መወረድ ጥርጥር የለበትም፡፡ ከዓለማት ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,3,'32:3','ይልቁንም «ቀጠፈው» ይላሉን? አይደለም፡፡ እርሱ ከጌታህ ዘንድ የሆነ እውነት ነው፡፡ በእርሱ ከአንተ በፊት ከአስፈራሪ (ነቢይ) ያልመጣባቸውን ሕዝቦች ልታስፈራራበት (ያወረደልህ ነው)፡፡ እነርሱ ሊምመሩ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(32,4,'32:4','አላህ ያ ሰማያትንና ምድርን በመካከላቸውም ያለውን ሁሉ በስድስት ቀኖች ውስጥ የፈጠረ ከዚያም በዐርሹ ላይ (ለሱ ክብር በሚስማማ መልኩ) የተደላደለ ነው፡፡ ከእርሱ ሌላ ለእናንተ ረዳትም አማላጅም ምንም የላችሁም፤ አትገሰጹምን?');
INSERT INTO "sadiq_sani" VALUES(32,5,'32:5','ነገሩን ሁሉ ከሰማይ ወደ ምድር ያዘጋጃል፡፡ ከዚያም ከምትቆጥሩት (ዘመን) ልኩ ሺህ ዓመት በሆነ ቀን ውስጥ ወደርሱ ይወጣል (ይመለሳል)፡፡');
INSERT INTO "sadiq_sani" VALUES(32,6,'32:6','ይህ (ይህንን የሠራው) ሩቁንም ቅርቡንም ሁሉ ዐዋቂው አሸናፊው፣ አዛኙ (ጌታ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,7,'32:7','ያ የፈጠረውን ነገር ሁሉ ያሳመረው የሰውንም ፍጥረት ከጭቃ የጀመረው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,8,'32:8','ከዚያም ዘሮቹን ከተንጣለለ፣ ከደካማ ውሃ ያደረገ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,9,'32:9','ከዚያም (ቅርጹን) አስተካከለው፡፡ በእርሱ ውስጥም ከመንፈሱ ነፋበት፤ (ነፍስ ዘራበት)፡፡ ለእናንተም ጆሮዎችን፣ ዓይኖችንና ልቦችንም አደረገላችሁ፡፡ በጣም ጥቂትን ብቻ ታመሰግናላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(32,10,'32:10','«በምድርም ውስጥ (በስብሰን) በጠፋን ጊዜ እኛ በአዲስ መፈጠር ውስጥ እንሆናለን?» አሉ፡፡ በእውነቱ እነርሱ በጌታቸው መገናኘት ከሓዲዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,11,'32:11','«በእናንተ የተወከለው መልአከ ሞት ይገድላችኋል፡፡ ከዚያም ወደ ጌታችሁ ትመለሳላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,12,'32:12','አመጸኞችም በጌታቸው ዘንድ ራሶቻቸውን ያቀረቀሩ ሆነው «ጌታችን ሆይ! አየን፣ ሰማንም፡፡ መልካምን እንሠራለንና (ወደ ምድረ ዓለም) መልሰን፡፡ እኛ አረጋጋጮች ነን፡፡» (የሚሉ ሲሆኑ) ብታይ ኖሮ (አስደንጋጭን ነገር ታይ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(32,13,'32:13','በሻንም ኖሮ ነፍስን ሁሉ ቅንነቷን (እምነቷን) በሰጠናት ነበር፡፡ ግን ገሀነምን ከአጋንንትና ከሰዎች የተሰበሰቡ ሆነው በእርግጥ እሞላለሁ ማለት ቃሉ ከእኔ ተረጋግጧል፡፡');
INSERT INTO "sadiq_sani" VALUES(32,14,'32:14','ይህንንም ቀናችሁን መገናኘትን በመርሳታችሁ ምክንያት (ቅጣትን) ቅመሱ! እኛ ተውናችሁ፡፡ ትሠሩት በነበራችሁትም ጥፋት ዘውታሪን ቅጣት ቅመሱ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(32,15,'32:15','በአንቀጾቻችን የሚያምኑት እነዚያ በእርሷ በተገሰጹ ጊዜ ሰጋጆች ሆነው የሚወድቁትና እነርሱም የማይኮሩ ሆነው በጌታቸው ምስጋና (ተጎናጽፈው) የሚያወድሱት ብቻ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,16,'32:16','ጌታቸውን ለመፍራትና ለመከጀል የሚጠሩት ሆነው ጎኖቻቸው ከመጋደሚያ ስፍራዎች ይራራቃሉ፡፡ ከሰጠናቸውም (ጸጋ) ይለገሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(32,17,'32:17','ይሠሩትም በነበሩት ለመመንዳት ከዓይኖች መርጊያ ለእነርሱ የተደበቀላቸውን (ጸጋ) ማንኛይቱም ነፍስ አታውቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(32,18,'32:18','አማኝ የሆነ ሰው አመጸኛ እንደ ሆነ ሰው ነውን? አይስተካከሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(32,19,'32:19','እነዚያ ያመኑና መልካሞችን የሰሩማ ለእነርሱ ይሠሩት በነበሩት ምክንያት መስተንግዶ ሲሆኑ መኖሪያ ገነቶች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,20,'32:20','እነዚያ ያመጹትማ፤ መኖሪያቸው እሳት ናት፡፡ ከእርሷ መውጣትን በፈለጉ ቁጥር በእርሷ ውስጥ ይመለሳሉ፡፡ ለእነርሱም «ያንን በእርሱ ታስተባብሉበት የነበራችሁትን የእሳት ቅጣት ቅመሱ» ይባላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(32,21,'32:21','ይመለሱ ዘንድም ከትልቁ ቅጣት በፊት (በዚህ ዓለም) ከትንሹ ቅጣት በእርግጥ እናቀምሳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(32,22,'32:22','በጌታውም አንቀጾች ከተገሰጸና ከዚያም ከተዋት ሰው ይበልጥ በደለኛ ማነው? (የለም)፤ እኛ ከተንኮለኞቹ ተበቃዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(32,23,'32:23','ሙሳንም መጽሐፍን በእርግጥ ሰጠነው፡፡ እርሱን ከመገናኘትህም በመጠራጠር ውስጥ አትሁን፡፡ ለእስራኤል ልጆችም መሪ አደረግነው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,24,'32:24','በታገሱና በተዓምራቶቻችን የሚያረጋግጡ በሆኑም ጊዜ ከእነርሱ በትዕዛዛችን የሚመሩ መሪዎች አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(32,25,'32:25','ጌታህ እርሱ በዚያ በእርሱ ይለያዩበት በነበሩት ነገር በትንሣኤ ቀን በመካከላቸው (በፍርድ) ይለያል፡፡');
INSERT INTO "sadiq_sani" VALUES(32,26,'32:26','ከክፍለ ዘመናት ሰዎች ብዙዎችን ያጠፋን መሆናችን በመኖሪያዎቻቸው የሚሄዱ ሲሆኑ');
INSERT INTO "sadiq_sani" VALUES(32,27,'32:27','እኛ ውሃን ወደ ደረቅ ምድር የምንነዳ እንስሶቻቸውና ነፍሶቻቸው ከእርሱ የሚበሉለትንም አዝመራ በእርሱ የምናወጣ መኾናችንን አያዩምን? አይመለከቱምን?');
INSERT INTO "sadiq_sani" VALUES(32,28,'32:28','«ከእውነተኞችም እንደሆናችሁ ይህ ፍርድ መቼ ነው?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(32,29,'32:29','«በፍርድ ቀን እነዚያን የካዱትን ሰዎች እምነታቸው አይጠቅማቸውም፡፡ እነርሱም ጊዜን አይሰጡም፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(32,30,'32:30','እነርሱንም ተዋቸው፡፡ ተጠባበቅም፤ እነርሱ ተጠባባቂዎች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,1,'33:1','አንተ ነቢዩ ሆይ! አላህን ፍራ፡፡ ከሓዲዎችንና መናፍቃንንም አትታዘዝ፡፡ አላህ ዐዋቂ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,2,'33:2','ከጌታህም ወደ አንተ የሚወረደውን ተከተል፡፡ አላህ በምትሠሩት ሁሉ ውሰጠ አዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,3,'33:3','በአላህም ላይ ተመካ፡፡ መመኪያም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,4,'33:4','አላህ ለአንድ ሰው በሆዱ ውስጥ ሁለትን ልቦች አላደረገም፡፡ ሚስቶቻችሁንም እነዚያን ከእነርሱ እንደናቶቻችሁ ጀርባዎች ይሁኑብን የምትሏቸውን እናቶቻችሁ አላደረገም፡፡ ልጅ በማድረግ የምታስጠጓቸውንም ልጆቻችሁ አላደረገም፡፡ ይህ በአፎቻችሁ የምትሉት ነው፡፡ አላህም እውነቱን ይናገራል፡፡ እርሱም ትክክለኛውን መንገድ ይመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,5,'33:5','ለአባቶቻቸው (በማስጠጋት) ጥሯቸው፡፡ እርሱ አላህ ዘንድ ትክክለኛ ነው፡፡ አባቶቻቸውንም ባታውቁ በሃይማኖት ወንድሞቻችሁና ዘመዶቻችሁ ናቸው፡፡ በእርሱ በተሳሳታችሁበት ነገርም በእናንተ ላይ ኀጢአት የለባችሁም፡፡ ግን ልቦቻችሁ ዐውቀው በሠሩት (ኀጢአት');
INSERT INTO "sadiq_sani" VALUES(33,6,'33:6','ነቢዩ በምእምናን ከነፍሶቻቸው ይበልጥ ተገቢ ነው፡፡ ሚስቶቹም እናቶቻቸው ናቸው፡፡ የዝምድና ባለቤቶችም ከፊላቸው በከፊሉ (ውርስ) በአላህ መጽሐፍ ውስጥ ከምእምናንና ከስደተኞቹ ይልቅ የተገባቸው ናቸው፡፡ግን ወደ ወዳጆቻችሁ (በኑዛዜ) መልካምን ብትሰሩ (ይፈቀዳል)፡፡ ይህ በመጽሐፉ ውሰጥ የተመዘገበ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,7,'33:7','ከነቢዮችም የጠበቀ ኪዳናቸውን ከአንተም፣ ከኑሕም፣ ከኢብራሒምም፣ ከሙሳም ከመርየም ልጅ ዒሳም በያዝን ጊዜ (አስታወስ)፡፡ ከእነርሱም የከበደን ቃል ኪዳን ያዝን፡፡');
INSERT INTO "sadiq_sani" VALUES(33,8,'33:8','(አላህ) እውነተኞችን ከውነታቸው ሊጠይቅ (ይህንን ሠራ)፡፡ ለከሓዲዎችም አሳማሚን ቅጣት አዘጋጀ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,9,'33:9','እናንተ ያመናችሁ ሆይ! ብዙ ሰራዊት በመጣችባችሁና በእነርሱ ላይ ነፋስንና ያላያችኋትን ሰራዊት በላክን ጊዜ በእናንተ ላይ (ያደረገላችሁን) የአላህን ጸጋ አስታውሱ፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,10,'33:10','ከበላያችሁም ከናንተ በታችም በመጡባችሁ ጊዜ፣ ዓይኖችም በቃበዙ፣ ልቦችም ላንቃዎች በደረሱና በአላህም ጥርጣሬዎችን በጠረጠራችሁ ጊዜ (ያደረገላችሁን አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(33,11,'33:11','እዚያ ዘንድ ምእምናን ተሞከሩ፡፡ ብርቱን መንቀጥቀጥም ተንቀጠቀጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,12,'33:12','መናፍቆችና እነዚያ በልቦቻቸው ውስጥ በሽታ ያለባቸውም «አላህና መልክተኛው ማታለልን እንጂ ሌላን አልቀጠሩንም» በሚሉ ጊዜ (ያደረገላችሁን አስታውሱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(33,13,'33:13','ከእነርሱም የሆኑ ጭፍሮች «የየስሪብ ሰዎች ሆይ! ለእናንተ ስፍራ የላችሁምና ተመለሱ» ባሉ ጊዜ (አስታውሱ)፡፡ ከእነርሱም «ከፊሎቹ እርሷ ነውረኛ ሳትሆን ቤቶቻችን ነውረኛ ናቸው» የሚሉ ሆነው ነቢዩን (ከጦሩ ለመመለስ) ፈቃድን ይጠይቃሉ፡፡ መሸሽን እንጂ ሌላ አይፈልጉም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,14,'33:14','በእነርሱም ላይ (ቤቶቻቸው) ከየቀበሌዋ በተገባባት ከዚያም ከኢስላም መመለስን በተጠየቁ ኖሮ በሠሯት ነበር፡፡ በእርሷም ጥቂትን ጊዜ እንጂ አይቆዩም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(33,15,'33:15','ጀርባዎችንም ላያዞሩ ከዚያ በፊት በእርግጥ አላህን ቃል ኪዳን የተጋቡ ነበሩ፡፡ የአላህም ቃል ኪዳን የሚጠየቁበት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,16,'33:16','ከሞት ወይም ከመገደል ብትሸሹ መሸሻችሁ ፈጽሞ አይጠቅማችሁም፡፡ ያን ጊዜም ጥቂትን እንጂ የምትጣቀሙ አትደረጉም በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,17,'33:17','«(አላህ) በእናንተ ክፉን ነገር ቢሻ ያ ከአላህ የሚጠብቃችሁ ማነው? ወይም ለእናንተ ችሮታን ቢሻ፤ (በክፉ የሚነካችሁ ማነው?)» በላቸው፤ ከአላህም ሌላ ለእነርሱ ወዳጅም ረዳትም አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,18,'33:18','ከእናንተ ውስጥ የሚያሳንፉትን፣ ለወንድሞቻቸውም «ወደኛ ኑ» የሚሉትን፣ ውጊያንም ጥቂትን እንጂ የማይመጡትን በእርግጥ አላህ ያውቃቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,19,'33:19','በእናንተ ላይ (እርዳታን) የነፈጉ ሆነው እንጅ (የማይመጡትን)፣ ሽብሩም በመጣ ጊዜ እንደዚያ ከሞት (መከራ) በርሱ ላይ የሚሸፍን ዐደጋ እንደወደቀበት ዓይኖቻቸው ወዲያና ወዲህ የምትዞር ኾና ወዳንተ ሲመለከቱ ታያቸዋለህ፡፡ ሽብሩም በኼደ ጊዜ በገንዘብ ላይ የሚሳሱ ኾነው በተቡ ምላሶች ይነድፏችኋል፡፡ እነዚያ አላመኑም፡፡ ስለዚህ አላህ ሥራዎቻቸውን አበላሸ፡፡ ይህም በአላህ ላይ ገርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,20,'33:20','(መናፍቆች) አሕዛብን አልኼዱም ብለው ያስባሉ፡፡ አሕዛቦቹም ቢመጡ እነርሱ በዘላኖች ውስጥ በገጠር (የራቁ) ሊሆኑ ይመኛሉ፡፡ ከወሬዎቻችሁ ይጠይቃሉ፡፡ በእናንተ ውስጥ በነበሩም ኖሮ ጥቂትን እንጂ አይዋጉም ነበር፡');
INSERT INTO "sadiq_sani" VALUES(33,21,'33:21','ለእናንተ አላህንና የመጨረሻውን ቀን የሚከጅል ለሆነ ሰው፣ አላህንም በብዙ ለሚያወሳ በአላህ መልዕክተኛ መልካም መከተል አልላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,22,'33:22','አማኞቹም አሕዛብን ባዩ ጊዜ «ይህ አላህና መልክተኛው የቀጠሩን ነው፡፡ አላህና መልክተኛውም እውነትን ተናገሩ» አሉ፡፡ (ይህ) እምነትንና መታዘዝንም እንጂ ሌላን አልጨመረላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,23,'33:23','ከአማኞቹ በእርሱ ላይ ለአላህ ቃል ኪዳን የገቡበትን በእውነት የፈጸሙ ወንዶች አልሉ፡፡ ከነርሱም ስለቱን የፈጸመ (ለሃይማኖቱ የተገደለ) አልለ፤ ከነርሱም ገና የሚጠባበቅ አልለ፡፡ (የገቡበትን ቃል) መለወጥንም አልለወጡም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,24,'33:24','አላህ እውነተኞችን በውነተኛነታቸው ሊመነዳ መናፍቃንንም ቢሻ ሊቀጣ ወይም (ቢመለሱ) በእነርሱ ላይ ጸጸታቸውን ሊቀበል (ይህን አደረገ)፡፡ አላህ መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,25,'33:25','እነዚያንም የካዱትን በቁጭታቸው የተመሉ መልካምን ነገር ያላገኙ ሆነው አላህ');
INSERT INTO "sadiq_sani" VALUES(33,26,'33:26','እነዚያንም ከመጽሐፉ ባለቤቶች (አሕዛቦችን) የረዷቸውን (ቁረይዟን) ከምሽጎቻቸው አወረዳቸው፡፡ በልቦቻቸውም ውስጥ መባባትን ጣለባቸው፡፡ ከፊሉን ትገድላላችሁ፡፡ ከፊሉንም ትማርካላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,27,'33:27','ምድራቸውንም፣ ቤቶቻቸውንም፣ ገንዘቦቻቸውንም ገና ያልረገጣችኋትንም ምድር አወረሳችሁ፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,28,'33:28','አንተ ነቢይ ሆይ! ለሚስቶችህ (እንዲህ) በላቸው «ቅርቢቱን ሕይወትና ጌጧን የምትፈልጉ እንደ ሆናችሁ ኑ፤ አጣቅማችኋለሁና፡፡ መልካምንም ማሰማራት (በመፍታት) አሰማራችኋለሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,29,'33:29','«አላህንና መልክተኛውን የመጨረሻይቱን አገርም የምትፈልጉ ብትሆኑም፤ እነሆ አላህ ከእናንተ ለመልካም ሠሪዎቹ ትልቅን ምንዳ አዘጋጅቷል፡፡»');
INSERT INTO "sadiq_sani" VALUES(33,30,'33:30','የነቢዩ ሴቶች ሆይ! ከእናንተ ውስጥ ግልጽ የሆነን መጥፎ ሥራ የምትሠራ ለእርሷ ቅጣቱ ሁለት እጥፍ ሆኖ ይነባበርባታል፡፡ ይህም በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,31,'33:31','ከእናንተም ለአላህና ለመልክተኛው የምትታዘዝ መልካም ሥራንም የምትሠራ ምንዳዋን ሁለት ጊዜ እንሰጣታለን፡፡ ለእርሷም የከበረን ሲሳይ አዘጋጅተንላታል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,32,'33:32','የነቢዩ ሴቶች ሆይ! ከሴቶች እንደማናቸው አይደላችሁም፡፡ አላህን ብትፈሩ (ትበልጣላችሁ)፡፡ ያ በልቡ ውስጥ በሽታ ያለበት እንዳይከጅል በንግግር አትለስልሱም፡፡ መልካምንም ንግግር ተናገሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,33,'33:33','በቤቶቻችሁም ውስጥ እርጉ፡፡ እንደ ፊተኛይቱ መሃይምነት ጊዜ መገለጥም በማጌጥ አትገለጡ፡፡ ሶላትንም በደንቡ ስገዱ፡፡ ዘካንም ስጡ፡፡ አላህንና መልክተኛውንም ታዘዙ፡፡ የነቢዩ ቤተሰቦች ሆይ! አላህ የሚሻው ከእናንተ ላይ እርክሰትን ሊያስወግድና ማጥራትንም ሊያጠራችሁ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,34,'33:34','ከአላህ አንቀጾችና ከጥበቡም በቤቶቻችሁ የሚነበበውን አስታውሱ፡፡ አላህ እዝነቱ ረቂቅ ውስጠ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,35,'33:35','ሙስሊሞች ወንዶችና ሙስሊሞች ሴቶች፣ ምእምናንና ምእምናትም፣ ታዛዦች ወንዶችና ታዛዦች ሴቶችም፣ እውነተኞች ወንዶችና እውነተኞች ሴቶችም፣ ታጋሾች ወንዶችና ታጋሾች ሴቶችም፣ አላህን ፈሪዎች ወንዶችና አላህን ፈሪዎች ሴቶችም፣ መጽዋቾች ወንዶችና መጽዋቾች ሴቶችም፣ ጿሚዎች ወንዶችና ጿሚዎች ሴቶችም፣ (ካልተፈቀደ ሥራ) ብልቶቻቸውን ጠባቂዎች ወንዶችና ጠባቂ ሴቶችም፣ አላህን በብዙ አውሺዎች ወንዶችና አውሺዎች ሴቶችም፣ አላህ ለእነርሱ ምሕረትንና ታላቅን ምንዳ አዘጋጅቶላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,36,'33:36','አላህና መልክተኛውም ነገርን በፈረዱ ጊዜ ለምእምናንና ለምእምናት ከነገራቸው ለእነርሱ ምርጫ ሊኖራቸው አይገባም! የአላህንና የመልክተኛውንም ትእዛዝ የጣሰ ሰው ግልጽ የሆነን መሳሳት በእርግጥ ተሳሳተ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,37,'33:37','ለእዚያም አላህ በእርሱ ላይ ለለገሰለትና (አንተም ነጻ በማውጣት) በእርሱ ላይ ለለገስክለት ሰው (ለዘይድ) አላህ ገላጩ የሆነን ነገር በነፍስህ ውስጥ የምትደብቅና አላህ ልትፈራው ይልቅ የተገባው ሲሆን ሰዎችን የምትፈራ ሆነህ «ሚስትህን ባንተ ዘንድ ያዝ፤ (ከመፍታት) አላህንም ፍራ» በምትል ጊዜ (አስታውስ)፡፡ ዘይድም ከእርሷ ጉዳይን በፈጸመ ጊዜ በምእምናኖች ላይ ልጅ አድርገው ባስጠጉዋቸው (ሰዎች) ሚስቶች ከእነርሱ ጉዳይን በፈጸሙ ጊዜ (በማግባት) ችግር እንዳይኖርባቸው እርሷን አጋባንህ፡፡ የአላህም ትእዛዝ ተፈጻሚ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,38,'33:38','በነቢዩ ላይ አላህ ለእርሱ በፈረደው ነገር ምንም ችግር ሊኖርበት አይገባም፡፡ በእነዚያ ከዚያ በፊት ባለፉት (ነቢያት) አላህ ደነገገው፡፡ የአላህም ትእዛዝ የተፈረደ ፍርድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,39,'33:39','ለእነዚያ የአላህን መልእክቶች ለሚያደርሱና ለሚፈሩት ከአላህ በስተቀርም አንድንም ለማይፈሩት (ተደነገገ)፡፡ ተቆጣጣሪም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,40,'33:40','ሙሐመድ ከወንዶቻችሁ የአንድም ሰው አባት አይደለም፡፡ ግን የአላህ መልክተኛና የነቢዮች መደምደሚያ ነው፡፡ አላህም በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,41,'33:41','እናንተ ያመናችሁ ሆይ! አላህን ብዙ ማውሳትን አውሱት፡፡');
INSERT INTO "sadiq_sani" VALUES(33,42,'33:42','በቀኑ መጀመሪያና መጨረሻም አጥሩት፡፡');
INSERT INTO "sadiq_sani" VALUES(33,43,'33:43','እርሱ ያ በእናንተ ላይ እዝነትን የሚያወርድ ነው፡፡ መላእክቶቹም (እንደዚሁ ምሕረትን የሚለምኑላችሁ ናቸው)፡፡ ከጨለማዎች ወደ ብርሃን ያወጣችሁ ዘንድ (ያዝንላችኋል)፡፡ ለአማኞችም በጣም አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,44,'33:44','በሚገናኙት ቀን መከባበሪያቸው ሰላም መባባል ነው፡፡ ለእነርሱም የከበረን ምንዳ አዘጋጅቶላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,45,'33:45','አንተ ነቢዩ ሆይ! እኛ መስካሪ፣ አብሳሪና አስፈራሪም አድርገን ላክንህ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,46,'33:46','ወደ አላህም በፈቃዱ ጠሪ፣ አብሪ ብርሃንም (አድርገን ላክንህ)፡፡');
INSERT INTO "sadiq_sani" VALUES(33,47,'33:47','አማኞችንም ከአላህ ዘንድ ለእነርሱ ታላቅ ችሮታ ያላቸው መኾኑን አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,48,'33:48','ከሓዲዎችንና መናፍቃንንም አትታዘዛቸው፡፡ ማሰቃየታቸውንም (ለአላህ) ተው፡፡ በአላህም ላይ ተጠጋ፡፡ መጠጊያም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,49,'33:49','እናንተ ያመናችሁ ሆይ! ምእምናትን ባገባችሁና ከዚያም ሳትነኩዋቸው በፊት በፈታችኋቸው ጊዜ ለእናንተ በእነርሱ ላይ የምትቆጥሩዋት ዒዳ ምንም የላችሁም፡፡ አጣቅሟቸውም፤ (ጉርሻ ስጧቸው)፡፡ መልካም ማሰናበትንም አሰናብቷቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,50,'33:50','አንተ ነቢዩ ሆይ! እኛ እነዚያን መህሮቻቸውን የሰጠሃቸውን ሚስቶችህን፣ አላህ ባንተ ላይ ከመለሰልህም እነዚያን እጅህ የጨበጠቻቸውን ምርኮኞች፣ እነዚያንም ከአንተ ጋር የተሰደዱትን የአጎትህን ሴቶች ልጆች፣ የአክስቶችህንም ሴቶች ልጆች፣ የየሹማህንም ሴቶች ልጆች፣ የየሹሜዎችህንም ሴቶች ልጆች (ማግባትን) ለአንተ ፈቅደንልሃል፡፡ የአመነችንም ሴት ነፍሷን (ራሷን) ለነቢዩ ብትሰጥ ነቢዩ ሊያገባት የፈለገ እንደ ኾነ ከምእምናን ሌላ ላንተ ብቻ የጠራች ስትኾን (ፈቀድንልህ)፡፡ በእነርሱ (በምእምናን) ላይ በሚስቶቻቸውና እጆቻቸው በጨበጧቸው (ባሮች ነገር) ግዴታ ያደረግንባቸውን በእርግጥ ዐውቀናል፡፡ ባንተ ላይ ችግር እንዳይኖር (ያለፉትን ፈቀድንልህ)፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,51,'33:51','ከእነርሱ የምትሻትን ታቆያለህ፡፡ የምትሻውንም ወደ አንተ ታስጠጋለህ፡፡ (በመፍታት) ከአራቅሃትም የፈለግሃትን (በመመለስ ብታስጠጋ) በአንተ ላይ ኀጢአት የለብህም፡፡ ይህ ዓይኖቻቸው ወደ መርጋት፣ ወደ አለማዘናቸውም፣ ለሁሉም በሰጠሃቸው ነገር ወደ መውደዳቸውም በጣም የቀረበ ነው፡፡ አላህም በልቦቻችሁ ውስጥ ያለውን ሁሉ ያውቃል፡፡ አላህም ዐዋቂ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,52,'33:52','ከእነዚህ በኋላ እጅህ ከጨበጠቻቸው (ባሮች) በስተቀር ሴቶች ለአንተ አይፈቀዱልህም፡፡ ከሚስቶችም መልካቸው ቢደንቅህም እንኳ በእነርሱ ልታላውጥ (አይፈቀደልህም)፡፡ አላህም በነገሩ ሁሉ ላይ ተጠባባቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,53,'33:53','እናንተ ያመናችሁ ሆይ! መድረሱን የማትጠባበቁ ስትኾኑ ወደ ምግብ ካልተፈቀደላችሁ በስተቀር የነቢዩን ቤቶች (በምንም ጊዜ) አትግቡ፡፡ ግን በተጠራችሁ ጊዜ ግቡ፡፡ በተመገባችሁም ጊዜ ወዲያውኑ ተበተኑ፡፡ ለወግ የምትጫወቱ ኾናችሁም (አትቆዩ)፡፡ ይህ ነቢዩን በእርግጥ ያስቸግራል፡፡ ከእናንተም ያፍራል፡፡ ግን አላህ ከእውነት አያፍርም፡፡ ዕቃንም (ለመዋስ) በጠየቃችኋቸው ጊዜ ከመጋረጃ ኋላ ኾናችሁ ጠይቋቸው፡፡ ይህ ለልቦቻችሁ፤ ለልቦቻቸውም የበለጠ ንጽሕና ነው፡፡ የአላህንም መልክተኛ ልታስቸግሩ ሚስቶቹንም ከእርሱ በኋላ ምንጊዜም ልታገቡ ለእናንተ አይገባችሁም፡፡ ይህ አላህ ዘንድ ከባድ (ኀጢአት) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,54,'33:54','ማንኛውንም ነገር ብትገልጹ ወይም ብትደብቁት (በርሱ ይመነዳችኋል)፡፡ አላህ ነገሩን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,55,'33:55','(የነቢዩ ሚስቶች) በአባቶቻቸው፣ በወንዶች ልጆቻቸውም፣ በወንድሞቻቸውም፣ ወንድሞቻቸው ወንዶች ልጆችም፣ በእኅቶቻቸው ወንዶች ልጆችም፣ (ምእምናት በኾኑት) በሴቶቻቸውም፣ እጆቻቸው በጨበጧቸውም ባሮች (አጠገብ በመገለጥ) በእነርሱ ላይ ኃጢአት የለባቸውም፡፡ (ሴቶች ሆይ! ታዘዙ) አላህንም ፍሩ፡፡ አላህ በነገሩ ሁሉ ላይ መስካሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,56,'33:56','አላህና መላእክቱ በነቢዩ ላይ የአክብሮት እዝነትን ያወርዳሉ፡፡ እናንተ ያመናችሁ ሆይ! በእርሱ ላይ የአክብሮት እዝነትን አውርዱ፡፡ የማክበርንም ሰላምታ ሰላም በሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,57,'33:57','እነዚያ አላህንና መልክተኛውን የሚያስከፉ በቅርቢቱ ዓለም በመጨረሻይቱም አላህ ረግሟቸዋል፡፡ ለእነርሱም አዋራጅን ቅጣት ደግሶላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,58,'33:58','እነዚያንም ምእምናንንና ምእምናነትን ባልሰሩት ነገር (በመዝለፍ) የሚያሰቃዩ ዕብለትንና ግልጽ ኃጢአትን በእርግጥ ተሸከሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,59,'33:59','አንተ ነቢዩ ሆይ! ለሚስቶቸህ፣ ለሴቶች ልጆችህም፣ ለምእምናን ሚስቶችም ከመከናነቢያዎቻቸው በላያቸው ላይ እንዲለቁ ንገራቸው፡፡ ይህ እንዲታወቁና (በባለጌዎች) እንዳይደፈሩ ለመኾን በጣም የቀረበ ነው፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(33,60,'33:60','መናፍቃንና እነዚያ በልቦቻቸው ውስጥ (የአመንዝራነት) በሽታ ያለባቸው በመዲናም ውሰጥ (በወሬ) አሸባሪዎቹ (ከዚህ ሥራቸው) ባይከለከሉ በእነርሱ ላይ በእርግጥ እንቀሰቅስሃለን፡፡ ከዚያም በእርሷ ውስጥ ጥቂትን እንጂ አይጎራበቱህም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,61,'33:61','የተረገሙ ኾነው እንጂ (አይጎራበቱህም)፡፡ በየትም ስፍራ ቢገኙ ይያዛሉ፡፡ መገደልንም ይገደላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,62,'33:62','(ይህቺ) በእነዚያ በፊት ባለፉት (ላይ የደነገጋት) የአላህ ድንጋጌ ናት፡፡ ለአላህም ድንጋጌ ፈጽሞ መለወጥን አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,63,'33:63','ሰዎች ከሰዓቲቱ ይጠይቁሃል፡፡ «ዕውቀትዋ አላህ ዘንደ ብቻ ነው፤» በላቸው፡፡ የሚያሳወቅህም ምንድን ነው! ሰዓቲቱ በቅርብ ጊዜ ልትኾን ይከጀላል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,64,'33:64','አላህ ከሓዲዎችን በእርግጥ ረግሟቸዋል፡፡ ለእነርሱም የተጋጋመችን እሳት አዘጋጅቷል፡፡');
INSERT INTO "sadiq_sani" VALUES(33,65,'33:65','በእርሷ ውስጥ ዘለዓለም ዘውታሪዎች ሲኾኑ፤ ወዳጅም ረዳትም አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(33,66,'33:66','ፊቶቻቸው በእሳት ውሰጥ በሚገለባበጡ ቀን «ዋ ምኞታችን! አላህን በተገዛን መልክተኛውንም በታዘዝን ኖሮ» እያሉ ይጸጸታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,67,'33:67','ይላሉም «ጌታችን ሆይ! እኛ ጌቶቻችንንና ታላላቆቻችንን ታዘዝን፡፡ መንገዱንም አሳሳቱን፡፡');
INSERT INTO "sadiq_sani" VALUES(33,68,'33:68','«ጌታችን ሆይ! ከቅጣቱ እጥፍን ስጣቸው፡፡ ታላቅን እርግማንም እርገማቸው፡፡»');
INSERT INTO "sadiq_sani" VALUES(33,69,'33:69','እናንተ ያመናችሁ ሆይ! እንደእነዚያ ሙሳን እንዳሰቃዩት ሰዎች አትኹኑ፡፡ ካሉትም ነገር ሁሉ አላህ አጠራው፡፡ አላህም ዘንድ ባለሞገስ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(33,70,'33:70','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፡፡ ትክክለኛንም ንግግር ተናገሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,71,'33:71','ሥራዎቻችሁን ለእናንተ ያበጅላችኋልና፡፡ ኀጢአቶቻችሁንም ለእናንተ ይምርላችኋል፡፡ አላህንና መልክተኛውን የሚታዘዝም ሰው በእርግጥ ታላቅን እድል አገኘ፡፡');
INSERT INTO "sadiq_sani" VALUES(33,72,'33:72','እኛ አደራን በሰማያትና በምድር፣ በተራራዎችም ላይ አቀረብናት፡፡ መሸከሟንም እንቢ አሉ፡፡ ከእርሷም ፈሩ፡፡ ሰውም ተሸከማት፡፡ እርሱ በጣም በደለኛ ተሳሳች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(33,73,'33:73','መናፍቃንንና መናፍቃትን፣ ወንዶች አጋሪዎችንና ሴቶች አጋሪዎችንም አላህ ሊቀጣና በምእምናንና በምእምናትም ላይ አላህ ንስሓን ሊቀበል (አደራዋን ሰው ተሸከማት)፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,1,'34:1','ምስጋና ለዚያ በሰማያትም ያለ በምድርም ያለ ሁሉ የርሱ ለኾነው ለአላህ ይገባው፡፡ በመጨረሻይቱም ዓለም ምስጋና ለእርሱ ብቻ ነው፡፡ እርሱም ብልሃተኛው ውስጠ ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,2,'34:2','በምድር ውስጥ የሚገባውን ሁሉ ከእርሷም የሚወጣውን ከሰማይም የሚወርደውን በእርሷም ውስጥ የሚያርገውን ሁሉ ያውቃል፡፡ እርሱም አዛኙ መሓሪው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,3,'34:3','እነዚያ የካዱትም «ሰዓቲቱ አትመጣብንም» አሉ፡፡ በላቸው «አይደለም፤ ሩቁን ሁሉ ዐዋቂ በኾነው ጌታዬ እምላለሁ፡፡ በእርግጥ ትመጣባችኋለች፡፡ የብናኝ ክብደት ያክል እንኳ በሰማያትና በምድር ውስጥ ከእርሱ አይርቅም፡፡ ከዚህ ያነሰም ኾነ የበለጠ የለም በግልጹ መጽሐፍ ውስጥ የተመዘገበ ቢኾን እንጅ፡፡»');
INSERT INTO "sadiq_sani" VALUES(34,4,'34:4','እነዚያን ያመኑትንና መልካሞችን የሠሩትን ሊመነዳ (ሰዓቲቱ ትመጣባችኋለች)፤ እነዚያ ለእነርሱ ምህረትና የከበረ ሲሳይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,5,'34:5','እነዚያም የሚያመልጡ መስሏቸው አንቀጾቻችንን ለማፍረስ የጣሩ እነዚያ ለእነርሱ ከመጥፎ ቅጣት የኾነ አሳማሚ ስቃይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,6,'34:6','እነዚያም ዕውቀትን የተሰጡት ያንን ከጌታህ ወዳንተ የተወረደውን እርሱ እውነትና አሸናፊ ምስጉን ወደ ኾነው አላህ መንገድ የሚመራ መኾኑን ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,7,'34:7','እነዚያም የካዱት «(ሙታችሁ) መበጣጠስን ሁሉ በተበጣጠሳችሁ ጊዜ እናንተ በአዲስ መፈጠር ውስጥ ትኾናላችሁ ብሎ የሚነግራችሁን ሰው እናሳያችሁን?» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,8,'34:8','በአላህ ላይ ውሸትን ቀጠፈን? ወይስ በእርሱ ዕብደት አለበትን? (አሉ)፡፡ እንዳሉት አይደለም፡፡ እነዚያ በመጨረሻይቱ (ዓለም) የሚያምኑት (በእርሷ) በቅጣት ውስጥ (አሁን) በራቀ ስህተትም ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,9,'34:9','ከሰማይና ከምድር በፊታቸውና በኋላቸው ወዳለው ሁሉ አይመለከቱምን? ብንሻ በእነርሱ ምድርን እንደረባባቸዋለን፡፡ ወይም በእነርሱ ላይ ከሰማይ ቁራጭን እንጥልባቸዋለን፤ በዚህ ውስጥ (ወደ ጌታው) ተመላሽ ለኾነ ባሪያ ሁሉ ምልክት አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(34,10,'34:10','ለዳውድም ከእኛ የኾነን ችሮታ በእርግጥ ሰጠነው፡፡ (አልንም)፡-«ተራራዎች ሆይ! ከእርሱ ጋር ውዳሴን መላልሱ፡፡ በራሪዎችንም (ገራንለት)፡፡ ብረትንም ለእርሱ አለዘብንለት፡፡»');
INSERT INTO "sadiq_sani" VALUES(34,11,'34:11','ሰፋፊዎችን ጥሩሮች ሥራ፡፡ በአሠራርዋም መጥን፡፡ መልካምንም ሥራ ሥሩ፡፡ እኔ የምትሠሩትን ነገር ሁሉ ተመልካች ነኝና (አልነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(34,12,'34:12','ለሱለይማንም ነፋስን የቀትር በፊት ጉዞዋ የወር መንገድ የቀትር በኋላ ጉዞዋም የወር መንገድ ሲኾን ገራንለት፡፡ የነሐስንም ምንጭ ለእርሱ አፈሰስንለት፡፡ ከጋኔኖችም በጌታው ፈቃድ በፊቱ የሚሠሩን (አደረግንለት)፡፡ ከነርሱም ውስጥ ከትእዛዛችን ዝንፍ የሚል ከነዲድ እሳት ቅጣት እናቀምሰዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(34,13,'34:13','ከምኩራቦች፣ ከምስሎችም እንደ ገንዳ ከኾኑ ገበታዎችም፣ ከተደላደሉ ታላላቅ ድስቶችም የሚሻውን ሁሉ ይሠሩለታል፡፡ (አልናቸውም) «የዳውድ ቤተሰቦች ሆይ! አመስጋኞች ኾናችሁ (ለጌታችሁ) ሥሩ፡፡» ከባሮቼ በጣም አመስጋኞቹም ጥቂቶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,14,'34:14','በእርሱም ላይ ሞትን በፈጸምንበት ጊዜ መሞቱን በትሩን የምትበላ ተንቀሳቃሽ ምስጥ እንጂ ሌላ አላመለከታቸውም፡፡ በወደቀ ጊዜም ጋኔኖች ሩቅን ምስጢር የሚያወቁ በኾኑ ኖሮ በአዋራጅ ስቃይ ውስጥ የማይቆይ እንደነበሩ ተረዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,15,'34:15','ለሰበእ በመኖሪያቸው በእውነት አስደናቂ ምልክት ነበራቸው፡፡ ከግራና ከቀኝ ሁለት አትክልቶች (ነበሯቸው)፡፡ «ከጌታችሁ ሲሳይ ብሉ፡፡ ለእርሱም አመስግኑ፡፡ (አገራችሁ) ውብ አገር ናት፡፡ (ጌታችሁ) መሓሪ ጌታም ነው» (ተባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(34,16,'34:16','(ከማመስገን) ዞሩም፡፡ በእነርሱም ላይ የግድቡን ጎርፍ ለቀቅንባቸው፡፡ በአትክልቶቻቸውም ሁለት አትክልቶች፣ ባለመርጋጋ ፍሬዎችን፣ ባለጠደቻና ከቁርቁራም ባለጥቂት ዛፎችን ለወጥናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,17,'34:17','በመካዳቸው ይህንን መነዳናቸው፤ (እንዲህ ያለውን ቅጣት) በጣም ከሓዲን እንጂ ሌላውን እንቀጣለን?');
INSERT INTO "sadiq_sani" VALUES(34,18,'34:18','በእነርሱና በዚያች በውስጧ በረከትን ባደረግንባት አገር (በሻም) መካከልም ቅጥልጥል ከተሞችን አደረግን፡፡ በእርሷም ጉዞን ወሰንን፡፡ «ጸጥተኞች ኾናችሁ በሌሊቶችም በቀኖችም ተጓዙ፤» (አልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(34,19,'34:19','«ጌታችን ሆይ! በጉዞዎቻችን መካከል አራርቅልን» አሉም፡፡ ነፍሶቻቸውንም በደሉ፡፡ (መገረሚያ) ወሬዎችም አደረግናቸው፡፡ መበታተንንም ሁሉ በታተንናቸው፡፡ በዚህ ውስጥ በጣም ታጋሽና አመስጋኝ ለሆነ ሁሉ መገሰጫዎች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(34,20,'34:20','ኢብሊስም በእነርሱ ላይ ምኞቱን በእርግጥ ፈጸመ፤ ከአመኑትም የሆኑት ጭፍሮች በስተቀር ተከተሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(34,21,'34:21','በመጨረሻይቱም ዓለም የሚያምነውን ከዚያ እርሱ ከእርሷ በመጠራጠር ውስጥ ከሆነው ሰው ለይተን እንድናውቅ እንጅ በእነርሱ ላይ ለእርሱ ምንም ስልጣን አልነበረውም፡፡ ጌታህም በነገሩ ሁሉ ላይ ተጠባባቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,22,'34:22','«እነዚያን ከአላህ ሌላ (አማልክት ብላችሁ) የምታስቧቸውን ጥሩ፡፡ በሰማያትም በምድርም ውስጥ የብናኝ ክብደት ያክል ምንንም አይችሉም፡፡ ለእነርሱም በሁለቱም ውስጥ ምንም ሽርክና የላቸውም፡፡ ከእነርሱም ለእርሱ ምንም አጋዥ የለውም፤» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,23,'34:23','ምልጃም እርሱ ለፈቀደለት ሰው ብቻ ቢሆን እንጂ እርሱ ዘንድ ምንም አትጠቅምም፡፡ ከልቦቻቸው ላይ ድንጋጤው በተገለጸም ጊዜ (ተማላጆቹ) «ጌታችሁ ምን አለ?» ይላሉ፡፡ (አማላጆቹ) «እውነትን አለ፤ እርሱም ከፍተኛው ታላቁ ጌታ ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,24,'34:24','«ከሰማያትና ከምድር (ዝናብንና በቃይን) የሚሰጣችሁ ማነው?» በላቸው፡፡ «አላህ ነው፡፡ እኛ ወይም እናንተ በትክክለኛው መንገድ ላይ ወይም በግልጽ መሳሳት ውስጥ ነን» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,25,'34:25','«ከአጠፋነው ጥፋት አትጠየቁም፡፡ ከምትሠሩትም ሥራ አንጠየቅም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,26,'34:26','«ጌታችን በመካከላችን ይሰበስባል፡፡ ከዚያም በመካከላችን በእውነት ይፈርዳል፡፡ እርሱም በትክክል ፈራጁ ዐዋቂው ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,27,'34:27','«እነዚያን ተጋሪዎች አድርጋችሁ በእርሱ ያስጠጋችኋቸውን (ጣዖታት) አሳዩኝ፡፡ ተዉ (አታጋሩ)፤ በእውነቱ እርሱ አሸናፊው ብልሃተኛው አላህ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,28,'34:28','አንተንም ለሰዎች ሁሉ በመላ አብሳሪና አስፈራሪ አድርገን ቢሆን እንጂ አላክንህም፡፡ ግን አብዛኞቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(34,29,'34:29','«እውነተኞችም እንደ ሆናችሁ ይህ ቀጠሮ መቼ ነው?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,30,'34:30','«ለእናንተ ከእርሱ አንዲትንም ሰዓት የማትዘገዩበት የማትቀድሙበትም የቀጠሮ ቀን አላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,31,'34:31','እነዚያ የካዱትም በዚህ ቁርኣን በዚያ ከበፊቱ ባለውም (መጽሐፍ) በጭራሽ አናምንም አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,32,'34:32','እነዚያ የኮሩት ለእነዚያ ለተዋረዱት «ከትክክለኛው እምነት ከመጣላችሁ በኋላ እኛ ከለከልናችሁን? አይደለም (እናንተው) ከሓዲዎች ነበራችሁ» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(34,33,'34:33','እነዚያም የተዋረዱት ለእነዚያ ለኮሩት «አይደለም፤ በአላህ እንድንክድና ለእርሱ ባላንጣዎችን እንድናደርግ ስታዙን (የከለከለን) የሌሊትና የቀን ዱለታችሁ ነው» ይላሉ፡፡ ቅጣቱንም ባዩ ጊዜ ጸጸትን ይገልጻሉ፡፡ በእነዚያም በካዱት አንገቶች ላይ እንዛዝሎችን እናደርጋለን፡፡ ይሠሩት የነበሩትን እንጂ ሌላን ይመነዳሉን?');
INSERT INTO "sadiq_sani" VALUES(34,34,'34:34','በከተማም አስፈራሪን አልላክንም፡፡ ነዋሪዎችዋ «እኛ በዚያ በርሱ በተላካችሁበት ከሓዲዎች ነን» ያሉ ቢኾኑ እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,35,'34:35','«እኛም በገንዘቦችና በልጆች ይበልጥ የበዛን ነን፡፡ እኛም የምንቀጣ አይደለንም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,36,'34:36','በላቸው «ጌታዬ ሲሳይን ለሚሻው ሰው ያሰፋል፤ ያጠባልም፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም፡፡»');
INSERT INTO "sadiq_sani" VALUES(34,37,'34:37','ገንዘቦቻችሁና ልጆቻችሁም ያች እኛ ዘንድ መቅረብን የምታቀርባችሁ አይደለችም፡፡ ያመነና መልካምን የሠራ ብቻ ሲቀር፡፡ እነዚያ ለእነርሱ በሠሩት መልካም ሥራ እጥፍ ምንዳ አልላቸው፡፡ እነርሱም በገነት ሰገነቶች ውስጥ ጸጥተኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,38,'34:38','እነዚያም የሚያቅቱ መስሏቸው አንቀጾቻችንን ለማበላሸት የሚጥሩት እነዚያ በቅጣቱ ውስጥ የሚጣዱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,39,'34:39','«ጌታዬ ሲሳይን ከባሮቹ ለሚሻው ሰው ያሰፋል፡፡ ለእርሱም ያጠብባል፡፡ ከማንኛውም ነገር የምትለግሱትን እርሱ ይተካዋል፡፡ እርሱም ከሲሳይ ሰጪዎች ሁሉ በላጭ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,40,'34:40','ሁሉንም በሚሰበስባቸውና ከዚያም ለመላእክቶቹ «እነዚህ እናንተን ይግገዙ ነበሩን?» በሚላቸው ቀን (የሚኾነውን አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(34,41,'34:41','(መላእክቶቹም) «ጥራት ይገባህ ከእነርሱ ሌላ ረዳታችን አንተ ብቻ ነህ፡፡ (እንደሚሉት) አይደለም ይልቁንም ጋኔንን ይግገዙ ነበሩ፡፡ አብዛኞቻቸው በእነርሱ አማኞች ናቸው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,42,'34:42','ዛሬም ከፊላችሁ ለከፊሉ መጥቀምንም ኾነ መጉዳትን አይችሉም፡፡ ለእነዚያም ለበደሉት «ያችን በእርሷ ታስተባብሉባት የነበራችሁትን የእሳት ቅጣት ቅመሱ» እንላቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(34,43,'34:43','በእነርሱም ላይ አንቀጾቻችን ግልጽ ኾነው በተነበቡላቸው ጊዜ «ይህ አባቶቻችሁ ይግገዙት ከነበሩት ነገር ሊከለክላችሁ የሚፈልግ ሰው እንጂ ሌላ አይደለም» አሉ፡፡ «ይህም (ቁርኣን) የተቀጣጠፈ ውሸት ነው እንጂ ሌላ አይደለም» አሉ፡፡ እነዚያም የካዱት እውነቱ በመጣላቸው ጊዜ «ይህ ግልጽ ድግምት ነው እንጂ ሌላ አይደለም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,44,'34:44','የሚያጠኑዋቸው የኾኑ መጽሐፍቶችንም ምንም አልሰጠናቸውም፡፡ ከአንተ በፊትም አስፈራሪ ነቢይን ወደእነርሱ አልላክንም፡፡');
INSERT INTO "sadiq_sani" VALUES(34,45,'34:45','እነዚያም ከእነርሱ በፊት የነበሩት አስተባብለዋል፡፡ የሰጠናቸውንም ከዐስር አንዱን (እነዚህ) አልደረሱም፡፡ መልክተኞቼንም አስተባበሉ፤ (ነቀፍኳቸውም)፡፡ መንቀፌም እንዴት ነበር!');
INSERT INTO "sadiq_sani" VALUES(34,46,'34:46','«የምገስጻችሁ ባንዲት ነገር ብቻ ነው፡፡ (እርሷም) ሁለት ሁለት አንድ አንድም ኾናችሁ ለአላህ እንድትነሱ ከዚያም በጓደኛችሁ (በሙሐመድ) ምንም ዕብደት የሌለበት መኾኑን መርምራችሁ እንድትርረዱ ነው፡፡ እርሱ ለእናንተ ከብርቱ ቅጣት በስተፊት አስጠንቃቂ ነው እንጂ ሌላ አይደለም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,47,'34:47','«ከዋጋ ማንኛውም የጠየቅኋችሁ ቢኖር እርሱ ለእናንተው ነው፤ ዋጋዬ በአላህ ላይ እንጂ በሌላ ላይ አይደለም፡፡ እርሱም በነገሩ ሁሉ ላይ ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,48,'34:48','«ጌታዬ እውነትን ያወርዳል፡፡ ሩቅ የኾኑትን ምስጢሮች ሁሉ ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,49,'34:49','«እውነቱ መጣ፡፡ ውሸትም መነሻም መድረሻም የለውም (ተወገደ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,50,'34:50','«ብሳሳት የምሳሳተው በራሴ ላይ ብቻ ነው፡፡ ብመራም ጌታዬ ወደኔ በሚያወርደው ነው፡፡ እርሱ ሰሚ ቅርብ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(34,51,'34:51','በደነገጡና ማምለጫ በሌላቸው ጊዜ ከቅርብ ስፍራም በተያዙ ጊዜ (ኹኔታቸውን) ብታይ ኖሮ (አስደናቂን ነገር ባየህ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(34,52,'34:52','«በእርሱም (አሁን) አመንን» ይላሉ፡፡ ለእነርሱም ከሩቅ ስፍራ (እምነትን) በቀላል ማግኘት ከየታቸው?');
INSERT INTO "sadiq_sani" VALUES(34,53,'34:53','በፊትም በእርሱ በእርግጥ ክደዋል፡፡ ከሩቅ ስፍራም በግምት ንግግርን ይጥላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(34,54,'34:54','ከዚህ ቀደም በመሰሎቻቸው እንደ ተሠራውም ብጤ በእነርሱና በሚፈልጉት መካከል ተጋረደ፡፡ እነርሱ በእርግጥ በአወላዋይ ጥርጣሬ ውስጥ ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,1,'35:1','ምስጋና ሰማያትንና ምድርን ፈጣሪ፣ መላእክትን ባለ ሁለት ሁለት፣ ባለ ሶስት ሶስትም፣ ባለ አራት አራትም ክንፎች የኾኑ መልክተኞች አድራጊ ለኾነው አላህ ይገባው፡፡ በፍጥረቱ ውስጥ የሚሻውን ይጨምራል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,2,'35:2','አላህ ለሰዎች ከችሮታ የሚከፍታት ለእርሷ ምንም አጋጅ የላትም፡፡ የሚያግደውም ከእርሱ በኋላ ለእርሱ ምንም ለቃቂ የለውም፡፡ እርሱም አሸነፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,3,'35:3','እናንተ ሰዎች ሆይ! በእናንተ ላይ (ያለውን) የአላህን ጸጋ አስታውሱ፡፡ ከአላህ ሌላ ፈጣሪ አለን? ከሰማይና ከምድርም ሲሳይን የሚሰጣችሁ አለን? ከእርሱ በስተቀር አምላክ (ሰጪም) የለም፡፡ ታዲያ ወዴት ትዞራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,4,'35:4','ቢያስተባብሉህም ከአንተ በፊት የነበሩ መልክተኞች በእርግጥ ተስተባብለዋል፡፡ ወደ አላህም ነገሮች ሁሉ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,5,'35:5','እናንተ ሰዎች ሆይ! የአላህ ቀጠሮ እውነት ነው፡፡ የቅርቢቱም ሕይወት አታታላችሁ፡፡ አታላዩም (ሰይጣን) በአላህ (መታገስ) አያታላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,6,'35:6','ሰይጣን ለናንተ ጠላት ነው፡፡ ስለዚህ ጠላት አድርጋችሁ ያዙት፡፡ ተከታዮቹን የሚጠራው ከእሳት ጓዶች እንዲኾኑ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,7,'35:7','እነዚያ የካዱት ለእነርሱ ብርቱ ቅጣት አልላቸው፡፡ እነዚያም ያመኑትና በጎ ስራዎችን የሠሩት ለእነርሱ ምሕረትና ታላቅ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,8,'35:8','መጥፎ ሥራው የተሸለመለትና መልካም አድርጎ ያየው ሰው (አላህ እንዳቀናው ሰው ነውን?) አላህም የሚሻውን ሰው ያጠምማል፡፡ የሚሻውንም ያቀናል፡፡ ስለዚህ በእነርሱ ላይ (ባለመቅናታቸው) ስለመቆላጨት ነፍስህ አትጥፋ፡፡ አላህ የሚሠሩትን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,9,'35:9','አላህም ያ ነፋሶችን የላከ ነው፡፡ ደመናዎችንም ትቀሰቅሳለች፤ ወደ ሙት (ድርቅ) አገርም እንነዳዋለን፡፡ በእርሱም ምድርን ከሞተች በኋላ ሕያው እናደርጋታለን፡፡ ሙታንንም');
INSERT INTO "sadiq_sani" VALUES(35,10,'35:10','ማሸነፍን የሚፈልግ የኾነ ሰው አሸናፊነት ለአላህ ብቻ ነው፤ (እርሱን በመግገዛት ይፈልገው)፡፡ መልካም ንግግር ወደርሱ ይወጣል፡፡ በጎ ሥራም ከፍ ያደርገዋል፡፡ እነዚያም መጥፎ ሥራዎችን የሚዶልቱ ለእነሱ ብርቱ ቅጣት አልላቸው፡፡ የእነዚያም ተንኮል እርሱ ይጠፋል፡፡');
INSERT INTO "sadiq_sani" VALUES(35,11,'35:11','አላህም ከዐፈር፣ ከዚያም ከፍትወት ጠብታ ፈጠራችሁ፡፡ ከዚያም ጎሳዎች አደረጋችሁ፡፡ ሴትም አታረግዝም አትወልድምም በዕውቀቱ ቢኾን እንጂ፡፡ ዕድሜው ከሚረዘምም አንድም አይረዘምም ከዕድሜውም አይጎደልም በመጽሐፉ ውስጥ ያለ ቢኾን እንጂ፡፡ ይህ በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,12,'35:12','ሁለቱ ባሕሮችም አይተካከሉም! ይህ ጣፋጭ፣ ጥምን ቆራጭ፣ መጠጡ በገር ተዋጭ ነው፡፡ ይህኛውም ጨው መርጋጋ ነው፡፡ እርጥብ ስጋንም ከሁሉም ትበላላችሁ፡፡ (ከጨው ባሕር) የምትለብሷትንም ጌጥ ታወጣላችሁ፡፡ ከችሮታው ልትፈልጉና ልታመሰግኑም መርከቦችን በእርሱ ውስጥ ውሃውን ቀዳጆች ኾነው (ሲንሻለሉ) ታያለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,13,'35:13','ሌሊትን በቀን ውስጥ ያስገባል፡፡ ቀንንም በሌሊት ውሰጥ ያስገባል፤ ፀሐይንና ጨረቃንም ገራ፡፡ ሁሉም እተወሰነ ጊዜ ድረስ ይሮጣሉ፡፡ ይህ ጌታችሁ አላህ ነው፡፡ ንግሥናው የእርሱ ብቻ ነው፡፡ እነዚያም ከእርሱ ሌላ የምትግገዟቸው የተምር ፍሬ ሽፋን እንኳ አይኖራቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,14,'35:14','ብትጠሩዋቸው ጥሪያችሁን አይሰሙም፡፡ ቢሰሙም ኖሮ ለእናንተ አይመልሱላችሁም፡፡ በትንሣኤም ቀን (እነርሱን በአላህ) ማጋራታችሁን ይክዳሉ፡፡ እንደ ውስጠ ዐዋቂው ማንም አይነግርህም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,15,'35:15','እናንተ ሰዎች ሆይ! እናንተ (ሁል ጊዜ) ወደ አላህ ከጃዮች ናችሁ፡፡ አላህም እርሱ ተብቃቂው ምስጉኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,16,'35:16','ቢሻ ያስወግዳችኋል፡፡ አዲስንም ፍጡር ያመጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(35,17,'35:17','ይህም በአላህ ላይ ምንም አስቸጋሪ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,18,'35:18','ኀጢአትን ተሸካሚም (ነፍስ) የሌላዋን ሸክም አትሸከምም፡፡ የተከበደችም (ነፍስ) ወደ ሸክሟ ብትጣራ (ተጠሪው) የቅርብ ዝምድና ባለቤት ቢኾንም እንኳ ከእርሷ አንዳችን የሚሸከምላት አታገኝም፡፡ የምታስጠነቅቀው እነዚያን ጌታቸውን በሩቅ የሚፈሩትን፣ ሶላትንም አስተካክለው ያደረሱትን ብቻ ነው፡፡ የተጥራራም ሰው የሚጥራራው ለራሱ ብቻ ነው፡፡ መመለሻም ወደ አላህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,19,'35:19','ዕውሩና የሚያየውም አይስተካከሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,20,'35:20','ጨለማዎችና ብርሃንም፤ (እንደዚሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(35,21,'35:21','ጥላና ሐሩርም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,22,'35:22','ሕያዋንና ሙታንም አይስተካከሉም፡፡ አላህ የሚሻውን ሰው ያሰማል፡፡ አንተም በመቃብር ውስጥ ያሉትን አሰሚ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,23,'35:23','አንተ አስጠንቃቂ እንጂ ሌላ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,24,'35:24','እኛ አብሳሪና አስፈራሪ አድርገን በእውነቱ (መምሪያ) ላክንህ፡፡ ማንኛይቱም ሕዝብ በውስጧ አስፈራሪ ያላለፈባት የለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,25,'35:25','ቢያስተባብሉህም እነዚያ ከበፊታቸው የነበሩት በእርግጥ አስተባብለዋል፡፡ መልክተኞቻቸው');
INSERT INTO "sadiq_sani" VALUES(35,26,'35:26','ከዚያም እነዚያን የካዱትን ያዝኳቸው፡፡ ቅጣቴም እንዴት ነበር! (በስፍራው ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(35,27,'35:27','አላህ ከሰማይ ውሃን ማውረዱን አላየህምን? በእርሱም ዓይነቶቻቸው የተለያዩ ፍሬዎችን አወጣን፡፡ ከጋራዎችም መልኮቻቸው የተለያዩ ነጮች፣ ቀዮችም፣ በጣም ጥቁሮችም የኾኑ መንገዶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,28,'35:28','ከሰዎችም፣ ከተንቀሳቃሾችም፣ ከቤት እንሰሳዎችም እንደዚሁ መልኮቻቸው የተለያዩ አልሉ፡፡ አላህን ከባሮቹ ውስጥ የሚፈሩት ዐዋቂዎቹ ብቻ ናቸው፡፡ አላህ አሸናፊ መሓሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,29,'35:29','እነዚያ የአላህን መጽሐፍ የሚያነቡ፣ ሶላትንም አስተካክለው ያደረሱ፣ ከሰጠናቸውም ሲሳይ በምስጢርም ኾነ በግልጽ የለገሱ፣ በፍጹም የማትከስርን ንግድ ተስፋ ያደርጋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(35,30,'35:30','ምንዳዎቻቸውን ሊሞላላቸው፣ ከችሮታውም ሊጨምርላቸው (ተስፋ ያደርጋሉ)፡፡ እርሱ በጣም መሓሪ አመስጋኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,31,'35:31','ያም ከመጽሐፉ ወዳንተ ያወረድንልህ ከበፊቱ ላለው አረጋጋጭ ሲኾን እርሱ እውነት ነው፡፡ አላህ በርግጥ በባሮቹ ውስጥ ዐዋቂ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,32,'35:32','ከዚያም እነዚያን ከባሮቻችን የመረጥናቸውን መጽሐፉን አወረስናቸው፡፡ ከእነርሱም ነፍሱን በዳይ አልለ፡፡ ከእነርሱም መካከለኛ አልለ፡፡ ከእነሱም በአላህ ፈቃድ በበጎ ሥራዎች ቀዳሚ አልለ፡፡ ያ (ማውረስ)፤ እርሱ ታላቅ ችሮታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,33,'35:33','የመኖሪያ ገነቶችን በውስጧ የወርቅ አንባሮችን፣ ሉልንም የሚሸለሙ ኾነው ይገቡባታል፡፡ በእርሷ ውስጥ አልባሳታቸውም ሐር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(35,34,'35:34','ይላሉም «ምስጋና ለእዚያ ከእኛ ላይ ሐዘንን ላስወገደልን አላህ ይገባው፡፡ ጌታችን በጣም መሓሪ አመስጋኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,35,'35:35','(እርሱም) ያ ከችሮታው የዘላለም መኖሪያን አገር ያሰፈረን ነው፡፡ በእርሷ ውስጥ መከራ አይነካንም፡፡ በእርሷም ውስጥ ድካም አይነካንም፡፡»');
INSERT INTO "sadiq_sani" VALUES(35,36,'35:36','እነዚያ የካዱትም ለእነርሱ የገሀነም እሳት አልላቸው፡፡ ይሞቱ ዘንድ በእነርሱ ላይ (በሞት) አይፈረድም፡፡ ከቅጣቷም ከእነርሱ አይቀለልላቸውም፡፡ እንደዚሁ ከሓዲን ሁሉ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(35,37,'35:37','እነርሱም በርሷ ውስጥ እርዳታን በመፈለግ በኀይል ይጮሃሉ፡፡ «ጌታችን ሆይ! ከእዚያ እንሠራው ከነበርነው ሌላ በጎ ሥራን እንሠራ ዘንድ አውጣን» (ይላሉ)፡፡ «በእርሱ ውስጥ ያስታወሰ ሰው የሚገሰጽበትን ዕድሜ አላቆየናችሁምን? አስጠንቃቂውም መጥቶላችኋል (አስተባብላችኋልም)፡፡ ስለዚህ ቅመሱ፡፡ ለበደለኞችም ምንም ረዳት የላቸውም» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(35,38,'35:38','አላህ የሰማያትንና የምድርን ምስጢር በእርግጥ ዐዋቂ ነው፡፡ እርሱ በልቦች ውስጥ ያለን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,39,'35:39','እርሱ ያ በምድር ውስጥ ምትኮች ያደረጋችሁ ነው፡፡ የካደም ሰው ክህደቱ በእርሱው ላይ ብቻ ነው፡፡ ከሓዲዎችንም ክሕደታቸው ከጌታቸው ዘንድ መጠላትን እንጂ ሌላ አይጨመርላቸውም፡፡ ከሓዲዎችንም ክሕደታቸው ከሳራን እንጂ አይጨምርላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,40,'35:40','በላቸው «እነዚያን ከአላህ ሌላ የምትግገዟቸውን ተጋሪዎቻችሁን አያችሁን? ከምድር ምንን እንደ ፈጠሩ አሳዩኝ፡፡ ወይስ ለእነርሱ በሰማያት ውስጥ ሽርክና አላቸውን? ወይስ መጽሐፍን ሰጠናቸውና እነርሱ ከርሱ በግልጽ አስረጅ ላይ ናቸውን? አይደለም በደለኞች ከፊላቸው ከፊሉን ማታለልን እንጂ አይቀጥሩም፡፡»');
INSERT INTO "sadiq_sani" VALUES(35,41,'35:41','አላህ ሰማያትንና ምድርን እንዳይወገዱ ይይዛቸዋል፡፡ ቢወገዱም ከእርሱ ሌላ አንድም የሚይዛቸው የለም፡፡ እነሆ እርሱ ታጋሽ መሓሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,42,'35:42','አስፈራሪም ቢመጣላቸው ከሕዝቦቹ ሁሉ ከአንደኛዋ ይበልጥ የተመሩ ሊኾኑ የመሓላቸውን ድካ አድርሰው በአላህ ማሉ፡፡ አስፈራሪም በመጣላቸው ጊዜ መበርገግን እንጂ ሌላ አልጨመረላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,43,'35:43','በምድር ላይ ኩራትንና በክፉ (ተንኮል) መዶለትንም (እንጂ አልጨመረላቸውም፡፡ ክፉ ተንኮልም በባለቤቱ ላይ እንጂ በሌላ ላይ አይሰፍርም፡፡ የቀድሞዎቹን ደንብ እንጂ ይጠባበቃሉን? ለአላህ ደንብም መልለወጥን አታገኝም፡፡ ለአላህ ደንብም መዛወርን አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(35,44,'35:44','በምድር ላይ አይኼዱምና የእነዚያን ከእነርሱ በፊት የነበሩትን ሰዎች መጨረሻ እንዴት እንደ ነበረ አይመለከቱምን? ከእነርሱም በኀይል የበረቱ ነበሩ፡፡ አላህም በሰማያትም ኾነ በምድር ውስጥ ምንም ነገር የሚያቅተው አይደለም፡፡ እርሱ ዐዋቂ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(35,45,'35:45','አላህም ሰዎችን በሠሩት ኀጢአት ቢይዝ ኖሮ በወለሏ (በምድር) ላይ ምንም ተንቀሳቃሽ');
INSERT INTO "sadiq_sani" VALUES(36,1,'36:1','የ.ሰ.(ያ ሲን)');
INSERT INTO "sadiq_sani" VALUES(36,2,'36:2','ጥበብ በተመላበት ቁርኣን እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,3,'36:3','አንተ በእርግጥ ከመልክተኞቹ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,4,'36:4','በቀጥታም መንገድ ላይ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,5,'36:5','አሸናፊ አዛኝ ከሆነው አምላክ መወረድን ተወረደ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,6,'36:6','አባቶቻቸው ያልተስፈራሩትን ሕዝቦች ልታስጠነቅቅበት (ተወረደ)፡፡ እነርሱ ዘንጊዎች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(36,7,'36:7','በአብዛኞቻቸው ላይ ቃሉ በእውነት ተረጋገጠ፡፡ ስለዚህ እነርሱ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,8,'36:8','እኛ በአንገቶቻቸው ላይ እንዛዝላዎችን አደረግን፡፡ እርሷም (እንዛዝላይቱ) ወደ አገጮቻቸው ደራሽ ናት፡፡ እነርሱም ራሶቻቸውን ያንጋጠጡ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,9,'36:9','ከስተፊታቸውም ግርዶን ከስተኋላቸውም ግርዶን አደረግን፡፡ ሸፈንናቸውም፡፡ ስለዚህ እነሱ አያዩም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,10,'36:10','ብታሰጠነቅቃቸውም ባታስጠነቅቃቸውም በእነርሱ ላይ እኩል ነው፡፡ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,11,'36:11','የምታስጠነቅቀው ግሳጼን የተከተለንና አልረሕማንን በሩቅ የፈራን ሰው ብቻ ነው፡፡ በምሕረትና በመልካም ምንዳም አብስረው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,12,'36:12','እኛ ሙታንን እኛ በእርግጥ ሕያው እናደርጋለን፡፡ ያስቀደሙትንም ሥራ ፈለጎቻቸውንም እንጽፋለን፡፡ ነገሩንም ሁሉ ገላጭ መሪ በኾነ መጽሐፍ ውስጥ አጠቃለልነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,13,'36:13','ለእነርሱም የከተማይቱን (የአንጾኪያን) ሰዎች ምሳሌ መልክተኞቹ በመጧት ጊዜ (የኾነውን) ግለጽላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,14,'36:14','ወደእነርሱ ሁለትን ሰዎች በላክንና ባስተባበሉዋቸው ጊዜ በሶስተኛም በአበረታንና እኛ ወደ እናንተ መልክተኞች ነን ባሏቸው ጊዜ (የኾነውን ምሳሌ ግለጽላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,15,'36:15','እናንተ መሰላችን ሰዎች እንጂ ሌላ አይደላችሁም፡፡ አልረሕማንም ምንም ነገር አላወረደም፡፡ እናንተ የምትዋሹ እንጂ ሌላ አይደላችሁም አሉዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,16,'36:16','(መልክተኞቹም) አሉ «ጌታችን ያውቃል፡፡ እኛ ወደእናንተ በእርግጥ መልክተኞች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(36,17,'36:17','«በእኛ ላይም ግልጽ የኾነ ማድረስ እንጂ ሌላ የለብንም፡፡»');
INSERT INTO "sadiq_sani" VALUES(36,18,'36:18','(ሕዝቦቹም) «እኛ በእናንተ ገደ ቢሶች ኾን፡፡ ባትከለከሉ በእርግጥ እንወግራችኋለን፡፡ ከእኛም አሳማሚ ቅጣት በእርግጥ ያገኛችኋል» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,19,'36:19','«ገደ ቢስነታችሁ ከእናንተው ጋር ነው፡፡ ብትገሰጹ (ትዝታላችሁን?) በእውነቱ እናንተ ወሰን አላፊዎች ሕዝቦች ናችሁ» አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,20,'36:20','ከከተማይቱም ሩቅ ዳርቻ የሚሮጥ ሰው መጣ፡፡ «ወገኖቼ ሆይ! መልክተኞቹን ተከተሉ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,21,'36:21','«እነርሱ ቅኑን መንገድ የተመሩ ሲኾኑ ዋጋን የማየጠይቁዋችሁን ሰዎች ተከተሉ» (አላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,22,'36:22','«ያንንም የፈጠረኝን፤ ወደርሱም የምትመለሱበትን (ጌታ) የማልገዛ ለእኔ ምን አለኝ?» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,23,'36:23','«ከእርሱ ሌላ አማልክትን እይዛለሁን? አልረህማን በጉዳት ቢሻኝ ምልጃቸው ከእኔ (ለመመለስ) ምንም አትጠቅመኝም፤ አያድኑኝምም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,24,'36:24','«እኔ ያን ጊዜ በግልጽ ስህተት ውስጥ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,25,'36:25','«እኔ በጌታችሁ አመንኩ፤ ስሙኝም፤» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,26,'36:26','«ገነትን ግባ» ተባለ፡፡ (እርሱም) አለ፡- «ወገኖቼ ቢያውቁ እመኛለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,27,'36:27','«ጌታዬ ለእኔ ምሕረት ያደረገልኝና ከተከበሩትም ያደረገኝ መኾኑን፡፡»');
INSERT INTO "sadiq_sani" VALUES(36,28,'36:28','ከእርሱም በኋላ በሕዝቦቹ ላይ (ልናጠፋቸው) ሰራዊትን ከሰማይ አላወረድንም፡፡ (በማንም ላይ) አወራጆችም አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,29,'36:29','(ቅጣታቸው) አንዲት ጩኸት እንጂ ሌላ አልነበረችም፡፡ ወዲያውኑም እነርሱ ጠፊዎች ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,30,'36:30','በባሮቹ ላይ ዋ ቁልጭት! ከመልክተኛ አንድም አይመጣቸውም በእርሱ የሚሳለቁበት ቢኾኑ እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,31,'36:31','ከእነርሱ በፊት ከክፍለ ዘመናት ሕዝቦች ብዙዎችን ማጥፋታችንንና እነርሱ ወደነርሱ የማይመለሱ መኾናቸውን አላወቁምን?');
INSERT INTO "sadiq_sani" VALUES(36,32,'36:32','ሁሉም እኛ ዘንድ የሚሰበሰቡ የሚቀረቡ እንጂ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,33,'36:33','የሞተችውም ምድር ለእነርሱ ምልክት ናት! ሕያው አደረግናት፡፡ ከእርሷም ፍሬን አወጣን፤ ከርሱም ይበላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,34,'36:34','በእርሷም ውሰጥ ከዘምበባዎችና ከወይኖች የኾኑ አትክልቶችን አደረግን፡፡ በእርሷም ውስጥ ምንጮችን አፈለቅን፡፡');
INSERT INTO "sadiq_sani" VALUES(36,35,'36:35','ከፍሬውና እጆቻቸው ከሠሩትም ይበሉ ዘንድ (ይህን አደረግን)፤ አያመሰግኑምን?');
INSERT INTO "sadiq_sani" VALUES(36,36,'36:36','ያ ምድር ከምታበቅለው ከነፍሶቻቸውም ከማያውቁትም ነገር ዓይነቶችን ሁሏንም የፈጠረ (አምላክ) ጥራት ይገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,37,'36:37','ሌሊቱም ለእነርሱ ምልክት ነው፡፡ ከእርሱ ላይ ቀንን እንገፍፋለን፡፡ ወዲያውኑም እነርሱ በጨለማ ውስጥ ይገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,38,'36:38','ፀሐይም ለእርሷ ወደ ሆነው መርጊያ ትሮጣለች፡፡ ይህ የአሸናፊው የዐዋቂው አምላክ ውሳኔ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,39,'36:39','ጨረቃንም የመስፈሪያዎች ባለቤት ሲኾን እንደ ዘንባባ አሮጌ ቀንዘል እስኪኾን ድረስ (መኼዱን) ለካነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,40,'36:40','ፀሐይ ጨረቃን ልትደርስበት አይገባትም፡፡ ሌሊትም ቀንን (ያለ ጊዜው) ቀዳሚ አይኾንም፡፡ ሁሉም በመዞሪያቸው ውስጥ ይዋኛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,41,'36:41','እኛም (የቀድሞ) ትውልዳቸውን በተሞላች መርከብ ውስጥ የጫን መኾናችን ለእነርሱ ምልክት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,42,'36:42','ከመሰሉም በእርሱ የሚሳፈሩበትን ለእነርሱ ፈጠርንላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,43,'36:43','ብንሻም እናሰጥማቸዋለን፡፡ ለእነርሱም ረዳት የላቸውም፡፡ እነርሱም የሚድዳኑ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,44,'36:44','ግን ከእኛ ለኾነው ችሮታና እስከ ጊዜ ሞታቸው ለማጣቀም (አዳንናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,45,'36:45','ለእነርሱም «በስተፊታችሁና በኋለችሁ ያለውን ነገር ተጠንቀቁ ይታዘንላችኋልና» በተባሉ ጊዜ (ፊታቸውን ያዞራሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,46,'36:46','ከጌታቸውም ተዓምራት ማንኛይቱም ተዓምር አትመጣላቸውም፤ ከእርሷ የሚሸሹ ቢኾኑ እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,47,'36:47','ለእነርሱም «አላህ ከሰጣችሁ ሲሳይ ለግሱ» በተባሉ ጊዜ እነዚያ የካዱት ለእነዚያ ላመኑት «አላህ ቢሻ ኖሮ የሚያበላውን ሰው እናበላለን? እናንተ በግልጽ ስህተት ውስጥ እንጂ በሌላ ላይ አይደላችሁም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,48,'36:48','«እውነተኞችም እንደኾናችሁ ይህ ቀጠሮ መቼ ነው?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,49,'36:49','እነርሱ የሚከራከሩ ሲኾኑ በድንገት የምትይዛቸው የኾነችን አንዲትን ጩኸት እንጂ ሌላን አይጠባበቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,50,'36:50','(ያን ጊዜ) መናዘዝንም አይችሉም፡፡ ወደ ቤተሰቦቻቸወም አይመለሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,51,'36:51','በቀንዱም ይነፋል፡፡ ወዲያውኑም እነርሱ ከመቃብሮቻቸው ወደ ጌታቸው በፍጥነት ይገሰግሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,52,'36:52','«ወይ ጥፋታችን! ከመኝታችን ማን ቀሰቀሰን? ይህ ያ አዛኙ ጌታ (በእርሱ) የቀጠረን መልክተኞቹም እውነትን የነገሩን ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,53,'36:53','(እርሷ) አንዲት ጩኸት እንጂ አይደለችም፡፡ ወዲያውኑም እነርሱ እኛ ዘንድ የሚሰበሰቡ የሚቀርቡ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,54,'36:54','«ዛሬም ማንኛይቱም ነፍስ ምንም አትበደልም፡፡ ትሠሩትም የነበራችሁትን እንጂ ሌላን አትመነዱም» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,55,'36:55','የገነት ሰዎች ዛሬ በእርግጥ በሥራዎች ውስጥ ተደሳቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,56,'36:56','እነርሱም ሚስቶቻቸውም በጥላዎች ውስጥ ናቸው፡፡ ባለ አጎበር አልጋዎች ላይ ተደጋፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,57,'36:57','በውስጧ ለእነርሱ ፍራፍሬዎች አሏቸው፡፡ ለእነርሱም የሚፈልጉት ሁሉ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,58,'36:58','(ለእነርሱም) አዛኝ ከሆነው ጌታ በቃል ሰላምታ አላቸው።');
INSERT INTO "sadiq_sani" VALUES(36,59,'36:59','(ይላልም) «እናንተ አመጸኞች ሆይ! ዛሬ (ከምእምናን) ተለዩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(36,60,'36:60','የአደም ልጆች ሆይ! ሰይጣንን አትገዙ፤ እርሱ ለእናንተ ግልጽ ጠላት ነውና በማለት ወደእናንተ አላዘዝኩምን?');
INSERT INTO "sadiq_sani" VALUES(36,61,'36:61','ተገዙኝም፤ ይህ ቀጥተኘ መንገድ ነው፤ (በማለትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,62,'36:62','ከእናንተም ብዙን ፍጡር በእርግጥ አሳስቷል፡፡ የምታውቁም አልነበራችሁምን?');
INSERT INTO "sadiq_sani" VALUES(36,63,'36:63','ይህቺ ያቺ ትቀጠሩዋት የነበረችው ገሀነም ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(36,64,'36:64','ትክዱ በነበራችሁት ምክንያት ዛሬ ግቧት (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(36,65,'36:65','ዛሬ በአፎቻቸው ላይ እናትምና እጆቻቸው ያነጋግሩናል፡፡ እግሮቻቸውም ይሠሩት በነበሩት ሁሉ ይመሰክራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,66,'36:66','ብንሻም ኖሮ በዐይኖቻቸው ላይ በአበስን ነበር፡፡ መንገድንም (እንደ ልመዳቸው) በተሽቀዳደሙ ነበር፡፡ እንዴትም ያያሉ?');
INSERT INTO "sadiq_sani" VALUES(36,67,'36:67','ብንሻም ኖሮ በስፍራቸው ላይ እንዳሉ ወደ ሌላ ፍጥረት በለወጠናቸው ነበር፡፡ መኼድንም መመለስንም ባልቻሉም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(36,68,'36:68','ዕድሜውንም የምናረዝመውን ሰው በፍጥረቱ (ወደ ደካማነት) እንመልሰዋለን፤ አያውቁምን?');
INSERT INTO "sadiq_sani" VALUES(36,69,'36:69','(ሙሐመድን) ቅኔንም አላስተማርነውም፡፡ ለእርሱም አይግገባውም፡፡ እርሱ (መጽሐፉ) መገሰጫና ገላጭ ቁርኣን እንጅ (ቅኔ) አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,70,'36:70','(ግሣጼነቱም ልቡ) ሕያው የኾነን ሰው ሊያስፈራራበትና ቃሉም በከሓዲዎች ላይ ይፈጸም ዘንድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,71,'36:71','እኛ እጆቻችን (ኃይሎቻችን) ከሠሩት ለእነርሱ እንስሳዎችን መፍጠራችንን አያውቁምን? እነርሱም ለእርሷ ባለ መብቶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,72,'36:72','ለእነርሱም ገራናት፡፡ ስለዚህ ከእርሷ ውስጥ የሚጋልቡት አልለ፡፡ ከእርሷም ይበላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,73,'36:73','ለእነርሱም በእርሷ ውስጥ (ሌሎች) ጥቅሞች መጠጦችም አሉዋቸው፡፡ ታዲያ አያመሰግኑምን;');
INSERT INTO "sadiq_sani" VALUES(36,74,'36:74','መረዳትንም በመከጀል ከአላህ ሌላ አማልክትን ያዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,75,'36:75','መርዳታቸውን አይችሉም፡፡ እነርሱም ለእርሳቸው (ወደ እሳት) የተቀረቡ ሰራዊት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,76,'36:76','ንግግራቸውም አያሳዝንህ፡፡ እኛ የሚደብቁትንም የሚገልጹትንም እናውቃለንና፡፡');
INSERT INTO "sadiq_sani" VALUES(36,77,'36:77','ሰውየው እኛ ከፍቶት ጠብታ የፈጠርነው መኾናችን አላወቀምን? ወዲያውም እርሱ (ትንሣኤን በመካድ) ግልጽ ተከራካሪ ይኾናልን?');
INSERT INTO "sadiq_sani" VALUES(36,78,'36:78','ለእኛም ምሳሌን አደረገልን፡፡ መፈጠሩንም ረሳ፡፡ «አጥንቶችን እነርሱ የበሰበሱ ሲኾኑ ሕያው የሚያደርጋቸው ማነው?» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,79,'36:79','«ያ በመጀመሪያ ጊዜ (ከኢምንት) ያስገኛት ሕያው ያደርጋታል፡፡ እርሱም በፍጡሩ ሁሉ (ኹኔታ) ዐዋቂ ነው» በለው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,80,'36:80','ያ ለእናንተ በእርጥብ ዛፍ እሳትን ያደረገላችሁ ነው፡፡ ወዲያውኑም እናንተ ከእርሱ ታቀጣጥላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(36,81,'36:81','ያ ሰማያትንና ምድርን የፈጠረ ብጤያቸውን በመፍጠር ላይ ቻይ አይደለምን? ነው እንጅ፡፡ እርሱም በብዙ ፈጣሪው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(36,82,'36:82','ነገሩም አንዳችን በሻ ጊዜ ኹን ማለት ነው፡፡ ወዲያው ይኾናልም፡፡');
INSERT INTO "sadiq_sani" VALUES(36,83,'36:83','ያ የነገሩ ሁሉ ስልጣን በእጁ የኾነው ጌታ ጥራት ይገባው፡፡ ወደእርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,1,'37:1','መሰለፍን በሚሰለፉት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,2,'37:2','መገሠጽንም በሚገሥጹት፤');
INSERT INTO "sadiq_sani" VALUES(37,3,'37:3','ቁርኣንንም በሚያነቡት እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,4,'37:4','አምላካችሁ በእርግጥ አንድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,5,'37:5','የሰማያትና የምድር በመካከላቸው ያለውም ፍጡር ሁሉ ጌታ ነው፡፡ የምሥራቆችም ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,6,'37:6','እኛ ቅርቢቱን ሰማይ በከዋክብት ጌጥ አጌጥናት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,7,'37:7','አመጸኛም ከሆነ ሰይጣን ሁሉ መጠበቅን ጠበቅናት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,8,'37:8','ወደላይኛው ሰራዊት አያዳምጡም፡፡ ከየወገኑም ሁሉ ችቦ ይጣልባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,9,'37:9','የሚባረሩ ሲሆኑ (ይጣልባቸዋል)፡፡ ለእነሱም ዘውታሪ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,10,'37:10','ንጥቂያን የነጠቀ ወዲያውም አብሪ ኮከብ የተከተለው ሲቀር፤ (እርሱ ይሰማዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,11,'37:11','ጠይቃቸውም፤ እነርሱ በአፈጣጠር ይበልጥ የበረቱ ናቸውን? ወይስ እኛ (ከእነርሱ በፊት) የፈጠርነው? (ፍጡር) እኛ ከሚጣበቅ ጭቃ ፈጠርናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,12,'37:12','ይልቁንም (በማስተባበላቸው) ተደነቅህ፤(ከመደነቅህ) ይሳለቃሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,13,'37:13','በተገሠጹም ጊዜ አይመለሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,14,'37:14','ተዓምርን ባዩም ጊዜ ለመሳለቅ ይጠራራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,15,'37:15','ይላሉም «ይህ ግልጽ ድግምት እንጂ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,16,'37:16','«በሞትንና ዐፈርና አጥንቶችም በኾን ጊዜ እኛ የምንቀሰቀስ ነን?');
INSERT INTO "sadiq_sani" VALUES(37,17,'37:17','«የቀድሞዎቹ አባቶችንም? (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,18,'37:18','«አዎን እናንተ ወራዶች ሆናችሁ (ትነሳላችሁ)» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,19,'37:19','እርሷም አንዲት ጩኸት ብቻ ናት፡፡ እነርሱም ወዲያውኑ (ምን እንደሚፈጸምባቸው) ያያሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,20,'37:20','«ዋ ጥፋታችን! ይህ የፍርዱ ቀን ነው» ይላሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,21,'37:21','«ይህ ያ በእርሱ ታስተባብሉ የነበራችሁት መለያው ቀን ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,22,'37:22','(ለመላእክቶችም) «እነዚያን ነፍሶቻቸውን የበደሉትን ሰዎች ጓደኞቻቸውንም ይግገዟቸው የነበሩትንም (ጣዖታት) ሰብስቡ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,23,'37:23','«ከአላህ ሌላ (የሚገዙዋቸውን ሰብስቧቸው) ወደ እሳት መንገድም ምሩዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,24,'37:24','«አቁሟቸውም፡፡ እነርሱ ተጠያቂዎች ናቸውና» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,25,'37:25','(ለእነርሱም) «የማትረዳዱት ለእናንተ ምን አላችሁ? (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,26,'37:26','በእርግጥ እነርሱ ዛሬ እጃቸውን የሰጡ ወራዳዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,27,'37:27','የሚወቃቀሱም ሆነው ከፊላቸው በከፊሉ ላይ ይመጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,28,'37:28','(ለአስከታዮቹ) እናንተ ከስተቀኝ ትመጡብን ነበር ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,29,'37:29','(አስከታዮቹም) ይላሉ «አይደለም ፈጽሞ ምእምናን አልነበራችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,30,'37:30','«ለእኛም በእናንተ ላይ ምንም ስልጣን አልነበረንም፡፡ በእርግጥ እናንተ ጠማሞች ሕዝቦች ነበራችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,31,'37:31','«በእኛም ላይ የጌታችን ቃል ተረጋገጠብን፤ እኛ (ሁላችንም ቅጣቱን) ቀማሾች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,32,'37:32','«ወደ ጥምመት ጠራናችሁም፡፡ እኛ ጠማሞች ነበርንና» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,33,'37:33','ስለዚህ እነርሱ በዚያ ቀን በቅጣቱ ነጋሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,34,'37:34','እኛ በአመጸኞች ሁሉ እንደዚሁ እንሠራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,35,'37:35','እነርሱ ከአላህ ሌላ አምላክ የለም በተባሉ ጊዜ ይኮሩ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,36,'37:36','እኛ ለዕብድ ባለቅኔ ብለን አማልክቶቻችንን የምንተው ነን? ይሉም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(37,37,'37:37','አይደለም እውነቱን (ሃይማኖት) አመጣ፡፡ መልክተኞቹንም እውነተኛነታቸውን አረጋገጠ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,38,'37:38','እናንተ አሳማሚውን ቅጣት በእርግጥ ቀማሾች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,39,'37:39','ትሠሩትም የነበራችሁትን እንጂ ሌላን አትመነዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,40,'37:40','ግን ምርጥ የሆኑት የአላህ ባሮች (ቅጣትን አይቀምሱም)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,41,'37:41','እነዚያ ለእነርሱ የታወቀ ሲሳይ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,42,'37:42','ፍራፍሬዎች (አሏቸው) እነርሱም የተከበሩ ናቸው፤');
INSERT INTO "sadiq_sani" VALUES(37,43,'37:43','በድሎት ገነቶች ውስጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,44,'37:44','ፊት ለፊት የሚታያዩ ሲሆኑ በአልጋዎች ላይ (ይንፈላሰሳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,45,'37:45','ከሚመነጭ ወይን ጠጅ በያዘ ብርጭቆ በእነርሱ ላይ ይዞርባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,46,'37:46','ነጭ ለጠጪዎች ጣፋጭ ከኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(37,47,'37:47','በእርሷ ውስጥም ምታት የለባትም፡፡ እነርሱም ከእርሷ የሚሰክሩ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,48,'37:48','እነርሱም ዘንድ ዓይናቸውን (በባሎቻቸው ላይ) አሳጣሪዎች የኾኑ ዓይናማዎች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,49,'37:49','እነርሱ ልክ የተሸፈነ (የሰጎን) ዕንቁላል ይመስላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,50,'37:50','የሚጠያየቁም ሆነው ከፊላቸው ወደ ከፊሉ ይመጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,51,'37:51','ከእነርሱ የሆነ ተናጋሪ ይላል «እኔ ጓደኛ ነበረኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,52,'37:52','«በእርግጥ አንተ ከሚያምኑት ነህን? የሚል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,53,'37:53','«በሞትንና ዐፈር አጥንቶችም በሆን ጊዜ እኛ በእርግጥ እንመረመራለን?» (የሚል)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,54,'37:54','እናንተ ተመልካቾች ናችሁን? ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,55,'37:55','ይመለከታልም፤ በገሀነም መካከልም (ጓደኛውን) ያየዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,56,'37:56','ይላል «በአላህ እምላለሁ፤ በእርግጥ ልታጠፋኝ ቀርበህ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(37,57,'37:57','«የጌታየም ጸጋ ባልነበረ ኖሮ ወደ እሳት ከሚቀርቡት እሆን ነበር፡፡»');
INSERT INTO "sadiq_sani" VALUES(37,58,'37:58','(የገነት ሰዎች ይላሉ) «እኛ የምንሞት አይደለንምን?');
INSERT INTO "sadiq_sani" VALUES(37,59,'37:59','«የመጀመሪያይቱ መሞታችን ብቻ ስትቀር፡፡ እኛም የምንቅቀጣ አይደለንምን?» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,60,'37:60','ይህ (ለገነት ሰዎች የተወሳው) እርሱ በእርግጥ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,61,'37:61','ለዚህ ብጤ ሠሪዎች ይሥሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,62,'37:62','በመስተንግዶነት ይህ ይበልጣልን? ወይስ የዘቁም ዛፍ?');
INSERT INTO "sadiq_sani" VALUES(37,63,'37:63','እኛ ለበዳዮች ፈተና አድርገናታል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,64,'37:64','እርሷ በገሀነም አዘቅት ውስጥ የምትወጣ ዛፍ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,65,'37:65','እንቡጧ ልክ የሰይጣናት ራሶች ይመስላል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,66,'37:66','እነርሱም ከእርሷ በይዎች ናቸው፤ ሆዶችንም ከእርሷ ሞይዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,67,'37:67','ከዚያም ለእነርሱ በእርሷ ላይ ከተኳሳ ውሃ ቅልቅል (መጠጥ) አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,68,'37:68','ከዚያም መመለሻቸው ወደ ተጋጋመች እሳት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,69,'37:69','እነሱ አባቶቻቸውን የተሳሳቱ ሆነው አግኝተዋልና፡፡');
INSERT INTO "sadiq_sani" VALUES(37,70,'37:70','እነሱም በፈለጎቻቸው ላይ ይገሰግሳሉና፡፡');
INSERT INTO "sadiq_sani" VALUES(37,71,'37:71','ከእነርሱ በፊትም የቀድሞዎቹ (ሕዝቦች) አብዛኞቻቸው በእርግጥ ተሳስተዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,72,'37:72','በውስጣቸውም አስፈራሪዎችን በእርግጥ ልከናል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,73,'37:73','የተስፈራሪዎቹም መጨረሻ እንዴት እንደነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,74,'37:74','ምርጥ የሆኑት የአላህ ባሮች ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,75,'37:75','ኑሕም በእርግጥ ጠራን፡፡ ጥሪውን ተቀባዮቹም ምንኛ አማርን!');
INSERT INTO "sadiq_sani" VALUES(37,76,'37:76','እርሱንም ቤተሰቦቹንም ከከባድ ጭንቅ አዳንን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,77,'37:77','ዘሮቹንም እነርሱን ብቻ ቀሪዎች አደረግን፤');
INSERT INTO "sadiq_sani" VALUES(37,78,'37:78','በርሱም ላይ በኋለኞቹ ሕዝቦች ውስጥ (መልካም ዝናን) ተውን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,79,'37:79','«በዓለማት ውስጥ ሰላም በኑሕ ላይ ይኹን፡፡»');
INSERT INTO "sadiq_sani" VALUES(37,80,'37:80','እኛ እንደዚሁ መልካም ሠሪዎችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,81,'37:81','እርሱ ከምእምናን ባሮቻችን ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(37,82,'37:82','ከዚያም ሌሎቹን አሰጠምን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,83,'37:83','ኢብራሂምም በእርግጥ ከተከታዮቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,84,'37:84','ወደ ጌታው በቅን ልብ በመጣ ጊዜ (የኾነውን አስታውስ)፡፡፡');
INSERT INTO "sadiq_sani" VALUES(37,85,'37:85','«ለአባቱና ለሕዝቦቹ ምንን ትግገዛላችሁ?» ባለ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,86,'37:86','«ለቅጥፈት ብላችሁ ከአላህ ሌላ አማልክትን ትሻላችሁን?');
INSERT INTO "sadiq_sani" VALUES(37,87,'37:87','«በዓለማትም ጌታ ሃሳባችሁ ምንድን ነው?» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,88,'37:88','በከዋክብትም መመልከትን ተመለከተ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,89,'37:89','«እኔ በሽተኛ ነኝም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,90,'37:90','ከእርሱም የሸሹ ሆነው ኼዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,91,'37:91','ወደ አማልክቶቻቸውም ተዘነበለ፡፡ አለም፡- «አትበሉምን?»');
INSERT INTO "sadiq_sani" VALUES(37,92,'37:92','«የማትናገሩት ለናንተ ምን አላችሁ?»');
INSERT INTO "sadiq_sani" VALUES(37,93,'37:93','በኀይል የሚመታቸውም ሆኖ በእነርሱ ላይ በድብቅ ተዘነበለ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,94,'37:94','ወደእርሱም (ሰዎቹ) እየሮጡ መጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,95,'37:95','አላቸው «የምትጠርቡትን ትግገዛላችሁን?»');
INSERT INTO "sadiq_sani" VALUES(37,96,'37:96','«አላህ እናንተንም የምትሠሩትንም የፈጠረ ሲኾን፡፡»');
INSERT INTO "sadiq_sani" VALUES(37,97,'37:97','«ለእርሱም ግንብን ገንቡ በእሳት ነበልባል ውስጥ ጣሉትም» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,98,'37:98','በእርሱም ተንኮልን አሰቡ፡፡ ዝቅተኞችም አደረግናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,99,'37:99','አለም «እኔ ወደ ጌታዬ ኼያጅ ነኝ፡፡ በእርግጥ ይመራኛልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(37,100,'37:100','ጌታዬ ሆይ! ከመልካሞቹ የሆነን (ልጅ) ስጠኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,101,'37:101','ታጋሽ በሆነ ወጣት ልጅም አበሰርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,102,'37:102','ከእርሱ ጋርም ለሥራ በደረሰ ጊዜ «ልጄ ሆይ! እኔ በሕልሜ እኔ የማርድህ ሆኜ አያለሁ፡፡ ተመልከትም፤ ምን ታያለህ?» አለው፡፡ «አባቴ ሆይ! የታዘዝከውን ሥራ፡፡ አላህ ቢሻ ከታጋሾቹ ሆኜ ታገኘኛለህ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,103,'37:103','ሁለቱም ትዕዛዙን በተቀበሉና በግንባሩም ጎን ላይ በጣለው ጊዜ (የሆነው ሆነ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,104,'37:104','ጠራነውም፤ (አልነው) «ኢብራሂም ሆይ!');
INSERT INTO "sadiq_sani" VALUES(37,105,'37:105','ራእይቱን በእውነት አረጋገጥክ፡፡» እኛ እንደዚሁ መልካም ሠሪዎችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,106,'37:106','ይህ እርሱ በእውነት ግልጽ ፈተና ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,107,'37:107','በታላቅ ዕርድም (መስዋዕት) ተቤዠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,108,'37:108','በእርሱም ላይ በኋለኞቹ ሕዝቦች ውስጥ መልካም ዝናን አስቀረንለት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,109,'37:109','ሰላም በኢብራሂም ላይ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,110,'37:110','እንደዚሁ መልካም ሠሪዎችን ሁሉ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,111,'37:111','እርሱ በእርግጥ ካመኑት ባሮቻችን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,112,'37:112','በኢስሐቅም አበሰርነው፡፡ ከመልካሞቹ የሆነ ነቢይ ሲሆን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,113,'37:113','በእርሱና በኢስሐቅ ላይም ባረክን፡፡ ከሁለቱም ዘሮች በጎ ሠሪ አልለ፡፡ ለነፍሱ ግልጽ በዳይም አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,114,'37:114','በሙሳና በሃሩን ላይም በእርግጥ ለገስን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,115,'37:115','እነርሱንም ሕዝቦቻቸውንም ከታላቅ ጭንቅ አዳንን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,116,'37:116','ረዳናቸውም፡፡ እነርሱም አሸናፊዎች ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,117,'37:117','በጣም የተብራራውንም መጽሐፍ ሰጠናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,118,'37:118','ቀጥተኛውንም መንገድ መራናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,119,'37:119','በሁለቱም ላይ በኋለኞቹ ሕዝቦች ውስጥ መልካም ዝናን ተውንላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,120,'37:120','ሰላም በሙሳና በሃሩን ላይ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,121,'37:121','እኛ እንደዚሁ በጎ አድራጊዎችን ሁሉ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,122,'37:122','ሁለቱም በእርግጥ ከአመኑት ባሮቻችን ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,123,'37:123','ኢልያስም በእርግጥ ከመልክተኞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,124,'37:124','ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፤ አላህን አትፈሩምን?');
INSERT INTO "sadiq_sani" VALUES(37,125,'37:125','በዕልን ትገዛላችሁን? ከሰዓሊዎቹ ሁሉ ይበልጥ በጣም አሳማሪ የሆነውንም አምላክ ትተዋላችሁን?');
INSERT INTO "sadiq_sani" VALUES(37,126,'37:126','አላህን ጌታችሁንና የቀድሞዎቹ አባቶቻችሁን ጌታ (ትተዋላችሁን)?');
INSERT INTO "sadiq_sani" VALUES(37,127,'37:127','አስተባበሉትም፡፡ ስለዚህ እነርሱ (ለቅጣት) የሚጣዱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,128,'37:128','ንጹሕ የተደረጉት የአላህ ባሮች ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,129,'37:129','በእርሱ ላይም በኋለኞቹ ሕዝቦች ውስጥ መልካም ዝናን ተውንለት፡፡');
INSERT INTO "sadiq_sani" VALUES(37,130,'37:130','ሰላም በኢልያሲን ላይ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,131,'37:131','እኛ እንደዚሁ መልካም ሠሪዎችን እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,132,'37:132','እርሱ በእርግጥ ከምእመናን ባሮቻችን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,133,'37:133','ሉጥም በእርግጥ ከመልክተኞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,134,'37:134','እርሱንና ቤተሰቦቹን ሁሉንም በአዳንናቸው ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,135,'37:135','(በቅጣቱ ውስጥ) ከቀሪዎቹ የሆነችው አሮጊት ብቻ ስትቀር፡፡');
INSERT INTO "sadiq_sani" VALUES(37,136,'37:136','ከዚያም ሌሎቹን አጠፋን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,137,'37:137','እናንተም ያነጋችሁ ስትሆኑ በእነርሱ (መኖሪያዎች) ላይ (በቀን) ታልፋላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,138,'37:138','በሌሊትም (ታልፋላችሁ)፤ ልብም አታደርጉምን?');
INSERT INTO "sadiq_sani" VALUES(37,139,'37:139','ዩኑስም በእርግጥ ከመልክተኞቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,140,'37:140','ወደ ተመላው መርከብ በኮበለለ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,141,'37:141','ዕጣም ተጣጣለ፡፡ ከተሸነፉትም ሆነ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,142,'37:142','እርሱም ተወቃሽ ሲሆን ዐሳው ዋጠው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,143,'37:143','እርሱ ለጌታው ከአወዳሾቹ ባልሆነም ኖሮ፤');
INSERT INTO "sadiq_sani" VALUES(37,144,'37:144','እስከሚቀሰቀሱበት ቀን ድረስ በሆዱ ውስጥ በቆየ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(37,145,'37:145','እርሱ በሽተኛ ሆኖ በቃይ በሌለው (በባሕር) ዳርቻ ላይ ጣልነውም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,146,'37:146','በእርሱም ላይ (በአጠገቡ) ከቅል የሆነችን ዛፍ አበቀልን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,147,'37:147','ወደ መቶ ሺሕ ሰዎችም (ዳግመኛ) ላክነው፡፡ ከቶውንም ይጨምራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,148,'37:148','አመኑም፡፡ እስከ ጊዜ ድረስም አጣቀምናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,149,'37:149','(የመካን ሰዎች) ጠይቃቸውም፡፡ «ለጌታህ ሴቶች ልጆች ለእነርሱም ወንዶች ልጆች ይኖራልን?»');
INSERT INTO "sadiq_sani" VALUES(37,150,'37:150','ወይስ እኛ እነርሱ የተጣዱ ሆነው መላእክትን ሴቶች አድርገን ፈጠርን?');
INSERT INTO "sadiq_sani" VALUES(37,151,'37:151','ንቁ! እነርሱ ከቅጥፈታቸው (የተነሳ) ይላሉ፡-');
INSERT INTO "sadiq_sani" VALUES(37,152,'37:152','«አላህ (መላእክትን) ወለደ፡፡» እነርሱም በእርግጥ ውሸታሞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,153,'37:153','በወንዶች ልጆች ላይ ይልቅ ሴቶችን ልጆች መረጥን?');
INSERT INTO "sadiq_sani" VALUES(37,154,'37:154','ለእናንተ ምን (አስረጅ) አላችሁ? እንዴት ትፈርዳላችሁ!');
INSERT INTO "sadiq_sani" VALUES(37,155,'37:155','አትገነዘቡምን?');
INSERT INTO "sadiq_sani" VALUES(37,156,'37:156','ወይስ ለእናንተ ግልጽ አስረጅ አላችሁን?');
INSERT INTO "sadiq_sani" VALUES(37,157,'37:157','«እውነተኞች እንደ ሆናችሁ መጽሐፋችሁን አምጡ» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,158,'37:158','በአላህና በአጋንንት መካከልም ዝምድናን አደረጉ፡፡ አጋንንትም እነርሱ (ይህን ባዮች በእሳት) የሚጣዱ መሆናቸውን በእርግጥ ዐውቀዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(37,159,'37:159','አላህ ከሚመጥኑት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,160,'37:160','ግን የተመረጡት የአላህ ባሮች (በአላህ ላይ አይዋሹም)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,161,'37:161','እናንተም የምትገዟቸውም ሁሉ፤');
INSERT INTO "sadiq_sani" VALUES(37,162,'37:162','በእርሱ (በአላህ) ላይ አንድንም አጥማሚዎች አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(37,163,'37:163','ያንን እርሱ ገሀነምን ገቢ የሆነውን ሰው ቢሆን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,164,'37:164','(ጂብሪል አለ) ከእኛም አንድም የለም፤ ለእርሱ የታወቀ ደረጃ ያለው ቢሆን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,165,'37:165','እኛም (ጌታን ለመግገዛት) ተሰላፊዎቹ እኛ ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,166,'37:166','እኛም (አላህን) አጥሪዎቹ እኛ ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(37,167,'37:167','እነርሱም (ከሓዲዎቹ) በእርግጥ ይሉ ነበሩ፡-');
INSERT INTO "sadiq_sani" VALUES(37,168,'37:168','«ከቀድሞዎቹ (መጻሕፍት) ገሳጭ መጽሐፍ እኛ ዘንድ በነበረን ኖሮ፤');
INSERT INTO "sadiq_sani" VALUES(37,169,'37:169','«የአላህ ምርጦች ባሮች በሆን ነበር፡፡»');
INSERT INTO "sadiq_sani" VALUES(37,170,'37:170','ግን (ቁርኣኑ በመጣላቸው ጊዜ) በእርሱ ካዱ፡፡ ወደፊትም በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,171,'37:171','(የእርዳታ) ቃላችንም መልክተኞች ለሆኑት ባሮቻችን በእውነት አልፋለች፡፡');
INSERT INTO "sadiq_sani" VALUES(37,172,'37:172','እነርሱ ተረጂዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,173,'37:173','ሰራዊቶቻችንም በእርግጥ አሸናፊዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,174,'37:174','ከእነርሱም (ከመካ ከሓዲዎች) እስከ ጥቂት ጊዜ ድረስ ዙር (ተዋቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(37,175,'37:175','እያቸውም ወደፊትም (የሚደርስባቸውን) ያያሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,176,'37:176','በቅጣታችንም ያቻኩላሉን?');
INSERT INTO "sadiq_sani" VALUES(37,177,'37:177','በቀያቸውም በወረደ ጊዜ የተስፈራሪዎቹ ንጋት ከፋ!');
INSERT INTO "sadiq_sani" VALUES(37,178,'37:178','እነርሱንም እስከ (ጥቂት) ጊዜ ድረስ ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(37,179,'37:179','ተመልከትም በእርግጥም ያያሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,180,'37:180','የማሸነፍ ጌታ የሆነው ጌታህ ከሚሉት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(37,181,'37:181','በመልክተኞቹም ላይ ሰላም ይኹኑ፤');
INSERT INTO "sadiq_sani" VALUES(37,182,'37:182','ምስጋናም ለዓለማት ጌታ ለአላህ ይሁን፡፡');
INSERT INTO "sadiq_sani" VALUES(38,1,'38:1','ጸ. (ሷድ) የክብር ባለቤት በሆነው ቁርኣን እምላለሁ (ብዙ አማልክት አሉ እንደሚሉት አይደለም)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,2,'38:2','ይልቁንም እነዚያ የካዱት ሰዎች በትዕቢትና በክርክር ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,3,'38:3','ከእነርሱ በፊት ከክፍለ ዘመናት ሕዝቦች ብዙን አጥፍተናል፡፡ (ጊዜው) የመሸሻና የማምለጫ ጊዜ ሳይሆንም (ለእርዳታ) ተጣሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,4,'38:4','ከእነርሱ የሆነ አስፈራሪም ስለ መጣላቸው ተደነቁ፡፡ ከሓዲዎቹም «ይህ ድግምተኛ (ጠንቋይ) ውሸታም ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,5,'38:5','«አማልክቶቹን አንድ አምላክ አደረጋቸውን? ይህ አስደናቂ ነገር ነው» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,6,'38:6','ከእነርሱም መኳንንቶቹ «ሂዱ፤ በአማልክቶቻችሁም (መግገዛት) ላይ ታገሱ፡፡ ይህ (ከእኛ) የሚፈለግ ነገር ነውና» እያሉ አዘገሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,7,'38:7','«ይህንንም በኋለኛይቱ ሃይማኖት አልሰማንም፡፡ ይህ ውሸትን መፍጠር እንጅ ሌላ አይደለም» (እያሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,8,'38:8','«ከመካከላችን በእርሱ ላይ ቁርኣን ተወረደን?» (አሉ)፡፡ በእውነት እነርሱ ከግሳጼዬ (ከቁርኣን) በመጠራጠር ውስጥ ናቸው፡፡ በእርግጥም ቅጣቴን ገና አልቀመሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,9,'38:9','ይልቁንም የአሸናፊውና የለጋሱ ጌታህ የችሮታው መጋዘኖች እነርሱ ዘንድ ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(38,10,'38:10','ወይስ የሰማያትና የምድር የመካከላቸውም ንግሥና የእነርሱ ነውን? (ነው ቢሉ) በመሰላሎችም ይውጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,11,'38:11','እዚያ ዘንድ ከአሕዛብ የሆኑ ተሸናፊ ሰራዊት ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,12,'38:12','ከእነርሱ በፊት የኑሕ ሕዝቦች፣ ዓድም፣ የችካሎች ባለቤት የሆነው ፈርዖንም አስተባበሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,13,'38:13','ሰሙድም፣ የሉጥ ሰዎችም፣ የአይከት ሰዎችም (አስተባበሉ)፡፡ እነዚህ አሕዛቦቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,14,'38:14','ሁሉም መልክተኞቹን ያስዋሹ እንጅ ሌላ አይደሉም፡፡ ቅጣቴም ተረጋገጠባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,15,'38:15','እነዚህም ለእርሷ መመለስ የሌላትን አንዲትን ጩኸት እንጅ ሌላን አይጠባበቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,16,'38:16','«ጌታችን ሆይ! ከምርመራው ቀን በፊት መጽሐፋችንን አስቸኩልልን» አሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,17,'38:17','በሚሉት ነገር ላይ ታገስ፡፡ የኀይል ባለቤት የሆነውን ባሪያችንንም ዳውድን አውሳላቸው፡፡ እርሱ በጣም መላሳ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(38,18,'38:18','እኛ ተራራዎችን ከቀትር በኋላና በረፋድም ከእርሱ ጋር የሚያወድሱ ሲሆኑ ገራንለት፡፡');
INSERT INTO "sadiq_sani" VALUES(38,19,'38:19','በራሪዎችንም (አእዋፍን) የሚሰበሰቡ ሆነው (ገራንለት)፡፡ ሁሉም ለእርሱ (ማወደስ) የሚመላለስ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,20,'38:20','መንግሥቱንም አበረታንለት፡፡ ጥበብንም ንግግርን መለየትንም ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,21,'38:21','የተከራካሪዎቹም ወሬ መጥቶልሃልን? ምኩራቡን በተንጠላጠሉ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,22,'38:22','በዳውድ ላይም በገቡ ጊዜ ከእነርሱም በደነገጠ ጊዜ «አትፍራ፤ (እኛ) ከፊላችን በከፊሉ ላይ ወሰን ያለፈ ሁለት ተከራካሪዎች ነን፡፡ በመካከላችንም በእውነት ፍረድ፡፡ አታዳላም፡፡ ወደ');
INSERT INTO "sadiq_sani" VALUES(38,23,'38:23','«ይህ ወንድሜ ነው፤ ለእርሱ ዘጠና ዘጠኝ ሴቶች በጎች አሉት፡፡ ለኔም አንዲት ሴት በግ');
INSERT INTO "sadiq_sani" VALUES(38,24,'38:24','«ሴት በግህን ወደ በጎቹ (ለመቀላቀል) በመጠየቁ በእርግጥ በደለህ፡፡ ከተጋሪዎችም ብዙዎቹ ከፊላቸው በከፊሉ ላይ ወሰን ያልፋሉ፡፡ እነዚያ ያመኑትና መልካሞችን የሠሩት ብቻ ሲቀሩ፡፡ እነርሱም በጣም ጥቂቶች ናቸው» አለ፡፡ ዳውድም የፈተንነው መኾኑን ዐወቀ፡፡ ጌታውንም ምሕረትን ለመነ፡፡ ሰጋጅ ኾኖ ወደቀም፡፡ በመጸጸት ተመለሰም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,25,'38:25','ለእርሱም እኛ ዘንድ መቅረብ (ክብር) መልካም መመለሻም አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,26,'38:26','ዳውድ ሆይ! እኛ በምድር ላይ ምትክ አድርገንሃልና በሰዎች መካከል በእውነት ፍረድ፡፡ ዝንባሌሀንም አትከተል፤ከአላህ መንገድ ያሳሳትሃልና፡፡ እነዚያ ከአላህ መንገድ የሚሳሳቱ የምርመራውን ቀን በመርሳታቸው ለእነርሱ ብርቱ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,27,'38:27','ሰማይንና ምድርን በመካከላቸውም ያለውን ሁሉ ለከንቱ አልፈጠርንም፡፡ ይህ የእነዚያ የካዱት ሰዎች ጥርጣሬ ነው፡፡ ለእነዚያም ለካዱት ሰዎች ከእሳት ወዮላቸው!');
INSERT INTO "sadiq_sani" VALUES(38,28,'38:28','በእውነቱ እነዚያን ያመኑትንና መልካሞችን የሠሩትን በምድር ውስጥ እንደሚያጠፉት እናደርጋለን? ወይስ አላህን ፊረዎቹን እንደ ከሓዲዎቹ እናደርጋለን?');
INSERT INTO "sadiq_sani" VALUES(38,29,'38:29','(ይህ) ወዳንተ ያወረድነው ብሩክ መጽሐፍ ነው፡፡ አንቀጾቹን እንዲያስተነትኑና የአእምሮዎች ባለቤቶችም እንዲገሰጹ (አወረድነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,30,'38:30','ለዳውድም ሱለይማንን ሰጠነው፡፡ ምን ያምር ባሪያ! (ሱለይማን)፣ እርሱ መላሳ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,31,'38:31','በእርሱ ላይ ከቀትር በኋላ በሦስት እግሮችና በአራተኛዋ ኮቴ ጫፍ የሚቆሙ ጮሌዎች ፈረሶች በተቀረቡለት ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,32,'38:32','አለም «እኔ (ፀሐይ) በግርዶ እስከ ተደበቀች ድረስ ከጌታዬ ማስታወስ ፋንታ ፈረስን መውደድን መረጥኩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,33,'38:33','«በእኔ ላይ መልሷት» (አለ) አጋዶችዋንና አንገቶችዋንም ማበስ ያዘ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,34,'38:34','ሱለይማንንም በእርግጥ ፈተንነው፡፡ በመንበሩም ላይ አካልን ጣልን፡፡ ከዚያም በመጸጸት ተመለሰ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,35,'38:35','«ጌታዬ ሆይ! ለእኔ ማር፡፡ ከእኔ በኋላ ለአንድም የማይገባንም ንግሥና ስጠኝ፡፡ አንተ ለጋሱ አንተ ብቻ ነህና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,36,'38:36','ነፋስንም በትእዛዙ ወደፈለገበት ስፍራ ልዝብ ኾና የምትፈስ ስትኾን ገራንለት፡፡');
INSERT INTO "sadiq_sani" VALUES(38,37,'38:37','ሰይጣናትንም ገንቢዎችንና ሰጣሚዎችን ሁሉ (ገራንለት)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,38,'38:38','ሌሎችንም በፍንጆች ተቆራኞችን (ገራንለት)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,39,'38:39','«ይህ ስጦታችን ነው፡፡ ያለግምት ለግስ፤ ወይም ጨብጥ» (አልነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,40,'38:40','ለእርሱም እኛ ዘንድ መቅረብ፤ መልካም መመለሻም በእርግጥ አልለው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,41,'38:41','ባሪያችንን አዩብንም አውሳላቸው፡፡ «እኔ ሰይጣን በጉዳትና በስቃይ ነካኝ» ሲል ጌታውን በተጣራ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,42,'38:42','«በእግርህ (ምድርን) ምታ፡፡ ይህ ቀዝቃዛ መታጠቢያ መጠጥም ነው» (ተባለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,43,'38:43','ለእርሱም ከእኛ ዘንድ ለችሮታ ባለአእምሮዎችንም ለመገሠጽ ቤተሰቦቹን ከእነርሱም ጋር መሰላቸውን ሰጠነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,44,'38:44','«በእጅህም ጭብጥ አርጩሜን ያዝ፡፡ በእርሱም (ሚስትህን) ምታ፡፡ ማላህንም አታፍርስ» (አልነው)፡፡ እኛ ታጋሸ ኾኖ አገኘነው፡፡ ምን ያምር ባሪያ እርሱ በጣም መላሳ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,45,'38:45','ባሮቻችንንም ኢብራሂምን፣ ኢስሐቅንና ያዕቆበንም፣ የኅይልና የማስተዋል ባለቤቶች የኾኑን አውሳላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,46,'38:46','እኛ ጥሩ በኾነች ጠባይ መረጥናቸው፡፡ (እርሷም) የመጨረሻይቱን አገር ማስታወስ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(38,47,'38:47','እነርሱም እኛ ዘንድ ከመልካሞቹ ምርጦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,48,'38:48','ኢስማዒልንም፣ አልየሰዕንም፣ ዙልኪፍልንም አውሳ፡፡ ሁሉም ከበላጮቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,49,'38:49','ይህ መልካም ዝና ነው፡፡ ለአላህ ፈሪዎችም በእርግጥ ውብ የኾነ መመለሻ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,50,'38:50','በሮቻቸው ለእነርሱ የተከፈቱ ሲኾኑ የመኖሪያ ገነቶች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,51,'38:51','በእርሷ ውስጥ የተደገፉ ኾነው በውስጧ በብዙ እሸቶችና በመጠጥም ያዝዛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,52,'38:52','እነርሱ ዘንድም ዓይኖቻቸውን (በባሎቻቸው ላይ) አሳጣሪዎች እኩያዎች (ሴቶች) አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,53,'38:53','ይህ ለምርመራው ቀን የምትቀጠሩት ተሰፋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,54,'38:54','ይህ ሲሳያችን ነው፡፡ ለእርሱ ምንም ማለቅ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,55,'38:55','ይህ (ለአማኞች ነው)፡፡ ለጠማሞችም በጣም የከፋ መመለሻ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,56,'38:56','የሚገቧት ስትኾን ገሀነም (አለቻቸው)፡፡ ምንጣፊቱም ምንኛ ከፋች፡፡');
INSERT INTO "sadiq_sani" VALUES(38,57,'38:57','ይህ (ለከሓዲዎች ነው)፡፡ ይቅመሱትም፡፡ የፈላ ውሃና እዥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,58,'38:58','ሌላም ከመሰሉ ዓይነቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,59,'38:59','ይህ ከእናንተ ጋር በመጋፈጥ ገቢ ጭፍራ ነው፤ (ይባላሉ)፡፡ ለእነርሱ አይስፋቸው፡፡ እነርሱ እሳትን ገቢዎች ናቸውና (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,60,'38:60','(ተከታዮቹ) ይላሉ «ይልቁን እናንተ አይስፋችሁ፡፡ እናንተ (ክህደቱን) ለእኛ አቀረባችሁት፡፡ መርጊያይቱም (ገሀነም) ከፋች፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,61,'38:61','«ጌታችን ሆይ! ይህንን ያቀረበልንን ሰው በእሳት ውስጥ እጥፍን ቅጣት ጨምርለት» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,62,'38:62','ይላሉም «ለእኛ ምን አለን? ከመጥፎዎች እንቆጥራቸው የነበሩትን ሰዎች (እዚህ) አናይምሳ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,63,'38:63','«መቀለጃ አድርገን ያዝናቸውን? ወይስ ዓይኖቻችን ከእነሱ ዋለሉ?»');
INSERT INTO "sadiq_sani" VALUES(38,64,'38:64','ይህ በእርግጥ እምነት ነው፡፡ (እርሱም) የእሳት ሰዎች (የእርስ በእርስ) መካሰስ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,65,'38:65','«እኔ አስፈራሪ ብቻ ነኝ፡፡ ኀያል አንድ ከኾነው አላህ በቀር ምንም አምላክ የለም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,66,'38:66','«የሰማያትና የምድር በመካከላቸውም ያለው ሁሉ ጌታ አሸናፊው መሓሪው ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,67,'38:67','በላቸው «እርሱ (ቁርኣን) ታላቅ ዜና ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,68,'38:68','«እናንተ ከእርሱ ዘንጊዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,69,'38:69','(በአደም ነገር) «በሚከራከሩ ጊዜ በላይኛው ሰራዊት ለእኔ ምንም ዕውቀት አልነበረኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,70,'38:70','«ወደእኔም አስፈራሪ ገላጭ መኾኔ እንጂ ሌላ አይወረድልኝም» (በል)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,71,'38:71','ጌታህ «ለመላእክት እኔ ሰውን ከጭቃ ፈጣሪ ነኝ ባለ ጊዜ» (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,72,'38:72','«ፍጥረቱንም ባስተካከልኩና ከመንፈሴ በነፋሁበት ጊዜ ለእርሱ ሰጋጆች ኾናችሁ ውደቁ» (አልኩ)፡፡');
INSERT INTO "sadiq_sani" VALUES(38,73,'38:73','መላእክትም መላውም ባንድነት ሰገዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,74,'38:74','ኢብሊስ ብቻ ሲቀር፡፡ ኮራ፤ ከከሓዲዎቹም ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(38,75,'38:75','(አላህም) «ኢብሊስ ሆይ! በሁለት እጆቼ (በኃይሌ) ለፈጠርኩት ከመስገድ ምን ከለከለህ? (አሁን) ኮራህን? ወይስ (ፊቱኑ) ከትዕቢተኞቹ ነበርክ?» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(38,76,'38:76','«እኔ ከእርሱ በላጭ ነኝ፡፡ ከእሳት ፈጠርከኝ፤ ከጭቃም ፈጠርከው» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,77,'38:77','(አላህ) አለው «ከእርሷ ውጣ፤ አንተ የተባረርክ ነህና፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,78,'38:78','«እርግማኔም እስከ ፍርዱ ቀን ድረስ ባንተ ላይ ይኹን፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,79,'38:79','«ጌታዬ ሆይ! እስከሚቀሰቀሱበት ቀን ድረስ አቆየኝም» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,80,'38:80','(አላህም) አለው «አንተ ከሚቆዩት ነህ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,81,'38:81','«እስከ ታወቀው ወቅት ቀን ድረስ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,82,'38:82','(እርሱም) አለ «በአሸናፊነትህ ይኹንብኝ፤ በመላ አሳስታቸዋለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,83,'38:83','«ከእነርሱ ምርጥ የኾኑት ባሮችህ ብቻ ሲቀሩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,84,'38:84','(አላህ) አለው «እውነቱም (ከኔ ነው)፡፡ እውነትንም እላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(38,85,'38:85','ከአንተና ከእነርሱ ውስጥ ከተከተሉህ ባንድ ላይ ገሀነምን በእርግጥ እሞላታለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(38,86,'38:86','በላቸው «በእርሱ ላይ ምንም ዋጋ አልጠይቃችሁም፤ እኔም ከተግደርዳሪዎቹ አይደለሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,87,'38:87','«እርሱ (ቁርኣን) ለዓለማት መገሠጫ እንጂ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(38,88,'38:88','«ትንቢቱንም (እውነት መኾኑን) ከጊዜ በኋላ በእርግጥ ታውቁታላችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(39,1,'39:1','የመጽሐፉ መወረድ አሸናፊው ጥበበኛው ከኾነው አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,2,'39:2','እኛ ወደ አንተ መጽሐፉን በእውነት (የተመላ) ሲኾን አወረድነው፡፡ አላህንም ሃይማኖትን ለእርሱ ብቻ ያጠራህ ኾነህ ተገዛው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,3,'39:3','ንቁ! ፍጹም ጥሩ የኾነው ሃይማኖት የአላህ ብቻ ነው፡፡ እነዚያም ከእርሱ ሌላ (ጣዖታትን) ረዳቶች የያዙት «ወደ አላህ ማቃረብን እንዲያቀርቡን እንጅ ለሌላ አንገዛቸውም» (ይላሉ)፡፡ አላህ በዚያ እነርሱ በእርሱ በሚለያዩበት ነገር በመካከላቸው ይፈርዳል፡፡ አላህ እርሱ ውሸታም ከሓዲ የኾነን ሰው አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,4,'39:4','አላህ ልጅን መያዝ በፈለገ ኖሮ ከሚፈጥረው ውስጥ የሚሻውን ይመርጥ ነበር፡፡ ጥራት ተገባው፡፡ እርሱ አሸናፊው አንዱ አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,5,'39:5','ሰማያትንና ምድርን በእወነት ፈጠረ፡፡ ሌሊትን በቀን ላይ ይጠቀልላል፡፡ ቀንንም በሌሊት ላይ ይጠቀልላል፤ ፀሐይንና ጨረቃንም ገራ፡፡ ሁሉም ለተወሰነ ጊዜ ይሮጣሉ፡፡ ንቁ! እርሱ አሸናፊው መሓሪው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,6,'39:6','ከአንዲት ነፍስ ፈጠራችሁ፡፡ ከዚያም ከእርሷ መቀናጆዋን አደረገ፡፡ ለእናንተም ከግመልና ከከብት፣ ከፍየል፣ ከበግ ስምንት ዓይነቶችን (ወንድና ሴት) አወረደ፡፡ በእናቶቻችሁ ሆዶች ውስጥ በሦስት ጨለማዎች ውስጥ ከመፍጠር በኋላ (ሙሉ) መፍጠርን ይፈጠራችኋል፡፡ ይህ ጌታችሁ አላህ ነው፡፡ ስልጣኑ የርሱ ብቻ ነው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ ታዲያ ወዴት ትዞራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,7,'39:7','ብትክዱ አላህ ከእናንተ የተብቃቃ ነው፡፡ ለባሪያዎቹም ከህደትን አይወድም፡፡ ብታመሰግኑም፤ እርሱን ይወድላችኋል፡፡ ማንኛይቱም ኃጢኣትን ተሸካሚ ነፍስ የሌላይቱን ኃጢኣት አትሸከምም፡፡ ከዚያም መመለሻችሁ ወደ ጌታችሁ ነው፡፡ ትሠሩት የነበራችሁትንም ይነግራችኋል፡፡ እርሱ በልቦች ውስጥ ያሉትን ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(39,8,'39:8','ሰውንም ጉዳት ባገኘው ጊዜ ጌታውን ወደርሱ ተመላሽ ኾኖ ይጠራል፡፡ ከዚያም ከእርሱ ዘንድ ጸጋን በሰጠው ጊዜ ያንን ከዚያ በፊት ወደርሱ ይጸልይበት የነበረውን (መከራ) ይረሳል፡፡ ከመንገዱ ለማሳሳትም ለአላህ ባላንጣዎችን ያደርጋል፡፡ «በክህደትህ ጥቂትን ተጣቀም፡፡ አንተ በእርግጥ ከእሳት ጓዶች ነህ» በለው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,9,'39:9','እርሱ የመጨረሻይቱን ዓለም (ቅጣት) የሚፈራ የጌታውንም ችሮታ የሚከጅል ሲኾን በሌሊት ሰዓቶች ሰጋጅና ቋሚ ኾኖ ለጌታው የሚግገዛ ሰው (እንደ ተስፋ ቢሱ ከሓዲ ነውን? በለው)፡፡ «እነዚያ የሚያውቁና እነዚያ የማያውቁ ይስተካከላሉን?» በላቸው፡፡ የሚገነዘቡት ባለ አእምሮዎቹ ብቻ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,10,'39:10','(ጌታችሁ) «እናንተ ያመናችሁ ባሮቼ ሆይ! ጌታችሁን ፍሩ፡፡ ለነዚያ በዚህች በቅርቢቱ ዓለም መልካም ለሠሩት መልካም ምንዳ አላቸው፡፡ የአላህ ምድርም ሰፊ ናት፤ (ብትቸገሩ ተሰደዱ)፡፡ ታጋሾቹ ምንዳቸውን የሚሰጡት ያለግምት ነው» (ይላል) በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,11,'39:11','በል «እኔ አላህን ሃይማኖትን ለርሱ ያጠራሁ ኾኜ እንድግገዛው ታዘዝኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,12,'39:12','«የሙስሊሞችም መጀመሪያ እንድኾን ታዘዝኩ፡፡»');
INSERT INTO "sadiq_sani" VALUES(39,13,'39:13','«እኔ ከጌታዬ ባምጽ የታላቁን ቀን ቅጣት እፈራለሁ» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,14,'39:14','አላህን «ሃይማኖቴን ለእርሱ ያጠራሁ ኾኜ እግገዛዋለሁ» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,15,'39:15','«ከእርሱ ሌላ የሻችሁትን ተገዙ፡፡ ከሳሪዎች ማለት እነዚያ በትንሣኤ ቀን ነፍሶቻቸውንና ቤተሰቦቻቸውን ያከሰሩ ናቸው፡፡ ንቁ! ይህ እርሱ ግልጽ ከሳራ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,16,'39:16','ለእነርሱ ከበላያቸው ከእሳት የኾኑ (ድርብርብ) ጥላዎች አልሏቸው፡፡ ከበታቻቸውም ጥላዎች አልሏቸው። ይህ አላህ ባሮቹን (እንዲጠነቀቁ) በእርሱ ያስፈራራበታል፡፡ «ባሮቼ ሆይ! ስለዚህ ፍሩኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(39,17,'39:17','እነዚያም ጣዖታትን የሚግገዟት ከመኾን የራቁ ወደ አላህም የዞሩ ለእነርሱ ብስራት አልላቸው፡፡ ስለዚህ ባሮቼን አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(39,18,'39:18','እነዚያን ንግግርን የሚያዳምጡትንና መልካሙን የሚከተሉትን (አብስር)፡፡ እነዚያ እነርሱ አላህ የመራቸው ናቸው፡፡ እነዚያም እነርሱ ባለአእምሮዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,19,'39:19','በእርሱ ላይ የቅጣት ቃል የተረጋገጠችበትን ሰው (ትመራዋለህን?) አንተ በእሳት ውስጥ ያለን ሰው ታድናለህን?');
INSERT INTO "sadiq_sani" VALUES(39,20,'39:20','ግን እነዚያ ጌታቸውን የፈሩ ለእነርሱ ከበላያቸው የታነጹ ሰገነቶች ያሏቸው ሰገነቶች ከስሮቻቸው ወንዞች የሚፈሱባቸው የኾኑ አልሏቸው፡፡ (ይህንን) አላህ ቃል ገባላቸው፡፡ አላህ ቃሉን አያፈርስም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,21,'39:21','አላህ ከሰማይ ውሃን እንዳወረደና በምድርም ውስጥ ምንጮች አድርጎ እንዳስገባው አላየህምን? ከዚያም በእርሱ ዓይነቶቹ የተለያዩን አዝመራ ያወጣል፡፡ ከዚያም ይደርቃል፡፡ ገርጥቶም ታየዋለህ፡፡ ከዚያም ስብርብር ያደርገዋል፡፡ በዚህ ውስጥ ለባለ አእምሮዎች ግሣጼ አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(39,22,'39:22','አላህ ደረቱን ለእስልምና ያስፋለትና እርሱም ከጌታው ዘንድ በብርሃን ላይ የኾነ ሰው (ልቡ እንደ ደረቀ ሰው ነውን?) ልቦቻቸውም ከአላህ መወሳት ለደረቁ ሰዎች ወዮላቸው፡፡ እነዚያ በግልጽ መሳሳት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,23,'39:23','አላህ ከዜና ሁሉ መልካምን፣ ተመሳሳይ፣ ተደጋጋሚ የኾነን መጽሐፍ አወረደ፡፡ ከእርሱ (ግሣጼ) የእነዚያ ጌታቸውን የሚፈሩት ሰዎች ቆዳዎች ይኮማተራሉ፡፡ ከዚያም ቆዳዎቻቸውና ልቦቻቸው ወደ አላህ (ተስፋ) ማስታወስ ይለዝባሉ፡፡ ይህ የአላህ መምሪያ ነው፡፡ በእርሱ የሚሻውን ሰው ይመራበታል፡፡ አላህም የሚያጠመው ሰው ለእርሱ ምንም አቅኚ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,24,'39:24','በትንሣኤ ቀን ክፉን ቅጣት በፊቱ የሚመክት ሰው (ከቅጣት እንደሚድን ነውን?) ለበዳዮችም «ትሠሩት የነበራችሁትን (ቅጣቱን) ቅመሱ» ይባላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,25,'39:25','ከእነሱ በፊት የነበሩት አስተባበሉ፡፡ ቅጣቱም ከማያውቁት በኩል መጣባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,26,'39:26','አላህም በቅርቢቱ ሕይወት ውርደትን አቀመሳቸው፡፡ የመጨረሻይቱም ዓለም ቅጣት በጣም ትልቅ ነው፡፡ የሚያውቁ ቢኾኑ ኖሮ (አያስተባብሉም ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,27,'39:27','በዚህም ቁርኣን ውስጥ ለሰዎች ይገሠጹ ዘንድ ከየምሳሌው ሁሉ በእርግጥ አብራራን፡፡');
INSERT INTO "sadiq_sani" VALUES(39,28,'39:28','መዛባት የሌለበት ዐረብኛ ቁርኣን ሲኾን (አብራራነው)፡፡ ሊጠነቀቁ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(39,29,'39:29','አላህ በእርሱ ተጨቃጫቂዎች የኾኑ ተጋሪዎች ያሉትን ባሪያና ለአንድ ሰው ብቻ ንጹሕ የኾነን ባሪያ (ለሚያጋራና ባንድነቱ ለሚያምን ሰው) ምሳሌ አደረገ፡፡ በምሳሌ ይስተካከላሉን? ምስጋና ለአላህ ይገባው፡፡ በእውነቱ አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,30,'39:30','አንተ ሟች ነህ፤ እነርሱም ሟቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,31,'39:31','ከዚያም እናንተ በትንሣኤ ቀን እጌታችሁ ዘንድ ትከራከራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,32,'39:32','በአላህም ላይ ከዋሸ እውነቱንም በመጣለት ጊዜ ከአስተባበለ ሰው ይበልጥ በዳይ ማን ነው?');
INSERT INTO "sadiq_sani" VALUES(39,33,'39:33','ያም በእውነት የመጣው በእርሱ ያመነውም እነዚያ እነርሱ አላህን ፊሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,34,'39:34','ለእነርሱ በጌታቸው ዘንድ የሚሹት ሁሉ አልላቸው፡፡ ይህ የበጎ አድራጊዎች ምንዳ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,35,'39:35','አላህ ያንን (በስሕተት) የሠሩትን መጥፎ ሥራ ከእነርሱ ሊሰርይላቸው በዚያም ይሠሩት በነበሩት በመልካሙ ሥራ ምንዳቸውን ሊመነዳቸው (ይህንን አደረገ)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,36,'39:36','አላህ ባሪያውን በቂ አይደለምን? በእነዚያ ከእርሱ ሌላ በኾኑትም (ጣዖታት) ያስፈራሩሃል፡፡ አላህ የሚያጠመውም ሰው ለርሱ ምንም አቅኚ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,37,'39:37','አላህ የሚያቀናውም ሰው ለእርሱ ምንም አጥማሚ የለውም፡፡ አላህ አሸናፊ የመበቀል ባለቤት አይደለምን?');
INSERT INTO "sadiq_sani" VALUES(39,38,'39:38','ሰማያትንና ምድርን የፈጠረ ማን እንደ ኾነ ብትጠይቃቸው «አላህ ነው» ይሉሃል፡፡ «ከአላህ ሌላ የምትግገዟቸውን (ጣዖታት) አያችሁን? (ንገሩኝ) አላህ በጉዳት ቢፈልገኝ እነርሱ ጉዳቱን ገላጮች ናቸውን? ወይስ በችሮታው ቢፈቅደኝ እነርሱ ችሮታውን አጋጆች ናቸውን?» በላቸው፡፡ «አላህ በቂዬ ነው፡፡ በእርሱ ላይ ተመኪዎች ይመካሉ» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,39,'39:39','(ሙሐመድ ሆይ!) በላቸው «ሕዝቦቼ ሆይ! ባላችሁበት ኹኔታ ላይ ሥሩ፡፡ እኔ ሠሪ ነኝና፡፡ ወደፊትም ታውቃላችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(39,40,'39:40','ያንን የሚያሳፍረው ቅጣት የሚመጠበትንና በእርሱ ላይ ዘውታሪ ቅጣት የሚሰፍርበትን (ታውቃላችሁ)');
INSERT INTO "sadiq_sani" VALUES(39,41,'39:41','እኛ መጽሐፉን ባንተ ላይ ለሰዎች (ጥቅም) በእውነት አወረድነው፡፡ የተመራም ሰው ለነፍሱ ነው፡፡ የጠመመም ሰው የሚጠምመው (ጉዳቱ) በእርሷ ላይ ነው፡፡ አንተም (ታስገድዳቸው ዘንድ) በእነርሱ ላይ ጠባቂ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,42,'39:42','አላህ ነፍሶችን በሞታቸው ጊዜ ይወስዳል፡፡ ያችንም ያልሞተችውን በእንቅልፏ ጊዜ (ይወስዳታል)፡፡ ታዲያ ያችን ሞትን የፈረደባትን ይይዛታል፡፡ ሌላይቱንም እስከ ተወሰነ ጊዜ ድረስ ይለቅቃታል፡፡ በዚህ ውስጥ ለሚያስተነትኑ ሕዝቦች ምልክቶች አልሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(39,43,'39:43','ይልቁንም (ቁረይሾች) ከአላህ ሌላ አማላጆችን ያዙ፡፡ «እነርሱ ምንንም የማይችሉና የማያውቁ ቢኾኑም?» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,44,'39:44','«ምልጃ መላውም የአላህ ብቻ ነው፡፡ የሰማያትና የምድር ሥልጣን የእርሱ ብቻ ነው፡፡ ከዚያም ወደእርሱ ትመለሳላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,45,'39:45','አላህም ብቻውን በተወሳ ጊዜ የእነዚያ በመጨረሻይቱ ዓለም የማያምኑት ሰዎች ልቦች ይሸማቀቃሉ፤ (ይደነብራሉ)፡፡ እነዚያም ከእርሱ ሌላ የኾኑት (ጣዖታት) በተወሱ ጊዜ እነርሱ ወዲያውኑ ይደሰታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,46,'39:46','«ሰማያትንና ምድርን የፈጠርክ ሩቁንም ቅርቡንም ዐዋቂ የኾንክ አላህ ሆይ! አንተ በባሮችህ መካከል በእዚያ በእርሱ ይለያዩበት በነበሩት ነገር ትፈርዳለህ» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,47,'39:47','ለእነዚያም ለበደሉት በምድር ያለው ሁሉ መላውም ከእርሱ ጋር መሰሉም ቢኖራቸው ኖሮ በትንሣኤ ቀን ከቅጣቱ ክፋት የተነሳ በእርሱ ነፍሶቻቸውን በተበዡበት ነበር፡፡ ለእነርሱም ከአላህ ዘንድ ያስቡት ያልነበሩት ሁሉ ይገለጽላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,48,'39:48','ለእነርሱም የሠሩዋቸው መጥፎዎቹ ይገለጹላቸዋል፡፡ በእነርሱም ላይ በእርሱ ያላግጡበት የነበሩት ቅጣት ይሰፍርባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,49,'39:49','ሰውም ጉዳት ባገኘው ጊዜ ይጠራናል፡፡ ከዚያም ከእኛ የኾነውን ጸጋ በሰጠነው ጊዜ «እርሱን የተሰጠሁት፤ በዕውቀት ብቻ ነው» ይላል፡፡ ይልቁንም እርሷ (ጸጋይቱ) መፈተኛ ናት፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,50,'39:50','እነዚያ ከእነርሱ በፊት የነበሩት ሕዝቦች በእርግጥ ብለዋታል፡፡ ይሠሩት የነበሩትም ነገር ከእነርሱ አልጠቀማቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,51,'39:51','የሠሩዋቸውም መጥፎዎች (ቅጣት) አገኛቸው፡፡ ከእነዚህም እነዚያ የበደሉት የሠሩዋቸው መጥፎዎች (ፍዳ) በእርግጥ ይነካቸዋል፡፡ እነርሱም አምላጮች አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,52,'39:52','አላህ ሲሳዩን ለሚሻው ሰው የሚያሰፋ የሚያጠብም መኾኑን አያውቁምን? በዚህ ውስጥ ለሚያምኑት ሕዝቦች ግሣጼዎች አሉበት፡፡');
INSERT INTO "sadiq_sani" VALUES(39,53,'39:53','በላቸው «እናንተ በነፍሶቻችሁ ላይ ድንበር ያለፋችሁ ባሮቼ ሆይ! ከአላህ እዝነት ተስፋ አትቁረጡ፡፡ አላህ ኃጢኣቶችን በመላ ይምራልና፡፡ እነሆ እርሱ መሓሪው አዛኙ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(39,54,'39:54','«ቅጣቱም ወደእናንተ ከመምጣቱና ከዚያም የማትርረዱ ከመኾናችሁ በፊት ወደ ጌታችሁ (በመጸጸት) ተመለሱ፡፡ ለእርሱም ታዘዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,55,'39:55','«እናንተ የማታውቁ ስትኾኑም ቅጣቱ በድንገት ሳይመጣባችሁ በፊት ከጌታችሁ ወደናንተ የተወረደውን መልካሙን (መጽሐፍ) ተከተሉ፡፡»');
INSERT INTO "sadiq_sani" VALUES(39,56,'39:56','(የካደች) ነፍስ «እኔ ከሚሳለቁት የነበርኩ ስኾን በአላህ በኩል ባጓደልኩት ዋ ጸጸቴ» ማለቷን (ለመፍራት)፤');
INSERT INTO "sadiq_sani" VALUES(39,57,'39:57','ወይም «አላህ በመራኝ ኖሮ ከሚጠነቀቁት እኾን ነበር» ማለቷን (ለመፍራት)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,58,'39:58','ወይም ቅጣቱን በምታይ ጊዜ «ለእኔ (ወደ ምድረ ዓለም) አንድ ጊዜ መመለስ በኖረኝና ከበጎ አድራጊዎቹ በኾንኩ» ማለቷን (ለመፍራት መልካሙን ተከተሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,59,'39:59','የለም ተመርተሃል፡፡ አንቀጾቼ በእርግጥ መጥተውልሃል፡፡ በእነርሱም አስተባብለሃል፡፡ ኮርተሃልም፡፡ ከከሓዲዎቹም ኾነሃል፤ (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,60,'39:60','በትንሣኤ ቀንም እነዚያን በአላህ ላይ የዋሹትን ፊቶቻቸው የጠቆሩ ኾነው ታያቸዋለህ፡፡ በገሀነም ውስጥ ለትዕቢተኞች መኖሪያ የለምን?');
INSERT INTO "sadiq_sani" VALUES(39,61,'39:61','እነዚያንም የተጠነቀቁትን በማግኛቸው ስፍራ ኾነው አላህ ያድናቸዋል፡፡ ክፉ ነገር አይነካቸውም፡፡ እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,62,'39:62','አላህ የነገሩ ሁሉ ፈጣሪ ነው፡፡ እርሱም በነገሩ ሁሉ ላይ አስተናባሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,63,'39:63','የሰማያትና የምድር (ድልብ) መክፈቻዎች የእርሱ ብቻ ናቸው፡፡ እነዚያም በአላህ አንቀጾች የካዱት እነዚያ እነሱ ከሳሪዎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,64,'39:64','«እናንተ መሃይማን ሆይ! ከአላህ ሌላን እንድግገዛ ታዙኛላችሁን?» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,65,'39:65','ብታጋራ ሥራህ በእርግጥ ይታበሳል፡፡ በእርግጥም ከከሓዲዎቹ ትኾናለህ ማለት ወደ አንተም ወደእነዚያም ካንተ በፊት ወደነበሩት በእርግጥ ተወርዷል፡፡');
INSERT INTO "sadiq_sani" VALUES(39,66,'39:66','ይልቅ አላህን ብቻ ተገዛም፡፡ ከአመስጋኞቹም ኹን፡፡');
INSERT INTO "sadiq_sani" VALUES(39,67,'39:67','አላህንም በትንሣኤ ቀን ምድር በመላ ጭብጡ ስትኾን፤ ሰማያትም በኀይሉ የሚጠቀለሉ ሲኾኑ (ከርሱ ጋር ሌላን በማጋራታቸው) ተገቢ ክብሩን አላከበሩትም፡፡ ከሚያጋሩት ጠራ፤ ላቀም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,68,'39:68','በቀንዱም ይንነፋል፡፡ በሰማያት ውስጥ ያለው ፍጡርና በምድርም ውስጥ ያለው ፍጡር ሁሉ አላህ የሻው ሲቀር በድንጋጤ ይሞታል፡፡ ከዚያም በእርሱ ሌላ (መንነፋት) ይንነፋል፡፡ ወዲያውም እነርሱ (የሚሠራባቸውን) የሚጠባበቁ ኾነው ቋሚዎች ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,69,'39:69','ምድርም በጌታዋ ብርሃን ታበራለች፡፡ መጽሐፉም ይቅቀረባል፡፡ ነቢያቱና ምስክሮቹም ይመጣሉ፡፡ በመካከላቸውም በእውነት ይፈረዳል፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(39,70,'39:70','ነፍስም ሁሉ የሠራችውን ሥራ ትሞላለች፤ (ትሰፈራለች)፡፡ እርሱም የሚሠሩትን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(39,71,'39:71','እነዚያ የካዱትም የተከፋፈሉ ጭፍሮች ኾነው ወደ ገሀነም ይንነዳሉ፡፡ በመጧትም ጊዜ ደጃፎችዋ ይከፈታሉ፡፡ ዘበኞችዋም «ከእናንተ የኾኑ መልክተኞች የጌታችሁን አንቀጾች');
INSERT INTO "sadiq_sani" VALUES(39,72,'39:72','«የገሀነምን ደጃፎች በውስጧ ዘውታሪዎች ስትኾኑ ግቡ፡፡ የትዕቢተኞችም መኖሪያ ምን ትከፋ!» ይባላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(39,73,'39:73','እነዚያም ጌታቸውን የፈሩት የተከፋፈሉ ጭፍሮች ኾነው ወደ ገነት ይንነዳሉ፡፡ በመጧትም ጊዜ ደጃፎችዋ የተከፈቱ ሲኾኑ ዘበኞችዋም ለእነርሱ «ሰላም በናንተ ላይ ይኹን፡፡ ተዋባችሁ፡፡ ዘውታሪዎች ኾናችሁ ግቧት» በሏቸው ጊዜ (ይገቧታል)፡፡');
INSERT INTO "sadiq_sani" VALUES(39,74,'39:74','«ምስጋና ለአላህ ለዚያ ተስፋ ቃሉን ለሞላልን፡፡ የገነትን ምድር በምንሻው ስፍራ የምንሰፍር ስንኾን ላወረሰን ይገባው» ይላሉም፡፡ የሠሪዎችም ምንዳ ምን ያምር!');
INSERT INTO "sadiq_sani" VALUES(39,75,'39:75','መላእክትንም በጌታቸው ምስጋና ቀላቅለው የሚያጠሩ ሲኾኑ በዐርሹ ዙሪያ ከባቢዎች ኾነው ታያለህ፡፡ በመካከላቸው በእውነት ይፈረዳል፡፡ ይባላልም፤ «ምስጋና ለአላህ ለዓለማት ጌታ ይገባው፡፡»');
INSERT INTO "sadiq_sani" VALUES(40,1,'40:1','ሐ.መ (ሓ.ሚም)፤');
INSERT INTO "sadiq_sani" VALUES(40,2,'40:2','የመጽሐፉ መወረድ አሸናፊ ዐዋቂ ከሆነው አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,3,'40:3','ኀጢአትን መሓሪ፣ ጸጸትንም ተቀባይ፣ ቅጣተ ብርቱ፣ የልግስና ባለቤት ከኾነው (አላህ የወረደ ነው)፡፡ ከእርሱ በቀር አምላክ የለም፡፡ መመለሻው ወደርሱ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,4,'40:4','በአላህ አንቀጾች እነዚያ የካዱት ቢኾኑ እንጅ ማንም (በመዝለፍ) አይከራከርም፡፡ በያገሮችም ውስጥ መዘዋወራቸው አይሸንግልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,5,'40:5','ከእነርሱ በፊት የኑሕ ሕዝቦች ከኋላቸውም አሕዛቦቹ አስተባባሉ፡፡ ሕዝቦችም ሁሉ መልክተኛቸውን ሊይዙ አሰቡ፡፡ በውሸትም በእርሱ እውነትን ሊያጠፉበት ተከራከሩ፡፡ ያዝኳቸውም፡፡ ታዲያ ቅጣቴ እንዴት ነበር?');
INSERT INTO "sadiq_sani" VALUES(40,6,'40:6','እንደዚሁም የጌታህ ቃል በእነዚያ በካዱት ላይ እነርሱ የእሳት ጓዶች ናቸው ማለት ተረጋገጠች፡፡');
INSERT INTO "sadiq_sani" VALUES(40,7,'40:7','እነዚያ ዐርሹን የሚሸከሙት እነዚያም በዙሪያው ያሉት በጌታቸው ምስጋና ያወድሳሉ፡፡ በእርሱም ያምናሉ፡፡ «ጌታችን ሆይ! ነገሩን ሁሉ በእዝነትና በዕውቀት ከበሃል፡፡ ስለዚህ ለእነዚያ ለተጸጸቱት መንገድህንም ለተከተሉት ምሕረት አድርግላቸው፡፡ የእሳትንም ቅጣት ጠብቃቸው፡፡» እያሉ ለእነዚያ ላመኑት ምሕረትን ይለምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,8,'40:8','«ጌታችን ሆይ! እነርሱንም፣ ከአባቶቻቸውና ከሚስቶቻቸውም፣ ከዝርዮቻቸውም፣ የበጀውን ሁሉ እነዚያን ቃል የገባህላቸውን የመኖሪያ ገነቶች አግባቸው፡፡ አንተ አሸናፊው ጥበበኛው አንተ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(40,9,'40:9','«ከቅጣቶችም ጠብቃቸው፡፡ በዚያ ቀንም የምትጠብቃውን ሰው በእርግጥ አዘንክለት፡፡» ይህም እርሱ ታላቅ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,10,'40:10','እነዚያ የካዱት ሰዎች «ወደ እምነት በምትጠሩና በምትክዱ ጊዜ አላህ (እናንተን) መጥላቱ ነፍሶቻችሁን (ዛሬ) ከመጥላታችሁ የበለጠ ነው» በማለት ይጥጠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,11,'40:11','«ጌታችን ሆይ! ሁለትን ሞት አሞትከን፡፡ ሁለትንም ሕይወት ሕያው አደረግከን፡፡ በኃጢኣቶቻችንም መሰከርን፡፡ ታዲያ (ከእሳት) ወደ መውጣት መንገድ አለን?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,12,'40:12','«ይህ እነሆ አላህ ብቻውን በተጠራ ጊዜ ስለ ካዳችሁ በእርሱም ተጋሪ ቢደረግ የምታምኑ ስለ ኾናችሁ ነው፡፡ ስለዚህ ፍርዱ ከፍተኛ ታላቅ ለኾነው አላህ ብቻ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(40,13,'40:13','እርሱ ያ (ለአምላክነቱ) ምልክቶቹን የሚያሳያችሁ ለእናንተም ከሰማይ ሲሳይን የሚያወርድላችሁ ነው፡፡ ወደእርሱም የሚመለስ ሰው ቢኾን እንጂ ሌላው አይገሰጽም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,14,'40:14','አላህንም ከሓዲዎች ቢጠሉም ሃይማኖትን ለእርሱ አጥሪዎች ኾናችሁ ተገዙት፡፡');
INSERT INTO "sadiq_sani" VALUES(40,15,'40:15','(እርሱ) ደረጃዎችን ከፍ አድራጊ የዐርሹ ባለቤት ነው፡፡ የመገናኛውን ቀን ያስፈራራ ዘንድ ከባሮቹ በሚሻው ሰው ላይ ከትዕዛዙ መንፈስን (ራእይን) ያወርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,16,'40:16','እነርሱ (ከመቃብር) በሚወጡበት ቀን በአላህ ላይ ከእነርሱ ምንም ነገር አይደበቅም፡፡ «ንግሥናው ዛሬ ለማን ነው?» (ይባላል፤)፡፡ «ለአሸናፊው ለአንዱ አላህ ብቻ ነው» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(40,17,'40:17','«ዛሬ ነፍስ ሁሉ በሠራችው ሥራ ትምመነዳለች፡፡ ዛሬ በደል የለም፡፡ አላህ በእርግጥ ምርመራው ፈጣን ነው» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(40,18,'40:18','ቅርቢቱንም (የትንሣኤን) ቀን ልቦች ጭንቀትን የተመሉ ኾነው ላንቃዎች ዘንድ የሚደርሱበትን ጊዜ አስጠንቅቃቸው፡፡ ለበዳዮች ምንም ወዳጅና ተሰሚ አማላጅ የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,19,'40:19','(አላህ) የዓይኖችን ክዳት ልቦችም የሚደብቁትን ሁሉ ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,20,'40:20','አላህም በእውነት ይፈርዳል፡፡ እነዚያም ከእርሱ ሌላ የሚግገዟቸው በምንም አይፈርዱም፡፡ አላህ እርሱ ሰሚው ተመልካቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,21,'40:21','የእነዚያን ከእነርሱ በፊት የነበሩትን ሕዝቦች ፍጻሜ እንዴት እንደነበረ ይመለከቱ ዘንድ በምድር ላይ አይኼዱምን? በኀይልና በምድር ላይ በተዋቸው ምልክቶች፤ ከእነርሱ ይበልጥ የበረቱ ነበሩ፡፡ አላህም በኀጢአቶቻቸው ያዛቸው፡፡ ለእነርሱም ከአላህ (ቅጣት) ምንም ጠባቂ አልነበራቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,22,'40:22','ይህ (መያዝ) እነርሱ መልክተኞቻቸው በተዓምራቶች ይመጡባቸው ስለነበሩና ስለ ካዱ ነው፡፡ አላህም ያዛቸው፡፡ እርሱ ኀያል ቅጣተ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(40,23,'40:23','ሙሳንም በተዓምራቶቻችንና በግልጽ ማስረጃ በእርግጥ ላክነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,24,'40:24','ወደ ፈርዖንና ወደ ሃማን፤ ወደ ቃሩንም (ላክነው)፡፡ «ድግምተኛ ውሸታም ነው» አሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,25,'40:25','ከእኛ ዘንድ እውነትን ይዞ በመጣላቸውም ጊዜ «የእነዚያን ከእርሱ ጋር ያመኑትን ሰዎች ወንዶች ልጆች ግደሉ፤ ሴቶቻቸውንም አስቀሩ» አሉ፡፡ የከሓዲዎችም ተንኮል በጥፋት ውስጥ እንጅ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,26,'40:26','ፈርዖንም «ተዉኝ፤ ሙሳን ልግደል፡፡ ጌታውንም ይጥራ፡፡ ያድነው እንደኾነ፡፡ እኔ ሃይማኖታችሁን ሊለውጥ፤ ወይም በምድር ውስጥ ጥፋትን ሊያሰራጭ እፈራለሁና» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,27,'40:27','ሙሳም «እኔ በምርመራው ቀን ከማያምን ትዕቢተኛ ሁሉ በጌታዬና በጌታችሁ ተጠበቅሁ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,28,'40:28','ከፈርዖንም ቤተሰቦች አንድ እምነቱን የሚደብቅ ምእመን ሰው አለ፡- «ሰውየውን ከጌታችሁ በተዓምራቶች በእርግጥ የመጣለችሁ ሲኾን ‹ጌታዬ አላህ ነው› ስለሚል ትገድላላችሁን? ውሸታምም ቢኾን ውሸቱ በርሱው ላይ ነው፡፡ እውነተኛ ቢኾን ግን የዚያ የሚያሰፈራራችሁ ከፊሉ ያገኛችኋል፡፡ አላህ ያንን እርሱ ድንበር አላፊ ውሸታም የኾነውን አይመራምና፡፡');
INSERT INTO "sadiq_sani" VALUES(40,29,'40:29','«ወገኖቼ ሆይ! በምድር ላይ አሸናፊዎች ስትኾኑ ዛሬ መንግስቱ የእናንተ ነው፡፡ ታዲያ ከአላህ ቅጣት ቢመጣብን የሚያድነን ማን ነው?» (አለ)፡፡ ፈርዖን «የማየውን ነገር እንጅ፤ አላመለክታችሁም፡፡ ቅኑንም መንገድ እንጅ አልመራችሁም» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,30,'40:30','ያም ያመነው ሰው አለ «እኔ በናንተ ላይ የአሕዛቦቹን ቀን ብጤ እፈራላችኋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,31,'40:31','«የኑሕን ሕዝቦች፣ የዓድንና የሰሙድንም፣ የእነዚያንም ከኋላቸው የነበሩትን ልማድ ብጤ (እፈራላችኋለሁ)፡፡ አላህም ለባሮቹ መበደልን የሚሻ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,32,'40:32','«ወገኖቼ ሆይ! እኔ በእናንተ ላይ የመጠራሪያን ቀን እፈራለሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(40,33,'40:33','ወደ ኋላ ዞራችሁ በምትሸሹበት ቀን፤ ለናንተ ከአላህ (ቅጣት) ምንም ጠባቂ የላችሁም፡፡ አላህም የሚያጠመው ሰው ለርሱ ምንም አቅኚ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,34,'40:34','ዩሱፍም ከዚህ በፊት ግልጽ ማስረጃዎችን በእርግጥ አመጣላቸሁ፡፡ ከዚያም እርሱ ካመጣላችሁ ነገር ከመጠራጠር አልተወገዳችሁም፡፡ በጠፋም ጊዜ «አላህ ከእርሱ ኋላ መልክተኛን በጭራሽ አይልክም» አላችሁ፡፡ አላህ እንደዚሁ ድንበር አላፊ ተጠራጣሪ የኾነን ሰው ያሳስታል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,35,'40:35','እነዚያ፤ የመጣላቸው አስረጅ ሳይኖር በአላህ ተዓምራቶች የሚከራከሩ (ክርክራቸው) አላህ ዘንድና እነዚያም አመኑት ዘንድ መጠላቱ በጣም ተለቀ፡፡ እንደዚሁ አላህ በኩሩ ጨካኝ (ሰው) ልብ ሁሉ ላይ ያትማል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,36,'40:36','ፈርዖንም አለ «ሃማን ሆይ! መንገዶችን እደርስ ዘንድ ረዢም ሕንጻን ለእኔ ካብልኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,37,'40:37','«የሰማያትን መንገዶች (እደርስ ዘንድ)፣ ወደ ሙሳም አምላክ እመለከት ዘንድ፡፡ እኔም ውሸታም ነው ብዬ በእርግጥ እጠረጥረዋለሁ» (አለ)፡፡ እንደዚሁም ለፈርዖን መጥፎ ሥራው ተሸለመለት፡፡ ከቅን መንገድም ታገደ፡፡ የፈርዖንም ተንኮል በከሳራ ውስጥ እንጅ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,38,'40:38','ያም ያመነው አለ «ወገኖቼ ሆይ! ተከተሉኝ፡፡ ቀጥታውን መንገድ እመራችኋለሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(40,39,'40:39','«ወገኖቼ ሆይ! ይህቺ ቅርቢቱ ሕይወት (ጥቂት) መጣቀሚያ ብቻ ናት፡፡ መጨረሻይቱም ዓለም እርሷ መርጊያ አገር ናት፡፡»');
INSERT INTO "sadiq_sani" VALUES(40,40,'40:40','«መጥፎን የሠራ ሰው ብጤዋን እንጅ አይምመነዳም፡፡ እርሱ ምእመን ኾኖ ከወንድ ወይም ከሴት በጎን የሠራም ሰው እነዚያ ገነትን ይገባሉ፡፡ በእርሷ ውስጥ ያለ ቁጥጥር ይመገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,41,'40:41','«ወገኖቼም ሆይ! ወደ መዳን የምጠራችሁ ስኾን ወደ እሳትም የምትጠሩኝ ስትኾኑ ለኔ ምን አለኝ!');
INSERT INTO "sadiq_sani" VALUES(40,42,'40:42','«በአላህ ልክድና በእርሱም ለእኔ ዕውቀት የሌለኝን ነገር በእርሱ እንዳጋራ ትጠሩኛላችሁ፡፡ እኔም ወደ አሸናፊው፤ መሓሪው (አላህ) እጠራችኋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,43,'40:43','«በእርግጥ ወደእርሱ የምትጠሩብኝ (ጣዖት) ለእርሱ በቅርቢቱም ኾነ በመጨረሻይቱ ዓለም (ተሰሚ) ጥሪ የለውም፡፡ መመለሻችንም በእርግጥ ወደ አላህ ነው፡፡ ወሰን አላፊዎቹም እነሱ የእሳት ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,44,'40:44','«ወደ ፊትም ለእናንተ የምላችሁን ምክር (ቅጣትን በምታዩ ጊዜ) ታስታውሳላችሁ፡፡ ነገሬንም ሁሉ ወደ አላህ አስጠጋለሁ፡፡ አላህ ባሮቹን ተመልካች ነውና፡፡»');
INSERT INTO "sadiq_sani" VALUES(40,45,'40:45','ከመከሩዋቸውም መጥፎዎች አላህ ጠበቀው፡፡ በፈርዖን ቤተሰቦችም ላይ ክፉ ቅጣት ሰፈረባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,46,'40:46','እሳት በጧትና በማታ በእርሷ ላይ ይቀርባሉ፡፡ ሰዓቲቱም በምትኾንበት ቀን «የፈርዖንን ቤተሰቦች ብርቱን ቅጣት (ገሀነምን) አግቡዋቸው» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(40,47,'40:47','በእሳትም ውስጥ የሚከራከሩበትን ጊዜ (አስታውስ)፡፡ ደካማዎቹም ለእነዚያ ለኮሩት «እኛ ለእናንተ ተከታዮች ነበርንና እናንተ (አሁን) ከእሳት ከፊሉን ከእኛ ላይ ገፍታሪዎች ናችሁን?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,48,'40:48','እነዚያም የኮሩት «እኛ ሁላችንም በውስጧ ነን፡፡ አላህ በባሮቹ መካከል በእርግጥ ፈረደ» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,49,'40:49','እነዚያም በእሳት ውሰጥ ያሉት ለገሀነም ዘበኞች «ጌታችሁን ለምኑልን፤ ከእኛ ላይ ከቅጣቱ አንድን ቀን ያቃልልን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,50,'40:50','(ዘበኞቹም) «መልእክተኞቻችሁ በተዓምራቶች ይመጡላችሁ አልነበሩምን?» ይላሉ፡፡ (ከሓዲዎቹም) «እንዴታ መጥተውናል እንጅ፤ (ግን አስተባበልናቸው)» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,51,'40:51','እኛ መልክተኞቻችንን፣ እነዚያንም ያመኑትን በቅርቢቱ ሕይወት ምስክሮችም በሚቆሙበት ቀን በእርግጥ እንረዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(40,52,'40:52','በደለኞችን ምክንያታቸው በማይጠቅማቸው ቀን (እንረዳለን)፡፡ ለእነርሱም ርግማን አልላቸው፡፡ ለእነርሱም መጥፎ አገር አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,53,'40:53','ሙሳንም መምሪያን በእርግጥ ሰጠነው፡፡ የእስራኤልንም ልጆች መጽሐፉን አወረስናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,54,'40:54','ለባለ አእምሮዎች መሪና ገሳጭ ሲኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(40,55,'40:55','(ሙሐመድ ሆይ!) ታገስም የአላህ ተስፋ እውነት ነውና፡፡ ለስህተትህም ምሕረትን ለምን፡፡ ከቀትር በኋላም በማለዳም ጌታሀን ከማመስገን ጋር አጥራው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,56,'40:56','እነዚያ የመጣላቸው አስረጅ ሳይኖር በአላህ አንቀጾች የሚከራከሩ በልቦቻቸው ውስጥ እነርሱ ደራሾቹ ያልኾኑት ኩራት ብቻ እንጅ ምንም የለም፡፡ በአላህም ተጠበቅ፡፡ እነሆ እርሱ ሰሚው ተመልካቹ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(40,57,'40:57','ሰማያትንና ምድርን መፍጠር ሰዎችን ከመፍጠር ይልቅ ታላቅ ነው፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,58,'40:58','ዕውርና የሚያይ እነዚያም አምነው መልካሞችን የሠሩና መጥፎ ሠሪው አይስተካከሉም፡፡ በጣም ጥቂትን ብቻ ትገሰጻላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,59,'40:59','ሰዓቲቱ በእርግጥ መጪ ናት፡፡ በእርሷ ጥርጥር የለም፡፡ ግን አብዛኞቹ ሰዎች አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,60,'40:60','ጌታችሁም አለ «ለምኑኝ፤ እቀበላችኋለሁና፡፡ እነዚያ እኔን ከመግገዛት የሚኮሩት ተዋራጆች ኾነው ገሀነምን በእርግጥ ይገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,61,'40:61','አላህ ያ ለእናንተ ሌሊትን በውስጡ ልታርፉበት (ጨለማ) ቀንንም (ልትሠሩበት) የሚያሳይ ያደረገላችሁ ነው፡፡ አላህ በሰዎች ላይ በእርግጥ የችሮታ ባለቤት ነውና፡፡ ግን አብዛኛዎቹ ሰዎች አያመሰግኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,62,'40:62','ይሃችሁ ጌታችሁ አላህ ነው፤ የነገሩ ሁሉ ፈጣሪ ነው፤ ከርሱ ሌላ አምላክ የለም፤ ታዲያ (ከእምነት) ወዴት ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,63,'40:63','እንደዚሁ እነዚያ በአላህ አንቀጾች ይክዱ የነበሩት (ከእምነት) ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,64,'40:64','አላህ ያ ምድርን መርጊያ ሰማይንም ጣሪያ ያደረገላችሁ ነው፡፡ የቀረጻችኁም ቅርጻችሁንም ያሳመረ ከጣፋጮችም ሲሳዮች የሰጣችሁ ነው፡፡ ይሃችሁ ጌታችሁ አላህ ነው፡፡ የዓለማትም ጌታ አላህ ላቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,65,'40:65','እርሱ ብቻ (ሁል ጊዜ) ሕያው ነው፤ ከርሱ ሌላ አምላክ የለም፤ ሃይማኖትንም ለእርሱ ያጠራችሁ ስትኾኑ «ምስጋና ለዓለማት ጌታ ለአላህ ይኹን» የምትሉ ኾናችሁ ተገዙት፡፡');
INSERT INTO "sadiq_sani" VALUES(40,66,'40:66','«እኔ ከጌታዬ አስረጂዎች በመጡልኝ ጊዜ እነዚያን ከአላህ ሌላ የምትገዙዋቸውን እንዳልገዛ ተከልክያለሁ፡፡ ለዓለማት ጌታም እንድገዛ ታዝዣለሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,67,'40:67','እርሱ ያ ከዐፈር ከዚያም ከፍትወት ጠብታ ከዚያም ከረጋ ደም የፈጠራችሁ ነው፡፡ ከዚያም ሕጻናት አድርጎ ያወጣችኋል፡፡ ከዚያም ጥንካሬያችሁን ትደርሱ ዘንድ፤ ከዚያም ሽማግሌዎች ትኾኑ ዘንድ (ያቆያችኋል)፡፡ ከእናንተም ውስጥ ከዚህ በፊት የሚሞት አልለ፡፡ (ይህንንም ያደረገው ልትኖሩና) የተወሰነ ጊዜንም ልትደርሱ ታውቁም ዘንድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,68,'40:68','እርሱ ያ ሕያው የሚያደርግ የሚገድልም ነው፡፡ አንዳችን ነገር በሻም ጊዜ ለእርሱ የሚለውን «ኹን» ነው፤ ወዲያውም ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,69,'40:69','ወደእነዚያ በአላህ አንቀጾች ወደሚከራከሩት (ከእምነት) እንዴት እንደሚመለሱ አታይምን?');
INSERT INTO "sadiq_sani" VALUES(40,70,'40:70','ወደእነዚያ በመጽሐፉ በዚያም መልክተኞቻችንን በእርሱ በላክንበት ወዳስተባበሉት (አታይምን?) ወደፊትም ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,71,'40:71','እንዛዝላዎቹና ሰንሰለቶቹ በአንገቶቻቸው ላይ በተደረጉ ጊዜ (በእርሷ) ይጎተታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,72,'40:72','በገሀነም ውስጥ (ይጎተታሉ)፤ ከዚያም በእሳት ውስጥ ይማገዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,73,'40:73','ከዚያም ለእነርሱ ይባላሉ (በአላህ) «ታጋሯቸው የነበራችሁት የታሉ?»');
INSERT INTO "sadiq_sani" VALUES(40,74,'40:74','«ከአላህ ሌላ (የምታጋሩዋቸው)፤ ከኛ ተሰወሩን?» «ከቶ ከዚህ በፊት ምንንም የምንገዛ አልነበርንም» ይላሉ፤ እንደዚሁ አላህ ከሓዲዎችን ያሳስታል፡፡');
INSERT INTO "sadiq_sani" VALUES(40,75,'40:75','ይህ (ቅጣት) ያላግባብ በምድር ውሰጥ ትደሰቱ በነበራችሁትና ትንበጣረሩ በነበራችሁት ነው (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(40,76,'40:76','የገሀነምን በሮች በውስጧ ዘውታሪዎች ስትኾኑ ግቡ (ይባላሉ)፡፡ የትዕቢተኞችም መኖሪያ (ገሀነም) ምን ትከፋ!');
INSERT INTO "sadiq_sani" VALUES(40,77,'40:77','ታገስም የአላህ ቀጠሮ እውነት ነውና፡፡ የዚያንም የምናስፈራራቸውን ከፊሉን (በሕይወትህ) ብናሳይህ (መልካም ነው)፡፡ ወይም ብንገድልህ ወደኛ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,78,'40:78','ካንተ በፊትም መልክተኞችን በእርግጥ ልከናል፡፡ ከእነርሱ ባንተ ላይ የተረክንልህ አልለ፡፡ ከእነርሱም ባንተ ላይ ያልተረክነው አልለ፡፡ ለማንኛውም መልክተኛ በአላህ ፈቃድ ካልኾነ ተዓምርን ሊያመጣ አይገባውም፡፡ የአላህም ትእዛዝ በመጣ ጊዜ በእውነት ይፈረዳል፡፡ እዚያ ዘንድም አጥፊዎቹ ይከስራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,79,'40:79','አላህ ያ ለእናንተ ግመልን ከብትንና ፍየልን ከእርሷ (ከፊሏን) ልትጋልቡ የፈጠረላችሁ ነው፡፡ ከእርሷም ትበላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,80,'40:80','ለእናንተም በእርሷ ውስጥ ጥቅሞች አልሏችሁ፡፡ በእርሷም ላይ (እቃን በመጫን) በልቦቻችሁ ያሰባችሁትን ጉዳይ ትደርሱ ዘንድ (ፈጠረላችሁ)፡፡ በእርሷም ላይ በመርከቦችም ላይ ትሳፈራላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,81,'40:81','ተዓምራቱንም ያሳያችኋል፡፡ ታዲያ ከአላህ ተዓምራት የትኛውን ትክዳላችሁ?');
INSERT INTO "sadiq_sani" VALUES(40,82,'40:82','የእነዚያን ከእነርሱ በፊት የነበሩትን ሕዝቦች ፍጻሜ እንዴት እንደነበረ ይመለከቱ ዘንድ በምድር ላይ አይኼዱምን? ከእነርሱ ይበልጥ ብዙዎች በኀይልና በምድር ላይ በተዋቸው ምልክቶችም በጣም ብርቱዎች ነበሩ፡፡ ያም ይሠሩት የነበሩት ከእነርሱ ምንም አልጠቀማቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(40,83,'40:83','መልክተኞቻቸውም ተዓምራቶችን ባመጡላቸው ጊዜ እነርሱ ዘንድ ባለው ዕውቀት ተደሰቱ፡፡ በእርሱ ይሳለቁበት የነበሩት ቅጣትም በእነርሱ ላይ ሰፈረባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(40,84,'40:84','ብርቱ ቅጣታችንንም ባዩ ጊዜ «በአላህ አንድ ሲኾን አምነናል፤ በእርሱም እናጋራ በነበርነው (ጣዖታት) ክደናል» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(40,85,'40:85','ብርቱ ቅጣታችንን ባዩ ጊዜ ማመናቸውም የሚጠቅማቸው አልነበረም፡፡ የአላህን ደንብ ያችን በባሮቹ ውስጥ ያለፈችውን (ተጠንቀቁ) በዚያ ጊዜም ከሓዲዎች ከሰሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,1,'41:1','ሐ.መ.(ሓ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,2,'41:2','(ይህ ቁርኣን) እጅግ በጣም ሩኅሩህ በጣም አዛኝ ከሆነው (አላህ) የተወረደ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,3,'41:3','አንቀጾቹ የተብራሩ የኾነ መጽሐፍ ነው፡፡ ዐረብኛ ቁርኣን ሲኾን ለሚያውቁ ሕዝቦች (የተብራራ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,4,'41:4','አብሳሪና አስፈራሪ ሲኾን (ተወረደ)፡፡ አብዛኛዎቻቸውም ተዉት፡፡ እነርሱም አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,5,'41:5','አሉም «ልቦቻችን ከእዚያ ወደእርሱ ከምትጠራን እምነት በመሸፈኛዎች ውስጥ ናቸው፡፡ በጆሮዎቻችንም ላይ ድንቁርና አልለ፤ በእኛና ባንተም መካከል ግርዶሽ አልለ፡፡ (በሃይማኖትህ) ሥስራም፤ እኛ ሠሪዎች ነንና፡፡»');
INSERT INTO "sadiq_sani" VALUES(41,6,'41:6','(እንዲህ) በላቸው «እኔ መሰላችሁ ሰው ብቻ ነኝ፡፡ አምላካችሁ አንድ አምላክ ብቻ ነው ወደእርሱም ቀጥ በሉ ምሕረትንም ለምኑት ማለት ወደእኔ ይወረድልኛል፡፡ ለአጋሪዎቹም ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,7,'41:7','ለእነዚያ ዘካን ለማይሰጡት እነርሱም በመጨረሻይቱ ዓለም እነርሱ ከሓዲዎች ለኾኑት (ወዮላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,8,'41:8','እነዚያ ያመኑትና በጎ ሥራዎችን የሠሩት ለእነርሱ የማይቆረጥ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,9,'41:9','በላቸው «እናንተ በዚያ ምድርን በሁለት ቀኖች ውስጥ በፈጠረው አምላክ በእርግጥ ትክዳላችሁን? ለእርሱም ባለንጣዎችን ታደርጋላችሁን? ያ (ይህንን የሠራው) የዓለማት ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,10,'41:10','በእርሷም ከበላይዋ የረጉ ጋራዎችን አደረገ፡፡ በእርሷም በረከትን አደረገ፡፡ በውስጧም ምግቦችዋን (ካለፉት ሁለት ቀኖች ጋር) በአራት ቀናት ውሰጥ ለጠያቂዎች ትክክል ሲኾኑ ወሰነ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,11,'41:11','ከዚያም ወደ ሰማይ እርሷ ጭስ ኾና ሳለች አሰበ፡፡ ለእርሷም ለምድርም «ወዳችሁም ኾነ ወይም ጠልታችሁ ኑ» አላቸው፡፡ «ታዛዦች ኾነን መጣን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,12,'41:12','በሁለት ቀኖችም ውስጥ ሰባት ሰማያት አደረጋቸው፡፡ በሰማይቱም ሁሉ ነገሯን አዘጋጀ፡፡ ቅርቢቱንም ሰማይ በመብራቶች አጌጥን፡፡ (ከሰይጣናት) መጠበቅንም ጠበቅናት፡፡ ይህ የአሸናፊው የዐዋቂው (ጌታ) ውሳኔ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,13,'41:13','(ከእምነት) እንቢ «ቢሉም እንደ ዓድና ሰሙድ መቅሰፍት ብጤ የኾነን መቅሰፍት አስጠነቅቃችኋለሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,14,'41:14','መልክተኞቹ አላህን እንጅ ሌላን አትግገዙ በማለት ከስተፊታቸውም ከስተኋለቸውም በመጡባቸው ጊዜ «ጌታችን በሻ ኖሮ መላእክትን ባወረደ ነበር፡፡ ስለዚህ እኛ በዚያ በእርሱ በተላካችሁበት ከሓዲዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,15,'41:15','ዓድም በምድር ላይ ያለ አግባብ ኮሩ፡፡ «ከእኛ ይበልጥ በኀይል ብርቱ ማን ነው?» አሉም፡፡ ያ የፈጠራቸው አላህ እርሱ በኀይል ከእነርሱ ይበልጥ የበረታ መኾኑን አያዩምን? በተዓምራታችንም ይክዱ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,16,'41:16','በቅርቢቱ ሕይወት የውርደትን ቅጣት ልናቀምሳቸውም በእነርሱ ላይ የሚንሻሻን ብርቱ ነፋስ መናጢዎች በኾኑ ቀናት ውስጥ ላክንባቸው፡፡ የመጨረሻይቱም (ዓለም) ስቃይ በጣም አዋራጅ ነው፡፡ እነርሱም አይረዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,17,'41:17','ሰሙዶችማ መራናቸው ዕውርነትንም በቅንነት ላይ መረጡ፡፡ ይሠሩትም በነበሩት ኃጢኣት አሳናሽ የኾነችው የመብረቅ ቅጣት ያዘቻቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,18,'41:18','እነዚያንም ያመኑትንና ይጠነቀቁ የነበሩትን አዳንናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,19,'41:19','የአላህም ጠላቶች ወደ እሳት የሚሰበሰቡበትን ቀን (አስታውስ)፡፡ እነርሱም የሚከመከሙ ኾነው ይነዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,20,'41:20','በመጧትም ጊዜ ጆሮዎቻቸው፣ ዓይኖቻቸውና ቆዳዎቻቸውም ይሠሩት በነበሩት ነገር በእነርሱ ላይ ይመሰክሩባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(41,21,'41:21','ለቆዳዎቻቸውም «በእኛ ላይ ለምን መሰከራችሁብን?» ይላሉ፡፡ «ያ አንዳቹን ነገር ሁሉ ያናገረው አላህ አናገረን፡፡ እርሱም በመጀመሪያ ጊዜ ፈጠራችሁ፡፡ ወደእርሱም ትመለሳላችሁ» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(41,22,'41:22','ጆሮዎቻችሁ ዓይኖቻችሁና ቆዳዎቻችሁም በእናንተ ላይ ከመመስከራቸው የምትደበቁ አልነበራችሁም፡፡ ግን አላህ ከምትሠሩት ብዙውን አያውቅም ብላችሁ ጠረጠራችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,23,'41:23','ይህም ያ በጌታችሁ የጠረጠራችሁት ጥርጣሪያችሁ አጠፋችሁ፡፡ ከከሳሪዎቹም ኾናችሁ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,24,'41:24','ቢታገሱም እሳት ለእነርሱ መኖሪያቸው ናት፡፡ ወደሚወዱት መመለስንም ቢጠይቁ እነርሱ ተቀባይ የሚያገኙ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,25,'41:25','ለእነርሱም ቁራኛዎችን አዘጋጀንላቸው፡፡ በፊታቸው ያለውንና በኋላቸው ያለውንም ሁሉ ለእነርሱ ሸለሙላቸው፡፡ ከጋኔንና ከሰውም ከእነርሱ በፊት በእርግጥ ካለፉት ሕዝቦች ጋር ኾነው ቃሉ በእነርሱ ላይ ተረጋገጠባቸው፡፡ እነርሱ በእርግጥ ከሳሪዎች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(41,26,'41:26','እነዚያም የካዱት «ይህንን ቁርኣን አታዳምጡ፡፡ ታሸንፉም ዘንድ (ሲነበብ) በእርሱ ውሰጥ ተንጫጩ» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,27,'41:27','እነዚያንም የካዱትን ብርቱን ቅጣት እናቀምሳቸዋለን፡፡ የእዚያነም ይሠሩት የነበሩትን መጥፎውን (ፍዳ) በእርግጥ እንመነዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(41,28,'41:28','ይህ የአላህ ጠላቶች ፍዳ ነው፡፡ እሳት በውስጧ ለእነርሱ የዘላለም መኖሪያ አገር አልላቸው፡፡ ከአንቀጾቻችን ይክዱ በነበሩት ምንዳን (ይምመነዳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,29,'41:29','እነዚያም የካዱት (በእሳት ውስጥ ኾነው) «ጌታችን ሆይ! እነዚያን ከጋኔንና ከሰው ያሳሳቱንን አሳየን፡፡ ከታችኞቹ ይኾኑ ዘንድ ከጫማዎቻችን ሥር እናደርጋቸዋለንና» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,30,'41:30','እነዚያ «ጌታችን አላህ ነው» ያሉ ከዚያም ቀጥ ያሉ «አትፍሩ፤ አትዘኑም፤ በዚያችም ተስፋ ቃል ትደረግላችሁ በነበራችሁት ገነት ተበሰሩ» በማለት በነሱ ላይ መላእክት ይወርዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,31,'41:31','«እኛ በቅርቢቱም ሕይወት በመጨረሻይቱም ረዳቶቻችሁ ነን! ለእናንተም በእርሷ ውስጥ ነፍሶቻችሁ የሚሹት ሁሉ አልላችሁ፡፡ ለእናንተም በእርሷ ውስጥ የምትፈልጉት ሁሉ አልላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,32,'41:32','«መሓሪ አዛኝ ከኾነው አላህ መስተንግዶ ሲኾን» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,33,'41:33','ወደ አላህ ከጠራና መልካምንም ከሠራ፣ «እኔ ከሙስሊሞች ነኝ» ካለም ሰው ይበልጥ ቃሉ ያማረ ማን ነው?');
INSERT INTO "sadiq_sani" VALUES(41,34,'41:34','መልካሚቱና ክፉይቱም (ጸባይ) አይተካከሉም፡፡ በዚያች እርሷ መልካም በኾነችው ጸባይ (መጥፎይቱን) ገፍትር፡፡ ያን ጊዜ ያ ባንተና በእርሱ መካከል ጠብ ያለው ሰው እርሱ ልክ እንደ አዛኝ ዘመድ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(41,35,'41:35','(ይህችንም ጠባይ) እነዚያ የታገሱት እንጅ ሌላው አያገኛትም፡፡ የትልቅም ዕድል ባለቤት እንጅ ማንም አይገጠማትም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,36,'41:36','ከሰይጣንም ጉትጎታ ቢያገኝህ (ከመልካም ጠባይም ቢመልስህ) በአላህ ተጠበቅ፡፡ እነሆ እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(41,37,'41:37','ሌሊትና ቀንም ፀሐይና ጨረቃም ከምልክቶቹ ናቸው፡፡ ለፀሐይና ለጨረቃ አትስገዱ፡፡ ለእዚያም ለፈጠራቸው አላህ ስገዱ፡፡ እርሱን ብቻ የምትግገዙ እንደ ኾናችሁ (ለሌላ አትስገዱ)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,38,'41:38','ቢኮሩም እነዚያ በጌታህ ዘንድ ያሉት (መላእክት) በቀንም በሌሊትም ለእርሱ ያወድሳሉ፡፡ እነርሱም አይሰለቹም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,39,'41:39','አንተ ምድርን ደረቅ ኾና ማየትህም ከምልክቶቹ ነው፡፡ በእርሷም ላይ ውሃን ባወረድን ጊዜ (በቡቃያዎችዋ) ትላወሳለች፡፡ ትነፋለችም፡፡ ያ ሕያው ያደረጋት ጌታ በእርግጥ ሙታንን ሕያው አድራጊ ነው፡፡ እነርሱ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(41,40,'41:40','እነዚያ አንቀጾቻችንን የሚያጣምሙ በእኛ ላይ አይደበቁም፡፡ (የሚያጣምምና) በእሳት ውስጥ የሚጣለው ሰው ይበልጣልን? ወይስ በትንሣኤ ቀን ጸጥተኛ ኾኖ የሚመጣው ሰው? የሻችሁትን ሥሩ (ዋጋችሁን ታገኛላችሁ)፡፡ እርሱ የምትሠሩትን ሁሉ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(41,41,'41:41','እነዚያ በቁርኣን እርሱ አሸናፊ መጽሐፍ ሲኾን በመጣላቸው ጊዜ የካዱት (ጠፊዎች ናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(41,42,'41:42','ከኋላውም ከፊቱም ውሸት አይመጣበትም፡፡ ጥበበኛ ምስጉን ከኾነው ጌታ የተወረደ ው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,43,'41:43','ካንተ በፊት ለነበሩት መልክተኞች የተባለው ብጤ እንጅ ለአንተ ሌላ አይባልም፡፡ ጌታህ የምህረት ባለቤትና የአሳማሚ ቅጣት ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,44,'41:44','በአጀምኛ የኾነ ቁርኣን ባደረግነው ኖሮ አንቀጾቹ «(በምናውቀው ቋንቋ) አይብራሩም ኖሯልን? (ቁርኣኑ) አጀምኛና (መልክተኛው) ዐረባዊ ይኾናልን?» ባሉ ነበር እርሱ ለእነዚያ ለአመኑት መምሪያና መፈወሻ ነው፡፡ እነዚያም የማያምኑት በጆሮዎቻቸው ውስጥ ድንቁርና ነው፡፡ (አይሰሙትም)፡፡ እነርሱም በእነርሱ ላይ እውርነት ነው፡፡ እነዚያ ከሩቅ ስፍራ እንደሚጥጠሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,45,'41:45','ሙሳንም መጽሐፉን በእርግጥ ሰጠነው፡፡ በእርሱም ተለያዩበት፡፡ ከጌታህም ያለፈች ቃል ባልነበረች ኖሮ በመካከላቸው በተፈረደ ነበር፡፡ እነርሱም ከእርሱ አወላዋይ በኾነ ጥርጣሬ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(41,46,'41:46','መልካም የሠራ ሰው ለነፍሱ ነው፡፡ ያጠፋም ሰው በእርሷ ላይ ነው፡፡ ጌታህም ለባሮቹ በዳይ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(41,47,'41:47','ጊዜይቱን ማወቅ ወደእርሱ ይመለሳል ከፍሬዎችም ከሽፋኖቻቸው አይወጡም፡፡ ከሴትም አንዲትም አታረግዝም፣ አትወልድምም በዕውቀቱ ቢኾን እንጅ፡፡ «ተጋሪዎቼ የት ናቸው?» በማለት በሚጠራቸው ቀንም «ከእኛ ውስጥ (ባላንጣ አልለ ብሎ) ምንም መስካሪ አለመኖሩን አስታወቅንህ» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,48,'41:48','ያም በፊት ይግገዙት የነበሩት (ጣዖት) ከእነርሱ ይጠፋቸዋል፡፡ ለእነርሱም ምንም መሸሻ የሌላቸው መኾኑን ያረጋግጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(41,49,'41:49','ሰው በጎ ነገርን ከመለመን አይሰለችም፡፡ ክፉም ቢያገኘው ወዲያውኑ በጣም ተስፋ ቆራጭ ተበሳጭ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(41,50,'41:50','ካገኘችውም መከራ በኋላ ከእኛ የኾነን እዝነት ብናቀምሰው «ይህ ለእኔ (በሥራዬ የተገባኝ)');
INSERT INTO "sadiq_sani" VALUES(41,51,'41:51','በሰውም ላይ ጸጋችንን በለገስን ጊዜ (ከማመስገን) ይዞራል፡፡ በጎኑም ይርቃል፡፡ መከራም ባገኘው ጊዜ የብዙ ልመና ባለቤት ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(41,52,'41:52','በላቸው «ንገሩኝ (ቁርኣኑ) ከአላህ ዘንድ ቢኾን ከዚያም በእርሱ ብትክዱ (ከውነት) በራቀ ጭቅጭቅ ላይ ከኾነ ሰው ይበልጥ የተሳሳተ ማን ነው?»');
INSERT INTO "sadiq_sani" VALUES(41,53,'41:53','እርሱም (ቁርኣን) እውነት መኾኑ ለእነርሱ እስከሚገለፅላቸው ድረስ በአጽናፎቹ ውስጥና በነፍሶቻቸውም ውስጥ ያሉትን ተዓምራቶቻችንን በእርግጥ እናሳያቸዋለን፡፡ ጌታህም እርሱ በነገሩ ሁሉ ላይ ዐዋቂ መኾኑ አይበቃቸውምን?');
INSERT INTO "sadiq_sani" VALUES(41,54,'41:54','ንቁ! እነርሱ ከጌታቸው መገናኘት በመጠራጠር ውስጥ ናቸው፡፡ ንቁ! እርሱ በነገሩ ሁሉ (ዕውቀቱ) ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,1,'42:1','ሐ.መ (ሐ፡ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(42,2,'42:2','ዐ.ሰ.ቀ (ዓይን ሲን ቃፍ)፤');
INSERT INTO "sadiq_sani" VALUES(42,3,'42:3','እንደዚሁ (በዚህች ሱራ ውስጥ እንዳለው) አሸናፊው ጥበበኛው አላህ ወዳንተ ያወርዳል፡፡ ወደእነዚያም ከአንተ በፊት ወደ ነበሩት ሕዝቦች (አውርዷል)፡፡');
INSERT INTO "sadiq_sani" VALUES(42,4,'42:4','በሰማያትና በምድር ያለው ሁሉ የእርሱ ነው፡፡ እርሱም የበላዩ ታላቁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,5,'42:5','(ከአላህ ፍራቻ) ሰማያት ከበላያቸው ሊቀደዱ ይቀርባሉ፡፡ መላእክትም ጌታቸውን እያመሰገኑ ያወድሳሉ፡፡ በምድርም ላለው ፍጡር ምሕረትን ይለምናሉ፡፡ ንቁ! አላህ እርሱ መሓሪው አዛኙ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,6,'42:6','እነዚያም ከእርሱ ሌላ (የጣዖታት) ረዳቶችን የያዙ፤ አላህ በእነርሱ ላይ ተጠባባቂ ነው፡፡ አንተም በእነርሱ ላይ ኀላፊ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,7,'42:7','እንደዚሁም የከተሞች እናት የኾነችውን (የመካን ሰዎች) በአካባቢዋ ያሉትንም ልታስፈራራ የመሰብሰቢያውንም ቀን በእርሱ ጥርጣሬ የሌለበት ሲኾን ልታስጠነቅቅ ዐረብኛ የኾነን ቁርኣን ወደ አንተ አወረድን፡፡ (ከእነርሱም) ከፊሉ በገነት ውሰጥ ከፊሉም በእሳት ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,8,'42:8','አላህም በሻ ኖሮ (ባንድ ሃይማኖት ላይ) አንድ ሕዝብ ባደረጋቸው ነበር፡፡ ግን የሚሻውን ሰው በእዝነቱ ውስጥ ያስገባል፡፡ በዳዮችም ለእነርሱ ወዳጅና ረዳት ምንም የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,9,'42:9','ከእርሱ ሌላ ረዳቶችን ያዙን? (ረዳቶች አይደሉም)፡፡ አላህም ረዳት እርሱ ብቻ ነው፡፡ እርሱም ሙታንን ሕያው ያደርጋል፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,10,'42:10','ከምንም ነገር ያ በእርሱ የተለያዩበት ፍርዱ ወደ አላህ ነው፡፡ «እርሱ አላህ ጌታዬ ነው፡፡ በእርሱ ላይ ተጠጋሁ፣ ወደርሱም እመለሳለሁ» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(42,11,'42:11','ሰማያትንና ምድርን ፈጣሪ ነው፡፡ ከነፍሶቻችሁ ሴቶችን ከቤት እንስሳዎችም ዓይነቶችን (ወንዶችና ሴቶችን) ለእናንተ አደረገላችሁ፡፡ በእርሱ ያበዛችኋል፡፡ የሚመስለው ምንም ነገር የለም፡፡ እርሱም ሰሚው ተመልካቹ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,12,'42:12','የሰማያትና የምድር (ድልቦች) መከፈቻዎች የእርሱ ናቸው፡፡ ሲሳይን ለሚሻው ሰው ያሰፋል፤ ያጠባልም፡፡ እርሱ ነገሩን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,13,'42:13','ለእናንተ ከሃይማኖት ያንን በእርሱ ኑሕን ያዘዘበትን ደነገገላችሁ፡፡ ያንንም ወዳንተ ያወረድነውን ያንንም በእርሱ ኢብራሂምን ሙሳንና ዒሳንም ያዘዝንበትን ሃይማኖትን በትክክል አቋቁሙ በእርሱም አትለያዩ ማለትን (ደነገግን)፡፡ በአጋሪዎቹ ላይ ያ ወደርሱ የምትጠራቸው ነገር ከበዳቸው፡፡ አላህ የሚሻውን ሰው ወደእርሱ (እምነት) ይመርጣል፡፡ የሚመለስንም ሰው ወደርሱ ይመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(42,14,'42:14','(የሃይማኖት ሰዎች) ዕውቀቱም ከመጣላቸው በኋላ በመካከላቸው ለምቀኝነት እንጅ ለሌላ አልተለያዩም፡፡ እስከ ተወሰነም ጊዜ (በማቆየት) ከጌታህ ያለፈች ቃል ባልነበረች ኖሮ በእነርሱ መካከል (አሁን) በተፈረደ ነበር፡፡ እነዚያም ከኋላቸው መጽሐፉን እንዲወርሱ የተደረጉት ከእርሱ በእርግጥ በአወላዋይ ጥርጣሬ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,15,'42:15','ለዚህም (ድንጋጌ ሰዎችን) ጥራ፡፡ እንደታዘዝከውም ቀጥ በል፡፡ ዝንባሌዎቻቸውንም አትከተል፡፡ «በላቸውም ከመጽሐፍ አላህ ባወረደው ሁሉ አመንኩ፡፡ በመካከላችሁም ላስተካክል ታዘዝኩ፡፡ አላህ ጌታችን ጌታችሁም ነው፡፡ ለእኛ ሥራዎቻችን አልሉን፡፡ ለእናንተም ሥራዎቻችሁ አልሏችሁ፡፡ በእኛና በእናንተ መካከል ክርክር የለም፡፡ አላህ በመካከላችን ይሰበስባል፡፡ መመለሻም ወደእርሱ ብቻ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(42,16,'42:16','እነዚያም በአላህ (ሃይማኖት) ለእርሱ ተቀባይ ካገኘ በኋላ የሚከራከሩት ማስረጃቸው በጌታቸው ዘንድ ብልሹ ናት፡፡ በእነርሱም ላይ ቁጣ አልለባቸው፡፡ ለእነርሱም ብርቱ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,17,'42:17','አላህ ያ መጽሐፉን በእውነት ያወረደ ነው፡፡ ሚዛንንም (እንደዚሁ)፡፡ ሰዓቲቱ ምንአልባት ቅርብ መኾንዋን ምን ያሳውቅሃል?');
INSERT INTO "sadiq_sani" VALUES(42,18,'42:18','እነዚያ በእርሷ የማያምኑት በእርሷ ያቻኩላሉ፡፡ እነዚያም ያመኑት ከእርሷ ፈሪዎች ናቸው፡፡ እርሷም እውነት መኾንዋን ያውቃሉ፡፡ ንቁ! እነዚያ በሰዓቲቱ የሚከራከሩት በእርግጥ (ከእውነት) በራቀ ስሕተት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,19,'42:19','አላህ በባሮቹ ሩህሩህ ነው፡፡ ለሚሻው ሰው (በሰፊው) ሲሳይን ይሰጣል፡፡ እርሱም ብርቱው አሸናፊው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,20,'42:20','(በሥራው) የኋለኛይቱን ዓለም አዝመራ የሚፈልግ የኾነ ሰው ለእርሱ በአዝመራው ላይ እንጨምርለታለን፡፡ የቅርቢቱንም ዓለም አዝመራ የሚፈልግ የኾነ ሰው ከእርሷ (የተወሰነለትን ብቻ) እንሰጠዋለን፡፡ ለእርሱም በኋለኛይቱ ዓለም ምንም ፋንታ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,21,'42:21','ከሃይማኖት አላህ በእርሱ ያልፈቀደውን ለእነርሱ የደነገጉ ተጋሪዎች ለእነርሱ አልሏቸውን? የፍርዱ ቃል ባልነበረ ኖሮ በመካከላቸው (አሁን) በተፈረደ ነበር፡፡ በደለኞችም ለእነርሱ አሳማሚ ቅጣት በእርግጥ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,22,'42:22','በደለኞችን ከሠሩት ሥራ (ዋጋ በትንሣኤ ቀን) ፈሪዎች ኾነው ታያቸዋለህ፡፡ እርሱም በእነርሱ ላይ ወዳቂ ነው፡፡ እነዚያም ያመኑት መልካሞችንም የሠሩት በገነቶች ጨፌዎች ውስጥ ናቸው፡፡ ለእነርሱ በጌታቸው ዘንድ የሚሹት ሁሉ አልላቸው፡፡ ይህ እርሱ ታላቅ ችሮታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,23,'42:23','ይህ (ታላቅ ችሮታ) ያ አላህ እነዚያን ያመኑትንና መልካሞችን የሠሩትን ባሮቹን የሚያበስርበት ነው፡፡ «በእርሱ (መልክቴን በማድረሴ) ላይ በዝምድና ውዴታን እንጅ ዋጋን አልጠይቃችሁም» በላቸው፡፡ መልካሚቱንም ሥራ የሚሠራ ሰው ለእርሱ በእርሷ መልካምን ነገር እንጨምርለታለን፡፡ አላህ መሓሪ አመስጋኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,24,'42:24','ይልቁንም «በአላህ ላይ ውሸትን ቀጠፈ» ይላሉን? አላህም ቢሻ በልብህ ላይ ያትማል፡፡ አላህም ውሸቱን ያብብሳል፡፡ እውነቱንም በቃላቱ ያረጋግጣል፡፡ እርሱ በልቦች ውስጥ ያለን ሁሉ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,25,'42:25','እርሱም ያ ከባሮቹ ንስሓን የሚቀበል፣ ከኃጢአቶችም ይቅር የሚል፣ የምትሠሩትንም ሁሉ የሚያውቅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,26,'42:26','እነዚያንም ያመኑትንና መልካሞችን የሠሩትን ልመናቸውን ይቀበላል፡፡ ከችሮታውም ይጨምርላቸዋል፡፡ ከሓዲዎቹም ለእነርሱ ብርቱ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,27,'42:27','አላህም ለባሮቹ (ሁሉ) ሲሳይን በዘረጋ ኖሮ በምድር ውስጥ (ሁሉም) ወሰን ባለፉ ነበር፡፡ ግን የሚሻውን በልክ ያወርዳል፡፡ እርሱ በባሮቹ ውስጥ ዐዋቂ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,28,'42:28','እርሱም ያ ተስፋ ከቆረጡ በኋላ ዝናምን የሚያወርድ ችሮታውንም የሚዘረጋ ነው፡፡ እርሱም ረዳቱ ምስጉኑ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,29,'42:29','ሰማያትንና ምድርንም መፍጠሩ፣ ከተንቀሳቃሽም በሁለቱ ውስጥ የበተነውን (መፍጠሩ) ከአስደናቂ ምልክቶቹ ነው፡፡ እርሱም በሚሻ ጊዜ እነርሱን በመሰብሰብ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,30,'42:30','ከመከራም ማንኛውም ያገኛችሁ ነገር እጆቻችሁ በሠሩት (ኃጢኣት) ምክንያት ነው፡፡ ከብዙውም ይቅር ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(42,31,'42:31','እናንተም በምድር ውስጥ የምታቅቱ አይደላችሁም፡፡ ለእናንተም ከአላህ ሌላ ከዘመድም ከረዳትም ምንም የላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,32,'42:32','በባሕር ላይ እንደ ጋራዎች ኾነው ተንሻላዮቹም (መርከቦች) ከአስደናቂ ምልክቶቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,33,'42:33','ቢሻ ነፋሱን የረጋ ያደርገዋል፡፡ በባሕሩም ጀርባ ላይ ረጊዎች ይኾናሉ፡፡ በዚህ ውሰጥ በጣም ታጋሽና አመስጋኝ ለኾነ ሁሉ በእርግጥ ምልክቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(42,34,'42:34','ወይም በሠሩት ኃጢኣት ምክንያት (በማስጠም) ያጠፋቸዋል፡፡ ከብዙም ይቅርታ ያደርጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(42,35,'42:35','(ከእነርሱ ለመበቀል)፤ እነዚያንም በአንቀጾቻችን የሚከራከሩትን ለማወቅ (ያጠፋቸዋል)፡፡ ለእነርሱ ምንም መሸሻ የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,36,'42:36','ከምንም ነገር የተሰጣችሁት የቅርቢቱ ሕይወት መጠቀሚያ ነው፡፡ አላህ ዘንድ ያለውም ምንዳ ለእነዚያ ለአመኑትና በጌታቸው ላይ ለሚጠጉት በላጭና ሁል ጊዜ ነዋሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,37,'42:37','ለእነዚያም የኀጢኣትን ታላላቆችና ጠያፎችን የሚርቁ በተቆጡም ጊዜ እነሱ የሚምሩት ለኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(42,38,'42:38','ለእነዚያም የጌታቸውን ጥሪ ለተቀበሉት፣ ሶላትንም ላዘወተሩት፣ ነገራቸውም በመካከላቸው መመካከር ለኾነው ከሰጠናቸውም ሲሳይ ለሚለግሱት፡፡');
INSERT INTO "sadiq_sani" VALUES(42,39,'42:39','ለእነዚያም በደል በደረሰባቸው ጊዜ እነርሱ (በመሰሉ) የሚመልሱ ለኾኑት (በላጭና ኗሪ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(42,40,'42:40','የመጥፎም ነገረ ዋጋ ብጤዋ መጥፎ ናት፡፡ ይቅርም ያለና ያሳመረ ሰው ምንዳው በአላህ ላይ ነው፡፡ እርሱ በደለኞችን አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,41,'42:41','ከተበደሉም በኋላ (በመሰሉ) የተበቀሉ እነዚያ በነርሱ ላይ ምንም (የወቀሳ) መንገድ የለባቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,42,'42:42','(የወቀሳ) መንገዱ በእነዚያ ሰዎችን በሚበድሉት፣ በምድርም ውስጥ ያለ ሕግ ወሰን በሚያልፉት፣ ላይ ብቻ ነው፡፡ እነዚያ ለእነርሱ አሳማሚ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,43,'42:43','የታገስና ምሕረት ያደረገም ሰው ይህ ከምርሮቹ (ከተፈላጊዎቹ) ነገሮች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(42,44,'42:44','አላህ የሚያጠመውም ሰው ከእርሱ በኋለ ለእርሱ ምንም ረዳት የለውም፡፡ በደለኞችንም ቅጣቱን ባዩ ጊዜ ወደ መመለስ መንገድ አልለን? የሚሉ ሲኾኑ ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(42,45,'42:45','ከውርደት የተነሳ ፈሪዎች ኾነው በዓይን ስርቆት ወደእርሷ (ወደ እሳት) እያስተዋሉ በእርሷ ላይ የሚቀረቡ ሲኾኑ ታያቸዋለህ፡፡ እነዚያም ያመኑት፡- «በእርግጥ ከሳሪዎቹ እነዚያ ነፍሶቻቸውንና ቤተሰቦቻቸውን በትንሣኤ ቀን ያከሰሩት ናቸው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(42,46,'42:46','ንቁ! በደለኞች በእርግጥ በዘውታሪ ስቃይ ውስጥ ናቸው፡፡ ለእነርሱም ከአላህ ሌላ የሚረዷቸው ዘመዶች ምንም አልነበሩዋቸውም፡፡ አላህም የሚያጠመው ሰው ለእርሱ (ወደ እውነት) ምንም መንገድ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,47,'42:47','ለእርሱ መመለስ የሌለው ቀን ከአላህ ከመምጣቱ በፊት ለጌታችሁ ታዘዙ፡፡ በዚያ ቀን ለእናንተ ምንም መጠጊያ የላችሁም፡፡ ለእናንተም ምንም መካድ የላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(42,48,'42:48','ቢዞሩም (ወቀሳ የለብህም)፡፡ በእነርሱ ላይ ጠባቂ አድርገን አልላክንህምና፡፡ ባንተ ላይ ማድረስ እንጅ ሌላ የለብህም፡፡ እኛም ሰውን ከእኛ የኾነን ጸጋ ባቀመስነው ጊዜ በርሷ ይደሰታል፡፡ እጆቻቸው ባስቀደሙትም ኀጢአት ምክንያት መከራ ብትደርስባቸው (ተስፋ ይቆርጣሉ)፡፡ ሰው (ውለታን) በጣም ከሐዲ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,49,'42:49','የሰማያትና የምድር ንግሥና የአላህ ነው፡፡ የሚሻውን ይፈጥራል፡፡ ለሚሻው ሰው ሴቶችን (ልጆች) ይሰጣል፡፡ ለሚሻውም ሰው ወንዶችን ይሰጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(42,50,'42:50','ወይም ወንዶችና ሴቶች አድርጎ ያጠናዳቸዋል፡፡ የሚሻውንም ሰው መካን ያደርገዋል፡፡ እርሱ ዐዋቂ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,51,'42:51','ለሰውም አላህ በራእይ፣ ወይም ከግርዶ ወዲያ፣ ወይም መልክተኛን (መልአክን) የሚልክና በፈቃዱ የሚሻውን ነገር የሚያወርድለት ቢኾን እንጅ (በገሃድ) ሊያናግረው ተገቢው አይደለም፡፡ እርሱ የበላይ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(42,52,'42:52','እንደዚሁም ወደ አንተ ከትእዛዛችን ሲኾን መንፈስን (ቁርኣንን) አወረድን፡፡ መጽሐፉም እምነቱም ምን እንደኾነ የምታውቅ አልነበርክም፡፡ ግን (መንፈሱን) ከባሮቻችን የምንሻውን ሰው በእርሱ የምንመራበት ብርሃን አደረግነው፡፡ አንተም ወደ ቀጥተኛው መንገድ በእርግጥ ትመራለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(42,53,'42:53','ወደዚያ በሰማያትና በምድር ያለው ሁሉ የእርሱ ወደ ኾነው አላህ መንገድ (ትመራለህ)፡፡ ንቁ! ነገሮቹ ሁሉ ወደ አላህ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,1,'43:1','ሐ.መ (ሓ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,2,'43:2','ገላጭ በኾነው መጽሐፍ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,3,'43:3','እኛ ታውቁ ዘንድ ዐረብኛ ቁርኣን አደረግነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,4,'43:4','እርሱም በመጽሐፎቹ እናት ውስጥ እኛ ዘንድ በእርግጥ ከፍተኛ ጥበብ የተመላ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,5,'43:5','ድንበር አላፊዎች ሕዝቦች ለመኾናችሁ ቁርኣኑን ከእናንተ ማገድን እናግዳለን?');
INSERT INTO "sadiq_sani" VALUES(43,6,'43:6','በፊተኞቹም ሕዝቦች ውስጥ ከነቢይ ብዙን ልከናል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,7,'43:7','ከነቢይም አንድም የሚመጣላቸው አልነበረም በእርሱ ይሳለቁበት የነበሩ ቢኾኑ እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,8,'43:8','ከእነርሱም በኀይል ይበልጥ የበረቱትን አጥፍተናል፡፡ የፊተኞቹም (አጠፋፍ) ምሳሌ አልፏል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,9,'43:9','«ሰማያትንና ምድርንም ማን ፈጠራቸው» ብለህ ብትጠይቃቸው «አሸናፊው ዐዋቂው (አላህ) በእርግጥ ፈጠራቸው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,10,'43:10','(እርሱ) ያ ምድርን ለእናንተ ምንጣፍ ያደረገላችሁ በርሷም ውስጥ ትምመሩ ዘንድ ለእናንተ መንገዶችን ያደረገላችሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,11,'43:11','ያም ከሰማያ ውሃን በልክ ያወረደላችሁ ነው፡፡ በእርሱም የሞተችውን አገር ሕያው አደረግን፡፡ እንደዚሁ (ከመቃብራችሁ) ትውወጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,12,'43:12','ያም ዓይነቶችን ሁሉ የፈጠረ ለእናንተም ከመርከቦችና ከቤት እንስሳዎች የምትጋልቧቸውን ያደረገላችሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,13,'43:13','በጀርባዎቹ ላይ እንድትደላደሉ ከዚያም በርሱ ላይ የተደላደላችሁ ጊዜ የጌታችሁን ጸጋ እንድታስታውሱ፤ እንድትሉም፡- «ያ ይህንን የማንችለው ስንኾን ለእኛ ያገራለን ጌታ ጥራት ይገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,14,'43:14','እኛም በእርግጥ ወደ ጌታችን ተመላሾች፤ነን» (እንድትሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,15,'43:15','ከባሮቹም ለእርሱ ቁራጭን (ልጅን) አደረጉለት፡፡ ሰው በእርግጥ ግልጽ ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,16,'43:16','ከሚፈጥረው ውስጥ ሴቶች ልጆችን ያዘን? በወንዶች ልጆችም (እናንተን) መረጣችሁን?');
INSERT INTO "sadiq_sani" VALUES(43,17,'43:17','አንዳቸውም ለአልረሕማን ምሳሌ ባደረገው ነገር (በሴት ልጅ) በተበሰረ ጊዜ እርሱ በቁጭት የተመላ ኾኖ ፊቱ የጠቆረ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,18,'43:18','በጌጥ (ተከልሶ) እንዲያድግ የሚደረገውን? እርሱም (ለደካማነቱ) በክርክር የሚያብራራውን ፍጡር (ሴትን) ለአላህ ያደርጋሉን?');
INSERT INTO "sadiq_sani" VALUES(43,19,'43:19','መላእክትንም እነርሱ የአልረሕማን ባሮች የኾኑትን ሴቶች አደረጉ፡፡ ሲፈጠሩ ነበሩን? መመስከራቸው በእርግጥ ትጻፋለች፡፡ ይጠየቃሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,20,'43:20','«አልረሕማንም በሻ ኖሮ ባልተገዛናቸው ነበር» አሉ፡፡ ለእነርሱ በዚህ ምንም ዕውቀት የላቸውም፡፡ እነርሱ የሚዋሹ እንጅ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,21,'43:21','ከእርሱ (ከቁርኣን) በፊት መጽሐፍን ሰጠናቸውን? ስለዚህ እነርሱ እርሱን የጨበጡ ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(43,22,'43:22','ከቶውንም «እኛ አባቶቻችንን በሃይማኖት ላይ አገኘናቸው፡፡ እኛም በፈለጎቻቸው ላይ ተመሪዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,23,'43:23','(ነገሩ) እንደዚሁም ነው፡፡ ከበፊትህ በማንኛይቱም ከተማ ውስጥ አስፈራሪን አልላክንም፤ ቅምጥሎችዋ እኛ አባቶቻችንን በሃይማኖት ላይ አገኘን፤ እኛም በፈለጎቻቸው ላይ ተከታዮች ነን ያሉ ቢኾኑ እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,24,'43:24','(አስፈራሪው) «አባቶቻችሁን በእርሱ ላይ ከአገኛችሁበት ይበልጥ ቀጥተኛን (ሃይማኖት) ባመጣላችሁም?» አላቸው፡፡ «እኛ በእርሱ በተላካችሁበት ነገር ከሓዲዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,25,'43:25','ከእነርሱም ተበቀልን፡፡ ያስተባባዮቹም መጨረሻ እንዴት እንደ ነበረ ተመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(43,26,'43:26','ኢብራሂምም ለአባቱና ለሕዝቦቹ ባለ ጊዜ (አስታውስ)፡፡ «እኔ ከምትግገዙት ሁሉ ንጹሕ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(43,27,'43:27','«ከዚያ ከፈጠረኝ በስተቀር (አልግገዛም)፡፡ እርሱ በእርግጥ ይመራኛልና፡፡»');
INSERT INTO "sadiq_sani" VALUES(43,28,'43:28','በዝርዮቹም ውስጥ ይመለሱ ዘንድ (ባንድ አምላክ ማመንን) ቀሪ ቃል አደረጋት፡፡');
INSERT INTO "sadiq_sani" VALUES(43,29,'43:29','ይልቅ እነዚህን (ቁረይሾችን)፣ አባቶቻቸውንም ቁርኣንና ገላጭ መልክተኛ እስከመጣላቸው ድረስ አጣቀምኳቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,30,'43:30','እውነቱም በመጣላቸው ጊዜ «ይህ ድግምት ነው፡፡ እኛም በእርሱ ከሓዲዎች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,31,'43:31','«ይህም ቁርኣን ከሁለቱ ከተሞች በታላቅ ሰው ላይ አይወረድም ኖሯልን?» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,32,'43:32','እነርሱ የጌታህን ችሮታ ያከፈፍላሉን? እኛ በቅርቢቱ ሕይወት ኑሯቸውን በመካከላቸው አከፋፍለናል፡፡ ከፊላቸውም ከፊሉን ሠራተኛ አድርጎ ይይዝ ዘንድ ከፊላቸውን ከከፊሉ በላይ በደረጃዎች (በሀብት) አበለጥን፡፡ የጌታህም ጸጋ (ገነት) ከሚሰበስቡት (ሃብት) በላጨ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(43,33,'43:33','ሰዎችም (በክሕደት) አንድ ሕዝብ የሚኾኑ ባልነበሩ ኖሮ በአልረሕማን ፤ለሚክዱት ሰዎች (በዚህች ዓለም) ለቤቶቻቸው ከብር የኾኑን ጣራዎች በእነርሱ ላይ የሚወጡባቸውንም (የብር) መሰላሎች ባደረግንላቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(43,34,'43:34','ለቤቶቻቸውም ደጃፎችን በእነርሱ ላይ የሚደገፉባቸውንም አልጋዎች (ከብር ባደረግንላቸው ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,35,'43:35','የወርቅ ጌጥንም (ባደረግንላቸው ነበር)፡፡ ይህም ሁሉ የቅርቢቱ ሕይወት መጣቀሚያ እንጅ ሌላ አይደለም፤ (ጠፊ ነው)፡፡ መጨረሻይቱም አገር በጌታህ ዘንድ ለጥንቁቆቹ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(43,36,'43:36','ከአልረሕማን ግሣጼ (ከቁርኣን) የሚደናበርም ሰው ለእርሱ ሰይጣንን እናስጠጋለን፡፡ ስለዚህ እርሱ ለእርሱ ቁራኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,37,'43:37','እነርሱም (ተደናባሪዎቹ) ተመሪዎች መኾናቸውን የሚያስቡ ሲኾኑ እነርሱ (ሰይጣናት) ከቀጥተኛው መንገድ በእርግጥ ያግዷቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,38,'43:38','(በትንሣኤ) ወደኛ በመጣም ጊዜ (ቁራኛዬ ሆይ!) «በእኔና ባንተ መካከል የምሥራቅና የምዕራብ ርቀት በኖረ ወይ ምኞቴ! ምን የከፋህ ቁራኛ ነህ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,39,'43:39','ስለ በደላችሁም ዛሬ እናንተ በቅጣት ተጋሪዎች መኾናችሁ አይጠቅማችሁም፤ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,40,'43:40','አንተ ደንቆሮዎችን ታሰማለህን? ወይስ ዕውሮችንና በግልጽ ጥመት ውስጥ የኾኑን ሰዎች ትመራለህን?');
INSERT INTO "sadiq_sani" VALUES(43,41,'43:41','አንተንም (ቅጣታቸውን ስታይ) ብንወስድህ እኛ ከእነርሱ ተበቃዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(43,42,'43:42','ወይም ያንን የቀጠርናቸውን ብናሳይህ እኛ በእነርሱ (ቅጣት) ላይ ቻይዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(43,43,'43:43','ያንንም ወደ አንተ የተወረደልህን አጥብቀህ ያዝ፡፡ አንተ በቀጥተኛው መንገድ ላይ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(43,44,'43:44','እርሱም (ቁርኣን) ለአንተ፣ ለሕዘቦችህም ታላቅ ክብር ነው፡፡ ወደ ፊትም (ከርሱ) ትጠየቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,45,'43:45','ከመልእክተኞቻችንም ካንተ በፊት የላክናቸውን (ተከታዮቻቸውን) ከአልረሕማን ሌላ የሚገዙዋቸው የኾኑን አማልክት አድርገን እንደ ኾነ ጠይቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,46,'43:46','ሙሳንም በተዓምራቶቻችን ወደ ፈርዖንና ወደ ሹማምንቶቹ በእርግጥ ላክን፡፡ «እኔ የዓለማት ጌታ መልክተኛ ነኝ» አላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,47,'43:47','በተዓምራታችንም በመጣባቸው ጊዜ ወዲያውኑ እነርሱ በእርሷ ይስቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,48,'43:48','ከተዓምርም አንዲትንም አናሳያቸውም እርሷ ከብጤዋ በጣም የበለጠች ብትኾን እንጅ፡፡ (ከክህደታቸው) ይመለሱም ዘንድ በቅጣት ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,49,'43:49','«አንተ ድግምተኛ (ዐዋቂ) ሆይ! ጌታህን አንተ ዘንድ ቃል በገባልህ ለእኛ ለምንልን፡፡ እኛ (ቅጣቱን ቢያነሳልን) ተመሪዎች ነን» አሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,50,'43:50','ስቃዩን ከእነርሱ ላይ በገለጥንም ጊዜ ወዲያውኑ ኪዳናቸውን ያፈርሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,51,'43:51','ፈርዖንም በሕዝቦቹ ውስጥ ተጣራ፡፡ አለም፤ «ሕዝቦቼ ሆይ! የምስር ግዛት የኔ አይደለምን? እነዚህም ጅረቶች ከስሬ የሚፈሱ ሲኾኑ (የኔ አይደሉምን?) አትመለከቱምን?');
INSERT INTO "sadiq_sani" VALUES(43,52,'43:52','«በውነቱ እኔ ከዚህ ያ እርሱ ወራዳ ንግግሩንም ሊገልጽ የማይቀርብ ከኾነው በላጭ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,53,'43:53','«በእርሱም ላይ የወርቅ አንባሮች ለምን አልተጣሉበትም ወይም መላእክት ከእርሱ ጋር ተቆራኝተው ለምን አልመጡም» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,54,'43:54','ሕዝቦቹንም አቄላቸው፡፡ ታዘዙትም፡፡ እነርሱ አመጸኞች ሕዝቦች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(43,55,'43:55','ባስቆጡንም ጊዜ ከእነርሱ በተበቀለን፡፡ ሁሉንም አሰጠምናቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,56,'43:56','(በጥፋት) ቀዳሚዎችና ለኋለኞቹ (መቀጣጫ) ምሳሌም አደረግናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,57,'43:57','የመርየም ልጅም ምሳሌ በተደረገ ጊዜ ሕዝቦችህ (ከሓዲዎቹ) ወዲያውኑ ከእርሱ ይስቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,58,'43:58','«አማልክቶቻችን ይበልጣሉን ወይስ እርሱ?» አሉም፡፡ ለክርክርም እንጅ ላንተ እርሱን ምሳሌ አላደረጉትም፡፡ በእውነት እነርሱ ክርክረ ብርቱ ሕዝቦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,59,'43:59','እርሱ በርሱ ላይ የለገስንለት ለእስራኤለም ልጆች ተዓምር ያደረግነው የኾነ ባሪያ እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,60,'43:60','ብንሻም ኖሮ በምድር ላይ ከእናነተ ምትክ መላእክትን ባደረግን ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(43,61,'43:61','እርሱም (ዒሳ) ለሰዓቲቱ (ማወቂያ) በእርግጥ ምልክት ነው፡፡ «በእርሷም አትጠራጠሩ፡፡ ተከተሉኝም፤ ይህ ቀጥተኛ መንገድ ነው» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,62,'43:62','ሰይጣንም አያግዳችሁ፡፡ እርሱ ለእናንተ ግልጽ ጠላት ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(43,63,'43:63','ዒሳ በታምራቶች በመጣ ጊዜም አላቸው «በእርግጥ በጥበብ መጣኋችሁ፡፡ የዚያንም በርሱ የምትለያዩበትን ከፊሉን ለናንተ ላብራራላችሁ (መጣሁ)፡፡ አላህንም ፍሩ፡፡ ታዘዙኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,64,'43:64','«አላህ ጌታዬ ጌታችሁም ነውና ተገዙት፡፡ ይህ ቀጥተኛ መንገድ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(43,65,'43:65','ከመካከላቸውም አሕዛቦቹ ተለያዩ፡፡ ለእነዚያም ለበደሉት ከአሳማሚ ቀን ቅጣት ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,66,'43:66','ሰዓቲቱን እነርሱ የማያውቁ ኾነው በድንገት ልትመጣባቸው እንጅ ይጠባበቃሉን?');
INSERT INTO "sadiq_sani" VALUES(43,67,'43:67','ወዳጆች በዚያ ቀን ከፊላቸው ለከፊሉ ጠላት ነው፡፡ አላህን ፈሪዎች ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,68,'43:68','(ለነርሱስ) «ባሮቼ ሆይ! ዛሬ ቀን በእናንተ ላይ ፍርሃት የለባችሁም እናንተም የምታዝኑ አይደላችሁም» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,69,'43:69','እነዚያ በአንቀጾቻችን ያመኑና ፍጹም ታዛዦች የነበሩ (ባሮቼ ሆይ!)');
INSERT INTO "sadiq_sani" VALUES(43,70,'43:70','«ገነትን ግቡ እናንተም ሚስቶቻችሁም ትደሰታላችሁ ትከበሩም አላችሁ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,71,'43:71','ከወርቅ የኾኑ ሰፋፊ ዝርግ ሳሕኖችና ኩባያዎች በእነርሱ ላይ ይዝዞራሉ፡፡ በእርሷም ውስጥ ነፍሶች የሚከጅሉት፣ ዓይኖችም የሚደሰቱበት ሁሉ አልለ፡፡ እናንተም በውስጧ ዘላለም ዘውታሪዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,72,'43:72','ይህችም ያቺ ትሠሩት በነበራችሁት ዋጋ የተሰጣችኋት ገነት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(43,73,'43:73','ለእናንተ በውስጧ ብዙ ፍረፍሬዎች በእርግጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,74,'43:74','አመጸኞች በገሀነም ስቃይ ውስጥ በእርግጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,75,'43:75','ከእነርሱ (ቅጣቱ) አይቀለልላቸውም፡፡ እነርሱም በእርሱ ውስጥ ተሰፋ ቆራጮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,76,'43:76','አልበደልናቸውምም፤ ግን በዳዮቹ እነርሱው ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,77,'43:77','«ማሊክ ሆይ! ጌታህ በኛ ላይ (በሞት) ይፍረድ፡፡» እያሉ ይጣራሉም፡፡ «እናንተ (በቅጣቱ ውስጥ) ዘላለም ነዋሪዎች ናችሁ» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(43,78,'43:78','እውነተኛውን መንገድ በእርግጥ አመጣንላችሁ፡፡ ግን አብዛኞቻችሁ እውነቱን ጠይዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,79,'43:79','ይልቁንም (በነቢዩ ላይ በማደም) ነገርን አጠነከሩን? እኛም (ተንኮላቸውን ወደእነርሱ በመመለስ) አጠንካሪዎች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(43,80,'43:80','ወይም እኛ ምስጢራቸውንና ውይይታቸውን የማንሰማ መኾናችንን ያስባሉን? አይደለም፤ መልክተኞቻችንም እነርሱ ዘንድ ይጽፋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,81,'43:81','«ለአልረሕማን ልጅ (የለውም እንጅ) ቢኖረው እኔ (ለልጁ) የተገዢዎቹ መጀመሪያ ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,82,'43:82','የሰማያትና የምድር ጌታ የዙፋኑ ጌታ ከሚሉት ነገር ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,83,'43:83','ተዋቸውም፡፡ ያንን ይስፈራሩበት የነበሩትን ቀናቸውን እስከሚገናኙ ድረስ (በስሕተታቸው ውስጥ) ይዋኙ ይጫዎቱም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,84,'43:84','እርሱም ያ በሰማይ ውስጥ ሊግገዙት የሚገባው በምድርም ውስጥ ሊግገዙት የሚገባው አምላክ ነው፡፡ እርሱም ብልሃተኛው ዐዋቂው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(43,85,'43:85','ያም የሰማያትና የምድር በመካከላቸውም ያለው ሁሉ ንግሥና የእርሱ ብቻ የኾነው አምላክ ላቀ፡፡ የሰዓቲቱም (የመምጫዋ ዘመን) ዕውቀት እርሱ ዘንድ ብቻ ነው፡፡ ወደእርሱም ትመለሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,86,'43:86','እነዚያም ከአላህ ሌላ የሚግገዟቸው እነርሱ የሚያውቁ ኾነው በእውነት ከመሰከሩት በስተቀር ምልጃን አይችሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(43,87,'43:87','ማን እንደፈጠራቸውም ብትጠይቃቸው «በእርግጥ አላህ ነው» ይላሉ፡፡ ታዲያ (ከእምነት) ወዴት ይዞራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(43,88,'43:88','(ነቢዩ) ጌታዬ ሆይ! እነዚህ የማያምኑ ሕዝቦች ናቸው፤ የማለቱም (ዕውቀት አላህ ዘንድ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(43,89,'43:89','እነርሱንም ተዋቸው፡፡ ነገሬ ሰላም ነው በልም፡፡ ወደፊትም (የሚመጣባቸውን) ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,1,'44:1','ሐ.መ (ሓ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,2,'44:2','አብራሪ በኾነው መጽሐፍ እንምላለን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,3,'44:3','እኛ (ቁርኣኑን) በተባረከች ሌሊት ውስጥ አወረድነው፡፡ እኛ አስፈራሪዎች ነበርንና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,4,'44:4','በውስጧ የተወሰነው ነገር ሁሉ ይልለያል፡፡');
INSERT INTO "sadiq_sani" VALUES(44,5,'44:5','ከእኛ ዘንድ የኾነ ትእዛዝ ሲኾን (አወረድነው)፡፡ እኛ (መልክተኞችን) ላኪዎች ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,6,'44:6','ከጌታህ በኾነው ችሮታ (ተላኩ)፡፡ እነሆ እርሱ ሰሚው ዐዋቂው ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,7,'44:7','የሰማያትና የምድር በመካከላቸውም ያለው ነገር ሁሉ ጌታ ከኾነው (ተላኩ)፡፡ የምታረጋግጡ እንደኾናችሁ (ነገሩ እንዳልነው መኾኑን እወቁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,8,'44:8','ከእርሱ ሌላ አምላክ የለም፡፡ ሕያው ያደርጋል፡፡ ይገድላልም፡፡ ጌታችሁ የመጀመሪያዎቹ አባቶቻችሁም ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,9,'44:9','በእውነቱ እነርሱ የሚጫወቱ ሲኾኑ በመጠራጠር ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,10,'44:10','ሰማይም በግልጽ ጭስ የምትመጣበትን ቀን ተጠባበቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,11,'44:11','ሰዎችን የሚሸፍን (በኾነ ጭስ) ይህ አሳማሚ ቅጣት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,12,'44:12','«ጌታችን ሆይ! ከእኛ ላይ ቅጣቱን ግለጥልን፡፡ እኛ አማኞች እንኾናለንና» (ይላሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,13,'44:13','(ቅጣቱ በወረደ ጊዜ) ለእነርሱ መግገሰጽ እንዴት ይኖራቸዋል? አስረጅ መልክተኛ የመጣቸው ሲኾን (የካዱም ሲኾኑ)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,14,'44:14','ከዚያም ከእርሱ የዞሩ፤ (ከሰው) «የተሰተማረ ዕብድ ነው» ያሉም ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,15,'44:15','እኛ ቅጣቱን ለጥቂት ጊዜ ገላጮች ነን፡፡ እናንተ (ወደ ክህደታችሁ) በእርግጥ ተመላሾች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,16,'44:16','ታላቂቱን ብርቱ አያያዝ በምንይዝበት ቀን፤ እኛ ተበቃዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,17,'44:17','ከእነርሱም በፊት የፈርዖንን ሕዝቦች በእርግጥ ሞከርን፡፡ ክቡር መልእክተኛም መጣላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,18,'44:18','«የአላህን ባሮች ወደኔ አድርሱ፡፡ እኔ ለእናንተ ታማኝ መልክተኛ ነኝና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,19,'44:19','«በአላህም ላይ አትኩሩ፡፡ እኔ ግልጽ የኾነን አስረጅ ያመጣሁላችሁ ነኝና (በማለት መጣላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,20,'44:20','«እኔም እንዳትወግሩኝ በጌታየና በጌታችሁ ተጠብቂያለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,21,'44:21','«በእኔም ባታምኑ ራቁኝ» (ተዉኝ አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,22,'44:22','ቀጥሎም (ስለ ዛቱበት) «እነዚህ አመጸኞች ሕዝቦች ናቸው» (አጥፋቸው) ሲል ጌታውን ለመነ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,23,'44:23','(ጌታው) «ባሮቼንም ይዘህ በሌሊት ኺድ፡፡ እናንተ የሚከተሉዋችሁ ናችሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,24,'44:24','«ባሕሩንም የተከፈተ ኾኖ ተወው፡፡ እነርሱ የሚሰጥጠሙ ሰራዊት ናቸውና» (አለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,25,'44:25','ከአትክልቶችና ከምንጮችም ብዙ ነገሮችን ተዉ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,26,'44:26','ከአዝመራዎችም ከመልካም መቀመጫም፡፡');
INSERT INTO "sadiq_sani" VALUES(44,27,'44:27','በእርሷ ተደሳቾች ከነበሩባትም ድሎት (ብዙን ነገር ተዉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,28,'44:28','(ነገሩ) እንደዚሁ ኾነ፡፡ ሌሎችንም ሕዝቦች አወረስናት፡፡');
INSERT INTO "sadiq_sani" VALUES(44,29,'44:29','ሰማይና ምድርም በእነርሱ ላይ አላለቀሱም፡፡ የሚቆዩም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(44,30,'44:30','የእስራኤልንም ልጆች አዋራጅ ከኾነ ስቃይ በእርግጥ አዳንናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,31,'44:31','ከፈርዖን፤ እርሱ የኮራ ከወሰን አላፊዎቹ ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,32,'44:32','ከማወቅም ጋር በዓለማት ላይ በእርግጥ መረጥናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,33,'44:33','ከታምራቶችም በውስጡ ግልጽ የኾነ ፈተና ያለበትን ሰጠናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,34,'44:34','እነዚህ (የመካ ከሓዲዎች) በእርግጥ ይላሉ፡-');
INSERT INTO "sadiq_sani" VALUES(44,35,'44:35','እርሷ የፊተኛይቱ ሞታችን እንጅ አይደለችም፡፡ እኛም የምንቀሰቀስ አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(44,36,'44:36','እውነተኞችም እንደ ኾናችሁ አባቶቻችንን አምጡልን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,37,'44:37','እነርሱ በላጮች ናቸውን ወይስ የቱብበዕ ሕዝቦችና እነዚያ ከእነርሱ በፊት የነበሩት? አጠፋናቸው፡፡ እነርሱ አመጸኞች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,38,'44:38','ሰማያትንና ምድርንም በሁለቱ መካከል ያለውንም ሁሉ ቀላጆች ኾነን አልፈጠርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(44,39,'44:39','ሁለቱንም በምር እንጅ አልፈጠርናቸውም፡፡ ግን አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(44,40,'44:40','የመለያው ቀን ለሁሉም ቀጠሯቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,41,'44:41','ዘመድ ከዘመዱ ምንንም የማይጠቅምበት እነርሱም የማይርረዱበት ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,42,'44:42','አላህ ያዘነለት ሰውና ያመነ ብቻ ሲቀር፡፡ (እርሱስ ይርረዳል)፡፡ እርሱ አሸናፊው አዛኙ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(44,43,'44:43','የዘቁመ ዛፍ');
INSERT INTO "sadiq_sani" VALUES(44,44,'44:44','የኀጢኣተኛው ምግብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,45,'44:45','እንደ ዘይት አተላ ነው፡፡ በሆዶች ውስጥ የሚፈላ ሲኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,46,'44:46','እንደ ገነፈለ ውሃ አፈላል (የሚፈላ ሲኾን)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,47,'44:47','ያዙት፤ ወደ ገሀነም መካከልም በኀይል ጎትቱት፡፡');
INSERT INTO "sadiq_sani" VALUES(44,48,'44:48','ከዚያም «ከራሱ በላይ ከፈላ ውሃ ስቃይ አንቧቡበት፡፡»');
INSERT INTO "sadiq_sani" VALUES(44,49,'44:49','«ቅመስ፤ አንተ አሸናፊው ክብሩ ነህና» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,50,'44:50','«ይህ ያ በእርሱ ትጠራጠሩበት የነበራችሁት ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(44,51,'44:51','ጥንቁቆቹ በእርግጥ በጸጥተኛ መኖሪያ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,52,'44:52','በአትክልቶችና በምንጮች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,53,'44:53','ፊት ለፊት የተቅጣጩ ኾነው ከስስ ሐርና ከወፍራም ሐር ይለብሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,54,'44:54','(ነገሩ) እንደዚሁ ነው፤ ዓይናማዎች የኾኑን ነጫጭ ሴቶችም እናጠናዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(44,55,'44:55','በእርሷም ውስጥ የተማመኑ ኾነው፤ ከፍራፍሬ ሁሉ ያዝዛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(44,56,'44:56','የፊተኛይቱን ሞት እንጅ (ዳግመኛ) በእርሷ ውስጥ ሞትን አይቀምሱም፡፡ የገሀነምንም ቅጣት (አላህ) ጠበቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,57,'44:57','ከጌታህ በኾነ ችሮታ (ጠበቃቸው)፡፡ ይህ እርሱ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,58,'44:58','(ቁርኣኑን) በቋንቋህም ያገራነው (ሕዝቦችህ) ይገነዘቡ ዘንድ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(44,59,'44:59','ተጠባበቅም እነርሱ ተጠባባቂዎች ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(45,1,'45:1','ሐ.መ (ሓ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(45,2,'45:2','የመጽሐፉ መወረድ አሸናፊ ጥበበኛ ከኾነው አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,3,'45:3','በሰማያትና በምድር ውስጥ ለምእምናን ሁሉ (ለችሎታው) እርገጠኛ ምልክቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(45,4,'45:4','እናንተንም በመፍጠር ከተንቀሳቃሽም (በምድር ላይ) የሚበትነውን ሁሉ (በመፍጠሩ) ለሚያረጋግጡ ሕዝቦች ተዓምራቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(45,5,'45:5','በሌሊትና መዓልት መተካካትም፣ ከሲሳይም (ከዝናም) አላህ ከሰማይ ባወረደው በርሱም ምድርን ከሞተች በኋላ ሕያው በማድረጉ፣ ነፋሶችንም (በያቅጣጫቸው) በማዘዋወሩ ለሚያውቁ ሕዝቦች ማስረጃዎች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(45,6,'45:6','እነዚህ ባንተ ላይ በውነት የምናነባቸው ሲኾኑ የአላህ ማስረጃዎች ናቸው፡፡ ከአላህና ከማስረጃዎቹም ወዲያ በየትኛው ንግግር ያምናሉ?');
INSERT INTO "sadiq_sani" VALUES(45,7,'45:7','ውሸታም ኀጢአተኛ ለኾነ ሁሉ ወዮለት፡፡ (ጥፋት ተገባው)፡፡');
INSERT INTO "sadiq_sani" VALUES(45,8,'45:8','የአላህን አንቀጾች በእርሱ ላይ የሚነበቡለት ሲኾኑ ይሰማል፡፡ ከዚያም የኮራ ሲኾን እንዳልሰማት ኾኖ (በክሕደቱ ላይ) ይዘወትራል፡፡ በአሳማሚም ቅጣት አብስረው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,9,'45:9','ከአንቀጾቻችንም አንዳችን ባወቀ ጊዜ መሳለቂያ አድርጎ ይይዛታል፡፡ እነዚያ ለእነርሱ አዋራጅ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,10,'45:10','ከፊታቸውም ገሀነም አልለች፡፡ የሰበሰቡትም ሀብት ከእነርሱ ላይ ምንንም አይመልስላቸውም፡፡ ከአላህ ሌላም ረዳቶች አድርገው የያዙዋቸው (አይጠቅሟቸውም)፡፡ ለእነርሱም ታላቅ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,11,'45:11','ይህ (ቁርኣን) መሪ ነው፡፡ እነዚያም በጌታቸው አንቀጾች የካዱት ለእነርሱ ከብርቱ ቅጣት የኾነ አሳማሚ ስቃይ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,12,'45:12','አላህ ያ ባሕርን በውስጡ ታንኳዎች በፈቃዱ እንዲንሻለሉበት ከችሮታውም እንድትፈልጉበት እንድታመሰግኑትም ለእናነተ የገራላችሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,13,'45:13','ለናንተም በሰማያት ያለውንና በምድርም ያለውን ሁሉ በመላ ከእርሱ ሲኾን የገራላችሁ ነው፡፡ በዚህ ለሚያስተነትኑ ሕዝቦች ተዓምራት አልለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(45,14,'45:14','ለእነዚያ ለአመኑት ሰዎች (ምሕረት አድርጉ) በላቸው፡፡ ለእነዚያ የአላህን ቀኖች ለማይፈሩት ይምራሉና፡፡ ሕዝቦችን ይሠሩት በነበሩት ነገር ይመነዳ ዘንድ (ምሕረት አድርጉ በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(45,15,'45:15','መልካምን የሠራ ሰው ለነፍሱ ነው፡፡ ያከፋም ሰው በእርሷ ላይ ነው፡፡ ከዚያም ወደ ጌታችሁ');
INSERT INTO "sadiq_sani" VALUES(45,16,'45:16','ለእስራኤል ልጆችም መጽሐፍንና ሕግን፣ ነቢይነትንም በእርግጥ ሰጠናቸው፡፡ ከመልካም ሲሳዮችም ለገስንላቸው፡፡ በዓለማት ላይም አበለጥናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,17,'45:17','ከትዕዛዝም ግልጾችን ሰጠናቸው፡፡ ዕውቀቱም ከመጣላቸው በኋላ በመካከላቸው ለምቀኝነት እንጅ ለሌላ አልተለያዩም፡፡ ጌታህ በትንሣኤ ቀን በዚያ በእርሱ ይለያዩበት በነበሩት ነገር በመካከላቸው በእርግጥ ይፈርዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(45,18,'45:18','ከዚያም ከነገሩ (ከሃይማኖት) በትክክለኛይቱ ሕግ ላይ አደረግንህ፡፡ ስለዚህ ተከተላት፡፡ የእነዚያንም የማያውቁትን ሕዝቦች ዝንባሌዎች አትከተል፡፡');
INSERT INTO "sadiq_sani" VALUES(45,19,'45:19','እነርሱ ከአላህ (ቅጣት) ምንንም ከአንተ አይገፈትሩልህምና፡፡ በደለኞም ከፊላቸው የከፊሉ ረዳቶች ናቸው፡፡ አላህም የጥንቁቆቹ ረዳት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,20,'45:20','ይሀ (ቁርኣን) ለሰዎች የልብ ብርሃኖች ነው፡፡ ለሚያረጋግጡም ሕዝቦች መሪና እዝነት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,21,'45:21','እነዚያ ኀጢአቶችን የሠሩ ሕይወታቸውም ሞታቸውም ትክክል ሲኾን እንደእነዚያ እንደ አመኑትና መልካሞችን እንደሠሩት ልናደርጋቸው ይጠረጥራሉን? (አይጠርጥሩ)፡፡ የሚፈርዱት ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(45,22,'45:22','አላህም ሰማያትንና ምድርን (ለችሎታው እንደሚያመለክትባቸውና) ነፍስም ሁሉ በሠራችው ሥራ ትምመነዳ ዘንድ በትክክል ፈጠረ፡፡ እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(45,23,'45:23','ዝንባሌውን አምላኩ አድርጎ የያዘውን፣ አላህም ከዕውቀት ጋር ያጠመመውን፣ በጆሮውና በልቡ ላይም ያተመበትን፣ በዓይኑም ላይ ሺፋን ያደረገበትን ሰው አየህን! ታዲያ ከአላህ በኋላ የሚያቀናው ማነው? አትገሠጹምን?');
INSERT INTO "sadiq_sani" VALUES(45,24,'45:24','እርሷም (ሕይወት) የቅርቢቱ ሕይወታችን እንጅ ሌላ አይደለችም፡፡ «እንሞታለን፤ ሕያውም እንኾናለን፡፡ ከጊዜም (ማለፍ) በስተቀር ሌላ አያጠፋንም» አሉ፡፡ ለእነርሱም በዚህ (በሚሉት) ምንም ዕውቀት የላቸውም፡፡ እነርሱ የሚጠራጠሩ እንጅ ሌላ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(45,25,'45:25','አንቀጾቻችንም ግልጾች ኾነው በእነርሱ ላይ በተነበቡላቸው ጊዜ ክርክራቸው «እውነተኞች እንደ ኾናችሁ አባቶቻችንን አምጡ» ማለት እንጅ ሌላ አልነበረም፡፡');
INSERT INTO "sadiq_sani" VALUES(45,26,'45:26','«አላህ ሕያው ያደርጋችኋል፤ ከዚያም ይገድላችኋል፣ ከዚያም ወደ ትንሣኤ ቀን ይሰበስባችኋል፡፡ በእርሱ ጥርጥር የለበትም፡፡ ግን አብዛኛዎቹ ሰዎች አያውቁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,27,'45:27','የሰማያትና የምድር ግዛት የአላህ ብቻ ነው፡፡ ሰዓቲቱም በምትቆምበት ቀን ያን ጊዜ አጥፊዎች ሁሉ ይከስራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(45,28,'45:28','ሕዝብንም ሁሉ ተንበርካኪ ኾና ታያታለህ፡፡ ሕዝብ ሁሉ ወደ መጽሐፏ ትጥጠራለች፡፡ «ዛሬ ትሠሩት የነበራችሁትን ነገር ትመነዳላችሁ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(45,29,'45:29','ይህ መጽሐፋችን ነው፡፡ በእናንተ ላይ በእውነት ይናገራል፡፡ እኛ ያንን ትሠሩት የነበራችሁትን እናስገለብጥ ነበርን (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(45,30,'45:30','እነዚያ ያመኑትና መልካሞችን የሠሩትማ ጌታቸው በእዝነቱ ውስጥ ያገባቸዋል፡፡ ይህ እርሱ ግልጽ የኾነ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,31,'45:31','እነዚያም የካዱትማ (ለእነርሱ ይባላሉ) «አንቀጾቼ በእናንተ ላይ ይነበቡ አልነበሩምን? ኮራችሁም፡፡ ከሓዲዎች ሕዝቦችም ነበራችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(45,32,'45:32','«የአላህ ቀጠሮ እውነት ነው፡፡ ሰዓቲቱም በእርሷ (መምጣት) ጥርጥር የለም፡፡» በተባለ ጊዜም «ሰዓቲቱ ምን እንደ ኾነች አናውቅም፡፡ መጠራጠርን የምንጠራጠር እንጅ ሌላ አይደለንም፡፡ እኛም አረጋጋጮች አይደለንም» አላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(45,33,'45:33','የሠሩዋቸውም ኀጢአቶች ለእነርሱ ይገለጹላቸዋል፡፡ ያም በእርሱ ይሳለቁበት የነበሩት ቅጣት በእነርሱ ላይ ይወርድባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(45,34,'45:34','«ይህንንም ቀናችሁን መገናኘትን እንደ ረሳችሁ ዛሬ እንረሳችኋለን፡፡ (እንተዋችኋለን)፡፡ መኖሪያችሁም እሳት ናት፡፡ ለእናንተም ከረዳቶች ምንም የላችሁም» ይባላል፡፡');
INSERT INTO "sadiq_sani" VALUES(45,35,'45:35','ይህ እናንተ የአላህን አንቀጾች መቀለጃ አድርጋችሁ በመያዛችሁና ቅርቢቱም ሕይወት ስለአታለለቻችሁ ነው፤ (ይባላሉ)፡፡ ዛሬ ከእርሷ አይወጥጡም፤ እነርሱም በወቀሳ አይታለፉም፡፡');
INSERT INTO "sadiq_sani" VALUES(45,36,'45:36','ምስጋናም ለአላህ ለሰማያት ጌታ ለምድርም ጌታ ለዓለማት ጌታ የተገባው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(45,37,'45:37','ኩራትም በሰማያትም በምድርም ለእርሱ ብቻ ነው፡፡ እርሱም አሸነፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,1,'46:1','ሐ.መ (ሓ ሚም)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,2,'46:2','የመጽሐፉ መወረድ አሸናፊ ጥበበኛ ከኾነው አላህ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,3,'46:3','ሰማያትንና ምድርን በመካከላቸው ያለውንም ሁሉ በምርና የተወሰነ ጊዜንም በመለካት እንጅ (ለቀልድ) አልፈጠርናቸውም፡፡ እነዚያ የካዱትም ከተስፈራሩት ነገር (ትተው) ዘዋሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,4,'46:4','«ከአላህ ሌላ የምትግገዟቸውን አያችሁን? ከምድር ምንን እንደ ፈጠሩ አሳዩኝ፡፡ ወይም በሰማያት ለእነርሱ (ከአላህ ጋር) መጋራት አላቸውን? (ያማልዱናል በማለታችሁ) እውነተኞች እንደ ኾናችሁ ከዚህ (ቁርኣን) በፊት የኾነን መጽሐፍ ወይም ከዕውቀት የኾነ ቅርስን አምጡልኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,5,'46:5','እስከ ትንሣኤ ቀን ድረስም ለእርሱ የማይመልስለትን (ጣዖት) ከአላህ ሌላ ከሚጠራ ሰው ይበልጥ የተሳሳተ ማነው? እነርሱም ከጥሪያቸው ዘንጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,6,'46:6','ሰዎችም በተሰበሰቡ ጊዜ (ጣዖቶቹ) ለእነርሱ ጠላቶች ይኾናሉ፡፡ (እነሱን) መገዛታቸውንም ከሓዲዎች ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,7,'46:7','በእነርሱም ላይ አንቀጾቻችን ግልጾች ኾነው በተነበቡ ጊዜ እነዚያ እውነቱን በመጣላቸው ጊዜ የካዱትን «ይህ ግልጽ ድግምት ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,8,'46:8','ይልቁንም ቀጠፈው ይላሉን? «ብቀጥፈው ለእኔ ከአላህ (ቅጣት ለማዳን) ምንንም አትችሉም፡፡ እርሱ ያንን በእርሱ የምትቀባዥሩበትን ዐዋቂ ነው፡፡ በእኔና በእናንተ መካከል መስካሪ በእርሱ በቃ፡፡ እርሱም መሓሪው አዛኙ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,9,'46:9','«ከመልክተኞች ብጤ የሌለኝ አይደለሁም፡፡ በእኔም በእናንተም ምን እንደሚሥሠራም አላውቅም፡፡ ወደእኔ የሚወረደውን እንጅ ሌላን አልከተልም፡፡ እኔም ግልጽ አስፈራሪ እንጅ ሌላ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,10,'46:10','(ሙሐመድ ሆይ!) በላቸው «እስቲ ንገሩኝ፤ (ቁርኣኑ) ከአላህ ዘንድ ቢኾን፡፡ በእርሱም ብትክዱ፣ ከእስራኤል ልጆችም መስካሪ በብጤው (በእርሱ) ላይ ቢመሰክር፣ ቢያምንም፣ (ከእምነት) ብትኮሩም፣ (በዳይ አትኾኑምን?)» አላህ በእርግጥ በደለኞችን ሕዝቦች አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(46,11,'46:11','እነዚያንም የካዱት ሰዎች ስለእነዚያ ስለ አመኑት ሰዎች (እምነቱ) «መልካም ነገር በኾነ ኖሮ ወደርሱ ባልቀደሙን ነበር» አሉ፡፡ በእርሱም ባልተመሩ ጊዜ «ይህ (ቁርኣን) ጥንታዊ ቅጥፈት ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,12,'46:12','ከበፊቱም የሙሳ መጽሐፍ መሪና ጸጋ ሲኾን አልለ፡፡ ይህም እነዚያን የበደሉትን ሊያስፈራራ በዐረብኛ ቋንቋ ሲኾን (የፊቶቹን መጻሕፍት) አረጋጋጭ መጽሐፍ ነው፡፡ ለበጎ አድራጊዎችም ብስራት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,13,'46:13','እነዚያ «ጌታችን አላህ ነው» ያሉ፣ ከዚያም ቀጥ ያሉ በነርሱ ላይ ፍርሃት የለባቸውም፡፡ እነርሱም አያዝኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(46,14,'46:14','እነዚያ የገነት ጓዶች ናቸው፡፡ በእርሷ ውስጥ ዘውታሪዎች ሲኾኑ፡፡ በዚያ ይሠሩት በነበሩት ምክንያት ታላቅን ምንዳ (ይምመነዳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,15,'46:15','ሰውንም በወላጆቹ በጎ መዋልን በጥብቅ አዘዝነው፡፡ እናቱ በችግር ላይ ኾና አረገዘችው፡፡ በችግርም ወለደችው፡፡ እርግዝናውና ከጡት መለያውም ሰላሳ ወር ነው፡፡ ጥንካሬውንም ወቅት በደረሰ ጊዜ (ከዚያ አልፎ) አርባ ዓመትንም በደረሰ ጊዜ «ጌታዬ ሆይ! ያችን በእኔ ላይና በወላጆቼ ላይ የለገስካትን ጸጋህን እንዳመሰግን የምትወደውንም መልካም ሥራ እንድሠራ ምራኝ፡፡ ዘሮቼንም ለእኔ አብጅልኝ፡፡ እኔ ወዳንተ ተመለስኩ፡፡ እኔም ከሙስሊሞ ነኝ» አለ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,16,'46:16','እነዚህ (ይህንን ባዮች) በገነት ጓዶች ውስጥ ሲኾኑ እነዚያ ከሠሩት ሥራ መልካሙን ከነሱ የምንቀበላቸውና ከኀጢአቶቻቸውም የምናልፋቸው ናቸው፡፡ ያንን ተስፋ ይስሰጡት የነበሩትን እውነተኛ ቀጠሮ (እንሞላላቸዋለን)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,17,'46:17','ያንንም ለወላጆቹ «ፎህ ለእናንተ ከእኔ በፊት የክፍል ዘመናት ሰዎች (ሳይወጡ) በእርግጥ ያለፉ ሲኾኑ (ከመቃብር) እንድወጥጣ ታስፈራሩኛላችሁን?» ያለውን ሁለቱም (ወላጆቹ) አላህን የሚለምኑ ሲኾኑ (ባታምን) «ወዮልህ፡፡ እመን፡፡ የአላህ ቀጠሮ እርግጠኛ ነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(46,18,'46:18','እነዚህ እነዚያ ከጋኔንም ከሰውም ከእነርሱ በፊት ካለፉት ሕዝቦች ጋር ቃሉ በእነርሱ ላይ የተረጋገጠባቸው ናቸው፡፡ እነርሱ ከሳሪዎች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(46,19,'46:19','ለሁሉም ከሠሩት ሥራ ደረጃዎች አልሏቸው፡፡ ሥራዎቻቸውንም ይሞላላቸው ዘንድ (ይህንን መነዳቸው) እነርሱም አይበደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(46,20,'46:20','እነዚያም የካዱት በእሳት ላይ በሚጋፈጡ ቀን «ጣፋጮቻችሁን በአነስተኛይቱ ሕይወታችሁ አሳለፋችሁ፡፡ በእርሷም ተጣቀማችሁ፡፡ ስለዚህ በምድር ላይ ያለ አግባብ ትኮሩ በነበራችሁትና ታምጹ በነበራችሁት ዛሬ የውርደትን ቅጣት ትምመነዳላችሁ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,21,'46:21','የዓድንም ወንድም (ሁድን) ሕዝቦቹን በአሕቃፍ ባስጠነቀቀ ጊዜ አውሳለቸው፡፡ በስተፊቱና በስተኋላውም ብዙ አስፈራሪዎች በእርግጥ አልፈዋል፡፡ «ከአላህ በስተቀር አትግገዙ እኔ በእናነተ ላይ የከባድ ቀንን ቅጣት እፈራላችኋለሁና» በማለት (ባሰጠነቀቀ ጊዜ)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,22,'46:22','«ከአማልክቶቻችን ልታዞረን መጣህብን? ከውነተኞቹም እንደኾንክ የምትዝትብንን (ቅጣት) አምጣው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,23,'46:23','«ዕውቀቱ አላህ ዘንድ ብቻ ነው፡፡ ያን በእርሱ የተላክሁበትንም አደርስላችኋለሁ፡፡ ግን እኔ የምትሳሳቱ ሕዝቦች ኾናችሁ አያችኋለሁ» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,24,'46:24','ሸለቆዎቻቸውን ተቅጣጪ አግዳሚ ደመና ኾኖ ባዩትም ጊዜ «ይህ አዝናቢያችን የኾነ ደመና ነው» አሉ፡፡ (ሁድም) «አይደለም፤ እርሱ ያ በእርሱ የተቻኮላችሁበት መዐት ነው፡፡ በውስጧ አሳማሚ ስቃይ ያለባት ነፋስ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(46,25,'46:25','በጌታዋ ትዕዛዝ አንዳቹን ሁሉ ታጠፋለች» (አላቸው)፡፡ ከመኖሪያዎቻቸውም በስተቀር ምንም የማይታዩ ኾኑ፡፡ እንደዚሁ አመጸኞችን ሕዝቦች ሁሉ እንቀጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(46,26,'46:26','በዚያም እናንተን በእርሱ ባላስመቸንበት (ድሎት) በእርግጥ አስመቸናቸው፡፡ ለእነርሱም መስሚያንና ማያዎችን፣ ልቦችንም አደረግንላቸው፡፡ ግን መስሚያቸውና ማያዎቻቸው፣ ልቦቻቸውም ከእነሱ ምንም አልጠቀሟቸውም፡፡ በአላህ አንቀጾች ይክዱ ነበሩና፡፡ በእነርሱም ላይ ያ በእርሱ ይሳለቁበት የነበሩት ቅጣት ወረደባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,27,'46:27','ከከተሞችም እነዚያን በአካባቢያችሁ የነበሩትን በእርግጥ አጠፋን (ከክሕደታቸው) ይመለሱም ዘንድ አስረጂዎችን መላለስን፡፡');
INSERT INTO "sadiq_sani" VALUES(46,28,'46:28','እነዚያም መቃረቢያ ይኾኑ ዘንድ ከአላህ ሌላ አማልክት አድርገው የያዙዋቸው አይረዷቸውም ኖሯልን? በእርግጥ ከነሱ ራቁ፡፡ ይህም ውሸታቸውና (በልማድ) ይቀጥፉት የነበሩት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(46,29,'46:29','ከጋኔን የኾኑን ጭፍሮች ቁርኣንን የሚያዳምጡ ሲኾኑ ወዳንተ ባዞርን ጊዜም (አስታውስ)፡፡ በተጣዱትም ጊዜ ዝም ብላችሁ አዳምጡ ተባባሉ፡፡ በተጨረሰም ጊዜ አስጠንቃቂዎች ኾነው ወደ ሕዝቦቻቸው ተመለሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(46,30,'46:30','አሉም «ሕዝቦቻችን ሆይ! እኛ ከሙሳ በኋላ የተወረደን መጽሐፍ በፊቱ ያለውን (መጽሐፍ) የሚያረጋግጥ ወደ እውነትና ወደ ቀጥተኛ መንገድ የሚመራ የኾነን ሰማን፡፡');
INSERT INTO "sadiq_sani" VALUES(46,31,'46:31','ወገኖቻችን ሆይ! የአላህን ጠሪ ተቀበሉ፡፡ በእርሱም እመኑ፡፡ (አላህ) ከኀጢአቶቻችሁ ለእናንተ ይምራልና፡፡ ከአሳማሚ ቅጣትም ያድናችኋልና፡፡');
INSERT INTO "sadiq_sani" VALUES(46,32,'46:32','የአላህንም ጠሪ የማይቀበል ሰው በምድር ውስጥ የሚያመልጥ አይደለም፡፡ ከእርሱም ሌላ ለእርሱ ረዳቶች የሉትም፡፡ እነዚያ በግልጽ ስሕተት ውስጥ ናቸው» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(46,33,'46:33','ያ ሰማያትንና ምድርን የፈጠረ እነርሱንም በመፍጠሩ ያልደከመው አላህ ሙታንን ሕያው በማድረግ ላይ ቻይ መኾኑን አላስተዋሉምን? (በማስነሳት) ቻይ ነው፡፡ እርሱ በነገሩ ሁሉ ላይ ቻይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(46,34,'46:34','እነዚያም የካዱት ሰዎች በእሳት ላይ በሚቀረቡ ቀን «ይህ እውነት አይደለምን?» (ይባላሉ)፡፡ «እውነት ነው፤ በጌታችን ይኹንብን» ይላሉ፡፡ (አላህም) «ትክዱ በነበራችሁት ምክንያት ቅጣቱን ቅመሱ» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(46,35,'46:35','ከመልክተኞችም የቆራጥነት ባለቤቶች የኾኑት እንደ ታገሱ ሁሉ ታገስ፡፡ ለእነርሱም (ለሕዝቦችህ ቅጣት) አትቻኮል፡፡ ያንን የሚስፈራሩበትን ቅጣት በሚያዩ ቀን እነርሱ ከቀን አንዲትን ሰዓት አንጅ እንዳልቆዩ ይኾናሉ፡፡ (ይህ ቁርኣን) በቂ ግሳጼ ነው አመጸኞችም ሕዝቦች እንጅ (ሌሎች) ይጥጠፋሉን?');
INSERT INTO "sadiq_sani" VALUES(47,1,'47:1','እነዚያ የካዱ ከአላህ መንገድም የከለከሉ (አላህ) ሥራዎቻቸውን አጠፋባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,2,'47:2','እነዚያም ያመኑ፣ በጎዎችንም የሠሩ፣ በሙሐመድ ላይም የወረደው እርሱ ከጌታቸው ሲኾን እውነት ስለ ኾነ ያመኑ ከእነርሱ ላይ ኃጢአቶቻቸውን ያባብሳል፡፡ ኹኔታቸውንም ሁሉ ያበጃል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,3,'47:3','ይህ እነዚያ የካዱት ውሸትን የተከተሉ በመኾናቸውና እነዚያም ያመኑት ከጌታቸው የኾነን እውነት ስለተከተሉ ነው፡፡ እንደዚሁ አላህ ለሰዎች ምሳሌዎቻቸውን ያብራራል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,4,'47:4','እነዚያንም የካዱትን (በጦር ላይ) ባገኛችሁ ጊዜ ጫንቃዎችን በኀይል ምቱ፡፡ ባደካማችኋቸውም ጊዜ (አትግደሏቸው ማርኳቸው)፡፡ ማሰሪያውንም አጥብቁ፡፡ በኋላም ወይም በነጻ ትለቋቸዋላችሁ፤ ወይም ትበዧቸዋላችሁ፡፡ (ይህም) ጦሪቱ መሳሪያዋን እስከምትጥል ድረስ ነው፡፡ (ነገሩ) ይህ ነው፡፡ አላህም ቢሻ ከእነርሱ (ያለጦር) በተበቀለ ነበር፡፡ ግን ከፊላችሁን በከፊሉ ሊሞክር (በዚህ አዘዛችሁ)፡፡ እነዚያንም በአላህ መንገድ ላይ የተገደሉትን ሥራዎቻቸውን ፈጽሞ አያጠፋባቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(47,5,'47:5','በእርግጥ ይመራቸዋል፡፡ ኹኔታቸውንም ሁሉ ያበጃል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,6,'47:6','ገነትንም ያገባቸዋል፡፡ ለእነርሱ አስታውቋታል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,7,'47:7','እናንተ ያመናችሁ ሆይ! አላህን (ሃይማኖቱን) ብትረዱ፤ ይረዳችኋል፡፡ ጫማዎቻችሁንም ያደላድላል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,8,'47:8','እነዚያም የካዱት ለእነርሱ ጥፋት ተገባቸው፡፡ ሥራዎቻቸውንም አጠፋባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,9,'47:9','ይህ እነርሱ አላህ ያወረደውን ስለጠሉ ነው፡፡ ስለዚህ ሥራዎቻቸውን አበላሸባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,10,'47:10','የእነዚያ ከእነርሱ በፊት የነበሩት (ከሓዲዎች) መጨረሻ እንዴት እንደ ነበረ ያዩ ዘንድ በምድር ለይ አልኼዱምን? አላህ በእነርሱ ላይ (ያላቸውን ሁሉ) አጠፋባቸው፡፡ ለከሐዲዎችም ሁሉ ብጤዎችዋ አልሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,11,'47:11','ይህ አላህ የእነዚያ ያመኑት ሰዎች ረዳት ስለኾነና ከሓዲዎችም ለእነርሱ ረዳት ስለሌላቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,12,'47:12','አላህ እነዚያን ያመኑትንና መልካሞችን የሠሩትን ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች ያገባቸዋል፡፡ እነዚያም የካዱት (በቅርቢቱ ዓለም) ይጣቀማሉ፡፡ እንስሳዎችም እንደሚበሉ ይበላሉ፡፡ እሳትም ለእነርሱ መኖሪያቸው ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(47,13,'47:13','ከከተማም እርሷ ከዚያች ካወጣችህ ከተማህ ይበልጥ በኀይል ጠንካራ የኾነች (ባለቤቶችዋን) ያጠፋናቸው ብዙ ናት፡፡ ለእነርሱም ረዳት አልነበራቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(47,14,'47:14','ከጌታው በኾነች አስረጅ ላይ የኾነ ምእመን ክፉ ሥራቸው ለእነርሱ እንደ ተሸለመላቸውና ዝንባሌዎቻቸውን እንደ ተከተሉት ነውን?');
INSERT INTO "sadiq_sani" VALUES(47,15,'47:15','የዚያች ጥንቁቆቹ ተስፋ የተሰጧት ገነት ምስል በውስጧ ሺታው ከማይለውጥ ውሃ ወንዞች፣ ጣዕሙ ከማይለወጥ ወተትም ወንዞች፣ ለጠጪዎች ሁሉ ጣፋጭ ከኾነች የወይን ጠጅም ወንዞች ከተነጠረ ማርም ወንዞች አልሉባት፡፡ ለእነርሱም በውስጧ ከፍሬዎች ሁሉ (በያይነቱ) ከጌታቸው ምሕረትም አልላቸው፡፡ (በዚች ገነት ውስጥ ዘውታሪ የኾነ ሰው) እርሱ በእሳት ውስጥ ዘውታሪ እንደኾነ ሰው፣ ሞቃትንም ውሃ እንደተጋቱ፣ አንጀቶቻቸውንም ወዲያውኑ እንደቆራረጠው ነውን? (አይደለም)፡፡');
INSERT INTO "sadiq_sani" VALUES(47,16,'47:16','ከእነርሱም ወዳንተ የሚያዳምጡ አልሉ፡፡ ከአንተም ዘንድ በወጡ ጊዜ ለእነዚያ ዕውቀት ለተሰጡት «አሁን ምን አለ?» ይላሉ፡፡ እነዚህ እነዚያ በልቦቻቸው ላይ አላህ ያተመባቸው ዝንባሌዎቻቸውንም የተከተሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,17,'47:17','እነዚያም የተመሩት (አላህ) መመራትን ጨመረላቸው፡፡ (ከእሳት) መጥጠበቂያቸውንም ሰጣቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,18,'47:18','ሰዓቲቱንም ድንገት የምትመጣባቸው መኾንዋን እንጅ ይጠባበቃሉን? ምልክቶችዋም በእርግጥ መጥተወል፡፡ በመጣቻቸውም ጊዜ ማስታወሳቸው ለእነርሱ እንዴት ይጠቅማቸዋል?');
INSERT INTO "sadiq_sani" VALUES(47,19,'47:19','እነሆ ከአላህ ሌላ አምላክ አለመኖሩንም ዕወቅ፡፡ ስለ ስህተትህም ለምእመናንም ምሕረትን ለምን፡፡ አላህም መዘዋወሪያችሁን፣ መርጊያችሁንም ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,20,'47:20','እነዚያም ያመኑት ሰዎች (መታገል ያለባት) «ሱራ አትወርድም ኖሮአልን?» ይላሉ፡፡ የጠነከረችም ሱራ በተወረደችና በውስጧም መጋደል በተወሳ ጊዜ እነዚያን በልቦቻቸው ውስጥ (የንፍቅና) በሺታ ያለባቸውን ሰዎች ከሞት የኾነ መከራ በርሱ ላይ እንደ ወደቀበት ሰው አስተያየት ወዳንተ ሲመለከቱ ታያቸዋለህ፡፡ ለእነሱም ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,21,'47:21','ታዛዥነትና መልካም ንግግር (ይሻላቸዋል) ትዕዛዙም ቁርጥ በኾነ ጊዜ ለአላህ (ትዕዛዝ) እውነተኞች በኾኑ ኖሮ ለእነርሱ የተሻለ በኾነ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(47,22,'47:22','ብትሽሾሙም በምድር ላይ ማበላሸትን ዝምድናችሁንም መቁረጥን ከጀላችሁን?');
INSERT INTO "sadiq_sani" VALUES(47,23,'47:23','እነዚህ እነዚያ አላህ የረገማቸው፣ ያደነቆራቸውም፣ ዓይኖቻቸውንም ያወራቸው ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,24,'47:24','ቁርኣንንም አያስተነትኑምን? በእውነቱ በልቦቻቸው ላይ ቁልፎቿ አልሉባትን?');
INSERT INTO "sadiq_sani" VALUES(47,25,'47:25','እነዚያ ለእነርሱ ቅኑ መንገድ ከተብራራላቸው በኋላ ወደ ኋላቸው የተመለሱት ሰይጣን ለእነርሱ መመለሳቸውን ሸለመላቸው፡፡ ለእነርሱም አዝዘናጋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,26,'47:26','ይህ እነርሱ ለእነዚያ አላህ ያወረደውን ለጠሉት «በነገሩ ከፊል እንታዘዛችኋለን» ያሉ በመኾናቸው ነው፡፡ አላህም መደበቃቸውን ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,27,'47:27','መላእክትም ፊቶቻቸውንና ጀርባዎቻቸውን የሚመቱ ሲኾኑ በገደሉዋቸው ጊዜ (ኹኔታቸው) እንዴት ይኾናል?');
INSERT INTO "sadiq_sani" VALUES(47,28,'47:28','ይህ እነርሱ አላህን ያስቆጣውን ነገር ስለ ተከተሉ ውዴታውንም ስለ ጠሉ ነው፡፡ ስለዚህ ሥራዎቻቸውን አበላሸባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(47,29,'47:29','እነዚያ በልቦቻቸው ውስጥ በሽታ ያለባቸው አላህ ቂሞቻቸውን አለማውጣቱን ጠረጠሩን?');
INSERT INTO "sadiq_sani" VALUES(47,30,'47:30','በሻንም ኖሮ እነርሱን ባሳየንህና በምልክታቸውም በእርግጥ ባወቅሃቸው ነበር፡፡ ንግግርንም በማሸሞራቸው በእርግጥ ታውቃቸዋለህ፡፡ አላህም ሥራዎቻችሁን ያውቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,31,'47:31','ከእናንተም ታጋዮቹንና ታጋሾቹን እስከምናውቅ፣ ወሬዎቻችሁንም እስከምንገልጽ ድረስ በእርግጥ እንሞክራችኋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(47,32,'47:32','እነዚያ የካዱና ከአላህም መንገድ ያገዱ ለእነርሱም ቅኑ መንገድ ከተገለጸላቸው በኋላ መልክተኛውን የተከራከሩ አላህን በምንም አይጎዱትም፡፡ ሥራዎቻቸውንም በእርግጥ ያበላሻል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,33,'47:33','እናንተ ያመናችሁ ሆይ! አላህን ተገዙ፡፡ መልክተኛውንም ታዘዙ፡፡ ሥራዎቻችሁንም አታበላሹ፡፡');
INSERT INTO "sadiq_sani" VALUES(47,34,'47:34','እነዚያ የካዱ፣ ከአላህም መንገድ ያገዱ፣ ከዚያም እነርሱ ከሓዲዎች ኾነው የሞቱ አላህ ለእነርሱ በፍጹም አይምርም፡፡');
INSERT INTO "sadiq_sani" VALUES(47,35,'47:35','እናንተም አሸናፊዎቹ ስትኾኑ አላህ ከእናንተ ጋር ሲሆን አትድከሙ፡፡ ወደ ዕርቅም አትጥሩ፡፡ ሥራዎቻችሁንም ፈጽሞ አያጎድልባችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(47,36,'47:36','ቅርቢቱ ሕይወት ጫወታና ዛዛታ ብቻ ናት፡፡ ብታምኑም ብትጠነቀቁም ምንዳዎቻችሁን ይሰጣችኋል፡፡ ገንዘቦቻችሁንም (ሁሉ) አይጠይቃችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(47,37,'47:37','እርሷን ቢጠይቃችሁና ችክ ቢልባችሁ ትሰስታላችሁ፡፡ ቂሞቻችሁንም ያወጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(47,38,'47:38','ንቁ! እናንተ እነዚያ በአላህ መንገድ ትለግሱ ዘንድ የምትጥጠሩ ናችሁ፡፡ ከእናንተም ውስጥ የሚሰስት ሰው አልለ፡፡ የሚሰስትም ሰው የሚሰስተው በራሱ ላይ ብቻ ነው፡፡ አላህም ከበርቴ ነው፡፡ እናንተም ድኾች ናችሁ፡፡ ብትሸሹም ሌሎችን ሕዝቦች ይለውጣል፡፡ ከዚያም (ባለመታዘዝ) ብጤዎቻችሁ አይኾኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(48,1,'48:1','እኛ ላንተ ግልጽ የኾነን መክፈት ከፈትንልህ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,2,'48:2','አላህ ከኃጢአትህ ያለፈውንና የሚመጣውን ላንተ ሊምር ጸጋውንም ባንተ ላይ ሊሞላ ቀጥተኛውንም መንገድ ሊመራህ (ከፈተልህ)፡፡');
INSERT INTO "sadiq_sani" VALUES(48,3,'48:3','አላህ ብርቱን እርዳታ ሊረዳህም (ከፈተልህ)፡፡');
INSERT INTO "sadiq_sani" VALUES(48,4,'48:4','እርሱ ያ በምእምናን ልቦች ውስጥ ከእምነታቸው ጋር እምነትን ይጨምሩ ዘንድ እርጋታን ያወረደ ነው፡፡ ለአላህም የሰማያትና የምድር ሰራዊቶች አልሉት፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,5,'48:5','ምእምናንንና ምእምናትን ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ ሊያገባቸው ከነርሱም ኃጢአቶቻቸውን ሊያብስላቸው (በትግል አዘዛቸው)፡፡ ይህም ከአላህ ዘንድ የኾነ ታላቅ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,6,'48:6','በአላህም ክፉ ጥርጣሬን ተጠራጣሪዎቹን መናፍቃንና መናፍቃትን፣ ወንዶች አጋሪዎቻችንና ሴቶች አጋሪዎችንም ያሰቃይ ዘንድ (በትግል አዘዘ)፡፡ በእነርሱ ላይ ክፉ ከባቢ አለባቸው፡፡ በእነርሱም ላይ አላህ ተቆጣባቸው፡፡ ረገማቸውም፡፡ ለእነርሱም ገሀነምን አዘጋጀላቸው፡፡ መመለሻነትዋም ከፋች፡፡');
INSERT INTO "sadiq_sani" VALUES(48,7,'48:7','ለአላህም የሰማያትና የምድር ሰራዊቶች አልሉት፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,8,'48:8','እኛ መስካሪ አብሳሪና አስፈራሪም አድርገን በእርግጥ ላክንህ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,9,'48:9','በአላህ ልታምኑ፣ በመልክተኛውም (ልታምኑ)፣ ልትረዱትም፣ ልታከብሩትም፣ (አላህን) በጧትና ከቀትር በኋላ ልታወድሱትም (ላክነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(48,10,'48:10','እነዚያ ቃል ኪዳን የሚጋቡህ ቃል ኪዳን የሚጋቡት አላህን ብቻ ነው፡፡ የአላህ እጅ (ኀይሉ) ከእጆቻቸው በላይ ነው፡፡ ያፈረሰም ሰው የሚያፈርሰው በነፍሱ ላይ ብቻ ነው፡፡ በእርሱ ላይ አላህን ቃል ኪዳን የተጋባበትን የሞላም ሰው ታላቅ ምንዳን በእርግጥ ይሰጠዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(48,11,'48:11','ከአዕራብ ሰዎች እነዚያ (ከዘመቻ) ወደ ኋላ የቀሩት ለአንተ «ገንዘቦቻችንና ቤተሰቦቻችን አስቸገሩን፤ ስለዚህ ምሕረትን ለምንልን፤» ይሉሃል፡፡ በልቦቻቸው ውስጥ የሌለውን ነገር በምላሶቻቸው ይናገራሉ፡፡ (አላህ) «በእናንተ ላይ ጉዳትን ቢሻ ወይም በእናንተ መጥቀምን ቢሻ ከአላህ (ለማገድ) ለእናንተ አንዳችን የሚችል ማነው? በእውነቱ አላህ፤በምትሠሰሩት ሁሉ ውስጥ ዐዋቂ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,12,'48:12','ይልቁንም መልክተኛውና ምእመናኖቹ ወደ ቤተሰቦቻቸው ፈጽሞ አለመመለሳቸውን ጠረጠራችሁ፡፡ ይህም በልቦቻችሁ ውስጥ ተሸለመላችሁ፡፡ መጥፎንም መጠርጠር ጠረጠራችሁ፡፡ ጠፊዎች ሕዝቦችም ኾናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,13,'48:13','በአላህና በመልክተኛው ያላመነም ሰው እኛ ላከሓዲዎች እሳትን አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(48,14,'48:14','የሰማያትና የምድር ንግሥናም የአላህ ነው፡፡ ለሚሻው ሰው ይምራል፤ የሚሻውንም ይቀጣል፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,15,'48:15','ወደ ዘረፋዎች ልትይዟት በኼዳችሁ ጊዜ እነዚያ ወደ ኋላ የቀሩት ሰዎች «ተዉን እንከተላችሁ» ይሏችኋል፡፡ (በዚህም) የአላህን ቃል ሊለውጡ ይፈልጋሉ፡፡ «ፈጽሞ አትከተሉንም፡፡ ይህን (ቃል) ከዚህ በፊት አላህ ብሏል» በላቸው፡፡ ይልቁንም «ትመቀኙናላችሁ» ይላሉም፡፡ በእውነት እነርሱ ጥቂትን እንጅ የማያውቁ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,16,'48:16','ከአዕራቦች ወደ ኋላ ለቀሩት በላቸው፡- «የብርቱ ጭካኔ ባለቤት ወደ ኾኑ ሕዝቦች (ውጊያ) ወደ ፊት ትጥጠራላችሁ፡፡ ትጋደሏቸዋላችሁ፤ ወይም ይሰልማሉ፡፡ ብትታዘዙም አላህ መልካም ምንዳን ይሰጣችኋል፡፡ ከአሁን በፊት እንደ ሸሻችሁ ብትሸሹም አሳማሚን ቅጣት ይቀጣችኋል፡፡»');
INSERT INTO "sadiq_sani" VALUES(48,17,'48:17','በዕውር ላይ (ከዘመቻ በመቅቱ) ኃጢአት የለበትም፡፡ በአንካሳም ላይ ኃጢአት የለበትም፡፡ በበሽተኛም ላይ ኃጢአት የለበትም፡፡ አላህንና መልክተኛውንም የሚታዘዝ ሰው ከሥሮቻቸው ወንዞች የሚፈሱባቸውን ገነቶች ያገባዋል፡፡ የሚሸሸውንም ሰው አሳማሚውን ቅጣት ይቀጣዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(48,18,'48:18','ከምእምናኖቹ በዛፊቱ ሥር ቃል ኪዳን በሚጋቡህ ጊዜ አላህ በእርግጥ ወደደ፡፡ በልቦቻቸውም ውስጥ ያለውን ዐወቀ፡፡ በእነርሱም ላይ እርጋታን አወረደ፡፡ ቅርብ የኾነንም መክፈት መነዳቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,19,'48:19','ብዙዎች ዘረፋዎችንም የሚወስዷቸው የኾኑን (መነዳቸው)፡፡ አላህም አሸናፊ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,20,'48:20','አላህ ብዙዎችን ዘረፋዎች የምትወስዷቸው የኾኑን ተስፋ አደረገላችሁ፡፡ ይህችንም ለእናንተ አስቸኮለላችሁ፡፡ ከእናንተም የሰዎችን እጆች ከለከለላችሁ፡፡ (ልታመሰግኑትና) ለምእምናንም ምልክት እንድትኾን ቀጥተኛውን መንገድ ይመራችሁም ዘንድ (ይህንን ሠራላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(48,21,'48:21','ሌላይቱንም በእርሷ ላይ ገና ያልቻላችኋትን አላህ ያወቃትን (ለእናንተ አዘጋጃት)፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,22,'48:22','እነዚያም የካዱት (የመካ ሰዎች በሑደይቢያ) በተወጉወችሁ ኖሮ ( ለሺሺት) ጀርባዎችን ባዞሩ ነበር፡፡ ከዚያም ዘመድም ረዳትም አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(48,23,'48:23','አላህ ያችን ከዚህ በፊት በእርግጥ ያለፈችውን ልማድ ደነገገ፡፡ ለአላህም ልማድ ለውጥን አታገኝም፡፡');
INSERT INTO "sadiq_sani" VALUES(48,24,'48:24','እርሱም ያ በመካ ውስጥ በእነርሱ ላይ ካስቻለችሁ በኋላ እጆቻቸውን ከእናንተ ላይ እጆቻችሁንም ከእነርሱ ላይ የከለከለ ነው፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,25,'48:25','እነርሱ እነዚያ የካዱ፣ ከተከበረው መስጊድም የከለከሏችሁ፣ መስዋእቱንም የታሰረ ሲኾን በስፍራው እንዳይደርስ (የከለከሉ) ናቸው፡፡ የማታውቋቸው የኾኑ ምእመናን ወንዶችና ምእምናት ሴቶች (ከከሓዲዎቹ ጋር) ባልነበሩ ያለ ዕውቀት እነርሱን መርገጣችሁና ከእነረሱ (በኩል) መከፋት የሚነካችሁ ባልኾነ ኖሮ (እጆቻችሁን ባላገድን ነበር) አላህ የሚሻውን ሰው በእዝነቱ ውስጥ ያገባ ዝንድ (እጆቻችሁን አገደ)፡፡ በተለዩ ኖሮ እነዚያን ከእነርሱ ውስጥ የካዱትን አሳማሚን ቅጣት በቀጣናቸው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(48,26,'48:26','እነዚያ የካዱት ደራይቱን የመሀይምነቲቱን ደራ በልቦቻቸው ውስጥ ባደረጉ ጊዜ (በቀጣናቸው ነበር)፡፡ አላህም በመልክተኛው ላይና በምእምናኖቹ ላይ እርጋታውን አወረደ፡፡ መጥጠበቂያይቱንም ቃል አስያዛቸው፡፡ በእርሷም ተገቢዎች ባለቤቶችዋም ነበሩ፡፡ አላህም በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(48,27,'48:27','አላህ ለመልክተኛው ሕልሙን እውነተኛ ሲኾን አላህ የሻ እንደ ኾነ ጸጥተኞች ኾናችሁ ራሶቻችሁን ላጭታችሁ፣ አሳጥራችሁም የማትፈሩ ስትኾኑ የተከበረውን መስጊድ በእርግጥ ትገባላችሁ፤ ያለውን በእርግጥ እውነት አደረገለት፡፡ (ከእርቁ) ያላወቃችሁትንም ነገር ዐወቀ፡፡ ከዚህም በፊት ቅርብን መክፈት አደረገ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,28,'48:28','እርሱ ያ መልክተኛውን በመሪ መጽሐፍና በእውነተኛ ሃይማኖት በሃይማኖት ሁሉ ላይ ሊያልቀው የላከ ነው፡፡ መስካሪም በአላህ በቃ፡፡');
INSERT INTO "sadiq_sani" VALUES(48,29,'48:29','የአላህ መልክተኛ ሙሐመድ እነዚያም ከእርሱ ጋር ያሉት (ወዳጆቹ) በከሓዲዎቹ ላይ ብርቱዎች በመካከላቸው አዛኞች ናቸው፡፡ አጎንባሾች፣ ሰጋጆች ኾነው ታያቸዋለህ፡፡ ከአላህ ችሮታንና ውዴታን ይፈልጋሉ፡፡ ምልክታቸው ከስግደታቸው ፈለግ ስትኾን በፊቶቻቸው ላይ ናት፡፡ ይህ በተውራት (የተነገረው) ጠባያቸው ነው፡፡ በኢንጂልም ውስጥ ምሳሌያቸው ቀንዘሉን አንደአወጣ አዝመራና፣ (ቀንዘሉ) እንዳበረታው፣ እንደወፈረምና፣ ገበሬዎቹን የሚያስደንቅ ኾኖ በአገዳዎቹ ላይ ተስተካክሎ እንደ ቆመ (አዝመራ) ነው፡፡ (ያበረታቸውና ያበዛቸው) ከሓዲዎችን በእነርሱ ሊያስቆጭ ነው፡፡ አላህም እነዚያን ያመኑትንና ከእነርሱ በጎዎችን የሠሩትን ምሕረትንና ታላቅ ምንዳን ተስፋ አድርጎላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(49,1,'49:1','እናንተ ያመናችሁ ሆይ! በአላህና በመልክተኛው ፊት (ነፍሶቻችሁን) አታስቀድሙ፡፡ አላህንም ፍሩ፡፡ አላህ ሰሚ ዐዋቂ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(49,2,'49:2','እናንተ ያመናችሁ ሆይ! ድምጾቻችሁን ከነቢዩ ድምጽ በላይ ከፍ አታድርጉ፡፡ ከፊላችሁም ለከፊሉ እንደሚጮህ በንግግር ለርሱ አትጩሁ፡፡ እናንተ የማታውቁ ስትኾኑ ሥራዎቻችሁ እንዳይበላሹ (ተከልከሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(49,3,'49:3','እነዚያ በአላህ መልክተኛ ዘንድ ድምጾቻቸውን ዝቅ የሚያደርጉ እነዚህ እነዚያ አላህ ልቦቻቸውን ለፍርሃት የፈተናቸው ናቸው፡፡ ለእነርሱም ምሕረትና ታላቅ ምንዳ አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,4,'49:4','እነዚያ ከክፍሎቹ ውጭ ኾነው የሚጠሩህ አብዛኞቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(49,5,'49:5','ወደእነርሱም እስክትወጣ ድረስ እነርሱ በታገሱ ኖሮ ለእነርሱ በላጭ በኾነ ነበር፡፡ አላህም እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,6,'49:6','እላንተ ያመናችሁ ሆይ! ነገረኛ ወሬን ቢያመጣላችሁ በስህተት ላይ ኾናችሁ ሕዝቦችን እንዳትጎዱና በሠራችሁት ነገር ላይ ተጸጸቾች እንዳትኾኑ አረጋግጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(49,7,'49:7','በውስጣችሁም የአላህ መልክተኛ መኖሩን ዕወቁ፡፡ ከነገሩ በብዙው ቢከተላችሁ ኖሮ በእርግጥ በተቸገራችሁ ነበር፡፡ ግን አላህ አምነትን ወደናንተ አስወደደ፡፡ በልቦቻችሁም ውሰጥ አጌጠው፡፡ ክህደትንና አመጽንም እንቢተኛነትንም ወደናንተ የተጠላ አደረገ፡፡ እነዚያ (እምነትን የወደዱና ክሕደተን የጠሉ) እነርሱ ቅኖቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,8,'49:8','ከአላህ በኾነው ችሮታና ጸጋ (ቅኖች ናቸው)፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,9,'49:9','ከምዕምናንም የኾኑ ሁለት ጭፍሮች ቢጋደሉ በመካከላቸው አስታርቁ፡፡ ከሁለቱ አንደኛይቱም በሌላይቱ ላይ ወሰን ብታልፍ ያችን ወሰን የምታልፈውን ወደ አላህ ትዕዛዝ እስከምትመለስ ድረስ ተጋደሉ፡፡ ብትመለስም በመካከላቸው በትክክል አስታርቁ፡፡ በነገሩ ሁሉ አስተካክሉም፡፡ አላህ አስተካካዮችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(49,10,'49:10','ምእመናኖች ወንድማመቾች ናቸው፡፡ በሁለት ወንድሞቻችሁም መካከል አስታርቁ፡፡ ይታዘንላችሁም ዘንድ አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(49,11,'49:11','እናንተ ያመናችሁ ሆይ! ወንዶች ከወንዶች አይቀልዱ፡፡ ከእነርሱ የበለጡ ሊኾኑ ይከጀላልና፡፡ ሴቶችም ከሴቶች (አይሳለቁ)፡፡ ከእነርሱ የበለጡ ሊኾኑ ይከጀላልና፡፡ ነፍሶቻችሁንም አታነውሩ፡፡ በመጥፎ ስሞችም አትጠራሩ፡፡ ከእምነት በኋላ የማመጽ ስም ከፋ፡፡ ያልተጸጸተም ሰው እነዚያ እነርሱ በዳዮቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,12,'49:12','እናንተ ያመናችሁ ሆይ! ከጥርጣሬ ብዙውን ራቁ፤ ከጥርጣሬ ከፊሉ ኃጢአት ነውና፡፡ ነውርንም አትከታተሉ፡፡ ከፊላችሁም ከፊሉን አይማ፡፡ አንደኛችሁ የወንድሙን ስጋ የሞተ ኾኖ ሊበላው ይወዳልን? (መብላቱን) ጠላችሁትም፤ (ሐሜቱንም ጥሉት)፡፡ አላህንም ፍሩ፡፡ አላህ ጸጸትን ተቀባይ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(49,13,'49:13','እናንተ ሰዎች ሆይ! እኛ ከወንድና ከሴት ፈጠርናችሁ፡፡ እንድትተዋወቁም ጎሳዎችና ነገዶች አደረግናችሁ፡፡ አላህ ዘንድ በላጫችሁ በጣም አላህን ፈሪያችሁ ነው፡፡ አላህ ግልጽን ዐዋቂ ውስጥንም ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,14,'49:14','የዐረብ ዘላኖች «አምነናል» አሉ፡፡ «አላመናችሁም፤ ግን ሰልመናል በሉ፡፡ «እምነቱም በልቦቻችሁ ውስጥ ገና (ጠልቆ) አልገባም፡፡ አላህንና መልክተኛውንም ብትታዘዙ ከሥራዎቻችሁ ምንንም አያጎድልባችሁም፡፡ አላህ መሓሪ አዛኝ ነውና» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,15,'49:15','(እውነተኛዎቹ) ምእምናን እነዚያ በአላህና በመልእከተኛው ያመኑት ከዚያም ያልተጠራጠሩት በገንዘቦቻቸውና በነፍሶ ቻቸውም በአላህ መንገድ የታገሉት ብቻ ናቸው፡፡ እነዚያ እነሱ እውነተኞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,16,'49:16','«አላህ በሰማያት ውስጥ ያለውንና በምድር ውስጥ ያለውን ሁሉ የሚያውቅ ሲኾን አላህን በሃይማኖታችሁ ታስታውቁ ታላችሁን?» በላቸው፡፡ አላህም በነገሩ ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,17,'49:17','በመስለማቸው በአንተ ላይ ይመጻደቃሉ «በስልምናችሁ በኔ ላይ አትመጻደቁ፤ ይልቁንም አላህ ወደ እምነት ስለ መራችሁ ይመጸደቅባችኋል፤ እውነተኞች ብትኾኑ» (መመጻደቅ ለአላህ ነው) በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(49,18,'49:18','አላህ የሰማያትንና የምድርን ሩቅ ምሰጢር ያውቃል፤ አላህም የምትሰሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,1,'50:1','ቀ (ቃፍ) በተከበረው ቁርኣን እምላለሁ (በሙሐመድ አላመኑም)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,2,'50:2','ይልቁንም ከነሱ ጎሳ የኾነ አስፈራሪ ስለ መጣላቸው ተደነቁ፡፡ ከሓዲዎቹም «ይህ አስደናቂ ነገር ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(50,3,'50:3','«በሞትንና ዐፈር በኾን ጊዜ (እንመለሳለን?) ይህ ሩቅ የኾነ መመለስ ነው፤» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,4,'50:4','ከእነርሱ (አካል) ምድር የምታጎድለውን በእርግጥ ዐውቀናል፡፡ እኛም ዘንድ ጠባቂ መጽሐፍ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(50,5,'50:5','(አልተመለከቱም)፡፡ ይልቁንም በቁርኣን በመጣላቸው ጊዜ አስተባበሉ፡፡ እነርሱም በተማታ ነገር ውሰጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,6,'50:6','ወደ ሰማይም ከበላያቸው ስትኾን ለእርሷ ምንም ቀዳዳዎች የሌሏት ኾና እንዴት እንደ ገነባናትና (በከዋክብት) እንዳጌጥናት አልተመለከቱምን?');
INSERT INTO "sadiq_sani" VALUES(50,7,'50:7','ምድርንም ዘረጋናት፡፡ በእርሷም ውስጥ ጋራዎችን ጣልንባት፡፡ በውስጧም ከሚያስደስት ዓይነት ሁሉ አበቀልን፡፡');
INSERT INTO "sadiq_sani" VALUES(50,8,'50:8','(ይህንን ያደረግነው) ተመላሽ ለኾነ ባሪያ ሁሉ ለማሳየትና ለማስገንዘብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,9,'50:9','ከሰማይም ብሩክን ውሃ አወረድን፡፡ በእርሱም አትክልቶችንና የሚታጨድን (አዝመራ) ፍሬ አበቀልን፡፡');
INSERT INTO "sadiq_sani" VALUES(50,10,'50:10','ዘምባባንም ረዣዢም ለእርሷ የተደራረበ እንቡጥ ያላት ስትኾን (አበቀልን)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,11,'50:11','ለባሮቹ ሲሳይ ትኾን ዘንድ (አዘጋጀናት)፡፡ በእርሱም የሞተችን አገር ሕያው አደረግንበት፡፡ (ከመቃብር) መውጣትም እንደዚሁ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,12,'50:12','ከእነርሱ በፊት የኑሕ ሕዝቦችና የረሰስ ሰዎች፤ ሰሙድም አስተባበሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(50,13,'50:13','ዓድም ፈርዖንም፤ የሉጥ ወንድሞችም፤');
INSERT INTO "sadiq_sani" VALUES(50,14,'50:14','የአይከት ሰዎችም የቱብበዕ ሕዝቦችም ሁሉም መልከተኞቹን አስተባበሉ፡፡ ዛቻዬም ተረጋገጠባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,15,'50:15','በፊተኛው መፍጠር ደከምን? በእውነቱ እነርሱ ከአዲስ መፍጠር በመጠራጠር ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,16,'50:16','ሰውንም ነፍሱ (በሐሳቡ) የምታጫውተውን የምናውቅ ስንኾን በእርግጥ ፈጠርነው፤ እኛም ከደም ጋኑ ጅማት ይበልጥ ወደርሱ ቅርብ ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(50,17,'50:17','ሁለቱ ቃል ተቀባዮች (መላእክት) ከቀኝና ከግራ ተቀማጮች ኾነው በሚቀበሉ ጊዜ (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,18,'50:18','ከቃል ምንም አይናገርም በአጠገቡ ተጠባባቂና ዝግጁ የኾኑ (መላእክት) ያሉበት ቢኾን እንጅ፡፡');
INSERT INTO "sadiq_sani" VALUES(50,19,'50:19','የሞትም መከራ እውነቱን ነገር ታመጣለች፡፡ (ሰው ሆይ)፡- «ይህ ያ ከርሱ ትሸሸው የነበርከው ነው» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,20,'50:20','በቀንዱም ውስጥ ይንነፋል፡፡ ያ (ቀን) የዛቻው (መፈጸሚያ) ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,21,'50:21','ነፍስም ሁሉ ከእርሷ ጋር ነጂና መስካሪ ያለባት ኾና ትመጣለች፡፡');
INSERT INTO "sadiq_sani" VALUES(50,22,'50:22','«ከዚህ ነገር በእርግጥ በዝንጋቴ ውስጥ ነበርክ፡፡ ሺፋንህንም ካንተ ላይ ገለጥንልህ፡፡ ስለዚህ ዛሬ ዓይንህ ስለታም ነው» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,23,'50:23','ቁራኛውም (መልአክ) «ይህ ያ እኔ ዘንድ ያለው ቀራቢ ነው» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(50,24,'50:24','«ሞገደኛ ከሓዲን ሁሉ በገሀነም ውስጥ ጣሉ፡፡»');
INSERT INTO "sadiq_sani" VALUES(50,25,'50:25','«ለበጎ ሥራ ከልካይ፣ በዳይ፣ ተጠራጣሪ የኾነን ሁሉ፤» (ጣሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,26,'50:26','«ያንን ከአላህ ጋር ሌላን አምላክ ያደረገውን በብርቱ ቅጣት ውስጥ ጣሉት፤» (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,27,'50:27','ቁራኛው (ሰይጣን) «ጌታችን ሆይ! እኔ አላሳሳትኩትም፡፡ ግን (ራሱ) በሩቅ ስሕተት ውስጥ ነበር» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(50,28,'50:28','(አላህ) «ወደእናንተ ዛቻን በእርግጥ ያስቀደምኩ ስኾን እኔ ዘንድ አትጨቃጨቁ» ይላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(50,29,'50:29','«ቃሉ እኔ ዘንድ አይለወጥም፡፡ እኔም ለባሮቼ ፈጽሞ በዳይ አይደለሁም» (ይላቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,30,'50:30','ለገሀነም «ሞላሽን? የምንልበትንና ጭማሪ አለን? የምትልበትን ቀን» (አስጠንቅቃቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,31,'50:31','ገነትም አላህን ለፈሩት እሩቅ ባልኾነ ስፍራ ትቅቀረባለች፡፡');
INSERT INTO "sadiq_sani" VALUES(50,32,'50:32','«ይህ ወደ አላህ ተመላሽና (ሕግጋቱን) ጠባቂ ለኾነ ሁሉ የተቀጠራችሁት ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,33,'50:33','«አልረሕማንን በሩቁ ኾኖ ለፈራና በንጹሕ ልብ ለመጣ» (ትቅቀረባለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,34,'50:34','«በሰላም ግቧት ይህ የመዘውተሪያ ቀን ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,35,'50:35','ለነርሱ በውስጧ ሲኾኑ የሚሹት ሁሉ አልላቸው፡፡ እኛም ዘንድ ጭማሪ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(50,36,'50:36','ከእነርሱም (ከቁረይሾች) በፊት ከክፍለ ዘመናት ሕዝቦች ብዙዎችን እነርሱ በኀያልነት ከእነርሱ ይበልጥ የበረቱ የኾኑትን (ማምለጫ ፍለጋ) በየአገሮቹም የመረመሩትን አጥፍተናል፡፡ ማምለጫ አለን?');
INSERT INTO "sadiq_sani" VALUES(50,37,'50:37','በዚህ ውሰጥ ለእርሱ ልብ ላለው ወይም እርሱ (በልቡ) የተጣደ ኾኖ (ወደሚነበብለት) ጆሮውን ለጣለ ሰው ግሳጼ አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(50,38,'50:38','ሰማያትንና ምድርን በመካከላቸው ያለውንም ሁሉ በስድስት ቀናት ውስጥ በእርግጥ ፈጠርን፡፡ ድካምም ምንም አልነካንም፡፡');
INSERT INTO "sadiq_sani" VALUES(50,39,'50:39','በሚሉትም ላይ ታገሥ፤ ጌታህንም ከማመስገን ጋር ፀሐይ ከመውጣቷ በፊት ከመግባቷም በፊት አወድሰው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,40,'50:40','ከሌሊቱም አወድሰው ከስግደቶችም በኋላዎች (አወድሰው)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,41,'50:41','(የሚነገርህን) አዳምጥም፡፡ ጠሪው ከቅርብ ስፍራ በሚጠራበት ቀን (ከመቃብራቸው ይወጣሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(50,42,'50:42','ጩኸቲቱን በእውነት የሚሰሙበት ቀን ያ (ከመቃብር) የመውጫው ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,43,'50:43','እኛ እኛው ሕያው እናደርጋለን፡፡ እንገድላለንም፡፡ መመለሻም ወደእኛ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,44,'50:44','የሚፈጥኑ ኾነው ምድር ከእነርሱ ላይ የምትሰነጣጠቅበት ቀን (የመውጫው ቀን ነው)፡፡ ይህ በእኛ ላይ ገር የኾነ መሰብሰብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(50,45,'50:45','እኛ የሚሉትን ሁሉ ዐዋቂ ነን፡፡ አንተም በእነርሱ ላይ አስገዳጅ አይደለህም፡፡ ስለዚህ ዛቻየን የሚፈራን ሰው በቁርኣን አስታውስ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,1,'51:1','መበተንን በታኞች በኾኑት (ነፋሶች)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,2,'51:2','ከባድ (ዝናምን) ተሸካሚዎች በኾኑትም (ደመናዎች)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,3,'51:3','ገር (መንሻለልን) ተንሻላዮች በኾኑትም (መርከቦች)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,4,'51:4','ነገርን ሁሉ አከፋፋዮች በኾኑትም (መላእክት) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,5,'51:5','የምትቀጠሩት (ትንሣኤ) እውነት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,6,'51:6','(እንደየሥራው) ዋጋን ማግኘትም፤ ኋኝ ነው፤ (የማይቀር ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,7,'51:7','የ(ከዋክብት) መንገዶች ባለቤት በኾነችው ሰማይ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,8,'51:8','እናንተ (የመካ ሰዎች) በተለያየ ቃል ውስጥ ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,9,'51:9','ከርሱ የሚዝዞር ሰው ይዝዞራል፡፡');
INSERT INTO "sadiq_sani" VALUES(51,10,'51:10','በግምት የሚናገሩ ውሸታሞች ተረገሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,11,'51:11','እነዚያ እነርሱ በሚሸፍን ስሕተተ ውስጥ ዘንጊዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(51,12,'51:12','የዋጋ መስጫው ቀን መቼ እንደ ኾነ ይጠይቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,13,'51:13','(እነርሱ) በእሳት ላይ በሚፈተኑበት ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,14,'51:14','«መከራችሁን ቅመሱ፤ ይህ ያ በእርሱ ትቻኮሉበት የነበራችሁት ነው» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,15,'51:15','አላህን ፈሪዎቹ በአትክልቶችና በምንጮች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,16,'51:16','ያንን ጌታቸው የሰጣቸውን ተቀባዮች ኾነው፤ (በገነት ውስጥ ይኾናሉ)፡፡ እነርሱ ከዚህ በፊት መልካም ሰሪዎች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(51,17,'51:17','ከሌሊቱ ጥቂትን ብቻ ይተኙ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,18,'51:18','በሌሊቱ መጨረሻዎችም እነርሱ ምሕረትን ይለምናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,19,'51:19','በገንዘቦቻቸውም ውስጥ ለለማኝና (ከልመና) ለተከለከለም (በችሮታቸው) መብት አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,20,'51:20','በምድርም ውስጥ ለሚያረጋግጡ ሰዎች ምልክቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,21,'51:21','በነፍሶቻችሁም ውስጥ (ምልክቶች አልሉ)፤ ታዲያ አትመለከቱምን?›');
INSERT INTO "sadiq_sani" VALUES(51,22,'51:22','ሲሳያችሁም የምትቀጠሩትም (ፍዳና ምንዳ) በሰማይ ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,23,'51:23','በሰማይና በምድር ጌታም እምላለሁ፡፡ እርሱ እናንተ እንደምትናገሩት ብጤ እርግጠኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,24,'51:24','የተከበሩት የኢብራሂም እንግዶች ወሬ መጥቶሃልን?');
INSERT INTO "sadiq_sani" VALUES(51,25,'51:25','በእርሱ ላይ በገቡና «ሰለም» ባሉ ጊዜ (አስታውስ)፡፡ «ሰላም ያልታወቃችሁ ሕዝቦች ናችሁ፤» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,26,'51:26','ወደ ቤተሰቡም ተዘነበለ፤» ወዲያውም የሰባ ወይፈንን አመጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,27,'51:27','ወደእነርሱም (አርዶና ጠብሶ) አቀረበው፡፡ «አትበሉም ወይ?» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,28,'51:28','ከእነርሱ መፍራትንም በልቡ አሳደረ፡፡ «አትፍራ» አሉት፡፡ በዐዋቂ ወጣት ልጅም አበሰሩት፡፡');
INSERT INTO "sadiq_sani" VALUES(51,29,'51:29','ሚስቱም እየጮኸች መጣች፡፡ ፊቷንም መታች፡፡ «መካን አሮጊት ነኝ» አለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,30,'51:30','እንደዚህ ጌታሽ ብሏል፡፡ «እነሆ እርሱ ጥበባ ዐዋቂ ነውና» አሏት፡፡');
INSERT INTO "sadiq_sani" VALUES(51,31,'51:31','«እናንተ መልክተኞች ሆይ! ታዲያ ነገራችሁ ምንድን ነው?» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,32,'51:32','(እነርሱም) አሉ፡- «እኛ ወደ አመጸኞች ሕዝቦች ተልከናል፡፡»');
INSERT INTO "sadiq_sani" VALUES(51,33,'51:33','በእነርሱ ላይ ከጭቃ የኾኑን ድንጋዮች ልንለቅባቸው (ተላክን)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,34,'51:34','በጌታህ ዘንድ ለድንበር አላፊዎቹ (በየስማቸው) ምልክት የተደረገባት ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(51,35,'51:35','ከምእምናንም፤ በእርሷ (በከተማቸው) ውስጥ የነበሩትን አወጣን፡፡');
INSERT INTO "sadiq_sani" VALUES(51,36,'51:36','በውስጧም ከሙስሊሞች ከአንድ ቤት (ቤተሰቦች) በስተቀር አላገኘንም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,37,'51:37','በውስጧም ለእነዚያ አሳማሚውን ቅጣት ለሚፈሩት ምልክትን አስቀረን፡፡');
INSERT INTO "sadiq_sani" VALUES(51,38,'51:38','በሙሳም (ወሬ) ውስጥ ወደ ፈርዖን በግልጽ ማስረጃ በላክነው ጊዜ (ምልክትን አደረግን)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,39,'51:39','ከድጋፉ (ከሰራዊቱ) ጋርም (ከእምነት) ዞረ፡፡ (እርሱ) «ድግምተኛ ወይም ዕብድ ነው» አለም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,40,'51:40','እርሱንም ሰራዊቱንም ያዝናቸው፡፡ እርሱ ተወቃሽ ሲኾን በባሕር ውስጥም ጣልናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,41,'51:41','በዓድም በእነርሱ ላይ መካንን ነፋስ በላክን ጊዜ (ምልክት አልለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,42,'51:42','በላዩ ላይ የመጣችበትን ማንኛውንም ነገር እንደ በሰበሰ አጥንት ያደረገችው ብትኾን እንጂ አትተወውም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,43,'51:43','በሰሙድም ለእነርሱ «እስከ ጊዜ (ሞታችሁ) ድረስ ተጣቀሙ» በተባሉ ጊዜ (ምልክት አልለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,44,'51:44','ከጌታቸው ትዕዛዝም ኮሩ፡፡ እነርሱም እያዩ ጩኸት ያዘቻቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,45,'51:45','መቆምንም ምንም አልቻሉም፡፡ የሚርረዱም አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,46,'51:46','የኑሕንም ሕዝቦች ከዚህ በፊት (አጠፋን)፡፡ እነርሱ አመጸኞች ሕዝቦች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(51,47,'51:47','ሰማይንም በኀይል ገነባናት፡፡ እኛም በእርግጥ ቻዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(51,48,'51:48','ምድርንም ዘረጋናት፡፡ ምን ያማርንም ዘርጊዎች (ነን!)');
INSERT INTO "sadiq_sani" VALUES(51,49,'51:49','ትገነዘቡም ዘንድ ከነገሩ ሁሉ ሁለት (ተቃራኒ) ዓይነትን ፈጠርን፡፡');
INSERT INTO "sadiq_sani" VALUES(51,50,'51:50','«ወደ አላህም ሽሹ፤ እኔ ለእናንተ ከርሱ ግልጽ አስጠንቃቂ ነኝና» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(51,51,'51:51','ከአላህም ጋር ሌላን አምላክ አታድርጉ፡፡ እኔ ለእናንተ ከእርሱ (የተላክሁ) ግልጽ አስፈራሪ ነኝና፡፡');
INSERT INTO "sadiq_sani" VALUES(51,52,'51:52','(ነገሩ) እንደዚሁ ነው፡፡ እነዚያን ከእነርሱ በፊት የነበሩትን ከመልክተኛ ማንም አልመጣቸውም፡፡ (እርሱ) «ድግምተኛ ወይም ዕብድ ነው» ያሉ ቢኾኑ እንጂ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,53,'51:53','በእርሱ (በዚህ ቃል) አደራ ተባብለዋልን? አይደለም፤ እነርሱ ጥጋበኞች ሕዝቦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,54,'51:54','ከእነርሱም (ክርክር) ዘወር በል፤ (ተዋቸው)፡፡ አንተ ምንም ተወቃሽ አይደለህምና፡፡');
INSERT INTO "sadiq_sani" VALUES(51,55,'51:55','ገሥጽም፤ ግሣጼ ምእመናንን ትጠቅማለችና፡፡');
INSERT INTO "sadiq_sani" VALUES(51,56,'51:56','ጋኔንንና ሰውንም ሊግገዙኝ እንጅ ለሌላ አልፈጠርኳቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,57,'51:57','ከነርሱም ምንም ሲሳይ አልፈልግም፡፡ ሊመግቡኝም አልሻም፡፡');
INSERT INTO "sadiq_sani" VALUES(51,58,'51:58','አላህ እርሱ ሲሳይን ሰጪ የብርቱ ኀይል ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(51,59,'51:59','ለእነዚያም ለበደሉት እንደ ጓደኞቻቸው ፋንታ ብጤ (የቅጣት) ፋንታ አልላቸው፡፡ ስለዚህ አያስቸኩሉኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(51,60,'51:60','ለነዚያም ለካዱት ከዚያ ከሚቀጠሩት ቀናቸው ወዮላቸው፡');
INSERT INTO "sadiq_sani" VALUES(52,1,'52:1','በጡር (ጋራ) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,2,'52:2','በተጻፈው መጽሐፍም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,3,'52:3','በተዘረጋ ብራና ላይ፤ (በተጻፈው)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,4,'52:4','በደመቀው ቤትም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,5,'52:5','ከፍ በተደረገው ጣራም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,6,'52:6','በተመላው ባሕርም እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,7,'52:7','የጌታህ ቅጣት በእርግጥ ወዳቂ ነው፤ (የማይቀር ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,8,'52:8','ለእርሱ ምንም ገፍታሪ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,9,'52:9','ሰማይ ብርቱ መዞርን በምትዞርበት ቀን (ይኾናል)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,10,'52:10','ተራራዎችም መኼድን በሚኼዱበት (ቀን)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,11,'52:11','ለአስተባባዮችም ያን ጊዜ ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,12,'52:12','ለእነዚያ እነርሱ በውሸት ውስጥ የሚጫወቱ ለኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(52,13,'52:13','ወደ ገሀነም እሳት በኀይል መግገፍተርን በሚግገፈተሩበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(52,14,'52:14','ይህቺ ያቺ በእርሷ ታስተባብሉባት የነበራችሁት እሳት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(52,15,'52:15','ይህ (ፊት ትሉ እንደነበራችሁት) ድግምት ነውን? ወይስ እናንተ አታዩምን?');
INSERT INTO "sadiq_sani" VALUES(52,16,'52:16','ግቧት ታገሱም ወይም አትታገሡ በእናንተ ላይ እኩል ነው፡፡ የምትምመነዱት ትሠሩት የነበራችሁትን (ፍዳ) ብቻ ነው (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,17,'52:17','አላህን ፈሪዎቹ በእርግጥ በገነቶችና በጸጋ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,18,'52:18','ጌታቸው በሰጣቸው ጸጋ ተደሳቾች ኾነው (በገነት ውስጥ ናቸው)፡፡ የገሀነምንም ስቃይ ጌታቸው ጠበቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,19,'52:19','«ትሠሩት በነበራችሁት ምክንያት ተደሳቾች ኾናችሁ ብሉ፤ ጠጡም፤» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,20,'52:20','በተደረደሩ አልጋዎች ላይ ተደጋፊዎች ኾነው (በገነት ይኖራሉ)፡፡ ዓይናማዎች በኾኑ ነጫጭ ሴቶችም እናጠናዳቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(52,21,'52:21','እነዚያም ያመኑትና ዝርያቸውም በእምነት የተከተለቻቸው ዝርያቸውን በእነርሱ እናስጠጋለን፡፡ ከሥራቸውም ምንም አናጎድልባቸውም፡፡ ሰው ሁሉ በሠራው ሥራ ተያዢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,22,'52:22','ከሚሹትም ሁሉ እሸትንና ስጋን እንጨምርላቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(52,23,'52:23','በውስጧ መጠጥን ይሰጣጣሉ፡፡ በውስጧ ውድቅ ንግግርና መውወንጀልም የለም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,24,'52:24','ለነርሱም የኾኑ ወጣቶች ልክ የተሸፈነ ሉል መስለው በእነርሱ ላይ (ለማሳለፍ) ይዘዋወራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,25,'52:25','የሚጠያየቁ ኾነውም ከፊላቸው በከፊሉ ላይ ይዞራል፡፡');
INSERT INTO "sadiq_sani" VALUES(52,26,'52:26','«እኛ ፊት በቤተሰቦቻችን ውስጥ (ከቅጣት) ፈሪዎች ነበርን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,27,'52:27','«አላህም በእኛ ላይ ለገሰ፡፡ የመርዛም እሳት ቅጣትንም ጠበቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(52,28,'52:28','«እኛ ከዚህ በፊት እንለምነው ነበርን፡፡ እነሆ እርሱ በጎ ዋይ አዛኝ ነውና» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,29,'52:29','(ሰዎችን) አስታውስም፡፡ አንተም በጌታህ ጸጋ ምክንያት ጠንቋይም ዕብድም አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,30,'52:30','ይልቁንም የሞትን አደጋ በእርሱ የምንጠባበቅበት የኾነ ባለ ቅኔ ነው ይላሉን?');
INSERT INTO "sadiq_sani" VALUES(52,31,'52:31','«ተጠባበቁ፤ እኔም ከእናንተ ጋር ከሚጠባበቁት ነኝ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,32,'52:32','አእምሮዎቻቸው በዚህ ያዟቸዋልን? አይደለም በእውነቱ እነርሱ ወሰን አላፊዎች ሕዝቦች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,33,'52:33','ወይም «ቀጠፈው ይላሉን?» አይደለም፤ በእውነቱ አያምኑም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,34,'52:34','እውነተኞችም ቢኾኑ መሰሉ የኾነን ንግግር ያምጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,35,'52:35','ወይስ ያለ አንዳች (ፈጣሪ) ተፈጠሩን? ወይስ እነርሱ ፈጣሪዎች ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(52,36,'52:36','ወይስ ሰማያትንና ምድርን ፈጠሩን? አይደለም አያረጋግጡም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,37,'52:37','ወይስ የጌታህ ግምጃ ቤቶች እነርሱ ዘንድ ናቸውን? ወይስ እነርሱ አሸናፊዎች ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(52,38,'52:38','ወይስ ለእነርሱ በእርሱ ላይ (ከሰማይ) የሚያዳምጡበት መሰላል አላቸውን? አድማጫቸውም (ሰማሁ ቢል) ግልጽን አስረጅ ያምጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,39,'52:39','ወይስ ለእርሱ ሴቶች ልጆች አሉትን? ለእናንተም ወንዶች ልጆች አሏችሁን?');
INSERT INTO "sadiq_sani" VALUES(52,40,'52:40','ወይስ ዋጋን ትጠይቃቸዋለህን? ስለዚህ እነርሱ ከዕዳው የተከበዱ ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(52,41,'52:41','ወይስ ሩቁ ምስጢር እነርሱ ዘንድ ነውን? ስለዚህ እነርሱ ይጽፋሉን?');
INSERT INTO "sadiq_sani" VALUES(52,42,'52:42','ወይስ ተንኮልን (ባንተ) ይሻሉን? እነዚያም የካዱት እነርሱ በተንኮል ተሸናፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,43,'52:43','ወይስ ለእነርሱ ከአላህ ሌላ አምላክ አላቸውን? አላህ ከሚያጋሩት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(52,44,'52:44','ከሰማይም ቁራጭን (በእነርሱ ላይ) ወዳቂ ኾኖ ቢያዩ ኖሮ «ይህ የተደራረበ ደመና ነው» ይሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(52,45,'52:45','ያንንም በእርሱ የሚግገደሉበትን ቀናቸውን እስከሚገናኙ ድረስ ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,46,'52:46','ተንኮላቸው ከእነርሱ ምንንም የማይጠቅማቸውንና እነርሱም የማይርረዱበትን ቀን (እስከ ሚገናኙ ተዋቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(52,47,'52:47','ለእነዚያ ለበደሉትም ከዚህ ሌላ ቅርብ ቅጣት አልላቸው፡፡ ግን አብዛኛዎቻቸው አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(52,48,'52:48','ለጌታህ ፍርድም ታገሥ፡፡ አንተ በጥበቃችን ውስጥ ነህና፡፡ ጌታህንም በምትነሳ ጊዜ ከማመስገን ጋር አወድሰው፡፡');
INSERT INTO "sadiq_sani" VALUES(52,49,'52:49','ከሌሊቱም አወድሰው፡፡ በከዋክብት መዳበቂያ ጊዜም (ንጋት ላይ አወድሰው)፡፡');
INSERT INTO "sadiq_sani" VALUES(53,1,'53:1','በኮከብ እምላለሁ በወደቀ (በገባ) ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,2,'53:2','ነቢያችሁ (ሙሐመድ) አልተሳሳተም፤ አልጠመመምም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,3,'53:3','ከልብ ወለድም አይናገርም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,4,'53:4','እርሱ (ንግግሩ) የሚወርረድ ራእይ እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,5,'53:5','ኀይሎቹ ብርቱው (መልአክ) አስተማረው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,6,'53:6','የዕውቀት ባለቤት የኾነው (አስተማረው፤ በተፈጥሮ ቅርጹ ኾኖ በአየር ላይ) ተደላደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,7,'53:7','እርሱ በላይኛው አድማስ ኾኖ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,8,'53:8','ከዚያም ቀረበ፤ ወረደም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,9,'53:9','(ከእርሱ) የሁለት ደጋኖች ጫፍ ያህል ወይም (ከዚህ) ይበልጥ የቀረበ ኾነም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,10,'53:10','ወደ ባሪያውም (አላህ) ያወረደውን አወረደ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,11,'53:11','(ነቢዩም በዓይኑ) ያየውን ልቡ አልዋሸም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,12,'53:12','ታዲያ በሚያየው ላይ ትከራከሩታላችሁን?');
INSERT INTO "sadiq_sani" VALUES(53,13,'53:13','በሌላይቱም መውረድ ጊዜ በእርግጥ አይቶታል፡፡');
INSERT INTO "sadiq_sani" VALUES(53,14,'53:14','በመጨረሻይቱ ቁርቁራ አጠገብ፤');
INSERT INTO "sadiq_sani" VALUES(53,15,'53:15','እርሷ ዘንድ መኖሪያይቱ ገነት ያለች ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(53,16,'53:16','ቁርቁራይቱን የሚሸፍን ነገር በሚሸፍናት ጊዜ (አየው)፡፡');
INSERT INTO "sadiq_sani" VALUES(53,17,'53:17','ዓይኑ (ካየው) አልተዘነበለም፡፡ ወሰንም አላለፈም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,18,'53:18','ከጌታው ታምራቶች ታላላቆቹን በእርግጥ አየ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,19,'53:19','አል-ላትንና አል-ዑዛን አያችሁን?');
INSERT INTO "sadiq_sani" VALUES(53,20,'53:20','ሦስተኛይቱንም አነስተኛዋን መናትን (አያችሁን? የምትግገዟቸው ኃይል አላቸውን?)');
INSERT INTO "sadiq_sani" VALUES(53,21,'53:21','ለእናንተ ወንድ ለእርሱም ሴት (ልጅ) ይኖራልን?');
INSERT INTO "sadiq_sani" VALUES(53,22,'53:22','ይህቺ ያን ጊዜ አድላዊ ክፍያ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(53,23,'53:23','እነርሱ እናንተና አባቶቻችሁ (አማልክት ብላችሁ) የጠራችኋቸው ስሞች ብቻ እንጅ ምንም አይደሉም፡፡ አላህ በእርሷ (መግገዛት) ምንም ማስረጃ አላወረደም፡፡ ከጌታቸውም መምሪያ የመጣላቸው ሲኾኑ ጥርጣሬንና ነፍሶች የሚዘነበሉበትን እንጅ ሌላ አይከተሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,24,'53:24','ለሰው የተመኘው ሁሉ አለውን?');
INSERT INTO "sadiq_sani" VALUES(53,25,'53:25','መጨረሻይቱም መጀመሪያይቱም (ዓለም) የአላህ ብቻ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,26,'53:26','በሰማያት ውስጥ ካለ መልአክም ብዙዎች (ሊማለዱለት) ለሚሻውና ለሚወደው ሰው ከፈቀደ በኋላ ቢኾን እንጅ ምልጃቸው ምንም አትጠቅምም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,27,'53:27','እነዚያ በመጨረሻይቱ ዓለም የማያምኑት መላእክትን በሴት አጠራር ይጠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,28,'53:28','ለእነርሱም በእርሱ ምንም ዕውቀት የላቸውም፡፡ ጥርጣሬን እንጅ ሌላን አይከተሉም፡፡ ጥርጣሬም ከእውነት ምንም አያብቃቃም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,29,'53:29','ከግሣጼያችንም (ከቁርኣን) ከሸሸና አነስተኛይቱን ሕይወት እንጅ ሌላን ከማይሻ ሰው ዙር (ተወው)፡፡');
INSERT INTO "sadiq_sani" VALUES(53,30,'53:30','ይህ (አነስተኛይቱን ሕይወት መምረጥ) ከዕውቀት ድካቸው ነው፡፡ ጌታህ እርሱ ከመንገዱ የተሳሳተውን በእርግጥ ዐዋቂ ነው፡፡ እርሱም የቀናውን ሰው ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,31,'53:31','በሰማያት ያለውና በምድር ያለው ሁሉ የአላህ ነው፡፡ እነዚያን ያጠፉትን በሠሩት ሊቀጣ እነዚያን ያሳመሩትንም በመልካሚቱ (ገነት) ሊመነዳ (የጠመመውንና የቀናውን ያውቃል)፡፡');
INSERT INTO "sadiq_sani" VALUES(53,32,'53:32','(እነርሱ) እነዚያ የኃጢኣትን ታላለቆችና አስጸያፊዎቹን የሚርቁ ናቸው፡፡ ግን ትናንሾቹ የሚማሩ ናቸው፡፡ ጌታህ ምሕረተ ሰፊ ነውና፡፡ ከምድር በፈጠራችሁ ጊዜ እናንተም በእናቶቻችሁ ሆዶች ውስጥ ሽሎች በኾናችሁ ጊዜ እርሱ በናንተ (ኹነታ) ዐዋቂ ነው፡፡ ነፍሶቻችሁንም አታወድሱ እርሱ የሚፈራውን ሰው ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,33,'53:33','ያንን (ከእምነት) የዞረውን አየህን?');
INSERT INTO "sadiq_sani" VALUES(53,34,'53:34','ጥቂትን የሰጠውንና ያቋረጠውንም (አየህን?)');
INSERT INTO "sadiq_sani" VALUES(53,35,'53:35','የሩቁ ምስጢር ዕውቀት እርሱ ዘንድ አልለን? ስለዚህ እርሱ ያያልን?');
INSERT INTO "sadiq_sani" VALUES(53,36,'53:36','ይልቁንም በዚያ በሙሳ ጽሑፎች ውስጥ ባለው ነገር አልተነገረምን?');
INSERT INTO "sadiq_sani" VALUES(53,37,'53:37','በዚያም (የታዘዘውን) በፈጸመው በኢብራሂም (ጽሑፎች ውስጥ ባለው አልተነገረምን?)');
INSERT INTO "sadiq_sani" VALUES(53,38,'53:38','(እርሱም ኃጢያት) ተሸካሚ ነፍስ የሌላይቱን ነፍስ ኃጢኣት አትሸከምም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,39,'53:39','ለሰውም ሁሉ የሠራው እንጅ ሌላ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,40,'53:40','ሥራውም ሁሉ ወደ ፊት ይታያል፡፡');
INSERT INTO "sadiq_sani" VALUES(53,41,'53:41','ከዚያም ሙሉውን ምንዳ ይመነዳዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(53,42,'53:42','መጨረሻውም ወደ ጌታህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,43,'53:43','እነሆ እርሱም አስሳቀ፣ አስለቀሰም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,44,'53:44','እነሆ እርሱም ገደለ፣ አስነሳም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,45,'53:45','እርሱም ሁለቱን ዓይነቶች ወንድንና ሴትን ፈጠረ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,46,'53:46','ከፍትወት ጠብታ (በማህጸን ውስጥ) በምትፈሰስ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,47,'53:47','የኋለኛይቱም ማስነሳት በእርሱ ላይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,48,'53:48','እነሆ እርሱም አከበረ፣ ጥሪተኛም አደረገ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,49,'53:49','እነሆ እርሱም የሺዕራ ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,50,'53:50','እነሆ እርሱም የፊተኛይቱን ዓድ አጥፍቷል፡፡');
INSERT INTO "sadiq_sani" VALUES(53,51,'53:51','ሰሙድንም (አጥፍቷል)፤ አላስቀረምም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,52,'53:52','በፊትም የኑሕን ሕዝቦች (አጥፍቷል)፡፡ እነርሱ በጣም በደለኞችና እጅግ የጠመሙ እነርሱ ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(53,53,'53:53','የተገለበጠችውንም ከተማ ደፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,54,'53:54','ያለበሳትንም አለበሳት፡፡');
INSERT INTO "sadiq_sani" VALUES(53,55,'53:55','ከጌታህም ጸጋዎች በየትኛው ትጠራጠራለህ?');
INSERT INTO "sadiq_sani" VALUES(53,56,'53:56','ይህ ከፊተኞቹ አስፈራሪዎች (ጎሳ) የኾነ አስፈራሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(53,57,'53:57','ቀራቢይቱ (ኅልፈተ ዓለም) ቀረበች፡፡');
INSERT INTO "sadiq_sani" VALUES(53,58,'53:58','ለእርሷ ከአላህ ሌላ ገላጭ የላትም፡፡');
INSERT INTO "sadiq_sani" VALUES(53,59,'53:59','ከዚህ ንግግር ትደነቃላችሁን?');
INSERT INTO "sadiq_sani" VALUES(53,60,'53:60','ትስቃላችሁምን? አታለቅሱምን?');
INSERT INTO "sadiq_sani" VALUES(53,61,'53:61','እናንተም ዘንጊዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(53,62,'53:62','ለአላህም ስገዱ ተገዙትም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,1,'54:1','ሰዓቲቱ (የትንሣኤ ቀን) ተቃረበች፤ ጨረቃም ተገመሰ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,2,'54:2','ተዓምርንም ቢያዩ (ከእምነት) ይዞራሉ፡፡ (ይህ) «ዘውታሪ ድግምት ነውም» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,3,'54:3','አስተባበሉም፡፡ ዝንባሌዎቻቸውንም ተከተሉ፡፡ ነገርም ሁሉ (ወሰን አለው)፤ ረጊ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,4,'54:4','ከዜናዎችም በእርሱ ውስጥ መገሰጥ ያለበት ነገር በእርግጥ መጣላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,5,'54:5','ሙሉ የኾነች ጥበብ (መጣቻቸው) ግን አስፈራሪዎቹ አያብቃቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,6,'54:6','ከነርሱም ዙር፡፡ ጠሪው (መልአክ) ወደ አስደንጋጭ ነገር የሚጠራበትን ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,7,'54:7','ዓይኖቻቸው ያቀረቀሩ ኾነው፤ ፍጹም የተበተነ አንበጣ መስለው ከመቃብሮቹ ይወጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,8,'54:8','ወደ ጠሪው አንገቶቻቸውን ሳቢዎች ቸኳዮች ኾነው (ይወጣሉ)፡፡ ከሓዲዎቹ (ያን ጊዜ ምን ይላሉ?) «ይህ ብርቱ ቀን ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,9,'54:9','ከእነርሱ በፊት የኑሕ ነገድ አስተባበለች፡፡ ባሪያችንንም (ኑሕን) አስተባበሉ፡፡ «ዕብድ ነውም» አሉ፡፡ ተገላመጠም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,10,'54:10','ጌታውንም «እኔ የተሸነፍኩ ነኝና እርዳኝ» ሲል ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,11,'54:11','ወዲያውም የሰማይን ደጃፎች በሚንቧቧ ውሃ ከፈትን፡፡');
INSERT INTO "sadiq_sani" VALUES(54,12,'54:12','የምድርንም ምንጮች አፈነዳን፡፡ ውሃውም (የሰማዩና የምድሩ) በእርግጥ በተወሰነ ኹነታ ላይ ተገናኘ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,13,'54:13','ባለ ሳንቃዎችና ባለሚስማሮች በኾነችም ታንኳ ላይ ጫንነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,14,'54:14','በጥበቃችን ስር ኾና ትንሻለላለች፡፡ ተክዶ ለነበረው ሰው ምንዳ (ይህንን ሠራን)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,15,'54:15','ተዓምር አድርገንም በእርግጥ ተውናት፡፡ ከተገሳጭም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,16,'54:16','ቅጣቴና ማስጠንቀቂያዎቼም እንዴት ነበሩ?');
INSERT INTO "sadiq_sani" VALUES(54,17,'54:17','ቁርኣንንም ለመገንዘብ በእርግጥ አገራነው፡፡ ተገንዛቢም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,18,'54:18','ዓድ አስተባበለች፡፡ ቅጣቴና ማስጠንቀቂያዎቼም እንዴት ነበሩ!');
INSERT INTO "sadiq_sani" VALUES(54,19,'54:19','እኛ በእነርሱ ላይ ዘወትር መናጢ በኾነ ቀን በኀይል የምትንሻሻ ነፋስን ላክንባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,20,'54:20','ሰዎቹን ልክ ከሥሮቻቸው የተጎለሰሱ የዘንባባ ግንዶች መስለው (ከተደበቁበት) ትነቅላቸዋለች፡፡');
INSERT INTO "sadiq_sani" VALUES(54,21,'54:21','ቅጣቴና ማስጠንቀቂያዎቼም እንዴት ነበሩ?');
INSERT INTO "sadiq_sani" VALUES(54,22,'54:22','ቁርኣንንም ለማስታወስ በእርግጥ አገራነው፡፡ ተገሳጭም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,23,'54:23','ሰሙድ በአስፈራሪዎቹ አስተባበለች፡፡');
INSERT INTO "sadiq_sani" VALUES(54,24,'54:24','«ከእኛ የኾነን አንድን ሰው እንከተለዋለን? እኛ ያን ጊዜ በስህተትና በዕብደት ውስጥ ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,25,'54:25','«ከኛ መካከል በእርሱ ላይ (ብቻ) ማስገንዘቢያ (ራእይ) ተጣለለትን? አይደለም እርሱ ውሸታም ኩሩ ነው» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,26,'54:26','ውሸታሙ ኩሩው ማን እንደኾነ ነገ በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,27,'54:27','እኛ ሴት ግመልን ለእነርሱ መፈተኛ ትኾን ዘንድ ላኪዎች ነን፡፡ ተጠባበቃቸውም፡፡ ታገስም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,28,'54:28','ውሃውንም በመካከላቸው የተከፈለ መኾኑን ንገራቸው፡፡ ከውሃ የኾነ ፋንታ ሁሉ (ተረኞቹ) የሚጣዱት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,29,'54:29','ጓደኛቸውንም ጠሩ፡፡ ወዲያውም (ሰይፍን) ተቀበለ፡፡ ወጋትም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,30,'54:30','ቅጣቴና ማስጠንቀቂያዎቼም እንዴት ነበሩ?');
INSERT INTO "sadiq_sani" VALUES(54,31,'54:31','እኛ በእነርሱ ላይ አንዲትን ጩኸት ላክንባቸው፡፡ ወዲያውም ከበረት አጣሪ (አጥር) እንደ ተሰባበረ ርግጋፊ ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,32,'54:32','ቁርኣንንም ለመገንዘብ አገራነው፡፡ ተገሳጭም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,33,'54:33','የሉጥ ሕዝብ በአስፈራሪዎቹ አስተባበለች፡፡');
INSERT INTO "sadiq_sani" VALUES(54,34,'54:34','እኛ በእነርሱ ላይ ጠጠርን ያዘለች ነፋስን ላክን፡፡ የሉጥ ቤተሰቦች ብቻ ሲቀሩ (እነርሱንስ) በሌሊት መጨረሻ ላይ አዳንናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,35,'54:35','ከእኛ በኾነ ጸጋ (አዳንናቸው)፡፡ እንደዚሁ ያመሰገነን ሰው እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(54,36,'54:36','ብርቱይቱን አያያዛችንንም በእርግጥ አስጠነቀቃቸው፡፡ በማስጠንቀቂያዎቹም ተከራከሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,37,'54:37','ከእንግዶቹም እንዲያስመቻቸው ደጋግመው ፈለጉት፡፡ ወዲያውም ዓይኖቻቸውን አበስን፡፡ «ቅጣቴንና ማስጠንቀቂያዎቼንም ቅመሱ» (አልናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,38,'54:38','በማለዳም ዘውታሪ የኾነ ቅጣት በእርግጥ ማለደባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,39,'54:39','«ቅጣቴንና ማስጠንቀቂያዎቼንም ቅመሱ» (ተባሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,40,'54:40','ቁርኣንንም ለመገንዘብ በእርግጥ አገራነው፡፡ ተገሳጭም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,41,'54:41','የፈርዖንንም ቤተሰቦች ማስጠንቀቂያዎች በእርግጥ መጡዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,42,'54:42','በተዓምራቶቻችን በሁሏም አስተባበሉ፡፡ የብርቱ ቻይንም አያያዝ ያዝናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,43,'54:43','ከሓዲዎቻችሁ ከእነዚህ በላጮች ናቸውን? ወይስ ለናንተ በመጽሐፎች ውስጥ (የተነገረ) ነፃነት አላችሁን?');
INSERT INTO "sadiq_sani" VALUES(54,44,'54:44','ወይስ «እኛ የተረዳን ክምቹዎች ነን» ይላሉን?');
INSERT INTO "sadiq_sani" VALUES(54,45,'54:45','ክምቹዎቹ በእርግጥ ድል ይምመታሉ፡፡ ጀርባዎችንም ያዞራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(54,46,'54:46','ይልቁንም ሰዓቲቱ (ትንሣኤ) ቀጠሮዋቸው ናት፡፡ ሰዓቲቱም በጣም የከበደችና የመረረች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(54,47,'54:47','አመጸኞች በስህተትና በእሳቶች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,48,'54:48','በእሳት ውስጥ በፊቶቻቸው በሚጎተቱበት ቀን «የሰቀርን መንካት ቅመሱ» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(54,49,'54:49','እኛ ሁሉን ነገር በልክ ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,50,'54:50','ትእዛዛችንም እንደ ዓይን ቅጽበት አንዲት (ቃል) እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(54,51,'54:51','ብጤዎቻችሁንም በእርግጥ አጠፋን፡፡ ተገሳጭም አልለን?');
INSERT INTO "sadiq_sani" VALUES(54,52,'54:52','የሠሩትም ነገር ሁሉ በመጽሐፎች ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,53,'54:53','ትንሹም ትልቁም ሁሉ የተጻፈ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,54,'54:54','አላህን ፈሪዎቹ በአትክልቶችና በወንዞች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(54,55,'54:55','(እነርሱም ውድቅ ቃልና መውወንጀል በሌለበት) በእውነት መቀመጫ ውስጥ ቻይ የሆነው ንጉሥ ዘንድ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,1,'55:1','አል-ረሕማን፤');
INSERT INTO "sadiq_sani" VALUES(55,2,'55:2','ቁርኣንን አስተማረ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,3,'55:3','ሰውን ፈጠረ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,4,'55:4','መናገርን አስተማረው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,5,'55:5','ፀሐይና ጨረቃ በተወሰነ ልክ (ይኼዳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,6,'55:6','ሐረግና ዛፍም (ለእርሱ) ይሰግዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,7,'55:7','ሰማይንም አጓናት፡፡ ትክክለኛነትንም ደነገገ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,8,'55:8','በሚዛን (ስትመዝኑ) እንዳትበድሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,9,'55:9','መመዘንንም በትክክል መዝኑ፡፡ ተመዛኙንም አታጉድሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,10,'55:10','ምድርንም ለፍጡሩ አደላደላት፡፡');
INSERT INTO "sadiq_sani" VALUES(55,11,'55:11','በውስጧ እሸት ባለ ሺፋኖች የኾኑ ዘንባባዎችም ያሉባት ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(55,12,'55:12','የገለባ ባለቤት የኾነ ቅንጣትም፡፡ ባለ መልካም መዐዛ ቅጠሎችም (ያሉባት ስትኾን)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,13,'55:13','(ሰዎችና ጋኔኖች ሆይ!) ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,14,'55:14','ሰውን እንደ ሸክላ ከሚቅጨለጨል ደረቅ ጭቃ ፈጠረው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,15,'55:15','ጃንንም ከእሳት ከኾነ ነበልባል ፈጠረው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,16,'55:16','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,17,'55:17','የሁለቱ ምሥራቆች ጌታ፤ የሁለቱ ምዕራቦችም ጌታ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,18,'55:18','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,19,'55:19','ሁለቱን ባሕሮች የሚገናኙ ሲኾኑ ለቀቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,20,'55:20','(እንዳይዋሐዱ) በመካከላቸው ጋራጅ አልለ፡፡ (አንዱ ባንዱ ላይ) ወሰን አያልፉም፡፡');
INSERT INTO "sadiq_sani" VALUES(55,21,'55:21','ከጌታችሁም ጸጋዎች በየትኛው ታሰተባብላላችሁ?›');
INSERT INTO "sadiq_sani" VALUES(55,22,'55:22','ሉልና መርጃን ከሁለቱ ይወጣል፡፡');
INSERT INTO "sadiq_sani" VALUES(55,23,'55:23','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,24,'55:24','እንደ ጋራዎች ኾነው በባሕር ውስጥ የተሠሩት ተንሻለዮቹም (ታንኳዎች) የእርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,25,'55:25','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,26,'55:26','በእርሷ (በምድር) ላይ ያለው ሁሉ ጠፊ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,27,'55:27','የልቅናና የልግሥና ባለቤት የኾነው የጌታህ ፊትም ይቀራል፡፡ (አይጠፋም)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,28,'55:28','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,29,'55:29','በሰማያትና በምድር ውስጥ ያሉት ሁሉ ይለምኑታል፡፡ በየቀኑ ሁሉ እርሱ በሥራ ላይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,30,'55:30','ከጌታችሁም ጸጋዎች በየትኛው ታሰተባብላላችሁ?›');
INSERT INTO "sadiq_sani" VALUES(55,31,'55:31','እናንተ ሁለት ከባዶች (ሰዎችና ጋኔኖች) ሆይ! ለእናንተ (መቆጣጠር) በእርግጥ እናስባለን፡፡');
INSERT INTO "sadiq_sani" VALUES(55,32,'55:32','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,33,'55:33','የጋኔንና የሰው ጭፍሮች ሆይ! ከሰማያትና ከምድር ቀበሌዎች መውጣትን ብትችሉ ውጡ፡፡ በስልጣን እንጅ አትወጡም፡፡ (ግን ስልጣን የላችሁም)');
INSERT INTO "sadiq_sani" VALUES(55,34,'55:34','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,35,'55:35','በሁለታችሁም ላይ ከእሳት ነበልባል፣ ጭስም ይላክባችኋል፡፡ (ሁለታችሁም) አትርረዱምም፡፡');
INSERT INTO "sadiq_sani" VALUES(55,36,'55:36','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,37,'55:37','ሰማይም በተሰነጠቀችና እንደ ጽጌረዳ፣ እንደታረበ ቆዳ በኾነች ጊዜ፤ (ጭንቁን ምን አበረታው)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,38,'55:38','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,39,'55:39','በዚያም ቀን ሰውም ጃንም ከኃጢኣቱ ገና አይጠየቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(55,40,'55:40','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,41,'55:41','ከሓዲዎች በምልክታቸው ይታወቃሉ፡፡ አናቶቻቸውንና ጫማዎቻቸውንም ይያዛሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,42,'55:42','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,43,'55:43','ይህቺ ያቺ አመጸኞች በእርሷ ያስተባብሉባት የነበረችው ገሀነም ናት (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,44,'55:44','በእርሷና በጣም ሞቃት በኾነ ፍል ውሃ መካከል ይመላለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,45,'55:45','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,46,'55:46','በጌታውም ፊት መቆምን ለፈራ ሰው ሁለት ገነቶች አልሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(55,47,'55:47','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,48,'55:48','የቀንዘሎች ባለ ቤቶች የኾኑ (ገነቶች አልሉት)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,49,'55:49','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,50,'55:50','በሁለቱ ውስጥ የሚፈስሱ ሁለት ምንጮች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,51,'55:51','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,52,'55:52','በሁለቱ ውስጥ ከፍራፍሬዎች ሁሉ ሁለት ዓይነቶች (እርጥብና ደረቅ) አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,53,'55:53','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,54,'55:54','የውስጥ ጉስጉሳቸው ከወፍራም ሐር በኾኑ ምንጣፎች ላይ የተመቻቹ ሲኾኑ (ይንፈላሰሳሉ)፡፡ የሁለቱም ገነቶች ፍሬ (ለለቃሚ) ቅርብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,55,'55:55','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,56,'55:56','በውስጣቸው ከበፊታቸው (ከባሎቻቸው በፊት) ሰውም ጃንም ያልገሰሳቸው ዓይኖቻቸውን (በባሎቻቸው ላይ) አሳጣሪዎች (ሴቶች) አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,57,'55:57','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,58,'55:58','ልክ ያቁትና መርጃን ይመስላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,59,'55:59','ከጌታችሁም ጸጋዎቸ በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,60,'55:60','የበጎ ሥራ ዋጋ በጎ እንጅ ሌላ ነውን?');
INSERT INTO "sadiq_sani" VALUES(55,61,'55:61','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,62,'55:62','ከሁለቱ ገነቶችም ሌላ ሁለት ገነቶች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,63,'55:63','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?›');
INSERT INTO "sadiq_sani" VALUES(55,64,'55:64','ከልምላሜያቸው የተነሳ ወደ ጥቁረት ያዘነበሉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,65,'55:65','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,66,'55:66','በውስጣቸው ሁለት የሚንፏፉ ምንጮች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,67,'55:67','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,68,'55:68','በውስጣቸው ፍራፍሬ፣ ዘምባባም፣ ሩማንም አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,69,'55:69','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,70,'55:70','በውስጣቸው ጠባየ መልካሞች መልክ ውቦች (ሴቶች) አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(55,71,'55:71','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,72,'55:72','በድንኳኖች ውስጥ የተጨጎሉ የዓይኖቻቸው ጥቁረትና ንጣት ደማቅ የኾኑ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(55,73,'55:73','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,74,'55:74','ከነርሱ በፊት ሰውም ጃንም አልገሰሳቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(55,75,'55:75','ከጌታችሁም ጸጋዎች በየትኛው ታስተባበላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,76,'55:76','በሚያረገርጉ አረንጓዴ ምንጣፎችና በሚያማምሩ ስጋጃዎች ላይ የተደላደሉ ሲኾኑ (ይቀመጣሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(55,77,'55:77','ከጌታችሁም ጸጋዎች በየትኛው ታስተባብላላችሁ?');
INSERT INTO "sadiq_sani" VALUES(55,78,'55:78','የግርማና የመክበር ባለቤት የኾነው ጌታህ ስም ተባረከ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,1,'56:1','መከራይቱ በወደቀች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,2,'56:2','ለመውደቋ አስተባባይ (ነፍስ) የለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,3,'56:3','ዝቅ አድራጊና ከፍ አድራጊ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(56,4,'56:4','ምድር መናወጥን በተናወጠች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,5,'56:5','ተራራዎችም መፍፈርፈርን በተፈረፈሩ ጊዜ (እንደተፈጨ ዱቄት በኾኑ ጊዜ)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,6,'56:6','የተበተነ ብናኝ በኾኑም ጊዜ');
INSERT INTO "sadiq_sani" VALUES(56,7,'56:7','ሦስት ዓይነቶችም በኾናችሁ ጊዜ (ታዋርዳለች፤ ታነሳለችም)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,8,'56:8','የቀኝ ጓዶችም ምን (የተከበሩ) የቀኝ ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,9,'56:9','የግራ ጓዶችም ምን (የተዋረዱ) የግራ ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,10,'56:10','(ለበጎ ሥራ) ቀዳሚዎቹም (ለገነት) ቀዳሚዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,11,'56:11','እነዚያ ባለሟሎቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,12,'56:12','በመጠቀሚያ ገነቶች ውስጥ ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,13,'56:13','ከፊተኞቹ ብዙ ጭፍሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,14,'56:14','ከኋለኞቹም ጥቂቶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,15,'56:15','በተታቱ አልጋዎች ላይ ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,16,'56:16','በእርሷ ላይ የተመቻቹና ፊት ለፊት የተቅጣጩ ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,17,'56:17','በእነርሱ ላይ ሁል ጊዜ የማያረጁ ወጣቶች ልጆች ይዘዋወራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,18,'56:18','ከጠጅ ምንጭም በብርጭቆዎችና በኩስኩስቶች በጽዋም (በእነርሱ ላይ ይዝዞራሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,19,'56:19','ከእርሷ የራስ ምታት አያገኛቸውም፤ አይሰክሩምም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,20,'56:20','ከሚመርጡትም ዓይነት በእሸቶች፡፡');
INSERT INTO "sadiq_sani" VALUES(56,21,'56:21','ከሚሹትም በኾነ የበራሪ ሥጋ (ይዞሩባቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,22,'56:22','ዓይናማዎች የኾኑ ነጫጭ ቆንጆዎችም አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,23,'56:23','ልክ እንደተሸፈነ ሉል መሰሎች የኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,24,'56:24','በዚያ ይሠሩት በነበሩት ምክንያት ዋጋ ይኾን ዘንድ (ይህንን አደረግንላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,25,'56:25','በውስጥዋ ውድቅ ንግግርንና መውወንጀልን አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,26,'56:26','ግን ሰላም መባባልን (ይሰማሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,27,'56:27','የቀኝም ጓዶች ምንኛ (የከበሩ) የቀኝ ጓዶች!');
INSERT INTO "sadiq_sani" VALUES(56,28,'56:28','በተቀፈቀፈ (እሾህ በሌለው) ቁርቁራ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,29,'56:29','(ፍሬው) በተነባበረ በሙዝ ዛፍም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,30,'56:30','በተዘረጋ ጥላ ሥርም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,31,'56:31','በሚንቧቧ ውሃ አጠገብም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,32,'56:32','ብዙ (ዓይነት) በኾኑ ፍራፍሬዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,33,'56:33','የማትቋረጥም የማትከለከልም የኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(56,34,'56:34','ከፍ በተደረጉ ምንጣፎችም (ሴቶችም መካከል)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,35,'56:35','እኛ አዲስ ፍጥረት አድርገን (ለእነርሱ) ፈጠርናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,36,'56:36','ደናግሎችም አደረግናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,37,'56:37','ለባሎቻቸው ተሽሞርሟሪዎች እኩያዎች (አደረግናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,38,'56:38','ለቀኝ ጓዶች (አዘጋጀናቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,39,'56:39','ከፊተኞቹ ብዙ ጭፍሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,40,'56:40','ከኋለኞቹም ብዙ ጭፍሮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,41,'56:41','የግራ ጓደኞችም ምንኛ (የተዋረዱ) የግራ ጓዶች! ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,42,'56:42','በመርዛም ነፋስ በጣም በፈላ ውሃም ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,43,'56:43','ከጥቁር ጭስም በኾነ ጥላ ውስጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,44,'56:44','ቀዝቃዛም መልካምም ያልኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,45,'56:45','እነርሱ ከዚህ በፊት ቅምጥሎች ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(56,46,'56:46','በታላቅ ኀጢአትም ላይ ይዘወትሩ ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(56,47,'56:47','ይሉም ነበሩ፡- «በሞትንና ዐፈር፣ ዐጥንቶችም በኾን ጊዜ እኛ በእርግጥ የምንቀሰቀስ ነን?');
INSERT INTO "sadiq_sani" VALUES(56,48,'56:48','«የፊተኞቹ አባቶቻችንም?»');
INSERT INTO "sadiq_sani" VALUES(56,49,'56:49','በላቸው፡- «ፊተኞቹም ኋለኞቹም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,50,'56:50','«በተወሰነ ቀን ቀጠሮ በእርግጥ ተሰብሳቢዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,51,'56:51','«ከዚያም እናንተ ጠማሞች አስተባባዮች ሆይ!');
INSERT INTO "sadiq_sani" VALUES(56,52,'56:52','« ከዘቁም ዛፍ በእርግጥ በይዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,53,'56:53','«ከእርሷም ሆዶቻችሁን ሞይዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,54,'56:54','«በእርሱ ላይም ከፈላ ውሃ ጠጪዎች ናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,55,'56:55','«የተጠሙንም ግመሎች አጠጣጥ ብጤ ጠጪዎች ናችሁ፡፡»');
INSERT INTO "sadiq_sani" VALUES(56,56,'56:56','ይህ በፍርዱ ቀን መስተንግዷቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,57,'56:57','እኛ ፈጠርናችሁ አታምኑምን?');
INSERT INTO "sadiq_sani" VALUES(56,58,'56:58','(በማኅፀኖች) የምታፈሱትን አያችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,59,'56:59','እናንተ ትፈጥሩታላችሁን? ወይስ እኛ ፈጣሪዎቹን ነን?');
INSERT INTO "sadiq_sani" VALUES(56,60,'56:60','እኛ ሞትን (ጊዜውን) በመካከላችሁ ወሰንን፡፡ እኛም ተሸናፊዎች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,61,'56:61','ብጤዎቻችሁን በመለወጥና በዚያ በማታውቁትም (ቅርጽ) እናንተን በመፍጠር ላይ (አንሸነፍም)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,62,'56:62','የፊተኛይቱንም አፈጣጠር በእርግጥ ዐውቃችኋል፡፡ አትገነዘቡምን?');
INSERT INTO "sadiq_sani" VALUES(56,63,'56:63','የምትዘሩትንም አያችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,64,'56:64','እናንተ ታበቅሉታላችሁን? ወይስ እኛ አብቃዮቹ ነን?');
INSERT INTO "sadiq_sani" VALUES(56,65,'56:65','ብነሻ ኖሮ ደረቅ ስብርብር ባደረግነውና የምትደነቁም በኾናችሁ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(56,66,'56:66','«እኛ በዕዳ ተያዦች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(56,67,'56:67','«በእርግጥም እኛ (ከመጠቀም) የተከለከልን ነን» (ትሉ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,68,'56:68','ያንንም የምትጠጡትን ውሃ አየችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,69,'56:69','እናንተ ከደመናው አወረዳችሁትን? ወይስ እኛ አውራጆቹ ነን?');
INSERT INTO "sadiq_sani" VALUES(56,70,'56:70','ብንሻ ኖሮ መርጋጋ ባደረግነው ነበር፡፡ አታመሰግኑምን?');
INSERT INTO "sadiq_sani" VALUES(56,71,'56:71','ያቺንም የምትሰብቋትን እሳት አያችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,72,'56:72','እናንተ ዛፏን ፈጠራችሁን? ወይስ እኛ ፈጣሪዎችዋ ነን?');
INSERT INTO "sadiq_sani" VALUES(56,73,'56:73','እኛ (ለገሀናም) ማስታወሻ ለመንገደኞችም መጠቀሚያ አደረግናት፡፡');
INSERT INTO "sadiq_sani" VALUES(56,74,'56:74','የታላቁን ጌታህንም ስም አወድስ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,75,'56:75','በከዋክብትም መጥለቂያዎች እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,76,'56:76','እርሱም ብታወቁ ታላቅ መሓላ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,77,'56:77','እርሱ የከበረ ቁርኣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,78,'56:78','በተጠበቀ መጽሐፍ ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,79,'56:79','የተጥራሩት እንጅ ሌላ አይነካውም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,80,'56:80','ከዓለማት ጌታ የተወረደ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,81,'56:81','በዚህ ንግግር እናንተ ቸልተኞች ናችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,82,'56:82','ሲሳያችሁንም (ዝናምን) እናንተ የምታስተባብሉት ታደርጋላችሁን?');
INSERT INTO "sadiq_sani" VALUES(56,83,'56:83','(ነፍስ) ጉረሮንም በደረሰች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,84,'56:84','እናንተ ያን ጊዜ የምታዩ ስትኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,85,'56:85','እኛም ግን አታዩም እንጅ ከእናንተ ይልቅ ወደእርሱ የቀረብን ስንኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(56,86,'56:86','የማትዳኙም ከኾናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,87,'56:87','እውነተኞች እንደ ኾናችሁ (ነፍሲቱን ወደ አካሉ) ለምን አትመልሷትም፡፡');
INSERT INTO "sadiq_sani" VALUES(56,88,'56:88','(ሟቹ) ከባለሟሎቹ ቢኾንማ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,89,'56:89','(ለእርሱ) ዕረፍት፣ መልካም ሲሳይም፣ የመጠቀሚያ ገነትም አልለው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,90,'56:90','ከቀኝ ጓዶችም ቢኾንማ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,91,'56:91','ከቀኝ ጓዶች ስለ ኾንክ ላንተ ሰላም አልለህ (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,92,'56:92','ከሚያስዋሹት ጠማማዎችም ቢኾንማ፡፡');
INSERT INTO "sadiq_sani" VALUES(56,93,'56:93','ከፈላ ውሃ የኾነ መስተንግዶ አልለው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,94,'56:94','በገሀነም መቃጠልም (አልለው)፡፡');
INSERT INTO "sadiq_sani" VALUES(56,95,'56:95','ይህ እርሱ እርግጠኛ እውነት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(56,96,'56:96','የታላቁንም ጌታህን ስም አወድስ፡፡');
INSERT INTO "sadiq_sani" VALUES(57,1,'57:1','በሰማያትና በምድር ያለው ሁሉ ለአላህ አሞገሰ፡፡ እርሱም ሁሉን አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,2,'57:2','የሰማያትና የምድር ንግሥና የእርሱ ብቻ ነው፡፡ ሕያው ያደርጋል፤ ይገድላልም፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,3,'57:3','እርሱ ፊትም ያለ፣ ኋላም ቀሪ፣ ግልጽም፣ ስውርም ነው፡፡ እርሱም ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,4,'57:4','እርሱ ያ ሰማያትንና ምድርን በስድስት ቀናት ውስጥ የፈጠረ፤ ከዚያም በዙፋኑ ላይ (ለሱ ክብር በሚስማማ መልኩ)የተደላደለ ነው፡፡ በምድር ውስጥ የሚገባውን፣ ከእርሷም የሚወጣውን፣ ከሰማይም የሚወርደውን በእርሷም ውስጥ የሚያርገውን ያውቃል፡፡ እርሱም የትም ብትኾኑ ከእናንተ ጋር ነው፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,5,'57:5','የሰማያትና የምድር ንግሥና የእርሱ ብቻ ነው፡፡ ነገሮችም (ሁሉ) ወደ አላህ ብቻ ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(57,6,'57:6','ሌሊትን በቀን ውስጥ ያስገባል፡፡ ቀንንም በሌሊት ውስጥ ያስገባል፡፡ እርሱም በልቦች ውስጥ የተደበቁትን ዐዋቂ ነወ፡፡');
INSERT INTO "sadiq_sani" VALUES(57,7,'57:7','በአላህና በመልክተኛው እመኑ፡፡ (አላህ) በእርሱ ተተኪዎች ባደረጋችሁም ገንዘብ ለግሱ፡፡ እነዚያም ከእናንተ ውስጥ ያመኑትና የለገሱት ለእነርሱ ታላቅ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,8,'57:8','መልክተኛው በጌታችሁ እንድታምኑ የሚጠራችሁ ሲኾን (ጌታችሁ) ኪዳናችሁንም');
INSERT INTO "sadiq_sani" VALUES(57,9,'57:9','እርሱ ከጨለማዎች ወደ ብርሃን ሊያወጣችሁ ያ ግልጾች የኾኑን አንቀጾች በበሪያው ላይ የሚያወርድ ነው፡፡ አላህም ለእናንተ በእርግጥ ርኅሩህ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,10,'57:10','የሰማያትና የምድር ውርስ ለአላህ ሲኾን በአላህ መንገድ የማትለግሱትም ለእናንተ ምን አልላችሁ? ከእናንተ ውስጥ (መካን) ከመክፈት በፊት የለገሰና የተጋደለ ሰው (ከተከፈተች በኋላ ከለገሰና ከተጋደለ ጋር) አይስተካከልም፡፡ ከእነዚያ በኋላ ከለገሱትና ከተጋደሉት እነዚህ በደረጃ በጣም የላቁ ናቸው፡፡ ሁሉንም አላህ መልካሚቱን ተስፋ ቃል ገብቶላቸዋል፡፡ አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,11,'57:11','ያ ለአላህ መልካም ብድርን የሚያበድር ለእርሱም (አላህ) የሚያነባብርለት ሰው ማነው? ለእርሱም መልካም ምንዳ አልለው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,12,'57:12','ምእመናንንና ምእምናትን በስተፊቶቻቸው፣ በቀኞቻቸውም ብርሃናቸው የሚሮጥ ሲኾን በምታያቸው ቀን (ምንዳ አልላቸው)፡፡ ዛሬ ብስራታችሁ በውስጦቻቸው ዘውታሪዎች ስትኾኑ ከሥሮቻቸው ወንዞች የሚፈስሱባቸው ገነቶች ናቸው (ይባላሉ)፡፡ ይህ እርሱ ታላቁ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,13,'57:13','መናፍቃንና መናፍቃት ለእነዚያ ለአመኑት «ተመልከቱን፤ ከብርሃናችሁ እናበራለንና» የሚሉበትን ቀን (አስታውስ)፡፡ «ወደ ኋላችሁ ተመለሱ፡፡ ብርሃንንም ፈልጉ» ይባላሉ፡፡ ግቢው በውስጡ ችሮታ (ገነት) ያለበት ውጭውም ከበኩሉ ስቃይ (እሳት) ያለበት የኾነ (ደጃፍ ባለው አጥር)፡፡');
INSERT INTO "sadiq_sani" VALUES(57,14,'57:14','«ከእናንተ ጋር አልነበርንምን?» በማለት ይጠሩዋቸዋል፡፡ «እውነት ነው፡፡ ግን እናንተ ነፍሶቻችሁን አጠፋችሁ፡፡ (በነቢዩ አደጋዎችን) ተጠባበቃችሁም፡፡ ተጠራጠራችሁም፡፡ የአላህም ትዕዛዝ እስከ መጣ ድረስ ከንቱ ምኞቶች አታለሏችሁ፡፡ አታላዩም (ሰይጣን) በአላህ (መታገስ) ሸነገላችሁ፤» ይሏቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(57,15,'57:15','«ዛሬም ከእናንተ ቤዛ አይወስድም፡፡ ከእነዚያ ከካዱትም፡፡ መኖሪያችሁ እሳት ናት፡፡ እርሷ ተገቢያችሁ ናት፡፡ ምን ትከፋም መመለሻ!» (ይሏቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(57,16,'57:16','ለነዚያ ለአመኑት ለአላህ ተግሳጽና ከቁርኣንም ለወረደው ልቦቻቸው ሊፈሩ እንደእነዚያም በፊት መጽሐፍን እንደ ተሰጡትና በእነርሱ ላይ ጊዜ እንደረዘመባቸው ልቦቻቸውም እንደ ደረቁት ላይኾኑ (ጊዜው) አልቀረበምን? ከእነርሱም ብዙዎቹ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,17,'57:17','አላህ ምድርን ከሞተች በኋላ ሕያው የሚያደርጋት መኾኑን ዕወቁ፡፡ ታወቁ ዘንድ አንቀጾችን ለእናንተ በእርግጥ አብራራንላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(57,18,'57:18','የመጸወቱ ወንዶችና፣ የመጸወቱ ሴቶች፣ ለአላህም መልካምን ብድር ያበደሩ ለእነርሱ መልካም ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,19,'57:19','እነዚያም በአላህና በመልክተኞቹ ያመኑት እነዚያ እነርሱ በጣም እውነተኞቹ በጌታቸውም ዘንድ መስካሪዎቹ ናቸው፡፡ ለእነርሱ ምንዳቸው ብርሃናቸውም አልላቸው፡፡ እነዚያም የካዱት በአንቀጾቻችንም ያስተባበሉት እነዚያ የእሳት ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,20,'57:20','ቅርቢቱ ሕይወት ጨዋታና ዛዛታ፣ ማጌጫም፣ በመካከላችሁም መፎካከሪያ፣ በገንዘቦችና በልጆችም ብዛት መበላለጫ ብቻ መኾንዋን ዕወቁ፡፡ (እርሷ) በቃዩ ገበሬዎችን እንደሚያስደስት ዝናም፣ ከዚያም በቃዩ እንደሚደርቅና ገርጥቶ እንደምታየው፣ ከዚያም የተሰባበረ እንደሚኾን ብጤ ናት፣ በመጨረሻይቱም ዓለም ብርቱ ቅጣት ከአላህም ምሕረትና ውዴታ አልለ፡፡ የቅርቢቱም ሕይወት የመታለያ ጥቅም እንጅ ሌላ አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(57,21,'57:21','ከጌታችሁ ወደ ኾነች ምሕረት፣ ወርዷ እደ ሰማይና ምድር ወርድ ወደ ሆነችም ገነት ተሽቀዳደሙ፡፡ ለእነዚያ በአላህና በመልክተኞቹ ላመኑት ተዘጋጅታለች፡፡ ይህ የአላህ ችሮታ ነው፡፡ ለሚሻው ሰው ይሰጠዋል፡፡ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,22,'57:22','በምድርም በነፍሶቻችሁም መከራ (ማንንም) አትነካም ሳንፈጥራት በፊት በመጽሐፍ የተመዘገበች ብትኾን እንጅ፡፡ ይህ በአላህ ላይ ገር ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,23,'57:23','(ይህ ማሳወቃችን) ባመለጣችሁ ጸጋ ላይ እንዳታዝኑ አላህም በሰጣችሁ ነገር (በትዕቢት) እንዳትደሰቱ ነው፡፡ አላህም ኩራተኛን ጉረኛን ሁሉ አይወድም፡፡');
INSERT INTO "sadiq_sani" VALUES(57,24,'57:24','(እነርሱም) እነዚያ የሚሰስቱና ሰዎችንም በስስት የሚያዝዙ ናቸው፡፡ (ከእውነት) የሚሸሽም ሰው (ጥፋቱ በራሱ ላይ ነው)፡፡ አላህም እርሱ ተብቃቂ ምስጉን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,25,'57:25','መልክተኞቻችንን በግልጽ ማስረጃዎች በእርግጥ ላክን፡፡ ሰዎችም በትክክል ቀጥ እንዲሉ መጽሐፎችንና ሚዛንን ወደእነርሱ አወረድን፤ ብረትንም በውስጡ ብርቱ ኀይልና ለሰዎችም መጠቃቀሚያዎች ያልሉበት ሲኾን አወረድን፡፡ (እንዲጠቀሙበት) አላህም ሃይማኖቱንና መልክተኞቹን በሩቅ ኾኖ የሚረዳውን ሰው ሊያውቅ (ሊገልጽ አወረደው)፡፡ አላህ ብርቱ አሸናፊ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(57,26,'57:26','ኑሕን፣ ኢብራሂምንም በእርግጥ ላክን፡፡ በዘሮቻቸውም ውስጥ ነቢይነትንና መጽሐፎችን አደረግን፡፡ ከእነርሱም ቅን አልለ፡፡ ከእነርሱም ብዙዎቹ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,27,'57:27','ከዚያም በዱካዎቻቸው ላይ መልክተኞቻችንን አስከታተልን፡፡ የመርየምን ልጅ ዒሳንም አስከተልን፡፡ ኢንጂልንም ሰጠነው፡፡ በእነዚያም በተከተሉት (ሰዎች) ልቦች ውሰጥ መለዘብንና እዝነትን፣ አዲስ የፈጠሩዋትንም ምንኩስና አደረግን፡፡ በእነርሱ ላይ (ምንኩስናን) አልጻፍናትም፡፡ ግን የአላህን ውዴታ ለመፈለግ ሲሉ (ፈጠሩዋት)፡፡ ተገቢ አጠባበቋንም፤ አልጠበቋትም፡፡ ከእነርሱም ለእነዚያ ላመኑት ምንዳቸውን ሰጠናቸው፡፡ ከእነርሱም ብዙዎቹ አመጸኞች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,28,'57:28','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፡፡ በመልእክተኛውም እመኑ፡፡ ከችሮታው ሁለትን ክፍሎች ይሰጣችኋልና፡፡ ለእናንተም በእርሱ የምትኼዱበት የኾነ ብርሃንን ያደርግላችኋል፡፡ ለእናንተም ይምራል፡፡ አላህም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(57,29,'57:29','(ይህም) የመጽሐፉ ሰዎች ከአላህ ችሮታ በምንም ላይ የማይችሉ መኾናቸውን ችሮታም በአላህ እጅ ነው፡፡ ለሚሻው ሰው ይሰጠዋል ማለትን እንዲያውቁ ነው፤ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,1,'58:1','አላህ የዚያችን በባሏ (ነገር) የምተከራከርህንና ወደ አላህ የምታሰሙተውን (ሴት) ቃል በእርግጥ ሰማ፡፡ አላህም (በንግግር) መመላለሳችሁን ይሰማል፡፡ አላህ ሰሚ ተመልካች ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(58,2,'58:2','እነዚያ ከእናንተ ውስጥ ከሚስቶቻቸው እንደናቶቻችን ጀርባዎች ይኹኑብን በማለት የሚምሉ እነርሱ እናቶቻቸው አይደሉም፡፡ እናቶቻቸው እኒያ የወለዱዋቸው ብቻ ናቸው፡፡ እነርሱም (በዚህ ቃል) ከንግግር የተጠላንና ውሸትን በእርግጥ ይናገራሉ፡፡ አላህም (ለሚጸጸት) ይቅር ባይ መሓሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,3,'58:3','እነዚያም ከሚስቶቻቸው እንደናቶቻችን ጀርባዎች ኹኑብን በማለት የሚምሉ ከዚያም ወደ ተናገሩት የሚመለሱ ሳይነካኩ በፊት ጫንቃን ነጻ ማውጣት በነርሱ ላይ አለባቸው፡፡ እነሆ በእርሱ ትገሰጹበታለችሁ፡፡ አላህም በምትሠሩት ሁሉ ውስጥ ዓዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,4,'58:4','ያላገኘም ሰው ከመነካከታቸው በፊት ሁለት የተከታተሉ ወሮችን መጾም አለበት፡፡ ያልቻለም ሰው ስድሳ ድኾችን ማብላት (አለበት)፡፡ ይህ በአላህና በመልክተኛው እንድታምኑ ነው፡፡ ይህችም የአላህ ሕግጋት ናት፡፡ ለከሓዲዎችም አሳማሚ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,5,'58:5','ግልጾች ማስረጃዎችን ያወረድን ስንኾን እነዚያ አላህንና መልክተኛውን የሚከራከሩ እነዚያ ከእነርሱ በፊት የነበሩት እንደተዋረዱ ይዋረዳሉ፡፡ ለከሓዲዎችም አዋራጅ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,6,'58:6','አላህ ሁላቸውንም በሚቀስቃሳቸው ቀን (ይቀጣቸዋል)፡፡ የሠሩትንም ሁሉ ይነግራቸዋል፡፡ የረሱት ሲኾኑ አላህ ዐውቆታል፡፡ አላህም በነገሩ ሁሉ ላይ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,7,'58:7','አላህ በሰማያት ውስጥ ያለውን በምድርም ውሰጥ ያለውን ሁሉ የሚያውቅ መኾኑን አታይምን? ከሦስት ሰዎች መሾካሾክ አይኖርም እርሱ አራተኛቸው ቢኾን እንጅ፡፡ ከአምስትም (አይኖርም) እርሱ ስድስተኛቸው ቢኾን እንጅ፡፡ ከዚያ ያነሰም የበዛም (አይኖርም) እርሱ የትም ቢኾኑ እንጂ፡፡ ከዚያም በትንሣኤ ቀን የሠሩትን ሁሉ ይነግራቸዋል፡፡ አላህ ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,8,'58:8','ወደእነዚያ (በመጥፎ) ከመሾካሾክ ወደ ተከለከሉት፣ ከዚያም ከእርሱ ወደ ተከለከሉት ነገር ወደሚመለሱት፣ በኃጢአትና ድንበር በማለፍ መልክተኛውን በመቃወም ወደሚንሾካሾኩት አላየህምን? (ሰላም ሊሉ) በመጡህም ጊዜ አላህ በእርሱ ባላናገረህ ቃል ያናግሩሃል፡፡ በነፍሶቻቸውም ውስጥ (ነቢይ ከኾንክ) «በምንለው ነገር አላህ አይቀጣንም ኖሮአልን?» ይላሉ፡፡ ገሀነም የሚገቧት ሲኾኑ በቂያቸው ናት፡፡ ምን ትከፋም መመለሻ!');
INSERT INTO "sadiq_sani" VALUES(58,9,'58:9','እናንተ ያመናችሁ ሆይ! በተንሾካሾካችሁ ጊዜ በኃጢአትና ወሰንን በማለፍ፣ መልክተኛውንም በመቃወም አትንሾካሾኩ፡፡ ግን በበጎ ሥራና አላህን በመፍራት ተወያዩ፡፡ ያንንም ወደእርሱ የምትሰበሰቡበትን አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(58,10,'58:10','(በመጥፎ) መንሾካሾክ ከሰይጣን ብቻ ነው፡፡ እነዚያ ያመኑት ያዝኑ ዘንድ (ይቀሰቅሰዋል)፡፡ በአላህ ፈቃድ ካልኾነ በቀር በምንም አይጎዳቸውም፡፡ በአላህ ላይም አማኞች ይጠጉ፡፡');
INSERT INTO "sadiq_sani" VALUES(58,11,'58:11','እናንተ ያመናችሁ ሆይ! ለእናንተ በመቀመጫዎች ስፍራ ተስፋፉ በተባላችሁ ጊዜ (ስፍራን) አስፉ፡፡ አላህ ያሰፋላችኋልና፡፡ ተነሱ በተባላችሁም ጊዜ ተነሱ፡፡ አላህ ከናንተ እነዚያን ያመኑትንና እነዚያንም ዕውቀትን የተሰጡትን በደረጃዎች ከፍ ያደርጋል፡፡ አላህም በምትሰሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,12,'58:12','እናንተ ያመናችሁ ሆይ! መልክተኛውን በተወያያችሁ ጊዜ ከመወያየታችሁ በፊት ምጽዋትን አስቀድሙ፡፡ ይህ ለእናንተ መልካም ነው፡፡ አጥሪም ነው፡፡ ባታገኙም አላህ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,13,'58:13','ከውይይታችሁ በፊት ምጽዋቶችን ከማስቀደም (ድህነትን) ፈራችሁን? ባልሠራችሁም ጊዜ አላህ ከእናንተ ጸጸትን የተቀበለ ሲኾን ሶላትን ስገዱ፡፡ ዘካንም ስጡ፡፡ አላህንና መልክተኛውንም ታዘዙ፡፡ አላህም ውስጥ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,14,'58:14','ወደእነዚያ አላህ በእነርሱ ላይ የተቆጣባቸውን ሕዝቦች ወደ ተወዳጁት (መናፍቃን) አላየኽምን? እነርሱ ከእናንተ አይደሉም፡፡ ከእነርሱም አይደሉም፡፡ እነርሱም የሚያውቁ ሲኾኑ በውሸት ላይ ይምላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(58,15,'58:15','አላህ ለእነርሱ ብርቱን ቅጣት አዘጋጀ፡፡ እነርሱ ይሠሩት የነበሩት ሥራ ምንኛ ከፋ!');
INSERT INTO "sadiq_sani" VALUES(58,16,'58:16','መሓሎቻቸውን ጋሻ አድርገው ያዙ፡፡ ከአላህም መንገድ አገዱ፡፡ ስለዚህ ለእነርሱ አዋራጅ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,17,'58:17','ገንዘቦቻቸውና ልጆቻቸው ከአላህ (ቅጣት) ምንንም ከእነርሱ አያድኗቸውም፡፡ እነዚያ የእሳት ጓዶች ናቸው፡፡ እነርሱ በእርሷ ውስጥ ዘውታሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,18,'58:18','አላህ የተሰበሰቡ ኾነው በሚያስነሳቸው ቀን ለእናንተ እንደሚምሉላችሁም እነርሱ (በሚጠቅም) ነገር ላይ መኾናቸውን የሚያስቡ ኾነው ለእርሱ በሚምሉበት ቀን (አዋራጅ ቅጣት አልላቸው)፡፡ ንቁ! እነርሱ ውሸታሞቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,19,'58:19','በእነርሱ ላይ ሰይጣን ተሾመባቸው፡፡ አላህንም ማስታወስን አስረሳቸው፡፡ እነዚያ የሰይጣን ጭፍሮች ናቸው፡፡ ንቁ! የሰይጣን ጭፍሮች ከሳሪዎቹ እነርሱ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,20,'58:20','እነዚያ አላህንና መልክተኛውን የሚከራከሩት እነዚያ በጣም በወራዶቹ ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(58,21,'58:21','አላህ፡- «እኔ በእርግጥ አሸንፋለሁ፣ መልክተኞቼም (ያሸንፋሉ)» ሲል፤ ጽፏል፡፡ አላህ ብርቱ አሸናፊ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(58,22,'58:22','በአላህና በመጨረሻው ቀን የሚያምኑትን ሕዝቦች አላህንና መልክተኛውን የሚከራከሩትን ሰዎች፤ አባቶቻቸው፣ ወይም ልጆቻቸው፣ ወይም ወንድሞቻቸው፣ ወይም ዘመዶቻቸው ቢኾኑም እንኳ የሚወዳጁ ኾነው አታገኛቸውም፡፡ እነዚያ በልቦቻቸው ውስጥ እምነትን ጽፏል፡፡ ከእርሱም በኾነ መንፈስ ደግፏቸዋል፡፡ ከሥሮቻቸውም ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘውታሪዎች ሲኾኑ ያገባቸዋል፡፡ አላህ ከእነርሱ ወዷል፡፡ ከእርሱም ወደዋል፡፡ እነዚያ የአላህ ሕዝቦች ናቸው፡፡ ንቁ! የአላህ ሕዝቦቸ እነርሱ ምኞታቸውን የሚያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,1,'59:1','በሰማያትና በምድር ውስጥ ያለው ሁሉ ለአላህ አሞገሰ፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,2,'59:2','እርሱ ያ ከመጽሐፉ ሰዎች እነዚያን የካዱትን ከቤቶቻቸው ለመጀመሪያው ማውጣት ያወጣቸው ነው፡፡ መውጣታቸውን አላሰባችሁም፡፡ እነርሱም ምሽጎቻቸው ከአላህ (ኀይል) የሚከላከሉላቸው መኾናቸውን አሰቡ፡፡ አላህም ካላሰቡት ስፍራ መጣባቸው፡፡ በልቦቻቸውም ውስጥ መርበድበድን ጣለባቸው፡፡ ቤቶቻቸውን በእጆቻቸውና በምእምናኖቹ እጆች ያፈርሳሉ፡፡ አስተውሉም የአእምሮ ባለቤቶች ሆይ!');
INSERT INTO "sadiq_sani" VALUES(59,3,'59:3','በእነርሱም ላይ አላህ ከአገር መውጣትን ባልጻፈ ኖሮ በቅርቢቱ ዓለም በቀጣቸው ነበር፡፡ ለእነርሱም በመጨረሻይቱ (ዓለም) የእሳት ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,4,'59:4','ይህ እነርሱ አላህንና መልክተኛውን በመከራከራቸው ነው፡፡ አላህንም የሚከራከር ሰው (ይቀጣዋል)፡፡ አላህ ቅጣተ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(59,5,'59:5','ከዘንባባ (ከተምር ዛፍ) ማንኛዋም የቆረጣችኋት ወይም በግንዶቿ ላይ የቆመች ኾና የተዋችኋት በአላህ ፈቃድ ነው፡፡ አመጸኞቸንም ያዋርድ ዘንድ (በመቁረጥ ፈቀደ)፡፡');
INSERT INTO "sadiq_sani" VALUES(59,6,'59:6','ከእነርሱም (ገንዘብ) በመልእክተኛው ላይ አላህ የመለሰው በእርሱ ላይ ፈረሶችንና ግመሎችን አላስጋለባችሁበትም፡፡ ግን አላህ መልክተኞቹን በሚሻው ሰው ላይ ይሾማል፡፡ አላህም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,7,'59:7','አላህ ከከተሞቹ ሰዎች (ሀብት) በመልክተኛው ላይ የመለሰው (ሀብት) ከእናንተ ውስጥ በሀብታሞቹ መካከል ተዘዋዋሪ እንዳይኾን፡፡ ለአላህና ለመልክተኛው፣ ለዝምድና ባለቤትም፣ አባት ለሌላቸው ልጆችም፣ ለድኾችም፣ ለመንገደኛም (የሚስሰጥ) ነው፡፡ መልክተኛውም የሰጣችሁን (ማንኛውንም) ነገር ያዙት፡፡ ከእርሱም የከለከላችሁን ነገር ተከልከሉ፡፡ አላህንም ፍሩ፡፡ አላህ ቅጣተ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(59,8,'59:8','ለእነዚያ የአላህን ችሮታና ውዴታውን የሚፈልጉ፣ አላህንና መልክተኛውንም የሚረዱ ኾነው ከአገሮቻቸውና ከገንዘቦቻቸው ለተወጡት ስደተኞች ድኾች (ይስሰጣል)፡፡ እነዚያ እነርሱ እውነተኞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,9,'59:9','እነዚያም ከበፊታቸው አገሪቱን መኖሪያ ያደረጉት እምነትንም የለመዱት ወደእነርሱ የተሰደዱትን ሰዎች ይወዳሉ፡፡ (ስደተኞቹ) ከተሰጡትም ነገር በልቦቻቸው ውስጥ ቅሬታን አያገኙም፡፡ በእነርሱ ላይ ችግር ቢኖርባቸውም በነፍሶቻቸው ላይ ሌላውን ይመርጣሉ፡፡ የነፍሱንም ንፍገት የሚጠበቅ ሰው እነዚያ እነሱ ምኞታቸውን አግኚዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,10,'59:10','እነዚያም ከበኋላቸው የመጡት «ጌታችን ሆይ! ለእኛም ለእነዚያም በእምነት ለቀደሙን ወንድሞቻችን ምሕረት አድርግ፡፡ በልቦቻችንም ውስጥ ለእነዚያ ለአመኑት (ሰዎች) ጥላቻን አታድርግ፡፡ ጌታችን ሆይ! አንተ ርኅሩህ አዛኝ ነህና» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(59,11,'59:11','ወደእነዚያ ወደ ነፈቁት አላየህምን? ከመጽሐፉ ሰዎች ውስጥ ለእነዚያ ለካዱት ወንድሞቻቸው (ከአገር) «ብትባረሩ አብረናችሁ እንወጣለን፡፡ በእናንተም (ጉዳይ) አንድንም በፍጹም አንታዘዝም፡፡ ብትገድሉም በእርግጥ እንረዳችኋለን» ይሏቸዋል፡፡ አላህም እነርሱ ውሸታሞች መኾናቸውን ይመሰክራል፡፡');
INSERT INTO "sadiq_sani" VALUES(59,12,'59:12','ቢወጥጡ አብረዋቸው አይወጡም፤ ቢገደሉም አይረዱዋቸውም፡፡ ቢረዷቸውም (ለሽሽት) ጀርባቸውን ያዞራሉ፡፡ ከዚያም እርዳታን አያገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(59,13,'59:13','እናንተ በልቦቻቸው ውስጥ በማስፈራት ከአላህ ይልቅ የበረታችሁ ናችሁ፡፡ ይህ እነርሱ የማያውቁ ሕዝቦች በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,14,'59:14','በተመሸጉ ከተሞች ውስጥ ወይም ከአጥሮች ጀርባ ኾነው እንጅ፤ የተሰበሰቡ ኾነው አይዋጉዋችሁም፡፡ ኀይላቸው በመካከላቸው ብርቱ ነው፡፡ ልቦቻቸው የተበታተኑ ሲኾኑ የተሰበሰቡ ናቸው ብለህ ትጠረጥራቸዋለህ፡፡ ይህ እነሱ አእምሮ የሌላቸው ሕዝቦች በመኾናቸው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,15,'59:15','(ብጤያቸው) እንደእነዚያ ከእነሱ በፊት በቅርብ ጊዜ የነገራቸውን ቅጣት እንደቀመሱት ብጤ ነው፡፡ ለእነርሱም አሳማሚ ቅጣት አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,16,'59:16','እንደ ሰይጣን ብጤ ለሰውየው «ካድ» ባለው ጊዜና በካደም ጊዜ «እኔ ከአንተ ንጹሕ ነኝ እኔ አላህን የዓለማትን ጌታ እፈራለሁ» እንዳለው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,17,'59:17','መጨረሻቸውም እነርሱ በውስጡ ዘውታሪዎች ሲኾኑ በእሳት ውስጥ መኾን ነው፡፡ ይህም የበዳዮች ዋጋ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,18,'59:18','እናንተ ያመናችሁ ሆይ! አላህን ፍሩ፡፡ ማንኛይቱም ነፍስ ለነገ ያስቀደመችውን ትመልከት፡፡ አላህንም ፍሩ፡፡ አላህ በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,19,'59:19','እንደነዚያም አላህን እንደረሱትና ነፍሶቻቸውን እንዳስረሳቸው (ሰዎች) አትኹኑ፡፡ እነዚያ እነርሱ አመጸኞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,20,'59:20','የእሳት ጓዶችና የገነት ጓዶች አይተካከሉም፡፡ የገነት ጓዶች እነርሱ ምኞታቸውን አግኚዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,21,'59:21','ይህንን ቁርኣን በተራራ ላይ ባወረድነው ኖሮ ከአላህ ፍራቻ (የተነሳ) ተዋራጅ ተሰንጣቂ ኾኖ');
INSERT INTO "sadiq_sani" VALUES(59,22,'59:22','እርሱ አላህ ነው፤ ያ ከእርሱ በቀር ሌላ አምላክ የሌለ፣ ሩቁንና ቅርቡን ዐዋቂ የኾነ ነው፡፡ እርሱ እጅግ በጣም ርኅሩህ በጣም አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(59,23,'59:23','እርሱ አላህ ነው፡፡ ያ ከእርሱ በቀር ሌላ አምላክ የሌለ፣ ንጉሡ ከጉድለት ሁሉ የጠራው፣ የሰላም ባለቤቱ፣ ጸጥታን ሰጪው፣ ባሮቹን ጠባቂው፣ አሸናፊው፣ ኀያሉ፣ ኩሩው ነው፡፡ አላህ ከሚያጋሩት ሁሉ ጠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(59,24,'59:24','እርሱ አላህ ፈጣሪው (ከኢምንት) አስገኚው፣ ቅርጽን አሳማሪው ነው፡፡ ለእርሱ መልካሞች ስሞች አልሉት፡፡ በሰማያትና በምድርም ያለው ሁሉ ለእርሱ ያሞግሳል፡፡ እርሱም');
INSERT INTO "sadiq_sani" VALUES(60,1,'60:1','እናንተ ያመናችሁ ሆይ! ጠላቶቼንና ጠላቶቻችሁን ወዳጆች አድርገችሁ አትያዙ፡፡ ከእውነቱ የመጣላችሁን ሃይማኖት በእርግጥ የካዱ ሲኾኑ ውዴታን ወደእነርሱ ታደርሳላችሁ፡፡ መልክተኛውንና እናንተን በአላህ በጌታችሁ ስላመናችሁ (ከመካ) ያወጣሉ፡፡ በመንገዴ ለመታገልና ውዴታዬን ለመፈለግ የወጣችሁ እንደኾናችሁ (ወዳጆች አድርጋችሁ አትያዙዋቸው)፡፡ እኔ የምትደብቁትንና የምትገልጹትን ሁሉ የማውቅ ስኾን ወደእነርሱ በፍቅር ትመሳጠራላችሁ፡፡ ከእናንተም (ይህንን) የሚሠራ ሰው ቀጥተኛውን መንገድ በእርግጥ ተሳሳተ፡፡');
INSERT INTO "sadiq_sani" VALUES(60,2,'60:2','ቢያሸንፏችሁ ለእናንተ ጠላቶች ይኾናሉ፡፡ እጆቻቸውንና ምላሶቻቸውንም ወደእናንተ በክፉ ይዘረጋሉ፡፡ ብትክዱም ተመኙ፡፡');
INSERT INTO "sadiq_sani" VALUES(60,3,'60:3','ዘመዶቻችሁም፣ ልጆቻችሁም በትንሣኤ ቀን አይጠቅሟችሁም፤ (አላህ) በመካከላችሁ ይለያል፡፡ አላህም የምትሠሩትን ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(60,4,'60:4','በኢብራሂምና በእነዚያ ከእርሱ ጋር በነበሩት (ምእምናን) መልካም መከተል አለቻችሁ፡፡ ለሕዝቦቻቸው «እኛ ከእናንተ ከአላህ ሌላ ከምትግገዙትም ንጹሖች ነን፡፡ በእናንተ ካድን፡፡ በአላህ አንድ ብቻ ሲኾን እስከምታምኑ ድረስ በእኛና በእናንተ መካከል ጠብና ጥላቻ ዘወትር ተገለጸ፡፡» ባሉ ጊዜ (መልካም መከተል አለቻችሁ)፡፡ ኢብራሂም ለአባቱ «እኔ ለአንተ ከአላህ (ቅጣት) ምንም የማልጠቅም ስኾን ለአንተ በእርግጥ ምሕረትን እለምንልኻለሁ» ማለቱ ብቻ ሲቀር፡፡ «ጌታችን ሆይ! በአንተ ላይ ተመካን፡፡ ወደ አንተም ተመለስን መመለሻም ወደ አንተ ብቻ ነው፤» (ባለው ተከተሉት)፡፡');
INSERT INTO "sadiq_sani" VALUES(60,5,'60:5','ጌታችን ሆይ! ለእነዚያ ለካዱት ሰዎች መሞከሪያ አታድርገን፡፡ ለእኛም ምሕረት አድርግልን፡፡ ጌታችን ሆይ! አንተ አሸናፊው ጥበበኛው አንተ ብቻ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(60,6,'60:6','ለእናንተ አላህንና የመጨረሻውን ቀን ለሚፈራ ሰው በእነርሱ መልካም መከተል አልላችሁ፡፡ የሚዞርም ሰው (ራሱን ይጎዳል)፡፡ አላህ ተብቃቂው ምስጉኑ እርሱ ብቻ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(60,7,'60:7','በናንተና በእነዚያ ከእነርሱ ጋር በተጣላችሁት ሰዎች መካከል አላህ መፋቀርን ሊያደርግ ይቻላል፡፡ አላህም ቻይ ነው፡፡ አላህም በጣም አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(60,8,'60:8','ከእነዚያ በሃይማኖት ካልተጋደሏችሁ ከአገሮቻችሁም ካላወጡዋችሁ (ከሓዲዎች) መልካም ብትውሉላቸውና ወደእነርሱ ብታስተካክሉ አላህ አይከለክላችሁም፡፡ አላህ ትክክለኞችን ይወዳልና፡፡');
INSERT INTO "sadiq_sani" VALUES(60,9,'60:9','አላህ የሚከለክላችሁ ከእነዚያ በሃይማኖት ከተጋደሉዋችሁ፣ ከቤቶቻችሁም ካወጡዋችሁ፣ እናንተም በማውጣት ላይ ከረዱት (ከሓዲዎች) እንዳትወዳጁዋቸው ብቻ ነው፡፡ ወዳጅ የሚያደርጓቸውም ሰዎች እነዚያ እነርሱ በዳዮቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(60,10,'60:10','እላንተ ያመናችሁ ሆይ! ምእምናት ስደተኞች ኾነው በመጧችሁ ጊዜ (ለሃይማኖት መሰደዳቸውን) ፈትኑዋቸው፡፡ አላህ በእምነታቸው ይበልጥ ዐዋቂ ነው፡፡ አማኞችም መኾናቸውን ብትውቁ ወደ ከሓዲዎቹ አትመልሱዋቸው፡፡ እነርሱ (ሴቶቹ) ለእነርሱ የተፈቀዱ አይደሉምና፡፡ እነርሱም (ወንዶቹ) ለእነርሱ አይፈቀዱምና፡፡ ያወጡትንም ገንዘብ ስጧቸው፡፡ መህራቸውንም በሰጣችኋቸው ጊዜ ብታገቡዋቸው በእናንተ ላይ ኃጢአት የለባችሁም፡፡ የከሓዲዎቹንም ሴቶች የጋብቻ ቃል ኪዳኖች አትያዙ፤ ያወጣችሁትንም ገንዘብ ጠይቁ፡፡ ያወጡትንም ይጠይቁ፡፡ ይህ የአላህ ፍርድ ነው፡፡ በመካከላችሁ ይፈርዳል፡፡ አላህም ዐዋቂ ጥበበኛ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(60,11,'60:11','ከሚስቶቻችሁም ወደ ከሓዲዎቹ አንዳቸው ቢያመልጧችሁ ቀጥሎም ብትዘምቱባቸው ለእነዚያ ሚስቶቻቸው ለኼዱባቸው (ሰዎች) ያወጡትን ብጤ ስጧቸው፡፡ ያንንም እናንተ በርሱ ያመናችሁበትን አላህን ፍሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(60,12,'60:12','አንተ ነቢዩ ሆይ! ምእምናቶቹ በአላህ ምንንም ላያጋሩ፣ ላይሰርቁም ላያመነዝሩም፣ ልጆቻቸውን ላይገድሉም፣ በእጆቻቸውና በእግሮቻቸው መካከልም የሚቀጣጥፉት የኾነን ኃጢኣት ላያመጡ (ላይሠሩ) በበጎም ሥራ ትዕዛዝህን ላይጥሱ ቃል ኪዳን ሊገቡልህ በመጡህ ጊዜ ኪዳን ተጋባቸው፡፡ ለእነርሱም አላህን ምሕረት ለምንላቸው፡፡ አላህ በጣም መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(60,13,'60:13','እላንተ ያመናቸሁ ሆይ! አላህ በእነርሱ ላይ የተቆጣባቸውን ሕዝቦች አትወዳጁ፡፡ ከሓዲዎች ከመቃብር ሰዎች ተስፋ እንደቆረጡ ከመጨረሻይቱ ዓለም (ምንዳ) በእርግጥ ተስፋ ቆርጠዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(61,1,'61:1','በሰማያት ውስጥ ያለው በምድርም ውስጥ ያለው ሁሉ ለአላህ አሞገሠ፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(61,2,'61:2','እላንተ ያመናችሁ ሆይ! የማትሠሩትን ነገር ለምን ትናገራላችሁ?');
INSERT INTO "sadiq_sani" VALUES(61,3,'61:3','የማትሠሩን ነገር መናገራችሁ አላህ ዘንድ መጠላቱ ተለቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(61,4,'61:4','አላህ እነዚያን እነርሱ ልክ እንደ ተናሰ ግንብ የተሰለፉ ኾነው በመንገዱ (በሃይማኖቱ) የሚጋደሉትን ይወዳል፡፡');
INSERT INTO "sadiq_sani" VALUES(61,5,'61:5','ሙሳም ለሕዝቦቹ «ሕዝቦቼ ሆይ! እኔ ወደእናንተ የአላህ መልክተኛ መኾኔን በእርግጥ የምታውቁ ስትኾኑ ለምን ታሰቃዩኛላችሁ?» ባለ ጊዜ (አስታውስ፡፡ ከእውነት) በተዘነበሉም ጊዜ አላህ ልቦቻቸውን አዘነበላቸው፡፡ አላህም አመጸኞችን ሕዝቦች አያቀናም፡፡');
INSERT INTO "sadiq_sani" VALUES(61,6,'61:6','የመርየም ልጅ ዒሳም፡- «የእስራኤል ልጆች ሆይ! እኔ ከተውራት በፊቴ ያለውን የማረጋግጥና ከእኔ በኋላ በሚመጣው መልክተኛ ስሙ አሕመድ በኾነው የማበስር ስኾን ወደእናንተ (የተላክሁ) የአላህ መልክተኛ ነኝ» ባለ ጊዜ (አስታውስ)፡፡ በግልጽ ተዓምራቶች በመጣቸውም ጊዜ «ይህ ግልጽ ድግምት ነው» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(61,7,'61:7','እርሱ ወደ ኢስላም የሚጥጠራ ሲኾን በአላህ ላይ ውሸትን ከቀጣጠፈ ሰውም ይበልጥ በደለኛ ማን ነው? አላህም በዳዮችን ሕዝቦች አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(61,8,'61:8','የአላህን ብርሃን በአፎቻቸው ሊያጠፉ ይሻሉ፡፡ አላህም ከሓዲዎቹ ቢጠሉም እንኳ ብርሃኑን ገላጭ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(61,9,'61:9','እርሱ ያ አጋሪዎቹ ቢጠሉም መልክተኛውን በመምሪያ (መጽሐፍ) በእውነተኛው ሃይማኖትም (በኢስላም) ከሃይማኖት ሁሉ በላይ ይፋ ሊያደርገው የላከ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(61,10,'61:10','«እናንተ ያመናችሁ ሆይ! ከአሳማሚ ቅጣት የምታድናችሁ የኾነችን ንግድ ላመላክታችሁን?» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(61,11,'61:11','(እርሷም) በአላህና በመልክተኛው ታምናላችሁ፣ በአላህ መንገድም በገንዘቦቻችሁ በነፍሶቻችሁም ትታገላላችሁ፣ ይህ የምታውቁ ብትኾኑ ለእናንተ የተሻለ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(61,12,'61:12','ኃጢኣቶቻችሁን ለእናንተ ይምራል፡፡ ከሥሮቻቸው ወንዞች የሚፈሱባቸውንም ገነቶች ያገባችኋል፡፡ በመኖሪያ ገነቶችም በሚያማምሩ ቤቶች ውስጥ (ያስቀምጣችኋል)፡፡ ይህ ታላቅ ዕድል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(61,13,'61:13','ሌላይቱንም የምትወዷትን (ጸጋ ይሰጣችኋለ)፡፡ ከአላህ የኾነ እርዳታና ቅርብ የኾነ የአገር መክፈት ነው፡፡ ምእምናንንም አብስር፡፡');
INSERT INTO "sadiq_sani" VALUES(61,14,'61:14','እናንተ ያመናችሁ ሆይ! የመርየም ልጅ ዒሳ ለሐዋርያቶቹ «ወደ አላህ ረዳቴ ማነው?» እንዳለ ሐወርያቶቹም «እኛ የአላህ ረዳቶች ነን» እንዳሉት የአላህ ረዳቶች ኹኑ፡፡ ከእስራኤልም ልጆች አንደኛዋ ጭፍራ አመነች፡፡ ሌላይቱም ጭፍራ ካደች፡፡ እነዚያን ያመኑትንም በጠላታቸው ላይ አበረታናቸው፤ አሸናፊዎችም ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(62,1,'62:1','በሰማያት ያለው በምድርም ያለው ሁሉ ለአላህ ንጉሥ፣ ቅዱስ፣ አሸናፊ፣ ጥበበኛ ለኾነው ያሞግሳል፡፡');
INSERT INTO "sadiq_sani" VALUES(62,2,'62:2','እርሱ ያ በመሃይሞቹ ውስጥ አንቀጾቹን (ቁርኣንን) በእነርሱ ላይ የሚያነብላቸው፣ (ከማጋራት) የሚያጠራቸውም፣ መጽሐፍንና ጥበብን የሚያስተምራቸውም የኾነን መልክተኛ (ሙሐመድን) ከእነርሱው ውስጥ የላከ ነው፡፡ እነርሱም ከእርሱ በፊት በግልጽ ስሕተት ውስጥ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(62,3,'62:3','ከነሱም ሌሎች ገና ያልተጠጉዋቸው በኾኑት ላይ (የላከው ነው)፡፡ እርሱም አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(62,4,'62:4','ይህ የአላህ ችሮታ ነው፡፡ ለሚሻው ሰው ይሰጠዋል፡፡ አላህም የታላቅ ችሮታ ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(62,5,'62:5','የእነዚያ ተውራትን የተጫኑትና ከዚያም ያልተሸከሟት (ያልሠሩባት) ሰዎች ምሳሌ መጽሐፎችን እንደሚሸከም አህያ ብጤ ነው፡፡ የእነዚያ በአላህ አንቀጾች ያስተባበሉት ሕዝቦች ምሳሌ ከፋ፡፡ አላህም በዳዮችን ሕዝቦች አይመራም፡፡');
INSERT INTO "sadiq_sani" VALUES(62,6,'62:6','«እናንተ አይሁዳውያን የኾናችሁ ሆይ! ከሰው ሁሉ በስተቀር እናንተ ብቻ ለአላህ ወዳጆች ነን ብትሉ እውነተኞች እንደኾናችሁ ሞትን ተመኙ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(62,7,'62:7','እጆቻቸውም ባስቀደሙት ኃጢአት ምክንያት በፍጹም አይመኙትም፡፡ አላህም በዳዮቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(62,8,'62:8','«ያ ከእርሱ የምትሸሹት ሞት እርሱ በእርግጥ አግኛችሁ ነው፡፡ ከዚያም ሩቁንና ቅርቡን ዐዋቂ ወደ ኾነው ጌታ ትመለሳላችሁ፡፡ ትሠሩትም የነበራችሁትን ሁሉ ይነግራችኋል» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(62,9,'62:9','እናንተ ያመናችሁ ሆይ! በዐርብ ቀን ለስግደት ጥሪ በተደረገ ጊዜ ወደ አላህ ማውሳት ኺዱ፡፡ መሸጥንም ተዉ፡፡ ይህ የምታውቁ ብትኾኑ ለእናንተ በጣም የተሻለ ነወ፡፡');
INSERT INTO "sadiq_sani" VALUES(62,10,'62:10','ሶለቲቱም በተፈጸመች ጊዜ በምድር ላይ ተበተኑ፡፡ ከአላህም ችሮታ ፈልጉ፡፡ አላህንም በብዙ አውሱ፡፡ ልትድኑ ይከጀላልና፡፡');
INSERT INTO "sadiq_sani" VALUES(62,11,'62:11','ንግድን ወይም ዛዛታን ባዩ ጊዜም ቆመህ የተዉህ ሲኾኑ ወደእርሷ ይበተናሉ፡፡ አላህ ዘንድ ያለው ዋጋ ከዛዛታም ከንግድም በላጭ ነው፡፡ «አላህም ከሰጪዎቹ ሁሉ ይበልጥ ሲሳይን ሰጭ ነው» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(63,1,'63:1','መናፍቃን በመጡህ ጊዜ «አንተ የአላህ መልክተኛ መኾንህን በእርግጥ (ምለን) እንመሰክራለን» ይላሉ፡፡ አላህም አንተ በእርግጥ መልክተኛው መኾንህን ያውቃል፡፡ አላህም መናፍቃን (ከልብ እንመሰክራለን በማለታቸው) ውሸታሞች መኾናቸውን ይመሰክራል፡፡');
INSERT INTO "sadiq_sani" VALUES(63,2,'63:2','መሓሎቻቸውን ጋሻ አድርገው ያዙ፡፡ ከአላህም መንገድ ከለከሉ፡፡ እነርሱ ይሠሩት የነበሩት ነገር ከፋ፡፡');
INSERT INTO "sadiq_sani" VALUES(63,3,'63:3','ይህ እነርሱ (በምላስ) ያመኑ ከዚያም (በልብ) የካዱ በመኾናቸው ነው፡፡ በልቦቻቸውም ላይ ታተመባቸው፤ ስለዚህ እነርሱ አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(63,4,'63:4','ባየሃቸውም ጊዜ አካሎቻቸው (ግዙፍነታቸው) ያስደንቁሃል፡፡ ቢናገሩም ለንግግራቸው ታዳምጣለህ፤ (ከዕውቀት ባዶ በመኾን)፡፡ እነርሱ ልክ በግድግዳ የተጠጉ ግንዶችን ይመስላሉ፡፡ ጩኸትን ሁሉ በእነርሱ ላይ ነው ብለው ያስባሉ፡፡ እነርሱ ጠላቶች ናቸውና ተጠንቀቃቸው፡፡ አላህ ይጋደላቸው፤ (ከእምነት) እንዴት ይመለሳሉ!');
INSERT INTO "sadiq_sani" VALUES(63,5,'63:5','ለእነሱም ፡- «ኑ የአላህ መልክተኛ ለእናንተ ምሕረትን ይለምንላችኋል፤» በተባሉ ጊዜ ራሶቻቸውን ያዞራሉ፡፡ እነርሱም ትዕቢተኞች ኾነው ሲሸሹ ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(63,6,'63:6','ምሕረትን ብትለምንላቸው ወይም ምሕረትን ባትለምንላቸው በእነርሱ ላይ እኩል ነው፡፡ አላህ ለእነርሱ በፍጹም አይምርም፡፡ አላህ አመጸኞችን ሕዝቦች አየመራምና፡፡');
INSERT INTO "sadiq_sani" VALUES(63,7,'63:7','እነርሱ እነዚያ ፡- «አላህ መልክተኛ ዘንድ ላሉት ሰዎች እስከሚበታተኑ ድረስ አትቀልቡ» የሚሉ ናቸው፡፡ የሰማያትና የምድር ድልቦችም የአላህ ናቸው፡፡ ግን መናፍቃን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(63,8,'63:8','«ወደ መዲና ብንመለስ አሸናፊው ወራዳውን በእርግጥ ከእርሷ ያወጣል» ይላሉ፡፡ አሸናፊነትም ለአላህ፣ ለመልክተኛውና ለምእምናን ነው፡፡ ግን መናፍቃን አያውቁም፡፡');
INSERT INTO "sadiq_sani" VALUES(63,9,'63:9','እናንተ ያመናችሁ ሆይ! ገንዘቦቻችሁና ልጆቻችሁ አላህን ከማስታወስ አያታሉዋችሁ፡፡ ይህንንም የሚሠሩ ሰዎች እነዚያ እነርሱ ከሳሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(63,10,'63:10','አንዳችሁንም ሞት ሳይመጣውና ጌታዬ ሆይ! እንድመጸውትና ከደጋጐቹም ሰዎች እንድኾን ወደ ጥቂት ጊዜ ብታቆየኝ ኖሮ እመኛለሁ ከማለቱ በፊት ከሰጠናችሁ ሲሳይ ለግሱ፡፡');
INSERT INTO "sadiq_sani" VALUES(63,11,'63:11','ማንኛይቱም ነፍስ የሞት ጊዜዋ በመጣበት ጊዜ አላህ በፍጹም አያቆያትም፡፡ አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,1,'64:1','በሰማያት ውስጥ ያለው በምድርም ውስጥ ያለው ሁሉ ለአላህ ያሞግሳል፡፡ ንግሥናው የእርሱ ብቻ ነው፡፡ ምስጋናም ለእርሱ ነው፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,2,'64:2','እርሱ ያ የፈጠራችሁ ነው፡፡ ከእናንተም ከሓዲ አልለ፡፡ ከእናንተም አማኝ አልለ፡፡ አላህም በምትሠሩት ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,3,'64:3','ሰማያትንና ምድርን በእውነት ፈጠረ፡፡ ቀረጻችሁም ቅርጻችሁንም አሳመረ፡፡ መመለሻችሁም ወደእርሱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,4,'64:4','በሰማያትና በምድር ውስጥ ያለውን ሁሉ ያውቃል፡፡ የምትደብቁትንም የምትገልጡትንም ያውቃል፡፡ አላህም በልቦች ውስጥ ያለውን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,5,'64:5','የእነዚያ ከአሁን በፊት የካዱትና የክሕደታቸውን ቅጣት ቅመሱ የተባሉት ሕዝቦች ወሬ አልመጣላችሁምን? ለእነሱም አሳማሚ ቅጣት አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,6,'64:6','ይህ (ቅጣት) እርሱ መልክተኞቻቸው በአስረጂዎች ይመጡባቸው ስለ ነበሩ «ሰዎችም ይመሩናልን?» ስላሉና ስለካዱ (ከእምነት) ስለ ዞሩም ነው፡፡ አላህም (ከእነሱ እምነት) ተብቃቃ፡፡ አላህም ተብቃቂ ምስጉን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,7,'64:7','እነዚያ የካዱትን በፍጹም የማይቀሰሱ መኾናቸውን አሰቡ፡፡ «አይደለም በጌታዬ እምላለሁ፡፡ በእርግጥ ትቀሰቀሳላችሁ፡፡ ከዚያም በሠራችሁት ሁሉ ትነገራላችሁ፡፡ ይህም በአላህ ላይ ቀላል ነው፡፡» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,8,'64:8','በአላህና በመልክተኛውም፡፡ በዚያም ባወረድነው ብርሃን እመኑ፡፡ «አላህም በምትሠሩት ሁሉ ውስጠ ዐዋቂ ነው» (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(64,9,'64:9','ለመሰብሰቢያ ቀን የሚሰበስብባችሁን ቀን (አስታውሱ)፤ ይህ የመጎዳዳት (መግለጫ) ቀን ነው፡፡ በአላህም የሚያምን ሰው መልካምንም የሚሠራ ከርሱ ኃጢአቶቹን ይሰርይለታል፤ (ይፍቅለታል)፤ ከሥሮቻቸውም ወንዞች የሚፈሱባቸውን ገነቶች በውስጣቸው ዘለዓለም ዘውታሪዎች ሲኾኑ ያገባዋል፤ ይህ ታላቅ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,10,'64:10','እነዚያም የካዱና በአንቀጾቻችን ያስተባበሉ እነዚያ በውስጡ ዘውታሪዎች ሲኾኑ የእሳት ጓዶች ናቸው፤ መመለሻቸውም ከፋ!');
INSERT INTO "sadiq_sani" VALUES(64,11,'64:11','ከመከራም (ማንንም) አይነካም በአላህ ፈቃድ ቢኾን እንጅ፤ በአላህም የሚያምን ሰው ልቡን (ለትዕግስት) ይመራዋል፤ አላህም ነገሩን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,12,'64:12','አላህንም ተገዙ፤ መልክተኛውንም ታዘዙ፤ ብትዞሩም (መልክተኛውን አትጎዱም)፤ በመልክተኛውም ላይ ያለበት ግልጽ ማድረስ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,13,'64:13','አላህ ከርሱ በቀር አምላክ የለም፤ በአላህም ላይ አማኞቹ ይመኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(64,14,'64:14','እላንተ ያመናችሁ ሆይ! ከሚስቶቻችሁና ከልጆቻችሁ (ከአላህ መንገድ በማስተጓጎል) ለናንተ ጠላቶች የኾኑ አልሉ፤ ስለዚህ ተጠንቀቋቸው፤ ይቅርታ ብታደርጉ ብታልፏቸውም ብተምሩዋቸውም አላህ በጣም መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,15,'64:15','ገንዘቦቻችሁና ልጆቻችሁ ለናንተ መፈተኛ ብቻ ናቸው፤ አላህም እርሱ ዘንድ ታላቅ ምንዳ አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(64,16,'64:16','አላህንም የቻላችሁትን የክል ፍሩት፤ ትዕዛዙን ስሙም፤ ታዘዙም፤ ለግሱም፤ ለነፍሶቻችሁ መልካምን (ይሰጣችኋልና)፤ የነፍሱንም ንፍገት የሚጠበቅ ሰው እነዚያ እነሱ ፍላጎታቸውን የሚያገኙ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,17,'64:17','ለአላህ መልካም ብድርን ብታበድሩ (ምንዳውን) ለናንተ ይደራርበዋል፤ ለናንተም ይምራል፤ አላህም አመስጋኝ ታጋሽ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(64,18,'64:18','ሩቁን ምስጢር ግልጹንም ሁሉ ዐዋቂው አሸናፊው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(65,1,'65:1','አንተ ነቢዩ ሆይ! ሴቶችን መፍታት ባሰባችሁ ጊዜ፤ ለዒዳቸው ፍቱዋቸው፡፡ ዒዳንም ቁጠሩ፡፡ አላህንም ጌታችሁን ፍሩ፡፡ ግልጽ የኾነችን ጠያፍ ካልሠሩ በስተቀር ከቤቶቻቸው አታውጡዋቸው፤ አይውጡም፡፡ ይህችም የአላህ ሕግጋት ናት፡፡ የአላህንም ሕግጋት የተላለፈ ሰው በእርግጥ ነፍሱን በደለ፡፡ ከዚህ (ፍች) በኋላ አላህ (የመማለስ) ነገርን ምናልባት ያመጣ እንደኾነ አታውቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(65,2,'65:2','ጊዜያቸውንም ለመዳረስ በተቃረቡ ጊዜ በመልካም ያዙዋቸው፡፡ ወይም በመልካም ተለያዩዋቸው፡፡ ከእናንተም ውስጥ ሁለት የትክክለኛነት ባለቤቶችን አስመስክሩ፡፡ ምስክርነትንም ለአላህ አስተካክሉ፡፡ ይህ በአላህና በመጨረሻው ቀን የሚያምን የኾነ ሰው ሁሉ በእርሱ ይገሰጽበታል፡፡ አላህንም የሚፈራ ሰው ለእርሱ መውጫን ያደርግለታል፡፡');
INSERT INTO "sadiq_sani" VALUES(65,3,'65:3','ከማያስበውም በኩል ሲሳየን ይሰጠዋል፡፡ በአላህም ላይ የሚጠጋ ሰው እርሱ በቂው ነው፡፡ አላህ ፈቃዱን አድራሽ ነው፡፡ አላህ ለነገሩ ሁሉ የተወሰነ ጊዜን በእርግጥ አድርጓል፡፡');
INSERT INTO "sadiq_sani" VALUES(65,4,'65:4','እኒያም ከሴቶቻቸው ከአደፍ ያቋረጡት (በዒዳቸው ሕግ) ብትጠራጠሩ ዒዳቸው ሦስት ወር ነው፡፡ እኒያም ገና አደፍን ያላዩት (ዒዳቸው እንደዚሁ ነው)፡፡ የእርግዝና ባለቤቶችም ጊዜያቸው እርጉዛቸውን መውለድ ነው፡፡ አላህንም የሚፈራ ሰው ከነገሩ ሁሉ ለርሱ መግራትን ያደርግለታል፡፡');
INSERT INTO "sadiq_sani" VALUES(65,5,'65:5','ይህ የአላህ ፍርድ ነው፡፡ ወደእናንተ አወረደው፡፡ አላህንም የሚፈራ ሰው ኀጢኣቶቹን ከእርሱ ይሰርይለታል፡፡ ምንዳንም ለእርሱ ያተልቅለታል፡፡');
INSERT INTO "sadiq_sani" VALUES(65,6,'65:6','ከችሎታችሁ ከተቀመጣችሁበት ስፍራ አስቀምጧቸው፡፡ በእነርሱም ላይ ልታጣብቡ አትጉዷቸው፡፡ የእርግዝና ባለቤቶችም ቢኾኑ እርጉዛቸውን እስኪወልዱ ድረስ በእነርሱ ላይ ቀልቡ፡፡ ለእናንተም (ልጆቻችሁን) ቢያጠቡላችሁ ምንዳዎቻቸውን ስጧቸው፡፡ በመካከላችሁም በመልካም ነገር ተመካከሩ፡፡ ብትቸጋገሩም ለእርሱ ሌላ (ሴት) ታጠባለታለች፡፡');
INSERT INTO "sadiq_sani" VALUES(65,7,'65:7','የችሎታ ባለቤት ከችሎታው ይቀልብ፡፡ በእርሱም ላይ ሲሳዩ የተጠበበበት ሰው አላህ ከሰጠው ይቀልብ አላህ ነፍስን የሰጣትን እንጅ አያስገድድም፡፡ አላህ ከችግር በኋላ ምቾትን በእርግጥ ያደርጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(65,8,'65:8','ከከተማም ከጌታዋ ትዕዛዝ ከመልክተኞቹም ያመጸች፣ ብርቱንም ቁጥጥር የተቆጣጠርናት፣ መጥፎንም ቅጣት የቀጣናት ብዙ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(65,9,'65:9','የነገርዋንም ቅጣት ቀመሰች፡፡ የነገርዋም መጨረሻ ከሳራ ኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(65,10,'65:10','አላህ ለእነርሱ ብርቱን ቅጣት አዘጋጀ፡፡ እናንተም የአእምሮ ባለቤቶች ሆይ! አላህን ፍሩ፡፡ እነዚያ ያመኑ (ሆይ)! አላህ ወደናንተ ግሳጼን በእርግጥ አወረደ፡፡ መልክተኛን (ላከ)፡፡');
INSERT INTO "sadiq_sani" VALUES(65,11,'65:11','እነዚያን ያመኑትንና መልካሞችን የሠሩትን ከጨለማዎች ወደ ብርሃን ያወጣ ዘንድ የአላህን አንቀጾች ገላጮች ሲኾኑ በእናንተ ላይ የሚያነብላችሁን (ላከ)፡፡ በአላህም አንቀጾች የሚያምን፣ መልካምን ሥራ የሚሠራም ከሥሮቻቸው ወንዞች የሚፈስሱባቸውን ገነቶች በውስጣቸው ዘለዓለም ዘውታሪዎች ሲኾኑ ያገባዋል፡፡ አላህ ለእርሱ ሲሳይን በእርግጥ አሳመረ፡፡');
INSERT INTO "sadiq_sani" VALUES(65,12,'65:12','አላህ ያ ሰባትን ሰማያት የፈጠረ ነው፡፡ ከምድርም መሰላቸውን (ፈጥሮአል)፤ በመካከላቸው ትእዛዙ ይወርዳል፡፡ አላህ በነገሩ ሁሉ ላይ ቻይ መኾኑን አላህም በነገሩ ሁሉ በዕውቀት ያካበበ መኾኑን በዕውቀት ያካበበ መኾኑን ታወቁ ዘንድ (ይህንን አሳወቃችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(66,1,'66:1','አንተ ነቢዩ ሆይ! አላህ ለአንተ የፈቀደልህን ነገር ሚስቶችህን ማስወደድን የምትፈልግ ስትኾን (ባንተ ላይ) ለምን እርም ታደርጋለህ? አላህም እጅግ መሓሪ አዛኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(66,2,'66:2','አላህ ለእናንተ የመሓሎቻችሁን መፍቻ ደነገገላችሁ፡፡ አላህም ረዳታችሁ ነው፡፡ እርሱም ዐዋቂው ጥበበኛው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(66,3,'66:3','ነቢዩ ከሚስቶቹ ወደ አንዷ ወሬን በመሰጠረ ጊዜ (አስታውስ)፡፡ እርሱንም በነገረችና አላህ እርሱን (ማውራትዋን) ባሳወቀው ጊዜ ከፊሉን አስታወቀ፡፡ ከፊሉንም ተወ፡፡ በእርሱም ባወራት ጊዜ «ይህን ማን ነገረህ?» አለች፡፡ «ዐዋቂው ውስጠ ዐዋቂው ነገረኝ» አላት፤');
INSERT INTO "sadiq_sani" VALUES(66,4,'66:4','ወደ አላህ ብትመለሱ ልቦቻችሁ በእርግጥ ተዘንብለዋልና (ትስማማላችሁ)፡፡ በእርሱም ላይ ብትረዳዱ አላህ እርሱ ረዳቱ ነው፡፡ ጂብሪልም ከምእምናንም መልካሙ ከዚህም በኋላ መላእክቱ ረዳቶቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(66,5,'66:5','(መላችሁንም) ቢፈታችሁ ከእናንተ የበለጡ ሚስቶችን ሙስሊሞች፣ አማኞች፣ ታዛዦች፣ ተጸጻቾች፣ (ለአላህ) ተገዢዎች፣ ጾመኛዎች ፈቶች፣ ደናግልም የኾኑትን ጌታው ሊለውጠው ይከጀላል፡፡');
INSERT INTO "sadiq_sani" VALUES(66,6,'66:6','እናንተ ያመናችሁ ሆይ! ነፍሶቻችሁንና ቤተሰቦቻችሁን መቀጣጠያዋ ሰዎችና ደንጋዮች ከኾነች እሳት ጠብቁ፡፡ በእርሷ ላይ ጨካኞች፣ ኀይለኞች የኾኑ መላእክት አልሉ፡፡ አላህን ያዘዛቸውን ነገር (በመጣስ) አያምጹም፡፡ የሚታዘዙትንም ሁሉ ይሠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(66,7,'66:7','እላንተ የካዳችሁ ሆይ! ዛሬ አታመካኙ፡፡ የምትመነዱት ያንን ትሠሩት የነበራችሁትን ብቻ ነው (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(66,8,'66:8','እናንተ ያመናችሁ ሆይ! ንጹሕ የኾነችን ጸጸት በመጸጸት ወደ አላህ ተመለሱ፡፡ ጌታችሁ ከእናንተ ኀጢኣቶቻችሁን ሊሰርይላችሁ ከሥሮቻቸው ወንዞች የሚፈሱባቸውንም ገነቶች ሊያስገባችሁ ይከጀላልና፡፡ አላህ ነቢዩን፣ እነዚያንም ከእርሱ ጋር ያመኑትን በማያሳፍርበት ቀን ብርሃናቸው በፊቶቻቸውና በቀኞቻቸው የሚሮጥ ሲኾን «ጌታችን ሆይ! ብርሃናችንን ሙላልን፣ ለእኛ ምሕረትም አድርግልን፣ አንተ በነገሩ ሁሉ ላይ ቻይ ነህና» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(66,9,'66:9','አንተ ነቢዩ ሆይ! ከሓዲዎችንና መናፍቃንን ታገል፡፡ በእነርሱም ላይ በርታ፡፡ መኖሪያቸውም ገሀነም ናት፡፡ ምን ትከፋም መመለሻ!');
INSERT INTO "sadiq_sani" VALUES(66,10,'66:10','አላህ ለእነዚያ ለካዱት ሰዎች የኑሕን ሴትና የሉጥን ሴት ምሳሌ አደረገ፡፡ ከባሮቻችን ከኾኑ ሁለት መልካም ባሪያዎች ሥር ነበሩ፡፡ ከዱዋቸውም፡፡ ከአላህም (ቅጣት ኑሕና ሉጥ) ከሚስቶቻቸው ምንም አልገፈተሩላቸውም፡፡ ከገቢዎቹም ጋር «እሳትን ግቡ» ተባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(66,11,'66:11','ለእነዚያ ለአመኑትም የፈርኦንን ሴት አላህ ምሳሌ አደረገ፡፡ «ጌታዬ ሆይ! አንተ ዘንድ በገነት ውስጥ ለእኔ ቤትን ገንባልኝ፡፡ ከፈርዖንና ከሥራውም አድነኝ፡፡ ከበደለኞቹ ሕዝቦችም አድነኝ» ባለች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(66,12,'66:12','የዒምራንን ልጅ መርየምንም ያችን ብልቷን የጠበቀችውን (ምሳሌ አደረገ)፡፡ በእርሱም ውስጥ ከመንፈሳችን ነፍፋን፡፡ በጌታዋ ቃላትና በመጻሕፍቱም አረጋገጠች፡፡ ከታዛዦቹም ነበረች፡፡');
INSERT INTO "sadiq_sani" VALUES(67,1,'67:1','ያ ንግሥና በእጁ የኾነው አምላክ ችሮታው በዛ፡፡ እርሱም በነገሩ ሁሉ ላይ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,2,'67:2','ያ የትኛችሁ ሥራው ይበልጥ ያማረ መኾኑን ሊሞክራችሁ ሞትንና ሕይወትን የፈጠረ ነው፡፡ እርሱም አሸናፊው መሓሪው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,3,'67:3','ያ ሰባትን ሰማያት የተነባበሩ ኾነው የፈጠረ ነው፡፡ በአልረሕማን አፈጣጠር ውስጥ ምንም መዛነፍን አታይም፡፡ ዓይንህንም መልስ፡፡ «ከስንጥቆች አንዳችን ታያለህን?»');
INSERT INTO "sadiq_sani" VALUES(67,4,'67:4','ከዚያም ብዙ ጊዜ ዓይንህን መላልስ፡፡ ዓይንህ ተዋርዶ እርሱም የደከመ ኾኖ ወዳንተ ይመለሳል፡፡');
INSERT INTO "sadiq_sani" VALUES(67,5,'67:5','ቅርቢቱን ሰማይም በእርግጥ በመብራቶች (በከዋክብት) አጌጥናት፡፡ ለሰይጣናትም መቀጥቀጫዎች አደረግናት፡፡ ለእነርሱም (ለሰይጣኖች) የእሳትን ቅጣት አዘጋጀን፡፡');
INSERT INTO "sadiq_sani" VALUES(67,6,'67:6','ለእነዚያም በጌታቸው ለካዱት የገሀነም ቅጣት አልላቸው፡፡ መመለሻይቱም ከፋች!');
INSERT INTO "sadiq_sani" VALUES(67,7,'67:7','በውስጧ በተጣሉ ጊዜ፤ እርሷ የምትፈላ ስትኾን ለእርሷ (እንደ አህያ) ማናፋትን ይሰማሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,8,'67:8','ከቁጭትዋ የተነሳ ልትቆራረጥ ትቀርባለች፡፡ በውስጧ ጭፍሮች በተጣሉ ቁጥር ዘበኞችዋ «አስፈራሪ (ነቢይ) አልመጣችሁምን?» በማለት ይጠይቋቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(67,9,'67:9','«አይደለም በእርግጥ አስፈራሪ መጥቶናል፡፡ አስተባበልንም፡፡ አላህም ምንንም አላወረደ እናንተ (አውርዷል ስትሉ) በትልቅ ስህተት ውስጥ እንጅ አይደላችሁም አልን» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,10,'67:10','«የምንሰማ ወይም የምናስብ በነበርንም ኖሮ በነዳጅ እሳት ጓዶች ውስጥ ባልኾን ነበር» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,11,'67:11','በኀጢኣታቸውም ያምናሉ ለእሳት ጓዶችም (ከእዝነት) መራቅ ተገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,12,'67:12','እነዚያ ጌታቸውን በሩቁ የሚፈሩ ለእነርሱ ምሕረትና ታላቅ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,13,'67:13','(ሰዎች ሆይ!) ቃላችሁንም መስጥሩ፡፡ ወይም በእርሱ ጩሁ፡፡ እርሱ በልቦች ውስጥ ያለን ሁሉ ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,14,'67:14','የፈጠረ አምላክ እርሱ ዕውቀት ረቂቁ፣ ውስጥ ዐዋቂው ሲኾን (ምስጢርን ሁሉ) አያውቅምን?');
INSERT INTO "sadiq_sani" VALUES(67,15,'67:15','እርሱ ያ ምድርን ለእናንተ የተገራች ያደረገላችሁ ነው፡፡ በጋራዎችዋና በመንገዶችዋም ኺዱ፡፡፡ ከሲሳዩም ብሉ፡፡ (ኋላ) መመለሻውም ወደእርሱ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,16,'67:16','በሰማይ ውስጥ ያለን (አላህ) በእናንተ ምድርን ቢደረባባችሁ ወዲያውም እርሷ የምታረገርግ ብትኾን ትተማመናላችሁን? (አትፈሩምን?)');
INSERT INTO "sadiq_sani" VALUES(67,17,'67:17','ወይም በሰማይ ውስጥ ያለን በእናንተ ላይ ጠጠርን ያዘለች ነፋስን ቢልክባችሁ ትተማመናላችሁን? (አትፈሩምን?) ማስጠንቀቄም እንዴት እንደኾነ ወደፊት ታውቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,18,'67:18','እነዚያም ከእነርሱ በፊት የነበሩት በእርግጥ አስተባበሉ፡፡ ጥላቻዬም እንዴት ነበር!');
INSERT INTO "sadiq_sani" VALUES(67,19,'67:19','ወደ አእዋፍ ከበላያቸው ክንፎቻቸውን ዘርግተው ተንሳፋፊዎች የሚሰበሰቡም ሲኾኑ አላዩምን? ከአልረሕማን በቀር (ባየር ላይ) የሚይዛቸው የለም፡፡ እርሱ በነገሩ ሁሉ ተመልካች ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,20,'67:20','በእውነቱ ያ እርሱ ከአልረሕማን ሌላ የሚረዳችሁ ለእናንተ የኾነ ሰራዊት ማነው? ከሓዲዎች በመታለል ውስጥ እንጅ በሌላ ላይ አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(67,21,'67:21','ወይም ሲሳዩን ቢይዝባችሁ ያ ሲሳይን የሚሰጣችሁ ማነው? በእውነቱ እነርሱ በሞገድና በመደንበር ውስጥ ችክ አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,22,'67:22','በፊቱ ላይ ተደፍቶ የሚኼድ ሰው ይበልጥ የቀና ነውን? ወይስ በቀጥተኛ መንገድ ላይ ተስተካክሎ የሚኼድ?');
INSERT INTO "sadiq_sani" VALUES(67,23,'67:23','«እርሱ ያ የፈጠራችሁ ለእናንተም መስሚያና ማያዎችን፣ ልቦችንም ያደረገላችሁ ነው፡፡ ጥቂትንም አታመስግኑም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,24,'67:24','«እርሱ ያ በምድር ላይ የበተናቸሁ ነው፡፡ ወደእርሱም ትሰበሰባላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,25,'67:25','«እውነተኞችም እንደኾናችሁ ይህ ቀጠሮ መቼ ነው?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(67,26,'67:26','«ዕውቀቱ አላህ ዘንድ ብቻ ነው፡፡ እኔ ግልጽ አስፈራሪ እንጅ ሌላ አይደለሁም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,27,'67:27','(ቅጣቱን) ቅርብ ኾኖ ባዩትም ጊዜ የእነዚያ የካዱት ሰዎች ፊቶች ይክከፋሉ፡፡ «ይህ ያ በእርሱ ትከራከሩበት የነበራችሁት ነው» ይባላሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(67,28,'67:28','«አያችሁን? አላህ ቢገድለኝ ከእኔ ጋር ያሉትንም (እንደዚሁ) ወይም (በማቆየት) ቢያዝንልን ከሓዲዎችን ከአሳማሚ ቅጣት የሚያድን ማን ነው?» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,29,'67:29','«እርሱ (እመኑበት የምላችሁ) አልረሕማን ነው፡፡ (እኛ) በእርሱ አመንን፡፡ በርሱም ላይ ተጠጋን፡፡ ወደ ፊትም በግልጽ መሳሳት ውስጥ የኾነው እርሱ ማን አንደ ኾነ በእርግጥ ታውቃላችሁ» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(67,30,'67:30','«አያችሁን? ውሃችሁ ሠራጊ ቢኾን ፈሳሺን ውሃ የሚያመጣላቸሁ ማን ነው?» በላቸው፡፡ (አላህ የዓለማት ጌታ ያመጣዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,1,'68:1','ነ.(ኑን)፤ በብርእ እምላለሁ በዚያም (መልአኮች) በሚጽፉት፡፡');
INSERT INTO "sadiq_sani" VALUES(68,2,'68:2','አንተ በጌታህ ጸጋ የተጎናጸፍክ ስትኾን ዕብድ አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(68,3,'68:3','ላንተም (ከጌታህ) የማይቋረጥ ዋጋ በእርግጥ አልለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,4,'68:4','አንተም በታላቅ ጠባይ ላይ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,5,'68:5','ወደፊትም ታያለህ፤ ያያሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(68,6,'68:6','ዕብደት በማንኛችሁ እንዳልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,7,'68:7','ጌታህ እርሱ ከመንገዱ የተሳሳተውን ሰው ዐዋቂ ነው፡፡ እርሱም ተመሪዎቹን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(68,8,'68:8','ለአስተባባዮችም አትታዝዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,9,'68:9','ብትመሳሰላቸውና ቢመሳሰሉህ ተመኙ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,10,'68:10','ወራዳንም ሁሉ አትታዘዝ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,11,'68:11','ሰውን አነዋሪንና በማሳበቅ ኼያጅን፡፡');
INSERT INTO "sadiq_sani" VALUES(68,12,'68:12','ለበጎ ነገር በጣም ከልካይን፣ ወሰን አላፊን፣ ኃጢአተኛን፤');
INSERT INTO "sadiq_sani" VALUES(68,13,'68:13','ልበ ደረቅን፣ ከዚህ በኋላ ዲቃላን (ሁሉ አትታዘዝ)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,14,'68:14','የገንዘብና የልጆች ባለቤት በመኾኑ (ያስተባብላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,15,'68:15','በእርሱ ላይ አንቀጾቻችን በሚነበቡ ጊዜ «የመጀመሪያዎቹ ሰዎች ተረቶች ናቸው» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(68,16,'68:16','በአፍንጫው ላይ በእርግጥ (የሚነወርበት) ምልክት እናደርግበታለን፡፡');
INSERT INTO "sadiq_sani" VALUES(68,17,'68:17','እኛ የአትክልቲቱን ባለቤቶቸ እንደ ሞከርን (የመካን ሰዎች) ሞከርናቸው፡፡ ማልደው (ፍሬዋን) ሊለቅሟት በማሉ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,18,'68:18','(በመሓላቸው) አያስቀሩምም፡፡');
INSERT INTO "sadiq_sani" VALUES(68,19,'68:19','እነርሱ የተኙ ኾነውም ሳሉ ከጌታህ የኾነ ዟሪ በእርሷ ላይ ዞረባት፡፡');
INSERT INTO "sadiq_sani" VALUES(68,20,'68:20','እንደ ሌሊት ጨለማም ኾና አነጋች፤ (ከሰለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,21,'68:21','ያነጉም ኾነው ተጠራሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,22,'68:22','«ቆራጮች እንደ ኾናችሁ በእርሻቸሁ ላይ ማልዱ» በማለት፡፡');
INSERT INTO "sadiq_sani" VALUES(68,23,'68:23','እነርሱ የሚንሾካሾኩ ኾነው ኼዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(68,24,'68:24','ዛሬ በእናንተ ላይ ድኻ እንዳይገባት በማለት፡፡');
INSERT INTO "sadiq_sani" VALUES(68,25,'68:25','(ድኾችን) በመከልከልም ላይ (በሐሳባቸው) ቻዮች ኾነው ማለዱ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,26,'68:26','(ተቃጥላ) ባዩዋትም ጊዜ «እኛ በእርግጥ ተሳሳቾች ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,27,'68:27','«ይልቁንም እኛ የተከለከልን ነን» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,28,'68:28','ትክክለኛቸው «ለእናንተ አላህን ለምን አታጠሩም አላልኳችሁምን?» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(68,29,'68:29','«ጌታችን ጥራት ይገባው፡፡ እኛ በዳዮች ነበርን፤» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,30,'68:30','የሚወቃቀሱም ኾነው ከፊሎቻቸው በከፊሉ ላይ መጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,31,'68:31','«ዋ ጥፋታችን! እኛ ድንበር አላፊዎች ነበርን፤» አሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,32,'68:32','«ጌታችን ከእርሷ የተሻለን (አትክልት) ሊለውጠን ይከጀላል፡፡ እኛ ወደ ጌታችን ከጃዮች ነን፤» (አሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,33,'68:33','ቅጣቱ እንደዚሁ ነው፡፡ የመጨረሻይቱም ዓለም ቅጣት ታላቅ ነው፡፡ የሚያውቁት በኾኑ ኖሮ (በተጠነቀቁ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,34,'68:34','ለጥንቁቆቹ በጌታቸው ዘንድ መጠቀሚያ ገነቶች አሏቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(68,35,'68:35','ሙስሊሞቹን እንደ ከሓዲዎች እናደርጋለን?');
INSERT INTO "sadiq_sani" VALUES(68,36,'68:36','ለእናንተ ምን (አስረጅ) አላችሁ? እንዴት ትፈርዳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,37,'68:37','በእውነቱ ለእናንተ በእርሱ የምታጠኑበት መጽሐፍ አላችሁን?');
INSERT INTO "sadiq_sani" VALUES(68,38,'68:38','በውስጡ ለእናንተ የምትመርጡት አላችሁ (የሚል)');
INSERT INTO "sadiq_sani" VALUES(68,39,'68:39','ወይስ ለእናንተ የምትፈርዱት አላችሁ በማለት (ቃል ኪዳን የገባንላችሁ) እስከ ትንሣኤ ቀን ደራሽ የኾኑ መሓላዎች ለእናንተ በእኛ ላይ አሏችሁን?');
INSERT INTO "sadiq_sani" VALUES(68,40,'68:40','በዚህ ማንኛቸው ተያያዥ እንደ ኾነ ጠይቃቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(68,41,'68:41','ወይስ ለእነርሱ (በፍርዳቸው ተስማሚ) «ተጋሪዎች» አሏቸውን? እውነተኞችም እንደኾኑ «ተጋሪዎቻቸውን» ያምጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,42,'68:42','ባት የሚገለጥበትን (ከሓዲዎች) ወደ መስገድም የሚጥጠሩበትንና የማይችሉበት ቀን (አስታውስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(68,43,'68:43','ዓይኖቻቸው የፈሩ ውርደት የምትሸፍናቸው ሲኾኑ፤ (ወደ ስግደት የሚጠሩበትን)፤ እነርሱም (በምድረ ዓለም) ደህና ኾነው ሳሉ ወደ ስግደት በእርግጥ ይጠሩ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,44,'68:44','በዚህም ንግግር ከሚያስተባብሉት ሰዎች ጋር ተዎኝ፡፡ (እነርሱን እኔ እበቃሃለሁ)፡፡ ከማያውቁት ስፍራ አዘናግተን እንይዛቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(68,45,'68:45','እነርሱንም አዘገያለሁ፡፡ ዘዴዬ ብርቱ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(68,46,'68:46','በእውነቱ ዋጋን ትጠይቃቸዋለህን? ስለዚህ እነርሱ ከዕዳ የተከበዱ ናቸውን?');
INSERT INTO "sadiq_sani" VALUES(68,47,'68:47','ወይስ እነርሱ ዘንድ የሩቁ ምስጢር ዕውቀት አልለን? ስለዚህ እነርሱ ይጽፋሉን?');
INSERT INTO "sadiq_sani" VALUES(68,48,'68:48','ለጌታህም ፍርድ ታገሥ፤ (በመበሳጨትና ባለ መታገሥ) እንደ ዓሣው ባለቤትም (እንደ ዮናስ) አትኹን፡፡ እርሱ በጭንቀት የተመላ ኾኖ (ጌታውን) በተጣራ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,49,'68:49','ከጌታው የኾነ ጸጋ ባላገኘው ኖሮ በምድረ በዳ ተወቃሽ ኾኖ በተጣለ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(68,50,'68:50','ጌታውም (በነቢይነት) መረጠው፡፡ ከደጋጎቹም አደረገው፡፡');
INSERT INTO "sadiq_sani" VALUES(68,51,'68:51','እነሆ እነዚያም የካዱት ሰዎች ቁርኣኑን በሰሙ ጊዜ በዓይኖቻቸው ሊጥሉህ (ሊያጠፉህ) ይቀርባሉ፡፡ «እርሱም በእርግጥ ዕብድ ነው» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(68,52,'68:52','ግን እርሱ (ቁርኣን) ለዓለማት መገሰጫ እንጂ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(69,1,'69:1','እውነትን አረጋጋጪቱ (ትንሣኤ)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,2,'69:2','አረጋጋጪቱም (እርሷ) ምንድን ናት!');
INSERT INTO "sadiq_sani" VALUES(69,3,'69:3','አረጋጋጪቱም ምን እንደኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(69,4,'69:4','ሰሙድና ዓድ በቆርቋሪይቱ (ትንሣኤ) አስተባበሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,5,'69:5','ሠሙድማ ወሰን በሌላት ጩኸት ተጠፋ፤ (ተወደሙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,6,'69:6','ዓድማ በኀይል በምትንሻሻ ብርቱ ነፋስ ተጠፉ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,7,'69:7','ተከታታይ በኾኑ ሰባት ሌሊቶችና ስምንት መዓልቶች ውስጥ በእነርሱ ላይ ለቀቃት፡፡ ሕዝቦቹንም፤ በውስጧ የተጣሉ ኾነው ልክ ክፍት የኾኑ የዘንባባ ግንዶች መስለው ታያቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,8,'69:8','ለእነርሱም ቀሪን ታያለህን?');
INSERT INTO "sadiq_sani" VALUES(69,9,'69:9','ፈርዖን ከበፊቱ የነበሩትም ሰዎች ተገልባጮቹም (ከተሞች) በኀጢአት (ሥራዎች) መጡ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,10,'69:10','የጌታቸውንም መልእክተኛ ትዕዛዝ ጣሱ፡፡ የበረታችንም አያያዝ ያዛቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,11,'69:11','እኛ ውሃው ባየለ ጊዜ በተንሻላይቱ ታንኳ ውስጥ ጫንናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,12,'69:12','ለእናንተ መገሰጫ ልናደርጋት አጥኒ የኾነችም ጆሮ ታጠናት ዘንድ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,13,'69:13','በቀንዱም አንዲት መንነፋት በተነፋች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,14,'69:14','ምድርና ጋራዎችም (ከስፍራቸው) በተነሱና አንዲትን መሰባበር በተሰባበሩ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,15,'69:15','በዚያ ቀን ኋኝቱ (ትንሣኤ) ትኾናለች፡፡');
INSERT INTO "sadiq_sani" VALUES(69,16,'69:16','ሰማይም ትቀደዳለች፡፡ ስለዚህ እርሷ በዚያ ቀን ደካማ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(69,17,'69:17','መላእክትም በየጫፎችዋ ላይ ይኾናሉ፡፡ የጌታህንም ዐርሽ በዚያ ቀን ስምንት መላእክት ከበላያቸው ይሸከማሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,18,'69:18','በዚያ ቀን ከእናንተ ተደባቂ (ነገሮች) የማይደበቁ ሲኾኑ ትቅቀረባላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,19,'69:19','መጽሐፉን በቀኙ የተሰጠ ሰውማ (ለጓደኞቹ) «እንኩ፤ መጽሐፌን አንብቡ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(69,20,'69:20','«እኔ ምርመራየን የምገናኝ መኾኔን አረጋገጥኩ፤» (ተዘጋጀሁም ይላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,21,'69:21','እርሱም በተወደደች ኑሮ ውሰጥ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(69,22,'69:22','በከፍተኛይቱ ገነት ውስጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,23,'69:23','ፍሬዎችዋ ቅርቦች የኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,24,'69:24','በአለፉት ቀናት ውስጥ (በምድረ ዓለም) ባስቀደማችሁት ምክንያት ብሉ፤ ጠጡም፤ (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,25,'69:25','መጽሐፉንም በግራው የተሰጠማ «ዋ ጥፋቴ! ምነው መጽሐፌን ባልተሰጠሁ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(69,26,'69:26','«ምርመራየንም ምን እንደኾነ ባላወቅኩ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,27,'69:27','«እርሷ (ሞት ሕይወቴን) ምነው ፈጻሚ በኾነች፡፡');
INSERT INTO "sadiq_sani" VALUES(69,28,'69:28','«ገንዘቤ ከእኔ ምንንም አላብቃቃኝ፤ (አልጠቀመኝም)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,29,'69:29','«ኀይሌ ከእኔ ላይ ጠፋ» (ይላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,30,'69:30','«ያዙት፤ እሰሩትም፡፡');
INSERT INTO "sadiq_sani" VALUES(69,31,'69:31','«ከዚያም በእሳት ውሰጥ አግቡት፡፡');
INSERT INTO "sadiq_sani" VALUES(69,32,'69:32','«ከዚያም ርዝመቷ ሰባ ክንድ በኾነች ሰንሰለት ውስጥ አግቡት፡፡»');
INSERT INTO "sadiq_sani" VALUES(69,33,'69:33','እርሱ ታላቅ በኾነው አላህ አያምንም ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(69,34,'69:34','ድኾችንም በማብላት ላይ አያግባባም ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(69,35,'69:35','ለእርሱም ዛሬ እዚህ ዘመድ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(69,36,'69:36','ምግብም ከእሳት ሰዎች ቁስል እጣቢ (እዥ) በስተቀር የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(69,37,'69:37','ኀጢኣተኞች እንጅ ሌላ አይበላውም፤ (ይባላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(69,38,'69:38','በምታዩትም ነገር እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,39,'69:39','በማታዩትም ነገር፡፡');
INSERT INTO "sadiq_sani" VALUES(69,40,'69:40','እርሱ (ቁርኣን) የተከበረ መልክተኛ ቃል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,41,'69:41','እርሱም የባለቅኔ ቃል (ግጥም) አይደለም፡፡ ጥቂትን ብቻ ታምናላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,42,'69:42','የጠንቋይም ቃል አይደለም፡፡ ጥቂትን ብቻ ታስታውሳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(69,43,'69:43','ከዓለማት ጌታ የተወረደ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,44,'69:44','በእኛ ላይም ከፊልን ቃላት (ያላልነውን) በቀጠፈ ኖሮ፤');
INSERT INTO "sadiq_sani" VALUES(69,45,'69:45','በኀይል በያዝነው ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(69,46,'69:46','ከዚያም ከእርሱ የልቡን ሥር (የተንጠለጠለበትን ጅማት) በቆረጥን ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(69,47,'69:47','ከእናንተም ውስጥ ከእርሱ ላይ ከልካዮች ምንም አይኖሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(69,48,'69:48','እርሱም (ቁርኣን) ለጥንቁቆቹ መገሠጫ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,49,'69:49','እኛም ከእናንተ ውስጥ አስተባባዮች መኖራቸውን በእርግጥ እናውቃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(69,50,'69:50','እርሱም (ቁርኣን) በከሓዲዎቹ ላይ ጸጸት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,51,'69:51','እርሱም የተረጋገጠ እውነት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(69,52,'69:52','የታላቁን ጌታህንም ስም አወድስ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,1,'70:1','ወዳቂ ከኾነው ቅጣት ጠያቂ ጠየቀ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,2,'70:2','በከሓዲዎች ላይ (ወዳቂ ከኾነው) ለእርሱ መላሽ የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(70,3,'70:3','የ(ሰማያት) መሰላሎች ባለቤት ከኾነው አላህ (መላሽ የለውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,4,'70:4','መላእክቱና መንፈሱም ልኩ አምሳ ሺሕ ዓመት በኾነ ቀን ውስጥ ወደእርሱ ያርጋሉ፤ (ይወጣሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,5,'70:5','መልካምንም ትዕግስት ታገሥ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,6,'70:6','እነርሱ (ያንን ቀን) ሩቅ አድርገው ያዩታል፡፡');
INSERT INTO "sadiq_sani" VALUES(70,7,'70:7','እኛም ቅርብ ኾኖ እናየዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(70,8,'70:8','ሰማይ እንደ ዘይት አተላ በምትኾንበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(70,9,'70:9','ጋራዎችም በተለያዩ ቀለማት እንደ ተነከረ ሱፍ (ነፋስ እንደሚያበረው) በሚኾኑበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(70,10,'70:10','ዘመድም ዘመድን በማይጠይቅበት ቀን፤ (መላሽ የለውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,11,'70:11','(ዘመዶቻቸውን) ያዩዋቸዋል፡፡ አመጸኛው ከዚያ ቀን ስቃይ (ነፍሱን) በልጆቹ ሊበዥ ይመኛል፡፡');
INSERT INTO "sadiq_sani" VALUES(70,12,'70:12','በሚስቱም በወንድሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(70,13,'70:13','በዚያችም በምታስጠጋው ጎሳው፡፡');
INSERT INTO "sadiq_sani" VALUES(70,14,'70:14','በምድርም ላይ ባለው ሁሉ (ሊበዥ) ከዚያም ሊያድነው (ይመኛል)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,15,'70:15','ይተው! እርሷ (እሳቲቱ) ለዟ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,16,'70:16','የራስን ቅል ቆዳ የምትሞሽልቅ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(70,17,'70:17','(ከእምነት) የዞረንና የሸሸን ሰው (ወደእርሷ) ትጠራለች፡፡');
INSERT INTO "sadiq_sani" VALUES(70,18,'70:18','ገንዘብን የሰበሰበንና (ዘካውን ሳይሰጥ) የቆጠረንም፤ (ትጠራለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,19,'70:19','ሰው ቅጠ ቢስ ኾኖ ተፈጠረ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,20,'70:20','ክፉ ነገር ባገኘው ጊዜ ብስጪተኛ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,21,'70:21','መልካምም ነገር (ድሎት) ባገኘ ጊዜ ከልካይ (ኾኖ ተፈጠረ)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,22,'70:22','ሰጋጆቹ ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,23,'70:23','እነዚያ እነርሱ በስግደታቸው ላይ ዘውታሪዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,24,'70:24','እነዚያም በገንዘቦቻቸው ላይ የታወቀ መብት ያለባቸው የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,25,'70:25','ለለማኝ ከልመና ለሚከለከልም (መብት ያለባቸው የኾኑት)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,26,'70:26','እነዚያም በፍርዱ ቀን እውነት የሚሉት (የሚያረጋግጡት)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,27,'70:27','እነዚያም እነርሱ ከጌታቸው ቅጣት ፈሪዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,28,'70:28','የጌታቸው ቅጣት (መምጣቱ) የማያስተማምን ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(70,29,'70:29','እነዚያም እነርሱ ብልቶቻቸውን ጠባቂዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,30,'70:30','በሚስቶቻቸው ወይም እጆቻቸው በያዙዋቸው (ባሪያዎች) ላይ ሲቀር፡፡ እነርሱ (በእነዚህ) የማይወቀሱ ናቸውና፡፡');
INSERT INTO "sadiq_sani" VALUES(70,31,'70:31','ከዚያም ወዲያ የፈለገ ሰው እነዚያ እነሱ ድንበር አላፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(70,32,'70:32','እነዚያም እነርሱ አደራዎቻቸውንና ቃል ኪዳናቸውን ጠባቂዎች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,33,'70:33','እነዚያም እነርሱ በምስክርነታቸው ትክክለኞች የኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,34,'70:34','እነዚያም እነርሱ በሶላቶቻቸው ላይ የሚጠባበቁት፡፡');
INSERT INTO "sadiq_sani" VALUES(70,35,'70:35','እነዚህ (ከዚህ በላይ የተወሱት ሁሉ) በገነቶች ውስጥ የሚከበሩ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(70,36,'70:36','ለእነዚያም ለካዱት ወደ አንተ በኩል አንገቶቻቸውን መዝዘው የሚያተኩሩት ምን አላቸው?');
INSERT INTO "sadiq_sani" VALUES(70,37,'70:37','ከቀኝና ከግራ ክፍልፍል ጭፍሮች ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(70,38,'70:38','ከእነርሱ እያንዳንዱ ሰው የጸጋይቱን ገነት መግባትን ይከጅላልን?');
INSERT INTO "sadiq_sani" VALUES(70,39,'70:39','ይከልከል፤ እኛ ከሚያውቁት ነገር (ከፍቶት ጠብታ) ፈጠርናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(70,40,'70:40','በምሥራቆችና በምዕራቦችም ጌታ እምላለሁ፡፡ እኛ በእርግጥ ቻዮች ነን፡፡');
INSERT INTO "sadiq_sani" VALUES(70,41,'70:41','ከእነርሱ የተሻለን በመለወጥ ላይ፡፡ እኛም ተሸናፊዎች አይደለንም፡፡');
INSERT INTO "sadiq_sani" VALUES(70,42,'70:42','ያንንም የሚስፈራሩበትን ቀናቸውን እስከሚገናኙ ድረስ ተዋቸው፡፡ ይዋኙ ይጫወቱም፡፡');
INSERT INTO "sadiq_sani" VALUES(70,43,'70:43','ወደ ጣዖቶች እንደሚሽቀዳደሙ ኾነው ከመቃብሮቻቸው ፈጥነው የሚወጡበትን ቀን (እስከሚገናኙ ድረስ ተዋቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(70,44,'70:44','ዓይኖቻቸው ያፈሩ ኾነው ውርደት ትሸፍናቸዋለች፡፡ ይህ ቀን ያ ይስፈራሩበት የነበሩት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(71,1,'71:1','እኛ ኑሕን «ሕዝቦችህን አሳማሚ ቅጣት ሳይመጣባቸው በፊት አስጠንቅቅ በማለት ወደ ሕዝቦቹ ላክነው፡፡»');
INSERT INTO "sadiq_sani" VALUES(71,2,'71:2','(እርሱም) አለ «ሕዝቦቼ ሆይ! እኔ ለእናንተ ገላጭ የኾንኩ አስጠንቃቂ ነኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,3,'71:3','«አላህን ተገዙት፣ ፍሩትም፣ ታዘዙኝም በማለት፤ (አስጠንቃቂ ነኝ)፡፡');
INSERT INTO "sadiq_sani" VALUES(71,4,'71:4','«ለእናንተ ከኀጢኣቶቻችሁ ይምራልና፡፡ ወደተወሰነው ጊዜም ያቆያችኋል፡፡ የአላህ (የወሰነው) ጊዜ በመጣ ወቅት አይቆይም፡፡ የምታውቁት ብትኾኑ ኖሮ (በታዘዛችሁ ነበር)፡፡»');
INSERT INTO "sadiq_sani" VALUES(71,5,'71:5','(ስለ ተቃወሙትም) «አለ ጌታዬ ሆይ! እኔ ሌሊትም ቀንም ሕዝቦቼን ጠራሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,6,'71:6','«ጥሪየም መሸሽን እንጅ ሌላ አልጨመረላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(71,7,'71:7','«እኔም ለእነርሱ ትምር ዘንድ (ወደ እምነት) በጠራኋቸው ቁጥር ጣቶቻቸውን በጆሮዎቻቸው ውስጥ አደረጉ፡፡ ልብሶቻቸውንም ተከናነቡ፡፡ (በመጥፎ ሥራቸው ላይ) ዘወተሩም፡፡ (ያለ ልክ) መኩራትንም ኮሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,8,'71:8','«ከዚያም እኔ በጩኸት ጠራኋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(71,9,'71:9','«ከዚያም እኔ ለእነርሱ ገለጽኩ፡፡ ለእነርሱም መመስጠርን መሰጠርኩ');
INSERT INTO "sadiq_sani" VALUES(71,10,'71:10','«አልኳቸውም፡- ጌታችሁን ምሕረትን ለምኑት፡፡ እርሱ በጣም መሐሪ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(71,11,'71:11','«በእናንተ ላይ ዝናምን ተከታታይ አድርጎ ይልካል፡፡');
INSERT INTO "sadiq_sani" VALUES(71,12,'71:12','«በገንዘቦችና በልጆችም ይለግሰላችኋል፡፡ ለእናንተም አትክልቶችን ያደርግላችኋል፡፡ ለእናንተም ወንዞችን ያደርግላችኋል፡፡»');
INSERT INTO "sadiq_sani" VALUES(71,13,'71:13','ለአላህ ልቅናን የማትሹት ለእናንተ ምን አላችሁ?');
INSERT INTO "sadiq_sani" VALUES(71,14,'71:14','በልዩ ልዩ ኹኔታዎች በእርግጥ የፈጠራችሁ ሲኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(71,15,'71:15','አላህ ሰባትን ሰማያት አንዱ ካንዱ በላይ ሲኾን እንዴት እንደፈጠረ አታዩምን?');
INSERT INTO "sadiq_sani" VALUES(71,16,'71:16','በውስጣቸውም ጨረቃን አብሪ አደረገ፡፡ ፀሐይንም ብርሃን አደረገ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,17,'71:17','አላህም ከምድር ማብቀልን አበቀላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,18,'71:18','ከዚያም በውስጧ ይመልሳችኋል፡፡ ማውጣትንም ያወጣችኋል፡፡');
INSERT INTO "sadiq_sani" VALUES(71,19,'71:19','አላህም ምድርን ለእናንተ ምንጣፍ አደረጋት፡፡');
INSERT INTO "sadiq_sani" VALUES(71,20,'71:20','ከእርሷ ሰፋፊዎችን መንገዶች ትገቡ ዘንድ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,21,'71:21','ኑሕ አለ፡- «ጌታዬ ሆይ! እነሱ አምመጹብኝ፡፡ ገንዘቡና ልጁም ከጥፋት በስተቀር ያልጨመረለትን ሰው ተከተሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(71,22,'71:22','«ታላቅንም ተንኮል የመከሩትን ሰዎች» (ተከተሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(71,23,'71:23','አሉም «አምላኮቻችሁን አትተዉ፡፡ ወድንም፣ ሱዋዕንም፣ የጉሥንም፣ የዑቅንም ነስርንም አትተው፡፡');
INSERT INTO "sadiq_sani" VALUES(71,24,'71:24','«በእርግጥም ብዙዎችን አሳሳቱ፡፡ ከሓዲዎችንም ጥመትን እንጅ ሌላን አትጨምርላቸው» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(71,25,'71:25','በኀጢኣቶቻቸው ምክንያት ተሰጠሙ፡፡ እሳትንም እንዲገቡ ተደረጉ፡፡ ለእነርሱም ከአላህ ሌላ የኾኑ ረዳቶችን አላገኙም፡፡');
INSERT INTO "sadiq_sani" VALUES(71,26,'71:26','ኑሕም አለ «ጌታዬ ሆይ! ከከሓዲዎቹ በምድር ላይ አንድንም አትተው፡፡');
INSERT INTO "sadiq_sani" VALUES(71,27,'71:27','«አንተ ብትተዋቸው ባሮችህን ያሳስታሉና፡፡ ኀጢኣተኛ ከሓዲንም እንጅ ሌላን አይወልዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(71,28,'71:28','«ጌታዬ ሆይ! ለእኔም ለወላጆቼም ምእመን ኾኖ በቤቴ ለገባም ሰው ሁሉ ለምእመናንና ለምእምናትም ምሕረት አድርግ፡፡ ከሓዲዎችንም ከጥፋት በቀር አትጨምርላቸው፤» (አለ)፡፡');
INSERT INTO "sadiq_sani" VALUES(72,1,'72:1','(ሙሐመድ ሆይ!) በል «እነሆ ከጂን የኾኑ ጭፈሮች (ቁርኣንን) አዳመጡ፡፡‹እኛ አስደናቂ የኾነን ቁርኣን ሰማንም› አሉ ማለት ወደእኔ ተወረደ፡፡');
INSERT INTO "sadiq_sani" VALUES(72,2,'72:2','‹ወደ ቀጥታ መንገድ የሚመራን (ቁርአን ሰማን)፡፡ በእርሱም አመንን፡፡ በጌታችንም አንድንም አናጋራም፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,3,'72:3','‹እነሆም የጌታችን ክብር ላቀ፡፡ ሚስትንም ልጅንም አልያዘም፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,4,'72:4','‹እነሆም ቂላችን በአላህ ላይ ወሰን ያለፈን (ውሸት) ይናገር ነበር፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,5,'72:5','‹እኛም ሰዎችና ጋኔኖች በአላህ ላይ ውሸትን (ቃል) አይናገሩም ማለትን ጠረጠርን፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,6,'72:6','‹እነሆም ከሰዎች የኾኑ ወንዶች በጋኔን ወንዶች ይጠበቁ ነበሩ፡፡ ኩራትንም ጨመሩዋቸው፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,7,'72:7','‹እነርሱም አላህ አንድንም አይቀሰቅስም ማለትን እንደጠረጠራችሁ ጠረጠሩ፡፡›');
INSERT INTO "sadiq_sani" VALUES(72,8,'72:8','‹እኛም ሰማይን (ለመድረስ) ፈለግን፡፡ ብርቱ ጠባቂዎችንና ችቦዎችንም ተሞልታ አገኘናት፡፡');
INSERT INTO "sadiq_sani" VALUES(72,9,'72:9','‹እኛም ከእርሷ (ወሬን) ለማደመጥ በመቀመጫዎች እንቀመጥ ነበርን፡፡ አሁን ግን የሚያዳምጥ ሰው ለእርሱ ተጠባባቂ ችቦን ያገኛል፡፡');
INSERT INTO "sadiq_sani" VALUES(72,10,'72:10','‹እኛም በምድር ውስጥ ባልሉ ሰዎች ክፉ ተሽቷል? ወይስ በእነርሱ ጌታቸው ደግን ነገር ሽቷል? ማለትን አናውቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(72,11,'72:11','‹እኛም ከእኛ ውስጥ ደጎች አልሉ፡፡ ከኛም ከዚህ ሌላ የኾኑ አልሉ፡፡ የተለያዩ መንገዶች (ባለ ቤቶች) ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(72,12,'72:12','‹እኛም አላህን በምድር ውስጥ ፈጽሞ የማናቅተው፤ ሸሽተንም ፈጽሞ የማናመልጠው መኾናችንን አረጋገጥን፡፡');
INSERT INTO "sadiq_sani" VALUES(72,13,'72:13','‹እኛም መሪውን በሰማን ጊዜ በርሱ አመንን፡፡ በጌታውም የሚያምን ሰው መግጎደልንም መጭጨመርንም አይፈራም፡፡');
INSERT INTO "sadiq_sani" VALUES(72,14,'72:14','«እኛም ከእኛ ውስጥ ሙስሊሞች አልሉ፡፡ ከእኛም ውስጥ በዳዮች አልሉ፡፡ የሰለሙም ሰዎች እነዚያ ቅንን መንገድ መረጡ፡፡»');
INSERT INTO "sadiq_sani" VALUES(72,15,'72:15','በዳዮቹማ ለገሀነም ማገዶ ኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(72,16,'72:16','እነሆም በመንገዲቱ ላይ ቀጥ ባሉ ኖሮ ብዙ ዝናምን ባጠጣናቸው ነበር (ማለትም ተወረደልኝ)፡፡');
INSERT INTO "sadiq_sani" VALUES(72,17,'72:17','በእርሱ ልንሞክራቸው (ባጠጣናቸው ነበር)፡፡ ከጌታውም ማስታወሻ የሚያፈገፍግ ሰው አስቸጋሪን ቅጣት ያገባዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(72,18,'72:18','እነሆ መስጊዶችም የአላህ ብቻ ናቸው፡፡ (በውስጣቸው) ከአላህ ጋር አንድንም አትገዙ (ማለትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(72,19,'72:19','እነሆም የአላህ ባሪያ (ሙሐመድ አላህን) የሚጠራው ኾኖ በተነሳ ጊዜ (ጋኔኖች) በእርሱ ላይ ድርብርቦች ሊኾኑ ቀረቡ (ማለትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(72,20,'72:20','«እኔ የምግገዛው ጌታየን ብቻ ነው፡፡ በእርሱም አንድንም አላጋራም» በል፡፡');
INSERT INTO "sadiq_sani" VALUES(72,21,'72:21','«እኔ ለእናንተ ጉዳትንም ቀጥታንም አልችልም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(72,22,'72:22','«እኔ ከአላህ (ቅጣት) አንድም አያድነኝም፡፡ ከእርሱም ሌላ መጠጊያን አላገኝም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(72,23,'72:23','«ከአላህ የኾነ ማድረስን መልክቶቹንም በስተቀር (አልችልም)፡፡ አላህንና መልክተኛውንም የሚያምጽ ሰው ለእርሱ የገሀነም እሳት አልለው፡፡ በውስጧ ዘላለም ዘውታሪዎች ሲኾኑ፡፡»');
INSERT INTO "sadiq_sani" VALUES(72,24,'72:24','የሚስፈራሩበትንም ባዩ ጊዜ ረዳቱ ደካማ፣ ቁጥሩም አነስተኛ የኾነው ማን እንደኾነ በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(72,25,'72:25','«የምትስፈራሩበት ነገር ቅርብ ወይም ጌታየ ለእርሱ የተወሰነ ጊዜን የሚያደርግለት (ሩቅ) መኾኑን አላውቅም» በላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(72,26,'72:26','«(እርሱ) ሩቁን ምስጢር ዐዋቂ ነው፡፡ በምስጢሩም ላይ አንድንም አያሳውቅም፡፡»');
INSERT INTO "sadiq_sani" VALUES(72,27,'72:27','ከመልክተኛ ለወደደው ቢኾን እንጅ (ለሌላ አይገልጽም)፡፡ እርሱም ከስተፊቱም ከስተኋላውም ጠባቂዎችን ያደርግለታል፡፡');
INSERT INTO "sadiq_sani" VALUES(72,28,'72:28','እነርሱ ዘንድ ባለው ነገር ዕውቀቱ የከበበና ነገሩንም ሁሉ በቁጥር ያጠቃለለ ሲኾን የጌታቸውን መልክቶች ያደረሱ መኾናቸውን ያውቅ ዘንድ (ጠባቂ ያደርጋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(73,1,'73:1','አንተ ተከናናቢው ሆይ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,2,'73:2','ሌሊቱን ሁሉ ጥቂት ሲቀር ቁም፤ (ስገደ)፡፡');
INSERT INTO "sadiq_sani" VALUES(73,3,'73:3','ግማሹን (ቁም)፤ ወይም ከርሱ ጥቂትን ቀንስ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,4,'73:4','ወይም በእርሱ ላይ ጨምር፡፡ ቁርኣንንም በዝግታ ማንበብን አንብብ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,5,'73:5','እኛ ባንተ ላይ ከባድ ቃልን እንጥላለንና፡፡');
INSERT INTO "sadiq_sani" VALUES(73,6,'73:6','የሌሊት መነሳት እርሷ (ሕዋሳትንና ልብን) ለማስማማት በጣም ብርቱ ናት፡፡ ለማንበብም ትክክለኛ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(73,7,'73:7','ላንተ በቀን ውስጥ ረዥም መዘዋወር አልለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,8,'73:8','የጌታህንም ስም አውሳ፤ ወደእርሱም (መግገዛት) መቋረጥን ተቋረጥ ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,9,'73:9','(እርሱም) የምሥራቅና የምዕራብ ጌታ ነው፡፡ ከእርሱ ሌላ አምላክ የለም፡፡ መጠጊያ አድርገህም ያዘው፡፡');
INSERT INTO "sadiq_sani" VALUES(73,10,'73:10','(ከሓዲዎች) በሚሉትም ላይ ታገሥ፡፡ መልካምንም መተው ተዋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(73,11,'73:11','የድሎት ባለቤቶችም ከኾኑት አስተባባዮች ጋር ተዎኝ፡፡ ጥቂትንም ጊዜ አቆያቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(73,12,'73:12','እኛ ዘንድ ከባድ ማሰሪያዎች እሳትም አልለና፡፡');
INSERT INTO "sadiq_sani" VALUES(73,13,'73:13','የሚያንቅ ምግብም፤ አሳማሚ ቅጣትም (አልለ)');
INSERT INTO "sadiq_sani" VALUES(73,14,'73:14','ምድርና ገራዎች በሚርገፈገፉበት፣ ጋራዎችም ፈሳሽ የአሸዋ ክምር በሚኾኑበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(73,15,'73:15','እኛ በእናንተ ላይ መስካሪ መልክተኛን ወደእናንተ ላክን፡፡ ወደ ፈርዖን መልክተኛን እንደላክን፡፡');
INSERT INTO "sadiq_sani" VALUES(73,16,'73:16','ፈርዖንም መልክተኛውን አመጸ፤ ብርቱ መያዝንም ያዝነው፡፡');
INSERT INTO "sadiq_sani" VALUES(73,17,'73:17','ብትክዱ ልጆችን ሸበቶዎች የሚያደርገውን ቀን (ቅጣት) እንዴት ትጠበቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(73,18,'73:18','ሰማዩ በርሱ (በዚያ ቀን) ተሰንጣቂ ነው፡፡ ቀጠሮው ተፈጻሚ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(73,19,'73:19','ይህቺ መገሠጫ ናት፡፡ (መዳንን) የሻም ሰው ወደ ጌታው መንገድን ይይዛል፡፡');
INSERT INTO "sadiq_sani" VALUES(73,20,'73:20','አንተ ከሌሊቱ ሁለት እጅ ያነሰን ግማሹንም ሲሶውንም የምትቆም መኾንህን ጌታህ በእርግጥ ያውቃል፡፡ ከእነዚያም አብረውህ ካሉት ከፊሎች (የሚቆሙ መኾናቸውን ያውቃል)፡፡ አላህም ሌሊትንና ቀንን ይለክካል፡፡ (ሌሊቱን) የማታዳርሱት መኾናችሁን ዐወቀ፡፡ በእናንተ ላይም (ወደ ማቃለል) ተመለሰላችሁ፡፡ ስለዚህ ከቁርኣን (በስግደት) የተቻላችሁን አንብቡ፡፡ ከእናንተ ውስጥ በሽተኞች፣ ሌሎችም ከአላህ ችሮታ ለመፈለግ በምድር ላይ የሚጓዙ፣ ሌሎችም በአላህ መንገድ (ሃይማኖት) የሚጋደሉ እንደሚኖሩ ዐወቀ፡፡ (አቃለለላቸውም)፡፡ ከእርሱም የተቻለችሁን አንብቡ፡፡ ሶላትንም ስገዱ፡፡ ዘካንም ስጡ፡፡ ለአላህም መልካም ብደርን አበድሩ፡፡ ከመልካም ሥራም ለነፍሶቻችሁ የምታስቀድሙትን ሁሉ እርሱ የተሻለና በምንዳም ታላቅ ኾኖ አላህ ዘንድ ታገኙታላችሁ፡፡ አላህንም ምሕረትን ለምኑት፡፡ አላህ በጣም መሓሪ አዛኝ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(74,1,'74:1','አንተ (ልብስህን) ደራቢው ሆይ!');
INSERT INTO "sadiq_sani" VALUES(74,2,'74:2','ተነሳ አስጠንቅቅም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,3,'74:3','ጌታህንም አክብር፡፡');
INSERT INTO "sadiq_sani" VALUES(74,4,'74:4','ልብስህንም አጥራ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,5,'74:5','ጣዖትንም ራቅ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,6,'74:6','ማብዛትንም የምትፈልግ ኾነህ፤ (በረከትን) አትለግስለለ');
INSERT INTO "sadiq_sani" VALUES(74,7,'74:7','ለጌታህም (ትዕዛዝ) ታገሥ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,8,'74:8','በቀንዱም በተነፋ ጊዜ (ነገሩ ይበረታል)፡፡');
INSERT INTO "sadiq_sani" VALUES(74,9,'74:9','ይህም (ጊዜ) ያ ቀን አስቸጋሪ ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(74,10,'74:10','በከሓዲዎች ላይ ገር ያልኾነ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(74,11,'74:11','አንድ ኾኖ ከፈጠርኩት ሰው ጋር ተዎኝ (እኔ እበቃሃለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(74,12,'74:12','ለእርሱም የተዘረጋን ገንዘብ (በያይነቱ) ያደረግሁለት፡፡');
INSERT INTO "sadiq_sani" VALUES(74,13,'74:13','(በያደባባዩ) ተገኝዎች የኾኑ ወንዶች ልጆችንም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,14,'74:14','ለእርሱም (ኑሮውን) መዘርጋትን የዘረጋሁለት (ከኾነ ሰው ገር ተዎኝ)፡፡');
INSERT INTO "sadiq_sani" VALUES(74,15,'74:15','ከዚያም ልጨምርለት ይከጅላል፡፡');
INSERT INTO "sadiq_sani" VALUES(74,16,'74:16','ይከልከል፤ እርሱ ለአንቀጾቻችን በእርግጥ ተቃዋሚ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(74,17,'74:17','በእርግጥ ችግርን አስገድደዋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,18,'74:18','እርሱ (በቁርኣን ነገር) ሐሰበ፤ ገመተም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,19,'74:19','ተረገመም፤ እንዴት ገመተ!');
INSERT INTO "sadiq_sani" VALUES(74,20,'74:20','ከዚያም (ከሞት በኋላ) ተረገመ፡፡ እንዴት ገመተ!');
INSERT INTO "sadiq_sani" VALUES(74,21,'74:21','ከዚያም እንደ ገና (በቁርኣኑ ነገር) ተመለከተ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,22,'74:22','ከዚያም ፊቱን አጨፈገገ፤ አኮማተረም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,23,'74:23','ከዚያም (ከማመን) ዞረ፤ ኮራም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,24,'74:24','አለም «ይህ (ከሌላ) የሚቀዳ ድግምት እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,25,'74:25','«ይህ የሰው ቃል እንጅ ሌላ አይደለም፡፡»');
INSERT INTO "sadiq_sani" VALUES(74,26,'74:26','በሰቀር ውስጥ በእርግጥ አገባዋለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,27,'74:27','ሰቀር ምን እንደኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(74,28,'74:28','(ያገኘችውን ሁሉ) አታስቀርም አትተውምም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,29,'74:29','ቆዳን በጣም አክሳይ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(74,30,'74:30','በእርሷም ላይ አሥራ ዘጠኝ (ዘበኞች) አሉባት፡፡');
INSERT INTO "sadiq_sani" VALUES(74,31,'74:31','የእሳትንም ዘበኞች መላእክት እንጅ ሌላ አላደረግንም፡፡ ቁጥራቸውንም ለእነዚያ ለካዱት መፈተኛ እንጅ አላደረግንም፡፡ እነዚያ መጽሐፍን የተሰጡት እንዲያረጋግጡ፣ እነዚያም ያመኑት እምነትን እንዲጨምሩ፣ እነዚያም መጽሐፍን የተሰጡትና ምእምናኖቹ እንዳይጠራጠሩ፣ እነዚያም በልቦቻቸው ውስጥ በሽታ ያለባቸው (መናፍቃን)፣ ከሓዲዎችም አላህ በዚህ (ቁጥር) ምን ምሳሌ ሽቷል? እንዲሉ (እንጅ አላደረግንም)፡፡ እንደዚሁ አላህ የሚሻውን ያጠምማል፡፡ የሚሻውንም ያቀናል፡፡ የጌታህንም ሰራዊት (ልክ) ከርሱ በቀር ማንም አያውቅም፡፡ እርሷም (የሰቀር እሳት) ለሰዎች መገሰጫ እንጅ ሌላ አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,32,'74:32','(ከክህደት) ይከልከሉ፡፡ በጨረቃ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,33,'74:33','በሌሊቱም በኼደ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,34,'74:34','በንጋቱም ባበራ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,35,'74:35','እርሷ (ሰቀር) ከታላላቆቹ (አደጋዎች) አንዷ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(74,36,'74:36','ለሰዎች አስፈራሪ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(74,37,'74:37','ከእናንተ (ወደ በጎ ነገር) መቅደምን ወይም ወደ ኋላ መቅረትን ለሻ ሰው (አስፈራሪ ስትኾኑ)፡፡');
INSERT INTO "sadiq_sani" VALUES(74,38,'74:38','ነፍስ ሁሉ በሠራችው ሥራ ተያዥ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(74,39,'74:39','የቀኝ ባለቤቶች ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,40,'74:40','(እነርሱስ) በገነቶች ውስጥ ይጠያየቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,41,'74:41','ከአመጸኞቹ ኹነታ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,42,'74:42','(ይሏቸዋልም) «በሰቀር ውስጥ ምን አገባችሁ?»');
INSERT INTO "sadiq_sani" VALUES(74,43,'74:43','(እነርሱም) ይላሉ «ከሰጋጆቹ አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,44,'74:44','«ለድኾችም የምናበላ አልነበርንም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,45,'74:45','«ከዘባራቂዎቹም ጋር እንዘባርቅ ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(74,46,'74:46','«በፍርዱ ቀንም እናስተባብል ነበርን፡፡');
INSERT INTO "sadiq_sani" VALUES(74,47,'74:47','«እርግጠኛው (ሞት) እስከ መጣን ድረስ፡፡»');
INSERT INTO "sadiq_sani" VALUES(74,48,'74:48','የአማላጆችም ምልጃ አትጠቅማቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,49,'74:49','ከግሣጼዬም (ከቁርኣን) የሚያፈገፍጉት ለእነርሱ ምን አልላቸው?');
INSERT INTO "sadiq_sani" VALUES(74,50,'74:50','እነርሱ ልክ ደንባሪዎች የሜዳ አህዮች ይመስላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,51,'74:51','ከዐንበሳ የሸሹ፡፡');
INSERT INTO "sadiq_sani" VALUES(74,52,'74:52','ይልቁንም ከእነርሱ እያንዳንዱ ሰው ሁሉ የተዘረጉ ጽሑፎችን እንዲስሰጥ ይፈልጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(74,53,'74:53','ይከልከሉ፡፡ ይልቁንም የመጨረሻይቱን ዓለም (ቅጣት) አይፈሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(74,54,'74:54','ይከልከሉ፡፡ እርሱ (ቁርኣን) መገሠጫ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(74,55,'74:55','ስለዚህ የሻ ሰው ይገሰጽበታል፡፡');
INSERT INTO "sadiq_sani" VALUES(74,56,'74:56','አላህም ካልሻ በስተቀር አይገሠጹም፡፡ እርሱ (አላህ) የመፈራት ባለቤት የምሕረትም ባለቤት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,1,'75:1','(ነገሩ ከሓዲዎች እንደሚሉት) አይደለም፡፡ በትንሣኤ ቀን እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,2,'75:2','(ራሷን) ወቃሽ በኾነች ነፍስም እምላለሁ፡፡ (በእርግጥ ትቀሰቀሳላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(75,3,'75:3','ሰው አጥንቶቹን አለመሰብሰባችንን ያስባልን?');
INSERT INTO "sadiq_sani" VALUES(75,4,'75:4','አይደለም ጣቶቹን (ፊት እንደ ነበሩ) በማስተካከል ላይ ቻዮች ስንኾን (እንሰበስባቸዋለን)፡፡');
INSERT INTO "sadiq_sani" VALUES(75,5,'75:5','ይልቁንም ሰው በፊቱ ባለው ነገር (በትንሣኤ) ሊያስተባብል ይፈልጋል፡፡');
INSERT INTO "sadiq_sani" VALUES(75,6,'75:6','«የትንሣኤው ቀን መቼ ነው?» ሲል ይጠይቃል፡፡');
INSERT INTO "sadiq_sani" VALUES(75,7,'75:7','ዓይንም በዋለለ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,8,'75:8','ጨረቃም በጨለመ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,9,'75:9','ፀሐይና ጨረቃም በተሰበሰቡ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,10,'75:10','«ሰው በዚያ ቀን መሸሻው የት ነው?» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(75,11,'75:11','ይከልከል፤ ምንም መጠጊያ የለም፡፡');
INSERT INTO "sadiq_sani" VALUES(75,12,'75:12','በዚያ ቀን መርጊያው ወደ ጌታህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,13,'75:13','ሰው በዚያ ቀን ባስቀደመውና ባስቆየው ሁሉ ይነገራል፡፡');
INSERT INTO "sadiq_sani" VALUES(75,14,'75:14','በእርግጥም ሰው በነፍሱ ላይ አስረጅ ነው፡፡ (አካሎቹ ይመሰክሩበታል)፡፡');
INSERT INTO "sadiq_sani" VALUES(75,15,'75:15','ምክንያቶቹን ሁሉ ቢያመጣም እንኳ (አይሰማም)፡፡');
INSERT INTO "sadiq_sani" VALUES(75,16,'75:16','በእርሱ (በቁርኣን ንባብ) ልትቸኩል ምላስህን በእርሱ አታላውስ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,17,'75:17','(በልብህ ውስጥ) መሰብሰቡና ማንበቡ በእኛ ላይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(75,18,'75:18','ባነበብነውም ጊዜ (ካለቀ በኋላ) ንባቡን ተከተል፡፡');
INSERT INTO "sadiq_sani" VALUES(75,19,'75:19','ከዚያም ማብራራቱ በእኛ ላይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,20,'75:20','(ከመጣደፍ) ተከልከል፡፡ ይልቁንም (ሰዎች) ፈጣኒቱን (ዓለም) ትወዳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,21,'75:21','መጨረሻይቱንም (ዓለም) ትተዋላችሁ፡፡ (አትዘጋጁላትም)፡፡');
INSERT INTO "sadiq_sani" VALUES(75,22,'75:22','ፊቶች በዚያ ቀን አብሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,23,'75:23','ወደ ጌታቸው ተመልካቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,24,'75:24','ፊቶችም በዚያ ቀን ጨፍጋጊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,25,'75:25','በርሳቸውም ዐደጋ እንደሚሠራባቸው ያረጋግጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,26,'75:26','ንቁ (ነፍስ) ብሪዎችን በደረሰች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(75,27,'75:27','«አሻሪው ማነው?» በተባለም ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,28,'75:28','(ሕመምተኛው) እርሱ (የሰፈረበት ነገር ከዚህ ዓለም) መልለየት መኾኑን ባረጋገጠም ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,29,'75:29','ባትም በባት በተጣበቀች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,30,'75:30','በዚያ ቀን መነዳቱ ወደ ጌታህ (ፍርድ) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,31,'75:31','አላመነምም አልሰገደምም፡፡');
INSERT INTO "sadiq_sani" VALUES(75,32,'75:32','ግን አስተባበለ፤ (ከእምነት) ዞረም፡፡');
INSERT INTO "sadiq_sani" VALUES(75,33,'75:33','ከዚያም እየተንጎባለለ ወደ ቤተሰቡ ኼደ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,34,'75:34','የምትጠላውን ነገር (አላህ) ያስከትልህ፡፡ ለአንተ ተገቢህም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,35,'75:35','ከዚያም ጥፋት ይጠጋህ ለአንተ የተገባህም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(75,36,'75:36','ሰው ስድ ኾኖ መትተውን ይጠረጥራልን?');
INSERT INTO "sadiq_sani" VALUES(75,37,'75:37','የሚፈሰስ ከኾነ የፍትወት ጠብታ አልነበረምን?');
INSERT INTO "sadiq_sani" VALUES(75,38,'75:38','ከዚያም የረጋ ደም ኾነ (አላህ ሰው አድርጎ) ፈጠረውም፡፡ አስተካከለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(75,39,'75:39','ከእርሱም ሁለት ዓይነቶችን ወንድና ሴትን አደረገ፡፡');
INSERT INTO "sadiq_sani" VALUES(75,40,'75:40','ይህ (ጌታ) ሙታንን ሕያው በማድረግ ላይ ቻይ አይደለምን?');
INSERT INTO "sadiq_sani" VALUES(76,1,'76:1','በሰው ላይ የሚታወስ ነገር ሳይኾን ከዘመናት የተወሰነ ጊዜ በእርግጥ አልፎበታል፡፡');
INSERT INTO "sadiq_sani" VALUES(76,2,'76:2','እኛ ሰውን (በሕግ ግዳጅ) የምንሞከረው ስንኾን ቅልቅሎች ከኾኑ የፍትወት ጠብታ ፈጠርነው፡፡ ሰሚ ተመልካችም አደረግነው፡፡');
INSERT INTO "sadiq_sani" VALUES(76,3,'76:3','እኛ ወይ አመስጋኝ ወይም ከሓዲ ሲኾን መንገዱን መራነው፤ (ገለጽንለት)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,4,'76:4','እኛ ለከሓዲዎች ሰንሰሎቶችንና እንዛዝላዎችን፣ እሳትንም አዘጋጅተናል፡፡');
INSERT INTO "sadiq_sani" VALUES(76,5,'76:5','በጎ አድራጊዎች መበረዣዋ ካፉር ከኾነች ጠጅ ይጠጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,6,'76:6','ከእርሷ የአላህ ባሮች የሚጠጡላት (ወደፈለጉበት) ማንቧቧትን የሚያንቧቧት ከኾነች ምንጭ (ይጠጣሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,7,'76:7','(ዛሬ) በስለታቸው ይሞላሉ፡፡ መከራው ተሰራጪ የኾነንም ቀን ይፈራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,8,'76:8','ምግብንም ከመውደዳቸው ጋር ለድኻ፣ ለየቲምም፣ ለምርኮኛም ለእስረኛም ያበላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,9,'76:9','«የምናበላችሁ ለአላህ ውዴታ ብቻ ነው፡፡ ከእናንተ ዋጋንም ማመስገንንም አንፈልግም፡፡');
INSERT INTO "sadiq_sani" VALUES(76,10,'76:10','«እኛ (ፊትን) የሚያጨፈግግን ብርቱ ቀን ከጌታችን እንፈራለንና፤» (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,11,'76:11','አላህም የዚያን ቀን ክፋት ጠበቃቸው፡፡ (ፊታቸው) ማማርንና መደሰትንም ገጠማቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(76,12,'76:12','በመታገሣቸውም ገነትንና የሐር ልብስን መነዳቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(76,13,'76:13','በውስጧ በባለ አጎበር አልጋዎች ላይ ተደጋፊዎች በውስጧ ፀሐይንም ጨረቃንም የማያዩ ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,14,'76:14','ዛፎችዋም በእነርሱ ላይ የቀረበች፣ ፍሬዎችዋም (ለለቃሚዎች) መግገራትን የተገራች ስትኾን (ገነትን መነዳቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,15,'76:15','በእነርሱም ላይ ከብር በኾኑ ዕቃዎች (ሰሐኖች) የብርጭቆዎች በኾኑ ኩባያዎችም ይዝዞርባቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(76,16,'76:16','መለካትን የለኳቸው በኾኑ የብር ብርጭቆዎች (ይዝዞርባቸዋል)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,17,'76:17','በእርሷም መበረዣዋ ዘንጀቢል የኾነችን ጠጅ ይጠጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,18,'76:18','በውስጧ ያለችውን ሰልሰቢል ተብላ የምትጠራውን ምንጭ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,19,'76:19','በእነርሱም ላይ (ባሉበት ኹነታ) የሚዘወትሩ ወጣት ልጆች ይዘዋወራሉ፡፡ ባየሃቸው ጊዜ የተበተነ ሉል ናቸው ብለህ ታስባቸዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,20,'76:20','እዚያም በተመለከትክ ጊዜ ጸጋንና ታላቅ ንግሥናን ታያለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,21,'76:21','አረንጓዴዎች የኾኑ የቀጭን ሐር ልብሶችና ወፍራም ሐርም በላያቸው ላይ አልለ፡፡ ከብር የኾኑ አንባሮችንም ይሸለማሉ፡፡ ጌታቸውም አጥሪ የኾነን መጠጥ ያጠጣቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(76,22,'76:22','«ይህ በእርግጥ ለእናንተ ዋጋ ኾነ፡፡ ሥራችሁም ምስጉን ኾነ፤» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(76,23,'76:23','እኛ ቁርኣንን ባንተ ላይ ማውረድን እኛው አወረድነው፡፡');
INSERT INTO "sadiq_sani" VALUES(76,24,'76:24','ለጌታህ ፍርድም ታገሥ፡፡ ከእነርሱም ኀጢአተኛን ወይም ከሓዲን አትታዘዝ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,25,'76:25','የጌታህንም ስም በጧትና ከቀትር በላይ አውሳ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,26,'76:26','ከሌሊቱም ለእርሱ ስገድ፡፡ በረዢም ሌሊትም አወድሰው፡፡');
INSERT INTO "sadiq_sani" VALUES(76,27,'76:27','እነዚያ ፈጣኒቱን ዓለም ይወዳሉ፡፡ ከባድን ቀንም በስተፊታቸው ኾኖ ይተዋሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(76,28,'76:28','እኛ ፈጠርናቸው፡፡ መለያልያቸውንም አጠነከርን፡፡ በሻንም ጊዜ መሰሎቻቸውን መለወጥን እንለውጣለን፡፡');
INSERT INTO "sadiq_sani" VALUES(76,29,'76:29','ይህቺ መገሠጫ ናት፡፡ የሻም ሰው ወደ ጌታው መንገድን ይይዛል፡፡');
INSERT INTO "sadiq_sani" VALUES(76,30,'76:30','አላህም ካልሻ በስተቀር አትሹም፡፡ አላህ ዐዋቂ ጥበበኛ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(76,31,'76:31','የሚሻውን ሰው በእዝነቱ ውስጥ ያገባዋል፡፡ በዳዮቹንም (ዝቶባቸዋል)፡፡ ለእነርሱ አሳማሚ ቅጣትን አዘጋጅቶላቸዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(77,1,'77:1','ተከታታይ ኾነው በተላኩት፣');
INSERT INTO "sadiq_sani" VALUES(77,2,'77:2','በኀይል መንፈስን ነፋሾች በኾኑትም፣');
INSERT INTO "sadiq_sani" VALUES(77,3,'77:3','መበተንን በታኞች በኾኑትም፤ (ነፋሶች)፣');
INSERT INTO "sadiq_sani" VALUES(77,4,'77:4','መለየትን ለይዎች በኾኑትም፣');
INSERT INTO "sadiq_sani" VALUES(77,5,'77:5','መገሠጫን (ወደ ነቢያት) ጣይዎች በኾኑትም፣');
INSERT INTO "sadiq_sani" VALUES(77,6,'77:6','ምክንያትን ለማስወገድ ወይም ለማስፈራራት (ጣይዎች በኾኑት መላእክት) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,7,'77:7','ያ የምትስፈራሩበት (ትንሣኤ) በእርግጥ ኋኝ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,8,'77:8','ከዋክብትም (ብርሃንዋ) በታበሰች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,9,'77:9','ሰማይም በተከፈተች ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,10,'77:10','ጋራዎችም በተንኮተኮቱ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,11,'77:11','መልክተኞቹም ወቅት በተደረገላቸው ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,12,'77:12','ለየትኛው ቀን ተቀጠሩ (የሚባል ሲኾን)፤');
INSERT INTO "sadiq_sani" VALUES(77,13,'77:13','ለመለያው ቀን (በተባለ ጊዜ፤ በፍጡሮች መካከል ይፈረዳል)፡፡');
INSERT INTO "sadiq_sani" VALUES(77,14,'77:14','የመለያውም ቀን ምን እንደኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(77,15,'77:15','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,16,'77:16','የፊተኞቹን (ሕዝቦች) አላጠፋንምን?');
INSERT INTO "sadiq_sani" VALUES(77,17,'77:17','ከዚያም የኋለኞቹን እናስከትላቸዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(77,18,'77:18','በአመጸኞች ሁሉ እንደዚሁ እንሠራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(77,19,'77:19','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,20,'77:20','ከደካማ ውሃ አልፈጠርናችሁምን?');
INSERT INTO "sadiq_sani" VALUES(77,21,'77:21','በተጠበቀ መርጊያ ውስጥም (በማሕፀን) አደረግነው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,22,'77:22','እስከ ታወቀ ልክ ድረስ፡፡ መጠንነውም፤ ምን ያማርንም');
INSERT INTO "sadiq_sani" VALUES(77,23,'77:23','መጠንነውም፤ ምን ያማርንም መጣኞች ነን!');
INSERT INTO "sadiq_sani" VALUES(77,24,'77:24','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,25,'77:25','ምድርን ሰብሳቢ አላደረግናትምን?');
INSERT INTO "sadiq_sani" VALUES(77,26,'77:26','ሕያዋን ኾናችሁ ሙታንም ኾናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,27,'77:27','በውስጧም ከፍተኛዎችን ተራራዎች አደረግን፡፡ ጣፋጭ ውሃንም አጠጣናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,28,'77:28','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,29,'77:29','«ወደዚያ በእርሱ ታስተባብሉ ወደ ነበራችሁት (ቅጣት) አዝግሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,30,'77:30','«ባለ ሦስት ቅርንጫፎች ወደ ኾነው ጥላ አዝግሙ፤» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(77,31,'77:31','አጠላይ ያልኾነ ከእሳቱም ላንቃ የማያድን ወደ ኾነው (አዝግሙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(77,32,'77:32','እርሷ እንደ ታላላቅ ሕንጻ የኾኑን ቃንቄዎች ትወረውራለች፡፡');
INSERT INTO "sadiq_sani" VALUES(77,33,'77:33','(ቃንቄውም) ልክ ዳለቻዎች ግመሎችን ይመስላል፡፡');
INSERT INTO "sadiq_sani" VALUES(77,34,'77:34','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,35,'77:35','ይህ የማይናገሩበት ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,36,'77:36','ይቅርታም ይጠይቁ ዘንድ፤ ለእነርሱ አይፈቀድላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(77,37,'77:37','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,38,'77:38','ይህ መለያው ቀን ነው፡፡ እናንተንም የፊተኞቹንም ሰብሰብናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,39,'77:39','ለናንተም (ለማምለጥ) ብልሃት እንዳላችሁ ተተናኮሉኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(77,40,'77:40','ላስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,41,'77:41','ጥንቁቆቹ በጥላዎችና በምንጮች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,42,'77:42','ከሚከጅሉትም ሁሉ በፍራፍሬዎች ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,43,'77:43','«ትሠሩት በነበራችሁት ዋጋ የምትደሰቱ ኾናችሁ ብሉ፣ ጠጡም፤» (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(77,44,'77:44','እኛ እንደዚሁ መልካም ሠሪዎችን ሁሉ እንመነዳለን፡፡');
INSERT INTO "sadiq_sani" VALUES(77,45,'77:45','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,46,'77:46','ብሉ፤ ጥቂትንም (ጊዜ) ተጠቀሙ፡፡ እናንተ አመጸኞች ናችሁና፡፡');
INSERT INTO "sadiq_sani" VALUES(77,47,'77:47','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,48,'77:48','«ለእነርሱ ስገዱም» በተባሉ ጊዜ አይሰግዱም፡፡');
INSERT INTO "sadiq_sani" VALUES(77,49,'77:49','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(77,50,'77:50','ከእርሱም ሌላ በየትኛው ንግግር ያምናሉ?');
INSERT INTO "sadiq_sani" VALUES(78,1,'78:1','ከምን ነገር ይጠያየቃሉ?');
INSERT INTO "sadiq_sani" VALUES(78,2,'78:2','ከታላቁ ዜና (ከቁርኣን ይጠያየቃሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,3,'78:3','ከዚያ እነርሱ በእርሱ የተለያዩበት ከኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,4,'78:4','ይከልከሉ፤ ወደፊት (የሚደርስበቸውን) በእርግጥ ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(78,5,'78:5','ከዚያም ይከልከሉ፤ ወደፊት ያውቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(78,6,'78:6','ምድርን ምንጣፍ አላደረግንምን?');
INSERT INTO "sadiq_sani" VALUES(78,7,'78:7','ጋራዎችንም ችካሎች አላደረግንምን?');
INSERT INTO "sadiq_sani" VALUES(78,8,'78:8','ብዙ ዓይነቶችም አድርገን ፈጠርናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(78,9,'78:9','እንቅልፋችሁንም ዕረፍት አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,10,'78:10','ሌሊቱንም ልባስ አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,11,'78:11','ቀኑንም (ለኑሮ) መስሪያ አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,12,'78:12','ከበላያችሁም ሰባትን ብርቱዎች (ሰማያት) ገነባን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,13,'78:13','አንጸባራቂ ብርሃንንም አደረግን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,14,'78:14','ከአረገዙት ደመናዎችም ተንቧቢ ውሃን አወረድን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,15,'78:15','በእርሱ እኽልንና ቅጠላ ቅጠልን እናወጣ ዘንድ (አወረድን)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,16,'78:16','የተቆላለፉ አትክልቶችንም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,17,'78:17','የመለያው ቀን የተወሰነ ጊዜ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,18,'78:18','በቀንዱ በሚነፋና የተለያዩ ጭፍሮች ኾናችሁ በምትመጡ ቀን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,19,'78:19','ሰማይም በምትከፈትና (ባለ) ደጃፎችም በምትኾንበት፡፡');
INSERT INTO "sadiq_sani" VALUES(78,20,'78:20','ጋራዎችም በሚነዱበት (እንደ) ሲሪብዱም በሚኾኑበት (ቀን) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,21,'78:21','ገሀነም መጠባበቂያ (ስፍራ) ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(78,22,'78:22','ለሕግ ተላላፊዎች መመለሻ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(78,23,'78:23','በውስጧ ብዙን ዘመናት ነዋሪዎች ሲኾኑ፤');
INSERT INTO "sadiq_sani" VALUES(78,24,'78:24','በውስጧ ቅዝቃዜንም መጠጥንም አይቀምሱም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,25,'78:25','ግን ሙቅ ውሃንና እዥን (ይቀምሳሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,26,'78:26','ተስማሚን ምንዳ ይምመነዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(78,27,'78:27','እነርሱ ምርመራን የማይፈሩ ነበሩና፡፡');
INSERT INTO "sadiq_sani" VALUES(78,28,'78:28','በአንቀጾቻችንም ማስተባበልን አስተባበሉና፡፡');
INSERT INTO "sadiq_sani" VALUES(78,29,'78:29','ነገሩንም ሁሉ የተጻፈ ሲኾን አጠቃለልነው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,30,'78:30','ቅመሱም ቅጣትንም እንጅ (ሌላን) አንጨምራችሁም (ይባላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,31,'78:31','ለጥንቁቆቹ መዳኛ ስፍራ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(78,32,'78:32','አትክልቶችና ወይኖችም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,33,'78:33','እኩያዎች የኾኑ ጡተ ጉቻማዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,34,'78:34','የተሞሉ ብርጭቆዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,35,'78:35','በውስጧ ትርፍ ቃልንም ማስዋሸትንም አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,36,'78:36','ከጌታህ የኾነን ምንዳ በቂ ስጦታን (ተሰጡ)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,37,'78:37','የሰማያትና የምድር በመካከላቸውም ያለው ሁሉ ጌታ በጣም አዛኝ ከኾነው (ተመነዱ)፡፡ ከእርሱ ጋር መነጋገርን አይችሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(78,38,'78:38','መንፈሱ (ጂብሪል) መላእክቶቹም የተሰለፉ ኾነው በሚቆሙ ቀን አል-ረሕማን ለእርሱ የፈቀደለትና ትክክለኛን ቃል ያለ ሰው ቢኾን እንጅ (መነጋገርን አይችሉም)፡፡');
INSERT INTO "sadiq_sani" VALUES(78,39,'78:39','ይህ የተረጋገጠው ቀን ነው፡፡ የሻም ሰው ወደ ጌታው መመለስን ይይዛል፡፡');
INSERT INTO "sadiq_sani" VALUES(78,40,'78:40','እኛ ሰው ሁሉ እጆቹ ያስቀደሙትን በሚያይበትና ከሓዲውም ዋ ምኞቴ! ምንነው ዐፈር በኾንኩ በሚልበት ቀን ቅርብ የኾነን ቅጣት አስጠነቀቅናችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,1,'79:1','በኃይል አውጪዎች በኾኑት፤');
INSERT INTO "sadiq_sani" VALUES(79,2,'79:2','በቀስታ መምዘዝንም መዘዦች በኾኑት፤');
INSERT INTO "sadiq_sani" VALUES(79,3,'79:3','መዋኘትም ዋኚዎች በኾኑት፤');
INSERT INTO "sadiq_sani" VALUES(79,4,'79:4','መቅደምንም ቀዳሚዎች በኾኑት፤');
INSERT INTO "sadiq_sani" VALUES(79,5,'79:5','ነገርንም አስተናባሪዎች በኾኑት (መላእክት) እምላለሁ፡፡ (በእርግጥ ትቀሰቀሳላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(79,6,'79:6','ተርገፍጋፊይቱ (ምድር) በምትርገፈገፍበት ቀን፤');
INSERT INTO "sadiq_sani" VALUES(79,7,'79:7','ለጣቂይቱ የምትከተላት ስትኾን (ትቀሰቀሳላችሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(79,8,'79:8','በዚያ ቀን ልቦች ተሸባሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,9,'79:9','ዓይኖቻቸው ተዋራጆች (አቀርቃሪዎች) ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,10,'79:10','«እኛ ወደ መጀመሪያይቱ ኹነታ ተመላሾች ነን?» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,11,'79:11','«የበሰበሱ አጥንቶች በኾን ጊዜ (እንቀሰቀሳለን?)»');
INSERT INTO "sadiq_sani" VALUES(79,12,'79:12','«ይህቺ ያን ጊዜ ባለ ከሳራ መመለስ ናት» ይላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,13,'79:13','እርሷም አንዲት ጩኸት ብቻ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,14,'79:14','ወዲያውኑም እነርሱ እንቅልፍ በሌለባት (ምድር) ላይ ይኾናሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,15,'79:15','የሙሳ ወሬ መጣልህን?');
INSERT INTO "sadiq_sani" VALUES(79,16,'79:16','ጌታው በተቀደሰው ሸለቆ በጡዋ በጠራው ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(79,17,'79:17','ወደ ፈርዖን ኺድ፡፡ እርሱ ወሰን አልፏልና፡፡');
INSERT INTO "sadiq_sani" VALUES(79,18,'79:18','በለውም፡- «ወደ መጥራራትህ ላንተ (መንገድ) አለህን?»');
INSERT INTO "sadiq_sani" VALUES(79,19,'79:19','«ወደ ጌታህም ልመራህ ትፈራውም ዘንድ፤ (መንገድ አለህን?)» አለው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,20,'79:20','ታላቂቱንም ተዓምር አሳየው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,21,'79:21','አስተባበለም፤ አመጸም፡፡');
INSERT INTO "sadiq_sani" VALUES(79,22,'79:22','ከዚያም (ለማጥፋት) የሚተጋ ኾኖ ዞረ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,23,'79:23','(ሰራዊቶቹን) ሰበሰበም፤ ተጣራም፡፡');
INSERT INTO "sadiq_sani" VALUES(79,24,'79:24','አለም፡- «እኔ ታላቁ ጌታችሁ ነኝ፡፡»');
INSERT INTO "sadiq_sani" VALUES(79,25,'79:25','አላህም በኋለኛይቱና በፊተኛይቱ (ቃል) ቅጣት ያዘው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,26,'79:26','በዚህ ውስጥ ለሚፈራ ሰው በእርግጥ መገምገሚያ አለበት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,27,'79:27','ባፈጣጠር እናንተ ይበልጥ የበረታችሁ ናችሁን? ወይንስ ሰማይ? (አላህ) ገነባት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,28,'79:28','ከፍታዋን አጓነ፤ አስተካከላትም፡፡');
INSERT INTO "sadiq_sani" VALUES(79,29,'79:29','ሌሊቷንም አጨለመ፡፡ ቀንዋንም ገለጸ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,30,'79:30','ምድርንም ከዚህ በኋላ ዘረጋት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,31,'79:31','ውሃዋንና ግጦሿን ከእርሷ አወጣ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,32,'79:32','ጋራዎችንም አደላደላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,33,'79:33','ለእናንተና ለእንስሶቻችሁ መጣቀሚያ ይኾን ዘንድ (ይህን አደረገ)፡፡');
INSERT INTO "sadiq_sani" VALUES(79,34,'79:34','ታላቂቱም መዐት በመጣች ጊዜ፣');
INSERT INTO "sadiq_sani" VALUES(79,35,'79:35','ሰው ሁሉ የሠራውን በሚያስታውስበት ቀን፣');
INSERT INTO "sadiq_sani" VALUES(79,36,'79:36','ገሀነምም ለሚያይ ሰው በተገለጠች ጊዜ፣');
INSERT INTO "sadiq_sani" VALUES(79,37,'79:37','የካደ ሰውማ፣');
INSERT INTO "sadiq_sani" VALUES(79,38,'79:38','ቅርቢቱንም ሕይወት የመረጠ፣');
INSERT INTO "sadiq_sani" VALUES(79,39,'79:39','ገሀነም እርሷ በእርግጥ መኖሪያው ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,40,'79:40','በጌታው ፊት መቆምን የፈራ ሰውማ ነፍሱንም ከዝንባሌዋ የከለከለ');
INSERT INTO "sadiq_sani" VALUES(79,41,'79:41','ገነት እርሷ በእርግጥ መኖሪያው ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(79,42,'79:42','«ከሰዓቲቱ መቼ ነው መኾኛዋ?» ሲሉ ይጠይቁሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(79,43,'79:43','አንተ እርሷን ከማስታወስ በምን ውስጥ ነህ?');
INSERT INTO "sadiq_sani" VALUES(79,44,'79:44','(የዕውቀት) መጨረሻዋ ወደ ጌታህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(79,45,'79:45','አንተ የሚፈራትን ሰው አስፈራሪ ብቻ ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(79,46,'79:46','እነርሱ በሚያዩዋት ቀን ያንዲት ቀን ምሽትን ወይም ረፏዷን እንጅ ያልቆዩ ይመስላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(80,1,'80:1','ፊቱን አጨፈገገ፤ ዞረም፡፡');
INSERT INTO "sadiq_sani" VALUES(80,2,'80:2','ዕውሩ ስለ መጣው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,3,'80:3','ምን ያሳውቅሃል? (ከኀጢአቶቹ) ሊጥራራ ይከጀላል፡፡');
INSERT INTO "sadiq_sani" VALUES(80,4,'80:4','ወይም ሊገሠጽ ግሠጼይቱም ልትጠቅመው (ይከጀላል)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,5,'80:5','የተብቃቃው ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(80,6,'80:6','አንተ ለእርሱ (ለእምነቱ በመጓጓት) በፈገግታ ትቀበለዋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(80,7,'80:7','ባይጥራራ (ባያምን) ባንተ ላይ ምንም የሌለብህ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(80,8,'80:8','እየገሰገሰ የመጣህ ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(80,9,'80:9','እርሱ (አላህን) የሚፈራ ሲኾን፤');
INSERT INTO "sadiq_sani" VALUES(80,10,'80:10','አንተ ከእርሱ ትዝዘናጋለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(80,11,'80:11','ተከልከል፡፡ እርሷ ማስገንዘቢያ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(80,12,'80:12','የሻም ሰው (ቁርኣኑን) ያስታውሰዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(80,13,'80:13','በተከበሩ ጽሑፎች ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,14,'80:14','ከፍ በተደረገች ንጹሕ በተደረገች (ጽሑፍ ውስጥ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,15,'80:15','በጸሐፊዎቹ (መላእክት) እጆች (ንጹሕ የተደረገች)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,16,'80:16','የተከበሩና ታዛዦች በኾኑት (ጸሐፊዎች እጆች)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,17,'80:17','ሰው ተረገመ፤ ምን ከሓዲ አደረገው?');
INSERT INTO "sadiq_sani" VALUES(80,18,'80:18','(ጌታው) በምን ነገር ፈጠረው? (አያስብምን?)');
INSERT INTO "sadiq_sani" VALUES(80,19,'80:19','ከፍትወት ጠብታ ፈጠረው፤ መጠነውም፡፡');
INSERT INTO "sadiq_sani" VALUES(80,20,'80:20','ከዚያም (መውጫ) መንገዱን አገራው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,21,'80:21','ከዚያም ገደለው፤ እንዲቀበርም አደረገው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,22,'80:22','ከዚያም (ማንሳቱን) በሻ ጊዜ ያስነሳዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(80,23,'80:23','በእውነት ያንን (ጌታው) ያዘዘውን ገና አልፈጸመም፡፡');
INSERT INTO "sadiq_sani" VALUES(80,24,'80:24','ሰውም ወደ ምግቡ ይመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(80,25,'80:25','እኛ ውሃን ማንቧቧትን ያንቧቧን መኾናችንን፡፡');
INSERT INTO "sadiq_sani" VALUES(80,26,'80:26','ከዚያም ምድርን (በደካማ ቡቃያ) መሰንጠቅን የሰነጠቅን፤');
INSERT INTO "sadiq_sani" VALUES(80,27,'80:27','በውስጧም እኽልን ያበቀልን፤');
INSERT INTO "sadiq_sani" VALUES(80,28,'80:28','ወይንንም፤ እርጥብ ያበቀልን፤');
INSERT INTO "sadiq_sani" VALUES(80,29,'80:29','የዘይት ወይራንም ዘንባባንም፤');
INSERT INTO "sadiq_sani" VALUES(80,30,'80:30','ጭፍቆች አትክልቶችንም፤');
INSERT INTO "sadiq_sani" VALUES(80,31,'80:31','ፍራፍሬንም ገለባንም፤ (ያበቀልን መኾናችንን ይመልከት)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,32,'80:32','ለእናንተም ለእንስሶቻችሁም መጠቀሚያ ይኾን ዘንድ (ይህን ሠራን)፡፡');
INSERT INTO "sadiq_sani" VALUES(80,33,'80:33','አደንቋሪይቱም (መከራ) በመጣች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(80,34,'80:34','ሰው ከወንድሙ በሚሸሽበት ቀን፤');
INSERT INTO "sadiq_sani" VALUES(80,35,'80:35','ከናቱም ካባቱም፤');
INSERT INTO "sadiq_sani" VALUES(80,36,'80:36','ከሚስቱም ከልጁም፤');
INSERT INTO "sadiq_sani" VALUES(80,37,'80:37','ከእነርሱ ለየሰው ሁሉ በዚያ ቀን (ከሌላው) የሚያብቃቃው ኹነታ አልለው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,38,'80:38','ፊቶች በዚያ ቀን አብሪዎች ናቸው፤');
INSERT INTO "sadiq_sani" VALUES(80,39,'80:39','ሳቂዎችም ተደሳቾችም ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(80,40,'80:40','ፊቶችም በዚያ ቀን በላያቸው ላይ ትቢያ አልለባቸው፤');
INSERT INTO "sadiq_sani" VALUES(80,41,'80:41','ጥቁረት ትሸፍናቸዋለች፤');
INSERT INTO "sadiq_sani" VALUES(80,42,'80:42','እነዚያ እነሱ ከሓዲዎቹ አመጸኞቹ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(81,1,'81:1','ፀሐይ በተጠቀለለች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,2,'81:2','ከዋክብትም በረገፉ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,3,'81:3','ተራራዎችም በተነዱ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,4,'81:4','የዐሥር ወር እርጉዞች ግመሎችም (ያለጠባቂ) በተተዉ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,5,'81:5','እንስሳትም ሁሉ በተሰበሰቡ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,6,'81:6','ባሕሮችም በተቃጠሉ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,7,'81:7','ነፍሶችም (በየአካሎቻቸው) በተቆራኙ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,8,'81:8','በሕይወቷ የተቀበረችውም ሴት ልጅ በተጠየቀች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,9,'81:9','በምን ወንጀል እንደ ተገደለች፤');
INSERT INTO "sadiq_sani" VALUES(81,10,'81:10','ጽሑፎችም በተዘረጉ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,11,'81:11','ሰማይም በተገሸለጠች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,12,'81:12','ገሀነምም በተነደደች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,13,'81:13','ገነትም በተቀረበች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,14,'81:14','ነፍስ ሁሉ (ከሥራ) ያቀረበችውን ታውቃለች፤');
INSERT INTO "sadiq_sani" VALUES(81,15,'81:15','ተመላሾችም በኾኑት (ዐዋድያት) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(81,16,'81:16','ኺያጆች ገቢዎች በኾኑት (ከዋክብት)፡፡');
INSERT INTO "sadiq_sani" VALUES(81,17,'81:17','በሌሊቱም (ጨለማውን) በጠነሰሰ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(81,18,'81:18','በንጋቱም (በብርሃን) በተነፈሰ ጊዜ፤ (እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(81,19,'81:19','እርሱ (ቁርኣን) የክቡር መልክተኛ ቃል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(81,20,'81:20','የኀይል ባለቤት በዙፋኑ ባለ ቤት ዘንድ ባለሟል የኾነ፡፡');
INSERT INTO "sadiq_sani" VALUES(81,21,'81:21','በዚያ ስፍራ ትእዛዙ ተሰሚ፤ ታማኝ የኾነ (መልክተኛ ቃል) ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(81,22,'81:22','ነቢያችሁም በፍጹም ዕብድ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(81,23,'81:23','በግልጹ አድማስም በእርግጥ አይቶታል፡፡');
INSERT INTO "sadiq_sani" VALUES(81,24,'81:24','እርሱም በሩቁ ወሬ ላይ ሰሳች (ንፉግ) አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(81,25,'81:25','እርሱም (ቁርኣን) የእርጉም ሰይጣን ቃል አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(81,26,'81:26','ታዲያ ወዴት ትኼዳላችሁ?');
INSERT INTO "sadiq_sani" VALUES(81,27,'81:27','እርሱ የዓለማት መገሠጫ እንጅ ሌላ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(81,28,'81:28','ከናንተ ቀጥተኛ መኾንን ለሻ ሰው (መገሰጫ ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(81,29,'81:29','የዓለማት ጌታ አላህ ካልሻም አትሹም፡፡');
INSERT INTO "sadiq_sani" VALUES(82,1,'82:1','ሰማይ በተሰነጠቀች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(82,2,'82:2','ከዋክብትም ተበታትነው በወደቁ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(82,3,'82:3','ባሕሮችም በተከፈቱና በተደበላለቁ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(82,4,'82:4','መቃብሮችም በተገለባበጡና (ሙታን) በተነሱ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(82,5,'82:5','ማንኛይቱም ነፍስ ያስቀደመችውንና ያቆየችውን ታውቃለች፡፡');
INSERT INTO "sadiq_sani" VALUES(82,6,'82:6','አንተ ሰው ሆይ! በቸሩ ጌታህ ምን አታለለህ?');
INSERT INTO "sadiq_sani" VALUES(82,7,'82:7','በዚያ በፈጠረህ አካለ ሙሉም ባደረገህ ባስተካከለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(82,8,'82:8','በማንኛውም በሻው ቅርፅ በገጣጠመህ (ጌታህ ምን አታለለህ)፡፡');
INSERT INTO "sadiq_sani" VALUES(82,9,'82:9','ተከልከሉ፤ በእውነቱ በፍርዱ ቀን ታስተባብላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(82,10,'82:10','በእናንተ ላይ ተጠባባቂዎች ያሉባችሁ፤ ስትኾኑ፤');
INSERT INTO "sadiq_sani" VALUES(82,11,'82:11','የተከበሩ ጸሐፊዎች የኾኑ፤ (ተጠባባቂዎች)፡፡');
INSERT INTO "sadiq_sani" VALUES(82,12,'82:12','የምትሠሩትን ሁሉ የሚያውቁ፡፡');
INSERT INTO "sadiq_sani" VALUES(82,13,'82:13','እውነተኞቹ ምእምናን በእርግጥ በገነት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(82,14,'82:14','ከሓዲዎቹም በእርግጥ በገሀነም ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(82,15,'82:15','በፍርዱ ቀን ይገቧታል፡፡');
INSERT INTO "sadiq_sani" VALUES(82,16,'82:16','እነርሱም ከርሷ ራቂዎች አይደሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(82,17,'82:17','የፍርድ ቀን ምን እንደኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(82,18,'82:18','ከዚያም የፍርዱ ቀን ምን እንደኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(82,19,'82:19','(እርሱ) ማንኛይቱም ነፍስ ለሌላይቱ ነፍስ ምንም ማድረግን የማትችልበት ቀን ነው፡፡ ነገሩም ሁሉ በዚያ ቀን ለአላህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,1,'83:1','ለሰላቢዎች ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,2,'83:2','ለእነዚያ ከሰዎች ባስሰፈሩ ጊዜ የሚያስሞሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,3,'83:3','ለእነርሱም (ለሰዎቹ) በሰፈሩ ወይም በመዘኑላቸው ጊዜ የሚያጎድሉ (ለኾኑት)፡፡');
INSERT INTO "sadiq_sani" VALUES(83,4,'83:4','እነዚያ እነርሱ ተቀስቃሾች መኾናቸውን አያውቁምን?');
INSERT INTO "sadiq_sani" VALUES(83,5,'83:5','በታላቁ ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(83,6,'83:6','ሰዎች ሁሉ ለዓለማት ጌታ (ፍርድ)፤ በሚቆሙበት ቀን (ተቀስቃሾች መኾናቸውን)፡፡');
INSERT INTO "sadiq_sani" VALUES(83,7,'83:7','በእውነት የከሓዲዎቹ (የሰላቢዎቹ) መጽሐፍ በእርግጥ በሲጂን ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,8,'83:8','ሲጂንም ምን እንደ ኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(83,9,'83:9','የታተመ መጽሐፍ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,10,'83:10','ለአስተባባዮች በዚያ ቀን ወዮላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,11,'83:11','ለእነዚያ በፍርዱ ቀን ለሚያስተባብሉት፡፡');
INSERT INTO "sadiq_sani" VALUES(83,12,'83:12','በእርሱም ወሰን አላፊ ኀጢአተኛ ሁሉ እንጅ ሌላ አያስተባብልም፡፡');
INSERT INTO "sadiq_sani" VALUES(83,13,'83:13','አንቀጾቻችን በእርሱ ላይ በተነበቡ ጊዜ «የመጀመሪያዎቹ ሰዎች ተረቶች ናቸው» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(83,14,'83:14','ይከልከል፤ ይልቁንም በልቦቻቸው ላይ ይሠሩት የነበሩት (ኀጢአት) ደገደገባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,15,'83:15','ይከልከሉ፤ እነርሱ በዚያ ቀን ከጌታቸው (ማየት) ተጋራጆች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,16,'83:16','ከዚያም እነርሱ በእርግጥ ገሀነምን ገቢዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,17,'83:17','ከዚያም «ይህ ያ በእርሱ ታስተባብሉበት የነበራችሁት ነው» ይባላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,18,'83:18','በእውነቱ የእውነተኞቹ ምእምናን መጽሐፍ በዒሊዮን ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,19,'83:19','ዒሊዮንም ምን እንደ ኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(83,20,'83:20','የታተመ መጽሐፍ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,21,'83:21','ባለሟልዎቹ ይጣዱታል፡፡');
INSERT INTO "sadiq_sani" VALUES(83,22,'83:22','እውነተኞቹ ምእምናን በእርግጥ በድሎት (ገነት) ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,23,'83:23','በባለ አጎበር አልጋዎች ላይ ኾነው ይመለከታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,24,'83:24','በፊቶቻቸው ላይ የድሎትን ውበት ታውቃለህ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,25,'83:25','ተጣርቶ ከታተመ ጠጅ ይጠጣሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,26,'83:26','ማተሚያው ምስክ ከኾነ፤ በዚህም ተሽቀዳዳሚዎች ይሽቀዳደሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,27,'83:27','መበረዣውም ከተስኒም ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(83,28,'83:28','ባለሟሎቹ ከእርሷ የሚጠጡላት ምንጭ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(83,29,'83:29','እነዚያ ያምመጹት በእነዚያ ባመኑት በእርግጥ ይስቁ ነበሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,30,'83:30','በእነርሱም ላይ ባለፉ ጊዜ ይጠቃቀሱ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(83,31,'83:31','ወደ ቤተሰቦቻቸውም በተመለሱ ጊዜ (በመሳለቃቸው) ተደሳቾች ኾነው ይመለሱ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(83,32,'83:32','ባዩዋቸውም ጊዜ «እነዚህ በእርግጥ ተሳሳቾች ናቸው» ይሉ ነበር፡፡');
INSERT INTO "sadiq_sani" VALUES(83,33,'83:33','በነርሱ ላይ ተጠባባቂዎች ተደርገው ያልተላኩ ሲኾኑ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,34,'83:34','ዛሬ (በትንሣኤ ቀን) እነዚያ ያመኑት በከሓዲዎቹ ይስቃሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(83,35,'83:35','በባለ አጎበር አልጋዎች ላይ ኾነው የሚመለከቱ ሲኾኑ (ይስቃሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(83,36,'83:36','ከሓዲዎች ይሠሩት የነበሩትን (ዋጋ) ተመነዱን? (ይላሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(84,1,'84:1','ሰማይ በተቀደደች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(84,2,'84:2','ለጌታዋም (ትዕዛዝ) በሰማች ጊዜ፤ (ልትሰማ) ተገባትም፤');
INSERT INTO "sadiq_sani" VALUES(84,3,'84:3','ምድርም በተለጠጠች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(84,4,'84:4','በውስጧ ያለውንም ሁሉ በጣለችና ባዶ በኾነች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(84,5,'84:5','ለጌታዋም በሰማች ጊዜ፤ (መስማት) ተገባትም፤ (ሰው ሁሉ ሥራውን ይገናኛል)፡፡');
INSERT INTO "sadiq_sani" VALUES(84,6,'84:6','አንተ ሰው ሆይ! አንተ ጌታህን (በሞት) እስከምትገናኝ ድረስ ልፋትን ለፊ ነህ፤ ተገናኚውም ነህ፡፡');
INSERT INTO "sadiq_sani" VALUES(84,7,'84:7','መጽሐፉን በቀኙ የተሰጠ ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(84,8,'84:8','በእርግጥ ቀላልን ምርመራ ይመረመራል፡፡');
INSERT INTO "sadiq_sani" VALUES(84,9,'84:9','ወደ ቤተሰቦቹም ተደሳች ኾኖ ይመለሳል፡፡');
INSERT INTO "sadiq_sani" VALUES(84,10,'84:10','መጽሐፉን በጀርባው ኋላ የተሰጠ ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(84,11,'84:11','(ዋ! ጥፋቴ በማለት) ጥፋትን በእርግጥ ይጠራል፡፡');
INSERT INTO "sadiq_sani" VALUES(84,12,'84:12','የተጋጋመች እሳትንም ይገባል፡፡');
INSERT INTO "sadiq_sani" VALUES(84,13,'84:13','እርሱ በቤተሰቡ ውስጥ ተደሳች ነበርና፡፡');
INSERT INTO "sadiq_sani" VALUES(84,14,'84:14','እርሱ (ወደ አላህ) የማይመለስ መኾኑን አስቧልና፡፡');
INSERT INTO "sadiq_sani" VALUES(84,15,'84:15','አይደለም (ይመለሳል)፡፡ ጌታው በእርሱ (መመለስ) ዐዋቂ ነበር፤ (ነውም)፡፡');
INSERT INTO "sadiq_sani" VALUES(84,16,'84:16','አትካዱ፤ በወጋገኑ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(84,17,'84:17','በሌሊቱም በሰበሰውም ሁሉ፤');
INSERT INTO "sadiq_sani" VALUES(84,18,'84:18','በጨረቃውም በሞላ ጊዜ (እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(84,19,'84:19','ከኹነታ በኋላ ወደ ሌላ ኹነታ ትለዋወጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(84,20,'84:20','የማያምኑት ለእነርሱ ምን አላቸው?');
INSERT INTO "sadiq_sani" VALUES(84,21,'84:21','በእነርሱም ላይ ቁርኣን በተነበበ ጊዜ የማይሰግዱት፤ (ምንአላቸው?)');
INSERT INTO "sadiq_sani" VALUES(84,22,'84:22','በእርግጡ እነዚያ የካዱት (በትንሣኤ) ያስተባብላሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(84,23,'84:23','አላህም (በልቦቻቸው) የሚቆጥሩትን ዐዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(84,24,'84:24','በአሳማሚ ቅጣትም አብስራቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(84,25,'84:25','ግን እነዚያ ያመኑና መልካሞችን የሠሩ ለእነርሱ የማይቆረጥ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,1,'85:1','የቡርጆች ባለቤት በኾነችው ሰማይ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(85,2,'85:2','በተቀጠረው ቀንም፤');
INSERT INTO "sadiq_sani" VALUES(85,3,'85:3','በተጣጅና በሚጣዱትም፤ (እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(85,4,'85:4','የጉድቡ ባለቤቶች ተረገሙ፡፡');
INSERT INTO "sadiq_sani" VALUES(85,5,'85:5','የማገዶ ባለቤት የኾነቺው እሳት (ባለቤቶች)፡፡');
INSERT INTO "sadiq_sani" VALUES(85,6,'85:6','እነርሱ በርሷ (አፋፍ) ላይ ተቀማጮች በኾኑ ጊዜ፤ (ተረገሙ)፡፡');
INSERT INTO "sadiq_sani" VALUES(85,7,'85:7','እነርሱም በምእምናኖቹ በሚሠሩት (ማሰቃየት) ላይ መስካሪዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,8,'85:8','ከእነርሱም በአላህ አሸናፊው፣ ምስጉኑ በኾነው (ጌታ) ማመናቸውን እንጅ ሌላ ምንም አልጠሉም፡፡');
INSERT INTO "sadiq_sani" VALUES(85,9,'85:9','በእዚያ የሰማያትና የምድር ንግሥና ለእርሱ ብቻ በኾነው (አላህ ማመናቸውን)፡፡ አላህም በነገሩ ሁሉ ላይ መስካሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,10,'85:10','እነዚያ ምእምንንና ምእምናትን ያሰቃዩ፣ ከዚያም ያልተጸጸቱ ለእነርሱ የገሀነም ቅጣት አልላቸው፡፡ ለእነርሱም የመቃጠሉ ስቃይ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,11,'85:11','እነዚያ ያመኑና መልካም ሥራዎችንም የሠሩ ለእነርሱ ከሥሮቻቸው ወንዞች የሚፈሱባቸው ገነቶች አሏቸው፡፡ ይህ ታላቅ ማግኘት ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,12,'85:12','የጌታህ በኀይል መያዝ ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,13,'85:13','እነሆ እርሱ መፍጠርን ይጀምራል፤ ይመልሳልም፡፡');
INSERT INTO "sadiq_sani" VALUES(85,14,'85:14','እርሱም ምሕረተ ብዙ ወዳድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,15,'85:15','የዙፋኑ ባለቤት የላቀው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,16,'85:16','የሚሻውን ሁሉ ሠሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,17,'85:17','የሰራዊቶቹ ወሬ መጣልህን?');
INSERT INTO "sadiq_sani" VALUES(85,18,'85:18','የፈርዖንና የሰሙድ፤ (ወሬ)፡፡');
INSERT INTO "sadiq_sani" VALUES(85,19,'85:19','በእውነቱ እነዚያ የካዱት በማስተባበል ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,20,'85:20','አላህም በዙሪያቸው (በዕውቀቱ) ከባቢ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,21,'85:21','ይልቁንም እርሱ የከበረ ቁርኣን ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(85,22,'85:22','የተጠበቀ በኾነ ሰሌዳ ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(86,1,'86:1','በሰማዩ በሌሊት መጪውም እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(86,2,'86:2','የሌሊት መጪውም ምን እንደ ኾነ ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(86,3,'86:3','ጨለማን ቀዳጁ ኮከብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(86,4,'86:4','ነፍስ ሁሉ በእርሷ ላይ ጠባቂ ያለባት እንጅ ሌላ አይደለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(86,5,'86:5','ሰውም ከምን እንደተፈጠረ ይመልከት፡፡');
INSERT INTO "sadiq_sani" VALUES(86,6,'86:6','ከተስፈንጣሪ ውሃ ተፈጠረ፡፡');
INSERT INTO "sadiq_sani" VALUES(86,7,'86:7','ከጀርባና ከእርግብግብቶች መካከል የሚወጣ ከኾነ (ውሃ)፡፡');
INSERT INTO "sadiq_sani" VALUES(86,8,'86:8','እርሱ (አላህ) በመመለሱ ላይ በእርግጥ ቻይ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(86,9,'86:9','ምስጢሮች በሚገለጡበት ቀን፡፡');
INSERT INTO "sadiq_sani" VALUES(86,10,'86:10','ለእርሱም (ለሰው) ኀይልና ረዳት ምንም የለውም፡፡');
INSERT INTO "sadiq_sani" VALUES(86,11,'86:11','የመመለስ ባለቤት በኾነችው ሰማይም እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(86,12,'86:12','(በበቃይ) የመሰንጠቅ ባለቤት በኾነችው ምድርም፤');
INSERT INTO "sadiq_sani" VALUES(86,13,'86:13','እርሱ (እውነትን ከውሸት) የሚለይ ቃል ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(86,14,'86:14','እርሱም ቀልድ አይደለም፡፡');
INSERT INTO "sadiq_sani" VALUES(86,15,'86:15','እነርሱ በእርግጥ ተንኮልን ይሠራሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(86,16,'86:16','(እኔ) ተንኮልንም እመልሳለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(86,17,'86:17','ከሓዲዎችንም ቀን ስጣቸው፡፡ ጥቂትን ጊዜ አቆያቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(87,1,'87:1','ከሁሉ በላይ የኾነውን ጌታህን ስም አሞግሥ፡፡');
INSERT INTO "sadiq_sani" VALUES(87,2,'87:2','የዚያን (ሁሉን ነገር) የፈጠረውን ያስተካከለውንም፡፡');
INSERT INTO "sadiq_sani" VALUES(87,3,'87:3','የዚያንም የወሰነውን፡፡ (ለተፈጠረለት ነገር) የመራውንም፡፡');
INSERT INTO "sadiq_sani" VALUES(87,4,'87:4','የዚያንም ግጦሽን (ለምለም አድርጎ) ያወጣውን፡፡');
INSERT INTO "sadiq_sani" VALUES(87,5,'87:5','(ከዚያ) ደረቅ ጥቁር ያደረገውንም (አምላክ ስም አሞግስ)፡፡');
INSERT INTO "sadiq_sani" VALUES(87,6,'87:6','(ቁርኣንን) በእርግጥ እናስነብብሃለን፤ አትረሳምም፡፡');
INSERT INTO "sadiq_sani" VALUES(87,7,'87:7','አላህ ከሻው ነገር በስተቀር፡፡ እርሱ ግልጹን የሚሸሸገውንም ሁሉ ያውቃልና፡፡');
INSERT INTO "sadiq_sani" VALUES(87,8,'87:8','ለገሪቱም (ሕግጋት) እንገጥምሃለን፡፡');
INSERT INTO "sadiq_sani" VALUES(87,9,'87:9','ግሣጼይቱ ብትጠቅም (ሰዎችን) ገሥጽም፡፡');
INSERT INTO "sadiq_sani" VALUES(87,10,'87:10','(አላህን) የሚፈራ ሰው በእርግጥ ይገሠጻል፡፡');
INSERT INTO "sadiq_sani" VALUES(87,11,'87:11','መናጢውም ይርቃታል፡፡');
INSERT INTO "sadiq_sani" VALUES(87,12,'87:12','ያ ታላቂቱን እሳት የሚገባው፡፡');
INSERT INTO "sadiq_sani" VALUES(87,13,'87:13','ከዚያም በእርሷ ውስጥ አይሞትም ሕያውም አይኾንም፡፡');
INSERT INTO "sadiq_sani" VALUES(87,14,'87:14','የተጥራራ ሰው በእርግጥ ዳነ፡፡');
INSERT INTO "sadiq_sani" VALUES(87,15,'87:15','የጌታውንም ስም ያወሳና የሰገደ፡፡');
INSERT INTO "sadiq_sani" VALUES(87,16,'87:16','ይልቁንም ቅርቢቱን ሕይወት ትመርጣላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(87,17,'87:17','መጨረሻይቱ (ሕይወት) በላጭ ሁል ጊዜ ዘውታሪም ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(87,18,'87:18','ይህ በፊተኞቹ መጻሕፍት ውስጥ ያልለ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(87,19,'87:19','በኢብራሂምና በሙሳ ጽሑፎች ውስጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(88,1,'88:1','የሸፋኝቱ (ትንሣኤ) ወሬ መጣህን?');
INSERT INTO "sadiq_sani" VALUES(88,2,'88:2','ፊቶች በዚያ ቀን ተዋራጆች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,3,'88:3','ሠሪዎች ለፊዎች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,4,'88:4','ተኳሳን እሳት ይገባሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(88,5,'88:5','በጣም ከፈላች ምንጭ ይጋታሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(88,6,'88:6','ለእነርሱ ዶሪዕ ከሚባል (እሾሃም) ዛፍ እንጅ ሌላ ምግብ የላቸውም፡፡');
INSERT INTO "sadiq_sani" VALUES(88,7,'88:7','የማያሰባ ከረኃብም የማያብቃቃ ከኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,8,'88:8','ፊቶች በዚያ ቀን ተቀማጣዮች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,9,'88:9','ለሥራቸው ተደሳቾች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,10,'88:10','በከፍተኛ ገነት ውስጥ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,11,'88:11','በውስጧ ውድቅን ነገር አይሰሙም፡፡');
INSERT INTO "sadiq_sani" VALUES(88,12,'88:12','በውስጧ ፈሳሾች ምንጮች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(88,13,'88:13','በውስጧ ከፍ የተደረጉ አልጋዎች አልሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(88,14,'88:14','በተርታ የተኖሩ ብርጭቆዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(88,15,'88:15','የተደረደሩ መከዳዎችም፡፡');
INSERT INTO "sadiq_sani" VALUES(88,16,'88:16','የተነጠፉ ስጋጃዎችም (አልሉ)፡፡');
INSERT INTO "sadiq_sani" VALUES(88,17,'88:17','(ከሓዲዎች) አይመለከቱምን? ወደ ግመል እንዴት እነደተፈጠረች!');
INSERT INTO "sadiq_sani" VALUES(88,18,'88:18','ወደ ሰማይም እንዴት ከፍ እንደ ተደረገች!');
INSERT INTO "sadiq_sani" VALUES(88,19,'88:19','ወደ ተራራዎችም እንዴት እንደ ተቸከሉ!');
INSERT INTO "sadiq_sani" VALUES(88,20,'88:20','ወደ ምድርም እንዴ እንደተዘረጋች (አይመለከቱምን?)');
INSERT INTO "sadiq_sani" VALUES(88,21,'88:21','አስታውስም፤ አንተ አስታዋሽ ብቻ ነህና፡፡');
INSERT INTO "sadiq_sani" VALUES(88,22,'88:22','በእነርሱ ላይ ተሿሚ (አስገዳጅ) አይደለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(88,23,'88:23','ግን (ከእውነት) የዞረና የካደ ሰው፤');
INSERT INTO "sadiq_sani" VALUES(88,24,'88:24','አላህ ታላቁን ቅጣት ይቀጣዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(88,25,'88:25','መመለሻቸው ወደእኛ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(88,26,'88:26','ከዚያም ምርመራቸው በእኛ ላይ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(89,1,'89:1','በጎህ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,2,'89:2','በዐሥር ሌሊቶችም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,3,'89:3','በጥንዱም በነጠላውም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,4,'89:4','በሌሊቱም በሚኼድ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,5,'89:5','በዚህ (መሓላ) ለባለ አእምሮ ታላቅ መሓላ አለበትን?');
INSERT INTO "sadiq_sani" VALUES(89,6,'89:6','ጌታህ በዓድ እንዴት እንደ ሠራ አታውቅምን?');
INSERT INTO "sadiq_sani" VALUES(89,7,'89:7','በኢረም በባለ ረዣዢሚቱ አዕማድ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,8,'89:8','በዚያች ብጤዋ በአገሮች ውስጥ ያልተፈጠረ በኾነችው፡፡');
INSERT INTO "sadiq_sani" VALUES(89,9,'89:9','በሰሙድም በእነዚያ በሸለቆው ቋጥኝን የቆረጡ በኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(89,10,'89:10','በፈርዖንም ባለ ችካሎች በኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(89,11,'89:11','በእነዚያ በአገሮች ላይ ግፍ የሠሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,12,'89:12','በእርሷም ውስጥ ጥፋትን ያበዙ በኾኑት (እንዴት እንደሠራ አታውቅምን?)');
INSERT INTO "sadiq_sani" VALUES(89,13,'89:13','በእነርሱ ላይም ጌታህ የቅጣትን አለንጋ አወረደባቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(89,14,'89:14','ጌታህ በመጠባበቂያ ላይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(89,15,'89:15','ሰውማ ጌታው በሞከረው ጊዜ፣ ባከበረውና ባጣቀመውም (ጊዜ) «ጌታዬ አከበረኝ (አበለጠኝ)» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(89,16,'89:16','በሞከረውና በእርሱ ላይ ሲሳዩን ባጠበበ ጊዜማ «ጌታዬ አሳነሰኝ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(89,17,'89:17','ይከልከል፤ ይልቁንም የቲምን አታከብሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,18,'89:18','ድኻንም በማብላት ላይ አትታዘዙም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,19,'89:19','የውርስንም ገንዘብ የመሰብሰብን አበላል ትበላላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,20,'89:20','ገንዘብንም ብዙን መውደድ ትወዳላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,21,'89:21','ተዉ፤ ምድር ደጋግማ በተሰባበረች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(89,22,'89:22','መላእክትም ሰልፍ ሰልፍ ኾነው ጌታህ (ለሱ ክብር በምስማማ መልኩ) በመጣ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(89,23,'89:23','ገሀነምም በዚያ ቀን በተመጣች ጊዜ በዚያ ቀን ሰው (ጥፋቱን) ይገነዘባል፡፡ መገንዘብም ለእርሱ ከየቱ?');
INSERT INTO "sadiq_sani" VALUES(89,24,'89:24','«ዋ እኔ! ምነው በሕይወቴ (መልካምን ሥራ) ባስቀደምኩ ኖሮ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(89,25,'89:25','በዚያ ቀንም የርሱን አቀጣጥ አንድም አይቀጣም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,26,'89:26','የእርሱንም አስተሳሰር አንድም አያስርም፡፡');
INSERT INTO "sadiq_sani" VALUES(89,27,'89:27','(ለአመነች ነፍስም) «አንቺ የረካሺው ነፍስ ሆይ!');
INSERT INTO "sadiq_sani" VALUES(89,28,'89:28','«ወዳጅ ተወዳጅ ኾነሽ ወደ ጌታሽ ተመለሺ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,29,'89:29','«በባሮቼም ውስጥ ግቢ፡፡');
INSERT INTO "sadiq_sani" VALUES(89,30,'89:30','ገነቴንም ግቢ፤» (ትባላለች)፡፡');
INSERT INTO "sadiq_sani" VALUES(90,1,'90:1','በዚህ አገር (በመካ) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(90,2,'90:2','አንተ በዚህ አገር ሰፋሪ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(90,3,'90:3','በወላጂም በወለዳቸውም ሰዎች (በአዳምና በዘሮቹ ሁሉ እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(90,4,'90:4','ሰውን ሁሉ በእርግጥ በልፋት ውስጥ ኾኖ ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(90,5,'90:5','በእርሱ ላይ አንድም የማይችል መኾኑን ይጠረጥራልን?');
INSERT INTO "sadiq_sani" VALUES(90,6,'90:6','«ብዙን ገንዘብ አጠፋሁ» ይላል፡፡');
INSERT INTO "sadiq_sani" VALUES(90,7,'90:7','አንድም ያላየው መኾኑን ያስባልን?');
INSERT INTO "sadiq_sani" VALUES(90,8,'90:8','ለእርሱ ሁለት ዓይኖችን አላደረግንለትምን?');
INSERT INTO "sadiq_sani" VALUES(90,9,'90:9','ምላስንና ሁለት ከንፈሮችንም፡፡');
INSERT INTO "sadiq_sani" VALUES(90,10,'90:10','ሁለትን መንገዶችም አልመራነውምን?');
INSERT INTO "sadiq_sani" VALUES(90,11,'90:11','ዓቀበቲቱንም አልወጣም፡፡');
INSERT INTO "sadiq_sani" VALUES(90,12,'90:12','ዓቀበቲቱም (መውጣቷ) ምን እንደኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(90,13,'90:13','(እርሱ) ጫንቃን መልቀቅ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(90,14,'90:14','ወይም የረኃብ ባለቤት በኾነ ቀን ማብላት፡፡');
INSERT INTO "sadiq_sani" VALUES(90,15,'90:15','የዝምድና ባለቤት የኾነን የቲም፤');
INSERT INTO "sadiq_sani" VALUES(90,16,'90:16','ወይም የዐፈር ባለቤት የኾነን ድኻ (ማብላት ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(90,17,'90:17','(ዐቀበቲቱን አልወጣም)፡፡ ከዚያም ከነዚያ ከአመኑትና በመታገስ አደራ ከተባባሉት፣ በማዘንም አደራ ከተባባሉት ሰዎች አልኾነም፡፡');
INSERT INTO "sadiq_sani" VALUES(90,18,'90:18','እነዚያ (ይህንን የፈጸሙ) የቀኝ ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(90,19,'90:19','እነዚያም በአንቀጾቻችን የካዱት እነርሱ የግራ ጓዶች ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(90,20,'90:20','በእነርሱ ላይ የተዘጋች እሳት አልለች፡፡');
INSERT INTO "sadiq_sani" VALUES(91,1,'91:1','በፀሐይና በብርሃኗ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(91,2,'91:2','በጨረቃም በተከተላት ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(91,3,'91:3','በቀኑም (ፀሐይን) በገለጻት ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(91,4,'91:4','በሌሊቱም በሸፈናት ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(91,5,'91:5','በሰማይቱም በገነባትም (ጌታ)፤');
INSERT INTO "sadiq_sani" VALUES(91,6,'91:6','በምድሪቱም በዘረጋትም፤');
INSERT INTO "sadiq_sani" VALUES(91,7,'91:7','በነፍስም ባስተካከላትም፤');
INSERT INTO "sadiq_sani" VALUES(91,8,'91:8','አመጽዋንና ፍርሃትዋንም ባሳወቃት (አምላክ እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(91,9,'91:9','(ከኀጢኣት) ያጠራት ሰው ፍላጎቱን በእርግጥ አገኘ፡፡');
INSERT INTO "sadiq_sani" VALUES(91,10,'91:10','(በኀጢኣት) የሸፈናትም ሰው በእውነት አፈረ፡፡');
INSERT INTO "sadiq_sani" VALUES(91,11,'91:11','ሠሙድ ወሰን በማለፍዋ አስተባበለች፡፡');
INSERT INTO "sadiq_sani" VALUES(91,12,'91:12','ጠማማዋ በተንቀሳቀሰ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(91,13,'91:13','ለእነርሱም የአላህ መልክተኛ (ሷሊህ) «የአላህን ግመል የመጠጥ ተራዋንም (ተጠንቀቁ)» አላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(91,14,'91:14','አስተባበሉትም፡፡ (ቋንጃዋን) ወጓትም፡፡ በኀጢኣታቸውም ምክንያት ጌታቸው (ቅጣትን) በእነርሱ ላይ ደመደመባቸው፤ አስተካከላትም፡፡');
INSERT INTO "sadiq_sani" VALUES(91,15,'91:15','ፍጻሜዋንም (የምታስከትለውን) አያፈራም፡፡');
INSERT INTO "sadiq_sani" VALUES(92,1,'92:1','በሌሊቱ እምላለሁ (በጨለማው) በሚሸፍን ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(92,2,'92:2','በቀኑም፤ በተገለጸ ጊዜ፡፡');
INSERT INTO "sadiq_sani" VALUES(92,3,'92:3','ወንድንና ሴትን በፈጠረውም (አምላክ እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(92,4,'92:4','ሥራችሁ በእርግጥ የተለያየ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(92,5,'92:5','የሰጠ ሰውማ ጌታውን የፈራም፡፡');
INSERT INTO "sadiq_sani" VALUES(92,6,'92:6','በመልካሚቱም (እምነት) ያረጋገጠ፤');
INSERT INTO "sadiq_sani" VALUES(92,7,'92:7','ለገሪቱ ሥራ እናዘጋጀዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(92,8,'92:8','የሰሰተ ሰውማ የተብቃቃም፤ (በራሱ የተመካ)፤');
INSERT INTO "sadiq_sani" VALUES(92,9,'92:9','በመልካሚቱ (እምነት) ያሰተባበለም፤');
INSERT INTO "sadiq_sani" VALUES(92,10,'92:10','ለክፉይቱ ሥራ እናዘጋጀዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(92,11,'92:11','በወደቀም ጊዜ ገንዘቡ ከእርሱ ምንም አይጠቅመውም፡፡');
INSERT INTO "sadiq_sani" VALUES(92,12,'92:12','ቅኑን መንገድ መግለጽ በእኛ ላይ አለብን፡፡');
INSERT INTO "sadiq_sani" VALUES(92,13,'92:13','መጨረሻይቱም መጀመሪያይቱም ዓለም የእኛ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(92,14,'92:14','የምትንቀለቀልንም እሳት አስጠነቀቅኳችሁ (በላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(92,15,'92:15','ከጠማማ በቀር ሌላ የማይገባት የኾነችን፡፡');
INSERT INTO "sadiq_sani" VALUES(92,16,'92:16','ያ ያስተባበለና ከትዕዛዝ የሸሸው፡፡');
INSERT INTO "sadiq_sani" VALUES(92,17,'92:17','አላህን በጣም ፈሪውም በእርግጥ ይርቃታል፡፡');
INSERT INTO "sadiq_sani" VALUES(92,18,'92:18','ያ የሚጥራራ ኾኖ ገንዘቡን (ለድኾች) የሚሰጠው፡፡');
INSERT INTO "sadiq_sani" VALUES(92,19,'92:19','ለአንድም ሰው እርሱ ዘንድ የምትመለስ ውለታ የለችም፡፡');
INSERT INTO "sadiq_sani" VALUES(92,20,'92:20','ግን የታላቅ ጌታውን ውዴታ ለመፈለግ (ይህንን ሠራ)፡፡');
INSERT INTO "sadiq_sani" VALUES(92,21,'92:21','ወደፊትም በእርግጥ ይደሰታል፡፡');
INSERT INTO "sadiq_sani" VALUES(93,1,'93:1','በረፋዱ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(93,2,'93:2','በሌሊቱም ጸጥ ባለ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(93,3,'93:3','ጌታህ አላሰናበተህም፤ አልጠላህምም፡፡');
INSERT INTO "sadiq_sani" VALUES(93,4,'93:4','መጨረሻይቱም (ዓለም) ከመጀመሪያይቱ ይልቅ ላንተ በላጭ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(93,5,'93:5','ጌታህም ወደ ፊት (ብዙን ስጦታ) በእርግጥ ይሰጥሃል፡፡ ትደሰታለህም፡፡');
INSERT INTO "sadiq_sani" VALUES(93,6,'93:6','የቲም ኾነህ አላገኘህምና አላስጠጋህምን? (አስጠግቶሃል)፡፡');
INSERT INTO "sadiq_sani" VALUES(93,7,'93:7','የሳትክም ኾነህ አገኘህ መራህም፡፡');
INSERT INTO "sadiq_sani" VALUES(93,8,'93:8','ድኻም ኾነህ አገኘህ፤ አከበረህም፡፡');
INSERT INTO "sadiq_sani" VALUES(93,9,'93:9','የቲምንማ አትጨቁን፡፡');
INSERT INTO "sadiq_sani" VALUES(93,10,'93:10','ለማኝንም አትገላምጥ፡፡');
INSERT INTO "sadiq_sani" VALUES(93,11,'93:11','በጌታህም ጸጋ አውራ፤ (ግለጻት)፡፡');
INSERT INTO "sadiq_sani" VALUES(94,1,'94:1','ልብህን ለአንተ አላሰፋንልህምን? (አስፍተንልሃል)፡፡');
INSERT INTO "sadiq_sani" VALUES(94,2,'94:2','ሸክምህንም ካንተ አውርደንልሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(94,3,'94:3','ያንን ጀርባህን ያከበደውን (ሸክም)፡፡');
INSERT INTO "sadiq_sani" VALUES(94,4,'94:4','መወሳትህንም ላንተ ከፍ አድርገንልሃል፡፡');
INSERT INTO "sadiq_sani" VALUES(94,5,'94:5','ከችግርም ጋር ምቾት አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(94,6,'94:6','ከችግር ጋር በእርግጥ ምቾት አልለ፡፡');
INSERT INTO "sadiq_sani" VALUES(94,7,'94:7','በጨረስክም ጊዜ ልፋ፡፡ (ቀጥል)፡፡');
INSERT INTO "sadiq_sani" VALUES(94,8,'94:8','ወደ ጌታህም ብቻ ከጅል፡፡');
INSERT INTO "sadiq_sani" VALUES(95,1,'95:1','በበለስና በዘይት ወይራ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(95,2,'95:2','በሲኒን ተራራም፤');
INSERT INTO "sadiq_sani" VALUES(95,3,'95:3','በዚህ በጸጥተኛው አገርም (እምላለሁ)፡፡');
INSERT INTO "sadiq_sani" VALUES(95,4,'95:4','ሰውን በጣም በአማረ አቋም ላይ ፈጠርነው፡፡');
INSERT INTO "sadiq_sani" VALUES(95,5,'95:5','ከዚያም (ከፊሉን) ከዝቅተኞች ሁሉ በታች አድርገን መለስነው፡፡');
INSERT INTO "sadiq_sani" VALUES(95,6,'95:6','ግን እነዚያ ያመኑት መልካሞችንም የሠሩት ለእነርሱ ተቆራጭ ያልኾነ ምንዳ አልላቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(95,7,'95:7','ታዲያ ከዚህ በኋላ በፍርዱ ምን አስተባባይ አደረገህ?');
INSERT INTO "sadiq_sani" VALUES(95,8,'95:8','አላህ ከፈራጆች ሁሉ ይበልጥ ፈራጅ አይደለምን? (ነው)፡፡');
INSERT INTO "sadiq_sani" VALUES(96,1,'96:1','አንብብ በዚያ (ሁሉን) በፈጠረው ጌታህ ስም፡፡');
INSERT INTO "sadiq_sani" VALUES(96,2,'96:2','ሰውን ከረጋ ደም በፈጠረው (ጌታህ ስም)፡፡');
INSERT INTO "sadiq_sani" VALUES(96,3,'96:3','አንብብ፤ ጌታህ በጣም ቸር ሲኾን፤');
INSERT INTO "sadiq_sani" VALUES(96,4,'96:4','ያ በብርዕ ያስተማረ፡፡');
INSERT INTO "sadiq_sani" VALUES(96,5,'96:5','ሰውን ያላወቀውን ሁሉ ያሳወቀ ሲኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(96,6,'96:6','በእውነቱ ሰው ወሰንን ያልፋል፡፡');
INSERT INTO "sadiq_sani" VALUES(96,7,'96:7','ራሱን የተብቃቃ (ባለ ሀብት) ኾኖ ለማየቱ፡፡');
INSERT INTO "sadiq_sani" VALUES(96,8,'96:8','መመለሻው ወደ ጌታህ ብቻ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(96,9,'96:9','አየህን? ያንን የሚከለክለውን፡፡');
INSERT INTO "sadiq_sani" VALUES(96,10,'96:10','ባሪያን በሰገደ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(96,11,'96:11','አየህን? ንገረኝ (ተከልካዩ) በትክክለኛ መንገድ ላይ ቢኾን፤');
INSERT INTO "sadiq_sani" VALUES(96,12,'96:12','ወይም (ፈጣሪውን) በመፍራት ቢያዝ፤');
INSERT INTO "sadiq_sani" VALUES(96,13,'96:13','አየህን? ንገረኝ (ከልካዩ) ቢያስተባብልና (ከእምነት) ቢሸሽ፤');
INSERT INTO "sadiq_sani" VALUES(96,14,'96:14','አላህ የሚያይ መኾኑን አያውቅምን?');
INSERT INTO "sadiq_sani" VALUES(96,15,'96:15','ይተው ባይከለከል አናቱን ይዘን እንጎትተዋለን፡፡');
INSERT INTO "sadiq_sani" VALUES(96,16,'96:16','ውሸታም ስሕተተኛ የኾነችውን አናቱን፡፡');
INSERT INTO "sadiq_sani" VALUES(96,17,'96:17','ሸንጎውንም ይጥራ፡፡');
INSERT INTO "sadiq_sani" VALUES(96,18,'96:18','(እኛም) ዘበኞቻችንን እንጠራለን፡፡');
INSERT INTO "sadiq_sani" VALUES(96,19,'96:19','ይከልከል አትታዘዘው፡፡ ስገድም፤ (ወደ አላህ) ተቃረብም፡፡');
INSERT INTO "sadiq_sani" VALUES(97,1,'97:1','እኛ (ቁርኣኑን) በመወሰኛይቱ ሌሊት አወረድነው፡፡');
INSERT INTO "sadiq_sani" VALUES(97,2,'97:2','መወሰኛይቱም ሌሊተ ምን እንደ ኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(97,3,'97:3','መወሰኛይቱ ሌሊት ከሺሕ ወር በላጭ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(97,4,'97:4','በርሷ ውስጥ መላእክትና መንፈሱ በጌታቸው ፈቃድ በነገሩ ሁሉ ይወርዳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(97,5,'97:5','እርስዋ እስከ ጎህ መውጣት ድረስ ሰላም ብቻ ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(98,1,'98:1','እነዚያ ከመጽሐፉ ባለቤቶች ከአጋሪዎቹም የካዱት ግልጹ አስረጅ እስከ መጣላቸው ድረስ (ከነበሩበት ላይ) ተወጋጆች አልነበሩም፡፡');
INSERT INTO "sadiq_sani" VALUES(98,2,'98:2','(አስረጁም) ከአላህ የኾነ መልክተኛ የተጥራሩን መጽሐፎች የሚያነብ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(98,3,'98:3','በውስጧ ቀጥተኛ የኾኑ ጽሑፎች ያሉባት የኾነችን፡፡');
INSERT INTO "sadiq_sani" VALUES(98,4,'98:4','እነዚያም መጽሐፉን የተሰጡት ሰዎች ግልጽ አስረጅ ከመጣላቸው በኋላ እንጅ አልተለያዩም፡፡');
INSERT INTO "sadiq_sani" VALUES(98,5,'98:5','አላህን ሃይማኖትን ለእርሱ ብቻ አጥሪዎች፣ ቀጥተኞች ኾነው ሊግገዙት፣ ሶላትንም አስተካክለው ሊሰግዱ ዘካንም ሊሰጡ እንጅ ያልታዘዙ ሲኾኑ (ተለያዩ)፡፡ ይህም የቀጥተኛይቱ (ሃይማኖት) ድንጋጌ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(98,6,'98:6','እነዚያ ከመጽሐፉ ሰዎች የካዱት፣ አጋሪዎቹም በገሀነም እሳት ውስጥ ናቸው፤ በውስጧ ዘውታሪዎች ሲኾኑ እነዚያ እነርሱ ከፍጥረት ሁሉ ክፉ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(98,7,'98:7','እነዚያ ያመኑትና መልካሞችንም የሠሩት፣ እነዚያ እነርሱ ከፍጥረት ሁሉ በላጭ ናቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(98,8,'98:8','በጌታቸው ዘንድ ምንዳቸው ከሥሮቻቸው ወንዞች የሚፈሱባቸው የመኖሪያ ገነቶች ነው፡፡ በውስጣቸው ዘለዓለም ዘውታሪዎች ሲኾኑ (ይገቡባቸወል)፡፡ አላህ ከእነርሱ ወደደ፡፡ ከእርሱም ወደዱ፡፡ ይህ ጌታውን ለፈራ ሰው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(99,1,'99:1','ምድር (በኀይል) መንቀጥቀጥዋን በተንቀጠቀጠች ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(99,2,'99:2','ምድር ሸክሞችዋን ባወጣችም ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(99,3,'99:3','ሰውም ምን ኾነች? ባለ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(99,4,'99:4','በዚያ ቀን ወሬዎችዋን ትናገራለች፡፡');
INSERT INTO "sadiq_sani" VALUES(99,5,'99:5','ጌታህ ለእርሷ በማሳወቁ ምክንያት፡፡');
INSERT INTO "sadiq_sani" VALUES(99,6,'99:6','በዚያ ቀን ሰዎች ሥራዎቻቸውን (ምንዳዋን) እንዲያዩ ይደረጉ ዘንድ (እንደየሥራቸው) የተለያዩ ኾነው (ከመቆሚያው ስፍራ) ይመለሳሉ፡፡');
INSERT INTO "sadiq_sani" VALUES(99,7,'99:7','የብናኝ ክብደት ያክልም መልካምን የሠራ ሰው ያገኘዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(99,8,'99:8','የብናኝ ክብደት ያክልም ክፉን የሠራ ሰው ያገኘዋል፡፡');
INSERT INTO "sadiq_sani" VALUES(100,1,'100:1','እያለከለኩ ሩዋጮች በኾኑት (ፈረሶች) እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(100,2,'100:2','(በሰኮናቸው) እያጋጩ እሳትን አውጪዎች በኾኑትም፤');
INSERT INTO "sadiq_sani" VALUES(100,3,'100:3','በማለዳ ወራሪዎች በኾኑትም፤');
INSERT INTO "sadiq_sani" VALUES(100,4,'100:4','በእርሱ አቧራን በቀሰቀሱትም፤');
INSERT INTO "sadiq_sani" VALUES(100,5,'100:5','በ(ጠላት) ክቢ መካከል በእርሱ (በጧት) በተጋፈጡትም እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(100,6,'100:6','ሰው ለጌታው ብርቱ ከሓዲ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(100,7,'100:7','እርሱም በዚህ ላይ መስካሪ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(100,8,'100:8','እርሱም ገንዘብን ለመውደድ በጣም ብርቱ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(100,9,'100:9','(ሰው) አያውቅምን? በመቃብሮች ያሉት (ሙታን) በተቀሰቀሱ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(100,10,'100:10','በልቦች ውስጥ ያለውም ሁሉ በተገለጸ ጊዜ (እንዴት እንደሚኾን)፡፡');
INSERT INTO "sadiq_sani" VALUES(100,11,'100:11','ጌታቸው በዚያ ቀን በእነርሱ (ነገር) በእርግጥ ውስጠ አዋቂ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(101,1,'101:1','ቆርቋሪይቱ (ጩኸት)፤');
INSERT INTO "sadiq_sani" VALUES(101,2,'101:2','ምን አስደናቂ ቆርቋሪ ናት!');
INSERT INTO "sadiq_sani" VALUES(101,3,'101:3','ቆርቋሪይቱም ምን እንደኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(101,4,'101:4','ሰዎች እንደ ተበታተነ ቢራቢሮ (ወይም ኩብኩባ) በሚኾኑበት ቀን፤');
INSERT INTO "sadiq_sani" VALUES(101,5,'101:5','ጋራዎችም እንደ ተነደፈ ሱፍ በሚኾኑበት (ቀን ልቦችን በድንጋጤ ትቆረቁራለች፡፡)');
INSERT INTO "sadiq_sani" VALUES(101,6,'101:6','ሚዛኖቹ የከበዱለት ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(101,7,'101:7','እርሱ በምትወደድ ኑሮ ውስጥ ይኾናል፡፡');
INSERT INTO "sadiq_sani" VALUES(101,8,'101:8','ሚዛኖቹም የቀለሉበት ሰውማ፤');
INSERT INTO "sadiq_sani" VALUES(101,9,'101:9','መኖሪያው ሃዊያህ ናት');
INSERT INTO "sadiq_sani" VALUES(101,10,'101:10','እርሷም ምን እንደ ኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(101,11,'101:11','(እርሷ) በጣም ተኳሳ እሳት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(102,1,'102:1','በብዛት መፎካከር (ጌታችሁን ከመገዛት) አዘነጋችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,2,'102:2','መቃብሮችን እስከ ጎበኛችሁ ድረስ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,3,'102:3','ተከልከሉ፤ ወደፊት (ውጤቱን) ታውቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,4,'102:4','ከዚያም ተከልከሉ፤ ወደ ፊት ታውቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,5,'102:5','በእውነቱ (የሚጠብቃችሁን) እርግጠኛ ዕውቀትን ብታውቁ ኖሮ፤ (ባልዘናጋችሁ ነበር)፡፡');
INSERT INTO "sadiq_sani" VALUES(102,6,'102:6','ገሀነምን በእርግጥ ታያላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,7,'102:7','ከዚያም እርግጠኛን ማየት ታዩዋታላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(102,8,'102:8','ከዚያም ከድሎታችሁ ሁሉ በዚያ ቀን ትጠየቃላችሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(103,1,'103:1','በጊዜያቱ እምላለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(103,2,'103:2','ሰው ሁሉ በእርግጥ በከሳራ ውስጥ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(103,3,'103:3','እነዚያ ያመኑትና መልካሞችን የሠሩት፣ በእውነትም አደራ የተባባሉት፣ በመታገስም አደራ የተባባሉት ብቻ ሲቀሩ፡፡');
INSERT INTO "sadiq_sani" VALUES(104,1,'104:1','ለሃሜተኛ ለዘላፊ ሁሉ ወዮለት፡፡');
INSERT INTO "sadiq_sani" VALUES(104,2,'104:2','ለዚያ ገንዘብን የሰበሰበና የቆጣጠረው (ያደለበውም) ለኾነ ፤(ወዮለት)፡፡');
INSERT INTO "sadiq_sani" VALUES(104,3,'104:3','ገንዘቡ የሚያዘወትረው መኾኑን ያስባል፡፡');
INSERT INTO "sadiq_sani" VALUES(104,4,'104:4','ይከልከል፤ በሰባሪይቱ (እሳት) ውስጥ በእርግጥ ይጣላል፡፡');
INSERT INTO "sadiq_sani" VALUES(104,5,'104:5','ሰባሪይቱም ምን እንደ ኾነች ምን አሳወቀህ?');
INSERT INTO "sadiq_sani" VALUES(104,6,'104:6','የተነደደችው የአላህ እሳት ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(104,7,'104:7','ያቺ በልቦች ላይ የምትዘልቅ የኾነችው፡፡');
INSERT INTO "sadiq_sani" VALUES(104,8,'104:8','እርሷ በእርግጥ በእነርሱ ላይ የተዘጋጀች ናት፡፡');
INSERT INTO "sadiq_sani" VALUES(104,9,'104:9','በተዘረጋች አዕማድ ውስጥ (የተዘጋች ናት)፡፡');
INSERT INTO "sadiq_sani" VALUES(105,1,'105:1','በዝሆኑ ባለቤቶች ጌታህ እንዴት እንደሠራ አላወቅህምን?');
INSERT INTO "sadiq_sani" VALUES(105,2,'105:2','ተንኮላቸውን በጥፋት ውስጥ (ከንቱ) አላደረገምን? (አድርጓል)፡፡');
INSERT INTO "sadiq_sani" VALUES(105,3,'105:3','በእነርሱም ላይ መንጎች የኾኑን ዎፎች ላከ፡፡');
INSERT INTO "sadiq_sani" VALUES(105,4,'105:4','ከተጠበሰ ጭቃ በኾነ ድንጋይ የምትወረውርባቸው የኾነችን፤ (አዕዋፍ)፡፡');
INSERT INTO "sadiq_sani" VALUES(105,5,'105:5','ቅጠሉ እንደ ተበላ አዝመራም አደረጋቸው፡፡');
INSERT INTO "sadiq_sani" VALUES(106,1,'106:1','ቁረይሽን ለማላመድ (ባለዝሆኖቹን አጠፋ)፡፡');
INSERT INTO "sadiq_sani" VALUES(106,2,'106:2','የብርድና የበጋን ወራት ጉዞ ሊያላምዳቸው ይህንን ሠራ፡፡');
INSERT INTO "sadiq_sani" VALUES(106,3,'106:3','ስለዚህ የዚህን ቤት (የከዕባን) ጌታ ይግገዙ፡፡');
INSERT INTO "sadiq_sani" VALUES(106,4,'106:4','ያንን ከረኃብ ያበላቸውን ከፍርሃትም ያረካቸውን (ጌታ ያምልኩ)፡፡');
INSERT INTO "sadiq_sani" VALUES(107,1,'107:1','ያንን በምርመራው የሚያስተባብለውን አየህን? (ዐወቅከውን?)');
INSERT INTO "sadiq_sani" VALUES(107,2,'107:2','ይህም ያ የቲምን በኀይል የሚገፈትረው፤');
INSERT INTO "sadiq_sani" VALUES(107,3,'107:3','ድኻንም በማብላት ላይ የማያግባባው ሰው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(107,4,'107:4','ወዮላቸው ለሰጋጆች፡፡');
INSERT INTO "sadiq_sani" VALUES(107,5,'107:5','ለእነዚያ እነርሱ ከስግደታቸው ዘንጊዎች ለኾኑት፤ (ሰጋጆች)፡፡');
INSERT INTO "sadiq_sani" VALUES(107,6,'107:6','ለእነዚያ እነርሱ ይዩልኝ ባዮች ለኾኑት፡፡');
INSERT INTO "sadiq_sani" VALUES(107,7,'107:7','የዕቃ ትውስትንም (ሰዎችን) የሚከለክሉ ለኾኑት (ወዮላቸው)፡፡');
INSERT INTO "sadiq_sani" VALUES(108,1,'108:1','እኛ በጣም ብዙ በጎ ነገሮችን ሰጠንህ፡፡');
INSERT INTO "sadiq_sani" VALUES(108,2,'108:2','ስለዚህ ለጌታህ ስገድ፤ (በስሙ) ሰዋም፡፡');
INSERT INTO "sadiq_sani" VALUES(108,3,'108:3','ጠይህ (የሚጠላህ) እርሱ በእርግጥ (ዘሩ) የተቆረጠው ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(109,1,'109:1','በላቸው «እናንተ ከሓዲዎች ሆይ!');
INSERT INTO "sadiq_sani" VALUES(109,2,'109:2','«ያንን የምትግገዙትን (ጣዖት አሁን) አልግገዛም፡፡');
INSERT INTO "sadiq_sani" VALUES(109,3,'109:3','«እናንተም እኔ የምግገዛውን (አምላክም አሁን) ተገዢዎች አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(109,4,'109:4','«እኔም ያንን የተገዛችሁትን (ወደፊት) ተገዢ አይደለሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(109,5,'109:5','«እናንተም እኔ የምግገዛውን (ወደፊት) ተገዢዎች አይደላችሁም፡፡');
INSERT INTO "sadiq_sani" VALUES(109,6,'109:6','«ለእናንተ ሃይማኖታችሁ አልላችሁ፡፡ ለእኔም ሃይማኖቴ አለኝ፡፡');
INSERT INTO "sadiq_sani" VALUES(110,1,'110:1','የአላህ እርዳታና (መካን) መክፈት በመጣ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(110,2,'110:2','ሰዎችንም ጭፍሮች እየኾኑ፤ በአላህ ሃይማኖት ውስጥ ሲገቡ ባየህ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(110,3,'110:3','ጌታህን ከማመስገን ጋር አጥራው፡፡ ምሕረትንም ለምነው፡፡ እርሱ ጸጸትን በጣም ተቀባይ ነውና፡፡');
INSERT INTO "sadiq_sani" VALUES(111,1,'111:1','የአቡ ለሀብ ሁለት እጆች ከሰሩ (ጠፉ፤ እርሱ) ከሰረም፡፡');
INSERT INTO "sadiq_sani" VALUES(111,2,'111:2','ከእርሱ ገንዘቡና ያም ያፈራው ሁሉ ምንም አልጠቀመውም፡፡');
INSERT INTO "sadiq_sani" VALUES(111,3,'111:3','የመንቀልቀል ባለቤት የኾነችን እሳት በእርግጥ ይገባል፡፡');
INSERT INTO "sadiq_sani" VALUES(111,4,'111:4','ሚስቱም (ትገባለች)፤ እንጨት ተሸካሚ ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(111,5,'111:5','በአንገትዋ ላይ ከጭረት የኾነ ገመድ ያለባት ስትኾን፡፡');
INSERT INTO "sadiq_sani" VALUES(112,1,'112:1','በል «እርሱ አላህ አንድ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(112,2,'112:2','«አላህ (የሁሉ) መጠጊያ ነው፡፡');
INSERT INTO "sadiq_sani" VALUES(112,3,'112:3','«አልወለደም፤ አልተወለደምም፡፡');
INSERT INTO "sadiq_sani" VALUES(112,4,'112:4','«ለእርሱም አንድም ብጤ የለውም፡፡»');
INSERT INTO "sadiq_sani" VALUES(113,1,'113:1','በል «በተፈልቃቂው ጎህ ጌታ እጠበቃለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(113,2,'113:2','«ከፈጠረው ፍጡር ሁሉ ክፋት፡፡');
INSERT INTO "sadiq_sani" VALUES(113,3,'113:3','«ከሌሊትም ክፋት ባጨለመ ጊዜ፤');
INSERT INTO "sadiq_sani" VALUES(113,4,'113:4','«በተቋጠሩ (ክሮች) ላይ ተፊዎች ከኾኑት (ደጋሚ) ሴቶችም ክፋት፡፡');
INSERT INTO "sadiq_sani" VALUES(113,5,'113:5','«ከምቀኛም ክፋት በተመቀኘ ጊዜ፤ (እጠበቃለሁ በል)፡፡»');
INSERT INTO "sadiq_sani" VALUES(114,1,'114:1','በል «በሰዎች ፈጣሪ እጠበቃለሁ፡፡');
INSERT INTO "sadiq_sani" VALUES(114,2,'114:2','«የሰዎች ሁሉ ንጉሥ በኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(114,3,'114:3','«የሰዎች አምላክ በኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(114,4,'114:4','«ብቅ እልም ባይ ከኾነው ጎትጓች (ሰይጣን) ክፋት፡፡');
INSERT INTO "sadiq_sani" VALUES(114,5,'114:5','«ከዚያ በሰዎች ልቦች ውስጥ የሚጎተጉት ከኾነው፡፡');
INSERT INTO "sadiq_sani" VALUES(114,6,'114:6','«ከጋኔኖችም ከሰዎችም (ሰይጣናት እጠበቃለሁ በል)፡፡»');
