INSERT INTO "saleh" VALUES(1,1,'1:1','ناھايىتى شەپقەتلىك ۋە مېھرىبان ئاللاھنىڭ ئىسمى بىلەن باشلايمەن.');
INSERT INTO "saleh" VALUES(1,2,'1:2','جىمى ھەمدۇ سانا ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر.');
INSERT INTO "saleh" VALUES(1,3,'1:3','ئاللاھ ناھايىتى شەپقەتلىك ۋە مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(1,4,'1:4','قىيامەت كۈنىنىڭ ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(1,5,'1:5','(رەببىمىز) ساڭىلا ئىبادەت قىلىمىز ۋە سەندىنلا ياردەم تىلەيمىز.');
INSERT INTO "saleh" VALUES(1,6,'1:6','بىزنى توغرا يولغا باشلىغىن.');
INSERT INTO "saleh" VALUES(1,7,'1:7','غەزىپىڭگە يولۇققانلارنىڭ ۋە ئازغانلارنىڭ يولىغا ئەمەس، سەن ئىنئام قىلغانلارنىڭ يولىغا (باشلىغىن).');
INSERT INTO "saleh" VALUES(2,1,'2:1','ئەلىف، لام، مىم.');
INSERT INTO "saleh" VALUES(2,2,'2:2','بۇ كىتابتا (يەنى قۇرئاندا) ھېچ شەك يوق، (ئۇ) تەقۋادارلارغا يېتەكچىدۇر.');
INSERT INTO "saleh" VALUES(2,3,'2:3','ئۇلار غەيبكە ئىشىنىدۇ، نامازنى ئادا قىلىدۇ، ئۇلار بىز بەرگەن مال ـ مۈلۈكتىن (ئاللاھ يولىغا) سەرپ قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,4,'2:4','ئۇلار ساڭا نازىل قىلىنغان كىتابقا، سەندىن ئىلگىرىكى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىشىنىدۇ ۋە ئاخىرەتكە شەكسىز ئىشىنىدۇ.');
INSERT INTO "saleh" VALUES(2,5,'2:5','ئەنە شۇلار پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار بەختكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(2,6,'2:6','شۈبھىسىزكى، كاپىرلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، بەرىبىر، ئۇلار ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(2,7,'2:7','ئاللاھ ئۇلارنىڭ دىللىرىنى ۋە قۇلاقلىرىنى پېچەتلىۋەتكەن (ئىماننىڭ نۇرى كىرمەيدۇ)، ئۇلارنىڭ كۆزلىرى پەردىلەنگەن (ھەقىقەتنى كۆرمەيدۇ)، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,8,'2:8','كىشىلەر ئارىسىدا ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشەندۇق دېگۈچىلەر بار، ھەقىقەتتە ئۇلار ئىشەنمەيدۇ (يەنى ئاغزىدا ئىشەندۇق دېگىنى بىلەن، كۆڭلىدە ئىشەنمەيدۇ).');
INSERT INTO "saleh" VALUES(2,9,'2:9','ئۇلار ئاللاھنى ۋە مۆمىنلەرنى ئالدىماقچى بولىدۇ، ھەقىقەتتە ئۇلار تۇيماستىن ئۆزلىرىنىلا ئالدايدۇ.');
INSERT INTO "saleh" VALUES(2,10,'2:10','ئۇلارنىڭ دىللىرىدا كېسەل (يەنى مۇناپىقلىق ۋە شەكلىنىش) بار، ئاللاھ ئۇلارنىڭ كېسىلىنى كۈچەيتىۋەتتى؛ يالغان سۆزلىگەنلىكلىرى (يەنى يالغاندىن ئىماننى دەۋا قىلغانلىقلىرى ۋە ئاللاھنىڭ ئايەتلىرىنى مەسخىرە قىلغانلىقلىرى) ئۈچۈن ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,11,'2:11','ئۇلارغا: «يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» دېيىلسە، «بىز ئىسلاھ قىلغۇچىلارمىز» دەيدۇ.');
INSERT INTO "saleh" VALUES(2,12,'2:12','بىلىڭلاركى، ئۇلار ھەقىقەتەن بۇزغۇنچىلاردۇر، لېكىن بۇنى ئۆزلىرى تۇيمايدۇ.');
INSERT INTO "saleh" VALUES(2,13,'2:13','ئۇلارغا (يەنى مۇناپىقلارغا): «ئىمان ئېيتقان كىشىلەردەك (چىن كۆڭلۈڭلار بىلەن) ئىمان ئېيتىڭلار» دېيىلسە، «بىز ئىمان ئېيتقان ئەخمەقلەرگە ئوخشاش ئىمان ئېيتامدۇق؟» دەيدۇ. بىلىڭلاركى، ئۇلارنىڭ ئۆزلىرىلا ئەخمەقلەر، لېكىن (بۇنى) ئۇلار تۇيمايدۇ.');
INSERT INTO "saleh" VALUES(2,14,'2:14','ئۇلار مۆمىنلەر بىلەن ئۇچراشقىنىدا: «بىز ئىمان ئېيتتۇق» دېيىشىدۇ، شاياتۇنلىرى (يەنى مۇناپىق كاتتىباشلىرى) بىلەن يالغۇز جايدا تېپىشقاندا بولسا: «بىز ھەقىقەتەن سىلەر بىلەن بىللىمىز، پەقەت (تىلىمىزنىڭ ئۇچىدىلا ئىمان ئېيتىپ قويۇپ) مۆمىنلەرنى مەسخىرە قىلىمىز» دەيدۇ. ');
INSERT INTO "saleh" VALUES(2,15,'2:15','مەسخىرە قىلغانلىقلىرى ئۈچۈن ئاللاھ ئۇلارنى جازالايدۇ، ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقىغان ھالدا يۈرۈشىدۇ.');
INSERT INTO "saleh" VALUES(2,16,'2:16','ئەنە شۇلار ھىدايەتنى بېرىپ، گۇمراھلىقنى ئالدى. شۇڭا سودىسى پايدا كەلتۈرمىدى، ئۇلار ھىدايەت تاپقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(2,17,'2:17','ئۇلار گوياكى (كېچىدە ئىسسىنىش ۋە يورۇقلۇق ئېلىش ئۈچۈن) ئوت ياققان كىشىلەرگە ئوخشايدۇ، ئوت ئۇلارنىڭ ئەتراپىنى يورۇتقاندا، ئاللاھ ئۇلارنىڭ (ئوتىنىڭ) يورۇقىنى ئۆچۈرۈۋەتتى، ئۇلارنى (ئەتراپىدىكى ھېچ نەرسىنى) كۆرەلمەيدىغان قاراڭغۇلۇقتا قالدۇردى. ');
INSERT INTO "saleh" VALUES(2,18,'2:18','ئۇلار گاستۇر (يەنى گاس ئاڭلىمىغاندەك، ياخشىلىقنى ئاڭلىمايدۇ)، گاچىدۇر (يەنى گاچا سۆزلىيەلمىگەندەك، ياخشى ئىشنى سۆزلىيەلمەيدۇ)، كوردۇر (يەنى كور بولۇپ قالغاندەك، توغرا يولنى كۆرمەيدۇ)، شۇڭا ئۇلار (گۇمراھلىقتىن) قايتمايدۇ.');
INSERT INTO "saleh" VALUES(2,19,'2:19','ياكى ئۇلار زۇلمەتلىك، گۈلدۈرمامىلىق ۋە چاقماقلىق قاتتىق يامغۇردا قالغان، گويا چاقماق سوقۇۋېتىپ ئۆلۈپ كېتىشتىن قورقۇپ قۇلاقلىرىنى بارماقلىرى بىلەن ئېتىۋالغان كىشىلەرگە ئوخشايدۇ. ئاللاھ كاپىرلارنى (ئۇلارنىڭ ھەممە ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,20,'2:20','چاقماق چاققاندا ئۇلار كۆز نۇرىدىن ئايرىلىپ قالغىلى تاس قالىدۇ، ئۇلار چاقماق يورۇقىدا مېڭىۋالىدۇ؛ قاراڭغۇلۇق قاپلىغاندا تۇرۇپ قالىدۇ. ئاللاھ خالىسا ئىدى، ئۇلارنى ئاڭلاش ۋە كۆرۈش قۇۋۋىتىدىن ئەلۋەتتە مەھرۇم قىلاتتى. ئاللاھ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(2,21,'2:21','ئى ئىنسانلار! تەقۋادارلاردىن بولۇشۇڭلار ئۈچۈن، سىلەرنى ۋە سىلەردىن بۇرۇنقىلار (يەنى ئۆتكەنكى ئۈممەتلەر)نى ياراتقان پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,22,'2:22','ئاللاھ سىلەرگە زېمىننى تۆشەك ۋە ئاسماننى بىنا (يەنى يۇلتۇزلارنىڭ زىچلىقىدا بىناغا ئوخشاش) قىلىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، سىلەرگە رىزىق بولۇش ئۈچۈن يامغۇر ئارقىلىق تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بەردى. شۇڭا ئاللاھقا شېرىك كەلتۈرمەڭلار، ھالبۇكى، سىلەر (ئاللاھنىڭ شېرىكى يوقلۇقىنى) بىلىپ تۇرىسىلەر.');
INSERT INTO "saleh" VALUES(2,23,'2:23','بەندىمىز (مۇھەممەد) گە بىز نازىل قىلغان قۇرئاندىن شەكلەنسەڭلار، قۇرئانغا ئوخشاش بىرەر سۈرىنى مەيدانغا چىقىرىپ بېقىڭلار (قۇرئانغا قارشى تۇرۇشتا) ئاللاھتىن باشقا ياردەمچىڭلارنىڭ ھەممىسىنى (ياردەمگە) چاقىرىڭلار، (قۇرئان ئىنساننىڭ سۆزى دېگەن گېپىڭلاردا) راستچىل بولساڭلار.');
INSERT INTO "saleh" VALUES(2,24,'2:24','ئەگەر مۇنداق قىلالمىساڭلار - ھەرگىزغۇ قىلالمايسىلەر - كاپىرلار ئۈچۈن تەييارلانغان، ئىنسان ۋە تاشلار يېقىلغۇ بولىدىغان دوزاختىن ساقلىنىڭلار.');
INSERT INTO "saleh" VALUES(2,25,'2:25','(ئى مۇھەممەد!) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدىغانلىقى بىلەن خۇشخەۋەر بەرگىن. ئۇلار جەننەتنىڭ بىرەر مېۋىسىدىن رىزىقلاندۇرۇلغان چاغدا: «بۇنىڭ بىلەن بۇرۇن (دۇنيادىمۇ) رىزىقلاندۇرۇلغان ئىدۇق» دەيدۇ. ئۇلارغا كۆرۈنۈشى دۇنيانىڭ مېۋىلىرىگە ئوخشايدىغان، تەمى ئوخشىمايدىغان مېۋىلەر بېرىلىدۇ. جەننەتلەردە ئۇلارغا پاك جۈپتىلەر بېرىلىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(2,26,'2:26','ئاللاھ ھەقىقەتەن پاشا ۋە ئۇنىڭدىنمۇ كىچىك نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن تارتىنىپ قالمايدۇ، مۆمىنلەر ئۇنى پەرۋەردىگارى تەرىپىدىن نازىل بولغان ھەقىقەت دەپ بىلىدۇ؛ كاپىرلار بولسا: «ئاللاھ بۇ نەرسىلەرنى مىسال قىلىپ كەلتۈرۈشتىن نېمىنى ئىرادە قىلىدۇ؟» دەيدۇ. ئاللاھ بۇ مىسال بىلەن نۇرغۇن كىشىلەرنى (ئۇنى ئىنكار قىلغانلىقتىن) ئازدۇرىدۇ ۋە نۇرغۇن كىشىلەرنى (ئۇنى تەستىقلىغانلىقتىن) ھىدايەت قىلىدۇ؛ شۇ ئارقىلىق پەقەت پاسىقلارنىلا ئازدۇرىدۇ.');
INSERT INTO "saleh" VALUES(2,27,'2:27','پاسىقلار ئاللاھ ۋەدە ئالغاندىن كېيىن بەرگەن ۋەدىسىنى بۇزىدۇ. ئاللاھ ئۇلاشقا بۇيرۇغان شەيئىنى ئۈزۈپ قويىدۇ (يەنى سىلە ـ رەھىم قىلمايدۇ)، يەر يۈزىدە بۇزۇقچىلىق قىلىدۇ؛ ئەنە شۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(2,28,'2:28','ئاللاھنى قانداقمۇ ئىنكار قىلىسىلەركى، جانسىز ئىدىڭلار، ئاللاھ سىلەرگە جان بەردى (يەنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ قارنىدا ئابىمەنىي ئىدىڭلار)، كېيىن (ئەجىلىڭلار توشقاندا) ئاللاھ جېنىڭلارنى ئالىدۇ، ئاندىن كېيىن تىرىلدۈرىدۇ، ئاندىن كېيىن ئاللاھنىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(2,29,'2:29','ئاللاھ يەر زېمىندىكى ھەممە نەرسىنى سىلەرنىڭ (پايدىلىنىشىڭلار) ئۈچۈن ياراتتى، ئاندىن ئاسماننى يارىتىشقا يۈزلىنىپ، ئۇنى قۇسۇرسىز يەتتە ئاسمان قىلىپ تاماملىدى. ئاللاھ ھەرنەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,30,'2:30','ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن يەر يۈزىدە خەلىپە (يەنى ئورۇنباسار) يارىتىمەن» دېدى. پەرىشتىلەر: «يەر يۈزىدە بۇزۇقچىلىق قىلىدىغان، قان تۆكىدىغان (شەخسنى) خەلىپە قىلامسەن؟ ھالبۇكى بىز سېنى پاك دەپ مەدھىيەلەيمىز، مۇقەددەس دەپ مەدھىيەلەيمىز» دېدى. ئاللاھ: «مەن ھەقىقەتەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن» دېدى.');
INSERT INTO "saleh" VALUES(2,31,'2:31','ئاللاھ پۈتۈن شەيئىلەرنىڭ ناملىرىنى ئادەمگە ئۆگەتتى، ئاندىن ئۇلارنى پەرىشتىلەرگە كۆرسىتىپ: «(خەلىپە بولۇشقا ئادەمگە قارىغاندا بىز ھەقلىق دەيدىغان قارىشىڭلاردا) راستچىل بولساڭلار، بۇ شەيئىلەرنىڭ ناملىرىنى ماڭا ئېيتىپ بېرىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(2,32,'2:32','پەرىشتىلەر: «سېنى (پۈتۈن كەمچىلىكلەردىن) پاك دەپ تونۇيمىز. بىز سەن بىلدۈرگەندىن باشقىنى بىلمەيمىز، ھەقىقەتەن سەن ھەممىنى بىلگۈچىدۇرسەن، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن» دېدى. ');
INSERT INTO "saleh" VALUES(2,33,'2:33','ئاللاھ: «ئى ئادەم! ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگىن» دېدى. ئادەم ئۇلارغا بۇ نەرسىلەرنىڭ ناملىرىنى ئېيتىپ بەرگەن چاغدا، ئاللاھ: «مەن سىلەرگە ئاسمانلاردىكى ۋە زېمىندىكى غەيبلەرنى ھەقىقەتەن بىلىپ تۇرىمەن، ئاشكارا ۋە يوشۇرۇن ئىشىڭلارنى بىلىپ تۇرىمەن، دېمىگەنمىدىم» دېدى.');
INSERT INTO "saleh" VALUES(2,34,'2:34','ئۆز ۋاقتىدا پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس (سەجدە قىلىشتىن) باش تارتتى، تەكەببۇرلۇق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى.');
INSERT INTO "saleh" VALUES(2,35,'2:35','بىز ئادەمگە: «ئى ئادەم! سەن خوتۇنۇڭ (ھەۋۋا) بىلەن جەننەتتە تۇرۇڭلار! جەننەتتىكى نەرسىلەردىن خالىغىنىڭلارچە كەڭتاشا يەپ ـ ئىچىڭلار، بۇ دەرەخكە يېقىنلاشماڭلار (يەنى مېۋىسىدىن يېمەڭلار)، بولمىسا (ئۆزۈڭلارغا) زۇلۇم قىلغۇچىلاردىن بولۇپ قالىسىلەر» دېدۇق.');
INSERT INTO "saleh" VALUES(2,36,'2:36','شەيتان ئۇ ئىككىسىنى تېيىلدۇردى (يەنى مەنئى قىلىنغان دەرەخنىڭ مېۋىسىنى يېگۈزۈپ خاتالاشتۇردى)، تۇرۇۋاتقان جەننەتتىن چىقاردى. (ئادەمگە، ھەۋۋاغا، ئىبلىسقا) «بىر ـ بىرىڭلار بىلەن ئۆچەكەشكەن ھالدا (جەننەتتىن يەر يۈزىگە) چۈشۈڭلار، يەر يۈزىدە ۋاقىتلىق (ئەجىلىڭلار توشقۇچە) يەرلىشىڭلار ۋە (نېمەتلەردىن) بەھرىمەن بولۇڭلار» دېدۇق.');
INSERT INTO "saleh" VALUES(2,37,'2:37','ئادەم پەرۋەردىگارىدىن بىرقانچە سۆز تەلىم ئالدى (يەنى گۇناھىنىڭ كەچۈرۈلۈشى ئۈچۈن ئوقۇيدىغان دۇئا ئۇنىڭغا ئىلھام ئارقىلىق بىلدۈرۈلدى)، ئاللاھ ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى. ئاللاھ ھەقىقەتەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (بەندىلىرىگە) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,38,'2:38','(ئۇلارغا) «ھەممىڭلار بۇ يەردىن چۈشۈڭلار، سىلەرگە مەن تەرەپتىن بىر يول كۆرسەتكۈچى (يەنى پەيغەمبەر) كېلىدۇ، يولۇمغا ئەگەشكەنلەرگە (ئاخىرەتتە) قورقۇنچ ۋە غەم ـ قايغۇ بولمايدۇ» دېدۇق. ');
INSERT INTO "saleh" VALUES(2,39,'2:39','كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(2,40,'2:40','ئى ئىسرائىل ئەۋلادى! مېنىڭ سىلەرگە بەرگەن نېمىتىمنى ياد ئېتىڭلار، ماڭا بەرگەن (ئىمان ئېيتىش ۋە تائەت ـ ئىبادەت قىلىش ھەققىدىكى) ۋەدەڭلارغا ۋاپا قىلىڭلار، مەنمۇ سىلەرگە قىلغان (ساۋاب بېرىش ھەققىدىكى) ۋەدەمگە ۋاپا قىلىمەن، مەندىنلا قورقۇڭلار.');
INSERT INTO "saleh" VALUES(2,41,'2:41','مەن نازىل قىلغان تەۋراتنى تەستىق قىلغۇچى قۇرئانغا ئىمان كەلتۈرۈڭلار، قۇرئاننى بىرىنچى بولۇپ ئىنكار قىلغۇچى بولماڭلار، ئايەتلىرىمنى ئەرزان باھادا سېتىۋەتمەڭلار، ماڭىلا تەقۋادارلىق قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,42,'2:42','ھەقنى باتىلغا (يەنى ئاللاھ نازىل قىلغان ھەقىقەتنى ئۆزەڭلار ئويدۇرۇپ چىققان نەرسىگە) ئارىلاشتۇرماڭلار ، ھەقنى بىلىپ تۇرۇپ يوشۇرماڭلار. ');
INSERT INTO "saleh" VALUES(2,43,'2:43','نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,44,'2:44','كىشىلەرنى ياخشى ئىشقا بۇيرۇپ ئۆزەڭلارنى ئۇنتۇمسىلەر؟ ھالبۇكى، سىلەر كىتاب (يەنى تەۋرات) نى ئوقۇپ تۇرىسىلەر، چۈشەنمەمسىلەر؟.');
INSERT INTO "saleh" VALUES(2,45,'2:45','سەۋر قىلىش، ناماز ئوقۇش ئارقىلىق (ئاللاھتىن) ياردەم تىلەڭلار. ناماز ئاللاھتىن قورققۇچىلار (كەمتەرلىك بىلەن ئاللاھقا بويسۇنغۇچىلار) دىن باشقىلارغا ھەقىقەتەن مۇشەققەتلىك ئىشتۇر.');
INSERT INTO "saleh" VALUES(2,46,'2:46','ئاللاھتىن قورققۇچىلار پەرۋەردىگارىغا چوقۇم مۇلاقات بولىدىغانلىقىغا ۋە ئاللاھنىڭ دەرگاھىغا چوقۇم قايتىپ بارىدىغانلىقىغا جەزمەن ئىشىنىدۇ.');
INSERT INTO "saleh" VALUES(2,47,'2:47',' ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) تامام جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار. ');
INSERT INTO "saleh" VALUES(2,48,'2:48','كىشى كىشىگە ئەسقاتمايدىغان، ھېچ كىشىنىڭ شاپائىتى قوبۇل قىلىنمايدىغان، ھېچ كىشىدىن فىدىيە ئېلىنمايدىغان، ئۇلار (كاپىرلار ۋە گۇناھكار بەندىلەر) ياردەمگە ئېرىشەلمەيدىغان كۈندىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(2,49,'2:49','ئۆز ۋاقتىدا سىلەرنى قاتتىق قىينىغان، ئوغۇللىرىڭلارنى بوغۇزلاپ، قىزلىرىڭلارنى (پايدىلىنىش ئۈچۈن) تىرىك قالدۇرغان پىرئەۋن گۇرۇھىدىن سىلەرنى قۇتقۇزدۇق. بۇ، پەرۋەردىگارىڭلارنىڭ (ياخشى ئادەم بىلەن يامان ئادەمنى ئايرىيدىغان) چوڭ سىنىقى ئىدى.');
INSERT INTO "saleh" VALUES(2,50,'2:50','ئۆز ۋاقتىدا دېڭىزنى يېرىپ سىلەرنى قۇتقۇزدۇق (يەنى دېڭىزنىڭ سۈيىنى ئوتتۇرىدىن بۆلۈپ، قۇرۇق يول ھاسىل قىلىپ، شۇ يەردىن سىلەرنى سالامەت ئۆتكۈزدۇق) ۋە پىرئەۋن گۇرۇھىنى كۆز ئالدىڭلاردا غەرق قىلدۇق.');
INSERT INTO "saleh" VALUES(2,51,'2:51','ئۆز ۋاقتىدا مۇساغا 40 كېچە (كۈتكەندىن كېيىن تەۋراتنى نازىل قىلىشنى) ۋەدە قىلدۇق. ئۇ سىلەردىن ئايرىلغاندىن كېيىن، ئۆزەڭلارغا زۇلۇم قىلىپ موزاينى مەبۇد قىلىۋالدىڭلار.');
INSERT INTO "saleh" VALUES(2,52,'2:52','شۇنىڭدىن كېيىن، شۈكۈر قىلسۇن دەپ، سىلەرنى ئەپۇ قىلدۇق.');
INSERT INTO "saleh" VALUES(2,53,'2:53','ئۆز ۋاقتىدا سىلەرنى ھىدايەت تاپسۇن، دەپ مۇساغا ھەقنى، كىتابنى ۋە فۇرقاننى بەردۇق. ');
INSERT INTO "saleh" VALUES(2,54,'2:54','ئۆز ۋاقتىدا مۇسا ئۆز قەۋمىگە: «ئى قەۋمىم! سىلەر موزاينى مەبۇد قىلىپ، ھەقىقەتەن ئۆزۈڭلارغا زۇلۇم قىلدىڭلار، ياراتقۇچىڭلارغا تەۋبە قىلىڭلار، موزايغا چوقۇنمىغانلار چوقۇنغانلارنى ئۆلتۈرسۇن» دېدى. مۇنداق قىلىش ياراتقۇچىڭلارنىڭ دەرگاھىدا سىلەر ئۈچۈن ياخشىدۇر. ئاللاھ تەۋبەڭلارنى قوبۇل قىلدى. ئاللاھ تەۋبىنى ھەقىقەتەن بەكمۇ قوبۇل قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,55,'2:55','ئۆز ۋاقتىدا: «ئى مۇسا! ئاللاھنى ئوپئوچۇق كۆرمىگۈچە ساڭا ھەرگىز ئىشەنمەيمىز» دېدىڭلار ـ دە، (سىلەرگە نازىل بولغان ئازابنى) كۆرۈپ تۇرغىنىڭلاردا سىلەرنى چاقماق سوقتى.');
INSERT INTO "saleh" VALUES(2,56,'2:56','ئاندىن كېيىن، سىلەرنى شۈكۈر قىلسۇن، دەپ ئۆلگىنىڭلاردىن كېيىن قايتا تىرىلدۈردۇق.');
INSERT INTO "saleh" VALUES(2,57,'2:57','سىلەرگە بۇلۇتنى سايىۋەن قىلىپ بەردۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (بىز سىلەرگە) «رىزىق قىلىپ بەرگەن پاك، شېرىن نەرسىلەردىن يەپ ـ ئىچىڭلار» (دېدۇق). ئۇلار (نېمەتلىرىمىزگە نانكورلۇق قىلىش بىلەن) بىزگە ئەمەس، پەقەت ئۆزلىرىگىلا زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(2,58,'2:58','ئۆز ۋاقتىدا (مۇسا ئارقىلىق ئەجدادلىرىڭلارغا): «بۇ شەھەرگە (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئۇ يەردە خالىغىنىڭلارچە كەڭتاشا يەپ ـ ئىچىڭلار، (بەيتۇلمۇقەددەس) دەرۋازىسىدىن (شۈكۈر قىلىش يۈزىسىدىن) سەجدە قىلغان ھالدا كىرىڭلار ۋە: (پەرۋەردىگارىمىز) گۇناھلىرىمىزنى كەچۈرگىن دەڭلار، گۇناھىڭلارنى مەغپىرەت قىلىمىز، ياخشىلارغا تېخىمۇ ئارتۇق ساۋاب بېرىمىز» دېدۇق.');
INSERT INTO "saleh" VALUES(2,59,'2:59','زۇلۇم قىلغۇچىلار ئۆزلىرىگە سۆزلەنگەن سۆزنى باشقا سۆزگە ئۆزگەرتىۋەتتى، زۇلۇم قىلغۇچىلار يولدىن چىققانلىقلىرى (يەنى ئاللاھقا ئاسىيلىق قىلغانلىقلىرى) ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب چۈشۈردۇق.');
INSERT INTO "saleh" VALUES(2,60,'2:60','ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمى ئۈچۈن سۇ تەلەپ قىلغان ئىدى، بىز ئۇنىڭغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دېدۇق. تاشتىن 12 بۇلاق ئېتىلىپ چىقتى، ھەممە ئادەم (يەنى ھەر بىر قەبىلە) ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى، (ئۇلارغا) «ئاللاھ بەرگەن رىزىقتىن يەڭلار ۋە ئىچىڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلماڭلار» (دېدۇق).');
INSERT INTO "saleh" VALUES(2,61,'2:61','ئۆز ۋاقتىدا (سىنا چۆلىدە تەرەنجىبىن ۋە بۆدۈنە بىلەنلا ئوزۇقلىنىۋاتقىنىڭلاردا) سىلەر: «ئى مۇسا! بىز بىر خىل يېمەكلىككە چىداپ تۇرالمايمىز، بىز ئۈچۈن پەرۋەردىگارىڭغا دۇئا قىلغىن، بىزگە زېمىننىڭ كۆكتاتلىرىدىن تەرخەمەك، سامساق، يېسىمۇق ۋە پىيازلارنى ئۆستۈرۈپ بەرسۇن» دېدىڭلار، مۇسا: «سىلەر ياخشىنى ناچارغا تېگىشەمسىلەر؟ (يەنى پىياز، سامساق، كۆكتاتنى تەرەنجىبىن بىلەن بۆدۈنىدىن ئارتۇق كۆرەمسىلەر؟) بىرەر شەھەرگە كىرىڭلار، (شۇ يەردە) تىلىگىنىڭلار بار» دېدى. ئۇلار خارلىقتا، موھتاجلىقتا قالدى. ئاللاھنىڭ غەزىپىگە تېگىشلىك بولدى. بۇ ئۇلارنىڭ ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى تۈپەيلىدىن بولدى. بۇ ئاللاھقا ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئاشقانلىقلىرى تۈپەيلىدىن بولدى.');
INSERT INTO "saleh" VALUES(2,62,'2:62','شۈبھىسىزكى، مۆمىنلەر، يەھۇدىيلار، ناسارالار (ئارىسىدىن) ۋە پەرىشتىلەرگە، يۇلتۇزلارغا چوقۇنغۇچىلار ئارىسىدىن ئاللاھقا، ئاخىرەت كۈنىگە ئىمان كەلتۈرگەن ۋە ياخشى ئەمەللەرنى قىلغانلار پەرۋەردىگارىنىڭ قېشىدا ئۆز ئەجرىدىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) قورقۇنچ يوقتۇر ۋە ئۇلار غەم – ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,63,'2:63','ئۆز ۋاقتىدا، بىز سىلەردىن چىن ۋەدە ئالغان ۋە تۇر تېغىنى ئۈستۈڭلارغا تىكلەپ قويغان، سىلەرگە: «(دۇنيادا ھالاك بولۇشتىن، ئاخىرەتتە ئازابقا قېلىشتىن) ساقلىنىشىڭلار ئۈچۈن بىز بەرگەن كىتابنى مەھكەم تۇتۇڭلار (يەنى تەۋراتقا ئەمەل قىلىڭلار)، ئۇنىڭدىكى ئەھكاملارنى ئېسىڭلاردا ساقلاڭلار» (دېگەن ئىدۇق).');
INSERT INTO "saleh" VALUES(2,64,'2:64','شۇنىڭدىن كېيىن (بەرگەن ۋەدەڭلاردىن) يۈز ئۆرۈدۈڭلار، سىلەرگە ئاللاھنىڭ پەزلى ـ مەرھەمىتى بولمىسا ئىدى، چوقۇم زىيان تارتقۇچىلاردىن بولاتتىڭلار. ');
INSERT INTO "saleh" VALUES(2,65,'2:65','ئاراڭلاردىكى شەنبە كۈنى (بېلىق تۇتماسلىق) توغرىسىدىكى شەرىئەت چەكلىمىسىدىن چىقىپ كەتكۈچىلەرنى (قانداق قىلغانلىقىمىزنى)، ئەلۋەتتە بىلىسىلەر، بىز ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق.');
INSERT INTO "saleh" VALUES(2,66,'2:66','ئۇنى (يەنى ئۇلارغا بېرىلگەن بۇ جازانى) زامانداشلىرى ۋە كېيىنكىلەر ئۈچۈن ئىبرەت، تەقۋادارلار ئۈچۈن ۋەز ـ نەسىھەت قىلدۇق.');
INSERT INTO "saleh" VALUES(2,67,'2:67','ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمىگە: «ئاللاھ ھەقىقەتەن سىلەرنى بىر كالا بوغۇزلاشقا بۇيرۇيدۇ» دېگەن ئىدى. ئۇلار: «بىزنى مەسخىرە قىلىۋاتامسەن؟» دېدى. مۇسا: «جاھىللاردىن (يەنى مەسخىرە قىلغۇچىلاردىن) بولۇپ قېلىشتىن ئاللاھقا سىغىنىپ پاناھ تىلەيمەن» دېدى.');
INSERT INTO "saleh" VALUES(2,68,'2:68','ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە قانداق كالا ئىكەنلىكىنى بايان قىلسۇن» دېدى. مۇسا: «ئاللاھ ئۇنى قېرىمۇ ئەمەس، ياشمۇ ئەمەس، ئوتتۇرا ياش بولسۇن، دەپ ئېيتتى، سىلەر بۇيرۇلغاننى قىلىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(2,69,'2:69','ئۇلار: «بىز ئۈچۈن پەرۋەردىگارىڭغا ئىلتىجا قىلغىنكى، بىزگە ئۇنىڭ رەڭگىنى بايان قىلسۇن» دېدى. مۇسا ئېيتتىكى، «ئاللاھ ھەقىقەتەن ئۇنىڭ رەڭگى قارىغۇچىلارنى زوقلاندۇرىدىغان ساپسېرىق بولسۇن دېدى».');
INSERT INTO "saleh" VALUES(2,70,'2:70','ئۇلار: «پەرۋەردىگارىڭغا ئىلتىجا قىلغىن، ئۇنىڭ قانداق ئىكەنلىكىنى بىزگە بايان قىلسۇن، (يەنى دېھقانچىلىققا ئىشلىتىلگەن كالىمۇ، ياكى سەمرىتىش ئۈچۈن بېقىلغان كالىمۇ؟) بۇ بىزگە مۈجمەل بولۇپ قالدى (بۇ ئېنىق بولىدىغان بولسا) خۇدا خالىسا (ئۇنى) بىز چوقۇم تاپىمىز» دېدى.');
INSERT INTO "saleh" VALUES(2,71,'2:71','مۇسا: «ئاللاھ ئۇنىڭ يەر ھەيدەپ، ئېكىن سۇغىرىپ كۆندۈرۈلگەن بولماسلىقىنى، بېجىرىم، ئالىسى يوق (يەنى رەڭگىدە سېرىقلىقتىن باشقا رەڭ يوق) بولۇشىنى ئېيتتى» دېدى. ئۇلار: «ئەمدى تولۇق بايان قىلدىڭ» دېدى. ئۇلار (كالىنى) تېپىپ كېلىپ بوغۇزلىدى، (ئۇلار سوئال سوراۋەرگەنلىكتىن) بۇنى ئورۇنلىيالمىغىلى تاس قالدى.');
INSERT INTO "saleh" VALUES(2,72,'2:72','ئۆز ۋاقتىدا سىلەر بىر كىشىنى ئۆلتۈرگەن ۋە (قاتىلنىڭ كىملىكى) توغرىسىدا دەتالاش قىلىشقان ئىدىڭلار. سىلەر يوشۇرماقچى بولغان نەرسىنى (يەنى قاتىلنىڭ ئىشىنى) ئاللاھ ئاشكارىلىغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,73,'2:73','(سىلەرگە مۇسا ئارقىلىق) دېدۇقكى، كالىنىڭ بىر پارچىسى بىلەن ئۆلۈكنى ئۇرۇڭلار (ئۇ تىرىلىپ قاتىلنى ئېيتىپ بېرىدۇ)، ئاللاھ ئۆلۈكلەرنى شۇنداق (يەنى كۆز ئالدىڭلاردا بۇ ئۆلۈكنى تىرىلدۈرگەندەك) تىرىلدۈرىدۇ، سىلەرنى چۈشەنسۇن دەپ قۇدرىتىنىڭ ئالامەتلىرىنى سىلەرگە كۆرسىتىدۇ.');
INSERT INTO "saleh" VALUES(2,74,'2:74','(ئى يەھۇدىيلار جامائەسى!) شۇنىڭدىن كېيىن (يەنى شانلىق مۆجىزىلەرنى كۆرگەندىن كېيىن) دىلىڭلار قېتىپ كەتتى، تاشتەك ياكى تاشتىنمۇ قاتتىق قېتىپ كەتتى. تاشلار ئارىسىدا ئىچىدىن (سۇ ئېتىلىپ چىقىپ) ئېرىقلار ھاسىل بولىدىغانلىرى بار، يېرىلىپ ئارىسىدىن سۇ چىقىدىغانلىرىمۇ بار؛ ئاللاھتىن قورقۇپ (تاغ چوققىلىرىدىن) دومىلاپ چۈشىدىغانلىرىمۇ بار (دېمەك، تاش يۇمشايدۇ، سىلەرنىڭ دىلىڭلار بولسا يۇمشىمايدۇ ۋە تەسىرلەنمەيدۇ)، ئاللاھ قىلمىشىڭلاردىن غاپىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(2,75,'2:75','(ئى مۆمىنلەر جامائەسى! دەۋىتىڭلار ئارقىلىق) يەھۇدىيلارنىڭ ئىمان ئېيتىشىنى ئۈمىد قىلامسىلەر؟ ھالبۇكى، ئۇلاردىن بىر گۇرۇھ ئادەم ئاللاھنىڭ كالامىنى ئاڭلاپ چۈشەنگەندىن كېيىن، ئۇنى بىلىپ تۇرۇپ (قەستەن) ئۆزگەرتىۋەتتى.');
INSERT INTO "saleh" VALUES(2,76,'2:76','ئۇلار مۆمىنلەر بىلەن ئۇچراشقانلىرىدا: «ئىمان ئېيتتۇق» دەيتتى. بىر ـ بىرى بىلەن يالغۇز جايدا تېپىشقانلىرىدا: «پەرۋەردىگارىڭلار قېشىدا مۆمىنلەر سىلەرگە قارشى پاكىت كۆرسەتسۇن دەپ ئاللاھ سىلەرگە بىلدۈرگەن ۋەھىينى (يەنى مۇھەممەد ئەلەيھسسالامنىڭ تەۋراتتىكى سۈپەتلىرىنى) ئۇلارغا سۆزلەپ بېرەمسىلەر؟ (بۇنى) چۈشەنمەمسىلەر!» دەيتتى.');
INSERT INTO "saleh" VALUES(2,77,'2:77','يەھۇدىيلار ئۆزلىرىنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى ئاللاھنىڭ بىلىپ تۇرىدىغانلىقىنى ئۇقمامدۇ؟');
INSERT INTO "saleh" VALUES(2,78,'2:78','ئۇلارنىڭ ساۋاتسىزلىرى كىتابنى (تەۋراتنى) بىلمەيدۇ، (ئەھبارلىرى ئويدۇرۇپ چىققان) ئويدۇرمىلارنىلا بىلىدۇ، گۇمان بىلەنلا ئىش قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,79,'2:79','كىتابنى ئۆز قوللىرى بىلەن يېزىپ ئۇنى ئاز پۇلغا سېتىش (دۇنيانىڭ ئازغىنا مەنپەئىتى) ئۈچۈن، بۇ ئاللاھ تەرىپىدىن نازىل بولغان دېگۈچىلەرگە ۋاي! قولى بىلەن يازغانلىرى (يەنى تەۋراتنى ئۆزگەرتكەنلىكلىرى) ئۈچۈن ئۇلارغا ۋاي! (بۇنىڭ بىلەن) ئېرىشكەن (ھارام) نەرسىلىرى ئۈچۈن ئۇلارغا ۋاي!');
INSERT INTO "saleh" VALUES(2,80,'2:80','ئۇلار: «بىزنى دوزاخ ئوتى ساناقلىق كۈنلەرلا كۆيدۈرىدۇ» دەيدۇ. (ئۇلارغا) «سىلەرگە ئاللاھ شۇنداق قىلىشقا ۋەدە بەرگەنمۇ، (ۋەدە بەرگەن بولسا) ئاللاھ ۋەدىسىگە ھەرگىزمۇ خىلاپلىق قىلمايدۇ، ياكى ئۆزۈڭلار بىلمەيدىغاننى ئاللاھ نامىدىن دەۋاتامسىلەر؟» دېگىن.');
INSERT INTO "saleh" VALUES(2,81,'2:81','ئۇنداق ئەمەس (سىلەرنى دوزاخ ئوتى كۆيدۈرىدۇ)، گۇناھ قىلغان ۋە گۇناھقا چۆمۈپ كەتكەنلەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(2,82,'2:82','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئەھلى جەننەتتۇر. ئۇلار جەننەتتە مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(2,83,'2:83','ئۆز ۋاقتىدا بىز ئىسرائىل ئەۋلادىدىن ئاللاھتىن باشقىغا ئىبادەت قىلماسلىققا، ئاتا ـ ئانىغا، خىش ـ ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە ياخشىلىق قىلىشقا، كىشىلەرگە ياخشى سۆز قىلىشقا، ناماز ئۆتەشكە، زاكات بېرىشكە ئەھدە ئالدۇق. ئاندىن ئاراڭلاردىكى ئازغىنا كىشىدىن باشقىڭلار ئەھدىدىن يېنىۋالدىڭلار، سىلەر ھەمىشە (ئەھدەڭلاردىن) يېنىۋالىسىلەر.');
INSERT INTO "saleh" VALUES(2,84,'2:84','ئۆز ۋاقتىدا بىر ـ بىرىڭلارنىڭ قېنىنى تۆكمەسلىككە، بىر ـ بىرىڭلارنى يۇرتلىرىڭلاردىن ھەيدەپ چىقارماسلىققا سىلەردىن چىن ئەھدە ئالغان ئىدۇق. كېيىن بۇنى (يەنى ئەھدىنى) ئېتىراپ قىلغان ئىدىڭلار، بۇنىڭغا ئۆزۈڭلار گۇۋاھسىلەر.');
INSERT INTO "saleh" VALUES(2,85,'2:85','كېيىن سىلەر (ئەھدىنى بۇزۇپ) بىر ـ بىرىڭلارنى ئۆلتۈردۈڭلار، ئىچىڭلاردىن بىر تۈركۈم كىشىلەرنى يۇرتىدىن ھەيدەپ چىقاردىڭلار، گۇناھ ۋە دۈشمەنلىك قىلىش بىلەن ئۇلارنىڭ قارشى تەرىپىدىكىلەرگە ياردەم بەردىڭلار، ئۇلار ئەسىر چۈشۈپ كەلسە، فىدىيە بېرىپ قۇتقۇزۇۋالىسىلەر؛ ھالبۇكى، ئۇلارنى يۇرتلىرىدىن ھەيدەپ چىقىرىش سىلەرگە ھارام قىلىنغان ئىدى (يەنى بىر ـ بىرىڭلارنى ئۆلتۈرۈش ۋە يۇرتىدىن ھەيدەپ چىقىرىشنى راۋا كۆرۈسىلەرۇ، ئۇلارنىڭ ئەسىرگە چۈشۈپ دۈشمەنلىرىڭلارنىڭ قولىدا بولۇپ قېلىشىنى راۋا كۆرمەيسىلەر). سىلەر كىتابنىڭ (يەنى تەۋراتنىڭ) بىر قىسىم ئەھكاملىرىغا ئىشىنىپ، بىر قىسىم ئەھكاملىرىنى ئىنكار قىلامسىلەر؟ سىلەردىن شۇنداق قىلغانلارنىڭ جازاسى پەقەت ھاياتىي دۇنيادا خارلىققا قېلىش، قىيامەت كۈنى قاتتىق ئازابقا دۇچار بولۇشتۇر. ئاللاھ قىلمىشىڭلاردىن غاپىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(2,86,'2:86','ئەنە شۇلار ئاخىرەتنى بېرىپ، دۇنيا تىرىكچىلىكىنى سېتىۋالغان كىشىلەردۇر. شۇنىڭ ئۈچۈن، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(2,87,'2:87','شەك ـ شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋرات) بەردۇق. ئۇنىڭدىن كېيىن ئارقىمۇئارقا پەيغەمبەرلەر ئەۋەتتۇق، مەريەم ئوغلى ئىيساغا مۆجىزىلەر بەردۇق، ھەمدە ئۇنى روھۇلقۇددۇس (يەنى جىبرىئىل) بىلەن يۆلىدۇق. ھەرقاچان بىرەر پەيغەمبەر كۆڭلۈڭلارغا ياقمايدىغان بىرنەرسە ئېلىپ كەلسە، تەكەببۇرلۇق قىلىۋېرەمسىلەر؟ بىر قىسىم پەيغەمبەرلەرنى ئىنكار قىلدىڭلار، يەنە بىر قىسىم پەيغەمبەرلەرنى ئۆلتۈردۈڭلار.');
INSERT INTO "saleh" VALUES(2,88,'2:88','ئۇلار: «دىللىرىمىز پەردىلەنگەن» دېدى. ئۇنداق ئەمەس، ئۇلارنىڭ كۇفرى سەۋەبلىك ئاللاھ ئۇلارغا لەنەت قىلدى، ئۇلاردىن ئىمان ئېيتىدىغانلار ناھايىتى ئاز.');
INSERT INTO "saleh" VALUES(2,89,'2:89','ئۇلارغا ئاللاھ تەرىپىدىن ئۇلاردىكى كىتاب (تەۋرات) نى تەستىقلايدىغان كىتاب (قۇرئان) نازىل بولغان چاغدا (ئۇنىڭغا ئىشەنمىدى). ئىلگىرى ئۇلار (مۇھەممەد ئەلەيھىسسالامنى ۋاسىتە قىلىپ) كاپىرلارغا قارشى ئۆزلىرىگە ياردەم كېلىشىنى تىلەيتتى، ئۇلار بىلىدىغان (تەۋراتتا سۈپىتى بايان قىلىنغان) پەيغەمبەر كەلگەن چاغدا ئۇنى ئىنكار قىلدى. ئاللاھ كاپىرلارغا (پەيغەمبەرلەرنىڭ تۈگەنچىسىنى ئىنكار قىلغان يەھۇدىيلارغا) لەنەت قىلدى.');
INSERT INTO "saleh" VALUES(2,90,'2:90','يەھۇدىيلارنىڭ ئاللاھ نازىل قىلغان قۇرئاننى ئىنكار قىلىشى ئاللاھنىڭ ئۆز پەزلىنى (پەيغەمبەرلىكنى) خالىغان بەندىسىگە چۈشۈرگەنلىكىگە ھەسەت قىلىش يۈزىسىدىندۇر. ئۇلارنىڭ ئۆزلىرىنى (ئىمانىنى) كۇفرىغا سېتىشى ئەجەب يامان ئىشتۇر. ئۇلار ئۈستى ـ ئۈستىگە غەزەپكە تېگىشلىك بولدى. كاپىرلار خارلىغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,91,'2:91','ئۇلارغا: «ئاللاھ نازىل قىلغان كىتابقا (قۇرئانغا) ئىمان كەلتۈرۈڭلار» دېيىلسە، ئۇلار: «ئۆزىمىزگە نازىل قىلىنغان كىتابقا (يەنى تەۋراتقا) ئىمان كەلتۈرىمىز» دەيدۇ. ئۇنىڭدىن كېيىنكى كىتابقا (قۇرئانغا) ئىشەنمەيدۇ. ھالبۇكى، ئۇ (يەنى قۇرئان) ھەقتۇر، ئۇلارنىڭ قولىدىكى كىتابنى تەستىق قىلغۇچىدۇر. (ئۇلارغا) «سىلەر (تەۋراتقا) ئىشىنىدىغان بولساڭلار، ئىلگىرى نېمە ئۈچۈن ئاللاھنىڭ پەيغەمبەرلىرىنى ئۆلتۈردۈڭلار؟» دېگىن.');
INSERT INTO "saleh" VALUES(2,92,'2:92','مۇسا سىلەرگە ھەقىقەتەن ئېنىق مۆجىزىلەرنى كەلتۈردى، مۇسا (تۇر تېغىغا كەتكەن) دىن كېيىن، ئۆزۈڭلارغا زۇلۇم قىلىپ موزاينى مەبۇد قىلىۋالدىڭلار.');
INSERT INTO "saleh" VALUES(2,93,'2:93','ئۆز ۋاقتىدا بىز سىلەردىن (تەۋراتقا ئەمەل قىلىشقا) چىن ۋەدە ئالغان ئىدۇق، سىلەرگە: «مەن نازىل قىلغان كىتابنى مەھكەم تۇتۇڭلار (بولمىسا ئۈستۈڭلارغا تاغنى تاشلايمىز)» دەپ تۇر تېغىنى ئۈستۈڭلارغا تىكلىدۇق ۋە (تەۋراتقا) قۇلاق سېلىڭلار (دېدۇق). ئۇلار: «سۆزۈڭگە قۇلاق سالدۇق، ئەمرىڭگە ئاسىيلىق قىلدۇق» دېيىشتى. ئۇلارنىڭ كۇفرى تۈپەيلىدىن دىللىرىغا موزاينىڭ مۇھەببىتى سىڭىپ كەتتى. ئۇلارغا: «ئەگەر سىلەر مۆمىن بولساڭلار، ئىمانىڭلار سىلەرنى ئەجەب يامان ئىشقا بۇيرۇيدىكىنا؟ (سىلەر مۆمىن ئەمەس، چۈنكى ئىمان موزايغا چوقۇنۇشقا بۇيرۇمايدۇ)» دېگىن.');
INSERT INTO "saleh" VALUES(2,94,'2:94','ئۇلارغا: «ئەگەر ئاخىرەت يۇرتى (جەننەت) ئاللاھنىڭ قېشىدا (سىلەر گۇمان قىلغاندەك) باشقىلارغا ئەمەس، يالغۇز سىلەرگىلا خاس بولسا، (سىلەرنى جەننەتكە ئۇلاشتۇرىدىغان) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار! (جەننەت بىزگىلا خاس دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار» دېگىن.');
INSERT INTO "saleh" VALUES(2,95,'2:95','ئۇلار قىلغان يامان ئەمەللىرى سەۋەبلىك، ئۆلۈمنى ھەرگىزمۇ ئارزۇ قىلمايدۇ. ئاللاھ زالىملارنى ئوبدان بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,96,'2:96','شۈبھىسىزكى، ئۇلارنى ھاياتقا ھەممە كىشىدىن، ھەتتا مۇشرىكلاردىنمۇ ھېرىس كۆرۈسەن؛ ھەر بىرى مىڭ يىل ئۆمۈر كۆرۈشنى ئارزۇ قىلىدۇ، ۋەھالەنكى ئۇزۇن ئۆمۈر كۆرۈش ئۇلارنى ئازابتىن قۇتۇلدۇرۇپ قالالمايدۇ. ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى ئېنىق كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,97,'2:97','ئېيتقىنكى، «جىبرىئىلغا دۈشمەن بولغان ئادەم (ئاللاھقا دۈشمەندۇر)، چۈنكى ئۇ (جىبرىئىل) ئاللاھنىڭ ئەمرى بىلەن ئۆزىدىن ئىلگىرىكىنى (ساماۋى كىتابلارنى) تەستىق قىلغۇچى، توغرا يول كۆرسەتكۈچى، مۆمىنلەرگە بېشارەت بەرگۈچى قۇرئاننى سېنىڭ قەلبىڭگە نازىل قىلدى. ');
INSERT INTO "saleh" VALUES(2,98,'2:98','كىمكى ئاللاھنى، ئاللاھنىڭ پەرىشتىلىرىنى، پەيغەمبەرلىرىنى، جىبرىئىلنى، مىكائىلنى دۈشمەن تۇتىدىكەن (ئاللاھنى دۈشمەن تۇتقان بولىدۇ)، شۈبھىسىزكى، ئاللاھ كاپىرلارنى دۈشمەن تۇتىدۇ».');
INSERT INTO "saleh" VALUES(2,99,'2:99','شەك ـ شۈبھىسىزكى، ساڭا بىز روشەن ئايەتلەرنى نازىل قىلدۇق، ئۇلارنى پەقەت توغرا يولدىن چىققانلارلا ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,100,'2:100','ھەر قاچان ئۇلار بىرەر ئەھدە تۈزۈشسە، بىر تۈركۈمى ئۇنى بۇزىدۇ، بەلكى ئۇلارنىڭ (يەھۇدىيلارنىڭ) تولىسى (تەۋراتقا) ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(2,101,'2:101','ئۇلارغا ئاللاھ تەرىپىدىن قوللىرىدىكى كىتابنى تەستىقلايدىغان بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە، كىتاب بېرىلگەنلەرنىڭ بىر تۈركۈمى (ئۇنىڭ پەيغەمبەرلىك دەلىللىرىنى) بىلمەيدىغاندەك، ئاللاھنىڭ كىتابىنى (تەۋراتنى) ئارقىسىغا چۆرۈۋەتتى (يەنى تەۋراتنىڭ سۆزلىرىگە ئەمەل قىلمىدى).');
INSERT INTO "saleh" VALUES(2,102,'2:102','ئۇلار سۇلايماننىڭ پادىشاھلىق زامانىدىكى شەيتانلارنىڭ (سېھىرگەرلىكىگە ئائىت) سۆزلىرىگە ئەگەشتى. سۇلايمان (سېھرىگەر بولغىنى ۋە سېھىر ئۆگىنىش بىلەن) كاپىر بولغىنى يوق، لېكىن شەيتانلار كىشىلەرگە سېھىر ئۆگىتىپ كاپىر بولدى. ئۇلار بابىلدىكى ھارۇت ـ مارۇت دېيىلىدىغان ئىككى پەرىشتىگە نازىل قىلىنغان سېھىرنى ئۆگىتەتتى. (بۇ ئىككى پەرىشتە) بىز كىشىلەرنى سىنايمىز، كاپىر بولمىغىن، دېمىگۈچە ھېچ كىشىگە سېھىر ئۆگەتمەيتتى. ئۇلار بۇ ئىككىسىدىن ئەر ـ خوتۇننى بىر ـ بىرىدىن ئايرىۋېتىدىغان نەرسىلەرنى (يەنى سېھىرگەرلىكنى) ئۆگىنەتتى، ئۇلار (سېھىر ئارقىلىق) ئاللاھنىڭ رۇخسىتىسىز ھېچ كىشىگە زىيان يەتكۈزەلمەيتتى. ھالبۇكى، ئۇلار ئۆزلىرىگە زىيىنى بار، پايدىسى يوق نەرسىنى ئۆگىنەتتى. يەھۇدىيلار (تەۋراتنى تاشلاپ) سېھىرنى سېتىۋالغۇچىغا ئاخىرەتتە (ئاللاھنىڭ رەھمىتىدىن ۋە جەننىتىدىن) ھېچ نېسىۋە يوق ئىكەنلىكىنى ئوبدان بىلەتتى، ئۇلار جانلىرىنىڭ بەدىلىگە سېتىۋالغان نەرسىسىنىڭ (يەنى ئۆزلىرى ئۈچۈن سېھىر ئۆگىنىشنى تاللىشىنىڭ) ئەجەبمۇ يامان ئىش ئىكەنلىكىنى كاشكى بىلسە ئىدى.');
INSERT INTO "saleh" VALUES(2,103,'2:103','ئەگەر ئۇلار ئىمان كەلتۈرسە (سېھىرنى تاشلاپ) ئاللاھتىن قورقسا، (ئۇلار ئۈچۈن) ئاللاھنىڭ دەرگاھىدىكى ساۋاب ئەلۋەتتە ياخشى ئىدى، كاشكى ئۇلار بۇنى بىلسە ئىدى.');
INSERT INTO "saleh" VALUES(2,104,'2:104','ئى مۆمىنلەر! (پەيغەمبەرگە) رائىنا دەپ خىتاب قىلماي، ئۇنزۇرنا دەپ خىتاب قىلىڭلار، (سۆزىگە) قۇلاق سېلىڭلار، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,105,'2:105','ئەھلى كىتاب ۋە مۇشرىكلاردىن بولغان كاپىرلار پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە بىرەر ياخشىلىقنىڭ چۈشۈشىنى ياقتۇرمايدۇ، ئاللاھ ئۆزىنىڭ خالىغان بەندىسىگە پەيغەمبەرلىكنى خاس قىلىدۇ، ئاللاھ چوڭ پەزل ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(2,106,'2:106','قانداقلا بىر ئايەتنى ئەمەلدىن قالدۇرساق ياكى ئۇنتۇلدۇرساق، (ئورنىغا) ئۇنىڭدىن ئارتۇق ياكى شۇنىڭغا ئوخشاش بىر ئايەتنى كەلتۈرىمىز. ئاللاھنىڭ ھەر نەرسىگە قادىر ئىكەنلىكىنى بىلمەمسەن؟');
INSERT INTO "saleh" VALUES(2,107,'2:107','ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا مەنسۇپ ئىكەنلىكىنى بىلمەمسەن؟ سىلەرگە ئاللاھتىن باشقا ھېچقانداق دوست ۋە مەدەتكار يوقتۇر.');
INSERT INTO "saleh" VALUES(2,108,'2:108','ئۇلار (يەنى يەھۇدىيلار) ئىلگىرى مۇسادىن سوئال سورىغاندەك، سىلەرمۇ پەيغەمبىرىڭلاردىن سوئال سورىماقچى بولامسىلەر؟ كىمكى ئىمانىنى كۇفرىغا تېگىشسە، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ.');
INSERT INTO "saleh" VALUES(2,109,'2:109','ئەھلى كىتاب ئىچىدىكى نۇرغۇن كىشىلەر ئۆزلىرىگە ھەقىقەت (يەنى دىنىڭلارنىڭ ھەقلىقى) ئاشكارا بولغاندىن كېيىن، ھەسەت قىلىش يۈزىسىدىن سىلەرنى ئىمانىڭلاردىن قايتۇرۇپ كاپىر قىلىشنى ئارزۇ قىلىدۇ. ئاللاھنىڭ ئەمرى كەلگەنگە قەدەر (يەنى ئاللاھ سىلەرگە ئۇلار بىلەن ئۇرۇش قىلىشقا رۇخسەت قىلغىچە) ئۇلارنى ئەپۇ قىلىڭلار ۋە كەچۈرۈڭلار. ئاللاھ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(2,110,'2:110','نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، ئۆزۈڭلار ئۈچۈن ئىشلىگەن ھەرقانداق ياخشى ئەمەل بولسا، ئاللاھنىڭ دەرگاھىدا ئۇنىڭ ساۋابىنى تاپىسىلەر، ئاللاھ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,111,'2:111','ئۇلار: «يەھۇدىي ياكى ناسارا بولمىغان ئادەم (يەنى يەھۇدىيلار يەھۇدىي بولمىغان ئادەم، ناسارالار ناسارا بولمىغان ئادەم) ھەرگىزمۇ جەننەتكە كىرمەيدۇ» دېيىشتى. بۇ، ئۇلارنىڭ قۇرۇق ئارزۇسىدۇر. «ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، دەلىلىڭلارنى كەلتۈرۈڭلار» دېگىن.');
INSERT INTO "saleh" VALUES(2,112,'2:112','ئۇنداق ئەمەس، كىمكى ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى ئاللاھقا تاپشۇرىدىكەن (ئاللاھنىڭ ئەمرىگە بويسۇنۇپ، ئەمەلىنى خالىس ئاللاھ ئۈچۈن قىلىدىكەن)، ئۇ پەرۋەردىگارىنىڭ دەرگاھىدا ساۋابتىن بەھرىمەن بولىدۇ. ئۇلارغا (ئاخىرەتتە) قورقۇنچ يوقتۇر ۋە ئۇلار غەم – ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,113,'2:113','يەھۇدىيلار: «ناسارالارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى. ناسارالارمۇ: «يەھۇدىيلارنىڭ ھېچقانداق ئاساسى يوق (يەنى ئۇلار توغرا دىندا ئەمەس)» دېدى. ھالبۇكى، ئۇلار كىتابنى (يەنى يەھۇدىيلار تەۋراتنى، ناسارالار ئىنجىلنى) ئوقۇيدۇ، بىلىمسىز ئادەملەر (يەنى ئەرەب مۇشرىكلىرى) مۇ ئۇلار دېگەندەك دېدى، ئاللاھ قىيامەت كۈنى ئۇلارنىڭ ئۆزئارا ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(2,114,'2:114',' ئاللاھنىڭ مەسجىدلىرىدە ئاللاھنىڭ نامىنىڭ ياد قىلىنىشىنى توسقان ۋە ئۇلارنى ۋەيران قىلىشقا ئورۇنغانلاردىنمۇ زالىم كىشى بارمۇ؟ مەسجىدلەرگە ئۇلارنىڭ پەقەت قورققان ھالدىلا كىرىشى لايىق ئىدى. ئۇلار دۇنيادا رەسۋا بولىدۇ. ئاخىرەتتە چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,115,'2:115','مەشرىقمۇ، مەغرىبمۇ (يەنى پۈتۈن يەر يۈزى) ئاللاھنىڭدۇر، قايسى تەرەپكە يۈزلەنسەڭلەرمۇ، ئۇ ئاللاھنىڭ تەرىپى (يەنى سىلەرگە ئىختىيار قىلغان قىبلىسى) دۇر. ئاللاھ (نىڭ مەغپىرىتى) ھەقىقەتەن كەڭدۇر، (ئاللاھ) ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,116,'2:116','ئۇلار (يەھۇدىيلار، ناسارالار، مۇشرىكلار): «ئاللاھنىڭ بالىسى بار» دەيدۇ. ئاللاھ (ئۇلارنىڭ گۇمان قىلغان نەرسىسىدىن) پاكتۇر. ئۇنداق ئەمەس، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر، ھەممىسى ئۇنىڭغا بويسۇنغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,117,'2:117','ئاللاھ ئاسمانلار ۋە زېمىننى يوقتىن بار قىلغۇچىدۇر. بىر ئىشنى ۋۇجۇدقا كەلتۈرۈشنى ئىرادە قىلسا، «ۋۇجۇدقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ.');
INSERT INTO "saleh" VALUES(2,118,'2:118','بىلمەيدىغانلار (قۇرەيش كاپىرلىرى): «ئاللاھ نېمە ئۈچۈن بىز بىلەن سۆزلەشمەيدۇ؟ ياكى نېمە ئۈچۈن بىزگە (راست پەيغەمبەرلىكنىڭ دەلىلى سۈپتىدە) بىرەر مۆجىزە كەلمەيدۇ؟» دەيدۇ. ئۇلاردىن بۇرۇن ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) شۇنداق سۆزنى قىلغان ئىدى؛ ئۇلارنىڭ دىللىرى بىر ـ بىرىگە ئوخشايدۇ. ھەقىقەتەن بىز ئايەتلىرىمىزنى جەزمەن ئىشىنىدىغان قەۋم ئۈچۈن بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(2,119,'2:119','بىز سېنى ھەقىقەتەن (مۆمىنلەرگە جەننەت بىلەن) خۇشخەۋەر بەرگۈچى، (كاپىرلارنى دوزاخ ئازابىدىن) ئاگاھلاندۇرغۇچى قىلىپ ھەق (دىن) بىلەن ئەۋەتتۇق. سەن ئەھلى دوزاخ (يەنى كاپىرلاردىن ئىمان ئېيتمىغانلار) توغرىسىدا جاۋابكار ئەمەسسەن.');
INSERT INTO "saleh" VALUES(2,120,'2:120','سەن يەھۇدىيلار ۋە ناسارالارنىڭ دىنىغا ئەگەشمىگۈچە ئۇلار سەندىن ھەرگىزمۇ رازى بولمايدۇ. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «توغرا يول پەقەت ئاللاھنىڭ يولىدۇر». ساڭا ئىلىم كەلگەندىن كېيىن (يەنى پاكىتلىق ھەقىقەت ساڭا ئاشكارا بولغاندىن كېيىن)، ئەگەر سەن ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگىشىدىغان بولساڭ، (سېنى ئاللاھنىڭ ئازابىدىن قۇتقۇزىدىغان) ھېچقانداق دوستمۇ بولمايدۇ، مەدەتكارمۇ بولمايدۇ.');
INSERT INTO "saleh" VALUES(2,121,'2:121','بىز ئاتا قىلغان كىتابنى تېگىشلىك رەۋىشتە ئوقۇيدىغانلار بار، ئەنە شۇلار ئۇنىڭغا ئىشىنىدۇ. كىملەركى كىتابنى (قۇرئاننى) ئىنكار قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(2,122,'2:122','ئى ئىسرائىل ئەۋلادى! سىلەرگە بەرگەن نېمىتىمنى ۋە سىلەرنى (بىر زامانلاردا) ھەممە جاھان ئەھلىدىن ئۈستۈن قىلغانلىقىمنى ياد ئېتىڭلار.');
INSERT INTO "saleh" VALUES(2,123,'2:123','بىراۋغا بىراۋ قىلچىلىك ياردەم قىلالمايدىغان، ھېچ كىشىنىڭ فىدىيىسى قوبۇل قىلىنمايدىغان، ھېچ كىشىنىڭ شاپائىتى پايدا قىلمايدىغان، ئۇلارغا (يەنى كاپىرلار ۋە گۇناھكارلارغا) ياردەم قىلىنمايدىغان كۈندىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(2,124,'2:124','ئۆز ۋاقتىدا ئىبراھىمنى پەرۋەردىگارى بىر قانچە ئەمىرلەر (شەرىئەت تەكلىپلىرى) بىلەن سىنىدى، ئىبراھىم ئۇلارنى بەجا كەلتۈردى. ئاللاھ (ئۇنىڭغا): «سېنى چوقۇم كىشىلەرگە (دىندا) پېشۋا قىلىمەن» دېدى، ئىبراھىم: «مېنىڭ بىر قىسىم ئەۋلادىمنىمۇ پېشۋا قىلساڭ» دېدى. ئاللاھ: «مېنىڭ (پېشۋا قىلىش) ئەھدەمگە زالىملار (يەنى كاپىرلار) ئېرىشەلمەيدۇ» دېدى.');
INSERT INTO "saleh" VALUES(2,125,'2:125','ئۆز ۋاقتىدا بىز كەبىنى كىشىلەر ئۈچۈن جەم بولىدىغان ۋە تىنچ جاي قىلىپ بەردۇق. (كىشىلەرگە) ماقامى ئىبراھىمنى نامازگاھ قىلىڭلار (يەنى شۇ يەردە ناماز ئوقۇڭلار دېدۇق). ئىبراھىم بىلەن ئىسمائىلغا ئۆيۈمنى (يەنى كەبەمنى) تاۋاپ قىلغۇچىلار، ئېتىكاپتا ئولتۇرغۇچىلار، رۇكۇ قىلغۇچىلار ۋە سەجدە قىلغۇچىلار ئۈچۈن پاك تۇتۇشنى بۇيرۇدۇق.');
INSERT INTO "saleh" VALUES(2,126,'2:126',' ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم! بۇ يەرنى (مەككىنى) تېنىچ شەھەر قىلىپ بەرگىن، ئاھالىسىدىن ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقانلارنى تۈرلۈك مېۋىلەر بىلەن رىزىقلاندۇرغىن» دېدى. ئاللاھ: «كاپىر بولغان ئادەمنىمۇ (رىزىقتىن) ئازغىنا مۇددەت (ھاياتلىقىدا) بەھرىمەن قىلىمەن، ئاندىن كېيىن ئۇنى (ئاخىرەتتە) دوزاخقا ھەيدەيمەن» دېدى. (بۇ) نېمىدېگەن يامان ئاقىۋەت!');
INSERT INTO "saleh" VALUES(2,127,'2:127','ئۆز ۋاقتىدا ئىبراھىم بىلەن ئىسمائىل كەبىنىڭ ئۇلىنى قوپۇرۇۋېتىپ: «پەرۋەردىگارىمىز! بىزنىڭ (خىزمىتىمىزنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمىزنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمىزنى) بىلىپ تۇرغۇچىسەن. ');
INSERT INTO "saleh" VALUES(2,128,'2:128','پەرۋەردىگارىمىز! ئىككىمىزنى ئۆزۈڭگە ئىتائەتمەن قىلغىن، بىزنىڭ ئەۋلادلىرىمىزدىنمۇ ئۆزۈڭگە ئىتائەتمەن ئۈممەت چىقارغىن، بىزگە ھەجىمىزنىڭ قائىدىلىرىنى بىلدۈرگىن، تەۋبىمىزنى قوبۇل قىلغىن، چۈنكى سەن تەۋبىنى ناھايىتى قوبۇل قىلغۇچىسەن، ناھايىتى مېھرىبانسەن. ');
INSERT INTO "saleh" VALUES(2,129,'2:129','پەرۋەردىگارىمىز! ئۇلارنىڭ ئىچىدىن ئايەتلىرىڭنى ئۇلارغا تىلاۋەت قىلىپ بېرىدىغان، كىتابىڭنى (قۇرئاننى)، ھېكمەتنى (يەنى سەھىھ سۈننەتنى) ئۇلارغا ئۆگىتىدىغان، ئۇلارنى (مۇشرىكلىك ۋە گۇناھلاردىن...) پاك قىلىدىغان بىر پەيغەمبەر ئەۋەتكىن، ھەقىقەتەن سەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن» دېدى.');
INSERT INTO "saleh" VALUES(2,130,'2:130','ئۆزىنى ئەخمەق قىلغانلاردىن باشقا كىم ئىبراھىمنىڭ دىنىدىن يۈز ئۆرۈيدۇ؟ ھەقىقەتەن بىز ئۇنى بۇ دۇنيادا (پەيغەمبەرلىككە) تاللىدۇق. شەك ـ شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ.');
INSERT INTO "saleh" VALUES(2,131,'2:131','ئۆز ۋاقتىدا پەرۋەردىگارى ئۇنىڭغا: «(پەرۋەردىگارىڭنىڭ ئەمرىگە) ئىتائەت قىلغىن» دېدى. ئۇ: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىتائەت قىلدىم» دېدى.');
INSERT INTO "saleh" VALUES(2,132,'2:132','ئىبراھىم ۋە يەئقۇب ئۆز ئوغۇللىرىغا ۋەسىيەت قىلىپ: «ئى ئوغۇللىرىم! ئاللاھ سىلەرگە مۇشۇ دىنىنى (يەنى ئىسلام دىنىنى) تاللىدى، پەقەت مۇسۇلمان پېتىڭلارچە ئۆلۈڭلار (يەنى ئىمانىڭلاردا مەھكەم تۇرۇڭلار، تاكى ئىمان بىلەن كېتىڭلار)» دېدى.');
INSERT INTO "saleh" VALUES(2,133,'2:133','يەئقۇب جان ئۈزۈۋاتقان ۋاقتىدا سىلەر يېنىدا بارمىدىڭلار؟ شۇ چاغدا يەئقۇب ئوغۇللىرىدىن: «مەن ئۆلگەندىن كېيىن سىلەر كىمگە ئىبادەت قىلىسىلەر؟» دەپ سورىدى. ئۇلار: «سېنىڭ ئىلاھىڭ ۋە ئاتا ـ بوۋىلىرىڭ ئىبراھىم، ئىسمائىل، ئىسھاقلارنىڭ ئىلاھى بولغان بىر ئاللاھقا ئىبادەت قىلىمىز، بىز ئۇنىڭغا ئىتائەت قىلغۇچىمىز» دېدى.');
INSERT INTO "saleh" VALUES(2,134,'2:134','ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى ئۆزلىرى ئۈچۈندۇر. سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس. ');
INSERT INTO "saleh" VALUES(2,135,'2:135','ئۇلار: «يەھۇدىي ياكى ناسارا بولۇڭلار، توغرا يول تاپىسىلەر» دەيدۇ. «ئۇنداق ئەمەس، بىز ئىبراھىمنىڭ ئىسلامغا ئۇيغۇن دىنىغا ئەگىشىمىز، ئۇ مۇشرىكلاردىن ئەمەس ئىدى» دېگىن.');
INSERT INTO "saleh" VALUES(2,136,'2:136','ئېيتىڭلاركى، «ئاللاھقا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغان ۋەھىيگە، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھىيگە، مۇساغا بېرىلگەن (تەۋراتقا)، ئىيساغا بېرىلگەن (ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەن (كىتابلارغا) ئىمان ئېيتتۇق، ئۇلاردىن ھېچبىرىنى ئايرىۋەتمەيمىز (يەھۇدىيلار ۋە ناسارالارغا ئوخشاش، ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ بەزىسىنى ئىنكار قىلمايمىز)، بىز ئاللاھقىلا بويسۇنغۇچىلارمىز».');
INSERT INTO "saleh" VALUES(2,137,'2:137','ئەگەر ئۇلار (يەنى ئەھلى كىتاب) سىلەر ئىمان ئېيتقاندەك ئىمان ئېيتسا، ئەلۋەتتە توغرا يول تاپقان بولىدۇ. ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (سەن بىلەن) ئاداۋەتتە بولغان بولىدۇ، ئۇلارغا قارشى ئاللاھ ساڭا كۇپايىدۇر. ئاللاھ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ھىيلە ـ مىكرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,138,'2:138',' ئاللاھنىڭ دىنىدا چىڭ تۇرۇڭلار، ئاللاھنىڭ دىنىدىن ياخشى كىمنىڭ دىنى بار؟ بىز ئاللاھقىلا ئىبادەت قىلغۇچىلارمىز.');
INSERT INTO "saleh" VALUES(2,139,'2:139','«بىز بىلەن ئاللاھ توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ ھالبۇكى، ئۇ ھەم بىزنىڭ پەرۋەردىگارىمىز، ھەم سىلەرنىڭ پەرۋەردىگارىڭلاردۇر. بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، بىز ئاللاھقىلا سادىقمىز» دېگىن.');
INSERT INTO "saleh" VALUES(2,140,'2:140','«ياكى ئىبراھىم، ئىسمائىل، ئىسھاق، يەئقۇب ۋە ئۇنىڭ ئەۋلادلىرى يەھۇدىي ئىدى ياكى ناسارا ئىدى» دەمسىلەر؟ ئېيتقىنكى، «(ئۇلارنىڭ قايسى دىندا ئىكەنلىكىنى) سىلەر ئوبدان بىلەمسىلەر؟ ياكى ئاللاھ ئوبدان بىلەمدۇ؟ قولىدا ئاللاھ نازىل قىلغان دەلىل ـ ئىسپات تۇرۇپ ئۇنى يوشۇرغان ئادەمدىن زالىم كىم بار؟ ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر».');
INSERT INTO "saleh" VALUES(2,141,'2:141','ئۇلار ئۆتكەن بىر ئۈممەتتۇر، ئۇلارنىڭ ئەمەللىرى ئۆزلىرى ئۈچۈندۇر، سىلەرنىڭ ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر. ئۇلارنىڭ قىلمىشىغا سىلەر جاۋابكار ئەمەس.');
INSERT INTO "saleh" VALUES(2,142,'2:142','بەزى ئەخمەق كىشىلەر: «ئۇلارنى (يەنى پەيغەمبەر ئەلەيھىسسالام بىلەن مۆمىنلەرنى) قاراپ كېلىۋاتقان قىبلىسىدىن (يەنى بەيتۇلمۇقەددەستىن) نېمە يۈز ئۆرۈگۈزگەندۇ؟» دەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، «مەشرىق ۋە مەغرىب (ھەممە تەرەپ) ئاللاھنىڭدۇر، ئاللاھ خالىغان كىشىنى توغرا يولغا باشلايدۇ».');
INSERT INTO "saleh" VALUES(2,143,'2:143','شۇنىڭدەك (يەنى سىلەرنى ئىسلامغا ھىدايەت قىلغاندەك) كىشىلەرگە (يەنى ئۆتكەنكى ئۈممەتلەرگە) شاھىت بولۇشۇڭلار ئۈچۈن ۋە پەيغەمبەرنىڭ سىلەرگە شاھىت بولۇشى ئۈچۈن، بىز سىلەرنى ياخشى ئۈممەت قىلدۇق. سەن يۈز كەلتۈرۈپ كېلىۋاتقان تەرەپنى (يەنى بەيتۇلمۇقەددەسنى) قىبلە قىلغانلىقىمىز پەيغەمبەرگە ئەگەشكەنلەرنى ئاسىيلىق قىلغانلاردىن (يەنى مۇرتەد بولۇپ كەتكەنلەردىن) ئايرىۋېلىشىمىز ئۈچۈنلا ئىدى. ئاللاھ ھىدايەت قىلغانلاردىن باشقىلارغا بۇ (يەنى قىبلىنىڭ ئۆزگەرتىلىشى) ھەقىقەتەن ئېغىردۇر. ئاللاھ سىلەرنىڭ ئىمانىڭلارنى (يەنى ئىلگىرى بەيتۇلمۇقەددەسكە قاراپ ئوقۇغان نامىزىڭلارنى) بىكار قىلىۋەتمەيدۇ. ئاللاھ كىشىلەرگە ھەقىقەتەن مېھرىباندۇر، ناھايىتى كۆيۈمچاندۇر.');
INSERT INTO "saleh" VALUES(2,144,'2:144','بىز سېنىڭ (كەبە قىبلەڭ بولۇشىنى تىلەپ) قايتا ـ قايتا ئاسمانغا قارىغانلىقىڭنى كۆرۈپ تۇرۇۋاتىمىز. سېنى چوقۇم سەن ياقتۇرىدىغان قىبلىگە يۈزلەندۈرىمىز. (نامازدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) قەيەردە بولماڭلار (نامازدا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. ھەقىقەتەن كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) بۇنىڭ (يەنى قىبلىنىڭ كەبىگە ئۆزگەرتىلىشىنىڭ) پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى چوقۇم بىلىدۇ، ئاللاھ ئۇلارنىڭ قىلمىشىدىن غاپىل ئەمەس.');
INSERT INTO "saleh" VALUES(2,145,'2:145','كىتاب بېرىلگەنلەرگە بارچە دەلىلنى كەلتۈرسەڭمۇ، ئۇلار سېنىڭ قىبلەڭگە بويسۇنمايدۇ، سەنمۇ ئۇلارنىڭ قىبلىسىگە بويسۇنمايسەن؛ ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) ھەم ئۆز ئارا بىر ـ بىرىنىڭ قىبلىسىگە بويسۇنمايدۇ. ساڭا ئىلىم (ۋەھىي) كەلگەندىن كېيىن، مۇبادا ئۇلارنىڭ نەپسى - خاھىشلىرىغا بويسۇنساڭ، ئۇ چاغدا سەن چوقۇم زۇلۇم قىلغانلاردىن بولىسەن.');
INSERT INTO "saleh" VALUES(2,146,'2:146','بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار بىلەن ناسارالار) ئۇنى (يەنى پەيغەمبەرنى) ئۆز ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ. ئۇلارنىڭ بىر تۈركۈمى، شۈبھىسىزكى، ھەقىقەتنى بىلىپ تۇرۇپ يوشۇرىدۇ.');
INSERT INTO "saleh" VALUES(2,147,'2:147','ھەقىقەت ئاللاھ تەرىپىدىن كەلگەندۇر، شۇڭا ھەرگىز شەك كەلتۈرگۈچىلەردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(2,148,'2:148','ھەر (ئۈممەت) نىڭ يۈز كەلتۈرىدىغان تەرىپى (قىبلىسى) بار، (ئى مۆمىنلەر!) ياخشى ئەمەللەرنى قىلىشقا ئالدىراڭلار، قەيەردە بولماڭلار، (قىيامەت كۈنى) ئاللاھ ھەممىڭلارنى (ھېساب ئۈچۈن) يىغىدۇ، ئاللاھ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(2,149,'2:149','(قەيەردىن سەپەرگە چىقساڭ، نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم (كەبە) تەرەپكە قىلغىن؛ بۇ، شەكسىز پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر، ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن غاپىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(2,150,'2:150','ئىنسانلار (يەھۇدىيلار ۋە مۇشرىكلار) غا سىلەرگە قارشى دەلىل ـ ئىسپات بولماسلىقى ئۈچۈن، (ئى مۇھەممەد! سەپەر قىلىشقا) قەيەردىن چىقمىغىن، (نامىزىڭدا) يۈزۈڭنى مەسجىدى ھەرەم تەرەپكە قىلغىن. (ئى مۆمىنلەر!) سىلەرمۇ قەيەردە بولماڭلار، (نامىزىڭلاردا) يۈزۈڭلارنى مەسجىدى ھەرەم تەرەپكە قىلىڭلار. پەقەت ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلارلا (قىبلىنىڭ ئۆزگەرگەنلىكىنى سىلەرگە قارشى دەلىل قىلىۋالىدۇ)، ئۇلاردىن (يەنى زالىملاردىن) قورقماڭلار، (مېنىڭ ئەمرىمنى تۇتۇش بىلەن) مەندىن قورقۇڭلار. (قىبلىنى ئۆزگەرتىشكە ئەمر قىلىشىم) سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزۈشۈم ۋە توغرا يولدا بولۇشۇڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(2,151,'2:151','شۇنىڭدەك (سىلەرگە بەرگەن نېمىتىمنى كامالەتكە يەتكۈزگىنىمدەك) ئۆز ئىچىڭلاردىن سىلەرگە بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ بېرىدىغان، سىلەرنى (مۇشرىكلىك ۋە گۇناھتىن) پاك قىلىدىغان، سىلەرگە كىتابنى (قۇرئاننى) ۋە ھېكمەتنى (يەنى سەھىھ سۈننەتني) ئۆگىتىدىغان، سىلەرگە بىلمىگەنلىرىڭلارنى بىلدۈرىدىغان بىر پەيغەمبەر ئەۋەتتۇق. ');
INSERT INTO "saleh" VALUES(2,152,'2:152','مېنى (تائەت ـ ئىبادەت بىلەن) ياد ئېتىڭلار، (مېنى ياد ئەتسەڭلار) مەنمۇ سىلەرنى (ساۋاب بېرىش بىلەن، مەغپىرەت قىلىش بىلەن) ياد ئېتىمەن. ماڭا شۈكۈر قىلىڭلار، ناشۈكۈرلۈك قىلماڭلار.');
INSERT INTO "saleh" VALUES(2,153,'2:153',' ئى مۆمىنلەر! سەۋر ۋە ناماز ئارقىلىق ياردەم تىلەڭلار. ئاللاھ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر. ');
INSERT INTO "saleh" VALUES(2,154,'2:154','ئاللاھنىڭ يولىدا ئۆلتۈرۈلگەنلەرنى (يەنى شەھىدلەرنى) ئۆلۈك دېمەڭلار، بەلكى ئۇلار تىرىكتۇر، لېكىن سىلەر بۇنى سەزمەيسىلەر.');
INSERT INTO "saleh" VALUES(2,155,'2:155',' بىز سىلەرنى بىرئاز قورقۇنچ، بىرئاز قەھەتچىلىك، ماللىرىڭلار، جانلىرىڭلار، بالىلىرىڭلار ۋە زىرائەتلىرىڭلارغا يېتىدىغان زىيان بىلەن چوقۇم سىنايمىز. (بېشىغا كەلگەن مۇسىبەت ۋە زىيان ـ زەخمەتلەرگە) سەۋر قىلغۇچىلارغا (جەننەت بىلەن) خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(2,156,'2:156','ئۇلارغا بىرەر مۇسىبەت كەلگەن چاغدا، ئۇلار: «بىز ئەلۋەتتە ئاللاھنىڭ ئىگىدارچىلىقىدىمىز (ئاللاھنىڭ بەندىلىرىمىز)، چوقۇم ئاللاھنىڭ دەرگاھىغا قايتىمىز» دەيدۇ.');
INSERT INTO "saleh" VALUES(2,157,'2:157','ئەنە شۇلار پەرۋەردىگارىنىڭ مەغپىرىتى ۋە رەھمىتىگە ئېرىشكۈچىلەردۇر، ئەنە شۇلار ھىدايەت تاپقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(2,158,'2:158',' سەفا بىلەن مەرۋە ھەقىقەتەن ئاللاھنىڭ (دىنىنىڭ) ئالامەتلىرىدىندۇر. ھەج قىلغان ياكى ئۆمرە قىلغان ئادەم ئۇلارنىڭ ئارىسىدا سەئيى قىلسا ھېچ گۇناھ يوق. كىمكى ئۆز ئىختىيارى بىلەن بىرەر ياخشىلىق قىلىدىكەن (ئاللاھ ئۇنىڭ مۇكاپاتىنى بېرىدۇ)، ئاللاھ ئەلۋەتتە شۈكۈرنىڭ مۇكاپاتىنى بەرگۈچىدۇر، ئاللاھ ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,159,'2:159',' بۇ كىتابتا (يەنى تەۋراتتا) كىشىلەرگە توغرا يولنى ئېنىق بايان قىلغىنىمىزدىن كېيىن، بىز نازىل قىلغان (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان) روشەن دەلىللەرنى ۋە توغرا يولنى يوشۇرىدىغانلارغا ئاللاھ لەنەت قىلىدۇ (يەنى رەھمىتىدىن يىراق قىلىدۇ)؛ لەنەت قىلغۇچىلار (پەرىشتىلەر ۋە مۆمىنلەر) مۇ ئۇلارغا لەنەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,160,'2:160','پەقەت تەۋبە قىلغانلار، (ئەمەلىنى) تۈزەتكەنلەر (يەنى يەھۇدىيلارنىڭ تەۋرات ئەھكاملىرىدىن يوشۇرغانلىرىنى) بايان قىلغانلارلا بۇنىڭدىن مۇستەسنا. ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىمەن، مەن تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇرمەن. ناھايىتى مېھرىباندۇرمەن.');
INSERT INTO "saleh" VALUES(2,161,'2:161','شۈبھىسىزكى، كاپىر بولۇپ، كاپىر پېتى ئۆلگەنلەر بار، ئەنە شۇلار چوقۇم ئاللاھنىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچرايدۇ. ');
INSERT INTO "saleh" VALUES(2,162,'2:162','ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ، ئۇلارغا بېرىلىدىغان ئازاب كېچىكتۈرۈلمەيدۇ.');
INSERT INTO "saleh" VALUES(2,163,'2:163','سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر؛ ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,164,'2:164','ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىشىدە، كىشىلەرگە پايدىلىق نەرسىلەرنى ئېلىپ دېڭىزدا ئۈزۈپ يۈرگەن كېمىلەردە، ئاللاھ بۇلۇتتىن ياغدۇرۇپ بەرگەن، ئۆلگەن زېمىننى تىرىلدۈرىدىغان يامغۇردا، يەر يۈزىگە تارقىتىۋەتكەن ھايۋاناتلاردا، شامال يۆنۈلۈشلىرىنىڭ ئۆزگىرىپ تۇرۇشىدا، ئاسمان ـ زېمىن ئارىسىدا بويسۇندۇرۇلغان بۇلۇتلاردا، چۈشىنىدىغان كىشىلەر ئۈچۈن ئەلۋەتتە (ئاللاھنىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىللەر باردۇر.');
INSERT INTO "saleh" VALUES(2,165,'2:165','بەزى ئادەملەر ئاللاھتىن غەيرىلەرنى ئاللاھقا شېرىك قىلىۋالىدۇ، ئۇلارنى (مۆمىنلەرنىڭ) ئاللاھنى دوست تۇتقىنىدەك دوست تۇتىدۇ، (يەنى ئۇلۇغلايدۇ ۋە ئۇلارغا بويسۇنىدۇ). مۆمىنلەر ئاللاھنى ھەممىدىن بەك دوست تۇتقۇچىلاردۇر، زالىملار (قىيامەت كۈنى ئۆزلىرىگە تەييارلانغان) ئازابنى كۆرگەن چاغدا پۈتۈن كۈچ ـ قۇۋۋەتنىڭ ئاللاھقا مەنسۇپ ئىكەنلىكىنى ۋە ئاللاھنىڭ ئازابىنىڭ قاتتىق ئىكەنلىكىنى كاشكى بىلسە ئىدى (ئاللاھقا شېرىك كەلتۈرمىگەن بولاتتى).');
INSERT INTO "saleh" VALUES(2,166,'2:166','ئەينى ۋاقىتتا ئەگەشتۈرگۈچىلەر ئازابنى كۆرۈپ، ئەگەشكۈچىلەردىن ئادا ـ جۇدا بولىدۇ. ئۇلارنىڭ ئۆزئارا مۇناسىۋىتى (يەنى دۇنيادىكى دوستلۇقى) ئۈزۈلىدۇ.');
INSERT INTO "saleh" VALUES(2,167,'2:167','ئەگەشكۈچىلەر: «كاشكى بىزلەرگە (دۇنياغا) قايتىشقا بولسا ئىدى، ئۇلار بىزدىن ئادا ـ جۇدا بولغاندەك بىزمۇ ئۇلاردىن ئادا ـ جۇدا بولاتتۇق» دەيدۇ. ئاللاھ ئۇلارغا (يامان) ئىشلىرىنىڭ ھەسرەت بولغانلىقىنى مۇشۇنداق كۆرسىتىدۇ، ئۇلار دوزاختىن چىقمايدۇ.');
INSERT INTO "saleh" VALUES(2,168,'2:168','ئى ئىنسانلار! زېمىندىكى ھالال ـ پاكىز نەرسىلەردىن يەڭلار، شەيتاننىڭ يوللىرىغا ئەگەشمەڭلار، چۈنكى شەيتان سىلەرگە ئوچۇق دۈشمەندۇر.');
INSERT INTO "saleh" VALUES(2,169,'2:169','شەيتان سىلەرنى ھەقىقەتەن يامانلىقلارغا، قەبىھ گۇناھلارغا ۋە ئاللاھ نامىدىن، يالغاندىن (ئاللاھ سىلەرگە ھالال قىلغان نەرسىلەرنى ھارام دەپ ۋە ھارام قىلغان نەرسىلەرنى ھالال دەپ) سىلەر بىلمەيدىغان نەرسىلەرنى ئېيتىشقا بۇيرۇيدۇ.');
INSERT INTO "saleh" VALUES(2,170,'2:170','ھەرقاچان ئۇلارغا (يەنى مۇشرىكلارغا): «ئاللاھ نازىل قىلغان نەرسىگە (يەنى ۋەھىيگە) ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، ئاتا ـ بوۋىلىرىمىزدىن قالغان دىنغا ئەگىشىمىز» دېيىشىدۇ. ئاتا ـ بوۋىلىرى ھېچ نەرسىنى چۈشەنمىگەن ۋە توغرا يولدا بولمىغان تۇرسا، (يەنە ئۇلارغا ئەگىشەمدۇ؟)');
INSERT INTO "saleh" VALUES(2,171,'2:171','كاپىرلارنى ھىدايەتكە دەۋەت قىلغۇچى خۇددى مەنىسىنى چۈشەنمەي تاۋۇشىنى ۋە نىدانىلا ئاڭلايدىغان چاھارپايلارنى چاقىرغان ئادەمگە ئوخشايدۇ، (كاپىرلار ھەقنى ئاڭلاشتىن) گاستۇر، (ھەقنى سۆزلەشتىن) گاچىدۇر، (ھەقنى كۆرۈشتىن) كوردۇر، ئۇلار (ۋەز ـ نەسىھەتنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(2,172,'2:172','ئى مۆمىنلەر! بىز سىلەرنى رىزىقلاندۇرغان ھالال نەرسىلەردىن يەڭلار، ئەگەر ئاللاھقىلا ئىبادەت قىلىدىغان بولساڭلار، ئاللاھقا (يەنى ئاللاھنىڭ ھەددى ـ ھېسابسىز نېمەتلىرىگە) شۈكۈر قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,173,'2:173','ئاللاھ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، ئاللاھتىن غەيرىنىڭ (يەنى بۇتلارنىڭ) نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى. كىمكى ئۆز ئىختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك يېسە، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ. ھەقىقەتەن ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,174,'2:174','ئاللاھ كىتابتا نازىل قىلغان نەرسىلەرنى يوشۇرىدىغان ۋە ئۇنى ئازغىنا پۇلغا ساتىدىغانلارنىڭ قارنىغا يېگىنى پەقەت ئوتتۇر (چۈنكى ئۇلارنىڭ يېگەن شۇ ھارام مېلى ئۇلارنى دوزاخقا ئېلىپ بارىدۇ)، قىيامەت كۈنى ئاللاھ ئۇلارغا (ئۇلارنى خۇشال قىلىدىغان) سۆز قىلمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,175,'2:175','ئەنە شۇلار ھىدايەتنى گۇمراھلىققا، مەغپىرەتنى ئازابقا (يەنى جەننەتنى دوزاخقا) تېگىشتى، ئۇلار دوزاخ ئازابىغا نېمىدېگەن چىداملىق!');
INSERT INTO "saleh" VALUES(2,176,'2:176','بۇ (قاتتىق ئازاب) شۇنىڭ ئۈچۈنكى، ئاللاھ كىتابنى ھەقىقەت بىلەن نازىل قىلدى، كىتاب توغرىسىدا ئىختىلاپ قىلغۇچىلار چۇڭقۇر نىزادىدۇر.');
INSERT INTO "saleh" VALUES(2,177,'2:177','سىلەرنىڭ كۈن چىققان ۋە كۈن پاتقان تەرەپكە يۈز كەلتۈرۈشۈڭلارنىڭ ئۆزىلا ياخشى ئەمەلگە ياتمايدۇ. بەلكى ئاللاھقا، ئاخىرەت كۈنىگە، پەرىشتىلەرگە، كىتابقا (يەنى ئاللاھ نازىل قىلغان كىتابلارغا)، پەيغەمبەرلەرگە ئىمان كەلتۈرۈش، ئاللاھنى سۆيۈش يۈزىسىدىن خىش ـ ئەقرىبالارغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە (يەنى پۇل ـ مېلىدىن ئالاقىسى ئۈزۈلۈپ قالغان مۇساپىرلارغا)، سائىللارغا ۋە قۇللارنىڭ ئازادلىققا ئېرىشىشىگە پۇل ـ مال ياردەم بېرىش، ناماز ئوقۇش، زاكات بېرىش، ئەھدىگە ۋاپا قىلىش، يوقسۇزلۇققا، كېسەللىككە ۋە (ئاللاھنىڭ يولىدا قىلىنغان) ئۇرۇشقا بەرداشلىق بېرىش ياخشى ئەمەلگە كىرىدۇ. ئەنە شۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) (ئىمانىدا) راستچىل ئادەملەردۇر، ئەنە شۇلار تەقۋادار ئادەملەردۇر. ');
INSERT INTO "saleh" VALUES(2,178,'2:178','ئى مۆمىنلەر! ئۆلتۈرۈلگەنلەر ئۈچۈن قىساس ئېلىش سىلەرگە پەرز قىلىندى، ھۆر ئادەم ئۈچۈن ھۆر ئادەمدىن، قۇل ئۈچۈن قۇلدىن، ئايال ئۈچۈن ئايالدىن قىساس ئېلىنىدۇ. قاتىل ئۈچۈن (دىنىي) قېرىندىشى تەرىپىدىن بىر نەرسە كەچۈرۈم قىلىنسا (ئۆلتۈرۈلگۈچىنىڭ ئىگىسى قاتىلدىن دىيەت ئېلىشقا رازى بولۇپ قىساس ئېلىشتىن ۋاز كەچسە، ئۇ، جىنايەتچىدىن دىيەتنى) چىرايلىقچە تەلەپ قىلىشى لازىم، (قاتىلمۇ دىيەتنى) ياخشىلىقچە (كېچىكتۈرمەستىن، كېمەيتىۋەتمەستىن تولۇق) بېرىشى لازىم. بۇ (ھۆكۈم) پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە بېرىلگەن) يېنىكلىتىشتۇر ۋە رەھمەتتۇر. شۇنىڭدىن كېيىن (يەنى دىيەتنى قوبۇل قىلغاندىن كېيىن)، چېكىدىن ئېشىپ كەتكەن (يەنى قاتىلنى ئۆلتۈرگەن) ئادەم قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(2,179,'2:179','ئى ئەقىل ئىگىلىرى! سىلەرگە قىساستا ھاياتلىق بار، (يەنى كىشى بىراۋنى ئۆلتۈرگەن تەقدىردە ئۆزىنىڭمۇ ئۆلتۈرۈلىدىغانلىقىنى بىلسە، ئۇ، ئادەم ئۆلتۈرۈشتىن يانىدۇ، شۇنىڭ بىلەن، ئۇنىڭ ئۆزىمۇ، ئۇ ئۆلتۈرمەكچى بولغان ئادەممۇ ئۆلۈشتىن ساقلىنىپ قالىدۇ). (ناھەق قان تۆكۈشتىن) ساقلىنىشىڭلار ئۈچۈن (قىساس يولغا قويۇلدى).');
INSERT INTO "saleh" VALUES(2,180,'2:180','ئەگەر سىلەردىن بىرەيلەن ئۆلۈش ئالدىدا كۆپ مال قالدۇرسا، ئۇنىڭ ئاتا ـ ئانىسىغا ۋە خىش ـ ئەقرىبالىرىغا ئادىللىق بىلەن ۋەسىيەت قىلىشى تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك بۇرچى سۈپىتىدە پەرز قىلىندى.');
INSERT INTO "saleh" VALUES(2,181,'2:181','ۋەسىيەتنى ئاڭلىغاندىن كىيىن، كىمكى ئۇنى ئۆزگەرتىدىكەن، ئۇنىڭ گۇناھى ئۆزگەرتكەنلەرگە يۈكلىنىدۇ. ئاللاھ ھەقىقەتەن ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,182,'2:182','كىمكى ۋەسىيەت قىلغۇچىنىڭ سەۋەنلىكى ياكى قەستەن ناتوغرا قىلغانلىقىنى بىلىپ، ئۇلارنىڭ (يەنى ۋەسىيەت قىلغۇچىلار بىلەن ۋەسىيەت قىلىنغۇچىلارنىڭ) ئارىسىنى تۈزەپ قويسا، ئۇنىڭغا ھېچ گۇناھ بولمايدۇ. ئاللاھ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,183,'2:183','ئى مۆمىنلەر! (گۇناھلاردىن) ساقلىنىشىڭلار ئۈچۈن، سىلەردىن ئىلگىرىكىلەرگە (يەنى ئىلگىرىكى ئۈممەتلەرگە) روزا پەرز قىلىنغاندەك، سىلەرگىمۇ (رامىزان) روزىسى پەرز قىلىندى.');
INSERT INTO "saleh" VALUES(2,184,'2:184','(بۇ پەرز قىلىنغان روزا) ساناقلىق كۈنلەردۇر، سىلەردىن كىمكى كېسەل ياكى سەپەر ئۈستىدە بولۇپ (روزا تۇتمىغان بولسا)، تۇتمىغان كۈنلەرنى (يەنى قازاسىنى) باشقا كۈنلەردە تۇتسۇن، روزىنى (قېرىلىق ياكى ئاجىزلىق تۈپەيلىدىن مۇشەققەت بىلەن) ئاران تۇتىدىغان كىشىلەر تۇتمىسا، (كۈنلۈكى ئۈچۈن) بىر مىسكىن تويغۇدەك تاماق فىدىيە بېرىشى لازىم. كىمكى فىدىيىنى (بەلگىلەنگەن مىقداردىن) ئارتۇق بەرسە، بۇ ئۆزى ئۈچۈن ياخشىدۇر. ئەگەر بىلسەڭلار، روزا تۇتۇش سىلەر ئۈچۈن (ئېغىز ئوچۇق يۈرۈشتىن ۋە فىدىيە بېرىشتىن) ياخشىدۇر.');
INSERT INTO "saleh" VALUES(2,185,'2:185','رامىزان ئېيىدا قۇرئان نازىل بولۇشقا باشلىدى، قۇرئان ئىنسانلارغا يېتەكچىدۇر، ھىدايەت قىلغۇچى ۋە ھەق بىلەن ناھەقنى ئايرىغۇچى روشەن ئايەتلەردۇر. سىلەردىن كىمكى رامىزان ئېيىدا ھازىر بولسا رامىزان روزىسىنى تۇتسۇن؛ كىمكى كېسەل ياكى سەپەر ئۈستىدە (يەنى مۇساپىر) بولۇپ (تۇتمىغان بولسا، تۇتمىغان كۈنلەر ئۈچۈن) باشقا كۈنلەردە تۇتسۇن. ئاللاھ سىلەرگە ئاسانلىقنى خالايدۇ، تەسلىكنى خالىمايدۇ، (ئاغزىڭلار ئوچۇق يۈرگەن كۈنلەرنىڭ قازاسىنى قىلىش بىلەن رامىزان روزىسىنىڭ) سانىنى تولدۇرۇشۇڭلارنى، سىلەرنى ھىدايەت قىلغانلىقىغا ئاللاھنى ئۇلۇغلىشىڭلارنى، (ئۇنىڭ ئىنئاملىرىغا) شۈكۈر قىلىشىڭلارنى خالايدۇ.');
INSERT INTO "saleh" VALUES(2,186,'2:186','مېنىڭ بەندىلىرىم سەندىن مەن توغرۇلۇق سورىسا (ئۇلارغا ئېيتقىنكى)، مەن ھەقىقەتەن ئۇلارغا يېقىنمەن (يەنى ئۇلارنىڭ ئەھۋالىنى، سۆزلىرىنى بىلىپ تۇرىمەن)، ماڭا دۇئا قىلسا، مەن دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلىمەن، ئۇلار توغرا يول تېپىش ئۈچۈن مېنىڭ دەۋىتىمنى قوبۇل قىلسۇن ۋە ماڭا ئىمان ئېيتسۇن (يەنى ھەمىشە ئىمان بىلەن بولسۇن).');
INSERT INTO "saleh" VALUES(2,187,'2:187','روزا كېچىلىرىدە ئاياللىرىڭلارغا يېقىنچىلىق قىلىش سىلەرگە ھالال قىلىندى، ئۇلار سىلەر ئۈچۈن كىيىمدۇر (يەنى ئۇلار سىلەرگە ئارىلىشىپ ياشايدۇ ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىدۇ)، سىلەرمۇ ئۇلار ئۈچۈن كىيىمسىلەر (يەنى سىلەرمۇ ئۇلارغا ئارىلىشىپ ياشايسىلەر ۋە كىيىم بەدەنگە يېپىشقاندەك يېپىشىسىلەر). ئاللاھ ئۆزۈڭلارنىڭ ئۆزۈڭلارنى ئالدىغانلىقىڭلارنى (يەنى روزا كېچىسى يېقىنچىلىق قىلغىنىڭلارنى) بىلدى. ئاللاھ تەۋبەڭلارنى قوبۇل قىلىپ سىلەرنى ئەپۇ قىلدى. ئەمدى ئۇلارغا (يەنى ئاياللىرىڭلارغا) يېقىنچىلىق قىلىڭلار، ئاللاھ سىلەرگە تەقدىر قىلغان نەرسىنى (يەنى پەرزەنتنى) تەلەپ قىلىڭلار، تاكى تاڭنىڭ ئاق يىپى قارا يىپىدىن ئايرىلغانغا (يەنى تاڭ يورۇغانغا) قەدەر يەڭلار ۋە ئىچىڭلار، ئاندىن كەچ كىرگۈچە روزا تۇتۇڭلار، سىلەر مەسجىدتە ئېتىكاپتا ئولتۇرغان چېغىڭلاردا (كۈندۈز بولسۇن كېچە بولسۇن)، ئۇلارغا يېقىنچىلىق قىلماڭلار، ئەنە شۇلار ئاللاھ بەلگىلىگەن چېگرىلاردۇر (يەنى مەنئى قىلغان ئىشلاردۇر)، ئۇنىڭغا يېقىنلاشماڭلار. كىشىلەرنىڭ (ھارام قىلىنغان ئىشلاردىن) ساقلىنىشلىرى ئۈچۈن، ئاللاھ ئايەتلىرىنى ئۇلارغا مۇشۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,188,'2:188','بىر ـ بىرىڭلارنىڭ ماللىرىنى ناھەق يەۋالماڭلار، بىلىپ تۇرۇپ كىشىلەرنىڭ بىر قىسىم ماللىرىنى زۇلۇم بىلەن يەۋېلىش ئۈچۈن ھاكىملارغا پارا بەرمەڭلار.');
INSERT INTO "saleh" VALUES(2,189,'2:189','(ئى مۇھەممەد!) سەندىن يېڭى چىققان ئاي (يەنى ئاينىڭ كىچىك چىقىپ چوڭىيىدىغانلىقى، ئاندىن كىچىكلەيدىغانلىقى) توغرۇلۇق سورىشىدۇ. (ئۇلارغا) ئېيتقىنكى، «ئۇ كىشىلەرنىڭ (تىرىكچىلىك، سودا ـ سېتىق، مۇئامىلە، روزا تۇتۇش، ئېغىز ئېچىش قاتارلىق) ئىشلارنىڭ ۋە ھەجنىڭ ۋاقىتلىرىنى ھېسابلىشى ئۈچۈندۇر». (جاھىلىيەت دەۋرىدە قىلغىنىڭلاردەك ئېھرامدىكى چېغىڭلاردا) ئۆيلەرنىڭ ئارقىسىدىن (تۆشۈك ئېچىپ) كىرىش ياخشىلىققا ياتمايدۇ. (ئاللاھنىڭ ئەمرىگە مۇخالىپ ئىش قىلىشتىن) قورققان ئادەم ياخشى ئەمەل (ئىگىسى) دۇر. (ئېھرامدىكى چېغىڭلاردا ئادەتتىكىدەك) ئۆيلەرنىڭ دەرۋازىلىرىدىن كىرىڭلار، بەختكە ئېرىشىشڭلار ئۈچۈن ئاللاھقا تەقۋادارلىق قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,190,'2:190','سىلەرگە ئۇرۇش ئاچقان ئادەملەرگە قارشى ئاللاھ يولىدا جىھاد قىلىڭلار، تاجاۋۇز قىلماڭلار، تاجاۋۇز قىلغۇچىلارنى ئاللاھ ھەقىقەتەن دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(2,191,'2:191','ئۇلارنى (يەنى تاجاۋۇزچىلارنى) ئۇچراتقان يەردە ئۆلتۈرۈڭلار، سىلەرنى يۇرتۇڭلاردىن چىقىرىۋەتكەن جايدىن، سىلەرمۇ ئۇلارنى چىقىرىۋېتىڭلار، زىيانكەشلىك قىلىش ئۇرۇشتىنمۇ ياماندۇر. تاكى ئۇلار ئۆزلىرى ئۇرۇش ئاچمىغىچە مەسجىدى ھەرەم ئەتراپىدا ئۇلار بىلەن ئۇرۇش قىلماڭلار، ئەگەر ئۇلار (مەسجىدى ھەرەم ئەتراپىدا) سىلەرگە ئۇرۇش ئاچسا، ئۇلارنى ئۆلتۈرۈۋېرىڭلار. كاپىرلارنىڭ جازاسى شۇنداق بولىدۇ.');
INSERT INTO "saleh" VALUES(2,192,'2:192','ئەگەر ئۇلار ئۇرۇشنى توختاتسا (سىلەرمۇ ئۇرۇشتىن قول يىغىڭلار)، ئاللاھ (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,193,'2:193','تاكى زىيانكەشلىك تۈگىگەن ۋە ئاللاھنىڭ دىنى يولغا قويۇلغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار ئۇرۇشنى توختاتسا، زۇلۇم قىلغۇچىلاردىن باشقىلارغا دۈشمەنلىك قىلىشقا بولمايدۇ.');
INSERT INTO "saleh" VALUES(2,194,'2:194','(ئۇرۇش) ھارام قىلىنغان ئاي (ئۇرۇش) ھارام قىلىنغان ئايغا تەڭدۇر (يەنى ئۇرۇش ھارام قىلىنغان ئايلاردا دۈشمىنىڭلار سىلەرگە چېقىلسا، شۇ ئايلاردا سىلەرمۇ ئۇلارغا چېقىلساڭلار بولىدۇ). ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەر تەڭ ـ باراۋەردۇر( يەنى ھۆرمىتى ساقلىنىشقا تېگىشلىك نەرسىلەرنى باشقىلار دەپسەندە قىلسا، ئىنتىقام ئېلىش يۈزىسىدىن، سىلەرمۇ ئۇلارنى دەپسەندە قىلساڭلار ھېچ يامىنى يوق). بىراۋ سىلەرگە قانچىلىك چېقىلغان بولسا، سىلەرمۇ ئۇنىڭغا شۇنچىلىك چېقىلىڭلار؛ ئاللاھقا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، ئاللاھ تەقۋادارلار بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(2,195,'2:195','ئاللاھنىڭ يولىدا (پۇل ـ مال) سەرپ قىلىڭلار، ئۆزۈڭلارنى ھالاكەتكە تاشلىماڭلار، ئېھسان قىلىڭلار، ئېھسان قىلغۇچىلارنى ئاللاھ ھەقىقەتەن دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(2,196,'2:196','ھەج بىلەن ئۆمرەنى ئاللاھ ئۈچۈن تولۇق ئورۇنلاڭلار، ئەگەر (دۈشمەن ياكى كېسەللىك سەۋەبىدىن) ھەجنى ياكى ئۆمرەنى ئادا قىلىشتا توسقۇنلۇققا ئۇچرىساڭلار، ئۇ ھالدا نېمە ئوڭاي بولسا (يەنى تۆگە، كالا، قويدىن نېمىنى تاپالىساڭلار)، شۇنى قۇربانلىق قىلىڭلار، قۇربانلىق قىلىنىدىغان مال جايىغا (بەلگىلەنگەن جايغا) يەتمىگۈچە چېچىڭلارنى چۈشۈرمەڭلار، سىلەردىن كىمكى كېسەل (يەنى چېچىنى چۈشۈرسە زىيان قىلىدىغان كېسەل) ياكى بېشىدا ئىللەت (يەنى باش ئاغرىقى، پىتقا ئوخشاشلار) بولۇپ (چېچىنى چۈشۈرسە)، فىدىيە قىلىش يۈزىسىدىن (ئۈچ كۈن) روزا تۇتسۇن، ياكى (ئالتە مىسكىنگە ئۈچ سا) سەدىقە بەرسۇن، ياكى (بىرەر مال) قۇربانلىق قىلسۇن، ئەمىنلىكتە بولغان چېغىڭلاردا (ئاراڭلاردىن) ئۆمرە قىلغاندىن كېيىن ھەج قىلىشتىن بەھرىمەن بولغان ئادەم (يەنى ھەجدىن بۇرۇن ئۆمرە قىلىپ تاماملىغان ئادەم) نېمە ئوڭاي بولسا، شۇنى قۇربانلىق قىلسۇن، (پۇلى يوقلۇقتىن ياكى قۇربانلىق مال يوقلۇقىدىن قۇربانلىق مال) تاپالمىغان ئادەم ھەج جەريانىدا ئۈچ كۈن روزا تۇتسۇن، (ھەجدىن) قايتقاندىن كېيىن يەتتە كۈن روزا تۇتسۇن، ئۇنىڭ ھەممىسى ئون كۈن بولىدۇ. مانا بۇ (يەنى ئۆمرە قىلغان ئادەمگە قۇربانلىق قىلىش لازىملىقى ياكى روزا تۇتۇش ھەققىدىكى ھۆكۈم) مەسجىدى ھەرەم دائىرىسىدە (يەنى ھەرەمدە) ئولتۇرۇشلۇق بولمىغان كىشىلەر ئۈچۈندۇر. ئاللاھقا تەقۋادارلىق قىلىڭلار، بىلىڭلاركى، ئاللاھنىڭ ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(2,197,'2:197','ھەج ۋاقتى مەلۇم بىرقانچە ئايدۇر (يەنى شەۋۋال، زۇلقەئدە ئايلىرى ۋە زۇلھەججە ئېيىنىڭ دەسلەپكى ئون كۈنىدۇر). بۇ ئايلاردا ھەج قىلىشنى نىيەت قىلغان (ئېھرام باغلىغان) ئادەمنىڭ جىنسىي ئالاقىگە مۇناسىۋەتلىك سۆز قىلىشى، گۇناھ قىلىشى ۋە جاڭجال قىلىشى مەنئى قىلىنىدۇ، سىلەر قانداقلىكى ياخشى ئەمەلنى قىلساڭلار، ئاللاھ ئۇنى بىلىپ تۇرىدۇ (ھەج ئۈچۈن) ئوزۇق ئېلىۋېلىڭلار، ئەڭ ياخشى ئوزۇق تەقۋادارلىقتۇر. ئى ئەقىل ئىگىلىرى! ماڭا تەقۋادارلىق قىلىڭلار.');
INSERT INTO "saleh" VALUES(2,198,'2:198','پەرۋەردىگارىڭلاردىن (ھەج مەۋسۈمىدە تىجارەت ۋە باشقا ئوقەت ئارقىلىق) رىزىق تەلەپ قىلساڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ. ئەرەفاتتىن قايتقان چېغىڭلاردا مەشئەرى ھەرەمدە (يەنى مۇزدەلىفەدە) ئاللاھنى ياد ئېتىڭلار، ئاللاھ سىلەرنى ھىدايەت قىلغانلىقى ئۈچۈن، ئۇنى ياد ئېتىڭلار، ئىلگىرى سىلەر ھەقىقەتەن گۇمراھ ئىدىڭلار (ئاللاھ سىلەرنى ھىدايەت قىلىشتىن ئىلگىرى گۇمراھلار قاتارىدا ئىدىڭلار).');
INSERT INTO "saleh" VALUES(2,199,'2:199','ئاندىن كېيىن، سىلەر (ئەرەفاتتىن) كىشىلەر قايتقان جاي بىلەن قايتىڭلار، ئاللاھتىن مەغپىرەت تەلەپ قىلىڭلار. ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,200,'2:200','سىلەر ھەجگە ئائىت ئىبادەتلەرنى ئادا قىلغاندىن كېيىن، ئاللاھنى ئاتا ـ بوۋاڭلارنى ياد ئەتكەندەك ياكى ئۇنىڭدىنمۇ زىيادە ياد ئېتىڭلار. بەزى كىشىلەر: «ئى پەرۋەردىگارىمىز! بىزگە (نېسىۋىمىزنى) بۇ دۇنيادىلا بەرگىن» دەيدۇ. ھالبۇكى، ئۇنىڭغا ئاخىرەتتە (ياخشىلىقتىن) ھېچ نېسىۋە يوقتۇر.');
INSERT INTO "saleh" VALUES(2,201,'2:201','بەزى كىشىلەر: «پەرۋەردىگارىمىز! بىزگە دۇنيادا ياخشىلىق ئاتا قىلغىن، ئاخىرەتتىمۇ ياخشىلىق ئاتا قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(2,202,'2:202','ئەنە شۇلار ئۆزلىرى قىلغان ئەمەللىرىنىڭ مول مېۋىسىنى كۆرىدۇ. ئاللاھنىڭ ھېساب ئېلىشى تېزدۇر (يەنى شۇنچە كۆپ خالايىقتىن قىسقا ۋاقىت ئىچىدە ھېساب ئېلىپ بولىدۇ).');
INSERT INTO "saleh" VALUES(2,203,'2:203','ساناقلىق كۈنلەردە (تەشرىق كۈنلىرى يەنى قۇربان ھېيتنىڭ 1 – 2 – 3 كۈنلىرىدە) ئاللاھنى ياد ئېتىڭلار (نامازلىرىڭلارنىڭ ئاخىرىدا تەكبىر ئېيتىڭلار)، (مىنادىن) ئالدىراپ ئىككى كۈندە قايتقان ئادەمگە ھېچ گۇناھ بولمايدۇ، تەخىر قىلىپ قايتقان ئادەمگىمۇ ھېچ گۇناھ بولمايدۇ. (يۇقىرىقى ئەھكاملار) تەقۋادارلىق قىلىپ (ھەجنى تولۇق ئادا قىلماقچى بولغان) ئادەم ئۈچۈندۇر. ئاللاھتىن قورقۇڭلار؛ بىلىڭلاركى، سىلەر ھېساب ئۈچۈن ئاللاھنىڭ دەرگاھىغا يىغىلىسىلەر.');
INSERT INTO "saleh" VALUES(2,204,'2:204','(ئى مۇھەممەد!) كىشىلەر ئارىسىدا شۇنداق ئادەم باركى، ئۇنىڭ دۇنيا تىرىكچىلىكى توغرىسىدىكى سۆزى سېنى قىزىقتۇرىدۇ (لېكىن ئۇ يالغانچى مۇناپىقتۇر)، ئۇ دىلىدىكى نەرسىگە (دىلى باشقا، تىلى باشقا ئەمەسلىكىگە) ئاللاھنى گۇۋاھ قىلىدۇ. ھالبۇكى، ئۇ (ساڭا ۋە ساڭا ئەگەشكۈچىلەرگە) ئەشەددىي دۈشمەندۇر (كۆرۈنۈشتە ئۇ شېرىن سۆزى ئارقىلىق دىندار قىياپەتكە كىرىۋالىدۇ).');
INSERT INTO "saleh" VALUES(2,205,'2:205','ئۇ (ھۇزۇرۇڭدىن) قايتقاندىن كېيىن، زېمىندا بۇزۇقچىلىق قىلىش ئۈچۈن ۋە زىرائەتلەرنى، ھايۋاناتلارنى ھالاك قىلىش ئۈچۈن تىرىشىدۇ (ئۇنىڭ بۇزغۇنچىلىقى ئەمەلدە ئىنسانلارنى ھالاك قىلىش ئۈچۈندۇر، چۈنكى زىرائەتلەرسىز ۋە ھايۋاناتلارسىز ئىنسانلارنىڭ ياشىيالىشى مۇمكىن ئەمەس). ئاللاھ بۇزۇقچىلىقنى ياقتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(2,206,'2:206','ئۇنىڭغا (ۋەز ـ نەسىھەت قىلىنىپ يامان سۆز ـ ھەرىكەتلىرىڭدىن قايتىپ) ئاللاھتىن قورققىن، دېيىلسە، غۇرۇرى ئۇنىڭغا گۇناھ يۈكلەيدۇ (يەنى ھەقتىن تەكەببۇرلۇق بىلەن باش تارتىپ، پىتنە ـ پاساتتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا جەھەننەم يېتەرلىكتۇر، جەھەننەم ناھايىتى يامان جايدۇر. ');
INSERT INTO "saleh" VALUES(2,207,'2:207','بەزى كىشىلەر باركى، ئۇلار ئاللاھنىڭ رازىلىقى ئۈچۈن جېنىنى پىدا قىلىدۇ (قىلغان ئەمەلى بىلەن پەقەت ئاللاھنىڭ رازىلىقىنىلا تىلەيدۇ). ئاللاھ بەندىلىرىگە تولىمۇ مەرھەمەتلىكتۇر.');
INSERT INTO "saleh" VALUES(2,208,'2:208','ئى مۆمىنلەر! ئىسلام دىنىغا پۈتۈنلەي كىرىڭلار (يەنى ئسلام دىنىنىڭ پۈتۈن ئەھكاملىرىغا بويسۇنۇڭلار، بەزى ئەھكاملىرىغا ئەمەل قىلىپ، بەزى ئەھكاملىرىنى تەرك ئەتمەڭلار)، شەيتاننىڭ يوللىرىغا (ۋە ئىغۋاسىغا) ئەگەشمەڭلار. شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر.');
INSERT INTO "saleh" VALUES(2,209,'2:209',' سىلەرگە (ئىسلام دىنىنىڭ ھەقلىقىنى كۆرسىتىدىغان) روشەن دەلىللەر كەلگەندىن كېيىن (توغرا يولدىن) تېيىپ كەتسەڭلار، بىلىڭلاركى، (سىلەر جازالىنىسىلەر، چۈنكى) ئاللاھ غالىبتۇر (يەنى سىلەردىن ئىنتىقام ئېلىشتىن ئاجىز ئەمەستۇر)، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,210,'2:210','ئۇلار (يەنى ئىسلامغا كىرىشنى تەرك ئەتكەنلەر) ئاللاھنىڭ بۇلۇت پارچىلىرى ئىچىدە كېلىشىنى، پەرىشتىلەرنىڭ كېلىشىنى ۋە ئىشىنىڭ پۈتۈشىنىلا كۈتەمدۇ؟ ۋەھالەنكى ئىشلار ئاللاھقىلا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(2,211,'2:211','(ئى مۇھەممەد!) ئىسرائىل ئەۋلادىدىن سورىغىنكى، بىز ئۇلارغا نەقەدەر كۆپ روشەن دەلىللەرنى ئاتا قىلدۇق. كىمكى ئاللاھنىڭ نېمىتىگە ئېرىشكەندىن كېيىن، ئۇنى (كۇفرىلىق قىلىپ) ئۆزگەرتىۋەتسە (ئاللاھ ئۇنىڭغا ئازاب قىلىدۇ)، ئاللاھنىڭ ئازابى ھەقىقەتەن قاتتىقتۇر. ');
INSERT INTO "saleh" VALUES(2,212,'2:212','كاپىرلارغا دۇنيا تىرىكچىلىكى چىرايلىق كۆرسىتىلدى (يەنى ئۇلار دۇنيانىڭ شەھۋەتلىرىگە، نېمەتلىرىگە مەپتۇن بولىدۇ) ۋە مۆمىنلەرنى مەسخىرە قىلىدۇ، ھالبۇكى قىيامەت كۈنى تەقۋادارلار ئۇلاردىن (دەرىجە جەھەتتە) ئۈستۈن تۇرىدۇ؛ ئاللاھ خالىغان كىشىگە ھېسابسىز رىزىق بېرىدۇ.');
INSERT INTO "saleh" VALUES(2,213,'2:213','ئىنسانلار (دەسلەپتە) بىر ئۈممەت (يەنى ھەق دىندا) ئىدى (كېيىن ئۇلارنىڭ بەزىسى ئىمان ئېيتىپ، بەزىسى ئىمان ئېيتماي ئىختىلاپ قىلىشتى)، ئاللاھ (مۆمىنلەرگە جەننەت بىلەن) خۇشخەۋەر بەرگۈچى، (كۇففارلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتى؛ (ئاللاھ) كىشىلەرنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىش ئۈچۈن، ئۇلارغا كىتابنى ھەقىقەت بىلەن نازىل قىلدى، پەقەت كىتاب بېرىلگەن كىشىلەرلا ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ھەسەت قىلىشىپ (يەنى كاپىرلار مۆمىنلەرگە ھەسەت قىلىپ)، كىتاب توغرىسىدا ئىختىلاپ قىلىشتى، ئاللاھ ئۆز ئىرادىسى بويىچە مۆمىنلەرنى ئۇلار (يەنى گۇمراھلار) ئىختىلاپ قىلىشقان ھەقىقەتكە ھىدايەت قىلدى. ئاللاھ خالىغان كىشىنى توغرا يولغا باشلايدۇ.');
INSERT INTO "saleh" VALUES(2,214,'2:214','سىلەر تېخى ئىلگىرىكىلەر ئۇچرىغان كۈلپەتلەرگە ئۇچرىماي تۇرۇپ جەننەتكە كىرىشنى ئويلامسىلەر؟ سىلەردىن ئىلگىرى ئۆتكەن (مۆمىن) لەر ئېغىرچىلىقلارغا، كۈلپەتلەرگە ئۇچرىغان ۋە چۆچۈتۈۋېتىلگەن ئىدى، ھەتتا پەيغەمبەر ۋە مۆمىنلەر: «ئاللاھنىڭ (بىزگە ۋەدە قىلغان) ياردىمى قاچان كېلىدۇ؟» دېگەن ئىدى. بىلىڭلاركى، ئاللاھنىڭ ياردىمى ھەقىقەتەن يېقىندۇر.');
INSERT INTO "saleh" VALUES(2,215,'2:215','(ئى مۇھەممەد!) سەندىن (ماللىرىدىن) قانداق نەرسىنى سەرپ قىلسا بولىدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «مال ـ مۈلكىڭلاردىن نېمىنى سەرپ قىلساڭلار، ئۇنى ئاتا - ئاناڭلارغا، خىش ـ ئەقرىبالىرىڭلارغا، مىسكىنلەرگە ۋە ئىبن سەبىللەرگە سەرپ قىلساڭلار بولىدۇ. ياخشىلىقتىن نېمىنى قىلماڭلار، ئاللاھ ئۇنى بىلىپ تۇرىدۇ».');
INSERT INTO "saleh" VALUES(2,216,'2:216','سىلەرگە جىھاد پەرز قىلىندى. ھالبۇكى، سىلەر ئۇنى ياقتۇرمايسىلەر، سىلەر بىرەر نەرسىنى ياقتۇرماسلىقىڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن پايدىلىقتۇر؛ سىلەر بىرەر نەرسىنى ياقتۇرۇشۇڭلار مۇمكىن، ئەمما ئۇ سىلەر ئۈچۈن زىيانلىقتۇر. (سىلەرگە نېمىنىڭ پايدىلىق ئىكەنلىكىنى) ئاللاھ بىلىدۇ، سىلەر بىلمەيسىلەر، (شۇنىڭ ئۈچۈن) ئاللاھ بۇيرۇغانغا ئالدىراڭلار.');
INSERT INTO "saleh" VALUES(2,217,'2:217','(ئى مۇھەممەد!) ئۇلار سەندىن: «ھارام قىلىنغان ئايدا ئۇرۇش قىلىشقا بولامدۇ؟» دەپ سورايدۇ، ئېيتقىنكى، «بۇ ئايدا ئۇرۇش قىلىش چوڭ گۇناھتۇر؛ ئاللاھنىڭ يولىدىن توسۇش، ئاللاھقا كۇفرىلىق قىلىش، مەسجىدى ھەرەمدە (ئىبادەت قىلىشتىن) توسۇش ۋە مەسجىدى ھەرەمدىن ئاھالىنى ھەيدەپ چىقىرىش ئاللاھنىڭ دەرگاھىدا تېخىمۇ چوڭ گۇناھتۇر، پىتنە (يەنى مۆمىنلەرگە زىيانكەشلىق قىلىش) ئادەم ئۆلتۈرۈشتىنمۇ قاتتىق گۇناھتۇر». ئۇلار (يەنى كۇففارلار) قولىدىن كەلسىلا سىلەرنى دىنىڭلاردىن قايتۇرۇۋەتكەنگە قەدەر سىلەر بىلەن داۋاملىق ئۇرۇشىدۇ. سىلەردىن كىمكى ئۆز دىنىدىن قايتىپ كاپىر پېتى ئۆلسە، ئۇنىڭ ئەمەللىرى دۇنيا ۋە ئاخىرەتتە بىكار بولۇپ كېتىدۇ. بۇنداق ئادەملەر ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(2,218,'2:218','شۈبھىسىزكى، ئىمان ئېيتقانلار، ھىجرەت قىلغانلار ۋە ئاللاھنىڭ يولىدا جىھاد قىلغانلار- ئەنە شۇنداق كىشىلەر ئاللاھنىڭ رەھمىتىنى ئۈمىد قىلىدۇ، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,219,'2:219','(ئى مۇھەممەد!) سەندىن ھاراق ۋە قىمار توغرىسىدا سورىشىدۇ، سەن ئۇلارغا: «بۇنىڭ ھەر ئىككىسىدە چوڭ گۇناھ ۋە كىشىلەرگە (ئازغىنا ماددى) پايدىمۇ بار، لېكىن ئۇلاردىكى گۇناھ پايدىغا قارىغاندا تېخىمۇ چوڭ» دېگىن، ئۇلار سەندىن نېمىنى سەدىقە قىلىش توغرۇلۇق سورايدۇ، «(ئېھتىياجدىن) ئارتۇقىنى (سەدىقە قىلىڭلار)» دېگىن. دۇنيا ۋە ئاخىرەتنىڭ (ئىشلىرىنى) تەپەككۇر قىلىشىڭلار ئۈچۈن ئاللاھ ئۆز ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,220,'2:220','(ئى مۇھەممەد!) ئۇلار سەندىن يېتىملەر توغرۇلۇق سورىشىدۇ، ئېيتقىنكى، «(تەربىيەلەش، ماللىرىنى كۆپەيتىپ) ئۇلارنى تۈزەش ياخشىدۇر، ئۇلار بىلەن (ئۇلارغا پايدىلىق بولغان رەۋىشتە) ئارىلىشىپ ياشىساڭلار بولىدۇ. چۈنكى، ئۇلار سىلەرنىڭ (دىنىي) قېرىنداشلىرىڭلاردۇر. ئاللاھ (يېتىملەرنىڭ ئىشلىرىنى) بۇزغۇچى ۋە تۈزىگۈچىنى بىلىپ تۇرىدۇ. ئەگەر ئاللاھ خالىسا، سىلەرنى (قىيىن ئىشقا تەكلىپ قىلىش بىلەن) چوقۇم جاپاغا سالاتتى». ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,221,'2:221','(ئى مۇسۇلمانلار!) مۇشرىك ئاياللار ئىمان ئېيتمىغىچە ئۇلارنى نىكاھىڭلارغا ئالماڭلار. (ھۆر) مۇشرىك ئايال (ھۆسن ـ جامالى ۋە مېلى بىلەن) سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ، مۆمىن دېدەك، ئەلۋەتتە ئۇنىڭدىن ئارتۇقتۇر. مۇشرىك ئەرلەر ئىمان ئېيتمىغىچە مۆمىن ئاياللارنى ئۇلارغا ياتلىق قىلماڭلار، (ھۆر) مۇشرىك ئەر سىلەرنى مەپتۇن قىلغان تەقدىردىمۇ مۆمىن قۇل، ئەلۋەتتە ئۇنىڭدىن ئارتۇقتۇر. ئەنە شۇلار (يەنى مۇشرىك ئەر ۋە ئاياللار سىلەرنى) دوزاخقا چاقىرىدۇ، ئاللاھ ئۆز ئىرادىسى بويىچە (سىلەرنى) جەننەتكە، مەغپىرەتكە چاقىرىدۇ، كىشىلەر پەند ـ نەسىھەت ئالسۇن دەپ ئاللاھ ئۇلارغا ئۆز ئايەتلىرىنى بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,222,'2:222','(ئى مۇھەممەد!) ئۇلار سەندىن ھەيز توغرۇلۇق (يەنى ھەيزدار ئايال بىلەن جىنسىي ئالاقە قىلىشنىڭ دۇرۇسلۇقى ياكى دۇرۇس ئەمەسلىكى توغرۇلۇق) سورايدۇ. ئېيتقىنكى، «ھەيز زىيانلىقتۇر (يەنى ھەيز مەزگىلىدە جىنسىي ئالاقە قىلىش ئەر ـ خوتۇن ھەر ئىككىسىگە زىيانلىقتۇر). شۇڭا ھەيز مەزگىلىدە ئاياللىرىڭلاردىن نېرى تۇرۇڭلار، (ھەيزدىن) پاك بولغىچە ئۇلارغا يېقىنچىلىق قىلماڭلار، پاك بولغاندا، ئۇلارغا ئاللاھ بۇيرۇغان جايدىن يېقىنچىلىق قىلىڭلار». ئاللاھ ھەقىقەتەن تەۋبە قىلغۇچىلارنى دوست تۇتىدۇ. (ھارامدىن ۋە نىجاسەتتىن) پاك بولغۇچىلارنى ھەقىقەتەن دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(2,223,'2:223','ئاياللىرىڭلار سىلەر ئۈچۈن (خۇددى) ئېكىنزارلىقتۇر (يەنى نەسىل تېرىيدىغان جايدۇر). ئېكىنزارلىقىڭلارغا خالىغان رەۋىشتە كېلىڭلار، ئۆزۈڭلار ئۈچۈن ئالدىنئالا ياخشى ئەمەل تەييارلاڭلار، (گۇناھتىن چەكلىنىش بىلەن) ئاللاھتىن قورقۇڭلار، بىلىڭلاركى، سىلەر ئاللاھقا مۇلاقات بولۇسىلەر، مۆمىنلەرگە (جەننەت بىلەن) خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(2,224,'2:224','ياخشىلىق قىلماسلىققا، تەقۋادار بولماسلىققا ۋە كىشىلەرنى ئەپلەشتۈرمەسلىككە قەسەم قىلىش شەكلى بىلەن (ئۇلارنى قىلىشقا) ئاللاھنى توسالغۇ قىلماڭلار (يەنى ئاللاھ بىلەن قەسەم قىلىشنى ياخشى ئىشلارنى قىلماسلىقنىڭ سەۋەبى قىلماڭلار)، ئاللاھ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,225,'2:225','مەقسەتسىز ئىچكەن قەسىمىڭلار ئۈچۈن ئاللاھ سىلەرنى جازاغا تارتمايدۇ، قەستەن ئىچكەن قەسىمىڭلار ئۈچۈن ئاللاھ سىلەرنى جازاغا تارتىدۇ. ئاللاھ مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(2,226,'2:226','ئاياللىرىغا يېقىنچىلىق قىلماسلىققا قەسەم ئىچكەن كىشىلەر تۆت ئاي كۈتىدۇ؛ ئەگەر ئۇلار (بۇ مۇددەت ئىچىدە ئاياللىرىغا يېقىنچىلىق قىلىشقا) قايتسا، (ئاياللىرىغا يامانلىق قىلغان گۇناھىنى) ئاللاھ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر. ئاللاھ ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(2,227,'2:227','ئەگەر ئۇلار تالاق قىلىش نىيىتىگە كەلسە، ئاللاھ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,228,'2:228','تالاق قىلىنغان ئاياللار ئۈچ ھەيز ئۆتكۈچە ئىددەت تۇتىدۇ. ئەگەر ئۇلار ئاللاھقا، ئاخىرەت كۈنىگە ئىشىنىدىكەن، بەچچىدانلىرىدىكى ئاللاھ ياراتقان نەرسىنى (يەنى بالىنى ياكى ھەيزنى) يوشۇرۇشى دۇرۇس ئەمەس، ئۇلارنىڭ ئەرلىرى ئەگەر ئەپلىشىپ قېلىشنى خالىسا، شۇ مۇددەت ئىچىدە ئۇلارنى قايتۇرۇپ كېلىشكە (باشقىلارغا قارىغاندا) ئەڭ ھەقلىقتۇر. ئاياللىرى ئۈستىدە ئەرلەرنىڭ ھەقلىرى بولغىنىدەك، ئەرلىرى ئۈستىدە ئاياللارنىڭمۇ ھەقلىرى بار (يەنى ئاياللار مۇۋاپىق دەرىجىدە ھوقۇقتىن بەھرىمەن بولۇشى كېرەك، مۇۋاپىق دەرىجىدە مەجبۇرىيەتمۇ ئۆتىشى كېرەك). (ئاياللارغا مەھرى بېرىش ۋە ئۇلارنىڭ تۇرمۇشىنى قامداش مەجبۇرىيەتلىرى ئەرلەرگە يۈكلەنگەنلىكتىن) ئەرلەر ئاياللاردىن بىر دەرىجە ئارتۇقلۇققا ئىگە. ئاللاھ غالىبتۇر. ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,229,'2:229','(قايتا يارىشىشقا بولىدىغان) تالاق ئىككى قېتىمدۇر، ئۇنىڭدىن كېيىن (خوتۇنىنى) چىرايلىقچە تۇتۇش ياكى ياخشىلىق بىلەن (زۇلۇم قىلماي، يامان گېپىنى قىلماي، كىشىلەرنى ئۇنىڭدىن يىراقلاشتۇرماي) قويۇۋېتىش لازىم، ئاللاھنىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىق خەۋپى (يەنى ياخشى ئۆتەلمەسلىك، ئاللاھ بەلگىلىگەن ئەر ـ خوتۇنلۇق ھوقۇقلىرىغا رىئايە قىلالماسلىق خەۋپى) بولمىسىلا، ئۆزەڭلارنىڭ ئاياللىرىڭلارغا (مەھرى ئۈچۈن) بەرگەن مال ـ مۈلۈكتىن ھېچقانداق نەرسىنى قايتۇرۇۋېلىش دۇرۇس بولمايدۇ. ئەگەر سىلەر ئۇلار (يەنى ئەر ـ ئايال) نىڭ ئاللاھنىڭ بەلگىلىمىلىرىگە رىئايە قىلالماسلىقىدىن قورقساڭلار، خوتۇننىڭ (تالاق خېتىنى ئېلىش مەقسىتىدە) مال بەرگەنلىكى ئۈچۈن ئۇ ئىككىسىگە (يەنى ئەر ـ خوتۇنغا) گۇناھ بولمايدۇ. ئەنە شۇلار ئاللاھنىڭ بەلگىلىمىلىرىدۇر، ئۇنىڭغا خىلاپلىق قىلماڭلار، ئاللاھنىڭ بەلگىلىمىلىرىگە خىلاپلىق قىلغۇچىلار زالىملاردۇر.');
INSERT INTO "saleh" VALUES(2,230,'2:230','ئەگەر (ئەر) ئۇنى (يەنى خوتۇنىنى) (ئۈچىنچى قېتىم) تالاق قىلسا، (خوتۇن) باشقا ئەرگە تەگمىگۈچە ئۇنىڭغا دۇرۇس بولمايدۇ. كېيىنكى ئەر ئۇنى تالاق قىلغاندىن كېيىن (ئىددىتىنى توشقۇزۇپ)، بۇرۇنقى ئەر بىلەن قايتا ياراشسا، ئۈلارغا ھېچ گۇناھ بولمايدۇ. ئەگەر ئۇلار ئاللاھنىڭ بەلگىلىمىلىرىگە رىئايە قىلالايدىغانلىقىنى (يەنى قايتا ياراشقاندىن كېيىن ياخشى ئۆتەلەيدىغانلىقىنى) تەسەۋۋۇر قىلسا، بۇ ئاللاھنىڭ بەلگىلىمىلىرىدۇركى، ئاللاھ ئۇنى بىلىدىغان (يەنى ئىشنىڭ ئاقىۋىتىگە پەمى يېتىدىغان) قەۋم ئۈچۈن بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,231,'2:231','سىلەر ئاياللارنى تالاق قىلغان بولساڭلار، ئۇلارنىڭ ئىددىتى توشاي دەپ قالغان بولسا، ئۇلار بىلەن چىرايلىقچە يارىشىپ، (ئۇلارنى نىكاھىڭلاردا) تۇتۇڭلار، ياكى ئۇلارنى ياخشىلىق بىلەن قويۇۋېتىڭلار، زۇلۇم قىلىش مەقسىتىدە زىيان يەتكۈزۈپ ئۇلارنى تۇتۇۋالماڭلار. كىمكى شۇنداق قىلىدىكەن (يەنى كىمكى چىرايلىقچە ئۆي تۇتۇش نىيىتى بىلەن ئەمەس، بەلكى ئىددىتىنى ئۇزارتىپ زۇلۇم قىلىش نىيىتىدە خوتۇنى بىلەن يارىشىدىكەن)، ئۆزىگە ئۆزى زۇلۇم قىلغان بولىدۇ. ئاللاھنىڭ ئايەتلىرىنى كۈلكە قىلىۋالماڭلار (يەنى ئاللاھنىڭ ئەھكاملىرىنى خىلاپلىق قىلىش يولى بىلەن مەسخىرە قىلماڭلار)، ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ۋە سىلەرگە ۋەز ـ نەسىھەت قىلىش ئۈچۈن نازىل قىلغان كىتابنى، ھېكمەتنى (كىتابتىكى ئەھكاملارنى) ياد ئېتىڭلار، ئاللاھقا تەقۋادارلىق قىلىڭلار. بىلىڭلاركى، ئاللاھ ھەر نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,232,'2:232','ئەگەر سىلەر ئاياللارنى تالاق قىلغان بولۇپ، ئۇلارنىڭ ئىددىتى توشقان ۋە قائىدە بويىچە (بۇرۇنقى) ئېرى بىلەن پۈتۈشكەن بولسا، ئۇلارنى نىكاھلىنىشتىن توسماڭلار. بۇنىڭ بىلەن، ئىچىڭلاردىن ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرگەنلەرگە نەسىھەت قىلىنىدۇ، بۇ (يەنى ۋەز ـ نەسىھەت ئېلىش، ئاللاھنىڭ ئەمرىلىرىنى تۇتۇش)، سىلەر ئۈچۈن ئەڭ پايدىلىقتۇر ۋە ئەڭ پاكتۇر، (شەرىئەت ئەھكاملىرىدىن سىلەرگە نېمىلەر ئەڭ پايدىلىق ئىكەنلىكىنى) ئاللاھ بىلىدۇ، سىلەر بىلمەيسىلەر.');
INSERT INTO "saleh" VALUES(2,233,'2:233','ئانىلار (بالىلارنى ئېمىتىش مۇددىتىدە) تولۇق ئېمىتمەكچى بولسا، تولۇق ئىككى يىل ئېمىتىشى لازىم. ئاتىلار ئانىلارنى قائىدە بويىچە يېمەك ـ ئىچمەك ۋە كىيىم ـ كېچەك بىلەن تەمىنلەپ تۇرۇشى كېرەك. كىشى پەقەت قولىدىن كېلىدىغان ئىشقىلا تەكلىپ قىلىنىدۇ. ئانىنى بالىسى سەۋەبلىك زىيان تارتقۇزماسلىق، ئاتىنى بالىسى سەۋەپلىك زىيان تارتقۇزماسلىق لازىم. (ئەگەر ئاتا ئۆلۈپ كەتسە) ئۇنىڭ ۋارىسى ئاتىغا ئوخشاش مەسئۇلىيەتنى (يەنى ئانا بولغۇچىغا نەپىقە بېرىش ۋە ئۇنىڭ ھەقلىرىگە رىئايە قىلىش قاتارلىقلارنى) ئۆز ئۈستىگە ئېلىشى كېرەك. ئەگەر ئاتا ـ ئانا كېڭىشىپ (بالىنى ئىككى يىل توشماستىنلا) ئايرىۋەتمەكچى بولسا، ئۇلارغا ھېچبىر گۇناھ بولمايدۇ. ئەگەر بالىلىرىڭلارنى ئىنىكئانىلارغا ئېمىتمەكچى بولساڭلار، قائىدە بويىچە ئۇلارنىڭ ھەققىنى بەرسەڭلارلا سىلەرگە ھېچ گۇناھ بولمايدۇ. ئاللاھتىن (يەنى ئاللاھنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىشتىن) قورقۇڭلار، بىلىڭلاركى، ئاللاھ سىلەرنىڭ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(2,234,'2:234','ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئاياللىرى قېلىپ قالغان كىشىلەر بولسا، ئاياللىرى تۆت ئاي ئون كۈن ئىددەت تۇتۇشى لازىم، ئىددىتى توشقاندىن كېيىن ئۆزلىرى توغرۇلۇق قائىدە بويىچە ئىش قىلسا، سىلەرگە (يەنى شۇ ئاياللارغا ئىگىدارچىلىق قىلغۇچىلارغا) ھېچ گۇناھ بولمايدۇ. ئاللاھ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(2,235,'2:235','ئىددىتى توشمىغان ئاياللارغا ئۇلارنى ئالىدىغانلىقىڭلارنى پۇرىتىپ ئۆتسەڭلار ياكى بۇنى دىلىڭلاردا يوشۇرۇن تۇتساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. ئۇلارغا ئېغىز ئاچىدىغانلىقىڭلار ئاللاھقا مەلۇمدۇر، لېكىن ئۇلار بىلەن يوشۇرۇن ۋەدىلىشىپ قويماڭلار، پەقەت (ئاشكارا ئېيتىلسا كىشى خىجىل بولمايدىغان) مۇۋاپىق سۆزنى قىلساڭلار بولىدۇ، ئىددىتى توشمىغىچە ئۇلارنى نىكاھىڭلارغا ئېلىشقا بەل باغلىماڭلار. بىلىڭلاركى، ئاللاھ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، ئۇنىڭدىن (ئاللاھنىڭ ئەمرىگە خىلاپلىق قىلىپ جازالىنىشتىن) ھەزەر قىلىڭلار؛ بىلىڭلاركى، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(2,236,'2:236','ئەگەر سىلەر ئاياللىرىڭلارغا يېقىنچىلىق قىلماي ياكى مەھرى تەيىنلىمەي تۇرۇپ ئۇلارنى تالاق قىلساڭلار (يەنى نىكاھىڭلارغا ئالغان ئايال بىلەن بىر يەردە بولمىغان ياكى مەھرىنى مۇئەييەنلەشتۈرمىگەن شارائىتتا) ئۇنى قويۇۋەتسەڭلار، سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن ئۇنىڭغا مۇتئە (بەلگىلىك مىقداردا پۇل ـ مال) بېرىشىڭلار كېرەك، ئۇلارغا پۇل - مال بېرىڭلار، باي ئۆز ھالىغا يارىشا، كەمبەغەلمۇ ئۆز ھالىغا يارىشا قائىدە بويىچە بەرسۇن، (بۇ) ياخشى ئادەملەرنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر.');
INSERT INTO "saleh" VALUES(2,237,'2:237','ئەگەر سىلەر ئۇلارغا يېقىنچىلىق قىلىشتىن ئىلگىرى ۋە مەھرى تەيىنلىنىپ بولغاندىن كېيىن تالاق قىلساڭلار، تەيىنلەنگەن مەھرىنىڭ يېرىمىنى بېرىڭلار، ئەمما ئاياللار ئۆتۈنسە (يەنى مەھرىدىن ۋاز كېچىپ پۈتۈنلەي ئالمىسا) ياكى نىكاھنىڭ تۈگۈنى ئۆز ئىلكىدە بولغان ئەرلەر ئۆتۈنسە (يەنى تەيىنلەنگەن مەھرىنىڭ ھەممىسىنى بەرسە)، شۇ بويىچە بىر تەرەپ قىلىش لازىم. سىلەر ئۆتۈنسەڭلار، (ئۆتۈنگەن ئادەم) تەقۋادارلىققا ئەڭ يېقىندۇر. ئۆزئارا ئېھسان قىلىشنى ئۇنتۇماڭلار. ئاللاھ ھەقىقەتەن قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,238,'2:238','(بەش) نامازغا بولۇپمۇ نامازدىگەرگە ئەھمىيەت بېرىڭلار (يەنى پۈتۈن شەرتلىرى بىلەن ۋاقتىدا تولۇق ئادا قىلىڭلار). ئاللاھنىڭ ھۇزۇرىدا (يەنى نامازدا) ئىتائەتمەنلىك بىلەن تۇرۇڭلار.');
INSERT INTO "saleh" VALUES(2,239,'2:239','قورقۇنچتا قالغىنىڭلاردا، پىيادە ياكى ئۇلاغلىق كېتىپ بېرىپ (ناماز ئوقۇڭلار). ئامانلىق تاپقىنىڭلاردا ئاللاھنىڭ سىلەرگە بىلمىگىنىڭلارنى ئۆگەتكىنىدەك ئاللاھنى ياد ئېتىڭلار (يەنى قورقۇنچ تۈگىگەندە، ئاللاھ نامازنى قانداق ئۆتەشكە ئەمر قىلغان بولسا، تولۇق ئەركانلىرى بىلەن شۇنداق ئۆتەڭلار).');
INSERT INTO "saleh" VALUES(2,240,'2:240','سىلەرنىڭ ئىچىڭلاردا ئۆزى ۋاپات بولۇپ ئايالى قېلىپ قالىدىغانلار ئۇلارنىڭ (يەنى ئاياللىرىنىڭ) بىر يىلغىچە يېمەك ـ ئىچمەك، كىيىم ـ كېچەك بىلەن تەمىنلىنىشىنى، ئۆيىدىن ھەيدەپ چىقىرىلماسلىقىنى ۋەسىيەت قىلسۇن. ئەگەر (ئاياللىرى) ئۆزلىرى چىقىپ كەتسە، ئۇلارنىڭ ئۆزلىرى توغرىسىدا قىلغان قائىدىلىك ئىشلىرى ئۈچۈن سىلەرگە (يەنى مېيىتنىڭ ئىگىلىرىگە) ھېچ گۇناھ بولمايدۇ. ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(2,241,'2:241','تالاق قىلىنغان ئاياللارغا قائىدە بويىچە نەپىقە بېرىش لازىم. (ئۇ) تەقۋادارلارنىڭ ئۆتەشكە تېگىشلىك مەجبۇرىيىتىدۇر.');
INSERT INTO "saleh" VALUES(2,242,'2:242','سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن ئاللاھ (ئۆز ئەھكاملىرىنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىنى سىلەرگە مۇشۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,243,'2:243','ئۆزلىرى مىڭلارچە تۇرۇقلۇق، ئۆلۈمدىن قورقۇپ، يۇرتلىرىدىن قېچىپ چىققان كىشىلەردىن خەۋىرىڭ يوقمۇ؟ ئاللاھ ئۇلارنى «ئۆلۈڭلار» دېدى (ئۇلار ئۆلدى). ئاندىن كېيىن ئاللاھ ئۇلارنى تىرىلدۈردى، ئاللاھ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋەلېكىن ئىنسانلارنىڭ كۆپچىلىكى (ئاللاھنىڭ نېمىتىگە) شۈكۈر قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,244,'2:244','ئاللاھ يولىدا جىھاد قىلىڭلار. بىلىڭلاركى، ئاللاھ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى، ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,245,'2:245','كىمكى ئاللاھقا قەرزى ھەسەنە بېرىدىكەن (يەنى ئاللاھنىڭ يولىدا پۇل ـ مېلىنى خۇشاللىق بىلەن سەرپ قىلىدىكەن)، ئاللاھ ئۇنىڭغا نەچچە ھەسسە (يەنى ئون ھەسسىدىن يەتتە يۈز ھەسسىگۈچە) كۆپ قايتۇرىدۇ. ئاللاھ (سىناش ئۈچۈن، بەزى كىشىلەرنىڭ رىزقىنى) تار قىلىدۇ، (بەزى كىشىلەرنىڭ رىزقىنى) كەڭ قىلىدۇ، (قىيامەت كۈنى) ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(2,246,'2:246','ساڭا مۇسا (نىڭ ۋاپاتى) دىن كېيىن ئىسرائىل ئەۋلادىدىن بولغان بىر جامائەنىڭ خەۋىرى يەتمىدىمۇ؟ ئۇلار ئۆز ۋاقتىدا ئۆزلىرىنىڭ پەيغەمبىرىگە: «بىزگە پادىشاھ تىكلەپ بەرگىن، (ئۇنىڭ بىلەن دۈشمەنلەرگە قارشى) ئاللاھ يولىدا جىھاد قىلايلى» دېيىشتى. پەيغەمبەر: «سىلەرگە جىھاد پەرز قىلىنسا جىھاد قىلماي قالارسىلەرمۇ؟» دېدى. ئۇلار: «يۇرتلىرىمىزدىن ھەيدەپ چىقىرىلغان ۋە ئوغۇللىرىمىزدىن جۇدا قىلىنغان تۇرساق قانداقمۇ ئاللاھ يولىدا جىھاد قىلمايلى؟» دېدى. ئۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئازغىنىسىدىن باشقا ھەممىسى جىھادتىن باش تارتتى. ئاللاھ زالىملارنى ئوبدان بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,247,'2:247','پەيغەمبىرى ئۇلارغا: «ئاللاھ ھەقىقەتەن سىلەرگە تالۇتنى پادىشاھ قىلىپ ئەۋەتتى» دېدى. ئۇلار (پەيغەمبىرىگە ئېتىراز بىلدۈرۈپ): «ئۇ قانداقمۇ بىزگە پادىشاھ بولىدۇ؟ (ئارىمىزدا پادىشاھ ئەۋلادلىرى بولغانلىقتىن) پادىشاھلىققا بىز ئۇنىڭدىن ھەقلىقمىز، ئۇنىڭ ئۈستىگە ئۇنىڭ مال ـ مۈلكى كۆپ بولمىسا» دېدى. پەيغەمبەر: «ئاللاھ ھەقىقەتەن سىلەرگە (پادىشاھ قىلىشقا) ئۇنى ئىختىيار قىلدى، ئۇنىڭ ئىلمىنى زىيادە ۋە بەدىنىنى بەستىلىك قىلدى، ئاللاھ سەلتەنەتنى خالىغان ئادەمگە بېرىدۇ. ئاللاھ (نىڭ پەزلى) كەڭدۇر، (ئۇنىڭغا كىمنىڭ لايىق بولىدىغانلىقىنى) ئوبدان بىلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(2,248,'2:248','ئۇلارغا پەيغەمبىرى ئېيتتىكى: «ئۇنىڭ پادىشاھلىقىنىڭ ئالامىتى شۇكى، سىلەرگە بىر ساندۇق كېلىدۇ، ئۇنىڭدا رەببىڭلار تەرىپىدىن سىلەرنى تەسكىن تاپقۇزىدىغان نەرسە ۋە مۇسانىڭ، ھارۇننىڭ تەۋەلىرى قالدۇرۇپ كەتكەن نەرسىلەر (مۇسانىڭ ھاسىسى، كىيىمى ۋە تەۋرات يېزىلغان بەزى تاختىلار) قاچىلانغان بولىدۇ، ئۇنى پەرىشتىلەر كۆتۈرۈپ كېلىدۇ. ئەگەر (ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان) بولساڭلار، بۇنىڭدا (يەنى ساندۇقنىڭ نازىل بولۇشىدا ئاللاھنىڭ تالۇتنى سىلەرگە پادىشاھلىققا تاللىغانلىقىغا) ئەلۋەتتە سىلەر ئۈچۈن (روشەن) ئالامەت بار».');
INSERT INTO "saleh" VALUES(2,249,'2:249','تالۇت ئەسكەرلىرى بىلەن (بەيتۇلمۇقەددەستىن) ئايرىلغان چاغدا: «ئاللاھ سىلەرنى بىر دەريا بىلەن سىنايدۇ، كىمكى ئۇنىڭدىن ئىچىدىكەن، ئۇ ماڭا تەۋە ئەمەس، كىمكى دەريا سۈيىنى تېتىمايدىكەن، ئۇ ھەقىقەتەن ماڭا تەۋەدۇر، ئۇنىڭدىن (ئۇسسۇزلۇقىنى بىرئاز بېسىش ئۈچۈن) بىر ئۇچۇم ئىچكەن ئادەم (بۇيرۇققا خىلاپلىق قىلغان بولمايدۇ)» دېدى. ئۇلارنىڭ ئىچىدىكى ئازغىنا كىشىدىن باشقا ھەممىسى ئۇنىڭدىن ئىچتى. تالۇت ۋە ئۇنىڭغا ئىمان ئېيتقان كىشىلەر دەريادىن ئۆتكەن چاغدا (دۈشمەننىڭ كۆپلۈكىنى كۆرۈپ قورقۇنچقا چۈشۈپ، ئۇلارنىڭ بىر قىسمى): «بۈگۈن بىزنىڭ جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇرۇشۇشقا كۈچىمىز يەتمەيدۇ» دېدى. (تالۇتنىڭ تەۋەلىرىدىن) ئاللاھقا مۇلاقات بولۇشقا ئىشىنىدىغانلار: «ئاللاھنىڭ ئىرادىسى بىلەن ئاز جامائە كۆپ جامائە ئۈستىدىن غەلىبە قىلىدۇ» دېدى. ئاللاھ بەرداشلىق بەرگۈچىلەر بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(2,250,'2:250',' ئۇلار جالۇت ۋە ئۇنىڭ ئەسكەرلىرى بىلەن ئۇچراشقان چاغدا: «پەرۋەردىگارىمىز! (قەلبىمىزنى) چىدامچانلىق بىلەن تولدۇرغىن، قەدەملىرىمىزنى (ئۇرۇش مەيدانىدا) مۇستەھكەم قىلغىن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېدى.');
INSERT INTO "saleh" VALUES(2,251,'2:251','مۆمىنلەر ئاللاھنىڭ ئىرادىسى بىلەن كاپىرلارنى مەغلۇپ قىلدى، داۋۇد جالۇتنى ئۆلتۈردى. ئاللاھ داۋۇدقا سەلتەنەتنى، ھېكمەتنى (پەيغەمبەرلىكنى) بەردى. ئۇنىڭغا خالىغان نەرسىلىرىنى (پايدىلىق ئىلىملەرنى) بىلدۈردى. ئاللاھ ئىنسانلارنىڭ بەزىسىنى بەزىسى بىلەن مۇداپىئە قىلىپ تۇرمىسا (كۈچلۈك تاجاۋۇزچىغا ئۇنىڭدىنمۇ كۈچلۈك بولغان بىرسىنى ئاپىرىدە قىلمىسا) ئىدى، يەر يۈزى ئەلۋەتتە خارابلىققا يۈزلىنەتتى، لېكىن ئاللاھ (يامانلىقنى ئۈستۈنلۈككە ئىگە قىلماسلىق بىلەن) پۈتۈن جاھان ئەھلىگە مەرھەمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,252,'2:252','بۇ ئاللاھنىڭ ئايەتلىرىدۇركى، ئۇنى بىز ساڭا توغرا رەۋىشتە ئوقۇپ بېرىمىز. (ئى مۇھەممەد!) سەن ئەلۋەتتە (ئاللاھنىڭ دەۋىتىنى يەتكۈزۈش ئۈچۈن ئەۋەتىلگەن) پەيغەمبەرلەرنىڭ بىرىسەن.');
INSERT INTO "saleh" VALUES(2,253,'2:253','ئەنە شۇ پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق؛ ئاللاھ ئۇلارنىڭ بەزىسى (يەنى مۇسا ئەلەيھىسسالام) بىلەن سۆزلەشتى ۋە بەزىسىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) دەرىجىسىنى يۇقىرى كۆتۈردى. مەريەم ئوغلى ئىيساغا (ئۆلۈكلەرنى تىرىلدۈرۈش، گاچىلارنى، بەرەس كېسىلىنى ساقايتىشقا ئوخشاش) روشەن مۆجىزىلەرنى ئاتا قىلدۇق. ئۇنى روھۇلقۇدۇس (يەنى جىبرىئىل ئەلەيھىسسالام) بىلەن كۈچلەندۈردۇق. ئەگەر ئاللاھ خالىغان بولسا ئىدى، ئۇلاردىن كېيىن كەلگەن ئۈممەتلەر ئۇلارغا روشەن دەلىللەر كەلگەندىن كېيىن ئۆزئارا ئۇرۇشماس ئىدى، لېكىن ئۇلار ئىختىلاپ قىلىشتى؛ بەزىسى ئىمان ئېيتتى، بەزىسى كاپىر بولدى. ئەگەر ئاللاھ خالىغان بولسا ئىدى، ئۇلار ئۆزئارا ئۇرۇشماس ئىدى، لېكىن ئاللاھ خالىغىنىنى قىلىدۇ.');
INSERT INTO "saleh" VALUES(2,254,'2:254','ئى مۆمىنلەر! سودا ـ سېتىق، دوستلۇق ۋە (ئاللاھنىڭ ئىزنىسىز) شاپائەت بولمايدىغان كۈن (يەنى قىيامەت كۈنى) يېتىپ كېلىشتىن بۇرۇن، سىلەرگە بىز رىزىق قىلىپ بەرگەن مال ـ مۈلۈكلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىڭلار. كاپىر بولغۇچىلار زالىملاردۇر.');
INSERT INTO "saleh" VALUES(2,255,'2:255','بىر ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر؛ ئاللاھ ھەمىشە تىرىكتۇر، ھەممىنى ئىدارە قىلىپ تۇرغۇچىدۇر؛ ئۇ مۈگدەپ قالمايدۇ، ئۇنى ئۇيقۇ باسمايدۇ؛ ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە ئاللاھنىڭ (مۈلكى) دۇر؛ ئاللاھنىڭ رۇخسىتىسىز كىممۇ ئاللاھنىڭ ئالدىدا شاپائەت قىلالىسۇن؛ ئاللاھ ئۇلارنىڭ ئالدىدىكى (دۇنيادا قىلغان)، كەينىدىكى (ئۇلار ئۈچۈن ئاخىرەتتە تەييارلىغان) ئىشلارنىڭ ھەممىسىنى بىلىپ تۇرىدۇ؛ ئۇلار ئاللاھنىڭ ئىلمىدىن (ئاللاھ) ئۇلارغا بىلدۈرۈشنى خالىغان نەرسىلەردىن (پەيغەمبەرلەرنىڭ تىلى ئارقىلىق بىلدۈرگەن نەرسىلەردىن) باشقا ھېچ نەرسىنى بىلمەيدۇ، ئاللاھنىڭ كۇرسى ئاسمانلارنى ۋە زېمىننى ئۆز ئىچىگە ئالىدۇ. ئاسمان ـ زېمىننى مۇھاپىزەت قىلىش ئۇنىڭغا ئېغىر كەلمەيدۇ. ئۇ يۇقىرى مەرتىۋىلىكتۇر، ھەممىدىن ئۇلۇغدۇر.');
INSERT INTO "saleh" VALUES(2,256,'2:256','دىندا (ئۇنىڭغا كىرىشكە) زورلاش يوقتۇر، ھىدايەت گۇمراھلىقتىن ئېنىق ئايرىلدى، كىمكى تاغۇت (بارلىق شەيتانىي كۈچ ۋە تۈزۈم) نى ئىنكار قىلىپ، ئاللاھقا ئىمان ئېيتىدىكەن، ئۇ سۇنماس، مەھكەم تۇتقىنى تۇتقان بولىدۇ، ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,257,'2:257','ئاللاھ مۆمىنلەرنىڭ ئىگىسىدۇر، ئۇلارنى (كۇفرىنىڭ) زۇلمەتلىرىدىن (ئىماننىڭ) يورۇقلۇقىغا چىقىرىدۇ؛ كاپىرلارنىڭ ئىگىسى تاغۇتتۇر، ئۇلارنى (ئىماننىڭ) يورۇقلۇقىدىن (گۇمراھلىقنىڭ) قاراڭغۇلۇقلىرىغا چىقىرىدۇ. ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(2,258,'2:258','پەرۋەردىگارى توغرىسىدا ئىبراھىم بىلەن مۇنازىرىلەشكەن، ئاللاھنىڭ ئاتا قىلغان پادىشاھلىقى ئۇنى ئىبراھىم بىلەن مۇنازىرىلىشىشكە ئېلىپ كەلگەن ئادەم (يەنى نەمرۇد) نى كۆرمىدىڭمۇ؟ ئەينى زاماندا ئىبراھىم (ئاللاھنىڭ بارلىقىغا دەلىل كۆرسىتىپ): «مېنىڭ پەرۋەردىگارىم (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنى) ئۆلتۈرەلەيدۇ» دېدى. ئۇ: «مەنمۇ (ئۆلۈكنى) تىرىلدۈرەلەيمەن، (تىرىكنى) ئۆلتۈرەلەيمەن» دېدى (نەمرۇد ئۆلۈمگە ھۆكۈم قىلىنغان ئىككى ئادەمنى چاقىرتىپ كېلىپ، بىرىنى ئۆلتۈردى، يەنە بىرىنى قويۇپ بەردى). ئىبراھىم (نەمرۇدنىڭ بۇنداق ھاماقەتلىكىنى كۆرۈپ): «ئاللاھ ھەقىقەتەن كۈننى شەرقتىن چىقىرالايدۇ، (خۇدالىق دەۋاسى قىلىدىغان بولساڭ) سەن ئۇنى غەربتىن چىقىرىپ باققىن» دېدى. (بۇنداق پاكىت ئالدىدا) كاپىر ئېغىز ئاچالماي قالدى. ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,259,'2:259','ياكى سەن تام، ئۆگزىلىرى يىقىلىپ ۋەيران بولغان (خارابە) شەھەر (يەنى بەيتۇلمۇقەددەس) دىن ئۆتكەن كىشىنى (يەنى ئۇزەيرنى) كۆرمىدىڭمۇ؟ ئۇ (ئەجەبلىنىپ): «ئاللاھ بۇ شەھەرنىڭ ئۆلگەن ئاھالىسىنى قانداق تىرىلدۈرەر؟» دېدى. ئاللاھ ئۇنى يۈز يىل ئۆلۈك ھالىتىدە تۇرغۇزۇپ ئاندىن (كامالى قۇدرىتىنى كۆرسىتىش ئۈچۈن) تىرىلدۈردى. ئاللاھ (پەرىشتە ئارقىلىق) ئۇنىڭدىن: «(بۇ ھالەتتە) قانچىلىك تۇردۇڭ؟» دەپ سورىدى. ئۇ: «بىر كۈن ياكى بىر كۈنگە يەتمەيدىغان ۋاقىت تۇردۇم» دېدى. ئاللاھ ئېيتتى: «ئۇنداق ئەمەس، (تولۇق) 100 يىل تۇردۇڭ! يېمەك- ئىچمىكىڭگە قارىغىنكى، بۇزۇلمىغان؛ ئېشىكىڭگە قارىغىنكى (ئۇنىڭ سۆڭەكلىرى چىرىپ كەتكەن). (ساڭا يۇقىرىقىدەك ئادەتتىن تاشقىرى ئىشلارنى قىلغانلىقىمىز) سېنى كىشىلەرگە (قۇدرىتىمىزنىڭ) دەلىلى قىلىش ئۈچۈندۇر. بۇ سۆڭەكلەرگە قارىغىنكى، ئۇنى قانداق قۇراشتۇرىمىز، ئاندىن ئۇنىڭغا گۆش قوندۇرىمىز. (يۇقىرىقى ئىشلار) ئۇنىڭغا ئېنىق بولغان چاغدا (يەنى ئۇ روشەن مۆجىزىلەرنى كۆرگەن چاغدا)، ئۇ: «مەن ئاللاھنىڭ ھەممە نەرسىگە قادىر ئىكەنلىكىنى بىلدىم» دېدى.');
INSERT INTO "saleh" VALUES(2,260,'2:260','ئۆز ۋاقتىدا ئىبراھىم: «پەرۋەردىگارىم، ئۆلۈكلەرنى قانداق تىرىلدۈرىدىغانلىقىڭنى ماڭا كۆرسەتكىن» دېدى. ئاللاھ: «(ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىمغا) ئىشەنمىدىڭمۇ؟» دېدى. ئىبراھىم: «ئىشەندىم، لېكىن كۆڭلۈم (تېخىمۇ) قارار تاپسۇن ئۈچۈن (كۆرۈشنى تىلەيمەن)» دېدى. ئاللاھ ئېيتتى: «قۇشتىن تۆتنى ئالغىن، ئۇلارنى ئۆزۈڭگە توپلىغىن (يەنى ئۇلارنى پارچىلاپ گۆشلىرى بىلەن پەيلىرىنى ئارىلاشتۇرغىن)، ئاندىن ھەربىر تاغقا ئۇلاردىن بىر بۆلۈكىنى قويغىن، ئاندىن ئۇلارنى چاقىرغىن، سېنىڭ ئالدىڭغا ئۇلار چاپسان كېلىدۇ». بىلگىنكى، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,261,'2:261','ئاللاھنىڭ يولىدا پۇل ـ مېلىنى سەرپ قىلغانلارنىڭ (سەرپ قىلغان نەرسىسى يەرگە تېرىلىپ) يەتتە باشاق چىقارغان، ھەر باشىقىدا 100 دان تۇتقان بىر دانغا ئوخشايدۇ. ئاللاھ خالىغان بەندىسىگە ھەسسىلەپ ساۋاب بېرىدۇ. ئاللاھنىڭ مەرھەمىتى (چەكلىك ئەمەس) كەڭدۇر (ئاللاھ مال سەرپ قىلغۇچىنىڭ نىيىتىنى بىلگۈچىدۇر).');
INSERT INTO "saleh" VALUES(2,262,'2:262','پۇل ـ مېلىنى ئاللاھ يولىدا سەرپ قىلىدىغان، ئارقىسىدىن ئۇنىڭغا مىننەت قىلمايدىغان، ۋە (خەير ـ ساخاۋىتىگە ئېرىشكەنلەرنى) رەنجىتمەيدىغان كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (قىيامەت كۈنى) قورقۇنچ يوقتۇر ۋە ئۇلار غەم ـ ئەنشدىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,263,'2:263','ياخشى سۆز ۋە كەچۈرۈش كېيىن ئەزىيەت ئېلىپ كېلىدىغان سەدىقىدىن ئەۋزەلدۇر. ئاللاھ (بەندىلەرنىڭ سەدىقىسىدىن) بىھاجەتتۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(2,264,'2:264','ئى مۆمىنلەر! پۇل ـ مېلىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان، ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان كەلتۈرمەيدىغان كىشى (نىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتكىنىگە) ئوخشاش، بەرگەن سەدىقەڭلارنى مىننەت قىلىش ۋە ئەزىيەت يەتكۈزۈش بىلەن بىكار قىلىۋەتمەڭلار. بۇنداق (پۇل ـ مېلىنى باشقىلارغا كۆرسىتىش ئۈچۈن سەرپ قىلىدىغان) ئادەم خۇددى ئۈستىگە توپا ـ چاڭ قونۇپ قالغان، قاتتىق يامغۇردىن كېيىن (يۇيۇلۇپ) بۇرۇنقىدەك بولۇپ قالغان سىلىق تاشقا ئوخشايدۇ. ئۇلار قىلغان ئەمەللىرى ئۈچۈن (ئاخىرەتتە) ھېچقانداق ساۋابقا ئىگە بولالمايدۇ. ئاللاھ كاپىر قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,265,'2:265','ماللىرىنى ئاللاھنىڭ رازىلىقىنى ئىزدەپ ۋە چىن كۆڭلىدىن چىقىرىپ سەرپ قىلىدىغانلار ئېگىزلىككە جايلاشقان، قاتتىق يامغۇردىن بەھرىمەن بولسا مېۋىسى ئىككى ھەسسە كۆپ بولىدىغان، قاتتىق يامغۇردىن بەھرىمەن بولمىسا شەبنەم بىلەنمۇ قانىدىغان (قايسى ھالدا بولسۇن مېۋە بېرىدىغان) بىر باغقا ئوخشايدۇ (دېمەك، بۇنداق خەير ـ ساخاۋەتلىك ئادەم مېلى كۆپ بولسا كۆپكە يارىشا، ئاز بولسا ئازغا يارىشا بېرىدۇ). ئاللاھ قىلغان ئەمەلىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,266,'2:266','ئاراڭلاردا كىممۇ ياقتۇرسۇنكى، ئۇنىڭ خورمىلىق، ئۈزۈملۈك، ئاستىدىن ئېرىقلار ئېقىپ تۇرىدىغان، مېۋىلەرنىڭ ھەممە خىللىرى بولغان بىر بېغى بولسۇن، ئۇنىڭ ئۆزى قېرىپ قالغان، بالىلىرى ئۇششاق بولۇپ، (بۇ باغ) قارا بوران ئېلىپ كەلگەن ئوتتا كۆيۈپ كەتسۇن. سىلەرنىڭ ئويلىنىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە ئايەتلىرىنى شۇنداق (گۈزەل مىسال بىلەن) بايان قىلىدۇ (يەنى پۇل ـ مېلىنى رىيا ئۈچۈن سەرپ قىلغان ياكى باشقىلارغا رەنجىتىش، مىننەت قىلىش بىلەن سەرپ قىلغان ئادەم، ساۋابىنى ئەڭ موھتاج تۇرۇقلۇق يوقىتىپ قويغان بولىدۇ).');
INSERT INTO "saleh" VALUES(2,267,'2:267','ئى مۆمىنلەر! سىلەر ئېرىشكەن نەرسىلەرنىڭ ۋە بىز سىلەرگە زېمىندىن چىقىرىپ بەرگەن نەرسىلەر (يەنى ئاشلىقلار، مېۋىلەر) نىڭ ياخشىلىرىدىن سەدىقە قىلىڭلار؛ ئۆزۈڭلارمۇ پەقەت كۆزۈڭلارنى يۇمۇپ تۇرۇپ ئالىدىغان ناچارلىرىنى ئىلغاپ سەدىقە قىلماڭلار. بىلىڭلاركى، ئاللاھ (بۇنداق سەدىقەڭلاردىن) بىھاجەتتۇر، ھەمدىگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(2,268,'2:268','شەيتان سىلەرنى پېقىرلىقتىن قورقىتىدۇ، يامان ئىشلارغا (يەنى بېخىللىققا، زاكات بەرمەسلىككە) بۇيرۇيدۇ؛ ئاللاھ سىلەرگە ئۆز مەغپىرىتىنى ۋە پەزلىنى ۋەدە قىلىدۇ. ئاللاھنىڭ مەرھەمىتى كەڭدۇر، ئۇ ھەممىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(2,269,'2:269','ھېكمەتنى (ياخشى ئەمەلگە يېتەكلەيدىغان پايدىلىق ئىلىمنى) خالىغان بەندىسىگە ئاتا قىلىدۇ؛ ھېكمەت ئاتا قىلىنغان ئادەمگە كۆپ ياخشىلىق ئاتا قىلىنغان بولىدۇ. پەقەت ئەقىللىق ئادەملەرلا بۇنىڭدىن پەند ـ نەسىھەت ئالىدۇ.');
INSERT INTO "saleh" VALUES(2,270,'2:270','نېمىنى خەير ـ ئېھسان قىلماڭلار ياكى (ئاللاھنىڭ يولىدا) نېمىنى ۋەدە قىلماڭلار، ئاللاھ ئۇنى ھەقىقەتەن بىلىپ تۇرىدۇ. زالىملار (زاكاتنى مەنئى قىلغۇچىلار ياكى پۇل ـ مېلىنى گۇناھ ئىشلارغا سەرپ قىلغۇچىلار) غا ھېچقانداق مەدەتكار چىقمايدۇ.');
INSERT INTO "saleh" VALUES(2,271,'2:271','سەدىقىنى ئاشكارا بەرسەڭلار، بۇ ياخشىدۇر، ئەگەر ئۇنى مەخپى بەرسەڭلار ۋە يوقسۇللارغا بەرسەڭلار، تېخىمۇ ياخشىدۇر. بۇ، سىلەرنىڭ بەزى گۇناھلىرىڭلارغا كەففارەت بولىدۇ، ئاللاھ سىلەرنىڭ قىلغان ئەمەلىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(2,272,'2:272','(ئى مۇھەممەد!) ئۇلارنى ھىدايەت قىلىش سېنىڭ مەسئۇلىيىتىڭ ئەمەس (ساڭا تاپشۇرۇلغان ۋەزىپە پەقەت كىشىلەرگە ئاللاھنىڭ ۋەھىيسىنى يەتكۈزۈشتۇر). لېكىن ئاللاھ خالىغان بەندىسىنى ھىدايەت قىلىدۇ. مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، ئۇ ئۆزەڭلارنىڭ پايدىسى ئۈچۈندۇر، سىلەر پەقەت ئاللاھنىڭ رازىلىقى ئۈچۈنلا خەير ـ ئېھسان قىلىسىلەر (خەير ـ ئېھسانىڭلار ئۈچۈن ئاللاھنىڭ رازىلىقىدىن باشقىنى كۆزدە تۇتماڭلار)، مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى قىلغان ياخشى ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ).');
INSERT INTO "saleh" VALUES(2,273,'2:273','ئۆزىنى ئاللاھ يولىغا ئاتىغان، (تىرىكچىلىك قىلىش ئۈچۈن) يىراق جايلارغا بارالمايدىغان يوقسۇللارغا خەير ـ ئېھسان قىلىنىشى كېرەك. ئەھۋالنى ئۇقمىغانلار، ئۇلارنىڭ نەرسە تىلىمىگەنلىكلىرىگە قاراپ، ئۇلارنى باي دەپ گۇمان قىلىدۇ، ئۇلارنىڭ چىرايىدىن تونۇيسەن، ئۇلار كىشىلەرگە چاپلىشىۋېلىپ تىلەمچىلىك قىلمايدۇ، مېلىڭلاردىن نېمىنى خەير ـ ئېھسان قىلماڭلار، ئاللاھ ھەقىقەتەن ئۇنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,274,'2:274','ماللىرىنى كېچە ـ كۈندۈز (ھەممە ۋاقىت)، يوشۇرۇن ۋە ئاشكارا يوسۇندا خەير ـ ئېھسان قىلىدىغانلار پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب تاپىدۇ، ئۇلارغا (ئاخىرەتتە) قورقۇنچ يوقتۇر ۋە ئۇلار غەم ـ ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,275,'2:275','جازانە، ئۆسۈم يەيدىغان ئادەملەر (قىيامەت كۈنى گۆرلىرىدىن) جىن چېپىلىپ قالغان ساراڭ ئادەملەردەك قوپىدۇ. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار ئاللاھ ھارام قىلغان ئىشنى ھالال بىلىپ: «سودا ـ سېتىقمۇ جازانىگە ئوخشاش» دېدى. ھالبۇكى ئاللاھ سودا ـ سېتىقنى ھالال قىلدى، جازانىنى، (بەدەلسىز بولغانلىقى، شەخسكە ۋە جەمئىيەتكە زىيانلىق بولغانلىقى ئۈچۈن) ھارام قىلدى. كىمكى پەرۋەردىگارى تەرىپىدىن ۋەز ـ نەسىھەت كەلگەندىن كېيىن (يەنى جازانە مەنئى قىلىنغاندىن كېيىن ئۇنىڭدىن) يانسا، بۇرۇن ئالغىنى ئۆزىنىڭ بولىدۇ، ئۇنىڭ ئىشى ئاللاھقا تاپشۇرۇلىدۇ. قايتا جازانە قىلغانلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(2,276,'2:276','ئاللاھ جازانىنىڭ (بەرىكىتىنى) ئۆچۈرۈۋېتىدۇ، سەدىقىنىڭ (بەرىكىتىنى) زىيادە قىلىدۇ. ئاللاھ ھەربىر ناشۈكۈر (يەنى جازانىنى ھالال سانىغۇچى) گۇناھكارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(2,277,'2:277','ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ناماز ئوقۇغان، زاكات بەرگەن كىشىلەر پەرۋەردىگارىنىڭ دەرگاھىدا (چوڭ) ساۋابقا ئېرىشىدۇ، ئۇلارغا (ئاخىرەتتە) قورقۇنچ يوقتۇر ۋە ئۇلار غەم ـ ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(2,278,'2:278','ئى ئىمان ئېيتقان كىشىلەر! (ھەقىقىي) مۆمىن بولساڭلار، ئاللاھ (نىڭ ئەمرىگە) مۇخالىپەتچىلىك قىلىشتىن ساقلىنىڭلار، (كىشىلەرنىڭ زىممىسىدە) قېلىپ قالغان جازانىنى (يەنى ئۆسۈمنى) ئالماڭلار.');
INSERT INTO "saleh" VALUES(2,279,'2:279','ئەگەر شۇنداق قىلمىساڭلار، ئاللاھ ۋە ئۇنىڭ رەسۇلىغا ئۇرۇش ئاچقان بولىسىلەر. ئەگەر (جازانە قىلىشتىن) تەۋبە قىلساڭلار، (قەرز بەرگەن) سەرمايەڭلار ئۆزۈڭلارغا قايتىدۇ، باشقىلارنى زىيان تارتقۇزمايسىلەر، ئۆزۈڭلارمۇ زىيان تارتمايسىلەر.');
INSERT INTO "saleh" VALUES(2,280,'2:280','ئەگەر قەرزدارنىڭ قولى قىسقا بولسا، ئۇنىڭ ھالى ياخشىلانغىچە كۈتۈڭلار، ئەگەر (خەيرلىك ئىش ئىكەنلىكىنى بىلسەڭلار، قىيىنچىلىقتا قالغان قەرزداردىن ئالىدىغان قەرزنى ئۇنىڭغا) سەدىقە قىلىپ بېرىۋەتكىنىڭلار سىلەر ئۈچۈن تېخىمۇ ياخشىدۇر.');
INSERT INTO "saleh" VALUES(2,281,'2:281','سىلەر شۇنداق بىر كۈندىن (يەنى قىيامەت كۈنىدىن) قورقۇڭلاركى، ئۇ كۈندە سىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاندىن ھەر كىشى قىلغان (ياخشى ـ يامان) ئەمەلىنىڭ نەتىجىسىنى تولۇق كۆرىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ، (يەنى ياخشى ئەمەلى كېمەيتىلىپ، يامان ئەمەلى كۆپەيتىلمەيدۇ).');
INSERT INTO "saleh" VALUES(2,282,'2:282','ئى مۆمىنلەر! مۇددەت بەلگىلەپ ئۆزئارا قەرز بېرىشسەڭلار، ئۇنى (ھۆججەت قىلىپ) يېزىپ قويۇڭلار، ئاراڭلاردىكى خەت بىلىدىغان كىشى ئۇنى (كەم ـ زىيادە قىلماي) ئادىللىق بىلەن يازسۇن، خەت بىلىدىغان كىشى (ئاللاھ ئۇنىڭغا بىلدۈرگەندەك توغرىلىق بىلەن يېزىشىنى) رەت قىلمىسۇن، ئۇ يازسۇن، زىممىسىدە باشقىلارنىڭ ھەققى بولغان ئادەم (يازىدىغان كىشىگە) ئېيتىپ بەرسۇن، (قەرزدار) پەرۋەردىگارى ئاللاھتىن قورقسۇن، قەرز ئالغان نەرسىدىن ھېچنېمىنى كېمەيتىۋەتمىسۇن، ئەگەر قەرز ئالغۇچى ئادەم ئەخمەق (گالۋاڭ، ئەقىلسىز، ئىسراپخور) ياكى ئاجىز (كىچىك بالا ياكى بەكمۇ قېرى) بولسا، ياكى (گەپ قىلالماسلىقى، گاچىلىقى، كېكەچلىكى تۈپەيلىدىن) ئۆزى ئېيتىپ بېرەلمىسە، ئۇنىڭ ئىشلىرىنى باشقۇرغۇچى ئادەم ئادىللىق بىلەن ئېيتىپ بەرسۇن، سىلەر ئەر كىشىڭلاردىن ئىككى كىشىنى گۇۋاھلىققا تەكلىپ قىلىڭلار؛ ئەگەر ئىككى ئەر كىشى يوق بولسا، سىلەر (ئادالىتىگە، دىيانىتىگە) رازى بولىدىغان كىشىلەردىن بىر ئەر، ئىككى ئايالنى گۇۋاھلىققا تەكلىپ قىلىڭلار، بۇ ئىككى ئايالنىڭ بىرسى ئۇنتۇپ قالسا، ئىككىنچىسى ئېسىگە سالىدۇ. گۇۋاھچىلار (گۇۋاھلىققا) چاقىرىلغان ۋاقتىدا (گۇۋاھ بولۇشتىن) باش تارتمىسۇن. قەرز مەيلى ئاز بولسۇن، مەيلى كۆپ بولسۇن، ئۇنى قايتۇرۇش ۋاقتى بىلەن قوشۇپ يېزىشتىن ئېرىنمەڭلار. ئاللاھنىڭ نەزىرىدە (ھۆكمىدە)، بۇ ئەڭ ئادىللىق ۋە ئەڭ ئىسپاتلىقتۇر (گۇۋاھلىقنىڭ ئۇنتۇلماسلىقىنى ئەڭ ئىسپاتلىغۇچىدۇر)، گۇمانلانماسلىقىڭلارغا ئەڭ يېقىندۇر. لېكىن ئاراڭلاردا قىلىشقان قولمۇقول سودىدا ھۆججەت يازمىساڭلارمۇ ھېچ گۇناھ بولمايدۇ. ئۆزئارا سودا قىلىشقان ۋاقتىڭلاردا گۇۋاھچى تەكلىپ قىلىڭلار، پۈتۈكچىگىمۇ، گۇۋاھچىغىمۇ زىيان يەتكۈزۈلمىسۇن؛ ئەگەر زىيان يەتكۈزسەڭلار گۇناھ ئۆتكۈزگەن بولىسىلەر، ئاللاھنىڭ (ئەمرىگە ۋە نەھيىسىگە) مۇخالىپەتچىلىك قىلىشتىن ساقلىنىڭلار، ئاللاھ سىلەرگە ئۆگىتىدۇ (يەنى سىلەرگە ئىككىلا دۇنيادا پايدىلىق بولغان ئىلىمنى بېرىدۇ)، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,283,'2:283','ئەگەر سىلەر سەپەر ئۈستىدە بولۇپ، خەت يازالايدىغان ئادەمنى تاپالمىساڭلار، بۇ چاغدا (قەرز ھۆججىتى يېزىپ بېرىشنىڭ ئورنىغا) گۆرۈ قويىدىغان نەرسىنى تاپشۇرساڭلار بولىدۇ؛ ئاراڭلاردا بىرىڭلار بىرىڭلارغا ئىشىنىپ ئامانەت قويسا، ئۇ چاغدا ئامانەتنى ئالغۇچى ئادەم ئامانەتنى تاپشۇرسۇن (يەنى قەرز بەرگۈچى قەرز ئالغۇچىغا ئىشىنىپ ئۇنىڭدىن بىر نەرسىنى گۆرۈ ئالمىسا، قەرزدار قەرزنى دېيىشكەن ۋاقتىدا قايتۇرسۇن)، (قەرزدار ئامانەتكە رىئايە قىلىشتا) پەرۋەردىگارىدىن قورقسۇن. گۇۋاھلىقنى يوشۇرماڭلار، كىمكى ئۇنى يوشۇرىدىكەن، ھەقىقەتەن ئۇنىڭ دىلى گۇناھكار بولىدۇ. ئاللاھ قىلمىشىڭلارنى تامامەن بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(2,284,'2:284','ئاسمانلاردىكى، زېمىندىكى شەيئىلەر (ئىگىدارچىلىق قىلىش جەھەتتىن بولسۇن، تەسەررۇپ قىلىش جەھەتتىن بولسۇن) ئاللاھنىڭ ئىلكىدىدۇر. دىلىڭلاردىكى (يامانلىقنى) مەيلى ئاشكارا قىلىڭلار، مەيلى يوشۇرۇڭلار، ئۇنىڭ ئۈچۈن ئاللاھ سىلەردىن ھېساب ئالىدۇ. ئاللاھ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ. ئاللاھ ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(2,285,'2:285',' پەيغەمبەر پەرۋەردىگارى تەرىپىدىن ئۇنىڭغا نازىل قىلىنغان كىتابقا ئىمان كەلتۈردى، مۆمىنلەرمۇ ئىمان كەلتۈردى، ئۇلارنىڭ ھەممىسى ئاللاھقا ۋە ئاللاھنىڭ پەرىشتىلىرىگە، كىتابلىرىغا ۋە پەيغەمبەرلىرىگە ئىمان كەلتۈردى. (ئۇلار) «ئاللاھنىڭ پەيغەمبەرلىرىنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىگە ئىمان ئېيتماي قالمايمىز)» دەيدۇ. ئۇلار: «بىز (دەۋىتىڭنى) ئاڭلىدۇق ۋە (ئەمرىڭگە) ئىتائەت قىلدۇق، پەرۋەردىگارىمىز، مەغپىرىتىڭنى تىلەيمىز، ئاخىر يانىدىغان جايىمىز سېنىڭ دەرگاھىڭدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(2,286,'2:286','ئاللاھ ھېچكىمنى قۇربى يەتمەيدىغان ئىشقا تەكلىپ قىلمايدۇ. كىشىنىڭ قىلغان ياخشىلىقى (نىڭ ساۋابى) ئۆزىگىدۇر، يامانلىقى (نىڭ جازاسى) مۇ ئۆزىگىدۇر. (ئۇلار) «پەرۋەردىگارىمىز! ئەگەر بىز ئۇنۇتساق ياكى خاتالاشساق (بىز ئۇنتۇش ياكى سەۋەنلىكتىن ئەمرىڭنى تولۇق ئورۇنلىيالمىساق)، بىزنى جازاغا تارتمىغىن. پەرۋەردىگارىمىز! بىزدىن ئىلگىرىكىلەرگە يۈكلىگىنىڭگە ئوخشاش بىزگە ئېغىر يۈك يۈكلىمىگىن (بىزنى قىيىن ئىشلارغا تەكلىپ قىلمىغىن)، پەرۋەردىگارىمىز! كۈچىمىز يەتمەيدىغان نەرسىنى بىزگە ئارتمىغىن، بىزنى كەچۈرگىن، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بىزنىڭ ئىگىمىزسەن، كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(3,1,'3:1','ئەلىف، لام، مىم.');
INSERT INTO "saleh" VALUES(3,2,'3:2','ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئاللاھ (ھەمىشە) تىرىكتۇر، مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,3,'3:3','(ئى مۇھەممەد!) (ئاللاھ) ساڭا ئۆزىدىن ئىلگىرىكى كىتابلارنى تەستىق قىلغۇچى كىتابنى (يەنى قۇرئاننى) ھەق بىلەن نازىل قىلدى. ئىلگىرى، كىشىلەرگە يول كۆرسەتكۈچى قىلىپ تەۋرات بىلەن ئىنجىلنى نازىل قىلغان ئىدى.');
INSERT INTO "saleh" VALUES(3,4,'3:4','ھەق بىلەن باتىلنى ئايرىغۇچى كىتابنى (يەنى بارلىق ساماۋى كىتابلارنى) نازىل قىلدى. شۈبھىسىزكى، ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ. ئاللاھ غالىبتۇر، (يامانلارنى) جازالىغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,5,'3:5','شۈبھىسىزكى، ئاسمان ۋە زېمىندىكى ھېچ نەرسە ئاللاھقا مەخپىي ئەمەس.');
INSERT INTO "saleh" VALUES(3,6,'3:6','ئۇ سىلەرنى بەچچىدانلاردا (يەنى ئاناڭلارنىڭ قورسىقىدىكى چېغىڭلاردا) ئۆزى خالىغان شەكىلگە كىرگۈزىدۇ. ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,7,'3:7','(ئى مۇھەممەد!) ئۇ (ئاللاھ) ساڭا كىتابنى (يەنى قۇرئاننى) نازىل قىلدى. ئۇنىڭدا مۇھكەم (يەنى مەنىسى ئوچۇق) ئايەتلەر باركى، ئۇلار كىتابنىڭ (يەنى پۈتۈن قۇرئاننىڭ) ئاساسىدۇر؛ يەنە باشقا مۇتەشابىھ (يەنى مەنىسى مۇئەييەن ئەمەس) ئايەتلەر باردۇر. دىللىرىدا ئەگرىلىك بار (يەنى گۇمراھلىققا مايىل) كىشىلەر پىتنە قوزغاش ۋە ئۆز رايى بويىچە مەنە بېرىش ئۈچۈن، مۇتەشابىھ ئايەتلەرگە ئەگىشىدۇ (يەنى مۇتەشابىھ ئايەتلەرنى ئۆز نەپسى - خاھىشى بويىچە چۈشەندۈرىدۇ). بۇنداق ئايەتلەرنىڭ (ھەقىقىي) مەنىسىنى پەقەت ئاللاھ بىلىدۇ. ئىلىمدا توشقانلار ئېيتىدۇ: «ئۇنىڭغا ئىشەندۇق، ھەممىسى پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان. (بۇنى) پەقەت ئەقىل ئىگىلىرىلا چۈشىنىدۇ.');
INSERT INTO "saleh" VALUES(3,8,'3:8','پەرۋەردىگارىمىز! بىزنى ھىدايەت قىلغىنىڭدىن كېيىن دىللىرىمىزنى توغرا يولدىن بۇرىۋەتمىگىن، بىزگە دەرگاھىڭدىن رەھمەت بېغىشلىغىن. شۈبھىسىزكى، سەن (بەندىلىرىڭگە ئاتالارنى) بەكمۇ بېغىشلىغۇچىسەن.');
INSERT INTO "saleh" VALUES(3,9,'3:9','پەرۋەردىگارىمىز! شۈبھىسىزكى، كېلىشى شەكسىز بولغان كۈندە (يەنى ھېساب ئېلىنىدىغان قىيامەت كۈنىدە) كىشىلەرنى توپلىغۇچىسەن. شۈبھىسىزكى، ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ».');
INSERT INTO "saleh" VALUES(3,10,'3:10','كاپىرلارنىڭ ماللىرى، بالىلىرى ئاللاھنىڭ ئازابى ئالدىدا ھەقىقەتەن ھېچ نەرسىگە دال بولالمايدۇ. ئۇلار (يەنى كۇففارلار) دوزاخنىڭ يېقىلغۇسىدۇر.');
INSERT INTO "saleh" VALUES(3,11,'3:11','(بۇ كۇففارلارنىڭ ھالى) پىرئەۋن تەۋەلىرىنىڭ ۋە ئۇلاردىن ئىلگىرىكىلەرنىڭ ھالىغا ئوخشايدۇ. ئۇلار بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقاردى، گۇناھى تۈپەيلىدىن ئاللاھ ئۇلارنى جازالىدى، ئاللاھنىڭ جازاسى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(3,12,'3:12','(ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، «سىلەر (دۇنيادا) مەغلۇپ قىلىنىسىلەر ۋە (ئاخىرەتتە) جەھەننەمگە توپلىنىسىلەر، جەھەننەم نېمىدېگەن يامان تۆشەك».');
INSERT INTO "saleh" VALUES(3,13,'3:13','(ئى يەھۇدىيلار جامائەسى! بەدرىدە جەڭ ئۈچۈن) ئۇچراشقان ئىككى گۇرۇھتا سىلەر ئۈچۈن ئىبرەت بار، بىرى ئاللاھ يولىدا ئۇرۇشۇۋاتقان گۇرۇھتۇر، يەنە بىرى مۇسۇلمانلارنى (يەنى ئۇلارنىڭ سانىنى) ئۆزلىرىدەك ئىككى باراۋەر ئوپئوچۇق كۆرگەن كاپىرلار گۇرۇھىدۇر (يەنى ئاللاھ كاپىرلارغا قورقۇنچ سېلىش بىلەن ئۇلارنى ئۇرۇشتىن قول يىغدۇرۇش ئۈچۈن، كاپىرلارغا مۇسۇلمانلارنىڭ سانىنى جىق كۆرسەتتى). ئاللاھ خالىغان كىشىگە ياردەم بېرىپ قوللايدۇ. بۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن ئىبرەت بار.');
INSERT INTO "saleh" VALUES(3,14,'3:14','ئاياللار، ئوغۇللار، ئالتۇن ـ كۈمۈشتىن توپلانغان كۆپ ماللار، ئارغىماقلار، چارۋىلار ۋە ئېكىنلەردىن ئىبارەت كۆڭۈل تارتىدىغان نەرسىلەرنىڭ مۇھەببىتى ئىنسانلارغا چىرايلىق كۆرسىتىلدى. ئۇلار دۇنيا تىرىكچىلىكىدە مەنپەئەتلىنىدىغان (باقاسى يوق) شەيئىلەردۇر؛ ئاللاھنىڭ دەرگاھىدا بولسا قايتىدىغان گۈزەل جاي (يەنى جەننەت) باردۇر (شۇنىڭ ئۈچۈن باشقىغا ئەمەس، جەننەتكە قىزىقىش كېرەك).');
INSERT INTO "saleh" VALUES(3,15,'3:15','(ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «سىلەرگە ئۇلاردىنمۇ (يەنى دۇنيانىڭ زىبۇزىننەتلىرى ۋە نېمەتلىرىدىنمۇ) ياخشى بولغان نەرسىلەرنى ئېيتىپ بېرەيمۇ؟ تەقۋادارلار ئۈچۈن پەرۋەردىگارى ھۇزۇرىدا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بولۇپ، (ئۇلار) جەننەتلەردە مەڭگۈ قالىدۇ، (جەننەتلەردە) پاك جۈپتىلەر بار، (تەقۋادارلار ئۈچۈن) يەنە ئاللاھنىڭ رىزاسى بار». ئاللاھ بەندىلىرىنى (يەنى ئۇلارنىڭ ھەممە ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,16,'3:16','ئۇلار (يەنى تەقۋادارلار): «پەرۋەردىگارىمىز! بىز شۈبھىسىز ئىمان ئېيتتۇق، بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، بىزنى دوزاخ ئازابىدىن ساقلىغىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(3,17,'3:17','ئۇلار (يەنى تەقۋادارلار) سەۋر قىلغۇچىلاردۇر، راستچىللاردۇر، (ئاللاھقا) ئىتائەت قىلغۇچىلاردۇر، (ياخشىلىق يوللىرىغا پۇل ـ ماللىرىنى) سەرپ قىلغۇچىلاردۇر ۋە سەھەرلەردە ئىستىغپار ئېيتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(3,18,'3:18','ئاللاھ ئادالەتنى بەرپا قىلغان ھالدا گۇۋاھلىق بەردىكى، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. پەرىشتىلەرمۇ، ئىلىم ئەھلىلىرىمۇ شۇنداق گۇۋاھلىق بەردى: ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. ئۇ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,19,'3:19','ھەقىقەتەن ئاللاھنىڭ نەزىرىدە مەقبۇل دىن ئىسلام (دىنى) دۇر. كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئۆزلىرىگە ئىلىم كەلگەندىن كېيىنلا ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى (يەنى ئىسلامنىڭ ھەقلىقىنى، مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىنى روشەن پاكىتلار ئارقىلىق بىلىپ تۇرۇپ، كۆرەلمەسلىكتىن ئىنكار قىلىشتى). كىمكى ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلىدىكەن (ئاللاھ ئۇنى يېقىندا جازالايدۇ)، ئاللاھ تېز ھېساب ئالغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(3,20,'3:20','(ئى مۇھەممەد!) ئەگەر ئۇلار سەن بىلەن (دىن بارىسىدا) مۇنازىرەلەشسە: «ماڭا ئەگەشكەنلەر بىلەن بىرلىكتە، مەن ئۆزەمنى ئاللاھقا تاپشۇردۇم (يەنى ئاللاھنىڭ ئەمرىلىرىگە بويسۇنغۇچىمىز)» دېگىن. كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە ئۇممىيلەرگە (يەنى ئەرەب مۇشرىكلىرىغا) : «مۇسۇلمان بولدۇڭلارمۇ؟» دېگىن، ئەگەر ئۇلار مۇسۇلمان بولسا، توغرا يول تاپقان بولىدۇ. ئەگەر ئۇلار يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ پەقەت تەبلىغ قىلىش مەسئۇلىيىتىڭ بار، ئاللاھ بەندىلىرىنى (يەنى ھەممە ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(3,21,'3:21','ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلىدىغانلار، پەيغەمبەرلەرنى ناھەق ئۆلتۈرىدىغانلار، كىشىلەرنىڭ ئىچىدىكى ئادالەتكە بۇيرۇيدىغانلار (يەنى ئاللاھقا دەۋەت قىلغۇچىلار) نى ئۆلتۈرىدىغانلارغا چوقۇم قاتتىق ئازاب بىلەن خۇش خەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(3,22,'3:22','ئەنە شۇلارنىڭ دۇنيا ۋە ئاخىرەتتىكى ئەمەللىرى بىكار بولدى، ئۇلارغا ھېچ ياردەم قىلغۇچى يوقتۇر.');
INSERT INTO "saleh" VALUES(3,23,'3:23','كىتابتىن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلارنىڭ ئۆلىمالىرى) نى كۆرمىدىڭمۇ؟ ئۇلار ئۆز ئارىسىدا (دەتالاش قىلىشقان ئىشلار توغرىسىدا) ئاللاھنىڭ كىتابى بويىچە ھۆكۈم چىقىرىشقا چاقىرىلسا، ئۇلارنىڭ بىر تۈركۈمى بۇنىڭدىن (يەنى ئاللاھنىڭ ھۆكمىنى قوبۇل قىلىشتىن) يۈز ئۆرۈيدۇ، ئۇلار ھەقتىن يۈز ئۆرۈگۈچىلەردۇر (يەنى ھەقتىن يۈز ئۆرۈش ئۇلارنىڭ تەبىئىتىگە سىڭىپ كەتكەن).');
INSERT INTO "saleh" VALUES(3,24,'3:24','بۇ ئۇلارنىڭ «دوزاخ ئوتى بىزنى ساناقلىق كۈنلەرلا (يەنى موزايغا چوقۇنغان 40 كۈنلۈك ۋاقىتلا) كۆيدۈرىدۇ» دېگەنلىكلىرى ۋە دىنىي جەھەتتە ئويدۇرۇپ چىقارغان نەرسىلىرىنىڭ ئۆزلىرىنى ئالدىغانلىقى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(3,25,'3:25','كېلىشى شەكسىز بولغان كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنى توپلايمىز، ھەر بىر كىشىنىڭ قىلغان ئىشىنىڭ جازاسى ۋە مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا (ئازابنى زىيادە قىلىۋېتىش ياكى ساۋابىنى كېمەيتىۋېتىش بىلەن) زۇلۇم قىلىنمايدىغان شۇ چاغدا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟.');
INSERT INTO "saleh" VALUES(3,26,'3:26','ئېيتقىنكى، «پادىشاھلىقنىڭ ئىگىسى بولغان ئى ئاللاھ! خالىغان ئادەمگە پادىشاھلىقنى بېرىسەن، خالىغان ئادەمدىن پادىشاھلىقنى تارتىپ ئالىسەن؛ خالىغان ئادەمنى ئەزىز قىلىسەن، خالىغان ئادەمنى خار قىلىسەن؛ ھەممە ياخشىلىق (نىڭ خەزىنىسى يالغۇز) سېنىڭ قولۇڭدىدۇر. سەن ھەقىقەتەن ھەممىگە قادىرسەن.');
INSERT INTO "saleh" VALUES(3,27,'3:27','كېچىنى كۈندۈزگە كىرگۈزىسەن، كۈندۈزنى كېچىگە كىرگۈزىسەن، (شۇنىڭ بىلەن كېچە-كۈندۈزنىڭ ئۇزۇن - قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)؛ جانلىقنى جانسىزدىن چىقىرىسەن، جانسىزنى جانلىقتىن چىقىرىسەن؛ ئۆزەڭ خالىغان كىشىگە ھەددى-ھېسابسىز رىزىق بېرىسەن».');
INSERT INTO "saleh" VALUES(3,28,'3:28','مۆمىنلەر مۆمىنلەرنى قويۇپ كاپىرلارنى ۋەلى قىلىۋالمىسۇن، كىمكى شۇنداق قىلىدىكەن، ئۇ ئاللاھتىن ياخشىلىق كۈتمىسۇن. ئۇلاردىن قورقۇپ دوستلۇق ئىزھار قىلىشىڭلار بۇنىڭدىن مۇستەسنا. ئاللاھ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ، ئاخىر قايتىدىغان جاي ئاللاھنىڭ دەرگاھىدۇر. ');
INSERT INTO "saleh" VALUES(3,29,'3:29','ئېيتقىنكى، «دىلىڭلاردىكىنى مەيلى يوشۇرۇڭلار، مەيلى ئاشكارىلاڭلار، ئاللاھ ئۇنى بىلىپ تۇرىدۇ. ئاللاھ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىمۇ بىلىپ تۇرىدۇ. ئاللاھ ھەر نەرسىگە قادىردۇر».');
INSERT INTO "saleh" VALUES(3,30,'3:30','ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئادەمنىڭ قىلغان ياخشى ئىشلىرى، يامان ئىشلىرى ئۆزىنىڭ ئالدىدا ئايان بولىدۇ، (ئۇلار يامان ئىشلىرىنى كۆرمەسلىكنى)، ئۆزى بىلەن يامان ئىشلىرىنىڭ ئارىسىدا (مەشرىق بىلەن مەغرىبنىڭ ئارىسىدەك) ئۇزۇن مۇساپە بولۇشىنى ئارزۇ قىلىدۇ. ئاللاھ سىلەرنى ئۆزىنىڭ ئازابىدىن قورقۇتىدۇ. ئاللاھ بەندىلىرىگە مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(3,31,'3:31','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر سىلەر ئاللاھنى دوست تۇتساڭلار، ماڭا ئەگىشىڭلاركى، ئاللاھ سىلەرنى دوست تۇتىدۇ، (ئۆتكەنكى) گۇناھىڭلارنى مەغپىرەت قىلىدۇ. ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(3,32,'3:32','ئېيتقىنكى، «ئاللاھقا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار». ئەگەر ئۇلار (ئىتائەت قىلىشتىن) يۈز ئۆرۈسە، ئاللاھ كاپىرلارنى (يەنى ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلارنى) دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(3,33,'3:33','ئاللاھ ھەقىقەتەن ئادەمنى، نۇھنى، ئىبراھىم ئەۋلادىنى، ئىمران ئەۋلادىنى (پەيغەمبەرلىككە) تاللاپ، (ئۇلارنى ئۆزلىرىنىڭ زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدى.');
INSERT INTO "saleh" VALUES(3,34,'3:34','ئۇ ئەۋلادلار بىر ـ بىرى بىلەن قانداشتۇر، ئاللاھ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلەرنىڭ نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,35,'3:35','ئۆز ۋاقتىدا ئىمراننىڭ ئايالى ئېيتتى: «پەرۋەردىگارىم! مەن قورسىقىمدىكى پەرزەنتىمنى (دۇنيا ئىشلىرىدىن) ئازاد قىلىنغان ھالدا چوقۇم سېنىڭ خىزمىتىڭگە ئاتىدىم، (بۇ نەزرەمنى) قوبۇل قىلغىن، سەن ھەقىقەتەن (دۇئايىمنى) ئاڭلاپ تۇرغۇچىسەن، (نىيىتىمنى) بىلىپ تۇرغۇچىسەن».');
INSERT INTO "saleh" VALUES(3,36,'3:36','ئۇ قىز تۇغقان ۋاقتىدا: «ئى پەرۋەردىگارىم! قىز تۇغدۇم» دېدى، ئۇنىڭ نېمە تۇغقانلىقىنى ئاللاھ ئوبدان بىلىدۇ. (ئۇ تەلەپ قىلغان) ئوغۇل، (ئاتا قىلىنغان) قىزغا ئوخشاش ئەمەس، (بەلكى بۇ قىز ئارتۇقتۇر). (ئىمراننىڭ ئايالى ئېيتتىكى) «مەن ئۇنى مەريەم دەپ ئاتىدىم، مەن ھەقىقەتەن ئۇنىڭ ئۈچۈن، ئۇنىڭ ئەۋلادلىرى ئۈچۈن ساڭا سىغىنىپ، قوغلاندى شەيتاندىن پاناھ تىلەيمەن».');
INSERT INTO "saleh" VALUES(3,37,'3:37','ئۇنى پەرۋەردىگارى چىرايلىقچە قوبۇل قىلدى، ئۇنى ئوبدان ئۆستۈردى، ئۇنىڭغا زەكەرىيانى كېپىل قىلدى (يەنى ئۇنىڭ خىزمىتىنى قىلىشقا زەكەرىيانى قويدى). زەكەرىيا ھەر قېتىم (ئۇنىڭ) ئىبادەتگاھىغا ئۇنى كۆرگىلى كىرسە، ئۇنىڭ يېنىدا بىرەر يېمەكلىك ئۇچرىتاتتى. ئۇ: «ئى مەريەم! بۇ ساڭا قەيەردىن كەلدى؟» دەيتتى، مەريەم: «ئۇ، ئاللاھ تەرىپىدىن كەلدى» دەيتتى. ئاللاھ ھەقىقەتەن خالىغان ئادەمگە (جاپا ـ مۇشەققەتسىزلا) ھەددى-ھېسابسىز رىزىق بېرىدۇ.');
INSERT INTO "saleh" VALUES(3,38,'3:38','شۇ ۋاقىتتا (ئاللاھنىڭ مەريەمگە قىلغان ئىنئامىنى كۆرگەن چاغدا) زەكەرىيا پەرۋەردىگارىغا دۇئا قىلىپ: «ئى پەرۋەردىگارىم! ماڭا ئۆز دەرگاھىڭدىن بىر ياخشى پەرزەنت ئاتا قىلغىن، ھەقىقەتەن سەن دۇئانى ئىشىتكۈچىسەن» دېدى.');
INSERT INTO "saleh" VALUES(3,39,'3:39','ئۇ ئىبادەتگاھىدا ناماز ئوقۇۋاتقاندا پەرىشتىلەر ئۇنىڭغا: «ساڭا ئاللاھنىڭ كەلىمىسى (يەنى ئىيسا) نى تەستىق قىلغۇچى، سەييىد، پەرھىزكار ۋە ياخشىلاردىن بولغان يەھيا ئىسىملىك بىر پەيغەمبەر بىلەن خۇش خەۋەر بېرىدۇ» دەپ نىدا قىلدى.');
INSERT INTO "saleh" VALUES(3,40,'3:40','ئۇ: «پەرۋەردىگارىم! مەن قېرىپ قالغان تۇرسام، ئايالىم تۇغۇتتىن قالغان تۇرسا قانداقمۇ مېنىڭ ئوغلۇم بولسۇن؟» دېدى. پەرىشتىلەر: «ئاللاھ شۇنىڭدەك خالىغىنىنى قىلىدۇ (يەنى قۇدرىتى ئىلاھىييە ھېچ نەرسىدىن ئاجىز ئەمەس)» دېدى.');
INSERT INTO "saleh" VALUES(3,41,'3:41','ئۇ: ‹‹پەرۋەردىگارىم! ماڭا (ئايالىمنىڭ ھامىلىدار بولغانلىقىغا) بىر نىشانە ئاتا قىلغىن» دېدى. ئاللاھ: ‹‹ساڭا بولىدىغان نىشانە شۇكى، ئۈچ كۈنگۈچە كىشىلەرگە سۆز قىلالمايسەن (يەنى زىكرى - تەسبىھ ئېيتىشتىن باشقا سۆزگە تىلىڭ كەلمەيدۇ). پەقەت ئىشارەتلا قىلىسەن، (نېمىتىمگە شۈكۈر قىلىش يۈزىسىدىن) پەرۋەردىگارىڭنى كۆپ ياد ئەت، كەچتە، ئەتىگەندە تەسبىھ ئېيت» دېدى.');
INSERT INTO "saleh" VALUES(3,42,'3:42','پەرىشتىلەر ئېيتتى: «ئى مەريەم! ئاللاھ ھەقىقەتەن سېنى (پۈتۈن ئاياللار ئىچىدىن) تاللىدى، سېنى پاك قىلدى، سېنى پۈتۈن جاھان ئاياللىرىدىن ئارتۇق قىلدى.');
INSERT INTO "saleh" VALUES(3,43,'3:43','ئى مەريەم! پەرۋەردىگارىڭغا ئىتائەت قىل، سەجدە قىل ۋە رۇكۇ قىلغۇچىلار بىلەن بىللە رۇكۇ قىل».');
INSERT INTO "saleh" VALUES(3,44,'3:44','(ئى مۇھەممەد!) بۇ ـ ساڭا ۋەھىي قىلىۋاتقىنىمىز ـ غەيب خەۋەرلىرىدىندۇر. مەريەمنى ئۇلارنىڭ قايسىسى تەربىيەسىگە ئېلىش مەسىلىسىدە (چەك تاشلاش يۈزىسىدىن) قەلەملىرىنى (سۇغا) تاشلىغان چاغدا، سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ، (مەريەمنىڭ كىمنىڭ تەربىيەسىدە بولۇشىنى) ئۆزئارا جاڭجال قىلىشقانلىرىدا سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ.');
INSERT INTO "saleh" VALUES(3,45,'3:45','ئۆز ۋاقتىدا پەرىشتىلەر ئېيتتى: «ئى مەريەم! ئاللاھ ساڭا (ئاتىنىڭ ۋاسىتىسىسىز) ئاللاھنىڭ بىر كەلىمىسى (دىن تۆرەلگەن بىر بوۋاق) بىلەن خۇش خەۋەر بېرىدۇكى، ئۇنىڭ ئىسمى، مەريەم ئوغلى مەسىھ ئىيسادۇر. ئۇ دۇنيا ۋە ئاخىرەتتە ئابرۇيلۇق ۋە ئاللاھقا يېقىنلاردىن بولىدۇ.');
INSERT INTO "saleh" VALUES(3,46,'3:46','ئۇ بۆشۈكتىمۇ (يەنى بوۋاقلىق چېغىدىمۇ)، ئوتتۇرا ياش بولغاندىمۇ كىشىلەرگە (پەيغەمبەرلەرنىڭ سۆزىنى) سۆزلەيدۇ ۋە (تەقۋادارلىقتا كامىل) ياخشى ئادەملەردىن بولىدۇ».');
INSERT INTO "saleh" VALUES(3,47,'3:47','مەريەم: «پەرۋەردىگارىم! ماڭا ئىنساننىڭ قولى تەگمىگەن تۇرسا قانداق بالام بولىدۇ؟» دېدى. پەرىشتە: «ئاللاھ خالىغىنىنى شۇنداق يارىتىدۇ (يەنى ئاتا ـ ئانا ئارقىلىقمۇ ۋە ئۇنىڭسىزمۇ يارىتىدۇ). بىرەر ئىشنىڭ بولۇشىنى ئىرادە قىلسا، ‹ۋۇجۇدقا كەل› دەيدۇ – دە، ئۇ ۋۇجۇدقا كېلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(3,48,'3:48','ئاللاھ ئۇنىڭغا خەتنى، ھېكمەتنى (يەنى سۆز - ھەرىكەتتە توغرا بولۇشنى)، تەۋراتنى، ئىنجىلنى ئۆگىتىدۇ.');
INSERT INTO "saleh" VALUES(3,49,'3:49','ئۇنى بەنى ئىسرائىلغا پەيغەمبەر قىلىپ ئەۋەتىدۇ. (ئۇ دەيدۇ:) «شۈبھىسىزكى، سىلەرگە مەن رەببىڭلار تەرىپىدىن بولغان (پەيغەمبەرلىكىمنىڭ راستلىقىنى كۆرسىتىدىغان) بىر مۆجىزە ئېلىپ كەلدىم. مەن سىلەرگە لايدىن قۇشنىڭ شەكلىدەك بىر نەرسە ياسايمەن، ئاندىن ئۇنىڭغا پۈۋلەيمەن ـ دە، ئاللاھنىڭ ئىزنى بىلەن ئۇ قۇش بولىدۇ. تۇغما كورنى، بەرەس كېسىلىنى ساقايتىمەن، ئاللاھنىڭ ئىزنى بىلەن ئۆلۈكلەرنى تىرىلدۈرىمەن، سىلەر يەيدىغان ۋە ئۆيۈڭلاردا ساقلايدىغان يېمەكلىكلەردىن خەۋەر بېرىمەن (يەنى سىلەرنىڭ يوشۇرۇن ئەھۋالىڭلارنى ئېيتىپ بېرەلەيمەن). شۈبھىسىزكى، ئەگەر سىلەر ئاللاھنىڭ مۆجىزىلىرىگە ئىشەنگۈچى بولساڭلار، بۇنىڭدا (يەنى مەن كەلتۈرگەن مۆجىزىلەردە) سىلەر ئۈچۈن ئەلۋەتتە (مېنىڭ راستلىقىمنى كۆرسىتىدىغان روشەن) ئالامەت بار.');
INSERT INTO "saleh" VALUES(3,50,'3:50','مەن ئىلگىرى كەلگەن تەۋراتنى تەستىق قىلغان ھالدا (كەلدىم)، سىلەرگە ھارام قىلىنغان بەزى نەرسىلەرنى ھالال قىلىش ئۈچۈن (كەلدىم). سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن دەلىل ئېلىپ كەلدىم. ئاللاھتىن قورقۇڭلار ۋە (مېنىڭ ئەمرىمگە) ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(3,51,'3:51','ئاللاھ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر».');
INSERT INTO "saleh" VALUES(3,52,'3:52','ئىيسا ئۇلار (يەنى يەھۇدىيلار) دىن كۇفرىنى (كۇفرىدا چىڭ تۇرۇپ، ئۇنى ئۆلتۈرۈش نىيىتىگە كەلگەنلىكىنى) سەزگەن چاغدا: «ئاللاھ ئۈچۈن (ئاللاھقا دەۋەت قىلىش يولىدا) ماڭا كىملەر ياردەم بېرىدۇ؟» دېدى. ھەۋارىيلار (يەنى ئۇنىڭ تەۋەلىرىدىن ھەقىقىي مۆمىنلەر) ئېيتتى: «ئاللاھ ئۈچۈن ساڭا بىز ياردەم بېرىمىز، ئاللاھقا ئىمان ئېيتتۇق. گۇۋاھ بولغىنكى، بىز بويسۇنغۇچىلارمىز.');
INSERT INTO "saleh" VALUES(3,53,'3:53','پەرۋەردىگارىمىز! سەن نازىل قىلغان كىتابقا ئىشەندۇق، پەيغەمبەرگە ئەگەشتۇق، بىزنى (ھىدايىتىڭگە) شاھىت بولغانلار قاتارىدا قىلغىن».');
INSERT INTO "saleh" VALUES(3,54,'3:54','ئۇلار مىكىر ئىشلەتتى (يەنى ئىيسا ئەلەيھىسسالامنى ئۆلتۈرمەكچى بولدى)، ئاللاھ ئۇلارنىڭ مىكرىنى بەربات قىلدى (ئىيسا ئەلەيھىسسالامنى قۇتقۇزۇپ ئاسمانغا ئېلىپ چىقىپ كەتتى). ئاللاھ مىكىر قىلغۇچىلارنىڭ جازاسىنى ئوبدان بەرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(3,55,'3:55','ئۆز ۋاقتىدا ئاللاھ ئېيتتى: «ئى ئىيسا! مەن سېنى (ئەجىلىڭ يەتكەندە) قەبزى روھ قىلىمەن، سېنى دەرگاھىمغا كۆتۈرىمەن (يەنى ئاسمانغا ئېلىپ چىقىمەن). سېنى كاپىرلاردىن پاك قىلىمەن (يەنى سېنى ئۆلتۈرمەكچى بولغان يامانلارنىڭ شەررىدىن ساقلايمەن)، ساڭا ئەگەشكەنلەرنى قىيامەتكىچە كاپىرلاردىن ئۈستۈن قىلىمەن. ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان ئىش (يەنى ئىيسانىڭ ئىشى) ئۈستىدە ئاراڭلاردا مەن ھۆكۈم چىقىرىمەن. ');
INSERT INTO "saleh" VALUES(3,56,'3:56','كاپىرلارغا بولسا دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب قىلىمەن، ئۇلارغا (ئۇلاردىن ئاللاھنىڭ ئازابىنى توسىدىغان) ھېچ مەدەتكار بولمايدۇ». ');
INSERT INTO "saleh" VALUES(3,57,'3:57','ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا كەلسەك، ئاللاھ ئۇلارنىڭ (قىلغان ياخشى ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ. ئاللاھ زالىملارنى دوست تۇتمايدۇ (زالىملارنى دوست تۇتمايدىغان زات قانداقمۇ بەندىلىرىگە زۇلۇم قىلسۇن؟).');
INSERT INTO "saleh" VALUES(3,58,'3:58','(ئى مۇھەممەد!) بۇ بولسا ساڭا بىز ئوقۇپ بېرىۋاتقان ئايەتلەردۇر ۋە ھېكمەتلىك قۇرئاندۇر.');
INSERT INTO "saleh" VALUES(3,59,'3:59','شۈبھىسىزكى، ئاللاھنىڭ نەزىرىدە ئىيسانىڭ مىسالى (ئۇ ئاتىسىز يارىتىلغانلىقتىن) ئادەمنىڭ (يەنى ئادەم ئەلەيھىسسالامنىڭ) مىسالىغا ئوخشايدۇ. ئادەمنى ئاللاھ (ئاتا ـ ئانىسىز) تۇپراقتىن ياراتتى، ئاندىن ئۇنىڭغا: «ۋۇجۇدقا كەل» دېدى ـ دە، ئۇ ۋۇجۇدقا كەلدى (ئىيسانىڭ ئىشى ئادەمنىڭ ئىشىدىن ئەجەبلىنەرلىك ئەمەس).');
INSERT INTO "saleh" VALUES(3,60,'3:60','(ئىيسا ھەققىدىكى بۇ سۆز) ئاللاھ تەرىپىدىن نازىل بولغان ھەقىقەتتۇر، سەن شەك قىلغۇچىلاردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(3,61,'3:61','سەن ئىيسا توغرۇلۇق ھەقىقىي مەلۇماتقا ئىگە بولغىنىڭدىن كېيىن، كىملەركى سەن بىلەن مۇنازىرىلەشسە، سەن ئۇلارغا:«كېلىڭلار، ئوغۇللىرىمىزنى ۋە ئوغۇللىرىڭلارنى، ئاياللىرىمىزنى ۋە ئاياللىرىڭلارنى، ئۆزلىرىمىزنى ۋە ئۆزلىرىڭلارنى يىغىپ، ئاللاھنىڭ لەنىتى يالغانچىلارغا بولسۇن، دەپ ئاللاھقا يالۋۇرۇپ دۇئا قىلايلى» دېگىن.');
INSERT INTO "saleh" VALUES(3,62,'3:62','شۈبھىسىزكى، بۇ ئەلۋەتتە راست قىسەسىدۇر. بىر ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر، ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,63,'3:63','ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە (ئۇلار بۇزغۇنچىلاردۇر)، شۈبھىسىزكى، ئاللاھ بۇزغۇنچىلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(3,64,'3:64',' «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئاللاھقىلا ئىبادەت قىلىش، ئاللاھقا ھېچ نەرسىنى شېرىك كەلتۈرمەسلىك، ئاللاھنى قويۇپ بىر ـ بىرىمىزنى خۇدا قىلىۋالماسلىقتەك ھەممىمىزگە ئورتاق بولغان بىر سۆزگە (يەنى تەۋھىدئەقىدىسىگە) كېلىڭلار» دېگىن، ئەگەر ئۇلار يۈز ئۆرۈسە (يەنى بۇنى قوبۇل قىلمىسا): «(ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) بىزنىڭ مۇسۇلمان ئىكەنلىكىمىزگە گۇۋاھ بولۇڭلار.');
INSERT INTO "saleh" VALUES(3,65,'3:65','ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) نېمە ئۈچۈن ئىبراھىم توغرىسىدا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنى ئۆزۈڭلارنىڭ دىنىدا دەپ گۇمان قىلىسىلەر؟) ھالبۇكى، تەۋرات بىلەن ئىنجىل ئىبراھىمدىن كېيىن نازىل بولدى. (سۆزۈڭلارنىڭ ئاساسسىز ئىكەنلىكىنى) چۈشەنمەمسىلەر؟.');
INSERT INTO "saleh" VALUES(3,66,'3:66','(ئى يەھۇدىيلار ۋە ناسارالار جامائەسى!) سىلەر شۇنداق كىشىلەرسىلەركى، بىلىدىغان نەرسەڭلار (ئىيسا) ئۈستىدە مۇنازىرىلەشتىڭلار، ئەمدى سىلەر بىلمەيدىغان نەرسەڭلار (ئىبراھىم ۋە ئۇنىڭ دىنى) ئۈستىدە نېمىشقا مۇنازىرىلىشىسىلەر؟ (ئىبراھىمنىڭ ئىشىدىكى ھەقىقەتنى) ئاللاھ بىلىدۇ، سىلەر بىلمەيسىلەر» دېگىن. ');
INSERT INTO "saleh" VALUES(3,67,'3:67','ئىبراھىم يەھۇدىيمۇ ئەمەس، ناسارامۇ ئەمەس ۋەلېكىن توغرا دىنغا ئېتىقاد قىلغۇچى، ئاللاھقا بويسۇنغۇچى ئىدى، مۇشرىكلاردىن ئەمەس ئىدى.');
INSERT INTO "saleh" VALUES(3,68,'3:68',' ئىبراھىمغا ئەڭ يېقىن بولغانلار، شۈبھىسىزكى، (ئىبراھىمنىڭ زامانىدا ۋە ئۇنىڭدىن كېيىن) ئۇنىڭغا (يەنى ئۇنىڭ يولىغا) ئەگەشكەنلەردۇر، بۇ پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) دۇر، (بۇ پەيغەمبەرنىڭ ئۈممىتى بولغان) مۆمىنلەردۇر. ئاللاھ مۆمىنلەرنىڭ مەدەتكارىدۇر.');
INSERT INTO "saleh" VALUES(3,69,'3:69',' ئەھلى كىتابتىن بىر تۈركۈم كىشىلەر سىلەرنى ئازدۇرۇشنى ئۈمىد قىلىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ئازدۇرىدۇ (بۇنىڭ ۋابالى ئۇلارغا ھەسسىلەپ ئازاب قىلىنىش بىلەن ئۆزلىرىگىلا قايتىدۇ). (لېكىن) ئۇلار (بۇنى) تۇيمايدۇ. ');
INSERT INTO "saleh" VALUES(3,70,'3:70','ئى ئەھلى كىتاب! ئاللاھنىڭ ئايەتلىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان قۇرئاننىڭ ھەقلىقىنى) بىلىپ تۇرۇپ نېمىشقا ئىنكار قىلىسىلەر؟.');
INSERT INTO "saleh" VALUES(3,71,'3:71','ئى ئەھلى كىتاب! نېمە ئۈچۈن ھەقنى باتىلغا ئارىلاشتۇرىسىلەر ۋە بىلىپ تۇرۇپ ھەقنى (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ كىتابلىرىڭلاردىكى سۈپىتىنى) يوشۇرىسىلەر؟.');
INSERT INTO "saleh" VALUES(3,72,'3:72','ئەھلى كىتابتىن بىر تۈركۈمى: «مۆمىنلەرگە نازىل قىلىنغان ۋەھىيگە ئەتىگەندە ئىمان ئېيتىڭلار، كەچقۇرۇنلۇقى يېنىۋېلىڭلار، (شۇنداق قىلساڭلار) ئۇلار (ئىماندىن) قايتىشى مۇمكىن.');
INSERT INTO "saleh" VALUES(3,73,'3:73','پەقەت سىلەرنىڭ دىنىڭلارغا ئەگەشكەنلەرگىلا ئىشىنىڭلار» دېدى. (ئى مۇھەممەد!) ئېيتقىنكى، «توغرا يول ئاللاھنىڭ يولىدۇر». (يەھۇدىيلار بىر ـ بىرىگە ئېيتىدۇ) بىرەرسىگە سىلەرگە بېرىلگەنگە ئوخشاش ۋەھىي بېرىلىشتىن ياكى ئۇلارنىڭ (يەنى مۇسۇلمانلارنىڭ) (قىيامەت كۈنى) پەرۋەردىگارىڭلارنىڭ دەرگاھىدا سىلەر بىلەن مۇنازىرىلىشىشىدىن (يەنى مۇنازىرىلىشىپ سىلەرنى يېڭىپ قويۇشىدىن قورقساڭلار پەقەت دىنىڭلارغا ئەگەشكەن ئادەمگىلا ئىشىنىڭلار). (ئى مۇھەممەد!) ئېيتقىنكى، «پەزل ـ كەرەم (يەنى پەيغەمبەرلىك) ئاللاھنىڭ ئىلكىدىدۇر. ئاللاھ ئۇنى خالىغان كىشىگە بېرىدۇ». ئاللاھ (نىڭ پەزلى) كەڭدۇر، (ئاللاھ) ھەممىنى بىلگۈچىدۇر');
INSERT INTO "saleh" VALUES(3,74,'3:74','ئاللاھ رەھمىتىنى ئۆزى خالىغان كىشىگە خاس قىلىدۇ، ئاللاھ ئۇلۇغ پەزل ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(3,75,'3:75','ئەھلى كىتابتىن بەزى كىشىلەر باركى، ئۇنىڭدا كۆپ مال ئامانەت قويساڭمۇ ساڭا ئۇنى قايتۇرۇپ بېرىدۇ؛ ئۇلاردىن يەنە بەزى كىشىلەرمۇ باركى، ئۇنىڭدا بىر دىنار ئامانەت قويساڭمۇ ئۇنىڭ بېشىدا تۇرمىغىچە (يەنى قايتا ـ قايتا سۈيلىمىگۈچە) ئۇنى ساڭا قايتۇرۇپ بەرمەيدۇ. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار: «ئۇممىيلەرنىڭ مېلىغا خىيانەت قىلساق بىزگە گۇناھ بولمايدۇ (يەنى بىزنىڭ دىنىمىزدا بولمىغانلارنىڭ ماللىرى بىزگە ھارام ئەمەس)» دەيدۇ، ئۇلار بىلىپ تۇرۇپ ئاللاھ نامىدىن يالغاننى توقۇيدۇ. ');
INSERT INTO "saleh" VALUES(3,76,'3:76','ئۇنداق ئەمەس (يەنى ئۇلار باشقا دىندىكىلەرگە زۇلۇم قىلسا گۇناھ بولىدۇ). ئەھدىگە ۋاپا قىلغان ۋە گۇناھتىن ساقلانغانلار (ئاللاھ دوست تۇتقان كىشىلەردۇر)، چۈنكى ئاللاھ تەقۋادارلارنى ھەقىقەتەن دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(3,77,'3:77','ئاللاھقا بەرگەن ئەھدىنى ۋە قەسەملىرىنى ئازغىنا بەدەلگە تېگىشىدىغانلار ئاخىرەتتە (ئاللاھنىڭ رەھمىتىدىن) ھېچقانداق نېسىۋىگە ئېرىشەلمەيدۇ، قىيامەت كۈنى ئاللاھ ئۇلارغا (ئۇلارنى خۇش قىلىدىغان) سۆز قىلمايدۇ، ئۇلارغا (رەھمەت كۆزى بىلەن) قارىمايدۇ، ئۇلارنى (گۇناھلىرىدىن) پاكلىمايدۇ، ئۇلار قاتتىق ئازابقا قالىدۇ. ');
INSERT INTO "saleh" VALUES(3,78,'3:78','ئۇلاردىن (يەنى يەھۇدىيلاردىن) بىر تۈركۈمى كىتابتا بولمىغان نەرسىلەرنى، سىلەرنىڭ كىتابتا بار ئىكەن دەپ ئويلىشىڭلار ئۈچۈن تىللىرىنى ئەگرى ـ بۈگرى قىلىپ ئوقۇيدۇ ۋە ئۇنى ئاللاھ تەرىپىدىن نازىل بولغان دەيدۇ. ھالبۇكى، ئۇ ئاللاھ تەرىپىدىن نازىل بولغان ئەمەس، ئۇلار بىلىپ تۇرۇپ ئاللاھ نامىدىن يالغاننى توقۇيدۇ.');
INSERT INTO "saleh" VALUES(3,79,'3:79','بىرەر ئىنسانغا ئاللاھ كىتابنى، ھېكمەتنى، پەيغەمبەرلىكنى بەرگەندىن كېيىن، ئۇنىڭ كىشىلەرگە: «ئاللاھنى قويۇپ ماڭا بەندە بولۇڭلار» دېيىشى لايىق بولمايدۇ (يەنى پەيغەمبەردىن مۇنداق گەپنىڭ سادىر بولۇشىنى تەسەۋۋۇر قىلغىلى بولمايدۇ) ۋەلېكىن (ئۇ): «كىتابنى ئۆگەتكەنلىكىڭلار ۋە ئوقۇغانلىقىڭلار ئۈچۈن رەببانى بولۇڭلار» (دەيدۇ).');
INSERT INTO "saleh" VALUES(3,80,'3:80','شۇنىڭدەك ئۇنىڭ پەرىشتىلەرنى ۋە پەيغەمبەرلەرنى رەب قىلىۋېلىشىڭلارغا بۇيرۇشى لايىق بولمايدۇ. مۇسۇلمان بولغىنىڭلاردىن كېيىن پەيغەمبىرىڭلار سىلەرنى كۇفرىغا بۇيرۇمدۇ؟.');
INSERT INTO "saleh" VALUES(3,81,'3:81','ئۆز ۋاقتىدىلا ئاللاھ پەيغەمبەرلەردىن چىن ئەھدە ئېلىپ: «سىلەرگە مەن كىتابنى ۋە ھېكمەتنى ئاتا قىلدىم، كېيىن سىلەرگە، سىلەردىكى نەرسىلەرنى (يەنى كىتاب بىلەن ھېكمەتنى) ئېتىراپ قىلغۇچى بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلسە ئۇنىڭغا، ئەلۋەتتە ئىمان ئېيتىشىڭلار كېرەك ۋە ئۇنىڭغا ئەلۋەتتە ياردەم بېرىشىڭلار كېرەك» (دېدى). ئاللاھ: «(بۇ ئەھدىنى) ئىقرار (يەنى ئېتىراپ) قىلدىڭلارمۇ؟ شۇنىڭ (يەنى بۇ ئىش) ئۈچۈن مېنىڭ ئەھدىمنى قوبۇل قىلدىڭلارمۇ؟» دېدى. ئۇلار: «ئېتىراپ قىلدۇق» دېدى. ئاللاھ: «(ئۆزۈڭلارنىڭ ۋە تەۋەلىرىڭلارنىڭ ئېتىراپ قىلغانلىقىغا) گۇۋاھ بولۇڭلار، مەنمۇ سىلەر بىلەن بىللە گۇۋاھ بولغۇچىلاردىندۇرمەن» دېدى.');
INSERT INTO "saleh" VALUES(3,82,'3:82','كىمكى شۇنىڭدىن كېيىن يۈز ئۆرۈسە (يەنى ئەھدىنى بۇزسا)، ئۇلار ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(3,83,'3:83','ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) ئاللاھنىڭ دىنىدىن (يەنى ئىسلام دىنىدىن) باشقا دىننى تىلەمدۇ؟ ھالبۇكى، ئاسمانلاردىكىلەر ۋە زېمىندىكىلەر ئىختىيارىي ۋە ئىختىيارسىز رەۋىشتە ئاللاھقا بويسۇنغان تۇرسا، ئۇلار (قىيامەت كۈنى) ئاللاھ تەرىپىگە قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(3,84,'3:84','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «بىز ئاللاھقا ئىمان ئېيتتۇق، بىزگە نازىل قىلىنغانغا (يەنى قۇرئانغا)، ئىبراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا ۋە ئۇنىڭ ئەۋلادلىرىغا نازىل قىلىنغان ۋەھىيگە، مۇساغا بېرىلگەنگە (يەنى تەۋراتقا)،ئىيساغا بېرىلگەنگە (يەنى ئىنجىلغا) ۋە پەيغەمبەرلەرگە پەرۋەردىگارى تەرىپىدىن بېرىلگەنگە (يەنى كىتابلارغا) ئىمان ئېيتتۇق، ئۇلارنىڭ ھېچبىرىنى ئايرىۋەتمەيمىز (يەنى ئۇلارنىڭ بەزىسىگە ئىمان ئېيتىپ، بەزىسىنى ئىنكار قىلمايمىز)، بىز ئاللاھقا بويسۇنغۇچىلارمىز».');
INSERT INTO "saleh" VALUES(3,85,'3:85','كىمكى ئىسلامدىن غەيرىي دىننى تىلەيدىكەن، ھەرگىز ئۇ (يەنى ئۇنىڭ دىنى) قوبۇل قىلىنمايدۇ، ئۇ ئاخىرەتتە زىيان تارتقۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,86,'3:86','ئىمان ئېيتقاندىن، پەيغەمبەرنىڭ ھەق ئىكەنلىكىگە شاھىت بولغاندىن ۋە ئۆزلىرىگە روشەن دەلىللەر كەلگەندىن كېيىن كاپىر بولغان قەۋمنى ئاللاھ قانداقمۇ ھىدايەت قىلسۇن! ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(3,87,'3:87','ئاللاھنىڭ، پەرىشتىلەرنىڭ ۋە ئىنسانلارنىڭ ھەممىسىنىڭ لەنىتىگە ئۇچراش ئۇلارغا بېرىلگەن جازادۇر.');
INSERT INTO "saleh" VALUES(3,88,'3:88','ئۇلار دوزاختا مەڭگۈ قالىدۇ. ئۇلاردىن ئازاب يېنىكلىتىلمەيدۇ. كېچىكتۈرۈلمەيدۇ.');
INSERT INTO "saleh" VALUES(3,89,'3:89','پەقەت تەۋبە قىلغانلار ۋە (بۇزۇق ئەمەللىرىنى) تۈزەتكەنلەر بۇنىڭدىن مۇستەسنا، ئاللاھ مەغپىرەت قىلغۇچىدۇر ۋە مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(3,90,'3:90','شۈبھىسىزكى، ئىمان ئېيتقاندىن كېيىن كاپىر بولغانلار، ئاندىن كۇفرىنى تېخىمۇ ئاشۇرغانلارنىڭ تەۋبىسى ھەرگىزمۇ قوبۇل قىلىنمايدۇ. ئەنە شۇلار گۇمراھلاردۇر.');
INSERT INTO "saleh" VALUES(3,91,'3:91','شۈبھىسىزكى، كاپىر بولۇپ كۇفرى بىلەن ئۆلگەنلەرنىڭ ھېچبىرىدىن يەر يۈزىگە بىر كېلىدىغان ئالتۇن فىدىيە بەرسىمۇ قوبۇل قىلىنمايدۇ، ئەنە شۇلار قاتتىق ئازابقا قالىدۇ. ئۇلارغا (ئۇلارنى ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار بولمايدۇ. ');
INSERT INTO "saleh" VALUES(3,92,'3:92','ياخشى كۆرگەن نەرسەڭلاردىن سەرپ قىلمىغىچە غىچەھەرگىز ياخشىلىققا ئېرىشەلمەيسىلەر، (ئاللاھنىڭ يولىدا) نېمىنى سەرپ قىلساڭلار، ئاللاھ ئۇنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,93,'3:93','تەۋرات نازىل قىلىنىشتىن ئىلگىرى، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) ئۆزىگە ھارام قىلغان يېمەكلىكلەر (يەنى تۆگىنىڭ گۆشى ۋە سۈتى) دىن باشقا يېمەكلىكلەرنىڭ ھەممىسى ئىسرائىل ئەۋلادىغا ھالال ئىدى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئەگەر راستچىل بولساڭلار، تەۋراتنى ئېلىپ كېلىپ ئوقۇپ بېقىڭلار».');
INSERT INTO "saleh" VALUES(3,94,'3:94','شۇنىڭدىن كېيىن (يەنى روشەن دەلىل مەيدانغا كەلگەندىن كېيىن)، كىمكى ئاللاھ نامىدىن يالغاننى توقۇيدىكەن، ئۇلار زالىملاردۇر.');
INSERT INTO "saleh" VALUES(3,95,'3:95','ئېيتقىنكى، «ئاللاھ راست ئېيتتى، (ئىسلام دىنىدىن ئىبارەت) ئىبراھىمنىڭ توغرا دىنىغا ئەگىشىڭلار، ئۇ (يەنى ئىبراھىم) مۇشرىكلاردىن ئەمەس ئىدى».');
INSERT INTO "saleh" VALUES(3,96,'3:96','ھەقىقەتەن ئىنسانلارغا (ئىبادەت ئۈچۈن) تۇنجى سېلىنغان ئۆي (يەنى بەيتۇللاھ) مەككىدىدۇر، مۇبارەكتۇر، جاھان ئەھلىگە ھىدايەتتۇر.');
INSERT INTO "saleh" VALUES(3,97,'3:97','ئۇنىڭدا ئوچۇق ئالامەتلەر باركى، ماقامى ئىبراھىم (شۇلارنىڭ بىرى)، بەيتۇللاھقا كىرگەن ئادەم ئەمىن بولىدۇ. قادىر بولالىغان كىشىلەرنىڭ ئاللاھ ئۈچۈن كەبىنى زىيارەت قىلىشى ئۇلارغا پەرز قىلىندى. كىمكى ئىنكار قىلىدىكەن (يەنى ھەجنى تەرك ئېتىدىكەن، زىيىنى ئۆزىگە)، شۈبھىسىزكى، ئاللاھ ئەھلى جاھاندىن (يەنى ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر.');
INSERT INTO "saleh" VALUES(3,98,'3:98','ئى مۇھەممەد! ئېيتقىنكى: «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) نېمە ئۈچۈن ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلىسىلەر؟ ۋەھالەنكى، ئاللاھ قىلمىشىڭلارغا شاھىتتۇر».');
INSERT INTO "saleh" VALUES(3,99,'3:99','(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار، ناسارالار) (ئىسلام دىنىنىڭ ھەق ئىكەنلىكىگە) شاھىت بولۇپ تۇرۇپ نېمە ئۈچۈن ئاللاھنىڭ يولىنى ئەگرى كۆرسىتىشنى قەستلەپ، مۆمىنلەرنى ئۇنىڭدىن توسىسىلەر؟ ئاللاھ قىلمىشىڭلاردىن غاپىل ئەمەستۇر».');
INSERT INTO "saleh" VALUES(3,100,'3:100','ئى مۆمىنلەر! ئەگەر ئەھلى كىتابتىن بىر پىرقىغە ئىتائەت قىلساڭلار، مۆمىن بولغىنىڭلاردىن كېيىن، ئۇلار سىلەرنى كاپىر قىلىپ قويىدۇ.');
INSERT INTO "saleh" VALUES(3,101,'3:101','سىلەرگە ئاللاھنىڭ ئايەتلىرى ئوقۇلۇۋاتقان (يەنى ۋەھىي ئۈزۈلۈپ قالماي ئاللاھنىڭ ئايەتلىرى نازىل بولۇۋاتقان) ۋە ئاراڭلاردا ئاللاھنىڭ پەيغەمبىرى (ھايات) تۇرغان تۇرسا، قانداقمۇ كاپىر بولۇسىلەر؟ كىمكى ئاللاھقا (يەنى ئاللاھنىڭ دىنىغا) مەھكەم يېپىشىدىكەن، ئۇ توغرا يولغا يېتەكلەنگەن بولىدۇ.');
INSERT INTO "saleh" VALUES(3,102,'3:102','ئى مۆمىنلەر! ئاللاھقا لايىق رەۋىشتە تەقۋادارلىق قىلىڭلار، پەقەت مۇسۇلمانلىق ھالىتىڭلار بىلەنلا ۋاپات بولۇڭلار.');
INSERT INTO "saleh" VALUES(3,103,'3:103','ھەممىڭلار ئاللاھنىڭ ئارغامچىسىغا (يەنى ئاللاھنىڭ دىنىغا) مەھكەم يېپىشىڭلار، ئايرىلماڭلار (يەنى سىلەردىن ئىلگىرى يەھۇدىيلار ۋە ناسارالار ئىختىلاپ قىلىشقاندەك، دىندا ئىختىلاپ قىلىشماڭلار). ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار. ئۆز ۋاقتىدا سىلەر ئۆزئارا دۈشمەن ئىدىڭلار، ئاللاھ دىلىڭلارنى بىرلەشتۈردى، ئاللاھنىڭ نېمىتى بىلەن ئۆزئارا قېرىنداش بولدۇڭلار، سىلەر دوزاخ چۇقۇرىنىڭ گىرۋىكىدە ئىدىڭلار، ئاللاھ سىلەرنى (ئىسلام ئارقىلىق) ئۇنىڭدىن قۇتقۇزدى. سىلەرنىڭ ھىدايەت تېپىشىڭلار ئۈچۈن، ئاللاھ ئايەتلىرىنى سىلەرگە شۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(3,104,'3:104','سىلەرنىڭ ئاراڭلاردا خەيرلىك ئىشلارغا دەۋەت قىلىدىغان، ياخشى ئىشلارغا بۇيرۇپ، يامان ئىشلارنى مەنئى قىلىدىغان بىر جامائە بولسۇن؛ ئەنە شۇلار مەقسىتىگە ئېرىشكۈچىلەردۇر. ');
INSERT INTO "saleh" VALUES(3,105,'3:105','روشەن دەلىللەر كەلگەندىن كېيىن ئايرىلىپ كېتىشكەن ۋە ئىختىلاپ قىلىشقان كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دەك بولماڭلار، ئەنە شۇلار چوڭ ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(3,106,'3:106','شۇ كۈندە (يەنى قىيامەت كۈنىدە) بەزى يۈزلەر ئاقىرىدۇ، بەزى يۈزلەر قارىيىدۇ. يۈزى قارايغانلارغا (پەرىشتىلەر ئېيتىدۇكى): «ئىمان ئېيتقىنىڭلاردىن كېيىن كاپىر بولدۇڭلارمۇ؟ كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار».');
INSERT INTO "saleh" VALUES(3,107,'3:107','يۈزى ئاقارغانلار ئاللاھنىڭ رەھمىتىدە (يەنى جەننەتتە) بولىدۇ، ئۇلار ئۇنىڭدا (يەنى جەننەتتە) مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(3,108,'3:108',' بۇ، ئاللاھنىڭ ئايەتلىرىدۇر، ئۇلارنى (ئى مۇھەممەد!) ساڭا ھەقىقىتى بىلەن ئوقۇپ بېرىمىز، ئاللاھ جاھان ئەھلىگە زۇلۇم قىلىشنى خالىمايدۇ.');
INSERT INTO "saleh" VALUES(3,109,'3:109','ئاسمانلار ۋە زېمىندىكى شەيئىلەر ئاللاھنىڭ (مۈلكى، مەخلۇقاتى ۋە بەندىلىرى) دۇر، ھەممە ئىشلار ئاللاھقا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(3,110,'3:110','(ئى مۇھەممەد ئۈممىتى!) سىلەر ئىنسانلارنىڭ مەنپەئىتى ئۈچۈن ئوتتۇرىغا چىقىرىلغان، ياخشىلىققا بۇيرۇپ يامانلىقتىن توسىدىغان، ئاللاھقا ئىمان ئېيتىدىغان ئەڭ ياخشى ئۈممەتسىلەر. ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار، ناسارالار) ئىمان ئېيتسا (يەنى مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان ۋەھىيگە ئىشەنسە)، ئۇلار ئۈچۈن (دۇنيا ۋە ئاخىرەتتە) ئەلۋەتتە ياخشى بولاتتى. ئۇلارنىڭ ئىچىدە مۆمىنلەرمۇ بار، ئۇلارنىڭ تولىسى پاسىقلاردۇر.');
INSERT INTO "saleh" VALUES(3,111,'3:111','ئۇلار سىلەرگە ئازار بېرىشتىن باشقا ھېچقانداق زىيان ـ زەخمەت يەتكۈزەلمەيدۇ؛ ئەگەر سىلەر بىلەن ئۇرۇشسا، سىلەرگە ئارقىسىنى قىلىپ قاچىدۇ، ئاندىن ئۇلارغا ياردەم بېرىلمەيدۇ.');
INSERT INTO "saleh" VALUES(3,112,'3:112','ئۇلارنىڭ قەيەردە بولمىسۇن ئاللاھنىڭ ۋە ئىنسانلارنىڭ ھىمايىسىدە بولغانلىرىدىن باشقىلىرى خارلىققا پېتىپ قالدى، ئاللاھنىڭ غەزىپىگە ئۇچرىدى. موھتاجلىققا چۆمدى. بۇ ئۇلارنىڭ ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ئۈچۈندۇر، ئۇلارنىڭ ئاللاھنىڭ پەرمانىدىن چىقىپ كەتكەنلىكلىرى ۋە ئاللاھنىڭ چەكلىمىسىدىن ئېشىپ كەتكەنلىكلىرى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(3,113,'3:113',' ئۇلار بىرخىل ئەمەس، ئەھلى كىتابتىن (يەنى يەھۇدىيلار، ناسارالاردىن) كېچىلىرى ئاللاھنىڭ ئايەتلىرىنى تىلاۋەت قىلىپ تۇرىدىغان ۋە سەجدە قىلىپ تۇرىدىغان بىر جامائە بار.');
INSERT INTO "saleh" VALUES(3,114,'3:114','ئۇلار ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشىنىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، خەيرلىك ئىشلارنى قىلىشقا ئالدىرايدۇ؛ ئەنە شۇلار ياخشىلاردىندۇر.');
INSERT INTO "saleh" VALUES(3,115,'3:115','ئۇلار ھەر قانداق ياخشىلىقنى قىلسۇن، ئۇ بىكار كەتمەيدۇ. ئاللاھ تەقۋادارلارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(3,116,'3:116','شۈبھىسىزكى، كاپىرلارنىڭ ماللىرى، بالىلىرى ئاللاھنىڭ ئازابى ئالدىدا ھەرگىز ھېچ نەرسىگە دال بولالمايدۇ؛ ئۇلار دوزاخ ئەھلىدۇر، دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(3,117,'3:117','ئۇلارنىڭ بۇ دۇنيادا (رىيا قىلىپ) بەرگەن سەدىقىلىرى، ئۆزلىرىگە زۇلۇم قىلغان بىر قەۋمنىڭ زىرائىتىگە تېگىپ ئۇنى نابۇت قىلىۋەتكەن سوغۇق شامالغا ئوخشايدۇ (ئىگىلىرىنىڭ گۇناھى تۈپەيلىدىن نابۇت قىلىنغان بۇ زىرائەتكە ئوخشاش، ئاللاھ ئۇلارنىڭ ياخشى ئەمەللىرىنى بەربات قىلىدۇ). ئۇلارغا ئاللاھ زۇلۇم قىلمىدى ۋەلېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(3,118,'3:118','ئى مۆمىنلەر! ئۆزۈڭلاردىن بولمىغانلارنى سىرداش قىلىۋالماڭلار، ئۇلار سىلەرگە بۇزغۇنچىلىق قىلىشتا بوشاڭلىق قىلىپ قالمايدۇ. ئۇلار سىلەرنىڭ مۇشەققەتتە قېلىشىڭلارنى ئارزۇ قىلىدۇ، ئۇلارنىڭ دۈشمەنلىكى ئېغىزلىرىدىن مەلۇم بولۇپ تۇرىدۇ، ئۇلارنىڭ دىللىرىدا يوشۇرغان دۈشمەنلىكى تېخىمۇ چوڭدۇر. شۈبھىسىزكى، ئەگەر چۈشىنىدىغان بولساڭلار، سىلەرگە نۇرغۇن ئايەتلەرنى بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(3,119,'3:119','(ئى مۆمىنلەر جامائەسى!) سىلەر ئۇلارنى دوست تۇتىسىلەر، ئۇلار سىلەرنى دوست تۇتمايدۇ (سىلەرگە بولغان دۈشمەنلىكنى يوشۇرۇن تۇتىدۇ)، سىلەر(ھەممە ساماۋى) كىتابقا ئىشىنىسىلەر (شۇنداق تۇرۇقلۇق ئۇلار سىلەرنى ئۆچ كۆرىدۇ)، ئۇلار سىلەر بىلەن ئۇچراشقاندا، ئىمان ئېيتتۇق، دەيدۇ. ئۆزلىرى يالغۇز قالغاندا سىلەرگە بولغان ئاچچىقىدىن بارماقلىرىنى چىشلەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، «ئاچچىقىڭلار بىلەن ئۆلۈڭلار! (يەنى ئاللاھ ئۆلگىنىڭلارغا قەدەر ئاچچىقىڭلارنى داۋاملاشتۇرسۇن!)» ئاللاھ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(3,120,'3:120','ئەگەر سىلەرگە (كەڭچىلىك، مولچىلىق، نۇسرەت، غەنىيمەت قاتارلىق) بىرەر ياخشىلىق يەتسە، ئۇلار بۇنىڭدىن قايغۇرىدۇ؛ ئەگەر سىلەرگە (ئېغىرچىلىق، قەھەتچىلىك ۋە مەغلۇبىيەت قاتارلىق) بىرەر يامانلىق يەتسە، ئۇلار بۇنىڭدىن خۇشال بولىدۇ. ئەگەر سىلەر (ئۇلارنىڭ ئەزىيىتىگە) سەۋر قىلساڭلار ۋە (سۆزۈڭلاردا، ھەرىكىتىڭلاردا ئاللاھتىن) قورقساڭلار، ئۇلارنىڭ ھىيلىسى سىلەرگە قىلچە زىيان يەتكۈزەلمەيدۇ. ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى ھەقىقەتەن تولۇق بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(3,121,'3:121','(ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن ئەتىگەندە ئۆيۈڭدىن (ئۇھۇدقا) چىقىپ مۆمىنلەرنى ئۇرۇش سەپلىرىگە ئورۇنلاشتۇردۇڭ، ئاللاھ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,122,'3:122','ئۆز ۋاقتىدا سىلەردىن ئىككى گۇرۇھ (يەنى سەلەمە ئايمىقى بىلەن بەنى ھارىسە ئايمىقى) ئاجىزلىق كۆرسەتمەكچى (يەنى قورقۇپ ئۇرۇش مەيدانىدىن قايتماقچى) بولدى؛ ئاللاھ ئۇلارنىڭ مەدەتكارى ئىدى. (مۆمىنلەر جىمى ئەھۋالى ۋە جىمى ئىشلىرىدا) ئاللاھقىلا تەۋەككۈل قىلسۇن!. ');
INSERT INTO "saleh" VALUES(3,123,'3:123','ئاللاھ سىلەرگە بەدرىدە نۇسرەت ئاتا قىلدى، ھالبۇكى، سىلەر كۈچسىز ئىدىڭلار (سانىڭلار ۋە قورالىڭلار ئاز ئىدى). ئاللاھنىڭ سىلەرگە بەرگەن نۇسرىتىگە شۈكۈر قىلىش يۈزىسىدىن ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(3,124,'3:124','(ئى مۇھەممەد!) ئۆز ۋاقتىدا سەن مۆمىنلەرگە: «پەرۋەردىگارىڭلار سىلەرگە ئۈچ مىڭ پەرىشتە چۈشۈرۈپ ياردەم بەرسە يەنە يەتمەمدۇ؟» دەيتتىڭ.');
INSERT INTO "saleh" VALUES(3,125,'3:125','شۇنداق، ئۇ يېتىدۇ. ئەگەر (جەڭدە) سەۋر قىلساڭلار، (ئاللاھتىن) قورقۇپ (ئەمرىگە ئىتائەت قىلساڭلار)، دۈشمەن دەرھال ھۇجۇم قىلىپ كەلگەندە پەرۋەردىگارىڭلار سىلەرگە بەش مىڭ نىشانلىق پەرىشتە بىلەن ياردەم بېرىدۇ.');
INSERT INTO "saleh" VALUES(3,126,'3:126','ئاللاھ ئۇنى (يەنى پەرىشتىلەر ئارقىلىق ياردەم بېرىشنى) پەقەت سىلەرگە خۇشخەۋەر بولسۇن ۋە دىلىڭلار شۇنىڭ بىلەن خاتىرجەم بولسۇن (شۇنىڭ بىلەن دۈشمەننىڭ سانىنىڭ كۆپلۈكىدىن قورقمىسۇن) دەپ قىلدى. ياردەم پەقەت غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھ تەرىپىدىندۇر.');
INSERT INTO "saleh" VALUES(3,127,'3:127','(ئاللاھنىڭ ياردىمى) كاپىرلارنىڭ بىر تۈركۈمىنى ھالاك قىلىش ئۈچۈن ياكى ئۇلارنى رەسۋا قىلىپ، ئۇلارنىڭ ئۈمىدسىز ھالدا مەغلۇپ بولۇشلىرى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(3,128,'3:128','(ئى مۇھەممەد!) ھېچ ئىش (يەنى بەندىلەرنىڭ ئىشلىرىنىڭ تەدبىرى) سېنىڭ ئىختىيارىڭدا ئەمەس، ئاللاھ يا ئۇلارنىڭ (مۇسۇلمان بولسا) تەۋبىسىنى قوبۇل قىلىدۇ، ياكى ئۇلار زالىم بولغانلىقى ئۈچۈن (كۇفرىدا چىڭ تۇرسا) ئۇلارنى ئازابقا دۇچار قىلىدۇ.');
INSERT INTO "saleh" VALUES(3,129,'3:129','ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھنىڭ مۈلكىدۇر. ئاللاھ خالىغان كىشىگە مەغپىرەت قىلىدۇ، خالىغان كىشىگە ئازاب قىلىدۇ. ئاللاھ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(3,130,'3:130',' ئى مۆمىنلەر! جازانىنى قاتمۇقات ئاشۇرۇپ يېمەڭلار، مەقسىدىڭلارغا ئېرىشىش ئۈچۈن ئاللاھتىن (نەھيى قىلغان ئىشلارنى تەرك ئېتىپ) قورقۇڭلار. ');
INSERT INTO "saleh" VALUES(3,131,'3:131','كاپىرلار ئۈچۈن تەييارلانغان دوزاختىن (يەنى دوزاخقا كىرىشكە سەۋەب بولىدىغان يامان ئىشلاردىن) ساقلىنىڭلار.');
INSERT INTO "saleh" VALUES(3,132,'3:132','سىلەرگە رەھمەت قىلىنىشى ئۈچۈن، ئاللاھقا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(3,133,'3:133','پەرۋەردىگارىڭلارنىڭ مەغپىرىتىگە ۋە تەقۋادارلار ئۈچۈن تەييارلانغان، كەڭلىكى ئاسمان ـ زېمىنچە كېلىدىغان جەننەتكە ئالدىراڭلار.');
INSERT INTO "saleh" VALUES(3,134,'3:134','تەقۋادارلار كەڭچىلىكتىمۇ، قىسىنچىلىقتىمۇ ئاللاھ يولىدا (پۇل ـ مال) سەرپ قىلىدىغانلار، (ئۆچ ئېلىشقا قادىر تۇرۇقلۇق) ئاچچىقىنى يۇتىدىغانلار، (يامانلىق قىلغان ياكى بوزەك قىلغان) كىشىلەرنى كەچۈرىدىغانلاردۇر. ئاللاھ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(3,135,'3:135','تەقۋادارلار يامان بىر گۇناھ قىلىپ قالسا ياكى ئۆزلىرىگە زۇلۇم قىلسا ئاللاھنى ياد ئېتىدۇ، گۇناھلىرى ئۈچۈن مەغپىرەت تەلەپ قىلىدۇ، گۇناھنى كەچۈرىدىغان ئاللاھتىن باشقا كىم بار؟ ئۇلار قىلمىشلىرىنى بىلىپ تۇرۇپ داۋاملاشتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(3,136,'3:136','ئۇلارنىڭ (يەنى يۇقىرىقى خىسلەتلەرگە ئىگە كىشىلەرنىڭ) مۇكاپاتى پەرۋەردىگارى تەرىپىدىن بولغان مەغپىرەت ۋە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردۇر. بۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ. ياخشى ئىش قىلغۇچىلارنىڭ ئېرىشىدىغان ساۋابى نېمىدېگەن ياخشى!.');
INSERT INTO "saleh" VALUES(3,137,'3:137','سىلەردىن ئىلگىرى نۇرغۇن ئۈممەتلەر كېلىپ كەتتى؛ يەر يۈزىدە سەير قىلىڭلار، (ھەقنى) يالغانغا چىقارغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار.');
INSERT INTO "saleh" VALUES(3,138,'3:138','بۇ (قۇرئان) (پۈتكۈل) ئىنسانلارغا باياندۇر (يەنى ھالال بىلەن ھارامنى ئوچۇق بايان قىلىدۇ)، تەقۋادارلارغا توغرا يول كۆرسەتكۈچىدۇر ۋە ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(3,139,'3:139','(جىھادتىن) بوشىشىپ قالماڭلار، شېھىت بولغانلار ئۈچۈن قايغۇرماڭلار، ئەگەر مۆمىن بولساڭلار، ئۈستۈنلۈك قازىنىسىلەر.');
INSERT INTO "saleh" VALUES(3,140,'3:140','ئەگەر سىلەرگە شىكەستىلىك يەتكەن بولسا (يەنى بۇ جەڭدە سىلەر يارىلانغان ۋە شەھىد بولغان بولساڭلار)، دۈشمەنلەرگىمۇ (بەدرى جېڭىدە) ئوخشاشلا شىكەستىلىك يەتتى (يەنى ئۇلار يارىلاندى ۋە ئۆلتۈرۈلدى). بۇ كۈنلەرنى ئىنسانلار ئارىسىدا ئايلاندۇرۇپ تۇرىمىز (يەنى غەلىبىنى ھەمىشە بىر پىرقىدە قىلماي، بىر كۈن بۇ پىرقىدە، بىركۈن ئۇ پىرقىدە قىلىمىز)، (بۇ) ئاللاھنىڭ (ھەقىقىي) مۆمىنلەرنى (مۇناپىقلاردىن) ئايرىشى ئۈچۈندۇر، سىلەرنى شەھىدلەردىن قىلىشى (يەنى سىلەردىن بەزىلەرنى شەھىدلىك دەرىجىسىگە مۇشەررەپ قىلىشى) ئۈچۈندۇر، ـ ئاللاھ زالىملارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(3,141,'3:141','مۆمىنلەرنى (ئۇلارغا يەتكەن مۇسىبەت ئارقىلىق) پاكلىشى ئۈچۈندۇر، كاپىرلارنى يوق قىلىشى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(3,142,'3:142','(ئى مۆمىنلەر جامائەسى!) ئاللاھ سىلەردىن (ھەقىقىي) جىھاد قىلغانلارنى ۋە (جىھادنىڭ جاپا ـ مۇشەققەتلىرىگە) چىدىغۇچىلارنى بىلمەي (يەنى ئايرىماي) تۇرۇپ، (پەقەت ئاغزىڭلاردا ئىمان ئېيتتۇق دېيىش بىلەنلا) جەننەتكە كىرىشنى ئويلامسىلەر؟.');
INSERT INTO "saleh" VALUES(3,143,'3:143','سىلەر ئۆلۈم (يەنى دۈشمەن) بىلەن ئۇچرىشىشتىن ئىلگىرى ئۇنى ئارزۇ قىلاتتىڭلار، مانا ئۇنى ئۆز كۆزۈڭلار بىلەن كۆردۈڭلار .');
INSERT INTO "saleh" VALUES(3,144,'3:144','مۇھەممەد پەقەت بىر پەيغەمبەردۇر، ئۇنىڭدىن بۇرۇن كۆپ پەيغەمبەرلەر ئۆتتى (ئۇلارنىڭ بەزىلىرى ئۆز ئەجىلى بىلەن ئۆلگەن، بەزىلىرى باشقىلار تەرىپىدىن ئۆلتۈرۈلگەن)؛ ئۇ ۋاپات بولسا ياكى ئۆلتۈرۈلسە، ئارقاڭلارغا يېنىۋالامسىلەر؟ (يەنى ئىماندىن يېنىپ مۇرتەد بولامسىلەر؟) كىمكى ئارقىسىغا يېنىۋالىدىكەن (يەنى مۇرتەد بولىدىكەن)، ئۇ ئاللاھقا قىلچە زىيان يەتكۈزەلمەيدۇ. ئاللاھ شۈكۈر قىلغۇچىلارنى مۇكاپاتلايدۇ.');
INSERT INTO "saleh" VALUES(3,145,'3:145','ھېچ كىشى ئاللاھنىڭ ئىزنىسىز ئۆلمەيدۇ، ئاللاھ ھەر ئادەمنىڭ ئەجىلىنى پۈتۈۋەتكەن (يەنى ئادەمنىڭ ئۆمرىنى قورقۇنچاقلىق ئۇزارتالمايدۇ، باتۇرلۇق قىسقارتالمايدۇ). كىمكى دۇنيا ساۋابىنى كۆزلىسە، ئۇنىڭغا دۇنيانىڭ ساۋابىنى بېرىمىز (ئۇنىڭ ئاخىرەتلىكتىن نېسىۋىسى بولمايدۇ)؛ كىمكى ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭغا ئاخىرەتنىڭ ساۋابىنى بېرىمىز، شۈكۈر قىلغۇچىلارنى مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(3,146,'3:146','نۇرغۇن پەيغەمبەرلەر بىلەن كۆپلىگەن خۇداگۇي ئۆلىمالار بىرلىكتە جەڭ قىلدى. ئۇلار ئاللاھ يولىدا يەتكەن كۈلپەتلەردىن روھسىزلانمىدى، بوشاشمىدى، باش ئەگمىدى، ئاللاھ ئۆزىنىڭ يولىدا دۇچ كەلگەن قىيىنچىلىقلارغا چىداشلىق بەرگۈچىلەرنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(3,147,'3:147','ئۇلارنىڭ سۆزى پەقەت: «پەرۋەردىگارىمىز! گۇناھلىرىمىزنى، ئىشىمىزدا چەكتىن ئاشقانلىقىمىزنى مەغپىرەت قىلغىن، قەدەملىرىمىزنى مۇستەھكەم قىلغىن ۋە كاپىر قەۋمگە قارشى بىزگە ياردەم بەرگىن» دېگەندىن باشقا بولمىدى.');
INSERT INTO "saleh" VALUES(3,148,'3:148','ئاللاھ ئۇلارغا دۇنيانىڭ ساۋابىنى (يەنى دۈشمەنلەر ئۈستىدىن غەلىبە قىلىشنى) ۋە ئاخىرەتنىڭ ئەڭ ياخشى مۇكاپاتىنى (يەنى جەننەت ۋە ئۇنىڭ نېمەتلىرىنى) ئاتا قىلدى، ئاللاھ ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(3,149,'3:149','ئى مۆمىنلەر! ئەگەر سىلەر كاپىرلارغا ئىتائەت قىلساڭلار، ئۇلار سىلەرنى ئارقاڭلارغا (يەنى كۇفرىغا) ياندۇرىدۇ، شۇنىڭ بىلەن زىيان تارتىپ قالىسىلەر.');
INSERT INTO "saleh" VALUES(3,150,'3:150','ئۇنداق ئەمەس (يەنى ئۇلار سىلەرنىڭ ئىتائەت قىلىدىغان مەدەتكارىڭلار ئەمەس)، ئاللاھ سىلەرنىڭ مەدەتكارىڭلاردۇر (ئۇنىڭغا ئىتائەت قىلىڭلار)، ئۇ ياردەم قىلغۇچىلارنىڭ ئەڭ ياخشىسىدۇر.');
INSERT INTO "saleh" VALUES(3,151,'3:151','ئاللاھ (ئىلاھلىقى) ھەققىدە ھېچبىر دەلىل چۈشۈرمىگەن نەرسىلەرنى (يەنى بۇتلارنى) ئاللاھقا شېرىك قىلغانلىقلىرى ئۈچۈن، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمىز، ئۇلارنىڭ بارىدىغان جايى دوزاختۇر، زالىملارنىڭ جايى (يەنى دوزاخ) ھەقىقەتەن يامان جايدۇر.');
INSERT INTO "saleh" VALUES(3,152,'3:152','ئاللاھ سىلەرگە قىلغان (دۈشمىنىڭلارغا قارشى نۇسرەت ئاتا قىلىشتىن ئىبارەت) ۋەدىسىدە تۇردى؛ ئاللاھنىڭ ئىزنى بىلەن ئۇلارنى قىرىۋاتاتتىڭلار، ئاللاھ سىلەر ياقتۇرىدىغان غەلىبىنى كۆرسەتكەندىن كېيىن زەئىپلىشىپ قالدىڭلار، پەيغەمبەرنىڭ ئەمرى توغرىسىدا جاڭجاللاشتىڭلار، (پەيغەمبەرنىڭ ئەمرىگە) بويسۇنمىدىڭلار، بەزىلىرىڭلار دۇنيانى كۆزلىدىڭلار، بەزىلىرىڭلار ئاخىرەتنى كۆزلىدىڭلار، ئاندىن ئاللاھ سىناش ئۈچۈن سىلەرنى دۈشمەندىن قايتۇردى (يەنى سىلەرنى غەلىبىدىن مەغلۇبىيەتكە يۈزلەندۈردى) ۋە سىلەرنى ھەقىقەتەن ئەپۇ قىلدى. ئاللاھ مۆمىنلەرگە مەرھەمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,153,'3:153','ئۆز ۋاقتىدا پەيغەمبەر (ئى ئاللاھنىڭ بەندىلىرى! مېنىڭ تەرىپىمگە كېلىڭلار، مەن رەسۇلۇللاھتۇرمەن، كىمكى جىھاد مەيدانىغا قايتسا، ئۇ جەننەتكە كىرىدۇ دەپ) ئارقاڭلاردىن سىلەرنى چاقىرىپ تۇراتتى، سىلەر ھېچ كىشىگە قارىماي قاچاتتىڭلار، (بۇنىڭدىن كېيىن) قولۇڭلاردىن كەتكەن نەرسىگە (يەنى غەنىيمەتكە)، بېشىڭلارغا كەلگەن مۇسىبەتكە (يەنى مەغلۇبىيەتكە) قايغۇرماسلىقىڭلار ئۈچۈن، ئاللاھ سىلەرگە غەم ئۈستىگە غەم بەردى (يەنى سىلەرنى غەنىيمەتتىن مەھرۇم قىلغاننىڭ ئۈستىگە مەغلۇبىيەت بىلەن جازالىدى). ئاللاھ قىلمىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(3,154,'3:154','غەمدىن كېيىن ئاللاھ سىلەرگە ئەمىنلىك چۈشۈرۈپ بەردى، سىلەرنىڭ بىر قىسمىڭلارنى (يەنى ھەقىقىي مۆمىنلەرنى) مۈگدەك باستى، يەنە بىر قىسمىڭلار (يەنى مۇناپىقلار) ئۆزلىرى بىلەن بولۇپ كەتتى (يەنى كۇففارلار قايتا ھۇجۇم قىلارمۇ، دەپ جان قايغۇسى بىلەن بولۇپ كەتتى). بۇلار، جاھىلىيەت دەۋرى كىشىلىرىگە ئوخشاش، ئاللاھقا قارىتا ناتوغرا گۇمانلاردا بولدى، ئۇلار: «بىزگە ئاللاھ ۋەدە قىلغان غەلىبىدىن نېسىۋە بارمۇ؟» دەيدۇ. ئېيتقىنكى، «ھەممە ئىش ھەقىقەتەن ئاللاھنىڭ ئىلكىدە». ئۇلار ساڭا ئاشكارا قىلمايدىغان نەرسىلەرنى ئۆز ئىچىدە يوشۇرۇشىدۇ؛ ئۇلار: «ئەگەر بىزگە غەلىبىدىن ئازراق نېسىۋە بولسا ئىدى، بۇ يەردە ئۆلتۈرۈلمەيتتۇق» دەيدۇ. (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر ئۆيلىرىڭلاردا بولغان تەقدىردىمۇ (ئاراڭلاردىكى) ئۆلتۈرۈلۈشى پۈتۈۋېتىلگەن كىشىلەر چوقۇم ئۆزلىرىنىڭ ئۆلىدىغان جايلىرىغا بېرىشىدۇ (يەنى ئاللاھنىڭ قازاسىدىن قېچىپ قۇتۇلغىلى بولمايدۇ). (ئاللاھنىڭ مۇنداق قىلىشى) كۆڭلۈڭلاردىكىنى سىناش ۋە قەلبىڭلاردىكى نەرسىلەرنى پاكلاش ئۈچۈندۇر. ئاللاھ كۆڭۈللەردىكىنى (ياخشىلىق بولسۇن، يامانلىق بولسۇن) بىلگۈچىدۇر».');
INSERT INTO "saleh" VALUES(3,155,'3:155','ئىككى قوشۇن (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار) بىر ـ بىرى بىلەن ئۇچراشقان كۈندە، ئىچىڭلاردىكى (جەڭدىن) ھەقىقەتەن يۈز ئۆرۈگەن كىشىلەرنى، بەزى قىلمىشلىرى (يەنى پەيغەمبەرنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلغانلىقى) تۈپەيلىدىن، شەيتان تېيىلدۇرۇشقا ئۇرۇندى؛ ئاللاھ ھەقىقەتەن ئۇلارنى ئەپۇ قىلدى. ئاللاھ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(3,156,'3:156','ئى مۆمىنلەر! سەپەرگە چىققان ياكى غازاتقا چىققان قېرىنداشلىرى ھەققىدە، ئۇلار يېنىمىزدا بولغاندا ئۆلمەيتتى ۋە ئۆلتۈرۈلمەيتتى، دېگەن كاپىرلارغا ئوخشاش بولماڭلار، ئاللاھ بۇنى ئۇلارنىڭ دىللىرىدا ھەسرەت قىلسۇن. ئاللاھ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ (ئۇلارنىڭ غازاتقا چىقماسلىقى ئۇلارنى ئۆلۈمدىن قۇتۇلدۇرالمايدۇ). ئاللاھ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,157,'3:157','شۈبھىسىزكى، ئەگەر سىلەر ئاللاھ يولىدا (يەنى غازاتتا) ئۆلتۈرۈلسەڭلار ياكى (غازاتقا كېتىپ بېرىپ ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار، سىلەرگە ئاللاھ تەرىپىدىن بولغان مەغپىرەت ۋە رەھمەت، ئەلۋەتتە، ئۇلارنىڭ توپلايدىغان دۇنياسىدىن ئارتۇقتۇر.');
INSERT INTO "saleh" VALUES(3,158,'3:158','ئەگەر سىلەر (ئۆز ئەجىلىڭلار بىلەن) ئۆلسەڭلار ياكى (جەڭ مەيدانىدا) ئۆلتۈرۈلسەڭلار، (ئاخىرەتتە) چوقۇم ئاللاھنىڭ دەرگاھىغا توپلىنىسىلەر (ئاللاھ سىلەرنى ئەمەلىڭلارغا قاراپ مۇكاپاتلايدۇ).');
INSERT INTO "saleh" VALUES(3,159,'3:159','ئاللاھنىڭ رەھمىتى بىلەن سەن ئۇلارغا مۇلايىم بولدۇڭ؛ ئەگەر قوپال، باغرى قاتتىق بولغان بولساڭ، ئۇلار چۆرەڭدىن تارقاپ كېتەتتى؛ ئۇلارنى ئەپۇ قىلغىن، ئۇلار ئۈچۈن مەغپىرەت تىلىگىن، ئىشتا ئۇلار بىلەن كېڭەشكىن؛ (كېڭەشكەندىن كېيىن) بىر ئىشقا بەل باغلىساڭ، ئاللاھقا تەۋەككۈل قىلغىن. ئاللاھ ھەقىقەتەن تەۋەككۈل قىلغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(3,160,'3:160','ئەگەر ئاللاھ سىلەرگە ياردەم بەرسە، سىلەرنى ھېچ كىشى يېڭەلمەيدۇ. ئەگەر ئاللاھ سىلەرگە ياردىمىنى تەرك ئەتسە، ئاللاھتىن باشقا سىلەرگە كىم ياردەم بېرىدۇ؟ مۆمىنلەر يالغۇز ئاللاھقا تەۋەككۈل قىلسۇن! (يەنى ھەممە ئىشنى ئاللاھقا تاپشۇرسۇن).');
INSERT INTO "saleh" VALUES(3,161,'3:161','ھېچقانداق بىر پەيغەمبەر غەنىيمەتكە خىيانەت قىلمايدۇ (يەنى بۇنى تەسەۋۋۇر قىلغىلى بولمايدۇ). كىمكى غەنىيمەتكە خىيانەت قىلىدىكەن، قىيامەت كۈنى ئۇ خىيانەت قىلغان نەرسىسى بىلەن (يەنى خىيانەت قىلغان نەرسىسى گەجگىسىگە ئارتىلغان ھالدا) كېلىدۇ. ئاندىن ھەر كىشىنىڭ قىلغان ئەمەلىگە تولۇق مۇكاپات، (ياكى) تولۇق جازا بېرىلىدۇ، ئۇلارغا (ياخشىلارنىڭ ساۋابىنى كېمەيتىۋېتىپ، ئاسىيلارنىڭ جازاسىنى ئاشۇرۇۋېتىپ) زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(3,162,'3:162','ئاللاھنىڭ رازىلىقىنى ئىزدىگەن كىشى ئاللاھنىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىگە ئوخشامدۇ؟ ئاللاھنىڭ غەزىپىگە ئۇچراشقا تېگىشلىك بولغان كىشىنىڭ بارىدىغان جايى جەھەننەمدۇر، جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(3,163,'3:163','ئاللاھنىڭ دەرگاھىدا ئۇلارنىڭ دەرىجىسى تۈرلۈك بولىدۇ. ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,164,'3:164','ئاللاھ مۆمىنلەرگە ئاللاھنىڭ ئايەتلىرىنى تىلاۋەت قىلىدىغان، ئۇلارنى (گۇناھلاردىن) پاك قىلىدىغان، ئۇلارغا كىتابنى ۋە ھېكمەتنى (يەنى قۇرئان ۋە سۈننەتنى) ئۆگىتىدىغان، ئۆزلىرىدىن بولغان بىر پەيغەمبەر ئەۋەتىپ، ئۇلارغا چوڭ ئېھسان قىلدى؛ ھالبۇكى، ئۇلار بۇرۇن ئوچۇق گۇمراھلىقتا ئىدى.');
INSERT INTO "saleh" VALUES(3,165,'3:165','سىلەرگە يەتكەن مۇسىبەت سىلەر دۈشمەنگە يەتكۈزگەن مۇسىبەتنىڭ يېرىمىغا تەڭ كېلىدىغان تۇرسا (يەنى ئۇھۇد غازىتىدا سىلەردىن 70 كىشى شېھىت بولغان تۇرسا، بەدرى غازىتىدا بولسا مۇشرىكلاردىن 70 كىشىنى ئۆلتۈرگەن، 70 كىشىنى ئەسىر ئالغان تۇرساڭلار)، سىلەر يەنە: «بۇ مۇسىبەت قەيەردىن كەلدى؟» دېدىڭلار. ئېيتقىنكى، «ئۇ سىلەرنىڭ ئۆزلىرىڭلاردىن (يەنى پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغانلىقىڭلار ۋە غەنىيمەتكە ھېرىس بولغانلىقىڭلاردىن) بولدى». ئاللاھ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(3,166,'3:166','ئىككى قوشۇن ئۇچراشقان كۈندە (يەنى مۇسۇلمانلار بىلەن مۇشرىكلار توقۇنۇشقان ئۇھۇد جېڭىدە) سىلەرگە كەلگەن مۇسىبەت ئاللاھنىڭ ئىرادىسى بىلەن كەلگەندۇر، بۇ ھەقىقىي مۆمىنلەرنى بىلىش (يەنى ئايرىش) ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(3,167,'3:167','ۋە مۇناپىقلارنى بىلىش (يەنى ئايرىش) ئۈچۈندۇركى، ئۇلارغا: «كېلىڭلار، ئاللاھ يولىدا ئۇرۇش قىلىڭلار ياكى (ئۆزۈڭلارنى) قوغداڭلار» دېيىلسە، ئۇلار: «ئۇرۇشتىن خەۋەر تاپقان بولساق، ئەلۋەتتە سىلەرگە ئەگىشەتتۇق (يەنى سىلەر بىلەن بىر سەپتە تۇرۇپ ئۇرۇش قىلاتتۇق)» دېدى. ئۇ كۈندە ئۇلار ئىمانغا قارىغاندا كۇفرىغا يېقىن ئىدى. ئۇلار كۆڭۈللىرىدە يوق نەرسىلەرنى ئېغىزلىرىدا دېدى، ئۇلار يوشۇرغاننى (يەنى مۇناپىقلىق بىلەن مۇشرىكلىكنى) ئاللاھ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(3,168,'3:168','(بۇ يەنە ئاللاھنىڭ) ئۇرۇشقا چىقىشنى خالىمىغان (مۇناپىقلارنى ئايرىشى ئۈچۈندۇركى، ئۇلار ئۆيلىرىدە) ئولتۇرۇپ قېرىنداشلىرىغا: «ئەگەر ئۇلار (يەنى مۆمىنلەر) بىزگە بويسۇنغان بولسا (يەنى نەسىھىتىمىزنى ئاڭلاپ ئۇرۇشتىن قايتقان بولسا)، ئۆلتۈرۈلمەيتتى» دېدى. (ئى مۇھەممەد! بۇ مۇناپىقلارغا) ئېيتقىنكى، «(ئەگەر ئۇرۇشقا چىقماسلىق ئۆلۈمدىن قۇتۇلدۇرىدىغان بولسا) ئۆلۈمنى ئۆزۈڭلاردىن دەپئى قىلىپ بېقىڭلار، ئەگەر (دەۋايىڭلاردا) راستچىل بولساڭلار». ');
INSERT INTO "saleh" VALUES(3,169,'3:169','ئاللاھنىڭ يولىدا شېھىت بولغانلارنى ئۆلۈك دەپ گۇمان قىلمىغىن، بەلكى ئۇلار تىرىك بولۇپ، ئاللاھنىڭ دەرگاھىدىكى رىزىقتىن بەھرىمەن قىلىنىدۇ (يەنى جەننەتنىڭ نېمەتلىرىدىن ئەتىگەن ـ ئاخشامدا مەڭگۈلۈك رىزىقلاندۇرۇلۇپ تۇرىدۇ).');
INSERT INTO "saleh" VALUES(3,170,'3:170','ئۇلار ئاللاھنىڭ ئۆزلىرىگە بەرگەن پەزلىدىن خۇرسەندۇر، ئۆزلىرىنىڭ ئارقىسىدىن تېخى يېتىپ كەلمىگەن (يەنى شېھىت بولماي تىرىك قالغان) قېرىنداشلىرىغا (ئاخىرەتتە) نە قورقۇنچ، (دۇنيادىن ئايرىلغانلىقىغا) نە قايغۇ يوق ئىكەنلىكى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ. ');
INSERT INTO "saleh" VALUES(3,171,'3:171','ئۇلار ئاللاھ تەرىپىدىن بېرىلگەن نېمەت ۋە پەزلنى، مۆمىنلەرنىڭ ئەجرىنى ئاللاھنىڭ بىكار قىلىۋەتمەيدىغانلىقى بىلەن خۇش خەۋەر بېرىشنى تىلەيدۇ.');
INSERT INTO "saleh" VALUES(3,172,'3:172','(ئۇھۇد غازىتىدا) يارىلانغاندىن كېيىن ئاللاھنىڭ ۋە پەيغەمبەرنىڭ چاقىرىقىغا ئاۋاز قوشقانلار، ئۇلار (يەنى مۆمىنلەر) دىن ياخشى ئەمەللەرنى قىلغانلار ۋە تەقۋادارلىق قىلغانلار چوڭ ساۋابقا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(3,173,'3:173','(مۇشرىكلار تەرەپدارى بولغان) ئادەملەر ئۇلارغا: «شەك ـ شۈبھىسىزكى، كىشىلەر (يەنى قۇرەيشلەر) سىلەرگە قارشى قوشۇن توپلىدى، ئۇلاردىن قورقۇڭلار» دېدى. بۇ سۆز ئۇلارنىڭ ئىمانىنى كۈچەيتتى. ئۇلار: «بىزگە ئاللاھ كۇپايە، ئاللاھ نېمىدېگەن ياخشى ھامىي!» دېدى.');
INSERT INTO "saleh" VALUES(3,174,'3:174','ئۇلار ئاللاھنىڭ نېمىتى ۋە پەزلى بىلەن قايتىپ كەلدى، ھېچقانداق زىيان ـ زەخمەتكە ئۇچرىمىدى، ئۇلار ئاللاھنىڭ رازىلىقىنى ئىزدىدى. ئاللاھ ئۇلۇغ پەزل ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(3,175,'3:175','ئەنە شۇ شەيتان ئۆز دوستلىرىنى (يەنى كۇففارلارنى) قورقىتىدۇ. ئەگەر مۆمىن بولساڭلار، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(3,176,'3:176','(ئى مۇھەممەد!) كۇفرىغا ئۆزلىرىنى ئاتىغانلار (يەنى سۆزلىرى ۋە ھەرىكەتلىرى بىلەن كۇفرىغا شاپاشلايدىغان مۇناپىقلار) سېنى غەمكىن قىلمىسۇن، ئۇلار ھەقىقەتەن (كۇفرى بىلەن) ئاللاھقا قىلچە زىيان يەتكۈزەلمەيدۇ. ئاللاھ ئاخىرەتتە ئۇلارغا (ساۋابتىن) ھېچبىر نېسىۋە بەرمەسلىكنى خالايدۇ؛ ئۇلار چوڭ ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(3,177,'3:177','ئىماننى كۇفرىغا تېگىشكەنلەر (يەنى مەزكۇر مۇناپىقلار) (كۇفرى بىلەن) ئاللاھقا قىلچە زىيان يەتكۈزەلمەيدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(3,178,'3:178','كاپىرلار ئۆزلىرىگە (جازا بەرمەستىن) مۆھلەت بېرىشىمىزنى (يەنى ئۆمرىنى ئۇزۇن قىلىشىمىزنى) ھەرگىز ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئۇلارغا مۆھلەت بېرىشىمىز (ۋە ئەجىلىنى كېچىكتۈرۈشىمىز) پەقەت ئۇلارنىڭ گۇناھىنىڭ كۆپىيىشى ئۈچۈندۇر. ئۇلار (ئاخىرەتتە) خار قىلغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(3,179,'3:179',' ئاللاھ ياماننى (يەنى مۇناپىقنى) ياخشىدىن (يەنى مۆمىندىن) ئايرىمىغىچە مۆمىنلەرنى (يەنى سىلەرنى) ھازىرقى پېتىڭلارچە قويمايدۇ. ئاللاھ سىلەرنى غەيبتىنمۇ خەۋەردار قىلمايدۇ ۋەلېكىن ئاللاھ پەيغەمبەرلىرىدىن خالىغىنىنى تاللاپ، ئۇنىڭغا (مۇھەممەد ئەلەيھىسسالامغا مۇناپىقلارنىڭ ھالىنى بىلدۈرگەندەك) غەيبنى بىلدۈرىدۇ؛ ئاللاھقا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە (پەرۋەردىگارىڭلاردىن) قورقساڭلار، چوڭ ساۋابقا ئېرىشىسىلەر.');
INSERT INTO "saleh" VALUES(3,180,'3:180','ئاللاھ ئۆز پەزلىدىن بەرگەن نەرسىلەرگە (يەنى پۇل ـ مالغا) بېخىللىق قىلىدىغانلار بېخىللىقنى ئۆزلىرى ئۈچۈن پايدىلىق دەپ گۇمان قىلمىسۇن، ئەمەلدە بۇ ئۇلار ئۈچۈن زىيانلىقتۇر؛ ئۇلارنىڭ بېخىللىق قىلغان نەرسىسى قىيامەت كۈنى ئۇلارنىڭ بوينىغا تاقاق قىلىپ سېلىنىدۇ. ئاسمانلارنىڭ ۋە يەرنىڭ مىراسى ئاللاھنىڭدۇر (يەنى كائىناتتىكى ھەممە نەرسە ئاللاھنىڭ مۈلكى بولۇپ، ئۇلار پانىي بولغاندىن كېيىن ئاللاھنىڭ دەرگاھىغا قايتىدۇ). ئاللاھ قىلغان ئەمەللىرىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(3,181,'3:181','«ئاللاھ ھەقىقەتەن پېقىر، بىز بولساق باي» دېگەن كىشىلەر (يەنى يەھۇدىيلار) نىڭ سۆزىنى ئاللاھ ھەقىقەتەن ئاڭلىدى، ئۇلارنىڭ سۆزلىرىنى ۋە پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرىنى خاتىرىلەپ قويىمىز (يەنى نامە ـ ئەمالىغا مۇئەككەل پەرىشتىلەرگە بۇنى ئۇلارنىڭ نامە ـ ئەمالىغا يېزىشنى بۇيرۇيمىز) ھەمدە ئاخىرەتتە پەرىشتىلەر تىلى ئارقىلىق، كۆيدۈرگۈچى ئازابنى تېتىڭلار، دەيمىز.');
INSERT INTO "saleh" VALUES(3,182,'3:182','بۇ سىلەرنىڭ قىلمىشىڭلار تۈپەيلىدىندۇر، ئاللاھ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر.');
INSERT INTO "saleh" VALUES(3,183,'3:183','ئۇلار: «(تەۋراتتا) ئاللاھ بىزنى (ئاسماندىن چۈشكەن) ئاتەش يەپ كېتىدىغان بىر قۇربانلىق كەلتۈرمىگۈچە ھەقىقەتەن ھېچبىر پەيغەمبەرگە ئىمان كەلتۈرمەسلىككە بۇيرۇدى» دېدى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە مۆجىزىلەر كەلدى ۋە سىلەر ئېيتقان مۆجىزە بىلەن ھەقىقەتەن نۇرغۇن پەيغەمبەر كەلدى؛ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارنى نېمىشقا ئۆلتۈردۈڭلار؟».');
INSERT INTO "saleh" VALUES(3,184,'3:184','(ئى مۇھەممەد!) ئۇلار سېنى يالغانغا چىقارغان بولسا (قايغۇرۇپ كەتمىگىن)، سەندىن ئىلگىرى ئۆتكەن، مۆجىزىلەرنى، كىتابلارنى (يەنى ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش ساماۋى كىتابلارنى) ۋە نۇرلۇق كىتابنى (يەنى تەۋرات، ئىنجىلغا ئوخشاشلارنى) ئېلىپ كەلگەن نۇرغۇن پەيغەمبەرلەرمۇ يالغانغا چىقىرىلغان ئىدى.');
INSERT INTO "saleh" VALUES(3,185,'3:185','ھەر بىرجان ئىگىسى ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر. سىلەرنىڭ ئەجرىڭلار (يەنى ئەمەللىرىڭلارنىڭ ساۋابى) قىيامەت كۈنى جەزمەن تولۇق بېرىلىدۇ. كىمكى دوزاختىن يىراق قىلىنغان ۋە جەننەتكە كىرگۈزۈلگەن ئىكەن، ئۇ مۇرادىغا يەتكەن بولىدۇ. دۇنيا تىرىكچىلىكى پەقەت ئالدايدىغان نەرسىدىنلا ئىبارەتتۇر (يەنى ئالدايدىغان نەرسە بولۇپ، ئازغىنا بەھرىمەن بولغاندىن كېيىن تۈگەيدۇ).');
INSERT INTO "saleh" VALUES(3,186,'3:186','سىلەر ماللىرىڭلاردا (سەدىقىغە بۇيرۇلۇش ۋە ئاپەت يېتىشى بىلەن) ۋە جانلىرىڭلاردا (قەتل قىلىنىش، ئەسىر ئېلىنىش، كېسەل بولۇش بىلەن) چوقۇم سىنىلىسىلەر. سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن ۋە مۇشرىكلاردىن چوقۇم نۇرغۇن يامان سۆز ئاڭلايسىلەر، ئەگەر (يۇقىرىقى ئەھۋاللار يۈز بەرگەندە) سەۋر قىلساڭلار (سۆزۈڭلار ۋە ھەرىكىتىڭلاردا ئاللاھتىن) قورقساڭلار، ئۇنداقتا بۇ ھەقىقەتەن ئىرادە بىلەن قىلىنىشقا تېگىشلىك ئىشلاردىندۇر.');
INSERT INTO "saleh" VALUES(3,187,'3:187','ئۆز ۋاقتىدا، ئاللاھ كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار) دىن كىتابنى (يەنى كىتابتىكى ئاللاھنىڭ ئەھكاملىرىنى) كىشىلەرگە چوقۇم بايان قىلىپ بېرىشكە ۋە يوشۇرماسلىققا ئەھدە ئالدى، لېكىن بۇنى (يەنى ئەھدىنى) ئۇلار ئارقىسىغا چۆرۈۋەتتى ۋە ئۇنى ئازغىنا بەدەلگە (يەنى دۇنيانىڭ ئەرزىمەس نەرسىسىگە) تېگىشتى. ئۇلارنىڭ تېگىشىپ ئالغىنى نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(3,188,'3:188','(ئى مۇھەممەد!) قىلغان ئىشلىرىدىن (يەنى ھەقنى ياپقانلىقىدىن) خۇشال بولىدىغان، قىلمىغان ئىشلىرى بىلەن مەدھىيەلىنىشنى ياخشى كۆرىدىغان كىشىلەرنى ھەرگىز ئاللاھنىڭ ئازابىدىن قۇتۇلىدۇ دەپ گۇمان قىلمىغىن، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(3,189,'3:189','ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھ ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(3,190,'3:190','شۈبھىسىزكى، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىدا، كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا ئەقىل ئىگىلىرى ئۈچۈن، ئەلۋەتتە روشەن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(3,191,'3:191','ئۇلار ئۆرە تۇرغاندىمۇ، ئولتۇرغاندىمۇ، ياتقاندىمۇ ئاللاھنى ئەسلەپ تۇرىدۇ، ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشى توغرىسىدا پىكىر يۈرگۈزىدۇ. (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! بۇنى بىكار ياراتمىدىڭ، سەن پاكتۇرسەن، بىزنى دوزاخ ئازابىدىن ساقلىغىن.');
INSERT INTO "saleh" VALUES(3,192,'3:192','پەرۋەردىگارىمىز! سەن كىمنىكى دوزاخقا كىرگۈزىدىكەنسەن، ئۇنى ئەلۋەتتە خار قىلغان بولىسەن. زالىملارغا ھېچقانداق ياردەمچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(3,193,'3:193','پەرۋەردىگارىمىز! بىز ھەقىقەتەن بىر چاقىرغۇچىنىڭ (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ) ”رەببىڭلارغا ئىمان ئېيتىڭلار“ دەپ ئىمانغا چاقىرغانلىقىنى ئاڭلاپ ئىمان ئېيتتۇق. پەرۋەردىگارىمىز! بىزنىڭ گۇناھلىرىمىزنى مەغپىرەت قىلغىن، يامانلىقلىرىمىزنى يوققا چىقارغىن، بىزنى ياخشىلارنىڭ قاتارىدا قەبزى روھ قىلغىن.');
INSERT INTO "saleh" VALUES(3,194,'3:194','پەرۋەردىگارىمىز! بىزگە پەيغەمبەرلىرىڭ ئارقىلىق ۋەدە قىلغان نەرسىنى (يەنى ئىتائەت قىلغانلارغا خاس بولغان جەننەتنى) بەرگىن، قىيامەت كۈنى بىزنى رەسۋا قىلمىغىن. سەن ھەقىقەتەن ۋەدەڭگە خىلاپلىق قىلمايسەن».');
INSERT INTO "saleh" VALUES(3,195,'3:195','ئۇلارنىڭ دۇئاسىنى پەرۋەردىگارى ئىجابەت قىلىپ: «مەن سىلەردىن ئەر بولسۇن، ئايال بولسۇن، ھەر قانداق بىر ياخشى ئىش قىلغۇچىنىڭ قىلغان ئەمەلىنى بىكار قىلىۋەتمەيمەن، سىلەر بىر ـ بىرىڭلاردىن (تۆرەلگەن)» دېدى. ئاندىن ھىجرەت قىلغانلار، يۇرتلىرىدىن ھەيدەپ چىقىرىلغانلار، مېنىڭ يولۇمدا (يەنى ئاللاھنىڭ دىنى ئۈچۈن) ئەزىيەت تارتقانلار، ئۇرۇشقا قاتناشقانلار، (يەنى مېنىڭ يولۇمدا ئۇرۇشقانلار) ۋە مېنىڭ يولۇمدا ئۆلتۈرۈلگەنلەرنىڭ گۇناھلىرىنى (مەغپىرىتىم ۋە رەھمىتىم بىلەن) ئەلۋەتتە يوققا چىقىرىمەن. ئەلۋەتتە ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن». بۇ (ئۇلارنىڭ ياخشى ئەمەللىرى ئۈچۈن) ئاللاھ تەرىپىدىن بېرىلگەن مۇكاپاتتۇر. ئاللاھنىڭ دەرگاھىدا ياخشى مۇكاپات (يەنى جەننەت) بار.');
INSERT INTO "saleh" VALUES(3,196,'3:196','كاپىرلارنىڭ (پۇل ـ مال، ئابرۇي، مەرتىۋە ئىزدەش يۈزىسىدىن) شەھەرلەردە كېزىپ يۈرۈشلىرى سېنى ئالدىمىسۇن.');
INSERT INTO "saleh" VALUES(3,197,'3:197','بۇ ئازغىنا بەھرىمەن بولۇشتۇر، كەلگۈسىدە ئۇلارنىڭ جايى جەھەننەم بولىدۇ. ئۇ نېمىدېگەن يامان تۆشەك!.');
INSERT INTO "saleh" VALUES(3,198,'3:198','پەرۋەردىگارىغا تەقۋادارلىق قىلغان كىشىلەر ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردىن بەھرىمەن بولۇپ، ئۇ يەردە مەڭگۈ قالىدۇ. بۇ، ئاللاھ تەرىپىدىن بولغان كۈتۈۋېلىشتۇر. ئاللاھنىڭ دەرگاھىدىكى نەرسىلەر (يەنى مۇكاپات ۋە ھۆرمەت) ياخشى بەندىلەر ئۈچۈن (كۇففارلار بەھرىمەن بولغان باقاسى يوق ئازغىنا راھەتتىن) ئارتۇقتۇر');
INSERT INTO "saleh" VALUES(3,199,'3:199','ئەھلى كىتاب ئارىسىدا ھەقىقەتەن ئاللاھقا ئىمان كەلتۈرىدىغان، سىلەرگە نازىل بولغان كىتابقا (يەنى قۇرئانغا)، ئۆزلىرىگە نازىل بولغان كىتابقا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىشىنىدىغانلار بار؛ ئۇلار ئاللاھتىن قورقىدۇ، ئاللاھنىڭ ئايەتلىرىنى ئازغىنا بەدەلگە تېگىشمەيدۇ (يەنى ئۇلارنىڭ كىتابلىرىدىكى مۇھەممەد ئەلەيھىسسالامنىڭ سۈپەتلىرى ۋە شەرىئەت ئەھكاملىرىنى، راھىبلارغا ئوخشاش، دۇنيانىڭ ئەرزىمەس نەرسىلىرىنى دەپ ئۆزگەرتىۋەتمەيدۇ)، ئۇلارغا پەرۋەردىگارىنىڭ دەرگاھىدا ساۋاب بېرىلىدۇ. ئاللاھ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(3,200,'3:200','ئى مۆمىنلەر! تائەت ـ ئىبادەتنىڭ مۇشەققەتلىرىگە ۋە سىلەرگە يەتكەن ئېغىرچىلىقلارغا سەۋر قىلىڭلار، دۈشمەنلەرگە قارشى زىيادە چىداملىق بولۇڭلار، (چېگرالىرىڭلارنى ساقلاپ) جىھادقا تەييار تۇرۇڭلار، مەقسىتىڭلارغا يېتىش ئۈچۈن ئاللاھتىن قورقۇڭلار (يەنى ئۇنىڭ ئەمرىگە مۇخالىپەتچىلىك قىلماڭلار).');
INSERT INTO "saleh" VALUES(4,1,'4:1','ئى ئىنسانلار! سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتقان، شۇ ئىنساندىن (يەنى ئۆز جىنسىدىن) ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتقان ۋە ئۇلاردىن (يەنى ئادەم بىلەن ھەۋۋادىن) نۇرغۇن ئەر ـ ئاياللارنى ياراتقان پەرۋەردىگارىڭلاردىن قورقۇڭلار، بىر ـ بىرىڭلاردىن نەرسە سورىغاندا نامى بىلەن سورايدىغان ئاللاھتىن قورقۇڭلار، سىلە ـ رەھىمنى ئۈزۈپ قويۇشتىن ساقلىنىڭلار. ئاللاھ ھەقىقەتەن سىلەرنى (يەنى پۈتۈن ئەھۋالىڭلارنى) كۆزىتىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,2,'4:2','يېتىملەرنىڭ مال ـ مۈلكىنى (ئۇلار بالاغەتكە يەتكەن چاغدا) بېرىڭلار، ياخشىسىنى يامانغا (يەنى ئۇلارنىڭ ماللىرىنىڭ ياخشىسىنى ئۆزۈڭلارنىڭ ماللىرىڭلارنىڭ يامىنىغا) تېگىشىۋالماڭلار؛ ئۇلارنىڭ مال ـ مۈلكىنى ئۆزۈڭلارنىڭ مال ـ مۈلكىگە قوشۇپ يەۋالماڭلار. بۇ ھەقىقەتەن چوڭ گۇناھتۇر.');
INSERT INTO "saleh" VALUES(4,3,'4:3','ئەگەر يېتىم قىزلارغا (ئۆيلىنىپ) ئادىل مۇئامىلىدە بولالماسلىقىڭلاردىن قورقساڭلار، ئۇنى تەرك ئېتىپ، ئۆزۈڭلار ياقتۇرىدىغان باشقا ئاياللاردىن ئىككىنى، ئۈچنى ۋە تۆتنى ئېلىشىڭلارغا بولىدۇ، ئەگەر (ئۇلارنىڭ ئارىسىدا) ئادىل بولالماسلىقىڭلاردىن قورقساڭلار، بىر خوتۇن بىلەن ياكى قول ئاستىڭلاردىكى چۆرىلەر بىلەن كۇپايىلىنىڭلار. بىر خوتۇن بىلەن كۇپايىلىنىش زۇلۇم قىلماسلىققا ئەڭ يېقىندۇر.');
INSERT INTO "saleh" VALUES(4,4,'4:4','ئاياللارغا ئۇلارنىڭ مەھرىلىرىنى خۇشاللىق بىلەن سوۋغا قىلىپ بېرىڭلار. ئەگەر ئۇلار ئۇنىڭدىن بىر قىسمىنى سىلەرگە ئۆتۈنۈپ بەرسە، ئۇنى مەززىلىك، سىڭىشلىق (يەنى پاك ـ ھالال بىلىپ) يەڭلار.');
INSERT INTO "saleh" VALUES(4,5,'4:5','ئاللاھ تىرىكچىلىكىڭلارنىڭ ئاساسى قىلغان ماللىرىڭلارنى ئەخمەقلەرگە تۇتقۇزۇپ قويماڭلار، ئۇلارنى يېمەك ـ ئىچمەك، كىيىم ـ كېچەكلەر بىلەن تەمىنلەڭلار. ئۇلارغا چىرايلىق سۆز قىلىڭلار.');
INSERT INTO "saleh" VALUES(4,6,'4:6','يېتىملەرنى نىكاھلىنىش يېشىغا يەتكەنگە قەدەر سىناپ تۇرۇڭلار. ئۇلاردا ماللىرىنى باشقۇرالايدىغان ھالەتنى بايقىساڭلار، ئۇلارغا مال ـ مۈلكىنى تاپشۇرۇپ بېرىڭلار؛ ئۇلارنىڭ چوڭ بولۇپ قېلىشىدىن قورقۇپ مال ـ مۈلكىنى بۇزۇپ ـ چېچىپ يەۋالماڭلار. (يېتىمگە ۋەسى بولغانلاردىن) كىمكى باي بولسا، (ۋەسى بولغانلىق ھەققى ئۈچۈن) يېتىمنىڭ مال ـ مۈلكىنى يېيىشتىن ئۆزىنى ساقلىسۇن، (سىلەردىن) كىمكى يوقسۇل بولسا، ئۇ (ئۆز ئەمگىكىنىڭ ھەققى ئۈچۈن) مۇۋاپىق رەۋىشتە يېسۇن، ئۇلارنىڭ مال ـ مۈلكىنى تاپشۇرۇپ بېرىدىغان چاغدا باشقىلارنى گۇۋاھ قىلىپ قويۇڭلار. ئاللاھ ھېساب ئېلىشقا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,7,'4:7','ئاتا ـ ئانىسى ۋە تۇغقانلىرى قالدۇرغان مىراستا (يەنى مېيىتنىڭ تەرەكىسىدە) ئەرلەرنىڭ ھەسسىسى بار. ئاتا-ئانىسى ۋە توغقانلىرى قالدۇرغان مىراستا ئاياللارنىڭمۇ ھەسسىسى بار. مەيلى ئۇ (يەنى تەرەكە) ئاز بولسۇن ياكى كۆپ بولسۇن، ھەر ئادەم (ئاللاھنىڭ ئادىل شەرىئىتىدە) بەلگىلەنگەن ھەسسىسىنى ئالىدۇ.');
INSERT INTO "saleh" VALUES(4,8,'4:8','تەقسىمات ۋاقتىدا (ۋارىس ئەمەس) تۇغقانلار، (تۇغقان ئەمەس) يېتىملەر ۋە مىسكىنلەر ھازىر بولسا، ئۇلارغا بۇنىڭدىن (يەنى تەرەكىدىن ئۇلارنىڭ كۆڭلىنى ئېلىش ئۈچۈن بىر ئاز نەرسە) بېرىڭلار، ئۇلارغا چىرايلىق سۆز قىلىڭلار (يەنى مىراسخورلارنىڭ مېلى، بىزنىڭ ئەمەس دېگەنگە ئوخشاش ئۆزرىنى ئېيتىڭلار).');
INSERT INTO "saleh" VALUES(4,9,'4:9','(كىملەركى) ئۆزلىرىدىن قېلىپ قالىدىغان كىچىك بالىلىرى بولۇپ، ئۇلاردىن قانداق ئەندىشە قىلىدىغان بولسا، باشقىلارنىڭ قېلىپ قالغان كىچىك بالىلىرى ئۈچۈنمۇ شۇنداق ئەندىشە قىلسۇن، (يېتىملەرنىڭ ئىشىدا) ئاللاھتىن قورقسۇن ۋە توغرا سۆزنى ئېيتسۇن.');
INSERT INTO "saleh" VALUES(4,10,'4:10','زۇلۇم قىلىپ يېتىملەرنىڭ مال ـ مۈلكىنى (ناھەق) يەۋالىدىغانلار، شۈبھىسىزكى، قورسىقىغا (قىيامەت كۈنى يېنىپ تۇرىدىغان) ئوتنى يەۋالغان بولىدۇ، ئۇلار يېنىپ تۇرغان ئوتقا (يەنى دوزاخقا) كىرىدۇ.');
INSERT INTO "saleh" VALUES(4,11,'4:11','ئاللاھ بالىلىرىڭلار (غا تېگىدىغان مىراس) ھەققىدە تەۋسىيە قىلىدۇكى، بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى تېگىدۇ، ئەگەر مېيىتنىڭ قىزى (ئىككى ياكى) ئىككىدىن كۆپ بولسا، ئۇ چاغدا ئۇلارغا مىراسنىڭ ئۈچتىن ئىككى ھەسسىسى تېگىدۇ، ئەگەر (ۋارىس) بىر قىزلا بولىدىغان بولسا، ئۇنىڭغا مىراسنىڭ يېرىمى تېگىدۇ، ئەگەر مېيىتنىڭ بالىلىرى بولسا، بۇ چاغدا ئۇنىڭ ئاتا ـ ئانىسىنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ. ئەگەر ئۇنىڭ بالىلىرى بولماي مىراسقا پەقەت ئۇنىڭ ئاتا ـ ئانىسى ۋارىسلىق قىلىدىغان بولسا، بۇ چاغدا ئۇنىڭ ئانىسىغا مىراسنىڭ ئۈچتىن بىرى تېگىدۇ (قالغىنى ئاتىسىغا تېگىدۇ). ئەگەر مېيىتنىڭ (ئاتا ـ ئانىسىدىن باشقا يەنە) قېرىنداشلىرى بولسا، ئانىسىغا مىراسنىڭ ئالتىدىن بىرى تېگىدۇ (قالغىنىنى ئاتىسى ئالىدۇ). (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ۋە قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئاتا ـ ئاناڭلار ۋە بالىلىرىڭلاردىن (يەنى بۇ ئىككىسىدىن) قايسىسىنىڭ مەنپەئەت جەھەتتىن ئۆزۈڭلارغا ئەڭ يېقىن ئىكەنلىكىنى بىلمەيسىلەر، بۇلار ئاللاھ تەرىپىدىن بېكىتىلگەن بەلگىلىمىدۇر. ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,12,'4:12','ئەگەر ئاياللىرىڭلارنىڭ بالىلىرى بولمىسا، بۇ چاغدا ئۇلار قالدۇرغان مىراسنىڭ يېرىمى (ئى ئەرلەر!) سىلەرگە تېگىدۇ. ئەگەر ئۇلارنىڭ بالىلىرى بولسا، ئۇلار قالدۇرغان مىراسنىڭ تۆتتىن بىرى سىلەرگە تېگىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى مېيىتنىڭ قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئەگەر سىلەرنىڭ بالىلىرىڭلار بولمىسا، بۇ چاغدا ئاياللىرىڭلارغا سىلەر قالدۇرغان مىراسنىڭ تۆتتىن بىرى تېگىدۇ، ئەگەر سىلەرنىڭ بالىلىرىڭلار بولسا، بۇ چاغدا ئاياللىرىڭلارغا سىلەر قالدۇرغان مىراستىن سەككىزدىن بىرى تېگىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. ئەگەر مىراس قالدۇرغۇچى ئاتا ـ ئانىسىز، بالىسىز ئەر ياكى ئايال بولۇپ، ئۇنىڭ پەقەت ئانا بىر قېرىندىشى ۋە ئانا بىر ھەمشىرىسى بولسا، ئۇلارنىڭ ھەر بىرىگە مىراسنىڭ ئالتىدىن بىرى تېگىدۇ. ئەگەر مىراس قالدۇرغۇچىنىڭ (يەنى مىراس قالدۇرغۇچى مېيىتنىڭ ئانا بىر، ئاتا باشقا) قېرىنداشلىرى ۋە ھەمشىرىلىرى بۇنىڭدىن (يەنى بىردىن) كۆپ بولسا، ئۇ چاغدا مىراسنىڭ ئۈچتىن بىرى ئۇلارنىڭ ئارىسىدا ئورتاق تەقسىم قىلىنىدۇ. (بۇ تەقسىمات) مېيىتنىڭ ۋەسىيىتى ئورۇنلانغان ياكى قەرزى تۆلىنىپ بولغاندىن كېيىن ئېلىپ بېرىلىدۇ. (مىراس قالدۇرغۇچى ئادەمنىڭ) ۋەسىيىتى ۋارىسلارغا زىيان يەتكۈزمەيدىغان بولۇشى كېرەك. ئۇ ئاللاھ تەرىپىدىن قىلىنغان تەۋسىيەدۇر، ئاللاھ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئەمرىگە خىلاپلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(4,13,'4:13','ئەنە شۇلار (يەنى ئاللاھ بەندىلىرىنىڭ ئەمەل قىلىشى ئۈچۈن بەلگىلەپ بەرگەن يۇقىرىدىكى ئەھكاملار) ئاللاھنىڭ قانۇنلىرىدۇر، كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، ئاللاھ ئۇنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇ ئۇ يەرلەردە مەڭگۈ قالىدۇ. بۇ چوڭ مۇۋەپپەقىيەت قازىنىشتۇر.');
INSERT INTO "saleh" VALUES(4,14,'4:14','كىمكى ئاللاھقا ۋە پەيغەمبىرىگە ئاسىيلىق قىلىپ، ئاللاھنىڭ قانۇنلىرىنىڭ سىرتىغا چىقىپ كېتىدىكەن، ئاللاھ ئۇنى دوزاخقا كىرگۈزىدۇ، ئۇ دوزاختا مەڭگۈ قالىدۇ، خورلىغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(4,15,'4:15','ئاياللىرىڭلاردىن پاھىشە قىلغانلارنى (ئىسپاتلاش ئۈچۈن) ئاراڭلاردىن تۆت گۇۋاھچى كەلتۈرۈڭلار؛ ئەگەر ئۇلار گۇۋاھلىق بەرسە (يەنى ئۇلار گۇۋاھلىق بېرىپ ئايالنىڭ جىنايىتىنى ئىسپاتلىسا)، تاكى ئاياللىرىڭلار ۋاپات بولغانغا ياكى ئاللاھ ئۇلارغا بىر چىقىش يولى بەرگەنگە قەدەر ئۇلارنى ئۆيدە تۇتۇڭلار.');
INSERT INTO "saleh" VALUES(4,16,'4:16','ئاراڭلاردا ئىككى كىشى پاھىشە قىلسا، ئۇلارنى ئەيىبلەڭلار؛ ئەگەر ئۇلار تەۋبە قىلىپ تۈزەلسە، ئۇلارنى كەچۈرۈڭلار، ئاللاھ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، تولىمۇ مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,17,'4:17','ئاللاھ قوبۇل قىلىشنى ۋەدە قىلغان تەۋبە يامانلىقنى نادانلىقتىن قىلىپ سېلىپ، ئاندىن ئۇزۇنغا قالماي تەۋبە قىلغانلارنىڭ تەۋبىسىدۇر؛ ئاللاھ ئەنە شۇلارنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، ئاللاھ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,18,'4:18','داۋاملىق يامان ئىشلارنى قىلىپ بېشىغا ئۆلۈم كەلگەن چاغدا، ئەمدى تەۋبە قىلدىم، دېگۈچىلەرنىڭ تەۋبىسى ۋە كاپىر پېتى ئۆلگۈچىلەرنىڭ تەۋبىسى (يەنى ئۆلۈم ئالدىدا ئىمان ئېيتتىم دېگۈچىلەرنىڭ ئىمانى) قوبۇل قىلىنمايدۇ. ئەنە شۇلارغا قاتتىق ئازاب تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(4,19,'4:19','ئى مۆمىنلەر! ئاياللارغا زورلۇق قىلىپ، ئۇلارنى مىراس قىلىپ ئالماق (يەنى بىر ئادەمنىڭ قولىدىن يەنە بىر ئادەمنىڭ قولىغا ئۆتۈپ تۇرىدىغان مال ئورنىدا قىلىۋالماق) سىلەرگە دۇرۇس بولمايدۇ؛ ئۇلار ئوپئوچۇق بىر پاھىشە ئىشنى قىلمىغان ھالەتتە، سىلەر ئۇلارغا بەرگەن مەھرىدىن بىر قىسمىنى يۇلۇۋېلىش ئۈچۈن ئۇلارغا بېسىم ئىشلەتمەڭلار، ئۇلار بىلەن چىرايلىقچە تىرىكچىلىك قىلىڭلار، ئەگەر ئۇلارنى ياقتۇرمىساڭلار (سەۋر قىلىڭلار)، چۈنكى سىلەر ياقتۇرمايدىغان بىر ئىشتا ئاللاھ كۆپ خەيرىيەتلەرنى پەيدا قىلىشى مۇمكىن.');
INSERT INTO "saleh" VALUES(4,20,'4:20','ئەگەر بىر خوتۇننى قويۇۋېتىپ، ئورنىغا يەنە بىر خوتۇننى ئالماقچى بولساڭلار، قويۇۋەتمەكچى بولغان خوتۇنغا (مەھرى قىلىپ) كۆپ مال بەرگەن بولساڭلارمۇ ئۇنىڭدىن ھېچ نەرسىنى قايتۇرۇۋالماڭلار. ئۇنى (ئايالغا) قارا چاپلاپ ۋە ئوچۇق زۇلۇم قىلىپ ئالامسىلەر؟.');
INSERT INTO "saleh" VALUES(4,21,'4:21','ئۆزئارا خىلۋەتتە بولۇشقان تۇرساڭلار ۋە ئاياللار سىلەردىن (نىكاھ ئەقدىسىدىن ئىبارەت) مۇستەھكەم ئەھدە ئالغان تۇرسا، ئۇنى قانداقمۇ قايتۇرۇۋالىسىلەر؟.');
INSERT INTO "saleh" VALUES(4,22,'4:22',' ئاتىلىرىڭلار ئالغان ئاياللارنى ئالماڭلار، لېكىن بۇنىڭدىن ئىلگىرى ئالغان بولساڭلار (ئەپۇ قىلىنىدۇ)، بۇ ھەقىقەتەن يامان ئىشتۇر، ئۆچ كۆرۈلىدىغان قىلىقتۇر، بۇ نېمىدېگەن يامان ئادەت!.');
INSERT INTO "saleh" VALUES(4,23,'4:23','سىلەرگە ئانىلىرىڭلارنى، قىزلىرىڭلارنى، ھەمشىرىلىرىڭلارنى، ئاتاڭلارنىڭ ھەمشىرىلىرىنى، ئاناڭلارنىڭ ھەمشىرىلىرىنى، قېرىندىشىڭلارنىڭ قىزلىرىنى، ھەمشىرەڭلارنىڭ قىزلىرىنى، سۈت ئانىلىرىڭلارنى، ئېمىلدەش ھەمشىرەڭلارنى، قېيىن ئاناڭلارنى، سىلەر بىر يەردە بولغان ئاياللىرىڭلارنىڭ (باشقا ئەردىن بولغان) ئۆيۈڭلاردا تەربىيەلەنگەن قىزلىرىنى (بۇ شەرت ئەمەس، ئۆيدە تەربىيەلەنمىگەن بولسىمۇ) ئېلىش ھارام قىلىندى. ئەگەر سىلەر ئۇلارنىڭ ئانىلىرى بىلەن بىر تۆشەكتە بولمىغان بولساڭلار، بۇ چاغدا ئۇلارنى ئالساڭلار ھېچ گۇناھ بولمايدۇ. يەنە ئۆز پۇشتىڭلاردىن بولغان بالىلىرىڭلارنىڭ ئاياللىرىنى ئېلىشىڭلار ۋە ئىككى ھەمشىرىنى بىر ۋاقىتتا ئەمرىڭلاردا ساقلىشىڭلار ھارام قىلىندى. لېكىن ئۆتكەنكىلەر (يەنى جاھىلىيەت دەۋرىدە قىلغىنىڭلار) ئەپۇ قىلىنىدۇ. ئاللاھ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,24,'4:24','ئەرلىك ئاياللارنى ئېلىشمۇ سىلەرگە ھارام قىلىندى. پەقەت سىلەرنىڭ باشقۇرۇشۇڭلار ئاستىدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا. بۇ ئاللاھنىڭ سىلەرگە پەرز قىلغان ھۆكۈملىرىدۇر، بۇلاردىن (يەنى يۇقىرىدىكى ئېلىش دۇرۇس بولمايدىغان ئاياللاردىن) باشقىلىرىنى زىنادىن ساقلىنىپ، ئىپپەتلىك بولغىنىڭلار ھالدا، ماللىرىڭلارنى سەرپ قىلىپ (يەنى مەھرىنى بېرىپ) نىكاھلاپ ئېلىشىڭلار ھالال قىلىندى. قايسىكى ئاياللارنى (نىكاھلاپ ئېلىپ) پايدىلانساڭلار، ئۇلارغا (نىكاھ ۋاقتىدا) بېكىتىلگەن مەھرىلىرىنى بېرىڭلار، مەھرى بېكىتىلگەندىن كېيىن، ئۆزئارا پۈتۈشكەن نەرسەڭلار توغرىسىدا (يەنى ئايالنىڭ رازىلىقى بىلەن ساقىت قىلىنغان مەھرى ئۈچۈن) سىلەرگە ھېچ گۇناھ بولمايدۇ. ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(4,25,'4:25','سىلەردىن مۆمىن ئاياللارنى ئېلىشقا قۇربى يەتمەيدىغانلار قول ئاستىدىكى مۆمىن چۆرىلەردىن ئالسۇن. ئاللاھ ئىمانىڭلارنى ئوبدان بىلىدۇ (يەنى ئۇلارنىڭ سىرتقى كۆرۈنۈشىگە قاراپ ئېلىۋېرىڭلار، ئىچكى ئەھۋالىنى ئاللاھقا تاپشۇرۇڭلار). سىلەر بىر ـ بىرىڭلار بىلەن دىنداشسىلەر، ئۇلارنىڭ خوجىلىرىنىڭ قوشۇلۇشى بىلەن ئۇلارنى خوتۇنلۇققا ئالساڭلار بولىدۇ، ئۇلارغا مەھرىلىرىنى كېمەيتىۋەتمەستىن ۋە كېچىكتۈرمەستىن بېرىڭلار. لېكىن ئۇلار ئاشكارا زىنا قىلمايدىغان، يوشۇرۇن ئاشنا تۇتمايدىغان ئىپپەتلىك بولۇشلىرى كېرەك. ئەگەر ئۇلار ئەرگە تەگكەندىن كېيىن پاھىشە قىلسا، ئۇلارغا ھۆر ئاياللارغا بېرىلىدىغان جازانىڭ يېرىمى بېرىلىشى كېرەك. بۇ (يەنى چۆرىلەرنى نىكاھلاپ ئېلىش)، ئاراڭلاردا زىناغا مۇپتىلا بولۇپ قېلىشتىن قورققان كىشى ئۈچۈندۇر. سەۋر قىلالىساڭلار سىلەر ئۈچۈن ياخشىدۇر. ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,26,'4:26',' ئاللاھ سىلەرگە (ھالال ۋە ھارامغا دائىر ئەھكاملارنى) بايان قىلىشنى، سىلەردىن بۇرۇنقىلار (يەنى پەيغەمبەرلەر ۋە ياخشىلار) نىڭ يوللىرىغا سىلەرنى ھىدايەت قىلىشنى، (گۇناھىڭلاردىن قىلغان) تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ. ئاللاھ ھەممىنى (يەنى بەندىلىرىنىڭ ھەممە ئەھۋالىنى) بىلگۈچىدۇر ۋە ھېكمەت بىلەن ئىش قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(4,27,'4:27','ئاللاھ سىلەرنىڭ تەۋبەڭلارنى قوبۇل قىلىشنى خالايدۇ (بۇ ئاللاھنىڭ رەھمىتىنىڭ كەڭلىكىنى كۆرسىتىش ئۈچۈن تەكرارلاندى). شەھۋەتلەرگە ئەگىشىدىغانلار (يەنى شەيتانغا ئەگىشىدىغانلار) توغرا يولدىن بۇرۇلۇپ كېتىشىڭلارنى (شۇلارغا ئوخشاش فاجىر بولۇشۇڭلارنى) خالايدۇ.');
INSERT INTO "saleh" VALUES(4,28,'4:28','ئاللاھ سىلەرنىڭ يۈكۈڭلارنى يېنىكلىتىشنى (يەنى شەرىئەت ئەھكاملىرىنى سىلەرگە ئاسانلاشتۇرۇشنى) خالايدۇ. ئىنسان (نەپسى خاھىشىنى تىزگىنلەشتىن) ئاجىز يارىتىلدى.');
INSERT INTO "saleh" VALUES(4,29,'4:29','ئى مۆمىنلەر! بىر ـ بىرىڭلارنىڭ ماللىرىنى (ئوغرىلىق، خىيانەت قىلىش، بۇلاش، جازانىخورلۇق، قىمار ئويناش قاتارلىق) ناھەق يول بىلەن يەۋالماڭلار، ئىككى تەرەپ رازى بولۇشۇپ قىلىشقان سودا ـ سېتىق ئارقىلىق ئېرىشىلگەن نەرسە بۇنىڭدىن مۇستەسنا. سىلەر ئۆزۈڭلارنى (يەنى بىر ـ بىرىڭلارنى) ئۆلتۈرمەڭلار، ئاللاھ ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,30,'4:30','كىمكى چەكتىن ئېشىپ ۋە زۇلۇم قىلىپ ئۇنى (يەنە ئاللاھ چەكلىگەن ئىشلارنى) قىلىدىكەن، ئۇنى دوزاخقا كىرگۈزىمىز، بۇ ئاللاھقا ئاساندۇر.');
INSERT INTO "saleh" VALUES(4,31,'4:31','ئەگەر سىلەر مەنئى قىلىنغان چوڭ گۇناھلاردىن ساقلانساڭلار، كىچىك گۇناھلىرىڭلارنى يوققا چىقىرىمىز ۋە سىلەرنى ئېسىل ماكانغا (يەنى جەننەتكە) كىرگۈزىمىز.');
INSERT INTO "saleh" VALUES(4,32,'4:32','ئى مۆمىنلەر! ئاللاھ سىلەردىكى بىر ـ بىرىڭلاردىن ئارتۇق قىلغان نەرسىلەرنى (ھەسەت قىلىش يۈزىسىدىن) ئارزۇ قىلماڭلار، ئەرلەر قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ، ئاياللارمۇ قىلغان ئەمەللىرىدىن ھەسسىدار بولىدۇ. ئاللاھتىن ئۇنىڭ پەزلىنى تىلەڭلار (ئاللاھ بېرىدۇ). ئاللاھ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(4,33,'4:33','ھەر بىر ئادەمنىڭ قالدۇرغان تەرەكىسى ئۈچۈن ۋارىسلار تەيىنلىدۇق، ئۇلار ئاتا ـ ئانىلار ۋە يېقىنلاردۇر. سىلەر بىلەن (ئۆزئارا ياردەم بېرىش ۋە مىراسخور بولۇشقا) ئەھدۇپەيمان قىلىشقان كىشىلەرگە ئۇلارنىڭ (مىراستىن) ھەسسىسىنى بېرىڭلار. ئاللاھ ھەقىقەتەن ھەممە نەرسىنى بايقاپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,34,'4:34','ئەرلەر ئاياللارنىڭ ھامىيلىرىدۇر، بۇ ئاللاھنىڭ ئۇلارنىڭ بەزىسىنى بەزىسىدىن (يەنى ئەرلەرنى كۈچ ـ قۇۋۋەت، غازاتقا چىقىش قاتارلىق جەھەتلەردە ئاياللاردىن) ئارتۇق قىلغانلىقىدىندۇر ۋە ئەرلەرنىڭ ئۆز پۇل ـ ماللىرىنى سەرپ قىلغانلىقىدىندۇر (يەنى ئاياللارنىڭ نەپىقىسى ئەرلەرنىڭ زىممىسىگە يۈكلەنگەنلىكتىندۇر). ياخشى ئاياللار ئىتائەت قىلغۇچىلاردۇر، ئەرلىرى يېنىدا بولمىغان چاغلاردا ئاللاھنىڭ پاناھىدا ئەرلىرىنىڭ ھەقلىرىنى ساقلىغۇچىلاردۇر. سىلەر سەركەشلىك قىلىشلىرىدىن قورقىدىغان ئاياللارغا نەسىھەت قىلىڭلار، (بۇ ئۈنۈم بەرمىسە) بىر تۆشەكتە بىللە ياتماڭلار، (بۇمۇ ئۈنۈم بەرمىسە) ئۇلارنى ئەدەبلەپ قويۇڭلار. ئەگەر سىلەرگە ئىتائەت قىلسا، ئۇلارنى بوزەك قىلىش خىيالىدا بولماڭلار. ئاللاھ ھەقىقەتەن سىلەردىن ئۈستۈندۇر، بۈيۈكتۇر (ئاللاھ ئاياللارغا زۇلۇم قىلغاننى جازالايدۇ). ');
INSERT INTO "saleh" VALUES(4,35,'4:35','ئەگەر ئەر ـ خوتۇن ئىككىيلەننىڭ ئىناقسىز بولۇپ قېلىشىدىن قورقساڭلار، ئەرنىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى، خوتۇننىڭ تۇغقانلىرىدىن بىر ھەققانىي كىشىنى ئەۋەتىڭلار، ئەگەر بۇ ئىككى كىشى بۇلارنى ئەپلەشتۈرۈشنى خالىسا، ئاللاھ ئەر ـ خوتۇن ئىككىسىنىڭ ئارىسىغا ئىناقلىق سالىدۇ. ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(4,36,'4:36','ئاللاھقا ئىبادەت قىلىڭلار، ئۇنىڭغا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا ـ ئاناڭلارغا، خىش ـ ئەقرىبالىرىڭلارغا، يېتىملەرگە، مىسكىنلەرگە، يېقىن قوشنىغا، يىراق قوشنىغا، ياندىكى ھەمراھقا (يەنى سەپەرداشقا، ساۋاقداشقا)، مۇساپىرغا، قول ئاستىڭلاردىكى قۇل ـ چۆرىلەرگە ياخشىلىق قىلىڭلار، شۈبھىسىزكى، ئاللاھ مۇتەكەببىر ، ماختانچاقنى ياقتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(4,37,'4:37','ئۇلار (يەنى مۇتەكەببىرلەر ، ماختانچاقلار) بېخىللىق قىلىدۇ، كىشىلەرنى بېخىللىققا بۇيرۇيدۇ، ئاللاھ ئۇلارغا ئۆز پەزلىدىن بەرگەن نەرسىلەرنى يوشۇرىدۇ، (ئاللاھنىڭ نېمىتىنى) ئىنكار قىلغۇچىلارغا خورلىغۇچى ئازابنى تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(4,38,'4:38','ئۇلار ماللىرىنى كىشىلەرگە كۆرسىتىش ئۈچۈن سەرپ قىلىدۇ، ئۇلار ئاللاھقا ئىشەنمەيدۇ، ئاخىرەت كۈنىگىمۇ ئىشەنمەيدۇ. كىمكى ئۆزىگە شەيتاننى ھەمراھ قىلىدىكەن (ئۇ شەيتاننىڭ بۇيرۇقى بويىچە ئىش قىلىدۇ)، شەيتان نېمىدېگەن يامان ھەمراھ!.');
INSERT INTO "saleh" VALUES(4,39,'4:39','ئەگەر ئۇلار ئاللاھقا، ئاخىرەت كۈنىگە ئىشەنسە ۋە ئاللاھ ئۆزلىرىگە بەرگەن مالدىن سەرپ قىلسا، ئۇلارغا نېمە زىيىنى بولاتتى؟ ئاللاھ ئۇلارنى ئوبدان بىلىدۇ (قىلمىشىغا قاراپ جازالايدۇ).');
INSERT INTO "saleh" VALUES(4,40,'4:40','ئاللاھ ھەقىقەتەن زەررىچە زۇلۇم قىلمايدۇ. ئەگەر كىشىنىڭ زەررىچە ياخشىلىقى بولسا، ئاللاھ ئۇنى ھەسسىلەپ زىيادە قىلىدۇ، ئۆز دەرگاھىدىن بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلىدۇ.');
INSERT INTO "saleh" VALUES(4,41,'4:41','بىز ھەر بىر ئۈممەتتىن بىر گۇۋاھچىنى كەلتۈرگەن ۋە (ئى مۇھەممەد!) سېنى بۇلارغا گۇۋاھچى قىلىپ كەلتۈرگەن چېغىمىزدا ئۇلارنىڭ ھالى قانداق بولىدۇ؟.');
INSERT INTO "saleh" VALUES(4,42,'4:42','كاپىرلار ۋە پەيغەمبەرگە ئاسىيلىق قىلغانلار ئۇ كۈندە (ئۇلار قىيامەتنىڭ دەھشىتىنى كۆرگەنلىكتىن) يەر بىلەن تۈپتۈز قىلىۋېتىلىشىنى (يەنى ئۆزىنىڭ يەرگە كۆمۈلۈپ تۇپراققا ئايلىنىپ كېتىشىنى) ئارزۇ قىلىدۇ، (ئۇلارنىڭ ھەممە ئەزاسى گۇۋاھ بولغانلىقتىن) ئاللاھتىن ھېچبىر سۆزنى يوشۇرالمايدۇ.');
INSERT INTO "saleh" VALUES(4,43,'4:43','ئى مۆمىنلەر! سىلەر مەست بولساڭلار، نېمە دەۋاتقىنىڭلارنى بىلگىنىڭلارغىچە، جۇنۇپ بولساڭلار، ـ يول ئۈستىدە بولغانلار (بۇنىڭدىن) مۇستەسنا، ـ غۇسلى قىلمىغىچە نامازغا يېقىنلاشماڭلار، ئەگەر كېسەل (يەنى كېسەلگە سۇ زىيان قىلىدىغان بولسا) ياكى سەپەر ئۈستىدە بولساڭلار ياكى ھاجەت قىلساڭلار ياكى ئاياللار بىلەن مۇناسىۋەت ئۆتكۈزسەڭلار، (مۇشۇنداق ئەھۋال ئاستىدا) سۇ تاپالمىساڭلار، پاك تۇپراقنى يۈزۈڭلارغا، قولۇڭلارغا سۈرتۈپ تەيەممۇم قىلىڭلار، ئاللاھ ھەقىقەتەن ئەپۇ قىلغۇچىدۇر، مەغپىرەت قىلغۇچىدۇر (يەنى ئاللاھ بەندىلىرىگە ھەرەج بولماسلىقى ئۈچۈن ئىبادەتنى ئاسانلاشتۇرۇپ بەرگۈچىدۇر).');
INSERT INTO "saleh" VALUES(4,44,'4:44','(ئى مۇھەممەد!) كىتابتىن (يەنى تەۋراتتىن) نېسىۋە بېرىلگەن كىشىلەر (يەنى يەھۇدىي موللىلىرى) نىڭ گۇمراھلىقنى سېتىۋالغانلىقىنى ۋە سىلەرنىڭ توغرا يولدىن ئېزىشىڭلارنى خالايدىغانلىقىنى كۆرمىدىڭمۇ؟.');
INSERT INTO "saleh" VALUES(4,45,'4:45','ئاللاھ سىلەرنىڭ دۈشمەنلىرىڭلارنى ئوبدان بىلىدۇ. ئاللاھ سىلەرنى ساقلاشقا يېتەرلىكتۇر ۋە سىلەرگە مەدەتكار بولۇشقا يېتەرلىكتۇر (يەنى سىلەرنى يەھۇدىيلارنىڭ مىكرىدىن ساقلاشقا ئاللاھ كۇپايىدۇر).');
INSERT INTO "saleh" VALUES(4,46,'4:46','يەھۇدىيلارنىڭ ئىچىدە كىتابنىڭ (يەنى تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدىغانلارمۇ بار، ئۇلار: «بىز (سۆزۈڭنى) ئاڭلىدۇق، (ئەمرىڭگە) بويسۇنمىدۇق، بىزگە قۇلاق سال، بىز ساڭا قۇلاق سالمايمىز» دەيدۇ، دىنغا تەنە قىلىش يۈزىسىدىن، رائىنا دېگەن سۆزنى تىللىرىنى ئەگرى قىلىپ ئېيتىدۇ. ئەگەر ئۇلار (يۇقىرىقى سۆزلىرىنىڭ ئورنىغا) ئاڭلىدۇق ۋە بويسۇندۇق، قۇلاق سال، ئۇنزۇرنا دېسە، ئۇلار ئۈچۈن (ئاللاھنىڭ دەرگاھىدا) تېخىمۇ ياخشى، تېخىمۇ توغرا بولاتتى، لېكىن كۇفرى سەۋەبلىك ئاللاھ ئۇلارنى رەھمىتىدىن يىراق قىلدى. ئۇلارنىڭ ئازغىنىسىدىن باشقىسى ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(4,47,'4:47','ئى كىتاب بېرىلگەنلەر! (يەنى يەھۇدىيلار) بىز نۇرغۇن يۈزلەرنى ئۆزگەرتىپ ئارقىسىغا قىلىپ قويۇشىمىزدىن (يەنى يۈزدىكى بۇرۇن، كۆز، قاش قاتارلىق سەزگۈ ئەزالىرىنى ئۆز جايلىرىدىن يوق قىلىۋېتىپ، ئۇلارنى ئارقا تەرەپكە پەيدا قىلىپ قويۇشىمىزدىن، سۈرەتلىرىنى مۇبەددەل قىلىشىمىزدىن) ئىلگىرى، ياكى ئۇلارغا شەنبە كۈنىنىڭ ھۆرمىتىنى ساقلىمىغانلارغا لەنەت قىلغاندەك لەنەت قىلىشىمىزدىن ئىلگىرى، ئۆزۈڭلاردىكى كىتابنى (يەنى تەۋراتنى) تەستىق قىلىدىغان، بىز نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ئىمان كەلتۈرۈڭلار (بولمىسا يۇقىرىقى ئىككى بالانىڭ بىرىگە ئۇچرايسىلەر)، ئاللاھنىڭ ھۆكمى ئىجرا قىلىنىدۇ (يەنى ئاللاھ بىرەر ئىشقا ئەمر قىلسا، ئۇ ئىش چوقۇم بولماي قالمايدۇ).');
INSERT INTO "saleh" VALUES(4,48,'4:48','ئاللاھ ھەقىقەتەن ئاللاھقا شېرىك كەلتۈرۈش گۇناھىنى مەغپىرەت قىلمايدۇ، خالىغان ئادەمنىڭ ئۇنىڭدىن باشقا گۇناھىنى مەغپىرەت قىلىدۇ. كىمكى ئاللاھقا شېرىك كەلتۈرىدىكەن، چوڭ گۇناھ قىلغان بولىدۇ.');
INSERT INTO "saleh" VALUES(4,49,'4:49','ئۆزلىرىنى پاك دەپ قارايدىغانلار (يەنى ئۆزلىرىنى تەقۋادارلىق بىلەن مەدھىيەلەيدىغانلار) نى كۆرمىدىڭمۇ؟ ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ دېگىنىدەك ئەمەس)، ئاللاھ خالىغان بەندىسىنى پاك قىلىدۇ، ئۇلارغا قىلچىلىكمۇ زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(4,50,'4:50','(ئى مۇھەممەد!) ئۇلارنىڭ ئاللاھقا تۆھمەت چاپلاۋاتقانلىقىغا قارىغىن! بۇ ئوپئوچۇق گۇناھتۇر.');
INSERT INTO "saleh" VALUES(4,51,'4:51','كىتابتىن نېسىۋە بېرىلگەنلەر (يەنى يەھۇدىيلار) نى كۆرمىدىڭمۇ؟ ئۇلار بۇتقا ۋە شەيتانغا ئىشىنىدۇ، كاپىرلارنى كۆرسىتىپ: «بۇلار (يەنى قۇرەيش كۇففارلىرى) نىڭ يولى ئىمان ئېيتقانلارنىڭكىدىن تېخىمۇ توغرىدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(4,52,'4:52','ئەنە شۇلار ئاللاھ رەھمىتىدىن يىراق قىلغان كىشىلەردۇر؛ ئاللاھ كىمنى رەھمىتىدىن يىراق قىلىدىكەن، ئۇنىڭغا (ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچبىر مەدەتكار تاپالمايسەن.');
INSERT INTO "saleh" VALUES(4,53,'4:53','ياكى ئۇلارنىڭ پادىشاھلىقتىن نېسىۋىسى بارمۇ؟ (ھېچقانداق نېسىۋىسى يوق) ئەگەر بولىدىغان بولسا، (زىيادە بېخىللىقتىن) ئۇلار كىشىلەرگە قىلچىلىك نەرسە بەرمەيدۇ.');
INSERT INTO "saleh" VALUES(4,54,'4:54','ياكى ئۇلار ئاللاھ ئۆز پەزلىدىن كىشىلەرگە بەرگەن نەرسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا بېرىلگەن پەيغەمبەرلىككە) ھەسەت قىلىشامدۇ؟ بىز ھەقىقەتەن ئىبىراھىم ئەۋلادىغا كىتاب، ھېكمەت بەردۇق ۋە ئۇلارغا بۈيۈك پادىشاھلىق بەردۇق.');
INSERT INTO "saleh" VALUES(4,55,'4:55','ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتقانلارمۇ بار، ئۇنىڭدىن يۈز ئۆرۈگەنلەرمۇ بار، ئۇلارغا (ئۇلارنىڭ كۇفرىغا جازا بېرىش يۈزىسىدىن) جەھەننەمنىڭ يېنىپ تۇرغان ئوتى يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,56,'4:56','شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى دوزاخقا كىرگۈزىمىز، ئۇلارنىڭ تېرىلىرى پىشىپ تۈگىگەن چاغدا ئازابنى تېتىتىش ئۈچۈن ئورنىغا باشقا تېرە يەڭگۈشلەيمىز. ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,57,'4:57','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمىز، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ئۇ يەردە ئۇلارغا پاك جۈپتىلەر بولىدۇ، ئۇلارنى جەننەتنىڭ مەڭگۈلۈك سايىسىگە داخىل قىلىمىز. ');
INSERT INTO "saleh" VALUES(4,58,'4:58','شۈبھىسىزكى، ئاللاھ سىلەرنى ئامانەتلەرنى ئىگىسىگە قايتۇرۇشقا، كىشىلەر ئارىسىدا ھۆكۈم قىلغاندا ئادىل ھۆكۈم قىلىشقا بۇيرۇيدۇ، ئاللاھ سىلەرگە نەسىھەت قىلغان ئىشلار نېمىدېگەن ياخشى، ئاللاھ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر (ئىشىڭلارنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,59,'4:59','ئى مۆمىنلەر! ئاللاھقا، پەيغەمبەرگە ۋە ئۆزۈڭلاردىن بولغان ئىش ئۈستىدىكىلەرگە ئىتائەت قىلىڭلار، ئەگەر سىلەر بىر شەيئىدە ئىختىلاپ قىلىشىپ قالساڭلار، بۇ توغرىدا ئاللاھقا ۋە پەيغەمبەرگە مۇراجىئەت قىلىڭلار، ئەگەر سىلەر ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان بولساڭلار بۇ (يەنى ئاللاھنىڭ كىتابىغا ۋە پەيغەمبىرىنىڭ سۈننىتىگە مۇراجىئەت قىلىش) سىلەر ئۈچۈن پايدىلىقتۇر، نەتىجە ئېتىبارى بىلەن گۈزەلدۇر.');
INSERT INTO "saleh" VALUES(4,60,'4:60','ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ۋە سەندىن بۇرۇن نازىل قىلىنغان كىتابلارغا (يەنى تەۋراتقا ۋە ئىنجىلغا) ئىمان كەلتۈردۇق دەۋالغان كىشىلەر (يەنى مۇناپىقلار) نى كۆرمىدىڭمۇ؟ ئۇلار ئەرزىنى تاغۇتنىڭ ئالدىغا ئېلىپ بارماقچى بولۇۋاتىدۇ؛ ھالبۇكى، ئۇلار تاغۇتنى ئىنكار قىلىشقا بۇيرۇلغان، شەيتان ئۇلارنى چوڭقۇر ئازدۇرۇشنى خالايدۇ.');
INSERT INTO "saleh" VALUES(4,61,'4:61','ئۇلار (يەنى مۇناپىقلار) غا: «ئاللاھ نازىل قىلغان كىتاب تەرەپكە ۋە پەيغەمبەر تەرەپكە كېلىڭلار» دېيىلسە، مۇناپىقلارنىڭ سەندىن قاتتىق يۈز ئۆرۈگەنلىكىنى كۆرىسەن.');
INSERT INTO "saleh" VALUES(4,62,'4:62','قىلمىشلىرى تۈپەيلىدىن ئۆز بېشىغا بىرەر مۇسىبەت كەلگەندە، سېنىڭ ئالدىڭغا كېلىپ ئاللاھ بىلەن قەسەم قىلغان ھالدا: «بىز پەقەت ياخشىلىق ۋە يارىشىشنىلا ئىرادە قىلغان ئىدۇق» دېگەنلەرنىڭ ھالى قانداق بولىدۇ؟.');
INSERT INTO "saleh" VALUES(4,63,'4:63','ئەنە شۇلار (يەنى مۇناپىقلار) (يالغان ئېيتىدۇ)، ئاللاھ ئۇلارنىڭ دىللىرىدىكىنى (يەنى مۇناپىقلىقىنى، مىكرىنى) بىلىدۇ، ئۇلاردىن يۈز ئۆرۈگىن، ئۇلارغا نەسىھەت قىلغىن، ئۇلارغا تەسىرلىك سۆزلەرنى قىلغىن.');
INSERT INTO "saleh" VALUES(4,64,'4:64','بىز ھەر قانداق پەيغەمبەرنى ئاللاھنىڭ ئىزنى بىلەن پەقەت ئىتائەت قىلىنىش ئۈچۈنلا ئەۋەتتۇق (ئۇنىڭغا ئىتائەت قىلغانلىق ئاللاھقا ئىتائەت قىلغانلىق بولىدۇ)، ئۇلار (يەنى مۇناپىقلار) (سېنىڭ ھۆكمىڭنى قوبۇل قىلماي) ئۆزلىرىگە زۇلۇم قىلغاندا، سېنىڭ يېنىڭغا كېلىپ ئاللاھتىن مەغپىرەت تىلىسە، پەيغەمبەرمۇ ئۇلار ئۈچۈن مەغپىرەت تىلىسە ئىدى، ئەلۋەتتە ئۇلار ئاللاھنى كەچۈرۈم قىلغۇچى، ناھايىتى مېھرىبان تاپاتتى.');
INSERT INTO "saleh" VALUES(4,65,'4:65','(ئى مۇھەممەد!) پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلار (يەنى مۇناپىقلار) ئۆز ئارىسىدىكى دەتالاشقا سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلمىغىچە، ئاندىن سېنىڭ چىقارغان ھۆكمىڭگە ئۇلارنىڭ دىللىرىدىكى قىلچە غۇم بولسىمۇ يوقالمىغىچە ۋە ئۇلار پۈتۈنلەي بويسۇنمىغىچە ئىمان ئېيتقان بولمايدۇ.');
INSERT INTO "saleh" VALUES(4,66,'4:66','ئەگەر بىز ئۇلارغا: «ئۆزۈڭلارنى ئۆلتۈرۈڭلار ياكى يۇرتۇڭلاردىن چىقىپ كېتىڭلار» دەپ ئەمر قىلغان بولساق (يەنى ئىلگىرىكىلەرگە يۈكلىگەندەك ئېغىر تەكلىپنى ئۇلارغا يۈكلىسەك) ئىدۇق، ئۇلارنىڭ ئازغىنىسىدىن باشقىسى بۇنى ئىجرا قىلمايتتى. ئەگەر ئۇلار قىلىنغان ۋەز ـ نەسىھەتكە ئەمەل قىلسا ئىدى، ئەلۋەتتە ئۇلارغا ياخشى بولاتتى ۋە ئىمانى مۇستەھكەم بولغان بولاتتى (گۇمراھلىق ۋە نىفاقتىن ئەڭ يىراق بولغان بولاتتى).');
INSERT INTO "saleh" VALUES(4,67,'4:67','ئۇ چاغدا دەرگاھىمىزدىن ئۇلارغا ئەلۋەتتە بۈيۈك ئەجىر (يەنى جەننەت) ئاتا قىلاتتۇق.');
INSERT INTO "saleh" VALUES(4,68,'4:68','ئۇلارنى ئەلۋەتتە توغرا يولغا ھىدايەت قىلاتتۇق.');
INSERT INTO "saleh" VALUES(4,69,'4:69','كىملەركى ئاللاھقا ۋە پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇلار (ئاخىرەتتە) ئاللاھنىڭ نېمىتىگە ئېرىشكەن پەيغەمبەرلەر، سىددىقلار، شېھىتلەر ۋە ياخشىلار بىلەن بىللە بولىدۇ، ئۇلار نېمىدېگەن ياخشى ھەمراھلار!');
INSERT INTO "saleh" VALUES(4,70,'4:70','بۇ پەزل ئاللاھ تەرىپىدىندۇر، ئاللاھ (پەزل ـ ئېھسانغا تېگىشلىك بولغانلارنى) بىلىشتە يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,71,'4:71','ئى مۆمىنلەر! (دۈشمەنلەرنىڭ شەررىدىن ساقلىنىش ئۈچۈن) تەييارلىق ھالىتىدە تۇرۇڭلار، ئۇرۇشقا تۈركۈم ـ تۈركۈم بولۇپ ياكى ھەممىڭلار بىر بولۇپ چىقىڭلار.');
INSERT INTO "saleh" VALUES(4,72,'4:72','شۈبھىسىزكى، ئاراڭلاردا ئۇرۇشقا چىقماي قېلىپ قالغانلار (يەنى مۇناپىقلار) بار، ئەگەر سىلەرگە بىرەر مۇسىبەت (يەنى مەغلۇبىيەت ياكى قىرغىنچىلىق) كەلسە، (ئۇ مۇناپىق) «ئاللاھ ماڭا ھەقىقەتەن مەرھەمەت قىلدى، چۈنكى مەن ئۇلار بىلەن (ئۇرۇشتا) بىللە بولمىدىم (بولمىسا ئۆلتۈرۈلگەنلەر قاتارىدا ئۆلتۈرۈلگەن بولاتتىم)» دەيدۇ.');
INSERT INTO "saleh" VALUES(4,73,'4:73','ئەگەر سىلەرگە ئاللاھنىڭ پەزلى (يەنى غەلىبە ۋە غەنىيمەت) يەتسە، گويا سىلەر بىلەن ئۇ (يەنى مۇناپىق) نىڭ ئارىسىدا ھېچقانداق دوستلۇق يوقتىكىدەك، «كاشكى مەن (ئۇرۇشتا) ئۇلار بىلەن بىللە بولغان بولسام، چوڭ بىر مۇۋەپپەقىيەتكە (يەنى غەنىيمەتتىن زور نېسىۋىگە) ئېرىشكەن بولاتتىم» دەيدۇ.');
INSERT INTO "saleh" VALUES(4,74,'4:74','دۇنيا تىرىكچىلىكىنى ئاخىرەتكە تېگىشىدىغانلار ئاللاھ يولىدا (جېنىنى، پۇل ـ مېلىنى پىدا قىلىپ) جىھاد قىلسۇن، كىمكى ئاللاھ يولىدا جىھاد قىلىپ ئۆلتۈرۈلسە ياكى غەلىبە قىلسا، بىز ئۇنىڭغا كاتتا ئەجىر ئاتا قىلىمىز.');
INSERT INTO "saleh" VALUES(4,75,'4:75','(ئى مۆمىنلەر!) سىلەرگە نېمە بولدىكى، ئاللاھنىڭ يولىدا جىھاد قىلمايسىلەر ۋە (دۇئا قىلىپ): «ئى پەرۋەردىگارىمىز! ئاھالىسى زالىم بۇ شەھەردىن بىزنى چىقارغىن، ئۆز دەرگاھىڭدىن بىزگە بىر ئىگىدارچىلىق قىلغۇچىنى مۇيەسسەر قىلغىن، ئۆز دەرگاھىڭدىن بىزگە بىر مەدەتكارنى مۇيەسسەر قىلغىن» دەيدىغان ئاجىز ئەرلەر، ئاجىز ئاياللار ۋە بالىلارنى قۇتقۇزۇش ئۈچۈن جىھاد قىلمايسىلەر؟.');
INSERT INTO "saleh" VALUES(4,76,'4:76','مۆمىنلەر ئاللاھنىڭ يولىدا جىھاد قىلىدۇ، كاپىرلار تاغۇتنىڭ يولىدا ئۇرۇش قىلىدۇ؛ شەيتاننىڭ دوستلىرى بىلەن ئۇرۇش قىلىڭلار (سىلەر ئۇلارنى يېڭىسىلەر)، شەيتاننىڭ تەدبىرى ھەقىقەتەن ئاجىزدۇر. ');
INSERT INTO "saleh" VALUES(4,77,'4:77','(ئى مۇھەممەد!) ئۇلارغا (يەنى مەككىدە تۇرۇپ ئۇرۇشۇشنى تەلەپ قىلغانلارغا): «(ئۇرۇشتىن) قولۇڭلارنى يىغىڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار» دېگەن كىشىلەرنى كۆرمىدىڭمۇ؟ (يەنى ئۇلاردىن ئەجەبلەنمەمسەن؟) بۇلارغا جىھاد پەرز قىلىنغان چاغدا، ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەر دۈشمەندىن گويا ئاللاھ (نىڭ ئازابى) دىن قورققاندەك بەلكى ئۇنىڭدىنمۇ بەتتەررەك قورقىدۇ. ئۇلار (ئۆلۈمدىن قورقۇپ): «پەرۋەردىگارىمىز! بىزگە نېمە ئۈچۈن جىھادنى پەرز قىلدىڭ؟ نېمە ئۈچۈن بىزنى ئۇزاققا قالماي كېلىدىغان ئەجەلگۈچە تەخىر قىلمىدىڭ؟ (يەنى ئەجىلىمىز يەتكۈچە تەخىر قىلساڭ، ئۇرۇشتا ئۆلتۈرۈلمىسەك بولماسمىدى؟)» دېدى. (ئى مۇھەممەد!) ئېيتقىنكى، «دۇنيانىڭ مەنپەئەتى ئازدۇر (يەنى دۇنيانىڭ نېمىتى پانىيدۇر)، (ئاللاھتىن) قورققان (ۋە ئۇنىڭ ئەمرىنى تۇتقانلار) ئۈچۈن ئاخىرەت ياخشىدۇر، سىلەرگە قىلچىلىك زۇلۇم قىلىنمايدۇ (يەنى قىلغان ئەمەلىڭلارنىڭ ساۋابى كېمەيتىلمەيدۇ)».');
INSERT INTO "saleh" VALUES(4,78,'4:78','سىلەر مۇستەھكەم قەلئەلەردە بولغان تەقدىردىمۇ قەيەردە بولماڭلار، (ئەجەل كەلگەندە) ئۆلۈم سىلەرنى تاپىدۇ. ئەگەر ئۇلار (يەنى مۇناپىقلار) بىرەر ياخشىلىققا ئېرىشسە، «بۇ ئاللاھتىن بولدى» دەيدۇ؛ ئەگەر ئۇلار بىرەر زىيان ـ زەخمەتكە ئۇچرىسا، «بۇ سەندىن (يەنى سېنىڭ دىنىڭغا كىرگەنلىكىمىزدىن) بولدى» دەيدۇ. (ئى مۇھەممەد! بۇ ئەخمەقلەرگە) ئېيتقىنكى، «(ياخشىلىق ۋە يامانلىقنىڭ) ھەممىسى ئاللاھ تەرىپىدىندۇر (يەنى ھەممىسىنى ئاللاھ ياراتقاندۇر)». بۇ قەۋمگە نېمە بولدىكىن، ئۇلار ھېچبىر سۆزنى (يەنى ھەممە شەيئىنىڭ ئاللاھنىڭ تەقدىرى بىلەن بولىدىغانلىقىنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(4,79,'4:79','(ئى ئىنسان!) ساڭا يەتكەن ياخشىلىق (مەرھەمەت قىلىش يۈزىسىدىن) ئاللاھ تەرىپىدىندۇر، ساڭا يەتكەن يامانلىق (مۇ قىلمىشلىرىڭ تۈپەيلىدىن) ئۆزۈڭدىندۇر. (ئى مۇھەممەد!) سېنى بىز (پۈتۈن) ئىنسانلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق، (سېنىڭ پەيغەمبەرلىكىڭگە) شاھىتلىققا ئاللاھ يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,80,'4:80','كىمكى پەيغەمبەرگە ئىتائەت قىلىدىكەن، ئۇ ئاللاھقا ئىتائەت قىلغان بولىدۇ (چۈنكى پەيغەمبەر ئاللاھنىڭ ئەمرىنى يەتكۈزىدۇ). كىمكى (ئى مۇھەممەد!) سەندىن يۈز ئۆرۈيدىكەن، (بىلگىنكى) بىز سېنى ئۇلارغا كۆزەتچى (يەنى ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ، ئەمەللىرىگە قاراپ ھېساب ئالغۇچى) قىلىپ ئەۋەتمىدۇق.');
INSERT INTO "saleh" VALUES(4,81,'4:81','ئۇلار (يەنى مۇناپىقلار): «ئەمرىڭگە ئىتائەت قىلدۇق» دەيدۇ، سېنىڭ يېنىڭدىن چىقىپ كەتكەن چاغدا، ئۇلارنىڭ بىر تۈركۈمى ئۆزلىرىنىڭ ئېيتقان سۆزلىرىنى ئۆزگەرتىۋېتىدۇ (يەنى ئەمرىڭگە خىلاپلىق قىلىدۇ)، ئاللاھ ئۇلارنىڭ ئۆزگەرتكەنلىرىنى خاتىرىلەپ تۇرىدۇ. سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى كەچۈرگىن)، ئاللاھقا تەۋەككۈل قىلغىن (تەۋەككۈل قىلغۇچىغا) ئاللاھنىڭ ھامىيلىقى يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,82,'4:82','ئۇلار قۇرئان ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئەگەر قۇرئان (مۇشرىكلار بىلەن مۇناپىقلار گۇمان قىلغاندەك) ئاللاھتىن غەيرىنىڭ تەرىپىدىن بولغان بولسا، ئەلۋەتتە ئۇنىڭدىن نۇرغۇن زىددىيەتلەرنى تاپقان بولاتتى. ');
INSERT INTO "saleh" VALUES(4,83,'4:83','(مۆمىنلەرگە ئائىت) ئامانلىقنىڭ ياكى قورقۇنچنىڭ بىرەر خەۋىرى ئۇلارغا (يەنى مۇناپىقلارغا) يەتسە، ئۇنى تارقىتىدۇ، ئەگەر ئۇنى پەيغەمبەرگە ۋە ئۇلار (يەنى مۆمىنلەر) نىڭ ئىچىدىكى ئىش ئۈستىدىكىلەرگە مەلۇم قىلسا، (شۇ) خەۋەرنى چىقارغۇچىلار ئۇنى (يەنى شۇ خەۋەرنىڭ ھەقىقىي ئەھۋالىنى) ئۇلاردىن ئەلۋەتتە بىلىۋالاتتى. (ئى مۆمىنلەر!) ئەگەر سىلەرگە ئاللاھنىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەرنىڭ ئازغىنا كىشىدىن باشقىلىرىڭلار، ئەلۋەتتە شەيتانغا ئەگىشىپ كېتەتتىڭلار.');
INSERT INTO "saleh" VALUES(4,84,'4:84','(ئى مۇھەممەد!) ئاللاھنىڭ يولىدا جىھاد قىلغىن (يەنى ئۆزۈڭ يالغۇز بولساڭمۇ جىھاد قىلغىن، ساڭا غەلىبە ۋەدە قىلىنغان. مۇناپىقلارنىڭ جىھادتىن قېلىپ قالغانلىقىغا ئەھمىيەت بېرىپ كەتمە.) سەن پەقەت ئۆزۈڭگىلا جاۋابكار سەن، مۆمىنلەرنى (جىھادقا) قىزىقتۇرغىن، ئاللاھنىڭ كاپىرلار كۈچىنى توسىدىغانلىقىمۇ ھەقىقەتتۇر. ئاللاھنىڭ كۈچى ئەڭ زوردۇر، ئاللاھنىڭ جازاسى ئەڭ قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(4,85,'4:85','كىمكى (كىشىلەر ئارىسىدا) ياخشى (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى ياخشى ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ؛ كىمكى بىر يامان (ئىش ئۈچۈن) شاپائەت قىلسا، ئۇنىڭدىن (يەنى يامان ئىشتىن) ئۇنىڭ نېسىۋىسى بولىدۇ. ئاللاھ ھەممە ئىشقا قادىردۇر.');
INSERT INTO "saleh" VALUES(4,86,'4:86','سىلەرگە بىر كىشى سالام بەرسە، ئۇنىڭغا تېخىمۇ ياخشى سالام بىلەن جاۋاب قايتۇرۇڭلار (يەنى بىر كىشى ئەسسالامۇ ئەلەيكۇم دەپ سالام بەرسە، ئۇنىڭغا ئەسسالامۇ ئەلەيكۇم ۋە رەھمەتۇللاھى ۋەبەرەكاتۇھۇ دەڭلار) ياكى ئۇنىڭ سالامىنى ئەينەن قايتۇرۇڭلار (يەنى ۋەئەلەيكۇم ئەسسالام دەڭلار). ئاللاھ ھەقىقەتەن ھەممە نەرسىدىن (يەنى بەندىلىرىنىڭ چوڭ ـ كىچىك ئىشلىرىدىن) ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,87,'4:87','ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. ئاللاھ شەكسىز بولىدىغان قىيامەت كۈنىدە سىلەرنى (ھېساب ئېلىش ئۈچۈن مەھشەرگاھقا)جەزمەن يىغىدۇ. ئاللاھتىنمۇ توغرا سۆزلۈك كىم بار؟ (يەنى سۆزىدە، ۋەدىسىدە ئاللاھتىنمۇ سادىق ھېچكىم يوق)');
INSERT INTO "saleh" VALUES(4,88,'4:88','ئى مۆمىنلەر! سىلەر نېمىشقا مۇناپىقلار توغرىسىدا ئىككى گۇرۇھقا بۆلۈنۈپ كېتىسىلەر؟ ئاللاھ ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن ئۇلارنى كاپىرلار ھۆكمىدە قىلدى، ئاللاھ ئازدۇرغان كىشىلەرنى سىلەر ھىدايەت قىلماقچى بولامسىلەر؟ كىمنىكى ئاللاھ گۇمراھ قىلىدىكەن، سەن ئۇنىڭغا ھەرگىز توغرا يول تېپىپ بېرەلمەيسەن.');
INSERT INTO "saleh" VALUES(4,89,'4:89','ئۇلار سىلەرنىڭ ئۆزلىرىدەك كاپىر بولۇشۇڭلارنى، شۇنىڭ بىلەن ئۆزلىرىگە ئوخشاش بولۇشۇڭلارنى ئۈمىد قىلىدۇ، ئۇلار ئاللاھ يولىدا ھىجرەت قىلمىغىچە (يەنى ھىجرەت قىلىش ئارقىلىق ئىمانىنى ئىسپات قىلمىغىچە) ئۇلارنى دوست تۇتماڭلار. ئەگەر ئۇلار ئاللاھ يولىدا ھىجرەت قىلىشتىن يۈز ئۆرۈسە، ئۇلارنى قەيەردە تاپساڭلار شۇ يەردە تۇتۇپ ئۆلتۈرۈڭلار، ئۇلارنى دوستمۇ تۇتماڭلار، ياردەمچىمۇ قىلماڭلار.');
INSERT INTO "saleh" VALUES(4,90,'4:90','پەقەت سىلەر بىلەن ئۆزلىرى ئارىسىدا ئەھدە بولغان بىر قەۋمگە سىغىنغانلار ياكى سىلەرگە قارشى ئۇرۇش قىلىشتىن ياكى ئۆز قەۋمىگە قارشى ئۇرۇش قىلىشتىن يۈرەكلىرى سىقىلىپ (يەنى سىلەر تەرەپمۇ ئەمەس، ئۆز قەۋمى تەرەپمۇ ئەمەس بولۇپ) سىلەرگە كەلگەنلەر بۇنىڭدىن مۇستەسنا (يەنى مۇنداقلارنى ئۆلتۈرمەڭلار ۋە ئەسىر ئالماڭلار). ئەگەر ئاللاھ خالىسا، ئۇلارنى سىلەرگە ھۆكۈمران قىلاتتى. ئۇلار، ئەلۋەتتە سىلەر بىلەن ئۇرۇشاتتى (يەنى ئاللاھ ئۇلارنى كۈچلۈك قىلىپ سىلەرگە قارشى ئۇرۇش قىلىشقا جۈرئەتلىك قىلاتتى)، ئەگەر ئۇلار سىلەردىن يىراق تۇرسا (يەنى سىلەرگە چېقىلمىسا)، سىلەر بىلەن ئۇرۇشمىسا ۋە سىلەرگە تەسلىم بولسا، ئاللاھ سىلەرنىڭ ئۇلارغا ھۇجۇم قىلىشىڭلارغا ھەرگىز رۇخسەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(4,91,'4:91','ئىمانىنى ئىزھار قىلىش بىلەن سىلەردىنمۇ ئامانلىققا ئېرىشمەكچى ۋە قەۋمىگە قايتقاندا (كۇفرىنى ئىزھار قىلىش بىلەن) ئۆز قەۋمىدىنمۇ ئامانلىققا ئېرىشمەكچى بولغان ئىككىنچى بىر خىل كىشىلەر (يەنى مۇناپىقلار) نى كۆرىسىلەر، ئۇلار ھەر قاچان پىتنە ـ پاساتقا (يەنى كۇفرىغا ياكى مۇسۇلمانلار بىلەن ئۇرۇش قىلىشقا) چاقىرىلسا ئۆزلىرىنى ئۇنىڭدىن تارتىدۇ، ئەگەر ئۇلار سىلەردىن يىراق تۇرمىسا، سىلەرگە تەسلىم بولمىسا، (سىلەر بىلەن) ئۇرۇشۇشتىن قول يىغمىسا، بۇ چاغدا ئۇلارنى قەيەردە كۆرسەڭلار شۇ يەردە (ئەسىر ئېلىڭلار) تۇتۇپ ئۆلتۈرۈڭلار، سىلەرگە (ئۇلارنىڭ خىيانىتى سەۋەبلىك) ئۇلارغا قارشى (ئەسىر ئېلىشقا، ئۆلتۈرۈشكە) روشەن پاكىت ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(4,92,'4:92','مۆمىن ئادەمنىڭ مۆمىن ئادەمنى سەۋەنلىك بىلەن ئەمەس، قەستەن ئۆلتۈرۈشى ھېچ دۇرۇس ئەمەس. كىمكى بىرەر مۆمىننى سەۋەنلىك بىلەن ئۆلتۈرۈپ قويىدىكەن، ئۇ بىر مۆمىن قۇلنى ئازاد قىلىشى ۋە ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرىغا دىيەت (يەنى تۆلەم) تۆلىشى كېرەك، ئۇلار (يەنى ئۆلتۈرۈلگۈچىنىڭ ۋارىسلىرى) دىيەتنى كەچۈرۈم قىلسا، (شۇنىڭ بىلەن قاتىل ئۇنى) تۆلىمىسە ھېچ گۇناھ بولمايدۇ. ئەگەر ئۆلتۈرۈلگۈچى مۆمىن سىلەرگە دۈشمەن (كۇففار) قەۋمدىن بولسا، بۇ چاغدا ئۆلتۈرۈپ قويغۇچى بىر نەپەر مۆمىن قۇلنى ئازاد قىلىشى لازىم (دىيەت كەلمەيدۇ، چۈنكى ئۆلتۈرۈلگۈچىنىڭ ئىگىسى مۇسۇلمان بولمىغاچقا ئۇنىڭغا ۋارىس بولمايدۇ). ئەگەر ئۆلتۈرۈلگۈچى ئاراڭلاردا مۇئاھىدە بولغان (كۇففار) قەۋمدىن بولسا، ئۇنىڭ ۋارىسلىرىغا دىيەت تۆلەش ۋە بىر نەپەر قۇلنى ئازاد قىلىش كېرەك. ئازاد قىلىدىغان قۇل تاپالمىغان ئادەم (يەنى قۇل ئازاد قىلىشقا قادىر بولالمىغان ئادەم) (تەۋبىسىنى ئاللاھنىڭ قوبۇل قىلىشى ئۈچۈن) ئۈزۈلدۈرمەي ئىككى ئاي روزا تۇتۇشى لازىم. ئاللاھ ھەممىنى بىلىپ تۇرغۇچى، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,93,'4:93','كىمكى بىر مۆمىننى قەستەن ئۆلتۈرىدىكەن، ئۇنىڭ جازاسى جەھەننەم بولىدۇ، ئۇ جەھەننەمدە مەڭگۈ قالىدۇ، ئاللاھنىڭ غەزىپىگە ۋە لەنىتىگە دۇچار بولىدۇ. ئاللاھ ئۇنىڭغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلايدۇ.');
INSERT INTO "saleh" VALUES(4,94,'4:94','ئى مۆمىنلەر! ئاللاھ يولىغا (يەنى غازاتقا) ئاتلانغان ۋاقتىڭلاردا ھەقىقەتلەپ ئىش قىلىڭلار (يەنى مۆمىن ياكى كاپىرلىقى ئېنىق بولغىچە ئۆلتۈرۈشكە ئالدىراپ كەتمەڭلار)، سىلەرگە مۇسۇلمان بولغانلىقىنى بىلدۈرۈپ سالام بەرگەن ئادەمگە ـ دۇنيانىڭ مال ـ مۈلكىنى (غەنىيمەتنى) كۆزلەپ ـ سەن مۆمىن ئەمەسسەن دېمەڭلار (يەنى ئۆلۈشتىن قورقۇپ سالام بەردىڭ دەپ ئۇنى ئۆلتۈرۈۋەتمەڭلار). ئاللاھنىڭ دەرگاھىدا (مۇنداقلارنى ئۆلتۈرۈشتىن سىلەرنى بىھاجەت قىلىدىغان) نۇرغۇن غەنىيمەتلەر (يەنى ساۋاب) بار. ئىلگىرى سىلەرمۇ ئەنە شۇنداق (كاپىر) ئىدىڭلار، كېيىن ئاللاھ سىلەرگە (ئىماننى) ئىلتىپات قىلدى، (ئۇلارنى ئۆزۈڭلارغا سېلىشتۇرۇپ) ھەقىقەتلەپ ئىش قىلىڭلار. ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(4,95,'4:95','مۆمىنلەردىن ئۆزرىسىز (ئەما، توكۇر، كېسەلگە ئوخشاش ئۆزرىسى بارلار بۇنىڭدىن مۇستەسنا) جىھادقا چىقمىغانلار ئاللاھ يولىدا ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلار بىلەن باراۋەر بولمايدۇ. ئاللاھ ماللىرىنى، جانلىرىنى تىكىپ جىھاد قىلغۇچىلارنى جىھادقا چىقمىغانلاردىن بىر دەرىجە ئۈستۈن قىلدى. بۇ ئىككى خىل كىشىلەر (يەنى ئۆزرىسى بولۇپ جىھادقا چىقالمىغانلار ۋە جىھادقا چىققۇچىلار) نىڭ ھەممىسىگە ئاللاھ جەننەتنى ۋەدە قىلدى. ئاللاھ جىھاد قىلغۇچىلارغا بۈيۈك ئەجىر ئاتا قىلىپ، ئۇلارنى (ئۆزرىسىز تۇرۇپ جىھادقا چىقمىغانلاردىن) ئارتۇق قىلدى.');
INSERT INTO "saleh" VALUES(4,96,'4:96','ئاللاھ (ئۇلارغا) ئۈستۈن مەرتىۋىلەر، مەغپىرەت ۋە رەھمەت ئاتا قىلدى، ئاللاھ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,97,'4:97','ئۆزلىرىگە زۇلۇم قىلغۇچىلار (يەنى ھىجرەتنى تەرك ئېتىپ كۇففارلار بىلەن بىللە تۇرغۇچىلار) نىڭ جانلىرى پەرىشتىلەر تەرىپىدىن ئېلىنىدىغان چاغدا، پەرىشتىلەر ئۇلاردىن: «(دىنىڭلارنىڭ ئىشىدا) قايسى ھالەتتە ئىدىڭلار؟» دەپ سورايدۇ. ئۇلار: «زېمىندا (يەنى مەككە زېمىنىدا) بىز (دىننى بەرپا قىلىشتىن) بوزەك قىلىنغان ئىدۇق» دەيدۇ. پەرىشتىلەر: «ھىجرەت قىلساڭلار ئاللاھنىڭ زېمىنى كەڭرى ئەمەسمىدى؟» دەيدۇ. ئەنە شۇ (ھىجرەت قىلمىغان) لارنىڭ بارىدىغان جايى جەھەننەمدۇر. جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(4,98,'4:98',' پەقەت ئەرلەردىن، ئاياللاردىن، بالىلاردىن چارىسىز قالغان (يەنى ھىجرەت قىلىشقا چارە تاپالمىغان)، يول بىلمەيدىغان ئاجىزلار بۇنىڭدىن مۇستەسنا.');
INSERT INTO "saleh" VALUES(4,99,'4:99','ئەنە شۇلارنى ئاللاھ ئەپۇ قىلغاي. ئاللاھ ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,100,'4:100','كىمكى ئاللاھ يولىدا ھىجرەت قىلىدىكەن، ئۇ زېمىندا چىقىش يولى ۋە كەڭچىلىك تاپىدۇ. كىمكى ئۆيىدىن ئاللاھنىڭ ۋە پەيغەمبەرنىڭ تەرىپىگە ھىجرەت قىلىپ چىقسا، ئاندىن يولدا ئۆلسە، ئۇنىڭ ئەجرىنى چوقۇم ئاللاھ بېرىدۇ. ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,101,'4:101','سەپەرگە چىققان ۋاقتىڭلاردا كاپىرلارنىڭ ئۆزۈڭلارغا زىيان ـ زەخمەت يەتكۈزۈپ قويۇشىدىن قورقۇپ، نامازنى قەسرى قىلىپ (يەنى تۆت رەكئەتنى ئىككى رەكئەت قىلىپ) ئوقۇساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. كاپىرلار سىلەرگە ھەقىقەتەن ئوپئوچۇق دۈشمەندۇر (ناماز ئوقۇۋاتقان پۇرسەتتىن پايدىلىنىپ سىلەرنى ئۆلتۈرۈۋېتىشتىن يانمايدۇ).');
INSERT INTO "saleh" VALUES(4,102,'4:102',' (ئى مۇھەممەد!) سەن مۆمىنلەر بىلەن (جىھادتا) بىللە بولۇپ ئۇلارغا ناماز ئوقۇپ بەرمەكچى بولغىنىڭدا، ئۇلارنىڭ بىر پىرقىسى سەن بىلەن بىرلىكتە نامازغا تۇرسۇن، قوراللىرىنى (ئېھتىيات يۈزىسىدىن) يېنىدا تۇتسۇن، سەجدىگە بېرىپ بولغاندىن كېيىن ئۇلار ئارقاڭلارغا ئۆتۈپ تۇرسۇن (يەنى ناماز ئوقۇپ بولغان پىرقە ئارقاڭلاردا كۆزەتچىلىك قىلىپ تۇرسۇن، ئۇلارنىڭ ئورنىغا) ناماز ئوقۇمىغان ئىككىنچى بىر پىرقە كېلىپ سەن بىلەن بىرلىكتە (يەنى ئارقاڭدا) ناماز ئوقۇسۇن، (دۈشمەنلىرىدىن) ئېھتىياتچانلىق بىلەن ھوشيار تۇرسۇن. (دۈشمەنلىرى بىلەن ئۇرۇشۇشقا تەييار تۇرۇش يۈزىسىدىن) قوراللىرىنى يېنىدا تۇتسۇن، كاپىرلار سىلەرنىڭ قوراللىرىڭلاردىن، ئەشيالىرىڭلاردىن غەپلەتتە قېلىشىڭلارنى، شۇنىڭ بىلەن سىلەرگە بىردىنلا (تۇيۇقسىزدىن) ھۇجۇم قىلىشنى ئارزۇ قىلىدۇ (يەنى دۈشمەنلەرنىڭ سىلەرگە ھۇجۇم قىلىش پۇرسىتىگە ئىگە بولالماسلىقى ئۈچۈن، ھەممىڭلار بىرلا ۋاقىتتا يىغىلىپ ناماز ئوقۇماي، يۇقىرىقى ئۇسۇل بويىچە ئوقۇڭلار). ئەگەر سىلەرگە يامغۇر سەۋەبلىك ھەرەج بولىدىغان بولسا ياكى كېسەل بولساڭلار، (بۇ چاغدا) قوراللىرىڭلارنى قويۇپ قويساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ. ئېھتىياتچان ۋە ھوشيار بولۇڭلار، ئاللاھ ھەقىقەتەن كاپىرلار ئۈچۈن خورلىغۇچى ئازاب تەييارلىدى.');
INSERT INTO "saleh" VALUES(4,103,'4:103',' نامازنى تۈگەتكىنىڭلاردا ئۆرە تۇرغان، ئولتۇرغان ۋە ياتقان ھالەتتە ئاللاھنى ياد ئېتىڭلار (ئاللاھ سىلەرگە دۈشمىنىڭلارغا قارشى ياردەم بېرىشى مۇمكىن). خاتىرجەم بولغان چېغىڭلاردا نامازنى (پۈتۈن شەرتلىرى بىلەن) مۇكەممەل ئادا قىلىڭلار. شۈبھىسىزكى، ناماز مۆمىنلەرگە ۋاقتى بەلگىلەنگەن (پەرز) پەرز قىلىندى.');
INSERT INTO "saleh" VALUES(4,104,'4:104','دۈشمەننىڭ ئارقىسىدىن قوغلاشقا سۇسلۇق قىلماڭلار؛ ئەگەر سىلەر قىينالساڭلار، ئۇلارمۇ خۇددى سىلەرگە ئوخشاش قىينىلىدۇ، سىلەر ئۇلار ئۈمىد قىلمايدىغان نەرسىنى (يەنى شېھىت بولۇشنى، ساۋابنى ۋە غەلىبىنى) ئاللاھتىن ئۈمىد قىلىسىلەر. ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,105,'4:105','(ئى مۇھەممەد!) سېنى كىشىلەر ئارىسىدا ئاللاھنىڭ كۆرسەتكىنى بويىچە ھۆكۈم قىلسۇن دەپ، ساڭا ھەقىقەتەن ھەق كىتابنى نازىل قىلدۇق. خائىنلارنىڭ تەرىپىنى ئالمىغىن.');
INSERT INTO "saleh" VALUES(4,106,'4:106','ئاللاھتىن مەغپىرەت تىلىگىن. ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر');
INSERT INTO "saleh" VALUES(4,107,'4:107','. (گۇناھ قىلىش بىلەن) ئۆزلىرىگە خىيانەت قىلغۇچىلارنىڭ تەرىپىنى ئالمىغىن. خىيانەتكە ئادەتلەنگەن، گۇناھقا چۆمگەن كىشىنى ئاللاھ ھەقىقەتەن دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(4,108,'4:108','ئۇلار (خىيانەتلىرىنى) ئىنسانلاردىن (خىجىل بولۇپ) يوشۇرىدۇ، ئاللاھتىن (خىجىل بولۇپ) يوشۇرمايدۇ؛ ئۇلار ئاللاھ رازى بولمايدىغان سۆزنى (يەنى بوھتان چاپلاش، يالغان گۇۋاھلىق بېرىش ۋە يالغان قەسەمنى) پىلانلىغان چاغدا، ئاللاھ ئۇلار بىلەن بىللە ئىدى (يەنى ئۇلارنىڭ ئەھۋالىنى بىلىپ تۇراتتى، سۆزىنى ئاڭلاپ تۇراتتى). ئۇلارنىڭ قىلمىشلىرىنى ئاللاھ تامامەن بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(4,109,'4:109',' سىلەر شۇنداق كىشىلەركى، ھاياتى دۇنيادا ئۇلارنىڭ (يەنى ئوغرى ۋە خائىنلارنىڭ) تەرىپىنى ئالدىڭلار، قىيامەت كۈنى كىم ئاللاھقا قارشى ئۇلارنىڭ تەرىپىنى ئالىدۇ؟ (يەنى ئاللاھنىڭ ئازابىنى كىم ئۇلاردىن دەپئى قىلىدۇ؟) ياكى كىم ئۇلارغا ھامىي بولىدۇ؟');
INSERT INTO "saleh" VALUES(4,110,'4:110','كىمكى بىرەر يامانلىق ياكى ئۆزىگە بىرەر زۇلۇم قىلىپ قويۇپ، ئاندىن ئاللاھتىن مەغپىرەت تەلەپ قىلسا، ئۇ ئاللاھنىڭ مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان ئىكەنلىكىنى كۆرىدۇ.');
INSERT INTO "saleh" VALUES(4,111,'4:111',' كىمكى (قەستەن) بىرەر گۇناھ قىلسا، ئۇنى ئۆزىنىڭ زىيىنىغا قىلىدۇ (يەنى ئۇنىڭ ۋابالىنى ئۆزى تارتىدۇ). ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,112,'4:112','كىمكى كىچىك بىر گۇناھ ياكى چوڭ بىر گۇناھ قىلىپ قويۇپ، ئۇنى بىگۇناھ ئادەمگە چاپلىسا، ئۇ شۇ بوھتاننى ۋە ئوپئوچۇق بىر گۇناھنى ئۆزىگە يۈكلىۋالغان بولىدۇ. ');
INSERT INTO "saleh" VALUES(4,113,'4:113',' (ئى مۇھەممەد!) ئەگەر ساڭا ئاللاھنىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى، ئۇلاردىن بىر گۇرۇھ ئادەم چوقۇم سېنى ئازدۇرۇشنى قەستلەيتتى؛ ئۇلار پەقەت ئۆزلىرىنى ئازدۇرىدۇ، ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ. ئاللاھ ساڭا كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى سۈننەتنى) نازىل قىلدى، ساڭا سەن بىلمىگەن نەرسىلەرنى (يەنى شەرىئەت ئەھكاملىرىنى ۋە غەيب ئىشلارنى) بىلدۈردى. ئاللاھنىڭ ساڭا پەزلى چوڭدۇر. ');
INSERT INTO "saleh" VALUES(4,114,'4:114',' ئۇلارنىڭ يوشۇرۇن سۆھبىتىنىڭ تولىسىدا خەيرىيەت يوقتۇر. پەقەت سەدىقىغە ياكى ياخشىلىققا ياكى كىشىلەرنى ئەپلەشتۈرۈشكە ئەمىر قىلغان كىشىلەر (نىڭ يوشۇرۇن سۆھبىتى) بۇنىڭدىن مۇستەسنا. كىمكى ئۇ (ئىش) لارنى ئاللاھنىڭ رازىلىقىنى تىلەش يۈزىسىدىن قىلىدىكەن، ئۇنىڭغا بۈيۈك ئەجىر ئاتا قىلىمىز.');
INSERT INTO "saleh" VALUES(4,115,'4:115',' كىمكى ئۆزىگە (مۆجىزىلەر ئارقىلىق) توغرا يول ئېنىق بولغاندىن كېيىن، پەيغەمبەرگە (يەنى ئۇنىڭ ئەمرىگە) مۇخالىپەتچىلىك قىلىدىكەن، مۆمىنلەرنىڭ يولىدىن غەيرىيگە ئەگىشىدىكەن، ئۇنى ئۆز يولىغا قويۇپ بېرىمىز، ئۇنى (ئاخىرەتتە) جەھەننەمگە كىرگۈزىمىز، جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(4,116,'4:116',' ئاللاھ ئۆزىگە بىرەر نەرسىنىڭ شېرىك كەلتۈرۈلۈشىنى (يەنى مۇشرىكلىك گۇناھىنى) ئەلۋەتتە مەغپىرەت قىلمايدۇ، بۇنىڭدىن باشقىنى (ئاللاھ) خالىغان ئادەمگە مەغپىرەت قىلىدۇ. كىمكى ئاللاھقا شېرىك كەلتۈرىدىكەن، ئۇ ھەقىقەتەن قاتتىق ئازغان بولىدۇ.');
INSERT INTO "saleh" VALUES(4,117,'4:117',' ئۇلار (يەنى مۇشرىكلار) ئاللاھنى قويۇپ، پەقەت (ئۆزلىرى) چىشى (ناملار بىلەن ئاتىۋالغان) بۇتلارغا ئىبادەت قىلىدۇ، پەقەت ئاللاھنىڭ ئىبادىتىدىن باش تارتقان شەيتانغا ئىبادەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(4,118,'4:118',' ئاللاھ شەيتاننى رەھمىتىدىن يىراق قىلدى، شەيتان ئېيتتى: «مەن ئەلۋەتتە سېنىڭ بەندىلىرىڭدىن مۇئەييەن ساندىكىلەرنى ئىگىلەيمەن.');
INSERT INTO "saleh" VALUES(4,119,'4:119','شۇنداقلا چوقۇم ئۇلارنى ئازدۇرىمەن، ئۇلارنى خام خىيال قىلدۇرىمەن (يەنى ھايات ئۇزۇن، قىيامەت، ھېساب ئېلىش يوق دېگەندەك باتىل ئەقىدىلەرنى كۆڭلىگە سالىمەن)، ئۇلارنى چوقۇم چاھارپايلارنىڭ قۇلاقلىرىنى يېرىشقا بۇيرۇيمەن، ئۇلارنى چوقۇم ئاللاھنىڭ مەخلۇقاتىنى ئۆزگەرتىۋېتىشكە بۇيرۇيمەن». كىمكى ئاللاھنى قويۇپ شەيتاننى دوست تۇتىدىكەن، ئۇ (دۇنيا ۋە ئاخىرەتتە) ئوپئوچۇق زىيان تارتقان بولىدۇ. ');
INSERT INTO "saleh" VALUES(4,120,'4:120','شەيتان ئۇلارغا (يالغان) ۋەدىلەرنى بېرىدۇ ۋە ئۇلارنى خام خىيالغا سالىدۇ (يەنى ئەمەلدە ئىشقا ئاشمايدىغان ئارزۇلارنى كۆڭلىگە سالىدۇ)، شەيتان ئۇلارغا پەقەت يالغاننىلا ۋەدە قىلىدۇ.');
INSERT INTO "saleh" VALUES(4,121,'4:121','ئەنە شۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر، ئۇلار جەھەننەمدىن قاچىدىغان جاي تاپالمايدۇ');
INSERT INTO "saleh" VALUES(4,122,'4:122','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمىز. ئۇلار جەننەتتە مەڭگۈ قالىدۇ. ئاللاھنىڭ ۋەدىسى ھەقتۇر، ئاللاھتىنمۇ راست سۆزلۈك كىم بار؟ (يەنى ئاللاھتىنمۇ راست سۆزلۈك ھېچ ئەھەدى بولمايدۇ).');
INSERT INTO "saleh" VALUES(4,123,'4:123',' (ئاللاھ ۋەدە قىلغان ساۋاب) سىلەرنىڭ قۇرۇق ئارزۇيۇڭلار ۋە ئەھلى كىتابنىڭ قۇرۇق ئارزۇسى بىلەن قولغا كەلمەيدۇ. كىمكى بىر يامانلىق قىلىدىكەن، بۇنىڭ ئۈچۈن (دۇنيادا ياكى ئاخىرەتتە) جازالىنىدۇ، ئۇ ئۆزى ئۈچۈن ئاللاھتىن باشقا (ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(4,124,'4:124',' ئەر ـ ئاياللاردىن مۆمىن بولۇپ تۇرۇپ ياخشى ئىشلارنى قىلغانلار جەننەتكە داخىل بولىدۇ، ئۇلارغا قىلچە زۇلۇم قىلىنمايدۇ (يەنى ئۇلارنىڭ قىلغان ئەمەللىرىنىڭ ساۋابى قىلچە كېمەيتىۋېتىلمەيدۇ).');
INSERT INTO "saleh" VALUES(4,125,'4:125',' ياخشى ئەمەللەرنى قىلغان ھالدا ئۆزىنى ئاللاھقا تاپشۇرغان (يەنى ئاللاھنىڭ ئەمرىگە بويسۇنۇپ ئەمەلىنى خالىس ئاللاھ ئۈچۈن قىلغان)، باتىل دىنلاردىن بۇرالغان ھالدا ئىبراھىمنىڭ دىنىغا ئەگەشكەن كىشىدىن دىنىي جەھەتتە ياخشىراق ئادەم بارمۇ؟ ئاللاھ ئىبراھىمنى دوست تۇتتى.');
INSERT INTO "saleh" VALUES(4,126,'4:126','ئاسمانلاردىكى، زېمىندىكى شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، بەندىلىرىدۇر، مەخلۇقاتلىرىدۇر). ئاللاھ ھەممە نەرسىنى تولۇق بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(4,127,'4:127',' (ئى مۇھەممەد!) سەندىن ئاياللار توغرىسىدا پەتىۋا سورايدۇ، ئېيتقىنكى، ئۇلار توغرىسىدا ئاللاھ سىلەرگە پەتىۋا بېرىدۇ. قۇرئاندا سىلەرگە تىلاۋەت قىلىنىدىغان (ئاياللارنىڭ مىراسىغا ئائىت) ئايەتلەرمۇ پەتىۋا بېرىدۇ. شۇنداق يېتىملەر توغرىسىدا پەتىۋا بېرىدۇكى، سىلەر ئۇلارنىڭ تەيىن قىلىنغان مىراسىنى بەرمەيسىلەر، (ئۇلارنىڭ مىراسى قولىمىزدىن چىقىپ كېتىپ قالىدۇ-دەپ) ئۇلارنى ئەرگە بەرمەيسىلەر (يەنى مۇنداق قىلماسلىقىڭلارغا پەتىۋا بېرىدۇ)، بوزەك قىلىنغان بالىلار(نىڭ ھەققىنى بېرىشىڭلار) ۋە يېتىملەرگە ھەققانىي بولۇشۇڭلار ھەققىدە پەتىۋا بېرىدۇ. (ئاياللارغا ۋە يېتىملەرگە) قانداقلا ياخشىلىق قىلساڭلار، شۈبھىسىزكى، ئاللاھ ئۇنى بىلىپ تۇرىدۇ (يەنى شۇنىڭغا ئاساسەن ئاللاھ سىلەرنى مۇكاپاتلايدۇ).');
INSERT INTO "saleh" VALUES(4,128,'4:128','ئەگەر بىرەر ئايال ئېرىنىڭ كۆڭۈلسىز بولۇشىدىن ياكى يۈز ئۆرۈشىدىن ئەندىشە قىلسا، ئۇلارنىڭ ئۆزئارا كېلىشىشى ھېچ گۇناھ ئەمەس، كېلىشىش (ئۈزلۈشۈپ كېتىشتىن) ياخشى. ئىنسانلارنىڭ تەبىئىتىگە بېخىللىق سىڭىپ كەتكەن، ئەگەر (ئاياللىرىڭلارغا) ياخشىلىق قىلساڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار (ئاللاھ سىلەرنى مۇكاپاتلايدۇ). ئاللاھ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(4,129,'4:129','قانچە تىرىشساڭلارمۇ ئاياللىرىڭلارغا (مۇھەببەتتە ۋە دىلنىڭ مايىللىقىدا) باراۋەر مۇئامىلىدە بولۇشقا ھەرگىز قادىر بولالمايسىلەر. شۇنىڭ ئۈچۈن بىرسىگە پۈتۈنلەي مايىل بولۇپ، ئىككىنچىسىنى (ئېرى باردەكمۇ ئەمەس، يوقتەكمۇ ئەمەس) ئېسىپ قويۇلغان ئايالدەك تاشلاپ قويماڭلار، ئەگەر (بۇ جەھەتتىكى قۇسۇرلىرىڭلارنى) تۈزەتسەڭلار، (ئۇلارغا زۇلۇم قىلىشتىن) ساقلانساڭلار، ئاللاھ ئەلۋەتتە (سىلەرگە) مەغپىرەت قىلىدۇ، رەھىم قىلىدۇ.');
INSERT INTO "saleh" VALUES(4,130,'4:130','ئەگەر ئىككىسى ئۈزلۈشۈپ كەتسە، ئاللاھ ئۆز پەزلى بىلەن ئۇلارنىڭ بىرسىنى ئىككىنچىسىدىن بىھاجەت قىلىدۇ، ئاللاھ (نىڭ پەزلى) كەڭدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,131,'4:131','ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، مەخلۇقاتىدۇر). سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا) ۋە سىلەرگە ئاللاھقا تەقۋادارلىق قىلىشنى تەۋسىيە قىلدۇق. ئەگەر كاپىر بولساڭلار (كۇفرىڭلار ئاللاھقا زىيان يەتكۈزەلمەيدۇ). ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر ئاللاھنىڭ (مۈلكى ۋە مەخلۇقاتى)دۇر. ئاللاھ (مەخلۇقاتتىن) بىھاجەتتۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(4,132,'4:132',' ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەر ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، مەخلۇقاتىدۇر). ھەممە ئىشنى باشقۇرۇشقا ئاللاھ يېتەرلىكتۇر. ');
INSERT INTO "saleh" VALUES(4,133,'4:133','ئى ئىنسانلار! ئەگەر ئاللاھ خالىسا، سىلەرنى يوق قىلىپ ئورنۇڭلارغا باشقىلارنى كەلتۈرىدۇ، ئاللاھ بۇنىڭغا قادىردۇر');
INSERT INTO "saleh" VALUES(4,134,'4:134','كىمكى (قىلغان ئەمەلى بىلەن) دۇنيانىڭ نېمىتىنى تىلەيدىكەن، (ئۇنىڭغا ئېيتىپ قويغىنكى) دۇنيا ۋە ئاخىرەتنىڭ نېمىتى ئاللاھنىڭ دەرگاھىدىدۇر (بەندە ئۆز پەرۋەردىگارىدىن دۇنيا ۋە ئاخىرەتنىڭ نېمىتىنى تىلىسۇن). ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,135,'4:135','ئى مۆمىنلەر! خۇدالىق ئۈچۈن گۇۋاھلىق بېرىشتە، ئۆزۈڭلارنىڭ ياكى ئاتا ـ ئاناڭلارنىڭ ياكى تۇغقانلىرىڭلارنىڭ زىيىنىغا (گۇۋاھلىق بېرىشكە) توغرا كەلگەن تەقدىردىمۇ، ئادالەتنى بەرپا قىلىشقا تىرىشىڭلار. (گۇۋاھلىق بېرىلگۈچى) باي بولسا (ئۇنىڭغا بولۇشماستىن)، ياكى پېقىر بولسا (ئۇنىڭغا ئىچ ئاغرىتماستىن) ھامان ئادىل گۇۋاھ بولۇڭلار. ئاللاھ ئۇلارغا سىلەردىن يېقىندۇر (يەنى ئۇلارنىڭ مەنپەئەتىنىڭ نېمىدە بولىدىغانلىقىنى ئوبدان بىلىدۇ)، نەپسى خاھىشىڭلارغا ئەگىشىپ (ھەقىقەتتىن) بۇرۇلۇپ كەتمەڭلار. ئەگەر تىلىڭلارنى تولغۇساڭلار (يەنى گۇۋاھلىقتىكى پاكىتنى بۇرمىلىساڭلار) ياكى گۇۋاھلىقتىن باش تارتساڭلار، بىلىڭلاركى ئاللاھ ھەقىقەتەن سىلەرنىڭ قىلمىشىڭلاردىن خەۋەردار بولۇپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,136,'4:136',' ئى مۆمىنلەر! ئاللاھقا، ئاللاھنىڭ پەيغەمبىرىگە ۋە ئاللاھ ئۇنىڭغا نازىل قىلغان كىتابقا (يەنى قۇرئانغا) ۋە ئىلگىرى ئاللاھ نازىل قىلغان كىتابلارغا (يەنى قۇرئاندىن ئىلگىرى نازىل قىلىنغان ساماۋى كىتابلارغا) ئىمان كەلتۈرۈڭلار. كىمكى ئاللاھنى، ئاللاھنىڭ پەرىشتىلىرىنى، كىتابلىرىنى، پەيغەمبەرلىرىنى ۋە ئاخىرەت كۈنىنى ئىنكار قىلىدىكەن، ئۇ قاتتىق ئازغان بولىدۇ.');
INSERT INTO "saleh" VALUES(4,137,'4:137',' ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن ئىمان ئېيتقان، ئاندىن كاپىر بولغان، ئاندىن كۇفرىدا ئەزۋەيلىگەن (يەنى شۇ كۇفرى بىلەن ئۆلگەن) كىشىلەرنى ئاللاھ مەغپىرەت قىلمايدۇ ۋە ئۇلارنى توغرا يولغا (يەنى جەننەتكە) يېتەكلىمەيدۇ.');
INSERT INTO "saleh" VALUES(4,138,'4:138',' (ئى مۇھەممەد!) مۇناپىقلارغا بېشارەت بەرگىنكى، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(4,139,'4:139',' ئۇلار مۆمىنلەرنى قويۇپ، كاپىرلارنى ۋەلى قىلىۋالىدۇ، ئۇلار كاپىرلارنىڭ قېشىدىن ئىززەت ۋە قۇدرەت تەلەپ قىلامدۇ؟ (يەنى كۇففارلارنىڭ دوستلۇقىدىن ئىززەت ۋە غەلىبە تىلەمدۇ؟) ئىززەت ۋە قۇدرەتنىڭ ھەممىسى ئاللاھقا خاستۇر.');
INSERT INTO "saleh" VALUES(4,140,'4:140','ئاللاھ كىتابتا (يەنى قۇرئاندا) سىلەرگە نازىل قىلدىكى، سىلەر ئاللاھنىڭ ئايەتلىرىنىڭ ئىنكار قىلىنىۋاتقان ۋە مەسخىرە قىلىنىۋاتقانلىقىنى ئاڭلىغان چېغىڭلاردا، ئۇلار (يەنى ئاللاھنىڭ ئايەتلىرىنى مەسخىرە قىلىۋاتقان كاپىرلار) باشقا پاراڭغا كىرىشمىگۈچە ئۇلار بىلەن بىللە ئولتۇرماڭلار. بولمىسا (گۇناھتا) ئۇلارغا ئوخشاش بولۇپ قالىسىلەر، ئاللاھ ھەقىقەتەن مۇناپىقلارنىڭ ۋە كاپىرلارنىڭ ھەممىسىنى (ئاخىرەتتە) جەھەننەمگە توپلايدۇ.');
INSERT INTO "saleh" VALUES(4,141,'4:141',' ئەگەر كاپىرلار زەپەر تاپسا، ئۇلار (كاپىرلارغا): «بىز سىلەردىن غالىب بولۇپ تۇرۇپ (سىلەرنى ئۆلتۈرمىدۇق) ئەمەسمۇ؟ سىلەرنى مۆمىنلەردىن ساقلىمىدۇقمۇ؟» دەيدۇ. ئاللاھ قىيامەت كۈنى ئاراڭلاردا (ھەققانىي) ھۆكۈم چىقىرىدۇ، ئاللاھ كاپىرلارغا مۆمىنلەرگە قارشى ھەرگىز يول بەرمەيدۇ.');
INSERT INTO "saleh" VALUES(4,142,'4:142','ئۇلار (يەنى مۇناپىقلار) سىلەرگە ھادىسە كېلىشىنى كۈتىدۇ، ئەگەر سىلەر ئاللاھنىڭ نۇسرىتىگە ئېرىشسەڭلار، ئۇلار: « بىز سىلەر بىلەن بىللە ئەمەسمىدۇق؟ (كاپىرلاردىن ئالغان غەنىيمەتلەردىن بىزگىمۇ شۈبھىسىزكى، مۇناپىقلار ئاللاھنى ئالدىماقچى بولۇشىدۇ، ئاللاھ ئۇلارنىڭ ئالدامچىلىقىغا يارىشا جازا بېرىدۇ، ئۇلار ناماز ئۈچۈن تۇرغاندا، خۇش ياقماسلىق بىلەن تۇرىدۇ (ساۋاب ئۈمىد قىلمايدۇ)، (نامازنى) كىشىلەرگە كۆرسىتىش ئۈچۈن ئوقۇيدۇ (يەنى رىياكارلىق قىلىدۇ)، ئاللاھنى پەقەت ئازغىنا ياد ئېتىدۇ');
INSERT INTO "saleh" VALUES(4,143,'4:143','مۇناپىقلار كۇفرى بىلەن ئىمان ئارىسىدا ئارىسالدى بولۇپ تۇرىدۇ، نە ئۇلارغا، نە بۇلارغا مەنسۈپ ئەمەس (يەنى مۆمىنلەرگىمۇ، كاپىرلارغىمۇ مەنسۈپ ئەمەس). كىمنى ئاللاھ گۇمراھ قىلغان ئىكەن (يەنى ئۆزلىرىنىڭ خاتا يولغا كىرىۋالغانلىقلىرى سەۋەبلىك ئاللاھ ئازغۇنلۇقىدا تاشلىۋەتكەن بولسا)، ئۇنىڭغا ھەرگىزمۇ توغرا يول تاپالمايسەن.');
INSERT INTO "saleh" VALUES(4,144,'4:144','ئى مۆمىنلەر! مۆمىنلەرنى قويۇپ، كاپىرلارنى ۋەلى قىلىۋالماڭلار، سىلەرنىڭ زىيىنىڭلارغا (يەنى مۇناپىق ئىكەنلىكىڭلارغا) ئاللاھنىڭ روشەن پاكىتى بولۇشىنى خالامسىلەر؟.');
INSERT INTO "saleh" VALUES(4,145,'4:145','مۇناپىقلار چوقۇم دوزاخنىڭ ئەڭ ئاستىنقى قەۋىتىگە تاشلىنىدۇ، ئۇلارغا ھەرگىزمۇ (ئازابتىن قۇتقۇزىدىغان) مەدەتكار تاپالمايسەن.');
INSERT INTO "saleh" VALUES(4,146,'4:146','پەقەت (مۇناپىقلىقتىن) تەۋبە قىلغان، (ئەمەللىرىنى) تۈزەتكەن، ئاللاھ (نىڭ كىتابى) غا چىڭ يېپىشقان، دىننى ئاللاھ ئۈچۈن خالىس قىلغان (يەنى قىلغان ئەمەلىدىن پەقەت ئاللاھنىڭ رازىلىقىنى كۆزلىگەن) كىشىلەر بۇنىڭدىن مۇستەسنا. ئەنە شۇلار مۆمىنلەر بىلەن بىللە (يەنى ئۇلارنىڭ قاتارىدا) دۇر. ئاللاھ مۆمىنلەرگە (ئاخىرەتتە جەننەتتىن ئىبارەت) بۈيۈك ئەجىر ئاتا قىلىدۇ. ');
INSERT INTO "saleh" VALUES(4,147,'4:147','ئەگەر (ئاللاھنىڭ نېمەتلىرىگە) شۈكۈر قىلساڭلار ۋە ئىمان ئېيتساڭلار، ئاللاھ سىلەرگە ئازاب قىلىپ نېمە قىلىدۇ؟ ئاللاھ شۈكۈر قىلغۇچىنى مۇكاپاتلىغۇچىدۇر، ھەممىنى بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(4,148,'4:148','زۇلۇمغا ئۇچرىغۇچىدىن باشقا (ھەر قانداق كىشىنىڭ) ئاشكارا يامان سۆز قىلىشىنى ئاللاھ ياقتۇرمايدۇ (زۇلۇمغا ئۇچرىغۇچىنىڭ زالىمدىن شىكايەت قىلىشىغا ۋە ئۇنىڭغا دۇئايىبەت قىلىشىغا بولىدۇ)، ئاللاھ (زۇلۇمغا ئۇچرىغۇچىنىڭ دۇئاسىنى) ئاڭلىغۇچىدۇر، (زالىمنى) بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(4,149,'4:149',' سىلەر ئاشكارا ياكى يوشۇرۇن بىر ياخشىلىقنى قىلساڭلار ياكى بىرەر يامانلىقنى كەچۈرسەڭلار (ساۋاب تاپىسىلەر). شۈبھىسىزكى، ئاللاھ ئەپۇ قىلغۇچىدۇر، قۇدرەتلىكتۇر. ');
INSERT INTO "saleh" VALUES(4,150,'4:150',' شۈبھىسىزكى، ئاللاھنى ۋە ئۇنىڭ پەيغەمبەرلىرىنى ئىنكار قىلغانلار، ئاللاھ بىلەن ئۇنىڭ پەيغەمبەرلىرىنىڭ ئارىسىنى (ئاللاھقا ئىشىنىپ پەيغەمبەرلىرىگە ئىشەنمەيمىز دېيىش بىلەن) ئاجرىتىۋەتمەكچى بولغانلار، (پەيغەمبەرلەرنىڭ) بەزىسىگە ئىشىنىمىز، بەزىسىگە ئىشەنمەيمىز دېگۈچىلەر ـ ئۇنىڭ (يەنى ئىمان بىلەن كۇفرىنىڭ) ئارىسىدا (ئوتتۇرا) يول تۇتماقچى بولغانلار.');
INSERT INTO "saleh" VALUES(4,151,'4:151','ئەنە شۇلار (ئىماننى دەۋا قىلغان تەقدىردىمۇ) راستىنلا كاپىرلاردۇر، كاپىرلارغا خورلىغۇچى ئازاب تەييارلىدۇق. ');
INSERT INTO "saleh" VALUES(4,152,'4:152','ئاللاھقا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقان، ئۇلاردىن ھېچبىرىنىڭ ئارىسىنى ئاجرىتىۋەتمىگەن (يەنى ھەممىسىگە ئىمان ئېيتقان) كىشىلەرگە ئاللاھ ئۇلارنىڭ (تېگىشلىك) ئەجىرلىرىنى ئاتا قىلىدۇ، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(4,153,'4:153','ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) سەندىن ئۆزلىرىگە ئاسماندىن بىر كىتاب چۈشۈرۈشنى سورايدۇ، ئۇلار مۇسادىن بۇنىڭدىنمۇ چوڭراقىنى سوراپ: «بىزگە ئاللاھنى ئاپئاشكارا كۆرسەتكىن» دېگەن ئىدى. شۇنىڭ بىلەن، ئۇلارنىڭ زۇلمى سەۋەبلىك ئۇلارنى چاقماق سوقتى (يەنى ئاسماندىن بىر ئوت كېلىپ ئۇلارنى ھالاك قىلدى). ئۇلار روشەن مۆجىزىلەر كەلگەندىن كېيىن موزاينى مەبۇد قىلىۋالدى. كېيىن بىز ئۇلارنى ئەپۇ قىلدۇق. بىز مۇساغا (ئۇنىڭ راست پەيغەمبەرلىكىنى ئىسپاتلايدىغان مۆجىزىلەردىن) روشەن پاكىت ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(4,154,'4:154','(تەۋرات شەرىئىتىنى قوبۇل قىلمىغانلىقلىرى سەۋەبلىك) ئۇلاردىن ئەھدە ئالماق ئۈچۈن ئۈستىگە تۇر (تېغى) نى تىكلەپ قويدۇق، ئۇلارغا: «(بەيتۇلمۇقەددەس) دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار» دېدۇق ھەمدە ئۇلارغا: «شەنبىنىڭ ھۆرمىتىنى ساقلاڭلار (يەنى شەنبىدە بېلىق تۇتۇش بىلەن ھەددىڭلاردىن ئاشماڭلار)» دېدۇق. ئۇلاردىن (بۇ ھەقتە) مەھكەم ۋەدە ئالدۇق.');
INSERT INTO "saleh" VALUES(4,155,'4:155',' ئۇلار ئەھدىنى بۇزغانلىقلىرى، ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى، پەيغەمبەرلەرنى ناھەق ئۆلتۈرگەنلىكلىرى ۋە (مۇھەممەد ئەلەيھىسسالامغا) دىللىرىمىز پەردىلەنگەن (يەنى سۆزۈڭنى چۈشەنمەيمىز) دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق)، بەلكى ئاللاھ ئۇلارنىڭ كۇفرى سەۋەبىدىن دىللىرىنى پېچەتلىۋەتتى، شۇڭا ئۇلارنىڭ ئازغىنىسىدىن باشقىسى ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(4,156,'4:156',' يەنە ئۇلارنىڭ كۇفرى (يەنى ئىيسا ئەلەيھىسسالامنى ئىنكار قىلغانلىقلىرى) ئۈچۈن ۋە مەريەمگە چوڭ بوھتان چاپلىغانلىقلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق).');
INSERT INTO "saleh" VALUES(4,157,'4:157',' يەنە ئاللاھنىڭ رەسۇلى ئىيسا ئىبنى مەريەمنى ھەقىقەتەن ئۆلتۈردۇق دېگەنلىكلىرى ئۈچۈن (ئۇلارغا لەنەت قىلدۇق). ۋاھالەنكى، ئۇلارنىڭ ئىيسانى ئۆلتۈرگىنىمۇ يوق، دارغا ئاسقىنىمۇ يوق ۋەلېكىن ئۇلارغا شۈبھە سېلىندى (يەنى ئىيسانى ئاستۇق دەپ گۇمان قىلىپ، ئىيسا ئەلەيھىسسالامغا ئوخشاپ قالغان باشقا بىرسىنى ئاستى). ئىيسا توغرىسىدا ئىختىلاپ قىلىشقانلار ھەقىقەتەن ئۇنىڭ ئۆلتۈرۈلگەنلىكى (مەسىلىسى) دە شۈبھىدىدۇر. ئۇلار بۇ (ئىشنىڭ ھەقىقىتى) نى بىلمەيدۇ، گۇمانغىلا ئاساسلىنىدۇ، ئۇلار ئىيسانى ھەرگىزمۇ ئۆلتۈرمىدى.');
INSERT INTO "saleh" VALUES(4,158,'4:158','بەلكى ئاللاھ ئۇنى ئۆز تەرىپىگە كۆتۈردى (يەنى ئىيسا ئەلەيھىسسالامنى ئاللاھ ئۇلارنىڭ شەررىدىن قۇتۇلدۇرۇپ ھۇزۇرىغا ئېلىپ چىقىپ كەتتى). ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,159,'4:159',' ئەھلى كىتابتىن (يەنى يەھۇدىيلار ۋە ناسارالاردىن) ھەر قاندىقى جان ئۈزۈش ئالدىدا ئۇنىڭغا (يەنى ئىيسانىڭ ھەق پەيغەمبەرلىكى ۋە ئاللاھنىڭ بەندىسى ئىكەنلىكىگە) ئىمان ئېيتىدۇ. ئىيسا قىيامەت كۈنى ئۇلار (يەھۇدىيلارنىڭ ئىيسانى دارغا ئاستۇق دېگەن دەۋاسىنىڭ ۋە ناسارالارنىڭ ئىيسا ئاللاھنىڭ ئوغلى دېگەن دەۋاسىنىڭ يالغانلىقى) غا گۇۋاھ بولىدۇ.');
INSERT INTO "saleh" VALUES(4,160,'4:160','يەھۇدىيلارنىڭ قىلغان ھەقسىزلىقلىرى (يەنى زۇلمى ۋە قىلغان گۇناھلىرى)، نۇرغۇن كىشىلەرنى ئاللاھنىڭ يولىدىن (يەنى ئاللاھنىڭ دىنىغا كىرىشتىن) توسقانلىقلىرى، چەكلەنگەن جازانىنى ئالغانلىقلىرى ۋە كىشىلەرنىڭ پۇل ـ ماللىرىنى ناھەق يېگەنلىكلىرى ئۈچۈن، ئۇلارغا (ئىلگىرى) ھالال قىلىنغان پاكىز نەرسىلەرنى ھارام قىلدۇق، ئۇلارنىڭ ئىچىدىن كاپىر بولغانلارغا قاتتىق ئازاب ھازىرلىدۇق[160ـ161].');
INSERT INTO "saleh" VALUES(4,161,'4:161','يەھۇدىيلارنىڭ قىلغان ھەقسىزلىقلىرى (يەنى زۇلمى ۋە قىلغان گۇناھلىرى)، نۇرغۇن كىشىلەرنى ئاللاھنىڭ يولىدىن (يەنى ئاللاھنىڭ دىنىغا كىرىشتىن) توسقانلىقلىرى، چەكلەنگەن جازانىنى ئالغانلىقلىرى ۋە كىشىلەرنىڭ پۇل ـ ماللىرىنى ناھەق يېگەنلىكلىرى ئۈچۈن، ئۇلارغا (ئىلگىرى) ھالال قىلىنغان پاكىز نەرسىلەرنى ھارام قىلدۇق، ئۇلارنىڭ ئىچىدىن كاپىر بولغانلارغا قاتتىق ئازاب ھازىرلىدۇق[160ـ161].');
INSERT INTO "saleh" VALUES(4,162,'4:162',' ئۇلارنىڭ ئىچىدىكى بىلىمى چوڭقۇر بولغانلارغا، ساڭا نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، سەندىن ئىلگىرى (ئۆتكەن پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتىدىغان مۆمىنلەرگە، نامازنى ئۆتىگۈچىلەرگە، زاكات بەرگۈچىلەرگە، ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقۇچىلارغا (جەننەتتە مەڭگۈ قېلىشتىن ئىبارەت) كاتتا ئەجىر ئاتا قىلىمىز. ');
INSERT INTO "saleh" VALUES(4,163,'4:163','(ئى مۇھەممەد!) بىز نۇھقا ۋە ئۇنىڭدىن كېيىنكى پەيغەمبەرلەرگە ۋەھىي قىلغاندەك، ھەقىقەتەن ساڭا ۋەھىي قىلدۇق ھەمدە ئىبىراھىمغا، ئىسمائىلغا، ئىسھاققا، يەئقۇبقا، يەئقۇبنىڭ ئەۋلادلىرىغا، ئىيساغا، ئەييۇبقا، يۇنۇسقا، ھارۇنغا، سۇلايمانغا ۋەھىي قىلدۇق، داۋۇدقا زەبۇرنى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(4,164,'4:164',' بىز نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا ئىلگىرى (ئى مۇھەممەد!) ساڭا بايان قىلغانلىرىمىزمۇ بار، ساڭا بايان قىلمىغانلىرىمىزمۇ بار، ئاللاھ مۇساغا (بىۋاسىتە) سۆز قىلدى.');
INSERT INTO "saleh" VALUES(4,165,'4:165','ئەۋەتىلگەن پەيغەمبەرلەردىن كېيىن ئاللاھنىڭ ئالدىدا كىشىلەر (يەنى پەيغەمبەر ئەۋەتىلگەن بولسا ئەلۋەتتە ئىمان ئېيتاتتىم ۋە ئىتائەت قىلاتتىم دېگۈچىلەر) گە باھانە بولۇپ قالماسلىقى ئۈچۈن، (ئىتائەت قىلغۇچىلارغا جەننەت بىلەن) خۇشخەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى دوزاختىن) ئاگاھلاندۇرغۇچى پەيغەمبەرلەرنى ئەۋەتتۇق، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,166,'4:166','(يەھۇدىيلار ۋە ناسارالار سېنىڭ پەيغەمبەرلىكىڭگە گۇۋاھلىق بەرمىسە گۇناھى ئۆزىگە) لېكىن ئاللاھ ساڭا نازىل قىلغان قۇرئان ئارقىلىق (پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، ئاللاھ قۇرئاننى (باشقىلار بىلمەيدىغان ئۆزىگە خاس) ئىلمى بىلەن نازىل قىلدى. پەرىشتىلەرمۇ (ئاللاھ ساڭا نازىل قىلغان قۇرئانغا ۋە پەيغەمبەرلىكىڭگە) گۇۋاھلىق بېرىدۇ، (باشقىلار گۇۋاھلىق بەرمىگەندىمۇ) ئاللاھنىڭ گۇۋاھلىقى يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(4,167,'4:167','شۈبھىسىزكى، كاپىر بولغانلار ۋە (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقانلار ھەقىقەتەن قاتتىق ئازدى.');
INSERT INTO "saleh" VALUES(4,168,'4:168',' شۈبھىسىزكى، كاپىر بولغانلار ۋە زۇلۇم قىلغانلارنى ئاللاھ مەغپىرەت قىلمايدۇ، توغرا يولغىمۇ (يەنى جەننەتنىڭ يولىغىمۇ) باشلىمايدۇ.');
INSERT INTO "saleh" VALUES(4,169,'4:169',' (ئۇلارنى) پەقەت جەھەننەمنىڭ يولىغا قويىۋىتىدۇ، ئۇلار جەھەننەمدە مەڭگۈ قالىدۇ، بۇ (يەنى ئۇلارنى جەھەننەمدە مەڭگۈ قالدۇرۇش) ئاللاھقا ئاسان.');
INSERT INTO "saleh" VALUES(4,170,'4:170',' ئى ئىنسانلار! پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) سىلەرگە پەرۋەردىگارىڭلاردىن ھەق (دىن) ئېلىپ كەلدى. ئىمان ئېيتىڭلاركى، (بۇ) سىلەر ئۈچۈن پايدىلىق، ئەگەر ئىنكار قىلساڭلار (يەنى كۇفرىنى داۋاملاشتۇرساڭلار) (ئاللاھ سىلەردىن بىھاجەت). چۈنكى ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مەخلۇقاتىدۇر، مۈلكىدۇر). ئاللاھ (بەندىلىرىنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(4,171,'4:171',' ئى ئەھلى كىتاب! (يەنى ناسارالار جامائەسى) دىنىڭلاردا ھەددىدىن ئاشماڭلار، ئاللاھنىڭ شەنىگە ھەق (سۆز) دىن باشقىنى ئېيتماڭلار (يەنى ئاللاھقا شېرىكى ۋە بالىسى بولۇشتىن پاك دەپ ئېتىقاد قىلىڭلار). مەسىھ ئىيسا ـ مەريەمنىڭ ئوغلى، پەقەت ئاللاھنىڭ رەسۇلىدۇر (سىلەر گۇمان قىلغاندەك ئاللاھنىڭ ئوغلى ئەمەستۇر)، مەريەمگە ئاللاھنىڭ ئىلقا قىلغان كەلىمىسىدۇر (يەنى ئاتىنىڭ ۋاسىتىسىسىز، ئاللاھنىڭ «ۋۇجۇدقا كەل» دېگەن سۆزىدىن يارىتىلغاندۇر)، ئاللاھ تەرىپىدىن كەلگەن بىر روھتۇر. ئاللاھقا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتىڭلار، ئاللاھ (ئىيسا، مەريەمدىن ئىبارەت) ئۈچتۇر دېمەڭلار، (خۇدا ئۈچ دەيدىغان ئېتىقادتىن) قايتىڭلار، (بۇ) سىلەرگە پايدىلىقتۇر، ئاللاھ پەقەت بىر ئىلاھتۇر، ئاللاھ بالىسى بولۇشتىن پاكتۇر، ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، مەخلۇقاتىدۇر)، ئاللاھ يېتەرلىك ھامىيدۇر.');
INSERT INTO "saleh" VALUES(4,172,'4:172',' مەسىھمۇ، (ئاللاھقا) يېقىن پەرىشتىلەرمۇ ئاللاھقا بەندە بولۇشتىن ھەرگىز باش تارتمايدۇ. كىملەركى ئاللاھقا بەندىچىلىك قىلىشتىن باش تارتىدىكەن ۋە كۆرەڭلەپ كېتىدىكەن، (بىلسۇنكى) ئاللاھ ئۇلارنىڭ ھەممىسىنى (قىيامەت كۈنى ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۆز دەرگاھىغا توپلايدۇ.');
INSERT INTO "saleh" VALUES(4,173,'4:173','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، ئاللاھ ئۇلارنىڭ (ئەمەللىرىنىڭ) ئەجرىنى تولۇق بېرىدۇ، نېمىتىنى ئۇلارغا ئاشۇرۇپ بېرىدۇ، (ئاللاھقا قۇلچىلىق قىلىشتىن) باش تارتقان ۋە كۆرەڭلەپ كەتكەنلەرگە كەلسەك، ئاللاھ ئۇلارغا قاتتىق ئازاب قىلىدۇ. ئۇلار ئۆزلىرى ئۈچۈن ئاللاھتىن باشقا (ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق دوست ۋە ھېچقانداق مەدەتكار تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(4,174,'4:174','ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (شانلىق مۆجىزىلەر بىلەن كۈچلەندۈرۈلگەن مۇھەممەد ئەلەيھىسسالامدىن ئىبارەت) ئوچۇق دەلىل كەلدى، سىلەرگە روشەن نۇرنى (يەنى قۇرئاننى) نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(4,175,'4:175','ئاللاھقا ئىمان ئېيتقان ۋە ئۇنىڭغا (يەنى قۇرئانغا) چىڭ يېپىشقانلارغا كەلسەك، ئاللاھ ئۇلارنى ئۆز رەھمىتىگە ۋە نېمىتىگە (يەنى جەننىتىگە) داخىل قىلىدۇ ۋە ئۇلارنى توغرا يولغا باشلايدۇ. ');
INSERT INTO "saleh" VALUES(4,176,'4:176',' (ئى مۇھەممەد!) سەندىن (ئاتا ـ ئانىسى، بالىسى يوق مېيىتنىڭ مىراسى توغرۇلۇق) پەتىۋا سورىشىدۇ، ئېيتقىنكى، «ئاللاھ ئاتا ـ ئانىسى، بالىسى يوق كىشى (قالدۇرغان مىراس) توغرىسىدا پەتىۋا بېرىدۇ؛ ئەگەر بىر كىشى ۋاپات بولسا، بالىسى بولمىسا، پەقەت بىر ھەمشىرىسى بولسا، ئۇ تەرەكىنىڭ يېرىمىنى ئالىدۇ. ئەگەر ئۇ ئايالنىڭ بالىسى بولمىسا، ئەر بىر تۇغقىنى ئۇنىڭ ۋارىسى بولىدۇ (يەنى مىراسنىڭ ھەممىسىنى ئالىدۇ). ئەگەر ئىككى ھەمشىرىسى بولسا، ئۇلار تەرەكىنىڭ ئۈچتىن ئىككى ھەسسىسىنى ئالىدۇ. ئەگەر ۋارىسلار ھەم ئەر، ھەم ئايال بىر تۇغقانلار بولسا (ئۇلارنىڭ ئىچىدىكى) بىر ئەرگە ئىككى ئايالنىڭ ھەسسىسى بېرىلىدۇ. ئاداشماسلىقىڭلار ئۈچۈن ئاللاھ سىلەرگە (شەرىئەت ئەھكاملىرىنى) بايان قىلىپ بېرىدۇ. ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر». ');
INSERT INTO "saleh" VALUES(5,1,'5:1','ئى مۆمىنلەر! ئەھدىلەرگە (يەنى ئاللاھ بىلەن سىلەرنىڭ ئاراڭلاردىكى ۋە سىلەرنىڭ ئۆز ئاراڭلاردىكى ئەھدىلەرگە) ۋاپا قىلىڭلار. سىلەرگە ھاراملىقى بايان قىلىنىدىغانلاردىنباشقا، ھايۋانلارنى بوغۇزلىغاندىن كېيىن (يېيىش) ھالال قىلىندى. لېكىن سىلەر ئېھرام (ياكى ھەرەم) دە بولغىنىڭلاردا شىكارنى ھالال سانىماڭلار، شۈبھىسىزكى، ئاللاھ (ھالال ـ ھارام توغرۇلۇق) خالىغان نەرسىنى ھۆكۈم قىلىدۇ.');
INSERT INTO "saleh" VALUES(5,2,'5:2',' ئى مۆمىنلەر! ئاللاھنىڭ دىنىنىڭ ئالامەتلىرىنى (يەنى ھەجدە تائەتنىڭ بەلگىسى ھېسابلانغان ئىشلارنى) (تەرك ئېتىش بىلەن) چەكلەنگەن ئايلار (دا ئۇرۇش قىلىش) نى، ھەدىيەنى (يەنى كەبىگە ئاتالغان قۇربانلىققا دەخلى ـ تەرۇز قىلىشنى)، قەلادە (يەنى ھەجدە قۇربانلىق قىلىنىدىغان مال ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن ھايۋانغا ئېسىپ قويۇلغان نەرسە) لەرگە (چېقىلىشنى)، پەرۋەردىگارىنىڭ پەزلىنى ۋە رازىلىقىنى تىلەپ بەيتۇللاھقا (ھەج ئۈچۈن ياكى ئۆمرە ئۈچۈن) كەلگۈچىلەرگە (چېقىلىشنى) ھالال سانىماڭلار. ئىھرامدىن چىققان چېغىڭلاردا شىكار قىلساڭلار بولىدۇ. بىرەر قەۋمنىڭ سىلەرنى مەسجىدى ھەرەم (زىيارىتى) دىن توسقانلىقتىن ئىبارەت دۈشمەنلىكى ھەرگىزمۇ سىلەرنىڭ ئۇلارغا چېقىلىشىڭلارغا سەۋەبچى بولمىسۇن. ياخشى ئىشقا ۋە تەقۋادارلىققا ياردەملىشىڭلار، گۇناھقا ۋە زۇلۇمغا ياردەملەشمەڭلار، ئاللاھ (نىڭ ئازابى) دىن قورقۇڭلار، ئاللاھنىڭ ئازابى ھەقىقەتەن قاتتىق. ');
INSERT INTO "saleh" VALUES(5,3,'5:3',' سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋان، قان، چوشقا گۆشى، ئاللاھتىن غەيرىينىڭ نامى تىلغا ئېلىنىپ بوغۇزلانغان ھايۋان، بوغۇپ ئۆلتۈرۈلگەن ھايۋان، ئۇرۇپ ئۆلتۈرۈلگەن ھايۋان، (ئېگىزدىن) يىقىلىپ ئۆلگەن ھايۋان، (ھايۋانلار تەرىپىدىن) ئۈسۈپ ئۆلتۈرۈلگەن ھايۋان، يىرتقۇچ ھايۋانلار يېرىپ ئۆلتۈرۈپ يېگەن ھايۋان (نىڭ گۆشىنى يېيىش) ھارام قىلىندى. لېكىن (يۇقىرىقى بەش تۈرلۈك ھايۋاندىن جېنى چىقمىغان چاغدا) بوغۇزلىغانلىرىڭلار ھالال بولىدۇ. بۇتلارغا ئېلىپ بېرىلىپ ئۇنىڭ يېنىدا بوغۇزلانغان ھايۋانلار ھارام قىلىندى. ئەزلام (يەنى جاھىلىيەت دەۋرىدە پال ئۈچۈن ئىشلىتىدىغان ئۈچ پارچە ياغاچ) بىلەن پال سېلىشىڭلار ھارام قىلىندى، بۇ گۇناھتۇر، كاپىرلار بۈگۈن سىلەرنىڭ دىنىڭلار (نى يوقىتىش) دىن ئۈمىدىنى ئۈزدى، ئۇلاردىن قورقماڭلار، مەندىن قورقۇڭلار. بۈگۈن سىلەرنىڭ دىنىڭلارنى پۈتۈن قىلدىم، سىلەرگە نېمىتىمنى تاماملىدىم، ئىسلام دىنىنى سىلەرنىڭ دىنىڭلار بولۇشقا تاللىدىم. كىمكى ئاچلىقتا ئىلاجىسىزلىقتىن، گۇناھنى مەقسەت قىلماستىن (ھارام قىلىنغان نەرسىلەرنى يېسە گۇناھ بولمايدۇ)، ئاللاھ مەغپىرەت قىلغۇچىدۇر، بەندىلىرىگە ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(5,4,'5:4',' (ئى مۇھەممەد!) ئۇلار سەندىن ئۆزلىرىگە (يېمەك ـ ئىچمەكتىن) نېمىلەرنىڭ ھالال قىلىنغانلىقىنى سورايدۇ، ئېيتقىنكى، «سىلەرگە پاك نەرسىلەر ۋە (ئىت، قارچىغا، قۇشقا ئوخشاش) شىكارغا ئۆگىتىلگەن، سىلەر ئاللاھنىڭ ئۆزۈڭلارغا ئۆگەتكىنى بويىچە ئۆگەتكەن ئوۋچى جانىۋارلارنىڭ شىكار قىلغان نەرسىلىرى ھالال قىلىندى. ئۇلارنىڭ سىلەر ئۈچۈن ئوۋلىغان نەرسىلىرىدىن يەڭلار (ئوۋچى جانىۋارلارنى شىكارغا قويۇپ بەرگەن چېغىڭلاردا) ئاللاھنىڭ نامىنى ياد قىلىڭلار (يەنى بىسمىللاھ دەڭلار)، ئاللاھ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) تىن ساقلىنىڭلار، ئاللاھ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر».');
INSERT INTO "saleh" VALUES(5,5,'5:5','بۈگۈن سىلەرگە پاك نەرسىلەر ھالال قىلىندى، كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ تامىقى سىلەرگە ھالالدۇر ۋە سىلەرنىڭ تامىقىڭلار ئۇلارغا ھالالدۇر. مۆمىنلەردىن بولغان ئىپپەتلىك ئاياللارنىڭ، سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن بولغان ئىپپەتلىك ئاياللارنىڭ مەھرىنى بەرسەڭلار، ئىپپەتلىك بولغىنىڭلار ۋە ئاشكارا ـ يوشۇرۇن پاھىشىنى كۆزلىمىگىنىڭلار ھالدا (ئۇلارنى ئالساڭلار)، ئۇلار سىلەرگە ھالالدۇر. كىمكى ئىماندىن يېنىۋالىدىكەن (ياكى شەرىئەت ئەھكاملىرىنى ئىنكار قىلىدىكەن)، ئۇنىڭ ئەمەلى (نىڭ ساۋابى) يوققا چىقىرىلىدۇ، ئاخىرەتتە ئۇ زىيان تارتقۇچىلاردىن بولىدۇ.');
INSERT INTO "saleh" VALUES(5,6,'5:6',' ئى مۆمىنلەر! (سىلەر تاھارەتسىز بولۇپ) ناماز (ئوقۇماقچى بولۇپ) تۇرغىنىڭلاردا، يۈزۈڭلارنى يۇيۇڭلار، قولۇڭلارنى جەينىكىڭلار بىلەن قوشۇپ يۇيۇڭلار، بېشىڭلارغا مەسھى قىلىڭلار، پۇتۇڭلارنى ئوشۇقۇڭلار بىلەن قوشۇپ يۇيۇڭلار، ئەگەر جۇنۇپ بولساڭلار پاكلىنىڭلار (يەنى غۇسلى قىلىڭلار). ئەگەر كېسەل بولساڭلار (سۇ زىيان قىلىدىغان بولسا) ياكى سەپەر ئۈستىدە بولۇپ (سۇ تاپالمىساڭلار) ياكى سىلەرنىڭ بىرسىڭلار ھاجەتخانىدىن كەلگەن (يەنى تاھارەت سۇندۇرغان) بولساڭلار ياكى ئاياللىرىڭلار بىلەن يېقىنچىلىق قىلغاندىن كېيىن (غۇسلى تاھارەت ئۈچۈن) سۇ تاپالمىساڭلار، پاك تۇپراقتا تەيەممۇم قىلىڭلار، ئۇنىڭ بىلەن يۈزۈڭلارغا، قولۇڭلارغا مەسھى قىلىڭلار، ئاللاھ سىلەرگە مۇشەققەتنى خالىمايدۇ، لېكىن ئاللاھ شۈكۈر قىلىشىڭلار ئۈچۈن سىلەرنى پاك قىلىشنى، (ئىسلام شەرىئىتىنى بايان قىلىش بىلەن) نېمىتىنى سىلەرگە تاماملاشنى خالايدۇ.');
INSERT INTO "saleh" VALUES(5,7,'5:7',' (ئى مۆمىنلەر!) ئاللاھنىڭ سىلەرگە بەرگەن (ئۇلۇغ) نېمىتىنى ۋە سىلەر بىلەن قىلغان مۇئاھىدىسىنى ئەسلەڭلار. ئەينى زاماندا سىلەر: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق» دېگەن ئىدىڭلار، ئاللاھتىن قورقۇڭلار (يەنى ئاللاھقا بەرگەن ئەھدىنى بۇزۇشتىن ساقلىنىڭلار). ئاللاھ ھەقىقەتەن (بەندىلىرىنىڭ) دىللىرىدىكى سىرلىرىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(5,8,'5:8',' ئى مۆمىنلەر! ئاللاھنىڭ رىزالىقى ئۈچۈن ئادالەتنى بەرقا قىلىڭلار. ئادىللىق بىلەن گۇۋاھلىق بېرىڭلار، بىرەر قەۋمگە بولغان ئۆچمەنلىكىڭلار (ئۇلارغا) ئادىل بولماسلىقىڭلارغا سەۋەب بولمىسۇن، (دۈشمىنىڭلارغىمۇ) ئادىل بولۇڭلار، بۇ (يەنى ئۇلارغا ئۆچمەن تۇرۇپ ئادىل بولۇشۇڭلار) تەقۋادارلىققا ئەڭ يېقىندۇر، ئاللاھتىن قورقۇڭلار، ئاللاھ ھەقىقەتەن قىلمىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(5,9,'5:9','ئاللاھ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ساۋاب ۋەدە قىلدى.');
INSERT INTO "saleh" VALUES(5,10,'5:10',' كاپىر بولغان ۋە بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغان كىشىلەر ئەھلى دوزاختۇر.');
INSERT INTO "saleh" VALUES(5,11,'5:11',' ئى مۆمىنلەر! ئاللاھنىڭ سىلەرگە بەرگەن (سىلەرنى دۈشمىنىڭلاردىن ساقلىغانلىق) نېمىتىنى ئەسلەڭلار. ئەينى زاماندا بىر جامائە سىلەرگە قوللىرىنى ئۇزاتماقچى (يەنى سىلەرنى ئۆلتۈرمەكچى) بولغان ئىدى، ئاللاھ ئۇلارنىڭ قوللىرىنى سىلەردىن توستى (يەنى سىلەرنى ئۇلارنىڭ زىيانكەشلىك قىلىشىدىن ساقلىدى). ئاللاھتىن (ئاللاھنىڭ ئەمرىنى ئورۇنلاش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن) قورقۇڭلار، مۆمىنلەر ئاللاھقا تەۋەككۈل قىلسۇن.');
INSERT INTO "saleh" VALUES(5,12,'5:12',' شۈبھىسىزكى، ئاللاھ ئىسرائىل ئەۋلادىدىن مەھكەم ئەھدە ئالدى، ئۇلارنىڭ ئارىسىدىن (يەنى ئون ئىككى ئايماقنىڭ ھەر بىرسىدىن بىردىن) ئون ئىككى باشلىقنى (كېپىللىك ئۈچۈن جەببارلارغا يەنى ئەمالىقەلەرگە) ئەۋەتتۇق ھەمدە ئاللاھ : «مەن سىلەرگە ياردەم بېرىمەن، (ئى ئىسرائىل ئەۋلادى! ئاللاھ بىلەن قەسەمكى) ئەگەر سىلەر نامازنى (تەلەپكە لايىق) ئۆتىسەڭلار، زاكات بەرسەڭلار، پەيغەمبەرلىرىمگە ئىمان ئېيتساڭلار ۋە ئۇلارغا ياردەم بەرسەڭلار، ئاللاھقا قەرزى ھەسەنە بەرسەڭلار (يەنى ئاللاھنىڭ يولىدا پۇل ـ مېلىڭلارنى سەرپ قىلساڭلار)، ئەلۋەتتە سىلەرنىڭ گۇناھىڭلارنى يوققا چىقىرىمەن، سىلەرنى ئەلۋەتتە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىمەن. شۇنىڭدىن (يەنى مەھكەم ئەھدىدىن) كېيىن سىلەردىن كىمكى (ئەمر قىلغان نەرسىلىرىمنى) ئىنكار قىلىدىكەن، ئۇ ئەلۋەتتە توغرا يولدىن ئازغان بولىدۇ» دېدى. ');
INSERT INTO "saleh" VALUES(5,13,'5:13','ئەھدىنى بۇزغانلىقلىرى ئۈچۈن ئۇلارنى رەھمىتىمىزدىن يىراق قىلدۇق، ئۇلارنىڭ دىللىرىنى (ئىماننى قوبۇل قىلىشقا يۇمشىمايدىغان دەرىجىدە) قاتتىق قىلدۇق، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى ئۆزگەرتىۋېتىدۇ، ئۇلار (تەۋراتتا) ئۆزلىرى ئۈچۈن قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى. (ئى مۇھەممەد!) ئۇلارنىڭ ئازغىنىسىدىن باشقىسىنىڭ خىيانەت قىلغانلىقىنى بايقاپ تۇرىسەن، (ئەگەر تەۋبە قىلسا) ئۇلارنى ئەپۇ قىلغىن، (ئەگەر ئىمان ئېيتسا) ئۇلارنى كەچۈرگىن، ئاللاھ ھەقىقەتەن ياخشىلىق قىلغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(5,14,'5:14','بىز ناسارامىز دېگەنلەردىنمۇ (ئاللاھنى بىر دەپ تونۇشقا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان ئېيتىشقا) مەھكەم ئەھدە ئالدۇق، ئۇلار (ئىنجىلدا) ئۆزلىرىگە قىلىنغان نەسىھەتنىڭ بىر قىسمىنى ئۇنتۇدى، (بۇنىڭ جازاسى ئۈچۈن) ئۇلارنىڭ ئارىسىغا قىيامەتكىچە ئاداۋەت ۋە دۈشمەنلىك سالدۇق، (شۇ چاغدا) ئاللاھ ئۇلارغا قىلمىشلىرىنى ئېيتىپ بېرىدۇ (يەنى ئۇلار ئۆزلىرىنىڭ قەبىھ ئەمەللىرىنىڭ جازاسىنى تارتىدۇ).');
INSERT INTO "saleh" VALUES(5,15,'5:15',' ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەرگە سىلەر كىتابتا (ئىنجىلدا ۋە تەۋراتتا) يوشۇرغان نۇرغۇن نەرسىلەرنى بايان قىلىپ بېرىدىغان ۋە (يوشۇرغان نەرسىلەردىن) نۇرغۇنىنى كەچۈرىدىغان (يەنى ئاشكارىلاپ سىلەرنى پاش قىلمايدىغان) رەسۇلىمىز كەلدى، سىلەرگە ئاللاھ تەرىپىدىن نۇر (يەنى مۇھەممەد ئەلەيھىسسالام) ۋە روشەن كىتاب (يەنى قۇرئان) كەلدى.');
INSERT INTO "saleh" VALUES(5,16,'5:16',' ئاللاھ شۇ كىتاب (يەنى قۇرئان) ئارقىلىق رازىلىقىنى تىلىگەنلەرنى تىنچلىق يوللىرى (يەنى ئاللاھنىڭ شەرىئىتى ۋە ئۇنىڭ ئەھكاملىرى) غا يېتەكلەيدۇ، ئىرادىسى بويىچە ئۇلارنى (كۇفرى) قاراڭغۇلۇقلىرىدىن (ئىماننىڭ) نۇرىغا چىقىرىدۇ ۋە ئۇلارنى توغرا يولغا ھىدايەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(5,17,'5:17',' مەريەم ئوغلى مەسىھ (يەنى ئىيسا) ئاللاھتۇر دېگەن كىشىلەر ھەقىقەتەن كاپىر بولدى. (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر ئاللاھ مەريەم ئوغلى مەسىھنى، مەسىھنىڭ ئانىسىنى (يەنى مەريەمنى) ۋە يەر يۈزىدىكى بارلىق كىشىلەرنى ھالاك قىلىشنى ئىرادە قىلسا، ئاللاھ (نىڭ ئىرادىسى) دىن بىرەر نەرسىنى توسۇشقا كىم قادىر بولالايدۇ؟» ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ ئىگىدارچىلىقى ئاللاھقا خاستۇر، ئاللاھ خالىغىنىنى خەلق ئېتىدۇ، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(5,18,'5:18','يەھۇدىيلار ۋە ناسارالار: «بىز ئاللاھنىڭ ئوغۇللىرىدۇرمىز (يەنى ئاتا ـ بالىدەك يېقىنمىز) ۋە دوستلىرىدۇرمىز» دېدى. ئېيتقىنكى، «(ئۇنداق بولسا) ئاللاھ نېمە ئۈچۈن سىلەرگە گۇناھىڭلار تۈپەيلىدىن ئازاب قىلىدۇ؟» ئەمەلىيەتتە سىلەرمۇ باشقا كىشىلەرگە ئوخشاش ئاللاھ ياراتقان ئىنسانسىلەر. ئاللاھ خالىغان ئادەمگە مەغپىرەت قىلىدۇ؛ خالىغان ئادەمگە ئازاب قىلىدۇ (يەنى مەغپىرەت قىلىنىشنى خالىغان كىشىنى ئاللاھ مەغپىرەت قىلىدۇ، ئازاب قىلىنىشنى خالىغان كىشىگە ئاللاھ ئازاب قىلىدۇ). ئاسمانلارنىڭ، يەر يۈزىنىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ ئىگىدارچىلىقى ئاللاھقا خاستۇر، ئەڭ ئاخىر بارىدىغان جاي ئاللاھنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(5,19,'5:19','ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) بىزگە (ساۋاب بىلەن) خۇشخەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى كەلمىدى دېمەسلىكىڭلار ئۈچۈن، پەيغەمبەرلەر ئۈزۈلۈپ قالغان بىر زاماندا سىلەرگە (شەرىئەتنى ۋە ئۇنىڭ ئەھكاملىرىنى) بايان قىلىپ بېرىدىغان رەسۇلىمىز كەلدى، ھەقىقەتەن سىلەرگە خۇشخەۋەر بەرگۈچى ۋە ئاگاھلاندۇرغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى (ئەمدى سىلەرگە ئۆزرە قالمىدى). ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(5,20,'5:20','ئۆز ۋاقتىدا، مۇسا ئۆز قەۋمىگە ئېيتتى: «ئى قەۋمىم! ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ياد قىلىڭلار، ئاللاھ ئۆز ۋاقتىدا ئىچىڭلاردا (سىلەرنى توغرا دىنغا يېتەكلەيدىغان) نۇرغۇن پەيغەمبەرلەرنى بارلىققا كەلتۈردى. سىلەردىن پادىشاھلار قىلدى (يەنى ئۆزەڭلارنى ئۆزەڭلارغا خوجا قىلدى). سىلەرگە جاھان ئەھلىدىن ھېچ كىشىگە بەرمىگەن (دەريانى يېرىپ ئوتتۇرىسىدىن يول ئېچىپ بېرىش، بۇلۇتلارنى سايىۋەن قىلىپ بېرىش، ئاسماندىن يېمەكلىك چۈشۈرۈپ بېرىش قاتارلىق) نېمەتلەرنى بەردى.');
INSERT INTO "saleh" VALUES(5,21,'5:21','ئى قەۋمىم! ئاللاھ سىلەرگە تەقدىر قىلغان (يەنى سىلەرنى كىرىشكە ئەمر قىلغان) مۇقەددەس زېمىنغا (يەنى بەيتۇلمۇقەددەسكە) كىرىڭلار، ئارقاڭلارغا چېكىنمەڭلار، بولمىسا (ئىككىلا دۇنيادا) زىيان تارتقۇچىلاردىن بولۇپ قالىسىلەر».');
INSERT INTO "saleh" VALUES(5,22,'5:22','ئۇلار: «ئى مۇسا! مۇقەددەس زېمىندا كۈچلۈك (يەنى جاسامەتلىك، قامەتلىك) بىر قەۋم بار (ئۇلار ئاد قەۋمىنىڭ قالدۇقى بولغان ئەمالىقەلەر بولۇپ)، ئۇلار چىقىپ كەتمىگۈچە بىز ئۇ يەرگە ھەرگىز كىرمەيمىز، ئۇلار ئۇيەردىن چىقىپ كەتسە چوقۇم كىرىمىز» دېدى.');
INSERT INTO "saleh" VALUES(5,23,'5:23',' ئاللاھتىن قورققۇچىلاردىن بولغان، ئاللاھنىڭ (ئىمان بىلەن تائەتتىن ئىبارەت) نېمىتىگە ئېرىشكەن ئىككى ئادەم (يەنى كالب ئىبن يەفۇننە بىلەن يۇشە ئىبن نۇن) ئۇلارغا: «(شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرىڭلار، (شەھەرنىڭ) دەرۋازىسىدىن ھۇجۇم قىلىپ كىرسەڭلار، چوقۇم غەلىبە قىلىسىلەر، ئەگەر (ئاللاھنىڭ ۋەدە قىلغان ياردىمىگە) ئىشەنسەڭلار، ئاللاھقا تەۋەككۈل قىلىڭلار» دېدى. ');
INSERT INTO "saleh" VALUES(5,24,'5:24',' ئۇلار: « ئى مۇسا! مادامىكى، ئۇلار مۇقەددەس يەردە ئىكەن، بىز ھەرگىز ئۇ يەرگە كىرمەيمىز، سەن پەرۋەردىگارىڭ بىلەن بىللە بېرىپ ئىككىڭلار ئۇرۇشۇڭلار، بىز بۇ يەردە ئولتۇرۇپ تۇرايلى» دېدى.');
INSERT INTO "saleh" VALUES(5,25,'5:25','مۇسا دېدى: «پەرۋەردىگارىم! مەن پەقەت ئۆزۈمگە ۋە قېرىنداشلىرىمغىلا ئىگىمەن، سەن بىز بىلەن (ئىتائىتىڭدىن باش تارتقان) قەۋمنىڭ ئارىسىنى ئايرىۋەتكىن».');
INSERT INTO "saleh" VALUES(5,26,'5:26','ئاللاھ دېدى: « ئۇلارنىڭ مۇقەددەس يەرگە كىرىشى 40 يىلغىچە ھارام قىلىندى، (بۇ جەرياندا) ئۇلار زېمىندا ئادىشىپ يۈرىدۇ. پاسىق قەۋم ئۈچۈن قايغۇرمىغىن».');
INSERT INTO "saleh" VALUES(5,27,'5:27',' (ئى مۇھەممەد!) ئۇلارغا (يەنى ئەھلى كىتابقا ۋە قەۋمىڭگە) ئادەمنىڭ ئىككى ئوغلى (يەنى ھابىل بىلەن قابىل) نىڭ ھېكايىسىنى راستلىق بىلەن ئوقۇپ بەرگىن. ئۆز ۋاقتىدا ئۇلار (يەنى ھابىل بىلەن قابىل) ئىككىيلەن قۇربانلىق قىلغان ئىدى. بىرسى (يەنى ھابىل) نىڭ قوبۇل بولدى، يەنە بىرسى (يەنى قابىل) نىڭ قوبۇل بولمىدى. (قابىل ھابىلغا) «مەن سېنى چوقۇم ئۆلتۈرىمەن» دېدى، ھابىل نېمىشقا؟ دېۋىدى، قابىل: «ئاللاھ سېنىڭ قۇربانلىقىڭنى قوبۇل قىلىپ، مېنىڭكىنى قوبۇل قىلمىدى» دېدى. (ھابىل) ئېيتتى: «ئاللاھ پەقەت تەقۋادارلارنىڭ (قۇربانلىقىنى) قوبۇل قىلىدۇ».');
INSERT INTO "saleh" VALUES(5,28,'5:28',' ئەگەر سەن مېنى ئۆلتۈرۈشكە قولۇڭنى سوزىدىغان بولساڭ، مەن سېنى ئۆلتۈرۈشكە قولۇمنى سوزمايمەن، مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئاللاھتىن قورقىمەن.');
INSERT INTO "saleh" VALUES(5,29,'5:29','مەن ھەقىقەتەن سېنىڭ مەن بىلەن مۇناسىۋەتلىك (يەنى مېنى ئۆلتۈرگەنلىك) ۋە ئۆزۈڭنىڭ (ئىلگىرىكى) گۇناھىڭنى (يەنى ئاتاڭ ئادەمنىڭ ئەمرىگە ئاسىيلىق قىلغانلىق گۇناھىڭنى) ئۈستۈڭگە ئېلىپ ئەھلى دوزاخلاردىن بولۇشۇڭنى تىلەيمەن، (كىشىلەرگە) زۇلۇم قىلغۇچىلارنىڭ جازاسى شۇدۇر.');
INSERT INTO "saleh" VALUES(5,30,'5:30','ئۇ (يەنى قابىل) نىڭ نەپسى ئۇنىڭغا قېرىندىشىنى (يەنى ھابىلنى) ئۆلتۈرۈشنى چىرايلىق كۆرسەتتى، شۇنىڭ بىلەن، ئۇنى ئۆلتۈرۈپ (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچىلاردىن بولدى.');
INSERT INTO "saleh" VALUES(5,31,'5:31','ئاللاھ ئۇنىڭغا قېرىندىشىنىڭ جەسىتىنى قانداق كۆمۈشنى كۆرسىتىش ئۈچۈن بىر قاغا ئەۋەتتى. قاغا (تۇمشۇقى ۋە ئىككى پۇتى بىلەن) يەرنى كولىدى. ئۇ (يەنى قابىل بۇنى كۆرۈپ): «ۋاي مەن قېرىندىشىمنىڭ جەسىتىنى كۆمۈشتە مۇشۇ قاغىچىلىك بولۇشتىن ئاجىز كەلدىممۇ؟» دېدى. شۇنىڭ بىلەن ئۇ نادامەت قىلغۇچىلاردىن بولدى.');
INSERT INTO "saleh" VALUES(5,32,'5:32','شۇ (يەنى قابىلنىڭ ھابىلنى ناھەق ئۆلتۈرگەنلىكى) سەۋەبتىن (تەۋراتتا) ئىسرائىل ئەۋلادىغا ھۆكۈم قىلدۇقكى، كىمكى ناھەق ئادەم ئۆلتۈرمىگەن ياكى يەر يۈزىدە بۇزغۇنچىلىق قىلمىغان بىر ئادەمنى ئۆلتۈرسە، ئۇ پۈتۈن ئىنسانلارنى ئۆلتۈرگەندەك بولىدۇ. كىمكى بىر ئادەمنى تىرىلدۈرسە (يەنى قۇتقۇزسا ياكى ھايات قېلىشىغا سەۋەبچى بولسا)، ئۇ پۈتۈن ئىنسانلارنى تىرىلدۈرگەندەك بولىدۇ. پەيغەمبەرلىرىمىز بەنى ئىسرائىلغا راستلا (شانلىق) مۆجىزىلەرنى ئېلىپ كەلدى، شۇنىڭدىن كېيىن ئۇلارنىڭ (يەنى ئىسرائىل ئەۋلادىنىڭ) تولىسى (كۇفرىدا، ئادەم ئۆلتۈرۈشتە) زېمىندا ھەددىدىن ئاشقۇچىلاردىن بولدى.');
INSERT INTO "saleh" VALUES(5,33,'5:33',' ئاللاھ ۋە ئۇنىڭ رەسۇلى بىلەن ئۇرۇش قىلىدىغانلارنىڭ، يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغانلارنىڭ جازاسى شۇكى، ئۇلار ئۆلتۈرۈلۈشى ياكى دارغا ئېسىلىشى ياكى ئوڭ قوللىرى ۋە سول پۇتلىرى كېسىلىشى ياكى سۈرگۈن قىلىنىشى كېرەك. بۇ (يەنى جازا) ئۇلار ئۈچۈن بۇ دۇنيادا رەسۋالىق (ئېلىپ كەلگۈچىدۇر)، ئاخىرەتتە ئۇلار چوڭ ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(5,34,'5:34',' سىلەر تۇتۇۋېلىشتىن ئىلگىرى تەۋبە قىلغانلار بۇنىڭدىن مۇستەسنا، بىلىڭلاركى، ئاللاھ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(5,35,'5:35',' ئى مۆمىنلەر! مەقسەتكە ئېرىشىشىڭلار ئۈچۈن ئاللاھ (نىڭ ئازابى) دىن قورقۇڭلار، ئاللاھقا (تائەت ـ ئىبادەت قىلىش، گۇناھتىن ساقلىنىش بىلەن) يېقىنچىلىقنى تىلەڭلار، ئاللاھ يولىدا جىھاد قىلىڭلار.');
INSERT INTO "saleh" VALUES(5,36,'5:36',' زېمىندىكى بارلىق نەرسىلەر، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان نەرسىلەر كاپىرلارنىڭ بولۇپ، ئۇلار قىيامەت كۈنى (ئاللاھ) نىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن ئۇنى فىدىيە قىلىپ بەرگەن تەقدىردىمۇ، ئۇلارنىڭ (فىدىيەسى) قوبۇل قىلىنمايدۇ، ئۇلار قىينىغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(5,37,'5:37','ئۇلار دوزاختىن چىقماقچى بولىدۇ-يۇ، ھەرگىز چىقالمايدۇ، ئۇلار دائىملىق ئازابقا قالىدۇ.');
INSERT INTO "saleh" VALUES(5,38,'5:38',' ئوغرىلىق قىلغۇچى ئەرنىڭ ۋە ئوغرىلىق قىلغۇچى ئايالنىڭ قىلمىشىنى جازالاپ، ئاللاھ تەرىپىدىن ئىبرەت قىلىش يۈزىسىدىن قوللىرىنى كېسىڭلار، ئاللاھ (ئۆز ئەمرىنى ئىشقا ئاشۇرۇشتا) غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(5,39,'5:39','كىمكى (ئوغرىلىق قىلىپ كىشىلەرگە) زۇلۇم قىلغاندىن كېيىن تەۋبە قىلسا، (ئەمەلىنى) تۈزىسە (يەنى ئوغرىلىق قىلىشتىن يانسا)، ئاللاھ ئۇنىڭ تەۋبىسىنى قوبۇل قىلىدۇ، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(5,40,'5:40','بىلمەمسەنكى، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھ خالىغان ئادەمگە ئازاب قىلىدۇ، خالىغان ئادەمگە مەغپىرەت قىلىدۇ، ئاللاھ ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(5,41,'5:41',' ئى پەيغەمبەر! دىللىرىدا ئىشەنمەي، ئېغىزلىرىدا: «ئىمان ئېيتتۇق» دەپ قويىدىغانلار (يەنى مۇناپىقلار) دىن ۋە يەھۇدىيلاردىن كۇفرىغا (پۇرسەت تاپسىلا كۇفرىنى ئىزھار قىلىشقا) ئالدىرايدىغان كىشىلەر سېنى غەمكىن قىلمىسۇن، ئۇلار (ئۆلىمالىرى توقۇغان) يالغانغا قۇلاق سالىدۇ. ساڭا كەلمىگەن (يەنى ئۆچلۈك قىلىپ سېنىڭ سۆھبىتىڭگە ھازىر بولمىغان) باشقا بىر قەۋمگە (يەنى خەيبەر يەھۇدىيلىرىنىڭ سۆزىگە) قۇلاق سالىدۇ، ئۇلار (تەۋراتنىڭ) سۆزلىرىنى ئۆزئورنىدىن ئۆزگەرتىۋېتىدۇ (يەنى ئاللاھنىڭ ئەھكاملىرىنى باشقىلارغا ئالماشتۇرۇۋېتىدۇ). ئۇلار: «ئەگەر سىلەرگە بۇ (ھۆكۈم) بېرىلسە، ئۇنى قوبۇل قىلىڭلار، ئەگەر ئۇ بېرىلمىسە قوبۇل قىلماڭلار» دەيدۇ، ئاللاھ بىركىمنىڭ گۇمراھلىقىنى ئىرادە قىلىدىكەن، ئۇنىڭ ئۈچۈن ئاللاھقا قارشى قولۇڭدىن ھېچ ئىش كەلمەيدۇ (يەنى ھېچ ئادەم ئۇنىڭدىن گۇمراھلىقنى دەپئى قىلىشقا قادىر بولالمايدۇ). دىللىرىنى (قىلمىشى قەبىھ بولغانلىقتىن كۇفرىدىن) پاك قىلىشنى ئاللاھ خالىمىغان كىشىلەر ئەنە شۇلاردۇر، ئۇلار دۇنيادا رەسۋالىققا، ئاخىرەتتە چوڭ ئازابقا قالىدۇ.');
INSERT INTO "saleh" VALUES(5,42,'5:42',' ئۇلار يالغانغا قۇلاق سالغۇچىلاردۇر، (پارا ۋە جازانىغا ئوخشاش) ھارامنى يېگۈچىلەردۇر. (ئى مۇھەممەد!) ئەگەر ئۇلار سېنىڭ ئالدىڭغا (دەۋالىشىپ) كەلسە، ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلىپ قويساڭ ياكى ئۇنى رەت قىلساڭ بولىدۇ (يەنى ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىش ياكى ئۇلاردىن يۈز ئۆرۈش سېنىڭ ئىختىيارىڭ)، ئەگەر ئۇلارنى رەت قىلساڭ، ئۇلار ساڭا قىلچە زىيان يەتكۈزەلمەيدۇ (چۈنكى ئاللاھ سېنى كىشىلەرنىڭ شەررىدىن ساقلايدۇ)، ئەگەر ئۇلارنىڭ ئارىسىدا ھۆكۈم قىلساڭ، ئادىللىق بىلەن ھۆكۈم قىلغىن، ئاللاھ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(5,43,'5:43',' ئۇلارنىڭ يېنىدا ئاللاھنىڭ ھۆكمىنى ئۆز ئىچىگە ئالغان تەۋرات تۇرسا (يەنى تەۋراتتىكى ھۆكۈملەرنى كۆرۈپ تۇرۇپ ئەمەل قىلمايۋاتسا)، (ئى مۇھەممەد!) قانداقچە سېنى ھۆكۈم چىقىرىشقا تەكلىپ قىلىدۇ؟ ئاندىن كېيىن (يەنى ھەقىقەت ئاشكارا بولغاندىن كېيىن) سېنىڭ ھۆكمىڭدىن يۈز ئۆرۈيدۇ، ئەنە شۇلار (تەۋراتقىمۇ، ساڭىمۇ) ئىشەنمىگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(5,44,'5:44',' بىز ھەقىقەتەن (مۇساغا) تەۋراتنى نازىل قىلدۇق، تەۋراتتا (توغرا يولغا يېتەكلەيدىغان) ھىدايەت ۋە نۇر بار. (ئاللاھنىڭ ھۆكمىگە) بويسۇنغان پەيغەمبەرلەر يەھۇدىيلار ئارىسىدا (تەۋرات بىلەن) ھۆكۈم قىلىدۇ. (يەھۇدىيلارنىڭ) زاھىتلىرى ۋە ئۆلىمالىرىمۇ (ئۆزگەرتىشتىن) ساقلاشقا بۇيرۇلغان كىتابۇللاھ (يەنى تەۋرات) بويىچە ھۆكۈم قىلىدۇ. ئۇلار تەۋراتنى (ئۆزگەرتىشتىن ساقلاشقا) نازارەتچى ئىدى. (ئى يەھۇدىيلارنىڭ ئۆلىمالىرى!) كىشىلەردىن قورقماڭلار، مەندىن قورقۇڭلار. مېنىڭ ئايەتلىرىمنى ئاز پۇلغا تېگىشمەڭلار (يەنى پۇل ـ مال، مەنسەپ ۋە پارا ئۈچۈن ئايەتلىرىمنىڭ ھۆكمىنى ئۆزگەرتىۋەتمەڭلار). كىملەركى ئاللاھ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمايدىكەن، ئۇلار كاپىرلاردۇر.');
INSERT INTO "saleh" VALUES(5,45,'5:45',' ئۇلار (يەنى ئىسرائىل ئەۋلادى) غا تەۋراتتا شۇنداق بەلگىلىدۇقكى، جانغا جان (يەنى ناھەق ئادەم ئۆلتۈرگەن ئۆلتۈرۈلىدۇ)، كۆزگە كۆز (يەنى كىشىنىڭ كۆزىنى ناھەق قۇيۇۋەتكەن ئادەمنىڭ كۆزى قۇيۇۋېتىلىدۇ)، بۇرۇنغا بۇرۇن (يەنى كىشىنىڭ بۇرنىنى ناھەق كەسكەننىڭ بۇرنى كېسىلىدۇ)، قۇلاققا قۇلاق (يەنى كىشىنىڭ قۇلىقىنى ناھەق كەسكەننىڭ قۇلىقى كېسىلىدۇ)، چىشقا چىش (يەنى كىشىنىڭ چىشىنى ناھەق تۆككەننىڭ چىشى تۆكۈلىدۇ) ۋە (كىشىنى) قانداق يارىدار قىلغان بولسا شۇنداق يارىدار قىلىنىش بىلەن قىساس ئېلىنىدۇ. كىمكى (جىنايەتچىنى) ئەپۇ قىلسا (يەنى ئۇنىڭدىن قىساس ئالمىسا)، بۇ ئۇنىڭ (گۇناھى) غا كەففارەت بولىدۇ، ئاللاھ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمايدىغان كىشىلەر زالىملاردۇر.');
INSERT INTO "saleh" VALUES(5,46,'5:46',' ئۇلارنىڭ (يەنى بەنى ئىسرائىل پەيغەمبەرلىرىنىڭ) ئارقىسىدىن ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى (يەنى ئۇنىڭ ئاللاھ تەرىپىدىن نازىل قىلىنغانلىقىنى) تەستىقلىغۇچى ئىيسا ئىبنى مەريەمنى ئەۋەتتۇق. ئۇنىڭغا ھىدايەت بىلەن نۇرنى ئۆز ئىچىگە ئالغان ئىنجىلنى ئاتا قىلدۇق، (ئىنجىل) ئۆزىدىن ئىلگىرى نازىل قىلىنغان تەۋراتنى تەستىقلىغۇچىدۇر (يەنى ئۇنىڭغا مۇۋاپىقتۇر)، تەقۋادارلارغا ھىدايەت ۋە پەند ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(5,47,'5:47','ئەھلى ئىنجىللار (يەنى ناسارالار) ئاللاھ ئىنجىلدا نازىل قىلغان ئەھكاملار بويىچە ھۆكۈم قىلسۇن، ئاللاھ نازىل قىلغان ئايەتلەر بويىچە ھۆكۈم قىلمىغانلار پاسىقلاردۇر. ');
INSERT INTO "saleh" VALUES(5,48,'5:48',' (ئى مۇھەممەد!) بىز ساڭا ئۆزىدىن ئىلگىرىكى (ساماۋى) كىتابلارنى تەستىقلىغۇچى ۋە ئۇلارغا شاھىت بولغۇچى ھەق كىتابنى (يەنى قۇرئاننى) نازىل قىلدۇق. (ئى مۇھەممەد!) ئۇلارنىڭ ئارىسىدا ئاللاھ ساڭا نازىل قىلغان قۇرئان (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ساڭا كەلگەن ھەقتىن بۇرۇلۇپ، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن. (ئى ئۈممەتلەر!) سىلەرنىڭ ھەر بىرىڭلارغا بىرخىل شەرىئەت ۋە ئوچۇق يول تەيىن قىلدۇق. ئەگەر ئاللاھ خالىسا، ئەلۋەتتە سىلەرنى بىر ئۈممەت قىلاتتى (يەنى پۈتۈن ئىنسانلارنى بىر دىندا قىلاتتى). لېكىن ئاللاھ سىلەرگە بەرگەن شەرىئەتلەر بارىسىدا سىلەرنى سىناش ئۈچۈن (كۆپ ئۈممەت قىلىپ ئايرىدى). ياخشى ئىشلارغا ئالدىراڭلار. ھەممىڭلار ئاللاھنىڭ دەرگاھىغا قايتىسىلەر، سىلەر ئىختىلاپ قىلىشقان نەرسىلەرنى (ئۇنىڭ قايسى ھەق، قايسى ناھەق ئىكەنلىكىنى) ئاللاھ سىلەرگە ئېيتىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(5,49,'5:49','ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) نىڭ ئارىسىدا ئاللاھ ساڭا نازىل قىلغان قۇرئاننىڭ (ئەھكامى) بويىچە ھۆكۈم قىلغىن، ئۇلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن، ئۇلارنىڭ ئاللاھ ساڭا نازىل قىلغان قۇرئاننىڭ بىر قىسمىدىن سېنى ۋاز كەچتۈرۈشىدىن ساقلانغىن. ئەگەر ئۇلار (ئاللاھ ساڭا نازىل قىلغان ھۆكۈمدىن) يۈز ئۆرۈسە، (ئى مۇھەممەد!) بىلگىنكى، ئۇلارنىڭ بىر قىسىم گۇناھلىرى تۈپەيلىدىن ئاللاھ ئۇلارنى جازالاشنى ئىرادە قىلىدۇ. شەك ـ شۈبھىسىزكى، كىشىلەردىن نۇرغۇنى پاسىقتۇر (يەنى پەرۋەردىگارىنىڭ تائىتىدىن باش تارتىپ، ھەققە خىلاپلىق قىلىپ گۇناھقا چۆمگۈچىلەردۇر).');
INSERT INTO "saleh" VALUES(5,50,'5:50','ئۇلار جاھىلىيەت دەۋرىنىڭ ھۆكمىنى تەلەپ قىلامدۇ؟ (ئاللاھقا) چىن ئىشىنىدىغان قەۋمنىڭ نەزىرىدە ھۆكۈمدە ئاللاھتىنمۇ ئادىل كىم بار؟');
INSERT INTO "saleh" VALUES(5,51,'5:51','ئى مۆمىنلەر! يەھۇدىي ۋە ناسارالارنى ۋەلى قىلىۋالماڭلار. ئۇلار بىر ـ بىرىنىڭ ۋەلىلىرىدۇر. سىلەردىن كىمكى ئۇلارنى ۋەلى قىلىۋالىدىكەن، ئۇمۇ شۇلاردىن سانىلىدۇ، ئاللاھ زالىم قەۋمنى ھەقىقەتەن ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(5,52,'5:52','دىللىرىدا كېسەللىك (يەنى مۇناپىقلىق) بارلارنىڭ (زامان ئۆزگىرىپ كاپىرلار زەپەر تاپسا) ئۆزىمىزگە بىر پالاكەتچىلىك كېلىشىدىن قورقىمىز دەپ، ئۇلار بىلەن دوست بولۇشقا ئالدىرىغانلىقىنى كۆرىسەن. ئاللاھ (رەسۇلۇللاھقا، مۇسۇلمانلارغا) غەلىبە (يەنى مەككىنى پەتھى قىلىشنى) ئاتا قىلىدۇ ياكى بىر ئىشنى (يەنى ئۇلارنىڭ مۇناپىقلىقىنى پاش قىلىشنى) مەيدانغا كەلتۈرىدۇ ـ دە، كۆڭلىدە يوشۇرغىنى (يەنى مۆمىنلەرنىڭ دۈشمەنلىرىنى دوست تۇتقانلىقى) ئۈچۈن ئۇلار نادامەت چېكىدۇ.');
INSERT INTO "saleh" VALUES(5,53,'5:53','(بۇ مۇناپىقلارنىڭ مۇسۇلمانلارغا قىلغان ھىيلە ـ مىكرى پاش قىلىنغاندا) مۆمىنلەر (بىر ـ بىرىگە): «سىلەر بىلەن چوقۇم بىللە ئىكەنلىكلىرىگە ئاللاھ نامى بىلەن قاتتىق قەسەم ئىچىشكەنلەر مۇشۇ كىشىلەرمۇ؟» دەيدۇ. ئۇلارنىڭ (يەنى مۇناپىقلارنىڭ) قىلغان ئەمەللىرى بىكار بولدى، ئۇلار (دۇنيا ۋە ئاخىرەتتە) زىيان تارتقۇچى بولدى. ');
INSERT INTO "saleh" VALUES(5,54,'5:54',' ئى مۆمىنلەر! سىلەردىن كىمكى مۇرتەد بولىدىكەن، ئاللاھ (ئۇنىڭ ئورنىغا) ئاللاھ ئۇلارنى دوست تۇتىدىغان، ئۇلارمۇ ئاللاھنى دوست تۇتىدىغان، مۆمىنلەرگە كۆيۈنىدىغان، كاپىرلارغا شەپقەتسىز، ئاللاھنىڭ يولىدا جىھاد قىلىدىغان ۋە مالامەت قىلغۇچىنىڭ مالامىتىدىن قورقمايدىغان بىر قەۋمنى كەلتۈرىدۇ. بۇ (يەنى يۇقىرىدىكى سۈپەتلەر بىلەن سۈپەتلىنىش) ئاللاھنىڭ پەزلىدۇركى، (ئاللاھ) ئۇنى (بەندىلىرىدىن) خالىغان كىشىگە بېرىدۇ، ئاللاھ (نىڭ پەزلى) كەڭدۇر، (ئاللاھ) ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(5,55,'5:55',' شۈبھىسىزكى، سىلەرنىڭ دوستۇڭلار ئاللاھتۇر، ئاللاھنىڭ رەسۇلىدۇر ۋە نامازنى تەلەپكە لايىق ئۆتەيدىغان، زاكات بېرىدىغان ۋە (ئاللاھنىڭ ئەمرىگە) كەمتەرلىك بىلەن بويسۇىدىغان مۆمىنلەردۇر .');
INSERT INTO "saleh" VALUES(5,56,'5:56',' كىمكى ئاللاھنى، ئاللاھنىڭ پەيغەمبىرىنى ۋە مۆمىنلەرنى دوست تۇتىدىكەن، (ئۇ ئاللاھنىڭ جامائەسىدىندۇر). ئاللاھنىڭ جامائەسى (ئەلۋەتتە) غالىبتۇر.');
INSERT INTO "saleh" VALUES(5,57,'5:57','ئى مۆمىنلەر! سىلەردىن ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دىن دىنىڭلارنى مەسخىرە ۋە ئويۇنچۇق قىلغانلارنى دوست قىلىۋالماڭلار. ئەگەر (ھەقىقىي) مۆمىن بولساڭلار، ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(5,58,'5:58','سىلەر نامازغا چاقىرغان (يەنى ئەزان ئېيتقان) چېغىڭلاردا، ئۇلار نامازنى (ھەم سىلەرنى) مەسخىرە ۋە ئويۇنچۇق قىلىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (نامازنىڭ ھېكمىتىنى ۋە نامازنىڭ نەپسىنى پاكلاشتىكى غايىسىنى) چۈشەنمەس قەۋمدۇر.');
INSERT INTO "saleh" VALUES(5,59,'5:59','(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىي ۋە ناسارالار جامائەسى) پەقەت بىزنىڭ ئاللاھقا، بىزگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا)، بۇنىڭدىن ئىلگىرى (پەيغەمبەرلەرگە) نازىل قىلىنغان كىتابلارغا ئىمان ئېيتقانلىقىمىز ئۈچۈن ۋە سىلەرنىڭ كۆپچىلىكىڭلار پاسىق بولغانلىقىڭلار ئۈچۈن بىزنى ئەيىبلەمسىلەر؟»');
INSERT INTO "saleh" VALUES(5,60,'5:60','(ئى مۇھەممەد!) ئېيتقىنكى، «سىلەرگە ئاللاھنىڭ نەزىرىدىكى بۇنىڭدىنمۇ يامان جازانى ئېيتىپ بېرەيمۇ؟ (ئۇ شۇنداق) كىشىلەرنىڭ (جازاسىكى)، ئاللاھ ئۇلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا غەزەپ قىلدى. ئاللاھ ئۇلارنىڭ بىر قىسمىنى مايمۇن ۋە چوشقا قىلدى، بىر قىسمىنى شەيتانغا ئىبادەت قىلىدىغان قىلدى، ئەنە شۇلارنىڭ ئورنى ئەڭ ياماندۇر (يەنى دوزاختۇر)، (ئۇلار) توغرا يولدىن ئەڭ ئاداشقاندۇر».');
INSERT INTO "saleh" VALUES(5,61,'5:61',' (يەھۇدىيلارنىڭ مۇناپىقلىرى) يېنىڭلارغا كەلگەن چاغدا، بىز ئىمان ئېيتتۇق، دەيدۇ. ۋاھالەنكى، ئۇلار (ئى مۇھەممەد! يېنىڭغا) كۇفرى بىلەن كىرىپ كۇفرى بىلەن چىقىپ كەتتى (يەنى سەندىن ئاڭلىغان ئىلىمدىن پايدىلانمىدى). ئاللاھ ئۇلارنىڭ يوشۇرغان نەرسىسىنى (يەنى نىفاقىنى ۋە كۇفرىنى) ئوبدان بىلىدۇ. ');
INSERT INTO "saleh" VALUES(5,62,'5:62','ئۇلار (يەنى يەھۇدىيلار) نىڭ ئارىسىدىكى نۇرغۇن كىشىلەرنىڭ گۇناھقا، زۇلۇم سېلىشقا ۋە ھارام يېيىشكە ئالدىرايدىغانلىقىنى كۆرىسەن. ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(5,63,'5:63','ئۇلارنىڭ زاھىتلىرى، ئۆلىمالىرى نېمىشقا ئۇلارنى يالغان سۆزلەشتىن، ھارام يېيىشتىن توسمايدۇ؟ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(5,64,'5:64','يەھۇدىيلار: «ئاللاھنىڭ قولى باغلاقلىقتۇر (يەنى ئاللاھ بېخىلدۇر ياكى پېقىردۇر)» دېدى. يەھۇدىيلارنىڭ قوللىرى باغلانسۇن! ئۇلار بۇ سۆزلىرى سەۋەبلىك ئاللاھنىڭ رەھمىتىدىن يىراق قىلىندى، ئەمەلدە ئاللاھنىڭ ئىككى قولى ئوچۇقتۇر، قانداق خالىسا شۇنداق رىزىق بېرىدۇ، پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان قۇرئان ئۇلارنىڭ كۆپچىلىكىدە يامانلىق بىلەن كۇفرىنى ئاشۇرىدۇ (يەنى ئۇلارنىڭ ساڭا دۈشمەنلىكىنى ۋە قۇرئاننى ئىنكار قىلىشىنى زىيادە قىلىدۇ). بىز ئۇلارنىڭ ئارىسىغا قىيامەتكىچە داۋاملىشىدىغان ئۆچمەنلىك ۋە دۈشمەنلىكنى سالدۇق، ئۇلار ھەر قاچان (رەسۇلۇللاھقا قارشى) ئۇرۇش ئوتىنى ياقماقچى بولسا، ئاللاھ ئۇنى ئۆچۈرىدۇ، ئۇلار يەر يۈزىدە (ئىسلامغا سۇيىقەست قىلىش ۋە مۇسۇلمانلار ئارىسىدا پىتنە قوزغاش بىلەن) بۇزغۇنچىلىق قىلىپ يۈرۈيدۇ، ئاللاھ بۇزغۇنچىلىق قىلغۇچىلارنى ياقتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(5,65,'5:65','ئەگەر ئەھلى كىتاب (يەنى يەھۇدىيلار ۋە ناسارالار) (ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ھەقىقىي) ئىمان ئېيتسا ۋە (ئاللاھ چەكلىگەن ئىشلاردىن) پەرھىز قىلسا ئىدى، ئۇلارنىڭ گۇناھلىرىنى بىز چوقۇم كەچۈرەتتۇق، ئۇلارنى چوقۇم نازۇ-نېمەتلىك جەننەتكە كىرگۈزەتتۇق.');
INSERT INTO "saleh" VALUES(5,66,'5:66','ئەگەر ئۇلار تەۋراتقا، ئىنجىلغا ۋە پەرۋەردىگارى تەرىپىدىن ئۇلارغا نازىل قىلىنغان باشقا كىتابلارغا ئەمەل قىلسا ئىدى، ئۇلار چوقۇم ئۈستىلىرىدىن ۋە ئاياغلىرى ئاستىدىن يېيىشەر ئىدى (يەنى ئۇلارغا ئاسمان ـ زېمىندىن كەڭ رىزىق بېرىلەتتى)، ئۇلاردىن بىر جامائە (يەنى مۇھەممەد ئەلەيھىسسالامغا ئىمان ئېيتقانلار) توغرا يولدىدۇر. ئۇلارنىڭ نۇرغۇنلىرى نېمىدېگەن يامان ئىش قىلغۇچىلار!');
INSERT INTO "saleh" VALUES(5,67,'5:67',' ئى پەيغەمبەر! پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان ئەھكاملارنىڭ ھەممىسىنى يەتكۈزگىن، ئەگەر تولۇق يەتكۈزمىسەڭ، ئاللاھ تاپشۇرغان ۋەزىپىنى ئادا قىلمىغان بولىسەن. ئاللاھ سېنى كىشىلەرنىڭ زىيانكەشلىكىدىن ساقلايدۇ. ئاللاھ ھەقىقەتەن كاپىر قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(5,68,'5:68','(ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) سىلەر تەۋراتقا، ئىنجىلغا ۋە سىلەرگە پەرۋەردىگارىڭلاردىن نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) تولۇق ئەمەل قىلمىغىچە، سىلەر ئېتىبارغا ئالغۇدەك دىندا بولغان بولمايسىلەر». پەرۋەردىگارىڭدىن نازىل قىلىنغان ئەھكاملار ئۇلارنىڭ كۆپچىلىكىدە يامانلىق ۋە كۇفرىنى ئاشۇرىدۇ، كاپىر قەۋم ئۈچۈن قايغۇرمىغىن (چۈنكى پەيغەمبەرلەرنى يالغانغا چىقىرىش ئۇلارنىڭ ئادىتىدۇر).');
INSERT INTO "saleh" VALUES(5,69,'5:69',' مۆمىنلەردىن، يەھۇدىيلاردىن، پەرىشتىلەرگە ۋە يۇلتۇزلارغا چوقۇنغۇچىلاردىن، ناسارالاردىن ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا قورقۇنچ يوقتۇر، ئۇلار غەم- ئەندىشە قىلمايدۇ .');
INSERT INTO "saleh" VALUES(5,70,'5:70','بىز بەنى ئىسرائىلدىن ھەقىقەتەن چىن ئەھدە ئالدۇق. ئۇلارغا (دىننىڭ ئىشلىرىنى بايان قىلىپ بېرىش ئۈچۈن) پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا ھەر قاچان بىرەر پەيغەمبەر ئۇلار ياقتۇرمايدىغان ئەھكامنى ئېلىپ كەلسە، (ئۇلار پەيغەمبەرلەرنىڭ) بىر بۆلۈكىنى يالغانچى قىلدى، بىر بۆلۈكىنى ئۆلتۈردى.');
INSERT INTO "saleh" VALUES(5,71,'5:71','ئۇلار، (بىز ئىسرائىل ئەۋلادى پەيغەمبەرلەرنى ئۆلتۈرۈش بىلەن) ئازابقا دۇچار بولمايمىز، دەپ ئويلىدى. ئۇلار كور بولدى (يەنى ھەقىقەتنى كۆرمىدى)، ئۇلار گاس بولدى (يەنى ھەقىقەتنى ئاڭلىمىدى)، ئاندىن كېيىن (ئۇلار تەۋبە قىلدى)، ئاللاھ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى. ئاندىن ئۇلارنىڭ نۇرغۇنى يەنە (ھەقنى كۆرۈشتىن) كور بولدى، يەنە (پەند ـ نەسىھەت ئاڭلاشتىن) گاس بولدى، ئاللاھ ئۇلارنىڭ قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(5,72,'5:72',' شۈبھىسىزكى، ئاللاھ مەريەمنىڭ ئوغلى ئىيسادۇر، دېگۈچىلەر كاپىر بولدى، ئىيسا: «ئى بەنى ئىسرائىل! مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان ئاللاھقا ئىبادەت قىلىڭلار، كىمكى ئاللاھقا شېرىك كەلتۈرىدىكەن (يەنى ئاللاھتىن غەيرىيگە ئىلاھ دەپ ئېتىقاد قىلىدىكەن)، ئاللاھ ئۇنىڭغا جەننەتنى ھارام قىلىدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ، زالىملارغا ھېچبىر مەدەتكار (يەنى ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرغۇچى) بولمايدۇ» دېدى.');
INSERT INTO "saleh" VALUES(5,73,'5:73','ئاللاھ ئۈچ ئىلاھنىڭ بىرىدۇر، دېگۈچىلەر شەكسىز كاپىر بولدى. بىر ئىلاھتىن باشقا ھېچ ئىلاھ يوقتۇر. ئەگەر ئۇلار ئېيتىۋاتقان سۆزلىرىدىن قايتمىسا، ئۇلارنىڭ ئىچىدىكى كاپىر بولغانلار (دۇنيا ۋە ئاخىرەتتە) قاتتىق ئازابقا قالىدۇ.');
INSERT INTO "saleh" VALUES(5,74,'5:74','ئۇلار ئاللاھقا تەۋبە قىلمامدۇ؟ ئاللاھتىن مەغپىرەت تەلەپ قىلمامدۇ؟ ئاللاھ (تەۋبە قىلغۇچىلارغا) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(5,75,'5:75','مەريەم ئوغلى ئىيسا پەقەت (ئۆتكەن پەيغەمبەرلەرگە ئوخشاش) پەيغەمبەردۇر، ئۇنىڭدىن ئىلگىرى نۇرغۇن پەيغەمبەرلەر ئۆتكەن، ئۇنىڭ ئانىسى ناھايىتى راستچىل ئايالدۇر، ئۇ ئىككىسى تاماق يەيتتى (يەنە ئۇلارمۇ باشقا مەخلۇقلارغا ئوخشاشلا گۆش، سۆڭەك، تومۇر ۋە پەيلەردىن تەركىب تاپقان ئىدى). ئۇلارغا (يەنى ئۇلارنىڭ ئېتىقادىنىڭ باتىللىقىغا) ئايەتلىرىمىزنى قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن؛ ئاندىن ئۇلارنىڭ ھەقتىن قانداق باش تارتقانلىقىغىمۇ قارىغىن.');
INSERT INTO "saleh" VALUES(5,76,'5:76',' (ئى مۇھەممەد!) «سىلەر ئاللاھتىن باشقا، سىلەرگە پايدا ـ زىيان يەتكۈزۈشكە قادىر بولالمايدىغان نەرسىلەرگە چوقۇنامسىلەر؟» دېگىن. ئاللاھ بولسا (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(5,77,'5:77','(ئى مۇھەممەد!) »ئى ئەھلى كىتاب! (يەنى يەھۇدىيلار ۋە ناسارالار) دىنىڭلاردا ھەقسىز رەۋىشتە چەكتىن ئېشىپ كەتمەڭلار، ئىلگىرى ئۆزلىرى ئازغان، نۇرغۇن كىشىلەرنى ئازدۇرغان ۋە توغرا يولدىن ئاداشقان قەۋمنىڭ نەپسى - خاھىشلىرىغا ئەگەشمەڭلار» دېگىن.');
INSERT INTO "saleh" VALUES(5,78,'5:78','بەنى ئىسرائىلدىن كاپىر بولغانلارغا داۋۇدنىڭ ۋە مەريەم ئوغلى ئىيسانىڭ تىلى بىلەن (يەنى زەبۇردا ۋە ئىنجىلدا) لەنەت قىلىندى. بۇ (يەنى ئۇلارنىڭ لەنەتكە ئۇچرىشى) ئۇلارنىڭ ئاسىيلىق قىلغانلىقلىرى ۋە ھەددىدىن ئېشىپ كەتكەنلىكلىرىدىن بولدى. ');
INSERT INTO "saleh" VALUES(5,79,'5:79','ئۇلار ئۆزلىرى قىلغان يامان ئىشلاردىن بىر ـ بىرىنى توسمايتتى؛ ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان! ');
INSERT INTO "saleh" VALUES(5,80,'5:80','ئۇلار (يەنى يەھۇدىيلار) نىڭ ئىچىدە نۇرغۇنلىرىنىڭ كاپىرلارنى (يەنى رەسۇلۇللاھنى ۋە مۇسۇلمانلارنى ئۆچ كۆرۈش يۈزىسىدىن مۇشرىكلارنى) دوست تۇتقانلىقىنى كۆرىسەن. ئۇلارنىڭ ئۆزلىرى ئۈچۈن ئالدىن تەييارلىغان ئەمەللىرى نېمىدېگەن يامان! (بۇ ئەمەللەر) ئۇلارغا ئاللاھنىڭ غەزىپىنى ئېلىپ كەلدى. ئۇلار مەڭگۈ ئازابقا قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(5,81,'5:81','ئەگەر ئۇلار ئاللاھقا، پەيغەمبەرگە ۋە پەيغەمبەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتسا ئىدى، كاپىرلارنى دوست تۇتمايتتى. لېكىن ئۇلارنىڭ تولىسى پاسىقلاردۇر.');
INSERT INTO "saleh" VALUES(5,82,'5:82','(ئى مۇھەممەد!) يەھۇدىيلار ۋە مۇشرىكلارنىڭ مۆمىنلەرگە ھەممىدىن قاتتىق دۈشمەن ئىكەنلىكىنى چوقۇم بايقايسەن. بىز ناسارا دېگەن كىشىلەرنىڭ دوستلۇق جەھەتتە مۆمىنلەرگە ھەممىدىن يېقىن ئىكەنلىكىنىمۇ چوقۇم بايقايسەن. بۇ، ناسارالارنىڭ ئىچىدە ئۆلىمالار، راھىبلار بولغانلىقى ۋە (ھەقنى قوبۇل قىلىشتا) تەكەببۇرلۇق قىلمايدىغانلىقلىرى ئۈچۈندۇر. ');
INSERT INTO "saleh" VALUES(5,83,'5:83','ئۇلار پەيغەمبەرگە نازىل قىلىنغان قۇرئاننى ئاڭلىغان چاغلىرىدا ھەقىقەتنى تونۇغانلىقلىرىدىن، ئۇلارنىڭ كۆزلىرىدىن ياش قۇيۇلغانلىقىنى كۆرىسەن، ئۇلار ئېيتىدۇ: «رەببىمىز، بىز ئىمان ئېيتتۇق، بىزنى (پەيغەمبىرىڭنى، كىتابىڭنى) ئېتىراپ قىلغۇچىلار قاتارىدا قىلغىن.');
INSERT INTO "saleh" VALUES(5,84,'5:84','بىز نېمىشقا ئاللاھقا، بىزگە كەلگەن ھەقىقەتكە ئىشەنمەيلى؟ ھالبۇكى، بىز پەرۋەردىگارىمىزنىڭ بىزنى ياخشى كىشىلەر قاتارىدا (جەننەتكە) كىرگۈزۈشنى ئۈمىد قىلىمىز». ');
INSERT INTO "saleh" VALUES(5,85,'5:85','بۇ سۆزلىرى ئۈچۈن ئاللاھ ئۇلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بىلەن مۇكاپاتلايدۇ. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ياخشىلىق قىلغۇچىلارغا بېرىلىدىغان مۇكاپات ئەنە شۇ.');
INSERT INTO "saleh" VALUES(5,86,'5:86',' كاپىر بولغانلار ۋە بىزنىڭ ئايەتلىرىمىزنى يالغانغا چىقارغانلار دوزاخ ئەھلىدۇر.');
INSERT INTO "saleh" VALUES(5,87,'5:87','ئى مۆمىنلەر! ئاللاھ سىلەرگە ھالال قىلغان پاك نەرسىلەرنى (تەركى دۇنيا بولۇش يۈزىسىدىن ئۆزۈڭلارغا) ھارام قىلماڭلار، (ئاللاھ بەلگىلەپ بەرگەن) چەكتىن ئاشماڭلار، ئاللاھ چەكتىن ئاشقۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(5,88,'5:88','ئاللاھ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، پاك نەرسىلەردىن يەڭلار، سىلەر ئىمان ئېيتقان ئاللاھقا تەقۋادارلىق قىلىڭلار.');
INSERT INTO "saleh" VALUES(5,89,'5:89','ئاللاھ سىلەرنى سەۋەنلىك بىلەن قىلغان قەسىمىڭلار ئۈچۈن جاۋابكارلىققا تارتمايدۇ، لېكىن سىلەرنى قەستەن قىلغان قەسىمىڭلار ئۈچۈن جاۋاپكارلىققا تارتىدۇ. (مۇنداق قەسىمىڭلارنى بۇزساڭلار) ئۇنىڭ كەففارىتى ئائىلەڭلارغا بېرىدىغان ئوتتۇرا دەرىجىلىك تاماق بىلەن ئون مىسكىننى بىر ۋاخ غىزالاندۇرۇشتۇر ياكى ئۇلارغا (يەنى ئون مىسكىنگە بەدىنىنى يېپىپ تۇرىدىغان) بىر قۇر كىيىم بېرىشتۇر ياكى بىر قۇل ياكى بىر چۆرىنى ئازاد قىلىشتۇر. كىمكى بۇنداق قىلىشقا كۈچى يەتمىسە، ئۈچ كۈن روزا تۇتۇشى لازىم. بۇ ئەنە شۇ ئىچكەن قەسىمىڭلارنى بۇزغانلىقىڭلارنىڭ كەففارىتىدۇر، قەسىمىڭلارغا رىئايە قىلىڭلار (يەنى قەستىن قىلغان قەسىمىڭلارنى ئورۇنداڭلار ياكى كەففارىتىنى ئادا قىلىڭلار). سىلەرنىڭ شۈكۈر قىلىشىڭلار ئۈچۈن ئاللاھ ئايەتلىرىنى سىلەرگە ئەنە شۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(5,90,'5:90','ئى مۆمىنلەر! ھاراق ئىچىش، قىمار ئويناش، بۇتلار (يەنى چوقۇنۇش ئۈچۈن تىكلەنگەن تاشلار) غا چوقۇنۇش، پال ئوقلىرى بىلەن پال سېلىش شەيتاننىڭ ئىشى، پاسكىنا قىلىقلاردۇر، بەختكە ئېرىشىشىڭلار ئۈچۈن شەيتاننىڭ ئىشىدىن يىراق بولۇڭلار.');
INSERT INTO "saleh" VALUES(5,91,'5:91','شەيتان ھاراق، قىمار ئارقىلىق ئاراڭلاردا دۈشمەنلىك، ئاداۋەت تۇغدۇرماقچى، سىلەرنى نامازدىن ۋە ئاللاھنى ياد ئېتىشتىن توسماقچى بولىدۇ، سىلەر ئەمدى (ھاراقتىن، قىماردىن) يانمامسىلەر؟.');
INSERT INTO "saleh" VALUES(5,92,'5:92','ئاللاھقا ۋە پەيغەمبەرگە ئىتائەت قىلىڭلار، (ئۇلارغا مۇخالىپەتچىلىك قىلىشتىن) ھەزەر قىلىڭلار، ئەگەر (ئىتائەت قىلىشتىن) باش تارتساڭلار، بىلىڭلاركى، رەسۇلىمىزنىڭ مەسئۇلىيىتى پەقەت ئوچۇق تەبلىغ قىلىشتۇر.');
INSERT INTO "saleh" VALUES(5,93,'5:93',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەر (ھارام قىلىنمىغان نەرسىلەرنى) يېسە، ئىچسە ھېچ گۇناھ بولمايدۇ، قاچانكى ئۇلار (ھارام نەرسىلەردىن) ساقلانسا، ئىمانىدا ۋە ياخشى ئەمەللىرىدە ئىزچىل بولسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن) يەنە ساقلانسا ۋە ھارام دەپ ئېتىقاد قىلسا، ئاندىن (ھارام قىلىنغان نەرسىلەردىن داۋاملىق) ساقلانسا ۋە ياخشى ئىشلارنى قىلسا، ئاللاھ ياخشى ئىش قىلغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(5,94,'5:94',' ئى مۆمىنلەر! كۆرمەي تۇرۇپ ئاللاھتىن قورقىدىغانلارنى بىلىش ئۈچۈن، قولۇڭلار ۋە نەيزەڭلار بىلەن ئوۋلىنىدىغان بىر ئاز ئوۋ بىلەن ئاللاھ سىلەرنى چوقۇم سىنايدۇ، كىمكى بۇنىڭدىن كېيىن ھەددىدىن ئاشسا (يەنى ئىھرامدا تۇرۇپ ئوۋ ئوۋلىسا)، ئۇ قىينىغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(5,95,'5:95',' ئى مۆمىنلەر! سىلەر ئوۋلىغان ھايۋانلارنى ئىھرامدا ياكى ھەرەمدە تۇرۇپ ئۆلتۈرمەڭلار. سىلەردىن كىمكى ئۇنى قەستەن ئۆلتۈرىدىكەن، ئىشنىڭ ۋابالىنى تېتىشى ئۈچۈن، ئۇنىڭ جازاسى ئىككى ئادىل كىشىنىڭ باھالىشى بويىچە ھېلىقى ئۆلتۈرۈلگەن ئوۋغا ئوخشاش ئۆي ھايۋانلىرىدىن بىرنى كەبىگە ئېلىپ بېرىپ قۇربانلىق قىلىشتۇر ياكى كەففارەت بېرىش يۈزىسىدىن مىسكىنلەرگە ئاش بېرىشتۇر ياكى ھەر مىسكىنگە بېرىدىغان ئاشنىڭ باراۋىرىگە بىر كۈن روزا تۇتۇشتۇر. ئۆتكەنكى گۇناھنى (يەنى بۇ ئايەت چۈشۈشتىن بۇرۇن ئىھرامدا تۇرۇپ ئوۋ ئۆلتۈرگەن بولساڭلار، بۇنى) ئاللاھ ئەپۇ قىلدى، كىمكى قايتا گۇناھ ئۆتكۈزسە (يەنى قايتىدىن ئوۋ ئۆلتۈرسە)، ئاللاھ ئۇنى جازالايدۇ، ئاللاھ غالىبتۇر، (ئاسىيلىق قىلغۇچىلارنى) جازالىغۇچىدۇر.');
INSERT INTO "saleh" VALUES(5,96,'5:96','سىلەرنىڭ ۋە يولۇچىلارنىڭ مەنپەئەتلىنىشى ئۈچۈن سىلەرگە دېڭىز ھايۋانلىرى، دېڭىزدىن چىقىدىغان يېمەكلىكلەر ھالال قىلىندى، مادامىكى سىلەر ئېھرامدا ياكى ھەرەمدە ئىكەنسىلەر، قۇرۇقلۇق ھايۋانلىرىنى ئوۋلاش سىلەرگە ھارام قىلىندى، سىلەر (قىيامەت كۈنى) دەرگاھىغا توپلىنىدىغان ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(5,97,'5:97',' ئاللاھ بەيتۇلھەرەم بولغان كەبىنى، ھەج ئېيىنى، ھەجدە قىلىنىدىغان قۇربانلىقنى، (قۇربانلىقنىڭ ئالامىتى سۈپىتىدە) بويۇنلىرىغا بەلگە ئېسىلغان تۆگىلەرنى ئىنسانلارنىڭ دۇنيا ۋە ئاخىرەتلىك ئىشلىرىنىڭ تۇتقۇسى قىلدى. بۇ، ئاللاھنىڭ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدىغانلىقىنى ۋە ئاللاھنىڭ ھەر شەيئىنى بىلگۈچى ئىكەنلىكىنى سىلەرنىڭ بىلىشىڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(5,98,'5:98','بىلىڭلاركى، ئاللاھنىڭ ئازابى قاتتىقتۇر، (ئىتائەت قىلغۇچىلارغا) ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(5,99,'5:99',' پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت تەبلىغ قىلىشتۇر. ئاللاھ سىلەرنىڭ ئاشكارا ۋە يوشۇرۇن قىلىپ يۈرگەن ئىشلىرىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(5,100,'5:100',' (ئى مۇھەممەد!) ئېيتقىنكى، گەرچە ھارامنىڭ كۆپلۈكى (ئى تىڭشىغۇچى) سېنى ئەجەبلەندۈرسىمۇ، ھارام بىلەن ھالال باراۋەر ئەمەس. ئى ئەقىل ئىگىلىرى! نىجات تېپىشىڭلار ئۈچۈن ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(5,101,'5:101','ئى مۆمىنلەر! ئەگەر سىلەرگە ئاشكارا قىلىنسا سىلەرنى بىئارام قىلىپ قويىدىغان نەرسىلەر توغرۇلۇق (پەيغەمبەردىن) سورىماڭلار. ئەگەر قۇرئان نازىل قىلىنىۋاتقان چاغدا ئۇلار توغرۇلۇق سورىساڭلار، سىلەرگە بىلدۈرۈلىدۇ، ئۆتكەندە سورىغانلىرىڭلارنى ئاللاھ ئەپۇ قىلدى. ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ھەلىمدۇر (يەنى ئاسىيلىق قىلغۇچىلارنى جازالاشقا ئالدىراپ كەتمەيدۇ). ');
INSERT INTO "saleh" VALUES(5,102,'5:102','سىلەردىن بۇرۇن ئۆتكەن بىر قەۋم (پەيغەمبەرلىرىدىن) شۇنداق مەسىلىلەرنى سورىغان ئىدى، كېيىن بۇنىڭ سەۋەبىدىن (يەنى سورىغان ئىشلىرى بايان قىلىنىپ ئۇلار ئەمەل قىلمىغانلىقتىن) كاپىر بولۇپ كەتتى.');
INSERT INTO "saleh" VALUES(5,103,'5:103','بەھىرە ، سائىبە ، ۋەسىلە ، ھام لارغا ئاللاھ (يۇقىرىقىدەك قىلىشنى) بۇيرۇغىنى يوق، (لېكىن كاپىرلار ئاللاھقا يالغاننى چاپلايدۇ، ئۇلارنىڭ تولىسى بۇنىڭ يالغانلىقىنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(5,104,'5:104','ئۇلارغا ئاللاھ نازىل قىلغان ھۆكۈمگە، پەيغەمبەر بايان قىلغان ھۆكۈمگە ئەمەل قىلىڭلار دېيىلسە، ئۇلار: «ئاتا ـ بوۋىلىرىمىزنىڭ دىنى بىزگە كۇپايە» دەيدۇ. ئۇلارنىڭ ئاتا ـ بوۋىلىرى ھېچ نەرسە بىلمەيدىغان ۋە ھىدايەت تاپمىغان تۇرسىمۇ (يەنىلا ئۇلارنىڭ دىنىغا ئەمەل قىلامدۇ؟).');
INSERT INTO "saleh" VALUES(5,105,'5:105',' ئى مۆمىنلەر! ئۆزۈڭلارنى گۇناھتىن ساقلاڭلار، سىلەر قاچانىكى توغرا يولدا بولساڭلار، باشقىلارنىڭ ئاداشقىنى سىلەرگە زىيان يەتكۈزەلمەيدۇ. سىلەر ھەممىڭلار ئاللاھنىڭ دەرگاھىغا قايتىسىلەر، ئاللاھ قىلغان ئەمەلىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(5,106,'5:106',' ئى مۆمىنلەر! ئاراڭلاردىن بىركىم ۋاپات بولۇش ئالدىدا ۋەسىيەت قىلماقچى بولسا، ئۇنىڭغا ئىچىڭلاردىن ئىككى ئادىل كىشى گۇۋاھ بولسۇن، ياكى (ئۆز دىنىڭلاردىكىلەردىن گۇۋاھ بولىدىغان ئىككى كىشى تېپىلمىسا) غەيرىي دىندىكىلەردىن ئىككى كىشى گۇۋاھ بولسۇن. ئەگەر سىلەر سەپەردە بولۇپ، بېشىڭلارغا ئۆلۈم دەھشىتى كەلگەندە، سىلەر (بۇ ئىككى گۇۋاھچىنىڭ راستچىللىقىدىن) گۇمانلانساڭلار، نامازدىن كېيىن ئۇلارنى ئېلىپ قېلىڭلار، ئۇلار: «بىز پايدىسىغا قەسەم قىلىدىغان كىشىلەرمىز، بىزنىڭ يېقىنىمىز بولغان تەقدىردىمۇ قەسىمىمىزنى ھېچ نەرسىگە ساتمايمىز، خۇدالىق ئۈچۈن بولغان گۇۋاھلىقنى يوشۇرمايمىز. ئەگەر ئۇنى يوشۇرساق، بىز ئەلۋەتتە گۇناھكارلاردىن بولىمىز» دەپ ئاللاھنىڭ نامى بىلەن قەسەم قىلسۇن.');
INSERT INTO "saleh" VALUES(5,107,'5:107',' ئەگەر ئۇ ئىككى گۇۋاھچىنىڭ (يالغان گۇۋاھلىق بەرگەنلىك ياكى مىراسقا خىيانەت قىلغانلىق) گۇناھى بىلىنسە، ئۇلارنىڭ ئورنىغا مىراس ئېلىشقا ئەڭ ھەقلىق بولغان ۋارىسلاردىن ئىككى كىشى گۇۋاھ بولۇپ: «بىزنىڭ گۇۋاھلىقىمىز ئەلۋەتتە ئۇلارنىڭ گۇۋاھلىقىدىنمۇ توغرىدۇر، بىز شەرىئەتتىن چىقىپ كەتمىدۇق، بولمىسا بىز چوقۇم زالىملاردىن بولىمىز» دەپ ئاللاھنىڭ نامى بىلەن قەسەم قىلسۇن.');
INSERT INTO "saleh" VALUES(5,108,'5:108','بۇ (ئۇسۇل) گۇۋاھچىلارنى ئەڭ توغرا گۇۋاھلىق بېرىدىغان قىلىشقا، قىلغان قەسىمىنىڭ رەت قىلىنىشىدىن (يەنى ئۇلاردىن كېيىن باشقىلار قەسەم قىلىشى بىلەن ئۆزلىرىنىڭ رەسۋا بولۇشىدىن) قورقىدىغان قىلىشقا ئەڭ يېقىن (ئۇسۇل) دۇر. ئاللاھتىن قورقۇڭلار، (ئاللاھنىڭ ئەمرىنى) ئاڭلاڭلار. ئاللاھ پاسىق قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(5,109,'5:109','ئاللاھ قىيامەت كۈنى پەيغەمبەرلەرنى يىغىپ: «(قەۋمىڭلارنى ئىمانغا دەۋەت قىلغىنىڭلاردا) قانداق جاۋابقا ئىگە بولدۇڭلار؟» دەپ سورايدۇ، ئۇلار: «(سېنىڭ بىلگىنىڭگە سېلىشتۇرغاندا) بىز ھېچ نەرسە بىلمەيمىز، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن» دەيدۇ.');
INSERT INTO "saleh" VALUES(5,110,'5:110',' ئۆز ۋاقتىدا ئاللاھ ئېيتتى: ئى مەريەم ئوغلى ئىيسا! ساڭا ۋە سېنىڭ ئاناڭغا بەرگەن نېمىتىمنى ئەسلىگىن؛ ئەينى زاماندا ساڭا روھۇلقۇدۇس (يەنى جىبرىئىل) بىلەن مەدەت بەردىم، (كىچىكىڭدە) بۆشۈكتە ۋە (پەيغەمبەر بولغان) ئوتتۇرا ياش ۋاقتىڭدا كىشىلەرگە سۆزلەيتتىڭ. ئەينى زاماندا ساڭا كىتابنى، ھېكمەتنى، تەۋراتنى ۋە ئىنجىلنى ئۆگەتتىم. ئەينى زاماندا مېنىڭ ئىزنىم بىلەن لايدىن قۇشنىڭ شەكلىدە بىر نەرسە ياسايتتىڭ ـ دە، ئۇنى پۈۋلىسەڭ ئىزنىم بىلەن ئۇچىدىغان قۇش بولاتتى. مېنىڭ ئىزنىم بىلەن تۇغما كورنى، بەرەس كېسىلىنى ساقايتاتتىڭ. ئەينى زاماندا، مېنىڭ ئىزنىم بىلەن ئۆلۈكلەرنى (تىرىلدۈرۈپ قەبرىلىرىدىن) چىقىراتتىڭ. ئەينى زاماندا بەنى ئىسرائىلنى ساڭا چېقىلىشتىن توستۇم، سەن ئۇلارغا مۆجىزىلەر بىلەن كەلگەن چېغىڭدا، ئۇلارنىڭ ئىچىدىكى كاپىرلار: «بۇ پەقەت روشەن سېھىردۇر» دېدى.');
INSERT INTO "saleh" VALUES(5,111,'5:111',' ئەينى زاماندا مەن ھەۋارىيلارغا: «ماڭا ۋە مېنىڭ پەيغەمبىرىمگە ئىمان ئېيتىڭلار» دەپ ۋەھىي قىلدىم. ئۇلار: «بىز ئىمان ئېيتتۇق، گۇۋاھ بولغىنكى، بىز (ئەمرىڭگە) بويسۇنغۇچىلارمىز» دېدى.');
INSERT INTO "saleh" VALUES(5,112,'5:112','ئۆز ۋاقتىدا ھەۋارىيلار: «ئى مەريەم ئوغلى ئىيسا! رەببىڭ بىزگە ئاسماندىن ئۈستىدە تاماق بار داستىخان چۈشۈرۈپ بېرەلەمدۇ؟» دېدى. ئىيسا: «ئەگەر (ئاللاھنىڭ قۇدرىتىگە) ئىشەنسەڭلار (مۇنداق سوئاللارنى قويۇشتا) ئاللاھتىن قورقۇڭلار» دېدى.');
INSERT INTO "saleh" VALUES(5,113,'5:113','ئۇلار: «بىز ئۇنىڭدىن يېيىشنى، كۆڭلىمىزنىڭ تىنچلىنىشىنى، سۆزۈڭنىڭ راستلىقىنى بىلىشنى ۋە ئۇنىڭغا (يەنى داستىخانغا) گۇۋاھچىلاردىن بولۇشنى ئىرادە قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(5,114,'5:114',' مەريەم ئوغلى ئىيسا: «ئى پەرۋەردىگارىمىز ئاللاھ! بىزگە ئۈستىدە تاماق بار داستىخان چۈشۈرگىن، بۇ كۈن بىزلەرگە ۋە بىزدىن كېيىنكىلەرگە بايرام بولۇپ قالسۇن، ئۇ داستىخان سەندىن بىزگە (قۇدرىتىڭنى ۋە پەيغەمبەرلىكىمنى كۆرسىتىدىغان) بىر مۆجىزە بولۇپ قالسۇن. بىزگە رىزىق بەرگىن، سەن رىزىق بەرگۈچىلەرنىڭ ياخشىسىسەن» دېدى.');
INSERT INTO "saleh" VALUES(5,115,'5:115','ئاللاھ (ئىيسانىڭ دۇئاسىنى ئىجابەت قىلىپ): «ئۇنى مەن سىلەرگە چوقۇم (ئاسماندىن) چۈشۈرىمەن، سىلەردىن كىمكى شۇنىڭدىن كېيىن كاپىر بولسا، ئۇنى شۇنداق بىر ئازاب بىلەن ئازابلايمەنكى، ئەھلى جاھاندىن ھېچ كىشىنى مۇنداق ئازابلىمايمەن» دېدى. ');
INSERT INTO "saleh" VALUES(5,116,'5:116','ئۆز ۋاقتىدا ئاللاھ ئېيتتى: «ئى مەريەم ئوغلى ئىيسا! سەن كىشىلەرگە، ئاللاھنى قويۇپ مەن بىلەن ئانامنى ئىككى ئىلاھ قىلىۋېلىڭلار، دېدىڭمۇ؟» ئىيسا ئېيتتى: «(رەببىم!) شەنىڭگە لايىق ئەمەس نەرسىلەردىن سېنى پاك دەپ ئېتىقاد قىلىمەنكى، ماڭا ئېيتىشقا تېگىشلىك بولمىغان سۆزلەرنى مەن ئېيتمايمەن. ئەگەر مەن بۇ سۆزنى ئېيتقان بولسام، ئۇنى سەن چوقۇم بىلىسەن (يەنى مېنىڭ ئۇنداق دېمىگەنلىكىم ساڭا مەلۇملۇق). سەن مېنىڭ زاتىمدىكىنى بىلىسەن، مەن سېنىڭ زاتىڭدىكىنى بىلمەيمەن، سەن غەيبلەرنى ناھايىتى ئوبدان بىلىسەن.');
INSERT INTO "saleh" VALUES(5,117,'5:117','مەن ئۇلارغا پەقەت سەن مېنى ئېيتىشقا بۇيرۇغان سۆزنى، يەنى مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان ئاللاھقا ئىبادەت قىلىڭلار، دېدىم. مەن ئۇلارنىڭ ئارىسىدا بولغان مۇددەتتە، ئۇلارنىڭ ئەمەللىرىنى كۆزىتىپ تۇرغان ئىدىم، مېنى قەبزى روھ قىلغىنىڭدىن كېيىن، ئۇلارنىڭ ئەمەللىرىنى سەن كۆزىتىپ تۇرغان ئىدىڭ، سەن ھەممە نەرسىدىن خەۋەردارسەن.');
INSERT INTO "saleh" VALUES(5,118,'5:118',' ئەگەر ئۇلارغا ئازاب قىلساڭ، ئۇلار سېنىڭ بەندىلىرىڭدۇر (ئۇلارنى خالىغىنىڭچە تەسەررۇپ قىلىسەن). ئەگەر ئۇلارغا مەغپىرەت قىلساڭ، سەن (ئىشىڭدا) غالىب، ھېكمەت بىلەن ئىش قىلغۇچىدۇرسەن».');
INSERT INTO "saleh" VALUES(5,119,'5:119','ئاللاھ ئېيتىدۇ: «بۇ كۈن (يەنى قىيامەت كۈنى) راستچىللارنىڭ راستچىللىقى ئۆزىگە پايدا قىلىدىغان كۈندۇر، ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردە مەڭگۈ قالىدۇ، ئاللاھ ئۇلاردىن رازى بولىدۇ، ئۇلار ئاللاھتىن مەمنۇن بولىدۇ، بۇ زور بەختتۇر». ');
INSERT INTO "saleh" VALUES(5,120,'5:120','ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلاردىكى پۈتۈن مەۋجۇداتنىڭ ئىگىدارچىلىقى ئاللاھقا مەنسۇپتۇر، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(6,1,'6:1','جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ياراتقان، زۇلمەتلەرنى ۋە نۇرنى پەيدا قىلغان ئاللاھقا خاستۇر! ئاندىن (يەنى مۇشۇنداق دەلىللەرنى كۆرۈپ تۇرۇپ) كاپىرلار پەرۋەردىگارىغا باشقىلارنى شېرىك كەلتۈرىدۇ.');
INSERT INTO "saleh" VALUES(6,2,'6:2',' ئاللاھ سىلەرنى (يەنى سىلەرنىڭ ئاتاڭلار ئادەمنى) لايدىن ياراتتى، ئاندىن ئەجىلىڭلارنى بېكىتتى، (سىلەرنىڭ تىرىلىدىغان) ۋاقتىڭلار ئاللاھنىڭ دەرگاھىدا مەلۇمدۇر، ئاندىن سىلەر ـ ئى كۇففارلار! (ئۆلگەندىن كېيىن تىرىلىشكە) شەك كەلتۈرىسىلەر.');
INSERT INTO "saleh" VALUES(6,3,'6:3',' ئاسمانلاردا ۋە زېمىندا (ئىبادەت قىلىنىشقا تېگىشلىك) ئاللاھ ئەنە شۇدۇر، ئۇ سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى، (ياخشى ـ يامان) قىلمىشىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(6,4,'6:4',' ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىدىن بىرەر ئايەت كەلسىلا ئۇنىڭدىن باش تارتىدۇ.');
INSERT INTO "saleh" VALUES(6,5,'6:5','پەرۋەردىگارىنىڭ ئايەتلىرىدىن بىرەر ئايەت كەلسىلا ئۇنىڭدىن باش تارتىدۇ. ئۇلارغا (ئاللاھ تەرىپىدىن) ھەق كىتاب (يەنى قۇرئان) كېلىۋېدى، ئۇلار ئۇنىڭغا چىنپۈتمىدى، ئۇزاققا قالماي ئۇلارغا ئۆزلىرى مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى كېلىدۇ [5].');
INSERT INTO "saleh" VALUES(6,6,'6:6','ئۇلاردىن بۇرۇن ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار كۆرمىدىمۇ؟ (يەنى ئۇنىڭدىن ئىبرەت ئالمىدىمۇ؟) ئۇلارنى زېمىندا سىلەرنى يەرلەشتۈرمىگەن بىر شەكىلدە يەرلەشتۈرگەن ئىدۇق، ئۇلارغا مول يامغۇرلار ياغدۇرۇپ بەرگەن ئىدۇق، ئۆستەڭلەرنى ئۇلارنىڭ ئاستىدىن ئېقىپ تۇرىدىغان قىلغان ئىدۇق، ئاندىن كېيىن ئۇلارنى قىلغان گۇناھلىرى تۈپەيلىدىن ھالاك قىلدۇق، ئۇلاردىن كېيىن باشقا خەلقنى پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(6,7,'6:7',' (ئى مۇھەممەد!) بىز ساڭا (ئۇلار تەلەپ قىلغاندەك) قەغەزگە يېزىلغان بىر كىتابنى نازىل قىلغان، كاپىرلار ئۇنى قوللىرى بىلەن تۇتۇپ كۆرگەن تەقدىردىمۇ، ئۇلار شەكسىزكى، بۇ پەقەت روشەن سېھىردۇر، دەيتتى، چۈنكى ئۇلارنىڭ غەرىزى ئەڭ روشەن دەلىل كەلگەندىمۇ ئىمان ئېيتماسلىقتۇر.');
INSERT INTO "saleh" VALUES(6,8,'6:8',' ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىگە گۇۋاھ بولۇپ) نېمىشقا بىر پەرىشتە چۈشمىدى؟» دەيدۇ. ئەگەر ئۇنىڭغا بىرەر پەرىشتە چۈشۈرگەن بولساق، ئۇلارنىڭ ئىشى تۈگىگەن بولاتتى (يەنى بۇ چاغدا ھەقنى ئىنكار قىلسا، ھالاك قىلىناتتى)، ئۇلارغا مۆھلەت بېرىلمەيتتى.');
INSERT INTO "saleh" VALUES(6,9,'6:9','بىز پەيغەمبەرنى پەرىشتىدىن قىلغان تەقدىردىمۇ، ئەلۋەتتە ئۇنى ئىنسان شەكلىدە قىلاتتۇق، ئۇلارنى يەنە شۈبھىگە چۈشۈرەتتۇق (يەنى ئۇلار پەرىشتىنى ئىنسان شەكلىدە كۆرگەندە: «بۇ ئىنسان، پەرىشتە ئەمەس» دەيتتى).');
INSERT INTO "saleh" VALUES(6,10,'6:10','سەندىن ئىلگىرى ئۆتكەن پەيغەمبەرلەر (مۇ كاپىرلار تەرىپىدىن) مەسخىرە قىلىندى، مەسخىرە قىلغۇچىلار مەسخىرە قىلغانلىقلىرىنىڭ ئاقىۋېتىنى كۆردى.');
INSERT INTO "saleh" VALUES(6,11,'6:11',' (ئى مۇھەممەد! بۇ مەسخىرە قىلغۇچىلارغا) ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، ئاندىن پەيغەمبەرلىرىنى ئىنكار قىلغانلارنىڭ ئاقىۋېتىنىڭ قانداق بولغانلىقىغا قاراپ بېقىڭلار».');
INSERT INTO "saleh" VALUES(6,12,'6:12','(ئى مۇھەممەد!) ئېيتقىنكى، «ئاسمانلاردىكى، زېمىندىكى مەۋجۇداتلار كىمنىڭ؟» (ئۇلارغا) ئېيتقىنكى، «ئاللاھنىڭ». ئاللاھ (ئېھسان قىلىش يۈزىسىدىن بەندىلىرىگە) رەھمەت قىلىشنى ئۆز ئۈستىگە ئالدى، ھېچقانداق گۇمان بولمىغان قىيامەت كۈنى ئاللاھ سىلەرنى ئەلۋەتتە يىغىدۇ، (كۇفرىنى ئىختىيار قىلىشى بىلەن) ئۆزىگە ئۆزى زىيان سالغانلار ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(6,13,'6:13',' كېچە ۋە كۈندۈزدە جىم تۇرغان (ياكى ھەرىكەتلىنىپ تۇرغان) شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭ مۈلكىدۇر، ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(6,14,'6:14',' (ئى مۇھەممەد!) بۇ مۇشرىكلارغا ئېيتقىنكى، «ئاسمانلارنى، زېمىننى يوقتىن بار قىلغۇچى ئاللاھتىن غەيرىينى مەبۇد تۇتامدىم؟ ئاللاھ رىزىق بېرىدۇ، ئۆزى رىزىققا موھتاج ئەمەس». (ئى مۇھەممەد!) ئېيتقىنكى، «مەن (بۇ ئۈممەت ئىچىدە خۇدانىڭ ئەمرىگە) بويسۇنغۇچىلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم. (ماڭا دېيىلدىكى) سەن ھەرگىز مۇشرىكلاردىن بولمىغىن».');
INSERT INTO "saleh" VALUES(6,15,'6:15','(ئۇلارغا يەنە) ئېيتقىنكى، «ئەگەر مەن رەببىمگە ئاسىيلىق قىلسام (يەنى پەرۋەردىگارىمدىن غەيرىيگە ئىبادەت قىلسام)، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ئەلۋەتتە قورقىمەن».');
INSERT INTO "saleh" VALUES(6,16,'6:16','مۇشۇ كۈندە كىمكى ئازابتىن قۇتۇلىدىكەن، ئاللاھ ئۇنىڭغا رەھمەت قىلغان بولىدۇ، ئەنە شۇ روشەن نىجات تاپقانلىقتۇر.');
INSERT INTO "saleh" VALUES(6,17,'6:17',' ئەگەر ئاللاھ ساڭا بىرەر كۈلپەت يەتكۈزسە، ئۇنى ئاللاھتىن باشقا ھېچكىم كۆتۈرىۋېتەلمەيدۇ. ئەگەر ئاللاھ ساڭا بىرەر ياخشىلىق يەتكۈزسە (ئۇ چاغدا ئۇنىڭغا ھېچكىم ئارىلىشالمايدۇ)، چۈنكى ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(6,18,'6:18','ئاللاھ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى ئاللاھ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر. ');
INSERT INTO "saleh" VALUES(6,19,'6:19',' (ئى مۇھەممەد!) ئېيتقىنكى، «(مېنىڭ پەيغەمبەرلىكىمنىڭ راستلىقى ئۈچۈن) قايسى نەرسە ئەڭ چوڭ گۇۋاھ؟» ئېيتقىنكى، «ئاللاھ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھتۇر (ماڭا ئاللاھنىڭ گۇۋاھلىقى كۇپايىدۇر). بۇ قۇرئان ماڭا سىلەرنى ۋە (قىيامەتكىچە) قۇرئان يەتكەن كىشىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ۋەھىي قىلىندى. (ئى مۇشرىكلار!) سىلەر ئاللاھ بىلەن باراۋەر باشقا ئىلاھ بار دەپ چوقۇم گۇۋاھلىق بېرەمسىلەر؟» (ئۇلارغا) ئېيتقىنكى، «مەن (مۇنداق دەپ) گۇۋاھلىق بەرمەيمەن». (ئى مۇھەممەد!) ئېيتقىنكى، «ئۇ ھەقىقەتەن بىر ئىلاھتۇر، سىلەر شېرىك كەلتۈرگەن بۇتلاردىن مەن ھەقىقەتەن ئادا ـ جۇدامەن».');
INSERT INTO "saleh" VALUES(6,20,'6:20','كىتاب بەرگەن كىشىلەر (يەنى يەھۇدىيلار ۋە ناسارالار) ئۇنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) (تەۋرات ۋە ئىنجىلدىكى سۈپىتى بويىچە) خۇددى ئۆزلىرىنىڭ ئوغۇللىرىنى تونۇغاندەك تونۇيدۇ، ئۆزلىرىگە ئۆزلىرى زىيان سالغانلار (ئېنىق پاكىتلار تۇرسىمۇ مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(6,21,'6:21',' ئاللاھقا يالغان چاپلىغان ياكى ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم ئادەملەر بارمۇ؟ زالىملار ھەقىقەتەن مەقسىتىگە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(6,22,'6:22','قىيامەت كۈنى ئۇلارنىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىمىز، ئاندىن شېرىك كەلتۈرگەنلەرگە: «ئاللاھنىڭ شېرىكلىرى دەپ ئېتىقاد قىلغانلىرىڭلار قەيەردە؟» دەيمىز.');
INSERT INTO "saleh" VALUES(6,23,'6:23',' ئاندىن ئۇلار پەقەت ئۆزرە ئېيتىپ (دۇنيادىكى چاغلىرىدىكىگە ئوخشاش يالغانغا ئىلتىجا قىلىپ): «پەرۋەردىگارىمىز! ئاللاھ بىلەن قەسەم قىلىمىزكى، بىز مۇشرىك بولمىغان ئىدۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(6,24,'6:24','ئۇلارنىڭ (بىز مۇشرىك بولمىغان دەپ) ئۆزلىرىگە قارشى قانداق يالغان سۆزلىگەنلىكىگە قارىغىن، ئۇلارنىڭ (ئاللاھنىڭ شېرىكلىرى دەپ) بوھتان چاپلىغان نەرسىلىرى ئۇلاردىن قاچىدۇ.');
INSERT INTO "saleh" VALUES(6,25,'6:25',' (ئى مۇھەممەد!) ئۇلارنىڭ ئىچىدە (قۇرئان ئوقۇغان چېغىڭدا، يامان غەرەزدە) ساڭا قۇلاق سالىدىغانلار بار، قۇرئاننى چۈشەنمەسلىكلىرى ئۈچۈن ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، قۇلاقلىرىنى ئېغىر قىلدۇق. ئۇلار ھەممە مۆجىزىلەرنى كۆرگەن تەقدىردىمۇ ئۇنىڭغا ئىمان ئېيتمايدۇ. ھەتتا كاپىرلار سېنىڭ يېنىڭغا مۇنازىرىلىشىپ كەلگەندىمۇ: «بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دەيدۇ. ');
INSERT INTO "saleh" VALUES(6,26,'6:26',' ئۇلار كىشىلەرنى قۇرئاندىن (ۋە مۇھەممەد ئەلەيھىسسالامغا ئەگىشىشىتىن) توسىدۇ، ئۆزلىرىمۇ ئۇنىڭدىن يىراق قاچىدۇ، ئۇلار پەقەت ئۆزلىرىنىلا ھالاك قىلىدۇ، ھالبۇكى ئۇلار (بۇنى) تۇيمايدۇ.');
INSERT INTO "saleh" VALUES(6,27,'6:27','ئەگەر ئۇلارنى دوزاخ ئۈستىدە توختىتىلغان چاغدا كۆرسەڭ (دەھشەتلىك بىر ھالىنى كۆرەتتىڭ). ئۇلار: «كاشكى دۇنياغا قايتۇرۇلساق ئىدۇق، پەرۋەردىگارىمىزنىڭ ئايەتلىرىنى ئىنكار قىلمايتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دەيدۇ. ');
INSERT INTO "saleh" VALUES(6,28,'6:28',' ئۇنداق ئەمەس، ئۇلارنىڭ ئىلگىرى يوشۇرغان ئىشلىرى ئۇلارغا ئاشكارا بولدى، ئۇلار دۇنياغا قايتۇرۇلغان تەقدىردىمۇ مەنئى قىلىنغان ئىشلارنى ئەلۋەتتە يەنە قىلاتتى. شۈبھىسىزكى، ئۇلار (ئىمان ئېيتاتتۇق دېگەن ۋەدىسىدە) يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(6,29,'6:29','ئۇلار: «ھاياتلىق پەقەت مۇشۇ دۇنيادىكى ھاياتىمىزدۇر، ئۆلگەندىن كېيىن تىرىلدۈرۈلمەيمىز» دەيدۇ. ');
INSERT INTO "saleh" VALUES(6,30,'6:30',' ئەگەر ئۇلارنى پەرۋەردىگارىنىڭ ئالدىدا توختىتىلغان چاغدا كۆرسەڭ (غايەت چوڭ ئىشنى كۆرۈسەن). ئاللاھ ئۇلارغا: «بۇ (ئۆلگەندىن كېيىن تىرىلىش) راست ئەمەسمىكەن؟» دەيدۇ. ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، راست ئىكەن» دەيدۇ. ئاللاھ: «كاپىر بولغانلىقىڭلار ئۈچۈن ئازابنى تېتىڭلار» دەيدۇ. ');
INSERT INTO "saleh" VALUES(6,31,'6:31','ئاللاھقا مۇلاقات بولۇشنى (يەنى قايتا تىرىلىشنى) ئىنكار قىلغانلار ھەقىقەتەن زىيان تارتتى. قىيامەت تۇيۇقسىز كەلگەن چاغدا، ئۇلار: «ئۇ دۇنيادىكى بىپەرۋالىقىمىزغا ۋاي ئېست» دەيدۇ. گۇناھلىرىنى ئۈستىلىرىگە يۈكلىۋالىدۇ، ئۇلارنىڭ (گۇناھلارنى) يۈكلىگىنى نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(6,32,'6:32',' دۇنيا تىرىكچىلىكى پەقەت ئويۇن، غەپلەتتىنلا ئىبارەت. تەقۋادارلىق قىلىدىغانلار ئۈچۈن ئەلۋەتتە ئۇ دۇنيا ياخشىدۇر، (بۇنى) چۈشەنمەمسىلەر؟.');
INSERT INTO "saleh" VALUES(6,33,'6:33','ئۇلارنىڭ سۆزى سېنى قايغۇغا سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار سېنى (ئىچىدە) ئىنكار قىلمايدۇ (لېكىن ئۇلار تەرسالىقتىن ئىنكار قىلىدۇ)، (ھەقىقەتتە) زالىملار ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,34,'6:34',' سەندىن بۇرۇن ئۆتكەن پەيغەمبەرلەرمۇ ئىنكار قىلىندى، ئۇلار ئىنكار قىلىنغانلىقىغا ۋە ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلدى، ئاخىر ئۇلار بىزنىڭ ياردىمىمىزگە ئېرىشتى، ئاللاھنىڭ سۆزلىرىنى ھېچقانداق ئادەم ئۆزگەرتەلمەيدۇ، شۈبھىسىزكى، پەيغەمبەرلەرنىڭ قىسسەلىرى ساڭا نازىل بولدى.');
INSERT INTO "saleh" VALUES(6,35,'6:35',' (ئى مۇھەممەد!) ئۇلارنىڭ (ئىسلامدىن) يۈز ئۆرۈگەنلىكى ساڭا ئېغىر كەلگەن بولسا، ئۇنداقتا يەر ئاستىغا كىرىشكە يول ياكى ئاسمانغا چىقىشقا شوتا تېپىپ بىر مۆجىزە كەلتۈرەلىسەڭ (كەلتۈرگىن)، ئەگەر ئاللاھ خالىسا، ئەلۋەتتە ئۇلارنى ھىدايەتكە بىرلەشتۈرەتتى. سەن ھەرگىز (ئاللاھنىڭ ھېكمىتىنى) چۈشەنمەيدىغانلاردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(6,36,'6:36',' (سۆزۈڭگە) ھەقىقىي قۇلاق سالىدىغانلار (سېنىڭ دەۋىتىڭنى) قوبۇل قىلىدۇ (سۆزۈڭگە قۇلاق سالمىغانلار ئۆلۈكلەرنىڭ ئورنىدا)، ئۆلۈكلەرنى ئاللاھ تىرىلدۈرىدۇ، ئاندىن ئۇلار ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(6,37,'6:37','ئۇلار (يەنى مەككە كۇففارلىرى): «نېمىشقا مۇھەممەدكە پەرۋەردىگارىدىن بىر مۆجىزە چۈشۈرۈلمىدى؟» دەيدۇ، ئېيتقىنكى، «ئاللاھ مۆجىزە چۈشۈرۈشكە ھەقىقەتەن قادىر». لېكىن ئۇلارنىڭ تولىسى (مۆجىزە نازىل بولۇپ ئىمان ئېيتمىسا بالاغا دۇچار بولىدىغانلىقىنى) بىلمەيدۇ. ');
INSERT INTO "saleh" VALUES(6,38,'6:38',' مەيلى يەر يۈزىدە ماڭىدىغان ھايۋان بولسۇن، مەيلى ئىككى قانىتى بىلەن ئۇچىدىغان ئۇچار قۇش بولسۇن، ھەممىسى سىلەرگە ئوخشاش ئۈممەتلەردۇر (يەنى ئاللاھ تەرىپىدىن يارىتىلغان مەخلۇقلاردۇر). لەۋھۇلمەھپۇزدا ھېچ نەرسىنى چۈشۈرۈپ قويمىدۇق (يەنى ھەممىنى تولۇق پۈتتۇق)، كېيىن ئۇلار پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىدۇ (ئاللاھ ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىدۇ).');
INSERT INTO "saleh" VALUES(6,39,'6:39','بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار گاسلاردۇر (يەنى قوبۇل قىلىش يۈزىسىدىن ئاڭلىمايدۇ)، گاچىلاردۇر (يەنى ھەقنى سۆزلىمەيدۇ). ئۇلار (كۇفرىنىڭ) قاراڭغۇلۇقى ئىچىدە قالغانلاردۇر، ئاللاھ خالىغان ئادەمنى (زالالەتنى ئىختىيار قىلغانلىقى ئۈچۈن) ئازدۇرىدۇ، خالىغان ئادەمنى (ھىدايەتنى ئىختىيار قىلغانلىقى ئۈچۈن) توغرا يولغا سالىدۇ.');
INSERT INTO "saleh" VALUES(6,40,'6:40',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئەگەر سىلەر راستچىل بولساڭلار ئېيتىپ بېقىڭلار، ئەگەر سىلەرگە ئاللاھنىڭ ئازابى ياكى قىيامەت كەلسە، ئاللاھتىن غەيرىيگە ئىلتىجا قىلامسىلەر؟.');
INSERT INTO "saleh" VALUES(6,41,'6:41',' ئۇنداق ئەمەس، سىلەر پەقەت ئاللاھقا ئىلتىجا قىلىسىلەر، ئەگەر ئاللاھ خالىسا، سىلەر ئاللاھنىڭ كۆتۈرۈۋېتىشىنى ئىلتىجا قىلغان بالانى ئاللاھ كۆتۈرۈۋېتىدۇ، سىلەر ئاللاھنىڭ شېرىكلىرى قىلىۋالغىنىڭلارنى ئۇنتۇيسىلەر».');
INSERT INTO "saleh" VALUES(6,42,'6:42',' شەك ـ شۈبھىسىزكى، سەندىن ئىلگىرىكى ئۈممەتلەرگە (نۇرغۇن پەيغەمبەرلەرنى) ئەۋەتتۇق (ئۇلار پەيغەمبەرلەرنى ئىنكار قىلدى). ئۇلارنى (ئاللاھقا تەۋبە قىلىپ) يېلىنسۇن دەپ نامراتلىق ۋە ئاغرىق ـ سىلاق بىلەن جازالىدۇق.');
INSERT INTO "saleh" VALUES(6,43,'6:43','ئۇلارغا بىزنىڭ ئازابىمىز چۈشكەن چاغدا نېمىشقا يېلىنمىدى؟ ئۇلارنىڭ دىللىرى قەساۋەتلەشتى (ئىمانغا يۇمشىمىدى)، شەيتان ئۇلارغا قىلمىشلىرىنى چىرايلىق كۆرسەتتى.');
INSERT INTO "saleh" VALUES(6,44,'6:44',' ئۇلار ئۆزلىرىگە قىلىنغان نەسىھەتنى ئۇنتۇغان چاغدا، ئۇلارغا (سىناش ئۈچۈن) پاراۋانلىقنىڭ ھەممە ئىشىكلىرىنى ئېچىۋەتتۇق، ئۇلار تاكى ئۆزلىرىگە بېرىلگەن نېمەتلەردىن (كۆرەڭلەپ) خۇشال ـ خۇرام تۇرغاندا (ئۇلارنى ئۇشتۇمتۇت جازالىدۇق)، ئۇلار ھەسرەتتە قالدى. ');
INSERT INTO "saleh" VALUES(6,45,'6:45',' زۇلۇم قىلغان قەۋمنىڭ يىلتىزى قۇرۇتۇلدى (يەنى ئۇلار پۈتۈنلەي ھالاك قىلىندى). جىمى ھەمدۇسانا (پەيغەمبەرلىرىگە ياردەم بېرىپ، كۇففارلارنى ھالاك قىلغانلىقى ئۈچۈن) ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!');
INSERT INTO "saleh" VALUES(6,46,'6:46','(ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر ئاللاھ سىلەرنىڭ قۇلىقىڭلارنى گاس، كۆزۈڭلارنى كور قىلىپ قويسا، دىللىرىڭلارنى پېچەتلىۋەتسە (يەنى سىلەرنى ھېچ نەرسىنى چۈشەنمەيدىغان قىلىپ قويسا)، ئۇلارنى ئاللاھتىن باشقا يەنە قايسى ئىلاھ ئەسلىگە كەلتۈرەلەيدۇ؟» قارىغىنكى، بىز (قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئايەتلىرىمىزنى قانداق بايان قىلغانمىز، ئەمما ئۇلار (شۇ ئايەتلەردىن) يۈز ئۆرۈيدۇ (يەنى ئىمان ئېيتمايدۇ).');
INSERT INTO "saleh" VALUES(6,47,'6:47','(ئى مۇھەممەد!) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ، ئەگەر سىلەرگە ئاللاھنىڭ ئازابى ئۇشتۇمتۇت ياكى ئاشكارا كەلسە، زالىم قەۋمدىن باشقا يەنە كىملەر ھالاك قىلىنىدۇ؟» ');
INSERT INTO "saleh" VALUES(6,48,'6:48',' بىز پەيغەمبەرلەرنى پەقەت (ساۋاب بىلەن) خۇش خەۋەر بەرگۈچى، (ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز، ئىمان ئېيتقان ۋە (ئەمەللىرىنى) تۈزىگەن كىشىلەرگە قورقۇنچ يوقتۇر، ئۇلار غەم – ئەندىشە قىلمايدۇ .');
INSERT INTO "saleh" VALUES(6,49,'6:49',' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، (ئاللاھقا) ئىتائەتسىزلىكلىرى تۈپەيلىدىن، ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(6,50,'6:50',' (ئى مۇھەممەد! سەندىن ئادەتتىن تاشقىرى ئىشلارنى تەلەپ قىلغۇچى كۇففارلارغا) ئېيتقىنكى، «مەن سىلەرگە، مەندە ئاللاھنىڭ خەزىنىلىرى بار، دېمەيمەن. مەن غەيبنىمۇ بىلمەيمەن. سىلەرگە، مەن بولسام ھەقىقەتەن بىر پەرىشتە، دەپمۇ ئېيتمايمەن. مەن پەقەت ماڭا قىلىنغان ۋەھىيگىلا ئەمەل قىلىمەن». ئېيتقىنكى، «كور ئادەم بىلەن كۆزى ساق ئادەم باراۋەرمۇ؟ (يەنى كاپىر بىلەن مۆمىن باراۋەرمۇ؟) ئويلاپ باقمامسىلەر؟»');
INSERT INTO "saleh" VALUES(6,51,'6:51',' پەرۋەردىگارىنىڭ دەرگاھىغا يىغىلىشتىن قورقىدىغانلارنىڭ (كۇفرىدىن، گۇناھتىن) ساقلىنىشلىرى ئۈچۈن، ئۇلارنى قۇرئان بىلەن ئاگاھلاندۇرغىن، ئۇلارغا ئاللاھتىن غەيرىي دوست ۋە شاپائەت قىلغۇچى يوقتۇر. ');
INSERT INTO "saleh" VALUES(6,52,'6:52',' ئاللاھنىڭ رازىلىقىنى دەپ ئەتىگەندە، ئاخشامدا پەرۋەردىگارىغا ئىبادەت قىلىدىغانلارنى (يېنىڭدىن) قوغلىۋەتمىگىن، ئۇلارنىڭ ھېسابىدىن ساڭا ھېچ نەرسە يۈكلەنمەيدۇ (يەنى ئۇلارنىڭ قىلمىشلىرى ئۈچۈن سەن جاۋابكارلىققا تارتىلمايسەن). سېنىڭ ھېسابىڭدىنمۇ ئۇلارغا ھېچ نەرسە يۈكلەنمەيدۇ. ئۇلارنى قوغلىۋەتسەڭ، زالىملاردىن بولۇپ قالىسەن.');
INSERT INTO "saleh" VALUES(6,53,'6:53',' ئۇلارنىڭ: «ئاللاھ ئارىمىزدىن (ھىدايەت قىلىش بىلەن) ئىنئام قىلغان كىشىلەر مۇشۇلارمۇ؟» دېيىشلىرى ئۈچۈن، ئۇلارنى بىر ـ بىرى بىلەن مۇشۇنداق سىنىدۇق (يەنى پېقىر، ئاجىز كىشىلەرنى ئىمان ئېيتقۇزۇش بىلەن، باي، كاتتا كىشىلەرنى سىنىدۇق). ئاللاھ شۈكۈر قىلغۇچىلارنى ئوبدان بىلىدۇ ئەمەسمۇ؟ (يەنى شۈكۈر قىلغۇچىلارنى ئاللاھ ھىدايەت قىلىدۇ). ');
INSERT INTO "saleh" VALUES(6,54,'6:54','بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقانلار يېنىڭغا كەلگەن چاغدا: «سىلەرگە ئامانلىق بولسۇن، پەرۋەردىگارىڭلار (بەندىلىرىگە) رەھمەت قىلىشنى (ئېھسان قىلىش يۈزىسىدىن) ئۆز ئۈستىگە ئالدى، سىلەردىن كىمكى بىلمەستىن بىرەر يامانلىق قىلىپ قويۇپ، ئاندىن كېيىن تەۋبە قىلسا ۋە (ئەمەلىنى) تۈزىسە، ئاللاھ ئەلۋەتتە ئۇنىڭغا مەغپىرەت قىلىدۇ ۋە رەھىم قىلىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(6,55,'6:55','(بۇ سۈرىدە مۇشرىكلارنىڭ گۇمراھلىقىنى كۆرسىتىدىغان دەلىللەرنى بايان قىلغىنىمىزدەك) گۇناھكارلارنىڭ يولى ئاشكارا بولسۇن ئۈچۈن، ئايەتلىرىمىزنى روشەن بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(6,56,'6:56','(ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر ئاللاھنى قايرىپ قويۇپ ئىبادەت قىلغان بۇتلارغا ئىبادەت قىلىشتىن مەن ھەقىقەتەن مەنئى قىلىندىم». ئېيتقىنكى، «مەن سىلەرنىڭ نەپسى خاھىشىڭلارغا ئەگەشمەيمەن، بولمىسا مەن ئازغان بولىمەن ۋە ھىدايەت تاپقۇچىلاردىن بولالماي قالىمەن».');
INSERT INTO "saleh" VALUES(6,57,'6:57',' ئېيتقىنكى، «ھەقىقەتەن مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان روشەن دەلىلگە (يەنى قۇرئانغا) ئاساسلىنىمەن، ھالبۇكى، سىلەر ئۇنى ئىنكار قىلدىڭلار، سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ ئىگىدارچىلىقىمدا ئەمەس. بارچە ھۆكۈم ئاللاھقا خاس، ئاللاھ ھەق ھۆكۈم قىلىدۇ، ئاللاھ ھۆكۈم قىلغۇچىلارنىڭ ياخشىسىدۇر». ');
INSERT INTO "saleh" VALUES(6,58,'6:58','ئېيتقىنكى، «سىلەر بالدۇر يۈز بېرىشىنى تەلەپ قىلغان ئازاب مېنىڭ قولۇمدىن كەلسە ئىدى، ئۇ چاغدا مەن بىلەن سىلەرنىڭ ئاراڭلاردىكى ئىش چوقۇم ھۆكۈم قىلىنىپ بولغان بولاتتى (يەنى سىلەردىن ئارام تېپىش ئۈچۈن، سىلەرنى ھالاك قىلغان بولاتتىم)». ئاللاھ زالىملارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(6,59,'6:59',' غەيبنىڭ ئاچقۇچلىرى ئاللاھنىڭ دەرگاھىدىدۇر، ئۇنى پەقەتلا ئاللاھ بىلىدۇ. قۇرۇقلۇقتىكى، دېڭىزدىكى نەرسىلەرنىڭ ھەممىسىنى ئاللاھ بىلىدۇ، (دەرەختىن) تۆكۈلگەن ياپراقتىن ئاللاھ بىلمەيدىغان بىرەرسىمۇ يوق. مەيلى قاراڭغۇ يەر ئاستىدىكى بىرەر دانە ئۇرۇق بولسۇن، مەيلى ھۆل ياكى قۇرۇق نەرسىلەر بولسۇن، ھەممىسى (ئاللاھقا مەلۇم بولۇپ) لەۋھۇلمەھپۇزدا يېزىقلىقتۇر.');
INSERT INTO "saleh" VALUES(6,60,'6:60','ئاللاھ سىلەرنى كېچىسى ئۇخلىتىدۇ، كۈندۈزدىكى قىلغان ئىشلىرىڭلارنى بىلىپ تۇرىدۇ، ئاندىن بەلگىلەنگەن مۇددەتكىچە ياشىشىڭلار ئۈچۈن، كۈندۈزدە سىلەرنى ئويغىتىدۇ، ئاندىن ئاللاھنىڭ دەرگاھىغا قايتىسىلەر، ئاندىن قىلغان ئەمەللىرىڭلارنى ئاللاھ سىلەرگە ئېيتىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(6,61,'6:61','ئاللاھ بەندىلىرىنىڭ ئۈستىدە قاھىردۇر (يەنى ئاللاھ بەندىلىرىنى تىزگىنلەپ تۇرغۇچىدۇر)، ئۇ سىلەرگە ساقلىغۇچى پەرىشتىلەرنى (يەنى قىلغان ـ ئەتكەننى خاتىرىلەپ تۇرىدىغان پەرىشتىلەرنى) ئەۋەتىدۇ، بىرىڭلارغا ئۆلۈم كەلسە، ئۇنى بىزنىڭ پەرىشتىلىرىمىز قەبزى روھ قىلىدۇ. ئۇلار (ئاللاھنىڭ ئەمرىنى ئورۇنلاشتا) بىپەرۋالىق قىلمايدۇ.');
INSERT INTO "saleh" VALUES(6,62,'6:62',' ئاندىن ئۇلار (يەنى بەندىلەر) ھەق ھۆكۈم قىلغۇچى، ئىگىسى ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىدۇ (قىيامەت كۈنى) ھۆكۈم قىلىش يالغۇز ئاللاھقىلا خاستۇر، ئاللاھ ئەڭ تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(6,63,'6:63','(ئى مۇھەممەد! بۇ كۇففارلارغا) ئېيتقىنكى، «قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرى (يەنى بالايىئاپەتلىرى) دىن سىلەرنى كىم قۇتقۇزىدۇ؟ (بۇ قاراڭغۇلۇقلارنى كۆرگەن چېغىڭلاردا) ئەگەر ئاللاھ بىزنى بۇنىڭدىن قۇتقۇزسا چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز، دەپ ئاللاھقا ئاشكارا ۋە يوشۇرۇن ئىلتىجا قىلىسىلەر».');
INSERT INTO "saleh" VALUES(6,64,'6:64',' ئېيتقىنكى، «(يالغۇز) ئاللاھ سىلەرنى ئۇ زۇلمەتلەردىن ۋە بارلىق غەمدىن قۇتقۇزىدۇ، ئاندىن سىلەر (بۇنى) تونۇغىنىڭلاردىن كېيىن، ئىمان ئېيتماي ئاللاھقا شېرىك كەلتۈرىسىلەر».');
INSERT INTO "saleh" VALUES(6,65,'6:65','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئاللاھ سىلەرنىڭ ئۈستۈڭلاردىن (نۇھ ۋە لۇت ئەلەيھىسسالاملارنىڭ قەۋمىگە، ئەسھابۇلپىلگە ئەۋەتىلگەن ئازابقا ئوخشاش) ياكى ئايىغىڭلار ئاستىدىن (پىرئەۋن ۋە قارۇنلارغا ئەۋەتىلگەن ئازابقا ئوخشاش) ئازاب ئەۋەتىشكە، ياكى سىلەرنى پىرقىلەر قىلىپ، ئارىلاشتۇرۇپ (يەنى ئۇرۇشقا سېلىپ) بىرىڭلارغا بىرىڭلارنىڭ ئازابىنى تېتىتىشقا قادىردۇر». ئۇلارنى چۈشەنسۇن دەپ، ئايەتلىرىمىزنى تۈرلۈك شەكىللەردە قانداق بايان قىلىدىغانلىقىمىزغا قارىغىن.');
INSERT INTO "saleh" VALUES(6,66,'6:66',' قۇرئان ھەق تۇرسا، ئۇنى قەۋمىڭ ئىنكار قىلدى. «مەن سىلەرگە ھامىي ئەمەس» دېگىن.');
INSERT INTO "saleh" VALUES(6,67,'6:67','(ئاللاھ تەرىپىدىن) ئالدىنئالا بېرىلگەن ھەر بىر خەۋەرنىڭ مەيدانغا كېلىدىغان ۋاقتى بار، ئۇزاققا قالماي بىلىسىلەر.');
INSERT INTO "saleh" VALUES(6,68,'6:68','بىزنىڭ ئايەتلىرىمىز توغرىسىدا (تەنە ۋە مەسخىرە قىلىپ) پاراڭ سېلىشىۋاتقان كىشىلەرنى كۆرگىنىڭدە، ئۇلار بۇنىڭدىن باشقا پاراڭغا چۈشمىگۈچە ئۇلار بىلەن بىللە ئولتۇرمىغىن. ئۇلار بىلەن بىللە ئولتۇرماسلىقنى شەيتان ساڭا ئۇنتۇلدۇرغان بولسا، ئېسىڭگە كەلگەندىن كېيىن، زالىم قەۋم بىلەن بىللە ئولتۇرمىغىن.');
INSERT INTO "saleh" VALUES(6,69,'6:69','تەقۋادارلارغا (قۇرئاننى مەسخىرە قىلغۇچى) كاپىرلارنىڭ ھېسابىدىن ھېچ نەرسە يۈكلەنمەيدۇ، لېكىن تەقۋادارلار ئۇلارغا ۋەز ـ نەسىھەت قىلىشلىرى كېرەك. (شۇنداق قىلغاندا) ئۇلار (قۇرئاننى مەسخىرە قىلىشتىن) ساقلىنىشلىرى مۇمكىن.');
INSERT INTO "saleh" VALUES(6,70,'6:70','دىننى ئويۇن ـ كۈلكە قىلىۋالغان، دۇنيا تىرىكچىلىكىگە ئالدانغانلارنى تەرك ئەت. ھەر قانداق ئادەمنىڭ ئۆز قىلمىشى تۈپەيلى ھالاك بولۇشتىن ساقلىنىشى ئۈچۈن، (كىشىلەرگە) قۇرئان بىلەن نەسىھەت قىلغىن، ئۇنىڭغا ئاللاھتىن باشقا دوست ۋە شاپائەت قىلغۇچى بولمايدۇ، ئۇ ھەر قانداق فىدىيە (تۆلەم) بەرسىمۇ قوبۇل قىلىنمايدۇ. ئەنە شۇلار قىلمىشلىرى تۈپەيلىدىن ھالاك بولىدىغانلاردۇر، ئۇلار كۇفرى تۈپەيلىدىن (ئۈچەيلىرىنى ئۆرتىۋېتىدىغان) قايناقسۇدىن بولغان شارابقا ۋە قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(6,71,'6:71','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، بىز ئاللاھنى قويۇپ، بىزگە پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە ئىبادەت قىلامدۇق؟ بىزنى ئاللاھ ھىدايەت قىلغاندىن كېيىن، ئارقىمىزغا چېكىنەمدۇق؟ (يەنى مۇرتەد بولامدۇق؟) (مۇبادا بىز شۇنداق قىلىدىغان بولساق، مۇنداق) بىر ئادەمگە: شەيتانلار ئۇنى بىر چۆلگە باشلاپ بارغان، (چۆلدە ئۇ) تېڭىرقاپ قالغان، دوستلىرى ئۇنى بېرى كەل دەپ توغرا يولغا چاقىرىۋاتقان (ئۇ چاقىرىقنى قوبۇل قىلماي ئاخىر ھالاك بولغان ئادەمگە) ئوخشاپ قالىمىز. (بۇ كۇففارلارغا) ئېيتقىنكى، ئاللاھنىڭ ھىدايىتى ھەقىقەتەن ھەقىقىي ھىدايەتتۇر. بىز ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇق.');
INSERT INTO "saleh" VALUES(6,72,'6:72','ناماز ئوقۇشقا ۋە ئاللاھتىن قورقۇشقا بۇيرۇلدۇق. (قىيامەت كۈنى) ئاللاھنىڭ دەرگاھىغا (ھېساب ئۈچۈن) يىغىلىسىلەر.');
INSERT INTO "saleh" VALUES(6,73,'6:73',' ئاللاھ ئاسمانلارنى، زېمىننى ھېكمەت بىلەن ياراتتى. ئاللاھ (بىرەر شەيئىگە) «ۋۇجۇدقا كەل» دېسە (دەرھال) ۋۇجۇدقا كېلىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) ئازابتىن قورقۇڭلار. ئاللاھنىڭ سۆزى ھەقتۇر. سۇر چېلىنىدىغان كۈندىكى (يەنى قىيامەت كۈنىدىكى) پادىشاھلىق ئاللاھقا خاستۇر، ئاللاھ يوشۇرۇن ۋە ئاشكارا نەرسىلەرنى بىلگۈچىدۇر، ئاللاھ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، (بەندىلىرىنىڭ ئەھۋالىدىن) خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(6,74,'6:74','ئۆز ۋاقتىدا، ئىبراھىم ئۆز ئاتىسى ئازەرگە: «(سەن ئۆزۈڭنى ياراتقان ئاللاھنى قويۇپ) بۇتلارنى مەبۇد قىلىۋالامسەن؟ مەن سېنى ۋە قەۋمىڭنى ھەقىقەتەن ئېنىق گۇمراھلىقتا كۆرۈمەن» دېدى.');
INSERT INTO "saleh" VALUES(6,75,'6:75','ئىبراھىمنىڭ (تەۋھىد بارىسىدا) قەتئىي ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، (ئۇنىڭغا ئاتىسى بىلەن قەۋمنىڭ گۇمراھلىقىنى كۆرسەتكىنىمىزدەك) بىز ئىبراھىمغا ئاسمانلارنىڭ، زېمىننىڭ ئاجايىپلىرىنى كۆرسەتتۇق.');
INSERT INTO "saleh" VALUES(6,76,'6:76','ئىبراھىم كېچىنىڭ قاراڭغۇلۇقى باسقاندا بىر يۇلتۇزنى كۆرۈپ (قەۋمىنىڭ سۆزىنى دەلىل كەلتۈرۈپ، ئۇلارنى مات قىلىش ئۈچۈن): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى. يۇلتۇز پېتىپ كېتىۋىدى، «مەن پېتىپ كەتكۈچىلەرنى (يەنى يۇلتۇزلارغا ئىبادەت قىلىشنى) ياقتۇرمايمەن» دېدى.');
INSERT INTO "saleh" VALUES(6,77,'6:77',' ئۇ ئاينىڭ تۇغقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر» دېدى. ئاي پېتىپ كېتىۋىدى، «ئەگەر پەرۋەردىگارىم مېنى ھىدايەت قىلمىغان بولسا، مەن چوقۇم ئازغۇچى قەۋمنىڭ قاتارىدا بولىدىكەنمەن» دېدى.');
INSERT INTO "saleh" VALUES(6,78,'6:78','ئۇ قۇياشنىڭ چىقىۋاتقانلىقىنى كۆرۈپ (يۇقىرىقى ئۇسۇل بويىچە): «بۇ مېنىڭ پەرۋەردىگارىمدۇر، بۇ ھەممىدىن (يەنى يۇلتۇز بىلەن ئايدىن) چوڭدۇر» دېدى. قۇياش پېتىپ كېتىۋىدى، ئۇ ئېيتتىكى: «ئى قەۋمىم! مەن ھەقىقەتەن سىلەر شېرىك كەلتۈرگەن مەبۇدلاردىن ئادا ـ جۇدامەن.');
INSERT INTO "saleh" VALUES(6,79,'6:79','مەن ھەقىقەتەن، باتىل دىنلاردىن ھەق دىنغا بۇرۇلۇپ، ئاسمانلارنى، زېمىننى ياراتقان زاتقا يۈزلەندىم، مەن مۇشرىكلاردىن ئەمەسمەن».');
INSERT INTO "saleh" VALUES(6,80,'6:80','ئىبراھىمنىڭ قەۋمى ئۇنىڭ بىلەن مۇنازىرىلەشتى، ئىبراھىم ئېيتتىكى، «مېنى ئاللاھ ھىدايەت قىلغان تۇرسا، مەن بىلەن ئاللاھ (نىڭ مەۋجۇتلۇقى ۋە بىرلىكى) توغرۇلۇق مۇنازىرىلىشەمسىلەر؟ مەن سىلەرنىڭ ئاللاھقا شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن قورقمايمەن، پەقەت پەرۋەردىگارىمنىڭلا خالىغان نەرسىسى بولىدۇ، پەرۋەردىگارىمنىڭ بىلىمى ھەممە نەرسىنى ئۆز ئىچىگە ئالغان، (مەبۇدلىرىڭلارنىڭ ماڭا ھېچبىر زىيان يەتكۈزەلمەيدىغانلىقىنى) ئويلىمامسىلەر؟');
INSERT INTO "saleh" VALUES(6,81,'6:81',' ئاللاھنىڭ شېرىكى بولۇش ھەققىدە ئاللاھ سىلەرگە ھېچقانداق دەلىل چۈشۈرمىگەن بۇتلارنى ئاللاھقا شېرىك كەلتۈرۈشتىن قورقمايۋاتساڭلار، سىلەرنىڭ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن مەن قانداقمۇ قورقاتتىم! ئەگەر سىلەر بىلسەڭلار (ئېيتىڭلارچۇ)، بىز بۇ ئىككى گۇرۇھ ئادەمدىن زادى قايسىمىز قورقماسلىققا ئەڭ تېگىشلىك؟».');
INSERT INTO "saleh" VALUES(6,82,'6:82','ئازابتىن ئەمىن بولۇش ئىمان ئېيتقان، ئىمانىغا شېرىك ئارىلاشتۇرمىغان ئادەملەرگە مەنسۇپتۇر، ئۇلار ھىدايەت تاپقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(6,83,'6:83',' ئۆز قەۋمىگە رەددىيە بېرىش ئۈچۈن ئىبراھىمغا بەرگەن دەلىلىمىز ئەنە شۇدۇر، خالىغان كىشىمىزنى بىر قانچە دەرىجە كۆتۈرىمىز، پەرۋەردىگارىڭ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(6,84,'6:84',' بىز ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق. ئۇلارنىڭ ھەر بىرىنى ھىدايەت قىلدۇق. (ئىبراھىمدىن) ئىلگىرى نۇھنى ھىدايەت قىلدۇق. ئىبراھىمنىڭ ئەۋلادىدىن داۋۇد، سۇلايمان، ئەييۇب، يۇسۇف، مۇسا، ھارۇنلارنى ھىدايەت قىلدۇق، ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(6,85,'6:85',' زەكەرىيا، يەھيا، ئىيسا ۋە ئىلياسلارنى ھىدايەت قىلدۇق. ئۇلارنىڭ ھەممىسى ياخشىلاردىندۇر.');
INSERT INTO "saleh" VALUES(6,86,'6:86',' ئىسمائىل، ئىليەسە، يۇنۇس ۋە لۇتلارنى ھىدايەت قىلدۇق. ئۇلارنى (ئۆز زامانىدىكى) تامامى جاھان ئەھلىدىن ئۈستۈن قىلدۇق.');
INSERT INTO "saleh" VALUES(6,87,'6:87',' ئۇلارنىڭ ئاتا ـ بوۋىلىرىدىن، ئەۋلادلىرىدىن، قېرىنداشلىرىدىن بەزىلىرىنى ھىدايەت قىلدۇق، ئۇلارنى تاللىدۇق، ئۇلارنى توغرا يولغا يېتەكلىدۇق. ');
INSERT INTO "saleh" VALUES(6,88,'6:88',' ئاللاھنىڭ توغرا يولى ئەنە شۇدۇر، ئاللاھ بەندىلىرىدىن خالىغان كىشىلەرنى شۇ يولغا باشلايدۇ. ئەگەر ئۇلار (يەنى شۇ پەيغەمبەرلەر) شېرىك كەلتۈرسە ئىدى، ئۇلارنىڭ قىلغان ياخشى ئەمەللىرى، ئەلۋەتتە بىكار بولۇپ كېتەتتى.');
INSERT INTO "saleh" VALUES(6,89,'6:89','ئۇلارغا كىتابنى، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئەگەر بۇ مۇشرىكلار ئۇلارنى (يەنى كىتاب، ھېكمەت ۋە پەيغەمبەرلىكنى) ئىنكار قىلسا، ئىنكار قىلمايدىغان باشقا بىر قەۋمگە ئۇلارنى تاپشۇرىمىز.');
INSERT INTO "saleh" VALUES(6,90,'6:90','ئەنە شۇلار (يەنى مەزكۇر پەيغەمبەرلەر) ئاللاھ ھىدايەت قىلغان كىشىلەردۇر، ئۇلارنىڭ يولىغا ئەگەشكىن. (ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «قۇرئاننى تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، قۇرئان پەقەت پۈتۈن جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر».');
INSERT INTO "saleh" VALUES(6,91,'6:91','ئۇلار ئاللاھنى ھەقىقىي رەۋىشتە تونۇمىدى. ئۇلار (يەنى يەھۇدىيلار) ئۆز ۋاقتىدا: «ئاللاھ ھېچ كىشىگە ھېچ نەرسە نازىل قىلمىدى» دېدى (يەنى ۋەھىينى ۋە پەيغەمبەرلەرنى ئىنكار قىلدى). (ئى مۇھەممەد!) ئېيتقىنكى، «مۇسا ئېلىپ كەلگەن ۋە كىشىلەرگە نۇر، ھىدايەت بولغان كىتابنى كىم نازىل قىلدى؟ ئۇ كىتابنى پارچە ـ پارچە قەغەزلەرگە كۆچۈرۈپ، بىر قىسمىنى ئاشكارىلاپ، كۆپ قىسمىنى يوشۇرىسىلەر. (ئى يەھۇدىيلار!) سىلەرگە ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار بىلمىگەن نەرسىلەر بىلدۈرۈلدى». (ئى مۇھەممەد! بۇ قۇرئاننى ۋە مۇسا ئېلىپ كەلگەن كىتابنى) «ئاللاھ نازىل قىلدى» دېگىن. ئاندىن، ئۇلارنى تەرك ئەتكىنكى، ئۇلار باتىللىرىدا (يەنى قالايمىقان سۆزلىرىدە) ئويناپ يۈرسۇن.');
INSERT INTO "saleh" VALUES(6,92,'6:92','بۇ (قۇرئان) مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن بىز نازىل قىلغان، ئۇنىڭدىن ئىلگىرى نازىل قىلىنغان (تەۋرات ۋە ئىنجىلغا ئوخشاش) كىتابلارنى ئېتىراپ قىلغۇچى مۇبارەك كىتابتۇر. ئاخىرەتكە ئىمان ئېيتىدىغانلار ئۇنىڭغا ئىمان ئېيتىدۇ، ئۇلار نامازلىرىنى ئۆز ۋاقتىدا تولۇق ئادا قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,93,'6:93',' ئاللاھقا يالغان چاپلىغان ياكى ئۆزىگە ھېچ نەرسە ۋەھىي قىلىنمىغان تۇرۇقلۇق، ماڭا ۋەھىي قىلىندى دېگەن ياكى ئاللاھ نازىل قىلغاندەك مەنمۇ كىتاب نازىل قىلالايمەن دېگەن كىشىلەردىنمۇ زالىم كىشىلەر بارمۇ؟ (ئى مۇھەممەد!) زالىملارنى سەكراتقا چۈشكەن ۋاقىتلىرىدا كۆرسەڭ (ئەلۋەتتە، قورقۇنچلۇق ھالىنى كۆرۈسەن). پەرىشتىلەر (يەنى ئازاب پەرىشتىلىرى) قوللىرىنى سوزۇپ: «سىلەر جانلىرىڭلارنى چىقىرىڭلار! سىلەر ئاللاھ توغرىسىدا ھەقسىز سۆزلەرنى قىلغانلىقىڭلار (يەنى ئىپتىرا قىلغانلىقىڭلار، شېرىك كەلتۈرگەنلىكىڭلار، بالا نىسبەت بەرگەنلىكىڭلار) ۋە ئاللاھنىڭ ئايەتلىرىنى مەنسىتمىگەنلىكىڭلار ئۈچۈن، بۈگۈن خورلىغۇچى ئازاب بىلەن جازالىنىسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(6,94,'6:94','بىز دەسلەپتە سىلەرنى قانداق خەلق ئەتكەن بولساق دەرگاھىمىزغا شۇنداق تەنھا كەلدىڭلار، سىلەرگە بىز ئاتا قىلغان نەرسىلەرنى ئارقاڭلاردا (يەنى دۇنيادا) قويۇپ كەلدىڭلار، (ئىبادەت قىلىنىشقا تېگىشلىك بولۇشتا) ئاللاھنىڭ شېرىكلىرى دەپ گۇمان قىلغان شاپائەتچىڭلارنى سىلەر بىلەن بىللە كۆرمەيمىزغۇ؟ ئۇلار بىلەن سىلەرنىڭ ئاراڭلاردىكى مۇناسىۋەت ھەقىقەتەن ئۈزۈلدى، سىلەر (شاپائەت قىلىدۇ) دەپ گۇمان قىلغان نەرسىلەر بەربات بولدى.');
INSERT INTO "saleh" VALUES(6,95,'6:95',' دانلارنى ۋە ئۇرۇقچىلارنى بىخ ئۇرغۇزغۇچى ھەقىقەتەن ئاللاھتۇر. تىرىكنى ئۆلۈكتىن ۋە ئۆلۈكنى تىرىكتىن چىقارغۇچى ئەنە شۇ ئاللاھتۇر، (ئاللاھقا ئىبادەت قىلىشتىن) قانداقمۇ باش تارتىسىلەر؟.');
INSERT INTO "saleh" VALUES(6,96,'6:96','ئاللاھ سۈبھىنى (قاراڭغۇدىن) يېرىپ چىقارغۇچىدۇر (يەنى تاڭنى يورۇتقۇچىدۇر)، ئاللاھ كېچىنى (سىلەر ئۈچۈن) ئىستىراھەت قىلىپ ياراتتى. (سىلەرنىڭ) ۋاقىت ھېسابلىشىڭلار ئۈچۈن قۇياش بىلەن ئاينى ياراتتى.');
INSERT INTO "saleh" VALUES(6,97,'6:97','قۇرۇقلۇق ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا (يەنى كېچىلىك يول يۈرگىنىڭلاردا) يول تېپىشىڭلار ئۈچۈن ئاللاھ سىلەرگە يۇلتۇزلارنى يارىتىپ بەردى. (ئاللاھنىڭ ئۇلۇغلۇقىنى) بىلىدىغان قەۋم ئۈچۈن، (ئاللاھنىڭ قۇدرىتىنىڭ) دەلىللىرىنى ھەقىقەتەن ئوچۇق بايان قىلدۇق. ');
INSERT INTO "saleh" VALUES(6,98,'6:98','ئاللاھ سىلەرنى بىر ئىنساندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، سىلەرنى ئاتاڭلارنىڭ پۇشتىدا، ئاناڭلارنىڭ بەچچىدانىدا قارارلاشتۇردى، چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى ھەقىقەتەن ئوچۇق بايان قىلدۇق. ');
INSERT INTO "saleh" VALUES(6,99,'6:99','ئاسماندىن سۇنى چۈشۈرگەن زات ئاللاھتۇر. بىز ئۇنىڭ بىلەن بارلىق ئۆسۈملۈكلەرنى ئۈندۈردۇق. ئۆسۈملۈكلەردىن يېشىل ياپراقلارنى چىقاردۇق. (ئۇنىڭدىن بوغداي ۋە ئارپا باشاقلىرىغا ئوخشاش) بىر ـ بىرىگە مىنگىشىپ كەتكەن دانلارنى يېتىشتۈرىمىز.، خورما دەرىخىنىڭ چېچەكلىرىدىن يەرگە يېقىن ساڭگىلاپ تۇرىدىغان خورما ساپاقلىرىنى يېتىشتۈرىمىز.، (يامغۇر سۈيى بىلەن) ئۈزۈملۈك باغلارنى يېتىشتۈرىمىز.، (يامغۇر سۈيى بىلەن) شەكىلدە، ھەجىمدە، تەمدە بىر ـ بىرىگە ئوخشايدىغان ۋە ئوخشىمايدىغان زەيتۇن، ئانار دەرەخلىرىنى يېتىشتۈرىمىز. (ئۇلارنىڭ ھەر بىرىنىڭ) يېڭى تۇتقان مېۋىسىگە ۋە پىشقان مېۋىسىگە قاراڭلار، ئۇلاردا ئاللاھنىڭ بارلىقىغا ئىشىنىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئوچۇق دەلىللەر بار.');
INSERT INTO "saleh" VALUES(6,100,'6:100','جىنلارنى ئاللاھ ياراتقان تۇرسا، (مۇشرىكلار) جىنلارنى ئاللاھقا شېرىك قىلىۋېلىشتى. ئاللاھقا جاھالەتتىن ئوغۇل، قىز پەرزەنتلەرنى ئويدۇرۇپ چىقىرىشتى. ئاللاھ (مۇشرىكلار ئىپتىرا قىلغان نەرسىلەردىن) پاكتۇر، ئۇلارنىڭ سۈپەتلىگەنلىرىدىن ئۈستۈندۇر .');
INSERT INTO "saleh" VALUES(6,101,'6:101',' ئاللاھ ئاسمانلارنى ۋە يەرنى ئۆرنەكسىز ياراتقۇچىدۇر، ئاللاھنىڭ خوتۇنى يوق تۇرسا، قانداقمۇ بالىسى بولسۇن؟ ھەممە نەرسىنى ئاللاھ ياراتقان، ھەممە نەرسىنى ئاللاھ ئوبدان بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(6,102,'6:102','ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئاللاھتىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، ئۇ ھەر نەرسىگە ھامىيدۇر.');
INSERT INTO "saleh" VALUES(6,103,'6:103','كۆزلەر ئاللاھنى كۆرمەيدۇ، ئاللاھ كۆزلەرنى كۆرۈپ تۇرىدۇ، ئاللاھ (بەندىلىرىگە) مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر. ');
INSERT INTO "saleh" VALUES(6,104,'6:104',' (ئى پەيغەمبەر، ئېيتقىنكى) سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن (ھىدايەتنى ئازغۇنلۇقتىن ئايرىپ كۆرسىتىدىغان) دەلىللەر كەلدى، كىمكى ھەقنى كۆرۈپ ئىمان ئېيتسا ئۆزىگە پايدا، كىمكى ئۇنىڭدىن كۆز يۇمسا (يەنى باش تارتسا) ئۆزىگە زىيان. مەن سىلەرگە (ئەمەلىڭلارنى كۆزىتىپ تۇرىدىغان) كۆزەتچى ئەمەسمەن (پەقەت سىلەرنى ئاگاھلاندۇرغۇچى پەيغەمبەرمەن).');
INSERT INTO "saleh" VALUES(6,105,'6:105',' ئۇلار (يەنى مۇشرىكلار): (ئى مۇھەممەد!) سەن (قۇرئاننى باشقىلاردىن) ئۆگىنىپسەن دېسۇن يەنە ئۇنى (يەنى قۇرئاننى) ھەقنى بىلىدىغان قەۋمگە بايان قىلايلى دەپئايەتلەرنى مۇشۇنداق شەكىللەردە بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(6,106,'6:106',' (ئى مۇھەممەد!) ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھىي قىلىنغان قۇرئانغا ئەمەل قىلغىن؛ ئاللاھتىن باشقا مەبۇد (بەرھەق) يوقتۇر، مۇشرىكلار بىلەن تەڭ بولمىغىن.');
INSERT INTO "saleh" VALUES(6,107,'6:107',' ئەگەر ئاللاھ (ئۇلارنىڭ ھىدايەت تېپىشنى) خالىسا، ئۇلار شېرىك كەلتۈرمەيتتى. سېنى ئۇلارغا كۆزەتچى قىلغىنىمىز يوق، سەن ئۇلارغا ھامىيمۇ ئەمەسسەن.');
INSERT INTO "saleh" VALUES(6,108,'6:108','ئاللاھقا شېرىك كەلتۈرىدىغانلارنىڭ خۇدانى قويۇپ ئىبادەت قىلىدىغان مەبۇدلىرىنى تىللىماڭلار، (ئۇنداق قىلساڭلار) ئۇلار بىلمىگەنلىكلىرىدىن ھەددىدىن ئېشىپ ئاللاھنى تىللايدۇ. شۇنىڭدەك بىز ھەر ئۈممەتكە ئۆزلىرىنىڭ ئەمەلىنى چىرايلىق كۆرسەتتۇق، ئاندىن ئۇلارنىڭ قايتىپ بارىدىغان جايى ئاللاھنىڭ دەرگاھىدۇر، ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ (يەنى ياخشىغا مۇكاپات بېرىدۇ، ياماننى جازالايدۇ).');
INSERT INTO "saleh" VALUES(6,109,'6:109',' ئەگەر ئۇلارغا بىرەر مۆجىزە كەلسە، ئۇنىڭغا چوقۇم ئىشىنىدىغانلىقلىرىنى (بىلدۈرۈپ) ئاللاھنىڭ نامى بىلەن قاتتىق قەسەم ئىچىشىدۇ. (ئى مۇھەممەد!) مۆجىزىلەر (مېنىڭ ئەمەس)، ئاللاھنىڭ دەرگاھىدىكى ئىش، دېگىن. (ئى مۆمىنلەر!) سىلەر نېمىنى بىلىسىلەر؟ مۆجىزىلەر كەلسە، ئۇلار، ئېھتىمال ئۇنىڭغا ئىشەنمەسلىكلىرى مۇمكىن.');
INSERT INTO "saleh" VALUES(6,110,'6:110','ئۇلارنىڭ دىللىرىنى، كۆزلىرىنى ھەق (نى چۈشىنىش ۋە كۆرۈش) تىن بۇرىۋېتىمىز، ئۇلار دەسلەپتە مۆجىزىلەرگە ئىشەنمىگىنىدەك (يەنە ئىشەنمەيدۇ)، ئۇلارنى گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشكە قويۇۋېتىمىز. ');
INSERT INTO "saleh" VALUES(6,111,'6:111','ئۇلارغا (يەنى كۇففارلارغا) پەرىشتىلەرنى چۈشۈرگەن، ئۆلۈكلەرنى ئۇلار بىلەن سۆزلەشتۈرگەن، بىز ھەممە نەرسىنى ئۇلارنىڭ ئالدىغا يىغىپ بەرگەن (يەنى ئۇلارنىڭ يۇقىرىقى تەلەپلىرىنى ئورۇنلىغان) تەقدىردىمۇ، ئاللاھ خالىمىغىچە ئۇلار ئىمان ئېيتمايدۇ، لېكىن ئۇلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(6,112,'6:112',' شۇنىڭدەك (يەنى بۇ مۇشرىكلارنى ساڭا دۈشمەن قىلغاندەك) ئىنسانلار ۋە جىنلارنىڭ شەيتانلىرىنى ھەر بىر پەيغەمبەرگە دۈشمەن قىلدۇق، ئۇلار ئالداش ئۈچۈن شېرىن سۆزلەر بىلەن بىر ـ بىرىگە ۋەسۋەسە قىلىدۇ، ئەگەر پەرۋەردىگارىڭ خالىسا، ئۇلار بۇ ئىشنى (يەنى پەيغەمبەرلەرگە دۈشمەنلىكنى) قىلمايتتى، ئۇلارنى ئۆزلىرى تۈزگەن ھىيلە ـ مىكىرلىرى بىلەن قويۇپ بەرگىن (ئاللاھ ساڭا ئۇلارغا قارشى ياردەم بېرىدۇ).');
INSERT INTO "saleh" VALUES(6,113,'6:113','ئاخىرەتكە ئىمان ئېيتمايدىغانلارنىڭ دىللىرى ئۇلارنىڭ شېرىن سۆزلىرىگە مايىل بولسۇن، ئۇنىڭدىن خۇرسەن بولسۇن ۋە ئۇلار قىلىۋاتقان گۇناھلارنى بۇلارمۇ قىلسۇن، دەپ ۋەسۋەسە قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,114,'6:114','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئاللاھ سىلەرگە روشەن كىتاب نازىل قىلغان تۇرسا، (ئارىمىزدىكى ئىختىلاپنى ھەل قىلىش ئۈچۈن) ئاللاھتىن غەيرىي ھۆكۈم قىلغۇچىنى تەلەپ قىلامدىم؟» بىز كىتاب ئاتا قىلغانلار (يەنى يەھۇدىيلار ۋە ناسارالارنىڭ ئۆلىمالىرى) ئۇنىڭ (يەنى قۇرئاننىڭ) پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەق كىتاب ئىكەنلىكىنى بىلىدۇ، سەن ھەرگىزمۇ شەك قىلغۇچىلاردىن بولمىغىن. ');
INSERT INTO "saleh" VALUES(6,115,'6:115','پەرۋەردىگارىڭنىڭ سۆزى ناھايىتى راستتۇر ، ناھايىتى توغرىدۇر، ئۇنىڭ سۆزىنى ئۆزگەرتەلەيدىغان ھېچ كىشى يوقتۇر. ئاللاھ (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(6,116,'6:116','ئەگەر سەن يەر يۈزىدىكى كۆپچىلىك كىشىلەر (يەنى كۇففارلار) گە ئىتائەت قىلساڭ، ئۇلار سېنى ئاللاھنىڭ يولىدىن ئازدۇرىدۇ، ئۇلار پەقەت گۇمانغىلا ئەگىشىدۇ، ئۇلار پەقەت يالغاننىلا سۆزلەيدۇ. ');
INSERT INTO "saleh" VALUES(6,117,'6:117',' (ئى مۇھەممەد!) سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ھەقىقەتەن ئوبدان بىلىدۇ، توغرا يول تاپقانلارنىمۇ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(6,118,'6:118',' ئاللاھنىڭ ئايەتلىرىگە ئىمان كەلتۈرگۈچى بولساڭلار، ئاللاھنىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى يەڭلار.');
INSERT INTO "saleh" VALUES(6,119,'6:119','زۆرۈرىيەت بولمىغىچە يېيىش ھارام قىلىنغان نەرسىلەرنى ئاللاھ سىلەرگە ئوچۇق بايان قىلغان تۇرسا، ئاللاھنىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان ھايۋانلارنى نېمىشقا يېمەيسىلەر؟ نۇرغۇن كىشىلەر نەپسى خاھىشىغا ئەگىشىپ، بىلمەستىن، (باشقىلارنى) ھەقىقەتەن ئازدۇرىدۇ، شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ (شەرئى دەلىلسىز ھالال، ھارام دەپ) ھەددىدىن ئاشقۇچىلارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(6,120,'6:120',' ئاشكارا ۋە يوشۇرۇن گۇناھلارنى تەرك ئېتىڭلار، شۈبھىسىزكى، گۇناھ قىلغۇچىلار ئۆز قىلمىشلىرى تۈپەيلىدىن (ئاخىرەتتە) جازاغا تارتىلىدۇ. ');
INSERT INTO "saleh" VALUES(6,121,'6:121',' (ئى مۆمىنلەر!) ئاللاھنىڭ ئىسمى ئېيتىلماي بوغۇزلانغان ھايۋانلارنى يېمەڭلار، ئۇنى يېيىش ئەلۋەتتە گۇناھتۇر. شۈبھىسىزكى، شەيتانلار ئۆز دوستلىرىنى (يەنى مۇشرىكلارنى) سىلەر بىلەن دەتالاش قىلىشقا ۋەسۋەسە قىلىدۇ، ئەگەر ئۇلارغا ئىتائەت قىلساڭلار، سىلەر چوقۇم مۇشرىك بولۇپ قالىسىلەر.');
INSERT INTO "saleh" VALUES(6,122,'6:122',' ئەسلىدە ئۆلۈك (يەنى كاپىر) بولۇپ، بىز ئۇنى (يەنى ئۇنىڭ قەلبىنى ئىمان بىلەن) تىرىلدۈرگەن، بىز ئۇنىڭغا كىشىلەر ئارىسىدا ھىدايەت تېپىشقا نۇر بەرگەن بىر ئادەم زۇلمەتتە (يەنى كۇفرىنىڭ قاراڭغۇلۇقىدا) قالغان ۋە ئۇنىڭدىن قۇتۇلالمىغان بىر ئادەم بىلەن ئوخشاشمۇ؟ كاپىرلارغا ئۇلارنىڭ قىلمىشلىرى ئەنە شۇنداق چىرايلىق كۆرسىتىلدى.');
INSERT INTO "saleh" VALUES(6,123,'6:123','ھىيلە ـ مىكىر ئىشلىتىشلىرى ئۈچۈن (مەككىدە قۇرەيشنىڭ چوڭلىرىنى باش گۇناھكار قىلغىنىمىزدەك)، ھەر بىر شەھەرنىڭ چوڭلىرىنى باش گۇناھكار قىلدۇق، ئۇلارنىڭ ئىشلەتكەن ھىيلە ـ مىكىرلىرى ئۆزلىرىگە يانىدۇ (يەنى ئۇلار شۇ ھىيلە ـ مىكىرلەرنىڭ ۋابالىغا قالىدۇ)، ئۇلار (بۇنى) سەزمەيدۇ.');
INSERT INTO "saleh" VALUES(6,124,'6:124',' ئۇلارغا (مۇھەممەد ئەلەيھىسسالامنىڭ راست پەيغەمبەرلىكىنى كۆرسىتىدىغان) بىرەر مۆجىزە كەلگەن چاغدا، (ئۇلار) بىزگىمۇ پەيغەمبەرلەرگە بېرىلگەن مۆجىزە بېرىلمىگۈچە (ئۇنىڭغا) ھەرگىز ئىشەنمەيمىز، دەيدۇ. ئاللاھ پەيغەمبەرلىك ۋەزىپىسىنى قەيەرگە ئورنىتىشنى (يەنى كىمنىڭ ئۇنىڭ ئەھلى بولىدىغانلىقىنى) ئوبدان بىلىدۇ، (بۇ) گۇناھكارلار ئۆزلىرى ئىشلەتكەن ھىيلە ـ مىكىرلەر تۈپەيلىدىن ئاللاھنىڭ دەرگاھىدا خورلۇققا ۋە قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(6,125,'6:125',' ئاللاھ كىمنى ھىدايەت قىلماقچى بولسا، ئۇنىڭ كۆكسىنى ئىسلام ئۈچۈن ئاچىدۇ (يەنى ئۇنىڭ قەلبىنى ئىمانغا كېڭەيتىپ بېرىدۇ)، ئاللاھ كىمنى ئازدۇرماقچى بولسا، ئۇ گويا ئاسمانغا چىقىۋاتقاندەك ئۇنىڭ كۆكسىنى تارايتىۋېتىدۇ. (ئىمان ئېيتىش) ئۇنىڭغا گويا ئاسمانغا چىقىشتىنمۇ قىيىن تۇيۇلىدۇ، ئىمان ئېيتمىغانلارنى ئاللاھ مۇشۇنداق ئازابلايدۇ.');
INSERT INTO "saleh" VALUES(6,126,'6:126','بۇ (يەنى ئىسلام دىنى) پەرۋەردىگارىڭنىڭ توغرا يولىدۇر، ئىبرەت ئالىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(6,127,'6:127','پەرۋەردىگارىنىڭ دەرگاھىدا ئۇلارغا دارۇسسالام (يەنى جەننەت) بار، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرى ئۈچۈن ئاللاھ ئۇلارغا مەدەتكاردۇر.');
INSERT INTO "saleh" VALUES(6,128,'6:128',' ئاللاھ (ئىنسانلارنى، جىنلارنى ھېساب ئۈچۈن) تولۇق يىغىدىغان كۈننى ئەسلىگىن، (بۇ كۈندە ئاللاھ ئېيتىدۇكى) «ئى جىنلار جامائەسى!سىلەر نۇرغۇن ئادەملەرنى ئازدۇردۇڭلار». ئۇلارنىڭ ئىنسانلاردىن بولغان دوستلىرى: «ئى پەرۋەردىگارىمىز! بىز بىر ـ بىرىمىزدىن پايدىلاندۇق، بىز ئەمدى سەن بەلگىلىگەن ۋاقىتقا (يەنى قىيامەت كۈنىگە) يەتتۇق» دەيدۇ، ئاللاھ: «دوزاخ سىلەرنىڭ جايىڭلاردۇر، سىلەر ئاللاھنىڭ خاھىشىغا باغلىق ھالدا ئۇ يەردە مەڭگۈ قالىسىلەر» دەيدۇ. شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(6,129,'6:129',' شۇنىڭغا (يەنى ئىنسانلار بىلەن جىنلارنى ئۆزئارا پايدىلاندۇرغىنىمىزغا) ئوخشاش، قىلمىشلىرى تۈپەيلىدىن زالىملارنىڭ بەزىسىنى بەزىسىگە سېلىپ قويىمىز.');
INSERT INTO "saleh" VALUES(6,130,'6:130','ئاللاھ ئېيتىدۇكى، «ئى جىنلار ۋە ئىنسانلار جامائەسى! سىلەرگە ئۆز ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى بايان قىلىپ بېرىدىغان، بۇ كۈنۈڭلاردىكى ئۇچرىشىشتىن سىلەرنى ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىدىمۇ؟» ئۇلار: «گۇناھلىرىمىزنى ئېتىراپ قىلدۇق» دەيدۇ. ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىغان. ئۇلار ئۆزلىرىنىڭ ھەقىقەتەن كاپىر بولغانلىقلىرىنى ئېتىراپ قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,131,'6:131',' بۇ شۇنىڭ ئۈچۈنكى، پەرۋەردىگارىڭ شەھەرلەرنىڭ ئاھالىسىنى (ئۇلارغا پەيغەمبەرلەر ئەۋەتىپ ئاگاھلاندۇرماستىن) غەپلەتتىكى ۋاقىتلىرىدا زۇلۇم قىلغانلىقلىرى تۈپەيلىدىن ھالاك قىلغىنى يوق.');
INSERT INTO "saleh" VALUES(6,132,'6:132','(ئاخىرەتتە) ھەر ئادەمنىڭ قىلغان ئەمەلىگە يارىشا دەرىجىسى بولىدۇ، پەرۋەردىگارىڭ ئۇلارنىڭ قىلغان ئىشلىرىدىن غاپىل ئەمەس.');
INSERT INTO "saleh" VALUES(6,133,'6:133','پەرۋەردىگارىڭ (خالايىقتىن ۋە ئۇلارنىڭ ئىبادەتلىرىدىن) بىھاجەتتۇر، رەھمەت ئىگىسىدۇر. ئەگەر ئۇ خالىسا، سىلەرنى ھالاك قىلىپ، سىلەرنى باشقا بىر قەۋمنىڭ ئەۋلادىدىن پەيدا قىلغاندەك، سىلەرنىڭ ئورنۇڭلارغا ئۆزى خالىغان (ئىتائەتمەن) باشقا بىر قەۋمنى قويىدۇ.');
INSERT INTO "saleh" VALUES(6,134,'6:134',' سىلەرگە ۋەدە قىلىنغان (قىيامەت، ئۆلگەندىن كېيىن تىرىلىش قاتارلىق) ئىشلار چوقۇم مەيدانغا كېلىدۇ، سىلەر قېچىپ قۇتۇلالمايسىلەر.');
INSERT INTO "saleh" VALUES(6,135,'6:135',' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئى قەۋمىم! سىلەر كۈچۈڭلارنىڭ يېتىشىچە ئىشلەڭلار (يەنى كۇفرىڭلاردا چىڭ تۇرۇڭلار)، مەنمۇ چوقۇم كۈچۈمنىڭ يېتىشىچە ئىشلەيمەن (يەنى مەنمۇ ئۆز دەۋىتىمدە چىڭ تۇرىمەن). ئاخىرەتتە كىمنىڭ ئاقىۋېتى ياخشى بولىدىغانلىقىنى ئۇزۇنغا قالماي بىلىسىلەر، زالىملار ئەلۋەتتە مەقسىتىگە ئېرىشەلمەيدۇ».');
INSERT INTO "saleh" VALUES(6,136,'6:136',' مۇشرىكلار ئاللاھ ياراتقان ئېكىنلەردىن ۋە چاھارپايلاردىن (يەنى تۆگە، كالا، قويلاردىن) ئاللاھ ئۈچۈن بىر ھەسسە (بۇتلىرى ئۈچۈن بىر ھەسسە) ئايرىپ قويۇپ، ئۆز گۇمانلىرىچە (ھېچقانداق دەلىلسىزلا): «بۇ ئاللاھ ئۈچۈندۇر، بۇ مەبۇدلىرىمىز ئۈچۈندۇر» دېدى. بۇتلىرىنىڭ نېسىۋېسىدىن ئاللاھنىڭكىگە قوشۇشقا بولمايتتى، ئاللاھنىڭ نېسىۋېسىدىن بۇتلىرىنىڭكىگە قوشۇشقا بولاتتى (مۇشرىكلار ئاللاھقا ئاتىغان ھەسسىسىدىن بۇتلارغا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسى ئىچىدە قالدۇراتتى ۋە ئاللاھ باي، بۇنىڭغا موھتاج ئەمەس، دەيتتى. بۇتلىرىغا ئاتىغان ھەسسىسىدىن ئاللاھقا ئاتىغان ھەسسىسىگە بىرەر نەرسە قوشۇلۇپ قالسا، ئۇنى بۇتلىرىنىڭ ھەسسىسىگە قايتۇرۇۋېتەتتى). ئۇلارنىڭ قارارى نېمىدېگەن قەبىھ!.');
INSERT INTO "saleh" VALUES(6,137,'6:137',' مۇشرىكلارنىڭ شېرىكلىرى (يەنى شەيتانلار) مۇشرىكلارنى ھالاك قىلىش ۋە ئۇلارنىڭ دىنىنى قالايمىقانلاشتۇرۇش ئۈچۈن، ئۇلارنىڭ نۇرغۇنلىرىغا بالىلىرىنى ئۆلتۈرۈشنى، شۇنىڭدەك (يەنى ئاللاھقا ۋە بۇتلارغا ھەسسە ئايرىپ قويۇشتەك تەقسىماتنى شەيتانلارغا ۋە مۇشرىكلارغا چىرايلىق كۆرسەتكەندەك) چىرايلىق كۆرسەتتى. ئەگەر ئاللاھ خالىغان بولسا ئىدى، ئۇلار بۇ (قەبىھ) ئىشنى قىلمايتتى، ئۇلارنى ئاللاھقا چاپلىغان يالغانلىرى بىلەن بىللە تەرك ئەت.');
INSERT INTO "saleh" VALUES(6,138,'6:138',' ئۇلار ئۆزلىرىنىڭ گۇمانىچە: «بۇ ھايۋانلار ۋە ئېكىنلەر (مەبۇدلىرىمىزغا خاس بولۇپ، باشقىلارغا) ھارامدۇر، ئۇلارنى پەقەت بىز خالىغان ئادەملەرلا يەيدۇ، بۇ ھايۋانلارنى مىنىش ھارامدۇر، بۇ ھايۋانلارنى بوغۇزلىغاندا ئاللاھنىڭ ئىسمىنى ئېيتىشقا بولمايدۇ» دېدى. ئۇلار ئاللاھقا يالغان چاپلىدى، ئاللاھ ئۇلارنى يالغان چاپلىغانلىقلىرى ئۈچۈن جازالايدۇ.');
INSERT INTO "saleh" VALUES(6,139,'6:139','ئۇلار: «بۇ ھايۋانلارنىڭ قورساقلىرىدىكى بالىلىرى بىزنىڭ ئەر كىشىلىرىمىزگە خاستۇر، بىزنىڭ خوتۇن كىشىلىرىمىزگە ھارامدۇر» دېدى. ئەگەر تۇغۇلغان ھايۋان ئۆلۈك بولسا، بۇ ئەر كىشىلەر بىلەن خوتۇن كىشىلەر ئارىسىدا ئورتاقتۇر (يەنى ئۇنى ھەممەيلەن تەڭ يەيدۇ) دەپ (ھالال ـ ھارامنى تەيىنلەشتى). ئاللاھقا يالغان چاپلىغانلىقلىرى ئۈچۈن ئاللاھ ئۇلارنى جازالايدۇ. شۈبھىسىزكى، ئاللاھ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(6,140,'6:140','ھاماقەتلىك ۋە نادانلىقلىرىدىن بالىلىرىنى ئۆلتۈرگەن ۋە ئاللاھ نامىدىن يالغان ئېيتىپ، ئاللاھ رىزىق قىلىپ بەرگەن نەرسىلەرنى ھارام بىلگەن كىشىلەر ھەقىقەتەن زىيان تارتتى، ئۇلار ھەقىقەتەن (توغرا يولدىن) ئاداشتى، ھىدايەت تاپمىدى.');
INSERT INTO "saleh" VALUES(6,141,'6:141',' ئاللاھ بىدىشلىك ۋە بىدىشسىز باغلارنى بەرپا قىلدى، مېۋىلىرى، دانلىرى (رەڭگى، تەمى، ھەجمى ۋە پۇرىقى) بىر ـ بىرىگە ئوخشىمايدىغان خورما دەرەخلىرىنى، زىرائەتلەرنى، (رەڭگى ۋە شەكلى) بىر ـ بىرىگە ئوخشايدىغان، (تەمى) ئوخشىمايدىغان زەيتۇن ۋە ئانارنى ياراتتى. مېۋىسى پىشقاندا، ئۇلاردىن يەڭلار، مېۋە (نىڭ ھوسۇلى) نى يىغقان كۈندە (يەنى يىغقان ۋاقىتتا)، ئۇنىڭ ھەققىنى ئادا قىلىڭلار، ئىسراپ قىلماڭلار، ئاللاھ ھەقىقەتەن ئىسراپ قىلغۇچىلارنى ياقتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(6,142,'6:142',' ئاللاھ چاھارپايلارنى (يەنى تۆگە، كالا، قويلارنى)، يۈك كۆتۈرىدىغانلارنى، بوغۇزلىنىدىغانلارنى ياراتتى. ئاللاھ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن يەڭلار، شەيتاننىڭ ئارقىسىغا كىرمەڭلار، شەيتان سىلەرگە ھەقىقەتەن ئوچۇق دۈشمەندۇر.');
INSERT INTO "saleh" VALUES(6,143,'6:143','(ئاللاھ سىلەرگە يېيىش ھالال قىلىنغان) سەككىز خىل چاھارپاينى ياراتتى: قويدىن ئىككى (قوچقار ۋە ساغلىق)، ئۆچكىدىن ئىككى (تېكە ۋە ئۇرغاچى). (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «ئاللاھ (قوي بىلەن ئۆچكىدىن) ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى قوزا بىلەن ئوغلاقنىمۇ؟ ئەگەر (سىلەر ئاللاھ بۇلارنى بىزگە ھارام قىلدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، قېنى ماڭا بىلىپ ئېيتىپ بېرىڭلار!». ');
INSERT INTO "saleh" VALUES(6,144,'6:144','(ئاللاھ سىلەرگە) تۆگىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى)، كالىدىن ئىككىنى (يەنى ئەركەك بىلەن چىشىنى) ياراتتى. (مۇشرىكلارغا) ئېيتقىنكى، «ئاللاھ ئىككى ئەركەكنى ھارام قىلدىمۇ؟ ياكى ئىككى چىشىنىمۇ ۋە ياكى ئىككى چىشىنىڭ قورسىقىدىكى بوتىلاق بىلەن موزاينىمۇ ؟ ياكى ئاللاھ بۇنى سىلەرگە تەۋسىيە قىلغان چاغدا سىلەر ھازىرمىدىڭلار؟ كىشىلەرنى ئازدۇرۇش ئۈچۈن، بىلمەي تۇرۇپ ئاللاھ نامىدىن يالغان ئېيتقان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ». ');
INSERT INTO "saleh" VALUES(6,145,'6:145','(ئى مۇھەممەد!) ئېيتقىنكى، ساڭا ۋەھىي قىلىنغان ئەھكاملار ئىچىدە، ئۆزى ئۆلۈپ قالغان نەرسە ياكى ئېقىپ چىققان قان ۋە ياكى چوشقا گۆشى ـ چوشقا نىجىس يېمەكلىكلەرگە ئادەتلەنگەنلىكى ئۈچۈن چوشقا گۆشى پاسكىنىدۇر ـ ۋە ئاللاھتىن غەيرىينىڭ ئىسمى ئېيتىلىپ بوغۇزلانغان گۇناھقا سەۋەب بولغان مالدىن باشقىسىنى ھەر قانداق ئادەم ئۈچۈن ھارام كۆرمەيمەن. كىمكى ئۇلاردىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) نائىلاج ۋە بىئىختىيار ھالدا ھاياتىنى ساقلاپ قالغۇدەك يېسە (ھېچ گۇناھ بولمايدۇ)، چۈنكى پەرۋەردىگارىڭ مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(6,146,'6:146',' يەھۇدىيلارغا توم تۇياقلىق (يەنى تۆگە، تۆگىقۇشقا ئوخشاش ئاچا تۇياقلىق بولماي توم تۇياقلىق بولغان) ھايۋانلارنىڭ ھەممىسىنى ھارام قىلدۇق، ئۇلارغا كالا، قويلارنىڭ دۈمبىسىدىكى ياكى ئۈچەيلىرىدىكى ياكى سۆڭىكىگە چاپلىشىقلىق ياغلىرىدىن باشقا ياغلىرىنى ھارام قىلدۇق، بۇ ئۇلارنىڭ (پەيغەمبەرلەرنى ئۆلتۈرۈش، جازانە قىلىش، كىشىلەرنىڭ مال ـ مۈلكىنى ناھەق يەۋېلىش قاتارلىق) زۇلۇملىرى تۈپەيلىدىن، ئۇلارغا بەرگەن جازايىمىزدۇر. بىز ئەلۋەتتە (بەرگەن خەۋەرلىرىمىزدە) راستچىلمىز.');
INSERT INTO "saleh" VALUES(6,147,'6:147',' (ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى يەھۇدىيلار) سېنى ئىنكار قىلسا، سەن: «پەرۋەردىگارىڭلار (قىلمىشىڭلار ئۈچۈن سىلەرنى ئالدىراپ جازالىمىغانلىقتىن) كەڭ رەھمەت ئىگىسىدۇر، (ئاللاھنىڭ رەھمىتىگە مەغرۇر بولماڭلاركى) ئاللاھنىڭ ئازابى گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(6,148,'6:148','مۇشرىكلار: «ئەگەر ئاللاھ خالىسا ئىدى، بىز ۋە ئاتا ـ بوۋىلىرىمىز شېرىك كەلتۈرمەيتتۇق ۋە ھېچ نەرسىنى ھارام قىلمايتتۇق» دەيدۇ. ئۇلاردىن بۇرۇنقى كىشىلەر تاكى (بىزنىڭ ئازابىمىز نازىل بولۇپ) ئازابىمىزنى تېتىغانغا قەدەر (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇنداق (يەنى مۇشرىكلار سېنى ئىنكار قىلغاندەك) ئىنكار قىلغان ئىدى. (ئۇلارغا) ئېيتقىنكى، «(سۆزۈڭلارنىڭ راستلىقىغا) پاكىتىڭلار بولسا، بىزگە چىقىرىپ كۆرسىتىڭلار، سىلەر پەقەت گۇمانغىلا ئاساسلىنىسىلەر، ئاللاھقا پەقەت يالغاننى چاپلايسىلەر».');
INSERT INTO "saleh" VALUES(6,149,'6:149',' (ئى مۇھەممەد!) ئېيتقىنكى، «(سىلەرنىڭ پاكىتىڭلار بولمىسا) ئاللاھنىڭ ئېنىق پاكىتى بار، ئەگەر ئۇ خالىسا ھەممىڭلارنى، ئەلۋەتتە، ھىدايەت قىلاتتى».');
INSERT INTO "saleh" VALUES(6,150,'6:150','(ئى مۇھەممەد!ئۇلارغا )«ئاللاھ بۇنى ھارام قىلدى، دەپ گۇۋاھلىق بېرىدىغان گۇۋاھچىلىرىڭلارنى چاقىرىپ كېلىڭلار»دېگىن، ئەگەر ئۇلار(ھازىر بولۇپ يالغاندىن)بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ نەپسى خاھىشلىرىغائەگەشمىگىن،ئۇلار پەرۋەردىگارىڭغا شېرىك كەلتۈرىدۇ. 150-ئايەت. ');
INSERT INTO "saleh" VALUES(6,151,'6:151',' ئېيتقىنكى، «سىلەر كېلىپ (ئاڭلاڭلار)، ئاللاھ ھارام قىلغان نەرسىلەرنى سىلەرگە ئوقۇپ بېرەي: سىلەر ئاللاھقا ھېچ نەرسىنى شېرىك كەلتۈرمەڭلار، ئاتا ـ ئاناڭلارغا ياخشىلىق قىلىڭلار، نامراتلىقتىن قورقۇپ بالاڭلارنى ئۆلتۈرمەڭلار، بىز ئۇلارغا ۋە سىلەرگە رىزىق بېرىمىز، ئاشكارا ۋە يوشۇرۇن يامان ئىشلارغا يېقىن كەلمەڭلار، ئاللاھنىڭ ئەمرىگە خىلاپلىق قىلىپ (ناھەق) ئادەم ئۆلتۈرمەڭلار، ھەقلىق رەۋىشتە ئۆلتۈرۈش بۇنىڭدىن مۇستەسنا. سىلەرنىڭ چۈشىنىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,152,'6:152','يېتىمنىڭ مېلىنى تاكى ئۇ (قۇرامىغا يېتىپ) ئەقلى يېتىلگەنگە قەدەر ئۇنىڭغا ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئۆلچەمنى ۋە تارازىنى توغرا قىلىڭلار، ھېچقانداق ئادەمنى كۈچى يەتمەيدىغان ئىشقا تەكلىپ قىلمايمىز. سۆز قىلغان (ھۆكۈم چىقارغان ياكى گۇۋاھلىق بەرگەن) چېغىڭلاردا ئادىل بولۇڭلار ، (ھۆكۈم چىقىرىلغان ياكى گۇۋاھلىق بېرىلگەن ئادەم) تۇققىنىڭلار بولغان تەقدىردىمۇ، ئاللاھقا بېرىلگەن ئەھدىگە ۋاپا قىلىڭلار. ئىبرەت ئېلىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ».');
INSERT INTO "saleh" VALUES(6,153,'6:153',' بۇ مېنىڭ توغرا يولۇمدۇر، شۇ يولدا مېڭىڭلار، (ناتوغرا) يوللاردا ماڭماڭلار. ئۇلار سىلەرنى ئاللاھنىڭ يولىدىن ئايرىۋېتىدۇ. (ئاللاھنىڭ ئەمرىنى تۇتۇپ دوزاختىن) ساقلىنىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە بۇ ئىشلارنى تەۋسىيە قىلىدۇ.');
INSERT INTO "saleh" VALUES(6,154,'6:154',' ئاندىن ئۇلار (يەنى ئىسرائىل ئەۋلادى) ئۆز پەرۋەردىگارىغا مۇلاقات بولىدىغانلىقىغا ئىشەنسۇن دەپ، ياخشىلىق قىلغان ئادەمگە بېرىدىغان نېمىتىمىزنى مۇكەممەللەشتۈرۈش، (ئىسرائىل ئەۋلادىنىڭ دىنىي ئىشلىرىدا ئېھتىياجلىق بولغان) ھەر بىر نەرسىنى تەپسىلىي بايان قىلىش، (ئىسرائىل ئەۋلادىنى) ھىدايەت قىلىش ۋە (ئۇلارغا) رەھمەت قىلىش يۈزىسىدىن، مۇساغا كىتاب (يەنى تەۋرات) ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(6,155,'6:155',' بۇ بىز نازىل قىلغان (قۇرئان) مۇبارەك كىتابتۇر، ئاللاھنىڭ رەھمىتىنى ئۈمىد قىلغۇچىلاردىن بولۇش ئۈچۈن ئۇنىڭغا ئەگىشىڭلار، (ئۇنىڭغا مۇخالىپەتچىلىك قىلىشتىن) ساقلىنىڭلار.');
INSERT INTO "saleh" VALUES(6,156,'6:156','(مېنىڭ قۇرئان نازىل قىلىشىم) سىلەرنىڭ، پەقەت بىزدىن ئىلگىرىكى ئىككى تائىپىگىلا (يەنى يەھۇدىيلار ۋە ناسارالارغىلا) كىتاب نازىل قىلىنغان، ئۇلارنىڭ ئوقۇغان كىتابلىرىدىن بىزنىڭ زادىلا خەۋىرىمىز يوق، دېمەسلىكىڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(6,157,'6:157','ياكى سىلەرنىڭ، ئەگەر بىزگە كىتاب نازىل قىلىنغان بولسا، بىز ئەلۋەتتە ئۇلاردىن ئارتۇق ھىدايەت تاپقان بولاتتۇق، دېمەسلىكىڭلار ئۈچۈندۇر. سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەقىقەتەن پاكىت، ھىدايەت ۋە رەھمەت نازىل بولدى. ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە ئۇلاردىن يۈز ئۆرۈگەن كىشىدىنمۇ زالىم ئادەم بارمۇ؟ بىزنىڭ ئايەتلىرىمىزدىن يۈز ئۆرۈگەنلەرنى ئۇلارنىڭ يۈز ئۆرۈگەنلىكلىرى ئۈچۈن قاتتىق ئازاب بىلەن جازالايمىز. ');
INSERT INTO "saleh" VALUES(6,158,'6:158','ئۇلار پەقەت (ئۆلۈم) پەرىشتىلىرىنىڭ ياكى پەرۋەردىگارىڭنىڭ ياكى پەرۋەردىگارىڭنىڭ (قۇياشنىڭ مەغرىبتىن چىقىشىدىن ئىبارەت) بەزى ئالامەتلىرىنىڭ كېلىشىنىلا كۈتىدۇ. پەرۋەردىگارىڭنىڭ بەزى ئالامەتلىرى كەلگەن كۈندە ئىلگىرى ئىمان ئېيتمىغانلارنىڭ ياكى ئىمان ئېيتىپ ياخشى ئىش قىلمىغانلارنىڭ ئېيتقان ئىمانى پايدىسىز بولىدۇ. «(سىلەر ئۆزۈڭلارغا كېلىدىغان ئىشلارنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە كۈتىمىز» دېگىن.');
INSERT INTO "saleh" VALUES(6,159,'6:159','دىنىدا بۆلگۈنچىلىك قىلىپ تۈرلۈك پىرقىلەرگە ئايرىلغانلاردىن سەن ئادا ـ جۇداسەن؛ ئۇلارنىڭ ئىشى پەقەت ئاللاھقىلا خاستۇر (يەنى ئاللاھنىڭ باشقۇرۇشىدىدۇر)، كېيىن (يەنى ئاخىرەتتە) ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى ئۆزلىرىگە ئېيتىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(6,160,'6:160','بىرەر ياخشىلىق قىلغان ئادەم ئون ھەسسە ئارتۇق ساۋابقا ئىگە بولىدۇ، بىرەر يامانلىق قىلغان ئادەمگە پەقەت ئۇنىڭ يامانلىقىغا باراۋەرلا جازا بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(6,161,'6:161','(ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «پەرۋەردىگارىم مېنى توغرا يولغا يەنى توغرا دىنغا، باتىل دىنلاردىن توغرا دىنغا بۇرالغان ئىبراھىمنىڭ دىنىغا باشلىدى، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى». ');
INSERT INTO "saleh" VALUES(6,162,'6:162','(ئى مۇھەممەد!) ئېيتقىنكى، «مېنىڭ نامىزىم، قۇربانلىقىم، ھاياتىم ۋە ماماتىم (دۇنيادا قىلغان ياخشىلىقلىرىم ۋە تائەت ـ ئىبادەتلىرىم) ئالەملەرنىڭ پەرۋەردىگارى ئاللاھ ئۈچۈندۇر. ');
INSERT INTO "saleh" VALUES(6,163,'6:163',' ئاللاھنىڭ شېرىكى يوقتۇر، مەن مۇشۇنىڭغا (يەنى يالغۇز ئاللاھقىلا خالىس ئىبادەت قىلىشقا) بۇيرۇلدۇم، مەن مۇسۇلمانلارنىڭ ئەۋۋىلىمەن».');
INSERT INTO "saleh" VALUES(6,164,'6:164',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئاللاھ ھەممە نەرسىنىڭ پەرۋەردىگارى تۇرسا، ئۇنىڭدىن باشقا پەرۋەردىگار تەلەپ قىلامدىمەن؟» ھەر ئادەم ئۆزى قىلغان گۇناھقا ئۆزى جاۋابكار، بىر گۇناھكار ئادەم باشقا ئادەمنىڭ گۇناھىغا جاۋابكار ئەمەس (يەنى بىر ئادەمنىڭ گۇناھى بىلەن باشقا بىر ئادەم جازاغا تارتىلمايدۇ). كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، سىلەر دەتالاش قىلىشقان ھەق بىلەن ناھەقنى ئۇ سىلەرگە ئېيتىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(6,165,'6:165','ئاللاھ سىلەرنى (ئۆتكەنكى ئۈممەتلەرنىڭ) ئورۇنباسارلىرى قىلدى. ئاللاھ سىلەرنى بەرگەن نېمەتلىرى بىلەن سىناش ئۈچۈن بەزىڭلارنى بەزىڭلاردىن بىر قانچە دەرىجە يۇقىرى قىلدى. پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلارنى) ئەلۋەتتە تېز جازالىغۇچىدۇر، (ئىتائەت قىلغۇچىلارنى) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(7,1,'7:1','ئەلىف، لام، مىم، ساد. ');
INSERT INTO "saleh" VALUES(7,2,'7:2','(ئى مۇھەممەد! كاپىرلارنى) ئاگاھلاندۇرۇشۇڭ ، مۆمىنلەرگە ۋەز ـ نەسىھەت قىلىشىڭ ئۈچۈن، بۇ كىتاب (يەنى قۇرئان) ساڭا نازىل قىلىندى. ئۇنىڭدىن (يەنى ئۇنى تەبلىغ قىلىشتا قەۋمىڭنىڭ ئۇنى ئىنكار قىلىشىدىن ئەنسىرەپ) يۈرىكىڭ سىقىلمىسۇن.');
INSERT INTO "saleh" VALUES(7,3,'7:3','(ئى ئىنسانلار!) سىلەر پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە نازىل قىلىنغان كىتابقا (يەنى قۇرئانغا) ئەگىشىڭلار، ئاللاھنى قويۇپ، (جىنلاردىن ۋە ئىنسانلاردىن بولغان، سىلەرنى ئازدۇرىدىغان) دوستلارغا ئەگەشمەڭلار، سىلەر ۋەز ـ نەسىھەتنى ئاز قوبۇل قىلىسىلەر.');
INSERT INTO "saleh" VALUES(7,4,'7:4','بىز نۇرغۇن شەھەر (ئاھالىسى) نى ھالاك قىلدۇق. بىزنىڭ ئازابىمىز ئۇلارغا كېچىسى ياكى چۈشلۈكى ئۇخلاۋاتقان چاغلىرىدا كەلدى.');
INSERT INTO "saleh" VALUES(7,5,'7:5',' بىزنىڭ ئازابىمىز كەلگەن چاغدا، ئۇلارنىڭ چەككەن پەريادى: «بىز ھەقىقەتەن زالىم ئىدۇق» دېيىشتىنلا ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(7,6,'7:6','بىز ئەلۋەتتە پەيغەمبەر ئەۋەتىلگەن ئۈممەتلەردىن (پەيغەمبەرلەر تەبلىغ قىلدىمۇ؟ سىلەر قانداق جاۋاب بەردىڭلار؟ دەپ) سورايمىز، پەيغەمبەرلەردىنمۇ ئەلۋەتتە (ئاللاھ تاپشۇرغان ۋەزىپىنى ئادا قىلدىڭلارمۇ؟ دەپ) سورايمىز.');
INSERT INTO "saleh" VALUES(7,7,'7:7',' (قىيامەت كۈنى ئۇلارنىڭ ئەھۋالىنى) ئۇلارغا ھەقىقىي بىلىش ئاساسىدا ئېيتىپ بېرىمىز، بىز ئۇلاردىن ئايرىلمىغان ئىدۇق (يەنى ئۇلارنىڭ ھەممە ئەھۋالىدىن خەۋەرداردۇرمىز). ');
INSERT INTO "saleh" VALUES(7,8,'7:8',' بۇ كۈندە بەندىلەرنىڭ (ياخشى يامان ئەمەللىرىنىڭ) ئۆلچىنىدىغانلىقى ھەقتۇر. ياخشى ئەمەللىرى ئېغىر چىققانلار نىجات تاپقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(7,9,'7:9','ياخشى ئەمەللىرى يېنىك چىققانلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى تۈپەيلىدىن ئۆزلىرىگە زىيان سالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(7,10,'7:10','شۈبھىسىزكى، سىلەرنى زېمىندا يەرلەشتۈردۇق، زېمىندا سىلەرگە تۇرمۇش لازىمەتلىكلىرىنى پەيدا قىلدۇق، (سىلەرگە پەرۋەردىگارىڭلارنىڭ مۇشۇنداق نېمەتلىرى بار تۇرۇقلۇق ئۇنىڭغا) ناھايىتى ئاز شۈكۈر قىلىسىلەر.');
INSERT INTO "saleh" VALUES(7,11,'7:11',' شۈبھىسىزكى بىز سىلەرنى ياراتتۇق، ئاندىن سىلەرنى شەكىلگە كىرگۈزدۇق، ئاندىن پەرىشتىلەرگە: «سىلەر ئادەمگە (ھۆرمەت بىلدۈرۈش يۈزىسىدىن) سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى. ئۇ سەجدە قىلغۇچىلاردىن بولمىدى.');
INSERT INTO "saleh" VALUES(7,12,'7:12',' ئاللاھ (ئىبلىسقا): ‹‹سېنى سەجدە قىلىشقا بۇيرۇغان چېغىمدا نېمىشقا سەجدە قىلمىدىڭ؟» دېدى. ئىبلىس: ‹‹مەن ئۇنىڭدىن ئارتۇق. مېنى ئوتتىن، ئۇنى لايدىن ياراتتىڭ» دېدى. ');
INSERT INTO "saleh" VALUES(7,13,'7:13','ئاللاھ (ئىبلىسقا) ئېيتتى: «سەن بۇ يەردىن (يەنى جەننەتتىن) چۈشۈپ كەت، بۇ يەردە ئۆزەڭنى چوڭ تۇتۇشۇڭغا بولمايدۇ، يوقال، سەن ھەقىقەتەن پەسكەشلەردىنسەن» .');
INSERT INTO "saleh" VALUES(7,14,'7:14',' ئىبلىس: ‹‹ماڭا كىشىلەر قايتا تىرىلدۈرۈلىدىغان كۈنگۈچە (يەنى قىيامەت كۈنىگۈچە) مۆھلەت بەرگىن» دېدى.');
INSERT INTO "saleh" VALUES(7,15,'7:15',' ئاللاھ: ‹‹ساڭا مۆھلەت بېرىلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(7,16,'7:16','ئىبلىس ئېيتتى: ‹‹ مېنى ئازدۇرغانلىقىڭ سەۋەبلىك ئۇلار (يەنى ئادەم ئەلەيھىسسالام ۋە ئۇنىڭ ئەۋلادىغا ۋەسۋەسە قىلىش) ئۈچۈن چوقۇم توغرا يولۇڭ ئۈستىدە ئولتۇرىمەن.');
INSERT INTO "saleh" VALUES(7,17,'7:17',' ئاندىن ئۇلارغا چوقۇم ئالدىدىن، ئارقىسىدىن، ئوڭىدىن، سولىدىن ھۇجۇم قىلىمەن، ئۇلارنىڭ كۆپچىلىكىنىڭ (نېمىتىڭگە) شۈكۈر قىلغۇچىلار ئەمەسلىكىنى كۆرىسەن››.');
INSERT INTO "saleh" VALUES(7,18,'7:18','ئاللاھ ئېيتتى: ‹‹جەننەتتىن ئەيىبلەنگەن ۋە (رەھمىتىمدىن) مەھرۇم قىلىنغان ھالدا چىق، ئۇلارنىڭ ئىچىدىن ساڭا ئەگەشكەنلەرنى قوشۇپ، ھەممىڭلار بىلەن چوقۇم جەھەننەمنى تولدۇرىمەن››.');
INSERT INTO "saleh" VALUES(7,19,'7:19','‹‹ئى ئادەم! سەن ئايالىڭ بىلەن ئىككىڭلار بىللە جەننەتتە تۇرۇڭلار، جەننەتنىڭ مېۋىلىرىدىن خالىغىنىڭلارچە يەڭلار، بۇ دەرەخكە يېقىنلاشماڭلار، (ئۇنىڭغا يېقىنلاشساڭلار) زالىملاردىن بولۇپ قالىسىلەر» .');
INSERT INTO "saleh" VALUES(7,20,'7:20',' ئاندىن شەيتان ئۇلارنىڭ يېپىقلىق ئەۋرەتلىرىنى ئېچىۋېتىش ئۈچۈن، ئۇلارغا ۋەسۋەسە قىلدى، شەيتان ئېيتتى: ‹‹پەرۋەردىگارىڭلارنىڭ سىلەرگە بۇ دەرەخنىڭ مېۋىسىنى يېيىشنى مەنئى قىلغانلىقى پەقەت سىلەرنىڭ پەرىشتە بولۇپ كەتمەسلىكىڭلار ياكى (جەننەتتە) مەڭگۈ تۇرۇپ قالماسلىقىڭلار ئۈچۈندۇر››.');
INSERT INTO "saleh" VALUES(7,21,'7:21',' شۇنىڭدەك ئۇلارغا: ‹‹مەن چوقۇم سىلەرگە سادىقمەن›› دەپ قەسەم ئىچىپ بەردى.');
INSERT INTO "saleh" VALUES(7,22,'7:22','ئالدامچىلىق ئۇسۇللىرى ئارقىلىق ئۇلارنى (يۇقىرى مەرتىۋىدىن تۆۋەن مەرتىۋىگە) چۈشۈردى. دەرەخنىڭ مېۋىسىنى يېگەندىن كېيىنلا ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەتتىكى دەرەخلەرنىڭ ياپراقلىرى بىلەن ئەۋرەتلىرىنى يېپىشقا كىرىشتى. ئۇلارنىڭ پەرۋەردىگارى (ئۇلارغا كايىپ): «مەن سىلەرگە بۇ دەرەخنى (يەنى مېۋىسىنى) يېيىشنى مەنئى قىلمىغانمىدىم؟ شۈبھىسىزكى، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر، دېمىگەنمىدىم؟›› دەپ نىدا قىلدى.');
INSERT INTO "saleh" VALUES(7,23,'7:23','ئۇلار: «پەرۋەردىگارىمىز! بىز ئۆزىمىزگە ئۆزىمىز زۇلۇم قىلدۇق، ئەگەر سەن بىزگە مەغپىرەت قىلمىساڭ، بىزگە رەھىم قىلمىساڭ، بىز چوقۇم زىيان تارتقۇچىلاردىن بولىمىز›› دېدى.');
INSERT INTO "saleh" VALUES(7,24,'7:24','ئاللاھ ئېيتتى: ‹‹سىلەر بىر ـ بىرىڭلارغا (يەنى شەيتان ئىنسانغا، ئىنسان شەيتانغا) دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەجىلىڭلار پۈتكەنگە قەدەر يەر يۈزىدە تۇرىسىلەر ۋە ئۇنىڭدىن پايدىلىنىسىلەر››. ');
INSERT INTO "saleh" VALUES(7,25,'7:25',' ئاللاھ ئېيتتى: ‹‹يەر يۈزىدە ياشايسىلەر، يەر يۈزىدە ئۆلۈسىلەر، (قىيامەت كۈنى) يەنە يەر ئاستىدىن چىقىرىلىسىلەر››.');
INSERT INTO "saleh" VALUES(7,26,'7:26',' ئى ئادەم بالىلىرى! سىلەرگە بىز ھەقىقەتەن ئەۋرىتىڭلارنى ياپىدىغان لىباسنى ۋە زىننەتلىنىدىغان لىباسنى چۈشۈردۇق، تەقۋادارلىق لىباسى ئەڭ ياخشىدۇر، ئەنە شۇلار (يەنى ئىنسان بالىلىرىغا سەترى ئەۋرەت ئۈچۈن لىباس ياراتماق) ئۇلارنىڭ ئىبرەت ئېلىشى ئۈچۈن ئاللاھنىڭ (بەندىلىرىگە بولغان پەزلى ـ مەرھەمىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر.');
INSERT INTO "saleh" VALUES(7,27,'7:27','ئى ئادەم بالىلىرى! شەيتان سىلەرنىڭ ئاتا ـ ئاناڭلارنى (ئازدۇرۇپ) جەننەتتىن چىقىرىۋەتكەندەك، سىلەرنىمۇ ئازدۇرمىسۇن، شەيتان ئۇلارنىڭ ئەۋرەتلىرىنى ئۆزلىرىگە كۆرسىتىش ئۈچۈن كىيىملىرىنى سالغۇزۇۋەتكەن ئىدى (يەنى ئۇلارنىڭ يالىڭاچ بولۇپ قېلىشىغا شەيتان سەۋەبچى بولغان ئىدى). شەيتان ۋە ئۇنىڭ قول ئاستىدىكىلىرى سىلەرنى كۆرەلەيدۇ، سىلەر ئۇلارنى كۆرەلمەيسىلەر، شۈبھىسىزكى، بىز شەيتانلارنى ئىمان ئېيتمايدىغانلارنىڭ دوستى قىلدۇق.');
INSERT INTO "saleh" VALUES(7,28,'7:28','ئۇلار (يەنى مۇشرىكلار) بىرەر يامان ئىشنى قىلغان چاغلىرىدا: ‹‹ئاتا ـ بوۋىلىرىمىزنىڭ مۇشۇنداق قىلغانلىقىنى كۆرگەن ئىدۇق، ئاللاھ بىزنىمۇ شۇنداق قىلىشقا بۇيرۇغان›› دەيدۇ. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، ‹‹ئاللاھ (بەندىلىرىنى) ھەقىقەتەن يامان ئىشقا بۇيرۇمايدۇ، ئۆزلىرىڭلار بىلمەيدىغان نەرسىلەرنى ئاللاھ نامىدىن يالغان ئېيتامسىلەر؟››.');
INSERT INTO "saleh" VALUES(7,29,'7:29','(ئى مۇھەممەد!) ئېيتقىنكى، «پەرۋەردىگارىم مېنى ئادىل بولۇشقا بۇيرۇدى. ھەر سەجدە قىلغاندا پۈتۈنلەي ئاللاھقا يۈزلىنىڭلار، ئاللاھقا ئىخلاس بىلەن ئىبادەت قىلىڭلار، سىلەر ئاللاھ دەسلەپتە پەيدا قىلغان ھالىتىڭلاردا (ئەسلىگە) قايتىسىلەر».');
INSERT INTO "saleh" VALUES(7,30,'7:30','ئاللاھ (سىلەردىن) بىر گۇرۇھنى ھىدايەت قىلدى؛ يەنە بىر گۇرۇھقا گۇمراھلىق تېگىشلىك بولدى. چۈنكى، ئۇلار ئاللاھنى قويۇپ، شەيتانلارنى دوست تۇتتى. شۇنداق تۇرۇقلۇق ئۇلار ئۆزلىرىنى توغرا يولدا دەپ گۇمان قىلىدۇ.');
INSERT INTO "saleh" VALUES(7,31,'7:31','ئى ئادەم بالىلىرى! ھەر ناماز ۋاقتىدا (ياكى تاۋاپ ۋاقتىدا ئەۋرىتىڭلارنى سەترى قىلىپ تۇرىدىغان) كىيىمىڭلارنى كىيىڭلار، يەڭلار، ئىچىڭلار، ئىسراپ قىلماڭلار، ئاللاھ ئىسراپ قىلغۇچىلارنى ھەقىقەتەن ياقتۇرمايدۇ.');
INSERT INTO "saleh" VALUES(7,32,'7:32','ئېيتقىنكى، «ئاللاھ بەندىلىرى ئۈچۈن ياراتقان لىباسلارنى، شېرىن، پاك رىزىقلارنى كىم ھارام قىلدى؟» ئېيتقىنكى، ‹‹ئۇلار بۇ دۇنيادا مۆمىنلەر ئۈچۈن يارىتىلغان (گەرچە ئۇلارغا كۇففارلار شېرىك بولسىمۇ)، ئاخىرەتتە بولسا مۆمىنلەرگىلا خاستۇر». (ئاللاھنىڭ بىر ۋە شېرىكى يوق ئىكەنلىكىنى) بىلىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(7,33,'7:33','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «پەرۋەردىگارىم ئاشكارا ۋە يوشۇرۇن يامان ئىشلارنىڭ ھەممىسىنى، گۇناھلارنى، (كىشىلەرگە) ناھەق چېقىلىشنى، (ئاللاھنىڭ شېرىكى بولۇشقا) ئاللاھ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرنى ئاللاھقا شېرىك كەلتۈرۈشنى، ئۆزۈڭلار بىلمەيدىغان نەرسىلەرنى ئاللاھ نامىدىن قالايمىقان سۆزلەشنى ھارام قىلدى››.');
INSERT INTO "saleh" VALUES(7,34,'7:34','ھەر بىر ئۈممەتنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ، (ھالاك بولىدىغان) ۋاقتى يەتكەندە، ئۇلار بىردەممۇ ئىلگىرى ـ كېيىن بولمايدۇ (يەنى دەرھال ھالاك بولىدۇ).');
INSERT INTO "saleh" VALUES(7,35,'7:35','ئى ئادەم بالىلىرى! ئىچىڭلاردىن مېنىڭ ئايەتلىرىمنى سىلەرگە بايان قىلىپ بېرىدىغان پەيغەمبەرلىرىم كەلگەندە، تەقۋادارلىق قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە قورقۇنچ يوقتۇر، ئۇلار غەم-ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(7,36,'7:36',' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار، ئۇنىڭغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلار دوزاخ ئەھلىدۇر، ئۇلار ھەمىشە دوزاختا قالغۇسىدۇر. ');
INSERT INTO "saleh" VALUES(7,37,'7:37',' ئاللاھقا يالغان چاپلىغان ياكى ئۇنىڭ ئايەتلىرىنى ئىنكار قىلغانلاردىنمۇ زالىم كىشى بارمۇ؟ ئۇلار لەۋھۇلمەھپۇزدا يېزىلغان (رىزىقتىن ۋە ئەجەلدىن) ئۆز نېسىۋىسىنى كۆرىدۇ، ھەتتا بىزنىڭ پەرىشتىلىرىمىز ئۇلارنىڭ جانلىرىنى ئېلىش ئۈچۈن كەلگەندىمۇ، ئۇلارغا: ‹‹ئاللاھنى قويۇپ ئىبادەت قىلغان مەبۇدلىرىڭلار قەيەردە؟›› دەيدۇ، ئۇلار: ‹‹ئۇلار بىزدىن غايىب بولۇپ كەتتى›› دەيدۇ. ئۇلار كاپىر بولغانلىقلىرىغا ئۆزلىرىنىڭ زىيىنىغا گۇۋاھلىق بېرىدۇ (يەنى كاپىر بولغانلىقلىرىغا ئىقرار قىلىدۇ). ');
INSERT INTO "saleh" VALUES(7,38,'7:38',' ئاللاھ (ئايەتلىرىنى ئىنكار قىلغانلارغا) ئېيتىدۇ: ‹‹سىلەردىن ئىلگىرى ئۆتكەن، جىنلاردىن ۋە ئىنسانلاردىن بولغان (كاپىر) ئۈممەتلەر بىلەن بىرلىكتە دوزاخقا كىرىڭلار››. ھەرقاچان بىر ئۈممەت دوزاخقا كىرىدىكەن، ئۇ ئۆزىدىن ئىلگىرىكى ئۈممەتكە (ئۇنىڭغا ئەگىشىپ گۇمراھ بولغانلىقى ئۈچۈن) لەنەت ئوقۇيدۇ. ئۇلارنىڭ ھەممىسى دوزاخقا كىرىپ بولۇپ جەملەشكەندە، ئۇلارنىڭ ئىچىدىكى كېيىن كىرگەنلەر (يەنى ئەگەشكۈچىلەر) ئىلگىرى كىرگەنلەرگە (يەنى باشلىقلىرىغا) قارىتىپ: ‹‹ئى پەرۋەردىگارىمىز! بۇلار بىزنى ئازدۇرغان. ئۇلارغا ئىككى ھەسسە دوزاخ ئازابى بەرگىن›› دەيدۇ. ئاللاھ: ‹‹ھەممىگە ئىككى ھەسسە ئازاب بېرىلىدۇ، لېكىن سىلەر (ئازابنىڭ دەھشىتىنى) بىلمەيسىلەر›› دەيدۇ.');
INSERT INTO "saleh" VALUES(7,39,'7:39','ئۇلارنىڭ ئارىسىدىكى ئىلگىرىكىلەر كېيىنكىلەرگە: ‹‹سىلەرنىڭ بىزدىن ھېچقانداق ئارتۇقچىلىقىڭلار يوق (يەنى گۇمراھلىقتا ۋە ئازابقا تېگىشلىك بولۇشتا بىر بىرىمىزگە ئوخشاشمىز)، قىلمىشلىرىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار›› دەيدۇ.');
INSERT INTO "saleh" VALUES(7,40,'7:40','شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ئۇلارغا ئىمان ئېيتىشقا گەدەنكەشلىك قىلغانلارغا ئاسماننىڭ دەرۋازىسى ئېچىلمايدۇ (يەنى ئۇلارنىڭ ياخشى ئەمەلى ۋە دۇئاسى قوبۇل بولمايدۇ)، تاكى تۆگە يىڭنىنىڭ تۆشۈكىدىن ئۆتكۈچە ئۇلار جەننەتكە كىرمەيدۇ (يەنى ئۇلار ھەرگىزمۇ جەننەتكە كىرمەيدۇ)، گۇناھكارلارغا مۇشۇنداق جازا بېرىمىز.');
INSERT INTO "saleh" VALUES(7,41,'7:41',' دوزاختا ئۇلارنىڭ ئاستىغا سالىدىغان كۆرپىسىمۇ ئوتتىن، ئۈستىگە يېپىنىدىغان يوتقىنىمۇ ئوتتىن بولىدۇ، زالىملارغا مۇشۇنداق جازا بېرىمىز.');
INSERT INTO "saleh" VALUES(7,42,'7:42','ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار ـ كىشىنى پەقەت كۈچى يېتىدىغان ئىشقىلا تەكلىپ قىلىمىز - ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە ھەمىشە قالىدۇ.');
INSERT INTO "saleh" VALUES(7,43,'7:43','ئۇلارنىڭ كۆڭۈللىرىدىكى ئۆچمەنلىكلىرىنى ئېلىپ تاشلىدۇق (يەنى جەننەتكە كىرگەنلەر ئارىسىدا ئۆزئارا ئاغرىق ـ ئاداۋەت بولمايدۇ). ئۇلارنىڭ (سارايلىرى) ئاستىدىن (جەننەتنىڭ) ئۆستەڭلىرى ئېقىپ تۇرىدۇ. ئۇلار: «جىمى ھەمدۇسانا بىزنى بۇ (نېمەتلەرنى ھاسىل قىلىشقا) يېتەكلىگەن ئاللاھقا بولسۇنكى، ئاللاھ بىزنى يېتەكلىمىگەن بولسا، توغرا يول تاپمىغان بولاتتۇق. ھەقىقەتەن پەرۋەردىگارىمىزنىڭ پەيغەمبەرلىرى بىزگە ھەقنى ئېلىپ كەلدى» دەيدۇ. ئۇلارغا: «قىلغان ئەمەلىڭلار ئۈچۈن سىلەرگە بېرىلگەن جەننەت مانا مۇشۇ›› دەپ نىدا قىلىنىدۇ (يەنى پەرىشتىلەر شۇنداق دەپ نىدا قىلىدۇ).');
INSERT INTO "saleh" VALUES(7,44,'7:44',' ئەھلى دوزاخقا: ‹‹بىز پەرۋەردىگارىمىز بىزگە ۋەدە قىلغان نەرسىنى ھەق تاپتۇق، سىلەرمۇ پەرۋەردىگارىڭلار ۋەدە قىلغان نەرسىنى ھەق تاپتىڭلارمۇ؟›› دەپ توۋلايدۇ. ئۇلار: ‹‹ھەئە›› دەيدۇ، شۇنىڭ بىلەن بىر جاكارچى ئۇلارنىڭ ئارىسىدا (مۇنداق دەپ) جاكارلايدۇ: ‹‹زالىملارغا ئاللاھنىڭ لەنىتى بولسۇن.');
INSERT INTO "saleh" VALUES(7,45,'7:45','زالىملار كىشىلەرنى ئاللاھنىڭ داغدام يولىدىن توسىدۇ، ئۇنىڭ ئەگرى يولدا بولۇشىنى تىلەيدۇ ھەمدە ئۇلار ئاخىرەتكە ئىشەنمەيدۇ. ');
INSERT INTO "saleh" VALUES(7,46,'7:46','ئۇلارنىڭ ئارىسىدا بىر توسما بولىدۇ. توسمىنىڭ ئۈستىدە (ئەئرافتا) ئەھلى جەننەت ۋە ئەھلى دوزاخنى ئۇلارنىڭ سىماسىدىن تونۇيدىغان ئادەملەر (يەنى ئەھلى ئەئراف) بولىدۇ. ئۇلار ئەھلى جەننەتكە: «سىلەرگە ئامانلىق تىلەيمىز›› دەپ توۋلايدۇ. ئۇلار جەننەتكە كىرمىگەن، (ئەمما) كىرىش ئۈمىدىدە تۇرغانلاردۇر. ');
INSERT INTO "saleh" VALUES(7,47,'7:47','ئۇلارنىڭ كۆزلىرى ئەھلى دوزاخ تەرەپكە بۇرۇلغاندا: ‹‹ئى پەرۋەردىگارىمىز!بىزنى زالىم قەۋم بىلەن بىللە قىلمىغىن›› دەيدۇ.');
INSERT INTO "saleh" VALUES(7,48,'7:48',' ئەئرافتىكىلەر (يەنى ياخشىلىقلىرى بىلەن يامانلىقلىرى تەڭ بولۇپ قالغان، جەننەتتىمۇ ئەمەس، دوزاختىمۇ ئەمەس كىشىلەر) سىماسىدىن تونۇۋالىدىغان (دوزىخى) ئادەملەرگە: ‹‹توپلىغان پۇل ـ ماللىرىڭلار ۋە تەكەببۇرلۇقۇڭلار سىلەرگە نېمىگە ئەسقاتتى؟›› دەپ توۋلايدۇ. ');
INSERT INTO "saleh" VALUES(7,49,'7:49','(ئەئرافتىكىلەر دوزىخىلارغا مۆمىنلەرنىڭ پېقىرلىرىنى كۆرسىتىپ تۇرۇپ) «سىلەر ئاللاھنىڭ رەھمىتىگە ئېرىشەلمەيدۇ، دەپ قەسەم قىلغان كىشىلەر مۇشۇلارمۇ؟ (مانا بۇلارغا) جەننەتكە كىرىڭلار، سىلەرگە (كەلگۈسىدىن) قورقۇنچ يوقتۇر، سىلەر غەم-ئەندىشە قىلمايسىلەر دېيىلدى دەيدۇ».');
INSERT INTO "saleh" VALUES(7,50,'7:50',' ئەھلى دوزاخ ئەھلى جەننەتكە: ‹‹سۇدىن ياكى ئاللاھ سىلەرگە بەرگەن ئىچىدىغان نەرسىلەردىن بىزگە بىر ئاز قۇيۇپ بەرسەڭلار!›› دەپ توۋلايدۇ، ئەھلى جەننەت ئۇلارغا (جاۋابەن): ‹‹ئاللاھ ئۇلارنى كاپىرلارغا ھارام قىلغان›› دەيدۇ. ');
INSERT INTO "saleh" VALUES(7,51,'7:51','كاپىرلار دىنلىرىنى مەسخىرە ۋە ئويۇنچۇق قىلىۋالدى، ئۇلارنى دۇنيا تىرىكچىلىكى ئالدىدى، ئۇلارنىڭ بۈگۈنكى كۈنگە مۇلاقات بولۇشنى ئۇنتۇغانلىقلىرى ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرىغا ئوخشاش، بىز بۈگۈن ئۇلارنى ئۇنتۇيمىز (يەنى ئۇلارنى دوزاختا قالدۇرىمىز).');
INSERT INTO "saleh" VALUES(7,52,'7:52','ئۇلارغا كىتاب (يەنى قۇرئان) بەردۇق، ئۇنى (يەنى قۇرئاننىڭ مەنىلىرىنى) بىلىش ئاساسىدا بايان قىلدۇق، ئۇنى ئىمان ئېيتقان قەۋمگە ھىدايەت قىلدۇق.');
INSERT INTO "saleh" VALUES(7,53,'7:53','ئۇلار (يەنى كۇففارلار) پەقەت (ئۆزلىرىگە ۋەدە قىلىنغان ئازابنىڭ) نەتىجىسىنىلا كۈتىدۇ. ئۇنىڭ نەتىجىسى كەلگەن كۈنى (يەنى قىيامەت كۈنى)، ئىلگىرى كىتابقا ئەمەل قىلمىغانلار: ‹‹پەرۋەردىگارىمىزنىڭ ئەلچىلىرى ھەقىقەتنى ئېلىپ كەلگەن ئىدى (بىز ئۇلارغا ئىمان ئېيتماپتۇق، بۈگۈن) بىزگە شاپائەت قىلىدىغان شاپائەتچىلەر بارمىدۇ؟ ياكى گۇناھ قىلىشتىن قول ئۈزۈپ، ياخشى ئەمەللەرنى قىلىش ئۈچۈن دۇنياغا قايتۇرۇلۇشىمىزغا بولامدىغاندۇ؟» دەيدۇ. ئۇلار ئۆزلىرىگە زىيان سالدى، ئۇلارنىڭ ئاللاھنىڭ شېرىكلىرى دەپ ئىپتىرا قىلغان مەبۇدلىرى ئۇلارنىڭ كۆزىدىن غايىب بولدى.');
INSERT INTO "saleh" VALUES(7,54,'7:54',' شۈبھىسىزكى، پەرۋەردىگارىڭلار ئاللاھ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە (يەنى ئالتە دەۋردە) ياراتتى، ئاندىن (ئۆزىنىڭ ئۇلۇغلۇقىغا لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى. ئاللاھ كېچە بىلەن (كېچىنىڭ قاراڭغۇلۇقى بىلەن) كۈندۈزنى ياپىدۇ، كېچە بىلەن كۈندۈز بىر ـ بىرىنى قوغلىشىدۇ (يەنى داۋاملىق ئورۇن ئالمىشىپ تۇرىدۇ)؛ ئاللاھ قۇياش، ئاي ۋە يۇلتۇزلارنى ئاللاھنىڭ ئەمرىگە بويسۇندۇرۇلغۇچى قىلىپ ياراتتى، يارىتىش ۋە ئەمر قىلىش (يەنى كائىناتنى تەسەررۇپ قىلىش) راستىنلا ئاللاھنىڭ ئىلكىدىدۇر. ئالەملەرنىڭ پەرۋەردىگارى ئاللاھنىڭ دەرىجىسى كاتتىدۇر.');
INSERT INTO "saleh" VALUES(7,55,'7:55','پەرۋەردىگارىڭلارغا تۆۋەنچىلىك بىلەن يوشۇرۇن دۇئا قىلىڭلار، (دۇئا قىلغاندا كەلسە ـ كەلمەس سۆزلەپ، توۋلاپ) ھەددىدىن ئاشۇرۋەتكەنلەرنى ئاللاھ ياقتۇرمايدۇ. ');
INSERT INTO "saleh" VALUES(7,56,'7:56',' ئاللاھ (پەيغەمبەرلەرنى ئەۋەتىپ) يەر يۈزىنى تۈزىگەندىن كېيىن، يەر يۈزىدە بۇزۇقچىلىق قىلماڭلار (ئاللاھنىڭ ئازابىدىن) قورققان ۋە (رەھمىتىنى) ئۈمىد قىلغان ھالدا دۇئا قىلىڭلار. شۈبھىسىزكى ئاللاھنىڭ رەھمىتى ياخشىلىق قىلغۇچىلارغا يېقىندۇر.');
INSERT INTO "saleh" VALUES(7,57,'7:57',' ئاللاھنىڭ رەھمىتى (يەنى يامغۇر) مەيدانغا كېلىش ئالدىدا، ئاللاھ بېشارەتچى قىلىپ شاماللارنى ئەۋەتىدۇ. شاماللار ئېغىر بۇلۇتلارنى كۆتۈرگەن چاغدا، ئۇلارنى ئۆلۈك (يەنى قاغجىراپ كەتكەن گىياھسىز) جايلارغا سۈرىمىز. ئۇنىڭدىن يامغۇر ياغدۇرۇپ تۈرلۈك ـ تۈرلۈك مېۋىلەرنى چىقىرىمىز. ئىبرەت ئېلىشىڭلار ئۈچۈن (زېمىندىن مېۋىلەرنى چىقارغاندەك، قىيامەتتە) ئۆلۈكلەرنى قەبرىلىرىدىن چىقىرىمىز.');
INSERT INTO "saleh" VALUES(7,58,'7:58','مۇنبەت يەرنىڭ گىياھى ئاللاھنىڭ ئىزنى بىلەن تولۇق ئۈنىدۇ (بۇ ۋەز ـ نەسىھەتكە قۇلاق سېلىپ، ئۇنىڭدىن پايدىلىنىدىغان مۆمىننىڭ مىسالىدۇر). ناچار يەرنىڭ گىياھى ئاز ئۆسىدۇ (بۇ ۋەز ـ نەسىھەتتىن پايدىلانمايدىغان كاپىرنىڭ مىسالىدۇر). شۈكۈر قىلىدىغان قەۋم ئۈچۈن ئايەتلىرىمىزنى مۇشۇنداق شەكىللەردە بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(7,59,'7:59','شەك - شۈبھىسىزكى، نۇھنى ئۆز قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق. ئۇ: ‹‹ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا مەبۇد (بەرھەق) يوقتۇر. مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىغا قېلىشىڭلاردىن قورقىمەن›› دېدى.');
INSERT INTO "saleh" VALUES(7,60,'7:60','قەۋمىنىڭ چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن روشەن گۇمراھلىقتا كۆرۈمىز›› دېدى.');
INSERT INTO "saleh" VALUES(7,61,'7:61','نۇھ ئېيتتى: ‹‹ئى قەۋمىم! مەن قىلچىمۇ گۇمراھ ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(7,62,'7:62','سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، سىلەرگە نەسىھەت قىلىمەن، مەن ئاللاھنىڭ ۋەھىي قىلىشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن');
INSERT INTO "saleh" VALUES(7,63,'7:63',' سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن، (كۇفرىدىن) ساقلىنىشىڭلار ۋە ئاللاھنىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، سىلەرگە ئاراڭلاردىكى بىر كىشى ئارقىلىق پەرۋەردىگارىڭلارنىڭ ۋەھىيسى كېلىشىدىن ئەجەبلىنەمسىلەر؟›› ');
INSERT INTO "saleh" VALUES(7,64,'7:64',' ئۇلار نۇھنى ئىنكار قىلدى، نۇھنى ۋە ئۇنىڭ بىلەن كېمىدە بىللە بولغانلارنى قۇتقۇزدۇق، ئايەتلىرىمىزنى ئىنكار قىلغانلارنى پۈتۈنلەي (سۇغا) غەرق قىلدۇق. شۈبھىسىزكى، ئۇلار (ھەقنى كۆرۈشتىن دىللىرى) كور قەۋم ئىدى.');
INSERT INTO "saleh" VALUES(7,65,'7:65','ئاد (قەۋمى) گە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد: ‹‹ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا مەبۇد (بەرھەق) يوقتۇر. (ئۇنىڭ ئازابىدىن) قورقمامسىلەر؟›› دېدى.');
INSERT INTO "saleh" VALUES(7,66,'7:66',' ئۇنىڭ قەۋمىنىڭ ئىمان ئېيتمىغان چوڭلىرى: ‹‹بىز سېنى ھەقىقەتەن ئەخمەق ھېسابلايمىز، بىز سېنى، ئەلۋەتتە، (پەيغەمبەرلىك دەۋايىڭدا) يالغانچىلاردىن دەپ گۇمان قىلىمىز›› دېدى.');
INSERT INTO "saleh" VALUES(7,67,'7:67','ھۇد ئېيتتى: ‹‹ئى قەۋمىم! مەن ئەخمەق ئەمەسمەن، لېكىن مەن ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(7,68,'7:68',' سىلەرگە پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزىمەن، مەن سىلەرگە ئىشەنچىلىك نەسىھەتچىمەن. ');
INSERT INTO "saleh" VALUES(7,69,'7:69',' پەرۋەردىگارىڭلارنىڭ ۋەھىيسى سىلەرنى ئاگاھلاندۇرۇش ئۈچۈن ئۆز ئىچىڭلاردىكى بىر كىشى ئارقىلىق كەلگەنلىكىدىن ئەجەبلىنەمسىلەر؟›› ئۆز ۋاقتىدا نۇھنىڭ قەۋمى ھالاك بولغاندىن كېيىن، ئاللاھنىڭ سىلەرنى ئۇلارنىڭ ئورۇنباسارلىرى قىلغانلىقىنى، ۋۇجۇدۇڭلارنى قامەتلىك، بەستلىك قىلغانلىقىنى ياد ئېتىڭلار، سىلەر بەختكە ئېرىشىشىڭلار ئۈچۈن ئاللاھنىڭ نېمەتلىرىنى ياد ئېتىڭلار.');
INSERT INTO "saleh" VALUES(7,70,'7:70','ئۇلار: ‹‹(ئى ھۇد!) ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان مەبۇدلارنى تاشلاپ، يالغۇز بىر ئاللاھقا ئىبادەت قىلىشىمىزغا دەۋەت قىلغىلى كەلدىڭمۇ؟ ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرگىن›› دېدى.');
INSERT INTO "saleh" VALUES(7,71,'7:71',' ھۇد: ‹‹سىلەرگە چوقۇم پەرۋەردىگارىڭلارنىڭ ئازابى ۋە غەزىپى نازىل بولىدۇ، سىلەر ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار ئات قويۇۋالغان، بۇ ھەقتە (ئۇلارغا ئىبادەت قىلىشقا) ئاللاھ ھېچقانداق دەلىل نازىل قىلمىغان (بۇتلارنىڭ) ئىسىملىرى ئۈستىدە مەن بىلەن مۇنازىرىلىشەمسىلەر؟ كۈتۈڭلار مەنمۇ ھەقىقەتەن سىلەر بىلەن بىرلىكتە (سىلەرگە نازىل بولىدىغان ئازابنى) كۈتىمەن›› دېدى.');
INSERT INTO "saleh" VALUES(7,72,'7:72','بىز ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغۇچىلارنى (يەنى مۆمىنلەرنى) رەھمىتىمىز بىلەن قۇتقۇزدۇق. بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تەلتۆكۈس ھالاك قىلدۇق، ئۇلار ئىمان ئېيتمىغان ئىدى.');
INSERT INTO "saleh" VALUES(7,73,'7:73','سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ ئەۋەتتۇق). ئۇ: «ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلار تەرىپىدىن (مېنىڭ پەيغەمبەرلىكىمنى ئىسپاتلايدىغان) روشەن مۆجىزە كەلدى. مانا بۇ ـ ئاللاھ (بىۋاسىتە) ياراتقان چىشى تۆگىمۇ سىلەرگە مېنىڭ مۆجىزەمدۇر. ئۇنى ئاللاھنىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارىي ئوتلىسۇن، ئۇنىڭغا زىيان ـ زەخمەت يەتكۈزمەڭلار. بولمىسا سىلەرنى قاتتىق ئازاب ھالاك قىلىدۇ. ');
INSERT INTO "saleh" VALUES(7,74,'7:74',' ياد ئېتىڭلاركى، ئۆز ۋاقتىدا ئاللاھ سىلەرنى ئاد قەۋمىدىن كېيىن ئۇلارنىڭ ئورۇنباسارلىرى قىلدى، سىلەرنى (ھىجر) زېمىنىغا ئورۇنلاشتۇردى، ئۇنىڭ تۈزلەڭلىرىگە ئىمارەتلەر سالدىڭلار، تاغلارنى تېشىپ ئۆيلەر ياسىدىڭلار. ئاللاھنىڭ نېمەتلىرىنى ياد ئېتىڭلار، زېمىندا بۇزغۇنچىلىق قىلماڭلار» دېدى.');
INSERT INTO "saleh" VALUES(7,75,'7:75',' ئۇنىڭ قەۋمىنىڭ تەكەببۇر چوڭلىرى بوزەك قىلىنغانلارغا يەنى ئۇلارنىڭ ئىچىدىكى مۆمىنلەرگە: «سىلەر سالىھنى ئۇنىڭ پەرۋەردىگارى تەرىپىدىن ئەۋەتىلگەن پەيغەمبەر دەپ بىلەمسىلەر؟» دېدى. ئۇلار: «بىز ئۇنىڭ پەيغەمبەرلىكىگە ئەلۋەتتە ئىشىنىمىز» دېدى.');
INSERT INTO "saleh" VALUES(7,76,'7:76',' ھېلىقى تەكەببۇرلار: «سىلەر ئىشەنگەنگە بىز ھەرگىز ئىشەنمەيمىز» دېدى.');
INSERT INTO "saleh" VALUES(7,77,'7:77',' ئۇلار چىشى تۆگىنى بوغۇزلىدى، پەرۋەردىگارىنىڭ ئەمرىنى تۇتۇشتىن باش تارتتى ۋە ئى سالىھ «راستتىنلا پەيغەمبەرلەردىن بولساڭ، بىزگە ۋەدە قىلغان ئازابنى كەلتۈرۈپ باق» دېدى.');
INSERT INTO "saleh" VALUES(7,78,'7:78','ئۇلارغا قاتتىق ئازاب يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى.');
INSERT INTO "saleh" VALUES(7,79,'7:79',' ئۇ (ئۇلار ھالاك بولغاندىن كېيىن) ئۇلارغا ئارقىسىنى قىلدى ۋە: «ئى قەۋمىم! سىلەرگە ھەقىقەتەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم، سىلەرگە نەسىھەت قىلدىم، لېكىن سىلەر نەسىھەت قىلغۇچىلارنى ياقتۇرمىدىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(7,80,'7:80',' لۇتنى (ئەھلى سەدومغا پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ ئۆز قەۋمىگە (ئۇلارنى ئەيىبلەش يۈزىسىدىن) مۇنداق دېگەن ئىدى: «سىلەر شۇنداق قەبىھ ئىشنى قىلامسىلەر؟ مۇنداق قەبىھ ئىشنى سىلەردىن ئىلگىرى جاھان ئەھلىدىن ھېچقانداق ئادەم قىلمىغان ئىدى.');
INSERT INTO "saleh" VALUES(7,81,'7:81','سىلەر ھەقىقەتەن ئاياللارنى تاشلاپ قويۇپ، ئەرلەر بىلەن جىنسىي تەلىپىڭلارنى قاندۇرىسىلەر، سىلەر ھەقىقەتەن ھەددىدىن ئاشقۇچى قەۋمسىلەر».');
INSERT INTO "saleh" VALUES(7,82,'7:82','لۇتنىڭ قەۋمىنىڭ بىردىنبىر جاۋابى: «لۇتنى ئۇنىڭ ئەگەشكۈچىلىرى بىلەن قوشۇپ شەھىرىڭلاردىن چىقىرىۋېتىڭلار، چۈنكى ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(7,83,'7:83','لۇتنى ئۇنىڭ خوتۇنىدىن باشقا، تەۋە كىشىلىرى بىلەن بىللە (ئۇنىڭ قەۋمىگە نازىل بولغان ئازابتىن) قۇتقۇزدۇق، پەقەت خوتۇنىلا (شەھەردە) قېلىپ ھالاك بولغۇچىلاردىن بولدى.');
INSERT INTO "saleh" VALUES(7,84,'7:84','ئۇلارغا بىر تۈرلۈك يامغۇر (يەنى تاش) ياغدۇردۇق، گۇناھكارلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(7,85,'7:85',' مەديەن (خەلقىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۇ (ئۆز قەۋمىگە) ئېيتتى: « ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر. سىلەرگە پەرۋەردىگارىڭلاردىن ھەقىقەتەن (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) مۆجىزە كەلدى، ئۆلچەمنى ۋە تارازىنى توغرىلاڭلار، كىشىلەرنىڭ نەرسىلىرىنى كەم بەرمەڭلار. (ئاللاھ پەيغەمبەرلەرنى ئەۋەتىپ) زېمىننى تۈزىگەندىن كېيىن، زېمىندا (گۇناھلار قىلىپ) بۇزغۇنچىلىق قىلماڭلار، ئەگەر مۆمىن بولساڭلار، ئەنە شۇ سىلەر ئۈچۈن ياخشىدۇر.');
INSERT INTO "saleh" VALUES(7,86,'7:86','سىلەر ئاللاھقا ئىمان ئېيتقانلارنى قورقۇتقان، ئاللاھنىڭ يولىدىن توسقان ۋە ئۇ يولنىڭ ئەگرىلىكىنى تىلىگەن ھالدا يوللاردا ئولتۇرماڭلار. ئەسلىدە سىلەر ئاز ئىدىڭلار، ئاللاھنىڭ سىلەرنىڭ سانىڭلارنى كۆپەيتكەنلىكىنى ياد ئېتىڭلار، بۇزۇقچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قاراڭلار.');
INSERT INTO "saleh" VALUES(7,87,'7:87',' ئەگەر سىلەردىن بىر گۇرۇھ ئادەم مېنىڭ پەيغەمبەرلىكىمگە ئىشەنگەن، يەنە بىر گۇرۇھ ئادەم ئىشەنمىگەن بولسا، ئاللاھ بىزنىڭ ئارىمىزدا ھۆكۈم چىقارغىچە سەۋر قىلىپ تۇرۇڭلار. ئاللاھ ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇر». ');
INSERT INTO "saleh" VALUES(7,88,'7:88','شۇئەيب قەۋمىنىڭ (ئىماندىن باش تارتقان) تەكەببۇر چوڭلىرى: «ئى شۇئەيب! سېنى چوقۇم ئەگەشكۈچىلىرىڭ (يەنى ساڭا ئىمان ئېيتقانلار) بىلەن قوشۇپ شەھىرىمىزدىن ھەيدەپ چىقىرىمىز، ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى. شۇئەيب: «بىز (بۇنىڭ ھەر ئىككىلىسىنى) يامان كۆرىدىغان تۇرساق (بىزنى يەنىلا شۇنىڭغا مەجبۇر قىلامسىلەر؟)» دېدى.');
INSERT INTO "saleh" VALUES(7,89,'7:89',' (شۇئەيب يەنە ئېيتتى) «ئاللاھ بىزنى سىلەرنىڭ دىنىڭلاردىن قۇتۇلدۇرغاندىن كېيىن ئۇنىڭغا قايتساق، ئەلۋەتتە، ئاللاھقا يالغان چاپلىغان بولىمىز. پەرۋەردىگارىمىز ئاللاھ خالىسىلا، بىز سىلەرنىڭ دىنىڭلارغا قايتمايمىز. پەرۋەردىگارىمىزنىڭ ئىلمى ھەممىنى ئۆز ئىچىگە ئالىدۇ، ئاللاھقا تەۋۋەككۈل قىلدۇق (يەنى ھەممە ئىشىمىزنى ئاللاھقا تاپشۇرىمىز). پەرۋەردىگارىمىز! بىز بىلەن قەۋمىمىزنىڭ ئارىسىدا ھەق ھۆكۈم چىقارغىن، سەن ھۆكۈم چىقارغۇچىلارنىڭ ئەڭ ياخشىسىدۇرسەن».');
INSERT INTO "saleh" VALUES(7,90,'7:90','ئۇنىڭ قەۋمىنىڭ ئىمانسىز چوڭلىرى: «ئەگەر سىلەر شۇئەيبكە ئەگەشسەڭلار، شۈبھىسىزكى، چوقۇم زىيان تارتىسىلەر» دېدى. ');
INSERT INTO "saleh" VALUES(7,91,'7:91','ئۇلارغا قاتتىق زىلزىلە يۈزلەندى، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى.');
INSERT INTO "saleh" VALUES(7,92,'7:92','شۇئەيبنى ئىنكار قىلغانلار (ئاللاھنىڭ ھالاك قىلىشى بىلەن) گويا بۇ يەردە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى. شۇئەيبنى ئىنكار قىلغانلار زىيان تارتقۇچىلاردىن بولدى.');
INSERT INTO "saleh" VALUES(7,93,'7:93','شۇئەيب ئۇلارغا ئارقىسىنى قىلىپ: «ئى قەۋمىم! سىلەرگە مەن ھەقىقەتەن پەرۋەدىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم ۋە نەسىھەت قىلدىم، مەن قانداقمۇ كاپىر قەۋمگە قايغۇراي!» دېدى.');
INSERT INTO "saleh" VALUES(7,94,'7:94','بىز مەيلى قايسى شەھەرگە بولسۇن، بىرەر پەيغەمبەر ئەۋەتكەن بولساق (شەھەر ئەھلى ئۇنى ئىنكار قىلغان بولسا)، ئۇلارنىڭ ئاللاھقا يالۋۇرۇشلىرى (تەۋبە قىلىشلىرى) ئۈچۈن، ئۇلارنى نامراتلىققا، كېسەللىككە گىرىپتار قىلدۇق.');
INSERT INTO "saleh" VALUES(7,95,'7:95',' ئاندىن (ئۇلارنىڭ بېشىغا كەلگەن) كۈلپەتنى نېمەتكە ئايلاندۇردۇق، شۇنىڭ بىلەن ئۇلار كۆپەيدى. ئۇلار: «بىزنىڭ ئاتا ـ بوۋىلىرىمىزنىڭ بېشىغا ھەقىقەتەن كۈلپەتلەر، نېمەتلەر كەلگەن ئىدى (بۇ ـ زاماننىڭ ئادىتى؛ ئاللاھ تەرىپىدىن كەلگەن ئوقۇبەت ئەمەس)» دېدى. ئۇلار خەۋەرسىز تۇرغاندا، ئۇلارنى ئۇشتۇمتۇت ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(7,96,'7:96',' (پەيغەمبەرلىرىنى ئىنكار قىلغانلىقتىن ھالاك قىلىنغان) شەھەرلەرنىڭ ئاھالىسى ئىمان ئېيتقان ۋە (كۇفرىدىن، گۇناھلاردىن) ساقلانغان بولسا ئىدى، ئەلۋەتتە، ئۇلارنى ئاسمان ـ زېمىننىڭ پاراۋانلىقلىرىغا مۇيەسسەر قىلاتتۇق. لېكىن ئۇلار (پەيغەمبەرلەرنى) ئىنكار قىلدى، شۇڭا ئۇلارنى ئۆز قىلمىشلىرى تۈپەيلىدىن ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(7,97,'7:97','(پەيغەمبەرلەرنى ئىنكار قىلىدىغان) شەھەرلەرنىڭ ئاھالىسى بىزنىڭ ئازابىمىزنىڭ ئۆزلىرىگە كېچىسى (غەپلەتتە) ئۇخلاۋاتقانلىرىدا كېلىشىدىن قورقمامدۇ؟');
INSERT INTO "saleh" VALUES(7,98,'7:98','شەھەرلەر ئاھالىسى بىزنىڭ ئازابىمىزنىڭ چۈش ۋاقتىدا ـ ئۇلار ئويناپ تۇرغانلىرىدا كېلىشىدىن قورقمامدۇ.');
INSERT INTO "saleh" VALUES(7,99,'7:99','ئۇلار ئاللاھنىڭ مەكرىدىن (يەنى ئۇلار تۇيۇقسىز تۇرغاندا ئاللاھنىڭ ئازاب نازىل قىلىشىدىن) قورقمامدۇ؟ پەقەت زىيان تارتقۇچى قەۋملا ئاللاھنىڭ مەكرىدىن قورقمايدۇ.');
INSERT INTO "saleh" VALUES(7,100,'7:100','زېمىننىڭ ئىلگىرىكى ئىگىلىرى (ھالاك بولغان) دىن كېيىن، (زېمىنغا) ۋارىس بولغۇچىلارنى، خالىساق، گۇناھى تۈپەيلىدىن ھالاك قىلىدىغانلىقىمىز، دىللىرىنى پىچەتلىۋېتىدىغانلىقىمىز ئۇلارغا (يەنى زېمىنغا ۋارىسلىق قىلغۇچىلارغا) ئايان بولمىدىمۇ؟ ئۇلار (ھەق سۆزنى) ئاڭلىمايدۇ. ');
INSERT INTO "saleh" VALUES(7,101,'7:101',' ئاشۇ شەھەرلەر (ئاھالىسى) نىڭ بەزى خەۋەرلىرىنى (ئى مۇھەممەد!) ساڭا ھېكايە قىلىپ بېرىمىز. شەك ـ شۈبھىسىزكى، پەيغەمبەرلىرى ئۇلارغا مۆجىزىلەر ئېلىپ كەلگەن ئىدى، ئۇلار ئىلگىرى (ھەقنى) ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇ مۆجىزىلەرگە) ئىمان ئېيتمىدى. ئاللاھ كاپىرلارنىڭ دىللىرىنى ئەنە شۇنداق پېچەتلەيدۇ.');
INSERT INTO "saleh" VALUES(7,102,'7:102',' ئۇلارنىڭ تولىسىنىڭ بەرگەن ۋەدىسىگە ۋاپا قىلىدىغانلىقىنى بايقىمىدۇق. ئۇلارنىڭ تولىسىنىڭ شەكسىز پاسىقلار (يەنى ئاللاھنىڭ ئەمر ـ پەرمانىدىن باش تارتقۇچىلار) ئىكەنلىكىنى بايقىدۇق.');
INSERT INTO "saleh" VALUES(7,103,'7:103',' مەزكۇز پەيغەمبەرلەردىن كېيىن، مۇسانى مۆجىزىلىرىمىز بىلەن، (مىسىرنىڭ مۇسا زامانىدىكى پادىشاھى) پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق. ئۇلار بۇ مۆجىزىلەرنى ئىنكار قىلدى، بۇزغۇنچىلىق قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(7,104,'7:104','مۇسا ئېيتتى: «ئى پىرئەۋن! مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئەۋەتكەن پەيغەمبەرمەن. ');
INSERT INTO "saleh" VALUES(7,105,'7:105','ئاللاھ نامىدىن پەقەت ھەقىقەتتىن باشقىنى ئېيتماسلىققا لايىقمەن. راستتىنلا پەرۋەردىگارىڭلار تەرىپىدىن سىلەرگە (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) بىر مۆجىزە ئېلىپ كەلدىم. شۇنىڭ ئۈچۈن، ئىسرائىل ئەۋلادىنى مەن بىلەن قويۇپ بەرگىن (مەن بىلەن بىللە ئاتا ـ بوۋىلىرىنىڭ ۋەتىنى مۇقەددەس زېمىنغا بارسۇن)».');
INSERT INTO "saleh" VALUES(7,106,'7:106',' پىرئەۋن (مۇساغا): «ئەگەر سەن (پەرۋەردىگارىڭ تەرىپىدىن) بىر مۆجىزە ئېلىپ كەلگەن بولساڭ، (بۇ دەۋايىڭدا) راستچىللاردىن بولساڭ، ئۇنى بىزگە كۆرسەتكىن» دېدى.');
INSERT INTO "saleh" VALUES(7,107,'7:107','مۇسا ھاسىسىنى تاشلىۋېدى، ئۇ ناگاھان ئاشكارا بىر ئەجدىھاغا ئايلاندى.');
INSERT INTO "saleh" VALUES(7,108,'7:108',' قولىنى (قولتۇقىدىن) چىقىرىۋىدى، ئۇ ناگاھان قارىغۇچىلارغا (نۇر چاقنىتىپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى.');
INSERT INTO "saleh" VALUES(7,109,'7:109',' پىرئەۋن قەۋمنىڭ چوڭلىرى ئېيتتى: «بۇ ھەقىقەتەن ناھايىتى ئۇستا سېھىرگەر ئىكەن. ');
INSERT INTO "saleh" VALUES(7,110,'7:110','ئۇ سىلەرنى زېمىنىڭلار (يەنى مىسىر زېمىنى) دىن ھەيدەپ چىقىرىۋەتمەكچى. نېمە مەسلىھەت بېرىسىلەر؟»');
INSERT INTO "saleh" VALUES(7,111,'7:111','ئۇلار ئېيتتى: «(ئۇلارنىڭ ئىشىنى ئۆزۈڭ بىر پىكىرگە كەلگۈچە) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىغقۇچى كىشىلەرنى ئەۋەتكىن.');
INSERT INTO "saleh" VALUES(7,112,'7:112',' ئۇلار بارلىق ئۇستا سېھىرگەرلەرنى سېنىڭ ھۇزۇرۇڭغا ئېلىپ كەلسۇن».');
INSERT INTO "saleh" VALUES(7,113,'7:113','(پىرئەۋن سېھىرگەرلىرىنى يىغىشقا ئادەملىرىنى ئەۋەتتى) سېھىرگەرلەر پىرئەۋننىڭ قېشىغا كەلدى، ئۇلار: «ئەگەر بىز غەلىبە قىلساق، بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(7,114,'7:114','پىرئەۋن: «ھەئە، (مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن كىشىلىرىم بولۇپ قالىسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(7,115,'7:115','ئۇلار (يەنى سېھىرگەرلەر): «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن، ياكى بىز (ئۆزىمىزنىڭكىنى) ئاۋۋال تاشلامدۇق؟» دېدى.');
INSERT INTO "saleh" VALUES(7,116,'7:116','مۇسا: «سىلەر تاشلاڭلار!» دېدى. ئۇلار ھاسا، ئارغامچىلارنى تاشلاپ كىشىلەرنىڭ كۆزلىرىنى باغلىدى، ئۇلارنى (يەنى كىشىلەرنى) قاتتىق چۆچۈتۈۋەتتى، ئۇلار (كىشىلەرنىڭ كۆزلىرىگە) چوڭ (كۆرۈنىدىغان) سېھىرنى كۆرسەتتى.');
INSERT INTO "saleh" VALUES(7,117,'7:117','مۇساغا: «ھاساڭنى تاشلىغىن » دەپ ۋەھىي قىلدۇق، (مۇسا ھاسىسىنى تاشلىۋىدى، ئۇ ئەجدىھاغا ئايلىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى دەرھال يۇتۇۋەتتى.');
INSERT INTO "saleh" VALUES(7,118,'7:118','ھەقىقەت ئاشكارا بولدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرى بەربات بولدى. ');
INSERT INTO "saleh" VALUES(7,119,'7:119',' بۇ يەردە ئۇلار (يەنى پىرئەۋن بىلەن قەۋمى) مەغلۇپ بولدى، خار بولغان ھالدا (شەھەرگە قايتتى).');
INSERT INTO "saleh" VALUES(7,120,'7:120','سېھىرگەرلەر سەجدىگە باردى.');
INSERT INTO "saleh" VALUES(7,121,'7:121','ئۇلار دېدى: «ئالەملەرنىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق.');
INSERT INTO "saleh" VALUES(7,122,'7:122','مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا (ئىمان ئېيتتۇق)».');
INSERT INTO "saleh" VALUES(7,123,'7:123','پىرئەۋن ئېيتتى: «سىلەر مەن رۇخسەت قىلماي تۇرۇپ مۇساغا ئىمان ئېيتتىڭلار، بۇ چوقۇم سىلەرنىڭ شەھەر (يەنى مىسىر) دىكى چېغىڭلاردا ئاھالىنى شەھەردىن ھەيدەپ چىقىرىش ئۈچۈن (مۇسا بىلەن بىرلىشىپ) ئالدىن پىلانلىغان ھىيلەڭلاردۇر. (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزاققا قالماي بىلىسىلەر.');
INSERT INTO "saleh" VALUES(7,124,'7:124','سىلەرنىڭ قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ ـ چەپ قىلىپ (ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول پۇتۇڭلار بىلەن ئوڭ قولۇڭلارنى) چوقۇم كېسىمەن، ئاندىن ھەممىڭلارنى چوقۇم دارغا ئېسىپ ئۆلتۈرىمەن».');
INSERT INTO "saleh" VALUES(7,125,'7:125','دەرگاھىغا قايتقۇچىلارمىز. ');
INSERT INTO "saleh" VALUES(7,126,'7:126','سەن بىزنى پەقەت پەرۋەردىگارىمىزنىڭ بىزگە نازىل قىلغان ئايەتلىرىگە ئىمان ئېيتقانلىقىمىز ئۈچۈنلا ئەيىبلەۋاتىسەن. پەرۋەردىگارىمىز! بىزگە سەۋر ئاتا قىلغىن، بىزنى مۇسۇلمان پېتىمىزچە قەبزى روھ قىلغىن».');
INSERT INTO "saleh" VALUES(7,127,'7:127','پىرئەۋن قەۋمىنىڭ چوڭلىرى (پىرئەۋنگە): «مۇسا بىلەن ئۇنىڭ قەۋمىنى زېمنىدا پىتنە ـ پاسات تۇغدۇرۇشقا، سېنى ۋە سېنىڭ ئىلاھلىرىڭنى تاشلاشقا قويۇۋېتەمسەن؟» دېدى. پىرئەۋن: «ئۇلارنىڭ بالىلىرىنى ئۆلتۈرىمىز، ئاياللىرىنى بولسا (خىزمەتكە سېلىشقا) قالدۇرۇپ قويىمىز، بىز ئەلۋەتتە ئۇلارنىڭ ئۈستىدىن ھۆكۈمرانلىق قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(7,128,'7:128','مۇسا ئۆز قەۋمىگە: «ئاللاھتىن مەدەت تىلەڭلار، سەۋر قىلىڭلار، يەر يۈزى ھەقىقەتەن ئاللاھنىڭ مۈلكىدۇر، (ئاللاھ) ئۇنىڭغا بەندىلىرىدىن ئۆزى خالىغان كىشىلەرنى ۋارىس قىلىدۇ؛ ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپ» دېدى.');
INSERT INTO "saleh" VALUES(7,129,'7:129','ئۇلار: «سەن بىزگە كېلىشتىن بۇرۇنمۇ، كەلگەندىن كېيىنمۇ ھامان خارلىنىپ كەلدۇق» دېدى. مۇسا: «پەرۋاردىگارىڭلارنىڭ دۈشمىنىڭلارنى ھالاك قېلىشى، (مىسىر) زېمىنىدا ئۇلارنىڭ ئورنىغا سىلەرنى دەسسىتىشى، سىلەرنىڭ قانداق ئىش قىلىدىغانلىقىڭلارغا قارىشى مۇھەققەقتۇر» دېدى.');
INSERT INTO "saleh" VALUES(7,130,'7:130',' ئاللاھقا قەسەمكى، بىز ھەقىقەتەن پىرئەۋن ۋە ئۇنىڭ تەۋەلىرىنىڭ ئىبرەت ئېلىشلىرى ئۈچۈن، ئۇلارنى قەھەتچىلىك بىلەن، مېۋىلىرىنىڭ ھوسۇلىنى كېمەيتىۋېتىش بىلەن سىنىدۇق.');
INSERT INTO "saleh" VALUES(7,131,'7:131','ئۇلار ئەگەر (پاراۋانلىق ـ مولچىلىقتەك) بىرەر ياخشىلىققا ئېرىشسە، بىز بۇنىڭغا ھەقلىقمىز، دەيدۇ. ئەگەر ئۇلارغا بىرەر يامانلىق كەلسە، ئۇلار: «بۇ مۇسا ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنىڭ شۇملۇقىدىن كەلدى»، دەيدۇ. ئۇلارغا يەتكەن ياخشىلىق بىلەن يامانلىق (مۇسانىڭ شۇملۇقىدىن ئەمەس) ئاللاھنىڭ تەقدىرىدۇر، لېكىن ئۇلارنىڭ تولىسى بۇنى (يەنى ئۇلارغا كەلگەن بالا ـ قازانىڭ ئۆز گۇناھلىرى تۈپەيلى ئاللاھ تەرىپىدىن كەلگەنلىكىنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(7,132,'7:132','ئۇلار (يەنى پىرئەۋن قەۋمى): «كۆزىمىزنى باغلاش ئۈچۈن ھەرقانداق مۆجىزە كەلتۈرسەڭمۇ بىز ساڭا ھەرگىز ئىشەنمەيمىز» دېدى.');
INSERT INTO "saleh" VALUES(7,133,'7:133','ئۇلارغا سۇ ئاپىتىنى، چېكەتكە، پىت، پاقا، قان (بالالىرىنى) روشەن مۆجىزىلەر قىلىپ ئەۋەتتۇق. (ئاللاھقا ئىمان ئېيتىشقا) ئۇلارنىڭ گەدەنكەشلىكى يول قويمىدى، ئۇلار گۇناھكار قەۋم بولدى.');
INSERT INTO "saleh" VALUES(7,134,'7:134','ئۇلارغا بالا نازىل بولغاندا، ئۇلار: «ئى مۇسا! پەرۋەردىگارىڭغا ئۆزىنىڭ ساڭا ئاتا قىلغان ئەھدى بىلەن (يەنى ساڭا بەرگەن پەيغەمبەرلىكنىڭ ھەققى - ھۆرمىتى بىلەن بىزدىن بالانى كۆتۈرۈۋېتىشكە) دۇئا قىلساڭ، ئاللاھقا قەسەمكى، ئەگەر بىزدىن بالانى كۆتۈرۈۋەتسەڭ، ساڭا چوقۇم ئىمان ئېيتىمىز، ئىسرائىل ئەۋلادىنى چوقۇم سەن بىلەن بىللە قويۇپ بېرىمىز» دېگەن ئىدى.');
INSERT INTO "saleh" VALUES(7,135,'7:135','ئۇلاردىن بالانى ۋاقىتلىق كۆتۈرۈۋەتكەن چېغىمىزدا، ئۇلار توساتتىن ئەھدىلىرىنى بۇزدى.');
INSERT INTO "saleh" VALUES(7,136,'7:136','ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە ئايەتلىرىمىزدىن غاپىل بولغانلىقلىرى ئۈچۈن، ئۇلارنى دېڭىزغا غەرق قىلىپ جازالىدۇق.');
INSERT INTO "saleh" VALUES(7,137,'7:137','بوزەك قىلىنغان قەۋمنى (يەنى بەنى ئىسرائىلنى) بىز بەرىكەتلىك قىلغان (شام) زېمىننىڭ شەرق تەرەپلىرىگە ۋە غەرب تەرەپلىرىگە (يەنى ھەممە تەرەپكە) ۋارىس قىلدۇق. ئىسرائىل ئەۋلادى سەۋر قىلغانلىقلىرى ئۈچۈن، پەرۋەردىگارىڭنىڭ ئۇلارغا قىلغان چىرايلىق ۋەدىسى تولۇق ئىشقا ئاشتى؛ پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ سالغانلىرى (يەنى ئىمارەتلىرى) نى ۋە ياسىغانلىرى (يەنى باغلىرى ۋە ئېكىنزارلىقلىرى) نى ۋەيران قىلدۇق.');
INSERT INTO "saleh" VALUES(7,138,'7:138','ئىسرائىل ئەۋلادىنى بىز (قۇلزۇم) دېڭىزىدىن ئۆتكۈزۈۋەتتۇق، ئۇلار بۇتلىرىغا چوقۇنۇۋاتقان بىر قەۋمنىڭ يېنىدىن ئۆتكەندە: «ئى مۇسا! بىزگىمۇ ئۇلارنىڭ بۇتلىرىغا ئوخشاش بۇت ئورنىتىپ بەرگىن» دېدى. مۇسا: «سىلەر ھەقىقەتەن نادان قەۋم ئىكەنسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(7,139,'7:139',' شۈبھىسىزكى، ئاشۇ كىشىلەرنىڭ باتىل دىنلىرى گۇمران بولغۇسىدۇر، ئۇلارنىڭ قىلغان ئەمەللىرى بىكاردۇر.');
INSERT INTO "saleh" VALUES(7,140,'7:140','مۇسا: «ئاللاھ سىلەرنى (زامانىڭلاردىكى) جاھان ئەھلىدىن ئارتۇق قىلغان تۇرسا، سىلەرگە ئۇنىڭدىن باشقا ئىلاھ ئىزدەمدىم؟» دېدى. ');
INSERT INTO "saleh" VALUES(7,141,'7:141',' ئۆز ۋاقتىدا سىلەرنى پىرئەۋننىڭ قەۋمىدىن قۇتقۇزدۇق، ئۇلار سىلەرگە قاتتىق ئازابنى تېتىتاتتى، ئوغۇللىرىڭلارنى ئۆلتۈرەتتى، ئاياللىرىڭلارنى (خورلاپ ئىشلىتىشكە) ئېلىپ قالاتتى، بۇنىڭدا پەرۋەردىگارىڭلاردىن سىلەرگە زور سىناق بار ئىدى. ');
INSERT INTO "saleh" VALUES(7,142,'7:142','مۇساغا (بىزگە مۇناجات قىلىشقا) ئوتتۇز كېچىنى ۋەدە قىلدۇق. ئۇنىڭغا يەنە ئون كېچىنى قوشتۇق، شۇنىڭ بىلەن، پەرۋەردىگارىنىڭ ۋەدە قىلغان ۋاقتى 40 كېچە بولدى. مۇسا قېرىندىشى ھارۇنغا: «(مەن قايتىپ كەلگەنگە قەدەر) قەۋمىمگە مېنىڭ ئورۇنباسارىم بولۇپ تۇرغىن، (ئۇلارنىڭ ئىشىنى) تۈزىگىن، بۇزۇقچىلىق قىلغۇچىلارنىڭ يولىدا ماڭمىغىن» دېدى.');
INSERT INTO "saleh" VALUES(7,143,'7:143','مۇسا بىز ۋەدە قىلغان ۋاقىتتا كەلگەن ۋە پەرۋەردىگارى ئۇنىڭغا (بىۋاسىتە) سۆز قىلغان چاغدا: «پەرۋەردىگارىم! ماڭا ئۆزۈڭنى كۆرسەتكىن، سېنى بىر كۆرۈپ ئالاي» دېدى. ئاللاھ: «مېنى (بۇ دۇنيادا) ھەرگىز كۆرەلمەيسەن (چۈنكى ئىنساننىڭ بۇ ئاجىز تېنى بۇنىڭغا تاقەت قىلالمايدۇ). لېكىن تاغقا قارىغىن، ئەگەر تاغ ئورنىدا مەھكەم تۇرالىسا، مېنى كۆرەلەيسەن» دېدى. پەرۋەردىگارى تاغقا تەجەللى قىلىش بىلەن، تاغنى تۈپتۈز قىلىۋەتتى، مۇسا بىھوش بولۇپ يىقىلدى. ئۇ ھوشىغا كېلىپ: « (پەرۋەرىگارىم!) سەن پاكتۇرسەن، ساڭا تەۋبە قىلدىم، مەن (سېنىڭ ئۇلۇغلۇقۇڭغا) ئىشەنگۈچىلەرنىڭ ئەۋۋىلىمەن» دېدى.');
INSERT INTO "saleh" VALUES(7,144,'7:144',' ئاللاھ ئېيتتى: «ئى مۇسا! مەن ھەقىقەتەن سېنى (زامانىڭدىكى) كىشىلەر ئارىسىدىن پەيغەمبەرلىككە ۋە مەن بىلەن (بىۋاسىتە) سۆزلىشىشكە تاللىدىم. ساڭا مەن ئاتا قىلغان پەيغەمبەرلىكنى قوبۇل قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن».');
INSERT INTO "saleh" VALUES(7,145,'7:145',' بىز ئۇنىڭ ئۈچۈن (تەۋرات) تاختىلىرىغا (ئىسرائىل ئەۋلادى دىنىدا موھتاج بولغان) ۋەز ـ نەسىھەت، (دىنىي) ئەھكاملارنىڭ ھەممىسىنى تەپسىلىي يازدۇق. (ئى مۇسا) ئۇنى (يەنى تەۋراتنى) مەھكەم تۇتقىن ھەمدە قەۋمىڭنى ئۇنىڭدىكى ئەڭ گۈزەل ئەھكاملارغا ئەمەل قىلىشقا بۇيرۇغىن، سىلەرگە پاسىقلار (يەنى پىرئەۋن ۋە ئۇنىڭ قەۋمى) نىڭ دىيارىنى كۆرسىتىمەن.');
INSERT INTO "saleh" VALUES(7,146,'7:146','يەر يۈزىدە ناھەق تەكەببۇرلۇق قىلىدىغانلارنى ئايەتلىرىمنى چۈشىنىشتىن يىراقلاشتۇرىمەن. ئۇلار بارلىق مۆجىزىلەرنى كۆرگەندىمۇ ئۇنىڭغا ئىشەنمەيدۇ، ئۇلار ئەگەر توغرا يولنى كۆرسە، ئۇنىڭدا ماڭمايدۇ، گۇمراھلىق يولىنى كۆرسە ئۇنىڭدا ماڭىدۇ، بۇ شۇنىڭ ئۈچۈنكى، ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى ۋە ئۇنىڭدىن غاپىل بولدى.');
INSERT INTO "saleh" VALUES(7,147,'7:147','بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتە ئاللاھقا مۇلاقات بولۇشنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى (ئىمانى بولمىغانلىقى ئۈچۈن) بىكار بولۇپ كېتىدۇ، ئۇلارغا پەقەت قىلغان ئەمەللىرىگە يارىشا جازا بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(7,148,'7:148','مۇسانىڭ قەۋمى مۇسادىن كېيىن (يەنى مۇسا پەرۋەردىگارىغا مۇناجات قىلىش ئۈچۈن تۇر تېغىغا كەتكەندىن كېيىن)، زىننەت بۇيۇملىرىدىن موزايدەك ئاۋاز چىقىرىدىغان بىر جانسىز موزاي ياسىدى. ئۇلار موزاينىڭ ئۆزلىرىگە سۆزلىيەلمەيدىغانلىقىنى، يول كۆرسىتەلمەيدىغانلىقىنى كۆرمىدىمۇ؟ ئۇلار ئۇنى مەبۇد قىلىۋالدى، ئۇلار (ئۆزلىرىگە) زۇلۇم قىلغۇچى بولدى.');
INSERT INTO "saleh" VALUES(7,149,'7:149','ئۇلار (موزايغا چوقۇنغانلىقلىرىغا) پۇشايمان قىلغان ۋە راست ئازغانلىقلىرىنى چۈشەنگەن چاغدا: «ئەگەر پەرۋەردىگارىمىز بىزگە رەھىم قىلمىسا ۋە مەغپىرەت قىلمىسا، بىز ئەلۋەتتە زىيان تارتقۇچىلاردىن بولىمىز» دېدى.');
INSERT INTO "saleh" VALUES(7,150,'7:150',' مۇسا غەزەپلەنگەن، ئېچىنغان ھالدا قەۋمىگە قايتىپ كېلىپ: «مەن يوق چاغدا (موزايغا چوقۇنۇپ) نەقەدەر يامان ئىش قىلدىڭلار ـ ھە! (كۈتۈپ تۇرماي) پەرۋەردىگارىڭلارنىڭ ئەمرىگە ئالدىراپ كەتتىڭلارمۇ؟» دېدى ۋە (غەزەپلەنگەنلىكتىن تەۋرات) تاختىلىرىنى (يەرگە) تاشلىدى، قېرىندىشىنىڭ چېچىدىن تۇتۇپ ئۆز تەرىپىگە تارتتى. (ھارۇن) ئېيتتى: «ئى قېرىندىشىم! (بۇ) قەۋم مېنى بوزەك تاپتى، مېنى ئۆلتۈرۈۋەتكىلى تاس قالدى. مېنى دۈشمەنلەر ئالدىدا كۈلكىگە قويمىغىن. مېنى زالىم قەۋم (يەنى موزايغا چوقۇنغانلار) قاتارىدا سانىمىغىن».');
INSERT INTO "saleh" VALUES(7,151,'7:151','مۇسا ئېيتتى: «پەرۋەردىگارىم! ماڭا ۋە مېنىڭ قېرىندىشىمغا مەغپىرەت قىلغىن، بىزنى رەھمىتىڭ دائىرىسىگە كىرگۈزگىن، سەن ئەڭ رەھىم قىلغۇچى زاتتۇرسەن».');
INSERT INTO "saleh" VALUES(7,152,'7:152','شۈبھىسىزكى، موزاينى مەبۇد قىلىۋالغانلار پەرۋەردىگارىنىڭ غەزىپىگە ئۇچرايدۇ، بۇ دۇنيادا خارلىققا قالىدۇ، (ئاللاھقا) بوھتان قىلغۇچىلارغا بىز مۇشۇنداق جازا بېرىمىز.');
INSERT INTO "saleh" VALUES(7,153,'7:153',' يامان ئىشلارنى قىلىپ قويۇپ، كېيىن تەۋبە قىلغان ۋە ئىمانىدا سەمىمىي بولغانلارنى تەۋبىسىدىن كېيىن پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلىدۇ، (ئۇلارغا) رەھىم قىلىدۇ.');
INSERT INTO "saleh" VALUES(7,154,'7:154',' مۇسا غەزىپى بېسىلغاندىن كېيىن (يەردە ياتقان تەۋرات) تاختىلىرىنى ئالدى. ئۇنىڭدا ‹پەرۋەردىگارىدىن قورققۇچىلارغا ھىدايەت قىلىنىدۇ ۋە رەھمەت قىلىنىدۇ› دەپ يېزىلغان ئىدى.');
INSERT INTO "saleh" VALUES(7,155,'7:155',' مۇسا، بىز بەلگىلەپ بەرگەن ۋاقىتتا ئېلىپ كېلىش ئۈچۈن، ئۆز قەۋمىدىن 70 كىشىنى تاللىدى؛ ئۇلارغا زىلزىلە يۈزلەنگەندە، مۇسا: «پەرۋەردىگارىم! خالىغان بولساڭ ئۇلارنى ۋە مېنى بۇرۇنلا ھالاك قىلغان بولاتتىڭ. ئارىمىزدىكى ئەخمەقلەرنىڭ قىلمىشى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟ بۇ پەقەت سېنىڭ سىنىقىڭدۇر، شۇ ئارقىلىق خالىغان بەندەڭنى ئازدۇرىسەن، خالىغان بەندەڭنى ھىدايەت قىلىسەن، سەن بىزنىڭ ئىگىمىزسەن، بىزگە مەغپىرەت قىلغىن، رەھىم قىلغىن، سەن ئەڭ ياخشى مەغپىرەت قىلغۇچىسەن.');
INSERT INTO "saleh" VALUES(7,156,'7:156',' بىزگە بۇ دۇنيادا ۋە ئاخىرەتتە تېگىشلىك ياخشىلىق قىلغىن، بىز ھەقىقەتەن ساڭا تەۋبە قىلدۇق» دېدى. ئاللاھ ئېيتتى: «ئازابىم بىلەن (بەندىلىرىمدىن) خالىغان كىشىنى ئازابلايمەن، مېنىڭ رەھمىتىم مەخلۇقاتنىڭ ھەممىسىگە ئورتاقتۇر. رەھمىتىمنى (كۇفرىدىن ۋە گۇناھتىن) ساقلانغۇچىلارغا، (ماللىرىنىڭ) زاكىتىنى بېرىدىغانلارغا ۋە بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىدىغانلارغا تېگىشلىك قىلىمەن».');
INSERT INTO "saleh" VALUES(7,157,'7:157','ئۇلار ئەلچىگە ـ ئۇممىي پەيغەمبەرگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ئەگىشىدۇ، ئۇلار ئۆز ئىلكىدىكى تەۋرات، ئىنجىللاردا ئۇنىڭ (سۈپىتىنىڭ) يېزىلغانلىقىنى كۆرىدۇ. ئۇ ئۇلارنى ياخشى ئىش قىلىشقا بۇيرۇيدۇ، يامان ئىش قىلىشتىن توسىدۇ، ئۇلارغا پاك نەرسىلەرنى ھالال قىلىدۇ، ناپاك نەرسىلەرنى ھارام قىلىدۇ، ئۇلارنىڭ ئېغىر يۈكىنى يېنىكلىتىدۇ، ئۇلارنى سېلىنغان تاقاق، كويزا ـ كىشەنلەردىن بوشىتىدۇ (يەنى ئۇلارغا يۈكلەنگەن ئېغىر ۋەزىپىلەرنى ئېلىپ تاشلايدۇ)، ئۇنىڭغا ئىمان ئېيتقانلار، ئۇنى ھۆرمەتلىگەنلەر، ئۇنىڭغا ياردەم بەرگەنلەر، ئۇنىڭغا نازىل قىلىنغان نۇر (يەنى قۇرئان) غا ئەگەشكۈچىلەر بەختكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(7,158,'7:158',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! مەن ھەقىقەتەن سىلەرنىڭ ھەممىڭلارغا ئاللاھ تەرىپىدىن ئەۋەتىلگەن ئەلچىمەن، ئاسمانلارنىڭ ۋە زېمىننىڭ ئىگىدارچىلىقى ئاللاھقا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئاللاھ تىرىلدۈرىدۇ ۋە ئۆلتۈرىدۇ. ئاللاھقا، ئۇنىڭ ئاللاھ ۋە ئاللاھنىڭ سۆزلىرىگە ئىمان كەلتۈرىدىغان ئەلچىسى ئۇممىي پەيغەمبەرگە ئىمان كەلتۈرۈڭلار، ھىدايەت تېپىشىڭلار ئۈچۈن ئۇنىڭغا ئەگىشىڭلار».');
INSERT INTO "saleh" VALUES(7,159,'7:159','مۇسانىڭ قەۋمىنىڭ (يەنى بەنى ئىسرائىلنىڭ) ئىچىدە (كىشىلەرنى) ھەق بىلەن توغرا يولغا باشلايدىغان، ھەق بىلەن توغرا ھۆكۈم قىلىدىغان بىر جامائە بار. ');
INSERT INTO "saleh" VALUES(7,160,'7:160','ۇلار (بەنى ئىسرائىل) نى 12 تارماققا (يەنى قەبىلىگە) جامائەلەر قىلىپ بۆلۈۋەتتۇق. مۇسانىڭ قەۋمى (باياۋاندا) ئۇنىڭدىن سۇ تەلەپ قىلغاندا، مۇساغا: «ھاساڭ بىلەن تاشنى ئۇرغىن» دەپ ۋەھىي قىلدۇق. (مۇسا ھاسىسى بىلەن تاشنى ئۇرۇۋىدى) ئۇنىڭدىن (قەبىلىلەرنىڭ سانى بويىچە) 12 بۇلاق ئېتىلىپ چىقتى، ھەر قەبىلە ئۆزىنىڭ سۇ ئىچىدىغان ئورنىنى بىلدى. ئۇلارغا بۇلۇتنى سايىۋەن قىلىپ بەردۇق، ئۇلارغا تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق. (ئۇلارغا ئېيتتۇقكى) «سىلەر بىز رىزىقلاندۇرغان لەززەتلىك نەرسىلەردىن يەڭلار». (ئۇلار بۇ ئۇلۇغ نېمەتلەرگە نانكورلۇق قىلىش بىلەن) بىزگە زىيان سالغىنى يوق، (ئۆزلىرىنى ئاللاھنىڭ ئازابىغا دۇچار قىلىش بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم سالدى.');
INSERT INTO "saleh" VALUES(7,161,'7:161',' ئۆز ۋاقتىدا (بىز ئۇلارغا) (يەنى بەنى ئىسرائىلنىڭ ئاتا ـ بوۋىلىرىغا): «سىلەر بۇ شەھەر (يەنى بەيتۇلمۇقەددەس) دە تۇرۇڭلار، ئۇ يەردىكى يېمەكلىكلەردىن خالىغىنىڭلارچە يەڭلار، شەھەر دەرۋازىسىدىن سەجدە قىلغان ھالدا كىرىڭلار، (كىرىۋاتقان چېغىڭلاردا) ئى ئاللاھ! گۇناھلىرىمىزنى كەچۈرگىن، دەڭلار، جىمى گۇناھلىرىڭلارنى مەغپىرەت قىلىمىز، ياخشىلىق قىلغۇچىلارغا (ساۋابىنى) زىيادە بېرىمىز» دېدۇق.');
INSERT INTO "saleh" VALUES(7,162,'7:162',' ئۇلارنىڭ ئىچىدىكى زالىملار ئۇلارغا ئېيتىلغان سۆز (ئاللاھنىڭ ئەمرىنى) باشقا سۆزلەرگە ئۆزگەرتىۋەتتى. ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئاسماندىن ئازاب ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(7,163,'7:163','ئۇلار (يەنى يەھۇدىيلار) دىن دېڭىز (يەنى قۇلزۇم دېڭىزى) بويىدىكى (ئەيلە) شەھىرىنىڭ ئەھۋالىنى سورىغىن. (ئۇنىڭ ئاھالىسى بېلىق تۇتۇش چەكلەنگەن كۈن) شەنبىدە ئاللاھنىڭ چەكلىمىسىدىن چىقاتتى. چۈنكى شەنبە كۈنى بېلىقلار سۇ ئۈستىدىن ئۇلارنىڭ ئالدىغا كېلەتتى. شەنبىدىن باشقا كۈنلەردە ئۇلارنىڭ ئالدىغا كەلمەيتتى. پاسىق بولغانلىقلىرى ئۈچۈن ئۇلارنى مۇشۇنداق سىنايمىز.');
INSERT INTO "saleh" VALUES(7,164,'7:164',' ئۆز ۋاقتىدا ئۇلارنىڭ ئىچىدىكى بىر جامائە: «ئاللاھ ھالاك قىلىدىغان ياكى قاتتىق ئازابلايدىغان قەۋمگە نېمە ئۈچۈن ۋەز ـ نەسىھەت قىلىسىلەر؟» دېدى. ئۇلار (يەنى ۋەز ـ نەسىھەت قىلغۇچىلار): «پەرۋەردىگارىڭلارنىڭ ئالدىدا (گۇناھقا سۈكۈت قىلىپ تۇردۇق دەپ) ئۆزرە ئېيتماسلىقىمىز ۋە ئۇلارنىڭ ئاللاھتىن قورقۇپ (گۇناھتىن چەكلىنىشلىرىنى ئۈمىد قىلغانلىقىمىز ئۈچۈن شۇنداق قىلدۇق)» دېدى.');
INSERT INTO "saleh" VALUES(7,165,'7:165','ئۇلار ئۆزلىرىگە قىلىنغان ۋەز ـ نەسىھەتنى قوبۇل قىلمىغان چاغدا، يامان ئىشتىن مەنئى قىلغۇچىلارنى قۇتقۇزدۇق. ئاللاھنىڭ ئەمرىدىن چىققانلىقلىرى ئۈچۈن، زالىملارنى قاتتىق ئازابقا دۇچار قىلدۇق. ');
INSERT INTO "saleh" VALUES(7,166,'7:166','ئۇلار نەھيى قىلىنغان نەرسىنى تەرك ئېتىشتىن باش تارتقانلىقلىرى ئۈچۈن، ئۇلارغا: «خار مايمۇن بولۇپ كېتىڭلار» دېدۇق.');
INSERT INTO "saleh" VALUES(7,167,'7:167',' (ئى مۇھەممەد!) ئۆز ۋاقتىدا، پەرۋەردىگارىڭ ئۇلارغا (يەنى يەھۇدىيلارغا) قىيامەتكىچە چوقۇم قاتتىق ئازابلايدىغان كىشىلەرنى ئەۋەتىپ تۇرىدىغانلىقىنى خەۋەر قىلدى. شۈبھىسىزكى، پەرۋەردىگارىڭ (ئاسىيلىق قىلغۇچىلارنى) ئەلۋەتتە تېز جازالىغۇچىدۇر، (ئىتائەت قىلغۇچىلارغا) ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(7,168,'7:168','ئۇلارنى (يەنى يەھۇدىيلارنى) يەر يۈزىدە تۈرلۈك پىرقىلەرگە بۆلۈۋەتتۇق. ئۇلارنىڭ ئىچىدە ياخشىلىرىمۇ بار، ياخشىلىرىدىن تۆۋەنلىرىمۇ بار، ئۇلارنىڭ (كۇفرىدىن ۋە گۇناھتىن) قايتىشلىرى ئۈچۈن، ئۇلارنى قاتتىقچىلىق ۋە كەڭچىلىك بىلەن سىنىدۇق.');
INSERT INTO "saleh" VALUES(7,169,'7:169','ئۇلاردىن كېيىن، (يامان) ئەۋلادلار تەۋراتقا ۋارىسلىق قىلدى، ئۇلار بۇ دۇنيانىڭ ئەرزىمەس پۇل ـ مېلىنى (ھالال بىلەن ھارامنى ئايرىماي، قارىقويۇق) ئالىدۇ، ۋە بىزنى ئاللاھ ئەپۇ قىلىدۇ، دەيدۇ. شۇنداق پۇل ـ مال ئۇلارنىڭ قولىغا كەلسە، ئۇلار يەنە ئالىدۇ. كىتابتا (يەنى تەۋراتتا) ئاللاھقا (يالغان چاپلىماي) پەقەت ھەقنىلا ئېيتىشلىرى توغرۇلۇق ئۇلاردىن چىن ۋەدە ئېلىنمىدىمۇ؟ ھالبۇكى، ئۇلار تەۋراتتىكى نەرسىلەرنى ئوقۇغان ئىدى، ھارامنى تەرك ئېتىپ، ئاللاھتىن قورقىدىغانلارغا ئاخىرەت ياخشىدۇر، بۇنى چۈشەنمەمسىلەر؟');
INSERT INTO "saleh" VALUES(7,170,'7:170','كىتابنى مەھكەم تۇتىدىغانلار، ناماز ئوقۇيدىغانلار (ساۋابقا ئېرىشىدۇ)، بىز ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى ھەقىقەتەن زايا قىلىۋەتمەيمىز.');
INSERT INTO "saleh" VALUES(7,171,'7:171','ئۆز ۋاقتىدا (تۇر) تېغىنى قومۇرۇپ ئۇلارنىڭ ئۈستىگە سايىۋەندەك تىكلىدۇق، ئۇلار تاغنى ئۈستىلىرىگە چۈشۈپ كېتىدۇ دەپ ئويلىدى. (ئۇلارغا ئېيتتۇقكى) «تەقۋادارلار قاتارىدا بولۇشۇڭلار ئۈچۈن ئۆزۈڭلارغا بېرىلگەن كىتابنى (يەنى تەۋراتنى) مەھكەم تۇتۇڭلار، ئۇنىڭدىكى ئەھكاملارغا ئەمەل قىلىڭلار».');
INSERT INTO "saleh" VALUES(7,172,'7:172','ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم بالىلىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: «مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟» دېدى (يەنى ئاللاھ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: «ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق» دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: «بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز» دېمەسلىكلىرى ياكى «ئاتا ـ بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟» دېمەسلىكلىرى ئۈچۈندۇر[172ـ173].');
INSERT INTO "saleh" VALUES(7,173,'7:173','ئۆز ۋاقتىدا پەرۋەردىگارىڭ ئادەم بالىلىرىنى (يەنى نەسلىنى) ئۇلارنىڭ (ئاتىلىرىنىڭ) پۇشتىدىن چىقاردى ۋە ئۇلارنى ئۆزلىرىگە گۇۋاھ قىلىپ: «مەن سىلەرنىڭ پەرۋەدىگارىڭلار ئەمەسمۇ؟» دېدى (يەنى ئاللاھ ئۆزىنىڭ ئۇلارنىڭ پەرۋەردىگارى ئىكەنلىكىگە ۋە بىرلىكىگە ئادەم بالىلىرىنى ئىقرار قىلدۇردى، ئۇلار ئىقرار قىلىپ بۇنى ئۆز ئۈستىگە ئالدى). ئۇلار: «ھەئە، سەن بىزنىڭ پەرۋەردىگارىمىزدۇرسەن، گۇۋاھلىق بەردۇق» دېدى. (ئۇلارنى گۇۋاھلىق بەرگۈزگەنلىكىمىز) قىيامەت كۈنى ئۇلارنىڭ: «بىز بۇنىڭدىن غەپلەتتە قالغان ئىكەنمىز» دېمەسلىكلىرى ياكى «ئاتا ـ بوۋىلىرىمىز ئىلگىرى (ساڭا بۇتلارنى) شېرىك كەلتۈرگەن ئىدى، بىزنىڭ پەقەت ئۇلارنىڭ ئەۋلادى بولۇپ قالغان يېرىمىز بار (يەنى بىزنىڭ ئۆزرىمىز بار) گۇمراھلارنىڭ قىلمىشلىرى تۈپەيلىدىن بىزنى ھالاك قىلامسەن؟» دېمەسلىكلىرى ئۈچۈندۇر[172ـ173].');
INSERT INTO "saleh" VALUES(7,174,'7:174','ئۇلارنىڭ (گۇمراھلىقتىن، ئاتا ـ بوۋىلىرىغا ئەگىشىشتىن) قايتىشلىرى ئۈچۈن، ئايەتلىرىمىزنى مۇشۇنداق تەپسىلىي بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(7,175,'7:175','(ئى مۇھەممەد!) ئۇلارغا شۇنداق بىر ئادەمنىڭ قىسسەسىنى ئوقۇپ بەرگىنكى، ئۇنىڭغا ئايەتلىرىمىزنى بەردۇق (يەنى كىتابۇللاھنىڭ بەزى ئىلىملىرىنى ئۆگەتتۇق). ئۇ ئايەتلىرىمىزدىن ئۆزىنى تارتتى، ئۇنىڭغا شەيتان ئاپىرىدە بولۇشى بىلەنلا گۇمراھلاردىن بولۇپ كەتتى.');
INSERT INTO "saleh" VALUES(7,176,'7:176',' ئەگەر بىز خالىساق، ئۇنى (تەقۋادار ئۆلىمالار دەرىجىسىگە) كۆتۈرەتتۇق، لېكىن ئۇ دۇنياغا بېرىلىپ كەتتى، نەپسى - خاھىشىغا بويسۇندى. ئۇ گويا بىر ئىتقا ئوخشايدۇكى، ئۇنى قوغلىۋەتسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، قوغلىۋەتمىسەڭمۇ تىلىنى چىقىرىپ ھەسىرەيدۇ، بۇ بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ئەنە شۇ قەۋمنىڭ مىسالىدۇر، ئويلاپ بېقىشلىرى ئۈچۈن، بۇ قىسسىنى (قەۋمىڭگە) سۆزلەپ بەرگىن.');
INSERT INTO "saleh" VALUES(7,177,'7:177','بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلىپ ئۆزلىرىگە زىيان سالغان قەۋمنىڭ مىسالى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(7,178,'7:178','ئاللاھ ھىدايەت قىلغان ئادەم ھىدايەت تاپقۇچىدۇر، ئاللاھ گۇمراھ قىلغان ئادەم زىيان تارتقۇچىدۇر.');
INSERT INTO "saleh" VALUES(7,179,'7:179','شەك ـ شۈبھىسىزكى، جىن ۋە ئىنسانلاردىن نۇرغۇنلىرىنى دوزاخقا (يېقىلغۇ بولۇش ئۈچۈن) ياراتتۇق. ئۇلار دىللىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق ھەقنى چۈشەنمەيدۇ. ئۇلار كۆزلىرى بولغان بىلەنمۇ، ئۇ ئارقىلىق (ئاللاھنىڭ قۇدرىتىنىڭ دەلىللىرىنى) كۆرمەيدۇ. ئۇلار قۇلاقلىرى بولغىنى بىلەنمۇ، ئۇ ئارقىلىق (ئاللاھنىڭ ئايەتلىرىنى ئىبرەت ئىلىپ) تىڭشىمايدۇ. ئۇلار گويا ھايۋانغا ئوخشايدۇ، ھايۋاندىنمۇ بەتتەر گۇمراھتۇر، ئەنە شۇلار غاپىلدۇر.');
INSERT INTO "saleh" VALUES(7,180,'7:180','ئاللاھنىڭ گۈزەل ئىسىملىرى بار، ئاللاھنى شۇ (گۈزەل ئىسىملىرى) بىلەن ئاتاڭلار، ئاللاھنىڭ ئىسىملىرىنى كەلسە ـ كەلمەس قوللىنىدىغانلارنى تەرك ئېتىڭلار، ئۇلار (ئاخىرەتتە) قىلمىشلىرىنىڭ جازاسىنى تارتىدۇ.');
INSERT INTO "saleh" VALUES(7,181,'7:181',' بىز ياراتقان ئۈممەتلەر ئىچىدە ھەق يولغا دەۋەت قىلىدىغان ۋە ھەقتە چىڭ تۇرىدىغان بىر ئۈممەت بار.');
INSERT INTO "saleh" VALUES(7,182,'7:182','بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلارنى تۇيدۇرماستىن، ئاستا ـ ئاستا ھالاكەتكە يېقىنلاشتۇرىمىز.');
INSERT INTO "saleh" VALUES(7,183,'7:183','ئۇلارغا مۆھلەت بېرىمەن، مېنىڭ ئازابىم ھەقىقەتەن قاتتىقتۇر. ');
INSERT INTO "saleh" VALUES(7,184,'7:184','ئۇلار ئۆزىنىڭ ھەمراھى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ مەجنۇن ئەمەسلىكىنى ئويلىمامدۇ؟ ئۇ پەقەت ئاشكارا ئاگاھلاندۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(7,185,'7:185','ئۇلار ئاسمانلارنىڭ ۋە زېمىننىڭ سەلتەنىتى ئۈستىدە، ئاللاھنىڭ ياراتقان مەخلۇقاتلىرى، ئۆزلىرىنىڭ ئەجىلى يېقىنلىشىدىغانلىقىنىڭ مۇمكىنلىكى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئۇلار (كالامۇللاھ ئىكەنلىكى شۇنچە روشەن بولغان) قۇرئاندىن باشقا يەنە قايسى سۆزگە ئىشىنىدۇ؟');
INSERT INTO "saleh" VALUES(7,186,'7:186','ئاللاھ ئازدۇرغان كىشىنى ھىدايەت قىلغۇچى بولمايدۇ، ئاللاھ ئۇلارنى گۇمراھلىقلىرىدا قويۇپ بېرىدۇكى، ئۇلار تېڭىرقاپ يۈرىدۇ.');
INSERT INTO "saleh" VALUES(7,187,'7:187','(ئى مۇھەممەد!) ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ، ئېيتقىنكى، «ئۇنىڭ قاچان بولىدىغانلىقىنى پەقەت پەرۋەردىگارىم (ئاللاھ) بىلىدۇ، ئۇنى پەقەت ئۆزى بەلگىلىگەن ۋاقىتتا مەيدانغا چىقىرىدۇ، قىيامەت ئاسمانلارنىڭ ۋە زېمىننىڭ (ئەھلىگە) ئېغىر (ۋەقەدۇر). ئۇ سىلەرگە تۇيۇقسىز كېلىدۇ». گويا سەن قىيامەت بىلەن ناھايىتى تونۇشتەك، ئۇنى سەندىن سورىشىدۇ. ئېيتقىنكى، «ئۇنىڭ ۋاقتىنى پەقەت ئاللاھ بىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى ئۇنىڭ ۋاقتىنىڭ مەخپىي بولۇشىدىكى سەۋەبنى) بىلمەيدۇ. ');
INSERT INTO "saleh" VALUES(7,188,'7:188',' ئاللاھ خالىمىغان ئىكەن، ئۆزۈمگە پايدا يەتكۈزۈش، زىياننى توسۇپ قېلىش قولۇمدىن كەلمەيدۇ. (قىيامەتنىڭ قاچان بولۇشىنى قانداقمۇ بىلەي؟) ئەگەر مەن غەيبنى بىلىدىغان بولسام، نۇرغۇن مەنپەئەت ھاسىل قىلغان بولاتتىم، زىيان ـ زەخمەتكىمۇ ئۇچرىمىغان بولاتتىم (لېكىن مەن غەيبنى بىلمەيمەن، شۇڭا ماڭا تەقدىر قىلىنغان ياخشى ـ يامانلىق يېتىپ تۇرىدۇ). مەن پەقەت ئىمان ئېيتىدىغان قەۋم ئۈچۈن ئاگاھلاندۇرغۇچى ۋە خۇشخەۋەر بەرگۈچى قىلىپ ئەۋەتىلگەن پەيغەمبەرمەن». ');
INSERT INTO "saleh" VALUES(7,189,'7:189',' ئۇ سىلەرنى بىر جاندىن (يەنى ئادەم ئەلەيھىسسالامدىن) ياراتتى، (ئادەم ئەلەيھىسسالامنىڭ) ئۇنسى ـ ئۈلپەت ئېلىشى ئۈچۈن، خوتۇنىنى (يەنى ھەۋۋانى) ئۇنىڭ ئۆز جىنسىدىن ياراتتى، خوتۇنى بىلەن يېقىنچىلىق قىلغاندىن كېيىن، خوتۇنى يېنىككىنە قورساق كۆتۈرۈپ ئېغىر بوي يۈردى، ئۇ (بالىنىڭ قورساقتا ئۆسۈپ چوڭىيىشى بىلەن) ئېغىرلاشقاندىن كېيىن، (ئەر ـ خوتۇن) ئىككىيلەن پەرۋەردىگارى ئاللاھقا: «ئەگەر بىزگە (ئەزالىرى) بېجىرىم پەرزەنت ئاتا قىلساڭ، نېمىتىڭگە ئەلۋەتتە شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ دۇئا قىلدى.');
INSERT INTO "saleh" VALUES(7,190,'7:190',' ئاللاھ ئۇلارغا بېجىرىم ئوغۇل پەرزەنت ئاتا قىلغان ئىدى، ئۇلارنىڭ ئەۋلادى ئاللاھقا شېرىك كەلتۈرۈپ (بۇتلارغا چوقۇندى)، ئاللاھ ئۇلارنىڭ شېرىك كەلتۈرگەن مەبۇدلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(7,191,'7:191',' ئۆزلىرى يارىتىلغان، ھېچ نەرسىنى يارىتالمايدىغان نەرسىلەرنى (يەنى بۇتلارنى) ئاللاھقا شېرىك كەلتۈرەمدۇ؟ ');
INSERT INTO "saleh" VALUES(7,192,'7:192',' ئۇ نەرسىلەر چوقۇنغۇچىلىرىغا ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ.');
INSERT INTO "saleh" VALUES(7,193,'7:193',' ئۇلارنى توغرا يولغا چاقىرساڭلار، ئۇلار سىلەرگە ئەگەشمەيدۇ. مەيلى ئۇلارنى دەۋەت قىلىڭلار، ياكى جىم تۇرۇڭلار، بۇ ئۇلار ئۈچۈن بەرىبىر ئوخشاش (يەنى سىلەر قايسى ھالەتتە بولماڭلار، ئۇلار سىلەرنىڭ دەۋىتىڭلارغا جاۋاب قايتۇرۇشقا قادىر ئەمەستۇر). ');
INSERT INTO "saleh" VALUES(7,194,'7:194','سىلەر ئاللاھنى قويۇپ ئىبادەت قىلغان بۇتلار سىلەرگە ئوخشاش مەخلۇقلاردۇر، (ئۇلارنى ئىلاھ دېگەن دەۋايىڭلاردا) راستچىل بولساڭلار، ئۇلارنى چاقىرىپ بېقىڭلار، چاقىرىقىڭلارنى ئىجابەت قىلىپ باقسۇن.');
INSERT INTO "saleh" VALUES(7,195,'7:195',' ئۇلار (يەنى بۇتلار) نىڭ ماڭىدىغان پۇتلىرى بارمۇ ياكى تۇتىدىغان قوللىرى بارمۇ ياكى كۆرىدىغان كۆزلىرى بارمۇ ۋە ئاڭلايدىغان قۇلاقلىرى بارمۇ؟ (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «شېرىكلىرىڭلارنى (يەنى بۇتلىرىڭلارنى ماڭا قارشى) چاقىرىڭلار، ئاندىن (قولۇڭلاردىن كېلىشىچە) ماڭا زىيانكەشلىك قىلىڭلار، ئازراقمۇ مۆھلەت بەرمەڭلار (مەن ئاللاھقا يۆلەنگەنلىكىم ئۈچۈن سىلەرگە پەرۋا قىلمايمەن).');
INSERT INTO "saleh" VALUES(7,196,'7:196',' ھەقىقەتەن كىتابنى (يەنى قۇرئاننى) نازىل قىلغان ئاللاھ مېنىڭ ئىگەمدۇر، ئۇ ياخشىلارغا ئىگىدارچىلىق قىلىدۇ.');
INSERT INTO "saleh" VALUES(7,197,'7:197',' سىلەرنىڭ ئاللاھنى قويۇپ ئىبادەت قىلىدىغان بۇتلىرىڭلار سىلەرگە ياردەم بېرەلمەيدۇ، ئۆزلىرىگىمۇ ياردەم بېرەلمەيدۇ».');
INSERT INTO "saleh" VALUES(7,198,'7:198','ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭلار، ئۇلار (ھىدايەت تاپماق تۈگۈل دەۋىتىڭلارنى) ئاڭلىمايدۇ، ئۇلار سىلەرگە قاراپ تۇرغاندەك كۆرۈنسىمۇ، لېكىن ئۇلار كۆرمەيدۇ.');
INSERT INTO "saleh" VALUES(7,199,'7:199',' ئەپۇنى دوست تۇتقىن، ياخشىلىققا (يەنى ياخشى سۆز قىلىشقا، ياخشى ئىش قىلىشقا) بۇيرۇغىن، نادانلار بىلەن تەڭ بولمىغىن (يەنى نادانلارنىڭ قىلغىنىنى قىلماي، ئۇلارغا مۇلايىم بولغىن).');
INSERT INTO "saleh" VALUES(7,200,'7:200','ئەگەر شەيتان ساڭا ۋەسۋەسە قىلسا، (شەيتاننىڭ ۋەسۋەسىسىدىن) ئاللاھقا سىغىنىپ پاناھ تىلىگىن. ئاللاھ ھەقىقەتەن (سۆزۈڭنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشىڭنى) بىلىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(7,201,'7:201',' تەقۋادار كىشىلەر شەيتاننىڭ ۋەسۋەسىسىگە ئۇچرىسا، ئاللاھنى ئەسلەيدۇ ـ دە، ھەقىقەتنى كۆرۈۋېلىپ، (شەيتاننىڭ ۋەسۋەسىسىدىن) خالاس تاپىدۇ. ');
INSERT INTO "saleh" VALUES(7,202,'7:202','شەيتانلار (كۇففار) بۇرادەرلىرىنى بولۇشىچە ئازدۇرىدۇ، ئۇلارنى ئازدۇرۇشتىن بوشىشىپ قالمايدۇ.');
INSERT INTO "saleh" VALUES(7,203,'7:203',' (ئۇلار تەلەپ قىلغان مۆجىزىلەردىن) بىرەر مۆجىزىنى كەلتۈرمىسەڭ، ئۇنى نېمىشقا (ئۆزۈڭ) ئىجاد قىلمىدىڭ؟ دېيىشىدۇ. (ئى مۇھەممەد!) ئۇلارغا ئېيتقىنكى: «(ئىش مېنىڭ ئىلكىمدە ئەمەس) مەن پەقەت پەرۋەردىگارىم تەرىپىدىن ماڭا ۋەھىي قىلىنغانغىلا ئەمەل قىلىمەن (ئۆزلۈكىمدىن مۆجىزە يارىتالمايمەن)، بۇ (قۇرئاندا سىلەرگە) پەرۋەردىگارىڭلار تەرىپىدىن كەلتۈرۈلگەن دەلىللەردۇر (قۇرئان باشقا مۆجىزىلەرگە ئېھتىياج قالدۇرمايدۇ)، ئىمان ئېيتقان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر».');
INSERT INTO "saleh" VALUES(7,204,'7:204','ئاللاھنىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن، قۇرئان ئوقۇلغان چاغدا، ئۇنى دىققەت بىلەن ئاڭلاڭلار ۋە جىم تۇرۇڭلار (يەنى سۆز قىلماڭلار).');
INSERT INTO "saleh" VALUES(7,205,'7:205',' پەرۋەردىگارىڭنى يېلىنغان ۋە ئۇنىڭدىن قورققان ھالدا ئىچىڭدە ياد ئەتكىن، ئەتىگەندە ـ ئاخشامدا ئۇنى پەس ئاۋازدا زىكرى قىلغىن، غاپىللاردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(7,206,'7:206',' پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) ئاللاھقا ئىبادەت قىلىشتىن باش تارتمايدۇ، ئاللاھقا تەسبىھ ئېيتىدۇ ۋە ئۇنىڭغا سەجدە قىلىدۇ.');
INSERT INTO "saleh" VALUES(8,1,'8:1','(ئى مۇھەممەد! ساھابىلىرىڭ) غەنىيمەتلەر (نى قانداق تەقسىم قىلىشىڭ) توغرۇلۇق سەندىن سورىشىدۇ، (ئۇلارغا) ئېيتقىنكى، «غەنىيمەتلەر (توغرىسىدا ھۆكۈم چىقىرىش) ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە خاستۇر. ئاللاھتىن قورقۇڭلار، (ئىختىلاپ قىلىشماي ئىتتىپاق بولۇپ) ئاراڭلارنى تۈزەڭلار. ئەگەر سىلەر مۆمىن بولساڭلار، ئاللاھقا ۋە ئاللاھنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار». ');
INSERT INTO "saleh" VALUES(8,2,'8:2',' پەقەت ئاللاھ ياد ئېتىلسە دىللىرىدا قورقۇنچ پەيدا بولىدىغان، ئاللاھنىڭ ئايەتلىرى تىلاۋەت قىلىنسا ئىمانى كۈچىيىدىغان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرلا (كامىل) مۆمىنلەردۇر.');
INSERT INTO "saleh" VALUES(8,3,'8:3','ئۇلار (مۇكەممەل رەۋىشتە) ناماز ئوقۇيدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن نەرسىلەردىن (ئاللاھ يولىدا) سەرپ قىلىدۇ.');
INSERT INTO "saleh" VALUES(8,4,'8:4',' ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا يۇقىرى مەرتىۋىلەرگە، مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتنىڭ تۈگىمەس نېمەتلىرىگە) ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(8,5,'8:5','(غەنىيمەت تەقسىماتى توغرىسىدىكى ھۆكۈمنىڭ ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە خاس قىلىنغانلىقىنى ئۇلارنىڭ ياقتۇرمىغانلىقى) پەرۋەردىگارىڭنىڭ سېنى ھەقلىق رەۋىشتە ئۆيۈڭدىن (يەنى ئۆيۈڭ جايلاشقان مەدىنىدىن ئۇرۇشقا) چىقارغانلىقىنى (ئۇلارنىڭ ياقتۇرمىغانلىقىغا) ئوخشايدۇ، مۆمىنلەردىن بىر گۇرۇھ ئادەم (دۈشمەنگە قارشى ئۇرۇشقا چىقىشنى) ئەلۋەتتە يامان كۆرىدۇ.');
INSERT INTO "saleh" VALUES(8,6,'8:6','ئۇلار گويا (ئالامەتلىرى) كۆرۈنۈپ تۇرۇۋاتقان ئۆلۈمگە ھەيدىلىۋاتقاندەك، ھەقىقەت ئايدىڭلاشقاندىن كېيىن، ھەقىقەت (يەنى ئۇرۇشقا چىقىش مەسىلىسى) ئۈستىدە سەن بىلەن مۇنازىرىلىشىدۇ.');
INSERT INTO "saleh" VALUES(8,7,'8:7',' ئۆز ۋاقتىدا ئاللاھ ئىككى گۇرۇھ (بىرى مۇشرىكلار كارۋىنى، يەنى بىرى مۇشرىكلار قوشۇنى) دىن بىرىنىڭ قولۇڭلارغا كەلتۈرۈلۈشىنى ۋەدە قىلدى، سىلەر قورالسىز گۇرۇھنىڭ (يەنى كارۋاننىڭ) قولۇڭلارغا كەلتۈرۈلۈشىنى ياقتۇردۇڭلار. ئاللاھ ئۆز سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىشنى (يەنى ئىسلام دىنىنى ئۈستۈن قىلىشنى)، كاپىرلارنىڭ يىلتىزىنى قۇرۇتۇشنى خالايدۇ.');
INSERT INTO "saleh" VALUES(8,8,'8:8',' ئاللاھ گۇناھكار ئادەملەرنىڭ ياقتۇرمىغىنىغا قارىماي، ھەقنى ھەق (يەنى ئىسلامنى ئاشكارا) قىلىدۇ، باتىل (يەنى كۇفرى) نى بەربات قىلىدۇ. ');
INSERT INTO "saleh" VALUES(8,9,'8:9','ئۆز ۋاقتىدا (دۇئا قىلىپ) پەرۋەردىگارىڭلاردىن ياردەم تىلىدىڭلار، ئاللاھ :«سىلەرگە ئارقىمۇ ـ ئارقا (چۈشىدىغان) مىڭ پەرىشتە بىلەن ياردەم بېرىمەن» دەپ دۇئايىڭلارنى ئىجابەت قىلدى. ');
INSERT INTO "saleh" VALUES(8,10,'8:10','ئاللاھ سىلەرگە پەقەت (غەلىبە قىلىدىغانلىقىڭلارغا) بېشارەت بېرىش ۋە كۆڭلۈڭلارنى ئارام تاپقۇزۇش ئۈچۈنلا ياردەم بەردى. ياردەم پەقەت ئاللاھ تەرىپىدىنلا كېلىدۇ. ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,11,'8:11',' ئۆز ۋاقتىدا ئاللاھ تىنچلاندۇرۇش يۈزىسىدىن سىلەرگە ئۇيقۇ بېغىشلىدى؛ (تاھارەت ئېلىپ، غۇسلى قىلىپ) پاك بولۇشۇڭلار ئۈچۈن، سىلەردىن شەيتاننىڭ ۋەسۋەسىسىنى كەتكۈزۈش ئۈچۈن، (ئاللاھنىڭ ياردىمىگە ئىشەنچ قىلىپ) كۆڭلۈڭلارنىڭ توق تۇرۇشى ئۈچۈن، قەدىمىڭلارنىڭ (قۇمغا پېتىپ كەتمەي) مەزمۇت تۇرۇشى ئۈچۈن، ئاللاھ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى. ');
INSERT INTO "saleh" VALUES(8,12,'8:12',' ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە: «مەن سىلەر بىلەن بىللە، مۆمىنلەرنىڭ قەدەملىرىنى (ئۇرۇش مەيدانىدا) مۇستەھكەم قىلىڭلار، كاپىرلارنىڭ دىللىرىغا قورقۇنچ سالىمەن» دەپ ۋەھىي قىلدى. (قىلىچ بىلەن كاپىرلارنىڭ) گەدەنلىرىگە چېپىڭلار (يەنى باشلىرىنى كېسىڭلار)، ئۇلارنىڭ بارماقلىرىغا (ھەممە ئەزاسىغا) چېپىڭلار.');
INSERT INTO "saleh" VALUES(8,13,'8:13',' بۇ شۇنىڭ ئۈچۈنكى، ئۇلار ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلدى. كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە قارشىلىق قىلىدىكەن، ئاللاھ ئۇنى قاتتىق جازالايدۇ. ');
INSERT INTO "saleh" VALUES(8,14,'8:14',' (ئى كاپىرلار جامائەسى! بۇ دۇنيادا) مۇشۇنداق جازانى تېتىڭلار. كاپىرلار (ئاخىرەتتە) دوزاخ ئازابىغا دۇچار بولىدۇ. ');
INSERT INTO "saleh" VALUES(8,15,'8:15',' ئى مۆمىنلەر! كاپىرلارنىڭ ھۇجۇمىغا دۇچ كەلگەن چېغىڭلاردا، ئۇلارغا ئارقاڭلارنى قىلماڭلار (يەنى قاچماڭلار).');
INSERT INTO "saleh" VALUES(8,16,'8:16','كىمكى قايتا ئۇرۇشۇش ياكى (ياردەم تىلەش) ئۈچۈن مۇسۇلمانلار جامائەسىگە قوشۇلۇش مەقسىتىدە بىر تەرەپكە يۆتكىلىش قىلماستىن، بەلكى دۈشمەنگە ئارقىسىنى قىلىدىكەن (يەنى قاچىدىكەن)، ئۇ ھەقىقەتەن ئاللاھنىڭ غەزىپىگە ئۇچرايدۇ، ئۇنىڭ جايى دوزاخ بولىدۇ. دوزاخ نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(8,17,'8:17','(ئى مۇسۇلمانلار! بەدرىدە) ئۇلارنى (يەنى مۇشرىكلارنى) سىلەر (ئۆز كۈچۈڭلار بىلەن) ئۆلتۈرگىنىڭلار يوق، بەلكى ئەمەلدە ئۇلارنى (سىلەرگە ياردەم بېرىپ، ئۇلارنىڭ دىللىرىدا قورقۇنچ پەيدا قىلىش بىلەن) ئاللاھ ئۆلتۈردى. (ئى مۇھەممەد! بىر سىقىم توپىنى مۇشرىكلارغا) ئاتقىنىڭدا سەن ئاتمىدىڭ، بەلكى ئەمەلدە ئۇنى (مۇشرىكلارنىڭ كۆزلىرىگە) ئاللاھ ئاتتى. (ئاللاھنىڭ مۇنداق قىلىشى) مۆمىنلەرگە (ساۋاب، غەلىبە، غەنىيمەتلەردىن ئىبارەت) چىرايلىق ئىنئاملارنى ئاتا قىلىش ئۈچۈن ئىدى. ئاللاھ ھەقىقەتەن (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى، ئەھۋاللىرىنى) بىلىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(8,18,'8:18',' بۇ (ئاللاھنىڭ مۆمىنلەرگە بەرگەن ئىنئامىدۇر). ئاللاھ كاپىرلارنىڭ ھىيلىسىنى چوقۇم مەغلۇپ قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,19,'8:19','(ئى كۇففارلار جامائەسى!) ئەگەر سىلەر غەلىبىنى تىلىسەڭلار، غەلىبە ئاللىقاچان سىلەرگە كەلدى (يەنى ئۇ سىلەرنىڭ زىيىنىڭلارغا كەلدى). ئەگەر (پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانساڭلار، بۇ سىلەر ئۈچۈن ياخشىدۇر، ئەگەر ئۇنىڭ بىلەن يەنە ئۇرۇشساڭلار، بىز ئۇنىڭغا يەنە ياردەم بېرىمىز، قوشۇنۇڭلار كۆپ بولغان تەقدىردىمۇ، ئۇ سىلەردىن ھېچ نەرسىنى دەپئى قىلىپ بېرەلمەيدۇ، ئاللاھ ھەقىقەتەن مۆمىنلەر بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(8,20,'8:20',' ئى مۆمىنلەر! ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، سىلەر (قۇرئاننى) ئاڭلاپ تۇرۇپ ئۇنىڭدىن يۈز ئۆرۈمەڭلار.');
INSERT INTO "saleh" VALUES(8,21,'8:21','ئاڭلىماي تۇرۇپ ئاڭلىدۇق دېگەن كىشىلەرنى (يەنى كاپىرلارنى) دورىماڭلار.');
INSERT INTO "saleh" VALUES(8,22,'8:22','شۈبھىسىزكى، ئاللاھنىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى (ھەقنى ئاڭلىماي) گاس بولۇۋالغان، (ھەقنى سۆزلىمەي) گاچا بولۇۋالغان، (ياخشى ـ ياماننى پەرق ئەتمەي) ئەقلىنى ئىشلەتمەيدىغان كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(8,23,'8:23','ئەگەر ئاللاھ ئۇلاردا بىرەر ياخشىلىق بار دەپ بىلسە ئىدى، ئەلۋەتتە ئۇلارغا (ھەقنى) ئاڭلىتاتتى؛ ئۇلارغا ئاڭلاتقان تەقدىردىمۇ، ئۇلار ھەقتىن ئەلۋەتتە يۈز ئۆرۈگەن ھالدا باش تارتاتتى. ');
INSERT INTO "saleh" VALUES(8,24,'8:24',' ئى مۆمىنلەر! ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنى تىرىلدۈرىدىغان (يەنى ئەبەدىي ھاياتقا ئېرىشتۈرىدىغان) ئىمانغا دەۋەت قىلسا، ئۇنى قوبۇل قىلىڭلار. بىلىڭلاركى، ئاللاھ كىشى بىلەن ئۇنىڭ قەلبى ئارىسىدا توسالغۇ بولالايدۇ (يەنى كىشىنىڭ دىلىنى ئىگىسىنىڭ خاھىشى بويىچە ئەمەس، بەلكى ئۆز خاھىشى بويىچە تەسەررۇپ قىلىدۇ). (قىيامەت كۈنى) سىلەر ئاللاھنىڭ دەرگاھىغا يىغىلىسىلەر.');
INSERT INTO "saleh" VALUES(8,25,'8:25','سىلەرنىڭ ئاراڭلاردىكى زۇلۇم قىلغانلارنىڭ بېشىغا كېلىش بىلەنلا چەكلىنىپ قالمايدىغان بالا ـ قازادىن ساقلىنىڭلار، بىلىڭلاركى، ئاللاھنىڭ ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(8,26,'8:26',' ياد ئېتىڭلاركى، ئۆز ۋاقتىدا سىلەر (مەككە) زېمىنىدا ئاز سانلىق بولۇپ، بوزەك قىلىنغان ئىدىڭلار، كىشىلەر (يەنى مۇشرىكلار) نىڭ تالان ـ تاراج قىلىشىدىن قورقاتتىڭلار ، شۈكۈر قىلىشىڭلار ئۈچۈن ئاللاھ سىلەرنى (مەدىنىدە) يەرلەشتۈردى، (بەدرى ئۇرۇشىدا) ئۆز ياردىمى بىلەن سىلەرنى كۈچلەندۈردى. سىلەرگە ھالال نەرسىلەر (يەنى غەنىيمەتلەر) نى رىزىق قىلىپ بەردى.');
INSERT INTO "saleh" VALUES(8,27,'8:27','ئى مۆمىنلەر! ئاللاھقا، پەيغەمبەرگە خىيانەت قىلماڭلار، سىلەرگە قويۇلغان ئامانەتلەرگە بىلىپ تۇرۇپ خىيانەت قىلماڭلار.');
INSERT INTO "saleh" VALUES(8,28,'8:28','بىلىڭلاركى، سىلەرنىڭ ماللىرىڭلار، بالىلىرىڭلار سىلەر ئۈچۈن بىر تۈرلۈك سىناقتۇر، ئاللاھنىڭ دەرگاھىدا چوڭ ساۋاب بار.');
INSERT INTO "saleh" VALUES(8,29,'8:29',' ئى مۆمىنلەر! ئەگەر ئاللاھتىن قورقساڭلار، ئاللاھ سىلەرگە ھەق بىلەن باتىلنى ئايرىيدىغان ھىدايەت ئاتا قىلىدۇ، گۇناھىڭلارنى كەچۈرىدۇ، سىلەرگە مەغپىرەت قىلىدۇ. ئاللاھ كاتتا پەزل ئىگىسىدۇر. ');
INSERT INTO "saleh" VALUES(8,30,'8:30',' (ئى مۇھەممەد!) ئۆز ۋاقتىدا كاپىرلار سېنى قاماققا ئېلىش ياكى ئۆلتۈرۈش ۋە ياكى (مەككىدىن) ھەيدەپ چىقىرىش ئۈچۈن مىكىر ئىشلەتتى، ئۇلار مىكىر ئىشلىتىدۇ، ئاللاھ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ، ئاللاھنىڭ تەدبىرى ئۇلارنىڭ مىكرىدىن ئۈنۈملۈكتۇر.');
INSERT INTO "saleh" VALUES(8,31,'8:31',' ئۇلارغا بىزنىڭ ئايەتلىرىمىز تىلاۋەت قىلىنسا، ئۇلار: «بىز (بۇ سۆزلەرنى) ئاللىقاچان ئاڭلىغان ئىدۇق، ئەگەر خالىساق ئۇنىڭغا ئوخشاش سۆزلەرنى بىزمۇ ئەلۋەتتە قىلالايتتۇق، بۇ پەقەت بۇرۇنقىلاردىن قالغان ئەپسانىلاردۇر» دېدى.');
INSERT INTO "saleh" VALUES(8,32,'8:32',' ئۆز ۋاقتىدا ئۇلار: «ئى ئاللاھ! ئەگەر بۇ (يەنى قۇرئان) سېنىڭ تەرىپىڭدىن نازىل بولغان ھەقىقەت بولىدىغان بولسا، ئاسماندىن ئۈستىمىزگە تاش ياغدۇرغىن ياكى بىزنى قاتتىق ئازاب بىلەن جازالىغىن!» دېدى.');
INSERT INTO "saleh" VALUES(8,33,'8:33','سەن ئۇلارنىڭ ئىچىدە تۇرغان چېغىڭدا (سېنى ھۆرمەتلەش يۈزىسىدىن) ئاللاھ ئۇلارغا ئازاب قىلمايدۇ، ئۇلار ئىستىغپار ئېيتىپ تۇرغان چاغدا ئاللاھ ئۇلارغا ئازاب قىلمايدۇ.');
INSERT INTO "saleh" VALUES(8,34,'8:34',' ئۇلار (باشقىلارنىڭ) مەسجىدى ھەرەمگە كىرىشىنى توسۇۋاتسا، ئاللاھ ئۇلارغا نېمىشقا ئازاب قىلمىسۇن؟ ئۇلار مەسجىدى ھەرەمنىڭ ئىگىلىرى ئەمەس، مەسجىدى ھەرەمنىڭ ئىگىلىرى تەقۋادارلاردىن باشقىلار ئەمەستۇر، لېكىن (بۇنى) ئۇلارنىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(8,35,'8:35','ئۇلارنىڭ مەسجىدى ھەرەمنىڭ يېنىدىكى ئىبادىتى پەقەت ئىسقىرتماق، چاۋاك چالماقتىنلا ئىبارەت بولدى. (ئى كاپىرلار!) كاپىر بولغانلىقىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار.');
INSERT INTO "saleh" VALUES(8,36,'8:36','شۈبھىسىزكى، كاپىرلار مال ـ مۈلۈكلىرىنى (باشقىلارنى) ئاللاھنىڭ يولىدىن توسۇش ئۈچۈن سەرپ قىلىدۇ، سەرپ قىلىنغاندىن كېيىن ئۇلارنىڭ مال ـ مۈلۈكلىرى ئۇلارغا ھەسرەت ئېلىپ كېلىدۇ، ئاخىر ئۇلار يېڭىلىدۇ. كاپىرلار جەھەننەمگىلا ھەيدەپ كىرگۈزۈلىدۇ.');
INSERT INTO "saleh" VALUES(8,37,'8:37',' ئاللاھنىڭ (ئۇلارنى دۇنيادا مەغلۇپ قىلىشى، ئاخىرەتتە جەھەننەمگە ھەيدىشى) كاپىرنى مۆمىندىن پەرقلەندۈرۈش ئۈچۈندۇر. ئاللاھ ھەممە كاپىرلارنى ئۈستى ـ ئۈستىگە دۆۋىلەپ توپلىغاندىن كېيىن، دوزاخقا تاشلايدۇ، ئەنە شۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(8,38,'8:38',' (ئى مۇھەممەد!) كاپىرلارغا ئېيتقىنكى، ئەگەر ئۇلار (كۇفرىدىن، پەيغەمبەر بىلەن دۈشمەنلىشىشتىن) يانسا، ئۇلارنىڭ ئۆتكەنكى گۇناھلىرى مەغپىرەت قىلىنىدۇ. ئەگەر ئۇلار پەيغەمبەر بىلەن قايتا دۈشمەنلەشسە، ئۆتكەنكى پەيغەمبەرلىرىمنى ئىنكار قىلغانلارنى ھالاك قىلىش مېنىڭ تۇتقان يولۇم بولۇپ كەلدى (ئۇلارنىمۇ شۇنداق قىلىمەن).');
INSERT INTO "saleh" VALUES(8,39,'8:39','پىتنە تۈگىگەن، دىن پۈتۈنلەي ئاللاھ ئۈچۈن بولغانغا قەدەر ئۇلار بىلەن ئۇرۇشۇڭلار؛ ئەگەر ئۇلار (يەنى ئىسلامغا داۋاملىق دۈشمەنلىك قىلىۋاتقانلار قىلمىشىدىن) يانسا، ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى كۆرىدۇ.');
INSERT INTO "saleh" VALUES(8,40,'8:40','ئەگەر ئۇلار (ئىماندىن) يۈز ئۆرۈسە، (ئى مۆمىنلەر!) بىلىڭلاركى، ئاللاھ سىلەرنىڭ مەدەتكارىڭلاردۇر، ئاللاھ نېمىدېگەن ياخشى مەدەتكار، نېمىدېگەن ياخشى ياردەمچى!.');
INSERT INTO "saleh" VALUES(8,41,'8:41',' (ئى مۆمىنلەر!) بىلىڭلاركى، سىلەر ئالغان غەنىيمەتنىڭ بەشتىن بىرى ئاللاھقا، پەيغەمبەرگە، پەيغەمبەرنىڭ خىش ـ ئەقرىبالىرىغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە خاستۇر. ئەگەر سىلەر ئاللاھقا، ئىككى گۇرۇھ (يەنى مۇسۇلمانلار بىلەن كاپىرلار) ئۇچرىشىپ ھەق بىلەن ناھەق ئايرىلغان كۈندە (يەنى بەدرى ئۇرۇشى بولغان كۈندە) بەندىمىزگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز نازىل قىلغان نەرسىلەر (يەنى ئايەتلەر ۋە پەرىشتىلەر) گە ئىشەنسەڭلار (ئاللاھنىڭ غەنىيمەت توغرىسىدىكى ھۆكمىنىڭ ئەنە شۇ ئىكەنلىكىنى بىلىڭلار). ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(8,42,'8:42','ئۆز ۋاقتىدا سىلەر ۋادىنىڭ (مەدىنىگە) يېقىن تەرىپىدە ئىدىڭلار، ئۇلار ۋادىنىڭ (مەدىنىگە) يىراق تەرىپىدە ئىدى. (قۇرەيشنىڭ) سودا كارۋىنى بولسا سىلەرنىڭ تۆۋىنىڭلاردا ئىدى. (مۇشرىكلار بىلەن ئۇچرىشىشنى) ۋەدىلەشكەن بولساڭلار، سىلەر (ئۆزۈڭلارنىڭ ئازلىقىنى، مۇشرىكلارنىڭ كۆپلۈكىنى كۆرۈپ) ئەلۋەتتە بۇنىڭغا خىلاپلىق قىلغان بولاتتىڭلار، لېكىن ئاللاھ بولۇشقا تېگىشلىك ئىشنى (يەنى مۇسۇلمانلارنى ئەزىز، مۇشرىكلارنى خار قىلىشنى) ئەمەلگە ئاشۇرۇش ئۈچۈن (سىلەرنى بەدرىدە مۇشرىكلار بىلەن ۋەدىسىز ئۇچراشتۇردى). ئاللاھنىڭ مۇنداق قىلىشى ھالاك بولىدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ھالاك بولۇشى، ياشايدىغانلارنىڭ روشەن دەلىلنى كۆرۈپ ئاندىن ياشىشى ئۈچۈن ئىدى. ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى)، ئەلۋەتتە، ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,43,'8:43',' (ئى مۇھەممەد!) ئۆز ۋاقتىدا، ئاللاھ چۈشۈڭدە ساڭا ئۇلارنى (يەنى دۈشمەنلىرىڭنى) ئاز كۆرسەتتى، ئەگەر ساڭا ئۇلارنى كۆپ كۆرسەتكەن بولسا، سىلەرگە چوقۇم قورقۇنچ چۈشەتتى ۋە جەڭ ئىشىدا چوقۇم دەتالاش قىلاتتىڭلار، لېكىن ئاللاھ (سىلەرنى مۇنداق قىلىشتىن) ساقلىدى. ئاللاھ دىللاردىكىنى ئەلۋەتتە بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,44,'8:44',' ئاللاھ بولۇشقا تېگىشلىك ئىشنى ئىشقا ئاشۇرۇش ئۈچۈن، دۈشمەن بىلەن ئۇچراشقان چېغىڭلاردا، (دۈشمەنلەرگە قارشى تۇرۇشقا جۈرئەت قىلسۇن دەپ) ئۇلارنى سىلەرنىڭ كۆزۈڭلارغا ئاز كۆرسەتتى ۋە سىلەرنى ئۇلارنىڭ كۆزلىرىگە (سىلەرگە قارشى تەييارلىقتا بولمىسۇن دەپ) ئاز كۆرسەتتى. ھەممە ئىش ئاللاھقا قايتۇرۇلىدۇ (ئاللاھ ئۆزى خالىغانچە ھۆكۈم قىلىدۇ).');
INSERT INTO "saleh" VALUES(8,45,'8:45',' ئى مۆمىنلەر! (مۇشرىكلاردىن) بىر جامائەگە (يەنى دۈشمەن قوشۇنىغا) ئۇچراشقان چېغىڭلاردا مۇستەھكەم تۇرۇڭلار. مۇۋەپپەقىيەت قازىنىشىڭلار ئۈچۈن ئاللاھنى كۆپ ياد ئېتىڭلار. ');
INSERT INTO "saleh" VALUES(8,46,'8:46','(پۈتۈن سۆز- ھەرىكەتلىرىڭلاردا) ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئىختىلاپ قىلىشماڭلار، بولمىسا، (دۈشمەن بىلەن ئۇچرىشىشتىن) قورقۇپ قالىسىلەر، كۈچ ـ قۇۋۋىتىڭلار كېتىپ قالىدۇ؛ سەۋر قىلىڭلار، ئاللاھ ھەقىقەتەن سەۋر قىلغۇچىلار بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(8,47,'8:47','يۇرتلىرىدىن چوڭچىلىق ۋە رىيا بىلەن چىققان كىشىلەر (يەنى قۇرەيش مۇشرىكلىرى) گە ئوخشاش بولماڭلار، ئۇلار (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسىدۇ، ئاللاھ ئۇلارنىڭ ھەممە ئىشلىرىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(8,48,'8:48',' ئۆز ۋاقتىدا شەيتان ئۇلارغا (قەبىھ) ئەمەللىرىنى چىرايلىق كۆرسىتىپ: «ھېچقانداق كىشى سىلەرنى يېڭەلمەيدۇ، مەن ھەقىقەتەن سىلەرگە مەدەتكار» دېگەن ئىدى. ئىككى قوشۇن ئۇچراشقان چاغدا شەيتان ئارقىسىغا چېكىندى ۋە: «مەن سىلەردىن ئادا ـ جۇدامەن ، سىلەر كۆرمەيۋاتقاننى ھەقىقەتەن كۆرۈپ تۇرۇۋاتىمەن، مەن راستلا ئاللاھتىن قورقىمەن، ئاللاھنىڭ ئازابى قاتتىقتۇر» دېدى');
INSERT INTO "saleh" VALUES(8,49,'8:49',' ئۆز ۋاقتىدا مۇناپىقلار ۋە دىللىرىدا كېسىلى (يەنى شەك) بارلار: «بۇلارنى (يەنى مۇسۇلمانلارنى) ئۇلارنىڭ دىنى ئالدىدى (يەنى مۇسۇلمانلار ئۆز دىنىغا مەغرۇر بولۇپ، ئۆزلىرىنى يېڭىلمەس دەپ گۇمان قىلىپ، ئاز سانلىق تۇرۇقلۇق كۆپ سانلىقلار بىلەن ئۇرۇشۇشقا چىقتى)» دېدى. كىمكى ئاللاھقا يۆلىنىدىكەن (ئاللاھ ئۇنىڭغا ئەلۋەتتە ياردەم بېرىدۇ). چۈنكى ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,50,'8:50','پەرىشتىلەر (بەدرى ئۇرۇشىدا) كاپىرلارنىڭ جانلىرىنى ئېلىۋاتقاندا، ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرۇۋاتقانلىقىنى كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرگەن بولاتتىڭ). (پەرىشتىلەر ئۇلارغا): «دوزاخنىڭ كۆيدۈرگۈچى ئازابىنى تېتىڭلار!» (دەيتتى). ');
INSERT INTO "saleh" VALUES(8,51,'8:51',' بۇ (ئازاب) سىلەرنىڭ قىلمىش جىنايىتىڭلار تۈپەيلىدىندۇر، ئاللاھ بەندىلىرىگە زۇلۇم قىلغۇچى ئەمەستۇر. ');
INSERT INTO "saleh" VALUES(8,52,'8:52','(بۇ كاپىرلارنىڭ گۇناھ قىلىش ئادىتى) پىرئەۋن جامائەسى ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇ. ئۇلار ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلدى، گۇناھلىرى تۈپەيلىدىن، ئاللاھ ئۇلارنى ھالاك قىلدى. ئاللاھ ھەقىقەتەن كۈچلۈكتۇر، ئاللاھنىڭ ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(8,53,'8:53',' بۇ (يەنى ئۇلارغا نازىل بولغان ئازاب) شۇنىڭ ئۈچۈنكى، بىرەر قەۋم ئۆزلىرىنىڭ ھالىتىنى (كۇفرى ۋە گۇناھ بىلەن) ئۆزگەرتمىگۈچە ئاللاھ ئۇلارغا بەرگەن نېمىتىنى ئۆزگەرتىۋەتمەيدۇ، ئاللاھ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,54,'8:54',' (ئۇلارنىڭ ئادىتى) پىرئەۋن جامائەسىنىڭ ۋە ئۇلاردىن بۇرۇنقىلارنىڭ ئادىتىگە ئوخشايدۇكى، ئۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغان ئىدى. گۇناھلىرى تۈپەيلىدىن ئۇلارنى ھالاك قىلدۇق، پىرئەۋن جامائەسىنى (دېڭىزغا) غەرق قىلدۇق، ئۇلارنىڭ ھەممىسى (كۇفرىلىق ۋە گۇناھ قىلىپ، ئۆزلىرىنى ئازابقا دۇچار قىلىش بىلەن) ئۆزلىرىگە زۇلۇم قىلغۇچى (قەۋم) بولدى.');
INSERT INTO "saleh" VALUES(8,55,'8:55',' ئاللاھنىڭ نەزىرىدە ھايۋانلارنىڭ ئەڭ يامىنى كۇفرىدا چىڭ تۇرغانلاردۇركى، ئۇلار ھەقىقەتەن ئىمان ئېيتمايدۇ. ');
INSERT INTO "saleh" VALUES(8,56,'8:56','(ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىكى مەلۇم كىشىلەر بىلەن (مۇشرىكلارغا ياردەم بەرمەسلىكلىرى ئۈستىدە) مۇئاھىدە تۈزدۈڭ، ئۇلار بولسا ھەر قېتىمدا مۇئاھىدىسىنى بۇزىدۇ، ئۇلار ئاللاھتىن قورقمايدۇ.');
INSERT INTO "saleh" VALUES(8,57,'8:57','ئەگەر ئۇرۇشتا ئۇلار ئۈستىدىن غەلىبە قىلساڭ، ئارقىدىكىلىرىنىڭ قېچىشى ۋە ئىبرەت ئېلىشى ئۈچۈن ئۇلارنى قاتتىق جازالىغىن (يەنى ئۇلارنى باشقىلارغا ئىبرەت قىلغىنكى، ئۇلارنىڭ سەن بىلەن ئۇرۇش قىلىشقا دەرمانى قالمىسۇن).');
INSERT INTO "saleh" VALUES(8,58,'8:58','ئەگەر سەن (مۇئاھىدە تۈزگەن) قەۋمدىن خىيانەت (ئالامەتلىرىنى) سەزسەڭ، مۇئاھىدىسىنى ئۇلارغا ئوچۇق ـ يورۇقلۇق بىلەن تاشلاپ بەرگىن (يەنى سەن بىلەن مۇئاھىدە تۈزۈشكەن قەۋمدىن خىيانەت شەپىسى كۆرۈلسە، ئۇلارغا تۇيۇقسىز ھۇجۇم قىلماستىن، مۇئاھىدىنىڭ ئەمەلدىن قالغانلىقىنى ئالدى بىلەن ئۇلارغا ئۇقتۇرۇپ قويغىن). ئاللاھ ھەقىقەتەن خائىنلارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(8,59,'8:59','(بەدرى ئۇرۇشىدا ئۆلتۈرۈلمەي قالغان) كاپىرلار قۇتۇلدۇق دەپ ئويلىمىسۇن، ئۇلار (پەرۋەردىگارىنىڭ جازاسىدىن) ھەقىقەتەن قېچىپ قۇتۇلالمايدۇ. ');
INSERT INTO "saleh" VALUES(8,60,'8:60',' دۈشمەنلىرىڭلار (بىلەن ئۇرۇش قىلىش) ئۈچۈن، قولۇڭلاردىن كېلىشىچە قورال كۈچى، جەڭ ئېتى تەييارلاڭلار، بۇنىڭ بىلەن، ئاللاھنىڭ دۈشمىنىنى، ئۆزۈڭلارنىڭ دۈشمىنىڭلارنى ۋە ئۇلاردىن باشقا سىلەر بىلمەيدىغان، ئاللاھ بىلىدىغان دۈشمەنلەرنى قورقۇتىسىلەر سىلەرنىڭ ئاللاھ يولىدا سەرپ قىلغىنىڭلار مەيلى نېمە بولسا بولسۇن، سىلەرگە ئۇنىڭ ساۋابى تولۇق بېرىلىدۇ، سىلەرگە زۇلۇم قىلىنمايدۇ (يەنى بۇ ساۋابتىن ھېچ نەرسە كېمەيتىلمەيدۇ).');
INSERT INTO "saleh" VALUES(8,61,'8:61',' ئەگەر ئۇلار تىنچلىققا مايىل بولسا، سەنمۇ تىنچلىققا مايىل بولغىن، ئاللاھقا تەۋەككۈل قىلغىن. ئاللاھ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,62,'8:62','ئەگەر ئۇلار (تەييارلىق قىلىۋېلىش ئۈچۈن سۈلھى بىلەن) سېنى ئالدىماقچى بولسا، ئاللاھ (ئۇلارنىڭ شەررىدىن) شەكسىز ساڭا كۇپايە قىلغۇچىدۇر. ئاللاھ سېنى ئۆزىنىڭ ياردىمى بىلەن ۋە مۆمىنلەر بىلەن كۈچلەندۈردى. ');
INSERT INTO "saleh" VALUES(8,63,'8:63','ئاللاھ مۆمىنلەرنىڭ دىللىرىنى بىرلەشتۈردى. سەن زېمىندىكى پۈتۈن بايلىقنى سەرپ قىلىپمۇ ئۇلارنىڭ دىللىرىنى بىرلەشتۈرەلمەيتتىڭ؛ لېكىن ئاللاھ (ئۆزىنىڭ قۇدرەت كامىلەسى بىلەن) ئۇلارنى ئىناق قىلدى. شۈبھىسىزكى، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,64,'8:64','ئى پەيغەمبەر! (يالغۇز) ئاللاھ ساڭا ۋە ساڭا ئەگەشكەن مۆمىنلەرگە كۇپايە قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(8,65,'8:65','ئى پەيغەمبەر! مۆمىنلەرنى (مۇشرىكلار بىلەن) ئۇرۇش قىلىشقا رىغبەتلەندۈرگىن، ئەگەر سىلەردە چىداملىق يىگىرمە ئادەم بولىدىغان بولسا، (دۈشمەندىن) 200 نى يېڭەلەيدۇ؛ ئەگەر سىلەردە (چىداملىق) 100 ئادەم بولىدىغان بولسا، كاپىرلاردىن 1000 نى يېڭەلەيدۇ؛ چۈنكى ئۇلار (جاھىل) قەۋم بولۇپ، چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(8,66,'8:66','ئاللاھ ئەمدى سىلەرنىڭ (يۈكۈڭلارنى) يىنىكلەتتى، سىلەرنىڭ ئاجىزلىقىڭلارنى بىلدى، ئەگەر سىلەردە چىداملىق 100 ئادەم بولىدىغان بولسا، 200 (دۈشمەن) نى يېڭەلەيدۇ، ئەگەر سىلەردە (چىداملىق) 1000 ئادەم بولىدىغان بولسا، ئاللاھنىڭ ئىزنى بىلەن 2000 (دۈشمەن) نى يېڭەلەيدۇ. ئاللاھ چىداملىقلار بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(8,67,'8:67',' پەيغەمبەرگە زېمىندا دۈشمەننى كۆپرەك ئۆلتۈرمەي تۇرۇپ (يەنى مۇشرىكلىكنىڭ ھەيۋىسىنى يوقىتىپ، ئىسلامغا قۇۋۋەت بەرمەي تۇرۇپ)، ئەسىرلەردىن فىدىيە (يەنى تۆلەم) ئېلىش لايىق ئەمەس ئىدى. (ئى مۆمىنلەر! سىلەر فىدىيە ئېلىش بىلەن) دۇنيا مەنپەئەتىنى كۆزلەيسىلەر، ئاللاھ سىلەرگە ئاخىرەتنى (يەنى ئاخىرەتنىڭ ساۋابىنى) تىلەيدۇ. ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,68,'8:68','ئاللاھنىڭ (ئوچۇق چەكلەنمىگەن ئىشنى قىلغانلارنى جازالىماسلىق دېگەن) ھۆكمى ئەزەلىسى بولمىسا ئىدى، (ئەسىرلەردىن) فىدىيە ئالغانلىقىڭلار ئۈچۈن ئەلۋەتتە زور ئازابقا دۇچار بولاتتىڭلار.');
INSERT INTO "saleh" VALUES(8,69,'8:69',' (ئى جىھاد قىلغۇچىلار جامائەسى!) غەنىيمەت ئالغان نەرسەڭلارنى ھالال ۋە پاك بىلىپ يەڭلار، ئاللاھ (نىڭ ئەمرىگە مۇخالىپەتچىلىك قىلىش) دىن قورقۇڭلار. ئاللاھ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(8,70,'8:70','ئى پەيغەمبەر! قولۇڭلاردىكى ئەسىرلەرگە ئېيتقىنكى، ئەگەر ئاللاھ دىلىڭلاردا ئىمان بارلىقىنى بىلسە (يەنى دىلىڭلاردا ئىمان بولسا، ئاللاھ ئۇنى بىلىدۇ)، سىلەرگە ئۆزۈڭلاردىن ئېلىنغان فىدىيىدىن ياخشىراق نەرسىنى بېرىدۇ، (ئىلگىرىكى گۇناھلىرىڭلارنى) مەغپىرەت قىلىدۇ. ئاللاھ (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(8,71,'8:71',' (ئى مۇھەممەد!) ئەگەر ئۇلار (يەنى مۇشۇ ئەسىرلەر ئىماننى دەۋا قىلىش ئارقىلىق) ساڭا خىيانەت قىلماقچى بولغان بولسا، ئۇنداقتا ئىلگىرى ئۇلار (كۇفرى بىلەن، ئەھدىنى بۇزۇش بىلەن) ئاللاھقا خىيانەت قىلغان ئىدى، ئاللاھ سېنى ئۇلارنىڭ ئۈستىدىن غالىب قىلدى. ئاللاھ (بولغۇسى ھەممە ئىشنى) بىلگۈچى، ئۆز ھېكمىتى بويىچە ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(8,72,'8:72',' ئىمان ئېيتقانلار، (ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى سۆيۈش يۈزىسىدىن يۇرتىنى تاشلاپ) ھىجرەت قىلغان، پۇل ـ ماللىرى ۋە جانلىرى بىلەن ئاللاھ يولىدا جىھاد قىلغانلار (يەنى مۇھاجىرلار)، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) ـ ئەنە شۇلار ئەلۋەتتە بىر ـ بىرىگە ئىگىدۇر (يەنى بىر ـ بىرىگە ياردەمچىدۇر ۋە دوستتۇر). ئىمان ئېيتقان ئەمما (مەدىنىگە) ھىجرەت قىلمىغانلار بىلەن سىلەرنىڭ ئوتتۇراڭلاردا تاكى ئۇلار ھىجرەت قىلغانغا قەدەر ئۆزئارا ئىگە بولۇش (يەنى ياردەملىشىش) بولمايدۇ؛ ئەگەر دىن ئىشىدا ئۇلار سىلەردىن ياردەم تەلەپ قىلسا، ئۇلارنىڭ دۈشمىنى بىلەن سىلەرنىڭ ئاراڭلاردا كېلىشىم بولمىسىلا، ئۇلارغا ياردەم بېرىشىڭلار كېرەك، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(8,73,'8:73','كاپىرلار بىر ـ بىرىنىڭ قاياشىدۇر ، ئەگەر بۇ (يۇقىرىدىكى) ھۆكۈمگە ئەمەل قىلمىساڭلار، يەر يۈزىدە پىتنە ۋە چوڭ قالايمىقانچىلىق تۇغۇلىدۇ.');
INSERT INTO "saleh" VALUES(8,74,'8:74','ئىمان ئېيتقانلار، (مەدىنىگە) ھىجرەت قىلغانلار، ئاللاھنىڭ يولىدا جىھاد قىلغانلار، (يۇرتىدا مۇھاجىرلارغا) جاي بەرگەنلەر ۋە ياردەم كۆرسەتكەنلەر (يەنى ئەنسارلار) ـ ئەنە شۇلار ھەقىقىي مۆمىنلەردۇر، ئۇلار (نىڭ گۇناھى) مەغپىرەت قىلىنىدۇ، ئۇلار (جەننەتتە) ئېسىل رىزىققا ئىگە بولىدۇ. ');
INSERT INTO "saleh" VALUES(8,75,'8:75',' كېيىن ئىمان ئېيتىپ (مەدىنىگە) ھىجرەت قىلغانلار ۋە سىلەر بىلەن بىللە جىھاد قىلغانلار سىلەردىن سانىلىدۇ. ئاللاھنىڭ ھۆكمىدە، ئۇرۇق ـ تۇغقانلار بىر ـ بىرىگە مىراسخور بولۇشقا (ياتلاردىن) ئەڭ ھەقلىقتۇر، شۈبھىسىزكى، ئاللاھ ھەر شەيئىنى بىلىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(9,1,'9:1','(بۇ) ئاللاھ ۋە ئۇنىڭ پەيغەمبىرىنىڭ سىلەر بىلەن مۇئاھىدە تۈزۈشكەن مۇشرىكلارغا قاراتقان ئادا ـ جۇدا بولۇش (يەنى مۇئاھىدىنى بىكار قىلىش) ئېلانىدۇر.');
INSERT INTO "saleh" VALUES(9,2,'9:2','(ئى مۇشرىكلار!) يەر يۈزىدە (خالىغىنىڭلارچە) تۆت ئاي يۈرۈڭلار، بىلىڭلاركى، سىلەر ئاللاھ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، (بىلىڭلاركى) ئاللاھ كاپىرلارنى خار قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(9,3,'9:3','(بۇ) چوڭ ھەج كۈنىدە ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى تەرىپىدىن كىشىلەرگە چىقىرىلغان ئېلاندۇركى، ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى مۇشرىكلاردىن ئادا ـ جۇدادۇر. ئەگەر (كۇفرىدىن) تەۋبە قىلساڭلار، بۇ سىلەرگە (گۇمراھلىقتا كېتىۋېرىشتىن) ياخشىدۇر، ئەگەر (ئىسلامدىن) يۈز ئۆرۈسەڭلار، بىلىڭلاركى، ئاللاھ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر. كاپىرلارغا قاتتىق ئازاب بىلەن بېشارەت بەرگىن.');
INSERT INTO "saleh" VALUES(9,4,'9:4','ئەمما مۇشرىكلار ئىچىدىكى سىلەر بىلەن مۇئاھىدە تۈزگەن، مۇئاھىدىگە ھېچقانداق خىلاپلىق قىلمىغان، سىلەرگە قارشى ھېچقانداق ئادەمگە ياردەم بەرمىگەنلەرگە كەلسەك، ئۇلار بىلەن تۈزگەن بۇ مۇئاھىدىگە ئۇنىڭ ۋاقتى توشقانغا قەدەر رىئايە قىلىڭلار. شۈبھىسىزكى، ئاللاھ تەقۋادارلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(9,5,'9:5','(ئۇرۇش قىلىش) ھارام قىلىنغان ئايلار ئۆتۈپ كەتكەندە، (كېلىشىمنىڭ بوزۇلۇشىغا سەۋەبچى بولغان) مۇشرىكلارنى قەيەردە ئۇچراتساڭلار، شۇ يەردە ئۆلتۈرۈڭلار، ئەسىرگە ئېلىڭلار، قورشاڭلار، ئۇلارنىڭ ئۆتىدىغان يوللىرىنى كۆزىتىپ تۇرۇڭلار. ئەگەر ئۇلار تەۋبە قىلسا، ناماز ئوقۇسا، زاكات بەرسە، ئۇلارنى قويۇپ بېرىڭلار. شۈبھىسىزكى، ئاللاھ (تەۋبە قىلغۇچىلارغا) مەغپىرەت قىلغۇچىدۇر، رەھىم قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,6,'9:6',' ئەگەر مۇشرىكلاردىن بىرەر كىشى سەندىن ئامانلىق تىلىسە، تاكى ئۇ ئاللاھنىڭ كالامىنى (يەنى قۇرئاننى) ئاڭلىغانغا (يەنى پىكىر قىلىپ، ئۇنىڭ ھەقىقىتىگە يەتكەنگە) قەدەر، ئۇنىڭغا ئامانلىق بەرگىن، ئاندىن (ئۇ ئىمان ئېيتمىسا)، ئۇنى (جېنى، مېلى) ئامان تاپىدىغان جايغا يەتكۈزۈپ قويغىن. بۇ شۇنىڭ ئۈچۈنكى، ئۇلار (ئىسلام دىنىنىڭ ھەقىقىتىنى) ئۇقمايدىغان قەۋمدۇر.');
INSERT INTO "saleh" VALUES(9,7,'9:7',' ئاللاھنىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ قارىشىچە، (ئەھدىسىگە ۋاپا قىلمايدىغان) مۇشرىكلاردا قانداقمۇ مۇئاھىدە دېگەن نەرسە بولسۇن؟ ئەمما مەسجىدى ھەرەم قېشىدا سىلەر بىلەن ئەھدە تۈزۈشكەن مۇشرىكلار ئەگەر ئەھدىسىگە ۋاپا قىلىدىكەن، سىلەرمۇ ۋاپا قىلىڭلار، شۈبھىسىزكى، ئاللاھ تەقۋادارلارنى (يەنى پەرۋەردىگاردىن قورققان، ئەھدىسىگە ۋاپا قىلغان، خىيانەت قىلمىغانلارنى) دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(9,8,'9:8','مۇشرىكلار ئەھدىسىگە قانداقمۇ ۋاپا قىلسۇن؟ ئەگەر ئۇلار سىلەرنىڭ ئۈستۈڭلاردىن غەلىبە قىلسا، سىلەرنىڭ ھەققىڭلاردا نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ. سىلەرنى ئېغىزلىرىدا خۇش قىلىدۇ، دىللىرىدا (ۋەدىسىدە تۇرۇشنى) خالىمايدۇ (يەنى ئاغزىدا چىرايلىق سۆزلىگىنى بىلەن، كۆڭۈللىرى ئاداۋەت، نىفاق بىلەن تولغان). ئۇلارنىڭ تولىسى پاسىقلاردۇر.');
INSERT INTO "saleh" VALUES(9,9,'9:9','ئۇلار ئاللاھنىڭ ئايەتلىرىنى (دۇنيانىڭ مال ـ مۈلۈكلىرىدىن) ئەرزىمەس نەرسىلەرگە تېگىشتى، (كىشىلەرنى) ئاللاھنىڭ يولىدىن توستى. ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان! ');
INSERT INTO "saleh" VALUES(9,10,'9:10','ئۇلار مۆمىنلەر ھەققىدە نە تۇغقانچىلىققا، نە ئەھدىگە رىئايە قىلمايدۇ. ئەنە شۇلار (سىلەرگە زۇلۇم قىلىشتا) ھەددىدىن ئاشقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(9,11,'9:11',' ئەگەر ئۇلار (كۇفرىدىن) تەۋبە قىلسا، ناماز ئۆتىسە، زاكات بەرسە، دىنىي جەھەتتە سىلەرنىڭ قېرىندىشىڭلار بولىدۇ (يەنى ھوقۇق ۋە مەجبۇرىيەتتە ئۇلار سىلەر بىلەن ئوخشاشتۇر). ئۇقىدىغان قەۋم ئۈچۈن، ئايەتلەرنى تەپسىلىي بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(9,12,'9:12',' ئەگەر ئۇلار ئەھدە بەرگەندىن كېيىن، قەسەملىرىنى بۇزسا ۋە دىنىڭلارنى ئەيىبلىسە، ئۇلارنىڭ (بۇنىڭدىن) چەكلىنىشلىرى ئۈچۈن، كۇفرىنىڭ كاتتىباشلىرىغا ئۇرۇش ئېچىڭلار. ئۇلارنىڭ قەسىمىنىڭ ھەقىقەتەن ئېتىبارى يوق.');
INSERT INTO "saleh" VALUES(9,13,'9:13','(ئى مۆمىنلەر جامائەسى! ئەھدە بەرگەندە) ئىچكەن قەسىمىنى بۇزغان، پەيغەمبەرنى (مەككە) دىن ھەيدەپ چىقىرىشنى قەستلىگەن ۋە سىلەرگە ئالدى بىلەن ھۇجۇم قىلغان قەۋمگە ئۇرۇش ئاچمامسىلەر؟ ئۇلاردىن قورقامسىلەر؟ ئەگەر مۆمىن بولساڭلار، قورقۇشۇڭلارغا ئاللاھ ئەڭ لايىقتۇر. ');
INSERT INTO "saleh" VALUES(9,14,'9:14',' ئۇلارغا ئۇرۇش ئېچىڭلار، ئاللاھ ئۇلارنى سىلەرنىڭ قولۇڭلار بىلەن جازالايدۇ. ئاللاھ ئۇلارنى خار قىلىدۇ، ئاللاھ سىلەرنى ئۇلار ئۈستىدىن غالىب قىلىدۇ. (ئىسلام دىنىنى ئۈستۈن قىلىپ، كاپىرلارنى جازالاپ) مۆمىن قەۋمنىڭ كۆڭلىگە شىپالىق (يەنى تەسەللى) بېرىدۇ');
INSERT INTO "saleh" VALUES(9,15,'9:15','ھەم ئۇلارنى دىللىرىدىكى ئاچچىقتىن خالاس قىلىدۇ. ئاللاھ خالىغان ئادەمنى تەۋبىگە مۇۋەپپەق قىلىدۇ. ئاللاھ ھەممىنى بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,16,'9:16',' ئاللاھ سىلەرنىڭ ئاراڭلاردىكى جىھاد قىلغانلارنى، ئاللاھتىن، ئاللاھنىڭ پەيغەمبىرىدىن ۋە مۆمىنلەردىن باشقىنى سىرداش قىلىۋالمىغانلارنى بىلمەي (يەنى ئوتتۇرىغا چىقارماي) تۇرۇپ سىلەر ئۆز ھالىمىزغا قويۇپ بېرىلىمىز، (يەنى سىنالمايمىز) دەپ ئويلامسىلەر؟ ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(9,17,'9:17','مۇشرىكلار ئۆزلىرىنىڭ كۇفرىنى ئىقرار قىلىپ تۇرۇقلۇق (يەنى كاپىرلىق بىلەن ئاللاھنىڭ مەسجىدلىرىنى ئاۋات قىلىش بىر ـ بىرى بىلەن سىغىشالمايدۇ)، (ئۇلارنىڭ) ئاللاھنىڭ مەسجىدلىرىنى ئاۋات قىلىشى توغرا ئەمەس، ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنىڭ نەتىجىسى بولمايدۇ، ئۇلار دوزاختا مەڭگۈ قالىدۇ. ');
INSERT INTO "saleh" VALUES(9,18,'9:18',' ئاللاھنىڭ مەسجىدلىرىنى پەقەت ئاللاھقا، ئاخىرەت كۈنىگە ئىمان ئېيتقان، ناماز ئۆتىگەن، زاكات بەرگەن، ئاللاھتىن باشقىدىن قورقمىغان كىشىلەرلا ئاۋات قىلىدۇ (يەنى مەسجىدنى رېمونت قىلىش، سەرەمجانلاشتۇرۇش، تازىلاش، مەسجىدتە داۋاملىق ناماز ئۆتەش، قۇرئان تىلاۋەت قىلىش قاتارلىق ئىشلارنى قىلىدۇ)، ئەنە شۇلارنىڭ ھىدايەت تاپقۇچىلاردىن بولۇشى كۆزدە تۇتۇلىدۇ.');
INSERT INTO "saleh" VALUES(9,19,'9:19',' سىلەر ھاجىلارنى سۇ بىلەن تەمىنلەشنى، مەسجىدى ھەرەمنى ئاۋات قىلىشنى ئاللاھقا، ئاخىرەت كۈنىگە ئىمان ئېيتقان ۋە ئاللاھنىڭ يولىدا جىھاد قىلغانلارنىڭ (ئىمانىغا) ئوخشاش ھېسابلامسىلەر؟ ئاللاھنىڭ نەزىرىدە ئۇلار ئوخشاش ئەمەس. ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(9,20,'9:20','ئىمان ئېيتقانلارنىڭ، ھىجرەت قىلغانلارنىڭ ۋە ماللىرى ھەم جانلىرى بىلەن ئاللاھنىڭ يولىدا جىھاد قىلغانلارنىڭ دەرىجىسى ئاللاھنىڭ دەرگاھىدا ئەڭ كاتتىدۇر؛ ئەنە شۇلار (ساۋاب تېپىش بىلەن) مەقسىتىگە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(9,21,'9:21','پەرۋەردىگارى ئۇلارغا ئۆزىنىڭ رەھمىتى، رازىلىقى ۋە جەننەتلىرى بىلەن خۇشخەۋەر بېرىدۇ، ئۇلار جەننەتلەردە مەڭگۈلۈك نېمەتكە ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(9,22,'9:22','ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. شۈبھىسىزكى، ئاللاھنىڭ دەرگاھىدا (ئۇلارغا) كاتتا ساۋاب بار.');
INSERT INTO "saleh" VALUES(9,23,'9:23','ئى مۆمىنلەر! ئەگەر ئاتىلىرىڭلار، قېرىندىشىڭلار ئىماندىن كۇفرىنى ئارتۇق كۆرسە (كۇفرىدا مەھكەم تۇرسا)، ئۇلارنى دوست تۇتماڭلار. ئىچىڭلاردىن كىملەركى، ئۇلارنى دوست تۇتىدىكەن، ئۇلار زالىملاردۇر (يەنى ئۇلارغا ئوخشاش مۇشرىكتۇر، چۈنكى مۇشرىكلىككە رازى بولغان ئادەممۇ مۇشرىك ھېسابلىنىدۇ).');
INSERT INTO "saleh" VALUES(9,24,'9:24','ئېيتقىنكى، «ئەگەر سىلەرنىڭ ئاتاڭلار، ئوغۇللىرىڭلار، قېرىندىشىڭلار، خوتۇنلىرىڭلار ، ئۇرۇق ـ تۇغقانلىرىڭلار (ھەمدە ئۇلاردىن باشقىلار)، تاپقان پۇل ـ ماللىرىڭلار، ئاقماي قېلىشىدىن قورققان تىجارىتىڭلار، ياخشى كۆرۈدىغان ئۆيلىرىڭلار سىلەر ئۈچۈن ئاللاھتىن، ئاللاھنىڭ پەيغەمبىرىدىن ۋە ئاللاھنىڭ يولىدا جىھاد قىلىشتىنمۇ سۆيۈملۈك بولسا (يەنى شۇلار بىلەن بولۇپ كېتىپ، ئاللاھنىڭ پەيغەمبىرىگە ياردەم بەرمىسەڭلار)، ئۇ ھالدا سىلەر تاكى ئاللاھنىڭ ئەمرى كەلگۈچە كۈتۈڭلار، ئاللاھ پاسىق قەۋمنى (يەنى ئاللاھنىڭ دىنىنىڭ چەك ـ چېگرىسىدىن چىقىپ كەتكۈچىلەرنى) ھىدايەت قىلمايدۇ».');
INSERT INTO "saleh" VALUES(9,25,'9:25',' ئاللاھ سىلەرگە نۇرغۇن جەڭ مەيدانلىرىدا ۋە ھۈنەين كۈنىدە (يەنى جېڭىدە) ھەقىقەتەن ياردەم بەردى. ئەينى ۋاقىتتا سانىڭلارنىڭ كۆپلۈكىدىن خۇشاللىنىپ كەتتىڭلار (يەنى بۈگۈن بىزنىڭ سانىمىز كۆپ، مەغلۇپ بولمايمىز دېدىڭلار، بۇ چاغدا سىلەرنىڭ سانىڭلار 12 مىڭ، دۈشمىنىڭلارنىڭ سانى 4000 ئىدى)، سانىڭلارنىڭ كۆپلۈكى سىلەرگە قىلچە ئەسقاتمىدى. (قاتتىق قورققىنىڭلاردىن) كەڭ زېمىن سىلەرگە تار تۇيۇلدى. ئاندىن (مەغلۇپ بولۇپ، رەسۇلۇللاھنى ئازغىنا مۆمىنلەر بىلەن تاشلاپ) ئارقاڭلارغا قاراپ قاچتىڭلار.');
INSERT INTO "saleh" VALUES(9,26,'9:26',' ئاندىن ئاللاھ پەيغەمبىرىگە ۋە مۆمىنلەرگە (مەرھەمەت قىلىپ) خاتىرجەملىك بېغىشلىدى، سىلەرگە قوشۇنلارنى (يەنى سىلەرگە ياردەمگە پەرىشتىلەرنى) چۈشۈردى، ئۇلارنى سىلەر كۆرمىدىڭلار (شۇنىڭ بىلەن سىلەر غەلىبە قىلدىڭلار)، ئاللاھ كاپىرلارنى (ئۆلتۈرۈش ۋە ئەسىر ئېلىنىش بىلەن) ئازابلىدى، كاپىرلارنىڭ جازاسى ئەنە شۇ.');
INSERT INTO "saleh" VALUES(9,27,'9:27','شۇنىڭدىن كېيىن، ئاللاھ خالىغان ئادەمگە تەۋبىنى نېسىپ قىلىدۇ (يەنى ئىسلام دىنىغا كىرىشكە مۇۋەپپەق قىلىدۇ). ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(9,28,'9:28',' ئى مۆمىنلەر! مۇشرىكلار (ئاللاھنى ئىنكار قىلغانلىقلىرى، ئېتىقادى بوزۇق بولغانلىقلىرى ئۈچۈن) پەقەت نىجىستۇر. مۇشۇ يىلدىن (يەنى ھىجرىيەنىڭ 9 ـ يىلىدىن) كېيىن ئۇلار مەسجىدى ھەرەمغا (ھەرەمگە) يېقىنلاشمىسۇن (يەنى ھەج قىلمىسۇن، ئۆمرە قىلمىسۇن). ئەگەر سىلەر (ئى مۆمىنلەر! مۇشرىكلارنىڭ ھەرەمگە كىرىشتىن مەنئى قىلىنىشى بىلەن) پېقىرلىقتىن قورقساڭلار، ئاللاھ خالىسا ئۆز پەزلى بىلەن سىلەرنى باي قىلىدۇ. شۈبھىسىزكى، ئاللاھ (سىلەرگە پايدىلىق ئىشلارنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,29,'9:29','سىلەر ئەھلى كىتابتىن ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار (يەھۇدىيلار ئۇزەيرىنى ئاللاھنىڭ ئوغلى، ناسارالار ئىيسانى ئاللاھنىڭ ئوغلى دەپ ئېتىقاد قىلغانلىقلىرى ئۈچۈن، ئۇلار ئاللاھقا ۋە ئاخىرەت كۈنىگە ئېتىقاد قىلىمىز دېگەن بىلەنمۇ ئېتىقاد قىلمىغاننىڭ ئورنىدىدۇر) بىلەن، ئاللاھ (ئۆزىنىڭ كىتابىدا) ۋە ئۇنىڭ پەيغەمبىرىنىڭ (سۈننىتىدە) ھارام قىلغان نەرسىلەرنى ھارام بىلمەيدىغانلار ۋە ھەق دىن (يەنى ئىسلام دىنى) غا ئېتىقاد قىلمايدىغانلار بىلەن، تاكى ئۇلار سىلەرگە بويسۇنۇپ خار ھالدا جىزىيە تۆلىگەنگە قەدەر، ئۇرۇش قىلىڭلار. ');
INSERT INTO "saleh" VALUES(9,30,'9:30',' يەھۇدىيلار: «ئۈزەير ئاللاھنىڭ ئوغلىدۇر» دېدى، ناسارالار: «مەسىھ (يەنى ئىيسا) ئاللاھنىڭ ئوغلىدۇر» دېدى. بۇ، ئۇلارنىڭ ئاغزىدىكى (دەلىلسىز) سۆزىدۇر، (ئۇلارنىڭ سۆزلىرى) ئىلگىرىكى كاپىرلارنىڭ (يەنى مۇشرىكلارنىڭ: «پەرىشتىلەر ئاللاھنىڭ قىزلىرىدۇر» دېگەن) سۆزلىرىگە ئوخشايدۇ. ئاللاھ ئۇلارغا لەنەت قىلسۇنكى، (ئاللاھنىڭ بالىسى يوقلۇقىغا روشەن دەلىل تۇرسا، ھەقىقەتتىن باتىلغا بۇرۇلۇپ) ئۇلار قانداقمۇ (ئاللاھنىڭ بالىسى بار دەپ) ئاللاھقا يالغان چاپلايدۇ. ');
INSERT INTO "saleh" VALUES(9,31,'9:31',' ئۇلار ئۆزلىرىنىڭ ھىبرىلىرىنى، راھىبلىرىنى (يەھۇدىيلار بىلەن ناسارالارنىڭ دىنىي باشلىقلىرىنى، ئۆلىمالىرىنى) ۋە مەريەم ئوغلى مەسىھنى مەبۇد قىلىۋالدى. (ھالبۇكى) ئۇلار (پەيغەمبەرلەر ئارقىلىق) پەقەت بىر ئىلاھقا ئىبادەت قىلىشقا بۇيرۇلغان ئىدى. ئۇنىڭدىن باشقا (يەنى ئالەملەرنىڭ پەرۋەردىگارى ئاللاھتىن باشقا) ھېچ مەبۇد (بەرھەق) يوقتۇر، ئاللاھ ئۇلارنىڭ شېرىك كەلتۈرگەنلىرىدىن پاكتۇر. ');
INSERT INTO "saleh" VALUES(9,32,'9:32',' ئۇلار (يەنى مۇشرىكلار، يەھۇدىيلار ۋە ناسارالار) ئاللاھنىڭ نۇرىنى (يەنى ئىسلام دىنىنى) ئېغىزلىرى (يەنى يالغان ـ ياۋىداق بوھتانلىرى) بىلەن ئۆچۈرمەكچى بولىدۇ، ئاللاھ ئۆز نۇرىنى ئۈستۈن قىلماي قالمايدۇ، كاپىرلار (بۇنى) يامان كۆرگەن تەقدىردىمۇ.');
INSERT INTO "saleh" VALUES(9,33,'9:33','ئاللاھ ھەق دىن (ئىسلام) نى بارلىق دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، ئۆزىنىڭ پەيغەمبىرىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى تولۇق) ھىدايەت ۋە ھەق دىن بىلەن ئەۋەتتى، مۇشرىكلار ئۇنىڭ (ئۈستۈن بولۇشىنى) يامان كۆرگەن تەقدىردىمۇ.');
INSERT INTO "saleh" VALUES(9,34,'9:34','ئى مۆمىنلەر! ھىبرىلەر ۋە راھىبلاردىن (يەھۇدىي ۋە ناسارا ئۆلىمالىرىدىن) نۇرغۇنلىرى كىشىلەرنىڭ پۇل ـ ماللىرىنى ھەقىقەتەن ھارام يەيدۇ، (كىشىلەرنى) ئاللاھنىڭ دىنىغا كىرىشتىن توسىدۇ، ئالتۇن ـ كۈمۈش يىغىپ، ئۇنى ئاللاھنىڭ يولىدا سەرپ قىلمايدىغانلارغا (دوزاختا بولىدىغان) قاتتىق ئازاب بىلەن بېشارەت بەرگىن.');
INSERT INTO "saleh" VALUES(9,35,'9:35','ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇ ئالتۇن ـ كۈمۈشلەر جەھەننەمنىڭ ئوتىدا قىزىتىلىپ، ئۇنىڭ بىلەن ئۇلارنىڭ پېشانىلىرى ، يانلىرى ۋە دۈمبىلىرى داغلىنىدۇ. ئۇلارغا: «بۇ سىلەرنىڭ ئۆزەڭلار ئۈچۈن يىغقان ئالتۇن ـ كۈمۈشۈڭلار (سىلەر بۇ دۇنيالىرىڭلاردىكى ئاللاھنىڭ ھەققىنى ئادا قىلمىدىڭلار). يىغقان ئالتۇن ـ كۈمۈشۈڭلارنىڭ ۋابالىنى تېتىڭلار» دېيىلىدۇ. ');
INSERT INTO "saleh" VALUES(9,36,'9:36',' شۈبھىسىزكى، ئاسمان ۋە زېمىن يارىتىلغاندىن تارتىپ (قەمەرى) ئايلارنىڭ سانى ئاللاھنىڭ دەرگاھىدا (يەنى لەۋھۇلمەھپۇزدا) 12 دۇر، ئۇلاردىن تۆتى (يەنى زۇلقەئدە، زۇلھەججە، مۇھەررەم، رەجەپ) ئۇرۇش قىلىش ھارام قىلىنغان ئايلاردۇر، بۇ توغرا دىندۇر (يەنى مەزكۇر تۆت ئايدا ئۇرۇشنىڭ ھارام قىلىنىشى ئاللاھنىڭ توغرا دىنىدۇر)، بۇ ئايلاردا (ئۇلارنىڭ ھۆرمىتىنى ساقلىماسلىق بىلەن، ئاللاھ چەكلىگەن گۇناھ ئىشلارنى قىلىش بىلەن) ئۆزۈڭلارغا زىيان سالماڭلار، مۇشرىكلار سىلەرگە بىرلىكتە ھۇجۇم قىلغاندەك، سىلەرمۇ ئۇلارغا قارشى بىرلىكتە ئۇرۇش قىلىڭلار. بىلىڭلاركى، ئاللاھ تەقۋادارلار بىلەن بىللىدۇر (يەنى ئاللاھنىڭ ئەمرىنى بەجا كەلتۈرۈش، مەنئى قىلغان ئىشلىرىدىن چەكلىنىش بىلەن ئاللاھتىن قورققۇچىلارغا ئاللاھنىڭ ياردەم بېرىدىغانلىقىغا ئىشەنچىدە بولۇڭلار).');
INSERT INTO "saleh" VALUES(9,37,'9:37','ئاينى ئارقىغا سۈرۈش (يەنى ئۇرۇش ھارام قىلىنغان ئاينى باشقا بىر ئايغا ئۆزگەرتىپ، يىلنىڭ 12 ئېيىنى 13 ئايغا كۆپەيتىش) كۇفرىنى زىيادە قىلىشتۇر، بۇنىڭ بىلەن كاپىرلار ئازدۇرۇلىدۇ؛ ئۇلار ئاللاھ ھارام قىلغان ئايلارنىڭ سانىغا مۇۋاپىقلىشىش ئۈچۈن، ئۇنى بىر يىل ھالال قىلسا، ئىككىنچى يىل ھارام قىلىدۇ، شۇنىڭ بىلەن ئۇلار ئاللاھ ھارام قىلغان ئاينى ھالال قىلىدۇ. ئۇلارغا ئۆزلىرىنىڭ يامان ئەمەللىرى چىرايلىق كۆرسىتىلدى. ئاللاھ كاپىر قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(9,38,'9:38','ئى مۆمىنلەر! سىلەرگە ئاللاھنىڭ يولىدا جىھاد قىلىشقا چىقىڭلار دېيىلسە، نېمىشقا يۇرتۇڭلاردىن ئايرىلغىڭلار كەلمەيدۇ؟ دۇنيا ھاياتىنى ئاخىرەتتىكى (بەخت) بىلەن تېگىشىشكە رازى بولدۇڭلارمۇ؟ دۇنيا ھاياتىدىن بەھرىمەن بولۇش ئاخىرەتنىڭ نېمەتلىرى بىلەن سېلىشتۇرغاندا، (پايدىسى) ئاز نەرسىدۇر.');
INSERT INTO "saleh" VALUES(9,39,'9:39',' ئەگەر سىلەر جىھادقا چىقمىساڭلار، ئاللاھ سىلەرگە قاتتىق ئازاب قىلىدۇ. (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا سىلەردىن باشقا قەۋمنى كەلتۈرىدۇ، سىلەر ئاللاھقا قىلچە زىيان يەتكۈزەلمەيسىلەر، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(9,40,'9:40','ئەگەر سىلەر ئاللاھنىڭ پەيغەمبىرىگە ياردەم قىلمىساڭلار (ئاللاھ ياردەم قىلىدۇ)، ئاللاھ ھەقىقەتەن ئۇنىڭغا ياردەم قىلغان ئىدى. ئۆز ۋاقتىدا كاپىرلار ئۇنى (مەككىدىن) ھەيدەپ چىقارغان ئىدى. ئۇنىڭغا پەقەت بىر كىشى (يەنى ئەبۇبەكرى سىددىق) ھەمراھ ئىدى. ئەينى زاماندا ئۇ ئىككىسى غاردا ئىدى. (رەسۇلۇللاھ) ھەمراھىغا: «غەم قىلمىغىن، ئاللاھ ھەقىقەتەن بىز بىلەن بىللە» دەيتتى، ئاللاھ ئۇنىڭغا (يەنى پەيغەمبىرىگە) خاتىرجەملىك بېغىشلىدى، ئۇنىڭغا (پەرىشتىلەردىن بولغان) قوشۇنلار بىلەن مەدەت بەردى، ئۇلارنى سىلەر كۆرمىدىڭلار، كاپىرلارنىڭ سۆزىنى (يەنى شېرىك كەلىمىسىنى) پەس قىلدى، ئاللاھنىڭ سۆزى (يەنى كەلىمە تەۋھىد) ئۈستۈندۇر، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,41,'9:41',' (ئى مۆمىنلەر جامائەسى!) سىلەر يىنىك بولغان ياكى ئېغىر بولغان ھالەتتە (يەنى مەيلى ياش ـ قېرى، پىيادە، ئۇلاغلىق بولۇڭلار ، ئوڭۇشلۇق ۋە قىيىن شارائىتتا بولۇڭلار ، ئىختىيارىي ۋە ئىختىيارسىز بولۇڭلار ، ھەممە ئەھۋالدا) جىھادقا چىقىڭلار، ئاللاھنىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلىڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر.');
INSERT INTO "saleh" VALUES(9,42,'9:42','ئەگەر (ئۇلارنىڭ دەۋەت قىلىنغىنى) ئاسان قولغا كېلىدىغان غەنىيمەت ۋە (يىراق ئەمەس) ئوتتۇراھال سەپەر بولىدىغان بولسا، ئۇلار چوقۇم ساڭا ئەگىشىپ (غەنىيمەت ئېلىش مەقسىتىدە) چىقاتتى. لېكىن، بۇ ئارىلىق ئۇلارغا يىراق بىلىندى. ئۇلار ئاللاھ بىلەن قەسەم ئىچىپ: «ئەگەر چىقىشقا قادىر بولالىغان بولساق، ئەلۋەتتە سىلەر بىلەن بىللە چىقاتتۇق» دەيدۇ، ئۇلار (يالغان قەسەملىرى تۈپەيلىدىن) ئۆزلىرىنى ئۆزلىرى ھالاك قىلىدۇ. ئاللاھ بىلىدۇكى، ئۇلار شەك ـ شۈبھىسىز يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(9,43,'9:43',' (ئى مۇھەممەد!) ئاللاھ سېنى كەچۈردى، (ئۆزرىسىدە) راستچىللار بىلەن يالغانچىلار (ساڭا ئېنىق بولمىغىچە نېمىشقا ئۇلارغا چىقماسلىققا) رۇخسەت بەردىڭ.');
INSERT INTO "saleh" VALUES(9,44,'9:44','(ئى مۇھەممەد!) ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغانلار سەندىن ماللىرى بىلەن ۋە جانلىرى بىلەن جىھاد قىلىشقا رۇخسەت سورىمايدۇ. ئاللاھ تەقۋادارلارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(9,45,'9:45',' (ئى مۇھەممەد!) پەقەت ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشەنمەيدىغانلار، دىللىرىدا (ئاللاھنىڭ بىرلىكىگە ۋە سېنىڭ پەيغەمبەرلىكىڭگە) گۇمانى بارلار سەندىن رۇخسەت سورايدۇ، ئۇلار ئۆز گۇمانىدا تېڭىرقاپ يۈرىدۇ. ');
INSERT INTO "saleh" VALUES(9,46,'9:46','ئەگەر ئۇلارنىڭ جىھادقا چىقىش نىيىتى بولسا، ئەلۋەتتە ئۇنىڭغا تەييارلىق قىلاتتى، لېكىن ئاللاھ ئۇلارنىڭ چىقىشىنى ياقتۇرمىدى، (ئۇلارنىڭ دىللىرىغا ھۇرۇنلۇقنى سېلىپ) ئۇلارنىڭ ئىرادىسىنى سۇندۇردى. (ئۇلارغا) «ئۆيلىرىدە قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار، ئاجىزلار) بىلەن قېلىڭلار» دېيىلدى.');
INSERT INTO "saleh" VALUES(9,47,'9:47',' ئەگەر ئۇلار سىلەر بىلەن بىرلىكتە چىققان بولسا، ئاراڭلاردا پەقەت پىتنە ـ پاساتنى كۆپەيتەتتى، ئاراڭلارغا بۆلگۈنچىلىك سېلىش ئۈچۈن چوقۇم سۇخەنچىلىك بىلەن شۇغۇللىناتتى، ئاراڭلاردا ئۇلارنىڭ سۆزىنى ئاڭلايدىغانلار بار. ئاللاھ زالىملارنى (يەنى مۇناپىقلارنىڭ ئىچى ـ تېشىنى) ئوبدان بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(9,48,'9:48','ئىلگىرى ئۇلار تاكى ئاللاھنىڭ ياردىمى كەلگەنگە، ئاللاھنىڭ دىنى ئۈستۈنلۈك قازانغانغا قەدەر، ھەقىقەتەن، بۆلگۈنچىلىك سېلىشقا ئۇرۇنغان، ساڭا ھىيلە ـ مىكىرلەرنى ئىشلەتكەن ئىدى. ھالبۇكى، ئۇلار بۇنداق بولۇشىنى (يەنى دىننىڭ ئۈستۈنلۈك قازىنىشىنى) يامان كۆرگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(9,49,'9:49','ئۇلارنىڭ ئارىسىدا: «ماڭا (يەنى مېنىڭ قېلىشىمغا) رۇخسەت قىلغىن، مېنى بالاغا گىرىپتار» دېگۈچىلەرمۇ بار. ئەمەلدە ئۇلار (مۇناپىقلىقتىن ئىبارەت چوڭ) بالاغا گىرىپتار بولدى. شۈبھىسىزكى، جەھەننەم كاپىرلارنى ئوراپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,50,'9:50',' ئەگەر سەن بىرەر ياخشىلىققا ئېرىشسەڭ، ئۇلارنىڭ كۆڭلى يېرىم بولىدۇ؛ ئەگەر ساڭا بىرەر مۇسىبەت كەلسە: «ئىشنىڭ ئالدىنى ئالغان ئىكەنمىز» دېيىشىپ خۇشال قايتىشىدۇ.');
INSERT INTO "saleh" VALUES(9,51,'9:51',' ئېيتقىنكى، «بىزگە پەقەت ئاللاھنىڭ تەقدىر قىلغان نەرسىسى يېتىدۇ، ئۇ بىزنىڭ مەدەتكارىمىزدۇر. مۆمىنلەر ئاللاھقا تەۋەككۈل قىلسۇن (يەنى مۆمىنلەر ھەممە ئىشنى ئاللاھقا تاپشۇرسۇن، ئۇندىن باشقا ھېچ ئەھەدىگە يۆلەنمىسۇن)». ');
INSERT INTO "saleh" VALUES(9,52,'9:52','ئېيتقىنكى، «سىلەر پەقەت بىزنىڭ (غەلىبە قىلىش ياكى شېھىت بولۇشتىن ئىبارەت) ئىككى خىل ياخشى ئاقىۋەتنىڭ بىرىگە ئېرىشىشىمىزنى كۈتۈۋاتىسىلەر، بىزمۇ ئاللاھنىڭ سىلەرگە ئۆز دەرگاھىدىن ئازاب چۈشۈرۈشنى ياكى بىزنىڭ قولىمىز ئارقىلىق سىلەرنى جازالىشىنى كۈتۈۋاتىمىز، سىلەرمۇ كۈتۈڭلار، بىزمۇ سىلەر بىلەن بىللە كۈتەيلى».');
INSERT INTO "saleh" VALUES(9,53,'9:53','ئېيتقىنكى، «(ئى مۇناپىقلار جامائەسى!) سىلەر ئىختىيارىي ياكى مەجبۇرىي يوسۇندا (قانچىلىك پۇل ـ مال سەرپ قىلساڭلارمۇ) ھەرگىز قوبۇل قىلىنمايدۇ، چۈنكى سىلەر ئاللاھنىڭ ئىتائىتىدىن چىققان قەۋم بولدۇڭلار». ');
INSERT INTO "saleh" VALUES(9,54,'9:54',' ئۇلارنىڭ بەرگەن نەپىقىلىرى شۇنىڭ ئۈچۈن قوبۇل بولمايدۇكى، ئۇلار ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى ئىنكار قىلىدۇ، نامازنى خۇش ياقماسلىق بىلەن ئوقۇيدۇ، (پۇل ـ مېلىنى) رازىلىق بىلەن بەرمەيدۇ. ');
INSERT INTO "saleh" VALUES(9,55,'9:55','ئۇلارنىڭ مال ـ مۈلكى ۋە ئەۋلادلىرى سېنى ئەجەبلەندۈرمىسۇن، ئاللاھ ئۇلارنى بۇ دۇنيادا شۇ نەرسىلەر بىلەن ئازابلاشنى ۋە ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشىنى ئىرادە قىلىدۇ.');
INSERT INTO "saleh" VALUES(9,56,'9:56','ئۇلار: «بىز چوقۇم سىلەر (يەنى سىلەرگە ئوخشاش مۇسۇلمانلار) دىن» دەپ، ئاللاھ بىلەن قەسەم ئىچىدۇ. ئۇلار سىلەردىن ئەمەس، لېكىن ئۇلار (زىيان ـ زەخمەتكە ئۇچراشتىن قورقۇپ، ئاغزىدا مۇسۇلمان بولغان) قورقۇنچاق قەۋمدۇر.');
INSERT INTO "saleh" VALUES(9,57,'9:57',' ئەگەر ئۇلار بىرەر قورغان ياكى غار ۋە ياكى گەمىنى تاپسا ئىدى، ئۇلار شۇ تەرەپكە قاراپ ئەلۋەتتە يۈگۈرۈشكەن بولاتتى. ');
INSERT INTO "saleh" VALUES(9,58,'9:58','(ئى مۇھەممەد!) ئۇلارنىڭ بەزىسى سېنىڭ سەدىقە (ۋە غەنىيمەت) لەرنى تەقسىم قىلىشىڭنى ئەيىبلەيدۇ، ئۇلارغا ئۇنىڭدىن بېرىلسە خۇش بولۇشىدۇ، بېرىلمىسە خاپا بولۇشىدۇ.');
INSERT INTO "saleh" VALUES(9,59,'9:59',' ئەگەر ئۇلار ئاللاھنىڭ ۋە ئاللاھنىڭ پەيغەمبىرىنىڭ ئۇلارغا بەرگىنىگە رازى بولسا ۋە: «ئاللاھ بىزگە كۇپايە قىلىدۇ، ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ئۆز پەزلىدىن ئاتا قىلىدۇ، بىز ھەقىقەتەن ئاللاھتىن (ئاللاھنىڭ پەزلىنى، ئېھسانىنى) ئۆتۈنۈپ سورىغۇچىلارمىز» دېسە، ئەلۋەتتە، ئۇلار ئۈچۈن ياخشى بولاتتى.');
INSERT INTO "saleh" VALUES(9,60,'9:60','زاكات پەقەت پېقىرلارغا، مىسكىنلەرگە، زاكات خادىملىرىغا، دىللىرىنى ئىسلامغا مايىل قىلىش كۆزدە تۇتۇلغانلارغا، ئىنسانلارنى ھۆرلۈككە ئېرىشتۈرۈشكە، قەرزدارلارغا، ئاللاھنىڭ يولىغا، ئىبن سەبىللەرگە بېرىلىدۇ، بۇ ئاللاھنىڭ بەلگىلىمىسىدۇر، ئاللاھ (بەندىلىرىنىڭ مەنپەئەتىنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,61,'9:61','ئۇلارنىڭ بەزىلىرى پەيغەمبەرگە (سۆزى ۋە ھەرىكىتى ئارقىلىق) ئەزىيەت يەتكۈزىدۇ ۋە (پەيغەمبەرنى) «ئاڭلىغان نەرسىسىنىڭ ھەممىسىگە ئىشىنىدۇ» دەيدۇ. ئېيتقىنكى، «(دۇرۇس، ئۇ) سىلەرگە پايدىلىق سۆزلەرنى ئاڭلاپ ئىشىنىدۇ، ئاللاھنىڭ سۆزلىرىگە ئىشىنىدۇ، مۆمىنلەرنىڭ سۆزىگە (ئۇلارنىڭ ئىخلاسىنى بىلگەنلىكتىن) ئىشىنىدۇ، ئۇ سىلەردىن ئىمان ئېيتقانلار ئۈچۈن رەھمەتتۇر». ئاللاھنىڭ پەيغەمبىرىگە ئەزىيەت يەتكۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(9,62,'9:62','ئۇلار سىلەرنى رازى قىلىش ئۈچۈن (پەيغەمبەرگە تېگىدىغان گەپنى قىلمىدۇق دەپ) ئاللاھ بىلەن قەسەم قىلىدۇ، ئەگەر ئۇلار راستىنلا مۆمىن بولىدىغان بولسا، ئاللاھنى ۋە ئۇنىڭ پەيغەمبىرىنى رازى قىلىشلىرى كېرەك ئىدى.');
INSERT INTO "saleh" VALUES(9,63,'9:63',' (بۇ مۇناپىقلار) ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە كىمكى قارشىلىق كۆرسىتىدىكەن، ئۇنىڭ دوزاخقا كىرىدىغانلىقىنى، دوزاختا مەڭگۈ قالىدىغانلىقىنى بىلمەمدۇ؟ ئەنە شۇ چوڭ رەسۋالىقتۇر.');
INSERT INTO "saleh" VALUES(9,64,'9:64','مۇناپىقلار ئۆزلىرى توغرىسىدا بىرەر سۈرە نازىل بولۇپ، ئۇلارنىڭ دىللىرىدىكىنى (يەنى نىفاقنى) مۆمىنلەرگە پاش قېلىشىدىن قورقىدۇ. ئېيتقىنكى، «سىلەر (ئاللاھنىڭ دىنىنى خالىغىنىڭلارچە) مەسخىرە قىلىڭلار، سىلەر (پاش بولۇشتىن) قورقىدىغان نەرسىنى (يەنى مۇناپىقلىقنى) ئاللاھ چوقۇم ئاشكارا قىلىدۇ».');
INSERT INTO "saleh" VALUES(9,65,'9:65','(ئى مۇھەممەد!) ئەگەر سەن ئۇلارنىڭ (مەسخىرە قىلغانلىقىنى) سورىساڭ، ئۇلار: «بىز پەقەت ئىچ پۇشۇقى قىلىپ ئوينىشىپ دەپ قويدۇق» دەيدۇ. (بۇ مۇناپىقلارغا) «سىلەر ئاللاھنىڭ دىنىنى، ئاللاھنىڭ ئايەتلىرىنى (يەنى كىتابىنى) ۋە ئاللاھنىڭ پەيغەمبىرىنى مەسخىرە قىلدىڭلارمۇ؟» دېگىن.');
INSERT INTO "saleh" VALUES(9,66,'9:66','سىلەر (يالغان قەسەم ئىچىپ) ئۆزرە ئېيتماڭلار، سىلەر ئىمان ئېيتقىنىڭلاردىن كېيىن، (پەيغەمبەرنى مەسخىرە قىلىش بىلەن) كاپىر بولدۇڭلار، سىلەردىن بىر گۇرۇھنى (راست تەۋبە قىلغانلىقلىرى ئۈچۈن) ئەپۇ قىلساق، يەنە بىر گۇرۇھنى گۇناھكار بولغانلىقلىرى (يەنى مۇناپىقلىق بىلەن گۇناھنى داۋاملاشتۇرغانلىقلىرى) ئۈچۈن جازالايمىز.');
INSERT INTO "saleh" VALUES(9,67,'9:67','مۇناپىق ئەرلەر بىلەن مۇناپىق ئاياللار (مۇناپىقلىقتا ۋە ئىماندىن يىراق بولۇشتا) بىر ـ بىرىگە ئوخشايدۇ، ئۇلار يامانلىققا بۇيرۇيدۇ، ياخشىلىقتىن توسىدۇ، ئۇلار قوللىرىنى يۇمۇۋالىدۇ (يەنى بېخىللىق قىلىپ سەدىقە بەرمەيدۇ). ئۇلار ئاللاھنى ئۇنتۇدى (يەنى ئاللاھنىڭ تائىتىنى تەرك ئەتتى)، ئاللاھمۇ ئۇلارنى ئۇنتۇدى (يەنى ئاللاھ ئۇلارنى ئۆزىنىڭ پەزلىدىن ۋە رەھمىتىدىن مەھرۇم قىلدى). شۈبھىسىزكى، مۇناپىقلار پاسىقلاردۇر.');
INSERT INTO "saleh" VALUES(9,68,'9:68',' ئاللاھ مۇناپىق ئەرلەرگە، مۇناپىق ئاياللار ۋە كۇففارلارغا دوزاخنى ۋەدە قىلدى، ئۇلار دوزاختا مەڭگۈ قالىدۇ. دوزاخ ئۇلارنى (ئازابلاشقا) يېتەرلىكتۇر، ئاللاھ ئۇلارغا لەنەت قىلدى، ئۇلار مەڭگۈلۈك ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(9,69,'9:69','(ئى مۇناپىقلار، سىلەرنىڭ ھالىڭلار) سىلەردىن بۇرۇن ئۆتكەن (ھالاك بولغان ئۈممەتلەرنىڭ ھالىغا) ئوخشايدۇ، ئۇلار كۈچ ـ قۇۋۋەتتە سىلەردىن كۈچلۈك، مال ـ مۈلكى ۋە ئەۋلادى سىلەرنىڭكىگە قارىغاندا كۆپ ئىدى. سىلەردىن بۇرۇن ئۆتكەنلەر (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋىسىدىن بەھرىمەن بولغاندەك، سىلەرمۇ (دۇنيانىڭ لەززەتلىرى جەھەتتىكى) نېسىۋەڭلاردىن بەھرىمەن بولدۇڭلار؛ ئۇلار باتىلغا چۆمگەندەك، سىلەرمۇ باتىلغا چۆمدۈڭلار؛ ئەنە شۇلارنىڭ ياخشى ئەمەللىرى دۇنيا ۋە ئاخىرەتتە ئېتىبارسىزدۇر، ئەنە شۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(9,70,'9:70',' ئۇلارغا ئۇلاردىن بۇرۇن ئۆتكەنلەرنىڭ نۇھ، ئاد ۋە سەمۇد قەۋملىرىنىڭ، ئىبراھىم قەۋمىنىڭ، مەديەن ئاھالىسىنىڭ، كۆمتۈرۈۋېتىلگەن قىشلاقلارنىڭ (يەنى لۇت ئەلەيھىسسالام قەۋمى قىشلاقلىرىنىڭ) خەۋەرلىرى كەلمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر ئېلىپ كەلدى (بۇ مۆجىزىلەرنى ئۇلار ئىنكار قىلدى). ئاللاھ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار (كاپىر بولۇش ۋە گۇناھ قىلىش بىلەن) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(9,71,'9:71',' مۆمىن ئەرلەر، مۆمىن ئاياللار بىر ـ بىرى بىلەن دوستتۇر، ئۇلار (كىشىلەرنى) ياخشى ئىشلارنى قىلىشقا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ، نامازنى (تولۇق) ئادا قىلىدۇ، زاكات بېرىدۇ، ئاللاھقا ۋە ئاللاھنىڭ پەيغەمبىرىگە ئىتائەت قىلىدۇ، ئەنە شۇلارغا ئاللاھ رەھىم قىلىدۇ، ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,72,'9:72',' ئاللاھ مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى ۋەدە قىلدى. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، ھەمىشە تۇرىدىغان جەننەتلەردە گۈزەل جايلارنى ۋەدە قىلدى. (ئۇلار ئېرىشىدىغان) ئاللاھنىڭ رازىلىقى (جەننەت نېمەتلىرىنىڭ ھەممىسىدىن) كاتتىدۇر، بۇ چوڭ بەختتۇر.');
INSERT INTO "saleh" VALUES(9,73,'9:73',' ئى پەيغەمبەر! كاپىرلارغا ۋە مۇناپىقلارغا قارشى جىھاد قىلغىن، ئۇلارغا (قارشى غازات قىلىش ۋە قورقۇنچ سېلىش بىلەن) قاتتىق مۇئامىلە قىلغىن، ئۇلارنىڭ جايى جەھەننەمدۇر، ئۇ نېمىدېگەن يامان جاي!. ');
INSERT INTO "saleh" VALUES(9,74,'9:74','ئۇلار (يەنى مۇناپىقلار) ھېچنېمە دېمىگەنلىكى توغرىسىدا ئاللاھ بىلەن قەسەم قىلىدۇ، ئۇلار ھەقىقەتەن كۇفرى سۆزىنى ئېيتتى، ئىسلام دىنىغا كىرگەنلىكىنى ئىپادىلىگەندىن كېيىن كاپىر بولدى، ئۇلار ئىشقا ئاشمايدىغان ئىشنى ئىشقا ئاشۇرۇشقا ئۇرۇندى (يەنى پەيغەمبەر ئەلەيھىسسالامنى ئۆلتۈرۈشنى قەستلىدى)، ئۇلارنىڭ (پەيغەمبەر ئەلەيھىسسالامنى) يامان كۆرۈشى پەقەت ئاللاھنىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارنى ئۆز پەزلىدىن باي قىلغانلىقى ئۈچۈندۇر، ئەگەر ئۇلار (مۇناپىقلىقتىن) تەۋبە قىلسا، ئۆزلىرىگە ياخشىدۇر، ئەگەر ئۇلار (ئىماندىن داۋاملىق) يۈز ئۆرۈسە، ئاللاھ ئۇلارنى دۇنيا ۋە ئاخىرەتتە قاتتىق ئازاب بىلەن جازالايدۇ، يەر يۈزىدە ئۇلارغا (ئۇلارنى ئازابتىن قۇتۇلدۇرىدىغان) بىرەر دوست ۋە بىرەر مەدەتكار تېپىلمايدۇ.');
INSERT INTO "saleh" VALUES(9,75,'9:75','ئۇلارنىڭ ئىچىدىكى بەزىلەر: «ئەگەر ئاللاھ بىزگە ئۆز پەزلىدىن ئاتا قىلسا (يەنى رىزقىمىزنى كەڭ قىلسا) ئەلۋەتتە سەدىقە بېرەتتۇق، ئەلۋەتتە ياخشىلاردىن بولاتتۇق» دەپ ئاللاھقا ئەھدە قىلدى.');
INSERT INTO "saleh" VALUES(9,76,'9:76','ئاللاھ ئۇلارغا ئۆز پەزلىدىن ئاتا قىلغان (يەنى باي قىلغان) چاغدا، بېخىللىق قىلىشتى (يەنى ئەھدىنى بۇزۇپ، ئاللاھقا ۋە ئاللاھنىڭ پەيغەمبىرىگە ئىتائەت قىلىشتىن يۈز ئۆرۈشتى).');
INSERT INTO "saleh" VALUES(9,77,'9:77',' ئۇلار (سەدىقە قىلىمىز، ياخشىلاردىن بولىمىز دەپ) ئاللاھقا بەرگەن ئەھدىسىگە خىلاپلىق قىلغانلىقلىرى، (ئىمانىنى، ئېھسانىنى دەۋا قىلىشتىكى) يالغانچىلىقلىرى ئۈچۈن، ئاللاھقا مۇلاقات بولىدىغان كۈن (يەنى قىيامەت كۈنى) گە قەدەر مۇناپىقلىقنىڭ دىللىرىدا (يىلتىز تارتىپ) تۇرۇشىنى ئاللاھ ئۇلارنىڭ (ئىشىنىڭ) ئاقىۋىتى قىلدى.');
INSERT INTO "saleh" VALUES(9,78,'9:78','ئاللاھنىڭ ئۇلارنىڭ ئىچكى سىرلىرىنى، (ئۆزئارا قىلىشقان) يوشۇرۇن سۆزلىرىنى بىلىپ تۇرىدىغانلىقىنى، پۈتۈن غەيبلەرنى چوڭقۇر بىلىدىغانلىقىنى ئۇلار ئۇقمامدۇ؟.');
INSERT INTO "saleh" VALUES(9,79,'9:79','(مۇناپىقلار) مۆمىنلەرنىڭ ئىچىدىكى مەردلىك بىلەن (كۆپ) سەدىقە قىلغۇچىلارنى، تاقىتىنىڭ يېتىشىچە (يەنى كەمبەغەل بولغانلىقتىن ئاز) سەدىقە قىلغۇچىلارنى ئەيىبلەيدۇ، ئۇلارنى مەسخىرە قىلىدۇ، ئاللاھ ئۇلارنى مەسخىرە قىلغانلىقلىرى ئۈچۈن جازالايدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(9,80,'9:80',' (ئى مۇھەممەد!) ئۇلار (يەنى مۇناپىقلار) ئۈچۈن مەيلى مەغپىرەت تەلەپ قىل، مەيلى مەغپىرەت تەلەپ قىلما (بەرىبىر ئوخشاش)، ئۇلار ئۈچۈن 70 قېتىم مەغپىرەت تەلەپ قىلساڭمۇ ئاللاھ ھەرگىز ئۇلارنى مەغپىرەت قىلمايدۇ. چۈنكى ئۇلار ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى ئىنكار قىلدى، ئاللاھ پاسىق قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(9,81,'9:81','رەسۇلۇللاھقا خىلاپلىق قىلىپ (تەبۈك غازىتىغا چىقماي) قېلىپ قالغانلار (يەنى مۇناپىقلار) ئۆيلىرىدە بەخىرامان ئولتۇرغانلىقلىرى بىلەن خۇشال بولۇشتى. ئۇلار ئاللاھنىڭ يولىدا ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلىشنى ياقتۇرمىدى. ئۇلار (بىر ـ بىرىگە): «ئىسسىقتا چىقماڭلار» دېيىشتى. (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «جەھەننەمنىڭ ئوتى تېخىمۇ قىزىقتۇر. ئەگەر ئۇلار چۈشىنىدىغان بولسا، (ئەلۋەتتە ئىسسىقتا رەسۇلۇللاھ بىلەن بىرگە چىقاتتى)». ');
INSERT INTO "saleh" VALUES(9,82,'9:82','ئۇلار قىلمىش (گۇناھ) لىرىنىڭ جازاسى ئۈچۈن، ئاز كۈلسۇن، كۆپ يىغلىسۇن. ');
INSERT INTO "saleh" VALUES(9,83,'9:83',' ئەگەر ئاللاھ سېنى (تەبۈك غازىتىدىن) قايتۇرۇپ (ئۆزرىسىز قېلىپ قالغان مۇناپىقلاردىن) بىر تۈركۈمنىڭ يېنىغا ئېلىپ بارسا، ئۇلار (ئىككىنچى بىر غازاتقا) چىقىشقا سەندىن ئىزنى سورىسا، (ئۇلارغا) ئېيتقىنكى، «سىلەر مەن بىلەن بىللە مەڭگۈ (غازاتقا) چىقماڭلار، ھەرگىز مەن بىلەن بىللە چىقىپ ئۇرۇش قىلماڭلار، شۈبھىسىزكى، سىلەر دەسلەپكى قېتىمدا (چىقماي، ئۆيۈڭلاردا بەخىرامان) ئولتۇرۇشقا رازى بولدۇڭلار، ئەمدى (غازاتقا چىقماي) قېلىپ قالغۇچىلار (يەنى ئاياللار، كىچىك بالىلار) بىلەن ئولتۇرۇڭلار».');
INSERT INTO "saleh" VALUES(9,84,'9:84',' (ئى مۇھەممەد!) سەن ئۇلارنىڭ ئارىسىدىن ئۆلگەن ھېچبىر ئادەمنىڭ نامىزىنى چۈشۈرمىگىن، (دەپنە قىلىش، زىيارەت ياكى دۇئا ئۈچۈن) ئۇنىڭ قەبرىسى ئۈستىدىمۇ تۇرمىغىن، چۈنكى ئۇلار ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى ئىنكار قىلدى. ئۇلار پاسىق ھالىتى بىلەن ئۆلدى.');
INSERT INTO "saleh" VALUES(9,85,'9:85',' ئۇلارنىڭ ماللىرى ۋە ئەۋلادلىرى سېنى ئەجەبلەندۈرمىسۇن. ئاللاھ دۇنيادا بۇلار ئارقىلىق ئۇلارنى جازالاشنى، ئۇلارنىڭ كاپىر پېتى جان ئۈزۈشىنى ئىرادە قىلىدۇ. ');
INSERT INTO "saleh" VALUES(9,86,'9:86',' بىرەر سۈرە نازىل قىلىنىپ: «ئاللاھقا ئىمان ئېيتىڭلار ۋە ئاللاھنىڭ پەيغەمبىرى بىلەن بىللە جىھاد قىلىڭلار» دېيىلسە، ئۇلارنىڭ ئارىسىدىكى مالدارلار سەندىن (غازاتتىن قېلىپ قېلىشقا) رۇخسەت سورايدۇ. ئۇلار: «بىزنى قوي، (غازاتقا چىقماي، ئۆيلىرىدە بەخىرامان) ئولتۇرغۇچىلار بىلەن بىللە بولايلى» دەيدۇ.');
INSERT INTO "saleh" VALUES(9,87,'9:87','ئۇلار ئۆيلىرىدە قالغۇچىلار (يەنى ئاياللار، كېسەللەر ۋە ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، ئۇلارنىڭ دىللىرى پېچەتلەندى. شۇنىڭ ئۈچۈن، ئۇلار (جىھادتىكى پەيغەمبەرگە ئىتائەت قىلىشتىن كېلىدىغان بەختنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(9,88,'9:88','لېكىن، پەيغەمبەر ۋە پەيغەمبەر بىلەن بىللە ئىمان ئېيتقانلار ماللىرى بىلەن، جانلىرى بىلەن جىھاد قىلدى، ئەنە شۇلار (ئىككىلا دۇنيانىڭ) ياخشىلىقلىرىغا نائىل بولىدۇ، ئەنە شۇلار مەقسەتكە يەتكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(9,89,'9:89',' ئاللاھ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى. ئۇلار ئۇنىڭدا مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر.');
INSERT INTO "saleh" VALUES(9,90,'9:90','ئەئرابىيلاردىن ئۆزرە بايان قىلغۇچىلار (جىھادقا چىقماسلىققا) رۇخسەت سوراپ كەلدى. (ئىماننى دەۋا قىلىپ) ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە يالغانچىلىق قىلغانلار جىھادقا چىقمىدى (يەنى ئەئرابىيلاردىن يەنە بىر تۈركۈمى جىھادقىمۇ چىقمىدى، ئۆزرە بايان قىلىپمۇ كەلمىدى). ئۇلاردىن كاپىر بولغانلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(9,91,'9:91','ئاجىزلار (يەنى ياشانغان بوۋايلار)، كېسەللەر، (جىھادقا چىقىشقا) خىراجەت تاپالمىغانلار ئاللاھقا ۋە ئاللاھنىڭ پەيغەمبىرىگە سادىق بولسىلا، جىھادقا چىقمىسا گۇناھ بولمايدۇ، ياخشى ئىش قىلغۇچىلارنى ئەيىبلەشكە يول يوقتۇر، ئاللاھ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(9,92,'9:92','يەنە مۇنداق كىشىلەرنىمۇ ئەيىبلەشكە يول يوقتۇركى، ئۇلار (جىھادقا چىقىش ئۈچۈن) سەندىن ئۇلاغ سوراپ كەلگەندە، (ئۇلارغا سەن) «مېنىڭ سىلەرنى تەمىنلەيدىغان ئۇلاغلىرىم يوق» دېدىڭ. ئۇلار (جىھادقا چىقىشقا) سەرپ قىلىدىغان نەرسە تاپالمىغانلىقلىرى ئۈچۈن غەمكىن، كۆزلىرىدىن ياش تۆككەن ھالدا قايتىشتى.');
INSERT INTO "saleh" VALUES(9,93,'9:93','ئۆزلىرى باي تۇرۇپ، (جىھادقا چىقماسلىققا) رۇخسەت سورىغانلار ئەيىبلىنىشى كېرەك، ئۇلار (جىھادقا چىقمىغان ئاياللار، كېسەللەر، ئاجىزلار) بىلەن بىللە بولۇشقا رازى بولدى، ئاللاھ ئۇلارنىڭ دىللىرىنى پېچەتلىۋەتتى، (شۇنىڭ ئۈچۈن) ئۇلار (ئاللاھنىڭ ئەمرىنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(9,94,'9:94','ئۇلارنىڭ يېنىغا (يەنى مەدىنىگە تەبۈك غازىتىدىن) قايتىپ كەلگەن چېغىڭلاردا، ئۇلار سىلەرگە (غازاتتىن قېلىپ قالغانلىقلىرى توغرىسىدا) ئۆزرە بايان قىلىدۇ. (ئى مۇھەممەد!) ئۇلارغا: «ئۆزرە بايان قىلماڭلار، سىلەرنىڭ (سۆزۈڭلارغا) ئىشەنمەيمىز، ئاللاھ سىلەرنىڭ ئەھۋالىڭلارنى (ۋە دىلىڭلاردىكى مۇناپىقلىقنى) بىزگە خەۋەر قىلدى. ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى سىلەرنىڭ (بۇنىڭدىن كېيىنكى) ئەمەلىڭلارنى كۆرۈپ تۇرىدۇ. (مۇناپىقلىقتىن تەۋبە قىلامسىلەر ياكى ئۇنىڭدا چىڭ تۇرامسىلەر؟)، ئاندىن كېيىن (يەنى ئۆلگىنىڭلاردىن كېيىن)، يوشۇرۇن ۋە ئاشكارا ئىشلارنى بىلگۈچى (ئاللاھ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاللاھ سىلەرگە پۈتۈن قىلمىشىڭلارنى ئېيتىپ بېرىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(9,95,'9:95','ئۇلار (يەنى مۇناپىقلار) نىڭ يېنىغا قايتىپ بارغىنىڭلاردا، ئۇلار سىلەرنىڭ ئۇلارنى ئەيىبلىمەسلىكىڭلار ئۈچۈن (يالغان ئۆزرىلەر بايان قىلىپ)، ئاللاھ بىلەن قەسەم قىلىدۇ. ئۇلاردىن يۈز ئۆرۈڭلار ، ئۇلار ھەقىقەتەن نىجىستۇر، قىلمىشلىرىنىڭ جازاسى ئۈچۈن ئۇلارنىڭ بارىدىغان جايى جەھەننەمدۇر.');
INSERT INTO "saleh" VALUES(9,96,'9:96','سىلەرنىڭ رازىلىقىڭلارغا ئېرىشىش ئۈچۈن، ئۇلار سىلەرگە قەسەم ئىچىپ بېرىدۇ، سىلەر ئۇلاردىن رازى بولساڭلار (بۇ ئۇلارغا پايدا بەرمەيدۇ)، چۈنكى ئاللاھ پاسىق قەۋمدىن رازى بولمايدۇ.');
INSERT INTO "saleh" VALUES(9,97,'9:97','ئەئرابىيلارنىڭ (يەنى قىر ئەرەبلىرىنىڭ) كۇفرى ۋە مۇناپىقلىقى تېخىمۇ زىيادىدۇر. ئاللاھ پەيغەمبىرىگە نازىل قىلغان شەرىئەت ئەھكاملىرىنى بىلمەسلىككە ئۇلار ئەڭ لايىقتۇر، ئاللاھ (مەخلۇقاتنىڭ ئەھۋالىنى) بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,98,'9:98','ئەئرابىيلاردىن بەزىلىرى (ئاللاھ يولىدا) بەرگەن نەرسىلىرىنى سېلىق ھېسابلايدۇ، سىلەرنىڭ ھادىسىگە ئۇچرىشىڭلارنى كۈتىدۇ، ئۇلارنىڭ ئۆزلىرى ھالاكەتكە ئۇچرىسۇن، ئاللاھ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچى، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,99,'9:99','ئەئرابىيلارنىڭ بەزىلىرى ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدۇ، (ئاللاھ يولىدا) بەرگەن نەرسىلىرىنى ئاللاھقا يېقىن بولۇشنىڭ ۋە پەيغەمبەرنىڭ دۇئاسىغا ئېرىشىشنىڭ ۋاسىتىسى قىلىدۇ، ئۇلار بۇ ئارقىلىق راستلا ئاللاھقا بولغان يېقىنچىلىققا ئېرىشىدۇ، ئاللاھ ئۇلارنى ئۆزىنىڭ رەھمىتى دائىرىسىگە كىرگۈزىدۇ، ئاللاھ ھەقىقەتەن (مۆمىن بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) تولىمۇ مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(9,100,'9:100','ھەممىدىن ئىلگىرى ئىمان ئېيتقان مۇھاجىرلار ۋە ئەنسارلاردىن، ياخشىلىق قىلىش بىلەن ئۇلارغا ئەگەشكەنلەر (يەنى تابىئىنلار ۋە قىيامەتكىچە ئۇلارنىڭ يولىدا ماڭغانلار) دىن ئاللاھ رازى بولدى، ئۇلارمۇ ئاللاھتىن مەمنۇن بولدى. ئاللاھ ئۇلارغا ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرنى تەييارلىدى، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، بۇ چوڭ بەختتۇر. ');
INSERT INTO "saleh" VALUES(9,101,'9:101',' ئەتراپىڭلاردا ئەئرابىيلاردىن مۇناپىقلار بار، مەدىنە ئاھالىسىدىنمۇ مۇناپىقلىقتا چىڭ تۇرغانلار بار، ئۇلارنى سەن بىلمەيسەن، بىز بىلىمىز. ئۇلارنى ئىككى قېتىم (يەنى دۇنيادا قەتل قىلىش ۋە ئەسىر ئېلىش بىلەن، ئۆلگەندە گۆر ئازابى بىلەن) جازالايمىز. ئاندىن ئۇلار (ئاخىرەتتە) چوڭ ئازابقا (يەنى دوزاخ ئازابىغا) قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(9,102,'9:102',' يەنە بىر قىسىم كىشىلەر گۇناھلىرىنى ئېتىراپ قىلدى (يەنى باشقىلارغا ئوخشاش جىھادقا چىقمىغانلىقىغا يالغان ئۆزرە ئېيتمىدى). ئۇلار ياخشى ئىشقا يامان ئىشنى ئارىلاشتۇرۇپ قويدى، ئاللاھ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلىشى مۇمكىن، ئاللاھ ھەقىقەتەن (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(9,103,'9:103','(ئى مۇھەممەد!) ئۇلارنىڭ ماللىرىنىڭ بىر قىسىمىنى سەدىقە ھېسابىدا ئالغىنكى، ئۇنىڭ بىلەن ئۇلارنى گۇناھلىرىدىن پاكلىغايسەن ۋە (ياخشىلىقلىرىنى) كۆپەيتكەيسەن، ئۇلارغا دۇئا قىلغىن، شۈبھىسىزكى، سېنىڭ دۇئايىڭ ئۇلارغا خاتىرجەملىك ئېلىپ كېلىدۇ، ئاللاھ ئۇلارنىڭ (سۆزىنى) ئاڭلاپ تۇرغۇچى، (نىيەتلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,104,'9:104','ئۇلار (يەنى تەۋبە قىلغۇچىلار) ئاللاھنىڭ ئۆز بەندىلىرىنىڭ تەۋبىسىنى قوبۇل قىلىدىغانلىقىنى، (خالىس نىيەتلىك كىشىلەرنىڭ) سەدىقىسىنى قوبۇل قىلىدىغانلىقىنى ۋە ئاللاھنىڭ تەۋبىنى بەكمۇ قوبۇل قىلىدىغانلىقىنى، ناھايىتى مېھرىبان ئىكەنلىكىنى بىلمەمدۇ؟');
INSERT INTO "saleh" VALUES(9,105,'9:105',' (ئۇلارغا) ئېيتقىنكى، «(خالىغان) ئەمەللەرنى قىلىڭلار، قىلغان ئەمەلىڭلارنى ئاللاھ ، ئاللاھنىڭ پەيغەمبىرى ۋە مۆمىنلەر كۆرۈپ تۇرىدۇ. يوشۇرۇن ۋە ئاشكارا (نەرسىلەرنى) بىلگۈچى (ئاللاھ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاللاھ قىلمىشىڭلارنى سىلەرگە خەۋەر قىلىدۇ».');
INSERT INTO "saleh" VALUES(9,106,'9:106',' يەنە بىر قىسىم (جىھادقا چىقمىغان) كىشىلەر (نىڭ ئىشى) ئاللاھنىڭ ئەمرىگە قالدۇرۇلدى، ئاللاھ يا ئۇلارنى (تەۋبە قىلمىسا) جازالايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ (ۋە مەغپىرەت قىلىدۇ). ئاللاھ ئۇلارنى (يەنى ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(9,107,'9:107',' بەزى كىشىلەر مۆمىنلەرگە زىيان يەتكۈزۈش، كۇفرىنى كۈچەيتىش، مۆمىنلەرنىڭ ئارىسىنى بوزۇش، ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە قارشى تۇرغان ئادەم (يەنى ئەبۇئامر راھىب) نىڭ كېلىشىنى كۈتۈش مەقسىتىدە مەسجىد بىنا قىلدى. ئۇلار: «بىز پەقەت ياخشىلىقنىلا ئىرادە قىلدۇق» دەپ چوقۇم قەسەم قىلىدۇ، ئاللاھ گۇۋاھلىق بېرىدۇكى، ئۇلار شەك ـ شۈبھىسىز يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(9,108,'9:108',' ئۇ مەسجىدتە مەڭگۈ ناماز ئوقۇمىغىن، بىرىنچى كۈنىدىن تارتىپلا تەقۋالىق ئاساسىغا قۇرۇلغان مەسجىد ھەقىقەتەن سېنىڭ ناماز ئوقۇشۇڭغا ئەڭ لايىقتۇر ، ئۇنىڭدا (گۇناھلاردىن) پاك بولۇشنى سۆيىدىغان كىشىلەر بار، ئاللاھ (ئىچكى ۋە تاشقى جەھەتتىن) پاك بولغۇچىلارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(9,109,'9:109',' (دىننىڭ) بىناسىنى ئاللاھنىڭ تەقۋادارلىق ۋە ئاللاھنىڭ رازىلىقى ئاساسىغا قۇرغان ئادەم ياخشىمۇ ياكى ئۇنى يىقىلاي دەپ قالغان يارنىڭ گىرۋىكىگە قۇرۇپ، ئۆزىمۇ ئۇنىڭ بىلەن بىللە دوزاخ ئوتىغا ئۆرۈلۈپ چۈشكەن ئادەم ياخشىمۇ؟ ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(9,110,'9:110','تاكى ئۇلارنىڭ يۈرەكلىرى پارە ـ پارە بولۇپ ئۆلمىگۈچە، ئۇلارنىڭ سالغان مەسجىدى (يەنى مەسجىدى زىرار) دىللىرىدىكى گۇمان (مۇناپىقلىق) نىڭ يىلتىزى بولۇپ قىلىۋېرىدۇ، ئاللاھ (مۇناپىقلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ يامان نىيىتىگە ئاساسەن، ئۇلارنى جازالاشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,111,'9:111','شۈبھىسىزكى، ئاللاھ مۆمىنلەردىن ئۇلارنىڭ جانلىرىنى، ماللىرىنى ئۇلارغا جەننەتنى بېرىپ سېتىۋالدى. ئۇلار ئاللاھنىڭ يولىدا ئۇرۇش قىلىپ (دۈشمەنلەرنى) ئۆلتۈرىدۇ ۋە ئۆلتۈرۈلىدۇ (يەنى دۈشمەنلەر بىلەن جىھاد قىلىپ شېھىت بولىدۇ)، (جىھاد قىلغۇچىلارغا جەننەتنى ۋەدە قىلىش) تەۋراتتا، ئىنجىلدا ۋە قۇرئاندا زىكرى قىلىنغان (ئاللاھنىڭ) راست ۋەدىسىدۇر، ۋەدىسىگە ئاللاھتىنمۇ بەك ۋاپا قىلغۇچى كىم بار؟ قىلغان بۇ سوداڭلاردىن خۇشال بولۇڭلار ، بۇ زور مۇۋەپپەقىيەتتۇر');
INSERT INTO "saleh" VALUES(9,112,'9:112','ئۇلار (گۇناھلىرىدىن) تەۋبە قىلغۇچىلار، (ئىخلاس بىلەن) ئىبادەت قىلغۇچىلار، (ئاللاھقا) ھەمدۇسانا ئېيتقۇچىلار، روزا تۇتقۇچىلار، رۇكۇ قىلغۇچىلار، سەجدە قىلغۇچىلار، ياخشى ئىشلارغا دەۋەت قىلىپ، يامان ئىشلاردىن توسقۇچىلار، ئاللاھنىڭ بەلگىلىمىلىرىگە رىئايە قىلغۇچىلار (يەنى ئاللاھنىڭ بېكىتكەن پەرزلىرىنى ئادا قىلىپ، چەكلىگەنلىرىدىن يانغۇچىلار)دۇر. (ئى مۇھەممەد! يۇقىرىدىكى سۈپەتلەرگە ئىگە) مۆمىنلەرگە (جەننەت بىلەن) خۇشخەۋەر بەرگىن. ');
INSERT INTO "saleh" VALUES(9,113,'9:113',' پەيغەمبەرگە ۋە مۆمىنلەرگە مۇشرىكلارنىڭ ئەھلى دوزاخ ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، مۇشرىكلار ئۇلارنىڭ تۇغقىنى بولغان تەقدىردىمۇ، ئۇلارغا مەغپىرەت تەلەپ قىلىشى دۇرۇس ئەمەس.');
INSERT INTO "saleh" VALUES(9,114,'9:114',' ئىبراھىمنىڭ ئاتىسىغا مەغپىرەت تەلەپ قىلىشى ئۇنىڭغا ئىلگىرى قىلغان ۋەدىسىنى ئورۇنلاش ئۈچۈن ئىدى. ئۇنىڭغا ئاتىسىنىڭ ئاللاھنىڭ دۈشمىنى ئىكەنلىكى ئېنىق مەلۇم بولغاندىن كېيىن، ئۇ ئاتىسى بىلەن ئادا ـ جۇدا بولدى. ئىبراھىم بولسا ھەقىقەتەن كۆپ ئاھ چەككۈچى (يەنى كۆڭلى يۇمشاق)، (يەتكەن ئەزىيەتلەرگە) چىداملىق ئىدى.');
INSERT INTO "saleh" VALUES(9,115,'9:115','ئاللاھ بىرەر قەۋمنى ھىدايەت قىلغاندىن كېيىن، ئۇلار ساقلىنىشقا تېگىشلىك ئىشلارنى بايان قىلماي تۇرۇپ، ئۇلارنى گۇمراھ قىلمايدۇ (يەنى گۇمراھلىققا ھۆكۈم قىلمايدۇ)، ئاللاھ ھەقىقەتەن ھەر نەرسىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(9,116,'9:116',' شۈبھىسىزكى، ئاسمانلارنىڭ ۋە زېمىننىڭ ئىگىدارچىلىقى ئاللاھقا خاستۇر. ئاللاھ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەرگە ئاللاھتىن باشقا ھېچ دوست ۋە مەدەتكار يوق.');
INSERT INTO "saleh" VALUES(9,117,'9:117','ئاللاھ ھەقىقەتەن پەيغەمبەرنىڭ ۋە ئۇنىڭغا قىيىنچىلىق پەيتىدە ئەگەشكەن مۇھاجىرلار ۋە ئەنسارلارنىڭ تەۋبىسىنى قوبۇل قىلدى. (ئەينى زاماندا) ئۇلارنىڭ ئارىسىدىكى بىر تۈركۈم كىشىلەرنىڭ دىللىرى ھەقتىن بۇرۇلۇپ كېتىشكە تاس قالغاندىن كېيىن، ئاللاھ ئۇلارنىڭ تەۋبىسىنى قوبۇل قىلدى. ئاللاھ ئۇلارغا تولىمۇ مەرھەمەتلىك، ناھايىتى مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(9,118,'9:118','جىھادقا چىقمىغان ئۈچ كىشىنىڭ تەۋبىسىنىمۇ ئاللاھ قوبۇل قىلدى. شۇنچە كەڭ زېمىن ئۇلارغا تار تۇيۇلغان، ئۇلارنىڭ جانلىرى سىقىلغان، ئۇلار ئاللاھقا تەۋبە قىلماي تۇرۇپ ئاللاھنىڭ غەزىپىدىن قۇتۇلۇش مۇمكىن ئەمەسلىكىگە ئىشەنگەن ئىدى. ئاندىن ئۇلارنىڭ تەۋبە قىلغۇچىلاردىن بولۇشى ئۈچۈن، ئاللاھ ئۇلارنى تەۋبىگە مۇۋەپپەق قىلدى. ئاللاھ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (بەندىلىرىگە) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(9,119,'9:119','ئى مۆمىنلەر! ئاللاھتىن قورقۇڭلار، راستچىللار بىلەن بىللە بولۇڭلار. ');
INSERT INTO "saleh" VALUES(9,120,'9:120','مەدىنە ئاھالىسى ۋە ئۇلارنىڭ ئەتراپىدىكى ئەئرابىيلارنىڭ رەسۇلۇللاھ بىلەن جىھادقا بىللە چىقماي قېلىپ قېلىشى، ئۆزلىرىنىڭ ئارامى بىلەنلا بولۇپ، جاپادا رەسۇلۇللاھ بىلەن بىللە بولماسلىقى توغرا ئەمەس ئىدى. بۇ (يەنى ئۇلارنى جىھادقا چىقماي قېلىپ قېلىشتىن مەنئى قىلىش) شۇنىڭ ئۈچۈنكى، ئۇلار ئاللاھنىڭ يولىدا ئۇچرىغان ئۇسسۇزلۇق، تارتقان جاپا ـ مۇشەققەت ۋە ئاچلىق، ئۇلارنىڭ كاپىرلارنى خاپىلىققا سالىدىغان ھەر بىر قەدىمى، دۈشمەنلەر ئۈستىدىن ئېرىشكەن (ئۇلارنى ئۆلتۈرۈش، ئەسىر ئېلىش، مەغلۇپ قىلىش قاتارلىق) ھەر بىر نەرسىسى ئۈچۈن (ئاللاھنىڭ دەرگاھىدا) ئۇلارغا ياخشىلىق (يەنى ساۋاب) يېزىلىدۇ، ئاللاھ ئەلۋەتتە ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ.');
INSERT INTO "saleh" VALUES(9,121,'9:121',' ئۇلارنىڭ (ئاللاھنىڭ يولىدا) مەيلى كىچىك بولسۇن ياكى چوڭ بولسۇن، سەرپ قىلغان نەرسىسى ۋە جىھاد ئۈچۈن باسقان ھەر بىر مۇساپىسى ئۇلارنىڭ قىلغان ئەمەللىرىگە ئەڭ ياخشى مۇكاپات بېرىش يۈزىسىدىن ئۇلارغا يېزىلىدۇ. ');
INSERT INTO "saleh" VALUES(9,122,'9:122',' مۆمىنلەرنىڭ ھەممىسىنىڭ جىھادقا چىقىشى شەرت ئەمەس (ئۇلارنىڭ بىر تۈركۈمى جىھادقا چىققان ئىكەن)، ئۇلارنىڭ ئىچىدىكى ھەر بىر جامائەدىن يەنە بىر تۈركۈمى دىنىي ئالىم بولۇپ، ئۇلار قەۋمىنىڭ قېشىغا قايتقاندىن كېيىن، قەۋمىنىڭ ئاللاھتىن قورقۇشى ئۈچۈن، ئۇلارنى ئاگاھلاندۇرۇش مەقسىتىدە نېمىشقا (ئىلىم تەلەپ قىلىشقا) چىقمىدى؟');
INSERT INTO "saleh" VALUES(9,123,'9:123','ئى مۆمىنلەر! سىلەر ئۆزۈڭلارغا يېقىن جايدىكى كاپىرلار بىلەن ئۇرۇش قىلىڭلار (يەنى سىلەرگە خەتەر بولماسلىق ئۈچۈن، ئالدى بىلەن ئەتراپىڭلاردىكى سىلەرگە ئورۇش ئاچقان كۇففارلارنى ئۇجۇقتۇرۇڭلار)، سىلەرنى ئۇلار قاتتىق ھېس قىلسۇن (يەنى ئۇرۇش ئۈستىدە ئۇلارغا قاتتىق قول بولۇڭلار ، ئۇلارغا ئىچىڭلار ئاغرىمىسۇن)، بىلىڭلاركى، ئاللاھ تەقۋادارلار بىلەن بىللىدۇر (يەنى ئاللاھ تەقۋادارلارغا مەدەتكاردۇر).');
INSERT INTO "saleh" VALUES(9,124,'9:124','(قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار (يەنى مۇناپىقلار) نىڭ ئارىسىدا (مەسخىرە قىلىش يۈزىسىدىن): «بۇ (يەنى سۈرە) قايسىڭلارنىڭ ئىمانىنى كۈچەيتتى» دەيدىغانلار بار، مۆمىنلەرگە كېلىدىغان بولساق، (ھەر سۈرىدە يېڭى دەلىل ـ ئىسپاتلار بولغانلىقى ئۈچۈن) بۇ (يەنى سۈرە) ئۇلارنىڭ ئىمانىنى كۈچەيتتى، ئۇلار (سۈرىنىڭ نازىل بولۇشىدىن) خۇشال بولىدۇ.');
INSERT INTO "saleh" VALUES(9,125,'9:125','دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) غا كېلىدىغان بولساق، بۇ (سۈرە) ئۇلارنىڭ كۇفرىغا كۇفرى قوشتى (يەنى ئۇلار مۇناپىقلىقتا تېخىمۇ ئەزۋەيلىدى، تېخىمۇ گۇمراھلاشتى)، ئۇلار كاپىر پېتى ئۆلدى.');
INSERT INTO "saleh" VALUES(9,126,'9:126','ئۇلار ئۆزلىرىنىڭ ھەر يىلى بىر قېتىم ياكى ئىككى قېتىم سىنىلىدىغانلىقىنى (يەنى ئۇلار توغرىسىدا ۋەھىي نازىل بولۇپ، سىرلىرىنىڭ پاش قىلىنىدىغانلىقىنى) بىلمەمدۇ؟ ئاندىن ئۇلار تەۋبە قىلمايدۇ (يەنى نىفاقتىن قايتمايدۇ)، ئىبرەتمۇ ئالمايدۇ.');
INSERT INTO "saleh" VALUES(9,127,'9:127','(ئۇلار پەيغەمبەر ئەلەيھىسسالامنىڭ ھۇزۇرىدىكى چاغدا، ئۇلارنىڭ ئەيىبىنى ئاچىدىغان قۇرئاندىن) بىرەر سۈرە نازىل قىلىنسا، ئۇلار بىر ـ بىرىگە قارىشىپ: «سىلەرنى بىرەرسى كۆرۈپ قالارمۇ» دېيىشىپ تىكىۋېتىدۇ، ئۇلار (ھەقىقەتنى) چۈشەنمەس قەۋم بولغانلىقى ئۈچۈن، ئاللاھ ئۇلارنىڭ دىللىرىنى ئۆزگەرتىۋەتتى. ');
INSERT INTO "saleh" VALUES(9,128,'9:128','(ئى ئىنسانلار!) شۈبھىسىزكى، سىلەرگە ئۆز ئاراڭلاردىن پەيغەمبەر كەلدى. سىلەرنىڭ كۈلپەت چېكىشىڭلار ئۇنىڭغا ئېغىر تۇيۇلىدۇ؛ ئۇ سىلەرنىڭ (ھىدايەت تېپىشىڭلارغا) ھېرىستۇر، مۆمىنلەرگە ئامراقتۇر، ناھايىتى كۆيۈمچاندۇر.');
INSERT INTO "saleh" VALUES(9,129,'9:129','(ئى پەيغەمبەر!) ئەگەر ئۇلار (ساڭا ئىمان ئېيتىشتىن) يۈز ئۆرۈسە، سەن: «ئاللاھ ماڭا كۇپايە قىلىدۇ، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم (يەنى ئاللاھقا يۆلەندىم، ئاللاھتىن باشقا ھېچ ئەھەدىدىن ئۈمىد كۈتمەيمەن ۋە قورقمايمەن)، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر» دېگىن.');
INSERT INTO "saleh" VALUES(10,1,'10:1','ئەلىف، لام، را. بۇ، ھېكمەتلىك كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر. ');
INSERT INTO "saleh" VALUES(10,2,'10:2','ئىنسانلارنىڭ بىرىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا): ئىنسانلار (يەنى كاپىرلار) نى (ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇرغىن، مۆمىنلەرگە پەرۋەردىگارىنىڭ دەرگاھىدا (قىلغان ياخشى ئەمەللىرى ئۈچۈن) يۇقىرى ئورۇنغا ئېرىشىدىغانلىقى بىلەن خۇشخەۋەر بەرگىن، دەپ ۋەھىي قىلغانلىقىمىزدىن ئۇلار ئەجەبلىنەمدۇ؟ كاپىرلار: «بۇ ھەقىقەتەن ئاشكارا سېھىرگەردۇر» دېدى.');
INSERT INTO "saleh" VALUES(10,3,'10:3','شۈبھىسىزكى، سىلەرنىڭ پەرۋەردىگارىڭلار ئاللاھتۇر، ئۇ ئىنسانلارنى، زېمىننى ئالتە كۈندە ياراتتى. ئاندىن ئەرش ئۈستىدە ئۆزىگە لايىق رەۋىشتە قارار ئالدى. ئاللاھ خالايىقنىڭ ئىشلىرىنى (ئۆز ھېكمىتىگە مۇۋاپىق) ئىدارە قىلىپ تۇرىدۇ، (قىيامەت كۈنى) ھەر قانداق شاپائەت قىلغۇچى ئالدى بىلەن ئاللاھنىڭ ئىزنىنى ئالىدۇ، ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار. ۋەز ـ نەسىھەت ئالمامسىلەر؟.');
INSERT INTO "saleh" VALUES(10,4,'10:4','(قىيامەت كۈنى) ھەممىڭلار ئاللاھنىڭ دەرگاھىغا قايتىسىلەر، ئاللاھنىڭ ۋەدىسى ھەقتۇر. شۈبھىسىزكى، مەخلۇقاتنى دەسلەپتە ئاللاھ ياراتقان، (ئۇلارنى ھالاك قىلغاندىن كېيىن، قىيامەت كۈنى) ئىمان ئېيتقانلارنى ۋە ياخشى ئەمەل قىلغانلارنى ئادىللىق بىلەن مۇكاپاتلاش ئۈچۈن قايتا تىرىلدۈرىدۇ. كاپىرلارنىڭ بولسا، ئۆزلىرىنىڭ كۇفرى تۈپەيلىدىن، (دوزاختىكى) ئىچىملىكى قاتتىق قاينىغان سۇ بولىدۇ، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(10,5,'10:5','ئاللاھ قۇياشنى نۇر چاچقۇچى ۋە ئاينى نۇرلۇق قىلىپ ياراتتى. يىللارنىڭ سانىنى ۋە (ۋاقىتلارنىڭ) ھېسابىنى بىلىشىڭلار ئۈچۈن، ئايغا مەنزىللەرنى (يەنى بۇرۇجلارنى) تەيىن قىلدى. ئاللاھ ئۇلارنى بىكار ياراتقىنى يوق (بەلكى بۈيۈك ھېكمەت ۋە زور مەنپەئەت ئۈچۈن ياراتتى)، ئاللاھ ئايەتلىرىنى (قۇدرىتى ئىلاھىيىنى) بىلىدىغان (ۋە ئۇنىڭ ھېكمىتىنى پىكىر قىلىدىغان) قەۋم ئۈچۈن تەپسىلىي بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(10,6,'10:6',' كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، ئاللاھ ئاسمانلاردا ۋە زېمىندا ياراتقان شەيئىلەردە (ئاللاھتىن) قورقىدىغان قەۋم ئۈچۈن، ھەقىقەتەن، (ئاللاھنىڭ بارلىقىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(10,7,'10:7','شۈبھىسىزكى، بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى ئاللاھقا مۇلاقات بولۇشنى كۆزلىمەيدىغانلار)، دۇنيا تىرىكچىلىكىدىن بەھرىمەن بولۇش بىلەنلا رازى بولغانلار ۋە ئۇنىڭ بىلەن ئارام تاپقانلار، بىزنىڭ ئايەتلىرىمىزدىن غاپىل بولغانلار ');
INSERT INTO "saleh" VALUES(10,8,'10:8',' ئەنە شۇلارنىڭ جايى، قىلمىشلىرى تۈپەيلىدىن، دوزاخ بولىدۇ.');
INSERT INTO "saleh" VALUES(10,9,'10:9',' شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلارنى پەرۋەردىگارى، ئۇلارنىڭ ئىمانى سەۋەبىدىن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان نازۇ-نېمەتلىك جەننەتلەرگە يېتەكلەيدۇ.');
INSERT INTO "saleh" VALUES(10,10,'10:10','ئۇلارنىڭ جەننەتتىكى دۇئاسى: «ئى رەببىمىز، بىز سېنى پاك دەپ ئېتىقاد قىلىمىز» دېيىشتىن ئىبارەت. ئۇلارنىڭ جەننەتتە (بىر ـ بىرىگە) بېرىدىغان سالامى: «(ئەھلى دوزاخ چېكىۋاتقان ئازابتىن) ئامان بولغايسەن (يەنى ئەسسالامۇ ئەلەيكۇم)» دېيىشتىن ئىبارەتتۇر. ئۇلارنىڭ دۇئاسىنىڭ ئاخىرى: «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!» دېيىشتىن ئىبارەتتۇر.');
INSERT INTO "saleh" VALUES(10,11,'10:11',' ئەگەر ئاللاھ كىشىلەر تەلەپ قىلغان يامانلىقنى ئىشقا ئاشۇرۇشقا ئۇلار تەلەپ قىلغان ياخشىلىقىنى ئىشقا ئاشۇرۇشقا ئالدىرىغاندەك ئالدىرىسا ئىدى، ئۇلارغا ئەجىلى چوقۇم يەتكەن بولاتتى (يەنى ھالاك بولاتتى). بىز بىلەن مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلارنى گۇمراھلىقتا تېڭىرقاپ يۈرۈشكە قويۇپ بېرىمىز.');
INSERT INTO "saleh" VALUES(10,12,'10:12','ئىنسانغا بىرەر زىيان ـ زەخمەت يەتسە، ياتسىمۇ، ئولتۇرسىمۇ، تۇرسىمۇ، (ئۇنى دەپئى قىلىشىمىزنى تىلەپ) بىزگە دۇئا قىلىدۇ؛ ئۇلارغا يەتكەن زىيان ـ زەخمەتنى كۆتۈرۈۋەتسەك، گويا ئۇنى كۆتۈرۈۋېتىشىمىزنى تىلەپ بىزگە دۇئا قىلمىغاندەك يامان ئىشنى يەنە داۋاملاشتۇرىدۇ. ھەددىدىن ئاشقۇچىلارنىڭ قىلمىشلىرى ئۇلارغا مۇشۇنداق چىرايلىق كۆرسىتىلدى.');
INSERT INTO "saleh" VALUES(10,13,'10:13','سىلەردىن ئىلگىرىكى ئۈممەتلەر زۇلۇم قىلغاندا (يەنى كاپىر بولغاندا، گۇمراھلىقتا ھەددىدىن ئاشقاندا)، ھەقىقەتەن ئۇلارنى ھالاك قىلدۇق، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى. ئۇلار (پەيغەمبەرلەر ئېلىپ كەلگەن مۆجىزىلەرگە) ئىمان كەلتۈرمىدى. گۇناھكار قەۋمنى شۇنىڭغا ئوخشاش جازالايمىز.');
INSERT INTO "saleh" VALUES(10,14,'10:14','سىلەرنىڭ قانداق قىلىدىغانلىقىڭلارنى كۆرۈش ئۈچۈن، ئۇلارنى (يەنى ئۆتكەن ئۈممەتلەرنى) (ھالاك قىلغان) دىن كېيىن، ئۇلارنىڭ ئورنىغا سىلەرنى زېمىندا ئورۇنباسار قىلدۇق.');
INSERT INTO "saleh" VALUES(10,15,'10:15','ئۇلارغا بىزنىڭ ئايەتلىرىمىز روشەن تىلاۋەت قىلىنسا، بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى قىيامەتكە ئىشەنمەيدىغانلار): «بۇنىڭدىن باشقا بىر قۇرئاننى كەلتۈرگىن ياكى ئۇنى ئۆزگەرتكىن (يەنى ئازابقا دائىر ئايەتنى رەھمەت ئايىتىگە ئۆزگەرت، ئىلاھلىرىمىزنى سۆككەننىڭ ئورنىغا مەدھىيەلە، ھارامنى ھالال قىل)» دەيدۇ. «مەن ئۇنى ئۆزۈمچە ئۆزگەرتەلمەيمەن، مەن پەقەت ماڭا قىلىنغان ۋەھىيگىلا ئەمەل قىلىمەن؛ ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، ئەلۋەتتە، بۈيۈك كۈننىڭ (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن قورقىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(10,16,'10:16',' «ئەگەر ئاللاھ ئىرادە قىلسا، سىلەرگە ئۇنى (يەنى قۇرئاننى) تىلاۋەت قىلماس ئىدىم، ئاللاھمۇ ئۇنى (مەن ئارقىلىق) سىلەرگە بىلدۈرمەس ئىدى. ئاراڭلاردا ئۇنىڭدىن (يەنى قۇرئان نازىل بولۇشتىن) ئىلگىرى ئۇزۇن زامان تۇردۇم، (مۆجىزە بولغان بۇ قۇرئاننىڭ پەقەت ئاللاھ تەرىپىدىن نازىل بولغانلىقىنى بىلىشكە) ئەقلىڭلارنى ئىشلەتمەمسىلەر؟» دېگىن. ');
INSERT INTO "saleh" VALUES(10,17,'10:17','ئاللاھقا يالغاننى چاپلىغان ياكى ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ گۇناھكارلار ھەرگىز بەختكە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(10,18,'10:18','ئۇلار ئاللاھنى قويۇپ، ئۆزلىرىگە پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، «بۇلار ئاللاھنىڭ دەرگاھىدا بىزگە شاپائەت قىلغۇچىلار» دېيىشىدۇ. ئەجەبا، ئاسمانلاردا ۋە زېمىندا بۇنداق نەرسە (يەنى ئاللاھنىڭ شېرىكى ياكى شاپائەت قىلغۇچى) بولۇپ، ئۇنى ئاللاھ بىلمەي قېلىپ، سىلەر ئاللاھقا ئۇنى ئېيتىپ بېرىۋاتامسىلەر؟ ئاللاھ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر.');
INSERT INTO "saleh" VALUES(10,19,'10:19',' ئىنسانلار (ئەسلىدە) بىر دىن (يەنى ھەق دىن) دا ئىدى. كېيىن ئۇلار ئىختىلاپ قىلىشتى (يەنى دىنلار كۆپەيدى). پەرۋەردىگارىڭنىڭ ئالدىنئالا ئېيتقان سۆزى (يەنى جازالاشنى قىيامەتكە قالدۇرۇشتىن ئىبارەت تەقدىرى) بولمىسا ئىدى، ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلاتتى (يەنى دىندا ئىختىلاپ قىلىشقانلىرى ئۈچۈن، ئۇلارغا بۇ دۇنيادىلا جازا بېرىلەتتى). ');
INSERT INTO "saleh" VALUES(10,20,'10:20','ئۇلار (يەنى ئەھلى مەككە): «نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) پەرۋەردىگارى تەرىپىدىن (بىز تەلەپ قىلغان مۆجىزىلەردىن) بىر مۆجىزە چۈشۈرۈلمىدى؟» دەيدۇ. ئېيتقىنكى، «غەيبنى بىلىش پەقەت ئاللاھقا خاس (مۆجىزىلەرنى ئاللاھ كەلتۈرىدۇ، مەن پەقەت تەبلىغ قىلغۇچىمەن)، سىلەر (ئاللاھنىڭ ئارىمىزدا ھۆكۈم چىقىرىشىنى) كۈتۈڭلار! شۈبھىسىزكى، مەنمۇ سىلەر بىلەن (شۇنى) كۈتكۈچىلەردىنمەن».');
INSERT INTO "saleh" VALUES(10,21,'10:21',' كىشىلەرنىڭ بېشىغا كەلگەن زىيان ـ زەخمەتنى كۆتۈرۈۋەتكەندىن كېيىن، ئۇلارغا رەھمىتىمىزنى تېتىتساق، ئۇلار بىزنىڭ ئايەتلىرىمىز ئۈستىدە مىكىر ئىشلىتىشكە (يەنى ئىنكار قىلىشقا) ئالدىرايدۇ. ئېيتقىنكى، «ئاللاھ ئۇلارنى قىلغان مىكرى ئۈچۈن ئەڭ تېز جازالايدۇ». شۈبھىسىزكى، بىزنىڭ پەرىشتىلىرىمىز مىكرىڭلارنى يېزىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(10,22,'10:22','ئاللاھ سىلەرنى قۇرۇقلۇقتا، دېڭىزدا سەپەر قىلدۇرىدۇ، سىلەر ئولتۇرغان كېمە (كىشىلەرنى ئېلىپ) مەيىن شامالدا مېڭىۋاتقان ۋە (بۇ) شامالدىن ئۇلار خۇشاللىنىۋاتقان چاغدا، بىردىنلا بوران چىقىپ (كېمە ئۆرۈلىدۇ)، ئۇلار تەرەپ ـ تەرەپتىن كۆتۈرۈلۈۋاتقان دېڭىز دولقۇنلىرى ئىچىدە قالىدۇ، كېمىدىكىلەر قورشىۋېلىنغانلىقىغا (يەنى ھالاك بولىدىغانلىقىغا) جەزم قىلىدۇ، (چوقۇنۇۋاتقان نەرسىلىرىنى تاشلاپ قويۇپ) «ئى خۇدا! ئەگەر سەن بىزنى بۇنىڭدىن (يەنى بالادىن) قۇتۇلدۇرساڭ، بىز چوقۇم شۈكۈر قىلغۇچىلاردىن بولىمىز» دەپ چىن كۆڭلى بىلەن دۇئا قىلىدۇ.');
INSERT INTO "saleh" VALUES(10,23,'10:23','ئاللاھ ئۇلارنى (ئۇ بالادىن) قۇتقۇزغاندىن كېيىن، ئۇلار يەر يۈزىدە يولسىزلىق بىلەن بۇزغۇنچىلىق قىلىدۇ. ئى ئىنسانلار! بۇزغۇنچىلىقىڭلارنىڭ گۇناھى ئۆزۈڭلارغا بولىدۇ، (سىلەر) بۇ دۇنيا (نىڭ ئۆتكۈنچى نېمەتلىرى) دىن بەھرىمەن بولىسىلەر (بۇنىڭ ھەسرىتى ئاخىرەتكە قالىدۇ)، ئاندىن سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى خەۋەر قىلىمىز.');
INSERT INTO "saleh" VALUES(10,24,'10:24',' ھاياتىي دۇنيانىڭ مىسالى (تېز ئۆزگۈرۈشىدە، نېمەتلىرىنىڭ ئۆتكۈنچى بولۇشىدا، كىشىلەرنىڭ ئۇنىڭ بىلەن مەغرۇر بولۇشىدا) بىز ئاسماندىن چۈشۈرگەن (يامغۇر) سۈيىگە ئوخشايدۇكى، ئۇنىڭ بىلەن زېمىننىڭ ئىنسانلار، ھايۋانلار يەيدىغان تۈرلۈك ئۆسۈملۈكلىرى (ئۈنۈپ چىقىپ) بىر ـ بىرى بىلەن گىرەلىشىپ كەتكەن، ھەتتا زېمىن گۈزەل تۈسكە كىرگەن، چىرايلىق ياسانغان، زېمىن ئىگىلىرى ئۇنىڭدىن مول ھوسۇل ئېلىشقا جەزم قىلىپ تۇرغان چاغدا، ناگاھان كېچىسى ياكى كۈندۈزى ئۇنىڭغا قازايىمىز يەتتى ـ دە، (زىرائەتلەرنى) تۈنۈگۈن (يەنى ئىلگىرى) يوقتەك، (پۈتۈنلەي) ئورۇپ تاشلانغاندەك قىلىۋەتتۇق (يەنى نابۇت قىلىپ، يوق قىلدۇق)، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(10,25,'10:25','ئاللاھ (كىشىلەرنى) ئامان جايغا (يەنى جەننەتكە) چاقىرىدۇ ۋە خالىغان ئادەمنى توغرا يولغا باشلايدۇ.');
INSERT INTO "saleh" VALUES(10,26,'10:26','ياخشى ئىش قىلغانلار جەننەتكە ۋە ئاللاھنىڭ جامالىنى كۆرۈشكە نائىل بولىدۇ، ئۇلارنىڭ يۈزلىرىنى قارىلىق ۋە خارلىق قاپلىمايدۇ (يەنى ئۇلارغا غەم ـ قايغۇ ۋە پەرىشانلىق يۈزلەنمەيدۇ). ئەنە شۇلار ئەھلى جەننەت بولۇپ، جەننەتتە مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(10,27,'10:27','(دۇنيادا) يامان ئىشلارنى قىلغۇچىلار (بىر يامانلىقنى قىلسا، قىلغان يامانلىقىغا) ئوخشاش (يەنى شۇنىڭغا لايىق) جازاغا ئۇچرايدۇ، ئۇلار خارلىققا دۇچار بولىدۇ، ئۇلارنى ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرغۇچى زادى بولمايدۇ، ئۇلارنىڭ يۈزلىرى گويا قاراڭغۇ كېچىنىڭ پارچىسى بىلەن ئورالغاندەك قارىيىپ كېتىدۇ، ئۇلار ئەھلى دوزاخ بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(10,28,'10:28','شۇ كۈن (يەنى قىيامەت كۈنى) دە (مۆمىنلەرنى، كاپىرلارنى) ھەممىسىنى يىغىمىز، ئاندىن مۇشرىكلارغا: «سىلەر ئاللاھقا شېرىك كەلتۈرگەنلىرىڭلار بىلەن بىللە، ئورنۇڭلاردا مىدىرلىماي تۇرۇڭلار» دەيمىز، ئۇلارنىڭ ئارىسىنى ئايرىيمىز، ئۇلارنىڭ شېرىك كەلتۈرگەنلىرى ئېيتىدۇ: «سىلەر بىزگە ئىبادەت قىلمىغان ئىدىڭلار (بەلكى نەپسى خاھىشىڭلارغا ئىبادەت قىلغان ئىدىڭلار).');
INSERT INTO "saleh" VALUES(10,29,'10:29','سىلەر بىلەن بىزنىڭ ئارىمىزدا گۇۋاھ بولۇشقا ئاللاھ كۇپايىكى، سىلەرنىڭ بىزگە چوقۇنۇشۇڭلاردىن بىز غاپىل ئىدۇق».');
INSERT INTO "saleh" VALUES(10,30,'10:30',' بۇ چاغدا ھەر ئادەم ئىلگىرى قىلغان ئەمەللىرى ئۈستىدە سىنىلىدۇ (يەنى ياخشىمۇ ـ يامانمۇ، ئۇنىڭ نەتىجىسىنى كۆرىدۇ)، ئۇلار ھەقىقىي ئىگىسى ـ ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىدۇ، ئۇلارنىڭ شاپائەت قىلىدۇ دەپ دەۋا قىلغانلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ.');
INSERT INTO "saleh" VALUES(10,31,'10:31',' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەرگە ئاسماندىن (يامغۇر ياغدۇرۇپ) زېمىندىن (گىياھ ئۈندۈرۈپ) كىم رىزىق بېرىدۇ؟ سىلەرنىڭ ئاڭلاش ۋە كۆرۈش قابىلىيىتىڭلارنى كىم باشقۇرىدۇ؟ تىرىك شەيئىلەرنى ئۆلۈك شەيئىلەردىن كىم پەيدا قىلىدۇ؟ ئۆلۈك شەيئىلەرنى تىرىك شەيئىلەردىن كىم پەيدا قىلىدۇ؟ (خالايىقنىڭ) ئىشلىرىنى كىم ئىدارە قىلىدۇ؟» ئۇلار (بۇلارغا جاۋابەن): «ئاللاھ» دەيدۇ. ئېيتقىنكى، «ئاللاھتىن غەيرىيگە چوقۇنۇش بىلەن ئاللاھنىڭ ئازابىدىن) قورقمامسىلەر؟.');
INSERT INTO "saleh" VALUES(10,32,'10:32','(يۇقىرىقىدەك چوڭ ئىشلارنى قىلغۇچى) سىلەرنىڭ ھەقىقىي پەرۋەردىگارىڭلار، ئەنە شۇ ئاللاھتۇر. ھەقىقەتتىن قالسا، گۇمراھلىقتىن غەيرىي نەرسە مەۋجۇت ئەمەس، نېمىشقا (ئىماندىن يۈز ئۆرۈپ گۇمراھلىققا) بۇرۇلۇپ كېتىسىلەر؟».');
INSERT INTO "saleh" VALUES(10,33,'10:33','پەرۋەردىگارىڭنىڭ ئاللاھقا ئىتائەت قىلىشتىن باش تارتقانلار توغرىسىدىكى ھۆكمى ھەقلىق بولدى. ئۇلار (ئاللاھنىڭ بىرلىكىگە، رەسۇلۇللاھنىڭ پەيغەمبەرلىكىگە) ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(10,34,'10:34','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرنىڭ شېرىك كەلتۈرگەن نەرسىلىرىڭلارنىڭ ئىچىدە مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «ئاللاھ مەۋجۇداتنى يوقلۇقتىن بار قىلىپ (ئاندىن يوق قىلىپ)، ئاندىن ئۇنى ئەسلىگە كەلتۈرەلەيدۇ. نېمىشقا (ھەقتىن باتىل تەرەپكە) بۇرۇلۇپ كېتىسىلەر؟».');
INSERT INTO "saleh" VALUES(10,35,'10:35','ئېيتقىنكى، « شېرىك كەلتۈرگەن نەرسىلىرىڭلارنىڭ ئارىسىدا ھەق يولغا يېتەكلىيەلەيدىغانلار بارمۇ؟» ئېيتقىنكى، «ئاللاھ ھەق يولغا يېتەكلىيەلەيدۇ، ھەق يولغا يېتەكلىيەلەيدىغان زاتقا بويسۇنۇشقا تېگىشلىكمۇ ياكى ھەق يولغا يېتەكلىيەلمەيدىغان، پەقەت ئۆزى باشقىلارنىڭ يېتەكلىشىگە موھتاج بولغان (نەرسىلەرگە) بويسۇنۇشقا تېگىشلىكمۇ؟ سىلەرگە نېمە بولدى؟ نېمىشقا مۇنداق (باتىل) ھۆكۈم چىقىرىسىلەر؟». ');
INSERT INTO "saleh" VALUES(10,36,'10:36',' ئۇلارنىڭ تولىسى پەقەت پەرەزگىلا تايىنىدۇ، پەرەز دېگەن ھەقىقەتنى ئىسپاتلاشتا ھەقىقەتەن ھېچ نەرسىگە يارىمايدۇ. شۈبھىسىزكى، ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى ئوبدان بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(10,37,'10:37',' بۇ قۇرئاننىڭ ئاللاھتىن باشقىسى تەرىپىدىن ۋۇجۇدقا چىقىرىلىشى ئەقىلغە سىغمايدۇ (چۈنكى قۇرئاننى ھېچ ئىنسان ئىجاد قىلالمايدۇ). قۇرئان ئىلگىرى كەلگەن (تەۋرات، ئىنجىل قاتارلىق ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، ئاللاھ بەلگىلىگەن ئەھكاملارنى بايان قىلغۇچىدۇر، ئۇنىڭدا ھېچ شەك يوقتۇر، (ئۇ) ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(10,38,'10:38','ئۇلار: «قۇرئاننى مۇھەممەد ئۆزى توقۇپ چىققان» دېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر (بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، ئاللاھتىن باشقا (ياردەمگە) چاقىرالايدىغانلىكى كىشىڭلارنى چاقىرىپ، قۇرئانغا ئوخشاش بىرەر سۈرىنى كەلتۈرۈپ بېقىڭلار».');
INSERT INTO "saleh" VALUES(10,39,'10:39',' ئۇنداق ئەمەس، ئۇلار قۇرئاننىڭ مەنىسىنى تولۇق چۈشەنمەي ۋە ھەقىقىتىگە تېخى ئەقلى يەتمەي تۇرۇپ ئۇنى ئىنكار قىلىشتى. ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) مۇشۇلارغا ئوخشاش يالغانغا چىقارغان ئىدى. زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراپ باققىن.');
INSERT INTO "saleh" VALUES(10,40,'10:40',' ئۇلارنىڭ ئىچىدە قۇرئانغا ئىشىنىدىغانلار بار، ئىشەنمەيدىغانلارمۇ بار، پەرۋەردىگارىڭ بۇزغۇنچىلارنى ھەممىدىن ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(10,41,'10:41','ئەگەر ئۇلار سېنى يالغانچى قىلىشتا چىڭ تۇرسا: «مېنىڭ قىلغان ئەمەلىم ئۆزۈم ئۈچۈندۇر، سىلەرنىڭ قىلغان ئەمەلىڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر، مېنىڭ قىلغان ئەمەلىمدىن سىلەر ئادا ـ جۇداسىلەر، سىلەرنىڭ قىلمىشىڭلاردىن مەنمۇ ئادا ـ جۇدامەن (يەنى بىراۋ باشقا بىراۋنىڭ قىلغان گۇناھىغا جاۋابكار ئەمەس)» دېگىن.');
INSERT INTO "saleh" VALUES(10,42,'10:42','ئۇلارنىڭ ئىچىدە ساڭا قۇلاق سالىدىغانلار بار، سەن گاسلارغا ئاڭلىتالامسەن؟ ئۇلار ئەقلىنى ئىشلەتمەيدىغان تۇرسا .');
INSERT INTO "saleh" VALUES(10,43,'10:43','ئۇلارنىڭ ئىچىدە ساڭا قارايدىغانلار بار (يەنى پەيغەمبەرلىكىڭنىڭ ئېنىق ئالامەتلىرىنى بايقايدىغانلار بار، لېكىن ئۇلار بايقىغانلىرىدىن پايدىلانمىغانلىقلىرى ئۈچۈن كوردۇر). سەن كورلارنى ھىدايەت قىلالامسەن؟ ئۇلار (ھەقىقەتنى) كۆرمەيدىغان تۇرسا.');
INSERT INTO "saleh" VALUES(10,44,'10:44',' شۈبھىسىزكى، ئاللاھ ئىنسانلارغا قىلچە زۇلۇم قىلمايدۇ، لېكىن ئىنسانلار (كۇفرى ۋە گۇناھ ئارقىلىق) ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلىدۇ.');
INSERT INTO "saleh" VALUES(10,45,'10:45','قىيامەت كۈنى ئاللاھ ئۇلارنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، (ئۇلارغا دۇنيادا تۇرغان مۇددىتى) كۈندۈزدە ئازغىنا ۋاقىت تۇرغاندەك قىسقا بىلىنىدۇ، ئۇلار ئۆزئارا تونۇشىدۇ، ئۆزلىرىنىڭ ئاللاھقا مۇلاقات بولۇشىنى (يەنى ئۆلگەندىن كېيىن تىرىلىشىنى) ئىنكار قىلغانلار چوقۇم زىيان تارتىدۇ، ئۇلار توغرا يول تاپمىدى.');
INSERT INTO "saleh" VALUES(10,46,'10:46','ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى ساڭا كۆرسىتىمىز (يەنى بۇ دۇنيادىلا، سەن ھايات ۋاقىتتىلا كۆرسىتىمىز)، ياكى ئۇنىڭدىن ئىلگىرى سېنى قەبزى روھ قىلىمىز، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ (مەيلى بالدۇر بولسۇن، مەيلى كېيىن بولسۇن، ئۇلارنى چوقۇم جازالايمىز)، ئاندىن ئاللاھ ئۇلارنىڭ قىلمىشلىرىغا گۇۋاھ بولىدۇ.');
INSERT INTO "saleh" VALUES(10,47,'10:47',' (ئۆتكەن ئۈممەتلەرنىڭ) ھەر بىرىگە پەيغەمبەر ئەۋەتىلگەن. (قىيامەت كۈنى) ئۇلارنىڭ پەيغەمبىرى كەلگەندە، ئۇلارنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(10,48,'10:48',' ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار (بىزگە ئېيتىپ بېرىڭلاركى) بۇ ۋەدە قاچان ئىشقا ئاشۇرۇلىدۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(10,49,'10:49','ئېيتقىنكى: «ئاللاھ خالىمىسىلا، مەن ئۆزۈمدىن زىياننى دەپئى قىلىشقا، پايدا قازىنىشقا قادىر ئەمەسمەن، ھەر ئۈممەتنىڭ (ھالاك بولىدىغان) مەلۇم ۋاقتى بولىدۇ، شۇ ۋاقىت يېتىپ كەلگەندە، ئۇلار بىردەممۇ كېچىكتۈرۈلمەيدۇ، بىردەممۇ ئىلگىرى سۈرۈلمەيدۇ».');
INSERT INTO "saleh" VALUES(10,50,'10:50','ئېيتقىنكى: «ئېيتىڭلارچۇ! ئەگەر ئاللاھنىڭ ئازابى سىلەرگە كېچىسى ياكى كۈندۈزى كەلسە (بۇنىڭدىن سىلەرگە نېمە پايدا؟) گۇناھكارلار (يەنى مۇشرىكلار) ئازابتىن قايسى نەرسىگە ئالدىرايدۇ؟');
INSERT INTO "saleh" VALUES(10,51,'10:51','(ئاللاھنىڭ) ئازابى يۈز بەرگەندە ئاندىن ئۇنىڭغا ئىشەندىڭلارمۇ؟ ئەمدى (ئېيتقان ئىمانىڭلار پايدىسىزدۇر، ئۇنىڭدىن ئىلگىرى مەسخىرە قىلىش بىلەن) ئازابنىڭ تېزرەك چۈشۈشىنى تەلەپ قىلاتتىڭلار».');
INSERT INTO "saleh" VALUES(10,52,'10:52','ئاندىن كېيىن (ئۆزلىرىگە) زۇلۇم قىلغانلارغا: «مەڭگۈلۈك ئازابنى تېتىڭلار، سىلەرگە پەقەت قىلمىشىڭلارنىڭ جازاسى بېرىلىدۇ» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(10,53,'10:53','ئۇلار سەندىن: «ئۇ (يەنى ۋەدە قىلىنغان ئازاب، ئۆلگەندىن كېيىن تىرىلىش) راستمۇ؟» دەپ سورايدۇ، سەن: «ھەئە، پەرۋەردىگارىم بىلەن قەسەمكى، ئۇ ئەلۋەتتە راستتۇر، سىلەر قېچىپ قۇتۇلالمايسىلەر» دېگىن.');
INSERT INTO "saleh" VALUES(10,54,'10:54',' ئەگەر ھەر بىر زۇلۇم قىلغان ئادەم زېمىندىكى ھەممە نەرسىگە ئىگە بولىدىغان بولسا (ئاللاھنىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە (ئۇنىڭ ھەممىسىنى) فىدىيە قىلىپ بىرىۋېتەتتى، ئۇلار ئازابنى كۆرگەن چاغدا نادامىتىنى يوشۇرۇن تۇتىدۇ (يەنى دۇنيادىكى قىلمىشلىرىغا ئىچىدە پۇشايمان قىلىدۇ). ئۇلارنىڭ (يەنى خالايىقنىڭ) ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(10,55,'10:55','راستلا ئاسمانلاردىكى ۋە زېمىندىكى بارلىق نەرسىلەر ئاللاھنىڭ مۈلكىدۇر، راستلا ئاللاھنىڭ ۋەدىسى ھەقتۇر، لېكىن ئۇلار (يەنى ئىنسانلار) نىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(10,56,'10:56','ئاللاھ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنى) ئۆلتۈرىدۇ، سىلەر ئاللاھنىڭ دەرگاھىغا قايتىسىلەر.');
INSERT INTO "saleh" VALUES(10,57,'10:57','ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن نەسىھەت بولغان، دىللاردىكى دەردكە (يەنى شەك ۋە نادانلىققا) شىپا بولغان، مۆمىنلەرگە ھىدايەت ۋە رەھمەت بولغان (قۇرئان) كەلدى.');
INSERT INTO "saleh" VALUES(10,58,'10:58','ئېيتقىنكى، «ئۇلار ئاللاھنىڭ پەزلى ۋە رەھمىتىدىن خۇشال بولسۇن، بۇ ئۇلارنىڭ يىغقان (دۇنيا ـ ماللىرىدىن) ياخشىدۇر».');
INSERT INTO "saleh" VALUES(10,59,'10:59','ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ! سىلەرگە ئاللاھ چۈشۈرگەن رىزىقتىن بەزىسىنى ھارام، بەزىسىنى ھالال قىلدىڭلار، ئاللاھ سىلەرگە مۇنداق قىلىشقا ئىزنى بەردىمۇ؟ ياكى ئاللاھقا يالغاننى چاپلامسىلەر؟».');
INSERT INTO "saleh" VALUES(10,60,'10:60','ئاللاھقا يالغاننى چاپلايدىغانلار قىيامەت كۈنى قانداق ئويلايدۇ؟ (يەنى جازاغا تارتىلمايمىز، دەپ ئويلامدۇ؟) شۈبھىسىزكى، ئاللاھ ئىنسانلارغا مەرھەمەتلىكتۇر (چۈنكى ئۇلارغا بولىدىغان ئازابنى كېچىكتۈردى)، لېكىن كىشىلەرنىڭ تولىسى (ئاللاھنىڭ نېمەتلىرىگە) شۈكۈر قىلمايدۇ.');
INSERT INTO "saleh" VALUES(10,61,'10:61',' سەن قايسى ھالەتتە بولمىغىن، قۇرئاندىن قايسى نەرسىنى ئوقۇمىغىن، سىلەر قايسىبىر ئىشنى قىلماڭلار، ئۇنىڭ بىلەن بولۇۋاتقان ۋاقتىڭلاردا ھامان بىز سىلەرنى كۆزىتىپ تۇرىمىز. ئاسمان ـ زېمىندىكى زەررە چاغلىق نەرسە ۋە ئۇنىڭدىن كىچىك ياكى چوڭ نەرسە بولسۇن، ھېچقايسىسى ئاللاھنىڭ بىلىشىدىن چەتتە قالمايدۇ. ئۇلارنىڭ ھەممىسى لەۋھۇلمەھپۇزدا خاتىرىلەنگەندۇر.');
INSERT INTO "saleh" VALUES(10,62,'10:62','راستلا ئاللاھنىڭ دوستلىرىغا (ئاخىرەتتە ئاللاھنىڭ ئازابىدىن) قورقۇش يوقتۇر، ئۇلار غەم-ئەندىشە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(10,63,'10:63',' ئۇلار ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلاردۇر.');
INSERT INTO "saleh" VALUES(10,64,'10:64',' ئۇلارغا دۇنيا ۋە ئاخىرەتتە خۇشخەۋەر بېرىلىدۇ (يەنى دۇنيادا جان ئۈزۈش ۋاقتىدا، ئاللاھنىڭ رازىلىقى ۋە رەھمىتىگە ئېرىشىدىغانلىقى بىلەن، ئاخىرەتتە نازۇ-نېمەتلىك جەننەتكە كىرىدىغانلىقى بىلەن خۇش خەۋەر بېرىلىدۇ). ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ، ئەنە شۇ كاتتا بەختتۇر.');
INSERT INTO "saleh" VALUES(10,65,'10:65','(ئى مۇھەممەد!) ئۇلارنىڭ سۆزى سېنى قايغۇغا سالمىسۇن، پۈتۈن كۈچ ـ قۇۋۋەت ئاللاھقا مەنسۈپ (ئاللاھ ساڭا مەدەتكاردۇر). ئاللاھ (ئۇلارنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(10,66,'10:66',' راستلا ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، بەندىسىدۇر، مەخلۇقاتىدۇر). ئاللاھتىن باشقىغا بۇتلارغا چوقۇنىدىغانلار پەقەت يوقىلاڭ پەرەزگىلا تايىنىدۇ، ئۇلار پەقەت جۆيلۈيدۇ.');
INSERT INTO "saleh" VALUES(10,67,'10:67','ئى ئىنسانلار! ئارام ئېلىشىڭلار ئۈچۈن ئاللاھ سىلەرگە كېچىنى ياراتتى، نەرسىلەرنى كۆرۈشۈڭلار ئۈچۈن كۈندۈزنى ياراتتى، (ئىبرەت قۇلىقى بىلەن) ئاڭلايدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(10,68,'10:68',' ئۇلار (يەنى كاپىرلار): «ئاللاھنىڭ بالىسى بار» دېيىشتى، ئاللاھ (بۇنىڭدىن) پاكتۇر، بىھاجەتتۇر (يەنى بالا قىلىۋېلىش ئېھتىياج ئۈچۈن بولىدۇ، ئاللاھ بولسا ھېچ نەرسىگە موھتاج ئەمەستۇر)، ئاسمانلاردىكى، زېمىندىكى ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، (ئاللاھنىڭ بالىسى بار دېگەن) بۇ سۆزۈڭلارغا ھېچقانداق پاكىتىڭلار يوق، سىلەر بىلمەيدىغان ئىشلارنى يالغاندىن ئاللاھقا چاپلامسىلەر؟.');
INSERT INTO "saleh" VALUES(10,69,'10:69',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئاللاھقا يالغاننى چاپلايدىغانلار ھەقىقەتەن نىجاتلىققا ئېرىشەلمەيدۇ».');
INSERT INTO "saleh" VALUES(10,70,'10:70',' ئۇلار بۇ دۇنيادىن (ئازغىنا ۋاقىتلا) بەھرىمەن بولىدۇ، ئاندىن ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئاندىن كاپىر بولغانلىقلىرى سەۋەبىدىن ئۇلارغا قاتتىق ئازابنى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(10,71,'10:71','ئۇلارغا نۇھنىڭ قىسسەسىنى ئوقۇپ بەرگىن، ئۆز ۋاقتىدا نۇھ قەۋمىگە: «ئى قەۋمىم! ئەگەر سىلەرگە مېنىڭ ئىچىڭلاردا تۇرۇشۇم ۋە سىلەرگە ئاللاھنىڭ ئايەتلىرىنى ئەسلىتىشىم ئېغىر كەلگەن بولسا، مەن ئاللاھقا تەۋەككۈل قىلدىم (يەنى ئاللاھقا يۆلەندىم، سىلەرگە پەرۋا قىلمايمەن). سىلەر بۇتلىرىڭلار بىلەن بىرلىكتە (مېنى قەستلەيدىغان) ئىشىڭلارنى قارار قىلىۋېرىڭلار، ئىشىڭلار سىلەرگە غۇۋا بولۇپ قالمىسۇن (يەنى مېنى قەستلەيدىغان ئىشىڭلار ئاشكارا بولسۇن)، ئاندىن مېنى بىردەممۇ كېچىكتۈرمەي، مېنىڭ ھەققىمدە (خالىغىنىڭلارنى) ئىجرا قىلىۋېرىڭلار.');
INSERT INTO "saleh" VALUES(10,72,'10:72','ئەگەر سىلەر (مېنىڭ نەسىھىتىمدىن) يۈز ئۆرۈسەڭلار، سىلەردىن ھەق سورىغىنىم يوق، مەن ئەجرىمنى پەقەت ئاللاھتىن سورايمەن، مەن (ئاللاھقا) بويسۇنغۇچىلاردىن بولۇشقا بۇيرۇلدۇم» دېدى.');
INSERT INTO "saleh" VALUES(10,73,'10:73',' لېكىن ئۇلار نۇھنى يالغانچى قىلدى. ئۇنى ۋە ئۇنىڭ بىلەن كېمىدە بىللە بولغۇچى (مۆمىن) لەرنى قۇتقۇزدۇق، ئۇلارنى (يەنى بىز قۇتقۇزغان كىشىلەرنى) (غەرق بولغانلارنىڭ) ئورۇنباسارلىرى قىلدۇق، بىزنىڭ مۆجىزىلىرىمىزنى ئىنكار قىلغانلارنى (توپان بالاسى بىلەن) غەرق قىلدۇق. (ئى مۇھەممەد!) ئاگاھلاندۇرۇلغۇچىلار (يەنى پەيغەمبەرلىرىنى يالغانچى قىلغانلار) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(10,74,'10:74',' نۇھتىن كېيىن نۇرغۇن پەيغەمبەرلەرنى ئۆزلىرىنىڭ قەۋمىگە ئەۋەتتۇق، (قەۋمىگە) ئۇلار روشەن مۆجىزىلەر بىلەن كەلدى، ئۇلار (يەنى پەيغەمبەرلەر كېلىشتىن) ئىلگىرى ئىنكار قىلىشقان نەرسىگە ئىمان ئېيتىشمىدى. (كۇفرىدا) ھەددىدىن ئاشقۇچىلارنىڭ دىللىرىنى ئەنە شۇنداق پېچەتلەيمىز.');
INSERT INTO "saleh" VALUES(10,75,'10:75','. ئۇلاردىن كېيىن مۇسا بىلەن ھارۇننى پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا نۇرغۇن مۆجىزىلەر بىلەن ئەۋەتتۇق. ئۇلار (ئىمان ئېيتىشتىن) بويۇنتاۋلىق قىلدى. ئۇلار گۇناھكار قەۋم ئىدى.');
INSERT INTO "saleh" VALUES(10,76,'10:76','ئۇلارغا بىز تەرەپتىن ھەقىقەت كەلگەن چاغدا ئۇلار: «بۇ ھەقىقەتەن روشەن سېھىردۇر» دېدى. ');
INSERT INTO "saleh" VALUES(10,77,'10:77','مۇسا: «سىلەرگە ھەقىقەت كەلسە ئۇنى سېھىر دەمسىلەر؟ ئەجەبا بۇ سېھىرمۇ؟ سېھىرگەرلەر مۇۋەپپەقىيەت قازىنالمايدۇ» دېدى.');
INSERT INTO "saleh" VALUES(10,78,'10:78','ئۇلار: «سەن بىزگە ئاتا ـ بوۋىلىرىمىزنىڭ دىنىدىن بىزنى ۋاز كەچتۈرۈش ۋە زېمىن (يەنى مىسىر زېمىنى) نىڭ سەلتەنىتىنى ئىككىڭلارنىڭ قىلىۋېلىش ئۈچۈن كەلدىڭمۇ؟ بىز ئىككىڭلارغا ئىشەنمەيمىز» دېدى.');
INSERT INTO "saleh" VALUES(10,79,'10:79','پىرئەۋن: «مېنىڭ ئالدىمغا بارلىق ئۇستا سېھىرگەرلەرنى ئېلىپ كېلىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(10,80,'10:80','سېھىرگەرلەر يېتىپ كەلگەندە، مۇسا ئۇلارغا: «(ئارغامچا ۋە ھاسىلىرىڭلاردىن) تاشلىماقچى بولغىنىڭلارنى تاشلاڭلار» دېدى.');
INSERT INTO "saleh" VALUES(10,81,'10:81',' ئۇلار تاشلىغاندا، مۇسا: «سىلەرنىڭ كۆرسەتكىنىڭلار سېھىردۇر، ئاللاھ چوقۇم ئۇنى بەربات قىلىدۇ، ئاللاھ بۇزغۇنچىلارنىڭ ئىشىنى ئەلۋەتتە تۈزىمەيدۇ» دېدى.');
INSERT INTO "saleh" VALUES(10,82,'10:82',' گۇناھكارلار ياقتۇرمىغان تەقدىردىمۇ، ئاللاھ پاكىتلىرى ئارقىلىق ھەقىقەتنى ئىسپاتلايدۇ. ');
INSERT INTO "saleh" VALUES(10,83,'10:83','مۇساغا پەقەت ئۆز قەۋمىدىن (يەنى بەنى ئىسرائىلنىڭ ئەۋلادىدىن) ئازغىنىسى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىنىڭ زىيانكەشلىك قىلىشىدىن قورققان ھالدا ئىمان ئېيتتى. پىرئەۋن زېمىندا (يەنى مىسىر زېمىنىدا) شەك ـ شۈبھىسىز مۇتەكەببىردۇر، ئۇ ئەلۋەتتە ھەددىدىن ئاشقۇچىلاردىندۇر.');
INSERT INTO "saleh" VALUES(10,84,'10:84',' مۇسا: «ئى قەۋمىم! ئەگەر ئاللاھقا ئىمان ئېيتقان بولساڭلار، ئاللاھقا بويسۇنغان بولساڭلار، ئۇنىڭغا تەۋەككۈل قىلىڭلار (يەنى ھەممە ئىشىڭلارنى ئاللاھقا تاپشۇرۇڭلار، ھەرقانداق ئەھۋالدا ئاللاھقا يۆلىنىڭلار)» دېدى.');
INSERT INTO "saleh" VALUES(10,85,'10:85','ئۇلار ئېيتتى: «ئاللاھقا تەۋەككۈل قىلدۇق، پەرۋەردىگارىمىز، زالىم قەۋمنى بىزگە زىيانكەشلىك قىلىش ئىمكانىيىتىگە ئىگە قىلمىغىن.');
INSERT INTO "saleh" VALUES(10,86,'10:86','بىزنى رەھمىتىڭ بىلەن زالىم قەۋمدىن قۇتۇلدۇرغىن».');
INSERT INTO "saleh" VALUES(10,87,'10:87','بىز مۇسا بىلەن ئۇنىڭ قېرىندىشىغا: «قەۋمىڭلار ئۈچۈن مىسىردا ئۆيلەر ياساڭلار، ئۆيلىرىڭلارنى ناماز ئوقۇيدىغان جاي قىلىڭلار، ناماز ئوقۇڭلار، مۆمىنلەرگە (دۈشمەنلىرى ئۈستىدىن غەلىبە قىلىدىغانلىقى بىلەن) خۇش خەۋەر بېرىڭلار» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(10,88,'10:88',' مۇسا ئېيتتى: «ئى پەرۋەردىگارىمىز! پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا ھاياتىي دۇنيادا زىننەت بۇيۇملىرىنى، تۈرلۈك مال ـ مۈلۈكلەرنى بەردىڭ.ئى پەرۋەردىگارىمىز! ئۇلار بۇنىڭ بىلەن كىشىلەرنى ئازدۇردى. ئۇلارنىڭ مال ـ مۈلۈكلىرىنى يوق قىلغىن، ئۇلارنىڭ دىللىرىنى قاتتىق قىلغىن، ئۇلار قاتتىق ئازابنى كۆرمىگۈچە ئىمان ئېيتمايدۇ ».');
INSERT INTO "saleh" VALUES(10,89,'10:89','ئاللاھ ئېيتتى: «ئىككىڭلارنىڭ دۇئاسى ئىجابەت قىلىندى، دەۋىتىڭلارنى داۋاملاشتۇرۇڭلار، نادانلارنىڭ يولىغا ئەگەشمەڭلار».');
INSERT INTO "saleh" VALUES(10,90,'10:90',' بىز ئىسرائىل ئەۋلادىنى دېڭىزدىن ئۆتكۈزۈۋەتتۇق، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ ئەسكەرلىرى زۇلۇم ۋە زوراۋانلىق قىلىش يۈزىسىدىن قوغلىدى. پىرئەۋن غەرق بولىدىغان ۋاقىتتا «ئىمان ئېيتتىمكى، ئىسرائىل ئەۋلادى ئىمان ئېيتقان ئىلاھتىن غەيرىي ئىلاھ يوقتۇر، مەن مۇسۇلمانلاردىنمەن» دېدى.');
INSERT INTO "saleh" VALUES(10,91,'10:91',' (ئۇنىڭغا دېيىلدىكى) «(ھاياتتىن ئۈمىد ئۈزگىنىڭدە) ئەمدى (ئىمان ئېيتامسەن؟) ئىلگىرى ئاللاھقا ئاسىيلىق قىلغان ۋە بۇزغۇنچىلاردىن بولغان ئىدىڭ.');
INSERT INTO "saleh" VALUES(10,92,'10:92','سەندىن كېيىنكىلەرگە ئىبرەت بولۇشۇڭ ئۈچۈن، بۈگۈن سېنىڭ جەسىتىڭنى قۇتقۇزىمىز (يەنى سېنىڭ جەسىتىڭنى دېڭىزدىن چىقىرىپ قويىمىز)». نۇرغۇن كىشىلەر، شەك ـ شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزدىن غاپىلدۇر.');
INSERT INTO "saleh" VALUES(10,93,'10:93','شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى (ئۇلارنىڭ دۈشمەنلىرىنى ھالاك قىلغاندىن كېيىن) ياخشى جايغا ئورۇنلاشتۇردۇق، ئۇلارنى پاك نەرسىلەر بىلەن رىزىقلاندۇردۇق، ئۇلارغا ئىلىم (يەنى ئاللاھنىڭ ھۆكمىنى ئىچىگە ئالغان تەۋرات) كەلگەندىن كېيىن، ئۇلار (دىن ئىشىدا)ئىختىلاپ قىلىشتى. پەرۋەردىگارىڭ قىيامەت كۈنى، ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(10,94,'10:94','مۇبادا سەن ساڭا بىز نازىل قىلغان كىتابتىن شەكلىنىدىغان بولساڭ، سەندىن ئىلگىرى كىتاب ئوقۇغانلار (يەنى تەۋرات بىلەن ئىنجىل نازىل قىلىنغان يەھۇدىيلار ۋە ناسارالار) دىن سوراپ باققىن، شۈبھىسىزكى، ساڭا پەرۋەردىگارىڭ تەرىپىدىن ھەق (قۇرئان) كەلدى. سەن ھەرگىز شەك قىلغۇچىلاردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(10,95,'10:95','سەن ھەرگىز ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغۇچىلاردىن بولمىغىن، (ئۇنداق بولساڭ) زىيان تارتقۇچىلاردىن بولۇپ قالىسەن.');
INSERT INTO "saleh" VALUES(10,96,'10:96','شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ لەنىتىگە تېگىشلىك بولغانلار ئىمان ئېيتمايدۇ. ');
INSERT INTO "saleh" VALUES(10,97,'10:97','ئۇلارغا پۈتۈن مۆجىزىلەر كۆرسىتىلگەن تەقدىردىمۇ، قاتتىق ئازابنى كۆرمىگۈچە (ئىمان ئېيتمايدۇ).');
INSERT INTO "saleh" VALUES(10,98,'10:98','(ھالاك بولغان شەھەرلەر ئاھالىلىرى ئىچىدە) يۇنۇس قەۋمىدىن باشقىسىغا (ئازابنىڭ ئالامىتىنى كۆرۈپ) ئېيتقان ئىماننىڭ پايدىسى بولغىنى يوق، يۇنۇس قەۋمى ئىمان ئېيتقاندا، دۇنيا ھاياتىدا رەسۋا قىلىدىغان ئازابنى ئۇلارنىڭ ئۈستىدىن كۆتۈرۈۋەتتۇق، ئۇلارنى مەلۇم ۋاقىتقىچە (يەنى ئەجىلى يەتكەنگە قەدەر دۇنيادىكى شەيئىلەردىن) بەھرىمەن قىلدۇق.');
INSERT INTO "saleh" VALUES(10,99,'10:99','ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، ئەلۋەتتە يەر يۈزىدىكى كىشىلەرنىڭ ھەممىسى ئىمان ئېيتاتتى، سەن كىشىلەرنى مۇسۇلمان بولۇشقا مەجبۇرلامسەن؟. ');
INSERT INTO "saleh" VALUES(10,100,'10:100','ھەر قانداق كىشى پەقەت ئاللاھنىڭ ئىرادىسى بولغاندىلا، ئاندىن ئىمان ئېيتىدۇ، (ئاللاھنىڭ ئايەتلىرى ئۈستىدە) پىكىر يۈرگۈزمەيدىغانلارنى ئاللاھ ئازابقا گىرىپتار قىلىدۇ.');
INSERT INTO "saleh" VALUES(10,101,'10:101',' ئېيتقىنكى، «ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى كۆزىتىڭلار. مۆجىزىلەر ۋە ئاگاھلاندۇرغۇچىلارنىڭ (يەنى پەيغەمبەرلەرنىڭ) ئىمان ئېيتمايدىغان قەۋمگە پايدىسى بولمايدۇ.');
INSERT INTO "saleh" VALUES(10,102,'10:102','ئۇلار پەقەت ئۆزلىرىدىن بۇرۇن ئۆتكەنلەرگە كەلگەن كۈنلەرنىڭ (يەنى ئازابنىڭ) (ئۆزلىرىگە كېلىشىنى) كۈتىدۇ». (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەر (زۇلۇمنىڭ ئاقىۋىتىنى) كۈتۈڭلار، مەنمۇ سىلەر بىلەن بىرلىكتە ھەقىقەتەن (سىلەرنىڭ ھالاك بولۇشۇڭلارنى) كۈتكۈچىمەن».');
INSERT INTO "saleh" VALUES(10,103,'10:103','ئاندىن كېيىن پەيغەمبەرلىرىمىزنى، مۆمىنلەرنى (ئازابتىن) قۇتقۇزدۇق، مۆمىنلەرنى قۇتقۇزۇشقا مۇشۇنداق مەسئۇل بولىمىز.');
INSERT INTO "saleh" VALUES(10,104,'10:104','(ئى مۇھەممەد! قەۋمىڭنىڭ مۇشرىكلىرىغا) ئېيتقىنكى، «ئى ئىنسانلار! ئەگەر سىلەر مېنىڭ دىنىمدىن گۇمانلىنىدىغان بولساڭلار، سىلەرنىڭ ئاللاھنى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارغا مەن چوقۇنمايمەن، لېكىن مەن سىلەرنى قەبزى روھ قىلىدىغان ئاللاھقا ئىبادەت قىلىمەن، مەن مۆمىنلەردىن بولۇشقا بۇيرۇلدۇم».');
INSERT INTO "saleh" VALUES(10,105,'10:105',' يەنە (ماڭا): «باتىل دىنلاردىن بۇرۇلۇپ، توغرا دىنغا يۈزلەنگىن، مۇشرىكلاردىن بولمىغىن.');
INSERT INTO "saleh" VALUES(10,106,'10:106','ئاللاھنى قويۇپ، ساڭا پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە ئىبادەت قىلمىغىن، ئەگەر ئۇلارغا ئىبادەت قىلساڭ، چوقۇم (ئۆزۈڭگە) زۇلۇم سالغۇچىلاردىن بولىسەن.');
INSERT INTO "saleh" VALUES(10,107,'10:107',' ئەگەر ئاللاھ ساڭا بىرەر زىيان ـ زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، يالغۇز ئاللاھتىن باشقا ئۇنى دەپئى قىلغۇچى بولمايدۇ، ئەگەر ئاللاھ ساڭا بىرەر ياخشىلىقنى ئىرادە قىلسا، ئاللاھنىڭ پەزلىنى قايتۇرغۇچى بولمايدۇ، ئاللاھ پەزلىنى بەندىلىرىدىن خالىغان ئادەمگە يەتكۈزىدۇ، ئاللاھ (بەندىلىرىنىڭ گۇناھىنى) مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) مېھرىباندۇر» (دېيىلدى).');
INSERT INTO "saleh" VALUES(10,108,'10:108',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە پەرۋەردىگارىڭلار تەرىپىدىن ھەق دىن كەلدى، كىمكى ھىدايەت تاپىدىكەن، ئۆزىنىڭ پايدىسى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، مەن سىلەرگە ھامىي ئەمەسمەن».');
INSERT INTO "saleh" VALUES(10,109,'10:109','(ئى مۇھەممەد!) ساڭا نازىل قىلىنغان ۋەھىيگە ئەگەشكىن (يەنى ئۇنىڭغا ئۆزۈڭ ئەمەل قىلىپ، باشقىلارغا ئۇنى يەتكۈزگىن). ئاللاھ (سەن بىلەن ئۇلارنىڭ ئارىسىدا) ھۆكۈم چىقارغىچە (دىنغا دەۋەت قىلىش يولىدىكى مۇشەققەتلەرگە) چىدىغىن، ئاللاھ ئەڭ ھەققانىي ھۆكۈم چىقارغۇچىدۇر.');
INSERT INTO "saleh" VALUES(11,1,'11:1','ئەلىف، لام، را. بۇ، ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىدىن خەۋەردار ئاللاھ تەرىپىدىن نازىل بولغان، ئايەتلەرنىڭ تۈزۈلۈشى پۇختا، (ئەقىدە، ئەھكام، ۋەز ۋە قىسسەلەر) تەپسىلىي بايان قىلىنغان كىتابتۇر. ');
INSERT INTO "saleh" VALUES(11,2,'11:2','(ئېيتقىنكى، بۇ كىتاب): سىلەرنىڭ ئاللاھتىن باشقىسىغا ئىبادەت قىلماسلىقىڭلار ئۈچۈن (نازىل بولغان). شۈبھىسىزكى، مەن سىلەرگە ئاللاھ تەرىپىدىن ئاگاھلاندۇرغۇچى ۋە خۇش خەۋەر بەرگۈچىمەن.');
INSERT INTO "saleh" VALUES(11,3,'11:3','سىلەر پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار. ئاللاھ سىلەرنى مۇئەييەن مۇددەتكىچە (يەنى ئۆمرۈڭلار ئاخىرلىشىپ، ئەجىلىڭلار يەتكۈچە ھاياتىي دۇنيادىن) ئوبدان بەھرىمەن قىلىدۇ. ياخشى ئىش قىلغۇچىغا قىلغان ياخشىلىقنىڭ ساۋابىنى بېرىدۇ، ئەگەر (ئىماندىن) يۈز ئۆرۈسەڭلار، سىلەرنىڭ قىيامەت كۈنىنىڭ ئازابىغا قېلىشىڭلاردىن ئەنسىرەيمەن.');
INSERT INTO "saleh" VALUES(11,4,'11:4','(ئۆلگەندىن كېيىن) ئاللاھنىڭ دەرگاھىغا قايتىسىلەر، ئاللاھ ھەر نەرسىگە قادىردۇر».');
INSERT INTO "saleh" VALUES(11,5,'11:5','راستتىنلا ئۇلار(يەنى كاپىرلار) (رەسۇلۇللاھ قۇرئان ئوقۇۋاتقاندا ئۇنىڭ يېنىدىن ئۆتكەنلىرىدە) ئۇنىڭدىن يوشۇرۇنۇش ئۈچۈن يۈز ئۆرۈيدۇ، راستلا ئۇلار كىيىملىرى بىلەن چۈمكىنىۋالغانلىرىدا، ئاللاھ ئۇلارنىڭ يوشۇرۇن ۋە ئاشكارا ئىشلىرىنى بىلىپ تۇرىدۇ، ئاللاھ ھەقىقەتەن دىللاردىكىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(11,6,'11:6','يەر يۈزىدىكى ھايۋانلارنىڭ ھەممىسىگە رىزىق بېرىشنى ئاللاھ (مەرھەمەت يۈزىسىدىن) ئۈستىگە ئالغان، ئاللاھ ئۇلارنىڭ تۇرار جايىنى ۋە (ئۆلگەندىن كېيىن) كۆمۈلىدىغان جايىنى بىلىدۇ، ئۇلارنىڭ ھەممىسى روشەن كىتابقا(يەنى لەۋھۇلمەھپۇزغا) يېزىلغاندۇر.');
INSERT INTO "saleh" VALUES(11,7,'11:7','ئاللاھ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە ياراتتى. (ئاسمان ـ زېمىننى يارىتىشتىن ئىلگىرى) ئاللاھنىڭ ئەرشى سۇ ئۈستىدە ئىدى. سىلەرنىڭ قايسىڭلارنىڭ ئەمەلى ياخشى ئىكەنلىكىنى سىناش ئۈچۈن ئاللاھ (ئۇلارنى ياراتتى). (ئى مۇھەممەد!) ئەگەر سەن(مەككە كۇففارلىرىغا): «سىلەر ئۆلگەندىن كېيىن چوقۇم تىرىلىسىلەر!» دېسەڭ، كاپىرلار چوقۇم ئېيتىدۇ: «بۇپەقەت روشەن سېھىردۇر».');
INSERT INTO "saleh" VALUES(11,8,'11:8','ئەگەر بىز ئۇلارغا نازىل بولىدىغان ئازابنى چەكلىك ۋاقىت كېچىكتۈرسەك ، ئۇلار چوقۇم(مەسخىرە قىلىش يۈزىسىدىن): «ئازاب نېمىشقا چۈشمەيدۇ؟» دەيدۇ. راستلا ئۇلارغا ئاللاھنىڭ ئازابى چۈشكەن كۈندە، ئۇلار قاچىدىغان يەر تاپالمايدۇ. ئۇلار مەسخىرە قىلغان ئازاب ئۇلارنى قورشىۋالىدۇ.');
INSERT INTO "saleh" VALUES(11,9,'11:9',' ئەگەر بىز ئىنسانغا رەھمىتىمىزنى تېتىتساق (يەنى نېمىتىمىزنى بەرسەك)، ئاندىن ئۇنى ئۇنىڭدىن تارتىۋالساق، ئۇ چوقۇم (ئاللاھنىڭ رەھمىتىدىن) ئۈمىدسىزلىنىپ كېتىدۇ، كۇفرانى نېمەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(11,10,'11:10',' ئەگەر بىز (پېقىرلىق ، كېسەللىك، قاتتىقچىلىق قاتارلىق) بالالارغا مۇپتىلا قىلغاندىن كېيىن، ئۇنىڭغا نېمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «مەندىن بالالار كۆتۈرۈلۈپ كەتتى (ئەمدى كەلمەيدۇ)» دەپ شەك -شۈبھىسىز خۇشاللىنىپ، مەغرۇرلىنىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(11,11,'11:11',' پەقەت (بالاغا ئۇچرىغاندا) سەۋرى قىلغان، (نېمەتكە ئېرىشكەندە) ياخشى ئىشلارنى قىلغان كىشىلەر(ياخشىلاردۇر). ئۇلارنىڭ گۇناھى مەغپىرەت قىلىنىدۇ، ئۇلار(ئاخىرەتتە) چوڭ مۇكاپاتقا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(11,12,'11:12','(ئى مۇھەممەد!) ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) «نېمىشقا ئۇنىڭغا بىر خەزىنە چۈشمىدى ياكى ئۇنىڭ بىلەن بىللە (ئۇنى تەستىقلايدىغان) بىرەر پەرىشتە كەلمىدى؟» دەيدىغانلىقى ئۈچۈن، ساڭا نازىل قىلىنغان ۋەھىينىڭ بىر قىسىمىنى يەتكۈزگۈڭ كەلمەيدىغاندۇ، يەتكۈزۈشتىن يۈرىكىڭ سىقىلىدىغاندۇ، سەن پەقەت (ئۇلارنى ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇرغۇچىسەن، ئاللاھ ھەممە نەرسىنى تەسەررۇپ قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(11,13,'11:13','ئۇلار قۇرئاننى(مۇھەممەد) ئۆزى ئويدۇرۇپ چىققان دېيىشەمدۇ؟ ئېيتقىنكى، «(بۇ سۆزۈڭلاردا) راستچىل بولساڭلار، ئاللاھتىن غەيرىي چاقىرالايدىغانلىكى كىشىلىرىڭلارنى (ياردەمگە) چاقىرىپ، (پاساھەت ۋە بالاغەت جەھەتتىن) قۇرئاندىكىگە ئوخشايدىغان ئون سۈرە ئىجاد قىلىپ بېقىڭلار».');
INSERT INTO "saleh" VALUES(11,14,'11:14','ئەگەر ئۇلار(بۇ چاقىرىقىڭلارغا) ئاۋاز قوشمىسا، بىلىڭلاركى، قۇرئان ئاللاھنىڭ ۋەھىي قىلىشى بىلەن نازىل قىلىنغان. ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر، سىلەر ئەمدىغۇ (ئاللاھقا) بويسۇنارسىلەر! . ');
INSERT INTO "saleh" VALUES(11,15,'11:15',' (قىلغان ياخشى ئەمەللىرى بىلەن پەقەت) ھاياتىي دۇنيانى ۋە ئۇنىڭ زىبۇ - زىننىتىنى(يەنى دۇنيانىڭ نېمەتلىرىنى) كۆزلەيدىغان كىشىلەرگە ئۇلارنىڭ ئەمەللىرىنىڭ ئەجرىنى بۇ دۇنيادىلا تولۇق بېرىمىز، دۇنيادا ئۇلارنىڭ ئەجرىدىن ھېچ نەرسە كېمەيتىلمەيدۇ.');
INSERT INTO "saleh" VALUES(11,16,'11:16','بۇنداق كىشىلەرگە ئاخىرەتتە دوزاختىن باشقا نەرسە يوقتۇر، ئۇلارنىڭ قىلغان ئىشلىرى يوققا چىقىرىلىدۇ، قىلغان(ياخشى) ئەمەللىرى ھېسابقا ئېلىنمايدۇ.');
INSERT INTO "saleh" VALUES(11,17,'11:17','پەرۋەردىگارى تەرىپىدىن كەلگەن روشەن دەلىل (يەنى قۇرئان) غا ئاساسلانغان ئادەم (يەنى مۇھەممەد ئەلەيھىسسالام) (ھاياتىي دۇنيانى كۆزلەيدىغان كىشىلەر بىلەن ئوخشاشمۇ؟). ئۇنىڭغا ئاللاھ تەرىپىدىن كەلگەن گۇۋاھلىق بەرگۈچى (يەنى جىبرىئىل ئەلەيھىسسالام) قۇرئاننى تىلاۋەت قىلىدۇ، قۇرئاندىن ئىلگىرى مۇساغا نازىل قىلىنغان كىتاب (يەنى تەۋرات) (ئىسرائىل ئەۋلادىغا) يېتەكچى ۋە رەھمەت قىلىپ(نازىل قىلىندى)، ئەنە شۇنداق كىشىلەر قۇرئانغا (ھەقىقىي) ئىشىنىدۇ، (ھەرقانداق) جامائەدىن كىمكى قۇرئاننى ئىنكار قىلىدىكەن، ئۇنىڭغا ۋەدە قىلىنغان جاي دوزاختۇر. قۇرئانغا شەك كەلتۈرمىگىن، ئۇ ھەقىقەتەن پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان ھەقىقەتتۇر، لېكىن ئىنسانلارنىڭ تولىسى ئىشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(11,18,'11:18','ئاللاھقا يالغاننى چاپلىغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ ئەنە شۇلار(قىيامەت كۈنى) پەرۋەردىگارىغا توغرىلىنىدۇ. گۇۋاھچىلار: «بۇلار پەرۋەردىگارى ھەققىدە يالغان سۆزلىگەنلەردۇر» دەيدۇ. راستلا ئاللاھنىڭ لەنىتى زالىملارغا بولىدۇ (يەنى ئاللاھ زالىملارنى رەھمىتىدىن يىراق قىلىدۇ).');
INSERT INTO "saleh" VALUES(11,19,'11:19','ئۇلار (كىشىلەرنى) ئاللاھنىڭ يولىدىن (يەنى ئىسلام دىنىدىن) توسىدۇ، ئاللاھنىڭ يولىنىڭ ئەگرى بولۇشىنى تىلەيدۇ، ئۇلار ئاخىرەتنى ئىنكار قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(11,20,'11:20','ئەنە شۇلار زېمىندا ئاللاھنىڭ ئازابىدىن قېچىپ قۇتۇلالمايدۇ، ئۇلارغا ئاللاھتىن باشقا (ئىگە بولىدىغان) ياردەمچىلەرمۇ بولمايدۇ، ئۇلار قاتمۇ-قات ئازابقا دۇچار بولىدۇ، ئۇلار (ھەقنى) ئاڭلاشقا ۋە (ھەقنى) كۆرۈشكە قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(11,21,'11:21','ئەنە شۇلار ئۆزلىرىگە زىيان سالغۇچىلاردۇر. ئۇلارنىڭ ئويدۇرۇپ چىقارغان مەبۇدلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالدى. ');
INSERT INTO "saleh" VALUES(11,22,'11:22',' شەك يوقكى، ئۇلار ئاخىرەتتە ئەڭ زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(11,23,'11:23','شۈبھىسىزكى ، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ھۇزۇرىدا ئاجىزلىق بىلدۈرگەنلەر ـ ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(11,24,'11:24','(كاپىرلار ۋە مۆمىنلەردىن ئىبارەت) ئىككى پىرقە بار، (بىر پىرقە) ئەماغا ۋە گاسقا ئوخشايدۇ، (يەنە بىر پىرقە بولسا) كۆزى كۆرۈدىغان ، قۇلىقى ئاڭلايدىغان ئادەمگە ئوخشايدۇ، ئۇلارنىڭ ئەھۋالى بىر ـ بىرىگە ئوخشامدۇ؟ ئىبرەت ئالمامسىلەر؟. ');
INSERT INTO "saleh" VALUES(11,25,'11:25',' شۈبھىسىزكى، بىز نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇ: «مەن ھەقىقەتەن سىلەرگە ئوچۇق ئاگاھلاندۇرغۇچىمەن.');
INSERT INTO "saleh" VALUES(11,26,'11:26','بىر ئاللاھتىن باشقىغا ئىبادەت قىلماڭلار، سىلەرنىڭ قاتتىق كۈننىڭ ئازابىغا ئۇچرىشىڭلاردىن قورقىمەن» دېدى.');
INSERT INTO "saleh" VALUES(11,27,'11:27','ئۇنىڭ قەۋمىنىڭ كاپىر كاتتىلىرى: «بىزنىڭچە، سەن پەقەت بىزگە ئوخشاش بىر ئادەمسەن ، بىزنىڭچە، ئارىمىزدىكى پەس كىشىلەر يەڭگىللىك بىلەن ساڭا ئەگەشكەن، بىزنىڭچە، سىلەرنىڭ بىزدىن ھېچقانداق ئارتۇقچىلىقىڭلار يوق، بەلكى بىز سىلەرنى يالغانچى دەپ ئويلايمىز» دېدى.');
INSERT INTO "saleh" VALUES(11,28,'11:28','نۇھ ئېيتتى: «ئى قەۋمىم، ئېيتىڭلارچۇ، ئەگەر مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان (دەۋىتىمنىڭ توغرىلىقىنى ئىسپاتلايدىغان) ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتىگە (يەنى پەيغەمبەرلىككە) سازاۋەر بولغان بولسام، ئۇ دەلىل سىلەر ئۈچۈن مەخپىي بولسا، سىلەر ئۇنى يامان كۆرسەڭلار، ئۇنى قوبۇل قىلىشقا سىلەرنى مەجبۇرلامدۇق؟ ');
INSERT INTO "saleh" VALUES(11,29,'11:29',' ئى قەۋمىم! سىلەرگە (دىننى) تەبلىغ قىلغانلىقىم ئۈچۈن سىلەردىن پۇل ـ مال تەلەپ قىلمايمەن، ماڭا ئەجىر بېرىشنى پەقەت ئاللاھ ئۆز ئۈستىگە ئالغان، مەن ئىمان ئېيتقان كىشىلەرنى (يېنىمدىن) قوغلىۋەتمەيمەن. (چۈنكى) ئۇلار، شۈبھىسىزكى، پەرۋەردىگارىغا مۇلاقات بولىدۇ، لېكىن مەن سىلەرنى نادان قەۋم كۆرۈمەن.');
INSERT INTO "saleh" VALUES(11,30,'11:30','ئى قەۋمىم! ئەگەر مەن ئۇلارنى قوغلىۋەتسەم، ئاللاھنىڭ ئازابىدىن مېنى كىم قۇتقۇزالايدۇ؟ ئويلىنىپ باقمامسىلەر؟.');
INSERT INTO "saleh" VALUES(11,31,'11:31','مەن سىلەرگە، مەندە ئاللاھنىڭ خەزىنىلىرى بار، دېمەيمەن، غەيبنى بىلىمەن، دېمەيمەن، مەن ئەلۋەتتە (ئۆزۈمنى) پەرىشتە دەپمۇ ئېيتمايمەن ھەمدە سىلەر كۆزگە ئىلمايدىغان كىشىلەرنى ئاللاھ ئۇلارغا ياخشىلىق بەرمەيدۇ دېمەيمەن. ئاللاھ ئۇلارنىڭ دىللىرىدىكىنى ئوبدان بىلىدۇ، بولمىسا مەن چوقۇم زالىملاردىن بولۇپ قالىمەن».');
INSERT INTO "saleh" VALUES(11,32,'11:32','ئۇلار(يەنى نۇھنىڭ قەۋمى): «ئى نۇھ! بىز بىلەن مۇنازىرلەشتىڭ، ناھايىتى كۆپ مۇنازىرلەشتىڭ. ئەگەر راست سۆزلىگۈچىلەردىن بولساڭ، بىزنى قورقۇتقان ئازابنى چۈشۈرۈپ باققىن» دېدى.');
INSERT INTO "saleh" VALUES(11,33,'11:33','نۇھ ئېيتتى: «ئۇنى (يەنى ئازابنى) ئەگەر خالىسا پەقەت ئاللاھلا چۈشۈرەلەيدۇ، سىلەر ھەرگىز قېچىپ قۇتۇلالمايسىلەر.');
INSERT INTO "saleh" VALUES(11,34,'11:34','مەن سىلەرگە نەسىھەت قىلماقچى بولسام، ئاللاھ سىلەرنى گۇمراھ قىلماقچى بولسا، ئۇ چاغدا مېنىڭ سىلەرگە قىلغان نەسىھىتىمنىڭ سىلەرگە پايدىسى بولمايدۇ، ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، سىلەر ئاللاھنىڭ دەرگاھىغا قايتىسىلەر».');
INSERT INTO "saleh" VALUES(11,35,'11:35','ياكى ئۇلار (يەنى قۇرەيشنىڭ كاپىرلىرى) قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى ئويدۇرۇپ چىققان دېيىشەمدۇ؟ «ئەگەر ئۇنى مەن ئويدۇرۇپ چىققان بولسام، ئۆز گۇناھىمغا ئۆزۈم مەسئۇل، مەن سىلەرنىڭ گۇناھىڭلاردىن ئادا ـ جۇدامەن» دېگىن.');
INSERT INTO "saleh" VALUES(11,36,'11:36','نۇھقا (مۇنداق) ۋەھىي قىلىندى: «قەۋمىڭدىن ئىلگىرى ساڭا ئىمان ئېيتقانلاردىن باشقا يەنە ئىمان ئېيتقۇچىلار بولمايدۇ، ئۇلارنىڭ قىلمىشىدىن قايغۇرمىغىن.');
INSERT INTO "saleh" VALUES(11,37,'11:37','بىزنىڭ كۆز ئالدىمىزدا ۋەھىيمىز بويىچە كېمە ياسىغىن، (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن ، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ».');
INSERT INTO "saleh" VALUES(11,38,'11:38','نۇھ كېمە ياساۋاتاتتى، قەۋمىنىڭ چوڭلىرى ئۇنىڭ ئالدىدىن ھەر قېتىم ئۆتكەندە ئۇنى مەسخىرە قىلىشتى. نۇھ ئېيتتى: « سىلەر (بۈگۈن) بىزنى مەسخىرە قىلساڭلار (كېلەچەكتە سىلەر غەرق قىلىنغاندا)، سىلەر بىزنى مەسخىرە قىلغاندەك بىزمۇ سىلەرنى چوقۇم مەسخىرە قىلىمىز.');
INSERT INTO "saleh" VALUES(11,39,'11:39','خار قىلغۇچى ئازاب (يەنى توپان بالاسى بىلەن غەرق بولۇش) نىڭ كىمنىڭ ئۈستىگە كېلىدىغانلىقىنى، دائىمىي ئازاب(يەنى جەھەننەم ئازابى) نىڭ كىمگە چۈشىدىغانلىقىنى كېلەچەكتە بىلىسىلەر». ');
INSERT INTO "saleh" VALUES(11,40,'11:40','(توپان بالاسى توغرىسىدىكى) بۇيرۇقىمىز يېتىپ كەلگەن ۋە يەر يۈزىدىن سۇ ئېتىلىپ چىققان چاغدا: «كېمىگە ھايۋاناتلاردىن (ئەركەك، چىشى بولۇپ) بىر جۇپتىن ئېلىۋالغىن، ­­_ ئائىلەڭدىكىلەر بىلەن ھالاك بولۇشى ھۆكۈم قىلىنغانلار (يەنى نۇھ ئەلەيھىسسالامنىڭ ئايالى ۋائىلە بىلەن ئوغلى كەنئان) بۇنىڭ سىرتىدا _ ۋە ئىمان ئېيتقانلار بىلەن كېمىگە چىققىن» دېدۇق، ئۇنىڭ بىلەن پەقەت ئازغىنا كىشىلەر ئىمان ئېيتتى.');
INSERT INTO "saleh" VALUES(11,41,'11:41','نۇھ (ئىمان ئېيتقان تەۋەلىرىگە) ئېيتتى: «كېمىگە چىقىڭلار، كېمىنىڭ مېڭىشى ۋە توختىشى ئاللاھنىڭ ئىسمى بىلەندۇر، شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارنىڭ گۇناھىنى) ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(11,42,'11:42',' كېمە ئۇلارنى ئېلىپ تاغدەك دولقۇنلار ئىچىدە ئۈزۈپ باراتتى، نۇھ كېمە مېڭىشتىن بۇرۇن ئۆزىدىن يىراقتا تۇرغان ئوغلىنى: «ئى ئوغلۇم، بىز بىلەن بىللە كېمىگە چىققىن، كاپىرلار بىلەن بىللە بولمىغىن» دەپ توۋلىدى.');
INSERT INTO "saleh" VALUES(11,43,'11:43','ئۇ: «مېنى سۇدا غەرق بولۇشتىن ساقلاپ قالىدىغان بىر تاغنىڭ ئۈستىگە چىقىۋالىمەن» دېدى. نۇھ: «بۈگۈن ئاللاھ رەھىم قىلغان ئادەمدىن باشقا ھېچ ئادەم ئۇنىڭ جازالىشىدىن قۇتۇلۇپ قالالمايدۇ» دېدى. ئۇلارنىڭ ئارىسىنى دولقۇن ئايرىۋەتتى ـ دە، ئۇ غەرق بولۇپ كەتتى. ');
INSERT INTO "saleh" VALUES(11,44,'11:44',' «ئى زېمىن! سۈيۈڭنى يۇتقىن، ئى بۇلۇت، تارقىغىن» دېيىلدى ، سۇ سوغۇلدى، ئاللاھنىڭ (كاپىرلارنى غەرق قىلىشتىن ئىبارەت) ھۆكمى ئىجرا قىلىندى، كېمە جۇدى تېغىنىڭ ئۈستىدە توختىدى، «زالىم قەۋم ھالاك بولسۇن» دېيىلدى.');
INSERT INTO "saleh" VALUES(11,45,'11:45','نۇھ پەرۋەردىگارىغا دۇئا قىلىپ: «پەرۋەردىگارىم! ئوغلۇم مېنىڭ ئائىلەمدىكىلەردىن ئىدى (سەن ماڭا ئۇلارنىڭ نىجات تېپىشىنى ۋەدە قىلغان ئىدىڭ)، سېنىڭ ۋەدەڭ ئەلۋەتتە ھەقتۇر، سەن ھەقىقەتەن ئەڭ توغرا ھۆكۈم قىلغۇچىسەن» دېدى.');
INSERT INTO "saleh" VALUES(11,46,'11:46','ئاللاھ ئېيتتى: «ئى نۇھ! ئۇ(مەن نىجات تېپىشىنى ۋەدە قىلغان) ئائىلەڭدىكىلەردىن ئەمەس، ئۇنىڭ ئەمەلى ياماندۇر، سەن ئېنىق بىلمىگەن نەرسەڭنى مەندىن سورىمىغىن، مەن سېنىڭ جاھىللاردىن بولماسلىقىڭنى نەسىھەت قىلىمەن».');
INSERT INTO "saleh" VALUES(11,47,'11:47','نۇھ ئېيتتى: «پەرۋەردىگارىم! مەن بىلمىگەن نەرسىنى سوراشتىن سېنىڭ پاناھىڭغا سىغىنىمەن، ئەگەر ماڭا مەغپىرەت قىلمىساڭ ۋە رەھىم قىلمىساڭ زىيان تارتقۇچىلاردىن بولىمەن».');
INSERT INTO "saleh" VALUES(11,48,'11:48','ئېيتىلدى (يەنى ئاللاھ ئېيتتى): «ئى نۇھ! ساڭا، سەن بىلەن (كېمىدە) بىللە بولغانلارنىڭ بىر قىسىم ئەۋلادىغا بىز تەرەپتىن نازىل بولغان ئامانلىق ۋە بەرىكەتلەر بىلەن (كېمىدىن) چۈشكىن، ئۇلارنىڭ يەنە بىر قىسىم ئەۋلادىنى (ھاياتىي دۇنيادىن) بەھرىمەن قىلىمىز، ئاندىن ئۇلار بىزنىڭ قاتتىق ئازابىمىزغا دۇچار بولىدۇ».');
INSERT INTO "saleh" VALUES(11,49,'11:49',' (ئى مۇھەممەد!) ئەنە شۇ (قىسسە) غەيبكە ئائىت خەۋەرلەردىندۇر، ساڭا ئۇنى ۋەھىي قىلدۇق، سەن ۋە سېنىڭ قەۋمىڭ بۇنىڭدىن ئىلگىرى ئۇنى بىلمەيتتىڭلار، سەۋر قىلغىن، شۈبھىسىزكى، ياخشى ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر.');
INSERT INTO "saleh" VALUES(11,50,'11:50','ئاد قەۋمىگە ئۇلارنىڭ قېرىندىشى ھۇدنى ئەۋەتتۇق، ھۇد ئېيتتى: «ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا مەبۇد (بەرھەق) يوقتۇر، سىلەر(ئاللاھتىن غەيرىيگە چوقۇنۇش بىلەن) پەقەت (ئاللاھقا) يالغاننى توقۇغۇچىلارسىلەر». ');
INSERT INTO "saleh" VALUES(11,51,'11:51','«ئى قەۋمىم! مەن تەبلىغ ئۈچۈن سىلەردىن ئەجىر سورىمايمەن، ماڭا ئەجىر بېرىشنى پەقەت مېنى ياراتقان ئاللاھ ئۈستىگە ئالغان، سىلەر چۈشەنمەمسىلەر؟»');
INSERT INTO "saleh" VALUES(11,52,'11:52',' «ئى قەۋمىم! پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، سىلەرگە ئۇ كۆپ يېغىن ياغدۇرۇپ بېرىدۇ، سىلەرگە تېخىمۇ كۈچ ـ قۇۋۋەت بېرىدۇ، سىلەر گۇناھقا چۆمگەن ھالدا (مېنىڭ دەۋىتىمدىن) يۈز ئۆرۈمەڭلار».');
INSERT INTO "saleh" VALUES(11,53,'11:53',' ئۇلار ئېيتتى: «ئى ھۇد! سەن بىزگە (ئۆزۈڭنىڭ راستلىقىڭنى كۆرسىتىدىغان) ھېچقانداق روشەن دەلىل كەلتۈرمىدىڭ، سېنىڭ سۆزۈڭ بىلەن بىز ئىلاھلىرىمىزنى تاشلىۋەتمەيمىز ۋە سېنىڭ(پەيغەمبەرلىكىڭگە) ئىشەنمەيمىز.');
INSERT INTO "saleh" VALUES(11,54,'11:54','بىز پەقەت سېنى بەزى ئىلاھلىرىمىز ساراڭ قىلىپ قويۇپتۇ دەيمىز» ھۇد ئېيتتى: «مەن ھەقىقەتەن ئاللاھنى گۇۋاھ قىلىمەن، سىلەرمۇ گۇۋاھ بولۇڭلاركى ، مەن سىلەرنىڭ ئاللاھنى قويۇپ شېرىك كەلتۈرگەن بۇتلىرىڭلاردىن ئادا ـ جۇدامەن،[54 ـ ].');
INSERT INTO "saleh" VALUES(11,55,'11:55','ھەممىڭلار ماڭا سۇيىقەست قىلىڭلار، ماڭا مۆھلەت بەرمەڭلار[ 55].');
INSERT INTO "saleh" VALUES(11,56,'11:56','شۈبھىسىزكى، مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار بولغان ئاللاھقا مەن تەۋەككۈل قىلدىم، ئاللاھنىڭ باشقۇرۇشىدا بولمىغان بىرمۇ مەخلۇق يوقتۇر؛ پەرۋەردىگارىم ھەقىقەتەن توغرا يولدىدۇر (يەنى ئادىل بولۇپ، ھېچ كىشىگە زۇلۇم قىلمايدۇ).');
INSERT INTO "saleh" VALUES(11,57,'11:57','ئەگەر سىلەر (مېنىڭ دەۋىتىمنى قوبۇل قىلىشتىن) يۈز ئۆرۈسەڭلار(يۈز ئۆرۈگىنىڭلارنىڭ ماڭا زىيىنى يوق)، سىلەرگە مەن پەرۋەردىگارىمنىڭ ئەلچىلىكىنى يەتكۈزدۈم (پەيغەمبەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىشتۇر)، پەرۋەردىگارىم (سىلەرنى ھالاك قىلىپ) ئورنۇڭلارغا باشقا بىر قەۋمنى كەلتۈرىدۇ، (شېرىك كەلتۈرۈش بىلەن) ئاللاھقا قىلچە زىيان يەتكۈزەلمەيسىلەر، پەرۋەردىگارىم ھەقىقەتەن ھەربىر نەرسىنى كۈزىتىپ تۇرغۇچىدۇر».');
INSERT INTO "saleh" VALUES(11,58,'11:58','بىزنىڭ (ئازابلاش) بۇيرۇقىمىز كەلگەندە، ھۇدنى ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى مەرھەمەت قىلىپ قۇتقۇزدۇق، ئۇلارنى قاتتىق ئازابتىن خالاس قىلدۇق.');
INSERT INTO "saleh" VALUES(11,59,'11:59','ئەنە شۇ ئاد قەۋمى پەرۋەردىگارىنىڭ مۆجىزىلىرىنى ئىنكار قىلدى، ئۇنىڭ پەيغەمبەرلىرىگە ئاسىيلىق قىلدى، ھەربىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى ھەققە قارشىلىق قىلىپ جېدەللەشكۈچى) نىڭ بۇيرۇقىغا ئەگەشتى. ');
INSERT INTO "saleh" VALUES(11,60,'11:60','ئۇلارغا بۇ دۇنيادا لەنەت قىلىندى، قىيامەت كۈنىمۇ ئۇلارغا لەنەت قىلىنىدۇ، بىلىڭلاركى، ئاد قەۋمى ھەقىقەتەن پەرۋەردىگارىنى ئىنكار قىلدى. ئاگاھ بولۇڭلاركى ، ھۇد قەۋمى بولغان ئاد ھالاك بولسۇن.');
INSERT INTO "saleh" VALUES(11,61,'11:61','سەمۇدقا ئۇلارنىڭ قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ سىلەرنى زېمىندىن (يەنى تۇپراقتىن) ياراتتى، سىلەرنى زېمىندا تۇرغۇزدى، ئاللاھتىن مەغپىرەت تىلەڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، پەرۋەردىگارىم ھەقىقەتەن يېقىندۇر، (دۇئانى) ئىجابەت قىلغۇچىدۇر». ');
INSERT INTO "saleh" VALUES(11,62,'11:62','ئۇلار: «ئى سالىھ! بۇنىڭدىن بۇرۇن سەن ئارىمىزدا ئۈمىد قىلىنغان كىشى ئىدىڭ (يەنى بۇ سۆزنى ئېيتىشىڭدىن بۇرۇن ئارىمىزدا چوڭ ئادەم بولۇپ قېلىشىڭنى ئۈمىد قىلاتتۇق)، بىزنى ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كەلگەن بۇتلارغا ئىبادەت قىلىشتىن توسۇمسەن؟ شەك شۈبھىسىزكى، ھەقىقەتەن بىز سەن دەۋەت قىلغان ئىشقا زور گۇماندىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(11,63,'11:63',' سالىھ ئېيتتى: «ئى قەۋمىم! ئېيتىڭلارچۇ، ئەگەر مەن پەرۋەردىگارىم تەرىپىدىن نازىل بولغان ئېنىق دەلىلگە ئاساسلانسام ۋە ئۇنىڭ رەھمىتى (يەنى پەيغەمبەرلىك) گە سازاۋەر بولسام، شۇنداق تۇرۇقلۇق ئاللاھقا قارشىلىق قىلسام، ئاللاھنىڭ ئازابىدىن مېنى كىم قۇتقۇزىدۇ؟ (مېنى ئۆزۈڭلارغا ئەگىشىشكە دەۋەت قىلىش بىلەن) ماڭا پەقەت زىياندىن باشقىسىنى زىيادە قىلالمايسىلەر.');
INSERT INTO "saleh" VALUES(11,64,'11:64',' ئى قەۋمىم! بۇ ئاللاھ (بىۋاسىتە) ياراتقان چىشى تۆگە سىلەرگە مېنىڭ مۆجىزەمدۇر، ئۇنى ئاللاھنىڭ زېمىنىغا قويۇۋېتىڭلار، ئىختىيارى ئوتلىسۇن، ئۇنىڭغا زىيان - زەخمەت يەتكۈزمەڭلار، بولمىسا سىلەرنى پات يېقىندا كېلىدىغان ئازاب ھالاك قىلىدۇ».');
INSERT INTO "saleh" VALUES(11,65,'11:65','ئۇلار چىشى تۆگىنى بوغۇزلىدى، سالىھ ئېيتتى: «ئۆيۈڭلاردا(ھاياتلىقتىن) ئۈچ كۈن بەھرىمەن بولۇۋېلىڭلار، بۇ ۋەدە يالغان ئەمەس».');
INSERT INTO "saleh" VALUES(11,66,'11:66',' بىزنىڭ (ئازاب قىلىش) ئەمرىمىز كەلگەندە، سالىھنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى رەھمىتىمىز بىلەن شۇ كۈننىڭ شەرمەندىچىلىكىدىن قۇتقۇزدۇق، پەرۋەردىگارىڭ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر. ');
INSERT INTO "saleh" VALUES(11,67,'11:67','زالىملارغا قاتتىق ئاۋاز يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى. ');
INSERT INTO "saleh" VALUES(11,68,'11:68','گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ئۇلارنىڭ تۇرغان جايلىرى ئادەمزات تەرىپىدىن ئاۋات قىلىنمىغاندەك) بولۇپ قالدى، بىلىڭلاركى، سەمۇد پەرۋەردىگارىنى ئىنكار قىلدى، سەمۇد ھالاك بولسۇن.');
INSERT INTO "saleh" VALUES(11,69,'11:69','شۈبھىسىزكى، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا خۇشخەۋەر ئېلىپ كەلدى. ئۇلار: «ساڭا ئامانلىق تىلەيمىز» دېدى. ئىبراھىم : «مەنمۇ سىلەرگە ئامانلىق تىلەيمەن» دېدى. ئۇ ئۇزاققا قالماي بىر موزاينى كاۋاپ قىلىپ ئېلىپ كەلدى.');
INSERT INTO "saleh" VALUES(11,70,'11:70','ئۇلارنىڭ (يەنى پەرىشتىلەرنىڭ) ئۇنىڭغا قول ئۇزاتمايۋاتقانلىقىنى كۆرۈپ، ئۇلارنى خوش كۆرمىدى، ئۇلاردىن قورقۇنچ ھېس قىلدى، ئۇلار: «قورقمىغىن (بىز رەببىڭنىڭ پەرىشتىلىرى، تاماق يېمەيمىز)، بىز ھەقىقەتەن لۇتنىڭ قەۋمىنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق» دېدى. ');
INSERT INTO "saleh" VALUES(11,71,'11:71','ئۇنىڭ ئايالى (يەنى سارە) بىر تەرەپتە تۇرۇپ (لۇت قەۋمىنىڭ ھالاك بولىدىغانلىقىدىن خۇشاللىنىپ) كۈلدى، بىز ئۇنىڭغا (يەنى سارەگە) ئىسھاق (ئاتلىق بالىسى بولىدىغانلىقى) بىلەن ۋە ئىسھاقتىن كېيىن كېلىدىغان يەئقۇب (ئاتلىق نەۋرىسى بولىدىغانلىقى) بىلەن خۇش خەۋەر بەردۇق.');
INSERT INTO "saleh" VALUES(11,72,'11:72','ئۇ (يەنى سارە): «ۋىيەي! مەن بىر موماي تۇرسام، ئېرىم بىر بوۋاي تۇرسا، تۇغامدىمەن؟ بۇ ھەقىقەتەن قىزىق ئىشقۇ!» دېدى.');
INSERT INTO "saleh" VALUES(11,73,'11:73','ئۇلار: «ئاللاھنىڭ ئەمرىدىن (يەنى قۇدرىتىدىن) ئەجەبلىنەمسەن؟ ئاللاھنىڭ رەھمىتى ۋە بەرىكەتلىرى سىلەرنىڭ ئائىلەڭلارغا (يەنى ئىبراھىمنىڭ ئائىلىسىدىكىلەرگە) نازىل بولسۇن. ئاللاھ ھەقىقەتەن مەدھىيەەلەشكە لايىقتۇر، ئۇلۇغلاشقا لايىقتۇر» دېدى.');
INSERT INTO "saleh" VALUES(11,74,'11:74','ئىبراھىمدىن قورقۇنچ يوقالغان ھەمدە خۇشخەۋەر ئۇنىڭغا يەتكەن چاغدا، ئۇ بىز بىلەن لۇتنىڭ قەۋمى توغرىسىدا مۇنازىرىلىشىشكە كىرىشتى. ');
INSERT INTO "saleh" VALUES(11,75,'11:75','ئىبراھىم بولسا ھەقىقەتەن كۆڭلى - كۆكسى كەڭ، كۆپ ئاھ چەككۈچى(يەنى دىلى يۇمشاق)، (ھەممە ئىشتا) ئاللاھقا مۇراجىئەت قىلغۇچى زاتتۇر.');
INSERT INTO "saleh" VALUES(11,76,'11:76','(پەرىشتىلەر ئېيتتى) «ئى ئىبراھىم! بۇنى (يەنى لۇت قەۋمى توغرىسىدا مۇنازىرىلىشىشنى) قويغىن، شۈبھىسىزكى، پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش توغرىسىدىكى) ئەمرى ئاللىقاچان چۈشتى، ئۇلارغا چوقۇم (مۇنازىرىلىشىش ياكى شاپائەت قىلىش بىلەن) قايتۇرغىلى بولمايدىغان ئازاب چۈشىدۇ».');
INSERT INTO "saleh" VALUES(11,77,'11:77','بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلەر) لۇتنىڭ يېنىغا كەلگەن چاغدا (لۇت ئۆز قەۋمىنىڭ ئۇلارغا چېقىلىپ قويۇشىدىن قورقۇپ) قىيىن ئەھۋالدا قالدى، ئۇلار توغرىسىدا يۈرىكى سىقىلدى ۋە: «بۇ بىر دىشۋار كۈندۇر» دېدى. ');
INSERT INTO "saleh" VALUES(11,78,'11:78','لۇتنىڭ قەۋمى ئۇنىڭ قېشىغا ئالدىراپ كېلىشتى، ئۇلار بۇرۇنلا يامان ئىشلارنى قىلاتتى (يەنى لىۋاتەت قىلاتتى). لۇت ئېيتتى: «ئى قەۋمىم! مېنىڭ بۇ قىزلىرىم سىلەرگە ئەڭ پاكتۇر، ئاللاھتىن قورقۇڭلار، مېھمانلىرىم ئالدىدا مېنى رەسۋا قىلماڭلار، ئاراڭلاردا قەبىھ ئىشتىن توسىدىغان بىرەر كاللىسى جايىدا ئادەم يوقمۇ؟».');
INSERT INTO "saleh" VALUES(11,79,'11:79','ئۇلار (لۇتقا) ئېيتتى: «قىزلىرىڭغا بىزنىڭ ھاجىتىمىز يوق ئىكەنلىكىنى سەن ئوبدان بىلىسەن، شۇنىڭدەك بىزنىڭ مەقسىتىمىزنىمۇ ئوبدان بىلىسەن».');
INSERT INTO "saleh" VALUES(11,80,'11:80','لۇت ئېيتتى: «كاشكى مېنىڭ (سىلەرگە قارشى تۇرىدىغان) كۈچ- قۇۋۋىتىم بولسا ئىدى ياكى ئىلتىجا قىلىدىغان كۈچلۈك جەمەتىم بولسا ئىدى (چوقۇم سىلەرگە زەربە بەرگەن بولاتتىم)».');
INSERT INTO "saleh" VALUES(11,81,'11:81','ئۇلار (يەنى پەرىشتىلەر): «ئى لۇت! شۈبھىسىزكى، بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىدۇرمىز، ئۇلار ھەرگىزمۇ ساڭا زىيان يەتكۈزەلمەيدۇ، ئائىلەڭدىكىلەرنى ئېلىپ كېچىنىڭ ئاخىرىدا (ئۇلارنىڭ ئىچىدىن) چىقىپ كەتكىن. ئايالىڭدىن باشقا، سىلەردىن ھېچ كىشى ئارقىسىغا قارىمىسۇن، ئۇ (يەنى ئايالىڭ) ئۇلار بىلەن بىرگە ھالاك بولغۇچىدۇر، شۈبھىسىزكى، ئۇلارغا ئازاب چۈشىدىغان چاغ تاڭ ۋاقتىدۇر، تاڭ يېقىن ئەمەسمۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(11,82,'11:82','بىزنىڭ (جازالاش) ئەمرىمىز چۈشكەندە، ئۇلارنىڭ يۇرتلىرىنى ئاستىن ـ ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق)، ئۇ يۇرتلارغا بىز ئۈستى ـ ئۈستىلەپ ساپال تاشلارنى ياغدۇردۇق.');
INSERT INTO "saleh" VALUES(11,83,'11:83','ئۇ ساپال تاشلارغا پەرۋەردىگارىڭنىڭ دەرگاھىدا بەلگە سېلىنغان ئىدى، ئۇ يۇرتلار زالىملار (يەنى قۇرەيش كاپىرلىرى) دىن يىراق ئەمەس.');
INSERT INTO "saleh" VALUES(11,84,'11:84','مەديەن (ئەھلى) گە ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇ ئېيتتى: «ئى قەۋمىم! ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۆلچەمدە ۋە تارازىدا كەم بەرمەڭلار، مەن سىلەرنى ھەقىقەتەن باي ھېسابلايمەن، مەن ھەقىقەتەن سىلەرنىڭ ھەممىنى قورشاپ تۇرغۇچى كۈن(يەنى قىيامەت كۈنى) نىڭ ئازابىغا دۇچار بولۇشۇڭلاردىن قورقىمەن.');
INSERT INTO "saleh" VALUES(11,85,'11:85','ئى قەۋمىم! سىلەر ئۆلچەمنى توغرا ئۆلچەڭلار، تارازىنى توغرا تارتىڭلار، كىشىلەرگە نەرسىلەرنى كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار.');
INSERT INTO "saleh" VALUES(11,86,'11:86','ئاللاھ سىلەرگە قالدۇرغان ھالال رىزىق (سىلەر يىغقان ھارامدىن) ياخشىدۇر، ئەگەر سىلەر مۆمىن بولساڭلار. مەن سىلەرگە مۇھاپىزەتچى ئەمەسمەن».');
INSERT INTO "saleh" VALUES(11,87,'11:87','ئۇلار: «ئى شۇئەيب! سېنىڭ نامىزىڭ (يەنى دىنىڭ) سېنى ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان نەرسىلەرنى (يەنى بۇتلارنى) بىزنى تاشلاتقۇزۇشقا ياكى ماللىرىمىزنى خالىغىنىمىزچە تەسەررۇپ قىلىشىمىزنى تەرك ئەتكۈزۈشكە بۇيرۇمدۇ؟ سەن تازىمۇ كۆڭلى ـ كۆكسى كەڭ، تازىمۇ توغرا يول تاپقان ئادەمسەن ـ دە» دېدى.');
INSERT INTO "saleh" VALUES(11,88,'11:88','شۇئەيب ئېيتتى: «ئى قەۋمىم! ئېيتىپ بېقىڭلارچۇ، پەرۋەردىگارىم ماڭا روشەن پاكىت (يەنى پەيغەمبەرلىك) ۋە كەڭتاشا ھالال رىزىق ئاتا قىلغان تۇرسا، (مەن شۇنداق نېمەتلەرنى ئاتا قىلغان پەرۋەردىگارىمنىڭ ئەمرىگە خىلاپلىق قىلامدىم؟) سىلەرگە قىلىش مەنئى قىلىنغان ئىشنى ئۆزۈممۇ قىلىشنى خالىمايمەن، مەن پەقەت (سىلەرنى) قولۇمدىن كېلىشىچە تۈزەشنىلا خالايمەن، مەن پەقەت ئاللاھنىڭ ياردىمى بولغاندىلا مۇۋەپپەقىيەت قازىنالايمەن، (ھەممە ئىشتا) ئاللاھقا تايىنىمەن ۋە ئۇنىڭغا يۈزلىنىمەن.');
INSERT INTO "saleh" VALUES(11,89,'11:89','ئى قەۋمىم! سىلەرنىڭ ماڭا قارشى تۇرغانلىقىڭلار سىلەرنى نۇھنىڭ قەۋمى ياكى ھۇدنىڭ قەۋمى ۋە ياكى سالىھنىڭ قەۋمى دۇچار بولغان ئازابقا دۇچار قىلمىسۇن. لۇت قەۋمى (نىڭ دىيارى) سىلەر (نىڭ جايىڭلار) دىن يىراق ئەمەس.');
INSERT INTO "saleh" VALUES(11,90,'11:90','پەرۋەردىگارىڭلاردىن مەغپىرەت تەلەپ قىلىڭلار، ئاندىن ئۇنىڭغا تەۋبە قىلىڭلار، مېنىڭ پەرۋەردىگارىم (تەۋبە قىلغۇچىلارغا) ھەقىقەتەن مەرھەمەتلىكتۇر، (ئۇلارنى) دوست تۇتقۇچىدۇر».');
INSERT INTO "saleh" VALUES(11,91,'11:91','ئۇلار: «ئى شۇئەيب ! بىز سېنىڭ ئېيتقانلىرىڭنىڭ نۇرغۇنىنى چۈشەنمەيمىز. بىز ئەلۋەتتە سېنى ئارىمىزدا كۈچسىز ئادەم دەپ قارايمىز، سېنىڭ قەۋم ـ قېرىنداشلىرىڭ بولمىغاندا ئىدى، بىز سېنى چوقۇم تاش ـ كېسەك قىلىپ ئۆلتۈرەتتۇق، سەن بىزگە ئەتىۋارلىق ئەمەسسەن» دېدى.');
INSERT INTO "saleh" VALUES(11,92,'11:92','شۇئەيب ئېيتتى: «ئى قەۋمىم! مېنىڭ قەۋم -قېرىنداشلىرىم سىلەرگە ئاللاھتىنمۇ ئەتىۋارلىقمۇ؟ سىلەر ئاللاھنى ئارقاڭلارغا چۆرىۋەتتىڭلار (يەنى ئاللاھقا ئىتائەت قىلمىدىڭلار ۋە ئاللاھنى ئۇلۇغلىمىدىڭلار)، پەرۋەردىگارىم سىلەرنىڭ قىلمىشلىرىڭلاردىن ئەلۋەتتە تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(11,93,'11:93','ئى قەۋمىم! سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، مەنمۇ ئەلۋەتتە ئۆز يولۇم بويىچە ھەرىكەت قىلاي! ئۇزاققا قالماي كىمنىڭ رەسۋا قىلغۇچى ئازابقا دۇچار بولىدىغانلىقىنى ۋە كىمنىڭ يالغانچى ئىكەنلىكىنى بىلىسىلەر، (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار! شۈبھىسىزكى، مەنمۇ سىلەر بىلەن بىللە كۈتىمەن».');
INSERT INTO "saleh" VALUES(11,94,'11:94','بىزنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانىمىز چۈشكەن چاغدا، شۇئەيبنى ۋە ئۇنىڭ بىلەن بىللە ئىمان ئېيتقانلارنى مەرھەمەت قىلىپ قۇتۇلدۇردۇق، (ئۆزلىرىگە) زۇلۇم قىلغانلارغا قاتتىق ئاۋاز (دىن پەيدا بولغان زىلزىلە) يۈزلەندى، ئۇلار ئۆز ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قالدى.');
INSERT INTO "saleh" VALUES(11,95,'11:95','گويا ئۇلار ئۆيلىرىدە تۇرمىغاندەك (يەنى ياشىمىغاندەك) بولۇپ قالدى. مەديەن ئاھالىسى خۇددى سەمۇد قەۋمى ھالاك بولغاندەك ھالاك بولسۇن! ');
INSERT INTO "saleh" VALUES(11,96,'11:96','بىز مۇسانى ھەقىقەتەن ئايەتلىرىمىز (يەنى ئاللاھتىن نازىل بولغان دىنىي ئەھكاملار) ۋە روشەن دەلىللەر (يەنى مۆجىزىلەر) بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار پىرئەۋننىڭ بۇيرۇقىغا بويسۇندى، پىرئەۋننىڭ بۇيرۇقى توغرا ئەمەس ئىدى[96 ـ 97].');
INSERT INTO "saleh" VALUES(11,97,'11:97','بىز مۇسانى ھەقىقەتەن ئايەتلىرىمىز (يەنى ئاللاھتىن نازىل بولغان دىنىي ئەھكاملار) ۋە روشەن دەلىللەر (يەنى مۆجىزىلەر) بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار پىرئەۋننىڭ بۇيرۇقىغا بويسۇندى، پىرئەۋننىڭ بۇيرۇقى توغرا ئەمەس ئىدى[96 ـ 97].');
INSERT INTO "saleh" VALUES(11,98,'11:98','پىرئەۋن قىيامەت كۈنى باشلامچىلىق بىلەن ئۆز قەۋمىنى دوزاخقا باشلاپ كىرىدۇ، ئۇ جاي نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(11,99,'11:99','ئۇلار بۇ دۇنيادىمۇ، قىيامەت كۈنىدىمۇ لەنەتكە ئۇچرايدۇ، ئۇلارغا بېرىلگەن (ئىككىلا دۇنيادا لەنەتكە ئۇچراشتىن ئىبارەت) بۇ سوۋغا نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(11,100,'11:100','مانا بۇلار (ئاھالىسىنى بىز ھالاك قىلغان) يۇرتلارنىڭ خەۋەرلىرىدۇر، ئۇنى ساڭا (ۋەھىي ئارقىلىق) بايان قىلىمىز. ئۇ يۇرتلاردىن ئىزى قالغانلىرىمۇ بار (يەنى ئاھالىسى ھالاك قىلىنىپ، يۇرتلىرىنىڭ ئورنى قالدۇرۇلغانلىرىمۇ بار)، ئىزى قالمىغانلىرىمۇ بار (يەنى ئاھالىسى بىلەن قوشۇپ ئورۇۋېتىلگەن زىرائەتتەك ۋەيران قىلىنغانلىرىمۇ بار).');
INSERT INTO "saleh" VALUES(11,101,'11:101','بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى. پەرۋەردىگارىڭنىڭ (ئۇلارنى ھالاك قىلىش) پەرمانى كەلگەندە، ئۇلارنىڭ ئاللاھنى قويۇپ ئىبادەت قىلغان مەبۇدلىرى ھېچ نەرسىگە ئەسقاتمىدى، ئۇلارغا پەقەت زىيان سالدى. ');
INSERT INTO "saleh" VALUES(11,102,'11:102','پەرۋەردىگارىڭ زالىم يۇرتلار (ئاھالىسى) نى ھالاك قىلسا، ئەنە شۇنداق ھالاك قىلىدۇ، ئاللاھنىڭ ئازابى ھەقىقەتەن تولىمۇ قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(11,103,'11:103',' ئاخىرەت ئازابىدىن قورقىدىغان ئادەم بۇ (قىسسە) دىن، ئەلۋەتتە ئىبرەت ئالىدۇ، ئەنە شۇ كۈن پۈتۈن خالايىق(ھېساب بېرىش ئۈچۈن) يىغىلىدىغان كۈندۇر، ئەنە شۇ كۈن ھەممە ھازىر بولىدىغان كۈندۇر.');
INSERT INTO "saleh" VALUES(11,104,'11:104','ئۇنى (يەنى قىيامەت كۈنىنى) بىز مۇئەييەن مۇددەتكىچە تەخىر قىلىمىز. ');
INSERT INTO "saleh" VALUES(11,105,'11:105','قىيامەت كۈنى بولغاندا، ھەرقانداق ئادەم پەقەت ئاللاھتىن ئىجازەت بولغاندىلا سۆزلەيدۇ، ئۇلارنىڭ بەزىلىرى بەتبەخت بولىدۇ، بەزىلىرى سائادەتمەن بولىدۇ.');
INSERT INTO "saleh" VALUES(11,106,'11:106',' بەتبەختلەرگە كەلسەك، ئۇلار دوزاخقا كىرىدۇ، ئۇلار دوزاختا (ئېشەك ھاڭرىغاندەك) توۋلاپ نالە ـ پەرياد چېكىدۇ.');
INSERT INTO "saleh" VALUES(11,107,'11:107','(دوزاخنىڭ) ئاسمان ـ زېمىنى يوقالمايلا تۇرسا، ئۇلار دوزاختا پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ، پەرۋەردىگارىڭ ئەلۋەتتە خالىغىنىنى قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(11,108,'11:108','سائادەتمەنلەرگە كەلسەك، ئۇلار جەننەتكە كىرىدۇ، (جەننەتنىڭ) ئاسمان ـ زېمىنى يوقالمايلا تۇرسا، ئۇلار جەننەتتە پەرۋەردىگارىڭ خالىغان زامانغىچە داۋاملىق تۇرىۋېرىدۇ ،(بۇ ئۇلارغا قىلىنغان) ئۈزۈلۈپ قالمايدىغان ئىنئامدۇر.');
INSERT INTO "saleh" VALUES(11,109,'11:109','ئۇلار (يەنى مۇشرىكلار) نىڭ بۇتلارغا چوقۇنۇشىنىڭ (گۇمراھلىق) ئىكەنلىكىدىن شەكلەنمە ، ئۇلار پەقەت ئۆزلىرىنىڭ بىز ساڭا قىسسەسىنى بايان قىلغان ئاتا ـ بوۋىلىرىغا ئوخشاشلا چوقۇنىدۇ، بىز ئۇلارنىڭ (ئازابتىن) تېگىشلىك نېسىۋىسىنى چوقۇم كېمەيتمەي بېرىمىز. ');
INSERT INTO "saleh" VALUES(11,110,'11:110','شۈبھىسىزكى، بىز مۇساغا كىتاپ (يەنى تەۋراتنى) بەردۇق، ئۇنىڭ توغرىسىدا ئىختىلاپ قىلىندى (يەنى ئۇنىڭ قەۋمى بۇ كىتاب ئۈستىدە ئىختىلاپ قىلىشتى). پەرۋەردىگارىڭنىڭ ئالدىن چىقارغان (ئۇلارنى جازالاشنى قىيامەتكە قالدۇرۇش ھەققىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا (مۇشۇ دۇنيادىلا ياخشىغا ياخشىچە، يامانغا يامانچە) چوقۇم ھۆكۈم چىقىرىلاتتى. ھەقىقەتەن ئۇلار ئۇنىڭدىن (يەنى قۇرئاندىن) زور گۇماندىدۇر.');
INSERT INTO "saleh" VALUES(11,111,'11:111',' پەرۋەردىگارىڭ ھەر بىر ئادەمگە ئۇنىڭ قىلغان ئەمەللىرىنىڭ مۇكاپاتىنى تولۇق بېرىدۇ، ئاللاھ ، ئەلۋەتتە ئۇلارنىڭ ئەمەللىرىدىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(11,112,'11:112','(ئى مۇھەممەد!) ئاللاھ سېنى بۇيرۇغاندەك توغرا يولدا بولغىن، ساڭا ئىمان ئېيتقانلارمۇ توغرا يولدا بولسۇن. (مەنئى قىلىنغان ئىشلارنى قىلىپ) ئاللاھنىڭ چەكلىمىلىرىدىن چىقىپ كەتمەڭلار، شۈبھىسىزكى، ئاللاھ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(11,113,'11:113','زالىملارغا مايىل بولماڭلار، (مايىل بولساڭلار) دوزاخ ئازابىغا قالىسىلەر، سىلەرگە ئاللاھتىن باشقا (دوزاختىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچى يوقتۇر، ئاندىن كېيىن ھېچ ياردەمگە ئېرىشەلمەيسىلەر.');
INSERT INTO "saleh" VALUES(11,114,'11:114','كۈندۈزنىڭ ئىككى تەرىپىدە (يەنى ئەتىگەندە ۋە كەچقۇرۇن ۋاقىتلىرىدا) ۋە كېچىنىڭ دەسلەپكى ۋاقىتلىرىدا ناماز ئۆتىگىن. شۈبھىسىزكى، ياخشى ئىشلار ئارقىلىق يامان ئىشلار يۇيۇلىدۇ. بۇ چۈشەنگۈچىلەر ئۈچۈن ۋەز ـ نەسىھەتتۇر. ');
INSERT INTO "saleh" VALUES(11,115,'11:115','(ئى مۇھەممەد! مۇشرىكلاردىن يەتكەن ئەزىيەتلەرگە) سەۋر قىلغىن، ئاللاھ ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ.');
INSERT INTO "saleh" VALUES(11,116,'11:116','سىلەردىن بۇرۇن ئۆتكەن ئۈممەتلەرنىڭ ئارىسىدا نېمىشقا (يامانلارنى) يەر يۈزىدە بۇزغۇنچىلىق قىلىشتىن توسىدىغان ئەقىل ئىگىلىرى بولمىدى؟ ئۇلار ئارىسىدىن بۇزغۇنچىلىقنى توسۇپ، بىزنىڭ نىجاتلىقىمىزغا ئېرىشكەن ئازغىنا كىشىلەر بۇنىڭدىن مۇستەسنا. ئۆزلىرىگە زۇلۇم قىلغۇچىلار ئەيش ـ ئىشرەتلىك تۇرمۇشنى قوغلىشىدۇ. ئۇلار گۇناھكار ئادەملەردۇر.');
INSERT INTO "saleh" VALUES(11,117,'11:117','پەرۋەردىگارىڭ ئاھالىسى ياخشى بولغان يۇرتلارنى زۇلۇم سېلىپ ھالاك قىلمايدۇ.');
INSERT INTO "saleh" VALUES(11,118,'11:118','ئەگەر پەرۋەردىگارىڭ خالىسا ئىدى، پۈتۈن ئادەملەرنى ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، ئۇلار(دىن توغرىسىدا) داۋاملىق ئوخشىمىغان پىكىردىدۇر، ');
INSERT INTO "saleh" VALUES(11,119,'11:119',' پەقەت پەرۋەردىگارىڭنىڭ رەھمىتىگە ئېرىشكەنلەر بۇنىڭدىن مۇستەسنا. شۇڭا ئاللاھ ئۇلارنى (ئوخشىمىغان پىكىر قىلىش تەبىئىتىدە) ياراتتى. پەرۋەردىگارىڭنىڭ: «مەن چوقۇم جىنلار ۋە ئىنسانلار بىلەن دوزاخنى توشقۇزىمەن» دېگەن سۆزى ئېنىق بەلگىلەندى[ 119].');
INSERT INTO "saleh" VALUES(11,120,'11:120','كۆڭلۈڭنى خاتىرجەم قىلىش ئۈچۈن، ساڭا پەيغەمبەرلەرنىڭ قىسسەلىرىنى بايان قىلىپ بېرىمىز، بۇنىڭدا ساڭا ھەقىقەت كەلدى، مۆمىنلەر ئۈچۈن ۋەز – نەسىھەت ۋە ئىبرەتلەر باردۇرقىسسەلەر.');
INSERT INTO "saleh" VALUES(11,121,'11:121','ئىمان ئېيتمايدىغانلارغا ئېيتقىنكى، «سىلەر ئۆز يولۇڭلار بويىچە ھەرىكەت قىلىڭلار، بىزمۇ ئۆز يولىمىز بويىچە ھەرىكەت قىلايلى.');
INSERT INTO "saleh" VALUES(11,122,'11:122','سىلەر (ئىشىڭلارنىڭ ئاقىۋىتىنى) كۈتۈڭلار، بىزمۇ ئەلۋەتتە (شۇنى) كۈتىمىز».');
INSERT INTO "saleh" VALUES(11,123,'11:123','ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش پەقەت ئاللاھقا خاستۇر، ھەممە ئىش ئاللاھقا قايتۇرۇلىدۇ، ئاللاھقا ئىبادەت قىلغىن ۋە ئاللاھقا تەۋەككۈل قىلغىن (يەنى ھەممە ئىشىڭنى ئاللاھقا تاپشۇرغىن). پەرۋەردىگارىڭ قىلىۋاتقان ئىشلىرىڭلاردىن غاپىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(12,1,'12:1','ئەلىف، لام، را. بۇ روشەن كىتابنىڭ ئايەتلىرىدۇر. ');
INSERT INTO "saleh" VALUES(12,2,'12:2','شۈبھىسىزكى، سىلەرنى چۈشەنسۇن دەپ، ئۇنى ئەرەبچە قۇرئان قىلىپ نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(12,3,'12:3','بۇ قۇرئاننى ساڭا ۋەھىي قىلىش ئارقىلىق ئەڭ چىرايلىق قىسسەنى ساڭا بايان قىلىپ بېرىمىز، بۇنىڭدىن بۇرۇن بۇ قىسسەدىن ھەقىقەتەن بىخەۋەر ئىدىڭ.');
INSERT INTO "saleh" VALUES(12,4,'12:4',' ئۆز ۋاقتىدا يۇسۇف ئۆز ئاتىسىغا: «ئى ئاتا! مەن ھەقىقەتەن چۈشۈمدە 11 يۇلتۇزنىڭ ۋە ئاي بىلەن كۈننىڭ ماڭا سەجدە قىلغانلىقىنى كۆردۈم» دېدى.');
INSERT INTO "saleh" VALUES(12,5,'12:5',' ئاتىسى (يەنى يەئقۇب ئەلەيھىسسالام): «ئى ئوغلۇم! چۈشۈڭنى قېرىنداشلىرىڭغا ئېيتمىغىن، ساڭا سۇيىقەست قىلىپ سالمىسۇن، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,6,'12:6','شۇنىڭغا (يەنى ساڭا ئۇلۇغ چۈش كۆرسەتكىنىگە) ئوخشاش، پەرۋەردىگارىڭ سېنى (پەيغەمبەرلىككە) تاللايدۇ، ساڭا چۈش تەبىرىنى بىلدۈرىدۇ، ئەجدادلىرىڭ ئىبراھىم، ئىسھاقلارغا ئىلگىرى ئاللاھ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بەرگىنىدەك، ساڭىمۇ، يەئقۇب ئەۋلادىغىمۇ ئۆزىنىڭ ئۇلۇغ نېمىتىنى بېرىدۇ. شۈبھىسىزكى، پەرۋەردىگارىڭ (ئۆز مەرھەمىتىگە كىمنىڭ لايىق ئىكەنلىكىنى) ئوبدان بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(12,7,'12:7','يۇسۇفنىڭ ۋە قېرىنداشلىرىنىڭ (قىسسەسىدە) سورىغۇچىلار ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ھەقىقەتەن نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(12,8,'12:8','ئۆز ۋاقتىدا ئۇلار: «يۇسۇف ۋە ئۇنىڭ قېرىندىشى (يەنى بۇنيامىن) ئاتىمىزغا بىزدىنمۇ سۆيۈملۈكتۇر، ھالبۇكى، بىز (كۈچلۈك) جامائەدۇرمىز، ئاتىمىز(نىڭ ئۇ ئىككىسىنى بىزدىن ئارتۇق كۆرۈشى) ئەلۋەتتە روشەن خاتادۇر» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,9,'12:9','(ئۇلار) «يۇسۇفنى ئۆلتۈرۋېتىڭلار ياكى ئۇنى يىراق بىر چەت جايغا تاشلىۋېتىڭلار، (شۇ چاغدا) ئاتاڭلارنىڭ مۇھەببىتى سىلەرگە قالىدۇ، بۇ (گۇناھ) تىن كېيىن (تەۋبە قىلىپ) ياخشى ئادەم بولۇپ كېتىسىلەر» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,10,'12:10','ئۇلاردىن بىرى سۆز قىلىپ: «يۇسۇفنى ئۆلتۈرمەڭلار، (ئەگەر ئۇنى ئاتىسىدىن يىراق قىلماقچى بولساڭلار، ئۇنى قۇدۇققا تاشلىۋېتىڭلار، ئۇنى يولدىن ئۆتكەن كارۋان ئېلىۋالسۇن» دېدى.');
INSERT INTO "saleh" VALUES(12,11,'12:11','ئۇلار ئېيتتى: «ئى ئاتىمىز! نېمىشقا يۇسۇف توغرىسىدا بىزگە ئىشەنمەيسەن؟ ھالبۇكى، بىز ئۇنىڭغا ھەقىقەتەن ياخشى نىيەتتىمىز.');
INSERT INTO "saleh" VALUES(12,12,'12:12',' ئەتە ئۇنى بىزگە قوشۇپ قويغىن، يەپ ـ ئىچىپ، ئويناپ ـ كۈلۈپ كىرسۇن، ئۇنى بىز چوقۇم ياخشى مۇھاپىزەت قىلىمىز».');
INSERT INTO "saleh" VALUES(12,13,'12:13',' يەئقۇب: «ئۇنى (يەنى يۇسۇفنى) ئېلىپ كەتسەڭلار راستلا خاتىرجەم بولالمايمەن، سىلەر غەپلەتتە قېلىپ، ئۇنى بۆرە يەپ قويۇشتىن ئەنسىرەيمەن» دېدى.');
INSERT INTO "saleh" VALUES(12,14,'12:14','ئۇلار: «بىز (كۈچلۈك) بىر جامائە تۇرۇقلۇق، ئۇنى بۆرىگە يېگۈزۈپ قويساق، ئۇ چاغدا بىز ئەلۋەتتە ـ زىيان تارتقۇچىلاردىن بولىمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,15,'12:15',' ئۇلار يۇسۇفنى ئېلىپ چىقىپ كەتكەن ۋە ئۇنى قۇدۇققا تاشلاشنى بىردەك قارار قىلىشقان چاغدا، بىز يۇسۇفكە: «كەلگۈسىدە ئۇلارغا بۇ ئىشنى (سېنىڭ يۇسۇف ئىكەنلىكىڭنى) ئۇلار ئۇقمايدىغان پەيتتە خەۋەر بېرىسەن» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(12,16,'12:16','ئۇلار كەچتە ئاتىسىنىڭ ئالدىغا يىغلاپ كېلىشتى.');
INSERT INTO "saleh" VALUES(12,17,'12:17','ئۇلار: «ئى ئاتىمىز! يۇسۇفنى نەرسىلىرىمىزنىڭ يېنىدا قويۇپ يۈگۈرۈشكىلى كېتىپ قېلىپتۇق، ئۇنى بۆرە يەپ كېتىپتۇ، راست گەپ قىلساقمۇ سەن بىزگە ئىشەنمەيسەن» دېيىشتى. ');
INSERT INTO "saleh" VALUES(12,18,'12:18','ئۇلار يۇسۇفنىڭ كۆڭلىكىنى يالغاندىن قانغا بوياپ ئېلىپ كېلىشكەن ئىدى. يەئقۇب: «بۇ ئىشنى نەپسىڭلار سىلەرگە چىرايلىق كۆرسىتىپ قىلغۇزۇپتۇ. مەن پەقەت چىرايلىقچە سەۋر قىلىمەن، سىلەرنىڭ (مۇشۇ يالغان) سۆزۈڭلارنى كۆتۈرۈشۈمگە ئاللاھ مەدەتكاردۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,19,'12:19','بۇ يەردىن بىر كارۋان ئۆتتى، ئۇلار سۇچىسىنى (سۇ ئېلىپ كېلىشكە) ئەۋەتتى، ئۇ سوغىسىنى قۇدۇققا تاشلىدى ۋە: «خۇشخەۋەر! مانا بىر ئوغۇل بالا» دېدى. ئۇلار ئۇنى تىجارەت مېلى سۈپىتىدە مەخپىي تۇتۇشتى، ئۇلارنىڭ قىلمىشلىرىنى ئاللاھ بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(12,20,'12:20',' ئۇلار يۇسۇفنى ساناقلىق بىر قانچە تەڭگىگە ئەرزان باھادا سېتىۋەتتى، چۈنكى ئۇلار يۇسۇفكە قىزىقمىغان ئىدى.');
INSERT INTO "saleh" VALUES(12,21,'12:21','ئۇنى سېتىۋالغان مىسىرلىق ئادەم ئايالىغا: «ئۇنى ياخشى كۈتكىن، بىزگە ئەسقاتار ياكى بالا قىلىۋالارمىز» دېدى. شۇنىڭدەك (يەنى يۇسۇفنى قۇدۇقتىن قۇتقۇزغىنىمىزدەك) يۇسۇفنى (مىسىر) زېمىنىغا يەرلەشتۈردۈق، ئۇنىڭغا چۈش تەبىرىنى ئۈگەتتۇق، ئاللاھ (خالىغان) ئىشنى ئەمەلگە ئاشۇرۇشقا قادىردۇر، لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(12,22,'12:22',' يۇسۇف قىرانىغا يەتكەندە، ئۇنىڭغا ھېكمەت بىلەن ئىلىم ئاتا قىلدۇق، ياخشى ئىش قىلغۇچىلارنى شۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(12,23,'12:23','يۇسۇف تۇرۇۋاتقان ئۆيدىكى ئايال (يەنى زۈلەيخا) يۇسۇفتىن ئۆزى بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى، ئۇ ئىشىكلەرنى تاقىۋېتىپ: «بېرى كەل!» دېدى. يۇسۇف: «خۇدا پاناھ بەرسۇن! ئۇ(يەنى ئېرىڭ) ھەقىقەتەن مېنىڭ خوجايىنىمدۇر، ئۇ مېنى ياخشى كۈتتى. ئۆزىگە زۇلۇم قىلغۇچىلار ھەقىقەتەن نىجات تاپالمايدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,24,'12:24',' ئۇ (يەنى زۈلەيخا) يۇسۇفكە (يېقىنچىلىق قىلىشقا) بەل باغلىدى، پەرۋەردىگارىنىڭ روشەن دەلىلىنى كۆرمىگەن بولسا، يۇسۇفمۇ ئۇنىڭغا مايىل بولغان بولاتتى. بىز يۇسۇفنى گۇناھتىن ۋە سەت ئىشتىن ئەنە شۇنداق ساقلىدۇق، شۈبھىسىزكى، يۇسۇف بىزنىڭ سادىق بەندىلىرىمىزدىن ئىدى.');
INSERT INTO "saleh" VALUES(12,25,'12:25','ئۇلارنىڭ ھەر ئىككىسى (يۇسۇف چىقىپ كېتىش ئۈچۈن، زۈلەيخا ئۇنى قايتۇرۇپ كېلىش ئۈچۈن) ئىشىككە قاراپ يۈگۈرۈشتى. (بۇ چاغدا) ئۇ (يەنى زۈلەيخا) يۇسۇفنىڭ كۆڭلىكىنى ئارقىسىدىن (تارتىپ) يىرتىۋەتتى، ئىشىك ئالدىدا ئۇنىڭ (يەنى زۈلەيخانىڭ) ئېرى بىلەن دوقۇرۇشۇپ قېلىشتى. ئۇ (يەنى زۈلەيخا): «سېنىڭ ئەھلىيەڭنى دەپسەندە قىلماقچى بولغان ئادەمنى پەقەت زىندانغا تاشلاش ياكى قاتتىق قىيناش بىلەن جازالاش كېرەك» دېدى.');
INSERT INTO "saleh" VALUES(12,26,'12:26','يۇسۇف: «ئۇ (يەنى زۈلەيخا) مەندىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدى» دېدى. ئۇ (يەنى زۈلەيخا) نىڭ ئائىلىسىدىكىلەردىن بىرسى گۇۋاھلىق بېرىپ: «يۇسۇفنىڭ كۆڭلىكى ئالدىدىن يىرتىلغان بولسا، ئۇنىڭ (يەنى زۈلەيخا) نىڭ سۆزى راست، يۇسۇفنىڭ سۆزى يالغان.');
INSERT INTO "saleh" VALUES(12,27,'12:27','يۇسۇفنىڭ كۆڭلىكى ئارقىسىدىن يىرتىلغان بولسا، ئۇ(يەنى زۈلەيخانىڭ) سۆزى يالغان، يۇسۇفنىڭ سۆزى راست» دېدى. ');
INSERT INTO "saleh" VALUES(12,28,'12:28','ئۇ (يەنى زۈلەيخانىڭ ئېرى) يۇسۇفنىڭ كۆڭلىكىنىڭ ئارقىسىدىن يىرتىلغانلىقىنى كۆرۈپ: «ئەي ئاياللار جامائەسى!بۇ چوقۇم سىلەرنىڭ ھىيلەڭلاردۇر، سىلەرنىڭ ھىيلەڭلار ھەقىقەتەن چوڭدۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,29,'12:29','يەنە ئېيتتى) «يۇسۇف! بۇنى سۆزلەپ يۈرمىگىن. (ئايالىم) سەن ئۆز گۇناھىڭغا كەچۈرۈم سورىغىن، سەن ھەقىقەتەن خاتالاشتىڭ».');
INSERT INTO "saleh" VALUES(12,30,'12:30','شەھەردىكى بەزى ئاياللار: «ئەزىز (يەنى مىسىرنىڭ ۋەزىرى) نىڭ خوتۇنى قۇلىغا كۆيۈپ قېلىپ، ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلىپتۇ، بىز ئۇنى، ئەلۋەتتە ئاشكارا ئازغان دەپ قارايمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,31,'12:31','ئۇ (يەنى زۈلەيخا) ئۇلارنىڭ گەپ ـ سۆزلىرىنى ئاڭلاپ، ئۇلارغا (مېھماندارچىلىققا چاقىرىپ) ئادەم ئەۋەتتى. ئۇلارغا (يۆلىنىدىغان) تەكىيە، ياستۇقلارنى تەييارلىدى، ئۇلارنىڭ ھەربىرىگە بىردىن پىچاق بەردى. (زۈلەيخا يۇسۇفكە) «سەن چىقىپ ئۇلار بىلەن كۆرۈشكىن، دېدى، ئۇلار يۇسۇفنى كۆرۈپ ھاڭ -تاڭ بولۇپ قېلىشتى ۋە ئۆزلىرىنىڭ قوللىرىنى كېسىۋېلىشتى. ئۇلار: «(بۇنداق چىرايلىق ئىنساننى ياراتقان) ئاللاھ پاكتۇر، بۇ ئادەمزات ئەمەس، پەقەت بىر ئېسىل پەرىشتىدۇر» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,32,'12:32',' (يەنى زۈلەيخا): «مانا بۇ سىلەر ئۇنىڭ ئۈچۈن مېنى ئەيىبلىگەن ھېلىقى ئادەم، مەن ئۇنىڭدىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلدىم، لېكىن ئۇ ئۆزىنى چىڭ ساقلىدى. ئەگەر ئۇ يەنىلا بۇيرۇقۇمنى قىلمىسا، چوقۇم زىندانغا تاشلىنىدۇ، چوقۇم خار ئادەم بولۇپ قالىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,33,'12:33','يۇسۇف: «ئى پەرۋەردىگارىم! ماڭا ئۇلار ئۈندىگەن نەرسىدىن زىندان سۆيۈملۈكتۇر، ئەگەر ئۇلارنىڭ ھىيلىسىنى مەندىن دەپئى قىلمىساڭ، (ئىنسانچىلىقتا) ئۇلارغا مايىل بولۇپ ، نادانلاردىن بولۇپ قالىمەن» دېدى.');
INSERT INTO "saleh" VALUES(12,34,'12:34','پەرۋەردىگارى ئۇنىڭ دۇئاسىنى ئىجابەت قىلىپ ئۇلارنىڭ ھىيلىسىنى يۇسۇفتىن دەپئى قىلدى. ئاللاھ (ئىلتىجا قىلغۇچىلارنىڭ دۇئاسىنى) ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(12,35,'12:35','ئاندىن ئۇلار (يۇسۇفنىڭ بىگۇناھ ئىكەنلىكىگە دائىر) پاكىتلارنى كۆرگەندىن كېيىن، ئۇنى بىرمەھەل قاماپ قويۇشنى لايىق تاپتى.');
INSERT INTO "saleh" VALUES(12,36,'12:36',' زىندانغا يۇسۇف بىلەن ئىككى يىگىت بىللە كىرگەن ئىدى. ئۇلارنىڭ بىرى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن (ئۈزۈمدىن) ھاراق سىقىۋاتقان كۆرۈپتىمەن» دېدى. ئىككىنچىسى: «مەن چۈشۈمدە ئۆزۈمنى ھەقىقەتەن بېشىمدا نان كۆتۈرۈپ تۇرغان، ناننى قۇشلار يەۋاتقان كۆرۈپتىمەن، تەبىرىنى بىزگە ئېيتىپ بەرسەڭ، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن (يەنى چۈشكە تەبىر بېرەلەيدىغانلاردىن) دەپ قارايمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,37,'12:37','يۇسۇف ئېيتتى: «ئىككىڭلارغا سىرتتىن تاماق كىرىشتىن بۇرۇن، ئۇنىڭ نېمە ئىكەنلىكىنى سىلەرگە ئېيتىپ بېرەلەيمەن، بۇ پەرۋەردىگارىم ماڭا بىلدۈرگەن نەرسىلەردىندۇر. شۈبھىسىزكى، مەن ئاللاھقا ئىشەنمەيدىغان، ئاخىرەتنى ئىنكار قىلىدىغان قەۋمنىڭ دىنىنى تاشلىدىم.');
INSERT INTO "saleh" VALUES(12,38,'12:38','مەن ئەجدادلىرىم ـ ئىبراھىم، ئىسھاق، يەئقۇبلارنىڭ دىنىغا ئەگەشتىم، ھېچقانداق نەرسىنى ئاللاھقا شېرىك كەلتۈرۈش بىزگە لايىق ئەمەس، بۇ ئاللاھنىڭ بىزگە ۋە ئىنسانلارغا قىلغان مەرھەمىتىدۇر، لېكىن ئىنسانلارنىڭ تولىسى شۈكۈر قىلمايدۇ.');
INSERT INTO "saleh" VALUES(12,39,'12:39','تۈرمىداش ئاغىنىلەر! تارقاق، كۆپ مەبۇدلار ياخشىمۇ ياكى قۇدرەتلىك بىر ئاللاھ ياخشىمۇ؟');
INSERT INTO "saleh" VALUES(12,40,'12:40',' سىلەرنىڭ ئاللاھنى قويۇپ ئىبادەت قىلىۋاتقىنىڭلار پەقەت سىلەر ئۆزۈڭلار ۋە ئاتا ـ بوۋاڭلار ئاتىۋالغان (بۇتلارنىڭ ۋە غەيرىينىڭ) ناملىرىدىنلا ئىبارەت، ئاللاھ (ئۇلارنىڭ ئاللاھنىڭ شېرىكى بولۇشى توغرىسىدا) ھېچقانداق دەلىل چۈشۈرگىنى يوق، ئىبادەت ۋە دىن بارىسىدىكى بارچە ھۆكۈملەر پەقەت ئاللاھقىلا مەنسۇپ، ئۇ سىلەرنى پەقەت ئۆزىگىلا ئىبادەت قىلىشقا بۇيرۇغان، بۇ توغرا دىندۇر. لېكىن كىشىلەرنىڭ تولىسى(بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(12,41,'12:41','تۈرمىداش ئاغىنىلەر! سىلەرنىڭ بىرىڭلار (زىنداندىن چىقىپ) خوجايىنغا ھاراق قۇيۇپ بېرىدۇ (يەنى ساقىيلىق ۋەزىپىسىنى ئۆتەيدۇ)، يەنە بىرىڭلار دارغا ئېسىلىدۇ، كاللىسىنى قۇشلار يەيدۇ، سىلەر سورىغان ئىشلار ھۆكۈم قىلىنىپ بولدى».');
INSERT INTO "saleh" VALUES(12,42,'12:42','يۇسۇف ئۇلارنىڭ ئىچىدىكى ئۆزىنىڭ قۇتۇلۇشىغا ئىشەنگەن بىرسىگە (يەنى ساقىيغا): «مېنى خوجايىنىڭنىڭ ئالدىدا ئەسلەپ قويغىن» دېگەن ئىدى. خوجايىنىنىڭ ئالدىدا يۇسۇفنى ئەسلەپ قويۇشنى شەيتان ئۇنىڭغا ئۇنتۇلدۇردى، شۇنىڭ بىلەن يۇسۇف زىنداندا بىر نەچچە يىل يېتىپ قالدى.');
INSERT INTO "saleh" VALUES(12,43,'12:43','پادىشاھ ئېيتتى: «مەن ھەقىقەتەن چۈشۈمدە يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپ كەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى كۆرۈپتىمەن، ئى ئەربابلار! سىلەر ئەگەر چۈش تەبىرىنى بىلسەڭلار، بۇ چۈشۈمگە تەبىر بېرىپ بېقىڭلار».');
INSERT INTO "saleh" VALUES(12,44,'12:44','ئۇلار ئېيتتى: «بۇ بىر قالايمىقان چۈش ئىكەن، بىز (مۇنداق) قالايمىقان چۈشكە تەبىر بېرىشنى ئۇقمايمىز».');
INSERT INTO "saleh" VALUES(12,45,'12:45','ھېلىقى ئىككىسىدىن (تۈرمىدىن) قۇتۇلغان ۋە ئۇزاق مۇددەتتىن كېيىن (يۇسۇفنى) ئەسلىگەن كىشى: «مەن سىلەرگە بۇ چۈشنىڭ تەبىرىنى ئېيتىپ بېرەلەيمەن، مېنى (يۇسۇفنىڭ يېنىغا) ئەۋەتىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(12,46,'12:46',' (ئۇنى ئەۋەتىشتى، ئۇ تۈرمىگە بېرىپ يۇسۇفنىڭ يېنىغا كىردى ـ دە) «ئى راستچىل يۇسۇف! يەتتە ئورۇق كالىنىڭ يەتتە سېمىز كالىنى يەپ كەتكەنلىكىنى ۋە يەتتە يېشىل باشاق بىلەن يەتتە قۇرۇق باشاقنى (كۆرۈپ چۈشىگەن چۈشنىڭ تەبىرىنى) بىزگە ئېيتىپ بەرسەڭ، مەن ئۇنى كىشىلەرگە (يەنى پادىشاھقا ۋە ئۇنىڭ دۆلەت ئەربابلىرىغا) خەۋەر قىلسام، ئۇلار (سېنىڭ پەزلىڭنى،بىلىمىڭنى) بىلسۇن» دېدى. ');
INSERT INTO "saleh" VALUES(12,47,'12:47','يۇسۇف ئېيتتى: «يەتتە يىل ئۈزۈلدۈرمەي تېرىقچىلىق قىلىڭلار، ئالغان ھوسۇلۇڭلاردىن يېيىش ئۈچۈن ئازغىنىسىنى خامان تەپكەندىن تاشقىرى، قالغىنىنى (مىتە چۈشۈپ كەتمەسلىكى ئۈچۈن) باشىقى بىلەن قويۇڭلار.');
INSERT INTO "saleh" VALUES(12,48,'12:48','شۇنىڭدىن كېيىن قەھەتچىلىك بولىدىغان يەتتە يىل كېلىدۇ، (بۇ يىللاردا) ئىلگىرى توپلىۋالغان ئاشلىقىڭلاردىن (ئۇرۇقلۇق ئۈچۈن) ساقلاپ قويۇلىدىغان ئازغىنىسىدىن باشقا ھەممىسىنى يەپ تۈگىتىسىلەر.');
INSERT INTO "saleh" VALUES(12,49,'12:49','شۇنىڭدىن كېيىن (مولچىلىق) يىللار كېلىدۇ، ئۇنىڭدا ھۆل ـ يېغىن كۆپ بولىدۇ، كىشىلەر مېۋىلەردىن تۈرلۈك ئىچىملىكلەرنى سىقىپ چىقىرىدۇ».');
INSERT INTO "saleh" VALUES(12,50,'12:50','(ساقىي پادىشاھقا بېرىپ يۇسۇفنىڭ ئۇنىڭ چۈشىگە بەرگەن تەبىرىنى ئېيتقاندىن كېيىن) پادىشاھ: «ئۇنى ئالدىمغا ئېلىپ كېلىڭلار» دېدى. (پادىشاھنىڭ) ئەلچىسى يۇسۇفنىڭ ئالدىغا بارغاندا، يۇسۇف ئەلچىگە: «خوجايىنىڭغا قايتىپ بېرىپ، ئۇنىڭدىن ھېلىقى قوللىرىنى كېسىۋالغان خوتۇنلارنىڭ ئىشىنى (بىلەمدۇ؟) سوراپ باققىن، شۈبھىسىزكى مېنىڭ پەرۋەردىگارىم ئۇلارنىڭ ھىيلىسىنى ئوبدان بىلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,51,'12:51','پادىشاھ (ھېلىقى خوتۇنلارنى يىغىپ ۋە ئۆزىنىڭ خوتۇنىنى چاقىرىپ): «سىلەر يۇسۇفتىن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغىنىڭلاردا، ھەقىقىي ئەھۋالىڭلار قانداق بولغان؟» دېدى. ئۇلار: «ئاللاھ پاكتۇر، يۇسۇفتە ئازراقمۇ گۇناھ بار دەپ بىلمەيمىز» دېدى. ئەزىزنىڭ خوتۇنى: «ئەمدى ھەقىقىي ئەھۋال ئايدىڭلاشتى، مەن ئۇنىڭ بىلەن مۇناسىۋەت ئۆتكۈزۈشنى تەلەپ قىلغان ئىدىم، يۇسۇف ھەقىقەتەن راستچىللاردىن ئىدى» دېدى.');
INSERT INTO "saleh" VALUES(12,52,'12:52','(يۇسۇف ئېيتتى) «بۇ (يەنى پادىشاھنىڭ ئەلچىسىنى قايتۇرۇۋەتكەنلىكىم) ئەزىز يوق چاغدا خوتۇنىغا خىيانەت قىلمىغانلىقىمنى ۋە خائىنلارنىڭ ھىيلىسىنى ئاللاھنىڭ مۇۋەپپەقىيەتكە ئېرىشتۈرمەيدىغانلىقىنى بىلىپ قىلىشى ئۈچۈندۇر». ');
INSERT INTO "saleh" VALUES(12,53,'12:53','«مەن ئۆزەمنى ئاقلىمايمەن، نەپسى دېگەن نەرسە ھەقىقەتەن يامان ئىشلارغا كۆپ بۇيرۇيدۇ، پەرۋەردىگارىم رەھمەت قىلىپ (ساقلىغان) ئادەم بۇنىڭدىن مۇستەسنا. مېنىڭ پەرۋەردىگارىم ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(12,54,'12:54',' پادىشاھ: «يۇسۇفنى ئالدىمغا ئېلىپ كېلىڭلار، ئۇنى ئۆزۈمنىڭ خاس ئادىمىم قىلىمەن» دېدى. يۇسۇف ئۇنىڭ بىلەن سۆزلىشىۋاتقاندا، ئۇ: «سەن بۈگۈن بىزنىڭ ئالدىمىزدا ھەقىقەتەن مەرتىۋىلىك، ئىشەنچلىك ئادەمسەن» دېدى.');
INSERT INTO "saleh" VALUES(12,55,'12:55',' يۇسۇف: «مېنى (مىسىر) زېمىنىنىڭ خەزىنىلىرىنى باشقۇرۇشقا قويغىن، مەن ھەقىقەتەن (ئۇنى باشقۇرۇش يوللىرىنى) بىلىدىغان، ئوبدان ساقلىيالايدىغان ئادەممەن» دېدى.');
INSERT INTO "saleh" VALUES(12,56,'12:56','بىز يۇسۇفنى (مىسىر) زېمىنىدا خالىغان جايدا تۇرىدىغان (مەملىكەتنى خالىغانچە تەسەررۇپ قىلىدىغان) قىلىپ يەرلەشتۈردۇق، خالىغان بەندىلىرىمىزنى رەھمىتىمىزگە ئېرىشتۈرىمىز، ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيمىز.');
INSERT INTO "saleh" VALUES(12,57,'12:57','ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارغا ئاخىرەتنىڭ ساۋابى ئەلۋەتتە، (بۇ دۇنيانىڭ نېمەتلىرىدىن) ئارتۇقتۇر.');
INSERT INTO "saleh" VALUES(12,58,'12:58',' يۇسۇفنىڭ قېرىنداشلىرى كېلىپ يۇسۇفنىڭ قېشىغا كىردى. يۇسۇف ئۇلارنى تونۇدى، ئۇلار بولسا يۇسۇفنى تونۇمىدى.');
INSERT INTO "saleh" VALUES(12,59,'12:59','يۇسۇف ئۇلارغا ئېھتىياجلىق ئاشلىقنى تەييارلاپ بەرگەندىن كېيىن ئېيتتى: «سىلەر مېنىڭ ئالدىمغا ئاتا بىر ئۇكاڭلارنى (يەنى بۇنيامىننى) ئېلىپ كېلىڭلار، مېنىڭ (ئاشلىقنى) تولۇق ئۆلچەپ بېرىۋاتقانلىقىمنى ۋە ناھايىتى مېھماندوست ئىكەنلىكىمنى كۆرمىدىڭلارمۇ؟.');
INSERT INTO "saleh" VALUES(12,60,'12:60','ئەگەر ئۇنى مېنىڭ ئالدىمغا ئېلىپ كەلمىسەڭلار، سىلەرگە بېرىدىغان ئاشلىقىم يوق، مېنىڭ يېنىمغا يېقىن يولىماڭلار».');
INSERT INTO "saleh" VALUES(12,61,'12:61','ئۇلار: «بىز ئۇنى ئاتىسىدىن سوراپ (سېنىڭ ئالدىڭغا ئېلىپ كېلىشكە) تىرىشىمىز، بىز چوقۇم مۇشۇنداق قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,62,'12:62','يۇسۇف خىزمەتچىلىرىگە: «ئۇلارنىڭ (بىزدىن ئاشلىق سېتىۋالغان) مال ـ مۈلۈكلىرىنى يۈك ـ تاقىلىرىنىڭ ئارىسىغا سېلىپ قويۇڭلار، ئۇلار ئائىلىسىگە قايتىپ بارغاندا، ئۇنى تونۇپ قايتىپ كېلىشى مۇمكىن» دېدى.');
INSERT INTO "saleh" VALUES(12,63,'12:63','ئۇلار ئاتىسىنىڭ ئالدىغا قايتىپ بارغىنىدا: «ئى ئاتىمىز! (مىسىرنىڭ خەزىنىدارى) بىزگە قايتا ئاشلىق بەرمەس بولدى، ئۇكىمىزنى (يەنى بۇنيامىننى) بىز بىلەن بىللە ئەۋەتكىن، (شۇنداق قىلساڭ) ئاشلىق ئالالايمىز، ئۇنى بىز چوقۇم ئوبدان مۇھاپىزەت قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,64,'12:64',' (يەئقۇب) ئۇلارغا ئېيتتى: «ئۇنىڭ توغرىسىدا ئىلگىرى سىلەرگە ئۇنىڭ قېرىندىشى (يەنى يۇسۇف) توغرىسىدا ئىشەنگەندەك ئىشىنەمدىم؟ (مەن سىلەرگە ۋە سىلەرنىڭ ئۇنى ساقلىشىڭلارغا ئىشەنمەيمەن) ئاللاھ ياخشى ساقلىغۇچىدۇر، ھەممىدىن مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(12,65,'12:65','ئۇلار يۈكلىرىنى ئاچقاندا (ئاشلىق سېتىۋالغان) مال مۈلۈكلىرىنىڭ ئۆزلىرىگە قايتۇرۇپ بېرىلگەنلىكىنى كۆردى، ئۇلار: «ئاتىمىز! بىز (بۇنىڭدىن ئارتۇق) يەنە نېمە تەلەپ قىلىمىز؟ بۇ مال ـ مۈلۈكلىرىمىز بىزگە قايتۇرۇپ بېرىلىپتۇ، (ئۇكىمىزنى ئېلىپ يەنە بارساق) ئائىلىمىزگە ئاشلىق ئېلىپ كېلىمىز، ئۇكىمىزنى مۇھاپىزەت قىلىمىز، بىر تۆگە ئاشلىقىنى ئارتۇق ئالىمىز، بۇ (يەنى بىر تۆگە ئاشلىق پادىشاھ ئۈچۈن) ئازغىنا ئاشلىقتۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,66,'12:66','يەئقۇب ئېيتتى: «بۇنيامىننى قوغداشتا ھەممىڭلار ھالاك بولمىساڭلارلا چوقۇم ماڭا قايتۇرۇپ ئېلىپ كېلىشكە ئاللاھنىڭ نامى بىلەن قەسەم قىلىپ چىن ۋەدە بەرمىگۈچە ئۇنى سىلەر بىلەن بىللە ئەۋەتمەيمەن». ئۇلار (يەنى ئوغۇللىرى) ئۇنىڭغا قەسەم قىلىپ چىن ۋەدە بەرگەندىن كېيىن، يەئقۇب: «بىزنىڭ ئېيتقان سۆزلىرىمىزگە ئاللاھ گۇۋاھتۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,67,'12:67','(ئۇ يەنە) «ئى ئوغۇللىرىم! (مىسىرغا) ھەممىڭلار بىر دەرۋازىدىن كىرمەي، باشقا- باشقا دەرۋازىلاردىن كىرىڭلار. ئاللاھنىڭ ھۆكمى ئالدىدا مەن سىلەردىن ھېچ نەرسىنى دەپئى قىلالمايمەن، ھۆكۈم پەقەت ئاللاھقىلا خاس، ئاللاھقا تەۋەككۈل قىلدىم، تەۋەككۈل قىلغۇچىلار ئاللاھقىلا تەۋەككۈل قىلسۇن!» دېدى.');
INSERT INTO "saleh" VALUES(12,68,'12:68','ئۇلار ئاتىسىنىڭ بۇيرۇقى بويىچە باشقا ـ باشقا دەرۋازىلاردىن كىرگەندە، (بۇ) ئاللاھنىڭ ھۆكمى ئالدىدا ئۇلار ئۈچۈن ھېچ نەرسىگە دال بولالمىدى، بۇ پەقەت يەئقۇبنىڭ كۆڭلىدىكى ئۈمىدىنىلا ئىپادىلىدى، خالاس. بىز يەئقۇبقا (ۋەھىي ئارقىلىق) بىلدۈرگەن ئىدۇق، شۇڭا ئۇ كەڭ بىلىم ئىگىسىدۇر، لېكىن كىشىلەرنىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(12,69,'12:69',' ئۇلار (يەنى يەئقۇبنىڭ بالىلىرى) يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئۇكىسىنى قۇچاقلىدى ۋە: «مەن ھەقىقەتەن سېنىڭ قېرىندىشىڭ بولىمەن، ئۇلارنىڭ بىزگە قىلغان ئىشلىرىدىن قايغۇرمىغىن» دېدى.');
INSERT INTO "saleh" VALUES(12,70,'12:70','يۇسۇف ئۇلارنى ئېھتىياجلىق ئاشلىق بىلەن تەمىنلىگەندىن كېيىن، قەدەھنى ئۇكىسىنىڭ يۈكى ئىچىگە سېلىپ قويدى. ئاندىن بىر جاكاچى چىقىپ: «ئى كارۋانلار! سىلەر چوقۇم ئوغرى ئىكەنسىلەر» دەپ توۋلىدى. ');
INSERT INTO "saleh" VALUES(12,71,'12:71','(كارۋاندىكىلەر) ئۇلارغا قاراپ: «نېمەڭلارنى يوقىتىپ قويدۇڭلار؟» دېدى.');
INSERT INTO "saleh" VALUES(12,72,'12:72','ئۇلار: «پادىشاھنىڭ (تامغىسى بېسىلغان) قەدەھنى يوقىتىپ قويدۇق، ئۇنى تېپىپ كەلگەن كىشىگە (مۇكاپات ئۈچۈن) بىر تۆگە ئاشلىق بېرىلىدۇ، مەن بۇنىڭغا كېپىل» دېدى.');
INSERT INTO "saleh" VALUES(12,73,'12:73','ئۇلار: «ئاللاھنىڭ نامى بىلەن قەسەمكى، سىلەر بىلىسىلەر، بىز بۇ يەرگە بۇزغۇنچىلىق قىلىش ئۈچۈن كەلمىدۇق، بىز ئوغرى ئەمەسمىز» دېدى. ');
INSERT INTO "saleh" VALUES(12,74,'12:74',' ئۇلار (يەنى يۇسۇفنىڭ خىزمەتچىلىرى): «سىلەرنىڭ شەرىئىتىڭلاردا ئوغرىنىڭ جازاسى نېمە؟ (ئەگەر قەدەھ يۈكۈڭلاردىن چىقىپ قېلىپ) يالغانچى بولۇپ قالساڭلارچۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(12,75,'12:75','ئۇلار: «ئۇنىڭ جازاسى يۈكىدىن قەدەھ تېپىلغان ئادەمنى قۇل قىلىش، زالىملارغا بىز مۇشۇنداق جازا بېرىمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,76,'12:76','ئۇلار يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىنى ئاختۇرۇشتىن ئىلگىرى، ئۇلارنىڭ يۈكىنى ئاختۇرۇشقا باشلىدى، ئاندىن يۇسۇفنىڭ ئۇكىسىنىڭ يۈكىدىن قەدەھنى تېپىپ چىقتى. بىز يۇسۇفكە مۇشۇ تەدبىرنى كۆرسەتتۇق، (مىسىر) پادىشاھىنىڭ قانۇنى بويىچە يۇسۇف ئۇكىسىنى ئېلىپ قالالمايتتى، لېكىن ئاللاھ ئۇنىڭ شۇنداق قىلىشىنى خالىدى، بىز خالىغان ئادەمنى (يۇسۇفنى كۆتۈرگەندەك) يۇقىرى دەرىجىلەرگە كۆتۈرىمىز، ھەربىر بىلىمدار ئۈستىدە ئۇنىڭدىنمۇ بىلىمدار زات بار.');
INSERT INTO "saleh" VALUES(12,77,'12:77','ئۇلار: «ئەگەر ئۇ ئوغرىلىق قىلغان بولسا، ئۇنىڭ قېرىندىشىمۇ (يەنى يۇسۇفمۇ) ئىلگىرى ئوغرىلىق قىلغان» دېيىشتى. يۇسۇف (ئۇلارنىڭ) بۇ سۆزىنى ئىچىدە بىلدى، لېكىن ئۇلارغا مەلۇم قىلمىدى، يۇسۇف (ئىچىدە): «سىلەرنىڭ مەۋقەيىڭلار ئەڭ ياماندۇر، سىلەرنىڭ (يۇسۇف ۋە ئۇنىڭ ئۇكىسى توغرىسىدا) قىلغان سۆزۈڭلارنىڭ يالغانلىقىنى ئاللاھ ئوبدان بىلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,78,'12:78','ئۇلار: «ئى ئالىيجاناب زات! ئۇنىڭ (ئۇنىڭدىن ئايرىلالمايدىغان) بىر قېرى ئاتىسى بار، ئۇنىڭ ئورنىغا بىزنىڭ بىرىمىزنى ئېلىپ قالغىن، بىز سېنى ھەقىقەتەن ياخشىلىق قىلغۇچىلاردىن ھېسابلايمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,79,'12:79','يۇسۇف: «خۇدا ساقلىسۇن، نەرسىمىزنى كىمنىڭ يۈكىدىن تاپقان بولساق، شۇنى ئېلىپ قالىمىز، بولمىسا بىز چوقۇم زالىملاردىن بولۇپ قالىمىز» دېدى.');
INSERT INTO "saleh" VALUES(12,80,'12:80',' ئۇلار (بۇنيامىننىڭ ئورنىغا ئۆز ئارىسىدىن بىرسىنى ئېلىپ قېلىشقا يۇسۇفنىڭ ماقۇل بولۇشىدىن) ئۈمىدسىزلەنگەندىن كېيىن، چەترەك يەرگە بېرىپ مەخپىي مەسلىھەتلەشتى، ئۇلارنىڭ چوڭى ئېيتتى: «ئاتاڭلارنىڭ سىلەردىن قەسەم قىلدۇرۇپ ۋەدە ئالغانلىقىنى ۋە ئىلگىرى يۇسۇف توغرىسىدىكى خاتالىقىڭلارنى بىلەمسىلەر؟ ئاتام ماڭا رۇخسەت قىلمىغىچە ياكى ئاللاھ مەن ئۈچۈن ھۆكۈم چىقارمىغىچە بۇ يەردىن ھەرگىز ئايرىلمايمەن، ئاللاھ ئەڭ ئادىل ھۆكۈم قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(12,81,'12:81','سىلەر قايتىپ كېتىپ ئاتاڭلار بىلەن كۆرۈشۈڭلار، ئاندىن ئېيتىڭلاركى، «ئى ئاتىمىز! سېنىڭ ئوغلۇڭ راستلا ئوغرىلىق قىلدى، بىز پەقەت بىلگەن نەرسىمىز ئۈستىدىلا گۇۋاھلىق بېرىۋاتىمىز، بىز غەيبنى بىلمەيمىز (يەنى ساڭا ۋەدە بەرگەن چېغىمىزدا ئۇنىڭ ئوغرىلىق قىلىدىغانلىقىنى بىلمىدۇق).');
INSERT INTO "saleh" VALUES(12,82,'12:82','بىز تۇرغان شەھەردىن (يەنى مىسىردىن) ۋە بىز بىلەن بىللە ماڭغان كارۋاندىن سوراپ باققىن، بىزنىڭ سۆزىمىز چوقۇم راست».');
INSERT INTO "saleh" VALUES(12,83,'12:83','يەئقۇب ئېيتتى: «ئۇنداق ئەمەس، نەپسىڭلار سىلەرگە بۇ ئىشنى چىرايلىق كۆرسەتتى، مەن پەقەت چىرايلىقچە سەۋر قىلىمەن، ئۇلارنىڭ (يەنى ئۈچ ئوغلۇمنىڭ) ھەممىسىنى ماڭا دەرقەمتە قىلىپ بېرىشنى ئاللاھتىن تىلەيمەن. ئاللاھ ھەقىقەتەن (مېنىڭ ھالىمنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر».');
INSERT INTO "saleh" VALUES(12,84,'12:84',' يەئقۇب ئۇلاردىن يۈز ئۆرۈپ: «ئىسىت يۇسۇف!» دېدى. قايغۇدىن (كۆپ يىغلاپ) ئىككى كۆزى ئاقىرىپ كەتتى (يەنى ئىككى كۆزى كۆرمەس بولۇپ قالدى)، ئۇ تولۇپ تاشقان ئاچچىقىنى ئىچىگە يۇتقان ئىدى.');
INSERT INTO "saleh" VALUES(12,85,'12:85','ئۇلار: «ئاللاھنىڭ نامى بىلەن قەسەمكى، سەن يۇسۇفنى ياد ئېتىۋېرىپ (ھەسرەتتىن) ھالاك بولۇشقا تاس قالىدىغان ياكى ھالاك بولىدىغان بولدۇڭ» دېدى.');
INSERT INTO "saleh" VALUES(12,86,'12:86','يەئقۇب ئېيتتى: «مەن قايغۇ ـ ھەسرىتىمنى پەقەت ئاللاھقىلا ئېيتىمەن، ئاللاھنىڭ بىلدۈرۈشى بىلەن سىلەر بىلمەيدىغان نەرسىلەرنى بىلىمەن.');
INSERT INTO "saleh" VALUES(12,87,'12:87',' ئى ئوغۇللىرىم! بېرىڭلار، يۇسۇفنى ۋە ئۇنىڭ ئۇكىسىنى ئىزدەڭلار، ئاللاھنىڭ رەھمىتىدىن نائۈمىد بولماڭلار، شۈبھىسىزكى، پەقەت كاپىر قەۋملا ئاللاھنىڭ رەھمىتىدىن ئۈمىدسىزلىنىدۇ».');
INSERT INTO "saleh" VALUES(12,88,'12:88','ئۇلار يۇسۇفنىڭ قېشىغا كىرگىنىدە: «ئى ئالىيجاناب زات! بىزگە ۋە ئائىلىمىزگە ئاچارچىلىق يەتتى، (ئالدىڭغا) ناچار، ئۆتمەس ماللار بىلەن كېلىپ قالدۇق، بىزگە يېتەرلىك ئاشلىق بەرگىن، بىزگە سەدىقە قىلغىن، ئاللاھ سەدىقە قىلغۇچىلارنى ھەقىقەتەن مۇكاپاتلايدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,89,'12:89','يۇسۇف: «سىلەر نادان ۋاقتىڭلاردا يۇسۇفكە ۋە ئۇنىڭ ئۇكىسىغا نېمىلەر قىلغانلىقىڭلارنى بىلەمسىلەر؟» دېدى.');
INSERT INTO "saleh" VALUES(12,90,'12:90','ئۇلار: «سەن راستلا يۇسۇفمۇ؟» دېدى. ئۇ: «مەن يۇسۇف، بۇ مېنىڭ ئىنىم. ئاللاھ بىزگە مەرھەمەت قىلدى. كىمكى ھەقىقەتەن تەقۋادارلىق قىلىدىكەن، سەۋر قىلىدىكەن (ياخشى مۇكاپاتقا ئېرىشىدۇ)، چۈنكى ئاللاھ ياخشى ئىش قىلغۇچىلارنىڭ ئەجرىنى بىكار قىلىۋەتمەيدۇ» دېدى.');
INSERT INTO "saleh" VALUES(12,91,'12:91','ئۇلار «ئاللاھنىڭ نامى بىلەن قەسەمكى، ئاللاھ سېنى ھەقىقەتەن بىزدىن ئارتۇق قىلىپتۇ، بىز جەزمەن خاتا قىلىپتۇق» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,92,'12:92','يۇسۇف ئېيتتى: «بۈگۈن سىلەر ئەيىبلەشكە ئۇچرىمايسىلەر، ئاللاھ سىلەرنى كەچۈرسۇن، ئاللاھ ئەڭ مەرھەمەتلىك زاتتۇر.');
INSERT INTO "saleh" VALUES(12,93,'12:93',' سىلەر مېنىڭ بۇ كۆڭلىكىمنى ئېلىپ بېرىپ ئاتامنىڭ يۈزىگە تاشلاڭلار، كۆزى ئېچىلىدۇ، پۈتۈن ئائىلەڭلاردىكىلەرنى ئېلىپ مېنىڭ يېنىمغا كېلىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(12,94,'12:94','ب ئەلەيھىسسالام): «مەن چوقۇم يۇسۇفنىڭ ھىدىنى ئېلىۋاتىمەن، سىلەر مېنى ئالجىپ قالغان دېمەيدىغان بولساڭلار (يۇسۇفنى ئەلۋەتتە ھايات دەيتتىم)» دېدى.');
INSERT INTO "saleh" VALUES(12,95,'12:95','ئۇلار (يەنى يەئقۇبنىڭ نەۋرىلىرى ۋە يېنىدىكى كىشىلەر): «ئاللاھنىڭ نامى بىلەن قەسەمكى، ھەقىقەتەن سەن تېخى يەنىلا بۇرۇنقى قايمۇقۇشۇڭدا ئىكەنسەن» دېدى. ');
INSERT INTO "saleh" VALUES(12,96,'12:96','خۇشخەۋەرچى كېلىپ كۆڭلەكنى يەئقۇبنىڭ يۈزىگە تاشلىدى، ئۇنىڭ كۆزى ئېچىلدى، يەئقۇب: «مەن سىلەرگە، ئاللاھنىڭ بىلدۈرۈشى بىلەن سىلەر بىلمىگەن نەرسىلەرنى بىلىمەن، دېمىگەنمىدىم!» دېدى.');
INSERT INTO "saleh" VALUES(12,97,'12:97','ئۇلار: «ئى ئاتىمىز! گۇناھلىرىمىز ئۈچۈن بىزگە مەغپىرەت تىلىگىن، بىز ھەقىقەتەن خاتالاشتۇق» دېيىشتى.');
INSERT INTO "saleh" VALUES(12,98,'12:98','يەئقۇب: «پەرۋەردىگارىمدىن سىلەرگە مەغپىرەت تىلەيمەن. ئاللاھ گۇناھلارنى ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر» دېدى.');
INSERT INTO "saleh" VALUES(12,99,'12:99','ئۇلار يۇسۇفنىڭ ئالدىغا كىرگەندە، يۇسۇف ئاتا ـ ئانىسىنى قۇچاقلىدى ۋە: «خۇدا خالىسا، مىسىرغا ئامان ـ ئېسەن كىرىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(12,100,'12:100','ئاتا ـ ئانىسىنى تەختىدە (يېنىدا) ئولتۇرغۇزدى، ئۇلار (شاھلارغا تازىم قىلىش ئادىتى بويىچە) يۇسۇفكە سەجدە قىلىشتى. ئۇ: «ئى ئاتا! مانا بۇ، بۇرۇن (كىچىك ۋاقتىمدا) كۆرگەن چۈشۈمنىڭ تەبىرىدۇر، ئۇ چۈشۈمنى پەرۋەردىگارىم ھەقىقەتەن راستىغا چىقاردى. پەرۋەردىگارىم مېنى زىنداندىن چىقىرىش بىلەن، قېرىنداشلىرىم بىلەن مېنىڭ ئارامنى شەيتان بۇزغاندىن كېيىن، سىلەرنى سەھرادىن بۇ يەرگە ئېلىپ كېلىش بىلەن ماڭا ئېھسان قىلدى. پەرۋەردىگارىم خالىغىنىنى (ئىشقا ئاشۇرۇشقا) ھەقىقەتەن تەدبىرلىكتۇر، ئۇ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(12,101,'12:101',' پەرۋەردىگارىم! ماڭا ھەقىقەتەن پادىشاھلىق ئاتا قىلدىڭ، چۈش تەبىرىنى بىلدۈردۈڭ، ئى ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى زات! دۇنيا ۋە ئاخىرەتتە مېنىڭ ئىگەمسەن، مېنى مۇسۇلمان پېتىمچە قەبزى روھ قىلغىن، مېنى ياخشى بەندىلەر قاتارىدا قىلغىن» دېدى. ');
INSERT INTO "saleh" VALUES(12,102,'12:102','مانا بۇلار (يەنى يۇسۇف قىسسەسى) بىر قىسىم غەيب خەۋەرلىرى بولۇپ، بىز ئۇنى ساڭا ۋەھىي قىلدۇق. ئۇلار (يەنى يۇسۇفنىڭ قېرىنداشلىرى) (ئۇنىڭغا قارشى) چارە -تەدبىر تۈزۈپ بىر قارارغا كېلىشكەندە، سەن ئۇلارنىڭ يېنىدا يوق ئىدىڭ.');
INSERT INTO "saleh" VALUES(12,103,'12:103','سەن گەرچە كىشىلەرنىڭ ئىمان ئېيتىشىغا ھېرىس بولساڭمۇ ،(لېكىن) ئۇلارنىڭ تولىسى ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(12,104,'12:104','قۇرئاننى تەبلىغ قىلغانلىقىڭغا ئۇلاردىن ھېچقانداق ئەجىر تەلەپ قىلمايسەن، قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(12,105,'12:105','ئاسمانلاردا ۋە زېمىندا (ئاللاھنىڭ قۇدرىتىنىڭ) نۇرغۇن ئالامەتلىرى باركى، ئۇلار ئۇنىڭ يېنىدىن دىققەت قىلماستىن ئۆتۈپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(12,106,'12:106',' ئۇلارنىڭ تولىسى ئاللاھقا شېرىك كەلتۈرۈپ تۇرۇپ ئىشىنىدۇ (يەنى ئاللاھنى ياراتقۇچى، رىزىق بەرگۈچى دەپ ئىقرار قىلىش بىلەن بىللە، باشقا نەرسىلەرگىمۇ چوقۇنىدۇ).');
INSERT INTO "saleh" VALUES(12,107,'12:107','ئۇلار ئاللاھنىڭ ئازابىدىن، بىرەر جازانىڭ ئۆزلىرىگە چۈشۈشىدىن ياكى ئۇلار سەزمەستىن قىيامەتنىڭ تۇيۇقسىز يېتىپ كېلىشىدىن ئەمىن بولدىمۇ؟');
INSERT INTO "saleh" VALUES(12,108,'12:108','ئېيتقىنكى، «بۇ مېنىڭ يولۇمدۇر، (كىشىلەرنى) ئاللاھقا دەۋەت قىلىمەن، مەن ۋە ماڭا ئەگەشكەنلەر روشەن دەلىلگە ئاساسلىنىمىز. ئاللاھ پاكتۇر، مەن مۇشرىكلاردىن ئەمەسمەن».');
INSERT INTO "saleh" VALUES(12,109,'12:109',' بىز سەندىن ئىلگىرى پەقەت شەھەر ئاھالىسىدىن بولغان ئەرلەرنى پەيغەمبەر قىلىپ، ئۇلارغا ۋەھىي قىلدۇق، ئۇلار(يەنى پەيغەمبەرلەرگە چىنپۈتمىگۈچىلەر) يەر يۈزىدە سەير قىلىپ يۈرۈپ ئۆزلىرىدىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئاخىرەت يۇرتى تەقۋادارلىق قىلغانلار ئۈچۈن ئەلۋەتتە ياخشىدۇر، سىلەر چۈشەنمەمسىلەر.');
INSERT INTO "saleh" VALUES(12,110,'12:110','ھەتتا پەيغەمبەرلەر (قەۋمىنىڭ ئىمان ئېيتىشىدىن) ئۈمىدسىزلەنگەن ۋە ( قەۋمى تەرىپىدىن) يالغانغا چىقىرىلغانلىقىغا جەزم قىلغان چاغدا، ئۇلارغا بىزنىڭ ياردىمىمىز يېتىپ كەلدى، بىز خالىغان ئادەملەرنى قۇتقۇزدۇق، بىزنىڭ ئازابىمىز گۇناھكار قەۋمدىن قايتۇرۇلمايدۇ. ');
INSERT INTO "saleh" VALUES(12,111,'12:111','ئۇلارنىڭ قىسسەسىدىن ئەقىل ئىگىلىرى ئەلۋەتتە ئىبرەت ئالىدۇ. (قۇرئان) يالغاندىن توقۇلغان سۆز ئەمەس، لېكىن، ئۇ ئۆزىدىن ئىلگىرى نازىل بولغان (ساماۋى) كىتابلارنى تەستىق قىلغۇچىدۇر، ھەممە نەرسىنى ئىنچىكە بايان قىلغۇچىدۇر، ئىمان ئېيتىدىغان قەۋم ئۈچۈن ھىدايەتتۇر ۋە رەھمەتتۇر.');
INSERT INTO "saleh" VALUES(13,1,'13:1','ئەلىف، لام، مىم، را. بۇ، كىتاب (يەنى قۇرئان) نىڭ ئايەتلىرىدۇر. (قۇرئاندا) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان نەرسىلەر ھەقتۇر، لېكىن ئىنسانلارنىڭ تولىسى ئۇنىڭغا (يەنى قۇرئاننىڭ ئاللاھ تەرىپىدىن نازىل بولغانلىقىغا) ئىشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(13,2,'13:2','ئاللاھ ئاسمانلارنى تۈۋرۈكسىز بەرپا قىلدى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر، ئاندىن ئۇ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى. كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى، (ئۇلارنىڭ) ھەربىرى مۇئەييەن مۇددەتكىچە (يەنى پانى دۇنيانىڭ تۈگەيدىغان ۋاقتىغىچە) سەير قىلىدۇ، ئاللاھ (ئۆز ھېكمىتى ۋە قۇدرىتى بىلەن) مەخلۇقاتنىڭ ئىشلىرىنى باشقۇرۇپ تۇرىدۇ، سىلەرنى پەرۋەردىگارىڭلارغا مۇلاقات بولۇشقا ئىشەنسۇن دەپ، (قۇدرىتىنىڭ) دەلىللىرىنى تەپسىلىي بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(13,3,'13:3','ئاللاھ يەر يۈزىنى (ئۇزۇنىسىغا ۋە توغرىسىغا) سوزۇپ كەڭ ياراتتى، يەر يۈزىدە تاغلارنى ۋە دەريالارنى ياراتتى، مېۋىلەرنىڭ ھەربىر تۈرىنى ئەركەك ـ چىشى ـ ئىككى جىنسلىق قىلىپ ياراتتى. كېچىنى كۈندۈزگە كىرىشتۈردى (يەنى كېچىنىڭ قاراڭغۇلۇقى بىلەن كۈندۈزنىڭ يورۇقلۇقىنى ياپتى). بۇلاردا تەپەككۇر قىلىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىللەر بار.');
INSERT INTO "saleh" VALUES(13,4,'13:4','يەر يۈزىدە بىر - بىرىگە تۇتىشاڭغۇ قىتئەلەر بار ۋە تۈرلۈك زىرائەتلەر بار، كۆپ شاخلىق بولغان ۋە كۆپ شاخلىق بولمىغان (بىر ئۇرۇقتىن بىر قانچە تال ئۈنۈپ چىققان، بىر ئۇرۇقتىن پەقەت بىر تال ئۈنۈپ چىققان) خورما دەرەخلىرى بار، (ئۇلارنىڭ ھەممىسى) بىر خىل سۇ بىلەن سۇغىرىلىدۇ، ئۇلارنىڭ بەزىسىنى بەزىسىدىن تەمدە ئارتۇق قىلىمىز، بۇلاردا چۈشىنىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(13,5,'13:5','(ئى مۇھەممەد!) ئەگەر (بىرەر نەرسىدىن) ئەجەبلىنىدىغان بولساڭ، ئۇلارنىڭ (يەنى كۇففارلارنىڭ): «بىز (ئۆلۈپ) توپا بولۇپ كەتكەندىن كېيىن قايتا تىرىلەمدۇق؟» دېگەن سۆزى ئەجەبلىنىشكە لايىقتۇر. ئەنە شۇلار پەرۋەردىگارىنى ئىنكار قىلغانلاردۇر، (قىيامەت كۈنى) ئەنە شۇلارنىڭ بويۇنلىرىغا تاقاقلار سېلىنىدۇ، ئەنە شۇلار دوزاخ ئەھلى بولۇپ، دوزاختا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(13,6,'13:6','ئۇلار سەندىن (ئاللاھنىڭ) ئېھسانىدىن ئىلگىرى ئازابنىڭ چاپسانراق چۈشۈشىنى تەلەپ قىلىدۇ، ئۇلاردىن بۇرۇن ئاللاھنىڭ ئازابىغا دۇچار بولغان ئۈممەتلەر ئۆتكەن، ئىنسانلار زۇلۇم قىلسىمۇ، پەرۋەردىگارىڭ ھەقىقەتەن ئۇلارنى مەغپىرەت قىلىدۇ. پەرۋەردىگارىڭ (گۇناھلارغا چۆمۈپ تەۋبە قىلمىغانلارنى) شەك - شۈبھىسىز قاتتىق جازالايدۇ.');
INSERT INTO "saleh" VALUES(13,7,'13:7',' كۇففارلار، نېمىشقا ئۇنىڭغا پەرۋەردىگارى تەرىپىدىن بىرەر مۆجىزە نازىل بولمىدى؟ دېيىشىدۇ، سەن پەقەت بىر ئاگاھلاندۇرغۇچى. ھەر قەۋمنىڭ بىر ھىدايەت قىلغۇچىسى (يەنى پەيغەمبىرى) بولىدۇ (مۆجىزىلەرگە كەلسەك، ئۇ ئاللاھنىڭ ئىلكىدىكى ئىش).');
INSERT INTO "saleh" VALUES(13,8,'13:8','ھەر بىر (ھامىلىدار) ئايالنىڭ قورسىقىدىكىنى ئاللاھ بىلىدۇ (يەنى ئوغۇلمۇ ـ قىزمۇ، بىرمۇ ـ كۆپمۇ، چوڭمۇ ـ كىچىكمۇ، چىرايلىقمۇ ـ سەتمۇ، بەختلىكمۇ ـ بەختسىزمۇ، ئۆمرى ئۇزۇنمۇ ـ قىسقىمۇ ـ ھەممىسىنى ئاللاھ بىلىدۇ). بەچچىدانلارنىڭ كىچىكلەپ كەتكەنلىكى ياكى يوغىناپ كەتكەنلىكىنىمۇ (يەنى بالىنىڭ مۇددىتى توشماي تۇغۇلىدىغانلىقىنى ياكى مۇددىتى ئېشىپ كېتىپ تۇغۇلىدىغانلىقىنى) (ئاللاھ) بىلىدۇ. ئاللاھنىڭ دەرگاھىدا ھەممە نەرسە ئۆلچەملىكتۇر.');
INSERT INTO "saleh" VALUES(13,9,'13:9','ئاللاھ مەخپىي ۋە ئاشكارا ئىشلارنىڭ ھەممىسىنى بىلگۈچىدۇر، ھەممىدىن بۈيۈكتۇر، ھەممىدىن ئۈستۈندۇر.');
INSERT INTO "saleh" VALUES(13,10,'13:10','سىلەرنىڭ ئىچىڭلاردىن يوشۇرۇن سۆز قىلغان، ئاشكارا سۆز قىلغان، كېچىسى يوشۇرۇنغان، كۈندۈزى ئاشكارا يۈرگەنلەرنىڭ ھەممىسى (ئاللاھ ئۈچۈن ئېيتقاندا) ئوخشاشتۇر (يەنى ئاللاھ ھەممىنى بىلىپ تۇرىدۇ). ');
INSERT INTO "saleh" VALUES(13,11,'13:11','ھەربىر ئادەمنىڭ ئالدى ـ كەينىدە ئاللاھنىڭ ئەمرى بويىچە ئۇنى قوغدايدىغان نۆۋەتچى پەرىشتىلەر بار. ھەر قانداق بىر قەۋم ئۆزىنىڭ ئەھۋالىنى ئۆزگەرتمىگۈچە (يەنى ئاللاھنىڭ بەرگەن نېمەتلىرىگە تۇزكورلۇق قىلىپ گۇناھلارغا چۆممىگۈچە) ئاللاھ ئۇلارنىڭ ئەھۋالىنى ئۆزگەرتمەيدۇ (يەنى ئاللاھ ئۇلارغا بەرگەن نېمەت، خاتىرجەملىك ۋە ئىززەت ـ ھۆرمەتنى ئېلىپ تاشلىمايدۇ)، ئاللاھ بىرەر قەۋمنى ھالاك قىلماقچى (ياكى ئازابلىماقچى) بولسا، ئۇنىڭغا قارشى تۇرغىلى بولمايدۇ، ئۇلارغا ئازابنى دەپئى قىلىدىغان ئاللاھتىن باشقا ئىگىمۇ بولمايدۇ.');
INSERT INTO "saleh" VALUES(13,12,'13:12',' ئاللاھ (چاقماقتىن) قورقىتىش، (يامغۇرغا) ئۈمىد تۇغدۇرۇش ئۈچۈن، سىلەرگە چاقماقنى كۆرسىتىدۇ، (قۇدرىتى بىلەن) قويۇق بۇلۇتلارنى پەيدا قىلىدۇ.');
INSERT INTO "saleh" VALUES(13,13,'13:13',' گۈلدۈرماما ئاللاھنى مەدھىيەلەش بىلەن تەسبىھ ئېيتىدۇ، پەرىشتىلەرمۇ ئاللاھتىن قورقۇپ تەسبىھ ئېيتىدۇ، ئاللاھ چاقماق چۈشۈرۈپ، ئۇنىڭ بىلەن ئۆزى خالىغان ئادەمنى ھالاك قىلىدۇ. ئۇلار ئاللاھ توغرىسىدا دەتالاش قىلىشىدۇ، ئاللاھنىڭ تەدبىرى كۈچلۈكتۇر.');
INSERT INTO "saleh" VALUES(13,14,'13:14','ھەقىقىي قىلىنغان دۇئا ئاللاھقا قارىتىلىدۇ، (يەنى ئىخلاس بىلەن قىلىنغان دۇئانى ئاللاھ ئىجابەت قىلىدۇ). كۇففارلارنىڭ ئاللاھنى قويۇپ، دۇئالىرىنى قاراتقان مەبۇدلىرى كۇففارلارنىڭ ھېچقانداق دۇئاسىنى ئىجابەت قىلمايدۇ. ئۇلار (يەنى كاپىرلار) شۇنداق بىر ئادەمگە ئوخشايدۇكى، ئۇ ئاغزىمىزغا سۇ چۈشسۇن دەپ (نېرىدىن) ئىككى ئالىقىنىنى سۇغا قارىتىپ ئېچىپ تۇرىدۇ، (ئەمەلىيەتتە سۇ ئاڭلىمايدىغان، ھېس ـ تۇيغۇسىز نەرسە بولغاچقا) سۇ ھەرگىز ئۇنىڭ ئاغزىغا چۈشمەيدۇ، كاپىرلارنىڭ مەبۇدلىرىغا قىلغان دۇئاسى پۈتۈنلەي بىكاردۇر. ');
INSERT INTO "saleh" VALUES(13,15,'13:15','ئاسمانلاردىكىلەر ۋە زېمىندىكىلەر (يەنى پەرىشتىلەر، ئىنسانلار ۋە جىنلار) ئىختىيارى ۋە مەجبۇرى يوسۇندا ئاللاھقا بويسۇنىدۇ، ئەتىگەن - ئاخشامدا (يەنى داۋاملىق تۈردە) ئۇلارنىڭ سايىلىرىمۇ بويسۇنىدۇ.');
INSERT INTO "saleh" VALUES(13,16,'13:16','(ئى مۇھەممەد! مۇشرىكلارغا) «ئاسمانلارنىڭ، زېمىننىڭ پەرۋەردىگارى كىم؟» دېگىن، «(ئۇلارنىڭ پەرۋەردىگارى) ئاللاھ» دېگىن. (ئۇلارغا) «سىلەر ئاللاھنى قويۇپ ئۆزەڭلارغا پايدا ـ زىيان يەتكۈزەلمەيدىغان مەبۇدلارنى (ئاللاھقا شېرىك) قىلىۋالدىڭلارمۇ؟» دېگىن؟ «كور (يەنى كاپىر) بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن) باراۋەر بولامدۇ ياكى قاراڭغۇلۇق (يەنى گۇمراھلىق) بىلەن نۇر (يەنى ھىدايەت) باراۋەر بولامدۇ؟» دېگىن، ئۇلار ئاللاھ ياراتقانغا ئوخشاش قانداقتۇر مەخلۇقاتلارنى ياراتقان مەبۇدلارنى پۈتۈن مەخلۇقاتنى ياراتقان ئاللاھقا شېرىك قىلىۋېلىپ، ئاللاھنىڭ ياراتقىنى بىلەن مەبۇدلىرىنىڭ ياراتقىنىنى ئايرىيالماي قالدىمۇ؟ ئاللاھ ھەممە نەرسىنى ياراتقۇچىدۇر، ئاللاھ بىردۇر، ھەممىگە غالىبتۇر.');
INSERT INTO "saleh" VALUES(13,17,'13:17','ئاللاھ ئاسماندىن يامغۇر ياغدۇردى، (يامغۇر سۈيى) جىلغىلاردا لىپمۇلىق ئاقتى، كەلكۈن سۇ ئۆزىنىڭ ئۈستىدە كۆپۈكلەرنى لەيلىتىپ ئاقتى، كىشىلەرنىڭ زىننەت بۇيۇملىرىنىڭ ياكى ئەسۋاب ياساش ئۈچۈن ئوتقا سېلىپ ئېرىتكەن مەدەنلەرنىڭمۇ شۇنىڭغا ئوخشاش كۆپۈكى بار (يەنى مەدەنلەرنىڭ كۆپۈكىمۇ خۇددى كەلكۈن سۇنىڭ كۆپۈكىگە ئوخشاش پايدىسىزدۇر). ئاللاھ ھەق بىلەن باتىلنى بۇ مىسال بىلەن ئەنە شۇنداق بايان قىلىدۇ، كۆپۈك بولسا ئېقىپ تۈگەيدۇ، ئىنسانلارغا پايدىلىق نەرسە زېمىندا قېلىپ قالىدۇ، ئاللاھ مىساللارنى ئەنە شۇنداق بايان قىلىدۇ. ');
INSERT INTO "saleh" VALUES(13,18,'13:18','ئاللاھنىڭ (ئىمانغا قىلغان دەۋىتىنى) قوبۇل قىلغانلارغا ئەڭ ئوبدان مۇكاپات بېرىلىدۇ، قوبۇل قىلمىغانلارنىڭ ئىگىدارچىلىقىدا زېمىندىكى پۈتۈن مال ـ مۈلۈك، يەنە شۇنىڭدەك بىر ھەسسە كېلىدىغان مال ـ مۈلۈك بولغان تەقدىردىمۇ، ئۇلار (ئاخىرەتتە ئاللاھنىڭ ئازابىدىن قۇتۇلۇش ئۈچۈن)، ئەلۋەتتە، ئۇنى پىدا قىلغان بولاتتى. ئۇلاردىن قاتتىق ھېساب ئېلىنىدۇ، ئۇلارنىڭ بارىدىغان جايى دوزاخ بولىدۇ، (دوزاخ) نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(13,19,'13:19',' (ئى مۇھەممەد!) پەرۋەردىگارىڭ تەرىپىدىن ساڭا نازىل قىلىنغان نەرسىنىڭ ھەق ئىكەنلىكىنى بىلىدىغان ئادەم، (ھەقنى كۆرمەيدىغان دىلى) كور ئادەم بىلەن ئوخشاش بولامدۇ؟ (ئاللاھنىڭ ئايەتلىرىدىن) پەقەت ئەقىل ئىگىلىرىلا پەند ـ نەسىھەت ئالىدۇ. ');
INSERT INTO "saleh" VALUES(13,20,'13:20','ئۇلار ئاللاھنىڭ ئەھدىگە (يەنى ئاللاھ تاپشۇرغان ئىشلارغا) ۋاپا قىلىدۇ، بەرگەن ۋەدىسىنى بۇزمايدۇ.');
INSERT INTO "saleh" VALUES(13,21,'13:21','ئۇلار ئاللاھنىڭ (خىش ـ ئەقرىبالارغا) سىلە ـ رەھىم قىلىشتىن ئىبارەت ئەمرىنى بەجا كەلتۈرىدۇ، ئاللاھتىن قورقىدۇ، قاتتىق ھېساب ئېلىنىشىدىن قورقىدۇ.');
INSERT INTO "saleh" VALUES(13,22,'13:22','پەرۋەردىگارىنىڭ رازىلىقىنى كۆزلەپ (يەتكەن كۈلپەتلەرگە) سەۋر قىلغانلار، (پەرز) نامازنى ئادا قىلغانلار، بىز رىزىق قىلىپ بەرگەن مال ـ مۈلۈكتىن (ئاللاھ يولىدا) يوشۇرۇن ۋە ئاشكارا يوسۇندا سەرپ قىلغانلار ۋە ياخشىلىقى ئارقىلىق يامانلىقىنى دەپئى قىلىدىغانلار (يەنى يامان ئىش قىلىپ قالسا، ئارقىدىن ياخشى ئىش قىلىدىغانلار، يامانلىق قىلغۇچىلارغا كەڭ قورساق بولۇپ، ياخشى مۇناسىۋەتتە بولىدىغانلار) ـ ئەنە شۇلارنىڭ ئاخىرەتلىكى ياخشى بولىدۇ.');
INSERT INTO "saleh" VALUES(13,23,'13:23','ئۇلار مەڭگۈ تۇرىدىغان جەننەتكە كىرىدۇ، ئۇلارنىڭ ئاتىلىرى، خوتۇنلىرى ۋە ئەۋلادىنىڭ ئىچىدىكى ياخشىلارمۇ جەننەتكە كىرىدۇ. پەرىشتىلەر ئۇلارنى (مۇبارەكلەش ئۈچۈن جەننەتنىڭ) ھەربىر دەرۋازىسىدىن كىرىدۇ.');
INSERT INTO "saleh" VALUES(13,24,'13:24',' (پەرىشتىلەر ئۇ دۇنيادا) «سەۋرنى دوست تۇتقانلىقىڭلار ئۈچۈن سىلەرگە ئامانلىق بولسۇن! ئاخىرەتلىكنىڭ ياخشى بولۇشى نېمىدېگەن ئوبدان!» دەيدۇ.');
INSERT INTO "saleh" VALUES(13,25,'13:25','ئاللاھقا بەرگەن ۋەدىسىنى مۇستەھكەملىگەندىن كېيىن بۇزغانلار، ئاللاھنىڭ سىلە ـ رەھىم قىلىشتىن ئىبارەت ئەمرىنى تۇتمىغانلار، يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار ـ ئەنە شۇلار لەنەتكە دۇچار بولىدۇ ۋە (ئۇلارنىڭ) ئاخىرەتلىكى يامان بولىدۇ.');
INSERT INTO "saleh" VALUES(13,26,'13:26',' ئاللاھ (ئۆزىنىڭ ھېكمىتىگە ئاساسەن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ ۋە (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ. (مۇشرىكلار) دۇنيا تىرىكچىلىكىگە خۇش بولۇپ كەتتى، دۇنيا تىرىكچىلىكى ئاخىرەتكە قارىغاندا ئۆتكۈنچى بەھرىمەن بولۇشتۇر.');
INSERT INTO "saleh" VALUES(13,27,'13:27','كۇففارلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن (مۇسانىڭ ۋە ئىيسانىڭ مۆجىزىسىدەك) بىرەر مۆجىزە كەلمىدى؟» دەيدۇ. (ئى مۇھەممەد!) «(ئىش ئاللاھنىڭ ئىلكىدە، مېنىڭ ئىلكىمدە ئەمەس) ئاللاھ ھەقىقەتەن خالىغان ئادەمنى گۇمراھ قىلىدۇ، تەۋبە قىلغانلارنى ھىدايەت قىلىدۇ» دېگىن. ');
INSERT INTO "saleh" VALUES(13,28,'13:28','(تەۋبە قىلغانلار) ئىمان ئېيتقانلار بولۇپ، ئۇلارنىڭ دىللىرى ئاللاھنى ياد ئېتىش بىلەن ئارام تاپىدۇ، بىلىڭلاركى، دىللار ئاللاھنى ياد ئېتىش بىلەن ئارام تاپىدۇ.');
INSERT INTO "saleh" VALUES(13,29,'13:29','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار خۇشاللىققا، گۈزەل قارارگاھقا (يەنى جەننەتكە) ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(13,30,'13:30',' (ئى مۇھەممەد!) بىز ساڭا ۋەھىي قىلغان كىتابنى ئۇلارغا ئوقۇپ بېرىشىڭ ئۈچۈن، سەندىن ئىلگىرى پەيغەمبەرلەرنى ئەۋەتكەندەك، سېنى شۇنداق بىر ئۈممەتكە ئەۋەتتۇقكى، ئۇنىڭدىن بۇرۇن نۇرغۇن ئۈممەتلەر ئۆتكەن، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) مەرھەمەتلىك ئاللاھنى ئىنكار قىلماقتا، (ئى مۇھەممەد! بۇ مۇشرىكلارغا) ئېيتقىنكى، «ئۇ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇنىڭغا تەۋەككۈل قىلدىم ۋە ئۇنىڭ دەرگاھىغا قايتىمەن».');
INSERT INTO "saleh" VALUES(13,31,'13:31','مۇبادا بىر قۇرئان (يەنى كىتاب) بولۇپ، ئۇ (تىلاۋەت قىلىنىش) بىلەن تاغلار يۆتكىلىدىغان ياكى يەر يېرىلىدىغان ۋە ياكى ئۆلۈكلەر سۆزلەيدىغان بولغان تەقدىردىمۇ (ئۇلار چوقۇم ئىمان ئېيتمايتتى). ھەممە ئىش ئاللاھنىڭ ئىلكىدىدۇر، ئەگەر ئاللاھ خالىسا، ئىنسانلارنىڭ ھەممىسىنى جەزمەن ھىدايەت قىلىدىغانلىقىنى مۆمىنلەر بىلمىدىمۇ؟ (ئىسلام دىنىنىڭ غەلىبە قىلىشىدىن ئىبارەت) ئاللاھنىڭ ۋەدىسى ئەمەلگە ئاشقانغا قەدەر، كاپىرلار ئۆز قىلمىشلىرى تۈپەيلىدىن ھامان تۈرلۈك بالالارغا دۇچار بولىدۇ، ياكى بالا ئۇلارنىڭ يۇرتىغا يېقىن بىر جايغا چۈشىدۇ، ئاللاھ ۋەدىسىگە ئەلۋەتتە خىلاپلىق قىلمايدۇ.');
INSERT INTO "saleh" VALUES(13,32,'13:32','سەندىن ئىلگىرى ئۆتكەن نۇرغۇن پەيغەمبەرلەر ھەقىقەتەن مەسخىرە قىلىندى. كاپىرلارغا بېرىلىدىغان ئازابنى بىر مۇددەت كېچىكتۈردۈم، ئاندىن ئۇلارنى جازالىدىم، ئۇلارغا بەرگەن جازايىم قانداق ئىكەن؟.');
INSERT INTO "saleh" VALUES(13,33,'13:33',' ھەر بىر كىشىنىڭ قىلغان ئەمەلىنى كۈزىتىپ تۇرغۇچى ئاللاھ (بۇتلارغا ئوخشامدۇ؟) ئۇلار بۇتلارنى ئاللاھقا شېرىك قىلدى. ئۇلارغا: « بۇتلارنىڭ ناملىرىنى ئاتاپ بېقىڭلار، يەر يۈزىدە ئاللاھ بىلمەيدىغان نەرسىلەر باردەك، سىلەر ئۇلارنى ئاللاھقا ئېيتىپ بېرەلەمسىلەر؟ ياكى قۇرۇق گۇمان بىلەن (ئۇلارنى ئاللاھنىڭ شېرىكلىرى دەۋاتامسىلەر؟)» دېگىن. بەلكى كاپىرلارغا ئۇلارنىڭ كۇفرى چىرايلىق كۆرسىتىلدى. ئۇلار توغرا يولدىن مەنئى قىلىندى. ئاللاھ گۇمراھ قىلغان ئادەمگە ھېچقانداق ھىدايەت قىلغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(13,34,'13:34','ئۇلار بۇ دۇنيادا ئازابقا دۇچار بولىدۇ، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىق بولىدۇ. ئۇلارنى ئاللاھنىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(13,35,'13:35',' تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇنىڭ مېۋىلىرى تۈگىمەيدۇ، ھەمىشە سايە چۈشۈپ تۇرىدۇ، ئەنە شۇ تەقۋادارلارنىڭ ئاقىۋەت بارىدىغان جايىدۇر؛ كاپىرلارنىڭ ئاقىۋەت بارىدىغان جايى دوزاختۇر.');
INSERT INTO "saleh" VALUES(13,36,'13:36',' (ئى مۇھەممەد!) بىز كىتاب (يەنى تەۋرات ۋە ئىنجىل) نازىل قىلغانلار (يەنى ئابدۇللا ئىبن سالام ۋە نەجاشىغا ئوخشاشلار) ساڭا نازىل قىلىنغان (قۇرئان) دىن شادلىنىدۇ. (ساڭا قارشى) ئىتتىپاق تۈزگەنلەرنىڭ ئارىسىدا (ھەقلىقىنى بىلىپ تۇرۇپ گەدەنكەشلىكتىن) قۇرئاننىڭ بەزى (جايلىرىنى) ئىنكار قىلىدىغانلارمۇ بار. (ئى مۇھەممەد!) «مەن پەقەت (يالغۇز) ئاللاھقىلا ئىبادەت قىلىشقا، ئاللاھقا (ھېچ نەرسىنى) شېرىك كەلتۈرمەسلىككە بۇيرۇلدۇم، (ئىنسانلارنى) ئاللاھقا ئىبادەت قىلىشقا دەۋەت قىلىمەن، ئاللاھنىڭلا دەرگاھىغا قايتىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(13,37,'13:37','شۇنىڭدەك (يەنى ئۆتكەنكى پەيغەمبەرلەرگە ئۆز تىلىدا كىتاب نازىل قىلغىنىمىزدەك)، قۇرئاننى ئەرەب تىلىدا (كىشىلەرنىڭ ئارىسىدا چىقىرىشقا) ھۆكۈم قىلىپ نازىل قىلدۇق، سەن ئىلىمگە ئىگە بولۇپ تۇرۇپ، ئۇلارنىڭ (يەنى ئەھلى كىتابنىڭ) نەپسى- خاھىشلىرىغا ئەگىشىپ كېتىدىغان بولساڭ، ساڭا ھېچقانداق ياردەم بەرگۈچى ۋە سېنى ئاللاھنىڭ ئازابىدىن ساقلىغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(13,38,'13:38','بىز سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارغا خوتۇنلار ۋە بالىلار بەردۇق، ھەرقانداق پەيغەمبەر ئاللاھنىڭ ئىزنىسىز مۆجىزە كەلتۈرەلمەيدۇ، ھەربىر ۋاقىتنىڭ (ئۆزىگە مۇناسىپ) ھۆكمى بولىدۇ.');
INSERT INTO "saleh" VALUES(13,39,'13:39','ئاللاھ (ئەھكاملاردىن) خالىغىنىنى بىكار قىلىدۇ، خالىغىنىنى ئۆز جايىدا قالدۇرىدۇ. لەۋھۇلمەھپۇز ئاللاھنىڭ دەرگاھىدىدۇر.');
INSERT INTO "saleh" VALUES(13,40,'13:40',' ئۇلارغا بىز ۋەدە قىلغان ئازابنىڭ بەزىسىنى ساڭا كۆرسەتسەكمۇ (يەنى سېنىڭ ھايات ۋاقتىڭدا نازىل قىلساقمۇ) ياكى سېنى (مۇشرىكلارغا ئازاب كېلىشتىن بۇرۇن) قەبزى روھ قىلساقمۇ، سېنىڭ مەسئۇلىيىتىڭ پەقەت تەبلىغ قىلىشتۇر. بىزنىڭ مەسئۇلىيىتىمىز ھېساب ئېلىشتۇر.');
INSERT INTO "saleh" VALUES(13,41,'13:41',' ئۇلارنىڭ يېرىنى چۆرىسىدىن تارايتىپ بېرىۋاتقانلىقىمىز (يەنى كۇففارلارنىڭ زېمىنىنى قەدەممۇ قەدەم پەتھى قىلىپ، ئىسلام يۇرتىغا قوشۇۋاتقانلىقىمىز) نى ئۇلار (يەنى مۇشرىكلار) كۆرمەيۋاتامدۇ؟ ئاللاھ (خالىغانچە) ھۆكۈم قىلىدۇ، ئاللاھنىڭ ھۆكمىگە ھېچ كىشى قارشى تۇرالمايدۇ. ئاللاھ تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(13,42,'13:42','ئۇلار (يەنى قۇرەيش كاپىرلىرى) دىن ئىلگىرى ئۆتكەنلەرمۇ (پەيغەمبەرلىرىگە) ھىيلە ـ مىكىر ئىشلەتكەن ئىدى. (ئۇلارنىڭ ھىيلە - مىكرىنى بەربات قىلىدىغان) يوشۇرۇن تەدبىرلەرنىڭ ھەممىسى ئاللاھنىڭ ئىلكىدىدۇر، ئاللاھ ھەربىر كىشىنىڭ قىلىۋاتقان (ياخشى- يامان) ئەمەللىرىنى بىلىپ تۇرىدۇ، كاپىرلار (ئازابنى كۆرگەندە) كىمنىڭ ئاخىرەتلىكىنىڭ ياخشى بولغانلىقىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(13,43,'13:43',' (ئى مۇھەممەد!) مەككە كۇففارلىرى: «سەن پەيغەمبەر ئەمەسسەن» دەيدۇ. ئېيتقىنكى، «راستلىقىمغا سىلەر بىلەن مېنىڭ ئوتتۇرامدا ئاللاھنىڭ گۇۋاھلىقى ۋە ئاللاھنىڭ كىتابىنى بىلىدىغانلار (يەنى ئەھلى كىتاب ئۆلىمالىرىدىن ئىمان ئېيتقانلار) نىڭ گۇۋاھلىقى يېتەرلىكتۇر».');
INSERT INTO "saleh" VALUES(14,1,'14:1','ئەلىف. لام. را. (بۇ قۇرئان شۇنداق) كىتابتۇركى، (ئى مۇھەممەد!) ئۇنى ساڭا كىشىلەرنى پەرۋەردىگارىنىڭ ئىزنى بىلەن قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا (يەنى ئىمانغا) چىقىرىشىڭ ئۈچۈن، غالىب، (ھەممە تىللاردا) مەدھىيەلەنگەن ئاللاھنىڭ يولىغا (باشلىشىڭ ئۈچۈن) نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(14,2,'14:2','ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، قاتتىق ئازابتىن كاپىرلارغا ۋاي!. ');
INSERT INTO "saleh" VALUES(14,3,'14:3','ئۇلار بۇ دۇنيانى ئاخىرەتتىن ئارتۇق كۆرىدۇ (كىشىلەرنى) ئاللاھنىڭ يولىدىن (يەنى ئىسلام دىنىدىن) توسىدۇ، ئاللاھنىڭ يولىنىڭ ئەگرى بولۇشىنى تىلەيدۇ، ئۇلار (ھەقتىن) يىراق بولغان گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(14,4,'14:4',' قانداقلا بىر پەيغەمبەرنى ئەۋەتمەيلى، ئۇنى بىز ئۆز قەۋمىگە (ئاللاھنىڭ شەرىئىتىنى) بايان قىلىش ئۈچۈن، پەقەت ئۆز قەۋمىنىڭ تىلى بىلەن (سۆزلەيدىغان) قىلىپ ئەۋەتتۇق. ئاللاھ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى پەيغەمبەرلەرنىڭ ۋەزىپىسى پەقەت تەبلىغ قىلىش بولۇپ، ھىدايەت قىلىش ئىشى ئاللاھنىڭ ئىلكىدىدۇر). ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(14,5,'14:5','بىز مۇسانى مۆجىزىلىرىمىز بىلەن ھەقىقەتەن (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (ئۇنىڭغا دېدۇقكى) «قەۋمىڭنى قاراڭغۇلۇقتىن (يەنى كۇفرىدىن) يورۇقلۇققا (يەنى ئىمانغا) چىقارغىن، ئۇلارغا ئاللاھنىڭ نېمەتلىرىنى ئەسلەتكىن». بۇنىڭدا (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ھەربىر (بەندە) ئۈچۈن ئەلۋەتتە نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(14,6,'14:6','مۇسا ئۆز ۋاقتىدا ئۆز قەۋمىگە: «ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار. ئاللاھ ئۆز ۋاقتىدا سىلەرنى پىرئەۋن قەۋمىدىن قۇتقۇزدى، ئۇلار سىلەرنى قاتتىق قىينايتتى، ئوغۇللىرىڭلارنى بوغۇزلايتتى، خوتۇن كىشىلىرىڭلارنى (خىزمەت قىلدۇرۇشقا) قالدۇراتتى. بۇنىڭدا پەرۋەردىگارىڭلار تەرىپىدىن (سىلەرگە) چوڭ سىناق بار ئىدى» دېدى.');
INSERT INTO "saleh" VALUES(14,7,'14:7',' ئۆز ۋاقتىدا پەرۋەردىگارىڭلار: «نېمىتىمگە شۈكۈر قىلساڭلار، (ئۇنى) تېخىمۇ زىيادە قىلىمەن، ئەگەر(كۇفرانى نېمەت) قىلساڭلار، مېنىڭ ئازابىم، ئەلۋەتتە، بەكمۇ قاتتىق بولىدۇ» دەپ جاكارلىدى.');
INSERT INTO "saleh" VALUES(14,8,'14:8',' مۇسا (ئۆز قەۋمىگە): «ئەگەر سىلەر ۋە پۈتۈن يەر يۈزىدىكى كىشىلەر كاپىر بولۇپ كەتسەڭلارمۇ (ئاللاھقا قىلچە زىيان يەتكۈزەلمەيسىلەر)، چۈنكى ئاللاھ ئەلۋەتتە (ھەممىدىن) بىھاجەتتۇر، مەدھىيەلەشكە لايىقتۇر» دېدى.');
INSERT INTO "saleh" VALUES(14,9,'14:9','سىلەردىن ئىلگىرى ئۆتكەن نۇھ، ئاد ۋە سەمۇد قەۋملىرىنىڭ ۋە ئۇلاردىن كېيىن كەلگەن، (سانىنى) پەقەت ئاللاھ بىلىدىغان ئۈممەتلەرنىڭ خەۋىرى سىلەرگە يەتمىدىمۇ؟ ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەر بىلەن كەلدى، ئۇلار قوللىرى بىلەن پەيغەمبەرلەرنىڭ ئېغىزلىرىغا ئىشارەت قىلىپ (يەنى ئۇلارنىڭ سۈكۈت قىلىشىنى كۆرسىتىپ): «بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز، بىز سىلەر دەۋەت قىلىۋاتقان ئىشقا ھەقىقەتەن زور گۇماندىمىز» دېدى.');
INSERT INTO "saleh" VALUES(14,10,'14:10','پەيغەمبەرلىرى: «ئاسمانلارنى ۋە زېمىننى ياراتقۇچى ئاللاھنىڭ (بىرلىكىدە) شەك بارمۇ؟ ئاللاھ گۇناھلىرىڭلارنى مەغپىرەت قىلىش ئۈچۈن، سىلەرنى (ئىمانغا) چاقىرىدۇ، سىلەرنى (جازالاشنى) مەلۇم مۇددەتكىچە (يەنى ئەجىلىڭلار يەتكۈچە) تەخىر قىلىدۇ» دېدى. ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، ئاتا ـ بوۋىلىرىمىز ئىبادەت قىلىپ كېلىۋاتقان (بۇتلار) غا ئىبادەت قىلىشىمىزدىن توسماقچى بولىسىلەر، (راستلىقىڭلار ئۈچۈن) بىزگە ئېنىق پاكىت كۆرسىتىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(14,11,'14:11',' پەيغەمبەرلىرى ئۇلارغا: «بىز پەقەت سىلەرگە ئوخشاش ئىنسانمىز، لېكىن ئاللاھ بەندىلىرىدىن خالىغان كىشىلەرگە (پەيغەمبەرلىكىنى) مەرھەمەت قىلىدۇ، ئاللاھنىڭ ئىزنىسىز سىلەرگە ھېچقانداق پاكىت كۆرسىتەلمەيمىز، مۆمىنلەر (ھەممە ئىشتا) يالغۇز بىر ئاللاھقىلا تەۋەككۈل قىلسۇن (يەنى ھەممە ئىشلىرىنى ئاللاھقا تاپشۇرسۇن)');
INSERT INTO "saleh" VALUES(14,12,'14:12','ئاللاھ بىزنى (ئاللاھنى تونۇيدىغان) يوللىرىمىزغا يېتەكلىگەن تۇرسا، نېمىشقا ئاللاھقا تەۋەككۈل قىلمايلى؟ بىزگە يەتكۈزگەن ئەزىيەتلىرىڭلارغا، ئەلۋەتتە، سەۋر قىلىمىز، تەۋەككۈل قىلغۇچىلار پەقەت ئاللاھقا تەۋەككۈل قىلسۇن» دېدى.');
INSERT INTO "saleh" VALUES(14,13,'14:13','كاپىرلار پەيغەمبەرلىرىگە: «سىلەرنى زېمىنىمىزدىن چوقۇم ھەيدەپ چىقىرىمىز ياكى چوقۇم بىزنىڭ دىنىمىزغا قايتىشىڭلار كېرەك» دېدى. ئۇلارغا پەرۋەردىگارى (مۇنداق) ۋەھىي قىلدى: «زالىملارنى چوقۇم ھالاك قىلىمىز.');
INSERT INTO "saleh" VALUES(14,14,'14:14','ئۇلارنى ھالاك قىلغاندىن كېيىن زېمىندا چوقۇم سىلەرنى تۇرغۇزىمىز، بۇ (يەنى غەلىبە) مېنىڭ ئالدىمدا تۇرۇشتىن (يەنى سوراققا تارتىلىشتىن) ۋە ئازابىمدىن قورققانلار ئۈچۈندۇر».');
INSERT INTO "saleh" VALUES(14,15,'14:15','پەيغەمبەرلەر ئاللاھتىن مەدەت تىلىدى، ھەر بىر ئۇچىغا چىققان تەرسا، شەپقەتسىز (يەنى ئاللاھقا ئىتائەت قىلىشقا گەدەنكەشلىك قىلىپ ھەقىقەتتىن باش تارتقۇچى) ھالاك بولدى.');
INSERT INTO "saleh" VALUES(14,16,'14:16',' ئۇنىڭ ئالدىدا جەھەننەم تۇرىدۇ، جەھەننەمدە ئۇ يىرىڭ ئارىلاش سۇ بىلەن سۇغىرىلىدۇ.');
INSERT INTO "saleh" VALUES(14,17,'14:17','(ئۇنىڭ ئاچچىقلىقىدىن) ئۇنى يۇتۇملاپ ئىچىپ تەسلىكتە يۇتىدۇ، ئۆلۈم ئۇنى قورشىۋالىدۇ. لېكىن ئۇ ھەرگىز ئۆلمەيدۇ، شۇنىڭدىن كېيىن تېخىمۇ قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(14,18,'14:18','پەرۋەردىگارىنى ئىنكار قىلغانلارنىڭ قىلغان (ياخشى) ئەمەللىرى بورانلىق كۈندە شامال ئۇچۇرۇپ كەتكەن بىر دۆۋە كۈلگە ئوخشىمايدۇ، كاپىرلار (بۇ دۇنيادا) قىلغان ياخشى ئەمەللىرى ئۈچۈن ئازراقمۇ ساۋابقا ئېرىشەلمەيدۇ، بۇ چوڭقۇر ئېزىشتۇر (يەنى چوڭ زىياندۇر).');
INSERT INTO "saleh" VALUES(14,19,'14:19',' ئاسمانلارنى ۋە زېمىننى ئاللاھنىڭ ھەق ئاساستا ياراتقانلىقىنى (يەنى بىكار ياراتمىغانلىقىنى) كۆرمىدىڭمۇ؟ ئەگەر ئاللاھ خالىسا، (ئى ئىنسانلار!) سىلەرنى يوق قىلىۋېتىپ (ئورنۇڭلارغا سىلەردىن ياخشى) يېڭى ئادەملەرنى يارىتىدۇ.');
INSERT INTO "saleh" VALUES(14,20,'14:20','بۇ ئاللاھقا قىيىن ئەمەس.');
INSERT INTO "saleh" VALUES(14,21,'14:21','ئۇلار (يەنى خالايىقلار) نىڭ ھەممىسى (قىيامەت كۈنى قەبرىلىرىدىن) چىقىپ، ئاللاھقا مۇلاقات بولىدۇ، ئاجىزلىرى (يەنى ئەگەشكۈچىلىرى) مۇتەكەببىرلىرىگە (يەنى ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرنىڭ ئەگەشكۈچىڭلار ئىدۇق، سىلەر بىزگە بولۇۋاتقان ئاللاھنىڭ ئازابىدىن ئازراق دەپئى قىلالامسىلەر؟» دەيدۇ. ئۇلار: «ئاللاھ بىزنى ھىدايەت قىلغان بولسا ئىدى، بىز ئەلۋەتتە سىلەرنى ھىدايەت قىلاتتۇق، بىز مەيلى نالە ـ پەرياد قىلايلى، مەيلى سەۋر قىلايلى، بەرىبىر ئوخشاش، بىزگە ھېچقانداق قاچىدىغان جاي يوق» دەيدۇ.');
INSERT INTO "saleh" VALUES(14,22,'14:22',' ئىش پۈتكەندە (يەنى ھېساب تۈگەپ، جەننەتىلەر بىلەن دوزىخىلار ئايرىلىپ بولغاندا)، شەيتان: «ئاللاھ ھەقىقەتەن سىلەرگە (ئىتائەت قىلغۇچىنى مۇكاپاتلاش، ئاسىيلىق قىلغۇچىنى جازالاشتىن ئىبارەت) راست ۋەدىنى قىلغان ئىدى، (ۋەدىسىگە ۋاپا قىلدى). مەن سىلەرگە (ئۆلگەندىن كېيىن تىرىلىش، ساۋاب، جازا دېگەنلەر يوق، دەپ يالغان) ۋەدە قىلغان ئىدىم، (ۋەدەمگە) خىلاپلىق قىلدىم، سىلەرگە مېنىڭ (كۇفرىغا، گۇناھقا زورلىغۇدەك) ھۆكۈمرانلىقىم بولغىنى يوق، مەن سىلەرنى پەقەت (گۇمراھلىققا) دەۋەت قىلدىم، سىلەر (دەۋىتىمنى) قوبۇل قىلدىڭلار، شۇنىڭ ئۈچۈن مېنى ئەيىبلىمەڭلار، ئۆزۈڭلارنى ئەيىبلەڭلار، مەن سىلەرگە ياردەم بېرىپ سىلەرنى قۇتقۇزالمايمەن، سىلەرمۇ ماڭا ياردەم بېرىپ مېنى قۇتقۇزالمايسىلەر، مەن سىلەرنىڭ بۇرۇن مېنى ئاللاھقا (ئىبادەتتە) شېرىك قىلغانلىقىڭلارنى ئېتىراپ قىلمايمەن، شۈبھىسىزكى، زالىملار چوقۇم قاتتىق ئازابقا دۇچار بولىدۇ» دەيدۇ.');
INSERT INTO "saleh" VALUES(14,23,'14:23',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار، پەرۋەردىگارىنىڭ ئىزنى بىلەن، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرىدۇ، ئۇ جەننەتلەردە مەڭگۈ قالىدۇ، (پەرىشتىلەر ئۇلارنى ھۆرمەتلەپ) سىلەرگە ئامانلىق بولسۇن، دەپ سالام بېرىدۇ.');
INSERT INTO "saleh" VALUES(14,24,'14:24','ئاللاھنىڭ مۇنداق بىر تەمسىل كۆرسەتكەنلىكىنى كۆرمىدىڭمۇ؟ كەلىمە تەييىبە (يەنى ياخشى سۆز، ئىمان كەلىمىسى) يىلتىزى يەرنىڭ ئاستىدا بولغان، شېخى ئاسمانغا تاقاشقان،');
INSERT INTO "saleh" VALUES(14,25,'14:25','پەرۋەردىگارىنىڭ ئىزنى بىلەن ۋاقتى ـ ۋاقتىدا مېۋە بېرىپ تۇرىدىغان ئېسىل دەرەخقە ئوخشايدۇ. ئاللاھ كىشىلەرگە ۋەز ـ نەسىھەت ئالسۇن دەپ، ئۇلارغا نۇرغۇن تەمسىللەرنى كەلتۈرىدۇ.');
INSERT INTO "saleh" VALUES(14,26,'14:26','يامان سۆز (يەنى كۇفرى كەلىمىسى) زېمىندىن قومۇرۇپ تاشلانغان، ھېچقانداق قارارى يوق ناچار دەرەخقە ئوخشايدۇ.');
INSERT INTO "saleh" VALUES(14,27,'14:27','ئاللاھ مۆمىنلەرنى مۇستەھكەم ئىمان بىلەن دۇنيادا ۋە ئاخىرەتتە مەھكەم تۇرغۇزىدۇ. ئاللاھ زالىملارنى گۇمراھ قىلىدۇ، ئاللاھ خالىغىنىنى قىلىدۇ.');
INSERT INTO "saleh" VALUES(14,28,'14:28','ئاللاھنىڭ بەرگەن نېمىتىگە كۇفرىلىق قىلغان ۋە ئۆز قەۋمىنى (ئازدۇرۇش بىلەن) ھالاكەت مەۋقەسىگە چۈشۈرۈپ قويغانلارنى كۆرمىدىڭمۇ؟.');
INSERT INTO "saleh" VALUES(14,29,'14:29',' (ھالاكەت مەۋقەسى) جەھەننەمدۇركى، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(14,30,'14:30',' ئۇلار (كىشىلەرنى) ئاللاھنىڭ يولىدىن (يەنى ئىسلام دىنىدىن) ئازدۇرۇش ئۈچۈن، ئاللاھقا نۇرغۇن شېرىكلەرنى پەيدا قىلىۋالدى. ئۇلارغا: «(دۇنيانىڭ نېمەتلىرىدىن) بەھرىمەن بولدۇڭلار، شۈبھىسىزكى، سىلەرنىڭ بارىدىغان جايىڭلار دوزاختۇر» دېگىن.');
INSERT INTO "saleh" VALUES(14,31,'14:31',' مۆمىن بەندىلىرىمگە ئېيتقىنكى، ئۇلار نامازنى ئادا قىلسۇن، ئالدى ـ ساتتىمۇ، دوستلۇقمۇ بولمايدىغان كۈن (يەنى قىيامەت كۈنى) كېلىشتىن بۇرۇن، بىز ئۇلارغا بەرگەن مال ـ مۈلۈكتىن يوشۇرۇن ۋە ئاشكارا يوسۇندا سەدىقە قىلسۇن.');
INSERT INTO "saleh" VALUES(14,32,'14:32','ئاللاھ ئاسمانلارنى ۋە زېمىننى ياراتتى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، يامغۇر سۈيى بىلەن سىلەرگە رىزىق قىلىپ نۇرغۇن مېۋىلەرنى ئۆستۈرۈپ بەردى، ئاللاھ سىلەرگە ئۆز ئەمرى بويىچە دېڭىزدا قاتنايدىغان كېمىلەرنى بويسۇندۇرۇپ بەردى، سىلەرگە دەريالارنى بويسۇندۇرۇپ بەردى.');
INSERT INTO "saleh" VALUES(14,33,'14:33','سىلەرگە ئاي بىلەن كۈننى تەرتىپلىك دەۋر قىلىپ تۇرىدىغان قىلىپ بويسۇندۇرۇپ بەردى، سىلەرگە كېچە بىلەن كۈندۈزنى بويسۇندۇرۇپ بەردى.');
INSERT INTO "saleh" VALUES(14,34,'14:34','ئاللاھ سىلەرگە سورىغان نەرسەڭلارنىڭ ھەممىسىنى بەردى، سىلەر ئاللاھنىڭ نېمەتلىرىنى ساناپ تۈگىتەلمەيسىلەر، ئىنسان، شەك ـ شۈبھىسىزكى، زۇلۇم قىلغۇچىدۇر، (ئاللاھنىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(14,35,'14:35','ئۆز ۋاقتىدا ئىبراھىم ئېيتتى: «پەرۋەردىگارىم! بۇ جاينى (يەنى مەككىنى) تىنچ (جاي) قىلىپ بەرگىن، مېنى ۋە مېنىڭ ئەۋلادلىرىمنى بۇتلارغا چوقۇنۇشتىن يىراق قىلغىن.');
INSERT INTO "saleh" VALUES(14,36,'14:36',' پەرۋەردىگارىم! ئۇلار (يەنى بۇتلار) نۇرغۇن كىشىلەرنى ئازدۇردى (يەنى نۇرغۇن كىشىلەرنىڭ ئېزىشىغا سەۋەبچى بولدى)، كىمكى ماڭا ئەگەشكەن ئىكەن، ئۇ مېنىڭ دىنىمدىدۇر، كىمكى ماڭا ئاسىيلىق قىلغان ئىكەن، سەن ئەلۋەتتە (ئۇنىڭغا) مەغپىرەت قىلىشقا ۋە مەرھەمەت قىلىشقا (قادىرسەن). ');
INSERT INTO "saleh" VALUES(14,37,'14:37','پەرۋەردىگارىمىز! ئەۋلادىمنىڭ بىر قىسىمىنى (يەنى بالام ئىسمائىل بىلەن ئايالىم ھاجەرنى) ناماز ئوقۇسۇن (يەنى ساڭا ئىبادەت قىلسۇن دەپ) سېنىڭ ھۆرمەتلىك ئۆيۈڭنىڭ قېشىدىكى ئېكىنسىز بىر ۋادىغا (يەنى مەككىگە) ئورۇنلاشتۇردۇم. پەرۋەردىگارىمىز! بىر قىسىم كىشىلەرنىڭ دىللىرىنى ئۇلارغا مايىل قىلغىن، ئۇلارنى شۈكۈر قىلسۇن دەپ تۈرلۈك مېۋىلەرنى ئۇلارغا رىزىق قىلىپ بەرگىن.');
INSERT INTO "saleh" VALUES(14,38,'14:38',' پەرۋەردىگارىمىز! سەن بىزنىڭ يوشۇرۇن ۋە ئاشكارا قىلغانلىرىمىزنى بىلىپ تۇرىسەن، ئاسمان ـ زېمىندىكى ھېچ نەرسە ئاللاھقا مەخپىي ئەمەس. ');
INSERT INTO "saleh" VALUES(14,39,'14:39','جىمى ھەمدۇسانا ياشانغىنىمدا ماڭا ئىسمائىل بىلەن ئىسھاقنى بەرگەن ئاللاھقا خاستۇر! پەرۋەردىگارىم! ھەقىقەتەن دۇئانى ئاڭلاپ تۇرغۇچىدۇر (يەنى دۇئا قىلغۇچىنىڭ دۇئاسىنى ئىجابەت قىلغۇچىدۇر).');
INSERT INTO "saleh" VALUES(14,40,'14:40','پەرۋەردىگارىم! مېنى ۋە بىر قىسىم ئەۋلادىمنى نامازنى ئادا قىلغۇچى قىلغىن، پەرۋەردىگارىمىز! دۇئايىمنى قوبۇل قىلغىن. ');
INSERT INTO "saleh" VALUES(14,41,'14:41','پەرۋەردىگارىمىز! ھېساب ئالىدىغان كۈندە (يەنى قىيامەت كۈنىدە) ماڭا، ئاتا ـ ئانامغا ۋە مۆمىنلەرگە مەغپىرەت قىلغىن».');
INSERT INTO "saleh" VALUES(14,42,'14:42','زالىملارنىڭ قىلمىشلىرىدىن ئاللاھنى بىخەۋەر دەپ ئويلىمىغىن، ئاللاھ ئۇلارنى جازالاشنى (شۇ كۈنىنىڭ دەھشىتىدىن) كۆزلەر چەكچىيىپ قالىدىغان كۈنىگۈچە (يەنى قىيامەت كۈنىگۈچە) كېچىكتۈرىدۇ.');
INSERT INTO "saleh" VALUES(14,43,'14:43',' (بۇ كۈندە) ئۇلار باشلىرىنى كۆتۈرۈپ، ئالغا قاراپ چاپىدۇ، كۆزلىرى ئېچىلىپ قالىدۇ، (تېڭىرقىغانلىقتىن) دىللىرى (ئەقىل ـ ئىدراكتىن) خالىي بولۇپ قالىدۇ. ');
INSERT INTO "saleh" VALUES(14,44,'14:44',' (ئى مۇھەممەد!) كىشىلەرنى ئازاب كېلىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرغىن، (بۇ كۈندە) زالىملار: «پەرۋەردىگارىمىز! بىزگە قىسقىغىنا ۋاقىت مۆھلەت بەرگىن (يەنى دۇنياغا قايتۇرغىن)، دەۋىتىڭنى قوبۇل قىلايلى، پەيغەمبەرلەرگە ئەگىشەيلى» دەيدۇ. (ئۇلارغا) سىلەر ئىلگىرى، بىز ھەرگىز يوقالمايمىز، دەپ قەسەم ئىچمىگەنمىدىڭلار؟ (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(14,45,'14:45',' ئۆزلىرىگە زۇلۇم قىلغانلارنى (ھالاك قىلغىنىمىزدىن كېيىن) سىلەر ئۇلارنىڭ جايلىرىدا تۇردۇڭلار (ئۇلارنىڭ جايلىرىدىن ئىبرەت ئالساڭلارچۇ!)، ئۇلارنى قانداق جازالىغانلىقىمىز سىلەرگە ئېنىق مەلۇم بولدى. سىلەرگە نۇرغۇن مىساللارنى كەلتۈردۇق (يەنى پەيغەمبەرلەرگە چىنپۈتمىگەنلەرنىڭ ئەھۋالىنى بايان قىلدۇق). ');
INSERT INTO "saleh" VALUES(14,46,'14:46','ئۇلار ھەقىقەتەن (پەيغەمبەرلەرگە ۋە مۆمىنلەرگە) مىكىر ئىشلەتتى. ئۇلارنىڭ مىكرى تاغنى ئۆرۈۋەتكۈدەك (چوڭ) بولسىمۇ، ئاللاھ ئۇلارنىڭ مىكرىنى بەربات قىلىدۇ.');
INSERT INTO "saleh" VALUES(14,47,'14:47','ئاللاھنى پەيغەمبەرلىرىگە قىلغان ۋەدىسىگە خىلاپلىق قىلىدۇ دەپ ئويلىمىغىن، ئاللاھ ھەقىقەتەن غالىبتۇر، (دوستلىرى ئۈچۈن دۈشمەنلىرىدىن) ئىنتىقام ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(14,48,'14:48',' ئۇ كۈندە زېمىنمۇ باشقا بىر زېمىنغا، ئاسمانلارمۇ باشقا بىر ئاسمانلارغا ئايلىنىدۇ، ئۇلار (يەنى پۈتۈن خالايىق قەبرىلىرىدىن چىقىپ) يېگانە ، قۇدرەتلىك ئاللاھنىڭ دەرگاھىدا (يەنى مەھشەرگاھدا) ھازىر بولىدۇ.');
INSERT INTO "saleh" VALUES(14,49,'14:49','(ئى مۇھەممەد!) ئۇ كۈندە گۇناھكارلار (يەنى كۇففارلار) نى زەنجىرلەر بىلەن بىر ـ بىرىگە چېتىلىپ باغلانغان ھالدا كۆرۈسەن.');
INSERT INTO "saleh" VALUES(14,50,'14:50','ئۇلارنىڭ كۆڭلىكى قارامايدىن بولىدۇ، ئوت ئۇلارنىڭ يۈزلىرىنى ئورىۋالىدۇ. ');
INSERT INTO "saleh" VALUES(14,51,'14:51','ئاللاھ ھەر كىشىنىڭ قىلمىشىغا قاراپ شۇنداق جازا بېرىدۇ، ئاللاھ ئەلۋەتتە تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(14,52,'14:52','كىشىلەرنىڭ ئاگاھلاندۇرۇلۇشى، ئاللاھنىڭ بىر ئىكەنلىكىنى بىلىشلىرى، ئەقىل ئىگىلىرىنىڭ ۋەز ـ نەسىھەت ئېلىشلىرى ئۈچۈن، بۇ (قۇرئان) تەبلىغدۇر (يەنى كىشىلەرگە تەبلىغ قىلىش ئۈچۈن نازىل قىلىندى).');
INSERT INTO "saleh" VALUES(15,1,'15:1','ئەلىف، لام، را. بۇ، كىتابنىڭ ـ روشەن قۇرئاننىڭ ئايەتلىرىدۇر.');
INSERT INTO "saleh" VALUES(15,2,'15:2','كاپىرلار (قىيامەتنىڭ دەھشىتىنى كۆرگەندە، دۇنيادىكى چېغىمىزدا) مۇسۇلمان بولغان بولساقچۇ، دەپ ئارمان قىلىدۇ.');
INSERT INTO "saleh" VALUES(15,3,'15:3','(ئى مۇھەممەد!) ئۇلارنى (يەنى كۇففارلارنى مەيلىگە) قويۇۋەت، يەپ ـ ئىچىپ، ئويناپ ـ كۈلۈپ يۈرسۇن، ئۇلار بىھۇدە ئارزۇلار بىلەن بولىۋەرسۇن، ئۇلار ئۇزاققا قالماي (ئۆز قىلمىشلىرىنىڭ يامانلىقىنى) بىلىدۇ.');
INSERT INTO "saleh" VALUES(15,4,'15:4','بىز قانداق بىر شەھەرنى ھالاك قىلمايلى، پەقەت ئۇنىڭ (ھالاك بولىدىغان) مۇئەييەن ۋاقتى بولىدۇ.');
INSERT INTO "saleh" VALUES(15,5,'15:5','ھەرقانداق ئۈممەتنىڭ (ھالاك بولىدىغان ۋاقتى) ئىلگىرى سۈرۈلمەيدۇ، ھەم كېچىكتۈرۈلمەيدۇ.');
INSERT INTO "saleh" VALUES(15,6,'15:6','ئۇلار ئېيتتى: «ئى قۇرئان نازىل قىلىنغان ئادەم! سەن ھەقىقەتەن مەجنۇنسەن.');
INSERT INTO "saleh" VALUES(15,7,'15:7','ئەگەر سەن (مەن رەسۇلۇللاھ دېگەن سۆزۈڭدە) راستچىللاردىن بولساڭ، (پەيغەمبەرلىكىڭگە گۇۋاھلىق بېرىدىغان) پەرىشتىلەرنى ئالدىمىزغا ئېلىپ كەلسەڭچۇ؟»');
INSERT INTO "saleh" VALUES(15,8,'15:8','پەرىشتىلەرنى پەقەت ھەق ئاساسىدا (يەنى ئازاب نازىل قىلىشىمىز مۇقەررەر بولغان چاغدىلا) نازىل قىلىمىز، بۇ چاغدا ئۇلارغا مۆھلەت بېرىلمەيدۇ (يەنى ئۇلارنىڭ ئازابى كېچىكتۈرۈلمەيدۇ).');
INSERT INTO "saleh" VALUES(15,9,'15:9','قۇرئاننى ھەقىقەتەن بىز نازىل قىلدۇق ۋە چوقۇم ئۇنى قوغدايمىز.');
INSERT INTO "saleh" VALUES(15,10,'15:10','شۈبھىسىزكى، سېنىڭدىن ئىلگىرى بۇرۇنقى ھەرقايسى تائىپىلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(15,11,'15:11','ئۇلار ئۆزلىرىگە پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى.');
INSERT INTO "saleh" VALUES(15,12,'15:12','شۇنداق مەسخىرە قىلىشنى بىز گۇناھكارلارنىڭ دىللىرىغا سالىمىز.');
INSERT INTO "saleh" VALUES(15,13,'15:13','ئۇلار قۇرئانغا ئىشەنمەيدۇ، بۇ بۇرۇندىن ئادەت بولۇپ قالغان (يەنى بۇرۇندىن تارتىپ پەيغەمبەرلىرىنى ئىنكار قىلغان قەۋملەرنى ئاللاھنىڭ ھالاك قىلىشى ئادەت بولۇپ كېلىۋاتقان) ئىش.');
INSERT INTO "saleh" VALUES(15,14,'15:14','بىز ئۇلارغا ئاسماندىن بىر ئىشىك ئېچىپ بەرگەن، ئۇلار ئۇنىڭدىن ئاسمانغا ئۆرلەپ (ئالەمى ئەرۋاھنى، پەرىشتىلەرنى كۆرگەن تەقدىردىمۇ)،');
INSERT INTO "saleh" VALUES(15,15,'15:15','ئۇلار چوقۇم: «كۆزلىرىمىز باغلىنىپ قالدى، بەلكىم سېھىرلەنگەن قەۋم بولۇپ قالساق كېرەك» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(15,16,'15:16',' بىز ئاسماندا بۇرۇجلارنى ياراتتۇق ۋە كۆزەتكۈچىلەر ئۈچۈن ئاسماننى (يۇلتۇزلار بىلەن) بېزىدۇق.');
INSERT INTO "saleh" VALUES(15,17,'15:17','ھەر بىر قوغلاندى شەيتاندىن ئاسماننى قوغدىدۇق.');
INSERT INTO "saleh" VALUES(15,18,'15:18','لېكىن (ئاسمان خەۋەرلىرىگە) ئوغرىلىقچە قۇلاق سالماقچى بولغان شەيتاننى روشەن ئاقار يۇلتۇز قوغلاپ يېتىدۇ (يەنى ھالاك قىلىدۇ).');
INSERT INTO "saleh" VALUES(15,19,'15:19',' يەر يۈزىنى يېيىپ كەڭ قىلدۇق، ئۇنىڭغا تاغلارنى ئورناتتۇق، يەر يۈزىدە ھەرخىل ئۆلچەملىك ئۆسۈملۈكلەرنى ئۆستۈردۇق.');
INSERT INTO "saleh" VALUES(15,20,'15:20','يەر يۈزىدە سىلەر ئۈچۈن ۋە سىلەر رىزقىنى بېرەلمەيدىغانلار (يەنى خادىملار ۋە ھايۋانلار) ئۈچۈن تۇرمۇش لازىمەتلىكلىرىنى ياراتتۇق (ئۇلارنىڭ يېمەك ـ ئىچمەكلىرىنى سىلەر ئەمەس، بىز يارىتىمىز).');
INSERT INTO "saleh" VALUES(15,21,'15:21','قانداق نەرسە بولمىسۇن، ئۇنىڭ خەزىنىسى (يەنى خەزىنىنىڭ ئاچقۇچى) بىزنىڭ دەرگاھىمىزدىدۇر، بىز ئۇنى پەقەت مەلۇم مىقداردىلا چۈشۈرىمىز.');
INSERT INTO "saleh" VALUES(15,22,'15:22','بىز يامغۇرنى ھەيدەيدىغان شاماللارنى ئەۋەتتۇق، بىز بۇلۇتتىن يامغۇر ياغدۇرۇپ سىلەرنى سۇغاردۇق، سىلەر ھەرگىز ئۇنى ساقلاشقا قادىر ئەمەسسىلەر.');
INSERT INTO "saleh" VALUES(15,23,'15:23','بىز (ھەممىنى) تىرىلدۈرىمىز، (ھەممىنى) ئۆلتۈرىمىز ۋە (ھەممىگە) ۋارىسلىق قىلىمىز.');
INSERT INTO "saleh" VALUES(15,24,'15:24',' بىز سىلەردىن ئىلگىرى ئۆتكەنلەرنى ھەقىقەتەن بىلىمىز ۋە كېيىن كېلىدىغانلارنىمۇ ھەقىقەتەن بىلىمىز.');
INSERT INTO "saleh" VALUES(15,25,'15:25','پەرۋەردىگارىڭ ھەقىقەتەن (ھېساب ئېلىش ۋە جازالاش ئۈچۈن) ئۇلارنى يىغىدۇ. ئاللاھ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(15,26,'15:26','شەك ـ شۈبھىسىزكى، بىز ئىنسان (يەنى ئادەم ئەلەيھىسسالام) نى قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ياراتتۇق.');
INSERT INTO "saleh" VALUES(15,27,'15:27','جىنلار (يەنى شەيتانلار ۋە ئۇلارنىڭ باشلىقى ئىبلىس) نى ئىلگىرى تۈتۈنسىز ئوت يالقۇنىدىن ياراتقان ئىدۇق.');
INSERT INTO "saleh" VALUES(15,28,'15:28','پەرۋەردىگارىڭ ئۆز ۋاقتىدا پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن قارا لايدىن ياسىلىپ شەكىلگە كىرگۈزۈلگەن (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن ئىنسان (يەنى ئادەم ئەلەيھىسسالام) نى يارىتىمەن.');
INSERT INTO "saleh" VALUES(15,29,'15:29',' مەن ئۇنى تولۇق ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرى تولۇق ۋە مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن چېغىمدا، سىلەر ئۇنىڭغا سەجدە قىلىڭلار) ».');
INSERT INTO "saleh" VALUES(15,30,'15:30','پەرىشتىلەرنىڭ بىرىمۇ ئايرىلىپ قالماي ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى.');
INSERT INTO "saleh" VALUES(15,31,'15:31','پەقەت ئىبلىسلا سەجدە قىلىشتىن باش تارتتى.');
INSERT INTO "saleh" VALUES(15,32,'15:32','ئاللاھ: «ئى ئىبلىس! سەن نېمىشقا سەجدە قىلمايسەن؟» دېدى.');
INSERT INTO "saleh" VALUES(15,33,'15:33','ئىبلىس: «قارا لايدىن ياساپ شەكىلگە كىرگۈزۈپ (چەكسە جىرىڭلايدىغان) قۇرۇق لايدىن يارىتىلغان ئىنسان مېنىڭ سەجدە قىلىشىمغا لايىق ئەمەس» دېدى.');
INSERT INTO "saleh" VALUES(15,34,'15:34','ئاللاھ ئېيتتى: «سەن جەننەتتىن يوقال، ھەقىقەتەن قوغلاندى بولدۇڭ.');
INSERT INTO "saleh" VALUES(15,35,'15:35',' ساڭا قىيامەت كۈنىگۈچە ھەقىقەتەن لەنەت بولسۇن».');
INSERT INTO "saleh" VALUES(15,36,'15:36','ئىبلىس: «پەرۋەردىگارىم! ماڭا خالايىقلار تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت كۈنى) گۈچە مۆھلەت بەرگىن (مېنى ھايات قالدۇرغىن)» دېدى.');
INSERT INTO "saleh" VALUES(15,37,'15:37','ئاللاھ: «ساڭا ھەقىقەتەن مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى[37ـ38].');
INSERT INTO "saleh" VALUES(15,38,'15:38','ئاللاھ: «ساڭا ھەقىقەتەن مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى[37ـ38].');
INSERT INTO "saleh" VALUES(15,39,'15:39','ئىبلىس ئېيتتى: «پەرۋەردىگارىم! سەن مېنى گۇمراھ قىلغانلىقىڭ ئۈچۈن، يەر يۈزىدە ئادەم بالىلىرىغا (گۇناھلارنى) ئەلۋەتتە چىرايلىق كۆرسىتىمەن، ئۇلارنىڭ ھەممىسىنى ئەلۋەتتە ئازدۇرىمەن.');
INSERT INTO "saleh" VALUES(15,40,'15:40','ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)».');
INSERT INTO "saleh" VALUES(15,41,'15:41','ئاللاھ ئېيتتى: «بۇ (تاللانغان بەندىلەرنىڭ يولى) مېنىڭ توغرا يولۇمدۇر.');
INSERT INTO "saleh" VALUES(15,42,'15:42','مېنىڭ بەندىلىرىمگە (يەنى ئۇلارنى ئازدۇرۇشقا) سېنىڭ كۈچۈڭ يەتمەيدۇ، پەقەت گۇمراھلاردىن ساڭا ئەگەشكەنلەرگىلا (سېنىڭ كۈچۈڭ يېتىدۇ)».');
INSERT INTO "saleh" VALUES(15,43,'15:43','شۈبھىسىزكى، دوزاخ ئۇلار (يەنى ئىبلىس ۋە ئۇنىڭ تەۋەلىرى) نىڭ ھەممىسىگە ۋەدە قىلىنغان جايدۇر.');
INSERT INTO "saleh" VALUES(15,44,'15:44','جەھەننەمنىڭ يەتتە دەرۋازىسى بولۇپ، ئۇلارنىڭ ھەربىرىدىن كىرىدىغان مۇئەييەن بىر بۆلۈك (ئادەم) بار.');
INSERT INTO "saleh" VALUES(15,45,'15:45','تەقۋادارلار ھەقىقەتەن (ئاخىرەتتە ياپيېشىل) باغ ـ بوستانلاردىن، (سۇ، ھەسەل ۋە مەي ئېقىپ تۇرىدىغان) بۇلاقلاردىن بەھرىمەن بولىدۇ.');
INSERT INTO "saleh" VALUES(15,46,'15:46','(ئۇلارغا) جەننەتكە ئامان ـ ئېسەن، تىنچ كىرىڭلار! دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(15,47,'15:47','ئۇلارنىڭ دىللىرىدىكى دۈشمەنلىكنى چىقىرىپ تاشلايمىز، ئۇلار ئۆزئارا قېرىنداش بولغان ھالدا تەختلەر ئۈستىدە بىر ـ بىرىگە قارىشىپ ئولتۇرىدۇ. ');
INSERT INTO "saleh" VALUES(15,48,'15:48','جەننەتتە ئۇلارغا چارچاش بولمايدۇ، ئۇلار جەننەتتىن چىقىرىۋېتىلمەيدۇ.');
INSERT INTO "saleh" VALUES(15,49,'15:49','(ئى مۇھەممەد!) مېنىڭ (مۆمىن) بەندىلىرىمگە خەۋەر قىلغىنكى، مەن تولىمۇ مەغپىرەت قىلغۇچىمەن، ناھايىتى رەھىم قىلغۇچىمەن.');
INSERT INTO "saleh" VALUES(15,50,'15:50','مېنىڭ ئازابىم قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(15,51,'15:51',' ئۇلارغا ئىبراھىمنىڭ مېھمانلىرى ھەققىدىكى (ھېكايىنى) ئېيتىپ بەرگىن.');
INSERT INTO "saleh" VALUES(15,52,'15:52','ئۆز ۋاقتىدا پەرىشتىلەر ئىبراھىمنىڭ يېنىغا كىرىپ سالام بەردى، ئىبراھىم: «بىز ھەقىقەتەن سىلەردىن قورقۇۋاتىمىز» دېدى.');
INSERT INTO "saleh" VALUES(15,53,'15:53',' ئۇلار: « قورقمىغىن، (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)، ھەقىقەتەن ساڭا (ئۆسۈپ چوڭ بولغىنىدا) ناھايىتى بىلىملىك (بولىدىغان) بىر ئوغۇل (يەنى ئىسھاق) بىلەن خۇشخەۋەر بېرىمىز» دېدى.');
INSERT INTO "saleh" VALUES(15,54,'15:54',' ئىبراھىم: «مەن قېرىغان تۇرسام، ماڭا (بالا بىلەن) خۇشخەۋەر بەردىڭلارمۇ؟ ماڭا نېمە بىلەن خۇشخەۋەر بېرىۋاتىسىلەر؟» دېدى.');
INSERT INTO "saleh" VALUES(15,55,'15:55','ئۇلار: «بىز ساڭا راست خۇشخەۋەر بەردۇق، نائۈمىد بولمىغىن» دېدى.');
INSERT INTO "saleh" VALUES(15,56,'15:56',' ئىبراھىم: «پەرۋەردىگارىنىڭ رەھمىتىدىن گۇمراھلاردىن باشقا كىممۇ نائۈمىد بولار؟» دېدى.');
INSERT INTO "saleh" VALUES(15,57,'15:57',' ئىبراھىم: «ئى ئەلچىلەر! نېمە ئىش بىلەن كەلدىڭلار؟» دېدى.');
INSERT INTO "saleh" VALUES(15,58,'15:58','ئۇلار ئېيتتى: «بىز ھەقىقەتەن گۇناھكار بىر قەۋم (نى جازالاش) ئۈچۈن ئەۋەتىلدۇق.');
INSERT INTO "saleh" VALUES(15,59,'15:59','پەقەت لۇتنىڭ تەۋەلىرىلا بۇنىڭدىن مۇستەسنا. بىز ئۇلار (يەنى لۇتنىڭ تەۋەلىرى ۋە ئۇنىڭ ئائىلىسىدىكىلەر) نىڭ ھەممىسىنى (ئازابتىن) چوقۇم قۇتۇلدۇرىمىز.');
INSERT INTO "saleh" VALUES(15,60,'15:60','پەقەت لۇتنىڭ خوتۇنىلا بۇنىڭدىن مۇستەسنا، بىز ئۇنىڭ چوقۇم باشقىلار بىلەن بىللە ئازابلىنىدىغانلىقىنى ئالدىن بېكىتتۇق».');
INSERT INTO "saleh" VALUES(15,61,'15:61',' ئەلچىلەر لۇتنىڭ ئائىلىسىگە كەلگەن چاغدا، ');
INSERT INTO "saleh" VALUES(15,62,'15:62','لۇت: «سىلەر ھەقىقەتەن ناتونۇش ئادەملەرغۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(15,63,'15:63','ئۇلار ئېيتتى: «ئۇنداق ئەمەس (بىز ئاللاھنىڭ ئەلچىلىرىمىز)، بىز سېنىڭ ئالدىڭغا ئۇلار (يەنى سېنىڭ قەۋمىڭ) شەكلىنىۋاتقان ئازابنى ئېلىپ كەلدۇق.');
INSERT INTO "saleh" VALUES(15,64,'15:64',' بىز ساڭا ھەقىقەتنى ئېلىپ كەلدۇق، بىز ئەلۋەتتە (سۆزىمىزدە) راستچىلمىز.');
INSERT INTO "saleh" VALUES(15,65,'15:65','كېچىنىڭ ئاخىرىدا ئائىلەڭدىكىلەرنى ئېلىپ يولغا چىققىن، ئۆزۈڭ ئۇلارنىڭ ئارقىسىدا ماڭغىن، سىلەردىن ھېچبىر ئادەم ئارقىسىغا قارىمىسۇن، سىلەر بۇيرۇلغان يەرگە يەتكۈچە ئالغا قاراپ مېڭىۋېرىڭلار».');
INSERT INTO "saleh" VALUES(15,66,'15:66','سەھەردە بۇلار (يەنى لۇتنىڭ قەۋمى) نىڭ يىلتىزى قۇرۇتۇلىدۇ (يەنى تەلتۆكۈس ھالاك قىلىنىدۇ) دېگەن ھۆكۈمنى لۇتقا ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(15,67,'15:67',' (سەدۇم) شەھرىنىڭ ئاھالىسى (يەنى لۇتنىڭ قەۋمى) خۇشال بولۇشۇپ يېتىپ كېلىشتى.');
INSERT INTO "saleh" VALUES(15,68,'15:68','لۇت ئېيتتى: «بۇلار مېنىڭ مېھمىنىم، (ئۇلارغا چېقىلىپ قويۇپ ئۇلارنىڭ ئالدىدا) مېنى رەسۋا قىلماڭلار.');
INSERT INTO "saleh" VALUES(15,69,'15:69','ئاللاھتىن قورقۇڭلار (مېھمىنىمنى خارلاش ئارقىلىق) مېنى خارلىماڭلار».');
INSERT INTO "saleh" VALUES(15,70,'15:70','ئۇلار: «بىز سېنى كەلگەن مۇساپىرلارنى ھىمايە قىلىشتىن توسمىغانمىدۇق؟» دېدى.');
INSERT INTO "saleh" VALUES(15,71,'15:71','لۇت: «بۇلار مېنىڭ قىزلىرىم (يەنى قەۋمىمنىڭ قىزلىرى) دۇر. ئەگەر (چېقىلماقچى) بولساڭلار (ئۇلارنى نىكاھلاپ ئېلىڭلار)» دېدى.');
INSERT INTO "saleh" VALUES(15,72,'15:72','(ئى مۇھەممەد!) سېنىڭ ھاياتىڭ بىلەن قەسەمكى، ئۇلار ئەلۋەتتە گۇمراھلىقلىرىدا تېڭىرقاپ يۈرۈشىدۇ.');
INSERT INTO "saleh" VALUES(15,73,'15:73','كۈن چىقار ۋاقتىدا ئۇلارغا قاتتىق چۇقان يۈزلەندى.');
INSERT INTO "saleh" VALUES(15,74,'15:74',' بىز ئۇ شەھەرنى ئاستىن ـ ئۈستۈن قىلىۋەتتۇق (يەنى كۆمتۈرۈۋەتتۇق) ۋە ئۇلارنىڭ ئۈستىگە (يامغۇردەك) ساپال تاش ياغدۇردۇق.');
INSERT INTO "saleh" VALUES(15,75,'15:75','ئۇنىڭدا (يەنى ئۇلارغا نازىل بولغان ئازابتا) كۆزەتكۈچىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(15,76,'15:76','ئۇ شەھەر (قۇرەيشلەر شامغا بارىدىغان) يول ئۈستىدە ھەقىقەتەن ھېلىمۇ مەۋجۇت تۇرماقتا.');
INSERT INTO "saleh" VALUES(15,77,'15:77','بۇنىڭدا مۆمىنلەر ئۈچۈن ھەقىقەتەن ئىبرەت بار.');
INSERT INTO "saleh" VALUES(15,78,'15:78','ئەيكەلىكلەر (يەنى شۇئەيب قەۋمى)، شۈبھىسىزكى، زالىم ئىدى (يەنى كۇفرىدا ھەددىدىن ئاشقانلار ئىدى).');
INSERT INTO "saleh" VALUES(15,79,'15:79','(شۇئەيبنىڭ پەيغەمبەرلىكىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۇلارنى ھالاك قىلدۇق، ئۇلار (يەنى بۇ ئىككى شەھەر) نىڭ ھەر ئىككىلىسى، شۈبھىسىزكى، ئاشكارا يول ئۈستىدىدۇر (ئى مەككە ئاھالىسى! ئۇلاردىن ئىبرەت ئالمامسىلەر؟).');
INSERT INTO "saleh" VALUES(15,80,'15:80','ھىجرىلىكلەر ھەقىقەتەن پەيغەمبەرلەرنى ئىنكار قىلدى (يەنى ئۇلارنىڭ سالىھ ئەلەيھىسسالامنى ئىنكار قىلغانلىقى باشقا پەيغەمبەرلەرنىمۇ ئىنكار قىلغانلىقىدۇر).');
INSERT INTO "saleh" VALUES(15,81,'15:81','ئۇلارغا مۆجىزىلىرىمىزنى كەلتۈردۇق (يەنى تاشتىن چىشى تۆگە چىقىرىشتەك قۇدرىتىمىزگە دالالەت قىلىدىغان مۆجىزىلەرنى ئۇلارغا كۆرسەتتۇق)، ئۇلار بۇ (مۆجىزە) لەردىن يۈز ئۆرۈدى.');
INSERT INTO "saleh" VALUES(15,82,'15:82','ئۇلار (ئازابتىن) ئەمىن بولغان ھالدا تاغلارنى تېشىپ (بۇ بىزنى ئاللاھنىڭ ئازابىدىن ساقلايدۇ دەپ) ئۆي سالاتتى.');
INSERT INTO "saleh" VALUES(15,83,'15:83','لېكىن قاتتىق چۇقان سەھەردىلا ئۇلارنى ھالاك قىلدى.');
INSERT INTO "saleh" VALUES(15,84,'15:84','ئۇلارنىڭ قىلغان ئەمگەكلىرى ئۆزلىرىگە ئەسقاتمىدى (يەنى ئۇلارنىڭ سالغان بىنالىرى ئۇلارنى ئاللاھنىڭ ئازابىدىن ساقلاپ قالالمىدى).');
INSERT INTO "saleh" VALUES(15,85,'15:85','بىز ئاسمانلارنى، زېمىننى ۋە ئۇلاردىكى نەرسىلەرنى پەقەت ھەق ئاساسىدىلا ياراتتۇق، قىيامەتنىڭ بولۇشى چوقۇمدۇر، (ئى مۇھەممەد! نادانلار بىلەن تەڭ بولمىغىن، ئۇلارغا) چىرايلىق مۇئامىلىدە بولغىن.');
INSERT INTO "saleh" VALUES(15,86,'15:86','پەرۋەردىگارىڭ ھەقىقەتەن ھەممىنى ياراتقۇچىدۇر، (بەندىلەرنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(15,87,'15:87','(نامازدا) تەكرارلىنىپ تۇرىدىغان يەتتە ئايەتنى (يەنى سۈرە فاتىھەنى) ۋە ئۇلۇغ قۇرئاننى ھەقىقەتەن ساڭا ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(15,88,'15:88','بەزى كاپىرلارنىڭ بىز بەھرىمەن قىلغان نەرسىلىرىگە كۆز سالمىغىن، ئۇلار (نىڭ ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، مۆمىنلەرگە كەمتەر بولغىن.');
INSERT INTO "saleh" VALUES(15,89,'15:89','«مەن ھەقىقەتەن (سىلەرنى ئاللاھنىڭ ئازابىنىڭ نازىل بولۇشىدىن) ئاشكارا ئاگاھلاندۇرغۇچىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(15,90,'15:90',' پارچىلىۋەتكۈچىلەرگە (يەنى يەھۇدىيلار ۋە ناسارالارغا تەۋرات بىلەن ئىنجىلنى) نازىل قىلغاندەك (ساڭا سۈرە فاتىھە بىلەن ئۇلۇغ قۇرئاننى نازىل قىلدۇق).');
INSERT INTO "saleh" VALUES(15,91,'15:91','ئۇلار (يەنى يەھۇدىيلار ۋە ناسارالار) قۇرئاننى پارچە ـ پارچە قىلىۋەتتى (يەنى قۇرئاننىڭ بەزىسىگە ئىشىنىپ، بەزىسىنى ئىنكار قىلدى).');
INSERT INTO "saleh" VALUES(15,92,'15:92','پەرۋەردىگارىڭنىڭ نامى بىلەن قەسەمكى، ئۇلار (يەنى خالايىق) نىڭ ھەممىسىنىڭ (بۇ دۇنيادا) قىلغان ئىشلىرىنى چوقۇم سورايمىز[92ـ 93].');
INSERT INTO "saleh" VALUES(15,93,'15:93','پەرۋەردىگارىڭنىڭ نامى بىلەن قەسەمكى، ئۇلار (يەنى خالايىق) نىڭ ھەممىسىنىڭ (بۇ دۇنيادا) قىلغان ئىشلىرىنى چوقۇم سورايمىز[92ـ 93].');
INSERT INTO "saleh" VALUES(15,94,'15:94','ساڭا بۇيرۇلغاننى (يەنى پەرۋەردىگارىڭنىڭ ئەمرىنى) ئاشكارا ئوتتۇرىغا قويغىن، مۇشرىكلار (نىڭ مەسخىرىلىرىگە) پەرۋا قىلمىغىن.');
INSERT INTO "saleh" VALUES(15,95,'15:95','سېنى مەسخىرە قىلغۇچىلارغا بىز چوقۇم تېتىيمىز.');
INSERT INTO "saleh" VALUES(15,96,'15:96','ئۇلار باشقا مەبۇدلارنى ئاللاھقا شېرىك قىلىدۇ، ئۇلار ئۇزاققا قالماي (ئىشنىڭ ئاقىۋىتىنى) بىلىدۇ.');
INSERT INTO "saleh" VALUES(15,97,'15:97',': بىز ئۇلارنىڭ سۆزلىرىدىن يۈرىكىڭنىڭ سېقىلىۋاتقانلىقىنى ئوبدان بىلىمىز. ');
INSERT INTO "saleh" VALUES(15,98,'15:98','پەرۋەردىگارىڭغا ھەمدۇ ۋە تەسبىھ ئېيتقىن، ھەم سەجدە قىلغۇچىلاردىن بولغىن. ');
INSERT INTO "saleh" VALUES(15,99,'15:99','سەن ئۆزۈڭگە ئۆلۈم كەلگەنگە (يەنى ئەجىلىڭ يەتكەنگە) قەدەر پەرۋەردىگارىڭغا ئىبادەت قىلغىن. ');
INSERT INTO "saleh" VALUES(16,1,'16:1','ئاللاھنىڭ پەرمانى (يەنى قىيامەت) چوقۇم كېلىدۇ، ئۇنىڭغا ئالدىراپ كەتمەڭلار، ئاللاھ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە يۈكسەكتۇر. ');
INSERT INTO "saleh" VALUES(16,2,'16:2','ئاللاھ ئۆز ئىرادىسى بويىچە خالىغان بەندىلىرىگە (يەنى پەيغەمبەرلىرىگە) پەرىشتىلەرنى ۋەھىي بىلەن نازىل قىلىپ (مۇنداق دەيدۇ): «(ئىنسانلارنى) ئاگاھلاندۇرۇڭلاركى، مەندىن باشقا ھېچ ئىلاھ يوقتۇر، مەندىن (يەنى ئازابىمدىن) قورقۇڭلار».');
INSERT INTO "saleh" VALUES(16,3,'16:3',' ئاللاھ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، ئاللاھ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن يۈكسەكتۇر.');
INSERT INTO "saleh" VALUES(16,4,'16:4',' ئۇ ئىنساننى ئابىمەنىيدىن ياراتتى، ئۇ بولسا (ئاللاھقا) ئاشكارا خۇسۇمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(16,5,'16:5','ئاللاھ چارۋا ماللارنى سىلەرنىڭ مەنپەئەتلىنىشىڭلار ئۈچۈن ياراتتى، ئۇلار (نىڭ يۇڭى ۋە تېرىسى) بىلەن ئىسسىنىسىلەر، ئۇلار (نىڭ نەسلى، سۈتى، سۆڭىكى ۋە قىغى) دىن پايدىلىنىسىلەر ۋە ئۇلار (نىڭ گۆشلىرى) نى يەيسىلەر.');
INSERT INTO "saleh" VALUES(16,6,'16:6','ئۇلارنى كەچتە ئوتلاقتىن قايتۇرۇپ كەلگەن ۋە ئەتىگەندە ئوتلاققا ئېلىپ چىققان ۋاقتىڭلاردا، چارۋا ماللار سىلەرگە زىننەت بولۇپ (خۇشاللىق بېغىشلايدۇ).');
INSERT INTO "saleh" VALUES(16,7,'16:7','ئۇلار يۈك ـ تاقلىرىڭلارنى سىلەر جاپا ـ مۇشەققەت بىلەن ئاران يېتىپ بارالايدىغان جايلارغا كۆتۈرۈپ بارىدۇ، سىلەرنىڭ پەرۋەردىگارىڭلار سىلەرگە ھەقىقەتەن شەپقەتلىكتۇر ۋە مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(16,8,'16:8','ئاللاھ ئاتنى، خېچىرنى ۋە ئېشەكنى مىنىشىڭلار ئۈچۈن ۋە زىننەت ئۈچۈن ياراتتى، ئاللاھ يەنە سىلەر بىلمەيدىغان نەرسىلەرنىمۇ يارىتىدۇ.');
INSERT INTO "saleh" VALUES(16,9,'16:9','توغرا يول كۆرسىتىشنى ئاللاھ ئۆز ئۈستىگە ئالغان. بەزى يوللار ئەگرى بولىدۇ، مۇبادا ئاللاھ خالىسا ئىدى، سىلەرنىڭ ھەممىڭلارنى ئەلۋەتتە توغرا يولغا سالغان بولاتتى.');
INSERT INTO "saleh" VALUES(16,10,'16:10','ئاللاھ سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭدىن ئىچىسىلەر ۋە شۇ يامغۇر ئارقىلىق ئۆسكەن دەل ـ دەرەخلەر بىلەن چارۋاڭلارنى باقىسىلەر.');
INSERT INTO "saleh" VALUES(16,11,'16:11','ئاللاھ سىلەرگە شۇ يامغۇر بىلەن زىرائەتلەرنى ئۆستۈرۈپ بېرىدۇ، زەيتۇن، خورما، ئۈزۈم ۋە تۈرلۈك مېۋىلەرنى ئۆستۈرۈپ بېرىدۇ، چوڭقۇر پىكىر قىلىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان دەلىللەر بار.');
INSERT INTO "saleh" VALUES(16,12,'16:12',' ئاللاھ سىلەرگە كېچىنى، كۈندۈزنى، كۈننى، ئاينى بويسۇندۇرۇپ بەردى، يۇلتۇزلارمۇ ئاللاھنىڭ ئەمرى بىلەن بويسۇندۇرۇلغاندۇر؛ چۈشىنىدىغان قەۋم ئۈچۈن بۇنىڭدا نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(16,13,'16:13','ئاللاھ يەنە يەر يۈزىدە سىلەر ئۈچۈن ياراتقان رەڭگارەڭ نەرسىلەرنى (سىلەرگە بويسۇندۇرۇپ بەردى)، ۋەز ـ نەسىھەت ئالىدىغان قەۋم ئۈچۈن بۇنىڭدا ئەلۋەتتە ئىبرەت بار.');
INSERT INTO "saleh" VALUES(16,14,'16:14','ئاللاھ سىلەرنى دېڭىزنىڭ يېڭى گۆشلىرى (يەنى بېلىقلىرى) نى يېسۇن، تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە ـ مارجانلار) نى چىقارسۇن دەپ، سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى. ئاللاھنىڭ نېمەتلىرىنى تەلەپ قىلىشىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىشىڭلار ئۈچۈن، ( ئاللاھنىڭ بويسۇندۇرۇشى بىلەن يېمەك ـ ئىچمەكلەر ۋە يۈك ـ تاقلار قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرۈسەن.');
INSERT INTO "saleh" VALUES(16,15,'16:15','زېمىننىڭ سىلەرنى تەۋرىتىۋەتمەسلىكى ئۈچۈن، ئاللاھ ئۇنىڭدا تاغلارنى بەرپا قىلدى، (زىرائەتلىرىڭلارنى ، چارۋا -ماللىرىڭلارنى سۇغىرىشىڭلار ئۈچۈن) ئېرىق ـ ئۆستەڭلەرنى، ئېزىپ قالماسلىقىڭلار ئۈچۈن يوللارنى پەيدا قىلدى.');
INSERT INTO "saleh" VALUES(16,16,'16:16','(يوللارغا) بەلگىلەرنى قىلدى. ئۇلار (كېچىسى قۇرۇقلۇقتا ۋە دېڭىزلاردا) يۇلتۇزلار بىلەن يول تاپىدۇ.');
INSERT INTO "saleh" VALUES(16,17,'16:17','(پۈتۈن كائىناتنى) ياراتقان ئاللاھ بىلەن (ھېچ نەرسىنى) يارىتالمايدىغانلار ئوخشاش بولامدۇ؟ ئويلىمامسىلەر.');
INSERT INTO "saleh" VALUES(16,18,'16:18','ئەگەر ئاللاھنىڭ (سىلەرگە بەرگەن) نېمىتىنى سانىساڭلار ، ئۇنىڭ ھېسابىنى ئېلىپ بولالمايسىلەر. شۈبھىسىزكى، ئاللاھ سىلەر (نىڭ بۇ جەھەتتىكى يېتەرسىزلىكىڭلار) نى ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، (بەندىلىرىگە) مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(16,19,'16:19','ئاللاھ سىلەر يوشۇرغان ۋە ئاشكارا قىلغاننىڭ ھەممىسىنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(16,20,'16:20','ئۇلارنىڭ ئاللاھنى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) ھېچ شەيئىنى يارىتالمايدۇ، بەلكى ئۇلارنىڭ ئۆزلىرى يارىتىلغاندۇر (يەنى ئىنسانلار تەرىپىدىن ياسالغاندۇر).');
INSERT INTO "saleh" VALUES(16,21,'16:21','ئۇلار (يەنى بۇتلار) تىرىك ئەمەس، ئۆلۈكتۈر ، ئۇلار (چوقۇنغۇچىلىرىنىڭ) قاچان قايتا تىرىلدۈرۈلىدىغانلىقىنى ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(16,22,'16:22','سىلەرنىڭ (ئىبادەت قىلىشقا لايىق) ئىلاھىڭلار بىردۇر، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى ( ئاللاھنىڭ بىرلىكىنى) ئىنكار قىلىدۇ، ئۇلار چوڭچىلىق قىلىپ، (ھەقنى قوبۇل قىلىشتىن) باش تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(16,23,'16:23','ئاللاھ ئۇلارنىڭ يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى راستلا بىلىپ تۇرىدۇ، ئاللاھ تەكەببۇرلۇق قىلغۇچىلارنى ھەقىقەتەن دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(16,24,'16:24','ئۇلارغا (يەنى مۇشرىكلارغا): «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار (مەسخىرە قىلىپ): «قەدىمكىلەرنىڭ ئەپسانىلىرىنى (نازىل قىلدى)» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(16,25,'16:25','ئۇلار قىيامەت كۈنى ئۆزلىرىنىڭ گۇناھلىرىغا تولۇق جاۋابكار بولۇشلىرى ۋە ئۆزلىرى تەرىپىدىن بىلمەستىن ئازدۇرۇلغانلارنىڭ بىر قىسىم گۇناھلىرىغا ئۆزلىرى جاۋابكار بولۇشلىرى ئۈچۈن (يۇقىرىقى بوھتان سۆزلەرنى قىلدى)، ئۇلارنىڭ جاۋابكار بولىدىغىنى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(16,26,'16:26','ئۇلار (يەنى مەككە كاپىرلىرى) دىن ئىلگىرى ئۆتكەن كاپىرلار (ئۆز پەيغەمبەرلىرىگە) ھىيلە ـ مىكىر ئىشلىتىپ ( ئاللاھنىڭ نۇرىنى ئۆچۈرمەكچى بولدى). ئاللاھ ئۇلارنىڭ (ھىيلە ـ مىكىر) بىناسىنى تەلتۆكۈس گۇمران قىلدى، ئۇلارنىڭ ئۈستىگە (بۇ بىنانىڭ) ئۆگزىسى ئۆرۈلۈپ چۈشتى، ئۇلارغا ئۆزلىرى ئويلىمىغان يەردىن ئازاب كەلدى. ');
INSERT INTO "saleh" VALUES(16,27,'16:27','«مېنىڭ شېرىكلىرىم قېنى قەيەردە؟ سىلەر ئۇلار توغرىسىدا (پەيغەمبەرلەر بىلەن) مۇنازىرىلىشەتتىڭلارغۇ؟» دەيدۇ. ئىلىم بېرىلگەنلەر (يەنى ئۇلارنى ئىمانغا دەۋەت قىلغان پەيغەمبەرلەر ۋە ئۆلىمالار): «بۈگۈن رەسۋالىق ۋە ئازاب ھەقىقەتەن كاپىرلارغا تېگىشلىكتۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(16,28,'16:28',' ئۇلار (كۇفرىنى ئىختىيار قىلىپ) ئۆزلىرىگە زۇلۇم قىلغان ھالەتتە، پەرىشتىلەر ئۇلارنىڭ جانلىرىنى ئالىدۇ. ئۇلار بويسۇنغانلىقلىرىنى بىلدۈرۈپ: «بىز ھېچقانداق يامان ئىش قىلمىدۇق» (دەيدۇ). ئۇنداق ئەمەس، ئاللاھ سىلەرنىڭ قىلمىشىڭلارنى ھەقىقەتەن تولۇق بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(16,29,'16:29','سىلەر (ئىچىدە) مەڭگۈ قالىدىغان جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، چوڭچىلىق قىلىپ، (ئاللاھقا ئىتائەت قىلىشتىن) باش تارتقۇچىلارنىڭ بارىدىغان جايى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(16,30,'16:30','تەقۋادارلارغا: «پەرۋەردىگارىڭلار (پەيغەمبىرىگە) نېمىلەرنى نازىل قىلدى؟» دېيىلسە، ئۇلار: «ياخشى سۆزلەرنى نازىل قىلدى» دېيىشىدۇ. ياخشى ئىش قىلغۇچىلار بۇ دۇنيادا مۇكاپاتلىنىدۇ، ئاخىرەت يۇرتى (ئۇلار ئۈچۈن تېخىمۇ) ياخشىدۇر. تەقۋادارلارنىڭ يۇرتى راستلا نېمىدېگەن گۈزەل! ');
INSERT INTO "saleh" VALUES(16,31,'16:31','ئۇلار ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان دائىم تۇرىدىغان جەننەتلەرگە كىرىدۇ، ئۇ يەردە ئۇلارنىڭ كۆڭلى خالىغان نەرسىلەر تېپىلىدۇ، ئاللاھ تەقۋادارلارنى ئەنە شۇنداق مۇكاپاتلايدۇ. ');
INSERT INTO "saleh" VALUES(16,32,'16:32','ئۇلار (مۇشرىكلىك ۋە گۇناھلاردىن) پاك بولغان شارائىتتا پەرىشتىلەر ئۇلارنى قەبزى روھ قىلىدۇ، پەرىشتىلەر ئۇلارغا: «سالام سىلەرگە! قىلغان ئەمەلىڭلارنىڭ خاسىيىتىدىن جەننەتكە كىرىڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(16,33,'16:33',' كاپىرلار پەقەت (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن) پەرىشتىلەرنىڭ چۈشۈشىنى ياكى پەرۋەردىگارىڭنىڭ ئەمرىنىڭ (يەنى ئازابىنىڭ) چۈشۈشىنىلا كۈتىدۇ، ئۇلاردىن بۇرۇن ئۆتكەن (كاپىر) لارمۇ ئەنە شۇنداق قىلغان ئىدى. ئاللاھ ئۇلارغا زۇلۇم قىلغىنى يوق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(16,34,'16:34','ئۇلار قىلمىشلىرىنىڭ جازاسىنى تارتتى، ئۇلارغا مەسخىرە قىلغانلىقلىرىنىڭ جازاسى (يەنى قاتتىق ئازابى) چۈشتى.');
INSERT INTO "saleh" VALUES(16,35,'16:35','مۇشرىكلار: «مۇبادا ئاللاھ خالىغان بولسا، بىز ۋە بىزنىڭ ئاتا -بوۋىلىرىمىز ئاللاھنى قويۇپ ھېچ نەرسىگە ئىبادەت قىلمىغان بولاتتۇق، (ئاللاھنىڭ ھۆكمىسىز) ھېچ نەرسىنى ھارام قىلمىغان بولاتتۇق» دېدى. ئۇلاردىن ئىلگىرىكىلەرمۇ ئەنە شۇنداق قىلغان ئىدى. پەيغەمبەرلەرنىڭ مەسئۇلىيىتى پەقەت روشەن تەبلىغ قىلىشتىنلا ئىبارەت (ھىدايەت قىلىش بولسا ئاللاھنىڭ ئىشىدۇر).');
INSERT INTO "saleh" VALUES(16,36,'16:36',' بىز ھەقىقەتەن ھەر بىر ئۈممەتكە: «ئاللاھقا ئىبادەت قىلىڭلار، تاغۇتتىن (يەنى شەيتانغا، بۇتلارغا، كاھىنلارغا ئوخشاش ھەرقانداق مەبۇدقا ئىبادەت قىلىشتىن) يىراق بولۇڭلار» دەپ پەيغەمبەر ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدا ئاللاھ ھىدايەت قىلغانلىرىمۇ بار، گۇمراھلىققا تېگىشلىك بولغانلىرىمۇ بار. يەر يۈزىدە سەير قىلىپ يۈرۈپ، (پەيغەمبەرلەرنى) ئىنكار قىلغانلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزىتىڭلار.');
INSERT INTO "saleh" VALUES(16,37,'16:37',' سەن ئۇلارنىڭ ھىدايەت تىپىشىغا ھېرىس بولساڭمۇ، ئاللاھ گۇمراھلىقىدا قالدۇرغان كىشىنى ھىدايەت قىلمايدۇ، ئۇلارنى (ئاللاھنىڭ ئازابىدىن قۇتۇلدۇرىدىغان) ھېچقانداق مەدەتكار يوق.');
INSERT INTO "saleh" VALUES(16,38,'16:38','ئۇلار: «ئاللاھ ئۆلۈكنى تىرىلدۈرمەيدۇ » دەپ قاتتىق قەسەم ئىچىشتى، ئۇنداق ئەمەس (يەنى ئۆلۈكنى تىرىلدۈرىدۇ)، ئاللاھ ۋەدىسىنى چوقۇم ئىشقا ئاشۇرىدۇ، لېكىن ئىنسانلارنىڭ تولىسى ( ئاللاھنىڭ قۇدرىتىنى) بىلمەيدۇ (شۇڭلاشقا ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلىدۇ ). ');
INSERT INTO "saleh" VALUES(16,39,'16:39',' ئاللاھ ئۇلار ئىختىلاپ قىلىشقان ھەق بىلەن ناھەقنى ئۇلارغا ئايرىپ بېرىش ۋە كاپىرلارغا ئۇلارنىڭ يالغانچىلىقلىرىنى بىلدۈرۈپ قويۇش ئۈچۈن (ئۇلارنى قايتىدىن تىرىلدۈرىدۇ). ');
INSERT INTO "saleh" VALUES(16,40,'16:40',' بىز بىرەر شەيئىنى ۋۇجۇدقا كەلتۈرمەكچى بولساق، ئۇنىڭغا: «ۋۇجۇدقا كەل» دەيمىز ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ.');
INSERT INTO "saleh" VALUES(16,41,'16:41','زۇلۇمغا ئۇچرىغاندىن كېيىن ئاللاھ يولىدا ھىجرەت قىلغانلارنى دۇنيادا چوقۇم ياخشى جايغا ئورۇنلاشتۇرىمىز، ئۇلار بىلىدىغان بولسا، ئۇلارغا ئاخىرەتنىڭ ساۋابى تېخىمۇ چوڭدۇر. ');
INSERT INTO "saleh" VALUES(16,42,'16:42','ئۇلار (ئېغىرچىلىقلارغا) سەۋر قىلدى، ئۇلار (ھەممە ئىشتا) پەرۋەردىگارىغا تەۋەككۈل قىلىدۇ.');
INSERT INTO "saleh" VALUES(16,43,'16:43',' سەندىن ئىلگىرى بىز پەقەت ئەرلەرگىلا ۋەھىي نازىل قىلىپ، ئۇلارنى پەيغەمبەر قىلىپ ئەۋەتتۇق، (ئى قۇرەيش جامائەسى!) بۇنى بىلمىسەڭلار، ئىلىم ئەھلىلىرى (يەنى تەۋرات ۋە ئىنجىلنى بىلگۈچىلەر) دىن سوراڭلار.');
INSERT INTO "saleh" VALUES(16,44,'16:44','(پەيغەمبەرلەرنى ئۆزلىرىنىڭ راستلىقىنى كۆرسىتىپ بېرىدىغان) دەلىللەر ۋە مۇقەددەس كىتابلار بىلەن (ئەۋەتتۇق)، بىز ساڭا قۇرئاننى ئىنسانلارغا چۈشۈرۈلگەن شەرىئەتنى بايان قىلىپ بەرسۇن، ئۇلار (بۇ قۇرئاننى) تەپەككۇر قىلىپ (ئۇنىڭدىن ۋەز ـ نەسىھەت ئالسۇن) دەپ نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(16,45,'16:45','ھىيلە ـ مىكىر ئىشلىتىپ يامان ئىش قىلغانلار ئاللاھنىڭ ئۆزلىرىنى يەرگە يۇتقۇزۇۋېتىشىدىن ياكى ئۆزلىرىگە ئويلىمىغان يەردىن ئازاب كېلىشىدىن قورقمامدۇ؟');
INSERT INTO "saleh" VALUES(16,46,'16:46','ياكى ئۆزلىرىنى سەپەر ئۇستىدىكى چاغلىرىدا جازاغا يولۇقتۇرۇشىدىن قورقمامدۇ؟ (بۇ چاغدا) ئۇلار قېچىپ قۇتۇلالمايدۇ.');
INSERT INTO "saleh" VALUES(16,47,'16:47','ياكى (ئاللاھنىڭ) ئۆزلىرىنى قەدەممۇقەدەم جازالىشىدىن قورقمامدۇ؟ سىلەرنىڭ پەرۋەردىگارىڭلار ئەلۋەتتە مەرھەمەتلىكتۇر، مېھرىباندۇر (شۇڭا سىلەرنى ئالدىراپ جازالىمىدى). ');
INSERT INTO "saleh" VALUES(16,48,'16:48',' ئۇلار ئاللاھ ياراتقان شەيئىلەرنىڭ سايىلىرى بويسۇنغان ھالدا ئاللاھقا سەجدە قىلىش يۈزىسىدىن ئوڭ ـ سولغا مايىل بولۇپ تۇرغانلىقىنى كۆرمىدىمۇ؟');
INSERT INTO "saleh" VALUES(16,49,'16:49','ئاسمانلاردىكى ۋە زېمىندىكى ھايۋاناتلار، پەرىشتىلەر ئاللاھقا سەجدە قىلىدۇ، ئۇلار ( ئاللاھقا سەجدە قىلىشتىن) چوڭچىلىق قىلىپ باش تارتمايدۇ. ');
INSERT INTO "saleh" VALUES(16,50,'16:50',' ئۇلار ئۆز ئۈستىدىكى پەرۋەردىگارىدىن قورقىدۇ، ئۆزلىرىگە بۇيرۇلغاننى قىلىدۇ.');
INSERT INTO "saleh" VALUES(16,51,'16:51','ئاللاھ: «ئىككى ئىلاھقا ئىبادەت قىلماڭلار، ئۇ پەقەت بىرلا ئىلاھتۇر، مەندىنلا قورقۇڭلار» دېدى.');
INSERT INTO "saleh" VALUES(16,52,'16:52','ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، ئىبادەت ھەمىشە ئاللاھقا خاستۇر، ئاللاھتىن باشقا بىرىدىن قورقامسىلەر؟');
INSERT INTO "saleh" VALUES(16,53,'16:53','سىلەر بەھرىمەن بولۇۋاتقان نېمەتلەرنىڭ ھەممىسىنى ئاللاھ بەرگەن، سىلەرگە بىر ئېغىرچىلىق يەتسە، ئاللاھقا يالۋۇرۇپ دۇئا قىلىسىلەر. ');
INSERT INTO "saleh" VALUES(16,54,'16:54',' ئاللاھ سىلەردىن ئېغىرچىلىقنى كۆتۈرۈۋەتسە ، ئاراڭلاردىن بىر بۆلۈك ئادەم دەرھال ئۆز پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ.');
INSERT INTO "saleh" VALUES(16,55,'16:55',' ئۇلار ئۆزلىرىگە بەرگەن (بېشىدىكى ئېغىرچىلىقنى كۆتۈرۈۋەتكەنلىكىمىزدىن ئىبارەت) نېمىتىمىزگە ناشۈكۈرلۈك قىلىدۇ، (سىلەر ئەجىلىڭلار يەتكۈچە دۇنيادىن) بەھرىمەن بولۇڭلار، (ئىشىڭلارنىڭ ئاقىۋىتىنى) ئۇزاققا قالماي بىلىسىلەر.');
INSERT INTO "saleh" VALUES(16,56,'16:56',' ئۇلار بىز ئۆزلىرىگە بەرگەن رىزىقتىن ھېچنەرسىدىن خەۋەرسىز (بۇتلىرىغا) نېسىۋە قالدۇرىدۇ. (ئى مۇشرىكلار!) ئاللاھنىڭ نامى بىلەن قەسەمكى، سىلەر ئۆزۈڭلار ئويدۇرۇپ چىقارغان نەرسەڭلار ئۈچۈن چوقۇم سوراققا تارتىلىسىلەر.');
INSERT INTO "saleh" VALUES(16,57,'16:57','ئۇلار ئاللاھقا قىزلارنى نىسبەت بېرىدۇ (ئاللاھنىڭ قىزى بار دەپ ئېتىقاد قىلىدۇ)، ئاللاھ (بۇنداق بوھتاندىن) پاكتۇر، ئۇلار ئۆزى ئارزۇ قىلىدىغانلىرىنى ئۆزلىرىگە نىسبەت بېرىدۇ.');
INSERT INTO "saleh" VALUES(16,58,'16:58','ئۇلارنىڭ بىرەرسىگە (خوتۇنىنىڭ) قىز تۇغقانلىق خوشخەۋىرى يەتكۈزۈلسە، چىرايى ئۆزگىرىپ، غەزەپناك بولۇپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(16,59,'16:59','يەتكۈزۈلگەن يامان خەۋەردىن قورقۇپ، ئۆز قەۋمىگە كۆرۈنمەي يوشۇرۇنۇۋالىدۇ. ئاندىن ئۇ نومۇسقا چىداپ قىزنى ساقلاپ قالامدۇ ياكى ئۇنى توپا ئاستىغا (تىرىك) كۆمەمدۇ؟ (شۇ ھەقتە ئويلىنىدۇ) ئۇلارنىڭ ھۆكمى (يەنى ئوغۇللارنى ئۆزلىرىگە نىسبەت بېرىپ، قىزلارنى ئاللاھقا نىسبەت بېرىشى) ھەقىقەتەن نېمىدېگەن قەبىھ!');
INSERT INTO "saleh" VALUES(16,60,'16:60',' ئاخىرەتكە ئىشەنمەيدىغانلار (ئوغۇلنى ياخشى كۆرۈپ، قىزنى يامان كۆرۈش، خوتۇنى ئوغۇل تۇغسا خۇشال بولۇپ، قىز تۇغسا ئاچچىقى كېلىش، ئار ـ نومۇستىن ۋە كەمبەغەللىكتىن قورقۇپ، قىزلارنى تىرىك كۆمۈشكە ئوخشاش) يامان سۈپەتلەرگە ئىگە، ئاللاھ بولسا ئەڭ ئالىي خىسلەتلەرگە ئىگە، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(16,61,'16:61',' ئەگەر ئاللاھ ئىنسانلارنى ئۇلارنىڭ زۇلمى تۈپەيلىدىن (چاپسان) جازالايدىغان بولسا، يەر يۈزىدە قىمىرلاپ تۇرىدىغان ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى، لېكىن ئاللاھ ئۇلارنى (جازالاشنى) مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلارنىڭ (ھالاك بولۇش) ۋاقتى كەلگەندە قىلچىمۇ كېچىكتۈرۈلمەيدۇ، قىلچىمۇ ئىلگىرى سۈرۈلمەيدۇ.');
INSERT INTO "saleh" VALUES(16,62,'16:62',' ئۇلار ئۆزلىرى يامان كۆرۈدىغاننى ئاللاھقا مەنسۇپ قىلىدۇ، ئۇلار ئاقىۋىتىنىڭ ياخشى بولىدىغانلىقى بىلەن جۆيلۈيدۇ، شۈبھىسىزكى، ئۇلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلار (دوزاختا) تاشلىنىپ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(16,63,'16:63',' (ئى مۇھەممەد!) ئاللاھ نامى بىلەن قەسەمكى، سەندىن ئىلگىرى ئۆتكەن ئۈممەتلەرگە ھەقىقەتەن (پەيغەمبەرلەرنى) ئەۋەتتۇق، شەيتان ئۇلارنىڭ (يەنى ئۈممەتلەرنىڭ) ئەمەللىرىنى ئۆزلىرىگە چىرايلىق كۆرسەتتى. شەيتان بۈگۈن ئۇلارنىڭ دوستىدۇر. ئۇلار ئاخىرەتتە قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(16,64,'16:64',' (ئى مۇھەممەد!) بىز كىتابنى (يەنى قۇرئاننى) پەقەت سېنىڭ كىشىلەرگە ئۇلار (دىنىدىكى) دەتالاش قىلىشقان نەرسىلەرنى بايان قىلىپ بېرىشىڭ ئۈچۈن، ئىمان ئېيتىدىغان قەۋمگە ھىدايەت ۋە رەھمەت بولسۇن ئۈچۈنلا نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(16,65,'16:65','ئاللاھ بۇلۇتتىن يامغۇر ياغدۇرۇپ، شۇنىڭ بىلەن قاغجىراپ قالغان زېمىندا (تۈرلۈك زىرائەتلەرنى) ئۈندۈردى، بۇنىڭدا (ۋەز ـ نەسىھەت) ئاڭلايدىغان قەۋم ئۈچۈن، ئەلۋەتتە، (ئاللاھنىڭ قۇدرىتىنىڭ چوڭلۇقىنى كۆرسىتىدىغان) دەلىللەر بار.');
INSERT INTO "saleh" VALUES(16,66,'16:66','چاھارپايلاردا سىلەر ئۈچۈن ھەقىقەتەن بىر ئىبرەت بار. سىلەرنى چاھارپايلارنىڭ قارنىدىكى ماياق ـ تېزەكتىن ۋە قاندىن ئايرىلىپ چىققان پاكىز ۋە تەملىك سۈت بىلەن سۇغىرىمىز.');
INSERT INTO "saleh" VALUES(16,67,'16:67',' (سىلەر ئۈچۈن يەنە بۇنداق ئىبرەتمۇ باركى) خورمىدىن، ئۈزۈمدىن شارابلار ۋە پاكىز يېمەكلىكلەر ياسايسىلەر، ئەقىلنى ئىشلىتىدىغان قەۋم ئۈچۈن بۇنىڭدا ھەقىقەتەن ئاللاھنىڭ بىرلىكىنى كۆرسىتىدىغان دەلىل بار. ');
INSERT INTO "saleh" VALUES(16,68,'16:68','پەرۋەردىگارىڭ ھەسەل ھەرىسىگە ئىلھام بىلەن بىلدۈردى: «تاغلارغا، دەرەخلەرگە ۋە (كىشىلەرنىڭ ھەسەل ھەرىلىرى ئۈچۈن) ياسىغان ئۆيلىرىگە ئۇۋا تۈزگىن. ');
INSERT INTO "saleh" VALUES(16,69,'16:69',' ئاندىن كېيىن، تۈرلۈك مېۋىلەر (يەنى گۈل ـ چېچەكلەر) دىن يېگىن، پەرۋەردىگارىڭنىڭ يوللىرىغا ئىتائەتمەنلىك بىلەن كىرگىن». ئۇنىڭ (يەنى ھەسەل ھەرىسىنىڭ) قارنىدىن ئىنسانلارغا شىپا بولىدىغان خىلمۇ ـ خىل رەڭلىك ئىچىملىك (يەنى ھەسەل) چىقىدۇ. بۇنىڭدا (ئاللاھنىڭ قۇدرىتىنىڭ چوڭلۇقىنى) تەپەككۇر قىلىدىغان قەۋم ئۈچۈن ئىبرەت بار.');
INSERT INTO "saleh" VALUES(16,70,'16:70',' ئاللاھ سىلەرنى ياراتتى، ئاندىن (ئەجىلىڭلار پۈتكەندە) سىلەرنى قەبزى روھ قىلىدۇ، ئاراڭلاردا ئۆمۈرنىڭ يامان (يەنى ئەڭ قېرىلىق) باسقۇچىغا ئۇلىشىدىغانلارمۇ بار، ئۇلار ھەتتا (بىر نەرسىلەرنى) بىلگەندىن كېيىن، ھېچ نەرسىنى بىلمەس بولۇپ قالىدۇ (يەنى ئىلگىرى بىلىدىغانلىرىنى ئۇنتۇپ، كۈچ ـ قۇۋۋىتى تۈگەپ، ئەقلى كېمىيىپ كىچىك بالىغا ئوخشاپ قالىدۇ). ئاللاھ ھەقىقەتەن (مەخلۇقاتنى باشقۇرۇشنى) بىلگۈچىدۇر، (ئۆزى ئىرادە قىلغان نەرسىنى ئىشقا ئاشۇرۇشقا) قادىردۇر.');
INSERT INTO "saleh" VALUES(16,71,'16:71','ئاللاھ سىلەرنىڭ بەزىڭلارنى بەزىڭلاردىن رىزىقتا ئۈستۈن قىلدى، رىزىقتا ئۈستۈن قىلىنغانلار ئۆزلىرىنىڭ رىزقىنى باراۋەر بولۇش ئۈچۈن قول ئاستىدىكىلەرگە بەرمەيدۇ، ئۇلار ئاللاھنىڭ نېمىتىنى ئىنكار قىلىشامدۇ؟');
INSERT INTO "saleh" VALUES(16,72,'16:72','ئاللاھ سىلەر (نىڭ ئۇنسى ـ ئۈلپەت ئېلىشىڭلار) ئۈچۈن خوتۇنۇڭلارنى ئۆز تىپىڭلاردىن ياراتتى، سىلەر ئۈچۈن ئۇلاردىن ئوغۇللارنى ۋە نەۋرىلەرنى ياراتتى. سىلەرگە تۈرلۈك شېرىن نەرسىلەرنى رىزىق قىلىپ بەردى. ئۇلار باتىلغا ئىشىنىپ ئاللاھنىڭ نېمىتىگە كۇفرىلىق قىلامدۇ؟');
INSERT INTO "saleh" VALUES(16,73,'16:73','ئۇلار ئاللاھنى قويۇپ، ئۆزلىرىگە ئاسمانلاردىن ھېچقانداق رىزىق چۈشۈرۈپ ۋە يەردىن ھېچقانداق رىزىق چىقىرىپ (يەنى ئاسماندىن يامغۇر ياغدۇرۇپ ۋە يەردىن زىرائەت ۋە دەرەخلەرنى ئۆستۈرۈپ) بېرەلمەيدىغان، (شۇنداق قىلىشقا) قۇدرىتى يەتمەيدىغان بۇتلارغا چوقۇنامدۇ؟');
INSERT INTO "saleh" VALUES(16,74,'16:74','سىلەر ئاللاھقا مىسال كەلتۇرمەڭلار (يەنى ئاللاھقا بۇتلارنى ئوخشاتماڭلار، ئاللاھقا شېرىك كەلتۈرمەڭلار). ئاللاھ ھەقىقەتەن (ھەممە ھەقىقەتنى) بىلىدۇ، سىلەر (ئاللاھنىڭ بۈيۈكلۈكىنى) بىلمەيسىلەر.');
INSERT INTO "saleh" VALUES(16,75,'16:75',' ئاللاھ بۇنداق بىر مىسال كەلتۈرىدۇ: ھېچقانداق ئىشنى ئۆز ئالدىغا بىر تەرەپ قىلالمايدىغان بىر قۇل بىز ئوبدان رىزىق (يەنى مال ـ مۈلۈك) ئاتا قىلغان ۋە ئۇنى يوشۇرۇن ياكى ئاشكارا يوسۇندا سەدىقە قىلىپ تۇرغان (ھۆر ـ ئەركىن) ئادەم بىلەن ئوخشاشمۇ؟ (يەنى ھېچ نەرسىگە ئىگە بولمىغان قۇلغا ئوخشايدىغان بۇتلارنى ھەممىگە ئىگە بولغان ۋە كائىناتنى خالىغانچە تەسەررۇپ قىلىدىغان ئاللاھ بىلەن قانداقمۇ تەڭلەشتۈرگىلى بولسۇن؟) جىمى ھەمدۇسانا ئاللاھقا خاستۇر! ئۇلار (يەنى كاپىرلار) نىڭ تولىسى (ھەمدۇساناغا ئاللاھنىڭلا لايىق ئىكەنلىكىنى، بۇتلارنىڭ ھەمدۇساناغا ۋە ئىبادەتكە لايىق ئەمەسلىكىنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(16,76,'16:76',' ئاللاھ يەنە مۇنداق مىسال كەلتۈرىدۇ؛ مۇنداق ئىككى ئادەم بار بولۇپ، ئۇلارنىڭ بىرى ھېچ ئىش قىلالمايدىغان ۋە ئىگىسىگە يۈك بولۇپ قالغان بىر گاچادۇركى، ئىگىسى بۇ گاچىنى مەيلى قەيەرگە ئەۋەتسۇن، ئۇ ھېچقانداق پايدا ئېلىپ كېلەلمەيدۇ. يەنە بىرى بولسا ئۆزى توغرا يولدا ماڭغان ۋە (كىشىلەرنى) ھەققانىي بولۇشقا بۇيرۇيدىغان ئادەمدۇر، بۇ ئىككى ئادەم ئوخشاشمۇ؟ (يەنى بۇ گاچا بىلەن ئۆزى توغرا يولدا تۇرۇپ پاساھەتلىك سۆزلەيدىغان، ئۆزى قۇرئان نۇرى بىلەن نۇرلانغان ئادەم بىر ـ بىرىگە ئوخشامدۇ؟ ئەقىللىق ئادەمنىڭ ئۇلارنى ئوخشاش ئورۇنغا قويمىغىنىدەك، بۇت ۋە تاشنى ھەممىنى بىلگۈچى قۇدرەتلىك ئاللاھ بىلەن ئوخشاش ئورۇنغا قويغىلى بولمايدۇ).');
INSERT INTO "saleh" VALUES(16,77,'16:77',' ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلىش ئاللاھقا خاستۇر، قىيامەتنىڭ قايىم بولۇشى كۆزنى يۇمۇپ ئاچقۇچىلىك ياكى ئۇنىڭدىنمۇ تېزدۇر. ئاللاھ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(16,78,'16:78','ئاللاھ سىلەرنى ئاناڭلارنىڭ قارنىدىن ھېچ نەرسىنى بىلمەيدىغان ھالىتىڭلار بىلەن چىقاردى، ئاللاھ سىلەرگە شۈكۈر قىلسۇن دەپ، قۇلاق، كۆز ۋە يۈرەكلەرنى ئاتا قىلدى. ');
INSERT INTO "saleh" VALUES(16,79,'16:79',' ئۇلار قۇشلارنىڭ ھاۋادا ئۇچۇشقا بويسۇندۇرۇلغانلىقىنى كۆرمىدىمۇ؟ ئۇلارنى (ھاۋادىن چۈشۈپ كېتىشتىن) پەقەت ئاللاھ تۇتۇپ تۇرىدۇ، بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار. ');
INSERT INTO "saleh" VALUES(16,80,'16:80',' ئاللاھ سىلەرگە ئۆيۈڭلارنى تۇرالغۇ جاي قىلىپ بەردى، سىلەرگە ھايۋانلارنىڭ تېرىلىرىدىن ئۆيلەرنى (چېدىرلەرنى) قىلىپ بەردى، سىلەر كۆچكەن كۈنۈڭلاردىمۇ، تۇرغان كۈنۈڭلاردىمۇ ئۇنىڭ يىنىكلىكىنى ھېس قىلىسىلەر، سىلەرگە قويلارنىڭ يۇڭلىرىدىن ، تۆگىلەرنىڭ يۇڭلىرىدىن، ئۆچكىلەرنىڭ يۇڭلىرىدىن ئۆي جاھازلىرىنى (يەنى سىلەر كىيىدىغان ۋە ئۆيۈڭلارنى سەرەمجانلاشتۇرىدىغان) ھەم مەلۇم مۇددەتكىچە (ئۆلۈم كەلگۈچە ياكى ئۇلار كونىراپ تۈگىگۈچە) پايدىلىنىدىغان نەرسىلەرنى قىلىپ بەردى. ');
INSERT INTO "saleh" VALUES(16,81,'16:81',' ئاللاھ سىلەرگە ئۆزى ياراتقان نەرسىلەر (يەنى دەرەخلەر، تاغلار ۋە بىنالار) دىن (كۈننىڭ ھارارىتىدىن ساقلىنىدىغان) سايىلەرنى قىلىپ بەردى. تاغلاردىن سىلەرگە (غار، ئۆڭكۈرگە ئوخشاش) تۇرالغۇ قىلىدىغان جايلارنى قىلىپ بەردى. سىلەرگە ئىسسىقتىن (ۋە سوغۇقتىن) ساقلىنىدىغان كىيىملەرنى، ئۇرۇشتا سىلەرنى (زىيان ـ زەخمەتتىن) ساقلايدىغان تۆمۈر كىيىملەرنى تەييار قىلدى. ئاللاھ سىلەرنى ئىتائەت قىلسۇن دەپ، سىلەرگە ئەنە شۇنداق مۇكەممەل نېمەتلەرنى بېرىدۇ.');
INSERT INTO "saleh" VALUES(16,82,'16:82','ئەگەر ئۇلار (يەنى مۇشرىكلار ئىماندىن) يۈز ئۆرۈسە (ساڭا ھېچ زىيىنى يوق)، سېنىڭ ۋەزىپەڭ پەقەت روشەن تەبلىغ قىلىشتۇر. ');
INSERT INTO "saleh" VALUES(16,83,'16:83','ئۇلار ئاللاھنىڭ نېمىتىنى تونۇيدۇ، ئاندىن (ئاللاھتىن باشقا بىرىگە ئىبادەت قىلىش بىلەن) ئۇ نېمەتلەرنى ئىنكار قىلىدۇ، ئۇلارنىڭ تولىسى كاپىردۇر (يەنى كاپىر پېتىچە ئۆلىدۇ).');
INSERT INTO "saleh" VALUES(16,84,'16:84',' شۇ كۈندە (يەنى قىيامەت كۈنىدە) ھەر ئۈممەتتىن (ئۇنىڭغا ئىمان بىلەن ياكى كۇفرى بىلەن گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز، ئاندىن كاپىرلارغا (ئۆزرە ئېيتىشقا) رۇخسەت قىلىنمايدۇ ھەمدە ئۇلاردىن پەرۋەردىگارىنى رازى قىلىش (يەنى پەرۋەردىگارىنى رازى قىلىدىغان ئەمەللەرنى قىلىش) تەلەپ قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(16,85,'16:85','زالىملار (يەنى مۇشرىكلار) ئازابنى كۆرگەن چاغدا، ئۇلاردىن (ئازاب) يىنىكلىتىلمەيدۇ ۋە كېچىكتۈرۈلمەيدۇ.');
INSERT INTO "saleh" VALUES(16,86,'16:86','مۇشرىكلار (دۇنيادا چوقۇنغان) بۇتلىرىنى كۆرگەن چاغدا: «ئى پەرۋەردىگارىمىز! بۇلار بولسا بىزنىڭ سېنى قويۇپ ئىبادەت قىلغان مەبۇدلىرىمىزدۇر» دەيدۇ. مەبۇدلىرى ئۇلارغا جاۋاب بېرىپ: «شۈبھىسىزكى، سىلەر ھەقىقەتەن يالغانچىسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(16,87,'16:87','شۇ كۈندە ئۇلار ئۆزلىرىنىڭ ئاللاھقا باش ئەگكەنلىكىنى بىلدۈرىدۇ. ئەمما ئۇلارنىڭ (دۇنيادىكى چاغلىرىدا: «مەبۇدلار بىزگە شاپائەت قىلىدۇ» دېگەن) يالغان ـ ياۋىداقلىرى يوققا چىقىدۇ.');
INSERT INTO "saleh" VALUES(16,88,'16:88','كاپىر بولغانلارغا ۋە (كىشىلەرنى) ئاللاھنىڭ يولىدىن (يەنى دىنىدىن) توسقانلارغا ئۇلارنىڭ قىلغان بۇزغۇنچىلىقلىرى ئۈچۈن ئازاپ ئۈستىگە ئازاپ قوشىمىز .');
INSERT INTO "saleh" VALUES(16,89,'16:89',' شۇ كۈندە بىز ھەر بىر ئۈممەتكە ئۇلارنىڭ ئىچىدىن ئۇلارغا قارشى (گۇۋاھلىق بېرىدىغان) بىر شاھىت (يەنى پەيغەمبەر) كەلتۈرىمىز ۋە (شۇ كۈندە) بىز سېنى ئۇلارغا (يەنى ئۈممىتىڭگە) شاھىت قىلىپ كەلتۈرىمىز. ساڭا بىز كىتابنى (يەنى قۇرئاننى) ھەممە نەرسىنى (يەنى كىشىلەر موھتاج بولىدىغان دىنىي ئىشلارنىڭ ھەممىسىنى) چۈشەندۈرۈپ بېرىدىغان، (دىللارغا) ھىدايەت، (بەندىلەرگە) رەھمەت، مۇسۇلمانلارغا مەڭگۈلۈك سائادەت بىلەن خوش خەۋەر يەتكۈزىدىغان قىلىپ نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(16,90,'16:90',' ئاللاھ ھەقىقەتەن (كىشىلەر ئارىسىدا) ئادىل بولۇشقا، (جىمى خەلققە) ياخشىلىق قىلىشقا، خىش ـ ئەقرىبالارغا سىلە ـ رەھىم قىلىشقا بۇيرۇيدۇ، قەبىھ (سۆز ـ ھەرىكەتلەر) دىن، يامان ئىشلاردىن ۋە زۇلۇم قىلىشتىن توسىدۇ. نەسىھەتنى قوبۇل قىلسۇن دەپ، ئاللاھ سىلەرگە پەند ـ نەسىھەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(16,91,'16:91','سىلەر ئەھدە تۈزۈشكەنلىرىڭلاردا ، ئاللاھنىڭ ئەھدىگە ۋاپا قىلىڭلار، قەسىمىڭلارنى (ئاللاھنىڭ نامىنى تىلغا ئېلىپ) پۇختىلىغاندىن كېيىن بۇزماڭلار، چۈنكى سىلەر ئاللاھنى گۇۋاھچى قىلدىڭلار. شۈبھىسىزكى، ئاللاھ قىلمىشىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(16,92,'16:92',' سىلەر پىششىق ئىگىرگەن يىپنى چۇۋۇپ پارچە ـ پارچە قىلىۋەتكەن خوتۇندەك بولماڭلار. بىر جامائە (سان ۋە مال ـ مۈلۈك جەھەتتە) يەنە بىر جامائەدىن كۆپ بولغانلىقى ئۈچۈن، قەسىمىڭلارنى ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالىسىلەر (يەنى ئۆز ئارا ياردەملىشىش ئۈستىدە بىر جامائە بىلەن ئىتتىپاق تۈزۈشكەندىن كېيىن، سانى، پۇل ـ مېلى كۆپرەك يەنە بىر جامائەنى كۆرۈپلا ئىلگىرىكى جامائە بىلەن تۈزۈشكەن ئەھدىنى بۇزۇپ، كېيىنكى جامائە بىلەن ئەھدى تۈزىسىلەر)، ئاللاھ سىلەرنى ھەقىقەتەن بۇنىڭ (يەنى ۋەدىگە ۋاپا قىلىش ئەمرى) بىلەن سىنايدۇ، سىلەر دەتالاش قىلىشقان ھەق ـ ناھەقنى قىيامەت كۈنى سىلەرگە چوقۇم ئېنىق ئايرىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(16,93,'16:93',' مۇبادا ئاللاھ خالىغان بولسا ئىدى، سىلەرنى (يەنى پۈتۈن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن (ئاللاھنىڭ ھېكمىتى ئۇلارنى ئۆز ئىختىيارىغا قويۇۋېتىشنى تەقەززا قىلدى)، ئاللاھ خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ، (قىيامەت كۈنى) پۈتۈن قىلمىشىڭلار ئۈستىدە سوراق قىلىنىسىلەر.');
INSERT INTO "saleh" VALUES(16,94,'16:94','قەدىمىڭلار (ئىسلام دىنىدا) ئىزچىل بولغاندىن كېيىن تېيىلىپ كەتمەسلىك ئۈچۈن، قەسىمىڭلارنى ئۆز ئاراڭلاردا ئالدامچىلىقنىڭ ۋاسىتىسى قىلىۋالماڭلار، (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقانلىقىڭلار (يەنى ئەھدىگە ۋاپا قىلىشتىن يۈز ئۆرۈگەنلىكىڭلار) ئۈچۈن ئازابنى تېتىيسىلەر، (سىلەر ئاخىرەتتە دوزاختا) چوڭ ئازابقا قالىسىلەر.');
INSERT INTO "saleh" VALUES(16,95,'16:95','سىلەر ئاللاھنىڭ ئەھدىنى ئەرزىمەس نەرسىگە سېتىۋەتمەڭلار؛ سىلەر ئەگەر بىلسەڭلار، ئاللاھنىڭ دەرگاھىدىكى ساۋاب سىلەر ئۈچۈن (پانى دۇنيانىڭ نەرسىلىرىدىن) ئارتۇقتۇر.');
INSERT INTO "saleh" VALUES(16,96,'16:96','(ئى ئىنسانلار!) سىلەرنىڭ ئىلكىڭلاردىكى نەرسىلەر تۈگەيدۇ، ئاللاھنىڭ دەرگاھىدىكى نەرسىلەر تۈگىمەستۇر. سەۋر قىلغۇچىلارغا، ئەلۋەتتە، ئۇلارنىڭ قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز.');
INSERT INTO "saleh" VALUES(16,97,'16:97',' ئەر ـ ئايال مۆمىنلەردىن كىمكى ياخشى ئەمەل قىلىدىكەن، بىز ئۇنى ئەلۋەتتە (دۇنيادا قانائەتچانلىق ، ھالال رىزىق ۋە ياخشى ئەمەللەرگە مۇۋەپپەق قىلىپ) ئوبدان ياشىتىمىز، ئۇلارغا، ئەلۋەتتە، قىلغان ئەمەلىدىنمۇ ياخشىراق ساۋاب بېرىمىز.');
INSERT INTO "saleh" VALUES(16,98,'16:98',' سەن قۇرئان ئوقۇماقچى بولغىنىڭدا، قوغلاندى شەيتاننىڭ (ۋەسۋەسىسىدىن) ئاللاھقا سىغىنىپ پاناھ تىلىگىن.');
INSERT INTO "saleh" VALUES(16,99,'16:99','شۈبھىسىزكى، مۆمىنلەرنىڭ ۋە پەرۋەردىگارىغا تەۋەككۈل قىلغۇچىلار (يەنى يۆلەنگۈچىلەر) نىڭ ئۈستىدىن شەيتان ھۆكۈمرانلىق قىلالمايدۇ (يەنى پەرۋەردىگارىغا چىن يۆلەنگەن مۆمىنلەرگە شەيتاننىڭ ۋەسۋەسىسى تەسىر قىلالمايدۇ).');
INSERT INTO "saleh" VALUES(16,100,'16:100',' شەيتان پەقەت ئۆزىنى دوست تۇتىدىغانلار ۋە ئۆزىنىڭ (ئىغۋا قىلىشى) بىلەن مۇشرىك بولۇپ كەتكەنلەر ئۈستىدىنلا ھۆكۈمرانلىق قىلىدۇ. ');
INSERT INTO "saleh" VALUES(16,101,'16:101',' بىز بىر ئايەتنى يەنە بىر ئايەتنىڭ ئورنىغا ئالماشتۇرغىنىمىزدا (يەنى بىر ھۆكۈمنى بىكار قىلىپ، ئۇنىڭ ئورنىغا باشقا بىر ھۆكۈمنى چۈشۈرگىنىمىزدە)، ـ ئاللاھ ئۆزى نازىل قىلغاننى (يەنى شۇ ھۆكۈمدە بەندىگە نېمە مەنپەئەت ۋە ھېكمەت بارلىقىنى) ئۆزى ئوبدان بىلىدۇ ـ ئۇلار: «(ئى مۇھەممەد!) سەن ( ئاللاھقا) يالغان چاپلىدىڭ» دەيدۇ، ئۇنداق ئەمەس، ئۇلارنىڭ تولىسى (ئاللاھنىڭ ھېكمىتىنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(16,102,'16:102','ئېيتقىنكى، قۇرئاننى جىبرىئىل مۆمىنلەرنىڭ (ئىمانىنى) مۇستەھكەم قىلىش ئۈچۈن، مۇسۇلمانلارغا ھىدايەت ۋە خۇش خەۋەر قىلىپ، پەرۋەردىگارىڭ ھۇزۇرىدىن راستچىللىق بىلەن نازىل قىلدى. ');
INSERT INTO "saleh" VALUES(16,103,'16:103','ئۇلار (يەنى مۇشرىكلار) نىڭ: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) قۇرئاننى بىر ئىنسان تەلىم بېرىدۇ» دېگەن سۆزىنى ئوبدان بىلىمىز، ئۇلار كۆرسەتكەن ئادەمنىڭ تىلى ئەرەب تىلى ئەمەس، بۇ (قۇرئان) بولسا روشەن ئەرەب تىلىدۇر.');
INSERT INTO "saleh" VALUES(16,104,'16:104','شۈبھىسىزكى، ئاللاھنىڭ ئايەتلىرىگە (يەنى قۇرئانغا) ئىشەنمەيدىغانلارنى ئاللاھ ھىدايەت قىلمايدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(16,105,'16:105',' پەقەت ئاللاھنىڭ ئايەتلىرىگە ئىشەنمەيدىغانلارلا ( ئاللاھقا) يالغان چاپلايدۇ، (مۇھەممەد ئەلەيھىسسالام يالغانچى ئەمەس) دەل ئۇلارنىڭ ئۆزلىرى يالغانچىدۇر.');
INSERT INTO "saleh" VALUES(16,106,'16:106','كىمكى ئاللاھقا ئىمان ئېيتقاندىن كېيىن ئىمانىدىن يېنىۋالسا، ـ قەلبى ئىمان بىلەن مۇستەھكەم تۇرسىمۇ مەجبۇرلاش ئاستىدا (ئاغزىدىلا) ئىماندىن يانغانلىقىنى بىلدۈرگەنلەر بۇنىڭدىن مۇستەسنا ـ كۇفرى بىلەن كۆڭلى ئازادە بولسا (يەنى ئىختىيارىي يوسۇندا مۇرتەد بولغان بولسا)، ئۇ ئاللاھنىڭ غەزىپىگە دۇچار بولىدۇ ۋە چوڭ ئازابقا قالىدۇ.');
INSERT INTO "saleh" VALUES(16,107,'16:107',' بۇ (يەنى ئازاب) ئۇلارنىڭ دۇنيا تىرىكچىلىكىنى ئاخىرەتتىن ئارتۇق كۆرگەنلىكلىرى ۋە ئاللاھنىڭ كاپىر قەۋمنى ھىدايەت قىلمايدىغانلىقى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(16,108,'16:108',' ئەنە شۇ كىشىلەر ئاللاھ تەرىپىدىن دىللىرى، قۇلاقلىرى ۋە كۆزلىرى پېچەتلىۋېتىلگەن كىشىلەردۇر، ئەنە شۇلار غاپىللاردۇر.');
INSERT INTO "saleh" VALUES(16,109,'16:109',' ئۇلار راستلا ئاخىرەتتە زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(16,110,'16:110','ئاندىن (ئى مۇھەممەد!) زىيانكەشلىككە ئۇچرىغاندىن كېيىن، ھەقىقەتەن (مەدىنىگە) ھىجرەت قىلغان. ئاندىن ( ئاللاھنىڭ يولىدا) جىھاد قىلغان ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىدىغانلارغا پەرۋەردىگارىڭ (مەغپىرەت قىلىدۇ). شۈبھىسىزكى، پەرۋەردىگارىڭ شۇنىڭدىن (يەنى ئۇلارنىڭ ھىجرەت، جىھاد ، سەۋر قىلغىنىدىن) كېيىن ئۇلارغا ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، رەھمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(16,111,'16:111',' ھەر ئادەم ئۆزى بىلەن بولۇپ كېتىدىغان، ھەر ئادەمگە قىلغان ئەمەلىنىڭ مۇكاپاتى تولۇق بېرىلىدىغان، ئۇلارغا زۇلۇم قىلىنمايدىغان كۈننى (يەنى قىيامەت كۈنىنى) ئەسلىگىن. ');
INSERT INTO "saleh" VALUES(16,112,'16:112','ئاللاھ مۇنداق بىر مىسالنى كەلتۈرىدۇ: بىر شەھەر (يەنى مەككە ئاھالىسى) بولۇپ، (ئەسلىدە) تىنچ ـ خاتىرجەم ئىدى. ئۇنىڭ رىزقى تەرەپ ـ تەرەپتىن كەڭتاشا كېلىپ تۇراتتى، (ئۇنىڭ ئاھالىسى) ئاللاھنىڭ نېمەتلىرىگە كۇفرىلىق قىلدى، ئاللاھ ئۇلارغا قىلمىشلىرى تۈپەيلىدىن ئاچلىقنىڭ ۋە قورقۇنچنىڭ ئەلىمىنى تېتىتتى. ');
INSERT INTO "saleh" VALUES(16,113,'16:113',' ئۇلارغا ئۆز ئىچىدىن بىر پەيغەمبەر (يەنى مۇھەممەد ئەلەيھىسسالام) كەلدى، ئۇنى ئۇلار ئىنكار قىلدى. ئۇلارغا ئازاب (يەنى قاتتىقچىلىق ۋە ئوقۇبەت) يۈزلەندى، ئۇلار (گۇناھلارغا چۆمگەنلىك تۈپەيلىدىن) زالىم ئىدى.');
INSERT INTO "saleh" VALUES(16,114,'16:114','ئاللاھ سىلەرگە رىزىق قىلىپ بەرگەن ھالال، شېرىن يېمەكلىكلەردىن يەڭلار، ئەگەر ئاللاھقىلا ئىبادەت قىلىدىغان بولساڭلار، ئاللاھنىڭ نېمىتىگە شۈكۈر قىلىڭلار.');
INSERT INTO "saleh" VALUES(16,115,'16:115','ئاللاھ سىلەرگە ئۆزى ئۆلۈپ قالغان ھايۋاننى، قاننى، چوشقا گۆشىنى، ئاللاھتىن غەيرىنىڭ نامى ئېيتىلىپ بوغۇزلانغان ھايۋاننى يېيىشنى ھارام قىلدى. كىمكى ئىختىيارىچە ئەمەس، ئىلاجىسىزلىقتىن (يۇقىرىقى ھارام قىلىنغان نەرسىلەردىن) ھاياتىنى ساقلاپ قالغۇدەك مىقداردا (يېسە) ئاللاھ (ئۇنى) مەغپىرەت قىلغۇچىدۇر، (ئۇنىڭغا) كۆيۈنگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(16,116,'16:116',' ئاغزىڭلارغا كەلگەن يالغاننى سۆزلەش ئۈچۈن (ھېچقانداق دەلىلسىز) «بۇ ھالال، بۇ ھارام» دېمەڭلار، چۈنكى (مۇنداقتا) ئاللاھ نامىدىن يالغاننى ئويدۇرغان بولىسىلەر، ئاللاھ نامىدىن يالغاننى ئويدورغۇچىلار ھەقىقەتەن (دۇنيا ۋە ئاخىرەتتە) مەقسىتىگە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(16,117,'16:117','(ئۇلار دۇنيادىن پەقەت) ئازغىنا بەھرىمەن بولىدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ. ');
INSERT INTO "saleh" VALUES(16,118,'16:118',' ئىلگىرى ساڭا بايان قىلغان نەرسىلەرنى يالغۇز يەھۇدىيلارغا ھارام قىلدۇق، (بۇنىڭ بىلەن) بىز ئۇلارغا زۇلۇم قىلمىدۇق، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(16,119,'16:119','شۈبھىسىزكى، بىلمەستىن يامان ئىشلارنى قىلغاندىن كېيىن تەۋبە قىلغان ۋە (ئەمەلىنى) تۈزەتكەنلەرگە پەرۋەردىگارىڭ ئەلۋەتتە مەغپىرەت قىلغۇچىدۇر، مەرھەمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(16,120,'16:120','ئىبراھىم ھەقىقەتەن (ياخشى خىسلەتلەرنى ئۆزلەشتۈرگۈچى) پېشىۋا، ئاللاھقا ئىتائەتمەن (باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىدى. ئۇ ئاللاھقا شېرىك كەلتۈرگۈچىلەردىن ئەمەس ئىدى.');
INSERT INTO "saleh" VALUES(16,121,'16:121',' ئىبراھىم ئاللاھنىڭ نېمەتلىرىگە شۈكۈر قىلغۇچى ئىدى. ئاللاھ ئۇنى (پەيغەمبەرلىككە) تاللىدى ۋە ئۇنى توغرا يولغا باشلىدى.');
INSERT INTO "saleh" VALUES(16,122,'16:122','ئۇنىڭغا دۇنيادا ياخشىلىق ئاتا قىلدۇق، شەك ـ شۈبھىسىزكى، ئاخىرەتتە ئۇ ياخشىلار قاتارىدا بولىدۇ. ');
INSERT INTO "saleh" VALUES(16,123,'16:123',' ئاندىن ساڭا: «باتىل دىنلاردىن ھەق دىنغا بۇرالغۇچى (يەنى ھەق دىنغا ئېتىقاد قىلغۇچى) ئىبراھىمنىڭ دىنىغا ئەگەشكىن، ئىبراھىم مۇشرىكلاردىن ئەمەس ئىدى» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(16,124,'16:124','شەنبە كۈنى ئۈستىدە ئىختىلاپ قىلغۇچىلار ئۈچۈنلا شەنبە كۈنىنى ئۇلۇغلاش بەلگىلەندى (يەنى شەنبە كۈنىنى ئۇلۇغلاش ۋە بۇ كۈندە ئىشلىمەسلىك ئىبراھىمنىڭ شەرىئىتىدە يوق ئىدى. يەھۇدىيلار ئاللاھنىڭ ئەمرىگە ئاسىيلىق قىلىپ دىندا ئىختىلاپ قىلىشقانلىقلىرى سەۋەبىدىن ئۇلارغا ئېغىرچىلىق تۇغدۇرۇش ئۈچۈن شۇنداق بەلگىلىمىنى چىقاردۇق). شۈبھىسىزكى، پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ھەق ـ ناھەق ئۈستىدە چوقۇم ھۆكۈم چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(16,125,'16:125','پەرۋەردىگارىڭنىڭ يولىغا (يەنى ئىسلام دىنىغا) ھېكمەتلىك ئۇسلۇبتا ياخشى ۋەز ـ نەسىھەت بىلەن دەۋەت قىلغىن، ئۇلار (يەنى مۇخالىپەتچىلىك قىلغۇچىلار) بىلەن چىرايلىق رەۋىشتە مۇنازىرىلەشكىن. پەرۋەردىگارىڭ ھەقىقەتەن ئۇنىڭ يولىدىن ئازغانلارنى ئوبدان بىلىدۇ، ھىدايەت تاپقۇچىلارنىمۇ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(16,126,'16:126','ئەگەر (ئۆزۈڭلارغا يەتكەن زىيان ـ زەخمەت ئۈچۈن) ئىنتىقام ئالماقچى بولساڭلار، ئۆزۈڭلارغا يەتكەن زىيان ـ زەخمەت قانچىلىك بولسا، شۇنچىلىك ئىنتىقام ئېلىڭلار (يەنى ئاشۇرۇۋەتمەڭلار)، ئەگەر سەۋر قىلساڭلار (يەنى ئىنتىقام ئالماي كەچۈرسەڭلار)، بۇ سەۋر قىلغۇچىلار (يەنى كەچۈرگۈچىلەر) ئۈچۈن (ئەلۋەتتە) ياخشىدۇر. ');
INSERT INTO "saleh" VALUES(16,127,'16:127',' (ئى مۇھەممەد!) (ئاللاھنىڭ يولىدا ساڭا يەتكەن ئەرزىيەتلەرگە) سەۋر قىلغىن، سەۋرەڭ پەقەت ئاللاھنىڭ ياردىمى بىلەنلا قولغا كېلىدۇ. ئۇلارنىڭ (ئىمان ئېيتمىغانلىقى) غا قايغۇرمىغىن. ئۇلارنىڭ ھىيلە ـ مىكرىسىدىن يۈرىكىڭ سىقىلمىسۇن. ');
INSERT INTO "saleh" VALUES(16,128,'16:128',' ئاللاھ ھەقىقەتەن تەقۋادارلىق قىلغۇچىلار ۋە ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(17,1,'17:1',' ئاللاھ (بارچە نۇقساندىن) پاكتۇر، ئۇ (مۇھەممەد ئەلەيھىسسالامغا) ئۆز قۇدرىتىنىڭ بەزى دەلىللىرىنى كۆرسىتىش ئۈچۈن، بەندىسىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) بىر كېچىدە مەسجىدى ھەرەمدىن ئەتراپىنى بەرىكەتلىك قىلغان مەسجىدى ئەقساغا ئېلىپ كەلدى. ھەقىقەتەن ئاللاھ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(17,2,'17:2',' مۇساغا كىتابنى (يەنى تەۋراتنى) ئاتا قىلدۇق، كىتابنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق، (ئۇلارغا) مەندىن غەيرىنى ھامىي (يەنى پەرۋەردىگار) قىلىۋالماڭلار!» (دېدۇق).');
INSERT INTO "saleh" VALUES(17,3,'17:3','(ئى) بىز نۇھ بىلەن بىللە كېمىگە چۈشۈرگەنلەرنىڭ ئەۋلادلىرى! (ئەجدادىڭلارنى غەرق بولۇپ كېتىشتىن قۇتقۇزدۇق، ئاللاھنىڭ نېمىتىگە شۈكۈر قىلىڭلار) نۇھ ھەقىقەتەن كۆپ شۈكۈر قىلغۇچى بەندە ئىدى.');
INSERT INTO "saleh" VALUES(17,4,'17:4','ئىسرائىل ئەۋلادىغا كىتابتا (يەنى تەۋراتتا): «پەلەستىن زېمىنىدا چوقۇم ئىككى قېتىم بۇزغۇنچىلىق قىلىسىلەر ۋە زۇلۇم قىلىش بىلەن، ئاللاھ چەكلىگەن ئىشلارنى قىلىش بىلەن تولىمۇ ھەددىڭلاردىن ئېشىپ كېتىسىلەر» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(17,5,'17:5','بىرىنچى قېتىملىق بۇزغۇنچىلىقنىڭ (جازاسى) ۋەدە قىلىنغان ۋاقىت كەلگەندە، (سىلەرنى جازالاش ئۈچۈن) سىلەرگە كۈچلۈك بەندىلىرىمىزنى ئەۋەتتۇق، (ئۇلار سىلەرنى ئۆلتۈرۈش، بۇلاڭ ـ تالاڭ قىلىش ئۈچۈن) ئۆيلەرنى ئاختۇردى. (سىلەرگە دۈشمەننى مۇسەللەت قىلىش) چوقۇم ئىشقا ئاشىدىغان ۋەدە ئىدى.');
INSERT INTO "saleh" VALUES(17,6,'17:6',' ئاندىن (يەنى تەۋبە قىلغىنىڭلاردىن) كېيىن سىلەرنىڭ ئۇلاردىن ئۈستۈنلۈكۈڭلارنى ئەسلىگە كەلتۈردۇق (يەنى دۈشمىنىڭلارنى ھالاك قىلدۇق، سىلەرنى ئۇنىڭ ئۈستىدىن غالىب قىلدۇق)، سىلەرگە نۇرغۇن مال ۋە ئەۋلادلار ئاتا قىلدۇق، سانىڭلارنى كۆپ قىلدۇق.');
INSERT INTO "saleh" VALUES(17,7,'17:7','(ئى ئىسرائىل ئەۋلادى!) ئەگەر ياخشىلىق قىلساڭلار، ياخشىلىقىڭلار ئۆزۈڭلار ئۈچۈندۇر، يامانلىق قىلساڭلارمۇ ئۆزۈڭلار ئۈچۈندۇر. ئىككىنچى قېتىملىق بۇزغۇنچىلىق (جازاسىنىڭ) ۋەدە قىلىنغان ۋاقتى كەلگەندە، (دۈشمەنلەرنىڭ خار قىلىشى بىلەن) يۈزۈڭلاردا قايغۇنىڭ ئالامەتلىرى كۆرۈلۈشى ، ئۇلارنىڭ بەيتۇلمۇقەددەسكە دەسلەپكى قېتىمدا كىرگەندەك كىرىشىۋە ئىشغال قىلغانلىكى يەرنى ۋەيران قىلىشى ئۈچۈن (ئۇلارنى بىز ئەۋەتتۇق).');
INSERT INTO "saleh" VALUES(17,8,'17:8','(ئەگەر تەۋبە قىلساڭلار) پەرۋەردىگارىڭلارنىڭ سىلەرگە رەھىم قىلىشى مۇھەققەق يېقىندۇر، ئەگەر سىلەر قايتساڭلار (يەنى قايتا بۇزغۇنچىلىق قىلساڭلار)، بىزمۇ قايتىمىز (يەنى بىز سىلەرنى قايتا جازالايمىز). بىز جەھەننەمنى كاپىرلار ئۈچۈن زىندان قىلدۇق.');
INSERT INTO "saleh" VALUES(17,9,'17:9',' بۇ قۇرئان ھەقىقەتەن ئەڭ توغرا يولغا باشلايدۇ، ياخشى ئىشلارنى قىلىدىغان مۆمىنلەرگە ئۇلارنىڭ چوڭ مۇكاپاتقا ئېرىشىدىغانلىقى بىلەن خۇشخەۋەر بېرىدۇ.');
INSERT INTO "saleh" VALUES(17,10,'17:10',' (يەنە مۆمىنلەرگە خۇشخەۋەر بېرىدۇكى) ئاخىرەتكە ئىشەنمەيدىغانلارغا قاتتىق ئازاب تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(17,11,'17:11','ئىنسان (ئۆزىگە ۋە بالا ـ چاقىلىرىغا) خەيرلىك دۇئا قىلغاندەك، (ئاچچىقى كەلگەندە) بەتدۇئا قىلىدۇ، ئىنسان ئالدىراڭغۇدۇر.');
INSERT INTO "saleh" VALUES(17,12,'17:12','كېچە بىلەن كۈندۈزنى (كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئىككى ئالامەت قىلدۇق، كېچىنىڭ ئالامىتىنى كۆتۈرۈۋەتتۇق، پەرۋەردىگارىڭلارنىڭ پەزلىنى تەلەپ قىلىشىڭلار (يەنى تۇرمۇش لازىمەتلىكلىرىنى تېپىش يولىدا سەئيى ھەرىكەتتە بولۇشۇڭلار) ئۈچۈن، (كېچە بىلەن كۈندۈزنىڭ ئالمىشىشى ئارقىلىق كۈنلەرنىڭ، ئايلارنىڭ) يىللارنىڭ سانىنى ۋە ھېسابىنى بىلىشىڭلار ئۈچۈن، كۈندۈزنىڭ ئالامىتىنى يورۇقلۇق قىلدۇق، (دىنىي ۋە دۇنياۋى) ئىشلارنىڭ ھەممىسىنى تەپسىلىي بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(17,13,'17:13',' ھەر بىر ئىنساننىڭ ئەمەلىنى ئۇنىڭ بوينىغا ئېسىپ قويىمىز (يەنى ئىنساننىڭ قىلغان ھەرقانداق ئەمەلى خۇددى بويۇنچاق بويۇندىن ئايرىلمىغىنىدەك ئۇنىڭدىن ھەرگىز ئايرىلمايدۇ، شۇنىڭغا يارىشا جازا بېرىلىدۇ)، قىيامەت كۈنى ئۇنىڭ نامە ـ ئەمالىنى كۆرسىتىمىز، ئۇ ئۇنى ئوچۇق كۆرىدۇ.');
INSERT INTO "saleh" VALUES(17,14,'17:14',' (ئۇنىڭغا) «نامە ـ ئەمالىڭنى ئوقۇغىن! بۈگۈن ئۆزۈڭگە (يەنى بۇ قىلمىشلىرىڭغا) ئۆزۈڭ گۇۋاھ بولۇشۇڭ كۇپايە» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(17,15,'17:15',' كىمكى ھىدايەت تاپىدىكەن، ھىدايەت تاپقانلىقىنىڭ پايدىسى ئۇنىڭ ئۆزى ئۈچۈندۇر. كىمكى ئازىدىكەن، ئازغانلىقنىڭ زىيىنى ئۇنىڭ ئۆزى ئۈچۈندۇر، بىر ئادەم يەنە بىر ئادەمنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ. پەيغەمبەر ئەۋەتمەي تۇرۇپ (ھېچ ئادەمنى) جازالىغىنىمىز يوق.');
INSERT INTO "saleh" VALUES(17,16,'17:16',' بىرەر شەھەر (ئاھالىسى) نى ھالاك قىلماقچى بولساق، ئۇنىڭدىكى دۆلەتمەن ئادەملەرنى (بىزگە ئىتائەت قىلىشقا) بۇيرۇيمىز، ئۇلار ئىتائەت قىلماي، پىسقى ـ پاسات قىلىدۇ ـ دە، ئۇلارغا ئازابىمىز تېگىشلىك بولىدۇ، شۇنىڭ بىلەن ئۇلارنى قورقۇنچلۇق رەۋىشتە ھالاك قىلىمىز.');
INSERT INTO "saleh" VALUES(17,17,'17:17',' نۇھتىن كېيىن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، پەرۋەردىگارىڭ بەندىلىرىنىڭ گۇناھلىرىنى تولۇق بىلىپ تۇرۇشتا، كۆرۈپ تۇرۇشتا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(17,18,'17:18',' بىز دۇنيانى كۆزلىگەنلەر ئىچىدىن خالىغان ئادەمگە (ئۇنىڭ خالىغىنىنى ئەمەس) بىزنىڭ خالىغىنىمىزنى بېرىمىز، ئاندىن ئۇنى (ئاخىرەتتە) جەھەننەم بىلەن جازالايمىز، جەھەننەمگە ئۇ خارلانغان، ( ئاللاھنىڭ رەھمىتىدىن) قوغلانغان ھالدا كىرىدۇ.');
INSERT INTO "saleh" VALUES(17,19,'17:19',' كىمكى ئاخىرەتنى كۆزلەيدىكەن ۋە مۆمىن بولۇپ ئاخىرەتكە لايىق ئەمەللەرنى قىلىدىكەن، مۇنداق ئادەملەرنىڭ قىلغان ئەمەلى (ئاللاھنىڭ دەرگاھىدا) قوبۇل بولىدۇ.');
INSERT INTO "saleh" VALUES(17,20,'17:20','بۇلارنىڭ ۋە ئۇلارنىڭ ھەر ئىككىسىگە پەرۋەردىگارىڭنىڭ ئاتا قىلغىنىدىن بېرىمىز. پەرۋەردىگارىڭنىڭ ئاتا قىلغىنى (ھېچ ئادەمدىن) مەنئى قىلىنمايدۇ،');
INSERT INTO "saleh" VALUES(17,21,'17:21',' ئۇلارنىڭ بەزىسىنى بەزىسىدىن قانداق ئۈستۈن قىلغانلىقىمىزغا قارىغىن، ئاخىرەت دەرىجىلىرىدىكى (پەرق) ئەلۋەتتە تېخىمۇ چوڭدۇر ۋە ئۈستۈنلۈك (جەھەتتىكى پەرق) زىيادە چوڭدۇر.');
INSERT INTO "saleh" VALUES(17,22,'17:22',' ئاللاھقا باشقا مەبۇدنى (شېرىك) قىلمىغىن، (ئۇنداق قىلساڭ ئاللاھنىڭ دەرگاھىدا) ئەيىبلەنگەن، (ئاللاھنىڭ ياردىمىدىن) مەھرۇم بولغان ھالدا (دوزاختا ھەمىشە) قالىسەن.');
INSERT INTO "saleh" VALUES(17,23,'17:23','پەرۋەردىگارىڭ پەقەت ئۇنىڭ ئۆزىگىلا ئىبادەت قىلىشىڭلارنى ۋە ئاتا ـ ئاناڭلارغا ياخشىلىق قىلىشىڭلارنى تەۋسىيە قىلدى. ئۇلارنىڭ بىرى، يا ئىككىلىسى سېنىڭ قول ئاستىڭدا بولۇپ ياشىنىپ قالسا، ئۇلارغا، ئوھوي! دېمىگىن (يەنى مالاللىقنى بىلدۈرىدىغان شۇنچىلىك سۆزنىمۇ قىلمىغىن). ئۇلارنى دۈشكەللىمىگىن، ئۇلارغا ھۆرمەت بىلەن يۇمشاق سۆز قىلغىن. ');
INSERT INTO "saleh" VALUES(17,24,'17:24','ئۇلارغا كامالىي مېھرىبانلىقتىن ناھايىتى كەمتەر مۇئامىلىدە بولغىن ۋە: «ئى پەرۋەردىگارىم! ئۇلار مېنى كىچىكلىكىمدە تەربىيەلىگىنىدەك ئۇلارغا مەرھەمەت قىلغىن» دېگىن.');
INSERT INTO "saleh" VALUES(17,25,'17:25',' پەرۋەردىگارىڭلار دىلىڭلاردىكىنى ئوبدان بىلگۈچىدۇر، ئەگەر سىلەر ياخشى بولساڭلار (يەنى ئاتا ـ ئاناڭلارنى قاخشاتقۇچى بولمىساڭلار، ئاللاھ سىلەرنىڭ خاتالىغىڭلارنى كەچۈرىدۇ). ئاللاھ ھەقىقەتەن تەۋبە قىلىپ تۇرغۇچىلارنى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(17,26,'17:26',' تۇغقانغا، مىسكىنگە، ئىبىن ـ سەبىلگە (خەيرى ـ ساخاۋەتتىن) ھەققىنى بەرگىن، (پۇل ـ مېلىڭنى ناتوغرا يوللارغا) ئىسراپ قىلمىغىن. ');
INSERT INTO "saleh" VALUES(17,27,'17:27',' ئىسراپ قىلغۇچىلار ھەقىقەتەن شەيتانلارنىڭ قېرىنداشلىرىدۇر، شەيتان پەرۋەردىگارىغا تولىمۇ كۇفرانى نېمەت قىلغان ئىدى.');
INSERT INTO "saleh" VALUES(17,28,'17:28',' ئەگەر سەن پەرۋەردىگارىڭدىن كۈتۈۋاتقان مەرھەمەت نازىل بولغاندىن كېيىن، ئاندىن ئۇلارغا خەيرى ـ ساخاۋەت قىلماقچى بولساڭ (يەنى قولۇڭدا ئۇلارغا بېرىدىغان بىر نەرسە بولمىسا)، ئۇلارغا چىرايلىق گەپ قىلغىن.');
INSERT INTO "saleh" VALUES(17,29,'17:29','قولۇڭنى بوينۇڭغا باغلىۋالمىغىن (يەنى بېخىللىق قىلمىغىن)، قولۇڭنى تولىمۇ ئېچىپمۇۋەتمىگىن (يەنى ئىسراپ قىلمىغىن)، (ئۇنداق قىلساڭ پۇلسىز قېلىپ) مالامەتكە، پۇشايمانغا قالىسەن.');
INSERT INTO "saleh" VALUES(17,30,'17:30','شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، خالىغان ئادەمنىڭ رىزقىنى تار قىلىدۇ، ئاللاھ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى، مەنپەئىتىنى) بىلىپ تۇرغۇچىدۇر، كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(17,31,'17:31','كەمبەغەللىكتىن قورقۇپ بالىلىرىڭلارنى ئۆلتۈرمەڭلار. ئۇلارنىڭ ۋە سىلەرنىڭ رىزقىڭلارنى بىز بېرىمىز، ئۇلارنى ئۆلتۈرۈش ھەقىقەتەن چوڭ گۇناھتۇر.');
INSERT INTO "saleh" VALUES(17,32,'17:32','زىناغا يېقىنلاشماڭلار، چۈنكى ئۇ قەبىھ ئىشتۇر، يامان يولدۇر.');
INSERT INTO "saleh" VALUES(17,33,'17:33','ناھەق ئادەم ئۆلتۈرمەڭلاركى، ئاللاھ (ناھەق ئادەم ئۆلتۈرۈشنى) ھارام قىلدى، كىمكى ناھەق ئۆلتۈرۈلىدىكەن، (قاتىلدىن قىساس ئېلىش، يا دىيەت ئېلىش، يا كەچۈرۈم قىلىش) ھوقۇقىنى ئۆلتۈرۈلگۈچىنىڭ ئىگىسىنىڭ قولىدا قىلدۇق، ئىگىسى قىساس ئېلىشتا چەكتىن چىقىپ كەتمىسۇن (قاتىلدىن غەيرىنى ئۆلتۈرۈش، يا ئۇنىڭ ئەزالىرىنى كېسىش، بىر ئادەم ئۈچۈن ئىككى ئادەمنى ئۆلتۈرۈش قاتارلىق ئىشلارنى قىلمىسۇن)، (ناھەق ئۆلتۈرۈلگۈچىنىڭ) ئىگىسىگە ئاللاھ ھەقىقەتەن مەدەتكاردۇر.');
INSERT INTO "saleh" VALUES(17,34,'17:34','يېتىمنىڭ مېلىنى تاكى ئۇ بالاغەتكە يەتكەنگە قەدەر (يېتىمگە) ئەڭ پايدىلىق ئۇسۇلدا تەسەررۇپ قىلىڭلار، ئەھدىگە ۋاپا قىلىڭلار، (قىيامەت كۈنى) ئەھدە ئۈستىدە (يەنى ئەھدىگە ۋاپا قىلغان ـ قىلمىغانلىق ئۈستىدە) ئەلۋەتتە سوئال ـ سوراق قىلىنىسىلەر.');
INSERT INTO "saleh" VALUES(17,35,'17:35',' سىلەر (باشقىلارغا ئاشلىق قاتارلىقلارنى) ئۆلچەپ بەرگەندە، تولۇق ئۆلچەڭلار ۋە توغرا تارازىدا تارتىپ بېرىڭلار. بۇنداق قىلىش (سىلەرگە دۇنيادا) ياخشىدۇر، ئاخىرەتلىكىڭلار ئۈچۈن تېخىمۇ ئوبداندۇر.');
INSERT INTO "saleh" VALUES(17,36,'17:36','بىلمەيدىغان نەرسەڭگە ئەگەشمە (يەنى بىلمىگەننى بىلدىم، كۆرمىگەننى كۆردۈم، ئاڭلىمىغاننى ئاڭلىدىم دېمە)، (ئىنسان قىيامەت كۈنى) قۇلاق، كۆز، دىل (يەنى سەزگۈ ئەزالىرى) نىڭ قىلمىشلىرى ئۈستىدە ھەقىقەتەن سوئال ـ سوراق قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(17,37,'17:37','سەن زېمىندا مەغرۇرانە يۈرمىگىن، (ھاكاۋۇرلۇقتىن ئايىغىڭ بىلەن) يەرنى تېشىۋېتەلمەيسەن، (ھەرقانچە گىدەيگىنىڭ بىلەن) ئېگىزلىكتە تاغلارغا تەڭلىشەلمەيسەن.');
INSERT INTO "saleh" VALUES(17,38,'17:38','يۇقىرىقى (ئىش) لارنىڭ ھەممىسى قەبىھ بولۇپ، ئاللاھنىڭ دەرگاھىدا ياماندۇر.');
INSERT INTO "saleh" VALUES(17,39,'17:39',' بۇلار پەرۋەردىگارىڭ ساڭا ۋەھىي قىلغان ھېكمەتلەرنىڭ بىر قىسمىدۇر، ئاللاھقا باشقا مەبۇدنى شېرىك قىلمىغىن، بولمىسا مالامەت قىلىنغان، (ئاللاھنىڭ رەھمىتىدىن) قوغلانغان ھالدا جەھەننەمگە تاشلىنىسەن.');
INSERT INTO "saleh" VALUES(17,40,'17:40','ئەجەبا ئاللاھ سىلەرگە ئوغۇللارنى خاس قىلىپ، پەرىشتىلەرنى ئۆزىنىڭ قىزلىرى قىلىۋالغانمۇ؟ ھەقىقەتەن سىلەر چوڭ سۆزنى قىلىۋاتىسىلەر.');
INSERT INTO "saleh" VALUES(17,41,'17:41',' ئۇلارنى ئىبرەت ئالسۇن دەپ (ۋەز ـ نەسىھەتنى) قۇرئاندا تۈرلۈك شەكىلدە بايان قىلدۇق، (بۇنىڭ بىلەن) ئۇلار (ھەقىقەتەن) تېخىمۇ يىراقلاشماقتا.');
INSERT INTO "saleh" VALUES(17,42,'17:42','مۇشرىكلار ئېيتقاندەك، ئاللاھ بىلەن بىرگە يەنە باشقا ئىلاھلار بولغان تەقدىردە ئەرش ئىگىسى ( ئاللاھ ئۈستىدىن) غەلىبە قىلىش ئۈچۈن ئەلۋەتتە يول ئىزدىگەن بولاتتى.');
INSERT INTO "saleh" VALUES(17,43,'17:43','ئاللاھ پاكتۇر، ئۇلارنىڭ بوھتان سۆزلىرىدىن يۈكسەك دەرىجىدە ئالىيدۇر.');
INSERT INTO "saleh" VALUES(17,44,'17:44','يەتتە ئاسمان ـ زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار ئاللاھنى پاك دەپ بىلىدۇ، (كائىناتتىكى) قانداقلىكى نەرسە بولمىسۇن، ئاللاھنى پاك دەپ مەدھىيەلەيدۇ (يەنى ئاللاھنىڭ ئۇلۇغلۇقىنى سۆزلەيدۇ)، لېكىن سىلەر (تىلىڭلار ئوخشاش بولمىغانلىقى ئۈچۈن) ئۇلارنىڭ مەدھىيەسىنى سەزمەيسىلەر، ئاللاھ ھەقىقەتەن (بەندىلىرىگە) ھەلىمدۇر (يەنى ئاسىيلىق قىلغانلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، (تەۋبە قىلغۇچىلارنى) مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(17,45,'17:45','(ئى مۇھەممەد!) سەن ئاخىرەتكە ئىشەنمەيدىغانلارغا قۇرئاننى ئوقۇغان چېغىڭدا ئۇلار بىلەن سېنىڭ ئاراڭدا يوشۇرۇن پەردە پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(17,46,'17:46',' قۇرئاننى چۈشەنمىسۇن دەپ ئۇلارنىڭ دىللىرىنى پەردىلىدۇق، ئۇلارنىڭ قۇلاقلىرىنى ئېغىر قىلدۇق، قۇرئاندا يالغۇز پەرۋەردىگارىڭنى تىلغا ئالساڭ، ئۇلار ئۈركۈپ قېچىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(17,47,'17:47','تىلاۋىتىڭگە ئۇلارنىڭ نېمە ئۈچۈن (يەنى مەسخىرە ئۈچۈن) قۇلاق سالىدىغانلىقىنى بىز ئوبدان بىلىمىز، ئۇلار قىرائىتىڭنى تىڭشىغان چاغلىرىدا (سېنىڭ توغراڭدا) پىچىرلىشىدۇ، ئەينى ۋاقىتتا زالىملار (مۆمىنلەرگە): «سىلەر پەقەت سېھىرلەنگەن ئادەمگىلا ئەگىشىۋاتىسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(17,48,'17:48','ئۇلارنىڭ سېنىڭ توغراڭدا (بىردەم سېھرىگەر، بىردەم شائىر ۋە بىردەم مەجنۇن دېگەندەك) نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار (مۇشۇنداق بوھتانلارنى چاپلاش بىلەن) ئازدى، ئۇلار (ھىدايەتكە) يول تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(17,49,'17:49',' مۇشرىكلار: ‹‹بىز قۇرۇق سۆڭەك بولۇپ ۋە چىرىپ توپا بولۇپ كەتكەندىن كېيىن چوقۇم قايتا تىرىلىپ يېڭىدىن ئادەم بولامدۇق؟›› دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(17,50,'17:50','سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (ئاللاھ سىلەرنى تىرىلدۈرىدۇ). ئۇلار: «بىزنى كىم تىرىلدۈرىدۇ؟» دەيدۇ، «سىلەرنى دەسلەپتە ياراتقان ئاللاھ تىرىلدۈرىدۇ» دېگىن. ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: «ئۇ قاچان بولىدۇ؟›› دېيىشىدۇ، (سەن) «ئۇ پات يېقىندا بولۇشى مۇمكىن›› دېگىن[50ـ51].');
INSERT INTO "saleh" VALUES(17,51,'17:51','سىلەر تاش ياكى تۆمۈر ياكى ھاياتلىقنى تەسەۋۋۇر قىلىش تېخىمۇ قىيىن بولغان بىر نەرسە بولۇپ كەتسەڭلارمۇ (ئاللاھ سىلەرنى تىرىلدۈرىدۇ). ئۇلار: «بىزنى كىم تىرىلدۈرىدۇ؟» دەيدۇ، «سىلەرنى دەسلەپتە ياراتقان ئاللاھ تىرىلدۈرىدۇ» دېگىن. ئۇلار ساڭا باشلىرىنى لىڭشىتىپ: «ئۇ قاچان بولىدۇ؟›› دېيىشىدۇ، (سەن) «ئۇ پات يېقىندا بولۇشى مۇمكىن›› دېگىن[50ـ51].');
INSERT INTO "saleh" VALUES(17,52,'17:52','شۇ كۈندە ئاللاھ سىلەرنى چاقىرىدۇ، سىلەر ئۇنىڭغا ھەمدى ئېيتىپ ئىجابەت قىلىسىلەر ۋە (دۇنيادا) پەقەت ئازغىنا تۇرغاندەك ئويلايسىلەر.');
INSERT INTO "saleh" VALUES(17,53,'17:53',' بەندىلىرىمگە ئېيتقىنكى، ئۇلار ياخشى سۆزلەرنى قىلسۇن، شۈبھىسىزكى، شەيتان ئۇلارنىڭ ئارىسىدا پىتنە قوزغايدۇ، شەيتان ئىنسانغا ھەقىقەتەن ئاشكارا دۈشمەندۇر.');
INSERT INTO "saleh" VALUES(17,54,'17:54','ئاللاھ سىلەرنى ئوبدان بىلىدۇ، خالىسا سىلەرگە رەھىم قىلىدۇ، خالىسا سىلەرنى ئازابلايدۇ ، سېنى ئۇلار (يەنى كاپىرلار) غا ھامىي قىلىپ ئەۋەتكىنىمىز يوق.');
INSERT INTO "saleh" VALUES(17,55,'17:55','پەرۋەردىگارىڭ ئاسمانلاردىكى، زېمىندىكى بەندىلىرىنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ. بىز ھەقىقەتەن پەيغەمبەرلەرنىڭ بەزىسىنى بەزىسىدىن ئۈستۈن قىلدۇق، داۋۇتقا زەبۇرنى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(17,56,'17:56','سىلەر ئاللاھنى قويۇپ خۇدا دەپ ئويلىغانلىرىڭلارنى چاقىرىڭلار. ئۇلارنىڭ قولىدىن سىلەردىن بالانى كۆتۈرۈۋېتىشمۇ ۋە ئۇنى (باشقىلارغا) يۆتكەپ قويۇشمۇ كەلمەيدۇ.');
INSERT INTO "saleh" VALUES(17,57,'17:57',' ئۇلار (ئاللاھنى قويۇپ ئىلاھ دەپ) ئىبادەت قىلىدىغانلارنىڭ (ئۆزلىرى ئىبادەت ئارقىلىق) پەرۋەردىگارىغا يېقىن بولۇشنى تىلەيدۇ، ئۇلارنىڭ ئارىسىدا (پەرۋەردىگارىغا) يېقىنراق بولغىنى (ھەم ئىبادەت ئارقىلىق ئاللاھقا يېقىن بولۇشنى تىلەيدۇ)، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ (ئۇنىڭ) ئازابىدىن قورقىدۇ، پەرۋەردىگارىنىڭ ئازابى ھەقىقەتەن ساقلىنىشقا تېگىشلىكتۇر.');
INSERT INTO "saleh" VALUES(17,58,'17:58','(ئاللاھقا ئاسىيلىق قىلغان) ھەرقانداق بىر شەھەر بولمىسۇن، ئۇنى بىز قىيامەت كۈنىدىن بۇرۇن ھالاك قىلىمىز ياكى ئۇنى قاتتىق ئازابلايمىز، بۇ لەۋھۇلمەھپۇزدا پۈتۈلۈپ كەتكەن (ھۆكۈم) دۇر.');
INSERT INTO "saleh" VALUES(17,59,'17:59','(قۇرەيش مۇشرىكلىرى تەلەپ قىلغان) مۆجىزىلەرنى مەيدانغا كەلتۈرمەسلىكىمىز پەقەت بۇرۇنقىلار (يەنى ئىلگىرىكى ئۈممەتلەرنىڭ) مۆجىزىلەرنى يالغانغا چىقارغانلىقى ئۈچۈندۇر. بىز سەمۇدقا چىشى تۆگىنى روشەن (مۆجىزە) قىلىپ بەردۇق، ئۇلار ئۇنى ئىنكار قىلدى. بىز مۆجىزىلەرنى پەقەت (بەندىلەرنى) قورقۇتۇش ئۈچۈنلا ئەۋەتىمىز.');
INSERT INTO "saleh" VALUES(17,60,'17:60','ئۆز ۋاقتىدا بىز ساڭا پەرۋەردىگارىڭنىڭ ھەقىقەتەن كىشىلەرنىڭ (ئەھۋالى) نى تولۇق بىلىدىغانلىقىنى ئېيتتۇق، بىز ساڭا (مىراج كېچىسى ئاسمان ـ زېمىندىكى ئاجايىپلاردىن) كۆرسەتكەن كۆرۈنۈشنى، قۇرئاندىكى لەنەت قىلىنغان دەرەخنى (يەنى قۇرئاندا زىكرى قىلىنغان زەققۇم دەرىخىنى) پەقەت كىشىلەر ئۈچۈن سىناق قىلدۇق، بىز ئۇلارنى (يەنى مۇشرىكلارنى) قورقۇتىمىز. بىزنىڭ قورقۇتىشىمىز ئۇلارنىڭ يولسىزلىقىنى تېخىمۇ كۈچەيتىدۇ. ');
INSERT INTO "saleh" VALUES(17,61,'17:61','ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار‍‌‍!» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى. ئىبلىس: «سەن لايدىن ياراتقانغا سەجدە قىلامدىمەن؟» دېدى.');
INSERT INTO "saleh" VALUES(17,62,'17:62','ئىبلىس: «ماڭا ئېيتىپ بەرگىنە، سەن مەندىن ئۈستۈن قىلغان ئادەم مۇشۇمۇ؟ ئەگەر ماڭا قىيامەتكىچە مۆھلەت بېرىدىغان بولساڭ، ئۇنىڭ ئەۋلادىنىڭ ئازغىنىسىدىن باشقىسىنى (ئازدۇرۇپ) تۈپ يىلتىزىدىن قۇرۇتۇۋېتىمەن (يەنى ئۇلارنى خالىغانچە يېتىلەيمەن)» دېدى.');
INSERT INTO "saleh" VALUES(17,63,'17:63','ئاللاھ (ئىبلىسقا) ئېيتتى: «بارغىن (ساڭا مۆھلەت بەردىم)، ئۇلاردىن (يەنى ئادەم بالىلىرىدىن) كىمكى ساڭا ئەگىشىدىكەن، (جەھەننەم ساڭا ۋە ئۇلارغا) بېرىلگەن تولۇق جازادۇر. ');
INSERT INTO "saleh" VALUES(17,64,'17:64','ئۇلارنىڭ ئىچىدىن قوزغىتالايدىغانلىكى ئادىمىڭنى ئاۋازىڭ بىلەن (يەنى گۇناھقا چاقىرىشىڭ بىلەن) قوزغاتقىن، ئاتلىق ۋە پىيادە قوشۇنىڭ بىلەن ئۇلارغا ھۇجۇم قىلغىن، ئۇلارنىڭ ماللىرىغا ۋە بالىلىرىغا شېرىك بولغىن، ئۇلارغا (يالغان) ۋەدىلەرنى بەرگىن ـ شەيتاننىڭ ئۇلارغا بەرگەن ۋەدىسى پەقەت ئالدامچىلىقتۇر.');
INSERT INTO "saleh" VALUES(17,65,'17:65','شۈبھىسىزكى، مېنىڭ (ئىخلاسمەن) بەندىلىرىم ئۈستىدىن سەن ھۆكۈمرانلىق قىلالمايسەن. پەرۋەردىگارىڭ ھەقىقەتەن ھامىي بولۇشقا يېتەرلىكتۇر».');
INSERT INTO "saleh" VALUES(17,66,'17:66',' پەرۋەردىگارىڭلار، سىلەرنى پەزلىنى (يەنى پەزلىدىن بولغان رىزقىڭلارنى) تەلەپ قىلسۇن دەپ، كېمىلەرنى سىلەر ئۈچۈن دېڭىزدا ماڭدۇرۇپ بېرىدۇ، ئۇ ھەقىقەتەن سىلەرگە ناھايىتى مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(17,67,'17:67','سىلەر دېڭىزدا بىرەر ئاپەتكە يولۇققان چېغىڭلاردا، ئېسىڭلارغا سىلەر چوقۇنۇۋاتقان مەبۇدلار كەلمەي، پەقەت ئاللاھلا كېلىدۇ (يەنى ئىنسان مۇنداق چاغدا ئاللاھتىن باشقىغا ئىلتىجا قىلمايدۇ)، ئاللاھ سىلەرنى (غەرق بولۇشتىن) قۇتقۇزۇپ، (ئامان ـ ئېسەن) قۇرۇقلۇققا چىقارغاندا يۈز ئۆرۈيسىلەر (يەنى ئىخلاسىڭلار تۈگەپ ئاۋۋالقى ھالىتىڭلارغا قايتىۋالىسىلەر). ئىنسان كۇفرىلىق قىلغۇچىدۇر (يەنى ئاللاھنىڭ نېمەتلىرىنى ئىنكار قىلىش ئىنساننىڭ تەبىئىتىدۇر).');
INSERT INTO "saleh" VALUES(17,68,'17:68','(دېڭىزدا غەرق بولۇپ كېتىشتىن قۇتۇلغىنىڭلار بىلەن) ئاللاھنىڭ سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن ياكى سىلەرگە ئاسماندىن تاش ياغدۇرۇپ ھالاك قىلىشىدىن، ئاندىن سىلەرنى (ئاللاھنىڭ ئازابىدىن ساقلايدىغان) ھېچ ھامىي تاپالماسلىقىڭلاردىن قورقمامسىلەر؟');
INSERT INTO "saleh" VALUES(17,69,'17:69','ياكى ئاللاھنىڭ سىلەرنى دېڭىزدا قايتا قاتناتقۇزۇپ (دېڭىزدىكى چېغىڭلاردا) قارا بوران چىقىرىپ، كۈفرىلىق قىلغانلىقىڭلار تۈپەيلىدىن سىلەرنى غەرق قىلىۋېتىشىدىن، ئاندىن سىلەرگە بىزدىن ئۆچ ئېلىپ بېرىدىغان ھېچ ئەھەدى تاپالماسلىقىڭلاردىن قورقمامسىلەر؟');
INSERT INTO "saleh" VALUES(17,70,'17:70','شەك ـ شۈبھىسىزكى، بىز ئادەم بالىلىرىنى ھۆرمەتلىك قىلدۇق، ئۇلارنى قۇرۇقلۇقتا (ئۇلاغلارغا) مىندۇردۇق، دېڭىزدا (كېمىلەرگە) چىقاردۇق، ئۇلارنى شېرىن يېمەكلىكلەر بىلەن رىزىقلاندۇردۇق، ئۇلارنى مەخلۇقاتلىرىمىزنىڭ نۇرغۇنىدىن ئۈستۈن قىلدۇق.');
INSERT INTO "saleh" VALUES(17,71,'17:71',' بىز ھەممە ئادەمنى ئۇلارنىڭ نامە ـ ئەمالى بىلەن چاقىرىدىغان كۈننى (ئېسىڭدا تۇتقىن)، نامە ـ ئەمالى ئوڭ قولىدىن بېرىلگەنلەر ئۆزلىرىنىڭ نامە ـ ئەمالىنى ئوقۇيدۇ، ئۇلارغا قىلچە زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(17,72,'17:72',' بۇ دۇنيادا (دىلى) كور بولغان ئادەم ئاخىرەتتىمۇ كور ۋە تېخىمۇ گۇمراھ بولىدۇ.');
INSERT INTO "saleh" VALUES(17,73,'17:73','ئۇلار سېنى بىز ساڭا ۋەھىي قىلغان قۇرئانغا خىلاپلىق قىلدۇرۇپ، بىزگە قۇرئاندىن غەيرىنى ئىپتىرا قىلدۇرۇشقا تاس قالدى، (ئۇلارنىڭ كۆڭلىدىكىدەك قىلغىنىڭدا) ئەلۋەتتە ئۇلار سېنى (ئۆزلىرىگە) دوست قىلىۋالاتتى.');
INSERT INTO "saleh" VALUES(17,74,'17:74','سېنى بىز (ھەقتە تۇرۇشتا) مۇستەھكەم قىلمىغان بولساق، ئۇلارغا مايىل بولۇپ كەتكىلى ھەقىقەتەن تاس قالغان ئىدىڭ.');
INSERT INTO "saleh" VALUES(17,75,'17:75','ئەگەر شۇنداق قىلىدىغان بولساڭ، ئەلۋەتتە ساڭا دۇنيا ۋە ئاخىرەتتە ھەسسىلەپ ئازاب بەرگەن بولاتتۇق، ئاندىن سەن بىزگە قارشى ياردەم بېرىدىغان ھېچ ئادەم تاپالمايتتىڭ.');
INSERT INTO "saleh" VALUES(17,76,'17:76',' (ئۇلار) سېنى زېمىندىن (يۇرتۇڭدىن) چىقىرىۋېتىش ئۈچۈن ئۇ يەردە سېنى خاتىرجەم تۇرغۇزماسلىققا ئورۇندى. شۇنداق بولغاندا، سەن چىقىپ كەتكەندىن كېيىن، ئۇلار ئازغىنا ۋاقىت تۇرالايتتى (يەنى ئازغىنا ۋاقىتتىن كېيىن ھالاك بولاتتى).');
INSERT INTO "saleh" VALUES(17,77,'17:77','(بۇ بىزنىڭ) سەندىن ئىلگىرى ئەۋەتكەن پەيغەمبەرلىرىمىز ھەققىدە تۇتقان يولىمىزدۇر (يەنى پەيغەمبەرلىرىنى ئارىسىدىن ھەيدەپ چىقارغان ھەرقانداق ئۈممەتنى ھالاك قىلىش ئاللاھنىڭ پەيغەمبەرلىرى ھەققىدە تۇتقان يولىدۇر)، تۇتقان يولىمىزدا ئۆزگىرىش تاپالمايسەن.');
INSERT INTO "saleh" VALUES(17,78,'17:78','(ئى مۇھەممەد!) كۈن قايرىلغان ۋاقىتتىن تارتىپ قاراڭغۇ چۈشكەن ۋاقىتقا قەدەر ناماز ئوقۇغىن. بامدات نامىزىنىمۇ ئوقۇغىن، (كۈندۈزنىڭ ۋە كېچىنىڭ پەرىشتىلىرى) چوقۇم بامدات نامىزىدا ھازىر بولىدۇ.');
INSERT INTO "saleh" VALUES(17,79,'17:79','ساڭا نەپلە (ئىبادەت) بولۇشى ئۈچۈن، كېچىنىڭ بىر قىسمىدا (ناماز ئوقۇشقا، قۇرئان ئوقۇشقا) ئويغانغىن، (ئى مۇھەممەد‍!) پەرۋەردىگارىڭنىڭ سېنى مەدھىيەلىنىدىغان ئورۇنغا (يەنى بۈيۈك شاپائەت ماقامىغا) تۇرغۇزۇشى ھەقىقەتتۇر.');
INSERT INTO "saleh" VALUES(17,80,'17:80','«پەرۋەردىگارىم! مېنى (قەبرەمگە) ئوڭۇشلۇق كىرگۈزگىن، (قەبرەمدىن) ئوڭۇشلۇق چىقارغىن، ماڭا دەرگاھىڭدىن ھەققە ياردەم بېرىدىغان قۇۋۋەت ئاتا قىلغىن» دېگىن. ');
INSERT INTO "saleh" VALUES(17,81,'17:81','«ھەقىقەت (يەنى ئىسلام) كەلدى، باتىل (يەنى كۇفرى) يوقالدى. باتىل ھەقىقەتەن ئوڭاي يوقىلىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(17,82,'17:82','بىز مۆمىنلەرگە (يەنى ئۇلارنىڭ دىللىرىغا) شىپا ۋە رەھمەت بولىدىغان قۇرئان ئايەتلىرىنى نازىل قىلىمىز، قۇرئان كاپىرلارغا زىياندىن باشقىنى زىيادە قىلمايدۇ (يەنى ئۇلار قۇرئاننى تەستىق قىلمىغانلىقتىن، ئۇلارنىڭ كۇفرى تېخىمۇ ئاشىدۇ).');
INSERT INTO "saleh" VALUES(17,83,'17:83',' ئىنسانغا(تۈرلۈك نېمەتلەرنى) بەرسەك (پەرۋەردىگارىنىڭ ئىبادىتىدىن) يۈز ئۆرۈيدۇ، (چوڭچىلىق قىلىپ) پەرۋەردىگارىدىن يىراقلىشىدۇ، ئۇنىڭغا بىرەر كۆڭۈلسىزلىك يەتسە، (ئاللاھنىڭ رەھمىتىدىن) مەيۈسلىنىپ كېتىدۇ. ');
INSERT INTO "saleh" VALUES(17,84,'17:84',' ھەر ئادەم (ھىدايەت ۋە گۇمراھلىقتا) ئۆز يولى بويىچە ئىش قىلىدۇ، پەرۋەردىگارىڭلار كىمنىڭ توغرا يولدا ئىكەنلىكىنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(17,85,'17:85',' ئۇلار سەندىن روھنىڭ ماھىيىتى ھەققىدە سورىشىدۇ. ‹‹روھنىڭ ماھىيىتىنى پەرۋەردىگارىم بىلىدۇ›› دېگىن، سىلەرگە پەقەت ئازغىنا ئىلىم بېرىلگەن.');
INSERT INTO "saleh" VALUES(17,86,'17:86','ئەگەر بىز خالىساق ساڭا ۋەھىي قىلغان قۇرئاننى ئەلۋەتتە (دىللاردىن ۋە مۇسھەفلەردىن) كۆتۈرۈۋېتەتتۇق، ئاندىن ئۇنى ئەسلىگە كەلتۈرۈشكە بىزگە قارشى ھېچقانداق ھامىي تاپالمايتتىڭ.');
INSERT INTO "saleh" VALUES(17,87,'17:87','پەقەت پەرۋەردىگارىڭ رەھمەت قىلىش يۈزىسىدىن ئۇنى كۆتۈرۈۋەتمىدى. ئاللاھنىڭ رەھمىتى ساڭا ھەقىقەتەن چوڭ بولدى.');
INSERT INTO "saleh" VALUES(17,88,'17:88','ئېيتقىنكى، «ئەگەر ئىنسانلار، جىنلار بۇ قۇرئاننىڭ ئوخشىشىنى مەيدانغا كەلتۈرۈش ئۈچۈن يىغىلىپ بىر- بىرىگە ياردەملەشكەن تەقدىردىمۇ، ئۇنىڭ ئوخشىشىنى كەلتۈرەلمەيدۇ».');
INSERT INTO "saleh" VALUES(17,89,'17:89',' بۇ قۇرئاندا ئىنسانلار ئۈچۈن تۈرلۈك مىساللارنى خىلمۇ خىل شەكىلدە بايان قىلدۇق، (روشەن پاكىتلارنىڭ بولۇشىغا قارىماي) ئىنسانلارنىڭ تولىسى پەقەت (ھەقىقەتنى) ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(17,90,'17:90',' ئۇلار ئېيتتى: «تاكى سەن بىزگە يەر ئاستىدىن بىر بۇلاقنى ئېتىلدۇرۇپ چىقارمىغىچە ساڭا ھەرگىز ئىشەنمەيمىز.');
INSERT INTO "saleh" VALUES(17,91,'17:91',' ياكى سېنىڭ خورمىلىق، تاللىق ۋە ئوتتۇرىسىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان بىر بېغىڭ بولسۇن.');
INSERT INTO "saleh" VALUES(17,92,'17:92','ياكى سەن ئېيتقاندەك ئۈستىمىزگە ئاسماننى پارچە ـ پارچە قىلىپ چۈشۈرگىن، ياكى (پەيغەمبەرلىكىڭگە) گۇۋاھچى قىلىپ ئاللاھنى ۋە پەرىشتىلەرنى ئالدىمىزغا كەلتۈرگىن.');
INSERT INTO "saleh" VALUES(17,93,'17:93',' ياكى سېنىڭ ئالتۇندىن ئۆيۈڭ بولسۇن، ياكى سەن (شوتا بىلەن) ئاسمانغا چىققىن، تاكى (ئاللاھ تەرىپىدىن سېنىڭ ئۇنىڭ بەندىسى ۋە پەيغەمبەرلىكى ئىكەنلىكىڭ يېزىلغان) بىز ئوقۇيالايدىغان بىر كىتابنى ئېلىپ چۈشمىگۈچە سېنىڭ ئاسمانغا چىققانلىقىڭغا ھەرگىز ئىشەنمەيمىز». (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئاللاھ پاكتۇر، مەن پەقەت بىر پەيغەمبەر ئىنسانمەن (ئاللاھنىڭ ئىزنى بولمىسا ھېچ ئادەم بىرەر مۆجىزە كەلتۈرەلمەيدۇ)».');
INSERT INTO "saleh" VALUES(17,94,'17:94','ئۇلار (يەنى مۇشرىكلار) نىڭ ئۆزلىرىگە ھىدايەت (يەنى پەيغەمبەر بىلەن قۇرئان) كەلگەن چاغدا ئىمان ئېيتماسلىقلىرى «ئاللاھ ئىنساننى پەيغەمبەر قىلىپ ئەۋەتەمدۇ؟» دېگەنلىكلىرى ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(17,95,'17:95','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «مۇبادا زېمىندا (يەنى زېمىن ئەھلىدەك) مېڭىپ يۈرۈيدىغان، ئولتۇراقلاشقان پەرىشتىلەر بولسا ئىدى، ئەلۋەتتە ئۇلارغا ئاسماندىن پەرىشتىنى پەيغەمبەر قىلىپ چۈشۈرەتتۇق».');
INSERT INTO "saleh" VALUES(17,96,'17:96','(ئى مۇھەممەد‍!) ئېيتقىنكى، «ئاللاھ مەن بىلەن سىلەرنىڭ ئاراڭلاردا مېنىڭ راستلىقىمغا گۇۋاھ بولۇشقا يېتەرلىكتۇر. ئاللاھ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، كۆرۈپ تۇرغۇچىدۇر».');
INSERT INTO "saleh" VALUES(17,97,'17:97',' كىمكى ئاللاھ ھىدايەت قىلغان ئىكەن، ئۇ ھىدايەت تاپقۇچىدۇر، ئاللاھ گۇمراھ قىلغان ئادەمگە ئاللاھتىن باشقا (ئازابتىن قۇتۇلدۇرىدىغان) ھېچ ياردەمچىلەرنى تاپالمايسەن، قىيامەت كۈنى ئۇلارنى دۈم ياتقۇزۇپ (سۆرەلگەن) كور، گاچا، گاس ھالدا يىغىمىز، ئۇلارنىڭ جايى جەھەننەم بولىدۇ، (جەھەننەمنىڭ) ئوتى پەسلەپ قالسا، ئۇلارغا (ئوتنى) تېخىمۇ يالقۇنجىتىمىز.');
INSERT INTO "saleh" VALUES(17,98,'17:98','ئۇلارنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ۋە «چىرىگەن سۆڭەك ۋە توپىغا ئايلانغىنىمىزدىن كېيىن راستلا قايتىدىن تىرىلىپ يېڭىدىن ئادەم بولامدۇق؟» دېگەنلىكلىرى ئۈچۈن، ئۇلارغا بېرىلگەن جازا ئەنە شۇدۇر.');
INSERT INTO "saleh" VALUES(17,99,'17:99','ئاسمانلارنى، زېمىننى ياراتقان ئاللاھنىڭ ئۇلارغا ئوخشاش ئىنسانلارنى يارىتالايدىغانلىقىنى ئۇلار بىلمەمدۇ؟ ئاللاھ ئۇلارنىڭ (ئۆلۈشى ۋە تىرىلىشى ئۈچۈن) شەك ـ شۈبھىسىز مۇئەييەن ۋاقىت بەلگىلىدى، زالىملار پەقەت كۇفرىغىلا ئۇنايدۇ.');
INSERT INTO "saleh" VALUES(17,100,'17:100','«ئەگەر پەرۋەردىگارىمنىڭ رەھمەت خەزىنىلىرى سىلەرنىڭ قولۇڭلاردا بولسا، چىقىمدىن (يەنى تۈگەپ كېتىشىدىن) قورقۇپ، چوقۇم بېخىللىق قىلاتتىڭلار، ئىنسان بېخىل كېلىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(17,101,'17:101',' بىز مۇساغا ھەقىقەتەن توققۇز روشەن مۆجىزە ئاتا قىلدۇق، مۇسا پىرئەۋنگە ۋە ئۇنىڭ قەۋمىگە كەلگەن چاغدا بىز مۇساغا: «(ئۇلاردىن) ئىسرائىل ئەۋلادىنى (قويۇۋېتىشنى) سورىغىن» (دېدۇق). پىرئەۋن ئۇنىڭغا: «ئى مۇسا! سېنى مەن چوقۇم جادۇ قىلىنغان دەپ ئويلايمەن» دېدى.');
INSERT INTO "saleh" VALUES(17,102,'17:102','مۇسا: «(ئى پىرئەۋن!) بۇلار (يەنى توققۇز مۆجىزە) نى پەقەت ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى (مېنىڭ راستلىقىمنى ئىسپاتلايدىغان) دەلىل قىلىپ نازىل قىلغانلىقىنى سەن (گەرچە تىلىڭدا ئىقرار قىلمىساڭمۇ دىلىڭدا ئەلۋەتتە) ئوبدان بىلىسەن، مەن سېنى، ئى پىرئەۋن، ھەقىقەتەن، ھالاك بولىدۇ دەپ ئويلايمەن» دېدى.');
INSERT INTO "saleh" VALUES(17,103,'17:103',' پىرئەۋن ئۇلارنى (مىسىر) زېمىنىدىن چىقىرىۋەتمەكچى بولدى، بىز پىرئەۋننى ۋە ئۇنىڭ بىلەن بىللە بولغانلارنى ـ ھەممىسىنى (دېڭىز) غا غەرق قىلىۋەتتۇق.');
INSERT INTO "saleh" VALUES(17,104,'17:104',' پىرئەۋننى غەرق قىلىۋەتكەندىن كېيىن، بىز ئىسرائىل ئەۋلادىغا: «(مىسىر) زېمىنىدا تۇرۇڭلار، ئاخىرەت ۋەدىسى (يەنى قىيامەت) كەلگەن چاغدا سىلەرنى (قەبرەڭلاردىن مەھشەرگاھقا) (كاپىرلار بىلەن مۆمىنلەرنى) ئارىلاش ئېلىپ كېلىمىز» دېدۇق.');
INSERT INTO "saleh" VALUES(17,105,'17:105','قۇرئاننى ھەقىقەت ئاساسىدا نازىل قىلدۇق، قۇرئانمۇ ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا نازىل بولدى، سېنى بىز پەقەت خوشخەۋەر بەرگۈچى، ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(17,106,'17:106','قۇرئاننى كىشىلەرگە دانە ـ دانە ئوقۇپ بېرىشىڭ ئۈچۈن ئۇنى بۆلۈپ ـ بۆلۈپ نازىل قىلدۇق، ئۇنى تەدرىجىي نازىل قىلدۇق. ');
INSERT INTO "saleh" VALUES(17,107,'17:107','«قۇرئانغا ئىشىنىڭلار ياكى ئىشەنمەڭلار (مەيلى، چۈنكى ئىشىنىشىڭلار قۇرئانغا كامالەت، ئىشەنمەسلىكىڭلار قۇرئانغا نۇقسان ئېلىپ كەلمەيدۇ)» دېگىن. شۈبھىسىزكى، قۇرئان نازىل بولۇشتىن ئىلگىرى كىتاب بېرىلگەنلەر قۇرئاننى ئاڭلىغان چاغلىرىدا (تەسىرلىنىپ) دەرھال سەجدىگە بارىدۇ.');
INSERT INTO "saleh" VALUES(17,108,'17:108','ئۇلار: «پەرۋەردىگارىمىز پاكتۇر، پەرۋەردىگارىمىزنىڭ ۋەدىسى چوقۇم ئورۇنلىنىدۇ» دەيدۇ. ');
INSERT INTO "saleh" VALUES(17,109,'17:109','ئۇلار يىغلىغان ھالدا سەجدە قىلىشقا يىقىلىدۇ (قۇرئاننى ئاڭلاش بىلەن ئۇلار ئاللاھتىن) تېخىمۇ ئەيمىنىدۇ. ');
INSERT INTO "saleh" VALUES(17,110,'17:110',' «ئاللاھ دەپ ئاتىساڭلارمۇ بولىدۇ، رەھمان دەپ ئاتىساڭلارمۇ بولىدۇ، قايسىنى ئاتىساڭلار (ھەممىسى ياخشىدۇر). چۈنكى ئاللاھنىڭ نۇرغۇن گۈزەل ئىسىملىرى بار» دېگىن، نامىزىڭدا (قىرائەتنى) (مۇشرىكلار ئاڭلاپ قېلىپ ساڭا ئەزىيەت يەتكۈزمەسلىگى ئۈچۈن) يۇقىرى ئاۋاز بىلەنمۇ ئوقۇمىغىن، (مۆمىنلەر ئاڭلىماي قالماسلىقى ئۈچۈن) پەس ئاۋاز بىلەنمۇ ئوقۇمىغىن، ئۇلارنىڭ ئارىسىدا ئوتتۇرا يول تۇتقىن. ');
INSERT INTO "saleh" VALUES(17,111,'17:111','ئېيتقىنكى، «جىمى ھەمدۇسانا بالىسى بولۇشتىن پاك بولغان، سەلتەنىتىدە شېرىكى بولمىغان، خارلىقتىن قۇتۇلۇش ئۈچۈن دوستقا موھتاج بولمىغان (يەنى خار بولمىغان، ياردەمچىگە موھتاج بولمىغان) ئاللاھقا خاستۇر! ئاللاھنى مۇكەممەل رەۋىشتە ئۇلۇغلىغىن».');
INSERT INTO "saleh" VALUES(18,1,'18:1','جىمى ھەمدۇسانا ئاللاھقا خاستۇركى، (ئاللاھ ئىنسانلارنى) ئۆز تەرىپىدىن بولغان قاتتىق ئازابتىن ئاگاھلاندۇرۇش ئۈچۈن، ياخشى ئەمەللەرنى قىلغان مۆمىنلەرگە ئوبدان مۇكاپات (يەنى جەننەت) بېرىدىغانلىقى بىلەن خۇشخەۋەر بېرىش ئۈچۈن، بەندىسى (يەنى پەيغەمبىرى مۇھەممەد) گە توغرا، ھېچقانداق قىڭغىرلىق (يەنى ئىختىلاپ، زىددىيەت) بولمىغان قۇرئاننى نازىل قىلدى[1 ـ 2].');
INSERT INTO "saleh" VALUES(18,2,'18:2','جىمى ھەمدۇسانا ئاللاھقا خاستۇركى، (ئاللاھ ئىنسانلارنى) ئۆز تەرىپىدىن بولغان قاتتىق ئازابتىن ئاگاھلاندۇرۇش ئۈچۈن، ياخشى ئەمەللەرنى قىلغان مۆمىنلەرگە ئوبدان مۇكاپات (يەنى جەننەت) بېرىدىغانلىقى بىلەن خۇشخەۋەر بېرىش ئۈچۈن، بەندىسى (يەنى پەيغەمبىرى مۇھەممەد) گە توغرا، ھېچقانداق قىڭغىرلىق (يەنى ئىختىلاپ، زىددىيەت) بولمىغان قۇرئاننى نازىل قىلدى[1 ـ 2].');
INSERT INTO "saleh" VALUES(18,3,'18:3','ئۇلار (يەنى ياخشى ئەمەللەرنى قىلغان مۆمىنلەر) جەننەتتە مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(18,4,'18:4','ئاللاھنىڭ بالىسى بار دېگۈچىلەرنى ئاگاھلاندۇرىدۇ.');
INSERT INTO "saleh" VALUES(18,5,'18:5','ئۇلار ۋە ئۇلارنىڭ ئاتا ـ بوۋىلىرى بۇ بوھتانلىرىدا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلارنىڭ ئېغىزلىرىدىن چىققان سۆز نېمىدېگەن چوڭ! ئۇلار پەقەت يالغاننىلا ئېيتىدۇ.');
INSERT INTO "saleh" VALUES(18,6,'18:6','ئەگەر ئۇلار قۇرئانغا ئىمان ئېيتمىسا، ئەپسۇسلىنىپ، ئۇلارنىڭ (ئىماندىن) يۈز ئۆرۈگەنلىكىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن.');
INSERT INTO "saleh" VALUES(18,7,'18:7',' ئىنسانلارنىڭ قايسىسىنىڭ ئەمەلى ئەڭ ياخشى ئىكەنلىكىنى سىناش ئۈچۈن، بىز ھەقىقەتەن يەر يۈزىدىكى شەيئىلەرنىڭ ھەممىسىنى يەر يۈزىنىڭ زىننىتى قىلدۇق. ');
INSERT INTO "saleh" VALUES(18,8,'18:8','بىز چوقۇم يەر يۈزىدىكى (دەل ـ دەرەخ، تاغ ۋە بىنا قاتارلىق) نەرسىلەرنىڭ ھەممىسىنى يوق قىلىپ، يەر يۈزىنى تۈپتۈز باياۋانغا ئايلاندۇرىمىز.');
INSERT INTO "saleh" VALUES(18,9,'18:9','ئەسھابۇلكەھفنى ۋە رەقىم (يەنى ئەسھابۇلكەھفنىڭ ئىسىملىرى يېزىلغان تاختا) نى (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىمىز ئىچىدىكى ئەڭھەيران قالارلىقى دەپ قارىدىڭمۇ؟. ');
INSERT INTO "saleh" VALUES(18,10,'18:10','ئۆز ۋاقتىدا بىر قانچە يىگىت غارنى پاناھ جاي قىلىۋالدى، ئۇلار: «پەرۋەردىگارىمىز! بىزگە رەھمەت خەزىنىلىرىڭدىن ئاتا قىلغىن، بىزنىڭ ئىشلىرىمىزنى تۈزەپ، بىزنى ھىدايەت تاپقۇچىلاردىن قىلغىن» دېدى.');
INSERT INTO "saleh" VALUES(18,11,'18:11','ئۇلارنى بىز غاردا ئۇزۇن يىللار ئۇخلىتىپ قويدۇق.');
INSERT INTO "saleh" VALUES(18,12,'18:12',' ئاندىن ئىككى گۇرۇھ (يەنى ئەسھابۇلكەھف ئىچىدىكى قانچىلىك ئۇخلىغانلىقلىرى ھەققىدە ئىختىلاپ قىلىشقان ئىككى گۇرۇھ) تىن قايسىسىنىڭ غاردا قانچىلىك ئۇزاق تۇرۇشقانلىرىنى ئىنچىكە ھېسابلايدىغانلىقىنى سىناش ئۈچۈن ئۇلارنى ئويغاتتۇق.');
INSERT INTO "saleh" VALUES(18,13,'18:13',' بىز ساڭا ئۇلارنىڭ ھېكايىسىنى راستلىقى بىلەن ئېيتىپ بېرىمىز، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا ئىمان ئېيتقان، بىز ھىدايەتنى زىيادە قىلغان يىگىتلەردۇر.');
INSERT INTO "saleh" VALUES(18,14,'18:14','ئۇلارنىڭ دىللىرىنى (يەنى ئىرادىسىنى) مۇستەھكەم قىلدۇق، ئۆز ۋاقتىدا ئۇلار (ئىمانسىز زالىم پادىشاھ ئالدىدا) دەس تۇرۇپ ئېيتتى: «بىزنىڭ پەرۋەردىگارىمىز ئاسمانلارنىڭ ۋە زېمىنىڭ پەرۋەردىگارىدۇر، بىز ئۇنى قويۇپ ھەرگىزمۇ باشقا ئىلاھقا ئىبادەت قىلمايمىز، ئەگەر بىز (ئاللاھنىڭ شېرىكى بار) دەيدىغان بولساق، بۇ چاغدا ھەقىقەتتىن تولىمۇ يىراقلاشقان بولىمىز.');
INSERT INTO "saleh" VALUES(18,15,'18:15','بىزنىڭ قەۋمىمىز ئاللاھنى قويۇپ (بۇتلارنى) ئىلاھ قىلىۋالدى، ئۇلار ئۆزلىرىنىڭ ئىلاھلارغا ئىبادەت قىلىشقا تېگىشلىك ئىكەنلىكىگە نېمىشقا ئېنىق دەلىل كەلتۈرمەيدۇ، ئاللاھقا يالغاننى چاپلىغان ئادەمدىنمۇ زالىم كىم بار؟».');
INSERT INTO "saleh" VALUES(18,16,'18:16','(ئى يىگىتلەر!) سىلەر ئۇلاردىن ۋە ئۇلارنىڭ ئاللاھتىن باشقا ئىبادەت قىلىدىغان بۇتلىرىدىن ئايرىلغان ئىكەنسىلەر، غارنى پاناھ جاي قىلىڭلار، پەرۋەردىگارىڭلار سىلەرگە كەڭ رەھمەت قىلىدۇ، ئاللاھ سىلەرنىڭ ئىشلىرىڭلارنى ئاسانلاشتۇرۇپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(18,17,'18:17',' كۈن چىققان چاغدا ئۇلارنىڭ غارنىڭ ئوڭ تەرىپىگە ئۆتۈپ كەتكەنلىكىنى، كۈن پاتقان چاغدا ئۇلارنىڭ غارنىڭ شىمال تەرىپىگە ئۆتۈپ كەتكەنلىكىنى كۆرۈسەن (يەنى غارنىڭ ئىشىكى شىمال تەرەپتە بولغانلىقتىن كۈننىڭ نۇرى غارنىڭ ئىككى تەرىپىگە چۈشۈپ تۇرىدۇ، ئاللاھ ئۇلارنى ھۆرمەتلىگەنلىكتىن، كۈن چىققاندىمۇ، پاتقاندىمۇ ئۇنىڭ نۇرى ئۇلارنىڭ ئۈستىگە چۈشمەيدۇ)، ئۇلار بولسا غارنىڭ (ئوتتۇرىسىدىكى) بوشلۇقتىدۇر، بۇ ئاللاھنىڭ (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلەردىندۇر، كىمنىكى ئاللاھ ھىدايەت قىلىدىكەن، ئۇ (ھەقىقىي) ھىدايەت تاپقۇچىدۇر، كىمنىكى (قىلغان يامان ئەمەلى تۈپەيلىدىن) ئاللاھ ئازدۇرىدىكەن، ئۇنىڭغا يول كۆرسەتكۈچى دوستنى ھەرگىز تاپالمايسەن.');
INSERT INTO "saleh" VALUES(18,18,'18:18','(مۇبادا ئۇلارنى كۆرىدىغان بولساڭ، كۆزلىرىنىڭ ئوچۇقلۇقى ۋە ئۆرۈلۈپ تۇرىدىغانلىقتىن) ئۇلارنى ئويغاق دەپ ئويلايسەن. ھالبۇكى، ئۇلار ئۇيقۇدىدۇر، ئۇلارنى بىز ئوڭ ۋە سول تەرەپكە ئۆرۈپ تۇرىمىز. ئۇلارنىڭ ئىتى (غار) نىڭ بوسۇغىسىدا ئىككى پۇتىنى سوزۇپ ياتىدۇ، ئەگەر ئۇلارنى كۆرىدىغان بولساڭ، ئەلۋەتتە ئۇلاردىن قاتتىق قورقۇپ، يۈز ئۆرۈپ چوقۇم قاچقان بولاتتىڭ. ');
INSERT INTO "saleh" VALUES(18,19,'18:19','ئۇلارنى ئۇخلاتقىنىمىزدەك، (غاردا قانچىلىك تۇرغانلىقلىرىنى) ئۆز ئارا سوراشسۇن دەپ (ئۆلۈمگە ئوخشاش ئۇزاق ئۇيقۇدىن) ئۇلارنى ئويغاتتۇق، ئۇلارنىڭ بىرى «(بۇ غاردا) قانچە ئۇزۇن تۇردۇڭلار؟» دېدى. ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق» دېيىشتى. ئۇلارنىڭ (بەزىسى) دېدىكى، «قانچە ئۇزاق تۇرغانلىقىڭلارنى پەرۋەردىگارىڭلار ئوبدان بىلىدۇ (بۇنى سۈرۈشتۈرۈشنىڭ پايدىسى يوق، بىز ھازىر ئاچ)، بىرىڭلارنى بۇيرۇڭلار، بۇ تەڭگىلەرنى شەھەرگە ئېلىپ بېرىپ، كىمنىڭ تامىقى ئەڭ پاكىز بولسا، ئۇنىڭدىن سىلەرگە سېتىۋېلىپ ئېلىپ كەلسۇن، ئۇ (شەھەرگە كىرىشتە ۋە تاماق سېتىۋېلىشتا) ئېھتىيات قىلسۇن، سىلەرنى ھېچ كىشىگە تۇيدۇرمىسۇن. ');
INSERT INTO "saleh" VALUES(18,20,'18:20',' ئەگەر شەھەردىكى كىشىلەر سىلەرنى تۇتۇۋالسا، سىلەرنى چالما - كېسەك قىلىپ ئۆلتۈرىدۇ ياكى سىلەرنى ئۆزلىرىنىڭ دىنىغا كىرىشكە مەجبۇرلايدۇ، مۇنداقتا ھەرگىزمۇ مەقسىتىڭلارغا ئېرىشەلمەيسىلەر».');
INSERT INTO "saleh" VALUES(18,21,'18:21','ئۇلارنى ئۇخلىتىپ ئويغاتقىنىمىزدەك، كىشىلەرگە ئاللاھنىڭ (قايتا تىرىلدۈرۈش توغرىسىدىكى) ۋەدىسىنىڭ ھەقلىقىنى ۋە قىيامەتنىڭ بولىدىغانلىقىدا شەك ـ شۈبھە يوق ئىكەنلىكىنى بىلدۈرۈش ئۈچۈن، كىشىلەرنى ئۇلارنىڭ ئەھۋالىدىن خەۋەردار قىلدۇق. ئۆز ۋاقتىدا ئۇلار ئەسھابۇلكەھفنىڭ ئىشى ئۈستىدە دەتالاش قىلىشتى، بەزى كىشىلەر: «(ئۇلارنىڭ يېنىغا ھېچ كىشىنىڭ كىرمەسلىكى ئۈچۈن) ئەسھابۇلكەھفنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشىكىگە) بىر تام ئېتىڭلار» دېدى. ئاللاھ ئەسھابۇلكەھفنىڭ ئەھۋالىنى ھەممىدىن ئوبدان بىلىدۇ، گېپى ئۆتىدىغان باشلىقلار (يەنى پادىشاھ ۋە شەھەر كاتتىباشلىرى): «غارنىڭ ئەتراپىغا (يەنى غارنىڭ ئىشىكى ئالدىغا) ئەلۋەتتە بىر مەسجىد سالايلى» دېدى.');
INSERT INTO "saleh" VALUES(18,22,'18:22',' ئۇلار (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ زامانىدىكى ناسارالار): «ئۇلار (يەنى ئەسھابۇلكەھف) ئۈچ بولۇپ، تۆتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ. بەزىلەر: «ئۇلار بەش بولۇپ، ئالتىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ. بۇ پۈتۈنلەي ئاساسسىز، قارىسىغا (ئېيتىلغان سۆزدۇر). يەنە بەزىلەر: «ئۇلار يەتتە بولۇپ، سەككىزىنچىسى ئۇلارنىڭ ئىتىدۇر» دەيدۇ. ئېيتقىنكى، «ئۇلارنىڭ سانىنى پەرۋەردىگارىم ئوبدان بىلىدۇ، ئۇندىن باشقا ئازغىنا كىشىلەر بىلىدۇ». ئۇلار توغرىسىدا (ناسارالار بىلەن) پەقەت يۈزەگىنە مۇنازىرىلەشكىن (يەنى بۇ ھەقتە مۇنازىرىلەشكۈچىلەرنى تەستىقمۇ قىلمايدىغان، يالغانغىمۇ چىقارمايدىغان دەرىجىدە مۇنازىرىلەشكىن). ئۇلارنىڭ ھېچبىرىدىن ئەسھابۇلكەھف قىسسەسىنى سورىمىغىن.');
INSERT INTO "saleh" VALUES(18,23,'18:23',' سەن بىرەر ئىشنى قىلماقچى بولساڭ، «ئەتە شۇ ئىشنى چوقۇم قىلىمەن» دېمەي،');
INSERT INTO "saleh" VALUES(18,24,'18:24','«خۇدا خالىسا» دېگەن سۆزنى قوشۇپ دېگىن (يەنى خۇدا خالىسا قىلىمەن دېگىن)، (خۇدا خالىسا دېگەن سۆزنى ئۇنتۇپ قالساڭ، پەرۋەردىگارىڭنى تىلغا ئالغىن (يەنى «خۇدا خالىسا» نى ئېسىڭگە كەلگەندە ئېيتقىن) ھەمدە: «پەرۋەردىگارىمنىڭ مېنى بۇنىڭدىنمۇ يېقىن توغرا يولغا باشلىشىنى (يەنى دىنىم، دۇنيايىمدا مېنى ئەڭ دۇرۇس ئىشلارغا مۇۋەپپەق قىلىشىنى) ئۈمىد قىلىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(18,25,'18:25','ئۇلار (يەنى ئەسھابۇلكەھف) ئۆز غارىدا ئۈچيۈز توققۇز يىل تۇردى. ');
INSERT INTO "saleh" VALUES(18,26,'18:26','ئېيتقىنكى، «ئۇلارنىڭ تۇرغان ۋاقتىنى ئاللاھ ئوبدان بىلىدۇ، ئاسمانلاردىكى، زېمىندىكى غەيبنى (بىلىش) ئاللاھقا خاستۇر. ئاللاھنىڭ كۆرۈشى نېمىدېگەن مۇكەممەل! ئاللاھنىڭ ئاڭلىشى نېمىدېگەن مۇكەممەل! ئۇلارغا ئاللاھتىن بۆلەك ھېچقانداق ياردەمچى بولمايدۇ، ئاللاھ ئۆز ھۆكمىدە ھېچ كىشىنى شېرىك قىلمايدۇ».');
INSERT INTO "saleh" VALUES(18,27,'18:27',' (ئى مۇھەممەد!) سەن پەرۋەردىگارىڭنىڭ كىتابىدىن ساڭا ۋەھىي قىلىنغاننى ئوقۇغىن، ئاللاھنىڭ سۆزلىرىنى ھېچ كىشى ئۆزگەرتەلمەيدۇ، سەن ھەرگىزمۇ ئاللاھتىن بۆلەك ئىلتىجاگاھ تاپالمايسەن.');
INSERT INTO "saleh" VALUES(18,28,'18:28',' سەن پەرۋەردىگارىڭنىڭ رازىلىقىنى تىلەپ، ئەتىگەن ـ ئاخشامدا ئۇنىڭغا ئىبادەت قىلىدىغانلار بىلەن سەۋرچان بولغان ھالدا بىللە بولغىن، ھاياتى دۇنيانىڭ زىبۇ - زىننىتىنى دەپ، ئۇلارنى كۆزگە ئىلماي قالمىغىن، بىز دىلىنى زىكرىمىزدىن غەپلەتتە قالدۇرغان، نەپسى خاھىشىغا ئەگەشكەن، داۋاملىق ئاشقۇنلۇق قىلىدىغان ئادەمگە ئەگەشمىگىن.');
INSERT INTO "saleh" VALUES(18,29,'18:29','(ئى مۇھەممەد!) «(بۇ) ھەق (قۇرئان) پەرۋەردىگارىڭلار تەرىپىدىن نازىل بولىدۇ، خالىغان ئادەم ئىمان ئېيتسۇن، خالىغان ئادەم كاپىر بولسۇن» دېگىن، بىز ھەقىقەتەن كاپىرلار ئۈچۈن تۈتۈن پەردىلىرى ئۇلارنى ئورىۋالىدىغان ئوتنى تەييارلىدۇق، ئۇلار (تەشنالىقتىن) سۇ تەلەپ قىلسا، ئۇلارغا مەدەن ئېرىتمىسىگە ئوخشاش، يۈزلەرنى كۆيدۈرۈۋېتىدىغان (ناھايىتى قىزىق) سۇ بېرىلىدۇ، بۇ نېمىدېگەن يامان شاراب! جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(18,30,'18:30','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كېلىدىغان بولساق، بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنىڭ ساۋابىنى ھەقىقەتەن يوققا چىقىرىۋەتمەيمىز.');
INSERT INTO "saleh" VALUES(18,31,'18:31',' ئەنە شۇلار ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار دائىم تۇرىدىغان جەننەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە ئۇلار ئالتۇن بىلەيزۈكلەرنى سالىدۇ، نېپىز ۋە قېلىن يىپەكتىن ئىشلەنگەن يېشىل كىيىملەرنى كىيىدۇ. (ئالتۇن ۋە ئېسىل رەختلەر بىلەن قاپلانغان) تەختلەرگە يۆلىنىپ ئولتۇرىدۇ. بۇ نېمىدېگەن ياخشى مۇكاپات! جەننەت نېمىدېگەن گۈزەل جاي!');
INSERT INTO "saleh" VALUES(18,32,'18:32','ئۇلارغا مۇنداق (بىرى مۆمىن، بىرى كاپىر بولغان) ئىككى ئادەمنى مىسال قىلىپ كەلتۈرگىن: ئۇلارنىڭ بىرىگە (يەنى كاپىرغا) بىز ئەتراپى خورمىزارلىق، (ئىككى باغنىڭ) ئوتتۇرىسى ئېكىنزارلىق بولغان تاللىق ئىككى باغ ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(18,33,'18:33',' ھەر ئىككىلىسى كەم ـ كۈتىسىز، تولۇق مېۋە بېرىدىغان باغلار بولۇپ، ئۇلارنىڭ ئارىسىدىن ئېقىپ تۇرىدىغان ئۆستەڭنى بەرپا قىلدۇق.');
INSERT INTO "saleh" VALUES(18,34,'18:34','ئۇ (يەنى باغ ئىگىسى) نۇرغۇن مال ـ مۈلۈككە ئىگە ئىدى، ئۇ بۇرادىرىگە پەخىرلىنىپ: « مەن سەندىن باي، ئادەملىرىم (يەنى خىزمەتچىلىرىم، بالىلىرىم، ياردەمچىلىرىم) سېنىڭدىن كۆپ» دېدى.');
INSERT INTO "saleh" VALUES(18,35,'18:35','ئۇ (مۆمىن بۇرادىرىنى يېتىلەپ) بېغىغا كىرىپ (كۇفرىلىق قىلىش بىلەن) ئۆزىگە زۇلۇم قىلغان ھالدا ئېيتتى: «بۇ باغ مەڭگۈ يوقالمايدۇ دەپ ئويلايمەن.');
INSERT INTO "saleh" VALUES(18,36,'18:36',' شۇنىڭدەك قىيامەت بولمايدۇ دەپ ئويلايمەن، ئەگەر مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ (يەنى قىيامەت بولغاندىمۇ) ئەلۋەتتە بۇ باغدىن ياخشىراق جاينى تاپىمەن».');
INSERT INTO "saleh" VALUES(18,37,'18:37','ئۇنىڭغا (مۆمىن) بۇرادىرى مۇنازىرىلەشكەن ھالدا ئېيتتى: «سېنى (ئەسلىدە) تۇپراقتىن، ئاندىن ئابىمەنىيدىن يارىتىپ، ئاندىن سېنى راۋۇرۇس ئىنسان قىلغان زاتنى ئىنكار قىلامسەن؟ ');
INSERT INTO "saleh" VALUES(18,38,'18:38','لېكىن مەن پەرۋەردىگارىم بولغان ئۇ ئاللاھنى (ئېتىراپ قىلىمەن)، پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن.');
INSERT INTO "saleh" VALUES(18,39,'18:39','سەن بېغىڭغا كىرگەن چېغىڭدا: ئاللاھنىڭ خالىغىنى بولىدۇ، ئاللاھنىڭ ياردىمى بولمىسا قولۇمدىن ھېچ ئىش كەلمەيدۇ، دېسەڭچۇ! سېنىڭ قارىشىڭچە، مېنىڭ مېلىم ۋە ئەۋلادىم سېنىڭدىن ئاز بولسىمۇ،');
INSERT INTO "saleh" VALUES(18,40,'18:40','ئۈمىدكى، پەرۋەردىگارىم ماڭا سېنىڭ بېغىڭدىن ياخشىراق نەرسە ئاتا قىلغاي. بېغىڭغا ئاسماندىن بىر ئاپەت يۈزلەندۈرۈش بىلەن بېغىڭ تۈپتۈز قاقاس يەرگە ئايلىنىپ قالغاي،');
INSERT INTO "saleh" VALUES(18,41,'18:41','ياكى ئۇنىڭ سۈيى سىڭىپ كېتىپ ئۇنى ئىزدەش (يەنى ئەسلىگە كەلتۈرۈش) سېنىڭ قولۇڭدىن كەلمىگەي».');
INSERT INTO "saleh" VALUES(18,42,'18:42','ئۇنىڭ مېۋىلىرى (يەنى بېغى) ۋەيران بولدى، ئۈزۈم بىدىشلىرى يەرگە يىقىلدى، ئۇ باغقا سەرپ قىلغان چىقىملىرىغا (ھەسرەت چېكىپ) ئىككى ئالقىنىنى ئۇۋۇلىغىلى تۇردى ھەمدە ئۇ: «مەن پەرۋەردىگارىمغا ھېچ ئەھەدىنى شېرىك كەلتۈرمىسەمچۇ» دېدى.');
INSERT INTO "saleh" VALUES(18,43,'18:43','ئۇنىڭغا ئاللاھتىن باشقا ياردەم قىلىدىغان (يەنى ئۇنىڭدىن بۇ بالانى دەپئى قىلىدىغان) ھېچ جامائە بولمىدى، ئۆزىگىمۇ ئۆزى ياردەم قىلالمىدى.');
INSERT INTO "saleh" VALUES(18,44,'18:44','بۇ يەردە (يەنى مۇنداق ھالەتتە) مەدەتكار بولۇش پەقەت بەرھەق ئاللاھقا خاستۇر، ئاللاھ (ئۆزىگە ئىمان ئېيتقۇچىلارغا دۇنيا ۋە ئاخىرەتتە) ياخشى مۇكاپات بەرگۈچىدۇر، (ئۇنىڭغا يۆلەنگەن ۋە ئۇنى ئۈمىد قىلغانلارنىڭ) ئاقىۋىتىنى ياخشى قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(18,45,'18:45','(ئى مۇھەممەد!) ئۇلارغا (يەنى كىشىلەرگە) دۇنيا تىرىكچىلىكى ھەققىدە مۇنداق مىسالنى كەلتۈرگىن: (ئۇ شۇنىڭغا) ئوخشايدۇكى، بىز ئاسماندىن يامغۇر ياغدۇردۇق، شۇ سۇ بىلەن زېمىننىڭ ئۆسۈملۈكلىرى بىر ـ بىرىگە چىرمىشىپ بولۇق ئۆستى، كېيىن ئۇ قۇرۇپ تۆكۈلدى، شاماللار ئۇنى (تەرەپ ـ تەرەپكە) ئۇچۇرۇپ كەتتى، ئاللاھ ھەر نەرسىگە قادىردۇر. ');
INSERT INTO "saleh" VALUES(18,46,'18:46',' ماللار ۋە بالىلار دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، باقى قالىدىغان ياخشى ئەمەللەرنىڭ ساۋابى پەرۋەردىگارىڭنىڭ نەزىرىدە تېخىمۇ ياخشىدۇر. ئۈمىد تېخىمۇ چوڭدۇر.');
INSERT INTO "saleh" VALUES(18,47,'18:47',' ئۇ كۈندە (يەنى قىيامەت كۈنىدە) بىز تاغلارنى يوق قىلىمىز، زېمىننى ئوچۇق، تۈپتۈز كۆرىسەن (يەنى ئۇنى توسۇپ تۇرىدىغان تاغ، دەرەخ، بىنالار بولمايدۇ)، ئۇلارنى (يەنى ئىنسانلارنى) (مەھشەرگاھقا) يىغىمىز، ئۇلاردىن بىر كىشىنىمۇ قالدۇرۇپ قويمايمىز.');
INSERT INTO "saleh" VALUES(18,48,'18:48',' ئۇلار (يەنى جىمى خالايىق) سەپ ـ سەپ بولۇشۇپ پەرۋەردىگارىڭغا توغرىلىنىدۇ، (كاپىرلارغا) «سىلەر بىزنىڭ ئالدىمىزغا سىلەرنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالەتتە (مالسىز، بالىسىز، يالىڭاچ) كەلدىڭلار، بەلكى سىلەر، سىلەرگە ۋەدە قىلىنغان چاغنى بېكىتمەيدۇ، دەپ ئويلىغانسىلەر (يەنى ئۆلگەندىن كېيىن تىرىلىش، ھېساپ بېرىش، جازاغا تارتىلىش يوق دەپ ئويلىغانسىلەر)» دەيمىز.');
INSERT INTO "saleh" VALUES(18,49,'18:49',' كىشىلەرنىڭ نامە ـ ئەمالى ئوتتۇرىغا قويۇلىدۇ، گۇناھكارلارنىڭ ئۇنىڭدىكى خاتىرىلەردىن قورققانلىقىنى كۆرىسەن، ئۇلار: «ۋاي بىزگە! بۇ نامە ـ ئەمالغا چوڭ ـ كىچىك گۇناھنىڭ ھەممىسى خاتىرىلىنىپتىغۇ؟ » دەيدۇ، ئۇلار قىلغان ـ ئەتكەنلىرىنىڭ ھەممىسىنىڭ نامە ـ ئەمالىغا خاتىرىلەنگەنلىكىنى كۆرىدۇ، پەرۋەردىگارىڭ ھېچ ئادەمگە زۇلۇم قىلمايدۇ.');
INSERT INTO "saleh" VALUES(18,50,'18:50','ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقا ھەممىسى سەجدە قىلدى، ئىبلىس جىنلاردىن ئىدى. شۇڭا ئۇ (سەجدە قىلمىغانلىقتىن) پەرۋەردىگارىنىڭ ئىتائىتىدىن چىقتى. مېنى قويۇپ ئىبلىسنى ۋە ئۇنىڭ ئەۋلادىنى دوست قىلىۋالامسىلەر؟ ھالبۇكى، ئۇلار سىلەرگە دۈشمەندۇر، ئىبلىس زالىملار ئۈچۈن نېمىدېگەن يامان بەدەل! (يەنى ئاللاھقا ئىبادەت قىلىشنىڭ ئورنىغا شەيتانغا چوقۇنۇش نېمىدېگەن يامان!). ');
INSERT INTO "saleh" VALUES(18,51,'18:51','ئۇلار (يەنى شەيتانلار) غا ئاسمانلارنىڭ ۋە زېمىننىڭ يارىتىلىشىنىمۇ، ئۇلارنىڭ ئۆزلىرىنىڭ يارىتىلىشىنىمۇ كۆرسەتكىنىم يوق (يەنى مەزكۇرلارنى خەلق ئەتكەن چېغىمدا ئۇلارنى ھازىر قىلغىنىم يوق)، (كىشىلەرنى) ئازدۇرغۇچىلارنى ياردەمچى قىلىۋالغىنىممۇ يوق.');
INSERT INTO "saleh" VALUES(18,52,'18:52',' شۇ كۈندە ئاللاھ (ئۇلارغا) ئېيتىدۇ: «سىلەر مېنىڭ شېرىكلىرىم دەپ ئويلىغىنىڭلارنى چاقىرىڭلار». ئۇلار چاقىرىدۇ، لېكىن ئۇلار (نىڭ شېرىكلىرى) جاۋاب بېرەلمەيدۇ، بىز ئۇلارنىڭ ئارىسىدا (ئۇلار ئۆتەلمەيدىغان) ھالاكەتلىك جاينى پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(18,53,'18:53',' گۇناھكارلار دوزاخنى كۆرۈپ، ئۇنىڭغا كىرىدىغانلىقىغا جەزم قىلىدۇ، دوزاختىن قاچىدىغان جاي تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(18,54,'18:54',' بۇ قۇرئاندا ئىنسانلارغا تۈرلۈك مىساللارنى بايان قىلدۇق. ئىنسان كۆپ دەتالاش قىلىدۇ (يەنى كۆپ جېدەللىشىش ئىنساننىڭ تەبىئىتىدۇر).');
INSERT INTO "saleh" VALUES(18,55,'18:55','ئىنسانلارغا ھىدايەت كەلگەن چاغدا، ئۇلارنىڭ ئىمان ئېيتماسلىقلىرى ۋە پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلماسلىقلىرى پەقەت (ئاللاھنىڭ) بۇرۇنقىلارغا تۇتقان يولىنى (يەنى ھالاك قىلىشنى كۈتىدىغانلىقلىرى) ياكى ئۆزلىرىگە كېلىدىغان ئازابنى كۆزلىرى بىلەن كۆرۈشنى (كۈتىدىغانلىقلىرى) ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(18,56,'18:56',' بىز پەيغەمبەرلەرنى پەقەت (ئىمان ئېيتقۇچىلارغا) خوشخەۋەر بەرگۈچى، (ئاسىيلىق قىلغۇچىلارنى) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتىمىز. كاپىرلار (پەيغەمبەرلەر ئېلىپ كەلگەن) ھەقىقەتنى يوققا چىقىرىش ئۈچۈن بىھۇدە سۆزلەرنى قىلىپ مۇنازىرىلىشىدۇ، ئۇلار مېنىڭ ئايەتلىرىمنى ۋە ئۇلارغا بېرىلگەن ئاگاھلاندۇرۇشلارنى مەسخىرە (يەنى ئويۇنچۇق) قىلىۋالدى.');
INSERT INTO "saleh" VALUES(18,57,'18:57',' پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن پەند ـ نەسىھەت قىلىنغان، ئۇ ئايەتلەردىن يۈز ئۆرۈگەن ۋە ئۆزىنىڭ قىلغان گۇناھلىرىنى ئۇنتۇغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ شۈبھىسىزكى، بىز ئۇلارنىڭ دىللىرىغا قۇرئاننى چۈشىنىشكە توسقۇنلۇق قىلىدىغان پەردىلەرنى تارتتۇق، قۇلاقلىرىنى ئېغىر قىلدۇق، ئەگەر ئۇلارنى ھىدايەتكە دەۋەت قىلساڭ، ئۇلار ھەرگىز ھىدايەت تاپمايدۇ (يەنى ئۇلارنى ئىمانغا، قۇرئانغا دەۋەت قىلساڭ، ئۇلار دەۋىتىڭنى ھەرگىز قوبۇل قىلمايدۇ).');
INSERT INTO "saleh" VALUES(18,58,'18:58',' پەرۋەردىگارىڭ ناھايىتى مەغپىرەت قىلغۇچىدۇر ۋە رەھىم قىلغۇچىدۇر. ئەگەر ئاللاھ ئۇلارنى قىلمىشىغا قاراپ جازالايدىغان بولسا، ئەلۋەتتە ئۇلارغا قىلىنىدىغان ئازابنى تېزلەتكەن بولاتتى، لېكىن ئۇلارغا بەلگىلەنگەن بىر ۋاقىت بار، (ئۇ ۋاقىت كەلگەندە) ئۇلار ئاللاھتىن بۆلەك ھېچ پاناھگاھ تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(18,59,'18:59','شۇ شەھەرلەرنىڭ ئاھالىلىرى (يەنى ھۇد، سالىھ، لۇت، شۇئەيب پەيغەمبەرلەرنىڭ قەۋملىرىگە ئوخشاش ئۆتكەنكى ئۈممەتلەر) زۇلۇم قىلغانلىقلىرى ئۈچۈن ئۇلارنى ھالاك قىلدۇق، ئۇلارنىڭ ھالاك بولۇشىغا مۇئەييەن ۋاقىتنى بەلگىلىدۇق.');
INSERT INTO "saleh" VALUES(18,60,'18:60','ئۆز ۋاقتىدا مۇسا ياش خادىمىغا (يەنى يۇشەئ ئىبنى نۇنغا): «ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يەتمىگۈچە مېڭىشىمنى توختاتمايمەن، (شۇ جايغا يەتكەنگە قەدەر) ئۇزاققىچە مېڭىۋېرىمەن» دېدى.');
INSERT INTO "saleh" VALUES(18,61,'18:61','ئۇلار ئىككى دەريانىڭ قوشۇلىدىغان جايىغا يېتىپ بارغاندا، بېلىقىنى ئۇنتۇپ قالدى، بېلىق دەرياغا يول ئېلىپ كىرىپ كەتتى.');
INSERT INTO "saleh" VALUES(18,62,'18:62','ئۇلار (ئىككى دەريانىڭ قوشۇلىدىغان جايىدىن) ئۆتكەندە، مۇسا ياش خادىمىغا: «ئەتىگەنلىك تامىقىمىزنى ئېلىپ كەلگىن، بۇ سەپىرىمىزدە ھەقىقەتەن چارچاپ كەتتۇق» دېدى.');
INSERT INTO "saleh" VALUES(18,63,'18:63','ياش خادىم: «بىز چوڭ تاش ئاستىدا ئارام ئېلىۋاتقاندا (يۈز بەرگەن ئىشتىن يەنى پىشۇرۇلغان بېلىقنىڭ تىرىلىپ، سېۋەتتىن سەكرەپ چىقىپ دەرياغا كىرىپ كەتكەنلىكىدىن ئىبارەت قىزىق ئىشتىن) خەۋىرىڭ بارمۇ؟ مەن راستلا بېلىقنى ئۇنتۇپتىمەن. بېلىقنىڭ (ئاجايىپ ۋەقەسى) نى ساڭا ئېيتىشنى پەقەت شەيتان ماڭا ئۇنتۇلدۇرۇپتۇ، بېلىق دەرياغا ئاجايىپ يول ئالغان ئىدى» دېدى.');
INSERT INTO "saleh" VALUES(18,64,'18:64',' مۇسا: «بىزنىڭ ئىزدەيدىغىنىمىز دەل مۇشۇ ئىدى» دېدى ـ دە، ئىككىسى كەلگەن ئىزى بويىچە كەينىگە يېنىشتى.');
INSERT INTO "saleh" VALUES(18,65,'18:65','ئۇ ئىككىسى (بېلىقنى ئۇنتۇغان چوڭ تاشنىڭ يېنىدا) بىزنىڭ بەندىلىرىمىزدىن چوڭ مەرھەمىتىمىزگە ئېرىشكەن ۋە بىزنىڭ ئۆزىمىزگە خاس ئىلمىمىزنى (يەنى ئىلمىي غەيبنى) بىز بىلدۈرگەن بىر بەندىنى (يەنى خىزىر ئەلەيھىسسالامنى) ئۇچراتتى.');
INSERT INTO "saleh" VALUES(18,66,'18:66','مۇسا ئۇنىڭغا: «ئاللاھ ساڭا بىلدۈرگەن توغرا ئىلىمنى ماڭا ئۆگىتىشىڭ ئۈچۈن ساڭا ئەگىشەيمۇ؟ (يەنى سۆھبىتىڭدە بولايمۇ؟)» دېدى.');
INSERT INTO "saleh" VALUES(18,67,'18:67','ئۇ ئېيتتى: «سەن مەن بىلەن بىللە بولۇشقا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن. ');
INSERT INTO "saleh" VALUES(18,68,'18:68','سەن چوڭقۇر تونۇپ يەتمىگەن (يەنى قارىماققا يامان، ئەمما ماھىيىتىنى سەن چۈشەنمىگەن) ئىشقا قانداقمۇ سەۋر ـ تاقەت قىلىپ تۇرالايسەن؟».');
INSERT INTO "saleh" VALUES(18,69,'18:69','مۇسا ئېيتتى: «ئاللاھ خالىسا مېنى سەۋر قىلغۇچى كۆرىسەن، سېنىڭ بۇيرۇقۇڭغا خىلاپلىق قىلمايمەن».');
INSERT INTO "saleh" VALUES(18,70,'18:70','(خىزىر) ئېيتتى: «ئەگەر سەن ماڭا ئەگەشسەڭ، (يولۇققان ئىش توغرىسىدا) مەن ئۇنى ساڭا سۆزلەپ بەرگۈچە ئۇنىڭدىن سورىما (يەنى ئۆزۈم سۆزلەپ بەرمىگۈچە قىلغان ئىشلىرىمدىن سورىما)».');
INSERT INTO "saleh" VALUES(18,71,'18:71','شۇنىڭ بىلەن ئۇ ئىككىسى بىرلىكتە مېڭىپ كېلىپ بىر كېمىگە چىقىشتى، (خىزىر) كېمىنى تېشىۋەتتى (يەنى كېمىنىڭ بىر تاختىسىنى سۇغۇرۇۋەتتى)، مۇسا (ئۇنىڭغا): «كېمىدىكىلەرنى غەرق بولۇپ كەتسۇن، دەپ كېمىنى تەشتىڭمۇ؟ سەن ھەقىقەتەن (قورقۇنچلۇق) چوڭ بىر ئىشنى قىلدىڭ» دېدى.');
INSERT INTO "saleh" VALUES(18,72,'18:72','(خىزىر) سەن ھەقىقەتەن مەن بىلەن بىللە بولۇشتا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن، دېمىدىممۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(18,73,'18:73','مۇسا ئېيتتى: «ئەھدىنى ئۇنتۇپ قالغانلىقىمغا مېنى ئەيىبلىمە، سەن بىلەن بىللە بولۇشتا مېنى قىيىن ھالغا چۈشۈرۈپ قويما».');
INSERT INTO "saleh" VALUES(18,74,'18:74','ئۇ ئىككىسى يەنە بىرلىكتە ماڭدى، ئۇلار بىر بالىنى ئۇچراتتى، (خىزىر) ئۇنى ئۆلتۈرۈپ قويدى. مۇسا: «سەن بىر بىگۇناھ جاننى ناھەق ئۆلتۈردۈڭ، سەن ھەقىقەتەن ياۋۇز ئىشنى قىلدىڭ». ');
INSERT INTO "saleh" VALUES(18,75,'18:75',' (خىزىر) ئېيتتى: «ساڭا مەن ھەقىقەتەن مەن بىلەن بىللە بولۇشقا سەۋر ـ تاقەت قىلىپ تۇرالمايسەن، دېمىدىممۇ؟ »');
INSERT INTO "saleh" VALUES(18,76,'18:76',' مۇسا ئېيتتى: «بۇنىڭدىن كېيىن يەنە سەندىن بىرەر ئىش توغرۇلۇق سورىسام (يەنى قىلغان ئىشىڭغا ئېتىراز بىلدۈرسەم)، مېنى ئۆزۈڭگە ھەمراھ قىلمىغىن، (ساڭا بەرگەن ۋەدەمگە ئۈچ قېتىم خىلاپلىق قىلغانلىقىم ئۈچۈن مېنىڭ ئالدىمدا (ماڭا ھەمراھ بولماسلىقتا) مەزۇرسەن».');
INSERT INTO "saleh" VALUES(18,77,'18:77','ئۇ ئىككىسى يەنە بىللە مېڭىپ بىر شەھەرگە يېتىپ كەلدى، شەھەر ئاھالىسىدىن تاماق سورىدى، شەھەر ئاھالىسى ئۇلارنى مېھمان قىلىشتىن باش تارتتى، ئۇلار بۇ شەھەردە ئۆرۈلۈپ چۈشەي دەپ قالغان بىر تامنى ئۇچراتتى، تۈزلەپ قويدى. مۇسا ئېيتتى: «ئەگەر خالىساڭ بۇ ئىش ئۈچۈن ئەلۋەتتە ئىش ھەققى ئالغان بولاتتىڭ».');
INSERT INTO "saleh" VALUES(18,78,'18:78','(خىزىر) ئېيتتى: «بۇ، سەن بىلەن مېنىڭ ئايرىلىش (ۋاقتىمىز)، سەن سەۋر ـ تاقەت قىلىپ تۇرالمىغان (يۇقىرىقى ئۈچ ئىشنىڭ) ھەقىقىتىنى ساڭا چۈشەندۈرۈپ بېرەي. ');
INSERT INTO "saleh" VALUES(18,79,'18:79','كېمىگە كەلسەك، ئۇ دېڭىزدا ئىشلەيدىغان بىرقانچە نەپەر كەمبەغەلنىڭ كېمىسى ئىدى، مەن ئۇنى تېشىپ ئەيبناك قىلماقچى بولدۇم، (چۈنكى) ئۇلارنىڭ ئالدىدا (يەنى بارىدىغان يوللىرىدا) ھەرقانداق ساق كېمىنى ئىگىسىدىن (زورلۇق بىلەن) تارتىۋالىدىغان بىر پادىشاھ بار ئىدى.');
INSERT INTO "saleh" VALUES(18,80,'18:80',' (ئۆلتۈرۈلگەن) بالىغا كەلسەك، ئۇنىڭ ئاتا ـ ئانىسى مۆمىن ئىدى. ئۇنىڭ گۇمراھلىق بىلەن كۇفرىنى ئاتا ـ ئانىسىغا تېڭىشىدىن قورقتۇق.');
INSERT INTO "saleh" VALUES(18,81,'18:81','شۇنىڭ ئۈچۈن بىز ئۇلارنىڭ پەرۋەردىگارىنىڭ ئۇلارغا ئۇ بالىغا قارىغاندا پاك ۋە كۆيۈملۈك پەرزەنت ئاتا قىلىشىنى ئىرادە قىلدۇق.');
INSERT INTO "saleh" VALUES(18,82,'18:82','تامغا كەلسەك، ئۇ شەھەردىكى ئىككى يېتىم بالىنىڭ ئىدى، تام ئاستىدا ئۇلارنىڭ (ھەققى بولغان) خەزىنىسى بار ئىدى (تام ئۆرۈلۈپ كەتسە، خەزىنىسى ئېچىلىپ قېلىپ كىشىلەر ئۇنى ئېلىپ كېتەتتى)، ئۇلارنىڭ ئاتا ـ ئانىسى ياخشى ئادەم ئىدى. پەرۋەردىگارىڭ ئۇلارنىڭ چوڭ بولغاندا تام ئاستىدىكى خەزىنىسىنى چىقىرىۋېلىشىنى ئىرادە قىلدى، بۇ پەرۋەردىگارىڭنىڭ (ئۇلارغا قىلغان) رەھمىتىدۇر، ئۇنى (يەنى يۇقىرىقى ئۈچ ئىشنى) مەن ئۆز خاھىشىم بويىچە قىلغىنىم يوق (بەلكى ئۇنى ئاللاھنىڭ ئەمرى ۋە ئىلھامى بىلەن قىلدىم)، سەن سەۋر ـ تاقەت قىلىپ تۇرالمىغان نەرسىلەرنىڭ ھەقىقىتى ئەنە شۇ».');
INSERT INTO "saleh" VALUES(18,83,'18:83','ئۇلار (يەنى يەھۇدىيلار) سەندىن زۇلقەرنەين توغرۇلۇق سورىشىدۇ. «سىلەرگە مەن ئۇنىڭ قىسسەسىنى (ۋەھىي ئاساسىدا) بايان قىلىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(18,84,'18:84','ئۇنىڭغا بىز زېمىندا ھەقىقەتەن كۈچ ـ قۇدرەت ئاتا قىلدۇق، ئۇنىڭغا ئۆز مەقسىتىگە يېتىشنىڭ پۈتۈن يوللىرىنى ئاسانلاشتۇرۇپ بەردۇق.');
INSERT INTO "saleh" VALUES(18,85,'18:85','زۇلقەرنەين (ئاللاھ ئۇنىڭغا ئاسانلاشتۇرۇپ بەرگەن) يولغا ماڭدى.');
INSERT INTO "saleh" VALUES(18,86,'18:86',' ئۇ كۈنپېتىش تەرەپكە يېتىپ بارغاندا، كۈننىڭ قارا لايلىق بۇلاققا پېتىپ كېتىۋاتقانلىقىنى ھېس قىلدى. ئۇ بۇلاقنىڭ يېنىدا بىر تۈرلۈك قەۋمنى ئۇچراتتى. بىز: «ئى زۇلقەرنەين! سەن ئۇلارنى جازالىغىن ياكى ئۇلارغا ياخشى مۇئامىلىدە بولغىن» دېدۇق.');
INSERT INTO "saleh" VALUES(18,87,'18:87',' زۇلقەرنەين ئېيتتى: «كىمكى زۇلۇم قىلغان بولسا، بىز ئۇنى جازالايمىز، ئاندىن ئۇ پەرۋەردىگارىنىڭ دەرگاھىغا قايتۇرۇلىدۇ، پەرۋەردىگارى ئۇنى قاتتىق جازالايدۇ.');
INSERT INTO "saleh" VALUES(18,88,'18:88',' كىمكى ئىمان ئېيتسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ئەڭ ياخشى مۇكاپات بېرىلىدۇ، ئۇنى قولاي ئىشقا بۇيرۇيمىز».');
INSERT INTO "saleh" VALUES(18,89,'18:89','ئاندىن زۇلقەرنەين (ئاللاھ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى.');
INSERT INTO "saleh" VALUES(18,90,'18:90',' زۇلقەرنەين كۈنچىقىش تەرەپكە بارغاندا، كۈن نۇرىنىڭ شۇنداق بىر قەۋمنىڭ ئۈستىگە چۈشۈۋاتقانلىقىنى كۆردىكى، ئۇ قەۋمگە بىز (كۈننىڭ ھارارىتىدىن) ساقلىنىدىغان (كىيىم، ئىمارەت قاتارلىق) نەرسىلەرنى ئاتا قىلمىدۇق.');
INSERT INTO "saleh" VALUES(18,91,'18:91',' ئەھۋال شۇنداق بولدى (يەنى زۇلقەرنەين كۈنچىقىش تەرەپتىكى كىشىلەرگىمۇ كۈنپېتىش تەرەپتىكى كىشىلەرگە قىلغان مۇئامىلىنى قىلدى). بىز ئۇنىڭ قولىدىكى نەرسىلەردىن تولۇق خەۋەردار ئىدۇق. ');
INSERT INTO "saleh" VALUES(18,92,'18:92','ئاندىن ئۇ (ئاللاھ ئۇنىڭغا قولايلىق قىلىپ بەرگەن) يولغا ماڭدى.');
INSERT INTO "saleh" VALUES(18,93,'18:93','زۇلقەرنەين ئىككى تاغ ئارىسىغا يېتىپ بارغاندا، ئىككى تاغ ئالدىدا ھېچقانداق سۆزنى چۈشەنمەيدىغان (يەنى ئۆزىنىڭ تىلىدىن غەيرى تىلنى بىلمەيدىغان) بىر قەۋمنى ئۇچراتتى. ');
INSERT INTO "saleh" VALUES(18,94,'18:94','ئۇلار: «ئى زۇلقەرنەين! يەجۇج بىلەن مەجۇج يەر يۈزىدە ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلاردۇر، بىز بىلەن ئۇلارنىڭ ئارىسىغا بىر توسما سېلىپ بېرىشىڭ ئۈچۈن، ساڭا بىر قىسىم مېلىمىزنى (باجغا ئوخشاش) تۆلىسەك بولامدۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(18,95,'18:95','زۇلقەرنەين ئېيتتى: «ئاللاھنىڭ ماڭا بەرگەن نەرسىلىرى (يەنى كۈچ ـ قۇۋۋەت ۋە پادىشاھلىق سىلەرنىڭ ماڭا بېرىدىغان مېلىڭلاردىن) ئارتۇقتۇر، ماڭا ئادەم كۈچى ياردەم قىلىڭلار، ئۇلار بىلەن سىلەرنىڭ ئاراڭلارغا مۇستەھكەم بىر توسما سېلىپ بېرەي.');
INSERT INTO "saleh" VALUES(18,96,'18:96','ماڭا تۆمۈر پارچىلىرىنى ئېلىپ كېلىڭلار، (تۆمۈر پارچىلىرى دۆۋىلىنىپ) ئىككى تاغنىڭ ئارىسى تەكشى بولغاندا، كۆرۈكلەرنى بېسىڭلار». تۆمۈر پارچىلىرى (قىزىپ) ئوتتەك بولغاندا، ئۇ: «ماڭا (ئېرىتىلگەن مىسنى) بېرىڭلار، ئۈستىگە تۆكەي» دېدى.');
INSERT INTO "saleh" VALUES(18,97,'18:97',' ئۇلار (يەنى يەجۇج ـ مەجۇج) ئۇنىڭ ئۈستىگە چىقىشقىمۇ، ئۇنى تېشىشكىمۇ قادىر بولالمىدى.');
INSERT INTO "saleh" VALUES(18,98,'18:98',' زۇلقەرنەين ئېيتتى: «بۇ (يەنى توسمىنىڭ بەرپا قىلىنىشى) پەرۋەردىگارىمنىڭ نېمىتىدۇر، پەرۋەردىگارىمنىڭ ۋەدىسى ئىشقا ئاشقاندا، ئۇ ئۇنى تۈپتۈز قىلىۋېتىدۇ، پەرۋەردىگارىمنىڭ ۋەدىسى ھەقتۇر».');
INSERT INTO "saleh" VALUES(18,99,'18:99',' ئۇ كۈندە (يەنى قىيامەت كۈنىدە، قىستاڭچىلىقتىن) كىشىلەرنى بىر ـ بىرىگە گىرەلەشتۈرۈۋېتىمىز، سۇر چېلىنىدۇ، ئۇلارنى (ھېساب ئۈچۈن) يېغىمىز.');
INSERT INTO "saleh" VALUES(18,100,'18:100','ئۇ كۈندە كاپىرلارغا جەھەننەمنى ئاشكارا كۆرسىتىمىز.');
INSERT INTO "saleh" VALUES(18,101,'18:101','ئۇلارنىڭ (بۇ دۇنيادىكى چاغلىرىدا دىللىرىنىڭ) كۆزلىرى مېنى ياد قىلىشتىن پەردىلەنگەن ئىدى، (ئۇلارنىڭ دىللىرىنى زۇلمەت باسقانلىقى ئۈچۈن كالامۇللاھنى) ئاڭلاشقا قادىر ئەمەس ئىدى. ');
INSERT INTO "saleh" VALUES(18,102,'18:102',' كاپىرلار مېنى قويۇپ بەندىلىرىمنى مەبۇد قىلىۋېلىشسا بولىدۇ دەپ ئويلامدۇ؟ بىز جەھەننەمنى كاپىرلارغا ھەقىقەتەن مەنزىلگاھ قىلىپ تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(18,103,'18:103','سىلەرگە ئەمەللىرى جەھەتتىن ئەڭ زىيان تارتقۇچىلارنى ئېيتىپ بېرەيلىمۇ؟ ');
INSERT INTO "saleh" VALUES(18,104,'18:104','ئۇلار ھاياتى دۇنيادا قىلغان ئەمەللىرى يوققا چىققان، ئەمما ئۆزلىرى ئوبدان ئىش قىلدۇق دەپ ئويلىغان ئادەملەردۇر.');
INSERT INTO "saleh" VALUES(18,105,'18:105','ئەنە شۇلار پەرۋەردىگارىنىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى ئىنكار قىلغان، نەتىجىدە قىلغان ئەمەللىرى بىكار بولۇپ كەتكەن كىشىلەردۇر، قىيامەت كۈنى ئۇلارنى (يەنى مۇنداق كىشىلەرنى) قىلچە ئېتىبارغا ئالمايمىز. ');
INSERT INTO "saleh" VALUES(18,106,'18:106',' كاپىر بولغانلىقلىرى، مېنىڭ ئايەتلىرىمنى ۋە پەيغەمبەرلىرىمنى مەسخىرە قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ جازاسى جەھەننەمدۇر.');
INSERT INTO "saleh" VALUES(18,107,'18:107','شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ مەنزىلگاھى فىردەۋس جەننەتلىرى بولىدۇ. ');
INSERT INTO "saleh" VALUES(18,108,'18:108',' ئۇلار فىردەۋس جەننەتلىرىدە مەڭگۈ قالىدۇ، ئۇنىڭدىن يۆتكىلىشنى خالىمايدۇ.');
INSERT INTO "saleh" VALUES(18,109,'18:109',' ئېيتقىنكى، «پەرۋەردىگارىمنىڭ سۆزلىرىنى يېزىش ئۈچۈن، ئەگەر دېڭىز (سۈيى) سىياھ بولۇپ كەتسە، پەرۋەردىگارىمنىڭ سۆزلىرى تۈگىمەي تۇرۇپ، دېڭىز (سۈيى) چوقۇم تۈگەپ كەتكەن بولاتتى، مۇبادا يەنە شۇنچىلىك دېڭىز (سۈيىنى) كەلتۈرسەكمۇ».');
INSERT INTO "saleh" VALUES(18,110,'18:110','(ئى مۇھەممەد‍!) ئېيتقىنكى، مەن پەقەت سىلەرگە ئوخشاش بىر ئىنسانمەن. ماڭا ۋەھىي قىلىنىدۇكى، سىلەرنىڭ ئىلاھىڭلار يالغۇز بىر ئىلاھتۇر. كىمكى، پەرۋەردىگارىغا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن (ساۋابىنى ئۈمىد قىلىپ ئازابىدىن قورقىدىكەن)، ياخشى ئىش قىلسۇن. پەرۋەردىگارىغا قىلىدىغان ئىبادەتكە ھېچكىمنى شېرىك كەلتۈرمىسۇن».');
INSERT INTO "saleh" VALUES(19,1,'19:1','كاف، ھا، يا، ئەين، ساد.');
INSERT INTO "saleh" VALUES(19,2,'19:2','بۇ، پەرۋەردىگارىڭنىڭ بەندىسى زەكەرىياغا قىلغان مەرھەمىتىنىڭ بايانىدۇر.');
INSERT INTO "saleh" VALUES(19,3,'19:3','ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا پەس ئاۋازدا مۇناجات قىلدى.');
INSERT INTO "saleh" VALUES(19,4,'19:4','ئۇ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن كۈچ ـ قۇۋۋىتىمدىن كەتتىم، چېچىم ئاقاردى. پەرۋەردىگارىم! ساڭا دۇئا قىلىپ ئۈمىدسىز بولۇپ قالغىنىم يوق.');
INSERT INTO "saleh" VALUES(19,5,'19:5','مەن تاغىلىرىمنىڭ بالىلىرىنىڭ ئورنۇمنى باسالماسلىقلىرىدىن (يەنى دىننى قولدىن بېرىپ قويۇشلىرىدىن) ھەقىقەتەن ئەنسىرەيمەن، مېنىڭ ئايالىم تۇغماستۇر، (پەزلىڭدىن) ماڭا بىر (ياخشى) پەرزەنت ئاتا قىلغىن.');
INSERT INTO "saleh" VALUES(19,6,'19:6','ئۇ ماڭا ۋارىس بولغاي ۋە يەئقۇب ئەۋلادىغا ۋارىس بولغاي، پەرۋەردىگارىم! ئۇنى سېنىڭ رازىلىقىڭغا ئېرىشىدىغان قىلغىن».');
INSERT INTO "saleh" VALUES(19,7,'19:7','(ئاللاھ ئېيتتىكى) «ئى زەكەرىيا! بىز ھەقىقەتەن ساڭا (پەرىشتىلەر ئارقىلىق) يەھيا ئىسىملىك بىر ئوغۇل بىلەن خۇشخەۋەر بېرىمىز، ئىلگىرى ھېچ ئادەمنى ئۇنىڭغا ئىسىمداش قىلمىدۇق». ');
INSERT INTO "saleh" VALUES(19,8,'19:8',' زەكەرىيا ئېيتتى: «ئايالىم تۇغماس تۇرسا، مەن قېرىپ مۈكچىيىپ كەتكەن تۇرسام، مېنىڭ قانداقمۇ ئوغلۇم بولسۇن؟».');
INSERT INTO "saleh" VALUES(19,9,'19:9',' (نىدا قىلغان پەرىشتە) ئېيتتى: «ئىش ئەنە شۇنداقتۇر، پەرۋەردىگارىڭ ئېيتتى: بۇ مەن ئۈچۈن ئاساندۇر، سېنى مەن ياراتتىم، ھالبۇكى سەن بۇرۇن يوق ئىدىڭ (يەنى سېنى يوقتىن بار قىلغاندەك، يەھيانى ئىككىڭلاردىن تۆرەلدۈرۈشكە قادىرمەن)».');
INSERT INTO "saleh" VALUES(19,10,'19:10','زەكەرىيا ئېيتتى: «پەرۋەردىگارىم! (ئايالىمنىڭ ھامىلدار بولغانلىقىغا) ماڭا بىر ئالامەت قىلىپ بەرسەڭ». ئاللاھ ئېيتتى: «سېنىڭ ئالامىتىڭ (شۇكى)، ساق تۇرۇپ ئۈچ كېچە ـ كۈندۈز كىشىلەرگە سۆز قىلالمايسەن». ');
INSERT INTO "saleh" VALUES(19,11,'19:11','زەكەرىيا نامازگاھىدىن قەۋمىنىڭ ئالدىغا چىقىپ، ئۇلارنى ئەتىگەن ـ ئاخشامدا تەسبىھ ئېيتىشقا ئىشارەت قىلدى.');
INSERT INTO "saleh" VALUES(19,12,'19:12',' (يەھيا تۇغۇلۇپ چوڭ بولغاندا ئۇنىڭغا) «ئى يەھيا! كىتابنى (يەنى تەۋراتنى) تىرىشىپ ئوقۇغىن» (دېدۇق). ئۇنىڭغا بالىلىق چېغىدىلا ئەقىل ـ پاراسەت ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(19,13,'19:13',' بىز ئۇنىڭغا مەرھەمەت قىلىش ۋە ئۇنى (ناچار خىسلەتلەردىن) پاك قىلىش يۈزىسىدىن (شۇنداق قىلدۇق)، يەھيا تەقۋادار ئىدى.');
INSERT INTO "saleh" VALUES(19,14,'19:14','ئۇنى ئاتا ـ ئانىسىغا كۆيۈمچان (قىلدۇق)، ئۇ مۇتەكەببىر (يەنى ئاتا ـ ئانىسىنى قاخشاتقۇچى)، (پەرۋەردىگارىغا) ئاسىيلىق قىلغۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(19,15,'19:15','ئۇ تۇغۇلغان كۈنىدە، ۋاپات بولغان كۈنىدە، تىرىلىپ (قەبرىدىن) تۇرغۇزۇلغان كۈنىدە (ئاللاھتائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(19,16,'19:16','(ئۇلارغا) قۇرئاندا مەريەم (قىسسەسى) نى بايان قىلغىن، ئەينى زاماندا ئۇ ئائىلىسىدىن ئايرىلىپ شەرق تەرەپتىكى بىر جايغا باردى.');
INSERT INTO "saleh" VALUES(19,17,'19:17',' ئۇ پەردە تارتىپ كىشىلەردىن يوشۇرۇندى، ئۇنىڭغا بىزنىڭ روھىمىزنى (يەنى جىبرىئىل ئەلەيھىسسالامنى) ئەۋەتتۇق، ئۇ مەريەمگە بېجىرىم ئادەم سۈرىتىدە كۆرۈندى. ');
INSERT INTO "saleh" VALUES(19,18,'19:18','مەريەم ئېيتتى: «مەن ھەقىقەتەن مەرھەمەتلىك ئاللاھقا سىغىنىپ سېنىڭ چېقىلىشىڭدىن پاناھ تىلەيمەن، ئەگەر سەن تەقۋادار بولساڭ (ماڭا چېقىلمىغىن)».');
INSERT INTO "saleh" VALUES(19,19,'19:19','جىبرىئىل ئېيتتى: «مەن ساڭا بىر پاك ئوغۇل بېرىش ئۈچۈن ئەۋەتىلگەن پەرۋەردىگارىڭنىڭ ئەلچىسىمەن».');
INSERT INTO "saleh" VALUES(19,20,'19:20','مەريەم ئېيتتى: «ماڭا كىشى يېقىنلاشمىغان تۇرسا، مەن پاھىشە قىلمىغان تۇرسام، قانداقمۇ مېنىڭ ئوغۇل بالام بولسۇن؟».');
INSERT INTO "saleh" VALUES(19,21,'19:21',' جىبرىئىل ئېيتتى: «ئەمەلىيەت سەن دېگەندەكتۇر، (لېكىن پەرۋەردىگارىڭ ئېيتتى: ئۇ ماڭا ئاساندۇر. ئۇنى كىشىلەرگە قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل ۋە بىز تەرەپتىن بولغان مەرھەمەت قىلدۇق، بۇ تەقدىر قىلىنىپ بولغان ئىشتۇر».');
INSERT INTO "saleh" VALUES(19,22,'19:22','مەريەم ھامىلدار بولدى، (ئائىلىسىدىن) يىراق بىر جايغا كەتتى. ');
INSERT INTO "saleh" VALUES(19,23,'19:23','تولغاقنىڭ قاتتىقلىقى ئۇنى (يۆلىنىۋېلىش ئۈچۈن) بىر خورما دەرىخىنىڭ يېنىغا كېلىۋېلىشقا مەجبۇر قىلدى. مەريەم ئېيتتى: «كاشكى مەن بۇنىڭدىن ئىلگىرى ئۆلۈپ كەتكەن بولسامچۇ! كىشىلەر تەرىپىدىن ئۇنتۇلۇپ كەتكەن (ئەرزىمەس) نەرسىگە ئايلىنىپ كەتسەمچۇ!».');
INSERT INTO "saleh" VALUES(19,24,'19:24',' جىبرىئىل ئۇنىڭغا خورما دەرىخىنىڭ ئاستىدىن نىدا قىلدى: «غەم قىلمىغىن، پەرۋەردىگارىڭ سېنىڭ ئاستىڭدىن (ئېقىپ تۇرىدىغان) بىر ئېرىقنى پەيدا قىلدى.');
INSERT INTO "saleh" VALUES(19,25,'19:25',' خورما دەرىخىنى ئۆز تەرىپىڭگە ئىرغىتقىن، ئالدىڭغا پىشقان يېڭى خورمىلار تۆكۈلىدۇ. ');
INSERT INTO "saleh" VALUES(19,26,'19:26',' (بۇ شېرىن خورمىدىن) يېگىن، (بۇ تاتلىق سۇدىن) ئىچكىن ۋە خۇرسەن بولغىن، بىرەر ئادەمنى كۆرسەڭ (ئۇ بالا توغرۇلۇق سورىسا): مەن ھەقىقەتەن مەرھەمەتلىك ئاللاھقا ۋەدە بەردىم، بۈگۈن ھېچ ئادەمگە سۆز قىلمايمەن دېگىن».');
INSERT INTO "saleh" VALUES(19,27,'19:27','مەريەم بوۋاقنى (يەنى ئىيسا ئەلەيھىسسالامنى) كۆتۈرۈپ قەۋمىنىڭ ئالدىغا كەلدى، ئۇلار ئېيتتى: «ئى مەريەم! سەن ھەقىقەتەن غەلىتە ئىش قىلدىڭ.');
INSERT INTO "saleh" VALUES(19,28,'19:28',' ئى ھارۇننىڭ ھەمشىرىسى! سېنىڭ ئاتاڭ يامان ئادەم ئەمەس ئىدى، ئاناڭمۇ يولدىن چىققان خوتۇن ئەمەس ئىدى».');
INSERT INTO "saleh" VALUES(19,29,'19:29','مەريەم بوۋاقنى كۆرسەتتى، ئۇلار: «بۆشۈكتىكى بوۋاققا قانداق سۆزلەيمىز» دېدى.');
INSERT INTO "saleh" VALUES(19,30,'19:30','بوۋاق ئېيتتى: «مەن ھەقىقەتەن ئاللاھنىڭ بەندىسىمەن، ماڭا كىتابنى بەردى (يەنى ئىنجىلنى بېرىدۇ) ۋە مېنى پەيغەمبەر قىلدى (يەنى قىلىدۇ).');
INSERT INTO "saleh" VALUES(19,31,'19:31',' قەيەردە بولاي مېنى بەرىكەتلىك قىلدى، ھاياتلا بولىدىكەنمەن، ماڭا نامازنى، زاكاتنى ئادا قىلىشنى تەۋسىيە قىلدى.');
INSERT INTO "saleh" VALUES(19,32,'19:32',' مېنى ئانامغا كۆيۈمچان قىلدى، مېنى مۇتەكەببىر، شەقى قىلمىدى.');
INSERT INTO "saleh" VALUES(19,33,'19:33',' مەن تۇغۇلغان كۈنۈمدە، ۋاپات بولغان كۈنۈمدە، تىرىلىپ قەبرەمدىن تۇرغۇزۇلغان كۈنۈمدە (ئاللاھتائالا تەرىپىدىن بولغان) ئامانلىققا ئېرىشىمەن».');
INSERT INTO "saleh" VALUES(19,34,'19:34','ئەنە شۇ ئىيسا ئىبىن مەريەمنىڭ (قىسسەسىدۇر). ئىيسا ئۇلار دەتالاش قىلىۋاتقان ئۆزىنڭ ئىشىنىڭ (ھەقىقىتى) ئۈستىدە ھەق گەپنى قىلدى. ');
INSERT INTO "saleh" VALUES(19,35,'19:35','«ئاللاھنىڭ شەنىگە بالىسى بولۇش لايىق ئەمەس، ئاللاھ (بالىدىن، شېرىكتىن) پاكتۇر، ئاللاھ بىرەر ئىش قىلىشنى ئىرادە قىلسا، ئۇنىڭغا ۋۇجۇتقا كەل دەيدۇ ـ دە، ئۇ ۋۇجۇتقا كېلىدۇ.');
INSERT INTO "saleh" VALUES(19,36,'19:36','ئاللاھ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، شۇنىڭ ئۈچۈن ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر».');
INSERT INTO "saleh" VALUES(19,37,'19:37',' ئىيسانىڭ ئىشى توغرىسىدا ئۇلار (يەنى ئەھلى كىتاب) ئارىسىدىكى پىرقىلەر ئىختىلاپ قىلىشتى، بۈيۈك كۈن (يەنى قىيامەت كۈنى) كەلگەن چاغدا، كاپىرلارغا ۋاي!');
INSERT INTO "saleh" VALUES(19,38,'19:38','ئۇلار بىزنىڭ دەرگاھىمىزغا كەلگەن كۈندە (يەنى قىيامەت كۈنىدە) قۇلاقلىرى تازا ئاڭلايدىغان، كۆزلىرى تازا كۆرىدىغان بولىدۇ، لېكىن زالىملار بۈگۈن (يەنى بۇ دۇنيادا) روشەن گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(19,39,'19:39','ئۇلارنى (يەنى خالايىقنى) ھەسرەت كۈنى (يەنى قىيامەت كۈنى)دىن ئاگاھلاندۇرغىن، ئۇ چاغدا ھەممە ئىش بىر تەرەپ قىلىنغان بولىدۇ. ھالبۇكى، ئۇلار غەپلەتتىدۇر، (قىيامەتنى) ئىنكار قىلماقتىدۇر.');
INSERT INTO "saleh" VALUES(19,40,'19:40','زېمىنغا ۋە زېمىن ئۈستىدىكى بارچىگە چوقۇم بىز ۋارىسلىق قىلىمىز. ئۇلار (ھېساب بېرىش ئۈچۈن) بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(19,41,'19:41',' (ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىبراھىم (قىسسەسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن تولىمۇ راستچىل پەيغەمبەر ئىدى.');
INSERT INTO "saleh" VALUES(19,42,'19:42',' ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ئېيتتى: «ئى ئاتا! نېمىشقا ئاڭلىمايدىغان، كۆرمەيدىغان ۋە ساڭا ھېچ نەرسىگە ئەسقاتمايدىغان نەرسىلەرگە چوقۇنىسەن.');
INSERT INTO "saleh" VALUES(19,43,'19:43','ئى ئاتا! ماڭا سەن بىلمەيدىغان ئىلىملەر نازىل بولدى، ماڭا ئەگەشكىن، سېنى توغرا يولغا باشلايمەن.');
INSERT INTO "saleh" VALUES(19,44,'19:44','ئى ئاتا! شەيتانغا چوقۇنمىغىن، شەيتان مەرھەمەتلىك ئاللاھقا ھەقىقەتەن ئاسىيلىق قىلدى.');
INSERT INTO "saleh" VALUES(19,45,'19:45','ئى ئاتا! سېنىڭ مەرھەمەتلىك ئاللاھنىڭ ئازابىغا قېلىشىڭدىن، شەيتاننىڭ ھەمراھىغا ئايلىنىپ قېلىشىڭدىن قورقىمەن».');
INSERT INTO "saleh" VALUES(19,46,'19:46',' ئۇ (يەنى ئازەر) ئېيتتى: «ئى ئىبراھىم! سەن مېنىڭ ئىلاھلىرىمدىن يۈز ئۆرۈمەكچىمۇسەن، ئەگەر سەن (مۇنداق قىلىشتىن) يانمىساڭ، چوقۇم سېنى تاش ـ كېسەك قىلىپ ئۆلتۈرىمەن، ئۇزاق زامان مەندىن يىراق تۇرغىن».');
INSERT INTO "saleh" VALUES(19,47,'19:47',' ئىبراھىم ئېيتتى: «ساڭا ئامانلىق بولسۇن، پەرۋەردىگارىمدىن ساڭا مەغپىرەت تىلەيمەن، ئاللاھ ماڭا ھەقىقەتەن مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(19,48,'19:48',' سىلەردىن ۋە سىلەر ئاللاھنى قويۇپ چوقۇنۇۋاتقان نەرسىلەردىن يىراق بولىمەن، پەرۋەردىگارىمغا ئىبادەت قىلىمەن، پەرۋەردىگارىمنىڭ ئىبادىتىدىن مەھرۇم بولۇپ قالماسلىقنى ئۈمىد قىلىمەن».');
INSERT INTO "saleh" VALUES(19,49,'19:49',' ئۇ ئۇلاردىن ۋە ئۇلارنىڭ ئاللاھنى قويۇپ چوقۇنۇۋاتقان نەرسىلىرىدىن يىراقلاشتى، ئۇنىڭغا بىز ئىسھاق ۋە يەئقۇبنى ئاتا قىلدۇق، ئۇ ئىككىسىنىڭ ھەر بىرسىنى پەيغەمبەر قىلدۇق.');
INSERT INTO "saleh" VALUES(19,50,'19:50','ئۇلارغا بىزنىڭ رەھمىتىمىزنى ئاتا قىلدۇق، ئۇلارنىڭ نامىنى بەلەن ۋە يۇقىرى قىلدۇق.');
INSERT INTO "saleh" VALUES(19,51,'19:51',' كىتابتا (يەنى قۇرئاندا) مۇسا (قىسسەسى) نى بايان قىلغىن. شۈبھىسىزكى، ئۇ ئاللاھ ئىبادىتىگە خاس قىلغان ھەم رەسۇل (يەنى ئەلچى)، ھەم پەيغەمبەر ئىدى.');
INSERT INTO "saleh" VALUES(19,52,'19:52','ئۇنىڭغا بىز تۇر تېغىنىڭ ئوڭ تەرىپىدىن نىدا قىلدۇق، ئۇنى بىز مۇناجات ئۈچۈن (دەرگاھىمىزغا) يېقىنلاشتۇردۇق.');
INSERT INTO "saleh" VALUES(19,53,'19:53',' ئۇنىڭغا بىز مەرھەمەت قىلىپ قېرىندىشى ھارۇننى پەيغەمبەر قىلىپ بەردۇق.');
INSERT INTO "saleh" VALUES(19,54,'19:54',' (ئى مۇھەممەد!) كىتابتا (يەنى قۇرئاندا) ئىسمائىل (نىڭ قىسسەسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن ۋەدىسىدە راستچىل، ھەم رەسۇل، ھەم پەيغەمبەر ئىدى.');
INSERT INTO "saleh" VALUES(19,55,'19:55',' ئۇ ئائىلىسىدىكىلەرنى ناماز ئوقۇشقا، زاكات بېرىشكە بۇيرۇيتتى، پەرۋەردىگارىنىڭ دەرگاھىدا ئۇنىڭ رازىلىقىغا ئېرىشكەن ئىدى.');
INSERT INTO "saleh" VALUES(19,56,'19:56','كىتابتا (يەنى قۇرئاندا) ئىدرىس (قىسسەسى) نى بايان قىلغىن، ئۇ ھەقىقەتەن راستچىل پەيغەمبەر ئىدى.');
INSERT INTO "saleh" VALUES(19,57,'19:57','ئۇنى بىز يۇقىرى ئورۇنغا كۆتۈردۇق.');
INSERT INTO "saleh" VALUES(19,58,'19:58','ئاللاھنىڭ نېمىتىگە ئېرىشكەن ئەنە شۇ پەيغەمبەرلەر ئادەم (ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز نۇھ بىلەن بىللە كېمىگە سالغانلارنىڭ نەسلىدىندۇر، ئىبراھىمنىڭ نەسلىدىندۇر، ئىسرائىل (يەنى يەئقۇب ئەلەيھىسسالام) نىڭ نەسلىدىندۇر، بىز (ئىمانغا) ھىدايەت قىلغان، (پەيغەمبەرلىككە) تاللىغانلاردىندۇر، ئۇلارغا مەرھەمەتلىك ئاللاھنىڭ ئايەتلىرى تىلاۋەت قىلىنغان چاغدا سەجدە قىلغان ۋە يىغلىغان ھالدا (زېمىنغا) يىقىلىدۇ.');
INSERT INTO "saleh" VALUES(19,59,'19:59','ئۇلار كەتكەندىن كېيىن (ئۇلارنىڭ) ئورنىنى باسقان ئورۇنباسارلار نامازنى تەلەپكە لايىق ئادا قىلمىدى، نەپسى - خاھىشلىرىغا ئەگەشتى، ئۇلار (قىيامەتتە) گۇمراھلىقنىڭ جازاسىنى تارتىدۇ.');
INSERT INTO "saleh" VALUES(19,60,'19:60',' تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا، ئەنە شۇلارلا جەننەتكە كىرىدۇ، ئۇلارنىڭ (ئەمەللىرىنىڭ ساۋابىدىن) ھېچ نەرسە كېمەيتىلمەيدۇ. ');
INSERT INTO "saleh" VALUES(19,61,'19:61',' ئۇ دائىمىي تۇرالغۇ بولغان، مەرھەمەتلىك ئاللاھ بەندىلىرىگە ۋەدە قىلغان، ئۇلار كۆرمەي تۇرۇپ (ئىشەنگەن) جەننەتلەردۇر، ئاللاھنىڭ (جەننەت بىلەن قىلغان) ۋەدىسى چوقۇم ئىشقا ئاشىدۇ.');
INSERT INTO "saleh" VALUES(19,62,'19:62',' ئۇلار جەننەتتە بىھۇدە سۆز ئاڭلىمايدۇ، پەقەت (پەرىشتىلەرنىڭ ئۇلارغا بەرگەن) سالامىنىلا ئاڭلايدۇ، ئۇلار جەننەتتە ئەتىگەن ـ ئاخشامدا (كۆڭلى تارتقان يىمەك ـ ئىچمەكلەردىن) رىزىققا ئىگە بولۇپ تۇرىدۇ. ');
INSERT INTO "saleh" VALUES(19,63,'19:63','بەندىلىرىمىز ئىچىدىكى تەقۋادارلارغا بىز مىراس قىلىپ بېرىدىغان جەننەت ئەنە شۇ.');
INSERT INTO "saleh" VALUES(19,64,'19:64','بىز (يەنى پەرىشتىلەر دۇنياغا) پەرۋەردىگارىڭنىڭ ئەمرى بىلەن چۈشىمىز، بىزنىڭ ئالدىمىزدىكى، ئارقىمىزدىكى ۋە ئالدى ـ كەينىمىز ئارىسىدىكى ئىشلارنىڭ ھەممىسى ئاللاھنىڭ ئىلكىدىدۇر، پەرۋەردىگارىڭ (بەندىلىرىنىڭ ئەمەللىرىدىن ھېچ نەرسىنى) ئۇنتۇمايدۇ.');
INSERT INTO "saleh" VALUES(19,65,'19:65',' ئۇ ئاسمانلارنىڭ، زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، ئۇنىڭغا ئىبادەت قىلغىن، ئاللاھنىڭ ئىبادىتىگە چىداملىق بولغىن، سەن ئۇنىڭ شېرىكى بارلىقىنى بىلەمسەن؟.');
INSERT INTO "saleh" VALUES(19,66,'19:66',' ئىنسان (يەنى ئۆلگەندىن كېيىن تىرىلىشىگە ئېتىقاد قىلمايدىغان كاپىر): «مەن ئۆلگەندىن كېيىن چوقۇم تىرىلەمدىم؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(19,67,'19:67',' ئىنسان بىز ئۇنى ئىلگىرى يوقتىن بار قىلغانلىقىمىزنى ئەسلىمەمدۇ؟. ');
INSERT INTO "saleh" VALUES(19,68,'19:68',' پەرۋەردىگارىڭ بىلەن قەسەمكى، ئۇلارنى ئەلۋەتتە (ئۇلارنى ئازدۇرغان) شەيتانلار بىلەن قوشۇپ يىغىمىز، ئاندىن ئۇلارنى جەھەننەمنىڭ چۆرىسىدە تىزلاندۇرۇپ ئولتۇرغۇزغان ھالدا ئەلۋەتتە ھازىر قىلىمىز.');
INSERT INTO "saleh" VALUES(19,69,'19:69','ئاندىن ھەربىر گۇرۇھنىڭ ئىچىدىن مەرھەمەتلىك ئاللاھقا ئەڭ ئاسىي بولغانلىرىنى تارتىپ چىقىرىمىز.');
INSERT INTO "saleh" VALUES(19,70,'19:70','ئاندىن بىز دوزاخ ئازابىغا ئەڭ لايىق بولغانلارنى ئەلۋەتتە ئوبدان بىلىمىز. ');
INSERT INTO "saleh" VALUES(19,71,'19:71',' سىلەرنىڭ ئىچىڭلاردىن دوزاخقا بارمايدىغان بىرەر كىشىمۇ قالمايدۇ، بۇ پەرۋەردىگارىڭنىڭ ئۆزگەرمەس ھۆكمىدۇر. ');
INSERT INTO "saleh" VALUES(19,72,'19:72','ئاندىن تەقۋادارلارنى (جەھەننەمدىن) قۇتقۇزىمىز، زالىملارنى جەھەننەمدە تىزلىنىپ ئولتۇرغان ھالدا قويىمىز. ');
INSERT INTO "saleh" VALUES(19,73,'19:73','ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنغان چاغدا، كاپىرلار مۆمىنلەرگە: «ئىككى گۇرۇھتىن قايسىسىنىڭ ئورنى ياخشى، سورۇنى گۈزەل؟» دېدى.');
INSERT INTO "saleh" VALUES(19,74,'19:74',' بۇلاردىن بۇرۇن (بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار مەيلى مال ـ مۈلۈك جەھەتتىن بولسۇن، مەيلى تاشقى كۆرۈنۈش جەھەتتىن بولسۇن، بۇلاردىن ئارتۇق ئىدى.');
INSERT INTO "saleh" VALUES(19,75,'19:75',' (ئى مۇھەممەد!) ئېيتقىنكى، «كىمكى گۇمراھلىقتا بولىدىكەن، مەرھەمەتلىك ئاللاھ ئۇنىڭغا مۆھلەت بېرىدۇ، تاكى ئۇلار ئازاب ۋە قىيامەتتىن ئىبارەت ئاللاھ ئۇلارغا ۋەدە قىلغان نەرسىلەرنى كۆرگەندە ئاندىن كىمنىڭ ئورنى يامان ۋە كىمنىڭ لەشكىرى ئاجىز ئىكەنلىكىنى بىلىدۇ».');
INSERT INTO "saleh" VALUES(19,76,'19:76','ئاللاھ ھىدايەت تاپقۇچىلارنىڭ ھىدايىتىنى زىيادە قىلىدۇ، باقى قالىدىغان ياخشى ئەمەللەر ساۋاب جەھەتتىن ۋە ئاقىۋەت جەھەتتىن پەرۋەردىگارىڭنىڭ دەرگاھىدا ئارتۇقتۇر. ');
INSERT INTO "saleh" VALUES(19,77,'19:77',' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان ۋە ماڭا (ئاخىرەتتە) ئەلۋەتتە مال ـ مۈلۈك، ئەۋلاد بېرىلىدۇ دېگەن ئادەمنى كۆردۈڭمۇ؟ ');
INSERT INTO "saleh" VALUES(19,78,'19:78',' ئۇ غەيبنى بىلگەنمۇ ياكى مەرھەمەتلىك ئاللاھنىڭ ئەھدىسىگە ئېرىشكەنمۇ؟');
INSERT INTO "saleh" VALUES(19,79,'19:79','ھەرگىز ئۇنداق بولغان ئەمەس، ئۇنىڭ دېگەنلىرىنى خاتىرىلەپ قويىمىز، ئۇنىڭغا بېرىدىغان ئازابنى كۈچەيتىمىز.');
INSERT INTO "saleh" VALUES(19,80,'19:80','ئۇنىڭ دېگەنلىرىنى (يەنى مېلىنى ۋە ئەۋلادىنى) باشقىلارغا مىراس قىلىپ قالدۇرىمىز، ئۇ ئالدىمىزغا تەنھا كېلىدۇ.');
INSERT INTO "saleh" VALUES(19,81,'19:81','ئۇلار (يەنى مۇشرىكلار) ئىززەتكە ئېرىشىش ئۈچۈن (يەنى ئىلاھلىرىنىڭ شاپائىتى بىلەن ئاللاھنىڭ ئالدىدا ئىززەتلىك بولۇش ئۈچۈن)، ئاللاھنى قويۇپ ئىلاھلارغا چوقۇندى.');
INSERT INTO "saleh" VALUES(19,82,'19:82','ئىش ئۇلارنىڭ گۇمان قىلغىنىدەك ئەمەس (يەنى ئىلاھلىرى ئۇلارنىڭ ئاللاھ ئالدىدا ئىززەت تېپىشلىرىنىڭ سەۋەبى بولالمايدۇ)، (ئىلاھلار) ئۇلارنىڭ (يەنى مۇشرىكلارنىڭ) چوقۇنۇشىنى ئىنكار قىلىدۇ، ئۇلار (يەنى مۇشرىكلار) نىڭ دۈشمىنىگە ئايلىنىدۇ.');
INSERT INTO "saleh" VALUES(19,83,'19:83',' بىزنىڭ شەيتانلارنى كاپىرلارغا ئەۋەتكەنلىكىمىزنى بىلمەمسەن؟ شەيتانلار ئۇلارنى (گۇناھلارغا) قىزىقتۇرىدۇ.');
INSERT INTO "saleh" VALUES(19,84,'19:84','ئۇلارنىڭ ھالاك بولۇشىغا ئالدىراپ كەتمىگىن، (ئۇلارنىڭ كۈنى ئاز قالغان بولۇپ) ئۇنى بىز ساناپ تۇرىمىز.');
INSERT INTO "saleh" VALUES(19,85,'19:85',' شۇ كۈندە (يەنى قىيامەت كۈنىدە) تەقۋادارلارنى مەرھەمەتلىك پەرۋەردىگارنىڭ دەرگاھىغا ئىززەت ـ ئىكرام بىلەن يىغىمىز.');
INSERT INTO "saleh" VALUES(19,86,'19:86',' گۇناھكارلارنى ئۇلار چاڭقىغان ھالدا جەھەننەمگە ھەيدەيمىز. ');
INSERT INTO "saleh" VALUES(19,87,'19:87','مەرھەمەتلىك ئاللاھنىڭ ئەھدىگە ئېرىشكەنلەردىن باشقىلار شاپائەت قىلىشقا قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(19,88,'19:88','ئۇلار: «ئاللاھنىڭ بالىسى بار» دېيىشتى.');
INSERT INTO "saleh" VALUES(19,89,'19:89','سىلەر شەك ـ شۈبھىسىز قەبىھ بىر سۆزنى قىلدىڭلار.');
INSERT INTO "saleh" VALUES(19,90,'19:90','بۇ سۆزنىڭ (يامانلىقىدىن) ئاسمانلار پارچىلىنىپ كەتكىلى، يەر يېرىلىپ كەتكىلى، تاغلار گۇمران بولۇپ كەتكىلى تاس قالدى.');
INSERT INTO "saleh" VALUES(19,91,'19:91','بۇ شۇنىڭ ئۈچۈنكى، ئۇلار ئاللاھنىڭ بالىسى بار دەپ دەۋا قىلدى. ');
INSERT INTO "saleh" VALUES(19,92,'19:92','بالىسى بولۇش ئاللاھقا لايىق ئەمەستۇر.');
INSERT INTO "saleh" VALUES(19,93,'19:93',' ئاسمانلاردا ۋە زېمىندا ئاللاھقا بەندە بولۇپ بويسۇنمايدىغان ھېچ مەخلۇق يوقتۇر.');
INSERT INTO "saleh" VALUES(19,94,'19:94',' شەك ـ شۈبھىسىزكى، ئاللاھ ئۇلارنى تولۇق ئىگەللىدى ۋە سانىنى مۇكەممەل بىلدى.');
INSERT INTO "saleh" VALUES(19,95,'19:95','قىيامەت كۈنى ئۇلارنىڭ ھەممىسى ئاللاھنىڭ دەرگاھىغا تەنھا كېلىدۇ.');
INSERT INTO "saleh" VALUES(19,96,'19:96','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ئۈچۈن ئاللاھ ھەقىقەتەن (ئۇلارنىڭ دىللىرىدا) مۇھەببەت پەيدا قىلىدۇ.');
INSERT INTO "saleh" VALUES(19,97,'19:97','(ئى مۇھەممەد!) تەقۋادارلارغا خۇشخەۋەر بېرىشىڭ ۋە سەركەش قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، بىز قۇرئاننى سېنىڭ تىلىڭ بىلەن چۈشىنىشلىك قىلىپ نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(19,98,'19:98','ئۇلاردىن ئىلگىرى نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلاردىن بىرەر ئەھەدىنى كۆرەلەمسەن ياكى ئۇلاردىن بىرەر پەس ئاۋاز ئاڭلىيالامسەن؟ (يەنى ئۇلار شۇنداق تەلتۆكۈس ھالاك قىلىندىكى، ئۇلاردىن كۆرگىلى ۋە ئاۋازىنى ئاڭلىغىلى ھېچ ئەھەدى قالمىدى).');
INSERT INTO "saleh" VALUES(20,1,'20:1','تاھا (يەنى ئى مۇھەممەد!).');
INSERT INTO "saleh" VALUES(20,2,'20:2','قۇرئاننى ساڭا سېنى جاپاغا سېلىش ئۈچۈن ئەمەس،');
INSERT INTO "saleh" VALUES(20,3,'20:3','پەقەت (ئاللاھتىن) قورقىدىغانلارغا ۋەز ـ نەسىھەت قىلىش ئۈچۈن نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(20,4,'20:4','ئۇ زېمىننى ۋە (كەڭ) ئېگىز ئاسمانلارنى ياراتقان زات تەرىپىدىن نازىل قىلىنغان.');
INSERT INTO "saleh" VALUES(20,5,'20:5','مەرھەمەتلىك ئاللاھ ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى.');
INSERT INTO "saleh" VALUES(20,6,'20:6',' ئاسمانلاردىكى، زېمىندىكى، ئۇلارنىڭ ئارىسىدىكى ۋە يەر ئاستىدىكى شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، ئاللاھنىڭ مەخلۇقاتىدۇر، ئاللاھنىڭ تەسەررۇپى ئاستىدۇر).');
INSERT INTO "saleh" VALUES(20,7,'20:7','ئەگەر سەن ئاشكارا سۆزلىسەڭ (ياكى يوشۇرۇن سۆزلىسەڭ، ئاللاھنىڭ نەزىرىدە ئوخشاشتۇر)، چۈنكى ئۇ سىرنى ۋە ئۇنىڭدىنمۇ مەخپىيرەك ئىشلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(20,8,'20:8',' ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئاللاھنىڭ گۈزەل ئىسىملىرى بار. ');
INSERT INTO "saleh" VALUES(20,9,'20:9','مۇسانىڭ قىسسەسىدىن خەۋىرىڭ بارمۇ؟ ');
INSERT INTO "saleh" VALUES(20,10,'20:10','ئۆز ۋاقتىدا مۇسا (بىر جايدا) ئوتنىڭ يورۇقىنى كۆردى. ئۇ ئائىلىسىدىكىلەرگە: «تۇرۇپ تۇرۇڭلار، مەن ئوتنىڭ يورۇقىنى كۆردۈم، مەن بېرىپ (ئۇنىڭدىن) بىرەر پارچە چوغ ئېلىپ كېلىشىم ياكى ئوت بار يەردە بىرەر يول باشلىغۇچىنى ئۇچرىتىشىم مۇمكىن» دېدى.');
INSERT INTO "saleh" VALUES(20,11,'20:11',' مۇسا ئوتنىڭ يېنىغا كەلگەندە نىدا قىلىندىكى، ئى مۇسا!');
INSERT INTO "saleh" VALUES(20,12,'20:12','مەن ھەقىقەتەن سېنىڭ پەرۋەردىگارىڭدۇرمەن، كەشىڭنى سالغىن، سەن ھەقىقەتەن مۇقەددەس ۋادى تۇۋادىسەن.');
INSERT INTO "saleh" VALUES(20,13,'20:13','مەن سېنى (پەيغەمبەرلىككە) تاللىدىم، سەن ۋەھىيگە قۇلاق سالغىن.');
INSERT INTO "saleh" VALUES(20,14,'20:14','مەن ھەقىقەتەن (ئىبادەتكە لايىق) ئاللاھتۇرمەن، مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوق، (يالغۇز) ماڭا ئىبادەت قىلغىن، مېنى زىكىر قىلىش ئۈچۈن ناماز ئوقۇغىن.');
INSERT INTO "saleh" VALUES(20,15,'20:15',' قىيامەتنىڭ بولۇشى مۇھەققەقتۇر، ھەر ئادەم ئۆزىنىڭ ئەمەلىگە يارىشا مۇكاپاتلىنىشى ئۈچۈن، ئۇنى (يەنى ئۇنىڭ قاچان بولۇشىنى) مەخپىي تۇتتۇم.');
INSERT INTO "saleh" VALUES(20,16,'20:16','قىيامەتكە ئىشەنمەيدىغان ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەم سېنى ئۇنىڭدىن (يەنى قىيامەتكە تەييارلىق قىلىشتىن ۋە ئۇنى تەستىق قىلىشتىن) توسمىسۇن، ئۇنداقتا ئۆزۈڭ ھالاك بولىسەن (چۈنكى ئاخىرەتتىن غاپىل بولۇش، ھالاكەتكە ئېلىپ بارىدۇ ).');
INSERT INTO "saleh" VALUES(20,17,'20:17',' ئى مۇسا! ئوڭ قولۇڭدىكى نېمە ئۇ؟» ');
INSERT INTO "saleh" VALUES(20,18,'20:18','مۇسا ئېيتتى: «ئۇ مېنىڭ ھاسام، ئۇنىڭغا تايىنىمەن، ئۇنىڭ بىلەن قويلىرىمغا غازاڭ قېقىپ بېرىمەن، ئۇنىڭدا مېنىڭ يەنە باشقا ئېھتىياجلىرىممۇ بار».');
INSERT INTO "saleh" VALUES(20,19,'20:19','ئاللاھ: «ئى مۇسا! ئۇنى (يەرگە) تاشلىغىن» دېدى.');
INSERT INTO "saleh" VALUES(20,20,'20:20','مۇسا ئۇنى تاشلىۋىدى، ئۇ ناگاھان مېڭىۋاتقان بىر يىلانغا ئايلاندى.');
INSERT INTO "saleh" VALUES(20,21,'20:21',' ئاللاھ ئېيتتى: « ئۇنى تۇتۇۋالغىن، قورقمىغىن، ئۇنى دەسلەپكى (يەنى ھاسىلىق) ھالىتىگە قايتۇرىمىز.');
INSERT INTO "saleh" VALUES(20,22,'20:22',' قولۇڭنى قولتۇقۇڭنىڭ ئاستىغا سالغىن (ئاندىن چىقارغىن)، ئۇ ھېچقانداق ئەيىب ـ نۇقسانسىز ھالدا ئاپئاق (نۇرلۇق بولۇپ) چىقىدۇ، (بۇ) ئىككىنچى مۆجىزىدۇر.');
INSERT INTO "saleh" VALUES(20,23,'20:23',' ساڭا چوڭ مۆجىزىلىرىمىزدىن بەزىسىنى كۆرسىتىمىز.');
INSERT INTO "saleh" VALUES(20,24,'20:24','پىرئەۋنگە بارغىن، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى».');
INSERT INTO "saleh" VALUES(20,25,'20:25',' مۇسا ئېيتتى: «پەرۋەردىگارىم! مېنىڭ كۆڭۈل-كۆكسۈمنى كەڭ قىلغىن.');
INSERT INTO "saleh" VALUES(20,26,'20:26','مېنىڭ ئىشىمنى ئاسانلاشتۇرغىن.');
INSERT INTO "saleh" VALUES(20,27,'20:27','تىلىمدىن كېكەچلىكنى كۆتۈرۈۋەتكىن.');
INSERT INTO "saleh" VALUES(20,28,'20:28','ئۇلار مېنىڭ سۆزۇمنى چۈشەنسۇن.');
INSERT INTO "saleh" VALUES(20,29,'20:29','ماڭا ئائىلەمدىن قېرىندىشىم ھارۇننى ياردەمچى قىلىپ بەرگىن[30 ـ 29].');
INSERT INTO "saleh" VALUES(20,30,'20:30','ماڭا ئائىلەمدىن قېرىندىشىم ھارۇننى ياردەمچى قىلىپ بەرگىن[30 ـ 29].');
INSERT INTO "saleh" VALUES(20,31,'20:31',' ئۇنىڭ بىلەن مېنى تېخىمۇ كۈچەيتكىن. ');
INSERT INTO "saleh" VALUES(20,32,'20:32','مېنىڭ ئىشىمغا ئۇنى شېرىك قىلغىن.');
INSERT INTO "saleh" VALUES(20,33,'20:33','بۇنىڭ بىلەن بىز ساڭا كۆپ تەسبىھ ئېيتقايمىز.');
INSERT INTO "saleh" VALUES(20,34,'20:34','ساڭا كۆپ زىكرى ئېيتقايمىز.');
INSERT INTO "saleh" VALUES(20,35,'20:35',' سەن ھەقىقەتەن بىزنى كۆرۈپ تۇرغۇچىسەن».');
INSERT INTO "saleh" VALUES(20,36,'20:36','ئاللاھ ئېيتتى: «ئى مۇسا! سېنىڭ سورىغان نەرسىلىرىڭ ساڭا بېرىلدى .');
INSERT INTO "saleh" VALUES(20,37,'20:37','(بۇنىڭدىن باشقا) ساڭا يەنە بىر قېتىم ئىنئام قىلغان ئىدۇق.');
INSERT INTO "saleh" VALUES(20,38,'20:38','ئۆز ۋاقتىدا ئاناڭنىڭ كۆڭلىگە پەقەت ۋەھىي ئارقىلىق بىلىنىدىغان نەرسىلەرنى سالدۇق».');
INSERT INTO "saleh" VALUES(20,39,'20:39','(ئېيتتۇقكى) «ئۇنى (يەنى بالىنى) ساندۇققا سالغىن، ئاندىن ئۇنى (ساندۇق بىلەن) دەرياغا تاشلىغىن، دەريا ئۇنى قىرغاققا تاشلىسۇن، ئۇنى مېنىڭ دۈشمىنىم ۋە ئۇنىڭ دۈشمىنى بولغان (پىرئەۋن) ئېلىپ (باقسۇن)». مېنىڭ قوغدىشىم ئاستىدا تەربىيەلىنىشىڭ ئۈچۈن، ساڭا مەن تەرەپتىن بولغان مۇھەببەتنى سالدىم.');
INSERT INTO "saleh" VALUES(20,40,'20:40',' ئۆز ۋاقتىدا سېنىڭ ھەمشىرەڭ (پىرئەۋىننىڭ ئائىلىسىگە) بېرىپ: «سىلەرگە ئۇنى باقىدىغان بىر كىشىنى كۆرسىتىپ قويايمۇ؟» دېدى. بىز سېنىڭ ئاناڭنى خۇش بولسۇن، قايغۇرمىسۇن دەپ سېنى ئۇنىڭغا قايتۇرۇپ بەردۇق، سەن بىر ئادەمنى ئۆلتۈرۈپ قويدۇڭ، سېنى بىز غەمدىن خالاس قىلدۇق، سېنى تۈرلۈك بالالار بىلەن سىنىدۇق، سەن مەديەنلىكلەر ئارىسىدا كۆپ يىللار تۇردۇڭ، ئاندىن، ئى مۇسا! تەقدىر بىلەن (بۇ يەرگە) كەلدىڭ. ');
INSERT INTO "saleh" VALUES(20,41,'20:41','سېنى مېنىڭ مۇھەببىتىمگە (يەنى پەيغەمبەر قىلىپ ۋەھىي نازىل قىلىشىمغا) تاللىدىم.');
INSERT INTO "saleh" VALUES(20,42,'20:42','(ئى مۇسا!) سەن ۋە سېنىڭ قېرىندىشىڭ (ھارۇن) مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، مېنىڭ زىكرىمگە سۇسلۇق قىلىپ قالماڭلار.');
INSERT INTO "saleh" VALUES(20,43,'20:43','پىرئەۋننىڭ يېنىغا بېرىڭلار، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى.');
INSERT INTO "saleh" VALUES(20,44,'20:44',' ئۇنىڭغا يۇمشاق سۆز قىلىڭلار، ئۇ ۋەز ـ نەسىھەتنى قوبۇل قىلىشى ياكى (ھەددىدىن ئاشقانلىقنىڭ ئاقىۋىتىدىن) قورقۇشى مۇمكىن.');
INSERT INTO "saleh" VALUES(20,45,'20:45','ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن ئۇنىڭ بىزنى ئالدىراپ جازالىشىدىن ياكى تېخىمۇ ھەددىدىن ئېشىشىدىن قورقىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(20,46,'20:46',' ئاللاھ ئېيتتى: «سىلەر قورقماڭلار، مەن ھەقىقەتەن سىلەر بىلەن بىللە (ئۇنىڭ سىلەرگە بەرگەن جاۋابىنى) ئاڭلاپ تۇرىمەن، (ئۇنىڭ سىلەرگە قىلىدىغانلىرىنى) كۆرۈپ تۇرىمەن.');
INSERT INTO "saleh" VALUES(20,47,'20:47','سىلەر پىرئەۋننىڭ يېنىغا بېرىپ، بىز ئىككىمىز پەرۋەردىگارىڭنىڭ ئەلچىسى بولىمىز، ئىسرائىل ئەۋلادىنى قويۇۋەتكىن، بىز بىلەن كەتسۇن، ئۇلارنى قىينىمىغىن. ساڭا بىز ھەقىقەتەن پەرۋەردىگارىڭنىڭ مۆجىزىسىنى ئېلىپ كەلدۇق، توغرا يولغا ئەگەشكەن ئادەم (ئاللاھنىڭ ئازابىدىن) ئامان قالىدۇ.');
INSERT INTO "saleh" VALUES(20,48,'20:48',' بىزگە شەك ـ شۈبھىسىز ۋەھىي قىلىندىكى، (ئاللاھنىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرۈگەن ئادەم (قاتتىق) ئازابقا دۇچار بولىدۇ، دەڭلار».');
INSERT INTO "saleh" VALUES(20,49,'20:49',' (ئۇلار پىرئەۋنگە كېلىپ ئاللاھ تائالا تەرىپىدىن ۋەھىي قىلىنغان نەرسىلەرنى يەتكۈزگەندىن كېيىن) پىرئەۋن: «ئى مۇسا، سىلەرنىڭ پەرۋەردىگارىڭلار كىم؟» دېدى.');
INSERT INTO "saleh" VALUES(20,50,'20:50','مۇسا ئېيتتى: «پەرۋەردىگارىمىز شۇنداق زاتتۇركى، ھەممە نەرسىگە (ئۆزىگە مۇناسىپ) شەكىل ئاتا قىلدى، (ئاندىن ئۇلارغا ياشاش يوللىرىنى، پايدىلىنىدىغان نەرسىلەرنى) كۆرسەتتى».');
INSERT INTO "saleh" VALUES(20,51,'20:51',' پىرئەۋن ئېيتتى: «ئۆتۈپ كەتكەن ئۈممەتلەرنىڭ ھالى قانداق؟ (يەنى قانداق بولغان؟) »');
INSERT INTO "saleh" VALUES(20,52,'20:52',' مۇسا ئېيتتى: «ئۇلار توغرىسىدىكى مەلۇمات پەرۋەردىگارىمنىڭ دەرگاھىدا لەۋھۇلمەھپۇزدا (خاتىرىلەكلىكتۇر)، پەرۋەردىگارىم ھەم خاتالاشمايدۇ ھەم ئۇنتۇمايدۇ.');
INSERT INTO "saleh" VALUES(20,53,'20:53',' ئۇ زېمىننى سىلەرگە بىسات قىلىپ بەردى، سىلەر ئۈچۈن نۇرغۇن يوللارنى ئېچىپ بەردى، بۇلۇتتىن يامغۇر ياغدۇرۇپ بەردى، ئۇنىڭ بىلەن نۇرغۇن ئۆسۈملۈكلەرنى ئۆستۈرۈپ بەردى».');
INSERT INTO "saleh" VALUES(20,54,'20:54','(ئۇلاردىن) يەڭلار ۋە مال - چارۋىلىرىڭلارنى بېقىڭلار، ئۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن (ئاللاھنىڭ بارلىقى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(20,55,'20:55','سىلەرنى زېمىندىن خەلق ئەتتۇق، (ئۆلگۈنۈڭلاردىن كېيىن) سىلەرنى يەنە زېمىنغا قايتۇرىمىز (توپىغا ئايلىنىسىلەر)، سىلەرنى (ھېساب ئېلىش ئۈچۈن) يەنە بىر قېتىم زېمىندىن چىقىرىمىز.');
INSERT INTO "saleh" VALUES(20,56,'20:56','شەك ـ شۈبھىسىزكى، ئۇنىڭغا بىزنىڭ ھەممە مۆجىزىلىرىمىزنى كۆرسەتتۇق. ئۇ ئىنكار قىلدى ۋە (ئىمان ئېيتىشتىن) باش تارتتى.');
INSERT INTO "saleh" VALUES(20,57,'20:57','پىرئەۋن ئېيتتى: «ئى مۇسا ! سېھرىڭ بىلەن بىزنى زېمىنىمىز (مىسىر) دىن چىقىرىۋېتىش ئۈچۈن كەلدىڭمۇ؟ ');
INSERT INTO "saleh" VALUES(20,58,'20:58','ساڭا بىز شۇنىڭغا ئوخشاش سېھىر بىلەن تاقابىل تۇرىمىز، سەن بىزگە ئۆز ئارا يىغىلىدىغان ۋاقىتنى ۋە مۇئەييەن جاينى بەلگىلەپ بەرگىن، سەنمۇ، بىزمۇ ئۇنىڭغا خىلاپلىق قىلمايلى».');
INSERT INTO "saleh" VALUES(20,59,'20:59',' مۇسا ئېيتتى: «سىلەرگە ۋەدە قىلىنغان چاغ بايرام كۈنى بولۇپ، كىشىلەر چاشكا ۋاقتىدا يىغىلسۇن».');
INSERT INTO "saleh" VALUES(20,60,'20:60','پىرئەۋن قايتىپ كېتىپ ھىيلە ـ مىكرنى (يەنى سېھىرگەرلەرنى) توپلىدى. ئاندىن ئۇ (سېھىرگەرلەرنى ۋە سېھرىدە ئىشلىنىدىغان نەرسىلەرنى ئېلىپ ۋەدىلەشكەن ۋاقىتتا) كەلدى.');
INSERT INTO "saleh" VALUES(20,61,'20:61','مۇسا ئۇلارغا ئېيتتى: «سىلەرگە ۋاي! ئاللاھقا يالغاننى چاپلىماڭلار (يەنى سېھرىڭلار ئارقىلىق مۆجىزىلىرىمگە تاقابىل تۇرۇشنى دەۋا قىلماڭلار)، ئاللاھ سىلەرنى قاتتىق ئازاب بىلەن ھالاك قىلىدۇ، ئاللاھقا بوھتان چاپلىغان ئادەم مەغلۇب بولىدۇ».');
INSERT INTO "saleh" VALUES(20,62,'20:62',' ئۇلار ئۆز ئىشى (يەنى مۇسا ۋە ھارۇننىڭ ئىشى) ئۈستىدە دەتالاش قىلىشتى، قىلغان دەتالىشىنى يوشۇرۇشتى.');
INSERT INTO "saleh" VALUES(20,63,'20:63','ئۇلار ئېيتتى: «بۇ ئىككىسى راستلا سېھىرگەر بولۇپ، سېھىر بىلەن سىلەرنى زېمىنىڭلاردىن چىقىرىۋەتمەكچى ۋە توغرا دىنىڭلارنى يوقاتماقچى بولىدۇ.');
INSERT INTO "saleh" VALUES(20,64,'20:64','ھىيلەڭلارنىڭ ھەممىسىنى جەملەڭلار (يەنى پۈتۈن چارە ـ تەدبىرلىرىڭلارنى بىر نۇقتىغا يىغىپ بىر ياقىدىن باش چىقىرىڭلار)، ئاندىن (قارىغۇچىلارغا ھەيۋەتلىك كۆرۈنۈش ئۈچۈن مەيدانغا) سەپ تارتىپ كېلىڭلار، بۈگۈن ئۈستۈنلۈك قازانغان ئادەم راستلا مەقسىتىگە ئېرىشىدۇ».');
INSERT INTO "saleh" VALUES(20,65,'20:65',' ئۇلار ئېيتتى: «ئى مۇسا! (ھاساڭنى) سەن ئاۋۋال تاشلامسەن؟ ياكى (ئارغامچا، ھاسىلىرىمىزنى) بىز ئاۋۋال تاشلامدۇق؟»');
INSERT INTO "saleh" VALUES(20,66,'20:66','مۇسا: «ئۇنداقتا سىلەر ئالدىدا تاشلاڭلار!» دېدى. (ئۇلار تاشلىغان ئىدى) ناگاھان ئۇلارنىڭ ئاغامچىلىرى، ھاسىلىرى ئۇلارنىڭ سېھىرىدىن (يەنى سېھىرنىڭ تەسىرىدىن) ئۇنىڭغا ھەرىكەتلىنىپ مېڭىۋاتقاندەك تۇيۇلدى.');
INSERT INTO "saleh" VALUES(20,67,'20:67',' (بۇنىڭدىن) مۇسا ئۆزىدە قورقۇنچ ھېس قىلدى.');
INSERT INTO "saleh" VALUES(20,68,'20:68','بىز (ئۇنىڭغا) ئېيتتۇقكى، «قورقمىغىن، سەن چوقۇم ئۈستۈنلۈك قازىنىسەن،');
INSERT INTO "saleh" VALUES(20,69,'20:69','قولۇڭدىكىنى تاشلىغىن، ئۇ ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋېتىدۇ، ئۇلارنىڭ كۆرسەتكىنى سېھىرگەرلەرنىڭ ھىيلىسىدۇر، سېھىرگەرلەر قەيەرگە بارسا مۇۋەپپەقىيەت قازىنالمايدۇ». ');
INSERT INTO "saleh" VALUES(20,70,'20:70','(مۇسا ھاسىسىنى تاشلىۋىدى، ئۇلارنىڭ كۆرسەتكەن سېھىرلىرىنى دەم تارتىپ يۇتۇۋەتتى) شۇنىڭ بىلەن سېھىرگەرلەر (سەجدە قىلغان ھالدا يىقىلىپ): «ھارۇن بىلەن مۇسانىڭ پەرۋەردىگارىغا ئىمان ئېيتتۇق» دېيىشتى.');
INSERT INTO "saleh" VALUES(20,71,'20:71','پىرئەۋن ئېيتتى: «سىلەرگە مەن رۇخسەت بېرىشتىن بۇرۇن ئۇنىڭغا ئىمان ئېيتتىڭلار، شۈبھىسىزكى، ئۇ چوقۇم سىلەرگە سېھىرنى ئۆگەتكەن چوڭۇڭلار ئىكەن، مەن چوقۇم سىلەرنىڭ قوللىرىڭلارنى، پۇتلىرىڭلارنى ئوڭ ـ چەپ قىلىپ (يەنى ئوڭ قول بىلەن سول پۇتنى، ئوڭ پۇت بىلەن سول قولنى) كېسىپ تاشلايمەن، سىلەرنى چوقۇم خورما دەرەخلىرىگە ئاسىمەن، سىلەر قايسىمىزنىڭ (يەنى مېنىڭ ياكى مۇسانىڭ پەرۋەردىگارىنىڭ) ئازابىنىڭ ئەڭ قاتتىق ۋە ئەڭ ئۇزۇن ئىكەنلىكىنى چوقۇم بىلىسىلەر».');
INSERT INTO "saleh" VALUES(20,72,'20:72','ئۇلار ئېيتتى: «سېنى (يەنى ساڭا ئەگىشىشنى) ئىختىيار قىلىپ، بىزگە كەلگەن (مۇسانىڭ راستلىقىنى كۆرسىتىدىغان) روشەن دەلىللەرنى ۋە بىزنى خەلق ئەتكەن ئاللاھنى ھەرگىز تاشلىمايمىز. (بىزنىڭ ھەققىمىزدە) نېمىنى ھۆكۈم قىلساڭ، شۇنى ھۆكۈم قىلغىن، سېنىڭ ھۆكمىڭ پەقەت بۇ دۇنيادىلا ئۆتىدۇ.');
INSERT INTO "saleh" VALUES(20,73,'20:73',' بىزنىڭ خاتالىقلىرىمىزنى، سېنىڭ مەجبۇرلىشىڭ ئارقىسىدا بىزنىڭ سېھىر كۆرسەتكەنلىكىمىزدىن ئىبارەت گۇناھىمىزنى مەغپىرەت قىلسۇن دەپ بىز ھەقىقەتەن پەرۋەردىگارىمىزغا ئىمان ئېيتتۇق، ئاللاھنىڭ (ساۋابى سېنىڭ مۇكاپاتىڭدىن) ئارتۇقتۇر؛ ئاللاھنىڭ (ئازابى سېنىڭ جازايىڭدىن) دائىمىيدۇر».');
INSERT INTO "saleh" VALUES(20,74,'20:74',' كىمكى گۇناھكار ھالدا ئاللاھقا مۇلاقات بولىدىكەن، ئۇ دوزاخقا كىرىدۇ، دوزاختا ئۇ ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ.');
INSERT INTO "saleh" VALUES(20,75,'20:75','كىمكى مۆمىن بولۇپ، ياخشى ئەمەللەرنى قىلغان ھالدا ئاللاھقا مۇلاقات بولىدىكەن، ئۇ (ئاللاھنىڭ دەرگاھىدا) يۇقىرى دەرىجىلەرگە ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(20,76,'20:76','ئۇ (دەرىجىلەر) دائىمىي تۇرالغۇ بولغان جەننەتلەر بولۇپ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدۇ، ئۇ يەرلەردە ئۇ مەڭگۈ قالىدۇ، ئەنە شۇ (كۇفرىدىن ۋە گۇناھلاردىن) پاك بولغان ئادەمگە بېرىلىدىغان مۇكاپاتتۇر.');
INSERT INTO "saleh" VALUES(20,77,'20:77',' بىز شەك ـ شۈبھىسىز مۇساغا ۋەھىي قىلدۇقكى، «سەن مېنىڭ بەندىلىرىمنى كېچىدە (مىسىردىن) ئېلىپ چىقىپ كەت، سەن (ھاساڭنى) دېڭىزغا ئۇرۇپ ئۇلارغا بىر قۇرۇق يول ئېچىپ بەرگىن، (پىرئەۋننىڭ ئارقاڭدىن قوغلاپ) يېتىۋېلىشىدىن قورقمىغىن، (دېڭىزدا غەرق بولۇپ كېتىشتىنمۇ) قورقمىغىن».');
INSERT INTO "saleh" VALUES(20,78,'20:78','پىرئەۋن لەشكەرلىرى بىلەن ئۇلارنى قوغلىدى، ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ لەشكىرىنى) دېڭىزدا دەھشەتلىك ھالەت ئورىۋالدى (يەنى ئۇلار دېڭىزدا غەرق بولدى).');
INSERT INTO "saleh" VALUES(20,79,'20:79',' پىرئەۋن ئۆز قەۋمىنى ئازدۇردى، (ئۇلارنى) توغرا يولغا باشلىمىدى.');
INSERT INTO "saleh" VALUES(20,80,'20:80','ئى ئىسرائىل ئەۋلادى! سىلەرنى ھەقىقەتەن دۈشمىنىڭلاردىن قۇتقۇزدۇق، (مۇناجات ئۈچۈن ۋە مۇساغا تەۋراتنى نازىل قىلىش ئۈچۈن) سىلەرگە (تۇرسىنا) نىڭ ئوڭ تەرىپىنى ۋەدە قىلدۇق، سىلەرگە تەرەنجىبىن بىلەن بۆدۈنىنى چۈشۈرۈپ بەردۇق.');
INSERT INTO "saleh" VALUES(20,81,'20:81',' (ئېيتتۇقكى) «بىز رىزىق قىلىپ بەرگەن پاك ـ ھالال نەرسىلەردىن يەڭلار، بۇ ھەقتە چەكتىن ئاشماڭلار، (ئۇنداق قىلساڭلار) سىلەرگە غەزىپىم نازىل بولىدۇ، كىمگە مېنىڭ غەزىپىم نازىل بولىدىكەن، ئۇ چوقۇم ھالاك بولىدۇ.');
INSERT INTO "saleh" VALUES(20,82,'20:82',' تەۋبە قىلغان، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، ئاندىن توغرا يولدا ماڭغان ئادەمنى مەن ئەلۋەتتە ناھايىتى مەغپىرەت قىلغۇچىمەن».');
INSERT INTO "saleh" VALUES(20,83,'20:83','«ئى مۇسا! ئالدىراپ (تۇرسىناغا) قەۋمىڭدىن ئىلگىرى كېلىشىڭنىڭ سەۋەبى نېمە؟» دېدۇق.');
INSERT INTO "saleh" VALUES(20,84,'20:84','مۇسا ئېيتتى: «ئۇلار ئىزىمنى قوغلىشىپ كېلىۋاتىدۇ، پەرۋەردىگارىم! سېنى رازى بولسۇن دەپ بۇ يەرگە ئالدىراپ كەلدىم».');
INSERT INTO "saleh" VALUES(20,85,'20:85','ئاللاھ ئېيتتى: «سەندىن (يەنى سەن كەتكەندىن) كېيىن قەۋمىڭنى ھەقىقەتەن سىنىدۇق، ئۇلارنى سامىرى ئازدۇردى». ');
INSERT INTO "saleh" VALUES(20,86,'20:86','مۇسا قەۋمىگە غەزەپلەنگەن ھالدا خاپا قايتتى، ئۇ ئېيتتى: «سىلەرگە پەرۋەردىگارىڭلار چىرايلىق ۋەدە قىلمىغانمىدى؟ (سىلەر ئەھدىنى ئۇنتۇغۇدەك) ۋاقىت شۇنچە ئۇزۇن بولۇپ كەتتىمۇ؟ ياكى سىلەر ئۆزۈڭلار پەرۋەردىگارىڭلارنىڭ غەزىپى چۈشۈشىنى ئىرادە قىلدىڭلارمۇ؟ شۇنىڭ ئۈچۈن سىلەرنىڭ ماڭا بەرگەن ۋەدەڭلارغا خىلاپلىق قىلدىڭلارمۇ؟».');
INSERT INTO "saleh" VALUES(20,87,'20:87','ئۇلار ئېيتتى: «ساڭا بەرگەن ۋەدىگە ئۆزلۈكىمىزدىن خىلاپلىق قىلغىنىمىز يوق، لېكىن بىز (پىرئەۋن) قەۋمىنىڭ زىننەتلىرىدىن نۇرغۇن ئېلىپ چىققان ئىدۇق، (سامىرىنىڭ بۇيرۇقى بويىچە) ئۇلارنى (ئوتقا) تاشلىدۇق، سامىرىمۇ (ئۆزى ئېلىپ چىققان زىبۇ- زىننەتلىرىنى) تاشلىدى».');
INSERT INTO "saleh" VALUES(20,88,'20:88','سامىرى (ئېرىتىلگەن زىبۇ - زىننەتلەردىن) ئۇلار ئۈچۈن بىر موزاي ھەيكەل ياسىدى، ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقاتتى، ئۇلار (يەنى سامىرى ۋە ئۇنىڭ تەۋەلىرى) ئېيتتى: «بۇ سىلەرنىڭ ئىلاھىڭلاردۇر ۋە مۇسانىڭ ئىلاھىدۇر، مۇسا (ئىلاھنى بۇ يەردە) ئۇنتۇپ قېلىپ (ئۇنى ئىزدەپ تۇرسىناغا كەتتى)».');
INSERT INTO "saleh" VALUES(20,89,'20:89','ئۇ موزاينىڭ ئۇلارنىڭ سۆزىگە جاۋاب بېرەلمەيدىغانلىقىنى، ئۇلارغا پايدا ـ زىيان يەتكۈزەلمەيدىغانلىقىنى ئۇلار ئۇقمامدۇ؟.');
INSERT INTO "saleh" VALUES(20,90,'20:90','شەك ـ شۈبھىسىزكى، ھارۇن ئۇلارغا (مۇسا قايتىپ كېلىشتىن) بۇرۇن: «ئى قەۋمىم! سىلەر موزاي بىلەن قايمۇقتۇرۇلدۇڭلار، سىلەرنىڭ پەرۋەردىگارىڭلار مەرھەمەتلىك ئاللاھتۇر، ماڭا بويسۇنۇڭلار، (موزايغا چوقۇنۇشنى تەرك ئېتىش بىلەن) بۇيرۇقىمغا ئىتائەت قىلىڭلار!» دېدى. ');
INSERT INTO "saleh" VALUES(20,91,'20:91','ئۇلار: «مۇسا قايتىپ كەلگەنگە قەدەر ئۇنىڭغا چوقۇنىۋېرىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(20,92,'20:92','مۇسا ئېيتتى: «ئى ھارۇن! ئۇلارنىڭ ئازغانلىقىنى كۆرگەن چېغىڭدا (خۇدالىق ئۈچۈن غەزەپلىنىشتە) ماڭا ئەگىشىشتىن نېمە توسالغۇ بولدى؟');
INSERT INTO "saleh" VALUES(20,93,'20:93','مېنىڭ بۇيرۇقىمغا خىلاپلىق قىلدىڭمۇ؟.');
INSERT INTO "saleh" VALUES(20,94,'20:94','ھارۇن ئېيتتى: « ئى قېرىندىشىم! مېنىڭ ساقىلىمنى ۋە چېچىمنى تارتمىغىن، مەن ھەقىقەتەن سېنىڭ، ئىسرائىل ئەۋلادىنى بۆلۈۋېتىپسەن، سۆزۈمگە دىققەت قىلماپسەن، دېيىشىڭدىن قورقتۇم». ');
INSERT INTO "saleh" VALUES(20,95,'20:95','مۇسا ئېيتتى: «ئى سامىرى! سەن نېمە بولدۇڭ؟ (يەنى مۇنداق يامان ئىشنى قىلىشىڭدىن غەرىزىڭ نېمە؟)».');
INSERT INTO "saleh" VALUES(20,96,'20:96','سامىرى ئېيتتى: «مەن ئۇلار كۆرمىگەننى كۆردۈم، ئەلچى (يەنى جىبرىئىل) نىڭ ئىزىدىن بىر چاڭگال توپىنى ئالدىم ـ دە، ئۇنى (موزايغا) چاچتىم، (شۇنىڭ بىلەن ئۇنىڭدىن موزاينىڭ ئاۋازىغا ئوخشاش ئاۋاز چىقتى)، شۇنىڭدەك (بۇنى) ماڭا نەپسىم چىرايلىق كۆرسەتتى». ');
INSERT INTO "saleh" VALUES(20,97,'20:97','مۇسا (سامىرىغا) ئېيتتى: «سەن بارغىن! سەن ئۆمۈرۋايەت (كۆرگەنلا كىشىگە) ماڭا يېقىنلاشماڭلار! دېگەيسەن (يەنى سېنىڭ كىشىگە يېقىنلاشماسلىقىڭ، كىشىنىڭ ساڭا يېقىنلاشماسلىقى بۇ دۇنيادا ساڭا بېرىلگەن جازادۇر)، شۈبھىسىزكى (ئاخىرەتتە جازالىنىدىغان) مۇئەييەن بىر ۋاقتىڭ بار، ئاللاھ ساڭا قىلغان بۇ ۋەدىسىگە خىلاپلىق قىلمايدۇ، سەن ھامان چوقۇنۇپ كېلىۋاتقان ئىلاھىڭغا قارىغىن، بىز ئۇنى چوقۇم (ئوتتا) كۆيدۈرىمىز، ئاندىن ئۇنىڭ (كۈلىنى) دېڭىزغا چېچىۋېتىمىز».');
INSERT INTO "saleh" VALUES(20,98,'20:98','سىلەرنىڭ ئىلاھىڭلار بىر ئاللاھتۇر، ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ ھەر نەرسىنى تولۇق بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(20,99,'20:99','(ئى مۇھەممەد!) شۇنىڭدەك، ساڭا ئۆتكەنكىلەرنىڭ بەزى خەۋەرلىرىنى بايان قىلىمىز، ساڭا ئۆز تەرىپىمىزدىن قۇرئاننى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(20,100,'20:100','كىمكى قۇرئاندىن يۈز ئۆرۈيدىكەن، ئۇنىڭغا قىيامەت كۈنى (چوڭ گۇناھتىن) ئېغىر يۈك يۈكلىنىدۇ.');
INSERT INTO "saleh" VALUES(20,101,'20:101',' (ئۇ گۇناھى ئۈچۈن بېرىلگەن ئازابنى مەڭگۈ تارتىدۇ) قىيامەت كۈنى ئۇلارغا يۈكلەنگەن بۇ يۈك نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(20,102,'20:102','بۇ سۇر چىلىنىدىغان كۈندۇر، بۇ كۈندە (شەكلى ئۆزگەرگەن) كۆك كۆزلۈك (قارا يۈزلۈك) گۇناھكارلارنى يىغىمىز. ');
INSERT INTO "saleh" VALUES(20,103,'20:103',' (قىيامەتنىڭ دەھشىتىنى كۆرۈپ دۇنيادا تۇرغان ۋاقتى قىسقا بىلىنگەنلىكتىن) (ئۇلار) «(دۇنيادا) پەقەت ئون كۈنلا تۇردۇڭلار» دېيىشىپ پىچىرلىشىدۇ. ');
INSERT INTO "saleh" VALUES(20,104,'20:104','ئۇلارنىڭ دەيدىغانلىرىنى بىز ئوبدان بىلىمىز، ئەينى زاماندا ئۇلارنىڭ ئەڭ توغرا سۆزلەيدىغىنى: «سىلەر پەقەت بىر كۈن تۇردۇڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(20,105,'20:105','ئۇلار سەندىن تاغلار توغرۇلۇق (يەنى تاغنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقى توغرۇلۇق) سورايدۇ، ئېيتقىنكى، «پەرۋەردىگارىم ئۇلارنى كۈكۈم - تالقان قىلىپ (ئاندىن شامالنى ئەۋەتىپ ئۇنى سورۇيدۇ).');
INSERT INTO "saleh" VALUES(20,106,'20:106','ئۇلارنى تۈزلەڭلىككە ئايلاندۇرىدۇ.');
INSERT INTO "saleh" VALUES(20,107,'20:107','ئۇ يەرلەردە نە چوڭقۇرلۇقنى، نە ئېگىزلىكنى كۆرمەيسەن».');
INSERT INTO "saleh" VALUES(20,108,'20:108','بۇ كۈندە (كىشىلەر ئاللاھنىڭ مەھشەرگاھقا) دەۋەت قىلغۇچىسىغا ئەگىشىدۇ، قىڭغىر كەتمەيدۇ، مەرھەمەتلىك ئاللاھنىڭ ئۇلۇغلۇقى ۋە ھەيۋىتىدىن قورقۇپ، بارلىق ئاۋازلار بېسىقىدۇ، پىچىرلاشقاندىن باشقىنى ئاڭلىمايسەن.');
INSERT INTO "saleh" VALUES(20,109,'20:109','بۇ كۈندە مەرھەمەتلىك ئاللاھ ئىزىن بەرگەن ۋە سۆزىدىن ئاللاھ رازى بولغان ئادەمنىڭ شاپائىتىدىن باشقا ھېچقانداق شاپائەت پايدا بەرمەيدۇ.');
INSERT INTO "saleh" VALUES(20,110,'20:110','ئاللاھ ئۇلارنىڭ ئالدىدىكىنى ۋە كەينىدىكىنى بىلىدۇ، ئۇلار بولسا ئۇنى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(20,111,'20:111','گۇناھكارلار مەخلۇقاتنى ئىدارە قىلىپ تۇرغۇچى(مەڭگۈ) ھايات ئاللاھقا ئېگىلىدۇ، ئاللاھقا شېرىك كەلتۈرگەنلەر شەكسىز زىيان تارتىدۇ.');
INSERT INTO "saleh" VALUES(20,112,'20:112','مۆمىن تۇرۇپ ياخشى ئەمەللەرنى قىلغانلار يامانلىقلىرىنىڭ ئېشىپ كېتىشىدىن ۋە ياخشىلىقلىرىنىڭ كېمىيىپ كېتىشىدىن قورقمايدۇ.');
INSERT INTO "saleh" VALUES(20,113,'20:113','ئۇلارنىڭ تەقۋادار بولۇشلىرى ئۈچۈن ياكى ئۇلارنى ۋەز ـ نەسىھەتكە ئىگە قىلىش ئۈچۈن، ساڭا ئەرەبچە قۇرئاننى نازىل قىلدۇق ۋە ئۇنىڭدا تۈرلۈك ئاگاھلاندۇرۇشلارنى بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(20,114,'20:114',' ئاللاھ (زاتى، سۈپىتى جەھەتتە پۈتۈن مەخلۇقاتتىن) ئۈستۈندۇر، ھەق پادىشاھتۇر، (ئى مۇھەممەد!) ساڭا قۇرئاننىڭ ۋەھىيسى تاماملىنىشتىن بۇرۇن ئوقۇشقا ئالدىراپ كەتمىگىن، (يەنى جىبرىئىل ساڭا قۇرئاننى ئوقۇپ بېرىۋاتقاندا، بىللە ئوقۇشقا ئالدىراپ كەتمەي، ئۇ تىلاۋىتىدىن فارىغ بولغىچە تىڭشاپ تۇرغىن). «ئى پەرۋەردىگارىم! ئىلمىمنى زىيادە قىلغىن!» دېگىن.');
INSERT INTO "saleh" VALUES(20,115,'20:115',' بىز ئىلگىرى ئادەمگە (دەرەخنىڭ مېۋىسىنى يېمەسلىكنى) تەۋسىيە قىلدۇق، ئۇ (بۇ تەۋسىيەنى) يادىدىن چىقاردى، ئۇنىڭدا ھېچقانداق چىڭ ئىرادە كۆرمىدۇق.');
INSERT INTO "saleh" VALUES(20,116,'20:116','ئۆز ۋاقتىدا بىز پەرىشتىلەرگە: «ئادەمگە (يەنى ئادەم ئەلەيھىسسالامغا) سەجدە قىلىڭلار» دېدۇق، ئىبلىستىن باشقىسى سەجدە قىلدى، ئۇ (سەجدە قىلىشتىن) باش تارتتى.');
INSERT INTO "saleh" VALUES(20,117,'20:117',' بىز دېدۇق: «ئى ئادەم! بۇ ھەقىقەتەن سېنىڭ دۈشمىنىڭدۇر، خوتۇنۇڭنىڭ دۈشمىنىدۇر، ئۇ سىلەر ئىككىڭلارنىڭ جەننەتتىن چىقىپ كېتىشىڭلارغا سەۋەبچى بولۇپ قالمىسۇن، (ئۇ چاغدا) سىلەر جاپا - مۇشەققەتتە قالىسىلەر. ');
INSERT INTO "saleh" VALUES(20,118,'20:118','جەننەتتە سەن ھەقىقەتەن ئاچ قالمايسەن ۋە يالىڭاچ قالمايسەن.');
INSERT INTO "saleh" VALUES(20,119,'20:119','ھەمدە ئۇنىڭدا ھەقىقەتەن ئۇسساپ كەتمەيسەن، ئىسسىپ كەتمەيسەن».');
INSERT INTO "saleh" VALUES(20,120,'20:120','شەيتان ئۇنىڭغا ۋەسۋەسە قىلىپ: «ئى ئادەم! ساڭا (مەن يېگەن ئادەم) ئۆلمەيدىغان دەرەخنى ۋە زاۋال تاپماس پادىشاھلىقنى كۆرسىتىپ قويايمۇ؟» دېدى. ');
INSERT INTO "saleh" VALUES(20,121,'20:121','ئۇلار ئۇ دەرەخنىڭ مېۋىسىنى يېدى، شۇنىڭ بىلەن ئۇلارنىڭ ئەۋرەتلىرى ئېچىلىپ قالدى، ئۇلار جەننەت (دەرەخلىرىنىڭ) يوپۇرماقلىرى بىلەن سەترى ئەۋرەت قىلىشقا كىرىشتى، ئادەم پەرۋەردىگارىنىڭ (ئەمرىگە) خىلاپلىق قىلدى، شۇنىڭ بىلەن ئۇ ئازدى. ');
INSERT INTO "saleh" VALUES(20,122,'20:122','ئاندىن پەرۋەردىگارى ئۇنى تاللىدى، ئۇنىڭ تەۋبىسىنى قوبۇل قىلدى ۋە ئۇنى ھىدايەت قىلدى. ');
INSERT INTO "saleh" VALUES(20,123,'20:123','ئاللاھ ئېيتتى: «سىلەر ھەممىڭلار جەننەتتىن بىر-بىرىڭلارغا دۈشمەن بولغان ھالدا چۈشۈڭلار، ئەگەر سىلەرگە مېنىڭ تەرىپىمدىن ھىدايەت كەلسە، كىمكى مېنىڭ ھىدايىتىمگە ئەگەشسە، ئۇ ئازمايدۇ ۋە ئاخىرەتتە شەقىي بولمايدۇ.');
INSERT INTO "saleh" VALUES(20,124,'20:124',' كىمكى مېنىڭ زىكرىمدىن يۈز ئۆرۈيدىكەن، ئۇنىڭ ھاياتى تار (يەنى خاتىرجەمسىز) بولىدۇ، قىيامەت كۈنى ئۇنى بىز كور قوپۇرىمىز».');
INSERT INTO "saleh" VALUES(20,125,'20:125','ئۇ: «ئى پەرۋەردىگارىم! نېمىشقا مېنى كور قوپۇردۇڭ، ۋاھالەنكى، (دۇنيادىكى چاغدا) مېنىڭ كۆزۈم كۆرەتتىغۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(20,126,'20:126','ئاللاھ ئېيتىدۇ: «شۇنىڭدەك ساڭا (روشەن) ئايەتلىرىمىز كەلدى، سەن ئۇلارنى تەرك ئەتتىڭ، سەن بۈگۈن شۇنىڭغا ئوخشاش تەرك ئېتىلىسەن».');
INSERT INTO "saleh" VALUES(20,127,'20:127',' ھەددىدىن ئاشقان ۋە پەرۋەردىگارىنىڭ ئايەتلىرىگە ئىمان ئېيتمىغانلارغا شۇنىڭغا ئوخشاش جازا بېرىمىز، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر، تېخىمۇ باقىيدۇر.');
INSERT INTO "saleh" VALUES(20,128,'20:128','ئۇلاردىن ئىلگىرى ئۆتكەن (پەيغەمبەرلىرىنى ئىنكار قىلغان) نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ ئۇلار شۇ ئۈممەتلەرنىڭ ماكانلىرىدا مېڭىپ يۈرىدۇ، ئەقىل ئىگىلىرى ئۈچۈن ئۇنىڭدا ھەقىقەتەن نۇرغۇن ئىبرەتلەر بار. ');
INSERT INTO "saleh" VALUES(20,129,'20:129','ئەگەر پەرۋەردىگارىڭنىڭ (ئازابنى ئۇلاردىن كېچىكتۈرۈشكە) قىلىۋەتكەن ھۆكمى ۋە ئالدىنئالا بەلگىلەپ قويغان ۋاقتى بولمىسا ئىدى، ئۇ چاغدا (ئۇلارنىڭ ھالاك بولۇشى) چوقۇم ئىدى. ');
INSERT INTO "saleh" VALUES(20,130,'20:130','ئۇلارنىڭ سۆزلىرىگە سەۋرى قىلغىن، مەمنۇن بولۇشۇڭ ئۈچۈن، كۈن چىقىشتىن ئىلگىرى، كۈن پېتىشتىن بۇرۇن، كېچە ۋاقىتلىرىدا ۋە كۈندۈزنىڭ دەسلەپكى ۋە ئاخىرقى ۋاقىتلىرىدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن (يەنى ناماز ئوقۇغىن) ۋە ھەمدە ئېيتقىن.');
INSERT INTO "saleh" VALUES(20,131,'20:131','بىز (كۇففارلاردىن) تۈرلۈك جامائەنى بەھرىمەن قىلغان دۇنيانىڭ نېمەتلىرى ۋە زىبۇ - زىننەتلىرىگە كۆز سالمىغىن، بۇنىڭ بىلەن ئۇلارنى سىنايمىز، پەرۋەردىگارىڭنىڭ رىزقى (يەنى ساۋابى بۇ پانىي نېمەتتىن) ياخشىدۇر ۋە ئەڭ باقىيدۇر.');
INSERT INTO "saleh" VALUES(20,132,'20:132',' (ئى مۇھەممەد!) ئائىلەڭدىكىلەرنى (ۋە ئۈممىتىڭنى) نامازغا بۇيرۇغىن، ئۆزۈڭمۇ ئۇنى ئادا قىلىشقا چىداملىق بولغىن، سەندىن بىز رىزىق تەلەپ قىلمايمىز. ساڭا بىز رىزىق بېرىمىز، ياخشى ئاقىۋەت پەقەت تەقۋادارلارغا خاستۇر.');
INSERT INTO "saleh" VALUES(20,133,'20:133','ئۇلار (يەنى مۇشرىكلار): «(مۇھەممەد) پەرۋەردىگارى تەرىپىدىن بىزگە بىرەر مۆجىزە كەلتۈرسىچۇ!» دەيدۇ، ئۇلارغا بۇرۇنقى ئۈممەتلەرنىڭ خەۋەرلىرىنى ئۆز ئىچىگە ئالغان قۇرئان كەلمىدىمۇ؟.');
INSERT INTO "saleh" VALUES(20,134,'20:134','ئەگەر بىز ئۇلارنى ئۇنىڭدىن (يەنى مۇھەممەد ئەلەيھىسسالامنى ئەۋەتىشتىن) ئىلگىرى ھالاك قىلغان بولساق، ئۇلار چوقۇم: «نېمىشقا بىزگە بىرەر پەيغەمبەر ئەۋەتمىدىڭ؟ بىز خار ۋە رەسۋا بولۇشتىن ئىلگىرى سېنىڭ ئايەتلىرىڭگە ئەگىشەتتۇق» دەيتتى.');
INSERT INTO "saleh" VALUES(20,135,'20:135','ئېيتقىنكى، ھەممە (سىلەرنىڭ ۋە بىزنىڭ ئىشلىرىمىزنىڭ ئاقىۋىتىنى) كۈتمەكتە. سىلەرمۇ كۈتۈڭلار، كىملەرنىڭ توغرا يول ئىگىلىرى ئىكەنلىكىنى ۋە كىملەرنىڭ ھىدايەت تاپقانلىقىنى (ئۇزاققا قالماي) بىلىسىلەر».');
INSERT INTO "saleh" VALUES(21,1,'21:1','كىشىلەرگە ئۇلارنىڭ (ئەمەللىرىدىن) ھېساب ئېلىنىدىغان ۋاقىت (يەنى قىيامەت) يېقىنلاشتى، ھالبۇكى، ئۇلار غەپلەتتىدۇر، (قىيامەت توغرۇلۇق ئويلىنىشتىن) يۈز ئۆرۈمەكتىدۇر.');
INSERT INTO "saleh" VALUES(21,2,'21:2','ئۇلارغا پەرۋەردىگارى تەرىپىدىن (قۇرئاندا) يېڭى ۋەز ـ نەسىھەت نازىل بولسىلا، ئۇلار ئۇنىڭغا مەسخىرە قىلغان ھالدا قۇلاق سېلىشىدۇ. ');
INSERT INTO "saleh" VALUES(21,3,'21:3',' ئۇلارنىڭ دىللىرى (ئاللاھنىڭ كالامىدىن) غەپلەتتىدۇر، كاپىرلار ئۆز ئارا: «بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) پەقەت سىلەرگە ئوخشاش (ئاددىي) بىر ئىنسان، سىلەر سېھىرنى كۆرۈپ تۇرۇپ ئۇنى قوبۇل قىلامسىلەر؟» دەپ يوشۇرۇن سۆزلىشىدۇ.');
INSERT INTO "saleh" VALUES(21,4,'21:4',' (پەيغەمبەر) ئېيتتى: «پەرۋەردىگارىم ئاسمان، زېمىندا ئېيتىلغان سۆزلەرنى بىلىپ تۇرىدۇ، ئاللاھ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر».');
INSERT INTO "saleh" VALUES(21,5,'21:5',' (كاپىرلار قۇرئان سېھرىدۇر دەپلا قالماستىن) «بەلكى ئۇ (مۇھەممەتنىڭ ) قالايمىقان چۈشلىرىدۇر، بەلكى ئۇنى ئۆزى ئويدۇرۇپ چىقارغاندۇر، بەلكى ئۇ شائىردۇر، ئىلگىرىكى پەيغەمبەرلەرگە ئوخشاش بىزگە بىرەر مۆجىزە كەلتۈرسۇن» دېيىشتى.');
INSERT INTO "saleh" VALUES(21,6,'21:6','ئۇلاردىن ئىلگىرى، بىز ھالاك قىلغان شەھەرلەردىن (يەنى ئاھالىسىدىن) ھېچقايسىسى ئىمان ئېيتمىغان تۇرسا، (مۆجىزىلەرنى كۆرسەتسەڭ) ئۇلار ئىمان ئېيتامدۇ؟ ');
INSERT INTO "saleh" VALUES(21,7,'21:7',' (ئى مۇھەممەد!) سەندىن ئىلگىرى (پەرىشتىلەرنى ئەمەس) پەقەت ئادەملەرنىلا پەيغەمبەر قىلىپ ئەۋەتتۇق، ئۇلارغا ۋەھىي قىلدۇق. ئەگەر (بۇنى) بىلمىسەڭلار، ئەھلى ئىلىمدىن سوراڭلار.');
INSERT INTO "saleh" VALUES(21,8,'21:8','پەيغەمبەرلەرنى تاماق يېمەيدىغان جەسەت قىلغىنىمىز يوق (يەنى ئۇلار بارلىق كىشىلەرگە ئوخشاش يەيدۇ، ئىچىدۇ) ھەمدە ئۇلار (دۇنيادا ئۆلمەي) مەڭگۈ قالىدىغان بولغىنى يوق (يەنى ئۇلارمۇ بارلىق كىشىلەرگە ئوخشاش ۋاپات بولىدۇ). ');
INSERT INTO "saleh" VALUES(21,9,'21:9','ئاندىن ئۇلارغا (پەيغەمبەرلەرگە ياردەم بېرىش، ئۇلارنى ئىنكار قىلغانلارنى ھالاك قىلىشتىن ئىبارەت) ۋەدىمىزنى راست قىلىپ بەردۇق، ئۇلارنى ۋە بىز خالىغانلارنى قۇتقۇزدۇق، (كۇفرىدا ۋە گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلارنى ھالاك قىلدۇق، ');
INSERT INTO "saleh" VALUES(21,10,'21:10',' سىلەرنىڭ شان ـ شەرىپىڭلارنى ئۆز ئىچىگە ئالغان كىتابنى (يەنى قۇرئاننى) سىلەرگە ھەقىقەتەن نازىل قىلىپ بەردۇق. (بۇنى) چۈشەنمەمسىلەر؟');
INSERT INTO "saleh" VALUES(21,11,'21:11','بىز (ئاھالىسى) كۇففار بولغان نۇرغۇن شەھەرلەرنى ھالاك قىلدۇق. ئۇلاردىن كېيىن (ئورنىغا) باشقا قەۋمنى پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(21,12,'21:12','ئۇلار بىزنىڭ ئازابىمىزنىڭ (كەلگەنلىكىنى) ھېس قىلغان چاغدا ئۇشتۇمتۇت قاچىدۇ. ');
INSERT INTO "saleh" VALUES(21,13,'21:13','(پەرىشتىلەر ئۇلارغا مەسخىرە قىلىپ ئېيتتى) « قاچماڭلار، باياشات تۇرمۇشۇڭلارغا، تۇرالغۇ جايلىرىڭلارغا قايتىڭلار، سىلەردىن (مۇھىم ئىشلاردا مەسلىھەت) سورىلىشى مۇمكىن».');
INSERT INTO "saleh" VALUES(21,14,'21:14','ئۇلار: «ۋاي ئىسىت! ھەقىقەتەن بىز زالىم بولۇپتۇق» دېيىشتى.');
INSERT INTO "saleh" VALUES(21,15,'21:15','ئۇلارنى ئورۇلغان زىرائەتتەك ھالاك قىلىپ، جانلىرىنى ئالغىنىمىزغا قەدەر، ئۇلار يۇقىرىقى پەريادىنى تەكرارلاپ تۇردى.');
INSERT INTO "saleh" VALUES(21,16,'21:16','بىز ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ ياراتقىنىمىز يوق (يەنى ئۇلارنى بىكار ياراتماستىن، قۇدرىتىمىزنىڭ ئالامەتلىرى سۈپىتىدە ياراتتۇق). ');
INSERT INTO "saleh" VALUES(21,17,'21:17',' ئەگەر بىز ئىچ پۇشۇقى قىلماقچى بولساق، قېشىمىزدىكى نەرسىلەر بىلەن قىلاتتۇق، ئەگەر (شۇنى) ئىرادە قىلغان بولساق (لېكىن بۇ ئۇلۇغ زاتىمىزغا مۇھال بولغانلىقتىن بۇنداق قىلىشنى ئىرادە قىلمىدۇق).');
INSERT INTO "saleh" VALUES(21,18,'21:18','بىز ھەق ئارقىلىق باتىلغا ھۇجۇم قىلىمىز، ھەق باتىلنى يوقىتىدۇ، باتىل ناگاھان يوقىلىدۇ، (ئى كۇففارلار جامائەسى! ئاللاھ (ئاسمان – زېمىننى بىكارغا ياراتقان دېگەندەك) سۆزۈڭلار سەۋەبلىك سىلەرگە ۋاي!.');
INSERT INTO "saleh" VALUES(21,19,'21:19',' ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ھەممىسى ئاللاھنىڭ ئىلكىدىدۇر، ئاللاھنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) ئاللاھنىڭ ئىبادىتىدىن تەكەببۇرلۇق قىلىپ باش تارتمايدۇ ۋە ھېرىپمۇ قالمايدۇ.');
INSERT INTO "saleh" VALUES(21,20,'21:20',' ئۇلار كېچە ـ كۈندۈز تەسبىھ ئېيتىدۇ، بوشاشمايدۇ.');
INSERT INTO "saleh" VALUES(21,21,'21:21',' ئۇلار (يەنى مۇشرىكلار) زېمىندىكى نەرسىلەرنى ئىلاھ قىلىۋالدى، ئۇلار (يەنى بۇ نەرسىلەر) ئۆلۈكلەرنى تىرىلدۈرەلەمدۇ؟');
INSERT INTO "saleh" VALUES(21,22,'21:22','ئەگەر ئاسمان ـ زېمىندا ئاللاھتىن باشقا ئىلاھلار بولسا ئىدى، (كائىناتنىڭ تەرتىپى) ئەلۋەتتە بۇزۇلاتتى، ئەرشنىڭ پەرۋەردىگارى ئاللاھ ئۇلارنىڭ سۈپەتلىگەن نەرسىلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(21,23,'21:23','ئاللاھنىڭ قىلغانلىرىدىن سوئال ـ سوراق قىلىنمايدۇ، ئۇلاردىن سوئال ـ سوراق قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(21,24,'21:24','ئۇلار ئاللاھنى قويۇپ، باشقا مەبۇدلارنى ئىلاھ قىلىۋالدىمۇ؟ ئېيتقىنكى: «(بۇنىڭغا) دەلىلىڭلارنى كۆرسىتىڭلار، مانا بۇ مەن بىلەن بىللە بولغانلارنىڭ كىتابى (يەنى قۇرئان) دۇر ۋە مەندىن ئىلگىرىكىلەرنىڭ (تەۋرات، ئىنجىل قاتارلىق) كىتابىدۇر، ئۇلارنىڭ تولىسى ھەقنى ئۇقمايدۇ، ئۇلار (تەۋھىد) تىن يۈز ئۆرۈگۈچىلەردۇر».');
INSERT INTO "saleh" VALUES(21,25,'21:25',' (ئى مۇھەممەد!) سەندىن ئىلگىرى ئەۋەتىلگەن پەيغەمبەرلەرنىڭ ھەممىسىگە: «مەندىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ماڭىلا ئىبادەت قىلىڭلار» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(21,26,'21:26','ئۇلار (يەنى مۇشرىكلار): «ئاللاھنىڭ (پەرىشتىلەردىن) بالىسى بار» دېيىشتى، ئۇنداق ئەمەس. ئاللاھ (بۇنىڭدىن) پاكتۇر، (پەرىشتىلەر ئاللاھنىڭ) ھۆرمەتلىك بەندىلىرىدۇر.');
INSERT INTO "saleh" VALUES(21,27,'21:27','ئۇلار (يەنى پەرىشتىلەر) ئاللاھقا ئالدى بىلەن سۆز قىلىشقا پېتىنالمايدۇ، ئۇلار ئاللاھنىڭ ئەمرى بويىچە ئىش قىلىدۇ.');
INSERT INTO "saleh" VALUES(21,28,'21:28',' ئاللاھ ئۇلارنىڭ قىلغانلىرىنى ۋە قىلماقچى بولغانلىرىنى بىلىپ تۇرىدۇ، ئۇلار ئاللاھ رازى بولغانلارغىلا شاپائەت قىلىدۇ، ئاللاھنىڭ ھەيۋىسىدىن تىترەپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(21,29,'21:29','ئۇلاردىن كىمكى، مەن ئاللاھتىن باشقا ئىلاھمەن، دەيدىكەن، ئۇنى دوزاخ بىلەن جازالايمىز، زالىملارنى شۇنداق جازالايمىز.');
INSERT INTO "saleh" VALUES(21,30,'21:30',' كاپىرلار بىلمەمدۇكى، ئاسمانلار بىلەن زېمىن بىر ـ بىرىگە تۇتاش ئىدى، ئۇلارنى ئايرىۋەتتۇق، ھەممە جانلىق مەۋجۇداتنى سۇدىن ياراتتۇق، ئۇلار (ئاللاھنىڭ قۇدرىتىگە) ئىشەنمەمدۇ؟ ');
INSERT INTO "saleh" VALUES(21,31,'21:31','تەۋرەپ ئۇلارنىڭ خاتىرجەمسىز بولماسلىقى ئۈچۈن، بىز زېمىندا تاغلارنى ياراتتۇق، ئۇلارنى (سەپەردە) كۆزلىگەن مەقسەتلىرىگە يېتىۋالسۇن دەپ، تاغلاردا كەڭ يوللارنى ياراتتۇق.');
INSERT INTO "saleh" VALUES(21,32,'21:32',' ئاسماننى بىز بىخەتەر ئۆگزە قىلدۇق، ھالبۇكى، ئۇلار (يەنى مۇشرىكلار) ئاسماندىكى (كۈن، ئاي، يۇلتۇزلارغا ئوخشاش ئاللاھنىڭ بارلىقىنى ۋە ئۇنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلەردىن يۈز ئۆرۈگۈچىدۇر.');
INSERT INTO "saleh" VALUES(21,33,'21:33',' ئاللاھ كېچىنى ۋە كۈندۈزنى، كۈننى ۋە ئاينى ياراتتى. ئۇلارنىڭ ھەر بىرى (ئۆزىگە خاس) پەلەكتە سەير قىلىدۇ. ');
INSERT INTO "saleh" VALUES(21,34,'21:34','(ئى مۇھەممەد!) سەندىن ئىلگىرى (ئىنسانلاردىن) ھېچ كىشىنى مەڭگۈ ياشاتقىنىمىز يوق، سەن ئۆلسەڭ، ئۇلار مەڭگۈ ياشامدۇ؟ (بۇنداق بولۇشقا ئۇلار ھەرگىزمۇ مۇيەسسەر بولمايدۇ، ئۇلارنىڭ ھەممىسى ئۆلىدۇ).');
INSERT INTO "saleh" VALUES(21,35,'21:35','ھەر بىر جان ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر. سىلەرنى بىز يامانلىققا مۇپتىلا قىلىش، ياخشىلىق بېرىش ئارقىلىق سىنايمىز. سىلەر بىزنىڭ دەرگاھىمىزغا قايتىسىلەر (ئەمەلىڭلارغا يارىشا جازالايمىز).');
INSERT INTO "saleh" VALUES(21,36,'21:36',' كاپىرلار سېنى كۆرگەن چاغدا: «سىلەرنىڭ مەبۇدلىرىڭلارنى ئەيىبلەيدىغان ئادەم مۇشۇمۇ؟ » (دېيىشىپ) سېنى مەسخىرە قىلىشىدۇ، ھالبۇكى، ئۇلار مەرھەمەتلىك ئاللاھ تەرىپىدىن نازىل بولغان قۇرئاننى ئىنكار قىلىشىدۇ.');
INSERT INTO "saleh" VALUES(21,37,'21:37','ئىنسان ئالدىراڭغۇ يارىتىلغان، سىلەرگە ئالامەتلىرىمنى (يەنى ماڭا ئاسىيلىق قىلغانلارنى جازالىيالايدىغانلىقىمنى) كۆرسىتىمەن. (ئۇنىڭ ۋاقتى سائىتىدىن بۇرۇن) مېنى ئالدىرىتىپ كەتمەڭلار.');
INSERT INTO "saleh" VALUES(21,38,'21:38','ئۇلار: «(سۆزۈڭلاردا) راستچىل بولساڭلار، بۇ ۋەدە (يەنى بىزگە ۋەدە قىلىنغان ئازابنىڭ يۈز بېرىدىغان ۋاقتى) قاچان؟ » دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(21,39,'21:39','ئەگەر كاپىرلار بۇ چاغدا يۈزلىرىدىن، دۈمبىلىرىدىن ئازابنى دەپئى قىلالمايدىغانلىقلىرىنى ۋە ھېچقانداق ياردەمگە ئېرىشەلمەيدىغانلىقلىرىنى بىلسە ئىدى (ئازابنىڭ بالدۇر يۈز بېرىشىنى تەلەپ قىلمايتتى).');
INSERT INTO "saleh" VALUES(21,40,'21:40','لېكىن قىيامەت ئۇلارغا تۇيۇقسىز كېلىدۇ ـ دە، ئۇلارنى ھەيرەتتە قالدۇرىدۇ، ئۇلار قىيامەتكە قارشىلىق كۆرسىتەلمەيدۇ، ئۇلارغا (تەۋبە قىلىۋېلىش ۋە ئۆزرە بايان قىلىش ئۈچۈن) مۆھلەتمۇ بېرىلمەيدۇ.');
INSERT INTO "saleh" VALUES(21,41,'21:41','سەندىن ئىلگىرى ھەقىقەتەن نۇرغۇن پەيغەمبەرلەر مەسخىرە قىلىندى، ئۇلارنى مەسخىرە قىلغۇچىلارغا مەسخىرە قىلغانلىقنىڭ جازاسى بولغان ئازاب نازىل بولدى.');
INSERT INTO "saleh" VALUES(21,42,'21:42','(ئى مۇھەممەد! مەسخىرە قىلغۇچىلارغا) «سىلەرنى كېچىدە ۋە كۈندۈزدە مەرھەمەتلىك ئاللاھنىڭ ئازابىدىن كىم ساقلايدۇ؟» دېگىن، بەلكى ئۇلار پەرۋەردىگارىنىڭ كالامىدىن يۈز ئۆرۈگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(21,43,'21:43','ئۇلارنىڭ بىزدىن باشقا ئۆزلىرىنى (قوغدايدىغان) مەبۇدلىرى بارمۇ؟ ئۇلارنىڭ (چوقۇنۇۋاتقان) مەبۇدلىرى ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمايدۇ، بىزنىڭ ياردىمىمىزگىمۇ ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(21,44,'21:44',' بۇلارنى (يەنى مۇشرىكلارنى) ۋە ئۇلارنىڭ ئەجداتلىرىنى (دۇنيانىڭ مال ـ مۈلكىدىن) بەھرىمەن قىلدۇق، ھەتتا (شۇ نېمەت ئىچىدە) ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى. ئۇلار بىزنىڭ زېمىننىڭ ئەتراپىنى قەدەممۇ قەدەم تارايتىۋاتقانلىقىمىزنى بىلمەمدۇ؟ (ئەھۋال مۇشۇنداق ئىكەن) ئۇلار غالىب ھېسابلىنامدۇ؟');
INSERT INTO "saleh" VALUES(21,45,'21:45',' (ئى مۇھەممەد!) «مەن سىلەرنى پەقەت ۋەھىي ئارقىلىق ئاگاھلاندۇرىمەن» دېگىن. گاسلار ئاگاھلاندۇرۇلغان چاغدا دەۋەتنى ئاڭلىمايدۇ (ئى مۇشرىكلار! سىلەر سەركەشلىكىڭلارنىڭ قاتتىقلىقىدىن خۇددى گاسقا ئوخشاش دەۋەتنى ئاڭلىمايسىلەر، ۋەز ـ نەسىھەتنى قوبۇل قىلمايسىلەر).');
INSERT INTO "saleh" VALUES(21,46,'21:46','ئەگەر ئۇلارغا پەرۋەردىگارىڭدىن ئازغىنا ئازاب يەتسىلا، ئۇلار چوقۇم: «ۋاي ئىسىت! (ئاللاھنىڭ پەيغەمبەرلىرىنى ئىنكار قىلىپ) ھەقىقەتەن (ئۆزىمىزگە) زۇلۇم قىلىپتۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(21,47,'21:47',' قىيامەت كۈنى بىز (ئەمەللەر تارتىلىدىغان) ئادالەت تارازىسىنى ئورنىتىمىز، ھېچ ئادەمگە قىلچە ئۇۋال قىلىنمايدۇ (يەنى ياخشى ئادەمنىڭ ياخشىلىقى كېمەيتىۋېتىلمەيدۇ، يامان ئادەمنىڭ يامانلىقى ئاشۇرۇۋېتىلمەيدۇ)، ئەگەر ئۇنىڭ قىچا چاغلىق ئەمەلى بولسىمۇ، ئۇنى ھازىر قىلىمىز، (بەندىلەرنىڭ ئەمەللىرىدىن) ھېساب ئېلىشقا بىز يېتەرلىكمىز.');
INSERT INTO "saleh" VALUES(21,48,'21:48','بىز ھەقىقەتەن مۇسا بىلەن ھارۇنغا فۇرقاننى (يەنى ھەق بىلەن باتىلنى، ھالال بىلەن ھارامنى، ھىدايەت بىلەن گۇمراھلىقنى ئايرىغۇچى تەۋراتنى)، نۇرنى، تەقۋادارلارغا (پايدىلىنىدىغان) ۋەز ـ نەسىھەتنى بەردۇق.');
INSERT INTO "saleh" VALUES(21,49,'21:49','تەقۋادارلارلا پەرۋەردىگارىنى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقىدۇ ھەمدە ئۇلار قىيامەتنىڭ (دەھشىتىدىن) تىترىگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(21,50,'21:50','بۇ بىز نازىل قىلغان مۇبارەك قۇرئاندۇر. سىلەر ئۇنى ئىنكار قىلامسىلەر؟');
INSERT INTO "saleh" VALUES(21,51,'21:51',' (مۇسا، ھارۇنلاردىن بۇرۇن) بىز ئىبراھىمغا ھەقىقەتەن (دۇنيا ۋە ئاخىرەتتە ياخشىلىق يوللىرىنى كۆرسىتىپ بېرىدىغان) ھىدايەت ئاتا قىلغان ئىدۇق، بىز ئۇنىڭ (پەيغەمبەرلىكنىڭ ئەھلى) ئىكەنلىكىنى بىلەتتۇق. ');
INSERT INTO "saleh" VALUES(21,52,'21:52','ئۆز ۋاقتىدا ئىبراھىم (كۇففارلار چوقۇنۇۋاتقان بۇتلارنى مەنسىتمەي) ئاتىسىغا ۋە قەۋمىگە: « سىلەر چوقۇنۇۋاتقان بۇ بۇتلار نېمە؟ (يەنى نېمە ئۈچۈن بۇ بۇتلارغا چوقۇنىسىلەر؟)» دېدى.');
INSERT INTO "saleh" VALUES(21,53,'21:53','ئۇلار: «ئاتا ـ بوۋىلىرىمىزنىڭ ئۇلارغا ئىبادەت قىلىپ كېلىۋاتقانلىقىنى بىلىمىز (يەنى ئۇلارغا ئاتا ـ بوۋىلىرىمىزغا تەقلىد قىلىپ چوقۇنىمىز)» دېدى.');
INSERT INTO "saleh" VALUES(21,54,'21:54',' ئىبراھىم: «سىلەرمۇ، ئاتا ـ بوۋاڭلارمۇ (پايدا ـ زىيان يەتكۈزەلمەيدىغان، ئاڭلىمايدىغان، جانسىز بۇتلارغا چوقۇنغانلىقىڭلار ئۈچۈن) ھەقىقەتەن روشەن گۇمراھلىقتا سىلەر» دېدى. ');
INSERT INTO "saleh" VALUES(21,55,'21:55','ئۇلار: «سەن راست دەۋاتامسەن ياكى چاقچاق قىلىۋاتامسەن؟» دېدى.');
INSERT INTO "saleh" VALUES(21,56,'21:56','ئىبراھىم ئېيتتى: «ئۇنداق ئەمەس، سىلەرنىڭ (ئىبادەت قىلىشىڭلارغا لايىق) پەرۋەردىگارىڭلار ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارىدۇر، ئۇلارنى ئۇ ياراتقان، مەنمۇ ئۇنىڭغا (قەتئىي دەلىللەر بىلەن) گۇۋاھلىق قىلغۇچىلاردىنمەن. ');
INSERT INTO "saleh" VALUES(21,57,'21:57','ئاللاھ بىلەن قەسەمكى، سىلەر قايتىپ كەتكەندىن كېيىن چوقۇم بۇتلىرىڭلارنى (چېقىۋېتىشنىڭ) ئامالىنى قىلىمەن».');
INSERT INTO "saleh" VALUES(21,58,'21:58',' ئىبراھىم بۇتلارنى چېقىپ پارچە ـ پارچە قىلىۋەتتى، ئۇلارنىڭ قايتىپ كېلىپ (بۇتلارنى كىمنىڭ چاققانلىقىنى سورىشى ئۈچۈن) پەقەت چوڭ بۇتنى (چاقماي) قالدۇردى.');
INSERT INTO "saleh" VALUES(21,59,'21:59','(ئۇلار بايرىمىدىن قايتىپ كېلىپ بۇتلىرىنىڭ چېقىۋېتىلگەنلىكىنى كۆرۈپ) «بۇتلىرىمىزنى مۇنداق قىلغان كىم؟ ئۇ ھەقىقەتەن زالىم ئىكەن» دېدى.');
INSERT INTO "saleh" VALUES(21,60,'21:60',' (ئۇلارنىڭ بەزىسى) «ئىبراھىم ئاتلىق بىر يىگىتنىڭ ئۇلارنى ئەيىبلىگەنلىكىنى ئاڭلىغان ئىدۇق (يەنى شۇ قىلغان بولۇشى مۇمكىن)» دېدى.');
INSERT INTO "saleh" VALUES(21,61,'21:61','ئۇلار (يەنى نەمرۇد ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرى): «ئىبراھىمنى كىشىلەرنىڭ ئالدىغا ئېلىپ كېلىڭلار، (ئىبراھىمنىڭ بۇتلارنى ئەيىبلىگەنلىكىگە) ئۇلار گۇۋاھلىق بەرسۇن» دېدى).');
INSERT INTO "saleh" VALUES(21,62,'21:62',' ئۇلار: «ئى ئىبراھىم! بۇتلىرىمىزنى مۇشۇنداق قىلغان سەنمۇ؟» دېدى. ');
INSERT INTO "saleh" VALUES(21,63,'21:63','(ئىبراھىم: «(ياق) بەلكى ئۇلارنىڭ مۇنۇ چوڭى شۇنداق قىلدى، ئەگەر ئۇلار سۆزلىيەلىسە، (كىمنىڭ چاققانلىقىنى) ئۇلارنىڭ ئۆزلىرىدىن سوراپ بېقىڭلار!» دېدى.');
INSERT INTO "saleh" VALUES(21,64,'21:64','ئۇلار بىر ـ بىرىنى ئەيىبلەپ: «(سىلەر بۇتلارغا چوقۇنۇشتا) ھەقىقەتەن زالىمسىلەر» دېدى. ');
INSERT INTO "saleh" VALUES(21,65,'21:65','ئاندىن ئۇلار (خىجالەتچىلىكتىن) باشلىرىنى تۆۋەن سېلىپ: «ئۇلارنىڭ گەپ قىلالمايدىغانلىقىنى سەن ئوبدان بىلىسەنغۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(21,66,'21:66',' ئىبراھىم ئېيتتى: «سىلەر ئاللاھنى قويۇپ سىلەرگە ھېچ پايدا يەتكۈزەلمەيدىغان ۋە زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنامسىلەر؟»');
INSERT INTO "saleh" VALUES(21,67,'21:67',' سىلەرگە ۋە ئاللاھنى قويۇپ سىلەر چوقۇنۇۋاتقان بۇتلارغا تۈفى! سىلەر چۈشەنمەمسىلەر؟»');
INSERT INTO "saleh" VALUES(21,68,'21:68','ئۇلار ئېيتتى: «ئىبراھىمنى كۆيدۈرۈڭلار، ئىلاھلىرىڭلارغا ياردەم بېرىڭلار، ئەگەر ئۇلارغا ھەقىقىي ياردەم بەرمەكچى بولساڭلار».');
INSERT INTO "saleh" VALUES(21,69,'21:69','بىز ئوتقا: «ئى ئوت! ئىبراھىمغا سالقىن ۋە ئامانلىق (بەخش ئېتىدىغان) بولۇپ بەرگىن» دېدۇق.');
INSERT INTO "saleh" VALUES(21,70,'21:70','ئۇلار ئىبراھىمغا زىيانكەشلىك قىلماقچى بولدى، بىز ئۇلارنى ئەڭ زىيان تارتقۇچىلار قىلدۇق.');
INSERT INTO "saleh" VALUES(21,71,'21:71','بىز ئىبراھىم بىلەن لۇتنى قۇتقۇزدۇق، ئۇلارنى بىز ئەھلى جاھان ئۈچۈن بەرىكەتلىك قىلغان زېمىنغا كۆچۈردۇق.');
INSERT INTO "saleh" VALUES(21,72,'21:72','بىز ئۇنىڭغا (يەنى ئىبراھىمغا) ئىسھاقنى بەردۇق، (ئۇ پەرۋەردىگارىدىن سورىمىسىمۇ) يەئقۇبنى ئوشۇق بەردۇق (يەنى يەئقۇب ئەلەيھىسسالامنى نەۋرە قىلىپ ياكى ئىبراھىم ئەلەيھىسسالامنىڭ تەلىۋىدىن زىيادە قىلىپ بەردۇق)، ئۇلارنىڭ ھەممىسىنى ياخشى ئادەملەر قىلدۇق.');
INSERT INTO "saleh" VALUES(21,73,'21:73',' ئۇلارنى بىزنىڭ ئەمرىمىز بويىچە (كىشىلەرنى بىزنىڭ دىنىمىزغا) يېتەكلەيدىغان پېشۋالار (يەنى پەيغەمبەرلەر) قىلدۇق، ئۇلارغا بىز ياخشى ئىشلارنى قىلىشنى، ناماز ئوقۇشنى، زاكات بېرىشنى ۋەھىي قىلدۇق، ئۇلار بىزگە خالىس ئىبادەت قىلاتتى.');
INSERT INTO "saleh" VALUES(21,74,'21:74',' لۇتقا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق (لۇت ئىبراھىم ئەلەيھىسسالامغا ئىمان ئېيتقان، ئەگەشكەن ۋە ئۇنىڭ بىلەن ھىجرەت قىلغان ئىدى)، ئۇنى (ئاھالىسى بەچچىۋازلىق، قاراقچىلىق قاتارلىق) يامان قىلىقلارنى قىلىدىغان شەھەردىن قۇتقۇزدۇق، ھەقىقەتەن ئۇلار يامان، پاسىق قەۋم ئىدى. ');
INSERT INTO "saleh" VALUES(21,75,'21:75','بىز ئۇنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇ ھەقىقەتەن ياخشىلاردىن ئىدى. ');
INSERT INTO "saleh" VALUES(21,76,'21:76',' نۇھنىڭ (قىسسەسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ (مەزكۇر پەيغەمبەرلەردىن) بۇرۇن (قەۋمىگە، پەرۋەردىگارىم يەر يۈزىدە كۇففارلاردىن بىرنىمۇ قويمىغىن، دەپ بەتدۇئا قىلغان ئىدى)، بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى ۋە ئۇنىڭ ئائىلىسىدىكىلەرنى (ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى) بۈيۈك بالادىن (يەنى توپان بالاسىدا غەرق بولۇشتىن) قۇتقۇزدۇق (يەنى نۇھنى قەۋمىنىڭ شەررىدىن قۇتقۇزۇپ، ئۇلارنى ھالاك قىلدۇق).');
INSERT INTO "saleh" VALUES(21,77,'21:77',' بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە قارشى ئۇنىڭغا ياردەم بەردۇق، ئۇلار ھەقىقەتەن يامان قەۋم ئىدى. ئۇلارنىڭ (بىرىنىمۇ قويماي) ھەممىسىنى غەرق قىلدۇق. ');
INSERT INTO "saleh" VALUES(21,78,'21:78',' داۋۇت بىلەن سۇلايماننىڭ (قىسسەسىنى بايان قىلغىن). ئۆز ۋاقتىدا بىر قەۋمنىڭ قويى كېچىسى زىرائەتنى يەپ (بۇزۇۋەتكەندە)، ئۇ ئىككىسى زىرائەت توغرىسىدا ھۆكۈم چىقارغان ئىدى. ئۇلارنىڭ ھۆكمىگە بىز شاھىت ئىدۇق.');
INSERT INTO "saleh" VALUES(21,79,'21:79','قانداق ھۆكۈم قىلىشنى بىز سۇلايمانغا بىلدۈردۇق، ئۇلارنىڭ ھەر بىرىگە ھېكمەتنى ۋە ئىلىمنى ئاتا قىلدۇق. تاغلارنى، قۇشلارنى داۋۇد بىلەن تەسبىھ ئېيتىشقا بويسۇندۇرۇپ بەردۇق، (شۇنداق قىلىشقا) بىز قادىر ئىدۇق.');
INSERT INTO "saleh" VALUES(21,80,'21:80','جەڭلەردە سىلەرنى (يارىدار بولۇشتىن) ساقلاش مەقسىتىدە، داۋۇدقا سىلەر ئۈچۈن ساۋۇت ياساشنى ئۆگەتتۇق، سىلەر (بۇنىڭغا) شۈكۈر قىلامسىلەر؟ (يەنى شۈكۈر قىلىڭلار ۋە نېمەتنىڭ قەدرىنى بىلىڭلار)');
INSERT INTO "saleh" VALUES(21,81,'21:81','سۇلايمانغا قاتتىق چىقىدىغان شامالنى بويسۇندۇرۇپ بەردۇق، شامال سۇلايماننىڭ ئەمرى بويىچە، بىز بەرىكەتلىك قىلغان زېمىنغا (يەنى شامغا) باراتتى، بىز ھەممىنى بىلگۈچىمىز.');
INSERT INTO "saleh" VALUES(21,82,'21:82','سۇلايمانغا يەنە بىر قىسىم شەيتانلارنى بويسۇندۇرۇپ بەردۇق، ئۇلار دېڭىزلارغا چۆكۈپ ئۇنىڭغا ئۈنچە ـ مارجان (گۆھەرلەرنى سۈزۈپ چىقاتتى)، ئۇلار يەنە (شەھەرلەرنى، ئېگىز سارايلارنى سېلىشتەك) باشقا ئىشلارنىمۇ قىلاتتى، بىز ئۇلارنى (سۇلايماننىڭ ئىتائىتىدىن چىقىشتىن) ساقلايتتۇق.');
INSERT INTO "saleh" VALUES(21,83,'21:83','(ئەييۇبنىڭ قسسەسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ پەرۋەردىگارىغا: «ھەقىقەتەن مېنى بالا (يەنى قاتتىق كېسەل) ئورىۋالدى، سەن ئەڭ مەرھەمەتلىكسەن» دەپ دۇئا قىلدى.');
INSERT INTO "saleh" VALUES(21,84,'21:84','ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭ بېشىغا كەلگەن بالانى كۆتۈرۈۋەتتۇق، ئۇنىڭغا رەھىم قىلغانلىقىمىز، ئىبادەت قىلغۇچىلارغا ئىبرەت قىلغانلىقىمىز ئۈچۈن، ئۇنىڭغا ئائىلىسىنى ۋە ئۇنىڭغا ئوخشاش بىر باراۋەرنى زىيادە ئاتا قىلدۇق (يەنى ئۇنىڭ ئۆلگەن بالىلىرىنىڭ ۋە يوقالغان ماللىرىنىڭ ئورنىغا بىر ھەسسە كۆپ بالا، بىر ھەسسە كۆپ مال بەردۇق).');
INSERT INTO "saleh" VALUES(21,85,'21:85',' ئىسمائىل، ئىدرىس ۋە زۇلكىفللارنىڭ (قىسسەسىنى بايان قىلغىن). (ئۇلارنىڭ) ھەممىسى سەۋرلىك ئىدى (يەنى ئاللاھقا ئىبادەت قىلىشتا چىداملىق ئىدى ۋە گۇناھلار ئالدىدا ئۆزلىرىنى تۇتۇۋالاتتى).');
INSERT INTO "saleh" VALUES(21,86,'21:86',' ئۇلارنى رەھمىتىمىز دائىرىسىگە كىرگۈزدۇق، ئۇلار ھەقىقەتەن ياخشىلاردىن ئىدى.');
INSERT INTO "saleh" VALUES(21,87,'21:87','يۇنۇسنىڭ (قىسسەسىنى بايان قىلغىن) . ئۆز ۋاقتىدا ئۇ (قەۋمىنىڭ ئىمان ئېيتمىغانلىقىدىن) خاپا بولۇپ (شەھىرىدىن) چىقىپ كەتكەن ئىدى. ئۇ بىزنى (بېلىقنىڭ قارنىدا) ئۆزىنى قىسمايدۇ دەپ ئويلىدى، ئۇ قاراڭغۇلۇقتا (يەنى بېلىقنىڭ قارنىدا): «(پەرۋەردىگارىم) سەندىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، سەن (جىمى كەمچىلىكلەردىن) پاكتۇرسەن، مەن ھەقىقەتەن (ئۆز نەپسىمگە) زۇلۇم قىلغۇچىلاردىن بولدۇم» دەپ نىدا قىلدى.');
INSERT INTO "saleh" VALUES(21,88,'21:88','ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنى (بېلىق يۇتقان چاغدىكى) غەمدىن خالاس قىلدۇق، شۇنىڭدەك مۆمىنلەرنى (ئېغىرچىلىقلاردىن) خالاس تاپقۇزىمىز.');
INSERT INTO "saleh" VALUES(21,89,'21:89','زەكەرىيانىڭ (قىسسەسىنى بايان قىلغىن). ئۆز ۋاقتىدا ئۇ: «ئى پەرۋەردىگارىم! مېنى (بالىسىز، ۋارىسسىز) يالغۇز قويمىساڭ، سەن ئەڭ ياخشى ۋارىستۇرسەن (يەنى ھەممە ئادەم ئۆلىدۇ، سەنلا باقىي قالىسەن)» دەپ نىدا قىلدى.');
INSERT INTO "saleh" VALUES(21,90,'21:90',' بىز ئۇنىڭ دۇئاسىنى ئىجابەت قىلدۇق، ئۇنىڭغا يەھيانى ئاتا قىلدۇق، ئۇنىڭغا خوتۇنىنى تۇغىدىغان قىلىپ بەردۇق. (يۇقىرىقى پەيغەمبەرلەرنىڭ دۇئاسىنى ئىجابەت قىلىشىمىز) شۇنىڭ ئۈچۈن ئىدىكى، ئۇلار ياخشى ئىشلارنى قىلىشقا ئالدىرايتتى، (رەھمىتىمىزنى) ئۈمىد قىلىپ، (ئازابىمىزدىن) قورقۇپ بىزگە دۇئا قىلاتتى، بىزگە كەمتەرلىك بىلەن ئىبادەت قىلاتتى.');
INSERT INTO "saleh" VALUES(21,91,'21:91',' نومۇسىنى ساقلىغان ئايالنىڭ (يەنى مەريەمنىڭ قىسسەسىنى بايان قىلغىن) ئۇنىڭغا بىزنىڭ تەرىپىمىزدىن بولغان روھنى پۈۋلىدۇق (پۈۋلەنگەن روھ ئۇنىڭ ئىچىگە كىرىپ، ئۇ ئىيساغا ھامىلدار بولدى)، ئۇنى ۋە ئوغلىنى (يەنى ئىيسا بىلەن مەريەمنى) ئەھلى جاھان ئۈچۈن (بىزنىڭ قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىل قىلدۇق.');
INSERT INTO "saleh" VALUES(21,92,'21:92','ئىنسانلار!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر. مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، ماڭىلا ئىبادەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(21,93,'21:93','ئۇلار (دىندا ئىختىلاپ قىلىشىپ) پىرقە ـ پىرقىلەرگە بۆلۈنۈپ كەتتى، ھەممىسى بىزنىڭ دەرگاھىمىزغا قايتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(21,94,'21:94','كىمكى مۆمىن بولۇپ ياخشى ئەمەللەرنى قىلىدىكەن، ئۇنىڭ ئەمەلىنىڭ (ساۋابى) زايا كەتمەيدۇ. ئۇنىڭ ئەمەلىنى (يەنى نامە ـ ئەمالىنى) چوقۇم خاتىرىلەپ قويىمىز.');
INSERT INTO "saleh" VALUES(21,95,'21:95',' بىز ھالاك قىلغان شەھەر (ئاھالىسى) نىڭ دۇنياغا قايتىشى مۇمكىن ئەمەس.');
INSERT INTO "saleh" VALUES(21,96,'21:96','يەجۇج ـ مەجۈج (توسمىسى) ئېچىۋېتىلگەندە، ئۇلار زېمىننىڭ ھەر بىر تۆپىلىكلىرىدىن ئالدىراپ چىقىپ كېلىدۇ. ');
INSERT INTO "saleh" VALUES(21,97,'21:97','راست ۋەدە (يەنى قىيامەتنىڭ ۋاقتى) يېقىنلاشتى، ئۇ كۈندە كاپىرلارنىڭ كۆزلىرى چەكچىيىپ قالىدۇ، (ئۇلار) «ۋاي ئىسىت! بۇنىڭدىن بىز غەپلەتتە قاپتۇق، بەلكى بىز ئۆزىمىزگە زۇلۇم قىپتۇق» (دېيىشىدۇ).');
INSERT INTO "saleh" VALUES(21,98,'21:98','(ئى مۇشرىكلار!) ھەقىقەتەن سىلەر ئاللاھنى قويۇپ ئىبادەت قىلغان نەرسەڭلار بىلەن دوزاخقا يېقىلغۇ بولىسىلەر، سىلەر دوزاخقا كىرىسىلەر. ');
INSERT INTO "saleh" VALUES(21,99,'21:99','ئەگەر سىلەر ئىبادەت قىلىۋاتقان مەبۇدلار ئىلاھلار بولىدىغان بولسا، دوزاخقا كىرمىگەن بولاتتى (ئىبادەت قىلغۇچى ۋە ئىبادەت قىلىنغۇچىلارنىڭ) ھەممىسى دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(21,100,'21:100','ئۇلار دوزاختا ھەمىشە پەرياد چېكىدۇ، (ئازابنىڭ قاتتىقلىقىدىن) ئۇلار دوزاختا ھېچ نەرسىنى ئاڭلىمايدۇ.');
INSERT INTO "saleh" VALUES(21,101,'21:101',' بىز تەرەپتىن بېرىلگەن بەخت ـ سائادەتكە تېگىشلىك بولغانلار (يەنى تائەت ـ ئىبادەتكە تەۋپىق بېرىلىپ توغرا يولدا ئىبادەت قىلغان سائادەتمەن بەندىلەر) دوزاختىن يىراق قىلىنىدۇ. ');
INSERT INTO "saleh" VALUES(21,102,'21:102','ئۇلار دوزاخنىڭ شەپىسىنى ئاڭلىمايدۇ، كۆڭلى تارتقان نازۇ - نېمەتلەر ئىچىدە مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(21,103,'21:103','چوڭ قورقۇنچ (يەنى قىيامەتنىڭ دەھشىتى) ئۇلارنى قايغۇغا سالمايدۇ. پەرىشتىلەر: «ئاللاھ سىلەرگە ۋەدە قىلغان كۈن مۇشۇ» (دەپ)، ئۇلارنى (جەننەتنىڭ دەرۋازىلىرى ئالدىدا) قارشى ئالىدۇ(103). ');
INSERT INTO "saleh" VALUES(21,104,'21:104','ئۇ كۈندە ئاسماننى (پۈتۈكچى) خەت يېزىلغان قەغەزنى يۆگىگەندەك يۆگەيمىز. مەخلۇقاتلارنى دەسلەپتە قانداق ياراتقان بولساق، شۇ ھالىتىدە ئەسلىگە قايتۇرىمىز. (بۇ) بىز ئۈستىمىزگە ئالغان ۋەدىدۇر، (ئۇنى) چوقۇم ئىشقا ئاشۇرىمىز.');
INSERT INTO "saleh" VALUES(21,105,'21:105',' بىز لەۋھۇلمەھپۇزدا (ئەزەلدە) يازغاندىن كېيىن، (داۋۇدقا نازىل قىلىنغان) زەبۇردا زېمىنغا ھەقىقەتەن مېنىڭ ياخشى (يەنى ۋارىسلىق قىلىشقا لاياقەتلىك) بەندىلىرىم ۋارىسلىق قىلىدۇ دەپ يازدۇق.');
INSERT INTO "saleh" VALUES(21,106,'21:106',' بۇ (قۇرئان) دا ئاللاھقا ئىبادەت قىلغۇچى قەۋم ئۈچۈن (خەۋەرلەر، ۋەز ـ نەسىھەتلەردىن) ئەلۋەتتە يېتەرلىك بار. ');
INSERT INTO "saleh" VALUES(21,107,'21:107',' (ئى مۇھەممەد!) سېنى بىز پۈتۈن ئەھلى جاھان ئۈچۈن پەقەت رەھمەت قىلىپلا ئەۋەتتۇق. ');
INSERT INTO "saleh" VALUES(21,108,'21:108','(ئى مۇھەممەد! مۇشرىكلارغا) «ماڭا ۋەھىي قىلىندىكى، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، سىلەر بويسۇنامسىلەر؟» دېگىن.');
INSERT INTO "saleh" VALUES(21,109,'21:109','ئەگەر ئۇلار يۈز ئۆرۈسە، ئېيتقىنكى، «مەن سىلەرگە (ھەقىقەتنى ھېچ كىشىنى ئايرىماستىن) ئوخشاش ئۇقتۇردۇم، سىلەرگە ۋەدە قىلىنغان (ئازاب) يېقىنمۇ، يىراقمۇ؟ ئۇقمايمەن.');
INSERT INTO "saleh" VALUES(21,110,'21:110','ئاللاھ ھەقىقەتەن ئاشكارا سۆزنىمۇ، سىلەرنىڭ يوشۇرغانلىرىڭلارنىمۇ بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(21,111,'21:111',' بىلمەسمەنكى، ئۇ (يەنى سىلەرگە بېرىلىدىغان جازانىڭ كېچىكىشى) سىلەر ئۈچۈن سىناق ۋە ۋاقىتلىق پايدىلىنىش بولۇشى مۇمكىن».');
INSERT INTO "saleh" VALUES(21,112,'21:112',' (پەيغەمبەر ئېيتتى) «پەرۋەردىگارىم! (مېنىڭ بىلەن مەككە كۇففارلىرىنىڭ ئارىسىدا) ئادىللىق بىلەن ھۆكۈم چىقارغىن، بىزنىڭ پەرۋەردىگارىمىز مەرھەمەتلىك ئاللاھتۇر، سىلەرنىڭ بوھتان سۆزلىرىڭلارغا قارشى ئاللاھتىن ياردەم تىلەيمەن».');
INSERT INTO "saleh" VALUES(22,1,'22:1','ئى ئىنسانلار! پەرۋەردىگارىڭلاردىن (يەنى ئازابىدىن) قورقۇڭلار، قىيامەتنىڭ زىلزىلىسى ھەقىقەتەن چوڭ ئىشتۇر.');
INSERT INTO "saleh" VALUES(22,2,'22:2',' ئۇ كۈندە ھەر بىر سۈت ئېمىتىۋاتقان ئايال (قاتتىق قورقۇنچتىن تېڭىرقاپ قېلىش بىلەن) ئېمىتىۋاتقان بوۋىقىنى ئۇنتۇيدۇ، ھەر بىر ھامىلىدار ئايالنىڭ بويىدىن ئاجراپ كېتىدۇ، ئىنسانلارنى مەست كۆرۈسەن، ھالبۇكى، ئۇلار (ھەقىقەتتە) مەست ئەمەس (دېمەك، قىيامەتنىڭ دەھشىتى ئۇلارنى ئەس ـ ھوشىدىن كەتكۈزۈۋەتكەن)، لېكىن ئاللاھنىڭ ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(22,3,'22:3','بەزى كىشىلەر ئاللاھ (نىڭ قۇدرىتى ۋە سۈپەتلىرى) ئۈستىدە ھېچقانداق دەلىلسىز مۇنازىرىلىشىدۇ، ھەر بىر سەركەش شەيتانغا (يەنى ھەقتىن توسقۇچى كۇففارلارنىڭ كاتتىباشلىرىغا) ئەگىشىدۇ. ');
INSERT INTO "saleh" VALUES(22,4,'22:4','ئاللاھ ھۆكۈم قىلىدۇكى، كىمكى شەيتانغا ئەگىشىدىكەن، شەيتان ئۇنى ئازدۇرىدۇ ۋە جەھەننەمنىڭ ئازابىغا سۆرەيدۇ.');
INSERT INTO "saleh" VALUES(22,5,'22:5','ئى ئىنسانلار! ئەگەر سىلەر قايتا تىرىلىشتىن شەكلەنسەڭلار (گۇمانىڭلارنىڭ تۈگىشى ئۈچۈن ئەسلىدىكى يارىتىلىشىڭلارغا قاراڭلار)، بىز سىلەرگە (ئاللاھنىڭ قۇدرىتىنى بايان قىلىش ئۈچۈن ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، ئۇنىڭ ئەۋلادىنى (ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن، ئاندىن شەكىلگە كىرگۈزۈلگەن ۋە كىرگۈزۈلمىگەن پارچە گۆشتىن ياراتتۇق. بىز خالىغان ئادەمنى بەچچىداندا مۇئەييەن مۇددەتكىچە قالدۇرىمىز، ئاندىن سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاقلىق ھالىتىڭلاردا چىقىرىمىز (ئاندىن سىلەرنى ئاستا ـ ئاستا كۈچ ـ قۇۋۋەتكە تولدۇرىمىز)، سىلەر (كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، بەزىڭلار ياشلىقىڭلاردا ۋاپات بولۇپ كېتىسىلەر، بەزىڭلار، (ئىلگىرى) بىلگەندىن كېيىن ھېچ نەرسىنى بىلمەس ھالىتىڭلارغا قايتۇرۇلۇشىڭلار ئۈچۈن، ناھايىتى قېرىپ كەتكۈچە (ھايات) قالدۇرۇلىسىلەر، (ئاللاھ تائالانىڭ ئۆلگەنلەرنىڭ قايتا تىرىلدۈرۈشكە قادىر ئىكەنلىكىنى كۆرسىتىدىغان يەنە بىر دەلىل شۇكى) سەن زېمىننى قاقاس كۆرۈسەن، ئۇنىڭغا بىز يامغۇر ياغدۇرساق، ئۇ جانلىنىدۇ ۋە كۆپۈشىدۇ، تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۈندۈرۈپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(22,6,'22:6',' بۇ (يەنى ئاللاھ تائالانىڭ ئىنساننى ۋە ئۆسۈملۈكلەرنى يۇقىرىقىدەك يارىتىشى)، ئاللاھنىڭ ھەق ئىكەنلىكىنى، ئۆلۈكلەرنى تىرىلدۈرەلەيدىغانلىقىنى ۋە ھەر نەرسىگە قادىر ئىكەنلىكىنى سىلەرگە بىلدۈرۈش ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(22,7,'22:7','شۇنداقلا قىيامەتنىڭ شەكسىز بولىدىغانلىقىنى، قەبرىلەردىكى ئۆلۈكلەرنى (ئۆلۈك زېمىننى ئۆسۈملۈكلەرنى ئۈندۈرۈش بىلەن تىرىلدۈرگەندەك) ئاللاھنىڭ تىرىلدۈرىدىغانلىقىنى بىلدۈرۈش ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(22,8,'22:8','بەزىلەر (توغرا تونۇشقا ئېلىپ بارىدىغان) ھېچقانداق ھەقىقىي ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا ئاللاھ توغرىسىدا مۇنازىرىلىشىدۇ، (ئۇنىڭ مۇنازىرىلىشىشى نوقۇل نەپسى - خاھىشى ۋە تەرسالىقىدىندۇر).');
INSERT INTO "saleh" VALUES(22,9,'22:9',' ئۇ (كىشىلەرنى) ئاللاھنىڭ يولىدىن ئازدۇرۇش ئۈچۈن مەغرۇرانە يۈرىدۇ، ئۇ دۇنيادا خارلىققا دۇچار بولىدۇ، ئاخىرەتتە ئۇنىڭغا كۆيدۈرگۈچى ئوتنىڭ ئازابىنى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(22,10,'22:10','(ئۇنىڭغا): «(خارلىق بىلەن ئازاب) سېنىڭ قىلمىشلىرىڭ يەنى كۇفرىڭ بىلەن گۇمراھلىقىڭ) تۈپەيلىدىندۇر، ئاللاھ بەندىلىرىگە ھەرگىز زۇلۇم قىلغۇچى ئەمەستۇر» (دېيىلىدۇ). ');
INSERT INTO "saleh" VALUES(22,11,'22:11','بەزىلەر ئاللاھقا شەك ـ شۈبھە بىلەن (يەنى تۇراقسىز ھالدا) ئىبادەت قىلىدۇ، ئەگەر ئۇنىڭغا ياخشىلىق يەتسە دىنىدا تۇرىدۇ (بۇنداق ئادەم ئاللاھقا دىلى بىلەن ئەمەس، پەقەت تىلى بىلەن ئىبادەت قىلىدۇ)، ئەگەر ئۇنىڭغا بەختسىزلىك يەتسە دىنىدىن يېنىۋالىدۇ، ئۇ دۇنيا ۋە ئاخىرەتتە زىيان تارتىدۇ، بۇ روشەن زىياندۇر. ');
INSERT INTO "saleh" VALUES(22,12,'22:12',' ئۇ ئاللاھنى قويۇپ پايدا ـ زىيان يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، بۇ چوڭقۇر گۇمراھلىقتۇر. ');
INSERT INTO "saleh" VALUES(22,13,'22:13',' ئۇ ئەلۋەتتە زىيىنى پايدىسىدىن يېقىن بولغان نەرسىگە چوقۇنىدۇ، (ئۇ مەبۇد) راستتىنلا يامان مەدەتكاردۇر، راستتىنلا يامان ھەمراھتۇر.');
INSERT INTO "saleh" VALUES(22,14,'22:14',' شۈبھىسىزكى، ئاللاھ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئاللاھ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى خالىغان ئادەمگە ساۋاب بېرىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ).');
INSERT INTO "saleh" VALUES(22,15,'22:15',' كىمكى ئاللاھ پەيغەمبەرگە دۇنيا ۋە ئاخىرەتتە ھەرگىز ياردەم بەرمەيدۇ دەپ گۇمان قىلىدىكەن، ئۇ تورۇسقا ئارغامچا سېلىپ بوغۇلۇپ ئالسۇن، قارىسۇنكى، ئۇنىڭ (بۇ) تەدبىرى غەزىپىنى باسالامدۇ؟ (يەنى ئاللاھ پەيغەمبىرى مۇھەممەد ئەلەيھىسسالامغا ياردەم بەرمەيدۇ، دەپ ئويلايدىكەن، ئۇ ئېسىلىپ ئۆلۈۋالسۇن، ئەگەر بۇ ئۇنىڭ خاپىلىقىنى باسالايدىغان بولسا. چۈنكى ئاللاھ پەيغەمبەرگە چوقۇم ياردەم بېرىدۇ). ');
INSERT INTO "saleh" VALUES(22,16,'22:16','شۇنىڭغا ئوخشاش (يەنى ئىلگىرىكى كىتابلارنى نازىل قىلغىنىمىزغا ئوخشاش)، قۇرئاننى (مۇھەممەد ئەلەيھىسسالامغا) روشەن ئايەتلەر قىلىپ نازىل قىلدۇق، ئاللاھ خالىغان ئادەمنى ھىدايەت قىلىدۇ (يەنى ئاللاھتىن باشقا ھېچ ھىدايەت قىلغۇچى يوق، ئاللاھ خالىغان ئادەمنى توغرا يولغا باشلايدۇ).');
INSERT INTO "saleh" VALUES(22,17,'22:17',' مۆمىنلەر، يەھۇدىيلار، يۇلتۇزلارغا چوقۇنغۇچىلار، ناسارالار، مەجۇسىيلەر (يەنى ئاتەشپەرەسلەر) ۋە مۇشرىكلار ئۈچۈن قىيامەت كۈنى ئاللاھ ھەقىقەتەن ھۆكۈم چىقىرىدۇ، (يەنى ئاللاھ تائالا مۆمىنلەر بىلەن مەزكۇر بەش پىرقە ئارىسىدا توغرا ھۆكۈم چىقىرىپ، مۆمىنلەرنى جەننەتكە، كۇففارلارنى دوزاخقا كىرگۈزىدۇ)، ئاللاھ ھەقىقەتەن ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,18,'22:18',' بىلمەمسەنكى، ئاسماندىكىلەر (يەنى پەرىشتىلەر)، زېمىندىكىلەر (يەنى ئىنسانلار، جىنلار ۋە زېمىندا ياشايدىغان بارلىق مەخلۇقاتلار)، كۈن، ئاي، يۇلتۇزلار، تاغلار، دەرەخلەر، ھايۋاناتلار ئاللاھقا بويسۇنۇپ، ئاللاھنىڭ تەسەررۇپ قىلىشى بويىچە ھەرىكەت قىلىدۇ) ۋە نۇرغۇن كىشىلەر ئاللاھقا سەجدە قىلىدۇ، نۇرغۇن كىشىلەر (كۇفرى سەۋەبلىك) ئازابقا دۇچار بولدى، ئاللاھنىڭ خارلىشىغا ئۇچرىغان ئادەمگە ھۆرمەتلىگۈچى چىقمايدۇ، ئاللاھ ھەقىقەتەن خالىغىنىنى قىلىدۇ (يەنى ئازاب قىلىش، رەھىم قىلىش، ئەزىز قىلىش، خار قىلىش، باي قىلىش، كەمبەغەل قىلىش ئاللاھنىڭ خاھىشىدىكى ۋە ھېكمىتىدىكى ئىش بولۇپ، ئاللاھقا ھېچ كىشى تەئەررۇز قىلالمايدۇ).');
INSERT INTO "saleh" VALUES(22,19,'22:19',' (مۆمىنلەر ۋە كۇففارلاردىن ئىبارەت) بۇ ئىككى (پىرقە) پەرۋەردىگارى توغرىسىدا مۇنازىرىلەشتى (يەنى مۆمىنلەر بىلەن كۇففارلار ئاللاھنىڭ دىنى ئۈستىدە بەس ـ مۇنازىرە قىلىشىپ، مۆمىنلەر ئاللاھنىڭ دىنىغا ياردەم بەرمەكچى بولدى، كۇففارلار ئاللاھنىڭ نۇرىنى ئۆچۈرمەكچى بولدى)، كاپىرلارغا ئوتتىن كىيىملەر پىچىلىدۇ، ئۇلارنىڭ باشلىرى ئۈستىدىن يۇقىرى ھارارەتلىك قايناقسۇ قۇيۇلىدۇ.');
INSERT INTO "saleh" VALUES(22,20,'22:20',' ئۇنىڭ بىلەن ئۇلارنىڭ ئىچ -باغرى ۋە تېرىلىرى ئېرىتىلىدۇ. ');
INSERT INTO "saleh" VALUES(22,21,'22:21','ئۇلار تۆمۈر توقماقلار بىلەن ئۇرۇلىدۇ. ');
INSERT INTO "saleh" VALUES(22,22,'22:22','ھەر قاچان ئۇلار (يېتىۋاتقان) غەم ـ قايغۇنىڭ قاتتىقلىقىدىن دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ، (ئۇلارغا) كۆيدۈرگۈچى (دوزاخ) ئازابىنى تېتىڭلار (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(22,23,'22:23',' ئاللاھ ھەقىقەتەن مۆمىنلەرنى ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى ۋە مەرۋايىتلارنى زىننەت بۇيۇملىرى قىلىدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ.');
INSERT INTO "saleh" VALUES(22,24,'22:24',' ئۇلار ياخشى سۆزگە (يەنى كەلىمە تەۋھىدكە) ھىدايەت قىلىندى. ئاللاھنىڭ يولىغا ھىدايەت قىلىندى.');
INSERT INTO "saleh" VALUES(22,25,'22:25',' كاپىرلارغا، (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقۇچىلارغا _ ئۇ ئولتۇرۇشلۇق بولسۇن ياكى سىرتتىن كەلگەن بولسۇن _ بىز كىشىلەرگە ئىبادەتگاھ قىلغان مەسجىدى ھەرەمدىن توسقۇچىلارغا (يەنى ھەج پەرزنى ئادا قىلىش ئۈچۈن مەسجىدى ھەرەمگە كەلگەن مۆمىنلەرنى توسقۇچىلارغا) (قاتتىق ئازابنى تېتىتىمىز)، كىمكى مەسجىدى ھەرەمدە زۇلۇم بىلەن گۇناھ قىلماقچى بولىدىكەن، ئۇنىڭغا قاتتىق ئازابنى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(22,26,'22:26',' ئۆز ۋاقتىدا ئىبراھىمغا بەيتۇللاھنىڭ ئورنىنى تەيىنلەپ بەردۇق، (ئۇنىڭغا ئېيتتۇقكى) «ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمىگىن، تاۋاپ قىلغۇچىلارغا، قىيامدا تۇرغۇچىلارغا، رۇكۇ قىلغۇچىلارغا، سەجدە قىلغۇچىلارغا مېنىڭ ئۆيۈمنى پاك قىلغىن.');
INSERT INTO "saleh" VALUES(22,27,'22:27',' كىشىلەرنىڭ ئارىسىدا (ئۇلارنى) ھەجگە چاقىرىپ نىدا قىلغىن، ئۇلار پىيادە ۋە ئورۇق تۆگىلەرگە مىنىپ كېلىدۇ، ئورۇق تۆگىلەر يىراق يوللارنى بېسىپ كېلىدۇ.');
INSERT INTO "saleh" VALUES(22,28,'22:28','كىشىلەر ئۆزلىرىگە تېگىشلىك بولغان (دىنىي ۋە دۇنياۋى) مەنپەئەتلەرنى كۆرسۇن، بەلگىلەنگەن كۈنلەردە (يەنى قۇربانلىق كۈنلىرىدە) ئاللاھ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋا ماللارنى (يەنى تۆگە، قوي، ئۆچكىلەرنى ئاللاھنىڭ نېمەتلىرىگە شۈكۈر قىلىش يۈزىسىدىن) ئاللاھنىڭ ئىسمىنى ئېيتىپ قۇربانلىق قىلسۇن». سىلەر قۇربانلىقىڭلارنىڭ گۆشىدىن يەڭلار، موھتاجقا، پېقىرغا بېرىڭلار.');
INSERT INTO "saleh" VALUES(22,29,'22:29',' ئاندىن ئۇلار كىرلىرىنى تازىلىسۇن (يەنى ئېھرامدىن چىققاندىن كېيىن چاچلىرىنى، تىرناقلىرىنى ئالسۇن)، ئۆز ئۈستىگە ئالغان ئىبادەتلىرىنى ئادا قىلسۇن، قەدىمىي بەيت (يەنى بەيتۇللاھنى) تاۋاپ قىلسۇن.');
INSERT INTO "saleh" VALUES(22,30,'22:30',' (ھەج ئىبادىتى) ئەنە شۇدۇر، كىمكى ئاللاھنىڭ دىنىنىڭ ئەھكاملىرىنى ئۇلۇغلىسا (يەنى ھەج جەريانىدا ئاللاھنىڭ بۇيرۇغانلىرىنى بەجا كەلتۈرۈپ، مەنئى قىلغان ئىشلىرىدىن چەكلەنسە)، بۇ، پەرۋەردىگارنىڭ دەرگاھىدا ئۇنىڭ (دۇنيا ۋە ئاخىرەتلىكى) ئۈچۈن ياخشىدۇر، سىلەرگە چاھارپايلاردىن قۇرئاندا (ھاراملىقى) بايان قىلىنغانلىرىدىن باشقىلىرى ھالال قىلىندى. سىلەر بۇتلاردىن ئىبارەت نىجىستىن ساقلىنىڭلار، يالغان گۇۋاھلىق بېرىشتىن ساقلىنىڭلار.');
INSERT INTO "saleh" VALUES(22,31,'22:31','ئاللاھقا خالىس ئىبادەت قىلىڭلار، ئۇنىڭغا شېرىك كەلتۈرمەڭلار، كىمكى ئاللاھقا شېرىك كەلتۈرىدىكەن، ئۇ گويا ئاسماندىن تاشلىنىپ، قۇشلار ئۇنى ئېلىپ قاچقاندەك ياكى بوران ئۇنى ئېلىپ بېرىپ يىراق جايغا تاشلىۋەتكەندەك بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(22,32,'22:32',' ئىش مانا شۇنداق، كىمكى دىنىي ئىشلارنى (جۈملىدىن ھەجنىڭ ئەمەللىرىنى، قۇربانلىقلارنى) ئۇلۇغلايدىكەن، بۇ، دىللارنىڭ تەقۋادارلىقىدىندۇر.');
INSERT INTO "saleh" VALUES(22,33,'22:33','مۇئەييەن مۇددەتكىچە (يەنى بوغۇزلايدىغان ۋاقىتقىچە قۇربانلىق ماللىرىدىن (سېغىپ، نەسىللەندۈرۈپ، مىنىپ) پايدىلىنىسىلەر، ئاندىن ئۇنى بوغۇزلاشقا تېگىشلىك جاي بەيتۇللاھنىڭ يېنىدۇر (يەنى ھەرەمدۇر).');
INSERT INTO "saleh" VALUES(22,34,'22:34','ھەر ئۈممەت ئاللاھ ئۇلارغا رىزىق قىلىپ بەرگەن چارۋىلارنى (شۈكۈر قىلىش يۈزىسىدىن قۇربانلىق قىلغانلىرىدا) ئاللاھنىڭ ئىسمىنى ئېيتسۇن دەپ، ئۇلارغا قۇربانلىقنى بەلگىلىدۇق، سىلەرنىڭ ئىلاھىڭلار بىر ئىلاھتۇر، ئۇنىڭغا بويسۇنۇڭلار، ئىتائەتمەنلەرگە خۇشخەۋەر بەرگىن. ');
INSERT INTO "saleh" VALUES(22,35,'22:35',' ئۇلار شۇنداق كىشىلەركى ئاللاھ ياد ئېتىلسە، ئۇلارنىڭ دىللىرى قورقۇپ (تىترەيدۇ)، ئۆزلىرىگە يەتكەن ئەزىيەتلەرگە سەۋر قىلىدۇ، ناماز ئۆتەيدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) بېرىدۇ.');
INSERT INTO "saleh" VALUES(22,36,'22:36','(كەبىگە ئېلىپ بېرىلىدىغان) تۆگىنى ئاللاھنىڭ (دىننىڭ) ئالامەتلىرىدىن (يەنى ھەجنىڭ ئەھكاملىرىدىن) قىلدۇق، ئۇلاردا سىلەرگە نۇرغۇن پايدا بار، ئۇلارنى قاتار قىلىپ تۇرغۇزۇپ (يەنى ئۇلارنىڭ ئالدى سول پۇتىنى باغلاپ، ئۈچ پۇت بىلەن تۇرغۇزۇپ بوغۇزلىغىنىڭلاردا) ئاللاھنىڭ ئىسمىنى تىلغا ئېلىڭلار (يەنى بىسمىللاھ دەڭلار)، ئۇلار بوغۇزلىنىپ جېنى چىققاندا، ئۇلارنى يەڭلار، قانائەتچان موھتاجلارغا ۋە سائىللارغا بېرىڭلار، سىلەرنى شۈكۈر قىلسۇن دەپ ئۇ تۆگىلەرنى سىلەرگە بويسۇندۇرۇپ بەردۇق.');
INSERT INTO "saleh" VALUES(22,37,'22:37',' ئاللاھقا ئۇلارنىڭ گۆشلىرى ۋە قانلىرى يېتىپ بارمايدۇ، ئاللاھقا يېتىدىغىنى پەقەت سىلەرنىڭ تەقۋادارلىقىڭلاردۇر، ئاللاھنىڭ سىلەرنىڭ ئۆزىنىڭ ئەھكاملىرىغا يېتەكلىگەنلىكىنى ئۇلۇغلىشىڭلار ئۈچۈن، ئاللاھ ئۇلارنى سىلەرگە ئاشۇنداق بويسۇندۇرۇپ بەردى. ياخشى ئىش قىلغۇچىلارغا (ئاخىرەتتە بەختكە ئېرىشىدىغانلىقى بىلەن) خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(22,38,'22:38','ئاللاھ مۆمىنلەرنى چوقۇم قوغدايدۇ، ئاللاھ ھەر قانداق خىيانەتچى، ناشۈكۈر بەندىنى دوست تۇتمايدۇ. ');
INSERT INTO "saleh" VALUES(22,39,'22:39',' ھۇجۇم قىلىنغۇچىلارغا، زۇلۇمغا ئۇچرىغانلىقلىرى ئۈچۈن، (قارشىلىق كۆرسىتىشكە) رۇخسەت قىلىندى، ئاللاھ ئۇلارغا ياردەم بېرىشكە ئەلۋەتتە قادىر.');
INSERT INTO "saleh" VALUES(22,40,'22:40','(ئۇلار) پەقەت پەرۋەردىگارىمىز ئاللاھ دېگەنلىكلىرى ئۈچۈنلا ئۆز يۇرتلىرىدىن ناھەق ھەيدەپ چىقىرىلدى. ئەگەر ئاللاھ ئىنسانلارنى بىر ـ بىرىگە قارشىلىق كۆرسەتكۈزمىگەن بولسا، راھىبلارنىڭ ئىبادەتخانىلىرى، چېركاۋلار، يەھۇدىيلارنىڭ ئىبادەتخانىلىرى ۋە ئاللاھنىڭ نامى كۆپ يادلىنىدىغان مەسجىدلەر ئەلۋەتتە ۋەيران قىلىناتتى. كىمكى ئاللاھنىڭ دىنىغا ياردەم بېرىدىكەن، ئەلۋەتتە ئاللاھ ئۇنىڭغا ياردەم بېرىدۇ، ئاللاھ ئەلۋەتتە كۈچلۈكتۇر، غالىبتۇر.');
INSERT INTO "saleh" VALUES(22,41,'22:41',' (ئاللاھنىڭ ياردىمىگە ئېرىشىشكە ھەقلىق بولغانلار) شۇنداق كىشىلەردۇركى، ئەگەر ئۇلارنى يەر يۈزىدە ئۈستۈنلۈككە ئىگە قىلساق، نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ، ياخشى ئىشلارغا بۇيرۇيدۇ، يامان ئىشلاردىن توسىدۇ. ئىشلارنىڭ ئاقىۋىتى ئاللاھقا مەنسۇپتۇر (يەنى ھەممە ئىشلار ئاخىرەتتە ئاللاھقا قايتىدۇ).');
INSERT INTO "saleh" VALUES(22,42,'22:42',' ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى)، ئىبراھىمنىڭ قەۋمى، لۇتنىڭ قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن؟[42 ـ 44]');
INSERT INTO "saleh" VALUES(22,43,'22:43',' ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى)، ئىبراھىمنىڭ قەۋمى، لۇتنىڭ قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن؟[42 ـ 44]');
INSERT INTO "saleh" VALUES(22,44,'22:44',' ئەگەر (مەككە مۇشرىكلىرى) سېنى ئىنكار قىلسا (سەن تۇنجى ئىنكار قىلىنغۇچى پەيغەمبەر ئەمەسسەن، سەندىن بۇرۇنقى پەيغەمبەرلەر ئىنكار قىلىنىپ سەۋر قىلىشقان)، ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئاد (خەلقى)، سەمۇد (خەلقى)، ئىبراھىمنىڭ قەۋمى، لۇتنىڭ قەۋمى، مەديەن ئاھالىسى (پەيغەمبەرلىرىنى) ئىنكار قىلغان، مۇسامۇ ئىنكار قىلىنغان، كاپىرلارغا مۆھلەت بەردىم، ئاندىن ئۇلارنى جازالىدىم، مېنىڭ (ئۇلارغا بەرگەن) جازايىم قانداق ئىكەن؟[42 ـ 44]');
INSERT INTO "saleh" VALUES(22,45,'22:45',' نۇرغۇن شەھەر ئاھالىلىرىنى زالىم بولغانلىقلىرى ئۈچۈن ھالاك قىلدۇق. شۇنىڭ بىلەن (شەھەرنىڭ ئۆيلىرىنىڭ) ئۆگزىلىرى ئۆرۈلۈپ، ئۈستىگە تاملىرى يىقىلغان، نۇرغۇن تاشلاندۇق قۇدۇقلار ۋە ئېگىز ئىمارەتلەر (ئاھالىسىز) قالغان.');
INSERT INTO "saleh" VALUES(22,46,'22:46','ئۇلار زېمىندا سەير قىلمىدىمۇ؟ شۇنىڭ بىلەن ئۇلار (يەنى سەير قىلىپ) چۈشىنىدىغان دىللارغا ياكى ئاڭلايدىغان قۇلاقلارغا ئىگە بولمىدىمۇ؟ ھەقىقەتەن كۆزلەرلا كور بولمايدۇ، لېكىن كۆكرەكلەردىكى قەلبلەر كور بولىدۇ (يەنى ھەقىقىي كورلۇق كۆزنىڭ كورلۇقى ئەمەس، دىلنىڭ كورلۇقىدۇر، قەلبى كور ئادەم ئىبرەت ئالمايدۇ ۋە چۈشەنمەيدۇ).');
INSERT INTO "saleh" VALUES(22,47,'22:47',' (ئى مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) (مەسخىرە قىلىش يۈزىسىدىن سەندىن ئازابنىڭ تېز نازىل بولۇشىنى تەلەپ قىلىدۇ (ئازاب چوقۇم نازىل بولىدۇ، لېكىن ئۇنىڭ نازىل بولۇشىنىڭ مۇئەييەن ۋاقتى ـ سائىتى بولىدۇ)، ئاللاھ ۋەدىسىگە ھەرگىز خىلاپلىق قىلمايدۇ (ئاللاھ بەندىلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ھەقىقەتەن پەرۋەردىگارىڭنىڭ دەرگاھىدىكى بىر كۈن سىلەر سانايدىغان مىڭ يىلچىلىكتۇر.');
INSERT INTO "saleh" VALUES(22,48,'22:48','نۇرغۇن شەھەر ئاھالىلىرى زالىم تۇرۇقلۇق، ئۇلارغا مەن مۆھلەت بەردىم (ئۇلار بۇ مۆھلەتكە مەغرۇر بولۇپ كەتتى)، ئاندىن (يەنى ئۇلارغا ئۇزاق مۇددەت مۆھلەت بەرگىنىمدىن كېيىن) ئۇلارنى جازالىدىم، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر (يەنى ئاخىر ھەممىسى ماڭا كېلىدۇ).');
INSERT INTO "saleh" VALUES(22,49,'22:49',' (ئى مۇھەممەد! ئاللاھنىڭ ئازابىنىڭ تېز نازىل بولۇشىنى تەلەپ قىلغۇچىلارغا) ئېيتقىنكى، «ئى ئىنسانلار! سىلەرگە مەن ھەقىقەتەن ئاشكارا ئاگاھلاندۇرغۇچىمەن». ');
INSERT INTO "saleh" VALUES(22,50,'22:50',' ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە ئېسىل رىزىققا (يەنى جەننەتكە) ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(22,51,'22:51','بىزنى ئاجىز بىلىپ ئايەتلىرىمىزنى يوققا چىقىرىش ئۈچۈن تىرىشقۇچىلار ـ ئەنە شۇلار ئەھلى دوزاختۇر.');
INSERT INTO "saleh" VALUES(22,52,'22:52',' بىز سەندىن ئىلگىرى ئەۋەتكەن قايسىبىر رەسۇل، قايسىبىر پەيغەمبەر بولمىسۇن، ئۇ (ئاللاھ نازىل قىلغان ئايەتلەرنى يەتكۈزۈپ قەۋمىنىڭ ھىدايەت تېپىشىنى ئۈمىد قىلىۋاتقاندا)، شەيتان ھامان ئۇنىڭ ئۈمىدىگە (شۈبھە) سالىدۇ. ئاللاھ شەيتاننىڭ سالغان شۈبھىسىنى بەربات قىلغاندىن كېيىن ئۆزىنىڭ ئايەتلىرىنى مۇستەھكەم قىلىدۇ. ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,53,'22:53','(ئاللاھتائالانىڭ مۇنداق قىلىشى) دىللىرىدا كېسەل بارلارنى، دىللىرى قەساۋەتلىشىپ كەتكەنلەرنى (يەنى ئاللاھنىڭ زىكرىگە دىلى ئېرىمەسلەرنى) شەيتاننىڭ سالغان شۈبھىسى بىلەن سىناش ئۈچۈندۇر. زالىملار (يەنى يۇقىرىدىكى مۇناپىقلار، مۇشرىكلار) ھەقىقەتەن (ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە) قاتتىق ئاداۋەتتىدۇر.');
INSERT INTO "saleh" VALUES(22,54,'22:54','(ئاللاھتائالانىڭ مۇنداق قىلىشى) ئىلىم ئەھلىلىرىنىڭ قۇرئاننىڭ پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەت ئىكەنلىكىنى بىلىشلىرى، شۇنىڭ بىلەن ئۇنىڭغا ئىمان ئېيتىشلىرى، دىللىرىنىڭ قۇرئانغا بوي سۇنۇشى ئۈچۈندۇر. ئاللاھ مۆمىنلەرنى ھەقىقەتەن توغرا يولغا باشلىغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,55,'22:55','كاپىرلار تاكى ئۇلارغا قىيامەت ئۇشتۇمتۇت كەلگۈچە ياكى ئۇلارغا قىيامەت كۈنىنىڭ ئازابى يەتكۈچە قۇرئاندىن ھامان شەكلىنىدۇ. ');
INSERT INTO "saleh" VALUES(22,56,'22:56','بۇ كۈندە پادىشاھلىق (يالغۇز) ئاللاھقا خاستۇر، ئاللاھ بەندىلەر ئارىسىدا (ئادىل) ھۆكۈم چىقىرىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار (تۈرلۈك) نازۇ- نېمەتلەر بار جەننەتلەردە بولىدۇ.');
INSERT INTO "saleh" VALUES(22,57,'22:57','كاپىرلار ۋە بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار (دوزاختا) خار قىلغۇچى ئازابقا دۇچار بولىدۇ. ');
INSERT INTO "saleh" VALUES(22,58,'22:58',' ئاللاھنىڭ يولىدا ھىجرەت قىلغانلارغا، ئاندىن (جىھاد قىلىپ) ئۆلتۈرۈلگەنلەرگە ياكى (كېسەل بىلەن) ئۆلگەنلەرگە ئاللاھ چوقۇم ياخشى رىزىق (يەنى جەننەتنىڭ مەڭگۈلۈك نېمەتلىرىنى) ئاتا قىلىدۇ. رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسى ھەقىقەتەن يالغۇز ئاللاھتۇر.');
INSERT INTO "saleh" VALUES(22,59,'22:59',' ئاللاھ ئۇلارنى ئەلۋەتتە ئۇلار مەمنۇن بولىدىغان جايغا (يەنى جەننەتكە) كىرگۈزىدۇ، ئاللاھ ھەقىقەتەن (ئۇلارنى مەمنۇن قىلىدىغان نەرسىلەرنى) بىلگۈچىدۇر، ھەلىمدۇر (يەنى ئۇلارنىڭ دۈشمەنلىرىنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(22,60,'22:60',' ئىش مانا شۇنداق، كىمكى قانچىلىك زۇلۇمغا ئۇچراپ شۇنچىلىك ئىنتىقامىنى ئالغان بولسا، ئاندىن يەنە زۇلۇمغا ئۇچرىسا، ئاللاھ ئۇنىڭغا چوقۇم ياردەم بېرىدۇ، ئاللاھ ھەقىقەتەن ناھايىتى ئەپۇ قىلغۇچىدۇر، ناھايىتى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,61,'22:61',' (شۇ نەرسە ئاللاھنىڭ قۇدرىتىنىڭ ئالامەتلىرىدىندۇركى) ئاللاھ كېچىنى كۈندۈزگە، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە ـ كۈندۈزنىڭ ئۇزۇن ـ قىسقا بولۇشى پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,62,'22:62',' (بۇ قۇدرەت) شۇنىڭ ئۈچۈندۇركى، ئاللاھ ھەقتۇر، (مۇشرىكلارنىڭ) ئاللاھنى قويۇپ ئىبادەت قىلىدىغان نەرسىلىرى (يەنى بۇتلىرى) باتىلدۇر، ئاللاھ (ھەممە نەرسىدىن) ئۈستۈندۇر، بۈيۈكتۇر.');
INSERT INTO "saleh" VALUES(22,63,'22:63',' ئاللاھنىڭ بۇلۇتتىن يامغۇر ياغدۇرۇپ بېرىدىغانلىقىنى، شۇنىڭ بىلەن زېمىننى يېشىلزارلىققا ئايلاندۇرىدىغانلىقىنى كۆرمىدىڭمۇ؟ ئاللاھ ھەقىقەتەن ناھايىتى مېھرىباندۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(22,64,'22:64','ئاسمانلاردىكى ۋە زېمىندىكى شەيئىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، ئاللاھنىڭ مەخلۇقاتىدۇر، ئاللاھنىڭ تەسەررۇپى ئاستىدىدۇر)، ئاللاھ ھەقىقەتەن ھەممىدىن بىھاجەتتۇر، ھەمدۇساناغا لايىقتۇر. ');
INSERT INTO "saleh" VALUES(22,65,'22:65',' ئاللاھنىڭ يەر يۈزىدىكى نەرسىلەرنى، ئۆزىنىڭ ئەمرى بويىچە دېڭىزدا يۈرۈۋاتقان كېمىلەرنى سىلەرگە بويسۇندۇرۇپ بەرگەنلىكىنى كۆرمىدىڭمۇ؟ (ئاسماننىڭ زېمىنغا چۈشۈپ كەتمەسلىكى ئۈچۈن، ئاللاھ ئاسماننى توختىتىپ تۇرىدۇ، پەقەت ئاللاھنىڭ ئىزنى بىلەن (ئاسماننىڭ قىيامەت بولغان چاغدا) چۈشۈپ كېتىشى بۇنىڭدىن مۇستەسنا. ئاللاھ ئىنسانلارغا ھەقىقەتەن ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(22,66,'22:66','ئاللاھ سىلەرگە ھاياتلىق بېرىدۇ، ئاندىن سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، ئىنسان ھەقىقەتەن (ئاللاھنىڭ نېمەتلىرىگە) ناشۈكۈرلۈك قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,67,'22:67',' ھەر ئۈممەت ئۈچۈن بىر شەرىئەت بېكىتتۇق، ئۇلار شۇ شەرىئەتكە ئەمەل قىلىدۇ، (مۇشرىكلار) شەرىئەت ئىشىدا سەن بىلەن جېدەللەشمىسۇن، كىشىلەرنى پەرۋەردىگارىڭنىڭ (ئىبادىتىگە) دەۋەت قىلغىن، سەن ھەقىقەتەن شەكسىز توغرا يولدىدۇرسەن. ');
INSERT INTO "saleh" VALUES(22,68,'22:68',' ئەگەر ئۇلار سەن بىلەن جېدەللەشسە، ئېيتقىنكى، «ئاللاھ سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(22,69,'22:69','ئاللاھ قىيامەت كۈنى سىلەر ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىدۇ».');
INSERT INTO "saleh" VALUES(22,70,'22:70',' بىلمەمسەنكى، ئاللاھ ئاسمان، زېمىندىكى شەيئىلەرنى بىلىپ تۇرىدۇ، بۇ ھەقىقەتەن لەۋھۇلمەھپۇزدا (يېزىلغان)، بۇ (يەنى بىلىش) ھەقىقەتەن ئاللاھقا ئاسان.');
INSERT INTO "saleh" VALUES(22,71,'22:71',' ئۇلار ئاللاھنى قويۇپ، (ئىبادەت قىلىشنىڭ دۇرۇسلۇقىغا) ئاللاھ ھېچقانداق دەلىل چۈشۈرمىگەن نەرسىلەرگە ۋە ئۆزلىرى بىلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، زالىملارغا ھېچقانداق مەدەتكار يوق. ');
INSERT INTO "saleh" VALUES(22,72,'22:72','كاپىرلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلسە، ئۇلارنىڭ چېھرىلىرىدە (قوشۇمىسى تۈرۈلگەنلىكتىن) ئىنكار قىلغانلىقنى كۆرۈسەن، ئۇلارنىڭ بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بەرگۈچىلەرگە ھۇجۇم قىلغىلى تاس قىلغانلىقىنى كۆرۈسەن. ئېيتقىنكى، «سىلەرگە مەن بۇنىڭدىنمۇ يامانراقىنى ئېيتىپ بېرەيمۇ؟ ئۇ دوزاختۇر. ئاللاھ ئۇنى كاپىرلارغا ۋەدە قىلدى، ئۇلار قايتىپ بارىدىغان جاي نېمىدېگەن يامان!».');
INSERT INTO "saleh" VALUES(22,73,'22:73','ئى ئىنسانلار! بىر مىسال كەلتۈرۈلىدۇ، ئۇنىڭغا قۇلاق سېلىڭلار، شۈبھىسىزكى، سىلەر ئاللاھنى قويۇپ چوقۇنۇۋاتقان بۇتلارنىڭ ھەممىسى يىغىلغان تەقدىردىمۇ بىر چىۋىننى يارىتالمايدۇ (ئەقلى بار ئادەم قانداقمۇ مۇشۇنداق بۇتلارنى مەبۇد قىلىۋېلىپ ئۇلارغا چوقۇنىدۇ!)، چىۋىن ئۇلارنىڭ بەدىنىدىن بىر نەرسىنى (يەنى بۇتقا سۈركەپ قويۇلغان خۇش پۇراق بىر نەرسىنى) ئېلىپ قاچسا، ئۇنى چىۋىندىن تارتىپ ئالالمايدۇ، بۇتمۇ ۋە (ئۇنىڭغا) چوقۇنغۇچىمۇ ئاجىزدۇر.');
INSERT INTO "saleh" VALUES(22,74,'22:74','ئۇلار (ئەرزىمەس بۇتلارنى ئاللاھقا شېرىك قىلىۋېلىپ) ئاللاھنى تېگىشلىك رەۋىشتە ئۇلۇغلاشمىدى، شۈبھىسىزكى، ئاللاھ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر. ');
INSERT INTO "saleh" VALUES(22,75,'22:75','ئاللاھ پەرىشتىلەردىنمۇ (پەيغەمبەرلىرىگە ۋەھىي يەتكۈزۈش ئۈچۈن)، ئىنسانلاردىنمۇ (شەرىئەتنى بەندىلىرىگە يەتكۈزۈش ئۈچۈن) ئەلچىلەرنى تاللايدۇ، ئاللاھ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (قىلمىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(22,76,'22:76','ئاللاھ ئۇلارنىڭ ئالدىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ئەھۋالىنى ۋە ھازىرقى ئىشلىرىنى) ۋە ئارقىسىدىكى نەرسىلەرنى (يەنى كەلگۈسىدىكى ئەھۋالىنى ۋە كەلگۈسىدىكى ئىشلىرىنى) بىلىدۇ، (بەندىلەرنىڭ) ئىشلىرىنىڭ ھەممىسى ئاللاھقا قايتۇرۇلىدۇ (ئاللاھ ئۇلارغا يا مۇكاپات، يا جازا بېرىدۇ).');
INSERT INTO "saleh" VALUES(22,77,'22:77',' ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن رۇكۇ قىلىڭلار، سەجدە قىلىڭلار (يەنى تەزەررۇ بىلەن ناماز ئوقۇڭلار)، (يالغۇز) پەرۋەردىگارىڭلارغا ئىبادەت قىلىڭلار، ياخشى ئىشلارنى (يەنى خىش ـ ئەقرىبالارغا سىلە ـ رەھىم قىلىش، يېتىم ـ يېسىرلەرنىڭ بېشىنى سىلاش، كېچىسى تەھەججۇد نامىزى ئۆتەش قاتارلىق ئىشلارنى) قىلىڭلار.');
INSERT INTO "saleh" VALUES(22,78,'22:78','ئاللاھنىڭ يولىدا (پۇل ـ مېلىڭلار بىلەن، جېنىڭلار بىلەن) تاقىتىڭلارنىڭ يېتىشىچە جىھاد قىلىڭلار، ئاللاھ (دىنغا ياردەم بېرىشكە ئۈممەتلەر ئارىسىدىن) سىلەرنى تاللىدى. (ئەڭ مۇكەممەل شەرىئەت ۋە ئۇلۇغ پەيغەمبەرنى سىلەرگە خاس قىلدى). سىلەرگە دىندا ھېچقانداق مۈشكۈللۈكنى قىلمىدى (سىلەرنى سىلەر تاقەت قىلالمايدىغان ئىشلارنى قىلىشقا تەكلىپ قىلمىدى). (بۇ) ئاتاڭلار ئىبراھىمنىڭ دىنىدۇر (بۇ توغرا دىن بولغانلىقى ئۈچۈن، ئۇنى مەھكەم ئۇچلاڭلار)، ئاللاھ سىلەرنى ئىلگىرى (يەنى قۇرئاندىن ئىلگىرىكى كىتابلاردا) مۇسۇلمان دەپ ئاتىغان ئىدى. قۇرئاندىمۇ شۇنداق ئاتىدى. (ئاللاھ) پەيغەمبەرنى سىلەرگە گۇۋاھ بولۇشقا ۋە سىلەرنى كىشىلەرگە گۇۋاھ بولۇشقا (تاللىدى)، ناماز ئوقۇڭلار، زاكات بېرىڭلار، ئاللاھقا يېپىشىڭلار، ئاللاھ سىلەرنىڭ ئىگەڭلاردۇر، ئاللاھ نېمىدېگەن ياخشى ئىگە! نېمىدېگەن ياخشى مەدەتكار!.');
INSERT INTO "saleh" VALUES(23,1,'23:1','مۆمىنلەر ھەقىقەتەن بەختكە ئېرىشتى.');
INSERT INTO "saleh" VALUES(23,2,'23:2','(شۇنداق مۆمىنلەركى) ئۇلار نامازلىرىدا (ئاللاھنىڭ ئۇلۇغلۇقىدىن سۈر بېسىپ كەتكەنلىكتىن) ئەيمىنىپ تۇرغۇچىلاردۇر. ');
INSERT INTO "saleh" VALUES(23,3,'23:3','ئۇلار بىھۇدە سۆز، بىھۇدە ئىشتىن يىراق بولغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,4,'23:4',' ئۇلار زاكات بەرگۈچىلەردۇر. ');
INSERT INTO "saleh" VALUES(23,5,'23:5','ئۇلار ئەۋرەتلىرىنى (ھارامدىن) ساقلىغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,6,'23:6',' (يەنى ئەۋرەتلىرىنى) پەقەت خوتۇنلىرىدىن، چۆرىلىرىدىن باشقىلاردىن ساقلىغۇچىلاردۇر (بۇلار بىلەن يېقىنچىلىق قىلغۇچىلار مالامەت قىلىنمايدۇ). ');
INSERT INTO "saleh" VALUES(23,7,'23:7',' بۇنىڭ سىرتىدىن (جىنسىي تەلەپنى قاندۇرۇشنى) تەلەپ قىلغۇچىلار ھەددىدىن ئاشقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,8,'23:8',' ئۇلار (يەنى مۆمىنلەر) ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر. ');
INSERT INTO "saleh" VALUES(23,9,'23:9','ئۇلار نامازلىرىنى (ۋاقتىدا، تەئدىل ئەركان بىلەن) ئادا قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,10,'23:10','ئەنە شۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە مۆمىنلەر نازۇ- نېمەتلىك جەننەتنىڭ) ۋارىسلىرىدۇر. ');
INSERT INTO "saleh" VALUES(23,11,'23:11',' ئۇلار (يۇقىرى دەرىجىلىك جەننەت) فىردەۋسكە ۋارىسلىق قىلىدۇ، فىردەۋستە مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(23,12,'23:12','بىز ئىنساننى ھەقىقەتەن لاينىڭ جەۋھىرىدىن ياراتتۇق.');
INSERT INTO "saleh" VALUES(23,13,'23:13',' ئاندىن ئۇنى بىر پۇختا قارارگاھ (يەنى بەچچىدان) دا (جايلاشقان) ئابىمەنىي قىلدۇق. ');
INSERT INTO "saleh" VALUES(23,14,'23:14',' ئاندىن ئابىمەنىينى لەختە قانغا ئايلاندۇردۇق، ئاندىن لەختە قاننى پارچە گۆشكە ئايلاندۇردۇق، ئاندىن پارچە گۆشنى سۆڭەككە ئايلاندۇردۇق، ئاندىن سۆڭەككە گۆش قوندۇردۇق، ئاندىن ئۇنى (جان كىرگۈزۈپ) باشقا مەخلۇققا ئايلاندۇردۇق، ئەڭ ماھىر ياراتقۇچى ئاللاھ (ھېكمەت ۋە قۇدرەت جەھەتتىن) ئۇلۇغدۇر.');
INSERT INTO "saleh" VALUES(23,15,'23:15',' ئاندىن سىلەر چوقۇم ئۆلۈسىلەر. ');
INSERT INTO "saleh" VALUES(23,16,'23:16','ئاندىن سىلەر ھەقىقەتەن قىيامەت كۈنى (قەبرەڭلاردىن) تۇرغۇزۇلىسىلەر.');
INSERT INTO "saleh" VALUES(23,17,'23:17','بىز ھەقىقەتەن سىلەرنىڭ ئۈستۈڭلاردا يەتتە قەۋەت ئاسماننى ياراتتۇق، بىز مەخلۇقاتتىن غاپىل ئەمەسمىز.');
INSERT INTO "saleh" VALUES(23,18,'23:18','بىز بۇلۇتتىن ھاجەتكە قاراپ يامغۇر ياغدۇردۇق، ئۇنى زېمىندا توختاتتۇق، ئۇنى قۇرۇتۇۋېتىشكە ئەلۋەتتە قادىرمىز.');
INSERT INTO "saleh" VALUES(23,19,'23:19',' شۇ يامغۇر بىلەن سىلەرگە خورما باغچىلىرى ۋە ئۈزۈم باغچىلىرىنى يېتىشتۈرۈپ بەردۇق، سىلەر ئۈچۈن ئۇ باغلاردا نۇرغۇن مېۋىلەر بار، ئۇلاردىن يەيسىلەر.');
INSERT INTO "saleh" VALUES(23,20,'23:20',' شۇ يامغۇر بىلەن تۇرسىنادىن چىقىدىغان (زەيتۇن) دەرىخىنى (ئۆستۈرۈپ بەردۇق)، ئۇنىڭدىن ماي چىقىدۇ، يېگۈچىلەر (نانلىرىنى شۇ ماي بىلەن) مايلايدۇ.');
INSERT INTO "saleh" VALUES(23,21,'23:21','چارۋىلاردا ھەقىقەتەن سىلەرگە ئىبرەت بار، ئۇلارنىڭ قورسىقىدىكى نەرسىلەر (يەنى سۈتلەر) بىلەن سىلەرنى سۇغىرىمىز، ئۇلاردا سىلەر ئۈچۈن نۇرغۇن مەنپەئەتلەر بار، ئۇلارنىڭ گۆشىنى يەيسىلەر.');
INSERT INTO "saleh" VALUES(23,22,'23:22',' ئۇلار (يەنى تۆگە ۋە ئۆكۈزلەر) بىلەن (قۇرۇقلۇقتا)، كېمە بىلەن (دېڭىزدا) يۈك توشۇيسىلەر.');
INSERT INTO "saleh" VALUES(23,23,'23:23',' بىز ھەقىقەتەن نۇھنى قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق، نۇھ ئېيتتى: «ئى قەۋمىم! (يالغۇز ئاللاھقا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوق، ئاللاھنىڭ ئازابىدىن) قورقمامسىلەر؟»');
INSERT INTO "saleh" VALUES(23,24,'23:24','ئۇنىڭ قەۋمىدىكى ئىمانسىز كاتتىلار ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەردىن ئۈستۈن بولۇۋالماقچى، ئەگەر ئاللاھ (پەيغەمبەر ئەۋەتىشنى) خالىسا ئىدى، ئەلۋەتتە، پەرىشتىلەرنى ئەۋەتەتتى، بۇنداق سۆزنى بۇرۇنقى ئاتا ـ بوۋىلىرىمىزدىن ئاڭلىغان ئەمەسمىز.');
INSERT INTO "saleh" VALUES(23,25,'23:25','ئۇ پەقەت بىر مەجنۇندۇر، سىلەر ئۇنى بىر مەزگىل كۈتۈڭلار».');
INSERT INTO "saleh" VALUES(23,26,'23:26',' نۇھ (ئۇلارنىڭ ئىمان ئېيتىشىدىن ئۈمىدىنى ئۈزگەندىن كېيىن): «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن» دېدى.');
INSERT INTO "saleh" VALUES(23,27,'23:27',' بىز نۇھقا: «بىزنىڭ كۆز ئالدىمىزدا ۋەھىيمىز بويىچە كېمە ياسىغىن، بىزنىڭ بۇيرۇقىمىز يېتىپ كەلگەن ۋە تونۇردىن سۇ ئېتىلىپ چىققان چاغدا، ھايۋانلاردىن (ئەركەك ـ چىشى بولۇپ) بىر جۈپتىن ئېلىۋالغىن، ئائىلەڭدىكىلەرنىمۇ ئېلىۋالغىن، ئۇلارنىڭ ئىچىدىكى ھالاك بولۇشقا ھۆكۈم قىلىنغانلار بۇنىڭدىن مۇستەسنا. (ئۆزلىرىگە) زۇلۇم قىلغانلار توغرىسىدا ماڭا سۆز ئاچمىغىن (يەنى ئۇلارغا شاپائەت قىلمىغىن)، ئۇلار چوقۇم (توپان بالاسىدا) غەرق قىلىنىدۇ» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(23,28,'23:28',' سەن بىلەن بىللە بولغان مۆمىنلەر بىلەن كېمىگە چىققان چېغىڭدا: «جىمى ھەمدۇسانا بىزنى زالىم قەۋمدىن قۇتقۇزغان ئاللاھقا خاستۇر» دېگىن.');
INSERT INTO "saleh" VALUES(23,29,'23:29','ھەمدە: «ئى پەرۋەردىگارىم! مېنى مۇبارەك مەنزىلگە چۈشۈرگىن، سەن (دوستلىرىڭنى) ئەڭ ياخشى ئورۇنلاشتۇرغۇچىسەن» دېگىن.');
INSERT INTO "saleh" VALUES(23,30,'23:30','ھەقىقەتەن بۇنىڭدا (يەنى نۇھنىڭ ئۈممىتىنىڭ ئىشلىرىدا ئەقىل ئىگىلىرى ئۈچۈن) نۇرغۇن ئىبرەتلەر بار، بىز بەندىلەرنى (پەيغەمبەرلەرنى ئەۋەتىش بىلەن) ئەلۋەتتە سىنايمىز. ');
INSERT INTO "saleh" VALUES(23,31,'23:31','ئۇلارنى ھالاك قىلغاندىن كېيىن باشقا بىر قەۋم پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(23,32,'23:32',' بىز ئۇلارغا ئۆزلىرىنىڭ ئىچىدىن بىر پەيغەمبەرنى (يەنى ھۇدنى) ئەۋەتتۇق. (ئۇ ئېيتتى): «ئاللاھقىلا ئىبادەت قىلىڭلار، سىلەرگە ئۇنىڭدىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئاللاھنىڭ (ئازابىدىن) قورقمامسىلەر؟»');
INSERT INTO "saleh" VALUES(23,33,'23:33','ئۇنىڭ قەۋمىنىڭ كاپىر بولغان، ئاخىرەت مۇلاقاتىنى يالغانغا چىقارغان ۋە بۇ دۇنيادا بىز تەرەپتىن باياشات تۇرمۇشقا ئىگە قىلىنغان كاتتىلىرى ئېيتتى: «بۇ پەقەت سىلەرگە ئوخشاش بىر ئىنساندۇر، ئۇ سىلەر يېگەننى يەيدۇ، سىلەر ئىچكەننى ئىچىدۇ..');
INSERT INTO "saleh" VALUES(23,34,'23:34','ئەگەر سىلەر ئۆزۈڭلارغا ئوخشاش بىر ئىنسانغا ئىتائەت قىلساڭلار، شۈبھىسىزكى، ئۇ چاغدا سىلەر چوقۇم زىيان تارتقۇچىلارسىلەر.');
INSERT INTO "saleh" VALUES(23,35,'23:35','ئۇ سىلەرنى، سىلەر ئۆلۈپ توپىغا ۋە قۇرۇق ئۇستىخانغا ئايلانغاندىن كېيىن تىرىلىسىلەر، دەپ ئاگاھلاندۇرامدۇ؟.');
INSERT INTO "saleh" VALUES(23,36,'23:36',' سىلەرگە ئاگاھلاندۇرۇلغان ئىش تولىمۇ يىراقتۇر.');
INSERT INTO "saleh" VALUES(23,37,'23:37','بىزنىڭ مۇشۇ دۇنيادىكى ھاياتىمىزدىن باشقا ھايات يوقتۇر، بىز ئۆلىمىز ۋە تىرىلىمىز (يەنى بىر تەرەپتىن ئۆلۈپ تۇرىمىز ۋە بىر تەرەپتىن تۇغۇلۇپ تۇرىمىز)، بىز قايتا تىرىلمەيمىز.');
INSERT INTO "saleh" VALUES(23,38,'23:38',' ئۇ پەقەت ئاللاھ نامىدىن يالغاننى توقۇغان ئادەمدۇر، بىز ئۇنىڭغا ئىشەنمەيمىز».');
INSERT INTO "saleh" VALUES(23,39,'23:39',' ھۇد ئېيتتى: «پەرۋەردىگارىم! ئۇلار مېنى يالغانغا چىقارغانلىقلىرى ئۈچۈن ماڭا ياردەم بەرگىن». ');
INSERT INTO "saleh" VALUES(23,40,'23:40','ئاللاھ ئېيتتى: «ئۇلار ئۇزۇنغا قالماي (كاپىر بولغانلىقلىرىغا) نادامەت چېكىدۇ».');
INSERT INTO "saleh" VALUES(23,41,'23:41',' ئۇلار ھەقلىق رەۋىشتە قاتتىق ئاۋاز بىلەن ھالاك بولدى. ئۇلارنى بىز لۆجە قىلىۋەتتۇق، زالىم قەۋم ئاللاھنىڭ رەھمىتىدىن يىراق بولسۇن.');
INSERT INTO "saleh" VALUES(23,42,'23:42','ئۇلارنى ھالاك قىلغاندىن كېيىن، باشقا بىر قەۋملەرنى پەيدا قىلدۇق.');
INSERT INTO "saleh" VALUES(23,43,'23:43','ھەر قانداق ئۈممەت ھالاك بولۇش ۋاقتى كەلمەي تۇرۇپ ھالاك بولمايدۇ، ھالاك بولۇش ۋاقتىدىن ئۆتۈپمۇ كەتمەيدۇ.');
INSERT INTO "saleh" VALUES(23,44,'23:44',' ئاندىن بىزنىڭ پەيغەمبەرلىرىمىزنى ئارقىمۇ ئارقا ئەۋەتتۇق، ھەر ئۈممەتكە ئۇنىڭ پەيغەمبىرى كەلگەن چاغدا، ئۇلار ئۇنى ئىنكار قىلغانلىقتىن، ئۇلارنى ئارقىمۇ ئارقا ھالاك قىلدۇق، ئۇلارنى (كىشىلەرگە) ھېكايە قىلىپ قالدۇردۇق، ئىمان ئېيتمايدىغان قەۋم ئاللاھنىڭ رەھمىتىدىن يىراق بولسۇن.');
INSERT INTO "saleh" VALUES(23,45,'23:45',' ئاندىن مۇسا ۋە ئۇنىڭ قېرىندىشى ھارۇننى بىزنىڭ ئايەتلىرىمىز بىلەن ۋە روشەن پاكىت بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار ھاكاۋۇرلۇق قىلىپ (ئىماندىن) باش تارتتى، ئۇلارمۇ تەكەببۇر قەۋم ئىدى[45ـ46]. ');
INSERT INTO "saleh" VALUES(23,46,'23:46',' ئاندىن مۇسا ۋە ئۇنىڭ قېرىندىشى ھارۇننى بىزنىڭ ئايەتلىرىمىز بىلەن ۋە روشەن پاكىت بىلەن پىرئەۋنگە ۋە ئۇنىڭ قەۋمىنىڭ چوڭلىرىغا (پەيغەمبەر قىلىپ) ئەۋەتتۇق، ئۇلار ھاكاۋۇرلۇق قىلىپ (ئىماندىن) باش تارتتى، ئۇلارمۇ تەكەببۇر قەۋم ئىدى[45ـ46]. ');
INSERT INTO "saleh" VALUES(23,47,'23:47',' ئۇلار: «بىزگە ئوخشاش ئىككى ئاددى ئىنسانغا ئىمان ئېيتامدۇق، ھالبۇكى، ئۇلارنىڭ قەۋمى بىزنىڭ (قۇلغا ئوخشاش) خىزمەتچىلىرىمىزدۇر» دېيىشتى.');
INSERT INTO "saleh" VALUES(23,48,'23:48','ئۇلار بۇ ئىككىسىنى (يەنى مۇسا بىلەن ھارۇننى) ئىنكار قىلىشتى. شۇنىڭ بىلەن ئۇلار ھالاك بولدى.');
INSERT INTO "saleh" VALUES(23,49,'23:49',' ئۇلار (يەنى بەنى ئىسرائىل) نى ھىدايەت تاپسۇن دەپ، بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(23,50,'23:50',' بىز مەريەمنىڭ ئوغلىنى (يەنى ئىيسانى) ۋە ئۇنىڭ ئانىسىنى (يەنى مەريەمنى) (كامالى قۇدرىتىمىزنىڭ) دەلىلى قىلدۇق، ئۇلارنى تۈپتۈز، ئېقىن سۇلۇق بىر ئېگىز جايغا ئورۇنلاشتۇردۇق. ');
INSERT INTO "saleh" VALUES(23,51,'23:51',' ئى پەيغەمبەرلەر! ھالال نەرسىلەرنى يەڭلار، ياخشى ئەمەللەرنى قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ قىلغان ئەمەللىرىڭلارنى ئوبدان بىلىمەن.');
INSERT INTO "saleh" VALUES(23,52,'23:52',' (ئى پەيغەمبەرلەر جامائەسى!) سىلەرنىڭ دىنىڭلار ھەقىقەتەن بىر دىندۇر، مەن سىلەرنىڭ پەرۋەردىگارىڭلاردۇرمەن، (مېنىڭ ئازابىمدىن) قورقۇڭلار.');
INSERT INTO "saleh" VALUES(23,53,'23:53','ئۇلار (يەنى ئۈممەتلەر) دىنىي جەھەتتە نۇرغۇن پىرقىلەرگە بۆلۈندى، ھەر پىرقە ئۆز دىنى بىلەن خۇشالدۇر.');
INSERT INTO "saleh" VALUES(23,54,'23:54',' ئۇلارنى ئۆلگەنلىرىگە قەدەر گۇمراھلىقتا تەرك ئەتكىن. ');
INSERT INTO "saleh" VALUES(23,55,'23:55',' (ئۇلار) بىزنىڭ ئۇلارغا مال ـ مۈلۈك ۋە ئەۋلادلارنى بەرگەنلىكىمىزنى ئۆزلىرىگە تېز ياخشىلىق قىلغانلىق دەپ ئويلامدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار ئۇقمايدۇ[ -56- 55');
INSERT INTO "saleh" VALUES(23,56,'23:56',' (ئۇلار) بىزنىڭ ئۇلارغا مال ـ مۈلۈك ۋە ئەۋلادلارنى بەرگەنلىكىمىزنى ئۆزلىرىگە تېز ياخشىلىق قىلغانلىق دەپ ئويلامدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار ئۇقمايدۇ[ -56- 55');
INSERT INTO "saleh" VALUES(23,57,'23:57','ھەقىقەتەن پەرۋەردىگارىنىڭ ھەيۋىسىدىن قورققۇچى كىشىلەر، ');
INSERT INTO "saleh" VALUES(23,58,'23:58','پەرۋەردىگارىنىڭ ئايەتلىرىگە ئىشەنگۈچى كىشىلەر،');
INSERT INTO "saleh" VALUES(23,59,'23:59','پەرۋەردىگارىغا شېرىك كەلتۈرمەيدىغان كىشىلەر،');
INSERT INTO "saleh" VALUES(23,60,'23:60','سەدىقە بېرىدىغان ئەمما پەرۋەردىگارىنىڭ دەرگاھىغا (ھېساب بېرىش ئۈچۈن) قايتىپ بارىدىغانلىقىدىن دىللىرى قورقۇپ تۇرىدىغان كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(23,61,'23:61','ئەنە شۇلار ياخشى ئىشلارنى قىلىشقا ئالدىرىغۇچىلاردۇر، ياخشى ئىشلارنى (باشقىلارنىڭ) ئالدىدا قىلىشقا تىرىشقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,62,'23:62','بىز ئىنساننى پەقەت تاقىتى يېتىدىغان (ئىش) قىلا تەكلىپ قىلىمىز، بىزنىڭ دەرگاھىمىزدا ھەقنى سۆزلەيدىغان كىتاب (يەنى بەندىلەرنىڭ ياخشى ـ يامان ئىشلىرى خاتىرىلەنگەن نامە ـ ئەمال بار، ئۇلارغا (ساۋابىنى كېمەيتىۋېتىش ياكى ئازابنى ئاشۇرۇۋېتىش بىلەن) قىلچە زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(23,63,'23:63',' ئۇلار (يەنى گۇناھكار كۇففارلار) نىڭ دىللىرى بۇنىڭدىن (يەنى قۇرئاندىن) غەپلەتتىدۇر، ئۇنىڭدىن (يەنى كۇفرىدىن) باشقا ئۇلار كېلەچەكتە نۇرغۇن (يامان) ئىشلارنى قىلىدۇ.');
INSERT INTO "saleh" VALUES(23,64,'23:64','ئۇلارنىڭ بايلىرىنى ئازاب بىلەن جازالىغان ۋاقتىمىزدا ناگاھ ئۇلار پەرياد قىلىدۇ.');
INSERT INTO "saleh" VALUES(23,65,'23:65',' (ئۇلارغا ئېيتىلىدۇ): «بۈگۈن پەرياد قىلماڭلار، ھەقىقەتەن سىلەر بىزنىڭ ياردىمىمىزگە ئېرىشەلمەيسىلەر».');
INSERT INTO "saleh" VALUES(23,66,'23:66',' مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلەتتى، (ئۇنى ئاڭلاشتىن يۈز ئۆرۈپ) ئارقاڭلارغا چېكىنىۋالاتتىڭلار.');
INSERT INTO "saleh" VALUES(23,67,'23:67',' ئۇلار ھەرەم ئەھلى بولغانلىقى بىلەن پەخىرلىنىپ ئىماندىن يۈز ئۆرۈيدۇ، كەچلىك پاراڭلىرىدا يامان سۆزلەرنى قىلىدۇ، (يەنى قۇرئانغا تەنە قىلىدۇ، پەيغەمبەر ئەلەيھىسسالامنى سۆكىدۇ).');
INSERT INTO "saleh" VALUES(23,68,'23:68',' ئۇلار (ھەق) كالام (يەنى ئۇلۇغ قۇرئان) ئۈستىدە چوڭقۇر پىكىر قىلمىدىمۇ؟ ياكى ئۇلارغا (ئاللاھ تەرىپىدىن) ئاتا ـ بوۋىلىرىغا كەلمىگەن (يېڭى) نەرسە كەلدىمۇ؟.');
INSERT INTO "saleh" VALUES(23,69,'23:69','ياكى ئۇلار ئۆزلىرىگە كەلگەن ئەلچىنى تونۇماي ئۇنى ئىنكار قىلىۋاتامدۇ؟');
INSERT INTO "saleh" VALUES(23,70,'23:70','ياكى ئۇلار ئۇنى مەجنۇن دېيىشەمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلارغا ئۇ ھەقىقەتنى ئېلىپ كەلدى. ئۇلارنىڭ تولىسى ھەقىقەتنى يامان كۆرىدۇ.');
INSERT INTO "saleh" VALUES(23,71,'23:71',' ئەگەر ھەقىقەت (يەنى قۇرئان) ئۇلارنىڭ نەپسى خاھىشلىرىغا بويسۇنىدىغان بولسا، ئەلۋەتتە ئاسمانلار، زېمىن ۋە ئۇلاردىكى مەخلۇقاتلار خاراب بولغان بولاتتى، ئۇنداق بولغىنى يوق، ئۇلارغا (ۋەز ـ نەسىھەتنى ئۆز ئىچىگە ئالغان) قۇرئاننى ئاتا قىلدۇق، ئۇلار ئۆزلىرىگە (ۋەز ـ نەسىھەت بولغان) قۇرئاندىن يۈز ئۆرۈيدۇ.');
INSERT INTO "saleh" VALUES(23,72,'23:72',' ياكى سەن ئۇلاردىن (ئەلچىلىكنى يەتكۈزگەنلىكىڭگە) ھەق تەلەپ قىلامسەن؟ (يەنى ئۇلار شۇنداق گۇمان قىلامدۇ؟) پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) ئەجرى ياخشىدۇر، ئاللاھ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر. ');
INSERT INTO "saleh" VALUES(23,73,'23:73',' سەن ئەلۋەتتە ئۇلارنى توغرا يولغا دەۋەت قىلىسەن. ');
INSERT INTO "saleh" VALUES(23,74,'23:74',' ئاخىرەتكە ئىشەنمەيدىغانلار ھەقىقەتەن توغرا يولدىن چەتنىگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(23,75,'23:75',' ئەگەر ئۇلار (يەنى مۇشرىكلار) غا رەھىم قىلساق، ئۇلارغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتسەك، چوقۇم گۇمراھلىقلىرىدا داۋاملىق تېڭىرقاپ يۈرۈشەتتى.');
INSERT INTO "saleh" VALUES(23,76,'23:76',' ئۇلارنى بىز ھەقىقەتەن ئازاب (يەنى قەھەتچىلىك) بىلەن جازالىدۇق، ئۇلار پەرۋەردىگارىغا بويسۇنمىدى، ئىلتىجا قىلىپ دۇئامۇ قىلمىدى.');
INSERT INTO "saleh" VALUES(23,77,'23:77',' تاكى ئۇلارغا قاتتىق ئازابنىڭ دەرۋازىسىنى ئاچقان چېغىمىزدا ناگاھان ئۇلار ئۈمىدسىزلىنىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(23,78,'23:78','ئاللاھ سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە دىللارنى ياراتتى، سىلەر ناھايىتى ئاز شۈكۈر قىلىسىلەر.');
INSERT INTO "saleh" VALUES(23,79,'23:79','ئاللاھ سىلەرنى زېمىندا (نەسىللەندۈرۈش يولى بىلەن) ياراتتى، (ھېساب بېرىش ئۈچۈن) ئۇنىڭ دەرگاھىغا يىغىلىسىلەر. ');
INSERT INTO "saleh" VALUES(23,80,'23:80','ئاللاھ (ئۆلۈكنى) تىرىلدۈرىدۇ، (تىرىكنىڭ) جېنىنى ئالىدۇ، كېچە ـ كۈندۈزنىڭ ئۆزگىرىپ تۇرۇشى ئاللاھنىڭ باشقۇرۇشىدىدۇر، چۈشەنمەمسىلەر؟.');
INSERT INTO "saleh" VALUES(23,81,'23:81',' (ئۇلار ئاللاھنىڭ قۇدرىتىنى ئويلاپ كۆرمىدى) بەلكى ئۇلار بۇرۇنقىلار نېمىدېگەن بولسا شۇنى دېدى.');
INSERT INTO "saleh" VALUES(23,82,'23:82',' ئۇلار ئېيتتى: «بىز ئۆلۈپ توپىغا ۋە (ئۇۋۇلۇپ كەتكەن) سۆڭەككە ئايلانغاندىن كېيىن، چوقۇم قايتا تىرىلەمدۇق؟.');
INSERT INTO "saleh" VALUES(23,83,'23:83','ھەقىقەتەن بىز ۋە بىزنىڭ ئاتا ـ بوۋىلىرىمىز ئىلگىرى مۇشۇنداق ئاگاھلاندۇرۇلغان ئىدۇق، بۇ پەقەت قەدىمكىلەردىن قالغان ئەپسانىلەردۇر».');
INSERT INTO "saleh" VALUES(23,84,'23:84',' ئېيتقىنكى، «زېمىن ۋە ئۇنىڭدىكى مەخلۇقاتلار كىمنىڭ؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)».');
INSERT INTO "saleh" VALUES(23,85,'23:85','ئۇلار: «ئاللاھنىڭ» دەيدۇ. (بۇنىڭدىن) پەند ـ نەسىھەت ئالمامسىلەر؟');
INSERT INTO "saleh" VALUES(23,86,'23:86','ئېيىتقىنكى، «يەتتە ئاسماننىڭ ۋە ئۇلۇغ ئەرشنىڭ پەرۋەردىگارى كىم؟»');
INSERT INTO "saleh" VALUES(23,87,'23:87',' ئۇلار «ئاللاھ» دەيدۇ. ئېيتقىنكى، «ئۇنىڭ (ئازابىدىن) قورقمامسىلەر؟»');
INSERT INTO "saleh" VALUES(23,88,'23:88','ئېيتقىنكى، «ھەممە شەيئىنىڭ پادىشاھلىقى كىمنىڭ قولىدا؟ (ئىلتىجا قىلغانلارغا) پاناھ بولالايدىغان ۋە ئۇنىڭغا قارشى ھېچ نەرسە پاناھ بولالمايدىغان كىم؟ ئەگەر سىلەر بىلسەڭلار (ماڭا بۇنى ئېيتىپ بېرىڭلار)».');
INSERT INTO "saleh" VALUES(23,89,'23:89','ئۇلار: «ئاللاھ» دەيدۇ. ئېيتقىنكى، «سىلەر قانداقمۇ قايمۇقتۇرۇلىسىلەر؟».');
INSERT INTO "saleh" VALUES(23,90,'23:90',' ئۇنداق ئەمەس، ئۇلارغا بىز ھەق سۆزنى ئېلىپ كەلدۇق، ئۇلار چوقۇم يالغانچىلاردۇر. ');
INSERT INTO "saleh" VALUES(23,91,'23:91',' ئاللاھنىڭ بالىسى يوق، ئۇنىڭغا باراۋەر باشقا بىر ئىلاھمۇ يوق، ئەگەر بۇنداق بولسا ئىدى، ئۇ چاغدا ھەر ئىلاھ ئۆزى ياراتقان مەخلۇقنى يالغۇز ئىگىلەيتتى، بەزىسى بەزىسىدىن غالىب كېلەتتى، ئاللاھ ئۇلارنىڭ ئاللاھقا نىسبەت بەرگەن نەرسىلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(23,92,'23:92',' ئاللاھ غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، مۇشرىكلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئۈستۈندۇر.');
INSERT INTO "saleh" VALUES(23,93,'23:93','ئېيتقىنكى، «پەرۋەردىگارىم! ئەگەر ماڭا ئۇلارغا ۋەدە قىلغان ئازابنى (دۇنيادا) كۆرسىتىدىغان بولساڭ،');
INSERT INTO "saleh" VALUES(23,94,'23:94',' ئېيتقىنكى: «پەرۋەردىگارىم! مېنى زالىم قەۋملەر ئىچىدە قىلمىغىن».');
INSERT INTO "saleh" VALUES(23,95,'23:95',' بىز ئۇلارغا ۋەدە قىلغان ئازابنى ساڭا كۆرسىتىشكە ئەلۋەتتە قادىرمىز.');
INSERT INTO "saleh" VALUES(23,96,'23:96',' (ئۇلارنىڭ قىلغان) يامانلىقىغا (ئەپۇ قىلىش بىلەن) ياخشىلىق قىلغىن، ئۇلارنىڭ (سېنى) سۈپەتلەشلىرىنى (يەنى ئۇلارنىڭ ساڭا قىلغان مەسخىرىسىنى) بىز ئوبدان بىلىمىز.');
INSERT INTO "saleh" VALUES(23,97,'23:97',' ئېيتقىنكى، «پەرۋەردىگارىم! ساڭا سىغىنىپ شەيتانلارنىڭ ۋەسۋەسىلىرىدىن پاناھ تىلەيمەن.');
INSERT INTO "saleh" VALUES(23,98,'23:98','پەرۋەردىگارىم! ساڭا سىغىنىپ شەيتانلارنىڭ ماڭا ھەمراھ بولۇشىدىن پاناھ تىلەيمەن».');
INSERT INTO "saleh" VALUES(23,99,'23:99','ئۇلارنىڭ (يەنى كاپىرلارنىڭ) بىرىگە ئۆلۈم كەلسە ئېيتىدۇكى، «پەرۋەردىگارىم! مېنى (دۇنياغا) قايتۇرغىن. ');
INSERT INTO "saleh" VALUES(23,100,'23:100','مەن قويۇپ كەلگەن مال ـ مۈلكۈم بىلەن ياخشىلىق قىلىشىم مۇمكىن». (ئۇنىڭ دۇنياغا قايتىشى) مۇمكىن ئەمەس، ئۇ چوقۇم شۇ سۆزنى قىلغۇچىدۇر، ئۇلار قەبرىلىرىدىن تۇرغۇزۇلغان كۈنگە قەدەر ئۇلارنىڭ ئالدىدا (ئۇلارنى دۇنياغا قايتىشتىن توسىدىغان) بىر توسما بولىدۇ.');
INSERT INTO "saleh" VALUES(23,101,'23:101',' سۇر چېلىنغان كۈندە ئۇلارنىڭ ئارىسىدىكى تۇغقانچىلىق مەۋجۇت بولماي قالىدۇ. بىر ـ بىرىنىڭ (ئەھۋالىنىمۇ) سوراشمايدۇ.');
INSERT INTO "saleh" VALUES(23,102,'23:102','تارازىسى ئېغىر كەلگەنلەر (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ كەتكەنلەر) بەختكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(23,103,'23:103','تارازىسى يېنىك كەلگەنلەر (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەنلەر) زىيان تارتقۇچىلاردۇر، جەھەننەمدە مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(23,104,'23:104','ئۇلارنىڭ يۈزلىرىنى ئوت كۆيدۈرىدۇ، كالپۇكلىرى قورۇلۇپ، چىشلىرى ئېچىلىپ (بەت چىراي) بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(23,105,'23:105',' (ئۇلارغا) «سىلەرگە (دۇنيادا) مېنىڭ ئايەتلىرىم ئوقۇپ بېرىلگەن، سىلەر ئۇنى (شۇنچە روشەن تۇرۇقلۇق) ئىنكار قىلغان ئەمەسمىدىڭلار» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(23,106,'23:106','ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! بەتبەختلىكىمىز ئۈستىمىزدىن غالىب كېلىپ گۇمراھ قەۋم بولۇپ قالغان ئىدۇق.');
INSERT INTO "saleh" VALUES(23,107,'23:107',' پەرۋەردىگارىمىز! بىزنى دوزاختىن چىقارغىن (ئاندىن دۇنياغا قايتۇرغىن)، ئەگەر بىز (شۇنىڭدىن كېيىن) يەنە گۇناھ قىلساق، بىز ھەقىقەتەن زالىم بولۇپ قالىمىز». ');
INSERT INTO "saleh" VALUES(23,108,'23:108',' ئاللاھ ئېيتىدۇ: «جەھەننەمدە خار ھالدا قېلىڭلار، (ئۈستۈڭلاردىن ئازابنىڭ كۆتۈرۈلۈپ كېتىشى ھەققىدە) ماڭا سۆز ئاچماڭلار».');
INSERT INTO "saleh" VALUES(23,109,'23:109','شۈبھىسىزكى، بەندىلىرىمدىن بىر تۈركۈم كىشىلەر: «پەرۋەردىگارىمىز! بىز ئىمان ئېيتتۇق، بىزگە مەغپىرەت قىلغىن، بىزگە رەھىم قىلغىن، سەن بولساڭ ئەڭ رەھىم قىلغۇچىسەن» دەيتتى.');
INSERT INTO "saleh" VALUES(23,110,'23:110',' ئۇلارنى مەسخىرە قىلدىڭلار، ھەتتا ئۇلار سىلەرگە مېنىڭ زىكرىمنى ئۇنتۇلدۇردى (يەنى ئۇلارنى مەسخىرە قىلىش بىلەن بولۇپ كېتىپ ماڭا ئىتائەت قىلىشنى ئۇنتۇدۇڭلار)، سىلەر ئۇلاردىن كۈلەتتىڭلار.');
INSERT INTO "saleh" VALUES(23,111,'23:111',' (سىلەردىن يەتكەن ئەزىيەتلەرگە) سەۋر قىلغانلىقلىرى ئۈچۈن، مەن ئۇلارنى بۈگۈن مۇكاپاتلىدىم، ئۇلار ھەقىقەتەن مەقسەتكە ئېرىشكۈچىلەردۇر. ');
INSERT INTO "saleh" VALUES(23,112,'23:112','ئاللاھ (ئۇلارغا): «زېمىندا قانچىلىك تۇردۇڭلار؟» دەيدۇ. ');
INSERT INTO "saleh" VALUES(23,113,'23:113','ئۇلار: «بىر كۈن ياكى بىر كۈندىنمۇ ئاز تۇردۇق، (ئاي ـ كۈنلەرنى) ھېسابلىغۇچى (پەرىشتە) دىن سورىغىن» دەيدۇ (ئۇلار دوزاختا چېكىۋاتقان ئازاب تۈپەيلىدىن، زېمىندا قانچىلىك تۇرغانلىقىنى ئۇنتۇيدۇ).');
INSERT INTO "saleh" VALUES(23,114,'23:114','ئاللاھ: «ئەگەر سىلەر بىلسەڭلەر (دۇنيادا) پەقەت ئازغىنا تۇردۇڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(23,115,'23:115',' ئى ئىنسانلار! بىزنى سىلەر ئۆزۈڭلارنى بىكار ياراتتى (يەنى سىلەر بىزنى ھايۋاناتلارنى ياراتقىنىمىزدەك ھېچقانداق ساۋابمۇ بەرمەيدىغان، جازامۇ بەرمەيدىغان قىلىپ ياراتتى). ئۆزۈڭلارنى بىزنىڭ دەرگاھىمىزغا قايتۇرۇلمايدۇ، دەپ ئويلامسىلەر؟');
INSERT INTO "saleh" VALUES(23,116,'23:116','ھەق پادىشاھ _ ئاللاھ ئۈستۈندۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئاللاھ ئۇلۇغ ئەرشنىڭ پەرۋەردىگارىدۇر. ');
INSERT INTO "saleh" VALUES(23,117,'23:117','كىمكى ھېچقانداق دەلىلى بولمىغان ھالدا ئاللاھقا قوشۇپ يەنە بىر مەبۇدقا ئىبادەت قىلىدىكەن، پەرۋەردىگارىنىڭ دەرگاھىدا ھېساب بېرىدۇ، كاپىرلار ھەقىقەتەن نىجات تاپمايدۇ.');
INSERT INTO "saleh" VALUES(23,118,'23:118',' «پەرۋەردىگارىم ! مەغپىرەت قىلغىن، رەھىم قىلغىن، سەن رەھىم قىلغۇچىلارنىڭ ياخشىسىسەن» دېگىن.');
INSERT INTO "saleh" VALUES(24,1,'24:1','(ئى مۇھەممەد!) بۇ بىر سۈرىدۇركى، ئۇنى بىز (ساڭا) نازىل قىلدۇق، ئۇنىڭدىكى (ئەھكاملارنى) پەرز قىلىپ بېكىتتۇق، (ئى مۆمىنلەر!) سىلەرنىڭ ۋەز ـ نەسىھەت ئېلىشىڭلار ئۈچۈن، ئۇنىڭدا (سىلەرنى ئەمەل قىلسۇن دەپ شەرىئەت ئەھكاملىرىنى ئۆز ئىچىگە ئالغان) روشەن ئايەتلەرنى نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(24,2,'24:2',' زىنا قىلغۇچى ئايال ۋە زىنا قىلغۇچى ئەرنىڭ ھەر بىرىنى يۈز دەررىدىن ئۇرۇڭلار، ئەگەر سىلەر ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتىدىغان بولساڭلار، ئاللاھنىڭ دىنىنىڭ (ئەھكامىنى ئىجرا قىلىشتا) ئۇلارغا رەھىم قىلماڭلار، ئۇلارنى جازالىغان چاغدا مۆمىنلەردىن بىر تۈركۈم كىشى ھازىر بولسۇن.');
INSERT INTO "saleh" VALUES(24,3,'24:3','زىنا قىلغۇچى ئەر پەقەت زىنا قىلغۇچى ئايالنى ياكى مۇشرىك ئايالنىلا ئالىدۇ، زىنا قىلغۇچى ئايال پەقەت زىنا قىلغۇچى ئەرگە ياكى مۇشرىككىلا تېگىدۇ، ئۇ (يەنى زىنا) مۆمىنلەرگە ھارام قىلىندى.');
INSERT INTO "saleh" VALUES(24,4,'24:4','ئىپپەتلىك ئاياللارنى زىنا بىلەن قارىلىغان، (بۇنىڭ راستلىقىغا ئادىل) تۆت گۇۋاھچىنى كەلتۈرەلمىگەن كىشىلەرنى 80 دەررە ئۇرۇڭلار (يەنى بىراۋغا زىنا بىلەن تۆھمەت چاپلىغان ھەربىر ئادەمنى قامچا ۋە شۇنىڭغا ئوخشاش نەرسىلەر بىلەن 80 نى ئۇرۇڭلار، چۈنكى ئۇلار ئىپپەتلىك ئاياللارغا يالغاندىن تۆھمەت چاپلىغان ۋە كىشىلەرنىڭ ئابرۇيىنى تۆككەن ئادەملەردۇر). ئۇلارنىڭ گۇۋاھلىقىنى (ئۇلار يالغانچىلىق ۋە تۆھمەت چاپلاش مەيدانىدا چىڭ تۇرىدىغانلا بولسا) ھەرگىز قوبۇل قىلماڭلار، ئۇلار پاسىقلاردۇر (يەنى ئۇلار بوھتان چاپلاشتىن ئىبارەت چوڭ گۇناھنى ئىشلىگەنلىكلىرى ئۈچۈن، ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر).');
INSERT INTO "saleh" VALUES(24,5,'24:5',' كېيىن تەۋبە قىلغان ۋە (ئەمەللىرىنى) تۈزەتكەنلەر (يەنى ئىپپەتلىك ئاياللارغا قايتا تۆھمەت چاپلىمىغانلار) بۇنىڭدىن مۇستەسنا (ئۇلارنى كەچۈرۈڭلار، گۇۋاھلىقىنى قوبۇل قىلىڭلار)، چۈنكى ئاللاھ مەغپىرەت قىلغۇچىدۇر (يەنى بەندە تەۋبە قىلىپ ئۆزىنى تۈزەتسە، ئۇنى ئاللاھ مەغپىرەت قىلىدۇ)، تولىمۇ مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(24,6,'24:6','خوتۇنلىرىنى زىنا بىلەن قارىلىغان ۋە ئۆزلىرىدىن باشقا گۇۋاھچىسى بولمىغانلار گۇۋاھلىق سۆزىنىڭ راست ئىكەنلىكىنى دەلىللەش ئۈچۈن، ئاللاھنىڭ نامى بىلەن تۆت قېتىم قەسەم قىلسۇن.');
INSERT INTO "saleh" VALUES(24,7,'24:7','بەشىنچى قېتىمىدا: «ئەگەر يالغانچى بولسام ماڭا ئاللاھنىڭ لەنىتى بولسۇن» دېسۇن. ');
INSERT INTO "saleh" VALUES(24,8,'24:8','خوتۇن كىشى ئېرىنىڭ يالغانچى ئىكەنلىكىگە ئاللاھنىڭ نامى بىلەن تۆت قېتىم قەسەم ئىچسە، جازاغا تارتىلىشتىن ساقلاپ قېلىنىدۇ.');
INSERT INTO "saleh" VALUES(24,9,'24:9','بەشىنچى قېتىمىدا، ئۇ ئەگەر راستچىللاردىن بولسا (يەنى ئېرىمنىڭ گېپى راست بولىدىغان بولسا) ماڭا ئاللاھنىڭ غەزىپى بولسۇن دەپ قەسەم قىلىدۇ.');
INSERT INTO "saleh" VALUES(24,10,'24:10',' ئەگەر سىلەرگە ئاللاھنىڭ پەزلى ۋە رەھمىتى بولمىسا ئىدى (چوقۇم رەسۋا بولاتتىڭلار)، ئاللاھ تەۋبىنى بەكمۇ قوبۇل قىلغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,11,'24:11','شۈبھىسىزكى، سىلەردىن بىر گۇرۇھ ئادەم (ئائىشەگە) بوھتان چاپلىدى. بۇنى ئۆزۈڭلار ئۈچۈن يامان دەپ گۇمان قىلماڭلار، (سەۋر قىلغانلىقىڭلار بىلەن ساۋابقا ئېرىشىدىغانلىقىڭلار ئۈچۈن ۋە ئائىشە، سەفۋانلارنىڭ پاكلىقى ئاشكارا بولىدىغانلىقى ئۈچۈن) بەلكى ئۇ سىلەر ئۈچۈن ياخشىدۇر. بوھتان چاپلىغۇچىلاردىن كىمنىڭ قانچىلىك گۇناھى بولسا، ئۇنىڭغا شۇنچىلىك جازا بېرىلىدۇ، بوھتاننىڭ چوڭ قىسمىنى تارقاتقان ئادەم (يەنى ئابدۇللاھ ئىبن ئۇبەي) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(24,12,'24:12',' بوھتاننى ئاڭلىغان چاغلىرىڭلاردا ئەر ـ ئايال مۆمىنلەر بىر-بىرىگە ياخشى گۇماندا بولۇپ، نېمە ئۈچۈن بۇ ئېنىق بوھتان دېمىدى؟');
INSERT INTO "saleh" VALUES(24,13,'24:13',' ئۇلار نېمىشقا تۆت نەپەر گۇۋاھچىنى كەلتۈرمىدى؟ گۇۋاھچىلارنى كەلتۈرەلمىگەن ئىكەن، ئۇلار ئاللاھنىڭ نەزىرىدە يالغانچى ھېسابلىنىدۇ.');
INSERT INTO "saleh" VALUES(24,14,'24:14',' سىلەرگە دۇنيا ۋە ئاخىرەتتە ئاللاھنىڭ پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، بوھتان چاپلىغىنىڭلار ئۈچۈن ئەلۋەتتە چوڭ ئازابقا دۇچار بولاتتىڭلار. ');
INSERT INTO "saleh" VALUES(24,15,'24:15','ئۆز ۋاقتىدا بوھتاننى تىلىڭلار بىلەن تارقاتتىڭلار، بىلمەيدىغان نەرسەڭلارنى ئېغىزىڭلارغا ئالدىڭلار، ئۇنى سەل چاغلىدىڭلار، ھالبۇكى، ئاللاھنىڭ نەزىرىدە، ئۇ چوڭ گۇناھتۇر.');
INSERT INTO "saleh" VALUES(24,16,'24:16',' ئۇنى ئاڭلىغان چېغىڭلاردا، نېمىشقا مۇنداق سۆزلەرنى قىلىش بىزگە مۇناسىپ ئەمەس، (ئى ئاللاھ!) سەن پاكتۇرسەنكى، بۇ چوڭ بوھتاندۇر دېمىدىڭلار.');
INSERT INTO "saleh" VALUES(24,17,'24:17',' ئاللاھ سىلەرگە نەسىھەت قىلىدۇكى، ئەگەر مۆمىن بولساڭلار، ھەرگىز قايتا مۇنداق سۆزلەرنى قىلماڭلار.');
INSERT INTO "saleh" VALUES(24,18,'24:18','ئاللاھ سىلەرگە (شەرىئەت ئەھكاملىرىنى ۋە گۈزەل ئەدەب ـ ئەخلاقلارنى كۆرسىتىپ بېرىدىغان) ئايەتلەرنى بايان قىلىدۇ، ئاللاھ (بەندىلەرگە پايدىلىق ئىشلارنى) ئوبدان بىلگۈچىدۇر، (تەدبىر قوللىنىشتا ۋە شەرىئىتىنى بەرپا قىلىشتا) ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,19,'24:19','مۆمىنلەر ئۈستىدە يامان سۆزلەرنىڭ تارىلىشىنى ياقتۇرىدىغان ئادەملەر، شۈبھىسىزكى، دۇنيا ۋە ئاخىرەتتە قاتتىق ئازابقا قالىدۇ، ئاللاھ (سىرلارنى ۋە نىيەتلەرنى) بىلىپ تۇرىدۇ، سىلەر بىلمەيسىلەر. ');
INSERT INTO "saleh" VALUES(24,20,'24:20','ئاللاھنىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى (سىلەرنى قاتتىق جازالايتتى)، ئاللاھ شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(24,21,'24:21',' ئى مۆمىنلەر! شەيتاننىڭ كەينىگە كىرمەڭلار، كىمكى شەيتاننىڭ كەينىگە كىرىدىكەن، شەيتان ئۇنى قەبىھ (سۆز ـ ھەرىكەتلەر) گە، يامان ئىشلارغا بۇيرۇيدۇ. ئاللاھنىڭ سىلەرگە پەزلى ۋە مەرھەمىتى بولمىسا ئىدى، سىلەردىن ھېچ ئادەم ئەبەدىي پاك بولماس ئىدى ۋەلېكىن ئاللاھ خالىغان بەندىسىنى پاك قىلىدۇ، ئاللاھ سىلەرنىڭ (سۆزلىرىڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيەتلىرىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,22,'24:22','ئاراڭلاردا ئەھلى كەرەم ۋە دۆلەتمەن بولغانلار خىش ـ ئەقرىبالارغا، مىسكىنلەرگە ۋە دىن يولىدا ھىجرەت قىلغانلارغا (بىر نەرسە) بەرمەسلىككە قەسەم قىلمىسۇن، (ئۇلارنىڭ گۇناھىنى) ئەپۇ قىلسۇن، كەچۈرسۇن، ئاللاھنىڭ سىلەرگە مەغپىرەت قىلىشىنى ياقتۇرمامسىلەر؟ ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(24,23,'24:23',' يامان ئىشتىن بىخەۋەر، ئىپپەتلىك، مۆمىن ئاياللارغا قارا چاپلايدىغانلار دۇنيا ۋە ئاخىرەتتە چوقۇم لەنەتكە ئۇچرايدۇ (يەنى ئاللاھنىڭ رەھمىتىدىن يىراق قىلىنىدۇ)، ئۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(24,24,'24:24','شۇ كۈندە (يەنى قىيامەت كۈنىدە) ئۇلارنىڭ تىللىرى، قوللىرى ۋە پۇتلىرى ئۇلارنىڭ قىلمىشلىرىدىن ئۇلارنىڭ زىيىنىغا گۇۋاھلىق بېرىدۇ. ');
INSERT INTO "saleh" VALUES(24,25,'24:25','شۇ كۈندە ئاللاھ ئۇلارغا تولۇق تېگىشلىك جازاسىنى بېرىدۇ، ئۇلار ئاللاھنىڭ ئاشكارا ھەق ئىكەنلىكىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(24,26,'24:26','يامان ئاياللار يامان ئەرلەرگە لايىقتۇر، يامان ئەرلەر يامان ئاياللارغا لايىقتۇر، ياخشى ئاياللار ياخشى ئەرلەرگە لايىقتۇر، ياخشى ئەرلەر ياخشى ئاياللارغا لايىقتۇر، ئەنە شۇلار ئۇلارنىڭ چاپلىغان بوھتانلىرىدىن پاكتۇر، ئۇلار مەغپىرەتتىن، (نازۇنېمەتلىك جەننەتلەردىكى) ئېسىل رىزىقتىن بەھرىمەن بولىدۇ.');
INSERT INTO "saleh" VALUES(24,27,'24:27','ئى مۆمىنلەر! باشقىلارنىڭ ئۆيلىرىگە (كىرىشكە) ئىجازەت سورىمىغىچە ۋە ئۆي ئىگىسىگە سالام بەرمىگۈچە كىرمەڭلار، ۋەز ـ نەسىھەت ئېلىشىڭلار (يەنى بۇ گۈزەل ئەخلاق ـ ئەدەبكە ئەمەل قىلىشىڭلار) ئۈچۈن بۇ (يەنى ئىجازەت سوراپ ۋە سالام بېرىپ كىرىش ئۇشتۇمتۇت كىرىشتىن) سىلەرگە ياخشىدۇر. ');
INSERT INTO "saleh" VALUES(24,28,'24:28',' ئەگەر باشقىلارنىڭ ئۆيلىرىدە ھېچ ئادەم بولمىسا سىلەرگە ئىجازەت بېرىلمىگۈچە كىرمەڭلار، ئەگەر سىلەرگە قايتىڭلار دېيىلسە قايتىپ كېتىڭلار، قايتىپ كېتىش سىلەر ئۈچۈن ئەڭ ياخشىدۇر، ئاللاھ سىلەرنىڭ قىلىدىغان ئەمەللىرىڭلارنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,29,'24:29','شەخسىي تۇرالغۇ بولمىغان (يەنى دۇكان ـ سارايلارغا ئوخشاش) ئۆيلەرگە ھاجىتىڭلار چۈشۈپ كىرسەڭلار، سىلەرگە ھېچ گۇناھ يوقتۇر. ئاللاھ سىلەرنىڭ ئاشكارىلىغىنىڭلارنىمۇ، يوشۇرغىنىڭلارنىمۇ بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(24,30,'24:30','مۆمىن ئەرلەرگە ئېيتقىنكى، (نا مەھرەملەرگە) تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى (زىنادىن) ساقلىسۇن، مۇنداق قىلىش ئۇلار ئۈچۈن ئەڭ ياخشىدۇر، ئاللاھ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىدىن تولۇق خەۋەردار.');
INSERT INTO "saleh" VALUES(24,31,'24:31','مۆمىنەلەرگە ئېيتقىنكى، نامەھرەملەرگە تىكىلىپ قارىمىسۇن، ئەۋرەتلىرىنى ياپسۇن. تاشقى زىننەتلىرىدىن باشقا زىننەتلىرىنى ئاشكارىلىمىسۇن. لېچەكلىرى بىلەن كۆكرەكلىرىنى ياپسۇن. (تاشقى زىننەتلىرىدىن باشقا) زىننەتلىرىنى ئەرلىرىدىن، ئاتىلىرىدىن، قېيىن ئاتىلىرىدىن، ئوغۇللىرىدىن، ئەرلىرىنىڭ ئوغۇللىرىدىن، ئۆز قېرىنداشلىرىدىن، قېرىنداشلىرىنىڭ ئوغۇللىرىدىن، ھەمشىرىلىرىنىڭ ئوغۇللىرىدىن، دىنداش ئاياللاردىن، قول ئاستىدىكى چۆرىلەردىن، خوتۇنلارغا ئېھتىياجى يوق خىزمەتچىلەر (يەنى قېرى، دەلدۈش بولغانلىقتىن جىنسىي شەھۋىتى يوقلار) دىن، ئاياللارنىڭ ئۇياتلىق جايلىرىنى ئۇقمايدىغان (يەنى بالاغەتكە يەتمىگەن) بالىلاردىن باشقا كىشىلەرگە كۆرسەتمىسۇن، زىننەتلىرىنى كىشىلەرگە بىلدۈرۈش ئۈچۈن ئاياغلىرىنى يەرگە ئۇرمىسۇن. ئى مۆمىنلەر! بەختكە ئېرىشىشىڭلار ئۈچۈن ھەممىڭلار ئاللاھقا تەۋبە قىلىڭلار.');
INSERT INTO "saleh" VALUES(24,32,'24:32','ئاراڭلاردىكى بويتاق ئەر، ئاياللارنىڭ ۋە ياخشى قۇل ۋە ياخشى چۆرىلىرىڭلارنىڭ بېشىنى ئوڭلاپ قويۇڭلار؛ ئەگەر ئۇلار يوقسۇز بولىدىغان بولسا (ئۇلارنىڭ يوقسۇزلۇقى سىلەرنىڭ ئۇلارنىڭ بېشىنى ئوڭلاپ قويۇشۇڭلارغا توسۇق بولمىسۇن)، ئاللاھ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلىدۇ، ئاللاھنىڭ (كەرىمى) كەڭدۇر، ئاللاھ بەندىلەرنىڭ مەنپەئىتىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(24,33,'24:33','ئۆيلىنەلمەيدىغانلار ئاللاھ ئۇلارنى ئۆز كەرىمى بىلەن باي قىلغىچە، ئۆزلىرىنى ئىپپەتلىك تۇتسۇن، قۇللىرىڭلاردىن توختام تۈزۈشنى تىلەيدىغانلار (يەنى خوجىسىغا مۇئەييەن پۇل ـ مال تۆلەش شەرتى بىلەن ئۆزىنىڭ ئازاد قىلىنىشىنى تىلەيدىغانلار) بىلەن، ئەگەر ئۇلارنىڭ ساداقىتىنى بايقىساڭلار، توختام تۈزۈڭلار، سىلەرگە ئاللاھ ئاتا قىلغان مال ـ مۈلۈكنىڭ بىر قىسمىنى (ئۆزلىرىنى ئازاد قىلىشقا ياردەم تەرىقىسىدە) ئۇلارغا بېرىڭلار، ئەگەر سىلەرنىڭ چۆرىلىرىڭلار ئىپپەتلىك بولۇشنى خالىسا، بۇ دۇنيانىڭ ئازغىنا مېلىنى دەپ ئۇلارنى پاھىشىغا مەجبۇرلىماڭلار، كىمكى ئۇلارنى پاھىشىغا مەجبۇرلايدىكەن، مەجبۇرلانغاندىن كېيىن ئاللاھ ئۇلارنى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا)رەھىم قىلغۇچىدۇر(يەنى پاھىشىغا مەجبۇرلانغانلىقى ئۈچۈن، ئاللاھ ئۇلارنى جاۋابكارلىققا تارتمايدۇ، مەجبۇرلىغۇچىلارنى قاتتىق جازالايدۇ). ');
INSERT INTO "saleh" VALUES(24,34,'24:34',' (ئى مۆمىنلەر!) بىز سىلەرگە روشەن ئايەتلەرنى، سىلەردىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن مىساللارنى ۋە تەقۋادارلار ئۈچۈن ۋەز – نەسىھەتلەرنى شەك – شۈبھىسىز نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(24,35,'24:35','ئاللاھ ئاسمانلارنىڭ ۋە زېمىننىڭ نۇرىدۇر، ئاللاھنىڭ (مۆمىن بەندىسىنىڭ قەلبىدىكى) نۇرى خۇددى (چىراق قويىدىغان) تەكچىگە ئوخشايدۇ، ئۇنىڭدا چىراق باردۇر، چىراق شىشىنىڭ ئىچىدىدۇر، شىشە گويا نۇرلۇق يۇلتۇزدۇر، چىراق مۇبارەك زەيتۇن دەرىخىنىڭ (يېغى) بىلەن يورۇتۇلغان، ئۇ (يەنى زەيتۇن دەرىخى) شەرق تەرەپتىمۇ ئەمەس، غەرب تەرەپتىمۇ ئەمەس (سەھرادا ئوچۇقچىلىقتا بولۇپ، كۈن بويى ئۇنىڭغا كۈن نۇرى چۈشۈپ تۇرىدۇ، شۇنىڭ بىلەن ئۇنىڭ مېۋىسى پىشىپ يېتىلگەن بولىدۇ)، يېغى (ئەڭ سۈزۈك بولىدۇ، سۈزۈكلۈكىدىن) ئوت تەگمىسىمۇ يورۇپ كېتەيلا دەپ قالىدۇ، (ئوت بىلەن يورۇتۇلسا) نۇر ئۈستىگە نۇر قوشۇلىدۇ، ئاللاھ خالىغان كىشىنى ئۇنىڭ نۇرىغا (يەنى قۇرئانغا) (ئەگىشىشكە) مۇۋەپپەق قىلىدۇ، ئاللاھ (ئىبرەت ئالسۇن دەپ) كىشىلەرگە مىساللارنى (ئۇلارنىڭ پەمىگە يېقىنلاشتۇرۇپ) بايان قىلىدۇ، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(24,36,'24:36','ئاللاھنىڭ ئۇلۇغلىنىشى ۋە ئىسمىنىڭ ياد ئېتىلىشى بۇيرۇلغان مەسجىدلەردە (مۆمىنلەر) ئەتىگەن – ئاخشامدا تەسبىھ ئېيتىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(24,37,'24:37',' ئۇلار شۇنداق ئەرلەركى، سودا ـ سېتىق ئۇلارنى ئاللاھنى زىكىر قىلىشتىن، ناماز ئۆتەشتىن، زاكات بېرىشتىن غەپلەتتە قالدۇرمايدۇ، ئۇلار دىللار ۋە كۆزلەر قالايمىقانلىشىپ كېتىدىغان كۈن (يەنى قىيامەت كۈنى) دىن قورقىدۇ.');
INSERT INTO "saleh" VALUES(24,38,'24:38','ئاللاھنى ئۆزلىرىنىڭ قىلغان ياخشى ئەمەللىرىگە مۇكاپات بەرسۇن ۋە ئۆزىنىڭ پەزلىدىن ئاشۇرۇپ بەرسۇن دەپ (ئۇلار شۇنداق ئىبادەتلەرنى قىلىدۇ). ئاللاھ خالىغان ئادەمگە ھېسابسىز رىزىق بېرىدۇ. ');
INSERT INTO "saleh" VALUES(24,39,'24:39','كاپىرلارنىڭ (ياخشى ئەمەل دەپ ئويلىغان) ئەمەللىرى باياۋاندىكى سەرابقا ئوخشايدۇ، تەشنا ئادەم ئۇنى سۇ دەپ گۇمان قىلىدۇ، ئۇنىڭ يېنىغا كەلسە ھېچ نەرسە كۆرۈنمەيدۇ (يەنى سۇ كۆرمەيدۇ)، ئۇ ئاللاھنىڭ ئۆز (ئەمەلىنىڭ) يېنىدا (كۆزىتىپ تۇرغۇچى) ئىكەنلىكىنى بايقايدۇ، ئاللاھ ئۇنىڭ قىلمىشىغا تېگىشلىك جازا بېرىدۇ، ئاللاھ تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,40,'24:40','ياكى ئۇلارنىڭ (ياخشى دەپ گۇمان قىلغان) ئەمەللىرى چوڭقۇر دېڭىزدىكى قاراڭغۇلۇققا ئوخشايدۇ، ئۇنى (يەنى دېڭىزنى) قاتمۇقات دولقۇنلار ۋە دولقۇنلارنىڭ ئۈستىدىكى قارا بۇلۇتلار قاپلاپ تۇرىدۇ. (مانا بۇ) قاتمۇقات قاراڭغۇلۇقلاردۇر، (بۇ قاراڭغۇلۇققا گىرىپتار بولغۇچى) قولىنى ئۇزاتسا بارماقلىرىنى كۆرەلمەيدۇ، ئاللاھ نۇر ئاتا قىلمىسا، كىم بولمىسۇن، نۇرنى كۆرەلمەيدۇ (يەنى ئاللاھ ھىدايەت قىلمىغان ئادەم ھەرگىز ھىدايەت تاپمايدۇ).');
INSERT INTO "saleh" VALUES(24,41,'24:41',' ئاللاھقا ئاسمانلاردىكى ۋە زېمىندىكىلەرنىڭ ۋە قاناتلىرىنى كەرگەن ھالدا (ئۇچۇپ كېتىۋاتقان) قۇشلارنىڭ تەسبىھ ئېيتىدىغانلىقىنى سەن بىلمەمسەن؟ ئاللاھ ھەر(مەخلۇق) نىڭ دۇئاسىنى ۋە تەسبىھىنى بىلىدۇ، ئاللاھ ئۇلارنىڭ قىلىدىغان ئىشلىرىنى تولۇق بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(24,42,'24:42','ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، (خالايىقنىڭ) ئاخىر قايتىدىغان جايى ئاللاھنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(24,43,'24:43','كۆرمەمسەنكى، ئاللاھ بۇلۇتلارنى ھەيدەيدۇ، ئاندىن ئۇلارنى بىر - بىرىگە قوشىدۇ، ئاندىن ئۇلارنى توپلايدۇ، شۇنىڭ بىلەن بۇلۇتنىڭ ئارىسىدىن يامغۇر ياغقانلىقىنى كۆرىسەن، ئاللاھ ئاسماندىكى تاغدەك بۇلۇتلاردىن مۆلدۈر ياغدۇرىدۇ، ئۇنى ئاللاھ خالىغان ئادەمگە يەتكۈزىدۇ (يەنى ئۇنىڭ بىلەن خالىغان ئادەمنىڭ زىرائىتى، مېۋىسى ۋە چارۋىلىرىغا زىيان يەتكۈزىدۇ) ۋە ئۇنىڭ (زىيىنىدىن) خالىغان ئادەمنى ساقلايدۇ، چاقماقنىڭ يورۇقى (قارىغۇچىلارنى) كۆزلىرىدىن ئايرىۋەتكىلى تاس قالىدۇ.');
INSERT INTO "saleh" VALUES(24,44,'24:44','ئاللاھ كېچە بىلەن كۈندۈزنى ئالماشتۇرۇپ تۇرىدۇ، بۇنىڭدا ئەقىل ئىگىلىرى ئۈچۈن ئەلۋەتتە ئىبرەت بار.');
INSERT INTO "saleh" VALUES(24,45,'24:45',' ئاللاھ ھەر بىر جاندارنى سۇدىن ياراتتى، ئۇلارنىڭ بەزىسى قورسىقى بىلەن ماڭىدۇ، بەزىسى ئىككى پۇت بىلەن ماڭىدۇ، بەزىسى تۆت پۇت بىلەن ماڭىدۇ، ئاللاھ خالىغان مەخلۇقاتلارنى يارىتىدۇ، ئاللاھ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(24,46,'24:46','شۈبھىسىزكى، بىز روشەن ئايەتلەرنى نازىل قىلدۇق، ئاللاھ ئۆزى خالىغان كىشىلەرنى توغرا يولغا باشلايدۇ.');
INSERT INTO "saleh" VALUES(24,47,'24:47',' (مۇناپىقلار) «ئاللاھقا ۋە پەيغەمبەرگە ئىمان ئېيتتۇق ۋە ئىتائەت قىلدۇق» دېيىشىدۇ، ئاندىن ئۇلاردىن بىر جامائە شۇنىڭدىن (يەنى ئىماننى دەۋا قىلغاندىن) كېيىن، (شەرىئەت ھۆكمىدىن) يۈز ئۆرۈيدۇ، ئۇلار (ھەقىقەتتە) مۆمىن ئەمەس.');
INSERT INTO "saleh" VALUES(24,48,'24:48',' ئۇلارنىڭ ئارىسىدا (پەيغەمبەر) ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار ئاللاھنىڭ ۋە پەيغەمبەرنىڭ (ھۆكمىگە) چاقىرىلسا، ئۇلاردىن بىر جامائە (پەيغەمبەرنىڭ ئالدىغا ھازىر بولۇشتىن) باش تارتىدۇ.');
INSERT INTO "saleh" VALUES(24,49,'24:49',' ئەگەر ھەقىقەت ئۇلارنىڭ تەرىپىدە بولىدىغان بولسا (پەيغەمبەر ئەلەيھىسسالامنىڭ ھەق ھۆكۈم چىقىرىدىغانلىقىنى بىلىدىغانلىقلىرى ئۈچۈن)، پەيغەمبەرگە بويسۇنغان ھالدا كېلىشىدۇ.');
INSERT INTO "saleh" VALUES(24,50,'24:50',' ئۇلارنىڭ دىللىرىدا (مۇناپىقلىق) ئىللىتى بارمۇ؟ ياكى (مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىدىن) گۇمانلىنامدۇ؟ يا ئاللاھنىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئۇلارغا ئادىل بولماسلىقىدىن قورقامدۇ؟ ياق، ئۇلار (رەسۇلۇللاھنىڭ ھۆكمىدىن باش تارتقانلىقلىرى ئۈچۈن) زالىمدۇر.');
INSERT INTO "saleh" VALUES(24,51,'24:51',' (پەيغەمبەر) مۆمىنلەرنىڭ ئارىسىدا ھۆكۈم چىقىرىش ئۈچۈن، ئۇلار ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە چاقىرىلغان چاغدا ئۇلار: «ئاڭلىدۇق ۋە ئىتائەت قىلدۇق، دېيىشلىرى كېرەك، مانا شۇنداق كىشىلەر مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(24,52,'24:52','ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلغان، ئاللاھتىن قورققان ۋە ئۇنىڭغا تەقۋادارلىق قىلغان كىشىلەر بەختكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(24,53,'24:53','(مۇناپىقلار) ئەگەر سەن ئۇلارنى (جىھادقا) چىقىشقا ئەمر قىلساڭ، چوقۇم چىقىدىغانلىقىغا ئاللاھ بىلەن كۈچلۈك قەسەم قىلىدۇ. ئېيتقىنكى، «قەسەم قىلماڭلار، (قەسىمىڭلار يالغان، سىلەردىن تەلەپ قىلىنىدىغىنى) ساداقەتلىك بىلەن ئىتائەت قىلىشتۇر، ئاللاھ سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن خەۋەرداردۇر».');
INSERT INTO "saleh" VALUES(24,54,'24:54','ئېيتقىنكى: «ئاللاھقا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەگەر ئۇلار (ئاللاھقا ئىتائەت قىلىشتىن) باش تارتسا، پەيغەمبەرنىڭ ئەلچىلىكنى يەتكۈزۈش مەسئۇلىيىتى بار، سىلەرنىڭ ئىتائەت قىلىش مەسئۇلىيىتىڭلار بار، ئەگەر پەيغەمبەرنىڭ (ئەمرىگە) ئىتائەت قىلساڭلار، ھىدايەت تاپىسىلەر، پەيغەمبەرنىڭ پەقەت چۈشىنىشلىك قىلىپ تەبلىغ قىلىش مەسئۇلىيىتى بار».');
INSERT INTO "saleh" VALUES(24,55,'24:55','ئاللاھ ئىچىڭلاردىكى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرگە، ئۇلاردىن بۇرۇن ئۆتكەنلەرنى زېمىندا ھۆكۈمران قىلغاندەك، ئۇلارنىمۇ چوقۇم ھۆكۈمران قىلىشىنى، ئۇلار ئۈچۈن تاللىغان دىنىنى چوقۇم مۇستەھكەم قىلىپ بېرىشنى ۋە ئۇلارنىڭ قورقۇنچىسىنى ئامانلىققا ئايلاندۇرۇپ بېرىشنى ۋەدە قىلدى، ئۇلار ماڭا ئىبادەت قىلىدۇ، ماڭا ھېچ نەرسىنى شېرىك كەلتۈرمەيدۇ، شۇ ۋەدىدىن كېيىن كاپىر بولغانلار ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(24,56,'24:56','ئاللاھنىڭ رەھمىتىگە ئېرىشىشىڭلار ئۈچۈن ناماز ئوقۇڭلار، زاكات بېرىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(24,57,'24:57','كاپىرلارنى زېمىندا ئاللاھنىڭ ئازابىدىن قېچىپ قۇتۇلالايدۇ دەپ ئويلىمىغىن، ئۇلارنىڭ جايى دوزاخ بولىدۇ، دوزاخ نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(24,58,'24:58',' ئى مۆمىنلەر! سىلەرنىڭ قۇللىرىڭلار، چۆرىلىرىڭلار ۋە ئىچىڭلاردىكى بالاغەتكە يەتمىگەن بالىلار ناماز بامداتتىن بۇرۇن، چۈشتە (ئۇخلاش ئۈچۈن) كىيىمىڭلارنى سالغان چېغىڭلاردا ۋە خۇپتەندىن كېيىن (مۇشۇنداق) ئۈچ ۋاقىتتا (يېنىڭلارغا كىرسە) سىلەردىن ئىجازەت سورىسۇن، (بۇ) ئۈچ ۋاقىت سىلەرنىڭ ئەۋرىتىڭلار (ئېچىلىپ قالىدىغان ۋاقىتتۇر)، بۇ ئۈچ ۋاقىتتىن باشقا ۋاقىتلاردا (ئۇلار سىلەرگە كىرسە) سىلەرگىمۇ، ئۇلارغىمۇ ھېچ گۇناھ بولمايدۇ، سىلەر بىر ـ بىرىڭلار بىلەن بېرىش ـ كېلىش قىلىپ تۇرىسىلەر، ئاللاھ سىلەرگە ئايەتلەرنى (يەنى شەرىئەت ئەھكاملىرىنى) ئەنە شۇنداق بايان قىلىدۇ، ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,59,'24:59',' كىچىك بالىلىرىڭلار بالاغەتكە يەتكەندە، ئۇلار ئىلگىرىكىلەر (يەنى چوڭلار كىرىشتە) ئىجازەت سورىغاندەك، (ئۆيگە كىرىشتە ھەممە ۋاقىت) ئىجازەت سورىسۇن، ئاللاھ ئايەتلىرىنى (دىننىڭ ئىشلىرىنى) سىلەرگە شۇنداق بايان قىلىدۇ، ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,60,'24:60','ياتلىق بولۇشنى خالىمايدىغان ياشانغان ئاياللار زىننەتلىرىنى ئاشكارىلىمىغان ھالدا (رىدا، نىمچىغا ئوخشاش) تاشقى كىيىملىرىنى سېلىۋەتسە (ئەرلەرنىڭ ئالدىدا ئادەتتىكى كىيىملىرى بىلەن يۈرسە)، ھېچ گۇناھ يوقتۇر، تاشقى كىيىملىرىنى سېلىۋېتىشتىن ساقلانسا ئۇلار ئۈچۈن ياخشىدۇر، ئاللاھ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(24,61,'24:61',' (جىھادقا چىقالمىسا) ئەماغا گۇناھ يوقتۇر، توكۇرغا گۇناھ يوقتۇر، كېسەلگە گۇناھ يوقتۇر. (ئى ئىنسانلار!) سىلەر ئۆز ئۆيلىرىڭلاردا (يەنى ئەرلىرىڭلارنىڭ، ئاياللىرىڭلارنىڭ ئۆيلىرىدە) ئاتىلىرىڭلارنىڭ ئۆيلىرىدە، ئانىلىرىڭلارنىڭ ئۆيلىرىدە، قېرىنداشلىرىڭلارنىڭ ئۆيلىرىدە، ھەمشىرەڭلارنىڭ ئۆيلىرىدە، ئاتاڭلارنىڭ قېرىنداشلىرىنىڭ ئۆيلىرىدە، ئاتاڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، ئاناڭلارنىڭ قېرىنداشلىرىنىڭ ئۆيلىرىدە، ئاناڭلارنىڭ ھەمشىرىلىرىنىڭ ئۆيلىرىدە، سىلەر ئاچقۇچلىرىنى باشقۇرىدىغانلارنىڭ ئۆيلىرىدە (يەنى ئۆي ئىگىسى سىرتقا چىقىپ كېتىپ، ئۆي بىلەن ئاچقۇچنى سىلەرگە تاپشۇرغانلارنىڭ ئۆيلىرىدە)، ياكى دوستلىرىڭلارنىڭ ئۆيلىرىدە (ئىجازەتسىز) بىرەر نېمە يېسەڭلار گۇناھ يوقتۇر، سىلەر توپلىشىپ يېسەڭلارمۇ، يا يەككە يېسەڭلارمۇ گۇناھ يوقتۇر. سىلەر ئۆيلەرگە كىرگەن چېغىڭلاردا ئۆزۈڭلارغا (يەنى ئۆيدىكى كىشىلەرگە) ئاللاھ بەلگىلىگەن مۇبارەك، پاك سالامنى بېرىڭلار (يەنى ئەسسالامۇ ئەلەيكۇم دەڭلار)، ئاللاھ سىلەرنى چۈشەنسۇن، دەپ ئايەتلەرنى مۇشۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(24,62,'24:62',' ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتقان كىشىلەر كامىل مۆمىنلەردۇر، ئۇلار (مۇسۇلمانلارغا پايدىلىق) بىر ئىش ئۈستىدە (مەسلىھەتلىشىش ئۈچۈن) پەيغەمبەر بىلەن جەم بولغان چاغلىرىدا، پەيغەمبەردىن ئىجازەت سورىمىغىچە كېتىپ قالمايدۇ، سەندىن ئىجازەت سورايدىغانلار ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىدىغان كىشىلەردۇر، بەزى شەخسىي ئىش ئۈچۈن ئۇلار سەندىن ئىجازەت سورىسا، ئۇلارنىڭ ئىچىدىن سەن خالىغان ئادەملەرگە ئىجازەت بەرگىن، ئۇلارغا سەن ئاللاھتىن مەغپىرەت تىلىگىن، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(24,63,'24:63',' (ئى مۆمىنلەر!) پەيغەمبەرنى سىلەر بىر ـ بىرىڭلارنى چاقىرغاندەك چاقىرماڭلار. سىلەردىن (پەيغەمبەرنىڭ سۆھبىتىدىن) ئاستا سۇغۇرۇلۇپ غىپپىدە چىقىپ كەتكەنلەرنى ئاللاھ ئەلۋەتتە بىلىدۇ، پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلغۇچىلار (دۇنيادا چوڭ بىر) پىتنىگە يولۇقۇشتىن، ياكى (ئاخىرەتتە) قاتتىق بىر ئازابقا دۇچار بولۇشتىن قورقسۇن.');
INSERT INTO "saleh" VALUES(24,64,'24:64','بىلىڭلاركى، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، ئاللاھنىڭ مەخلۇقاتىدۇر، ئاللاھنىڭ تەسەررۇپى ئاستىدىدۇر)، ئاللاھ سىلەرنىڭ ئەمەلىي ئەھۋالىڭلارنى بىلىپ تۇرىدۇ، بەندىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىدىغان كۈندە (يەنى قىيامەت كۈنىدە) ئاللاھ ئۇلارنىڭ قىلغان ئىشلىرىنى ئۇلارغا ئېيتىپ بېرىدۇ، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(25,1,'25:1','پۈتۈن جاھان ئەھلىنى (ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇرغۇچى بولۇشى ئۈچۈن، بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامغا) ھەق بىلەن باتىلنى ئايرىغۇچى قۇرئاننى نازىل قىلغان ئاللاھنىڭ بەرىكىتى بۈيۈكتۇر.');
INSERT INTO "saleh" VALUES(25,2,'25:2',' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھنىڭ بالىسى يوقتۇر، پادىشاھلىقتا شېرىكىمۇ يوقتۇر، ئاللاھ ھەممە نەرسىنى ياراتتى، ئاندىن (ئۆزىنىڭ خاھىشى بويىچە) ئۇنىڭغا مۇناسىپ ئۆلچەمنى بەلگىلىدى.');
INSERT INTO "saleh" VALUES(25,3,'25:3',' مۇشرىكلار ئاللاھنى قويۇپ، ھېچ نەرسىنى يارىتالمايدىغان ۋە ئۆزلىرى يارىتىلغان ئىلاھلار (يەنى بۇتلار) غا ئىبادەت قىلدى. ئۇلار (يەنى بۇتلار) ئۆزلىرىدىن بىرەر زىياننى دەپئى قىلالمايدۇ. ئۆزلىرىگە بىرەر پايدىنى كەلتۈرەلمەيدۇ، (باشقىلارنىڭ) جېنىنى ئېلىشقا، (باشقىلارغا) ھايات بېغىشلاشقا ياكى (ئۆلۈكلەرنى) تىرىلدۈرۈشكە قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(25,4,'25:4','كاپىرلار: «بۇ (يەنى قۇرئان) پەقەت ئۇ (يەنى مۇھەممەد) ئۆزى توقۇغان يالغاندۇر، ئۇنىڭغا ئۇنى توقۇشقا باشقا بىر قەۋم (يەنى ئەھلى كىتاب) ياردەم بەرگەن» دەيدۇ، ئۇلار (پەيغەمبەر ئەلەيھىسسالامغا بوھتان چاپلاش بىلەن ئۇنىڭغا) زۇلۇم قىلدى ۋە يالغاننى چاپلىدى.');
INSERT INTO "saleh" VALUES(25,5,'25:5',' ئۇلار ئېيتتى: «(بۇ) بۇرۇنقىلارنىڭ ئەپسانىلىرىدۇركى، ئۇلارنى ئۇ يازدۇرۇۋالغان، (ھىفزى قىلىۋېلىشى ئۈچۈن) ئۇنىڭغا ئەتىگەن - ئاخشامدا ئوقۇپ بېرىلىۋاتىدۇ».');
INSERT INTO "saleh" VALUES(25,6,'25:6','ئېيتقىنكى، «ئۇنى ئاسمانلاردىكى ۋە زېمىندىكى سىرنى بىلىپ تۇرىدىغان ئاللاھ نازىل قىلدى، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، (بەندىلەرگە) ناھايىتى مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(25,7,'25:7','مۇشرىكلار (مۇھەممەد ئەلەيھىسسالامنى مەسخىرە قىلىپ) ئېيتتى: «بازارلاردا (بىز تاماق يېگەندەك) تاماق يەيدىغان، (بىز ماڭغاندەك) مېڭىپ يۈرىدىغان بۇ قانداق پەيغەمبەر؟ ئۇنىڭ بىلەن بىللە ئاگاھلاندۇرغۇچى بولۇش ئۈچۈن ئاللاھ ئۇنىڭغا نېمىشقا بىر پەرىشتە ئەۋەتمىدى.');
INSERT INTO "saleh" VALUES(25,8,'25:8',' يا ئۇنىڭغا بىر خەزىنە بېرىلمىدى، يا ئۇنىڭغا مېۋىلىرىنى يەيدىغان بىر باغ بېرىلمىدى؟» زالىملار (يەنى كاپىرلار): «سىلەر پەقەت سېھىرلەنگەن بىر ئادەمگە ئەگىشىۋاتىسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(25,9,'25:9','مۇشرىكلارنىڭ سېنىڭ ئۈچۈن نۇرغۇن مىساللارنى كەلتۈرگەنلىكىگە قارىغىن، ئۇلار ئازدى، توغرا يول تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(25,10,'25:10','ئاللاھنىڭ بەرىكىتى بۈيۈكتۇركى، ئەگەر ئۇ خالىسا ساڭا ئۇلارنىڭ ئېيتقانلىرىدىنمۇ ياخشى، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان باغلارنى ۋە چوڭ سارايلارنى ئاتا قىلاتتى.');
INSERT INTO "saleh" VALUES(25,11,'25:11','ياق، ئۇلار قىيامەتنى ئىنكار قىلدى، قىيامەتنى ئىنكار قىلغانلارغا بىز دوزاخنى تەييارلىدۇق. ');
INSERT INTO "saleh" VALUES(25,12,'25:12','دوزاخ ئۇلارنى يىراقتىن كۆرگەن چاغدا، ئۇلار دوزاخنىڭ غەزەپتىن قاينىغان ۋە (ئېشەكتەك) ھاڭرىغان ئاۋازنى ئاڭلايدۇ.');
INSERT INTO "saleh" VALUES(25,13,'25:13',' ئۇلار زەنجىر بىلەن باغلانغان ھالدا دوزاخنىڭ تار يېرىگە تاشلانغان چاغدا، بۇ يەردە ئۇلار (ئۆزلىرىنىڭ) ئۆلۈمىنى تىلەيدۇ.');
INSERT INTO "saleh" VALUES(25,14,'25:14',' (ئۇلارغا) بۈگۈن سىلەر بىر ئۆلۈمنى ئەمەس، تالاي ئۆلۈملەرنى تىلەڭلار (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(25,15,'25:15','ئۇلارغا ئېيتقىنكى، «شۇ (دوزاخ) ياخشىمۇ يا تەقۋادارلارغا ۋەدە قىلىنغان، (كىرگەن كىشى) مەڭگۈ قالىدىغان جەننەت ياخشىمۇ؟» جەننەت تەقۋادارلارغا بېرىلگەن مۇكاپاتتۇر ۋە ئۇلارنىڭ ئاخىرى بارىدىغان جايىدۇر.');
INSERT INTO "saleh" VALUES(25,16,'25:16','ئۇلار جەننەتتە خالىغان نېمەتلەردىن بەھرىمەن بولىدۇ، جەننەتتە مەڭگۈ قالىدۇ، بۇ پەرۋەردىگارىڭدىن ئىشقا ئاشۇرۇش تىلىنىدىغان ۋەدىدۇر.');
INSERT INTO "saleh" VALUES(25,17,'25:17',' شۇ كۈندە ئاللاھ ئۇلارغا قوشۇپ، ئۇلار ئاللاھنى قويۇپ چوقۇنغان نەرسىلىرىنى يىغىدۇ. ئاندىن ئاللاھ (ئۇلارغا): «مېنىڭ بۇ بەندىلىرىمنى سىلەر ئازدۇردۇڭلارمۇ؟ ياكى (توغرا) يولدىن ئۇلار ئۆزلىرى ئازدىمۇ؟» دەيدۇ. ');
INSERT INTO "saleh" VALUES(25,18,'25:18',' (ئىبادەت قىلىنغۇچىلار) ئېيتىدۇ: «ئى ئاللاھ! سەن پاكتۇرسەن، سېنى قويۇپ باشقىلارنى دوست تۇتۇش (يەنى سەندىن باشقىغا چوقۇنۇش) بىزگە (ۋە سېنىڭ مەخلۇقاتىڭدىن ھېچ ئەھەدىگە) لايىق ئەمەس، لېكىن سەن ئۇلارنى ۋە ئۇلارنىڭ ئاتا -بوۋىلىرىنى شۇ قەدەر نېمەتكە چۆمدۈردۇڭكى، ئۇلار سېنى ياد ئېتىشنى ئۇنتۇدى. ئۇلار ھالاك بولغۇچى قەۋم بولدى».');
INSERT INTO "saleh" VALUES(25,19,'25:19','شۈبھىسىزكى، ئىبادەت قىلىنغۇچىلار سىلەرنىڭ (ئۇلارنى مەبۇد دېگەن) سۆزۈڭلارنى ئىنكار قىلدى، (ئى كۇففارلار!) سىلەر (ئۆزۈڭلاردىن ئازابنى) دەپئى قىلىشقىمۇ ۋە (بۇ بالادىن ئۆزۈڭلارنى قۇتۇلدۇرۇشقا) ياردەم بېرىشكىمۇ قادىر بولالمايسىلەر، سىلەردىن كىمكى (ئاللاھقا شېرىك كەلتۈرۈش بىلەن ئۆزىگە) زۇلۇم قىلىدىكەن، ئۇنىڭغا بىز (ئاخىرەتتە) قاتتىق ئازابنى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(25,20,'25:20','بىز سەندىن ئىلگىرى ئەۋەتكەن پەيغەمبەرلەرنىڭ ھەممىسىلا ئەلۋەتتە تاماق يەيتتى، بازارلاردا مېڭىپ يۈرەتتى، بەزىڭلارنى بەزىڭلار بىلەن سىنىدۇق، سەۋر قىلامسىلەر؟ (يەنى سەۋر قىلىڭلار) پەرۋەردىگارىڭ (سەۋر قىلغۇچىلار بىلەن سەۋر قىلمىغۇچىلارنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(25,21,'25:21','بىزگە مۇلاقات بولۇشنى ئۈمىد قىلمايدىغانلار (يەنى مۇشرىكلار): «نېمىشقا بىزگە پەرىشتىلەر چۈشۈرۈلمەيدۇ، ياكى (بىز نېمىشقا) پەرۋەردىگارىمىزنى كۆرمەيمىز؟» دېيىشتى. شۈبھىسىزكى، ئۇلار ئۆزلىرىنى چوڭ تۇتتى ۋە تولىمۇ ھەددىدىن ئاشتى.');
INSERT INTO "saleh" VALUES(25,22,'25:22',' ئۇلار (يەنى مۇشرىكلار) (ئۆزلىرىنىڭ جېنىنى ئېلىش ئۈچۈن چۈشكەن) پەرىشتىلەرنى كۆرگەن كۈنى گۇناھكارلارغا خۇشخەۋەر بولمايدۇ، پەرىشتىلەر ئۇلارغا: «(سىلەرگە جەننەت) ھارامدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(25,23,'25:23',' بىز ئۇلارنىڭ قىلغان ياخشى ئەمەللىرىنى بىر تەرەپ قىلىپ، ئۇنى ئۇچۇپ يۈرگەن توزاندەك قىلىۋېتىمىز، (يەنى ئۇلار ئىمانسىز بولغانلىقتىن، قىلغان ياخشى ئەمەللىرىنى يوققا چىقىرىمىز).');
INSERT INTO "saleh" VALUES(25,24,'25:24',' ئەھلى جەننەت بۇ كۈندە ئەڭ ياخشى جايدا ۋە ئەڭ ياخشى ئارامگاھتا بولىدۇ.');
INSERT INTO "saleh" VALUES(25,25,'25:25',' شۇ كۈندە ئاسمان بۇلۇت بىلەن بىرلىكتە يېرىلىدۇ، پەرىشتىلەر (بەندىلەرنىڭ نامە ـ ئەمالىنى ئېلىپ) ئارقىمۇئارقا چۈشىدۇ.');
INSERT INTO "saleh" VALUES(25,26,'25:26',' پادىشاھلىق بۇ كۈندە مەرھەمەتلىك ئاللاھقا خاستۇر، بۇ كۈن كاپىرلارغا قېيىندۇر.');
INSERT INTO "saleh" VALUES(25,27,'25:27','شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك ـ شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ[27ـ29]. ');
INSERT INTO "saleh" VALUES(25,28,'25:28','شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك ـ شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ[27ـ29]. ');
INSERT INTO "saleh" VALUES(25,29,'25:29','شۇ كۈنى زالىم (يەنى كاپىر) ئىككى قولىنى چىشلەپ: «ئىسىت! پەيغەمبەر بىلەن (نىجاتلىق) يولىنى تۇتسامچۇ، ئىسىت! پالانىنى دوست تۇتمىغان بولسامچۇ؟ قۇرئان ماڭا يەتكەندىن كېيىن، ئۇ (يەنى پالانى) مېنى قۇرئاندىن، شەك ـ شۈبھىسىزكى، ئازدۇردى» دەيدۇ، شەيتان ئىنساننى (ئازدۇرۇپ بولۇپ) تاشلىۋېتىدۇ[27ـ29]. ');
INSERT INTO "saleh" VALUES(25,30,'25:30',' پەيغەمبەر ئېيتتى: «ئى پەرۋەردىگارىم! شۈبھىسىزكى، مېنىڭ قەۋمىم بۇ قۇرئاننى تاشلاندۇق قىلىپ قويدى».');
INSERT INTO "saleh" VALUES(25,31,'25:31','شۇنىڭدەك (يەنى ساڭا قەۋمىڭنىڭ مۇشرىكلىرىدىن دۈشمەنلەرنى قىلغاندەك) ھەربىر پەيغەمبەرگە بىر قىسىم گۇناھكارلارنى ئۇنىڭ دۈشمىنى قىلدۇق، پەرۋەردىگارىڭ يول كۆرسەتكۈچى ۋە ياردەمچى بولۇشقا ساڭا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(25,32,'25:32',' كاپىرلار: «قۇرئان نېمىشقا ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىر قېتىمدىلا نازىل قىلىنمىدى؟» دېدى. سېنىڭ دىلىڭنى مۇستەھكەم قىلىش ئۈچۈن، ئۇنى پارچە ـ پارچە نازىل قىلدۇق .');
INSERT INTO "saleh" VALUES(25,33,'25:33',' ئۇلار (يەنى مۇشرىكلار) قانداق بىر سوئالنى تاشلىمىسۇن، (ئۇنىڭغا قارىتا) بىز ساڭا ھەق جاۋابنى ۋە ھەممىدىن گۈزەل چۈشەندۈرۈشنى نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(25,34,'25:34',' ئۇلار جەھەننەمگە يۈزلىرى بىلەن سۈرۈلىدۇ، ئۇلارنىڭ جايى ئەڭ يامان، يولى ئەڭ ئازغۇندۇر.');
INSERT INTO "saleh" VALUES(25,35,'25:35',' شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق ھەمدە ئۇنىڭ قېرىندىشى ھارۇننى ئۇنىڭغا ياردەمچى قىلدۇق.');
INSERT INTO "saleh" VALUES(25,36,'25:36',' ئۇلارغا: «بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغان قەۋمگە بېرىڭلار» دېدۇق، (ئۇ قەۋم كۇفرىدا چىڭ تۇرغانلىقتىن) ئۇلارنى دەھشەتلىك تۈردە ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(25,37,'25:37','نۇھنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلغان چاغدا ئۇلارنى غەرق قىلدۇق ھەمدە ئۇلارنى كىشىلەرگە ئىبرەت قىلدۇق، زالىملارغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(25,38,'25:38','ئاد قەۋمىنى، سەمۇد قەۋمىنى، رەس ئاھالىسىنى ۋە ئۇلارنىڭ ئارىسىدىكى نۇرغۇن ئۈممەتلەرنى ھەم (ھالاك قىلدۇق). ');
INSERT INTO "saleh" VALUES(25,39,'25:39',' ھەربىر قەۋمگە نۇرغۇن مىساللارنى بايان قىلدۇق، (ۋەز ـ نەسىھەت ئۈنۈم بەرمىگەنلىكتىن) ھەربىر قەۋمنى ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(25,40,'25:40',' ئۇلار ھەقىقەتەن (شامغا بېرىپ كېلىشتە) يامان يامغۇر (يەنى تاش) ياغدۇرۇلغان شەھەردىن ئۆتىدۇ، ئەجەبا ئۇلار ئۇنى كۆرۈپ (ئىبرەت ئالمامدۇ؟) ياق (يەنى ئىبرەت ئالمايدۇ)، ئۇلار (قىيامەت كۈنى) قايتا تىرىلىشنى ئۈمىد قىلمايدۇ.');
INSERT INTO "saleh" VALUES(25,41,'25:41',' ئۇلار سېنى كۆرسە پەقەت مەسخىرە قىلىۋېلىپ: «ئاللاھ پەيغەمبەرقىلىپ ئەۋەتكەن مۇشۇمۇ؟ ئەگەر دىنىمىزدا چىڭ تۇرمىساق (مۇھەممەد) بىزنى ئىلاھلىرىمىزدىن ئازدۇرۇۋەتكىلى تاس قالاتتى» (دېدى). كىمنىڭ يولىنىڭ ئەڭ خاتا ئىكەنلىكىنى ئۇلار (ئاخىرەتتە) ئازابنى كۆرگەن چاغدا بىلىدۇ[41 ـ 42]. ');
INSERT INTO "saleh" VALUES(25,42,'25:42',' ئۇلار سېنى كۆرسە پەقەت مەسخىرە قىلىۋېلىپ: «ئاللاھ پەيغەمبەرقىلىپ ئەۋەتكەن مۇشۇمۇ؟ ئەگەر دىنىمىزدا چىڭ تۇرمىساق (مۇھەممەد) بىزنى ئىلاھلىرىمىزدىن ئازدۇرۇۋەتكىلى تاس قالاتتى» (دېدى). كىمنىڭ يولىنىڭ ئەڭ خاتا ئىكەنلىكىنى ئۇلار (ئاخىرەتتە) ئازابنى كۆرگەن چاغدا بىلىدۇ[41 ـ 42]. ');
INSERT INTO "saleh" VALUES(25,43,'25:43',' ئېيتىپ باقساڭچۇ؟ نەپسى خاھىشىنى ئىلاھ قىلىۋالغان ئادەمگە (نەپسى خاھىشىغا ئەگىشىشتىن ئۇنى ساقلاش ئۈچۈن) ھامىي بولالامسەن؟');
INSERT INTO "saleh" VALUES(25,44,'25:44',' ئۇلارنىڭ تولىسىنى (گېپىڭنى) ئاڭلايدىغان ياكى چۈشىنىدىغان ئادەملەر دەپ گۇمان قىلامسەن؟ ئۇلار (چۈشەنمەسلىكتە) پەقەت چاھارپايلارغا ئوخشاشتۇر، بەلكى ئۇلاردىنمۇ بەتتەر گۇمراھتۇر.');
INSERT INTO "saleh" VALUES(25,45,'25:45',' پەرۋەردىگارىڭنىڭ سايىنى قانداق سوزغانلىقىنى كۆرمىدىڭمۇ؟ ئەگەر خالىسا ئۇنى ئەلۋەتتە مۇقىم قىلاتتى، ئاندىن قۇياشنى سايىگە دەلىل قىلدۇق (يەنى كۈننىڭ چىققانلىقىنى سايىنىڭ بارلىقىغا دەلىل قىلدۇق).');
INSERT INTO "saleh" VALUES(25,46,'25:46',' ئاندىن ئۇنى ئاستا – ئاستا يوقاتتۇق.');
INSERT INTO "saleh" VALUES(25,47,'25:47','ئاللاھ سىلەرگە كېچىنى لىباس، ئۇيقۇنى (بەدىنىڭلار ئۈچۈن) راھەت قىلدى، كۈندۈزنى (تىرىكچىلىك ئۈچۈن زېمىندا) يېيىلىدىغان ۋاقىت قىلدى.');
INSERT INTO "saleh" VALUES(25,48,'25:48',' ئاللاھ رەھمىتىنى (يەنى يامغۇرنى) ياغدۇرۇش ئالدىدا شامالنى خۇشخەۋەر قىلىپ ئەۋەتتى، يامغۇر بىلەن ئۆلۈك زېمىننى تىرىلدۈرۈش ۋە بىز ياراتقان ھايۋانلارنى، نۇرغۇن ئىنسانلارنى سۇ بىلەن تەمىنلەش ئۈچۈن، بۇلۇتتىن پاك سۇنى چۈشۈرۈپ بەردۇق[48 ـ 49].');
INSERT INTO "saleh" VALUES(25,49,'25:49',' ئاللاھ رەھمىتىنى (يەنى يامغۇرنى) ياغدۇرۇش ئالدىدا شامالنى خۇشخەۋەر قىلىپ ئەۋەتتى، يامغۇر بىلەن ئۆلۈك زېمىننى تىرىلدۈرۈش ۋە بىز ياراتقان ھايۋانلارنى، نۇرغۇن ئىنسانلارنى سۇ بىلەن تەمىنلەش ئۈچۈن، بۇلۇتتىن پاك سۇنى چۈشۈرۈپ بەردۇق[48 ـ 49].');
INSERT INTO "saleh" VALUES(25,50,'25:50','ئۇلارنى ۋەز ـ نەسىھەت ئالسۇن دەپ، يامغۇرنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق (يەنى گاھ ئۇ يەرگە، گاھ بۇ يەرگە ياغدۇردۇق)، ئىنسانلارنىڭ تولىسى (ئاللاھقا) كۇفرىلىق قىلىدۇ.');
INSERT INTO "saleh" VALUES(25,51,'25:51',' ئەگەر خالىساق ئەلۋەتتە ھەر شەھەرگە بىر ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتەتتۇق.');
INSERT INTO "saleh" VALUES(25,52,'25:52','شۇنىڭ ئۈچۈن كاپىرلارغا ئىتائەت قىلمىغىن، قۇرئان ئارقىلىق ئۇلارغا قارشى (پاكىتلارنى ئوتتۇرىغا قويۇپ) بارلىق كۈچۈڭ بىلەن كۈرەش قىلغىن.');
INSERT INTO "saleh" VALUES(25,53,'25:53',' ئاللاھ (قۇدرىتى بىلەن) بىرى تاتلىق بولغان، بىرى تۇزلۇق بولغان دېڭىزنى (ئۇلار بىر ـ بىرىگە تۇتىشاڭغۇ بولغان ھالدا) (قويۇۋەتتى، ئارىلىشىپ كەتمەسلىكى ئۈچۈن) ئۇلارنىڭ ئارىسىدا پەردە، توسما بەرپا قىلدى.');
INSERT INTO "saleh" VALUES(25,54,'25:54','ئاللاھ ئىنساننى ئابىمەنىيدىن ياراتتى، ئۇنى بالىلار نىسبەت بېرىلىدىغان ئەر ۋە قۇدا ـ باجىلىق پەيدا قىلىدىغان ئايال (دىن ئىبارەت ئىككى خىل قىلىپ) ياراتتى، پەرۋەردىگارىڭ ھەممىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(25,55,'25:55','ئۇلار (يەنى مۇشرىكلار) ئاللاھنى قويۇپ ئۇلارغا پايدىمۇ يەتكۈزەلمەيدىغان، زىيانمۇ يەتكۈزەلمەيدىغان نەرسىلەرگە چوقۇنىدۇ، كاپىر پەرۋەردىگارىغا ئاسىيلىق قىلىش بىلەن (شەيتانغا) ياردەم بەرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(25,56,'25:56','سېنى پەقەت (جەننەت بىلەن) خۇشخەۋەر بەرگۈچى، (دوزاختىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(25,57,'25:57',' ئېيتقىنكى، «مەن (ئاللاھنىڭ ئەمرىنى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، پەقەت (مېنىڭ تىلەيدىغىنىم شۇكى، مېنىڭ دىنىمغا ئەگىشىش بىلەن) پەرۋەردىگارى تەرەپكە يول ئېلىشنى خالايدىغانلار (يول ئالسۇن)».');
INSERT INTO "saleh" VALUES(25,58,'25:58',' (ھەممە ئىشىڭدا) ئۆلمەيدىغان مەڭگۈ ھايات ئاللاھقا يۆلەنگىن، ئۇنىڭغا ھەمد ئېيتىش بىلەن ئاللاھنى پاك دەپ ئېتىقاد قىلغىن، ئۇ بەندىلەرنىڭ گۇناھلىرىنى تولۇق بىلىشتە يېتەرلىكتۇر[ 58]. ');
INSERT INTO "saleh" VALUES(25,59,'25:59',' ئاللاھ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى، ئاللاھ ناھايىتى مەرھەمەتلىكتۇر، بۇنى (يەنى ئاللاھنىڭ بۈيۈكلۈكىنى) بىلىدىغان ئادەمدىن سورىغىن (ساڭا ئاللاھنىڭ سۈپەتلىرىنى ئېيتىپ بېرىدۇ).');
INSERT INTO "saleh" VALUES(25,60,'25:60','ئۇلارغا (يەنى مۇشرىكلارغا): «رەھمانغا (يەنى مەرھەمەتلىك ئاللاھقا) سەجدە قىلىڭلار!» دېيىلسە، ئۇلار: «رەھمان دېگەن نېمە؟ سەن بىزنى بۇيرۇغان نەرسىگە سەجدە قىلامدۇق؟» دەيدۇ. بۇ سۆز ئۇلارنى (ئىماندىن) تېخىمۇ يىراقلاشتۇرىدۇ.');
INSERT INTO "saleh" VALUES(25,61,'25:61','ئاسماندا بۇرۇجلارنى، يېنىپ تۇرىدىغان چىراقنى (يەنى كۈننى) ۋە نۇرلۇق ئاينى ياراتقان ئاللاھنىڭ بەرىكىتى بۈيۈكتۇر.');
INSERT INTO "saleh" VALUES(25,62,'25:62',' ئاللاھ (ئۆزىنىڭ قۇدرىتىنى) ئەسلىمەكچى بولغانلار ياكى (ئۆزىنىڭ نېمىتىگە) شۈكۈر قىلماقچى بولغانلار ئۈچۈن كېچە بىلەن كۈندۈزنى ئالمىشىپ تۇرىدىغان قىلىپ بەردى. ');
INSERT INTO "saleh" VALUES(25,63,'25:63','مەرھەمەتلىك ئاللاھنىڭ (ياخشى كۆرىدىغان) بەندىلىرى زېمىندا ئۆزلىرىنى تۆۋەن تۇتۇپ تەمكىنلىك بىلەن ماڭىدۇ، نادانلار ئۇلارغا (ياقتۇرمايدىغان) سۆز قىلسا، ئۇلار: «سىلەرگە ئامانلىق تىلەيمىز» دەيدۇ (يەنى گۇناھ بولمايدىغان سۆزلەرنى قىلىدۇ).');
INSERT INTO "saleh" VALUES(25,64,'25:64','ئۇلار كېچىلەرنى پەرۋەردىگارىغا سەجدە قىلىش ۋە قىيامدا تۇرۇش بىلەن (يەنى ناماز ئوقۇش بىلەن) ئۆتكۈزىدۇ.');
INSERT INTO "saleh" VALUES(25,65,'25:65',' ئۇلار (يەنى ئاللاھ ياخشى كۆرىدىغان بەندىلەر) ئېيتىدۇ: «پەرۋەردىگارىمىز! بىزدىن جەھەننەم ئازابىنى دەپئى قىلغىن، جەھەننەمنىڭ ئازابى ھەقىقەتەن (سېنىڭ دۈشمەنلىرىڭدىن) ئايرىلمايدۇ.');
INSERT INTO "saleh" VALUES(25,66,'25:66','جەھەننەم ھەقىقەتەن يامان قارارگاھتۇر، يامان جايدۇر». ');
INSERT INTO "saleh" VALUES(25,67,'25:67','ئۇلار (يەنى ئاللاھ ياخشى كۆرىدىغان بەندىلەر) خىراجەت قىلغاندا، ئىسراپچىلىقمۇ قىلمايدۇ، بېخىللىقمۇ قىلمايدۇ، ئوتتۇراھال خىراجەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(25,68,'25:68',' ئۇلار ئاللاھقا ئىككىنچى بىر مەبۇدنى شېرىك قىلمايدۇ، ئاللاھ ھارام قىلغان ناھەق ئادەم ئۆلتۈرۈش ئىشىنى قىلمايدۇ، زىنا قىلمايدۇ، كىمكى بۇ (گۇناھلار) نى قىلىدىكەن، (ئاخىرەتتە) ئۇ جازاغا ئۇچرايدۇ.');
INSERT INTO "saleh" VALUES(25,69,'25:69','قىيامەت كۈنى ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، ئۇ مەڭگۈ ئازاب ئىچىدە خارلانغان ھالدا قالىدۇ.');
INSERT INTO "saleh" VALUES(25,70,'25:70',' پەقەت (ئۇلارنىڭ ئىچىدىن) (بۇ دۇنيادىكى چېغىدا) تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارلا بۇ ھالدا قالمايدۇ، ئاللاھ ئۇلارنىڭ گۇناھلىرىنى ياخشىلىققا ئالماشتۇرىدۇ، ئاللاھ تولىمۇ مەغپىرەت قىلغۇچىدۇر ۋە مەرھەمەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(25,71,'25:71',' كىمكى (گۇناھلىرىغا) تەۋبە قىلىدىكەن ۋە ئەمەلىنى تۈزەيدىكەن، ئۇ ئاللاھقا يۈزلەنگەن بولىدۇ (يەنى ئاللاھ ئۇنىڭ تەۋبىسىنى قوبۇل قىلىپ، ئۇنىڭدىن رازى بولىدۇ).');
INSERT INTO "saleh" VALUES(25,72,'25:72','ئۇلار (يەنى ئاللاھ ياخشى كۆرىدىغان بەندىلەر) يالغان گۇۋاھلىق بەرمەيدۇ، يامان سۆزنى ئاڭلاپ قالغان چاغدا ئالىيجانابلىق بىلەن ئۆتۈپ كېتىدۇ. ');
INSERT INTO "saleh" VALUES(25,73,'25:73',' ئۇلارغا پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز ـ نەسىھەت قىلىنسا، گاس، كور بولۇۋالمايدۇ (يەنى ئۇنى بېرىلىپ ئىخلاس بىلەن ئاڭلايدۇ).');
INSERT INTO "saleh" VALUES(25,74,'25:74','ئۇلار: «ئى پەرۋەردىگارىمىز! بىزگە ئاياللىرىمىز ۋە ئەۋلاتلىرىمىز ئارقىلىق شادلىق بېغىشلىشىڭنى (يەنى بىزگە ساڭا ئىتائەتمەن پەرزەنت ئاتا قىلىشىڭنى) تىلەيمىز، بىزنى تەقۋادارلارنىڭ پېشىۋاسى (يەنى تەقۋادارلارنىڭ نەمۇنىسى، ياخشىلىققا دەۋەت قىلغۇچى) قىلغىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(25,75,'25:75',' ئەنە شۇلار سەۋرلىك بولغانلىقى ئۈچۈن جەننەت بىلەن مۇكاپاتلىنىدۇ، ئۇلار جەننەتتە (پەرىشتىلەر تەرىپىدىن قىلىنغان) دۇئا ۋە سالام بىلەن قارشى ئېلىنىدۇ. ');
INSERT INTO "saleh" VALUES(25,76,'25:76',' ئۇلار جەننەتتە مەڭگۈ قالىدۇ، جەننەت نېمىدېگەن گۈزەل قارارگاھ! نېمىدېگەن گۈزەل جاي!');
INSERT INTO "saleh" VALUES(25,77,'25:77','ئېيتقىنكى، «ئەگەر سىلەرنىڭ دۇئايىڭلار بولمىسا، پەرۋەردىگارىم سىلەرگە پەرۋا قىلمايدۇ، (ئى كاپىرلار! پەيغەمبەرنى) ئىنكار قىلدىڭلار، (ئاخىرەتتە) سىلەر قۇتۇلالمايدىغان ئازابقا دۇچار بولۇسىلەر».');
INSERT INTO "saleh" VALUES(26,1,'26:1','تا، سىم، مىم.');
INSERT INTO "saleh" VALUES(26,2,'26:2','بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر.');
INSERT INTO "saleh" VALUES(26,3,'26:3','(ئى مۇھەممەد!) ئۇلارنىڭ ئىمان ئېيتمىغانلىقىدىن ئۆزۈڭنى ھالاك قىلىۋېتىشىڭ مۇمكىن.');
INSERT INTO "saleh" VALUES(26,4,'26:4',' ئەگەر بىز ئۇلارنىڭ (ئىمان ئېيتىشىنى) خالايدىغان بولساق، ئۇلارغا ئاسماندىن (ئۇلارنى ئىمانغا مەجبۇرلايدىغان) بىر ئايەتنى (ئالامەتنى) نازىل قىلاتتۇق - تە، ئۇنىڭغا ئۇلار باش ئەگكەن بولاتتى.');
INSERT INTO "saleh" VALUES(26,5,'26:5',' ئۇلارغا مەرھەمەتلىك ئاللاھ تەرىپىدىن قۇرئاندىن يېڭىدىن بىرنەرسە نازىل بولسىلا، ئۇلار ئۇنىڭدىن يۈز ئۆرۈيدۇ.');
INSERT INTO "saleh" VALUES(26,6,'26:6',' ئۇلار راستلا (قۇرئاننى) ئىنكار قىلدى، مەسخىرە قىلغان نەرسىنىڭ خەۋەرلىرى (يەنى ئاقىۋىتى) ئۇلارغا كېلىدۇ.');
INSERT INTO "saleh" VALUES(26,7,'26:7',' ئۇلار زېمىننى (يەنى زېمىننىڭ ئاجايىباتلىرىنى) كۈزەتمىدىمۇ؟ زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق.');
INSERT INTO "saleh" VALUES(26,8,'26:8',' بۇنىڭدا ئەلۋەتتە (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئالامەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,9,'26:9',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,10,'26:10',' ئۆز ۋاقتىدا پەرۋەردىگارىڭ مۇساغا نىدا قىلدى: «سەن زالىم قەۋمگە بارغىن.');
INSERT INTO "saleh" VALUES(26,11,'26:11',' (ئۇلار) پىرئەۋننىڭ قەۋمىدۇر، ئۇلار (ئاللاھنىڭ جازالىشىدىن) قورقمامدۇ؟».');
INSERT INTO "saleh" VALUES(26,12,'26:12','مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن.');
INSERT INTO "saleh" VALUES(26,13,'26:13',' (ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن) يۈرىكىم سىقىلىدۇ، تىلىم كېكەچ، شۇڭا (ماڭا ياردەمدە بولۇش ئۈچۈن) ھارۇننى (پەيغەمبەر) قىلىپ ئەۋەتكىن.');
INSERT INTO "saleh" VALUES(26,14,'26:14',' (ئۇلارنىڭ دەۋاسىچە) ئۇلارنىڭ ئالدىدا مېنىڭ گۇناھىم بار، ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن».');
INSERT INTO "saleh" VALUES(26,15,'26:15',' ئاللاھ ئېيتتى: «ھەرگىز ئۇنداق ئەمەس (يەنى سېنى ئۇلار ھەرگىز ئۆلتۈرەلمەيدۇ)، سىلەر مېنىڭ مۆجىزىلىرىمنى ئېلىپ بېرىڭلار، بىز ھەقىقەتەن سىلەر بىلەن بىللە (مۇنازىرەڭلارنى) ئاڭلاپ تۇرىمىز.');
INSERT INTO "saleh" VALUES(26,16,'26:16',' سىلەر پىرئەۋنگە بېرىپ: بىز ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارىنىڭ ئەلچىسىمىز.');
INSERT INTO "saleh" VALUES(26,17,'26:17','ئىسرائىل ئەۋلادىنى (قۇللۇقۇڭدىن) بوشاتقىن، ئۇلار بىز بىلەن (شامغا) كەتسۇن دەڭلار».');
INSERT INTO "saleh" VALUES(26,18,'26:18',' (ئۇلار پىرئەۋنگە كېلىپ ئەلچىلىكنى يەتكۈزدى)، پىرئەۋن (مۇساغا) ئېيتتى: «سېنى بىز ئۆيىمىزدە كىچىكىڭدىن تەربىيەلەپ چوڭ قىلمىدۇقمۇ؟ ئارىمىزدا كۆپ يىللار تۇرمىدىڭمۇ؟. ');
INSERT INTO "saleh" VALUES(26,19,'26:19',' سەن ھېلىقى قىلغان ئىشىڭنى قىلمىدىڭمۇ؟ (يەنى قىبتىنى ئۆلتۈرمىدىڭمۇ؟) سەن تۇزكورلاردىنسەن». ');
INSERT INTO "saleh" VALUES(26,20,'26:20',' مۇسا ئېيتتى: «مەن ئۇ ئىشنى قىلغان چېغىمدا نادانلاردىن ئىدىم.');
INSERT INTO "saleh" VALUES(26,21,'26:21','سىلەردىن قورقۇپ، سىلەردىن قاچتىم، پەرۋەردىگارىم ماڭا ھېكمەت ئاتا قىلدى، مېنى پەيغەمبەر قىلدى.');
INSERT INTO "saleh" VALUES(26,22,'26:22','سەن ئىسرائىل ئەۋلادىنى قۇل قىلدىڭ، سېنىڭ ماڭا مىننەت قىلغان نېمىتىڭ ئەنە شۇدۇر»[');
INSERT INTO "saleh" VALUES(26,23,'26:23',' پىرئەۋن ئېيتتى: «ئالەملەرنىڭ پەرۋەردىگارى دېگەن نېمە؟».');
INSERT INTO "saleh" VALUES(26,24,'26:24',' مۇسا ئېيتتى: «ئۇ ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى پۈتۈن مەخلۇقاتلارنىڭ پەرۋەردىگارىدۇر. ئەگەر سىلەر ھەقىقىي ئىشىنىدىغان بولساڭلار».');
INSERT INTO "saleh" VALUES(26,25,'26:25',' پىرئەۋن چۆرىسىدىكىلەرگە: «(ئۇنىڭ جاۋابىنى) ئاڭلاۋاتامسىلەر؟» دېدى.');
INSERT INTO "saleh" VALUES(26,26,'26:26','مۇسا ئېيتتى: «(ئۇ) سىلەرنىڭ پەرۋەردىگارىڭلاردۇر ۋە سىلەرنىڭ ئاتا ـ بوۋاڭلارنىڭ پەرۋەردىگارىدۇر».');
INSERT INTO "saleh" VALUES(26,27,'26:27','پىرئەۋن ئېيتتى: «سىلەرگە ئەۋەتىلگەن (بۇ) ئەلچى ئەلۋەتتە مەجنۇندۇر».');
INSERT INTO "saleh" VALUES(26,28,'26:28',' مۇسا ئېيتتى: «(ئۇ) مەشرىقنىڭ، مەغرىبنىڭ ۋە ئۇلارنىڭ ئارىسىدىكى مەخلۇقاتنىڭ پەرۋەردىگارىدۇر. ئەگەر سىلەر چۈشىنىدىغان بولساڭلار».');
INSERT INTO "saleh" VALUES(26,29,'26:29',' پىرئەۋن ئېيتتى: «ئەگەر مەندىن غەيرىنى ئىلاھ قىلىۋالىدىغان بولساڭ، سېنى چوقۇم زىندانغا تاشلايمەن».');
INSERT INTO "saleh" VALUES(26,30,'26:30','مۇسا ئېيتتى: «(مېنىڭ راست پەيغەمبەر ئىكەنلىكىمنى ئىسپاتلايدىغان) روشەن دەلىل كەلتۈرسەممۇ (زىندانغا تاشلامسەن؟)».');
INSERT INTO "saleh" VALUES(26,31,'26:31',' پىرئەۋن ئېيتتى: «ئەگەر سۆزۈڭ راست بولسا روشەن دەلىلىڭنى كەلتۈرگىن».');
INSERT INTO "saleh" VALUES(26,32,'26:32','مۇسا ھاسىسىنى تاشلىۋىدى، ناگاھان ئوپئوچۇق ئەجدىھاغا ئايلاندى.');
INSERT INTO "saleh" VALUES(26,33,'26:33',' مۇسا (قوينىدىن) قولىنى چىقىرىۋىدى، ناگاھان ئۇ قارىغۇچىلارغا (نۇر چاقناپ تۇرىدىغان) ئاپئاق بولۇپ كۆرۈندى.');
INSERT INTO "saleh" VALUES(26,34,'26:34',' پىرئەۋن چۆرىسىدىكى (قەۋمنىڭ) چوڭلىرىغا ئېيتتى: «بۇ ھەقىقەتەن ئۇستا سېھىرگەر ئىكەن، ئۇ سېھىر ئارقىلىق سىلەرنى زېمىنىڭلاردىن ھەيدەپ چىقارماقچى، (بۇ ھەقتە) نېمە مەسلىھەت بېرىسىلەر؟»[34ـ35]');
INSERT INTO "saleh" VALUES(26,35,'26:35',' پىرئەۋن چۆرىسىدىكى (قەۋمنىڭ) چوڭلىرىغا ئېيتتى: «بۇ ھەقىقەتەن ئۇستا سېھىرگەر ئىكەن، ئۇ سېھىر ئارقىلىق سىلەرنى زېمىنىڭلاردىن ھەيدەپ چىقارماقچى، (بۇ ھەقتە) نېمە مەسلىھەت بېرىسىلەر؟»[34ـ35]');
INSERT INTO "saleh" VALUES(26,36,'26:36','ئۇلار ئېيتتى: «(ئۇلارنىڭ ئىشىنى) تەخىر قىلغىن، شەھەرلەرگە (سېھىرگەرلەرنى) يىغقۇچى كىشىلەرنى ئەۋەتكىن.');
INSERT INTO "saleh" VALUES(26,37,'26:37',' ئۇلار سېنىڭ ھوزۇرۇڭغا ناھايىتى ئۇستا سېھىرگەرلەرنىڭ ھەممىسىنى ئېلىپ كەلسۇن».');
INSERT INTO "saleh" VALUES(26,38,'26:38',' مۇئەييەن كۈندىكى بەلگىلەنگەن ۋاقىتتا سېھىرگەرلەر توپلاندى.');
INSERT INTO "saleh" VALUES(26,39,'26:39',' كىشىلەرگە: «سىلەر يىغىلىپ بولدۇڭلارمۇ؟ ئەگەر سېھىرگەرلەر غالىب چىقسا ئۇلارغا بويسۇنۇشىمىز مۇمكىن» دېيىلدى[39 ـ 40].');
INSERT INTO "saleh" VALUES(26,40,'26:40',' كىشىلەرگە: «سىلەر يىغىلىپ بولدۇڭلارمۇ؟ ئەگەر سېھىرگەرلەر غالىب چىقسا ئۇلارغا بويسۇنۇشىمىز مۇمكىن» دېيىلدى[39 ـ 40].');
INSERT INTO "saleh" VALUES(26,41,'26:41',' سېھىرگەرلەر پىرئەۋننىڭ قېشىغا يېتىپ كەلگەندە، ئۇلار پىرئەۋنگە: «ئەگەر غەلىبە قىلساق بىزگە چوقۇم مۇكاپات بېرىلەمدۇ؟» دېدى. ');
INSERT INTO "saleh" VALUES(26,42,'26:42',' پىرئەۋن: « ھەئە، ئۇ چاغدا (سىلەرگە مۇكاپات بېرىلىدۇ، ئۇنىڭ ئۈستىگە) سىلەر چوقۇم مېنىڭ يېقىن ئادەملىرىمدىن بولۇپ قالىسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(26,43,'26:43','مۇسا ئۇلارغا: «تاشلايدىغان نەرسەڭلارنى تاشلاڭلار!» دېدى.');
INSERT INTO "saleh" VALUES(26,44,'26:44',' ئۇلار ئارغامچىلىرىنى، ھاسىلىرىنى تاشلىدى. ئۇلار: «پىرئەۋننىڭ كاتتىلىقى بىلەن قەسەمكى، بىز شەك شۈبھىسىز غەلىبە قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(26,45,'26:45','مۇسا ھاسىسىنى تاشلىدى، (ئۇ ئەجدىھاغا ئايلىنىپ) ئۇلارنىڭ ئويدۇرما نەرسىلىرىنى يۇتۇۋەتتى. ');
INSERT INTO "saleh" VALUES(26,46,'26:46',' سىھىرگەرلەر سەجدىگە باردى.');
INSERT INTO "saleh" VALUES(26,47,'26:47','ئۇلار: «ئالەملەرنىڭ پەرۋەردىگارىغا ـ مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا ـ ئىمان ئېيتتۇق» دېدى[47ـ48].');
INSERT INTO "saleh" VALUES(26,48,'26:48','ئۇلار: «ئالەملەرنىڭ پەرۋەردىگارىغا ـ مۇسا ۋە ھارۇننىڭ پەرۋەردىگارىغا ـ ئىمان ئېيتتۇق» دېدى[47ـ48].');
INSERT INTO "saleh" VALUES(26,49,'26:49',' پىرئەۋن: «مەن رۇخسەت قىلماي تۇرۇپ سىلەر مۇساغا ئىمان ئېيتتىڭلار، ئۇ چوقۇم سىلەرگە سېھىرنى ئۈگەتكەن باشلىقىڭلار ئىكەن، (سىلەرنى قانداق جازالايدىغانلىقىمنى) ئۇزۇنغا قالماي بىلىسىلەر، قولۇڭلارنى، پۇتۇڭلارنى چوقۇم ئوڭ ـ چەپ قىلىپ (يەنى ئوڭ قولۇڭلار بىلەن سول پۇتۇڭلارنى ياكى سول قولۇڭلار بىلەن ئوڭ پۇتۇڭلارنى) كېسىمەن، ھەممىڭلارنى چوقۇم دارغا ئاسىمەن» دېدى.');
INSERT INTO "saleh" VALUES(26,50,'26:50','سېھىرگەرلەر: «(بۇنىڭدىمۇ بىزگە) ھېچ زىيان يوق، بىز ئەلۋەتتە پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز،');
INSERT INTO "saleh" VALUES(26,51,'26:51',' بىز مۇساغا ھەممىدىن بۇرۇن ئىمان ئېيتقۇچىلار بولغانلىقىمىز ئۈچۈن، پەرۋەردىگارىمىزنىڭ خاتالىقلىرىمىزنى مەغپىرەت قىلىشىنى ئەلۋەتتە ئۈمىد قىلىمىز» دېدى[ـ 51].');
INSERT INTO "saleh" VALUES(26,52,'26:52','بىز مۇساغا: «كېچىدە بەندىلىرىمنى (يەنى بەنى ئىسرائىلنى) ئېلىپ ئاتلانغىن. سىلەر ئەلۋەتتە قوغلىنىسىلەر، (يەنى پىرئەۋن ئۆز قەۋمى بىلەن سىلەرنى قوغلاپ چىقىدۇ)» دەپ ۋەھىي قىلدۇق.');
INSERT INTO "saleh" VALUES(26,53,'26:53',' پىرئەۋن شەھەرلەرگە (ئەسكەر) توپلىغۇچىلارنى ئەۋەتتى.');
INSERT INTO "saleh" VALUES(26,54,'26:54',' (پىرئەۋن ئېيتتىكى:) «بۇ كىشىلەر ھەقىقەتەن بىر ئوچۇم ئادەملەردۇر.');
INSERT INTO "saleh" VALUES(26,55,'26:55',' ئۇلار ھەقىقەتەن بىزنىڭ ئاچچىقىمىزنى كەلتۈرۈپ قويدى.');
INSERT INTO "saleh" VALUES(26,56,'26:56',' بىز ھەقىقەتەن ئېھتىياتچان جامائەمىز».');
INSERT INTO "saleh" VALUES(26,57,'26:57','بىز ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنى) باغلاردىن، بۇلاقلاردىن، خەزىنىلەردىن ۋە ئېسىل تۇرالغۇدىن ئايرىۋەتتۇق[57ـ58]. ');
INSERT INTO "saleh" VALUES(26,58,'26:58','بىز ئۇلارنى (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنى) باغلاردىن، بۇلاقلاردىن، خەزىنىلەردىن ۋە ئېسىل تۇرالغۇدىن ئايرىۋەتتۇق[57ـ58]. ');
INSERT INTO "saleh" VALUES(26,59,'26:59',' شۇنداق قىلىپ ئۇلارنى بەنى ئىسرائىلغا مىراس قىلىپ بەردۇق.');
INSERT INTO "saleh" VALUES(26,60,'26:60',' ئۇلار (يەنى پىرئەۋن بىلەن ئۇنىڭ قوشۇنى) ئۇلارنى كۈن چىققان چاغدا قوغلاپ چىقتى.');
INSERT INTO "saleh" VALUES(26,61,'26:61','ئىككى توپ (يەنى پىرئەۋن توپى بىلەن مۇسا ئەلەيھىسسالامنىڭ توپى) بىر ـ بىرىنى كۆرۈپ تۇرۇشقان چاغدا، مۇسانىڭ ئادەملىرى: «ئۇلار: (يەنى پىرئەۋن بىلەن قوشۇنى)بىزگە چوقۇم يېتىشىۋالىدىغان بولدى» دېدى.');
INSERT INTO "saleh" VALUES(26,62,'26:62','مۇسا ئېيتتى: «ئۇنداق بولمايدۇ (يەنى ھەرگىز يېتىشەلمەيدۇ) پەرۋەردىگارىم ھەقىقەتەن مەن بىلەن بىللە، مېنى (قۇتۇلۇش يولىغا) باشلايدۇ».');
INSERT INTO "saleh" VALUES(26,63,'26:63',' بىز مۇساغا: «ھاساڭ بىلەن دەرياغا ئۇرغىن» دەپ ۋەھىي قىلدۇق، (مۇسا ھاسىسى بىلەن ئۇرۇۋېدى) دەريا يېرىلدى، (ھەر بىر يېرىلغان) قىسمى چوڭ تاغدەك بولۇپ قالدى.');
INSERT INTO "saleh" VALUES(26,64,'26:64',' ئىككىنچى بىر گۇرۇھنى بۇ يەرگە يېقىنلاشتۇردۇق (يەنى پىرئەۋن بىلەن قوشۇنىنى بەنى ئىسرائىلنىڭ ئارقىسىدىن دەرياغا كىرگۈزدۇق).');
INSERT INTO "saleh" VALUES(26,65,'26:65',' مۇسا بىلەن ئۇنىڭ ھەمراھلىرىنى پۈتۈنلەي قۇتقۇزدۇق. ');
INSERT INTO "saleh" VALUES(26,66,'26:66',' ئاندىن ئىككىنچى گۇرۇھنى (يەنى پىرئەۋن بىلەن قەۋمىنى) غەرق قىلدۇق.');
INSERT INTO "saleh" VALUES(26,67,'26:67',' بۇنىڭدا (يەنى پىرئەۋن بىلەن ئۇنىڭ قەۋمىنىڭ غەرق بولۇشىدا) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى. ');
INSERT INTO "saleh" VALUES(26,68,'26:68','سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,69,'26:69','ئۇلارغا ئىبراھىمنىڭ قىسسەسىنى ئوقۇپ بەرگىن.');
INSERT INTO "saleh" VALUES(26,70,'26:70','ئۆز ۋاقتىدا ئۇ ئاتىسىغا ۋە قەۋمىگە: «نېمىگە ئىبادەت قىلىسىلەر؟» دېدى(70].');
INSERT INTO "saleh" VALUES(26,71,'26:71','ئۇلار: «بۇتلارغا ئىبادەت قىلىمىز، بۇ ئىبادەتنى داۋالاشتۇرىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(26,72,'26:72',' ئىبراھىم ئېيتتى: «دۇئا قىلغان چېغىڭلاردا ئۇلار (دۇئايىڭلارنى) ئاڭلامدۇ؟.');
INSERT INTO "saleh" VALUES(26,73,'26:73',' يا سىلەرگە پايدا يەتكۈزەلەمدۇ؟ يا زىيان يەتكۈزەلەمدۇ؟».');
INSERT INTO "saleh" VALUES(26,74,'26:74',' ئۇلار ئېيتتى: «ئۇنداق ئەمەس، ئاتا ـ بوۋىلىرىمىزنىڭ شۇنداق قىلغانلىقىنى بايقىدۇق».');
INSERT INTO "saleh" VALUES(26,75,'26:75',' ئىبراھىم ئېيتتى: «ئېيتىپ بېقىڭلارچۇ! سىلەرنىڭ ئىبادەت قىلغىنىڭلار نېمە؟');
INSERT INTO "saleh" VALUES(26,76,'26:76','سىلەرنىڭ ئەڭ قەدىمكى ئەجدادلىرىڭلارنىڭ ئىبادەت قىلغىنى نېمە؟');
INSERT INTO "saleh" VALUES(26,77,'26:77',' ئۇلار (يەنى بۇتلار) مېنىڭ دۈشمىنىمدۇر، پەقەت ئالەملەرنىڭ پەرۋەردىگارى (مېنىڭ پەرۋەردىگارىمدۇر).');
INSERT INTO "saleh" VALUES(26,78,'26:78',' ئۇ مېنى ياراتقان، ئۇ مېنى ھىدايەت قىلىدۇ.');
INSERT INTO "saleh" VALUES(26,79,'26:79','ئۇ مېنى تاماق بىلەن تەمىنلەيدۇ، ئۇسسۇلۇق بىلەن تەمىنلەيدۇ.');
INSERT INTO "saleh" VALUES(26,80,'26:80','ئاغرىپ قالسام ئۇ مېنى ساقايتىدۇ. ');
INSERT INTO "saleh" VALUES(26,81,'26:81',' ئۇ مېنى قەبزى روھ قىلىدۇ، كېيىن يەنە تىرىلدۈرىدۇ.');
INSERT INTO "saleh" VALUES(26,82,'26:82',' قىيامەت كۈنى ئۇنىڭ مېنىڭ خاتالىقلىرىمنى مەغپىرەت قىلىشىنى ئۈمىد قىلىمەن.');
INSERT INTO "saleh" VALUES(26,83,'26:83',' پەرۋەردىگارىم! ماڭا ئىلىم ھېكمەت ئاتا قىلغىن، مېنى ياخشىلارغا قوشقىن.');
INSERT INTO "saleh" VALUES(26,84,'26:84','كېيىنكىلەر ئارىسىدا ياخشى نامىمنى قالدۇرغىن.');
INSERT INTO "saleh" VALUES(26,85,'26:85','مېنى نازۇ-نېمەتلىك جەننەتنىڭ ۋارىسلىرىدىن قىلغىن.');
INSERT INTO "saleh" VALUES(26,86,'26:86',' ئاتامغا مەغپىرەت قىلغىن، ئۇ ھەقىقەتەن گۇمراھلاردىن بولدى.');
INSERT INTO "saleh" VALUES(26,87,'26:87','ئۇلار (يەنى خالايىقلار) (ھېساب بېرىش ئۈچۈن) تىرىلدۈرىلىدىغان كۈندە مېنى رەسۋا قىلمىغىن.');
INSERT INTO "saleh" VALUES(26,88,'26:88',' ئۇ كۈنى (ھېچ كىشىگە) مال ۋە ئوغۇللار پايدا يەتكۈزەلمەيدۇ.');
INSERT INTO "saleh" VALUES(26,89,'26:89',' پەقەت (ئاللاھنىڭ دەرگاھى) غا پاك قەلب بىلەن كەلگەن ئادەمگىلا پايدا يەتكۈزۈلىدۇ». ');
INSERT INTO "saleh" VALUES(26,90,'26:90','جەننەت تەقۋادارلارغا يېقىنلاشتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(26,91,'26:91','دوزاخ گۇمراھلارغا ئوچۇق كۆرسىتىلىدۇ.');
INSERT INTO "saleh" VALUES(26,92,'26:92',' ئۇلارغا: «سىلەر ئىلگىرى ئاللاھنى قويۇپ چوقۇنغان نەرسەڭلار قەيەردە؟ ئۇلار سىلەرگە ياردەم بېرەلەمدۇ؟ يا ئۆزلىرىگە ياردەم بېرەلەمدۇ؟» دېيىلىدۇ[92ـ93].');
INSERT INTO "saleh" VALUES(26,93,'26:93',' ئۇلارغا: «سىلەر ئىلگىرى ئاللاھنى قويۇپ چوقۇنغان نەرسەڭلار قەيەردە؟ ئۇلار سىلەرگە ياردەم بېرەلەمدۇ؟ يا ئۆزلىرىگە ياردەم بېرەلەمدۇ؟» دېيىلىدۇ[92ـ93].');
INSERT INTO "saleh" VALUES(26,94,'26:94','بۇتلار، گۇمراھلار ۋە ئىبلىسنىڭ قوشۇنلىرىنىڭ ھەممىسى دوزاخقا ئۈستى ـ ئۈستىلەپ تاشلىنىدۇ[94ـ95]. ');
INSERT INTO "saleh" VALUES(26,95,'26:95','بۇتلار، گۇمراھلار ۋە ئىبلىسنىڭ قوشۇنلىرىنىڭ ھەممىسى دوزاخقا ئۈستى ـ ئۈستىلەپ تاشلىنىدۇ[94ـ95]. ');
INSERT INTO "saleh" VALUES(26,96,'26:96',' ئۇلار دوزاختا جېدەللىشىپ ئېيتىدۇ: «ئاللاھ بىلەن قەسەمكى، بىز ئوپئوچۇق گۇمراھلىقتا ئىدۇق[96ـ97].');
INSERT INTO "saleh" VALUES(26,97,'26:97',' ئۇلار دوزاختا جېدەللىشىپ ئېيتىدۇ: «ئاللاھ بىلەن قەسەمكى، بىز ئوپئوچۇق گۇمراھلىقتا ئىدۇق[96ـ97].');
INSERT INTO "saleh" VALUES(26,98,'26:98',' ئۆز ۋاقتىدا بىز سىلەرنى (ئىبادەتتە) ئالەملەرنىڭ پەرۋەردىگارى بىلەن ئوخشاش ئورۇندا قوياتتۇق.');
INSERT INTO "saleh" VALUES(26,99,'26:99',' بىزنى پەقەت گۇناھكارلارلا ئازدۇردى.');
INSERT INTO "saleh" VALUES(26,100,'26:100','شۇنىڭ ئۈچۈن بىزگە شاپائەت قىلغۇچىلار يوق.');
INSERT INTO "saleh" VALUES(26,101,'26:101',' ۋە يېقىن دوستمۇ يوق.');
INSERT INTO "saleh" VALUES(26,102,'26:102','ئەگەر بىزگە (دۇنياغا) قايتىشقا بولسا ئىدى، بىز مۆمىنلەردىن بولاتتۇق».');
INSERT INTO "saleh" VALUES(26,103,'26:103',' شۈبھىسىزكى، بۇنىڭدا (يەنى ئىبراھىم ۋە ئۇنىڭ قەۋمىنىڭ قىسسەسىدە) (ئەقىل ئىگىلىرى ئۈچۈن) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,104,'26:104',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,105,'26:105','نۇھنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(26,106,'26:106','ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى نۇھ ئېيتتى: «سىلەر (ئاللاھتىن) قورقمامسىلەر؟.');
INSERT INTO "saleh" VALUES(26,107,'26:107',' مەن ھەقىقەتەن سىلەرگە سادىق بىر پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(26,108,'26:108',' سىلەر ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,109,'26:109',' تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن.');
INSERT INTO "saleh" VALUES(26,110,'26:110',' ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار».');
INSERT INTO "saleh" VALUES(26,111,'26:111','ئۇلار: «(ئى نۇھ!) ساڭا تۆۋەن تەبىقىدىكى ئادەملەر ئەگەشكەن تۇرسا، ساڭا بىز ئىمان ئېيتامدۇق؟» دېدى.');
INSERT INTO "saleh" VALUES(26,112,'26:112',' نۇھ ئېيتتى: «مەن ئۇلارنىڭ نېمە قىلغانلىقىنى ئۇقمايمەن.');
INSERT INTO "saleh" VALUES(26,113,'26:113','ئەگەر بىلسەڭلار ئۇلاردىن ھېساب ئېلىشنى پەقەت پەرۋەردىگارىم ئۈستىگە ئالغان.');
INSERT INTO "saleh" VALUES(26,114,'26:114',' مەن مۆمىنلەرنى قوغلىۋەتمەيمەن.');
INSERT INTO "saleh" VALUES(26,115,'26:115',' مەن پەقەت ئوچۇق ـ ئاشكارا ئاگاھلاندۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(26,116,'26:116','ئۇلار ئېيتتى: «ئى نۇھ! ئەگەر سەن (پەيغەمبەرلىك دەۋىتىڭدىن) يانمىساڭ، سەن چوقۇم تاش ـ كېسەك قىلىنىپ ئۆلتۈرۈلگۈچىلەردىن بولىسەن».');
INSERT INTO "saleh" VALUES(26,117,'26:117',' نۇھ ئېيتتى: «پەرۋەردىگارىم! قەۋمىم مېنى ھەقىقەتەن ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(26,118,'26:118',' مەن بىلەن ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقارغىن، مېنى ۋە مەن بىلەن بىللە بولغان مۆمىنلەرنى قۇتقۇزغىن».');
INSERT INTO "saleh" VALUES(26,119,'26:119','بىز ئۇنى ۋە (ئادەملەر، ھايۋانلار بىلەن) لىق تولغان كېمىدە ئۇنىڭ بىلەن بىللە بولغانلارنى قۇتقۇزدۇق. ');
INSERT INTO "saleh" VALUES(26,120,'26:120',' (ئۇلارنى قۇتقۇزغاندىن) كېيىن ئاندىن قالغانلارنى غەرق قىلدۇق.');
INSERT INTO "saleh" VALUES(26,121,'26:121',' بۇنىڭدا (تەپەككۇر قىلغۇچىلار ئۈچۈن) ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلار (يەنى كىشىلەر) نىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,122,'26:122','سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,123,'26:123',' ئاد (خەلقى) پەيغەمبەرلەرنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(26,124,'26:124','ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى ھۇد ئېيتتى: «سىلەر (ئاللاھتىن) قورقمامسىلەر؟.');
INSERT INTO "saleh" VALUES(26,125,'26:125',' مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(26,126,'26:126',' سىلەر ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,127,'26:127',' تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن.');
INSERT INTO "saleh" VALUES(26,128,'26:128',' سىلەر ھەر بىر ئېگىز جايغا ئويۇن ـ كۈلكە ئۈچۈن بىر ئالامەت (يەنى ئېگىز بىنا) سالامسىلەر؟');
INSERT INTO "saleh" VALUES(26,129,'26:129','(گويا سىلەر ئۆلمەيدىغاندەك) دۇنيادا مەڭگۈ قېلىشنى ئۈمىد قىلىپ پۇختا سارايلارنى سالامسىلەر؟');
INSERT INTO "saleh" VALUES(26,130,'26:130','ئەگەر (بىراۋنى) جازالىساڭلار، زالىملاردەك جازالايسىلەر.');
INSERT INTO "saleh" VALUES(26,131,'26:131','سىلەر ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,132,'26:132',' سىلەرگە سىلەر بىلىدىغان نېمەتلەرنى ئاتا قىلغان ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(26,133,'26:133','ئۇ سىلەرگە چارۋىلارنى، ئوغۇللارنى، باغلارنى، بۇلاقلارنى ئاتا قىلدى[133ـ134]. ');
INSERT INTO "saleh" VALUES(26,134,'26:134','ئۇ سىلەرگە چارۋىلارنى، ئوغۇللارنى، باغلارنى، بۇلاقلارنى ئاتا قىلدى[133ـ134]. ');
INSERT INTO "saleh" VALUES(26,135,'26:135','مەن ھەقىقەتەن سىلەرنىڭ بۈيۈك كۈننىڭ ئازابىغا قېلىشىڭلاردىن قورقىمەن».');
INSERT INTO "saleh" VALUES(26,136,'26:136',' ئۇلار ئېيتتى: «نەسىھەت قىلامسەن، قىلمامسەن، بەرىبىر بىزگە ئوخشاش.');
INSERT INTO "saleh" VALUES(26,137,'26:137','بۇ پەقەت بۇرۇنقىلاردىن قالغان ئادەت.');
INSERT INTO "saleh" VALUES(26,138,'26:138',' بىز ھەرگىز جازالانمايمىز».');
INSERT INTO "saleh" VALUES(26,139,'26:139','ئۇلار ئۇنى (يەنى ھۇدنى) ئىنكار قىلدى، ئۇلارنى بىز ھالاك قىلدۇق، بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,140,'26:140',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,141,'26:141','سەمۇد (خەلقى) پەيغەمبەرلەرنى ئىنكار قىلدى. ');
INSERT INTO "saleh" VALUES(26,142,'26:142','ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى سالىھ ئېيتتى: «(ئاللاھتىن) قورقمامسىلەر؟.');
INSERT INTO "saleh" VALUES(26,143,'26:143',' مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن. ');
INSERT INTO "saleh" VALUES(26,144,'26:144','سىلەر ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,145,'26:145',' تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن.');
INSERT INTO "saleh" VALUES(26,146,'26:146',' سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر؟)[146ـ148].');
INSERT INTO "saleh" VALUES(26,147,'26:147',' سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر؟)[146ـ148].');
INSERT INTO "saleh" VALUES(26,148,'26:148',' سىلەر بۇ يەرلەردە، باغچىلاردىن، بۇلاقلاردىن، زىرائەتلەردىن، يۇمشاق پىشقان خورمىلاردىن بەھرىمەن بولۇپ خاتىرجەم ھالدا داۋاملىق قالىمىز (دەپ ئويلامسىلەر؟)[146ـ148].');
INSERT INTO "saleh" VALUES(26,149,'26:149','ئۇستىلىق بىلەن تاغلارنى تېشىپ ئۆي ياسامسىلەر؟');
INSERT INTO "saleh" VALUES(26,150,'26:150','ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,151,'26:151',' زېمىندا بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان ھەددىدىن ئاشقۇچىلارنىڭ ئەمرىگە ئىتائەت قىلماڭلار»[151ـ152].');
INSERT INTO "saleh" VALUES(26,152,'26:152',' زېمىندا بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان ھەددىدىن ئاشقۇچىلارنىڭ ئەمرىگە ئىتائەت قىلماڭلار»[151ـ152].');
INSERT INTO "saleh" VALUES(26,153,'26:153','ئۇلار ئېيتتى: «سەن ھەقىقەتەن سېھىر قىلىنغانلاردىنسەن.');
INSERT INTO "saleh" VALUES(26,154,'26:154',' سەن پەقەت بىزگە ئوخشاش بىر ئىنسانسەن، ئەگەر سەن راستچىللاردىن بولساڭ، بىرەر مۆجىزە كەلتۈرۈپ باققىن».');
INSERT INTO "saleh" VALUES(26,155,'26:155','سالىھ ئېيتتى: «بۇ چىشى تۆگە (سۈيۈڭلاردىن بىر كۈن) ئىچىدۇ، سىلەرمۇ مۇئەييەن بىر كۈن ئىچىسىلەر.');
INSERT INTO "saleh" VALUES(26,156,'26:156',' ئۇنىڭغا يامانلىق قىلماڭلار، بولمىسا سىلەر بۈيۈك كۈننىڭ ئازابىغا دۇچار بولۇسىلەر».');
INSERT INTO "saleh" VALUES(26,157,'26:157','ئۇلار چىشى تۆگىنى بوغۇزلىدى، شۇنىڭ بىلەن ئۇلار نادامەتكە قالدى.');
INSERT INTO "saleh" VALUES(26,158,'26:158','ئۇلارغا ئازاب چۈشتى، بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,159,'26:159',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,160,'26:160',' لۇتنىڭ قەۋمى پەيغەمبەرلەرنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(26,161,'26:161',' ئۆز ۋاقتىدا ئۇلارغا قېرىندىشى لۇت ئېيتتى: «سىلەر (ئاللاھتىن) قورقمامسىلەر؟ ');
INSERT INTO "saleh" VALUES(26,162,'26:162',' مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(26,163,'26:163',' سىلەر ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,164,'26:164',' تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن.');
INSERT INTO "saleh" VALUES(26,165,'26:165',' سىلەر ئەھلى جاھان ئىچىدىن لىۋاتە قىلىپ، پەرۋەردىگارىڭلار سىلەر ئۈچۈن ياراتقان ئاياللىرىڭلارنى تاشلاپ قويامسىلەر؟ سىلەر ھەقىقەتەن (بۇزۇقچىلىقتا) ھەددىدىن ئاشقۇچى قەۋمسىلەر»[165ـ166]. ');
INSERT INTO "saleh" VALUES(26,166,'26:166',' سىلەر ئەھلى جاھان ئىچىدىن لىۋاتە قىلىپ، پەرۋەردىگارىڭلار سىلەر ئۈچۈن ياراتقان ئاياللىرىڭلارنى تاشلاپ قويامسىلەر؟ سىلەر ھەقىقەتەن (بۇزۇقچىلىقتا) ھەددىدىن ئاشقۇچى قەۋمسىلەر»[165ـ166]. ');
INSERT INTO "saleh" VALUES(26,167,'26:167','ئۇلار ئېيتتى: «ئى لۇت! ئەگەر (دەۋىتىڭدىن) قايتمىساڭ، چوقۇم سۈرگۈن قىلىنىسەن».');
INSERT INTO "saleh" VALUES(26,168,'26:168','لۇت ئېيتتى: «مەن سىلەرنىڭ قىلىقىڭلاردىن قاتتىق يىرگىنىمەن.');
INSERT INTO "saleh" VALUES(26,169,'26:169',' پەرۋەردىگارىم! قىلمىشلىرى تۈپەيلىدىن (ئۇلارغا كېلىدىغان ئازابتىن) مېنى ۋە تەۋەلىرىمنى قۇتقۇزغىن».');
INSERT INTO "saleh" VALUES(26,170,'26:170',' ئۇنى ۋە ئۇنىڭ تەۋەلىرىنىڭ ھەممىسىنى قۇتقۇزدۇق.');
INSERT INTO "saleh" VALUES(26,171,'26:171','پەقەت موماينى (يەنى لۇتنىڭ ئايالىنى قالدۇرۇپ قويۇپ) ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(26,172,'26:172',' ئاندىن قالغانلارنى ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(26,173,'26:173','ئۇلارنىڭ ئۈستىگە بىز يامغۇر (يەنى تاش) ياغدۇردۇق، ئاگاھلاندۇرۇلغۇچىلارغا ياغدۇرۇلغان يامغۇر نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(26,174,'26:174','بۇنىڭدا ئەلۋەتتە (چوڭ) بىر ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(26,175,'26:175',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,176,'26:176',' ئەيكىلىقلار پەيغەمبەرلىرىنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(26,177,'26:177',' ئۆز ۋاقتىدا شۇئەيب ئۇلارغا ئېيتتى: «سىلەر (ئاللاھتىن) قورقمامسىلەر؟');
INSERT INTO "saleh" VALUES(26,178,'26:178','مەن ھەقىقەتەن سىلەرگە سادىق پەيغەمبەرمەن.');
INSERT INTO "saleh" VALUES(26,179,'26:179','ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(26,180,'26:180','تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچقانداق ھەق تەلەپ قىلمايمەن، ئۇنى پەقەت ئالەملەرنىڭ پەرۋەردىگارىدىن تىلەيمەن.');
INSERT INTO "saleh" VALUES(26,181,'26:181',' ئۆلچەمنى تولدۇرۇپ بېرىڭلار، كەم بەرگۈچىلەردىن بولماڭلار.');
INSERT INTO "saleh" VALUES(26,182,'26:182',' (نەرسىلەرنى) توغرا تارازىدا تارتىڭلار،');
INSERT INTO "saleh" VALUES(26,183,'26:183','كىشىلەرگە نەرسىلىرىنى (يەنى قايسى يول بىلەن بولمىسۇن، كىشىلەرنىڭ ھەقلىرىنى) كەم بەرمەڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار.');
INSERT INTO "saleh" VALUES(26,184,'26:184',' سىلەرنى ۋە ئىلگىرىكى ئۈممەتلەرنى ياراتقان ئاللاھتىن قورقۇڭلار».');
INSERT INTO "saleh" VALUES(26,185,'26:185',' ئۇلار ئېيتتى: «سەن سېھىر قىلىنغۇچىلاردىنسەن.');
INSERT INTO "saleh" VALUES(26,186,'26:186',' سەن پەقەت بىزگە ئوخشاش (ئاددى) ئادەمسەن، سېنى بىز ھەقىقەتەن يالغانچى دەپ گۇمان قىلىمىز.');
INSERT INTO "saleh" VALUES(26,187,'26:187',' ئەگەر (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە ئاسماندىن ئازاب چۈشۈرگىن».');
INSERT INTO "saleh" VALUES(26,188,'26:188',' شۇئەيب ئېيتتى: «پەرۋەردىگارىم سىلەرنىڭ قىلمىشىڭلارنى ئوبدان بىلىدۇ».');
INSERT INTO "saleh" VALUES(26,189,'26:189','ئۇلار شۇئەيبنى ئىنكار قىلدى، ئۇلارنى سايە كۈنىنىڭ ئازابى ھالاك قىلدى. ئۇ ھەقىقەتەن بۈيۈك كۈننىڭ ئازابى ئىدى.');
INSERT INTO "saleh" VALUES(26,190,'26:190',' بۇنىڭدا ئەلۋەتتە (چوڭ) ئىبرەت بار، ئۇلارنىڭ تولىسى ئىمان ئېيتقۇچىلاردىن بولمىدى.');
INSERT INTO "saleh" VALUES(26,191,'26:191',' سېنىڭ پەرۋەردىگارىڭ ھەقىقەتەن ناھايىتى غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(26,192,'26:192',' شۈبھىسىزكى، قۇرئان ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(26,193,'26:193','ئاگاھلاندۇرغۇچىلاردىن بولۇشۇڭ ئۈچۈن، ئىشەنچلىك جىبرىئىل ئۇنى سېنىڭ قەلبىڭگە ئېلىپ چۈشتى[193ـ194].');
INSERT INTO "saleh" VALUES(26,194,'26:194','ئاگاھلاندۇرغۇچىلاردىن بولۇشۇڭ ئۈچۈن، ئىشەنچلىك جىبرىئىل ئۇنى سېنىڭ قەلبىڭگە ئېلىپ چۈشتى[193ـ194].');
INSERT INTO "saleh" VALUES(26,195,'26:195','(قۇرئان) ئوچۇق ئەرەبى تىلدا (نازىل بولدى). ');
INSERT INTO "saleh" VALUES(26,196,'26:196',' ھەقىقەتەن قۇرئان ئىلگىرىكى (پەيغەمبەرلەرنىڭ) كىتابلىرىدا تىلغا ئېلىنغان.');
INSERT INTO "saleh" VALUES(26,197,'26:197',' (ئابدۇللا ئىبنى سالام ۋە ئۇنىڭ ئىمان ئېيتقان ھەمراھلىرىغا ئوخشاش) بەنى ئىسرائىل ئۆلىمالىرىنىڭ قۇرئاننى بىلىشى مۇشرىكلارغا (قۇرئاننىڭ توغرىلىقىنى كۆرسىتىدىغان) دەلىل بولمامدۇ؟');
INSERT INTO "saleh" VALUES(26,198,'26:198','ئەگەر بىز قۇرئاننى ئەرەب بولمىغان بىر ئادەمگە نازىل قىلغان بولساق، ئۇ قۇرئاننى ئۇلارغا ئوقۇپ بەرسە، ئۇلار ئۇنىڭغا ئىشەنمەيتتى[198ـ199].');
INSERT INTO "saleh" VALUES(26,199,'26:199','ئەگەر بىز قۇرئاننى ئەرەب بولمىغان بىر ئادەمگە نازىل قىلغان بولساق، ئۇ قۇرئاننى ئۇلارغا ئوقۇپ بەرسە، ئۇلار ئۇنىڭغا ئىشەنمەيتتى[198ـ199].');
INSERT INTO "saleh" VALUES(26,200,'26:200','شۇنىڭدەك گۇناھكارلارنىڭ دىللىرىغا ئىشەنمەسلىكنى سالدۇق (ئۇلار قۇرئاننى چۈشىنىپ، ئۇنىڭ پاساھەت ـ بالاغىتىنى ۋە مۆجىزە ئىكەنلىكىنى بىلىپ تۇرۇپ ئىمان ئېيتمىدى).');
INSERT INTO "saleh" VALUES(26,201,'26:201',' ئۇلار (ئاللاھنىڭ) قاتتىق ئازابىنى كۆرمىگۈچە قۇرئانغا ئىشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(26,202,'26:202',' ئۇ ئازاب ئۇلارغا ئۇشتۇمتۇت كېلىدۇ، ئۇلار ئۇنىڭ (كەلگەنلىكىنى) تۇيماي قالىدۇ.');
INSERT INTO "saleh" VALUES(26,203,'26:203',' (ئۇلارغا تۇيۇقسىز ئازاب كەلگەندە) ئۇلار: «بىزگە (ئىمان ئېيتىۋېلىش ئۈچۈن) مۆھلەت بېرىلەمدۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(26,204,'26:204',' ئۇلار بىزنىڭ ئازابىمىزنىڭ بۇرۇنراق كېلىشىنى تىلەمدۇ؟');
INSERT INTO "saleh" VALUES(26,205,'26:205',' ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ[205ـ207].');
INSERT INTO "saleh" VALUES(26,206,'26:206',' ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ[205ـ207].');
INSERT INTO "saleh" VALUES(26,207,'26:207',' ئېيتىپ باققىنا! ئەگەر ئۇلارنى (نۇرغۇن) يىللار (نېمەتلىرىمىزدىن) بەھرىمەن قىلساق، ئاندىن ئۇلارغا ئاگاھلاندۇرۇلغان ئازاب كەلسە، ئۇلارنىڭ بەھرىمەن بولغان نېمەتلىرى (ئۇلاردىن ئازابنى دەپئى قىلىشتا) ھېچ نەرسىگە ئەسقاتمايدۇ[205ـ207].');
INSERT INTO "saleh" VALUES(26,208,'26:208',' قانداقلىكى شەھەر بولمىسۇن، ۋەز ـ نەسىھەت قىلىش ئۈچۈن پەيغەمبەرلەرنى ئەۋەتمىگۈچە، ئۇنى ھالاك قىلمىدۇق، [208ـ].');
INSERT INTO "saleh" VALUES(26,209,'26:209',' (ئۇلارنى جازالاشتا) بىز زالىم بولمىدۇق.');
INSERT INTO "saleh" VALUES(26,210,'26:210',' قۇرئاننى شەيتانلار ئېلىپ چۈشكىنى يوق. ');
INSERT INTO "saleh" VALUES(26,211,'26:211','بۇ ئۇلارغا لايىق ئەمەس. ئۇلارمۇ (ئۇنىڭغا) قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(26,212,'26:212',' شۈبھىسىزكى، ئۇلار (يەنى شەيتانلار) (ئوغرىلىقچە) تىڭشاشتىن مەنئى قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(26,213,'26:213','ئاللاھتىن باشقا ھېچ ئىلاھقا ئىبادەت قىلمىغىن، ئۇنداق قىلساڭ، ئازاب قىلىنغۇچىلاردىن بولۇپ قالىسەن.');
INSERT INTO "saleh" VALUES(26,214,'26:214',' يېقىن خىش - ئەقرىبالىرىڭنى ئاگاھلاندۇرغىن.');
INSERT INTO "saleh" VALUES(26,215,'26:215',' ساڭا ئەگەشكەن مۆمىنلەرگە مۇلايىم بولغىن.');
INSERT INTO "saleh" VALUES(26,216,'26:216',' ئەگەر ئۇلار ساڭا ئاسىيلىق قىلسا: «مەن سىلەرنىڭ قىلمىشلىرىڭلاردىن ھەقىقەتەن ئادا ـ جۇدا مەن» دېگىن.');
INSERT INTO "saleh" VALUES(26,217,'26:217','ناھايىتى غالىب، مېھرىبان ئاللاھقا يۆلەنگىن.');
INSERT INTO "saleh" VALUES(26,218,'26:218',' ئاللاھ سېنى (نامازغا) قوپقىنىڭدا كۆرۈپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(26,219,'26:219','ناماز ئوقۇغۇچىلار ئارىسىدىكى (سەجدىگە بارغانلىق، رۇكۇغا تۇرغانلىق ۋە قىيامدا تۇرغانلىق) ھەرىكىتىڭنى كۆرۈپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(26,220,'26:220','شۈبھىسىزكى، ئاللاھ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(26,221,'26:221','(ئى مۇھەممەد! مەككە كۇففارلىرىغا ئېيتقىنكى) سىلەرگە مەن شەيتانلارنىڭ كىمگە چۈشىدىغانلىقىنى ئېيتىپ بېرەيمۇ؟');
INSERT INTO "saleh" VALUES(26,222,'26:222','ئۇلار ھەربىر ئىغۋا توقۇغۇچى، گۇناھكارغا چۈشىدۇ.');
INSERT INTO "saleh" VALUES(26,223,'26:223','ئۇلار (پەرىشتىلەرنىڭ سۆزلىرىنى ئوغرىلىقچە) ئاڭلايدۇ، ئۇلار (يەنى شەيتانلار) نىڭ تولىسى يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(26,224,'26:224',' شائىرلارغا گۇمراھلار ئەگىشىدۇ.');
INSERT INTO "saleh" VALUES(26,225,'26:225','ئۇلارنىڭ (سۆز) ۋادىلىرىدا تېڭىرقاپ يۈرگەنلىكىنى كۆرمەمسەن؟ ');
INSERT INTO "saleh" VALUES(26,226,'26:226',' ئۇلار قىلمايدىغان نەرسىلىرىنى قىلدۇق دەپ سۆزلەيدۇ.');
INSERT INTO "saleh" VALUES(26,227,'26:227',' پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان، ئاللاھنى كۆپ زىكرى قىلغان، زۇلۇمغا ئۇچرىغاندىن كېيىن ئۆزىنى قوغدىغان شائىرلار بۇنىڭدىن مۇستەسنا، زۇلۇم قىلغۇچىلار ئۇزاققا قالماي قايسى جايغا قايتىدىغانلىقىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(27,1,'27:1','تا، سىن. بۇ، قۇرئاننىڭ ۋە روشەن كىتابنىڭ ئايەتلىرىدۇر.');
INSERT INTO "saleh" VALUES(27,2,'27:2','مۆمىنلەر ئۈچۈن ھىدايەتتۇر ۋە خۇشخەۋەردۇر.');
INSERT INTO "saleh" VALUES(27,3,'27:3',' (شۇنداق مۆمىنلەركى) ئۇلار نامازنى ئادا قىلىدۇ، زاكات بېرىدۇ ۋە ئاخىرەتكە جەزمەن ئىشىنىدۇ.');
INSERT INTO "saleh" VALUES(27,4,'27:4','ئاخىرەتكە ئىشەنمەيدىغانلارغا ئۇلارنىڭ ئەمەللىرىنى ھەقىقەتەن چىرايلىق كۆرسەتتۇق، ئۇلار (گۇمراھلىقلىرىدا) تېڭىرقاپ يۈرۈشىدۇ.');
INSERT INTO "saleh" VALUES(27,5,'27:5','ئۇلار (دۇنيادا) قاتتىق ئازابقا دۇچار بولىدۇ. ئاخىرەتتە ئۇلار ئەڭ زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(27,6,'27:6','ساڭا قۇرئان ئەلۋەتتە ھېكمەت بىلەن ئىش قىلغۇچى، ھەممىنى بىلگۈچى ئاللاھ تەرىپىدىن بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(27,7,'27:7',' ئۆز ۋاقتىدا مۇسا ئەھلىسىگە: «مەن ھەقىقەتەن ئوت كۆردۈم، مەن ئوتنىڭ يېنىغا بېرىپ (يول توغرۇلۇق) بىرەر خەۋەر ئۇقۇپ كېلەي ياكى سىلەرنىڭ ئىسىنىشىڭلار ئۈچۈن ئوتتىن بىر چوغ ئېلىپ كېلەي» دېدى.');
INSERT INTO "saleh" VALUES(27,8,'27:8','مۇسا ئوتنىڭ يېنىغا كەلگەندە (مۇنداق) نىدا ئاڭلاندى: «ئاللاھ ئوتنىڭ يېنىدىكى كىشىنى ۋە ئوتنىڭ ئەتراپىدىكىلەرنى مۇبارەك قىلدى. ئالەملەرنىڭ پەرۋەردىگارى ئاللاھ پاكتۇر.');
INSERT INTO "saleh" VALUES(27,9,'27:9',' ئى مۇسا! شۈبھىسىزكى، مەن غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھتۇرمەن.');
INSERT INTO "saleh" VALUES(27,10,'27:10',' ھاساڭنى تاشلىغىن!» مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە، ئارقىسىغا بۇرۇلۇپ چېكىندى، كەينىگە (يەنى قورققىنىدىن ئەجدىھاغا) قارىيالمىدى. (ئاللاھ ئېيتتى) «ئى مۇسا! قورقمىغىن، مېنىڭ ھۇزۇرۇمدا پەيغەمبەرلەر ئەلۋەتتە قورقمايدۇ.');
INSERT INTO "saleh" VALUES(27,11,'27:11',' لېكىن كىمكى (ئۆزىگە) زۇلۇم قىلسا، ئاندىن يامان ئەمەللىرىنى ياخشى ئەمەلگە ئۆزگەرتسە، مەن (ئۇنىڭغا) ناھايىتى مەغپىرەت قىلغۇچىدۇرمەن، ناھايىتى مېھرىباندۇرمەن.');
INSERT INTO "saleh" VALUES(27,12,'27:12',' قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز ئاپئاق بولۇپ چىقىدۇ، (بۇ) مېنىڭ پىرئەۋن ۋە ئۇنىڭ قەۋمىگە ئېلىپ بېرىشىڭ ئۈچۈن بەرگەن توققۇز مۆجىزەمنىڭ ئىچىدىدۇر، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى ».');
INSERT INTO "saleh" VALUES(27,13,'27:13',' ئۇلارغا بىزنىڭ نۇرغۇن روشەن ئايەتلىرىمىز نازىل بولغان چاغدا، ئۇلار: «بۇ روشەن سېھىردۇر» دېدى.');
INSERT INTO "saleh" VALUES(27,14,'27:14',' ئۇلار ئۇ ئايەتلەرنى ئىچىدە ئېتىراپ قىلدى، لېكىن ئۇلار ئۇنى زۇلۇم ۋە تەكەببۇرلۇق قىلىش يۈزىسىدىن ئىنكار قىلدى. بۇزغۇنچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(27,15,'27:15','بىز ھەقىقەتەن داۋۇدقا، سۇلايمانغا (دۇنيا ۋە دىن ئىلىملىرىدىن كەڭ) ئىلىم ئاتا قىلدۇق، ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزنى نۇرغۇن بەندىلىرىدىن ئارتۇق قىلغان ئاللاھقا خاستۇر!». ');
INSERT INTO "saleh" VALUES(27,16,'27:16',' سۇلايمان (پەيغەمبەرلىكتە، ئىلىمدە، پادىشاھلىقتا ئاتىسى) داۋۇدقا ۋارىسلىق قىلدى. ئۇ: «ئى ئىنسانلار! بىزگە قۇشلارنىڭ تىلى تەلىم بېرىلدى. (دۇنيانىڭ نېمەتلىرىدىن) ھەممە نەرسە ئاتا قىلىندى، بۇ ئەلۋەتتە (ئاللاھنىڭ) روشەن ئېھسانىدۇر» دېدى.');
INSERT INTO "saleh" VALUES(27,17,'27:17',' سۇلايماننىڭ جىنلاردىن، ئىنسانلاردىن ۋە قۇشلاردىن بولغان قوشۇنلىرى توپلاندى (سۇلايمان ئەلەيھىسسالام ئۇلارنىڭ ئالدىدا دەبدەبە بىلەن ماڭاتتى). ئۇلار تەرتىپلىك ئورۇنلاشتۇرۇلغان ئىدى.');
INSERT INTO "saleh" VALUES(27,18,'27:18',' ئۇلار (شامدىكى بىر) چۈمۈلىلەر ۋادىسىغا يېتىپ كەلگەندە، بىر (چىشى) چۈمۈلە ئېيتتى: «ئى چۈمۈلىلەر! ئۇۋىلىرىڭلارغا كىرىپ كېتىڭلار، سۇلايمان ۋە ئۇنىڭ قوشۇنى ئۇقماستىن سىلەرنى يەنچىۋەتمىسۇن».');
INSERT INTO "saleh" VALUES(27,19,'27:19',' سۇلايمان چۈمۈلىنىڭ سۆزىدىن تەبەسسۇم قىلىپ كۈلدى ۋە ئېيتتى: «پەرۋەردىگارىم! سەن مېنى ماڭا ۋە ئاتا ـ ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشقا، سەن رازى بولىدىغان ياخشى ئەمەلنى قىلىشقا مۇۋەپپەق قىلغىن، رەھمىتىڭ بىلەن مېنى ياخشى بەندىلىرىڭ قاتارىغا كىرگۈزگىن».');
INSERT INTO "saleh" VALUES(27,20,'27:20','ئۇ قۇشلارنى كۆزدىن كەچۈرگەندىن كېيىن ئېيتتى: «ماڭا نېمە بولدى؟ ھۆپۆپنى كۆرمەيمەنغۇ! ياكى ئۇ يوقاپ كەتتىمۇ.');
INSERT INTO "saleh" VALUES(27,21,'27:21',' ئۇنى چوقۇم قاتتىق جازالايمەن، يا ئۇنى چوقۇم بوغۇزلايمەن، يا چوقۇم (يوقاپ كەتكەنلىكىنى ئاقلايدىغان) بىر روشەن دەلىل كەلتۈرىدۇ».');
INSERT INTO "saleh" VALUES(27,22,'27:22','ئۇزاق ئۆتمەي ھۆپۈپ كەلدى، ئۇ ئېيتتى: «مەن سەن بىلمىگەن ئىشنى بىلىپ (كەلدىم)، ساڭا مەن سەبەدىن (يەنى يەمەندىكى سەبە شەھرىدىن) مۇھىم بىر خەۋەر ئېلىپ كەلدىم.');
INSERT INTO "saleh" VALUES(27,23,'27:23',' مەن ھەقىقەتەن ئۇلارغا (يەنى سەبە ئاھالىسىگە) بىر ئايالنىڭ (يەنى بىلقىسنىڭ) پادىشاھلىق قىلىۋاتقانلىقىنى كۆردۈم، ئۇنىڭغا (ئۆزىگە ۋە سەلتەنەتىگە كېرەكلىك) ھەممە نەرسە بېرىلگەن ئىكەن، ئۇ چوڭ تەختكە ئىگە ئىكەن.');
INSERT INTO "saleh" VALUES(27,24,'27:24',' ئۇنىڭ ۋە قەۋمىنىڭ ئاللاھنى قويۇپ قۇياشقا چوقۇنىدىغانلىقىنى بايقىدىم، شەيتان ئۇلارغا قىلمىشلىرىنى (يەنى ئاللاھنى قويۇپ، قۇياشقا چوقۇنغانلىقلىرىنى) چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ھىدايەت تاپمايدۇ.');
INSERT INTO "saleh" VALUES(27,25,'27:25',' ئۇلار ئاسمانلاردىكى ۋە زېمىندىكى سىرلارنى ئاشكارىلىغۇچى، سىلەرنىڭ يوشۇرۇن ۋە ئاشكارا ئىشىڭلارنى بىلىپ تۇرغۇچى ئاللاھقا سەجدە قىلمايدۇ.');
INSERT INTO "saleh" VALUES(27,26,'27:26',' ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇ بۈيۈك ئەرشنىڭ پەرۋەردىگارىدۇر».');
INSERT INTO "saleh" VALUES(27,27,'27:27',' سۇلايمان ئېيتتى: «سەن راست دەۋاتامسەن، يا يالغانمۇ؟ قارايمىز.');
INSERT INTO "saleh" VALUES(27,28,'27:28',' بۇ خېتىمنى ئېلىپ بېرىپ ئۇلارغا تاشلىغىن، ئاندىن ئۇلاردىن نېرىراق (جايدا يوشۇرۇنۇپ) تۇر، ئۇلارنىڭ قانداق جاۋاپ قايتۇرىدىغانلىقىغا قارىغىن».');
INSERT INTO "saleh" VALUES(27,29,'27:29',' ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! ماڭا ھەقىقەتەن سۇلايماندىن بىر پارچە قىممەتلىك خەت كەلدى.');
INSERT INTO "saleh" VALUES(27,30,'27:30',' (ئۇنىڭ مەزمۇنى شۇكى) ناھايىتى شەپقەتلىك ۋە مېھرىبان ئاللاھنىڭ ئىسمى بىلەن باشلايمەن[ـ30].');
INSERT INTO "saleh" VALUES(27,31,'27:31',' سىلەر ماڭا ھاكاۋۇرلۇق قىلماڭلار، مېنىڭ ئالدىمغا مۇسۇلمان بولغان ھالدا كېلىڭلار».');
INSERT INTO "saleh" VALUES(27,32,'27:32','ئۇ (يەنى بىلقىس) ئېيتتى: «ئى ئۇلۇغلار! مېنىڭ (بۇ) ئىشىمدا مەسلىھەت بېرىڭلار، سىلەرنى ئۈستىدە قويماي تۇرۇپ ھېچ ئىشنى بېكىتكىنىم يوق».');
INSERT INTO "saleh" VALUES(27,33,'27:33',' ئۇلار ئېيتتى: «بىز كۈچلۈك ۋە جەڭگىۋارمىز، ئىش سېنىڭ ئىختىيارىڭدىدۇر، (بىزنى) نېمىگە بۇيرۇيدىغانلىقىڭنى (ئويلاپ) كۆرگىن».');
INSERT INTO "saleh" VALUES(27,34,'27:34',' ئۇ (يەنى بىلقىس) ئېيتتى: «شۈبھىسىزكى، پادىشاھلار بىرەر شەھەرگە ھۇجۇم قىلىپ كىرسە، ئۇنى خاراب قىلىدۇ، شەھەرنىڭ مۆتىۋەر ئادەملىرىنى (ئۆلتۈرۈش، ئەسىرگە ئېلىش ۋە سۈرگۈن قىلىش بىلەن) خار قىلىدۇ، ئۇلارمۇ شۇنداق قىلىدۇ.');
INSERT INTO "saleh" VALUES(27,35,'27:35',' مەن چوقۇم ئۇلارغا سوۋغا ئەۋەتىمەن، ئەلچىلەرنىڭ نېمە خەۋەر ئېلىپ كېلىدىغانلىقىغا قارايمەن».');
INSERT INTO "saleh" VALUES(27,36,'27:36','ئەلچى سۇلايماننىڭ ئالدىغا يېتىپ كەلگەندە، سۇلايمان ئېيتتى: «سىلەر ماڭا مال بىلەن ياردەم قىلماقچىمۇ؟ ئاللاھنىڭ ماڭا بەرگەنلىرى سىلەرنىڭ بەرگەنلىرىڭلاردىن ياخشىدۇر، بەلكى سىلەر سوۋغاڭلار بىلەن كۆرەڭلەپ كېتىسىلەر. ');
INSERT INTO "saleh" VALUES(27,37,'27:37',' سەن قايتىپ كەتكىن، ئۇلارنىڭ ئۈستىگە ئۇلار تاقابىل تۇرالمايدىغان بىر قوشۇن بىلەن چوقۇم بارىمىز، ئۇلارنى يۇرتىدىن خار، كەمسىتىلگەن ھالدا چوقۇم چىقىرىۋېتىمىز».');
INSERT INTO "saleh" VALUES(27,38,'27:38','سۇلايمان ئېيتتى: «ئى ئۇلۇغلار! ئۇلار مېنىڭ قېشىمغا مۇسۇلمان بولۇپ كېلىشتىن بۇرۇن، (سىلەردىن) كىم ئۇنىڭ تەختىنى ئېلىپ كېلىدۇ».');
INSERT INTO "saleh" VALUES(27,39,'27:39',' جىندىن بولغان ئىفرىت ئېيتتى: «مەن ئۇنى ئورنۇڭدىن تۇرۇشتىن بۇرۇن ساڭا ئېلىپ كېلىمەن، مەن ئۇنى ئېلىپ كېلىشكە ئەلۋەتتە قادىرمەن، ئىشەنچلىكمەن».');
INSERT INTO "saleh" VALUES(27,40,'27:40',' (نازىل بولغان) كىتابنى چوڭقۇر بىلىدىغان زات (يەنى ئاسەف ئىبن بۇرخىيا): «ئۇنى مەن ساڭا كۆزۈڭنى يۇمۇپ ئاچقۇچە ئېلىپ كېلىمەن» دېدى (ئۇ دۇئا قىلىۋېدى، تەخت دەرھال ئالدىدا ھازىر بولدى). سۇلايمان تەختنىڭ يېنىدا تۇرغانلىقىنى كۆرگەندە: «بۇ پەرۋەردىگارىمنىڭ (ماڭا قىلغان) ئېھسانىدۇر، ئۇ شۈكۈر قىلامدىم، يا تۇزكورلۇق قىلامدىم، بۇنىڭ بىلەن مېنى سىنىدى، كىمكى شۈكۈر قىلىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن شۈكۈر قىلىدۇ، كىمكى تۇزكورلۇق قىلىدىكەن، (بىلىش كېرەككى) ھەقىقەتەن پەرۋەردىگارىم (ئۇنىڭ شۈكۈر قىلىشىدىن بىھاجەتتۇر)، (ئاللاھنىڭ) كەرىمى كەڭدۇر» دېدى.');
INSERT INTO "saleh" VALUES(27,41,'27:41','سۇلايمان (بىلقىسنىڭ يېتىپ كېلىشى يېقىنلاشقان ۋاقىتتا ئۇنىڭ ئەقلىنى سىناش يۈزىسىدىن) ئېيتتى: «ئۇنىڭ (يەنى بىلقىسنىڭ) تەختىنىڭ (شەكلىنى) ئۆزگەرتىڭلار، ئۇ تەختىنى تونۇمدۇ، تونىمامدۇ؟ قارايمىز».');
INSERT INTO "saleh" VALUES(27,42,'27:42',' بىلقىس كەلگەندە (ئۇنىڭغا): «سېنىڭ تەختىڭ مۇشۇنداقمۇ؟» دېيىلدى. ئۇ: «شۇدەك تۇرىدۇ» دېدى. (سۇلايمان ئاللاھنىڭ نېمىتىنى سۆزلەش يۈزىسىدىن ئېيتتى.) ئۇنىڭدىن (يەنى بىلقىستىن) بۇرۇن بىزگە (ئاللاھنى ۋە ئاللاھنىڭ قۇدرىتىنى بىلدۈرىدىغان) ئىلىم بېرىلدى، بىز مۇسۇلمان بولدۇق.');
INSERT INTO "saleh" VALUES(27,43,'27:43',' ئۇنى ئاللاھنى قويۇپ چوقۇنغان نەرسىلىرى (ئاللاھقا ئىبادەت قىلىشتىن) توستى. چۈنكى ئۇ كاپىر قەۋمدىن ئىدى.');
INSERT INTO "saleh" VALUES(27,44,'27:44',' ئۇنىڭغا (يەنى بىلقىسقا): «سارايغا كىرگىن» دېيىلدى، ئۇ (يەنى بىلقىس) ساراينى چوڭ سۇ دەپ گۇمانلىنىپ (كىيىمىنى كۆتۈرۈپ) ئىككى پاچىقىنى ئاچتى، سۇلايمان ئېيتتى: «شۈبھىسىزكى، ئۇ ئەينەكتىن ياسالغان سارايدۇر». ئۇ (يەنى بىلقىس) ئېيتتى: «پەرۋەردىگارىم، مەن ھەقىقەتەن (ئاللاھقا شېرىك كەلتۈرۈپ، قۇياشقا چوقۇنۇش بىلەن) ئۆزۈمگە زۇلۇم قىلدىم، سۇلايمان بىلەن بىللە ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا بويسۇندۇم (يەنى سۇلايماننىڭ دىنىغا ئەگىشىپ ئىسلامغا كىردىم)».');
INSERT INTO "saleh" VALUES(27,45,'27:45',' بىز ھەقىقەتەن سەمۇدقا (يەنى سەمۇد قەبىلىسىگە نەسەب جەھەتتىن) قېرىندىشى سالىھنى (پەيغەمبەر قىلىپ) ئەۋەتتۇق، (سالىھ) «ئاللاھقا ئىبادەت قىلىڭلار» (دېدى)، ئۇزۇن ئۆتمەي ئۇلار جېدەللىشىدىغان (يەنى دىن بارىسىدا جىدەللىشىدىغان مۆمىنلەر ۋە كاپىرلاردىن ئىبارەت) ئىككى گۇرۇھقا بۆلۈنۈپ كەتتى.');
INSERT INTO "saleh" VALUES(27,46,'27:46','سالىھ ئېيتتى: «ئى قەۋمىم، سىلەر نېمىشقا رەھمەتتىن بۇرۇن ئازابنىڭ كېلىشىنى تىلەيسىلەر؟ سىلەر رەھمەتكە ئېرىشىشىڭلار ئۈچۈن نېمىشقا ئاللاھتىن مەغپىرەت تەلەپ قىلمايسىلەر؟»');
INSERT INTO "saleh" VALUES(27,47,'27:47',' ئۇلار ئېيتتى: «(بىزگە كەلگەن قەھەتچىلىك) سېنىڭ ۋە سەن بىلەن بولغان كىشىلەرنىڭ شۇملۇقىدىن كەلدى». سالىھ ئېيتتى: «سىلەرگە كېلىدىغان ياخشى -يامانلىق ئاللاھ تەرىپىدىن كېلىدۇ، بەلكى سىلەرنى ئاللاھ سىنايدۇ».');
INSERT INTO "saleh" VALUES(27,48,'27:48',' شەھەردە (يەنى ھىجرىدە) يەر يۈزىدە بۇزغۇنچىلىق قىلىدىغان، ئىسلاھ قىلمايدىغان توققۇز نەپەر كىشى بار ئىدى.');
INSERT INTO "saleh" VALUES(27,49,'27:49','ئۇلار: «ئۆز ئارا ئاللاھ بىلەن قەسەم قىلىڭلار» دېدى. ئۇلار ئېيتتى: «بىر كېچىدە سالىھنى ۋە ئۇنىڭ تەۋەلىرىنى چوقۇم ئۆلتۈرەيلى، ئاندىن چوقۇم ئۇنىڭ (يەنى ئۇلارنىڭ) ئىگىسىگە، ئۇ ئۆلتۈرۈلگەن چاغدا بىز ئۈستىدە ئەمەس ئىدۇق، بىز ھەقىقەتەن راستچىلمىز، دەيلى».');
INSERT INTO "saleh" VALUES(27,50,'27:50','ئۇلار (سالىھقا قارشى) سۇيىقەست پىلانلىدى. ئۇلارنى (ئۇلارنىڭ ھالاك بولۇشىنى تېزلىتىش ئۈچۈن)سۇيىقەستى ئۈچۈن تۇيۇقسىز جازالىدۇق.');
INSERT INTO "saleh" VALUES(27,51,'27:51',' ئۇلارنىڭ سۇيىقەستىنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىنكى، ئۇلارنى ۋە ئۇلارنىڭ قەۋمىنى پۈتۈنلەي ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(27,52,'27:52','ئۇلار زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئەنە ئۇلارنىڭ ئۆيلىرى (ئادەمزاتتىن) خالىي بولۇپ قالدى. ئاللاھنىڭ (قۇدرىتىنى) بىلىدىغان قەۋم ئۈچۈن بۇنىڭدا (چوڭ) ئىبرەت بار.');
INSERT INTO "saleh" VALUES(27,53,'27:53','(سالىھ بىلەن) ئىمان ئېيتقان ۋە تەقۋادارلىق قىلغانلارنى (ئازابتىن) قۇتقۇزدۇق. ');
INSERT INTO "saleh" VALUES(27,54,'27:54','(لۇتنىڭ قىسسەسىنى بايان قىلغىن) ئەينى زاماندا لۇت ئۆزىنىڭ قەۋمىگە ئېيتتى: «سىلەر بىلىپ تۇرۇپ قەبىھ ئىشلارنى قىلامسىلەر؟');
INSERT INTO "saleh" VALUES(27,55,'27:55',' سىلەر ئاياللارنى قويۇپ، جىنسىي تەلىۋىڭلارنى ئەرلەر بىلەن قاندۇرامسىلەر؟ بەلكى سىلەر نادان قەۋمسىلەر».');
INSERT INTO "saleh" VALUES(27,56,'27:56',' ئۇلارنىڭ بىردىن بىر جاۋابى: «لۇتنىڭ تەۋەلىرىنى شەھرىڭلاردىن ھەيدەپ چىقىرىڭلار، ئۇلار پاك كىشىلەردۇر» دېيىشتىن ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(27,57,'27:57',' بىز ئۇنى ۋە خوتۇنىدىن باشقا تەۋەلىرىنى قۇتقۇزدۇق، تەقدىرىمىز بىلەن ئۇنى (يەنى سالىھنىڭ خوتۇنىنى ئازابقا) قالغۇچىلاردىن قىلدۇق.');
INSERT INTO "saleh" VALUES(27,58,'27:58','ئۇلارنىڭ ئۈستىگە بىز يامغۇر (يەنى تاش) ياغدۇردۇق. ئاگاھلاندۇرۇلغۇچىلارغا ياغدۇرۇلغان يامغۇر نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(27,59,'27:59',' «جىمى ھەمدۇسانا ئاللاھقا خاستۇر! ئۇنىڭ (پەيغەمبەرلىككە) تاللىغان بەندىلىرىگە ئامانلىق تىلەيمەن!» دېگىن. ئاللاھ ياخشىمۇ ياكى ئۇلارنىڭ شېرىك كەلتۈرگەن بۇتلىرى ياخشىمۇ؟.');
INSERT INTO "saleh" VALUES(27,60,'27:60',' ئاسمانلارنى ۋە زېمىننى ياراتقان، سىلەرگە بۇلۇتتىن يامغۇر ياغدۇرۇپ بەرگەن، ئۇنىڭ بىلەن گۈزەل باغلارنى يېتىشتۈرۈپ بەرگەن كىم؟ ئۇ باغلارنىڭ دەرەخلىرىنى ئۆستۈرۈش سىلەرنىڭ قولۇڭلاردىن كەلمەيدۇ، ئاللاھتىن باشقا ئىلاھ بارمۇ؟ باشقا ئىلاھ يوقتۇر، ئۇلار (ھەقىقەتتىن) بۇرۇلۇپ كەتكەن قەۋمدۇر.');
INSERT INTO "saleh" VALUES(27,61,'27:61',' زېمىننى (ئىنسانلارغا ۋە ھايۋانلارغا) تۇرالغۇ قىلغان، ئۇنىڭ تۈرلۈك تەرەپلىرىدە دەريالارنى ئاققۇزغان، ئۇنىڭ ئۈستىدە (تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى ئورناتقان، ئىككى دېڭىز ئارىسىدا (يەنى تاتلىق سۇ بىلەن شورلۇق سۇ ئارىسىدا ئارىلىشىپ كەتمەسلىكى ئۈچۈن) توساقلارنى قىلغان كىم؟ ئاللاھتىن باشقا ئىلاھ بارمۇ؟ (باشقا ئىلاھ يوق) ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(27,62,'27:62',' بېشىغا كۈن چۈشكەن ئادەم دۇئا قىلسا (ئۇنىڭ دۇئاسىنى) ئىجابەت قىلىدىغان، ئۇنىڭ بېشىغا كەلگەن ئېغىرچىلىقنى كۆتۈرۈۋېتىدىغان ۋە سىلەرنى زېمىننىڭ ئورۇنباسارلىرى قىلغان كىم؟ ئاللاھتىن باشقا ئىلاھ بارمۇ؟ سىلەر ئازغىنا ۋەز ـ نەسىھەت ئالىسىلەر.');
INSERT INTO "saleh" VALUES(27,63,'27:63','قۇرۇقلۇقنىڭ ۋە دېڭىزنىڭ قاراڭغۇلۇقلىرىدا سىلەرگە يول كۆرسىتىپ بېرىدىغان، رەھمىتىدىن (يەنى يامغۇر ياغدۇرۇپ بېرىشتىن) ئىلگىرى شاماللارنى بېشارەت قىلىپ ئەۋەتىدىغان كىم؟ ئاللاھتىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) ئاللاھ ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(27,64,'27:64',' (ئىنساننى) دەسلەپتە خەلق ئەتكەن، ئاندىن ئۇنى تىرىلدۈرىدىغان كىم؟ سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىدىغان كىم؟ ئاللاھتىن باشقا ئىلاھ بارمۇ؟ (ھېچ ئىلاھ يوقتۇر) ئېيتقىنكى، «(ئەگەر دەۋايىڭلاردا) راستچىل بولىدىغان بولساڭلار، دەلىللىرىڭلارنى كەلتۈرۈڭلار».');
INSERT INTO "saleh" VALUES(27,65,'27:65',' ئاللاھتىن بۆلەك ئاسمانلاردىكى ۋە زېمىندىكىلەر غەيبنى بىلمەيدۇ، ئۇلار (يەنى خالايىق) قاچان تىرىلىدىغانلىقلىرىنى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(27,66,'27:66',' ئۇلارنىڭ ئاخىرەت توغرىسىدىكى ئىلمى پىشىپ يېتىلدىمۇ؟ ئۇنداق ئەمەس، ئۇلار ئاخىرەت توغرۇلۇق گۇماندىدۇر، ئۇلار ئاخىرەت توغرۇلۇق كوردۇر.');
INSERT INTO "saleh" VALUES(27,67,'27:67','كاپىرلار (يەنى قايتا تىرىلىشنى ئىنكار قىلغۇچى مەككە مۇشرىكلىرى) ئېيتتى: «بىز ۋە بىزنىڭ ئاتا ـ بوۋىلىرىمىز توپا بولۇپ كەتكەندىن كېيىن (قەبرىمىزدىن تىرىلىپ) چوقۇم چىقىرىلامدۇق؟');
INSERT INTO "saleh" VALUES(27,68,'27:68','ھەقىقەتەن بىزگە ۋە ئاتا ـ بوۋىلىرىمىزغا ئىلگىرى (قايتا تىرىلىش) ۋەدە قىلىنغان ئىدى. بۇ پەقەت بۇرۇنقىلارنىڭ قىسسەلىرىدۇر».');
INSERT INTO "saleh" VALUES(27,69,'27:69',' سەن (بۇ كۇففارلارغا) ئېيتقىن: «زېمىندا سەير قىلىڭلار، گۇناھكارلار (يەنى پەيغەمبەرلەرنى ئىنكار قىلغۇچىلار) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار» (ئۇلارنى ئاللاھ ھالاك قىلمىدىمۇ؟ ئىلگىرىكى كۇففارلارنىڭ بېشىغا كەلگەن كۈن كېيىنكى كۇففارلارنىڭ بېشىغىمۇ كېلىدۇ).');
INSERT INTO "saleh" VALUES(27,70,'27:70',' (ئى مۇھەممەد!) سەن ئۇلارنىڭ (ئىمان ئېيتمىغانلىقىدىن) قايغۇرمىغىن، ئۇلارنىڭ ھىيلە ـ مىكرىدىن ئىچىڭ پۇشمىسۇن (ئاللاھ سېنى ئۇلارنىڭ شەررىدىن ساقلايدۇ).');
INSERT INTO "saleh" VALUES(27,71,'27:71',' ئۇلار: «ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار، (سىلەر بىزگە ۋەدە قىلغان) ئازاب قاچان چۈشىدۇ؟» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(27,72,'27:72',' «سىلەر ئالدىراپ كەتكەن ئازابنىڭ بىر قىسمى سىلەرگە يېقىنلاشقان بولۇشى مۇمكىن» دېگىن .');
INSERT INTO "saleh" VALUES(27,73,'27:73','ەرۋەردىگارىڭ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر، لېكىن ئۇلارنىڭ تولىسى (پەرۋەردىگارىغا) شۈكۈر قىلمايدۇ.');
INSERT INTO "saleh" VALUES(27,74,'27:74',' پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرغانلىرىنى ۋە ئاشكارا قىلغانلىرىنى (يەنى ئۇلارنىڭ پەيغەمبەر ئەلەيھىسسالامغا قارىتا ئاداۋىتىنى ۋە سۇيىقەستىنى) شەك ـ شۈبھىسىز بىلىدۇ.');
INSERT INTO "saleh" VALUES(27,75,'27:75','ئاسمان - زېمىندا لەۋھۇلمەھپۇزغا يېزىلمىغان ھېچبىر سىر يوق.');
INSERT INTO "saleh" VALUES(27,76,'27:76',' (پەيغەمبەرلەرنىڭ تۈگەنچىسى مۇھەممەد ئەلەيھىسسالامغا نازىل بولغان) بۇ قۇرئان ھەقىقەتەن ئىسرائىل ئەۋلادىغا ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرىنىڭ كۆپىنى بايان قىلىپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(27,77,'27:77','قۇرئان مۆمىنلەر ئۈچۈن ھەقىقەتەن ھىدايەتتۇر ۋە رەھمەتتۇر.');
INSERT INTO "saleh" VALUES(27,78,'27:78',' شۈبھىسىزكى، پەرۋەردىگارىڭ ئۇلار (يەنى بەنى ئىسرائىل ئەۋلادى) نىڭ ئارىسىدا (قىيامەت كۈنى) ئادىل ھۆكۈم چىقىرىدۇ، ئاللاھ غالىبتۇر، (بەندىلىرىنىڭ ئىشلىرىنى) بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(27,79,'27:79','ئاللاھقا تەۋەككۈل قىلغىن (يەنى ئىشىڭنى ئاللاھقا تاپشۇرغىن، ھەممە ئىشىڭدا ئاللاھقا يۆلەنگىن، ئاللاھ ساڭا مەدەتكاردۇر)، (ئى مۇھەممەد!) شۈبھىسىزكى، سەن روشەن ھەق (دىن) ئۈستىدەدۇرسەن.');
INSERT INTO "saleh" VALUES(27,80,'27:80',' سەن ئۆلۈكلەرگە ۋە يۈز ئۆرۈگەن گاسلارغا (يەنى دىللىرىنىڭ ئۆلۈكلىكىدە ئۆلۈكلەرگە ۋە ھەقنى ئاڭلىماسلىقتا گاسلارغا ئوخشايدىغان كۇففارلارغا) دەۋەتنى ئاڭلىتالمايسەن.');
INSERT INTO "saleh" VALUES(27,81,'27:81',' سەن (دىلى) كورلارنى گۇمراھلىقتىن ئايرىپ، ھىدايەت قىلالمايسەن، سەن (دەۋىتىڭنى) پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتىپ مۇسۇلمان بولغانلارغىلا ئاڭلىتالايسەن.');
INSERT INTO "saleh" VALUES(27,82,'27:82',' ئۇلار (يەنى كۇففارلار) غا ئالدىنئالا ئېيتىلغان سۆز ئىشقا ئاشقاندا (يەنى ئازاپ ۋە قىيامەت يېقىنلاشقاندا)، ئۇلارغا زېمىندىن بىر تۈرلۈك ھايۋاننى چىقىرىمىزكى، ئۇ ئۇلارغا ئىنسانلارنىڭ بىزنىڭ ئايەتلىرىمىزگە ئىشەنمىگەنلىكىنى سۆزلەيدۇ.');
INSERT INTO "saleh" VALUES(27,83,'27:83','ئۇ كۈندە ھەر ئۈممەتنىڭ ئىچىدىن بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلاردىن بىر توپ ئادەمنى (ھېساب ئېلىش ۋە جازالاش ئۈچۈن) يىغىمىز (ئالدى ـ كەينى يىغىلغىچە) ئۇلار توختىتىپ تۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(27,84,'27:84',' ئۇلار (ئاللاھنىڭ دەرگاھىغا) كەلگەن چاغلىرىدا، ئاللاھ ئۇلارغا: «سىلەر تولۇق بىلمەي تۇرۇپ مېنىڭ ئايەتلىرىمنى ئىنكار قىلدىڭلارمۇ؟ سىلەر (ئەمرى قىلىنغان نەرسىلەردىن) زادى نېمىلەرنى قىلدىڭلار؟ » دەيدۇ[84');
INSERT INTO "saleh" VALUES(27,85,'27:85','ئۇلار (ئۆزلىرىگە) زۇلۇم قىلغانلىقلىرى ئۈچۈن، ئۇلارغا ئازاب ھەقلىق بولدى. ئۇلار (ئۆزرىلىرى بولمىغانلىقى ئۈچۈن) سۆز قىلالمايدۇ.');
INSERT INTO "saleh" VALUES(27,86,'27:86',' ئۇلارغا كېچىنى ئارام ئالىدىغان (ۋاقىت) قىلغانلىقىمىزنى، كۈندۈزنى (ھاياتلىق يولىدا ھەرىكەتلىنىش ئۈچۈن) يورۇق قىلغانلىقىمىزنى ئۇلار ئۇقمامدۇ؟ بۇنىڭدا ئىمان ئېيتىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(27,87,'27:87','ئۇ كۈندە سۇر چېلىنىدۇ، ئاللاھ خالىغانلاردىن باشقا ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى قورقۇنچ باسىدۇ، ھەممە ئاللاھقا بويسۇنغان ھالدا كېلىدۇ.');
INSERT INTO "saleh" VALUES(27,88,'27:88',' تاغلارنى تۇرغۇن ھالەتتە گۇمان قىلىسەن، ھالبۇكى، ئۇلار بۇلۇتتەك چۆگىلەپ تۇرىدۇ، (بۇ) ھەممە نەرسىنى پۇختا ياراتقان ئاللاھنىڭ ھۈنىرىدۇر، ئاللاھ ھەقىقەتەن سىلەرنىڭ قىلمىشلىرىڭلاردىن تولۇق خەۋەرداردۇر. ');
INSERT INTO "saleh" VALUES(27,89,'27:89','ياخشى ئىش قىلغانلار قىلغان ياخشىلىقىدىن ئوبدانراق مۇكاپاتقا ئېرىشىدۇ، ئۇلار بۇ كۈندە قورقۇنچتىن ئەمىن بولىدۇ.');
INSERT INTO "saleh" VALUES(27,90,'27:90',' يامان ئىش قىلغانلار دوزاخقا يۈزى بىلەن تاشلىنىدۇ، سىلەر پەقەت قىلمىشىڭلارغا يارىشا جازالىنىسىلەر ياكى مۇكاپاتلىنىسىلەر. ');
INSERT INTO "saleh" VALUES(27,91,'27:91',' (ئى مۇھەممەد! ئېيتقىنكى) «مەن پەقەت ئاللاھ ھۆرمەتلىك قىلغان بۇ شەھەر (يەنى مەككە مۇكەررەمە) نىڭ پەرۋەردىگارىغا ئىبادەت قىلىشقا بۇيرۇلدۇم. ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، مەن مۇسۇلمانلاردىن بولۇشقا، قۇرئان ئوقۇشقا بۇيرۇلدۇم». كىمكى ھىدايەت تاپىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈندۇر، كىمكى ئازىدىكەن (ئۇنىڭ ۋابالى ئۆزىگە بولىدۇ). سەن (ئۇنىڭغا) ئېيتقىنكى، «مەن پەقەت ئاگاھلاندۇرغۇچىلاردىنمەن»[91- 92].');
INSERT INTO "saleh" VALUES(27,92,'27:92',' (ئى مۇھەممەد! ئېيتقىنكى) «مەن پەقەت ئاللاھ ھۆرمەتلىك قىلغان بۇ شەھەر (يەنى مەككە مۇكەررەمە) نىڭ پەرۋەردىگارىغا ئىبادەت قىلىشقا بۇيرۇلدۇم. ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، مەن مۇسۇلمانلاردىن بولۇشقا، قۇرئان ئوقۇشقا بۇيرۇلدۇم». كىمكى ھىدايەت تاپىدىكەن، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈندۇر، كىمكى ئازىدىكەن (ئۇنىڭ ۋابالى ئۆزىگە بولىدۇ). سەن (ئۇنىڭغا) ئېيتقىنكى، «مەن پەقەت ئاگاھلاندۇرغۇچىلاردىنمەن»[91- 92].');
INSERT INTO "saleh" VALUES(27,93,'27:93',' (ئى مۇھەممەد!) ئېيتقىنكى، «جىمى ھەمدۇسانا ئاللاھقا خاستۇر، ئاللاھ سىلەرگە (قۇدرىتىنى كۆرسىتىپ بېرىدىغان) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر ئۇلارنى (تونۇش پايدا بەرمەيدىغان چاغدا) تونۇيسىلەر، پەرۋەردىگارىڭ سىلەرنىڭ قىلغان ئەمەللىرىڭلاردىن غاپىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(28,1,'28:1',' تاسن، مىم.');
INSERT INTO "saleh" VALUES(28,2,'28:2','بۇ، روشەن كىتاب (يەنى قۇرئان) ئايەتلىرىدۇر.');
INSERT INTO "saleh" VALUES(28,3,'28:3','ئىمان ئېيتقان قەۋم ئۈچۈن ساڭا مۇسا ۋە پىرئەۋن قىسسەسىنى ھەقلىق ئاساستا ئوقۇپ بېرىمىز.');
INSERT INTO "saleh" VALUES(28,4,'28:4',' شۈبھىسىزكى، پىرئەۋن (مىسىر) زېمىنىدا (زومىگەرلىكتە) ھەددىدىن ئاشتى، ئاھالىسىنى بۆلەكلەرگە بۆلۈپ، ئۇلاردىن بىر تائىپە (يەنى بەنى ئىسرائىل) نى بوزەك قىلدى. ئۇلارنىڭ ئوغۇللىرىنى ئۆلتۈرۈپ، قىزلىرىنى (خىزمەتكە سېلىش ئۈچۈن) تىرىك قالدۇردى، پىرئەۋن ھەقىقەتەن بۇزغۇنچىلاردىن ئىدى. ');
INSERT INTO "saleh" VALUES(28,5,'28:5',' بىز (مىسىر) زېمىنىدا بوزەك قىلىنغانلارغا مەرھەمەت قىلىشنى ئىرادە قىلىمىز، ئۇلارنى يولباشچىلاردىن قىلىشنى، ئۇلارنى پىرئەۋن ۋە ئۇنىڭ قەۋمىنىڭ (مۈلكىگە) ۋارىس قىلىشنى ئىرادە قىلىمىز.');
INSERT INTO "saleh" VALUES(28,6,'28:6','ئۇلارنى زېمىندا كۈچ ـ قۇۋۋەتكە ئىگە قىلماقچىمىز، بىز پىرئەۋنگە، (ۋەزىرى) ھامانغا ۋە ئۇلارنىڭ قوشۇنىغا ئۇلار (يەنى بوزەك قىلىنغانلار) دىن قورقىدىغان نەرسىنى كۆرسىتىمىز.');
INSERT INTO "saleh" VALUES(28,7,'28:7','مۇسانىڭ ئانىسىغا ئىلھام بىلەن بىلدۈردۇقكى: «مۇسانى ئېمىتكىن، ئۇنىڭ زىيانكەشلىككە ئۇچرىشىدىن قورقساڭ، ئۇنى (ساندۇققا سېلىپ) دەرياغا (يەنى نىل دەرياسىغا) تاشلىغىن، ئۇنى (ھالاك بولارمىكىن دەپ) قورقمىغىن، (ئۇنىڭ پىراقىدىن) قايغۇرمىغىن، ئۇنى ساڭا چوقۇم قايتۇرىمىز ۋە ئۇنى پەيغەمبەرلەردىن قىلىمىز».');
INSERT INTO "saleh" VALUES(28,8,'28:8',' پىرئەۋننىڭ ئائىلىسىدىكىلەر مۇسانى (نىل دەرياسىدىن) سۈزۈۋالدى. ئاقىۋەت (مۇسا) ئۇلارغا دۈشمەن ۋە خاپىلىق بولدى. شۈبھىسىزكى، پىرئەۋن، ھامان ۋە ئۇلارنىڭ قوشۇنلىرى خاتالاشقان ئىدى.');
INSERT INTO "saleh" VALUES(28,9,'28:9',' پىرئەۋننىڭ ئايالى: «(بۇ بالا) ماڭا ۋە ساڭا كۆز نۇرى (يەنى خۇشاللىق) بولسۇن، ئۇنى ئۆلتۈرمەڭلار، بەلكى ئۇ بىزگە پايدا يەتكۈزەر ياكى ئۇنى ئوغۇل قىلىۋالارمىز» دېدى. ھالبۇكى، ئۇلار (پىرئەۋننىڭ ۋە ئۇنىڭ ياردەمچىلىرىنىڭ ھالاكىتىنىڭ مۇسانىڭ قولىدا بولىدىغانلىقىنى) ئۇقمايتتى.');
INSERT INTO "saleh" VALUES(28,10,'28:10','مۇسانىڭ ئانىسى (مۇسانىڭ پىرئەۋننىڭ قولىغا چۈشۈپ قالغانلىقىنى ئاڭلاپ) ئەس ـ ھوشىنى يوقاتتى، (ئۇنىڭ ئاللاھنىڭ بالىنى قايتۇرۇش ۋەدىسىگە) ئىشەنگۈچىلەردىن بولۇشى ئۈچۈن، ئۇنىڭ كۆڭلىنى خاتىرجەم قىلمىغان بولساق، ئۇ بالىنى ئاشكارىلاپ قويغىلى تاس قالغان ئىدى.');
INSERT INTO "saleh" VALUES(28,11,'28:11','ئانىسى مۇسانىڭ ھەمشىرىسىگە: «(خەۋەرنى ئۇقۇش ئۈچۈن) مۇساغا ئەگەشكىن» دېدى. ھەمشىرىسى مۇسانى يىراقتىن كۆرۈپ تۇردى. ھالبۇكى، ئۇلار (ئۇنى) تۇيمايتتى.');
INSERT INTO "saleh" VALUES(28,12,'28:12',' (ئۆز ئانىسى كېلىشتىن) ئىلگىرى مۇسانى سۈت ئەمگۈزگۈچى ئاياللارنىڭ ئېمىتىشىدىن توستۇق، مۇسانىڭ ھەمشىرىسى: «سىلەرگە مۇسانى ئوبدان باقىدىغان بىر ئائىلىنى كۆرسىتىپ قويايمۇ؟ ئۇلار مۇسانى ئىخلاس بىلەن باقىدۇ» دېدى[12');
INSERT INTO "saleh" VALUES(28,13,'28:13','ئانىسىنىڭ خۇشال بولۇشى، قايغۇرماسلىقى ۋە ئاللاھنىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى بىلىشى ئۈچۈن بىز مۇسانى ئۇنىڭغا قايتۇردۇق، لېكىن ئىنسانلارنىڭ تولىسى (ئاللاھنىڭ ۋەدىسىنىڭ ھەق ئىكەنلىكىنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(28,14,'28:14',' مۇسا ئۆسۈپ يېتىلگەندە، ئەقلى توشقاندا ئۇنىڭغا پەيغەمبەرلىكنى ۋە ئىلىمنى ئاتا قىلدۇق، بىز ياخشىلارغا مۇشۇنداق مۇكاپات بېرىمىز. ');
INSERT INTO "saleh" VALUES(28,15,'28:15',' مۇسا شەھەر ئاھالىسىدىن ھېچ كىشىنى كۆرگىلى بولمايدىغان بىر ۋاقىتتا (يەنى چۈشلۈك ئۇيقۇدىكى چاغدا) شەھەرگە كىردى، ئۇ شەھەردە ئىككى كىشىنىڭ ئۇرۇشۇۋاتقانلىقىنى كۆردى. (ئۇرۇشۇۋاتقانلاردىن) بىرى ئۆز قەۋمىدىن بولسا، يەنە بىرى ئۇنىڭ دۈشمىنى ئىدى. ئۆز قەۋمىدىن بولغان ئادەم دۈشمىنىگە قارشى ئۇنىڭدىن ياردەم تىلىدى. مۇسا ئۇنى مۇشت بىلەن بىرنى ئۇرۇپ ئۆلتۈرۈپ قويدى. مۇسا ئېيتتى: «بۇ شەيتاننىڭ ئىشىدۇر، شۈبھىسىزكى، شەيتان ئازدۇرغۇچى ئاشكارا دۈشمەندۇر». ');
INSERT INTO "saleh" VALUES(28,16,'28:16','مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۆزۈمگە زۇلۇم قىلدىم. ماڭا مەغپىرەت قىلغىن». ئاللاھ ئۇنىڭغا مەغپىرەت قىلدى، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(28,17,'28:17',' مۇسا ئېيتتى: «پەرۋەردىگارىم! ماڭا (مەغپىرەت قىلغانلىق) نېمىتىڭ سەۋەبى بىلەن مەن ھەرگىز گۇناھكارلارغا ياردەمچى بولمايمەن».');
INSERT INTO "saleh" VALUES(28,18,'28:18',' مۇسا ئەتىسى ئەتىگەندە قورقۇنچ ئىچىدە ئەتراپىغا قاراپ شەھەر ئارىلاپ يۈرەتتى. توساتتىن تۈنۈگۈن ئۇنىڭدىن ياردەم تىلىگەن ئادەم (بىر قىبتى بىلەن ئۇرۇشۇپ تۇرغان ھالدا) مۇسادىن يەنە ياردەم تىلىدى. مۇسا ئۇنىڭغا: «سەن ھەقىقەتەن ئاشكارا گۇمراھ ئىكەنسەن» دېدى.');
INSERT INTO "saleh" VALUES(28,19,'28:19',' مۇسا ئىككىسىنىڭ (ئورتاق) دۈشمىنى بولغان ئادەم (يەنى قىبتى) گە قول ئۇزاتماقچى بولغاندا، ياردەم تىلىگەن ئادەم: «ئى مۇسا! سەن تۈنۈگۈن بىر ئادەمنى ئۆلتۈرگەندەك مېنى ئۆلتۈرمەكچىمۇسەن؟ سەن زېمىندا ئىسلاھ قىلغۇچىلاردىن بولۇشنى خالىماي، پەقەت زومىگەر بولۇشنىلا خالايسەن» دېدى. ');
INSERT INTO "saleh" VALUES(28,20,'28:20',' بىر ئادەم شەھەرنىڭ يىراق يېرىدىن ئالدىراپ كېلىپ: «ئى مۇسا! شۈبھىسىزكى، كاتتىلار سېنى ئۆلتۈرۈشنى مەسلىھەت قىلىشىۋاتىدۇ، (شەھەردىن دەرھال) چىقىپ كەتكىن، مەن ئەلۋەتتە ساڭا سادىق كىشىلەردىنمەن» دېدى.');
INSERT INTO "saleh" VALUES(28,21,'28:21',' مۇسا قورقۇنچ ئىچىدە ئەتراپقا باققان ھالدا شەھەردىن چىقتى، ئۇ: «ئى پەرۋەردىگارىم! مېنى زالىم قەۋمدىن قۇتقۇزغىن» دېدى. ');
INSERT INTO "saleh" VALUES(28,22,'28:22',' ئۇ مەديەن تەرەپكە يۈزلەنگەن چاغدا: «پەرۋەردىگارىم مېنى توغرا يولغا يېتەكلىشى مۇمكىن» دېدى. ');
INSERT INTO "saleh" VALUES(28,23,'28:23',' مۇسا مەديەندىكى بىر بۇلاقنىڭ يېنىغا كەلگەندە (ماللىرىنى) سۇغىرىۋاتقان بىر توپ كىشىلەرنى كۆردى. ئۇلاردىن باشقا يەنە (قويلىرىنى سۇدىن) توسۇپ تۇرغان ئىككى ئايالنى كۆردى. مۇسا ئۇلارغا: «سىلەرگە نېمە بولدى؟ (يەنى قويلىرىڭلارنى سۇدىن توسۇپ تۇرۇپ تۇرۇپسىلەرغۇ؟)» دېدى، ئۇلار: «پادىچىلار قويلىرىنى سۇغۇرۇپ بولغاندىن كېيىن، ئاندىن بىز سۇغىرىمىز، ئاتىمىز بولسا ياشىنىپ قالغان بوۋايدۇر» دېدى.');
INSERT INTO "saleh" VALUES(28,24,'28:24','مۇسا ئۇلارنىڭ قويلىرىنى سۇغۇرۇپ بەردى، ئاندىن ئارقىسىغا بۇرۇلۇپ (بىر دەرەخنىڭ) سايىسىگە بېرىپ (ئولتۇرۇپ): «پەرۋەردىگارىم! ماڭا رىزىقتىن نېمىنىلا بەرسەڭ، مەن ھەقىقەتەن ئۇنىڭغا موھتاج» دېدى.');
INSERT INTO "saleh" VALUES(28,25,'28:25',' ئۇلارنىڭ (يەنى ئۇ ئىككى ئايالنىڭ) بىرى مۇسانىڭ يېنىغا ئۇياتچانلىق بىلەن مېڭىپ كېلىپ: «(قويلىرىمىزنى) سۇغىرىپ بەرگەنلىكىڭنىڭ ھەققىنى بېرىش ئۈچۈن ئاتام سېنى راستلا چاقىرىدۇ» دېدى. مۇسا ئۇنىڭ (يەنى شۇئەيبنىڭ) قېشىغا كېلىپ ئەھۋالنى ھېكايە قىلىپ بەردى، شۇئەيب: « قورقمىغىن، زالىم قەۋمدىن قۇتۇلدۇڭ» دېدى.');
INSERT INTO "saleh" VALUES(28,26,'28:26','ئۇلارنىڭ (يەنى ئاياللارنىڭ) بىرى ئېيتتى: «ئى ئاتا، ئۇنى سەن ئىشلەتكىن، بۇ سەن ئىشلەتكەنلەرنىڭ ئەڭ ياخشىسىدۇر، كۈچلۈك، ئىشەنچلىكتۇر».');
INSERT INTO "saleh" VALUES(28,27,'28:27',' ئۇ (يەنى شۇئەيب) ئېيتتى: «ماڭا سەككىز يىل ئىشلەپ بېرىشىڭ بەدىلىگە ساڭا بۇ ئىككى قىزىمنىڭ بىرىنى بېرىشنى خالايمەن، ئەگەر ئون يىلنى توشقۇزۇۋەتسەڭ (ئۇمۇ) ئىختىيارىڭ، (ئون يىلنى شەرت قىلىپ) سېنى مۇشەققەتكە سېلىپ قويۇشنى خالىمايمەن، خۇدا خالىسا، مېنىڭ ياخشى ئادەم ئىكەنلىكىمنى بايقايسەن».');
INSERT INTO "saleh" VALUES(28,28,'28:28',' مۇسا ئېيتتى: «بۇ ئىككىمىزنىڭ ئارىمىزدىكى توختامدۇر، ئىككى مۇددەت (يەنى سەككىز يىل بىلەن ئون يىل) نىڭ قايسىسىنى توشقۇزسام توشقۇزاي، ماڭا ئارتۇقچە تەلەپ قويۇشقا بولمايدۇ، ئاللاھ بىزنىڭ سۆزىمىزگە گۇۋاھتۇر».');
INSERT INTO "saleh" VALUES(28,29,'28:29','مۇسا (ئۆز ئارا كېلىشكەن) مۇددەتنى (يەنى كېلىشكەن مۇددەتنىڭ تولۇقراقى بولغان ئون يىلنى) توشقۇزغاندىن كېيىن ئايالىنى ئېلىپ (مىسىرغا قاراپ) يولغا چىقتى، ئۇ تۇر تېغى تەرىپىدە ئوت كۆردى، ئۇ ئايالىغا: «شۈبھىسىزكى، مەن (يىراقتىن) ئوت كۆردۈم، ئۇ يەردىن مەن سىلەرگە بىر خەۋەر ئېلىپ كېلەي، يا سىلەرنىڭ ئىسسىنىشىڭلار ئۈچۈن ئوتتىن بىر پارچە چوغ ئېلىپ كېلەي» دېدى (مۇسا ئەلەيھىسسالام ئوتنىڭ يېنىغا كېلىپ، ئۇنىڭ ئوت ئەمەس نۇر ئىكەنلىكىنى بىلدى).');
INSERT INTO "saleh" VALUES(28,30,'28:30',' مۇسا ئوتنىڭ يېنىغا كەلگەندە ۋادىنىڭ ئوڭ تەرىپىنىڭ گىرۋىكىگە توغرا كېلىدىغان مۇبارەك جايدىكى دەرەخ تەرەپتىن (مۇنداق) نىدا ئاڭلاندى: «ئى مۇسا، (ساڭا سۆز قىلىۋاتقان) مەن، ھەقىقەتەن، ئالەملەرنىڭ پەرۋەردىگارى ئاللاھتۇرمەن.');
INSERT INTO "saleh" VALUES(28,31,'28:31',' سەن ھاساڭنى تاشلىغىن». مۇسا ھاسىسىنىڭ گويا ئەجدىھادەك تېز ھەرىكەتلىنىۋاتقانلىقىنى كۆرگەندە ئارقىسىغا بۇرۇلۇپ قاچتى، كەينىگە قارىمىدى، (نىدا قىلىندىكى) «ئى مۇسا! ئالدىڭغا ماڭغىن، قورقمىغىن، سەن ھەقىقەتەن (قورقۇنچتىن) ئەمىن بولغۇچىلاردىنسەن.');
INSERT INTO "saleh" VALUES(28,32,'28:32','قولۇڭنى قوينۇڭغا سالغىن، ئۇ ھېچقانداق ئىللەتسىز (بىر پارچە ئايدەك پارلاپ) ئاپئاق بولۇپ چىقىدۇ، قورقۇنچنى دەپئى قىلىش ئۈچۈن، قولۇڭنى يىغىۋالغىن (يەنى قوينۇڭغا سېلىۋالغىن)، بۇ ئىككىسى پىرئەۋنگە (ۋە ئۇنىڭ قەۋمىنىڭ) چوڭلىرىغا پەرۋەردىگارىڭ تەرىپىدىن كەلگەن پاكىتتۇر، ئۇلار ھەقىقەتەن (بىزنىڭ ئىتائىتىمىزدىن چىققۇچى) پاسىق قەۋم ئىدى». ');
INSERT INTO "saleh" VALUES(28,33,'28:33',' مۇسا ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن ئۇلاردىن بىر ئادەمنى ئۆلتۈرۈپ قويدۇم، شۇنىڭ ئۈچۈن ئۇلارنىڭ مېنى ئۆلتۈرۈشىدىن قورقىمەن.');
INSERT INTO "saleh" VALUES(28,34,'28:34',' قېرىندىشىم ھارۇننىڭ تىلى مېنىڭدىن راۋاندۇر، ئۇنى مەن بىلەن (پەيغەمبەرلىكىمنى) تەستىقلايدىغان ياردەمچى قىلىپ ئەۋەتكىن، مەن ھەقىقەتەن ئۇلارنىڭ مېنى ئىنكار قىلىشىدىن قورقىمەن».');
INSERT INTO "saleh" VALUES(28,35,'28:35',' ئاللاھ ئېيتتى: «قېرىندىشىڭ ئارقىلىق سېنى كۈچەيتىمىز، ئىككىڭلارنى غالىب قىلىمىز، شۇنىڭ ئۈچۈن ئۇلار ئىككىڭلارغا زىيانكەشلىك قىلالمايدۇ، سىلەر ئىككىڭلار ۋە ئىككىڭلارغا ئەگەشكەنلەر بىزنىڭ ئايەتلىرىمىز ئارقىلىق غەلىبە قىلىسىلەر».');
INSERT INTO "saleh" VALUES(28,36,'28:36',' مۇسا ئۇلارغا بىزنىڭ روشەن مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار: «بۇ ئويدۇرۇپ چىقىرىلغان سېھىردۇر، بىزنىڭ ئاتا ـ بوۋىلىرىمىزنىڭ (زامانىدا) بۇنداق نەرسىلەرنىڭ بارلىقىنى ئاڭلىمىغان ئىدۇق» دېدى.');
INSERT INTO "saleh" VALUES(28,37,'28:37',' مۇسا ئېيتتى: «پەرۋەردىگارىم ئۆزىنىڭ دەرگاھىدىن كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، ئاخىرەتنىڭ ياخشى ئاقىۋىتىگە كىمنىڭ ئىگە بولىدىغانلىقىنى ئوبدان بىلىدۇ، زالىملار (يەنى ئاللاھقا يالغاننى چاپلىغۇچى فاجىرلار) چوقۇم مۇۋەپپەقىيەت قازىنالمايدۇ».');
INSERT INTO "saleh" VALUES(28,38,'28:38','ۋن ئېيتتى: «ئى كاتتىلار! مەن ئۆزۈمدىن باشقا يەنە بىر ئىلاھنىڭ بارلىقىنى بىلمەيمەن. ئى ھامان، مېنىڭ ئۈچۈن پىششىق خىش پىشۇرۇپ ئېگىز بىر بىنا سالغىن، مەن مۇسانىڭ ئىلاھىنى كۆرۈشۈم مۇمكىن، مەن ھەقىقەتەن ئۇنى (ئاسماندا بىر پەرۋەردىگار بار دېگەن دەۋاسىدا) يالغانچىلاردىن دەپ گۇمان قىلىمەن».');
INSERT INTO "saleh" VALUES(28,39,'28:39',' پىرئەۋن ۋە ئۇنىڭ قوشۇنى (مىسىر) زېمىنىدا زۇلۇم قىلىپ (ئىمان ئېيتىشقا) بويۇنتاۋلىق قىلدى. ئۇلار ئۆزلىرىنى بىز تەرەپكە قايتۇرۇلمايمىز (يەنى قىيامەت ۋە قايتا تىرىلىش يوق، ھېساب ئېلىنىشمۇ، جازاغا تارتىلىشمۇ يوق) دەپ گۇمان قىلدى.');
INSERT INTO "saleh" VALUES(28,40,'28:40','بىز پىرئەۋننى ۋە ئۇنىڭ قوشۇنلىرىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىۋەتتۇق (يەنى ئۇلارنى دېڭىزدا غەرق قىلىۋەتتۇق، ئۇلاردىن بىر ئادەممۇ ساق قالغىنى يوق). (ئى مۇھەممەد!) زالىملارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا (ئىبرەت كۆزى بىلەن) قارىغىن.');
INSERT INTO "saleh" VALUES(28,41,'28:41',' ئۇلارنى بىز دوزاخقا ئۈندەيدىغان پېشۋالار قىلدۇق (يەنى ئۇلارنى بىز دۇنيادا گۇمراھلار ئەگىشىدىغان كۇفرىنىڭ باشلىقلىرى قىلدۇق)، قىيامەت كۈنى ئۇلار ياردەمگە ئېرىشەلمەيدۇ (يەنى قىيامەت كۈنى ئۇلاردىن ئازابنى دەپئى قىلىدىغان مەدەتكار بولمايدۇ).');
INSERT INTO "saleh" VALUES(28,42,'28:42','ئۇلارغا بۇ دۇنيادا لەنەتنى ئەگەشتۈردۇق (يەنى ئۇلارغا بۇ دۇنيادا ئاللاھ، پەرىشتىلەر ۋە مۆمىنلەر لەنەت ئوقۇيدۇ)، قىيامەت كۈنى ئۇلار ئاللاھنىڭ رەھمىتىدىن يىراق قىلىنغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(28,43,'28:43','شۈبھىسىزكى، بىز (نۇھنىڭ قەۋمى ئاد، سەمۇد ۋە لۇتنىڭ قەۋمى قاتارلىق) ئىلگىرىكى ئۈممەتلەرنى ھالاك قىلغاندىن كېيىن، كىشىلەر (يەنى بەنى ئىسرائىل) نىڭ دىللىرىغا (ھەقىقەتنى كۆرىدىغان) نۇر بېرىپ، ئۇلارغا ھىدايەت ۋە رەھمەت قىلدۇق، ئۇلار ۋەز ـ نەسىھەت ئالسۇن دەپ، مۇساغا كىتابنى (يەنى تەۋراتنى) بەردۇق.');
INSERT INTO "saleh" VALUES(28,44,'28:44',' (ئى مۇھەممەد!) بىز غەربىي تاغنىڭ بىر تەرىپىدە مۇساغا ۋەھىي نازىل قىلغىنىمىزدا سەن يوق ئىدىڭ، (بۇنى) ئۆز كۆزۈڭ بىلەنمۇ كۆرمىگەن ئىدىڭ.');
INSERT INTO "saleh" VALUES(28,45,'28:45',' لېكىن بىز نۇرغۇن ئۈممەتلەرنى ياراتتۇق، ئۇلارنىڭ ئۆمرى ئۇزۇن بولدى، سەن مەديەن ئاھالىسىنىڭ ئىچىدە ئۇلارغا بىزنىڭ ئايەتلىرىمىزنى تىلاۋەت قىلىپ تۇرغۇچى بولغىنىڭ يوق، لېكىن بىز (سېنى ئۇلاردىن باشقا بىر قەۋمگە پەيغەمبەر قىلىپ) ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(28,46,'28:46',' بىز تۇر تېغىنىڭ بىر تەرىپىدە (مۇساغا) سۆز قىلغىنىمىزدا سەن يوق ئىدىڭ، لېكىن بۇ سەندىن ئىلگىرى ھېچبىر ئاگاھلاندۇرغۇچى كەلمىگەن قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ئۇلارنىڭ ۋەز ـ نەسىھەت ئېلىشى ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان رەھمەتتۇر.');
INSERT INTO "saleh" VALUES(28,47,'28:47',' ئۇلارنىڭ قىلغان ئىشلىرى (يەنى كۇفرى ۋە گۇناھلىرى) تۈپەيلىدىن، ئۇلارغا بىرەر ئازاب كەلسە، «پەرۋەردىگارىمىز! بىزگە بىرەر پەيغەمبەر ئەۋەتكەن بولساڭ ئىدىڭ، سېنىڭ ئايەتلىرىڭگە ئەگىشەتتۇق ۋە مۆمىنلەردىن بولاتتۇق» دېمەسلىكلىرى ئۈچۈن (سېنى ئۇلارغا پەيغەمبەر قىلىپ ئەۋەتتۇق).');
INSERT INTO "saleh" VALUES(28,48,'28:48',' ئۇلارغا بىز ۋەھىي قىلغان ھەقىقەت نازىل بولغان چاغدا، ئۇلار: «ئۇنىڭغا (يەنى مۇھەممەد ئەلەيھىسسالامغا) نېمىشقا مۇساغا بېرىلگەندەك مۆجىزە بېرىلمىدى؟» دېدى. ئۇلار ئىلگىرى مۇساغا بېرىلگەن مۆجىزىنى ئىنكار قىلمىدىمۇ؟ ئۇلار: «(تەۋرات بىلەن قۇرئان) بىر ـ بىرىنى تەستىق قىلىدىغان سېھىردۇر» دېدى. ئۇلار: «بىز ھەقىقەتەن (ئىككى كىتابنىڭ) ھەربىرىنى ئىنكار قىلغۇچىمىز» دېدى.');
INSERT INTO "saleh" VALUES(28,49,'28:49','ئېيتقىنكى، «ئەگەر (ئۇ ئىككى كىتاب سېھىردۇر دېگەن سۆزۈڭلاردا) راستچىل بولىدىغان بولساڭلار، ئۇ ئىككى كىتابقا قارىغاندا، ئاللاھ تەرىپىدىن نازىل بولغان تېخىمۇ توغرا بىر كىتاب كەلتۈرۈپ بېقىڭلار، مەن ئۇنىڭغا ئەگىشەي».');
INSERT INTO "saleh" VALUES(28,50,'28:50',' ئەگەر ئۇلار ساڭا جاۋاپ بەرمىسە، بىلگىنكى، ئۇلار پەقەت ئۆزلىرىنىڭ نەپسى خاھىشىغىلا ئەگىشىدۇ. ئاللاھ ۋەھىي قىلغان توغرا يولنى قويۇپ نەپسى خاھىشىغا ئەگەشكەن كىشىدىنمۇ ئازغۇن ئادەم بارمۇ؟ ئاللاھ ھەقىقەتەن زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(28,51,'28:51',' ۋەز ـ نەسىھەت ئالسۇن دەپ، شۈبھىسىزكى، ئۇلارغا قۇرئاننى ئۈزۈلدۈرمەي چۈشۈردۇق.');
INSERT INTO "saleh" VALUES(28,52,'28:52','قۇرئاندىن ئىلگىرى بىز كىتاب (يەنى ئىنجىل، تەۋرات) نازىل قىلغانلار (يەنى ناسارا ۋە يەھۇدىيلارنىڭ مۆمىنلىرى) قۇرئانغا ئىشىنىدۇ.');
INSERT INTO "saleh" VALUES(28,53,'28:53',' ئۇلارغا قۇرئان تىلاۋەت قىلىنغان چاغدا، ئۇلار: «بىز قۇرئانغا ئىشەندۇق. شۈبھىسىزكى، ئۇ پەرۋەردىگارىمىز تەرىپىدىن نازىل بولغان ھەقىقەتتۇر. بىز ھەقىقەتەن بۇنىڭدىن بۇرۇن مۇسۇلمان ئىدۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(28,54,'28:54',' ئۇلارنىڭ قىلغان سەۋر ـ تاقىتى ئۈچۈن ئۇلارغا قوش ساۋاب بېرىلىدۇ، ئۇلار ياخشىلىق ئارقىلىق يامانلىقنى دەپئى قىلىدۇ، بىز ئۇلارغا رىزىق قىلىپ بەرگەن نەرسىلەردىن ياخشىلىق يوللىرىغا سەرپ قىلىدۇ. ');
INSERT INTO "saleh" VALUES(28,55,'28:55',' ئۇلار بىھۇدە سۆزلەرنى ئاڭلىغاندا ئۇنىڭدىن يۈز ئۆرۈپ (يەنى قۇلاق سالماي): «بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈن، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزۈڭلار ئۈچۈن، سىلەرگە ئامانلىق بولسۇن، بىز نادانلاردىن دوستلۇق تىلىمەيمىز» دەيدۇ.');
INSERT INTO "saleh" VALUES(28,56,'28:56',' شۈبھىسىزكى، سەن خالىغان ئادىمىڭنى ھىدايەت قىلالمايسەن، لېكىن ئاللاھ ئۆزى خالىغان ئادەمنى ھىدايەت قىلىدۇ، ئاللاھ ھىدايەت تاپقۇچىلارنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(28,57,'28:57',' ئۇلار: «ئەگەر بىز سەن بىلەن توغرا يولغا ئەگىشىدىغان بولساق، زېمىنىمىزدىن ھەيدەپ چىقىرىلىمىز» دېدى. ئۇلارنى بىز تىنچ ھەرەمگە يەرلەشتۈرمىدۇقمۇ؟ ھەرەمگە تۈرلۈك مېۋىلەرنىڭ ھەممىسى كەلتۈرۈلىدۇ، (بۇ) بىزنىڭ دەرگاھىمىزدىن چۈشۈرۈلگەن رىزىقتۇر، لېكىن ئۇلارنىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(28,58,'28:58',' بىز نۇرغۇن شەھەرنى ھالاك قىلدۇق، ئۇلارنىڭ ئاھالىسى كەيىپ ـ ساپالىق تۇرمۇش كەچۈرەتتى، بۇ ئۇلارنىڭ تۇرالغۇ جايلىرىدۇر، ئۇلار ھالاك بولغاندىن كېيىن (ئۇ جايلاردا) ئازغىنا ۋاقىتتىن تاشقىرى ئادەم تۇرغان ئەمەس، ئۇلار (نىڭ مال ـ مۈلكى ۋە يۇرتىغا) بىز ۋارىسلىق قىلدۇق.');
INSERT INTO "saleh" VALUES(28,59,'28:59',' پەرۋەردىگارىڭ شەھەرلەرنىڭ مەركىزىگە، ئۇلارنىڭ ئاھالىسىگە بىزنىڭ ئايەتلىرىمىزنى ئوقۇپ بېرىدىغان بىر پەيغەمبەر ئەۋەتمىگۈچە شەھەرلەرنى ھالاك قىلغۇچى بولمىدۇق، شەھەرلەرنىڭ ئاھالىسى زالىم بولمىغىچە شەھەرلەرنى ھالاك قىلغۇچىمۇ بولمىدۇق.');
INSERT INTO "saleh" VALUES(28,60,'28:60',' سىلەرگە قانداق نەرسە بېرىلمىسۇن، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىدۇر ۋە دۇنيا تىرىكچىلىكىنىڭ زىننىتىدۇر، ئاللاھنىڭ دەرگاھىدىكى ساۋاب ئەڭ ياخشىدۇر، ئەڭ باقىيدۇر، چۈشەنمەمسىلەر؟');
INSERT INTO "saleh" VALUES(28,61,'28:61',' بىز ياخشى ۋەدىنى قىلغان (يەنى جەننەتنى ۋەدە قىلغان) ئادەم ئۇنى تاپىدۇ، (ئۇ) دۇنيا تىرىكچىلىكىدىكى پايدىلىنىدىغان نەرسىلەر بىلەن بىز پايدىلاندۇرغان، ئاندىن قىيامەت كۈنى ئازابقا دۇچار بولغان ئادەم بىلەن ئوخشاشمۇ؟');
INSERT INTO "saleh" VALUES(28,62,'28:62','شۇ كۈنى ئاللاھ ئۇلارغا: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟ » دەپ نىدا قىلىدۇ.');
INSERT INTO "saleh" VALUES(28,63,'28:63','ئۆزلىرىگە ئازاب تېگىشلىك بولغانلار (يەنى مۇشرىكلارنىڭ چوڭلىرى): «پەرۋەردىگارىمىز! بىز (سېنىڭ يولۇڭدىن) ئازدۇرغانلارنى (يەنى ئەگەشكۈچىلىرىمىزنى) ئۆزىمىز ئازغاندەك ئازدۇردۇق. (پەرۋەردىگارىمىز!) ئۇلاردىن يىراقلىشىپ ساڭا يېقىنلاشتۇق، ئۇلار بىزگە ئىبادەت قىلمايتتى (پەقەت نەپسى خاھىشلىرىغا ئىبادەت قىلاتتى)» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(28,64,'28:64','ئۇلارغا: «شېرىك كەلتۈرگەن مەبۇدلىرىڭلارنى چاقىرىڭلار» دېيىلگەن ھامان، ئۇلار شېرىك كەلتۈرگەن مەبۇدلىرىنى چاقىرىدۇ، ئۇلاردىن جاۋاب كەلمەيدۇ، ئۇلار ئازابنى كۆرگەن چاغدا، (كاپىر بولغانلىقلىرىغا نادامەت قىلىپ) (دۇنيادىكى ۋاقتىدا) ھىدايەت تاپقان بولۇشلىرىنى ئارزۇ قىلىدۇ.');
INSERT INTO "saleh" VALUES(28,65,'28:65','ئۇ كۈندە ئاللاھ ئۇلارغا نىدا قىلىپ ئېيتىدۇ: «پەيغەمبەرلەرگە نېمە بىلەن جاۋاپ بەردىڭلار؟».');
INSERT INTO "saleh" VALUES(28,66,'28:66',' ئۇ كۈندە ئۇلارغا بارلىق خەۋەرلەر قاراڭغۇ بولىدۇ، ئۇلار ئۆز ئارا سوراشمايدۇ.');
INSERT INTO "saleh" VALUES(28,67,'28:67',' تەۋبە قىلغان، ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغان ئادەمگە كەلسەك، ئۇنىڭ مەقسەتكە ئېرىشكۈچىلەردىن بولۇشى ئۈمىدلىكتۇر.');
INSERT INTO "saleh" VALUES(28,68,'28:68','ئاللاھ (مەخلۇقاتىدىن) خالىغىنىنى يارىتىدۇ، خالىغان ئادەمنى (پەيغەمبەرلىككە) تاللايدۇ، تاللاش ھوقۇقى ئۇلاردا ئەمەس. ئاللاھ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەنلىرىدىن ئۈستۈندۇر.');
INSERT INTO "saleh" VALUES(28,69,'28:69','پەرۋەردىگارىڭ ئۇلارنىڭ دىللىرىدا يوشۇرۇن تۇتقانلىرىنى ۋە (ئېغىزلىرىدا) دېگەنلىرىنى بىلىپ تۇرىدۇ. ');
INSERT INTO "saleh" VALUES(28,70,'28:70',' ئۇ ئاللاھتۇر، ئۇنىڭدىن بۆلەك ھېچ مەبۇد (بەرھەق) يوقتۇر، دۇنيا ۋە ئاخىرەتتە جىمى ھەمدۇسانا ئاللاھقا مەنسۇپتۇر، ھۆكۈم چىقىرىش ئاللاھقا مەنسۇپتۇر، سىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(28,71,'28:71','«ئېيتىپ بېقىڭلارچۇ! ئەگەر ئاللاھ كېچىنى سىلەر ئۈچۈن قىيامەت كۈنىگۈچە سوزىدىغان بولسا، ئاللاھتىن بۆلەك قايسى ئىلاھ سىلەرگە يورۇقلۇق ئېلىپ كېلەلەيدۇ؟ سىلەر ئاڭلىمامسىلەر؟» دېگىن.');
INSERT INTO "saleh" VALUES(28,72,'28:72','«ئېيتىپ بېقىڭلارچۇ؟ ئەگەر ئاللاھ كۈندۈزنى سىلەر ئۈچۈن قىيامەت كۈنىگۈچە سوزىدىغان بولسا، ئاللاھتىن بۆلەك قايسى ئىلاھ سىلەرگە ئارام ئېلىشىڭلار ئۈچۈن كېچىنى ئېلىپ كېلەلەيدۇ؟ سىلەر كۆرمەمسىلەر؟» دېگىن.');
INSERT INTO "saleh" VALUES(28,73,'28:73','سىلەرنى كېچىدە ئارام ئالسۇن، كۈندۈزدە (ھاياتلىق يولىدا ھەرىكەتلىنىپ) ئاللاھنىڭ پەزلىنى تەلەپ قىلسۇن ۋە (ئاللاھنىڭ نېمەتلىرىگە) شۈكۈر قىلسۇن دەپ، سىلەر ئۈچۈن كېچە بىلەن كۈندۈزنى يارىتىشى ئاللاھنىڭ رەھمىتىدىندۇر.');
INSERT INTO "saleh" VALUES(28,74,'28:74',' ئۇ كۈندە ئاللاھ ئۇلارغا نىدا قىلىپ: «سىلەر گۇمان قىلغان مېنىڭ شېرىكلىرىم قەيەردە؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(28,75,'28:75',' بىز ھەر ئۈممەتتىن (ئۇلارنىڭ ئەمەللىرىگە گۇۋاھلىق بېرىدىغان) بىر گۇۋاھچى (يەنى پەيغەمبەر) نى چىقىرىمىز، پاكىتىڭلارنى كەلتۈرۈڭلار دەيمىز. ئاندىن ئۇلار ھەقىقەتنىڭ ئاللاھقا مەنسۇپ ئىكەنلىكىنى بىلىدۇ، ئۇلارنىڭ ئويدۇرۇپ چىققان نەرسىلىرى (يەنى مەبۇدلىرى) ئۆزلىرىنى ئۇلاردىن چەتكە ئالىدۇ.');
INSERT INTO "saleh" VALUES(28,76,'28:76',' قارۇن ھەقىقەتەن مۇسانىڭ قەۋمىدىن ئىدى. قارۇن ئۇلارغا يوغانلىق قىلدى، قارۇنغا خەزىنىلەردىن شۇ قەدەر كۆپ بەرگەن ئىدۇقكى، ئۇلارنىڭ ئاچقۇچلىرىنى (كۆتۈرۈش) كۈچلۈك بىر جامائەگىمۇ ھەقىقەتەن ئېغىرلىق قىلاتتى، ئەينى ۋاقىتتا قارۇنغا قەۋمى ئېيتتى: «كۆرەڭلەپ كەتمە، ئاللاھ ھەقىقەتەن كۆرەڭلەپ كەتكۈچىلەرنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(28,77,'28:77',' ئاللاھ ساڭا بەرگەن بايلىق بىلەن ئاخىرەت يۇرتىنى تىلىگىن، دۇنيادىكى نېسىۋەڭنىمۇ ئۇنتۇمىغىن، ئاللاھ ساڭا ياخشىلىق قىلغاندەك، سەنمۇ (ئاللاھنىڭ بەندىلىرىگە) ياخشىلىق قىلغىن، يەر يۈزىدە بۇزغۇنچىلىقنى تىلىمىگىن، ئاللاھ ھەقىقەتەن بۇزغۇنچىلىق قىلغۇچىلارنى دوست تۇتمايدۇ».');
INSERT INTO "saleh" VALUES(28,78,'28:78',' قارۇن: «مېنىڭ ئالاھىدە بىلىمىم بولغانلىقتىن بۇ بايلىققا ئېرىشتىم» دېدى. قارۇندىن ئىلگىرى ئۆتكەن ئۈممەتلەردىن ئۇنىڭغا قارىغاندا تېخىمۇ كۈچلۈك، توپلىغان (مېلى) تېخىمۇ كۆپ بولغان كىشىلەرنى ئاللاھنىڭ ھالاك قىلغانلىقىنى ئۇ بىلمىدىمۇ؟ گۇناھكارلارنىڭ گۇناھلىرىنىڭ سورىلىشى (ئاللاھ بۇنى بىلگەنلىكى ئۈچۈن) ھاجەتسىزدۇر. ');
INSERT INTO "saleh" VALUES(28,79,'28:79',' قارۇن ئۆز قەۋمىنىڭ ئالدىغا بارلىق زىننىتى بىلەن ھەشەمەتلىك ھالدا چىقتى. دۇنيا تىرىكچىلىكىنى كۆزلەيدىغانلار: «كاشكى بىزگە قارۇنغا بېرىلگەندەك بايلىق بېرىلسىچۇ! ئۇ ھەقىقەتەن (دۇنيالىقتىن) چوڭ نېسىۋىگە ئىگە ئىكەن» دېدى.');
INSERT INTO "saleh" VALUES(28,80,'28:80','ئىلىملىك كىشىلەر: «ۋاي سىلەرگە! ئىمان ئېيتقان ۋە ياخشى ئەمەلنى قىلغان كىشىگە ئاللاھنىڭ ساۋابى ياخشىدۇر. ئۇ ساۋاب پەقەت سەۋر قىلغۇچىلارغىلا بېرىلىدۇ» دېدى.');
INSERT INTO "saleh" VALUES(28,81,'28:81',' قارۇننى ئۇنىڭ ئۆيى بىلەن قوشۇپ يەرگە يۇتقۇزدۇق، ئۇنىڭدىن ئاللاھنىڭ ئازابىنى دەپئى قىلىدىغان بىر جامائە بولمىدى. قارۇن ئۆزىنى قوغدىيالمىدى. ');
INSERT INTO "saleh" VALUES(28,82,'28:82',' تۈنۈگۈن تېخى قارۇننىڭ دەرىجىسىنى ئارزۇ قىلغانلار دېيىشتىكى، «پاھ، كۆردۈڭمۇ؟ ئاللاھ بەندىلىرىدىن خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدىكەن، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدىكەن، ئاللاھ بىزگە مەرھەمەت قىلمىغان (يەنى ئارزۇ قىلغىنىمىزنى بەرگەن) بولسا ئىدى، بىزنى ئەلۋەتتە يەر يۇتاتتى، ۋاي كۆردۈڭمۇ؟ كاپىرلار نىجات تاپمايدۇ».');
INSERT INTO "saleh" VALUES(28,83,'28:83',' ئەنە شۇ ئاخىرەت يۇرتىنى يەر يۈزىدە چوڭچىلىق قىلىشنى ۋە بۇزغۇنچىلىق قىلىشنى كۆزلىمەيدىغانلارغا خاس قىلدۇق، (ياخشى) ئاقىۋەت تەقۋادارلارغا مەنسۇپتۇر.');
INSERT INTO "saleh" VALUES(28,84,'28:84',' كىمكى ياخشى ئىش قىلغان ئىكەن، ئۇ قىلغان ئىشلىرىدىنمۇ ياخشى مۇكاپاتقا (يەنى قىلغان ياخشىلىقىدىن نەچچە ھەسسە ئارتۇق ساۋابقا) ئېرىشىدۇ، كىمكى يامان ئىش قىلىدىكەن، يامان ئىش قىلغانلارغا پەقەت قىلغان ئىشلىرىغا يارىشا جازا بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(28,85,'28:85',' ساڭا قۇرئاننى (يەنى ئۇنى تەبلىغ قىلىشنى ۋە ئۇنىڭ ئەھكامىغا ئەمەل قىلىشنى) پەرز قىلغان زات سېنى ئەلۋەتتە قايتىدىغان يەرگە (يەنى مەككىگە) قايتۇرىدۇ، ئېيتقىنكى، «كىمنىڭ ھىدايەت ئېلىپ كەلگەنلىكىنى، كىمنىڭ ئوپئوچۇق گۇمراھلىقتا ئىكەنلىكىنى پەرۋەردىگارىم ھەممىدىن ئوبدان بىلىدۇ».');
INSERT INTO "saleh" VALUES(28,86,'28:86','سەن (پەيغەمبەر بولۇشتىن ئىلگىرى) قۇرئاننىڭ ساڭا نازىل قىلىنىشىنى ئۈمىد قىلمىغان ئىدىڭ، پەقەت پەرۋەردىگارىڭ رەھمەت قىلىپ (ساڭا ئۇنى نازىل قىلدى)، كاپىرلارغا ياردەمچى بولمىغىن.');
INSERT INTO "saleh" VALUES(28,87,'28:87',' ساڭا ئاللاھنىڭ ئايەتلىرى نازىل قىلىنغاندىن كېيىن، ئۇ ئايەتلەرگە (ئەمەل قىلىش) تىن كاپىرلار سېنى توسمىسۇن، پەرۋەردىگارىڭغا (يەنى ئۇنى بىردەپ بىلىشكە ۋە ئۇنىڭغا ئىبادەت قىلىشقا) دەۋەت قىلغىن، ھەرگىزمۇ مۇشرىكلاردىن بولمىغىن (يەنى ئۇلارنىڭ نەپسى خاھىشلىرىغا ماسلاشمىغىن).');
INSERT INTO "saleh" VALUES(28,88,'28:88','ئاللاھقا قوشۇپ باشقا بىر ئىلاھقا ئىبادەت قىلما، ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر، ئاللاھنىڭ زاتىدىن باشقا بارلىق نەرسە يوقىلىدۇ، (مەخلۇقاتتا ئىجرا بولىدىغان) ھۆكۈم پەقەت ئاللاھقا خاستۇر، سىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(29,1,'29:1','ئەلىف، لام، مىم. ');
INSERT INTO "saleh" VALUES(29,2,'29:2',' ئىنسانلار «ئىمان ئېيتتۇق» دەپ قويۇش بىلەنلا سىنالماي تەرك ئېتىلىمىز، دەپ ئويلامدۇ؟');
INSERT INTO "saleh" VALUES(29,3,'29:3',' ئۇلاردىن بۇرۇن ئۆتكەنلەرنى بىز ھەقىقەتەن سىنىدۇق، ئاللاھ (ئىمانىدا) راستچىللارنى چوقۇم بىلىدۇ، (ئىمانىدا) يالغانچىلارنىمۇ چوقۇم بىلىدۇ.');
INSERT INTO "saleh" VALUES(29,4,'29:4','يامان ئىش قىلىدىغانلار بىزدىن (يەنى ئازابىمىزدىن) قېچىپ قۇتۇلالايمىز دەپ ئويلامدۇ؟ ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(29,5,'29:5','كىمكى ئاللاھقا مۇلاقات بولۇشنى ئۈمىد قىلىدىكەن، (بىلسۇنكى) ئاللاھنىڭ (بۇنىڭغا بەلگىلىگەن) ۋاقتى چوقۇم يېتىپ كېلىدۇ، ئاللاھ (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (بەندىلىرىنىڭ تاشقى ۋە ئىچكى ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(29,6,'29:6',' كىمكى جىھاد قىلىدىكەن، ئۇنىڭ قىلغان جىھادى ئۆزىنىڭ پايدىسى ئۈچۈندۇر، ئاللاھ ھەقىقەتەن تامامى ئەھلى جاھاندىن بىھاجەتتۇر.');
INSERT INTO "saleh" VALUES(29,7,'29:7',' شۈبھىسىزكى، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ گۇناھلىرىنى يوققا چىقىرىمىز، ئۇلارنى ئەلۋەتتە قىلغان ئەمەللىرىنىڭ ئەڭ ياخشىسى بىلەن مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(29,8,'29:8',' ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق، ئەگەر ئاتا ـ ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، سىلەر مېنىڭ دەرگاھىمغا قايتىپ بارىسىلەر، سىلەرگە قىلمىشلىرىڭلارنى خەۋەر قىلىمەن.');
INSERT INTO "saleh" VALUES(29,9,'29:9',' ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارنى چوقۇم ياخشىلار قاتارىغا كىرگۈزىمىز.');
INSERT INTO "saleh" VALUES(29,10,'29:10','بەزى كىشىلەر: «ئاللاھقا ئىمان ئېيتتۇق» دەيدۇ، ئاللاھنىڭ يولىدا بىرەر ئەزىيەت تارتسا، كىشىلەر تەرىپىدىن يەتكەن كۈلپەتنى ئاللاھنىڭ ئازابى بىلەن ئوخشاش بىلىدۇ، ئەگەر پەرۋەردىگارىڭدىن مەدەت كەلسە: «بىز ھەقىقەتەن سىلەر بىلەن بىللە ئىدۇق» دەيدۇ. ئاللاھ جاھان ئەھلىنىڭ دىللىرىدىكىنى ھەممىدىن ئوبدان بىلمەمدۇ؟');
INSERT INTO "saleh" VALUES(29,11,'29:11',' ئاللاھ مۆمىنلەرنى چوقۇم بىلىدۇ، مۇناپىقلارنىمۇ چوقۇم بىلىدۇ.');
INSERT INTO "saleh" VALUES(29,12,'29:12','كاپىرلار مۆمىنلەرگە: «بىزنىڭ يولىمىز (يەنى دىنىمىز) غا ئەگىشىڭلار، سىلەرنىڭ گۇناھىڭلارنى بىز ئۈستىمىزگە ئالىمىز» دېدى. كاپىرلار مۆمىنلەرنىڭ گۇناھلىرىدىن ئازراقمۇ ئۈستىگە ئالالمايدۇ، ئۇلار ھەقىقەتەن يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(29,13,'29:13',' ئۇلار ئۆزلىرىنىڭ يۈكلىرىنى ۋە ئۇنىڭغا قوشۇپ باشقا يۈكلەرنى (يەنى ئۆزلىرىنىڭ گۇناھلىرىنى ۋە ئازدۇرغانلىرىنىڭ گۇناھلىرىنى) ئۈستىگە ئالىدۇ، قىيامەت كۈنى ئۇلار ئۆزلىرى ئويدۇرۇپ چىققان يالغان سۆزلىرى ئۈچۈن سوراققا تارتىلىدۇ.');
INSERT INTO "saleh" VALUES(29,14,'29:14',' بىز ھەقىقەتەن نۇھنى قەۋمىگە (پەيغەمبەر قىلىپ) ئەۋەتتۇق، نۇھ (قەۋمىنى تەۋھىدكە دەۋەت قىلىپ) ئۇلارنىڭ ئىچىدە 50 يىل كەم 1000 يىل تۇردى (نۇھ ئەلەيھىسسالامنىڭ قەۋمى بۇتپەرەس بولۇپ، ئۇنىڭ پەيغەمبەرلىكىگە چىنپۈتمىدى). ئۇلار زالىم بولغانلىقلىرى (يەنى كۇفرىدا ۋە گۇمراھلىقتا چىڭ تۇرۇپ ئۆزلىرىگە زۇلۇم قىلغانلىقلىرى) ئۈچۈن، ئاللاھ ئۇلارنى توپان (بالاسى) بىلەن ھالاك قىلدى.');
INSERT INTO "saleh" VALUES(29,15,'29:15',' بىز نۇھنى ۋە كېمىدىكىلەرنى (غەرق بولۇپ كېتىشتىن) قۇتقۇزدۇق، توپاننى پۈتۈن ئەھلى جاھان ئۈچۈن ئىبرەت قىلدۇق.');
INSERT INTO "saleh" VALUES(29,16,'29:16',' ئىبراھىمنىمۇ (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئۆز ۋاقتىدا ئۇ قەۋمىگە ئېيتتى: «بىر ئاللاھقا ئىبادەت قىلىڭلار ۋە ئۇنىڭدىن قورقۇڭلار، ئەگەر بىلسەڭلار مۇنداق قىلىش سىلەر ئۈچۈن ياخشىدۇر.');
INSERT INTO "saleh" VALUES(29,17,'29:17','ئاللاھنى قويۇپ بۇتلارغىلا ئىبادەت قىلىسىلەر (بۇتلارنى ئۆز قولۇڭلار بىلەن ياسىۋالغانسىلەر، بۇتلارنىڭ قولىدىن پايدا ـ زىيان كەلمەيدۇ) ۋە يالغاننىلا ئويدۇرىسىلەر، سىلەرنىڭ ئاللاھنى قويۇپ چوقۇنۇۋاتقىنىڭلار (يەنى بۇتلار) سىلەرگە رىزىق بېرىشكە قادىر ئەمەس، رىزىقنى ئاللاھنىڭ دەرگاھىدىن تەلەپ قىلىڭلار (رىزىق بېرىشكە پەقەت ئاللاھنىڭ ئۆزى قادىردۇر)، ئاللاھقا ئىبادەت قىلىڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(29,18,'29:18',' سىلەر (مېنى) ئىنكار قىلساڭلار (ئىنكار قىلغانلىقىڭلار بىلەن ماڭا قىلچە زىيان يەتكۈزەلمەيسىلەر، پەقەت ئۆزۈڭلارغا زىيان يەتكۈزىسىلەر)، سىلەردىن ئىلگىرىكى نۇرغۇن ئۈممەتلەرمۇ (پەيغەمبەرلىرىنى) ئىنكار قىلغان (شۇنىڭ بىلەن ئۇلارغا ئاللاھنىڭ ئازابى نازىل بولغان، سىلەرگىمۇ شۇنداق ئازاب نازىل بولىدۇ)، پەيغەمبەرنىڭ مەسئۇلىيىتى پەقەت (ئاللاھنىڭ ئەمىرلىرىنى) چۈشىنىشلىك قىلىپ يەتكۈزۈشتۇر».');
INSERT INTO "saleh" VALUES(29,19,'29:19',' ئۇلار (يەنى ئىنكار قىلغۇچىلار) ئاللاھنىڭ مەخلۇقاتىنى دەسلەپتە يوقتىن قانداق بارلىققا كەلتۈرگەنلىكىنى، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدىغانلىقىنى كۆرمىدىمۇ؟ بۇ ئاللاھقا ھەقىقەتەن ئاساندۇر (ئۇلار بۇنى قانداقمۇ ئىنكار ق ىلىدۇ، دەسلەپتە يارىتىشقا قادىر بولغان زات قايتا ئەسلىگە كەلتۈرۈشكە ئەلۋەتتە قادىردۇر).');
INSERT INTO "saleh" VALUES(29,20,'29:20','ئېيتقىنكى، «زېمىندا سەير قىلىڭلار، ئاللاھنىڭ مەخلۇقاتىنى قانداق قىلىپ (يوقتىن) بار قىلغانلىقىغا قاراڭلار، ئاندىن ئاللاھ ئۇلارنى (قىيامەتتە) قايتا پەيدا قىلىدۇ، ئاللاھ ھەقىقەتەن ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(29,21,'29:21',' ئاللاھ خالىغان ئادەمنى جازالايدۇ، خالىغان ئادەمگە رەھمەت قىلىدۇ، (قىيامەتتە) ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر. ');
INSERT INTO "saleh" VALUES(29,22,'29:22',' سىلەر مەيلى زېمىندا بولسۇن، مەيلى ئاسماندا بولسۇن، ئاللاھ (نىڭ ئازابى) دىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە ئاللاھتىن بۆلەك دوستمۇ يوق، مەدەتكارمۇ يوق».');
INSERT INTO "saleh" VALUES(29,23,'29:23',' ئاللاھنىڭ ئايەتلىرىنى ۋە ئۇنىڭغا مۇلاقات بولۇشنى (يەنى ئاخىرەتنى) ئىنكار قىلغانلار -ئەنە شۇلار مېنىڭ رەھمىتىمدىن نائۈمىد بولدى. ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(29,24,'29:24',' ئىبراھىمنىڭ قەۋمىنىڭ جاۋابى: «ئىبراھىمنى ئۆلتۈرۈڭلار ياكى كۆيدۈرۈڭلار!» دېيىشتىن ئىبارەت بولدى. ئاللاھ ئۇنى ئوتتىن قۇتۇلدۇردى (ئۇلار ئىبراھىم ئەلەيھىسسالامنى ئوتقا تاشلىغاندا، ئاللاھ ئۇنىڭغا ئوتنى سۈرۈن ۋە ئامان جاي قىلىپ بەردى). ھەقىقەتەن بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(29,25,'29:25',' ئىبراھىم ئېيتتى: «بۇ دۇنيادا (بۇتلارغا چوقۇنۇش مەقسىتىدە يىغىلىشىڭلارنىڭ) ئاراڭلاردىكى دوستلۇققا سەۋەب بولۇشى ئۈچۈن، ئاللاھنى قويۇپ بۇتلارنى مەبۇد قىلىۋالدىڭلار، ئاندىن قىيامەت كۈنى بەزىڭلارنى بەزىڭلار ئىنكار قىلىسىلەر، بەزىڭلارغا بەزىڭلار لەنەت ئوقۇيسىلەر (يەنى قىيامەت كۈنى مەزكۇر دوستلۇق دۈشمەنلىككە ئايلىنىدۇ، ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەردىن ئادا ـ جۇدا بولىدۇ، ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە لەنەت ئوقۇيدۇ، چۈنكى ئۇلارنىڭ دۇنيادىكى دوستلۇقى ئاللاھ ئۈچۈن بولغان ئەمەس)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرنى (دوزاختىن قۇتۇلدۇرىدىغان) ھېچقانداق ياردەمچى بولمايدۇ».');
INSERT INTO "saleh" VALUES(29,26,'29:26',' لۇت ئىبراھىمغا ئىمان ئېيتتى. ئىبراھىم ئېيتتى: «مەن چوقۇم پەرۋەردىگارىم تەرىپىگە ھىجرەت قىلىمەن (يەنى ئاللاھنىڭ رازىلىقىنى ئىزدەش يۈزىسىدىن ۋەتىنىمنى تەرك ئېتىپ، ئاللاھ ئەمر قىلغان جايغا ھىجرەت قىلىپ بارىمەن). ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر».');
INSERT INTO "saleh" VALUES(29,27,'29:27',' ئىبراھىمغا ئىسھاقنى، يەئقۇبنى ئاتا قىلدۇق (يەنى ئىبراھىم ئەلەيھىسسالام خۇدالىق ئۈچۈن قەۋمىدىن ئايرىلغاندىن كېيىن، ياخشى پەرزەنت ئىسھاقنى بەردۇق ۋە ئىسھاقنىڭ ئوغلى بولغان نەۋرىسى يەئقۇبنى بەردۇق)، (ئۇنىڭدىن كېيىن) پەيغەمبەرلىكنى، كىتابنى (يەنى ساماۋى كىتابلارنى) ئۇنىڭ ئەۋلادىغا خاس قىلدۇق، دۇنيادىكى مۇكاپاتنى ئۇنىڭغا ئاتا قىلدۇق (يەنى جىمى دىنلاردا ئۇنى ياخشى نامغا ئىگە قىلدۇق)، ئۇ ھەقىقەتەن ئاخىرەتتە ياخشى بەندىلەردىندۇر.');
INSERT INTO "saleh" VALUES(29,28,'29:28',' لۇتنى (قەۋمىگە پەيغەمبەر قىلىپ ئەۋەتتۇق)، ئەينى ۋاقىتتا ئۇ قەۋمىگە ئېيتتى: «سىلەر ھەقىقەتەن قەبىھ ئىش قىلىۋاتىسىلەر، سىلەردىن ئىلگىرى جاھان ئەھلىدىن بىرىمۇ مۇنداق قەبىھ ئىشنى قىلغان ئەمەس.');
INSERT INTO "saleh" VALUES(29,29,'29:29','سىلەر ھەقىقەتەن لىۋاتە قىلامسىلەر؟ يوللارنى توسۇپ بۇلاڭچىلىق قىلامسىلەر؟ سورۇنلىرىڭلاردا ئوپئوچۇق يامان ئىشلارنى قىلىۋېرەمسىلەر؟» ئۇنىڭ قەۋمىنىڭ جاۋابى: «ئەگەر سەن راستچىللاردىن بولساڭ، بىزگە ئاللاھنىڭ ئازابىنى كەلتۈرگىن» دېيىشتىنلا ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(29,30,'29:30','لۇت: «پەرۋەردىگارىم! بۇزغۇنچى قەۋمگە قارشى ماڭا ياردەم بەرگىن (يەنى ئۇلارنى ھالاك قىلىپ ماڭا ياردەم بەرگىن، ئۇلار بۇزغۇنچى ئەخمەقلەر بولۇپ، ئۇلارنىڭ تۈزىلىشى ئۈمىد قىلىنمايدۇ، ئۇلار گۇمراھلىققا، بۇزۇقچىلىققا چۆمۈپ كەتكەن)» دېدى.');
INSERT INTO "saleh" VALUES(29,31,'29:31',' ئەلچىلىرىمىز (يەنى پەرىشتىلەر) ئىبراھىمغا (پەرزەنت بېرىلىشتىن ئىبارەت) خۇشخەۋەر ئېلىپ كەلگەن چاغدا، ئۇلار: «بىز بۇ شەھەر ئاھالىسىنى چوقۇم ھالاك قىلىمىز، ئۇنىڭ ئاھالىسى ھەقىقەتەن زالىم ئىدى» دېدى.');
INSERT INTO "saleh" VALUES(29,32,'29:32',' ئىبراھىم: «ئۇ شەھەردە لۇت بارغۇ (يەنى لۇتقا ئوخشاش بىر ياخشى پەيغەمبەر تۇرۇۋاتقان شەھەر ئاھالىسىنى قانداقمۇ ھالاك قىلىسىلەر؟)» دېدى. پەرىشتىلەر ئېيتتى: «بۇ شەھەردە بار كىشىلەرنى بىز (يەنى لۇتنى ۋە ئۇنىڭغا ئەگەشكەن مۆمىنلەرنى) ئوبدان بىلىمىز، ئۇنى ۋە ئۇنىڭ ئايالىدىن باشقا كىشىلىرىنى ئەلۋەتتە قۇتۇلدۇرىمىز، پەقەت ئايالى (كۇفرىدا قەۋمىگە ھەمنەپەس بولغانلىقى ئۈچۈن) قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ».');
INSERT INTO "saleh" VALUES(29,33,'29:33',' بىزنىڭ ئەلچىلىرىمىز لۇتقا كەلگەن چاغدا، لۇت ئۇلارنى قوغداپ قالالمايدىغانلىقىدىن ئەنسىرەپ قايغۇردى ۋە يۈرىكى سىقىلدى، ئۇلار ئېيتتى: «سەن قورقمىغىن ۋە قايغۇرمىغىن، بىز چوقۇم سېنى ۋە ئايالىڭدىن بۆلەك كىشىلىرىڭنى چوقۇم قۇتۇلدۇرىمىز، پەقەت ئۇ قېلىپ ھالاك بولغۇچىلاردىن بولىدۇ».');
INSERT INTO "saleh" VALUES(29,34,'29:34',' بۇ شەھەرنىڭ ئاھالىسى ئاللاھنىڭ ئىتائىتىدىن چىققانلىقلىرى ئۈچۈن، ئۇلارغا بىز چوقۇم ئاسماندىن ئازاب چۈشۈرىمىز.');
INSERT INTO "saleh" VALUES(29,35,'29:35',' چۈشۈنىدىغان قەۋم ئۈچۈن (ئىبرەت قىلىپ) ئۇنىڭدىن (يەنى شەھەر خارابىسىنىڭ ئىزلىرىدىن) روشەن نىشاننى ھەقىقەتەن قالدۇردۇق.');
INSERT INTO "saleh" VALUES(29,36,'29:36',' مەديەن (ئاھالىسىگە) ئۇلارنىڭ قېرىندىشى شۇئەيبنى (پەيغەمبەر قىلىپ ئەۋەتتۇق)، شۇئەيب: «ئى قەۋمىم! بىر ئاللاھقا ئىبادەت قىلىڭلار، ئاخىرەت كۈنىدىن قورقۇڭلار، يەر يۈزىدە بۇزغۇنچىلىق قىلىپ پىتنە ـ پاسات تېرىماڭلار» دېدى.');
INSERT INTO "saleh" VALUES(29,37,'29:37',' ئۇلار شۇئەيبنى ئىنكار قىلدى، شۇنىڭ بىلەن ئۇلارغا قاتتىق زىلزىلە يۈزلەندى ـ دە، ئۇلار ئۆيلىرىدە ئولتۇرغان پېتى قېتىپ قېلىشتى. ');
INSERT INTO "saleh" VALUES(29,38,'29:38',' ئادنى ۋە سەمۇدنى (ھالاك قىلدۇق)، (ئى ئەھلى مەككە!) سىلەرگە ھەقىقەتەن ئۇلارنىڭ (ھىجازدىكى، يەمەندىكى) تۇرالغۇ جايلىرىدىن ئۇلارنىڭ ھالاك بولغانلىقى مەلۇم بولدى، شەيتان ئۇلارغا (كۇفرىدىن ۋە گۇناھلاردىن ئىبارەت) ئەمەللىرىنى چىرايلىق كۆرسەتتى، ئۇلارنى توغرا يولدىن توستى، ئۇلار ئەقىل ئىگىلىرى ئىدى (لېكىن ئۇلار تەكەببۇرلۇق يۈزىسىدىن ئەقلىنى جايىدا ئىشلەتمىدى).');
INSERT INTO "saleh" VALUES(29,39,'29:39',' (دۇنيا ـ دەپنىسى نۇرغۇن) قارۇننى، پىرئەۋننى ۋە (ئۇنىڭ زۇلۇمدا ياردەمچى بولغان ۋەزىرى) ھاماننى (ھالاك قىلدۇق)، شۈبھىسىزكى، مۇسا ئۇلارغا روشەن مۆجىزىلەرنى ئېلىپ كەلدى، ئۇلار زېمىندا چوڭچىلىق قىلدى (يەنى ئاللاھقا ئىبادەت قىلىشتىن، پەيغەمبەرگە ئىتائەت قىلىشتىن بويۇنتاۋلىق قىلدى)، ئۇلار (ئازابىمىزدىن) قېچىپ كېتەلمىدى.');
INSERT INTO "saleh" VALUES(29,40,'29:40','(بۇ گۇناھكارلارنىڭ) ھەربىرىنى گۇناھى تۈپەيلىدىن جازالىدۇق، ئۇلارنىڭ بەزىسىگە تاش ياغدۇردۇق، ئۇلارنىڭ بەزىسىنى قاتتىق ئاۋاز ھالاك قىلدى، ئۇلارنىڭ بەزىسىنى (مال ـ مۈلكى بىلەن قوشۇپ) يەرگە يۇتقۇزدۇق، ئۇلارنىڭ بەزىسىنى (سۇدا) غەرق قىلدۇق، ئاللاھ ئۇلارغا زۇلۇم قىلمىدى، لېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى. ');
INSERT INTO "saleh" VALUES(29,41,'29:41',' ئاللاھنى قويۇپ باشقا نەرسىلەرنى مەبۇد قىلىۋالغانلارنىڭ مىسالى ئۆي ياسىغان ئۆمۈچۈكنىڭ مىسالىغا ئوخشايدۇ (يەنى ئۆمۈچۈكنىڭ ئۆيى ئۇنى ئىسسىق ـ سوغۇقتىن قوغدىيالمىغاندەك، ئۇلارنىڭ چوقۇنۇۋاتقانلىرىمۇ ئۇلارغا ھېچقانداق پايدا ـ زىيان يەتكۈزەلمەيدۇ)، ئۆيلەرنىڭ ئەڭ ئاجىزى ئۆمۈچۈكنىڭ ئۆيىدۇر. ئەگەر ئۇلار (بۇ ھەقىقەتنى) بىلسە ئىدى (ئاللاھتىن باشقىغا چوقۇنمايتتى).');
INSERT INTO "saleh" VALUES(29,42,'29:42',' ئاللاھ ئۇلار ئاللاھنى تاشلاپ ئىبادەت قىلىۋاتقان نەرسىنى ئوبدان بىلىدۇ، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(29,43,'29:43',' بىز ئىنسانلارغا (قۇرئاندا ئۇلارنىڭ زېھنىگە يېقىنلاشتۇرۇش ئۈچۈن) بايان قىلغان بۇ تەمسىللەرنى پەقەت ئىلىم ئىگىلىرىلا چۈشىنەلەيدۇ.');
INSERT INTO "saleh" VALUES(29,44,'29:44','ئاللاھ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، ھەقىقەتەن بۇنىڭدا مۆمىنلەر ئۈچۈن (ئاللاھنىڭ بىرلىكىنى كۆرسىتىدىغان) دەلىل بار.');
INSERT INTO "saleh" VALUES(29,45,'29:45',' (ئى مۇھەممەد!) ساڭا ۋەھىي قىلىنغان كىتابنى (يەنى قۇرئاننى) تىلاۋەت قىلغىن، نامازنى (تەئدىل ئەركان بىلەن) ئوقۇغىن، ناماز ھەقىقەتەن قەبىھ ئىشلاردىن ۋە گۇناھلاردىن توسىدۇ، ئاللاھنى ياد ئېتىش ھەممىدىن (يەنى ئۇنىڭدىن باشقا ھەممە ئىبادەتتىن) ئۇلۇغدۇر، ئاللاھ قىلىۋاتقان (ھەممە) ئىشىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(29,46,'29:46',' ئەھلى كىتاب بىلەن پەقەت ئەڭ چىرايلىق رەۋىشتە مۇنازىرىلىشىڭلار، ئۇلارنىڭ ئىچىدىكى زۇلۇم قىلغۇچىلار (يەنى سىلەرگە قارشى ئۇرۇش قىلغۇچىلار ۋە فىدىيە بېرىشتىن باش تارتقۇچىلار) بۇنىڭدىن مۇستەسنا، ئېيتىڭلاركى، «بىزگە نازىل قىلىنغان كىتابقا ۋە سىلەرگە نازىل قىلىنغان كىتابقا ئىمان ئېيتتۇق، سىلەرنىڭ ئىلاھىڭلار ۋە بىزنىڭ ئىلاھىمىز بىردۇر، بىز ئاللاھقا بويسۇنغۇچىلارمىز».');
INSERT INTO "saleh" VALUES(29,47,'29:47',' (ئى مۇھەممەد!) شۇنىڭدەك (يەنى سەندىن ئىلگىرىكىلەرگە كىتاب نازىل قىلغاندەك) ساڭىمۇ كىتاب (يەنى قۇرئان) نازىل قىلدۇق. بىز كىتاب بەرگەنلەر (يەنى يەھۇدىيلار، ناسارالار) دىن قۇرئانغا ئىشىنىدىغانلار بار، بۇلاردىن (يەنى مەككە ئاھالىسىدىن) مۇ قۇرئانغا ئىشىنىدىغانلار بار، بىزنىڭ ئايەتلىرىمىزنى پەقەت كاپىرلارلا ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(29,48,'29:48','ئىلگىرى سەن كىتاب ئوقۇشنى بىلمەيتتىڭ، خەت يېزىشنىمۇ بىلمەيتتىڭ، (مۇبادا سەن كىتاب ئوقۇغان، خەت يازىدىغان بولساڭ) ئۇ چاغدا ھەققە قارشى تۇرغۇچىلار چوقۇم گۇمانلىناتتى.');
INSERT INTO "saleh" VALUES(29,49,'29:49',' ئۇنداق ئەمەس (يەنى ئىش زالىملار گۇمان قىلغاندەك ئەمەس)، قۇرئان ئىلىم بېرىلگەنلەرنىڭ كۆڭۈللىرىدە ساقلانغان روشەن ئايەتلەردۇر، بىزنىڭ ئايەتلىرىمىزنى پەقەت زالىملارلا ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(29,50,'29:50',' ئۇلار: «نېمىشقا ئۇنىڭغا پەرۋەردىگارىدىن مۆجىزىلەر نازىل قىلىنمايدۇ» دېدى. ئېيتقىنكى، «مۆجىزىلەر ھەقىقەتەن ئاللاھنىڭ دەرگاھىدىدۇر (مېنىڭ قولۇمدا ئەمەستۇر)، مەن پەقەت ئاشكارا ئاگاھلاندۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(29,51,'29:51',' بىزنىڭ ئۇلارغا تىلاۋەت قىلىنىپ تۇرىدىغان كىتابنى ساڭا نازىل قىلغانلىقىمىز ئۇلارغا (مۆجىزە بولۇشقا) كۇپايە قىلمىدىمۇ؟ بۇ كىتابتا ئىمان ئېيتقان قەۋم ئۈچۈن شەك ـ شۈبھىسىز رەھمەت ۋە ۋەز ـ نەسىھەت بار.');
INSERT INTO "saleh" VALUES(29,52,'29:52',' ئېيتقىنكى، «مەن بىلەن سىلەرنىڭ ئاراڭلاردا (مېنىڭ راست پەيغەمبەر ئىكەنلىكىمگە) گۇۋاھ بولۇشقا ئاللاھ كۇپايىدۇر، ئاللاھ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى بىلىپ تۇرىدۇ، باتىلغا ئىشەنگەن، ئاللاھنى ئىنكار قىلغان ئادەملەر زىيان تارتقۇچىلاردۇر».');
INSERT INTO "saleh" VALUES(29,53,'29:53',' ئۇلار سەندىن ئازابنىڭ چاپسان كېلىشىنى تەلەپ قىلىدۇ، ئاللاھ (ئۇلارنىڭ ھالاك بولۇشىنىڭ) مۇئەييەن ۋاقتىنى بەلگىلىمىگەن بولسا ئىدى، ئۇلارغا ئازاب ئەلۋەتتە (ئۇلار تەلەپ قىلغان ۋاقىتتىلا) كېلەتتى، ئۇلارغا ئازاب چوقۇم ئۇلار خەۋەرسىز ھالەتتە تۇرغاندا ئۇشتۇمتۇت كېلىدۇ.');
INSERT INTO "saleh" VALUES(29,54,'29:54','ئۇلار سەندىن ئازابنىڭ تېز كېلىشىنى تەلەپ قىلىدۇ، شەك ـ شۈبھىسىزكى، جەھەننەم (قىيامەت كۈنى) كاپىرلارنى قورشاپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(29,55,'29:55',' ئۇ كۈندە ئازاب ئۇلارنى ئۈستىلىرىدىن، ئاياغلىرىنىڭ ئاستىدىن ئورىۋالىدۇ. ئاللاھ ئۇلارغا: «(دۇنيادا قىلغان ئەمەللىرىڭلارنىڭ) جازاسىنى تېتىڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(29,56,'29:56',' ئى مۆمىن بەندىلىرىم! مېنىڭ زېمىنىم ھەقىقەتەن كەڭدۇر، ماڭىلا ئىبادەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(29,57,'29:57',' ھەربىر جاندار ئۆلۈمنىڭ تەمىنى تېتىغۇچىدۇر، ئاندىن بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(29,58,'29:58',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەرنى جەننەتنىڭ ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان ئالىي جايلىرىغا ئورۇنلاشتۇرىمىز، ئۇ يەرلەردە ئۇلار مەڭگۈ قالىدۇ، ياخشى ئەمەللەرنى قىلغۇچىلارغا بېرىلگەن مۇكاپات نېمىدېگەن ياخشى!. ');
INSERT INTO "saleh" VALUES(29,59,'29:59',' ئۇلار (ئاللاھنىڭ يولىدا يەتكەن ئەزىيەتلەرگە) سەۋر قىلىدۇ، (ھەممە ئىشلىرىدا) پەرۋەردىگارىغا يۆلىنىدۇ.');
INSERT INTO "saleh" VALUES(29,60,'29:60',' نۇرغۇن ھايۋانلار ئۆز رىزقىنى ئۈستىگە ئالالمايدۇ (يەنى ئۆز رىزقىنى تېپىپ يېيىشتىن ئاجىزدۇر)، ئۇلارنى ۋە سىلەرنى ئاللاھ رىزىقلاندۇرىدۇ، ئاللاھ (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(29,61,'29:61',' ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتتى، كۈننى ۋە ئاينى كىم (بەندىلەرنىڭ مەنپەئىتى ئۈچۈن) بويسۇندۇردى؟» دەپ سورىساڭ، ئۇلار چوقۇم: «ئاللاھ» دەپ جاۋاب بېرىدۇ، ئۇلار قانداقمۇ (ئاللاھنىڭ ئىبادىتىدىن) باش تارتىدۇ!');
INSERT INTO "saleh" VALUES(29,62,'29:62','ئاللاھ بەندىلىرىدىن قايسى ئادەمنىڭ رىزقىنى كەڭ قىلماقچى بولىدىكەن، ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلماقچى بولىدىكەن) ئۇنى تار قىلىدۇ، ئاللاھ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(29,63,'29:63','ئەگەر سەن ئۇلاردىن: «بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تىرىلدۈرگەن كىم؟» دەپ سورىساڭ، ئۇلار چوقۇم «ئاللاھ» دەپ جاۋاپ بېرىدۇ. ئېيتقىنكى، «(سىلەرگە قارشى پاكىتلار بار بولغانلىقتىن) جىمى ھەمدۇسانا ئاللاھقا خاستۇر»، ئەمما ئۇلارنىڭ تولىسى (بۇ سۆزلىرىدىكى زىددىيەتنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(29,64,'29:64','دۇنيا تىرىكچىلىكى پەقەت ئويۇن ـ كۈلكە ۋە غەپلەتتىن ئىبارەتتۇر، شۈبھىسىزكى، ئاخىرەت يۇرتى ھەقىقىي ھايات (يۇرتىدۇر)، ئەگەر ئۇلار بىلسە ئىدى (بۇ دۇنيانى ئۇ دۇنيادىن ئارتۇق كۆرمەيتتى).');
INSERT INTO "saleh" VALUES(29,65,'29:65',' ئۇلار بەرگەن نېمىتىمىز (يەنى دېڭىزدىن ئۇلار كېمىگە چىقىپ (غەرق بولۇشتىن قورققان) چاغلىرىدا، ئاللاھقا كامالى ئىخلاس بىلەن ئىلتىجا قىلىدۇ، ئاللاھ ئۇلارنى ئامان ـ ئېسەن قۇرۇقلۇققا چىقارغان چاغدا (بالا ـ قازادىن قۇتۇلدۇرغان ئاللاھنى ئۇنتۇپ)، توساتتىن (ئاللاھقا) شېرىك كەلتۈرىدۇ.');
INSERT INTO "saleh" VALUES(29,66,'29:66','قۇتۇلدۇرغانلىقىمىزغا ناشۈكۈرلۈك قىلىپ باقسۇن، قالغان ئۆمۈرلىرىدىن) بەھرىمەن بولسۇن، بۇلار (ئىشنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىدۇ.');
INSERT INTO "saleh" VALUES(29,67,'29:67',' ئۇلار (يەنى مۇشرىكلار) كۆرمەمدۇكى، بىز ھەرەمنى ئامان (جاي) قىلدۇق، ھالبۇكى، ئۇلارنىڭ ئەتراپىدىكى كىشىلەر ئەسىر ئېلىنىۋاتىدۇ ۋە ئۆلتۈرۈلۈۋاتىدۇ، ئۇلار باتىلغا ئىشىنىپ، ئاللاھنىڭ نېمىتىنى ئىنكار قىلامدۇ؟');
INSERT INTO "saleh" VALUES(29,68,'29:68',' ئاللاھقا يالغاننى چاپلىغان ياكى ھەق كەلگەندە ئۇنى ئىنكار قىلغان ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ جەھەننەمدە كاپىرلارغا جاي يوقمىدۇ؟ (ئەلۋەتتە بار). ');
INSERT INTO "saleh" VALUES(29,69,'29:69',' بىز ئۈچۈن كۈرەش قىلغانلارنى ئەلۋەتتە يولىمىزغا يېتەكلەيمىز، ئاللاھ ھەقىقەتەن ياخشى ئىش قىلغۇچىلار بىلەن بىللىدۇر.');
INSERT INTO "saleh" VALUES(30,1,'30:1','ئەلىف، لام، مىم.');
INSERT INTO "saleh" VALUES(30,2,'30:2','رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش ئاللاھنىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر ئاللاھنىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، ئاللاھ خالىغان كىشىگە ياردەم بېرىدۇ، ئاللاھ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر[2ـ5]. ');
INSERT INTO "saleh" VALUES(30,3,'30:3','رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش ئاللاھنىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر ئاللاھنىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، ئاللاھ خالىغان كىشىگە ياردەم بېرىدۇ، ئاللاھ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر[2ـ5]. ');
INSERT INTO "saleh" VALUES(30,4,'30:4','رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش ئاللاھنىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر ئاللاھنىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، ئاللاھ خالىغان كىشىگە ياردەم بېرىدۇ، ئاللاھ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر[2ـ5]. ');
INSERT INTO "saleh" VALUES(30,5,'30:5','رۇملۇقلار يېقىن بىر جايدا يېڭىلدى، ئۇلار يېڭىلگەندىن كېيىن بىر قانچە يىل ئىچىدە يېڭىدۇ، ئىلگىرى ۋە كېيىن ھەممە ئىش ئاللاھنىڭ باشقۇرۇشىدىدۇر، بۇ كۈندە مۆمىنلەر ئاللاھنىڭ بەرگەن ياردىمى بىلەن خۇشال بولىدۇ، ئاللاھ خالىغان كىشىگە ياردەم بېرىدۇ، ئاللاھ غالىبتۇر، (مۆمىنلەرگە) ناھايىتى مېھرىباندۇر[2ـ5]. ');
INSERT INTO "saleh" VALUES(30,6,'30:6','ئاللاھ (ئۇلارنى غالىب قىلىدىغانلىقىنى) ۋەدە قىلدى. ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ (چۈنكى ئاللاھنىڭ ۋەدىسى ھەقتۇر، سۆزى راستتۇر)، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(30,7,'30:7',' ئۇلار ھاياتىي دۇنيانىڭ تاشقى كۆرۈنۈشىنىلا بىلىدۇ، ئۇلار ئاخىرەتتىن (يەنى ئاخىرەت ئىشىدا ئويلىنىشتىن ۋە ئاخىرەت ئۈچۈن ئىشلەشتىن) غەپلەتتىدۇر.');
INSERT INTO "saleh" VALUES(30,8,'30:8',' ئۇلار (ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىنى بىلىشلىرى ئۈچۈن) ئۆزلىرى ئۈستىدە پىكىر يۈرگۈزمەمدۇ؟ ئاللاھ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى (يۇلتۇز قاتارلىق) نەرسىلەرنى پەقەت ھەق ۋە مۇئەييەن ۋاقىتلىق قىلىپ ياراتتى، شەك – شۈبھىسىزكى، نۇرغۇن كىشىلەر پەرۋەردىگارىغا مۇلاقات بولۇشنى (قايتا تىرىلىدىغانلىقىنى، قىلغان ئەمەلىگە يارىشا مۇكاپاتلىنىدىغانلىقىنى ياكى جازالىنىدىغانلىقىنى) ئىنكار قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(30,9,'30:9',' ئۇلار يەر يۈزىدە كېزىپ ئۆزلىرىدىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنلەر) قۇۋۋەتتە بۇلاردىن كۈچلۈك ئىدى. ئۇلار زىرائەت تېرىشتا، ئىمارەت سېلىشتا بۇلار (يەنى قۇرەيشلەر) دىن ئۈستۈن ئىدى. ئۇلارغا پەيغەمبەرلىرى مۆجىزىلەرنى ئېلىپ كەلدى (لېكىن ئۇلار مۆجىزىلەرنى ئىنكار قىلدى). ئاللاھ ئۇلارغا زۇلۇم قىلمىدى (يەنى ئاللاھ ئۇلارنى گۇناھسىز ھالاك قىلغىنى يوق)، لېكىن ئۇلار ئۆزلىرى (كۇفرى ئۈچۈن ۋە پەيغەمبەرلەرنىڭ مۆجىزىلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئۆزلىرىگە زۇلۇم قىلدى (شۇنىڭ بىلەن ئۇلار ھالاك بولۇشقا تېگىشلىك بولدى).');
INSERT INTO "saleh" VALUES(30,10,'30:10',' ئاندىن يامان ئىش قىلغانلارنىڭ ئاقىۋىتى ئەڭ يامان بولىدۇ، چۈنكى ئۇلار ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغان ۋە مەسخىرە قىلغان كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(30,11,'30:11',' ئاللاھ مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدۇ. ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلدۈرىدۇ، ئاندىن ئاللاھنىڭ دەرگاھىغا (ھېساب ئۈچۈن) قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(30,12,'30:12',' قىيامەت قايىم بولغان كۈندە گۇناھكارلار ئۈمىدسىزلىنىپ كېتىدۇ. ');
INSERT INTO "saleh" VALUES(30,13,'30:13',' ئۇلارغا شېرىك كەلتۈرگەن بۇتلىرىدىن شاپائەت قىلغۇچىلار بولمايدۇ، ئۇلار شېرىك كەلتۈرگەن بۇتلىرىدىن تانىدۇ.');
INSERT INTO "saleh" VALUES(30,14,'30:14',' قىيامەت قايىم بولغان كۈندە (مۆمىنلەر بىلەن كاپىرلار) بىر ـ بىرىدىن ئايرىلىدۇ.');
INSERT INTO "saleh" VALUES(30,15,'30:15','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار باغچىدا (يەنى جەننەتنىڭ باغچىلىرىدا) يايرىتىلىدۇ.');
INSERT INTO "saleh" VALUES(30,16,'30:16','بىزنىڭ ئايەتلىرىمىزنى ۋە ئاخىرەتتىكى مۇلاقاتنى ئىنكار قىلغان ۋە يالغانغا چىقارغانلارغا كەلسەك، ئۇلار داۋاملىق ئازابتا قالىدۇ. ');
INSERT INTO "saleh" VALUES(30,17,'30:17','سىلەر ئاخشامدا ۋە ئەتىگەندە ئاللاھقا تەسبىھ ئېيتىڭلار.');
INSERT INTO "saleh" VALUES(30,18,'30:18',' ئاسمانلاردىكى، زېمىندىكى ھەمدۇسانا، كەچ ۋە پېشىن ۋاقىتلىرىدىكى ھەمدۇسانانىڭ ھەممىسى ئاللاھقا خاستۇر.');
INSERT INTO "saleh" VALUES(30,19,'30:19',' ئاللاھ جانسىز نەرسىلەردىن جانلىق نەرسىلەرنى چىقىرىدۇ، جانلىق نەرسىلەردىن جانسىز نەرسىلەرنى چىقىرىدۇ، ئۆلگەن زېمىننى تىرىلدۈرىدۇ (يەنى قۇرغاق زېمىننى ئۆسۈملۈكلەر بىلەن كۆكەرتىدۇ)، سىلەرمۇ مۇشۇنداق تىرىلدۈرۈلىسىلەر.');
INSERT INTO "saleh" VALUES(30,20,'30:20','ئاللاھنىڭ سىلەرنى تۇپراقتىن ياراتقانلىقى، ئاندىن سىلەرنىڭ ئىنسان بولۇپ زېمىندا تارىلىپ يۈرۈشۈڭلار ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر.');
INSERT INTO "saleh" VALUES(30,21,'30:21',' ئاياللار بىلەن ئۇنسى- ئۈلپەت ئېلىشىڭلار ئۈچۈن (ئاللاھنىڭ) ئۇلارنى سىلەرنىڭ ئۆز تىپىڭلاردىن ياراتقانلىقى، ئاراڭلاردا (يەنى ئەر- خوتۇن ئارىسىدا) مېھر ـ مۇھەببەت ئورناتقانلىقى ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن، شەك ـ شۈبھىسىزكى، بۇنىڭدا نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(30,22,'30:22','ئاللاھنىڭ ئاسمانلارنى، زېمىننى ياراتقانلىقى، تىللىرىڭلارنىڭ، رەڭگىلىرىڭلارنىڭ خىلمۇخىل بولۇشى ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، بۇنىڭدا بىلىملىك كىشىلەر ئۈچۈن ھەقىقەتەن نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(30,23,'30:23','سىلەرنىڭ كېچىسى ۋە كۈندۈزى ئۇخلىشىڭلار، (كۈندۈزى) ئاللاھنىڭ مەرھەمىتىدىن (رىزىق) تەلەپ قىلىپ (ھەرىكەتلىنىشىڭلار) ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، شەك ـ شۈبھىسىزكى، بۇنىڭدا ئاڭلايدىغان قەۋم ئۈچۈن نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(30,24,'30:24',' ئاللاھنىڭ سىلەرگە چاقماقنى قورقۇنچ ۋە ئۈمىد قىلىپ كۆرسىتىشى، بۇلۇتتىن يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تىرىلدۈرۈشى ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، چۈشىنەلەيدىغان قەۋم ئۈچۈن بۇنىڭدا، شەك ـ شۈبھىسىزكى، نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(30,25,'30:25',' ئاللاھنىڭ ئاسمان - زېمىننى ئۆز ئەمرى بىلەن (مۇئەللەق) تۇرغۇزۇشى ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، ئاندىن ئاللاھ (قەبرىلەردە كۆمۈلۈپ ياتقان) سىلەرنى يەر ئاستىدىن چاقىرسا، دەرھال چىقىسىلەر.');
INSERT INTO "saleh" VALUES(30,26,'30:26',' ئاسمانلاردىكى ۋە زېمىندىكى ھەممە ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، ئاللاھنىڭ مەخلۇقاتىدۇر ۋە ئاللاھنىڭ تەسەررۇپى ئاستىدىدۇر)، ھەممە ئاللاھقا بويسۇنغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(30,27,'30:27',' مەخلۇقاتنى دەسلەپتە (يوقتىن) بار قىلىدىغان، ئاندىن ئۇنى (ئۆلگەندىن كېيىن) تىرىلىدۈرىدىغان ئاللاھ ئەنە شۇدۇر، ئۇ (يەنى مەخلۇقاتنى تىرىلدۈرۈش) ئاللاھقا (ئۇنى دەسلەپتە يوقتىن بار قىلغانغا قارىغاندا) ئوڭايدۇر، ئاسمانلاردا ۋە زېمىندا ئەڭ ئالىي سۈپەت ئاللاھقا خاستۇر، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(30,28,'30:28',' ئاللاھ سىلەرگە ئۆزۈڭلاردىن بىر مىسال كەلتۈرىدۇ، بىز سىلەرگە رىزىق قىلىپ بەرگەن مال- مۈلۈككە قۇللىرىڭلارنىڭ شېرىك بولۇشىغا، ئۇ بارىدا ئۇلارنىڭ سىلەر بىلەن باراۋەر بولۇشىغا، ئۇلاردىن ئۆزلىرىڭلار (يەنى ئازاد كىشىلەر) دىن قورققاندەك قورقۇشقا رازىمۇسىلەر؟ (بۇنىڭغا رازى بولمىغان ئىكەنسىلەر، مەخلۇقاتنىڭ ئاللاھقا شېرىك بولۇشىغا قانداق رازى بولدۇڭلار؟) چۈشىنىدىغان قەۋم ئۈچۈن ئايەتلەرنى مۇشۇنداق تەپسىلىي بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(30,29,'30:29',' ئۇنداق ئەمەس (يەنى ئاللاھقا شېرىك كەلتۈرۈشتە ئۇلارنىڭ ھېچقانداق ئۆزرىسى يوق)، (ئۆزلىرىگە) زۇلۇم قىلغانلار بىلىمسىزلىكتىن نەپسى خاھىشىغا ئەگەشتى. ئاللاھ گۇمراھ قىلغان ئادەمنى كىم ھىدايەت قىلالايدۇ؟ ئۇلارغا ھەرگىز ياردەم قىلغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(30,30,'30:30','باتىل دىنلاردىن بۇرۇلۇپ ئىسلام دىنىغا يۈزلەنگىن، ئاللاھنىڭ دىنىغا (ئەگەشكىنكى) ئاللاھ ئىنسانلارنى شۇ (فىترەت) بىلەن ياراتقان، ئاللاھنىڭ ياراتقىنىدا ئۆزگىرىش بولمايدۇ، بۇ توغرا دىندۇر، لېكىن ئىنسانلارنىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(30,31,'30:31','ئاللاھقا تەۋبە بىلەن قايتىڭلار، ئاللاھتىن قورقۇڭلار، نامازنى ئادا قىلىڭلار، مۇشرىكلاردىن بولماڭلار.');
INSERT INTO "saleh" VALUES(30,32,'30:32','سىلەر دىندا ئىختىلاپ قىلىشىپ، پىرقە ـ پىرقە بولۇپ، ھەرپىرقە ئۆز دىنى بىلەن خۇشاللىنىدىغانلاردىن (بولماڭلار).');
INSERT INTO "saleh" VALUES(30,33,'30:33',' ئىنسانلارغا بىرەر زىيان ـ زەخمەت يەتكەن چاغدا، ئۇلار پەرۋەردىگارىغا ئىخلاس بىلەن ئىلتىجا قىلىدۇ، ئاندىن ئاللاھ ئۇلارغا رەھمىتىنى تېتىتقان چاغدا، ئۇلاردىن بىر پىرقە دەرھال پەرۋەردىگارىغا شېرىك كەلتۈرىدۇ.');
INSERT INTO "saleh" VALUES(30,34,'30:34',' ئۇلار بىزنىڭ بەرگەن نېمەتلىرىمىزگە نا شۈكۈرلۈك قىلىپ باقسۇن، قېنى؟ (ئى مۇشرىكلار!) (دۇنيا تىرىكچىلىكىدە سىلەرگە بەرگەن نېمەتلىرىمىزدىن) بەھرىمەن بولۇڭلار، سىلەر (پانىي دۇنيانىڭ نېمىتىدىن بەھرىمەن بولغانلىقىڭلارنىڭ ئاقىۋىتىنى) كەلگۈسىدە بىلىسىلەر.');
INSERT INTO "saleh" VALUES(30,35,'30:35',' ياكى ئۇلارغا ئۇلارنىڭ بۇتلارغا چوقۇنىشىنىڭ توغرىلىقىنى سۆزلەيدىغان بىرەر كىتاب نازىل قىلدۇقمۇ؟ (مۇنداق بولغىنى يوق).');
INSERT INTO "saleh" VALUES(30,36,'30:36',' ئىنسانلارغا رەھمەتنى تېتىتساق، ئۇلار بۇنىڭدىن خۇشاللىنىپ كېتىدۇ، ئەگەر ئۇلارغا قىلمىشلىرى تۈپەيلىدىن بىرەر كۆڭۈلسىزلىك يەتسە، دەرھال ئۈمىدسىزلىنىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(30,37,'30:37',' ئۇلار بىلمەمدۇكى، ئاللاھ خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، ھەقىقەتەن بۇنىڭدا (رىزىق بەرگەن ئاللاھنىڭ ھېكمىتىگە) ئىشىنىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(30,38,'30:38','خىش ـ ئەقرىبالارغا، مىسكىنلەرگە، ئىبن سەبىللەرگە (سىلە ـ رەھىم ۋە ياخشىلىقتىن تېگىشلىك) ھەققىنى بەرگىن، ئاللاھنىڭ رازىلىقىنى كۆزلەيدىغانلار ئۈچۈن مۇنداق قىلىش ياخشىدۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(30,39,'30:39',' سىلەر كىشىلەرنىڭ پۇل ـ مېلى ئىچىدە ئۆستۈرۈش ئۈچۈن بىرەر پۇل ـ مال بەرسەڭلار، ئاللاھنىڭ دەرگاھىدا ئۇ ئۆسمەيدۇ (يەنى كىشىلەر بەرگىنىمدىن جىقراق قايتۇرسۇن دېگەن نىيەت بىلەن بېرىلگەن سوۋغىنىڭ ئاللاھنىڭ دەرگاھىدا ساۋابى بولمايدۇ)، ئاللاھنىڭ رازىلىقىنى كۆزلەپ بەرگەن سەدىقەڭلار (ياكى ئېھسانىڭلار) غا ھەسسىلەپ ساۋاب بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(30,40,'30:40',' ئاللاھ سىلەرنى خەلق ئەتتى، ئاندىن سىلەرگە رىزىق بەردى. ئاندىن سىلەرنى قەبزى روھ قىلىدۇ، ئاندىن سىلەرنى تىرىلدۈرىدۇ، شېرىك كەلتۈرگەنلىرىڭلاردىن قايسىسى بۇ ئىشلارنىڭ قايسىبىرىنى قىلالايدۇ؟ ئاللاھ پاكتۇر، ئۇلارنىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن ئالىيدۇر.');
INSERT INTO "saleh" VALUES(30,41,'30:41',' ئىنسانلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن، قۇرۇقلۇقتا ۋە دېڭىزدا ئاپەت يۈزبەردى، ئاللاھ ئۇلارنى تەۋبە قىلسۇن دەپ قىلمىشلىرىنىڭ بىر قىسمىنىڭ (جازاسىنى) ئۇلارغا تېتىتتى.');
INSERT INTO "saleh" VALUES(30,42,'30:42',' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، يەر يۈزىدە سەير قىلىڭلار، ئۆتكەنكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قاراڭلار (ئۆزلىرىگە زۇلۇم قىلغانلارنىڭ جايلىرىغا قاراڭلاركى، ئۇلارنىڭ پەيغەمبەرلەرنى ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتى قانداق بولغان، ئاللاھتائالا ئۇلارنىڭ دىيارلىرىنى خاراپ قىلىپ، ئۇلارنى ئىبرەت ئالغۇچىلار ئۈچۈن ئىبرەت قىلدى). ئۇلارنىڭ تولىسى مۇشرىك ئىدى.');
INSERT INTO "saleh" VALUES(30,43,'30:43','ئاللاھ تەرىپىدىن كېلىدىغان، قارشى تۇرغىلى بولمايدىغان كۈن (يەنى قىيامەت) كېلىشتىن ئىلگىرى توغرا دىنغا يۈزلەنگىن، بۇ كۈندە ئۇلار (نىڭ بىر پىرقىسى جەننەتكە، بىر پىرقىسى جەھەننەمگە) ئايرىلىدۇ.');
INSERT INTO "saleh" VALUES(30,44,'30:44',' كىمكى كاپىر بولسا (دوزاختا ئەبەدىي قېلىپ) كۇفرىنىڭ جازاسىنى ئۆزى تارتىدۇ، كىملەركى ياخشى ئەمەل قىلسا، ئۆزلىرى ئۈچۈن (ئاخىرەتتە) جاي راستلايدۇ.');
INSERT INTO "saleh" VALUES(30,45,'30:45',' ئاللاھ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مەرھەمىتىدىن مۇكاپاتلايدۇ، ئاللاھ ھەقىقەتەن كاپىرلارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(30,46,'30:46',' ئاللاھنىڭ سىلەرگە ئۆزىنىڭ رەھمىتىنى تېتىتىش، ئەمرى بويىچە كېمىلەرنى دېڭىزلاردا ماڭدۇرۇش، ئۆزىنىڭ مەرھەمىتىدىن تىلىشىڭلارۋە شۈكۈر قىلىشىڭلار ئۈچۈن شاماللارنى يامغۇر بىلەن خۇشخەۋەر بەرگۈچى قىلىپ ئەۋەتىشى ئاللاھنىڭ (كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر.');
INSERT INTO "saleh" VALUES(30,47,'30:47',' (ئى مۇھەممەد!) شۈبھىسىزكى، سەندىن ئىلگىرى نۇرغۇن پەيغەمبەرلەرنى قەۋمىگە (پەيغەمبەر قىلپ) ئەۋەتتۇق، ئۇ پەيغەمبەرلەر ئۇلارغا (ئۆزلىرىنىڭ راست پەيغەمبەرلىكلىرىنى ئىسپاتلايدىغان) نۇرغۇن روشەن مۆجىزىلەرنى ئېلىپ كەلدى (ئۇلار مۆجىزىلەرنى ئىنكار قىلىپ ئاسىيلىق قىلدى). ئاسىيلىق قىلغانلارنى جازالىدۇق، مۆمىنلەرگە ياردەم قىلىش بىزگە تېگىشلىك بولدى.');
INSERT INTO "saleh" VALUES(30,48,'30:48',' ئاللاھ شاماللارنى ئەۋەتىپ بۇلۇتنى قوزغايدۇ، ئاندىن ئۇنى ئاسماندا خالىغىنى بويىچە تارقىتىدۇ، (گاھى) ئۇنى تارقاق پارچىلارغا بۆلىدۇ، شۇنىڭ بىلەن يامغۇرنىڭ بۇلۇتنىڭ ئارىسىدىن چۈشۈۋاتقانلىقىنى كۆرىسەن، ئاللاھ يامغۇرنى خالىغان بەندىلىرىنىڭ ئۈستىگە ياغدۇرغان چاغدا، ئۇلار دەرھال خۇشال بولۇپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(30,49,'30:49',' ھالبۇكى، ئۇلار يامغۇر يېغىشتىن بۇرۇن ھەقىقەتەن ئۈمىدسىزلىنىپ كەتكەن ئىدى.');
INSERT INTO "saleh" VALUES(30,50,'30:50','ئاللاھنىڭ رەھمىتىنىڭ نەتىجىلىرىگە قارىغىنكى، ئۇ زېمىننى ئۆلگەندىن كېيىن قانداق تىرىلدۈرىدۇ، شۈبھىسىزكى، ئاللاھ ھەقىقەتەن ئۆلۈكلەرنى تىرىلدۈرگۈچىدۇر، ئاللاھ ھەممە نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(30,51,'30:51','ئەگەر بىز (زىرائەتكە زىيانلىق) شامالنى ئەۋەتسەك، ئۇلار (شامالنىڭ تەسىرىدىن) زىرائەتنىڭ سارغىيىپ كەتكەنلىكىنى كۆرسە، شۇنىڭدىن كېيىن ئۇلار (ئاللاھنىڭ ئىلگىرىكى نېمەتلىرىگە) كۇفرىلىق قىلىدۇ.');
INSERT INTO "saleh" VALUES(30,52,'30:52',' شۈبھىسىزكى، سەن ئۆلۈكلەرگە (سۆزۈڭنى) ئاڭلىتالمايسەن، ئارقىسىغا قاراپ يۈز ئۆرۈگەن گاسلارغىمۇ چاقىرىقىڭنى ئاڭلىتالمايسەن. ');
INSERT INTO "saleh" VALUES(30,53,'30:53',' كورلارنىمۇ گۇمراھلىقتىن قۇتقۇزۇپ توغرا يولغا سالالمايسەن، سەن پەقەت بىزنىڭ ئايەتلىرىمىزگە ئىشىنىدىغانلارغىلا (سۆزۈڭنى) ئاڭلىتالايسەن، ئۇلار مۇسۇلمانلاردۇر.');
INSERT INTO "saleh" VALUES(30,54,'30:54',' ئاللاھ سىلەرنى ئاجىز ياراتتى، ئاندىن سىلەرنى ئاجىزلىقتىن كۈچلۈك قىلدى، ئاندىن (سىلەرنى) كۈچلۈكلۈكتىن ئاجىز قىلدى ۋە (سىلەرگە) قېرىلىقنى يەتكۈزدى، ئاللاھ نېمىنى خالىسا شۇنى يارىتىدۇ، ئۇ ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(30,55,'30:55','ەت قايىم بولغان كۈندە (گۇناھكارلار (دۇنيادا) پەقەت ئازغىنا ۋاقىت تۇرغانلىقىغا قەسەم قىلىدۇ، ئۇلار دۇنيادىكى چاغلىرىدىمۇ مۇشۇنداق يالغانچىلىق قىلاتتى.');
INSERT INTO "saleh" VALUES(30,56,'30:56',' ئىلمى بار، ئىمانى بار ئادەملەر ئېيتتى: «سىلەر ئاللاھنىڭ تەقدىرى بويىچە (دۇنيادا ۋە قەبرىلەردە) قىيامەت كۈنىگۈچە قالدىڭلار، مانا بۇ قىيامەت كۈنىدۇر، لېكىن سىلەر بىلمەيتتىڭلار (يەنى قىيامەتنىڭ ھەقلىقىغا ئىشەنمەيتتىڭلار)».');
INSERT INTO "saleh" VALUES(30,57,'30:57',' بۇ كۈندە زالىملارنىڭ (ئېيتقان) ئۆزرىسىنىڭ پايدىسى بولمايدۇ، ئۇلاردىن ئۇلارنىڭ (تەۋبە قىلىشى يا ئىتائەت قىلىشى بىلەن) ئاللاھنى رازى قىلىشىمۇ تەلەپ قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(30,58,'30:58',' شەك ـ شۈبھىسىزكى، بۇ قۇرئاندا كىشىلەرگە تۈرلۈك مىساللارنى بايان قىلدۇق، ئەگەر سەن ئۇلارغا (قۇرئان ئايەتلىرىدىن) بىرەر ئايەتنى كەلتۈرسەڭ، كاپىرلار (دىللىرى قەساۋەتلىشىپ كەتكەنلىكتىن) چوقۇم ئېيتىدۇكى، «سىلەر پەقەت يالغانچىلار سىلەر».');
INSERT INTO "saleh" VALUES(30,59,'30:59',' (ئاللاھنىڭ بىرلىكىنى) بىلمەيدىغان (كۇففار) لارنىڭ دىللىرىنى مۇشۇنداق پېچەتلەيمىز.');
INSERT INTO "saleh" VALUES(30,60,'30:60',' (ئى مۇھەممەد!) (مۇشرىكلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن، ئاللاھنىڭ (ساڭا نۇسرەت ئاتا قىلىش ۋە دىنىڭنى ئۈستۈنلۈككە ئىگە قىلىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، (ئاخىرەتكە) ئىشەنمەيدىغانلار سېنى يەڭگىلتەك قىلىپ قويمىسۇن.');
INSERT INTO "saleh" VALUES(31,1,'31:1','ئەلىف، لام، مىم.');
INSERT INTO "saleh" VALUES(31,2,'31:2',' بۇ، ھېكمەتلىك كىتابنىڭ ئايەتلىرىدۇر.');
INSERT INTO "saleh" VALUES(31,3,'31:3',' (ئۇ) (تەئدىل ئەركان بىلەن) ناماز ئوقۇيدىغان، (ئاللاھنىڭ رىزاسىنى تىلەش يۈزىسىدىن كۆڭۈل ئازادىلىكى بىلەن) زاكات بېرىدىغان ۋە ئاخىرەتكە جەزمەن ئىشىنىدىغان ياخشى ئىش قىلغۇچى كىشىلەرگە ھىدايەتتۇر ۋە رەھمەتتۇر[3ـ4].');
INSERT INTO "saleh" VALUES(31,4,'31:4',' (ئۇ) (تەئدىل ئەركان بىلەن) ناماز ئوقۇيدىغان، (ئاللاھنىڭ رىزاسىنى تىلەش يۈزىسىدىن كۆڭۈل ئازادىلىكى بىلەن) زاكات بېرىدىغان ۋە ئاخىرەتكە جەزمەن ئىشىنىدىغان ياخشى ئىش قىلغۇچى كىشىلەرگە ھىدايەتتۇر ۋە رەھمەتتۇر[3ـ4].');
INSERT INTO "saleh" VALUES(31,5,'31:5','ئاشۇلار (يەنى يۇقىرىقى سۈپەتلەرگە ئىگە كىشىلەر) پەرۋەردىگارىنىڭ توغرا يولىدا بولغۇچىلاردۇر، ئەنە شۇلار مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(31,6,'31:6',' بەزى كىشىلەر بىلىمسىزلىكتىن، (كىشىلەرنى) ئاللاھنىڭ يولىدىن ئازدۇرۇش ئۈچۈن بىھۇدە گەپلەرنى سېتىۋالىدۇ ۋە ئاللاھنىڭ يولىنى مەسخىرە قىلىدۇ، ئەنە شۇلار خور قىلغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(31,7,'31:7',' ئەگەر ئۇنىڭغا بىزنىڭ ئايەتلىرىمىز (يەنى قۇرئان ئايەتلىرى) ئوقۇپ بېرىلسە، گويا ئۇنى ئاڭلىمىغاندەك، ئىككى قۇلىقى ئېغىرلىشىپ قالغاندەك (ئاللاھنىڭ ئايەتلىرىگە قۇلاق سېلىشتىن) تەكەببۇرلۇق بىلەن يۈز ئۆرۈيدۇ، (ئى مۇھەممەد!) ئۇنىڭغا قاتتىق ئازاب بىلەن خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(31,8,'31:8',' شۈبھىسىزكى، ئىمان ئېيتقانلار ۋە ياخشى ئەمەللەرنى قىلغانلار نازۇنېمەتلىك جەننەتلەرگە كىرىدۇ.');
INSERT INTO "saleh" VALUES(31,9,'31:9','ئۇ جەننەتلەردە ئۇلار مەڭگۈ قالىدۇ. بۇ ئاللاھنىڭ راست ۋەدىسىدۇر (ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ)، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(31,10,'31:10',' ئاللاھ ئاسمانلارنى تۈۋرۈكسىز ياراتتىكى، سىلەر ئۇنى كۆرۈپ تۇرۇۋاتىسىلەر. يەر تەۋرەپ سىلەرنى خاتىرجەمسىز قىلمىسۇن دەپ، يەردە تاغلارنى ئورناتتى. زېمىندا تۈرلۈك جاندارلارنى تاراتتى، بۇلۇتتىن يامغۇر سۈيىنى چۈشۈرۈپ، ئۇنىڭ بىلەن زېمىندا تۈرلۈك پايدىلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق.');
INSERT INTO "saleh" VALUES(31,11,'31:11',' مانا بۇلار ئاللاھنىڭ (ياراتقان) مەخلۇقاتلىرىدۇر، ماڭا كۆرسىتىپ بېرىڭلاركى، ئاللاھتىن باشقا مەبۇدلىرىڭلار زادى نېمىلەرنى ياراتتى؟ بەلكى زالىملار ئوپئوچۇق گۇمراھلىقتىدۇر[11');
INSERT INTO "saleh" VALUES(31,12,'31:12','بىز لوقمانغا ھەقىقەتەن ھېكمەت ئاتا قىلدۇق، (ئۇنىڭغا ئېيتتۇقكى) «ئاللاھقا شۈكۈر قىلغىن، كىمكى شۈكۈر قىلىدىكەن، ئۆزى ئۈچۈن قىلغان بولىدۇ، كىمكى كۇفرىلىق قىلىدىكەن، شۈبھىسىزكى، ئاللاھ (بەندىلەردىن) بىھاجەتتۇر، مەدھىيەگە لايىقتۇر».');
INSERT INTO "saleh" VALUES(31,13,'31:13',' ئۆز ۋاقتىدا لوقمان (ھەكىم) ئوغلىغا نەسىھەت قىلىپ: «ئى ئوغۇلچىقىم، ئاللاھقا شېرىك كەلتۈرمىگىن، شېرىك كەلتۈرۈش ھەقىقەتەن زور گۇناھتۇر» دېدى');
INSERT INTO "saleh" VALUES(31,14,'31:14',' ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق. ئانىسى ئۇنى (قورسىقىدا) ئۈستى ـ ئۈستىگە ئاجىزلىق بىلەن كۆتۈردى. ئىككى يىلدا ئۇنى ئەمچەكتىن ئايرىدى. (ئى ئىنسان!) ماڭا ۋە ئاتا ـ ئاناڭغا شۈكۈر قىلغىن، ئاخىر قايتىدىغان جاي مېنىڭ دەرگاھىمدۇر.');
INSERT INTO "saleh" VALUES(31,15,'31:15',' ئەگەر ئاتا ـ ئاناڭ سېنى سەن بىلمەيدىغان نەرسىنى ماڭا شېرىك كەلتۈرۈشكە زورلىسا، ئۇلارغا ئىتائەت قىلمىغىن، ئۇلارغا دۇنيادا ياخشى مۇئامىلىدە بولغىن (يەنى دىنىڭغا زىيان يەتمەيدىغان ئاساستا ياخشىلىق قىلغىن)، ماڭا تائەت بىلەن قايتقان ئادەمنىڭ يولىغا ئەگەشكىن، ئاندىن مېنىڭ دەرگاھىمغا قايتىسىلەر، سىلەرگە قىلمىشىڭلارنى ئېيتىپ بېرىمەن.');
INSERT INTO "saleh" VALUES(31,16,'31:16',' (لوقمان ئېيتتى) «ئى ئوغۇلچىقىم! قىلمىشىڭ قىچا چاغلىق نەرسە بولۇپ، ئۇ ئۇيۇل تاشنىڭ ئىچىدە (يەنى ئەڭ مەخپىي جايدا) يا ئاسمانلارنىڭ قېتىدا ياكى زېمىننىڭ ئاستىدا بولسىمۇ، ئاللاھ ئۇنى ھازىر قىلىدۇ (ئۇنىڭ ھېسابىنى ئالىدۇ). ئاللاھ ھەقىقەتەن ئىنچىكە كۈزەتكۈچىدۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(31,17,'31:17','ئى ئوغۇلچىقىم! نامازنى (ۋاقتىدا تەئدىل ئەركان بىلەن) ئوقۇغىن، (كىشىلەرنى) ياخشىلىققا بۇيرۇغىن، يامانلىقتىن توسقىن، ساڭا يەتكەن كۈلپەتلەرگە سەۋر قىلغىن (چۈنكى ھەقىقەتكە دەۋەت قىلغۇچى ئەزىيەتلەرگە ئۇچرايدۇ)، بۇ ھەقىقەتەن قىلىش ئۈچۈن ئىرادە تىكلەشكە تېگىشلىك ئىشلاردىندۇر.');
INSERT INTO "saleh" VALUES(31,18,'31:18',' كىشىلەردىن مەنسىتمەسلىك بىلەن يۈز ئۆرۈمىگىن، زېمىندا غادىيىپ ماڭمىغىن، ئاللاھ ھەقىقەتەن ھاكاۋۇر، ئۆزىنى چوڭ تۇتقۇچىلارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(31,19,'31:19','ئوتتۇراھال ماڭغىن، ئاۋازىڭنى پەسلەتكىن، ئاۋازلارنىڭ ئەڭ زېرىكەرلىكى ھەقىقەتەن ئېشەكلەرنىڭ ئاۋازىدۇر».');
INSERT INTO "saleh" VALUES(31,20,'31:20',' بىلمەمسىلەركى، ئاللاھ ئاسمانلاردىكى ۋە زېمىندىكى ھەممىنى سىلەرگە بويسۇندۇرۇپ بەردى (يەنى سىلەرنىڭ پايدىلىنىشىڭلارغا مۇۋاپىقلاشتۇرۇپ بەردى). سىلەرگە ئاشكارا ۋە يوشۇرۇن (يەنى ماددىي ۋە مەنىۋى) نېمەتلەرنى كامالەتكە يەتكۈزۈپ بەردى. بەزى كىشىلەر ھېچقانداق ئىلىمسىز، ھىدايەتسىز ۋە نۇرلۇق كىتابسىز ھالدا ئاللاھ بارىسىدا جېدەللىشىدۇ.');
INSERT INTO "saleh" VALUES(31,21,'31:21',' ئەگەر ئۇلارغا: «ئاللاھ نازىل قىلغان نەرسە (يەنى قۇرئان) غا ئەگىشىڭلار» دېيىلسە، ئۇلار: «ياق، بىز ئاتا ـ بوۋىلىرىمىزنىڭ دىنىغا ئەگىشىمىز» دەيدۇ، شەيتان ئۇلارنى دوزاخ ئازابىغا چاقىرغان تۇرسا (ئۇلار يەنىلا ئاتا ـ بوۋىلىرىنىڭ دىنىغا ئەگىشەمدۇ؟).');
INSERT INTO "saleh" VALUES(31,22,'31:22',' ياخشى ئىش قىلغۇچى بولغان ھالدا ئىخلاس بىلەن ئاللاھقا بويسۇنغان ئادەم مەھكەم تۇتقىغا ئېسىلغان بولىدۇ، ئىشلارنىڭ ئاقىۋىتى ئاللاھقا مەنسۇپتۇر');
INSERT INTO "saleh" VALUES(31,23,'31:23',' كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرى سېنى قايغۇغا سالمىسۇن، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتىدۇ، ئۇلارغا (دۇنيادىكى) قىلمىشلىرىنى ئېيتىپ بېرىمىز، ئاللاھ ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(31,24,'31:24',' ئۇلارنى (دۇنيادىن) ئازغىنا (مۇددەت) بەھرىمەن قىلىمىز، ئاندىن ئۇلارنى قاتتىق ئازابنى (تېتىشقا) مەجبۇرلايمىز.');
INSERT INTO "saleh" VALUES(31,25,'31:25',' ئەگەر سەن ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم خەلق ئەتتى؟» دەپ سورىساڭ، ئۇلار چوقۇم «ئاللاھ» دەپ جاۋاپ بېرىدۇ، ئېيتقىنكى، «جىمى ھەمدۇسانا ئاللاھقا خاستۇر!» بەلكى ئۇلارنىڭ تولىسى ئۇقمايدۇ. ');
INSERT INTO "saleh" VALUES(31,26,'31:26',' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر ئاللاھنىڭ مۈلكىدۇر، ئاللاھ ھەقىقەتەن (مەخلۇقاتىدىن ۋە ئۇلارنىڭ ئىبادىتىدىن) بىھاجەتتۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(31,27,'31:27',' ئەگەر يەر يۈزىدىكى دەرەخلەرنىڭ ھەممىسى قەلەم بولغان، دېڭىز (سىيا) بولغان، ئۇنىڭغا يەنە يەتتە دېڭىز (نىڭ سىياسى) قوشۇلغان تەقدىردىمۇ ئاللاھنىڭ سۆزلىرىنى (يېزىپ) تۈگەتكىلى بولمايدۇ، ئاللاھ ھەقىقەتەن غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(31,28,'31:28','(ئى ئىنسانلار!) سىلەرنىڭ (دەسلەپتە) يارىتىلىشىڭلار، (ئۆلگەندىن كېيىن) تىرىلدۈرۈلۈشىڭلار پەقەت بىر ئادەمنىڭ يارىتىلىشى، بىر ئادەمنىڭ تىرىلدۈرۈلۈشىگە ئوخشاشتۇر. ئاللاھ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئەمەللىرىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(31,29,'31:29',' ئاللاھنىڭ كېچىنى كۈندۈزگە ۋە كۈندۈزنى كېچىگە كىرگۈزىدىغانلىقىنى، كۈن بىلەن ئاينى بويسۇندۇرىدىغانلىقىنى كۆرمەمسەن؟ (كۈن بىلەن ئاينىڭ) ھەر بىرى مۇئەييەن مۇددەت (يەنى قىيامەت) كىچە (ئۆز ئوربىتىسىدا) سەير قىلىدۇ. ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(31,30,'31:30','بۇ شۇنىڭ ئۈچۈنكى، ئاللاھ ھەق (ئىلاھ) دۇر، ئۇلارنىڭ ئاللاھنى قويۇپ ئىبادەت قىلغانلىرى (يەنى بۇتلىرى) باتىلدۇر، ئاللاھ ئالىيدۇر، بۈيۈكتۇر.');
INSERT INTO "saleh" VALUES(31,31,'31:31',' كۆرمەمسەنكى، كېمىلەرنىڭ ئاللاھنىڭ مەرھەمىتى بىلەن دېڭىزدا يۈرۈشى ئاللاھنىڭ سىلەرگە (ئۆز قۇدرىتىنىڭ) بىر قىسىم دەلىللىرىنى كۆرسىتىشى ئۈچۈندۇر. بۇنىڭدا ھەر بىر سەۋر قىلغۇچى، شۈكۈر قىلغۇچى (بەندە ئۈچۈن ھەقىقەتەن) (چوڭ) ئىبرەت بار.');
INSERT INTO "saleh" VALUES(31,32,'31:32',' (دېڭىزدا) ئۇلارنى تاغلاردەك دولقۇنلار ئورىۋالغان چاغدا، ئۇلار كامالى ئىخلاس بىلەن ئاللاھقا ئىلتىجا قىلىدۇ، ئاللاھ ئۇلارنى (دېڭىز خەتەرلىرىدىن) قۇتۇلدۇرۇپ قۇرۇقلۇققا چىقارغان چاغدا، ئۇلارنىڭ بەزىسى توغرا يولدا بولىدۇ. بىزنىڭ ئايەتلىرىمىزنى پەقەت خىيانەتكار (ئاللاھنىڭ نېمەتلىرىدىن تانغان) ئادەملا ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(31,33,'31:33',' ئى ئىنسانلار! پەرۋەردىگارىڭلارغا (بۇيرۇقلىرىنى ئورۇنلاش، مەنئى قىلغان ئىشلاردىن چەكلىنىش بىلەن) تەقۋادارلىق قىلىڭلار، شۇنداق بىر كۈندىن قورقۇڭلاركى، (ئۇ كۈندە) ئاتا بالىسىغا ئەسقاتمايدۇ، بالىمۇ ئاتىسىغا ئەسقاتمايدۇ، ئاللاھنىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى ئالداپ قويمىسۇن، شەيتاننىڭ سىلەرنى ئاللاھنىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن ئالدىشىغا يول قويماڭلار.');
INSERT INTO "saleh" VALUES(31,34,'31:34',' قىيامەت (نىڭ بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن ئاللاھنىڭ دەرگاھىدىدۇر. ئاللاھ يامغۇرنى (ئۆزى بەلگىلىگەن ۋاقىتتا ۋە ئۆزى بەلگىلىگەن جايغا) ياغدۇرىدۇ، بەچچىداندىكىلەرنىڭ نېمە ئىكەنلىكىنى (يەنى ئوغۇلمۇ، قىزمۇ، بېجىرىممۇ، كەمتۈكمۇ، بەختلىكمۇ، بەختسىزمۇ) ئاللاھ بىلىدۇ، ھېچ ئادەم ئەتە نېمىلەرنى قىلىدىغانلىقىنى (يەنى ئۇنىڭغا نېمە ئىش بولىدىغانلىقىنى، ياخشى ـ يامان ئىشلاردىن نېمىلەرنى قىلىدىغانلىقىنى) بىلمەيدۇ، ھېچ ئادەم ئۆزىنىڭ قەيەردە ئۆلىدىغانلىقىنى بىلمەيدۇ، ئاللاھ ھەقىقەتەن (پۈتۈن ئىشلارنى) بىلىپ تۇرغۇچىدۇر، (شەيئىلەرنىڭ ئىچكى ۋە تاشقى تەرەپلىرىدىن) تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(32,1,'32:1','ئەلىف، لام، مىم.');
INSERT INTO "saleh" VALUES(32,2,'32:2',' (بۇ) كىتاب (يەنى قۇرئان) نىڭ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغانلىقىدا ھېچ شەك يوقتۇر[2');
INSERT INTO "saleh" VALUES(32,3,'32:3','مۇشرىكلار: «مۇھەممەد ئۇنى ئۆزى توقۇدى» دەيدۇ. ھەرگىز ئۇنداق ئەمەس، بەلكى ئۇ سەندىن ئىلگىرى ھېچقانداق بىر پەيغەمبەر كەلمىگەن بىر قەۋمنى ھىدايەت تاپسۇن دەپ، ئۇلارنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، پەرۋەردىگارىڭ تەرىپىدىن كەلگەن ھەقىقەتتۇر.');
INSERT INTO "saleh" VALUES(32,4,'32:4',' ئاللاھ ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتى، ئاندىن ئەرش ئۈستىدە (ئۆزىگە لايىق رەۋىشتە) قارار ئالدى. سىلەرگە ئاللاھتىن باشقا ھېچقانداق ئىگە يوق، شاپائەت قىلغۇچىمۇ يوق (بۇنى) پىكىر قىلىپ (ئىمان ئېيتمامسىلەر؟).');
INSERT INTO "saleh" VALUES(32,5,'32:5',' ئاللاھ ئاسماندىن زېمىنغىچە بولغان (مەخلۇقاتىنىڭ) ئىشلىرىنى ئىدارە قىلىپ تۇرىدۇ، ئاندىن ئۇ ئىشلار ئۇزۇنلۇقى سىلەر ساناۋاتقان مىڭ يىلدەك كېلىدىغان بىر كۈندە ئاللاھنىڭ دەرگاھىغا ئۆرلەيدۇ.');
INSERT INTO "saleh" VALUES(32,6,'32:6','ئۇ (ئاللاھ) يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچىدۇر، غالىبتۇر. (بەندىلىرىگە) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(32,7,'32:7',' ئۇ ھەممە نەرسىنى چىرايلىق (يەنى ھېكمىتىگە مۇۋاپىق، جايىدا) ياراتتى. دەسلەپتە ئىنساننى (يەنى ئىنسانلارنىڭ ئاتىسى ئادەم ئەلەيھىسسالامنى) لايدىن ياراتتى.');
INSERT INTO "saleh" VALUES(32,8,'32:8','اندىن ئۇنىڭ نەسلىنى ئەرزىمەس سۇنىڭ جەۋھىرى (يەنى مەنىي) دىن ياراتتى');
INSERT INTO "saleh" VALUES(32,9,'32:9','ئاندىن ئۇنى راۋۇرۇس يارىتىپ، ئۇنىڭغا ئۆزىگە مەنسۇپ روھنى كىرگۈزدى (يەنى جاننى كىرگۈزدى). يەنە سىلەر ئۈچۈن قۇلاقلارنى، كۆزلەرنى ۋە يۈرەكلەرنى ياراتتى، سىلەر ئاز شۈكۈر قىلىسىلەر.');
INSERT INTO "saleh" VALUES(32,10,'32:10','ئۇلار: «يەر ئاستىدا يوقالغاندىن كېيىن (يەنى گۆردە توپىغا ئايلىنىپ كەتكەندىن كېيىن)، راستلا قايتا يارىتىلامدۇق؟» دەيدۇ، بەلكى ئۇلار پەرۋەردىگارىغا مۇلاقات بولۇشنى ئىنكار قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(32,11,'32:11','ئېيتقىنكى، «سىلەرنىڭ (جېنىڭلارنى ئېلىشقا) مۇئەككەل قىلىنغان پەرىشتە سىلەرنىڭ جېنىڭلارنى ئالىدۇ، ئاندىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر».');
INSERT INTO "saleh" VALUES(32,12,'32:12',' ئەگەر سەن گۇناھكارلارنىڭ پەرۋەردىگارىنىڭ دەرگاھىدا باشلىرىنى ساڭگىلىتىپ تۇرغانلىقىنى كۆرىدىغان بولساڭ (ئەلۋەتتە قورقۇنچلۇق ھالەتنى كۆرگەن بولاتتىڭ)، ئۇلار ئېيتىدۇ: «پەرۋەردىگارىمىز! (ئىشنىڭ ھەقىقىتىنى) كۆردۇق، ئاڭلىدۇق. بىزنى ياخشى ئەمەل قىلىش ئۈچۈن (دۇنياغا) قايتۇرغىن، بىز (ئەمدى) ھەقىقەتەن (سېنىڭ ۋەدەڭنىڭ ھەق ئىكەنلىكىگە) ئىشەنگۈچىلەرمىز».');
INSERT INTO "saleh" VALUES(32,13,'32:13','ئەگەر بىز خالىساق، ھەر بىر كىشىگە ئەلۋەتتە ھىدايەت بېرەتتۇق، لېكىن مېنىڭ (گۇناھكارلارنى ئازاب قىلىشتىن ئىبارەت) ھۆكمۈم بېكىتىلىپ بولدى. مەن چوقۇم جىنلار ۋە ئىنسانلار (يەنى ئۇلارنىڭ گۇناھكارلىرى) ـ ھەممىسى بىلەن جەھەننەمنى تولدۇرىمەن.');
INSERT INTO "saleh" VALUES(32,14,'32:14','(ئەھلى دوزاخقا ئېيتىلىدۇ:) «مۇشۇ كۈندىكى ئۇچرىشىشىڭلارنى ئۇنتۇپ قالغانلىقىڭلار ئۈچۈن (بۇ خورلىغۇچى ئازابنى) تېتىڭلار، بىز (مۇ) ھەقىقەتەن سىلەرنى ئۇنتۇدۇق، قىلمىشىڭلار تۈپەيلىدىن مەڭگۈلۈك ئازابنى تېتىڭلار».');
INSERT INTO "saleh" VALUES(32,15,'32:15',' بىزنىڭ ئايەتلىرىمىزگە ھەقىقەتەن شۇنداق كىشىلەر ئىمان ئېيتىدۇكى، ئۇلارغا شۇ ئايەتلەر بىلەن ۋەز ـ نەسىھەت قىلىنسا (ئاللاھنىڭ ئايەتلىرىنى ئۇلۇغلاش يۈزىسىدىن ئاللاھقا) سەجدە قىلغان ھالدا يىقىلىدۇ، پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمد ئېيتىدۇ. ئۇلار (پەرۋەردىگارىنىڭ تائەت ـ ئىبادىتىدىن) چوڭچىلىق قىلىپ باش تارتمايدۇ.');
INSERT INTO "saleh" VALUES(32,16,'32:16',' ئۇلارنىڭ يانلىرى ئورۇن ـ كۆرپىدىن يىراق بولىدۇ (يەنى ئۇلار كېچىسى ئىبادەت قىلىپ ئاز ئۇخلايدۇ)، ئۇلار پەرۋەردىگارىنىڭ (ئازابىدىن) قورقۇپ، (رەھمىتىنى) ئۈمىد قىلىپ ئۇنىڭغا دۇئا قىلىدۇ، ئۇلارغا بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ياخشىلىق يوللىرىغا) سەرپ قىلىدۇ.');
INSERT INTO "saleh" VALUES(32,17,'32:17',' ئۇلارنىڭ قىلغان ئەمەللىرىگە مۇكاپات يۈزىسىدىن ئاللاھنىڭ ھۇزۇرىدا ساقلانغان ۋە ئۇلارنى خۇشال قىلىدىغان كاتتا نېمەتنى ھېچكىم بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(32,18,'32:18',' مۆمىن ئادەم پاسىق ئادەمگە ئوخشامدۇ؟ ئۇلار (ئاخىرەتلىك ساۋابتا) باراۋەر ئەمەس.');
INSERT INTO "saleh" VALUES(32,19,'32:19',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا كەلسەك، ئۇلارنىڭ قارارگاھى جەننەت بولىدۇ، (ئۇ) ئۇلارنىڭ قىلغان ئەمەللىرىنى مۇكاپاتلاش يۈزىسىدىن ئۇلار ئۈچۈن تەييارلانغان جايدۇر. ');
INSERT INTO "saleh" VALUES(32,20,'32:20',' ئاللاھنىڭ ئىتائىتىدىن چىققانلارغا كەلسەك، ئۇلارنىڭ جايى دوزاخ بولىدۇ، ھەر قاچان ئۇلار دوزاختىن چىقماقچى بولسا، ئۇلار دوزاخقا قايتۇرۇلىدۇ. ئۇلارغا: «سىلەر ئىنكار قىلغان دوزاخنىڭ ئازابىنى تېتىڭلار» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(32,21,'32:21',' ئۇلارنىڭ تەۋبە قىلىشى ئۈچۈن بىز ئۇلارغا چوقۇم ئەڭ چوڭ ئازابتىن (يەنى ئاخىرەت ئازابىدىن) ئىلگىرى ئەڭ يېقىنقى ئازاب (يەنى دۇنيا ئازابى) نى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(32,22,'32:22',' پەرۋەردىگارىنىڭ ئايەتلىرى بىلەن ۋەز ـ نەسىھەت قىلىنغان، ئاندىن ئۇنىڭدىن يۈز ئۆرۈگەن ئادەمدىنمۇ زالىم ئادەم بارمۇ؟ بىز گۇناھكارلارنى چوقۇم جازالايمىز. ');
INSERT INTO "saleh" VALUES(32,23,'32:23',' شەك ـ شۈبھىسىزكى، بىز مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق. (ئى مۇھەممەد!) سەن مۇساغا مۇلاقات بولۇشتىن شەكلەنمىگىن، بىز تەۋراتنى ئىسرائىل ئەۋلادىغا يېتەكچى قىلدۇق. ');
INSERT INTO "saleh" VALUES(32,24,'32:24',' ئۇلار (كۈلپەتلەرگە) سەۋر قىلغان، بىزنىڭ ئايەتلىرىمىزگە جەزمەن ئىشەنگەن چاغدا، ئۇلاردىن بىر قىسىم كىشىلەرنى بىزنىڭ ئەمرىمىز بىلەن توغرا يول كۆرسىتىدىغان پېشۋالار قىلدۇق. ');
INSERT INTO "saleh" VALUES(32,25,'32:25',' سېنىڭ پەرۋەردىگارىڭ قىيامەت كۈنى ئۇلارنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان (دىنىي) ئىشلار ئۈستىدە ئەلۋەتتە ھۆكۈم چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(32,26,'32:26','ئۇلاردىن ئىلگىرى ئۆتكەن قانچىلىغان ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى ئۇلار بىلمەمدۇ؟ ئۇلار (يەنى مەككىلىكلەر) ئۇلار (يەنى ھالاك بولغانلار) نىڭ يۇرتىدىن ئۆتۈپ تۇرىدۇ، بۇنىڭدا (بىزنىڭ قۇدرىتىمىزنىڭ كاتتىلىقىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار، ئۇلار ئاڭلىمامدۇ؟.');
INSERT INTO "saleh" VALUES(32,27,'32:27',' ئۇلار كۆرمەمدۇكى، بىز گىياھسىز زېمىنغا يامغۇر سۈيىنى سۈرۈپ ئېلىپ بارىمىز، ئۇنىڭ بىلەن ئۇلارنىڭ ھايۋاناتلىرى ۋە ئۆزلىرى يەيدىغان زىرائەتلەرنى ئۈندۈرۈپ چىقىرىمىز، ئۇلار (بۇنى) كۆرمەمدۇ؟.');
INSERT INTO "saleh" VALUES(32,28,'32:28',' ئۇلار (يەنى مەككە كۇففارلىرى مەسخىرە قىلىش يۈزىسىدىن): «(بىز بىلەن سىلەرنىڭ ئاراڭلاردا چىقىرىلىدىغان) بۇ ھۆكۈم قاچان ئەمەلگە ئاشىدۇ؟ ئەگەر سىلەر (دەۋايىڭلاردا) راستچىل بولساڭلار (ئېيتىپ بېرىڭلار)» دەيدۇ.');
INSERT INTO "saleh" VALUES(32,29,'32:29',' ئېيتقىنكى، «ھۆكۈم چىقىرىلىدىغان كۈندە كاپىرلارنىڭ ئېيتقان ئىمانىنىڭ پايدىسى بولمايدۇ، ئۇلارغا مۆھلەتمۇ بېرىلمەيدۇ».');
INSERT INTO "saleh" VALUES(32,30,'32:30',' سەن ئۇلاردىن يۈز ئۆرۈگىن (يەنى سۆزلىرىگە پەرۋا قىلىپ كەتمىگىن). سەن (ئاللاھنىڭ ئۇلارغا چۈشىدىغان ئازابىنى) كۈتكىن، ئۇلارمۇ ھەقىقەتەن (سىلەرگە چۈشىدىغان نەرسىلەرنى) كۈتكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(33,1,'33:1','ئى پەيغەمبەر! ئاللاھقا تەقۋادارلىق قىلىشنى (داۋاملاشتۇرغىن)، كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، ئاللاھ ھەقىقەتەن (بەندىلەرنىڭ ئەمەللىرىنى) بىلىپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) ھېكمەت بىلەن باشقۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(33,2,'33:2',' ساڭا پەرۋەردىگارىڭ تەرىپىدىن ۋەھىي قىلىنغان نەرسىگە (يەنى قۇرئانغا) ئەگەشكىن. ئاللاھ ھەقىقەتەن قىلغان ئەمەلىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(33,3,'33:3','(ھەممە ئىشىڭدا) ئاللاھقا يۆلەنگىن، ئاللاھ (ساڭا) ھامىي بولۇشقا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(33,4,'33:4',' ئاللاھ ھېچ ئادەمنىڭ ئىچىدە ئىككى يۈرەك ياراتقىنى يوق، سىلەر زىھار قىلغان ئاياللىرىڭلارنى ئاللاھ سىلەرگە ئانا قىلغىنى يوق، سىلەر بالا قىلىۋالغانلارنىمۇ ئاللاھ (ھەممە ھۆكۈمدە ئۆز پۇشتۇڭلاردىن بولغان) بالىلىرىڭلارنىڭ ھۆكمىدە قىلمىدى، بۇ (يەنى ئۆز پۇشتۇڭلاردىن بولمىغانلارنى بالا دېيىش) پەقەت ئاغزىڭلاردا ئېيتىلغان سۆزدۇر، ئاللاھ ھەق (سۆز) نى ئېيتىدۇ، توغرا يولغا باشلايدۇ.');
INSERT INTO "saleh" VALUES(33,5,'33:5',' ئۇلارنى ئاتىلىرىنىڭ ئىسىملىرى بىلەن چاقىرىڭلار، بۇ ئاللاھنىڭ دەرگاھىدا توغرىدۇر، ئەگەر ئۇلارنىڭ ئاتىلىرىنى بىلمىسەڭلار، ئۇ چاغدا ئۇلار سىلەرنىڭ دىنىي قېرىنداشلىرىڭلاردۇر، دوستلىرىڭلاردۇر، سىلەر سەۋەنلىكتىن قىلىپ سالغان ئىشلاردا سىلەرگە ھېچ گۇناھ بولمايدۇ، لېكىن قەستەن قىلغان ئىشىڭلاردا (سىلەرگە گۇناھ بولىدۇ)، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(33,6,'33:6','پەيغەمبەر مۆمىنلەرگە ئۇلارنىڭ ئۆز نەپسىلىرىدىنمۇ چارىدۇر، پەيغەمبەرنىڭ ئاياللىرى ئۇلارنىڭ ئانىلىرىدۇر، كىتابۇللاھتا ئۇرۇق ـ تۇغقانلارنىڭ بەزىسى بەزىسىگە (مىراس ئېلىشتا) مۆمىنلەردىن ۋە مۇھاجىرلاردىن يېقىندۇر، لېكىن (مۆمىنلەردىن ۋە مۇھاجىرلاردىن بولغان) دوستلىرىڭلارغا ۋەسىيەت قىلساڭلار دۇرۇس بولىدۇ، بۇ (يەنى تۇغقانلارنىڭ ئۆز ئارا مىراسقا ۋارىسلىق قىلىشى) كىتابتا (يەنى ئاللاھنىڭ كىتابىدا) يېزىلغاندۇر.');
INSERT INTO "saleh" VALUES(33,7,'33:7',' ئۆز ۋاقتىدا بىز پەيغەمبەرلەردىن ئەھدە ئالدۇق، سەندىن، نۇھتىن، ئىبراھىمدىن، مۇسادىن، مەريەم ئوغلى ئىيسادىن مەھكەم ئەھدە ئالدۇق.');
INSERT INTO "saleh" VALUES(33,8,'33:8',' ئاللاھ (قىيامەت كۈنى) راستچىللاردىن راستچىللىقى توغرۇلۇق سورايدۇ (يەنى پەيغەمبەرلەردىن پەيغەمبەرلىكىنى يەتكۈزگەنلىكىنى سورايدۇ)، ئاللاھ كاپىرلارغا قاتتىق ئازاب تەييارلىدى.');
INSERT INTO "saleh" VALUES(33,9,'33:9',' ئى مۆمىنلەر! ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ئەسلەڭلار، ئۆز ۋاقتىدا ئۈستۈڭلارغا (كۇففارلاردىن ئۇيۇشقان ئۇرۇشقۇچى) قوشۇن كەلگەن ئىدى. بىز ئۇلارغا قارشى بوران ۋە سىلەرگە كۆرۈنمەيدىغان قوشۇن (يەنى پەرىشتىلەرنى) ئەۋەتتۇق، ئاللاھ سىلەرنىڭ قىلغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(33,10,'33:10',' ئەينى ۋاقىتتا ئۇلار يۇقىرى تەرىپىڭلاردىنمۇ، تۆۋەن تەرىپىڭلاردىنمۇ (ھۇجۇم قىلىپ) كەلگەن ئىدى. بۇ چاغدا كۆزۈڭلار چەكچىيىپ قالغان، يۈرىكىڭلار ئاغزىڭلارغا قاپلىشىپ قالغان، ئاللاھ ھەققىدە تۈرلۈك گۇمانلاردا بولغان ئىدىڭلار.');
INSERT INTO "saleh" VALUES(33,11,'33:11',' بۇ يەردە مۆمىنلەر سىنالغان ۋە (بېشىغا كەلگەن كۈننىڭ قاتتىقلىقىدىن گويا ئۇلارنى يەر سىلكىگەندەك) قاتتىق تەۋرىنىشكە ئۇچرىغان ئىدى.');
INSERT INTO "saleh" VALUES(33,12,'33:12',' ئەينى ۋاقىتتا مۇناپىقلار ۋە دىللىرىدا (مۇناپىقلىق) كېسىلى بارلار: «ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى بىزلەرگە قۇرۇق ۋەدىلەرنى قىلىپتىكەن» دېيىشەتتى. ');
INSERT INTO "saleh" VALUES(33,13,'33:13',' ئەينى ۋاقىتتا ئۇلاردىن بىر گۇرۇھ ئادەم: «ئى يەسرىبلىكلەر! بۇ يەردە تۇرماي قايتىڭلار!» دېيىشەتتى، ئۇلاردىن يەنە بىر گۇرۇھ ئادەم: «بىزنىڭ ئۆيلىرىمىز ھەقىقەتەن ئوچۇقچىلىقتا» دېيىشىپ، پەيغەمبەردىن رۇخسەت سورىشاتتى، بۇلارنىڭ ئۆيلىرى ئوچۇقچىلىقتا ئەمەس، ئۇلار پەقەت قېچىشنىلا ئويلايدۇ.');
INSERT INTO "saleh" VALUES(33,14,'33:14',' ئەگەر شەھەرگە تۆت تەرەپتىن ھۇجۇم قىلىپ كىرىلسە، ئاندىن ئۇلاردىن ئىمانىدىن قايتىش، مۇسۇلمانلارغا قارشى ئۇرۇش قىلىش تەلەپ قىلىنسا، ئۇلار بۇ تەلەپنى ئۈزلۈكسىز رەۋىشتە ئاز ۋاقىت ئىچىدىلا قوبۇل قىلاتتى.');
INSERT INTO "saleh" VALUES(33,15,'33:15',' ئۇلار (يەنى مۇناپىقلار) ئىلگىرى ئاللاھقا (جەڭدە) چېكىنمەسلىككە ۋەدە بەرگەن ئىدى. ئاللاھقا بەرگەن ۋەدىسى سورىلىدۇ.');
INSERT INTO "saleh" VALUES(33,16,'33:16',' ئېيتقىنكى، «ئەگەر سىلەر ئۆلۈمدىن يا ئۆلتۈرۈلۈشتىن قاچساڭلار، قېچىشنىڭ سىلەرگە ھېچقانداق پايدىسى يوق، قاچقاندىمۇ سىلەر (دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولالايسىلەر».');
INSERT INTO "saleh" VALUES(33,17,'33:17',' ئېيتقىنكى: «ئەگەر ئاللاھ سىلەرگە بىرەر يامانلىقنى ئىرادە قىلسا، كىم ئاللاھقا قارشى تۇرۇپ سىلەرنى قوغدىيالايدۇ، ئەگەر ئاللاھ سىلەرگە رەھمەت قىلىشنى ئىرادە قىلسا (كىم ئۇنىڭغا ئارىلىشالايدۇ؟)» ئۇلار ئۆزلىرى ئۈچۈن ئاللاھتىن باشقا ھېچ ئىگە ۋە ھېچ ياردەمچى تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(33,18,'33:18',' ئاللاھ ئىچىڭلاردىكى باشقىلارنى (جىھادتىن) توسقۇچىلارنى ۋە قېرىنداشلىرىغا (مۇھەممەدنى ۋە ساھابىلىرىنى تەرك ئېتىپ) بىزنىڭ قېشىمىزغا كېلىڭلار دېگۈچىلەرنى ئوبدان بىلىدۇ، ئۇلار جەڭگە (رىيا قىلىپ) ئاندا ـ ساندا قاتنىشىدۇ.');
INSERT INTO "saleh" VALUES(33,19,'33:19',' ئۇلار سىلەرگە (ياردەمدە بولۇشقا) بېخىلدۇر، ئەگەر (ئۇلارغا دۈشمەن تەرەپتىن) خەۋپ كېلىدىغان بولسا، ئۇلارنىڭ ساڭا سەكراتقا چۈشۈپ قالغان ئادەمدەك (قورقۇنچتىن) كۆزلىرىنى پىلدىرلىتىپ قاراۋاتقانلىقىنى كۆرىسەن. قورقۇنچ كەتكەندە، ئۇلار پۇل ـ مالغا ئاچكۆز بولغان ھالدا سىلەرنى كەسكىن تىللار بىلەن رەنجىتىدۇ. ئۇلار (چىن دىللىرى بىلەن) ئىمان ئېيتقان ئەمەس. (ئۇلارنىڭ كۇفرى سەۋەبلىك) ئەمەللىرىنى ئاللاھ بىكار قىلدى، بۇ ئاللاھ ئۈچۈن ئاساندۇر.');
INSERT INTO "saleh" VALUES(33,20,'33:20',' ئۇلار (يەنى مۇناپىقلار) (قاتتىق قورققانلىقتىن) ئىتتىپاقداش قوشۇننى چېكىنمىدى دەپ ئويلايدۇ، ئەگەر ئىتتىپاقداش قوشۇن قايتا كەلسە، ئۇلار (قىردا) ئەئرابىيلارنىڭ (يەنى قىر ئەرەبلىرىنىڭ) يېنىدا تۇرۇپ سىلەرنىڭ خەۋەرلىرىڭلارنى ئۇققاچ (تىنچ يېتىشىنى) ئارزۇ قىلىدۇ، ئۇلار (ئۇرۇش ۋاقتىدا) ئاراڭلاردا بولغان تەقدىردىمۇ، (قورققانلىقتىن) جەڭگە (رىيا قىلىپ) ئاندا ـ ساندا قاتناشقان بولاتتى.');
INSERT INTO "saleh" VALUES(33,21,'33:21',' سىلەرگە ـ ئاللاھنى، ئاخىرەت كۈنىنى ئۈمىد قىلغان ۋە ئاللاھنى كۆپ ياد ئەتكەنلەرگە ـ رەسۇلۇللاھ ئەلۋەتتە ياخشى ئۈلگىدۇر.');
INSERT INTO "saleh" VALUES(33,22,'33:22',' مۆمىنلەر ئىتتىپاقداش قوشۇننى كۆرگەن چاغدا: «بۇ (يەنى قېيىن ئەھۋالدا قالغاندا دۈشمەن ئۈستىدىن غەلىبە قىلىش) ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى بىزگە ۋەدە قىلغان ئىشتۇر. ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى راست ئېيتتى» دېيىشتى. (بۇ ئىش) ئۇلارنىڭ (ئاللاھقا بولغان) ئىمانىنى ۋە (ئاللاھنىڭ بۇيرۇقلىرىغا بولغان) بويسۇنۇشىنى تېخىمۇ كۈچەيتتى.');
INSERT INTO "saleh" VALUES(33,23,'33:23',' مۆمىنلەرنىڭ ئىچىدە (رەسۇلۇللاھ بىلەن غازاتقا چىققاندا ساباتلىق بولۇپ، شېھىت بولغانغا قەدەر دادىللىق بىلەن جەڭ قىلىش توغرۇلۇق) ئاللاھقا بەرگەن ئەھدىنى ئىشقا ئاشۇرغان نۇرغۇن كىشىلەر بار. ئۇلارنىڭ بەزىسى (ئەھدىگە ۋاپا قىلىپ) شېھىت بولدى، بەزىسى (شېھىت بولۇشنى) كۈتكمەكتە، ئۇلار (پەرۋەردىگارىغا بەرگەن ئەھدىسىنى) ھەرگىز ئۆزگەرتكىنى يوق.');
INSERT INTO "saleh" VALUES(33,24,'33:24','ئاللاھ راستچىللارغا راستچىللىقى ئۈچۈن (ئاخىرەتتە ئەڭ ياخشى) مۇكاپات بېرىدۇ، مۇناپىقلارنى ئەگەر خالىسا ئازابلايدۇ، يا ئۇلارنى تەۋبىگە مۇۋەپپەق قىلىدۇ، ئاللاھ ھەقىقەتەن (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(33,25,'33:25',' ئاللاھ كاپىرلار (يەنى مەدىنىگە ھۇجۇم قىلغان ئىتتىپاقداش قوشۇن) نى خاپا قايتۇردى، ئۇلار مەقسىتىگە يېتەلمىدى، ئاللاھ (دۈشمەنگە بوران ۋە پەرىشتىلەرنى ئاپىرىدە قىلىپ) مۆمىنلەرنى ئۇرۇشسىز غەلىبىگە ئېرىشتۈردى، ئاللاھ كۈچلۈكتۇر، غالىبتۇر.');
INSERT INTO "saleh" VALUES(33,26,'33:26',' ئاللاھ ئەھلى كىتابتىن مۇشرىكلارغا ياردەم بەرگەنلەرنى (يەنى رەسۇلۇللاھ بىلەن قىلغان ئەھدىنى بۇزغان بەنى قۇرەيزە يەھۇدىيلىرىنى) قورغانلىرىدىن چۈشۈردى، ئۇلارنىڭ دىللىرىغا قورقۇنچ سالدى. (ئۇلارنىڭ) بىر بۆلۈكىنى ئۆلتۈردۇڭلار ۋە بىر بۆلۈكىنى ئەسىر ئالدىڭلار.');
INSERT INTO "saleh" VALUES(33,27,'33:27',' ئاللاھ سىلەرگە ئۇلارنىڭ زېمىنىنى، قورو ـ جايلىرىنى، مال ـ مۈلۈكلىرىنى ۋە (تېخى) سىلەرنىڭ قەدىمىڭلار دەسسەپ باقمىغان زېمىننى مىراس قىلىپ بەردى، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(33,28,'33:28','. ئى پەيغەمبەر! (ئارتۇق خىراجەت سوراپ سېنى رەنجىتكەن) ئاياللىرىڭغا ئېيتقىنكى، «ئەگەر سىلەر دۇنيا تىرىكچىلىكىنى (يەنى پاراۋان تۇرمۇشنى) ۋە دۇنيانىڭ زىبۇ زىننىتىنى كۆزلىسەڭلار، كېلىڭلار، سىلەرگە بىر ئاز نەرسە بېرەي، سىلەرنى چىرايلىقچە قويۇپ بېرەي.');
INSERT INTO "saleh" VALUES(33,29,'33:29','ئەگەر سىلەر ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى، ئاخىرەت يۇرتىنى ئىختىيار قىلساڭلار، ئاللاھ ھەقىقەتەن ئىچىڭلاردىكى ياخشى ئىش قىلغۇچىلارغا چوڭ ساۋابنى (يەنى كۆز كۆرمىگەن، قۇلاق ئاڭلىمىغان ۋە ئىنساننىڭ كۆڭلىگە كەلمىگەن نازۇنېمەتلەر بار جەننەتنى) تەييار قىلدى».');
INSERT INTO "saleh" VALUES(33,30,'33:30','ئى پەيغەمبەرنىڭ ئاياللىرى! ئىچىڭلاردىن كىمكى ئاشكارا قەبىھ ئىشنى قىلىدىكەن، ئۇنىڭغا ھەسسىلەپ ئازاب قىلىنىدۇ، بۇ ئاللاھقا ئاساندۇر.');
INSERT INTO "saleh" VALUES(33,31,'33:31',' سىلەردىن كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا ۋە ياخشى ئەمەل قىلسا، ئۇنىڭغا ساۋابنى ئىككى قاتلاپ بېرىمىز، ئۇنىڭغا (جەننەتتە) ئېسىل رىزىق تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(33,32,'33:32',' ئى پەيغەمبەرنىڭ ئاياللىرى! سىلەر باشقا ئاياللارنىڭ ھېچبىرىگە ئوخشىمايسىلەر، سىلەر (يات ئەرلەرگە سۆز قىلغاندا) نازلىنىپ سۆز قىلماڭلار، (نازلىنىپ سۆز قىلساڭلار) دىلىدا كېسەل بار ئادەم (سىلەرگە قارىتا) تەمەدە بولۇپ قالىدۇ، (گۇماندىن خالىي) ياخشى سۆزنى سۆزلەڭلار.');
INSERT INTO "saleh" VALUES(33,33,'33:33',' ئۆيلىرىڭلاردا ئولتۇرۇڭلار، ئىلگىرىكى جاھىلىيەت دەۋرىدىكى ئاياللارنىڭ ياسىنىپ چىققىنىدەك ياسىنىپ چىقماڭلار، ناماز ئوقۇڭلار، زاكات بېرىڭلار، ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، ئى پەيغەمبەرنىڭ ئائىلىسىدىكىلەر! ئاللاھ سىلەردىن گۇناھنى ساقىت قىلىشنى ۋە سىلەرنى تامامەن پاك قىلىشنى خالايدۇ.');
INSERT INTO "saleh" VALUES(33,34,'33:34',' ئۆيلىرىڭلاردا ئاللاھنىڭ ئايەتلىرىدىن ۋە ھېكمەت (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ھەدىسلىرى) دىن ئوقۇلۇۋاتقان نەرسىلەرنى ياد ئېتىڭلار، ئاللاھ ھەقىقەتەن سىرلارنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(33,35,'33:35',' مۇسۇلمان ئەرلەر ۋە مۇسۇلمان ئاياللارغا، مۆمىن ئەرلەر ۋە مۆمىن ئاياللارغا، تائەت ـ ئىبادەت قىلغۇچى ئەرلەر ۋە تائەت ـ ئىبادەت قىلغۇچى ئاياللارغا، راستچىل ئەرلەر ۋە راستچىل ئاياللارغا، سەۋر قىلغۇچى ئەرلەر ۋە سەۋر قىلغۇچى ئاياللارغا، خۇدادىن قورققۇچى ئەرلەر ۋە خۇدادىن قورققۇچى ئاياللارغا، سەدىقە بەرگۈچى ئەرلەر ۋە سەدىقە بەرگۈچى ئاياللارغا، روزا تۇتقۇچى ئەرلەر ۋە روزا تۇتقۇچى ئاياللارغا، نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئەرلەر ۋە نەپسىلىرىنى ھارامدىن ساقلىغۇچى ئاياللارغا، ئاللاھنى كۆپ زىكرى قىلغۇچى ئەرلەر ۋە ئاللاھنى كۆپ زىكرى قىلغۇچى ئاياللارغا ئاللاھ مەغپىرەت ۋە كاتتا ساۋاب تەييارلىدى.');
INSERT INTO "saleh" VALUES(33,36,'33:36',' ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان چاغدا، ئەر ـ ئايال مۆمىنلەرنىڭ ئۆز ئىشىدا ئىختىيارلىقى بولمايدۇ (يەنى ئاللاھ ۋە ئاللاھنىڭ پەيغەمبىرى بىرەر ئىشتا ھۆكۈم چىقارغان ئىكەن، ھېچ ئادەمنىڭ ئۇنىڭغا مۇخالىپەتچىلىك قىلىشىغا بولمايدۇ)، كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلسا، ھەقىقەتەن ئۇ ئوپئوچۇق ئازغان بولىدۇ.');
INSERT INTO "saleh" VALUES(33,37,'33:37',' ئۆز ۋاقتىدا سەن، ئاللاھ نېمەت بەرگەن، سەنمۇ ئىنئام قىلغان كىشىگە: «خوتۇنۇڭنى نىكاھىڭدا تۇتقىن، ئاللاھتىن قورققىن!» دېدىڭ، ئاللاھ ئاشكارىلىماقچى بولغان نەرسىنى كۆڭلۈڭدە يوشۇردۇڭ، كىشىلەرنىڭ تەنە قىلىشىدىن قورقتۇڭ، ئاللاھتىن قورقۇشۇڭ ئەڭ ھەقلىق ئىدى. مۆمىنلەرگە ئۇلارنىڭ بالا قىلىۋالغان ئوغۇللىرىنىڭ قويۇپ بەرگەن خوتۇنلىرىنى نىكاھلاپ ئالسا گۇناھ بولماسلىقى ئۈچۈن، زەيد ئۇنى (يەنى زەينەبنى) قويۇۋەتكەندىن كېيىن ساڭا نىكاھلاپ بەردۇق، ئاللاھنىڭ (سېنىڭ زەينەبنى ئېلىشىڭ توغرىسىدىكى) ئەمرى چوقۇم ئورۇنلىنىدۇ.');
INSERT INTO "saleh" VALUES(33,38,'33:38',' ئاللاھ پەيغەمبەرگە ھالال قىلغان ئىشقا ھېچ گۇناھ بولمايدۇ. (بۇ) ئاللاھنىڭ ئۆتكەنكى (پەيغەمبەرلەرگە) تۇتقان يولىدۇر. ئاللاھنىڭ ئەمرى ئەزەلدىنلا بېكىتىلىپ كەتكەن (ئۆزگەرمەس) ھۆكۈمدۇر.');
INSERT INTO "saleh" VALUES(33,39,'33:39',' ئۇلار (يەنى پەيغەمبەرلەر) ئاللاھنىڭ ئەمر ـ پەرمانلىرىنى يەتكۈزىدۇ، ئاللاھتىن قورقىدۇ، ئاللاھتىن باشقا ھېچ كىشىدىن قورقمايدۇ (ئى مۇھەممەد! سەنمۇ ئاللاھتىن غەيرىيدىن قورقماسلىقتا شۇ پەيغەمبەرلەرگە ئەگەشكىن)، ئاللاھ (ھەممە ئىشلاردىن) ھېساب ئېلىشقا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(33,40,'33:40','مۇھەممەد ئاراڭلاردىكى ئەرلەردىن ھېچبىرىنىڭ ئاتىسى ئەمەس، لېكىن (ئۇ) ئاللاھنىڭ پەيغەمبىرى ۋە پەيغەمبەرلەرنىڭ ئاخىرقىسىدۇر (ئاللاھ ئۇنىڭ بىلەن پەيغەمبەرلىكنى ئاخىرلاشتۇرغان، ئۇنىڭدىن كېيىن ھېچقانداق پەيغەمبەر كەلمەيدۇ)، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر (سىلەرنىڭ سۆز ـ ھەرىكىتىڭلاردىن ھېچ نەرسە ئاللاھقا مەخپىي ئەمەس).');
INSERT INTO "saleh" VALUES(33,41,'33:41',' ئى مۆمىنلەر! ئاللاھنى كۆپ ياد قىلىڭلار. ');
INSERT INTO "saleh" VALUES(33,42,'33:42','ئۇنىڭغا ئەتىگەن ـ ئاخشامدا تەسبىھ ئېيتىڭلار (چۈنكى بۇ ئىككى ۋاقىتتا پەرىشتىلەر چۈشۈپ تۇرغانلىقتىن، ئۇ ۋاقىتلارنىڭ ئەۋزىلى ھېسابلىنىدۇ). ');
INSERT INTO "saleh" VALUES(33,43,'33:43',' ئاللاھ سىلەرنى قاراڭغۇلۇقتىن نۇرغا (يەنى گۇمراھلىقتىن ھىدايەتكە) چىقىرىش ئۈچۈن، سىلەرگە رەھمەت قىلىپ تۇرىدۇ، ئۇنىڭ پەرىشتىلىرى سىلەرگە مەغپىرەت تەلەپ قىلىپ تۇرىدۇ، ئاللاھ مۆمىنلەرگە ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(33,44,'33:44','ئۇلار ئاللاھقا مۇلاقات بولغان كۈندە ئۇلارغا ئاللاھ تەرىپىدىن يوللىنىدىغان سالام، (سىلەرگە) ئامانلىق بولسۇن (دېگەن سۆزدىن ئىبارەت)، ئاللاھ ئۇلارغا ئېسىل مۇكاپات تەييارلىدى.');
INSERT INTO "saleh" VALUES(33,45,'33:45',' ئى پەيغەمبەر! سېنى بىز ھەقىقەتەن گۇۋاھچى، بېشارەتچى، ئاگاھلاندۇرغۇچى، ئاللاھنىڭ ئىزنى بىلەن ئۇنىڭ (بىرلىكىگە، تائەت ـ ئىبادىتىگە) دەۋەت قىلغۇچى ۋە نۇرلۇق چىراق قىلىپ ئەۋەتتۇق[45ـ46].');
INSERT INTO "saleh" VALUES(33,46,'33:46',' ئى پەيغەمبەر! سېنى بىز ھەقىقەتەن گۇۋاھچى، بېشارەتچى، ئاگاھلاندۇرغۇچى، ئاللاھنىڭ ئىزنى بىلەن ئۇنىڭ (بىرلىكىگە، تائەت ـ ئىبادىتىگە) دەۋەت قىلغۇچى ۋە نۇرلۇق چىراق قىلىپ ئەۋەتتۇق[45ـ46].');
INSERT INTO "saleh" VALUES(33,47,'33:47',' مۆمىنلەرگە ئۇلارنىڭ ئاللاھنىڭ بۈيۈك ئېھسانىغا ئېرىشىدىغانلىقىدىن خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(33,48,'33:48',' كاپىرلارغا ۋە مۇناپىقلارغا ئىتائەت قىلمىغىن، ئۇلارنىڭ (ساڭا قىلغان) ئازار ـ كۈلپەتلىرىگە پەرۋا قىلمىغىن (ئاللاھقا تەۋەككۈل قىلغىن)، ئاللاھ (ئۇنىڭغا) تەۋەككۈل قىلغانلارغا يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(33,49,'33:49','ئى مۆمىنلەر! سىلەر مۆمىن ئاياللارنى ئالساڭلار، ئاندىن ئۇلارغا يېقىنچىلىق قىلىشتىن بۇرۇن ئۇلارنى قويۇۋەتسەڭلار، بۇ چاغدا ئۇلار سىلەر ئۈچۈن ئىددەت تۈتماسلىقى كېرەك، ئۇلارغا ئاز ـ تولا نەرسە بېرىڭلار، ئۇلارنى چىرايلىقچە قويۇپ بېرىڭلار.');
INSERT INTO "saleh" VALUES(33,50,'33:50','ئى پەيغەمبەر! ساڭا بىز سەن مەھرىلىرىنى بەرگەن ئاياللىرىڭنى، ئاللاھ ساڭا غەنىيمەت قىلىپ بەرگەن، ئەسىر چۈشكەن ئاياللاردىن ئىلكىڭگە ئۆتكەنلەرنى ھالال قىلدۇق، ئاتاڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى، ئاناڭنىڭ ئەر قېرىندىشى ۋە ھەمشىرە قېرىندىشىنىڭ قىزلىرىنى (ساڭا ھالال قىلدۇق). بۇلار سەن بىلەن بىرلىكتە ھىجرەت قىلدى. ئەگەر بىر مۆمىن خوتۇن ئۆزىنى پەيغەمبەرگە بەخش قىلسا، پەيغەمبەر ئۇنى (مەھرىسىز) ئېلىشنى خالىسا، بۇنى مۆمىنلەرگە ئەمەس، يالغۇز ساڭىلا (ھالال قىلدۇق)، ئۇلارنىڭ ئاياللىرى ۋە چۆرىلىرى توغرۇلۇق بىزنىڭ چىقارغان بەلگىلىمىلىرىمىز بىزگە مەلۇمدۇر، ساڭا ھەرەج بولۇپ قالماسلىقى ئۈچۈن (سېنى ئالاھىدىلىكلەرگە ئىگە قىلدۇق)، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(33,51,'33:51',' ئاياللىرىڭدىن خالىغىنىڭنىڭ نۆۋىتىنى ئارقىغا سۈرسەڭ، خالىغىنىڭ بىلەن (نۆۋىتىدىن باشقا ۋاقىتتا) بىر يەردە بولساڭ بولىۋېرىدۇ، (ۋاقىتلىق) ئايرىلىپ تۇرغان ئاياللىرىڭدىن بىرى بىلەن ھەمتۆشەك بولماقچى بولساڭ، ساڭا ھېچ گۇناھ يوقتۇر، (مانا) بۇ ئۇلارنىڭ خۇشال بولۇشىغا، قايغۇرماسلىقىغا، بەرگەنلىرىڭگە ئۇلارنىڭ ھەممىسىنىڭ رازى بولۇشىغا ئەڭ يېقىندۇر، ئاللاھ دىلىڭلاردىكىنى بىلىپ تۇرىدۇ، ئاللاھ ھەممىنى بىلگۈچىدۇر، ھەلىمدۇر (يەنى جازالاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(33,52,'33:52',' (ئى پەيغەمبەر! ئىلكىڭدىكى ئاياللىرىڭدىن باشقا) يەنە خوتۇن ئالساڭ، ساڭا ھالال بولمايدۇ، باشقا ئاياللارنىڭ جامالى سېنى جەلپ قىلغان تەقدىردىمۇ، ئاياللىرىڭنى قويۇۋېتىپ ئۇلارنى ئەمرىڭگە ئېلىش (ساڭا ھالال بولمايدۇ). پەقەت سېنىڭ ئىلكىڭدىكى چۆرىلەر بۇنىڭدىن مۇستەسنا، ئاللاھ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(33,53,'33:53','ئى مۆمىنلەر! پەيغەمبەرنىڭ ئۆيلىرىگە كىرمەڭلار، پەقەت تاماققا چاقىرىلغان ۋاقتىڭلاردىلا كىرىڭلار، (باشقا ۋاقىتتا رۇخسەت بىلەن كىرگەندە) تاماقنىڭ پىشىشىنى كۈتۈپ ئولتۇرماڭلار، لېكىن چاقىرىلغاندا كىرىڭلار يۇ، تاماق يەپ بولغاندىن كېيىن تارقاپ كېتىڭلار، (تاماقتىن كېيىن) پاراڭ سېلىشىپ ئولتۇرماڭلار، بۇ (يەنى تاماقتىن كېيىن پاراڭ سېلىشىپ ئولتۇرۇش) پەيغەمبەرنى رەنجىتىدۇ، پەيغەمبەر (بۇنى ئېيتىشقا) سىلەردىن خىجىل بولىدۇ، ئاللاھ ھەق (نى ئېيتىش) تىن خىجىل بولمايدۇ. سىلەر پەيغەمبەرنىڭ ئاياللىرىدىن بىر نەرسە سوراپ (ئالماقچى) بولساڭلار، پەردە ئارقىسىدىن سوراپ ئېلىڭلار، مۇنداق قىلىش سىلەرنىڭ دىللىرىڭلارنىمۇ، ئۇلارنىڭ دىللىرىنىمۇ ئەڭ پاك تۇتىدۇ. سىلەرنىڭ رەسۇلۇللاھنى رەنجىتىپ قويۇشۇڭلار دۇرۇس ئەمەس، رەسۇلۇللاھتىن كېيىن ئۇنىڭ ئاياللىرىنى ئېلىشىڭلارمۇ مەڭگۈ دۇرۇس ئەمەس، بۇنداق قىلىش (يەنى رەسۇلۇللاھنى رەنجىتىش ۋە ئۇنىڭ ئاياللىرىنى ئېلىش) ھەقىقەتەن ئاللاھنىڭ دەرگاھىدا چوڭ گۇناھتۇر.');
INSERT INTO "saleh" VALUES(33,54,'33:54',' بىرەر نەرسىنى ئاشكارىلىساڭلارمۇ، يا (دىلىڭلاردا) يوشۇرساڭلارمۇ (ئاللاھ ھامان بىلىدۇ)، چۈنكى ئاللاھ ھەقىقەتەن ھەممە نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(33,55,'33:55',' ئۇلارغا (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ ئاياللىرىغا) ئاتىلىرى بىلەن، ئوغۇللىرى بىلەن، قېرىنداشلىرى بىلەن، قېرىنداشلىرىنىڭ ئوغۇللىرى بىلەن، ھەمشىرىلىرىنىڭ ئوغۇللىرى بىلەن، مۆمىن ئاياللار بىلەن، قۇل، چۆرىلىرى بىلەن كۆرۈشۈش ھېچ گۇناھ ئەمەس، (ئى پەيغەمبەرنىڭ ئاياللىرى!) ئاللاھتىن قورقۇڭلار، ئاللاھ ھەقىقەتەن ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(33,56,'33:56',' ئاللاھ ھەقىقەتەن پەيغەمبەرگە رەھمەت يوللايدۇ (يەنى پەيغەمبەر ئەلەيھىسسالامغا رەھمەت قىلىدۇ، شەنىنى ئۇلۇغ قىلىدۇ، دەرىجىسىنى ئۈستۈن قىلىدۇ)، پەرىشتىلەرمۇ ھەقىقەتەن (پەيغەمبەرگە) مەغپىرەت تەلەپ قىلىدۇ، ئى مۆمىنلەر! سىلەر پەيغەمبەرگە دۇرۇد ئېيتىڭلار ۋە ئامانلىق تىلەڭلار (چۈنكى رەسۇلۇللاھنىڭ سىلەرنىڭ ئۈستۈڭلاردىكى ھەققى چوڭدۇر، ئۇ سىلەرنى گۇمراھلىقتىن ھىدايەتكە، زۇلمەتتىن يورۇقلۇققا چىقارغۇچىدۇر).');
INSERT INTO "saleh" VALUES(33,57,'33:57',' ئاللاھنى ۋە ئۇنىڭ پەيغەمبىرىنى رەنجىتىدىغانلارنى ئاللاھ دۇنيا ۋە ئاخىرەتتە رەھمىتىدىن يىراق قىلىدۇ ۋە ئۇلارغا خار قىلغۇچى ئازابنى تەييارلايدۇ.');
INSERT INTO "saleh" VALUES(33,58,'33:58','مۆمىنلەر ۋە مۆمىنەلەرگە قىلمىغان ئىشلارنى (چاپلاپ) ئۇلارنى رەنجىتىدىغانلار (شۇ) بوھتاننى ۋە روشەن گۇناھنى ئۈستىگە ئارتىۋالغان بولىدۇ.');
INSERT INTO "saleh" VALUES(33,59,'33:59','ئى پەيغەمبەر! ئاياللىرىڭغا، قىزلىرىڭغا ۋە مۆمىنلەرنىڭ ئاياللىرىغا ئېيتقىنكى، پۈركەنجى بىلەن بەدىنىنى ئورىۋالسۇن، بۇنداق قىلغاندا ئۇلارنىڭ (ھۆر ئاياللار ئىكەنلىكى) ئەڭ ئوڭاي تونۇلىدۇ ـ دە، باشقىلار ئۇلارغا چېقىلمايدۇ. ئاللاھ (بەندىلىرىگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(33,60,'33:60',' ئەگەر مۇناپىقلار، (نىفاقتىن) دىللىرىدا كېسەل بارلار، مەدىنىدە يالغان خەۋەر تارقاتقۇچىلار (قىلمىشلىرىدىن) يانمىسا، (ئى مۇھەممەد) ئەلۋەتتە بىز سېنى ئۇلارغا مۇسەللەت قىلىمىز، ئاندىن ئۇلار مەدىنىدە لەنەتكە ئۇچرىغان ھالدا سەن بىلەن ئازغىنا ۋاقىت تۇرالايدۇ (يەنى ئۇلار مەدىنىدىن سۈرگۈن قىلىنىپ ھەيدەپ چىقىرىلىدۇ، چىقىپ كېتىشكە تەييارلىق قىلىۋېلىش ئۈچۈن مەدىنىدە پەقەت ئازغىنا ۋاقىت تۇرالايدۇ). ئۇلار قەيەردە بايقالسا، شۇ يەردە تۇتۇلىدۇ ۋە ئۆلتۈرۈلىدۇ[60ـ61].');
INSERT INTO "saleh" VALUES(33,61,'33:61',' ئەگەر مۇناپىقلار، (نىفاقتىن) دىللىرىدا كېسەل بارلار، مەدىنىدە يالغان خەۋەر تارقاتقۇچىلار (قىلمىشلىرىدىن) يانمىسا، (ئى مۇھەممەد) ئەلۋەتتە بىز سېنى ئۇلارغا مۇسەللەت قىلىمىز، ئاندىن ئۇلار مەدىنىدە لەنەتكە ئۇچرىغان ھالدا سەن بىلەن ئازغىنا ۋاقىت تۇرالايدۇ (يەنى ئۇلار مەدىنىدىن سۈرگۈن قىلىنىپ ھەيدەپ چىقىرىلىدۇ، چىقىپ كېتىشكە تەييارلىق قىلىۋېلىش ئۈچۈن مەدىنىدە پەقەت ئازغىنا ۋاقىت تۇرالايدۇ). ئۇلار قەيەردە بايقالسا، شۇ يەردە تۇتۇلىدۇ ۋە ئۆلتۈرۈلىدۇ[60ـ61].');
INSERT INTO "saleh" VALUES(33,62,'33:62',' بۇ (يەنى مۇناپىقلارغا ئاللاھنىڭ تۇتقان يولى) ئاللاھنىڭ ئۆتكەنكى ئۈممەتلەرگە تۇتقان يولىدۇر، ئاللاھنىڭ تۇتقان يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن.');
INSERT INTO "saleh" VALUES(33,63,'33:63',' كىشىلەر سەندىن قىيامەتنىڭ ۋاقتىنى سورايدۇ. «ئۇنى (مەن بىلمەيمەن) پەقەت ئاللاھ بىلىدۇ» دېگىن. سەن نېمە بىلىسەن، قىيامەت يېقىن بولۇشى مۇمكىن.');
INSERT INTO "saleh" VALUES(33,64,'33:64','ئاللاھ ھەقىقەتەن كاپىرلارنى رەھمىتىدىن يىراق قىلدى ۋە ئۇلارغا دوزاخنى تەييارلىدى.');
INSERT INTO "saleh" VALUES(33,65,'33:65',' ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇلار ھېچقانداق (ئۇلارنى قوغدايدىغان) دوست ۋە (ئۇلاردىن ئازابنى دەپئى قىلىدىغان) ياردەمچى تاپالمايدۇ.');
INSERT INTO "saleh" VALUES(33,66,'33:66',' دوزاختا ئۇلارنىڭ يۈزلىرى تۈرۈلۈپ كېتىدىغان كۈندە، ئۇلار: «كاشكى بىز ئاللاھقا ئىتائەت قىلغان بولساقچۇ! پەيغەمبەرگە ئىتائەت قىلغان بولساقچۇ!» دەيدۇ.');
INSERT INTO "saleh" VALUES(33,67,'33:67',' ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن باشلىقلىرىمىزغا، كاتتىلىرىمىزغا ئىتائەت قىلدۇق، ئۇلار بىزنى توغرا يولدىن ئازدۇردى، پەرۋەردىگارىمىز! ئۇلارغا ئازابنى ئىككى ھەسسە بەرگىن ۋە ئۇلارغا قاتتىق لەنەت قىلغىن» دەيدۇ[67 ـ 68].');
INSERT INTO "saleh" VALUES(33,68,'33:68',' ئۇلار: «پەرۋەردىگارىمىز! بىز ھەقىقەتەن باشلىقلىرىمىزغا، كاتتىلىرىمىزغا ئىتائەت قىلدۇق، ئۇلار بىزنى توغرا يولدىن ئازدۇردى، پەرۋەردىگارىمىز! ئۇلارغا ئازابنى ئىككى ھەسسە بەرگىن ۋە ئۇلارغا قاتتىق لەنەت قىلغىن» دەيدۇ[67 ـ 68].');
INSERT INTO "saleh" VALUES(33,69,'33:69','ئى مۆمىنلەر! سىلەر مۇساغا ئازار قىلغان كىشىلەر (يەنى بەنى ئىسرائىل) دەك بولماڭلار، ئاللاھ مۇسانى ئۇلارنىڭ ئېيتقانلىرىدىن (يەنى تۆھمەتلىرىدىن) ئاقلىدى، مۇسا ئاللاھنىڭ دەرگاھىدا ئابرۇيلۇق ئىدى.');
INSERT INTO "saleh" VALUES(33,70,'33:70',' ئى مۆمىنلەر! ئاللاھتىن قورقۇڭلار، توغرا سۆزنى قىلىڭلار. ');
INSERT INTO "saleh" VALUES(33,71,'33:71','ئاللاھ سىلەرنىڭ ئەمەللىرىڭلارنى تۈزەيدۇ (يەنى سىلەرنى ياخشى ئەمەللەرگە مۇۋەپپەق قىلىدۇ)، گۇناھلىرىڭلارنى مەغپىرەت قىلىدۇ، كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلسا زور مۇۋەپپەقىيەت قازانغان بولىدۇ.');
INSERT INTO "saleh" VALUES(33,72,'33:72','شۈبھىسىزكى، بىز ئامانەتنى (يەنى پەرزلەرنى ۋە شەرىئەت تەكلىپلىرىنى) ئاسمانلارغا، زېمىنغا ۋە تاغلارغا تەڭلىدۇق، ئۇلار ئۇنى ئۈستىگە ئالمىدى، ئۇنىڭ (ئېغىرلىقى) دىن قورقتى، ئۇنى ئىنسان ئۈستىگە ئالدى، ئىنسان ھەقىقەتەن (ئۆزىگە) زۇلۇم قىلغۇچىدۇر، ھەقىقەتەن ناداندۇر.');
INSERT INTO "saleh" VALUES(33,73,'33:73',' (مەزكۇر ئامانەتكە خىيانەت قىلغانلىقلىرى ئۈچۈن) مۇناپىق ئەرلەرنى، مۇناپىق ئاياللارنى، مۇشرىك ئەرلەرنى، مۇشرىك ئاياللارنى ئاللاھ ئازابلايدۇ، (مەزكۇر ئامانەتكە رىئايە قىلغانلىقلىرى ئۈچۈن) مۆمىن ئەرلەر ۋە مۆمىن ئاياللارنى ئاللاھ ئەپۇ قىلىدۇ، (ئاللاھ مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(34,1,'34:1','جىمى ھەمدۇسانا ئاللاھقا خاستۇر، ئاسمانلاردىكى ۋە زېمىندىكى ھەممە نەرسە ئاللاھنىڭ مۈلكىدۇر، ئاخىرەتتىكى ھەمدۇسانامۇ ئاللاھقا خاستۇر، ئاللاھ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(34,2,'34:2',' ئاللاھ زېمىننىڭ قوينىغا كىرىپ كېتىدىغان (يەنى يامغۇر، كان ۋە ئۆلۈكلەرگە ئوخشاش) نەرسىلەرنى، زېمىندىن چىقىدىغان (ئۆسۈملۈك، بۇلاق ۋە قۇدۇقلارنىڭ سۇلىرى قاتارلىق) نەرسىلەرنى، ئاسماندىن چۈشىدىغان (پەرىشتە، يامغۇر، ئەجەل، رىزىق قاتارلىق) نەرسىلەرنى، ئاسمانغا ئۆرلەيدىغان (ياخشى ئەمەللەر، مەقبۇل دۇئالار قاتارلىق) نەرسىلەرنى بىلىپ تۇرىدۇ، ئاللاھ بەندىلىرىگە ناھايىتى مېھرىباندۇر، (تەۋبە قىلغۇچىلارنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(34,3,'34:3',' كاپىرلار: «بىزگە قىيامەت كەلمەيدۇ» دەيدۇ، ئېيتقىنكى، «ئۇنداق ئەمەس، پەرۋەردىگارىم بىلەن قەسەمكى، قىيامەت سىلەرگە چوقۇم كېلىدۇ، ئاللاھ غەيىبنى بىلگۈچىدۇر، ئاسمانلاردىكى ۋە زېمىندىكى زەررە چاغلىق نەرسە ئاللاھتىن يىراق ئەمەستۇر، ئۇنىڭ (يەنى زەررە) دىن كىچىكرەك نەرسە بولسۇن، چوڭراق نەرسە بولسۇن، لەۋھۇلمەھپۇزدا خاتىرىلەنمىگىنى يوق».');
INSERT INTO "saleh" VALUES(34,4,'34:4','(ئۇلارنىڭ لەۋھۇلمەھپۇزدا خاتىرىلىنىشى) ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى مۇكاپاتلاش ئۈچۈندۇر، ئەنە شۇلار مەغپىرەتكە ۋە ئېسىل رىزىققا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(34,5,'34:5',' بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(34,6,'34:6',' ئىلىم بېرىلگەنلەر (يەنى ساھابىلەر ۋە كېيىنكى ئەمەل قىلغۇچى ئۆلىمالار ۋە ئۇلارغا ئوخشاش كىشىلەر) ساڭا پەرۋەردىگارىڭ تەرىپىدىن نازىل قىلىنغان قۇرئاننىڭ شۈبھىسىز ھەق ئىكەنلىكىنى، (ئىنسانلارنى) غالىب، ھەمدۇساناغا لايىق ئاللاھنىڭ يولىغا باشلايدىغانلىقىنى بىلىدۇ. ');
INSERT INTO "saleh" VALUES(34,7,'34:7',' كاپىرلار (بىر ـ بىرىگە) ئېيتىدۇ: «سىلەر (قەبرىلەردە چىرىپ) تىتما ـ تالاڭ بولۇپ كەتكەندىن كېيىن چوقۇم يېڭىدىن يارىتىلىسىلەر دەپ خەۋەر بېرىدىغان بىر ئادەمنى سىلەرگە كۆرسىتىپ قويايلىمۇ؟.');
INSERT INTO "saleh" VALUES(34,8,'34:8',' ئۇ ئاللاھقا يالغاننى توقۇدىمۇ؟ يا ئۇ ئېلىشىپ قالدىمۇ؟» ھەرگىز ئۇنداق ئەمەس، ئاخىرەتكە ئىمان كەلتۈرمەيدىغانلار ئازابتا ۋە چوڭقۇر گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(34,9,'34:9',' ئۇلار ئالدىدىكى ۋە ئارقىسىدىكى (ئۇلارنى ئوراپ تۇرغان) ئاسمان بىلەن زېمىنغا قارىمامدۇ؟ ئەگەر خالىساق ئۇلارنى يەرگە يۇتقۇزىمىز، ياكى ئاسماننى ئۇلارنىڭ ئۈستىگە پارچە ـ پارچە قىلىپ چۈشۈرىمىز. بۇنىڭدا ئاللاھقا قايتقۇچى ھەر بەندە ئۈچۈن ئەلۋەتتە (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) دەلىل بار.');
INSERT INTO "saleh" VALUES(34,10,'34:10','بىز داۋۇدقا دەرگاھىمىزدىن ھەقىقەتەن پەزل (يەنى پەيغەمبەرلىك، زەبۇر، تاغلارنىڭ ۋە قۇشلارنىڭ بويسۇنۇشى، تۆمۈرنىڭ يۇمشاق بولۇپ بېرىشى، ساۋۇت ياساشنى بىلدۈرۈشلەر) ئاتا قىلدۇق. «ئى تاغلار! ئى قۇشلار! داۋۇت بىلەن بىرلىكتە تەسبىھ ئېيتىڭلار» (دېدۇق). تۆمۈرنى داۋۇدقا يۇمشاق قىلىپ بەردۇق. ');
INSERT INTO "saleh" VALUES(34,11,'34:11','(بىز ئۇنىڭغا ئېيتتۇق) «مۇكەممەل ساۋۇتلارنى ياسىغىن، ساۋۇتلارنى ياساشتا (ھالقىلىرىنى بىر ـ بىرىگە) تەكشى قىلغىن، (ئى داۋۇد ئائىلىسىدىكىلەر!) ياخشى ئىش قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ ئەمەللىرىڭلارنى كۆرۈپ تۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(34,12,'34:12',' سۇلايمانغا شامالنى (بويسۇندۇرۇپ بەردۇق)، شامال چۈشتىن ئىلگىرى بىر ئايلىق مۇساپىنى، چۈشتىن كېيىن بىر ئايلىق مۇساپىنى باساتتى، ئۇنىڭغا مىس (چىقىدىغان) بۇلاقنى ئاققۇزۇپ بەردۇق، بەزى جىنلار پەرۋەردىگارىنىڭ ئەمرى بويىچە سۇلايماننىڭ ئالدىدا ئىشلەيتتى، ئۇلاردىن كىمكى بىزنىڭ ئەمرىمىزگە خىلاپلىق قىلىدىكەن، ئۇنىڭغا بىز دوزاخ ئازابىنى تېتىتىمىز. ');
INSERT INTO "saleh" VALUES(34,13,'34:13',' ئۇلار سۇلايمانغا ئۇ خالىغان كاتتا سارايلارنى، (مىستىن ـ ئەينەكتىن ئاجايىپ) ھەيكەللەرنى، كۆلدەك چوڭ لېگەنلەرنى، (چوڭلۇقتىن) مىدىرلىمايدىغان قازانلارنى ياسايتتى، (ئېيتتۇقكى) «ئى داۋۇد ئائىلىسىدىكىلەر! (ئاللاھنىڭ بۇ چوڭ نېمەتلىرىگە) شۈكۈر قىلىڭلار»، مېنىڭ بەندىلىرىمدىن (ئاللاھنىڭ نېمەتلىرىگە) شۈكۈر قىلغۇچى بەك ئاز. ');
INSERT INTO "saleh" VALUES(34,14,'34:14',' سۇلايماننىڭ ۋاپاتىنى ھۆكۈم قىلغىنىمىزدا (يەنى ئۇنى ۋاپات قىلدۇرغىنىمىزدا) سۇلايماننىڭ ئۆلگەنلىكىدىن جىنلارنى پەقەت ئۇنىڭ ھاسىسىنى يېگەن قۇرۇت خەۋەردار قىلدى. سۇلايمان (ھاسىسىنى قۇرۇت يەپ) يىقىلغان چاغدا جىنلارغا ئېنىق بولدىكى، ئەگەر ئۇلار غەيبنى بىلىدىغان بولسا ئىدى، خار قىلغۇچى ئازابتا (يەنى شۇنچە ئۇزاق ۋاقىت ئېغىر ئەمگەكتە) قالمىغان بولاتتى.');
INSERT INTO "saleh" VALUES(34,15,'34:15',' شۈبھىسىزكى، سەبەئ قەۋمىگە ئۇلارنىڭ تۇرىدىغان جايىدا (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان بۈيۈك) دەلىل بار ئىدى، ئۇلارنىڭ ئوڭ تەرىپىدە بىر تۈركۈم باغلار، سول تەرىپىدە بىر تۈركۈم باغلار بار ئىدى. (ئۇلارغا ئېيتتۇقكى) «پەرۋەردىگارىڭلار بەرگەن رىزىقتىن يەڭلار ۋە ئۇنىڭغا شۈكۈر قىلىڭلار، (سىلەرنىڭ جايىڭلار) بەلەن جايدۇر، (سىلەرگە رىزىق بەرگەن پەرۋەردىگارىڭلار شۈكۈر قىلغۇچىنى) مەغپىرەت قىلغۇچى پەرۋەردىگاردۇر. ');
INSERT INTO "saleh" VALUES(34,16,'34:16',' ئۇلار (شۈكۈردىن) باش تارتتى، شۇنىڭ بىلەن ئۇلارغا قاتتىق سەلنى ئەۋەتتۇق. ئۇلارنىڭ باغلىرىنى ئاچچىق مېۋىلىك، يۇلغۇنلۇق، ئاز ـ تولا سىدرىلىق (يەنى ياۋا چىلانلىق) باغلارغا ئايلاندۇردۇق.');
INSERT INTO "saleh" VALUES(34,17,'34:17',' ئۇلار كۇفرىلىق قىلغانلىقلىرى ئۈچۈن مۇشۇنداق جازالىدۇق، بىز پەقەت كۇفرىلىق قىلغۇچىلارنىلا جازالايمىز.');
INSERT INTO "saleh" VALUES(34,18,'34:18',' ئۇلار بىلەن بىز بەرىكەتلىك قىلغان قىشلاقلار ئارىسىدا، كۆرۈنۈپ تۇرىدىغان (تۇتىشىپ كەتكەن) نۇرغۇن قىشلاقلارنى بەرپا قىلدۇق، ئۇلارنىڭ ئارىسىدىكى مۇساپىنى ئۆلچەملىك قىلدۇق (يەنى سەپەردە يېمەك ـ ئىچمەك ئېلىۋالمىسىمۇ بولىدىغان، ئەتىگەن چىققان يولۇچى چۈشتە بىر ئۆتەڭگە يېتىپ بارسا، كەچتە بىر ئۆتەڭگە يېتىپ بارىدىغان قىلدۇق)، (ئۇلارغا ئېيتتۇقكى) «ئۇلارنىڭ ئارىسىدا كېچىلەردە ۋە كۈندۈزلەردە تىنچ ـ ئامان مېڭىڭلار!».');
INSERT INTO "saleh" VALUES(34,19,'34:19',' ئۇلار: «پەرۋەردىگارىمىز! سەپەرلىرىمىز ئارىسىنى (يەنى ئۆتەڭلار ئارىسىدىكى مۇساپىنى) يىراق قىلغىن» دېدى. ئۇلار ئۆزلىرىگە زۇلۇم قىلدى. ئۇلارنى بىز (كېيىنكى كىشىلەرگە) قىسسە قىلىپ قالدۇردۇق. ئۇلارنى تىتما - تىتما قىلىۋەتتۇق، بۇنىڭدا ھەر بىر (بالاغا) سەۋر قىلغۇچى، (نېمەتكە) شۈكۈر قىلغۇچى ئۈچۈن نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(34,20,'34:20',' ئىبلىس ئۇلارنى (ئازدۇرۇشتىن ئىبارەت) گۇمانىنى ھەقىقەتەن ئىشقا ئاشۇردى. چۈنكى بىر تۈركۈم مۆمىنلەردىن باشقا ئۇلارنىڭ ھەممىسى ئىبلىسقا ئەگەشتى.');
INSERT INTO "saleh" VALUES(34,21,'34:21',' ئىبلىسنىڭ ئۇلار (يەنى ئەگەشكۈچىلەر) ئۈستىدىن (ۋەسۋەسە قىلىش بىلەن) ھۆكۈمرانلىق قىلىشى پەقەت بىزنىڭ ئاخىرەتكە ئىمان ئېيتقۇچىلار بىلەن ئۇنىڭغا شەك كەلتۈرگۈچىلەرنى بىلىۋېلىشىمىز ئۈچۈندۇر. پەرۋەردىگارىڭ ھەممە نەرسىنى كۆزەتكۈچىدۇر.');
INSERT INTO "saleh" VALUES(34,22,'34:22',' ئېيتقىنكى، «سىلەر ئاللاھنى قويۇپ مەبۇد دەپ قارىغىنىڭلارنى چاقىرىڭلار، ئۇلار ئاسمانلاردا ۋە زېمىندا زەررە چاغلىق نەرسىگە ئىگە بولالمايدۇ، ئۇلارنىڭ ئاسمان ـ زېمىننىڭ (يارىتىلىشى ۋە باشقۇرۇلۇشىدا ئاللاھقا) ھېچ شېرىكچىلىگى يوق، ئۇلارنىڭ ئىچىدە ئاللاھقا (ئاسمان ـ زېمىننى ئىدارە قىلىشتا ياردەملىشىدىغان) ھېچ ياردەمچىمۇ يوق».');
INSERT INTO "saleh" VALUES(34,23,'34:23','ئاللاھ رۇخسەت قىلغان ئادەمدىن باشقىغا ئاللاھنىڭ دەرگاھىدا شاپائەت پايدا بەرمەيدۇ. شاپائەت قىلغۇچىلارنىڭ دىللىرىدىن قورقۇنچ كۆتۈرۈلگەندە، ئۇلار ئاندىن (بەزىسى بەزىسىگە): «پەرۋەردىگارىڭلار (شاپائەت توغرۇلۇق) نېمە دېدى؟» دەيدۇ. ئۇلار (جاۋابەن): «ھەقنى (دېدى) (يەنى مۆمىنلەرگە شاپائەت قىلىشقا رۇخسەت قىلدى)» دەيدۇ. ئاللاھ يۈكسەكتۇر، كاتتىدۇر.');
INSERT INTO "saleh" VALUES(34,24,'34:24',' ئېيتقىنكى، «سىلەرگە ئاسمانلاردىن ۋە زېمىندىن كىم رىزىق بېرىپ تۇرىدۇ؟» ئېيتقىنكى، «ئاللاھ (رىزىق بېرىپ تۇرىدۇ)، شۈبھىسىزكى، بىز ياكى سىلەر چوقۇم ھىدايەتتىدۇرمىز، يا روشەن گۇمراھلىقتىدۇرمىز».');
INSERT INTO "saleh" VALUES(34,25,'34:25','ئېيتقىنكى، «بىزنىڭ قىلغان گۇناھىمىز ئۈچۈن سىلەر سوراققا تارتىلمايسىلەر، سىلەرنىڭ قىلمىشىڭلار ئۈچۈن بىز سوراققا تارتىلمايمىز».');
INSERT INTO "saleh" VALUES(34,26,'34:26','ئېيتقىنكى، «پەرۋەردىگارىمىز بىزنى (قىيامەت كۈنى) يىغىدۇ. ئاندىن بىزنىڭ ئارىمىزدا ھەقلىق بىلەن ھۆكۈم چىقىرىدۇ، ئاللاھ ئادىل ھۆكۈم قىلغۇچىدۇر، (خالايىقنىڭ ئەھۋالىنى) بىلگۈچىدۇر».');
INSERT INTO "saleh" VALUES(34,27,'34:27',' ئېيتقىنكى، «ماڭا ئېيتىپ بېقىڭلارچۇ، سىلەرنىڭ ئاللاھنىڭ شېرىكى دەپ ئاتىۋالغىنىڭلار ھەرگىزمۇ ئۇنداق ئەمەس، بەلكى ئۇ ـ ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر».');
INSERT INTO "saleh" VALUES(34,28,'34:28',' سېنى بىز پەقەت پۈتۈن ئىنسانلار ئۈچۈن (مۆمىنلەرگە جەننەت بىلەن) خۇشخەۋەر بەرگۈچى ۋە (كاپىرلارنى ئازاب بىلەن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق. لېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(34,29,'34:29',' ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار، بۇ ۋەدە (يەنى سىلەر بىزنى قورقۇتۇۋاتقان ئازاب) قاچان ئىشقا ئاشىدۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(34,30,'34:30',' ئېيتقىنكى، «سىلەرگە (ئازاب ئۈچۈن) ۋەدە قىلىنغان بىر كۈن باركى، ئۇنىڭدىن بىردەممۇ ئىلگىرى ـ كېيىن بولمايسىلەر».');
INSERT INTO "saleh" VALUES(34,31,'34:31',' كاپىرلار: «بۇ قۇرئانغا ۋە ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ھەرگىز ئىشەنمەيمىز» دەيدۇ. ئەگەر زالىملارنى پەرۋەردىگارىڭنىڭ دەرگاھىدا توختىتىپ قويۇلغان چاغدا بەزىسى بەزىسى بىلەن جېدەللىشىۋاتقاندا كۆرسەڭ ئىدىڭ (ئەلۋەتتە قورقۇنچلۇق ھالنى كۆرەتتىڭ)، ئاجىزلار (يەنى ئەگەشكۈچىلەر) كۈچلۈكلەر (يەنى ئەگەشتۈرگۈچىلەر) گە: «ئەگەر سىلەر بولمىساڭلار بىز چوقۇم ئىمان ئېيتاتتۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(34,32,'34:32',' كۈچلۈكلەر ئاجىزلارغا: «سىلەرگە ھىدايەت كەلگەندىن كېيىن بىز سىلەرنى ئۇنىڭدىن توستۇقمۇ؟ ھەرگىز توسمىدۇق، سىلەر ئۆزۈڭلار گۇناھ قىلغۇچى بولدۇڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(34,33,'34:33',' ئاجىزلار كۈچلۈكلەرگە: «ھەرگىز ئۇنداق ئەمەس، (سىلەرنىڭ بىزگە) كېچە ـ كۈندۈز قىلغان ھىيلە ـ مىكرىڭلار (بىزنى ئىماندىن توستى)، ئۆز ۋاقتىدا سىلەر بىزنى ئاللاھنى ئىنكار قىلىشقا ۋە ئۇنىڭغا شېرىك كەلتۈرۈشكە بۇيرۇيتتۇڭلار» دەيدۇ. ئازابنى كۆرگەن چاغدا، ئۇلار (ھەر ئىككى گۇرۇھ ئىمان ئېيتمىغانلىقلىرىغا) ئىچىدە پۇشايمان قىلىدۇ، كاپىرلارنىڭ بويۇنلىرىغا تاقاقلارنى سالىمىز، ئۇلارغا پەقەت قىلمىشلىرىنىڭ جازاسى بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(34,34,'34:34',' قايسى شەھەرگە بولمىسۇن، بىرەر ئاگاھلاندۇرغۇچى ئەۋەتسەكلا ئۇنىڭ دۆلەتمەن ئادەملىرى: «شۈبھىسىزكى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ئىنكار قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(34,35,'34:35','ئۇلار يەنە: «بىزنىڭ ماللىرىمىز ۋە بالىلىرىمىز ئەڭ كۆپ، بىز ئازابلانمايمىز» دېدى.');
INSERT INTO "saleh" VALUES(34,36,'34:36','ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم كىمنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (كىمنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، لېكىن كىشىلەرنىڭ تولىسى (بۇنى) ئۇقمايدۇ».');
INSERT INTO "saleh" VALUES(34,37,'34:37',' ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى بىزگە يېقىنلاشتۇرالمايدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارغا قىلغان ئەمەللىرى ئۈچۈن ھەسسىلەپ ساۋاب بېرىلىدۇ، ئۇلار (جەننەتنىڭ) ئېسىل ئۆيلىرىدە (ھەر قانداق كۆڭۈلسىزلىكتىن) ئەمىن بولغان ھالدا تۇرىدۇ.');
INSERT INTO "saleh" VALUES(34,38,'34:38','بىزنىڭ ئايەتلىرىمىزگە كۈچىنىڭ بېرىچە قارشى تۇرغانلار ئازابقا دۇچار قىلىنغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(34,39,'34:39',' ئېيتقىنكى، «ھەقىقەتەن، پەرۋەردىگارىم قايسى بەندىسىنىڭ رىزقىنى كەڭ قىلىشنى خالىسا ئۇنى كەڭ قىلىدۇ، (قايسى بەندىسىنىڭ رىزقىنى تار قىلىشنى خالىسا ئۇنى) تار قىلىدۇ، (ئاللاھنىڭ يولىدا) بەرگەن نەرسەڭلارنىڭ ئورنىنى ئاللاھ تولدۇرۇپ بېرىدۇ، ئۇ رىزىق بەرگۈچىلەرنىڭ ياخشىسىدۇر».');
INSERT INTO "saleh" VALUES(34,40,'34:40',' ئۇ كۈندە ئاللاھ ئۇلار (يەنى مۇشرىكلار) نىڭ ھەممىسىنى (ھېساب ئېلىش ئۈچۈن) يىغىدۇ، ئاندىن پەرىشتىلەرگە: «بۇ كىشىلەر سىلەرگە چوقۇنغانمىدى؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(34,41,'34:41',' ئۇلار ئېيتىدۇ: «سەن پاكتۇرسەن، بىزنىڭ دوستىمىز ئۇلار ئەمەس، سەن. (ئۇلار بىزگە چوقۇنمايتتى) بەلكى جىنغا چوقۇناتتى. ئۇلارنىڭ تولىسى جىنغا ئىمان كەلتۈرەتتى».');
INSERT INTO "saleh" VALUES(34,42,'34:42',' بۈگۈن سىلەر بىر ـ بىرىڭلارغا پايدا ـ زىيان يەتكۈزەلمەيسىلەر، بىز (ئاللاھتىن غەيرىيگە چوقۇنغۇچى) زالىملارغا: «سىلەر چىنپۈتمىگەن دوزاخ ئازابىنى تېتىڭلار» دەيمىز.');
INSERT INTO "saleh" VALUES(34,43,'34:43',' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز ئوقۇپ بېرىلگەندە، ئۇلار: «بۇ پەقەت سىلەرنى ئاتا ـ بوۋاڭلار چوقۇنغان بۇتلارغا چوقۇنۇشتىن توسماقچى بولغان ئادەمدۇر» دەيدۇ ھەمدە ئۇلار: «بۇ پەقەت توقۇلغان يالغان سۆزدۇر» دەيدۇ. كاپىرلارغا ھەقىقەت يېتىپ كەلگەندە، ئۇلار: «بۇ پەقەت ئوپئوچۇق سېھىردۇر» دېدى.');
INSERT INTO "saleh" VALUES(34,44,'34:44',' ئۇلارغا ئوقۇيدىغان ھېچقانداق كىتابلارنى ئاتا قىلغىنىمىز يوق. سەندىن ئىلگىرى ئۇلارغا ھېچقانداق پەيغەمبەرمۇ ئەۋەتكىنىمىز يوق.');
INSERT INTO "saleh" VALUES(34,45,'34:45',' ئۇلاردىن ئىلگىرى ئۆتكەنلەر (ھەقنى) ئىنكار قىلغان، ئۇلار (يەنى مەككە كۇففارلىرى) ئۇلار (يەنى ئىلگىرىكىلەر) ئېرىشكەن كۈچ ـ قۇۋۋەتنىڭ ئوندىن بىرىگىمۇ ئېرىشمىگەن، ئۇلار مېنىڭ پەيغەمبەرلىرىمنى ئىنكار قىلدى. ئۇلارغا مېنىڭ جازايىم قانداق بولدى؟');
INSERT INTO "saleh" VALUES(34,46,'34:46','ئېيتقىنكى، «مەن سىلەرگە بىر ئىشنى تەۋسىيە قىلىمەن: سىلەر خۇدالىق ئۈچۈن ئىككىدىن، يا بىردىن تۇرۇڭلار (يەنى ئاللاھنىڭ رازىلىقى ئۈچۈن يىغىلىپ ياكى يەككە ھالدا ھەقنى ئىزدەڭلار)، ئاندىن پىكىر يۈرگۈزۈڭلار». ھەمراھىڭلاردا (يەنى مۇھەممەدتە) ھېچ مەجنۇنلۇق يوق، پەقەت ئۇ قاتتىق ئازاب نازىل بولۇشتىن ئىلگىرى سىلەرگە ئەۋەتىلگەن بىر ئاگاھلاندۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(34,47,'34:47',' ئېيتقىنكى، «سىلەردىن ھېچقانداق ھەق تەلەپ قىلغىنىم يوق، ئۇ سىلەرگە بولسۇن؛ ئەجرىمنى پەقەت ئاللاھتىن تىلەيمەن. ئاللاھ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر».');
INSERT INTO "saleh" VALUES(34,48,'34:48',' ئېيتقىنكى، «پەرۋەردىگارىم، ھەقىقەتەن ھەق ئارقىلىق (باتىلنى) ئاتىدۇ (يەنى پاكىتنى روشەن بايان قىلىدۇ)، ئاللاھ غەيبنى تامامەن بىلگۈچىدۇر».');
INSERT INTO "saleh" VALUES(34,49,'34:49','ئېيتقىنكى، «ھەق (يەنى ئىسلام) كەلدى (باتىل يوقالدى)، باتىل (بىرەر مەخلۇقنى) پەيدا قىلالمايدۇ، (يوقالغان بىرەر مەخلۇقنى) ئەسلىگە كەلتۈرەلمەيدۇ».');
INSERT INTO "saleh" VALUES(34,50,'34:50',' ئېيتقىنكى، «ئەگەر ئازسام ئازغانلىقىمنىڭ زىيىنى ئۆزۈمگىدۇر، ئەگەر ھىدايەت تاپسام (بۇ) پەرۋەردىگارىمنىڭ ماڭا ۋەھىي قىلغان قۇرئانىنىڭ سەۋەبىدىندۇر، ئاللاھ ھەقىقەتەن ئاڭلاپ تۇرغۇچىدۇر، يېقىندۇر».');
INSERT INTO "saleh" VALUES(34,51,'34:51',' ئەگەر سەن ئەينى ۋاقىتتا ئۇلارنىڭ قورقۇپ قاچىدىغان يەر تاپالمايۋاتقانلىقىنى، دوزاخقا (ئېلىپ بېرىلىش ئۈچۈن مەھشەرگاھقا) يېقىن جايدا تۇتۇلۇۋاتقانلىقىنى كۆرسەڭ (ئەلۋەتتە قورقۇنچلۇق ھالىنى كۆرەتتىڭ).');
INSERT INTO "saleh" VALUES(34,52,'34:52',' ئۇلار (ئازابنى كۆرگەندە): «ئىمان ئېيتتۇق» دەيدۇ، ئۇلار قانداقمۇ يىراق جايدىن ئىماننى قولغا كەلتۈرەلەيدۇ.');
INSERT INTO "saleh" VALUES(34,53,'34:53',' ئۇلار ئىلگىرى ئۇنى (يەنى پەيغەمبەرنى) ئىنكار قىلغان ئىدى. ئۇلار غەيب (ئىشلىرى) ئۈستىدە يىراق جايدىن قارىسىغا سۆزلەيدۇ.');
INSERT INTO "saleh" VALUES(34,54,'34:54','ئۇلار بىلەن ئۇلارنىڭ ئارزۇ قىلغان نەرسىلىرى (يەنى ئىمان ئېيتىش ۋە جەننەتكە كىرىش) ئارىسىدا توسالغۇ پەيدا قىلىندى. ئۇلارغا ئىلگىرى ئۇلارغا ئوخشاشلارغا قىلىنغاندەك قىلىندى. چۈنكى ئۇلار ھازىر ئىمان كەلتۈرگەن نەرسىلىرى ئۈستىدە (دۇنيادىكى چاغلىرىدا) چوڭقۇر شەكتە ئىدى.');
INSERT INTO "saleh" VALUES(35,1,'35:1','جىمى ھەمدۇسانا ئاسمانلارنى ۋە زېمىننى ئۆرنەكسىز ياراتقۇچى، پەرىشتىلەرنى ئىككى قاناتلىق، ئۈچ قاناتلىق، تۆت قاناتلىق ئەلچىلەر قىلغۇچى ئاللاھقا خاستۇر! ئاللاھ يارىتىشتا خالىغىنىنى زىيادە قىلىدۇ، ئاللاھ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(35,2,'35:2',' ئاللاھ رەھمىتىدىن كىشىلەرگە ئاچقان نەرسىنى ھەرگىزمۇ توسۇپ قالالىغۇچى بولمايدۇ، ئاللاھ رەھمىتىدىن توسقان نەرسىنى، ئاللاھ توسقاندىن كېيىن، ئۇنى ھېچ قويۇپ بەرگۈچى بولمايدۇ، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(35,3,'35:3',' ئى ئىنسانلار! ئاللاھنىڭ سىلەرگە بەرگەن نېمىتىنى ياد ئېتىڭلار، ئاللاھتىن باشقا سىلەرگە ئاسماندىن ۋە زېمىندىن رىزىق بېرىپ تۇرىدىغان ياراتقۇچى بارمۇ؟ ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، (ئاللاھنىڭ ئىبادىتىدىن) قانداقمۇ باش تارتىسىلەر؟.');
INSERT INTO "saleh" VALUES(35,4,'35:4','(ئى مۇھەممەد!) ئەگەر ئۇلار سېنى ئىنكار قىلسا (قايغۇرۇپ كەتمە)، ھەقىقەتەن سەندىن ئىلگىرىكى پەيغەمبەرلەرمۇ ئىنكار قىلىنغان، ھەممە ئىش ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(35,5,'35:5',' ئى ئىنسانلار! ئاللاھنىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، سىلەرنى ھەرگىز دۇنيا تىرىكچىلىكى ئالداپ قويمىسۇن، شەيتاننىڭ سىلەرنى ئاللاھنىڭ ئەپۇسىنىڭ كەڭلىكى بىلەن ئالدىشىغا يول قويماڭلار.');
INSERT INTO "saleh" VALUES(35,6,'35:6',' شەيتان ھەقىقەتەن سىلەرگە دۈشمەندۇر، ئۇنى دۈشمەن تۇتۇڭلار، شەيتان ئۆزىنىڭ تەۋەلىرىنى ئەھلى دوزاختىن بولۇشقا چاقىرىدۇ.');
INSERT INTO "saleh" VALUES(35,7,'35:7',' كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار مەغپىرەتكە ۋە كاتتا ساۋابقا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(35,8,'35:8',' ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن، شۇنىڭ بىلەن ئۇنى چىرايلىق ھېسابلىغان كىشى (ھىدايەت تاپقان كىشى بىلەن ئوخشاشمۇ؟) ئاللاھ ھەقىقەتەن خالىغان كىشىنى گۇمراھ قىلىدۇ (خالىغان كىشىنى ھىدايەت قىلىدۇ). شۇڭا سەن ئۇلار ئۈچۈن (يەنى ئىمان ئېيتمىغانلىقلىرى ئۈچۈن) قايغۇرۇپ ئۆزۈڭنى ھالاك قىلىۋالمىغىن، ئاللاھ ھەقىقەتەن ئۇلارنىڭ قىلمىشلىرىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(35,9,'35:9','ئاللاھ شاماللارنى ئەۋەتىپ (ئۇنىڭ بىلەن) بۇلۇتنى قوزغايدۇ، بىز بۇلۇتلارنى ئۆلۈك (يەنى قاغجىراق) بىر يەرگە ھەيدەيمىز (بۇلۇتتىن يامغۇر ياغدۇرىمىز)، شۇ يامغۇر بىلەن ئۆلگەن زېمىننى تىرىلدۈرىمىز. ئۆلۈكلەرنىڭ تىرىلدۈرۈلۈشى ئەنە شۇنداقتۇر.');
INSERT INTO "saleh" VALUES(35,10,'35:10',' كىمكى (دۇنيادا) شەرەپ ئىزدەيدىكەن، شەرەپنىڭ ھەممىسى ئاللاھقا مەنسۇپ (ئۇنى ئاللاھتىن تىلىسۇن). ياخشى سۆز ئاللاھنىڭ دەرگاھىغا ئۆرلەيدۇ، ياخشى ئەمەل ئۇنى كۆتۈرىدۇ، (پەيغەمبەرگە قارشى) ھىيلە ـ مىكىرلەرنى تۈزىدىغانلار قاتتىق ئازابقا دۇچار بولىدۇ، ئۇلارنىڭ ھىيلە ـ مىكرى ئىشقا ئاشمايدۇ.');
INSERT INTO "saleh" VALUES(35,11,'35:11','ئاللاھ سىلەرنى (يەنى ئەسلىڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن ياراتتى. ئاندىن ئابىمەنىيدىن پەيدا قىلدى. ئاندىن سىلەرنى (ئەر ـ ئايالدىن ئىبارەت) بىر جۈپ قىلدى، ھەر قانداق ئايالنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى ئاللاھنىڭ ئىلمىنىڭ سىرتىدا ئەمەس، ھەرقانداق ئادەمنىڭ ئۆمرىنىڭ ئۇزۇن ياكى قىسقا بولۇشى لەۋھۇلمەھپۇزدا يېزىلغان، بۇ ھەقىقەتەن ئاللاھقا ئاسان.');
INSERT INTO "saleh" VALUES(35,12,'35:12',' ئىككى دېڭىز ئوخشاش ئەمەس. بۇنىڭ سۈيى تاتلىق، تەملىك بولۇپ (گالدىن) سىلىق ئۆتىدۇ، بۇ ناھايىتى ئاچچىق، تۇزلۇق، ئۇلارنىڭ ھەر ئىككىسىدىن يېڭى گۆشلەرنى (يەنى بېلىقلارنى) يەيسىلەر، سىلەر تاقايدىغان زىننەت بۇيۇملىرى (يەنى ئۈنچە ـ مارجانلار) نى چىقىرىسىلەر. (ئاللاھنىڭ ئەمرى بىلەن يۈك ـ تاق، يېمەك ـ ئىچمەكلەر قاچىلانغان) كېمىلەرنىڭ دېڭىزدا دولقۇن يېرىپ كېتىۋاتقانلىقىنى كۆرىسەن. بۇ ئاللاھنىڭ نېمىتىنى تەلەپ قىلىپ شۈكۈر قىلىشىڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(35,13,'35:13',' ئاللاھ كېچىنى كۈندۈزگە كىرگۈزىدۇ، كۈندۈزنى كېچىگە كىرگۈزىدۇ (شۇنىڭ بىلەن كېچە ـ كۈندۈزنىڭ ئۇزۇن ـ قىسقا بولۇشى جايلار، پەسىللەر بويىچە نۆۋەتلىشىپ تۇرىدۇ)، ئاللاھ كۈن بىلەن ئاينى (بەندىلىرىنىڭ مەنپەئىتىگە) بويسۇندۇرۇپ بەردى. ھەر ئىككىلىسى مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق ئۇنىڭغا خاستۇر، ئاللاھنى قويۇپ چوقۇنۇۋاتقان مەبۇدلىرىڭلار قىلچىلىك نەرسىگە ئىگە ئەمەس.');
INSERT INTO "saleh" VALUES(35,14,'35:14',' ئەگەر ئۇلارنى چاقىرساڭلار، سىلەرنىڭ چاقىرغىنىڭلارنى ئاڭلىمايدۇ، ئاڭلىغان تەقدىردىمۇ سىلەرگە جاۋاپ قايتۇرالمايدۇ، قىيامەت كۈنى ئۇلار (ئاللاھقا ئۇلارنى) شېرىك قىلغانلىقىڭلارنى ئىنكار قىلىدۇ، (مۇشرىكلارنىڭ ۋە ئۇلارنىڭ مەبۇدلىرىنىڭ قىيامەت كۈنىدىكى ئەھۋالىنى ھېچ ئەھەدى) ساڭا ھەممىدىن خەۋەردار زاتتەك (يەنى ئاللاھتەك) ئېيتىپ بېرەلمەيدۇ.');
INSERT INTO "saleh" VALUES(35,15,'35:15',' ئى ئىنسانلار! سىلەر ئاللاھقا موھتاجسىلەر، ئاللاھ (ھەممىدىن) بىھاجەتتۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(35,16,'35:16',' ئەگەر ئاللاھ خالىسا، سىلەرنى ھالاك قىلىپ (ئورنۇڭلارغا) يېڭى بىر خەلقنى پەيدا قىلىدۇ.');
INSERT INTO "saleh" VALUES(35,17,'35:17','بۇ ئاللاھقا قىيىن ئەمەس');
INSERT INTO "saleh" VALUES(35,18,'35:18',' ھېچبىر گۇناھكار ئادەم بىراۋنىڭ گۇناھىنى ئۈستىگە ئالالمايدۇ، گۇناھى ئېغىر بىر ئادەم باشقا بىراۋنى ئۆز گۇناھىنى ئۈستىگە ئېلىۋېلىشقا چاقىرسا، ئۇ يېقىن تۇغقىنى بولغان تەقدىردىمۇ، ئۇنىڭ گۇناھىدىن ئازراق نەرسىنى ئۈستىگە ئېلىۋالالمايدۇ، سەن پەقەت پەرۋەردىگارىنى كۆرمەي تۇرۇپ ئۇنىڭدىن قورقىدىغانلارنى، نامازنى ئادا قىلىدىغانلارنى ئاگاھلاندۇرىسەن، كىمكى (گۇناھلاردىن) پاكلىنىدىكەن، ئۇ ئۆزى ئۈچۈن پاكلانغان بولىدۇ. ئاخىر قايتىدىغان جاي ئاللاھنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(35,19,'35:19',' كور ئادەم بىلەن كۆزى ساق ئادەم (يەنى كاپىر بىلەن مۆمىن) باراۋەر بولمايدۇ.');
INSERT INTO "saleh" VALUES(35,20,'35:20',' زۇلمەت بىلەن نۇر (يەنى باتىل بىلەن ھەق) باراۋەر بولمايدۇ.');
INSERT INTO "saleh" VALUES(35,21,'35:21','سايە بىلەن ئىسسىق (يەنى جەننەت بىلەن دوزاخ) باراۋەر بولمايدۇ.');
INSERT INTO "saleh" VALUES(35,22,'35:22','تىرىكلەر بىلەن ئۆلۈكلەر (يەنى مۆمىنلەر بىلەن كاپىرلار) باراۋەر بولمايدۇ، شۈبھىسىزكى، ئاللاھ (ھەق دەۋەتنى) خالىغان كىشىلەرگە ئاڭلىتىدۇ. سەن قەبرىلەردىكىلەرگە ئاڭلىتالمايسەن.');
INSERT INTO "saleh" VALUES(35,23,'35:23',' سەن پەقەت بىر ئاگاھلاندۇرغۇچىسەن.');
INSERT INTO "saleh" VALUES(35,24,'35:24',' ھەقىقەتەن بىز سېنى ھەق (دىن) بىلەن (مۆمىنلەرگە) خۇشخەۋەر بەرگۈچى، (كاپىرلارغا) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق، قانداقلا بىر ئۈممەت بولمىسۇن، ئۇنىڭغا پەيغەمبەر كەلگەن.');
INSERT INTO "saleh" VALUES(35,25,'35:25',' ئۇلار سېنى ئىنكار قىلسا، ئۇلاردىن بۇرۇن ئۆتكەن ئۈممەتلەرمۇ (ئۆزلىرىنىڭ پەيغەمبەرلىرىنى) ئىنكار قىلغان، پەيغەمبەرلىرى ئۇلارغا روشەن مۆجىزىلەرنى، (ئىبراھىم ئەلەيھىسسالامغا نازىل قىلىنغان سەھىپىلەرگە ئوخشاش) سەھىپىلەرنى، (تەۋرات، ئىنجىلدەك) نۇرلۇق كىتابلارنى ئېلىپ كەلدى.');
INSERT INTO "saleh" VALUES(35,26,'35:26',' ئاندىن كاپىرلارنى جازالىدىم، (ئۇلارغا بەرگەن) ئازابىم قانداق ئىكەن؟ (يەنى ئۇلارغا قانداق قاتتىق ئازاب قىلدىم؟)');
INSERT INTO "saleh" VALUES(35,27,'35:27','كۆرمەمسەنكى، ئاللاھ بۇلۇتتىن يامغۇر ياغدۇردى. ئۇنىڭ بىلەن تۈرلۈك، رەڭگارەڭ مېۋىلەرنى چىقاردى، (شۇنىڭدەك ئاللاھ تاغلارنى ياراتتى) تاغلارنىڭ ئاق، قىزىللىرى، ھەر خىل رەڭدىكى چىپارلىرى ۋە قاپقارىلىرىمۇ بار.شۇنىڭدەك ئىنسانلارنى، ھايۋانلارنى، چاھارپايلارنىمۇ خىلمۇ خىل رەڭلىك قىلىپ ياراتتى، ئاللاھنىڭ بەندىلىرى ئىچىدە ئاللاھدىن پەقەت ئالىملارلا قورقىدۇ،ئاللاھ ھەقىقەتەن غالىپتۇر، (بەندىلەر ئىچىدە تەۋبە قىلغانلارنى)مەغپىرەت قىلغۇچىدۇر. [28] ');
INSERT INTO "saleh" VALUES(35,28,'35:28',' شۈبھىسىزكى، ئاللاھنىڭ كىتابىنى تىلاۋەت قىلىپ تۇرىدىغانلار، نامازنى ئادا قىلىدىغانلار ۋە بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن (ئاللاھنىڭ رازىلىقى ئۈچۈن) يوشۇرۇن ۋە ئاشكارا سەرپ قىلىدىغانلار كاسات بولمايدىغان تىجارەتنى ئۈمىد قىلىدۇ');
INSERT INTO "saleh" VALUES(35,29,'35:29',' ئاللاھ ئۇلارغا ئەجىرلىرىنى تولۇق بېرىدۇ ۋە مەرھەمىتىدىن ئۇلارغا ئاشۇرۇپ بېرىدۇ، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر. ساڭا بىز ۋەھىي قىلغان كىتاب (يەنى قۇرئان) ھەقتۇر. ئىلگىرىكى كىتابلارنىڭ (راستلىقىنى) ئىسپاتلىغۇچىدۇر، ئاللاھ ھەقىقەتەن بەندىلىرىنى تولۇق بىلگۈچىدۇر، كۆرۈپ تۇرغۇچىدۇر[31].');
INSERT INTO "saleh" VALUES(35,30,'35:30',' ئاللاھ ئۇلارغا ئەجىرلىرىنى تولۇق بېرىدۇ ۋە مەرھەمىتىدىن ئۇلارغا ئاشۇرۇپ بېرىدۇ، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(35,31,'35:31','ساڭا بىز ۋەھىي قىلغان كىتاب (يەنى قۇرئان) ھەقتۇر. ئىلگىرىكى كىتابلارنىڭ (راستلىقىنى) ئىسپاتلىغۇچىدۇر، ئاللاھ ھەقىقەتەن بەندىلىرىنى تولۇق بىلگۈچىدۇر، كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(35,32,'35:32',' ئاندىن بىز كىتاب (يەنى قۇرئان) نى بەندىلىرىمىزدىن بىز تاللىغان كىشىلەرگە مىراس قىلىپ بەردۇق. ئۇلارنىڭ بەزىسى ئۆزىگە زۇلۇم قىلغۇچىدۇر (يەنى قۇرئاننى تىلاۋەت قىلغان بىلەن بەك ئەمەل قىلىپ كەتمەيدۇ)، بەزىسى ئوتتۇراھالدۇر (يەنى كۆپ چاغلاردا قۇرئانغا ئەمەل قىلىپ، قىسمەن چاغلاردا ئەمەل قىلمايدۇ) ۋە بەزىسى ئاللاھنىڭ ئىزنى بويىچە ياخشى ئىشلارنى قىلىشقا ئالدىرىغۇچىدۇر. ئەنە شۇ كاتتا مەرھەمەتتۇر.');
INSERT INTO "saleh" VALUES(35,33,'35:33',' ئۇلار ھەمىشە تۇرىدىغان جەننەتلەرگە كىرىدۇ. ئۇلار جەننەتلەردە ئالتۇن بىلەيزۈكلەرنى، مەرۋايىتلارنى تاقايدۇ، ئۇلارنىڭ كىيىمى يىپەكتىن بولىدۇ. ');
INSERT INTO "saleh" VALUES(35,34,'35:34',' ئۇلار ئېيتتى: «جىمى ھەمدۇسانا بىزدىن غەم ـ قايغۇنى كەتكۈزۈۋەتكەن ئاللاھقا مەنسۇپتۇر! بىزنىڭ پەرۋەردىگارىمىز ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(35,35,'35:35','ئاللاھ مەرھەمىتىدىن بىزنى جەننەتتە تۇرغۇزدى. جەننەتتە بىز ھەرگىز جاپا تارتمايمىز، ھەرگىز چارچىمايمىز».');
INSERT INTO "saleh" VALUES(35,36,'35:36',' كاپىرلار دوزاخ ئازابىغا دۇچار بولىدۇ، ئۇلارنىڭ ئۆلۈپ (ئارام تېپىپ قالماسلىقى ئۈچۈن) جانلىرى ئېلىنمايدۇ، ئۇلاردىن ئازابمۇ يېنىكلىتىلمەيدۇ، كۇفرىدا ھەددىدىن ئاشقان ھەر ئىنساننى ئەنە شۇنداق جازالايمىز.');
INSERT INTO "saleh" VALUES(35,37,'35:37','ئۇلار دوزاختا: «پەرۋەردىگارىمىز! بىزنى چىقىرىۋەتسەڭ (دۇنيادىكى ۋاقىتتا) قىلغان ئەمەللىرىمىزدىن باشقا ئەمەللەرنى قىلساق» دەپ يالۋۇرۇپ توۋلايدۇ. (ئاللاھ ئېيتىدۇ:) «سىلەرگە ئۆمرۈڭلارنى ئۇزۇن قىلىپ، ۋەز ـ نەسىھەت ئالىدىغان ئادەم ۋەز ـ نەسىھەت ئالالىغۇدەك ۋاقىت بەرمىدىممۇ؟ سىلەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلدىغۇ، ئەمدى ئازابنى تېتىڭلار، زالىملارغا ھېچقانداق ياردەم بەرگۈچى يوق».');
INSERT INTO "saleh" VALUES(35,38,'35:38',' ئاللاھ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيبنى بىلگۈچىدۇر. ئاللاھ ھەقىقەتەن دىللاردىكى سىرلارنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(35,39,'35:39',' ئاللاھ سىلەرنى زېمىندا ئورۇنباسارلار قىلدى، كىمكى كاپىر بولىدىكەن، ئۇنىڭ كۇفرىنىڭ زىيىنى ئۆزىگە بولىدۇ، كاپىرلارنىڭ كۇفرى پەرۋەردىگارىنىڭ دەرگاھىدا پەقەت غەزەپنىلا زىيادە قىلىدۇ (يەنى ئۇلارنىڭ كۇفرى ئۇلارنى ئاللاھنىڭ رەھمىتىدىن تېخىمۇ يىراقلاشتۇرىدۇ)، كاپىرلارنىڭ كۇفرى ئۇلارغا پەقەت ھالاكەتنىلا زىيادە قىلىدۇ. ');
INSERT INTO "saleh" VALUES(35,40,'35:40',' ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ؟ سىلەر ئاللاھنى قويۇپ چوقۇنۇۋاتقان مەبۇدلىرىڭلار (نېمە سەۋەبتىن چوقۇنۇشقا لايىق بولدى؟) ماڭا ئېيتىپ بېرىڭلاركى، ئۇلار زېمىندىن نېمىلەرنى ياراتتى؟ يا ئۇلار ئاسمانلارنى ئاللاھ بىلەن بىرلىكتە ياراتتىمۇ؟ يا ئۇلارغا بىز بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابتىكى روشەن دەلىللەرگە ئاساسلىنامدۇ؟» ھەرگىز ئۇنداق ئەمەس، زالىملار بىر ـ بىرىگە پەقەت يالغاننىلا ۋەدە قىلىشىدۇ.');
INSERT INTO "saleh" VALUES(35,41,'35:41',' ئاللاھ ھەقىقەتەن ئاسمانلارنى ۋە زېمىننى (قۇدرىتى ۋە گۈزەل ھېكمىتى بىلەن) چۈشۈپ كېتىشتىن توختىتىپ تۇرىدۇ. ئەگەر ئۇلار چۈشۈپ كەتسە، ئۇلارنى ئاللاھتىن باشقا ھېچ ئەھەدى توختىتىپ تۇرالمايدۇ. ئاللاھ ھەقىقەتەن ھەلىمدۇر (يەنى كۇففارلار ئازابقا تېگىشلىك بولسىمۇ، ئۇلارنى جازالاشقا ئالدىراپ كەتمەيدۇ)، ناھايىتى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(35,42,'35:42',' ئۇلار ئۆزلىرىگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلسە، ھەر قانداق ئۈممەتكە قارىغاندا ئەڭ ھىدايەت تاپقۇچى بولىدىغانلىقلىرى بىلەن قاتتىق قەسەم ئىچىشتى. ئۇلارغا ئاگاھلاندۇرغۇچى كەلگەن چاغدا (ھىدايەتتىن) تېخىمۇ يىراق بولۇشتى. ');
INSERT INTO "saleh" VALUES(35,43,'35:43','(بۇنداق بولۇشى) ئۇلارنىڭ زېمىندا تەكەببۇرلۇق قىلغانلىقلىرى ۋە ھىيلە ـ مىكىر ئىشلەتكەنلىكلىرىدىندۇر. ھىيلە ـ مىكىرنىڭ ۋابالى پەقەت ھىيلە ـ مىكىر ئىشلەتكەن ئادەمنىڭ ئۆزىگە بولىدۇ، ئۇلار پەقەت ئىلگىرىكى ئۈممەتلەرگە قوللىنىلغان (ئاللاھنىڭ ئۇلارنى ھالاك قىلىش ۋە جازالاشتىن ئىبارەت) يولىنىلا كۈتىدۇ، ئاللاھنىڭ (مەخلۇقاتلار ئۈستىدە قوللانغان) يولىدا ھەرگىزمۇ ھېچقانداق ئۆزگىرىشنى كۆرمەيسەن، ئاللاھ يولىدا ھەرگىز يۆتكىلىشنىمۇ (يەنى ئازابنىڭ ئازابقا تېگىشلىك بولغانلار ئۈستىدىن باشقىلارنىڭ ئۈستىگە يۆتكىلىشىنى) كۆرمەيسەن.');
INSERT INTO "saleh" VALUES(35,44,'35:44',' ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرىكىلەر (يەنى ھالاك بولغان ئۈممەتلەر) نىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆرمىدىمۇ؟ ئۇلار (يەنى ئۆتكەنكى ئۈممەتلەر) بۇلاردىن تېخىمۇ كۈچلۈك ئىدى. ئاسمانلاردىكى ۋە زېمىندىكى ھېچ نەرسە ئاللاھنى ئىلاجىسىز قالدۇرالمايدۇ. ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(35,45,'35:45','ئەگەر ئاللاھ ئىنسانلارنى ئۇلارنىڭ قىلمىشلىرى تۈپەيلىدىن جازالايدىغان بولسا، يەر يۈزىدە ھېچبىر جان ئىگىسىنى قويمىغان بولاتتى ۋەلېكىن ئاللاھ ئۇلار (دىن ھېساب ئېلىش) نى مۇئەييەن ۋاقىتقىچە تەخىر قىلىدۇ، ئۇلار (دىن ھېساب ئېلىش) نىڭ ۋاقتى يېتىپ كەلگەندە (ئۇلارنىڭ ئەمەللىرىگە قاراپ جازا بېرىدۇ)، ئاللاھ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(36,1,'36:1','ياسىن.');
INSERT INTO "saleh" VALUES(36,2,'36:2',' ھېكمەتلىك قۇرئان بىلەن قەسەمكى ');
INSERT INTO "saleh" VALUES(36,3,'36:3',' (ئى مۇھەممەد!) شەك ـ شۈبھىسىزكى، سەن پەيغەمبەرلەردىنسەن[ - 3].');
INSERT INTO "saleh" VALUES(36,4,'36:4',' توغرا يولدىسەن. ');
INSERT INTO "saleh" VALUES(36,5,'36:5',' قۇرئان ئاتا ـ بوۋىلىرى ئاگاھلاندۇرۇلمىغان (يەنى ئۇلارغا پەيغەمبەر كەلمىگەن)، غاپىل قالغان بىر قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، غالىب، ناھايىتى مېھرىبان ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر[5 ـ 6].');
INSERT INTO "saleh" VALUES(36,6,'36:6',' قۇرئان ئاتا ـ بوۋىلىرى ئاگاھلاندۇرۇلمىغان (يەنى ئۇلارغا پەيغەمبەر كەلمىگەن)، غاپىل قالغان بىر قەۋمنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، غالىب، ناھايىتى مېھرىبان ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر[5 ـ 6].');
INSERT INTO "saleh" VALUES(36,7,'36:7',' ئۇلارنىڭ كۆپچىلىكىگە (ئازاب توغرىسىدىكى) ھۆكۈم ھەقىقەتەن تېگىشلىك بولدى. شۇنىڭ ئۈچۈن ئۇلار ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(36,8,'36:8',' بىز ھەقىقەتەن ئۇلارنىڭ بويۇنلىرىغا ئېڭەكلىرىگۈچە تاقاقلارنى تاقىدۇق، شۇنىڭ ئۈچۈن ئۇلار (ھەقكە) باشلىرىنى ئىگەلمەيدۇ.');
INSERT INTO "saleh" VALUES(36,9,'36:9',' ئۇلارنىڭ ئالدىدا بىر توسۇق، كەينىدە بىر توسۇق قىلدۇق، ئۇلارنىڭ كۆزلىرىنى پەردىلىدۇق، شۇنىڭ ئۈچۈن ئۇلار (ئىماننىڭ يوللىرى توسۇلۇپ كەتكەنلىكى ئۈچۈن) ھېچ نەرسىنى كۆرەلمەيدۇ.');
INSERT INTO "saleh" VALUES(36,10,'36:10','ئۇلارنى ئاگاھلاندۇرامسەن، ئاگاھلاندۇرمامسەن، ئۇلارغا بەرىبىر ئوخشاش، ئىمان ئېيتمايدۇ.');
INSERT INTO "saleh" VALUES(36,11,'36:11','سەن پەقەت قۇرئانغا ئەگەشكەن، مەرھەمەتلىك ئاللاھنى كۆرمەي تۇرۇپ قورققان كىشىنى ئاگاھلاندۇرالايسەن (يەنى ئاگاھلاندۇرۇشۇڭنىڭ پايدىسى بولىدۇ)، ئۇنىڭغا (ئاللاھنىڭ ئۇنىڭ گۇناھلىرىنى) مەغپىرەت قىلىدىغانلىقى ۋە ئېسىل ساۋاب (يەنى جەننەت ئاتا قىلىدىغانلىقى) بىلەن خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(36,12,'36:12',' بىز، شۈبھىسىزكى، ئۆلۈكلەرنى تىرىلدۈرىمىز، ئۇلارنىڭ (دۇنيادا) قىلغان (ياخشى ـ يامان) ئەمەللىرىنى ۋە ئىشلىرىنى خاتىرىلەپ قويىمىز، ھەممە شەيئىنى روشەن دەپتەردە (يەنى لەۋھۇلمەھپۇزدا) تولۇق خاتىرىلەپ قويغانمىز.');
INSERT INTO "saleh" VALUES(36,13,'36:13',' ئۇلارغا (يەنى كۇففارلارغا) شەھەر (يەنى ئەنتاكىيە) ئاھالىسىنى مىسال قىلىپ كەلتۈرگىن، ئۆز ۋاقتىدا ئۇلارغا پەيغەمبەرلەر كەلگەن ئىدى.');
INSERT INTO "saleh" VALUES(36,14,'36:14',' ئەينى زاماندا ئۇلارغا ئىككى پەيغەمبەر ئەۋەتتۇق. ئۇلار ئۇ ئىككى پەيغەمبەرنى ئىنكار قىلدى. ئۈچىنچى پەيغەمبەرنى ئەۋەتىش بىلەن (ئۇلارغا) ياردەم قىلدۇق، پەيغەمبەرلەر (ئۇلارغا): «بىز ھەقىقەتەن (ئاللاھنىڭ) سىلەرگە (يەنى سىلەرنى ھىدايەت قىلىشقا) ئەۋەتىلگەن ئەلچىلىرىمىز» دېدى.');
INSERT INTO "saleh" VALUES(36,15,'36:15','ئۇلار: «سىلەر پەقەت بىزگە ئوخشاش ئىنسان، مېھرىبان ئاللاھ ھېچ نەرسە نازىل قىلغىنى يوق، سىلەر پەقەت يالغانچىسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(36,16,'36:16','ئەلچىلەر: «پەرۋەردىگارىمىز بىلىدۇكى، بىز شەك ـ شۈبھىسىز سىلەرگە ئەۋەتىلگەن ئەلچىلەرمىز.');
INSERT INTO "saleh" VALUES(36,17,'36:17','بىزنىڭ مەسئۇلىيىتىمىز پەقەت (روشەن دەلىللەر بىلەن) چۈشىنىشلىك قىلىپ تەبلىغ قىلىشتۇر» دېدى.');
INSERT INTO "saleh" VALUES(36,18,'36:18',' ئۇلار (ئەلچىلەرگە): «بىز سىلەردىن شۇم پال ئالدۇق. ئەگەر سىلەر (دەۋىتىڭلارنى) توختاتمىساڭلار، سىلەرنى چوقۇم تاش ـ كېسەك قىلىپ ئۆلتۈرىمىز ۋە بىزنىڭ قاتتىق جازايىمىزغا ئۇچرايسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(36,19,'36:19',' ئەلچىلەر: «(كۇفرىڭلار سەۋەبىدىن) شۇملۇقۇڭلار ئۆزۈڭلار بىلەن بىللىدۇر، سىلەرگە ۋەز ـ نەسىھەت قىلىنسا (شۇم پال ئالامسىلەر؟) ھەرگىز ئۇنداق ئەمەس (يەنى ئىش سىلەر گۇمان قىلغاندەك ئەمەس)، سىلەر (ئاللاھقا شېرىك كەلتۈرۈش بىلەن) ھەددىدىن ئاشقۇچى قەۋمسىلەر» دېدى.');
INSERT INTO "saleh" VALUES(36,20,'36:20',' شەھەرنىڭ يىراق جايىدىن بىر كىشى (يەنى ھەبىب نەجار) يۈگۈرۈپ كېلىپ ئېيتتى: «ئى قەۋمىم! پەيغەمبەرلەرگە ئەگىشىڭلار.');
INSERT INTO "saleh" VALUES(36,21,'36:21','توغرا يولدا بولغان، (ئىمانغا دەۋەت قىلغانلىقىغا) سىلەردىن ھەق سورىمايدىغان كىشىلەرگە ئەگىشىڭلار.');
INSERT INTO "saleh" VALUES(36,22,'36:22',' مەن نېمىشقا مېنى ياراتقان ۋە سىلەر (ئۆلگەندىن كېيىن) دەرگاھىغا قايتىدىغان ئاللاھقا ئىبادەت قىلماي؟');
INSERT INTO "saleh" VALUES(36,23,'36:23',' مەن ئاللاھتىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام، ئەگەر مېھرىبان ئاللاھ ماڭا بىرەر زىيان ـ زەخمەت يەتكۈزمەكچى بولسا، ئۇلارنىڭ شاپائىتى مەندىن ھېچ نەرسىنى دەپئى قىلالمايدۇ ھەمدە مېنى قۇتقۇزالمايدۇ.');
INSERT INTO "saleh" VALUES(36,24,'36:24',' ئەگەر ئۇنداق بولسا (يەنى ئاللاھتىن غەيرىينى ئىلاھ قىلىۋالىدىغان بولسام)، مەن ھەقىقەتەن ئوپئوچۇق گۇمراھلىقتا بولىمەن.');
INSERT INTO "saleh" VALUES(36,25,'36:25',' مەن ھەقىقەتەن (سىلەرنى ياراتقان) پەرۋەردىگارىڭلارغا ئىمان ئېيتتىم، (ئىمانىمغا) قۇلاق سېلىڭلار (ۋە ئۇنىڭغا گۇۋاھ بولۇڭلار)».');
INSERT INTO "saleh" VALUES(36,26,'36:26','(قەۋمى ئۇنى ئۆلتۈرۈۋەتكەندىن كېيىن، ئۇنىڭغا) «جەننەتكە كىرگىن» دېيىلدى، ئۇ: «كاشكى قەۋمىم پەرۋەردىگارىمنىڭ ماڭا مەغپىرەت قىلغانلىقىنى ۋە مېنى ھۆرمەتلىكلەردىن قىلغانلىقىنى بىلسە ئىدى» دېدى[26 ـ 27]. ');
INSERT INTO "saleh" VALUES(36,27,'36:27','(قەۋمى ئۇنى ئۆلتۈرۈۋەتكەندىن كېيىن، ئۇنىڭغا) «جەننەتكە كىرگىن» دېيىلدى، ئۇ: «كاشكى قەۋمىم پەرۋەردىگارىمنىڭ ماڭا مەغپىرەت قىلغانلىقىنى ۋە مېنى ھۆرمەتلىكلەردىن قىلغانلىقىنى بىلسە ئىدى» دېدى[26 ـ 27]. ');
INSERT INTO "saleh" VALUES(36,28,'36:28',' ئۇنىڭدىن كېيىن ئۇنىڭ قەۋمىنى (جازالاش ئۈچۈن) ئاسماندىن قوشۇن (يەنى پەرىشتىلەر) چۈشۈرمىدۇق (ھەر قەۋمنى ھالاك قىلىش ئۈچۈن پەرىشتىلەرنى) چۈشۈرۈۋەرگۈچى بولمىدۇق.');
INSERT INTO "saleh" VALUES(36,29,'36:29',' پەقەت بىر ئاۋاز بىلەنلا ناگاھان ئۇلار قېتىپ قالدى.');
INSERT INTO "saleh" VALUES(36,30,'36:30',' (ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغۇچى) بەندىلەرگە ئەپسۇسكى، ئۇلارغا بىرەر پەيغەمبەر كەلسىلا ئۇنى مەسخىرە قىلىشتى.');
INSERT INTO "saleh" VALUES(36,31,'36:31',' ئۇلار (يەنى مەككە ئاھالىسى) ئۇلاردىن ئىلگىرى ئۆتكەن نۇرغۇن ئۈممەتلەرنى ھالاك قىلغانلىقىمىزنى بىلمەمدۇ؟ ئۇلار بۇلارنىڭ يېنىغا (يەنى دۇنياغا ئەبەدىي) قايتمايدۇ.');
INSERT INTO "saleh" VALUES(36,32,'36:32',' ئۇلارنىڭ ھەممىسى (قىيامەت كۈنى ھېساب بېرىش ئۈچۈن) ھۇزۇرىمىزغا ھازىر قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(36,33,'36:33',' ئۆلۈك (يەنى قۇرغاق) زېمىننى (يامغۇر بىلەن) تىرىلدۈرۈپ، ئۇنىڭدىن ئۇلار يەيدىغان ئاشلىقنى ئۆستۈرگەنلىكىمىز ئۇلارغا (ئاللاھنىڭ كامالى قۇدرىتىنى كۆرسىتىدىغان) ئالامەتتۇر.');
INSERT INTO "saleh" VALUES(36,34,'36:34',' بىز زېمىندا نۇرغۇنلىغان خورما باغلىرىنى، ئۈزۈم باغلىرىنى بەرپا قىلدۇق، شۇ زېمىندا بۇلاقلارنى ئېقىتتۇق.');
INSERT INTO "saleh" VALUES(36,35,'36:35','ئۇلار ئۇنىڭ (يەنى باغلارنىڭ) مېۋىسىنى يېسۇن، (بۇ مېۋىلەرنى) ئۇلارنىڭ قوللىرى ياراتقان ئەمەس، ئۇلار (ئاللاھنىڭ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟.');
INSERT INTO "saleh" VALUES(36,36,'36:36',' ئاللاھ (پۈتۈن ئەيىب ـ نۇقسانلاردىن) پاكتۇر، ئۇ پۈتۈن شەيئىلەرنى جۈپ ياراتتى. زېمىندىن ئۈنۈپ چىقىدىغان نەرسىلەرنىڭ، ئۇلارنىڭ ئۆزلىرىنىڭ ۋە ئۇلار بىلمەيدىغان نەرسىلەر (يەنى ئاجايىپ مەخلۇقاتلار) نىڭ ھەممىسىنىڭ جۈپتى بار.');
INSERT INTO "saleh" VALUES(36,37,'36:37','ئۇلارغا (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر ئالامەت كېچىدىن ئىبارەتكى، ئۇنىڭدىن كۈندۈزنى ئايرىۋېتىمىز ـ دە (يەنى كۈندۈزنىڭ نۇرىنى يوق قىلىۋېتىمىز ـ دە)، ئۇلار شۇھامان قاراڭغۇدا قالىدۇ. ');
INSERT INTO "saleh" VALUES(36,38,'36:38',' كۈن بەلگىلەنگەن جايىغا قاراپ سەير قىلىدۇ، بۇ، غالىب، ھەممىنى بىلگۈچى ئاللاھنىڭ ئالدىنئالا بەلگىلىگەن ئىشىدۇر.');
INSERT INTO "saleh" VALUES(36,39,'36:39',' ئايغا سەير قىلىدىغان مەنزىللەرنى بەلگىلىدۇق، ئۇ (ئاخىرقى مەنزىلگە يېتىپ بارغاندا) خورمىنىڭ قۇرۇپ قالغان شېخىغا ئوخشاش بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(36,40,'36:40',' كۈننىڭ ئايغا يېتىۋېلىشى (يەنى ئىككىسىنىڭ جەم بولۇپ قېلىشى)، كېچىنىڭ كۈندۈزدىن ئېشىپ كېتىشى (يەنى ۋاقتى كەلمەستىن كۈندۈزنىڭ ئورنىنى ئېلىشى) مۇمكىن ئەمەس، ھەر بىرى پەلەكتە ئۈزۈپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(36,41,'36:41',' ئۇلارغا (بىزنىڭ كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) ئالامەت شۇكى، بىز ئۇلارنىڭ ئورۇق - ئەۋلادىنى (يۈكلەر بىلەن) توشقۇزۇلغان كېمىسىگە چۈشۈردۇق.');
INSERT INTO "saleh" VALUES(36,42,'36:42',' بىز ئۇلار ئۈچۈن شۇ كېمىگە ئوخشاش ئۇلار چۈشىدىغان نەرسىلەرنى ياراتتۇق.');
INSERT INTO "saleh" VALUES(36,43,'36:43',' ئەگەر بىز خالىساق ئۇلارنى غەرق قىلىۋېتىمىز، ئۇلارغا ياردەم بەرگۈچىمۇ بولمايدۇ ۋە ئۇلارنى قۇتۇلدۇرغۇچىمۇ بولمايدۇ.');
INSERT INTO "saleh" VALUES(36,44,'36:44',' پەقەت بىز رەھىم قىلغانلىقىمىز ۋە ئۇلارنى مۇئەييەن مۇددەتكىچە (ھاياتتىن) بەھرىمەن قىلىدىغانلىقىمىز ئۈچۈن (ئۇلار قۇتۇلدۇرۇلىدۇ)[44');
INSERT INTO "saleh" VALUES(36,45,'36:45','ئۇلارغا: «رەھمەتكە ئېرىشىشىڭلار ئۈچۈن ئالدىڭلاردىكىدىن (يەنى دۇنيا ئازابىدىن) ۋە ئارقاڭلاردىكىدىن (يەنى ئاخىرەت ئازابىدىن) قورقۇڭلار» دېيىلسە (ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ).');
INSERT INTO "saleh" VALUES(36,46,'36:46','پەرۋەردىگارىنىڭ (پەيغەمبەرنىڭ راستلىقىنى ئىسپاتلايدىغان) ئايەتلىرىدىن بىر ئايەت كەلسىلا، ئۇلار بۇنىڭدىن يۈز ئۆرۈيدۇ.');
INSERT INTO "saleh" VALUES(36,47,'36:47','ئەگەر ئۇلارغا: «ئاللاھ سىلەرگە رىزىق قىلىپ بەرگەن نەرسىلەردىن (پېقىرلارغا) سەدىقە قىلىپ بېرىڭلار» دېيىلسە، كاپىرلار مۆمىنلەرگە (مەسخىرە قىلىش يۈزىسىدىن): «ئەگەر ئاللاھ خالىسا رىزىق تەمىنلەيدىغان ئادەمنى بىز تەمىنلەمدۇق؟ سىلەر پەقەت روشەن گۇمراھلىقتاسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(36,48,'36:48',' ئۇلار: «ئەگەر راستچىل بولساڭلار، بۇ ۋەدە قاچان ئىشقا ئاشىدۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(36,49,'36:49',' ئۇلار پەقەت بىر ئاۋازنىلا (يەنى ئىسراپىل ئەلەيھىسسالامنىڭ بىرىنچى قېتىملىق چالغان سۇرىنىلا) كۈتىدۇ، ئۇلار (دۇنيادا سودا ـ سېتىق ۋە شۇنىڭغا ئوخشاش ئىشلار ئۈستىدە) جاڭجاللىشىپ تۇرغان ھالەتلىرىدە، ئۇ ئاۋاز ئۇلارنى ھالاك قىلىدۇ. ');
INSERT INTO "saleh" VALUES(36,50,'36:50',' ئۇلار بىر ـ بىرىگە ۋەسىيەت قالدۇرۇشقىمۇ، ئائىلىسىگە قايتىشقىمۇ قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(36,51,'36:51',' (ئىككىنچى قېتىملىق) سۇر چېلىنغان ھامان ئۇلار قەبرىلىرىدىن چىقىپ پەرۋەردىگارى تەرەپكە يۈگۈرىدۇ.');
INSERT INTO "saleh" VALUES(36,52,'36:52',' ئۇلار: «ۋاي ئىسىت! بىزنى ئۇخلاۋاتقان يېرىمىزدىن (يەنى قەبرىمىزدىن) كىم ئويغاتتى؟» دەيدۇ (چۈنكى ئۇلار ئىككى سۇر ئارىسىدا ئۇيقۇدا بولۇپ ئازابتىن خالىي بولىدۇ)، (پەرىشتىلەر يا مۆمىنلەر ئۇنىڭغا جاۋابەن) «مېھرىبان ئاللاھنىڭ ۋەدە قىلغىنى مۇشۇ (كۈن)، پەيغەمبەرلەر راست ئېيتقان» دەيدۇ.');
INSERT INTO "saleh" VALUES(36,53,'36:53',' پەقەت بىر ئاۋاز بىلەنلا ئۇلارنىڭ ھەممىسى ھۇزۇرىمىزغا ھازىر قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(36,54,'36:54',' ئۇ كۈندە ھېچبىر ئىنسانغا قىلچە زۇلۇم قىلىنمايدۇ، سىلەرگە پەقەت قىلمىشىڭلارغا يارىشا جازا بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(36,55,'36:55',' شۈبھىسىزكى، ئۇ كۈندە جەننەت ئەھلىلىرى نېمەت ئىچىدىدۇر.');
INSERT INTO "saleh" VALUES(36,56,'36:56','1. ئۇلار ۋە ئۇلارنىڭ جۈپتىللىرى سايىلىرى ئاستىدا تەخىتلەرگە يۆلەنگەن ھالدا تۇرىدۇ.56');
INSERT INTO "saleh" VALUES(36,57,'36:57','ئۇلار ئۈچۈن جەننەتتە (تۈرلۈك) مېۋىلەر بار، ئۇلار ئۈچۈن جەننەتتە (ئۇلارنىڭ) كۆڭلى تارتقان نەرسىلەر بولىدۇ.');
INSERT INTO "saleh" VALUES(36,58,'36:58','مېھرىبان پەرۋەردىگارى تەرىپىدىن (ئۇلارغا) سالام دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(36,59,'36:59','(گۇناھكارلارغا) «ئى گۇناھكارلار! بۈگۈن (بىر تەرەپكە) ئايرىلىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(36,60,'36:60',' سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): «ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر» دەپ تەۋسىيە قىلمىدىممۇ؟[60ـ61]');
INSERT INTO "saleh" VALUES(36,61,'36:61',' سىلەرگە (پەيغەمبەرلىرىم ئارقىلىق): «ئى ئادەم بالىلىرى! شەيتانغا چوقۇنماڭلار، ئۇ ھەقىقەتەن سىلەرگە ئاشكارا دۈشمەندۇر، ماڭىلا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر» دەپ تەۋسىيە قىلمىدىممۇ؟[60ـ61]');
INSERT INTO "saleh" VALUES(36,62,'36:62','شۈبھىسىزكى، شەيتان سىلەردىن نۇرغۇن كىشىلەرنى ئازدۇردى، (بۇنى) چۈشەنمەمسىلەر؟');
INSERT INTO "saleh" VALUES(36,63,'36:63',' سىلەرگە ۋەدە قىلىنغان جەھەننەم مانا شۇ.');
INSERT INTO "saleh" VALUES(36,64,'36:64','سىلەر (دۇنيادىكى چېغىڭلاردا) كاپىر بولغانلىقىڭلار ئۈچۈن بۈگۈن جەھەننەمگە كىرىڭلار.');
INSERT INTO "saleh" VALUES(36,65,'36:65',' شۇ كۈندە ئۇلارنىڭ ئېغىزلىرىنى پېچەتلىۋېتىمىز، ئۇلارنىڭ قىلمىشلىرىنى قوللىرى بىزگە سۆزلەپ بېرىدۇ، پۇتلىرى گۇۋاھلىق بېرىدۇ (يەنى ھەر ئەزا ئۇنىڭدىن سادىر بولغان ئىشنى سۆزلەپ بېرىدۇ).');
INSERT INTO "saleh" VALUES(36,66,'36:66',' ئەگەر بىز خالىساق، ئۇلارنىڭ كۆزلىرىنى ئەلۋەتتە يوق قىلىۋېتەتتۇق، ئاندىن ئۇلار يول مېڭىشقا ئالدىرايتتى، لېكىن ئۇلار قانداقمۇ كۆرەلىسۇن؟ ');
INSERT INTO "saleh" VALUES(36,67,'36:67',' ئەگەر بىز خالىساق، ئۇلارنى ئۆيلىرىدىلا سۈرىتى مۇبەددەل (يەنى مايمۇن، چوشقا ياكى تاش) قىلىۋېتەتتۇق، شۇنىڭ بىلەن ئۇلار ئالدىغىمۇ ماڭالمايتتى، ئارقىغىمۇ قايتالمايتتى.');
INSERT INTO "saleh" VALUES(36,68,'36:68',' بىز ئۆمرىنى ئۇزۇن قىلغان ئادەمنىڭ تېنىنى ئاجىزلىتىۋېتىمىز (يەنى ياشلىق، قىرانلىق ۋاقتىدىكىدىن ئاجىزلايدۇ، قېرىيدۇ)، ئۇلار (بۇنى) چۈشەنمەمدۇ؟.');
INSERT INTO "saleh" VALUES(36,69,'36:69',' ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز ـ نەسىھەتتۇر ۋە روشەن قۇرئاندۇر[69ـ70].');
INSERT INTO "saleh" VALUES(36,70,'36:70',' ئۇنىڭغا (يەنى پەيغەمبەرگە) بىز شېئىر تەلىم بەرمىدۇق، ئۇنىڭغا شېئىر مۇناسىپمۇ ئەمەس. ئۇ پەقەت تىرىكلەرنى ئاگاھلاندۇرۇش، كاپىرلارغا ئازابنىڭ تېگىشلىك ئىكەنلىكىنى (بىلدۈرۈش ئۈچۈن نازىل بولغان) ۋەز ـ نەسىھەتتۇر ۋە روشەن قۇرئاندۇر[69ـ70].');
INSERT INTO "saleh" VALUES(36,71,'36:71','ئۇلار بىلمەمدۇكى، ئۇلار ئۈچۈن ھايۋانلارنى (قۇدرەت) قولىمىز بىلەن خەلق ئەتتۇق، ئۇلار ھايۋانلارنى باشقۇرۇپ تۇرغۇچىلاردۇر. ');
INSERT INTO "saleh" VALUES(36,72,'36:72',' ھايۋانلارنى ئۇلارغا بويسۇندۇرۇپ بەردۇق، ئۇلار ئۇ ھايۋانلارنىڭ بەزىسىنى مىنىدۇ، بەزىسىنى يەيدۇ.');
INSERT INTO "saleh" VALUES(36,73,'36:73',' ئۇلار شۇ ھايۋانلاردىن پايدىلىنىدۇ ۋە سۈتلىرىنى ئىچىدۇ، ئۇلار (پەرۋەردىگارىنىڭ بۇ نېمەتلىرىگە) شۈكۈر قىلمامدۇ؟');
INSERT INTO "saleh" VALUES(36,74,'36:74',' ئۇلار ياردەمگە ئېرىشىشنى ئۈمىد قىلىپ، ئاللاھنى قويۇپ باشقا مەبۇدلارنى ئىلاھ قىلىۋالدى.');
INSERT INTO "saleh" VALUES(36,75,'36:75','ئۇ مەبۇدلار ئۇلارغا ياردەم بېرەلمەيدۇ. بۇلار شۇ مەبۇدلار ئۈچۈن (يەنى ئۇلارنىڭ خىزمىتى ئۈچۈن) ھازىرلانغان قوشۇندۇر.');
INSERT INTO "saleh" VALUES(36,76,'36:76',' (ئى، مۇھەممەد!) ئۇلار (يەنى مۇشرىكلار) نىڭ سۆزى (يەنى سېنى ئىنكار قىلغانلىقى) سېنى غەمكىن قىلمىسۇن. بىز ھەقىقەتەن ئۇلارنىڭ دىللىرىدىكىنى ۋە ئاشكارا قىلغان (سۆز ۋە ھەرىكەتلىرى) نى بىلىپ تۇرىمىز.');
INSERT INTO "saleh" VALUES(36,77,'36:77','ئىنسان بىلمەمدۇكى، بىز ھەقىقەتەن ئۇنى ئابىمەنىيدىن ياراتتۇق. ئەمدى ئۇ (پەرۋەردىگارىغا) ئاشكارا خۇسۇمەتچى بولۇپ قالدى.');
INSERT INTO "saleh" VALUES(36,78,'36:78',' ئۇ بىزگە (ئىنساننىڭ قايتا تىرىلىشىنى يىراق ساناپ، چىرىگەن سۆڭەكلەرنى) مىسال قىلىپ كۆرسەتتى. ئۆزىنىڭ يارىتىلغانلىقىنى بولسا ئۇنتۇدى، ئۇ: «چىرىپ كەتكەن سۆڭەكلەرنى كىم تىرىلدۈرەلەيدۇ؟» دېدى.');
INSERT INTO "saleh" VALUES(36,79,'36:79',' ئېيتقىنكى، «ئۇنى ئەڭ دەسلەپتە ياراتقان زات تىرىلدۈرىدۇ، ئۇ ھەر بىر مەخلۇقنى بىلگۈچىدۇر».');
INSERT INTO "saleh" VALUES(36,80,'36:80','ئاللاھ سىلەر ئۈچۈن يېشىل دەرەختىن ئوت پەيدا قىلدى. سىلەر ئۇنىڭ بىلەن ئوت ياقىسىلەر.');
INSERT INTO "saleh" VALUES(36,81,'36:81',' ئاسمانلارنى ۋە زېمىننى ياراتقان زات ئۇلارنىڭ ئوخشىشىنى يارىتىشقا قادىر ئەمەسمۇ؟ ئۇ بۇنىڭغا قادىر، ئاللاھ ماھىر ياراتقۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(36,82,'36:82','ئاللاھ بىرەر شەيئىنى (يارىتىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇتقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇتقا كېلىدۇ.');
INSERT INTO "saleh" VALUES(36,83,'36:83',' ئاللاھ پاكتۇركى، ھەر بىر نەرسىنىڭ ئىگىلىك ھوقوقى ئۇنىڭ قولىدىدۇر، (ئۆلگەندىن كېيىن) ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(37,1,'37:1','سەپ ـ سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (ئاللاھتىن مەدەت تىلەپ) كالامۇللاھنى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى[1 ـ 3]. ');
INSERT INTO "saleh" VALUES(37,2,'37:2','سەپ ـ سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (ئاللاھتىن مەدەت تىلەپ) كالامۇللاھنى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى[1 ـ 3]. ');
INSERT INTO "saleh" VALUES(37,3,'37:3','سەپ ـ سەپ بولۇپ تۇرغان، دۈشمەنلەرگە قارشى ئات سېلىپ تۇرغان، (ئاللاھتىن مەدەت تىلەپ) كالامۇللاھنى تىلاۋەت قىلىپ تۇرغان (غازات قىلغۇچى) قوشۇن بىلەن قەسەمكى[1 ـ 3]. ');
INSERT INTO "saleh" VALUES(37,4,'37:4','سىلەرنىڭ ئىلاھىڭلار شەك ـ شۈبھىسىز بىر ئىلاھتۇر.');
INSERT INTO "saleh" VALUES(37,5,'37:5',' (ئۇ) ئاسمانلارنىڭ، زېمىننىڭ، ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ ۋە مەشرىقلەرنىڭ پەرۋەردىگارىدۇر.');
INSERT INTO "saleh" VALUES(37,6,'37:6',' بىز ھەقىقەتەن يېقىن ئاسماننى يۇلتۇزلار (نۇرى) بىلەن زىننەتلىدۇق.');
INSERT INTO "saleh" VALUES(37,7,'37:7','ئاللاھنىڭ ئىتائىتىدىن چىققۇچى ھەر بىر شەيتاندىن ئۇنى قوغدىدۇق.');
INSERT INTO "saleh" VALUES(37,8,'37:8',' ئۇلار پەرىشتىلەرنىڭ (سۆزلىرىدىن ھېچ نەرسىنى) ئاڭلىيالمايدۇ، ئۇلار، قوغلىنىشى ئۈچۈن، ھەر تەرەپتىن ئاققۇچى يۇلتۇزلار بىلەن ئېتىلىدۇ، ');
INSERT INTO "saleh" VALUES(37,9,'37:9','ئۇلار (ئاخىرەتتە) دائىملىق ئازابقا قالىدۇ.');
INSERT INTO "saleh" VALUES(37,10,'37:10',' لېكىن (ئاسمان خەۋەرلىرىدىن) ئوغرىلىقچە بىر نەرسە ئاڭلىغان شەيتاننى يورۇق يۇلتۇز قوغلاپ (كۆيدۈرۈپ تاشلايدۇ).');
INSERT INTO "saleh" VALUES(37,11,'37:11','ئۇلاردىن (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچىلاردىن) سوراپ باققىن! ئۇلارنى يارىتىش قىيىنمۇ؟ يا بىزنىڭ ياراتقانلىرىمىز (يەنى ئاسمان ـ زېمىن ۋە ئۇلارنىڭ ئارىسىدىكى پەرىشتىلەر ۋە چوڭ مەخلۇقاتلار) نى يارىتىش قىيىنمۇ؟ بىز ھەقىقەتەن ئۇلارنى (يەنى ئۇلارنىڭ ئەسلى بولغان ئادەمنى) يېپىشقاق لايدىن ياراتتۇق.');
INSERT INTO "saleh" VALUES(37,12,'37:12',' (ئى مۇھەممەد!) سەن ئۇلارنىڭ ئاللاھنىڭ قۇدرىتىنى كۆرۈپ تۇرۇپ، ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغانلىقىدىن بەلكى ئەجەبلىنەرسەن. ئۇلار بولسا سېنى مەسخىرە قىلىشىدۇ.');
INSERT INTO "saleh" VALUES(37,13,'37:13',' ئۇلارغا (قۇرئان بىلەن) ۋەز ـ نەسىھەت قىلىنسا (ۋەز ـ نەسىھەتنى) قوبۇل قىلمايدۇ.');
INSERT INTO "saleh" VALUES(37,14,'37:14',' ئۇلار بىرەر مۆجىزىنى كۆرسە مەسخىرە قىلىشىدۇ.');
INSERT INTO "saleh" VALUES(37,15,'37:15',' ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ[15ـ17].');
INSERT INTO "saleh" VALUES(37,16,'37:16',' ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ[15ـ17].');
INSERT INTO "saleh" VALUES(37,17,'37:17',' ئۇلار: «بۇ روشەن سېھىردۇر، بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغاندىن كېيىن راستلا تىرىلەمدۇق؟ بىزنىڭ بۇرۇنقى ئاتىلىرىمىزمۇ تىرىلەمدۇ؟» دەيدۇ[15ـ17].');
INSERT INTO "saleh" VALUES(37,18,'37:18','ئېيتقىنكى، «ھەئە، سىلەر خار ھالدا تىرىلىسىلەر».');
INSERT INTO "saleh" VALUES(37,19,'37:19',' بىر ئاۋازنى (يەنى ئىسراپىلنىڭ چالغان سۇرىنى) ئاڭلاش بىلەنلا (مەھشەرگاھقا) ھازىر بولۇپ (ئۆزلىرىگە نېمە قىلىنىدىغانلىقىغا) قارىشىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(37,20,'37:20',' ئۇلار: «ۋاي ئىسىت! بىزگە بۇ قىيامەت كۈنىدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(37,21,'37:21',' بۇ سىلەر ئىنكار قىلغان (ھەق بىلەن باتىل) ئايرىلىدىغان كۈندۇر.');
INSERT INTO "saleh" VALUES(37,22,'37:22',' (ئاللاھ پەرىشتىلىرىگە ئېيتىدۇ) زۇلۇم قىلغۇچىلارنى، ئۇلارنىڭ (مۇشرىكلىكتىكى، كۇفرىدىكى) قاياشلىرىنى ۋە ئۇلارنىڭ ئاللاھنى قويۇپ چوقۇنغان مەبۇدلىرىنى (مەھشەرگاھقا) توپلاڭلار، ئاندىن ئۇلارنى دوزاخنىڭ يولىغا باشلاڭلار[22ـ23].');
INSERT INTO "saleh" VALUES(37,23,'37:23',' (ئاللاھ پەرىشتىلىرىگە ئېيتىدۇ) زۇلۇم قىلغۇچىلارنى، ئۇلارنىڭ (مۇشرىكلىكتىكى، كۇفرىدىكى) قاياشلىرىنى ۋە ئۇلارنىڭ ئاللاھنى قويۇپ چوقۇنغان مەبۇدلىرىنى (مەھشەرگاھقا) توپلاڭلار، ئاندىن ئۇلارنى دوزاخنىڭ يولىغا باشلاڭلار[22ـ23].');
INSERT INTO "saleh" VALUES(37,24,'37:24',' ئۇلارنى توختىتىپ تۇرۇڭلار، چۈنكى ئۇلار (جىمى سۆزلىرى ۋە ئىشلىرىدىن) سوئال ـ سوراق قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(37,25,'37:25',' (ئاندىن) ئۇلارغا: «نېمىشقا (دۇنيادىكى ھالىتىڭلارغا ئوخشاش) بىر ـ بىرىڭلارغا ياردەم قىلمايسىلەر؟» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(37,26,'37:26',' بەلكى ئۇلار بۈگۈن بويسۇنغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(37,27,'37:27',' ئۇلار بىر ـ بىرىگە قاراپ مۇنازىرىلىشىدۇ.');
INSERT INTO "saleh" VALUES(37,28,'37:28','ئەگەشكۈچىلەر ئەگەشتۈرگۈچىلەرگە: «سىلەر (سۆزۈڭلارنىڭ راستلىقىغا) قەسەم ئىچىپ بىزنى ئىماندىن توساتتىڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(37,29,'37:29','(ئەگەشتۈرگۈچىلەر ئەگەشكۈچىلەرگە) ئېيتىدۇ: «سىلەر (ئۆز ئىختىيارىڭلار بىلەن) مۆمىن بولمىدىڭلار.');
INSERT INTO "saleh" VALUES(37,30,'37:30',' بىزنىڭ سىلەرنى (بىزگە ئەگىشىشكە) زورلايدىغان كۈچىمىز يوق ئىدى، بەلكى سىلەر (بىزگە ئوخشاش) ئازغان قەۋم ئىدىڭلار.');
INSERT INTO "saleh" VALUES(37,31,'37:31',' پەرۋەردىگارىمىزنىڭ بىزنى (جازالاش) سۆزى بىزگە تېگىشلىك بولدى. بىز ھەقىقەتەن (شۇ سۆز بويىچە ئازابنى) چوقۇم تېتىغۇچىمىز.');
INSERT INTO "saleh" VALUES(37,32,'37:32',' سىلەرنى بىز ئازدۇردۇق، چۈنكى بىز ھەقىقەتەن ئازغان ئىدۇق».');
INSERT INTO "saleh" VALUES(37,33,'37:33','ئۇلار (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەر) بۇ كۈندە (يەنى قىيامەت كۈنىدە) ئازابتا ئورتاقتۇر.');
INSERT INTO "saleh" VALUES(37,34,'37:34',' بىز ھەقىقەتەن گۇناھكارلارغا مۇشۇنداق قىلىمىز، (يەنى ئەگەشتۈرگۈچىلەر بىلەن ئەگەشكۈچىلەرنىڭ ھەممىسىگە ئازاب قىلىمىز).');
INSERT INTO "saleh" VALUES(37,35,'37:35',' ئۇلار ھەقىقەتەن مۇشۇنداق ئىدىكى، ئۇلارغا: «بىر ئاللاھتىن باشقا ھېچ ئىلاھ يوق» (دەڭلار) دېيىلسە، (كەلىمە تەۋھىدتىن) تەكەببۇرلۇق قىلىپ باش تارتاتتى.');
INSERT INTO "saleh" VALUES(37,36,'37:36',' ئۇلار: «ئىلاھلىرىمىزنى ھەقىقەتەن بىر مەجنۇن شائىر ئۈچۈن (يەنى ئۇنىڭ سۆزى ئۈچۈن) تاشلىۋېتەمدۇق؟» دەيتتى. ');
INSERT INTO "saleh" VALUES(37,37,'37:37','ھەرگىز ئۇنداق ئەمەس (يەنى ئىش ئۇلارنىڭ بوھتان قىلغىنىدەك ئەمەس)، ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ھەق (دىن) نى ئېلىپ كەلدى ۋە پەيغەمبەرلەرنى تەستىق قىلدى.');
INSERT INTO "saleh" VALUES(37,38,'37:38',' (ئى گۇناھكارلار!) سىلەر (چوقۇم) قاتتىق ئازابنى تېتىغۇچىسىلەر.');
INSERT INTO "saleh" VALUES(37,39,'37:39','سىلەر پەقەت قىلمىشىڭلارغا يارىشا جازالىنىسىلەر.');
INSERT INTO "saleh" VALUES(37,40,'37:40',' پەقەت ئاللاھنىڭ سادىق بەندىلىرىلا جازاغا تارتىلمايدۇ.');
INSERT INTO "saleh" VALUES(37,41,'37:41',' ئۇلار مەلۇم رىزىقتىن (يەنى تۈرلۈك مېۋىلەردىن) بەھرىمەن بولىدۇ، ئۇلار ھۆرمەتلىنىدۇ[41ـ42].');
INSERT INTO "saleh" VALUES(37,42,'37:42',' ئۇلار مەلۇم رىزىقتىن (يەنى تۈرلۈك مېۋىلەردىن) بەھرىمەن بولىدۇ، ئۇلار ھۆرمەتلىنىدۇ[41ـ42].');
INSERT INTO "saleh" VALUES(37,43,'37:43',' ئۇلار نازۇنېمەتلىك باغلاردا، تەختلەر ئۈستىدە بىر ـ بىرىگە قارىشىپ ئولتۇرىدۇ[43ـ44]. ');
INSERT INTO "saleh" VALUES(37,44,'37:44',' ئۇلار نازۇنېمەتلىك باغلاردا، تەختلەر ئۈستىدە بىر ـ بىرىگە قارىشىپ ئولتۇرىدۇ[43ـ44]. ');
INSERT INTO "saleh" VALUES(37,45,'37:45',' ئۇلارغا ئېقىپ تۇرغان، ئاپئاق، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان شارابلار بىلەن تولدۇرۇلغان جاملار ئايلاندۇرۇپ تۇرۇلىدۇ[45ـ46].');
INSERT INTO "saleh" VALUES(37,46,'37:46',' ئۇلارغا ئېقىپ تۇرغان، ئاپئاق، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان شارابلار بىلەن تولدۇرۇلغان جاملار ئايلاندۇرۇپ تۇرۇلىدۇ[45ـ46].');
INSERT INTO "saleh" VALUES(37,47,'37:47',' ئۇنىڭدا مەست قىلىدىغان نەرسە يوق، ئۇلار ئۇنى ئىچىش بىلەن مەست بولمايدۇ.');
INSERT INTO "saleh" VALUES(37,48,'37:48',' ئۇلارنىڭ يېنىدا ئەرلىرىدىن غەيرىيگە كۆز سالمايدىغان شەھلا كۆزلۈك (جۈپتىلەر) بولىدۇ.');
INSERT INTO "saleh" VALUES(37,49,'37:49',' ئۇلار (ئاقلىقتا) گويا قول تەگمەي ساقلانغان تۆگىقۇشنىڭ تۇخۇملىرىغا ئوخشايدۇ.');
INSERT INTO "saleh" VALUES(37,50,'37:50',' ئۇلار (يەنى ئەھلى جەننەت) بىر ـ بىرىگە قارىشىپ پاراڭ سېلىشىدۇ.');
INSERT INTO "saleh" VALUES(37,51,'37:51','ئۇلاردىن بىرى ئېيتىدۇ: «مېنىڭ بىر دوستۇم بولۇپ، ئۇ (ماڭا) "سەن (ئۆلگەندىن كېيىن تىرىلىشكە) ھەقىقىي ئىشىنەمسەن[51ـ52].');
INSERT INTO "saleh" VALUES(37,52,'37:52','ئۇلاردىن بىرى ئېيتىدۇ: «مېنىڭ بىر دوستۇم بولۇپ، ئۇ (ماڭا) "سەن (ئۆلگەندىن كېيىن تىرىلىشكە) ھەقىقىي ئىشىنەمسەن[51ـ52].');
INSERT INTO "saleh" VALUES(37,53,'37:53','بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا بىز چوقۇم (قىلمىشلىرىمىزدىن) ھېساب بېرەمدۇق؟" دەيتتى».');
INSERT INTO "saleh" VALUES(37,54,'37:54',' ئۇ (بۇرادەرلىرىگە): «سىلەر ئۇنى (يەنى دوستۇمنى) كۆرەمسىلەر؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(37,55,'37:55',' ئۇ قاراپلا ئۇنى (يەنى كاپىر دوستىنى) دوزاخنىڭ ئوتتۇرىسىدا كۆرىدۇ.');
INSERT INTO "saleh" VALUES(37,56,'37:56',' ئۇ (يەنى مۆمىن) ئېيتىدۇ: «ئاللاھ بىلەن قەسەمكى، مېنى (ئازدۇرۇپ) ھالاك قىلغىلى تاس قېلىپتىكەنسەن.');
INSERT INTO "saleh" VALUES(37,57,'37:57',' ئەگەر پەرۋەردىگارىمنىڭ (مېنى ئىمانىمدا مۇستەھكەم قىلىشتىن ئىبارەت) مەرھەمىتى بولمىغان بولسا، ئەلۋەتتەمەن (سەن بىلەن بىللە دوزاخقا) ھازىر قىلىنغانلاردىن بولاتتىم.');
INSERT INTO "saleh" VALUES(37,58,'37:58',' بىر قتېىم ئۆلگەندىن كېيىن ئەمدى ئۆلمەيمىز، ئازابقا دۇچار بولمايمىز ئەمەسمۇ؟ [58ـ59].');
INSERT INTO "saleh" VALUES(37,59,'37:59',' بىر قتېىم ئۆلگەندىن كېيىن ئەمدى ئۆلمەيمىز، ئازابقا دۇچار بولمايمىز ئەمەسمۇ؟ [58ـ59].');
INSERT INTO "saleh" VALUES(37,60,'37:60',' بۇ (يەنى ئەھلى جەننەت ئېرىشكەن نېمەت) ھەقىقەتەن زور بەختتۇر.');
INSERT INTO "saleh" VALUES(37,61,'37:61',' ئىشلىگۈچىلەر مۇشۇنداق زور بەختكە ئېرىشىش ئۈچۈن ئىشلىسۇن».');
INSERT INTO "saleh" VALUES(37,62,'37:62',' بۇ (يەنى جەننەتنىڭ نېمەتلىرى) ياخشى زىياپەتمۇ ياكى زەققۇم دەرىخىمۇ؟');
INSERT INTO "saleh" VALUES(37,63,'37:63',' زەققۇمنى بىز زالىملار (يەنى گۇمراھلار) ئۈچۈن (ئاخىرەتتە) ئازاب قىلدۇق.');
INSERT INTO "saleh" VALUES(37,64,'37:64','شۈبھىسىزكى، ئۇ جەھەننەمنىڭ قەئرىدە ئۆسىدىغان دەرەختۇر.');
INSERT INTO "saleh" VALUES(37,65,'37:65','ئۇنىڭ مېۋىسى گويا شەيتانلارنىڭ باشلىرىغا ئوخشايدۇ.');
INSERT INTO "saleh" VALUES(37,66,'37:66',' ئۇلار شۈبھىسىز شۇ مېۋىلەردىن يەيدۇ، ئۇنىڭ بىلەن قورساقلىرىنى تويغۇزىدۇ.');
INSERT INTO "saleh" VALUES(37,67,'37:67',' ئاندىن ئۇلار ئۇنىڭ ئۈستىگە (يىرىڭ بىلەن) قايناقسۇنىڭ ئارىلاشتۇرمىسىنى ئىچىدۇ.');
INSERT INTO "saleh" VALUES(37,68,'37:68',' ئاندىن ئۇلارنىڭ قايتىدىغان جايى چوقۇم دوزاخ بولىدۇ.');
INSERT INTO "saleh" VALUES(37,69,'37:69',' ئۇلار ھەقىقەتەن ئاتا ـ بوۋىلىرىنى گۇمراھ بىلدى.');
INSERT INTO "saleh" VALUES(37,70,'37:70',' ئۇلار ئاتا ـ بوۋىلىرىنىڭ ئىزلىرىدىن يۈگۈردى.');
INSERT INTO "saleh" VALUES(37,71,'37:71',' ئۇلاردىن ئىلگىرى بۇرۇنقىلار (يەنى ئۆتكەنكى ئۈممەتلەر) نىڭ تولىسى ھەقىقەتەن ئازدى.');
INSERT INTO "saleh" VALUES(37,72,'37:72',' بىز ئۇلارغا ھەقىقەتەن ئاگاھلاندۇرغۇچىلارنى (يەنى پەيغەمبەرلەرنى) ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(37,73,'37:73',' ئاگاھلاندۇرۇلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولىدىغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(37,74,'37:74',' لېكىن ئاللاھنىڭ سادىق بەندىلىرى (ئازابتىن قۇتۇلدى).');
INSERT INTO "saleh" VALUES(37,75,'37:75',' نۇھ ھەقىقەتەن بىزگە ئىلتىجا قىلدى. بىز (ئۇنىڭ ئىلتىجاسىنى) نېمىدېگەن ياخشى قوبۇل قىلغۇچىدۇرمىز!.');
INSERT INTO "saleh" VALUES(37,76,'37:76',' بىز ئۇنى ۋە ئۇنىڭ تەۋەلىرىنى چوڭ غەمدىن (يەنى سۇدا غەرق بولۇپ كېتىشتىن) قۇتقۇزدۇق.');
INSERT INTO "saleh" VALUES(37,77,'37:77',' ئۇنىڭ نەسلىنى باقىي قالدۇردۇق.');
INSERT INTO "saleh" VALUES(37,78,'37:78','كېيىنكىلەر (يەنى كېيىنكى ئەۋلادلار) ئىچىدە ئۇنىڭغا ياخشى نام قالدۇردۇق.');
INSERT INTO "saleh" VALUES(37,79,'37:79','خالايىقلار نۇھقا سالام يوللايدۇ.');
INSERT INTO "saleh" VALUES(37,80,'37:80',' بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(37,81,'37:81',' ئۇ ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىن ئىدى.');
INSERT INTO "saleh" VALUES(37,82,'37:82',' ئاندىن بىز باشقىلارنى (يەنى نۇھ قەۋمىنىڭ كاپىرلىرىنى سۇدا) غەرق قىلدۇق.');
INSERT INTO "saleh" VALUES(37,83,'37:83',' ئىبراھىممۇ ھەقىقەتەن نۇھنىڭ دىنىغا تەۋەلەردىن ئىدى.');
INSERT INTO "saleh" VALUES(37,84,'37:84','ئۆز ۋاقتىدا ئىبراھىم پەرۋەردىگارىغا ساغلام (يەنى مۇشرىكلىكتىن، شەكتىن پاك) دىل بىلەن كەلگەن ئىدى. ');
INSERT INTO "saleh" VALUES(37,85,'37:85',' ئۆز ۋاقتىدا ئۇ ئاتىسى (ئازەر) گە ۋە قەۋمىگە (ئۇلارنى ئەيىبلەپ) ئېيتتى: «سىلەر نېمىگە ئىبادەت قىلىۋاتىسىلەر؟');
INSERT INTO "saleh" VALUES(37,86,'37:86',' سىلەر ئاللاھنى قويۇپ يالغان ئىلاھلارغا چوقۇنامسىلەر؟');
INSERT INTO "saleh" VALUES(37,87,'37:87',' ئالەملەرنىڭ پەرۋەردىگارى بولغان (ئاللاھقا) نېمىشقا گۇمان قىلىسىلەر؟»');
INSERT INTO "saleh" VALUES(37,88,'37:88',' ئۇ يۇلتۇزلارغا قارىدى، [88 ـ ]');
INSERT INTO "saleh" VALUES(37,89,'37:89',' ئاندىن: «مەن ھەقىقەتەن كېسەل بولۇپ قالىمەن» دېدى[ 89].');
INSERT INTO "saleh" VALUES(37,90,'37:90','ئۇلار ئۇنىڭدىن يۈز ئۆرۈپ ئايرىلىشتى (يەنى ئۇنى تاشلاپ كېتىشتى).');
INSERT INTO "saleh" VALUES(37,91,'37:91','ئىبراھىم ئاستا ئۇلارنىڭ بۇتلىرىنىڭ يېنىغا بېرىپ: «(مەسخىرە قىلىش يۈزىسىدىن، بۇ تاماقتىن) يېمەمسىلەر؟ نېمىشقا گەپ قىلمايسىلەر؟» دېدى[91ـ92].');
INSERT INTO "saleh" VALUES(37,92,'37:92','ئىبراھىم ئاستا ئۇلارنىڭ بۇتلىرىنىڭ يېنىغا بېرىپ: «(مەسخىرە قىلىش يۈزىسىدىن، بۇ تاماقتىن) يېمەمسىلەر؟ نېمىشقا گەپ قىلمايسىلەر؟» دېدى[91ـ92].');
INSERT INTO "saleh" VALUES(37,93,'37:93',' ئاندىن ئۇلارغا يۈزلىنىپ ئوڭ قولى بىلەن ئۇرۇپ پاچاقلاشقا كىرىشتى.');
INSERT INTO "saleh" VALUES(37,94,'37:94',' ئۇلار (يەنى قەۋمى) ئىبراھىمنىڭ يېنىغا ئالدىراپ ـ سالدىراپ كېلىپ: «بىز ئۇلارغا چوقۇنۇۋاتساق، سەن ئۇلارنى چېقىپ تاشلامسەن؟» دېدى.');
INSERT INTO "saleh" VALUES(37,95,'37:95','ئىبراھىم ئېيتتى: «(ئۆزۈڭلار) ئويۇپ ياسىغان بۇتلارغا چوقۇنامسىلەر؟.');
INSERT INTO "saleh" VALUES(37,96,'37:96',' ھالبۇكى، سىلەرنى ۋە ياسىغان نەرسەڭلارنى ئاللاھ ياراتقاندۇر».');
INSERT INTO "saleh" VALUES(37,97,'37:97',' ئۇلار: «ئىبراھىم ئۈچۈن بىر بىنا سېلىڭلار، (ئۇنى ئوتۇن بىلەن تولدۇرۇپ ئوت يېقىڭلار، ئوت يانغاندا) ئۇنى ئوتقا تاشلاڭلار» دېدى. (ئۇلار ئىبراھىمنى ئوتقا تاشلاش بىلەن) ئۇنىڭغا سۇيىقەست قىلماقچى بولدى.');
INSERT INTO "saleh" VALUES(37,98,'37:98',' بىز (ئىبراھىمنى ئوتتىن سالامەت قۇتقۇزۇش بىلەن) ئۇلارنى مەغلۇپ قىلدۇق.');
INSERT INTO "saleh" VALUES(37,99,'37:99',' ئىبراھىم ئېيتتى: «مەن ھەقىقەتەن پەرۋەردىگارىم مېنى بۇيرۇغان جايغا ھىجرەت قىلىمەن، ئۇ مېنى يېتەكلەيدۇ.');
INSERT INTO "saleh" VALUES(37,100,'37:100','پەرۋەردىگارىم! ماڭا بىر ياخشى پەرزەنت ئاتا قىلغىن».');
INSERT INTO "saleh" VALUES(37,101,'37:101',' بىز ئىبراھىمغا ناھايىتى مۇلايىم بىر ئوغۇل بىلەن خۇشخەۋەر بەردۇق.');
INSERT INTO "saleh" VALUES(37,102,'37:102',' ئۇ ئىبراھىمنىڭ ئىش ـ كۈشلىرىگە يارىغۇدەك بولغان چاغدا، ئىبراھىم: «ئى ئوغلۇم! مەن سېنى بوغۇزلاپ قۇربانلىق قىلىشقا (ئەمر قىلىنىپ) چۈشەپتىمەن (پەيغەمبەرلەرنىڭ چۈشى ھەقىقەتتۇر ۋە ئۇلارنىڭ ئىشلىرى ئاللاھنىڭ ئەمرى بويىچە بولىدۇ)، ئويلاپ باققىنا! سېنىڭ قانداق پىكرىڭ بار؟» دېدى. ئۇ ئېيتتى: «ئى ئاتا! نېمىگە بۇيرۇلغان بولساڭ، شۇنى ئىجرا قىلغىن، خۇدا خالىسا مېنى (ئۇنىڭغا) سەۋر قىلغۇچى تاپىسەن».');
INSERT INTO "saleh" VALUES(37,103,'37:103',' ئىككىسى (ئاللاھنىڭ ئەمرىگە) بويسۇندى، ئىبراھىم ئۇنى (يەنى ئوغلىنى بوغۇزلاش ئۈچۈن) يېنى ياتقۇزدى.');
INSERT INTO "saleh" VALUES(37,104,'37:104',' بىز ئۇنىڭغا: «ئى ئىبراھىم! (ھېلىقى) چۈشنى ئىشقا ئاشۇردۇڭ (يەنى چۈشۈڭدە بۇيرۇلغاننى بەجا كەلتۈردۈڭ)» دەپ نىدا قىلدۇق. بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز[104ـ105].');
INSERT INTO "saleh" VALUES(37,105,'37:105',' بىز ئۇنىڭغا: «ئى ئىبراھىم! (ھېلىقى) چۈشنى ئىشقا ئاشۇردۇڭ (يەنى چۈشۈڭدە بۇيرۇلغاننى بەجا كەلتۈردۈڭ)» دەپ نىدا قىلدۇق. بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز[104ـ105].');
INSERT INTO "saleh" VALUES(37,106,'37:106',' بۇ ھەقىقەتەن روشەن سىناقتۇر.');
INSERT INTO "saleh" VALUES(37,107,'37:107',' بىز ئۇنىڭ ئورنىغا چوڭ بىر قۇربانلىقنى (يەنى جەننەتتىن چىققان قوچقارنى) بەردۇق.');
INSERT INTO "saleh" VALUES(37,108,'37:108',' كېيىنكىلەر ئىچىدە ئۇنىڭغا ياخشى نام قالدۇردۇق.');
INSERT INTO "saleh" VALUES(37,109,'37:109',' ئىبراھىمغا سالام بولسۇن!');
INSERT INTO "saleh" VALUES(37,110,'37:110','بىز ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(37,111,'37:111',' ئۇ ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر.');
INSERT INTO "saleh" VALUES(37,112,'37:112','ئۇنىڭغا بىز پەيغەمبەر بولىدىغان ۋە ياخشىلاردىن بولىدىغان ئىسھاق بىلەن خۇشخەۋەر بەردۇق.');
INSERT INTO "saleh" VALUES(37,113,'37:113','ئىبراھىمغا ۋە ئىسھاققا (دىننىڭ ۋە دۇنيانىڭ) بەرىكەتلىرىنى ئاتا قىلدۇق، ئۇلارنىڭ نەسلىدىن ياخشى ئىش قىلغۇچىلارمۇ، (گۇناھ ۋە كۇفرى بىلەن) ئۆزىگە ئاشكارا زۇلۇم قىلغۇچىلارمۇ چىقىدۇ.');
INSERT INTO "saleh" VALUES(37,114,'37:114',' بىز ھەقىقەتەن مۇسا بىلەن ھارۇنغا (پەيغەمبەرلىك بىلەن) ئېھسان قىلدۇق. ');
INSERT INTO "saleh" VALUES(37,115,'37:115',' ئۇلارنى ۋە ئۇلارنىڭ قەۋمىنى (يەنى بەنى ئىسرائىلنى) چوڭ غەمدىن (يەنى پىرئەۋننىڭ ئۇلارنى قۇل قىلىشىدىن) خالاس تاپقۇزدۇق.');
INSERT INTO "saleh" VALUES(37,116,'37:116',' بىز ئۇلارغا ياردەم بەردۇق، شۇنىڭ ئۈچۈن ئۇلار غەلىبە قىلدى.');
INSERT INTO "saleh" VALUES(37,117,'37:117',' ئۇلارغا روشەن بىر كىتابنى (يەنى تەۋراتنى) بەردۇق.');
INSERT INTO "saleh" VALUES(37,118,'37:118','ئۇلارنى توغرا يولغا باشلىدۇق.');
INSERT INTO "saleh" VALUES(37,119,'37:119','كېيىنكىلەر ئىچىدە ئۇلارغا ياخشى نام قالدۇردۇق.');
INSERT INTO "saleh" VALUES(37,120,'37:120',' مۇساغا ۋە ھارۇنغا (بىزدىن) سالام بولسۇن!');
INSERT INTO "saleh" VALUES(37,121,'37:121',' بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(37,122,'37:122','ئۇلار ھەقىقەتەن بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر');
INSERT INTO "saleh" VALUES(37,123,'37:123',' ئىلياسمۇ ھەقىقەتەن پەيغەمبەرلەردىندۇر. ');
INSERT INTO "saleh" VALUES(37,124,'37:124','ئۆز ۋاقتىدا ئۇ قەۋمىگە ئېيتتى: «(ئاللاھتىن) قورقمامسىلەر؟ ');
INSERT INTO "saleh" VALUES(37,125,'37:125','سىلەر ياراتقۇچىلارنىڭ ئەڭ ئەۋزىلى (ئاللاھنى) تەرك ئېتىپ، بەئلى (ناملىق بۇت) قا چوقۇنامسىلەر؟ ');
INSERT INTO "saleh" VALUES(37,126,'37:126',' ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر ۋە بۇرۇنقى ئاتا ـ بوۋاڭلارنىڭ پەرۋەردىگارىدۇر».');
INSERT INTO "saleh" VALUES(37,127,'37:127',' ئۇلار ئۇنى ئىنكار قىلدى. شەك ـ شۈبھىسىزكى، ئۇلار (ئازابقا) ھازىر قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(37,128,'37:128','پەقەت ئاللاھنىڭ ئىخلاسمەن بەندىلىرى بۇنىڭدىن مۇستەسنا.');
INSERT INTO "saleh" VALUES(37,129,'37:129','كېيىنكىلەر ئىچىدە ئۇنىڭغا (يەنى ئىلياسقا) ياخشى نام قالدۇردۇق.');
INSERT INTO "saleh" VALUES(37,130,'37:130','ئىلياسقا (بىزدىن) سالام بولسۇن!.');
INSERT INTO "saleh" VALUES(37,131,'37:131',' بىز ھەقىقەتەن ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(37,132,'37:132','ھەقىقەتەن ئۇ بىزنىڭ مۆمىن بەندىلىرىمىزدىندۇر.');
INSERT INTO "saleh" VALUES(37,133,'37:133',' لۇت ھەقىقەتەن پەيغەمبەرلەردىندۇر.');
INSERT INTO "saleh" VALUES(37,134,'37:134',' ئۆز ۋاقتىدا لۇتنى ۋە ئۇنىڭ تەۋەلىرىنىڭ ھەممىسىنى قۇتقۇزدۇق.');
INSERT INTO "saleh" VALUES(37,135,'37:135',' پەقەت موماي (يەنى لۇتنىڭ ئايالى) نى قالدۇرۇپ ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(37,136,'37:136',' ئاندىن قالغانلارنى (يەنى ئۇنىڭ قەۋمىدىن بولغان كۇففارلارنى) ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(37,137,'37:137',' (ئى ئەھلى مەككە!) سىلەر ئۇلارنىڭ يەرلىرىدىن ئەتىگەن ـ ئاخشامدا ئۆتۈپ تۇرىسىلەر، سىلەر چۈشەنمەمسىلەر؟[137 ـ 138].');
INSERT INTO "saleh" VALUES(37,138,'37:138',' (ئى ئەھلى مەككە!) سىلەر ئۇلارنىڭ يەرلىرىدىن ئەتىگەن ـ ئاخشامدا ئۆتۈپ تۇرىسىلەر، سىلەر چۈشەنمەمسىلەر؟[137 ـ 138].');
INSERT INTO "saleh" VALUES(37,139,'37:139',' يۇنۇس ھەقىقەتەن پەيغەمبەرلەردىندۇر');
INSERT INTO "saleh" VALUES(37,140,'37:140',' ئۆز ۋاقتىدا ئۇ قەۋمىدىن قېچىپ (كىشىلەر بىلەن) توشقان بىر كېمىگە چىقىۋالدى.');
INSERT INTO "saleh" VALUES(37,141,'37:141',' (كېمىدىكىلەر چەك چىققان ئادەمنى دېڭىزغا تاشلاپ كېمىنىڭ يۈكىنى يېنىكلىتىش ئۈچۈن) چەك تاشلاشتى، يۇنۇس (چەكتە) مەغلۇب بولغانلاردىن بولۇپ (دېڭىزغا تاشلاندى).');
INSERT INTO "saleh" VALUES(37,142,'37:142',' ئۇنى چوڭ بىر بېلىق يۇتۇۋەتتى. ئۇ (قەۋمىنى تاشلاپ، پەرۋەردىگارىنىڭ ئىزنىسىز چىققانلىقى ئۈچۈن) ئەيىبلىنىشكە تېگىشلىك ئىدى.');
INSERT INTO "saleh" VALUES(37,143,'37:143',' ئەگەر ئۇ تەسبىھ ئېيتقۇچىلاردىن بولمىسا ئىدى، ');
INSERT INTO "saleh" VALUES(37,144,'37:144','بېلىقنىڭ قارنىدا ئەلۋەتتە قىيامەتكىچە قالاتتى[-144].');
INSERT INTO "saleh" VALUES(37,145,'37:145','ئۇنى بىز (دېڭىز ساھىلىدىكى) بىر قۇرغاق يەرگە (بېلىقنىڭ قارنىدىن) ئاتتۇق، ئەينى ۋاقىتتا ئۇ كېسەل ئىدى.');
INSERT INTO "saleh" VALUES(37,146,'37:146',' ئۇنىڭ ئۈستىگە (سايە تاشلاپ تۇرۇش ئۈچۈن) بىر تۈپ كاۋىنى ئۆستۈرۈپ بەردۇق. ئ');
INSERT INTO "saleh" VALUES(37,147,'37:147','ئۇنى بىز 100 مىڭ، بەلكى (بۇنىڭدىنمۇ) كۆپ (بىر قەۋمگە پەيغەمبەر قىلىپ) ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(37,148,'37:148',' (ئۇلار ۋەدە قىلىنغان ئازابنى كۆرگەن چاغدا) ئىمان ئېيتتى. مۇئەييەن مۇددەتكىچە (يەنى ئەجىلى يەتكەنگە قەدەر) ئۇلارنى (ھاياتتىن) بەھرىمەن قىلدۇق.');
INSERT INTO "saleh" VALUES(37,149,'37:149','(ئى مۇھەممەد!) ئۇلار (يەنى ئەھلى مەككە) دىن سوراپ باققىنكى، قىزلار پەرۋەردىگارىڭغا، ئوغۇللار ئۇلارغا مەنسۇپمۇ؟');
INSERT INTO "saleh" VALUES(37,150,'37:150','ياكى بىز پەرىشتىلەرنى چىشى ياراتقان بولۇپ، ئۇلار (بىزنىڭ ياراتقانلىقىمىزنى) كۆرۈپ تۇرغانمۇ؟ (شۇنىڭ ئۈچۈن شۇنداق دەمدۇ؟).');
INSERT INTO "saleh" VALUES(37,151,'37:151',' بىلىڭلاركى، ئۇلار ھەقىقەتەن يالغاننى توقۇپ: «ئاللاھنىڭ بالىسى بار» دەيدۇ. ئۇلار ئەلۋەتتە يالغانچىلاردۇر[151- 152].');
INSERT INTO "saleh" VALUES(37,152,'37:152',' بىلىڭلاركى، ئۇلار ھەقىقەتەن يالغاننى توقۇپ: «ئاللاھنىڭ بالىسى بار» دەيدۇ. ئۇلار ئەلۋەتتە يالغانچىلاردۇر[151- 152].');
INSERT INTO "saleh" VALUES(37,153,'37:153',' ئاللاھ ئوغۇللارنى تاللىماي، قىزلارنى تاللىغانمۇ؟');
INSERT INTO "saleh" VALUES(37,154,'37:154',' سىلەرگە نېمە بولدى؟ قانداقچە مۇنداق ھۆكۈم چىقىرىسىلەر؟');
INSERT INTO "saleh" VALUES(37,155,'37:155','ئويلاپ باقمامسىلەر؟ ');
INSERT INTO "saleh" VALUES(37,156,'37:156','ياكى (ئاللاھنىڭ بالىسى بارلىقىغا) ئېنىق پاكىتىڭلار بارمۇ؟');
INSERT INTO "saleh" VALUES(37,157,'37:157',' (ئەگەر سۆزۈڭلاردا) راستچىل بولساڭلار كىتابىڭلار (يەنى تەۋرات) نى ئېلىپ كېلىپ (ماڭا كۆرسىتىڭلار).');
INSERT INTO "saleh" VALUES(37,158,'37:158',' ئۇلار: «ئاللاھ بىلەن جىنلارنىڭ ئارىسىدا قۇدا ـ باجىلىق بار» دەپ جۆيلۈدى. جىنلار شەك ـ شۈبھىسىز بىلىدۇكى، ئۇلار (يەنى شۇ سۆزنى قىلغۇچىلار) (دوزاخقا) چوقۇم ھازىر قىلىنىپ (شۇ يەردە ئازاب چېكىدۇ). ');
INSERT INTO "saleh" VALUES(37,159,'37:159','ئاللاھ ئۇلارنىڭ (ئاللاھنىڭ بالىسى بار دەپ) سۈپەتلىگەنلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(37,160,'37:160',' لېكىن ئاللاھنىڭ ئىخلاسمەن بەندىلىرى بۇنىڭدىن مۇستەسنا (يەنى ئاللاھنى ئۇلارنىڭ سۈپەتلىگەنلىرىدىن پاك ئېتىقاد قىلىدۇ).');
INSERT INTO "saleh" VALUES(37,161,'37:161','(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (ئاللاھ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان مەبۇدلارمۇ ئازدۇرالمايدۇ[161- 163].');
INSERT INTO "saleh" VALUES(37,162,'37:162','(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (ئاللاھ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان مەبۇدلارمۇ ئازدۇرالمايدۇ[161- 163].');
INSERT INTO "saleh" VALUES(37,163,'37:163','(ئى كاپىرلار!) شۈبھىسىزكى، سىلەر (ئاللاھ) دوزاخقا كىرىشنى (پۈتۈۋەتكەن) كىشىدىن باشقا ھېچ ئەھەدىنى ئازدۇرالمايسىلەر، سىلەر چوقۇنۇۋاتقان مەبۇدلارمۇ ئازدۇرالمايدۇ[161- 163].');
INSERT INTO "saleh" VALUES(37,164,'37:164',' (پەرىشتىلەر) ئېيتىدۇ: «بىزنىڭ ھەر بىرىمىزنىڭ مۇئەييەن ئورنى بار.');
INSERT INTO "saleh" VALUES(37,165,'37:165','بىز ھەقىقەتەن (ئىبادەتتە) سەپ تارتىپ تۇرغۇچىلارمىز.');
INSERT INTO "saleh" VALUES(37,166,'37:166',' بىز ھەقىقەتەن (ئاللاھقا) تەسبىھ ئېيتقۇچىلارمىز».');
INSERT INTO "saleh" VALUES(37,167,'37:167','ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، ئاللاھنىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ[167- 169].');
INSERT INTO "saleh" VALUES(37,168,'37:168','ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، ئاللاھنىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ[167- 169].');
INSERT INTO "saleh" VALUES(37,169,'37:169','ئۇلار (يەنى قۇرەيش كۇففارلىرى) ھەمىشە: «ئەگەر بىزدە بۇرۇنقىلارنىڭكىدەك (يەنى ئۆتكەنكى ئۈممەتلەرنىڭ كىتابلىرىدەك) بىر كىتاب بولسا ئىدى. ئەلۋەتتە، ئاللاھنىڭ ئىخلاسمەن بەندىلىرى بولاتتۇق» دەيدۇ[167- 169].');
INSERT INTO "saleh" VALUES(37,170,'37:170',' ئۇلار ئۇنى (يەنى قۇرئاننى) ئىنكار قىلىشتى، ئۇلار ئۇزاققا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) بىلىدۇ.');
INSERT INTO "saleh" VALUES(37,171,'37:171',' بىزنىڭ پەيغەمبەر بەندىلىرىمىز ھەققىدىكى سۆزلىرىمىز ئالدىنئالا ئېيتىلغان.');
INSERT INTO "saleh" VALUES(37,172,'37:172',' ئۇلار چوقۇم نۇسرەت تاپقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(37,173,'37:173','بىزنىڭ قوشۇنىمىز چوقۇم غەلىبە قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(37,174,'37:174','ئۇلار (يەنى مەككە كۇففارلىرى) دىن ۋاقىتلىق يۈز ئۆرۈپ تۇرغىن.');
INSERT INTO "saleh" VALUES(37,175,'37:175',' ئۇلارنى (ئۇلارغا ئازاب نازىل بولغان چاغدا) كۆرگىن، ئۇلارمۇ ئۇزۇنغا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) كۆرىدۇ.');
INSERT INTO "saleh" VALUES(37,176,'37:176','ئۇلار ئازابىمىزنىڭ بالدۇر نازىل بولۇشىنى تەلەپ قىلامدۇ؟.');
INSERT INTO "saleh" VALUES(37,177,'37:177',' ئازاب ئۇلارنىڭ ھويلىسىغا چۈشكەن چاغدا، ئاگاھلاندۇرۇلغۇچىلارنىڭ ئەتىگىنى نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(37,178,'37:178',' ئۇلاردىن ۋاقىتلىق يۈز ئۆرۈپ تۇرغىن. ');
INSERT INTO "saleh" VALUES(37,179,'37:179',' ئۇلارنى (ئۇلارغا ئازاب نازىل بولغان چاغدا) كۆرگىن، ئۇلارمۇ ئۇزۇنغا قالماي (كۇفرىنىڭ ئاقىۋىتىنى) كۆرىدۇ.');
INSERT INTO "saleh" VALUES(37,180,'37:180',' قۇدرەت ئىگىسى بولغان پەرۋەردىگارىڭ ئۇلارنىڭ سۈپەتلىگەنلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(37,181,'37:181','پەيغەمبەرلەرگە سالام بولسۇن! ');
INSERT INTO "saleh" VALUES(37,182,'37:182','جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!');
INSERT INTO "saleh" VALUES(38,1,'38:1','ساد. شانلىق قۇرئان بىلەن قەسەمكى، (بۇ قۇرئان ھەقىقەتەن مۆجىزىدۇر).');
INSERT INTO "saleh" VALUES(38,2,'38:2',' بەلكى كاپىرلار ئىماندىن باش تارتماقتا ۋە (رەسۇلۇللاھقا) مۇخالىپەتچىلىك قىلماقتا.');
INSERT INTO "saleh" VALUES(38,3,'38:3','ئۇلاردىن ئىلگىرى (كۇفرى تۈپەيلىدىن) نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار (ئازابنى كۆرگەندە) پەرياد قىلدى، ھالبۇكى، (بۇ) قاچىدىغان ۋاقىت ئەمەس.');
INSERT INTO "saleh" VALUES(38,4,'38:4','ئۇلارغا ئۆزلىرىنىڭ ئىچىدىن بىر ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلگەنلىكىگە ئۇلار ھەيران قېلىشتى، كاپىرلار ئېيتتى: «بۇ بىر يالغانچى سېھىرگەردۇر.');
INSERT INTO "saleh" VALUES(38,5,'38:5',' ئۇ نۇرغۇن ئىلاھلارنى بىر ئىلاھ قىلماقچىمۇ؟ بۇ ھەقىقەتەن غەلىتە ئىش».');
INSERT INTO "saleh" VALUES(38,6,'38:6',' ئۇلارنىڭ (يەنى قۇرەيشنىڭ) كاتتىلىرى (سورۇنلىرىدىن) قوزغىلىپ چىقىپ ئېيتتى: «سىلەر مېڭىڭلار، مەبۇدلىرىڭلارغا ئىبادەت قىلىشتا چىڭ تۇرۇڭلار، بۇ ھەقىقەتەن (بىزدىن) ئىرادە قىلىنىدىغان بىر ئىش (يەنى مۇھەممەد سىلەرنى دىنىڭلاردىن تاندۇرۇپ، سىلەرگە ئۆزىنىڭ ئۈستۈنلۈك قازىنىشىنى ئىرادە قىلىدۇ).');
INSERT INTO "saleh" VALUES(38,7,'38:7',' بۇنى بىز باشقا بىر دىندىن (يەنى ناسارا دىنىدىكىلەردىن) ئاڭلىمىدۇق، بۇ پەقەت ئويدۇرمىدۇر.');
INSERT INTO "saleh" VALUES(38,8,'38:8',' قۇرئان بىزگە نازىل بولماي ئۇنىڭغا نازىل بولدىمۇ؟» بەلكى ئۇلار مېنىڭ زىكرىم (يەنى قۇرئان) دىن شەكتىدۇر، بەلكى ئۇلار تېخى مېنىڭ ئازابىمنى تېتىغىنى يوق.');
INSERT INTO "saleh" VALUES(38,9,'38:9',' ياكى ئۇلاردا غالىب، ناھايىتى كەرەملىك پەرۋەردىگارىڭنىڭ رەھمىتىنىڭ خەزىنىلىرى بارمۇ؟.');
INSERT INTO "saleh" VALUES(38,10,'38:10',' ياكى ئۇلار ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقىغا ئىگىمۇ؟ (ئەگەر ئۇلار ئىگە بولسا، ئۇلار ئۆزلىرىنى ئاسمانغا ئېلىپ چىقىدىغان) شوتىلارغا چىقسۇن.');
INSERT INTO "saleh" VALUES(38,11,'38:11',' ئۇلار (رەسۇلۇللاھقا قارشى) جامائەلەردىن ئۇيۇشقان، مەغلۇب قىلىنىدىغان قوشۇندۇر.');
INSERT INTO "saleh" VALUES(38,12,'38:12',' ئۇلاردىن (يەنى قۇرەيش مۇشرىكلىرىدىن) ئىلگىرى نۇھ قەۋمى، ئاد (قەۋمى) ۋە قوزۇقلار ئىگىسى پىرئەۋن (قەۋمى) (پەيغەمبەرلىرىنى) ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(38,13,'38:13','سەمۇد قەۋمى، لۇت قەۋمى ۋە ئەيكىلىكلەر (يەنى شۇئەيب ئەلەيھىسسالام قەۋمى) مۇ (پەيغەمبەرلىرىنى ئىنكار قىلدى)، ئەنە شۇلار (پەيغەمبەرلەرگە قارشى ئۇيۇشقان) جامائەلەر ئىدى.');
INSERT INTO "saleh" VALUES(38,14,'38:14',' ئۇلار (يەنى جامائەلەر) دىن پەيغەمبەرلەرنى ئىنكار قىلمىغان بىرىمۇ يوق، شۇنىڭ ئۈچۈن ئۇلارغا مېنىڭ ئازابىم تېگىشلىك بولدى.');
INSERT INTO "saleh" VALUES(38,15,'38:15',' بۇلار پەقەت بىر ئاۋاز (يەنى بىرىنچى قېتىملىق سۇر) نىلا كۈتىدۇ، ئۇ قايتىلانمايدۇ.');
INSERT INTO "saleh" VALUES(38,16,'38:16',' ئۇلار (مەسخىرە قىلىش يولى بىلەن): «پەرۋەردىگارىمىز! (سەن بىزگە ئازابتىن ۋەدە قىلغان) نېسىۋىمىزنى بىزگە بالدۇرراق- قىيامەت كۈنىدىن ئىلگىرى بەرگىن» دېيىشتى.');
INSERT INTO "saleh" VALUES(38,17,'38:17','ئۇلارنىڭ سۆزلىرىگە سەۋر قىلغىن، (دىندا) كۈچلۈك بەندىمىز داۋۇدنى ئەسلىگىن، ئۇ ھەقىقەتەن ئاللاھقا ئىتائەت قىلغۇچى ئىدى.');
INSERT INTO "saleh" VALUES(38,18,'38:18',' بىز ھەقىقەتەن تاغلارنى (داۋۇدقا) بويسۇندۇرۇپ بەردۇق، ئۇلار داۋۇد بىلەن ئەتىگىنى ـ ئاخشىمى تەسبىھ ئېيتاتتى.');
INSERT INTO "saleh" VALUES(38,19,'38:19',' قۇشلارنىمۇ ئۇنىڭغا بويسۇندۇرۇپ بەردۇق، ئۇلارمۇ (تەرەپ ـ تەرەپتىن) توپلىنىپ، داۋۇد بىلەن بىللە تەسبىھ ئېيتاتتى، (تاغلارنىڭ، قۇشلارنىڭ) ھەممىسى ئاللاھقا ئىتائەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(38,20,'38:20',' داۋۇدنىڭ سەلتەنىتىنى كۈچەيتتۇق، ئۇنىڭغا ھېكمەت ۋە (ھەممە ئادەم چۈشىنىدىغان) روشەن سۆز ـ ئىبارىلەرنى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(38,21,'38:21',' (ئى مۇھەممەد!) دەۋا قىلغۇچىلارنىڭ قىسسەسى ساڭا يەتتىمۇ؟ ئۆز ۋاقتىدا ئۇلار (داۋۇد ئىبادەت قىلىۋاتقان) مەسجىدنىڭ تېمىدىن ئارتىلىپ چۈشۈشتى.');
INSERT INTO "saleh" VALUES(38,22,'38:22',' ئەينى ۋاقىتتا ئۇلار داۋۇدنىڭ يېنىغا كىردى. داۋۇد ئۇلاردىن قورقتى، ئۇلار (داۋۇدقا): «قورقمىغىن، بىز بىرىمىز ـ بىرىمىزگە چېقىلغان ئىككى دەۋاگەرمىز، بىزنىڭ ئارىمىزدا ئادىللىق بىلەن ھۆكۈم قىلغىن، ناھەق ھۆكۈم چىقارمىغىن، بىزنى توغرا يولغا باشلىغىن» دېدى. ');
INSERT INTO "saleh" VALUES(38,23,'38:23',' (ئۇلارنىڭ بىرى) «شۈبھىسىزكى، مېنىڭ بۇ قېرىندىشىمنىڭ 99 ساغلىقى بار، مېنىڭ بىر ساغلىقىم بار، قېرىندىشىم ئۇ بىر ساغلىقنىمۇ ماڭا بەرگىن دەيدۇ، (ئارىمىزدا بۇ توغرۇلۇق مۇنازىرە بولۇپ) ئۇ مېنى سۆزدە يېڭىپ قويدى» دېدى. ');
INSERT INTO "saleh" VALUES(38,24,'38:24','داۋۇد ئېيتتى: «ئۇ سېنىڭ ساغلىقىڭنى ئۆزىنىڭ ساغلىقلىرىغا قوشۇۋېلىشنى تەلەپ قىلىپ راستلا سېنى بوزەك قىلىپتۇ، نۇرغۇن شېرىكلەر (يەنى دوستلار)، شۈبھىسىزكى، بىر ـ بىرىنىڭ (ھەققىگە) چېقىلىدۇ، پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەل قىلغانلارلا (بىر ـ بىرىنىڭ ھەققىگە چېقىلمايدۇ) بۇلار ئازدۇر». داۋۇد بىزنىڭ ئۇنى سىنىغانلىقىمىزنى بىلدى. پەرۋەردىگارىدىن مەغپىرەت تەلەپ قىلدى، سەجدىگە باردى. (ئاللاھقا) تەۋبە قىلدى.');
INSERT INTO "saleh" VALUES(38,25,'38:25',' بىز ئۇنىڭ خاتالىقىنى ئەپۇ قىلدۇق، ئۇ بىزنىڭ دەرگاھىمىزدا ئەلۋەتتە يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ.');
INSERT INTO "saleh" VALUES(38,26,'38:26',' ئى داۋۇد! سېنى بىز ھەقىقەتەن يەر يۈزىدە خەلىپە قىلدۇق، كىشىلەرنىڭ ئارىسىدا ئادىللىق بىلەن ھۆكۈم چىقارغىن، نەپسى خاھىشقا ئەگەشمىگىنكى، ئۇ سېنى ئاللاھنىڭ يولىدىن ئازدۇرىدۇ، ئاللاھنىڭ يولىدىن ئازغانلار ھېساب كۈنى (يەنى قىيامەت كۈنى) نى ئۇنتۇغانلىقلىرى ئۈچۈن ھەقىقەتەن قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(38,27,'38:27',' ئاسماننى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنى بىكار ياراتمىدۇق، ئۇ (يەنى ئۇلارنى بىكار يارىتىلغان دەپ قاراش) كاپىرلارنىڭ گۇمانىدۇر، كاپىرلارغا دوزاختىن ۋاي!.');
INSERT INTO "saleh" VALUES(38,28,'38:28','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى يەر يۈزىدە بۇزغۇنچىلىق قىلغانلار بىلەن ئوخشاش قىلامدۇق ياكى تەقۋادارلارنى فاجىرلارغا ئوخشاش قىلامدۇق؟.');
INSERT INTO "saleh" VALUES(38,29,'38:29',' (بۇ) ئۇلارنىڭ ئايەتلەرنى تەپەككۈر قىلىشلىرى ئۈچۈن، ئەقىل ئىگىلىرىنىڭ ۋەز ـ نەسىھەت ئېلىشلىرى ئۈچۈن بىز ساڭا نازىل قىلغان مۇبارەك كىتابتۇر.');
INSERT INTO "saleh" VALUES(38,30,'38:30','بىز داۋۇدقا سۇلايماننى ئاتا قىلدۇق، سۇلايمان نېمىدېگەن ياخشى بەندە! ئۇ (ئاللاھقا) ھەقىقەتەن ئىتائەت قىلغۇچى ئىدى.');
INSERT INTO "saleh" VALUES(38,31,'38:31',' ئۆز ۋاقتىدا كەچقۇرۇنلىقى ئۇنىڭغا ياخشى يۈگۈرۈك ئاتلار توغرىلاندى.');
INSERT INTO "saleh" VALUES(38,32,'38:32','سۇلايمان ئېيتتى: «مەن ھەقىقەتەن ئاتلارنى پەرۋەردىگارىمنى ئەسلەش ئۈچۈن ياخشى كۆردۈم، ئۇلار (كۆزۈمدىن) غايىب بولغىچە (ئۇلارنى كۆزدىن كۆچۈرۈش بىلەن بولدۇم)».');
INSERT INTO "saleh" VALUES(38,33,'38:33',' سۇلايمان ئېيتتى: «ئاتلارنى مېنىڭ ئالدىمغا ئېلىپ كېلىڭلار». (ئاتلار ئېلىپ كېلىنگەندىن كېيىن) سۇلايمان ئۇلارنىڭ پاچاقلىرىنى، بويۇنلىرىنى سىلاشقا باشلىدى.');
INSERT INTO "saleh" VALUES(38,34,'38:34',' بىز ھەقىقەتەن سۇلايماننى سىنىدۇق، ئۇنىڭ تەختى ئۈستىگە بىر جەسەتنى تاشلىدۇق، ئاندىن ئۇ (بۇنىڭ سىناق ئىكەنلىكىنى بىلىپ) تەۋبە قىلدى. ');
INSERT INTO "saleh" VALUES(38,35,'38:35',' سۇلايمان ئېيتتى: «پەرۋەردىگارىم! ماڭا مەغپىرەت قىلغىن، ماڭا مەندىن كېيىن (مەندىن باشقا) ھېچ ئادەمگە مۇيەسسەر بولمايدىغان پادىشاھلىقنى ئاتا قىلغىن، سەن ھەقىقەتەن كۆپ ئاتا قىلغۇچىسەن».');
INSERT INTO "saleh" VALUES(38,36,'38:36','بىز سۇلايمانغا شامالنى بويسۇندۇرۇپ بەردۇق. شامال سۇلايماننىڭ بۇيرۇقى بويىچە ئۇ خالىغان تەرەپكە لەرزان ماڭاتتى.');
INSERT INTO "saleh" VALUES(38,37,'38:37',' ھەمدە ئۇنىڭغا (كاتتا بىنالارنى سالالايدىغان) قۇرغۇچى ۋە (دېڭىزلارغا چۆكۈپ ئۈنچە ـ مارجانلارنى ئېلىپ چىقالايدىغان) غەۋۋاس شەيتانلارنى بويسۇندۇرۇپ بەردۇق.');
INSERT INTO "saleh" VALUES(38,38,'38:38','يەنە نۇرغۇن شەيتانلارغا ئىشكەل سېلىنغاندۇر.');
INSERT INTO "saleh" VALUES(38,39,'38:39',' (بىز سۇلايمانغا ئېيتتۇقكى) «بۇ بىزنىڭ (ساڭا بەرگەن كەڭ) ئاتايىمىز، (ئۇنىڭدىن سەن خالىغان كىشىگە) بەرگىن ياكى بەرمىگىن، (بۇ ھەقتە سەندىن) ھېساب ئېلىنمايدۇ».');
INSERT INTO "saleh" VALUES(38,40,'38:40',' ئۇ بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن يېقىنلىققا ۋە ياخشى ئاقىۋەتكە ئىگە بولىدۇ.');
INSERT INTO "saleh" VALUES(38,41,'38:41',' بەندىمىز ئەييۇبنى ئەسلىگىن، ئۇ ئۆز ۋاقتىدا پەرۋەردىگارىغا (ئىلتىجا قىلىپ): «شەيتان ماڭا ھەقىقەتەن جاپا ـ مۇشەققەت ۋە ئازاب يەتكۈزدى» دەپ نىدا قىلدى.');
INSERT INTO "saleh" VALUES(38,42,'38:42',' ئۇنىڭغا: «پۇتۇڭ بىلەن يەرنى تەپكىن» دېدۇق، ئۇ پۇتى بىلەن يەرنى تېپىۋىدى، سۈزۈك بىر بۇلاق ئېتىلىپ چىقتى. (بىز ئۇنىڭغا) «بۇ يۇيۇنسىمۇ بولىدىغان، ئىچسىمۇ بولىدىغان سوغۇق سۇدۇر» (دېدۇق).');
INSERT INTO "saleh" VALUES(38,43,'38:43','بىز ئۇنىڭ (كېسەل ۋاقتىدا ئاپەتكە ئۇچراپ يوق بولغان) ئەھلى ـ ئەۋلادىنى (يېڭىدىن) بەردۇق، ئۇلار بىلەن بىللە بىر باراۋەر ئارتۇق بەردۇق، (بۇ) بىزنىڭ نازىل قىلغان رەھمىتىمىزدۇر، ئەقىل ئىگىلىرى ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(38,44,'38:44',' (ئۇنىڭغا) «قولۇڭ بىلەن بىر باغلام چۆپنى ئېلىپ، ئۇنىڭ بىلەن (ئايالىڭنى) ئۇرغىن. قەسىمىڭنى بۇزمىغىن» (دېدۇق). بىز ئەييۇبنى ھەقىقەتەن سەۋرچان بايقىدۇق، ئۇ نېمىدېگەن ئوبدان بەندە! ھەقىقەتەن ئۇ (ئاللاھقا) تەۋبە بىلەن يۈزلەنگۈچىدۇر.');
INSERT INTO "saleh" VALUES(38,45,'38:45',' ئىبادەتتە كۈچلۈك، (دىندا) بەسىرەتلىك بەندىلىرىمىز ئىبراھىم، ئىسھاق ۋە يەئقۇبنى ئەسلىگىن.');
INSERT INTO "saleh" VALUES(38,46,'38:46',' ئۇلارنى بىز پاك ـ خىسلەتلىك، ئاخىرەتنى ئەسلەپ تۇرىدىغان سەمىمىي كىشىلەر قىلدۇق.');
INSERT INTO "saleh" VALUES(38,47,'38:47',' ئۇلار بىزنىڭ دەرگاھىمىزدا ھەقىقەتەن ئالاھىدە تاللانغان كىشىلەردۇر، ھەقىقەتەن ياخشى كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(38,48,'38:48',' ئىسمائىلنى، ئىليەسەنى ۋە زۇلكىفلنى ئەسلىگىن، (ئۇلارنىڭ) ھەممىسى ياخشىلاردىندۇر.');
INSERT INTO "saleh" VALUES(38,49,'38:49',' بۇ ۋەز ـ نەسىھەتتۇر، تەقۋادارلارغا ئەلۋەتتە ياخشى ئاقىۋەت بار.');
INSERT INTO "saleh" VALUES(38,50,'38:50',' (ئۇ) تۇرالغۇ بولغان جەننەتلەردىن ئىبارەت بولۇپ، ئۇلارغا ئۇنىڭ دەرۋازىلىرى ئوچۇقتۇر.');
INSERT INTO "saleh" VALUES(38,51,'38:51',' ئۇلار جەننەتلەردە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتلەردە نۇرغۇن مېۋىلەرنى، نۇرغۇن شارابلارنى تەلەپ قىلىپ (ئەكەلدۈرۈپ) تۇرىدۇ.');
INSERT INTO "saleh" VALUES(38,52,'38:52',' ئۇلارنىڭ يانلىرىدا ئەرلىرىدىن غەيرىيگە كۆز سالمايدىغان، ياش قۇرامى ئوخشاش جۈپتىلىرى بولىدۇ.');
INSERT INTO "saleh" VALUES(38,53,'38:53',' بۇ سىلەرگە قىيامەت كۈندە ۋەدە قىلىنغان نېمەتتۇر.');
INSERT INTO "saleh" VALUES(38,54,'38:54',' بۇ ھەقىقەتەن بىزنىڭ ئاتايىمىزدۇركى، ئۇ ھەرگىز تۈگىمەيدۇ.');
INSERT INTO "saleh" VALUES(38,55,'38:55',' بۇ (ھەقىقەتتۇر)، كاپىرلارنىڭ (ئاخىرەتتە بولىدىغان جايى) ھەقىقەتەن ئەڭ يامان جايدۇر.');
INSERT INTO "saleh" VALUES(38,56,'38:56','ئۇ جەھەننەمدۇر، ئۇلار جەھەننەمگە كىرىدۇ، ئۇ نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(38,57,'38:57',' بۇ (كۆيدۈرگۈچى) قىزىق سۇدۇر، قان ـ يىرىڭدۇر، ئۇلار بۇنى تېتىسۇن.');
INSERT INTO "saleh" VALUES(38,58,'38:58',' يەنە (ئۇلارغا) مۇشۇ خىلدىكى باشقا تۈرلۈك ئازابلار بار.');
INSERT INTO "saleh" VALUES(38,59,'38:59','(ئەگەشكۈچىلىرى بىلەن دوزاخقا كىرگەن چاغدا، ئەگەشتۈرگۈچىلەرگە ئېيتىلىدۇ) «بۇ جامائە سىلەر بىلەن بىرگە كىرگۈچىدۇر». (ئەگەشتۈرگۈچىلەر ئېيتىدۇ) «ئۇلار قارشى ئېلىنمايدۇ، ئۇلار چوقۇم (بىزگە ئوخشاش) ئازابنى تېتىغۇچىدۇر».');
INSERT INTO "saleh" VALUES(38,60,'38:60',' ئەگەشكۈچىلەر ئېيتىدۇ: «بەلكى سىلەر قارشى ئېلىنمايسىلەر، سىلەر ئازابنى بىزگە ئېلىپ كەلدىڭلار (يەنى بىزنى ئازدۇرۇپ دوزاخقا كىرىشىمىزگە سەۋەبچى بولدۇڭلار)، بۇ نېمىدېگەن يامان جاي!»');
INSERT INTO "saleh" VALUES(38,61,'38:61','ئۇلار (يەنى ئەگەشكۈچىلەر): «پەرۋەردىگارىمىز! بىزگە بۇ ئازابنى ئېلىپ كەلگەن ئادەمگە دوزاخ ئازابىنى ئىككى ھەسسە زىيادە قىلغىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(38,62,'38:62',' ئۇلار (يەنى كاپىرلارنىڭ كاتتىباشلىرى) ئېيتىدۇ: «بىز (دۇنيادىكى چاغدا) يامانلاردىن ھېسابلايدىغان ئادەملەرنى (يەنى مۆمىنلەرنى) (دوزاختا) كۆرمەيمىزغۇ؟');
INSERT INTO "saleh" VALUES(38,63,'38:63',' ئۇلارنى بىز (دۇنيادىكى چاغدا) مەسخىرە قىلاتتۇقمۇ؟ ياكى ئۇلاردىن كۆزلەر ئېغىپ كەتتىمۇ؟ (يەنى ياكى ئۇلار دوزاختا بىز بىلەن بىللە تۇرسىمۇ كۆرمەيۋاتامدۇق؟)».');
INSERT INTO "saleh" VALUES(38,64,'38:64',' ئەھلى دوزاخنىڭ بۇ رەۋىشتە ئۆزئارا جاڭجاللىشىشى چوقۇمدۇر.');
INSERT INTO "saleh" VALUES(38,65,'38:65',' (ئى مۇھەممەد! مەككە كاپىرلىرىغا) ئېيتقىنكى، «مەن پەقەت بىر ئاگاھلاندۇرغۇچىمەن، غالىب بىر ئاللاھتىن باشقا ھېچ ئىلاھ يوقتۇر.');
INSERT INTO "saleh" VALUES(38,66,'38:66',' (ئۇ ئاللاھ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر، غالىبتۇر. (ئۆزى خالىغان بەندىلەرنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر».');
INSERT INTO "saleh" VALUES(38,67,'38:67','(ئۇلارغا) ئېيتقىنكى، «ئۇ (يەنى ئاللاھنىڭ بىرلىكىگە دەۋەت قىلغۇچى پەيغەمبەر ئىكەنلىكىم) چوڭ خەۋەردۇر.');
INSERT INTO "saleh" VALUES(38,68,'38:68',' سىلەر ئۇنىڭدىن يۈز ئۆرۈيسىلەر.');
INSERT INTO "saleh" VALUES(38,69,'38:69',' پەرىشتىلەر (ئادەم ئەلەيھىسسالام توغرىسىدا) مۇنازىرىلىشىۋاتقان چاغدا، مەن ئۇلارنىڭ ئەھۋالىنى بىلمەيتتىم.');
INSERT INTO "saleh" VALUES(38,70,'38:70','ماڭا پەقەت ۋەھيى قىلىنىدۇكى، مەن روشەن بىر ئاگاھلاندۇرغۇچىمەن»');
INSERT INTO "saleh" VALUES(38,71,'38:71',' ئۆز ۋاقتىدا پەرۋەردىگارىڭ پەرىشتىلەرگە ئېيتتى: «مەن ھەقىقەتەن لايدىن بىر ئادەم يارىتىمەن.');
INSERT INTO "saleh" VALUES(38,72,'38:72','ئۇنى مەن راۋۇرۇس ياراتقان (يەنى ئۇنى يارىتىپ سۈرەتكە كىرگۈزۈپ، ئەزالىرىنى تولۇق، مۇكەممەل ئىنسان ھالىتىگە كەلتۈرگەن) ۋە ئۇنىڭغا جان كىرگۈزگەن ۋاقتىمدا ئۇنىڭغا سەجدە قىلىڭلار».');
INSERT INTO "saleh" VALUES(38,73,'38:73',' پەرىشتىلەرنىڭ بىرىمۇ قالماي، ھەممىسى (ئادەم ئەلەيھىسسالامغا) سەجدە قىلدى.');
INSERT INTO "saleh" VALUES(38,74,'38:74',' پەقەت ئىبلىسلا بويۇنتاۋلىق قىلدى، ئۇ كاپىرلاردىن بولۇپ كەتتى.');
INSERT INTO "saleh" VALUES(38,75,'38:75',' ئاللاھ ئېيتتى: «ئى ئىبلىس! مەن ئۆز قولۇم بىلەن ياراتقان ئىنسانغا سەجدە قىلىشتىن ساڭا نېمە توسقۇنلۇق قىلدى (ئۇنىڭغا سەجدە قىلماسلىقىڭ تەكەببۇرلۇق قىلغانلىقىڭدىنمۇ ياكى يۇقىرى مەرتىۋىلىكلەردىن بولغانلىقىڭدىنمۇ؟».');
INSERT INTO "saleh" VALUES(38,76,'38:76','ئىبلىس: «مەن ئۇنىڭدىن ئارتۇق، مېنى ئوتتىن ياراتتىڭ، ئۇنى لايدىن ياراتتىڭ» دېدى.');
INSERT INTO "saleh" VALUES(38,77,'38:77',' ئاللاھ: «سەن جەننەتتىن يوقال! سەن ھەقىقەتەن قوغلاندى بولدۇڭ، ساڭا ھەقىقەتەن قىيامەتكىچە مېنىڭ لەنىتىم بولسۇن» دېدى[77ـ78].');
INSERT INTO "saleh" VALUES(38,78,'38:78',' ئاللاھ: «سەن جەننەتتىن يوقال! سەن ھەقىقەتەن قوغلاندى بولدۇڭ، ساڭا ھەقىقەتەن قىيامەتكىچە مېنىڭ لەنىتىم بولسۇن» دېدى[77ـ78].');
INSERT INTO "saleh" VALUES(38,79,'38:79','ئىبلىس: «پەرۋەردىگارىم! ماڭا (خالايىق) تىرىلدۈرۈلىدىغان كۈن (يەنى قىيامەت) گۈچە مۆھلەت بەرسەڭ (يەنى مېنى ھايات قالدۇرساڭ)» دېدى.');
INSERT INTO "saleh" VALUES(38,80,'38:80',' ئاللاھ: «جەزمەن ساڭا مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى[80ـ81].');
INSERT INTO "saleh" VALUES(38,81,'38:81',' ئاللاھ: «جەزمەن ساڭا مەلۇم ۋاقىتقىچە مۆھلەت بېرىلىدۇ» دېدى[80ـ81].');
INSERT INTO "saleh" VALUES(38,82,'38:82',' ئىبلىس ئېيتتى: «ئىززىتىڭ بىلەن قەسەمكى، ئۇلارنىڭ ھەممىسىنى چوقۇم ئازدۇرىمەن.');
INSERT INTO "saleh" VALUES(38,83,'38:83','ئۇلاردىن پەقەت تاللانغان بەندىلىرىڭلا بۇنىڭدىن مۇستەسنا (يەنى ئۇلارنى ئازدۇرالمايمەن)».');
INSERT INTO "saleh" VALUES(38,84,'38:84',' ئاللاھ ئېيتتى: «مېنىڭ سۆزۈم ھەقتۇر، مەن ھەقنى سۆزلەيمەن.');
INSERT INTO "saleh" VALUES(38,85,'38:85',' سەن بىلەن ۋە ئۇلاردىن ساڭا ئەگەشكەنلەرنىڭ ھەممىسى بىلەن چوقۇم جەھەننەمنى توشقۇزىمەن».');
INSERT INTO "saleh" VALUES(38,86,'38:86',' ئېيتقىنكى، «(ئاللاھ تەرىپىدىن كەلگەن ۋەھىينى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھېچ ھەق تەلەپ قىلمايمەن، مەن (قۇرئاننى) ئىپتىرا قىلغۇچىلاردىن (يەنى يالغاندىن توقۇغۇچىلاردىن) ئەمەسمەن.');
INSERT INTO "saleh" VALUES(38,87,'38:87',' قۇرئان پەقەت جاھان ئەھلى ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(38,88,'38:88',' بىر مۇددەتتىن كېيىن ئۇنىڭ راستلىقىنى چوقۇم بىلىسىلەر»[‍88].');
INSERT INTO "saleh" VALUES(39,1,'39:1','بۇ كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر. ');
INSERT INTO "saleh" VALUES(39,2,'39:2',' ساڭا بىز (بۇ) كىتابنى ھەقىقەتەن ھەق بىلەن (يەنى ھەقىقەتنى ئۆز ئىچىگە ئالغان ھالدا) نازىل قىلدۇق، دىنىڭنى شېرىكتىن (ۋە رىيادىن) ساپ قىلغىنىڭ ھالدا ئاللاھقا ئىبادەت قىلغىن.');
INSERT INTO "saleh" VALUES(39,3,'39:3',' بىلىڭلاركى، (شېرىكتىن ۋە رىيادىن) ساپ دىن ئاللاھقا خاستۇر، ئاللاھنى قويۇپ مەبۇدلىرىنى ھىمايىچى قىلىۋالغانلار: «بىزنىڭ ئۇلارغا چوقۇنۇشىمىز پەقەت ئۇلارنىڭ بىزنى ئاللاھقا يېقىنلاشتۇرۇشى ئۈچۈندۇر» (دەيدۇ). ئاللاھ ھەقىقەتەن (قىيامەت كۈنى) ئۇلارنىڭ ئىختىلاپ قىلىشقان نەرسىلىرى ئۈستىدە ھۆكۈم چىقىرىدۇ، ئاللاھ ھەقىقەتەن يالغانچىنى، (ئاللاھنىڭ نېمەتلىرىگە) كۇفرىلىق قىلغۇچىنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(39,4,'39:4',' ئەگەر ئاللاھ بالا تۇتۇشنى خالىسا ئىدى، ئەلۋەتتە، مەخلۇقات ئىچىدىن خالىغاننى ئىختىيار قىلاتتى، ئاللاھ (بالا تۇتۇشتىن) پاكتۇر، ئۇ غالىب بىر ئاللاھتۇر.');
INSERT INTO "saleh" VALUES(39,5,'39:5',' ئاللاھ ئاسمانلارنى ۋە زېمىننى ھەق ئاساسىدا ياراتتى. ئاللاھ كېچىنى كۈندۈزگە كىرگۈزىدۇ ۋە كۈندۈزنى كېچىگە كىرگۈزىدۇ، ئاللاھ كۈن بىلەن ئاينى (بەندىلەرنىڭ مەنپەئەتىگە) بويسۇندۇرۇپ بەردى، ئۇلارنىڭ ھەر بىرى مۇئەييەن ۋاقىتقىچە (يەنى قىيامەتكىچە ئۆز ئوقىدا) سەير قىلىدۇ، بىلىڭلاركى، ئۇ (يەنى ئاللاھ ئۆز ئىشىدا) غالىبتۇر. (بەندىلىرىنىڭ گۇناھلىرىنى) تولىمۇ مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(39,6,'39:6',' ئاللاھ سىلەرنى بىر ئادەمدىن ياراتتى، ئاندىن ئۇنىڭدىن ئۇنىڭ جۈپتىنى (يەنى ھەۋۋانى) ياراتتى ۋە ھايۋانلاردىن سەككىزنى سىلەر ئۈچۈن ياراتتى، ئاللاھ سىلەرنى ئاناڭلارنىڭ قارنىدا بىر ھالەتتىن يەنە بىر ھالەتكە تەرەققىي قىلدۇرۇپ (يەنى ئابىمەنىي، ئاندىن لەختە قان، ئاندىن پارچە گۆش باسقۇچلىرىغا بۆلۈپ)، ئۈچ قاراڭغۇلۇق (يەنى بالاھەمراھىنىڭ پەردىسى، بەچچىدان ۋە ئانىنىڭ قورسىقى) ئىچىدە يارىتىدۇ، ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، پادىشاھلىق ئاللاھقا خاستۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (ئۇنىڭغا ئىبادەت قىلىشتىن ئۇنىڭدىن غەيرىيگە چوقۇنۇشقا) بۇرۇلۇپ كېتىسىلەر.');
INSERT INTO "saleh" VALUES(39,7,'39:7','ئەگەر كاپىر بولساڭلار ئاللاھ سىلەردىن بىھاجەتتۇر، ئاللاھ بەندىلىرىنىڭ كاپىر بولۇشىغا رازى بولمايدۇ. ئەگەر سىلەر شۈكۈر قىلساڭلار، ئاللاھ سىلەرنىڭ قىلغان شۈكۈرۈڭلاردىن رازى بولىدۇ، بىر گۇناھكار ئادەم باشقا بىراۋنىڭ گۇناھىنى ئۈستىگە ئالمايدۇ (يەنى ھەر ئادەم قىلغان گۇناھىغا ئۆزى جاۋابكار بولىدۇ)، ئاندىن سىلەر پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتىسىلەر، ئاللاھ قىلمىشىڭلارنى سىلەرگە ئېيتىپ بېرىدۇ، ئاللاھ ھەقىقەتەن دىللاردىكى سىرلارنى تولۇق بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(39,8,'39:8',' ئىنسانغا بىرەر زىيان ـ زەخمەت يەتسە، پەرۋەردىگارىغا يۈزلەنگەن ھالدا ئۇنىڭغا دۇئا قىلىدۇ، ئاندىن ئاللاھ ئۆز تەرىپىدىن ئۇنىڭغا نېمەت ئاتا قىلغان (ئۇنىڭ بېشىغا كەلگەن كۈلپەتنى كۆتۈرۈۋەتكەن) چاغدا، ئاللاھقا زىيان ـ زەخمەتنى كۆتۈرۈۋېتىش توغرۇلۇق ئىلگىرى قىلغان دۇئاسىنى ئۇنتۇپ كېتىدۇ، (باشقىلارنى) ئاللاھنىڭ يولىدىن ئازدۇرۇش ئۈچۈن ئاللاھقا شېرىكلەرنى كەلتۈرىدۇ، ئېيتقىنكى، «سەن كۇفرىڭ بىلەن تۇرۇپ، (بۇ پانىي دۇنيادىن) ئازغىنا (ۋاقىت) بەھرىمەن بولغىن، سەن چوقۇم دوزىخىلاردىندۇرسەن.');
INSERT INTO "saleh" VALUES(39,9,'39:9',' (ئاللاھقا شېرىك كەلتۈرگەن ئادەم ياخشىمۇ؟) ياكى ئاخىرەت (ئازابىدىن) قورقۇپ، پەرۋەردىگارىنىڭ رەھمىتىنى ئۈمىد قىلىپ، كېچىنىڭ سائەتلىرىنى سەجدە قىلغان ۋە قىيامدا تۇرغان ھالدا ئىبادەت قىلىپ ئۆتكۈزگەن ئادەم (ياخشىمۇ؟)». ئېيتقىنكى، «بىلىدىغانلار بىلەن بىلمەيدىغانلار باراۋەر بولامدۇ؟ پەقەت (ساغلام) ئەقىل ئىگىلىرى ئىبرەت ئالىدۇ».');
INSERT INTO "saleh" VALUES(39,10,'39:10','ئېيتقىنكى، «ئى (ئاللاھنىڭ) مۆمىن بەندىلىرى! پەرۋەردىگارىڭلارغا تەقۋادارلىق قىلىڭلار، بۇ دۇنيادا ياخشى ئىش قىلغانلار (ئاخىرەتتە) ياخشىلىققا (يەنى جەننەتكە) ئېرىشىدۇ. ئاللاھنىڭ يېرى كەڭدۇر (كۇففارلار ئارىسىدىن شۇ يەرگە ھىجرەت قىلىڭلار)، پەقەت سەۋر قىلغۇچىلارغا ئۇلارنىڭ ئەجرى ھېسابسىز بېرىلىدۇ».');
INSERT INTO "saleh" VALUES(39,11,'39:11','ئېيتقىنكى، «مەن دىننى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئاللاھقا ئىبادەت قىلىشقا بۇيرۇلدۇم.');
INSERT INTO "saleh" VALUES(39,12,'39:12',' ھەمدە (بۇ ئۈممەتنىڭ ئىچىدە) مۇسۇلمانلارنىڭ ئەۋۋىلى بولۇشقا بۇيرۇلدۇم».');
INSERT INTO "saleh" VALUES(39,13,'39:13',' ئېيتقىنكى، «ئەگەر مەن پەرۋەردىگارىمغا ئاسىيلىق قىلسام، شۈبھىسىزكى، بۈيۈك كۈننىڭ ئازابىدىن قورقىمەن (يەنى ئاللاھنىڭ ئەمرىگە ئاسىيلىق قىلسام، قىيامەت كۈنى ئۇنىڭ مېنى جەھەننەم ئوتى بىلەن ئازابلىشىدىن قورقىمەن)».');
INSERT INTO "saleh" VALUES(39,14,'39:14',' ئېيتقىنكى، «دىنىمنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئاللاھقا ئىبادەت قىلىمەن.');
INSERT INTO "saleh" VALUES(39,15,'39:15',' سىلەر ئاللاھتىن باشقا خالىغىنىڭلارغا ئىبادەت قىلىڭلار!» ئېيتقىنكى، «قىيامەت كۈنى ئۆزلىرىنى ۋە ئائىلىسىدىكىلەرنى زىيان تارتقۇزغۇچىلار قاتتىق زىيان تارتقۇچىلاردۇر، بىلىڭلاركى، ئەنە شۇ روشەن زىياندۇر.');
INSERT INTO "saleh" VALUES(39,16,'39:16',' ئۇلارنى ئۈستىدىنمۇ قاتمۇقات ئوت، ئاستىدىنمۇ قاتمۇقات ئوت ئورىۋالىدۇ، شۇ (قاتتىق ئازاب) بىلەن ئاللاھ بەندىلىرىنى قورقىتىدۇ». ئى بەندىلىرىم! (ئازابىمدىن) قورقۇڭلار.');
INSERT INTO "saleh" VALUES(39,17,'39:17',' تاغۇتقا چوقۇنۇشتىن يىراق بولغانلارغا، ئاللاھقا (ۋە ئۇنىڭ ئىبادىتىگە) قايتقانلارغا (جەننەت بىلەن) خۇشخەۋەر بېرىلىدۇ، ');
INSERT INTO "saleh" VALUES(39,18,'39:18','سۆزگە قۇلاق سېلىپ ئۇنىڭ ئەڭ ياخشىسىغا ئەگىشىدىغانلارغا (يەنى تەقۋادار بەندىلىرىمگە) خۇشخەۋەر بەرگىن، ئەنە شۇلار ئاللاھ ھىدايەت قىلغان كىشىلەردۇر، ئەنە شۇلار (ساغلام) ئەقىل ئىگىلىرىدۇر.');
INSERT INTO "saleh" VALUES(39,19,'39:19',' كىمگە ئازاب ۋەدىسى تېگىشلىك بولسا (ئۇ چوقۇم دوزاخقا كىرىدۇ)، سەن دوزاختىكى ئادەمنى قۇتقۇزالامسەن؟');
INSERT INTO "saleh" VALUES(39,20,'39:20',' لېكىن (دۇنيادا) پەرۋەردىگارىدىن قورققانلار (جەننەتتە) قەۋەت ـ قەۋەت ئالىي ئىمارەتلەردىن بەھرىمەن بولىدۇ، ئۇلارنىڭ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدۇ، (بۇ) ئاللاھنىڭ ۋەدىسىدۇر، ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ.');
INSERT INTO "saleh" VALUES(39,21,'39:21',' سەن بىلمەمسەنكى، ئاللاھ بۇلۇتتىن يامغۇر ياغدۇرىدۇ. ئاندىن ئۇنى يەرگە سىڭدۈرۈپ بۇلاقنى پەيدا قىلىدۇ. ئاندىن ئاللاھ ئۇنىڭ بىلەن رەڭگى خىلمۇخىل زىرائەتلەرنى ئۈندۈرىدۇ. ئاندىن ئۇ قۇرۇيدۇ، ئۇنىڭ سارغىيىپ كەتكەنلىكىنى كۆرىسەن. ئاندىن ئاللاھ ئۇنى شاخ ـ شۇمبىغا ئايلاندۇرىدۇ، ئۇنىڭدا ھەقىقەتەن ئەقىل ئىگىلىرى ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) ئىبرەت بار.');
INSERT INTO "saleh" VALUES(39,22,'39:22',' ئاللاھ كۆكسىنى ئىسلام ئۈچۈن كەڭ قىلغان، پەرۋەردىگارىنىڭ نۇرى ئۈستىدە بولغان ئادەم (دىلى كور ئادەم بىلەن) ئوخشاشمۇ؟ ئاللاھنىڭ زىكرىنى تەرك ئەتكەنلىكتىن دىللىرى قېتىپ كەتكەنلەرگە ۋاي! ئەنە شۇلار روشەن گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(39,23,'39:23',' ئاللاھ سۆزلەرنىڭ ئەڭ چىرايلىقى بولغان قۇرئاننى نازىل قىلدى. (پاساھەتتە، بالاغەتتە) ئۇنىڭ بەزىسى بەزىسىگە ئوخشاپ كېتىدۇ، (ۋەز ـ نەسىھەتلەر، ئەھكاملار، قىسسەلەر ئۇنىڭدا) تەكرارلىنىدۇ، (قۇرئاندىكى ئازاب ئايەتلىرى تىلاۋەت قىلىنغان چاغدا) پەرۋەردىگارىدىن قورقىدىغان كىشىلەرنىڭ بەدەنلىرى تىترەيدۇ، ئاندىن ئاللاھنىڭ زىكرى ئۈچۈن (يەنى ئاللاھنىڭ رەھمىتى، مەغپىرىتىگە دائىر ئايەتلەر تىلاۋەت قىلىنغان چاغدا)، ئۇلارنىڭ بەدەنلىرى ۋە دىللىرى يۇمشاپ (ئارام تاپىدۇ)، ئەنە شۇ (يەنى قۇرئان) ئاللاھنىڭ ھىدايىتىدۇركى، ئۇنىڭ بىلەن (ئاللاھ) خالىغان ئادەمنى ھىدايەت قىلىدۇ، ئاللاھ ئازدۇرغان ئادەمگە ھېچ ھىدايەت قىلغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(39,24,'39:24',' قىيامەت كۈنى قاتتىق ئازابنى يۈزى بىلەن مۇداپىئە قىلىدىغان ئادەم (ئازابقا دۇچار بولمايدىغان ئادەم بىلەن ئوخشاشمۇ؟) زالىملارغا (يەنى كاپىرلارغا): «قىلغان گۇناھىڭلارنىڭ جازاسىنى تېتىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(39,25,'39:25',' ئۇلاردىن ئىلگىرى ئۆتكەن (ئۈممەت) لەر (پەيغەمبەرلىرىنى) ئىنكار قىلدى، ئويلىمىغان يەردىن ئۇلارغا ئازاب كەلدى.');
INSERT INTO "saleh" VALUES(39,26,'39:26',' ئاللاھ ئۇلارغا بۇ دۇنيادا خارلىقنىڭ (تەمىنى) تېتىتتى، ئاخىرەتنىڭ ئازابى تېخىمۇ بۈيۈكتۇر، ئۇلار (ئاخىرەتنىڭ ئازابىنى) بىلسە ئىدى (پەيغەمبەرلىرىنى ئىنكار قىلمايتتى).');
INSERT INTO "saleh" VALUES(39,27,'39:27',' بىز كىشىلەرنى ۋەز ـ نەسىھەت ئالسۇن دەپ، بۇ قۇرئاندا ئۇلارغا تۈرلۈك مىساللارنى بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(39,28,'39:28','قۇرئان ئەرەبچىدۇر، ئەگرىلىكتىن خالىدۇر، ئۈمىدكى، ئۇلار (قۇرئاننىڭ مەنىسىنى چۈشىنىپ كۇفرىدىن) ساقلانغاي.');
INSERT INTO "saleh" VALUES(39,29,'39:29','ئاللاھ (سىلەرگە مۇنداق) بىر مىسالنى كەلتۈرىدۇ: بىر قۇل بولۇپ، ئۇنىڭغا (ئىشقا سېلىشنى) تالىشىدىغان نۇرغۇن خوجايىنلار ئىگىدارچىلىق قىلىدۇ (بىرسى بۇ ئىشقا، بىرسى ئۇ ئىشقا بۇيرۇيدۇ، ئۇ كىمنى رازى قىلىشنى بىلەلمەي قالىدۇ)، يەنە بىر قۇل بولۇپ، ئۇ بىر ئادەمگىلا خاستۇر، بۇ ئىككى قۇل (يەنى ئۇنىڭ ئەھۋالى) باراۋەرمۇ؟ جىمى ھەمدۇسانا ئاللاھقا خاستۇر! بەلكى ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(39,30,'39:30','سەن ھەقىقەتەن ئۆلىسەن، ئۇلارمۇ ھەقىقەتەن ئۆلىدۇ.');
INSERT INTO "saleh" VALUES(39,31,'39:31',' ئاندىن كېيىن سىلەر قىيامەت كۈنى پەرۋەردىگارىڭلارنىڭ دەرگاھىدا بىر ـ بىرىڭلار بىلەن دەۋا قىلىشىسىلەر.');
INSERT INTO "saleh" VALUES(39,32,'39:32','ئاللاھقا يالغاننى چاپلىغان، نازىل بولغان راست سۆزنى (يەنى قۇرئاننى) ئىنكار قىلغان ئادەمدىنمۇ زالىم كىشى بارمۇ؟ جەھەننەمدە كاپىرلارغا ئورۇن يوقمۇ؟');
INSERT INTO "saleh" VALUES(39,33,'39:33','راست سۆز (يەنى قۇرئان) نى ئېلىپ كەلگەن كىشى ۋە ئۇنى ئېتىراپ قىلغان كىشىلەر ـ ئەنە شۇلار تەقۋادارلاردۇر.');
INSERT INTO "saleh" VALUES(39,34,'39:34',' ئۇلار پەرۋەردىگارىنىڭ دەرگاھىدا ئۆزلىرى خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، ئەنە شۇ ياخشى ئىش قىلغۇچىلارنىڭ مۇكاپاتىدۇر.');
INSERT INTO "saleh" VALUES(39,35,'39:35','ئاللاھ ئۇلارنىڭ ئەڭ يامان ئەمەللىرىنى يوققا چىقىرىدۇ، ئۇلارغا قىلغان ئەڭ ياخشى ئەمەللىرى بويىچە ساۋاب بېرىدۇ.');
INSERT INTO "saleh" VALUES(39,36,'39:36',' ئاللاھ بەندىسىگە (يەنى مۇھەممەد ئەلەيھىسسالامنى قوغداشقا ۋە ئۇنىڭغا ياردەم بېرىشكە) يېتەرلىك ئەمەسمۇ؟ ئۇلار سېنى ئاللاھتىن باشقا مەبۇدلار بىلەن قورقىتىدۇ، ئاللاھ گۇمراھ قىلغان ئادەمنى ھېچ ھىدايەت قىلغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(39,37,'39:37','ئاللاھ ھىدايەت قىلغان ئادەمنى ھېچ ئازدۇرغۇچى بولمايدۇ، ئاللاھ غالىب، (دۈشمەنلىرىنى) جازالىغۇچى ئەمەسمۇ؟');
INSERT INTO "saleh" VALUES(39,38,'39:38',' ئەگەر ئۇلاردىن: «ئاسمانلارنى ۋە زېمىننى كىم ياراتقان» دەپ سورىساڭ، شۈبھىسىزكى، ئۇلار: «ئاللاھ ياراتقان» دەيدۇ. ئېيتقىنكى، «ئەگەر ئاللاھ ماڭا بىرەر زىيان ـ زەخمەت يەتكۈزۈشنى ئىرادە قىلسا، ئۇنى دەپئى قىلىشقا، يا ئاللاھ ماڭا رەھمەت قىلىشنى ئىرادە قىلسا، ئۇنى توسۇۋېلىشقا ئاللاھتىن باشقا سىلەر ئىبادەت قىلىۋاتقان مەبۇدلار قادىر بولالامدۇ؟ ماڭا ئېيتىپ بېرىڭلارچۇ؟» ئېيتقىنكى، «ماڭا ئاللاھ كۇپايىدۇر، تەۋەككۈل قىلغۇچىلار ئۇنىڭغا تەۋەككۇل قىلسۇن (يەنى ھەممە ئىشنى ئاللاھقا تاپشۇرسۇن)».');
INSERT INTO "saleh" VALUES(39,39,'39:39',' ئېيتقىنكى، «ئى قەۋمىم! سىلەر ئۆز ھالىڭلار بويىچە ئىشلەڭلار، مەنمۇ ئۆز ھالىم بويىچە ئىشلەيمەن، سىلەر ئۇزۇنغا قالماي خارلىغۇچى ئازابنىڭ كىمگە كېلىدىغانلىقىنى، مەڭگۈلۈك ئازابنىڭ كىمگە چۈشىدىغانلىقىنى بىلىسىلەر»[39ـ40].');
INSERT INTO "saleh" VALUES(39,40,'39:40',' ئېيتقىنكى، «ئى قەۋمىم! سىلەر ئۆز ھالىڭلار بويىچە ئىشلەڭلار، مەنمۇ ئۆز ھالىم بويىچە ئىشلەيمەن، سىلەر ئۇزۇنغا قالماي خارلىغۇچى ئازابنىڭ كىمگە كېلىدىغانلىقىنى، مەڭگۈلۈك ئازابنىڭ كىمگە چۈشىدىغانلىقىنى بىلىسىلەر»[39ـ40].');
INSERT INTO "saleh" VALUES(39,41,'39:41',' شۈبھىسىزكى، بىز ساڭا كىتابنى ئىنسانلار ئۈچۈن ھەق بىلەن نازىل قىلدۇق، كىمكى ھىدايەت تاپىدىكەن، ئۆزى ئۈچۈن ھىدايەت تاپقان بولىدۇ، كىمكى ئازىدىكەن، ئۆزىنىڭ زىيىنى ئۈچۈن ئازغان بولىدۇ، سەن ئۇلارغا ھامىي ئەمەسسەن (يەنى ئۇلارنى ئىمانغا زورلاشقا مۇئەككەل ئەمەسسەن).');
INSERT INTO "saleh" VALUES(39,42,'39:42',' ئىنسانلار ئۆلىدىغان چاغلىرىدا، ئاللاھ ئۇلارنىڭ جانلىرىنى ئالىدۇ. ئۆلمىگەنلەرنىڭ جانلىرىنىمۇ ئۇخلىغان چاغلىرىدا ئالىدۇ. ئۆلۈمگە ھۆكۈم قىلىنغان (يەنى ئەجىلى يەتكەنلەر) نىڭ جانلىرىنى تۇتۇپ قېلىپ، قالغانلارنىڭ جانلىرىنى مۇئەييەن ۋاقىتقىچە (يەنى ئەجىلى يەتكۈچە) قويۇپ بېرىدۇ، بۇنىڭدا پىكىر يۈرگۈزىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(39,43,'39:43','(ئۇلار پىكىر يۈرگۈزمىدى) بەلكى ئاللاھنى قويۇپ، شاپائەتچىلەرنى تۇتتى. ئېيتقىنكى، «ئۇلار (يەنى شاپائەتچى قىلىنغانلار) ھېچ نەرسىگە قادىر بولالمايدىغان ۋە (ھېچ نەرسىنى) سەزمەيدىغان تۇرسىمۇ (ئۇلارنى شاپائەتچى تۇتامدۇ؟).');
INSERT INTO "saleh" VALUES(39,44,'39:44','شاپائەتنىڭ ھەممىسى ئاللاھنىڭ ئىلكىدىدۇر (ھېچ ئادەم ئاللاھنىڭ ئىزنىسىز شاپائەت قىلالمايدۇ)، ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر. ئاندىن سىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر».');
INSERT INTO "saleh" VALUES(39,45,'39:45','يالغۇز ئاللاھ تىلغا ئېلىنسا، ئاخىرەتكە ئىشەنمەيدىغانلارنىڭ دىللىرى سىقىلىپ كېتىدۇ. ئاللاھتىن باشقا مەبۇدلار تىلغا ئېلىنسا، ئۇلار خۇش بولۇپ كېتىدۇ. ');
INSERT INTO "saleh" VALUES(39,46,'39:46',' ئېيتقىنكى، «ئى ئاسمانلارنى ۋە زېمىننى ياراتقۇچى، يوشۇرۇننى ۋە ئاشكارىنى بىلگۈچى ئاللاھ! سەن بەندىلىرىڭنىڭ ئارىسىدا ئۇلار ئىختىلاپ قىلىشقان نەرسىلەر ئۈستىدە ھۆكۈم چىقىرىسەن».');
INSERT INTO "saleh" VALUES(39,47,'39:47','ئەگەر (ئۆزلىرىگە) زۇلۇم قىلغانلارنىڭ ئىگىدارچىلىقىدا يەر يۈزىدىكى نەرسىلەرنىڭ ھەممىسى يەنە شۇنىڭدەك بىر ھەسسە كۆپ نەرسە بولىدىغان بولسا، قىيامەت كۈنىدىكى قاتتىق ئازاب ئۈچۈن ئۇنى فىدىيە بەرگەن بولاتتى، ئۇلارغا ئاللاھ تەرىپىدىن (بۇ دۇنيادىكى چاغلىرىدا) ئويلاپ باقمىغان ئازابلار ئاشكارا بولىدۇ. ');
INSERT INTO "saleh" VALUES(39,48,'39:48','ئۇلارغا ئۇلارنىڭ (دۇنيادا) قىلغان يامان ئىشلىرى ئاشكارا بولىدۇ، ئۇلارنىڭ (دۇنيادىكى چاغدا پەيغەمبەرنىڭ دەۋىتىنى) مەسخىرە قىلغانلىقلىرىنىڭ جازاسى ئۈچۈن ئۇلارنى (قاتتىق) ئازاب ئورىۋالىدۇ.');
INSERT INTO "saleh" VALUES(39,49,'39:49',' ئىنسانغا بىرەر مۇسىبەت يەتسە، بىزگە دۇئا قىلىپ (ئىلتىجا قىلىدۇ)، ئاندىن ئۇنىڭغا مەرھەمەت قىلىپ بىرەر نېمىتىمىزنى ئاتا قىلساق، ئۇ: «بۇ نېمەت ماڭا ئۆز ئىلمىمدىن (يەنى پەزلىمدىن، ئەمگىكىمدىن، تىرىشچانلىقىمدىن، رىزىق تېپىشنىڭ يوللىرىنى بىلگەنلىكىمدىن) كەلدى» دەيدۇ. (ئىش ئۇ ئويلىغاندەك ئەمەس) بەلكى ئۇ سىناقتۇر (يەنى نېمەت ئاتا قىلىش بىلەن، ئۇنى ئىتائەت قىلامدۇ؟ ئاسىيلىق قىلامدۇ؟ دەپ سىنايمىز). لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(39,50,'39:50',' مۇنداق سۆزنى ئۇلاردىن ئىلگىرىكىلەرمۇ قىلغان، ئۇلارنىڭ ئېرىشكەن مال ـ مۈلۈكلىرى ئۇلارغا ئەسقاتمىدى.');
INSERT INTO "saleh" VALUES(39,51,'39:51',' ئۇلار (دۇنيادا) قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتتى. بۇلارنىڭ ئىچىدىكى زۇلۇم قىلغانلارمۇ قىلغان يامانلىقلىرىنىڭ جازاسىنى تارتىدۇ، ئۇلار ئازابىمىزدىن قۇتۇلالمايدۇ.');
INSERT INTO "saleh" VALUES(39,52,'39:52',' ئۇلار بىلمەمدۇكى، ئاللاھ (سىناش يۈزىسىدىن) خالىغان ئادەمنىڭ رىزقىنى كەڭ قىلىدۇ، (خالىغان ئادەمنىڭ رىزقىنى) تار قىلىدۇ، شەك ـ شۈبھىسىزكى، بۇنىڭدا ئىمان ئېيتقان قەۋم ئۈچۈن نۇرغۇن ئالامەتلەر بار.');
INSERT INTO "saleh" VALUES(39,53,'39:53',' (ئى مۇھەممەد! مېنىڭ تىلىمدىن) ئېيتقىنكى، «(گۇناھلارنى قىلىۋېرىپ) ئۆزلىرىگە جىنايەت قىلغان بەندىلىرىم! ئاللاھنىڭ رەھمىتىدىن ئۈمىدسىزلەنمەڭلار. ئاللاھ ھەقىقەتەن (خالىغان ئادەمنىڭ) جىمى گۇناھلىرىنى مەغپىرەت قىلىدۇ، شۈبھىسىزكى، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(39,54,'39:54',' سىلەرگە ئازاب كېلىپ، ياردەمگە ئېرىشەلمىگىنىڭلاردىن بۇرۇن پەرۋەردىگارىڭلار تەرەپكە قايتىڭلار ۋە ئۇنىڭغا ئىتائەت قىلىڭلار. ');
INSERT INTO "saleh" VALUES(39,55,'39:55',' سىلەر تۇيمىغان ھالەتتە سىلەرگە ئازابنىڭ ئۇشتۇمتۇت كېلىشىدىن ئىلگىرى سىلەرگە نازىل قىلىنغان ئەڭ گۈزەل قۇرئانغا ئەگىشىڭلار». ');
INSERT INTO "saleh" VALUES(39,56,'39:56','ھەر قانداق ئادەم: «ئاللاھنىڭ تائىتىدە كەتكۈزۈپ قويغانلىرىمغا ھەسرەت! مەن (ئاللاھنىڭ شەرىئىتىنى ۋە دىنىنى) مەسخىرە قىلغۇچىلاردىن ئىدىم» دەپ قالماسلىق ئۈچۈن.');
INSERT INTO "saleh" VALUES(39,57,'39:57',' ياكى «ئاللاھ مېنى ھىدايەت قىلغان بولسا (ھىدايەت تېپىپ) ئەلۋەتتە تەقۋادارلاردىن بولاتتىم» دەپ قالماسلىق ئۈچۈن.');
INSERT INTO "saleh" VALUES(39,58,'39:58','ياكى ئازابنى كۆرگەن چاغدا: «كاشكى دۇنياغا قايتىشىمغا بولسا ئىدى، (ئۇ چاغدا ئاللاھقا ئىتائەت قىلىپ) ياخشىلاردىن بولار ئىدىم» دەپ قالماسلىق ئۈچۈن (پەرۋەردىگارىغا بويسۇنسۇن).');
INSERT INTO "saleh" VALUES(39,59,'39:59',' دۇرۇس، ھەقىقەتەن ساڭا مېنىڭ نۇرغۇن ئايەتلىرىم (يەنى ھىدايەتكە سەۋەب بولىدىغان قۇرئان) كەلدى. سەن ئۇلارنى ئىنكار قىلدىڭ، بويۇنتاۋلىق قىلدىڭ، سەن كاپىرلاردىن بولدۇڭ.');
INSERT INTO "saleh" VALUES(39,60,'39:60',' قىيامەت كۈنى ئاللاھقا يالغاننى چاپلىغانلارنىڭ يۈزلىرىنى قاپقارا كۆرىسەن، جەھەننەمدە مۇتەكەببىرلەرگە ئورۇن يوقمۇ؟ ');
INSERT INTO "saleh" VALUES(39,61,'39:61',' ئاللاھ (شېرىكتىن ۋە گۇناھتىن) ساقلانغۇچىلارنى بەختكە ئېرىشتۈرىدىغان يوللار ئارقىلىق (ئازابتىن) قۇتۇلدۇرىدۇ، ئۇلارغا كۈلپەت يەتمەيدۇ، ئۇلار قايغۇرمايدۇ.');
INSERT INTO "saleh" VALUES(39,62,'39:62',' ئاللاھ ھەممە نەرسىنى ياراتقۇچىدۇر، ھەممە نەرسىگە ھامىيدۇر (ئۇلارنى خالىغانچە تەسەررۇپ قىلىدۇ).');
INSERT INTO "saleh" VALUES(39,63,'39:63',' ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى ئاللاھنىڭ ئىلكىدىدۇر، ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلار - ئەنە شۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(39,64,'39:64','(ئى مۇھەممەد!) ئېيتقىنكى، «ئى نادانلار! مېنى ئاللاھتىن غەيرىيگە ئىبادەت قىلىشقا بۇيرۇمسىلەر؟»');
INSERT INTO "saleh" VALUES(39,65,'39:65','ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە: «ئەگەر سەن ئاللاھقا شېرىك كەلتۈرسەڭ، سېنىڭ ئەمەلىڭ ئەلۋەتتە بىكار بولۇپ كېتىدۇ، سەن ئەلۋەتتە زىيان تارتقۇچىلاردىن بولۇپ قالىسەن، بەلكى يالغۇز ئاللاھقا ئىبادەت قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن» دەپ ۋەھىي قىلىندى[65 ـ 66].');
INSERT INTO "saleh" VALUES(39,66,'39:66','ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە: «ئەگەر سەن ئاللاھقا شېرىك كەلتۈرسەڭ، سېنىڭ ئەمەلىڭ ئەلۋەتتە بىكار بولۇپ كېتىدۇ، سەن ئەلۋەتتە زىيان تارتقۇچىلاردىن بولۇپ قالىسەن، بەلكى يالغۇز ئاللاھقا ئىبادەت قىلغىن ۋە شۈكۈر قىلغۇچىلاردىن بولغىن» دەپ ۋەھىي قىلىندى[65 ـ 66].');
INSERT INTO "saleh" VALUES(39,67,'39:67',' ئۇلار ئاللاھنى ھەقىقىي رەۋىشتە تونۇمىدى. قىيامەت كۈنى زېمىن پۈتۈنلەي ئاللاھنىڭ چاڭگىلىدا بولىدۇ، ئاسمانلار ئاللاھنىڭ ئوڭ قولىدا قاتلىنىپ تۇرىدۇ، ئاللاھ ئۇلار (يەنى مۇشرىكلار) نىڭ شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر ۋە ئۈستۈندۇر.');
INSERT INTO "saleh" VALUES(39,68,'39:68',' سۇر چېلىنغاندا ئاسمانلاردىكى ۋە زېمىندىكى ئاللاھ خالىغاندىن باشقا مەخلۇقاتلارنىڭ ھەممىسى ئۆلىدۇ، ئاندىن سۇر ئىككىنچى قېتىم چېلىنغاندا ناگاھان ئۇلار تۇرۇپ (نېمىگە بۇيرۇلىدىغانلىقلىرىغا) قاراپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(39,69,'39:69','مەھشەرگاھ پەرۋەردىگارىنىڭ نۇرى بىلەن يورۇيدۇ، نامە ـ ئەماللار ھازىر قىلىنىدۇ، پەيغەمبەرلەر ۋە گۇۋاھچىلار كەلتۈرۈلىدۇ، ئۇلارنىڭ ئارىسىدا ئادىل ھۆكۈم چىقىرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(39,70,'39:70','ھەر ئادەمگە قىلغان ئەمەلىنىڭ مۇكاپات ياكى جازاسى تولۇق بېرىلىدۇ، ئاللاھ ئۇلارنىڭ ئەمەللىرىنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(39,71,'39:71',' كاپىرلار جەھەننەمگە توپ ـ توپ بولۇپ ھەيدىلىدۇ، ئۇلار جەھەننەمگە يېتىپ كەلگەندە، جەھەننەمنىڭ دەرۋازىلىرى ئېچىلىدۇ، جەھەننەمنى باشقۇرغۇچى پەرىشتىلەر ئۇلارغا: «ئاراڭلاردىن سىلەرگە پەرۋەردىگارىڭلارنىڭ ئايەتلىرىنى سىلەرگە ئوقۇپ بېرىدىغان، بۈگۈنكى ئۇچرىشىشنىڭ بارلىقىدىن ئاگاھلاندۇرىدىغان پەيغەمبەرلەر كەلمىگەنمۇ؟» دەيدۇ. ئۇلار: «شۇنداق (پەيغەمبەرلەر كەلگەن، بىزنى ئاگاھلاندۇرغان)، لېكىن ئازابقا دۇچار بولۇش ھۆكمى (كۇفرى ۋە قىلمىشلىرى تۈپەيلىدىن) كاپىرلارغا تېگىشلىك بولدى» دەيدۇ.');
INSERT INTO "saleh" VALUES(39,72,'39:72',' (ئۇلارغا) «جەھەننەمنىڭ دەرۋازىلىرىدىن كىرىڭلار، جەھەننەمدە مەڭگۈ قېلىڭلار، مۇتەكەببىرلارنىڭ جايى نېمىدېگەن يامان!» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(39,73,'39:73',' پەرۋەردىگارىغا تەقۋادارلىق قىلغانلار جەننەتكە توپ ـ توپ بولغان ھالدا ماڭدۇرۇلىدۇ، ھالبۇكى، ئۇلار جەننەتكە يېتىپ كەلگەن چاغدا ئۇنىڭ دەرۋازىلىرى ئېچىلىپ بولغان بولىدۇ، جەننەتكە مۇئەككەل پەرىشتىلەر ئۇلارغا: «سىلەرگە ئامانلىق بولسۇن، سىلەر (گۇناھلارنىڭ كىرلىرىدىن) پاك بولدۇڭلار، جەننەتكە كىرىڭلار، (ئۇنىڭدا) مەڭگۈ قېلىڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(39,74,'39:74',' ئۇلار (جەننەتكە كىرىپ ئورۇنلاشقان چاغدا) ئېيتىدۇ: «جىمى ھەمدۇسانا ئاللاھقا خاستۇركى، ئۇ بىزگە قىلغان ۋەدىسىنى ئىشقا ئاشۇردى، جەننەتنىڭ زېمىنىغا بىزنى ۋارىس قىلدى. جەننەتتە ئۆزىمىز خالىغان جايدا تۇرىمىز، ئىشلىگۈچىلەرنىڭ ئەجرى (يەنى جەننەت) نېمىدېگەن ياخشى!».');
INSERT INTO "saleh" VALUES(39,75,'39:75',' پەرىشتىلەرنىڭ پەرۋەردىگارىغا تەسبىھ ئېيتقان، ھەمدى ئېيتقان ھالدا ئەرشنىڭ چۆرىسىنى ئوراپ تۇرغانلىقىنى كۆرىسەن، ئۇلارنىڭ ئارىسىدا ھەققانىي ھۆكۈم چىقىرىلىدۇ (مۆمىنلەر جەننەتكە كىرىدۇ، كاپىرلار دوزاخقا كىرىدۇ)، «جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(40,1,'40:1','ھامىم.');
INSERT INTO "saleh" VALUES(40,2,'40:2',' (بۇ) كىتاب (يەنى قۇرئان) غالىب، ھەممىنى بىلگۈچى ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(40,3,'40:3',' (ئاللاھ) گۇناھنى مەغپىرەت قىلغۇچىدۇر، تەۋبىنى قوبۇل قىلغۇچىدۇر. (ئاللاھنىڭ) ئازابى قاتتىقتۇر، (ئاللاھ) ئىنئام ئىگىسىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئاقىۋەت قايتىدىغان جاي ئۇنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(40,4,'40:4','ئاللاھنىڭ ئايەتلىرى (يەنى قۇرئان) توغرىسىدا پەقەت كاپىرلار مۇجادىلە قىلىشىدۇ، ئۇلارنىڭ شەھەرلەردە بېرىپ ـ كېلىپ يۈرۈشلىرىگە مەغرۇر بولۇپ كەتمىگىن.');
INSERT INTO "saleh" VALUES(40,5,'40:5',' ئۇلار (يەنى مەككە كاپىرلىرى) دىن بۇرۇن، نۇھنىڭ قەۋمى ۋە ئۇلاردىن كېيىنكى (پەيغەمبەرلىرىگە قارشى ئۇيۇشقان ئاد ۋە سەمۇدقا ئوخشاش) جامائەلار (پەيغەمبەرلىرىنى) ئىنكار قىلدى. (پەيغەمبەرلىرىنى ئىنكار قىلغۇچى ئۈممەتلەردىن) ھەر ئۈممەت ئۆزلىرىنىڭ پەيغەمبىرىنى ھالاك قىلىشنى قەستلىدى. ھەقنى بىكار قىلىش ئۈچۈن باتىل (سۆزلەر ئارقىلىق پەيغەمبەرلىرى بىلەن) مۇنازىرە قىلىشتى، شۇنىڭ بىلەن ئۇلارنى ھالاك قىلدىم، مېنىڭ ئازابىم قانداق ئىكەن؟');
INSERT INTO "saleh" VALUES(40,6,'40:6','شۇنىڭدەك (يەنى ئىلگىرىكى ئۈممەتلەرگە تېگىشلىك بولغاندەك) پەرۋەردىگارىڭنىڭ ئازابى كاپىرلارغا تېگىشلىك بولدى. ئۇلار دوزاخ ئەھلىدۇر.');
INSERT INTO "saleh" VALUES(40,7,'40:7','ئەرشنى كۆتۈرۈپ تۇرغان ۋە ئەرشنىڭ چۆرىسىدىكى پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، ئۇنىڭغا ئىمان ئېيتىدۇ، مۆمىنلەرگە مەغپىرەت تىلەيدۇ. (ئۇلار ئېيتىدۇ) «پەرۋەردىگارىمىز! سېنىڭ رەھمىتىڭ ۋە ئىلمىڭ ھەممە نەرسىنى ئۆز ئىچىگە ئالىدۇ، تەۋبە قىلغانلارغا ۋە سېنىڭ يولۇڭغا ئەگەشكەنلەرگە مەغپىرەت قىلغىن، ئۇلارنى دوزاخ ئازابىدىن ساقلىغىن.');
INSERT INTO "saleh" VALUES(40,8,'40:8',' پەرۋەردىگارىمىز! سەن ئۇلارنى، ئۇلارنىڭ ئاتا ـ بوۋىلىرىدىن، ئاياللىرىدىن، ئەۋلادلىرىدىن ياخشى بولغانلارنى ئۇلارغا ۋەدە قىلغان مەڭگۈلۈك جەننەتكە كىرگۈزگىن، سەن ھەقىقەتەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن.');
INSERT INTO "saleh" VALUES(40,9,'40:9',' پەرۋەردىگارىمىز! سەن ئۇلارنى يامان ئىشلاردىن ساقلىغىن، سەن كىمنى يامان ئىشلاردىن ساقلايدىكەنسەن، شۈبھىسىزكى، بۇ كۈندە سەن ئۇنىڭغا رەھمەت قىلغان بولىسەن، بۇ چوڭ بەختتۇر».');
INSERT INTO "saleh" VALUES(40,10,'40:10',' شۈبھىسىزكى، كاپىرلارغا: «ئاللاھنىڭ (دۇنيادا) سىلەرگە بولغان غەزىپى سىلەرنىڭ (ئاخىرەتتە) بىر ـ بىرىڭلارغا بولغان غەزىپىڭلاردىن ئېشىپ چۈشىدۇ، چۈنكى سىلەر ئىمانغا دەۋەت قىلىناتتىڭلار، لېكىن ئىمان ئېيتمايتتىڭلار» دەپ نىدا قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(40,11,'40:11',' ئۇلار: «پەرۋەردىگارىمىز! بىزنى ئىككى قېتىم ئۆلتۈرۈپ، ئىككى قېتىم تىرىلدۈردۈڭ، بىز گۇناھىمىزغا ئىقرار قىلدۇق، ئەمدى چىقىش يولى تېپىلارمۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(40,12,'40:12',' بۇ شۇنىڭ ئۈچۈندۇركى، سىلەر ئاللاھنى بىر دەپ بىلىشكە چاقىرىلغاندا (بۇنى) ئىنكار قىلدىڭلار، ئەگەر ئاللاھقا شېرىك كەلتۈرۈلسە (يەنى لات، ئۇززا قاتارلىق بۇتلارغا چوقۇنۇشقا چاقىرىلساڭلار) ئۇنى تەستىق قىلىسىلەر (يەنى ئۇلارنىڭ ئىلاھلىقىنى ئېتىراپ قىلىسىلەر)، ھۆكۈم يۈكسەك، بۈيۈك ئاللاھقا خاستۇر.');
INSERT INTO "saleh" VALUES(40,13,'40:13',' ئاللاھ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەرگە كۆكتىن يامغۇر بىلەن رىزىق چۈشۈرۈپ بېرىدۇ، پەقەت ئاللاھنىڭ تائىتىگە قايتقان كىشىلەرلا (ئاللاھنىڭ ئايەتلىرىدىن) ۋەز ـ نەسىھەت ئالىدۇ.');
INSERT INTO "saleh" VALUES(40,14,'40:14',' دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئاللاھقا ئىبادەت قىلىڭلار، كاپىرلار ياقتۇرمىغان تەقدىردىمۇ.');
INSERT INTO "saleh" VALUES(40,15,'40:15',' ئاللاھنىڭ مەرتىۋىسى يۇقىرىدۇر، (ئاللاھ) ئەرشنىڭ ئىگىسىدۇر، (خالايىق) مۇلاقات بولۇشىدىغان كۈن (يەنى قىيامەت كۈنى) دىن ئاگاھلاندۇرۇش ئۈچۈن، ئاللاھ ئۆز ھۆكمى بويىچە بەندىلىرىدىن خالىغان ئادەمگە ۋەھىي چۈشۈرىدۇ.');
INSERT INTO "saleh" VALUES(40,16,'40:16','ئۇلار قەبرىلەردىن چىققان كۈندە ئاللاھقا ئۇلاردىن (يەنى ئۇلارنىڭ ئەھۋالىدىن، ئەمەللىرىدىن، سىرلىرىدىن) ھېچ نەرسە مەخپىي قالمايدۇ، بۈگۈن پادىشاھلىق كىمگە خاس؟ غالىب بىر ئاللاھقا خاستۇر. ');
INSERT INTO "saleh" VALUES(40,17,'40:17','بۈگۈن ھەر ئادەمگە قىلغان ئەمەلىگە يارىشا جازا بېرىلىدۇ، بۈگۈن زۇلۇم يوقتۇر، ئاللاھ ھەقىقەتەن تېز ھېساب ئالغۇچىدۇر.');
INSERT INTO "saleh" VALUES(40,18,'40:18',' ئۇلارنى قىيامەت كۈنىدىن ئاگاھلاندۇرغىن، ئۇ چاغدا دىللار (قورقۇنچنىڭ قاتتىقلىقىدىن) بوغۇزلارغا قاپلىشىۋالىدۇ، ئۇلار قايغۇ ـ ھەسرەتكە تولىدۇ، زالىملارغا يېقىن دوست ۋە شاپائىتى قوبۇل قىلىنىدىغان شاپائەتچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(40,19,'40:19','ئاللاھ كۆزلەرنىڭ خىيانىتىنى ۋە دىللاردىكى يوشۇرۇن نەرسىلەرنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(40,20,'40:20','ئاللاھ ئادىللىق بىلەن ھۆكۈم چىقىرىدۇ، ئۇلار ئاللاھنى قويۇپ ئىبادەت قىلىۋاتقان مەبۇدلار ھېچ نەرسىگە ھۆكۈم چىقىرىشقا قادىر ئەمەس، ئاللاھ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئۇلارنىڭ ئىشلىرىنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(40,21,'40:21',' ئۇلار زېمىندا سەير قىلىپ، ئۇلاردىن ئىلگىرى ئۆتكەنلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ ئۇلار بولسا زېمىندا كۈچ ـ قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي ـ ئىمارەت قۇرۇش) جەھەتتە بۇلار (يەنى سېنىڭ قەۋمىڭنىڭ كاپىرلىرى) دىن ئۈستۈن ئىدى، گۇناھى تۈپەيلىدىن ئاللاھ ئۇلارنى جازالىدى، ئۇلارنى ھېچ ئەھەدى ئاللاھنىڭ ئازابىدىن ساقلاپ قالالمىدى.');
INSERT INTO "saleh" VALUES(40,22,'40:22','بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەرنى ئېلىپ كەلگەن ئىدى، ئۇلار ئىشەنمىدى، شۇنىڭ بىلەن ئاللاھ ئۇلارنى جازالىدى. ئاللاھ ھەقىقەتەن كۈچلۈكتۇر، (ئاللاھنىڭ) ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(40,23,'40:23',' بىز مۇسانى مۆجىزىلىرىمىز بىلەن، روشەن دەلىل بىلەن ھەقىقەتەن پىرئەۋنگە، ھامانغا ۋە قارۇنغا ئەۋەتتۇق، ئۇلار (مۇسانى) يالغانچى، سېھىرگەر دېيىشتى[23ـ24].');
INSERT INTO "saleh" VALUES(40,24,'40:24',' بىز مۇسانى مۆجىزىلىرىمىز بىلەن، روشەن دەلىل بىلەن ھەقىقەتەن پىرئەۋنگە، ھامانغا ۋە قارۇنغا ئەۋەتتۇق، ئۇلار (مۇسانى) يالغانچى، سېھىرگەر دېيىشتى[23ـ24].');
INSERT INTO "saleh" VALUES(40,25,'40:25',' مۇسا ئۇلارغا دەرگاھىمىزدىن ھەق (يەنى پەيغەمبەرلىك) بىلەن كەلگەندە، ئۇلار: «مۇسا بىلەن ئىمان ئېيتقانلارنىڭ ئوغۇللىرىنى (نەسلىنى قۇرۇتۇش ئۈچۈن) ئۆلتۈرۈڭلار، ئاياللىرىنى (خىزمەتكە سېلىش ئۈچۈن) قالدۇرۇڭلار» دېدى. كاپىرلارنىڭ ھىيلە ـ مىكرى پەقەت بەربات بولىدۇ.');
INSERT INTO "saleh" VALUES(40,26,'40:26',' پىرئەۋن ئېيتتى: «مېنى قويۇۋېتىڭلار، مۇسانى ئۆلتۈرىمەن، مۇسا (ئۆزىنى مەندىن قۇتۇلدۇرۇش ئۈچۈن) پەرۋەردىگارىنى چاقىرسۇن، مەن ھەقىقەتەن مۇسانىڭ دىنىڭلارنى ئۆزگەرتىۋېتىشىدىن ياكى زېمىندا (يەنى دۆلەتتە) قالايمىقانچىلىق تۇغدۇرۇشىدىن قورقىمەن».');
INSERT INTO "saleh" VALUES(40,27,'40:27',' مۇسا ئېيتتى: «مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلار (بولغان ئاللاھ) قا سىغىنىپ، ھېساب كۈنى (يەنى قىيامەت كۈنى) گە ئىشەنمەيدىغان ھەر بىر مۇتەكەببىر (نىڭ زىيانكەشلىك قىلىشى) دىن پاناھ تىلەيمەن».');
INSERT INTO "saleh" VALUES(40,28,'40:28',' پىرئەۋن خانىدانىغا مەنسۇپ، ئىمانىنى يوشۇرىدىغان بىر مۆمىن ئادەم ئېيتتى: «سىلەر "پەرۋەردىگارىم ئاللاھ" دېگەن كىشىنى ئۆلتۈرەمسىلەر؟ ھالبۇكى، ئۇ سىلەرگە ھەقىقەتەن پەرۋەردىگارىڭلارنىڭ دەرگاھىدىن روشەن دەلىللەرنى ئېلىپ كەلدى. ئەگەر ئۇ (پەيغەمبەرلىك دەۋاسىدا) يالغانچى بولسا، يالغانچىلىقنىڭ زىيىنى ئۇنىڭ ئۆزىگىدۇر، ئەگەر ئۇ راستچىل بولسا، ئۇ سىلەرنى قورقۇتقان ئازابنىڭ بىر قىسمى سىلەرگە كېلىدۇ، ئاللاھ ھەقىقەتەن ھەددىدىن ئاشقان يالغانچىنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(40,29,'40:29',' ئى قەۋمىم! بۈگۈن سەلتەنەت سىلەرنىڭ ئىلكىڭلاردىدۇر، (مىسىر) زېمىنىدا سىلەر غالىبسىلەر، ئەگەر بىزگە ئاللاھنىڭ ئازابى كەلسە، ئۇنىڭدىن قۇتۇلۇشقا بىزگە كىم ياردەم بېرىدۇ؟» پىرئەۋن ئېيتتى: «مەن سىلەرگە پەقەت نېمىنى توغرا دەپ قارىسام، شۇنى مەسلىھەت بېرىمەن. سىلەرنى مەن پەقەت توغرا يولغا باشلايمەن». ');
INSERT INTO "saleh" VALUES(40,30,'40:30',' مۆمىن ئادەم ئېيتتى: «ئى قەۋمىم! سىلەرنىڭ نۇھ، ئاد، سەمۇد قەۋملىرىگە ئوخشاش ۋە ئۇلاردىن كېيىنكىلەرگە ئوخشاش جامائەلەر ئۇچرىغان جازاغا ئۇچرىشىڭلاردىن قورقىمەن، ئاللاھ بەندىلىرىگە زۇلۇم قىلىشنى خالىمايدۇ[30ـ31].');
INSERT INTO "saleh" VALUES(40,31,'40:31',' مۆمىن ئادەم ئېيتتى: «ئى قەۋمىم! سىلەرنىڭ نۇھ، ئاد، سەمۇد قەۋملىرىگە ئوخشاش ۋە ئۇلاردىن كېيىنكىلەرگە ئوخشاش جامائەلەر ئۇچرىغان جازاغا ئۇچرىشىڭلاردىن قورقىمەن، ئاللاھ بەندىلىرىگە زۇلۇم قىلىشنى خالىمايدۇ[30ـ31].');
INSERT INTO "saleh" VALUES(40,32,'40:32',' ئى قەۋمىم! مەن سىلەر ئۈچۈن ھەقىقەتەن قىيامەت كۈنىدىن ئەنسىرەيمەن.');
INSERT INTO "saleh" VALUES(40,33,'40:33',' ئۇ كۈندە (دوزاخ ئازابىنىڭ دەھشىتىدىن قورقۇپ) ئارقاڭلارغا چېكىنىسىلەر، سىلەرگە ئاللاھنىڭ ئازابىدىن ھېچ قۇتۇلدۇرغۇچى بولمايدۇ. كىمكى ئاللاھ ئۇنى گۇمراھ قىلىدىكەن، ئۇنى ھىدايەت قىلغۇچى بولمايدۇ.');
INSERT INTO "saleh" VALUES(40,34,'40:34',' شەك ـ شۈبھىسىزكى، ئىلگىرى سىلەرگە يۇسۇف (ئاللاھنىڭ دەرگاھىدىن) روشەن مۆجىزىلەرنى ئېلىپ كەلدى. ئۇ ئېلىپ كەلگەن دەلىلدىن سىلەر ھامان شەكلەندىڭلار، تاكى ئۇ ۋاپات بولغاندا، (دەلىل ـ پاكىتسىز) ھالدا، ئاللاھ ئۇنىڭدىن كېيىن ھەرگىز پەيغەمبەر ئەۋەتمەيدۇ دېدىڭلار، ئاللاھ (گۇناھتا) ھەددىدىن ئاشقۇچىنى، (دىندا) شەكلەنگۈچىنى مۇشۇنداق ئازدۇرىدۇ».');
INSERT INTO "saleh" VALUES(40,35,'40:35',' ئاللاھنىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (ئاللاھ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلار ئاللاھنىڭ دەرگاھىدا ۋە مۆمىنلەرنىڭ نەزىرىدە قاتتىق نەپرەتكە قالىدۇ، بۇزغۇنچىلىق قىلىپ (ئىماندىن باش تارتقان)، (بەندىلەرگە) زومىگەرلىك قىلغان ھەر قانداق ئادەمنىڭ دىلىنى ئاللاھ مۇشۇنداق پېچەتلىۋېتىدۇ.');
INSERT INTO "saleh" VALUES(40,36,'40:36','پىرئەۋن (ۋەزىرى ھامانغا): «ئى ھامان! ماڭا بىر ئېگىز بىنا سالغىن، ئۇنىڭ بىلەن مەن دەرۋازىلارغا- ئاسماننىڭ دەرۋازىلىرىغا يېتىشىم مۇمكىن، شۇنىڭ بىلەن مەن مۇسانىڭ ئىلاھىنى كۆرەي، مەن ھەقىقەتەن مۇسانى يالغانچى دەپ گۇمان قىلىمەن» دېدى. پىرئەۋنگە ئۇنىڭ يامان ئەمەلى شۇنداق چىرايلىق كۆرسىتىلدى، پىرئەۋن (گۇمراھلىقى تۈپەيلىدىن ھىدايەت) يولىدىن مەنئى قىلىندى، پىرئەۋننىڭ ھىيلە ـ مىكرى پۈتۈنلەي بەربات بولدى[36ـ37].');
INSERT INTO "saleh" VALUES(40,37,'40:37','پىرئەۋن (ۋەزىرى ھامانغا): «ئى ھامان! ماڭا بىر ئېگىز بىنا سالغىن، ئۇنىڭ بىلەن مەن دەرۋازىلارغا- ئاسماننىڭ دەرۋازىلىرىغا يېتىشىم مۇمكىن، شۇنىڭ بىلەن مەن مۇسانىڭ ئىلاھىنى كۆرەي، مەن ھەقىقەتەن مۇسانى يالغانچى دەپ گۇمان قىلىمەن» دېدى. پىرئەۋنگە ئۇنىڭ يامان ئەمەلى شۇنداق چىرايلىق كۆرسىتىلدى، پىرئەۋن (گۇمراھلىقى تۈپەيلىدىن ھىدايەت) يولىدىن مەنئى قىلىندى، پىرئەۋننىڭ ھىيلە ـ مىكرى پۈتۈنلەي بەربات بولدى[36ـ37].');
INSERT INTO "saleh" VALUES(40,38,'40:38','(پىرئەۋن خانىدانىدىن) ئىمان ئېيتقان كىشى ئېيتتى: «ئى قەۋمىم! سىلەر ماڭا ئەگىشىڭلار، مەن سىلەرنى توغرا يولغا باشلايمەن.');
INSERT INTO "saleh" VALUES(40,39,'40:39','ئى قەۋمىم! بۇ دۇنيا تىرىكچىلىكى پەقەت (ۋاقىتلىق) پايدىلىنىشتىن ئىبارەتتۇر، ئاخىرەت ھەقىقەتەن (مەڭگۈلۈك) قارارگاھتۇر.');
INSERT INTO "saleh" VALUES(40,40,'40:40',' كىمكى (بۇ دۇنيادا) بىرەر يامانلىقنى قىلىدىكەن، ئۇنىڭغا (ئاخىرەتتە) شۇنىڭغا لايىق جازا بېرىلىدۇ، مەيلى ئەر بولسۇن، مەيلى ئايال بولسۇن، كىمكى ئۆزى مۆمىن تۇرۇپ بىرەر ياخشى ئىش قىلىدىكەن، ئەنە شۇلار جەننەتكە كىرىدۇ، جەننەتتە ئۇلارغا ھېسابسىز رىزىق بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(40,41,'40:41',' ئى قەۋمىم! ماڭا نېمىكى، سىلەرنى مەن (دوزاختىن) قۇتۇلۇشقا دەۋەت قىلىۋاتسام، سىلەر مېنى دوزاخقا دەۋەت قىلىۋاتىسىلەرغۇ!');
INSERT INTO "saleh" VALUES(40,42,'40:42',' سىلەر مېنى ئاللاھنى ئىنكار قىلىشقا، مەن بىلمەيدىغان نەرسىلەرنى ئۇنىڭغا شېرىك كەلتۈرۈشكە دەۋەت قىلىسىلەر، ھالبۇكى، مەن سىلەرنى غالىب، (بەندىلەرنىڭ گۇناھلىرىنى) ناھايىتى مەغپىرەت قىلغۇچى (ئاللاھ) قا دەۋەت قىلىمەن.');
INSERT INTO "saleh" VALUES(40,43,'40:43','ھەقىقەتەن سىلەر مېنى ئۇنىڭغا (يەنى بۇتقا) ئىبادەت قىلىشقا چاقىرىسىلەر، ئۇنىڭ دۇنيا ۋە ئاخىرەتتە (ئۆزىگە ئىبادەت قىلىشقا) چاقىرغىنى يوق، بىزنىڭ ئاخىر بارىدىغان جايىمىز ئاللاھنىڭ دەرگاھىدۇر، (گۇمراھلىقتا) ھەددىدىن ئاشقۇچىلار ئەھلى دوزاختۇر.');
INSERT INTO "saleh" VALUES(40,44,'40:44',' (سىلەرگە ئازاب نازىل بولغان چاغدا) سىلەرگە ئېيتقان سۆزۈمنىڭ (راستلىقىنى) ئەسلەيسىلەر، مەن ئىشىمنى ئاللاھقا تاپشۇرىمەن، ئاللاھ ھەقىقەتەن بەندىلىرىنى كۆرۈپ تۇرغۇچىدۇر».');
INSERT INTO "saleh" VALUES(40,45,'40:45',' ئاللاھ ئۇنى پىرئەۋن جامائەسىنىڭ سۇيىقەستلىرىدىن ساقلىدى. پىرئەۋن جامائەسىگە ئەڭ يامان ئازاب چۈشتى.');
INSERT INTO "saleh" VALUES(40,46,'40:46','ئۇلار ئەتىگىنى ـ ئاخشىمى ئوتقا توغرىلىنىپ تۇرىدۇ، قىيامەت قايىم بولغان كۈندە: «ئى پىرئەۋن جامائەسى! ئازابنىڭ ئەڭ قاتتىقىغا (دوزاخ ئوتىغا) كىرىڭلار!» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(40,47,'40:47',' ئۇ چاغدا ئۇلار دوزاختا ئۆزئارا مۇنازىرىلىشىدۇ، ئاجىز (ئەگەشكۈچىلەر) ھاكاۋۇر (ئەگەشتۈرگۈچىلىرىگە): «شۈبھىسىزكى، بىز سىلەرگە ئەگەشكەن ئىدۇق، بىزدىن دوزاخ ئازابىدىن بىر قىسىمىنى ئېلىپ تاشلىيالامسىلەر؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(40,48,'40:48','ھاكاۋۇر (ئەگەشتۈرگۈچىلىرى): «شۈبھىسىزكى، بىز ھەممىمىز دوزاختادۇرمىز، ئاللاھ ھەقىقەتەن بەندىلەرنىڭ ئارىسىدا ھۆكۈم چىقاردى» دەيدۇ. ');
INSERT INTO "saleh" VALUES(40,49,'40:49','دوزاختىكىلەر دوزاخقا مۇئەككەل پەرىشتىلەرگە: «سىلەر پەرۋەردىگارىڭلارغا دۇئا قىلىڭلار، ئۇ بىزدىن بىر كۈنلۈك ئازابنى يېنىكلەتسە ئىكەن» دەيدۇ.');
INSERT INTO "saleh" VALUES(40,50,'40:50','ئۇلار (يەنى دوزاخقا مۇئەككەل پەرىشتىلەر): «پەيغەمبىرىڭلار سىلەرگە روشەن مۆجىزىلەرنى ئېلىپ كەلمىگەنمىدى؟» دەيدۇ. ئۇلار (يەنى كۇففارلار): «ھەئە، ئېلىپ كەلگەن ئىدى» دەيدۇ. ئۇلار (يەنى پەرىشتىلەر): «سىلەر دۇئا قىلىڭلار، (لېكىن) كاپىرلارنىڭ دۇئاسى پايدىسىزدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(40,51,'40:51',' شەك ـ شۈبھىسىزكى، بىز پەيغەمبىرىمىزگە، مۆمىنلەرگە ھاياتىي دۇنيادا ۋە (پەرىشتە، پەيغەمبەر ۋە مۆمىنلەردىن بەندىلەرنىڭ ئەمەللىرىگە گۇۋاھ بولىدىغان) گۇۋاھچىلار ھازىر بولىدىغان كۈندە ئەلۋەتتە ياردەم بېرىمىز.');
INSERT INTO "saleh" VALUES(40,52,'40:52',' ئۇ كۈندە كاپىرلارنىڭ ئۆزرىلىرى پايدا بەرمەيدۇ، ئۇلار لەنەتكە ئۇچرايدۇ، ئۇلار ئاخىرەتنىڭ ئازابىغا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(40,53,'40:53','شۈبھىسىزكى، بىز مۇساغا ھىدايەت ئاتا قىلدۇق، ئىسرائىل ئەۋلادىغا كىتاب (يەنى تەۋرات) نى مىراس قىلىپ بەردۇق.');
INSERT INTO "saleh" VALUES(40,54,'40:54',' (ئۇ) ئەقىل ئىگىلىرى ئۈچۈن ھىدايەتتۇر ۋە نەسىھەتتۇر. ');
INSERT INTO "saleh" VALUES(40,55,'40:55',' (ئى مۇھەممەد! مۇشرىكلارنىڭ يەتكۈزگەن ئەزىيەتلىرىگە) سەۋر قىلغىن، ئاللاھنىڭ (ساڭا ۋە سېنىڭ تەۋەلىرىڭگە ياردەم بېرىش) ۋەدىسى ھەقىقەتەن ھەقتۇر، گۇناھىڭغا ئىستىغپار ئېيتقىن، ئاخشىمى ـ ئەتىگىنى پەرۋەردىگارىڭغا ھەمدى بىلەن تەسبىھ ئېيتقىن.');
INSERT INTO "saleh" VALUES(40,56,'40:56','شۈبھىسىزكى، ئاللاھنىڭ ئايەتلىرى ئۈستىدە ئۆزلىرىگە (ئاللاھ تەرىپىدىن) كەلگەن ھېچقانداق دەلىل بولماستىن (قارىسىغا) جاڭجاللىشىدىغانلارنىڭ كۆكرەكلىرىدە پەقەت كىبرى بار، ئۇلار (ئاللاھنىڭ نۇرىنى ئۆچۈرۈشتىن ئىبارەت) مەقسىتىگە يېتەلمەيدۇ، ئاللاھقا سىغىنىپ (ئۇلارنىڭ شەررىدىن پاناھ تىلىگىن)، ھەقىقەتەن ئاللاھ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(40,57,'40:57',' ئاسمانلارنى ۋە زېمىننى يارىتىش ئەلۋەتتە ئىنسانلارنى يارىتىشتىن كۆپ قىيىندۇر ۋەلېكىن ئىنسانلارنىڭ كۆپچىلىكى (بۇنى) ئۇقمايدۇ.');
INSERT INTO "saleh" VALUES(40,58,'40:58','ئەما بىلەن كۆزى كۆرۈدىغان ئادەم (يەنى مۆمىن بىلەن كاپىر) باراۋەر بولمايدۇ، ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان ئادەملەر بىلەن يامان ئىش قىلغۇچى ئادەملەر باراۋەر بولمايدۇ. سىلەر ئاز ۋەز ـ نەسىھەت ئالىسىلەر. ');
INSERT INTO "saleh" VALUES(40,59,'40:59',' قىيامەت چوقۇم كېلىدۇ، ئۇنىڭدا شەك يوق ۋەلېكىن ئىنسانلارنىڭ تولىسى (ئۇنىڭغا) ئىشەنمەيدۇ (شۇنىڭ ئۈچۈن ئۇلار ئۆلگەندىن كېيىن تىرىلىشنى، قىيامەت كۈنى بېرىلىدىغان مۇكاپات بىلەن جازانى ئىنكار قىلىدۇ).');
INSERT INTO "saleh" VALUES(40,60,'40:60','پەرۋەردىگارىڭلار ئېيتىدۇ: «ماڭا دۇئا قىلىڭلار، مەن (دۇئايىڭلارنى) قوبۇل قىلىمەن (تىلىگىنىڭلارنى بېرىمەن)، شۈبھىسىزكى، مېنىڭ ئىبادىتىمدىن چوڭچىلىق قىلىپ باش تارتىدىغانلار خار ھالدا جەھەننەمگە كىرىدۇ».');
INSERT INTO "saleh" VALUES(40,61,'40:61',' (كۈندۈزى ئىشلەپ يەتكەن ھارغىنلىقتىن) ئارام ئېلىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە كېچىنى (قاراڭغۇ قىلىپ) ياراتتى، (تىرىكچىلىك يولىدا ھەرىكەتلىنىشىڭلار ئۈچۈن) كۈندۈزنى يورۇق ياراتتى. ئاللاھ ئىنسانلارغا ھەقىقەتەن مەرھەمەتلىكتۇر ۋەلېكىن ئىنسانلارنىڭ تولىسى (ئاللاھنىڭ ئېھسانىغا شۈكۈر قىلمايدۇ، ئاللاھنىڭ مەرھەمىتى ۋە نېمەتلىرىگە نانكورلۇق قىلىدۇ).');
INSERT INTO "saleh" VALUES(40,62,'40:62',' ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ھەممە نەرسىنى ياراتقۇچىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، قانداقمۇ (ئاللاھقا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىسىلەر؟');
INSERT INTO "saleh" VALUES(40,63,'40:63','ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلار (ھىدايەتتىن ۋە ھەقىقەتتىن) شۇنداق بۇرۇلۇپ كېتىدۇ (ئى مۇھەممەد! قەۋمىڭنىڭ ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلىقىغا قايغۇرۇپ كەتمىگىن، ئۇلاردىن ئىلگىرى ئۆتكەنلەرمۇ شۇنداق قىلغان).');
INSERT INTO "saleh" VALUES(40,64,'40:64',' ئاللاھ سىلەرگە زېمىننى تۇرالغۇ، ئاسماننى بىنا قىلدى. سىلەرنى سۈرەتكە كىرگۈزدى. سۈرىتىڭلارنى چىرايلىق قىلدى. سىلەرگە (تۈرلۈك) پاك نەرسىلەرنى رىزىق قىلىپ بەردى. ئەنە شۇ ئاللاھ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئالەملەرنىڭ پەرۋەردىگارى ئاللاھنىڭ بەرىكىتى بۈيۈكتۇر.');
INSERT INTO "saleh" VALUES(40,65,'40:65',' ئاللاھ ھاياتتۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، دىنىڭلارنى (شېرىكتىن ۋە رىيادىن) ساپ قىلغان ھالدا ئۇنىڭغا ئىبادەت قىلىڭلار، جىمى ھەمدۇسانا ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!');
INSERT INTO "saleh" VALUES(40,66,'40:66','(ئى مۇھەممەد!) ئېيتقىنكى: «پەرۋەردىگارىمدىن ماڭا روشەن دەلىللەر كەلگەن چاغدا، سىلەر ئاللاھنى قويۇپ ئىبادەت قىلىۋاتقان بۇتلىرىڭلارغا ئىبادەت قىلىشتىن مەنئى قىلىندىم، ئالەملەرنىڭ پەرۋەردىگارىغا بويسۇنۇشقا بۇيرۇلدۇم».');
INSERT INTO "saleh" VALUES(40,67,'40:67',' ئاللاھ سىلەرنى (يەنى ئاتاڭلار ئادەم ئەلەيھىسسالامنى) تۇپراقتىن، (ئۇنىڭ ئەۋلادىنى ئالدى بىلەن) ئابىمەنىيدىن، ئاندىن لەختە قاندىن ياراتتى، ئاندىن ئۇ سىلەرنى (ئاناڭلارنىڭ قارنىدىن) بوۋاق ھالىتىڭلاردا چىقىرىدۇ، ئاندىن (كۈچ ـ قۇۋۋەت ۋە ئەقىلدە كامالەت يېشى) قىران ۋاقتىڭلارغا يېتىسىلەر، ئاندىن قېرىيسىلەر- بەزىڭلار بۇرۇن ۋاپات بولۇپ كېتىسىلەر- ئاندىن سىلەر مۇئەييەن ۋاقىتقىچە ياشايسىلەر، سىلەر (ئاللاھنىڭ قۇدرىتىنىڭ دەلىللىرىنى) چۈشەنگەيسىلەر.');
INSERT INTO "saleh" VALUES(40,68,'40:68',' ئاللاھ تىرىلدۈرۈشكە ۋە ئۆلتۈرۈشكە قادىر، ئۇ بىرەر ئىشنى (قىلىشنى) ئىرادە قىلسا، ئۇنىڭغا «ۋۇجۇدقا كەل» دەيدۇ ـ دە، ئۇ ۋۇجۇدقا كېلىدۇ.');
INSERT INTO "saleh" VALUES(40,69,'40:69',' ئاللاھنىڭ ئايەتلىرى ئۈستىدە جاڭجاللىشىۋاتقانلارنى كۆرمىدىڭمۇ؟ ئۇلار قانداقمۇ (ھىدايەتتىن گۇمراھلىققا) بۇرۇلۇپ كېتىدۇ؟');
INSERT INTO "saleh" VALUES(40,70,'40:70','ئۇلار كىتابنى (يەنى قۇرئاننى) ئىنكار قىلدى، پەيغەمبەرلىرىمىزگە بىز نازىل قىلغان مۆجىزىلەرنى ئىنكار قىلدى. ئۇلار ئۇزاققا قالماي (ئىنكار قىلغانلىقلىرىنىڭ ئاقىۋىتىنى) بىلىدۇ');
INSERT INTO "saleh" VALUES(40,71,'40:71',' ئۇ چاغدا (يەنى دوزاخقا كىرگەندە) ئۇلارنىڭ بويۇنلىرىدا تاقاقلار ۋە زەنجىرلەر بولىدۇ، ئۇلار قايناقسۇغا سۆرەپ كىرىلىدۇ، ئاندىن ئوتتا كۆيدۈرۈلىدۇ[71ـ72].');
INSERT INTO "saleh" VALUES(40,72,'40:72',' ئۇ چاغدا (يەنى دوزاخقا كىرگەندە) ئۇلارنىڭ بويۇنلىرىدا تاقاقلار ۋە زەنجىرلەر بولىدۇ، ئۇلار قايناقسۇغا سۆرەپ كىرىلىدۇ، ئاندىن ئوتتا كۆيدۈرۈلىدۇ[71ـ72].');
INSERT INTO "saleh" VALUES(40,73,'40:73',' ئاندىن ئۇلارغا: «ئاللاھنى قويۇپ شېرىك كەلتۈرگەن مەبۇدلىرىڭلار قەيەردە؟» دېيىلىدۇ، ئۇلار: «ئۇ مەبۇدلار ئۆزلىرىنى بىزدىن چەتكە ئالدى، بەلكى بىز ئىلگىرى ھېچنەرسىگە ئىبادەت قىلمايتتۇق» دەيدۇ. ئاللاھ كاپىرلارنى مۇشۇنداق گۇمراھ قىلىدۇ[73ـ74]. ');
INSERT INTO "saleh" VALUES(40,74,'40:74',' ئاندىن ئۇلارغا: «ئاللاھنى قويۇپ شېرىك كەلتۈرگەن مەبۇدلىرىڭلار قەيەردە؟» دېيىلىدۇ، ئۇلار: «ئۇ مەبۇدلار ئۆزلىرىنى بىزدىن چەتكە ئالدى، بەلكى بىز ئىلگىرى ھېچنەرسىگە ئىبادەت قىلمايتتۇق» دەيدۇ. ئاللاھ كاپىرلارنى مۇشۇنداق گۇمراھ قىلىدۇ[73ـ74]. ');
INSERT INTO "saleh" VALUES(40,75,'40:75',' بۇ (ئازاب) سىلەرنىڭ زېمىندا ھەقسىز رەۋىشتە (گۇناھ قىلىپ پۇل ـ مالنى ھارام يوللارغا سەرپ قىلغانلىقىڭلار ئۈچۈن) خۇشال بولغانلىقىڭلار ۋە ھاكاۋۇر بولۇپ كەتكەنلىكىڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(40,76,'40:76',' سىلەر دوزاخنىڭ دەرۋازىلىرىدىن كىرىپ ئۇ يەردە مەڭگۈ قېلىڭلار، مۇتەكەببۇرلارنىڭ جايى نېمىدېگەن يامان! ');
INSERT INTO "saleh" VALUES(40,77,'40:77','(ئى مۇھەممەد! قەۋمىڭنىڭ سېنى ئىنكار قىلغانلىقىغا) سەۋر قىلغىن، ئاللاھنىڭ (ئۇلارنى ئازابلاش توغرىسىدىكى) ۋەدىسى ھەقىقەتەن ھەقتۇر، ساڭا بىز ئۇلارغا ۋەدە قىلغان ئازابنىڭ بىر قىسمىنى كۆرسەتسەك (بۇ ئاللاھنىڭ ۋەدىسىگە ۋاپا قىلغانلىقىدۇر) ياكى سېنى (ئۇنى كۆرسىتىشتىن بۇرۇن) ۋاپات قىلدۇرساق، ئۇلار بىزنىڭ دەرگاھىمىزغا قايتۇرۇلىدۇ، (قىيامەتتە ئۇلار ئازابقا دۇچار بولىدۇ).');
INSERT INTO "saleh" VALUES(40,78,'40:78','سەندىن ئىلگىرى بىز ھەقىقەتەن نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق، ئۇلارنىڭ ئارىسىدىن ساڭا بايان قىلىپ بەرگەنلىرىمىزمۇ، بايان قىلىپ بەرمىگەنلىرىمىزمۇ بار، ھېچقانداق پەيغەمبەر ئاللاھنىڭ ئىزنىسىز بىرەر مۆجىزە كەلتۈرەلمەيدۇ، ئاللاھنىڭ ئەمرى (يەنى قىيامەت) كەلگەن چاغدا (كىشىلەرنىڭ ئارىسىدا) ئادالەت بىلەن ھۆكۈم چىقىرىلىدۇ، بۇ چاغدا ھەقكە قارشى تۇرغۇچىلار زىيان تارتىدۇ.');
INSERT INTO "saleh" VALUES(40,79,'40:79',' مىنىشىڭلار ۋە گۆشلىرىنى يېيىشىڭلار ئۈچۈن ئاللاھ سىلەرگە ھايۋانلارنى ياراتتى.');
INSERT INTO "saleh" VALUES(40,80,'40:80','ئۇلار سىلەرگە نۇرغۇن پايدا كەلتۈرىدۇ، ئۇلارغا مىنىپ دىلىڭلارغا پۈككەن ھاجىتىڭلاردىن چىقىسىلەر، (قۇرۇقلۇقتا يۈك ـ تاقلىرىڭلارنى) ئۇلارغا ئارتىسىلەر، (دېڭىزدا) كېمىلەرگە قاچىلايسىلەر.');
INSERT INTO "saleh" VALUES(40,81,'40:81','ئاللاھ سىلەرگە (قۇدرىتىنىڭ) ئالامەتلىرىنى كۆرسىتىدۇ، سىلەر ئاللاھنىڭ (قۇدرىتىنىڭ) قايسى ئالامەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(40,82,'40:82',' ئۇلار زېمىندا سەير قىلىپ ئۇلاردىن ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۈزەتمىدىمۇ؟ بۇلاردىن ئۇلارنىڭ سانى كۆپ ئىدى، زېمىندا كۈچ ـ قۇۋۋەت ۋە يادىكارلىقلار (يەنى ئۆي ـ ئىمارەت) جەھەتتە بۇلاردىن ئۈستۈن ئىدى. ئۇلارنىڭ قولغا كەلتۈرگەن نەرسىلىرى ئۇلارغا ئەسقاتمىدى.');
INSERT INTO "saleh" VALUES(40,83,'40:83','ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەر بىلەن كەلگەندە، ئۆزلىرىدە بار ئىلىم بىلەن پەخىرلىنىپ (پەيغەمبەرلەرنى ئىنكار قىلدى ۋە مەسخىرە قىلدى)، ئۇلارغا ئۆزلىرى مەسخىرە قىلغان (ۋە كەلسە دەپ ئالدىرىغان) ئازاب نازىل بولدى.');
INSERT INTO "saleh" VALUES(40,84,'40:84',' ئۇلار بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا: «بىز بىر ئاللاھقا ئىمان ئېيتتۇق، بىز بۇرۇن (ئاللاھقا) شېرىك قىلغانلىرىمىزنى ئىنكار قىلدۇق» دېدى.');
INSERT INTO "saleh" VALUES(40,85,'40:85','بىزنىڭ ئازابىمىزنى كۆرگەن چاغدا ئېيتقان ئىماننىڭ ئۇلارغا پايدىسى بولمىدى، بۇ (يەنى ئازابنى كۆرگەندە ئېيتقان ئىماننىڭ پايدىلىق بولماسلىقى) ئاللاھنىڭ بەندىلىرى ئارىسىدا تۇتقان يولىدۇر، بۇ چاغدا كاپىرلار زىيان تارتقۇچى بولدى.');
INSERT INTO "saleh" VALUES(41,1,'41:1','ھامىم.');
INSERT INTO "saleh" VALUES(41,2,'41:2','(بۇ) ناھايىتى شەپقەتلىك، مېھرىبان ئاللاھ تەرىپىدىن نازىل قىلىنغان (ۋەھىيدۇر). ');
INSERT INTO "saleh" VALUES(41,3,'41:3',' ئايەتلىرى ئوچۇق بايان قىلىنغان كىتابتۇر، بىلىدىغان قەۋم ئۈچۈن نازىل بولغان ئەرەبچە قۇرئاندۇر.');
INSERT INTO "saleh" VALUES(41,4,'41:4','(مۆمىنلەرگە جەننەت بىلەن) خۇشخەۋەر بەرگۈچىدۇر، (كاپىرلارنى دوزاخ بىلەن) ئاگاھلاندۇرغۇچىدۇر. ئۇلار (يەنى مۇشرىكلار) نىڭ تولىسى (ئۇنىڭ ئايەتلىرىنى پىكىر قىلىشتىن) يۈز ئۆرۈدى. ئۇلار (ئۇنىڭغا) قۇلاق سالمايدۇ.');
INSERT INTO "saleh" VALUES(41,5,'41:5',' ئۇلار (پەيغەمبەرگە): «بىزنىڭ دىللىرىمىز سەن بىزنى دەۋەت قىلغان نەرسىلەردىن پەردىلەنگەن، قۇلاقلىرىمىز ئېغىر، سەن بىلەن بىزنىڭ ئارىمىزدا پەردە (يەنى دىنىي جەھەتتە ئوخشىماسلىق) بار، سەن ئۆز ئىشىڭنى قىل، بىزمۇ ھەقىقەتەن ئۆز ئىشىمىزنى قىلايلى (يەنى سەنمۇ ئۆز دىنىڭ بىلەن بول، بىزمۇ ئۆز دىنىمىز بىلەن بولايلى)» دەيدۇ.');
INSERT INTO "saleh" VALUES(41,6,'41:6','سەن (ئۇ مۇشرىكلارغا): «مەن سىلەرگە ئوخشاش ئىنسانمەن، ماڭا، ئىلاھىڭلار بىر ئىلاھتۇر، دەپ ۋەھىي قىلىنىدۇ، ئۇنىڭغا (ئىمان ۋە تائەت ـ ئىبادەت بىلەن) يۈزلىنىڭلار ۋە ئۇنىڭدىن مەغپىرەت تىلەڭلار!» دېگىن، مۇشرىكلارغا ۋاي!');
INSERT INTO "saleh" VALUES(41,7,'41:7','ئۇلار زاكات بەرمەيدۇ، ئاخىرەتكە ئىشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(41,8,'41:8','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا ئەلۋەتتە ئۈزلۈكسىز ساۋاب بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(41,9,'41:9',' (سەن ئۇلارغا) «سىلەر زېمىننى ئىككى كۈندە ياراتقان ئاللاھنى ئىنكار قىلامسىلەر ۋە ئۇنىڭغا شېرىكلەر كەلتۈرەمسىلەر؟ ئۇ ئالەملەرنىڭ پەرۋەردىگارىدۇر» دېگىن.');
INSERT INTO "saleh" VALUES(41,10,'41:10',' ئۇ زېمىننىڭ ئۈستىدە نۇرغۇن تاغلارنى ياراتتى، زېمىننى بەرىكەتلىك قىلدى، ئەھلى زېمىننىڭ رىزقىنى بەلگىلىدى، (زېمىننى يارىتىش بىلەن ئاھالىسىنىڭ رىزقىنى بەلگىلەشنى كەم ـ زىيادىسىز) باراۋەر تۆت كۈندە (تاماملىدى)، (زېمىننىڭ ۋە ئۇنىڭدىكى نەرسىلەرنىڭ يارىتىلىش مۇددىتىنى) سورىغۇچىلار ئۈچۈن (بۇ جاۋابتۇر).');
INSERT INTO "saleh" VALUES(41,11,'41:11',' ئاندىن ئۇ ئاسماننى (يارىتىشقا) يۈزلەندى، ھالبۇكى، ئۇ تۇمان ھالىتىدە ئىدى، ئاسمان بىلەن زېمىنغا: «ئىختىيارىي ياكى ئىختىيارسىز ھالدا كېلىڭلار (يەنى ئەمرىمنى قوبۇل قىلىڭلار)» دېدى. ئىككىلىسى «ئىختىيارىي كەلدۇق» دېدى.');
INSERT INTO "saleh" VALUES(41,12,'41:12','ئاللاھ يەتتە ئاسماننى ئىككى كۈندە ياراتتى. ھەر ئاسماننىڭ ئىشىنى ئۆزىگە بىلدۈردى (يەنى ھەر ئاسماننىڭ ئېھتىياجلىق ئىشلىرىنى ئورۇنلاشتۇردى). ئەڭ تۆۋەنكى ئاسماننى يۇلتۇزلار بىلەن زىننەتلىدۇق ۋە قوغدىدۇق، بۇ ئەنە شۇ غالىب، ھەممىنى بىلگۈچى ئاللاھنىڭ تەقدىرىدۇر.');
INSERT INTO "saleh" VALUES(41,13,'41:13','ئەگەر ئۇلار (بۇ بايانلاردىن كېيىن ئىماندىن) يۈز ئۆرۈسە (سەن ئۇلارغا): «سىلەرنى ئاد ۋە سەمۇد قەۋمىلىرىنىڭ بېشىغا كەلگەن ئازابقا ئوخشاش ئازابتىن ئاگاھلاندۇرىمەن» دېگىن.');
INSERT INTO "saleh" VALUES(41,14,'41:14','ئۆز ۋاقتىدا ئۇلارغا ئالدى ـ كەينىدىن (يەنى ھەممە تەرەپتىن) پەيغەمبەرلەر كېلىپ: «بىر ئاللاھتىن باشقىغا ئىبادەت قىلماڭلار!» دېسە، ئۇلار: «ئەگەر پەرۋەردىگارىمىز خالىسا، چوقۇم پەرىشتىلەرنى چۈشۈرگەن بولاتتى، بىز سىلەرنىڭ پەيغەمبەرلىكىڭلارنى ھەقىقەتەن ئىنكار قىلىمىز» دېدى.');
INSERT INTO "saleh" VALUES(41,15,'41:15',' ئاد بولسا زېمىندا (ئاللاھنىڭ بەندىلىرى ھۇد ۋە ئۇنىڭ بىلەن ئىمان ئېيتقانلارغا) ھەقسىز رەۋىشتە يوغانچىلىق قىلدى. ئۇلار: «كۈچ ـ قۇۋۋەتتە بىزدىن كىم ئارتۇق؟» دېدى. ئۇلار ئۆزلىرىنى ياراتقان ئاللاھنىڭ كۈچ ـ قۇۋۋەتتە ئۆزلىرىدىن ئۈستۈن ئىكەنلىكىنى بىلمىدىمۇ؟ ئۇلار بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(41,16,'41:16',' ئۇلارغا بۇ دۇنيادىلا خار قىلغۇچى ئازابنى تېتىتىشىمىز ئۈچۈن، شۇم كۈنلەردە قاتتىق سوغۇق شامالنى ئەۋەتتۇق، ئاخىرەتنىڭ ئازابى تېخىمۇ خار قىلغۇچىدۇر، ئۇلار (ئازابنى دەپئى قىلىدىغان) ياردەمگە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(41,17,'41:17',' سەمۇدقا بولسا توغرا يولنى كۆرسەتتۇق، ئۇلار ھىدايەتتىن كورلۇق (يەنى گۇمراھلىق) نى ئارتۇق بىلدى. قىلمىشلىرى تۈپەيلىدىن، ئۇلارنى خار قىلغۇچى چاقماق ئازابى ھالاك قىلدى.');
INSERT INTO "saleh" VALUES(41,18,'41:18','ئىمان ئېيتقانلارنى ۋە تەقۋادارلارنى (يەنى سالىھ ئەلەيھىسسالام بىلەن ئۇنىڭغا ئەگەشكەنلەرنى ئازابتىن) قۇتقۇزدۇق.');
INSERT INTO "saleh" VALUES(41,19,'41:19',' ئۇ كۈندە ئاللاھنىڭ دۈشمەنلىرى دوزاخقا توپلىنىدۇ، ئالدى ـ ئارقىسى يىغىلىپ بولغىچە توختىتىپ قويۇلىدۇ.');
INSERT INTO "saleh" VALUES(41,20,'41:20',' ئۇلار دوزاخقا كەلگەندە، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى ۋە تېرىلىرى ئۇلارنىڭ قىلمىشلىرى ھەققىدە گۇۋاھلىق بېرىدۇ.');
INSERT INTO "saleh" VALUES(41,21,'41:21',' ئۇلار ئۆزلىرىنىڭ تېرىلىرىگە: «نېمىشقا بىزنىڭ زىيىنىمىزغا گۇۋاھلىق بەردىڭلار؟» دەيدۇ، تېرىلىرى: «ھەر نەرسىنى سۆزلەتكەن ئاللاھ بىزنى سۆزلەتتى، ئۇ دەسلەپتە سىلەرنى ياراتتى. ئۇنىڭ دەرگاھىغا قايتۇرۇلىسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(41,22,'41:22',' سىلەر (دۇنيادىكى چېغىڭلاردا) قۇلىقىڭلارنىڭ، كۆزۈڭلارنىڭ، تېرىلىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىشىدىن ساقلانمىغان ئىدىڭلار (يەنى يامان ئىشلارنى قىلغان چېغىڭلاردا، رەسۋا بولۇشتىن قورقۇپ، كىشىلەردىن يوشۇرغان ئىدىڭلار، ۋەھالەنكى، ئەزالىرىڭلارنىڭ سىلەرنىڭ زىيىنىڭلارغا گۇۋاھلىق بېرىدىغانلىقىنى گۇمان قىلمىغان ئىدىڭلار)، لېكىن سىلەر قىلمىشىڭلاردىن نۇرغۇن ئىشلارنى «ئاللاھ بىلمەيدۇ» دەپ گۇمان قىلدىڭلار.');
INSERT INTO "saleh" VALUES(41,23,'41:23',' پەرۋەردىگارىڭلارغا قىلغان ئەنە شۇ (قەبىھ) گۇمانىڭلار سىلەرنى ھالاك قىلدى. شۇنىڭ بىلەن زىيان تارتقۇچىلاردىن بولدۇڭلار.');
INSERT INTO "saleh" VALUES(41,24,'41:24',' ئەگەر ئۇلار (ئازابقا) چىدىسا، (چىدامچانلىقنىڭ پايدىسى يوق) دوزاخ ئۇلارنىڭ جايىدۇر، ئەگەر ئۇلار (ئاللاھتىن) رازىلىق تىلىسە، ھەرگىز رازىلىققا ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(41,25,'41:25',' ئۇلارغا بىز (شەيتانلاردىن) دوستلارنى مۇسەللەت قىلدۇق، ئۇلار (يەنى شەيتانلار) ئۇلارغا ئالدى ـ كەينىدىكى نەرسىلەرنى (يەنى ئۇلارنىڭ ھازىرقى ۋە كەلگۈسىدىكى قەبىھ ئەمەللىرىنى) چىرايلىق كۆرسەتتى، ئۇلارغا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان (گۇناھكار، شەقى) ئۈممەتلەر قاتارىدا ئازاب سۆزى تېگىشلىك بولدى، چۈنكى ئۇلار زىيان تارتقۇچىلار ئىدى.');
INSERT INTO "saleh" VALUES(41,26,'41:26','كاپىرلار: «بۇ قۇرئانغا قۇلاق سالماڭلار، ئۇنى ئېلىشتۇرۇۋېتىڭلار، سىلەر غەلىبە قىلىشىڭلار مۇمكىن» دەيدۇ.');
INSERT INTO "saleh" VALUES(41,27,'41:27',' بىز كاپىرلارغا چوقۇم قاتتىق ئازابنى تېتىتىمىز، بىز ئۇلارنىڭ يامان قىلمىشلىرىغا ئەڭ قاتتىق جازا بېرىمىز.');
INSERT INTO "saleh" VALUES(41,28,'41:28',' ئاللاھنىڭ دۈشمەنلىرىگە بېرىلىدىغان جازا ئەنە شۇ دوزاختۇر، ئۇلارنىڭ مەڭگۈلۈك تۇرىدىغان جايى دوزاختۇر، بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلىقلىرى ئۈچۈن (ئۇلارغا) شۇنداق جازا بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(41,29,'41:29',' كاپىرلار: «پەرۋەردىگارىمىز! ئىنسانلاردىن ۋە جىنلاردىن بىزنى ئازدۇرغانلارنى بىزگە كۆرسەتكىن، (ئۇلارنىڭ) ئەڭ خارلاردىن بولۇشى ئۈچۈن ئۇلارنى ئاياغلىرىمىزنىڭ ئاستىدا چەيلەيمىز» دېدى.');
INSERT INTO "saleh" VALUES(41,30,'41:30',' شۈبھىسىزكى، «پەرۋەردىگارىمىز ئاللاھتۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (ئاللاھ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ[30ـ32].');
INSERT INTO "saleh" VALUES(41,31,'41:31',' شۈبھىسىزكى، «پەرۋەردىگارىمىز ئاللاھتۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (ئاللاھ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ[30ـ32].');
INSERT INTO "saleh" VALUES(41,32,'41:32',' شۈبھىسىزكى، «پەرۋەردىگارىمىز ئاللاھتۇر» دېگەنلەر، ئاندىن توغرا يولدا بولغانلارغا پەرىشتىلەر چۈشۈپ: «قورقماڭلار، غەم قىلماڭلار، سىلەرگە ۋەدە قىلىنغان جەننەت ئۈچۈن خۇشال بولۇڭلار، بىز دۇنيادا، ئاخىرەتتە سىلەرنىڭ دوستلىرىڭلارمىز، جەننەتتە سىلەر ئۈچۈن كۆڭلۈڭلار تارتقان نەرسىلەرنىڭ ھەممىسى ۋە تىلىگەن نەرسەڭلارنىڭ ھەممىسى بار، (ئۇلار) ناھايىتى مەغپىرەت قىلغۇچى، ناھايىتى مېھرىبان (ئاللاھ) تەرىپىدىن بېرىلگەن زىياپەتتۇر» دەيدۇ[30ـ32].');
INSERT INTO "saleh" VALUES(41,33,'41:33','ئاللاھقا (يەنى ئاللاھنىڭ تەۋھىدىگە ۋە تائىتىگە) دەۋەت قىلغان، ياخشى ئەمەللەرنى قىلغان ۋە «مەن ھەقىقەتەن مۇسۇلمانلاردىنمەن» دېگەن كىشىدىنمۇ ياخشى سۆزلۈك ئادەم بارمۇ؟');
INSERT INTO "saleh" VALUES(41,34,'41:34',' ياخشى ئىش بىلەن يامان ئىش باراۋەر بولمايدۇ، ياخشى خىسلەت ئارقىلىق (يامان خىسلەتكە) تاقابىل تۇرغىن، (شۇنداق قىلساڭ) سەن بىلەن ئۆزىنىڭ ئارىسىدا ئاداۋەت بار ئادەم گويا سىرداش دوستۇڭدەك بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(41,35,'41:35',' بۇ خىسلەتكە پەقەت سەۋرچان ئادەملەرلا ئېرىشەلەيدۇ، بۇ خىسلەتكە پەقەت بۈيۈك نېسىۋە ئىگىسىلا ئېرىشەلەيدۇ.');
INSERT INTO "saleh" VALUES(41,36,'41:36',' ئەگەر شەيتان سېنى ۋەسۋەسە قىلىپ (يۇقىرىقى خىسلەتنى ئۆزلەشتۈرمەسلىككە كۈشكۈرتسە) ئاللاھقا سىغىنىپ (شەيتاننىڭ شەررىدىن) پاناھ تىلىگىن، ئاللاھ ھەقىقەتەن (بەندىلىرىنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر، (ئىشلىرىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(41,37,'41:37',' كېچە بىلەن كۈندۈز، كۈن بىلەن ئاي ئاللاھنىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇر، قۇياشقا سەجدە قىلماڭلار، ئايغىمۇ سەجدە قىلماڭلار، ئۇلارنى ياراتقان ئاللاھقا سەجدە قىلىڭلار، ئەگەر پەقەت ئاللاھقىلا ئىبادەت قىلىدىغان بولساڭلار.');
INSERT INTO "saleh" VALUES(41,38,'41:38','ئەگەر كۇففارلار چوڭچىلىق قىلىپ (ئاللاھقا سەجدە قىلىشتىن) باش تارتسا، پەرۋەردىگارىڭنىڭ دەرگاھىدىكىلەر (يەنى پەرىشتىلەر) كېچە ـ كۈندۈز ئاللاھقا تەسبىھ ئېيتىپ تۇرىدۇ، ئۇلار مالال بولۇپ قالمايدۇ.');
INSERT INTO "saleh" VALUES(41,39,'41:39',' (بۇمۇ) ئاللاھنىڭ (بىرلىكىنى ۋە قۇدرىتىنى كۆرسىتىدىغان) ئالامەتلىرىدىندۇركى، سەن زېمىننى قاقاس كۆرىسەن، ئۇنىڭغا يامغۇر ياغدۇرغان ۋاقتىمىزدا، زېمىن كۆتۈرۈلىدۇ ۋە كۆپۈشىدۇ، شۈبھىسىزكى، زېمىننى تىرىلدۈرگەن زات ئۆلۈكلەرنى ئەلۋەتتە تىرىلدۈرگۈچىدۇر، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(41,40,'41:40','شۈبھىسىزكى، بىزنىڭ ئايەتلىرىمىزنى بۇرمىلاپ چۈشەندۈرىدىغانلار بىزگە مەخپىي قالمايدۇ، قىيامەت كۈنى دوزاخقا تاشلىنىدىغان ئادەم ياخشىمۇ ياكى قىيامەت كۈنى (ئاللاھنىڭ ئازابىدىن) ئەمىن بولغان ھالدا كېلىدىغان ئادەم ياخشىمۇ؟ خالىغىنىڭلارنى قىلىڭلار، شۈبھىسىزكى، ئاللاھ قىلمىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(41,41,'41:41','شۈبھىسىزكى، قۇرئان كەلگەندە ئۇنى ئىنكار قىلغانلار (قاتتىق جازاغا ئۇچرايدۇ)، شۈبھىسىزكى، قۇرئان غالىب كىتابتۇر.');
INSERT INTO "saleh" VALUES(41,42,'41:42','ئۇنىڭغا ئالدىدىنمۇ، ئارقىسىدىنمۇ (يەنى ھېچقايسى تەرىپىدىن) باتىل يۈزلەنمەيدۇ، ئۇ ھېكمەت بىلەن ئىش قىلغۇچى، مەدھىيەگە لايىق ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(41,43,'41:43',' ساڭا (مۇشرىكلار تەرىپىدىن) ئېيتىلىدىغان سۆزلەر پەقەت ئىلگىرىكى پەيغەمبەرلەرگە (قەۋملىرى تەرىپىدىن) ئېيتىلغان سۆزلەردۇر. شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ مەغپىرەت ئىگىسىدۇر ۋە قاتتىق ئازاب ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(41,44,'41:44',' مۇبادا بىز ئۇنى ئەجەم تىلىدىكى قۇرئان قىلىپ نازىل قىلغان بولساق، ئۇلار ئەلۋەتتە: «نېمىشقا ئۇنىڭ ئايەتلىرى (بىز چۈشىنىدىغان تىلدا) روشەن بايان قىلىنمىدى؟ پەيغەمبەر ئەرەب، كىتاب ئەجەمچىمۇ؟» دەيتتى. ئۇ ئىمان كەلتۈرگەنلەرگە ھىدايەتتۇر ۋە (دىللاردىكى شەك ـ شۈبھىگە) شىپادۇر، ئىمان ئېيتمايدىغانلارنىڭ قۇلاقلىرى (قۇرئاننى ئاڭلاشتىن) ئېغىردۇر (يەنى قۇرئاننىڭ دەۋىتىگە قۇلاق سالمىغانلارنىڭ ۋە ئۇنىڭدىن يۈز ئۆرۈگەنلەرنىڭ گويا قۇلاقلىرى ئېغىردۇر)، قۇرئان ئۇلارنىڭ (دىللىرىغا) كورلۇقتۇر (يەنى ئۇلار ھىدايەت نۇرىنى كۆرمەيدۇ)، ئەنە شۇلار يىراقتىن نىدا قىلىنغۇچىلاردۇر (يەنى ئۇلار ھەقنى قوبۇل قىلماسلىقتا ئىمانغا يىراقتىن چاقىرىلغان، مۇساپىنىڭ يىراقلىقىدىن چاقىرىقنى ئاڭلىمىغان كىشىلەرگە ئوخشايدۇ).');
INSERT INTO "saleh" VALUES(41,45,'41:45',' بىز ھەقىقەتەن مۇساغا كىتاب (يەنى تەۋراتنى) ئاتا قىلدۇق، ئۇ ھەقتە ئىختىلاپ بولدى. ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى قىيامەتكىچە كېچىكتۈرۈش) ۋەدىسى بولمىسا ئىدى، ئەلۋەتتە ئۇلارنىڭ ئارىسىدا ھۆكۈم چىقىرىلاتتى، ئۇلار ھەقىقەتەن قۇرئاندىن زىيادە شەكتىدۇر.');
INSERT INTO "saleh" VALUES(41,46,'41:46',' كىمكى ياخشى ئىش قىلىدىكەن، پايدىسى ئۆزىگىدۇر، كىمكى يامان ئىش قىلىدىكەن زىيىنىمۇ ئۆزىگىدۇر. پەرۋەردىگارىڭ بەندىلەرگە زۇلۇم قىلغۇچى ئەمەستۇر.');
INSERT INTO "saleh" VALUES(41,47,'41:47',' قىيامەتنىڭ ۋاقتىنى بىلىش پەقەت ئاللاھقا خاستۇر (يەنى قىيامەتنىڭ قاچان بولىدىغانلىقىنى ئاللاھتىن باشقا كىشى بىلمەيدۇ). مېۋىلەرنىڭ بوغۇنلىرىدىن چىقىشى، ھەر قانداق چىشىنىڭ ھامىلدار بولۇشى ۋە تۇغۇشى ئاللاھنىڭ ئىلمىنىڭ سىرتىدا ئەمەستۇر، ئۇ كۈندە ئاللاھ ئۇلارغا: «مېنىڭ شېرىكلىرىم (يەنى سىلەر ماڭا شېرىك كەلتۈرگەن نەرسىلەر) قەيەردە؟» دەپ نىدا قىلىدۇ، ئۇلار: «ساڭا مەلۇم قىلىمىزكى، ئارىمىزدا ھېچ كىشى (سېنىڭ شېرىكىڭ بار دەپ) گۇۋاھلىق بېرەلمەيدۇ» دەيدۇ.');
INSERT INTO "saleh" VALUES(41,48,'41:48',' ئۇلار ئىلگىرى (يەنى دۇنيادىكى چاغدا) چوقۇنغان مەبۇدلىرى ئۇلاردىن ئۆزلىرىنى چەتكە ئالىدۇ، ئۇلار (ئاللاھنىڭ ئازابىدىن) قاچىدىغان جاي يوق ئىكەنلىكىنى ئېنىق بىلىدۇ.');
INSERT INTO "saleh" VALUES(41,49,'41:49',' ئىنسان (ئۆزىگە) ياخشىلىق تىلەشتىن زېرىكمەيدۇ، ئەگەر ئۇنىڭغا بىرەر يامانلىق يېتىپ قالسا (ئاللاھنىڭ رەھمىتىدىن) تولىمۇ ئۈمىدسىزلىنىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(41,50,'41:50',' ئەگەر بىز ئۇنىڭغا كۈلپەتتىن كېيىن رەھمىتىمىزنى تېتىتساق، ئۇ چوقۇم: «بۇ ئۆزۈمنىڭ تىرىشچانلىقىمدىن كەلگەن، قىيامەتنىڭ بولىدىغانلىقىغا ئىشەنمەيمەن، مۇبادا (قىيامەت بولۇپ) مەن پەرۋەردىگارىمغا قايتۇرۇلغاندىمۇ ئۇنىڭ دەرگاھىدا چوقۇم جەننەتكە ئېرىشىمەن» دەيدۇ. ئەلۋەتتە كاپىرلارغا قىلمىشلىرىنى خەۋەر قىلىمىز ۋە ئۇلارغا قاتتىق ئازابنى تېتىتىمىز.');
INSERT INTO "saleh" VALUES(41,51,'41:51',' ئىنسانغا نېمەت ئاتا قىلساق (پەرۋەردىگارىغا شۈكۈر قىلىشتىن) يۈز ئۆرۈيدۇ، (ئاللاھنىڭ ئەمرىگە بويسۇنۇشتىن باش تارتىپ) ھاكاۋۇرلىشىپ كېتىدۇ، ئەگەر ئۇنىڭغا يامانلىق يەتسە (ئاللاھقا يۈزلىنىپ) ئۈزلۈكسىز دۇئا قىلىدۇ.');
INSERT INTO "saleh" VALUES(41,52,'41:52',' (ئى مۇھەممەد! ئېيتقىنكى) «ئېيتىپ بېقىڭلارچۇ (قۇرئان مەن ئېيتقاندەك) ئاللاھ تەرىپىدىن نازىل بولغان بولسا، سىلەر (ئويلىماستىنلا ئۇنى ئىنكار قىلساڭلار، ھالىڭلار قانداق بولىدۇ). (ھەقتىن) يىراق ئىختىلاپتا بولغان كىشىدىنمۇ گۇمراھ ئادەم بارمۇ؟ (يەنى سىلەردىنمۇ گۇمراھ ئادەم يوق)».');
INSERT INTO "saleh" VALUES(41,53,'41:53',' ئۇلارغا بىز تاكى ئۇلارغا قۇرئاننىڭ ھەق ئىكەنلىكى ئېنىق بولغىچە، (ئاسمان ـ زېمىن) ئەتراپىدىكى ۋە ئۆزلىرىدىكى (قۇدرىتىمىزگە دالالەت قىلىدىغان) ئالامەتلەرنى كۆرسىتىپ تۇرىمىز، پەرۋەردىگارىڭنىڭ ھەممە نەرسىنى كۆرۈپ تۇرىدىغانلىقى (سېنىڭ راستلىقىڭنىڭ دەلىلى بولۇشقا) ئۇلارغا كۇپايە ئەمەسمۇ؟');
INSERT INTO "saleh" VALUES(41,54,'41:54',' بىلىڭلاركى، ئۇلار ھەقىقەتەن پەرۋەردىگارىغا مۇلاقات بولۇشتىن شەكلىنىدۇ، بىلىڭلاركى، ئاللاھ ھەممە نەرسىنى ھەقىقەتەن تولۇق بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,1,'42:1','ھامىم، ');
INSERT INTO "saleh" VALUES(42,2,'42:2','ئەين، سىين، قاف.');
INSERT INTO "saleh" VALUES(42,3,'42:3','غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھ ساڭا ۋە سەندىن ئىلگىرىكى (پەيغەمبەر) لەرگە مۇشۇنداق ۋەھىي قىلىدۇ.');
INSERT INTO "saleh" VALUES(42,4,'42:4','ئاسمانلاردىكى شەيئىلەر ۋە زېمىندىكى شەيئىلەر ئاللاھنىڭدۇر (يەنى ئاللاھنىڭ مۈلكىدۇر، ئاللاھنىڭ تەسەررۇپى ئاستىدىدۇر)، ئاللاھ ئۈستۈندۇر، كاتتىدۇر.');
INSERT INTO "saleh" VALUES(42,5,'42:5','(ئاللاھنىڭ كاتتىلىقىدىن) ئاسمانلار يۇقىرى تەرىپىدىن يېرىلىپ كېتىشكە تاس قالىدۇ، پەرىشتىلەر پەرۋەردىگارىغا تەسبىھ ئېيتىدۇ، ھەمدى ئېيتىدۇ، يەر يۈزىدىكىلەرگە مەغپىرەت تىلەيدۇ. بىلىڭلاركى، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(42,6,'42:6',' ئاللاھنى قويۇپ (باشقىلارنى) ئىگە قىلىۋالغانلارنىڭ (ئەمەللىرىنى) ئاللاھ كۈزىتىپ تۇرغۇچىدۇر، سەن ئۇلارغا ھامىي ئەمەسسەن. ');
INSERT INTO "saleh" VALUES(42,7,'42:7','مەككە ئاھالىسىنى ۋە ئۇنىڭ ئەتراپىدىكىلەرنى ئاگاھلاندۇرۇشۇڭ ئۈچۈن، (كىشىلەرنى) قىيامەت كۈنىدىن ئاگاھلاندۇرۇشۇڭ ئۈچۈن، ساڭا بىز مۇشۇنداق ئەرەبچە قۇرئاننى نازىل قىلدۇق. ئۇ كۈندە ھېچ شەك يوقتۇر، (ئۇ كۈندە) بىر پىرقە (كىشىلەر) جەننەتتە ۋە بىر پىرقە كىشىلەر جەھەننەمدە بولىدۇ.');
INSERT INTO "saleh" VALUES(42,8,'42:8',' ئەگەر ئاللاھ خالىسا، ئۇلارنى (يەنى پۈتۈن ئىنسانلارنى) ئەلۋەتتە بىر ئۈممەت (يەنى بىر دىندا) قىلاتتى، لېكىن ئاللاھ خالىغان ئادەمنى رەھمىتىگە داخىل قىلىدۇ، زالىملارغا ھېچقانداق ئىگە، ھېچقانداق مەدەتكار بولمايدۇ.');
INSERT INTO "saleh" VALUES(42,9,'42:9',' بەلكى ئۇلار ئاللاھتىن باشقىلارنى ئىگە قىلىۋالدى، پەقەت ئاللاھ ئىگىدۇر، ئاللاھ ئۆلۈكلەرنى تىرىلدۈرىدۇ، ئاللاھ ھەر نەرسىگە قادىر.');
INSERT INTO "saleh" VALUES(42,10,'42:10',' سىلەر (دىن ئۈستىدە ۋە ئۇنىڭدىن باشقا) ھەر قانداق نەرسە ئۈستىدە ئىختىلاپ قىلىشماڭلار، ئۇنىڭغا ئاللاھ ھۆكۈم چىقىرىدۇ. (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) ئەنە شۇ ئاللاھ مېنىڭ پەرۋەردىگارىمدۇر، ئۇنىڭغا تەۋەككۈل قىلدىم، (ھەممە ئىشتا) ئاللاھقا مۇراجىئەت قىلىمەن.');
INSERT INTO "saleh" VALUES(42,11,'42:11','ئاللاھ ئاسمانلارنى ۋە زېمىننى ياراتقۇچىدۇر، ئاللاھ سىلەرگە ئۆز جىنسىڭلاردىن جۈپتىلەرنى (يەنى ئاياللارنى) ياراتتى. ھايۋانلارغىمۇ ئۆز تىپىدىن جۈپتىلەرنى ياراتتى. بۇ ئارقىلىق ئاللاھ سىلەرنى كۆپەيتىدۇ، ھېچ شەيئى ئاللاھقا ئوخشاش ئەمەستۇر، ئاللاھ ھەممىنى ئاڭلاپ تۇرغۇچىدۇر، ھەممىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,12,'42:12',' ئاسمانلارنىڭ ۋە زېمىننىڭ (خەزىنىلىرىنىڭ) ئاچقۇچلىرى ئاللاھنىڭ ئىلكىدىدۇر، ئاللاھ خالىغان كىشىنىڭ رىزقىنى (سىناش يۈزىسىدىن) كەڭ قىلىدۇ، خالىغان كىشىنىڭ رىزقىنى (بالاغا مۇپتىلا قىلىپ سىناش يۈزىسىدىن) تار قىلىدۇ، ئاللاھ ھەقىقەتەن ھەممە نەرسىنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,13,'42:13','ئاللاھ سىلەرگە دىندىن نۇھقا تەۋسىيە قىلغان نەرسىنى، ساڭا (يەنى مۇھەممەد ئەلەيھىسسالامغا) بىز ۋەھىي قىلغان نەرسىنى، ئىبراھىمغا، مۇساغا ۋە ئىيساغا بىز تەۋسىيە قىلغان نەرسىنى بايان قىلدى. سىلەر دىننى بەرپا قىلىڭلار، دىندا تەپرىقىچىلىك قىلماڭلار، مۇشرىكلارغا سەن ئۇلارنى دەۋەت قىلغان نەرسە (يەنى تەۋھىد) ئېغىر كەلدى، ئاللاھ تەۋھىدكە خالىغان ئادەمنى تاللايدۇ، ئاللاھنىڭ (تائىتىگە) قايتىدىغان ئادەمنى تەۋھىدكە باشلايدۇ.');
INSERT INTO "saleh" VALUES(42,14,'42:14',' (يەھۇدىي، ناسارا قاتارلىقلار) ئۇلارغا ئىلىم كەلگەندىن كېيىن (يەنى ئۇلار رەسۇلۇللاھنىڭ ھەق پەيغەمبەرلىكىنى بىلگەندىن كېيىن)، ئاندىن ئۆزئارا ھەسەت قىلىشىپ پىرقىلەرگە بۆلۈندى. ئەگەر پەرۋەردىگارىڭنىڭ (ئۇلارنى جازالاشنى) قىيامەتكىچە تەخىر قىلىشتىن ئىبارەت سۆزى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە ھۆكۈم چىقىرىلىپ بولغان بولاتتى (يەنى ئۇلار تېز ھالاك قىلىنغان بولاتتى). ئۇلاردىن كېيىن كىتابقا ۋارىسلىق قىلغانلار (يەنى رەسۇلۇللاھ بىلەن زامانداش بولغان ئەھلى كىتاب) (كىتاب ھەققىدە) قاتتىق دەرگۇماندىدۇر.');
INSERT INTO "saleh" VALUES(42,15,'42:15',' شۇنىڭ ئۈچۈن (يەنى دىندا ئىختىلاپ قىلىشقانلىقى ئۈچۈن) (ئى مۇھەممەد! بارلىق ئىنسانلارنى توغرا دىن ئاساسىدا ئىتتىپاق بولۇشقا) چاقىرغىن، بۇيرۇلغىنىڭ بويىچە توغرا يولدا چىڭ تۇرغىن، ئۇلار (يەنى مۇشرىكلار) نىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن ۋە ئېيتقىن: «ئاللاھ نازىل قىلغان (بارلىق) كىتابلارغا ئىمان ئېيتتىم، ئاراڭلاردا ئادىل بولۇشقا بۇيرۇلدۇم، ئاللاھ بىزنىڭ پەرۋەردىگارىمىزدۇر، سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، بىزنىڭ ئەمەللىرىمىز ئۆزىمىز ئۈچۈندۇر، سىلەرنىڭ ئەمەللىرىڭلارمۇ ئۆزلىرىڭلار ئۈچۈندۇر، سىلەر بىلەن بىزنىڭ ئارىمىزدا جېدەللىشىشكە (ئورۇن) يوق (چۈنكى ھەقىقەت ئاشكارىدۇر)، ئاللاھ (قىيامەت كۈنى) بىزنى (ھۆكۈم چىقىرىش ئۈچۈن) يىغىدۇ، ئاخىر قايتىدىغان جاي ئاللاھنىڭ دەرگاھىدۇر».');
INSERT INTO "saleh" VALUES(42,16,'42:16',' كىشىلەر ئاللاھنىڭ دىنىنى قوبۇل قىلغاندىن كېيىن، (كىشىلەرنى ئىماندىن توسۇش ئۈچۈن) ئاللاھنىڭ دىنىدا خۇسۇمەت قىلىشقانلارنىڭ دەلىللىرى ئاللاھنىڭ دەرگاھىدا باتىلدۇر، ئۇلار (دۇنيادا) غەزەپكە ئۇچرايدۇ، (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(42,17,'42:17',' ئاللاھ كىتاب (يەنى قۇرئان) نى ھەق بىلەن نازىل قىلدى ۋە ئادالەتنى نازىل قىلدى. سەن نېمە بىلىسەن؟ قىيامەت يېقىن بولۇشى مۇمكىن.');
INSERT INTO "saleh" VALUES(42,18,'42:18',' قىيامەتكە ئىشەنمەيدىغانلار ئۇنىڭ بولۇشىنى ئالدىرىتىدۇ، ئۇنىڭغا ئىشىنىدىغانلار بولسا قىيامەتتىن قورقىدۇ، قىيامەتنى ھەق دەپ بىلىدۇ، بىلىڭلاركى، قىيامەت توغرۇلۇق مۇجادىلە قىلىدىغانلار ئەلۋەتتە چوڭقۇر گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(42,19,'42:19',' ئاللاھ بەندىلىرىگە كۆيۈنگۈچىدۇر، ئۇ خالىغان ئادەمگە (كەڭ) رىزىق بېرىدۇ، ئاللاھ كۈچلۈكتۇر، غالىبتۇر.');
INSERT INTO "saleh" VALUES(42,20,'42:20',' كىمكى (ئۆزىنىڭ ئەمەلى بىلەن) ئاخىرەتنىڭ ساۋابىنى كۆزلىسە، ئۇنىڭ ساۋابىنى زىيادە بېرىمىز، كىمكى (ئەمەلى بىلەن) دۇنيانىڭ مەنپەئىتىنى كۆزلىسە، (ئۇنىڭ تىلىگەن) مەنپەئىتىنىڭ بەزىسىنى بېرىمىز، ئۇنىڭغا ئاخىرەتتە (ساۋابتىن) ھېچ نېسىۋە يوق. ');
INSERT INTO "saleh" VALUES(42,21,'42:21',' ئۇلارنىڭ ئاللاھ رۇخسەت قىلمىغان نەرسىلەرنى دىن قىلىپ بېكەتكەن مەبۇدلىرى بارمۇ؟ (ئاللاھنىڭ ئۇلارغا بېرىلىدىغان ئازابنى قىيامەتكىچە تەخىر قىلىش توغرىسىدىكى) ھۆكمى بولمىسا ئىدى، ئۇلارنىڭ ئارىسىدا ئەلۋەتتە (دۇنيادىلا) ھۆكۈم چىقىرىلغان بولاتتى، (ئۆزلىرىگە) زۇلۇم قىلغۇچىلار (يەنى كاپىرلار) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(42,22,'42:22','كاپىرلارنىڭ (قىيامەت كۈنى دۇنيادا) قىلغان گۇناھلىرىنىڭ جازاسىدىن قورقۇپ تۇرغانلىقىنى كۆرىسەن، (ئۇلار مەيلى قورقسۇن، قورقمىسۇن) جازا ئۇلارغا (چوقۇم) نازىل بولغۇسىدۇر، ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار جەننەتلەرنىڭ باغچىلىرىدا بولىدۇ، ئۇلار پەرۋەردىگارىنىڭ ھۇزۇرىدا خالىغان نەرسىلەردىن بەھرىمەن بولىدۇ، بۇ كاتتا ئىنئامدۇر. ');
INSERT INTO "saleh" VALUES(42,23,'42:23',' ئاللاھ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان بەندىلىرىگە ئەنە شۇ ئىنئام بىلەن خۇشخەۋەر بېرىدۇ. «(دىننى) تەبلىغ قىلغانلىقىمغا سىلەردىن ھەق تەلەپ قىلمايمەن، پەقەت تۇغقانچىلىق ھەققى ئۈچۈن مېنى دوست تۇتۇشۇڭلارنى (يەنى پەرۋەردىگارىمنىڭ دەۋىتىنى يەتكۈزۈشۈمگە چېقىلماسلىقىڭلارنى) تىلەيمەن» دېگىن، كىمكى بىرەر ياخشى ئىش قىلسا، ئۇنىڭغا بىز ساۋابنى زىيادە بېرىمىز، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(42,24,'42:24',' ئۇلار (يەنى مۇشرىكلار): «ئۇ (يەنى مۇھەممەد) ئاللاھقا يالغاننى توقۇدى» دەمدۇ؟ ئەگەر ئاللاھ خالىسا (يەنى ئۇلار گۇمان قىلغاندەك، ئاللاھقا يالغاننى توقۇغان بولساڭ) سېنىڭ قەلبىڭنى پېچەتلىۋەتكەن بولاتتى، ئاللاھ باتىلنى يوق قىلىدۇ، (پەيغەمبىرىگە نازىل قىلغان) سۆزلىرى ئارقىلىق ھەقنى ھەق قىلىدۇ، ئاللاھ ھەقىقەتەن دىللاردىكى سىرلارنى بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,25,'42:25',' ئاللاھ بەندىلىرىدىن تەۋبىنى قوبۇل قىلىدۇ، يامان ئىشلارنى ئەپۇ قىلىدۇ، قىلىۋاتقان ئىشىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(42,26,'42:26',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ دۇئاسىنى ئاللاھ ئىجابەت قىلىدۇ، (كامالى) پەزلىدىن ئۇلارغا ساۋابنى زىيادە بېرىدۇ، كاپىرلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(42,27,'42:27',' ئەگەر ئاللاھ بەندىلىرىنىڭ رىزقىنى كەڭ قىلسا، ئۇلار ئەلۋەتتە زېمىندا پىتنە ـ پاسات تېرىيتتى. لېكىن ئاللاھ خالىغان نەرسىنى ئۆلچەم بىلەن چۈشۈرىدۇ، ئاللاھ ھەقىقەتەن بەندىلىرىنىڭ (ئەھۋالىدىن) تولۇق خەۋەرداردۇر، (ئۇنى) كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,28,'42:28',' ئۇلار (يامغۇرنىڭ يېغىشىدىن ئۈمىدسىزلەنگەندىن كېيىن) ئاللاھ يامغۇر ياغدۇرۇپ بېرىدۇ، رەھمىتىنى يايىدۇ، ئاللاھ (ھەممىنىڭ) ئىگىسىدۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(42,29,'42:29','ئاسمانلارنىڭ، زېمىننىڭ يارىتىلىشى ۋە ئۇ ئىككىسىدە جاندارلارنىڭ يېيىلىشى ئاللاھنىڭ (قۇدرىتىنىڭ) دەلىللىرىدىندۇر، ئاللاھ ئەگەر خالىسا، ئۇلارنى جەمئى قىلىشقا قادىردۇر.');
INSERT INTO "saleh" VALUES(42,30,'42:30','سىلەرگە (يەنى جېنىڭلار ياكى مېلىڭلارغا) ھەرقانداق بىر مۇسىبەت يەتسە، ئۇ سىلەرنىڭ قىلغان گۇناھىڭلار تۈپەيلىدىن كەلگەن بولىدۇ، ئاللاھ نۇرغۇن گۇناھىڭلارنى ئەپۇ قىلىدۇ (ئەپۇ قىلمايدىغان بولسا، بالا ۋە مۇسىبەتتىن ھېچ قۇتۇلماس ئىدىڭلار).');
INSERT INTO "saleh" VALUES(42,31,'42:31',' (ئى مۇشرىكلار!) سىلەر زېمىندا ئاللاھنىڭ ئازابىدىن قېچىپ قۇتۇلالمايسىلەر، سىلەرگە ئاللاھتىن باشقا ھېچ ئىگە ۋە مەدەتكار يوق.');
INSERT INTO "saleh" VALUES(42,32,'42:32','دېڭىزدا قاتنىغۇچى تاغدەك كېمىلەر ئاللاھنىڭ قۇدرىتىنىڭ دەلىللىرىدىندۇر.');
INSERT INTO "saleh" VALUES(42,33,'42:33','ئەگەر ئاللاھ خالىسا، شامالنى توختىتىۋېتىدۇ ـ دە، كېمىلەر دېڭىزنىڭ ئۈستىدە توختاپ قالىدۇ، بۇنىڭدا (قاتتىقلىقتا) سەۋر قىلغۇچى، (كەڭچىلىكتە) شۈكۈر قىلغۇچى ھەر مۆمىن ئۈچۈن نۇرغۇن ئىبرەتلەر بار.');
INSERT INTO "saleh" VALUES(42,34,'42:34',' ياكى ئاللاھ خالىسا، ئىچىدىكى كىشىلەرنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن كېمىلەرنى (بوران بىلەن) ھالاك قىلىدۇ، ئاللاھ نۇرغۇن كىشلەرنىڭ گۇناھلىرىنى ئەپۇ قىلىدۇ.');
INSERT INTO "saleh" VALUES(42,35,'42:35','بىزنىڭ ئايەتلىرىمىز ھەققىدە دەتالاش قىلىدىغانلار ئۆزلىرىگە ئاللاھنىڭ ئازابىدىن قېچىپ قۇتۇلىدىغان جاي يوق ئىكەنلىكىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(42,36,'42:36','(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبىرلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا ئاللاھنىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىيدۇر[36ـ39].');
INSERT INTO "saleh" VALUES(42,37,'42:37','(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبىرلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا ئاللاھنىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىيدۇر[36ـ39].');
INSERT INTO "saleh" VALUES(42,38,'42:38','(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبىرلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا ئاللاھنىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىيدۇر[36ـ39].');
INSERT INTO "saleh" VALUES(42,39,'42:39','(دۇنيانىڭ نېمەتلىرىدىن) سىلەرگە بېرىلگەن ھەر قانداق نەرسە دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر، ئىمان ئېيتقان، پەرۋەردىگارىغا تەۋەككۈل قىلىدىغان كىشىلەرگە، گۇناھى كەبىرلەردىن، قەبىھ ئىشلاردىن ساقلانغۇچىلارغا، دەرغەزەپ بولغانلىرىدا كەچۈرەلەيدىغانلارغا، پەرۋەردىگارىنىڭ دەۋىتىگە ئاۋاز قوشالايدىغانلارغا، نامازنى (تەئدىل ئەركان بىلەن) ئۆتەيدىغانلارغا، ئىشلىرىنى مەسلىھەت بىلەن قارار قىلىدىغانلارغا، بىز رىزىق قىلىپ بەرگەن نەرسىلەردىن سەدىقە قىلىدىغانلارغا، ئۇچرىغان زۇلۇمغا قارشى تۇرالايدىغانلارغا ئاللاھنىڭ ھۇزۇرىدىكى ساۋاب تېخىمۇ ياخشىدۇر، تېخىمۇ باقىيدۇر[36ـ39].');
INSERT INTO "saleh" VALUES(42,40,'42:40',' بىر يامانلىقنىڭ جازاسى شۇنىڭغا ئوخشاش بىر يامانلىقتۇر (يەنى ساڭا بىر كىشى قانچىلىك چېقىلسا، شۇنچىلىك چېقىلماي ئاشۇرۇۋەتسەڭ بولمايدۇ)، كىمكى (ئىنتىقام ئېلىشقا قادىر تۇرۇقلۇق) ئەپۇ قىلسا ۋە (ئۆزى بىلەن يامانلىق قىلغۇچىنىڭ ئارىسىنى) تۈزىسە، ئۇنىڭ ئەجرىنى ئاللاھ بېرىدۇ، ئاللاھ ھەقىقەتەن زۇلۇم قىلغۇچىلارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(42,41,'42:41',' زۇلۇمغا ئۇچرىغۇچى ئادەم ئىنتىقامنى ئالسا ئۇنى ئەيىبلەشكە بولمايدۇ.');
INSERT INTO "saleh" VALUES(42,42,'42:42',' ئەيىبلىنىدىغانلار پەقەت كىشىلەرگە زۇلۇم قىلىدىغان، زېمىندا ناھەق رەۋىشتە پىتنە ـ پاسات تېرىيدىغان ئادەملەردۇر، ئەنە شۇلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(42,43,'42:43','كىمكى (ئەزىيەتكە) سەۋر قىلسا، (ئاللاھنىڭ رازىلىقى ئۈچۈن) ئىنتىقام ئالمىسا، بۇ ئەلۋەتتە مەرغۇپ ئىشلاردىندۇر.');
INSERT INTO "saleh" VALUES(42,44,'42:44',' ئاللاھ گۇمراھ قىلغان ئادەمگە شۇنىڭدىن كېيىن ھېچ ئىگە يوقتۇر، زالىملار ئازابنى كۆرگەن چاغدا، ئۇلارنىڭ «(دۇنياغا) قايتىشقا يول بارمىدۇ؟» دېگەنلىكىنى كۆرىسەن.');
INSERT INTO "saleh" VALUES(42,45,'42:45','ئۇلارنىڭ قورققان، خارلانغان ھالدا، (دوزاخقا) كۆزىنىڭ قۇيرۇقى بىلەن قارىغان ھالدا دوزاخقا توغرىلىنىدىغانلىقىنى كۆرىسەن، مۆمىنلەر: «شۈبھىسىزكى، زىيان تارتقۇچىلار قىيامەت كۈنى ئۆزلىرىگە ۋە ئائىلىسىدىكىلەرگە زىيان سالغۇچىلاردۇر» دەيدۇ، بىلىڭلاركى، زالىملار ھەقىقەتەن دائىملىق ئازابتىدۇر.');
INSERT INTO "saleh" VALUES(42,46,'42:46',' ئۇلارغا ئاللاھتىن باشقا ياردەم بېرىدىغان دوستلار بولمايدۇ، ئاللاھ كىمنى گۇمراھ قىلسا، ئۇنىڭغا (ئۇنى دۇنيادا ھەقكە، ئاخىرەتتە جەننەتكە ئېرىشتۈرگۈچى) ھېچ يول بولمايدۇ.');
INSERT INTO "saleh" VALUES(42,47,'42:47',' ئاللاھ تەرىپىدىن كەلگەن، قارشى تۇرغىلى بولمايدىغان بىر كۈن كېلىشتىن بۇرۇن پەرۋەردىگارىڭلارنىڭ دەۋىتىنى قوبۇل قىلىڭلار، بۇ كۈندە سىلەرگە قاچىدىغان جاي تېپىلماي قالىدۇ. (قىلمىشىڭلار نامە ـ ئەمالىڭلارغا يېزىقلىق بولغانلىقى، ئەزايىڭلار ئۇنىڭغا گۇۋاھ بولغانلىقى ئۈچۈن) سىلەر (ئۇنى) ئىنكار قىلالمايسىلەر.');
INSERT INTO "saleh" VALUES(42,48,'42:48','ئەگەر ئۇلار (يەنى مۇشرىكلار) (ئىماندىن) يۈز ئۆرۈسە، بىز سېنى ئۇلارغا كۆزەتچى قىلىپ ئەۋەتكىنىمىز يوق، سېنىڭ ۋەزىپەڭ پەقەت تەبلىغ قىلىشتۇر، ئەگەر ئىنسانغا بىزنىڭ رەھمىتىمىزنى تېتىتساق خۇش بولۇپ كېتىدۇ، ئۇلارنىڭ قىلغان گۇناھلىرى تۈپەيلىدىن (بېشىغا) بىرەر ھادىسە كەلسە، ئىنسان تولىمۇ كۇفرانە نېمەت قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(42,49,'42:49',' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھ نېمىنى خالىسا شۇنى يارىتىدۇ، خالىغان ئادەمگە قىز پەرزەنت ئاتا قىلىدۇ، خالىغان ئادەمگە ئوغۇل پەرزەنت ئاتا قىلىدۇ.');
INSERT INTO "saleh" VALUES(42,50,'42:50',' ياكى ئۇلارغا ئوغۇل، قىزنى ئارىلاش بېرىدۇ، خالىغان ئادەمنى تۇغماس قىلىدۇ، ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(42,51,'42:51',' ئاللاھ ھەر قانداق ئادەمگە پەقەت ۋەھىي ئارقىلىق ياكى پەردە ئارقىسىدىنلا ياكى بىر پەيغەمبەرنى ئەۋەتىپ ئۆز ئىزنى بىلەن ئۇنىڭغا خالىغان ۋەھىينى قىلىش ئارقىلىقلا سۆز قىلغان. ئاللاھ ھەقىقەتەن (مەخلۇقلارنىڭ سۈپەتلىرىدىن) ئۈستۈندۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(42,52,'42:52','شۇنىڭدەك (يەنى باشقا پەيغەمبەرلەرگە ۋەھىي قىلغاندەك) ئەمرىمىز بويىچە ساڭا قۇرئاننى ۋەھىي قىلدۇق، سەن (ۋەھىيدىن ئىلگىرى) قۇرئاننىڭ ۋە ئىماننىڭ نېمە ئىكەنلىكىنى ئۇقمايتتىڭ، لېكىن بىز قۇرئاننى بىر نۇر قىلدۇقكى، ئۇنىڭ بىلەن بەندىلىرىمىزدىن خالىغان كىشىلەرنى ھىدايەت قىلىمىز. شەك ـ شۈبھىسىزكى سەن توغرا يولغا باشلايسەن. ');
INSERT INTO "saleh" VALUES(42,53,'42:53','(ئۇ) ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئۆزىگە خاس بولغان ئاللاھنىڭ يولىدۇر. بىلىڭلاركى، ھەممە ئىش ئاللاھقا قايتىدۇ.');
INSERT INTO "saleh" VALUES(43,1,'43:1','ھا، مىم.');
INSERT INTO "saleh" VALUES(43,2,'43:2',' روشەن كىتاب بىلەن قەسەمكى');
INSERT INTO "saleh" VALUES(43,3,'43:3','سىلەرنى (ئۇنىڭ مەنىسىنى) چۈشەنسۇن دەپ، بىز ئۇنى ھەقىقەتەن ئەرەبچە قۇرئان قىلدۇق.');
INSERT INTO "saleh" VALUES(43,4,'43:4',' ھەقىقەتەن ئۇ لەۋھۇلمەھپۇزدا بىزنىڭ دەرگاھىمىزدا (مۇقىملاشقان) يۇقىرى مەرتىۋىلىك ھېكمەتلىك (كىتاب) دۇر.');
INSERT INTO "saleh" VALUES(43,5,'43:5','سىلەر (گۇمراھلىقتا) ھەددىدىن ئاشقان قەۋم بولغانلىقىڭلار ئۈچۈن سىلەرگە ۋەز ـ نەسىھەت قىلىشنى (يەنى قۇرئاننى نازى قىلىشنى) تەرك قىلامدۇق؟');
INSERT INTO "saleh" VALUES(43,6,'43:6','ئىلگىرىكى ئۈممەتلەرگە نۇرغۇن پەيغەمبەرلەرنى ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(43,7,'43:7','ئۇلارغا قانداق بىر پەيغەمبەر كەلمىسۇن، ئۇلار ئۇنى (قەۋمىڭ سېنى مەسخىرە قىلغاندەك) مەسخىرە قىلدى.');
INSERT INTO "saleh" VALUES(43,8,'43:8',' ئۇلار (يەنى مەككە كاپىرلىرى) دىن كۈچ ـ قۇۋۋەتتە ئۈستۈن بولغانلارنى ھالاك قىلدۇق، ئىلگىرىكىلەرنىڭ قىسسەلىرى قۇرئاندا ئۆتتى.');
INSERT INTO "saleh" VALUES(43,9,'43:9','ئەگەر سەن ئۇلاردىن: «ئاسمانلار ۋە زېمىننى كىم ياراتتى» دەپ سورىساڭ، ئۇلار: «ئەلۋەتتە ئاسمانلارنى ۋە زېمىننى غالىب، ھەممىنى بىلگۈچى (ئاللاھ) ياراتتى» دەيدۇ.');
INSERT INTO "saleh" VALUES(43,10,'43:10',' ئاللاھ سىلەر ئۈچۈن زېمىننى قارارگاھ قىلدى، (سەپەردە كۆزلىگەن يېرىڭلارغا) بېرىۋېلىشىڭلار ئۈچۈن سىلەرگە زېمىندا نۇرغۇن يوللارنى ياراتتى.');
INSERT INTO "saleh" VALUES(43,11,'43:11',' ئاللاھ بۇلۇتتىن (سىلەرگە زىيان يەتكۈزمەي، پايدا يەتكۈزىدىغان) ئۆلچەمدە يامغۇر ياغدۇردى، ئۇنىڭ بىلەن بىز (گىيا ئۈنمەس قاقاس) ئۆلۈك زېمىننى تىرىلدۈردۇق، سىلەر مۇشۇنداق (يەنى ئۆلۈك يەردىن ئۆسۈملۈك چىقىرىلغاندەك) (قەبرەڭلاردىن) چىقىرىلىسىلەر.');
INSERT INTO "saleh" VALUES(43,12,'43:12','ئاللاھ مەخلۇقاتلارنىڭ پۈتۈن تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرۋەردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر[12ـ14]. ');
INSERT INTO "saleh" VALUES(43,13,'43:13','ئاللاھ مەخلۇقاتلارنىڭ پۈتۈن تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرۋەردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر[12ـ14]. ');
INSERT INTO "saleh" VALUES(43,14,'43:14','ئاللاھ مەخلۇقاتلارنىڭ پۈتۈن تۈرلىرىنى ياراتتى، سىلەرگە ئۈستىگە چىقىشىڭلار ئۈچۈن كېمە ۋە مىنىشىڭلار ئۈچۈن ھايۋان قاتارلىقلارنى ياراتتى. ئاندىن سىلەر ئۇلارنىڭ ئۈستىگە چىققانلىرىڭلاردا پەرۋەردىگارىڭلارنىڭ نېمىتىنى ئەسلەپ: «بىزگە بۇنى بويسۇندۇرۇپ بەرگەن زات پاكتۇر، بىز ئۇنىڭغا (مىنىشكە) قادىر ئەمەس ئىدۇق، بىز ھەقىقەتەن پەرۋەردىگارىمىزنىڭ دەرگاھىغا قايتقۇچىلارمىز» دېگەيسىلەر[12ـ14]. ');
INSERT INTO "saleh" VALUES(43,15,'43:15',' ئۇلار (پەرىشتىلەرنى ئاللاھنىڭ قىزلىرى دېيىش بىلەن) ئاللاھنىڭ بىر قىسىم بەندىلىرىنى ئاللاھنىڭ بىر جۈزئى قىلىۋالدى. (مۇنداق دېگۈچى) ئىنسان ھەقىقەتەن ئاللاھقا ئاشكارا ناشۈكۈرلۈك قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(43,16,'43:16','ئاللاھ مەخلۇقاتتىن ئۆزى ئۈچۈن قىزلارنى (بالا) تۇتۇپ، سىلەرنى ئوغۇللارغا خاس قىلدىمۇ؟ ');
INSERT INTO "saleh" VALUES(43,17,'43:17',' ئەگەر ئۇلارنىڭ بىرىگە مەرھەمەتلىك ئاللاھقا مەسەل قىلغان نەرسە (يەنى قىز بالا) بىلەن بېشارەت بېرىلسە (يەنى خوتۇنىنىڭ قىز تۇغقانلىقى خەۋەر قىلىنسا)، قايغۇغا چۆمگەن ھالدا ئۇنىڭ يۈزى قارىداپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(43,18,'43:18',' (ئۇلار ئاللاھقا) زىبۇزىننەت ئىچىدە چوڭ بولىدىغان، مۇنازىرىدە مەقسىتىنى ئوچۇق بايان قىلالمايدىغان (قىزلارنى مەنسۇپ قىلامدۇ؟).');
INSERT INTO "saleh" VALUES(43,19,'43:19',' ئۇلار مەرھەمەتلىك ئاللاھنىڭ بەندىلىرى بولغان پەرىشتىلەرگە قىز دەپ ئېتىقاد قىلدى، ئاللاھ پەرىشتىلەرنى ياراتقاندا، ئۇلار ئۈستىدە بارمىدى؟ (يەنى ئۇلار پەرىشتىلەرنىڭ قىز ئىكەنلىكىنى قەيەردىن بىلىدۇ؟) ئۇلارنىڭ گۇۋاھلىقى (ئۇلارنىڭ نامە ـ ئەمالىغا) يېزىلىدۇ، (قىيامەت كۈنى) ئۇلار سوراققا تارتىلىدۇ.');
INSERT INTO "saleh" VALUES(43,20,'43:20','ئۇلار: «مەرھەمەتلىك ئاللاھ خالىسا، بىز ئۇلارغا (يەنى پەرىشتىلەرگە) چوقۇنمايتتۇق» دېدى. بۇ توغرۇلۇق ئۇلارنىڭ بىلىمى (يەنى ئاساسى) يوق، ئۇلار پەقەت جۆيلۈيدۇ.');
INSERT INTO "saleh" VALUES(43,21,'43:21',' ئۇلارغا بىز قۇرئاندىن ئىلگىرى (قانداقتۇر) بىر كىتاب بەرگەن بولۇپ، ئۇلار شۇ كىتابقا مەھكەم ئىسىلامدۇ؟');
INSERT INTO "saleh" VALUES(43,22,'43:22','ئۇنداق ئەمەس، ئۇلار: «ھەقىقەتەن ئاتا ـ بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، بىز ئۇلارنىڭ ئىزىدىن مېڭىپ ھىدايەت تاپقۇچىدۇرمىز» دەيدۇ.');
INSERT INTO "saleh" VALUES(43,23,'43:23',' شۇنىڭدەك سەندىن ئىلگىرى ھەر قاچان بىرەر شەھەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) ئەۋەتسەكلا، ئۇنىڭ دۆلەتمەن ئادەملىرى: «بىز ھەقىقەتەن ئاتا ـ بوۋىلىرىمىزنىڭ بىر خىل دىنغا ئېتىقاد قىلغانلىقىنى بىلىمىز، شەك ـ شۈبھىسىزكى، بىز ئۇلارنىڭ ئىزلىرىدىن ماڭىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(43,24,'43:24','(ھەر پەيغەمبەر ئۆز قەۋمىگە) «ئاتا ـ بوۋاڭلار ئېتىقاد قىلىپ كەلگەن دىنغا قارىغاندا ئەڭ توغرا بىر دىننى كەلتۈرسەم يەنىلا ئاتا ـ بوۋاڭلارغا ئەگىشەمسىلەر؟» دېدى. ئۇلار: «سىلەر ئېلىپ كەلگەن دىنغا ئىشەنمەيمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(43,25,'43:25',' بىز ئۇلارنى جازالىدۇق، (توغرا يولنى) ئىنكار قىلغۇچىلارنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىغا قارىغىن.');
INSERT INTO "saleh" VALUES(43,26,'43:26',' ئۆز ۋاقتىدا ئىبراھىم ئاتىسىغا ۋە قەۋمىگە ئېيتتى: «سىلەر چوقۇنۇۋاتقان بۇتلاردىن مەن راستلا ئادا ـ جۇدامەن.');
INSERT INTO "saleh" VALUES(43,27,'43:27','پەقەت مېنى ياراتقان ئاللاھ مېنى (توغرا يولغا) باشلايدۇ».');
INSERT INTO "saleh" VALUES(43,28,'43:28','ئىبراھىم كەلىمە تەۋھىدنى ئەۋلادىدىن (شېرىك كەلتۈرگەنلەر) ئىمانغا قايتسۇن دەپ ئەۋلادى ئىچىدە قالدۇرۇپ كەتتى.');
INSERT INTO "saleh" VALUES(43,29,'43:29',' بەلكى مەن بۇلار (يەنى مەككە ئاھالىسى) نى ۋە بۇلارنىڭ ئاتا ـ بوۋىلىرىنى (ئۇزۇن ئۆمۈر ۋە نېمەتتىن) بەھرىمەن قىلدىم، تاكى ئۇلارغا ھەق (يەنى قۇرئان) ۋە روشەن پەيغەمبەر (مۇھەممەد ئەلەيھىسسالام) كەلگۈچە.');
INSERT INTO "saleh" VALUES(43,30,'43:30','ئۇلارغا ھەق (يەنى قۇرئان) كەلگەندە، ئۇلار: «بۇ سېھىردۇر، بىز ھەقىقەتەن ئۇنى ئىنكار قىلغۇچىمىز» دېيىشتى.');
INSERT INTO "saleh" VALUES(43,31,'43:31',' ئۇلار: «بۇ قۇرئان نېمىشقا ئىككى شەھەر (يەنى مەككە بىلەن تائىف ئادەملىرىدىن) بىر كاتتا ئادەمگە نازىل قىلىنمىدى؟ » دېيىشتى.');
INSERT INTO "saleh" VALUES(43,32,'43:32','پەرۋەردىگارىڭنىڭ رەھمىتىنى ئۇلار تەقسىم قىلىپ بېرەمدۇ؟ بىز ئۇلارنىڭ ھاياتىي دۇنيادىكى رىزقىنى ئۇلارنىڭ ئارىسىدا تەقسىم قىلدۇق، ئۇلارنىڭ بەزىسى بەزىسىنى (ھەق بېرىپ) ئىشقا سالسۇن دەپ، ئۇلارنىڭ بەزىسىنىڭ دەرىجىسىنى بەزىسىدىن ئۈستۈن قىلدۇق، پەرەۋردىگارىڭنىڭ رەھمىتى ئۇلارنىڭ توپلىغان نەرسىلىرى (يەنى پۇل ـ ماللىرى) دىن ياخشىدۇر.');
INSERT INTO "saleh" VALUES(43,33,'43:33',' ئىنسانلار (كۇففارلارنىڭ باياشات تۇرمۇشىنى كۆرۈپ كۇفرىغا قىزىقىپ ئۇلارغا قوشۇلۇپ) ھەممىسى (كۇفرىدا) بىر ئۈممەت بولۇپ قالمايدىغان بولسا ئىدى، مەرھەمەتلىك ئاللاھنى ئىنكار قىلغان كىشىلەرنىڭ ئۆيلىرىنىڭ ئۆگزىلىرىنى ۋە ئۇنىڭغا چىقىدىغان شوتىلىرىنى كۈمۈشتىن قىلىپ بېرەتتۇق.');
INSERT INTO "saleh" VALUES(43,34,'43:34',' ئۇلارنىڭ ئۆيلىرىنىڭ ئىشىكلىرىنى ۋە تەختلىرىنىمۇ كۈمۈشتىن قىلىپ بېرەتتۇق، ئۇلار تەختلەرگە يۆلىنىپ ئولتۇراتتى.');
INSERT INTO "saleh" VALUES(43,35,'43:35',' (يۇقىرىقىلارنىڭ بەزىسىنى) ئالتۇندىن قىلىپ بېرەتتۇق، ئۇلارنىڭ ھەممىسى پەقەت دۇنيا تىرىكچىلىكىدە پايدىلىنىدىغان نەرسىلەردۇر. پەرەۋردىگارىڭنىڭ ھۇزۇرىدىكى ئاخىرەت (يەنى جەننەت ۋە ئۇنىڭدىكى نازۇنېمەتلەر) تەقۋادارلارغا خاستۇر.');
INSERT INTO "saleh" VALUES(43,36,'43:36',' كىمكى مېھرىبان ئاللاھنى ياد ئېتىشتىن (يەنى قۇرئاندىن) يۈز ئۆرۈيدىكەن، بىز ئۇنىڭغا شەيتاننى ھۆكۈمران قىلىمىز (شەيتان ئۇنى ۋەسۋەسە قىلىدۇ)، شەيتان ئۇنىڭغا ھەمىشە ھەمراھ بولىدۇ. ');
INSERT INTO "saleh" VALUES(43,37,'43:37','شەك ـ شۈبھىسىزكى، شەيتانلار ئۇلارنى (يەنى ئازغۇن كۇففارلارنى) توغرا يولدىن توسىدۇ، ئۇلار بولسا ئۆزلىرىنى توغرا يولدا دەپ گۇمان قىلىدۇ.');
INSERT INTO "saleh" VALUES(43,38,'43:38','كاپىر ئادەم (ھەمراھى بىلەن قىيامەت كۈنى) دەرگاھىمىزغا كەلگەن چاغدا (ھەمراھىغا): «كاشكى سەن بىلەن مېنىڭ ئارامدا شەرق بىلەن غەربنىڭ ئارىسىدەك ئارىلىق بولسا ئىكەن. (سەن) نېمىدېگەن يامان ھەمراھ! (چۈنكى سەن مېنىڭ بەختسىزلىكىمگە سەۋەبچى بولدۇڭ)» دەيدۇ.');
INSERT INTO "saleh" VALUES(43,39,'43:39','(ئۇلارغا) «(دۇنيادىكى چاغدا) زۇلۇم قىلغانلىقىڭلار (يەنى شېرىك كەلتۈرگەنلىكىڭلار) ئۈچۈن بۈگۈن سىلەرنىڭ ئازابتا ئورتاق بولغانلىقىڭلار سىلەرگە پايدا بەرمەيدۇ» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(43,40,'43:40',' (ئى مۇھەممەد!) سەن گاسلارغا ئاڭلىتالامسەن؟ يا كورلارنى توغرا يولغا سالالامسەن؟ (يەنى كۇففارلار گاسلارغا، كورلارغا ئوخشايدۇ) ئاشكارا گۇمراھلىقتا بولغان كىشىنى ھىدايەت قىلالامسەن؟ ');
INSERT INTO "saleh" VALUES(43,41,'43:41','(ئۇلارنى جازالاشتىن ئىلگىرى) سېنى ئېلىپ كەتسەك (يەنى ۋاپات قىلدۇرساق ۋاپاتىڭدىن كېيىن) ئۇلارنى چوقۇم جازالايمىز.');
INSERT INTO "saleh" VALUES(43,42,'43:42',' ياكى بىز ساڭا (ھاياتىڭدا) ئۇلارغا ۋەدە قىلغان ئازابنى كۆرسىتىمىز، بىز، شۈبھىسىزكى، ئۇلارغا (يەنى ئۇلارغا ئازاب قىلىشقا) قادىرمىز.');
INSERT INTO "saleh" VALUES(43,43,'43:43','(ئى مۇھەممەد!) ساڭا ۋەھىي قىلىنغان قۇرئانغا چىڭ ئېسىلغىن، سەن ھەقىقەتەن توغرا يولدىدۇرسەن.');
INSERT INTO "saleh" VALUES(43,44,'43:44',' قۇرئان ساڭا ۋە سېنىڭ قەۋمىڭگە ئەلۋەتتە (ئۇلۇغ) شەرەپتۇر، سىلەر كەلگۈسىدە (بۇ نېمەت ئۈستىدە) سورىلىسىلەر.');
INSERT INTO "saleh" VALUES(43,45,'43:45',' سەندىن بۇرۇن بىز ئەۋەتكەن پەيغەمبەرلىرىمىزدىن سوراپ باققىنكى، مېھرىبان ئاللاھتىن باشقا ئىبادەت قىلىنىدىغان ئىلاھلارنى بەلگىلىدۇقمۇ؟ (پەيغەمبەرلەرنىڭ ئىچىدە ئاللاھتىن غەيرىيگە ئىبادەت قىلىشقا دەۋەت قىلغىنى بارمۇ؟)');
INSERT INTO "saleh" VALUES(43,46,'43:46',' بىز ھەقىقەتەن مۇسانى پىرئەۋنگە ۋە ئۇنىڭ تەۋەلىرىگە (ئۇنىڭ راست پەيغەمبەر ئىكەنلىكىنى ئىسپاتلايدىغان شانلىق) مۆجىزىلىرىمىز بىلەن ئەۋەتتۇق، مۇسا (پىرئەۋنگە): «مەن ھەقىقەتەن جىمى ئالەملەرنىڭ پەرۋەردىگارىنىڭ (سېنى ۋە قەۋمىڭنى بىر ئاللاھقا ئىبادەت قىلىشقا دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) ئەلچىسىمەن» دېدى.');
INSERT INTO "saleh" VALUES(43,47,'43:47','مۇسا ئۇلارغا بىزنىڭ مۆجىزىلىرىمىزنى ئېلىپ كەلگەندە، ئۇلار ناگاھان مۆجىزىلەرنى (مەسخىرە قىلىپ) كۈلدى. ');
INSERT INTO "saleh" VALUES(43,48,'43:48',' ئۇلارغا بىر ـ بىرىدىن كاتتا مۆجىزىلەرنى كۆرسەتتۇق، ئۇلارنى (كۇفرىدىن) قايتسۇن دەپ (تۈرلۈك) ئازابلار بىلەن جازالىدۇق.');
INSERT INTO "saleh" VALUES(43,49,'43:49',' (ئۇلار ئازابنى كۆرگەندە) «ئى سېھىرگەر! پەرەۋردىگارىڭغا ئۇنىڭ ساڭا بەرگەن (دۇئايىڭنى ئىجابەت قىلىشتىن ئىبارەت) ئەھدى بويىچە بىز ئۈچۈن دۇئا قىلساڭ (ئەگەر ئۇ دۇئايىڭ بىلەن بىزدىن ئازابنى كۆتۈرۈۋەتسە)، بىز چوقۇم ئىمان ئېيتىمىز» دېدى.');
INSERT INTO "saleh" VALUES(43,50,'43:50',' (مۇسانىڭ دۇئاسى بىلەن) ئۇلاردىن ئازابنى كۆتۈرۈۋەتكىنىمىزدە، ناگاھان ئۇلار (ئەھدىنى) بۇزۇپ (كۇفرىدا) چىڭ تۇردى.');
INSERT INTO "saleh" VALUES(43,51,'43:51',' پىرئەۋن ئۆز قەۋمى ئىچىدە (پەخىرلەنگەن ھالدا) نىدا قىلىپ ئېيتتى: «ئى قەۋمىم! مىسىرنىڭ پادىشاھلىقى، ئاستىمدىن ئېقىپ تۇرغان بۇ دەريالار مېنىڭ ئەمەسمۇ؟ (مېنىڭ بۇ بۈيۈكلۈكۈمنى) كۆرمەمسىلەر؟ بەلكى مەن ئوچۇق گەپ قىلالمايدىغان بۇ ئەرزىمەس ئادەمدىن (يەنى مۇسا ئەلەيھىسسالامدىن) ياخشىمەن»[51ـ52].');
INSERT INTO "saleh" VALUES(43,52,'43:52',' پىرئەۋن ئۆز قەۋمى ئىچىدە (پەخىرلەنگەن ھالدا) نىدا قىلىپ ئېيتتى: «ئى قەۋمىم! مىسىرنىڭ پادىشاھلىقى، ئاستىمدىن ئېقىپ تۇرغان بۇ دەريالار مېنىڭ ئەمەسمۇ؟ (مېنىڭ بۇ بۈيۈكلۈكۈمنى) كۆرمەمسىلەر؟ بەلكى مەن ئوچۇق گەپ قىلالمايدىغان بۇ ئەرزىمەس ئادەمدىن (يەنى مۇسا ئەلەيھىسسالامدىن) ياخشىمەن»[51ـ52].');
INSERT INTO "saleh" VALUES(43,53,'43:53','نېمە ئۈچۈن ئۇنىڭغا (ئاسماندىن) ئالتۇن بىلەيزۈكلەر تاشلانمىدى؟ ياكى نېمىشقا (ئۇنىڭ راستلىقىغا گۇۋاھلىق بېرىش ئۈچۈن) ئۇنىڭ بىلەن بىللە پەرىشتىلەر ھەمراھ بولۇپ كەلمىدى؟»');
INSERT INTO "saleh" VALUES(43,54,'43:54','پىرئەۋن قەۋمىنى گوللىغانلىقتىن، قەۋمى ئۇنىڭغا ئىتائەت قىلدى، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى.');
INSERT INTO "saleh" VALUES(43,55,'43:55','ئۇلار بىزنى دەرغەزەپ قىلغاندا، ئۇلارنى بىز (ئەڭ قاتتىق جازالار بىلەن) جازالىدۇق، ئۇلارنىڭ ھەممىسىنى (دېڭىزدا) غەرق قىلدۇق.');
INSERT INTO "saleh" VALUES(43,56,'43:56','ئۇلارنى بىز (كۇففارلارنىڭ ئازابقا تېگىشلىك بولۇشىدا) كېيىنكىلەرگە نەمۇنە ۋە ئىبرەت قىلدۇق.');
INSERT INTO "saleh" VALUES(43,57,'43:57',' (قۇرئاندا) مەريەمنىڭ ئوغلى (يەنى ئىيسا ئەلەيھىسسالام) مىسال كەلتۈرۈلسە، ناگاھان سېنىڭ قەۋمىڭ (خۇشاللىقتىن) سۈرەن سېلىشىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(43,58,'43:58','ئۇلار: «بىزنىڭ ئىلاھلىرىمىز ياخشىمۇ ياكى ئىيسامۇ؟» دېدى. ئۇلار بۇ مىسالنى پەقەت خۇسۇمەت قىلىش ئۈچۈنلا كەلتۈردى، بەلكى ئۇلار جېدەلخور قەۋمدۇر.');
INSERT INTO "saleh" VALUES(43,59,'43:59',' ئۇ (يەنى ئىيسا) (ناسارالار گۇمان قىلغاندەك ئىلاھ ياكى ئىلاھنىڭ ئوغلى ئەمەس) پەقەت پەيغەمبەرلىك نېمىتىمىزگە ئېرىشكەن بىر بەندىدۇر، ئۇنى بىز ئىسرائىل ئەۋلادىغا (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) بىر دەلىل قىلدۇق.');
INSERT INTO "saleh" VALUES(43,60,'43:60','ئەگەر بىز خالىساق، ئەلۋەتتە سىلەرگە زېمىندا ئورۇنباسار بولىدىغان پەرىشتىلەرنى ۋۇجۇتقا كەلتۈرەتتۇق.');
INSERT INTO "saleh" VALUES(43,61,'43:61','ئۇ (يەنى ئىيسا) ئەلۋەتتە قىيامەتنىڭ ئالامىتىدۇر، ئۇنىڭغا (يەنى قىيامەتكە) شەك كەلتۈرمەڭلار. (ئى مۇھەممەد! ئۇلارغا ئېيتقىنكى) «ماڭا ئەگىشىڭلار، بۇ (مېنىڭ دەۋەت قىلغىنىم) توغرا يولدۇر.');
INSERT INTO "saleh" VALUES(43,62,'43:62','سىلەرنى شەيتان (توغرا يولدا) مېڭىشتىن توسمىسۇن (يەنى شەيتاننىڭ ۋەسۋەسىسىگە ئالدانماڭلار)، شەيتان سىلەرگە ھەقىقەتەن ئاشكارا دۈشمەندۇر».');
INSERT INTO "saleh" VALUES(43,63,'43:63',' ئىيسا روشەن مۆجىزىلەر بىلەن كېلىپ ئېيتتى: «سىلەرگە مەن ھېكمەت ئېلىپ كەلدىم. سىلەرگە ئىختىلاپ قىلىۋاتقان نەرسەڭلارنىڭ (يەنى دىنىي ئىشلارنىڭ) بەزىسىنى بايان قىلىپ بېرىمەن، ئاللاھتىن قورقۇڭلار، ماڭا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(43,64,'43:64',' ئاللاھ ھەقىقەتەن مېنىڭ پەرۋەردىگارىمدۇر ۋە سىلەرنىڭمۇ پەرۋەردىگارىڭلاردۇر، ئۇنىڭغا ئىبادەت قىلىڭلار، بۇ توغرا يولدۇر».');
INSERT INTO "saleh" VALUES(43,65,'43:65','(ناسارالار) نىڭ پىرقىلىرى (ئىيسا توغرىسىدا) ئۆز ـ ئارا ئىختىلاپ قىلىشتى، زالىملارغا قاتتىق كۈن (يەنى قىيامەت كۈنىنىڭ) ئازابىدىن ۋاي!');
INSERT INTO "saleh" VALUES(43,66,'43:66','ئۇلار پەقەت قىيامەتنىڭ ئۆزلىرى تۇيمىغان ھالدا ئۇشتۇمتۇت كېلىشىنىلا كۈتىدۇ.');
INSERT INTO "saleh" VALUES(43,67,'43:67',' بۇ كۈندە دوستلار بىر ـ بىرىگە دۈشمەن بولىدۇ، پەقەت (خۇدالىق ئۈچۈن دوستلاشقان) تەقۋادارلارلا (ئۇنداق ئەمەستۇر).');
INSERT INTO "saleh" VALUES(43,68,'43:68','(ئۇلارغا) «بەندىلىرىم! بۈگۈن سىلەرگە قورقۇنچ يوق، سىلەر غەم ـ ئەندىشە قىلمايسىلەر » (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(43,69,'43:69',' ئۇلار بىزنىڭ ئايەتلىرىمىزگە ئىمان ئېيتقان ۋە مۇسۇلمان بولغانلار ئىدى.');
INSERT INTO "saleh" VALUES(43,70,'43:70',' (ئۇلارغا) «سىلەر ئاياللىرىڭلار بىلەن بىللە خۇشال ـ خۇرام ھالدا جەننەتكە كىرىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(43,71,'43:71',' ئۇلارغا ئالتۇن لېگەنلەردە (تائام)، ئالتۇن جاملاردا (شاراب) تۇتۇلىدۇ. جەننەتتە كۆڭۈللەر تارتىدىغان، كۆزلەر لەززەتلىنىدىغان نەرسىلەر بار، سىلەر جەننەتتە مەڭگۈ قالىسىلەر.');
INSERT INTO "saleh" VALUES(43,72,'43:72','سىلەر (دۇنيادا) قىلغان (ياخشى) ئەمەلىڭلار بىلەن ۋارىس بولغان جەننەت ئەنە شۇدۇر.');
INSERT INTO "saleh" VALUES(43,73,'43:73',' جەننەتتە نۇرغۇن (تۈرلۈك) مېۋىلەر بار، ئۇنىڭدىن يەيسىلەر.');
INSERT INTO "saleh" VALUES(43,74,'43:74',' گۇناھكارلار (يەنى كۇففارلار) ھەقىقەتەن دوزاخ ئازابىدا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(43,75,'43:75','ئۇلاردىن ئازاب (بىردەممۇ) يېنىكلىتىلمەيدۇ، ئۇلار دوزاختا (ھەر قانداق ياخشىلىقتىن) ئۈمىدسىزلەردۇر.');
INSERT INTO "saleh" VALUES(43,76,'43:76',' ئۇلارغا بىز زۇلۇم قىلمىدۇق ۋەلېكىن ئۇلار ئۆزلىرىگە ئۆزلىرى زۇلۇم قىلدى.');
INSERT INTO "saleh" VALUES(43,77,'43:77',' ئۇلار (يەنى كۇففارلار) (دوزاخقا مۇئەككەل پەرىشتىگە): «ئى مالىك! پەرۋەردىگارىڭ بىزگە ئۆلۈم ھۆكۈم قىلسۇن» دەپ توۋلايدۇ. مالىك: «سىلەر ئازابتا چوقۇم قالىسىلەر» دەيدۇ.');
INSERT INTO "saleh" VALUES(43,78,'43:78',' بىز سىلەرگە ھەقىقەتەن ھەق (دىن) نى كەلتۈردۇق ۋەلېكىن سىلەرنىڭ كۆپچىلىكىڭلار ھەق (دىن) نى يامان كۆرگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(43,79,'43:79','ئۇلار (مۇھەممەد ئەلەيھىسسالامغا سۇيىقەست قىلىشتا) بىر ئىشنى قارار قىلغان بولسا، بىز (مۇھەممەد ئەلەيھىسسالامغا) ياردەم بېرىشتە بىر ئىشنى قارار قىلىمىز.');
INSERT INTO "saleh" VALUES(43,80,'43:80',' ئۇلار گۇمان قىلامدۇكى، بىز ئۇلارنىڭ سىرلىرىنى ۋە پىچىرلاشقانلىقلىرىنى ئاڭلىمايمىز، ھەرگىز ئۇنداق ئەمەس (ئۇنى ئاڭلاپ تۇرىمىز)، بىزنىڭ ئەلچىلىرىمىز (يەنى پەرىشتىلىرىمىز) ئۇلارنىڭ ئالدىدا (ئۇلارنىڭ ئەمەللىرىنى) يېزىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(43,81,'43:81',' «ئەگەر مېھرىبان ئاللاھنىڭ بالىسى بولىدىغان بولسا، مەن (ئۇ بالىغا) ئىبادەت قىلغۇچىلارنىڭ ئەۋۋىلى بولاتتىم» دېگىن.');
INSERT INTO "saleh" VALUES(43,82,'43:82','ئاسمانلارنىڭ ۋە زېمىننىڭ پەرۋەردىگارى، ئەرشنىڭ پەرۋەردىگارى ئۇلارنىڭ سۆزلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(43,83,'43:83','ئۇلارنى (يەنى مەككە كۇففارلىرىنى) (گۇمراھلىقلىرىدا ۋە نادانلىقلىرىدا) قويۇپ بەرگىن، ئۇلار ۋەدە قىلىنغان كۈنىگە مۇلاقات بولغانغا قەدەر (باتىللىرىغا) چۆمسۇن ۋە (دۇنياسى بىلەن) ئوينىسۇن.');
INSERT INTO "saleh" VALUES(43,84,'43:84',' ئاللاھ ئاسماندىمۇ ئىلاھتۇر، زېمىندىمۇ ئىلاھتۇر، ئاللاھ ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(43,85,'43:85','ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پادىشاھلىقى ئىلكىدە بولغان زات كاتتىدۇر، قىيامەتنىڭ (بولىدىغان ۋاقتى) توغرىسىدىكى بىلىم ھەقىقەتەن (يالغۇر) ئاللاھنىڭ دەرگاھىدىدۇر. سىلەر ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(43,86,'43:86','ئۇلارنىڭ ئاللاھنى قويۇپ چوقۇنغان نەرسىلىرى شاپائەت قىلالمايدۇ. پەقەت ھەق بىلەن گۇۋاھلىق بەرگەن (يەنى لائىلاھە ئىللەللاھ دېگەن، تىلى بىلەن ئېيتقاننى دىلى بىلەن) ھەقىقىي بىلگەنلەر بۇنىڭدىن مۇستەسنا. ');
INSERT INTO "saleh" VALUES(43,87,'43:87','ئەگەر سەن ئۇلاردىن ئۇلارنى كىم خەلق ئەتكەنلىكلىرىنى سورىساڭ، ئۇلار چوقۇم «ئاللاھ» دەيدۇ، ئۇلار قانداقمۇ (ئاللاھقا ئىبادەت قىلىشتىن بۇتلارغا ئىبادەت قىلىشقا) بۇرۇلۇپ كېتىدۇ. ');
INSERT INTO "saleh" VALUES(43,88,'43:88',' (ئاللاھ تائالا مۇھەممەد ئەلەيھىسسالامنىڭ) «ئى پەرۋەردىگارىم! بۇلار ھەقىقەتەن ئىمان ئېيتمايدىغان قەۋمدۇر» دېگەن سۆزىنى (بىلىدۇ).');
INSERT INTO "saleh" VALUES(43,89,'43:89','سەن ئۇلاردىن يۈز ئۆرۈگىن، سالام دېگىن (يەنى ئۇلارغا بەتدۇئامۇ قىلمىغىن، دىنغا دەۋەتمۇ قىلمىغىن)، ئۇلار كەلگۈسىدە (كۇفرىنىڭ ئاقىۋىتىنى) بىلىدۇ.');
INSERT INTO "saleh" VALUES(44,1,'44:1','ھا، مىم.');
INSERT INTO "saleh" VALUES(44,2,'44:2','روشەن قۇرئان بىلەن قەسەم قىلىمەن.');
INSERT INTO "saleh" VALUES(44,3,'44:3',' بىز قۇرئاننى ھەقىقەتەن مۇبارەك كېچىدە (يەنى شەبى قەدردە) نازىل قىلدۇق، بىز ھەقىقەتەن ئىنسانلارنى (قۇرئان بىلەن) ئاگاھلاندۇرغۇچى بولدۇق.');
INSERT INTO "saleh" VALUES(44,4,'44:4','ئۇ كېچىدە ھەر بىر ھېكمەتلىك (يەنى ھېكمەتنىڭ تەقەززاسى بويىچە قىلىنغان) ئىش (يەنى بەندىلەرنىڭ رىزقى، ئەجىلى ۋە باشقا ئەھۋالى) ئايرىلىدۇ.');
INSERT INTO "saleh" VALUES(44,5,'44:5',' (شۇ كېچە تەقدىر قىلىنغان ئىشلارنىڭ ھەممىسى) بىزنىڭ دەرگاھىمىزدىن بولغان ئىشتۇر، بىز ھەقىقەتەن (ئىنسانلارغا پەيغەمبەرلەرنى) ئەۋەتكۈچى بولدۇق.');
INSERT INTO "saleh" VALUES(44,6,'44:6',' (شۇ) پەرۋەردىگارىڭنىڭ رەھمىتىدىندۇر. ئۇ ھەقىقەتەن (بەندىلەرنىڭ سۆزلىرىنى) ئاڭلاپ تۇرغۇچىدۇر. (ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(44,7,'44:7',' ئەگەر سىلەر ھەقىقىي ئىشەنگۈچى بولساڭلار، (ئۇ) ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنىڭ پەرۋەردىگارىدۇر.');
INSERT INTO "saleh" VALUES(44,8,'44:8',' ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر. ئۇ (ئۆلۈكنى) تىرىلدۈرەلەيدۇ، (تىرىكنىڭ) جېنىنى ئالالايدۇ، ئۇ سىلەرنىڭ پەرۋەردىگارىڭلاردۇر، ئىلگىرىكى ئاتا ـ بوۋاڭلارنىڭ پەرۋەردىگارىدۇر.');
INSERT INTO "saleh" VALUES(44,9,'44:9',' ئۇنداق ئەمەس (يەنى ئۇلار ھەقىقىي ئىشەنگۈچى ئەمەس)، بەلكى ئۇلار (قىيامەتتىن) شەكتە بولۇپ (ھەقنى ئىستىھزا قىلىپ) ئوينايدۇ.');
INSERT INTO "saleh" VALUES(44,10,'44:10',' (ئى مۇھەممەد!) سەن ئاسمان (ھەممە ئادەم) ئوچۇق (كۆرىدىغان) تۈتۈننى كەلتۈرىدىغان كۈندە (ئۇلارغا بولىدىغان ئازابنى) كۈتكىن.');
INSERT INTO "saleh" VALUES(44,11,'44:11',' (ئۇ تۈتۈن) كىشىلەرنى ئورىۋالىدۇ، بۇ قاتتىق ئازابتۇر.');
INSERT INTO "saleh" VALUES(44,12,'44:12',' (ئۇلار) «پەرۋەردىگارىمىز! بىزدىن ئازابنى كۆتۈرۈۋەتكىن، بىز ھەقىقەتەن ئىمان كەلتۈرگۈچىدۇرمىز» (دەيدۇ).');
INSERT INTO "saleh" VALUES(44,13,'44:13',' (ئازاب كۆتۈرۈلۈپ كەتكەندە) ئۇلار قانداقمۇ ئىبرەت ئالسۇن، ھالبۇكى، ئۇلارغا روشەن پەيغەمبەر كەلدى (شۇنداق تۇرۇقلۇق ئۇلار ئىمان ئېيتمىدى).');
INSERT INTO "saleh" VALUES(44,14,'44:14',' ئاندىن ئۇلار ئۇنىڭدىن يۈز ئۆرۈدى ۋە: «ئۇ ئۆگىتىلگەن (يەنى قۇرئاننى كىشىلەردىن ئۆگەنگەن) دۇر، مەجنۇندۇر» دېدى.');
INSERT INTO "saleh" VALUES(44,15,'44:15',' بىز ھەقىقەتەن ئازابنى (سىلەردىن) ئازغىنا ۋاقىت كۆتۈرۈۋېتىمىز، (ئاندىن) سىلەر ھەقىقەتەن (ئىلگىرىكى كاپىرلىق ھالىتىڭلارغا) قايتىۋالىسىلەر.');
INSERT INTO "saleh" VALUES(44,16,'44:16','ئۇلارنى قاتتىق تۇتقان كۈنىمىزدە چوقۇم جازالايمىز.');
INSERT INTO "saleh" VALUES(44,17,'44:17','ئۇلاردىن ئىلگىرى بىز ھەقىقەتەن پىرئەۋننىڭ قەۋمىنى سىنىدۇق، ئۇلارغا ئېسىل بىر پەيغەمبەر كەلدى.');
INSERT INTO "saleh" VALUES(44,18,'44:18',' (مۇسا ئۇلارغا ئېيتتى) «ماڭا ئاللاھنىڭ بەندىلىرىنى (يەنى بەنى ئىسرائىلنى) تاپشۇرۇپ بېرىڭلار، مەن سىلەرگە ھەقىقەتەن ئىشەنچلىك پەيغەمبەر.');
INSERT INTO "saleh" VALUES(44,19,'44:19','ئاللاھقا تەكەببۇرلۇق قىلماڭلار، مەن سىلەرگە ھەقىقەتەن روشەن مۆجىزە بىلەن كەلدىم.');
INSERT INTO "saleh" VALUES(44,20,'44:20',' مەن ھەقىقەتەن مېنىڭ پەرۋەردىگارىم ۋە سىلەرنىڭ پەرۋەردىگارىڭلارغا سېغىنىپ، سىلەرنىڭ مېنى ئۆلتۈرۈشۈڭلاردىن پاناھ تىلەيمەن.');
INSERT INTO "saleh" VALUES(44,21,'44:21',' ئەگەر ماڭا ئىمان كەلتۈرمىسەڭلار مېنى (ئۆز ھالىمغا) قويۇۋېتىڭلار».');
INSERT INTO "saleh" VALUES(44,22,'44:22',' مۇسا پەرۋەردىگارىغا: «بۇلار گۇناھكار قەۋمدۇر» دەپ دۇئا قىلدى.');
INSERT INTO "saleh" VALUES(44,23,'44:23','(ئاللاھ ئېيتتى) «مېنىڭ بەندىلىرىمنى كېچىدە ئېلىپ چىقىپ كەتكىن، سىلەر ھەقىقەتەن قوغلىنىسىلەر.');
INSERT INTO "saleh" VALUES(44,24,'44:24',' دېڭىزنى تىپتىنچ (يەنى سەن ئۆتۈپ بولغاندىن كېيىن قۇرۇق ھالىتى بويىچە) قويغىن، ئۇلار ھەقىقەتەن غەرق قىلىنغۇچى قوشۇندۇر».');
INSERT INTO "saleh" VALUES(44,25,'44:25','ئۇلار (غەرق بولۇپ) نۇرغۇن باغلارنى، بۇلاقلارنى، زىرائەتلەرنى، چىرايلىق جايلارنى قالدۇردى[25ـ26].');
INSERT INTO "saleh" VALUES(44,26,'44:26','ئۇلار (غەرق بولۇپ) نۇرغۇن باغلارنى، بۇلاقلارنى، زىرائەتلەرنى، چىرايلىق جايلارنى قالدۇردى[25ـ26].');
INSERT INTO "saleh" VALUES(44,27,'44:27','ئۇلار بەھرىمەن بولۇۋاتقان نېمەتلەرنى قالدۇردى.');
INSERT INTO "saleh" VALUES(44,28,'44:28','شۇنداق قىلىپ، ئۇلارنى باشقىلارغا (يەنى بەنى ئىسرائىلغا) مىراس قىلىپ بەردۇق.');
INSERT INTO "saleh" VALUES(44,29,'44:29',' ئۇلارغا ئاسمانمۇ، زېمىنمۇ يىغلىمىدى، ئۇلارغا مۆھلەتمۇ بېرىلمىدى.');
INSERT INTO "saleh" VALUES(44,30,'44:30',' شەك ـ شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى خار قىلغۇچى ئازابتىن – پىرئەۋن (ئازابى) دىن قۇتقۇزدۇق، پىرئەۋن ھەقىقەتەن مۇتەكەببىر ئىدى، ھەددىدىن ئاشقۇچىلاردىن ئىدى[30 ـ 31].');
INSERT INTO "saleh" VALUES(44,31,'44:31',' شەك ـ شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىنى خار قىلغۇچى ئازابتىن – پىرئەۋن (ئازابى) دىن قۇتقۇزدۇق، پىرئەۋن ھەقىقەتەن مۇتەكەببىر ئىدى، ھەددىدىن ئاشقۇچىلاردىن ئىدى[30 ـ 31].');
INSERT INTO "saleh" VALUES(44,32,'44:32',' ئۇلارنى بىلىپ تۇرۇپ (ئۆز زامانىدىكى) جاھان ئەھلىدىن ئارتۇق قىلدۇق. ');
INSERT INTO "saleh" VALUES(44,33,'44:33',' ئۇلارغا (يەنى تەپەككۇر قىلغۇچىلارغا) روشەن سىناق بار نۇرغۇن مۆجىزىلەرنى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(44,34,'44:34',' بۇلار (يەنى قۇرەيش كۇففارلىرى) چوقۇم ئېيتىدۇ:');
INSERT INTO "saleh" VALUES(44,35,'44:35','«پەقەت بىرلا قېتىم ئۆلىمىز، قايتا تىرىلمەيمىز، ئەگەر (ئاخىرەت بار دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، بىزنىڭ ئاتا ـ بوۋىلىرىمىزنى (تىرىلدۈرۈپ) ئەكېلىپ بېقىڭلار»[35ـ36].');
INSERT INTO "saleh" VALUES(44,36,'44:36','«پەقەت بىرلا قېتىم ئۆلىمىز، قايتا تىرىلمەيمىز، ئەگەر (ئاخىرەت بار دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، بىزنىڭ ئاتا ـ بوۋىلىرىمىزنى (تىرىلدۈرۈپ) ئەكېلىپ بېقىڭلار»[35ـ36].');
INSERT INTO "saleh" VALUES(44,37,'44:37',' ئۇلار (يەنى مۇشرىكلار) كۈچلۈكمۇ ياكى تۈببە قەۋمىمۇ؟ ئۇلاردىن ئىلگىرى ئۆتكەن (نۇرغۇن) قەۋملەرنى ھالاك قىلدۇق. چۈنكى ئۇلار گۇناھكار ئىدى.');
INSERT INTO "saleh" VALUES(44,38,'44:38','ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئويناپ (يەنى بىكارغا) ياراتقىنىمىز يوق.');
INSERT INTO "saleh" VALUES(44,39,'44:39',' ئۇلارنى بىز پەقەت ھەق ياراتتۇق ۋەلېكىن ئىنسانلارنىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(44,40,'44:40','باتىل بىلەن ھەقنى ئايرىيدىغان كۈن (يەنى قىيامەت كۈنى) ئىنسانلارنىڭ ھەممىسىنىڭ (ھېسابى ئۈچۈن) تەيىنلەنگەن ۋاقىتتۇر. ');
INSERT INTO "saleh" VALUES(44,41,'44:41',' ئۇ كۈندە دوست دوستقا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار ياردەمگىمۇ ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(44,42,'44:42',' پەقەت ئاللاھ رەھمەت قىلغان كىشى بۇنىڭدىن مۇستەسنا. ئاللاھ ھەقىقەتەن غالىبتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(44,43,'44:43',' زەققۇم دەرىخى ھەقىقەتەن گۇناھكارلارنىڭ تامىقىدۇر[43ـ44].');
INSERT INTO "saleh" VALUES(44,44,'44:44',' زەققۇم دەرىخى ھەقىقەتەن گۇناھكارلارنىڭ تامىقىدۇر[43ـ44].');
INSERT INTO "saleh" VALUES(44,45,'44:45',' ئۇ ئېرىتىلگەن مىستەك (قىزىق) دۇر، ئۇ قورساقلاردا قايناقسۇدەك قاينايدۇ[45ـ46].');
INSERT INTO "saleh" VALUES(44,46,'44:46',' ئۇ ئېرىتىلگەن مىستەك (قىزىق) دۇر، ئۇ قورساقلاردا قايناقسۇدەك قاينايدۇ[45ـ46].');
INSERT INTO "saleh" VALUES(44,47,'44:47',' (دوزاخقا مۇئەككەل پەرىشتىلەرگە) «ئۇنى تۇتۇپ سۆرەپ دوزاخنىڭ ئوتتۇرىسىغا ئېلىپ بېرىڭلار، ئاندىن ئۇنىڭ بېشىغا قايناقسۇ قۇيۇپ ئازابلاڭلار» دېيىلىدۇ[47ـ48]. ');
INSERT INTO "saleh" VALUES(44,48,'44:48',' (دوزاخقا مۇئەككەل پەرىشتىلەرگە) «ئۇنى تۇتۇپ سۆرەپ دوزاخنىڭ ئوتتۇرىسىغا ئېلىپ بېرىڭلار، ئاندىن ئۇنىڭ بېشىغا قايناقسۇ قۇيۇپ ئازابلاڭلار» دېيىلىدۇ[47ـ48]. ');
INSERT INTO "saleh" VALUES(44,49,'44:49','(ئۇنىڭغا خارلاش ۋە مەسخىرە قىلىش يۈزىسىدىن) «(بۇ ئازابنى) تېتىغىن، سەن ھەقىقەتەن ئىززەتلىك ئۇلۇغ زات ئىدىڭ.');
INSERT INTO "saleh" VALUES(44,50,'44:50',' بۇ (ئازاب) ھەقىقەتەن (ھايات ۋاقتىڭلاردا) سىلەر شەكلەنگەن نەرسىدۇر» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(44,51,'44:51','تەقۋادارلار ھەقىقەتەن بىخەتەر جايدا بولىدۇ.');
INSERT INTO "saleh" VALUES(44,52,'44:52',' باغلاردا، بۇلاقلارنىڭ ئارىسىدا بولىدۇ.');
INSERT INTO "saleh" VALUES(44,53,'44:53',' ئۇلار قېلىن، يۇپقا يىپەك كىيىملەرنى كىيىپ بىر ـ بىرىگە قاراپ ئولتۇرۇشىدۇ.');
INSERT INTO "saleh" VALUES(44,54,'44:54',' (ئۇلارنى تۈرلۈك ھۆرمەتلەر بىلەن) مۇشۇنداق ئىكرام قىلدۇق، شەھلا كۆزلۈك ھۆرلەرنى ئۇلارغا جۈپ قىلىمىز.');
INSERT INTO "saleh" VALUES(44,55,'44:55','ئۇلار جەننەتتە (مەيدە ئېغىرلىشىش ۋە كېسەللىكلەردىن) ئەمىن بولغان ھالدا (خىزمەتچىلەردىن) ھەممە مېۋىلەرنى (كەلتۈرۈشنى) تەلەپ قىلىدۇ. ');
INSERT INTO "saleh" VALUES(44,56,'44:56',' ئۇلار ئىلگىرىكى (يەنى دۇنيادىكى) ئۆلۈمدىن باشقا (جەننەتتە) ھېچقانداق ئۆلۈمنى تېتىمايدۇ، ئاللاھ ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ.');
INSERT INTO "saleh" VALUES(44,57,'44:57',' (بۇ) پەرۋەردىگارىڭنىڭ مەرھەمىتىدىندۇر، بۇ زور مۇۋەپپەقىيەتتۇر.');
INSERT INTO "saleh" VALUES(44,58,'44:58',' ئۇلارنىڭ ۋەز ـ نەسىھەت ئېلىشى ئۈچۈن قۇرئاننى سېنىڭ تىلىڭ بىلەن ئاسانلاشتۇرۇپ بەردۇق.');
INSERT INTO "saleh" VALUES(44,59,'44:59',' (ئى مۇھەممەد! ئۇلارغا چۈشىدىغان ئازابنى) كۈتكىن، شۈبھىسىزكى، ئۇلار (سېنىڭ ھالاك بولۇشۇڭنى) كۈتكۈچىدۇر.');
INSERT INTO "saleh" VALUES(45,1,'45:1','ھا، مىم.');
INSERT INTO "saleh" VALUES(45,2,'45:2',' (بۇ) كىتاب غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(45,3,'45:3',' ئاسمانلاردا ۋە زېمىندا مۆمىنلەر ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى كۆرسىتىدىغان) ھەقىقەتەن نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(45,4,'45:4',' سىلەرنىڭ يارىتىلىشىڭلاردا ۋە (ئاللاھ زېمىندا) تارقىتىۋەتكەن جانىۋارلاردا (ئاللاھنىڭ قۇدرىتىگە) جەزمەن ئىشىنىدىغان قەۋم ئۈچۈن نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(45,5,'45:5',' كېچە بىلەن كۈندۈزنىڭ نۆۋەتلىشىپ تۇرۇشىدا، ئاللاھنىڭ بۇلۇتتىن (كۆكتىن) يامغۇر ياغدۇرۇپ ئۇنىڭ بىلەن ئۆلگەن زېمىننى تىرىلدۈرۈشىدە ۋە شاماللارنىڭ يۆنىلىشىنى ئۆزگەرتىپ تۇرۇشىدا، چۈشىنىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن دەلىللەر بار.');
INSERT INTO "saleh" VALUES(45,6,'45:6',' ئەنە شۇلار ئاللاھنىڭ ئايەتلىرىدۇر. (ئى مۇھەممەد!) ئۇنى ساڭا ھەقلىق بىلەن تىلاۋەت قىلىپ بېرىمىز. ئۇلار (يەنى مەككە كۇففارلىرى) ئاللاھنى ۋە ئۇنىڭ ئايەتلىرىنى قويۇپ قايسى سۆزگە ئىشىنىدۇ؟.');
INSERT INTO "saleh" VALUES(45,7,'45:7',' ھەر بىر گۇناھكار يالغانچىغا ۋاي!.');
INSERT INTO "saleh" VALUES(45,8,'45:8',' ئۇ ئاللاھنىڭ ئايەتلىرىنىڭ ئۆزىگە ئوقۇپ بېرىلىۋاتقانلىقىنى ئاڭلاپ تۇرىدۇ، ئاندىن تەكەببۇرلۇق قىلغان ھالدا گويا ئۇنى ئاڭلىمىغاندەك (كۇفرىدا) چىڭ تۇرىدۇ (گۇمراھلىقتا تېخىمۇ ئەزۋەيلەيدۇ)، ئۇنىڭغا قاتتىق ئازاب بىلەن خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(45,9,'45:9',' ئۇ بىزنىڭ ئايەتلىرىمىزدىن بىرەر نەرسىنى بىلگەن چاغدا (يەنى ئۇنىڭغا قۇرئان ئايەتلىرى يەتكەن چاغدا)، ئۇنى مەسخىرە قىلىدىغان نەرسە قىلىۋالىدۇ، ئەنە شۇلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(45,10,'45:10',' ئۇلارنى ئالدىدا جەھەننەم (كۈتۈپ تۇرىدۇ)، ئۇلارنىڭ ئېرىشكەن نەرسىلىرى (يەنى دۇنيادا تاپقان ماللىرى ۋە بالىلىرى) ۋە ئاللاھنى قويۇپ دوست تۇتقان نەرسىلىرى (يەنى بۇتلىرى) ئۇلارغا ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلار چوڭ ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(45,11,'45:11',' بۇ (قۇرئان ئۇنىڭغا ئىشەنگەن ۋە ئۇنىڭغا ئەگەشكەن كىشىگە كامىل) ھىدايەتتۇر، پەرۋەردىگارىنىڭ ئايەتلىرىنى ئىنكار قىلغانلار ئەڭ قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(45,12,'45:12','اللاھنىڭ ئەمرى بىلەن دېڭىزدا كېمىلەرنىڭ يۈرۈشى ئۈچۈن، سىلەرنىڭ ئاللاھنىڭ پەزلىدىن تەلەپ قىلىشىڭلار ئۈچۈن (يەنى تىجارەت قىلىشىڭلار، بېلىق تۇتۇشۇڭلار، دېڭىز ئاستىدىن ئۈنچە ـ مەرۋايىتلارنى سۈزۈۋېلىشىڭلار ئۈچۈن) ۋە (ئاللاھقا) شۈكۈر قىلىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە دېڭىزنى بويسۇندۇرۇپ بەردى.');
INSERT INTO "saleh" VALUES(45,13,'45:13',' ئاللاھ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسىنى (كامالى) پەزلىدىن سىلەرگە بويسۇندۇرۇپ بەردى، بۇنىڭدا ھەقىقەتەن (قۇدرىتى ئىلاھىييەنى) تەپەككۈر قىلىدىغان قەۋم ئۈچۈن (ئاللاھنىڭ قۇدرىتى ۋە بىرلىكىنى كۆرسىتىدىغان) (روشەن) دەلىللەر بار.');
INSERT INTO "saleh" VALUES(45,14,'45:14',' مۆمىنلەرگە ئېيتقىنكى، ئاللاھ ھەر قەۋمنىڭ قىلمىشلىرىغا يارىشا جازا بېرىدىغانلىقى ئۈچۈن، ئۇلار ئاللاھنىڭ كۈنلىرىنى ئۈمىد قىلمايدىغانلارنى (يەنى ئاللاھنىڭ ئازابىدىن قورقمايدىغان كۇففارلارنىڭ يەتكۈزگەن ئەزىيەتلىرىنى) كەچۈرسۇن.');
INSERT INTO "saleh" VALUES(45,15,'45:15','كىمكى (دۇنيادا) ياخشى ئىش قىلسا، ئۇ ئۆزىنىڭ پايدىسى ئۈچۈن قىلىدۇ، كىمكى يامان ئىش قىلسا، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن قىلىدۇ، ئاندىن كېيىن پەرۋەردىگارىڭلارنىڭ دەرگاھىغا قايتۇرۇلىسىلەر (ئاللاھ ياخشىلىققا مۇكاپات، يامانلىققا جازا بېرىدۇ).');
INSERT INTO "saleh" VALUES(45,16,'45:16',' شەك ـ شۈبھىسىزكى، بىز ئىسرائىل ئەۋلادىغا كىتابنى (يەنى تەۋراتنى)، ھېكمەتنى ۋە پەيغەمبەرلىكنى ئاتا قىلدۇق، ئۇلارغا پاك نەرسىلەرنى رىزىق قىلىپ بەردۇق، ئۇلارنى (زامانىدىكى) جاھان ئەھلىدىن ئۈستۈن قىلدۇق.');
INSERT INTO "saleh" VALUES(45,17,'45:17',' ئۇلارغا دىن ئىشىدا روشەن دەلىللەرنى بەردۇق، پەقەت ئۇلارغا ئىلىم (يەنى دىننىڭ راستلىقىغا قەتئى دەلىللەر) كەلگەندىن كېيىنلا، ئاندىن ئۇلار ئۆزئارا ھەسەت قىلىشىپ ئىختىلاپ قىلىشتى. پەرۋەردىگارىڭ ھەقىقەتەن قىيامەت كۈنى ئۇلار ئىختىلاپ قىلىشقان ئىشلار ئۈستىدە ھۆكۈم چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(45,18,'45:18','ئاندىن سېنى بىز (ئى مۇھەممەد!) دىن ئىشىدا (روشەن) بىر يولدا قىلدۇق، شۇ يولغا ئەگەشكىن، بىلمەيدىغانلارنىڭ نەپسى خاھىشلىرىغا ئەگەشمىگىن.');
INSERT INTO "saleh" VALUES(45,19,'45:19',' شۈبھىسىزكى، (ئۇلارنىڭ گۇمراھلىقىغا ماسلىشىدىغان بولساڭ) ئۇلار سەندىن ئاللاھنىڭ (ئازابى) دىن ھېچ نەرسىنى دەپئى قىلالمايدۇ، شۈبھىسىزكى، (دۇنيادا) زالىملارنىڭ بەزىسى بەزىسىگە دوستتۇر، ئاللاھ تەقۋادارلارنىڭ دوستىدۇر.');
INSERT INTO "saleh" VALUES(45,20,'45:20',' بۇ (قۇرئان) ئىنسانلار ئۈچۈن روشەن پاكىتلاردۇر، ھىدايەتتۇر، (ئۆلگەندىن كېيىن تىرىلىشكە) جەزمەن ئىشەنگەن قەۋم ئۈچۈن رەھمەتتۇر.');
INSERT INTO "saleh" VALUES(45,21,'45:21',' يامان ئىشلارنى قىلغانلار (يەنى گۇناھكار كۇففارلار) ئۆزلىرىنى ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلاردەك قىلىشىمىزنى، ھاياتلىقتا، ماماتلىقتا ئۇلار بىلەن ئوخشاش قىلىشىمىزنى ئويلامدۇ؟ (يەنى ئۇلارنى ئوخشاش قىلىشىمىز مۇمكىن ئەمەس) ئۇلارنىڭ چىقارغان ھۆكمى نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(45,22,'45:22',' ئاللاھ ئاسمانلارنى ۋە زېمىننى ھەقلىق بىلەن ياراتتى، ھەر ئىنساننىڭ قىلمىشىغا يارىشا جازا ياكى مۇكاپات بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(45,23,'45:23',' (ئى مۇھەممەد!) ماڭا نەپسى خاھىشنى ئىلاھ قىلىۋالغان، ئاللاھ ئازدۇرغان، (ھەقنى) بىلىپ تۇرغان، ئاللاھ قۇلىقىنى ۋە قەلبىنى پېچەتلىۋەتكەن ۋە كۆزىنى پەردىلىگەن ئادەمنى ئېيتىپ بەرگىن، ئاللاھ ئازدۇرغاندىن كېيىن، ئۇنى كىم ھىدايەت قىلالايدۇ؟ سىلەر (بۇلاردىن) ۋەز ـ نەسىھەت ئالمامسىلەر؟');
INSERT INTO "saleh" VALUES(45,24,'45:24',' ئۇلار (يەنى قىيامەتنى ئىنكار قىلغۇچىلار): «ھايات دېگەن پەقەت دۇنيادىكى ھاياتىمىزدۇر، ئۆلىمىز ۋە تىرىلىمىز (يەنى تۇغۇلىمىز). پەقەت زاماننىڭ ئۆتۈشى بىلەن يوق بولىمىز» (دەيدۇ). ئۇلار بۇ خۇسۇستا ھېچقانداق مەلۇماتقا ئىگە ئەمەس، ئۇلار پەقەت گۇمان بىلەن سۆزلەيدۇ.');
INSERT INTO "saleh" VALUES(45,25,'45:25','ئۇلارغا بىزنىڭ ئايەتلىرىمىز ئوچۇق ئوقۇپ بېرىلسە، ئۇلارنىڭ پاكىتى پەقەت «ئەگەر راستچىل بولساڭلار، ئاتا ـ بوۋىلىرىمىزنى كەلتۈرۈپ بېقىڭلار (يەنى تىرىلدۈرۈڭلار)» دېيىشتىن ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(45,26,'45:26',' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئاللاھ سىلەرنى تىرىلدۈرەلەيدۇ، ئاندىن (ئەجىلىڭلار پۈتكەندە) ۋاپات قىلدۇرالايدۇ، ئاندىن شەك ـ شۈبھىسىز قىيامەت كۈنىدە سىلەرنى يىغىدۇ، لېكىن كىشىلەرنىڭ تولىسى (ئاللاھنىڭ قۇدرىتىنى) بىلمەي (قايتا تىرىلىشنى ئىنكار قىلدى)».');
INSERT INTO "saleh" VALUES(45,27,'45:27',' ئاسمانلار بىلەن زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، قىيامەت قايىم بولغان كۈندە كاپىرلار زىيان تارتىدۇ.');
INSERT INTO "saleh" VALUES(45,28,'45:28',' ھەر ئۈممەتنى (قورقۇنچنىڭ قاتتىقلىقىدىن) تىزلانغان ھالدا كۆرىسەن، ھەر ئۈممەت ئۆزىنىڭ نامە ـ ئەمالىغا چاقىرىلىدۇ، (ئۇلارغا) «بۈگۈن قىلمىشىڭلارغا يارىشا مۇكاپاتقا ئېرىشىسىلەر ياكى جازاغا ئۇچرايسىلەر» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(45,29,'45:29',' بۇ بىزنىڭ كىتابىمىز (يەنى نامە ـ ئەمال) سىلەرگە ھەق بىلەن سۆزلەيدۇ، بىز قىلمىشىڭلارنى يازدۇرۇپ تۇرغان ئىدۇق (يەنى پەرىشتىلەرنى ئەمەللىرىڭلارنى يېزىپ تۇرۇشقا بۇيرۇيتتۇق).');
INSERT INTO "saleh" VALUES(45,30,'45:30',' ئىمان ئېيتقان ۋە ياخشى ئىشلارنى قىلغانلارغا كەلسەك، پەرۋەردىگارى ئۇلارنى ئۆزىنىڭ رەھمىتى دائىرىسىگە كىرگۈزىدۇ، بۇ روشەن مۇرادقا يېتىشتۇر.');
INSERT INTO "saleh" VALUES(45,31,'45:31','كاپىرلارغا كەلسەك، (ئۇلارنى ئەيىبلەش يۈزىسىدىن) ئۇلارغا: «مېنىڭ ئايەتلىرىم سىلەرگە ئوقۇپ بېرىلمىدىمۇ؟ سىلەر چوڭچىلىق قىلىپ (ئۇنىڭغا ئىمان ئېيتىشتىن) باش تارتتىڭلار، سىلەر گۇناھقا چۆمگەن قەۋم ئىدىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(45,32,'45:32',' (سىلەرگە) «ئاللاھنىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر، قىيامەتتە (يەنى قىيامەتنىڭ بولۇشىدا) ھېچ شەك يوق» دېيىلسە، سىلەر (ھەددىدىن زىيادە سەركەشلىكىڭلاردىن): «قىيامەتنىڭ نېمە ئىكەنلىكىنى بىلمەيمىز، پەقەت ئۇنى بولۇشى مۇمكىن دەپ گۇمان قىلىمىز، (ئۇنىڭغا) بىز ھەقىقىي ئىشەنمەيمىز» دېدىڭلار.');
INSERT INTO "saleh" VALUES(45,33,'45:33',' (ئاخىرەتتە) ئۇلارنىڭ قىلغان يامان ئىشلىرى ئاشكارا بولىدۇ، ئۇلار (دۇنيادىكى چاغلىرىدا) مەسخىرە قىلغان ئازاب ئۇلارنى ئورىۋالىدۇ.');
INSERT INTO "saleh" VALUES(45,34,'45:34',' (ئۇلارغا) دېيىلىدۇ: «سىلەرنىڭ مۇشۇ كۈندىكى مۇلاقاتنى ئۇنتۇغانلىقىڭلارغا ئوخشاش، بۈگۈن سىلەرنى ئۇنتۇيمىز (يەنى سىلەرنى ئازابقا تاشلاپ قويۇپ ئۇنتۇپ قالغاندەك مۇئامىلە قىلىمىز)، سىلەرنىڭ جايىڭلار دوزاخ بولىدۇ، سىلەرگە (سىلەرنى ئازابتىن قۇتقۇزىدىغان) ھېچقانداق ياردەمچىمۇ بولمايدۇ.');
INSERT INTO "saleh" VALUES(45,35,'45:35',' بۇ شۇنىڭ ئۈچۈنكى، سىلەر ئاللاھنىڭ ئايەتلىرىنى مەسخىرە قىلىدىغان نەرسە قىلىۋالدىڭلار ۋە سىلەرنى دۇنيا تىرىكچىلىكى ئالدىدى». بۈگۈن ئۇلار دوزاختىن چىقىرىلمايدۇ، ئۇلاردىن (تەۋبە قىلىش ۋە ئىبادەت قىلىش بىلەن) ئاللاھنى رازى قىلىشمۇ تەلەپ قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(45,36,'45:36','جىمى ھەمدۇسانا ئاسمانلارنىڭ پەرۋەردىگارى، زېمىننىڭ پەرۋەردىگارى ۋە ئالەملەرنىڭ پەرۋەردىگارى ئاللاھقا خاستۇر!');
INSERT INTO "saleh" VALUES(45,37,'45:37',' ئاسمانلاردىكى ۋە زېمىندىكى ئۇلۇغلۇق ئاللاھقا خاستۇر، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(46,1,'46:1','ھا، مىم.');
INSERT INTO "saleh" VALUES(46,2,'46:2',' (بۇ قۇرئان) غالىب، ھېكمەت بىلەن ئىش قىلغۇچى ئاللاھ تەرىپىدىن نازىل قىلىنغاندۇر. ');
INSERT INTO "saleh" VALUES(46,3,'46:3','ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ھەق ئاساستا مۇئەييەن مۇددەتكىچە (يەنى قىيامەتكىچە قالىدىغان) قىلىپ ياراتتۇق. كاپىرلار ئاگاھلاندۇرۇلغان نەرسىلەردىن يۈز ئۆرۈگۈچىدۇر.');
INSERT INTO "saleh" VALUES(46,4,'46:4',' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر ئاللاھنى قويۇپ چوقۇنۇۋاتقان بۇتلىرىڭلارنىڭ (ئەھۋالىنى) ئېيتىپ بېرىڭلار، ئۇلار زېمىننىڭ قايسى قىسمىنى ياراتقانلىقىنى ماڭا كۆرسىتىڭلار، ياكى ئاسمانلارنى يارىتىشتا ئۇلارنىڭ ئاللاھ بىلەن ئورتاقچىلىقى بارمۇ؟ بۇ (قۇرئان) دىن ئىلگىرى (سىلەرگە نازىل قىلىنغان) كىتاب ياكى (ئىلگىرىكىلەردىن) قالغان ئىلىم بولسا ماڭا ئېلىپ كېلىڭلار، ئەگەر سىلەر راستچىل بولساڭلار».');
INSERT INTO "saleh" VALUES(46,5,'46:5',' ئاللاھنى قويۇپ قىيامەتكىچە دۇئانى ئىجابەت قىلىشقا قادىر بولالمايدىغان بۇتلارغا چوقۇنىدىغان ئادەمدىنمۇ گۇمراھ ئادەم بارمۇ؟ بۇتلار ئۇلارنىڭ دۇئاسىدىن غاپىلدۇر.');
INSERT INTO "saleh" VALUES(46,6,'46:6',' (قىيامەت كۈنى) ئىنسانلار (ھېساب بېرىش ئۈچۈن) توپلانغان چاغدا، بۇتلار ئۇلارغا (يەنى بۇتپەرەسلەرگە) دۈشمەن بولىدۇ، ئۇلار تەرىپىدىن ئىبادەت قىلىنغانلىقىنى ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(46,7,'46:7',' ئۇلارغا بىزنىڭ روشەن ئايەتلىرىمىز تىلاۋەت قىلىنسا، كاپىرلار ئۆزلىرىگە كەلگەن ھەقىقەتنى (يەنى قۇرئاننى) باھالاپ: «بۇ روشەن سېھىردۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(46,8,'46:8','ياكى ئۇلار قۇرئاننى ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئۆزى توقۇغان دىېيىشەمدۇ؟ ئېيتقىنكى، «ئەگەر ئۇنى مەن توقۇغان بولسام، سىلەر مەندىن ئاللاھنىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمايسىلەر، سىلەرنىڭ قۇرئان توغرىسىدىكى تۆھمەتلىرىڭلارنى ئاللاھ ئوبدان بىلىدۇ، ئاللاھ مەن بىلەن سىلەرنىڭ ئاراڭلاردا گۇۋاھ بولۇشقا يېتەرلىكتۇر (يەنى مېنىڭ راستچىللىقىمغا ۋە سىلەرنىڭ يالغانچىلىقىڭلارغا گۇۋاھ بولىدۇ)، ئاللاھ (تەۋبە قىلغۇچىنى) مەغپىرەت قىلغۇچىدۇر، (مۆمىن بەندىلىرىگە) ناھايىتى مېھرىباندۇر». ');
INSERT INTO "saleh" VALUES(46,9,'46:9',' ئېيتقىنكى، «مەن (ئاللاھ ئىنسانلارنى تەۋھىدكە دەۋەت قىلىش ئۈچۈن ئەۋەتكەن) تۇنجى پەيغەمبەر ئەمەسمەن، (مەندىن ئىلگىرىكى نۇرغۇن پەيغەمبەرلەر ئېلىپ كەلگەن ۋەھىي بىلەن كەلدىم، نېمىشقا مېنى ئىنكار قىلىسىلەر؟) ماڭا ۋە سىلەرگە نېمە قىلىنىدىغانلىقىنى ئۇقمايمەن (چۈنكى ئاللاھنىڭ تەقدىرىنى ئالدىن ئۇققىلى بولمايدۇ)، مەن پەقەت ماڭا ۋەھىي قىلىنغان نەرسىگىلا ئەگىشىمەن، مەن پەقەت (سىلەرنى ئاللاھنىڭ ئازابىدىن) ئوچۇق ئاگاھلاندۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(46,10,'46:10',' (ئى مۇھەممەد! مۇشرىكلارغا) ئېيتقىنكى، «سىلەر ئېيتىپ بېقىڭلارچۇ! قۇرئان ئاللاھ تەرىپىدىن نازىل بولغان تۇرسا، سىلەر ئۇنىڭغا ئىشەنمىسەڭلار (ھالىڭلار قانداق بولىدۇ؟) ئىسرائىل ئەۋلادىدىن بىر گۇۋاھچى (يەنى ئابدۇللاھ ئىبن سالام) قۇرئاننىڭ ئاللاھ تەرىپىدىن نازىل بولغانلىقىغا گۇۋاھلىق بېرىپ ئىمان كەلتۈرسە، سىلەر تەكەببۇرلۇق قىلىپ (ئىماندىن) باش تارتساڭلار (ھالىڭلار قانداق بولىدۇ؟) شۈبھىسىزكى، ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ».');
INSERT INTO "saleh" VALUES(46,11,'46:11','كاپىرلار مۆمىنلەرگە: «ئەگەر (قۇرئان بىلەن ئىسلام دىنى) ياخشى بولىدىغان بولسا ئىدى، ئۇلار (يەنى ئاجىز مۇسۇلمانلار) ئۇنىڭغا بىزدىن بۇرۇن ئىشەنمىگەن بولاتتى» دېدى، ئۇلار قۇرئان بىلەن ھىدايەت تاپمىغانلىقلىرى ئۈچۈن، «بۇ قەدىمقى ئويدۇرمىدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(46,12,'46:12','قۇرئاندىن ئىلگىرى مۇسانىڭ كىتابى (يەنى تەۋرات) ئەھلى جاھانغا پېشۋا ۋە رەھمەت ئىدى. بۇ ئەرەب تىلىدىكى كىتابتۇر، (ئىلگىرىكى كىتابلارنى) تەستىق قىلغۇچىدۇر، (ئاللاھ ئۇنى) زالىملارنى ئاگاھلاندۇرۇش ئۈچۈن، ياخشى ئىش قىلغۇچى مۆمىنلەرگە (جەننەت بىلەن) خۇشخەۋەر بېرىش ئۈچۈن نازىل قىلدى.');
INSERT INTO "saleh" VALUES(46,13,'46:13',' «پەرۋەردىگارىمىز ئاللاھتۇر» دېگەن، ئاندىن توغرا يولدا بولغانلارغا، شۈبھىسىزكى، (كەلگۈسىدىن) غەم قىلىش، (كەتكەنگە) قايغۇرۇش يوقتۇر.');
INSERT INTO "saleh" VALUES(46,14,'46:14',' ئەنە شۇلار ئەھلى جەننەتتۇر، ئۇلار جەننەتتە مەڭگۈ قالىدۇ، (بۇ) ئۇلارنىڭ قىلغان (ياخشى) ئەمەللىرىنى مۇكاپاتلاش ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(46,15,'46:15',' بىز ئىنساننى ئاتا ـ ئانىسىغا ياخشىلىق قىلىشقا بۇيرۇدۇق. ئىنساننى ئانىسى مۇشەققەت بىلەن قورساق كۆتۈرۈپ، مۇشەققەت بىلەن تۇغدى. ئۇنىڭغا قورساق كۆتۈرۈش مۇددىتى ۋە ئۇنى سۈتتىن ئايرىش مۇددىتى 30 ئايدۇر. تاكى ئۇ (بوۋاق ئۆسۈپ) كۈچ ـ قۇۋۋەتكە تولۇپ، 40 ياشقا يەتكەندە، (ئۇ): «پەرۋەردىگارىم! سېنىڭ ماڭا ۋە ئاتا ـ ئانامغا بەرگەن نېمىتىڭگە شۈكۈر قىلىشنى ۋە سەن رازى بولىدىغان ياخشى ئىشنى قىلىشىمنى ماڭا ئىلھام قىلغىن، مەن ئۈچۈن مېنىڭ ئەۋلادىمنى تۈزىگىن (يەنى مېنىڭ ئەۋلادىمنى ياخشى ئادەملەر قىلغىن)، مەن ھەقىقەتەن ساڭا (جىمى گۇناھلاردىن) تەۋبە قىلدىم، مەن ھەقىقەتەن مۇسۇلمانلاردىندۇرمەن» دەيدۇ.');
INSERT INTO "saleh" VALUES(46,16,'46:16',' ئەنە شۇنداق كىشىلەرنىڭ ياخشى ئىشلىرىنى قوبۇل قىلىپ، يامان ئىشلىرىنى كەچۈرۈم قىلىمىز، ئۇلار (ئاللاھ يامان ئىشلىرىنى كەچۈرگەنلەر بىلەن) جەننەتتە بولىدۇ، (بۇ) ئۇلارغا قىلىنغان راست ۋەدىدۇر.');
INSERT INTO "saleh" VALUES(46,17,'46:17',' بەزى ئادەم (يەنى ئاتا ـ ئانىسى ئىمانغا دەۋەت قىلغان فاجىر بالا): «ۋاي ـ ۋۇي! سىلەر مېنى قايتا تىرىلىشتىن قورقۇتۇۋاتامسىلەر، (مەندىن ئىلگىرى) نۇرغۇن ئۈممەتلەر ئۆتتى (ئۇلاردىن ھېچ ئادەم تىرىلدۈرۈلگىنى يوق)» دەيدۇ، ئۇ ئىككىسى ئاللاھقا پەرياد قىلىپ: «ۋاي ساڭا! ئىمان ئېيتقىن، ئاللاھنىڭ ۋەدىسى ھەقىقەتەن ھەقتۇر» دەيدۇ. ئۇ: «بۇ پەقەت ئاۋالقىلاردىن قالغان ئەپسانىلەردۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(46,18,'46:18','ئەنە شۇنداق كىشىلەرگە (ئاللاھنىڭ ئازاب قىلىش) سۆزى ھەقلىق بولدى، ئۇلار بولسا ئۇلاردىن ئىلگىرى ئۆتكەن ئىنسانلاردىن ۋە جىنلاردىن بولغان ئۈممەتلەر قاتارىغا كىرگۈزۈلدى، ئۇلار ھەقىقەتەن زىيان تارتقۇچى بولدى. ');
INSERT INTO "saleh" VALUES(46,19,'46:19','(مۆمىنلەرنىڭ ۋە كاپىرلارنىڭ) ھەممىسىنىڭ قىلغان ئەمەللىرىگە يارىشا دەرىجىلىرى بولىدۇ، ئۇلارنىڭ ئەمەللىرىنىڭ ساۋابى ۋە جازاسى (كەم ـ زىيادە قىلىنماي) تولۇق بېرىلىدۇ، ئۇلارغا زۇلۇم قىلىنمايدۇ. ');
INSERT INTO "saleh" VALUES(46,20,'46:20',' كاپىرلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا ئەيىبلەش يۈزىسىدىن ئېيتىلىدۇ): «سىلەر لەززەتلىرىڭلارنى ھاياتىي دۇنيادا بەھرىمەن بولۇپ تۈگەتتىڭلار، سىلەر زېمىندا ھەقسىز رەۋىشتە چوڭچىلىق قىلىپ، ئىماندىن باش تارتقانلىقىڭلار ۋە ئاللاھنىڭ ئىتائىتىدىن چىققانلىقىڭلار تۈپەيلىدىن، بۈگۈن خار قىلغۇچى ئازاب بىلەن جازالىنىسىلەر».');
INSERT INTO "saleh" VALUES(46,21,'46:21',' (ئى مۇھەممەد!) ئادنىڭ قېرىندىشى (يەنى ھۇد ئەلەيھىسسالام) نىڭ ئۆز قەۋمى بىلەن بولغان قىسسەسىنى بايان قىلغىن، ئۆز ۋاقتىدا ئۇ ئەھقاف (دېگەن يەر) دىكى قەۋمىنى (ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇردى، ئۇنىڭدىن (يەنى ھۇدتىن) ئىلگىرى ۋە ئۇنىڭدىن كېيىن نۇرغۇن پەيغەمبەرلەر ئۆتتى. ئۇ (يەنى ھۇد) (قەۋمىنى ئاگاھلاندۇرۇپ) ئېيتتى: «سىلەر پەقەت ئاللاھقىلا ئىبادەت قىلىڭلار، مەن ھەقىقەتەن سىلەرنىڭ (ئاللاھتىن غەيرىيگە ئىبادەت قىلساڭلار) بۈيۈك كۈننىڭ ئازابىغا قېلىشىڭلاردىن قورقىمەن».');
INSERT INTO "saleh" VALUES(46,22,'46:22',' ئۇلار: «(ئى ھۇد!) سەن بىزنى ئىلاھلىرىمىزدىن توسقىلى كەلدىڭمۇ؟ ئەگەر سەن (سۆزۈڭدە) راستچىللاردىن بولساڭ، بىزگە بىزنى قورقۇتقان ئازابنى كەلتۈرگىن» دېدى.');
INSERT INTO "saleh" VALUES(46,23,'46:23',' ئۇ: «(سىلەرگە ئازابنىڭ قاچان كېلىشىنى) پەقەت ئاللاھ بىلىدۇ، مەن سىلەرگە ئەۋەتىلگەن نەرسىنى تەبلىغ قىلىمەن ۋەلېكىن مەن سىلەرنى نادان قەۋم كۆرىمەن» دېدى. ');
INSERT INTO "saleh" VALUES(46,24,'46:24',' ئۇلار ئۆزلىرىنىڭ ۋادىسىغا سۈرۈلۈۋاتقان بۇلۇتنى كۆرۈپ: «مانا بۇ بىزگە يامغۇر ياغدۇرغۇچى بۇلۇتتۇر» دېيىشتى. (ھۇد ئېيتتى) «ئۇنداق ئەمەس، ئۇ سىلەر بالدۇرراق ئىشقا ئېشىشىنى تەلەپ قىلغان نەرسىدۇر، ئۇ بىر بوران بولۇپ، ئۇنىڭ ئىچىدە قاتتىق ئازاب بار.');
INSERT INTO "saleh" VALUES(46,25,'46:25',' ئۇ پەرۋردىگارىنىڭ بىر ئەمرى بىلەن ھەممە نەرسىنى ھالاك قىلىدۇ» (يەنى بوران كېلىپ ئۇلارنى ھالاك قىلدى)، شۇنىڭ بىلەن ئۇلارنىڭ جايلىرىنىڭ (يەنى تۇرغان ماكانلىرىنىڭ ئىزىدىن) باشقا نەرسە كۆرۈنمەيدىغان بولۇپ قالدى، گۇناھكار قەۋمنى مۇشۇنداق جازالايمىز.');
INSERT INTO "saleh" VALUES(46,26,'46:26','بىز ئۇلارنى (يەنى ئاد قەۋمىنى) (پۇل ـ مال، كۈچ ـ قۇۋۋەت جەھەتتە) سىلەرنى قادىر قىلمىغان نەرسىلەرگە قادىر قىلدۇق. (ئۇلارنىڭ شۇ نېمەتلەرگە شۈكۈر قىلىپ، بۇ ئارقىلىق نېمەتلەرنى بەرگۈچى ئاللاھنى تونۇشى ئۈچۈن) ئۇلارغا قۇلاق، كۆز، قەلبلەرنى بەردۇق، ئۇلار ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن، ئۇلارنىڭ قۇلاقلىرى، كۆزلىرى، دىللىرى ئۇلاردىن (ئاللاھنىڭ ئازابىدىن) ھېچ نەرسىنى دەپئى قىلمىدى، ئۇلار مەسخىرە قىلغان ئازاب ئۇلارغا نازىل بولدى.');
INSERT INTO "saleh" VALUES(46,27,'46:27',' (ئى مەككە ئاھالىسى!) بىز ھەقىقەتەن سىلەرنىڭ ئەتراپىڭلاردىكى شەھەرلەرنى ھالاك قىلدۇق، ئۇلارنى (كۇفرىدىن) يانسۇن دەپ ئايەتلەرنى تەكرار بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(46,28,'46:28',' (ئۇلارغا ئازاب كەلگەندە) ئۇلارنىڭ خۇداغا يېقىن قىلىدۇ، دەپ ئاللاھتىن باشقا تۇتقان مەبۇدلىرى ئۇلارغا نېمىشقا ياردەم قىلمىدى؟ بەلكى ئۇلارنىڭ مەبۇدلىرى غايىب بولدى، بۇ (يەنى بۇتلار بىزنى خۇداغا يېقىن قىلىدۇ دېگەن سۆزى) ئۇلارنىڭ ئويدۇرمىسىدۇر ۋە (ئاللاھقا) چاپلىغان بوھتانلىرىدۇر.');
INSERT INTO "saleh" VALUES(46,29,'46:29',' (ئى مۇھەممەد!) ئۆز ۋاقتىدا بىر تۈركۈم جىنلارنى ساڭا قۇرئان تىڭشاشقا ئەۋەتتۇق، ئۇلار قۇرئان تىلاۋىتىگە ھازىر بولغاندا (بىر ـ بىرىگە): «جىم تۇرۇڭلار» دېيىشتى، قۇرئان تىلاۋەت قىلىنىپ بولغاندىن كېيىن ئۇلار ئۆز قەۋمىگە ئاگاھلاندۇرغۇچى بولۇپ قايتىشتى.');
INSERT INTO "saleh" VALUES(46,30,'46:30',' ئۇلار ئېيتتى: «ئى قەۋمىمىز! ھەقىقەتەن بىز مۇسادىن كېيىن نازىل بولغان، ئۇنىڭدىن ئىلگىرىكى كىتابلارغا ئۇيغۇن كېلىدىغان، ھەق دىنغا ۋە توغرا يولغا باشلايدىغان كىتابنى (يەنى قۇرئاننى) تىڭشىدۇق».');
INSERT INTO "saleh" VALUES(46,31,'46:31','«ئى قەۋمىمىز! ئاللاھقا دەۋەت قىلغۇچى (يەنى مۇھەممەد ئەلەيھىسسالام) نىڭ دەۋىتىنى قوبۇل قىلىڭلار ۋە ئاللاھقا ئىمان ئېيتىڭلار، ئاللاھ بەزى گۇناھلارنى مەغپىرەت قىلىدۇ، سىلەرنى قاتتىق ئازابتىن ساقلايدۇ».');
INSERT INTO "saleh" VALUES(46,32,'46:32',' كىمكى ئاللاھقا (ئىمان ئېيتىشقا) دەۋەت قىلغۇچىنىڭ (يەنى رەسۇلۇللاھنىڭ) دەۋىتىنى قوبۇل قىلمىسا، ئازابتىن قېچىپ قۇتۇلالمايدۇ، ئاللاھتىن باشقا (ئۇنى ئاللاھنىڭ ئازابىدىن ساقلىغۇچى) ياردەمچىلەر بولمايدۇ، ئۇلار (يەنى رەسۇلۇللاھنىڭ دەۋىتىنى قوبۇل قىلمىغۇچىلار) ئوپئوچۇق گۇمراھلىقتىدۇر.');
INSERT INTO "saleh" VALUES(46,33,'46:33',' ئۇلار (يەنى ئۆلگەندىن كېيىن تىرىلىشنى ئىنكار قىلغۇچى كۇففارلار) بىلمەمدۇكى، ئاسمانلارنى ۋە زېمىننى ياراتقان ۋە ئۇلارنى يارىتىشتا چارچاپ قالمىغان ئاللاھ ئۆلۈكلەرنى تىرىلدۈرۈشكە قادىر ئەمەسمۇ؟ دۇرۇس، ئۇ ھەقىقەتەن ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(46,34,'46:34',' كاپىرلار دوزاخقا توغرىلىنىدىغان كۈندە (ئۇلارغا): «بۇ (يەنى سىلەر تېتىۋاتقان ئازاب) راست ئەمەسمۇ؟ » (دېيىلىدۇ) ئۇلار: «پەرۋەردىگارىمىز بىلەن قەسەمكى، ئەلۋەتتە راستتۇر» دەيدۇ، ئاللاھ (ئۇلارغا): «كاپىر بولغانلىقىڭلار تۈپەيلىدىن ئازابنى تېتىڭلار» دەيدۇ.');
INSERT INTO "saleh" VALUES(46,35,'46:35',' (ئى مۇھەممەد!) سەن (مۇشرىكلارنىڭ سالغان جاپاسىغا) ئىرادىلىك پەيغەمبەرلەر سەۋر قىلغاندەك سەۋر قىلغىن، ئۇلارغا بولىدىغان ئازابقا ئالدىراپ كەتمىگىن. ئۇلار (ئاخىرەتتە) ۋەدە قىلىنغان ئازابنى كۆرگەندە، (ئۇلار دۇنيادا) گويا كۈندۈزدە بىر دەملا تۇرغاندەك ھېس قىلىدۇ، (بۇ قۇرئان سىلەرگە) تەبلىغدۇر، پەقەت پاسىق قەۋملا ھالاك قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(47,1,'47:1','كاپىر بولغان ۋە (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقانلارنىڭ (ياخشى) ئەمەللىرىنى ئاللاھ بىكار قىلىۋېتىدۇ. ');
INSERT INTO "saleh" VALUES(47,2,'47:2','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنىڭ، مۇھەممەدكە نازىل قىلىنغان قۇرئانغا ـ قۇرئان ئۇلارنىڭ پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتتۇر – ئىمان ئېيتقانلارنىڭ گۇناھلىرىنى ئاللاھ ئۆچۈرۈپ تاشلايدۇ، ئۇلارنىڭ ھالىنى ياخشىلايدۇ (شۇنىڭ بىلەن ئۇلار گۇناھ قىلمايدۇ).');
INSERT INTO "saleh" VALUES(47,3,'47:3',' بۇ شۇنىڭ ئۈچۈندۇركى، كاپىرلار باتىلغا (يەنى شەيتانغا) ئەگەشتى، مۆمىنلەر بولسا، پەرۋەردىگارى تەرىپىدىن كەلگەن ھەقىقەتكە (يەنى قۇرئانغا) ئەگەشتى، ئاللاھ ئىنسانلارغا ئۇلارنىڭ ئەھۋالىنى شۇنداق بايان قىلىدۇ.');
INSERT INTO "saleh" VALUES(47,4,'47:4',' سىلەر كۇففارلارغا (جەڭ مەيدانىدا) ئۇچراشقان چېغىڭلاردا، ئۇلارنى ئۆلتۈرۈڭلار، سىلەر ئۇلارنى قىرىپ (مەغلۇپ قىلىپ، ئۇلاردا سىلەرگە قارشىلىق كۆرسىتىدىغان كۈچ قالمىغان چاغدا ئۇلارنى ئۆلتۈرۈشتىن توختاپ)، ئۇلارنى ئەسىر ئېلىڭلار، ئاندىن ئۇلارنى ئېھسان قىلىش يۈزىسىدىن قويۇپ بېرىڭلار ياكى فىدىيە ئېلىپ قويۇپ بېرىڭلار، تاكى ئۇرۇش ئۆزىنىڭ ئېغىر يۈكىنى تاشلىغانغا قەدەر (يەنى ئۇرۇش توختىغانغا قەدەر) ئىش مانا شۇنداقتۇر. ئەگەر ئاللاھ خالىسا ئەلۋەتتە (سىلەرنى ئۇرۇشقا تەكلىپ قىلماستىنلا) ئۇلارنى جازالىغان بولاتتى، لېكىن ئاللاھ بەزىڭلارنى بەزىڭلار بىلەن سىناش ئۈچۈن (يەنى ئىمانىڭلارنى سىناش ئۈچۈن جىھادقا ئەمر قىلدى)، ئاللاھنىڭ يولىدا ئۆلتۈرۈلگەنلەرنىڭ (يەنى شېھىتلەرنىڭ) ئەمەللىرىنى ئاللاھ بىكار قىلىۋەتمەيدۇ.');
INSERT INTO "saleh" VALUES(47,5,'47:5',' ئاللاھ ئۇلارنى ھىدايەت قىلىدۇ ۋە ھالىنى ياخشىلايدۇ.');
INSERT INTO "saleh" VALUES(47,6,'47:6','ئاللاھ ئۇلارنى جەننەتكە كىرگۈزىدۇ، ئاللاھ ئۇنى ئۇلارغا تونۇتتى (يەنى جەننەتكە كىرگەن ئادەم ئۆزىنىڭ ئۇ يەردىكى جايىنى ئىلگىرى كۆرگەندەكلا بىلىدۇ).');
INSERT INTO "saleh" VALUES(47,7,'47:7',' ئى مۆمىنلەر! سىلەر ئاللاھقا (يەنى ئاللاھنىڭ دىنىغا) ياردەم بەرسەڭلار، ئاللاھ سىلەرگە (دۈشمىنىڭلارغا قارشى) ياردەم بېرىدۇ، قەدىمىڭلارنى (ئۇرۇش مەيدانلىرىدا) بەرقارار قىلىدۇ.');
INSERT INTO "saleh" VALUES(47,8,'47:8','كاپىرلارنى ئاللاھ ھالاك قىلىدۇ، ئۇلارنىڭ (ياخشى) ئەمەللىرىنى بىكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(47,9,'47:9',' بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار ئاللاھ نازىل قىلغان كىتابنى (يەنى قۇرئاننى) يامان كۆرۈش بىلەن، ئۆزلىرىنىڭ ئەمەللىرىنى بەربات قىلدى.');
INSERT INTO "saleh" VALUES(47,10,'47:10','ئۇلار زېمىندا سەير قىلىپ ئىلگىرىكىلەرنىڭ ئاقىۋىتىنىڭ قانداق بولغانلىقىنى كۆزەتمىدىمۇ؟ ئاللاھ ئۇلارنى ھالاك قىلدى. كاپىرلىرىمۇ شۇنداق ئاقىۋەتكە قالىدۇ.');
INSERT INTO "saleh" VALUES(47,11,'47:11',' بۇ شۇنىڭ ئۈچۈندۇركى، ئاللاھ مۆمىنلەرنىڭ مەدەتكارىدۇر، كاپىرلارغا مەدەتكار يوقتۇر.');
INSERT INTO "saleh" VALUES(47,12,'47:12','شۈبھىسىزكى، ئاللاھ ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كاپىرلار (ھاياتى دۇنيادا ئۇنىڭ لەززەتلىرىدىن) بەھرىمەن بولىدۇ ۋە چاھارپايلاردەك يەپ ـ ئىچىدۇ، (ئاخىرەتتە) ئۇلارنىڭ جايى دوزاخ بولىدۇ.');
INSERT INTO "saleh" VALUES(47,13,'47:13',' سېنى ھەيدەپ چىقارغان شەھەرنىڭ (يەنى مەككىنىڭ) ئاھالىسىدىن كۈچلۈك بولغان نۇرغۇن شەھەرلەرنىڭ ئاھالىسىنى ھالاك قىلدۇق، ئۇلارغا ھېچ قانداق ياردەم قىلغۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(47,14,'47:14',' پەرۋەردىگارى تەرىپىدىن بولغان روشەن دەلىللەرگە ئاساسلانغان ئادەم (يەنى مۆمىن) ئۆزىنىڭ يامان ئەمەلى چىرايلىق كۆرسىتىلگەن ۋە نەپسى خاھىشلىرىغا ئەگەشكەن ئادەمگە (يەنى كاپىرغا) ئوخشامدۇ؟.');
INSERT INTO "saleh" VALUES(47,15,'47:15','تەقۋادارلارغا ۋەدە قىلىنغان جەننەتنىڭ سۈپىتى شۇكى، ئۇ يەردە رەڭگى ئۆزگەرمىگەن سۇدىن ئۆستەڭلار، تەمى ئۆزگەرمىگەن سۈتتىن ئۆستەڭلار، ئىچكۈچىلەرگە لەززەت بېغىشلايدىغان مەيدىن ئۆستەڭلار ۋە ساپ ھەسەلدىن ئۆستەڭلار بولىدۇ، ئۇلارغا جەننەتتە بەھرىمەن بولىدىغان تۈرلۈك مېۋىلەر بولىدۇ ۋە پەرۋەردىگارى تەرىپىدىن مەغپىرەت بولىدۇ، (مۇنداق تەقۋادارلار) دوزاختا مەڭگۈ قالىدىغان، قايناقسۇ بىلەن سۇغىرىلىپ (قىزىقلىقىدىن) ئۈچەيلىرى پارە ـ پارە قىلىنىدىغانلار بىلەن ئوخشاشمۇ؟.');
INSERT INTO "saleh" VALUES(47,16,'47:16',' ئۇلاردىن (يەنى مۇناپىقلاردىن) بەزى كىشىلەر سېنىڭ سۆزۈڭگە قۇلاق سالىدۇ، ئۇلار سېنىڭ يېنىڭدىن چىققاندا، ئىلىم بېرىلگەن كىشىلەرگە (يەنى ساھابىلەرنىڭ ئۆلىمالىرىغا مەسخىرە قىلىش يۈزىسىدىن): «بايا ئۇ (يەنى مۇھەممەد) نېمە دېدى؟» دەيدۇ، ئەنە شۇلار ئاللاھ دىللىرىنى پېچەتلىۋەتكەنلەردۇر، نەپسى خاھىشلىرىغا ئەگەشكەنلەردۇر.');
INSERT INTO "saleh" VALUES(47,17,'47:17',' ھىدايەت تاپقانلارنى ئاللاھ تېخىمۇ ھىدايەت قىلىدۇ، ئۇلارغا تەقۋادارلىقنىڭ مۇكاپاتىنى بېرىدۇ.');
INSERT INTO "saleh" VALUES(47,18,'47:18',' ئۇلار (يەنى مەككە كۇففارلىرى) پەقەت قىيامەتنىڭ ئۇلارغا تۇيۇقسىز كېلىشىنىلا كۈتىدۇ، شۈبھىسىزكى، قىيامەتنىڭ ئالامەتلىرى كەلدى، ئۇلارغا قىيامەت كەلگەندە ئۇلار قانداقمۇ ۋەز ـ نەسىھەت ئالىدۇ (يەنى بۇ چاغدا ۋەز ـ نەسىھەت ئالغانلىقىنىڭ پايدىسى بولمايدۇ).');
INSERT INTO "saleh" VALUES(47,19,'47:19','بىلگىنكى، ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، گۇناھىڭ ئۈچۈن، ئەر ـ ئايال مۆمىنلەر ئۈچۈن مەغپىرەت تىلىگىن، ئاللاھ سىلەرنىڭ (بۇ دۇنيادىكى) ھەرىكىتىڭلارنى ۋە (ئاخىرەتتىكى) جايىڭلارنى بىلىدۇ (شۇڭا ئاخىرەتلىك ئۈچۈن تەييارلىنىڭلار).');
INSERT INTO "saleh" VALUES(47,20,'47:20',' مۆمىنلەر (جىھادنى تەلەپ قىلىش يۈزىسىدىن): «نېمىشقا (جىھاد ئەمرى قىلىنغان) بىرەر سۈرە نازىل قىلىنمىدى؟» دەيدۇ، جىھاد زىكرى قىلىنغان ئېنىق سۈرە نازىل قىلىنغاندا، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلارنىڭ) ساڭا ئۆلۈم ئالدىدا (ئۆلۈمدىن قورقۇپ) ئايلىنىپ كەتكەن ئادەمدەك قاراۋاتقانلىقىنى كۆرىسەن، (ئى مۇھەممەد!) (ئۇلارنىڭ ساڭا) ئىتائەت قىلىشى ۋە چىرايلىق سۆز قىلىشى ئۇلار ئۈچۈن ئەلۋەتتە ياخشى ئىدى، ئۇرۇش قارار قىلىنغان چاغدا، ئۇلار ئاللاھقا سادىق بولۇشسا ئەلۋەتتە ئۇلار ئۈچۈن ياخشى ئىدى[20 ـ 21].');
INSERT INTO "saleh" VALUES(47,21,'47:21',' مۆمىنلەر (جىھادنى تەلەپ قىلىش يۈزىسىدىن): «نېمىشقا (جىھاد ئەمرى قىلىنغان) بىرەر سۈرە نازىل قىلىنمىدى؟» دەيدۇ، جىھاد زىكرى قىلىنغان ئېنىق سۈرە نازىل قىلىنغاندا، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلارنىڭ) ساڭا ئۆلۈم ئالدىدا (ئۆلۈمدىن قورقۇپ) ئايلىنىپ كەتكەن ئادەمدەك قاراۋاتقانلىقىنى كۆرىسەن، (ئى مۇھەممەد!) (ئۇلارنىڭ ساڭا) ئىتائەت قىلىشى ۋە چىرايلىق سۆز قىلىشى ئۇلار ئۈچۈن ئەلۋەتتە ياخشى ئىدى، ئۇرۇش قارار قىلىنغان چاغدا، ئۇلار ئاللاھقا سادىق بولۇشسا ئەلۋەتتە ئۇلار ئۈچۈن ياخشى ئىدى[20 ـ 21].');
INSERT INTO "saleh" VALUES(47,22,'47:22','سىلەر (ئىسلام) دىن يۈز ئۆرۈسەڭلار زېمىندا بۇزغۇنچىلىق قىلارسىلەرمۇ ۋە سىلە ـ رەھىمنى ئۈزۈپ قويارسىلەرمۇ؟.');
INSERT INTO "saleh" VALUES(47,23,'47:23','ئەنە شۇنداق كىشىلەرنى ئاللاھ رەھمىتىدىن يىراق قىلدى، ئۇلارنى (ھەقنى ئاڭلاشتىن) گاس قىلدى، (ھىدايەت يولىنى كۆرۈشتىن) كور قىلدى.');
INSERT INTO "saleh" VALUES(47,24,'47:24',' ئۇلار (ھەقنى تونۇش ئۈچۈن) قۇرئاننى پىكىر قىلمامدۇ؟ بەلكى ئۇلارنىڭ دىللىرىدا قۇلۇپ بار (شۇڭا ئۇلار چۈشەنمەيدۇ).');
INSERT INTO "saleh" VALUES(47,25,'47:25','توغرا يول ئېنىق بولغاندىن كېيىن ئارقىسىغا قايتىۋالغانلار (يەنى ئىماندىن كۇفرىغا قايتقانلار)غا (ئۇلارنىڭ بۇ ئىشىنى) شەيتان چىرايلىق كۆرسەتتى، (شەيتان ئارزۇ ۋە ئەجەلنىڭ ئۇزۇنلۇقى بىلەن) ئۇلارنىڭ كۆڭلىنى تىندۇردى.');
INSERT INTO "saleh" VALUES(47,26,'47:26',' بۇ (يەنى شەيتاننىڭ ئۇلارنى ئازدۇرۇشى) شۇنىڭ ئۈچۈندۇركى، ئۇلار ئاللاھ نازىل قىلغان نەرسىنى يامان كۆرگەن كاپىرلارغا: «بىز بەزى ئىشلاردا سىلەرگە ئىتائەت قىلىمىز» دېدى، ئاللاھ ئۇلارنىڭ سىرلىرىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(47,27,'47:27',' (ئازاب) پەرىشتىلىرى ئۇلارنىڭ يۈزلىرىگە ۋە ئارقىلىرىغا ئۇرغان ھالدا ئۇلارنىڭ جانلىرىنى ئېلىۋاتقاندا (ئۇلارنىڭ ھالى) قانداق بولىدۇ؟.');
INSERT INTO "saleh" VALUES(47,28,'47:28',' بۇ (ئازاب) شۇنىڭ ئۈچۈندۇركى، ئۇلار ئاللاھنى دەرغەزەپ قىلىدىغان نەرسىلەرگە ئەگەشتى، ئاللاھنىڭ رازىلىقىنى (يەنى ئاللاھنى رازى قىلىدىغان ئىشلارنى) ياقتۇرمىدى، شۇنىڭ بىلەن ئاللاھ ئۇلارنىڭ ئەمەللىرىنى بىكار قىلدى.');
INSERT INTO "saleh" VALUES(47,29,'47:29',' دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار) ئاللاھ ئۆزلىرىنىڭ (ئىسلامغا بولغان) دۈشمەنلىكىنى ئاشكارىلىمايدۇ دەپ ئويلامدۇ؟');
INSERT INTO "saleh" VALUES(47,30,'47:30',' ئەگەر بىز خالىساق، ئۇلارنى چوقۇم ساڭا بىلدۈرەتتۇق، چوقۇم ئۇلارنىڭ سىماسىدىن تونۇيتتۇڭ، سەن ئەلۋەتتە سۆزىنىڭ ئۇسلۇبىدىن ئۇلارنى تونۇيسەن، ئاللاھ سىلەرنىڭ ئەمەللىرىڭلارنى بىلىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(47,31,'47:31',' بىز سىلەرنى ئەلۋەتتە (جىھادقا ئەمر قىلىش ۋە مۇشەققەتلىك ئىشلارغا تەكلىپ قىلىش بىلەن) سىنايمىز، تاكى سىلەرنىڭ ئىچىڭلاردىن (ئاللاھنىڭ يولىدا) جىھاد قىلغۇچىلارنى ۋە (جىھادنىڭ مۇشەققەتلىرىگە) چىداشلىق بەرگۈچىلەرنى بىلگەنگە، سىرلىرىڭلاردىن ۋاقىپ بولغانغا قەدەر.');
INSERT INTO "saleh" VALUES(47,32,'47:32',' شۈبھىسىزكى، كاپىر بولغانلار، (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقانلار، توغرا يول (يەنى رەسۇلۇللاھنىڭ ھەق ئىكەنلىكى) ئېنىق بولغاندىن كېيىن پەيغەمبەر بىلەن دۈشمەنلەشكەنلەر ئاللاھقا قىلچە زىيان يەتكۈزەلمەيدۇ، ئاللاھ ئۇلارنىڭ (سەدىقە بەرگەنگە ئوخشاش) ئەمەللىرىنى بىكار قىلىدۇ (شۇنىڭ بىلەن ئاخىرەتتە ئۇنىڭ ساۋابىغا ئېرىشەلمەيدۇ).');
INSERT INTO "saleh" VALUES(47,33,'47:33',' ئى مۆمىنلەر! ئاللاھقا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەمەللىرىڭلارنى (كۇفرى، نىفاق ۋە رىيا بىلەن) بىكار قىلىۋەتمەڭلار.');
INSERT INTO "saleh" VALUES(47,34,'47:34',' شۈبھىسىزكى، كاپىر بولغان ۋە (كىشىلەرنى) ئاللاھنىڭ يولىدىن توسقانلار، ئاندىن كاپىر پىتى ئۆلگەنلەرنى ئاللاھ ھەرگىز مەغپىرەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(47,35,'47:35',' (كاپىرلار بىلەن جەڭ قىلغان چېغىڭلاردا) بوشاپ قالماڭلار، (كاپىرلارنى) سۈلھكە چاقىرماڭلار، ھالبۇكى، سىلەر غالىپتۇرسىلەر، ئاللاھ سىلەر بىلەن بىرگىدۇر، ئاللاھ ھەرگىز ئەمەلىڭلارنىڭ (ساۋابىنى) كېمەيتىۋەتمەيدۇ. ');
INSERT INTO "saleh" VALUES(47,36,'47:36',' دۇنيا تىرىكچىلىكى پەقەت ئويۇن ۋە تاماشادىن ئىبارەتتۇر، ئەگەر سىلەر ئىمان ئېيتساڭلار ۋە تەقۋادارلىق قىلساڭلار (ئاللاھ) سىلەرگە ئەجرىڭلارنى بېرىدۇ، مال ـ مۈلكۈڭلارنىڭ ھەممىسىنى (بېرىشنى) سورىمايدۇ (پەقەت زاكاتنى بېرىشىڭلارنى سورايدۇ).');
INSERT INTO "saleh" VALUES(47,37,'47:37',' ئەگەر ئۇ سىلەردىن مال ـ مۈلكۈڭلارنى جىددى سورىسا، بېخىللىق قىلىسىلەر، بۇ بېخىللىق سىلەرنىڭ (ئىسلامغا بولغان) ئاداۋىتىڭلارنى ئاشكارىلايدۇ.');
INSERT INTO "saleh" VALUES(47,38,'47:38',' سىلەر شۇنداق كىشىلەركى، ئاللاھنىڭ يولىدا (پۇل ـ مال) سەرپ قىلىشقا چاقىرىلساڭلار، سىلەرنىڭ ئاراڭلاردا بېخىللىق قىلىدىغانلار بار، كىمكى بېخىللىق قىلىدىكەن، ئۇ ئۆزىنىڭ زىيىنى ئۈچۈن بېخىللىق قىلىدۇ، ئاللاھ (سىلەرنىڭ مال ـ مۈلكۈڭلاردىن) بىھاجەتتۇر، سىلەر بولساڭلار (ئاللاھقا) موھتاجسىلەر، ئەگەر سىلەر (ئاللاھنىڭ تائىتىدىن) يۈز ئۆرۈسەڭلار، ئاللاھ سىلەرنىڭ ئورنۇڭلارغا باشقا بىر قەۋمنى ئالماشتۇرىدۇ، ئۇلار سىلەرگە ئوخشاش بولمايدۇ (بەلكى ئاللاھقا ئىتائەت قىلىدۇ).');
INSERT INTO "saleh" VALUES(48,1,'48:1','ئاللاھنىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق[1ـ3].وَيَنْصُرَكَ اللَّهُ نَصْرًا عَزِيزًا');
INSERT INTO "saleh" VALUES(48,2,'48:2','ئاللاھنىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق[1ـ3].');
INSERT INTO "saleh" VALUES(48,3,'48:3','ئاللاھنىڭ سېنىڭ ئىلگىرىكى ۋە كېيىنكى گۇناھلىرىڭنى كەچۈرۈشى ئۈچۈن، ساڭا بەرگەن نېمىتىنى مۇكەممەللەشتۈرۈشى ئۈچۈن، سېنى توغرا يولغا باشلىشى ئۈچۈن، ساڭا كۈچلۈك ياردەم بېرىشى ئۈچۈن، ساڭا بىز ھەقىقەتەن روشەن غەلىبە ئاتا قىلدۇق[1ـ3].');
INSERT INTO "saleh" VALUES(48,4,'48:4',' مۆمىنلەرنىڭ ئىمانىغا ئىمان قوشۇلۇشى ئۈچۈن، ئاللاھ ئۇلارنىڭ دىللىرىغا تەمكىنلىكنى چۈشۈردى، ئاسمانلارنىڭ ۋە زېمىننىڭ (پەرىشتىلەردىن، جىنلاردىن، ھايۋانلاردىن ۋە تەبىئەت ھادىسىلىرىدىن بولغان) قوشۇنلىرى ئاللاھنىڭدۇر، ئاللاھ (خەلقنىڭ ئەھۋالىنى) بىلىپ تۇرغۇچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(48,5,'48:5','(مۆمىنلەرنىڭ دىللىرىغا ئاللاھنىڭ تەمكىنلىك چۈشۈرۈشى) ئەر ـ ئايال مۆمىنلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان، ئۇلار مەڭگۈ قالىدىغان جەننەتلەرگە كىرگۈزۈشى ۋە ئاللاھنىڭ ئۇلارنىڭ گۇناھلىرىنى كەچۈرۈشى ئۈچۈندۇر، ئاللاھنىڭ دەرگاھىدا، بۇ زور مۇۋەپپەقىيەتتۇر.');
INSERT INTO "saleh" VALUES(48,6,'48:6',' (مۆمىنلەرنىڭ دىللىرىغا ئاللاھنىڭ تەمكىنلىك چۈشۈرۈشى) مۇناپىق ۋە مۇناپىقەلەرگە، مۇشرىك ۋە مۇشرىكەلەرگە ئازاب قىلىش ئۈچۈندۇر، ئۇلار ئاللاھقا قارىتا (ئاللاھ پەيغەمبىرىگە ۋە مۆمىنلەرگە ياردەم) بەرمەيدۇ دەيدىغان يامان ئويلاردا بولدى، (ئۇلار مۆمىنلەرگە يەتسۇن دېگەن) ھالاكەت ئۇلارنىڭ ئۆزلىرىگە يېتىدۇ. ئاللاھ ئۇلارغا غەزەپ قىلدى، ئۇلارنى رەھمىتىدىن يىراق قىلدى، ئۇلارغا جەھەننەمنى تەييارلىدى، جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(48,7,'48:7',' ئاسمانلارنىڭ ۋە زېمىننىڭ قوشۇنلىرى ئاللاھنىڭدۇر، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(48,8,'48:8',' بىز ھەقىقەتەن سېنى (قىيامەت كۈنى خەلققە) گۇۋاھلىق بەرگۈچى، (مۆمىنلەرگە جەننەت بىلەن) خۇشخەۋەر بەرگۈچى، (كاپىرلارنى ئازابتىن) ئاگاھلاندۇرغۇچى قىلىپ ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(48,9,'48:9','سىلەرنىڭ ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان ئېيتىشىڭلار ئۈچۈن، ئۇنى (يەنى پەيغەمبەرنى) ئۇلۇغلىشىڭلار ۋە ھۆرمەتلىشىڭلار ئۈچۈن، ئەتىگەندە ۋە ئاخشامدا تەسبىھ ئېيتىشىڭلار ئۈچۈن (بىز پەيغەمبەرنى ئەۋەتتۇق).');
INSERT INTO "saleh" VALUES(48,10,'48:10',' شۈبھىسىزكى، (ئى مۇھەممەد! ھۇدەيبىيەدە) ساڭا (رىزۋان) بەيئىتىنى قىلغانلار (ھەقىقەتتە) ئاللاھقا بەيئەت قىلغان بولىدۇ، ئاللاھنىڭ قولى ئۇلارنىڭ قولىنىڭ ئۈستىدىدۇر (يەنى ئاللاھ ئۇلارنىڭ قىلغان بەيئىتىنى كۆرۈپ تۇرغۇچىدۇر، ئاللاھ ئۇلارنى بەيئەت قىلغانلىقى ئۈچۈن مۇكاپاتلايدۇ)، كىمكى ئەھدىنى بۇزىدىكەن، ئەھدىنى بۇزغانلىقىنىڭ زېيىنى ئۇنىڭ ئۆزىگە بولىدۇ، ئاللاھ بىلەن قىلغان ئەھدىسىگە ۋاپا قىلغانلارغا ئاللاھ بۈيۈك ئەجىر ئاتا قىلىدۇ.');
INSERT INTO "saleh" VALUES(48,11,'48:11',' (ھۇدەيبىيەگە چىقماي) قېلىپ قالغان (مۇناپىق) ئەئرابىيلار ساڭا: «بىز ماللىرىمىز، بالا ـ چاقىلىرىمىز بىلەن بولۇپ قېلىپ (سەن بىلەن چىقالمىدۇق)، بىز ئۈچۈن مەغپىرەت تەلەپ قىلغىن» دەيدۇ، ئۇلارنىڭ تىلى بىلەن ئېيتقىنى دىلىدىكى سۆز ئەمەس (يەنى ئۇلار يالغان ئۆزرە ئېيتىدۇ). ئېيتقىنكى، «ئەگەر ئاللاھ سىلەرگە بىرەر زىياننى ياكى پايدىنى ئىرادە قىلسا، كىم سىلەر ئۈچۈن ئاللاھنىڭ ئالدىدا بىرەر نەرسىگە دال بولالايدۇ؟ (يەنى كىم سىلەردىن ئاللاھنىڭ خاھىشى ۋە قازاسىنى توسىيالايدۇ؟) ئۇنداق ئەمەس (يەنى ئىش سىلەر ئويلىغاندەك ئەمەس)، ئاللاھ سىلەرنىڭ قىلمىشىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(48,12,'48:12',' بەلكى سىلەر، پەيغەمبەر ۋە مۆمىنلەر ئائىلىسىگە مەڭگۈ قايتمايدۇ، دەپ گۇمان قىلدىڭلار، بۇ (گۇمراھلىق) دىللىرىڭلاردا چىرايلىق تۇيۇلدى، سىلەر يامان گۇماندا (يەنى ئۇلار ئۆلتۈرۈلۈپ بىرىمۇ ساق قايتمايدۇ دەيدىغان ئويدا) بولدۇڭلار، سىلەر ھالاك بولغۇچى قەۋم بولدۇڭلار».');
INSERT INTO "saleh" VALUES(48,13,'48:13',' كىمكى ئاللاھ غا ۋە ئۇنىڭ پەيغەمبىرىگە ئىشەنمەيدىكەن(ئۇ كاپىردۇر)، شۈبھىسىزكى، بىز كاپىرلارغا دوزاخنى تەييارلىدۇق. ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھ خالىغان ئادەمگە مەغپىرەت قىلىدۇ، خالىغان ئادەمگە ئازاب قىلىدۇ، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر[14-13].');
INSERT INTO "saleh" VALUES(48,14,'48:14','(ھۇدەيبىيەگە چىقماي) قېلىپ قالغانلار غەنىيمەت ئېلىش ئۈچۈن چىققان ۋاقىتلىرىدا: «بىزمۇ سىلەر بىلەن چىقايلى» دەيدۇ، ئۇلار ئاللاھنىڭ (خەيبەر غەنىيمىتى ھۇدەيبىيەگە چىققانلارغا خاس دېگەن) سۆزىنى ئۆزگەرتمەكچى بولىدۇ. (ئۇلارغا) ئېيتقىنكى، «بىز بىلەن چىقىشىڭلارغا ھەرگىز بولمايدۇ، ئاللاھ ئىلگىرى مۇشۇنداق دېگەن». ئۇلار: «ئۇنداق ئەمەس (يەنى ئاللاھ ئۇنداق دېمىگەن)، سىلەر بىزگە ھەسەت قىلىۋاتىسىلەر» دەيدۇ. ئۇنداق ئەمەس، ئۇلار (ئاللاھنىڭ كالامىنى) پەقەت يۈزەگىنە چۈشىنىدۇ.');
INSERT INTO "saleh" VALUES(48,15,'48:15','(ھۇدەيبىيىگە چىقماي) قېلىپ قالغان ئەئرابىيلارغا: «سىلەر جەڭگىۋار بىر قەۋم بىلەن (ئۇرۇشۇشقا) چاقىرىلىسىلەر، ياكى ئۇلار (ئۇرۇشسىزلا) مۇسۇلمان بولىدۇ (ئۇلار بىلەن ئۇرۇشمايسىلەر)، ئەگەر سىلەر (بۇيرۇققا) بويسۇنساڭلار، ئاللاھ سىلەرگە چىرايلىق ئەجىر ئاتا قىلدۇ، ئەگەر سىلەر ئىلگىرى قېلىپ قالغاندەك، يەنە قېلىپ قالساڭلار، ئاللاھ سىلەرگە قاتتىق ئازاب قىلىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(48,16,'48:16','(ھۇدەيبىيىگە چىقماي) قېلىپ قالغان ئەئرابىيلارغا: «سىلەر جەڭگىۋار بىر قەۋم بىلەن (ئۇرۇشۇشقا) چاقىرىلىسىلەر، ياكى ئۇلار (ئۇرۇشسىزلا) مۇسۇلمان بولىدۇ (ئۇلار بىلەن ئۇرۇشمايسىلەر)، ئەگەر سىلەر (بۇيرۇققا) بويسۇنساڭلار، ئاللاھ سىلەرگە چىرايلىق ئەجىر ئاتا قىلدۇ، ئەگەر سىلەر ئىلگىرى قېلىپ قالغاندەك، يەنە قېلىپ قالساڭلار، ئاللاھ سىلەرگە قاتتىق ئازاب قىلىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(48,17,'48:17','(جىھادقا چىقمىسا) ئەماغا گۇناھ بولمايدۇ، توكۇرغىمۇ گۇناھ بولمايدۇ، كېسەلگىمۇ گۇناھ بولمايدۇ، كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىدىكەن، ئۇنى ئاللاھ ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، كىمكى (ئۆزرىسىز جىھادتىن) باش تارتىدىكەن، ئاللاھ ئۇنى (دۇنيادا خورلۇق بىلەن، ئاخىرەتتە ئوت بىلەن) قاتتىق ئازابلايدۇ.');
INSERT INTO "saleh" VALUES(48,18,'48:18',' ئاللاھ مۆمىنلەردىن ھەقىقەتەن رازى بولدى، (ئى مۇھەممەد!) ئۆز ۋاقتىدا ئۇلار (ھۇدەيبىيەدە) دەرەخ (سايىسى) ئاستىدا ساڭا بەيئەت قىلدى. ئاللاھ ئۇلارنىڭ دىلىدىكىنى (يەنى راستلىق بىلەن ۋاپانى) بىلىدۇ، ئاللاھ ئۇلارغا (ئۇلار بەيئەت قىلىۋاتقاندا) تەمكىنلىك چۈشۈرۈپ بەردى ۋە ئۇلارنى يېقىن غەلىبە (يەنى خەيبەرنىڭ پەتھى قىلىنىشى) بىلەن ۋە ئۇلار ئالىدىغان نۇرغۇن غەنىيمەتلەر بىلەن مۇكاپاتلىدى. ئاللاھ غالىپتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر[18ـ 19].');
INSERT INTO "saleh" VALUES(48,19,'48:19',' ئاللاھ مۆمىنلەردىن ھەقىقەتەن رازى بولدى، (ئى مۇھەممەد!) ئۆز ۋاقتىدا ئۇلار (ھۇدەيبىيەدە) دەرەخ (سايىسى) ئاستىدا ساڭا بەيئەت قىلدى. ئاللاھ ئۇلارنىڭ دىلىدىكىنى (يەنى راستلىق بىلەن ۋاپانى) بىلىدۇ، ئاللاھ ئۇلارغا (ئۇلار بەيئەت قىلىۋاتقاندا) تەمكىنلىك چۈشۈرۈپ بەردى ۋە ئۇلارنى يېقىن غەلىبە (يەنى خەيبەرنىڭ پەتھى قىلىنىشى) بىلەن ۋە ئۇلار ئالىدىغان نۇرغۇن غەنىيمەتلەر بىلەن مۇكاپاتلىدى. ئاللاھ غالىپتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر[18ـ 19].');
INSERT INTO "saleh" VALUES(48,20,'48:20',' ئاللاھ سىلەر ئالىدىغان نۇرغۇن غەنىيمەتلەرنى سىلەرگە ۋەدە قىلدى، بۇنى (يەنى خەيبەرنىڭ غەنىيمىتىنى) ھەممىدىن بۇرۇن ئاتا قىلدى، سىلەرنى دۈشمەنلەرنىڭ قول سېلىشىدىن ساقلىدى، (غەنىيمەتلەر، مەككىنىڭ پەتھى قىلىنىشى، مەسجىدى ھەرامغا كىرىش) مۆمىنلەرگە نىشان بولۇپ قېلىشى ئۈچۈن ۋە سىلەرنى توغرا يولغا باشلاش ئۈچۈن (ئاللاھ شۇنداق قىلدى).');
INSERT INTO "saleh" VALUES(48,21,'48:21',' ئاللاھ يەنە ئۆز كۈچۈڭلار بىلەن قولغا كەلتۈرەلمەيدىغان، ئاللاھ (ئۇنىڭ سىلەرگە مۇيەسسەر بولىدىغانلىقىنى) تولۇق بىلگەن بىر غەنىيمەت (يەنى مەككىنىڭ پەتھى قىلىنىشى) نى (سىلەرگە مۇيەسسەر قىلدى). ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(48,22,'48:22',' ئەگەر كاپىرلار سىلەر بىلەن (ھۇدەيبىيەدە) ئۇرۇشسا، ئەلۋەتتە، ئارقىسىغا قاراپ قاچاتتى، ئاندىن (ئۆزلىرىگە) ھېچ ئىگە ۋە ياردەمچى تاپالمايتتى.');
INSERT INTO "saleh" VALUES(48,23,'48:23',' بۇ (يەنى كاپىرلارنى مەغلۇپ قىلىش، مۆمىنلەرنى غەلىبە قىلدۇرۇش) ئاللاھنىڭ ئىلگىرىدىن تارتىپ تۇتۇپ كېلىۋاتقان يولىدۇر، ئاللاھنىڭ يولىدا ھېچقانداق ئۆزگىرىش تاپالمايسەن (يەنى ئاللاھنىڭ تۇتقان يولى ئۆزگەرمەيدۇ).');
INSERT INTO "saleh" VALUES(48,24,'48:24',' ئاللاھ ئۇلارنى سىلەرگە قول سېلىشتىن توستى ھەمدە ئۇلار ئۈستىدىن غەلىبە قىلغىنىڭلاردىن كېيىن، مەككىنىڭ ئىچىدە (يەنى ھۇدەيبىيەدە) سىلەرنى ئۇلارغا قول سېلىشىڭلاردىن توستى، ئاللاھ قىلىدىغان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(48,25,'48:25','ئۇلار كاپىر بولدى،سىلەرنى مەسجىدى ھەرەمدىن تۇتۇپ تۇرۇلغان قۇربانلىقنىڭ ئۆز جايىغا يېتىشتىن توستى،مەككىدە سىلەر بىلمەيدىغان ئەر ئايال مۆمىنلەر بولۇپ، سىلەر بىلمەستىن ئۇلارنى ئ‍ۆلتۈرۈپ قويۇش بىلەن گۇناھقا گىرىپتار بولۇشىڭلارنىڭ خەۋپى بولمىسا ئىدى اللە خالىغان ئادەمنى رەھمىتى كىرگۈزۈش ئ‍ۈچۈن ، ئەگەر ئۇلار ئايرىلسا ، ئۇلارنىڭ ئارسىدىكى كاپىرلارنى قاتتىق ئازاپ بىلەب ئازاپلايتۇق.');
INSERT INTO "saleh" VALUES(48,26,'48:26',' كاپىرلار قىزىق قانلىقىنى – جاھىلىيەت دەۋرىنىڭ قىزىق قانلىقىنى – دىللىرىغا پۈككەن چاغدا، ئاللاھ پەيغەمبىرىگە ۋە مۆمىنلەرگە تەمكىنلىكنى چۈشۈردى. ئۇلارغا تەقۋا كەلىمىسىنى (يەنى كەلىمە تەۋھىدنى) ئىختىيار قىلدى. مۆمىنلەر تەقۋا كەلىمىسىگە ئەڭ لايىق ئىدى ۋە ئۇنىڭ ئەھلى ئىدى، ئاللاھ ھەر نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(48,27,'48:27','ئاللاھ پەيغەمبىرىگە، ھەق رەۋىشتە، ھەقىقىي راست چۈش كۆرسەتتى. (ئى مۇھەممەد! سەن ۋە ساھابىلىرىڭ) خۇدا خالىسا (دۈشمەندىن) ئەمىن بولغان، (بەزىلىرىڭلار) چاچلىرىڭلارنى چۈشۈرگەن ۋە (بەزىلىرىڭلار) قىرقىغان ھالدا، قورقماي چوقۇم مەسجىدى ھەرامغا كىرىسىلەر، ئاللاھ (سۈلھتىكى) سىلەر بىلمەيدىغان (پايدىنى) بىلىدۇ. ئاللاھ بۇنىڭدىن بۇرۇن (سىلەرگە) يېقىن بىر غەلىبىنى (يەنى خەيبەرنىڭ پەتھىسىنى مۇيەسسەر) قىلدى.');
INSERT INTO "saleh" VALUES(48,28,'48:28',' ئاللاھ ھەق دىننى پۈتۈن دىنلاردىن ئۈستۈن قىلىش ئۈچۈن، پەيغەمبىرىنى ھىدايەت بىلەن ۋە ھەق دىن بىلەن ئەۋەتتى (مۇھەممەد ئەلەيھىسسالامنىڭ ئاللاھنىڭ پەيغەمبىرى ئىكەنلىكىگە) گۇۋاھلىق بېرىشكە ئاللاھ يېتەرلىكتۇر.');
INSERT INTO "saleh" VALUES(48,29,'48:29','مۇھەممەد ئاللاھنىڭ رەسۇلىدۇر، ئۇنىڭ بىلەن بىرگە بولغان مۆمىنلەر كۇففارلارغا قاتتىقتۇر، ئۆزئارا كۆيۈمچاندۇر، ئۇلارنى رۇكۇ قىلغان، سەجدە قىلغان ھالدا كۆرىسەن. ئۇلار ئاللاھنىڭ پەزلىنى ۋە رازىلىقىنى تىلەيدۇ، ئۇلارنىڭ يۈزلىرىدە سەجدىنىڭ ئالامەتلىرى بار، ئەنە شۇ ئۇلارنىڭ تەۋراتتىكى سۈپىتىدۇر، ئۇلارنىڭ ئىنجىلدىكى سۈپىتى بولسا (ئۇلار) شاخ چىقارغان، كۈچلىنىپ چوڭايغان، ئاندىن ئۆز غولى بىلەن ئۆرە تۇرغان، بولۇقلۇقى ۋە كۆركەم كۆرۈنۈشى بىلەن دېھقانلارنى مەمنۇن قىلغان بىر زىرائەتكە ئوخشايدۇ، (مۆمىنلەرنى مۇنداق زىرائەتكە ئوخشىتىش) كۇففارلارنى خاپا قىلىش ئۈچۈندۇر، ئاللاھ ئۇلارنىڭ ئىچىدىن ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارغا مەغپىرەت ۋە كاتتا ئەجىر ۋەدە قىلدى.');
INSERT INTO "saleh" VALUES(49,1,'49:1','ئى مۆمىنلەر! سىلەر ئاللاھنىڭ ۋە ئاللاھنىڭ پەيغەمبىرىنىڭ ئالدىدا (ھېچقانداق بىر ئىشنى ۋە سۆزنى) ئالدى بىلەن قىلماڭلار، ئاللاھتىن قورقۇڭلار، ئاللاھ ھەقىقەتەن (سۆزۈڭلارنى) ئاڭلاپ تۇرغۇچىدۇر، (نىيىتىڭلارنى ۋە ئەھۋالىڭلارنى) بىلىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(49,2,'49:2',' ئى مۆمىنلەر! (رەسۇلۇللاھقا سۆز قىلغان چېغىڭلاردا) ئاۋازىڭلارنى پەيغەمبەرنىڭ ئاۋازىدىن يۇقىرى قىلماڭلار، سىلەر بىر ـ بىرىڭلار بىلەن (توۋلاپ) يۇقىرى ئاۋازدا سۆزلەشكەندەك، پەيغەمبەرگە (توۋلاپ) يۇقىرى ئاۋازدا سۆز قىلماڭلار، (ئۇنداق قىلساڭلار) ئۆزلىرىڭلار تۇيماستىن، (قىلغان ياخشى) ئەمەللىرىڭلار بىكار بولۇپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(49,3,'49:3',' شۈبھىسىزكى، رەسۇلۇللاھنىڭ يېنىدا پەس ئاۋازدا گەپ قىلغانلارنىڭ دىللىرىنى ئاللاھ تەقۋا ئۈچۈن سىنىغان، ئۇلار مەغپىرەتكە ۋە كاتتا ساۋابقا (يەنى جەننەتكە) ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(49,4,'49:4','شۈبھىسىزكى، سېنى ھۇجرىلار (يەنى پاك ئاياللىرىڭنىڭ تۇرالغۇ جايلىرى) ئارقىسىدىن توۋلايدىغانلارنىڭ تولىسى ئەقىلسىز كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(49,5,'49:5','سەن ئۇلارنىڭ ئالدىغا چىققىنىڭغا قەدەر ئۇلار سەۋر قىلىپ تۇرۇشسا، ئۇلار ئۈچۈن ئەلۋەتتە ياخشى بولاتتى، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(49,6,'49:6',' ئى مۆمىنلەر! ئەگەر سىلەرگە بىر پاسىق ئادەم بىرەر خەۋەر ئېلىپ كەلسە، (ئىشنىڭ ھەقىقىتىنى) بىلمەستىن بىرەر قەۋمنى رەنجىتىپ قويۇپ، قىلمىشىڭلارغا پۇشايمان قىلىپ قالماسلىقىڭلار ئۈچۈن، (ئۇ خەۋەرنى) ئېنىقلاپ كۆرۈڭلار.');
INSERT INTO "saleh" VALUES(49,7,'49:7',' بىلىڭلاركى، ئىچىڭلاردا رەسۇلۇللاھ (يەنى ئاللاھنىڭ پەيغەمبىرى) بار، ئەگەر ئۇ نۇرغۇن ئىشلاردا سىلەرگە ئىتائەت قىلىدىغان بولسا، چوقۇم قىيىن ئەھۋالدا قالاتتىڭلار، لېكىن ئاللاھ سىلەرگە ئىماننى قىزغىن سۆيگۈزدى ۋە ئۇنى دىلىڭلاردا كۆركەم قىلدى، سىلەرگە پىسقىنى، كۇفرىنى ۋە گۇناھنى يامان كۆرسەتتى، ئەنە شۇلار ئاللاھنىڭ پەزلى ۋە نېمىتى بىلەن توغرا يولدا بولغۇچىلاردۇر. ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر[7ـ8].');
INSERT INTO "saleh" VALUES(49,8,'49:8',' بىلىڭلاركى، ئىچىڭلاردا رەسۇلۇللاھ (يەنى ئاللاھنىڭ پەيغەمبىرى) بار، ئەگەر ئۇ نۇرغۇن ئىشلاردا سىلەرگە ئىتائەت قىلىدىغان بولسا، چوقۇم قىيىن ئەھۋالدا قالاتتىڭلار، لېكىن ئاللاھ سىلەرگە ئىماننى قىزغىن سۆيگۈزدى ۋە ئۇنى دىلىڭلاردا كۆركەم قىلدى، سىلەرگە پىسقىنى، كۇفرىنى ۋە گۇناھنى يامان كۆرسەتتى، ئەنە شۇلار ئاللاھنىڭ پەزلى ۋە نېمىتى بىلەن توغرا يولدا بولغۇچىلاردۇر. ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر[7ـ8].');
INSERT INTO "saleh" VALUES(49,9,'49:9',' ئەگەر مۆمىنلەردىن ئىككى گۇرۇھ ئۇرۇشۇپ قالسا، ئۇلارنىڭ ئارىسىنى تۈزەپ قويۇڭلار، ئەگەر ئۇلارنىڭ بىرى ئىككىنچىسىگە تاجاۋۇز قىلسا، تاجاۋۇز قىلغۇچى تاكى ئاللاھنىڭ ھۆكمىگە قايتقانغا قەدەر (يەنى تاجاۋۇزىنى توختاتقانغا قەدەر) ئۇنىڭ بىلەن ئۇرۇشۇڭلار، ئەگەر ئۇلار (ئاللاھنىڭ ئەمرىگە) قايتسا، ئۇلارنىڭ ئارىسىنى (ھېچبىر تەرەپكە يان باسماستىن) ئادىللىق بىلەن تۈزەپ قويۇڭلار، (ھەممە ئىشتا) ئادىل بولۇڭلار، ئاللاھ ھەقىقەتەن ئادىللارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(49,10,'49:10',' مۆمىنلەر ھەقىقەتەن (دىندا) قېرىنداشلاردۇر، (ئۇرۇشۇپ قالسا) قېرىنداشلىرىڭلارنىڭ ئارىسىنى تۈزەڭلار، رەھمەتكە ئېرىشىشىڭلار ئۈچۈن، ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(49,11,'49:11',' ئى مۆمىنلەر! بىر قەۋم يەنە بىر قەۋمنى (يەنى بىر جامائە يەنە بىر جامائەنى، بىر ئادەم يەنە بىر ئادەمنى) مەسخىرە قىلمىسۇن، مەسخىرە قىلىنغان قەۋم (ئاللاھنىڭ نەزىرىدە) مەسخىرە قىلغۇچى قەۋمدىن ياخشىراق بولۇشى مۇمكىن، سىلەرنىڭ ئاراڭلاردىكى ئاياللارمۇ ئۆزئارا مەسخىرە قىلىشمىسۇن، مەسخىرە قىلىنغۇچى ئاياللار (ئاللاھنىڭ دەرگاھىدا) مەسخىرە قىلغۇچى ئاياللاردىن ياخشىراق بولۇشى مۈمكىن، بىر ـ بىرىڭلارنى ئەيىبلىمەڭلار، بىر ـ بىرىڭلارنى يامان لەقەم بىلەن چاقىرماڭلار، ئىماندىن كېيىن پىسقى بىلەن ئاتاش (يەنى مۆمىننى پاسىق) دەپ ئاتاش نېمىدېگەن يامان! (مۇنداق نەھيى قىلىنغان ئىشلاردىن) تەۋبە قىلمىغانلار زالىملاردۇر.');
INSERT INTO "saleh" VALUES(49,12,'49:12',' ئى مۆمىنلەر! نۇرغۇن گۇمانلاردىن ساقلىنىڭلار (يەنى ئائىلىدىكىلىرىڭلارغا ۋە كىشىلەرگە گۇمانخورلۇق قىلماڭلار)، بەزى گۇمانلار ھەقىقەتەن گۇناھتۇر، (مۆمىنلەرنىڭ ئەيىبىنى) ئىزدىمەڭلار، بىر ـ بىرىڭلارنىڭ غەيۋىتىنى قىلماڭلار، سىلەرنىڭ بىرىڭلار ئۆلگەن قېرىندىشىڭلارنىڭ گۆشىنى يېيىشنى ياقتۇرامسىلەر؟ ئۇنى ياقتۇرمايسىلەر. ئاللاھتىن قورقۇڭلار، ئاللاھ (تەۋبە قىلغۇچىلارنىڭ قىلغان) تەۋبىسىنى بەكمۇ قوبۇل قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(49,13,'49:13',' ئى ئىنسانلار! سىلەرنى بىز ھەقىقەتەن بىر ئەر، بىر ئايالدىن، (ئادەم بىلەن ھەۋۋادىن ئىبارەت) بىر ئاتا بىر ئانىدىن ياراتتۇق، ئۆز ئارا تونۇشۇشۇڭلار ئۈچۈن سىلەرنى نۇرغۇن مىللەت ۋە ئۇرۇق قىلدۇق، ھەقىقەتەن ئەڭ تەقۋادار بولغانلىرىڭلار ئاللاھنىڭ دەرگاھىدا ئەڭ ھۆرمەتلىك ھېسابلىنىسىلەر (يەنى كىشىلەرنىڭ بىر ـ بىرىدىن ئارتۇق بولۇشى نەسەب بىلەن ئەمەس، تەقۋادارلىق بىلەن بولىدۇ)، ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھەممىدىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(49,14,'49:14',' ئەئرابىيلار: «ئىمان ئېيتتۇق» دەيدۇ، (ئۇلارغا) ئېيتقىنكى، «(تېخى) ئىمان ئېيتمىدىڭلار ۋەلېكىن سىلەر، بويسۇندۇق، دەڭلار، ئىمان تېخى دىلىڭلارغا كىرمىدى، ئەگەر سىلەر ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلساڭلار، ئاللاھ سىلەرنىڭ ئەمەلىڭلاردىن ھېچ نەرسىنى كېمەيتىۋەتمەيدۇ، ئاللاھ ھەقىقەتەن (مۆمىنلەرگە) ناھايىتى مەغپىرەت قىلغۇچىدۇر، (ئۇلارغا) ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(49,15,'49:15',' شۈبھىسىزكى، (ھەقىقىي) مۆمىنلەر ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان كەلتۈرگەن، ئاندىن (ئىمانىدا) شەك كەلتۈرمىگەن، ماللىرى بىلەن، جانلىرى بىلەن ئاللاھنىڭ يولىدا جىھاد قىلغانلاردۇر، ئەنە شۇلار (ئىمان دەۋاسىدا) راستچىللاردۇر».');
INSERT INTO "saleh" VALUES(49,16,'49:16',' (ئى مۇھەممەد!) ئېيتقىنكى، «ئاللاھقا دىنىڭلارنى (يەنى دىنىڭلارنى دىلىڭلارنىڭ تەستىق قىلغانلىقىنى) خەۋەر قىلامسىلەر؟ ھالبۇكى، ئاللاھ ئاسمانلاردىكىنى ۋە زېمىندىكىنى بىلىپ تۇرىدۇ، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر».');
INSERT INTO "saleh" VALUES(49,17,'49:17',' ئۇلار ساڭا مۇسۇلمان بولغانلىقىنى مىننەت قىلىدۇ، (ئۇلارغا) «سىلەر ماڭا مۇسۇلمان بولغانلىقىڭلارنى مىننەت قىلماڭلار، بەلكى ئاللاھ سىلەرنى ئىمانغا ھىدايەت قىلغانلىقىنى مىننەت قىلىدۇ، ئەگەر سىلەر راست (مۆمىن) بولساڭلار» دېگىن. ');
INSERT INTO "saleh" VALUES(49,18,'49:18','ئاللاھ ھەقىقەتەن ئاسمانلاردىكى ۋە زېمىندىكى غەيىبنى بىلىپ تۇرىدۇ، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(50,1,'50:1','قاف. شەرەپلىك قۇرئان بىلەن قەسەم قىلىمەن.');
INSERT INTO "saleh" VALUES(50,2,'50:2',' بەلكى ئۇلار (يەنى مۇشرىكلار) ئۆز ئىچىدىن ئۇلارغا بىر ئاگاھلاندۇرغۇچى كەلگەنلىكىدىن ئەجەبلەندى، كاپىرلار ئېيتتى: «بۇ قىزىق ئىشتۇر.');
INSERT INTO "saleh" VALUES(50,3,'50:3',' بىز ئۆلۈپ توپا بولۇپ كەتكەندىن كېيىن (تىرىلىپ ئىلگىرىكى ھالىتىمىزگە قايتامدۇق)، بۇنداق قايتىش (ئەقىلدىن) يىراقتۇر».');
INSERT INTO "saleh" VALUES(50,4,'50:4','ئۇلارنى زېمىننىڭ كېمەيتكىنىدىن (يەنى ئۇلارنىڭ جەسەتلىرىنى يەر يەپ كەتكىنىدىن) بىز ھەقىقەتەن خەۋەردارمىز، بىزنىڭ دەرگاھىمىزدا ھەممىنى ئۆز ئىچىگە ئالغان كىتاب (يەنى لەۋھۇلمەھپۇز) بار. ');
INSERT INTO "saleh" VALUES(50,5,'50:5',' بەلكى ئۇلار بولسا ئۆزلىرىگە نازىل قىلىنغان ھەقىقەتنى (قۇرئاننى) ئىنكار قىلدى، ئۇلار قالايمىقان ھالەتتىدۇر.');
INSERT INTO "saleh" VALUES(50,6,'50:6',' ئۇلار ئاسماننى قانداق (تۈۋرۈكسىز) بەرپا قىلغانلىقىمىزغا، ئۇنى (يۇلتۇزلار بىلەن) بېزىگەنلىكىمىزگە، ئۇنىڭدا ھېچقانداق يوچۇق يوقلۇقىغا قارىمامدۇ؟');
INSERT INTO "saleh" VALUES(50,7,'50:7','زېمىننى (توغرىسىغا ۋە ئۇزۇنىسىغا سوزۇپ) كەڭ ياراتتۇق، زېمىندە (ئۇنىڭ تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى قىلدۇق ۋە ئۇنىڭدا تۈرلۈك چىرايلىق ئۆسۈملۈكلەرنى ئۆستۈردۇق.');
INSERT INTO "saleh" VALUES(50,8,'50:8','ئاللاھقا قايتقۇچى ھەر بىر بەندىگە (ئاللاھنىڭ قۇدرىتىنى) كۆرسىتىش ئۈچۈن ۋە (ئاللاھنىڭ ئۇلۇغلۇقىنى) ئەسلىتىش ئۈچۈن (شۇنداق قىلدۇق).');
INSERT INTO "saleh" VALUES(50,9,'50:9',' كۆكتىن (بۇلۇتتىن) بەرىكەتلىك سۇنى چۈشۈردۇق، ئۇنىڭ بىلەن باغلارنى ۋە ئاشلىقلارنى، ئېگىز ئۆسكەن، مېۋىلىرى سانجاق ـ سانجاق بولۇپ كەتكەن خورمىلارنى ئۆستۈردۇق[9 ـ 10]. ');
INSERT INTO "saleh" VALUES(50,10,'50:10',' كۆكتىن (بۇلۇتتىن) بەرىكەتلىك سۇنى چۈشۈردۇق، ئۇنىڭ بىلەن باغلارنى ۋە ئاشلىقلارنى، ئېگىز ئۆسكەن، مېۋىلىرى سانجاق ـ سانجاق بولۇپ كەتكەن خورمىلارنى ئۆستۈردۇق[9 ـ 10]. ');
INSERT INTO "saleh" VALUES(50,11,'50:11',' (ئۇلارنىڭ ھەممىسىنى) بەندىلەرنىڭ رىزقى ئۈچۈن (ئۆستۈردۇق)، ئۇ سۇ بىلەن ئۆلۈك (يەنى قاقاس) زېمىننى تىرىلدۈردۇق، ئۆلۈكلەرنىڭ تىرىلىشىمۇ شۇنىڭغا ئوخشاشتۇر.');
INSERT INTO "saleh" VALUES(50,12,'50:12','ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قېرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى)، تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرىنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى[12ـ14].');
INSERT INTO "saleh" VALUES(50,13,'50:13','ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قېرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى)، تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرىنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى[12ـ14].');
INSERT INTO "saleh" VALUES(50,14,'50:14','ئۇلاردىن ئىلگىرى (پەيغەمبەرلىرىنى) ئىنكار قىلغانلار نۇھنىڭ قەۋمى رەس ئاھالىسى، سەمۇد (قەۋمى)، ئاد (قەۋمى)، پىرئەۋن، لۇتنىڭ قېرىنداشلىرى، ئەيكە ئاھالىسى (يەنى شۇئەيب قەۋمى)، تۈببە قەۋمى ئىدى، ئۇلارنىڭ ھەممىسى پەيغەمبەرلىرىنى ئىنكار قىلدى، شۇنىڭ بىلەن مېنىڭ ئازابىم (ئۇلارغا) تېگىشلىك بولدى[12ـ14].');
INSERT INTO "saleh" VALUES(50,15,'50:15',' (ئىنسانلارنى) دەسلەپتە يارىتىشتىن ئاجىز كەلدۇقمۇ؟ ھەرگىز ئۇنداق ئەمەس (بىز بۇنىڭدا ئاجىزلىق قىلمىغاچقا، ئۆلگەندىن كېيىن تىرىلدۈرۈشتىمۇ ئاجىزلىق قىلمايمىز). ئۇلار يېڭىدىن يارىتىلىشقا (يەنى قىيامەتتە تىرىلدۈرۈشكە) قارىتا گۇماندىدۇر.');
INSERT INTO "saleh" VALUES(50,16,'50:16','بىز ھەقىقەتەن ئىنساننى ياراتتۇق، بىز ئۇنىڭ كۆڭلىگە كەلگەننى بىلىمىز، بىز ئۇنىڭ جان تومۇرىدىنمۇ يېقىنمىز.');
INSERT INTO "saleh" VALUES(50,17,'50:17',' ئىنساننىڭ ئوڭ تەرىپىدە ۋە سول تەرىپىدە ئولتۇرۇپ خاتىرىلەيدىغان ئىككى پەرىشتە بار،');
INSERT INTO "saleh" VALUES(50,18,'50:18','ئۇ قانداق بىر سۆزنى قىلمىسۇن، ئۇنىڭ ئالدىدا ھامان پەرىشتە ھازىر بولۇپ، كۈزىتىپ تۇرىدۇ[ـ18].');
INSERT INTO "saleh" VALUES(50,19,'50:19',' ئۆلۈمنىڭ سەكراتى ھەقىقەتنى ئېلىپ كەلدى (يەنى ئاخىرەتنى ئىنكار قىلغۇچى ئاخىرەتنىڭ دەھشىتىنى ئېنىق كۆرىدۇ)، سەن ئۇنىڭدىن (يەنى ئۆلۈمدىن) قاچاتتىڭ.');
INSERT INTO "saleh" VALUES(50,20,'50:20','سۇر چېلىنىدۇ، ئەنە شۇ (كۇففارلارغا) ئازاب ۋەدە قىلىنغان كۈندۇر.');
INSERT INTO "saleh" VALUES(50,21,'50:21','ھەر ئىنسان (مەھشەرگاھقا) كېلىدۇ، ئۇنىڭ بىلەن (ئۇنى مەھشەرگاھقا) ھەيدىگۈچى ۋە (ئۇنىڭ ئەمەلىگە) گۇۋاھ بولغۇچى (ئىككى پەرىشتە) بىللە كېلىدۇ.');
INSERT INTO "saleh" VALUES(50,22,'50:22',' ئۇنىڭغا: «ئى ئىنسان! (دۇنيادىكى چېغىڭدا) بۇ (ئېغىر كۈن) دىن غەپلەتتە ئىدىڭ، ئەمدى پەردەڭنى سەندىن كۆتۈرۋەتتۇق، بۈگۈن كۆزۈڭ ئۆتكۈردۇر» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(50,23,'50:23',' ئۇنىڭغا مۇئەككەل پەرىشتە: «مانا بۇ (نامە ـ ئەمال) ئالدىمدا تەيياردۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(50,24,'50:24',' (ئاللاھتائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، ئاللاھقا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)[24ـ26].');
INSERT INTO "saleh" VALUES(50,25,'50:25',' (ئاللاھتائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، ئاللاھقا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)[24ـ26].');
INSERT INTO "saleh" VALUES(50,26,'50:26',' (ئاللاھتائالا ئىككى پەرىشتىگە خىتاب قىلىپ) «ھەققە قارشى ياخشىلىقتىن توسقۇچى، ھەددىدىن ئاشقۇچى، (دىنغا) شەك كەلتۈرگۈچى، ئاللاھقا باشقا مەبۇدنى شېرىك قىلغۇچى ھەر بىر كاپىرنى دوزاخقا تاشلاڭلار، ئۇنى قاتتىق ئازابقا تاشلاڭلار» (دەيدۇ)[24ـ26].');
INSERT INTO "saleh" VALUES(50,27,'50:27',' ئۇنىڭ ھەمراھى (بولغان شەيتان): «پەرۋەردىگارىمىز! مەن ئۇنى ئازدۇرمىدىم، لېكىن (ئۇ ئۆز ئىختىيارى بىلەن) چوڭقۇر گۇمراھلىقتا بولدى» (دەيدۇ).');
INSERT INTO "saleh" VALUES(50,28,'50:28',' ئاللاھ: «مېنىڭ ئالدىمدا مۇنازىرىلەشمەڭلار (بۇ يەردە مۇنازىرە پايدا بەرمەيدۇ)، سىلەرنى مەن ئالدىنئالا (ئازابىمدىن) ئاگاھلاندۇرغان ئىدىم،');
INSERT INTO "saleh" VALUES(50,29,'50:29','مېنىڭ سۆزۈم ئۆزگەرمەيدۇ، مەن بەندىلەرگە زۇلۇم قىلغۇچى ئەمەسمەن» دەيدۇ[ـ29].');
INSERT INTO "saleh" VALUES(50,30,'50:30',' ئۇ كۈندە بىز جەھەننەمگە: «توشتۇڭمۇ؟» دەيمىز، جەھەننەم: «يەنە بارمۇ؟» دەيدۇ.');
INSERT INTO "saleh" VALUES(50,31,'50:31',' جەننەت تەقۋادارلارغا يىراق بولمىغان جايغا يېقىنلاشتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(50,32,'50:32','مانا بۇ (نېمەتتىن) سىلەرگە ۋەدە قىلىنغان نەرسىلەر ئاللاھنىڭ (تائىتىگە) قايتقۇچى، (ئەمرىنى) ساقلىغۇچى ھەر بىر (بەندە) ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(50,33,'50:33',' كىمكى مېھرىبان ئاللاھنى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقسا ۋە تەۋبە قىلغۇچى قەلب بىلەن كەلسە (ئۇلارغا) «جەننەتكە ئامانلىق بىلەن كىرىڭلار، بۇ مەڭگۈ قېلىش كۈنىدۇر» (دېيىلىدۇ)[33ـ34].');
INSERT INTO "saleh" VALUES(50,34,'50:34','كىمكى مېھرىبان ئاللاھنى كۆرمەي تۇرۇپ، ئۇنىڭدىن قورقسا ۋە تەۋبە قىلغۇچى قەلب بىلەن كەلسە (ئۇلارغا) «جەننەتكە ئامانلىق بىلەن كىرىڭلار، بۇ مەڭگۈ قېلىش كۈنىدۇر» (دېيىلىدۇ)[33ـ34].');
INSERT INTO "saleh" VALUES(50,35,'50:35',' جەننەتتە ئۇلارغا خالىغان نەرسىلەر بار ۋە بىزنىڭ ھۇزۇرىمىزدا (ئۇلارغا) زىيادە (نېمەتلەر) بار.');
INSERT INTO "saleh" VALUES(50,36,'50:36',' بىز ئۇلاردىن (يەنى قۇرەيش كۇففارلىرىدىن) ئىلگىرى ئۇلارغا قارىغاندا كۈچلۈك بولغان نۇرغۇن ئۈممەتلەرنى ھالاك قىلدۇق، ئۇلار (يەنى شۇ ئۈممەتلەر) ھەر قايسى جايلارنى كەزدى، (ئۇلارغا) ئۆلۈمدىن قاچىدىغان جاي تېپىلدىمۇ؟');
INSERT INTO "saleh" VALUES(50,37,'50:37','بۇنىڭدا (ھەقىقەت ئۈستىدە پىكىر يۈرگۈزىدىغان) قەلبكە ئىگە ئادەم ياكى (ۋەز ـ نەسىھەتكە) ھۇزۇرى قەلب بىلەن قۇلاق سالىدىغان ئادەم ئۈچۈن ئەلۋەتتە ۋەز ـ نەسىھەت بار.');
INSERT INTO "saleh" VALUES(50,38,'50:38','بىز ھەقىقەتەن ئاسمانلارنى، زېمىننى ۋە ئۇلارنىڭ ئارىسىدىكى نەرسىلەرنى ئالتە كۈندە ياراتتۇق، بىز ھېچقانداق چارچاپ قالغىنىمىز يوق.');
INSERT INTO "saleh" VALUES(50,39,'50:39',' (ئى مۇھەممەد!) كاپىرلارنىڭ سۆزلىرىگە سەۋر قىلغىن، كۈن چىقىشتىن ئىلگىرى ۋە كۈن پېتىشتىن ئىلگىرى پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن.');
INSERT INTO "saleh" VALUES(50,40,'50:40','كېچىدە ۋە سەجدىدىن كېيىن (يەنى پەرز نامازلارنىڭ ئارقىسىدا) تەسبىھ ئېيتقىن.');
INSERT INTO "saleh" VALUES(50,41,'50:41','نىدا قىلغۇچى (يەنى ئىسراپىل) يېقىن جايدىن نىدا قىلىدىغان (يەنى ئۇنىڭ ئاۋازى ھەممىگە ئوخشاش يېتىدىغان) كۈندە (نىداغا) قۇلاق سالغىن.');
INSERT INTO "saleh" VALUES(50,42,'50:42',' ئۇلار ھەقنى ئېلىپ كېلىدىغان ئاۋازنى (يەنى ئىككىنچى قېتىملىق سۇرنى) ئاڭلىغان كۈن ـ ئەنە شۇ (كۈن قەبرىلەردىن) چىقىش كۈنىدۇر.');
INSERT INTO "saleh" VALUES(50,43,'50:43','بىز ھەقىقەتەن كىشىلەرنى ۋاپات قىلدۇرىمىز ۋە تىرىلدۈرىمىز، ئاخىر بارىدىغان جاي بىزنىڭ دەرگاھىمىزدۇر.');
INSERT INTO "saleh" VALUES(50,44,'50:44',' ئۇ كۈندە يەر يېرىلىپ ئۇلار (قەبرىلىرىدىن) تېز چىقىپ كېلىدۇ، ئەنە شۇ يىغىش بىزگە ئاساندۇر.');
INSERT INTO "saleh" VALUES(50,45,'50:45',' بىز ئۇلارنىڭ (يەنى قۇرەيش كۇففارلىرىنىڭ) ئېيتىدىغان سۆزلىرىنى ئوبدان بىلىمىز، سەن ئۇلارنى (ئىسلامغا) زورلىغۇچى ئەمەسسەن، مېنىڭ ئاگاھلاندۇرۇشۇمدىن قورقىدىغانلارغا (قۇرئان بىلەن) ۋەز ـ نەسىھەت قىلغىن. ');
INSERT INTO "saleh" VALUES(51,1,'51:1','(تۇپراقنى) سورىغۇچى شاماللار بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(51,2,'51:2',' (يامغۇرلارنىڭ) ئېغىرلىقىنى كۆتۈرگۈچى بۇلۇتلار بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(51,3,'51:3',' (سۇ ئۈستىدە) يىنىك ماڭغۇچى كېمىلەر بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(51,4,'51:4',' ئىشنى (يەنى بەندىلەر ئارىسىدا رىزىقلارنى، يۇرتلار ئارىسىدا يامغۇرنى) تەقسىم قىلغۇچى پەرىشتىلەر بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(51,5,'51:5',' سىلەرگە ۋەدە قىلىنغان (ساۋاب، ئازاب، قىيامەت قاتارلىق) لار ئەلۋەتتە راستتۇر. ');
INSERT INTO "saleh" VALUES(51,6,'51:6','(ياخشى ئەمەللەرنى) مۇكاپاتلاش، (يامان ئەمەللەرنى) جازالاش چوقۇم بولىدىغان ئىشتۇر.');
INSERT INTO "saleh" VALUES(51,7,'51:7',' يوللىرى بار ئاسمان بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(51,8,'51:8',' (پەيغەمبەر ھەققىدە) سىلەرنىڭ سۆزۈڭلار ھەقىقەتەن خىلمۇخىلدۇر[ـ8].');
INSERT INTO "saleh" VALUES(51,9,'51:9','(ئاللاھتائالانىڭ ئىلمىي ئەزەلىسىدە ھىدايەتتىن) بۇرۇۋېتىلگەن ئادەم (قۇرئانغا ۋە مۇھەممەد ئەلەيھىسسالامغا ئىمان كەلتۈرۈشتىن) بۇرۇۋېتىلىدۇ.');
INSERT INTO "saleh" VALUES(51,10,'51:10',' كاززاپلارغا لەنەت بولسۇنكى، ');
INSERT INTO "saleh" VALUES(51,11,'51:11','ئۇلار جاھالەتكە چۆمگەن بولۇپ (ئاخىرەت ئىشىدىن) غەپلەتتىدۇر[ـ11].');
INSERT INTO "saleh" VALUES(51,12,'51:12','ئۇلار رەسۇلۇللاھتىن: «قىيامەت قاچان بولىدۇ؟» دەپ سورايدۇ.');
INSERT INTO "saleh" VALUES(51,13,'51:13','ئۇ بولسا ئۇ كۈندە دوزاختا ئازابلىنىدىغان كۈندۇر.');
INSERT INTO "saleh" VALUES(51,14,'51:14','(دوزاخقا مۇئەككەل پەرىشتىلەر ئۇلارغا) «سىلەرگە قىلىنغان ئازابنى تېتىڭلار، مانا بۇ سىلەر (دۇنيادىكى چېغىڭلاردا) ئالدىراتقان نەرسىدۇر» (دەيدۇ).');
INSERT INTO "saleh" VALUES(51,15,'51:15',' شۈبھىسىزكى، تەقۋادارلار جەننەتلەردە ۋە (ئېقىپ تۇرغان) بۇلاقلارنىڭ ئارىسىدا بولىدۇ.');
INSERT INTO "saleh" VALUES(51,16,'51:16',' ئۇلار پەرۋەردىگارى ئاتا قىلغان نەرسىلەرنى قوبۇل قىلىدۇ، ئۇلار بۇنىڭدىن ئىلگىرى (يەنى دۇنيادىكى چاغدا) ياخشى ئىش قىلغۇچىلار ئىدى.');
INSERT INTO "saleh" VALUES(51,17,'51:17',' ئۇلار كېچىسى ئاز ئۇخلايتتى.');
INSERT INTO "saleh" VALUES(51,18,'51:18',' ئۇلار سەھەرلەردە (پەرۋەردىگارىدىن) مەغپىرەت تىلەيتتى.');
INSERT INTO "saleh" VALUES(51,19,'51:19',' ئۇلارنىڭ پۇل ـ ماللىرىدا سائىللار ۋە (ئىپپەتلىكلىكىدىن سائىللىق قىلمايدىغان) موھتاجلار ھوقۇققا ئىگە ئىدى (يەنى ئۇلار سائىللارغا ۋە موھتاجلارغا سەدىقە قىلاتتى).');
INSERT INTO "saleh" VALUES(51,20,'51:20',' زېمىندا ۋە ئۆزۈڭلاردا ئاللاھقا چىن ئېتىقاد قىلىدىغانلار ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار، (بۇنى) كۆرمەمسىلەر[20ـ21].');
INSERT INTO "saleh" VALUES(51,21,'51:21',' زېمىندا ۋە ئۆزۈڭلاردا ئاللاھقا چىن ئېتىقاد قىلىدىغانلار ئۈچۈن (ئاللاھنىڭ قۇدرىتىنى ۋە بىرلىكىنى كۆرسىتىدىغان) نۇرغۇن ئالامەتلەر بار، (بۇنى) كۆرمەمسىلەر[20ـ21].');
INSERT INTO "saleh" VALUES(51,22,'51:22','ئاسماندا سىلەرنىڭ رىزقىڭلار بار، سىلەرگە ۋەدە قىلىنغان ساۋاب بار.');
INSERT INTO "saleh" VALUES(51,23,'51:23',' ئاسماننىڭ ۋە زېمىننىڭ پەرۋەردىگارى بىلەن قەسەمكى، سىلەرگە ۋەدە قىلىنغان نەرسە ئۆزۈڭلارنىڭ سۆزلەۋاتقىنىڭلاردەك (يەنى سۆزلىگىنىڭلارنىڭ ئۆزەڭلارغا ئېنىق بولغىنىدەك) ھەقتۇر.');
INSERT INTO "saleh" VALUES(51,24,'51:24',' (ئى مۇھەممەد!) ساڭا ئىبراھىمنىڭ ھۆرمەتلىك مېھمانلىرىنىڭ خەۋىرى يەتتىمۇ؟');
INSERT INTO "saleh" VALUES(51,25,'51:25','ئۆز ۋاقتىدا ئۇلار ئىبراھىمنىڭ يېنىغا كىرىپ سالام دېدى، ئىبراھىم سالامنى ئىلىك ئالدى. (ئىچىدە) «ناتونۇش ئادەملەرغۇ» دېدى.');
INSERT INTO "saleh" VALUES(51,26,'51:26',' شۇنىڭ بىلەن ئۇ ئاستا ئائىلىسىگە چىقىپ (پىشۇرۇلغان) بىر سېمىز موزاينى ئېلىپ كىرىپ ئۇلارنىڭ ئالدىغا قويۇپ: «يېمەمسىلەر» دېدى[26ـ27].');
INSERT INTO "saleh" VALUES(51,27,'51:27',' شۇنىڭ بىلەن ئۇ ئاستا ئائىلىسىگە چىقىپ (پىشۇرۇلغان) بىر سېمىز موزاينى ئېلىپ كىرىپ ئۇلارنىڭ ئالدىغا قويۇپ: «يېمەمسىلەر» دېدى[26ـ27].');
INSERT INTO "saleh" VALUES(51,28,'51:28',' (ئۇلارنىڭ تاماق يېمىگەنلىكىنى كۆرۈپ) دىلىدا ئۇلاردىن قورقتى، ئۇلار «قورقمىغىن (بىز پەرۋەردىگارىڭنىڭ ئەلچىلىرىمىز)» دېدى، ئۇلار ئىبراھىمغا بىلىملىك بىر ئوغۇل بىلەن خۇشخەۋەر بەردى.');
INSERT INTO "saleh" VALUES(51,29,'51:29',' ئىبراھىمنىڭ ئايالى سۈرەن سېلىپ كېلىپ (ئەجەبلەنگەنلىكىدىن ئۆزىنىڭ) يۈزىنى كاچاتلاپ: «مەن تۇغماس موماي تۇرسام (قانداق تۇغىمەن؟)» دېدى.');
INSERT INTO "saleh" VALUES(51,30,'51:30',' ئۇلار: «پەرۋەردىگارىڭ شۇنداق دېدى. ئۇ ھەقىقەتەن ھېكمەت بىلەن ئىش قىلغۇچىدۇر، ھەممىنى بىلگۈچىدۇر» دېدى.');
INSERT INTO "saleh" VALUES(51,31,'51:31',' ئىبراھىم: «ئى ئەلچىلەر! سىلەر نېمە ئىش بىلەن ئەۋەتىلدىڭلار» دېدى.');
INSERT INTO "saleh" VALUES(51,32,'51:32',' ئۇلار: «بىز ھەقىقەتەن بىر گۇناھكار قەۋمنى (ھالاك قىلىش ئۈچۈن) ئەۋەتىلدۇق (يەنى لۇت قەۋمىنىڭ ئۈستىگە لايدىن پىشۇرۇلغان تاش ياغدۇرۇش ئۈچۈن ئەۋەتىلدۇق).');
INSERT INTO "saleh" VALUES(51,33,'51:33',' ئۇلارنىڭ ئۈستىگە پەرۋەردىگارىڭنىڭ دەرگاھىدا (گۇناھى) ھەددىدىن ئاشقۇچىلار ئۈچۈن بەلگە قويۇلغان (يەنى ھالاك قىلىنغۇچىنىڭ ئىسمى يېزىلغان)، لايدىن پىشۇرۇلغان تاش ياغدۇرىمىز»[33ـ34].');
INSERT INTO "saleh" VALUES(51,34,'51:34',' ئۇلارنىڭ ئۈستىگە پەرۋەردىگارىڭنىڭ دەرگاھىدا (گۇناھى) ھەددىدىن ئاشقۇچىلار ئۈچۈن بەلگە قويۇلغان (يەنى ھالاك قىلىنغۇچىنىڭ ئىسمى يېزىلغان)، لايدىن پىشۇرۇلغان تاش ياغدۇرىمىز»[33ـ34].');
INSERT INTO "saleh" VALUES(51,35,'51:35',' (ھالاك بولمىسۇن دەپ لۇتنىڭ) شەھىرىدىكى مۆمىنلەرنى (سىرتقا) چىقىرىۋەتتۇق.');
INSERT INTO "saleh" VALUES(51,36,'51:36',' بىز ئۇ شەھەردىن مۇسۇلمانلاردىن پەقەت بىر ئائىلىنىلا تاپتۇق.');
INSERT INTO "saleh" VALUES(51,37,'51:37',' ئۇ شەھەردە قاتتىق ئازابتىن قورقىدىغانلار ئۈچۈن نىشان قالدۇردۇق.');
INSERT INTO "saleh" VALUES(51,38,'51:38','مۇسانىڭ (قىسسەسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇنى بىز پىرئەۋنگە روشەن دەلىل بىلەن ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(51,39,'51:39',' پىرئەۋن ئۆز قوشۇنى بىلەن (ئىماندىن) يۈز ئۆرىدى، ئۇ: «(مۇسا) سېھىرگەردۇر ياكى مەجنۇندۇر» دېدى.');
INSERT INTO "saleh" VALUES(51,40,'51:40',' پىرئەۋننى ۋە ئۇنىڭ قوشۇنىنى جازالىدۇق، ئۇلارنى دېڭىزغا تاشلىدۇق، پىرئەۋن ئەيىبلەنگۈچىدۇر.');
INSERT INTO "saleh" VALUES(51,41,'51:41',' ئادنىڭ (قىسسەسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا يامان بوراننى ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(51,42,'51:42',' ئۇ (يەنى بوران) ھەر قانداق نەرسىنىڭ يېنىدىن ئۆتسە، ئۇنى چىرىگەن سۆڭەكتەك (تىتما ـ تالان) قىلىۋېتەتتى.');
INSERT INTO "saleh" VALUES(51,43,'51:43','سەمۇدنىڭ (قىسسەسىدىمۇ) بىر تۈرلۈك ئالامەت بار، ئۆز ۋاقتىدا ئۇلارغا: «سىلەر ۋاقىتلىق پايدىلىنىڭلار» دېدۇق.');
INSERT INTO "saleh" VALUES(51,44,'51:44',' ئۇلار پەرۋەردىگارىنىڭ ئەمرىگە قارشى چىقتى، ئۇلارنى چاقماق ھالاك قىلدى، ھالبۇكى، ئۇلار (ئازابنىڭ چۈشۈۋاتقانلىقىنى) كۆزلىرى بىلەن كۆرۈپ تۇراتتى.');
INSERT INTO "saleh" VALUES(51,45,'51:45',' ئۇلار (ئازاب چۈشكەن ۋاقىتتا) ئورۇنلىرىدىن تۇرۇشقىمۇ، ئۆزلىرىنى قوغداشقىمۇ قادىر بولالمىدى.');
INSERT INTO "saleh" VALUES(51,46,'51:46',' ئىلگىرى نۇھنىڭ قەۋمىنى ھالاك قىلدۇق، ئۇلار ھەقىقەتەن پاسىق قەۋم ئىدى.');
INSERT INTO "saleh" VALUES(51,47,'51:47','ئاسماننى قۇدرەت بىلەن بەرپا قىلدۇق، بىز ھەقىقەتەن ئۇنى داۋاملىق كېڭەيتكۈچىلەرمىز.');
INSERT INTO "saleh" VALUES(51,48,'51:48',' زېمىننى يايدۇق، بىز نېمىدېگەن ياخشى يايغۇچىمىز');
INSERT INTO "saleh" VALUES(51,49,'51:49',' سىلەر ئىبرەت ئېلىشىڭلار ئۈچۈن ھەر بىر نەرسىنى جۈپ ياراتتۇق.');
INSERT INTO "saleh" VALUES(51,50,'51:50','(ئى مۇھەممەد!) ئېيتقىنكى، «ئاللاھ تەرەپكە قېچىڭلار (يەنى ئاللاھقا ئىلتىجا قىلىڭلار)، مەن ھەقىقەتەن سىلەرگە ئاللاھ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن.');
INSERT INTO "saleh" VALUES(51,51,'51:51',' سىلەر باشقا مەبۇدنى ئاللاھقا شېرىك قىلماڭلار، مەن ھەقىقەتەن ئۇنىڭ تەرىپىدىن (كەلگەن) ئوچۇق ئاگاھلاندۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(51,52,'51:52',' ئۇلاردىن ئىلگىرىكى ئۈممەتلەرمۇ شۇنداق ئىدى، ئۇلارغا ھەر پەيغەمبەر كەلسىلا ئۇلار (ئۇنى): «بىر سېھرىگەر ياكى بىر مەجنۇن» دېدى.');
INSERT INTO "saleh" VALUES(51,53,'51:53','ئۇلار (پەيغەمبەرلەرنى ئىنكار قىلىشنى) بىر ـ بىرىگە تەۋسىيە قىلىشقانمۇ؟ ھەرگىز ئۇنداق ئەمەس (يەنى ئۇلار بىر ـ بىرىگە ئۇنداق تەۋسىيە قىلىشقان ئەمەس) ئۇلار ھەددىدىن ئاشقۇچى قەۋمدۇر[53');
INSERT INTO "saleh" VALUES(51,54,'51:54','ئۇلاردىن يۈز ئۆرۈگىن، سەن مالامەت قىلىنغۇچى ئەمەسسەن.');
INSERT INTO "saleh" VALUES(51,55,'51:55','ـ نەسىھەت قىلغىن، ۋەز ـ نەسىھەت مۆمىنلەرگە پايدىلىق.');
INSERT INTO "saleh" VALUES(51,56,'51:56','جىنلارنى، ئىنسانلارنى پەقەت ماڭا ئىبادەت قىلىش ئۈچۈنلا ياراتتىم.');
INSERT INTO "saleh" VALUES(51,57,'51:57',' ئۇلاردىن مەن رىزىق تىلىمەيمەن ۋە ئۇلارنىڭ مېنى ئوزۇقلاندۇرۇشىنى تىلىمەيمەن.');
INSERT INTO "saleh" VALUES(51,58,'51:58',' ئاللاھ ھەقىقەتەن ھەممىگە رىزىق بەرگۈچىدۇر، قۇدرەتلىكتۇر، (ئاللاھنىڭ) قۇۋۋىتى ئارتۇقتۇر.');
INSERT INTO "saleh" VALUES(51,59,'51:59',' (ئۆزلىرىگە) زۇلۇم قىلغانلاردىن (ئازابتىن) ئۆتكەنكى (ھالاك بولغان) دوستلىرىنىڭ نېسىۋىسىدەك نېسىۋىسى بولىدۇ، بۇلار (ئازابىمغا) ئالدىراتمىسۇن.');
INSERT INTO "saleh" VALUES(51,60,'51:60',' كاپىرلارغا ۋەدە قىلىنغان كۈندىن كاپىرلارغا ۋاي!.');
INSERT INTO "saleh" VALUES(52,1,'52:1','تۇر تېغى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(52,2,'52:2',' ئوچۇق قەغەزگە يېزىلغان كىتاب (يەنى قۇرئان) بىلەن قەسەمكى[2ـ3]، ');
INSERT INTO "saleh" VALUES(52,3,'52:3',' ئوچۇق قەغەزگە يېزىلغان كىتاب (يەنى قۇرئان) بىلەن قەسەمكى[2ـ3]، ');
INSERT INTO "saleh" VALUES(52,4,'52:4','بەيتۇلمەئمۇر بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(52,5,'52:5',' ئاسمان بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(52,6,'52:6',' (قىيامەت كۈنى) ئوت بولۇپ ياندۇرۇلغان دېڭىز بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(52,7,'52:7',' پەرۋەردىگارىڭنىڭ ئازابى چوقۇم يۈز بەرگۈسىدۇر.');
INSERT INTO "saleh" VALUES(52,8,'52:8',' ئۇنىڭغا ھېچ ئادەم قارشى تۇرالمايدۇ.');
INSERT INTO "saleh" VALUES(52,9,'52:9','ئۇ كۈندە ئاسمان قاتتىق تەۋرەيدۇ. ');
INSERT INTO "saleh" VALUES(52,10,'52:10',' تاغلار قاتتىق سەير قىلىدۇ (يەنى زېمىننىڭ ئۈستىدىن توزۇپ يوقىلىپ كېتىدۇ). ');
INSERT INTO "saleh" VALUES(52,11,'52:11','ئۇ كۈندە (پەيغەمبەرلەرنى) ئىنكار قىلغانلارغا ۋاي!.');
INSERT INTO "saleh" VALUES(52,12,'52:12',' ئۇلار (دۇنيادا) غەپلەتكە چۆمگەنلەردۇر.');
INSERT INTO "saleh" VALUES(52,13,'52:13',' ئۇ كۈندە ئۇلار دوزاخقا قاتتىق ئىتتىرىلىدۇ.');
INSERT INTO "saleh" VALUES(52,14,'52:14',' (ئۇلارغا) «مانا بۇ سىلەر يالغانغا چىقارغان دوزاختۇر» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(52,15,'52:15',' (ئۇلارغا) «(كۆزلىرىڭلار بىلەن كۆرۈپ تۇرۇۋاتقان بۇ ئازاب) سېھىرمۇ ياكى سىلەر كۆرمەيۋاتامسىلەر؟');
INSERT INTO "saleh" VALUES(52,16,'52:16','دوزاخقا كىرىڭلار، (ئازابقا) مەيلى سەۋر قىلىڭلار، مەيلى سەۋر قىلماڭلار، سىلەرگە بەرىبىر ئوخشاش (چۈنكى سىلەر دوزاختا مەڭگۈ قالىسىلەر)، سىلەر پەقەت قىلمىشىڭلارنىڭ جازاسىنى تارتىسىلەر» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(52,17,'52:17','تەقۋادارلار ھەقىقەتەن جەننەتلەردە ۋە نېمەت ئىچىدە بولىدۇ.');
INSERT INTO "saleh" VALUES(52,18,'52:18',' ئۇلار پەرۋەردىگارى بەرگەن نەرسىلەر بىلەن ھۇزۇرلىنىدۇ، پەرۋەردىگارى ئۇلارنى دوزاخ ئازابىدىن ساقلايدۇ.');
INSERT INTO "saleh" VALUES(52,19,'52:19',' (ئۇلارغا) «قىلغان ئەمەللىرىڭلارنىڭ (مۇكاپاتى) ئۈچۈن كۆڭۈللۈك يەڭلار ۋە ئىچىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(52,20,'52:20','ئۇلار قاتار تىزىلغان تەختلەر ئۈستىدە يۆلەنگەن ھالدا ئولتۇرىدۇ، شەھلا كۆزلۈك ھۈرلەرنى ئۇلارغا جۈپ قىلىپ بېرىمىز.');
INSERT INTO "saleh" VALUES(52,21,'52:21',' ئۆزلىرى ئىمان ئېيتقان، ئەۋلادلىرىمۇ ئەگىشىپ ئىمان ئېيتقانلارنىڭ ئەۋلادلىرىنى ئۇلار بىلەن تەڭ دەرىجىدە قىلىمىز، ئۇلارنىڭ (ياخشى) ئەمەللىرىدىن قىلچىمۇ كېمەيتىۋەتمەيمىز، ھەر ئادەم ئۆزىنىڭ قىلمىشىغا مەسئۇلدۇر.');
INSERT INTO "saleh" VALUES(52,22,'52:22','ئۇلار كۆرۈۋاتقان نېمەتنىڭ ئۈستىگە يەنە (ئۇلارنى) كۆڭلى تارتقان مېۋە ۋە كۆڭلى تارتقان گۆش بىلەن تەمىنلەيمىز.');
INSERT INTO "saleh" VALUES(52,23,'52:23',' ئۇلار جەننەتتە ئۆزئارا (مەي قاچىلانغان) جاملارنى تۇتىشىدۇ، (مەي ئىچىش بىلەن) ئۇلاردىن (بۇ دۇنيادىكىدەك) بىھۇدە سۆز ۋە گۇناھ سادىر بولمايدۇ.');
INSERT INTO "saleh" VALUES(52,24,'52:24','گويا سەدەپنىڭ ئىچىدىكى گۆھەردەك (چىرايلىق) غىلمانلار ئۇلارنى ئايلىنىپ يۈرۈپ (ئۇلارنىڭ) خىزمىتىنى قىلىدۇ.');
INSERT INTO "saleh" VALUES(52,25,'52:25',' ئۇلار (يەنى ئەھلى جەننەت) بىر ـ بىرىگە قارىشىپ پاراڭ سېلىشىدۇ.');
INSERT INTO "saleh" VALUES(52,26,'52:26',' ئۇلار ئېيتىدۇ: «بىز ھەقىقەتەن (دۇنيادىكى چاغدا) ئائىلىمىزدە (ئاللاھنىڭ ئازابىدىن) قورقاتتۇق.');
INSERT INTO "saleh" VALUES(52,27,'52:27','ئاللاھ بىزگە مەرھەمەت قىلدى، بىزنى زەھەرلىك ئازابتىن ساقلىدى.');
INSERT INTO "saleh" VALUES(52,28,'52:28','بىز ھەقىقەتەن ئىلگىرى ئاللاھقا ئىبادەت قىلاتتۇق، ئاللاھ ھەقىقەتەن ئېھسان قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر».');
INSERT INTO "saleh" VALUES(52,29,'52:29',' (ئى مۇھەممەد! سەن قۇرئان بىلەن قەۋمىڭگە) ۋەز ـ نەسىھەت قىلغىن، سەن پەرۋەردىگارىڭنىڭ نېمىتى بىلەن كاھىنمۇ ئەمەسسەن ۋە مەجنۇنمۇ ئەمەسسەن.');
INSERT INTO "saleh" VALUES(52,30,'52:30',' ئۇلار ئېيتىدۇ: «ئۇ شائىردۇر، بىز ئۇنىڭ زاماننىڭ ھادىسىلىرىگە (يولۇقۇشىنى) كۈتىمىز».');
INSERT INTO "saleh" VALUES(52,31,'52:31',' (ئى مۇھەممەد!) ئېيتقىنكى، «سىلەر كۈتۈڭلار، ھەقىقەتەن مەن سىلەر بىلەن بىرگە كۈتكۈچىلەردىنمەن (يەنى سىلەر مېنىڭ ھالاك بولۇشۇمنى كۈتسەڭلار، مەنمۇ سىلەرنىڭ ھالاك بولۇشۇڭلارنى كۈتىمەن)»[ـ31].');
INSERT INTO "saleh" VALUES(52,32,'52:32','ئۇلارنىڭ ئەقلى ئۇلارنى مۇشۇنداق (سۆز) قىلىشقا بۇيرۇمدۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇلار (كۇفرىدا) ھەددىدىن ئاشقان قەۋمدۇر.');
INSERT INTO "saleh" VALUES(52,33,'52:33','ياكى ئۇلار: «(مۇھەممەد قۇرئاننى) ئۆزى توقۇدى» دېيىشەمدۇ؟ ئۇنداق ئەمەس، ئۇلار (قۇرئانغا) ئىشەنمەيدۇ. ');
INSERT INTO "saleh" VALUES(52,34,'52:34',' ئەگەر (مۇھەممەد ئۇنى توقۇدى دېگەن سۆزۈڭلاردا) راستچىل بولساڭلار، (تۈزۈلۈشتە، گۈزەللىكتە، باياندا) قۇرئانغا ئوخشايدىغان بىر سۆزنى كەلتۈرۈپ بېقىڭلار.');
INSERT INTO "saleh" VALUES(52,35,'52:35',' ياكى ئۇلار ياراتقۇچىسىز يارىتىلغانمۇ ياكى ئۇلار ئۆزلىرى ياراتقۇچىمۇ؟ ');
INSERT INTO "saleh" VALUES(52,36,'52:36','ياكى ئۇلار ئاسمانلارنى ۋە زېمىننى ياراتقانمۇ؟ بەلكى ئۇلار (ئاللاھنىڭ بىرلىكىگە) ئىشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(52,37,'52:37','ئۇلارنىڭ يېنىدا پەرۋەردىگارىنىڭ خەزىنىلىرى بارمۇ؟ ياكى ئۇلار (شەيئىلەرنى خالىغانچە تەسەررۇپ قىلىدىغان) (كائىناتنى) بويسۇندۇرغۇچىمۇ؟');
INSERT INTO "saleh" VALUES(52,38,'52:38',' ياكى ئۇلارنىڭ (ئاسمانغا چىقىپ) سۆز تىڭشايدىغان شوتىسى بارمۇ؟ ئەگەر بولسا، ئۇلارنىڭ سۆز ئاڭلىغۇچىسى ئوچۇق بىر پاكىتىنى كەلتۈرسۇن.');
INSERT INTO "saleh" VALUES(52,39,'52:39',' ياكى ئاللاھنىڭ قىزلىرى، سىلەرنىڭ ئوغۇللىرىڭلار بارمۇ؟');
INSERT INTO "saleh" VALUES(52,40,'52:40','ياكى سەن ئۇلاردىن (دەۋىتىڭ ئۈچۈن) ھەق سوراپ، شۇ زىياندىن (ئۇلارغا) ئېغىر يۈك ئارتىلىپ قېلىپ (ئىمان ئېيتمامدۇ؟)');
INSERT INTO "saleh" VALUES(52,41,'52:41','ياكى ئۇلارنىڭ يېنىدا ئىلمى غەيب بولۇپ (ئۇنىڭدىن غەيب نەرسىلەرنى) يېزىۋالامدۇ؟');
INSERT INTO "saleh" VALUES(52,42,'52:42',' ياكى ئۇلار ساڭا ھىيلە ـ مىكىر ئىشلىتەمدۇ؟ كاپىرلارنىڭ ھىيلە ـ مىكىرلىرى مەغلۇپ بولىدۇ.');
INSERT INTO "saleh" VALUES(52,43,'52:43',' ياكى ئۇلارنىڭ ئاللاھتىن باشقا مەبۇدى بارمۇ؟ ئۇلارنىڭ شېرىك كەلتۈرگەن (بۇتلىرىدىن) ئاللاھ پاكتۇر.');
INSERT INTO "saleh" VALUES(52,44,'52:44','ئەگەر ئۇلار ئاسماننىڭ بىر پارچىسى چۈشۈپ كەتكەنلىكىنى كۆرسە: «بۇ توپلاشقان بۇلۇتتۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(52,45,'52:45','ئۇلارنى ھالاك قىلىدىغان كۈنگۈچە تەرك ئەتكىن.');
INSERT INTO "saleh" VALUES(52,46,'52:46','ئۇ كۈندە ئۇلارنىڭ ھىيلە ـ مىكرى ھېچ نەرسىگە ئەسقاتمايدۇ، ئۇلارغا ياردەممۇ قىلىنمايدۇ.');
INSERT INTO "saleh" VALUES(52,47,'52:47','ئۇلار ئۇنىڭدىن (يەنى ئاخىرەتتىكى ئازابتىن) بۇرۇن (دۇنيادىمۇ بىر تۈرلۈك ئازابقا دۇچار بولىدۇ)، لېكىن ئۇلارنىڭ تولىسى (بۇنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(52,48,'52:48','پەرۋەردىگارىڭنىڭ (ئۇلارغا بولىدىغان ئازابنى تەخىر قىلىش) ھۆكمىگە سەۋر قىلغىن، سەن ھەقىقەتەن بىزنىڭ ھىمايىمىزدىدۇرسەن، (ئۇيقۇدىن) تۇرغان ۋاقتىڭدا پەرۋەردىگارىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن.');
INSERT INTO "saleh" VALUES(52,49,'52:49',' كېچىدىمۇ، يۇلتۇزلار پاتقاندىن كېيىنمۇ ئۇنىڭغا تەسبىھ ئېيتقىن.');
INSERT INTO "saleh" VALUES(53,1,'53:1','ساقىغان يۇلتۇزلار بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(53,2,'53:2','سىلەرنىڭ ھەمراھىڭلار (يەنى مۇھەممەد ئەلەيھىسسالام) گۇمراھ بولمىدى ۋە يولدىن ئاداشمىدى.');
INSERT INTO "saleh" VALUES(53,3,'53:3','(ئۇ) ئۆز نەپسى خاھىشى بويىچە سۆزلىمەيدۇ.');
INSERT INTO "saleh" VALUES(53,4,'53:4','پەقەت ئۇنىڭغا نازىل قىلىنغان ۋەھىينىلا سۆزلەيدۇ.');
INSERT INTO "saleh" VALUES(53,5,'53:5','ۋەھىينى ئۇنىڭغا كۈچلۈك پەرىشتە (جىبرىئىل) تەلىم بەردى.');
INSERT INTO "saleh" VALUES(53,6,'53:6',' ئۇنىڭ (يەنى جىبرىئىلنىڭ) كۆرۈنۈشى چىرايلىق بولۇپ، يۇقىرى ئۇپۇقتا (ئۆز شەكلىدە) تۇردى[6ـ7].');
INSERT INTO "saleh" VALUES(53,7,'53:7',' ئۇنىڭ (يەنى جىبرىئىلنىڭ) كۆرۈنۈشى چىرايلىق بولۇپ، يۇقىرى ئۇپۇقتا (ئۆز شەكلىدە) تۇردى[6ـ7].');
INSERT INTO "saleh" VALUES(53,8,'53:8',' ئاندىن ئۇ ئاستا ـ ئاستا (مۇھەممەد ئەلەيھىسسالامغا) يېقىنلىشىپ تۆۋەنگە ساڭگىلىدى.');
INSERT INTO "saleh" VALUES(53,9,'53:9','ئۇ (پەيغەمبەر ئەلەيھىسسالامغا) ئىككى ياچاق مىقدارى ياكى ئۇنىڭدىنمۇ يېقىنراق يېقىنلاشتى.');
INSERT INTO "saleh" VALUES(53,10,'53:10',' ئاللاھ بەندىسىگە (جىبرىئىل ئارقىلىق تېگىشلىك) ۋەھىيلەرنى ۋەھىي قىلدى.');
INSERT INTO "saleh" VALUES(53,11,'53:11','ئۇنىڭ كۆرگىنىنى دىلى ئىنكار قىلمىدى (يەنى راست كۆردى).');
INSERT INTO "saleh" VALUES(53,12,'53:12','(ئى مۇشرىكلار جامائەسى!) ئۇنىڭ كۆرگەنلىرى ئۈستىدە ئۇنىڭ بىلەن مۇنازىرە قىلىشامسىلەر؟ ');
INSERT INTO "saleh" VALUES(53,13,'53:13',' ئۇ جىبرىئىلنى ھەقىقەتەن ئىككىنچى قېتىم سىدرەتۇلمۇنتەھا (دەرىخىنىڭ) يېنىدا كۆردى[13ـ14].');
INSERT INTO "saleh" VALUES(53,14,'53:14','ئىككىنچى قېتىم سىدرەتۇلمۇنتەھا (دەرىخىنىڭ) يېنىدا كۆردى[13ـ14].');
INSERT INTO "saleh" VALUES(53,15,'53:15','جەننەتۇلمەئۋا بولسا سىدرەتۇلمۇنتەھانىڭ يېنىدىدۇر.');
INSERT INTO "saleh" VALUES(53,16,'53:16',' (ئۇ) سىدرەتۇلمۇنتەھانى (ئالەملەرنىڭ پەرۋەردىگارىنىڭ نۇرى) قاپلىغان چاغدا (كۆردى).');
INSERT INTO "saleh" VALUES(53,17,'53:17','(پەيغەمبەر) نىڭ كۆزى (ئوڭ ـ سولغا) بۇرالمىدى، (كۆزلەنگەن نەرسىدىن) ئۆتۈپ كەتمىدى.');
INSERT INTO "saleh" VALUES(53,18,'53:18',' شۈبھىسىزكى، ئۇ پەرۋەردىگارىڭنىڭ (قۇدرىتىنى كۆرسىتىدىغان بۈيۈك ئالامەتلەرنى) كۆردى.');
INSERT INTO "saleh" VALUES(53,19,'53:19',' ئېيتىپ بېقىڭلارچۇ! لات، ئۇززا ۋە ئۈچىنچىسى بولغان ماناتلار (ئاللاھتائالادەك كۈچ ـ قۇۋۋەتكە ئىگىمۇ؟)[19ـ20].');
INSERT INTO "saleh" VALUES(53,20,'53:20',' ئېيتىپ بېقىڭلارچۇ! لات، ئۇززا ۋە ئۈچىنچىسى بولغان ماناتلار (ئاللاھتائالادەك كۈچ ـ قۇۋۋەتكە ئىگىمۇ؟)[19ـ20].');
INSERT INTO "saleh" VALUES(53,21,'53:21',' (ئى مۇشرىكلار جامائەسى!) (سىلەرچە ياخشى ھېسابلانغان) ئوغۇل بالا سىلەرگە خاس بولۇپ، (سىلەرچە يامان ھېسابلانغان) قىز بالا ئاللاھقا خاسمۇ؟');
INSERT INTO "saleh" VALUES(53,22,'53:22',' ئۇنداقتا بۇ ئادالەتسىز تەقسىماتتۇر. ');
INSERT INTO "saleh" VALUES(53,23,'53:23','بۇ بۇتلار پەقەت سىلەر ۋە ئاتا ـ بوۋاڭلار ئاتىۋالغان قۇرۇق ناملاردىنلا ئىبارەت، ئاللاھ بۇنى ئىسپاتلايدىغان ھېچبىر دەلىل ـ پاكىت چۈشۈرگىنى يوق، ئۇلارغا پەرۋەردىگارى تەرىپىدىن شەك ـ شۈبھىسىز ھىدايەت (يەنى پەيغەمبەر ۋە قۇرئان) كەلگەن تۇرۇقلۇق، ئۇلار پەقەت گۇمانغىلا ۋە نەپسىلىرىنىڭ خاھىشىغىلا ئەگىشىدۇ.');
INSERT INTO "saleh" VALUES(53,24,'53:24',' ئىنسان نېمىنى ئارزۇ قىلسا شۇ بولامدۇ؟');
INSERT INTO "saleh" VALUES(53,25,'53:25',' ئاخىرەت ۋە دۇنيا ئاللاھنىڭ ئىلكىدىدۇر (يەنى دۇنيا ۋە ئاخىرەتتىكى ئىشلارنىڭ ھەممىسى ئاللاھنىڭ خاھىشى بويىچە بولىدۇ).');
INSERT INTO "saleh" VALUES(53,26,'53:26',' ئاسمانلاردا نۇرغۇن پەرىشتىلەر بولۇپ، ئۇلارنىڭ شاپائىتى ھېچ نەرسىگە ئەسقاتمايدۇ، پەقەت ئاللاھ ئۆزى خالىغان ۋە رازى بولغان ئادەمگە شاپائەت قىلىشقا رۇخسەت قىلسىلا (ئاندىن) ئۇلارنىڭ شاپائىتى ئەسقاتىدۇ. ');
INSERT INTO "saleh" VALUES(53,27,'53:27','شۈبھىسىزكى، ئاخىرەتكە ئىمان ئېيتمايدىغانلار پەرىشتىلەرنى (ئاللاھنىڭ قىزلىرى دەپ) چىشى جىنس نامى بىلەن ئاتايدۇ.');
INSERT INTO "saleh" VALUES(53,28,'53:28',' ئۇلار مۇنداق ئاتاشتا ھېچقانداق ئىلىمگە ئىگە ئەمەس، ئۇلار پەقەت گۇمانغىلا ئاساسلىنىدۇ، گۇمان دېگەن ھەقنى ئىسپاتلاشتا ھېچ نەرسىگە يارىمايدۇ.');
INSERT INTO "saleh" VALUES(53,29,'53:29','بىزنىڭ زىكرىمىز (يەنى ئىمان بىلەن قۇرئان) دىن باش تارتقان، پەقەت دۇنيا تىرىكچىلىكنىلا كۆزلىگەنلەردىن يۈز ئۆرۈگىن.');
INSERT INTO "saleh" VALUES(53,30,'53:30',' ئۇلارنىڭ ئىلىمدىن يەتكەن يېرى ئەنە شۇ، شۈبھىسىزكى، پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغان ئادەمنى ئوبدان بىلىدۇ، ھىدايەت تاپقان ئادەمنىمۇ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(53,31,'53:31','ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھنىڭ مۈلكىدۇر، (ئاللاھ) يامانلىق قىلغانلارنى قىلمىشلىرىغا قاراپ جازالايدۇ، ياخشىلىق قىلغانلارغا چىرايلىق مۇكاپات بېرىدۇ.');
INSERT INTO "saleh" VALUES(53,32,'53:32','ئۇلار چوڭ گۇناھلاردىن ۋە قەبىھ ئىشلاردىن يىراق بولىدۇ. كىچىك گۇناھلار بۇنىڭدىن مۇستەسنا، ھەقىقەتەن پەرۋەردىگارىڭنىڭ مەغپىرىتى كەڭدۇر، ئاللاھ سىلەرنى زېمىندىن ياراتقان چاغدىكى (ۋاقتىڭلاردىن تارتىپ، ئاناڭلارنىڭ قورساقلىرىدىكى بالا) ۋاقتىڭلاردىن تارتىپ ئوبدان بىلىدۇ، شۇنىڭ ئۈچۈن سىلەر ئۆزۈڭلارنى پاك ھېسابلىماڭلار، ئاللاھ تەقۋادار بولغان ئادەمنى ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(53,33,'53:33',' (ئى مۇھەممەد!) ئىماندىن يۈز ئۆرۈگەن ئادەمدىن خەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(53,34,'53:34',' ئۇ (شەرت قىلىنغان مالدىن) ئازغىنا بەردى، قالغىنىنى بەرمىدى.');
INSERT INTO "saleh" VALUES(53,35,'53:35',' ئۇنىڭ دەرگاھىدا ئىلمىي غەيب بولۇپ، ئۇ غەيبنى بىلەمدۇ؟');
INSERT INTO "saleh" VALUES(53,36,'53:36',' ئۇ مۇسانىڭ ۋە ئىبراھىمنىڭ سەھىپىلىرىدىكى سۆزدىن خەۋەردار بولمىدىمۇ؟');
INSERT INTO "saleh" VALUES(53,37,'53:37','ئىبراھىم ۋاپادار كىشى ئىدى، ');
INSERT INTO "saleh" VALUES(53,38,'53:38','بىر گۇناھكار ئادەم يەنە بىراۋنىڭ گۇناھىنى كۆتەرمەيدۇ (يەنى بىراۋ باشقا بىراۋنىڭ گۇناھى تۈپەيلىدىن جازاغا تارتىلمايدۇ)[ 38].');
INSERT INTO "saleh" VALUES(53,39,'53:39',' ئىنسان پەقەت ئۆزىنىڭ ئىشلىگەن ئىشىنىڭ نەتىجىسىنى كۆرىدۇ.');
INSERT INTO "saleh" VALUES(53,40,'53:40',' ئۇنىڭ قىلغان ئىشى كەلگۈسىدە كۆرۈلىدۇ (يەنى ئۇنىڭ ئەمەلى قىيامەت كۈنى ئۇنىڭغا توغرىلىنىدۇ).');
INSERT INTO "saleh" VALUES(53,41,'53:41','ئاندىن ئۇنىڭغا تولۇق مۇكاپات (ياكى) تولۇق جازا بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(53,42,'53:42','ھەممىنىڭ ئاخىر بارىدىغان جايى پەرۋەردىگارىڭنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(53,43,'53:43','ئاللاھ ئىنساننى كۈلدۈرەلەيدۇ ۋە يىغلىتالايدۇ.');
INSERT INTO "saleh" VALUES(53,44,'53:44',' ئاللاھ ئۆلتۈرۈشكە ۋە تىرىلدۈرۈشكە قادىر.');
INSERT INTO "saleh" VALUES(53,45,'53:45',' ئاللاھ بىر جۈپنى ـ ئەركەك بىلەن چىشىنى ياراتتى.');
INSERT INTO "saleh" VALUES(53,46,'53:46',' ئېتىلىپ چىققان مەنىدىن (ياراتتى).');
INSERT INTO "saleh" VALUES(53,47,'53:47',' (ھېساب ئۈچۈن ئىنسانلارنى) ئىككىنچى قېتىم يارىتىشنى ئۈستىگە ئالدى.');
INSERT INTO "saleh" VALUES(53,48,'53:48',' ئاللاھ ئىنساننى باي قىلالايدۇ ۋە (ئاز بىلەنمۇ) مەمنۇن قىلالايدۇ.');
INSERT INTO "saleh" VALUES(53,49,'53:49',' ئاللاھ شىئرا يۇلتۇزىنىڭ پەرۋەردىگارىدۇر.');
INSERT INTO "saleh" VALUES(53,50,'53:50',' ئۇ (يەنى ئاللاھ) قەدىمكى ئادنى ھالاك قىلدى.');
INSERT INTO "saleh" VALUES(53,51,'53:51',' سەمۇدنىمۇ (ھالاك قىلدى، ئۇلاردىن بىرىنىمۇ) قويمىدى.');
INSERT INTO "saleh" VALUES(53,52,'53:52','ئىلگىرى نۇھنىڭ قەۋمىنى (ھالاك قىلغان ئىدى)، شۈبھىسىزكى، ئۇلار تېخىمۇ زالىم، تېخىمۇ سەركەش ئىدى.');
INSERT INTO "saleh" VALUES(53,53,'53:53','. لۇت قەۋمىنىڭ شەھەرلىرىنى دۈم كۆمتۈرىۋەتتى، ئۇنى (دەھشەتلىك تاشلار) قاپلىۋالدى[53 ـ 54].');
INSERT INTO "saleh" VALUES(53,54,'53:54','. لۇت قەۋمىنىڭ شەھەرلىرىنى دۈم كۆمتۈرىۋەتتى، ئۇنى (دەھشەتلىك تاشلار) قاپلىۋالدى[53 ـ 54].');
INSERT INTO "saleh" VALUES(53,55,'53:55','ئىنسان!) پەرۋەردىگارىڭنىڭ قايسىبىر نېمىتىدىن گۇمانلىنىسەن؟');
INSERT INTO "saleh" VALUES(53,56,'53:56',' بۇ (يەنى مۇھەممەد ئەلەيھىسسالام) ئىلگىرىكى ئاگاھلاندۇرغۇچىلارنىڭ جۈملىسىدىن بولغان بىر ئاگاھلاندۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(53,57,'53:57','قىيامەت يېقىنلاشتى.');
INSERT INTO "saleh" VALUES(53,58,'53:58','ئۇنىڭغا ئاللاھتىن باشقا كەشىپ قىلغۇچى (يەنى ئۇنىڭ ۋاقتىنى بىلگۈچى ياكى ئۇنىڭ ئازابىنى دەپئى قىلغۇچى) يوقتۇر.');
INSERT INTO "saleh" VALUES(53,59,'53:59','(ئى مۇشرىكلار جامائەسى!) مەسخىرە قىلىپ بۇ قۇرئاندىن ئەجەبلىنەمسىلەر؟.');
INSERT INTO "saleh" VALUES(53,60,'53:60','(ئۇنى ئاڭلىغان چاغدا) كۈلەمسىلەر؟ يىغلىمامسىلەر؟.');
INSERT INTO "saleh" VALUES(53,61,'53:61',' سىلەر غاپىلدۇرسىلەر.');
INSERT INTO "saleh" VALUES(53,62,'53:62','ئاللاھقا سەجدە قىلىڭلار ۋە (ئۇنىڭغا) ئىبادەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(54,1,'54:1','قىيامەت يېقىنلاشتى، ئاي يېرىلدى. ');
INSERT INTO "saleh" VALUES(54,2,'54:2',' (قۇرەيش كۇففارلىرى) بىرەر مۆجىزىنى كۆرسىلا (ئىماندىن) يۈز ئۆرۈپ، بۇ «داۋاملاشقۇچى سېھىردۇر» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(54,3,'54:3',' ئۇلار (پەيغەمبەر ئەلەيھىسسالامنى) ئىنكار قىلدى، نەپسى خاھىشلىرىغا ئەگەشتى، (ياخشىلىقتىن يا يامانلىقتىن بولسۇن) ھەر ئىش (شۇ ئىشنىڭ ئەھلىنى جەننەتكە يا دوزاخقا) ئورۇنلاشتۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(54,4,'54:4','ئۇلارغا ھەقىقەتەن (قۇرئان) خەۋەرلىرىدىن ئىبرەت ئېلىنىدىغان مىقداردا نەرسە كەلدى.');
INSERT INTO "saleh" VALUES(54,5,'54:5','مۇكەممەل ھېكمەت كەلدى، (ئاللاھ ئۇلارنى شەقى قىلغانلىقى ئۈچۈن) ئۇلارغا ئاگاھلاندۇرۇشلارنىڭ پايدىسى بولمايدۇ.');
INSERT INTO "saleh" VALUES(54,6,'54:6','ئۇلاردىن يۈز ئۆرۈگىن، ئۇ كۈندە چاقىرغۇچى (يەنى ئىسراپىل) قىيىن ئىشقا (يەنى ھېسابقا) چاقىرىدۇ (يەنى سۇر چالىدۇ).');
INSERT INTO "saleh" VALUES(54,7,'54:7','ئۇلار (قورقۇنچتىن) تىكىلىپ قارىيالمىغان ھالدا گويا تارىلىپ كەتكەن چېكەتكىدەك، قەبرىلىرىدىن چىقىپ كېلىدۇ.');
INSERT INTO "saleh" VALUES(54,8,'54:8','ئۇلار چاقىرغۇچىنىڭ ئالدىغا بويۇنلىرىنى سوزغان ھالدا يۈگرەيدۇ، كاپىرلار: «بۇ قىيىن كۈندۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(54,9,'54:9',' ئۇلاردىن بۇرۇن نۇھنىڭ قەۋمى ئىنكار قىلدى، ئۇلار بەندىمىزنى (يەنى نۇھنى) ئىنكار قىلىپ، مەجنۇن دېدى. ئۇ (دىنغا دەۋەت قىلىشتىن) ھەيۋە بىلەن مەنئى قىلىندى.');
INSERT INTO "saleh" VALUES(54,10,'54:10',' ئۇ پەرۋەردىگارىڭغا: «ھەقىقەتەن مەن بوزەك قىلىندىم، ياردەم قىلغىن» دەپ دۇئا قىلدى.');
INSERT INTO "saleh" VALUES(54,11,'54:11','ئاسماننىڭ دەرۋازىلىرىنى قۇيۇلۇپ ياغقۇچى يامغۇر بىلەن ئېچىۋەتتۇق.');
INSERT INTO "saleh" VALUES(54,12,'54:12','زېمىندىن بۇلاقلارنى ئېتىلدۇرۇپ چىقاردۇق، (ئاللاھ تەقدىر قىلغان ئۇلارنى غەرق قىلىپ ھالاك قىلىش) ئىشىغا بىنائەن، يامغۇر سۈيى بىلەن بۇلاق سۈيى (بىر ـ بىرىگە) قوشۇلدى.');
INSERT INTO "saleh" VALUES(54,13,'54:13','نۇھنى تاختا ۋە مىخلار بىلەن ياسالغان كېمىگە سالدۇق.');
INSERT INTO "saleh" VALUES(54,14,'54:14',' كېمە بىزنىڭ ھىمايىمىز ئاستىدا ماڭاتتى، ئىنكار قىلىنغاننى (يەنى نۇھنى) مۇكاپاتلاش ئۈچۈن (نۇھنىڭ قەۋمىنى غەرق قىلدۇق).');
INSERT INTO "saleh" VALUES(54,15,'54:15',' ئۇنى (يەنى توپان بالاسىنى) ئىبرەت قىلىپ قالدۇردۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO "saleh" VALUES(54,16,'54:16',' مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO "saleh" VALUES(54,17,'54:17',' ھەقىقەتەن بىز قۇرئاننى چۈشىنىپ (ۋەز – نەسىھەت ئېلىش) ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO "saleh" VALUES(54,18,'54:18','ئاد قەۋمى (پەيغەمبىرى ھۇدنى) ئىنكار قىلدى، مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO "saleh" VALUES(54,19,'54:19','شۈبھىسىزكى، ئۇلارنى بىز شۇملۇقى ئۈزۈلمەيدىغان شۇم كۈندە سوغۇق بوران ئەۋەتىپ (ھالاك قىلدۇق)، ');
INSERT INTO "saleh" VALUES(54,20,'54:20','بوران كىشىلەرنى (ئورۇنلىرىدىن) يۇلۇپ كېتەتتى، گويا ئۇلار قومۇرىۋېتىلگەن خورما كۆتەكلىرىدەك ياتاتتى[ـ20].');
INSERT INTO "saleh" VALUES(54,21,'54:21','مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن!');
INSERT INTO "saleh" VALUES(54,22,'54:22',' بىز ھەقىقەتەن قۇرئاننى ھىپزى ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO "saleh" VALUES(54,23,'54:23','سەمۇد ئاگاھلاندۇرغۇچىلارنى (يەنى پەيغەمبەرلەرنى) يالغانغا چىقاردى.');
INSERT INTO "saleh" VALUES(54,24,'54:24','ئۇلار ئېيتتى: «ئارىمىزدىكى (بىزگە ئوخشاش) بىر ئادەمگە ئەگىشەمدۇق؟ (ئۇنداق قىلساق) ئۇ چاغدا بىز ھەقىقەتەن گۇمراھلىققا ۋە ساراڭلىققا مۇپتىلا بولىمىز.');
INSERT INTO "saleh" VALUES(54,25,'54:25',' ئارىمىزدىن ئۇنىڭغا ۋەھىي نازىل قىلىندىمۇ؟ ھەرگىز ئۇنداق ئەمەس، ئۇ يالغانچىدۇر، مۇتەكەببىردۇر».');
INSERT INTO "saleh" VALUES(54,26,'54:26','ئۇلار ئەتە (يەنى ئاخىرەتتە) كىمنىڭ يالغانچى، مۇتەكەببىر ئىكەنلىكىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(54,27,'54:27',' بىز ھەقىقەتەن سىناش ئۈچۈن ئۇلارغا چىشى تۆگىنى ئەۋەتىمىز، (ئى سالىھ) ئۇلارنى (ئۇلارنىڭ نېمە قىلارىنى) كۈتكىن، (ئۇلارنىڭ ئەزىيەتلىرىگە) سەۋر قىلغىن.');
INSERT INTO "saleh" VALUES(54,28,'54:28',' سۇنىڭ ئۇلار بىلەن تۆگىنىڭ ئارىسىدا بۆلۈنگەنلىكىنى، قايسىسىنىڭ نۆۋىتى كەلسە شۇنىڭ ھازىر بولىدىغانلىقىنى ئۇلارغا ئۇقتۇرۇپ قويغىن.');
INSERT INTO "saleh" VALUES(54,29,'54:29',' ئۇلار بۇرادىرىنى (تۆگىنى ئۆلتۈرۈشكە) چاقىردى. ئۇ قىلىچنى ئېلىپ تۆگىنى بوغۇزلىدى.');
INSERT INTO "saleh" VALUES(54,30,'54:30','مېنىڭ ئازابىم ۋە ئاگاھلاندۇرۇشلىرىم قانداق ئىكەن؟ ');
INSERT INTO "saleh" VALUES(54,31,'54:31','شۈبھىسىزكى، بىز ئۇلارغا قاتتىق بىر ئاۋازنى ئەۋەتىپ (ھالاك قىلىش بىلەن) ئۇلارنى قوتان ياسىغۇچىنىڭ تاشلاندۇق شاخ ـ شۇمبىلىرىدەك قىلىۋەتتۇق.');
INSERT INTO "saleh" VALUES(54,32,'54:32',' بىز ھەقىقەتەن قۇرئاننى چۈشىنىپ (ۋەز – نەسىھەت ئېلىش) ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO "saleh" VALUES(54,33,'54:33','لۇتنىڭ قەۋمى ئاگاھلاندۇرۇشلارغا چىنپۈتمىدى.');
INSERT INTO "saleh" VALUES(54,34,'54:34',' بىز ھەقىقەتەن ئۇلارنى تاش ياغدۇرۇپ (ھالاك قىلدۇق)، پەقەت لۇتنىڭ تەۋەلىرى بۇنىڭ سىرتىدىدۇر. ئۇلارنى نېمىتىمىز يۈزىسىدىن سەھەردە قۇتۇلدۇردۇق، شۈكۈر قىلغۇچىنى بىز مۇشۇنداق مۇكاپاتلايمىز[34ـ35].');
INSERT INTO "saleh" VALUES(54,35,'54:35',' بىز ھەقىقەتەن ئۇلارنى تاش ياغدۇرۇپ (ھالاك قىلدۇق)، پەقەت لۇتنىڭ تەۋەلىرى بۇنىڭ سىرتىدىدۇر. ئۇلارنى نېمىتىمىز يۈزىسىدىن سەھەردە قۇتۇلدۇردۇق، شۈكۈر قىلغۇچىنى بىز مۇشۇنداق مۇكاپاتلايمىز[34ـ35].');
INSERT INTO "saleh" VALUES(54,36,'54:36',' شۈبھىسىزكى، لۇت ئۇلارنى بىزنىڭ جازالىشىمىزدىن ئاگاھلاندۇردى، ئۇلار ئاگاھلاندۇرۇشلاردىن شەكلەندى.');
INSERT INTO "saleh" VALUES(54,37,'54:37','ئۇلار لۇتتىن مېھمانلىرىنى (يەنى ئادەمزات شەكلىدە كەلگەن پەرىشتىلەرنى) (ئۇلار بىلەن لىۋاتە قىلىش ئۈچۈن) قوغدىماسلىقنى سورىدى. ئۇلارنىڭ كۆزلىرىنى كور قىلىۋەتتۇق، (ئۇلارغا) «ئازابىمنى ۋە ئاگاھلاندۇرۇشلىرىمنى تېتىڭلار» (دېدۇق).');
INSERT INTO "saleh" VALUES(54,38,'54:38','ھەقىقەتەن ئۇلارغا ئەتىگەندە دائىمىي (يەنى ئاخىرەتنىڭ ئازابىغا ئۇلىنىپ كېتىدىغان) ئازاب نازىل بولدى.');
INSERT INTO "saleh" VALUES(54,39,'54:39',' (ئۇلارغا): «مېنىڭ ئازابىمنى ۋە ئاگاھلاندۇرۇشلىرىمنى تېتىڭلار» (دېيىلدى).');
INSERT INTO "saleh" VALUES(54,40,'54:40',' بىز ھەقىقەتەن قۇرئاننى چۈشىنىپ (ۋەز – نەسىھەت ئېلىش) ئۈچۈن ئاسان قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟.');
INSERT INTO "saleh" VALUES(54,41,'54:41','شەك ـ شۈبھىسىزكى، پىرئەۋن جامائەسىگە ئاگاھلاندۇرۇشلار كەلدى.');
INSERT INTO "saleh" VALUES(54,42,'54:42','ئۇلار بىزنىڭ پۈتۈن ئايەتلىرىمىزنى يالغانغا چىقاردى، ئۇلارنى غالىب، قۇدرەتلىك ھالەتتە تۇرۇپ ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(54,43,'54:43',' (ئى ئەرەب جامائەسى!) ئۇلار (يەنى مەن ھالاك قىلغان ئۆتكەنكى ئۈممەتلەر) دىن سىلەرنىڭ كۇففارلىرىڭلار ئارتۇقمۇ؟ ياكى سىلەرگە ساماۋى كىتابلاردا (ئاللاھنىڭ ئازابىدىن) كەچۈرۈم قىلىنىش بارمۇ؟');
INSERT INTO "saleh" VALUES(54,44,'54:44',' ياكى ئۇلار «بىز (مۇھەممەد ئۈستىدىن) غالىب قەۋممىز» دەمدۇ؟');
INSERT INTO "saleh" VALUES(54,45,'54:45','(مۇشرىكلار) توپى مەغلۇپ قىلىنىدۇ، ئارقىغا قاراپ قاچىدۇ.');
INSERT INTO "saleh" VALUES(54,46,'54:46','ھەرگىز ئۇنداق ئەمەس (يەنى ئۇلارغا بېرىلىدىغان ئازاب بۇلا ئەمەس)، قىيامەت ئۇلارغا (ئازاب قىلىنىش) ۋەدە قىلىنغان ۋاقىتتۇر، قىيامەت تېخىمۇ قىيىندۇر، تېخىمۇ جاپالىقتۇر.');
INSERT INTO "saleh" VALUES(54,47,'54:47',' گۇناھكارلار ھەقىقەتەن (دۇنيادا) گۇمراھلىقتىدۇر، (ئاخىرەتتە) دوزاختىدۇر.');
INSERT INTO "saleh" VALUES(54,48,'54:48','ئۇلار دوزاختا دۈم ياتقۇزۇلۇپ سورىلىدىغان كۈندە، (ئۇلارغا) «دوزاخنىڭ ئازابىنى تېتىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(54,49,'54:49',' بىز ھەقىقەتەن ھەممە نەرسىنى ئۆلچەملىك ياراتتۇق.');
INSERT INTO "saleh" VALUES(54,50,'54:50',' (بىرەر شەيئىنى ياراتماقچى بولساق) بىزنىڭ ئەمرىمىز پەقەت بىر سۆزدۇر، (ئۇ) كۆزنى يۇمۇپ ئاچقاننىڭ ئارىلىقىدا (ئورۇنلىنىدۇ).');
INSERT INTO "saleh" VALUES(54,51,'54:51',' بىز ھەقىقەتەن (ئۆتكەنكى ئۈممەتلەردىن كۇفرىدا) سىلەرگە ئوخشاش بولغانلارنى ھالاك قىلدۇق، ئىبرەت ئالغۇچى بارمۇ؟');
INSERT INTO "saleh" VALUES(54,52,'54:52','ئۇلارنىڭ قىلغان (ياخشى ۋە يامان) ئىشلىرىنىڭ ھەممىسى نامە ـ ئەماللىرىدا مەۋجۇتتۇر.');
INSERT INTO "saleh" VALUES(54,53,'54:53','كىچىك ئىش، چوڭ ئىشنىڭ ھەممىسى خاتىرىلەنگەندۇر.');
INSERT INTO "saleh" VALUES(54,54,'54:54','شۈبھىسىزكى، تەقۋادارلار جەننەتلەردە بولىدۇ، (سۇ، مەي، ھەسەل، سۈت) ئۆستەڭلىرىدىن بەھرىمەن بولىدۇ.');
INSERT INTO "saleh" VALUES(54,55,'54:55','(ئۇلار) قۇدرەتلىك ئاللاھنىڭ دەرگاھىدا كۆڭۈلدىكىدەك جايدا بولىدۇ.');
INSERT INTO "saleh" VALUES(55,1,'55:1','مېھرىبان ئاللاھ ');
INSERT INTO "saleh" VALUES(55,2,'55:2','قۇرئاننى تەلىم بەردى (ئۇنى ھىپزى قىلىشنى ۋە چۈشىنىشنى ئاسانلاشتۇرۇپ بەردى)،');
INSERT INTO "saleh" VALUES(55,3,'55:3','ئىنساننى ياراتتى.');
INSERT INTO "saleh" VALUES(55,4,'55:4','ئۇنىڭغا (مەقسىتىنى ئۇقتۇرۇش ئۈچۈن) سۆزلەشنى ئۆگەتتى.');
INSERT INTO "saleh" VALUES(55,5,'55:5','كۈن بىلەن ئاي (ئۆز بۇرجلىرىدا) مۇئەييەن ھېساب بويىچە سەير قىلىدۇ.');
INSERT INTO "saleh" VALUES(55,6,'55:6','ئوت ـ چۆپلەر، دەل ـ دەرەخلەر سەجدە قىلىدۇ (يەنى ئاللاھنىڭ ئىرادىسىگە بويسۇنىدۇ).');
INSERT INTO "saleh" VALUES(55,7,'55:7','ئاللاھ ئاسماننى ئېگىز ياراتتى. ئۆلچەمدە زۇلۇم قىلماسلىقىڭلار ئۈچۈن، ئاللاھ تارازىنى بېكىتتى[7ـ8]،');
INSERT INTO "saleh" VALUES(55,8,'55:8','ئاللاھ ئاسماننى ئېگىز ياراتتى. ئۆلچەمدە زۇلۇم قىلماسلىقىڭلار ئۈچۈن، ئاللاھ تارازىنى بېكىتتى[7ـ8]،');
INSERT INTO "saleh" VALUES(55,9,'55:9','ئۆلچەمدە ئادىل بولۇڭلار، تارازىدا كەم بەرمەڭلار.');
INSERT INTO "saleh" VALUES(55,10,'55:10','زېمىننى كىشىلەرنىڭ (پايدىلىنىشى) ئۈچۈن (يايپاڭ) قىلدى.');
INSERT INTO "saleh" VALUES(55,11,'55:11','زېمىندا مېۋىلەر، بوغۇنلۇق خورمىلار بار.');
INSERT INTO "saleh" VALUES(55,12,'55:12','سامىنى بار دانلىق زىرائەتلەر، خۇش بۇي ئۆسۈملۈكلەر بار.');
INSERT INTO "saleh" VALUES(55,13,'55:13',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,14,'55:14','(ئاللاھ) ئىنساننى (يەنى ئاتاڭلار ئادەمنى) (چەكسە جاراڭلايدىغان) ساپالدەك قۇرۇق لايدىن ياراتتى.');
INSERT INTO "saleh" VALUES(55,15,'55:15',' جىنلارنى ئوتنىڭ يالقۇنىدىن ياراتتى.');
INSERT INTO "saleh" VALUES(55,16,'55:16',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,17,'55:17',' ئۇ ئىككى مەشرىق (يەنى كۈن بىلەن ئاينىڭ چىقىدىغان ئىككى جايى) نىڭ پەرۋەردىگارىدۇر، ئىككى مەغرىب (يەنى كۈن بىلەن ئاينىڭ پاتىدىغان ئىككى جايى) نىڭ پەرۋەردىگارىدۇر.');
INSERT INTO "saleh" VALUES(55,18,'55:18',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟ ');
INSERT INTO "saleh" VALUES(55,19,'55:19','ئۇ ئىككى دېڭىزنى (بىرى تاتلىق، بىرى ئاچچىق) ئاققۇزدى، ئۇلار ئۇچرىشىدۇ.');
INSERT INTO "saleh" VALUES(55,20,'55:20',' ئۇلارنىڭ ئارىسىدا توسما بولۇپ، بىر ـ بىرىگە قوشۇلۇپ كەتمەيدۇ.');
INSERT INTO "saleh" VALUES(55,21,'55:21',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,22,'55:22','ئۇلاردىن مەرۋايىت بىلەن مارجان چىقىدۇ. ');
INSERT INTO "saleh" VALUES(55,23,'55:23',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟ ');
INSERT INTO "saleh" VALUES(55,24,'55:24',' دېڭىزلاردا قاتناۋاتقان تاغدەك كېمىلەر ئاللاھنىڭ ئىلكىدىدۇر. ');
INSERT INTO "saleh" VALUES(55,25,'55:25',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,26,'55:26',' زېمىننىڭ ئۈستىدىكى ھەممە يوقىلىدۇ.');
INSERT INTO "saleh" VALUES(55,27,'55:27',' ئەزىمەتلىك ۋە كەرەملىك پەرۋەردىگارىڭنىڭ زاتى مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(55,28,'55:28','(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,29,'55:29','ئاسمانلاردىكى ۋە زېمىندىكى ھەممە ئاللاھتىن (مەدەت ۋە رىزىق) تىلەيدۇ (يەنى ھەممە ئاللاھقا موھتاجدۇر)، ئاللاھ ھەر كۈنى بىر ھالەتتىدۇر (يەنى ئاللاھ ھەر ۋاقىت ۋۇجۇدقا چىقىرىدىغان ئىشلار بىلەن بولۇپ، ئۇلارنى يارىتىپ تۇرىدۇ، ئەھۋاللارنى يېڭىلاپ تۇرىدۇ، مەخلۇقاتلارنى پەيدا قىلىپ تۇرىدۇ). ');
INSERT INTO "saleh" VALUES(55,30,'55:30',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,31,'55:31','ئى ئىنسانلار ۋە جىنلار جامائەسى! سىلەردىن (يەنى سىلەرنىڭ ئەمەللىرىڭلاردىن) ھېساب ئېلىشقا يۈزلىنىمىز.');
INSERT INTO "saleh" VALUES(55,32,'55:32',' (ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,33,'55:33',' ئى جىنلار ۋە ئىنسانلار جامائەسى! ئەگەر سىلەر ئاسمانلارنىڭ ۋە زېمىننىڭ چېگرىلىرىدىن ئۆتۈپ كېتەلىسەڭلار، ئۆتۈپ كېتىڭلار، سىلەر پەقەت قۇۋۋەت بىلەن ئۆتۈپ كېتەلەيسىلەر .');
INSERT INTO "saleh" VALUES(55,34,'55:34','(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,35,'55:35','(قىيامەت كۈنى) سىلەرگە قىزىق ئوتنىڭ يالقۇنى ۋە تۈتۈنى ئەۋەتىلىدۇ، سىلەر ئۆزۈڭلارنى قوغدىيالمايسىلەر.');
INSERT INTO "saleh" VALUES(55,36,'55:36',' (ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,37,'55:37',' (قىيامەت كۈنى پەرىشتىلەرنىڭ چۈشۈشى ئۈچۈن) ئاسمان يېرىلغان چاغدا، ئاسمان (دوزاخنىڭ قىزىقلىقىدىن) قىزىل گۈلدەك، قىزىل چەمدەك بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(55,38,'55:38','(ئى ئىنسانلار! جىنلار!) پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟ ');
INSERT INTO "saleh" VALUES(55,39,'55:39',' بۇ كۈندە ھېچبىر ئىنسان ۋە جىن ئۆزىنىڭ گۇناھىدىن سورالمايدۇ (يەنى گۇناھلىرىنىڭ ئالاھىدە ئالامىتى بولغانلىقتىن مۇنداق سوراشقا ھاجەت بولمايدۇ.');
INSERT INTO "saleh" VALUES(55,40,'55:40',' (ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,41,'55:41',' گۇناھكارلار ئۆزلىرىنىڭ (يۈزلىرى قارا، كۆزلىرى كۆك بولۇشتىن ئىبارەت) بەلگىلىرىدىن تونۇلىدۇ، ئۇلار كوكۇلىلىرىدىن ۋە پۇتلىرىدىن تۇتۇلۇپ (دوزاخقا تاشلىنىدۇ).');
INSERT INTO "saleh" VALUES(55,42,'55:42','(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,43,'55:43',' بۇ، گۇناھكارلار ئىنكار قىلىدىغان جەھەننەمدۇر.');
INSERT INTO "saleh" VALUES(55,44,'55:44',' ئۇلار دوزاخ بىلەن قايناقسۇ ئارىسىدا بېرىپ ـ كېلىپ تۇرىدۇ (يەنى گاھىدا ئوت بىلەن، گاھىدا قايناقسۇ بىلەن ئازابلىنىدۇ.');
INSERT INTO "saleh" VALUES(55,45,'55:45','(ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,46,'55:46','پەرۋەردىگارىڭنىڭ ئالدىدا تۇرۇپ (ھېساب بېرىشتىن) قورققان ئادەمگە ئىككى جەننەت بار.');
INSERT INTO "saleh" VALUES(55,47,'55:47',' (ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,48,'55:48',' ئۇ ئىككى جەننەتتە تۈرلۈك مېۋىلىك دەرەخلەر بار.');
INSERT INTO "saleh" VALUES(55,49,'55:49',' (ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
INSERT INTO "saleh" VALUES(55,50,'55:50',' ئۇ ئىككى جەننەتتە ئېقىپ تۇرغان ئىككى بۇلاق بار.');
INSERT INTO "saleh" VALUES(55,51,'55:51',' (ئى ئىنسانلار! جىنلار!) سىلەر پەرۋەردىگارىڭلارنىڭ قايسى نېمەتلىرىنى ئىنكار قىلىسىلەر؟');
