INSERT INTO "saleh" VALUES(56,3,'56:3','زېمىن قاتتىق تەۋرىتىلگەن، تاغلار پارچىلىنىپ توزاندەك توزۇپ كەتكەن چاغدا، قىيامەت (بەزىلەرنى دوزاخقا كىرگۈزۈش بىلەن دەرىجىسىنى) چۈشۈرىدۇ، (بەزىلەرنى جەننەتكە كىرگۈزۈش بىلەن دەرىجىسىنى) كۆتۈرىدۇ[3ـ6].');
