INSERT INTO "saleh" VALUES(56,8,'56:8',' (بىرىنچى پىرقە) سائادەتمەنلەردۇر، سائادەتمەنلەر قانداق ئادەملەر؟ (ئۇلار نامە ـ ئەمالى ئوڭ تەرىپىدىن بېرىلىدىغان) ئادەملەردۇر.');
INSERT INTO "saleh" VALUES(56,9,'56:9',' (ئىككىنچى پىرقە) بەختسىز ئادەملەردۇر، بەختسىز ئادەملەر قانداق ئادەملەر؟ (ئۇلار نامە ـ ئەمالى سول تەرىپىدىن بېرىلىدىغان ئادەملەردۇر).');
INSERT INTO "saleh" VALUES(56,10,'56:10',' (ئۈچىنچى پىرقە ياخشى ئىشلارنى) ئەڭ ئالدىدا قىلغۇچىلار بولۇپ، (ئۇلار جەننەتكە) ئەڭ ئالدىدا كىرگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(56,11,'56:11',' ئۇلار نازۇنېمەتلىك جەننەتلەردە ئاللاھقا يېقىن بولغۇچىلاردۇر[11ـ12].');
