INSERT INTO "saleh" VALUES(56,19,'56:19',' ئۇ شارابنى ئىچىش بىلەن ئۇلارنىڭ بېشى ئاغرىمايدۇ، مەست بولمايدۇ.');
INSERT INTO "saleh" VALUES(56,20,'56:20','ئۇلارغا ئىختىيار قىلغان مېۋىلەر ۋە كۆڭۈللىرى تارتقان قۇش گۆشلىرى بېرىلىدۇ[20ـ21].');
