INSERT INTO "saleh" VALUES(56,22,'56:22','ئۇلارغا سەدەپنىڭ ئىچىدىكى گۆھەرگە ئوخشايدىغان شەھلا كۆزلۈك ھۆرلەر بېرىلىدۇ.');
