INSERT INTO "saleh" VALUES(56,42,'56:42',' ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ[42ـ44].');
INSERT INTO "saleh" VALUES(56,43,'56:43',' ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ[42ـ44].');
INSERT INTO "saleh" VALUES(56,44,'56:44',' ئۇلار (بەدەننىڭ تۆشۈكلىرىگە كىرىپ كېتىدىغان ئاتەشلىك شامالنىڭ، زىيادە ھارارەتلىك قايناقسۇنىڭ ۋە قارا تۈتۈندىن بولغان سالقىنمۇ ئەمەس، كۆركەممۇ ئەمەس سايىنىڭ ئىچىدە بولىدۇ[42ـ44].');
INSERT INTO "saleh" VALUES(56,45,'56:45','چۈنكى ئۇلار بۇنىڭدىن ئىلگىرى (يەنى دۇنيادا) شەھۋەتكە چۆمگەن ئىدى.');
INSERT INTO "saleh" VALUES(56,46,'56:46',' ئۇلار چوڭ گۇناھتا (يەنى كۇفرىدا) چىڭ تۇرغان ئىدى.');
INSERT INTO "saleh" VALUES(56,47,'56:47','ئۇلار: «بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا چوقۇم تىرىلدۈرۈلەمدۇق؟ ئاتا ـ بوۋىلىرىمىزمۇ تىرىلدۈرۈلەمدۇ؟» دەيتتى[47ـ48].');
INSERT INTO "saleh" VALUES(56,48,'56:48','ئۇلار: «بىز ئۆلۈپ توپىغا ۋە قۇرۇق سۆڭەككە ئايلانغان چاغدا چوقۇم تىرىلدۈرۈلەمدۇق؟ ئاتا ـ بوۋىلىرىمىزمۇ تىرىلدۈرۈلەمدۇ؟» دەيتتى[47ـ48].');
INSERT INTO "saleh" VALUES(56,49,'56:49',' ئېيتقىنكى، «ئىلگىرىكىلەر ۋە كېيىنكىلەر مەلۇم كۈننىڭ مۇئەييەن ۋاقتىدا (يەنى قىيامەتتە) توپلىنىدۇ[49ـ50].');
INSERT INTO "saleh" VALUES(56,50,'56:50',' ئېيتقىنكى، «ئىلگىرىكىلەر ۋە كېيىنكىلەر مەلۇم كۈننىڭ مۇئەييەن ۋاقتىدا (يەنى قىيامەتتە) توپلىنىدۇ[49ـ50].');
INSERT INTO "saleh" VALUES(56,51,'56:51','ئاندىن سىلەر، ئى قايتا تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلار!');
INSERT INTO "saleh" VALUES(56,52,'56:52','چوقۇم زەققۇم دەرىخىدىن يەيسىلەر.');
INSERT INTO "saleh" VALUES(56,53,'56:53',' ئۇنىڭدىن قورساقلىرىڭلارنى تولدۇرىسىلەر.');
INSERT INTO "saleh" VALUES(56,54,'56:54','ئۇنىڭ ئۈستىگە ھارارىتى يۇقىرى بولغان قايناقسۇنى تەشنا بولغان تۆگىلەردەك ئىچىسىلەر»[54ـ55].');
INSERT INTO "saleh" VALUES(56,55,'56:55','ئۇنىڭ ئۈستىگە ھارارىتى يۇقىرى بولغان قايناقسۇنى تەشنا بولغان تۆگىلەردەك ئىچىسىلەر»[54ـ55].');
INSERT INTO "saleh" VALUES(56,56,'56:56',' مانا بۇ، ئۇلارنىڭ قىيامەت كۈنىدىكى زىياپىتىدۇر.');
INSERT INTO "saleh" VALUES(56,57,'56:57',' (ئىنسانلار!) سىلەرنى بىز ياراتتۇق، سىلەر قايتا تىرىلىشكە ئىشەنمەمسىلەر؟');
INSERT INTO "saleh" VALUES(56,58,'56:58','سىلەر (ئاياللارنىڭ بەچچىدانىغا) تۆكۈلگەن مەنىنى دەپ بېقىڭلارچۇ! ');
INSERT INTO "saleh" VALUES(56,59,'56:59','ئۇنى سىلەر (ئىنسان قىلىپ) يارىتامسىلەر؟ ياكى بىز (ئىنسان قىلىپ) يارىتامدۇق؟');
INSERT INTO "saleh" VALUES(56,60,'56:60',' بىز سىلەرنىڭ ئاراڭلاردا ئۆلۈمنى بەلگىلىدۇق (يەنى ھەر بىرىڭلارنىڭ ئۆلۈش ۋاقتىنى بېكىتتۇق)، سىلەرنى باشقا بىر قەۋمگە ئالماشتۇرۇشتىن ۋە سىلەرنى سىلەر بىلمەيدىغان بىر شەكىلدە يارىتىشتىن ئاجىز ئەمەسمىز[60ـ61].');
INSERT INTO "saleh" VALUES(56,61,'56:61',' بىز سىلەرنىڭ ئاراڭلاردا ئۆلۈمنى بەلگىلىدۇق (يەنى ھەر بىرىڭلارنىڭ ئۆلۈش ۋاقتىنى بېكىتتۇق)، سىلەرنى باشقا بىر قەۋمگە ئالماشتۇرۇشتىن ۋە سىلەرنى سىلەر بىلمەيدىغان بىر شەكىلدە يارىتىشتىن ئاجىز ئەمەسمىز[60ـ61].');
INSERT INTO "saleh" VALUES(56,62,'56:62','شۈبھىسىزكى، سىلەر دەسلەپكى يارىتىشنى بىلدىڭلار (يەنى دەسلەپتە ئاللاھنىڭ سىلەرنى يوقتىن بار قىلغانلىقىنى تونۇدۇڭلار). سىلەر (ئاللاھنىڭ سىلەرنى دەسلەپ ياراتقاندەك قايتا يارىتىشىنى) ئويلىمامسىلەر؟ ');
INSERT INTO "saleh" VALUES(56,63,'56:63',' سىلەر تېرىغان زىرائەتنى دەپ بېقىڭلارچۇ!');
INSERT INTO "saleh" VALUES(56,64,'56:64','ئۇنى سىلەر ئۈندۈردۈڭلارمۇ ياكى بىز ئۈندۈرۈپ (دان چىقىدىغان زىرائەت قىلىپ ئۆستۈردۇقمۇ؟).');
INSERT INTO "saleh" VALUES(56,65,'56:65','ئەگەر بىز خالىساق ئەلۋەتتە ئۇنى دانسىز قۇرۇق چۆپكە ئايلاندۇراتتۇق ـ دە، سىلەر ئەجەبلىنىپ قايغۇراتتىڭلار.');
INSERT INTO "saleh" VALUES(56,66,'56:66','سىلەر: «بىز ھەقىقەتەن (ئەمگەك بىلەن ئۇرۇقنى) زىيان تارتتۇق، بەلكى بىز (رىزىقتىن) مەھرۇم قالدۇق» (دەيتتىڭلار)[66ـ67].');
INSERT INTO "saleh" VALUES(56,67,'56:67','سىلەر: «بىز ھەقىقەتەن (ئەمگەك بىلەن ئۇرۇقنى) زىيان تارتتۇق، بەلكى بىز (رىزىقتىن) مەھرۇم قالدۇق» (دەيتتىڭلار)[66ـ67].');
INSERT INTO "saleh" VALUES(56,68,'56:68','سىلەر ئىچىۋاتقان سۇنى دەپ بېقىڭلارچۇ!');
INSERT INTO "saleh" VALUES(56,69,'56:69','ئۇنى بۇلۇتتىن سىلەر چۈشۈردۈڭلارمۇ ياكى بىز چۈشۈردۇقمۇ؟');
INSERT INTO "saleh" VALUES(56,70,'56:70','ئەگەر بىز خالىساق ئۇنى تۇزلۇق قىلىپ قوياتتۇق (پەرۋەردىگارىڭلارنىڭ زور نېمەتلىرىگە) نېمىشقا شۈكۈر قىلمايسىلەر؟ ');
INSERT INTO "saleh" VALUES(56,71,'56:71',' سىلەر ياندۇرۇۋاتقان ئوتنى دەپ بېقىڭلارچۇ!');
INSERT INTO "saleh" VALUES(56,72,'56:72','ئۇنىڭ دەرىخىنى سىلەر ئۆستۈردۈڭلارمۇ ياكى بىز ئۆستۈردۇقمۇ؟');
INSERT INTO "saleh" VALUES(56,73,'56:73','ئۇنى بىز ئىبرەت ۋە يولۇچىلار پايدىلىنىدىغان نەرسە قىلدۇق.');
INSERT INTO "saleh" VALUES(56,74,'56:74','ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئىپتىرالىرىدىن) پاك ئېتىقاد قىلغىن.');
INSERT INTO "saleh" VALUES(56,75,'56:75','يۇلتۇزلارنىڭ جايلىرى بىلەن قەسەم قىلىمەن.');
INSERT INTO "saleh" VALUES(56,76,'56:76','شۈبھىسىزكى، ئەگەر بىلسەڭلار، ئۇ (يۇلتۇزلار ئاللاھنىڭ ئۇلۇغ قۇدرىتىنى كۆرسىتىدىغانلىقى ئۈچۈن) ئەلۋەتتە كاتتا قەسەمدۇر.');
INSERT INTO "saleh" VALUES(56,77,'56:77','شەك ـ شۈبھىسىزكى، ئۇ ئۇلۇغ قۇرئاندۇر.');
INSERT INTO "saleh" VALUES(56,78,'56:78','كىتابتا (يەنى لەۋھۇلمەھپۇزدا) ساقلانغاندۇر.');
INSERT INTO "saleh" VALUES(56,79,'56:79','ئۇنى پەقەت پاك بولغانلارلا تۇتىدۇ.');
INSERT INTO "saleh" VALUES(56,80,'56:80',' ئۇ ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر. ');
INSERT INTO "saleh" VALUES(56,81,'56:81','(ئى كۇففارلار جامائەسى!) بۇ قۇرئاننى ئىنكار قىلامسىلەر؟ ');
INSERT INTO "saleh" VALUES(56,82,'56:82','(سىلەر ئاللاھنىڭ رىزىق بەرگەنلىكىگە) شۈكۈر قىلىشنىڭ ئورنىغا (رىزىق بەرگۈچىنى) ئىنكار قىلامسىلەر؟');
INSERT INTO "saleh" VALUES(56,83,'56:83','جان ھەلقۇمغا يەتكەن چاغدا (سەكراتتىكى كىشىگە) قاراپ تۇرىسىلەر[83ـ84].');
INSERT INTO "saleh" VALUES(56,84,'56:84','جان ھەلقۇمغا يەتكەن چاغدا (سەكراتتىكى كىشىگە) قاراپ تۇرىسىلەر[83ـ84].');
INSERT INTO "saleh" VALUES(56,85,'56:85','بىز (ئىلمىمىز ۋە قۇدرىتىمىز بىلەن) ئۇنىڭغا سىلەردىن يېقىنمىز، لېكىن سىلەر (ئۇنى) كۆرمەيسىلەر (يەنى بىلمەيسىلەر).');
INSERT INTO "saleh" VALUES(56,86,'56:86',' ئەگەر (سىلەر گۇمان قىلغاندەك ئەمەلىڭلارغا قاراپ) جازاغا تارتىلمايدىغان بولساڭلار نېمىشقا ئۇنى (يەنى ئۇ مېيىتنىڭ جېنىنى بەدىنىگە) قايتۇرمايسىلەر؟ ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار[86ـ87].');
INSERT INTO "saleh" VALUES(56,87,'56:87',' ئەگەر (سىلەر گۇمان قىلغاندەك ئەمەلىڭلارغا قاراپ) جازاغا تارتىلمايدىغان بولساڭلار نېمىشقا ئۇنى (يەنى ئۇ مېيىتنىڭ جېنىنى بەدىنىگە) قايتۇرمايسىلەر؟ ئەگەر (سۆزۈڭلاردا) راستچىل بولساڭلار[86ـ87].');
INSERT INTO "saleh" VALUES(56,88,'56:88',' ئەگەر ئۇ مېيىت مۇقەررەبلەردىن (يەنى تائەت ـ ئىبادەت ۋە ياخشى ئىشلارنى ئەڭ ئالدىدا قىلغۇچىلاردىن) بولىدىغان بولسا، (ئۇنىڭ مۇكاپاتى) راھەت ـ پاراغەت، ياخشى رىزىق ۋە نازۇنېمەتلىك جەننەت بولىدۇ[88ـ89].');
INSERT INTO "saleh" VALUES(56,89,'56:89',' ئەگەر ئۇ مېيىت مۇقەررەبلەردىن (يەنى تائەت ـ ئىبادەت ۋە ياخشى ئىشلارنى ئەڭ ئالدىدا قىلغۇچىلاردىن) بولىدىغان بولسا، (ئۇنىڭ مۇكاپاتى) راھەت ـ پاراغەت، ياخشى رىزىق ۋە نازۇنېمەتلىك جەننەت بولىدۇ[88ـ89].');
INSERT INTO "saleh" VALUES(56,90,'56:90',' ئەگەر ئۇ سائادەتمەنلەردىن بولىدىغان بولسا«ساڭا سالام، سەن سائادەتمەنلەردىن ئىكەنسەن» دېيىلىدۇ. [90ـ91]');
INSERT INTO "saleh" VALUES(56,91,'56:91',' ئەگەر ئۇ سائادەتمەنلەردىن بولىدىغان بولسا«ساڭا سالام، سەن سائادەتمەنلەردىن ئىكەنسەن» دېيىلىدۇ. [90ـ91]');
INSERT INTO "saleh" VALUES(56,92,'56:92',' ئەگەر ئۇ تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلاردىن بولىدىغان بولسا، يۇقىرى ھارارەتلىك قايناقسۇ بىلەن كۈتۈۋېلىنىدۇ[92ـ93].');
INSERT INTO "saleh" VALUES(56,93,'56:93',' ئەگەر ئۇ تىرىلىشنى ئىنكار قىلغۇچى گۇمراھلاردىن بولىدىغان بولسا، يۇقىرى ھارارەتلىك قايناقسۇ بىلەن كۈتۈۋېلىنىدۇ[92ـ93].');
INSERT INTO "saleh" VALUES(56,94,'56:94','جەھەننەمدە كۆيدۈرۈلىدۇ.');
INSERT INTO "saleh" VALUES(56,95,'56:95',' شەك ـ شۈبھىسىزكى، بۇ ئېنىق ھەقىقەتتۇر.');
INSERT INTO "saleh" VALUES(56,96,'56:96',' ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئېتىرازلىرىدىن) پاك ئېتىقاد قىلغىن.');
INSERT INTO "saleh" VALUES(57,1,'57:1',' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھقا تەسبىھ ئېيتتى، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(57,2,'57:2',' ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ئاللاھ ئۆلتۈرەلەيدۇ، تىرىلدۈرەلەيدۇ، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(57,3,'57:3','ئاللاھنىڭ ئىپتىداسى يوقتۇر، ئىنتىھاسى يوقتۇر (يەنى ئۇ ھەممە نەرسە يوق ۋاقىتتىمۇ بار ئىدى، ھەممە نەرسە يوقالغاندىن كېيىنمۇ بار)، (ئاللاھ) ئاشكارىدۇر (يەنى مەۋجۇتلۇقىنى كۆرسەتكۈچى دەلىللەر بىلەن ئەقىللەرگە ئاشكارىدۇر)، مەخپىيدۇر (يەنى ئۇنى كۆز بىلەن كۆرگىلى بولمايدۇ، ئۇنىڭ زاتىنىڭ ماھىيىتىنى تونۇشقا ئەقىللەر ئېرىشەلمەيدۇ)، ئاللاھ ھەممە نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(57,4,'57:4',' ئاللاھ ئاسمانلارنى ۋە زېمىننى ئالتە كۈندە ياراتتى، (ئاندىن ئۆزىگە لايىق رەۋىشتە) ئەرش ئۈستىدە قارار ئالدى، ئاللاھ زېمىنغا كىرىپ كېتىدىغان نەرسىلەرنى، زېمىندىن چىقىدىغان نەرسىلەرنى، ئاسماندىن چۈشىدىغان نەرسىلەرنى، ئاسمانغا ئۆرلەيدىغان نەرسىلەرنى بىلىپ تۇرىدۇ، سىلەر قەيەردە بولساڭلار، ئاللاھ سىلەر بىلەن بىرگە، ئاللاھ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(57,5,'57:5','ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر، ھەممە ئىش ئاللاھقا قايتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(57,6,'57:6',' ئاللاھ كېچىنى كۈندۈزگە كىرگۈزىدۇ، كۈندۈزنى كېچىگە كىرگۈزىدۇ، ئاللاھ دىللاردىكى سىرلارنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(57,7,'57:7',' ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىمان كەلتۈرۈڭلار، ئاللاھ سىلەرنى ئىگە قىلغان ماللاردىن سەدىقە قىلىڭلار، سىلەردىن ئىمان ئېيتقانلار ۋە سەدىقە قىلغانلار چوڭ ساۋابقا ئىگە بولىدۇ.');
INSERT INTO "saleh" VALUES(57,8,'57:8','(ئى كۇففارلار!) سىلەر نېمىشقا ئاللاھقا ئىمان كەلتۈرمەيسىلەر؟ ھالبۇكى، پەيغەمبەر سىلەرنى پەرۋەردىگارىڭلارغا ئىمان كەلتۈرۈشكە چاقىرىدۇ، ئاللاھ سىلەردىن چىن ئەھدە ئالدى، ئەگەر ئىمان كەلتۈرۈشنى ئىرادە قىلساڭلار (ئىمانغا ئالدىراڭلار).');
INSERT INTO "saleh" VALUES(57,9,'57:9','سىلەرنى كۇفرىنىڭ قاراڭغۇلۇقىدىن (ئىماننىڭ) نۇرىغا چىقىرىش ئۈچۈن، ئاللاھ بەندىسىگە روشەن ئايەتلەرنى نازىل قىلدى، ئاللاھ شەك ـ شۈبھىسىز سىلەرگە مەرھەمەتلىكتۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(57,10,'57:10','نېمىشقا سىلەر ئاللاھنىڭ يولىدا (پۇل ـ مال) سەرپ قىلمايسىلەر، ئاسمانلارنىڭ ۋە زېمىننىڭ مىراسى ئاللاھقا خاستۇر، سىلەردىن مەككە پەتھى قىلىنىشتىن ئىلگىرى پۇل ـ مال سەرپ قىلغانلار ۋە (رەسۇلۇللاھ بىلەن بىرلىكتە تۇرۇپ دۈشمەنلەر بىلەن) ئۇرۇشقانلار، (مەككە پەتھى قىلىنغاندىن كېيىن پۇل ـ مال) نەپىقە قىلغانلار ۋە ئۇرۇشقانلار (بىر ـ بىرى بىلەن) باراۋەر ئەمەس، ئەنە شۇلارنىڭ دەرىجىسى (مەككە پەتھى قىلىنغاندىن) كېيىن نەپىقە قىلىنغان ۋە ئۇرۇشقانلارنىڭ (دەرىجىسىدىن) چوڭدۇر، (ئۇلارنىڭ) ھەر بىرىگە ئاللاھ جەننەتنى ۋەدە قىلدى. ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(57,11,'57:11','كىمكى ئاللاھقا قەرزى ھەسەنە بېرىدىكەن (يەنى ئاللاھنىڭ رازىلىقىنى تىلەپ، ئۇنىڭ يولىدا پۇل ـ مال سەرپ قىلىدىكەن)، ئاللاھ ئۇنىڭغا ھەسسىلەپ قايتۇرىدۇ، ئۇنىڭغا چوڭ ساۋاب بېرىدۇ.');
INSERT INTO "saleh" VALUES(57,12,'57:12','ئۇ كۈندە مۆمىن ئەرلەرنى، مۆمىن ئاياللارنى كۆرۈسەنكى، ئۇلارنىڭ نۇرى ئۇلارنىڭ ئالدىدا ۋە ئۇلارنىڭ ئوڭ تەرىپىدە ماڭىدۇ، (ئۇلارغا) «بۈگۈن سىلەرگە ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەر بىلەن خۇشخەۋەر بېرىلىدۇ، ئۇ يەرلەردە مەڭگۈ قالىسىلەر، بۇ ئۇلۇغ مۇۋەپپەقىيەتتۇر» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(57,13,'57:13','ئۇ كۈندە مۇناپىق ئەرلەر، مۇناپىق ئاياللار مۆمىنلەرگە: «بىزنى كۈتۈپ تۇرۇڭلار، سىلەرنىڭ نۇرۇڭلاردىن ئازراق ئالايلى» دەيدۇ. ئۇلارغا (مەسخىرە يۈزىسىدىن): «ئارقاڭلارغا قايتىپ نۇر تىلەڭلار» دېيىلىدۇ، شۇنىڭ بىلەن ئۇلارنىڭ ئارىسىغا بىر سېپىل سوقۇلىدۇ، سېپىلنىڭ دەرۋازىسى بولىدۇ، دەرۋازىنىڭ ئىچى تەرىپىدە رەھمەت، تېشىدا ئازاب بولىدۇ.');
INSERT INTO "saleh" VALUES(57,14,'57:14','ئۇلار مۆمىنلەرنى: «بىز سىلەر بىلەن بىللە ئەمەسمىدۇق» دەپ توۋلايدۇ، مۆمىنلەر: «شۇنداق، لېكىن سىلەر ئۆزۈڭلارنى پىتنىگە سالدىڭلار (يەنى كۆرۈنۈشتە بىز بىلەن بىللە بولساڭلارمۇ، لېكىن ئۆزۈڭلارنى مۇناپىقلىق بىلەن ھالاك قىلدىڭلار، مۆمىنلەرگە بالايى ـ ئاپەتلەر كېلىشىنى) كۈتتۈڭلار، (ئىسلام دىنىدىن) شەكلەندىڭلار، (ئاللاھنىڭ رەھمىتىنىڭ كەڭلىكىگە بولغان قۇرۇق) ئارزۇلار سىلەرنى ئالدىدى، تاكى سىلەرگە ئۆلۈم كەلدى، شەيتان سىلەرنى ئاللاھنىڭ (ئەپۇ قىلىدىغانلىقى ۋە ئازاب قىلمايدىغانلىقى) بىلەن ئالدىدى» دەيدۇ.');
INSERT INTO "saleh" VALUES(57,15,'57:15',' بۈگۈن سىلەردىن ۋە كاپىرلاردىن فىدىيە ئېلىنمايدۇ، جايىڭلار دوزاختۇر، دوزاخ سىلەرگە ئەڭ لايىقتۇر، دوزاخ نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(57,16,'57:16','مۆمىنلەرگە ئۇلارنىڭ دىللىرى ئاللاھنىڭ زىكرىگە ۋە نازىل بولغان ھەقىقەتكە (يەنى قۇرئان ئايەتلىرىگە) ئېرىيدىغان ۋاقىت يەتمىدىمۇ؟ ئۇلار ئىلگىرى كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) دەك بولمىسۇن، (ئۇلار بىلەن پەيغەمبەرلىرىنىڭ ئارىلىقىدىكى) زاماننىڭ ئۇزىرىشى بىلەن ئۇلارنىڭ دىللىرى قېتىپ كەتتى، ئۇلارنىڭ نۇرغۇنى ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(57,17,'57:17',' بىلىڭلاركى، ئاللاھ زېمىننى ئۆلگەندىن كېيىن تىرىلدۈرىدۇ (يەنى قاقاس، قۇرغاق زېمىننى يامغۇر بىلەن كۆكەرتىدۇ)، بىز ھەقىقەتەن سىلەرنى چۈشەنسۇن دەپ سىلەرگە (كامالى قۇدرىتىمىزنى كۆرسىتىدىغان) دەلىللەرنى بايان قىلدۇق.');
INSERT INTO "saleh" VALUES(57,18,'57:18',' سەدىقە بەرگۈچى ئەرلەرگە، سەدىقە بەرگۈچى ئاياللارغا ۋە ئاللاھقا قەرزى ھەسەنە بەرگۈچىلەرگە (يەنى ئاللاھنىڭ رازىلىقىنى تىلەپ كەمبەغەللەرگە سەدىقە بەرگۈچىلەر، ياخشىلىق يوللىرىغا خۇشاللىق بىلەن پۇل ـ مال سەرپ قىلغۇچىلارغا) ھەسسىلەپ (ساۋاب) بېرىلىدۇ، ئۇلارغا زور مۇكاپات (يەنى جەننەت) بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(57,19,'57:19',' ئاللاھقا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقانلار ـ ئەنە شۇلار پەرۋەردىگارىنىڭ دەرگاھىدا سىددىقلاردۇر، شېھىتلەردۇر، ئۇلار (ئاخىرەتتە) ساۋابقا ۋە نۇرغا ئىگە بولىدۇ، بىرنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار ۋە يالغانغا چىقارغانلار ـ ئەنە شۇلار ئەھلى دوزاختۇر.');
INSERT INTO "saleh" VALUES(57,20,'57:20',' بىلىڭلاركى، دۇنيا تىرىكچىلىكى ئويۇندىن، (كىشىنى ئاخىرەتتە غەپلەتتە قالدۇرىدىغان) مەشغۇلاتتىن، زىبۇ - زىننەتتىن، ئۆزئارا پەخىرلىنىشتىن، پۇل ـ مال، پەرزەنت كۆپەيتىپ پەخىرلىنىشتىن ئىبارەتتۇر، (بىرەر قېتىم يېغىش بىلەن ئۆستۈرگەن) ئۆسۈملۈكى دېھقانلارنى خۇرسەن قىلغان يامغۇرغا ئوخشايدۇ، ئاندىن ئۇ ئۆسۈملۈك قۇرۇپ قالىدۇ، ئۇنىڭ سارغىيىپ قالغانلىقىنى كۆرۈسەن، ئاندىن ئۇ يەنچىلگەن چۆپكە ئايلىنىدۇ. ئاخىرەتتە (كۇففارلار ئۈچۈن) قاتتىق ئازاب بار، (ياخشى بەندىلەر ئۈچۈن) ئاللاھنىڭ مەغپىرىتى ۋە رازىلىقى بار، دۇنيا تىرىكچىلىكى پەقەت (غاپىل ئادەم) ئالدىنىدىغان بەھرىمەن بولۇشتۇر.');
INSERT INTO "saleh" VALUES(57,21,'57:21',' پەرۋەردىگارىڭلارنىڭ مەغپىرىتىنى، كەڭلىكى ئاسمان ـ زېمىندەك كېلىدىغان جەننەتنى قولغا كەلتۈرۈڭلار، (ئۇ) جەننەت ئاللاھقا ۋە ئۇنىڭ پەيغەمبەرلىرىگە ئىمان ئېيتقانلارغا تەييارلانغان، بۇ ئاللاھنىڭ پەزلىدۇر، (ئاللاھ) ئۇنى خالىغان ئادەمگە ئاتا قىلىدۇ، ئاللاھ ئۇلۇغ پەزل ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(57,22,'57:22',' يەر يۈزىدىكى بارلىق ھادىسىلەر ۋە ئۆزۈڭلار ئۇچرىغان مۇسىبەتلەر بىز ئۇلارنى يارىتىشتىن بۇرۇن لەۋھۇلمەھپۇزغا يېزىلغان، بۇ (ئىش)، شۈبھىسىزكى، ئاللاھقا ئاساندۇر. ');
INSERT INTO "saleh" VALUES(57,23,'57:23',' (ئاللاھتائالانىڭ ئۇلارنى لەۋھۇلمەھپۇزغا يېزىشى) قولۇڭلاردىن كەتكەن نەرسىگە قايغۇرۇپ كەتمەسلىكىڭلار ۋە ئاللاھ بەرگەن نەرسىلەرگە خۇش بولۇپ كەتمەسلىكىڭلار ئۈچۈندۇر، ئاللاھ مۇتەكەببىرلەر ۋە ئۆزلىرىنى چوڭ تۇتقۇچىلارنى دوست تۇتمايدۇ.');
INSERT INTO "saleh" VALUES(57,24,'57:24','ئۇلار بېخىللىق قىلىدىغان ۋە كىشىلەرنى بېخىللىق قىلىشقا بۇيرۇيدىغانلاردۇر، كىمكى (ئاللاھنىڭ يولىدا نەپىقە قىلىشتىن) باش تارتىدىكەن (ئاللاھقا ھېچ زىيان يەتكۈزەلمەيدۇ)، چۈنكى ئاللاھ بىھاجەتتۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(57,25,'57:25',' بىز ھەقىقەتەن پەيغەمبەرلىرىمىزنى روشەن مۆجىزىلەر بىلەن ئەۋەتتۇق ۋە ئۇلار بىلەن بىللە، ئىنسانلار ئادالەتنى بەرپا قىلسۇن دەپ، كىتابنى، قانۇننى چۈشۈردۇق. تۆمۈرنى ياراتتۇق، (تۆمۈردىن ئۇرۇش قوراللىرى ياسىلىدىغانلىقى ئۈچۈن) تۆمۈر كۈچ ـ قۇۋۋەتنى ئۆز ئىچىگە ئالغان، تۆمۈردە ئىنسانلار ئۈچۈن نۇرغۇن مەنپەئەتلەر بار، ئاللاھ ئاللاھنى كۆرمەي تۇرۇپ (قوراللارنى ئىشلىتىپ) ئاللاھقا ۋە پەيغەمبەرلىرىگە ياردەم بەرگەنلەرنى بىلىش (مەلۇم قىلىش) ئۈچۈن (تۆمۈرنى ياراتتى)، ئاللاھ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر.');
INSERT INTO "saleh" VALUES(57,26,'57:26',' بىز ھەقىقەتەن نۇھنى، ئىبراھىمنى پەيغەمبەر قىلىپ ئەۋەتتۇق، پەيغەمبەرلىكنى ۋە كىتابنى ئۇلارنىڭ ئەۋلادىغا (ئاتا) قىلدۇق، ئۇلاردىن ھىدايەت تاپقانلىرى بار، ئۇلارنىڭ نۇرغۇنى ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(57,27,'57:27',' ئۇلاردىن كېيىن پەيغەمبەرلىرىمىزنى داۋاملىق ئەۋەتتۇق، ئارقىدىن مەريەم ئوغلى ئىيسانى ئەۋەتتۇق، ئۇنىڭغا ئىنجىلنى ئاتا قىلدۇق، ئۇنىڭغا ئەگەشكەنلەرنىڭ دىللىرىغا شەپقەت ۋە مېھرىبانلىقنى سالدۇق، رەھبانىيەتنى (تەركىدۇنيالىقنى) ئۇلار ئۆزلىرى پەيدا قىلدى، ئۇلارغا ئۇنى بىز بېكىتمىدۇق، ئۇلار ئۇنى پەقەت ئاللاھنىڭ رازىلىقىنى تىلەپ (پەيدا قىلدى)، لېكىن ئۇلار ئۇنىڭغا لايىق دەرىجىدە ئەمەل قىلمىدى، ئۇلاردىن ئىمان ئېيتقانلارنىڭ ئەجرىنى ھەسسىلەپ بەردۇق، ئۇلاردىن نۇرغۇنى ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(57,28,'57:28','ئى (ئىيساغا) ئىمان ئېيتقان كىشىلەر! ئاللاھتىن قورقۇڭلار، ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرى (مۇھەممەد ئەلەيھىسسالامغا) ئىمان ئېيتىڭلار، ئاللاھ سىلەرگە رەھمىتىدىن ئىككى ھەسسە بېرىدۇ، سىلەرگە سىلەر ماڭىدىغان نۇرنى پەيدا قىلىدۇ، سىلەرگە مەغپىرەت قىلىدۇ، ئاللاھ بەكمۇ مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(57,29,'57:29',' ئەھلى كىتابنىڭ ئاللاھنىڭ پەزلىدىن ھېچ نەرسىگە قادىر ئەمەسلىكلىرىنى، پەزلنىڭ ھەممىسى ئاللاھنىڭ قولىدا بولۇپ، (ئاللاھنىڭ) ئۇنى خالىغان ئادەمگە بېرىدىغانلىقىنى، ئاللاھنىڭ ئۇلۇغ پەزل ئىگىسى ئىكەنلىكىنى بىلىشى ئۈچۈن (ئاللاھ شۇ سۆزنى قىلدى).');
INSERT INTO "saleh" VALUES(58,1,'58:1','ئاللاھ ھەقىقەتەن ئېرى توغرىسىدا سەن بىلەن مۇنازىرىلەشكەن ۋە ئاللاھقا شىكايەت قىلغان ئايالنىڭ سۆزىنى ئاڭلىدى، ئاللاھ ئىككىڭلارنىڭ سۆزۈڭلارنى ئاڭلاپ تۇرىدۇ، ئاللاھ ھەقىقەتەن ئاڭلىغۇچىدۇر، كۆرگۈچىدۇر.');
INSERT INTO "saleh" VALUES(58,2,'58:2',' سىلەردىن ئاياللىرىنى زىھار قىلغانلارنىڭ (يەنى ئاياللىرىنى ئانىلىرىنىڭ ئۇچىلىرىغا ئوخشاش دېگۈچىلەرنىڭ) ئاياللىرى ئۇلارنىڭ ئانىلىرى ئەمەستۇر، پەقەت ئۇلارنى تۇغقان ئاياللارلا ئۇلارنىڭ ئانىلىرىدۇر، ھالبۇكى، ئۇلار (يەنى زىھار قىلغۇچىلار) ئەلۋەتتە يامان سۆزنى، يالغان سۆزنى قىلىدۇ. ئاللاھ، شەك ـ شۈبھىسىزكى، (تەۋبە قىلغۇچىنى) ئەپۇ قىلغۇچىدۇر، مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(58,3,'58:3','ئاياللىرىنى زىھار قىلىپ، ئاندىن قىلغان سۆزىگە (يەنى زىھار قىلغانلىقىغا) پۇشايمان قىلغانلار (ئەر ـ ئايال ئىككىسى) يېقىنچىلىق قىلىشتىن ئىلگىرى بىر قۇلنى ئازاد قىلىشى لازىم. بۇنىڭ بىلەن سىلەرگە ۋەز ـ نەسىھەت قىلىنىدۇ، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(58,4,'58:4','(ئازاد قىلىشقا) قۇل تاپالمىغان ئادەم، ئايالى بىلەن يېقىنچىلىق قىلىشتىن ئىلگىرى، ئۇدا ئىككى ئاي روزا تۇتۇشى لازىم، (قېرىلىق ياكى كېسەللىك سەۋەبى بىلەن روزا تۇتۇشقا) قادىر بولالمىغان ئادەم (60 مىسكىنگە بىر ۋاخ تاماق بېرىشى لازىم)، بۇ (ئەھكام) ئاللاھ ۋە ئۇنىڭ پەيغەمبىرى ( ئەمر قىلغان ئىشلارغا) تەستىق قىلىشىڭلار ئۈچۈن (بايان قىلىندى). بۇلار ئاللاھنىڭ قانۇنىدۇر، (بۇ قانۇننى) ئىنكار قىلغۇچىلار قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(58,5,'58:5',' شۈبھىسىزكى، ئاللاھ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقانلار ئۇلاردىن بۇرۇنقىلار ھالاك قىلىنغاندەك ھالاك قىلىنىدۇ. بىز ھەقىقەتەن (ھالال، ھارام، پەرز ۋە ئەھكاملارنى ئۆز ئىچىگە ئالغان) روشەن ئايەتلەرنى نازىل قىلدۇق، كاپىرلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(58,6,'58:6','ئۇ كۈندە ئاللاھ ئۇلارنىڭ ھەممىسىنى تىرىلدۈرىدۇ، ئۇلارنىڭ (دۇنيادىكى چاغدا قىلغان) قىلمىشلىرىنى ئېيتىپ بېرىدۇ، ئاللاھ ئۇلارنىڭ قىلمىشلىرىنى (نامە ـ ئەمالىغا) خاتىرىلەپ قويغان، ئۇلار بولسا ئۇنتۇپ قالغان، ئاللاھ ھەممە نەرسىنى كۆزىتىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(58,7,'58:7',' ئاللاھنىڭ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدىغانلىقىنى بىلمەمسەن؟ ئۈچ ئادەم پىچىرلىشىدىكەن، ئاللاھ ئۇلارنىڭ تۆتىنچىسىدۇر، بەش ئادەم پىچىرلىشىدىكەن، ئاللاھ ئۇلارنىڭ ئالتىنچىسىدۇر، مەيلى ئۇنىڭدىن ئاز ياكى كۆپ ئادەم پىچىرلاشسۇن، ئۇلار قەيەردە بولمىسۇن، ئاللاھ ھامان ئۇلار بىلەن بىللىدۇر، ئاندىن قىيامەت كۈنى ئۇلارنىڭ قىلمىشلىرىنى ئاللاھ ئۇلارغا ئېيتىپ بېرىدۇ، ئاللاھ ھەقىقەتەن ھەممە نەرسىنى تولۇق بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(58,8,'58:8','پىچىرلىشىشتىن مەنئى قىلىنغان، ئاندىن مەنئى قىلىنغان نەرسىگە قايتقان كىشىلەرنى كۆرمىدىڭمۇ؟ ئۇلار گۇناھ بىلەن، زۇلۇم بىلەن ۋە پەيغەمبەرگە قارشى تۇرۇش بىلەن پىچىرلىشىدۇ، ئۇلار سېنىڭ يېنىڭغا كەلگەندە، ئاللاھ ساڭا سالام قىلىشتا قوللانمىغان سۆزلەر بىلەن سالام قىلىدۇ، ئۇلار ئۆز ئىچىدە: «ئاللاھ نېمىشقا بۇ سۆزىمىز بىلەن بىزنى جازالىمايدۇ؟» دەيدۇ، دوزاخ ئۇلارغا كۇپايىدۇر، ئۇلار دوزاخقا كىرىدۇ، دوزاخ نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(58,9,'58:9','ئى مۆمىنلەر! سىلەر پىچىرلاشساڭلار گۇناھ، زۇلۇم ۋە پەيغەمبەرنىڭ ئەمرىگە خىلاپلىق قىلىش توغرىسىدا پىچىرلاشماڭلار، ياخشىلىق ۋە تەقۋادارلىق توغرىسىدا پىچىرلىشىڭلار، سىلەر ئاللاھتىن قورقۇڭلار، سىلەر ئۇنىڭ دەرگاھىغا يىغىلىسىلەر.');
INSERT INTO "saleh" VALUES(58,10,'58:10',' گۇناھ توغرىسىدا پىچىرلىشىش مۆمىنلەرنى قايغۇغا سېلىش ئۈچۈن شەيتان تەرىپىدىن بولغاندۇر، (بۇنداق پىچىرلىشىش) ئاللاھنىڭ ئىرادىسى بولمىسىلا مۆمىنلەرگە ھېچقانداق زىيان يەتكۈزەلمەيدۇ، مۆمىنلەر ئاللاھقا تەۋەككۈل قىلسۇن.');
INSERT INTO "saleh" VALUES(58,11,'58:11',' ئى مۆمىنلەر! ئەگەر (سورۇندا) بەزىلەر سىلەرگە: «ئورۇن چىقىرىپ بېرىڭلار» دېسە، ئورۇن چىقىرىپ بېرىڭلار، ئاللاھ سىلەرگە (رەھمىتىنى ۋە جەننىتىنى) كېڭەيتىپ بېرىدۇ، ئەگەر سىلەرگە (باشقىلارغا ئورۇن بوشىتىپ بېرىش ئۈچۈن): «ئورنۇڭلاردىن تۇرۇپ بېرىڭلار» دېيىلسە، ئورنۇڭلاردىن تۇرۇپ بېرىڭلار، ئاللاھ سىلەردىن ئىمان ئېيتقانلار ۋە ئىلىم بېرىلگەنلەرنى بىر قانچە دەرىجە يۇقىرى كۆتۈرىدۇ، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەردار. ');
INSERT INTO "saleh" VALUES(58,12,'58:12',' ئى مۆمىنلەر! پەيغەمبەر بىلەن مەخپىي سۆزلەشمەكچى بولساڭلار، ئالدى بىلەن (يوقسۇللارغا) سەدىقە بېرىپ، ئاندىن سۆزلىشىڭلار، بۇ سىلەر ئۈچۈن ياخشىراقتۇر ۋە پاكراقتۇر، ئەگەر (سەدىقە قىلىدىغان نەرسە) تاپالمىساڭلار (سىلەرگە گۇناھ بولمايدۇ)، چۈنكى ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(58,13,'58:13','سىلەر رەسۇلۇللاھ بىلەن مەخپىي سۆزلىشىشتىن ئىلگىرى سەدىقىلەر بېرىشتىن (كەمبەغەل بولۇپ قالىمىز) دەپ قورقتۇڭلارمۇ؟ سىلەر سەدىقە بەرمىدىڭلار، ئاللاھ سىلەرنى ئەپۇ قىلدى، ناماز ئوقۇڭلار، زاكات بېرىڭلار، ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئىتائەت قىلىڭلار، قىلىۋاتقان ئىشىڭلاردىن ئاللاھ تولۇق خەۋەرداردۇر. ');
INSERT INTO "saleh" VALUES(58,14,'58:14',' ئاللاھنىڭ غەزىپىگە ئۇچرىغان قەۋمنى دوست تۇتقان كىشىلەرنى كۆرمىدىڭمۇ؟ ئۇلار سىلەرگىمۇ ۋە ئۇ قەۋمگىمۇ (يەنى يەھۇدىيلارغىمۇ) مەنسۇپ ئەمەس، ئۇلار بىلىپ تۇرۇپ يالغاندىن قەسەم ئىچىدۇ.');
INSERT INTO "saleh" VALUES(58,15,'58:15',' ئۇلارغا ئاللاھ قاتتىق ئازاب تەييارلىدى، ئۇلارنىڭ قىلمىشلىرى نېمىدېگەن يامان!');
INSERT INTO "saleh" VALUES(58,16,'58:16','ئۇلار (يالغان) قەسەملىرىنى قالقان قىلىۋالدى، كىشىلەرنى ئاللاھنىڭ يولىدىن توستى، ئۇلار خار قىلغۇچى ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(58,17,'58:17','ئۇلارنىڭ ماللىرى ۋە بالىلىرى (ئۇلارغا ئاخىرەتتە بولىدىغان) ئاللاھنىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمايدۇ، ئەنە شۇلار ئەھلى دوزاختۇر، ئۇلار دوزاختا مەڭگۈ قالغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(58,18,'58:18',' ئۇ كۈندە ئاللاھ ئۆلگەنلەرنىڭ ھەممىسىنى تىرىلدۈرىدۇ، ئۇلار (دۇنيادىكى چاغلىرىدا «بىز مۇسۇلمانمىز» دەپ يالغاندىن) سىلەرگە قەسەم ئىچكەندەك ئاللاھقا قەسەم ئىچىدۇ، ئۇلار بۇ قەسەمنىڭ ئۆزلىرىگە پايدىسى بولىدۇ دەپ ئويلايدۇ، ئاگاھ بولۇڭلاركى، ئۇلار ھەقىقەتەن يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(58,19,'58:19',' ئۇلارنىڭ ئۈستىدىن شەيتان غەلىبە قىلدى، (شەيتان) ئۇلارغا ئاللاھنىڭ زىكرىنى ئۇنتۇلدۇردى، ئۇلار شەيتاننىڭ قوشۇنىدۇر، بىلىڭلاركى، شەيتاننىڭ قوشۇنى زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(58,20,'58:20','شۈبھىسىزكى، ئاللاھ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقۇچىلار ئەڭ خار ئىنسانلار قاتارىدىدۇر.');
INSERT INTO "saleh" VALUES(58,21,'58:21',' ئاللاھ (لەۋھۇلمەھپۇزغا): «مەن ۋە مېنىڭ پەيغەمبەرلىرىم چوقۇم غەلىبە قىلىمىز» دەپ يازدى، ئاللاھ ھەقىقەتەن كۈچلۈكتۇر، غالىبتۇر.');
INSERT INTO "saleh" VALUES(58,22,'58:22','ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىشىنىدىغان بىر قەۋمنىڭ ئاللاھ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشقانلارنى _ ئۇلارنىڭ ئانىلىرى، ئوغۇللىرى، يا قېرىنداشلىرى، يا ئۇرۇق ـ تۇغقانلىرى بولغان تەقدىردىمۇ _ دوست تۇتقانلىقىنى كۆرمەيسەن (يەنى ئۇلارنى دوست تۇتۇشى مۇمكىن ئەمەس)، ئەنە شۇلارنىڭ دىللىرىدا ئاللاھ ئىماننى مۇستەھكەم قىلدى ۋە ئۆز دەرگاھىدىن بولغان روھ بىلەن ئۇلارنى كۈچەيتتى، ئاللاھ ئۇلارنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ، ئاللاھ ئۇلاردىن رازى بولىدۇ، ئاللاھتىن ئۇلارمۇ مەمنۇن بولىدۇ، ئەنە شۇلار ئاللاھنىڭ قوشۇنىدۇر، بىلىڭلاركى، ئاللاھنىڭ قوشۇنى مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(59,1,'59:1',' ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنىڭ ھەممىسى ئاللاھقا تەسبىھ ئېيتتى، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(59,2,'59:2','ئاللاھ ئەھلى كىتابتىن بولغان كاپىرلارنى (يەنى بەنى نەزىر يەھۇدىيلىرىنى) تۇنجى قېتىملىق سۈرگۈندە ئۆيلىرىدىن ھەيدەپ چىقاردى، سىلەر ئۇلارنى (كۈچلۈك تۇرۇپ مۇنداق خار ھالدا) چىقىپ كېتىدۇ دەپ گۇمان قىلمىغان ئىدىڭلار، ئۇلار ئۆزلىرىنىڭ قورغانلىرىنى ئۆزلىرىنى ئاللاھنىڭ (ئازابىدىن) توسىيالايدۇ دەپ ئويلىغان ئىدى، ئۇلارغا ئاللاھنىڭ ئازابى ئۇلار ئويلىمىغان يەردىن كەلدى. ئاللاھ ئۇلارنىڭ دىللىرىغا قورقۇنچ سالدى، ئۇلار قورغانلىرىنى ئۆزلىرىنىڭ قوللىرى ۋە مۆمىنلەرنىڭ قوللىرى بىلەن بۇزدى، ئى ئەقىل ئىگىلىرى! (ئۇلارنىڭ ھالىدىن) ئىبرەت ئېلىڭلار.');
INSERT INTO "saleh" VALUES(59,3,'59:3','ئەگەر ئاللاھ ئۇلارنى سۈرگۈن بولۇشقا ھۆكۈم قىلمىغان بولسا ئىدى، ئەلۋەتتە ئۇلارنى دۇنيادا (ئۆلتۈرۈش) بىلەن ئازابلايتتى، ئۇلار ئاخىرەتتە دوزاخ ئازابىغا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(59,4,'59:4',' بۇ (يەنى ئۇلارنىڭ سۈرگۈن قىلىنىشى) شۇنىڭ ئۈچۈندۇركى، ئۇلار ئاللاھ بىلەن ۋە ئۇنىڭ پەيغەمبىرى بىلەن قارشىلاشتى، كىمكى ئاللاھ بىلەن قارشىلىشىدىكەن، شۈبھىسىز، ئاللاھ ئۇنى قاتتىق جازالايدۇ.');
INSERT INTO "saleh" VALUES(59,5,'59:5',' (ئى مۇسۇلمانلار!) سىلەر مەيلى (بەنى نەزىرنىڭ) خورمىلىرىنى كېسىڭلار، مەيلى ئۇنى بۇرۇنقى پېتى ئۆرە قالدۇرۇڭلار، ھەممىسىگە ئاللاھ رۇخسەت قىلىدۇ، (ئاللاھنىڭ رۇخسەت قىلىشى) ئاللاھنىڭ ئىتائىتىدىن چىققۇچىلارنى (يەنى يەھۇدىيلارنى) رەسۋا قىلىش ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(59,6,'59:6',' ئاللاھ پەيغەمبىرىگە ئۇلاردىن (يەنى بەنى نەزىرنىڭ ماللىرىدىن) قايتۇرغان غەنىيمەتلەرنى سىلەر ئات ـ تۆگىلەرنى چاپتۇرۇپ (يەنى ئەجىر قىلىپ، جاپا ـ مۇشەققەت تارتىپ) قولغا كەلتۈرگەن ئەمەس، لېكىن ئاللاھ پەيغەمبەرلىرىنى خالىغان ئادەمگە مۇسەللەت قىلىدۇ، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(59,7,'59:7',' ئاللاھ پەيغەمبىرىگە غەنىيمەت قىلىپ بەرگەن ئەھلى قۇرا (يەنى قۇرەيزە، بەنى نەزىر، پەدەك ۋە خەيبەر كۇففارلىرى) نىڭ ماللىرىنى، ئىچىڭلاردىكى بايلار ئارىسىدا قولدىن قولغا ئۆتۈپ يۈرمەسلىكى ئۈچۈن، ئاللاھقا، پەيغەمبەرگە، پەيغەمبەرنىڭ خىش ـ ئەقرىبالىرىغا، يېتىملەرگە، مىسكىنلەرگە، ئىبن سەبىللەرگە خاس قىلدى، پەيغەمبەر سىلەرگە بەرگەننى ئېلىڭلار، پەيغەمبەر چەكلىگەن نەرسىدىن چەكلىنىڭلار، ئاللاھتىن قورقۇڭلار، ھەقىقەتەن ئاللاھنىڭ ئازابى قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(59,8,'59:8','(ئۇ غەنىيمەتنىڭ بىر قىسمى) دىيارىدىن ھەيدەپ چىقىرىلغان، مال ـ مۈلكىدىن ئايرىلغان پېقىر مۇھاجىرلارغا خاستۇر، ئۇلار ئاللاھنىڭ پەزلىنى ۋە رازىلىقىنى تىلەيدۇ، ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ياردەم بېرىدۇ، ئەنە شۇلار (ئىماندا) سادىق ئادەملەردۇر.');
INSERT INTO "saleh" VALUES(59,9,'59:9','ئۇلاردىن (يەنى مۇھاجىرلاردىن) مەدىنىدە يەرلىك بولغان، ئىمانى كۈچلۈك بولغانلار (يەنى ئەنسارلار) يېنىغا ھىجرەت قىلىپ كەلگەنلەرنى (يەنى مۇھاجىرلارنى) دوست تۇتىدۇ، ئۇلارغا بېرىلگەن نەرسىلەر ئۈچۈن ئىچى تارلىق قىلمايدۇ، ئۇلار موھتاج تۇرۇقلۇق (مۇھاجىرلارنىڭ مەنپەئىتىنى) ئۆزلىرىنىڭ (مەنپەئىتى) دىن ئەلا بىلىدۇ، ئۆز نەپسىنىڭ بېخىللىقىدىن ساقلانغانلار مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(59,10,'59:10',' ئۇلاردىن كېيىن كەلگەنلەر: «پەرۋەردىگارىمىز! بىزگە ۋە بىزدىن ئىلگىرى ئىمان ئېيتقان قېرىنداشلىرىمىزغا مەغپىرەت قىلغىن، دىللىرىمىزدا مۆمىنلەرگە قارشى دۈشمەنلىك پەيدا قىلمىغىن، پەرۋەردىگارىمىز! سەن ناھايىتى مەغپىرەت قىلغۇچىسەن، مېھرىبانسەن» دەيدۇ.');
INSERT INTO "saleh" VALUES(59,11,'59:11','مۇناپىقلارنىڭ ئۆزلىرىنىڭ (مۇھەممەد ئەلەيھىسسالامنىڭ پەيغەمبەرلىكىنى) ئىنكار قىلغان ئەھلى كىتاب بۇرادەرلىرىگە: «يۇرتۇڭلاردىن چىقىرىۋېتىلسەڭلار، بىز چوقۇم سىلەر بىلەن بىرگە چىقىپ كېتىمىز، ھەرگىز سىلەرنىڭ زىيىنىڭلار ھېسابىغا باشقا ئادەمگە ئىتائەت قىلمايمىز، سىلەرگە ئۇرۇش ئېچىلسا چوقۇم سىلەرگە ياردەم بېرىمىز» دېيىشكەنلىكىنى كۆرمىدىڭمۇ؟ ئاللاھ گۇۋاھلىق بېرىدۇكى، مۇناپىقلار چوقۇم يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(59,12,'59:12',' ئەگەر ئۇلار ھەيدەپ چىقىرىلسا، مۇناپىقلار ئۇلار بىلەن بىللە چىقمايدۇ، ئەگەر ئۇلارغا ئۇرۇش ئېچىلسا، مۇناپىقلار ئۇلارغا ياردەم بەرمەيدۇ، بەرگەن تەقدىردىمۇ يۈز ئۆرۈپ قاچىدۇ، ئەھلى كىتاب ياردەمگە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(59,13,'59:13',' شۈبھىسىزكى، سىلەر ئۇلارنىڭ دىللىرىدا ئاللاھقا قارىغاندىمۇ قورقۇنچلۇقسىلەر (يەنى ئۇلار سىلەردىن ئاللاھتىن قورققاندىنمۇ بەك قورقىدۇ)، بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار ئاللاھنىڭ (ئۇلۇغلۇقىنى) چۈشەنمەيدىغان قەۋمدۇر. ');
INSERT INTO "saleh" VALUES(59,14,'59:14',' ئۇلار پەقەت مۇستەھكەم شەھەر ئىچىدە ياكى تاملارنىڭ ئارقىسىدا تۇرغاندىلا، ئاندىن سىلەرگە قارشى بىرلىشىپ ئۇرۇش قىلالايدۇ، ئۇلارنىڭ ئۇرۇشى ئۆز ئىچىدە قاتتىقتۇر، ئۇلارنى ئۆم دەپ ئويلايسەن، ھالبۇكى، ئۇلارنىڭ دىللىرى تارقاقتۇر، بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار چۈشەنمەيدىغان قەۋمدۇر.');
INSERT INTO "saleh" VALUES(59,15,'59:15','ئۇلار (يەنى يەھۇدىيلار) ئۇلاردىن ئىلگىرى يېقىندىلا ئۆتكەن، قىلمىشلىرىنىڭ ۋابالىنى تېتىغانلارغا (يەنى مۇشرىكلارغا) ئوخشايدۇ، ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(59,16,'59:16','ئۇلار (يەنى مۇناپىقلار يەھۇدىيلارنى ئۇرۇشقا قىزىقتۇرۇشتا) شەيتانغا ئوخشايدۇ، ئۆز ۋاقتىدا شەيتان ئىنسانغا: «كاپىر بولغىن» دېدى. ئىنسان كاپىر بولغان چاغدا، شەيتان: «مەن سەندىن ئادا ـ جۇدامەن، مەن ھەقىقەتەن ئالەملەرنىڭ پەرۋەردىگارى ئاللاھتىن قورقىمەن» دېدى.');
INSERT INTO "saleh" VALUES(59,17,'59:17',' ئۇلارنىڭ (يەنى شەيتان بىلەن ئىنساننىڭ) ئاقىۋىتى بىرلىكتە دوزاخقا كىرىش، دوزاختا مەڭگۈ قىلىش بولدى، ئەنە شۇ زالىملارنىڭ جازاسىدۇر.');
INSERT INTO "saleh" VALUES(59,18,'59:18',' ئى مۆمىنلەر! ئاللاھتىن قورقۇڭلار، ھەر ئادەم ئەتە (يەنى قىيامەت كۈنى) ئۈچۈن (ياخشى ئەمەللەردىن) نېمىلەرنى تەييارلىغانلىقىغا قارىسۇن، ئاللاھتىن قورقۇڭلار، ئاللاھ ھەقىقەتەن قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(59,19,'59:19',' سىلەر ئاللاھنى ئۇنتۇغان، شۇنىڭ بىلەن ئاللاھ ئۇلارغا ئۆزلىرىنى ئۇنتۇلدۇرغان كىشىلەردەك بولماڭلار، ئەنە شۇلار پاسىقلاردۇر.');
INSERT INTO "saleh" VALUES(59,20,'59:20',' ئەھلى دوزاخ بىلەن ئەھلى جەننەت باراۋەر بولمايدۇ، ئەھلى جەننەت بولسا مەقسەتكە ئېرىشكۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(59,21,'59:21','ئەگەر بىز قۇرئاننى بىرەر تاغقا نازىل قىلساق، چوقۇم سەن ئۇنىڭ ئاللاھتىن قورققانلىقتىن باش ئەگكەنلىكىنى، يېرىلىپ كەتكەنلىكىنى كۆرەتتىڭ، بىز بۇ مىساللارنى كىشىلەرگە ئۇلار (ئاللاھنىڭ قۇدرىتىنى) پىكىر قىلسۇن دەپ بايان قىلىمىز.');
INSERT INTO "saleh" VALUES(59,22,'59:22','ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ يوشۇرۇننى، ئاشكارىنى بىلگۈچىدۇر، ئۇ ناھايىتى شەپقەتلىكتۇر، ناھايىتى مېھرىباندۇر، (يەنى دۇنيا ۋە ئاخىرەتتە) كەڭ رەھمەت ئىگىسىدۇر. ');
INSERT INTO "saleh" VALUES(59,23,'59:23',' ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، ئۇ (مۇتلەق) پادىشاھتۇر (يەنى پۈتۈن مەخلۇقاتنىڭ مالىكىدۇر)، پاكتۇر (يەنى ھەممە نۇقساندىن پاكتۇر)، (مۆمىنلەرگە) سالامەتلىك بېغىشلىغۇچىدۇر، (دوستلارنى قورقۇنچتىن، ئازابتىن) ئەمىن قىلغۇچىدۇر، ھەممىنى كۆزىتىپ تۇرغۇچىدۇر، غالىبتۇر، قەھر قىلغۇچىدۇر، ئۇلۇغلۇق ئىگىسىدۇر، (مۇشرىكلارنىڭ) شېرىك كەلتۈرگەن نەرسىلىرىدىن پاكتۇر.');
INSERT INTO "saleh" VALUES(59,24,'59:24',' ئاللاھ (ھەممىنى) ياراتقۇچىدۇر، (يوقلۇقتىن) پەيدا قىلغۇچىدۇر، (ھەممە نەرسىگە) سۈرەت (شەكىل) بېغىشلىغۇچىدۇر، ئەڭ چىرايلىق ئىسىملارغا ئىگىدۇر، ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر ئۇنىڭغا تەسبىھ ئېيتىپ تۇرىدۇ، ئۇ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(60,1,'60:1','ئى مۆمىنلەر، مېنىڭ دۈشمىنىمنى ۋە سىلەرنىڭ دۈشمىنىڭلارنى دوست تۇتماڭلار، ئۇلار سىلەرگە كەلگەن ھەقىقەتنى (يەنى ئىسلامنى، قۇرئاننى) ئىنكار قىلغان تۇرسا، ئۇلارغا دوستلۇق يەتكۈزىسىلەر، ئۇلار پەرۋەردىگارىڭلار ئاللاھقا ئىمان ئېيتقىنىڭلار ئۈچۈن پەيغەمبەرنى ۋە سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقاردى، ئەگەر سىلەر مېنىڭ يولۇمدا جىھاد قىلىپ ۋە رازىلىقىمنى تىلەپ چىققان بولساڭلار (ئۇ چاغدا مېنىڭ دۈشمىنىمنى ۋە سىلەرنىڭ دۈشمىنىڭلارنى دوست تۇتماڭلار)، سىلەر ئۇلارغا يوشۇرۇن دوستلۇق يەتكۈزىسىلەر، مەن سىلەر يوشۇرغان ۋە ئاشكارىلىغان نەرسىلەرنى بىلىپ تۇرىمەن، كىمكى شۇنداق قىلىدىكەن (يەنى ئۇلارنى دوست تۇتۇپ، ئۇلارغا پەيغەمبەرنىڭ خەۋەرلىرىنى يەتكۈزىدىكەن)، ئۇ ھەقىقەتەن توغرا يولدىن ئازغان بولىدۇ.');
INSERT INTO "saleh" VALUES(60,2,'60:2','ئەگەر ئۇلار سىلەرنى يەڭسە سىلەرگە دۈشمەن بولىدۇ، سىلەرگە زىيانكەشلىك قىلىدۇ، سىلەرنى تىللايدۇ، سىلەرنىڭ مۇرتەد بولۇشۇڭلارنى ئارزۇ قىلىدۇ.');
INSERT INTO "saleh" VALUES(60,3,'60:3','قىيامەت كۈنى سىلەرنىڭ تۇغقانلىرىڭلار ۋە ئەۋلادلىرىڭلار سىلەرگە ھېچ پايدا يەتكۈزەلمەيدۇ، ئاللاھ ئاراڭلارنى ئايرىۋېتىدۇ (يەنى سىلەر جەننەتتە بولساڭلار، ئۇلار كۇففارلارنىڭ قاتارىدا دوزاختا بولىدۇ)، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(60,4,'60:4','ئىبراھىم ۋە ئۇنىڭ بىلەن بىللە بولغان (مۆمىنلەر) سىلەرگە ھەقىقەتەن ئوبدان نەمۇنىدۇر، ئۆز ۋاقتىدا ئۇلار قەۋمىگە: «ھەقىقەتەن بىز سىلەردىن ۋە سىلەر ئاللاھنى قويۇپ ئىبادەت قىلىۋاتقانلىرىڭلاردىن ئادا ـ جۇدامىز، بىزنىڭ ئارىمىزدىكى ئاداۋەت ۋە ئۆچمەنلىك تاكى سىلەر يالغۇز بىر ئاللاھقا ئىمان ئېيتقىنىڭلارغا قەدەر ئەبەدىي ساقلىنىپ قالىدۇ» دېدى. لېكىن ئىبراھىمنىڭ ئاتىسىغا ئېيتقان «ئەلۋەتتە ساڭا مەغپىرەت تىلەيمەن، ئاللاھنىڭ ئازابىدىن ھېچ نەرسىنى سەندىن دەپئى قىلالمايمەن» دېگەن سۆزىنى سىلەرنىڭ نەمۇنە قىلىشىڭلارغا بولمايدۇ. (ئىبراھىم ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەر ئېيتتى) «پەرۋەردىگارىمىز! ساڭا تەۋەككۈل قىلدۇق، ساڭا قايتتۇق، ئاخىر قايتىدىغان جاي سېنىڭ دەرگاھىڭدۇر.');
INSERT INTO "saleh" VALUES(60,5,'60:5',' پەرۋەردىگارىمىز! كاپىرلارنى بىزگە زىيانكەشلىك قىلىش ئىمكانىيىتىگە ئىگە قىلمىغىن (ئۇلار بىزگە ھۆكۈمران بولۇپ، بىزنى كۆتۈرگىلى بولمايدىغان ئازاب بىلەن دىنىمىزدىن چىقارمىسۇن)، پەرۋەردىگارىمىز! بىزگە مەغپىرەت قىلغىن، سەن ھەقىقەتەن غالىبسەن، ھېكمەت بىلەن ئىش قىلغۇچىسەن».');
INSERT INTO "saleh" VALUES(60,6,'60:6','(ئى مۇھەممەدنىڭ ئۈممىتى!) ئىبراھىم ۋە ئۇنىڭغا ئەگەشكەن مۆمىنلەر سىلەرگە، ئاللاھنى ۋە ئاخىرەت كۈنىنى ئۈمىد قىلىدىغانلارغا ياخشى نەمۇنىدۇر، كىمكى (ئىمانىدىن) يۈز ئۆرۈيدىكەن، (بىلسۇنكى) ئاللاھ (ئۇنىڭدىن ۋە ئۇنداقلاردىن) بىھاجەتتۇر، (ئاللاھ) مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(60,7,'60:7','بەلكىم ئاللاھ سىلەر بىلەن سىلەر ئۆچ كۆرۈدىغان ئادەملەر ئارىسىدا دوستلۇق پەيدا قىلىدۇ، ئاللاھ ھەر نەرسىگە قادىردۇر، ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(60,8,'60:8','(كۇففارلاردىن) سىلەر بىلەن ئۇرۇش قىلمىغان ۋە سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقارمىغانلارغا كەلسەك، ئاللاھ ئۇلارغا ياخشىلىق قىلىشىڭلاردىن، ئۇلارغا ئادىل بولۇشۇڭلاردىن سىلەرنى توسمايدۇ، شۈبھىسىزكى، ئاللاھ ئادىللارنى دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(60,9,'60:9',' ئاللاھ دىن ئۈچۈن سىلەر بىلەن ئۇرۇشقان، سىلەرنى يۇرتۇڭلاردىن ھەيدەپ چىقارغان ۋە سىلەرنى ھەيدەپ چىقىرىشقا ياردەملەشكەنلەرنى دوست تۇتۇشۇڭلاردىن سىلەرنى توسىدۇ، كىمكى ئۇلارنى دوست تۇتىدىكەن، ئۇلار زالىملاردۇر.');
INSERT INTO "saleh" VALUES(60,10,'60:10',' ئى مۆمىنلەر! سىلەرگە مۆمىن ئاياللار ھىجرەت قىلىپ كەلسە، ئۇلارنى سىناپ كۆرۈڭلار، ئۇلارنىڭ ئىمانىنى ئاللاھ ئوبدان بىلىدۇ، ئەگەر ئۇلارنى (سىنىغاندىن كېيىن ھەقىقىي) مۆمىنە دەپ تونۇساڭلار، ئۇلارنى كاپىرلارغا قايتۇرۇپ بەرمەڭلار، ئۇلار كاپىرلارغا ھالال ئەمەس، كاپىرلارمۇ ئۇلارغا ھالال ئەمەس، كاپىر ئەرلىرىنىڭ ئۇلارغا بەرگەن مەھرىنى قايتۇرۇپ بېرىڭلار، ئۇلارنىڭ مەھرىلىرىنى بېرىپ نىكاھىڭلارغا ئالساڭلار سىلەرگە ھېچ گۇناھ بولمايدۇ، كاپىر ئاياللارنى ئەمرىڭلاردا ساقلىماڭلار، ئۇلارغا بەرگەن مەھرىڭلارنى تەلەپ قىلىڭلار، كاپىرلارمۇ (ھىجرەت قىلىپ كەلگەن مۆمىن ئاياللىرىغا بەرگەن) مەھرىلىرىنى تەلەپ قىلسۇن، ئاللاھنىڭ ھۆكمى ئەنە شۇ. ئاللاھ ئاراڭلاردا ھۆكۈم چىقىرىدۇ، ئاللاھ ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(60,11,'60:11','ئەگەر سىلەرنىڭ بەزى ئاياللىرىڭلار كاپىرلار تەرەپكە ئۆتۈپ كەتسە، (كاپىرلار بىلەن) ئۇرۇشۇپ غەنىيمەت ئالغان ۋاقتىڭلاردا، ئاياللىرى كاپىرلارغا كىرىپ كەتكەن كىشىلەرگە (غەنىيمەتتىن) ئۇلارنىڭ بەرگەن مەھرىنىڭ مىقدارىنى بېرىڭلار (يەنى مۇسۇلمانلاردىن بىرىنىڭ ئايالى كۇففارلار تەرەپكە قېچىپ كەتسە، سىلەر كۇففارلار بىلەن ئۇرۇشۇپ ئالغان غەنىيمەت مالدىن مەزكۇر ئايالنىڭ ئېرىگە مەھرى مىقدارى مال بېرىڭلار)، سىلەر ئۆزۈڭلار ئىشەنگەن ئاللاھتىن قورقۇڭلار.');
INSERT INTO "saleh" VALUES(60,12,'60:12',' ئى پەيغەمبەر! مۆمىن ئاياللار ساڭا كېلىپ، ئاللاھقا ھېچ نەرسىنى شېرىك كەلتۈرمەسلىككە، ئوغرىلىق قىلماسلىققا، زىنا قىلماسلىققا، بالىلىرىنى (جاھىلىيەت دەۋرىدىكىدەك قىز بالىلىرىنى نومۇستىن ياكى كەمبەغەللىكتىن قورقۇپ) ئۆلتۈرمەسلىككە، باشقىلارنىڭ بالىسىنى يالغاندىن ئەرلىرىنىڭ بالىسى قىلىۋالماسلىققا، سەن بۇيرۇغان ياخشى ئىشلاردىن باش تارتماسلىققا بەيئەت قىلسا (يەنى يۇقىرىقى شەرتلەر ئاساسىدا ۋەدە بەرسە)، ئۇلارنىڭ بەيئىتىنى قوبۇل قىلغىن، ئۇلار ئۈچۈن ئاللاھتىن مەغپىرەت تىلىگىن، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(60,13,'60:13','ئى مۆمىنلەر! ئاللاھ غەزەپ قىلغان قەۋمنى دوست تۇتماڭلار، ئۇلار ئاخىرەتنىڭ (ساۋابىدىن) قەبرىلىرىدە ياتقان كۇففارلار ئۈمىدىنى ئۈزگەندەك ھەقىقەتەن ئۈمىدىنى ئۈزدى.');
INSERT INTO "saleh" VALUES(61,1,'61:1','ئاسمانلاردىكى نەرسىلەر (يەنى پەرىشتىلەر) ۋە زېمىندىكى نەرسىلەر (يەنى ئىنسانلار، ئۆسۈملۈكلەر، جانسىز شەيئىلەر) ئاللاھقا تەسبىھ ئېيتتى، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(61,2,'61:2',' ئى مۆمىنلەر! سىلەر نېمە ئۈچۈن قىلمايدىغان ئىشنى قىلىمىز دەيسىلەر؟ (يەنى ئەمەلدە سىلەر قىلمايدىغان ياخشى ئىشلارنى نېمە ئۈچۈن ئاغزىڭلاردا قىلىمىز دەيسىلەر؟) ');
INSERT INTO "saleh" VALUES(61,3,'61:3','سىلەرنىڭ قىلمايدىغان ئىشنى (قىلىمىز) دېيىشىڭلار ئاللاھنىڭ دەرگاھىدا ئەڭ ئۆچ (كۆرۈلىدىغان نەرسىدۇر).');
INSERT INTO "saleh" VALUES(61,4,'61:4','شۈبھىسىزكى، ئاللاھ ئۆزىنىڭ يولىدا مۇستەھكەم بىنادەك سەپ بولۇپ جىھاد قىلغانلارنى (يەنى ئۇرۇش مەيدانىدا دۈشمەنلەرگە ئۇچراشقان چاغدا مەۋقەسىدە چىڭ تۇرۇپ غازات قىلغانلارنى) دوست تۇتىدۇ.');
INSERT INTO "saleh" VALUES(61,5,'61:5','ئۆز ۋاقتىدا مۇسا قەۋمىگە: «ئى قەۋمىم! مېنىڭ ئاللاھنىڭ سىلەرگە ئەۋەتكەن پەيغەمبەر ئىكەنلىكىمنى بىلىپ تۇرۇپ سىلەر ماڭا نېمىشقا ئازار بېرىسىلەر؟» دېدى، ئۇلار (ھەقتىن) بۇرۇلۇپ كەتكەندىن كېيىن، ئاللاھ ئۇلارنىڭ دىللىرىنى ھىدايەتتىن بۇرىۋەتتى، ئاللاھ پاسىق قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(61,6,'61:6','ئۆز ۋاقتىدا مەريەمنىڭ ئوغلى ئىيسا: «ئى ئىسرائىل ئەۋلادى! مەن سىلەرگە ھەقىقەتەن ئاللاھ ئەۋەتكەن، مەندىن بۇرۇن كەلگەن تەۋراتنى تەستىق قىلغۇچى، مەندىن كېيىن كېلىدىغان ئەھمەد ئىسىملىك پەيغەمبەر بىلەن خۇشخەۋەر بەرگۈچى پەيغەمبەرمەن» دېدى. ئىيسا ئۇلارغا روشەن مۆجىزىلەرنى ئېلىپ كەلگەن چاغدا، ئۇلار: «بۇ ئوپئوچۇق سېھىردۇر» دېدى. ');
INSERT INTO "saleh" VALUES(61,7,'61:7',' ئىسلامغا دەۋەت قىلىنىۋېتىپ، ئاللاھقا يالغاننى (يەنى ئاللاھنىڭ شېرىكى بار، بالىسى بار دېگەندەك سۆزلەرنى) چاپلىغان كىشىدىنمۇ زالىم كىشى بارمۇ؟ ئاللاھ زالىم (يەنى كاپىر) قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(61,8,'61:8','ئۇلار ئاللاھنىڭ نۇرىنى (يەنى ئاللاھنىڭ دىنىنى ۋە نۇرلۇق شەرىئىتىنى) ئېغىزلىرى بىلەن ئۆچۈرۈۋەتمەكچى بولىدۇ، كاپىرلار يامان كۆرگەن تەقدىردىمۇ، ئاللاھ ئۆزىنىڭ نۇرىنى مۇكەممەل قىلغۇچىدۇر (يەنى ئۆزىنىڭ دىنىنى ئاشكارىلىغۇچىدۇر).');
INSERT INTO "saleh" VALUES(61,9,'61:9','مۇشرىكلار يامان كۆرگەن تەقدىردىمۇ، ئاللاھ ھەق دىننى بارلىق دىنلار ئۈستىدىن غالىب قىلىش ئۈچۈن پەيغەمبىرىنى ھىدايەت بىلەن ۋە ھەق دىن بىلەن ئەۋەتتى.');
INSERT INTO "saleh" VALUES(61,10,'61:10','ئى مۆمىنلەر! سىلەرگە سىلەرنى قاتتىق ئازابتىن قۇتۇلدۇرىدىغان بىر تىجارەتنى كۆرسىتىپ قويايمۇ؟');
INSERT INTO "saleh" VALUES(61,11,'61:11','ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە (چىن) ئىمان ئېيتقايسىلەر، ئاللاھنىڭ يولىدا مېلىڭلار بىلەن، جېنىڭلار بىلەن جىھاد قىلغايسىلەر، ئەگەر بىلسەڭلار، سىلەر ئۈچۈن بۇ ياخشىدۇر.');
INSERT INTO "saleh" VALUES(61,12,'61:12','(شۇنداق قىلساڭلار) ئاللاھ سىلەرنىڭ گۇناھلىرىڭلارنى مەغپىرەت قىلىدۇ، سىلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، دائىمىي تۇرالغۇ بولغان جەننەتلەردىكى گۈزەل سارايلاردا (تۇرغۇزىدۇ)، بۇ چوڭ مۇۋەپپەقىيەتتۇر.');
INSERT INTO "saleh" VALUES(61,13,'61:13',' سىلەرگە (مەرھەمەت قىلىنىدىغان) سىلەر ياخشى كۆرىدىغان ئىككىنچى بىر نېمەت ئاللاھتىن كېلىدىغان نۇسرەت ۋە يېقىن غەلىبە (يەنى مەككىنىڭ پەتھى قىلىنىشى) دۇر، (ئى مۇھەممەد!) مۆمىنلەرگە (بۇ روشەن مەرھەمەت بىلەن) خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(61,14,'61:14','ئى مۆمىنلەر! ھاۋارىيلار (يەنى ئىيسا ئەلەيھىسسالامغا دەسلەپتە ئىمان ئېيتقانلار) دەك ئاللاھنىڭ ياردەمچىلىرى بولۇڭلار، مەريەمنىڭ ئوغلى ئىيسا ھەۋارىيلارغا: « ئاللاھقا (دەۋەت قىلىشتا) كىملەر ياردەمچىلىرىم بولىدۇ؟» دېدى، ھەۋارىيلار: «بىز ئاللاھنىڭ ياردەمچىلىرىمىز» دېدى. ئىسرائىل ئەۋلادىدىن بىر تائىپە ئىمان ئېيتتى، يەنە بىر تائىپە كاپىر بولدى، مۆمىنلەرگە ئۇلارنىڭ ئۆز دۈشمەنلىرىگە قارشى تۇرۇشىدا يار ـ يۆلەكتە بولدۇق، شۇنىڭ بىلەن مۆمىنلەر غالىب بولدى.');
INSERT INTO "saleh" VALUES(62,1,'62:1','ئاللاھقا ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر تەسبىھ ئېيتىدۇ. ئاللاھ (مۇتلەق) پادىشاھتۇر، (ھەممە نۇقسانلاردىن) پاكتۇر، غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(62,2,'62:2',' ئاللاھ ئۇممىيلەرگە (يەنى يېزىشنى ۋە ئوقۇشنى ئۇقمايدىغان ئەرەبلەرگە) ئۇلارنىڭ ئىچىدىن بىر پەيغەمبەرنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) ئەۋەتتى، ئۇ (يەنى پەيغەمبەر) ئۇلارغا قۇرئان ئايەتلىرىنى ئوقۇپ بېرىدۇ، ئۇلارنى (گۇناھلاردىن) پاك قىلىدۇ، ئۇلارغا كىتابنى (يەنى قۇرئاننى) ۋە ھېكمەتنى (يەنى پەيغەمبەرنىڭ سۈننىتىنى) ئۆگىتىدۇ، گەرچە ئۇلار ئىلگىرى ئوچۇق گۇمراھلىقتا بولسىمۇ');
INSERT INTO "saleh" VALUES(62,3,'62:3','ھەمدە (پەيغەمبەرنى) تېخى ئۇلارنىڭ زامانىدا بولمىغان (كېيىن كېلىدىغان) ئىككىنچى بىر قەۋمگە (يەنى قىيامەتكىچە كېلىدىغان مۇسۇلمانلارغا) ئەۋەتتى (يەنى پەيغەمبەر ئەلەيھىسسالامنىڭ پەيغەمبەرلىكى زامانداشلىرىغىلا ئەمەس، بەلكى قىيامەتكىچە كېلىدىغان كىشىلەرگىمۇ ئومۇمىيدۇر)، ئاللاھ غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(62,4,'62:4',' مانا بۇ ئاللاھنىڭ پەزلىدۇركى، ئۇنى خالىغان ئادەمگە بېرىدۇ، ئاللاھ ئۇلۇغ پەزل ئىگىسىدۇر.');
INSERT INTO "saleh" VALUES(62,5,'62:5',' تەۋراتنى كۆتۈرۈشكە (يەنى ئەمەل قىلىشقا) تەكلىپ قىلىنغان، ئاندىن ئۇنى كۆتۈرمىگەنلەر (يەنى تەۋرات بېرىلگەن ۋە ئۇنىڭغا ئەمەل قىلىشقا تەكلىپ قىلىنغان بولسىمۇ، ئۇنىڭغا ئەمەل قىلمىغان ۋە ھىدايەتتىن پايدىلانمىغان يەھۇدىيلار) خۇددى كىتاب يۈكلەنگەن ئېشەككە ئوخشايدۇ (يەنى پايدىلىق چوڭ ـ چوڭ كىتابلارنى ئۈستىگە ئارتىۋالغان، چارچاشتىن باشقا، ئۇلاردىن ھېچقانداق پايدىلىنالمايدىغان ئېشەككە ئوخشايدۇ)، ئاللاھنىڭ ئايەتلىرىنى ئىنكار قىلغان قەۋمنىڭ مىسالى نېمىدېگەن يامان! ئاللاھ زالىم قەۋمنى ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(62,6,'62:6','(ئى مۇھەممەد!) ئېيتقىنكى، «ئى يەھۇدىيلار! ئەگەر سىلەر باشقىلارنى ئەمەس، پەقەت ئۆزۈڭلارنىلا ئاللاھنىڭ دوستلىرى دەپ دەۋا قىلىدىغان بولساڭلار، (ئاخىرەتنىڭ راھىتىگە تېزرەك يېتىش ئۈچۈن) ئۆلۈمنى ئارزۇ قىلىپ بېقىڭلار، ئەگەر (بۇ دەۋايىڭلاردا) راستچىل بولساڭلار».');
INSERT INTO "saleh" VALUES(62,7,'62:7','ئۇلار قىلغان گۇناھلىرى تۈپەيلىدىن، ئۆلۈمنى ھەرگىز ئارزۇ قىلمايدۇ، ئاللاھ زالىملارنى بىلگۈچىدۇر. ');
INSERT INTO "saleh" VALUES(62,8,'62:8','c (ئى مۇھەممەد!) «سىلەر قاچىدىغان ئۆلۈم چوقۇم سىلەرگە يولۇقىدۇ، ئاندىن سىلەر غەيبنى ۋە ئاشكارىنى بىلگۈچى (ئاللاھ) نىڭ دەرگاھىغا قايتۇرۇلىسىلەر، ئاللاھ سىلەرگە قىلمىشىڭلارنى ئېيتىپ بېرىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(62,9,'62:9','ئى مۆمىنلەر! جۈمە كۈنى جۈمە نامىزىغا ئەزان ئېيتىلسا، ئاللاھنى ياد ئېتىشكە (يەنى جۈمە خۇتبىسىنى ئاڭلاشقا ۋە جۈمە نامىزىنى ئادا قىلىشقا) ئالدىراپ بېرىڭلار، ئېلىم ـ سېتىمنى قويۇپ تۇرۇڭلار، ئەگەر بىلسەڭلار بۇ سىلەر ئۈچۈن ياخشىدۇر.');
INSERT INTO "saleh" VALUES(62,10,'62:10','ناماز ئوقۇلۇپ بولغاندا (يەنى نامازدىن فارىغ بولغىنىڭلاردىن كېيىن) زېمىنغا تارىلىپ (يەنى ئۆز مەشغۇلاتىڭلار بىلەن بولۇپ)، ئاللاھنىڭ پەزلىدىن تەلەپ قىلىڭلار، مەقسىتىڭلارغا ئېرىشىش ئۈچۈن ئاللاھنى كۆپ ياد ئېتىڭلار.');
INSERT INTO "saleh" VALUES(62,11,'62:11','(ئى مۇھەممەد!) ئۇلار بىرەر تىجارەتنى ياكى تاماشانى كۆرسە، سېنى ئۆرە تۇرۇپ (خۇتبە ئوقۇۋاتقان) پېتىڭ تاشلاپ، ئۇنىڭغا (يەنى تىجارەتكە) يۈگۈرىدۇ، (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، ئاللاھنىڭ دەرگاھىدىكى نەرسە (يەنى ساۋاب ۋە نېمەت) تاماشادىن ۋە تىجارەتتىن ياخشىدۇر، ئاللاھ رىزىق بەرگۈچىلەرنىڭ ئەڭ ياخشىسىدۇر.');
INSERT INTO "saleh" VALUES(63,1,'63:1','مۇناپىقلار سېنىڭ ئالدىڭغا كەلگەن چاغدا: «گۇۋاھلىق بېرىمىزكى، سەن ھەقىقەتەن ئاللاھنىڭ پەيغەمبىرىدۇرسەن» دەيدۇ، سېنىڭ ھەقىقەتەن ئاللاھنىڭ پەيغەمبىرى ئىكەنلىكىڭنى ئاللاھ بىلىدۇ، ئاللاھ گۇۋاھلىق بېرىدۇكى، مۇناپىقلار (بۇ سۆزلىرىدە) چوقۇم يالغانچىلاردۇر.');
INSERT INTO "saleh" VALUES(63,2,'63:2',' ئۇلار ئۆزلىرىنىڭ يالغان قەسەملىرىنى قالقان قىلىۋالدى، (كىشىلەرنى) ئاللاھنىڭ يولىدىن (يەنى ئاللاھنىڭ دىنىدىن) توستى، ئۇلارنىڭ قىلمىشى نېمىدېگەن يامان!.');
INSERT INTO "saleh" VALUES(63,3,'63:3',' بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلار (ئېغىزلىرىدا) ئىشىنىپ، ئاندىن (دىللىرىدا) ئىنكار قىلدى، ئۇلارنىڭ دىللىرى پېچەتلەندى (يەنى دىللىرىغا ھىدايەت يېتىپ بارالمايدۇ)، ئۇلار (ئىماننى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(63,4,'63:4',' ئۇلارغا قارايدىغان بولساڭ، بەدەن قۇرۇلۇشى (سالاپەتلىكى) سېنى ھەيران قالدۇرىدۇ، ئەگەر ئۇلار سۆزلىسە، (سۆزىنىڭ پاساھىتىدىن) سۆزىگە قۇلاق سالىسەن. (ئىلىمسىز، ئەقىلسىز، پاراسەتسىزلىكتە) گويا ئۇلار تامغا يۆلەپ قويۇلغان (چىرىپ قالغان) ياغاچلاردۇر. ئۇلار (قورقۇنچتىن) ھەرقانداق ئاۋازنى ئۆزلىرىگە قارىتىلغان دەپ گۇمانلىنىدۇ. ئۇلار (ساڭا ۋە مۆمىنلەرگە) دۈشمەندۇر. ئۇلاردىن ھەزەر ئەيلىگىن. ئاللاھ ئۇلارنى ھالاك قىلسۇن! ئۇلار قانداقمۇ (ھىدايەتتىن گۇمراھلىققا) بۇرۇلۇپ كېتىدۇ؟ ');
INSERT INTO "saleh" VALUES(63,5,'63:5','ئۇلارغا ئۆزرە ئېيتىپ: «كېلىڭلار، رەسۇلۇللاھ سىلەرگە مەغپىرەت تىلەيدۇ» دېيىلسە، (مەسخىرە قىلىش يۈزىسىدىن) باشلىرىنى چايقايدۇ، ئۇلارنىڭ تەكەببۇرلۇق قىلغان ھالدا (دەۋەت قىلىنغان نەرسىدىن) يۈز ئۆرۈگەنلىكىنى كۆرۈسەن.');
INSERT INTO "saleh" VALUES(63,6,'63:6','ئۇلارغا (مەيلى) مەغپىرەت تەلەپ قىلغىن، (مەيلى) مەغپىرەت تەلەپ قىلمىغىن، بەرىبىر ئوخشاش. ئاللاھ ئۇلارغا ھەرگىز مەغپىرەت قىلمايدۇ. شۈبھىسىزكى، پاسىق قەۋمنى ئاللاھ ھىدايەت قىلمايدۇ.');
INSERT INTO "saleh" VALUES(63,7,'63:7','ئۇلار: «رەسۇلۇللاھنىڭ يېنىدىكى كىشىلەرگە ئىقتىسادىي ياردەم بەرمەڭلار، ئۇلار تاراپ كەتسۇن» دەيدۇ، ئاسمانلارنىڭ ۋە زېمىننىڭ خەزىنىلىرى ئاللاھنىڭ ئىلكىدىدۇر. ۋەلېكىن مۇناپىقلار (بۇنى) چۈشەنمەيدۇ.');
INSERT INTO "saleh" VALUES(63,8,'63:8',' ئۇلار (يەنى مۇناپىقلار): «مەدىنىگە قايتساق، ئەڭ ئەزىز ئادەم (يەنى ئابدۇللاھ ئىبن ئۇبەي ۋە مۇناپىقلارنىڭ ئۆزلىرىنى دېمەكچى) ئەڭ خار ئادەمنى (يەنى رەسۇلۇللاھنى، مۆمىنلەرنى دېمەكچى) مەدىنىدىن چىقىرىۋېتىدۇ» دېيىشىدۇ. غەلىبە ئاللاھقا، ئاللاھنىڭ پەيغەمبىرىگە ۋە مۆمىنلەرگە مەنسۇپ، لېكىن مۇناپىقلار (غەلىبىنىڭ ئاللاھنىڭ دۈشمەنلىرىگە ئەمەس، دوستلىرىغا مەنسۇپ ئىكەنلىكىنى) بىلمەيدۇ.');
INSERT INTO "saleh" VALUES(63,9,'63:9',' ئى مۆمىنلەر! ماللىرىڭلار ۋە بالىلىرىڭلار سىلەرنى ئاللاھنىڭ زىكرىدىن (يەنى ئاللاھنىڭ تائەت ـ ئىبادىتىدىن) غەپلەتتە قالدۇرمىسۇن، كىملەركى شۇنداق قىلىدىكەن، ئۇلار زىيان تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(63,10,'63:10',' بىرىڭلارغا ئۆلۈم كېلىپ: «پەرۋەردىگارىم! نېمىشقا مېنى (يەنى ئەجىلىمنى) بىر ئاز كېچىكتۈرمىدىڭ، سەدىقە قىلىپ ياخشىلاردىن بولار ئىدىم» دېيىشتىن بۇرۇن، ئۆزۈڭلارغا رىزىق قىلىپ بېرىلگەن نەرسىلەردىن ئاللاھ يولىدا سەدىقە قىلىڭلار.');
INSERT INTO "saleh" VALUES(63,11,'63:11',' ئاللاھ ھېچ ئادەمنى ئەجىلى كەلگەندە ھەرگىز كېچىكتۈرمەيدۇ، ئاللاھ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر. ');
INSERT INTO "saleh" VALUES(64,1,'64:1','ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەر ئاللاھقا تەسبىھ ئېيتىدۇ، پادىشاھلىق ئاللاھقا مەنسۇپتۇر، جىمى ھەمدۇسانا ئاللاھقا خاستۇر، ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(64,2,'64:2','ئاللاھ سىلەرنى خەلق ئەتتى، ئاراڭلاردا مۆمىنلەرمۇ بار، كاپىرلارمۇ بار، ئاللاھ سىلەرنىڭ قىلىۋاتقان ئىشىڭلارنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(64,3,'64:3',' ئاللاھ ئاسمانلارنى ۋە زېمىننى ھەق ياراتتى، سىلەرنى سۈرەتكە كىرگۈزدى، سۈرىتىڭلارنى چىرايلىق قىلدى، ئاخىر قايتىدىغان جاي ئۇنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(64,4,'64:4','ئاللاھ ئاسمانلاردىكى ۋە زېمىندىكى نەرسىلەرنى بىلىپ تۇرىدۇ، ئاللاھ (نىيەتلىرىڭلاردىن، ئەمەللىرىڭلاردىن) يوشۇرغانلىرىڭلارنى ۋە ئاشكارىلىغانلىرىڭلارنى بىلىپ تۇرىدۇ، ئاللاھ دىللاردىكى سىرلارنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(64,5,'64:5','سىلەرگە ئىلگىرى كاپىر بولۇپ ئەمەللىرىنىڭ ۋابالىنى تېتىغانلارنىڭ خەۋىرى كەلمىدىمۇ؟ ئۇلار (ئاخىرەتتە) قاتتىق ئازابقا دۇچار بولىدۇ.');
INSERT INTO "saleh" VALUES(64,6,'64:6',' بۇ شۇنىڭ ئۈچۈندۇركى، ئۇلارغا پەيغەمبەرلىرى روشەن مۆجىزىلەر بىلەن كەلگەن ئىدى، ئۇلار: «ئىنسان بىزنى ھىدايەت قىلالامدۇ؟» دېدى. (پەيغەمبەرنى) ئىنكار قىلدى، (ئىماندىن) يۈز ئۆرۈدى. ئاللاھ (ئۇلارنىڭ ئىمانىغا) موھتاج ئەمەستۇر، ئاللاھ مەخلۇقاتتىن بىھاجەتتۇر، مەدھىيەگە لايىقتۇر.');
INSERT INTO "saleh" VALUES(64,7,'64:7','كاپىرلار ھەرگىز تىرىلدۈرۈلمەيمىز دەپ گۇمان قىلىشتى، ئېيتقىنكى: «ھەرگىز ئۇنداق ئەمەس، پەرۋەردىگارىم بىلەن قەسەمكى، سىلەر چوقۇم تىرىلدۈرۈلىسىلەر، ئاندىن سىلەرگە قىلمىشىڭلاردىن خەۋەر بېرىلىدۇ، بۇ ئاللاھقا ئاساندۇر».');
INSERT INTO "saleh" VALUES(64,8,'64:8',' ئاللاھقا، ئاللاھنىڭ پەيغەمبىرىگە ۋە بىز نازىل قىلغان نۇرغا (يەنى قۇرئانغا) ئىمان ئېيتىڭلار، ئاللاھ قىلىۋاتقان ئىشىڭلاردىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(64,9,'64:9','ئۇ كۈندە ئاللاھ سىلەرنى قىيامەت ئۈچۈن توپلايدۇ، ئۇ زىيان تارتىدىغان كۈندۇر (يەنى ئۇ كۈندە ئىماننى تەرك ئەتكەنلىكى بىلەن كاپىرنىڭ زىيان تارتقانلىقى، ياخشى ئەمەللەرنى دېگەندەك قىلالمىغانلىقى بىلەن مۆمىننىڭ زىيان تارتقانلىقى ئاشكارا بولىدۇ). كىمكى ئاللاھقا ئىمان ئېيتىپ ياخشى ئىش قىلىدىكەن، ئاللاھ ئۇنىڭ يامان ئىشلىرىنى يوققا چىقىرىدۇ، ئۇنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ، ئۇلار ئۇ جەننەتلەردە مەڭگۈ قالىدۇ، بۇ كاتتا مۇۋەپپەقىيەتتۇر.');
INSERT INTO "saleh" VALUES(64,10,'64:10',' ئاللاھنىڭ (بىرلىكىنى ۋە قۇدرىتىنى) ئىنكار قىلغانلار، ئايەتلىرىمىزنى يالغانغا چىقارغانلار ئەھلى دوزاخ بولۇپ، ئۇلار دوزاختا مەڭگۈ قالىدۇ، ئۇ نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(64,11,'64:11','كىشىگە يەتكەن ھەر قانداق مۇسىبەت پەقەت ئاللاھنىڭ ئىزنى (يەنى قازاسى) بىلەنلا (يېتىدۇ)، كىمكى ئاللاھقا (يەنى ھەر قانداق مۇسىبەت ئاللاھنىڭ قازاسى بىلەن يېتىدۇ دېگەن سۆزگە) ئىشىنىدىكەن، ئاللاھ ئۇنىڭ قەلبىنى (سەۋرگە) يېتەكلەيدۇ، ئاللاھ ھەر نەرسىنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(64,12,'64:12','ئاللاھقا ئىتائەت قىلىڭلار، پەيغەمبەرگە ئىتائەت قىلىڭلار، ئەگەر (پەيغەمبەرنىڭ دەۋىتىدىن) يۈز ئۆرۈسەڭلار (پەيغەمبەرگە زىيان يوق، ئۆزۈڭلارغا زىيان)، پەيغەمبىرىمىزنىڭ ۋەزىپىسى پەقەت (دەۋەتنى) روشەن تەبلىغ قىلىشتۇر.');
INSERT INTO "saleh" VALUES(64,13,'64:13','ئاللاھتىن باشقا ھېچ مەبۇد (بەرھەق) يوقتۇر، مۆمىنلەر ئاللاھقا تەۋەككۈل قىلسۇن.');
INSERT INTO "saleh" VALUES(64,14,'64:14',' ئى مۆمىنلەر! سىلەرنىڭ ئاياللىرىڭلاردىن، بالىلىرىڭلاردىن سىلەرگە دۈشمەن بولىدىغانلىرىمۇ بار، ئۇلاردىن ئېھتىيات قىلىڭلار، ئەگەر (ئۇلارنى) ئەپۇ قىلساڭلار، كەچۈرسەڭلار، مەغپىرەت قىلساڭلار (ئاللاھ سىلەرنىمۇ مەغپىرەت قىلىدۇ). ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر. ');
INSERT INTO "saleh" VALUES(64,15,'64:15',' سىلەرنىڭ ماللىرىڭلار، بالىلىرىڭلار (سىلەرگە ئاللاھ تەرىپىدىن) بىر تۈرلۈك سىناقتۇر، ئاللاھنىڭ دەرگاھىدا كاتتا ساۋاب بار.');
INSERT INTO "saleh" VALUES(64,16,'64:16',' تاقىتىڭلارنىڭ يېتىشىچە ئاللاھقا تەقۋادارلىق قىلىڭلار، (ۋەزىنى) ئاڭلاڭلار، (ئەمرىگە) ئىتائەت قىلىڭلار، (ئاللاھنىڭ يولىدا ماللىرىڭلارنى) سەرپ قىلىڭلار، (بۇ) ئۆزۈڭلار ئۈچۈن پايدىلىقتۇر. كىمكى نەپسىنىڭ بېخىللىقىدىن ساقلىنىدىكەن، ئۇ مەقسىتىگە ئېرىشكۈچىدۇر.');
INSERT INTO "saleh" VALUES(64,17,'64:17','ئاللاھقا قەرزى ھەسەنەقەرزى ھەسەنە بەرسەڭلار (يەنى مەمنۇنىيەت بىلەن پۇل – مال سەرپ قىلساڭلار)، ئاللاھ سىلەرگە ئۇنى ھەسسىلەپ بېرىدۇ، سىلەرگە مەغپىرەت قىلىدۇ، ئاللاھ ئاز ياخشىلىققا كۆپ ساۋاب بەرگۈچىدۇر، ھەلىمدۇر (يەنى بەندىلىرىنى ئازابلاشقا ئالدىراپ كەتمەيدۇ).');
INSERT INTO "saleh" VALUES(64,18,'64:18',' (ئاللاھ) غەيبنى ۋە ئاشكارىنى بىلگۈچىدۇر، غالىبتۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(65,1,'65:1','ئى پەيغەمبەر! (ئۈممىتىڭگە ئېيتقىنكى) سىلەر ئاياللارنى تالاق قىلساڭلار، ئۇلارنىڭ ئىددىتىگە (لايىق پەيتتە يەنى پاكلىق ھالىتىدە) تالاق قىلىڭلار. ئىددەتنى ساناڭلار، پەرۋەردىگارىڭلاردىن قورقۇڭلار، (ئۇلارنىڭ ئىددىتى توشمىغىچە) ئۇلارنى ئۆيلىرىدىن چىقىرىۋەتمەڭلار، ئۇلارمۇ (ئۆيىدىن) چىقمىسۇن، ئۇلار پەقەت ئوچۇق پاھىشە قىلسىلا ئاندىن (ھەدنى ئىجرا قىلىش ئۈچۈن) چىقىدۇ، ئەنە شۇ ئاللاھنىڭ قانۇنىدۇر، كىمكى ئاللاھنىڭ قانۇنىدىن ھالقىپ كېتىدىكەن، ئۇ ئۆزىگە زۇلۇم قىلغان بولىدۇ، سەن بىلمەيسەنكى، ئاللاھ شۇنىڭدىن كېيىن (يەنى تالاقتىن كېيىن) بىرەر ئىشنى مەيدانغا كەلتۈرۈشى مۇمكىن.');
INSERT INTO "saleh" VALUES(65,2,'65:2',' ئۇلارنىڭ ئىددىتى توشقان چاغدا، ئۇلار بىلەن چىرايلىقچە يېنىشىڭلار ياكى ئۇلار بىلەن چىرايلىقچە ئۈزلىشىپ كېتىڭلار. (يېنىشقان، ئاجراشقان چېغىڭلاردا) ئاراڭلاردىن ئىككى ئادىل گۇۋاھچىنى گۇۋاھ قىلىڭلار. خۇدالىق ئۈچۈن (توغرا) گۇۋاھ بولۇڭلار. بۇنىڭ بىلەن (يەنى بۇ ھۆكۈم بىلەن) ئاللاھقا ۋە ئاخىرەت كۈنىگە ئىمان ئېيتقان كىشىگە ۋەز ـ نەسىھەت قىلىنىدۇ. كىمكى ئاللاھتىن قورقىدىكەن، ئاللاھ ئۇنىڭغا چىقىش يولى بېرىدۇ.');
INSERT INTO "saleh" VALUES(65,3,'65:3',' ئاللاھ ئۇنىڭغا ئويلىمىغان يەردىن رىزىق بېرىدۇ. كىمكى ئاللاھقا تەۋەككۈل قىلسا، ئاللاھ ئۇنىڭغا كۇپايە قىلىدۇ. ئاللاھ ھەقىقەتەن مەقسىتىگە يېتەلەيدۇ. ئاللاھ ھەقىقەتەن ھەر بىر نەرسە ئۈچۈن مۇئەييەن مىقدار، مۇئەييەن ۋاقىت بەلگىلىدى.');
INSERT INTO "saleh" VALUES(65,4,'65:4','ئاياللىرىڭلارنىڭ ئىچىدىكى ھەيزدىن ئۈمىدىنى ئۈزگەنلەردىن (يەنى يېشى چوڭ بولغانلىقتىن ھەيز كۆرمەيدىغانلارنىڭ ئىددىتىنىڭ قانچىلىك بولىدىغانلىقىدىن) شۈبھىدە بولساڭلار، ئۇلارنىڭ ئىددىتى ئۈچ ئايدۇر. ھەيز كۆرمىگەنلەرنىڭ ئىددىتى ھەم ئۈچ ئايدۇر. ھامىلىدار ئاياللارنىڭ ئىددىتى (مەيلى ئايرىلىپ كەتكەن بولسۇن، مەيلى ئېرى ئۆلۈپ كەتكەن بولسۇن) يەڭگىش بىلەنلا تۈگەيدۇ. كىمكى ئاللاھتىن قورقىدىكەن، ئاللاھ ئۇنىڭ ئىشىنى ئاسانلاشتۇرۇپ بېرىدۇ.');
INSERT INTO "saleh" VALUES(65,5,'65:5','ئەنە شۇ ئاللاھنىڭ سىلەرگە نازىل قىلغان ھۆكمىدۇر. كىمكى ئاللاھتىن قورقىدىكەن، ئاللاھ ئۇنىڭ گۇناھلىرىنى يوققا چىقىرىدۇ، ئۇنىڭغا كاتتا ساۋاب بېرىدۇ.');
INSERT INTO "saleh" VALUES(65,6,'65:6','ئۇلارنى (يەنى قويۇۋېتىلگەن ئاياللىرىڭلارنى) قۇدرىتىڭلارنىڭ يېتىشىچە ئۆزۈڭلار ئولتۇرۇۋاتقان جايىڭلارنىڭ بىر قىسمىدا ئولتۇرغۇزۇڭلار. ئۇلارنى تەڭلىكتە قالدۇرۇش ئۈچۈن زىيان يەتكۈزمەڭلار (يەنى ئۇلارنى چىقىپ كېتىشكە مەجبۇرلاش ئۈچۈن تۇرالغۇ جەھەتتىن، نەپىقە جەھەتتىن قىسماڭلار). ئەگەر ئۇلار ھامىلىدار بولسا، يەڭگىگەنگە قەدەر ئۇلارنى تەمىنلەڭلار. ئەگەر ئۇلار سىلەر ئۈچۈن (بالىلىرىڭلارنى) ئېمىتىپ بەرسە، ئۇلارنىڭ (ئېمىتىش) ھەققىنى بېرىڭلار. ھەققانىي ئاساستا ئۆزئارا مەسلىھەتلىشىڭلار. ئەگەر پىكىردە كېلىشەلمىسەڭلار، ئۇنى (يەنى بالىنى) باشقا بىر ئايال ئېمىتسۇن. ');
INSERT INTO "saleh" VALUES(65,7,'65:7',' باي ئادەم (ئايالىنى ۋە بالىسىنى) ئۆزىنىڭ بايلىقىغا يارىشا تەمىنلىسۇن، رىزقى تار قىلىنغان ئادەم ئاللاھنىڭ ئۇنىڭغا بەرگىنىگە يارىشا تەمىنلىسۇن (يەنى ھەر ئادەم ئۆزىنىڭ ئىقتىسادىي ئەھۋالىغا يارىشا خىراجەت قىلسۇن). ئاللاھ ئىنساننى پەقەت تاقىتى يېتىدىغان ئىشقىلا تەكلىپ قىلىدۇ (يەنى پېقىر ئادەمنى باينى تەكلىپ قىلغان ئىشقا تەكلىپ قىلمايدۇ)، ئاللاھ قىيىنچىلىقتىن كېيىن ئاسانلىقنى بېرىدۇ (يەنى پېقىرلىقتىن كېيىن بايلىقنى بېرىدۇ).');
INSERT INTO "saleh" VALUES(65,8,'65:8',' نۇرغۇن شەھەرنىڭ ئاھالىلىرى پەرۋەردىگارىنىڭ ۋە ئۇنىڭ پەيغەمبىرىنىڭ ئەمرىدىن باش تارتتى. ئۇلاردىن قاتتىق ھېساب ئالدۇق ۋە ئۇلارنى قاتتىق جازالىدۇق. ');
INSERT INTO "saleh" VALUES(65,9,'65:9',' ئۇلار قىلمىشلىرىنىڭ ۋابالىنى تېتىدى. ئۇلارنىڭ قىلمىشلىرىنىڭ ئاقىۋىتى زىيان تارتىشتىن ئىبارەت بولدى.');
INSERT INTO "saleh" VALUES(65,10,'65:10','ئاللاھ ئۇلارغا (ئاخىرەتتە) قاتتىق ئازاب تەييارلىدى. ئى ئىمان ئېيتقان ئەقىل ئىگىلىرى! ئاللاھتىن قورقۇڭلار، ئاللاھ سىلەرگە قۇرئاننى نازىل قىلدى.');
INSERT INTO "saleh" VALUES(65,11,'65:11',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلارنى زۇلمەتتىن نۇرغا چىقىرىش ئۈچۈن سىلەرگە ئاللاھنىڭ روشەن ئايەتلىرىنى تىلاۋەت قىلىپ بېرىدىغان پەيغەمبەرنى ئەۋەتتى. كىمكى ئاللاھقا ئىمان ئېيتىدىكەن ۋە ياخشى ئەمەل قىلىدىكەن، (ئاللاھ) ئۇنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ. ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ئاللاھ ئۇلارغا ئېسىل رىزىق (يەنى جەننەتتىكى رىزىق ۋە نېمەتلەرنى) بېرىدۇ.');
INSERT INTO "saleh" VALUES(65,12,'65:12',' ئاللاھ يەتتە ئاسماننى ياراتتى زېمىننىمۇ ئاسمانلارغا ئوخشاش (يەنى يەتتە قىلىپ) ياراتتى. ئاللاھنىڭ ئەمرى ئۇلارنىڭ ئارىسىدا جارى بولۇپ تۇرىدۇ، (سىلەرگە بۇنى بىلدۈرۈشۈم) ئاللاھنىڭ ھەممىگە قادىر ئىكەنلىكىنى ۋە ئاللاھنىڭ ھەممە نەرسىنى تولۇق بىلىدىغانلىقىنى بىلىشىڭلار ئۈچۈندۇر.');
INSERT INTO "saleh" VALUES(66,1,'66:1','ئى پەيغەمبەر! نېمە ئۈچۈن ئاللاھ ساڭا ھالال قىلغان نەرسىنى ئاياللىرىڭنىڭ رازىلىقىنى تىلەش يۈزىسىدىن ھارام قىلىسەن؟ ئاللاھ ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(66,2,'66:2','ئاللاھ سىلەرگە (كەففارەت) بىلەن قەسىمىڭلاردىن چىقىشنى بەلگىلىدى. ئاللاھ سىلەرنىڭ ئىگەڭلاردۇر. ئاللاھ ھەر نەرسىنى بىلگۈچىدۇر. ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(66,3,'66:3',' ئۆز ۋاقتىدا پەيغەمبەر ئاياللىرىدىن بىرىگە (يەنى ھەفسەگە) بىر سۆزنى يوشۇرۇن ئېيتقان ئىدى. ئۇ (يەنى ھەفسە) ئۇنى (ئائىشەگە) ئېيتىپ قويغان، ئاللاھ بولسا سىرنىڭ ئاشكارىلانغانلىقىنى (پەيغەمبەرگە) بىلدۈرگەن چاغدا، ئۇ (يەنى پەيغەمبەر) ئۇنىڭغا (ھەفسەگە) ئۇ (يەنى ھەفسە) ئاشكارىلاپ قويغان سۆزلەرنىڭ بەزىسىنى ئېيتتى (يەنى يۈزىگە سالدى)، يەنە بەزىسىنى ئېيتمىدى (يەنى يۈزىگە سالمىدى). پەيغەمبەر ئۇنىڭغا سىرنى ئاشكارىلاپ قويغانلىقىنى ئېيتقان چاغدا، ئۇ (يەنى ھەفسە): «بۇنى ساڭا كىم خەۋەر قىلدى؟» دېدى. پەيغەمبەر: «ماڭا (بۇنى) ھەممىنى بىلىپ تۇرغۇچى، ھەممىدىن تولۇق خەۋەردار ئاللاھ خەۋەر قىلدى» دېدى.');
INSERT INTO "saleh" VALUES(66,4,'66:4',' ئەگەر (ئائىشە، ھەفسە) ئىككىڭلار ئاللاھقا تەۋبە قىلساڭلار (ئۆزۈڭلار ئۈچۈن ياخشىدۇر). سىلەرنىڭ دىلىڭلار (رەسۇلۇللاھقا ئىخلاس قىلىشتىن) بۇرۇلۇپ كەتتى، ئەگەر سىلەر (رەسۇلۇللاھ خوپ كۆرمەيدىغان ئىش ئۈستىدە) ئۇنىڭغا تاقابىل تۇرساڭلار، ئۇ چاغدا ئاللاھ ئۇنىڭ مەدەتكارىدۇر، جىبرىلمۇ ئۇنىڭ مەدەتكارىدۇر، مۆمىنلەرنىڭ ياخشىلىرىمۇ ئۇنىڭ مەدەتكارىدۇر. ئۇلاردىن باشقا، پەرىشتىلەرمۇ (ئۇنىڭغا) يۆلەنچۈكتۇر.');
INSERT INTO "saleh" VALUES(66,5,'66:5',' ئەگەر ئۇ سىلەرنى قويۇۋەتسە، پەرۋەردىگارى سىلەرنىڭ ئورنۇڭلارغا، سىلەردىن ياخشى، (ئاللاھنىڭ ۋە پەيغەمبەرنىڭ ئەمرىگە) بويسۇنغۇچى، (ئاللاھقا ۋە پەيغەمبەرگە) ئىمان ئېيتقۇچى، ئىتائەت قىلغۇچى، گۇناھلاردىن تەۋبە قىلغۇچى، ئىبادەت قىلغۇچى، روزا تۇتقۇچى چوكانلارنى ۋە قىزلارنى ئۇنىڭغا بېرىشى مۇمكىن.');
INSERT INTO "saleh" VALUES(66,6,'66:6',' ئى مۆمىنلەر! ئۆزۈڭلارنى ۋە بالا ـ چاقاڭلارنى ئىنسان ۋە تاشلار يېقىلغۇ بولىدىغان، رەھىم قىلمايدىغان قاتتىق قول پەرىشتىلەر مۇئەككەل بولغان دوزاختىن ساقلاڭلار. ئۇ پەرىشتىلەر ئاللاھنىڭ بۇيرۇقىدىن چىقمايدۇ، نېمىگە بۇيرۇلسا شۇنى ئىجرا قىلىدۇ.');
INSERT INTO "saleh" VALUES(66,7,'66:7',' ئى كاپىرلار! بۈگۈن ئۆزرە ئېيتماڭلار، سىلەر پەقەت قىلمىشىڭلارنىڭ جازاسىنى تارتىسىلەر.');
INSERT INTO "saleh" VALUES(66,8,'66:8',' ئى مۆمىنلەر! ئاللاھقا سەمىمىي تەۋبە قىلىڭلار. ئۈمىدكى، پەرۋەردىگارىڭلار سىلەرنىڭ گۇناھىڭلارنى يوققا چىقىرىدۇ. سىلەرنى ئاستىدىن ئۆستەڭلەر ئېقىپ تۇرىدىغان جەننەتلەرگە كىرگۈزىدۇ. ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئاللاھ پەيغەمبەرنى ۋە ئۇنىڭ بىلەن بىللە بولغان مۆمىنلەرنى رەسۋا قىلمايدۇ. ئۇلارنىڭ نۇرى ئۇلارنىڭ ئالدىدا ۋە ئوڭ تەرىپىدە يۈرىدۇ. ئۇلار: «ئى پەرۋەردىگارىمىز! بىزگە نۇرىمىزنى مۇكەممەللەشتۈرۈپ بەرگىن، بىزگە مەغپىرەت قىلغىن، سەن بولساڭ ھەقىقەتەن ھەر نەرسىگە قادىرسەن» دەيدۇ.');
INSERT INTO "saleh" VALUES(66,9,'66:9',' ئى پەيغەمبەر! سەن كۇففار ۋە مۇناپىقلار بىلەن كۈرەش قىلغىن. ئۇلارغا قاتتىق مۇئامىلە قىلغىن. ئۇلارنىڭ جايى جەھەننەمدۇر، جەھەننەم نېمىدېگەن يامان جاي!.');
INSERT INTO "saleh" VALUES(66,10,'66:10','ئاللاھ كۇففارلارغا (ئۇلارنىڭ مۆمىنلەر بىلەن تۇغقانچىلىقىنىڭ پايدا بەرمەسلىكىدە) نۇھنىڭ ئايالىنى ۋە لۇتنىڭ ئايالىنى مىسال قىلىپ كۆرسەتتى. ئۇلار بولسا بىزنىڭ بەندىلىرىمىزدىن ئىككى ياخشى بەندىمىزنىڭ ئەمرىدە ئىدى. ئۇلارنىڭ ئىككىلىسى ئەرلىرىگە (ئىمان ئېيتماسلىق بىلەن) خىيانەت قىلدى. ئۇلارنىڭ ئەرلىرى (يەنى نۇھ بىلەن لۇت) (پەيغەمبەر تۇرۇقلۇق) ئۇلاردىن ئاللاھنىڭ ئازابىدىن ھېچ نەرسىنى دەپئى قىلالمىدى. (ئۇ ئىككىسىگە) «دوزاخقا كىرگۈچىلەر بىلەن بىللە كىرىڭلار» دېيىلدى.');
INSERT INTO "saleh" VALUES(66,11,'66:11',' ئاللاھ ئىمان ئېيتقانلارغا پىرئەۋننىڭ ئايالىنى مىسال قىلىپ كۆرسەتتى. ئۆز ۋاقتىدا ئۇ: «ئى پەرۋەردىگارىم! دەرگاھىڭدا ماڭا جەننەتتە بىر ئۆي بىنا قىلغىن، مېنى پىرئەۋندىن ۋە ئۇنىڭ يامان ئەمەلىدىن قۇتۇلدۇرغىن، مېنى زالىم قەۋمدىن قۇتۇلدۇرغىن» دېدى');
INSERT INTO "saleh" VALUES(66,12,'66:12',' ھەمدە ئىمراننىڭ قىزى مەريەمنى مىسال قىلىپ كۆرسەتتى، ئۇ ئىپپىتىنى ساقلىدى، ئۇنىڭغا بىزنىڭ تەرىپىمىزدىن بولغان روھنى پۈۋلىدۇق (پۈۋلەنگەن روھ ئۇنىڭ ئىچىگە كىرىپ، ئىيساغا ھامىلىدار بولدى). ئۇ پەرۋەردىگارىنىڭ سۆزلىرىنى ۋە (نازىل قىلغان) كىتابلىرىنى تەستىق قىلدى ۋە ئاللاھقا ئىتائەت قىلغۇچىلاردىن بولدى. ');
INSERT INTO "saleh" VALUES(67,1,'67:1','(پۈتۈن ئاسمان ـ زېمىننىڭ) پادىشاھلىقى ئىلكىدە بولغان ئاللاھنىڭ بەرىكىتى بۈيۈكتۇر. ئاللاھ ھەر نەرسىگە قادىردۇر.');
INSERT INTO "saleh" VALUES(67,2,'67:2','سىلەردىن قايسىڭلارنىڭ ئەمەلى ئەڭ ياخشى ئىكەنلىكىنى سىناش ئۈچۈن، ئاللاھ ئۆلۈمنى ۋە تىرىكلىكنى ياراتتى، ئاللاھ غالىبتۇر، (تەۋبە قىلغۇچىنى) ناھايىتى مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(67,3,'67:3',' ئاللاھ يەتتە ئاسماننى بىرىنى بىرىنىڭ ئۈستىدە قىلىپ ياراتتى، مېھرىبان ئاللاھنىڭ يارىتىشىدا ھېچ نۇقساننى كۆرمەيسەن. سەن (ئاسمانلارغا) تەكرار قاراپ باققىنكى، بىرەر يوچۇقنى كۆرەمسەن؟');
INSERT INTO "saleh" VALUES(67,4,'67:4','ئاندىن سەن يەنە ئىككى قېتىم قارىغىن. كۆزۈڭ (ئاسمانلاردا بىرەر نۇقساننى تېپىشتىن) ئۈمىد ئۈزگەن ۋە تالغان ھالدا قايتىدۇ.');
INSERT INTO "saleh" VALUES(67,5,'67:5','(بىزگە بارلىق ئاسمانلاردىن) ئەڭ يېقىن ئاسماننى چىراغلار (يەنى يورۇق يۇلتۇزلار) بىلەن بېزىدۇق ۋە ئۇلارنى (پەرىشتىلەرنىڭ سۆزلىرىنى ئوغرىلىقچە تىڭشاشقا ئۇرۇنغان) شەيتانلارنى ئاتىدىغان نەرسە قىلدۇق (يەنى شەيتانلار يۇلتۇزلاردىن چىققان نۇر بىلەن ئېتىلىدۇ)، شەيتانلارغا (ئاخىرەتتە) دوزاخ ئازابىنى تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(67,6,'67:6','پەرۋەردىگارىنى ئىنكار قىلغانلارمۇ دوزاخ ئازابىغا دۇچار بولىدۇ. دوزاخ نېمىدېگەن يامان جاي!');
INSERT INTO "saleh" VALUES(67,7,'67:7',' ئۇلار دوزاخقا تاشلانغان چاغدا، قايناپ تۇرغان دوزاخنىڭ (ئېشەك ھاڭرىغاندەك) سەت ئاۋازىنى ئاڭلايدۇ.');
INSERT INTO "saleh" VALUES(67,8,'67:8','دوزاخ غەزەپتىن پارچىلىنىپ كېتىشكە تاس قالىدۇ. دوزاخقا ھەر قاچان بىر توپ ئادەم تاشلانغان چاغدا، دوزاخقا مۇئەككەل پەرىشتىلەر ئۇلاردىن: «سىلەرگە ئاگاھلاندۇرغۇچى (يەنى پەيغەمبەر) كەلمىگەنمىدى؟» دەپ سورايدۇ.');
INSERT INTO "saleh" VALUES(67,9,'67:9',' ئۇلار: «ئۇنداق ئەمەس، بىزگە ھەقىقەتەن ئاگاھلاندۇرغۇچى كەلگەن، بىز (ئۇنى) ئىنكار قىلدۇق ھەمدە بىز: ئاللاھ (ھېچ ئادەمگە ۋەھىيدىن) بىرەر نەرسە نازىل قىلىنغىنى يوق، سىلەر پەقەت چوڭقۇر ئازغۇنلۇقتا- دېدۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(67,10,'67:10',' ئۇلار: «ئەگەر بىز (پەيغەمبەرنىڭ سۆزلىرىنى) ئاڭلىغان، ياكى چۈشەنگەن بولساق، ئەھلى دوزاخ قاتارىدا بولماس ئىدۇق» دەيدۇ.');
INSERT INTO "saleh" VALUES(67,11,'67:11','ئۇلار گۇناھىنى تونۇيدۇ، ئەھلى دوزاخ ئاللاھنىڭ رەھمىتىدىن يىراق بولسۇن!');
INSERT INTO "saleh" VALUES(67,12,'67:12','پەرۋەردىگارىدىن كۆرمەي تۇرۇپ قورققانلار مەغپىرەتكە ۋە كاتتا ساۋابقا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(67,13,'67:13',' سىلەر سۆزۈڭلارنى مەخپىي قىلىڭلار ياكى ئاشكارا قىلىڭلار (ئاللاھ ھامان ئۇنى بىلىپ تۇرىدۇ). ئاللاھ ھەقىقەتەن دىللاردىكى سىرلارنى بىلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(67,14,'67:14',' (مەخلۇقاتنى) ياراتقان زات بىلمەمدۇ؟ ئۇ شەيئىلەرنىڭ نازۇك تەرەپلىرىنى بىلگۈچىدۇر، ھەممىدىن تولۇق خەۋەرداردۇر.');
INSERT INTO "saleh" VALUES(67,15,'67:15','ئاللاھ سىلەرگە زېمىننى مېڭىشقا ئاسان قىلدى، زېمىننىڭ ئەتراپىدا مېڭىڭلار. ئاللاھنىڭ (بەرگەن) رىزقىدىن يەڭلار، سىلەر تىرىلگەندىن كېيىن ئاللاھنىڭ دەرگاھىغا قايتۇرۇلىسىلەر.');
INSERT INTO "saleh" VALUES(67,16,'67:16',' ئاسماندىكى زاتنىڭ يەر تەۋرىگەن چاغدا سىلەرنى يەرگە يۇتقۇزۇۋېتىشىدىن قورقمامسىلەر؟ ');
INSERT INTO "saleh" VALUES(67,17,'67:17',' ياكى ئاسماندىكى زاتنىڭ سىلەرگە تاش ياغدۇرۇشىدىن قورقمامسىلەر؟ (سىلەر ئازابنى كۆرگەن چاغدا) مېنىڭ ئاگاھلاندۇرۇشۇمنىڭ قانداق ئىكەنلىكىنى (يەنى ھەق ئىكەنلىكىنى) بىلىسىلەر.');
INSERT INTO "saleh" VALUES(67,18,'67:18',' ئۇلاردىن ئىلگىرى ئۆتكەنلەر (پەيغەمبەرلىرىنى) يالغانغا چىقاردى. (ئۇلارنى ئازاب چۈشۈرۈش بىلەن) ئەيىبلىگەنلىكىم قانداق ئىكەن؟');
INSERT INTO "saleh" VALUES(67,19,'67:19','ئۇلار قاناتلىرىنى يايغان ۋە يىغقان ھالدا ئۈستىدە ئۇچۇپ كېتىۋاتقان قۇشلارغا (ئىبرەت كۆزى بىلەن) قارىمامدۇ؟ (قاناتلىرىنى كەرگەن ۋە يىغقان ھالدا) ئۈستىلىرىدە ئۇچۇۋاتقان قۇشلارنى (قاناتلىرىنى ئاچقان ۋە يىغقان چاغدا يەرگە چۈشۈپ كېتىشتىن) پەقەت ئاللاھ (قۇدرىتى بىلەن) تۇتۇپ تۇرىدۇ، ئاللاھ ھەقىقەتەن ھەر نەرسىنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(67,20,'67:20','سىلەرگە ئاللاھ ياردەم بەرگەندىن تاشقىرى، يەنە كىم سىلەرگە ياردەمچى قوشۇن بولالايدۇ؟ كاپىرلار پەقەت ئالدىنىش ئىچىدىدۇر (يەنى شەيتان ئۇلارنى ئازاب نازىل بولمايدۇ دەپ ئالدايدۇ).');
INSERT INTO "saleh" VALUES(67,21,'67:21','ئەگەر ئاللاھ رىزقىنى تۇتۇپ قالسا، سىلەرگە كىم رىزىق بېرىدۇ؟ ئۇلار تەكەببۇرلۇقتا، ھەقتىن يىراقلىشىشتا ئەزۋەيلەپ كەتكەندۇر.');
INSERT INTO "saleh" VALUES(67,22,'67:22','زادى قايسى ئادەم يول تاپالايدۇ؟ بېشىنى ساڭگىلىتىپ ماڭغان ئادەممۇ ياكى قەددىنى رۇس تۇتۇپ تۈز يولدا ماڭغان ئادەممۇ؟ ');
INSERT INTO "saleh" VALUES(67,23,'67:23',' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «ئاللاھ سىلەرنى ياراتتى، سىلەر ئۈچۈن قۇلاقنى، كۆزلەرنى، دىللارنى ياراتتى، سىلەر ئاز شۈكۈر قىلسىلەر».');
INSERT INTO "saleh" VALUES(67,24,'67:24','ئېيتقىنكى، «ئاللاھ سىلەرنى يەر يۈزىدە يارىتىپ كۆپەيتتى، (ھېساب بېرىش ئۈچۈن) ئاللاھنىڭ دەرگاھىغا توپلىنىسىلەر».');
INSERT INTO "saleh" VALUES(67,25,'67:25',' ئۇلار: «ئەگەر سىلەر راستچىل بولساڭلار، بۇ ۋەدە قاچان ئىشقا ئاشۇرۇلىدۇ» دەيدۇ.');
INSERT INTO "saleh" VALUES(67,26,'67:26',' (ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «(بۇ ھەقتىكى) ئىلىم (يەنى قىيامەتنىڭ قاچان بولۇشى) ئاللاھنىڭ دەرگاھىدىدۇر، مەن پەقەت ئوچۇق ئاگاھلاندۇرغۇچىمەن».');
INSERT INTO "saleh" VALUES(67,27,'67:27',' ئۇلار ئازابنىڭ ئۆزلىرىگە يېقىنلىقىنى كۆرگەن چاغدا يۈزلىرى قارىداپ كېتىدۇ، ئۇلارغا: «مانا بۇ سىلەر (دۇنيادىكى چېغىڭلاردا) تەلەپ قىلغان نەرسىدۇر» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(67,28,'67:28','(ئى مۇھەممەد! سېنىڭ ئۆلۈمىڭنى ئارزۇ قىلىدىغان مۇشرىكلارغا) ئېيتقىنكى، «ئېيتىپ بېقىڭلارچۇ! ئەگەر ئاللاھ (سىلەر ئارزۇ قىلغاندەك) مېنى ۋە مەن بىلەن بولغان (مۆمىنلەرنى) ھالاك قىلىدىغان بولسا، ياكى (بىزنىڭ ئەجىلىمىزنى كېچىكتۈرۈپ) بىزگە رەھىم قىلىدىغان بولسا، كاپىرلارنى قاتتىق ئازابتىن كىم قۇتۇلدۇرىدۇ؟»');
INSERT INTO "saleh" VALUES(67,29,'67:29',' ئېيتقىنكى، «ئۇ ناھايىتى مېھرىبان ئاللاھتۇر، ئۇنىڭغا ئىمان ئېيتتۇق ۋە ئۇنىڭغا تەۋەككۈل قىلدۇق، سىلەر كىمنىڭ روشەن گۇمراھلىقتا ئىكەنلىكىنى كەلگۈسىدە بىلىسىلەر».');
INSERT INTO "saleh" VALUES(67,30,'67:30',' (ئۇلارغا) «ئېيتىپ بېقىڭلارچۇ؟ (سۈيىڭلار يەر ئاستىغا) سىڭىپ كەتسە، سىلەرگە كىم ئېقىن سۇ كەلتۈرۈپ بېرىدۇ» دېگىن.');
INSERT INTO "saleh" VALUES(68,1,'68:1',' نۇن. قەلەم بىلەن ۋە ئۇلار يازغان نەرسىلەر بىلەن قەسەمكى');
INSERT INTO "saleh" VALUES(68,2,'68:2','مۇھەممەد!) سەن پەرۋەردىگارىڭنىڭ نېمىتى بىلەن، (مۇشرىكلار ئېيتقاندەك) مەجنۇن ئەمەسسەن.');
INSERT INTO "saleh" VALUES(68,3,'68:3','مۇقەررەركى، سەن ئەلۋەتتە ئۆكسۈمەس ساۋابقا ئېرىشىسەن.');
INSERT INTO "saleh" VALUES(68,4,'68:4','(ئى مۇھەممەد!) سەن ھەقىقەتەن بۈيۈك ئەخلاققا ئىگىسەن.');
INSERT INTO "saleh" VALUES(68,5,'68:5',' كەلگۈسىدە قايسىڭلارنىڭ مەجنۇن ئىكەنلىكىنى سەن كۆرۈسەن، ئۇلارمۇ كۆرىدۇ[5ـ6].');
INSERT INTO "saleh" VALUES(68,6,'68:6',' كەلگۈسىدە قايسىڭلارنىڭ مەجنۇن ئىكەنلىكىنى سەن كۆرۈسەن، ئۇلارمۇ كۆرىدۇ[5ـ6].');
INSERT INTO "saleh" VALUES(68,7,'68:7',' شۈبھىسىزكى، سېنىڭ پەرۋەردىگارىڭ ئۆزىنىڭ يولىدىن ئازغانلارنى ئوبدان بىلىدۇ، ھىدايەت تاپقۇچىلارنىمۇ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(68,8,'68:8','ئىنكار قىلغۇچىلارغا ئىتائەت قىلمىغىن.');
INSERT INTO "saleh" VALUES(68,9,'68:9',' ئۇلار سېنىڭ ئۆزلىرى بىلەن مادارا قىلىشىڭنى، شۇنىڭ بىلەن ئۆزلىرىنىڭ (سەن بىلەن) مادارا قىلىشىنى ئارزۇ قىلىدۇ.');
INSERT INTO "saleh" VALUES(68,10,'68:10',' قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن[10ـ13].');
INSERT INTO "saleh" VALUES(68,11,'68:11',' قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن[10ـ13].');
INSERT INTO "saleh" VALUES(68,12,'68:12',' قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن[10ـ13].');
INSERT INTO "saleh" VALUES(68,13,'68:13',' قەسەمخور، پەس، غەيۋەتخور، سۇخەنچى، بېخىل، ھەددىدىن ئاشقۇچى، گۇناھكار، قوپال، ئۇنىڭ ئۈستىگە ھارامدىن بولغان ئادەم (يەنى ۋەلىد ئىبن مۇغىرە) گە ئىتائەت قىلمىغىن[10ـ13].');
INSERT INTO "saleh" VALUES(68,14,'68:14',' بىزنىڭ ئايەتلىرىمىز ئۇنىڭغا ئوقۇپ بېرىلسە، ئۇنىڭ مال ـ مۈلكى ۋە ئوغۇللىرى بولغانلىقى ئۈچۈن، «(بۇ) بۇرۇنقىلارنىڭ ھېكايىلىرىدۇر»، دەيدۇ[14ـ15].');
INSERT INTO "saleh" VALUES(68,15,'68:15',' بىزنىڭ ئايەتلىرىمىز ئۇنىڭغا ئوقۇپ بېرىلسە، ئۇنىڭ مال ـ مۈلكى ۋە ئوغۇللىرى بولغانلىقى ئۈچۈن، «(بۇ) بۇرۇنقىلارنىڭ ھېكايىلىرىدۇر»، دەيدۇ[14ـ15].');
INSERT INTO "saleh" VALUES(68,16,'68:16','ئۇنىڭ بۇرنىنى داغلاپ بەلگە سالىمىز.');
INSERT INTO "saleh" VALUES(68,17,'68:17','شۈبھىسىزكى، بىز ئۇلارنى (يەنى مەككە مۇشرىكلىرىگە ئوخشاش ۋەھىينى ئىنكار قىلغانلارنى) مەلۇم باغنىڭ ئىگىلىرىنى سىنىغاندەك (قەھەتچىلىك، ئاچارچىلىققا دۇچار قىلىش بىلەن) سىنىدۇق. ئۇلار (يەنى باغنىڭ ئىگىلىرى) ئۇنىڭ مېۋىلىرىنى (كەمبەغەللەر ئۇقمىسۇن، ئۇلارغا ھېچ نەرسە بەرمەيلى دېيىشىپ) سەھەردىلا ئۈزۈۋېلىشقا قەسەم ئىچىشكەن ئىدى.');
INSERT INTO "saleh" VALUES(68,18,'68:18','ئۇلار خۇدا خالىسا دېمىگەن ئىدى. ');
INSERT INTO "saleh" VALUES(68,19,'68:19',' ئۇلار ئۇخلاۋاتقاندا، باغقا پەرۋەردىگارىڭ تەرىپىدىن ئازاب (يەنى يانغىن) نازىل بولۇپ، باغ (كۆيۈپ) قاپقارا كۈلدەك بولۇپ قالدى[19ـ20].');
INSERT INTO "saleh" VALUES(68,20,'68:20',' ئۇلار ئۇخلاۋاتقاندا، باغقا پەرۋەردىگارىڭ تەرىپىدىن ئازاب (يەنى يانغىن) نازىل بولۇپ، باغ (كۆيۈپ) قاپقارا كۈلدەك بولۇپ قالدى[19ـ20].');
INSERT INTO "saleh" VALUES(68,21,'68:21',' ئۇلار: «ئەگەر (مېۋىلەرنى) ئۈزمەكچى بولساڭلار باغقا بېرىڭلار» دەپ، ئەتىگەندە بىر ـ بىرىنى توۋلاشتى[21ـ22].');
INSERT INTO "saleh" VALUES(68,22,'68:22',' ئۇلار: «ئەگەر (مېۋىلەرنى) ئۈزمەكچى بولساڭلار باغقا بېرىڭلار» دەپ، ئەتىگەندە بىر ـ بىرىنى توۋلاشتى[21ـ22].');
INSERT INTO "saleh" VALUES(68,23,'68:23','ئۇلار ماڭدى، يولدا كېتىۋېتىپ: «بۈگۈن باغقا ھەرگىز بىر مىسكىنمۇ كىرمىسۇن» دېيىشىپ پىچىرلاشتى[23 ـ 24].');
INSERT INTO "saleh" VALUES(68,24,'68:24','ئۇلار ماڭدى، يولدا كېتىۋېتىپ: «بۈگۈن باغقا ھەرگىز بىر مىسكىنمۇ كىرمىسۇن» دېيىشىپ پىچىرلاشتى[23 ـ 24].');
INSERT INTO "saleh" VALUES(68,25,'68:25','ئۇلار: «(مىسكىنلەرنى باغقا كىرىشتىن) توسىيالايمىز دېگەن ئوي بىلەن، ئەتىگەندە تۇرۇشتى.');
INSERT INTO "saleh" VALUES(68,26,'68:26',' ئۇلار باغنىڭ (كۆيۈپ قاپقارا بولۇپ كەتكەنلىكىنى) كۆرۈپ: «بىز ھەقىقەتەن ئېزىپ قېلىپتۇق، ياق بىز (باغنىڭ مېۋىسىدىن) قۇرۇق قېلىپتۇق» دېدى[26ـ27].');
INSERT INTO "saleh" VALUES(68,27,'68:27',' ئۇلار باغنىڭ (كۆيۈپ قاپقارا بولۇپ كەتكەنلىكىنى) كۆرۈپ: «بىز ھەقىقەتەن ئېزىپ قېلىپتۇق، ياق بىز (باغنىڭ مېۋىسىدىن) قۇرۇق قېلىپتۇق» دېدى[26ـ27].');
INSERT INTO "saleh" VALUES(68,28,'68:28',' ئۇلارنىڭ ياخشىسى (يەنى ئەڭ ئەقىللىقى): «سىلەر نېمىشقا ئاللاھقا تەۋبە قىلىپ تەسبىھ ئېيتمايسىلەر دېمىگەنمىدىم؟» دېدى.');
INSERT INTO "saleh" VALUES(68,29,'68:29','ئۇلار: «پەرۋەردىگارىمىز! سېنى پاك دەپ ئېتىقاد قىلىمىزكى، بىز (مىسكىنلەرنىڭ ھەققىنى مەنئى قىلىش بىلەن) ھەقىقەتەن زۇلۇم قىلغۇچىلاردىن بولدۇق» دېدى.');
INSERT INTO "saleh" VALUES(68,30,'68:30','شۇنىڭ بىلەن ئۇلار بىر ـ بىرگە قارىشىپ ئۆز ئارا مالامەت قىلىشتى.');
INSERT INTO "saleh" VALUES(68,31,'68:31','ئۇلار دېيىشتى: «ۋاي ھالىمىزغا! بىز ھەقىقەتەن ھەددىمىزدىن ئېشىپتۇق. ');
INSERT INTO "saleh" VALUES(68,32,'68:32',' ئۇمىدكى، پەرۋەردىگارىمىز بىزگە بۇنىڭدىن ياخشىراق باغنى بەرگەي، بىز ئەلۋەتتە پەرۋەردىگارىمىزدىن ئۈمىد قىلغۇچىلارمىز».');
INSERT INTO "saleh" VALUES(68,33,'68:33',' ئازاب مانا مۇشۇنداقتۇر (يەنى قۇرەيشكىمۇ باغ ئىگىلىرىگە چۈشكەن ئازاب چۈشىدۇ). ئەگەر ئۇلار بىلسە، ئاخىرەتنىڭ ئازابى تېخىمۇ قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(68,34,'68:34','. تەقۋادارلار پەرۋەردىگارىڭنىڭ دەرگاھىدا ھەقىقەتەن نازۇنېمەتلىك جەننەتلەردىن بەھرىمەن بولىدۇ.');
INSERT INTO "saleh" VALUES(68,35,'68:35',' بىز مۇسۇلمانلارنى كاپىرلار بىلەن باراۋەر قىلامدۇق؟');
INSERT INTO "saleh" VALUES(68,36,'68:36','سىلەرگە نېمە بولدى؟ قانداقسىگە مۇنداق ھۆكۈم چىقىرىسىلەر!');
INSERT INTO "saleh" VALUES(68,37,'68:37','ياكى سىلەردە (ئاسماندىن نازىل بولغان) بىر كىتاب بولۇپ، ئۇنىڭدىن سىلەرگە (قىيامەت كۈنى) خالىغىنىڭلار بار، دېگەننى ئوقۇدۇڭلارمۇ؟[37ـ38]');
INSERT INTO "saleh" VALUES(68,38,'68:38','ياكى سىلەردە (ئاسماندىن نازىل بولغان) بىر كىتاب بولۇپ، ئۇنىڭدىن سىلەرگە (قىيامەت كۈنى) خالىغىنىڭلار بار، دېگەننى ئوقۇدۇڭلارمۇ؟[37ـ38]');
INSERT INTO "saleh" VALUES(68,39,'68:39',' ياكى سىلەرنىڭ ھۆكۈم قىلغان نەرسەڭلارنىڭ بولۇشى ئۈچۈن بىز سىلەر بىلەن قىيامەت كۈنى ئاندىن ۋاقتى توشىدىغان مەھكەم بىر ئەھدى تۈزۈشكەنمۇ؟');
INSERT INTO "saleh" VALUES(68,40,'68:40','ئۇلاردىن سوراپ باققىنكى، ئۇلارنىڭ ئارىسىدا قايسىسى يۇقىرىقى ئىشلارغا كېپىللىك قىلالايدۇ؟');
INSERT INTO "saleh" VALUES(68,41,'68:41',' ياكى ئۇلارنىڭ (يۇقىرىقى ئىشلارغا كېپىللىك قىلىدىغان شېرىكلىرى) بارمۇ؟ ئەگەر ئۇلار (دەۋاسىدا) راستچىل بولسا، شېرىكلىرىنى كەلتۈرۈپ باقسۇن. ');
INSERT INTO "saleh" VALUES(68,42,'68:42','ئۇ كۈندە (يەنى قىيامەت كۈنىدە) ئىش قىيىنلىشىدۇ. ئۇلار (ئىمانىنى سىناش ئۈچۈن) سەجدە قىلىشقا چاقىرىلىدۇ، ئۇلار سەجدە قىلالمايدۇ.');
INSERT INTO "saleh" VALUES(68,43,'68:43',' ئۇلار (قورقۇنچتىن) تىكىلىپ قارىيالمايدۇ، ئۇلارغا خارلىق يۈزلىنىدۇ، ئۇلار (دۇنيادا) ساغلام چاغلىرىدا سەجدىگە دەۋەت قىلىنغان ئىدى (ئۇلار سەجدىدىن باش تارتاتتى).');
INSERT INTO "saleh" VALUES(68,44,'68:44',' بۇ قۇرئاننى ئىنكار قىلغانلارنى ماڭا قويۇۋەتكىن، ئۇلارنى تۇيدۇرماستىن، ئاستا ـ ئاستا ھالاكەتكە يۈزلەندۈرىمىز.');
INSERT INTO "saleh" VALUES(68,45,'68:45',' ئۇلارغا مۆھلەت بېرىمەن. شۈبھىسىزكى، مېنىڭ تەدبىرىم پۇختىدۇر. ');
INSERT INTO "saleh" VALUES(68,46,'68:46',' ياكى ئۇلاردىن (دىنغا دەۋەت قىلغانلىقىڭغا) ھەق سوراپ، ئۇلارنىڭ (ئىقتىسادىي) يۈكىنى ئېغىرلاشتۇرۇۋەتتىڭمۇ؟');
INSERT INTO "saleh" VALUES(68,47,'68:47',' ياكى ئۇلارنىڭ يېنىدا غەيب (يەنى غەيبنى ئىچىگە ئالغان لەۋھۇلمەھپۇز) بار بولۇپ، ئۇلار (ئېيتىدىغانلىرىنى شۇنىڭغا) يازامدۇ؟');
INSERT INTO "saleh" VALUES(68,48,'68:48','پەرۋەردىگارىڭنىڭ ھۆكمىگە سەۋر قىلغىن، (ئالدىراڭغۇلۇقتا، زېرىككەكلىكتە) بېلىقنىڭ ساھىبىغا (يەنى يۇنۇسقا) ئوخشاش بولمىغىن. ئۆز ۋاقتىدا ئۇ (بېلىقنىڭ قارنىدا تۇرۇپ) غەمكىن ھالدا پەرۋەردىگارىغا ئىلتىجا قىلدى.');
INSERT INTO "saleh" VALUES(68,49,'68:49',' ئەگەر ئۇ پەرۋەردىگارىنىڭ نېمىتىگە ئېرىشمىسە ئىدى، ئەلۋەتتە، ئۇ (قىلمىشىغا) مالامەت قىلىنغان ھالدا (دەرەخمۇ يوق، تاغمۇ يوق) بىر دالىغا تاشلانغان بولاتتى.');
INSERT INTO "saleh" VALUES(68,50,'68:50','پەرۋەردىگارى ئۇنى (پەيغەمبەرلىككە) تاللىدى، ئۇنى ياخشىلاردىن قىلدى.');
INSERT INTO "saleh" VALUES(68,51,'68:51','كاپىرلار قۇرئاننى ئاڭلىغان چاغلىرىدا (ساڭا بولغان دۈشمەنلىكىنىڭ قاتتىقلىقىدىن يامان) كۆزلىرى بىلەن سېنى يىقىتىۋېتىشكە تاس قالىدۇ. سېنى: «شۈبھىسىز بىر مەجنۇن» دېيىشىدۇ.');
INSERT INTO "saleh" VALUES(68,52,'68:52','قۇرئان پەقەت تامام ئەھلى جاھان ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(69,1,'69:1','(بولۇشى ئېنىق) قىيامەت. ');
INSERT INTO "saleh" VALUES(69,2,'69:2',' قىيامەت نېمىدۇر؟ ');
INSERT INTO "saleh" VALUES(69,3,'69:3','قىيامەتنىڭ نېمە ئىكەنلىكىنى قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(69,4,'69:4',' سەمۇد ۋە ئاد (قەبىلىلىرى) قىيامەتنى ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(69,5,'69:5','سەمۇد بولسا ئادەتتىن تاشقىرى قاتتىق ئاۋاز بىلەن ھالاك قىلىندى.');
INSERT INTO "saleh" VALUES(69,6,'69:6',' ئاد بولسا ھەددىدىن زىيادە قاتتىق ۋە سوغۇق بوران بىلەن ھالاك قىلىندى.');
INSERT INTO "saleh" VALUES(69,7,'69:7','ئاللاھ ئۇلارغا بوراننى يەتتە كېچە، سەككىز كۈندۈز ئۈزۈلدۈرمەي ئاپىرىدە قىلدى. (ئاد) قەۋمىنىڭ (ئۆز ماكانلىرىدا ھالاك بولۇپ) خورما دەرىخىنىڭ پور كۆتەكلىرىدەك ياتقانلىقىنى كۆرۈسەن.');
INSERT INTO "saleh" VALUES(69,8,'69:8',' ئۇلاردىن بىرەر ئادەمنىڭ قالغانلىقىنى كۆرەلەمسەن؟');
INSERT INTO "saleh" VALUES(69,9,'69:9','پىرئەۋن ھەم ئۇنىڭدىن ئىلگىرىكىلەر ۋە شەھەرلىرى دۈم كۆمتۈرۈۋېتىلگەنلەر (يەنى لۇت قەۋمى) خاتا ئىشلارنى قىلدى.');
INSERT INTO "saleh" VALUES(69,10,'69:10','ئۇلار پەرۋەردىگارىنىڭ پەيغەمبىرىگە ئاسىيلىق قىلدى. شۇنىڭ بىلەن ئۇلارنى ئاللاھ قاتتىق جازالىدى.');
INSERT INTO "saleh" VALUES(69,11,'69:11',' شۈبھىسىزكى، (نۇھنىڭ زامانىدا) چوڭ سۇ يامراپ كەتكەن چاغدا، سىلەرنى (يەنى ئەجدادىڭلارنى) بىز كېمىگە سالدۇق');
INSERT INTO "saleh" VALUES(69,12,'69:12',' سىلەرگە ئۇنى ئىبرەت قىلىش ئۈچۈن، ئېسىدە تۇتۇۋالىدىغان قۇلاقلارنىڭ ئۇنى تۇتۇۋېلىشى ئۈچۈن (بىز شۇنداق قىلدۇق)[ـ12].');
INSERT INTO "saleh" VALUES(69,13,'69:13','بىرىنچى قېتىملىق سۇر چېلىنغان چاغدا،');
INSERT INTO "saleh" VALUES(69,14,'69:14','زېمىن ۋە تاغلار (ئورۇنلىرىدىن) كۆتىرىلىپ (بىر ـ بىرىگە) ئۇرۇلۇپ (ئۇۋىلىپ كەتكەن) چاغدا، ');
INSERT INTO "saleh" VALUES(69,15,'69:15','بۇ كۈندە چوڭ ئىش (يەنى قىيامەت) يۈز بېرىدۇ.');
INSERT INTO "saleh" VALUES(69,16,'69:16',' ئاسمان يېرىلىدۇ، بۇ كۈندە ئاسمان (قالايمىقانلىشىپ) ئاجىزلىشىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(69,17,'69:17',' پەرىشتىلەر ئاسماننىڭ ئەتراپىدا تۇرىدۇ. بۇ كۈندە پەرۋەردىگارىڭنىڭ ئەرشىنى سەككىز پەرىشتە (باشلىرى) ئۈستىدە كۆتۈرۈپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(69,18,'69:18','بۇ كۈندە سىلەر (ئاللاھقا) (ھېساب بېرىش ئۈچۈن) توغرىلىنىسىلەر، سىلەر ھېچقانداق بىر سىرنى يوشۇرۇپ قالالمايسىلەر.');
INSERT INTO "saleh" VALUES(69,19,'69:19','نامە ـ ئەمالى ئوڭ قولىغا بېرىلگەن ئادەم (خۇشاللىقتىن): «بۇ مېنىڭ نامە ـ ئەمالىمنى ئېلىپ ئوقۇپ بېقىڭلار، مەن ھېساباتىمغا مۇلاقات بولىدىغانلىقىمغا مۇقەررەر ئىشەنگەن ئىدىم» دەيدۇ[19ـ20].');
INSERT INTO "saleh" VALUES(69,20,'69:20','نامە ـ ئەمالى ئوڭ قولىغا بېرىلگەن ئادەم (خۇشاللىقتىن): «بۇ مېنىڭ نامە ـ ئەمالىمنى ئېلىپ ئوقۇپ بېقىڭلار، مەن ھېساباتىمغا مۇلاقات بولىدىغانلىقىمغا مۇقەررەر ئىشەنگەن ئىدىم» دەيدۇ[19ـ20].');
INSERT INTO "saleh" VALUES(69,21,'69:21','ئۇ كۆڭۈللۈك تۇرمۇشتا بولىدۇ.');
INSERT INTO "saleh" VALUES(69,22,'69:22',' ئېسىل جەننەتتە بولىدۇ.');
INSERT INTO "saleh" VALUES(69,23,'69:23','ئۇنىڭ مېۋىلىرى (ئۆرە تۇرغاندىمۇ، ئولتۇرغاندىمۇ، ياتقاندىمۇ قولىنى سوزۇپلا ئالالايدىغان دەرىجىدە) يېقىن بولىدۇ.');
INSERT INTO "saleh" VALUES(69,24,'69:24',' (ئۇلارغا) «ئۆتكەنكى كۈنلەردە (يەنى دۇنيادىكى چاغلاردا) ياخشى ئەمەللەرنى قىلغانلىقىڭلار ئۈچۈن، خۇشال ـ خۇرام يەڭلار، ئىچىڭلار» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(69,25,'69:25',' نامە ـ ئەمالى سول قولىغا بېرىلگەن ئادەم ئېيتىدۇكى، «ماڭا نامە ـ ئەمالىم بېرىلمىگەن بولسىچۇ! ');
INSERT INTO "saleh" VALUES(69,26,'69:26',' ھېسابىمنىڭ نېمە بولغانلىقىنى بىلمىسەم ئىدىم! ');
INSERT INTO "saleh" VALUES(69,27,'69:27','دۇنيادا ئۆلۈمىم ھاياتىمنى (ئاخىرەتتە قايتا تىرىلمەس قىلىپ) ئاخىرلاشتۇرسىچۇ!');
INSERT INTO "saleh" VALUES(69,28,'69:28','مېنىڭ مال ـ مۈلكۈم ماڭا ھېچ نەرسىگە ئەسقاتمىدى.');
INSERT INTO "saleh" VALUES(69,29,'69:29','مېنىڭ ھوقۇقۇم قولۇمدىن كەتتى».');
INSERT INTO "saleh" VALUES(69,30,'69:30',' (ئاللاھ دوزاخقا مۇئەككەل پەرىشتىلەرگە) ئېيتىدۇ: «ئۇنى تۇتۇڭلار، ئۇنىڭغا تاقاق سېلىڭلار،');
INSERT INTO "saleh" VALUES(69,31,'69:31',' ئاندىن ئۇنى دوزاخقا تاشلاڭلار، ئ');
INSERT INTO "saleh" VALUES(69,32,'69:32','اندىن ئۇنى ئۇزۇنلۇقى 70 گەز كېلىدىغان زەنجىر بىلەن باغلاڭلار».');
INSERT INTO "saleh" VALUES(69,33,'69:33','(ئۇنىڭ بۇنداق ئازابلىنىشى شۇنىڭ ئۈچۈندۇركى) ئۇ ھەقىقەتەن ئۇلۇغ ئاللاھقا ئىمان ئېيتمىغان ئىدى.');
INSERT INTO "saleh" VALUES(69,34,'69:34','مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايتتى.');
INSERT INTO "saleh" VALUES(69,35,'69:35',' بۈگۈن ئۇنىڭغا بۇ يەردە (ئۇنىڭدىن ئازابنى كۆتۈرۈۋېتىدىغان) دوست يوقتۇر.');
INSERT INTO "saleh" VALUES(69,36,'69:36','ئۇنىڭغا يىرىڭدىن باشقا تاماق يوقتۇر.');
INSERT INTO "saleh" VALUES(69,37,'69:37',' ئۇنى پەقەت ئازغان ئادەملەرلا (يەنى كاپىرلارلا) يەيدۇ.');
INSERT INTO "saleh" VALUES(69,38,'69:38',' مەن سىلەر كۆرۈپ تۇرىدىغان نەرسىلەر بىلەن قەسەم قىلىمەن،');
INSERT INTO "saleh" VALUES(69,39,'69:39','سىلەر كۆرمەيدىغان نەرسىلەر بىلەنمۇ قەسەم قىلىمەن (يەنى بارلىق مەخلۇقات بىلەن قەسەم قىلىمەن)[ـ39].');
INSERT INTO "saleh" VALUES(69,40,'69:40','بۇ قۇرئان ئەلۋەتتە ھۈرمەتلىك پەيغەمبەرنىڭ (ئاللاھ تەرىپىدىن يەتكۈزگەن) سۆزىدۇر.');
INSERT INTO "saleh" VALUES(69,41,'69:41','ئۇ شائىرنىڭ سۆزى ئەمەستۇر. سىلەر ناھايىتى ئاز ئىشىنىسىلەر.');
INSERT INTO "saleh" VALUES(69,42,'69:42','ئۇ كاھىننىڭ سۆزىمۇ ئەمەس، سىلەر ئاز ۋەز ـ نەسىھەت ئالىسىلەر.');
INSERT INTO "saleh" VALUES(69,43,'69:43','(ئۇ) ئالەملەرنىڭ پەرۋەردىگارى تەرىپىدىن نازىل قىلىنغاندۇر.');
INSERT INTO "saleh" VALUES(69,44,'69:44','ئەگەر ئۇ بىزنىڭ نامىمىزدىن يالغاننى توقۇيدىغان بولسا،');
INSERT INTO "saleh" VALUES(69,45,'69:45','ئەلۋەتتە ئۇنى قۇدرىتىمىز بىلەن جازالايتتۇق.');
INSERT INTO "saleh" VALUES(69,46,'69:46',' ئاندىن ئۇنىڭ ئاساسى قىزىل تومۇرىنى كېسىپ تاشلايتتۇق.');
INSERT INTO "saleh" VALUES(69,47,'69:47','سىلەردىن ھېچبىر ئادەم ئۇنى قوغداپ قالالمايتتى.');
INSERT INTO "saleh" VALUES(69,48,'69:48',' شەك ـ شۈبھىسىزكى، بۇ قۇرئان تەقۋادارلار ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(69,49,'69:49',' بىز شەك ـ شۈبھىسىز بىلىمىزكى، سىلەرنىڭ ئاراڭلاردا (قۇرئان ئايەتلىرى شۇنچە روشەن تۇرۇقلۇق) ئۇنى ئىنكار قىلغۇچىلار بار.');
INSERT INTO "saleh" VALUES(69,50,'69:50',' قۇرئان كاپىرلارغا (ئۇلار قۇرئاننى تەستىق قىلغۇچىلارنىڭ ساۋابىنى ۋە ئىنكار قىلغۇچىلارنىڭ ئازابىنى كۆرگەن چاغدا) ئەلۋەتتە ھەسرەت ئېلىپ كېلىدۇ.');
INSERT INTO "saleh" VALUES(69,51,'69:51','قۇرئان، مۇقەررەركى شەكسىز ھەقىقەتتۇر. ');
INSERT INTO "saleh" VALUES(69,52,'69:52','ئۇلۇغ پەرۋەردىگارىڭنى (مۇشرىكلارنىڭ ئىپتىرالىرىدىن) پاك ئېتىقاد قىلغىن.');
INSERT INTO "saleh" VALUES(70,1,'70:1','بىر تىلىگۈچى (يەنى نەزر ئىبن ھارىس) كاپىرلارغا چوقۇم بولىدىغان ئازابنىڭ (چۈشۈشىنى) تىلىدى.');
INSERT INTO "saleh" VALUES(70,2,'70:2','ئۇ (ئازاب) نى ھېچ ئادەم قايتۇرالمايدۇ.');
INSERT INTO "saleh" VALUES(70,3,'70:3','ئۇ (ئازاب) ئۆرلەش يوللىرىنىڭ ئىگىسى ئاللاھ تەرىپىدىن بولغان.');
INSERT INTO "saleh" VALUES(70,4,'70:4',' پەرىشتىلەر ۋە روھ (يەنى جىبرىئىل) ئاللاھقا شۇنداق بىر كۈندە ئۆرلەيدۇكى، كۈننىڭ ئۇزۇنلۇقى 50 مىڭ يىل كېلىدۇ.');
INSERT INTO "saleh" VALUES(70,5,'70:5','(ئى مۇھەممەد! قەۋمىڭنىڭ يەتكۈزگەن ئەزىيەتلىرىگە) چىرايلىقچە سەۋر قىلغىن.');
INSERT INTO "saleh" VALUES(70,6,'70:6',' ئۇلار ئۇ ئازابنى يىراق دەپ قارايدۇ.');
INSERT INTO "saleh" VALUES(70,7,'70:7','بىز ئۇنى يېقىن دەپ قارايمىز.');
INSERT INTO "saleh" VALUES(70,8,'70:8','ئۇ كۈندە ئاسمان ئېرىتىلگەن مىستەك بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(70,9,'70:9','تاغلار ئېتىلغان رەڭلىك يۇڭدەك بولۇپ قالىدۇ.');
INSERT INTO "saleh" VALUES(70,10,'70:10',' (ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم ـ قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ[10ـ14].');
INSERT INTO "saleh" VALUES(70,11,'70:11',' (ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم ـ قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ[10ـ14].');
INSERT INTO "saleh" VALUES(70,12,'70:12',' (ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم ـ قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ[10ـ14].');
INSERT INTO "saleh" VALUES(70,13,'70:13',' (ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم ـ قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ[10ـ14].');
INSERT INTO "saleh" VALUES(70,14,'70:14',' (ھەر ئادەم ئۆز ھالى بىلەن بولۇپ قالىدىغانلىقى ئۈچۈن) دوست دوستىدىن كۆرۈپ تۇرۇپ (ھال) سورىمايدۇ، گۇناھكار ئادەم ئۆز ئورنىغا ئوغۇللىرىنى، ئايالىنى، قېرىندىشىنى ۋە ئۆزى مەنسۇپ بولغان قەۋم ـ قېرىنداشلىرىنى ۋە يەر يۈزىدىكى جىمى ئادەمنى بېرىپ بولسىمۇ، بۇ كۈندىكى ئازابتىن قۇتۇلۇشنى ئارزۇ قىلىدۇ[10ـ14].');
INSERT INTO "saleh" VALUES(70,15,'70:15','ياق، ئۇ مۇقەررەر دوزاختۇر.');
INSERT INTO "saleh" VALUES(70,16,'70:16','(يەنى بۇ گۇناھكار كاپىر مۇنداق ئارزۇدىن يانسۇن، پىدا قىلىنغان ھېچقانداق نەرسە ئۇنى ئازابتىن قۇتقۇزالمايدۇ، ئۇنىڭ ئالدىدا دوزاخ يېنىپ تۇرىدۇ). (ئۇ ھارارىتىنىڭ قاتتىقلىقىدىن) باشنىڭ تېرىسىنى شىلىدۇ.');
INSERT INTO "saleh" VALUES(70,17,'70:17',' ئۇ (ھەقتىن) يۈز ئۆرۈپ بۇرالغان ۋە پۇل ـ مال يىغىپ ساقلىغان (ئۇنىڭدىن ئاللاھنىڭ ۋە مىسكىنلەرنىڭ ھەققىنى ئادا قىلمىغان) لارنى چاقىرىدۇ[17ـ18].');
INSERT INTO "saleh" VALUES(70,18,'70:18',' ئۇ (ھەقتىن) يۈز ئۆرۈپ بۇرالغان ۋە پۇل ـ مال يىغىپ ساقلىغان (ئۇنىڭدىن ئاللاھنىڭ ۋە مىسكىنلەرنىڭ ھەققىنى ئادا قىلمىغان) لارنى چاقىرىدۇ[17ـ18].');
INSERT INTO "saleh" VALUES(70,19,'70:19','ئىنسان ھەقىقەتەن چىدامسىز يارىتىلدى.');
INSERT INTO "saleh" VALUES(70,20,'70:20',' ئۇنىڭغا (يوقسۇزلۇق، ياكى كېسەللىك، يا قورقۇنچتەك) بىرەر كۆڭۈلسىزلىك يەتكەن چاغدا، زارلانغۇچىدۇر.');
INSERT INTO "saleh" VALUES(70,21,'70:21',' بىرەر ياخشىلىق يەتكەن چاغدا، بېخىللىق قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(70,22,'70:22','پەقەت (تۆۋەندىكىلەر بۇنىڭدىن) مۇستەسنا: ئۇلار نامازغا ھەمىشە رىئايە قىلغۇچىلاردۇر[22ـ23].');
INSERT INTO "saleh" VALUES(70,23,'70:23','پەقەت (تۆۋەندىكىلەر بۇنىڭدىن) مۇستەسنا: ئۇلار نامازغا ھەمىشە رىئايە قىلغۇچىلاردۇر[22ـ23].');
INSERT INTO "saleh" VALUES(70,24,'70:24','ئۇلار ماللىرىدىن تىلەيدىغان پېقىرغا ۋە تىلىمەيدىغان پېقىرغا مۇئەييەن ھەق (يەنى زاكات) بېرىدۇ[24ـ25].');
INSERT INTO "saleh" VALUES(70,25,'70:25','ئۇلار ماللىرىدىن تىلەيدىغان پېقىرغا ۋە تىلىمەيدىغان پېقىرغا مۇئەييەن ھەق (يەنى زاكات) بېرىدۇ[24ـ25].');
INSERT INTO "saleh" VALUES(70,26,'70:26',' ئۇلار قىيامەت كۈنىگە تەستىق قىلىدىغانلاردۇر.');
INSERT INTO "saleh" VALUES(70,27,'70:27',' ئۇلار پەرۋەردىگارىنىڭ ئازابىدىن قورققۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,28,'70:28',' شۈبھىسىزكى، ئۇلارنىڭ پەرۋەردىگارىنىڭ ئازابى (ئاللاھ ئەمىن قىلغاندىن باشقا ھېچبىر ئادەم ئۈچۈن) ئەمىن بولغىلى بولمايدىغان نەرسىدۇر.');
INSERT INTO "saleh" VALUES(70,29,'70:29',' ئۇلار ئەۋرەتلىرىنى ھارامدىن ساقلىغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,30,'70:30','پەقەت خوتۇنلىرى ۋە چۆرىلىرى بۇنىڭدىن مۇستەسنا، (ئۆزلىرىنىڭ خوتۇنلىرى ۋە چۆرىلىرى بىلەن يېقىنچىلىق قىلغانلار) مالامەت قىلىنمايدۇ،');
INSERT INTO "saleh" VALUES(70,31,'70:31','(جىنسىي تەلىپىنى قاندۇرۇشنى) بۇنىڭ سىرتىدىن تىلىگۈچىلەر ھەددىدىن ئاشقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,32,'70:32','ئۇلار ئۆزلىرىگە تاپشۇرۇلغان ئامانەتلەرگە ۋە بەرگەن ئەھدىگە رىئايە قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,33,'70:33','ئۇلار توغرا گۇۋاھلىق بەرگۈچىلەردۇر.');
INSERT INTO "saleh" VALUES(70,34,'70:34',' ئۇلار نامىزىنى ئادا قىلغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,35,'70:35',' ئەنە شۇلار جەننەتلەردە ھۆرمەت قىلىنغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(70,36,'70:36',' (ئى مۇھەممەد) كاپىرلارغا نېمە بولدىكىن، ئۇلار سەن تەرەپكە تېز كېلىشىپ، ئوڭ ـ سول تەرىپىڭدە توپ ـ توپ بولۇپ ئولتۇرۇشىدۇ[36ـ37].');
INSERT INTO "saleh" VALUES(70,37,'70:37',' (ئى مۇھەممەد) كاپىرلارغا نېمە بولدىكىن، ئۇلار سەن تەرەپكە تېز كېلىشىپ، ئوڭ ـ سول تەرىپىڭدە توپ ـ توپ بولۇپ ئولتۇرۇشىدۇ[36ـ37].');
INSERT INTO "saleh" VALUES(70,38,'70:38','ئۇلاردىن ھەر بىر ئادەم نازۇنېمەتلىك جەننەتكە كىرگۈزۈلۈشنى تەمە قىلامدۇ؟');
INSERT INTO "saleh" VALUES(70,39,'70:39','ياق (يەنى ئۇلار جەننەتكە مەڭگۈ كىرەلمەيدۇ). شۈبھىسىزكى، بىز ئۇلارنى ئۇلار بىلىدىغان نەرسىلەردىن ياراتتۇق.');
INSERT INTO "saleh" VALUES(70,40,'70:40',' مەشرىقلەرنىڭ ۋە مەغرىبلەرنىڭ پەرۋەردىگارى بىلەن قەسەمكى، ئۇلارنى ئۇلاردىن ياخشىراق ئادەملەر بىلەن ئالماشتۇرۇشقا (يەنى ئۇلارنى ھالاك قىلىپ ئورنىغا ئۇلاردىن ئارتۇق ۋە ئاللاھقا ئىتائەتچان بىر قەۋمنى كەلتۈرۈشكە) بىز ئەلۋەتتە قادىرمىز. بىز (بۇنىڭدىن) ئاجىز ئەمەسمىز[40ـ41].');
INSERT INTO "saleh" VALUES(70,41,'70:41',' مەشرىقلەرنىڭ ۋە مەغرىبلەرنىڭ پەرۋەردىگارى بىلەن قەسەمكى، ئۇلارنى ئۇلاردىن ياخشىراق ئادەملەر بىلەن ئالماشتۇرۇشقا (يەنى ئۇلارنى ھالاك قىلىپ ئورنىغا ئۇلاردىن ئارتۇق ۋە ئاللاھقا ئىتائەتچان بىر قەۋمنى كەلتۈرۈشكە) بىز ئەلۋەتتە قادىرمىز. بىز (بۇنىڭدىن) ئاجىز ئەمەسمىز[40ـ41].');
INSERT INTO "saleh" VALUES(70,42,'70:42',' (ئى مۇھەممەد) ئۇلارنى قويۇۋەتكىن، تاكى ئۇلار ۋەدە قىلىنغان كۈنگە مۇلاقات بولغانغا قەدەر (باتىلغا) چۆمسۇن، (دۇنياسىدا) ئوينىسۇن.');
INSERT INTO "saleh" VALUES(70,43,'70:43','ئۇ كۈندە ئۇلار قەبرىلىرىدىن چىقىپ، خۇددى تىكلەپ قويغان بۇتلىرىغا قاراپ يۈگۈرگەندەك (مەھشەرگاھقا) يۈگۈرىدۇ.');
INSERT INTO "saleh" VALUES(70,44,'70:44',' ئۇلار (ئاللاھتىن ئۇيىلىپ) يۇقىرى قارىيالمايدۇ، ئۇلارنى خورلۇق ئورىۋالىدۇ، ئەنە شۇ ئۇلارغا ۋەدە قىلىنغان كۈندۇر.');
INSERT INTO "saleh" VALUES(71,1,'71:1','بىز ھەقىقەتەن نۇھنى قەۋمىگە: «قەۋمىڭگە قاتتىق ئازاب (يەنى دۇنيادىكى چاغلىرىدا توپان بالاسى، ئاخىرەتتە دوزاخ ئازابى) كېلىشتىن بۇرۇن ئۇلارنى ئاگاھلاندۇرغىن» دەپ ئەۋەتتۇق.');
INSERT INTO "saleh" VALUES(71,2,'71:2',' نۇھ ئېيتتى: «ئى قەۋمىم! مەن ھەقىقەتەن سىلەرگە (ئەۋەتىلگەن) ئاشكارا ئاگاھلاندۇرغۇچىمەن.');
INSERT INTO "saleh" VALUES(71,3,'71:3',' ئاللاھقا ئىبادەت قىلىڭلار، ئۇنىڭدىن قورقۇڭلار ۋە ئاللاھقا ئىتائەت قىلىڭلار.');
INSERT INTO "saleh" VALUES(71,4,'71:4',' ئۇ بەزى گۇناھىڭلارنى مەغپىرەت قىلىدۇ، سىلەرنى مۇئەييەن مۇددەتكىچە كېچىكتۈرىدۇ (يەنى ئۆمرۈڭلارنى ئۇزۇن قىلىدۇ). شۈبھىسىزكى، ئەگەر بىلسەڭلار، ئاللاھنىڭ (ئىنسانغا بېكىتكەن) ئەجىلى كەلگەن چاغدا (ئەجەل) كېچىكتۈرۈلمەيدۇ».');
INSERT INTO "saleh" VALUES(71,5,'71:5','نۇھ ئېيتتى: «پەرۋەردىگارىم! مەن ھەقىقەتەن قەۋمىمنى كېچە ـ كۈندۈز (ئىمانغا) دەۋەت قىلدىم.');
INSERT INTO "saleh" VALUES(71,6,'71:6','مېنىڭ دەۋىتىم ئۇلارنى تېخىمۇ قاچۇرۇۋەتتى.');
INSERT INTO "saleh" VALUES(71,7,'71:7','مەن ئۇلارنى سېنىڭ مەغپىرىتىڭگە ئېرىشسۇن دەپ، ھەر قاچان دەۋەت قىلسام، (دەۋىتىمنى ئاڭلىماسلىق ئۈچۈن) بارماقلىرىنى قۇلاقلىرىغا تىقىۋالدى. (سۆزۈمنى ئاڭلىماسلىق ياكى مېنى كۆرمەسلىك ئۈچۈن) كىيىملىرى بىلەن باشلىرىنى چۈمكىۋالدى. (كۇفرىدا) چىڭ تۇردى، چوڭچىلىق قىلىپ (ئىماندىن) تېخىمۇ باش تارتتى.');
INSERT INTO "saleh" VALUES(71,8,'71:8','ئاندىن مەن ئۇلارنى توۋلاپ دەۋەت قىلدىم.');
INSERT INTO "saleh" VALUES(71,9,'71:9',' ئاندىن ئۇلارنى ئاشكارا دەۋەت قىلدىم ۋە يوشۇرۇن دەۋەت قىلدىم.');
INSERT INTO "saleh" VALUES(71,10,'71:10',' (ئۇلارغا) ئېيتتىم: پەرۋەردىگارىڭلاردىن مەغپىرەت تىلەڭلار، ئۇ ھەقىقەتەن مەغپىرەت قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(71,11,'71:11',' ئۇ سىلەرگە مول يامغۇر ياغدۇرۇپ بېرىدۇ،');
INSERT INTO "saleh" VALUES(71,12,'71:12','سىلەرنىڭ ماللىرىڭلارنى ۋە ئوغۇللىرىڭلارنى كۆپەيتىپ بېرىدۇ. سىلەرگە باغلارنى، ئۆستەڭلارنى ئاتا قىلىدۇ، ');
INSERT INTO "saleh" VALUES(71,13,'71:13',' نېمە ئۈچۈن سىلەر ئاللاھنىڭ بۈيۈكلۈكىدىن قورقمايسىلەر؟ ');
INSERT INTO "saleh" VALUES(71,14,'71:14',' ھالبۇكى ئاللاھ سىلەرنى بىر قانچە باسقۇچلارغا بۆلۈپ ياراتتى،');
INSERT INTO "saleh" VALUES(71,15,'71:15','سىلەر كۆرمىدىڭلارمىكىن، ئاللاھ قانداق قىلىپ يەتتە ئاسماننى بىرىنىڭ ئۈستىدە بىرىنى قىلىپ ياراتتى،');
INSERT INTO "saleh" VALUES(71,16,'71:16',' ئاينى ئۇلاردا (يەنى دۇنيانىڭ ئاسمىنىدا) نۇر قىلدى، قۇياشنى (نۇر چاچقۇچى) چىراغ قىلدى.');
INSERT INTO "saleh" VALUES(71,17,'71:17','ئاللاھ سىلەرنى زېمىندىن ياراتتى (چۈنكى ئاتاڭلار ئادەمنى تۇپراقتىن ياراتقان ئىدى).');
INSERT INTO "saleh" VALUES(71,18,'71:18',' ئاندىن ئاللاھ سىلەرنى (ئۆلگەندىن كېيىن) زېمىنغا قايتۇرىدۇ (يەنى زېمىندا دەپنە قىلىنىسىلەر)، ئاندىن سىلەرنى (قىيامەت كۈنى) زېمىندىن چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(71,19,'71:19',' زېمىندا كەڭ يوللاردا مېڭىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە زېمىننى يېيىپ بەردى»[19ـ20].');
INSERT INTO "saleh" VALUES(71,20,'71:20',' زېمىندا كەڭ يوللاردا مېڭىشىڭلار ئۈچۈن، ئاللاھ سىلەرگە زېمىننى يېيىپ بەردى»[19ـ20].');
INSERT INTO "saleh" VALUES(71,21,'71:21','نۇھ ئېيتتى: «پەرۋەردىگارىم! ئۇلار ھەقىقەتەن ماڭا ئاسىيلىق قىلدى، مېلىغا، بالىسىغا ۋە ئۆزلىرىگە زىياندىن باشقىنى زىيادە قىلمىغان كىشىلەرگە (يەنى بايلىرىغا، كاتتىباشلىرىغا) ئەگەشتى.');
INSERT INTO "saleh" VALUES(71,22,'71:22',' ئۇلار (يەنى كاتتىباشلار) (ماڭا چىنپۈتمىدى، ماڭا ۋە ماڭا ئەگەشكەنلەرگە ئەزىيەت يەتكۈزۈپ) چوڭ ھىيلە تۈزدى.');
INSERT INTO "saleh" VALUES(71,23,'71:23','ئۇلار: ‹مەبۇدلىرىڭلارنى ھەرگىز تەرك ئەتمەڭلار، ۋەدنى، سۇۋائنى، يەغۇسنى، يەئۇقنى ۋە نەسرىنى ھەرگىز تەرك ئەتمەڭلار› دېدى.');
INSERT INTO "saleh" VALUES(71,24,'71:24','ئۇلار ھەقىقەتەن نۇرغۇن كىشىلەرنى ئازدۇردى، زالىملارنى تېخىمۇ گۇمراھ قىلغىن».');
INSERT INTO "saleh" VALUES(71,25,'71:25',' ئۇلار گۇناھلىرى تۈپەيلىدىن (توپان بىلەن) غەرق قىلىندى، دوزاخقا كىرگۈزۈلدى، ئۇلار ئۆزلىرى ئۈچۈن ئاللاھتىن باشقا ھېچقانداق ياردەمچى تاپالمىدى.');
INSERT INTO "saleh" VALUES(71,26,'71:26','نۇھ ئېيتتى: «پەرۋەردىگارىم يەر يۈزىدە كاپىرلارنىڭ بىرىنىمۇ قويمىغىن.');
INSERT INTO "saleh" VALUES(71,27,'71:27','ئەگەر ئۇلارنى قويساڭ، شۈبھىسىزكى، بەندىلىرىڭنى ئازدۇرىدۇ، ئۇلارنىڭ تاپقان بالىلىرىمۇ پەقەت فاجىر، كاپىر بولىدۇ.');
INSERT INTO "saleh" VALUES(71,28,'71:28','پەرۋەردىگارىم! ماڭا، ئاتا ـ ئانامغا، مېنىڭ ئۆيۈمگە مۆمىن بولۇپ كىرگەن كىشىگە ۋە مۆمىن ئەرلەرگە، مۆمىن ئاياللارغا مەغپىرەت قىلغىن، زالىملارغا پەقەت ھالاكەتنى زىيادە قىلغىن». ');
INSERT INTO "saleh" VALUES(72,1,'72:1','(ئى مۇھەممەد! قەۋمىڭگە) ئېيتقىنكى، «بىر قانچە نەپەر جىننىڭ (مەندىن قۇرئان) تىڭشاپ (ئىمان ئېيتقانلىقى) ماڭا ۋەھىي قىلىندى. ئۇلار (قەۋمىگە قايتىپ بارغاندىن كېيىن) ئېيتتى: بىز توغرا يولغا باشلايدىغان ئاجايىپ قۇرئاننى ھەقىقەتەن ئاڭلىدۇق.');
INSERT INTO "saleh" VALUES(72,2,'72:2','ئۇنىڭغا ئىمان ئېيتتۇق، پەرۋەردىگارىمىزغا ھېچكىمنى شېرىك كەلتۈرمەيمىز.');
INSERT INTO "saleh" VALUES(72,3,'72:3',' پەرۋەردىگارىمىزنىڭ ئەزىمىتى كاتتىدۇر، خوتۇنى ۋە بالىسى يوقتۇر.');
INSERT INTO "saleh" VALUES(72,4,'72:4',' بىزنىڭ ئارىمىزدىكى ئەخمەق (يەنى ئىبلىس) ئاللاھقا ۋە ئۇنىڭ كاتتىلىقىغا نالايىق سۆزلەرنى قىلدى.');
INSERT INTO "saleh" VALUES(72,5,'72:5',' بىز، ئىنسانلار ۋە جىنلار ئاللاھقا يالغاننى چاپلىمايدۇ، دەپ ئويلىغان ئىدۇق.');
INSERT INTO "saleh" VALUES(72,6,'72:6',' ئىنسانلاردىن بەزى كىشىلەر جىنلاردىن بولغان بەزى كىشىلەرگە سىغىناتتى، شۇنىڭ بىلەن جىنلار تېخىمۇ تەكەببۇرلىشىپ كەتتى.');
INSERT INTO "saleh" VALUES(72,7,'72:7',' ئۇلار (يەنى ئىنساننىڭ كۇففارلىرى)، سىلەرگە ئوخشاش، ئاللاھ ھېچ ئادەمنى (ئۆلگەندىن كېيىن) تىرىلدۈرمەيدۇ، دەپ گۇمان قىلدى». ');
INSERT INTO "saleh" VALUES(72,8,'72:8',' (جىنلار ئېيتتى) «بىز (ئاسمان ئەھلىنىڭ سۆزلىرىنى تىڭشاش ئۈچۈن) ئاسمانغا (چىقىشنى) تىلىدۇق، ئاسماننىڭ كۈچلۈك مۇھاپىزەتچىلەر (يەنى پەرىشتىلەر) بىلەن ۋە (كۆيدۈرگۈچى) يۇلتۇزلار بىلەن تولۇپ كەتكەنلىكىنى بايقىدۇق.');
INSERT INTO "saleh" VALUES(72,9,'72:9',' بىز ھەمىشە (ئوغرىلىقچە تىڭشاش ئۈچۈن) ئاسماننىڭ ئولتۇرۇشقا بولىدىغان جايلىرىدا ئولتۇراتتۇق، كىمكى ئەمدى (ئوغرىلىقچە) ئاڭلاشقا ئورۇنىدىكەن، ئۇنى كۆزىتىپ تۇرغان كۆيدۈرگۈچى يۇلتۇزلارنى بايقايدۇ.');
INSERT INTO "saleh" VALUES(72,10,'72:10',' بىز (جىنلار جامائەسى) زېمىندىكىلەرگە يامانلىق ئىرادە قىلىندىمۇ ياكى پەرۋەردىگارى ئۇلارغا ياخشىلىق ئىرادە قىلدىمۇ؟ بىلمەيمىز.');
INSERT INTO "saleh" VALUES(72,11,'72:11','بىزنىڭ ئىچىمىزدە ياخشىلارمۇ ۋە ئۇنىڭدىن تۆۋەن تۇرىدىغانلارمۇ بار، بىز تۈرلۈك يوللارغا بۆلۈندۇق.');
INSERT INTO "saleh" VALUES(72,12,'72:12',' بىز جەزمەن ئىشەندۇقكى، ئاللاھنى (ئۆز ئىرادىسىنى ئىشقا ئاشۇرۇشتىن) ھەرگىز ئاجىز قىلالمايمىز، ئۇنىڭ (ئازابى) دىنمۇ قېچىپ قۇتۇلالمايمىز. ');
INSERT INTO "saleh" VALUES(72,13,'72:13','بىز ھىدايەتنى (يەنى قۇرئاننى) ئاڭلىغان چېغىمىزدا ئۇنىڭغا ئىمان كەلتۈردۇق، كىمكى پەرۋەردىگارىغا ئىمان ئېيتىدىكەن، (ياخشىلىقلىرىنىڭ) كېمىيىپ كېتىشىدىن ۋە (يامانلىقلىرىنىڭ ئارتتۇرۇلۇپ) زۇلۇم قىلىنىشتىن قورقمايدۇ.');
INSERT INTO "saleh" VALUES(72,14,'72:14',' بىزدىن مۇسۇلمانلار ۋە ھەق يولدىن چىققۇچىلارمۇ بار، مۇسۇلمان بولغانلار توغرا يولنى ئىزدىگەن بولىدۇ.');
INSERT INTO "saleh" VALUES(72,15,'72:15','توغرا يولدىن چىققۇچىلار جەھەننەمگە ئوتۇن بولىدۇ».');
INSERT INTO "saleh" VALUES(72,16,'72:16','ئەگەر ئۇلار (يەنى كۇففارلار) توغرا يولدا بولسا ئىدى، ئۇلارنى سىناش ئۈچۈن مول يامغۇر ياغدۇرۇپ بېرەتتۇق (يەنى رىزقىنى كەڭ قىلىپ بېرەتتۇق)، كىمكى پەرۋەردىگارىنىڭ زىكرىدىن (يەنى ئاللاھنىڭ تائەت ـ ئىبادىتىدىن) يۈز ئۆرۈيدىكەن، ئاللاھ ئۇنى قاتتىق ئازابقا دۇچار قىلىدۇ[16ـ17].');
INSERT INTO "saleh" VALUES(72,17,'72:17','ئەگەر ئۇلار (يەنى كۇففارلار) توغرا يولدا بولسا ئىدى، ئۇلارنى سىناش ئۈچۈن مول يامغۇر ياغدۇرۇپ بېرەتتۇق (يەنى رىزقىنى كەڭ قىلىپ بېرەتتۇق)، كىمكى پەرۋەردىگارىنىڭ زىكرىدىن (يەنى ئاللاھنىڭ تائەت ـ ئىبادىتىدىن) يۈز ئۆرۈيدىكەن، ئاللاھ ئۇنى قاتتىق ئازابقا دۇچار قىلىدۇ[16ـ17].');
INSERT INTO "saleh" VALUES(72,18,'72:18',' مەسجىدلەر ئاللاھقا خاستۇر، ئاللاھقا قوشۇپ ھېچكىمگە ئىبادەت قىلماڭلار.');
INSERT INTO "saleh" VALUES(72,19,'72:19',' ئاللاھنىڭ بەندىسى (يەنى مۇھەممەد ئەلەيھىسسالام) ئاللاھقا ئىبادەت قىلىشقا تۇرغاندا، (قۇرئان تىڭشاش ئۈچۈن) جىنلار (ئولىشىپ) ئۇنى بېسىۋىلىشقا تاس قالىدۇ.');
INSERT INTO "saleh" VALUES(72,20,'72:20','(ئى مۇھەممەد! دىنىڭدىن يېنىپ كېتىشىڭنى تەلەپ قىلغان بۇ كۇففارلارغا) ئېيتقىنكى، «پەرۋەردىگارىمغا ئىبادەت قىلىمەن، ئۇنىڭغا ھېچ ئەھەدىنى شېرىك كەلتۈرمەيمەن».');
INSERT INTO "saleh" VALUES(72,21,'72:21','ئېيتقىنكى، «مەن سىلەرگە پايدا ـ زىيان يەتكۈزۈشكە قادىر بولالمايمەن (بۇنىڭغا پەقەت ئاللاھ قادىر بولالايدۇ)».');
INSERT INTO "saleh" VALUES(72,22,'72:22',' ئېيتقىنكى، «(ئەگەر مەن ئاللاھقا ئاسىي بولسام) مېنى ئاللاھنىڭ ئازابىدىن ھېچ كىشى قۇتقۇزالمايدۇ، ئاللاھتىن باشقا ھېچ پاناھگاھمۇ تاپالمايمەن (شۇنداق تۇرۇقلۇق، مەن سىلەرنىڭ تەلىپىڭلارنى قانداق قوبۇل قىلالايمەن؟).');
INSERT INTO "saleh" VALUES(72,23,'72:23','مەن پەقەت (سىلەرگە) ئاللاھنىڭ ئەلچىلىكىنى يەتكۈزۈشكە قادىرمەن، كىمكى ئاللاھقا ۋە ئۇنىڭ پەيغەمبىرىگە ئاسىيلىق قىلىدىكەن (يەنى كىمكى ئاللاھنى ۋە ئاللاھنىڭ پەيغەمبىرىنى ئىنكار قىلىدىكەن، ئاللاھقا مۇلاقات بولۇشقا ئىشەنمەيدىكەن، ئاللاھنىڭ ئايەتلىرىگە قۇلاق سېلىشتىن يۈز ئۆرۈيدىكەن)، ئۇ دوزاخ ئازابىغا دۇچار بولىدۇ، دوزاختا مەڭگۈ قالىدۇ.');
INSERT INTO "saleh" VALUES(72,24,'72:24','ئۇلار (يەنى مۇشرىكلار) ئاگاھلاندۇرۇلغان ئازابنى كۆرگەن چاغدا كىمنىڭ ياردەمچىسى ئاجىز ۋە سانى ئاز ئىكەنلىكىنى بىلىدۇ». ');
INSERT INTO "saleh" VALUES(72,25,'72:25','(ئى مۇھەممەد! ئۇلارغا) ئېيتقىنكى، «سىلەرگە ئاگاھلاندۇرۇلغان ئازاب يېقىنمۇ ياكى پەرۋەردىگارىم ئۇنىڭغا (ئۇزاق) ۋاقىت تەيىن قىلامدۇ؟ ئۇقمايمەن.');
INSERT INTO "saleh" VALUES(72,26,'72:26',' ئاللاھ غەيبنى بىلگۈچىدۇر، ئۆزىنىڭ غەيبىدىن ھېچبىر ئادەمنى خەۋەردار قىلمايدۇ.');
INSERT INTO "saleh" VALUES(72,27,'72:27',' پەقەت ئاللاھ ئۆزى مەمنۇن بولغان پەيغەمبىرىگىلا (بەزى غەيبنى بىلدۈرىدۇ)، ئۇنىڭ ئالدىغا ۋە ئارقىسىغا كۆزەتچى (پەرىشتە) سېلىپ قويىدۇ.');
INSERT INTO "saleh" VALUES(72,28,'72:28','(بۇ ئاللاھنىڭ) ئۇنىڭ (يەنى پەيغەمبەرنىڭ) ئاللاھنىڭ ئەلچىلىكىنى يەتكۈزگەنلىكىنى بىلىپ تۇرۇشى ئۈچۈندۇر. ئاللاھ ئۇلارنىڭ سۆز ـ ھەرىكەتلىرىنى تولۇق بىلگۈچىدۇر، ھەر شەيئىنىڭ سانىنى تولۇق ئىگەللىگۈچىدۇر».');
INSERT INTO "saleh" VALUES(73,1,'73:1','ئى كىيىمگە يۆگىنىۋالغۇچى (پەيغەمبەر!) ');
INSERT INTO "saleh" VALUES(73,2,'73:2','كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە ـ دانە، ئوچۇق) ئوقۇغىن[2ـ4].');
INSERT INTO "saleh" VALUES(73,3,'73:3','كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە ـ دانە، ئوچۇق) ئوقۇغىن[2ـ4].');
INSERT INTO "saleh" VALUES(73,4,'73:4','كېچىنىڭ ئازغىنىسىدىن باشقىسىدا، يېرىمىدا ياكى يېرىمىدىنمۇ ئازراقىدا ياكى يېرىمىدىن كۆپرەكىدە ناماز ئوقۇغىن، قۇرئاننى تەرتىل بىلەن (يەنى دانە ـ دانە، ئوچۇق) ئوقۇغىن[2ـ4].');
INSERT INTO "saleh" VALUES(73,5,'73:5',' ساڭا ھەقىقەتەن ۋەزنىلىك قۇرئاننى نازىل قىلىمىز.');
INSERT INTO "saleh" VALUES(73,6,'73:6',' شۈبھىسىزكى، كېچىنىڭ قىيامى (يەنى كېچىدە كىشىنىڭ ئىبادەت قىلىشى ئۈچۈن ئۇيقۇدىن ئويغىنىپ تۇرۇشى) ئەڭ مۇۋاپىقتۇر، (تىنچ بولغانلىقى ئۈچۈن) كېچىنىڭ قىرائىتى ئەڭ توغرا بولىدۇ.');
INSERT INTO "saleh" VALUES(73,7,'73:7','ھەقىقەتەن كۈندۈزى ئىش بىلەن ئالدىراش بولىسەن، (شۇڭا ئىبادەتكە) كېچىسى بېرىلگىن.');
INSERT INTO "saleh" VALUES(73,8,'73:8','پەرۋەردىگارىڭنىڭ نامىنى ياد ئەتكىن، (ئىبادىتىڭدە) ئۇنىڭغا تولۇق يۈزلەنگىن.');
INSERT INTO "saleh" VALUES(73,9,'73:9','ئۇ مەشرىقنىڭ ۋە مەغرىبنىڭ پەرۋەردىگارىدۇر، ئۇنىڭدىن باشقا ھېچ ئىلاھ يوقتۇر، ئۇنى ھامىي قىلىۋالغىن (يەنى ھەممە ئىشىڭنى ئۇنىڭغا تاپشۇرغىن).');
INSERT INTO "saleh" VALUES(73,10,'73:10',' ئۇلارنىڭ سۆزلىرىگە سەۋر قىلغىن، ئۇلارنى چىرايلىقچە تەرك ئەتكىن.');
INSERT INTO "saleh" VALUES(73,11,'73:11',' (ئى مۇھەممەد! مېنىڭ ئايەتلىرىمنى) ئىنكار قىلغۇچى دۆلەتمەنلەرنى ماڭا قويۇۋەتكىن (يەنى ئۇلارنىڭ جازاسىنى ئۆزۈم بېرىمەن)، ئۇلارغا ئازغىنا مۆھلەت بەرگىن.');
INSERT INTO "saleh" VALUES(73,12,'73:12','شۈبھىسىزكى، بىزنىڭ دەرگاھىمىزدا (ئۇلارغا) ئېغىر ئىشكەللەر ۋە (كۆيدۈرگۈچى) دوزاخ بار.');
INSERT INTO "saleh" VALUES(73,13,'73:13',' (زەققۇم، زەرىدىن ئىبارەت) گالدىن ئۆتمەيدىغان تاماق ۋە قاتتىق ئازاب بار.');
INSERT INTO "saleh" VALUES(73,14,'73:14','ئۇ كۈندە زېمىن ۋە تاغلار قاتتىق تەۋرەيدۇ، تاغلار بىر دۆۋە كۆچمە قۇمغا ئايلىنىدۇ.');
INSERT INTO "saleh" VALUES(73,15,'73:15',' شۈبھىسىزكى، بىز پىرئەۋنگە بىر پەيغەمبەرنى ئەۋەتكەندەك، سىلەرگە (ئەمەلىڭلارغا) گۇۋاھلىق بەرگۈچى بىر پەيغەمبەرنى ئەۋەتتۇق. ');
INSERT INTO "saleh" VALUES(73,16,'73:16','پىرئەۋن پەيغەمبەرگە (يەنى مۇسا ئەلەيھىسسالامغا) ئاسىيلىق قىلدى (يەنى پىرئەۋن مۇسا ئەلەيھىسسالامغا ئىمان ئېيتماي، ئۇنى يالغانغا چىقاردى، ئى قۇرەيش جامائەسى! سىلەر مۇھەممەد ئەلەيھىسسالامغا ئاسىيلىق قىلغاندەك، پىرئەۋنمۇ مۇسا ئەلەيھىسسالامغا ئاسىيلىق قىلغان ئىدى)، شۇنىڭ بىلەن ئۇنى قاتتىق جازالىدۇق.');
INSERT INTO "saleh" VALUES(73,17,'73:17','ئەگەر ئىمان ئېيتمىساڭلار، (دەھشىتىدىن) بالىلارنىڭ چېچىنى ئاقارتىۋېتىدىغان كۈندىن قانداق ساقلىنىسىلەر؟ ');
INSERT INTO "saleh" VALUES(73,18,'73:18','ئۇ كۈننىڭ (دەھشىتىدىن) ئاسمان يېرىلىدۇ، ئاللاھنىڭ ۋەدىسى چوقۇم ئەمەلگە ئاشىدۇ (چۈنكى ئاللاھ ۋەدىسىگە خىلاپلىق قىلمايدۇ). ');
INSERT INTO "saleh" VALUES(73,19,'73:19',' بۇ ئايەتلەر ھەقىقەتەن ۋەز ـ نەسىھەتتۇر، (بۇ ۋەز ـ نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن.');
INSERT INTO "saleh" VALUES(73,20,'73:20','(ئى مۇھەممەد!) شۈبھىسىزكى، پەرۋەردىگارىڭ سېنىڭ ۋە سەن بىلەن بولغانلارنىڭ (يەنى ساھابىلەر) دىن بىر توپ ئادەمنىڭ (تەھەججۇد نامىزى ئۈچۈن) كېچىنىڭ ئۈچتىن ئىككى ھەسسىسىدە، يېرىمىدا ۋە ئۈچتىن بىر ھەسسىسىدە تۇرىدىغانلىقىڭلارنى بىلىدۇ. كېچە بىلەن كۈندۈزنىڭ (ئۇزۇنلۇقى) نى ئاللاھ ئالدىنئالا بەلگىلەيدۇ، سىلەرنىڭ ئۇنى ھېسابلاپ بولالمايدىغانلىقىڭلارنى ئاللاھ بىلىدۇ. ئاللاھ سىلەرگە رەھىم قىلدى قۇرئاندىن سىلەرگە قولاي بولغاننى ئوقۇڭلار. ئاللاھ بىلىدۇكى، بەزىڭلار كېسەل بولۇپ قالىسىلەر، بەزىلەر ئاللاھنىڭ پەزلىنى تىلەپ (يەنى تىجارەت قىلىپ) يەر يۈزىدە سەپەر قىلىدۇ، يەنى بەزىلەر ئاللاھنىڭ يولىدا جىھاد قىلىدۇ، شۇنىڭ ئۈچۈن ئۇنىڭدىن (يەنى تەھەججۇد نامىزىدىن) قولاي بولغاننى ئوقۇڭلار، (پەرز) نامازنى ئادا قىلىڭلار، زاكاتنى بېرىڭلار، ئاللاھقا قەرزى ھەسەنە بېرىڭلار (يەنى ئاللاھنىڭ رازىلىقى ئۈچۈن ياخشىلىق يوللىرىغا پۇل ـ مال سەرپ قىلىڭلار)، ئۆزۈڭلار ئۈچۈن (دۇنيادا) قايسىبىر ياخشى ئىشنى قىلساڭلار، ئاللاھنىڭ دەرگاھىدا تېخىمۇ ياخشى، تېخىمۇ كاتتا ساۋابقا ئېرىشىسىلەر (دۇنيا بولسا پانىيدۇر، ئاخىرەت بولسا باقىيدۇر. ياخشى بەندىلەر ئۈچۈن ئاللاھنىڭ دەرگاھىدىكى ساۋاب ھەممىدىن ئارتۇقتۇر). ئاللاھتىن مەغپىرەت تىلەڭلار، ئاللاھ ھەقىقەتەن ناھايىتى مەغپىرەت قىلغۇچىدۇر، ناھايىتى مېھرىباندۇر.');
INSERT INTO "saleh" VALUES(74,1,'74:1','ئى (ۋەھىي نازىل بولغان چاغدا) كىيىمگە چۈمكىلىۋالغۇچى (پەيغەمبەر!)');
INSERT INTO "saleh" VALUES(74,2,'74:2','تۇر! (ئىنسانلارنى) ئاللاھنىڭ ئازابىدىن ئاگاھلاندۇر.');
INSERT INTO "saleh" VALUES(74,3,'74:3','پەرۋەردىگارىڭنى ئۇلۇغلا.');
INSERT INTO "saleh" VALUES(74,4,'74:4',' كىيىمىڭنى پاك تۇت.');
INSERT INTO "saleh" VALUES(74,5,'74:5',' ئازابتىن (يەنى ئازابقا قالدۇرىدىغان ئىشلاردىن) يىراق بول.');
INSERT INTO "saleh" VALUES(74,6,'74:6','كۆپ ئېلىش نىيىتىدە بەرمە (يەنى بىر كىشىگە بىرەر سوۋغا بەرسەڭ، ئۇنىڭدىن بەرگەن نەرسەڭدىن كۆپرەك نەرسە ئېلىشنى تەمە قىلما)،');
INSERT INTO "saleh" VALUES(74,7,'74:7','پەرۋەردىگارىڭنىڭ (رازىلىقى) ئۈچۈن (قەۋمىڭدىن يەتكەن ئەزىيەتكە) سەۋر قىل.');
INSERT INTO "saleh" VALUES(74,8,'74:8',' سۇر چېلىنغان ۋاقىت ـ ئۇ كۈن قىيىن كۈندۇر[8ـ9].');
INSERT INTO "saleh" VALUES(74,9,'74:9',' سۇر چېلىنغان ۋاقىت ـ ئۇ كۈن قىيىن كۈندۇر[8ـ9].');
INSERT INTO "saleh" VALUES(74,10,'74:10',' كاپىرلارغا ئاسان ئەمەستۇر. ');
INSERT INTO "saleh" VALUES(74,11,'74:11','(ئى مۇھەممەد!) مەن تەنھا ياراتقان كىشىنى (يەنى ۋەلىد ئىبن مۇغىرەنى) ماڭا قويۇۋەت.');
INSERT INTO "saleh" VALUES(74,12,'74:12',' ئۇنىڭغا نۇرغۇن مال بەردىم.');
INSERT INTO "saleh" VALUES(74,13,'74:13','ئۇنىڭغا كۆز ئالدىدا تۇرىدىغان ئوغۇللارنى بەردىم.');
INSERT INTO "saleh" VALUES(74,14,'74:14',' ئۇنىڭ ئابرويىنى ئۆستۈردۈم.');
INSERT INTO "saleh" VALUES(74,15,'74:15',' ئاندىن ئۇ يەنە كۆپ بېرىشىمنى تەمە قىلىدۇ.');
INSERT INTO "saleh" VALUES(74,16,'74:16','ياق، (يەنى ئۇنىڭ خالىغىنى بولمايدۇ)، چۈنكى ئۇ بىزنىڭ ئايەتلىرىمىزگە قارشى چىقتى.');
INSERT INTO "saleh" VALUES(74,17,'74:17','ئۇنىڭغا قاتتىق ئازابىمنى تېتىتىمەن.');
INSERT INTO "saleh" VALUES(74,18,'74:18',' ئۇ (پەيغەمبەر ۋە قۇرئان ھەققىدە) ئويلاندى، (قۇرئان توغرۇلۇق نېمە دېيىشنى ۋە قانداق بوھتان قىلىشنى) پىلانلىدى.');
INSERT INTO "saleh" VALUES(74,19,'74:19',' لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى؟ ');
INSERT INTO "saleh" VALUES(74,20,'74:20','يەنە لەنەت بولسۇنكى، ئۇ قانداق پىلانلىدى.');
INSERT INTO "saleh" VALUES(74,21,'74:21','ئاندىن ئۇ قارىدى،');
INSERT INTO "saleh" VALUES(74,22,'74:22',' ئاندىن قوشۇمىسىنى تۈردى ۋە چىرايىنى پۈرۈشتۈردى،');
INSERT INTO "saleh" VALUES(74,23,'74:23',' ئاندىن (ئىماندىن) يۈز ئۆرۈدى (پەيغەمبەرگە ئەگىشىشتىن) تەكەببۇرلۇق بىلەن باش تارتتى، ');
INSERT INTO "saleh" VALUES(74,24,'74:24','«بۇ (كالامۇللاھ ئەمەس) پەقەت ئۆگىنىلگەن سېھىردۇر، بۇ پەقەت ئىنسان سۆزىدۇر» دېدى[24ـ25].');
INSERT INTO "saleh" VALUES(74,25,'74:25','«بۇ (كالامۇللاھ ئەمەس) پەقەت ئۆگىنىلگەن سېھىردۇر، بۇ پەقەت ئىنسان سۆزىدۇر» دېدى[24ـ25].');
INSERT INTO "saleh" VALUES(74,26,'74:26','ئۇنى مەن سەقەرگە (يەنى دوزاخقا) سالىمەن.');
INSERT INTO "saleh" VALUES(74,27,'74:27','سەقەرنىڭ نېمىلىكىنى قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(74,28,'74:28','ئۇ ھېچ نەرسىنى قالدۇرمايدۇ، قويمايدۇ (بەلكى كۆيدۈرۈپ تاشلايدۇ).');
INSERT INTO "saleh" VALUES(74,29,'74:29','ئۇ (چوڭلۇقىدىن) ئىنسانلارنىڭ (كۆزلىرىگە يىراق مۇساپىلەردىن) كۆرۈنۈپ تۇرىدۇ. ');
INSERT INTO "saleh" VALUES(74,30,'74:30','ئۇنىڭغا 19 پەرىشتە مۇئەككەلدۇر.');
INSERT INTO "saleh" VALUES(74,31,'74:31',' بىز دوزاخ مۇئەككەللىرىنى پەقەت پەرىشتىلەردىن قىلدۇق، بىز ئۇلارنىڭ سانى بىلەن پەقەت كاپىرلارنى سىنىدۇق، تاكى ئەھلى كىتاب (ئۆزلىرىنىڭ ساماۋى كىتابلىرىدىكى بۇ ساننى كۆرۈپ قۇرئاننىڭ ھەق ئىكەنلىكىگە) ئىمان ئېيتماي، مۆمىنلەرنىڭ (پەيغەمبەر ئەلەيھىسسالامنىڭ بەرگەن خەۋىرىگە ئىشەنگەنلىكلىرىدىن ۋە ئەھلى كىتابنىڭ قۇرئان خەۋەرلىرىنىڭ تەۋرات، ئىنجىلدىكى خەۋەرلەرگە ئۇيغۇن ئىكەنلىكىنى ئېتىراپ قىلغانلىقىدىن) ئىمانى كۈچەيگەي! ئەھلى كىتاب ۋە مۆمىنلەر (بۇنىڭدىن) شەكلەنمەيدۇ، دىللىرىدا كېسەل بارلار (يەنى مۇناپىقلار)، كاپىرلار: «بۇ مىسالدىن ئاللاھ نېمىنى ئىرادە قىلىدۇ» دەيدۇ، ئاللاھ مۇشۇنداق خالىغان ئادەمنى گۇمراھ قىلىدۇ، خالىغان ئادەمنى ھىدايەت قىلىدۇ، پەرۋەردىگارىڭنىڭ قوشۇنىنى (يەنى پەرىشتىلەرنىڭ سانىنىڭ كۆپلۈكىنى، ئۇلارنىڭ كۈچ ـ قۇۋۋىتىنى ۋە ئۇلارنىڭ يارىتىلىشتىكى چوڭلۇقىنى) پەقەت ئۇ ئۆزى بىلىدۇ، بۇ، (يەنى ئاللاھ بايان قىلغان دوزاخ) پەقەت ئىنسانلار ئۈچۈن ۋەز ـ نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(74,32,'74:32','ياق (يەنى ئۇ مەسخىرە قىلغۇچىلار قەبىھ قىلمىشىدىن يانسۇن)، ئاي بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(74,33,'74:33','ئۆتۈپ كەتكەن كېچە بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(74,34,'74:34',' يورۇغان سۈبھى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(74,35,'74:35','شۈبھىسىزكى، دوزاخ بالالاردىن بىرىدۇر (ئۇنى قانداقمۇ مەسخىرە قىلىسىلەر ۋە ئۇنىڭغا چىنپۈتمەيسىلەر؟).');
INSERT INTO "saleh" VALUES(74,36,'74:36',' ئۇ ئىنسانلارنى ئاگاھلاندۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(74,37,'74:37',' سىلەردىن (جەننەتكە) ئىلگىرىلەشنى خالىغان ياكى (دوزاختىن) چېكىنىشنى خالىغان ئادەمنى ئاگاھلاندۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(74,38,'74:38',' ھەر ئىنسان قىلمىشلىرى تۈپەيلىدىن (دوزاختا) مەھبۇستۇر.');
INSERT INTO "saleh" VALUES(74,39,'74:39','پەقەت ئەسھابى يەمىن (يەنى سائادەتمەن مۆمىنلەر) بۇنىڭدىن مۇستەسنا.');
INSERT INTO "saleh" VALUES(74,40,'74:40',' ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ[40ـ42].');
INSERT INTO "saleh" VALUES(74,41,'74:41',' ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ[40ـ42].');
INSERT INTO "saleh" VALUES(74,42,'74:42',' ئۇلار جەننەتلەردە بولۇپ، گۇناھكارلاردىن: «سىلەرنى دوزاخقا كىرگۈزگەن نېمە؟» دەپ سورايدۇ[40ـ42].');
INSERT INTO "saleh" VALUES(74,43,'74:43',' ئۇلار (جاۋابەن) ئېيتىدۇ: «بىز ناماز ئوقۇمىدۇق.');
INSERT INTO "saleh" VALUES(74,44,'74:44','مىسكىنلەرگە تائام بەرمىدۇق.');
INSERT INTO "saleh" VALUES(74,45,'74:45','بىز بىھۇدە سۆز قىلغۇچىلار بىلەن بىللە بىھۇدە سۆز قىلاتتۇق.');
INSERT INTO "saleh" VALUES(74,46,'74:46',' قىيامەت كۈنىنى ئىنكار قىلاتتۇق،');
INSERT INTO "saleh" VALUES(74,47,'74:47',' تاكى بىز ئۆلگۈچە (شۇنداق قىلدۇق)».');
INSERT INTO "saleh" VALUES(74,48,'74:48','شاپائەت قىلغۇچىلارنىڭ شاپائىتى ئۇلارغا پايدا بەرمەيدۇ (يەنى ئۇلارنى ئاللاھنىڭ ئازابىدىن قۇتقۇزىدىغان شاپائەتچى بولمايدۇ، ئۇلارغا پۈتۈن ئەھلى زېمىن شاپائەت قىلغان تەقدىردىمۇ، ئۇلارنىڭ شاپائىتى قوبۇل قىلىنمايدۇ).');
INSERT INTO "saleh" VALUES(74,49,'74:49','ئۇلار نېمىشقا ۋەز (يەنى قۇرئان) دىن يۈز ئۆرۈيدۇ؟ ');
INSERT INTO "saleh" VALUES(74,50,'74:50','گويا ئۇلار يولۋاستىن قاچقان ياۋايى ئېشەكلەردۇر (يەنى بۇ مۇشرىكلار مۇھەممەد ئەلەيھىسسالامنى كۆرگەندە، ئۇنىڭدىن خۇددى يولۋاسنى كۆرۈپ قاچقان ياۋا ئېشەكلەردەك قاچىدۇ)[50ـ51].');
INSERT INTO "saleh" VALUES(74,51,'74:51','گويا ئۇلار يولۋاستىن قاچقان ياۋايى ئېشەكلەردۇر (يەنى بۇ مۇشرىكلار مۇھەممەد ئەلەيھىسسالامنى كۆرگەندە، ئۇنىڭدىن خۇددى يولۋاسنى كۆرۈپ قاچقان ياۋا ئېشەكلەردەك قاچىدۇ)[50ـ51].');
INSERT INTO "saleh" VALUES(74,52,'74:52',' بەلكى ئۇلاردىن ھەر كىشى ئۆزىگە ئوچۇق سەھىپىلەر بېرىلىشىنى خالايدۇ (يەنى ئۇلار ئاللاھنىڭ مۇھەممەد ئەلەيھىسسالامغا نازىل قىلىنغان كىتابىدەك كىتاب نازىل قىلىنىشىنى تەمە قىلىدۇ).');
INSERT INTO "saleh" VALUES(74,53,'74:53','ياق (يەنى ئۇلار مۇنداق تەمەدىن يانسۇن). بەلكى ئۇلار (ئاخىرەتتىن) قورقمايدۇ.');
INSERT INTO "saleh" VALUES(74,54,'74:54','ياق، مۇقەررەركى، ئۇ (يەنى قۇرئان) ۋەز ـ نەسىھەتتۇر (ئۇلار ئۆزلىرىنىڭ بەختىيار بولۇشىنى كۆزلەيدىغان بولسا، قۇرئان ئۇلارنىڭ ۋەز ـ نەسىھەت ئېلىشىغا يېتەرلىكتۇر).');
INSERT INTO "saleh" VALUES(74,55,'74:55','خالىغان ئادەم ئۇنىڭدىن ۋەز ـ نەسىھەت ئالىدۇ.');
INSERT INTO "saleh" VALUES(74,56,'74:56',' ئۇلار پەقەت خالىسا، ئاندىن ۋەز ـ نەسىھەت ئالىدۇ، ئاللاھ (بەندىلىرى) قورقۇشقا تېگىشلىك زاتتۇر، (بەندىلىرىنى) مەغپىرەت قىلىشقا لايىق زاتتۇر.');
INSERT INTO "saleh" VALUES(75,1,'75:1','قىيامەت كۈنى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(75,2,'75:2','ئۆزىنى مالامەت قىلغۇچى نەپسى بىلەن قەسەمكى، (سىلەر چوقۇم تىرىلدۈرۈلىسىلەر، سىلەردىن چوقۇم ھېساب ئېلىنىدۇ).');
INSERT INTO "saleh" VALUES(75,3,'75:3',' ئىنسان بىزنى ئۆزىنىڭ (چىرىپ تارقىلىپ كەتكەن) سۆڭەكلىرىنى جەمئى قىلالمايدۇ دەپ گۇمان قىلامدۇ؟');
INSERT INTO "saleh" VALUES(75,4,'75:4','ئۇنداق ئەمەس (يەنى ئۇنىڭ سۆڭەكلىرىنى جەمئى قىلىمىز)، ئۇنىڭ (ئەڭ ئىنچىكە، ئەڭ نازۇك بولغان) بارماقلىرىنى ئەسلىگە كەلتۈرەلەيمىز.');
INSERT INTO "saleh" VALUES(75,5,'75:5','بەلكى ئىنسان كەلگۈسى ھاياتىدا گۇناھتا ئەزۋەيلىمەكچى بولىدۇ.');
INSERT INTO "saleh" VALUES(75,6,'75:6','ئۇ (مەسخىرە قىلىش يۈزىسىدىن): «قىيامەت كۈنى قاچان بولىدۇ؟» دەپ سورايدۇ.');
INSERT INTO "saleh" VALUES(75,7,'75:7','ئۇ (مەسخىرە قىلىش يۈزىسىدىن): «قىيامەت كۈنى قاچان بولىدۇ؟» دەپ سورايدۇ.');
INSERT INTO "saleh" VALUES(75,8,'75:8','كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ[7ـ10]. ');
INSERT INTO "saleh" VALUES(75,9,'75:9','كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ[7ـ10]. ');
INSERT INTO "saleh" VALUES(75,10,'75:10','كۆزلەر تورلاشقان، ئاينىڭ نۇرى ئۆچكەن، كۈن بىلەن ئاي بىرلەشتۈرۈلگەن چاغدا، ئىنسان بۇ كۈندە: «قاچىدىغان جاي قەيەردە؟» دەيدۇ[7ـ10]. ');
INSERT INTO "saleh" VALUES(75,11,'75:11',' ياق، (ئاللاھنىڭ ئازابىدىن) قاچىدىغان پاناھگاھ يوقتۇر.');
INSERT INTO "saleh" VALUES(75,12,'75:12','بۇ كۈندە قارارگاھ پەرۋەردىگارىڭنىڭ تەرىپىدۇر.');
INSERT INTO "saleh" VALUES(75,13,'75:13','بۇ كۈندە ئىنسانغا (چوڭ بولسۇن، كىچىك بولسۇن، ياخشىلىق بولسۇن، يامانلىق بولسۇن) ئىلگىرى ئاخىرى قىلغان ئەمەللىرى ئۇقتۇرۇلىدۇ.');
INSERT INTO "saleh" VALUES(75,14,'75:14',' بەلكى ئىنسان ئۆزىگە (يەنى قىلغان يامان ئىشلىرىغا) ئۆزى گۇۋاھتۇر.');
INSERT INTO "saleh" VALUES(75,15,'75:15',' ئۇ ئۆزىنى ئاقلاش ئۈچۈن قانچە ئۆزرە ئېيتقان تەقدىردىمۇ (ئۆزرىسى قوبۇل قىلىنمايدۇ). ');
INSERT INTO "saleh" VALUES(75,16,'75:16',' (ساڭا جىبرىئىل ئارقىلىق ۋەھىي نازىل بولۇۋاتقاندا، ئۇنى ئېسىڭغا ئېلىۋېلىش ئۈچۈن) ئالدىراپ تىلىڭنى مىدىرلاتما.');
INSERT INTO "saleh" VALUES(75,17,'75:17','ئۇنى توپلاش ۋە ئوقۇپ بېرىش بىزنىڭ مەسئۇلىيىتىمىزدۇر.');
INSERT INTO "saleh" VALUES(75,18,'75:18',' ساڭا ئۇنى ئوقۇپ بەرگىنىمىزدە (يەنى جىبرىئىل ساڭا ئوقۇپ بەرگەندە)، ئۇنىڭ ئوقۇشىغا ئەگەشكىن.');
INSERT INTO "saleh" VALUES(75,19,'75:19','ئاندىن ئۇنى چۈشەندۈرۈپ بېرىش بىزنىڭ مەسئۇلىيىتىمىزدۇر.');
INSERT INTO "saleh" VALUES(75,20,'75:20','ھەرگىز ئۇنداق ئەمەس (يەنى ئۆلگەندىن كېيىن تىرىلىش، ھېساب بېرىش سىلەر گۇمان قىلغاندەك يوق ئىش ئەمەس)، بەلكى سىلەر بۇ دۇنيانى دوست تۇتىسىلەر. ');
INSERT INTO "saleh" VALUES(75,21,'75:21','ئاخىرەتكە كۆڭۈل بۆلمەيسىلەر.');
INSERT INTO "saleh" VALUES(75,22,'75:22',' بۇ كۈندە، (سائادەتمەنلەرنىڭ) يۈزلىرى نۇرلۇق بولىدۇ.');
INSERT INTO "saleh" VALUES(75,23,'75:23','(ئۇلار) پەرۋەردىگارىغا قاراپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(75,24,'75:24',' بۇ كۈندە، نۇرغۇن يۈزلەر تۇتۇق بولىدۇ.');
INSERT INTO "saleh" VALUES(75,25,'75:25','ئۆزلىرىنىڭ چوڭ مۈشكۈللۈككە دۇچار بولىدىغانلىقىغا ئۇلار جەزمەن ئىشىنىدۇ.');
INSERT INTO "saleh" VALUES(75,26,'75:26','راستلا جان ھەلقۇمىغا يەتكەندە، «(بۇ كېسەلگە) كىم ئىلاج قىلالايدۇ» دېيىلىدۇ[26ـ27].');
INSERT INTO "saleh" VALUES(75,27,'75:27','راستلا جان ھەلقۇمىغا يەتكەندە، «(بۇ كېسەلگە) كىم ئىلاج قىلالايدۇ» دېيىلىدۇ[26ـ27].');
INSERT INTO "saleh" VALUES(75,28,'75:28',' (سەكراتتا ياتقان ئادەم) بۇنىڭ (دۇنيادىن) ئايرىلىش ئىكەنلىكىنى جەزمەن بىلىدۇ.');
INSERT INTO "saleh" VALUES(75,29,'75:29',' (سەكراتنىڭ قاتتىقلىقىدىن) پاچاق پاچاققا كىرىشىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(75,30,'75:30',' بۇ كۈندە ھەيدەپ بېرىلىدىغان جاي پەرۋەردىگارىڭنىڭ دەرگاھىدۇر.');
INSERT INTO "saleh" VALUES(75,31,'75:31','(كاپىر قۇرئانغا) ئىشەنمىدى، ناماز ئوقۇمىدى.');
INSERT INTO "saleh" VALUES(75,32,'75:32',' لېكىن ئۇ (قۇرئاننى) ئىنكار قىلدى ۋە (ئىماندىن) يۈز ئۆرۈدى.');
INSERT INTO "saleh" VALUES(75,33,'75:33','ئاندىن كېرىلگەن ھالدا ئۆيىگە قايتتى.');
INSERT INTO "saleh" VALUES(75,34,'75:34','ۋاي سېنىڭ ھالىڭغا! ۋاي سېنىڭ ھالىڭغا!');
INSERT INTO "saleh" VALUES(75,35,'75:35','يەنە ۋاي سېنىڭ ھالىڭغا! ۋاي سېنىڭ ھالىڭغا!');
INSERT INTO "saleh" VALUES(75,36,'75:36','ئىنسان ئۆزىنى بىكار قويۇپ بېرىلىدۇ (يەنى شەرىئەت ئىشلىرىغا تەكلىپ قىلىنماي، مەيلىگە قويۇۋېتىلگەن ھايۋانغا ئوخشاش) دەپ ئويلامدۇ؟');
INSERT INTO "saleh" VALUES(75,37,'75:37','ئۇ (بەچچىدانلارغا) تۆكىلىدىغان (ئاجىز مەنىي) ئەمەسمىدى؟');
INSERT INTO "saleh" VALUES(75,38,'75:38','ئاندىن ئۇ لەختە قان بولدى، ئاندىن ئاللاھ ئۇنى چىرايلىق شەكىلدە ياراتتى.');
INSERT INTO "saleh" VALUES(75,39,'75:39',' ئۇ مەنىيدىن ئەر ـ ئايال (ئىككى تىپنى) ياراتتى');
INSERT INTO "saleh" VALUES(75,40,'75:40','(يەنى يۇقىرىقىدەك ئىشلارنى قىلالىغان ئاللاھ) ئۆلۈكلەرنى تىرىلدۈرۈشكە قادىر ئەمەسمۇ؟');
INSERT INTO "saleh" VALUES(76,1,'76:1',' ئىنسان (ئانىنىڭ قورسىقىدا) بىر مۇددەتنى ئۆتكۈزدى، ئۇ (ئەرزىمگەنلىكتىن) تىلغا ئېلىنىدىغان نەرسە ئەمەس ئىدى.');
INSERT INTO "saleh" VALUES(76,2,'76:2','شۈبھىسىزكى، بىز ئىنساننى ئارىلاشما مەنىي (يەنى ئەر بىلەن ئاياللارنىڭ مەنىيسى) دىن ياراتتۇق، ئۇنى بىز سىنايمىز، شۇنىڭ ئۈچۈن ئۇنى بىز (ئۇنى نازىل قىلغان ئايەتلىرىمىزنى ئاڭلىسۇن، كائىناتنى ياراتقۇچىنىڭ بىرلىكىگە دالالەت قىلىدىغان دەلىللەرنى كۆرسۇن دەپ) ئاڭلايدىغان، كۆرۈدىغان قىلىپ ياراتتۇق.');
INSERT INTO "saleh" VALUES(76,3,'76:3','شۈبھىسىزكى، ئۇنىڭغا بىز (پەيغەمبەر ئەۋەتىش بىلەن ياخشى ـ يامان) يولنى كۆرسەتتۇق، ئۇ مۆمىن بولۇپ ياخشى يولدا مېڭىپ (ئاللاھنىڭ نېمىتىگە) شۈكۈر قىلغۇچىدۇر، ياكى (فاجىر بولۇپ يامان يولدا مېڭىپ) كۇفرىلىق قىلغۇچىدۇر. ');
INSERT INTO "saleh" VALUES(76,4,'76:4',' شۈبھىسىزكى، بىز كاپىرلارغا (پۇتلىرىغا سېلىنىدىغان) زەنجىرلەرنى، (بويۇنلىرىغا سېلىنىدىغان) تاقاقلارنى ۋە يېنىپ تۇرغان دوزاخنى تەييارلىدۇق.');
INSERT INTO "saleh" VALUES(76,5,'76:5','ھەقىقەتەن ياخشىلار كافۇر ئارىلاشتۇرۇلغان (مەي بىلەن تولدۇرۇلغان) جاملاردىن ئىچىدۇ.');
INSERT INTO "saleh" VALUES(76,6,'76:6',' (ئۇ كافۇر جەننەتتىكى بىر) بۇلاقتىن ئېتىلىپ چىققان بولۇپ، ئۇنىڭدىن ئاللاھنىڭ (ياخشى) بەندىلىرى ئىچىدۇ، ئۇ بۇلاقنى ئۇلار (خالىغان جايلىرىغا) ئېقىتىپ بارالايدۇ. ');
INSERT INTO "saleh" VALUES(76,7,'76:7','ئۇلار ئۆز ئۈستىگە قەسەم ئىچىپ ئالغان ئىشنى ئورۇنلايدۇ ۋە دەھشىتى كەڭ دائىرىلىك بولغان كۈندىن (يەنى قىيامەت كۈنىدىن) قورقىدۇ.');
INSERT INTO "saleh" VALUES(76,8,'76:8',' ئۆزى موھتاج تۇرۇقلۇق، مىسكىنگە، يېتىمگە ۋە ئەسىرگە تائام بېرىدۇ.');
INSERT INTO "saleh" VALUES(76,9,'76:9',' (ئۇلار ئېيتىدۇ) «سىلەرگە بىز ئاللاھنىڭ رازىلىقى ئۈچۈن تائام بېرىمىز، سىلەردىن (بۇنىڭ بەدىلىگە) ھېچقانداق مۇكاپات ۋە تەشەككۈر تەلەپ قىلمايمىز.');
INSERT INTO "saleh" VALUES(76,10,'76:10','شۈبھىسىزكى، بىز پەرۋەردىگارىمىز تەرىپىدىن مەيدانغا كەلتۈرۈلگەن ئېغىر، قاتتىق كۈندىن قورقىمىز».');
INSERT INTO "saleh" VALUES(76,11,'76:11',' ئاللاھ ئۇلارنى شۇ كۈننىڭ شەررىدىن ساقلايدۇ، ئۇلارنىڭ (يۈزلىرىگە) نۇر، (دىللىرىغا) خۇشاللىق ئاتا قىلىدۇ.');
INSERT INTO "saleh" VALUES(76,12,'76:12',' ئۇلارنىڭ سەۋر ـ تاقەتلىرى ئۈچۈن ئاللاھ ئۇلارنى جەننەت بىلەن ۋە (ئۇ يەردىكى) يىپەك (لىباسلار) بىلەن مۇكاپاتلايدۇ.');
INSERT INTO "saleh" VALUES(76,13,'76:13',' ئۇلار جەننەتتە تەختلەرگە يۆلىنىپ ئولتۇرىدۇ، ئۇلار جەننەتتە قاتتىق ئىسسىقنىمۇ، قاتتىق سوغۇقنىمۇ كۆرمەيدۇ.');
INSERT INTO "saleh" VALUES(76,14,'76:14','جەننەتتىكى (دەرەخلەرنىڭ) سايىلىرى ئۇلارغا يېقىندۇر، جەننەتنىڭ مېۋىلىرىنى ئۈزۈش ئۇلارغا ئاسان قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(76,15,'76:15','ئۇلارغا (تاماق قاچىلانغان) كۈمۈش تەخسىلەر، (مەي تولدۇرۇلغان) كۈمۈشتىن ياسالغان (شىشىدەك سۈزۈك) جاملار ئايلاندۇرۇپ سۇنۇلۇپ تۇرىدۇ، (ساقىيلار، ئۇلارنىڭ ئېھتىياجىغا قاراپ) لايىق ئۆلچەيدۇ (يەنى ئىچكۈچىلەرنىڭ ئېھتىياجىدىن ئېشىپمۇ قالمايدۇ، كېمىپمۇ قالمايدۇ)[15ـ16].');
INSERT INTO "saleh" VALUES(76,16,'76:16','ئۇلارغا (تاماق قاچىلانغان) كۈمۈش تەخسىلەر، (مەي تولدۇرۇلغان) كۈمۈشتىن ياسالغان (شىشىدەك سۈزۈك) جاملار ئايلاندۇرۇپ سۇنۇلۇپ تۇرىدۇ، (ساقىيلار، ئۇلارنىڭ ئېھتىياجىغا قاراپ) لايىق ئۆلچەيدۇ (يەنى ئىچكۈچىلەرنىڭ ئېھتىياجىدىن ئېشىپمۇ قالمايدۇ، كېمىپمۇ قالمايدۇ)[15ـ16].');
INSERT INTO "saleh" VALUES(76,17,'76:17','ئۇلارغا يەنە جەننەتتە زەنجىبىل ئارىلاشتۇرۇلغان جاملار (يەنى جامدىكى مەيلەر) سۇنۇلىدۇ. ');
INSERT INTO "saleh" VALUES(76,18,'76:18','جەننەتتە سەلسەبىل دەپ ئاتىلىدىغان بىر بۇلاقمۇ بار.');
INSERT INTO "saleh" VALUES(76,19,'76:19',' قېرىماي ھەمىشە ياش تۇرىدىغان غىلمانلار نۆۋەت بىلەن ئۇلارنىڭ خىزمىتىنى قىلىپ تۇرىدۇ، ئۇلارنى كۆرگەن چېغىڭدا (ئۇلارنىڭ گۈزەللىكى، سۈزۈكلۈكى ۋە نۇرلۇقلۇقىغا قاراپ) ئۇلارنى تېرىلىپ كەتكەن مەرۋايىتمىكىن دەپ قالىسەن.');
INSERT INTO "saleh" VALUES(76,20,'76:20','قاچانكى سەن قارايدىغان بولساڭ، بۇ يەردە (تەسۋىرلەپ تۈگەتكىلى بولمايدىغان) نېمەتلەرنى ۋە كاتتا پادىشاھلىقنى كۆرۈسەن.');
INSERT INTO "saleh" VALUES(76,21,'76:21',' ئۇلارنىڭ ئۇچىسىدا يۇپقا يىپەك ۋە قېلىن يىپەكتىن (تەييارلانغان) يېشىل كىيىملەر بولىدۇ، (ئۇلار) كۈمۈش ئۈزۈكلەرنى تاقايدۇ، پەرۋەردىگارى ئۇلارغا پاك شاراب سۇنىدۇ.');
INSERT INTO "saleh" VALUES(76,22,'76:22','شۈبھىسىزكى، بۇ سىلەرگە بېرىلگەن مۇكاپاتتۇر، سىلەرنىڭ مېھنىتىڭلار قوبۇل بولدى. ');
INSERT INTO "saleh" VALUES(76,23,'76:23','(ئى مۇھەممەد!) ھەقىقەتەن بىز ساڭا قۇرئاننى بۆلۈپ ـ بۆلۈپ نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(76,24,'76:24','سەن پەرۋەردىگارىڭنىڭ ھۆكمىگە سەۋر قىلغىن، سەن ئۇلارنىڭ ئىچىدىن ھېچبىر گۇناھكارغا ياكى كۇفرىلىق قىلغۇچىغا بويسۇنمىغىن.');
INSERT INTO "saleh" VALUES(76,25,'76:25','ئەتىگەن ـ ئاخشامدا پەرۋەردىگارىڭنىڭ نامىنى ياد قىلغىن (يەنى ناماز ئوقۇغىن، پەرۋەردىگارىڭغا كۆپ تائەت ـ ئىبادەت قىلغىن).');
INSERT INTO "saleh" VALUES(76,26,'76:26','كېچىنىڭ ئاز قىسمىدا ئۇنىڭغا سەجدە قىل، كۆپ قىسمىدا ئۇنىڭغا تەسبىھ ئېيت.');
INSERT INTO "saleh" VALUES(76,27,'76:27',' شۈبھىسىزكى، بۇلار دۇنيانى (ئاخىرەتتىن) ئارتۇق بىلىدۇ، كەلگۈسىدىكى قىيىن بىر كۈنگە (يەنى قىيامەتكە) سەل قارايدۇ.');
INSERT INTO "saleh" VALUES(76,28,'76:28',' بىز ئۇلارنى ياراتتۇق، ئۇلارنىڭ بەدىنىنى مەزمۇت قىلدۇق، ئەگەر بىز خالىساق (ئۇلارنى ھالاك قىلىپ) ئورنىغا ئۇلارغا ئوخشاش باشقا ئادەمنى دەسسىتەتتۇق.');
INSERT INTO "saleh" VALUES(76,29,'76:29',' شۈبھىسىزكى، بۇ (ئايەتلەر) ۋەز ـ نەسىھەتتۇر (بۇ ۋەز ـ نەسىھەتتىن پايدىلىنىشنى) خالىغان ئادەم پەرۋەردىگارىغا يەتكۈزىدىغان يولنى تۇتسۇن.');
INSERT INTO "saleh" VALUES(76,30,'76:30','پەقەت ئاللاھ خالىغاندىلا، ئاندىن سىلەر خالايسىلەر (يەنى ھەممە ئىش ئاللاھنىڭ خاھىشىغا باغلىقتۇر). ئاللاھ ھەقىقەتەن ھەممىنى بىلگۈچىدۇر، ھېكمەت بىلەن ئىش قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(76,31,'76:31',' ئاللاھ خالىغان ئادەمنى رەھمىتىنىڭ دائىرىسىگە كىرگۈزىدۇ. ئاللاھ زالىملارغا قاتتىق ئازابنى تەييارلىدى.');
INSERT INTO "saleh" VALUES(77,1,'77:1','ئارقىمۇئارقا چىققۇچى (لەرزان) شاماللار بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(77,2,'77:2','قاتتىق چىققۇچى بورانلار بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(77,3,'77:3',' (بۇلۇتلارنى ئاللاھ خالىغان تەرەپلەرگە) تارقاتقۇچى شاماللار بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(77,4,'77:4',' (ھەق بىلەن باتىلنىڭ، ھارام بىلەن ھالالنىڭ) ئارىسىنى ئايرىغۇچى پەرىشتىلەر بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(77,5,'77:5',' (بەندىلەرگە) ئۆزرە (قالدۇرماسلىق) ياكى (ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇرۇش ئۈچۈن ۋەھىينى (پەيغەمبەرلەرگە) ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى[5ـ6]، ');
INSERT INTO "saleh" VALUES(77,6,'77:6',' (بەندىلەرگە) ئۆزرە (قالدۇرماسلىق) ياكى (ئاللاھنىڭ ئازابىدىن) ئاگاھلاندۇرۇش ئۈچۈن ۋەھىينى (پەيغەمبەرلەرگە) ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى[5ـ6]، ');
INSERT INTO "saleh" VALUES(77,7,'77:7','سىلەر ئاگاھلاندۇرۇلغان ئىش (يەنى قىيامەت، ھېساب ۋە جازا) چوقۇم مەيدانغا كەلگۈچىدۇر.');
INSERT INTO "saleh" VALUES(77,8,'77:8','يۇلتۇزلارنىڭ (نۇرى) ئۆچۈرۈلگەن چاغدا.');
INSERT INTO "saleh" VALUES(77,9,'77:9',' ئاسمان يېرىلغان چاغدا،');
INSERT INTO "saleh" VALUES(77,10,'77:10',' تاغلار تىتىلىپ (شامالدا ئۇچۇپ يۈرگەن زەررىلەردەك) ئۇچۇپ يۈرگەن چاغدا،');
INSERT INTO "saleh" VALUES(77,11,'77:11','پەيغەمبەرلەرگە (ئۇلار بىلەن ئۈممەتلىرى ئارىسىدا ھۆكۈم چىقىرىلىشقا) ۋاقىت بەلگىلەنگەن چاغدا (ۋەدە قىلىنغان قىيامەت مەيدانغا كېلىدۇ).');
INSERT INTO "saleh" VALUES(77,12,'77:12','(پەيغەمبەرلەرنىڭ قەۋملىرىگە ئائىت ئىشلار) قايسى بۈيۈك كۈنگە كېچىكتۈرۈلدى؟');
INSERT INTO "saleh" VALUES(77,13,'77:13','خالايىقنىڭ ئارىسىدا ھۆكۈم چىقىرىلىدىغان كۈن ئۈچۈن (تەخىر قىلىندى). ');
INSERT INTO "saleh" VALUES(77,14,'77:14','ھۆكۈم چىقىرىلىدىغان كۈننىڭ نېمىلىكىنى (يەنى ئۇنىڭ دەھشىتىنى) قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(77,15,'77:15',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي! ');
INSERT INTO "saleh" VALUES(77,16,'77:16','(پەيغەمبەرلىرىنى ئىنكار قىلغانلىقلىرى ئۈچۈن) ئىلگىرىكىلەرنى ھالاك قىلمىدۇقمۇ؟');
INSERT INTO "saleh" VALUES(77,17,'77:17',' ئاندىن ئۇلارغا ئەگەشتۈرۈپ كېيىنكىلەرنى ھالاك قىلدۇق.');
INSERT INTO "saleh" VALUES(77,18,'77:18',' گۇناھكارلارنى مۇشۇنداق قىلىمىز (يەنى جازالايمىز).');
INSERT INTO "saleh" VALUES(77,19,'77:19',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي! ');
INSERT INTO "saleh" VALUES(77,20,'77:20','سىلەرنى بىز ئاجىز مەنىدىن ياراتمىدۇقمۇ؟');
INSERT INTO "saleh" VALUES(77,21,'77:21',' ئۇنى بىز مەلۇم ۋاقىتقىچە (يەنى تۇغۇلغىچە) پۇختا ئارامگاھ (يەنى بەچچىدان) دا قىلدۇق[21ـ22].');
INSERT INTO "saleh" VALUES(77,22,'77:22',' ئۇنى بىز مەلۇم ۋاقىتقىچە (يەنى تۇغۇلغىچە) پۇختا ئارامگاھ (يەنى بەچچىدان) دا قىلدۇق[21ـ22].');
INSERT INTO "saleh" VALUES(77,23,'77:23','بىز (ئۇنى ئابىمەنىيدىن يارىتىشقا) قادىر بولدۇق، بىز (ئۇنى ئەڭ چىرايلىق شەكىلدە يارىتىشقا) نېمىدېگەن ياخشى قادىر بولغۇچىمىز!');
INSERT INTO "saleh" VALUES(77,24,'77:24','بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,25,'77:25',' زېمىننى بىز ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟ ');
INSERT INTO "saleh" VALUES(77,26,'77:26','(يەنى) تىرىكلەرنى ۋە ئۆلۈكلەرنى (ئۆز ئىچىگە ئالىدىغان قىلمىدۇقمۇ؟) ');
INSERT INTO "saleh" VALUES(77,27,'77:27',' بىز زېمىنغا ئېگىز تاغلارنى ئورناتتۇق، سىلەرنى تاتلىق سۇ بىلەن سۇغاردۇق.');
INSERT INTO "saleh" VALUES(77,28,'77:28',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,29,'77:29','(ئۇلارغا دوزاخ مۇئەككەللىرى): «سىلەر (دۇنيادىكى چېغىڭلاردا) ئىنكار قىلغان (دوزاخ) ئازابىغا بېرىڭلار، ');
INSERT INTO "saleh" VALUES(77,30,'77:30','(ئۇلارغا دوزاخ مۇئەككەلىرى ئۈچ شاخلىق سايىگە بېرىڭلار» (دەيدۇ).');
INSERT INTO "saleh" VALUES(77,31,'77:31','ئۇ سايە (جەھەننەمنىڭ تۈتۈنى بولۇپ كىشىنى) سەگىدەتمەيدۇ، (جەھەننەمنىڭ) يالقۇنىنىمۇ توسمايدۇ.');
INSERT INTO "saleh" VALUES(77,32,'77:32','ئۇ يالقۇن قەسىردەك (چوڭ) ئۇچقۇنلارنى چىقىرىدۇ.');
INSERT INTO "saleh" VALUES(77,33,'77:33',' ئۇ ئۇچقۇنلار قارا ـ سېرىق تۆگىلەرگە ئوخشايدۇ.');
INSERT INTO "saleh" VALUES(77,34,'77:34',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,35,'77:35',' بۇ كۈندە ئۇلار سۆز قىلالمايدۇ.');
INSERT INTO "saleh" VALUES(77,36,'77:36',' ئۇلارنىڭ ئۆزرە ئېيتىشىغا ئىزنى بېرىلمەيدۇ.');
INSERT INTO "saleh" VALUES(77,37,'77:37','بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,38,'77:38','بۇ (خالايىقنىڭ ئارىسىدا) ھۆكۈم چىقىرىلىدىغان كۈندۇر، (ئۇ كۈندە) سىلەرنى ۋە ئاۋۋالقىلارنى (يەنى ئۆتكەنكى ئۈممەتلەرنى) (ئاراڭلاردا ھۆكۈم چىقىرىش ئۈچۈن) يىغىمىز.');
INSERT INTO "saleh" VALUES(77,39,'77:39',' ئەگەر (ئازابتىن قۇتۇلۇشقا) بىرەر چارەڭلار بولسا، چارە قىلىپ بېقىڭلار.');
INSERT INTO "saleh" VALUES(77,40,'77:40',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,41,'77:41','شۈبھىسىزكى، تەقۋادارلار سايىلەردىن، بۇلاقلاردىن ۋە كۆڭلى تارتقان مېۋىلەردىن بەھرىمەن بولىدۇ[41ـ42].');
INSERT INTO "saleh" VALUES(77,42,'77:42','شۈبھىسىزكى، تەقۋادارلار سايىلەردىن، بۇلاقلاردىن ۋە كۆڭلى تارتقان مېۋىلەردىن بەھرىمەن بولىدۇ[41ـ42].');
INSERT INTO "saleh" VALUES(77,43,'77:43','(ئۇلارغا) «دۇنيادا قىلغان (ياخشى) ئەمەللىرىڭلار ئۈچۈن خۇشال ـ خۇرام يەڭلار ۋە ئىچىڭلار» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(77,44,'77:44',' شۈبھىسىزكى، بىز ياخشى ئىش قىلغۇچىلارنى مۇشۇنداق مۇكاپاتلايمىز.');
INSERT INTO "saleh" VALUES(77,45,'77:45',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي! ');
INSERT INTO "saleh" VALUES(77,46,'77:46','(كۇففارلارغا) «(دۇنيانىڭ لەززەتلىرىدىن) ۋاقىتلىق يەڭلار ۋە بەھرىمەن بولۇڭلار، شۈبھىسىزكى، سىلەر گۇناھكارسىلەر» (دېيىلىدۇ).');
INSERT INTO "saleh" VALUES(77,47,'77:47',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي!');
INSERT INTO "saleh" VALUES(77,48,'77:48',' ئۇلارغا: «رۇكۇ قىلىڭلار» دېيىلسە، ئۇلار رۇكۇ قىلمايدۇ.');
INSERT INTO "saleh" VALUES(77,49,'77:49',' بۇ كۈندە (ئۇ كۈننى) ئىنكار قىلغۇچىلارغا ۋاي! ');
INSERT INTO "saleh" VALUES(77,50,'77:50',' ئۇلار (شانلىق دەلىللەرنى ئۆز ئىچىگە ئالغان، پاساھەت ـ بالاغەتتە ئەڭ يۇقىرى بولغان بۇ قۇرئانغا ئىشەنمىسە) باشقا قايسى سۆزگە ئىشىنىدۇ؟ ');
INSERT INTO "saleh" VALUES(78,1,'78:1','(مۇشرىكلار ئۆزئارا نېمە توغرۇلۇق) سورىشىدۇ؟');
INSERT INTO "saleh" VALUES(78,2,'78:2','ئۇلار چوڭ بىر خەۋەر (يەنى قىيامەت توغرۇلۇق) سورىشىدۇ.');
INSERT INTO "saleh" VALUES(78,3,'78:3','ئۇلار شۇ (يەنى قىيامەت) توغرىسىدا ئىختىلاپ قىلىشقۇچىلاردۇر. ');
INSERT INTO "saleh" VALUES(78,4,'78:4',' ھەرگىز ئۇنداق ئەمەس (قىيامەت ئۇلارنىڭ ئويلىغىنىدەك يالغان ئەمەس)، ئۇلار (ھەقىقىي ئەھۋالنى) كەلگۈسىدە بىلىدۇ.');
INSERT INTO "saleh" VALUES(78,5,'78:5','ھەرگىز ئۇنداق ئەمەس، ئۇلار (بېشىغا كەلگەن ئازابنى) كەلگۈسىدە بىلىدۇ.');
INSERT INTO "saleh" VALUES(78,6,'78:6','(زېمىننىڭ ئۈستىدە مۇقىملىشىشىڭلار ۋە كەڭ تۈزلەڭلىكلەرگە زىرائەت تېرىپ پايدىلىنىشىڭلار ئۈچۈن) زېمىننى بىسات قىلمىدۇقمۇ؟');
INSERT INTO "saleh" VALUES(78,7,'78:7',' (زېمىننىڭ تەۋرەپ كەتمەسلىكى ئۈچۈن) تاغلارنى قوزۇق قىلمىدۇقمۇ؟');
INSERT INTO "saleh" VALUES(78,8,'78:8','سىلەرنى ئەر ـ ئايال قىلىپ ياراتتۇق.');
INSERT INTO "saleh" VALUES(78,9,'78:9','ئۇيقۇنى سىلەرنىڭ (بەدىنىڭلار ئۈچۈن) راھەت قىلدۇق.');
INSERT INTO "saleh" VALUES(78,10,'78:10','كېچىنى لىباس قىلدۇق.');
INSERT INTO "saleh" VALUES(78,11,'78:11','كۈندۈزنى (سىلەر) تىرىكچىلىك قىلىدىغان (ۋاقىت) قىلدۇق.');
INSERT INTO "saleh" VALUES(78,12,'78:12',' ئۈستۈڭلاردا مۇستەھكەم يەتتە ئاسماننى بىنا قىلدۇق.');
INSERT INTO "saleh" VALUES(78,13,'78:13',' (سىلەر ئۈچۈن) يېنىپ تۇرغان چىراغنى (قۇياشنى) ياراتتۇق.');
INSERT INTO "saleh" VALUES(78,14,'78:14',' ئاشلىقلارنى، ئوت ـ چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول يامغۇر ياغدۇرۇپ بەردۇق[14ـ16].');
INSERT INTO "saleh" VALUES(78,15,'78:15',' ئاشلىقلارنى، ئوت ـ چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول يامغۇر ياغدۇرۇپ بەردۇق[14ـ16].');
INSERT INTO "saleh" VALUES(78,16,'78:16',' ئاشلىقلارنى، ئوت ـ چۆپلەرنى، دەرەخلىرى قويۇق باغچىلارنى ئۆستۈرۈش ئۈچۈن بۇلۇتلاردىن مول يامغۇر ياغدۇرۇپ بەردۇق[14ـ16].');
INSERT INTO "saleh" VALUES(78,17,'78:17',' قىيامەت كۈنى ھەقىقەتەن (مۇكاپاتلاش ۋە جازالاش) ۋاقتىدۇر. ');
INSERT INTO "saleh" VALUES(78,18,'78:18',' ئۇ كۈنى سۇر چېلىنىدۇ، سىلەر توپ ـ توپ بولۇپ كېلىسىلەر.');
INSERT INTO "saleh" VALUES(78,19,'78:19','ئاسمان ئېچىلىپ نۇرغۇن ئىشىكلەر پەيدا بولىدۇ.');
INSERT INTO "saleh" VALUES(78,20,'78:20',' تاغلار ھەرىكەتلەندۈرۈلۈپ سەرابتەك بولۇپ قالىدۇ (يەنى تاغلار توزۇپ يىراقتىن قارىغۇچىغا سۇدەك تۇيۇلىدۇ).');
INSERT INTO "saleh" VALUES(78,21,'78:21',' جەھەننەم ھەقىقەتەن (كۇففارلارنى) كۈتۈپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(78,22,'78:22','جەھەننەم، شۈبھىسىزكى، گۇناھكارلارنىڭ جايىدۇر.');
INSERT INTO "saleh" VALUES(78,23,'78:23','ئۇلار جەھەننەمدە ئۇزاق مۇددەت قالىدۇ.');
INSERT INTO "saleh" VALUES(78,24,'78:24','ئۇلار جەھەننەمدە (جەھەننەمنىڭ ھارارىتىنى يىنىكلىتىلگەن) بىرەر سۆرۈنلۈككە ۋە (تەشنالىقنى قاندۇرىدىغان) بىرەر ئىچىملىككە ئېرىشەلمەيدۇ.');
INSERT INTO "saleh" VALUES(78,25,'78:25',' ئۇلارنىڭ ئېرىشىدىغىنى پەقەت يۇقىرى ھارارەتلىك قايناقسۇ ۋە يىرىڭدىنلا ئىبارەت بولىدۇ. ');
INSERT INTO "saleh" VALUES(78,26,'78:26','بۇ ئۇلارغا بېرىلىدىغان مۇۋاپىق جازادۇر. ');
INSERT INTO "saleh" VALUES(78,27,'78:27',' چۈنكى ئۇلار (قىلمىشلىرىدىن) ھېساب ئېلىنىشتىن قورقمايتتى');
INSERT INTO "saleh" VALUES(78,28,'78:28','ھەمدە بىزنىڭ ئايەتلىرىمىزنى قاتتىق ئىنكار قىلاتتى.');
INSERT INTO "saleh" VALUES(78,29,'78:29',' ھەر نەرسىنى تولۇق خاتىرىلەپ قويغان ئىدۇق.');
INSERT INTO "saleh" VALUES(78,30,'78:30','(ئۇلارغا ئېيتىمىزكى) «تېتىڭلار! سىلەرگە پەقەت ئازابنىلا زىيادە قىلىمىز».');
INSERT INTO "saleh" VALUES(78,31,'78:31','شۈبھىسىزكى، تەقۋادارلار نېمەتكە ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(78,32,'78:32','(ئۇ نېمەت) باغلار، ئۈزۈملەردۇر.');
INSERT INTO "saleh" VALUES(78,33,'78:33','تەڭتۇش قىزلاردۇر.');
INSERT INTO "saleh" VALUES(78,34,'78:34','(شاراب بىلەن) تولدۇرۇلغان جاملاردۇر.');
INSERT INTO "saleh" VALUES(78,35,'78:35','ئۇلار جەننەتتە يالغان سۆز، بىھۇدە سۆز ئاڭلىمايدۇ.');
INSERT INTO "saleh" VALUES(78,36,'78:36',' (ئەنە شۇلار) پەرۋەردىگارىڭنىڭ بەرگەن مۇكاپاتى، يېتەرلىك ئاتاسىدۇر.');
INSERT INTO "saleh" VALUES(78,37,'78:37',' ئۇ ئاسمانلارنىڭ، زېمىننىڭ ۋە ئۇلارنىڭ ئارىسىدىكى شەيئىلەرنىڭ پەرۋەردىگارىدۇر. ناھايىتى شەپقەتلىكتۇر. (ئۇ كۈندە ھېچ كىشى) ئۇنىڭغا سۆز قىلىشقا قادىر بولالمايدۇ.');
INSERT INTO "saleh" VALUES(78,38,'78:38','جىبرىئىل ۋە پەرىشتىلەر سەپ بولۇپ تۇرۇپ كېتىدىغان كۈندە (يەنى قىيامەتتە)، ئۇلار(نىڭ ئارىسىدا) مېھرىبان ئاللاھنىڭ ئىزنىگە ئېرىشكەن ۋە توغرا سۆزنى قىلغانلاردىن باشقا ھېچ ئەھەدىنىڭ سۆز قىلىشىغا بولمايدۇ.');
INSERT INTO "saleh" VALUES(78,39,'78:39',' ئەنە شۇ چوقۇم بولىدىغان كۈندۇر. كىمكى (ئىمان بىلەن، ياخشى ئەمەل بىلەن) پەرۋەردىگارىغا قايتىدىغان يولنى تۇتۇشنى خالىسا (تۇتسۇن).');
INSERT INTO "saleh" VALUES(78,40,'78:40',' بىز سىلەرنى ھەقىقەتەن (يۈز بېرىشى) يېقىن ئازابتىن (يەنى ئاخىرەت ئازابىدىن) ئاگاھلاندۇردۇق، ئۇ كۈندە كىشى ئىلگىرى قىلغان ئەمەللىرىنى كۆرىدۇ. كاپىر: «كاشكى مەن تۇپراق بولۇپ كەتكەن بولسامچۇ!» دەيدۇ.');
INSERT INTO "saleh" VALUES(79,1,'79:1','(كۇففارلارنىڭ جانلىرىنى) قاتتىق تارتىپ ئالغۇچى پەرىشتىلەر بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(79,2,'79:2','(مۆمىنلەرنىڭ جانلىرىنى) سىلىق ئالغۇچى پەرىشتىلەر بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(79,3,'79:3','(ئاللاھنىڭ ئەمرىنى ئاسماندىن) تېز ئېلىپ چۈشكۈچى پەرىشتىلەر بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(79,4,'79:4','(مۆمىنلەرنىڭ جانلىرىنى ئېلىپ جەننەتكە) ئىلگىرلىگۈچى پەرىشتىلەر بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(79,5,'79:5',' (ئاللاھنىڭ ئەمرى بىلەن) كائىناتنىڭ ئىشلىرىنى باشقۇرغۇچى پەرىشتىلەر بىلەن قەسەمكى، (سىلەر چوقۇم تىرىلدۈرۈلىسىلەر، سىلەردىن چوقۇم ھېساب ئېلىنىدۇ).');
INSERT INTO "saleh" VALUES(79,6,'79:6','بۇ كۈن (يەنى قىيامەت كۈنى) دە زېمىن (ۋە ئۇنىڭدىكى شەيئىلەر قوشۇلۇپ) تەۋرەيدۇ.');
INSERT INTO "saleh" VALUES(79,7,'79:7','ئۇنىڭ ئارقىسىدىنلا ئاسمان (ۋە ئۇنىڭدىكى شەيئىلەر قوشۇلۇپ) تەۋرەيدۇ.');
INSERT INTO "saleh" VALUES(79,8,'79:8',' بۇ كۈندە دىللارنى (يەنى كۇففارلارنىڭ دىللىرىنى) قورقۇنچ باسىدۇ.');
INSERT INTO "saleh" VALUES(79,9,'79:9',' شۇ (دىللارنىڭ ئىگىلىرىنىڭ) كۆزلىرى (قورقۇنچتىن) تىكىلىپ قارىيالمايدۇ.');
INSERT INTO "saleh" VALUES(79,10,'79:10','ئۇلار ئېيتىدۇ: «راستلا بىز ئاۋۋالقى ھالىمىزغا قايتۇرۇلامدۇق؟ (يەنى ئۆلگەندىن كېيىن تىرىلەمدۇق؟)');
INSERT INTO "saleh" VALUES(79,11,'79:11',' ئۇ بىزنىڭ چىرىگەن سۆڭەك بولۇپ قالغان ۋاقتىمىز ئەمەسمۇ؟» ');
INSERT INTO "saleh" VALUES(79,12,'79:12','ئۇلار ئېيتتى: «ئۇنداق بولىدىغان بولسا، بۇ (يەنى ئۆلگەندىن كېيىن تىرىلىش) زىيانلىق قايتىشتۇر (يەنى بىز ئۆلگەندىن كېيىن تىرىلىدىغان بولساق، دوزاخ ئەھلى بولغانلىقىمىز ئۈچۈن زىيان تارتقۇچىلارمىز)».');
INSERT INTO "saleh" VALUES(79,13,'79:13',' بىر سەيھە (يەنى ئىسراپىلنىڭ سۇر چېلىشى) بىلەنلا ئۇلار (يەنى جىمى خالايىق) (يەر ئاستىدىن) زېمىننىڭ ئۈستىگە چىقىپ قالىدۇ[13ـ14].');
INSERT INTO "saleh" VALUES(79,14,'79:14',' بىر سەيھە (يەنى ئىسراپىلنىڭ سۇر چېلىشى) بىلەنلا ئۇلار (يەنى جىمى خالايىق) (يەر ئاستىدىن) زېمىننىڭ ئۈستىگە چىقىپ قالىدۇ[13ـ14].');
INSERT INTO "saleh" VALUES(79,15,'79:15','ساڭا مۇسانىڭ خەۋىرى كەلدىمۇ؟');
INSERT INTO "saleh" VALUES(79,16,'79:16',' ئۆز ۋاقتىدا ئۇنى پەرۋەردىگارى تۇۋا ناملىق مۇقەددەس ۋادىدا (مۇنداق دەپ) چاقىردى: ');
INSERT INTO "saleh" VALUES(79,17,'79:17','«سەن پىرئەۋننىڭ قېشىغا بارغىن، ئۇ ھەقىقەتەن ھەددىدىن ئاشتى.');
INSERT INTO "saleh" VALUES(79,18,'79:18','ئۇنىڭغا : پاك بولۇشنى خالامسەن.18');
INSERT INTO "saleh" VALUES(79,19,'79:19',' سېنى پەرۋەردىگارىمنى تونۇشقا يېتەكلىشىمنى خالامسەن؟ (ئۇنى تونۇساڭ پەرۋەردىگارىڭدىن) قورقىسەن ـ دە، (ياخشى ئەمەللەرنى قىلىسەن)، دېگىن».');
INSERT INTO "saleh" VALUES(79,20,'79:20',' مۇسا پىرئەۋنگە چوڭ مۆجىزىنى كۆرسەتتى.');
INSERT INTO "saleh" VALUES(79,21,'79:21',' پىرئەۋن (ئاللاھنىڭ پەيغەمبىرى مۇسانى) ئىنكار قىلدى ۋە (ئاللاھنىڭ ئەمرىگە) ئاسىيلىق قىلدى.');
INSERT INTO "saleh" VALUES(79,22,'79:22','ئاندىن پىرئەۋن (مۇسادىن) يۈز ئۆرۈپ (ھىيلە ـ مىكىر ئىشلىتىشكە) كىرىشتى.');
INSERT INTO "saleh" VALUES(79,23,'79:23',' (ئۇ سېھرىگەرلەرنى، ئەسكەرلىرىنى، تەۋەلىرىنى) توپلىدى ھەم نىدا قىلىپ');
INSERT INTO "saleh" VALUES(79,24,'79:24','دېدى: «مەن سىلەرنىڭ بۈيۈك پەرۋەردىگارىڭلار بولىمەن».');
INSERT INTO "saleh" VALUES(79,25,'79:25','ئاللاھ ئۇنى دۇنيا ۋە ئاخىرەتنىڭ ئازابى بىلەن جازالىدى.');
INSERT INTO "saleh" VALUES(79,26,'79:26',' شەك ـ شۈبھىسىزكى، ئاللاھتىن قورقىدىغانلار بۇنىڭدىن ئىبرەت ئالىدۇ.');
INSERT INTO "saleh" VALUES(79,27,'79:27',' سىلەرنى يارىتىش مۈشكۈلمۇ ياكى ئاسماننى يارىتىش مۈشكۈلمۇ؟ ئاللاھ ئاسماننى ياراتتى.');
INSERT INTO "saleh" VALUES(79,28,'79:28','ئۇنىڭ ئېگىزلىكىنى يۈكسەك قىلدى ۋە كەم ـ كۈتىسىز قىلدى.');
INSERT INTO "saleh" VALUES(79,29,'79:29','ئۇنىڭ كېچىسىنى قاپقاراڭغۇ، كۈندۈزىنى يوپيورۇق قىلدى.');
INSERT INTO "saleh" VALUES(79,30,'79:30',' شۇنىڭدىن كېيىن زېمىننى (ئەھلى زېمىننىڭ تۇرۇشىغا لايىق قىلىپ) يايدى.');
INSERT INTO "saleh" VALUES(79,31,'79:31','زېمىندىن بۇلاقلارنى ئاقتۇردى، ئۆسۈملۈكلەرنى ئۆستۈردى. ');
INSERT INTO "saleh" VALUES(79,32,'79:32',' تاغلارنى (زېمىندا) مۇقىم قىلدى.');
INSERT INTO "saleh" VALUES(79,33,'79:33',' ئاللاھتائالا ئۇلارنىڭ ھەممىسىنى سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ پايدىلىنىشى ئۈچۈن (ياراتتى).');
INSERT INTO "saleh" VALUES(79,34,'79:34',' قىيامەت كەلگەندە،');
INSERT INTO "saleh" VALUES(79,35,'79:35','(شۇ) كۈندە ئىنسان (ياخشى ـ يامان) قىلمىشلىرىنى ئەسلەيدۇ.');
INSERT INTO "saleh" VALUES(79,36,'79:36','قارىغۇچىلارغا جەھەننەم ئوچۇق كۆرسىتىلىدۇ.');
INSERT INTO "saleh" VALUES(79,37,'79:37','كىمكى (كۇفرىدا، گۇناھتا) چېكىدىن ئاشىدىكەن،');
INSERT INTO "saleh" VALUES(79,38,'79:38',' دۇنيا تىرىكچىلىكىنى (ئاخىرەتتىن) ئارتۇق كۆرىدىكەن،');
INSERT INTO "saleh" VALUES(79,39,'79:39',' ئۇنىڭ جايى ھەقىقەتەن جەھەننەم بولىدۇ.');
INSERT INTO "saleh" VALUES(79,40,'79:40','پەرۋەردىگارىنىڭ ئالدىدا (سوراققا تارتىلىشتىن) قورقىدىغان، ئۆزىنى نەپسى خاھىشىغا بېرىلىشتىن (يەنى ھارام قىلىنغان نەرسىلەردىن) چەكلىگەن ئادەمگە كەلسەك، ');
INSERT INTO "saleh" VALUES(79,41,'79:41',' ھەقىقەتەن ئۇنىڭ جايى جەننەت بولىدۇ.');
INSERT INTO "saleh" VALUES(79,42,'79:42',' ئۇلار سەندىن قىيامەتنىڭ قاچان بولىدىغانلىقىنى سورايدۇ.');
INSERT INTO "saleh" VALUES(79,43,'79:43',' سەن ئۇنى قانداقمۇ بايان قىلىپ بېرەلەيسەن؟');
INSERT INTO "saleh" VALUES(79,44,'79:44',' ئۇنى بىلىش پەرۋەردىگارىڭغا خاستۇر.');
INSERT INTO "saleh" VALUES(79,45,'79:45',' سېنىڭ ۋەزىپەڭ پەقەت قىيامەتتىن قورقىدىغان ئادەمنى ئاگاھلاندۇرۇشتۇر.');
INSERT INTO "saleh" VALUES(79,46,'79:46',' ئۇلار قىيامەتنى كۆرگەن كۈندە، گويا ئۆزلىرىنى (دۇنيادا) پەقەت بىر كەچقۇرۇن ياكى بىر ئەتىگەن تۇرغاندەك (گۇمان قىلىدۇ).');
INSERT INTO "saleh" VALUES(80,1,'80:1','يېنىغا ئەما كەلگەنلىكى ئۈچۈن (پەيغەمبەر ئەلەيھىسسالام) تەرىنى تۈردى ۋە يۈزىنى ئۆرىۋالدى[1ـ2].');
INSERT INTO "saleh" VALUES(80,2,'80:2','يېنىغا ئەما كەلگەنلىكى ئۈچۈن (پەيغەمبەر ئەلەيھىسسالام) تەرىنى تۈردى ۋە يۈزىنى ئۆرىۋالدى[1ـ2].');
INSERT INTO "saleh" VALUES(80,3,'80:3',' (ئى مۇھەممەد!) نېمە بىلىسەن؟ (بەلكى) ئۇ (سەندىن ئالغان مەرىپەت بىلەن گۇناھلىرىدىن) پاكلىنىشى مۇمكىن.');
INSERT INTO "saleh" VALUES(80,4,'80:4','ياكى ۋەز ـ نەسىھەت ئاڭلاپ ئۇنىڭدىن پايدىلىنىشى مۇمكىن. ');
INSERT INTO "saleh" VALUES(80,5,'80:5','ئۆزىنى (ئىماندىن) بىھاجەت ھېسابلايدىغان ئادەمگە كەلسەك، سەن ئۇنىڭغا يۈزلىنىسەن (يەنى ئۇنىڭغا قۇلاق سېلىپ، تەبلىغ قىلىشقا كۆڭۈل بۆلىسەن)[5ـ6].');
INSERT INTO "saleh" VALUES(80,6,'80:6','ئۆزىنى (ئىماندىن) بىھاجەت ھېسابلايدىغان ئادەمگە كەلسەك، سەن ئۇنىڭغا يۈزلىنىسەن (يەنى ئۇنىڭغا قۇلاق سېلىپ، تەبلىغ قىلىشقا كۆڭۈل بۆلىسەن)[5ـ6].');
INSERT INTO "saleh" VALUES(80,7,'80:7','ئۇ (كۇفرىدىن، گۇناھلاردىن) پاكلانمىسا، ساڭا نېمە زىيىنىدى.');
INSERT INTO "saleh" VALUES(80,8,'80:8','سەندىن مەرىپەت تىلەپ، ئاللاھتىن قورققان ھالدا يۈگۈرۈپ كەلگەن كىشىگە كەلسەك[8ـ9]،');
INSERT INTO "saleh" VALUES(80,9,'80:9','سەندىن مەرىپەت تىلەپ، ئاللاھتىن قورققان ھالدا يۈگۈرۈپ كەلگەن كىشىگە كەلسەك[8ـ9]،');
INSERT INTO "saleh" VALUES(80,10,'80:10',' سەن ئۇنىڭغا قارىماي قويىسەن.');
INSERT INTO "saleh" VALUES(80,11,'80:11','(بۇنىڭدىن كېيىن) ئۇنداق قىلمىغىن. بۇ ئايەتلەر ۋەزدۇر.');
INSERT INTO "saleh" VALUES(80,12,'80:12','خالىغان ئادەم ئۇنىڭدىن ۋەز ـ نەسىھەت ئالىدۇ.');
INSERT INTO "saleh" VALUES(80,13,'80:13','ئۇ (يەنى قۇرئان ئايەتلىرى) قىممەتلىك، (قەدرى) يۇقىرى پاك سەھىپىلەرگە يېزىلغاندۇر[13ـ14].');
INSERT INTO "saleh" VALUES(80,14,'80:14','ئۇ (يەنى قۇرئان ئايەتلىرى) قىممەتلىك، (قەدرى) يۇقىرى پاك سەھىپىلەرگە يېزىلغاندۇر[13ـ14].');
INSERT INTO "saleh" VALUES(80,15,'80:15',' (ئۇ) ھۆرمەتلىك ياخشى (پەرىشتىلەردىن بولغان) پۈتۈكچىلەرنىڭ قولىدىدۇر[15ـ16].');
INSERT INTO "saleh" VALUES(80,16,'80:16',' (ئۇ) ھۆرمەتلىك ياخشى (پەرىشتىلەردىن بولغان) پۈتۈكچىلەرنىڭ قولىدىدۇر[15ـ16].');
INSERT INTO "saleh" VALUES(80,17,'80:17','ئىنسانغا لەنەت بولسۇنكى، ئۇ ئەجەب كاپىر بولدى!');
INSERT INTO "saleh" VALUES(80,18,'80:18','ئاللاھ ئۇنى نېمىدىن ياراتتى؟');
INSERT INTO "saleh" VALUES(80,19,'80:19','ئاللاھ ئۇنى ئابىمەنىيدىن ياراتتى، (ئۇ تولۇق يارىتىلىپ بولغىچە) ئاللاھ ئۇنى بىر قانچە باسقۇچلارغا بۆلۈپ پەيدىن ـ پەي ياراتتى.');
INSERT INTO "saleh" VALUES(80,20,'80:20','ئاندىن ئۇنىڭغا يولنى ئاسانلاشتۇرۇپ بەردى.');
INSERT INTO "saleh" VALUES(80,21,'80:21',' ئاندىن ئۇنى ۋاپات قىلدۇردى، ئۇنىڭغا (كۆمۈلىدىغان) قەبرىنى بەرپا قىلدى.');
INSERT INTO "saleh" VALUES(80,22,'80:22','ئاندىن ئاللاھ ئۇنى خالىغان ۋاقىتتا تىرىلدۈرىدۇ. ');
INSERT INTO "saleh" VALUES(80,23,'80:23','ھەرگىز ئۇنداق ئەمەس (يەنى بۇ كاپىر ئىنسان تەكەببۇرلۇقىدىن يانسۇن)، ئۇ ئاللاھنىڭ بۇيرۇغانلىرىنى بەجا كەلتۈرمىدى.');
INSERT INTO "saleh" VALUES(80,24,'80:24',' ئىنسان ئۆزىنىڭ يېمەكلىكلىرىگە (ئىبرەت نەزىرى بىلەن) قارىسۇن.');
INSERT INTO "saleh" VALUES(80,25,'80:25','(بىز بۇلۇتتىن) زور مىقداردا يامغۇر ياغدۇردۇق.');
INSERT INTO "saleh" VALUES(80,26,'80:26',' ئاندىن زېمىننى (ئۇنىڭدىن ئۆسۈملۈكلەرنى چىقىرىش بىلەن) ياردۇق.');
INSERT INTO "saleh" VALUES(80,27,'80:27','سىلەرنىڭ ۋە ھايۋانلىرىڭلارنىڭ مەنپەئەتلىنىشى ئۈچۈن، زېمىندا ئاشلىقلارنى، ئۈزۈمنى، ئوتياشلارنى، زەيتۇننى، خورمىنى، دەرەخلىرى قويۇق باغچىلارنى، تۈرلۈك مېۋىلەرنى، ئوت ـ چۆپلەرنى ئۆستۈردۇق[27ـ32].');
