INSERT INTO "saleh" VALUES(80,33,'80:33','(قىيامەتنىڭ) قۇلاقنى گاس قىلغۇدەك قاتتىق ئاۋازى كەلگەن چاغدا (ھەممە ئادەم ئۆز ھالى بىلەن بولۇپ قالىدۇ). ');
INSERT INTO "saleh" VALUES(80,34,'80:34','ئۇ كۈندە كىشى ئۆزىنىڭ قېرىندىشىدىن، ئانىسىدىن، ئاتىسىدىن، خوتۇنىدىن ۋە بالىلىرىدىن قاچىدۇ[34ـ36].');
