INSERT INTO "saleh" VALUES(80,40,'80:40',' يەنە بۇ كۈندە نۇرغۇن يۈزلەرنى چاڭ ـ توزان بېسىپ كەتكەن، قارىداپ كەتكەن بولىدۇ[40ـ41]،');
