INSERT INTO "saleh" VALUES(81,2,'81:2','يۇلتۇزلار تۆكۈلگەن چاغدا،');
INSERT INTO "saleh" VALUES(81,3,'81:3',' تاغلار گۇمران بولغان چاغدا،');
INSERT INTO "saleh" VALUES(81,4,'81:4',' بوغاز تۆگىلەر تاشلىۋېتىلگەن چاغدا،');
INSERT INTO "saleh" VALUES(81,5,'81:5','ياۋايى ھايۋانلار توپلانغان چاغدا،');
INSERT INTO "saleh" VALUES(81,6,'81:6',' دېڭىزلار ئوت بولۇپ لاۋۇلدىغان چاغدا،');
INSERT INTO "saleh" VALUES(81,7,'81:7',' جانلار (ئۆز قاياشلىرىغا) قوشۇلغان چاغدا،');
INSERT INTO "saleh" VALUES(81,8,'81:8','تىرىك كۆمۈۋېتىلگەن قىزدىن سەن قايسى گۇناھ بىلەن ئۆلتۈرۈلدۈڭ؟ دەپ سورالغان چاغدا[8ـ9]،');
INSERT INTO "saleh" VALUES(81,9,'81:9','تىرىك كۆمۈۋېتىلگەن قىزدىن سەن قايسى گۇناھ بىلەن ئۆلتۈرۈلدۈڭ؟ دەپ سورالغان چاغدا[8ـ9]،');
INSERT INTO "saleh" VALUES(81,10,'81:10',' نامە ـ ئەماللار ئېچىلغان چاغدا،');
INSERT INTO "saleh" VALUES(81,11,'81:11',' ئاسمان ئېچىپ تاشلانغان چاغدا،');
INSERT INTO "saleh" VALUES(81,12,'81:12','دوزاخ قىزىتىلغان چاغدا، ');
INSERT INTO "saleh" VALUES(81,13,'81:13','جەننەت (تەقۋادارلارغا) يېقىنلاشتۇرۇلغان چاغدا،');
INSERT INTO "saleh" VALUES(81,14,'81:14','ھەر ئادەم ئۆزىنىڭ قىلغان (ياخشى ـ يامان) ئىشلىرىنى بىلىدۇ. ');
INSERT INTO "saleh" VALUES(81,15,'81:15','كۈندۈزى يوشۇرۇنغۇچى يۇلتۇزلار بىلەن، پاتىدىغان ۋاقتىدا يوشۇرۇنىدىغان يۇلتۇزلار بىلەن قەسەم قىلىمەن[15ـ16]،');
INSERT INTO "saleh" VALUES(81,16,'81:16','كۈندۈزى يوشۇرۇنغۇچى يۇلتۇزلار بىلەن، پاتىدىغان ۋاقتىدا يوشۇرۇنىدىغان يۇلتۇزلار بىلەن قەسەم قىلىمەن[15ـ16]،');
INSERT INTO "saleh" VALUES(81,17,'81:17','قاراڭغۇلۇققا چۆمگەن كېچە بىلەن قەسەم قىلىمەن،');
INSERT INTO "saleh" VALUES(81,18,'81:18',' يورۇشقا باشلىغان سۈبھى بىلەن قەسەم قىلىمەن. ');
INSERT INTO "saleh" VALUES(81,19,'81:19','قۇرئان ھەقىقەتەن (ئاللاھنىڭ) ئىززەتلىك بىر ئەلچى (ئارقىلىق نازىل قىلىنغان) سۆزىدۇر.');
INSERT INTO "saleh" VALUES(81,20,'81:20','ئۇ (ئەلچى) كۈچلۈكتۇر، ئاللاھنىڭ دەرگاھىدا مەرتىۋىلىكتۇر.');
INSERT INTO "saleh" VALUES(81,21,'81:21',' پەرىشتىلەر ئۇنىڭغا ئىتائەت قىلغۇچىدۇر. بۇ يەردە (يەنى ئاسماندا) ئۇ ئىشەنچلىكتۇر.');
INSERT INTO "saleh" VALUES(81,22,'81:22',' سىلەرنىڭ ھەمراھىڭلار (يەنى مۇھەممەد ئەلەيھىسسالام) مەجنۇن ئەمەستۇر.');
INSERT INTO "saleh" VALUES(81,23,'81:23',' ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) جىبرىئىلنى ھەقىقەتەن روشەن ئۇپۇقنىڭ شەرقىدە كۆردى.');
INSERT INTO "saleh" VALUES(81,24,'81:24','ئۇ (يەنى مۇھەممەد ئەلەيھىسسالام) (غەيبنى) تەبلىغ قىلىشتا بېخىل ئەمەستۇر.');
INSERT INTO "saleh" VALUES(81,25,'81:25',' ئۇ (يەنى قۇرئان) قوغلاندى شەيتاننىڭ سۆزى ئەمەستۇر.');
INSERT INTO "saleh" VALUES(81,26,'81:26',' قەيەرگە بارىسىلەر؟ ');
INSERT INTO "saleh" VALUES(81,27,'81:27','ئۇ (يەنى قۇرئان) پۈتۈن ئەھلى جاھانغا نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(81,28,'81:28','سىلەرنىڭ ئاراڭلاردىن (دىن بارىسىدا) راۋۇرۇس بولۇشنى خالايدىغانلارغا (قۇرئان) نەسىھەتتۇر.');
INSERT INTO "saleh" VALUES(81,29,'81:29','پەقەت ئالەملەرنىڭ پەرۋەردىگارى ئاللاھ خالىغاندىلا، ئاندىن سىلەر خالايسىلەرغىچە.');
INSERT INTO "saleh" VALUES(82,1,'82:1','ئاسمان يېرىلغان چاغدا، ');
INSERT INTO "saleh" VALUES(82,2,'82:2','يۇلتۇزلار تۆكۈلگەن چاغدا،');
INSERT INTO "saleh" VALUES(82,3,'82:3','دېڭىزلار بىر ـ بىرىگە قوشۇلغان چاغدا،');
INSERT INTO "saleh" VALUES(82,4,'82:4',' قەبرىلەر ئاستى ـ ئۈستى قىلىنغان (يەنى ئۆلۈكلەر چىقىرىلغان) چاغدا،');
INSERT INTO "saleh" VALUES(82,5,'82:5','ھەر ئادەم ئىلگىرى ـ كېيىن قىلغان ئىشلىرىنىڭ ھەممىسىنى بىلىدۇ.');
INSERT INTO "saleh" VALUES(82,6,'82:6',' ئى ئىنسان! سېنى مەرھەمەتلىك پەرۋەردىگارىڭغا ئاسىيلىق قىلىشقا نېمە ئالدىدى؟');
INSERT INTO "saleh" VALUES(82,7,'82:7','ئۇ سېنى ياراتتى، (ئەزالىرىڭنى) بېجىرىم، قامىتىڭنى تۈز قىلدى.');
INSERT INTO "saleh" VALUES(82,8,'82:8',' سېنى ئۇ خالىغان شەكىلدە قۇراشتۇردى.');
INSERT INTO "saleh" VALUES(82,9,'82:9','ھەرگىز ئۇنداق قىلماڭلار (يەنى ئاللاھنىڭ كەرەمىگە مەغرۇر بولماڭلار). (ئى مەككە كۇففارلىرى!) بەلكى سىلەر دىننى ئىنكار قىلىسىلەر.');
INSERT INTO "saleh" VALUES(82,10,'82:10','ھالبۇكى سىلەرنىڭ ئۈستۈڭلاردا سىلەرنى كۈزىتىپ تۇرغۇچى پەرىشتىلەر بار.');
INSERT INTO "saleh" VALUES(82,11,'82:11','ئۇلار (ئاللاھنىڭ دەرگاھىدا) ھۆرمەتلىك پەرىشتىلەر بولۇپ، (سىلەرنىڭ سۆزلىرىڭلارنى ۋە ئەمەللىرىڭلار) نى يېزىپ تۇرىدۇ.');
INSERT INTO "saleh" VALUES(82,12,'82:12','ئۇلار قىلمىشىڭلارنى بىلىپ تۇرىدۇ. ');
INSERT INTO "saleh" VALUES(82,13,'82:13','مۆمىنلەر شەك ـ شۈبھىسىز جەننەتتە بولىدۇ،');
INSERT INTO "saleh" VALUES(82,14,'82:14',' كاپىرلار شەك ـ شۈبھىسىز دوزاختا بولىدۇ.');
INSERT INTO "saleh" VALUES(82,15,'82:15','ئۇلار قىيامەت كۈنى دوزاخقا كىرىدۇ.');
INSERT INTO "saleh" VALUES(82,16,'82:16','ئۇلار ھەرگىز دوزاختىن ئايرىلمايدۇ. ');
INSERT INTO "saleh" VALUES(82,17,'82:17','قىيامەت كۈنىنىڭ (دەھشەتلىكلىكىنى) قانداقلىقىنى قانداق بىلەلەيسەن؟ ');
INSERT INTO "saleh" VALUES(82,18,'82:18','قىيامەت كۈنىنىڭ (دەھشەتلىكلىكىنى) قانداقلىقىنى قانداق بىلەلەيسەن؟ ');
INSERT INTO "saleh" VALUES(82,19,'82:19',' ئۇ كۈندە بىر ئادەمنىڭ يەنە بىر ئادەمگە ياردەم بېرىش قولىدىن كەلمەيدۇ، بۇ كۈندە ھۆكۈم ئاللاھقا خاستۇر.');
INSERT INTO "saleh" VALUES(83,1,'83:1','(ئۆلچەمدە ۋە تارازىدا) كەم بەرگۈچىلەرگە ۋاي!');
INSERT INTO "saleh" VALUES(83,2,'83:2',' ئۇلار كىشىلەردىن ئۆلچەپ ئالغان چاغدا، تولۇق ئالىدۇ.');
INSERT INTO "saleh" VALUES(83,3,'83:3','كىشىلەرگە ئۆلچەپ ياكى تارتىپ بەرگەن چاغدا، كەم بېرىدۇ.');
INSERT INTO "saleh" VALUES(83,4,'83:4','ئۇلار بۈيۈك بىر كۈندە تىرىلىدىغانلىقىغا ئىشەنمەمدۇ[4ـ5].');
INSERT INTO "saleh" VALUES(83,5,'83:5','ئۇلار بۈيۈك بىر كۈندە تىرىلىدىغانلىقىغا ئىشەنمەمدۇ[4ـ5].');
INSERT INTO "saleh" VALUES(83,6,'83:6',' ئۇ كۈندە ئىنسانلار ئالەملەرنىڭ پەرۋەردىگارىنىڭ ھۇزۇرىدا تىك تۇرىدۇ');
INSERT INTO "saleh" VALUES(83,7,'83:7','ئۇلار (تارازىدا، ئۆلچەمدە) كەم بېرىشتىن يانسۇن، يامان ئادەملەرنىڭ نامە ـ ئەمالى چوقۇم سىججىندا بولىدۇ.');
INSERT INTO "saleh" VALUES(83,8,'83:8','سىججىن دېگەن نېمە؟ ئۇنى قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(83,9,'83:9',' ئۇ (يامان ئادەملەرنىڭ قىلمىشلىرى) خاتىرىلەنگەن بىر دەپتەردۇر.');
INSERT INTO "saleh" VALUES(83,10,'83:10','بۇ كۈندە ئىنكار قىلغۇچىلارغا ۋاي! ');
INSERT INTO "saleh" VALUES(83,11,'83:11','ئۇلار قىيامەت كۈنىنى ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(83,12,'83:12',' ئۇنى پەقەت (كۇفرىدا) ھەددىدىن ئاشقان، ئىنتايىن گۇناھكار ئادەملا ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(83,13,'83:13','ئۇنىڭغا بىزنىڭ ئايەتلىرىمىز تىلاۋەت قىلىنسا، ئۇ: «(بۇ) قەدىمكىلەرنىڭ ھېكايىلىرىدۇر» دەيدۇ.');
INSERT INTO "saleh" VALUES(83,14,'83:14','ھەرگىز ئۇنداق ئەمەس، بەلكى ئۇلارنىڭ گۇناھلىرى تۈپەيلىدىن دىللىرى قارىيىپ كەتكەن.');
INSERT INTO "saleh" VALUES(83,15,'83:15','ھەرگىز ئۇنداق ئەمەس، ھەقىقەتەن ئۇلار بۇ كۈندە پەرۋەردىگارىنى كۆرۈشتىن مەنئى قىلىنغان. ');
INSERT INTO "saleh" VALUES(83,16,'83:16',' ئاندىن ئۇلار ھەقىقەتەن دوزاخقا داخىل بولغۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(83,17,'83:17',' ئاندىن (ئۇلارغا): «مانا بۇ سىلەر ئىنكار قىلغان نەرسە (يەنى ئازاب) دۇر» دېيىلىدۇ.');
INSERT INTO "saleh" VALUES(83,18,'83:18','راستلا ياخشى بەندىلەرنىڭ نامە ـ ئەمالى شەك ـ شۈبھىسىز ئىللىيوندا بولىدۇ.');
INSERT INTO "saleh" VALUES(83,19,'83:19',' ئىللىيوننىڭ نېمە ئىكەنلىكىنى سەن قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(83,20,'83:20','(ئۇ) ياخشىلارنىڭ ئەمەللىرى خاتىرىلەنگەن دەپتەردۇر.');
INSERT INTO "saleh" VALUES(83,21,'83:21',' ئۇنىڭغا ئاللاھنىڭ يېقىن پەرىشتىلىرى گۇۋاھ بولىدۇ.');
INSERT INTO "saleh" VALUES(83,22,'83:22',' ياخشىلار نېمەت ئىچىدە (يەنى جەننەتتە) بولىدۇ.');
INSERT INTO "saleh" VALUES(83,23,'83:23',' ئۇلار تەختلەر ئۈستىدە تۇرۇپ (ئاللاھنىڭ جەننەتتە ئۇلارغا بەرگەن تۈرلۈك نېمەتلىرىگە) قارايدۇ.');
INSERT INTO "saleh" VALUES(83,24,'83:24','ئۇلارغا قارايدىغان بولساڭ، نېمەتنىڭ (ئۇلارنىڭ چىرايلىرىدىن چىقىپ تۇرغان) ئەسىرىنى تونۇيسەن.');
INSERT INTO "saleh" VALUES(83,25,'83:25',' ئۇلارغا ئاغزى پېچەتلەنگەن ساپ شاراب سۇنۇلىدۇ،');
INSERT INTO "saleh" VALUES(83,26,'83:26','ئەڭ ئاخىرىقى يۇتۇمىدىمۇ ئۇنىڭدىن ئىپارنىڭ ھىدى كېلىپ تۇرىدۇ. قىزىققۇچىلار شۇنىڭغا قىزىقسۇن!');
INSERT INTO "saleh" VALUES(83,27,'83:27','ئۇنىڭغا (يەنى ساپ شارابقا) تەسنىمنىڭ (سۈيى) ئارىلاشتۇرۇلغان.');
INSERT INTO "saleh" VALUES(83,28,'83:28',' تەسنىم جەننەتتىكى بىر بۇلاق بولۇپ، ئۇنىڭدىن ئاللاھنىڭ يېقىن بەندىلىرى ئىچىدۇ.');
INSERT INTO "saleh" VALUES(83,29,'83:29',' كاپىرلار ھەقىقەتەن مۆمىنلەرنى (مەسخىرە قىلىپ) كۈلەتتى.');
INSERT INTO "saleh" VALUES(83,30,'83:30','ئۆيلىرىگە قايتقاندا (مۆمىنلەرنىڭ غەيۋىتىنى قىلىپ) مەززە قىلغان ھالدا قايتىشاتتى.');
INSERT INTO "saleh" VALUES(83,31,'83:31','مۆمىنلەرنى كۆرگەندە: «بۇلار شەكسىز ئاداشقانلار» دېيىشەتتى. ');
INSERT INTO "saleh" VALUES(83,32,'83:32','ئۇلار مۆمىنلەرنى كۈزىتىشكە ئەۋەتىلگىنى يوق.');
INSERT INTO "saleh" VALUES(83,33,'83:33',' ئۇلار مۆمىنلەرنى كۈزىتىشكە ئەۋەتىلگىنى يوق.');
INSERT INTO "saleh" VALUES(83,34,'83:34',' بۈگۈن (يەنى قىيامەت كۈنى) مۆمىنلەر كاپىرلاردىن كۈلىدۇ،');
INSERT INTO "saleh" VALUES(83,35,'83:35','(كاپىرلار) نىڭ ھالىغا (ئېسىل) تەختلەر ئۈستىدە ئولتۇرۇپ نەزەر تاشلايدۇ.');
INSERT INTO "saleh" VALUES(83,36,'83:36','(كاپىرلار مۆمىنلەرنى) مەسخىرە قىلغان قىلمىشلىرىنىڭ جازاسىنى تارتتىمۇ؟');
INSERT INTO "saleh" VALUES(84,1,'84:1','ئاسمان يېرىلغان، ');
INSERT INTO "saleh" VALUES(84,2,'84:2',' پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاپ ئىتائەت قىلغان، پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاشقا، ئىتائەت قىلىشقا لايىق بولغان چاغدا،');
INSERT INTO "saleh" VALUES(84,3,'84:3','زېمىن سوزۇلغان، ');
INSERT INTO "saleh" VALUES(84,4,'84:4','قوينىدىكى نەرسىلەرنى (يەنى ئۆلۈكلەرنى، مەدەنلەرنى) سىرتقا چىقىرىپ قۇرۇقدىلىپ قالغان،');
INSERT INTO "saleh" VALUES(84,5,'84:5','پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاپ ئىتائەت قىلغان، پەرۋەردىگارىنىڭ ئەمرىنى ئاڭلاشقا، ئىتائەت قىلىشقا لايىق بولغان چاغدا (ئىنسان خىيالىغا كەلمىگەن دەھشەتلىك ئەھۋاللارغا يولۇقىدۇ).');
INSERT INTO "saleh" VALUES(84,6,'84:6',' ئى ئىنسان! سەن ھەقىقەتەن پەرۋەردىگارىڭغا مۇلاقات بولغىنىڭغا قەدەر (ئۆز ئىشىڭدا) سەئيى ـ ئىجتىھات قىلىسەن (يەنى ئەمەلىڭنىڭ نەتىجىسىنى كۆرىسەن).');
INSERT INTO "saleh" VALUES(84,7,'84:7',' نامە ـ ئەمالى ئوڭ تەرىپىدىن بېرىلگەن ئادەمدىن ئاسان ھېساب ئېلىنىدۇ[7ـ8].');
INSERT INTO "saleh" VALUES(84,8,'84:8',' نامە ـ ئەمالى ئوڭ تەرىپىدىن بېرىلگەن ئادەمدىن ئاسان ھېساب ئېلىنىدۇ[7ـ8].');
INSERT INTO "saleh" VALUES(84,9,'84:9',' (ئۇ جەننەتتىكى) ئائىلىسىگە خۇشال ـ خۇرام قايتىدۇ.');
INSERT INTO "saleh" VALUES(84,10,'84:10',' نامە ـ ئەمالى ئارقا تەرىپىدىن بېرىلگەن ئادەم');
INSERT INTO "saleh" VALUES(84,11,'84:11','«ۋاي» دەپ توۋلايدۇ (يەنى ئۆلۈمنى ئارزۇ قىلىدۇ)،');
INSERT INTO "saleh" VALUES(84,12,'84:12','دوزاخقا كىرىدۇ.');
INSERT INTO "saleh" VALUES(84,13,'84:13','چۈنكى ئۇ (دۇنيادىكى چېغىدا) ئائىلىسىدە شاد ـ خۇرام ئىدى..');
INSERT INTO "saleh" VALUES(84,14,'84:14','ئۇ پەرۋەردىگارىنىڭ دەرگاھىغا قايتمايمەن دەپ گۇمان قىلدى.');
INSERT INTO "saleh" VALUES(84,15,'84:15',' ئۇنداق ئەمەس، (يەنى ئۇ پەرۋەردىگارىنىڭ دەرگاھىغا قايتىدۇ). پەرۋەردىگارى ھەقىقەتەن ئۇنى كۆرۈپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(84,16,'84:16',' شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى[16ـ18]،');
INSERT INTO "saleh" VALUES(84,17,'84:17',' شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى[16ـ18]،');
INSERT INTO "saleh" VALUES(84,18,'84:18',' شەپەق (يەنى كۈن پاتقاندىن كېيىنكى ئۇپۇقتىكى قىزىللىق) بىلەن، كېچە ۋە ئۇنىڭ قاراڭغۇلۇقى باسقان نەرسىلەر بىلەن، نۇرى كامالەتكە يەتكەن ئاي (يەنى تولۇن ئاي) بىلەن قەسەمكى[16ـ18]،');
INSERT INTO "saleh" VALUES(84,19,'84:19',' سىلەر جەزمەن بىر ھالدىن يەنە بىر ھالغا يۆتكىلىپ تۇرىسىلەر.');
INSERT INTO "saleh" VALUES(84,20,'84:20',' ئۇلار (يەنى مۇشرىكلار) نېمىشقا ئىمان ئېيتمايدۇ؟');
INSERT INTO "saleh" VALUES(84,21,'84:21','ئۇلارنىڭ يېنىدا قۇرئان ئوقۇلغان چاغدا نېمىشقا سەجدە قىلمايدۇ؟');
INSERT INTO "saleh" VALUES(84,22,'84:22','بەلكى كاپىرلار (ھەقىقەتنى) ئىنكار قىلىدۇ.');
INSERT INTO "saleh" VALUES(84,23,'84:23',' ئۇلارنىڭ دىللىرىدىكى يوشۇرۇن نەرسىلەرنى ئاللاھ ئوبدان بىلىدۇ.');
INSERT INTO "saleh" VALUES(84,24,'84:24','ئۇلارغا قاتتىق ئازاب بىلەن خۇشخەۋەر بەرگىن.');
INSERT INTO "saleh" VALUES(84,25,'84:25','پەقەت ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغان كىشىلەر ئۈزۈلمەس ساۋابقا ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(85,1,'85:1','بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى[1ـ3]،');
INSERT INTO "saleh" VALUES(85,2,'85:2','بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى[1ـ3]،');
INSERT INTO "saleh" VALUES(85,3,'85:3','بۇرۇجلارنىڭ ئىگىسى بولغان ئاسمان بىلەن، قىيامەت كۈنى بىلەن، جۈمە كۈنى بىلەن، ھارپا كۈنى بىلەن قەسەمكى[1ـ3]،');
INSERT INTO "saleh" VALUES(85,4,'85:4','ئورەكلەرنىڭ ئىگىللىرىگە لەنەت بولسۇن.4');
INSERT INTO "saleh" VALUES(85,5,'85:5',' ئۇ ئورەكلەر لاۋۇلداپ تۇرغان ئوتلار بىلەن تولدۇرۇلغان ئىدى. ');
INSERT INTO "saleh" VALUES(85,6,'85:6',' ئەينى ۋاقىتتا ئۇلار ئورەكلەر ئۈستىدە ئولتۇرۇپ ئۆزلىرىنىڭ مۆمىنلەرگە قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇراتتى[6ـ7]،');
INSERT INTO "saleh" VALUES(85,7,'85:7',' ئەينى ۋاقىتتا ئۇلار ئورەكلەر ئۈستىدە ئولتۇرۇپ ئۆزلىرىنىڭ مۆمىنلەرگە قىلىۋاتقان ئىشلىرىنى كۆرۈپ تۇراتتى[6ـ7]،');
INSERT INTO "saleh" VALUES(85,8,'85:8',' ئۇلار مۆمىنلەرنى پەقەت غالىب، مەدھىيەگە لايىق ئاللاھقا ئىمان ئېيتقانلىقلىرى ئۈچۈنلا يامان كۆردى. ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر. ئاللاھ ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر[8ـ9].');
INSERT INTO "saleh" VALUES(85,9,'85:9',' ئۇلار مۆمىنلەرنى پەقەت غالىب، مەدھىيەگە لايىق ئاللاھقا ئىمان ئېيتقانلىقلىرى ئۈچۈنلا يامان كۆردى. ئاسمانلارنىڭ ۋە زېمىننىڭ پادىشاھلىقى ئاللاھقا خاستۇر. ئاللاھ ھەممە نەرسىنى كۆرۈپ تۇرغۇچىدۇر[8ـ9].');
INSERT INTO "saleh" VALUES(85,10,'85:10',' مۆمىن ئەر ۋە مۆمىن ئاياللارغا زىيانكەشلىك قىلغاندىن كېيىن كۇفرىدىن قايتمىغانلار، شۈبھىسىزكى، جەھەننەمنىڭ ئازابىغا دۇچار بولىدۇ، ئۇلارغا ئوت بىلەن ئازاب قىلىنىدۇ.');
INSERT INTO "saleh" VALUES(85,11,'85:11',' ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار، شۈبھىسىزكى، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەرگە سازاۋەر بولىدۇ. بۇ چوڭ بەختتۇر.');
INSERT INTO "saleh" VALUES(85,12,'85:12','پەرۋەردىگارىڭنىڭ جازاسى شەك ـ شۈبھىسىز قاتتىقتۇر.');
INSERT INTO "saleh" VALUES(85,13,'85:13',' ئاللاھ (خالايىقنى) ھەقىقەتەن يوقتىن بار قىلىدۇ (يەنى ئۆلگەندىن كېيىن ئۇلارنى) تىرىلدۈرىدۇ.');
INSERT INTO "saleh" VALUES(85,14,'85:14','ئاللاھ (مۆمىن بەندىلەرنىڭ گۇناھىنى) مەغپىرەت قىلغۇچىدۇر. (ئۇلارنى) دوست تۇتقۇچىدۇر، ');
INSERT INTO "saleh" VALUES(85,15,'85:15','ئەرشنىڭ ئىگىسىدۇر، ناھايىتى ئۇلۇغدۇر،');
INSERT INTO "saleh" VALUES(85,16,'85:16',' خالىغىنىنى قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(85,17,'85:17','(ئى مۇھەممەد!) سەن لەشكەرلەرنىڭ ـ پىرئەۋن ۋە سەمۇدنىڭ قىسسەسىدىن خەۋەر تاپتىڭمۇ؟[17ـ18]');
INSERT INTO "saleh" VALUES(85,18,'85:18','(ئى مۇھەممەد!) سەن لەشكەرلەرنىڭ ـ پىرئەۋن ۋە سەمۇدنىڭ قىسسەسىدىن خەۋەر تاپتىڭمۇ؟[17ـ18]');
INSERT INTO "saleh" VALUES(85,19,'85:19',' (قۇرەيش مۇشرىكلىرى ئاشۇ كاپىرلارغا نازىل بولغان ئازابتىن ئىبرەت ئالماي) داۋاملىق ئىنكار قىلىشىۋاتىدۇ.');
INSERT INTO "saleh" VALUES(85,20,'85:20',' ئاللاھ كاپىرلارنىڭ ئەتراپىنى قورشاپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(85,21,'85:21','بەلكى (ئۇلار ئىنكار قىلغان نەرسە) لەۋھۇلمەھپۇزدا ساقلانغان ئۇلۇغ قۇرئاندۇر[21ـ22].');
INSERT INTO "saleh" VALUES(85,22,'85:22','بەلكى (ئۇلار ئىنكار قىلغان نەرسە) لەۋھۇلمەھپۇزدا ساقلانغان ئۇلۇغ قۇرئاندۇر[21ـ22].');
INSERT INTO "saleh" VALUES(86,1,'86:1','ئاسمان بىلەن، تارىق (يەنى كېچىدە ئاشكارا بولغۇچى يۇلتۇز) بىلەن قەسەم قىلىمەن.');
INSERT INTO "saleh" VALUES(86,2,'86:2',' تارىقنىڭ نېمىلىكىنى بىلەمسەن؟');
INSERT INTO "saleh" VALUES(86,3,'86:3',' ئۇ يورۇق يۇلتۇزدۇر.');
INSERT INTO "saleh" VALUES(86,4,'86:4',' ھەر بىر ئادەم بىلەن (شۇ ئادەمنى) ساقلىغۇچى (پەرىشتە) بار.');
INSERT INTO "saleh" VALUES(86,5,'86:5',' ئىنسان نېمىدىن يارىتىلغانلىقىغا قارىسۇن.');
INSERT INTO "saleh" VALUES(86,6,'86:6',' ئۇ ئېتىلىپ چىققۇچى مەنىدىن يارىتىلدى.');
INSERT INTO "saleh" VALUES(86,7,'86:7',' ئۇ مەنى ئۇمۇرتقا بىلەن قوۋۇرغا سۆڭىكىنىڭ ئارىسىدىن چىقىدۇ. ');
INSERT INTO "saleh" VALUES(86,8,'86:8','شەك ـ شۈبھىسىزكى، ئاللاھ ئۇنى ئەسلىگە كەلتۈرۈشكە (يەنى تىرىلدۈرۈشكە) قادىردۇر.');
INSERT INTO "saleh" VALUES(86,9,'86:9','سىرلار ئاشكارا قىلىنىدىغان كۈندە (يەنى قىيامەتتە)،');
INSERT INTO "saleh" VALUES(86,10,'86:10','ئۇنىڭدىن (يەنى ئىنساندىن) (ئازابنى دەپئى قىلىدىغان) ھېچ كۈچ ۋە ياردەمچى يوقتۇر.');
INSERT INTO "saleh" VALUES(86,11,'86:11',' يامغۇرلۇق بۇلۇت بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(86,12,'86:12','يېرىلىپ (گىياھلارنى ئۈندۈرۈپ بېرىدىغان) زېمىن بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(86,13,'86:13','قۇرئان شەك ـ شۈبھىسىز (ھەق بىلەن باتىلنى ئايرىغۇچى) سۆزدۇر، ');
INSERT INTO "saleh" VALUES(86,14,'86:14','ئۇ چاقچاق ئەمەستۇر.');
INSERT INTO "saleh" VALUES(86,15,'86:15','ئۇلار (يەنى مۇشرىكلار) (قۇرئاننى يوققا چىقىرىش ئۈچۈن) ھەقىقەتەن ھىيلە ـ مىكىر ئىشلىتىدۇ.');
INSERT INTO "saleh" VALUES(86,16,'86:16','مەن ئۇلارنىڭ ھىيلە ـ مىكرىگە تاقابىل تۇرىمەن.');
INSERT INTO "saleh" VALUES(86,17,'86:17',' (ئى مۇھەممەد!) كاپىرلارنىڭ ھالاك بولۇشىغا ئالدىرىمىغىن، ئۇلارغا ئازراق مۆھلەت بەرگىن (ئۇزاققا قالماي ئۇلارغا قانداق قىلغانلىقىمنى كۆرىسەن).');
INSERT INTO "saleh" VALUES(87,1,'87:1','ھەممىدىن ئۈستۈن پەرۋەردىگارىڭنىڭ نامىنى پاك ئېتىقاد قىلغىن.');
INSERT INTO "saleh" VALUES(87,2,'87:2',' ئۇ (پۈتۈن مەخلۇقاتنى) ياراتتى، (ئۇلارنى) بېجىرىم (يەنى چىرايلىق ۋە مۇناسىپ شەكىلدە) قىلدى. ');
INSERT INTO "saleh" VALUES(87,3,'87:3','مەخلۇقاتقا پايدىلىق ئىشلارنى (تەقدىر قىلدى، ئۇلارغا پايدىلىنىش يوللىرىنى) كۆرسەتتى. ');
INSERT INTO "saleh" VALUES(87,4,'87:4','ئوت ـ چۆپلەرنى ئۆستۈرۈپ بەردى.');
INSERT INTO "saleh" VALUES(87,5,'87:5','ئاندىن ئۇلارنى قارامتۇل قۇرۇق ئوت ـ خەسكە ئايلاندۇردى.');
INSERT INTO "saleh" VALUES(87,6,'87:6',' (ئى مۇھەممەد!) ساڭا (قۇرئاننى) ئوقۇتىمىز، ئاللاھ (نەسخى) قىلماقچى بولغان نەرسىدىن باشقىنى ئۇنتۇمايسەن. ئاللاھ (بەندىلەرنىڭ) ئاشكارا ۋە يوشۇرۇن (سۆز ـ ھەرىكەتلىرىنى) بىلىپ تۇرىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(87,7,'87:7',' (ئى مۇھەممەد!) ساڭا (قۇرئاننى) ئوقۇتىمىز، ئاللاھ (نەسخى) قىلماقچى بولغان نەرسىدىن باشقىنى ئۇنتۇمايسەن. ئاللاھ (بەندىلەرنىڭ) ئاشكارا ۋە يوشۇرۇن (سۆز ـ ھەرىكەتلىرىنى) بىلىپ تۇرىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(87,8,'87:8',' سېنى بىز ئاسان يولغا (يەنى ئىسلام شەرىئىتىگە) مۇۋەپپەق قىلىمىز.');
INSERT INTO "saleh" VALUES(87,9,'87:9','ۋەز ـ نەسىھەتنىڭ پايدىسى بولىدىغان بولسا، (كىشىلەرگە قۇرئان بىلەن) ۋەز ـ نەسىھەت قىلغىن.');
INSERT INTO "saleh" VALUES(87,10,'87:10',' ئاللاھتىن (قورققان) ئادەم ۋەز ـ نەسىھەتنى قوبۇل قىلىدۇ.');
INSERT INTO "saleh" VALUES(87,11,'87:11','ئەڭ شەقى ئادەم ۋەز ـ نەسىھەتتىن قاچىدۇ.');
INSERT INTO "saleh" VALUES(87,12,'87:12','ئۇ كاتتا ئوتقا (يەنى دوزاخقا) كىرىدۇ.');
INSERT INTO "saleh" VALUES(87,13,'87:13',' ئاندىن ئۇ ئوتتا ئۆلمەيدۇ ۋە (ئوبدان) ياشىمايدۇ.');
INSERT INTO "saleh" VALUES(87,14,'87:14',' (كۇفرىدىن ۋە گۇناھتىن) پاك بولغان ئادەم مەقسىتىگە يەتتى.');
INSERT INTO "saleh" VALUES(87,15,'87:15',' ئۇ پەرۋەردىگارىنىڭ نامىنى ياد ئەتتى، ئاندىن ناماز ئوقۇدى. ');
INSERT INTO "saleh" VALUES(87,16,'87:16','سىلەر دۇنيا تىرىكچىلىكىنى ئارتۇق كۆرىسىلەر.');
INSERT INTO "saleh" VALUES(87,17,'87:17',' ئاخىرەت ياخشىدۇر ۋە باقىيدۇر.');
INSERT INTO "saleh" VALUES(87,18,'87:18',' بۇ (يەنى بۇ سۈرىدىكى ۋەزلەر) شەك ـ شۈبھىسىز بۇرۇنقى كىتابلاردا ـ ئىبراھىم ۋە مۇسالارنىڭ كىتابلىرىدا باردۇر[18ـ19]. ');
INSERT INTO "saleh" VALUES(87,19,'87:19',' بۇ (يەنى بۇ سۈرىدىكى ۋەزلەر) شەك ـ شۈبھىسىز بۇرۇنقى كىتابلاردا ـ ئىبراھىم ۋە مۇسالارنىڭ كىتابلىرىدا باردۇر[18ـ19]. ');
INSERT INTO "saleh" VALUES(88,1,'88:1','(ئى مۇھەممەد!) ساڭا ھەقىقەتەن قىيامەتنىڭ خەۋىرى كەلدى.');
INSERT INTO "saleh" VALUES(88,2,'88:2','بۇ كۈندە نۇرغۇن ئادەملەر خار بولغۇچىدۇر.');
INSERT INTO "saleh" VALUES(88,3,'88:3','ئۇلار دۇنيادا گۇناھ قىلغۇچىلاردۇر، (ئاخىرەتتە) جاپا تارتقۇچىلاردۇر.');
INSERT INTO "saleh" VALUES(88,4,'88:4','ئۇلار ھارارىتى قاتتىق دوزاخقا كىرىدۇ. ');
INSERT INTO "saleh" VALUES(88,5,'88:5',' (سۇلىرى) قايناپ تۇرغان بۇلاقتىن سۇغىرىلىدۇ.');
INSERT INTO "saleh" VALUES(88,6,'88:6',' ئۇلار ئۈچۈن زەرىدىن (يەنى بەتبۇي، ئاچچىق تىكەندىن) باشقا يېمەكلىك بولمايدۇ.');
INSERT INTO "saleh" VALUES(88,7,'88:7','زەرى كىشىنى سەمرىتمەيدۇ، ئاچلىقنى پەسەيتمەيدۇ.');
INSERT INTO "saleh" VALUES(88,8,'88:8','بۇ كۈندە نۇرغۇن كىشىلەر باركى، ئۇلار نېمەت بىلەن خۇشالدۇر. ');
INSERT INTO "saleh" VALUES(88,9,'88:9','بۇ دۇنيادا قىلغان (سەئيى - ئەمەللىرىدىن) مەمنۇندۇر.');
INSERT INTO "saleh" VALUES(88,10,'88:10',' ئۇلار ئالىي جەننەتتىدۇر.');
INSERT INTO "saleh" VALUES(88,11,'88:11','ئۇ يەردە يامان سۆز ئاڭلىمايدۇ.');
INSERT INTO "saleh" VALUES(88,12,'88:12','ئۇ يەردە بۇلاقلار ئېقىپ تۇرىدۇ. ');
INSERT INTO "saleh" VALUES(88,13,'88:13',' ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت ـ رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار[13ـ16].');
INSERT INTO "saleh" VALUES(88,14,'88:14',' ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت ـ رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار[13ـ16].');
INSERT INTO "saleh" VALUES(88,15,'88:15',' ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت ـ رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار[13ـ16].');
INSERT INTO "saleh" VALUES(88,16,'88:16',' ئۇ يەردە ئېگىز تەختلەر، قاتار تىزىلغان قەدەھلەر، رەت ـ رەت قويۇلغان ياستۇقلار، سېلىنغان ئېسىل بىساتلار بار[13ـ16].');
INSERT INTO "saleh" VALUES(88,17,'88:17','ئۇلار قارىمامدۇكى، تۆگىلەر قانداق يارىتىلدى،');
INSERT INTO "saleh" VALUES(88,18,'88:18','ئاسمان قانداق ئېگىز قىلىندى،');
INSERT INTO "saleh" VALUES(88,19,'88:19','تاغلار قانداق تۇرغۇزۇلدى،');
INSERT INTO "saleh" VALUES(88,20,'88:20','زېمىن قانداق يېيىتىلدى.');
INSERT INTO "saleh" VALUES(88,21,'88:21',' سەن (ئۇلارغا) ۋەز ـ نەسىھەت قىلغىن، سەن پەقەت (ئۇلارغا) ۋەز ـ نەسىھەت قىلغۇچىسەن.');
INSERT INTO "saleh" VALUES(88,22,'88:22',' سەن ئۇلارغا مۇسەللەت (يەنى ئۇلارنى ئىمانغا مەجبۇرلىغۇچى) ئەمەسسەن.');
INSERT INTO "saleh" VALUES(88,23,'88:23',' لېكىن كىمكى (ۋەز ـ نەسىھەتتىن) يۈز ئۆرۈپ (ئاللاھنى) ئىنكار قىلىدىكەن،');
INSERT INTO "saleh" VALUES(88,24,'88:24','ئاللاھ ئۇنى قاتتىق ئازابلايدۇ.');
INSERT INTO "saleh" VALUES(88,25,'88:25','ئۇلار (ئۆلگەندىن كېيىن) ھەقىقەتەن بىزنىڭ دەرگاھىمىزغا قايتىدۇ.');
INSERT INTO "saleh" VALUES(88,26,'88:26','ئاندىن ئۇلاردىن ھېساب ئېلىشقا بىز مەسئۇلمىز. ');
INSERT INTO "saleh" VALUES(89,1,'89:1','سۈبھى بىلەن، ');
INSERT INTO "saleh" VALUES(89,2,'89:2','(زۇلھەججىنىڭ دەسلەپكى) ئون كېچىسى بىلەن، ');
INSERT INTO "saleh" VALUES(89,3,'89:3','قۇربانلىق كۈنى بىلەن، ھارپا كۈنى بىلەن، ');
INSERT INTO "saleh" VALUES(89,4,'89:4','(كائىناتنىڭ ئاجايىپ ھەرىكىتى بىلەن) ئۆتكەن كېچە بىلەن قەسەمكى، (كاپىرلارغا چوقۇم ئازاب قىلىمىز) ');
INSERT INTO "saleh" VALUES(89,5,'89:5','ئەقىل ئىگىسىگە بۇنىڭدا (يەنى يۇقىرقى نەرسىلەردە قانائەتلىنەرلىك)قەسەم بارمۇ؟. ');
INSERT INTO "saleh" VALUES(89,6,'89:6','پەرۋەردىگارىڭنىڭ ئاد ـ كۈچلۈك ئىرەمنى قانداق جازالىغانلىقىنى كۆرمىدىڭمۇ؟[6ـ7].');
INSERT INTO "saleh" VALUES(89,7,'89:7','پەرۋەردىگارىڭنىڭ ئاد ـ كۈچلۈك ئىرەمنى قانداق جازالىغانلىقىنى كۆرمىدىڭمۇ؟[6ـ7].');
INSERT INTO "saleh" VALUES(89,8,'89:8','بۇنداق ئادەملەر باشقا شەھەرلەردە يارىتىلمىغان ئىدى.');
INSERT INTO "saleh" VALUES(89,9,'89:9','شۇنىڭدەك ۋادىقورادا تاشلارنى كېسىپ (ئۆيلەرنى بىنا قىلغان) سەمۇدنى ئاللاھ قانداق جازالىدى.');
INSERT INTO "saleh" VALUES(89,10,'89:10','شۇنىڭدەك قۇزۇقلار ئىگىسى پىرئەۋننى قانداق جازالىدى.');
INSERT INTO "saleh" VALUES(89,11,'89:11','ئۇلار شەھەرلەردە (زۇلۇم قىلىشتا) ھەددىدىن ئاشتى.');
INSERT INTO "saleh" VALUES(89,12,'89:12','ئۇ يەرلەردە بۇزغۇنچىلىقنى كۆپ قىلدى.');
INSERT INTO "saleh" VALUES(89,13,'89:13','پەرۋەردىگارىڭ ئۇلارغا قاتتىق ئازابنى نازىل قىلدى.');
INSERT INTO "saleh" VALUES(89,14,'89:14','پەرۋەردىگارىڭ (بەندىلەرنى) ئەلۋەتتە كۆزىتىپ تۇرغۇچىدۇر.');
INSERT INTO "saleh" VALUES(89,15,'89:15','پەرۋەردىگارى ئىنساننى سىناپ ئەزىز قىلسا ۋە ئۇنىڭغا نېمەت بەرسە، ئۇ: «پەرۋەردىگارىم مېنى ئەزىز قىلدى» دەيدۇ.');
INSERT INTO "saleh" VALUES(89,16,'89:16','پەرۋەردىگارى ئىنساننى سىناپ رىزقىنى تار قىلسا، ئۇ: «پەرۋەردىگارىم مېنى خار قىلدى» دەيدۇ.');
INSERT INTO "saleh" VALUES(89,17,'89:17','ھەرگىز ئۇنداق ئەمەس، سىلەر يېتىمنى ھۆرمەت قىلمايسىلەر.');
INSERT INTO "saleh" VALUES(89,18,'89:18',' بىر ـ بىرىڭلارنى مىسكىنگە ئاش، نان بېرىشكە تەرغىب قىلمايسىلەر.');
INSERT INTO "saleh" VALUES(89,19,'89:19',' مىراسنى (ھالال- ھارام دېمەستىن) ھە دەپ يەيسىلەر.');
INSERT INTO "saleh" VALUES(89,20,'89:20','مالنى (ھېرىسلىق بىلەن) ناھايىتى دوست تۇتىسىلەر.');
INSERT INTO "saleh" VALUES(89,21,'89:21',' (ئى غاپىللار!) بۇنىڭدىن يېنىڭلار، زېمىن ئارقىمۇئارقا تەۋرىتىلگەن چاغدا.');
INSERT INTO "saleh" VALUES(89,22,'89:22',' پەرۋەردىگارىڭ كەلگەن ۋە پەرىشتىلەر سەپ ـ سەپ بولۇپ كەلگەن چاغدا.');
INSERT INTO "saleh" VALUES(89,23,'89:23',' (گۇناھكارلارنى كۆرۈش ئۈچۈن) جەھەننەم كەلتۈرۈلگەن چاغدا، شۇ كۈندە ئىنسان (يەنى كاپىر) (دۇنيادىكى قىلمىشلىرىنى) ئەسلەيدۇ، (بۇ چاغدا) ئەسلىگەننىڭ ئۇنىڭغا نېمە پايدىسى بولسۇن.');
INSERT INTO "saleh" VALUES(89,24,'89:24','ئۇ ئېيتىدۇكى: «كاشكى مەن (دۇنيادىكى) ھايات ۋاقتىمدا ياخشى ئىشلارنى قىلغان بولسامچۇ!» ');
INSERT INTO "saleh" VALUES(89,25,'89:25','شۇ كۈندە ھەر ئەھەدى (ئاللاھقا ئاسىيلىق قىلغانلارنى) ئاللاھ جازالىغاندەك جازالىمايدۇ.');
INSERT INTO "saleh" VALUES(89,26,'89:26','ھەمدە ھېچ ئەھەدى ئاللاھ باغلىغاندەك باغلىمايدۇ. ');
INSERT INTO "saleh" VALUES(89,27,'89:27','ئى ئارام تاپقۇچى جان!');
INSERT INTO "saleh" VALUES(89,28,'89:28','سەن پەرۋەردىگارىڭدىن مەمنۇن، پەرۋەردىگارىڭ سەندىن رازى بولغان ھالدا، ئۇنىڭ دەرگاھىغا قايتقىن.');
INSERT INTO "saleh" VALUES(89,29,'89:29','سەن مېنىڭ (ياخشى) بەندىلىرىم قاتارىغا قوشۇلغىن.');
INSERT INTO "saleh" VALUES(89,30,'89:30',' مېنىڭ جەننىتىمگە كىرگىن. ');
INSERT INTO "saleh" VALUES(90,1,'90:1','بۇ شەھەر (يەنى مەككە) بىلەن قەسەم قىلىمەن.');
INSERT INTO "saleh" VALUES(90,2,'90:2','(ئى مۇھەممەد!) بۇ شەھەردە (ئۇرۇش قىلىش) ساڭا ھالالدۇر.');
INSERT INTO "saleh" VALUES(90,3,'90:3','ئادەم (ئەلەيھىسسالام) ۋە (ئۇنىڭ ياخشى) پەرزەنتلىرى بىلەن قەسەم قىلىمەن. ');
INSERT INTO "saleh" VALUES(90,4,'90:4',' بىز ئىنساننى ھەقىقەتەن جاپا ـ مۇشەققەت ئىچىدە قىلىپ ياراتتۇق.');
INSERT INTO "saleh" VALUES(90,5,'90:5','ئۇ ئۆزىگە ھېچ كىشىنىڭ كۈچى يەتمەيدۇ دەپ ئويلامدۇ؟');
INSERT INTO "saleh" VALUES(90,6,'90:6','ئۇ: «كۆپ مال سەرپ قىلدىم» دەيدۇ.');
INSERT INTO "saleh" VALUES(90,7,'90:7',' ئۇ ئۆزىنىڭ (مال) سەرپ قىلغانلىقىنى ھېچ كىشى كۆرمىدى دەپ ئويلامدۇ؟');
INSERT INTO "saleh" VALUES(90,8,'90:8','ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟[8ـ10] ');
INSERT INTO "saleh" VALUES(90,9,'90:9','ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟[8ـ10] ');
INSERT INTO "saleh" VALUES(90,10,'90:10','ئىنسان ئۈچۈن ئىككى كۆز، بىر تىل، ئىككى كالپۇك ياراتمىدۇقمۇ؟ ئۇنىڭغا ياخشى يول بىلەن يامان يولنى كۆرسەتمىدۇقمۇ؟[8ـ10] ');
INSERT INTO "saleh" VALUES(90,11,'90:11',' ئۇ داۋان ئاشمىدى.');
INSERT INTO "saleh" VALUES(90,12,'90:12','داۋان ئېشىشىنىڭ نېمىلىكىنى قانداق بىلەتتىڭ؟');
INSERT INTO "saleh" VALUES(90,13,'90:13','(داۋان ئېشىش دېگەن) قۇل ئازاد قىلماقتۇر،');
INSERT INTO "saleh" VALUES(90,14,'90:14','ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر[14ـ16].');
INSERT INTO "saleh" VALUES(90,15,'90:15','ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر[14ـ16].');
INSERT INTO "saleh" VALUES(90,16,'90:16','ياكى ئاچارچىلىق كۈندە تۇغقانچىلىقى بولغان يېتىمگە ياكى توپىدا ياتقان مىسكىن (يەنى ھالى ناچار كەمبەغەل) گە تاماق بەرمەكتۇر[14ـ16].');
INSERT INTO "saleh" VALUES(90,17,'90:17','شۇنىڭ بىلەن بىللە (يەنى يۇقىرىقىلارنى قىلىش بىلەن بىللە) ئىمان ئېيتقانلاردىن، ئۆزئارا سەۋر قىلىشقا تەۋسىيە قىلىشقانلاردىن، ئۆزئارا مەرھەمەت قىلىشقا تەۋسىيە قىلىشقانلاردىن بولماقتۇر.');
INSERT INTO "saleh" VALUES(90,18,'90:18','ئەنە شۇلار (يەنى يۇقىرىدىكى ئىشلارنى قىلغان كىشىلەر) سائادەتمەن كىشىلەردۇر.');
INSERT INTO "saleh" VALUES(90,19,'90:19','بىزنىڭ ئايەتلىرىمىزنى ئىنكار قىلغانلار بەتبەختلەردۇر.');
INSERT INTO "saleh" VALUES(90,20,'90:20','ئۇلار دوزاخقا سولىنىدۇ. ');
INSERT INTO "saleh" VALUES(91,1,'91:1','قۇياش ۋە ئۇنىڭ نۇرى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(91,2,'91:2',' قۇياشقا ئەگەشكەن چاغدىكى ئاي بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(91,3,'91:3','قۇياشنى ئاشكارا قىلغان كۈندۈز بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(91,4,'91:4','قۇياشنى يوشۇرغان چاغدىكى كېچە بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(91,5,'91:5',' ئاسمان بىلەن ۋە ئۇنى بىنا قىلغان زات بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(91,6,'91:6',' زېمىن بىلەن ۋە ئۇنى بىنا قىلغان زات بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(91,7,'91:7','نەپس بىلەن ۋە ئۇنى چىرايلىق قىلىپ ياراتقان، ئۇنىڭغا ياخشى ـ يامانلىقنى بىلدۈرگەن زات بىلەن قەسەمكى[7ـ8]،');
INSERT INTO "saleh" VALUES(91,8,'91:8','نەپس بىلەن ۋە ئۇنى چىرايلىق قىلىپ ياراتقان، ئۇنىڭغا ياخشى ـ يامانلىقنى بىلدۈرگەن زات بىلەن قەسەمكى[7ـ8]،');
INSERT INTO "saleh" VALUES(91,9,'91:9','نەپسىنى پاك قىلغان ئادەم چوقۇم مۇرادىغا يېتىدۇ.');
INSERT INTO "saleh" VALUES(91,10,'91:10','نەپسىنى (كۇفرى ۋە پىسقى ـ فۇجۇر بىلەن) كەمسىتكەن ئادەم چوقۇم نائۈمىد بولىدۇ.');
INSERT INTO "saleh" VALUES(91,11,'91:11','سەمۇد ھەددىدىن ئاشقانلىقى ئۈچۈن (پەيغەمبەرنى) ئىنكار قىلدى.');
INSERT INTO "saleh" VALUES(91,12,'91:12',' ئەينى زاماندا سەمۇدنىڭ ئەڭ بەتبەخت ئادىمى (مۆجىزە بولۇپ كەلگەن تۆگىنى) ئۆلتۈرۈشكە ئالدىرىدى.');
INSERT INTO "saleh" VALUES(91,13,'91:13','ئۇلارغا ئاللاھنىڭ پەيغەمبىرى (يەنى سالىھ ئەلەيھىسسالام): «ئاللاھنىڭ تۆگىسىگە چېقىلماڭلار، ئۇنى سۇ ئىچىشتىن توسماڭلار» دېدى.');
INSERT INTO "saleh" VALUES(91,14,'91:14','ئۇلار پەيغەمبەرنى (يەنى سالىھ ئەلەيھىسسالامنى) يالغانچى قىلدى، تۆگىنى ئۆلتۈردى، گۇناھى تۈپەيلىدىن ئاللاھ ئۇلارنى تەلتۆكۈس ھالاك قىلدى، ئومۇميۈزلۈك ھالاك قىلدى.');
INSERT INTO "saleh" VALUES(91,15,'91:15','ئاللاھ ئۇنىڭ (يەنى ئۇلارنى ھالاك قىلغانلىقنىڭ) ئاقىۋىتىدىن قورقمايدۇ.');
INSERT INTO "saleh" VALUES(92,1,'92:1','قاراڭغۇلۇقى ئالەمنى قاپلىغان كېچە بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(92,2,'92:2','يورۇغان چاغدىكى كۈندۈز بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(92,3,'92:3','ئەر ـ ئايالنى ياراتقان زات ئاللاھ بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(92,4,'92:4',' سىلەرنىڭ ئەمەلىڭلار شەك ـ شۈبھىسىز خىلمۇخىلدۇر.');
INSERT INTO "saleh" VALUES(92,5,'92:5',' (پۇل ـ مېلىنى ئاللاھنىڭ رازىلىقى ئۈچۈن) سەرپ قىلغان، تەقۋادارلىق قىلغان، (كەلىمە تەۋھىدنى) تەستىق قىلغان ئادەمگە كەلسەك[5ـ6]، ');
INSERT INTO "saleh" VALUES(92,6,'92:6',' (پۇل ـ مېلىنى ئاللاھنىڭ رازىلىقى ئۈچۈن) سەرپ قىلغان، تەقۋادارلىق قىلغان، (كەلىمە تەۋھىدنى) تەستىق قىلغان ئادەمگە كەلسەك[5ـ6]، ');
INSERT INTO "saleh" VALUES(92,7,'92:7','ئۇنىڭغا بىز ياخشى يولنى مۇيەسسەر قىلىپ بېرىمىز.');
INSERT INTO "saleh" VALUES(92,8,'92:8','بېخىللىق قىلىپ (ئاللاھنىڭ ساۋابىدىن) ئۆزىنى بىھاجەت ھېسابلىغان، كەلىمە تەۋھىدنى ئىنكار قىلغان ئادەمگە كەلسەك[8ـ9]،');
INSERT INTO "saleh" VALUES(92,9,'92:9','بېخىللىق قىلىپ (ئاللاھنىڭ ساۋابىدىن) ئۆزىنى بىھاجەت ھېسابلىغان، كەلىمە تەۋھىدنى ئىنكار قىلغان ئادەمگە كەلسەك[8ـ9]،');
INSERT INTO "saleh" VALUES(92,10,'92:10',' بىز ئۇنىڭغا يامان يولنى مۇيەسسەر قىلىپ بېرىمىز.');
INSERT INTO "saleh" VALUES(92,11,'92:11',' ئۇ (دوزاخقا) تاشلانغان چاغدا، ئۇنىڭ مېلى ھېچ نەرسىگە ئەسقاتمايدۇ.');
INSERT INTO "saleh" VALUES(92,12,'92:12',' توغرا يولنى كۆرسىتىشكە شەك ـ شۈبھىسىز بىز مەسئۇلمىز.');
INSERT INTO "saleh" VALUES(92,13,'92:13',' دۇنيا ۋە ئاخىرەت شەك ـ شۈبھىسىز بىزنىڭ ئىلكىمىزدىدۇر.');
INSERT INTO "saleh" VALUES(92,14,'92:14','سىلەرنى لاۋۇلداپ تۇرغان ئوتتىن ئاگاھلاندۇردۇم.');
INSERT INTO "saleh" VALUES(92,15,'92:15','ئۇنىڭغا پەقەت (ھەقنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرۈگەن ئادەملا كىرىدۇ[15ـ16].');
INSERT INTO "saleh" VALUES(92,16,'92:16','ئۇنىڭغا پەقەت (ھەقنى) ئىنكار قىلغان، (ئىماندىن) يۈز ئۆرۈگەن ئادەملا كىرىدۇ[15ـ16].');
INSERT INTO "saleh" VALUES(92,17,'92:17',' پاكلىنىش ئۈچۈن مال ـ مۈلكىنى (ياخشىلىق يوللىرىغا) سەرپ قىلىدىغان ئادەم ئۇنىڭدىن (يەنى ئوتتىن) يىراق قىلىنىدۇ[17ـ18].');
INSERT INTO "saleh" VALUES(92,18,'92:18',' پاكلىنىش ئۈچۈن مال ـ مۈلكىنى (ياخشىلىق يوللىرىغا) سەرپ قىلىدىغان ئادەم ئۇنىڭدىن (يەنى ئوتتىن) يىراق قىلىنىدۇ[17ـ18].');
INSERT INTO "saleh" VALUES(92,19,'92:19',' ئۇنىڭ قىلغان ياخشىلىقى بىراۋنىڭ ياخشىلىقىنى ياندۇرۇش ئۈچۈن ئەمەس، پەقەت ئۇلۇغ پەرۋەردىگارىنىڭ رازىلىقى ئۈچۈندۇر[19ـ20].');
INSERT INTO "saleh" VALUES(92,20,'92:20',' ئۇنىڭ قىلغان ياخشىلىقى بىراۋنىڭ ياخشىلىقىنى ياندۇرۇش ئۈچۈن ئەمەس، پەقەت ئۇلۇغ پەرۋەردىگارىنىڭ رازىلىقى ئۈچۈندۇر[19ـ20].');
INSERT INTO "saleh" VALUES(92,21,'92:21','ئۇ (يەنى ئېھسان قىلغۇچى ئادەم) (پەرۋەردىگارىنىڭ بەرگەن مۇكاپاتىدىن) چوقۇم رازى بولىدۇ.');
INSERT INTO "saleh" VALUES(93,1,'93:1','چاشگاھ ۋاقتى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(93,2,'93:2',' قاراڭغۇلۇقى (ئەتراپنى قاپلىغان) چاغدىكى كېچە بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(93,3,'93:3',' (ئى مۇھەممەد!) پەرۋەردىگارىڭ سېنى تاشلىۋەتكىنىمۇ يوق، يامان كۆرۈپ قالغىنىمۇ يوق.');
INSERT INTO "saleh" VALUES(93,4,'93:4','ساڭا ئاخىرەت ئەلۋەتتە دۇنيادىن ياخشىدۇر،');
INSERT INTO "saleh" VALUES(93,5,'93:5',' پەرۋەردىگارىڭ ساڭا (ساۋاب، ھۆرمەت ۋە شاپائەت قاتارلىقلارنى) ئاتا قىلىدۇ، سەن مەمنۇن بولىسەن.');
INSERT INTO "saleh" VALUES(93,6,'93:6','ئۇ سېنى يېتىم بىلىپ پاناھ بەرمىدىمۇ؟');
INSERT INTO "saleh" VALUES(93,7,'93:7','قايمۇققان بىلىپ ھىدايەت قىلمىدىمۇ؟');
INSERT INTO "saleh" VALUES(93,8,'93:8','سېنى يوقسۇز بىلىپ (كىشىلەردىن) بىھاجەت قىلمىدىمۇ؟');
INSERT INTO "saleh" VALUES(93,9,'93:9','يېتىمنى خارلىما.');
INSERT INTO "saleh" VALUES(93,10,'93:10','سائىلغا قوپاللىق قىلما. ');
INSERT INTO "saleh" VALUES(93,11,'93:11','پەرۋەردىگارىڭنىڭ (ساڭا بەرگەن) نېمىتىنى (كىشىلەرگە) سۆزلەپ بەرگىن (چۈنكى نېمەتنى سۆزلەپ بەرگەنلىك نېمەتكە شۈكۈر قىلغانلىق بولىدۇ).');
INSERT INTO "saleh" VALUES(94,1,'94:1','(ئى مۇھەممەد!) سېنىڭ كۆكسىڭنى (ھىدايەت، ھېكمەت ۋە قۇرئاننىڭ نۇرى بىلەن) كەڭ قىلمىدۇقمۇ؟ ');
INSERT INTO "saleh" VALUES(94,2,'94:2','سېنىڭ بېلىڭنى ئېگىۋەتكەن ئېغىر يۈكنى ئۈستۈڭدىن ئېلىپ تاشلىدۇق[2ـ3].');
INSERT INTO "saleh" VALUES(94,3,'94:3','سېنىڭ بېلىڭنى ئېگىۋەتكەن ئېغىر يۈكنى ئۈستۈڭدىن ئېلىپ تاشلىدۇق[2ـ3].');
INSERT INTO "saleh" VALUES(94,4,'94:4','سېنىڭ شۆھرىتىڭنى ئۈستۈن قىلدۇق.');
INSERT INTO "saleh" VALUES(94,5,'94:5','ئەلۋەتتە ھەر بىر مۈشكۈللۈك بىلەن بىر ئاسانلىق بار.');
INSERT INTO "saleh" VALUES(94,6,'94:6',' ئەلۋەتتە شۇ مۈشكۈللۈك بىلەن يەنە بىر ئاسانلىق بار.');
INSERT INTO "saleh" VALUES(94,7,'94:7','(ئى مۇھەممەد! دەۋەتتىن) بوشىغان چېغىڭدا (ئاللاھقا) ئىبادەت قىلىشقا تىرىشقىن.');
INSERT INTO "saleh" VALUES(94,8,'94:8','پەرۋەردىگارىڭغا يۈزلەنگىن.');
INSERT INTO "saleh" VALUES(95,1,'95:1','ئەنجۈر بىلەن ۋە زەيتۇن بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(95,2,'95:2',' سىنا تېغى بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(95,3,'95:3','بۇ تىنچ شەھەر (يەنى مەككە مۇكەررەمە) بىلەن قەسەمكى،');
INSERT INTO "saleh" VALUES(95,4,'95:4','بىز ئىنساننى شەك ـ شۈبھىسىز ئەڭ چىرايلىق شەكىلدە ياراتتۇق.');
INSERT INTO "saleh" VALUES(95,5,'95:5','ئاندىن ئۇنى (چىرايلىق ياراتقانلىق نېمىتىمىزگە شۈكۈر قىلماي ئاسىيلىق قىلغانلىقتىن) دوزاخقا قايتۇردۇق. ');
INSERT INTO "saleh" VALUES(95,6,'95:6','پەقەت ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغانلار بۇنىڭدىن مۇستەسنا. بۇلارغا ئۈزۈلمەس ساۋاب بېرىلىدۇ.');
INSERT INTO "saleh" VALUES(95,7,'95:7','(ئى ئىنسان!) بۇنىڭدىن كېيىن (يەنى روشەن دەلىللەر بايان قىلىنغاندىن كېيىن) يەنە نېمىشقا قىيامەتنى ئىنكار قىلىسەن؟');
INSERT INTO "saleh" VALUES(95,8,'95:8','ئاللاھ ئەڭ ئادىل ھۆكۈم قىلغۇچى ئەمەسمۇ؟');
INSERT INTO "saleh" VALUES(96,1,'96:1','ياراتقان پەرۋەردىگارىڭنىڭ ئىسمى بىلەن ئوقۇغىن.');
INSERT INTO "saleh" VALUES(96,2,'96:2',' ئۇ ئىنساننى لەختە قاندىن ياراتتى.');
INSERT INTO "saleh" VALUES(96,3,'96:3','ئوقۇغىن، پەرۋەردىگارىڭ ئەڭ كەرەملىكتۇر.');
INSERT INTO "saleh" VALUES(96,4,'96:4',' ئۇ قەلەم بىلەن (خەت يېزىشنى) ئۆگەتتى.');
INSERT INTO "saleh" VALUES(96,5,'96:5','ئىنسانغا بىلمىگەن نەرسىلەرنى بىلدۈردى.');
INSERT INTO "saleh" VALUES(96,6,'96:6','شەك ـ شۈبھىسىز ئىنسان ئۆزىنى باي ساناپ (ئاللاھقا بويسۇنۇشتىن باش تارتىپ)، راستىنلا ھەددىدىن ئاشىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(96,7,'96:7','شەك ـ شۈبھىسىز ئىنسان ئۆزىنى باي ساناپ (ئاللاھقا بويسۇنۇشتىن باش تارتىپ)، راستىنلا ھەددىدىن ئاشىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(96,8,'96:8',' (ئى ئىنسان!) بارىدىغان يېرىڭ پەرۋەردىگارىڭنىڭ دەرگاھىدۇر (قىلمىشىڭغا قاراپ جازاغا ياكى مۇكاپاتقا ئېرىشىسەن).');
INSERT INTO "saleh" VALUES(96,9,'96:9',' ناماز ئوقۇۋاتقان بەندىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) توسقان ئادەمنىڭ (ھالىنى) ماڭا ئېيتىپ بەرسەڭچۇ؟[9ـ10]');
INSERT INTO "saleh" VALUES(96,10,'96:10',' ناماز ئوقۇۋاتقان بەندىنى (يەنى مۇھەممەد ئەلەيھىسسالامنى) توسقان ئادەمنىڭ (ھالىنى) ماڭا ئېيتىپ بەرسەڭچۇ؟[9ـ10]');
INSERT INTO "saleh" VALUES(96,11,'96:11','ماڭا (ئۇنىڭ ھالىنى) ئېيتىپ بەرسەڭچۇ؟ ئەگەر ئۇ (ھەددىدىن ئاشقۇچى) (نامازنى توسۇشنىڭ ئورنىغا) توغرا يولدا بولغان بولسا،');
INSERT INTO "saleh" VALUES(96,12,'96:12',' ياكى تەقۋادارلىققا ئەمر قىلغان بولسا (بۇ ئۇنىڭ ئۆزى ئۈچۈن ياخشى بولماسمىدى؟)');
INSERT INTO "saleh" VALUES(96,13,'96:13',' ماڭا ئېيتىپ بەرسەڭچۇ؟ ئەگەر ئۇ (قۇرئاننى) ئىنكار قىلسا، ئىماندىن (يۈز ئۆرۈسە)،');
INSERT INTO "saleh" VALUES(96,14,'96:14','ئاللاھنىڭ (ئۇنى) كۆرۈپ تۇرىدىغانلىقىنى بىلمىدىمۇ؟ ');
INSERT INTO "saleh" VALUES(96,15,'96:15','ئۇ گۇمراھلىقتىن يانسۇن، ئەگەر ئۇ بۇنىڭدىن يانمىسا، چوقۇم ئۇنىڭ كوكۇلىسىدىن ـ يالغانچى گۇناھكارنىڭ كوكۇلىسىدىن تۇتۇپ (جەھەننەمگە) سۆرەيمىز[15 ـ 16].');
INSERT INTO "saleh" VALUES(96,16,'96:16','ئۇ گۇمراھلىقتىن يانسۇن، ئەگەر ئۇ بۇنىڭدىن يانمىسا، چوقۇم ئۇنىڭ كوكۇلىسىدىن ـ يالغانچى گۇناھكارنىڭ كوكۇلىسىدىن تۇتۇپ (جەھەننەمگە) سۆرەيمىز[15 ـ 16].');
INSERT INTO "saleh" VALUES(96,17,'96:17','ئۇ ئۆزىنىڭ سورۇنداشلىرىنى ياردەمگە چاقىرسۇن!');
INSERT INTO "saleh" VALUES(96,18,'96:18',' بىز زابانىيىلەر (يەنى جەھەننەمنىڭ كۈچلۈك ـ قاتتىق قول پەرىشتىلىرى) نى چاقىرىمىز.');
INSERT INTO "saleh" VALUES(96,19,'96:19','(بۇ فاجىر گۇمراھلىقتىن) يانسۇن! ئۇنىڭغا ئىتائەت قىلمىغىن، (ئاللاھقا) سەجدە قىلغىن (شۇنىڭ بىلەن ئاللاھقا) يېقىنلىق ھاسىل قىلغىن.');
INSERT INTO "saleh" VALUES(97,1,'97:1','بىز قۇرئاننى ھەقىقەتەن شەبى قەدرىدە نازىل قىلدۇق.');
INSERT INTO "saleh" VALUES(97,2,'97:2',' شەبى قەدرىنىڭ نېمە ئىكەنلىكىنى بىلەمسەن؟ ');
INSERT INTO "saleh" VALUES(97,3,'97:3','شەبى قەدرى (شەرەپ ۋە پەزىلەتتە) مىڭ ئايدىن ئارتۇقتۇر.');
INSERT INTO "saleh" VALUES(97,4,'97:4','پەرىشتىلەر ۋە جىبرىئىل شۇ كېچىدە پەرۋەردىگارىنىڭ ئەمرى بىلەن (ئاللاھ تەقدىر قىلغان) بارلىق ئىش ئۈچۈن (زېمىنغا) چۈشىدۇ.');
INSERT INTO "saleh" VALUES(97,5,'97:5','شۇ كېچە تاڭ يورۇغانغا قەدەر پۈتۈنلەي تىنچ ـ ئامانلىقتىن ئىبارەتتۇر.');
INSERT INTO "saleh" VALUES(98,1,'98:1','ئەھلى كىتابتىن (يەنى يەھۇدىيلار ۋە ناسارالاردىن) كاپىر بولغانلار ۋە مۇشرىكلار ئۆزلىرىگە روشەن دەلىل كەلگەنگە قەدەر كۇفرىدىن ئايرىلغۇچى بولمىدى.');
INSERT INTO "saleh" VALUES(98,2,'98:2','(ئۇ روشەن دەلىل) پاك سەھىپىلەرنى تىلاۋەت قىلىپ تۇرىدىغان، ئاللاھ تەرىپىدىن كەلگەن پەيغەمبەردۇر.');
INSERT INTO "saleh" VALUES(98,3,'98:3','ئۇ سەھىپىلەر توغرا ئەھكاملارنى ئۆز ئىچىگە ئالغان.');
INSERT INTO "saleh" VALUES(98,4,'98:4',' كىتاب بېرىلگەنلەر (يەنى يەھۇدىيلار ۋە ناسارالار) پەقەت ئۆزلىرىگە روشەن دەلىل كەلگەندىن كېيىنلا ئىختىلاپ قىلىشتى.');
INSERT INTO "saleh" VALUES(98,5,'98:5',' ئۇلار پەقەت ئىبادەتنى ئاللاھقا خالىس قىلغان، ھەق دىنغا ئېتىقاد قىلغان ھالدا (يالغۇز) ئاللاھقىلا ئىبادەت قىلىشقا بۇيرۇلدى. نامازنى ئادا قىلىشقا، زاكاتنى بېرىشكە (بۇيرۇلدى)، ئەنە شۇلار (يەنى ئىبادەت، ئىخلاس ناماز، زاكاتلار) توغرا دىندۇر.');
INSERT INTO "saleh" VALUES(98,6,'98:6',' ئەھلى كىتابتىن ۋە مۇشرىكلاردىن كاپىر بولغانلار ھەقىقەتەن دوزاخقا كىرىدۇ. دوزاختا مەڭگۈ قالىدۇ، ئەنە شۇلار مەخلۇقاتلارنىڭ يامىنىدۇر.');
INSERT INTO "saleh" VALUES(98,7,'98:7','ئىمان ئېيتقان ۋە ياخشى ئەمەللەرنى قىلغانلار ـ ئەنە شۇلار مەخلۇقاتلارنىڭ ياخشىسىدۇر.');
INSERT INTO "saleh" VALUES(98,8,'98:8','ئۇلارغا پەرۋەردىگارىنىڭ دەرگاھىدا بېرىلىدىغان مۇكاپات (ئۇلارنىڭ) تۇرار جايى بولغان، ئاستىدىن ئۆستەڭلار ئېقىپ تۇرىدىغان جەننەتلەردۇركى، ئۇلار جەننەتلەردە مەڭگۈ قالىدۇ. ئاللاھ ئۇلاردىن رازى بولىدۇ، ئۇلارمۇ ئاللاھتىن مەمنۇن بولىدۇ. بۇنىڭغا پەرۋەردىگارىدىن قورققان ئادەم ئېرىشىدۇ.');
INSERT INTO "saleh" VALUES(99,1,'99:1','زېمىن قاتتىق تەۋرىتىلگەندە،');
INSERT INTO "saleh" VALUES(99,2,'99:2',' زېمىن (قوينىدىكى) يۈكلىرىنى (يەنى مەدەنلەر، ئوتلار، ئۆلۈكلەرنى) چىقارغاندا،');
INSERT INTO "saleh" VALUES(99,3,'99:3','ئىنسان ئۇنىڭغا (يەنى زېمىنغا): «نېمە بولدى؟» دېگەندە،');
INSERT INTO "saleh" VALUES(99,4,'99:4','بۇ كۈندە زېمىن ئۆزىنىڭ خەۋەرلىرىنى (يەنى ئۇنىڭ ئۈستىدە ئىشلەنگەن ياخشى ـ يامان ئىشلارنى) مەلۇم قىلىدۇ.');
INSERT INTO "saleh" VALUES(99,5,'99:5',' (ئۇنىڭ مەلۇم قىلىشى) پەرۋەردىگارىنىڭ ئۇنى شۇنداق قىلىشقا ئەمر قىلغانلىقىدىندۇر.');
INSERT INTO "saleh" VALUES(99,6,'99:6','بۇ كۈندە كىشىلەر قىلغان ئەمەللىرىنىڭ نەتىجىسىنى كۆرۈش ئۈچۈن توپ ـ توپ بولۇپ تارىلىپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(99,7,'99:7',' كىمكى زەررىچىلىك ياخشى ئىش قىلىدىكەن، ئۇنىڭ مۇكاپاتىنى كۆرىدۇ.');
INSERT INTO "saleh" VALUES(99,8,'99:8',' كىمكى زەررىچىلىك يامان ئىش قىلىدىكەن، ئۇنىڭ جازاسىنى تارتىدۇ.');
INSERT INTO "saleh" VALUES(100,1,'100:1','(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا ـ چاڭ چىقارغۇچى، شۇ توپا ـ چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى[1ـ5]،');
INSERT INTO "saleh" VALUES(100,2,'100:2','(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا ـ چاڭ چىقارغۇچى، شۇ توپا ـ چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى[1ـ5]،');
INSERT INTO "saleh" VALUES(100,3,'100:3','(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا ـ چاڭ چىقارغۇچى، شۇ توپا ـ چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى[1ـ5]،');
INSERT INTO "saleh" VALUES(100,4,'100:4','(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا ـ چاڭ چىقارغۇچى، شۇ توپا ـ چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى[1ـ5]،');
INSERT INTO "saleh" VALUES(100,5,'100:5','(غازىلارنىڭ دۈشمەنگە ھۇجۇم قىلىشىدا) ھاسىراپ يۈگۈرگۈچى، (قاتتىق يۈگۈرگەنلىكتىن) تۇۋاقلىرىدىن ئوت چىقارغۇچى، ئەتىگەندە (دۈشمەن ئۈستىگە) باستۇرۇپ كىرگۈچى، (قاتتىق چاپقانلىقتىن) توپا ـ چاڭ چىقارغۇچى، شۇ توپا ـ چاڭ بىلەن دۈشمەن توپى ئىچىگە كىرىپ كەتكۈچى ئاتلار بىلەن قەسەمكى[1ـ5]،');
INSERT INTO "saleh" VALUES(100,6,'100:6',' ئىنسان ھەقىقەتەن پەرۋەردىگارىنىڭ (نېمەتلىرىنى) شەكسىز ئىنكار قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(100,7,'100:7','ئىنسان ھەقىقەتەن ئۇنىڭغا (يەنى ئاللاھنىڭ نېمىتىنى ئىنكار قىلغانلىقىغا) ئەلۋەتتە ئۆزى گۇۋاھتۇر.');
INSERT INTO "saleh" VALUES(100,8,'100:8',' ئىنسان پۇل ـ مالغا ئىنتايىن ھېرىستۇر.');
INSERT INTO "saleh" VALUES(100,9,'100:9',' قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟[9ـ11]');
INSERT INTO "saleh" VALUES(100,10,'100:10',' قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟[9ـ11]');
INSERT INTO "saleh" VALUES(100,11,'100:11',' قەبرىلەردىكى ئۆلۈكلەر (سىرتقا) چىقىرىلغان، دىللاردىكى سىرلار ئاشكارا قىلىنغان چاغدا، ئۇلارنىڭ ھەممە ئەھۋالىدىن پەرۋەردىگارىنىڭ ھەقىقەتەن خەۋەردار ئىكەنلىكىنى (بۇ نادان ئىنسان) بىلمەمدۇ؟[9ـ11]');
INSERT INTO "saleh" VALUES(101,1,'101:1','قىيامەت، ');
INSERT INTO "saleh" VALUES(101,2,'101:2','قىيامەت نېمىدۇر.');
INSERT INTO "saleh" VALUES(101,3,'101:3',' قىيامەتنىڭ نېمە ئىكەنلىكىنى قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(101,4,'101:4','شۇ كۈندە ئىنسانلار تارىلىپ كەتكەن پەرۋانىدەك بولۇپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(101,5,'101:5','تاغلار ئېتىلغان يۇڭدەك بولۇپ كېتىدۇ.');
INSERT INTO "saleh" VALUES(101,6,'101:6','تارازىسى ئېغىر كەلگەن (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ چۈشكەن) ئادەمگە كەلسەك، ئۇ كۆڭۈللۈك تۇرمۇشتا (يەنى نازۇنېمەتلىك جەننەتتە) بولىدۇ[6ـ7]. ');
INSERT INTO "saleh" VALUES(101,7,'101:7','تارازىسى ئېغىر كەلگەن (يەنى ياخشىلىقلىرى يامانلىقلىرىنى بېسىپ چۈشكەن) ئادەمگە كەلسەك، ئۇ كۆڭۈللۈك تۇرمۇشتا (يەنى نازۇنېمەتلىك جەننەتتە) بولىدۇ[6ـ7]. ');
INSERT INTO "saleh" VALUES(101,8,'101:8',' تارازىسى يېنىك كەلگەن (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەن، يا ياخشىلىقى بولمىغان) ئادەمگە كەلسەك، ئۇنىڭ جايى ھاۋىيە بولىدۇ[8ـ9].');
INSERT INTO "saleh" VALUES(101,9,'101:9',' تارازىسى يېنىك كەلگەن (يەنى يامانلىقلىرى ياخشىلىقلىرىنى بېسىپ كەتكەن، يا ياخشىلىقى بولمىغان) ئادەمگە كەلسەك، ئۇنىڭ جايى ھاۋىيە بولىدۇ[8ـ9].');
INSERT INTO "saleh" VALUES(101,10,'101:10','ھاۋىيەنىڭ نېمە ئىكەنلىكىنى سەن قانداق بىلەلەيسەن؟');
INSERT INTO "saleh" VALUES(101,11,'101:11','ھاۋىيە قىزىق ئوتتۇر. ');
INSERT INTO "saleh" VALUES(102,1,'102:1','تاكى قەبرىلەرنى زىيارەت قىلغىنىڭلارغا (يەنى قەبرىلەرگە كۆمۈلگىنىڭلارغا) قەدەر، (پۇل ـ مال، بالىلار بىلەن) پەخىرلەنمەك سىلەرنى غەپلەتتە قالدۇردى[1ـ2]. ');
INSERT INTO "saleh" VALUES(102,2,'102:2','تاكى قەبرىلەرنى زىيارەت قىلغىنىڭلارغا (يەنى قەبرىلەرگە كۆمۈلگىنىڭلارغا) قەدەر، (پۇل ـ مال، بالىلار بىلەن) پەخىرلەنمەك سىلەرنى غەپلەتتە قالدۇردى[1ـ2]. ');
INSERT INTO "saleh" VALUES(102,3,'102:3','راستلا كەلگۈسىدە بىلىسىلەر.');
INSERT INTO "saleh" VALUES(102,4,'102:4','ئاندىن راستلا كەلگۈسىدە بىلىسىلەر.');
INSERT INTO "saleh" VALUES(102,5,'102:5',' ئەگەر سىلەر راستلا ھەقىقىي بىلسەڭلار (غەپلەتتە قالماڭلار).');
INSERT INTO "saleh" VALUES(102,6,'102:6','سىلەر چوقۇم جەھەننەمنى كۆرىسىلەر.');
INSERT INTO "saleh" VALUES(102,7,'102:7','ئاندىن ئۇنى چوقۇم ئۆز كۆزۈڭلار بىلەن كۆرىسىلەر.');
INSERT INTO "saleh" VALUES(102,8,'102:8',' ئاندىن (دۇنيادا سىلەرگە بېرىلگەن) نېمەتلەردىن سوئال ـ سوراق قىلىنىسىلەر.');
INSERT INTO "saleh" VALUES(103,1,'103:1','زامان بىلەن قەسەمكى، ');
INSERT INTO "saleh" VALUES(103,2,'103:2','ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، بىر ـ بىرىگە ھەقنى تەۋسىيە قىلىشقان، بىر ـ بىرىگە سەۋرنى تەۋسىيە قىلىشقان كىشىلەردىن باشقا ھەممە ئادەم چوقۇم زىيان ئىچىدىدۇر[2 ـ 3].');
INSERT INTO "saleh" VALUES(103,3,'103:3','ئىمان ئېيتقان، ياخشى ئەمەللەرنى قىلغان، بىر ـ بىرىگە ھەقنى تەۋسىيە قىلىشقان، بىر ـ بىرىگە سەۋرنى تەۋسىيە قىلىشقان كىشىلەردىن باشقا ھەممە ئادەم چوقۇم زىيان ئىچىدىدۇر[2 ـ 3].');
INSERT INTO "saleh" VALUES(104,1,'104:1','(كىشىلەرنى) ئەيىبلىگۈچى سۇخەنچىگە ۋاي!.');
INSERT INTO "saleh" VALUES(104,2,'104:2','ئۇ مال توپلىدى ۋە ئۇنى (ياخشىلىق يوللىرىغا سەرپ قىلماي ساقلاش ئۈچۈن) سانىدى. ');
INSERT INTO "saleh" VALUES(104,3,'104:3','ئۇ مېلى ئۆزىنى (دۇنيادا مەڭگۈ قالدۇرىدۇ دەپ ئويلايدۇ).');
INSERT INTO "saleh" VALUES(104,4,'104:4','ھەرگىز ئۇنداق ئەمەس، ئۇ چوقۇم ھۇتەمەگە تاشلىنىدۇ.');
INSERT INTO "saleh" VALUES(104,5,'104:5','ھۇتەمەنىڭ نېمىلىكىنى قانداق بىلەلەيسەن؟ ');
INSERT INTO "saleh" VALUES(104,6,'104:6',' ئۇ، ئاللاھنىڭ ياندۇرغان ئوتىدۇركى، (ئۇنىڭ ئەلىمى) يۈرەكلەرگە يېتىپ بارىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(104,7,'104:7',' ئۇ، ئاللاھنىڭ ياندۇرغان ئوتىدۇركى، (ئۇنىڭ ئەلىمى) يۈرەكلەرگە يېتىپ بارىدۇ[6ـ7].');
INSERT INTO "saleh" VALUES(104,8,'104:8','ئۇلار چوقۇم دوزاخقا سولىنىدۇ.');
INSERT INTO "saleh" VALUES(104,9,'104:9',' ئېگىز تۈۋرۈكلەرگە باغلىنىدۇ.');
INSERT INTO "saleh" VALUES(105,1,'105:1','پەرۋەردىگارىڭنىڭ فىل ئىگىلىرىنى قانداق قىلغانلىقىنى كۆرمىدىڭمۇ؟ ');
INSERT INTO "saleh" VALUES(105,2,'105:2',' ئۇ ئۇلارنىڭ ھىيلە ـ مىكرىنى بەربات قىلمىدىمۇ؟');
INSERT INTO "saleh" VALUES(105,3,'105:3','ئۇ ئۇلارنىڭ ئۈستىگە توپ ـ توپ قۇشلارنى ئەۋەتتى. ');
INSERT INTO "saleh" VALUES(105,4,'105:4','قۇشلار ئۇلارغا ساپال تاشلارنى ئېتىپ،');
INSERT INTO "saleh" VALUES(105,5,'105:5','ئۇلارنى چاينىۋېتىلگەن ساماندەك قىلىۋەتتى.');
INSERT INTO "saleh" VALUES(106,1,'106:1','قۇرەيش قوغدالغانلىقلىرى ئۈچۈن،');
INSERT INTO "saleh" VALUES(106,2,'106:2',' ئۇلار قىشلىق ۋە يازلىق سەپىرىدە قوغدالغانلىقلىرى ئۈچۈن،');
INSERT INTO "saleh" VALUES(106,3,'106:3','بۇ ئۆي (يەنى بەيتۇللاھ) نىڭ پەرۋەردىگارىغا ئىبادەت قىلسۇنكى، ئۇ ئۇلارنى ئاچلىقتا ئوزۇقلاندۇردى، ئۇلارنى قورقۇنچتىن ئەمىن قىلدى[3ـ4]. ');
INSERT INTO "saleh" VALUES(106,4,'106:4','بۇ ئۆي (يەنى بەيتۇللاھ) نىڭ پەرۋەردىگارىغا ئىبادەت قىلسۇنكى، ئۇ ئۇلارنى ئاچلىقتا ئوزۇقلاندۇردى، ئۇلارنى قورقۇنچتىن ئەمىن قىلدى[3ـ4]. ');
INSERT INTO "saleh" VALUES(107,1,'107:1','دىننى ئىنكار قىلغان ئادەمنى كۆردۈڭمۇ؟');
INSERT INTO "saleh" VALUES(107,2,'107:2',' ئۇ يېتىمنى دۆشكەلەيدىغان، مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايدىغان ئادەمدۇر[2ـ3]،');
INSERT INTO "saleh" VALUES(107,3,'107:3',' ئۇ يېتىمنى دۆشكەلەيدىغان، مىسكىنگە تاماق بېرىشنى تەرغىب قىلمايدىغان ئادەمدۇر[2ـ3]،');
INSERT INTO "saleh" VALUES(107,4,'107:4','شۇنداق ناماز ئوقۇغۇچىلارغا ۋايكى، ئۇلار نامازلىرىدىن غەپلەتتىدۇر [4ـ5].');
INSERT INTO "saleh" VALUES(107,5,'107:5','شۇنداق ناماز ئوقۇغۇچىلارغا ۋايكى، ئۇلار نامازلىرىدىن غەپلەتتىدۇر [4ـ5].');
INSERT INTO "saleh" VALUES(107,6,'107:6','نامازنى رىيا قىلىدىغانلاردۇر . ');
INSERT INTO "saleh" VALUES(107,7,'107:7','ئۇلار قولقا بەرمەيدۇ.');
INSERT INTO "saleh" VALUES(108,1,'108:1','بىز ساڭا ھەقىقەتەن نۇرغۇن ياخشىلىقلارنى ئاتا قىلدۇق.');
INSERT INTO "saleh" VALUES(108,2,'108:2','پەرۋەردىگارىڭ ئۈچۈن ناماز ئوقۇغىن ۋە قۇربانلىق قىلغىن.');
INSERT INTO "saleh" VALUES(108,3,'108:3','ھەقىقەتەن سېنىڭ دۈشمىنىڭنىڭ نام ـ نىشانى قالمايدۇ.');
INSERT INTO "saleh" VALUES(109,1,'109:1','(ئى مۇھەممەد! سېنى بۇتلارغا چوقۇنۇشقا ئۈندىگۈچى كۇففارلارغا) ئېيتقىنكى، «ئى كاپىرلار! ');
INSERT INTO "saleh" VALUES(109,2,'109:2','سىلەر چوقۇنۇۋاتقان بۇتلارغا مەن چوقۇنمايمەن.');
INSERT INTO "saleh" VALUES(109,3,'109:3','سىلەرمۇ مەن ئىبادەت قىلىۋاتقان ئاللاھقا ئىبادەت قىلمايسىلەر.');
INSERT INTO "saleh" VALUES(109,4,'109:4','سىلەر چوقۇنغان بۇتلارغا مېنىڭ چوقۇنغىنىم يوق.');
INSERT INTO "saleh" VALUES(109,5,'109:5',' سىلەرنىڭمۇ مەن ئىبادەت قىلغان ئاللاھقا ئىبادەت قىلغىنىڭلار يوق.');
INSERT INTO "saleh" VALUES(109,6,'109:6',' سىلەرنىڭ دىنىڭلار ئۆزۈڭلار ئۈچۈن، مېنىڭ دىنىممۇ ئۆزۈم ئۈچۈن».');
INSERT INTO "saleh" VALUES(110,1,'110:1',' ئاللاھنىڭ ياردىمى ۋە غەلىبىسى كەلگەن ۋە ئاللاھنىڭ دىنىغا كىشىلەرنىڭ توپ ـ توپ بولۇپ كىرگەنلىكىنى كۆرگىنىڭدە[1ـ2]،');
INSERT INTO "saleh" VALUES(110,2,'110:2',' ئاللاھنىڭ ياردىمى ۋە غەلىبىسى كەلگەن ۋە ئاللاھنىڭ دىنىغا كىشىلەرنىڭ توپ ـ توپ بولۇپ كىرگەنلىكىنى كۆرگىنىڭدە[1ـ2]،');
INSERT INTO "saleh" VALUES(110,3,'110:3','رەببىڭغا تەسبىھ ئېيتقىن، ھەمدى ئېيتقىن ۋە ئۇنىڭدىن مەغپىرەت تىلىگىن. ئاللاھ ھەقىقەتەن تەۋبىنى بەك قوبۇل قىلغۇچىدۇر.');
INSERT INTO "saleh" VALUES(111,1,'111:1','ئەبۇ لەھەبنىڭ ئىككى قولى قۇرۇپ كەتسۇن! (ئەمەلدە) قۇرۇپ كەتتى.');
INSERT INTO "saleh" VALUES(111,2,'111:2',' ئۇنىڭغا مال ـ مۈلكى ۋە ئېرىشكەن نەرسىلىرى ئەسقاتمىدى.');
INSERT INTO "saleh" VALUES(111,3,'111:3','ئۇ لاۋۇلداپ تۇرغان ئوتقا (يەنى دوزاخقا) كىرىدۇ.');
INSERT INTO "saleh" VALUES(111,4,'111:4','ئۇنىڭ ئوتۇن توشۇغۇچى (يەنى سۇخەنچى) خوتۇنىمۇ لاۋۇلداپ تۇرغان ئوتقا كىرىدۇ.');
INSERT INTO "saleh" VALUES(111,5,'111:5','ئۇنىڭ بوينىدا مەھكەم ئېشىلگەن ئارغامچا بولىدۇ. ');
INSERT INTO "saleh" VALUES(112,1,'112:1','(ئى مۇھەممەد!) ئېيتقىنكى، ئۇ ـ ئاللاھ بىردۇر.');
INSERT INTO "saleh" VALUES(112,2,'112:2','ھەممە ئاللاھقا موھتاجدۇر.');
INSERT INTO "saleh" VALUES(112,3,'112:3','ئاللاھ بالا تاپقانمۇ ئەمەس، تۇغۇلغانمۇ ئەمەس.');
INSERT INTO "saleh" VALUES(112,4,'112:4','ھېچ كىشى ئۇنىڭغا تەڭداش بولالمايدۇ.');
INSERT INTO "saleh" VALUES(113,1,'113:1','«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (ئاللاھ) قا سىغىنىپ پاناھ تىلەيمەن» دېگىن[1ـ5].');
INSERT INTO "saleh" VALUES(113,2,'113:2','«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (ئاللاھ) قا سىغىنىپ پاناھ تىلەيمەن» دېگىن[1ـ5].');
INSERT INTO "saleh" VALUES(113,3,'113:3','«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (ئاللاھ) قا سىغىنىپ پاناھ تىلەيمەن» دېگىن[1ـ5].');
INSERT INTO "saleh" VALUES(113,4,'113:4','«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (ئاللاھ) قا سىغىنىپ پاناھ تىلەيمەن» دېگىن[1ـ5].');
INSERT INTO "saleh" VALUES(113,5,'113:5','«مەخلۇقاتنىڭ شەررىدىن، قاراڭغۇلۇقى بىلەن كىرگەن كېچىنىڭ شەررىدىن، تۈگۈنلەرگە دەم سالغۇچى سېھىرگەرلەرنىڭ شەررىدىن، ھەسەتخورنىڭ ھەسەت قىلغان چاغدىكى شەررىدىن سۈبھىنىڭ پەرۋەردىگارى (ئاللاھ) قا سىغىنىپ پاناھ تىلەيمەن» دېگىن[1ـ5].');
INSERT INTO "saleh" VALUES(114,1,'114:1','«ئىنسانلارنىڭ پەرۋەردىگارى، ئىنسانلارنىڭ پادىشاھى، ئىنسانلارنىڭ ئىلاھى (ئاللاھ) قا سىغىنىپ، كىشىلەرنىڭ دىللىرىدا ۋەسۋەسە قىلغۇچى جىنلاردىن ۋە ئىنسانلاردىن بولغان يوشۇرۇن شەيتاننىڭ ۋەسۋەسىسىنىڭ شەررىدىن پاناھ تىلەيمەن» دېگىن[1ـ6].');
