INSERT INTO "sayyid_qutb" VALUES(1,1,'1:1','اللہ کے نام سے جو بےانتہا مہربان اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(1,2,'1:2','تعریف اللہ ہی کے لئے ہے جو تمام کائنات کا رب ہے');
INSERT INTO "sayyid_qutb" VALUES(1,3,'1:3','نہایت مہربان اور رحم فرمانے والا');
INSERT INTO "sayyid_qutb" VALUES(1,4,'1:4','روز جزاء کا مالک ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(1,5,'1:5','ہم تیری ہی عبادت کرتے ہیں اور تجھی سے مدد مانگتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(1,6,'1:6','ہمیں سیدھا راستہ دکھا');
INSERT INTO "sayyid_qutb" VALUES(1,7,'1:7','ان لوگوں کا راستہ جن پر تونے انعام فرمایا ‘ جو معتوب نہیں ہوئے ‘ جو بھٹکے ہوئے نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,1,'2:1','الف ، لام ، میم ۔');
INSERT INTO "sayyid_qutb" VALUES(2,2,'2:2','یہ اللہ کی کتاب ہے۔ اس میں کوئی شک نہیں ۔ ہدایت ہے ان پرہیز گاروں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(2,3,'2:3','جو غیب پر ایمان لاتے ہیں ، نماز قائم کرتے ہیں ، جو رزق ہم نے ان کو دیا ہے ، اس میں سے خرچ کرتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(2,4,'2:4','جو کتاب تم پر نازل کی گئی ہے اور جو کتابیں تم سے پہلے نازل کی گئی تھیں ، ان سب پر ایمان لاتے ہیں اور آخرت پر یقین رکھتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,5,'2:5','ایسے لوگ اپنے رب کی طرف سے راہ راست پر ہیں اور وہی فلاح پانے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,6,'2:6','جن لوگوں نے ان (باتوں کو تسلیم کرنے سے ) انکار کردیا ، ان کے لئے یکساں ہے ، خواہ تم انہیں خبردار کرو یا نہ کرو ، بہرحال وہ ماننے والے نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,7,'2:7','اللہ نے ان کے دلوں اور ان کے کانوں پر مہر لگادی ہے۔ اور آنکھوں پر پردہ پڑگیا ہے۔ وہ سخت سزا کے مستحق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,8,'2:8','بعض لوگ ایسے بھی ہیں جو کہتے ہیں کہ ہم اللہ پر اور آخرت کے دن پر ایمان لائے ہیں ، حالانکہ درحقیقت وہ مومن نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,9,'2:9','وہ اللہ اور ایمان لانے والوں کے ساتھ دھوکہ بازی کررہے ہیں مگر دراصل وہ خود اپنے آپ ہی کو دھوکہ میں ڈال رہے ہیں اور انہیں اس کا شعور نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,10,'2:10','ان کے دلوں میں ایک بیماری ہے جسے اللہ نے اور زیادہ بڑھادیا ۔ اور جو جھوٹ بولتے ہیں ، اس کی پاداش میں ان کے دردناک سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,11,'2:11','جب کبھی ان سے کہا گیا کہ زمین میں فساد برپا نہ کرو ، تو انہوں نے یہی کہا کہ ہم تو اصلاح کرنے والے ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,12,'2:12','خبردار ، حقیقت میں یہی لوگ مفسد ہیں مگر انہیں شعور نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,13,'2:13','اور جب ان سے کہا گیا کہ جس طرح دوسرے لوگ ایمان لائے ہیں اسی طرح تم بھی ایمان لاؤ تو انہوں نے یہی جواب دیا کہ ہم بیوقوفوں کی طرح ایمان لائیں ؟ “۔۔۔ خبردار ، حقیقت میں یہ تو خود بیوقوف ہیں ، مگر یہ جانتے نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,14,'2:14','جب یہ اہل ایمان سے ملتے ہیں تو کہتے ہیں کہ ہم ایمان لائے ہیں ، اور جب علیحدگی میں اپنے شیطانوں سے ملتے ہیں ، تو کہتے ہیں کہ اصل میں تو ہم تمہارے ساتھ ہیں اور ان لوگوں سے محض مذاق کررہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,15,'2:15','اللہ ان سے مذاق کررہا ہے ۔ وہ ان کی رسی دراز کئے جاتا ہے اور یہ اپنی سرکشی میں اندھوں کی طرح بھٹکتے چلے جاتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,16,'2:16','یہ وہ لوگ ہیں جنہوں نے ہدایت کے بدلے گمراہی خرید لی ہے ، مگر یہ سودا ان کے لئے نفع بخش نہیں ہے اور یہ ہرگز صحیح راستے پر نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,17,'2:17','ان کی مثال ایسی ہے جیسے ایک شخص نے آگ روشن کی اور جب اس نے سارے ماحول کو روشن کردیا تو اللہ نے ان کا نور بصارت سلب کرلیا اور انہیں اس حال میں چھوڑدیا کہ تاریکیوں میں انہیں کچھ نظر نہیں آتا ،');
INSERT INTO "sayyid_qutb" VALUES(2,18,'2:18','یہ بہرے ہیں ، گونگے ہیں ، اندھے ہیں ، یہ اب نہ پلٹیں گے');
INSERT INTO "sayyid_qutb" VALUES(2,19,'2:19','یا پھر ان کی مثال یوں سمجھو کہ آسمان سے زور کی بارش ہورہی ہے اور اس کے ساتھ اندھیری گھٹا اور کڑک اور چمک بھی ہے ، یہ بجلی کے کڑاکے سن کر اپنی جانوں کو ہر طرف سے گھیرے میں لئے ہوئے ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,20,'2:20','چمک سے ان کی یہ حالت ہورہی ہے کہ گویا عنقریب بجلی ان کی بصارت اچک لے جائے گی ۔ جب ذرا کچھ روشنی انہیں محسوس ہوتی ہے تو اس میں کچھ دور چل لیتے ہیں اور جب ان پر اندھیرا چھا جاتا ہے تو کھڑے ہوجاتے ہیں ۔ اللہ چاہتا تو ان کی سماعت اور بصارت بالکل ہی سلب کرلیتا ، یقینا وہ ہر چیز پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,21,'2:21','لوگو ، بندگی اختیارکرو ، اپنے اس رب کی جو تمہارا اور تم سے پہلے جو لوگ ہوگزرے ہیں ان سب کا خالق ہے ، تمہارے بچنے کی توقع اسی صورت سے ہوسکتی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,22,'2:22','وہی تو ہے جس نے تمہارے لئے زمین کا فرش بچھایا ، آسمان کی چھت بنائی ، اوپر سے پانی برسایا اور اس کے ذریعے سے ہر طرح کی پیداوار نکال کر تمہارے لئے رزق بہم پہنچایا ۔ پس جب تم یہ جانتے ہو تو دوسروں کو اللہ کا مقابل نہ ٹھیراؤ۔');
INSERT INTO "sayyid_qutb" VALUES(2,23,'2:23','اور اگر تمہیں اس امر میں شک ہے کہ یہ کتاب جو ہم نے اپنے بندے پر اتاری ہے ، یہ ہماری ہے یا نہیں ، تو اس کے مانند ایک ہی سورت بنالاؤ، اپنے سارے ہمنواؤں کو بلالو ، ایک اللہ کو چھوڑ کر باقی جس کی چاہو مدد لے لو ، اگر تم سچے ہو تو یہ کام کر کے دکھاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(2,24,'2:24','لیکن اگر تم نے ایسا نہ کیا اور یقینا کبھی نہیں کرسکتے تو ڈرو اس آگ سے جس کا ایندھن بنیں گے انسان اور پتھر جو مہیا کی گئی ہے منکرین خدا کے لئے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,25,'2:25','اور اے پیغمبر ، جو لوگ اس کتاب پر ایمان لے آئیں اور (اس کے مطابق) اپنے عمل درست کرلیں ، انہیں خوشخبری دے دو کہ ان کے لئے ایسے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی ۔ ان باغوں کے پھل صورت میں دنیا کے پھلوں سے ملتے جلتے ہوں گے ۔ جب کوئی پھل انہیں کھانے کو دیا جائے تو وہ کہیں گے کہ ایسے ہی پھل اس سے پہلے دنیا میں ہم کو دیئے جاتے تھے ۔ ان کے لئے وہاں پاکیزہ بیویاں ہوں گی ، اور وہ وہاں ہمیشہ رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,26,'2:26','ہاں ، اللہ اس سے ہرگز نہیں شرماتا کہ مچھر یا اس سے بھی حقیر تر کسی چیز کی تمثیلیں دے ۔ جو لوگ حق بات قبول کرنے والے ہیں ، وہ انہی تمثیلوں کو دیکھ کر جان لیتے ہیں کہ یہ حق ہے ، جو ان کے رب کی طرف سے آیا ہے اور جو ماننے والے نہیں ہیں ، وہ انہیں سن کر کہنے لگتے ہیں کہ ایسی تمثیلوں سے اللہ کو کیا سروکار ؟ اسی طرح اللہ ایک ہی بات سے بہتوں کو گمراہی میں مبتلا کردیتا ہے ، اور بہتوں کو راہ راست دکھا دیتا ہے اور گمراہی میں انہی کو مبتلا کرتا ہے جو فاسق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,27,'2:27','اللہ کے عہد کو مضبوطی سے باندھ لینے کے بعد توڑ دیتے ہیں ، اللہ نے جسے جوڑنے کا حکم دیا ہے ، اسے کاٹتے ہیں اور زمین میں فساد برپا کرتے ہیں ، حقیقت میں یہی لوگ نقصان اٹھانے والے ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,28,'2:28','تم اللہ کے ساتھ کفر کا رویہ کیسے اختیار کرتے ہو ، حالانکہ تم بےجان تھے ۔ اس نے تم کو زندگی عطاکی ، پھر وہی تمہاری جان سلب کرے گا ، پھر وہی تمہیں دوبارہ زندگی عطاکرے گا ، پھر اسی کی طرف تمہیں پلٹ کرجانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,29,'2:29','وہی تو ہے جس نے تمہارے لئے زمین کی ساری چیزیں پیدا کیں ۔ پھر اوپر کی طرف متوجہ فرمائی اور سات آسمان استوار کئے اور وہ ہر چیز کا علم رکھنے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,30,'2:30','پھر ذرا اس وقت کا تصور کرو جب تمہارے رب نے فرشتوں سے کہا تھا کہ میں زمین پر خلیفہ بنانے والا ہوں ۔ “ انہوں نے عرض کیا کیا آپ زمین پر کسی ایسے کو مقرر کرنے والے ہیں جو اس کے نظام کو بگاڑ دے اور خونریزیاں کرے گا ؟ آپ کی حمد وثناکے ساتھ تسبیح اور آپ کی تقدیس تو ہم کر ہی رہے ہیں ۔ “ فرمایا میں جانتا ہوں ، جو کچھ تم نہیں جانتے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,31,'2:31','اس کے بعد اللہ نے آدم کو ساری چیزوں کے نام سکھائے ، پھر انہیں فرشتوں کے سامنے پیش کیا اور فرمایا اگر تمہارا خیال صحیح ہے (کہ کسی خلیفہ کے تقرر سے نظام بگڑجائے گا) تو ذرا ان چیزوں کے نام بتاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(2,32,'2:32','“ انہوں نے عرض کیا نقص سے پاک تو آپ ہی کی ذات ہے ہم تو بس اتنا ہی جانتے ہیں جتنا آپ نے ہم کو دیا ہے۔ حقیقت میں سب کچھ جاننے اور سمجھنے والا آپ کے سوا کوئی نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,33,'2:33','“ پھر اللہ نے آدم سے کا تم انہیں ان چیزوں کے نام بتاؤ ؟ “ جب اس نے ان کو سارے نام بتادیئے تو اللہ نے فرمایا میں نے تم سے کہا نہ تھا کہ میں آسمانوں اور زمین کی وہ ساری حقیقتیں جانتا ہوں جو تم سے مخفی ہیں اور جو کچھ تم ظاہر کرتے ہو وہ بھی مجھے معلوم ہے اور جو کچھ تم چھپاتے ہو اسے بھی میں جانتا ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,34,'2:34','پھر جب ہم نے فرشتوں کو حکم دیا کے آدم کے آگے جھک جاؤ ‘ مگر ابلیس نے انکار کیا ۔ وہ اپنی بڑائی کے گھمنڈ میں پڑگیا اور نافرمانوں میں شامل ہوگیا۔');
INSERT INTO "sayyid_qutb" VALUES(2,35,'2:35','پھر ہم نے آدم سے کہا کہ تم اور تمہاری بیوی ‘ دونوں جنت میں رہو اور یہاں بفراغت جو چاہو کھاؤ ‘ مگر اس درخت کا رخ نہ کرنا ‘ ورنہ ظالموں میں شمار ہوگے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,36,'2:36','آخرکار شیطان نے ان دونوں کو اس درخت کی ترغیب دے کر ہمارے حکم کی پیروی سے ہٹادیا اور انہیں اس حالت سے نکلوا کر چھوڑا جس میں وہ تھے');
INSERT INTO "sayyid_qutb" VALUES(2,37,'2:37','اس وقت آدم نے اپنے رب سے چند کلمات سیکھ کر توبہ کی ‘ جس کو اس کے رب نے قبول کرلیا ۔ کیونکہ وہ بڑا معاف کرنے والا اور رحم کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,38,'2:38','ہم نے کہا تم سب یہاں اتجاؤ۔ پھر جو میری طرف سے کوئی ہدایت تمہارے پاس پہنچے تو جون لوگ میری اس ہدایت کی پیروی کریں گے ‘ ان کے لئے کسی خوف اور رنج کا موقع نہ ہوگا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,39,'2:39','اور جو اس کو قبول کرنے سے انکار کریں گے وہ ہماری آیات کو جھٹلائیں گے ‘ وہ آگ میں جانے والے ہیں جہاں وہ ہمیشہ رہیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,40,'2:40','اے بنی اسرائیل ذرا خیال کرو میری اس نعمت کا جو میں نے تم کو عطا کی تھی ۔ میرے ساتھ تمہارا جو عہد تھا اسے تم پورا کرو تو میرا جو عہد تمہارے ساتھ تھا ‘ اسے میں پورا کروں ‘ اور مجھ ہی سے تم ڈرو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,41,'2:41','اور میں نے جو کتاب بھیجی ہے اس پر ایمان لاؤ ۔ یہ اس کتاب کی تائید میں ہے جو تمہارے پاس پہلے سے موجود تھی ‘ لہٰذا سب سے پہلے تم ہی اس کے منکر نہ بن جاؤ۔ تھوڑی قیمت پر میری آیات کو نہ بیچ ڈالو۔ اور میرے غضب سے بچو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,42,'2:42','باطل کا رنگ چڑھا کر حق کو مشتبہ نہ بناؤ اور نہ جانتے بوجھتے حق کو چھپانے کی کوشش کرو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,43,'2:43','نماز قائم کرو ‘ زکوٰۃ دو اور جو لوگ میرے آگے جھک رہے ہیں ان کے ساتھ تم بھی جھک جاؤ ۔');
INSERT INTO "sayyid_qutb" VALUES(2,44,'2:44','تم دوسروں کو تو نیکی کا راستہ اختیار کرنے کے لئے کہتے ہو ‘ مگر اپنے آپ کو بھول جاتے ہو ؟ حالانکہ تم کتاب کی تلاوت کرتے ہو ۔ کیا تم عقل سے بالکل کام نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(2,45,'2:45','صبر اور نماز سے مدد لو ‘ بیشک نماز ایک مشکل سخت کام ہے، مگر ان فرماںبردار بندوں کے لیے مشکل نہیں');
INSERT INTO "sayyid_qutb" VALUES(2,46,'2:46',' جو سمجھتے ہیں کہ آخر کار انہیں اپنے رب سے ملنا ہے اور اسی کی طرف پلٹ کر جانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,47,'2:47','اے بنی اسرائیل یاد کرو میری اس نعمت کو جس سے میں نے تمہیں نوازا تھا اور اس بات کو کہ میں نے تمہیں دنیا کی ساری قوموں پر فضیلت عطا کی تھی ،');
INSERT INTO "sayyid_qutb" VALUES(2,48,'2:48','اور ڈرو اس دن سے جب کوئی کسی کے ذرا کام نہ آئے گا ، نہ کسی کی طرف سے سفارش قبول ہوگی ، نہ کسی کو فدیہ لے کر چھوڑا جائے گا اور نہ مجرموں کو کہیں سے مدد مل سکے گی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,49,'2:49','یاد کرو وہ وقت جب ہم نے تم کو فرعونیوں کی غلامی سے نجات بخشی ۔ انہوں نے تمہیں سخت عذاب میں مبتلاکررکھا تھا ، تمہارے لڑکوں کو ذبح کرتے تھے اور تمہاری لڑکیوں کو زندہ رہنے دیتے تھے اور اس حالت میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,50,'2:50','یاد کرو وہ وقت ، جب ہم نے سمندر پھاڑ کر تمہارے لئے راستہ بنایا ، پھر اس میں سے تمہیں گزاردیا ، پھر وہیں تمہاری آنکھوں کے سامنے فرعونیوں کو غرقاب کیا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,51,'2:51','یاد کرو ، جب موسیٰ کو ہم نے چالیس شبانہ روز کی قراردار پر بلایا ، تو اس کے پیچھے تم بچھڑے کو اپنا معبود بنابیٹھے ۔ اس وقت تم نے بڑی زیادتی کی تھی ،');
INSERT INTO "sayyid_qutb" VALUES(2,52,'2:52','مگر اس پر بھی ہم نے تمہیں معاف کیا کہ شایداب تم شکرگزار بنو۔');
INSERT INTO "sayyid_qutb" VALUES(2,53,'2:53','یاد کرو کہ (ٹھیک اس وقت جب تم یہ ظلم کررہے تھے ) ہم نے موسیٰ کو کتاب اور فرقان عطاکی تاکہ تم اس کے ذریعے سیدھا راستہ پاسکو۔');
INSERT INTO "sayyid_qutb" VALUES(2,54,'2:54','یاد کرو ، جب موسیٰ (یہ نعمت لئے ہوئے پلٹا تو اس ) نے اپنی قوم سے کہا کہ لوگو ! تم نے بچھڑے کو معبود بناکر اپنے اوپر سخت ظلم کیا ہے ، لہٰذا تم لوگ اپنے خالق کے حضور توبہ کرو اور اپنی جانوں کو ہلاک کرو ، اسی میں تمہارے خالق کے نزدیک تمہاری بہتری ہے ۔ اس وقت تمہارے خالق نے تمہاری توبہ قبول کرلی کہ وہ بڑامعاف کرنے والا اور رحم کرنے والا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,55,'2:55','یاد کرو ، جب تم نے موسیٰ سے کہا تھا کہ ہم تمہارے کہنے کا ہرگز یقین نہ کریں گے ، جب تک کہ اپنی آنکھوں سے علانیہ خدا کو (تم سے کلام کرتا) نہ دیکھ لیں ، اس وقت تک تمہارے دیکھتے دیکھتے ایک زبردست صاعقہ نے تم کو آلیا۔ تم بےجان ہوکر گرچکے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,56,'2:56','مگر پھر ہم نے تم کو جلااٹھایا ، شاید کہ تم احسان کے بعد شکرگزار بن جاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(2,57,'2:57','ہم نے تم پر ابر کا سایہ کیا ۔ من وسلویٰ کی غذا تمہارے لئے فراہم کی اور تم سے کہا کہ جو پاک چیزیں ہم نے تمہیں بخشی ہیں ، انہیں کھاؤمگر تمہارے اسلاف نے جو کچھ کیا ، وہ ہم پر ظلم نہ تھا بلکہ انہوں نے آپ اپنے ہی اوپر ظلم کیا “');
INSERT INTO "sayyid_qutb" VALUES(2,58,'2:58','اور پھر یاد کرو ، جب ہم نے کہا تھا کہ یہ بستی جو تمہارے سامنے ہے ، اس میں داخل ہوجاؤ ، اس کی پیداوار سج طرح چاہو ، مزے سے کھاؤ ، مگر بستی کے درازے میں سجدہ ریز ہوتے داخل ہونا اور کہتے جانا حطۃ حطۃ ہم تمہاری خطاؤں سے درگزر کریں گے اور نیکوکاروں کو مزید فضل وکرم سے نوازیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,59,'2:59','مگر جو بات کہی گئی تھی ، ظالموں نے اسے بدل کر کچھ کردیا۔ آخرکار ہم نے ظلم کرنے والوں پر آسمان سے عذاب نازل کیا ۔ یہ سزا تھی ان نافرمانیوں کی جو وہ کررہے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,60,'2:60','اور یاد کرو جب موسیٰ نے اپنی تمام قوم کے لئے پانی کی دعا کی ، تو ہم نے کہاں فلاں چٹان پر اپنا عصامارو۔ چناچہ اس سے بارہ چشمے پھوٹ نکلے اور ہر قبیلے نے جان لیا کہ کون سی جگہ اس کے پانی لینے کی ہے ۔ اس وقت یہ ہدایت کردی گئی تھی کہ اللہ کا دیا ہوا رزق کھاؤ پیو اور زمین میں فساد نہ پھیلاتے پھرو “');
INSERT INTO "sayyid_qutb" VALUES(2,61,'2:61','یاد کرو ، جب تم نے کہا تھا اے موسیٰ ! ہم ایک ہی طرح کے کھانے پر صبر نہیں کرسکتے۔ اپنے رب سے دعا کرو کہ ہمارے لئے زمین کی پیداوار ساگ ، ترکاری ، گیہوں ، لہسن ، پیاز ، دالوغیرہ پیدا کرے ۔ “ تو موسیٰ نے کہا کیا ایک بہتر چیز کی بجائے تم ادنیٰ درجے کی چیز لینا چاہتے ہو ؟ اچھا کسی شہری آبادی میں جارہو ۔ جو کچھ تم مانگتے ہو ، وہاں مل جائے گا ۔ “ آخر کار نوبت یہاں تک پہنچی کہ ذلت و خواری اور پستی و بدحالی ان پر مسلط ہوگئی اور وہ اللہ کے غضب میں گھر گئے ۔ یہ نتیجہ تھا اس کا کہ وہ اللہ کی آیات سے کفر کرنے لگے اور پیغمبروں کا ناحق قتل کرنے لگے ۔ یہ نتیجہ تھا ان کی نافرمانیوں کا اور اس بات کا کہ وہ حدود شرع سے نکل جاتے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,62,'2:62','یقین جانو ! کہ نبی عرب کے ماننے والے ہوں یا یہودی ، عیسائی ہوں یا صابی جو بھی اللہ اور روز آخرت پر ایمان لائے گا اور نیک عمل کرے گا ، اس کا اجر اس کے رب کے پاس ہے اور اس کے لئے کسی خوف اور رنج کا موقع نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,63,'2:63','یاد کرو وہ وقت ، جب ہم نے طور کو تم پر اٹھاکر تم سے پختہ عہد لیا اور کہا تھا کہ جو کتاب ہم تمہیں دے رہے ہیں ، اسے مضبوطی سے تھامنا اور جو احکام وہدایات اس میں درج ہیں انہیں یاد رکھنا ۔ اسی ذریعے سے توقع کی جاسکتی ہے کہ تم تقویٰ کی روش پر چلو۔');
INSERT INTO "sayyid_qutb" VALUES(2,64,'2:64','“ مگر اس کے بعد تم اپنے عہد سے پھرگئے ۔ اس پر بھی اللہ کے فضل اور اس کی رحمت نے تمہارا ساتھ نہ چھوڑا ، ورنہ تم کبھی کے تباہ ہوچکے ہوتے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,65,'2:65','پھر تمہیں اپنی قوم کے ان لوگوں کا قصہ تو معلوم ہی ہے جنہوں نے سبت کا قانون توڑا تھا ۔ ہم نے انہیں کہہ دیا کہ بندر بن جاؤ اور اس حال میں رہو کہ ہر طرف سے تم پر دھتکار پڑے۔');
INSERT INTO "sayyid_qutb" VALUES(2,66,'2:66','اس طرح ہم نے ان کے انجام ، اس زمانے کے لوگوں اور بعد میں آنے والی نسلوں کے لئے عبرت اور ڈرانے والوں کے لئے نصیحت بناکر چھوڑا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,67,'2:67','پھر وہ واقعہ یاد کرو ، جب موسیٰ نے اپنی قوم سے کہا اللہ تمہیں گائے ذبح کرنے کا حکم دیتا ہے ۔ کہنے لگے تم ہم سے مذاق کرتے ہو ؟ موسیٰ (علیہ السلام) نے کہا میں اس سے خدا کی پناہ مانگتا ہوں کہ جاہلوں کی سی باتیں کروں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,68,'2:68','بولے اچھا اپنے رب سے درخواست کرو کہ وہ ہمیں اس گائے کی تفصیل بتائے ۔ موسیٰ (علیہ السلام) نے کہا اللہ کا ارشاد ہے کہ وہ ایسی گائے ہونی چاہئے جو نہ بوڑھی ہو نہ بچھیا ، بلکہ اوسط عمر کی ہو ، جو حکم دیا جاتا ہے اس کی تعمیل کرو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,69,'2:69','پھر کہنے لگے اپنے رب سے یہ اور پوچھ دو کہ اس کا رنگ کیسا ہو ؟ موسیٰ (علیہ السلام) نے کہا ! وہ فرماتا ہے ، زرد رنگ کی گائے ہونی چاہئے ، جس کا رنگ ایسا شوخ ہو کہ دیکھنے والوں کا جی خوش ہوجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,70,'2:70','پھر بولے اپنے رب سے صاف صاف پوچھ کر بتاؤ کیسی گائے مطلوب ہے ، ہمیں اس کے تعین میں اشتباہ ہوگیا ہے ، اللہ نے چاہا تو ہم کا پتہ پالیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,71,'2:71','موسیٰ (علیہ السلام) نے جواب دیا ! اللہ کہتا ہے کہ وہ ایسی گائے ہے جس سے خدمت نہیں لی جاتی ، نہ زمین جوتتی ہے ، نہ پانی کھینچتی ہے ، صحیح سالم اور بےداغ ہے ، اس پر وہ پکار اٹھے کے ہاں ، اب تم نے ٹھیک بتایا ہے ۔ پھر انہوں نے اسے ذبح کیا ، ورنہ وہ ایسا کرتے معلوم نہ ہوتے تھے “');
INSERT INTO "sayyid_qutb" VALUES(2,72,'2:72','اور تمہیں یاد ہے ، وہ واقعہ جب تم نے ایک شخص کی جان لی تھی ، پھر اس کے بارے میں جھگڑنے اور ایک دوسرے پر قتل کا الزام تھوپنے لگے تھے اور اللہ نے فیصلہ کرلیا تھا کہ جو کچھ تم چھپاتے ہو ، وہ اسے کھول کر رکھ دے گا ،');
INSERT INTO "sayyid_qutb" VALUES(2,73,'2:73','اس وقت ہم نے حکم دیا کہ مقتول کی لاش کو اس کے حصے سے ضرب لگاؤ۔ دیکھو اس طرح اللہ مردوں کو زندگی بخشتا ہے اور تمہیں اپنی نشانیاں دکھاتا ہے تاکہ تم سمجھو ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,74,'2:74','مگر ایسی نشانیاں دیکھنے کے بعد بھی آخر کار تمہارے دل سخت ہوگئے ، پتھروں کی طرح سخت ، بلکہ سختی میں کچھ ان سے بڑھے ہوئے ، کیونکہ پتھروں میں سے کوئی ایسا بھی ہوتا ہے جس میں چشمے پھوٹ بہتے ہیں ، کوئی پھٹتا ہے اور اس میں سے پانی نکل آتا ہے اور کوئی خدا کے خوف سے لرز کر بھی گر پڑتا ہے ، اللہ تمہارے کرتوتوں سے بیخبر نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,75,'2:75','اے مسلمانو ! اب کیا تم ان لوگوں سے یہ توقع رکھتے ہو کہ یہ تمہاری دعوت پر ایمان لے آئیں گے ؟ حالانکہ ان میں سے ایک گروہ کا شیوہ یہ رہا ہے کہ اللہ کا کلام سنا اور خوب سمجھ بوجھ کر دانستہ اس میں تحریف کی ۔');
INSERT INTO "sayyid_qutb" VALUES(2,76,'2:76','محمد رسول صلى الله عليه وسلم اللہ کے ماننے والوں سے ملتے ہیں تو کہتے ہیں کہ ہم بھی انہیں مانتے ہیں اور جب آپس میں ایک دوسرے سے تخلئے کی بات چیت ہوتی ہے تو کہتے ہیں کہ کیا بیوقوف ہوگئے ہو ؟ ان لوگوں کو وہ باتیں بتاتے ہو جو اللہ نے تم پر کھولی ہیں تاکہ تمہارے رب کے پاس تمہارے مقابلے میں انہیں حجت پیش کریں ؟');
INSERT INTO "sayyid_qutb" VALUES(2,77,'2:77','اور کیا یہ جانتے نہیں کہ جو کچھ وہ چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں اللہ کو سب باتوں کی خبر ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,78,'2:78','ان میں سے ایک دوسرا گروہ امیوں کا ہے ، جو کتاب کا علم رکھتے نہیں بس اپنی بےبنیاد اور آرزوؤں کے لئے بیٹھے ہیں اور محض وہم و گمان پر چلے جارہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,79,'2:79','پس ہلاکت اور تباہی ہے ان لوگوں کے لئے جو اپنے ہاتھوں سے شرع کا نوشتہ لکھتے ہیں ۔ پھر لوگوں سے کہتے ہیں کہ یہ اللہ کے پاس سے آیا ہوا ہے تاکہ اس کے معاوضے میں تھوڑا سا فائدہ حاصل کرلیں ۔ ان کے ہاتھوں کا یہ لکھا ہوا ، ان کے لئے تباہی کا سامان ہے اور ان کی یہ کمائی بھی ان کے لئے موجب ہلاکت ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,80,'2:80','وہ کہتے ہیں کہ دوزخ کی آگ ہمیں ہرگز چھونے والی نہیں ہے۔ الَّا یہ کہ چند روز کی سزا مل جائے تو مل جائے ۔ ان سے پوچھو کہ کیا تم نے اللہ سے کوئی عہد لیا ہے ، جس کی خلاف ورزی وہ نہیں کرسکتا ، یا بات یہ ہے کہ تم اللہ کے ذمے ڈال کر ایسی باتیں کہہ دیتے ہو جن کے متعلق تمہیں علم نہیں ہے کہ اس نے ان کا ذمہ لیا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,81,'2:81','آخر تمہیں دوزخ کی آگ کیوں چھوئے گی ۔ جو بھی بدی کمائے گا اور اپنی خطاکاری کے چکر میں پڑا رہے گا وہ دوزخی ہے اور دوزخ میں وہ ہمیشہ رہے گا');
INSERT INTO "sayyid_qutb" VALUES(2,82,'2:82','اور جو لوگ ایمان لائیں گے اور نیک عمل کریں گے وہی جنتی ہیں اور جنت میں ہمیشہ رہیں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,83,'2:83','یاد کرو اسرائیل کی اولاد سے ہم نے پختہ عہد لیا تھا کہ اللہ کے سوا کسی کی عبادت نہ کرنا ، رشتہ داروں کے ساتھ یتیموں کے ساتھ نیک سلوک کرنا ، لوگوں سے بھلی بات کہنا ، نماز قائم کرنا اور زکوٰۃ دینا ، مگر تھوڑے آدمیوں کے سوا تم سب اس عہد سے پھرگئے اور اب تک پھر ہوئے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,84,'2:84','پھر ذرا یاد کرو ، ہم نے تم سے مضبوط عہدلیا تھا کہ آپس میں ایک دوسرے کا خون نہ بہانا اور نہ ایک دوسرے کو گھر سے بےگھر کرنا ، تم نے اس کا اقرار کیا تھا ۔ تم خود اس پر گواہ ہو ،');
INSERT INTO "sayyid_qutb" VALUES(2,85,'2:85','مگر آج وہی تم ہو کہ اپنے بھائی بندوں کو قتل کرتے ہو ، اپنی برادری کے کچھ لوگوں کو بےخانماں کردیتے ہو ، ظلم و زیادتی کے ساتھ ان کے خلاف جتھا بندیاں کرتے ہو ، اور جب لڑائی میں پکڑے ہوئے تمہارے پاس آتے ہیں تو ان کی رہائی کے لئے فدیہ کا لین دین کرتے ہو ، حالانکہ ان کو ان کے گھر وں سے نکالنا ہی تم پر حرام تھا۔ تو کیا تم کتاب کے ایک حصہ پر ایمان لاتے ہو اور دوسرے حصے کے ساتھ کفر کرتے ہو ؟ پھر تم میں سے جو لوگ ایسا کریں ان کی سزا اس کے سوا کیا ہے کہ دنیا کی زندگی میں ذلیل و خوار ہوکر رہیں اور آخرت میں شدید ترین عذاب کی طرف پھیر دئیے جائیں ؟ اللہ ان حرکات سے بیخبر نہیں ہے جو تم کررہے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,86,'2:86','یہ وہ لوگ ہیں جنہوں نے آخرت کو بیچ کر دنیا کی زندگی خرید لی ہے ، لہٰذا نہ ان کی سزا میں تخفیف ہوگی اور نہ انہیں کوئی مدد پہنچ سکے گی۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,87,'2:87','ہم نے موسیٰ کو کتاب دی ، اس کے بعد پے درپے رسول بھیجے ، آخر کار عیسیٰ (علیہ السلام) ابن مریم کو روشن نشانیاں دے کر بھیجا اور روح پاک سے اس کی مدد کی ۔ پھر تمہارا کیا ڈھنگ ہے کہ جب بھی کوئی رسول تمہاری خواہشات نفس کے خلاف کوئی چیز لے کر تمہارے پاس آیا ، تو تم نے اس کے مقابلے میں سرکشی ہی کی ، کسی کو جھٹلایا اور کسی کو قتل کرڈالا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,88,'2:88','وہ کہتے ہیں ؟ ہمارے دل محفوظ ہیں “ نہیں ، اصل بات یہ ہے کہ ان کے کفر کی وجہ سے ان پر اللہ کی پھٹکار پڑی ہے ، اسی لئے وہ کم ہی ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(2,89,'2:89','اور اب جو ایک کتاب اللہ کی طرف سے ان کے پاس آئی ہے اس کے ساتھ ان کا برتاؤ کیا ہے ؟ باجودیکہ وہ اس کتاب کی تصدیق کرتی ہے ۔ جو ان کے پاس پہلے سے موجود تھی ، باجودیکہ اس کی آمد سے پہلے وہ خود کفار کے مقابلے میں فتح ونصرت کی دعائیں مانگا کرتے تھے ، مگر جب وہ چیز آگئی ۔ جسے وہ پہچان بھی گئے تو انہوں نے اسے ماننے سے انکار کردیا ۔ خدا کی لعنت ان منکرین پر ،');
INSERT INTO "sayyid_qutb" VALUES(2,90,'2:90','کیسا برا ذریعہ ہے ، جس سے یہ اپنے نفس کی تسلی حاصل کرتے ہیں کہ جو ہدایت اللہ نے نازل کی ہے ، اس کو قبول کرنے سے صرف اس ضد کی بناپر انکار کررہے ہیں کہ اللہ نے اپنے فضل (وحی و رسالت) سے اپنے جس بندے کو چاہا ، نواز دیا ۔ لہٰذا اب یہ غضب بالائے غضب کے مستحق ہوگئے ہیں اور ایسے کافروں کے لئے سخت ذلت آمیز سزا مقرر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,91,'2:91','جب ان سے کہا جاتا ہے کہ جو کچھ خدا نے نازل کیا ہے ، اس پر ایمان لاؤ، تو وہ کہتے ہیں ہم تو صرف اس چیز پر ایمان لاتے ہیں جو ہمارے ہاں (یعنی نسل بنی اسرائیل) اتری ہے ۔ “ اس دائرے کے باہر جو کچھ آیا ہے ، اسے ماننے سے انکار کرتے ہیں ، حالانکہ وہ حق ہے اور اس تعلیم کی تصدیق وتائید کررہا ہے جو ان کے ہاں پہلے سے موجود تھی ۔ اچھا ، ان سے کہو ؟ اگر تم اس تعلیم ہی پر ایمان رکھنے والے ہو جو تمہارے ہاں آئی تھی ، تو اس سے پہلے اللہ کے ان پیغمبروں کو (جو خود بنی اسرائیل میں پیدا ہوئے تھے) کیوں قتل کرتے رہے ؟');
INSERT INTO "sayyid_qutb" VALUES(2,92,'2:92','تمہارے پاس موسیٰ کیسی کیسی روشن نشانیوں کے ساتھ آیا ۔ پھر بھی تم ایسے ظالم تھے کہ اس کے پیٹھ موڑتے ہی بچھڑے کو معبود بنابیٹھے ،');
INSERT INTO "sayyid_qutb" VALUES(2,93,'2:93','پھر ذرا اس میثاق کو یاد کرو ، جو طور کو تمہارے اوپر اٹھاکر ہم نے تم سے لیا تھا ۔ ہم نے تاکید کی تھی کہ جو ہدایات ہم دے رہے ہیں ان کی سختی سے پابندی کرو۔ اور کان لگا کر سنو تمہارے اسلاف نے کہا کہ ہم نے سن لیامگرمانیں گے نہیں ۔ اور ان کی باطل پرستی کا یہ حال تھا کہ دلوں میں ان کے بچھڑا ہی بسا ہوا تھا ۔ کہو ؟ اگر تم مومن ہو تو یہ عجب ایمان ہے ، جو ایسی بری حرکات کا تمہیں حکم دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,94,'2:94','ان سے کہو اگر واقعی اللہ کے نزدیک آخرت کا گھر تمام انسانوں کو چھوڑ کر صرف تمہارے ہی لئے مخصوص ہے ، تب تو تمہیں چاہئے کہ موت کی تمنا کرو ، اگر تم اپنے اس خیال میں سچے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,95,'2:95','یقین جانو کہ یہ کبھی اس کی تمنا نہ کریں گے ، اس لئے کہ اپنے ہاتھوں میں جو کچھ کماکر انہوں نے وہاں بھیجا ، اس کا اقتضاء یہی ہے (کہ یہ وہاں جانے کی تمنا نہ کریں) ، اللہ ان کے حال سے خوب واقف ہے ،');
INSERT INTO "sayyid_qutb" VALUES(2,96,'2:96','تم انہیں سب بڑھ کر جینے کا حریص پاؤگے ، حتٰی کہ یہ اس معاملے میں مشرکوں سے بھی بڑھے ہوئے ہیں ۔ ان میں سے ایک ایک شخص یہ چاہتا ہے کہ کسی طرح ہزار برس جیئے حالانکہ لمبی عمر بہرحال انہیں عذاب سے تو دور نہیں پھینک سکتی ، جیسے کچھ اعمال یہ کررہے ہیں اللہ تو انہیں دیکھ ہی رہا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,97,'2:97','ان سے کہو جو کوئی جبرئیل سے عدوات رکھتا ہو ، اسے معلوم ہونا چاہئے کہ جبرئیل نے اللہ ہی کے اذن سے یہ قرآن تمہارے قلب پر نازل کیا ، جو پہلے آئی ہوئی کتابوں کی تصدیق و تائید کرتا ہے ۔ اور ایمان لانے والوں کے لئے ہدایت اور کامیابی کی بشارت بن کر آیا ہے');
INSERT INTO "sayyid_qutb" VALUES(2,98,'2:98','(اگر جبرئیل سے ان کی عداوت کا یہی سبب ہے تو کہہ دو کہ ) جو اللہ اور اس کے فرشتوں اور اس کے رسولوں اور جبرئیل اور میکائیل کے دشمن ہیں ، اللہ ان کافروں کا دشمن ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,99,'2:99','ہم نے تمہاری طرف ایسی آیات نازل کی ہیں جو صاف صاف حق کا اظہار کرنے والی ہیں ۔ اور ان کی پیروی کرنے سے صرف وہی لوگ انکار کرتے ہیں جو فاسق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,100,'2:100','کیا ہمیشہ ایسا ہی نہیں ہوتا رہا کہ جب انہوں نے کوئی عہد کیا تو ان میں سے ایک نہ ایک گروہ نے اسے ضرور ہی بالائے طاق رکھ دیا ؟ بلکہ ان میں اسے اکثر ایسے ہی ہیں جو سچے دل سے ایمان نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(2,101,'2:101','اور جب ان کے پاس اللہ کی طرف سے کوئی رسول اس کتاب کی تصدیق وتائید کرتا ہوا آیا جو ان کے ہاں پہلے سے موجود تھی تو ان اہل کتاب میں سے ایک گروہ نے کتاب اللہ کو اس طرح پس پشت ڈالا کہ وہ گویا وہ کچھ جانتے ہی نہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,102,'2:102','اور لگے ان چیزوں کی پیروی کرنے جو شیاطین سلیمان کی سلطنت میں نام لے کر پیش کیا کرتے تھے ۔ حالانکہ سلیمان نے کبھی کفر نہیں کیا ۔ کفر کے مرتکب تو وہ شیاطین تھے جو لوگوں کو جادوگری کی تعلیم بابل کے دو فرشتوں ہاروت وماروت پر نازل کی گئی تھی وہ (فرشتے تو آزمائش تھے) جب کسی کو اس کی تعلیم دیتے تھے تو پہلے صاف طور پر متنبہ کردیا کرتے تھے کہ دیکھ ہم محض ایک آزمائش ہیں تو کفر میں مبتلا نہ ہو ۔ “ پھر بھی وہی لوگ ان سے وہ چیز سیکھتے تھے جس سے شوہر اور بیوی میں جدائی ڈال دیں ۔ ظاہر تھا کہ اذن الٰہی کے بغیر وہ اس ذریعے سے کسی کو بھی ضرر نہ پہنچاسکتے تھے ۔ مگر اس کے باجود وہ ایسی چیزیں سیکھتے تھے جو خود ان کے لئے نفع بخش نہیں بلکہ نقصان دہ تھی۔ اور انہیں خوب معلوم تھا کہ جو اس چیز کا خریدار بنا اس کے لئے آخرت میں کوئی حصہ نہیں ۔ کتنی بری متاع تھی جس کے بدلے انہوں نے اپنی جانوں کو بیچ ڈالا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,103,'2:103','کاش انہیں خبر ہوتی ۔ اگر وہ ایمان اور تقویٰ اختیار کرتے تو اللہ کے ہاں اس کا وہ بدلہ ملتا جوان کے لئے زیادہ بہتر تھا ۔ کاش انہیں خبر ہوتی۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,104,'2:104','اے ایمان والو ! راعنا نہ کہا کرو ، بلکہ انظرنا کہو ، اور توجہ سے سنو اور یہ کافر عذاب الیم کے مستحق ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,105,'2:105','یہ لوگ جنہوں نے دعوت حق کو قبول کرنے سے انکار کردیا ، خواہ اہل کتاب میں سے ہوں یا مشرک ہوں ، ہرگز یہ پسند نہیں کرتے کہ تمہارے رب کی طرف سے تم پر کوئی بھلائی نازل ہو مگر اللہ جس کو چاہتا ہے اپنی رحمت کے لئے چن لیتا ہے ۔ اور وہ برا فضل فرمانے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,106,'2:106','ہم اپنی جس آیت کو منسوخ کردیتے ہیں یا بھلادیتے ہیں اس کی جگہ اس سے بہتر لاتے ہیں یا کم ازکم ویسی ہی۔ “ اس کی جگہ اس سے بہتر لے آتا ہے ۔ اور وہ ہر چیز کا مالک ہے ،');
INSERT INTO "sayyid_qutb" VALUES(2,107,'2:107','کیا تم جانتے نہیں ہو کہ اللہ تعالیٰ ہر چیزپر قدرت رکھتا ہے ۔ کیا تمہیں خبر نہیں ہے کہ زمین و آسمان کی فرماں روائی اللہ ہی کے لئے ہے اور اس کے سوا کوئی تمہاری خبرگیری کرنے والا اور تمہاری مدد کرنے والا نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,108,'2:108','پھر کیا تم اپنے رسول سے اس قسم کے سوالات اور مطالبے کرنا چاہتے ہو ، جیسے اس سے پہلے موسیٰ سے کئے جاچکے ہیں ؟ حالانکہ جس شخص نے ایمان کی روش کو کفر کی روش سے بدل لیا وہ راہ راست سے بھٹک گیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,109,'2:109','اہل کتاب میں سے اکثر لوگ یہ چاہتے ہیں کہ کسی طرح تمہیں ایمان سے پھیر کر کفر کی طرف پلتا لے جائیں اگرچہ ان پر حق طاہر ہوچکا ہے مگر اپنے نفس کے حسد کی بنا پر تمہارے لئے ان کی یہ خواہش ہے۔ “ اس کے جواب میں تم عفو و درگزر سے کام لو یہاں تک کہ اللہ خود اپنا فیصلہ نافذ کردے ۔ مطمئن رہو کہ اللہ ہر چیز پر قدرت رکھتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,110,'2:110','نماز قائم کرو اور زکوٰۃ دو ۔ تم اپنی عاقبت کے لئے جو بھلائی کماکر آگے بھیجو گے ، اللہ کے ہاں اسے موجود پاؤگے جو کچھ تم کرتے ہو وہ سب اللہ کی نظر میں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,111,'2:111','ان کا کہنا ہے کہ کوئی شخص جنت میں نہ جائے اگ ، جب تک کہ وہ یہودی نہ ہو یا عیسائی نہ ہو ۔ یہ ان کی تمنائیں ہیں ان سے کہو اپنی دلیل پیش کرو ، اگر تم اپنے دعوے میں سچے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,112,'2:112','دراصل نہ تمہاری کچھ خصوصیت ہے نہ کسی اور کی ۔ حق یہ ہے کہ جو بھی اپنی ہستی کو اللہ کی اطاعت میں سونپ دے اور عملاًنیک روش پر چلے ۔ اس کے لئے اس کے رب کے پاس اس کا اجر ہے اور ایسے لوگوں کے لئے کسی خوف یا رنج کا موقع نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,113,'2:113','یہودی کہتے ہیں کہ عیسائیوں کے پاس کچھ نہیں ۔ عیسائی کہتے ہیں یہودیوں کے پاس کچھ نہیں ۔ حالانکہ دونوں ہی کتاب پڑھتے ہیں اور اس قسم کے دعوے ایسے لوگوں کے بھی ہیں جن کے پاس کتاب کا علم نہیں ۔ یہ اختلاف جن میں یہ لوگ مبتلا ہیں ان کا فیصلہ قیامت کے روز اللہ کردے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,114,'2:114','اور اس شخص سے بڑھ کر ظالم کون ہوگا جو اللہ کے معبدوں میں اس کے نام کی یاد سے روکے اور ان کی ویرانی کے درپے ہو ؟ ایسے لوگ اس قابل ہیں کہ ان کی عبادت گاہوں میں قدم نہ رکھیں اور اگر وہاں جائیں بھی ، تو ڈرتے ہوئے جائیں ۔ ان کے لئے تو دنیا میں رسوائی ہے اور آخرت میں عذاب عظیم ۔');
INSERT INTO "sayyid_qutb" VALUES(2,115,'2:115','مشرق اور مغرب سب اللہ کے ہیں جس کی طرف بھی تم رخ کروگے اسی طرح اللہ کا رخ ہے ۔ اللہ بڑی وسعت والا اور سب کچھ جاننے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,116,'2:116','ان کا قول ہے کہ اللہ نے کسی کو بیٹا بنایا ہے۔ اللہ پاک ہے ان باتوں سے اصل حقیقت یہ ہے کہ زمین و آسمان کی تمام موجودات اس کی ملک ہیں ۔ سب کے سب اس کے مطیع فرمان ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,117,'2:117','وہ آسمانوں اور زمین کا موجد ہے اور جس بات کا وہ فیصلہ کرتا ہے ، اس کے لئے بس یہ کم دیتا ہے کہ ہوجا ، وہ ہوجاتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,118,'2:118','نادان کہتے ہیں کہ اللہ خود ہم سے بات کیوں نہیں کرتا یا کوئی نشانی ہمارے پس کیوں نہیں آتی ۔ ایسی ہی باتیں ان سے پہلے لوگ بھی کیا کرتے تھے ۔ ان سب کی ذہنیتیں ایک جیسی ہیں ۔ یقین دلانے والوں کے لئے تو ہم نشانیاں صاف صاف نمایاں کرچکے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,119,'2:119','ہم نے تم علم حق کے ساتھ خوشخبری دینے والا اور ڈرانے والا بناکر بھیجا ہے۔ اب جو لوگ جہنم سے رشتہ جوڑ چکے ہیں ان کی طرف سے تم ذمہ دار و جواب دہ نہیں ہو۔');
INSERT INTO "sayyid_qutb" VALUES(2,120,'2:120','یہودی اور عیسائی تم سے ہرگز راضی نہ ہوں گے جب تک تم ان کے طریقے پر چلنے نہ لگو ۔ صاف صاف کہہ دو کہ راستہ بس وہی ہے جو اللہ نے بنایا ہے ۔ ورنہ اگر اس علم کے بعد ، جو تمہارے پاس آچکا ہے ، تم نے ان کی خواہشات کی پیروی کی ، تو اللہ کی پکڑ سے بچانے والا کوئی دوست اور مددگار تمہارے لئے نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,121,'2:121','جن لوگوں کو ہم نے کتاب دی ہے ، وہ اسے اسی طرح پڑھتے ہیں جیسا کہ پڑھنے کا حق ہے ۔ وہ اس پرسچے دل سے ایمان لاتے ہیں ، اور جو اس کے ساتھ کفر کا رویہ اختیار کریں وہی اصل میں نقصان اٹھانے والے ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,122,'2:122','اے بنی اسرائیل یاد کرو میری نعمت جس سے میں نے تمہیں نوازا تھا اور یہ کہ میں نے تمہیں دنیا کی تمام قوموں پر فضیلت دی تھی');
INSERT INTO "sayyid_qutb" VALUES(2,123,'2:123','اور ڈرو اس دن سے جب کوئی کسی کے ذرا کام نہ آئے گا نہ کسی کا فدیہ قبول کیا جائے گا ، نہ کوئی سفارش کسی آدمی کو فائدہ دے گی اور نہ مجرموں کو کہیں سے کوئی مدد پہنچ سکے گی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,124,'2:124','یاد کرو جب ابراہیم (علیہ السلام) کو اس کے رب نے چند باتوں میں آزمایا اور وہ ان سب میں پورا اتر گیا تو اس نے کہا میں تجھے لوگوں کا پیشوا بنانے والا ہوں۔ “ ابراہیم (علیہ السلام) نے عرض کیا ! اور میری اولاد سے بھی یہی وعدہ ہے ؟ اس نے جواب دیا میرا وعدہ ظالموں سے متعلق نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,125,'2:125','اور یہ کہ ہم نے اس گھر کو لوگوں کے لئے مرکز اور امن کی جگہ قرار دیا تھا اور لوگوں کو حکم دیا تھا کہ ابراہیم (علیہ السلام) جہاں عبادت کے لئے کھڑا ہوتا ہے اس مقام کو مستقل جائے نماز بنالو اور ابراہیم (علیہ السلام) اور اسماعیل (علیہ السلام) کو تاکید کی تھی کہ میرے اس گھر کو طواف اور اعتکاف اور رکوع اور سجدہ کرنے والوں کے لئے پاک رکھو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,126,'2:126','اور یہ کہ ابراہیم (علیہ السلام) نے کہا ! اے میرے رب ، اس شہر کو امن کا شہر بنا اور اس کے باشندوں میں جو اللہ اور آخرت کو مانیں ، انہیں ہر قسم کے پھلوں کا رزق دے۔ جواب میں اس کے رب نے فرمایا ! اور جو نہ مانے گا دنیا کی چند روزہ زندگی کا سامان تو میں اسے بھی دوں گا مگر آخر کار اسے جہنم کی طرف گھسیٹوں گا اور وہ بدترین ٹھکانہ ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,127,'2:127','اور یاد کرو ابراہیم (علیہ السلام) جب اس گھر کی دیواریں اٹھا رہے تھے ، تو یہ دعا کرتے جاتے تھے ! اے ہمارے رب ! ہم سے یہ خدمت قبول فرمالے تو سب کی سننے اور سب کچھ جاننے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,128,'2:128','اے ہمارے رب ، ہم دونوں کو اپنا مسلم (مطیع فرمان) بنا ، ہماری نسل سے ایک ایسی قوم اٹھا جو تیری مسلم ہو ، ہمیں اپنی عبادت کے طریقے بتا ، اور ہماری کوتاہیوں سے درگزرفرما ، تو بڑا معاف کرنے والا ، رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,129,'2:129','اور اے رب ! ان لوگوں میں خود ان کی قوم سے ایک رسول اٹھائیو ، جو انہیں تیری آیات سنائے ۔ “ ان کو کتاب اور حکمت کی تعلیم دے اور ان کی زندگیاں سنوار دے ۔ تو بڑا مقتدر اور حکیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,130,'2:130','اور کون ہے جو ابراہیم کے طریقے سے نفرت کرے ؟ جس نے خود کو اپنے آپ کو حماقت وجہالت میں مبتلا کرلیا ہو ، اس کے سوا کون یہ حرکت کرسکتا ہے ؟ ابراہیم تو وہ شخص ہے جو کو ہم نے دنیا میں اپنے کام کے لئے چن لیا تھا اور آخٰرت میں اس کا شمار صالحین میں ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(2,131,'2:131','اس کا حال یہ تھا کہ جب اس کے رب نے اس سے کہا مسلم ہوجا تو اس نے فوراً کہا میں مالک کائنات کا مسلم ہوگیا “');
INSERT INTO "sayyid_qutb" VALUES(2,132,'2:132','اسی طریقے پر چلنے کی ہدایت اس نے اپنی اولاد کو کی تھی اور اسی کی وصیت یعقوب اپنی اولاد کو کر گیا ۔ اس نے کہا تھا میرے بچو اللہ نے تمہارے لئے یہی دین پسند کیا ہے ۔ لہٰذا مرتے دم تک مسلم رہنا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,133,'2:133','پھر کیا تم اس وقت موجود تھے جب یعقوب اس دنیا سے رخصت ہورہا تھا اور اس نے مرتے وقت اپنے بیٹوں سے پوچھا بچو میرے بعد تم کس کی بندگی کروگے ؟ ان سب نے جواب دیا ! ہم اس اللہ کی بندگی کریں گے جسے آپ کے بزرگوں ابراہیم (علیہ السلام) اسماعیل (علیہ السلام) اور اسحاق (علیہ السلام) نے اللہ مانا ہے اور ہم اس کے مسلم ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,134,'2:134','وہ کچھ لوگ تھے جو گزرگئے ۔ جو کچھ انہوں نے کمایا ، وہ ان کے لئے ہے اور جو کچھ تم کماؤگے وہ تمہارے لئے ہے ۔ تم سے یہ نہ پوچھا جائے گا کہ وہ کیا کرتے تھے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,135,'2:135','یہودی کہتے ہیں ! یہودی ہوجاؤ ہدایت پاؤگے ، عیسائی کہتے ہیں عیسائی ہوجاؤ تو ہدایت پاؤگے ۔ ان سے کہو ، نہیں بلکہ سب کو چھوڑ کر ابراہیم (علیہ السلام) کا طریقہ ........ اور ابراہیم (علیہ السلام) مشرکوں میں سے نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(2,136,'2:136','کہو ہم ایمان لائے اللہ پر اور اس ہدایت پر جو ہماری طرف نال ہوئی ہے اور جو ابراہیم (علیہ السلام) ، اسماعیل (علیہ السلام) ، اسحاق (علیہ السلام) ، یعقوب (علیہ السلام) اور اولاد یعقوب (علیہم السلام) کی طرف نازل ہوئی تھی اور جو موسیٰ (علیہ السلام) اور عیسیٰ (علیہ السلام) اور دوسرے تمام پیغمبروں کو ان کے رب کی طرف سے دی گئی ۔ ہم ان کے درمیان کوئی تفریق نہیں کرتے اور ہم اللہ کے مسلم ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,137,'2:137','پھر اگر وہ اسی طرح ایمان لائیں جس طرح تملائے تو ہدایت پر ہیں اور اگر وہ اس سے منہ پھیریں تو ، کھلی بات ہے کہ وہ ہٹ دھرمی میں پڑگئے ہیں ۔ لہٰذا اطمینان رکھو کہ ان کے مقابلے میں اللہ تمہاری حمایت کے لئے کافی ہے وہ سب کچھ سنتا اور جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,138,'2:138','کہو اللہ کا رنگ اختیار کرو ۔ اس کے رنگ سے اچھا اور کوئی رنگ نہ ہوگا ؟ اور ہم اس کی بندگی کرنے والے لوگ ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,139,'2:139','اے نبی ان سے کہو ! کیا تم اللہ کے بارے میں ہم سے جھگڑتے ہو حالانکہ وہی ہمارا رب بھی ہے اور تمہارا رب بھی ۔ ہمارے اعمال ہمارے لئے ہیں ، تمہارے اعمال تمہارے لئے ہیں ، اور ہم اللہ کے لئے اپنی بندگی کو خالص کرچکے ہیں');
INSERT INTO "sayyid_qutb" VALUES(2,140,'2:140','یا پھر تمہارا کہنا یہ ہے کہ ابراہیم (علیہ السلام) ، اسماعیل (علیہ السلام) ، اسحاق (علیہ السلام) ویعقوب (علیہ السلام) اور اولاد یعقوب (علیہم السلام) سب یہودی یا نصرانی تھے ؟ کہو تم زیادہ جانتے ہو یا اللہ ؟ اس شخص سے بڑا ظالم کون ہوگا جس کے ذمے اللہ کی طرف سے گواہی ہو اور وہ اسے چھپائے ؟ تمہاری حرکات سے اللہ غافل تو نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(2,141,'2:141','وہ کچھ لوگ تھے جو گزرچکے ۔ ان کی کمائی ان کے لئے تھی اور تمہاری تمہارے لئے ۔ تم سے ان کے اعمال کے متعلق سوال نہیں ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,142,'2:142','نادان لوگ ضرور کہیں گے ! انہیں کیا ہوا کہ پہلے یہ جس قبلے کی طرف رخ کرکے نماز پڑھتے تھے ، اس سے یکایک پھرگئے ؟ اے نبی ! ان سے کہو کہ مشرق ومغرب سب اللہ کے ہیں ۔ اللہ جسے چاہتا ہے سیدھی راہ دکھا دیتا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,143,'2:143','اور اس طرح تو ہم نے تم مسلمانوں کو ایک امت وسط “ بنایا ہے تاکہ تم دنیا کے لوگوں پر گواہ ہو اور رسول تم پر گواہ ہو۔ پہلے جس طرف تم رخ کرتے تھے ، اس کو تو ہم نے صرف یہ دیکھنے کے لئے قبلہ مقرر کیا تھا کہ کون رسول کی پیروی کرتا ہے اور کون الٹا پھرجاتا ہے ۔ معاملہ تھا تو بڑا سخت ، مگر ان لوگوں کے لئے کچھ بھی سخت ثابت نہیں ہواجو اللہ کی ہدایت سے فیض یاب تھے ، اللہ تمہارے اس ایمان کو ہرگز ضائع نہ کرے گا یقین جانو کہ وہ لوگوں کے حق میں نہایت شفیق رحیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,144,'2:144','یہ تمہارے منہ کا بار بار آسمان کی طرف اٹھنا ہم دیکھ رہے ہیں ۔ لو ہم اس قبلے کی طرف تمہیں پھیر دیتے ہیں جسے تم پسند کرتے ہو ۔ مسجد حرام کی طرف رخ پھیر دو ۔ اب جہاں کہیں تم ہو اس کی طرف منہ کرکے نماز پڑھا کرو ۔ یہ لوگ جنہیں کتاب دی گئی ہے ، خوب جانتے ہیں کہ تحویل قبلہ کا یہ حکم ان کے رب ہی کی طرف سے ہے اور برحق ہے ، مگر اس کے باوجود جو کچھ یہ کررہے ہیں ، اللہ تعالیٰ اس سے غافل نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,145,'2:145','تم ان اہل کتاب کے پاس خواہ کوئی نشانی لے آؤ ، ممکن نہیں کہ یہ تمہار قبلے کی پیروی کرنے لگیں اور نہ تمہارے لئے یہ ممکن ہے کہ ان کے قبلے کی پیروی کرو ، اور ان میں سے کوئی گروہ بھی دوسرے کے قبلے کی پیروی کے لئے تیا ر نہیں ہے اور اگر تم نے اس علم کے بعد ، جو تمہارے پاس آچکا ہے ، ان کی خواہشات کی پیروی کی تو یقیناً تمہارا شمار ظالموں میں ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(2,146,'2:146','جن لوگوں کو ہم نے کتاب دی ہے ، وہ اس مقام کو (جسے قبلہ بنایا گیا ہے ) ایسا پہچانتے ہیں ہیں ، جیسے اپنی اولاد کو پہچانتے ہیں ۔ مگر ان میں سے ایک گروہ جانتے بوجھتے حق کو چھپارہا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,147,'2:147','یہ قطعی ایک امر حق ہے تمہارے رب کی طرف سے ، لہٰذا اس کے متعلق تم ہرگز کسی شک میں نہ پڑو۔');
INSERT INTO "sayyid_qutb" VALUES(2,148,'2:148','ہر ایک کے لئے ایک رخ ہے ، جس کی طرف وہ مڑتا ہے ۔ بس تم بھلائیوں کی طرف سبقت کرو ۔ جہاں بھی تم ہوگے ، اللہ تمہیں پالے گا ۔ اس کی قدرت سے کوئی چیز باہر نہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,149,'2:149','تمہارا گزر جس مقام سے بھی ہو ، وہیں سے اپنا رخ (نماز کے وقت ) مسجد حرام کی طرف پھیردو ، کیونکہ یہ تمہارے رب کا بالکل برحق فیصلہ ہے اور اللہ تم لوگوں کے اعمال سے بیخبر نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,150,'2:150','اور جہاں بھی تمہارا گزر ہو ، اپنا رخ مسجد حرام کی طرف پھیرا کرو اور جہاں بھی تم ہو ، اسی کی طرف منہ کرکے نماز پڑھو تاکہ لوگوں کو تمہارے خلاف کوئی حجت نہ ملے ۔ ہاں جو ظالم ہیں ان کی زبان کسی حال میں بھی بند نہ ہوگی ۔ تو ان سے تم نہ ڈرو ، بلکہ مجھ سے ڈرو ، اور اس لئے کہ میں تم پر اپنی نعمت پوری کردوں ۔ اس توقع پر کہ تم یہ ہدایت و فلاح کا راستہ پالو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,151,'2:151','جس طرح میں نے تمہارے درمیان خودتم میں سے ایک رسول بھیجا ، جو تمہیں میری آیات سناتا ہے ، تمہاری زندگی کو سنوارتا ہے ، تمہیں کتاب اور سنت کی تعلیم دیتا ہے اور تمہیں وہ باتیں سکھاتا ہے جو تم نہ جانتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(2,152,'2:152','لہٰذا تم مجھے یاد رکھو ، میں تمہیں یاد رکھوں گا اور میرا شکر ادا کرو ، کفران نعمت نہ کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,153,'2:153','اے ایمان لانے والو ! صبر اور نماز سے مددلو اللہ صبر کرنے والوں کے ساتھ ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,154,'2:154','اور جو اللہ کی راہ میں مارے جائیں ، انہیں مردہ نہ کہو ، ایسے لوگ تو حقیقت میں زندہ ہیں ، مگر تمہیں ان کی زندگی کا شعور نہیں ہوتا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,155,'2:155','اور ہم ضرور تمہیں خوف وخطر ، فاقہ کشی ، جان ومال کے نقصانات اور آمدنیوں کے گھاٹے میں مبتلا کرکے تمہاری آزمائش کریں گے۔ اور خوشخبری سنائیں ان صبر کرنے والوں کو ،');
INSERT INTO "sayyid_qutb" VALUES(2,156,'2:156','ان حالات میں جو لوگ صبر کریں اور جب کوئی مصیبت پڑے تو کہیں کہ ہم اللہ ہی کے ہیں اور اللہ ہی کی طرف ہمیں پلٹ کر جانا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,157,'2:157','ان پر ان کے رب کی طرف سے بڑی عنایت ہوگی ، اس کی رحمت ان پر سایہ کرے گی اور ایسے لوگ راست رو ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,158,'2:158','بیشک صفا ومروہ اللہ کی نشانیوں میں سے ہیں ۔ لہٰذا جو شخص بیت اللہ کا حج یا عمرہ کرے اس کے لئے کوئی گناہ کی بات نہیں کہ وہ ان دونوں پہاڑوں کے درمیان سعی کرے اور جو شخص برضا ورغبت کوئی بھلائی کا کام کرے گا ، اللہ کو اس کا علم ہے اور وہ اس کی قدر کرنے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,159,'2:159','جو لوگ ہماری نازل کی ہوئی روشن تعلیمات اور ہدایات کو چھپاتے ہیں ، دراں حالیکہ ہم انہیں سب انسانوں کی راہنمائی کے لئے اپنی کتاب میں بیان کرچکے ہیں ۔ یقین کرو کہ اللہ بھی ان پر لعنت کرتا ہے اور تمام لعنت کرنے والے بھی ان پر لعنت بھیجتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(2,160,'2:160','البتہ جو اس روش سے باز آجائیں اور اپنے طرز عمل کی اصلاح کریں اور جو کچھ چھپاتے تھے ، اسے بیان کرنے لگیں ، ان کو میں معاف کردوں گا اور میں بڑا درگزر کرنے والا اور رحم کرنے والا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(2,161,'2:161','جن لوگوں نے کفر کا رویہ اختیار کیا اور کفر کی حالت ہی میں جان دے دی ان پر اللہ اور فرشتوں اور تمام انسانوں کی لعنت ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,162,'2:162','اس لعنت زدہ زندگی کی حالت میں وہ ہمیشہ رہیں گے ، نہ ان کی سزا میں تخفیف ہوگی اور نہ انہیں پھر کوئی دوسری مہلت دی جائے گی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,163,'2:163','تمہارا اللہ ایک ہی اللہ ہے ، اس رحمٰن اور رحیم کے سوا کوئی اور اللہ نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,164,'2:164','جو لوگ عقل سے کام لیتے ہیں ان کے لئے آسمانوں اور زمین کی ساخت میں ، رات اور دن کے پیہم ایک دوسرے کے بعد آنے میں ، ان کشتیوں میں جو انسانوں کے لئے نفع کی چیزیں لئے ہوئے دریاؤں اور سمندروں میں چلتی پھرتی ہیں ، بارش کے اس پانی میں جسے اللہ اوپر سے برساتا ہے پھر اس کے ذریعہ زمین کو زندگی بخشتا ہے ۔ اور اپنے اسی انتظام کی بدولت زمین میں ہر قسم کی جاندار مخلوق کو پھیلاتا ہے ۔ ہواؤں کی گردش میں اور ان بادلوں میں جو (زمین و آسمان کے درمیان تابع فرمان بناکر کر کھے گئے ہیں ، بیشمار نشانیاں ہیں) ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,165,'2:165','اور کچھ لوگ ایسے ہیں جو اللہ کے سوا دوسروں کو اس کا ہمسر اور مدمقابل بناتے ہیں ۔ اور ان کے لئے ایسے گرویدہ ہیں جیسے اللہ کے ساتھ گرویدگی ہونی چاہئے ۔ کاش ، جو کچھ عذاب سامنے دیکھ کر ، انہیں سوجھنے والا ہے ۔ وہ آج ہی ان ظالموں کو سوجھ جائے کہ ساری طاقتیں اور سارے اختیارات اللہ ہی کے قبضے میں ہیں ۔ اور یہ کہ اللہ سزا دینے میں بھی بہت سخت ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,166,'2:166','جب وہ سزا دے گا اس وقت کیفیت یہ ہوگی کہ وہی پیشوا اور راہ نما ، جن کی دنیا میں پیروی کی گئی تھی ، اپنے پیروؤں سے بےتعلقی ظاہر کریں گے ، مگر سزا پاکر رہیں گے ۔ اور ان کے اسباب و وسائل کا سلسلہ کٹ جائے گا');
INSERT INTO "sayyid_qutb" VALUES(2,167,'2:167','اور وہ لوگ جو دنیا میں ان کی پیروی کرتے تھے ، کہیں گے کاش ہم کو پھر ایک موقع دیا جاتا تو جس طرح آج یہ ہم سے بیزاری کا اظہار کررہے ہیں ، ہم ان سے بیزار ہوکر دکھادیتے ۔ یوں اللہ ان لوگوں کے وہ اعمال ، جو یہ دنیا میں کررہے ہیں ، ان کے سامنے اس طرح لائے گا کہ یہ حسرتوں اور پشیمانیوں کے ساتھ ہاتھ ملتے رہیں گے مگر آگ سے بچنے کی کوئی راہ نہ پائیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,168,'2:168','لوگو ! زمین میں جو حلال اور پاک چیزیں ہیں انہیں کھاؤ اور شیطان کے بتائے ہوئے راستوں پر نہ چلو ۔ وہ تو تمہارا کھلا دشمن ہے ،');
INSERT INTO "sayyid_qutb" VALUES(2,169,'2:169','تمہیں بدی اور فحش کا حکم دیتا ہے اور یہ سکھاتا ہے کہ تم اللہ کے نام پر وہ باتیں کہو جن کے متعلق تمہیں علم نہیں ہے کہ اللہ نے فرمائی ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,170,'2:170','ان سے جب کہا جاتا ہے کہ اللہ نے جو احکام نازل کئے ہیں ان کی پیروی کرو تو جواب دیتے ہیں کہ ہم تو اسی طریقے کی پیروی کریں گے جس پر ہم نے اپنے باب دادا کو پایا ہے ۔ اچھا اگر ان کے باپ دادا نے عقل سے کچھ بھی کام نہ لیا ہو اور وہ راہ راست نہ پائی ہو تو کیا پھر بھی یہ انہی کی پیروی کئے چلے جائیں گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(2,171,'2:171','ان کی حالت بالکل ایسی ہے جیسے چرواہا جانوروں کو پکارتا ہے ۔ اور وہ ہانک پکار کی صدا کے سوا کچھ نہیں سنتے ۔ یہ بہرے ہیں ، گونگے ہیں ، اندھے ہیں ، اس لئے کوئی بات ان کی سمجھ میں نہیں آتی۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,172,'2:172','اے ایمان لانے والو ! اگر تم حقیقت میں اللہ ہی کی بندگی کرنے والے ہو ، تو پاک چیزیں ہم نے تمہیں بخشی ہیں انہیں بےتکلف کھاؤ اور اللہ کا شکر ادا کرو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,173,'2:173','اللہ کی طرف سے اگر کوئی پابندی تم پر ہے ، تو وہ یہ ہے کہ مردار نہ کھاؤ۔ خون سے اور سور کے گوشت سے پرہیزکرو ۔ اور کوئی ایسی چیز نہ کھاؤ جس اللہ کے سوا کسی اور کا نام لیا گیا ہو۔ ہاں جو شخص مجبوری کی حالت میں ہو اور وہ ان میں سے کوئی چیز کھالے بغیر اس کے کہ وہ قانون شکنی کا ارادہ رکھتا ہو یا ضرورت کی حد سے تجاوز کرے ، تو اس پر کچھ گناہ نہیں اللہ بخشنے والا اور رحم کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,174,'2:174','حق یہ ہے کہ جو لوگ ان احکام کو چھپاتے ہیں جو اللہ نے اپنی کتاب میں نازل کئے ہیں اور تھوڑے سے دنیوی فائدوں پر انہیں بھینٹ چڑھاتے ہیں ، وہ دراصل اپنے پیٹ آگ سے بھر رہے ہیں ۔ قیامت کے روز اللہ ہرگز ان سے بات نہ کرے گا ، نہ انہیں پاکیزہ ٹھہرائے گا اور ان کے لئے دردناک سزا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,175,'2:175','یہ وہ لوگ ہیں جنہوں نے ہدایت کے بدلے ضلالت خریدی اور مغفرت کے بدلے عذاب مول لیا ۔ کیسا عجیب ہے ان کا حوصلہ کہ جہنم کا عذاب برداشت کرنے کے لئے تیار ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,176,'2:176','یہ سب کچھ اس وجہ سے ہوا کہ اللہ نے تو ٹھیک ٹھیک حق کے مطابق کتاب نازل کی تھی ، مگر جن لوگوں نے کتاب میں اختلاف نکالے وہ اپنے جھگڑوں میں حق سے بہت دورنکل گئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,177,'2:177','نیکی یہ نہیں ہے کہ تم نے اپنے چہرے مشرق کی طرف کرلئے یا مغرب کی طرف ، بلکہ نیکی یہ ہے کہ آدمی اللہ کو اور یوم آخر اور ملائکہ کو اور اللہ کی نازل کی ہوئی کتاب اور اس کے پیغمبروں کو دل سے مانے اور اللہ کی محبت میں اپنا مال رشتے داروں اور یتیموں پر ، مسکینوں اور مسافروں پر ، مدد کے لئے ہاتھ پھیلانے والوں پر اور غلاموں کی رہائی پر خرچ کرے ، نماز قائم کرے اور زکوٰۃ دے اور نیک وہ لوگ ہیں کہ جب عہد کریں ، تو وفا کریں اور تنگی اور مصیبت کے وقت میں اور حق و باطل کی جنگ میں صبر کریں ۔ یہ ہیں راست باز لوگ اور یہی متقی ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,178,'2:178','اے ایمان والو ! تمہارے لئے قتل کے مقدمات میں قصاص کا حکم لکھ دیا گیا ہے ۔ آزاد آدمی نے قتل کیا ہو ، تو اس آزاد ہی سے بدلہ لیاجائے ، غلام قاتل ہو ، تو وہ غلام ہی قتل کیا جائے اور عورت اس جرم کی مرتکب ہو تو اس عورت ہی سے قصاص لیاجائے ۔ ہاں کسی قاتل کے ساتھ اس کا بھائی کچھ نرمی کرنے کے لئے تیار ہو ، تو معروف طریقے کے مطابق خون بہا کا تصفیہ ہونا چاہئے اور قاتل کو لازم ہے کہ وہ راستی کے ساتھ خون بہا ادا کرے ۔ تمہارے رب کی طرف سے تخفیف اور رحمت ہے ۔ اس پر بھی جو زیادتی کرے اس کے لئے دردناک سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,179,'2:179','عقل رکھنے والو ! تمہارے قصاص میں زندگی ہے ۔ امید ہے کہ تم اس قانون کی خلاف ورزی سے پرہیز کروگے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,180,'2:180','تم پر فرض کیا گیا کہ جب تم میں سے کسی کی موت کا وقت آئے اور وہ اپنے پیچھے مال چھوڑرہاہو ، تو والدین اور رشتہ داروں کے لئے معروف طریقے سے وصیت کرے ۔ یہ حق ہے متقی لوگوں پر ۔');
INSERT INTO "sayyid_qutb" VALUES(2,181,'2:181','پھر جنہوں نے وصیت سنی اور بعد میں اسے بدل ڈالا ، تو اس کا گناہ بدلنے والوں پر ہوگا۔ اللہ سب کچھ سنتا اور جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,182,'2:182','البتہ جس کو یہ اندیشہ ہو کہ وصیت کرنے والے نے نادانستہ یا قصداً حق تلفی کی ہے اور معاملہ سے تعلق رکھنے والوں کے درمیان وہ اصلاح کرے تو اس پر کچھ گناہ نہیں ہے ، اللہ بخشنے والا اور رحم فرمانے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,183,'2:183','اے ایمان والو ! تم پر روزے فرض کردیئے گئے ہیں جس طرح تم سے پہلے انبیاء کے پیروؤں پر فرض کئے گئے تھے ۔ اس سے توقع ہے کہ تم میں تقویٰ کی صفت پیدا ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(2,184,'2:184','چند مقرر دنوں کے روزے ہیں ۔ اگر تم میں کوئی بیمار ہو ، یا سفر پر ہو ، تو دوسرے دنوں میں اتنی تعداد پوری کرلے اور جو روزہ رکھنے کی قدرت رکھتے ہیں (پھر بھی نہ رکھیں ) تو وہ فدیہ دیں ۔ ایک روزے کا فدیہ ایک مسکین کو کھانا کھلانا ہے اور جو اپنی خوشی سے کچھ زیادہ بھلائی کرے ، تو یہ اسی کے لئے بہتر ہے ۔ لیکن اگر تم سمجھو ، تو تمہارے حق میں اچھا یہی ہے کہ روزہ رکھو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,185,'2:185','رمضان وہ مہینہ ہے ، جس میں قرآن نازل کیا گیا ، جو انسانوں کے لئے سراسر ہدایت ہے اور ایسی واضح تعلیمات پر مشتمل ہے ، جو راہ راست دکھانے والی اور حق و باطل کا فرق کھول کر رکھ دینے والی ہیں ۔ لہٰذا اب جو شخص اس مہینے کو پائے ، اس کو لازم ہے اس کے پورے مہینے کے روزے رکھے اور جو کوئی مریض ہو یا سفر پر ہو ، تو دوسرے دنوں میں روزوں کی تعداد کو پوری کرے ۔ اللہ تمہارے ساتھ نرمی چاہتا ہے ، سختی نہیں کرنا چاہتا۔ اس لئے یہ طریقہ تمہیں بتایا جارہا ہے تاکہ تم روزوں کی تعداد کو پورا کرسکو اور جس ہدایت سے اللہ نے تمہیں سرفراز کیا ہے ، اس پر اللہ کی کبریائی کا اظہار واعتراف کرو اور شکر گزار بنو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,186,'2:186','میرے بندے اگر تم سے میرے متعلق پوچھیں تو انہیں بتادو کہ میں ان سے قریب ہی ہوں۔ پکارنے والا جب مجھے پکارتا ہے ، میں اس کی پکار کو سنتا اور جواب دیتا ہوں۔ لہٰذا انہیں چاہئے کہ میری دعوت پر لبیک کہیں اور مجھ پر ایمان لائیں شاید کہ وہ راہ راست پالیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,187,'2:187','تمہارے لیے روزوں کے مہینے میں راتوں کو اپنی بیویوں کے پاس جانا جائز قرار دیا گیا ہے ۔ وہ تمہارے لئے لباس ہیں اور تم ان کے لئے لباس ہو ۔ اللہ کو معلوم ہوگیا کہ تم لوگ چپکے چپکے اپنے آپ سے خیانت کررہے تھے مگر میں نے تمہارے قصور معاف کردیئے اور تم سے درگز فرمایا ۔ اب تم راتوں کو کھاؤ پیو یہاں تک کہ تم کو سیاہی شب کی دھاری سے سپیدہ صبح کی دھاری نمایاں نظر آجائے ۔ تب یہ سب کام چھوڑ کر رات تک اپنا روزہ پورا کرو ، اور جب تم مسجدوں میں معتکف ہو ، بیویوں سے مباشرت نہ کرو۔ یہ اللہ کی باندھی ہوئی حدیں ہیں ، ان کے قریب نہ پھٹکنا ، اس طرح اللہ اپنے احکام لوگوں کے لئے بصراحت بیان کرتا ہے ، توقع ہے کہ وہ غلط رویے سے بچیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,188,'2:188','اور تم لوگ نہ آپس میں ایک دوسرے کے مال نارواطریقے سے کھاؤ اور نہ حاکموں کے آگے ان کو اس غرض کے لئے پیش کرو کہ تمہیں دوسروں کے مال کا کوئی حصہ قصداً ظالمانہ طریقہ سے کھانے کا موقع مل جائے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,189,'2:189','اے نبی ، لوگ تم سے چاند کی گھٹنی بڑھنی کے متعلق پوچھتے ہیں ، کہو یہ لوگوں کے لئے تاریخوں کی تعین کی اور حج کی علامتیں ہیں ، نیز ان سے کہو کہ یہ کوئی نیکی کا کام نہیں ہے کہ تم اپنے گھروں میں پیچھے کی طرف داخل ہوتے ہو ۔ نیکی تو اصل یہ ہے کہ آدمی اللہ کی ناراضی سے بچے ۔ لہٰذا تم اپنے گھروں میں دروازے ہی سے آیا کرو ۔ البتہ اللہ سے ڈرتے رہو ، شاید کہ تمہیں فلاح نصیب ہوجائے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,190,'2:190','اور تم اللہ کی راہ میں ان لوگوں سے لڑو جو تم سے لڑتے ہیں ، مگر زیادتی نہ کرو کہ اللہ زیادتی کرنے والوں کو پسند نہیں کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,191,'2:191','ان سے لڑو جہاں بھی تمہارا ان سے مقابلہ پیش آئے اور انہیں نکالو جہاں سے انہوں نے تم کو نکالا ، اس لئے کہ قتل اگرچہ برا ہے ، مگر فتنہ اس سے بھی زیادہ برا ہے ۔ اور مسجد حرام کے قریب جب تک وہ تم سے نہ لڑیں تم بھی نہ لڑو۔ مگر جب وہ وہاں لرنے سے نہ چوکیں تو تم بھی بےتکلف انہیں مارو کہ ایسے کافروں کی یہی سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,192,'2:192','پھر اگر وہ باز آجائیں تو جان لو کہ اللہ معاف کرنے والا رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,193,'2:193','تم ان سے لڑتے رہو ، یہاں تک کہ فتنہ باقی نہ رہے اور دین اللہ کے لئے ہوجائے ۔ پھر اگر وہ باز آجائیں تو سمجھ لو کہ ظالموں کے سوا اور کسی پر دست درازی جائز نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,194,'2:194','ماہ حرام کا بدلہ ماہ حرام ہی ہے اور تمام حرمتوں کا لحاظ برابری کے ساتھ ہوگا لہٰذا جو تم پر دست درازی کرے ، تم بھی اسی طرح اس پر دست درازی کرو البتہ اللہ سے ڈرتے رہو اور یہ جان رکھو کہ اللہ انہی لوگوں کے ساتھ جو اس کی حدود توڑنے سے پرہیز کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(2,195,'2:195','اللہ کی راہ میں خرچ کرو اور اپنے ہاتھوں اپنے آپ کو ہلاکت میں نہ ڈالو۔ احسان کا طریقہ اختیار کرو کہ اللہ محسنوں کو پسند کرتا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,196,'2:196','اللہ کی خوشنودی کے لئے جب حج اور عمرہ کی نیت کرو ، تو اسے پورا کرو اور اگر کہیں جاؤ تو جو قربانی میسر آئے ، اللہ کی جناب میں پیش کرو اور اپنے سر نہ مونڈو جب تک قربانی اپنی جگہ نہ پہنچ جائے ۔ مگر جو شخص مریض ہو یا جس کے سر میں کوئی تکلیف ہو اور اس بنا پر اپنا سر منڈوالے تو اسے چاہئے کہ فدیے کے طور پر روزہ رکھے یا صدقہ دے یا قربانی دے ، اور اگر قربانی میسر نہ ہو تو تین روزے حج کے زمانے میں اور سات گھر پہنچ کر ، اس طرح پورے دس روزے رکھ لے ۔ یہ رعایت ان لوگوں کے لئے ہے جن کے گھر مسجد حرام کے قریب نہ ہوں ۔ اللہ کے ان احکام کی خلاف ورزی سے بچو اور خوب جان لو کہ اللہ سخت سزا دینے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,197,'2:197','حج کے مہینے سب کو معلوم ہیں ۔ جو شخص ان مقرر مہینوں میں حج کی نیت کرے اسے خبردار رہنا چاہئے کہ حج کے دوران میں اس سے کوئی شہوانی فعل ، کوئی بدفعلی ، کوئی لڑائی جھگڑے کی بات سرزد نہ ہو اور جو نیک کام تم کروگے وہ اللہ کے علم میں ہوگا۔ سفر حج کے لئے زاد راہ ساتھ لے جاؤ اور سب سے بہتر زاد راہ پرہیزگاری ہے ۔ پس اے ہوشمندو ، میری نافرمانی سے پرہیز کرو');
INSERT INTO "sayyid_qutb" VALUES(2,198,'2:198','اور حج کے ساتھ ساتھ تم اپنے رب کا فضل بھی تلاش کرتے جاؤ تو اس میں کوئی مضائقہ نہیں ۔ پھر جب عرفات سے چلو تو مشعر حرام کے پاس ٹھہر کر اللہ کو یاد کرو ۔ اور اس طرح یاد کرو جس کی ہدایت اس نے تمہیں کی ہے ، ورنہ اس سے پہلے تم بھٹکے ہوئے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,199,'2:199','پھر جہاں سے سب لوگ پلٹتے ہیں ، وہیں سے تم بھی پلٹو اور اللہ سے معافی چاہو ، یقینا ً وہ معاف کرنے والا اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,200,'2:200','پھر جب اپنے رب کے احکام ادا کرچکو تو جس طرح پہلے اپنے آباء و اجداد کا ذکر کرتے تھے ، اس طرح اب اللہ کا ذکر کرو ، بلکہ اس سے بھی بڑھ کر (مگر اللہ کو یاد کرنے والے لوگوں میں بھی بہت فرق ہے ) ان میں سے کوئی تو ایسا ہے جو کہتا ہے کہ اے ہمارے رب ، ہمیں دنیا ہی میں سب کچھ دے دے ، ایسے شخص کے لئے آخرت میں کوئی حصہ نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,201,'2:201','اور کوئی کہتا ہے ، اے ہمارے رب ہمیں دنیا میں بھی بھلائی دے اور آخرت میں بھی بھلائی دے ۔ اور آگ کے عذاب سے ہمیں بچا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,202,'2:202','ایسے لوگ اپنی کمائی کے مطابق (دونوں جگہ) حصہ پائیں گے اور اللہ کو حساب چکاتے کچھ دیر نہیں لگتی ۔');
INSERT INTO "sayyid_qutb" VALUES(2,203,'2:203','یہ گنتی کے چند روز ہیں جو تمہیں اللہ کی یاد میں بسر کرنے چاہئیں ۔ پھر جو کوئی جلدی کرکے دوہی دن میں واپس ہوگیا ہو تو کوئی حرج نہیں اور جو کچھ دیر زیادہ ٹھہر کر پلٹا تو ھی کوئی حرج نہیں ۔ بشرطیکہ یہ دن اس نے تقویٰ کے ساتھ بسر کئے ہوں ۔ اللہ کی نافرمانی سے بچو اور خوب جان رکھو کہ ایک روز اس کے حضور میں تمہاری پیشی ہونے والی ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,204,'2:204','انسانوں میں کوئی تو ایسا ہے جس کی باتیں دنیا کی زندگی میں تمہیں بہت بھلی معلوم ہوتی ہیں اور اپنی نیک نیتی پر وہ بار بار اللہ کو گواہ ٹھہراتا ہے ۔ مگر حقیقت میں وہ بدترین دشمن حق ہوتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,205,'2:205','جب اسے اقتدار حاصل ہوتا ہے ، تو زمین میں اس کی ساری دوڑدھوپ اس لئے ہوتی ہے کہ فساد پھیلائے ، کھیتوں کو غارت کرے اور نسلوں کو تباہ کرے ۔ حالانکہ اللہ (جسے وہ گواہ بنارہا ہے) فساد کو ہرگز پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(2,206,'2:206','اور جب اس سے کہا جاتا ہے کہ اللہ سے ڈرو ، تو اپنے وقار کا خیال اسے گناہ پر جما دیتا ہے ۔ ایسے شخص کے لئے تو بس جہنم ہی کافی ہے ۔ اور وہ بہت برا ٹھکانہ ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,207,'2:207','دوسری طرف انسانوں ہی میں کوئی ایسا بھی ہے جو رضائے الٰہی کی طلب میں اپنی جان کھپا دیتا ہے اور ایسے بندوں پر اللہ بہت مہربان ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,208,'2:208','اے ایمان لانے والو ! تم پورے کے پورے اسلام میں آجاؤ اور شیطان کی پیروی نہ کرو کہ وہ تمہارا کھلا دشمن ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,209,'2:209','جو صاف صاف ہدایات تمہارے پاس آچکی ہیں ۔ اگر ان کو پالینے کے بعد بھی تم نے لغزش کھائی ، تو خوب جان رکھو کہ اللہ تم سب پر غالب اور حکیم ودانا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,210,'2:210','کیا اب وہ اس کے منتظر ہیں کہ اللہ بادلوں کا چترلگائے ۔ فرشتوں کے پرے ساتھ لئے ، خود سامنے آموجود ہو اور فیصلہ ہی کرڈالاجائے ۔ آخر کار سارے معاملات پیش تو اللہ ہی کے حضور ہونے والے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,211,'2:211','بنی اسرائیل سے پوچھو کیسی کھلی کھلی نشانیاں ہم نے انہیں دکھائی ہیں (اور پھر یہ بھی انہی سے پوچھ لو کہ ) اللہ کی نعمت پانے کے بعد جو قوم اس کو شقاوت سے بدلتی ہے اسے کیسی سخت سزا دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,212,'2:212','جن لوگوں نے کفر کی راہ اختیار کی ہے ، ان کے لئے دنیا کی زندگی بڑی محبوب ودل پسند بنادی گئی ہے ۔ ایسے لوگ ایمان کی راہ اختیار کرنے والوں کا مذاق اڑاتے ہیں ، مگر قیامت کے روز پرہیزگارلوگ ہی ان کے مقابلے میں عالی مقام ہوں گے ۔ رہا دنیا کا رزق تو اللہ کو اختیار ہے ، جسے چاہے بےحساب دے دے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,213,'2:213','ابتداء میں سب لوگ ایک ہی طریقے پر تھے ، (پھر یہ حالت باقی نہ رہی اور اختلافات رونما ہوگئے) تب اللہ نے نبی بھیجے جو راست روی پر بشارت دینے والے اور کج روی کے نتائج سے ڈرانے والے تھے ۔ اور ان کے ساتھ کتاب برحق نازل کی تاکہ حق کے بارے میں لوگوں کے درمیان جو اختلافات رونما ہوگئے تھے ، ان کا فیصلہ کرے ۔ اختلافات ان لوگوں نے کیا جنہیں حق کا علم دیا جاچکا تھا۔ انہوں نے روشن ہدایات پالینے کے بعد ، محض اس لئے حق کو چھوڑ کر مختلف طریقے نکالے کہ وہ آپس میں زیادتی کرنا چاہتے تھے ، پس جو لوگ انبیاء پر ایمان لے آئے ، انہیں اللہ نے اپنے اذن سے اس حق کا راستہ دکھایا جس میں لوگوں نے اختلاف کیا تھا ، اللہ جسے چاہتا ہے راہ راست دکھا دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,214,'2:214','پھر کیا تم نے یہ سمجھ رکھا ہے کہ یونہی جنت کا داخلہ تمہیں مل جائے گا ، حالانکہ ابھی تم پر وہ سب کچھ نہیں گزرا ہے ، جو تم سے پہلے ایمان لانے والوں پر گزرچکا ہے ۔ ان پر سختیاں گزریں ، مصیبتیں آئیں اور مارے گئے حتیٰ کہ وقت کا رسول اور اس کے ساتھی اہل ایمان چیخ اٹھے کہ اللہ کی مدد کب آئے گی (اس وقت انہیں تسلی دی گئی ) ہاں اللہ کی مدد قریب ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,215,'2:215','لوگ پوچھتے ہیں ہم کیا خرچ کریں ؟ “ جواب دو کہ جو مال بھی تم خرچ کرو اپنے والدین پر ، رشتے داروں پر ، یتیموں اور مسکینوں پر اور مسافروں پر خرچ کرو اور جو بھلائی بھی تم کروگے اللہ اس سے باخبر ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,216,'2:216','تمہیں جنگ کا حکم دیا گیا ہے اور وہ تمہیں ناگوار ہے ۔ ہوسکتا ہے کہ ایک چیز تمہیں ناگوار ہو اور وہی تمہارے لئے بہتر ہو ۔ اور ہو سکتا ہے کہ ایک چیز تمہیں پسند ہو اور وہی تمہارے لئے بری ہو ۔ اللہ جانتا ہے ، تم نہیں جانتے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,217,'2:217','لوگ پوچھتے ہیں کہ ماہ حرام میں لڑنا کیسا ہے ؟ کہو اس میں لڑنا بہت برا ہے ، مگر راہ خدا سے لوگوں کو روکنا اور اللہ سے کفر کرنا اور مسجد حرام کا راستہ اللہ پرستوں پر بند کرنا اور حرم کے رہنے والوں کو وہاں سے نکالنا اللہ کے نزدیک اس سے بھی زیادہ برا ہے اور فتنہ خونریزی سے شدید تر ہے ۔ وہ تو تم سے لڑتے ہی جائیں گے حتیٰ کہ اگر ان کا بس چلے تو تمہارے دین سے تم کو پھیر لے جائیں (اور یہ خوب سمجھ لو کہ ) تم میں سے جو کوئی اپنے دین سے پھرے گا اور کفر کی حالت میں جان دے گا ، اس کے اعمال دنیا وآخرت میں ضائع ہوجائیں گے ۔ ایسے سب لوگ جہنمی ہیں اور ہمیشہ جہنم میں رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,218,'2:218','بخلاف اس کے جو لوگ ایمان لائے ہیں اور جنہوں نے اللہ کی راہ میں اپنا گھر بار چھوڑا اور جہاد کیا ہے ، وہ رحمت الٰہی کے جائز امیدوار ہیں اور اللہ ان کی لغزشوں کو معاف کرنے والا اور اپنی رحمت سے انہیں نوازنے والا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,219,'2:219','پوچھتے ہیں : شراب اور جوئے کا کیا حکم ہے ؟ کہو ان دونوں چیزوں میں بڑی خرابی ہے ۔ اگرچہ ان میں لوگوں کے لئے کچھ منافع بھی ہیں مگر ان کا گناہ فائدے سے بہت زیادہ ہے ۔ “ پوچھتے ہیں : ہم کیا خرچ کریں ؟ کہو جو کچھ تمہاری ضرورت سے زیادہ ہو ، اس طرح اللہ تمہارے لئے صاف صاف احکام بیان کرتا ہے ، شاید کہ تم فکر کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,220,'2:220','دنیا وآخرت دونوں کی پوچھتے ہیں یتیموں کے ساتھ کیا معاملہ کیا جائے ؟ کہو جس طرز عمل میں ان کے لئے بھلائی ہو ، وہی اختیار کرنا بہتر ہے۔ اگر تم اپنا اور ان کا خرچ اور رہنا مشترکہ رکھو تو اس میں کوئی مضائقہ نہیں ۔ آخر وہ تمہارے بھائی بند ہی تو ہیں ۔ برائی کرنے والے اور بھلائی کرنے والے دونوں کا حال اللہ پر روشن ہے ۔ اللہ چاہتا تو اس معاملے میں تم پر سختی کرتا ۔ مگر وہ صاحب اختیار ہونے کے ساتھ صاحب حکمت بھی ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,221,'2:221','اور تم مشرک عورتوں سے ہرگز نکاح نہ کرنا ، جب تک وہ ایمان نہ لے آئیں ۔ ایک مومن لونڈی ایک مشریف شریف زادی سے بہتر ہے۔ اگرچہ وہ تمہیں بہت پسند ہو۔ اور اپنی عورتوں کے نکاح مشرک مردوں سے کبھی نہ کرنا ، جب تک وہ ایمان نہ لے آئیں ۔ ایک مومن غلام مشرک شریف سے بہتر ہے ، اگرچہ وہ تمہیں بہت پسند ہو۔ یہ لوگ تمہیں آگ کی طرف بلاتے ہیں اور اللہ اپنے اذن سے تم کو جنت اور مغفرت کی طرف بلاتا ہے ، اور وہ اپنے احکام واضح طور پر لوگوں کے سامنے بیان کرتا ہے ، توقع ہے کہ وہ سبق لیں گے اور نصیحت قبول کریں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,222,'2:222','پوچھتے ہیں حیض کا کیا حکم ہے ؟ کہو وہ ایک گندگی کی حالت ہے ۔ اس میں عورتوں سے الگ رہو اور ان کے قریب نہ جاؤ، جب تک کہ وہ پاک صاف نہ ہوجائیں ۔ پھر جب وہ پاک ہوجائیں تو ان کے پاس جاؤ۔ اس طرح جیسا کہ اللہ نے تم کو حکم دیا ہے ۔ اللہ ان لوگوں کو پسند کرتا ہے جو بدی سے باز رہیں اور پاکیزگی اختیار کریں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,223,'2:223','تمہاری عورتیں تمہاری کھیتیاں ہیں ۔ تمہیں اختیار ہے ، جس طرح چاہو ، اپنی کھیتی میں جاؤ ، مگر اپنے مستقبل کی فکر کرو اور اللہ کی ناراضی سے بچو ۔ خوب جان لو کہ تمہیں ایک دن اس سے ملنا ہے ۔ اور اے نبی ! جو تمہاری ہدایات کو مان لیں انہیں (صلاح وسعادت کی ) خوشخبری دے دو ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,224,'2:224','اللہ کے نام کو ایسی قسمیں کھانے کے لئے استعمال نہ کرو ، جن سے مقصود نیکی اور تقویٰ اور بندگان اللہ کی بھلائی کے کاموں سے باز رہناہو۔ اللہ تمہاری ساری باتیں سن رہا ہے اور سب کچھ جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(2,225,'2:225','جو بےمعنی قسمیں تم بلاارادہ کھالیا کرتے ہو ، ان پر اللہ گرفت نہیں کرتا ، مگر جو قسمیں تم سچے دل سے کھاتے ہو ، ان کی باز پرس وہ ضرور کرے گا ۔ اللہ بہت درگزر کرنے والا اور بردبار ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,226,'2:226','جو لوگ اپنی عورتوں سے تعلق نہ رکھنے کی قسم کھابیٹھتے ہیں ، ان کے لئے چار مہینے کی مہلت ہے ۔ اگر انہوں نے رجوع کرلیا ، تو اللہ معاف کرنے والا اور رحیم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,227,'2:227','اور اگر انہوں نے طلاق ہی کی ٹھان لی ہو مانے رہیں کہ اللہ سب کچھ سنتا اور جانتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,228,'2:228','جن عورتوں کو طلاق دی گئی ہو ، وہ تین مرتبہ ایام ماہواری آنے تک اپنے آپ کو روکیں رکھیں ، اور ان کے لئے یہ جائز نہیں ہے کہ اللہ نے ان کے رحم میں جو کچھ خلق فرمایا ہو ، اسے چھپائیں انہیں ہرگز ایسا نہ کرنا چاہئے ۔ اگر وہ اللہ اور روز آخر پر ایمان رکھتی ہیں۔ ان کے شوہر تعلقات درست کرلینے پر آمادہ ہوں ، تو وہ اس عدت کے دوران میں انہیں پھر اپنی زوجیت میں واپس لے لینے کے حق دار ہیں ۔ عورتوں کے لئے بھی معروف طریقے پر ویسے ہی حقوق ہیں ۔ جیسے مردوں کے حقوق ان پر ہیں ۔ البتہ مردوں کو ان پر ایک درجہ حاصل ہے ۔ اور سب پر اللہ غالب اقتدار رکھنے والا اور حکیم ودانا موجود ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,229,'2:229','طلاق دو بار ہے۔ پھر یا تو سیدھی طرح عورت کو روک لیا جائے یا بھلے طریقے سے اس کو رخصت کردیا جائے اور رخصت کرتے وقت ایسا کرنا تمہارے لئے جائز نہیں ہے کہ جو کچھ تم انہیں دے چکے ہو ، اس میں سے کچھ واپس لے لو ، البتہ یہ صورت مستثنیٰ ہے کہ زوجین کو اللہ کی حدود پر قائم نہ رہ سکنے کا اندیشہ ہو۔ ایسی صورت میں اگر انہیں یہ خوف ہو کہ وہ دونوں حدود الٰہی پر قائم نہ رہیں گے ۔ تو ان کے درمیان یہ معاملہ ہوجانے میں مضائقہ نہیں کہ عورت اپنے شوہر کو کچھ معاوضہ دے کر علیحدگی حاصل کرے ۔ یہ اللہ کی مقرر کردہ حدود ہیں ۔ ان سے تجاوز نہ کرو اور جو لوگ حدود الٰہی سے تجاوز کریں ، وہی ظالم ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,230,'2:230','پھر اگر (دو بار طلاق دینے کے بعد) تیسری بار طلاق دے دی ، تو وہ عورت پھر اس کے لئے حلال نہ ہوگی ، الا یہ کہ اس کا نکاح کسی دوسرے شخص سے ہو اور وہ اسے طلاق دے دے ۔ تب اگر پہلا شوہر اور عورت دونوں یہ خیال کریں کہ حدود الٰہی قائم رکھیں گے تو ان کے لئے ایک دوسرے کی طرف رجوع کرلینے میں کوئی مضائقہ نہیں ۔ یہ اللہ کی مقرر کردہ حدود ہیں ، جنہیں وہ ان لوگوں کی ہدایت کے لئے واضح کررہا ہے ۔ جو (جو اس کی حدوں کو توڑنے کا انجام جانتے ہیں۔ ) “');
INSERT INTO "sayyid_qutb" VALUES(2,231,'2:231','اور جب تم عورتوں کو طلاق دے دو اور ان کی عدت پوری ہونے کو آجائے ، تو یا بھلے طریقے سے انہیں روک لو یا بھلے طریقے سے رخصت کردو ۔ محض ستانے کی خاطر انہیں نہ روکے رکھنا یہ زیادتی ہوگی اور جو ایسا کرے گا ، وہ درحقیقت اپنے اوپر ظلم کرے گا۔ اللہ کی آیات کا کھیل نہ بناؤ۔ بھول نہ جاؤ کہ اللہ نے کس نعمت عظمیٰ سے تمہیں سرفراز کیا ہے ۔ وہ تمہیں نصیحت کرتا ہے کہ جو کتاب اور حکمت اس نے تم پر نازل کی ہے ، اس کا احترام ملحوظ رکھو ، اللہ سے ڈرو ، اور خوب جان لو کہ اللہ کو ہر بات کی خبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,232,'2:232','جب تم اپنی عورتوں کو طلاق دے چکو اور وہ اپنی عدت پوری کرلیں تو پھر اس میں مانع نہ ہو ، کہ وہ اپنے زیر تجویز شوہروں سے نکاح کرلیں ، جبکہ وہ معروف طریقے سے باہم مناکحت پر راضی ہوں۔ تمہیں نصیحت کی جاتی ہے کہ ایسی جرأت ہرگز نہ کرنا ، اگر تم اللہ اور روز آخر پر ایمان لانے والے ہو ، تمہارے لئے شائستہ اور پاکیزہ طریقہ یہی ہے کہ اس سے باز رہو ۔ اللہ جانتا ہے اور تم نہیں جانتے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,233,'2:233','جو باپ چاہتے ہوں کہ ان کی اولاد پوری مدت رضاعت تک دودھ پئے ، تو مائیں اپنے بچوں کو کامل دو سال دودھ پلائیں۔ اس صورت میں بچے کے باپ کو معروف طریقے سے انہیں کھاناکپڑا دینا ہوگا۔ مگر کسی پر اس کی وسعت سے زیادہ بڑھ کر بار نہ ڈالنا چاہئے۔ نہ تو ماں کو اس وجہ سے تکلیف میں ڈالا جائے کہ بچہ اس کا ہے ، نہ ہی باپ کو اس وجہ سے تنگ کیا جائے کہ بچہ اس کا ہے ۔ دودھ پلانے والی کا یہ حق جیسا کہ بچے کے باپ پر ہے ، ویسا ہی اس کے وارث پر بھی ہے ۔ لیکن اگر فریقین باہمی رضامندی اور مشورے سے دودھ چھڑانا چاہیں ، تو ایسا کرنے میں کوئی مضائقہ نہیں ۔ اگر تمہارا خیال اپنی اولاد کو کسی غیر عورت سے دودھ پلوانے کا ہو تو اس میں بھی کوئی حرج نہیں ، بشرطیکہ اس کا جو کچھ معاوضہ طے کردو ، وہ معروف طریقے پر ادا کرو۔ اللہ سے ڈرو اور جان رکھو کہ جو کچھ تم کرتے ہو ، سب اللہ کی نظر میں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,234,'2:234','تم میں سے جو لوگ مرجائیں ، ان کے پیچھے اگر ان کی بیویاں زندہ ہوں ، تو وہ اپنے آپ کو چار مہینے دس دن روکے رکھیں ، پھر جب ان کی مدت پوری ہوجائے ، تو انہیں اختیار ہے ، اپنی ذات کے معاملے میں معروف طریقے سے جو چاہیں کریں ۔ تم پر اس کی کوئی ذمہ داری نہیں۔ اللہ تم سب کے اعمال سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,235,'2:235','زمانہ عدت میں خواہ تم ان پر بیوہ عورتوں کے ساتھ منگنی کا ارادہ اشارے کنایے میں ظاہر کردو ، خواہ دل میں چھپائے رکھو ، دونوں صورتوں میں کوئی مضائقہ نہیں ۔ اللہ جانتا ہے کہ ان کا خیال تو تمہارے دل میں آئے گا ہی ۔ مگر دیکھو ، خفیہ عہدوپیمان نہ کرنا ۔ اگر کوئی بات کرنی ہے تو معروف طریقے سے کرو۔ اور عقد نکاح باندھنے کا فیصلہ اس وقت تک نہ کرو جب تک کہ عدت پوری نہ ہوجائے ۔ خوب سمجھ لو کہ اللہ تمہارے دلوں کا حال تک جانتا ہے۔ لہٰذا اس سے ڈرو اور یہ بھی جان لو کہ اللہ بردبار ہے۔ (چھوٹی چھوٹی ) باتوں سے درگزرفرماتا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,236,'2:236','تم پر کچھ گناہ نہیں ، اگر اپنی عورتوں کو طلاق دے دو ، قبل اس کے کہ ہاتھ لگانے کی نوبت آئے یا مہر مقرر ہو۔ اس صورت میں انہیں کچھ نہ کچھ دینا ضرور چاہئے ۔ خوشحال آدمی اپنی مقدرت کے مطابق اور غریب اپنی مقدرت کے مطابق معروف طریقہ سے دے ۔ یہ حق ہے نیک آدمیوں پر ۔');
INSERT INTO "sayyid_qutb" VALUES(2,237,'2:237','اگر تم نے ہاتھ لگانے سے پہلے طلاق دی ہو ، لیکن مہر مقرر کیا جاچکا ہو تو اس صورت میں نصف مہر دینا ہوگا۔ یہ اور بات ہے کہ عورت نرمی برتے (اور مہر نہ لے) یا وہ مرد ، جس کے اختیار میں عقد نکاح ہے ، نرمی سے کام لے (اور پورا مہر دے دے ) اور تم (یعنی مرد) نرمی سے کام لو ، یہ تو تقویٰ سے زیادہ مناسبت رکھتا ہے ۔ آپس کے معاملات میں فیاضی کو نہ بھولو ۔ تمہارے اعمال کو اللہ دیکھ رہا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,238,'2:238','اپنی نمازوں کی نگہداشت رکھو ، خصوصاً اس نماز کی جو درمیان میں ہے ۔ اللہ کے آگے اس طرح کھڑے ہو جیسے فرمان بردار غلام کھڑے ہوتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,239,'2:239','بدامنی کی حالت ہو ، تو خواہ پیدل ہو ، خواہ سوار ہو ، جس طرح ممکن ہو نماز پڑھو۔ اور جب امن میسر آجائے تو اللہ کو اس طریقے سے یاد کرو جو اس نے تمہیں سکھایا دیا ہے ۔ جس سے تم پہلے ناواقف تھے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,240,'2:240','تم میں سے جو لوگ وفات پائیں اور پیچھے بیویاں چھوڑ رہے ہوں ، ان کو چاہئے کہ اپنی بیویوں کے حق میں ، یہ وصیت کرجائیں کہ ایک سال تک ان کو نان ونفقہ دیا جائے اور وہ گھروں سے نہ نکالی جائیں ۔ پھر اگر وہ خود نکل جائیں تو اپنی ذات کے معاملے میں ، معروف طریقے سے وہ جو کچھ بھی کریں اس کی کوئی ذمہ داری تم پر نہیں ہے ، اللہ سب پر غالب ، اقتدار رکھنے والا اور حکیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,241,'2:241','اس طرح جن عورتوں کو طلاق دی گئی ہو ، انہیں بھی مناسب طور پر کچھ نہ کچھ دے کر رخصت کیا جائے ۔ یہ حق ہے متقی لوگوں پر ۔');
INSERT INTO "sayyid_qutb" VALUES(2,242,'2:242','اس طرح اللہ اپنے احکام تمہیں صاف صاف بتاتا ہے ۔ امید ہے کہ تم سمجھ بوجھ کر کام کروگے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,243,'2:243','تم نے ان لوگوں کے حال پر کچھ غور کیا ، جو موت کے ڈر سے اپنے گھر بار چھوڑ کر نکلے تھے اور ہزاروں کی تعداد میں تھے ؟ اللہ نے ان سے فرمایا : مرجاؤ۔ اور پھر اس نے ان کو دوبارہ زندگی بخشی ۔ حقیقت یہ ہے کہ اللہ انسان پر بڑافضل فرمانے والا ہے ، مگر اکثر لوگ شکر ادا نہیں کرتے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,244,'2:244','اللہ کی راہ میں جنگ کرو اور خوب جان رکھو کہ اللہ سننے والا اور جاننے والا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,245,'2:245','تم میں سے کون ہے ، جو اللہ کو قرض حسن دے تاکہ اللہ اسے کئی گنا بڑھا چڑھا کر واپس کردے ۔ گھٹانا بھی اس کے اختیار میں ہے اور بڑھانا بھی ، اور اسی کی طرف تمہیں پلٹ کرجانا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,246,'2:246','پھر تم نے اس معاملے پر غور کیا ، جو موسیٰ کے بعد سرداران بنی اسرائیل کو پیش آیا تھا ؟ انہوں نے اپنے نبی سے کہا ہمارے لئے ایک بادشاہ مقرر کردو تاکہ ہم اللہ کی راہ میں جنگ کریں۔ نبی نے پوچھا ! کیا کہیں ایسا تو نہ ہوگا کہ تم کو لڑائی کا حکم دیا جائے اور پھر تم نہ لڑو ؟ وہ کہنے لگے : بھلایہ کیسے ہوسکتا ہے کہ ہم راہ اللہ میں لڑیں ، جبکہ ہمیں اپنے گھروں سے نکال دیا گیا اور ہمارے بال بچے ہم سے جدا کردئیے گئے ہیں ؟ “ مگر جب ان کو جنگ کا حکم دیا گیا ؟ تو ایک قلیل تعداد کے سوا وہ سب پیٹھ موڑ گئے ، اور اللہ ان میں سے ایک ایک ظالم کو ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,247,'2:247','ان کے نبی نے ان سے کہا کہ اللہ نے طالوت کو تمہارے لئے بادشاہ مقرر کیا ہے ۔ یہ سن کر وہ بولے : ہم پر بادشاہ بننے کا وہ کیسے حق دار ہوگیا ؟ اس کے مقابلے میں بادشاہی کے ہم زیادہ مستحق ہیں ۔ وہ تو کوئی بڑا مالدار آدمی نہیں ہے۔ “ نبی نے جواب دیا : اللہ نے تمہارے مقابلے میں اس کو منتخب کیا ہے اور اس کو دماغی اور جسمانی دونوں قسم کی اہلیتیں فراوانی کے ساتھ عطا فرمائی ہیں اور اللہ کو اختیار ہے کہ اپنا ملک جسے چاہے دے ، اللہ بڑی وسعت رکھتا ہے اور سب کچھ اس کے علم میں ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,248,'2:248','ان کے نبی نے کہا : اللہ کی طرف سے اس کے بادشاہ مقرر ہونے کی علامت یہ ہے کہ اس کے عہد میں وہ صندوق تمہیں واپس مل جائے گا ، جس میں تمہارے رب کی طرف سے تمہارے لئے سکون قلب کا سامان ہے ، جس میں آل موسیٰ اور آل ہارون کے چھوڑے ہوئے تبرکات ہیں اور جس کو فرشتے اٹھائے ہوئے ہیں ۔ اگر تم مومن ہو ، تو یہ تمہارے لئے بہت بڑی نشانی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,249,'2:249','پھر جب طالوت لشکر لے کر چلا ، تو اس نے کہا : ایک دریا پر اللہ کی طرف تمہاری آزمائش ہونے والی ہے ۔ جو اس کا پانی پئے گا وہ میرا ساتھی نہیں ۔ میرا ساتھی صرف وہ ہے کہ جو اس سے پیاس نہ بجھائے ہاں ایک آدھ چلو کوئی بھر لے ، مگر ایک گروہ قلیل کے سوا وہ سب اس دریا سے سیراب ہوئے ۔ “ پھر جب طالوت اور اس کے ساتھی مسلمان دریا پار کرکے آگے بڑھے تو انہوں نے طالوت سے کہہ دیا کہ آج ہم میں جالوت اور اس کے لشکروں کا مقابلہ کرنے کی طاقت نہیں ہے ۔ “ لیکن جو لوگ یہ سمجھتے تھے کہ انہیں اللہ سے ملنا ہے انہوں نے کہا بارہا ایسا ہوا ہے کہ ایک قلیل گروہ اللہ کے اذن سے ایک بڑے گروہ پر غالب آگیا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,250,'2:250','اور جب وہ جالوت اور اس کے لشکر کے مقابلے پر نکلے تو انہوں نے دعا کی اے ہمارے رب ! ہم پر صبر کا فیضان کر ، ہمارے قدم جمادے اور اس کافر گروہ پر ہمیں فتح نصیب کر “');
INSERT INTO "sayyid_qutb" VALUES(2,251,'2:251','آخر کار اللہ کے اذن سے انہوں نے کافروں کو ماربھگایا اور داؤد نے جالوت کو قتل کردیا اور اللہ نے اسے سلطنت اور حکمت سے نوازا اور جن جن چیزوں کا چاہا ، علم دیا۔ اگر اللہ اس طرح انسانوں کے ایک گروہ کو دوسرے گروہ کے ذریعے ہٹاتا نہ رہتا ، تو زمین کا نظام بگڑجاتا۔ لیکن دنیا کے لوگوں پر اللہ کا بڑا فضل ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,252,'2:252','یہ اللہ کی آیات ہیں ، جو ہم ٹھیک ٹھیک تم کو سنا رہے ہیں اور تم یقیناً ان لوگوں میں سے ہو جو رسول بناکر بھیجے گئے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,253,'2:253','یہ رسول (ایسے تھے کہ ) ہم نے ان میں سے بعض کو بعض پر زیادہ فضیلت دی ۔ ان میں کوئی ایسا تھا جس سے اللہ تعالیٰ خود ہمکلام ہوا۔ کسی کو اس نے دوسری حیثیتوں سے بلند درجے دیئے اور آخر میں عیسیٰ ابن مریم کو روشن نشانیاں عطا کیں اور روح پاک سے اس کی مدد کی۔ اگر اللہ چاہتا ، تو ممکن نہ تھا کہ ان رسولوں کے بعد ، جو لوگ روشن نشانیاں دیکھ چکے تھے ، وہ آپس میں لڑتے مگر (اللہ کی مشیئت یہ نہ تھی کہ وہ لوگوں کو جبراً اختلافات سے روکے ، اس وجہ سے ) انہوں نے باہم اختلاف کیا ۔ پھر کوئی ایمان لایا اور کسی نے کفر کی راہ اختیار کی ۔ ہاں اللہ چاہتاتو وہ ہرگز نہ لڑتے مگر جو اللہ چاہتا ہے کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(2,254,'2:254','اے لوگو ! جو ایمان لائے ہو ، جو کچھ مال ومتاع ہم نے تم کو بخشا ہے ، اس میں خرچ کرو ، قبل اس کے کہ وہ دن آئے ، جس میں نہ خرید وفروخت ہوگی ، نہ دوستی کام آئے گی اور نہ سفارش چلے گی ۔ اور ظالم اصل میں وہی ہیں جو کفر کی راہ اختیار کرتے ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,255,'2:255','اللہ زندہ جاوید ہستی ، جو تمام کائنات کو سنبھالے ہوئے ہے ، اس کے سوا کوئی الٰہ نہیں ہے ۔ وہ نہ سوتا ہے اور نہ اسے اونگھ لگتی ہے ۔ زمین و آسمان میں جو کچھ ہے ، اسی کا ہے ۔ کون ہے جو اس کی جناب میں اس کی اجازت کے بغیر سفارش کرسکے ؟ جو کچھ بندوں کے سامنے ہے ۔ اسے بھی وہ جانتا ہے اور جو کچھ ان سے اوجھل ہے ، اس سے بھی وہ واقف ہے اور اس کی معلومات میں سے کوئی چیز ان کی گرفت ادراک میں نہیں آسکتی ۔ الا یہ کہ کسی چیز کا علم وہ خود ہی ان کو دینا چاہے اس کی حکومت آسمانوں اور زمین پر چھائی ہوئی ہے اور ان کی نگہبانی اس کے لئے کوئی تھکادینے والا کام نہیں ہے ۔ بس وہی بزرگ و برتر ذات ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,256,'2:256','دین کے معاملے میں کوئی زور زبردستی نہیں ہے ۔ صحیح بات غلط خیالات سے الگ چھانٹ کر رکھ دی گئی ہے ۔ اب جو کوئی طاغوت کا انکار کرکے اللہ پر ایمان لے آیا ، اس نے ایک ایسا مضبوط سہارا تھام لیا جو کبھی ٹوٹنے والا نہیں اور اللہ سب کچھ سننے والا اور جاننے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,257,'2:257','جو لوگ ایمان لاتے ہیں ان کا حامی اور مددگار اللہ ہے ۔ اور وہ ان کو تاریکیوں سے روشنی میں نکال لاتا ہے اور جو لوگ کفر کی راہ اختیار کرتے ہیں ان کا حامی و مددگار طاغوت ہے ۔ اور وہ انہیں روشنی سے تاریکیوں کی طرف کھینچ لے جاتے ہیں ۔ یہ آگ میں جانے والے لوگ ہیں جہاں یہ ہمیشہ رہیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,258,'2:258','کیا تم نے اس شخص کے حال پر غور نہیں کیا جس نے ابراہیم (علیہ السلام) سے جھگڑا کیا تھا۔ جھگڑا اس بات پر کہ ابراہیم (علیہ السلام) کا رب کون ہے اور اس بناپر کہ اس شخص کو اللہ نے حکومت دے رکھی تھی ۔ جب ابراہیم (علیہ السلام) نے کہا کہ میرا رب وہ ہے جس کے اختیار میں زندگی اور موت ہے ۔ “ تو اس نے جواب دیا : زندگی اور موت میرے اختیار میں ہے۔ “ ابراہیم (علیہ السلام) نے کہا : اچھا ، اللہ سورج کو مشرق سے نکالتا ہے تو ذرا اسے مغرب سے نکال لا۔ “ یہ سن کر وہ منکر حق ششدر رہ گیا ، مگر اللہ ظالموں کو راہ راست نہیں دکھایا کرتا۔');
INSERT INTO "sayyid_qutb" VALUES(2,259,'2:259','یا پھر مثال کے طور پر اس شخص کو دیکھو ، جس کا گزر ایک ایسی بستی پر ہوا ، جو اپنی چھتوں پر اوندھی گری پڑی تھی ۔ اس نے کہا : یہ آبادی جو ہلاک ہوچکی ہے ، اسے اللہ کس طرح دوبارہ زندگی بخشے گا ؟ “ اس پر اللہ نے اس کی روح مکمل قبض کرلی اور وہ سو برس تک مردہ پڑا رہا۔ پھر اللہ نے اسے دوبارہ زندگی بخشی اور اس سے پوچھا : بتاؤ کتنی مدت پڑے رہے ہو ؟ “ اس نے کہا : ایک دن یا چند گھنٹے رہا ہوں گا “ فرمایا : تم پر سو برس اسی حالت میں گزر چکے ہیں ۔ اب ذرا اپنے کھانے اور پانی کو دیکھو کہ اس میں ذرا تغیر نہیں آیا ہے۔ دوسری طرف ذرا اپنے گدھے کو بھی دیکھو (کہ اس کا پنجر تک بوسیدہ ہورہا ہے) اور یہ ہم نے اس لئے کیا ہے کہ ہم تمہیں لوگوں کے لئے ایک نشانی بنادینا چاہتے ہیں ۔ پھر دیکھو کہ ہڈیوں کے اس پنجر کو ہم کس طرح اٹھاکر گوشت پوست اس پر چڑھاتے ہیں ۔ “ اس طرح جب حقیقت اس کے سامنے بالکل نمایاں ہوگئی ، تو اس نے کہا : میں جانتا ہوں کہ اللہ ہر چیز پر قدرت رکھتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,260,'2:260','جب ابراہیم (علیہ السلام) نے کہا کہ میرا رب مجھے دکھادے تو مردوں کو کیسے زندہ کرتا ہے ؟ “ فرمایا کیا تو ایمان نہیں رکھتا ؟ “ اس نے عرض کیا ایمان تو رکھتا ہوں مگر دل کا اطمینان درکار ہے ۔ فرمایا اچھا چار پرندے لے اور ان کو اپنے سے مانوس کرلے پھر ان کا ایک ایک جز ایک ایک پہاڑ پر رکھ دے ۔ پھر ان کو پکار ، وہ تیرے پاس دوڑے چلے آئیں گے ۔ خوب جان لے کہ اللہ نہایت بااقتدار اور حکیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,261,'2:261','جو لوگ اپنے مال اللہ کی راہ میں صرف کرتے ہیں ، ان کے خرچ کی مثال ایسی ہے جیسے ایک دانہ بویا جائے اور اس سے سات بالیاں نکلیں اور ہر بالی میں سودانے ہوں ۔ اسی طرح اللہ جس کے عمل کو چاہتا ہے ، فروانی عطا کرتا ہے ۔ وہ فراخ دست بھی ہے اور علیم بھی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,262,'2:262','جو لوگ اپنے مال اللہ کی راہ میں خرچ کرتے ہیں اور خرچ کرکے پھر احسان نہیں جتاتے ، نہ دکھ دیتے ہیں ، ان کا اجر ان کے رب کے پاس ہے ۔ اور ان کے لئے کسی رنج اور خوف کا موقعہ نہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,263,'2:263','ایک میٹھا بول اور کسی ناگوار بات پر ذرا سی چشم پوشی اس خیرات سے بہتر ہے ، جس کے پیچھے دکھ ہو۔ “ اور اللہ بےنیاز ہے اور بربادی اس کی صفت ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,264,'2:264','اے ایمان لانے والو ! اپنے صدقات کو احسان جتاکر اور دکھ دے کر اس شخص کی طرح خاک میں نہ ملادو ، جو اپنا مال محض لوگوں کے دکھانے کو خرچ کرتا ہے اور نہ اللہ پر ایمان رکھتا ہے ، نہ آخرت پر ۔ اس شخص کے خرچ کی مثال ایسی ہے جیسے ایک چٹان تھی ، جس پر مٹی کی تہہ جمی ہوئی تھی ۔ اس پر جب زور کامینہ برسا تو ساری مٹی بہہ گئی اور صاف چٹان کی چٹان رہ گئی ۔ ایسے لوگ اپنے نزدیک خیرات کرکے جو نیکی کماتے ہیں ، اس سے کچھ بھی ان کے ہاتھ نہیں آتا ، اور کافروں کو سیدھی راہ دکھانا ، اللہ کا دستور نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,265,'2:265','بخلاف اس کے جو لوگ اپنے مال محض اللہ کی رضاجوئی کے لئے دل کے پورے ثبات وقرار کے ساتھ خرچ کرتے ہیں ۔ ان کے خرچ کی مثال ایسی ہے ، جیسے کسی سطح مرتفع پر ایک باغ ہو ۔ اگر زور کی بارش ہوجائے تو دگنا پھل لائے ، اور اگر زور کی بارش نہ بھی ہو تو ایک ہلکی پھوار ہی اس کے لئے کافی ہوجائے ۔ تم جو کچھ کرتے ہو ، سب اللہ کی نظر میں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,266,'2:266','کیا تم میں سے کوئی یہ پسند کرتا ہے کہ اس کے پاس ایک ہر اباغ ہو ، نہروں سے سیراب ، کھجوروں اور انگوروں اور ہر قسم کے پھلوں سے لدا ہوا ، اور وہ عین اس وقت میں ایک تیز بگولے کی زد میں آکر جھلس جائے جبکہ وہ خود بوڑھا ہو اور اس کے کم سن بچے ابھی کسی لائق نہ ہوں ؟ اس طرح اللہ اپنی باتیں تمہارے سامنے بیان کرتا ہے ، شاید کہ تم غوروفکر کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,267,'2:267','اے لوگو ! جو ایمان لائے ہو ، جو مال تم نے کمائے ہیں اور جو ہم نے زمین سے تمہارے لئے نکالا ہے ، اس میں سے بہتر حصہ راہ اللہ میں خرچ کرو۔ ایسا نہ ہو کہ اس کی راہ میں دینے کے لئے بری سے بری چیز چھاٹنے کی کوشش کرنے لگو ، حالانکہ وہی چیز اگر کوئی تمہیں دے ، تم ہرگز اسے لینا گوارا نہ کروگے ۔ الا یہ کہ اس کو قبول کرنے میں تم اغماض برت جاؤ۔ تمہیں جان لینا چاہئے کہ اللہ بےنیاز ہے اور بہترین صفات سے متصف ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,268,'2:268','شیطان تمہیں مفلسی سے ڈراتا ہے اور شرمناک طرزعمل اختیار کرنے کی ترغیب دیتا ہے ۔ ، مگر اللہ تمہیں اپنی بخشش اور فضل کی امید دلاتا ہے ۔ اللہ بڑا فراخ دست اور دانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,269,'2:269','جس کو چاہتا ہے ، حکمت عطا کرتا ہے اور جس کو حکمت ملی ، اسے حقیقت میں بڑی دولت مل گئی ۔ ان باتوں سے صرف وہی لوگ سبق لیتے ہیں جو دانشمند ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,270,'2:270','تم نے جو کچھ بھی خرچ کیا ہو اور جو نذر بھی مانی ہو ، اللہ کو اس کا علم ہے ، اور ظالموں کا کوئی مددگار نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(2,271,'2:271','اگر اپنے صدقات اعلانیہ دو تو یہ بھی اچھا ہے ، لیکن اگر چھپا کر حاجت مندوں کو دو تو یہ تمہارے حق میں زیادہ بہتر ہے ۔ تمہاری بہت سی برائیاں اس طرز عمل سے محو ہوجاتی ہیں ۔ اور تم جو کچھ کرتے ہو اللہ کو بہرحال اس کی خبر ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,272,'2:272','لوگوں کو ہدایت دینے کی ذمہ داری تم پر نہیں ہے ۔ ہدایت تو اللہ ہی جسے چاہتا ہے بخشتا ہے ۔ اور خیرات میں جو مال تم خرچ کرتے ہو ، وہ تمہارے اپنے لئے بھلائی ہے ۔ آخر تم اسی لئے خرچ کرتے ہو کہ اللہ کی رضا حاصل ہو ۔ تو کچھ مال تم خیرات میں خرچ کروگے ، اس کا پورا پورا اجر تمہیں دیاجائے گا اور تمہاری حق تلفی ہرگز نہ ہوگی ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,273,'2:273','خاص طور پر مدد کے مستحق وہ تنگ دست لوگ ہیں جو اللہ کے کام میں ایسے گھر گئے ہیں کہ اپنی ذاتی کسب معاش کے لئے زمین میں کوئی دوڑ دھوپ نہیں کرسکتے ۔ ان کی خودداری دیکھ کر ناواقف آدمی یہ گمان کرتا ہے کہ یہ خوش حال ہیں ۔ تم ان کے چہروں سے ان کی اندرونی حالت پہچان سکتے ہو۔ مگر وہ ایسے لوگ نہیں ہیں کہ لوگوں کے پیچھے پڑ کر کچھ مانگیں۔ ان کی اعانت میں جو مال تم خرچ روگے وہ اللہ سے پوشیدہ نہ رہے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,274,'2:274','جو لوگ اپنے شب وروز کھلے اور چھپے خرچ کرتے ہیں ان کا اجر ان کے رب کے پاس ہے ۔ اور ان کے لئے کسی خوف ورنج کا مقام نہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,275,'2:275','جو لوگ سود کا مال کھاتے ہیں ان کا حال اس شخص کا سا ہوتا ہے جسے شیطان نے چھوکر باؤلا کردیا ہو اور اس کی حالت میں ان کے مبتلا ہونے کی وجہ یہ ہے کہ وہ کہتے ہیں : تجارت بھی تو آخر سود ہی جیسی چیز ہے “ حالانکہ اللہ نے تجارت کو حلال کیا ہے اور سود کو حرام کیا ہے ۔ لہٰذا جس شخص کو اس کے رب کی طرف سے یہ نصیحت آپہنچے اور آئندہ وہ سود خواری سے باز آجائے تو جو کچھ وہ پہلے کھاچکا ، سو کھاچکا اس کا معاملہ اللہ کے حوالے ہے اور جو اس حکم کے بعد پھر اسی حرکت کا اعادہ کرے ، وہ جہنمی ہے ، جہاں وہ ہمیشہ رہے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(2,276,'2:276','اللہ سود کا مٹھ ماردیتا ہے اور صدقات کو نشوونما دیتا ہے اور اللہ کسی ناشکرے بدعمل انسان کو پسند نہیں کرتا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,277,'2:277','جو لوگ ایمان لے آئیں اور نیک عمل کریں اور نماز قائم کریں اور زکوٰۃ دیں ، ان کا اجر بیشک ان کے رب کے پاس ہے اور ان کے لئے کسی خوف اور رنج کا موقعہ نہیں ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,278,'2:278','اے لوگو ، جو ایمان لائے ہو ، اللہ سے ڈرو اور جو کچھ تمہارا سود لوگوں پر باقی رہ گیا ہے ، اسے چھوڑ دو ، اگر وقعی تم ایمان لائے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(2,279,'2:279','لیکن اگر تم نے ایسا نہ کیا ، تو آگاہ ہوجاؤ کہ اللہ اور رسول کی طرف سے تمہارے خلاف اعلان جنگ ہے ۔ اب بھی توبہ کرلو اور سود چھوڑدو تو اپنا اصل سرمایہ تم لینے کے حق دار ہو نہ تم ظلم کرو ، نہ تم پر ظلم کیا جائے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,280,'2:280','تمہارا قرض دار تنگ دست ہو تو ہاتھ کھلنے تک اسے مہلت دو اور جو صدقہ کردو تو یہ تمہارے لئے زیادہ بہتر ہے اگر تم سمجھو ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,281,'2:281','اس دن کی رسوائی اور مصیبت سے بچو ، جبکہ تم اللہ کی طرف واپس ہوگے ۔ وہاں ہر شخص کو اس کی کمائی ہوئی نیکی یا بدی کا پورا پورا بدلہ مل جائے گا اور کسی پر ظلم ہرگز نہ ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,282,'2:282','اے لوگوجو ایمان لائے ہو ، جب کسی مقررہ مدت کے لئے تم آپس میں قرض کا لین دین کرو ، تو اسے لکھ لیا کرو ۔ فریقین کے درمیان انصاف کے ساتھ ایک شخص دستاویز تحریر کرے جسے اللہ نے لکھنے پڑھنے کی قابلیت بخشی ہو اسے لکھنے سے انکار نہ کرنا چاہئے ۔ اور املا وہ شخص کرائے جس پر حق آتا ہے اور اسے اللہ ، اپنے رب سے ڈرنا چاہئے کہ جو معاملہ طے ہوا اس میں کوئی کمی بیشی نہ کرے ۔ لیکن اگر قرض لینے والا خود نادان یا ضعیف ہو ، یا املا نہ کرسکتا ہو ، تو اس کا ولی انصاف کے ساتھ املا کرائے پھر اپنے مردوں میں سے دوآدمیوں کی اس پر گواہی کرالو اور اگر دو مرد نہ ہوں تو ایک مرد اور دو عورتیں ہوں تاکہ ایک بھول جائے تو دوسری اسے یاد دلائے ۔ یہ گواہ ایسے لوگوں میں سے ہونے چاہئیں ، جن کی گواہی تمہارے درمیان مقبول ہوگواہوں کو جب گواہ بننے کے لئے کہا جائے تو انہیں انکار نہ کرنا چاہئے معاملہ خواہ چھوٹا ہو یا بڑا ، معیاد کی تعین کے ساتھ ساتھ اس کی دستاویز لکھوالینے میں تساہل نہ کرو ۔ اللہ کے نزدیک یہ طریقہ تمہارے لئے زیادہ مبنی بر انصاف ہے ۔ اس سے شہادت قائم ہونے میں زیادہ سہولت ہوتی ہے اور تمہارے شکوک و شبہات میں مبتلا ہونے کا امکان کم رہ جاتا ہے ہاں جو تجارتی لین دین دست بدست تم لوگ آپس میں کرتے ہو ، اس کو نہ لکھا جائے تو کوئی حرج نہیں ، مگر تجارتی معاملے طے کرتے وقت گواہ کرلیا کرو۔ “ کاتب اور گواہ کو نہ ستایا جائے ۔ ایساکروگے تو گناہ کا ارتکاب کروگے ۔ اللہ کے غضب سے بچو ۔ وہ تم کو صحیح طریق عمل کی تعلیم دیتا ہے اور اسے ہر چیز کا علم ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,283,'2:283','اگر تم سفر کی حالت میں ہو ، اور دستاویز لکھنے کے لئے کوئی کاتب نہ ملے ، تو رہن بالقبض پر معاملہ کرو ۔ اگر تم میں سے کوئی شخص دوسرے پر بھروسہ کرکے اس کے ساتھ کوئی معاملہ کرے ، تو جس پر بھروسہ کیا گیا ہو ، اسے چاہئے کہ امانت ادا کرے اور اللہ ، اپنے رب سے ڈرے اور شہادت ہرگز نہ چھپاؤ اور جو شہادت چھپاتا ہے اس کا دل گناہ آلودہ ہے اور اللہ تمہارے اعمال سے بیخبر نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(2,284,'2:284','آسمانوں اور زمینوں میں جو کچھ ہے ، سب اللہ کا ہے ۔ تم اپنے دل کی باتیں خواہ ظاہر کرو یا چھپاؤ، اللہ بہرحال ان کا حساب تم سے لے گا ۔ پھر اسے اختیار ہے ، جسے چاہے ، معاف کردے اور جسے چاہے ، سزادے ۔ وہ ہر چیز پر قدرت رکھتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,285,'2:285','رسول اس ہدایت پر ایمان لایا ہے ، جو اس کے رب کی طرف سے اس پر نازل ہوئی ہے ۔ اور جو لوگ اس رسول کے ماننے والے ہیں ، انہوں نے بھی اس ہدایت کو دل سے تسلیم کیا ہے ۔ وہ سب اللہ اور فرشتوں اور اس کی کتابوں اور اس کے رسولوں کو مانتے ہیں اور ان کا قول یہ ہے کہ ہم اللہ کے رسولوں کو ایک دوسرے سے الگ نہیں کرتے ۔ “ ہم نے حکم سنا اور اطاعت قبول کی ۔ مالک ، ہم تجھ سے خطابخشی کے طالب ہیں اور ہمیں تیری ہی طرف پلٹنا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(2,286,'2:286','اللہ کسی متنفس پر اس کی قدرت سے زیادہ ذمہ داری کا بوجھ نہیں ڈالتا ۔ ہر شخص نے جو نیکی کمائی ہے ، اس کا پھل اسی کے لئے ہے اور جو بدی سمیٹی ہے اس کا وبال اسی پر ہے ۔ اے ہمارے رب ۔ ہم سے بھول چوک میں جو قصور ہوجائیں ۔ ان پر گرفت نہ کر ۔ مالک ، ہم پر وہ بوجھ نہ ڈال ، جو تونے ہم سے پہلے لوگوں پر ڈالے تھے ۔ پروردگار ، جس بار کو اٹھانے کی ہم میں طاقت نہیں ہے ۔ وہ ہم پر نہ رکھ ، ہمارے ساتھ نرمی کر ۔ ہم سے درگزر فرما ، ہم پر رحم کر ، تو ہمارا مولیٰ ہے ۔ کافروں کے مقابلے میں ہماری مدد کر۔');
INSERT INTO "sayyid_qutb" VALUES(3,1,'3:1','الم');
INSERT INTO "sayyid_qutb" VALUES(3,2,'3:2','اللہ وہ زندہ جاوید ہستی جو نظام کائنات کو سنبھالے ہوئے ہے ‘ حقیقت میں اس کے سوا کوئی الٰہ نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,3,'3:3','اس نے تم پر کتاب نازل کی ‘ جو حق لے کر آئی ہے اور ان تمام کتابوں کی تصدیق کررہی ہے جو پہلے سے آئی ہوئی تھیں تورات اور انجیل نازل کرچکا ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(3,4,'3:4','اس سے پہلے وہ انسانوں کی ہدایت کے لئے اور اس نے یہ فرقان اتارا ہے ‘ اب جو لوگ اللہ کے فرامین کو قبول کرنے سے انکار کریں ان کو یقیناً سخت سزاملے گی ‘ اللہ بےپناہ طاقت کا مالک ہے اور برائی کا بدلہ دینے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(3,5,'3:5','‘ زمین و آسمان کی کوئی چیز اللہ سے پوشیدہ نہیں');
INSERT INTO "sayyid_qutb" VALUES(3,6,'3:6','‘ وہی تو ہے جو تمہاری ماؤں کے پیٹ میں تمہاری صورتیں ‘ جیسی چاہتا ہے بناتا ہے ‘ اس زبردست حکمت والے کے سوا کوئی اور الٰہ نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,7,'3:7','وہی اللہ ہے ‘ جس نے یہ کتاب تم پر نازل کی ہے ۔ اس کتاب میں دوطرح کی آیات ہیں : ایک محکمات ‘ جو کتاب کی اصل بنیادیں ہیں اور دوسری متشابہات ‘ جن لوگوں کے دلوں میں ٹیڑھ ہے ‘ وہ فتنے کی تلاش میں ہمیشہ متشابہات کے پیچھے پڑے رہتے ہیں اور ان کو معنی پہنانے کی کوشش کیا کرتے ہیں ۔ حالانکہ ان کا حقیقی مفہوم اللہ کے سوا کوئی نہیں جانتا ۔ بخلاف اس کے جو لوگ علم میں پختہ کار ہیں ‘ وہ کہتے ہیں کہ ہمارا ان پر ایمان ہے ‘ یہ سب ہمارے رب کی طرف سے ہیں اور سچ یہ ہے کہ کسی چیز سے صحیح سبق صرف دانش مند لوگ ہی حاصل کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,8,'3:8','وہ اللہ سے دعا کرتے رہتے ہیں کہ پروردگار ! جب تو ہمیں سیدھے رستے پر لاچکا ہے تو پھر ہمارے دلوں کو کجی میں مبتلا نہ کردیجیو ‘ ہمیں اپنے خزانہ غیب سے رحمت عطا کر کہ تو ہی فیاض حقیقی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,9,'3:9','پروردگار ! تویقیناً سب لوگوں کو ایک روز جمع کرنے والا ہے ‘ جس کے آنے میں کوئی شبہ نہیں ہے ‘ تو ہرگز اپنے وعدے سے ٹلنے والا نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,10,'3:10','جن لوگوں نے کفر کا رویہ اختیار کیا ہے ‘ انہیں اللہ کے مقابلے میں نہ ان کا مال کچھ کام دے گا ‘ نہ اولاد ‘ وہ دوزخ کا ایندھن بن کر رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,11,'3:11','ان کا انجام ویسا ہی ہوگا جیسا کہ فرعون کے ساتھیوں اور ان سے پہلے نافرمانوں کا ہوچکا ہے کہ انہوں نے آیات الٰہی کو جھٹلایا ‘ نتیجہ یہ ہوا کہ اللہ نے ان کے گناہوں پر انہیں پکڑلیا اور حق یہ ہے کہ اللہ سخت عذاب دینے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,12,'3:12','پس اے محمد ! جن لوگوں نے تمہاری دعوت کو قبول کرنے سے انکار کردیا ہے ۔ ان سے کہہ دو کہ قریب ہے وہ وقت ‘ جب تم مغلوب ہوجاؤگے اور جہنم ہی براٹھکانہ ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,13,'3:13','تمہارے لئے ان دوگروہوں میں نشان عبرت تھا ‘ جو ایک دوسرے سے نبرآزما ہوئے ۔ ایک گروہ اللہ کی راہ میں لڑرہا تھا اور دوسرا گروہ کافر تھا۔ دیکھنے والے بچشم دیکھ رہے تھے کہ وہ ان سے دوچند ہیں اور اللہ فتح ونصرت سے جس کو چاہتا ہے مدد دیتا ہے ۔ دیدہ بینا رکھنے والوں کے لئے اس میں بڑا سبق پوشیدہ ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,14,'3:14','لوگوں کے لئے مرغوبات نفس ‘ عورتیں ‘ اولاد ‘ سونے چاندی کے ڈھیر ‘ چیدہ گھوڑے ‘ مویشی اور زرعی زمینیں ‘ خوش آیند بنادی گئی ہیں ۔ مگر یہ سب دنیا کی چند روزہ زندگی کے سامان ہیں۔ حقیقت میں جو بہتر ٹھکانہ ہے ‘ وہ اللہ کے پاس ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,15,'3:15','کہو : میں تمہیں بتاؤں کہ اس سے زیادہ اچھی چیز کیا ہے ؟ جو لوگ تقویٰ کی روش اختیار کریں ‘ ان کے لئے ان کے رب کے پاس باغ ہیں ۔ جس کے نیچے نہریں بہتی ہوں گی ‘ وہاں انہیں ہمیشگی کی زندگی حاصل ہوگی ‘ پاکیزہ بیویاں ان کی رفیق ہوں گی اور اللہ کی رضا سے وہ سرفراز ہوں گے ‘ اللہ اپنے بندوں کے رویے پر گہری نظر رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,16,'3:16','یہ وہ لوگ ہیں جو کہتے ہیں مالک ! ہم ایمان لائے ہماری خطاؤں سے درگزر فرما اور ہمیں آتش دوزخ سے بچا۔');
INSERT INTO "sayyid_qutb" VALUES(3,17,'3:17','یہ لوگ صبر کرنے والے ہیں ۔ راست باز ہیں ۔ فرمانبردار اور فیاض ہیں اور رات کی آخری گھڑیوں میں اللہ سے مغفرت کی دعائیں مانگا کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,18,'3:18','اللہ نے خود اس بات کی شہادت دی ہے کہ اس کے سوا کوئی الٰہ نہیں ہے ۔ اور فرشتے اور سب اہل علم بھی راستی کے ساتھ اس پر گواہ ہیں کہ اس زبردست حکیم کے سوا فی الواقع کوئی الٰہ نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,19,'3:19','اللہ کے نزدیک دین صرف اسلام ہے ۔ اس دین سے ہٹ کر جو مختلف طریقے ان لوگوں نے اختیار کئے ‘ جنہیں کتاب دی گئی تھی ۔ ان کے اس طرز عمل کی کوئی وجہ اس کے سوا نہ تھی کہ انہوں نے علم آجانے کے بعد آپس میں ایک دوسرے پر زیادتی کرنے کے لئے ایسا کیا اور جو کوئی اللہ کے احکام ہدایات کی اطاعت سے انکار کردے ‘ اللہ کو اس سے حساب لینے میں دیر نہیں لگتی ۔');
INSERT INTO "sayyid_qutb" VALUES(3,20,'3:20','اب اگر یہ لوگ تم سے جھگڑا کریں ‘ تو ان سے کہو ۔ میں نے اور میرے پیروؤں نے تو اللہ کے آگے سرتسلیم خم کردیا ہے ۔ “ پھر اہل کتاب اور غیر اہل کتاب دونوں سے پوچھو۔ کیا تم نے بھی اس کی اطاعت اور بندگی قبول کی ۔ “ اگر کی تو راہ راست پاگئے۔ اور اگر اس سے منہ موڑا تو تم پر صرف پیغام پہنچادینے کی ذمہ داری تھی ۔ آگے خود اللہ اپنے بندوں کے معاملات دیکھنے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,21,'3:21','وہ لوگ جو اللہ کی ہدایات واحکامات ماننے سے انکار کرتے ہیں اور اس کے پیغمبروں کو ناحق قتل کرتے ہیں اور ایسے لوگوں کی جان کے درپے ہوجاتے ہیں ‘ جو خلق خدا سے راستی اور عدل کا حکم دینے کے لئے اٹھیں ‘ ان کو دردناک سزا کی خوشخبری سنادو');
INSERT INTO "sayyid_qutb" VALUES(3,22,'3:22','یہ وہ لوگ ہیں جن کے اعمال دنیا وآخرت دونوں میں ضائع ہوگئے ۔ اور ان کا مددگار کوئی نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(3,23,'3:23','تم نے دیکھا نہیں کہ جن لوگوں کو کتاب کے علم میں سے کچھ حصہ ملا ہے ۔ ان کا حال کیا ہے ؟ انہیں جب کتاب الٰہی کی طرف بلایا جاتا ہے ‘ تاکہ وہ ان کے درمیان فیصلے کرے ‘ تو ان میں سے ایک فریق پہلو تہی کرتا ہے اور اس فیصلے کی طرف آنے سے منہ پھیرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,24,'3:24','ان کا یہ طرز عمل اس وجہ سے ہے کہ وہ کہتے ہیں ۔ کہ آتش دوزخ تو ہمیں مس تک نہ کرے گی اور اگر دوزخ کی سزا ہم کو ملے گی بھی تو بس چند روز “ ان کے خودساختہ عقیدوں نے ان کو اپنے دین کے معاملے میں بڑی غلط فہمیوں میں ڈال رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,25,'3:25','مگر کیا بنے گی ان پر جب ہم انہیں اس روز جمع کریں گے جس کا آنا یقینی ہے ؟ اسی روز ہر شخص کو اس کی کمائی کا بدلہ پورا پورا دیدیا جائے گا اور کسی پر ظلم نہ ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,26,'3:26','کہو خدایا ‘ ملک کے مالک ! تو جسے چاہے ‘ حکومت دے اور جس سے چاہے ‘ حکومت چھین لے ‘ جسے چاہے عزت دے اور جسے چاہے ذلیل کردے ‘ بھلائی تیرے اختیار میں ہے ۔ بیشک تو ہر چیز پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,27,'3:27','تو رات کو دن میں پروتا ہوالے آتا ہے اور دن کو رات میں ‘ جاندار میں سے بےجان کو نکالتا ہے اور بےجان میں سے جاندار کو ‘ اور جسے چاہتا ہے بیشمار رزق دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,28,'3:28','مومنین ‘ اہل ایمان کو چھوڑ کر ‘ کافروں کو اپنا رفیق اور دوست ہرگز نہ بنائیں ۔ جو ایسا کرے گا اس کا اللہ سے کوئی تعلق نہ ہوگا۔ ہاں یہ معاف ہے کہ تم ان کے ظلم سے بچنے کے لئے بظاہر ایسا طرزعمل اختیار کرجاؤ مگر اللہ تمہیں اپنے آپ سے ڈراتا ہے اور تمہیں اس کی طرف پلٹ کر جانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,29,'3:29','اے نبی لوگوں کو خبردار کردو کہ تمہارے دلوں میں جو کچھ ہے اسے خواہ تم چھپاؤ ‘ یا ظاہر کرو ‘ اللہ بہرحال اسے جانتا ہے ۔ زمین و آسمان کی کوئی چیز اس کے علم سے باہر نہیں اور اس کا اقتدار ہر چیز پر حاوی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,30,'3:30','وہ دن آنے والا ہے جب ہر نفس اپنے کئے کا پھل پائے گا ۔ خواہ اس نے بھلائی کی ہو یا برائی ‘ اسی روز آدمی یہ تمنا کرے گا کہ کاش ابھی یہ دن اس سے دور ہوتا ‘ اللہ تمہیں اپنے آپ سے ڈراتا ہے اور اپنے بندوں کا نہایت خیر خواہ ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,31,'3:31','اے نبی لوگوں سے کہہ دو کہ اگر تم حقیقت میں اللہ سے محبت رکھتے ہو ‘ تو میری پیروی اختیار کرو ‘ اللہ تم سے محبت کرے گا اور تمہاری خطاؤں سے درگزر فرمائے گا۔ وہ بڑامعاف کرنے والا اور رحیم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,32,'3:32','کہو کہ اللہ اور رسول کی اطاعت قبول کرلو۔ “ پھر اگر وہ تمہاری یہ دعوت قبول نہ کریں تو یقیناً یہ ممکن نہیں ہے کہ اللہ ایسے لوگوں سے محبت کرے جو اس کی اور اس کے رسول کی اطاعت کرنے سے انکار کرتے ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,33,'3:33','اللہ نے آدم اور نوح اور آل ابراہیم اور آل عمران کو تمام دنیا والوں پر ترجیح دے کر (رسالت کے لئے) منتخب کیا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(3,34,'3:34','یہ ایک سلسلے کے لوگ تھے ‘ جو ایک دوسرے کی نسل سے پیدا ہوئے تھے ۔ اللہ سب کچھ سنتا اور جانتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,35,'3:35','جب عمران کی عورت کہہ رہی تھی کہ میرے پروردگار ! میں اس بچے کو جو میرے پیٹ میں ہے تیری نظر کتی ہوں ۔ وہ تیرے کام کے لئے وقف ہوگا۔ میری اس پیش کش کو قبول فرمایا ‘ تو سننے اور جاننے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,36,'3:36','پھر جب وہ اپنی بچی اس کے ہاں پیدا ہوئی تو اس نے کہا : مالک ! میرے ہاں تو لڑکی پیدا ہوگئی ہے ۔ حالانکہ جو کچھ اس نے جنا تھا ‘ اللہ کو اس کی خبر تھی .........اور لڑکا لڑکی کی طرح نہیں ہوتا۔ خیر ‘ میں نے اس کا نام مریم رکھ دیا ہے ۔ اور میں اسے اور اس کی آئندہ نسل کو شیطان مردود کے فتنے سے تیری پناہ میں دیتی ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(3,37,'3:37','“ آخر کار اس کے رب نے اس لڑکی کو بخوشی قبول کرلیا ‘ اسے بڑی اچھی لڑکی بناکر اٹھایا اور زکریا کو اس کا سرپرست بنادیا.........زکریا جس اس کے پاس محراب میں جاتا تو اس کے پاس کچھ نہ کچھ کھانے پینے کا سامان پاتا ۔ پوچھتا مریم ! یہ تیرے پاس کہاں سے آیا۔ وہ جواب دیتی اللہ کے پاس سے آیا ہے اللہ جسے چاہتا ہے بےحساب دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,38,'3:38','یہ حال دیکھ کر زکریا نے اپنے رب کو پکارا پروردگار ! اپنی قدرت سے مجھے نیک اولاد عطاکر ‘ تو ہی دعا سننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(3,39,'3:39','جواب میں فرشتوں نے آواز دی ‘ جب وہ محراب میں کھڑا نماز پڑھ رہا تھا کہ اللہ تجھے یحییٰ کی خوشخبری دیتا ہے ۔ وہ اللہ کی طرف سے ایک فرمان کی تصدیق کرنے والا بن کر آئے گا ۔ اس میں سرداری اور بزرگی کی شان ہوگی ۔ کمال درجے کا ضابطہ ہوگا ۔ نبوت سے سرفراز ہوگا ۔ اور صالحین میں شمار ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,40,'3:40','زکریا نے کہا ۔ پروردگار ! بھلا میرے ہاں لڑکا کہاں سے ہوگا۔ میں تو بہت بوڑھا ہوچکا ہوں اور میر بیوی بانجھ ہے کہا ‘ اللہ جو چاہتا ہے کرتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,41,'3:41','عرض کیا مالک ! پھر کوئی نشان میرے لئے مقرر فرمادے۔ “ کہاَ نشان یہ ہے کہ تم تین دن تک لوگوں سے اشارہ کے سوا کوئی بات نہ کروگے ۔ “ (نہ کرسکوگے) اس دوران میں اپنے رب کو بہت یاد کرنا اور صبح وشام اس کی تسبیح کرتے رہنا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,42,'3:42','اور جب فرشتوں نے کہا اے مریم ! اللہ نے تجھے برگزیدہ کیا اور پاکیزگی عطا کی اور تمام دنیا کی عورتوں پر تجھ کو ترجیح دے کر اپنی خدمت کے لئے چن لیا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,43,'3:43','اے مریم ! اپنے رب کی تابع فرمان بن کر رہ ‘ اس کے آگے سربسجود ہو اور جو بندے اس کے حضور جھکنے والے ہیں اس کے ساتھ تو بھی جھک جا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,44,'3:44','اے محمد ! یہ غیب کی خبریں ہیں جو ہم تم کو وحی کے ذریعہ سے بتا رہے ہیں ‘ ورنہ تم اس وقت وہاں موجود نہ تھے ۔ جب ہیکل کے خادم یہ فیصلہ کرنے کے لئے اپنے اپنے قلم پھینک رہے تھے اور نہ تم اس وقت حاضر تھے جب ان کے درمیان جھگڑا برپا تھا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,45,'3:45','اور جب فرشتوں نے کہا اے مریم ! اللہ تجھے ایک فرمان کی خوشخبری دیتا ہے ۔ اس کا نام مسیح عیسیٰ بن مریم ہوگا۔ دنیا وآخرت میں معزز ہوگا ‘ اللہ کے مقرب بندوں میں شمار کیا جائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,46,'3:46','لوگوں سے گہوارے میں بھی کلام کرے گا اور بڑی عمر کو پہنچ کر بھی اور وہ ایک مرد صالح ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(3,47,'3:47','یہ سن کر مریم بولی : پروردگار ‘ میرے بچہ کہاں سے ہوگا مجھے تو کسی شخص نے ہاتھ تک نہیں لگایا۔ “ جواب ملا۔ ایسا ہی ہوگا ‘ اللہ جو چاہتا ہے پیدا کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,48,'3:48','(فرشتوں نے پھر اپنے سلسلہ کلام میں کہا ) اور اللہ اسے کتاب و حکمت کی تعلیم دے گا ‘ تورات اور انجیل کا علم سکھائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,49,'3:49','اور جب وہ بحیثیت رسول بنی اسرائیل کے پاس آیا تو اس نے کہا میں تمہارے رب کی طرف سے تمہارے پاس نشانی لیکر آیا ہوں ۔ میں تمہارے سامنے مٹی سے پرندے کی صورت کا ایک مجسمہ بناتا ہوں ۔ اور اس میں پھونک مارتا ہوں ۔ وہ اللہ کے حکم سے پرندہ بن جاتا ہے ۔ میں اللہ کے حکم سے مادر زاد اندھے اور کوڑھی کو اچھا کرتا ہوں اور مردے کو زندہ کرتا ہوں ۔ میں تمہیں بتاتا ہوں کہ تم کیا کھاتے ہو اور کیا اپنے گھروں میں ذخیرہ کرکے رکھتے ہو ۔ اس میں تمہارے لئے کافی نشانی ہے ۔ اگر تم ایمان لانیوالے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,50,'3:50','اور میں تعلیم وہدایت کی تصدیق کرنیوالا بن کر آیا ہوں جو تورات میں سے اس وقت میرے زمانہ میں موجود ہے ۔ اور اس لیے آیا ہوں کہ تمہارے لئے بعض ان چیزوں کو حلال کردو جو تم پر حرام کی گئیں ہیں ۔ دیکھو ‘ میں تمہارے رب کی طرف سے تمہارے پاس نشانیاں لیکر آیا ہوں۔ لہٰذا اللہ سے ڈرو اور میری اطاعت کرو ۔');
INSERT INTO "sayyid_qutb" VALUES(3,51,'3:51','اللہ میرا رب بھی ہے اور تمہارا رب بھی ‘ لہٰذا تم اس کی بندگی اختیار کرو ‘ یہی سیدھا راستہ ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,52,'3:52','جب عیسیٰ (علیہ السلام) نے محسوس کیا کہ بنی اسرائیل کفر و انکار پر آمادہ ہیں تو اس نے کہا : کون ہے اللہ کی راہ میں میرا مددگار ہوتا ہے۔ حواریوں نے جواب دیا ہم اللہ کے مددگار ہیں ‘ ہم اللہ پر ایمان لائے ۔ گواہ رہو کہ ہم مسلم ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,53,'3:53','مالک جو فرمان تو نے نازل کیا ہے ‘ ہم نے اسے مان لیا ہے اور رسول کی پیروی قبول کی ‘ ہمارا نام گواہی دینے والوں میں لکھ دے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,54,'3:54','پھر بنی اسرائیل مسیح کے خلاف خفیہ تدبیریں کرنے لگے جواب میں اللہ نے بھی اپنی خفیہ تدبیر کی اور ایسی تدبیروں میں اللہ سب سے بڑھ کر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,55,'3:55','(وہ اللہ کی خفیہ تدبیر ہی تھی ۔ ) جب اس نے کہا کہ اے عیسیٰ ! اب میں تجھے واپس لے لوں گا اور تجھ کو اپنی طرف اٹھا لوں گا۔ “ اور جنہوں نے تیرا انکار کیا ان سے تجھے پاک کردوں گا ۔ اور تیری پیروی کرنے والوں کو قیامت تک ان لوگوں پر بالادست رکھوں گا جنہوں نے تیرا انکار کیا ہے۔ پھر تم سب کو آخرکار میرے پاس آنا ہے ۔ اس وقت میں ان باتوں کا فیصلہ کروں گا۔ جن میں تمہارے درمیان اختلاف ہوا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,56,'3:56','جن لوگوں نے کفر و انکار کی روش اختیار کی ہے انہیں دنیا وآخرت دونوں میں سزا دوں گا اور وہ کوئی مددگار نہ پائیں گے ‘');
INSERT INTO "sayyid_qutb" VALUES(3,57,'3:57','اور جنہوں نے ایمان اور نیک عمل کا رویہ اختیار کیا ہے ‘ انہیں ان کے اجر پورے دئیے جائیں گے اور خوب جان لو کہ ظالموں سے اللہ ہرگز محبت نہیں کرتا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,58,'3:58','یہ آیات اور حکمت سے لبریز تذکرے ہیں جو ہم تمہیں سنا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,59,'3:59','‘ اللہ کے نزدیک عیسیٰ کی مثال آدم کی سی ہے کہ اللہ نے اسے مٹی سے پیدا کیا اور حکم دیا کہ ہوجا اور وہ ہوگیا۔');
INSERT INTO "sayyid_qutb" VALUES(3,60,'3:60','یہ اصل حقیقت ہے جو تمہارے رب کی طرف سے بتائی جارہی ہے ۔ اور تم ان لوگوں میں شامل نہ ہو جو اس میں شک کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,61,'3:61','یہ علم آجانے کے بعد جو کوئی آپ سے جھگڑا کرے تو اس سے کہو کہ آؤ ہم اور تم خود بھی آجائیں ۔ اور اپنے بال بچوں کو بھی لے کر آئیں اور اللہ سے دعا کریں کہ جو جھوٹا ہے اس پر اللہ کی لعنت۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,62,'3:62','یہ بالکل صحیح واقعات ہیں ‘ اور حقیقت یہ ہے کہ اللہ کے سوا کوئی الٰہ نہیں ہے ۔ اور وہ اللہ ہی کی ہستی ہے جس کی طاقت سب سے بالا اور جس کی حکمت نظام عالم میں کارفرما ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,63,'3:63','پس اگر لوگ منہ موڑیں تو ان کا مفسد ہونا صاف کھل جائے گا اور اللہ مفسدوں کے حال سے واقف ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,64,'3:64','کہو اہل کتاب آؤ ایک ایسی بات کی طرف جو ہمارے اور تمہارے درمیان یکساں ہے ۔ یہ کہ ہم اللہ کے سوا کسی کی بندگی نہ کریں اور اس کے ساتھ کسی کو شریک نہ ٹھہرائیں اور ہم میں سے کوئی اللہ کے سوا اپنا رب نہ بنالے ۔ “ اس دعوت کو قبول کرنے سے اگر وہ منہ موڑیں تو صاف کہہ دو کہ گواہ رہو ہم تو مسلم ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,65,'3:65','اے اہل کتاب تم ابراہیم (علیہ السلام) کے بارے میں ہم سے کیوں جھگڑتے ہو ؟ تورات اور انجیل تو ابراہیم (علیہ السلام) کے بعد نازل ہوئی ہیں ‘ پھر کیا تم اتنی بات بھی نہیں سمجھتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(3,66,'3:66','تم لوگ جن چیزوں کا علم رکھتے ہو ان میں تو خوب بحثیں کرچکے ‘ اب ان معاملات میں کیوں بحثیں کرتے ہو جن کا تمہارے پاس کچھ علم بھی نہیں ‘ اللہ جانتا ہے ‘ تم نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(3,67,'3:67','ابراہیم (علیہ السلام) نہ یہودی تھا نہ عیسائی ‘ بلکہ وہ تو ایک مسلم یکسو تھا اور وہ ہرگز مشرکوں میں سے نہ تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,68,'3:68','ابراہیم (علیہ السلام) سے نسبت رکھنے کا حق سب سے زیادہ اگر کسی کو پہنچتا ہے تو ان لوگوں کو پہنچتا ہے جنہوں اس کی پیروی کی اور اب یہ نبی اور اس کے ماننے والے اس کی نسبت کے زیادہ حق دار ہیں ۔ اللہ صرف انہی کا حامی و مددگار ہے جو ایمان رکھتے ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,69,'3:69','اہل کتاب کا ایک گروہ چاہتا ہے کہ کسی طرح تمہیں راہ راست سے ہٹادے ‘ حالانکہ وہ اپنے سوا کسی کو گمراہی میں نہیں ڈال رہے ہیں مگر انہیں اس کا شعور نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,70,'3:70','اے اہل کتاب ! کیوں اللہ کی آیات کا انکار کررہے ہو حالانکہ تم خود ان کا مشاہدہ کررہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(3,71,'3:71','اہل کتاب ! کیوں حق کو باطل کا رنگ چڑھا کر مشتبہ بناتے ہو ؟ کیوں جانتے بوجھتے حق کو چھپاتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(3,72,'3:72','اہل کتاب میں سے ایک گروہ کہتا ہے کہ اس نبی کے ماننے والوں پر جو کچھ نازل ہوا ہے ‘ اس پر صبح ایمان لاؤ اور شام کو اس کا انکار کردو ۔ شاید اس ترکیب سے یہ لوگ اپنے ایمان سے پھرجائیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,73,'3:73','نیز یہ لوگ آپس میں کہتے ہیں کہ اپنے مذہب والے کے سوا کسی کی بات نہ مانو ‘ اے نبی ان سے کہہ دو کہ اصل میں ہدایت تو اللہ کی ہدایت ہے اور یہ اسی کی دین کہ کسی کو وہی کچھ دیا جائے گا جو کبھی تم کو دیا گیا تھا۔ یا یہ کہ دوسروں کو تمہارے رب کے حضور پیش کرنے کے لئے تمہارے خلاف قوی حجت مل جائے ۔ “ اے نبی ! ان سے کہو کہ فضل وشرف اللہ کے اختیار میں ہے ‘ جسے چاہے عطا فرمائے ۔ وہ وسیع النظر ہے اور سب کچھ جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,74,'3:74','اپنی رحمت کے لئے جس کو چاہتا ہے مخصوص کرلیتا ہے اور اس کا فضل بہت بڑا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,75,'3:75','اہل کتاب میں سے کوئی تو ایسا ہے کہ اگر تم اس کے اعتماد پر مال و دولت کا ایک ڈھیر بھی دے دو تو وہ تمہارا مال تمہیں ادا کردے گا ‘ اور کسی کا حال یہ ہے کہ اگر تم ایک دینار کے معاملے میں بھی اس پر بھروسہ کرو تو وہ ادا نہ کرے گا الّا یہ کہ تم اس کے سر پر سوار ہوجاؤ۔ ان کی اس اخلاقی حالت کا سبب یہ ہے کہ وہ کہتے ہیں ۔ امیوں کے معاملے میں ہم پر کوئی موأخذہ نہیں ہے۔ “ اور یہ بات وہ محض جھوٹ گھڑکر اللہ کی طرف منسوب کرتے ہیں ‘ حالانکہ انہیں معلوم ہے کہ اللہ نے ایسی کوئی بات نہیں فرمائی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,76,'3:76','آخر کیوں ان سے باز پرس نہ ہوگی ؟ جو بھی اپنے عہد کو پورا کرے گا اور ہر برائی سے بچ کر رہے گا وہ اللہ کا محبوب بنے گا ‘ کیونکہ پرہیز گار لوگ اللہ کو پسند ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,77,'3:77','رہے وہ لوگ جو اللہ کے عہد اور اپنی قسموں کو تھوڑی قیمت پر بیچ ڈالتے ہیں ‘ تو ان کے لئے آخرت میں کوئی حصہ نہیں ‘ اللہ قیامت کے روز ان سے بات کرے گا نہ ان کی طرف دیکھے گا اور نہ انہیں پاک کرے گا ۔ بلکہ ان کے لئے تو سخت دردناک سزا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,78,'3:78','اور ان میں سے کچھ لوگ ایسے ہیں جو کتاب پڑھتے ہوئے اسی طرح زبان کا الٹ پھیر کرتے ہیں کہ تم سمجھو کہ جو کچھ وہ پڑ رہے ہیں وہ کتاب ہی کی عبارت ہے۔ حالانکہ وہ کتاب کی عبارت نہیں ہوتی ۔ وہ کہتے ہیں کہ یہ جو کچھ پڑھ رہے ہیں یہ اللہ کی طرف سے ہے ‘ حالانکہ وہ اللہ کی طرف سے نہیں ہوتا ‘ وہ جان بوجھ کر جھوٹی بات اللہ کی طرف منسوب کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,79,'3:79','کسی انسان کا کام یہ نہیں ہے کہ اللہ تو اس کو کتاب اور حکم اور نبوت عطا فرمائے اور وہ لوگوں سے کہے کہ اللہ کے بجائے تم میرے بندے بن جاؤ ‘ وہ تو یہی کہے گا کہ سچے ربانی بنو ‘ جیسا کہ اس کتاب کی تعلیم کا تقاضا ہے جسے تم پڑھتے اور پڑھاتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(3,80,'3:80','وہ تم سے ہرگز یہ نہ کہے گا کہ فرشتوں کو یا پیغمبروں کو اپنا رب بنالو کیا یہ ممکن ہے کہ ایک نبی تمہیں کفر کا حکم دے جبکہ تم مسلم ہو۔');
INSERT INTO "sayyid_qutb" VALUES(3,81,'3:81','یاد کرو جب اللہ نے پیغمبروں سے عہد لیا تھا کہ آج ہم نے تمہیں کتاب و حکمت و دانش سے نوازا ہے ‘ کل اگر کوئی دوسرا رسول تمہارے پاس ‘ اسی تعلیم کی تصدیق کرتا ہوا آئے جو پہلے سے تمہارے پاس موجود ہے ‘ تو تم کو اس پر ایمان لانا ہوگا اور اس کی مدد کرنی ہوگی ۔ “ یہ ارشاد فرماکر اللہ نے پوچھا کیا تم اس کا اقرار کرتے ہو ‘ اور اس پر میری طرف سے عہد کی بھاری ذمہ داری اٹھاتے ہو۔ “ انہوں نے کہا ہاں ہم اقرار کرتے ہیں ‘ اللہ نے فرمایا۔ اچھا تو گواہ رہو اور میں بھی تمہارے ساتھ گواہ ہوں ‘');
INSERT INTO "sayyid_qutb" VALUES(3,82,'3:82','اس کے بعد جو اپنے عہد سے پھرجائے وہی فاسق ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,83,'3:83','کیا یہ لوگ اللہ کی اطاعت کا طریقہ چھوڑ کر کوئی اور دین چاہتے ہیں ؟ حالانکہ زمین و آسمان کی ساری چیزیں چار وناچار اللہ ہی کی تابع فرمان (مسلم) ہیں اور اسی کی طرف سب کو پلٹنا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,84,'3:84','اے نبی کہو کہ ہم اللہ کو مانتے ہیں ‘ اس تعلیم کو مانتے ہیں جو ہم پر نازل کی گئی ‘ ان تعلیمات کو بھی مانتے ہیں جو ابراہیم (علیہ السلام) ‘ اسماعیل (علیہ السلام) ‘ اسحاق (علیہ السلام) ‘ یعقوب (علیہ السلام) پر نازل ہوئی تھیں ۔ اور ان ہدایات پر بھی یقین رکھتے ہیں جو موسیٰ (علیہ السلام) اور عیسیٰ (علیہ السلام) اور دوسرے پیغمبروں کو ان کے رب کی طرف سے دی گئی ۔ ہم ان کے درمیان فرق نہیں کرتے اور ہم اللہ کے تابع فرمان ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(3,85,'3:85','اس اسلام کے سوا جو شخص کوئی اور طریقہ اختیار کرنا چاہے ‘ اس کا وہ طریقہ ہرگز قبول نہ کیا جائے گا اور آخرت میں وہ ناکام ونامراد رہے گا۔');
INSERT INTO "sayyid_qutb" VALUES(3,86,'3:86','کیسے ہوسکتا ہے کہ اللہ ان لوگوں کو ہدایت بخشے ‘ جنہوں نے نعمت ایمان پالینے کے بعد پھر کفر اختیار کیا حالانکہ وہ اس بات پر گواہی دے چکے ہیں کہ یہ رسول حق پر ہے اور ان کے پاس روشن نشانیاں آچکی ہیں ۔ اللہ ظالموں کو تو ہدایت نہیں دیا کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,87,'3:87','ان کے ظلم کا صحیح بدلہ یہی ہے کہ ان پر اللہ اور فرشتوں اور تمام انسانوں کی پھٹکار ہے');
INSERT INTO "sayyid_qutb" VALUES(3,88,'3:88','اسی حالت میں وہ ہمیشہ رہیں گے ‘ نہ ان کی سزا میں تخفیف ہوگی اور نہ انہیں مہلت دی جائے گی ۔');
INSERT INTO "sayyid_qutb" VALUES(3,89,'3:89','البتہ وہ لوگ بچ جائیں گے جو اس کے بعد توبہ کرکے اپنے طرز عمل کی اصلاح کرلیں گے ۔ اللہ بخشنے والا اور رحم فرمانے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,90,'3:90','مگر جن لوگوں نے ایمان لانے کے بعد کفر کیا ‘ پھر اپنے کفر میں بڑھتے چلے گئے ان کی توبہ بھی قبول نہ ہوگی ‘ ایسے لوگ تو پکے گمراہ ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,91,'3:91','یقین رکھو ‘ جن لوگوں نے کفر اختیار کیا اور کفر ہی کی حالت میں جان دیدی ان میں سے کوئی اگر اپنے آپ کو سزا سے بچانے کے لئے روئے زمین بھر کر سونا فدیہ میں دے تو اسے قبول نہ کیا جائے گا ۔ ایسے لوگوں کے لئے درناک سزا تیار ہے۔ اور وہ اپنا کوئی مددگار نہ پائیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,92,'3:92','تم نیکی کو نہیں پہنچ سکتے جب تک کہ تم وہ چیزیں (اللہ کی راہ میں) خرچ نہ کرو جنہیں تم عزیز رکھتے ہو اور جو کچھ تم خرچ کروگے اللہ اس سے بیخبر نہ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(3,93,'3:93','کھانے کی یہ ساری چیزیں (جو شریعت محمدی میں حلال ہیں ) وہ بنی اسرائیل کے لئے بھی حلال تھیں ۔ البتہ بعض چیزیں ایسی تھیں جنہیں توراۃ کے نازل کئے جانے سے پہلے اسرائیل نے خود اپنے اوپر حرام کرلیا تھا۔ ان سے کہو ‘ اگر تم سچے ہو تو لاؤ توراۃ اور پیش کرو اس کی کوئی عبارت');
INSERT INTO "sayyid_qutb" VALUES(3,94,'3:94','اس کے بعد بھی جو لوگ اپنی جھوٹی گھڑی ہوئی باتیں اللہ کی طرف منسوب کرتے رہیں وہی درحقیقت ظالم ہیں ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,95,'3:95','کہو ‘ اللہ نے جو کچھ فرمایا ہے ‘ سچ فرمایا ہے ‘ تم کو یکسو ہوکر ابراہیم کے طریقے کی پیروی کرنی چاہئے ‘ اور ابراہیم شرک کرنے والوں میں سے نہ تھا');
INSERT INTO "sayyid_qutb" VALUES(3,96,'3:96','بیشک سب سے پہلی عبادت گاہ ‘ جو انسانوں کے لئے تعمیر ہوئی وہ وہی ہے جو مکہ میں واقع ہے ۔ اس کو خیر و برکت دی گئی تھی اور تمام جہاں والوں کے لئے مرکز ہدایت بنایا گیا تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,97,'3:97','اس میں کھلی نشانیاں ہیں ‘ ابراہیم کا مقام عبادت ہے اور اس کا حال یہ ہے کہ جو اس میں داخل ہوا مامون ہوگیا ۔ لوگوں پر اللہ کا حق یہ ہے کہ جو اس گھر تک پہنچنے کی استطاعت رکھتا ہو ‘ وہ اس کا حج کرے ‘ اور جو کوئی اس حکم کی پیروی سے انکار کرے تو اسے معلوم ہونا چاہئے کہ اللہ تمام دنیا والوں سے بےنیاز ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,98,'3:98','کہو اے اہل کتاب تم کیوں اللہ کی آیات ماننے سے انکار کرتے ہو ؟ جو حرکتیں تم کررہے ہو اللہ سب کچھ دیکھ رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(3,99,'3:99','کہو اے اہل کتاب یہ تمہاری کیا روش ہے کہ جو اللہ کی بات مانتا ہے اسے تم بھی اللہ کے راستے سے روکتے ہو اور چاہتے ہو کہ وہ ٹیڑھی راہ چلے ‘ حالانکہ تم خود اس پر گواہ ہو ‘ تمہاری حرکتوں سے اللہ غافل نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,100,'3:100','اے ایمان والو ! اگر تم نے ان اہل کتاب میں سے ایک گروہ کی بات مانی تو یہ تمہیں ایمان سے پھر کفر کی طرف پھیر لے جائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,101,'3:101','تمہارے کفر کی طرف جانے کا اب کیا موقعہ باقی ہے جبکہ تم کو اللہ کی آیات سنائی جارہی ہیں ۔ اور تمہارے درمیان اس کا رسول ہے ؟ جو اللہ کا دامن مضبوطی کے ساتھ تھامے گا وہ ضرور راہ راست پالے گا۔');
INSERT INTO "sayyid_qutb" VALUES(3,102,'3:102','اے ایمان لانے والو ! اللہ سے ڈرو جیسا کہ اس سے ڈرنے کا حق ہے ۔ تم کو موت نہ آئے مگر اس حال میں کہ تم مسلم ہو۔');
INSERT INTO "sayyid_qutb" VALUES(3,103,'3:103','سب مل کر اللہ کی رسی کو مضبوط پکڑلو اور تفرقہ میں نہ پڑو۔ اللہ کے اس احسان کو یاد رکھو جو اس نے تم پر کیا ہے ‘ تم ایک دوسرے کے دشمن تھے ‘ اس نے تمہارے دل جوڑ دئیے اور اس کے فضل وکرم سے تم بھائی بھائی بن گئے ۔ تم آگ سے بھرے ہوئے ایک گڑھے کے کنارے کھڑے تھے ‘ اللہ نے تم کو اس سے بچالیا ۔ اس طرح اللہ اپنی نشانیاں تمہارے سامنے روشن کرتا ہے ‘ شاید کہ ان علامتوں سے تمہیں اپنی فلاح کا سیدھا راستہ نظر آجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,104,'3:104','تم میں کچھ لوگ ضرور ایسے رہنے چاہئیں جو نیکی کی طرف بلائیں ‘ بھلائی کا حکم دیں ‘ اور برائیوں سے روکتے رہیں ۔ جو لوگ یہ کام کریں گے وہی فلاح پائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,105,'3:105','کہیں تم ان لوگوں کی طرح نہ ہوجانا جو فرقوں میں بٹ گئے اور کھلی کھلی واضح ہدایات پانے کے بعد پھر اختلافات میں مبتلا ہوئے ۔ جنہوں نے یہ روش اختیار کی وہ اس روزسخت سزا پائیں گے');
INSERT INTO "sayyid_qutb" VALUES(3,106,'3:106','جبکہ کچھ لوگ سرخرو ہوں گے اور کچھ لوگوں کا منہ کالا ہوگا۔ جن کا منہ کالا ہوگا (ان سے کہا جائے گا کہ ) نعمت ایمان پانے کے بعد بھی تم نے کافرانہ رویہ اختیار کیا ؟ اچھا تو اب اس کفران نعمت کے صلہ میں عذاب کا مزہ چکھو۔');
INSERT INTO "sayyid_qutb" VALUES(3,107,'3:107','رہے وہ لوگ جن کے چہرے روشن ہوں گے تو ان کو اللہ کے دامن رحمت میں جگہ ملے گی اور ہمیشہ اسی حالت میں رہیں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,108,'3:108','یہ اللہ کے ارشادات ہیں جو ہم تمہیں ٹھیک ٹھیک سنا رہے ہیں کیونکہ اللہ دنیا والوں پر ظلم کرنے کا کوئی ارادہ نہیں رکھتا');
INSERT INTO "sayyid_qutb" VALUES(3,109,'3:109','زمین و آسمان کی ساری چیزوں کا مالک اللہ ہے اور سارے معاملات اللہ کے حضور پیش ہوتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,110,'3:110','اب دنیا میں وہ بہترین گروہ تم ہو جسے انسانیت کی ہدایت واصلاح کے لئے میدان میں لایا گیا ہے ۔ تم نیکی کا حکم دیتے ہو ‘ بدی سے روکتے ہو اور اللہ پر ایمان رکھتے ہو۔ یہ اہل کتاب ایمان لاتے تو انہی کے حق میں بہتر تھا ۔ اگرچہ ان میں کچھ لوگ ایماندار بھی پائے جاتے ہیں مگر ان کے بیشتر افراد نافرمان ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,111,'3:111','یہ تمہارا کچھ نہیں بگاڑسکتے ‘ زیادہ سے زیادہ بس کچھ ستاسکتے ہیں ۔ اگر یہ تم سے لڑیں گے تو مقابلہ میں پیٹھ دکھائیں گے ‘ پھر ایسے بےبس ہوں گے کہ کہیں سے ان کو مدد نہ ملے گی ۔');
INSERT INTO "sayyid_qutb" VALUES(3,112,'3:112','یہ جہاں پائے گئے ان پر ذلت کی مار پڑی ‘ کہیں اللہ کے ذمہ یا انسانوں کے ذمہ میں پناہ مل گئی تو اور بات ہے ۔ یہ اللہ کے غضب میں گھر چکے ہیں ‘ ان پر محتاجی اور مغلوبی مسلط کردی گئی ہے ‘ اور یہ سب کچھ صرف اس وجہ سے ہوا کہ یہ اللہ کی آیات کے ساتھ کفر کرتے رہے اور انہوں نے پیغمبروں کو ناحق قتل کیا ۔ یہ ان کی نافرمانیوں اور زیادتیوں کا انجام ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,113,'3:113','مگر سارے اہل کتاب یکساں نہیں ہیں ۔ ان میں کچھ ایسے لوگ بھی ہیں جو راہ راست پر قائم ہیں ‘ راتوں کو اللہ کی آیات پڑھتے ہیں اور اس کے آگے سجدہ ریز ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,114,'3:114','اللہ اور روز آخرت پر ایمان رکھتے ہیں ‘ نیکی کا حکم دیتے ہیں ‘ برائیوں سے روکتے ہیں اور بھلائی کے کاموں میں سرگرم رہتے ہیں ۔ یہ صالح لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,115,'3:115','اور جو نیکی بھی یہ کریں گے اس کی ناقدری نہ کی جائے گی ‘ اللہ پرہیز گار لوگوں کو خوب جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,116,'3:116','رہے وہ لوگ جنہوں نے کفر کا رویہ اختیار کیا تو اللہ کے مقابلے میں ان کو نہ ان کا مال کچھ کام دے گا نہ اولاد ‘ وہ تو آگ میں جانے والے لوگ ہیں اور آگ ہی میں ہمیشہ رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,117,'3:117','جو کچھ وہ اپنی اس دنیا کی زندگی میں خرچ کررہے ہیں اس کی مثال اس ہوا کی سی ہے جس میں پالاہو اور وہ ان لوگوں کی کھیتی پر چلے جنہوں نے اپنے اوپر ظلم کیا ہے اور اسے برباد کرکے رکھ دے ۔ اللہ نے ان پر ظلم نہیں کیا ۔ درحقیقت یہ خود اپنے اوپر ظلم کررہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,118,'3:118','اے ایمان لانے والو ! اپنی جماعت کے لوگوں کے سوا دوسروں کو اپنا راز دار نہ بناؤ۔ وہ تمہاری خرابی کے کسی موقع سے فائدہ اٹھانے میں نہیں چوکتے ۔ تمہیں جس چیز سے نقصان پہنچے وہی ان کو محبوب ہے ۔ ان کے دل کا بغض ان کے منہ سے نکلا پڑتا ہے اور جو کچھ وہ اپنے سینوں میں چھپائے ہوئے ہیں وہ اس سے شدید تر ہے ۔ ہم نے تمہیں صاف صاف ہدایات دے دی ہیں ‘ اگر تم عقل رکھتے ہو (تو ان سے تعلق رکھنے میں احتیاط برتوگے )');
INSERT INTO "sayyid_qutb" VALUES(3,119,'3:119','تم ان سے محبت رکھتے ہو مگر وہ تم سے محبت نہیں رکھتے ۔ حالانکہ تم تمام کتب آسمانی کو مانتے ہو ۔ جب وہ تم سے ملتے ہیں تو کہتے ہیں کہ ہم نے بھی (تمہارے رسول اور تمہاری کتاب کو ) مان لیا ہے ‘ مگر جب جدا ہوتے ہیں تو تمہارے خلاف ان کا غیض وغضب کا یہ حال ہوتا ہے کہ اپنی انگلیاں چبانے لگتے ہیں ۔ ان سے کہہ دو کہ اپنے غصے میں آپ جل مرو ‘ اللہ دلوں کے چھپے ہوئے راز تک جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,120,'3:120','تمہارا بھلاہوتا ہے تو ان کو برامعلوم ہوتا ہے اور تم پر کوئی مصیبت آتی ہے تو یہ خوش ہوتے ہیں ۔ مگر ان کی کوئی تدبیر تمہارے خلاف کارگر نہیں ہوسکتی بشرطیکہ تم صبر سے کام لو اور اللہ سے ڈر کر کام کرتے رہو ۔ جو یہ کچھ کررہے ہیں اللہ اس پر حادی ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,121,'3:121','اس وقت کو یاد کرو کہ جب تم صبح سویرے اپنے گھر سے نکلے تھے اور مسلمانوں کو جنگ کے لئے جابجا مامور کررہے تھے ‘ اور اللہ ساری باتیں سنتا ہے اور باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,122,'3:122','یاد کرو جب تم میں سے دو گروہ بزدلی دکھانے پر آمادہ ہوگئے حالانکہ اللہ کی ان کی مدد پر موجود تھا ‘ اور مومنوں کو اللہ ہی پر بھروسہ رکھنا چاہئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,123,'3:123','اس سے پہلے جنگ بدر میں اللہ تمہاری مدد کرچکا تھا حالانکہ اس وقت تم بہت کمزور تھے ‘ لہٰذا تم کو چاہئے کہ اللہ کی ناشکری سے بچو ‘ امید ہے کہ اب تم شکر گزار بنوگے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,124,'3:124','یاد کرو جب تم مومنین سے کہہ رہے تھے کیا تمہارے لئے یہ بات کافی نہیں ہے کہ اللہ تین ہزار فرشتے اتار کر تمہاری مدد کرے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(3,125,'3:125','بیشک اگر تم صبر کرو اور اللہ سے ڈرتے ہوئے کام کرو تو جس آن دشمن تمہارے اوپر چڑھ آئیں گے اسی آن تمہارا رب (تین ہزار نہیں ) پانچ ہزار صاحب نشان فرشتوں سے تمہاری مدد کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(3,126,'3:126','یہ بات اللہ نے تمہیں اس لئے بتادی ہے کہ تم خوش ہوجاؤ اور تمہارے دل مطمئن ہوجائیں ۔ فتح ونصرت جو کچھ بھی ہے اللہ کی طرف سے ہے جو بڑی قوت والا اور دانا اور بینا ہے');
INSERT INTO "sayyid_qutb" VALUES(3,127,'3:127','تاکہ کفر کی راہ چلنے والوں کا ایک بازو کاٹ دے ‘ یا ان کو ایسی ذلیل شکست دے کہ وہ نامرادی کے ساتھ پسپا ہوجائیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,128,'3:128','اے پیغمبر فیصلے کے اختیارات میں تمہارا کوئی حصہ نہیں ہے ۔ اللہ کو اختیار ہے ‘ انہیں معاف کرے ‘ چاہے سزا دے کیونکہ وہ ظالم ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(3,129,'3:129','زمین و آسمان میں جو کچھ ہے اس کا مالک ہے جس کو چاہے بخش دے اور جس کو چاہے عذاب دے ۔ وہ معاف کرنے والا اور رحیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,130,'3:130','اے ایمان لانے والو ! یہ بڑھتا اور چڑھتا سود کھانا چھوڑ دو اور اللہ سے ڈرو ‘ امید ہے کہ فلاح پاؤگے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,131,'3:131','اس آگ سے بچو جو کافروں کے لئے مہیا کی گئی ہے');
INSERT INTO "sayyid_qutb" VALUES(3,132,'3:132','اور اللہ اور رسول کا حکم مان لو ‘ توقع ہے کہ تم پر رحم کیا جائے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,133,'3:133','دوڑ کر چلو اس راہ پر جو تمہارے رب کی بخشش اور اس جنت کی طرف جاتی ہے جس کی وسعت زمین اور آسمانوں جیسی ہے ‘ اور وہ ان اللہ ترس لوگوں کے لئے مہیا کی گئی ہے');
INSERT INTO "sayyid_qutb" VALUES(3,134,'3:134','جو ہر حال میں مال خرچ کرتے ہیں ‘ خواہ بدحال ہوں یا خوش حال ‘ جو غصے کو پی جاتے ہیں اور دوسروں کے قصور معاف کردیتے ہیں ‘ ایسے نیک لوگ اللہ کو بہت پسند ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,135,'3:135','اور جن کا حال یہ ہے کہ اگر کبھی کوئی فحش کام ان سے سرزد ہوجاتا ہے یا کسی گناہ کا ارتکاب کرکے وہ اپنے اوپر ظلم کربیٹھتے ہیں تو معاً اللہ انہیں یاد آجاتا ہے اور اس سے وہ اپنے قصوروں کی معافی چاہتے ہیں ‘ کیونکہ اللہ کے سوا اور کون ہے جو گناہ معاف کرسکتا ہو۔ اور وہ دیدہ دانستہ اپنے کئے پر اصرار نہیں کرتے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,136,'3:136','ایسے لوگوں کی جزاء ان کے رب کے پاس یہ ہے کہ وہ ان کو معاف کردے گا اور ایسے باغوں میں انہیں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی اور وہاں وہ ہمیشہ رہیں گے ۔ کیا اچھا بدلہ ہے نیک عمل کرنے والوں کے لئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,137,'3:137','تم سے پہلے بہت سے دور گزرچکے ہیں ‘ زمین میں چل پھر کر دیکھ لو کہ ان لوگوں کا انجام کیا ہوا ‘ جنہوں نے (اللہ کے احکام وہ ہدایات) کو جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(3,138,'3:138','یہ لوگوں کے لئے ایک صاف اور صریح تنبیہ ہے اور جو اللہ سے ڈرتے ہوں ان کے لئے ہدایت اور نصیحت');
INSERT INTO "sayyid_qutb" VALUES(3,139,'3:139','دل شکستہ نہ ہو ‘ غم نہ کرو ‘ تم ہی غالب رہوگے اگر تم مومن ہو۔');
INSERT INTO "sayyid_qutb" VALUES(3,140,'3:140','اس وقت اگر تمہیں چوٹ لگی ہے تو اس سے پہلے ایسی ہی چوٹ تمہارے مخالف فریق کو بھی لگ چکی ہے ۔ یہ تو زمانہ کے نشیب و فراز ہیں جنہیں ہم لوگوں کے درمیان گردش دیتے رہتے ہیں۔ تم پر یہ وقت اس لئے لایا گیا ہے کہ اللہ دیکھنا چاہتا تھا کہ تم میں سچے مومن کون ہیں ‘ ان لوگوں کو چھانٹ لینا چاہتا تھا جو واقعی راستی کو گواہ ہوں کیونکہ ظالم لوگ اللہ کو پسند نہیں');
INSERT INTO "sayyid_qutb" VALUES(3,141,'3:141','اور وہ اس آزمائش کے ذریعہ مومنوں کا الگ چھانٹ کر کافروں کی سرکوبی کرنا چاہتا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(3,142,'3:142','کیا تم نے یہ سمجھ رکھا ہے کہ یونہی جنت میں چلے جاؤگے حالانکہ ابھی اللہ نے تو یہ دیکھا ہی نہیں کہ تم میں کون وہ لوگ ہیں جو اس کی راہ میں جانیں لڑانے والے اور اس کی خاطر صبر کرنے والے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,143,'3:143','تم موت کی تمناکر رہے تھے ‘ مگر یہ اس وقت کی بات تھی جب موت سامنے نہ آئی تھی ‘ لواب وہ تمہارے سامنے آگئی ہے اور تم نے اسے آنکھوں سے دیکھ لیا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,144,'3:144','محمد اس کے سوا کچھ نہیں کہ بس ایک رسول ہیں ‘ ان سے پہلے اور رسول بھی گزرچکے ہیں ‘ پھر کیا اگر وہ مرجائیں یا قتل کردیئے جائیں تو تم لوگ الٹے پاؤں پھر جاؤگے ؟ یاد رکھو ! جو الٹا پھرے گا وہ اللہ کا کچھ نقصان نہ کرے گا ‘ البتہ جو اللہ کے شکر گزار بندے بن کر رہیں گے انہیں وہ اس کی جزادے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,145,'3:145','کوئی ذی روح اللہ کے اذن کے بغیر نہیں مرسکتا ۔ موت کا وقت تو لکھا ہوا ہے ۔ جو شخص ثواب دنیا کے ارادے سے کام کرے گا اس کو ہم دنیا میں سے دیں گے ‘ اور جو ثواب آخرت کے ارادے سے کام کرے گا وہ آخرت کا ثواب پائے گا اور شکر کرنے والوں کو ہم ان کی جزا ضرور عطا کریں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,146,'3:146','اس سے پہلے کتنے ہی نبی ایسے گزرے ہیں جن کے ساتھ مل کر بہت سے اللہ پرستوں نے جنگ کی ۔ اللہ کی راہ میں جو مصیبتیں ان پر پڑیں ان سے وہ دل شکستہ نہیں ہوئے ‘ انہوں نے کمزوری نہیں دکھائی ‘ وہ باطل کے آگے سرنگوں نہیں ہوئے ۔ ایسے صابروں کو اللہ پسند کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,147,'3:147','ان کی دعا بس یہ تھی اے ہمارے رب ! ہماری غلطیوں اور کوتاہیوں سے درگزر فرما ‘ ہمارے کام میں تیرے حدود سے جو تجاوز ہوگیا ہو اسے معاف کردے ‘ ہمارے قدم جمادے اور کافروں کے مقابلہ میں ہماری مدد کر۔');
INSERT INTO "sayyid_qutb" VALUES(3,148,'3:148','آخرکار اللہ نے ان کو دنیا میں بھی ثواب دیا اور اس سے بہتر ثواب آخرت میں بھی عطا کیا۔ اللہ کو ایسے ہی نیک لوگ پسند ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(3,149,'3:149','اے لوگو ! جو ایمان لائے ہو ‘ اگر تم ان لوگوں کے اشارے پر چلوگے جنہوں نے کفر کی راہ اختیار کی ہے تو وہ تم کو الٹاپھیر لے جائیں گے اور تم نامراد ہوجاؤگے۔');
INSERT INTO "sayyid_qutb" VALUES(3,150,'3:150','(ان کی باتیں غلط ہیں ) حقیقت یہ ہے کہ اللہ تمہارا حامی و مددگار ہے اور وہ بہترین مدد کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,151,'3:151','عنقریب وہ وقت آنے والا ہے جب ہم منکرین حق کے دلوں میں رعب بٹھادیں گے ‘ اس لئے کہ انہوں نے اللہ کے ساتھ ان کو خدائی میں شریک ٹھہرایا ہے ‘ جن کے شریک ہونے پر اللہ نے کوئی سند نازل نہیں کی ۔ ان کا آخری ٹھکانہ جہنم ہے اور بہت ہی بری ہے وہ قیام گاہ جو ان ظالموں کو نصیب ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(3,152,'3:152','اللہ نے (تائید ونصرت) جو وعدہ تم سے کیا تھا ‘ وہ تو اس نے پورا کردیا ۔ ابتدا میں (اس کے حکم سے تم ہی ان کو قتل کررہے تھے ۔ مگر جب تم نے کمزوری دکھائی اور اپنے کام میں باہم اختلاف کیا ‘ اور جونہی کہ وہ چیزا للہ نے تمہیں دکھائی جس کی محبت میں تم گرفتار تھے (یعنی مال غنیمت) تم اپنے سردار کے حکم کی خلاف ورزی کربیٹھے ......اس لئے کہ تم میں سے کچھ لوگ دنیا کے طالب تھے اور کچھ آخرت کی خواہش رکھتے تھے ۔ تب اللہ نے تمہیں کافروں کے مقابلہ میں پسپا کردیاتا کہ تمہاری آزمائش کرے ۔ اور حق یہ ہے کہ اللہ نے پھر بھی تمہیں معاف ہی کردیا کیونکہ مومنوں پر اللہ بڑی نظر عنایت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,153,'3:153','یاد کرو جب تم بھاگے چلے جارہے تھے ‘ کسی کی طرف پلٹ کر دیکھنے تک کا ہوش تمہیں نہ تھا اور رسول تمہارے پیچھے تم کو پکاررہا تھا۔ اس وقت تمہاری اس روش کا بدلہ اللہ نے تمہیں یہ دیا کہ تم کو رنج پر رنج دیئے تاکہ آئندہ کے لئے تمہیں یہ سبق ملے کہ جو کچھ تمہارے ہاتھ سے جائے یا جو مصیبت تم پر نازل ہو اس پر ملول نہ ہو۔ اللہ تمہارے سب اعمال سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,154,'3:154','اس غم کے بعد پھر اللہ نے تم میں سے کچھ لوگوں پر اطمینان کی سی حالت طاری کردی کہ وہ اونگھنے لگے ۔ مگر ایک دوسرا گروہ ‘ جس کے لئے ساری اہمیت بس اپنی ذات ہی کی تھی ‘ اللہ کے متعلق طرح طرح کے جاہلانہ گمان کرنے لگا جو سراسر خلاف حق تھے ۔ یہ لوگ اب کہتے ہیں کہ اس کام کو چلانے میں ہمارا بھی کوئی حصہ ہے ؟ “ ان سے کہو (کسی کا حصہ نہیں) اس کام کے سارے اختیارات اللہ کے ہاتھ میں ہیں۔ “ دراصل یہ لوگ اپنے دلوں میں جو بات چھپائے ہوئے ہیں ‘ اسے تم پر ظاہر نہیں کرتے ۔ اس کا اصل مطلب یہ ہے کہ اگر (قیادت) کے اختیارات میں ہمارا کچھ حصہ ہوتا تو ہم یہاں نہ مارے جاتے ۔ “ ان سے کہہ دو کہ اگر تم اپنے گھروں میں بھی ہوتے تو جن لوگوں کی موت لکھی ہوئی تھی وہ خود اپنی قتل گاہوں کی طرف نکل آتے۔ “ اور یہ معاملہ جو پیش آیا ‘ یہ تو اس لئے تھا کہ جو کچھ تمہارے سینوں میں پوشیدہ ہے اللہ اسے آزمالے اور جو کھوٹ تمہارے دلوں میں ہے اسے چھانٹ دے ‘ اللہ دلوں کا حال خوب جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,155,'3:155','تم میں سے جو لوگ مقابلہ کے دن پیٹھ پھیرگئے تھے ان کی لغزش کا سبب یہ تھا کہ ان کی بعض کمزوریوں کی وجہ سے شیطان نے ان کے قدم ڈگمگاڈئیے تھے ۔ اللہ نے انہیں معاف کردیا ‘ اللہ بہت درگزر کرنے والا اور بردبار ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,156,'3:156','اے لوگو ! جو ایمان لائے ہو ‘ کافروں کی سی باتیں نہ کروجن کے عزیز واقاب اگر کبھی سفر پر جاتے ہیں یا جنگ میں شریک ہوتے ہیں (اور وہاں کسی حادثہ سے دوچار ہوجاتے ہیں (تو وہ کہتے ہیں کہ اگر وہ ہمارے پاس ہوتے تو نہ مارے جاتے اور نہ قتل ہوتے ۔ اللہ اس قسم کی باتوں کو ان کے دلوں میں حسر واندوہ کا سبب بنا دیتا ہے ‘ ورنہ دراصل مارنے اور جلانے والا تو اللہ ہی ہے اور تمہاری تمام حرکات پر وہی نگراں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,157,'3:157','اگر تم اللہ کی راہ میں مارے جاؤ یا مرجاؤتو اللہ کی جو رحمت اور بخشش تمہارے حصہ میں آئے گی وہ ان ساری چیزوں سے زیادہ بہتر ہے جنہیں یہ لوگ جمع کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,158,'3:158','اور خواہ تم مرو یا مارے جاؤ بہرحال تم سب کو جانا اللہ ہی کی طرف ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,159,'3:159','(اے پیغمبر) یہ اللہ کی بڑی رحمت ہے کہ تم ان لوگوں کے لئے بڑے نرم مزاج واقعہ ہوئے ہو۔ ورنہ اگر تم کہیں تند خو اور سنگ دل ہوتے تو یہ سب تمہارے گردوپیش سے چھٹ جاتے ۔ ان کے قصور معاف کردو ‘ ان کے حق میں دعائے مغفرت کرو ‘ اور دین کے کام میں ان کو بھی شریک مشورہ رکھو ‘ پھر جب تمہارا عزم کسی رائے پر مستحکم ہوجائے تو اللہ پر بھروسہ کرو ‘ اللہ کو وہ لوگ پسند ہیں جو اسی کے بھروسے پر کام کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(3,160,'3:160','اللہ تمہاری مدد پر ہو تو کوئی طاقت تم پر غالب آنے والی نہیں ‘ اور وہ تمہیں چھوڑ دے ‘ تو اس کے بعد کون ہے جو تمہاری مدد کرسکتا ہو ؟ پس جو سچے مومن ہیں ان کو اللہ ہی پر بھروسہ رکھنا چاہئے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,161,'3:161','کسی نبی کا یہ کام نہیں ہوسکتا کہ وہ خیانت کرجائے ......اور جو کوئی خیانت کرے تو وہ اپنی خیانت سمیت قیامت کے روز حاضر ہوجائے گا ‘ پھر ہر متنفس کو اس کی کمائی کا پورا پورا بدلہ مل جائے گا اور کسی پر کچھ ظلم نہ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(3,162,'3:162','بھلایہ کیسے ہوسکتا ہے کہ جو شخص ہمیشہ اللہ کی رضاپر چلنے والا ہو وہ اس شخص کے سے کام کرے جو اللہ کے غضب میں گھر گیا ہو اور جس کا آخری ٹھکانہ جہنم ہو اور جو بدترین ٹھکانہ ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(3,163,'3:163','اللہ کے نزدیک دونوں قسم کے آدمیوں میں بدرجہا فرق ہے اور اللہ سب کے اعمال پر نظر رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,164,'3:164','درحقیقت اہل ایمان پر تو اللہ نے یہ بہت بڑا احسان کیا ہے کہ ان کے درمیان خود انہی میں سے ایک ایسا پیغمبر اٹھایا جو اس کی آیات انہیں سناتا ہے ‘ ان کی زندگیوں کو سنوراتا ہے اور ان کو کتاب اور دانائی کی تعلیم دیتا ہے ‘ حالانکہ اس سے پہلے یہی لوگ صریح گمراہیوں میں پڑے ہوئے تھے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,165,'3:165','اور تمہارا کیا حال ہے کہ جب تم پر مصیبت آپڑی تو تم کہنے لگے یہ کہاں سے آئی ؟ حالانکہ (جنگ بدر میں) اس سے دوگنی مصیبت تمہارے ہاتھوں (طریق مخالف پر) پڑچکی ہے ۔ اے نبی صلى الله عليه وسلم ان سے کہو ‘ یہ مصیبت تمہاری اپنی لائی ہوئی ہے ‘ اللہ ہر چیز پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,166,'3:166','جو نقصان لڑائی کے دن تمہیں پہنچا وہ اللہ کے اذن سے تھا اور اس لئے تھا کہ اللہ دیکھ لے تم میں سے مومن کون ہیں اور منافق کون ؟');
INSERT INTO "sayyid_qutb" VALUES(3,167,'3:167','وہ منافق کہ جب ان سے کہا گیا آؤ اللہ کی راہ میں جنگ کرو یا کم ازکم (اپنے شہر کی) مدافعت ہی کرو “ تو کہنے لگے اگر ہمیں علم ہوتا کہ آج جنگ ہوگی تو ہم ضرور تمہارے ساتھ چلتے ۔ “ یہ بات جب وہ کہہ رہے تھے اس وقت وہ ایمان کی بہ نسبت کفر سے زیادہ قریب تھے ۔ وہ اپنی زبانوں سے وہ باتیں کہتے ہیں جو ان کے دلوں میں نہیں ہوتیں ‘ اور جو کچھ وہ دلوں میں چھپاتے ہیں اللہ اسے خوب جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,168,'3:168','یہ وہی لوگ ہیں جو خودتو بیٹھے رہے اور ان کے جو بھائی بند لڑنے لگے اور مارے گئے ان کے متعلق انہوں نے کہہ دیا کہ اگر وہ ہماری بات مان لیتے تو نہ مارے جاتے۔ ان سے کہو اگر تم اپنے اس قول میں سچے ہو تو خودتمہاری موت جب آئے ‘ اسے ٹال کر دکھادینا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,169,'3:169','جو لوگ اللہ کی راہ میں قتل ہوئے ہیں انہیں مردہ نہ سمجھو ‘ وہ تو حقیقت میں زندہ ہیں ‘ اپنے رب کے پاس رزق پا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,170,'3:170','جو کچھ اللہ نے اپنے فضل سے انہیں دیا ہے اس پر خوش وخرم ہیں ‘ اور مطمئن ہیں کہ جو اہل ایمان ان کے پیچھے دنیا میں رہ گئے ہیں اور ابھی وہاں نہیں پہنچے ہیں ان کے لئے بھی کسی خوف اور رنج کا موقع نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,171,'3:171','وہ اللہ کے انعام اور اس کے فضل پر شاداں وفرحاں ہیں اور ان کو معلوم ہوچکا ہے کہ اللہ مومنوں کے اجر کو ضائع نہیں کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(3,172,'3:172','(ایسے مومنوں کے اجر کو) جنہوں نے زخم کھانے کے بعد بھی اللہ اور رسول کی پکار پر لبیک کہا ......ان میں جو اشخاص نیکوکار اور پرہیزگار ہیں ان کے لئے بڑا اجر ہے');
INSERT INTO "sayyid_qutb" VALUES(3,173,'3:173','جن سے لوگوں نے کہا کہ تمہارے خلاف بڑی فوجیں جمع ہوئی ہیں ‘ ان سے ڈرو “ تو یہ سن کر ان کا ایمان اور بڑھ گیا اور انہوں نے جواب دیا کہ ہمارے لئے اللہ کافی ہے اور وہی بہترین کارساز ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,174,'3:174','آخرکار وہ اللہ تعالیٰ کی نعمت اور فضل کے ساتھ پلٹ آئے ‘ ان کو کسی قسم کا ضرور بھی نہ پہنچا اور اللہ کی رضا پر چلنے کا شرف بھی انہیں حاصل ہوگیا ‘ اللہ بڑا فضل فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,175,'3:175','اب تمہیں معلوم ہوگیا کہ وہ دراصل شیطان تھا جو اپنے دوستوں سے خواہ مخواہ ڈرا رہا تھا۔ لہٰذا آئندہ تم انسانوں سے نہ ڈرنا ‘ مجھ سے ڈرنا اگر تم حقیقت میں صاحب ایمان ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,176,'3:176','(اے پیغمبر) جو لوگ آج کفر کی راہ میں بڑی دوڑ دھوپ کررہے ہیں ان کی سرگرمیاں تمہیں آزردہ نہ کریں ‘ یہ اللہ کا کچھ بھی نہ بگاڑ سکیں گے ۔ اللہ کا ارادہ یہ ہے کہ ان کے لئے آخرت میں کوئی حصہ نہ رکھے ‘ اور بالآخر ان کو سخت سزا ملنے والی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,177,'3:177','جو لوگ ایمان کو چھوڑ کر کفر کے خریدار بنے ہیں وہ یقیناً اللہ کا کوئی نقصان نہیں کررہے ہیں ‘ ان کے لئے دردناک عذاب تیار ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,178,'3:178','یہ ڈھیل جو ہم انہیں دئیے جاتے ہیں اس کو یہ کافر اپنے حق میں بہتری نہ سمجھیں ‘ ہم تو انہیں اس لئے ڈھیل دے رہے ہیں کہ یہ خوب بار گناہ سمیٹ لیں ‘ پھر ان کے لئے سخت ذلیل کرنے والی سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,179,'3:179','اللہ مومنوں کو اس حالت میں ہرگز نہ رہنے دے گا جس میں تم لوگ اس وقت پائے جاتے ہو ۔ وہ پاک لوگوں کو ناپاک لوگوں سے الگ کرکے رہے گا ۔ مگر اللہ کا یہ طریقہ نہیں ہے کہ تم لوگوں کو غیب پر مطلع کردے ۔ (غیب کی باتیں بتانے کے لئے تو ) اللہ اپنے رسولوں میں سے جس کو چاہتا ہے منتخب کرلیتا ہے ۔ لہٰذا (امور غیب کے بارے میں ) اللہ اور اس کے رسولوں پر ایمان رکھو ۔ اگر تم ایمان اور خدا ترسی کی روش پر چلوگے تو تم کو بڑا اجر ملے گا ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,180,'3:180','جن لوگوں کو اللہ نے اپنے فضل سے نوازا ہے اور پھر وہ بخل سے کام لیتے ہیں وہ اس خیال میں نہ رہیں کہ یہ بخیلی ان کے لئے اچھی ہے ۔ نہیں ‘ یہ ان کے حق میں نہایت بری ہے ۔ جو کچھ وہ اپنی کنجوسی سے جمع کررہے ہیں وہی قیامت کے روز ان کے گلے کا طوق بن جائے گا۔ زمین اور آسمانوں کی میراث اللہ ہی کے لئے ہے اور جو کچھ تم کررہے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(3,181,'3:181','اللہ ان لوگوں کا قول سنا جو کہتے ہیں کہ اللہ فقیر ہے اور ہم غنی ہیں ۔ ان کی یہ باتیں بھی ہم لکھ لیں گے اور اس سے پہلے جو وہ پیغمبروں کو ناحق قتل کرتے رہے ہیں وہ بھی ان کے نامہ اعمال میں ثبت ہے ۔ (جب فیصلہ کا وقت آئے گا اس وقت) ہم ان سے کہیں گے کہ لو ‘ اب عذاب جہنم کا مزا چکھو ‘');
INSERT INTO "sayyid_qutb" VALUES(3,182,'3:182','یہ تمہارے اپنے ہاتھوں کی کمائی ہے ‘ اللہ اپنے بندوں کے لئے ظالم نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,183,'3:183','جو لوگ کہتے ہیں اللہ نے ہم کو ہدایت کردی ہے کہ ہم کسی رسول کو تسلیم نہ کریں جب تک وہ ہمارے سامنے ایسی قربانی نہ کرے جسے (غیب سے آکر) آگ کھالے ۔ “ ان سے کہو تمہارے پاس مجھ سے پہلے بہت سے رسول آچکے ہیں جو بہت سی روشن نشانیاں لائے تھے اور وہ نشانی بھی لائے تھے جس کا تم ذکر کرتے ہو ‘ پھر اگر (ایمان لانے کے لئے یہ شرط پیش کرنے میں ) تم سچے ہو تو ان رسولوں کو تم نے کیوں قتل کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(3,184,'3:184','اب اے نبی صلى الله عليه وسلم ‘ اگر یہ لوگ تمہیں جھٹلاتے ہیں تو بہت سے رسول تم سے پہلے جھٹلائے جاچکے ہیں جو کھلی کھلی نشانیاں اور صحیفے اور روشنی بخشنے والی کتابیں لائے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,185,'3:185','آخرکار ہر شخص نفس کو مرنا ہے ۔ اور تم سب اپنے اپنے پورے اجر قیامت کے روز پانے والے ہو ‘ کامیاب دراصل وہ ہے جو وہاں آتش دوزخ سے بچ جائے اور جنت میں داخل کردیا جائے ۔ رہی یہ دنیا ‘ تو یہ محض ایک ظاہر فریب چیز ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,186,'3:186','مسلمانو ‘ تمہیں مال اور جان دونوں کی آزمائشیں پیش آکر رہیں گی ‘ اور تم اہل کتاب اور مشرکین سے بہت سی تکلیف دہ باتیں سنوگے ۔ اگر ان سب حالات میں تم صبر اور اللہ ترسی کی روش پر قائم رہو ‘ تو یہ بڑے حوصلے کا کام ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,187,'3:187','اہل کتاب کو وہ عہد بھی یاد دلاؤ جو اللہ نے ان سے لیا تھا کہ تمہیں کتاب کی تعلیمات کو لوگوں میں پھیلانا ہوگا۔ مگر انہوں نے کتاب کو پس پشت ڈال دیا ۔ اور تھوری قیمت پر اسے بیچ ڈالا۔ کتنا برا کاروبار ہے جو یہ کررہے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,188,'3:188','تم ان لوگوں کو عذاب سے محفوظ نہ سمجھو جو اپنے کرتوتوں پر خوش ہیں اور چاہتے ہیں کہ ایسے کاموں کی تعریف انہیں حاصل ہو جو فی الواقعہ انہوں نے نہیں کئے ۔ حقیقت میں ان کے لئے دردناک سزا تیار ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,189,'3:189','اور زمین وآسمانوں کا مالک اللہ ہے اور اس کی قدرت سب پر حاوی ہے');
INSERT INTO "sayyid_qutb" VALUES(3,190,'3:190','زمین وآسمانوں کی پیدائش میں اور رات اور دن کے باری باری آنے میں ان ہوشمندلوگوں کے لئے بہت نشانیاں ہیں');
INSERT INTO "sayyid_qutb" VALUES(3,191,'3:191','جو اٹھتے ‘ بیٹھتے اور لیٹتے ‘ ہر حال میں اللہ کو یاد کرتے ہیں اور زمین و آسمان کی ساخت میں غور وفکر کرتے ہیں ۔ (وہ بےاختیار بول اٹھتے ہیں۔ ) پروردگار ! یہ سب کچھ تو نے فضول اور بےمقصد نہیں بنایا ہے ۔ تو پاک ہے اس سے کہ عبث کام کرے ۔ پس اے رب ‘ ہمیں دوزخ کے عذاب سے بچا لے');
INSERT INTO "sayyid_qutb" VALUES(3,192,'3:192','تو نے جسے دوزخ میں ڈالا اسے درحقیقت بڑی ذلت ورسوائی میں ڈال دیا ‘ اور پھر ایسے ظالموں کا کوئی مددگارنہ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(3,193,'3:193','مالک ! ہم نے ایک پکارنے والے کو سنا جو ایمان کی طرف بلاتا تھا اور کہتا تھا کہ اپنے رب کی مانو ‘ ہم نے اس کی دعوت قبول کرلی ۔ پس اے ہمارے آقا ! جو قصور ہم سے ہوئے ہیں ان سے درگزر فرما ‘ جو برائیاں ہم میں ہیں انہیں دور کردے اور ہمارا خاتمہ نیک لوگوں کے ساتھ کر۔');
INSERT INTO "sayyid_qutb" VALUES(3,194,'3:194','خداوند ‘ جو وعدے تو نے اپنے رسولوں کے ذریعہ سے کئے ہیں ان کو ہمارے ساتھ پورا کر اور قیامت کے دن ہمیں رسوائی میں نہ ڈال ‘ بیشک تو اپنے وعدے کے خلاف کرنے والا نہیں ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,195,'3:195','جواب میں ان کے رب نے فرمایا میں تم میں سے کسی کا عمل ضائع کرنیوالا نہیں ہوں ۔ خواہ مرد ہو یا عورت ‘ تم سب ایک دوسرے کے ہم جنس ہو ۔ لہٰذا جن لوگوں نے میری خاطر اپنے وطن چھوڑے اور جو میری راہ میں اپنے گھروں سے نکالے گئے اور ستائے گئے اور میرے لئے لڑے اور مارے گئے ان کے سب قصور معاف کردوں گا اور انہیں ایسے باغوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہوں گی ۔ یہ ان کی جزا ہے اللہ کے ہاں اور بہترین جزا اللہ ہی کے پاس ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,196,'3:196','اے نبی صلى الله عليه وسلم ! دنیا کے ملکوں میں اللہ کے نافرمان لوگوں کی چلت پھرت تمہیں کسی دھوکے میں نہ ڈالے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,197,'3:197','یہ محض چند روزہ زندگی کا تھوڑا سالطف ہے ‘ پھر یہ سب جہنم میں جائیں گے جو بدترین جائے قرار ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,198,'3:198','برعکس اس کے جو لوگ اپنے رب سے ڈرتے ہوئے زندگی بسر کرتے ہیں ان کے لئے ایسے باغ ہیں جن کے نیچے نہریں بہتی ہیں ۔ ان باغوں میں وہ ہمیشہ رہیں گے ‘ اللہ کی طرف سے یہ سامان ضیافت ہے ان کے لئے اور جو کچھ اللہ کے پاس ہے نیک لوگوں کے لئے وہی سب سے بہتر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(3,199,'3:199','اہل کتاب میں سے کچھ لوگ ایسے ہیں جو اللہ کو مانتے ہیں ‘ اس کتاب پر ایمان لاتے ہیں جو تمہاری طرف بھیجی گئی ہے اور اس کتاب پر بھی ایمان رکھتے ہیں جو اس سے پہلے خود ان کی طرف بھیجی گئی تھی ‘ اللہ کے آگے جھکے ہوئے ہیں ‘ اور اللہ کی آیات کو تھوڑی سی قیمت پر بیچ نہیں دیتے ۔ ان کا اجر ان کے رب کے پاس ہے اور اللہ حساب چکانے میں دیر نہیں لگاتا۔ “');
INSERT INTO "sayyid_qutb" VALUES(3,200,'3:200','اے لوگوجو ایمان لائے ہو ‘ صبر سے کام لو ‘ باطل پرستوں کے مقابلہ میں پامردی دکھاؤ ‘ حق کی خدمت کے لئے کمربستہ رہو اور اللہ سے ڈرتے رہو ‘ امید ہے فلاح پاؤگے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,1,'4:1','” لوگو ! اپنے سے ڈرو جس نے تم کو ایک جان سے پیدا کیا اور اسی جان سے اس کا جوڑا بنایا اور ان دونوں سے بہت مرد و عورت دنیا میں پھیلا دیئے ، اسی خدا سے ڈرو جس کا واسطہ دے کر تم ایک دوسرے سے اپنے حق مانگتے ہو ۔ اور رشتہ و قرابت کے تعلقات کو بگاڑنے سے پرہیز کرو۔ یقین جانو کہ اللہ تم پر نگرانی کر رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,2,'4:2','” یتیموں کے مال ان کو واپس دو ‘ اچھے کو برے مال سے نہ بدل لو ‘ ان کے مال اپنے مال کے ساتھ ملا کر نہ کھاو ‘ یہ بہت بڑا گناہ ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,3,'4:3','” اگر تم یتیموں کے ساتھ بےانصافی کرنے سے ڈرتے ہو تو جو عورتیں تم کو پسندآئیں ان میں سے دو ‘ تین تین ‘ چار چار سے نکاح کرلو ۔ لیکن اگر تمہیں اندیشہ ہو کہ انکے ساتھ عدل نہ کرسکو گے تو پھر ایک ہی بیوی کرو یا ان عورتوں کو زوجیت میں لاو جو تمہارے قبضہ میں آئی ہیں ‘ بےانصافی سے بچنے کیلئے یہ زیادہ قرین صواب ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,4,'4:4','” اور عورتوں کے مہر خوشدلی کے ساتھ ادا کرو ‘ البتہ اگر وہ خود اپنی خوشی سے مہر کا کوئی حصہ تمہیں معاف کردیں تو اسے تم مزے سے کھا سکتے ہو');
INSERT INTO "sayyid_qutb" VALUES(4,5,'4:5','اور اپنے وہ مال جنہیں اللہ نے تمہارے لئے قیام زندگی کا ذریعہ بنایا ہے ‘ نادان لوگوں کے حوالے نہ کرو البتہ انہیں کھانے اور پہننے کیلئے دو اور انہیں نیک ہدایات کرو');
INSERT INTO "sayyid_qutb" VALUES(4,6,'4:6','اور یتیموں کی آزمائش کرتے رہو ‘ یہاں تک کہ وہ نکاح کے قابل عمر کو پہنچ جائیں پھر اگر تم ان کے اندر اہلیت پاوتو ان کے مال ان کے حوالے کر دو ‘ ایسا بھی نہ کرنا کہ حد انصاف سے تجاوز کر کے اس خوف سے ان کے مال ہی جلدی جلدی کھا جاو کہ وہ بڑے ہو کر اپنے حق کا مطالبہ کریں گے ۔ یتیم کا جو سرپرست مالدار ہو وہ پرہیز گاری سے کام لے اور جو غریب ہو وہ معروف طریقہ سے کھائے پھر جب ان کے مال ان کے حوالے کرنے لگو تو لوگوں کو اس پر گواہ بناو اور حساب لینے کے لئے اللہ کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,7,'4:7','مردوں کیلئے اس مال میں حصہ ہے ‘ جو ماں باپ اور رشتہ داروں نے چھوڑا ہو ‘ اور عورتوں کیلئے بھی اس مال میں حصہ ہے ‘ جو ماں باپ اور رشتہ داروں نے چھوڑا ہو ‘ خواہ تھوڑا ہو یا بہت اور یہ حصہ (اللہ کی طرف سے) مقرر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,8,'4:8','اور جب تقسیم کے موقعہ پر کنبہ کے لوگ اور یتیم اور مسکین آئیں تو اس مال میں سے ان کو بھی کچھ دو اور ان کے ساتھ بھلے مانسوں کی سی بات کرو ۔');
INSERT INTO "sayyid_qutb" VALUES(4,9,'4:9','” لوگوں کو اس بات کا خیال کرکے ڈرنا چاہئے کہ اگر وہ خود اپنے پیچھے بےبس اولاد چھوڑتے تو مرتے وقت انہیں اپنے بچوں کے حق میں کیسے کچھ اندیشے لاحق ہوتے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,10,'4:10','پس چاہئے کہ وہ خدا کا خوف کریں ۔ اور راستی کی بات کریں ۔ جو لوگ ظلم کے ساتھ یتیموں کا مال کھاتے ہیں درحقیقت وہ اپنے پیٹ آگ سے بھرتے ہیں ۔ اور وہ ضرور جہنم کی بڑھکتی ہوئی آگ میں جھونکے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(4,11,'4:11','” تمہاری اولاد کے بارے میں تمہیں اللہ ہدایت کرتا ہے کہ مرد کا حصہ دو عورتوں کے برابر ہوگا ۔ اگر میت کی وارث دو سے زیادہ لڑکیاں ہوں تو انہیں ترکے کا دو تہائی دیا جائے اور اگر ایک ہی لڑکی وارث ہو تو آدھا ترکہ اس کا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,12,'4:12','اور تمہاری بیویوں نے جو کچھ چھوڑا ہو اس کا آدھا حصہ تمہیں ملے گا اگر وہ بےاولاد ہوں ‘ ورنہ اولاد ہونے کی صورت میں ترکہ کا ایک چوتھائی حصہ تمہارا ہے جبکہ وصیت جو انہوں نے کی ہو پوری کردی جائے اور قرض جو انہوں نے چھوڑا ہو ادا کردیا جائے ۔ اور وہ تمہارے ترکہ میں سے چوتھائی کی حقدار ہوں گی ۔ اگر تم بےاولاد ہو ‘ ورنہ صاحب اولاد ہونے کی صورت میں ان کا حصہ آٹھواں ہوگا ۔ بعد اس کے کہ جو وصیت تم نے کی ہو وہ پوری کردی جائے اور جو قرض تم نے چھوڑا ہو وہ ادا کردیا جائے ۔ ” اور اگر وہ مرد یا عورت جس کی میراث تقسیم ہو رہی ہے کلالہ ہو (بےاولاد بھی ہو اور اس کے ماں باپ بھی زندہ نہ ہوں) مگر اس کا ایک بھائی یا بہن موجود ہو تو بھائی بہن ہر ایک کو چھٹا حصہ ملے گا ۔ اور اگر بھائی بہن ایک سے زیادہ ہوں تو تو کل ترکہ کے ایک تہائی میں وہ سب شریک ہوں گے ۔ جب کہ وصیت جو کی گئی ہو پوری کردی جائے اور قرض جو میت نے چھوڑا ہو ادا کردیا جائے بشرطیکہ وہ ضرر رساں نہ ہو ۔ یہ حکم ہے اللہ کی طرف سے اور اللہ دانا وبینا اور نرم خو ہے');
INSERT INTO "sayyid_qutb" VALUES(4,13,'4:13','یہ اللہ کی مقرر کی ہوئی حدیں ہیں ۔ جو اللہ اور اس کے رسول کی اطاعت کرے گا اسے اللہ ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی اور ان باغوں میں جو ہمیشہ رہیں گے ۔ اور یہی بڑی کامیابی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,14,'4:14','اور جو اللہ اور اس کے رسول کی نافرمانی کرے گا اور اس کی مقرر کی ہوئی حدوں سے تجاوز کرجائے گا اسے اللہ آگ میں ڈالے گا جس میں وہ ہمیشہ رہے گا ۔ اور اس کیلئے رسوا کن سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,15,'4:15','تمہاری عورتوں میں سے جو بدکاری کی مرتکب ہوں ان پر اپنے میں سے چار آدمیوں کی گواہی لو ‘ اور اگر چار آدمی گواہی دیدیں تو ان کو گھروں میں بند رکھو یہاں تک کہ انہیں موت آجائے یا اللہ ان کیلئے کوئی راستہ نکال دے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,16,'4:16','اور تم میں سے جو اس فعل کا ارتکاب کریں ان دونوں کو تکلیف دو ‘ پھر آکر وہ توبہ کریں اور اپنی صلاح کرلیں تو انہیں چھوڑ دو کہ اللہ بہت توبہ قبول کرنے والا اور رحم فرمانے والا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,17,'4:17','ہاں یہ جان لو کہ اللہ پر توبہ کی قبولیت کا حق انہی لوگوں کے لئے ہے جو نادانی کی وجہ سے برا فعل کر گزرتے ہیں اور اس کے بعد جلدی ہی توبہ کرلیتے ہیں ۔ ایسے لوگوں پر اللہ اپنی نظرعنایت سے پھر متوجہ ہوتا ہے ۔ اور اللہ ساری باتوں کی خبر رکھنے والا اور حکیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,18,'4:18','مگر توبہ ان لوگوں کے لئے نہیں ہے جو برے کام کئے چلے جاتے ہیں یہاں تک کہ جب ان میں سے کسی کی موت کا وقت قریب آجاتا ہے اس وقت وہ کہتا ہے کہ میں نے توبہ کی ۔ اور اسی طرح توبہ ان کے لئے بھی نہیں ہے جو مرتے دم تک کافر رہیں ۔ ایسے لوگوں کے لئے تو ہم نے دردناک سزا تیار کر رکھی ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,19,'4:19','” اے لوگو جو ایمان لائے ہو تمہارے لئے یہ حلال نہیں ہے کہ زبردستی عورتوں کے وارث بن بیٹھو ، اور نہ یہ حلال ہے کہ انہیں تنگ کرکے اس مہر کا کچھ حصہ اڑا لینے کی کوشش کرو جو تم انہیں دے چکے ہو ۔ ہاں اگر وہ کسی صریح بدچلنی کی مرتکب ہوں (تو ضرور تنگ کرنے کا حق ہے) ان کے ساتھ بھلے طریقے سے زندگی بسر کرو ۔ اگر وہ تمہیں ناپسند ہوں تو ہو سکتا ہے کہ ایک چیز تمہیں پسند نہ ہو مگر اللہ نے اسی میں بہت کچھ بھلائی رکھ دی ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(4,20,'4:20','اور اگر تم ایک بیوی کی جگہ دوسری بیوی لے آنے کا ارادہ ہی کرلو تو خواہ تم نے اسے ڈھیر سا مال ہی کیوں نہ دیا ہو ‘ اس میں سے کچھ واپس نہ لینا ۔ کیا تم اسے بہتان لگا کر اور صریح ظلم کرکے واپس لوگے ؟');
INSERT INTO "sayyid_qutb" VALUES(4,21,'4:21','اور آخر تم اسے کس طرح واپس لے لوگے جبکہ تم ایک دوسرے سے لطف اندوز ہوچکے ہو اور وہ تم سے پختہ عہد لے چکی ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(4,22,'4:22','اور جن عورتوں سے تمہارے باپ نکاح کرچکے ہیں ان سے ہر گز نکاح نہ کرو ‘ مگر جو پہلے ہوچکا سو ہوچکا ، درحقیقت یہ ایک بےحیائی کا فعل ہے ‘ ناپسندیدہ ہے ۔ اور برا چلن ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,23,'4:23','” تم پر حرام کردی گئیں ہیں تمہاری مائیں ‘ بیٹیاں ‘ بہنیں ‘ پھوپھیاں ‘ خالائیں ‘ بھتجیاں ‘ بھنجیاں ‘ اور تمہاری وہ مائیں جنہوں نے تم کو دودھ پلایاہو ‘ اور تمہاری دودھ شریک بہنیں اور تمہاری بیویوں کی مائیں ‘ اور تمہاری بیویوں کی لڑکیاں جنہوں نے تمہاری گودوں میں پڑورش پائی ہو ان بیویوں کی لڑکیاں جن سے تمہارا تعلق زن وشو ہوچکا ہو ورنہ اگر تعلق زن وشو نہ ہوا تو تم پر کوئی مواخذہ نہیں ہے ۔ اور تمہارے ان بیٹوں کی بیویاں جو تمہارے صلب سے ہوں اور یہ بھی تم پر حرام کیا گیا ہے کہ ایک نکاح میں دو بہنوں کو جمع کرو ‘ مگر جو پہلے ہوگیا سو ہوگیا ۔ اللہ بخشنے والا اور رحم کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,24,'4:24','” اور وہ عورتیں بھی تم پر حرام ہیں جو کسی دوسرے کے نکاح میں ہوں ، البتہ ایسی عورتیں اس سے مستثنی ہیں جو جنگ میں تمہارے ہاتھ آجائیں ، یہ اللہ کا قانون ہے جس کی پابندی تم پر لازم کردی گئی ہے ۔ ان کے سوا جتنی عورتیں ہیں انہیں اپنے اموال کے ذریعے سے حاصل کرنا تمہارے لئے حلال کردیا گیا ہے ، بشرطیکہ حصار نکاح میں ان کو محفوظ کرو ‘ نہ یہ کہ آزاد شہوت رانی کرنے لگو ، پھر جو ازدواجی زندگی کا لطف تم ان سے اٹھاؤ اس کے بدلے انکے مہر بطور فرض کے ادا کرو ‘ البتہ مہر کی قرار داد ہوجانے کے بعد آپس کی رضا مندی سے تمہارے درمیان اگر کوئی سمجھوتہ ہوجائے تو اس میں کوئی حرج نہیں ‘ اللہ علیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,25,'4:25','اور جو شخص تم میں سے اتنی مقدرت نہ رکھتا ہو کہ خاندانی مسلمان عورتوں سے نکاح کرسکے ‘ اسے چاہیے کہ تمہاری ان لونڈیوں میں سے کسی کے ساتھ نکاح کرے جو تمہارے قبضہ میں ہوں اور مومنہ ہوں ۔ اللہ تمہارے ایمان کا حال خوب جانتا ہے ‘ تم سب ایک ہی گروہ کے لوگ ہو ‘ لہذا ان کے سرپرستوں کی اجازت سے ان کے ساتھ نکاح کرلو اور معروف طریقے سے ان کے مہر ادا کر دو ‘ تاکہ وہ حصار نکاح میں محفوظ ہو کر رہیں ‘ نہ آزاد شہوت رانی کرتی پھریں اور نہ چوری چھپے آشنائیاں کریں ، پھر جب وہ حصار نکاح میں محفوظ ہوجائیں اور اس کے بعد کسی بدچلنی کی مرتکب ہوں تو ان پر اس سزا کی بہ نسبت آدھی سزا ہے جو ان عورتوں (محصنات) کے لئے مقرر ہے ۔ یہ سہولت تم میں سے ان لوگوں کے لئے پیدا کی گئی ہے جن کو شادی نہ کرنے سے بند تقوی کے ٹوٹ جانے کا اندیشہ ہو ۔ لیکن اگر تم صبر کرو تو یہ تمہارے لئے بہتر ہے اور اللہ بخشنے والا اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,26,'4:26','اللہ چاہتا ہے کہ تم پر ان طریقوں کو واضح کرے اور انہی طریقوں پر تمہیں چلائے جن کی پیروی تم سے پہلے گزرے ہوئے صلحاء کرتے تھے ۔ وہ اپنی رحمت کے ساتھ تمہاری طرف متوجہ ہونے کا ارادہ رکھتا ہے اور وہ علیم بھی ہے اور دانا بھی ۔');
INSERT INTO "sayyid_qutb" VALUES(4,27,'4:27','ہاں ‘ اللہ تو تم پر رحمت کے ساتھ توجہ کرنا چاہتا ہے مگر جو لوگ خود اپنی خواہشات نفس کی پیروی کر رہے ہیں وہ چاہتے ہیں کہ تم راہ راست سے ہٹ کر دور نکل جاؤ ،');
INSERT INTO "sayyid_qutb" VALUES(4,28,'4:28','اللہ تم پر سے پابندیوں کو ہلکا کرنا چاہتا ہے کیونکہ انسان کمزور پیدا کیا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,29,'4:29','” اے لوگو جو ایمان لائے ہو آپس میں ایک دوسرے کے مال باطل طریقوں سے نہ کھاؤ ۔ لین دین ہونا چاہئے آپس کی رضا مندی سے اور اپنے آپ کو قتل نہ کرو ‘ یقین مانو کہ اللہ تمہارے اوپر مہربان ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,30,'4:30','جو شخص ظلم و زیادتی کے ساتھ ایسا کرے گا اس کو ہم ضرور آگ میں جھونکیں گے اور یہ اللہ کے لئے کوئی مشکل کام نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,31,'4:31','اگر تم ان بڑے بڑے گناہوں سے پرہیز کرتے رہو جن سے تمہیں منع کیا جارہا ہے تو تمہاری چھوٹی موٹی برائیوں کو ہم تمہارے حساب سے ساقط کردیں گے اور تم کو عزت کی جگہ داخل کریں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,32,'4:32','اور جو کچھ اللہ نے تم میں سے کسی کو دوسروں کے مقابلے میں زیادہ دیا ہے اس کی نسبت تمنا نہ کرو ‘ جو کچھ مردوں نے کمایا ہے اس کے مطابق ان کا حصہ ہے ‘ اور جو کچھ عورتوں نے کمایا ہے اس کے مطابق ان کا حصہ ہے ہاں اللہ سے اس کے فضل کی دعا مانگتے رہو ‘ یقینا اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,33,'4:33','اور ہم نے ہر اس ترکے کے حقدار مقرر کردیئے ہیں جو والدین اور رشتہ دار چھوڑیں ‘ اب رہے وہ لوگ جن سے تمہارے عہد و پیمان ہوں تو ان کا حصہ انہیں دو ‘ یقینا اللہ ہر چیز پر نگران ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,34,'4:34','” مرد عورتوں پر قوام ہیں ‘ اس بنا پر کہ اللہ نے ان میں سے ایک کو دوسرے پر فضیلت دی ہے ‘ اور اس بنا پر کہ مرد اپنے مال خرچ کرتے ہیں ۔ پس جو صالح عورتیں ہیں وہ اطاعت شعار ہوتی ہیں اور مردوں کے پیچھے اللہ کی حفاظت اور نگرانی میں ان کے حقوق کی حفاظت کرتی ہیں ۔ اور جن عورتوں سے تمہیں سرکشی کا اندیشہ ہو تو انہیں سمجھاؤ خواب گاہوں میں ان سے علیحدہ رہو اور مارو ‘ پھر اگر وہ تمہاری مطیع ہوجائیں تو خواہ مخواہ ان پر دست درازی کے لئے بہانے تلاش نہ کرو ‘ یقین رکھو کہ اوپر اللہ موجود ہے جو بڑا اور بالاتر ہے ۔ اور تم لوگوں کو کہیں میاں اور بیوی کے تعلقات بگڑنے کا اندیشہ ہو تو ایک حکم مرد کے رشتہ داروں میں سے اور ایک عورت کے رشتہ داروں میں سے مقرر کرو ‘ وہ دونوں اصلاح کرنا چاہیں گے تو اللہ ان کے درمیان موافقت کی صورت پیدا کر دے گا ‘ اللہ سب کچھ جانتا اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,35,'4:35','” اور اگر تم لوگوں کو کہیں میاں اور بیوی کے تعلقات صاف ٹوٹ جانے کا اندیشہ ہو تو ایک حکم مرد کے رشتے داروں میں سے اور ایک عورت کے رشتہ داروں میں سے مقرر کرو ‘ وہ دونوں اصلاح کرنا چاہیں گے تو اللہ ان کے درمیان موافقت کی صورت نکال دے گا اللہ سب کچھ جانتا ہے اور باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,36,'4:36','” اور تم سب اللہ کی بندگی کرو ‘ اس کے ساتھ کسی کو شریک نہ بناؤ ‘ ماں باپ کے ساتھ نیک برتاؤ کرو ‘ قرابت داروں اور یتیموں اور مسکینوں کے ساتھ حسن سلوک سے پیش آؤ ‘ اور پڑوسی رشتہ دار سے ‘ اجنبی ہمسایہ سے ‘ پہلو کے ساتھی اور مسافر سے اور ان لونڈی غلاموں سے جو تمہارے قبضے میں ہوں ‘ احسان کا معاملہ رکھو ‘ یقین جانو اللہ کسی ایسے شخص کو پسند نہیں کرتا جو اپنے پندار میں مغرور ہو اور اپنی بڑائی پر فخر کرے');
INSERT INTO "sayyid_qutb" VALUES(4,37,'4:37','ایسے لوگ بھی اللہ کو پسند نہیں ہیں جو کنجوسی کرتے ہیں اور دوسروں کو بھی کنجوسی کی ہدایت کرتے ہیں اور جو کچھ اللہ نے اپنے فضل سے انہیں دیا ہے اسے چھپاتے ہیں ‘ ایسے کافر نعمت لوگوں کے لئے ہم نے رسوا کن عذاب مہیا کر رکھا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,38,'4:38','اور وہ لوگ بھی اللہ کو ناپسند ہیں جو اپنے مال محض لوگوں کو دکھانے کے لئے خرچ کرتے ہیں اور درحقیقت نہ اللہ پر ایمان رکھتے ہیں نہ روز آخر پر ‘ سچ تو یہ ہے کہ شیطان جس کا رفیق ہوا ہو اسے بہت ہی بری رفاقت میسر آئی');
INSERT INTO "sayyid_qutb" VALUES(4,39,'4:39','آخر ان لوگوں پر کیا آفت آجاتی اگر وہ اللہ اور روز آخر پر ایمان رکھتے اور جو کچھ اللہ نے دیا ہے اس میں سے خرچ کرتے اور اگر یہ ایسا کرتے تو اللہ سے ان کی نیکی کا حال چھپا نہ رہ جاتا');
INSERT INTO "sayyid_qutb" VALUES(4,40,'4:40','اللہ کسی پر ذرہ برابر بھی ظلم نہیں کرتا ‘ اگر کوئی ایک نیکی کرے تو اللہ اسے دوچند کرتا ہے اور پھر اپنی طرف سے بڑا اجر عطا فرماتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,41,'4:41','پھر سوچو کہ اس وقت یہ لوگ کیا کریں گے جب ہم ہر امت میں سے ایک گواہ لائیں گے اور ان لوگوں پر تمہیں (یعنی آپ کو اے محمد صلى الله عليه وسلم گواہ کی حیثیت سے کھڑا کریں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,42,'4:42','اس وقت وہ سب لوگ جنہوں نے رسول کی بات نہ مانی اور اس کی نافرمانی کرتے رہے ‘ تمنا کریں گے کہ کاش زمین پھٹ جائے اور وہ اس میں سما جائیں ۔ وہاں یہ اپنی کوئی بات اللہ سے نہ چھپا سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(4,43,'4:43','” اے لوگو جو ایمان لائے ہو ‘ جب تم نشے کی حالت میں ہو تو نماز کے قریب نہ جاؤ ۔ نماز اس وقت پڑھنی چاہئے جب تم جانو کہ کیا کہہ رہے ہو ‘ اور اسی طرح جنابت کی حالت میں نماز کے قریب نہ جاؤ جب تک غسل نہ کرلو ‘ الا یہ کہ راستے سے گزرتے ہو ‘ اور اگر کبھی ایسا ہو کہ تم بیمار ہو ‘ یا سفر میں ہو ‘ یا تم میں سے کوئی شخص رفع حاجت کرکے آئے ‘ یا تم نے عورتوں سے لمس کیا ہو ‘ اور پھر پانی نہ ملے تو پاک مٹی سے کام لو اور اس سے اپنے چہروں اور ہاتھوں پر مسح کرلو ‘ بیشک اللہ نرمی سے کام لینے والا اور بخشش فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,44,'4:44','” تم نے ان لوگوں کو بھی دیکھا جنہیں کتاب کے علم کا کچھ حصہ دیا گیا ہے ؟ وہ خود ضلالت کے خریدار بنے ہوئے ہیں اور چاہتے ہیں کہ تم بھی راہ گم کر دو');
INSERT INTO "sayyid_qutb" VALUES(4,45,'4:45','اللہ تمہارے دشمنوں کو خوب جانتا ہے اور تمہاری حمایت ومددگاری کے لئے اللہ ہی کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,46,'4:46','جن لوگوں نے یہودیت کا طریقہ اختیار کیا ہے ان میں کچھ لوگ ہیں جو الفاظ کو ان کے محل سے پھیر دیتے ہیں اور دین حق کے خلاف نیش زنی کرکے اپنی زبانوں کو توڑ موڑ کر کہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,47,'4:47','” اے وہ لوگو جنہیں کتاب دی گئی تھی ‘ مان لو اس کتاب کو جو ہم نے اب نازل کی ہے اور جو اس کتاب کی تصدیق وتائید کرتی ہے جو تمہارے پاس پہلے سے موجود تھی ‘ اس پر ایمان لے آؤ قبل اس کے کہ ہم چہرے بگاڑ کر پیچھے پھیر دیں یا ان کو اسی لعنت زدہ کردیں جس طرح سبت والوں کے ساتھ ہم نے کیا تھا اور یاد رکھو کہ اللہ کا حکم نافذ ہوکر رہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,48,'4:48','اللہ بس شرک ہی کو معاف نہیں کرتا ‘ اس کے ماسوا دوسرے جس قدر گناہ ہیں وہ جس کے لئے چاہتا ہے معاف کردیتا ہے ‘ اللہ کے ساتھ جس نے کسی اور کو شریک ٹھہرایا ‘ اس نے تو بہت ہی بڑا جھوٹ تصنیف کیا اور بڑے سخت گناہ کی بات کی ۔');
INSERT INTO "sayyid_qutb" VALUES(4,49,'4:49','” تم نے ان لوگوں کو بھی دیکھا جو بہت اپنی پاکیزگی نفس کا دم بھرتے ہیں ؟ حالانکہ پاکیزگی تو اللہ ہی جسے چاہتا ہے عطا کرتا ہے اور (انہیں جو پاکیزگی نہیں ملتی تو درحقیقت ان پر ذرہ برابر ظلم نہیں کیا جاتا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,50,'4:50','دیکھو تو سہی ‘ یہ اللہ پر بھی جھوٹے افتراء گھڑنے سے نہیں چوکتے اور ان کے صریحا گناہ گار ہونے کے لئے یہی ایک گناہ کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,51,'4:51','” کیا تم نے ان لوگوں کو نہیں دیکھا جنہیں کتاب کے علم میں سے کچھ حصہ دیا گیا ہے اور ان کا حال یہ ہے کہ جبت اور طاغوت کو مانتے ہیں اور کافروں کے متعلق کہتے ہیں کہ ایمان لانے والوں سے تو یہی زیادہ صحیح راستے پر ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(4,52,'4:52','ایسے ہی لوگ ہیں جن پر اللہ نے لعنت کی ہے اور جس پر اللہ لعنت کر دے پھر تم اس کا کوئی مددگار نہیں پاؤ گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,53,'4:53','کیا حکومت میں ان کا کوئی حصہ ہے ؟ اگر ایسا ہوتا تو دوسروں کو ایک پھوٹی کوڑی تک نہ دیتے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,54,'4:54','پھر کیا یہ دوسروں سے اس لئے حسد کرتے ہیں اللہ نے انہیں اپنے فضل سے نواز دیا ؟ اگر یہ بات ہے تو انہوں معلوم ہو کہ ہم نے ابراہیم (علیہ السلام) کی اولاد کو کتاب اور حکمت عطا کی اور ملک عظیم بخش دیا ‘');
INSERT INTO "sayyid_qutb" VALUES(4,55,'4:55','مگر ان میں سے کوئی اس پر ایمان لایا اور کوئی اس سے منہ موڑ گیا ‘ اور منہ موڑنے والوں کے لئے تو بس جہنم کی بھڑکتی ہوئی آگ ہی کافی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,56,'4:56','جن لوگوں نے ہماری آیات کو ماننے سے انکار کردیا ہے انہیں بالیقین ہم آگ میں جھونکیں گے اور جب ان کے بدن کی کھال گل جائے گی تو اس کی جگہ دوسری کھال پیدا کردیں گے تاکہ وہ خوب عذاب کا مزا چکھیں ‘ اللہ بڑی قدرت رکھتا ہے اور اپنے فیصلوں کو عمل میں لانے کی حکمت خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(4,57,'4:57','اور جن لوگوں نے ہماری آیات کو مان لیا اور نیک عمل کئے ان کو ہم ایسے باغوں میں داخل کریں گے جن کے نیچے نہریں بہتی ہوں گی ‘ جہاں وہ ہمیشہ ہمیشہ رہیں گے اور ان کو پاکیزہ بیویاں ملیں گی اور انہیں ہم گھنی چھاؤں میں رکھیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,58,'4:58','” مسلمانو ! اللہ تمہیں حکم دیتا ہے کہ امانتیں اہل امانت کے سپرد کرو ‘ اور جب لوگوں کے درمیان فیصلہ کرو تو عدل کے ساتھ کرو ‘ اللہ تم کو نہایت عمدہ نصیحت کرتا ہے اور یقینا اللہ سب کچھ سنتا اور دیکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,59,'4:59','” اے لوگو جو ایمان لائے ہو ‘ اطاعت کرو اللہ کی اور اطاعت کرو رسول صلى الله عليه وسلم کی اور ان لوگوں کی جو تم میں سے صاحب امر ہوں ‘ پھر اگر تمہارے درمیان کسی معاملہ میں نزاع ہوجائے تو اسے اللہ اور رسول کی طرف پھیر دو ۔ اگر تم واقعی اللہ اور روز آخرت پر ایمان رکھتے ہو ۔ یہی ایک صحیح طریق کار ہے اور انجام کے اعتبار سے بھی بہتر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,60,'4:60','” اے نبی صلى الله عليه وسلم تم نے دیکھا نہیں ان لوگوں کو جو دعوی تو کرتے ہیں کہ ہم ایمان لائے ہیں اس کتاب پر جو تمہاری طرف نازل کی گئی ہے اور ان کتابوں پر جو تم سے پہلے نازل کی گئی تھیں ، مگر چاہتے یہ ہیں کہ اپنے معاملات کا فیصلہ کرانے کے لئے طاغوت کی طرف رجوع کریں ‘ حالانکہ انہیں طاغوت سے کفر کرنے کا حکم دیا گیا تھا ‘ شیطان انہیں بھٹکا کر راہ راست سے بہت دور لے جانا چاہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,61,'4:61','اور جب ان سے کہا جاتا ہے کہ آؤ اس چیز کی طرف جو اللہ نے نازل کی ہے اور آؤ رسول کی طرف تو ان منافقوں کو تم دیکھتے ہو کہ یہ تمہاری طرف آنے سے کتراتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,62,'4:62','پھر اس وقت کیا ہوتا ہے جب ان کے اپنے ہاتھوں کی کمائی ہوئی مصیبت ان پر آپڑتی ہے ؟ اس وقت یہ تمہارے پاس قسمیں کھاتے ہوئے آتے ہیں اور کہتے ہیں کہ خدا کی قسم ہم تو صرف بھلائی چاہتے تھے اور ہماری نیت تو یہ تھی کہ فریقین میں کسی طرح موافقت ہو جائے');
INSERT INTO "sayyid_qutb" VALUES(4,63,'4:63','اللہ جانتا ہے جو کچھ ان کے دلوں میں ہے ، ان سے تعرض مت کرو انہیں سمجھاؤ اور ایسی نصیحت کرو جو ان کے دلوں میں اتر جائے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,64,'4:64','(انہیں بتاؤ کہ) ہم نے جو رسول بھی بھیجا ہے اسی لئے بھیجا ہے کہ اذن خداوندی کی بنا پر اس کی اطاعت کی جائے ۔ اگر انہوں نے یہ طریقہ اختیار کیا ہوتا کہ جب وہ اپنے نفس پر ظلم کر بیٹھے تھے تو تمہارے پاس آجاتے اور اللہ سے معافی مانگتے اور رسول بھی ان کے لئے معافی کی درخواست کرتا ‘ تویقینا اللہ کو بخشنے والا اور رحم کرنے والا پاتے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,65,'4:65','نہیں اے محمد صلى الله عليه وسلم تمہارے رب کی قسم یہ کبھی مومن نہیں ہو سکتے جب تک کہ اپنے باہمی اختلافات میں یہ تم کو فیصلہ کرنے والا نہ مان لیں پھر جو کچھ تم فیصلہ کرو اس پر اپنے دلوں میں کوئی تنگی نہ محسوس کریں ‘ بلکہ سربسر تسلیم کرلیں۔');
INSERT INTO "sayyid_qutb" VALUES(4,66,'4:66','” ہم نے انہیں حکم دیا ہوتا کہ اپنے آپ کو ہلاک کر دو یا اپنے گھروں سے نکل جاؤ تو ان میں سے کم ہی آدمی اس پر عمل کرتے ‘ حالانکہ جو نصیحت انہیں کی جاتی ہے اگر یہ اس پر عمل کرتے تو یہ ان کے لئے زیادہ بہتری اور زیادہ ثابت قدمی کا موجب ہوتا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,67,'4:67','اور جب یہ ایسا کرتے تو ہم انہیں اپنی طرف سے بہت بڑا اجر دیتے');
INSERT INTO "sayyid_qutb" VALUES(4,68,'4:68','اور انہیں سیدھا راستہ دکھا دیتے');
INSERT INTO "sayyid_qutb" VALUES(4,69,'4:69','” جو لوگ اللہ اور رسول کی اطاعت کریں گے وہ ان لوگوں کے ساتھ ہوں گے جن پر اللہ نے انعام فرمایا ہے یعنی انبیاء اور صدیقین اور شہداء اور صالحین اور اچھے ہیں یہ رفیق جو کسی کو میسر آئیں');
INSERT INTO "sayyid_qutb" VALUES(4,70,'4:70','یہ حقیقی فضل ہے جو اللہ کی طرف سے ملتا ہے اور حقیقت جاننے کے لئے بس اللہ ہی کا علم کافی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,71,'4:71','” اے لوگو جو ایمان لائے ہو ‘ مقابلہ کے لئے ہر وقت تیار رہو ۔ پھر جیسا موقع ہو الگ الگ دستوں کی شکل میں نکلو یا اکٹھے ہو کر');
INSERT INTO "sayyid_qutb" VALUES(4,72,'4:72','ہاں ‘ تم میں کوئی کوئی آدمی ایسا بھی ہے جو لڑائی سے جی چراتا ہے اگر تم پر کوئی مصیبت آئے تو کہتا ہے اللہ نے مجھ پر بڑا فضل کیا کہ میں ان لوگوں کے ساتھ نہ گیا');
INSERT INTO "sayyid_qutb" VALUES(4,73,'4:73','اور اگر اللہ کی طرف سے تم پر فضل ہو تو کہتا ہے اور اس طرح کہتا ہے کہ گویا تمہارے اور اس کے درمیان محبت کا تو کوئی تعلق تھا ہی نہیں کہ کاش میں بھی ان کے ساتھ ہوتا تو بڑا کام بن جاتا');
INSERT INTO "sayyid_qutb" VALUES(4,74,'4:74','(ایسے لوگوں کو معلوم ہو کہ) اللہ راہ میں لڑنا چاہئے ان لوگوں کو جو آخرت کے بدلے دنیا کی زندگی کو فروخت کردیں ‘ پھر جو اللہ کی راہ میں لڑے گا اور مارا جائے گا یا غالب رہے گا اسے ضرور ہم اجر عظیم عطا کریں گے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,75,'4:75','” آخر کیا وجہ ہے کہ تم اللہ کی راہ میں ان بےبس مردوں ‘ عورتوں اور بچوں کی خاطر نہ لڑو جو کمزور پاکر دبا لئے گئے ہیں اور فریاد کر رہے ہیں کہ خدایا ہم کو اس بستی سے نکال جس کے باشندے ظالم ہیں اور اپنی طرف سے ہمارا کوئی حامی و مددگار پیدا کر دے');
INSERT INTO "sayyid_qutb" VALUES(4,76,'4:76','” جن لوگوں نے ایمان کا راستہ اختیار کیا ہے ‘ وہ اللہ کی راہ میں لڑتے ہیں اور جنہوں نے کفر کا راستہ اختیار کیا ہے وہ طاغوت کی راہ لڑتے ہیں ‘ پس شیطان کے ساتھیوں سے لڑو اور یقین جانو کہ شیطان کی چالیں حقیقت میں نہایت کمزور ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(4,77,'4:77','’ تم نے ان لوگوں کو بھی دیکھا جن سے کہا گیا تھا کہ اپنے ہاتھ روکے رکھو اور نماز قائم کرو اور زکوۃ دو ؟ اب جو انہیں لڑائی کا حکم دیا گیا تو ان میں سے ایک فریق کا حال یہ ہے کہ لوگوں سے ایسا ڈر رہے ہیں جیسا خدا سے ڈرنا چاہئے یا کچھ اس سے بھی بڑھ کر ‘ کہتے ہیں خدایا ‘ یہ ہم پر لڑائی کا حکم کیوں لکھ دیا ؟ کیوں نہ ہمیں ابھی کچھ اور مہلت دی ؟ ان سے کہو ‘ دنیا کا سرمایہ زندگی تھوڑا ہے ‘ اور آخرت ایک خدا ترس انسان کے لئے زیادہ بہتر ہے ‘ اور تم پر ظلم ایک شمہ برابر بھی نہ کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(4,78,'4:78','رہی موت تو جہاں بھی تم ہو وہ بہرحال تمہیں آکر رہے گی خواہ تم کیسی ہی مضبوط عمارتوں میں ہو۔ اگر انہیں کوئی فائدہ پہنچتا ہے تو کہتے ہیں کہ یہ اللہ کی طرف سے ہے ‘ اور اگر کوئی نقصان پہنچتا ہے تو کہتے ہیں کہ اے نبی یہ آپ کی بدولت ہے ‘ کہو سب کچھ اللہ ہی کی طرف سے ہے ‘ ان لوگوں کو کیا ہوگیا ہے کہ کوئی بات ان کی سمجھ میں نہیں آتی ۔');
INSERT INTO "sayyid_qutb" VALUES(4,79,'4:79','’ اے انسان تجھے جو بھلائی بھی حاصل ہوتی ہے اللہ کی عنایت سے ہوتی ہے ‘ اور جو مصیبت تجھ پر آتی ہے وہ تیرے اپنے کسب وعمل کی بدولت ہے ‘ ۔ اے محمد صلى الله عليه وسلم ہم نے تم کو لوگوں کے لئے رسول بنا کر بھیجا ہے اور اس پر خدا کی گواہی کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,80,'4:80','جس نے رسول کی اطاعت کی اس نے دراصل خدا کی اطاعت کی ‘ اور جو منہ موڑ گیا ‘ تو بہرحال ہم نے تمہیں ان لوگوں پر پاسبان بنا کر تو نہیں بھیجا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,81,'4:81','وہ منہ پر کہتے ہیں ہم مطیع فرمان ہیں مگر جب تمہارے پاس سے نکلتے ہیں تو ان میں سے ایک گروہ راتوں کو جمع ہو کر تمہاری باتوں کے خلاف مشورے کرتا ہے ‘ اللہ انکی یہ ساری سرگوشیاں لکھ رہا ہے ‘ تم انکی پروا نہ کرو اور اللہ پر بھروسہ رکھو ‘ وہی بھروسہ کے لئے کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,82,'4:82','کیا یہ لوگ قرآن پر غور نہیں کرتے ؟ اگر یہ اللہ کی سوا کسی اور کی طرف سے ہوتا تو اس میں بہت کچھ اختلاف بیانی پائی جاتی');
INSERT INTO "sayyid_qutb" VALUES(4,83,'4:83','یہ لوگ جہاں کوئی اطمینان بخش یا خوفناک خبر سن پاتے ہیں اسے لے کر پھیلا دیتے ہیں ‘ حالانکہ اگر یہ اسے رسول اور اپنی جماعت کے ذمہ دار اصحاب تک پہنچائیں تو وہ ایسے لوگوں کے علم میں آجائے جو ان کے درمیان اس بات کی صلاحیت رکھتے ہیں کہ اس سے صحیح نتیجہ اخذ کرسکیں ‘ تم لوگوں پر اللہ کی مہربانی اور رحمت نہ ہوتی تو (تمہاری کمزوریاں ایسی تھیں کہ) معدودے چند کے سوا تم سب شیطان کے پیچھے لگ گئے ہوتے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,84,'4:84','” پس اے نبی صلى الله عليه وسلم تم اللہ کی راہ میں لڑو ‘ تم اپنی ذات کے سوا کسی اور کے ذمہ دار نہیں ہو ۔ البتہ اہل ایمان کو لڑنے کے لئے اکساؤ ‘ بعید نہیں کہ اللہ کافروں کا زور توڑ دے ‘ اللہ کا زور سب سے زیادہ زبردست اور اس کی سزا سب سے زیادہ سخت ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(4,85,'4:85','” جو بھلائی کی سفارش کرے گا وہ اس میں سے حصہ پائے گا اور جو برائی کی سفارش کرے گا وہ اس میں سے حصہ پائے گا اور اللہ ہر چیز پر نظر رکھنے والا ہے ۔ “');
INSERT INTO "sayyid_qutb" VALUES(4,86,'4:86','” اور جب کوئی احترام کے ساتھ تمہیں سلام کرے تو اس سے بہتر طریقے سے جواب دو یا کم از کم اسی طرح لوٹا دو ‘ اللہ ہر چیز کا حساب لینے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,87,'4:87','” اللہ کے سوا کوئی اور حاکم نہیں ہے ۔ وہ تم کو قیامت کے دن جمع کرے گا جس میں شک نہیں ہے اور اللہ سے سچی کس کی بات ہے');
INSERT INTO "sayyid_qutb" VALUES(4,88,'4:88','” پھر تم کو کیا ہوا کہ منافقوں کے بارے میں دو فریق ہو رہے ہیں ‘ اور اللہ نے تو ان کو ان کے اعمال کے سبب الٹ دیا ہے ‘ کیا تم ان لوگوں کو راہ پر لانا چاہتے ہو جن کو اللہ نے گمراہ کردیا ہے اور جس کو اللہ نے گمراہ کیا تم اس کے لئے کوئی راہ نہ پاؤ گے ‘');
INSERT INTO "sayyid_qutb" VALUES(4,89,'4:89','وہ تو چاہتے ہیں کہ تم بھی اسی طرح کافر ہوجاؤ جس طرح وہ کافر ہوئے ہیں ‘ اس طرح تم سب برابر ہوجاؤ ‘ لہذا تم ان میں سے کسی کو دوست نہ بناؤ یہاں تک کہ وہ اللہ کی راہ میں ہجرت کرلیں ‘ پھر اگر یہ اس شرط کو قبول نہ کریں تو ان کو پکڑو ‘ مار ڈالو ‘ جہاں بھی پاؤ اور ان میں سے کسی کو دوست اور مددگار نہ بناؤ');
INSERT INTO "sayyid_qutb" VALUES(4,90,'4:90','” اللہ چاہتا تو ان کو تم پر مسلط کردیتا اور وہ بھی تم سے لڑتے ۔ لہذا اگر وہ تم سے کنارہ کش ہوجائیں اور لڑنے سے باز رہیں اور تمہاری طرف صلح وآشتی کا ہاتھ بڑھائیں تو اللہ نے تمہارے لئے ان پر دست درازی کی کوئی سبیل نہیں رکھی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,91,'4:91','” ایک اور قسم کے منافق تمہیں ایسے ملیں گے جو چاہتے ہیں کہ تم سے بھی امن میں رہیں اور اپنی قوم سے بھی مگر جب کبھی فتنہ کا موقعہ پائیں گے اس میں کود پڑے گے ۔ ایسے لوگ اگر تمہارے مقابلے سے باز نہ رہیں اور صلح وسلامتی تمہارے آگے پیش نہ کریں اور اپنے ہاتھ نہ روکیں تو جہاں وہ ملیں انہیں پکڑو اور مارو ‘ ان پر ہاتھ اٹھانے کے لئے ہم نے تمہیں کھلی حجت دے دی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,92,'4:92','” کسی مومن کا یہ کام نہیں ہے کہ دوسرے مومن کو قتل کرے الا یہ کہ اس سے چوک ہوجائے ‘ اور جو شخص کسی مومن کو غلطی سے قتل کر دے تو اس کا کفارہ یہ ہے کہ ایک مومن کو غلامی سے آزاد کر دے اور مقتول کے وارثوں کو خون بہا دے ‘ الا یہ کہ وہ خون بہا معاف کردیں ‘ لیکن اگر وہ مسلمان مقتول کسی ایسی قوم سے تھا جس سے تمہاری دشمنی ہو تو اس کا کفارہ مومن غلام آزاد کرنا ہے اور اگر وہ کسی ایسی غیر مسلم قومکا فرد تھا جس سے تمہارا معاہدہ ہو تو اس کے وارثوں کو خون بہا دیا جائے گا اور ایک مومن غلام کو آزاد کرنا ہوگا ۔ پھر جو غلام نہ پائے ہو پے درپے دو مہینے روزے رکھے ۔ یہ اس گناہ پر اللہ سے توبہ کرنے کا طریقہ ہے اور اللہ علیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,93,'4:93','رہا وہ شخص جو کسی مومن کو جان بوجھ کر قتل کرے تو اس کی جزا جہنم ہے جس میں وہ ہمیشہ رہے گا ۔ اس پر اللہ کا غضب اور اس کی لعنت ہے اور اللہ نے اس کے لئے سخت عذاب مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,94,'4:94','(اے لوگو جو ایمان لائے ہو ‘ جب تم اللہ کی راہ میں جہاد کے لئے نکلو تو دوست دشمن میں تمیز کرو اور جو تمہاری طرف سلام سے تقدیم کرے اسے فورا نہ کہہ دو کہ تو مومن نہیں ہے ۔ اگر تم دنیوی فائدہ چاہتے ہو تو اللہ کے پاس تمہارے لئے بہت سے اموال غنیمت ہیں۔ آخر اسی حالت میں تم خود بھی تو اس سے پہلے مبتلا رہ چکے ہو ‘ پھر اللہ نے تم پر احسان کیا ‘ لہذا تحقیق سے کام لو ‘ جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,95,'4:95','(مسلمانوں میں سے وہ لوگ جو معذوری کے بغیر گھر بیٹھے رہتے ہیں اور وہ جو اللہ کی راہ میں جان ومال سے جہاد کرتے ہیں دونوں کی حیثیت یکساں نہیں ہے ۔ اللہ نے بیٹھنے والوں کی بہ نسبت جان ومال سے جاہد کرنے والوں کا درجہ بڑا رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,96,'4:96','اگرچہ ہر ایک کے لئے اللہ نے بھلائی کا وعدہ فرمایا ہے ‘ مگر اس کے ہاں مجاہدوں کی خدمات کا معاوضہ بیٹھنے والوں سے بہت زیادہ ہے ان کے لئے اللہ کی طرف سے بڑے درجے ہیں اور مغفرت اور رحمت ہے اور اللہ بڑا معاف کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,97,'4:97','(جو لوگ اپنے نفس پر ظلم کر رہے تھے ان کی روحیں جب فرشتوں نے قبض کیں تو ان سے پوچھا کہ یہ تم کس حال میں مبتلا تھے ؟ انہوں نے جواب دیا کہ ہم زمین میں کمزور ومجبور تھے ۔ فرشتوں نے کہا ‘ کیا خدا کی زمین وسیع نہ تھی کہ تم اس میں ہجرت کرتے ؟ یہ وہ لوگ ہیں جن کا ٹھکانا جہنم ہے اور وہ بڑا ہی برا ٹھکانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,98,'4:98','ہاں جو مرد ‘ عورتیں اور بچے واقعی بےبس ہیں اور نکلنے کا کوئی راستہ اور ذریعہ نہیں پاتے ‘۔');
INSERT INTO "sayyid_qutb" VALUES(4,99,'4:99','بعید نہیں کہ اللہ انہیں معاف کر دے ۔ اللہ بڑا معاف کرنے والا اور درگزر فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,100,'4:100','(جو کوئی اللہ کی راہ میں ہجرت کرے گا وہ زمین میں پناہ لینے کے لئے بہت جگہ اور بسر اوقات کے لئے بڑی گنجائش پائے گا ‘ اور جو اپنے گھر سے اللہ اور رسول کی طرف ہجرت کے لئے نکلے ‘ پھر راستہ ہی میں اسے موت آجائے اس کا اجر اللہ کے ذمے واجب ہوگیا ‘ اللہ بہت بخشش فرمانے والا اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(4,101,'4:101','(اور جب تم لوگ سفر کے لئے نکلو تو کوئی مضائقہ نہیں اگر نماز میں اختصار کر دو (خصوصا) جبکہ تمہیں اندیشہ ہو کہ کافر تمہیں ستائیں گے کیونکہ وہ کھلم کھلا تمہاری دشمنی پر تلے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,102,'4:102','(اور اے نبی ‘ جب تم مسلمانوں کے درمیان ہو اور (حالت جنگ میں) انہیں نماز پڑھانے کھڑے ہو تو چاہئے کہ ان میں سے ایک گروہ تمہارے ساتھ کھڑا ہو اور اپنے اسلحہ لئے رہے ‘ پھر جب وہ سجدہ کرلے تو پیچھے چلا جائے اور دوسرا گروہ جس نے ابھی نماز نہیں پڑھی ہے آکر تمہارے ساتھ نماز پڑھے اور وہ بھی چوکنا رہے اور اپنے اسلحہ لئے رہے ‘ کیونکہ کفار اس تاک میں ہیں کہ تم اپنے ہتھیاروں اور اپنے سامان کی طرف سے ذرا غافل ہو تو وہ تم پر یکبارگی ٹوٹ پڑیں ۔ البتہ اگر تم بارش کی وجہ سے تکلیف محسوس کرو یا بیمار ہو تو اسلحہ رکھ دینے میں مضائقہ نہیں ‘ مگر پھر بھی چوکنے رہو یقین رکھو کہ اللہ نے کافروں کیلئے رسوا کن عذاب مہیا کر رکھا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,103,'4:103','پھر جب نماز سے فارغ ہوجاؤ تو کھڑے اور بیٹھے اور لیٹے ہر حال میں اللہ کو یاد کرتے رہو اور جب اطمینان نصیب ہوجائے تو پوری نماز پڑھو ۔ نماز درحقیقت ایسا فرض ہے جو پابندی وقت کے ساتھ ایمان پر لازم کیا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,104,'4:104','(اس گروہ کے تعاقب میں کمزوری نہ دکھاؤ اگر تم تکلیف اٹھا رہے ہو تو تمہاری طرح وہ بھی تکلیف اٹھا رہے ہیں اور تم اللہ سے اس چیز کے امیدوار ہو جس کے وہ امیدوار نہیں ہیں ‘ اللہ سب کچھ جانتا ہے اور وہ حکیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,105,'4:105','(اے نبی صلى الله عليه وسلم ہم نے یہ کتاب حق کے ساتھ تمہاری طرف نازل کی ہے ‘ تاکہ جو راہ راست اللہ نے تمہیں دکھائی ہے اس کے مطابق لوگوں کے درمیان فیصلہ کرو ‘ تم بدیانت لوگوں کی طرف سے جھگڑنے والے نہ بنو ‘ ۔');
INSERT INTO "sayyid_qutb" VALUES(4,106,'4:106','اور اللہ سے درگزر کی درخواست کرو ‘ وہ بڑا درگزر فرمانے والا اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(4,107,'4:107','جو لوگ اپنے نفس سے خیانت کرتے ہی تم ان کی حمایت نہ کرو ‘ اللہ کو ایسا شخص پسند نہیں ہے جو خیانت کار اور معصیت پیشہ ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(4,108,'4:108','یہ لوگ انسانوں سے اپنی حرکات چھپا سکتے ہیں مگر خدا سے نہیں چھپا سکتے ‘ وہ تو اس وقت بھی ان کے ساتھ ہوتا ہے جب یہ راتوں کو چھپ کر اس کی مرضی کے خلاف مشورے کرتے ہیں ‘ ان کے سارے اعمال پر اللہ محیط ہے');
INSERT INTO "sayyid_qutb" VALUES(4,109,'4:109','ہاں تم لوگوں نے ان مجرموں کی طرف سے دنیا کی زندگی میں تو جھگڑا کرلیا ‘ مگر قیامت کے روز ان کے لئے اللہ سے کون جھگڑا کرے گا ؟ آخر وہاں کون ان کا وکیل ہوگا ؟');
INSERT INTO "sayyid_qutb" VALUES(4,110,'4:110','(اگر کوئی شخص برا فعل کر گزرے یا اپنے نفس پر ظلم کر جائے اور اس کے بعد اللہ سے درگزر کی درخواست کرے تو اللہ کو درگزر کرنے والا اور رحیم پائے گا');
INSERT INTO "sayyid_qutb" VALUES(4,111,'4:111','مگر جو برائی کمائے تو اس کی یہ کمائی اسی کے لئے وبال ہوگی ‘ اللہ کو سب باتوں کی خبر ہے اور وہ حکیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,112,'4:112','پھر جس نے کوئی خطا یا گناہ کر کے اس کا الزام کسی بےگناہ پر تھوپ دیا اس نے تو بڑے بہتان اور صریح گناہ کا بار سمیٹ لیا۔');
INSERT INTO "sayyid_qutb" VALUES(4,113,'4:113','(اے نبی صلى الله عليه وسلم اگر اللہ کا فضل تم پر نہ ہوتا اور اس کی رحمت تمہارے شامل حال نہ ہوتی تو ان میں سے ایک گروہ نے تو تمہیں غلط فہمی میں مبتلا کرنے کا فیصلہ کر ہی لیا تھا “ حالانکہ درحقیقت وہ خود اپنے سوا کسی کو غلط فہمی میں مبتلا نہیں کر رہے تھے ۔ اور تمہارے کوئی نقصان نہ کرسکتے تھے ۔ اللہ نے تم پر کتاب اور حکمت نازل کی ہے اور تم کو وہ کچھ بتایا ہے جو تمہیں معلوم نہ تھا اور اس کا فضل تم پر بہت ہے');
INSERT INTO "sayyid_qutb" VALUES(4,114,'4:114','(لوگوں کی خفیہ سرگوشیوں میں اکثر وبیشتر کوئی بھلائی نہیں ہوتی ‘ ہاں اگر کوئی پوشیدہ طور پر صدقہ و خیرات کی تلقین کرے یا کسی نیک کام کے لئے یا لوگوں کے معاملات میں اصلاح کرنے کے لئے کسی سے کچھ کہے تو یہ البتہ بھلی بات ہے ‘ اور جو کوئی اللہ کی رضا جوئی کے لئے ایسا کرے اسے ہم بڑا اجر عطا کریں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,115,'4:115','( ” مگر جو شخص رسول کی مخالفت پر کمر بستہ ہو اور اہل ایمان کی روش کے سوا کسی اور روشن پر چلے ‘ درآنحالیکہ اس پر راہ راست واضح ہوچکی ہو تو اس کو ہم اس طرف چلائیں گے جدھر خود وہ پھر گیا اور اسے جہنم میں جھونکیں گے جو بدترین جائے قرار ہے');
INSERT INTO "sayyid_qutb" VALUES(4,116,'4:116','اللہ کے ہاں بس شرک کی بخشش نہیں ہے ۔ اس کے سوا اور سب کچھ معاف ہو سکتا ہے جسے وہ معاف کرنا چاہئے جس نے اللہ کے ساتھ کسی کو شریک ٹھہرایا وہ تو گمراہی میں بہت دور نکل گیا');
INSERT INTO "sayyid_qutb" VALUES(4,117,'4:117','(وہ اللہ کو چھوڑ کر دیویوں کو معبود بناتے ہیں ۔ وہ اس باغی شیطان کو معبود بناتے ہیں جس کو اللہ نے لعنت زدہ کیا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,118,'4:118','وہ اس شیطان کی اطاعت کر رہے ہیں) جس نے اللہ سے کہا تھا کہ ” میں تیرے بندوں سے ایک مقرر حصہ لے کر رہوں گا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,119,'4:119','میں انہیں بہکاؤں گا ‘ میں انہیں آرزوؤں میں الجھاؤں گا ‘ میں انہیں حکم دوں گا اور وہ میرے حکم سے جانوروں کے کان پھاڑیں گے اور میں انہیں حکم دوں گا اور وہ میرے حکم سے خدائی ساخت میں ردوبدل کریں گے ۔ اس شیطان کو جس نے اللہ کے بجائے اپنا ولی و سرپرست بنا لیا وہ صریح نقصان میں پڑ گیا');
INSERT INTO "sayyid_qutb" VALUES(4,120,'4:120','وہ ان لوگوں سے وعدے کرتا ہے اور انہیں امیدیں دلاتا ہے ‘ مگر شیطان کے سارے وعدے بجز فریب کے اور کچھ نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,121,'4:121','(ان لوگوں کا ٹھکانا جہنم ہے جس سے خلاصی کی کوئی صورت یہ نہ پائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,122,'4:122','رہے وہ لوگ جو ایمان لے آئیں اور نیک عمل کریں ‘ تو انہیں ہم ایسے باغوں میں داخل کریں گے جس کے نیچے نہریں بہتی ہوں گی اور وہ وہاں ہمیشہ ہمیشہ رہیں گے ۔ یہ اللہ کا سچا وعدہ ہے اور اللہ سے بڑھ کر کون اپنی بات میں سچا ہوگا');
INSERT INTO "sayyid_qutb" VALUES(4,123,'4:123','(انجام کار نہ تمہاری آرزوؤں پر موقوف ہے نہ اہل کتاب کی آرزوؤں پر ‘ جو بھی برائی کرے گا اس کا پھل پائے گا اور اس کے میں اپنے لئے کوئی حامی و مددگار نہ پاسکے گا');
INSERT INTO "sayyid_qutb" VALUES(4,124,'4:124','اور جو نیک عمل کرے گا ‘ خواہ مرد ہو یا عورت ‘ بشرطیکہ ہو وہ مومن ‘ تو ایسے ہی لوگ جنت میں داخل ہوں گے اور ان کی ذرہ برابر حق تلفی نہ ہونے پائے گی');
INSERT INTO "sayyid_qutb" VALUES(4,125,'4:125','اس شخص سے بہتر اور کس کا طریق زندگی ہو سکتا ہے جس نے اللہ کے آگے سرتسلیم خم کردیا اور اپنا رویہ نیک رکھا اور یکسو ہوکر ابراہیم (علیہ السلام) کے طریقے کی پیروی کی)');
INSERT INTO "sayyid_qutb" VALUES(4,126,'4:126','آسمانوں اور زمین میں جو کچھ ہے اللہ کا ہے اور اللہ ہر چیز پر محیط ہے)');
INSERT INTO "sayyid_qutb" VALUES(4,127,'4:127','(لوگ تم سے عورتوں کے معاملہ میں فتوی پوچھتے ہیں ۔ کہو اللہ تعالیٰ تمہیں ان کے معاملے میں فتوی دیتا ہے ‘ اور ساتھ ہی وہ احکام بھی یاد دلاتا ہے جو پہلے سے تم کو اس کتاب میں سنائے جارہے ہیں ۔ یعنی وہ احکام جو ان یتیم لڑکیوں کے متعلق ہیں جن کے حق تم ادا نہیں کرتے اور جن کے نکاح کرنے سے تم باز رہتے ہو (یا لالچ کی بنا پر تم خود ان سے نکاح کرلینا چاہتے ہو) اور وہ احکام جو ان بچوں کے متعلق ہیں جو بیچارے کوئی زور نہیں رکھتے ۔ اللہ تمہیں ہدایت کرتا ہے کہ یتیموں کے ساتھ انصاف پر قائم رہو ‘ اور جو بھلائی تم کرو گے وہ اللہ کے علم سے چھپی نہ رہ جائے گی)');
INSERT INTO "sayyid_qutb" VALUES(4,128,'4:128','(اگر کسی عورت کو اپنے شوہر سے بدسلوکی یا بےرخی کا خطرہ ہو تو کوئی مضائقہ نہیں کہ میاں اور بیوی (کچھ حقوق کی کمی بیشی پر) آپس میں صلح کرلیں۔ صلح بہرحال بہتر ہے ۔ نفس تنگ دلی کی طرف جلدی مائل ہوجاتے ہیں ‘ لیکن اگر تم لوگ احسان سے پیش آؤ اور خدا ترسی سے کام لو تو یقین رکھو کہ اللہ تمہارے اس طرز عمل سے بیخبر نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(4,129,'4:129','بیویوں کے درمیان پورا پورا عدل کرنا تمہارے بس میں نہیں ہے ۔ تم چاہو بھی تو اس پر قادر نہیں ہو سکتے ۔ لہذا (قانون الہی کا منشا پورا کرنیکے لئے یہ کافی ہے کہ) ایک بیوی کی طرف اس طرح نہ جھک جاؤ کہ دوسری کو ادھر لٹکتا چھوڑ دو ‘ اگر تم اپنا طرز عمل درست رکھو اور اللہ سے ڈرتے رہو تو اللہ چشم پوشی کرنے والا اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,130,'4:130','لیکن اگر زوجین ایک دوسرے سے الگ ہی ہوجائیں تو اللہ اپنی وسیع قدرت سے ہر ایک کو دوسرے کی محتاج سے بےنیاز کر دے گا ۔ اللہ کا دامن بہت کشادہ ہے اور وہ دانا وبینا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,131,'4:131','آسمان اور زمین میں جو کچھ ہے سب اللہ ہی کا ہے ۔ تم سے پہلے جن کو ہم نے کتاب دی تھی انہیں بھی یہی ہدایت کی تھی اور اب مت کو بھی یہی ہدایت کرتے ہیں کہ خدا سے ڈرتے ہوئے کام کرو لیکن اگر تم نہیں مانتے تو نہ مانو آسمان و زمین کی ساری چیزوں کا مالک اللہ ہی ہے اور وہ بےنیاز ہے ‘ ہر تعریف کا مستحق');
INSERT INTO "sayyid_qutb" VALUES(4,132,'4:132','اللہ ہی مالک ہے ان سب چیزوں کا جو آسمانوں میں ہیں اور جو زمین میں ہیں اور کارسازی کے لئے بس وہی کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(4,133,'4:133','اگر وہ چاہے تو تم لوگوں کو ہٹا کر تمہاری جگہ دوسروں کو لے آئے اور وہ اس کی پوری قدرت رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,134,'4:134','جو شخص محض ثواب دنیا کا طالب ہو اسے معلوم ہونا چاہئے کہ اللہ کے پاس ثواب دنیا بھی ہے اور ثواب آخرت بھی اور اللہ سمیع وبصیر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,135,'4:135','(اے لوگو ! جو ایمان لائے ہو انصاف کے علمبردار اور اللہ کے لئے گواہی دینے والے بنو اگرچہ تمہارے انصاف اور تمہاری گواہی کی زد خود تمہاری اپنی ذات پر یا تمہارے والدین اور رشتہ داروں پر ہی کیوں نہ پڑتی ہو ۔ فریق معاملہ خواہ مالدار ہو یا غریب اللہ تم سے زیادہ ان کا خیر خواہ ہے لہذا اپنی خواہش نفس کی پیروی میں عدل سے باز نہ رہو ‘ اور اگر تم نے لگی لپٹی بات کہی یا سچائی سے پہلو بچایا تو جان رکھو کہ جو کچھ تم کرتے ہو اللہ کو اس کی خبر ہے');
INSERT INTO "sayyid_qutb" VALUES(4,136,'4:136','اے لوگو جو ایمان لائے ہو ‘ ایمان لاؤ اللہ پر اور اس کے رسول پر اور اس کتاب پر جو اللہ نے اپنے رسول پر نازل کی ہے اور اس کتاب پر جو اس سے پہلے وہ نازل کرچکا ہے ۔ جس نے اللہ اور اس کے ملائکہ اور اس کی کتابوں اور اس کے رسولوں اور روز آخرت سے کفر کیا اور گمراہی میں بھٹک کر بہت دور نکل گیا');
INSERT INTO "sayyid_qutb" VALUES(4,137,'4:137','(رہے وہ لوگ جو ایمان لائے ‘ پھر کفر کیا ‘ پھر ایمان لائے ‘ پھر کفر کیا ‘ پھر اپنے کفر میں بڑھتے چلے گئے تو اللہ ہر گز ان کو معاف نہ کرے گا اور نہ کبھی ان کو راہ راست دکھائے گا');
INSERT INTO "sayyid_qutb" VALUES(4,138,'4:138','(جو منافق اہل ایمان کو چھوڑ کر کافروں کو اپنا رفق بناتے ہیں ‘ انہیں یہ مژدہ سنا دو کہ ان کے لئے دردناک سزا تیار ہے');
INSERT INTO "sayyid_qutb" VALUES(4,139,'4:139','کیا یہ لوگ عزت کی طلب میں ان کے پاس جاتے ہیں ؟ حالانکہ عزت تو ساری کی ساری اللہ ہی کے لئے ہے');
INSERT INTO "sayyid_qutb" VALUES(4,140,'4:140','اللہ اس کتاب میں تم کو پہلے ہی حکم دے چکا ہے کہ جہاں تم سنو کہ اللہ کی آیات کے خلاف کفر بکا جا رہا ہے اور ان کا مذاق اڑایا جا رہا ہے وہاں نہ بیٹھو جب تک کہ لوگ کسی دوسری بات میں نہ لگ جائیں ۔ اب اگر تم ایسا کرتے ہو تو تم بھی انہی کی طرح ہو ۔ یقین جانو کہ اللہ منافقوں اور کافروں کی جہنم میں ایک جگہ جمع کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,141,'4:141','یہ منافق تمہارے معاملے میں انتظار کر رہے ہیں (کہ اونٹ کس کروٹ بیٹھتا ہے) اگر اللہ کی طرف سے فتح تمہاری ہوئی تو آکر کہیں گے کیا ہم تمہارے ساتھ نہ تھے ؟ اگر کافروں کا پلہ بھاری رہا تو ان سے کہیں گے کہ کیا ہم تمہارے خلاف لڑنے پر قادر نہ تھے اور پھر بھی ہم نے تم کو مسلمانوں سے بچایا ؟ بس اللہ ہی تمہارے اور انکے معاملے کا فیصلہ قیامت کے روز کرے گا اور (اس فیصلے میں) اللہ نے کافروں کے لئے مسلمانوں پر غالب آنے کی ہر گز کوئی سبیل نہیں رکھی ۔');
INSERT INTO "sayyid_qutb" VALUES(4,142,'4:142','یہ منافق اللہ کے ساتھ دھوکہ بازی کر رہے ہیں حالانکہ درحقیقت اللہ ہی نے انہیں دھوکہ میں ڈال رکھا ہے ۔ جب یہ نماز کے لئے اٹھتے ہیں تو کسمساتے ہوئے محض لوگوں کو دکھانے کی خاطر اٹھتے ہیں اور خدا کو کم ہی یاد کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(4,143,'4:143','کفر و ایمان کے درمیان ڈانوا ڈول ہیں ۔ نہ پورے اس طرف ہیں نہ پورے اس طرف ۔ جسے اللہ نے بھٹکا دیا ہو اس کے لئے تم کوئی راستہ نہیں پا سکتے۔');
INSERT INTO "sayyid_qutb" VALUES(4,144,'4:144','(اے لوگو ! جو ایمان لائے ہو ‘ مومنوں کو چھوڑ کر کافروں کو اپنا رفیق نہ بناؤ۔ کیا تم چاہتے ہو کہ اللہ کو اپنے خلاف صریح حجت دے دو ؟');
INSERT INTO "sayyid_qutb" VALUES(4,145,'4:145','یقین جانو کہ منافق جہنم کے سب سے نچلے طبقے میں جائیں گے اور تم کسی کو ان کا مددگار نہ پاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(4,146,'4:146','البتہ جو ان میں سے تائب ہوجائیں اور اپنے طرز عمل کی اصلاح کرلیں اور اللہ کا دامن تھام لیں اور اپنے دین کو اللہ کے لئے خالص کردیں ‘ ایسے لوگ مومنوں کے ساتھ ہیں اور اللہ مومنوں کو ضرور اجر عظیم عطا فرمائے گا');
INSERT INTO "sayyid_qutb" VALUES(4,147,'4:147','آخر اللہ کو کیا پڑی ہے کہ تمہیں خواہ مخواہ سزا دے ۔ اگر تم شکر گزار بندے بنے رہو اور ایمان کی روشن پر چلو ۔ اللہ بڑا قدر دان ہے اور سب کے حال سے واقف ہے');
INSERT INTO "sayyid_qutb" VALUES(4,148,'4:148','(اللہ اس کو پسند نہیں کرتا کہ آدمی بدگوئی پر زبان کھولے ‘ الا یہ کہ کسی پر ظلم کیا گیا ہو اور اللہ سب کچھ سننے اور جاننے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,149,'4:149','مظلوم ہونے کی صورت میں اگرچہ تم کو بدگوئی کا حق ہے) لیکن اگر تم ظاہر و باطن میں بھلائی ہی کئے جاؤ ‘ یا کم از کم برائی سے درگزرکو ‘ تو اللہ (کی صفت بھی یہی ہے کہ وہ بڑا معاف کرنیوالا ہے حالانکہ سزا دینے پر) پوری قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(4,150,'4:150','جو لوگ اللہ اور اس کے رسولوں سے کفر کرتے ہیں اور چاہتے ہیں کہ اللہ اور اس کے رسولوں کے درمیان فرق کردیں ‘ اور کہتے ہیں کہ ہم کسی کو مانیں گے اور کسی کو نہ مانیں گے اور کفر و ایمان کے پیچ میں ایک راہ نکالنے کا ارادہ رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,151,'4:151','وہ سب پکے کافر ہیں اور ایسے کفروں کیلئے ہم نے وہ سزا مہیا کر رکھی ہے جو انہیں ذلیل و خوار کردینے والی ہوگی ۔');
INSERT INTO "sayyid_qutb" VALUES(4,152,'4:152','بخلاف اس کے جو لوگ اللہ اور اس کے تمام رسولوں کو مانیں اور ان کے درمیان تفریق نہ کریں ‘ ان کو ہم ضرور ان کے اجر عطا کریں گے اور اللہ بڑا درگزر فرمانے والا اور رحم کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(4,153,'4:153','اے نبی ! یہ اہل کتاب اگر آج تم سے مطالبہ کر رہے ہیں کہ تم آسمان سے کوئی تحریر ان پر نازل کراؤ تو اس سے بڑھ چڑھ کر مجرمانہ مطالبے یہ پہلے موسیٰ سے کرچکے ۔ اس سے تو انہوں نے کہا تھا کہ ہمیں خدا کو علانیہ دکھا دو اور اسی سرکشی کی وجہ سے یکایک ان پر بجلی ٹوٹ پڑی تھی ۔ پھر انہوں نے بچھڑے کو اپنا معبود بنا لیا حالانکہ یہ کھلی کھلی نشانیاں دیکھ چکے تھے ۔ اس پر بھی ہم نے ان سے درگزر کیا ۔ ہم نے موسیٰ (علیہ السلام) کو صریح فرمان عطا کیا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,154,'4:154','اور ان لوگوں پر طور کو اٹھا کر ان سے اس فرمان کی اطاعت کا عہد لیا ۔ ہم نے ان کو حکم دیا کہ دروازے میں سجدہ ریز ہوتے ہوئے داخل ہو۔');
INSERT INTO "sayyid_qutb" VALUES(4,155,'4:155','ہم نے ان سے کہا کہ اللہ کا قانون نہ توڑو اور اس پر ان سے پختہ عہد لیا ۔ آخر کار ان کی عہد شکنی کی وجہ سے اور اس وجہ سے کہ انہوں نے اللہ کی آیات کو جھٹلایا اور متعدد پیغمبروں کو ناحق قتل کیا ‘ اور یہاں تک کہا کہ ہمارے دل غلافوں میں محفوظ ہیں حالانکہ درحقیقت انکی باطل پرستی کے سبب سے اللہ نے ان کے دلوں پر ٹھپہ لگا دیا ہے اور اسی وجہ سے یہ بہت کم ایمان لاتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(4,156,'4:156','پھر اپنے کفر میں یہ اتنے بڑھے کہ مریم پر سخت بہتان لگایا');
INSERT INTO "sayyid_qutb" VALUES(4,157,'4:157','اور خود کہا کہ ہم نے مسیح ‘ عیسیٰ (علیہ السلام) ابن مریم ‘ رسول اللہ کو قتل کردیا ہے حالانکہ فی الواقع انہوں نے نہ اس کو قتل کیا نہ صلیب پرچڑھایا بلکہ معاملہ ان کیلئے مشتبہ کردیا گیا اور جن لوگوں نے اس بارے میں اختلاف کیا ہے وہ بھی دراصل شک میں مبتلا ہیں ۔ ان کے پاس اس معاملے میں کوئی علم نہیں ہے ۔ محض گمان ہی کی پیروی ہے ۔ انہوں نے مسیح کو یقینا قتل نہیں کیا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,158,'4:158','بلکہ اللہ نے اس کو اپنی طرف اٹھالیا ‘ اللہ زبردست طاقت رکھنے والا اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(4,159,'4:159','اور اہل کتاب میں سے کوئی ایسا نہ ہوگا جو اس کی موت سے پہلے اس پر ایمان نہ لے آئے گا اور قیامت کے روز وہ ان پر گواہی دے گا');
INSERT INTO "sayyid_qutb" VALUES(4,160,'4:160','غرض ان یہودیوں کے اسی ظالمانہ رویے کی بناء پر اور اس بناء پر کہ یہ بکثرت اللہ کے راستے سے روکتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(4,161,'4:161','اور سود لیتے ہیں جس سے انہیں منع کیا گیا تھا اور لوگوں کے مال ناجائز طریقوں سے کھاتے ہیں ‘ ہم نے بہت سی وہ پاک چیزیں ان پر حرام کردیں جو پہلے ان کیلئے حلال تھیں ‘ اور وہ لوگ جو ان میں سے کافر ہیں ان کیلئے ہم نے دردناک عذاب تیار کر رکھا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,162,'4:162','مگر ان میں جو لوگ پختہ علم رکھنے والے ہیں اور ایماندار ہیں وہ سب اس تعلیم پر ایمان لاتے ہیں جو اے نبی ‘ تمہاری طرف نازل کی گئی ہے اور جو تم سے پہلے نازل کی گئی تھی ۔ اس طرح کے ایمان لانے والے اور نماز و زکوۃ کی پابندی کرنے والے اور اللہ اور روز آخرت پر سچا عقیدہ رکھنے والے لوگوں کو ہم ضرور اجر عظیم عطا کریں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,163,'4:163','اے نبی ! ہم نے تمہاری طرف اسی طرح وحی بھیجی جس طرح نوح (علیہ السلام) اور اس کے بعد کے پیغمبروں کی طرف بھیجی تھی ۔ ہم نے ابراہیم (علیہ السلام) ‘ اسماعیل (علیہ السلام) ‘ اسحاق (علیہ السلام) ‘ یعقوب (علیہ السلام) اور اولاد یعقوب (علیہ السلام) ‘ عیسیٰ (علیہ السلام) ‘ ایوب (علیہ السلام) یونس (علیہ السلام) ‘ ہارون (علیہ السلام) اور سلیمان (علیہ السلام) کی طرف وحی بھیجی ۔ ہم نے داؤد (علیہ السلام) کو زبور دی');
INSERT INTO "sayyid_qutb" VALUES(4,164,'4:164','ہم نے ان رسولوں پر بھی وحی نازل کی جن کا ذکر ہم اس سے پہلے تم سے کرچکے ہیں اور ان رسولوں پر بھی جن کا ذکرہم اس سے پہلے تم سے کرچکے ہیں اور ان رسولوں پر بھی جن کا ذکر تم سے نہیں کیا ۔ ہم نے موسیٰ (علیہ السلام) سے اس طرح گفتگو کی جس طرح گفتگو کی جاتی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,165,'4:165','یہ سارے رسول خوش خبری دینے والے اور ڈرانے والے بنا کر بھیجے گئے تاکہ ان کو مبعوث کردینے کے بعد لوگوں کے پاس اللہ کے مقابلے میں کوئی حجت نہ رہے اور اللہ بہرحال غالب رہنے والا اور حکیم ودانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,166,'4:166','لوگ نہیں مانتے تو نہ مانیں مگر اللہ گواہی دیتا ہے کہ اے نبی صلى الله عليه وسلم جو کچھ اس نے تم پر نازل کیا ہے اپنے علم سے نازل کیا ہے اور اس پر ملائکہ بھی گواہ ہیں ‘ اگرچہ اللہ کا گواہ ہونا بالکل کفایت کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(4,167,'4:167','جو لوگ ان کو ماننے سے خود انکار کرتے ہیں اور دوسروں کو خدا کے راستے سے روکتے ہیں وہ یقینا گمراہی میں حق سے بہت دور نکل گئے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(4,168,'4:168','اسی طرح جن لوگوں نے کفر وبغاوت کا طریقہ اختیار کیا اور ظلم وستم پر اتر آئے اللہ انکو ہر گز معاف نہ کرے گا اور انہیں کوئی راستہ ۔');
INSERT INTO "sayyid_qutb" VALUES(4,169,'4:169','بجز جہنم کے راستہ کے نہ دکھائے گا جس میں وہ ہمیشہ رہیں گے ۔ اللہ کیلئے یہ کوئی مشکل کام نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,170,'4:170','لوگو ! یہ رسول تمہارے پاس تمہارے رب کی طرف سے حق لے کر آگیا ہے ‘ ایمان لے آؤ تمہارے ہی لئے بہتر ہے اور اگر انکار کرتے ہو تو جان لو کہ آسمانوں اور زمین میں جو کچھ ہے سب اللہ کا ہے اور اللہ علیم بھی ہے اور حکیم بھی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,171,'4:171','اے اہل کتاب ‘ اپنے دین میں غلو نہ کرو۔ اور اللہ کی طرف حق کے سواء کوئی بات منسوب نہ کرو مسیح عیسیٰ (علیہ السلام) ابن مریم اس کے سوا کچھ نہ تھا کہ اللہ کا ایک فرمان تھا جو اللہ نے مریم کی طرف بھیجا اور ایک روح تھی اللہ کی طرف سے (جس نے مریم کے رحم میں بچہ کی شکل اختیار کی) پس تم اللہ اور اس کے رسولوں پر ایمان لاؤ اور نہ کہو کہ تین ہیں ۔ باز آجاؤ ‘ یہ تمہارے لئے بہتر ہے ۔ اللہ تو بس ایک ہی خدا ہے ۔ وہ پاک ہے اس سے کہ کوئی اس کا بیٹا ہو ۔ زمین و آسمان کی ساری چیزین اس کی ملک ہیں ‘ اور ان کی کفالت وخبرگیری کیلئے بس وہی کافی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(4,172,'4:172','مسیح نے کبھی اس بات کو عار نہیں سمجھا کہ وہ اللہ کا بندہ ہو اور نہ مقرب ترین فرشتے اس کو اپنے لئے عار سمجھتے ہیں ، اگر کوئی اللہ کی بندگی کو اپنے لئے عار سمجھتا ہے اور تکبر کرتا ہے تو ایک وقت آئے گا جب اللہ سب کو گھیر کر اپنے سامنے حاضر کرے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,173,'4:173','اس وقت وہ لوگ جنہوں نے ایمان لا کر نیک طرز عمل اختیار کیا ہے اپنے اجر پورے پورے پائیں گے اور اللہ اپنے فضل سے ان کو مزید اجر عطا فرمائے گا ۔ اور جن لوگوں نے بندگی کو عار سمجھا اور تکبر کیا ہے ان کو اللہ دردناک سزا دے گا اور اللہ کے سوا جن جن کی سرپرستی ومددگاری پر وہ بھروسہ رکھتے ہیں ‘ ان میں سے کسی کو بھی وہ وہاں نہ پائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,174,'4:174','لوگو ! تمہارے رب کی طرف سے تمہارے پاس دلیل روشن آگئی ہے اور ہم نے تمہاری طرف ایسی روشنی بھیج دی ہے جو تمہیں صاف صاف راستہ دکھانے والی ہے ۔ اس قرآن کریم میں اللہ رب العالمین کیلئے برہان ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(4,175,'4:175','جو لوگ بات مان لیں گے اور رب کی پناہ ڈھونڈیں گے انکو اللہ اپنی رحمت اور اپنے فضل وکرم کے دامن میں لے لیگا اور اپنی طرف آنے کا سیدھا راستہ ان کر دکھا دے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(4,176,'4:176','لوگ تم سے کلالہ کے معاملے میں فتوی پوچھتے ہیں ‘ کہو اللہ تمہیں فتوی دیتا ہے ‘ اگر کوئی شخص بےاولاد مر جائے اور اس کی ایک بہن ہو تو وہ اس کے ترکہ میں سے نصف پائے گی اور اگر بہن بےاولاد مر جائے تو بھائی اس کا وارث ہوگا ۔ اگر میت کی وارث دو بہنیں ہوں تو وہ ترکے میں سے دو تہائی کی حقدار ہوں گی اور اگر بھائی اور بہنیں ہوں تو عورتوں کا اکہرا اور مردوں کا دوہرا حصہ ہوگا ۔ اللہ تمہارے لئے احکام کی توضیح کرتا ہے تاکہ تم بھٹکتے نہ پھرو اور اللہ ہر چیز کا علم رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,1,'5:1','اے لوگو جو ایمان لائے ہو ‘ بندشوں کی پوری پابندی کرو۔ تمہارے لئے مویشی کی قسم کے سب جانور حلال کئے گئے سوائے ان کے جو آگے چل کر تم کو بتائے جائیں گے لیکن احرام کی حالت میں شکار کو اپنے لئے حلال نہ کرلو ‘ بیشک اللہ تعالیٰ جو چاہتا ہے حکم دیتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,2,'5:2','اے لوگو جو ایمان لائے ہو ‘ خدا پرستی کی نشانیوں کو بےحرمت نہ کرو ، ۔۔۔۔۔ حرام مہینوں میں کسی کو حلال نہ کرلو ‘ قربانی کے جانوروں پر دست درازی نہ کرو ‘ ان جانوروں پر ہاتھ نہ ڈالو جن کی گردنوں میں نذر خداوندی کی علامت کے طور پر پٹے پڑے ہوئے ہوں اور نہ انکو چھیڑو جو اپنے رب کے فضل اور خوشنودی کی تلاش میں مکان محترم (کعبہ) کی طرف جارہے ہوں ۔ ہاں جب احرام کی حالت ختم ہوجائے تو شکار تم کرسکتے ہو ۔۔۔۔۔۔ اور دیکھو ‘ ایک گروہ نے جو تمہارے لئے مسجد حرام کا راستہ بند کردیا ہے تو اس پر تمہارا حصہ تمہیں اتنا مشتعل نہ کر دے کہ تم بھی ان کے مقابلے میں ناروا زیادتیاں کرنے لگو۔ نہیں ‘ جو کام نیکی اور خدا ترسی کے ہیں ان میں سب سے تعاون کرو اور جو گناہ اور زیادتی کے کام میں ناروا زیادتیاں کرنے لگو۔ نہیں ‘ جو کام نیکی اور خدا ترسی کے ہیں ان میں سب سے تعاون کرو اور جو گناہ اور زیادتی کے کام ہیں ان میں کسی سے تعاون نہ کرو ۔ اللہ سے ڈرو ‘ اس کی سزا بہت سخت ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,3,'5:3','تم پر حرام کیا گیا مردار ‘ خون ‘ سور کا گوشت ‘ وہ جانور جو خدا کے سوا کسی اور کے نام پر ذبح کیا گیا ہو ‘ وہ جو گلا گھٹ کر یا چوٹ کھا کر ‘ یا بلندی سے گر کر ‘ یا ٹکر کھا کر مرا ہو ‘ یا جسے کسی درندے نے پھاڑا ہو ؛۔۔۔ سوائے اس کے جسے تم نے زندہ پاکر ذبح کرلیا اور وہ جو کسی آستانے پر ذبح کیا گیا ۔ نیز یہ بھی تمہارے لئے ناجائز ہے کہ پانسوں کے ذریعے اپنی قسمت معلوم کرو ۔ یہ سب افعال فسق ہیں ۔ آج کافروں کو تمہارے دین کی طرف سے پوری مایوسی ہوچکی ہے لہذا تم ان سے نہ ڈرو بلکہ مجھ سے ڈرو ۔ آج میں نے تمہارے دین کو تمہارے لئے مکمل کردیا ہے اور اپنی نعمت تم پر تمام کردی ہے اور تمہارے لئے اسلام کو تمہارے دین کی حیثیت سے قبول کرلیا ہے ۔ (لہذا حرام و حلال کی جو قیود تم پر عائد کی گئی ہیں ان کی پابندی کرو) البتہ جو شخص بھوک سے مجبور ہو کر ان میں سے کوئی چیز کھالے ‘ بغیر اس کے کہ گناہ کی طرف اس کا میلان ہو تو بیشک اللہ معاف کرنے والا اور رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(5,4,'5:4','لوگ پوچھتے ہیں کہ ان کے لئے کیا حلال کیا گیا ہے ‘ کہو تمہارے لئے ساری پاک چیزیں حلال کردی گئی ہیں اور جن شکاری جانوروں کو تم نے سدھایا ہو ۔۔۔۔۔ جن کو خدا کے دیئے ہوئے علم کی بنا پر تم شکار کی تعلیم دیا کرتے ہو ۔۔۔۔۔۔ وہ جس جانور کو تمہارے لئے پکڑ رکھیں اس کو بھی تم کھاسکتے ہو البتہ اس پر اللہ کا نام لے لو اور اللہ کا قانون توڑنے سے ڈرو اللہ کو حساب لیتے دیر نہیں لگتی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,5,'5:5','آج تمہارے لئے ساری پاک چیزیں حلال کردی گئی ہیں ، اہل کتاب کا کھانا تمہارے لئے حلال ہے اور تمہارا کھانا ان کے لئے ۔ اور محفوظ عورتیں بھی تمہارے لئے حلال ہیں خواہ وہ اہل ایمان کے گروہ سے ہوں یا ان قوموں میں سے جن کو تم سے پہلے کتاب دی گئی تھی ‘ بشرطیکہ تم انکے مہر ادا کر کے نکاح میں ان کے محافظ بنو ‘ نہ یہ کہ آزاد شہوت رانی کرنے لگو یا چوری چھپے آشنائیاں کرو اور جو کسی نے ایمان کی روش پر چلنے سے انکار کیا تو اس کا سارا کارنامہ زندگی ضائع ہوجائے گا اور وہ آخرت میں دلوالیہ ہو گا');
INSERT INTO "sayyid_qutb" VALUES(5,6,'5:6','” اے لوگو جو ایمان لائے ہو ‘ جب تم نماز کے لئے اٹھو تو چاہئے کہ اپنے منہ اور ہاتھ کہنیوں تک دھولو سروں پر ہاتھ پھیر لو اور پاؤں ٹخنوں تک دھو لیا کرو۔ اگر جنابت کی حالت میں ہو تو نہا کر پاک ہوجاؤ ۔ اگر بیمار ہو یا سفر کی حالت میں ہو یا تم میں سے کوئی شخص رفع حاجت کر کے آئے یا تم نے عورتوں کو ہاتھ لگایا ہو ‘ اور پانی نہ ملے تو پاک مٹی سے کام لو ‘ بس اس پر ہاتھ مار کر اپنے منہ اور ہاتھوں پر پھیرلیا کرو ، اللہ تم پر زندگی کو تنگ نہیں کرنا چاہتا مگر وہ چاہتا ہے کہ تمہیں پاک کرے اور اپنی نعمت تم پر تمام کر دے ‘ شاید کہ تم شکر گزار بنو)');
INSERT INTO "sayyid_qutb" VALUES(5,7,'5:7','اللہ نے تم کو جو نعمت عطا کی ہے اس کا خیال رکھو اور اس پختہ عہد و پیمان کو نہ بھولو جو اس نے تم سے لیا ہے یعنی تمہارا یہ قول کہ ” ہم نے سنا اور اطاعت قبول کی “ اللہ سے ڈرو ‘ اللہ دلوں کے راز تک جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(5,8,'5:8','اے لوگو جو ایمان لائے ہوں اللہ کی خاطر راستی پر قائم رہنے والے اور انصاف کی گواہی دینے والے بنو کسی گروہ کی دشمنی تم کو اتنا مشتعل نہ کر دے کہ انصاف سے پھر جاؤ ۔ عدل کرو ‘ یہ خدا ترسی سے زیادہ مناسبت رکھتا ہے ۔ اللہ سے ڈر کر کام کرتے رہو ۔ جو کچھ تم کرتے ہو اللہ اس سے پوری طرح باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(5,9,'5:9','جو لوگ ایمان لائیں اور نیک عمل کریں ‘ اللہ نے ان سے وعدہ کیا ہے کہ ان کی خطاؤں سے درگزر کیا جائے گا اور انہیں بڑا اجر ملے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,10,'5:10','اور وہ لوگ جو کفر کریں اور اللہ کی آیات کو جھٹلائیں تو وہ دوزخ میں جانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(5,11,'5:11','(اے لوگو ‘ جو ایمان لائے ہو ‘ اللہ کے اس احسان کو یاد کرو جو اس نے (ابھی حال ہی میں) تم پر کیا ہے ‘ جب کہ ایک گروہ نے تم پر دست درازی کا ارادہ کرلیا تھا مگر اللہ نے ان کے ان کے ہاتھ تم پر اٹھنے سے روک دیئے ۔ اللہ سے ڈر کر کام کرتے رہو ایمان رکھنے والوں کو اللہ پر ہی پر بھروسہ کرنا چاہئے)');
INSERT INTO "sayyid_qutb" VALUES(5,12,'5:12','اللہ نے بنی اسرائیل سے پختہ عہد لیا تھا اور ان میں بارہ نقیب مقرر کئے تھے ، اور ان سے کہا تھا کہ ” میں تمہارے ساتھ ہوں ، اگر تم نے نماز قائم رکھی اور زکوۃ دی اور میرے رسولوں کو مانا اور ان کی مدد کی اور اپنے خدا کو اچھا قرض دیتے رہے تو یقین رکھو کہ میں تمہاری برائیاں تم سے زائل کر دوں گا اور تم کو ایسے باغوں میں داخل کروں گا جن کے نیچے نہریں بہتی ہوں گی مگر اس کے بعد جس نے تم میں سے کفر کی روش اختیار کی تو درحقیقت اس نے سواء السبیل گم کردی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,13,'5:13','پھر یہ انکو اپنے عہد کو توڑ ڈالنا تھا جس کی وجہ سے ہم نے ان کو اپنی رحمت سے دور پھینک دیا اور انکے دل کو سخت کردیا ، اب ان کا حال یہ ہے کہ الفاظ کا الٹ پھیر کر کے بات کو کہیں سے کہیں لے جاتے ہیں ۔ جو تعلیم انہیں دی گئی تھی اس کا بڑا حصہ بھول چکے ہیں ‘ اور آئے دن تمہیں ان کی کسی نہ کسی خیانت کا پتہ چلتا رہتا ہے ان میں سے بہت کم لوگ اس عیب سے بچے ہوئے ہیں ۔ لہذا انہیں معاف کرو اور انکی حرکات سے چشم پوشی کرتے رہو ‘ اللہ ان لوگوں کو پسند کرتا ہے جو احسان کی روش رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(5,14,'5:14','اسی طرح ہم نے ان لوگوں سے بھی پختہ عہد لیا تھا جنہوں نے کہا تھا کہ ہم نصاری ہیں مگر ان کو بھی جو سبق یاد کرایا گیا تھا اس کا ایک بڑا حصہ انہوں نے فراموش کردیا ۔ آخر کار ہم نے ان کے درمیان قیامت تک کیلئے دشمنی اور آپس میں بغض وعناد کا بیج بو دیا اور ضرور ایک وقت آئے گا جب اللہ انہیں بتائے گا کہ یہ دنیا میں کیا کیا بناتے رہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,15,'5:15','اے اہل کتاب ‘ ہمارا رسول تمہارے پاس آگیا ہے جو کتاب الہی کی بہت سی ان باتوں کو تمہارے سامنے کھول رہا ہے جن پر تم پردہ ڈالا کرتے تھے اور بہت سی باتوں سے درگزر بھی کرجاتا ہے ۔ تمہارے پاس اللہ کی طرف سے روشنی آگئی ہے اور ایک ایسی حق نما کتاب ۔');
INSERT INTO "sayyid_qutb" VALUES(5,16,'5:16','جس کے ذریعے سے اللہ تعالیٰ ان لوگوں کو جو اس کی رضا کے طالب ہیں سلامتی کے طریقے بتاتا ہے اور اپنے اذن سے ان کو اندھیروں سے نکال کر اجالے کی طرف لاتا ہے اور راہ راست کی طرف ان کی راہنمائی کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,17,'5:17','یقینا کفر کیا ان لوگوں نے جنہوں نے کہا کہ مسیح ابن مریم خدا ہے ۔ اے نبی ! ان سے کہو کہ اگر خدا مسیح ابن مریم کو اور اس کی ماں اور تمام زمین والوں کو ہلاک کردینا چاہے تو کس کی مجال ہے کہ اس کو اس ارادے سے باز رکھ سکے ؟ اللہ تو زمین اور آسمانوں کا اور ان سب چیزوں کا مالک ہے جو زمین اور آسمان کے درمیان پائی جاتی ہے ۔ جو کچھ چاہتا ہے پیدا کرتا ہے اور اس کی قدرت ہر چیز پر حاوی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,18,'5:18','یہود ونصاری کہتے کہ ہم اللہ کے بیٹے اور اس کے چہیتے ہیں۔ ان سے پوچھو ‘ پھر تمہارے گناہوں پر وہ تمہیں سزا کیوں دیتا ہے ؟ وہ حقیقت تم بھی ویسے ہی انسان ہو جیسے اور انسان خدا نے پیدا کئے ہیں ۔ وہ جسے چاہتا ہے معاف کرتا ہے ۔ اور جسے چاہتا ہے سزا دیتا ہے ۔ زمین و آسمان اور ان کی ساری موجودات اس کی ملک ہیں اور اس کی طرف سب کو جانا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,19,'5:19','اے اہل کتاب ‘ یہ رسول ایسے وقت تمہارے پاس آیا اور دین کی واضح تعلیم دے رہا ہے جبکہ رسولوں کی آمد کا سلسلہ ایک مدت سے بند تھا تاکہ تم یہ بات کہ سکو کہ ہمارے پاس کوئی بشارت دینے والا اور ڈرانے والا نہیں آیا ۔ سو دیکھو اب وہ بشارت دینے اور ڈرانے والا آگیا ۔۔۔۔۔۔ اور اللہ ہر چیز پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,20,'5:20','یاد کرو جب موسیٰ نے اپنی قوم سے کہا تھا کہ ” اے میری قوم کے لوگو ‘ اللہ کی اس نعمت کا خیال کرو جو اس نے تمہیں عطا کی تھی ۔ اس نے تم میں سے نبی پیدا کئے ‘ تم کو فرمان روا بنایا اور تم کو وہ کچھ دیا جو دنیا میں کسی کو نہ دیا تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,21,'5:21','اے برادران قوم ! اس مقدس سرزمین میں داخل ہوجاؤ جو اللہ تمہارے لئے لکھدی ہے ۔ پیچھے نہ ہٹو ورنہ ناکام ونامراد پلٹو گے ۔ “ انہوں نے جواب دیا ” اے موسیٰ وہاں تو بڑے زبردست لوگ رہتے ہیں ‘ ہم وہاں ہر گز نہ جائیں گے جب تک وہ وہاں سے نکل نہ جائیں ، ہاں اگر وہ نکل آئے تو ہم داخل ہونے کیلئے تیار ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,22,'5:22','ان ڈرنے والوں میں دو شخص ایسے بھی تھے جن کو اللہ نے اپنی نعمت سے نوازا تھا ‘ انہوں نے کہا کہ ” ان جباروں کے مقابلے میں دروازے کے اندر کہا ۔ ” اے موسیٰ ہم تو وہاں کبھی نہ جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(5,23,'5:23','جب تک وہ وہاں موجود ہیں ۔ بس تم اور تمہارے رب دونوں جاؤ اور لڑو ‘ ہم تو یہاں بیٹھے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,24,'5:24','اس پر موسیٰ (علیہ السلام) نے کہا ” اے میرے رب میرے اختیار میں کوئی نہیں ہے مگر میری اپنی ذات یا میرا بھائی ‘ پس تو ہمیں ان نافرمان لوگوں سے الگ کر دے ۔ “ اللہ نے جواب دیا : ” تو وہ ملک اب چالیس سال تک ان پر حرام ہے ۔ یہ زمین میں مارے مارے پھریں گے ‘ ان نافرمانوں کی حالت پر ہر گز ترس نہ کھاؤ ۔');
INSERT INTO "sayyid_qutb" VALUES(5,25,'5:25','اس پر موسیٰ (علیہ السلام) نے کہا ” اے میرے رب ‘ میرے اختیار میں کوئی نہیں مگر میری اپنی ذات یا میرا بھائی ‘ پس تو ہمیں ان نافرمان لوگوں سے الگ کر دے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,26,'5:26','اللہ نے جواب دیا ” اچھا تو وہ ملک چالیس سال تک ان پر حرام ہے ‘ یہ زمین میں مارے مارے پھریں گے ‘ ان نافرمانوں کی حالت پر ہر گز ترس نہ کھاؤ۔ )');
INSERT INTO "sayyid_qutb" VALUES(5,27,'5:27','اور ذرا انہیں آدم کے دو بیٹوں کا قصہ بھی بےکم وکاست سنا دو ۔ جب ان دونوں نے قربانی کی تو ان میں سے ایک کی قربانی قبول کی گئی اور دوسرے کی نہ کی گئی۔ اس نے کہا ” میں تجھے مار ڈالوں گا ۔ “ اس نے جواب دیا ” اللہ تو متقیوں ہی کی نذریں قبول کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,28,'5:28','اگر تو مجھے قتل کرنے کے لئے ہاتھ اٹھائے گا تو میں تجھے قتل کرنے کے لئے ہاتھ نہ اٹھاؤں گا ۔ میں اللہ رب العالمین سے ڈرتا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,29,'5:29','میں چاہتا ہوں کہ میرا اور اپنا گناہ تو ہی سمیٹ لے اور دوزخی بن کر رہے ۔ ظالموں کے ظلم کا یہی ٹھیک بدلہ ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,30,'5:30','آخرکا اس کے نفس نے اپنے بھائی کا قتل اس کے لئے آسان کردیا اور وہ اسے مار کر ان لوگوں میں شامل ہوگیا جو نقصان اٹھانے والے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,31,'5:31','پھر اللہ نے ایک کوا بھیجا جو زمین کھودنے لگا تاکہ اسے بتائے کہ اپنے بھائی کی لاش کیسے چھپائے ۔ یہ دیکھ کر وہ بولا ” افسوس مجھ پر ! میں اس کوے جیسا بھی نہ ہوسکا کہ اپنے بھائی کی لاش چھپانے کی تدبیر نکال لیتا ، اس کے بعد وہ اپنے کئے پر بہت بچھتایا');
INSERT INTO "sayyid_qutb" VALUES(5,32,'5:32','سی وجہ سے بنی اسرائیل پر ہم نے یہ فرمان لکھ دیا تھا کہ ’ جس نے کسی انسان کو خون کے بدلے یا زمین میں فساد پھیلانے کے سوا کسی اور وجہ سے قتل کیا اس نے گویا تمام انسانوں کو قتل کردیا اور جس نے کسی کو زندگی بخشی اس نے گویا تمام انسانوں کو زندگی بخش دی ۔ مگر ان کا حال یہ ہے کہ ہمارے رسول پے در پے ان کے پاس کھلی کھلی ہدایات لے کر آئے پھر بھی ان میں بکثرت لوگ زمین میں زیادتیاں کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(5,33,'5:33','(جو لوگ اللہ اور اس کے رسول سے لڑتے ہیں اور زمین میں اس لئے تگ ودو کرتے پھرتے ہیں کہ فساد برپا کریں ان کی سزا یہ ہے کہ قتل کئے جائیں ‘ یا سولی پر چڑھائے جائیں ‘ یا ان کے ہاتھ اور پاؤں مخالف سمتوں سے کاٹ ڈالے جائیں ‘ یا وہ جلا وطن کردیئے جائیں ۔ یہ ذلت ورسوائی تو ان کے لئے دنیا میں ہے اور آخرت میں ان کے لئے اس سے بڑی سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(5,34,'5:34','مگر جو لوگ توبہ کرلیں قبل اس کے کہ تم ان پر قابو پاؤ۔۔۔۔۔ تو تمہیں معلوم ہونا چاہئے کہ اللہ معاف کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(5,35,'5:35','(اے لوگو جو ایمان لائے ہو ‘ اللہ سے ڈرو اور اس کی جناب میں باریابی کا ذریعہ تلاش کرو اور اس کی راہ میں جدوجہد کرو ‘ شاید کہ تمہیں کامیابی نصیب ہوجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,36,'5:36','خوب جان لو کہ جن لوگوں نے کفر کا رویہ اختیار کیا ہے اگر ان کے قبضے میں ساری زمین کی دولت ہو اور اتنی ہی اور اس کے ساتھ اور وہ چاہیں کہ اسے فدیہ میں دے کر روز قیامت کے عذاب سے بچ جائیں ‘ تب بھی وہ ان سے قبول نہ کی جائے گی اور انہیں دردناک سزا مل کر رہے گی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,37,'5:37','وہ چاہیں گے کہ دوزخ کی آگ سے نکل بھاگیں مگر نہ نکل سکیں گے اور انہیں قائم رہنے والا عذاب دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(5,38,'5:38','(اور چور خواہ عورت ہو یا مرد دونوں کے ہاتھ کاٹ دو ‘ یہ ان کی کمائی کا بدلہ ہے اور اللہ کی طرف سے عبرتناک سزا۔ اللہ کی قدرت سب پر غالب ہے اور وہ دانا وبینا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,39,'5:39','پھر جو ظلم کرنے کے بعد توبہ کرے اور اپنی اصلاح کرلے تو اللہ کی نظر عنایت پھر اس پر مائل ہوجائے گی ‘ اللہ بہت درگزر کرنے والا اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,40,'5:40','کیا تم جانتے نہیں ہو کہ اللہ زمین اور آسمانوں کی سلطنت کا مالک ہے ؟ جسے چاہے سزا دے اور جسے چاہے معاف کردے ‘ وہ ہر چیز کا اختیار رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,41,'5:41','اے پیغمبر صلى الله عليه وسلم تمہارے لئے باعث رنج نہ ہوں وہ لوگ جو کفر کی راہ میں بڑی تیز گامی دکھا رہے ہیں خواہ وہ ان میں سے ہوں جو منہ سے کہتے ہیں ہم ایمان لائے مگر دل ان کے ایمان نہیں لائے یا ان میں سے جو یہودی ہیں ‘ جن کا حال یہ ہے کہ جھوٹ کیلئے کان لگاتے ہیں ‘ اور دوسرے لوگوں کی خاطر جو تمہارے پاس کبھی نہیں آئے سن گن لیتے پھرتے ہیں ‘ کتاب اللہ کے الفاظ کو ان کا صحیح محل متعین ہونے کے باوجود اصل معنی سے پھیرتے ہیں اور لوگوں سے کہتے ہیں کہ اگر تمہیں یہ حکم دیا جائے تو مانو نہیں تو نہ مانو۔ جسے اللہ ہی نے فتنہ مین ڈالنے کا ارادہ کرلیا ہو اس کو اللہ کی گرفت سے بچانے کیلئے تم کچھ نہیں کرسکتے ، یہ وہ لوگ ہیں جن کے دلوں کو اللہ نے پاک کرنا نہ چاہا ‘ ان کیلئے دنیا میں رسوائی ہے اور آخرت میں سخت سزا۔');
INSERT INTO "sayyid_qutb" VALUES(5,42,'5:42','یہ جھوٹ سننے والے اور حرام کے مال کھانے والے ہیں ‘ لہذا اگر یہ تمہارے پاس (اپنے مقدمات لے کر) آئیں تو تمہیں اختیار دیا جاتا ہے کہ چاہو ان کا فیصلہ کرو ورنہ انکار کر دو تو یہ تمہارا کچھ بگاڑ نہیں سکتے ‘ اور فیصلہ کرو تو پھر ٹھیک ٹھیک انصاف کے ساتھ کرو کہ اللہ انصاف کرنے والوں کو پسند کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(5,43,'5:43','اور یہ تمہیں کیسے حکم بناتے ہیں جبکہ ان کے پاس تورات موجود ہے جس میں اللہ کا حکم لکھا ہوا ہے اور پھر یہ اس سے منہ موڑ رہے ہیں ؟ اصل بات یہ ہے کہ یہ لوگ ایمان ہی نہیں رکھتے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,44,'5:44','ہم نے توراۃ نازل کی جس میں ہدایت اور روشنی تھی ، ہمارے نبی جو مسلم تھے ‘ اسی کے مطابق ان یہودیوں کے معاملات کا فیصلہ کرتے تھے ۔ اور اسی طرح ربانی اور احبار بھی (اسی پر فیصلہ کا مدار رکھتے تھے) کیونکہ انہیں کتاب اللہ کی حفاظت کا ذمہ دار بنایا گیا تھا اور وہ اس پر گواہ تھے ۔ پس (اے گروہ یہود) تم لوگوں سے نہ ڈرو بلکہ مجھ سے ڈرو اور میری آیات کو ذرا ذرا سے معاوضے سے بیچنا چھوڑ دو ، جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی کافر ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,45,'5:45','تورات میں ہم نے یہودیوں پر یہ حکم لکھ دیا تھا کہ جان کے بدلے جان ‘ آنکھ کے بدلے آنکھ ‘ ناک کے بدلے ناک ‘ کان کے بدلے کان ‘ دانت کے بدلے دانت ‘ اور تمام زخموں کیلئے برابر کا بدلہ ‘ جو قصاص کا صدقہ کر دے تو وہ اس کے لئے کفارہ ہے ۔ اور جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی ظالم ہیں');
INSERT INTO "sayyid_qutb" VALUES(5,46,'5:46','پھر ہم نے ان پیغمبروں کے بعد مریم (علیہ السلام) کے بیٹے عیسیٰ (علیہ السلام) کو بھیجا ، تورات میں سے جو کچھ اس کے سامنے موجود تھا وہ اس کی تصدیق کرنے والا تھا اور ہم نے اس کو انجیل عطا کی جس میں راہنمائی اور روشنی تھی اور وہ بھی تورات میں سے جو کچھ اس وقت موجود تھا اس کی تصدیق کرنے والی تھی اور خدا ترس لوگوں کیلئے سراسر ہدایت اور نصیحت تھی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,47,'5:47','ہمارا حکم تھا کہ اہل انجیل اس قانون کے مطابق فیصلہ کریں جو اللہ نے اس پر نازل کیا ہے اور جو لوگ اللہ کے نازل کردہ قانون کے مطابق فیصلہ نہ کریں وہی فاسق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(5,48,'5:48','پھر اے نبی (علیہ السلام) ہم نے تمہاری طرف یہ کتاب بھیجی جو حق لے کر آئی ہے اور الکتاب میں سے جو کچھ اس کے آگے موجود ہے اس کی تصدیق کرنیوالی اور اس کی محافظ ونگہبان ہے ۔ لہذا تم خدا کے نازل کردہ قانون کے مطابق لوگوں کے معاملات کا فیصلہ کرو اور جو حق تمہارے پاس آیا ہے اس سے منہ موڑ کر ان کی خواہشات کی پیروی نہ کرو ۔ ہم نے تم ” انسانوں “ میں سے ہر ایک کیلئے ایک شریعت اور ایک راہ عمل مقرر کی ۔ اگر تمہارا خدا چاہتا تو تم سب کو ایک امت بھی بنا سکتا تھا ‘ لیکن اس نے یہ اس لئے کیا کہ جو کچھ اس نے تم لوگوں کو دیا ہے اس میں تمہاری آزمائش کرے ، لہذا بھلائیوں میں ایک دوسرے سے سبقت لے جانے کی کوشش کرو۔ آخر کار تم سب کو خدا کی طرف پلٹ کر جانا ہے ‘ پھر وہ تمہیں اصل حقیقت بتا دے گا جس میں تم اختلاف کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(5,49,'5:49','پس اے نبی صلى الله عليه وسلم تم اللہ کے نازل کردہ قانون کے مطابق ان لوگوں کے معاملات کا فیصلہ کرو اور ان کی خواہشات کی پیروی نہ کرو ۔ ہوشیار رہو کہ یہ لوگ تم کو فتنہ میں ڈال کر اس ہدایت سے ذرہ بھر منحرف نہ کرنے پائیں جو خدا نے تمہاری طرف نازل کی ہے ‘ پھر اگر یہ اس سے منہ موڑیں تو جان لو کہ اللہ نے ان کے بعض گناہوں کی پاداش میں ان کو مبتلائے مصیبت کرنے کا ارادہ ہی کرلیا ہے ۔ اور یہ حقیقت ہے کہ ان لوگوں میں سے اکثر فاسق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,50,'5:50','اگر یہ خدا کے قانون سے منہ موڑتے ہیں تو کیا پھر جاہلیت کا فیصلہ چاہتے ہیں ‘ حالانکہ جو لوگ اللہ پر یقین رکھتے ان کے نزدیک اللہ سے بہتر فیصلہ کرنیوالا کون ہو سکتا ہے');
INSERT INTO "sayyid_qutb" VALUES(5,51,'5:51','اے لوگو جو ایمان لائے ہو ‘ یہودیوں اور عیسائیوں کو اپنا رفیق نہ بناؤ۔ یہ آپس ہی میں ایک دوسرے کے رفیق ہیں ۔ اور اگر تم میں سے کوئی ان کو اپنا رفیق بناتا ہے تو اس کا شمار بھی پھر انہیں میں ہے یقینا اللہ ظالموں کو اپنی راہنمائی سے محروم کردیتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(5,52,'5:52','تم دیکھتے ہو کہ جن کے دلوں میں نفاق کی بیماری ہے وہ انہی میں دوڑ دھوپ کرتے پھرتے ہیں کہتے ہیں ہمیں ڈر لگتا ہے کہ کہین ہم کسی مصیبت کے چکر میں نہ پھنس جائیں ، مگر بعید نہی کہ اللہ جب تمہیں فیصلہ کن فتح بخشے گا یا اپنی طرف سے کوئی اور بات ظاہر کرے گا تو یہ لوگ اپنے اس نفاق پر جسے یہ دلوں میں چھپائے ہوئے ہیں نادم ہوں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,53,'5:53','اور اس وقت اہل ایمان کہیں گے ” کیا یہ وہی لوگ ہیں جو اللہ کے نام سے کڑی کڑی قسمیں کھا کر یقین دلاتے تھے کہ ہم تمہارے ساتھ ہیں ؟ “۔۔۔۔۔ ان کے سب اعمال ضائع ہوگئے اور آخر کار یہ ناکام ونامراد ہو کر رہے');
INSERT INTO "sayyid_qutb" VALUES(5,54,'5:54','اے لوگو ! جو ایمان لائے ہو ‘ اگر تم میں سے کوئی اپنے دین سے پھرتا ہے ۔ (تو پھر جائے) اللہ اور بہت سے لوگ ایسے پیدا کر دے گا جو اللہ کو محبوب ہوں گے اور اللہ ان کو محبوب ہوگا جو مومنوں پر نرم اور کفار پر سخت ہوں گے ‘ جو اللہ کی راہ میں جدوجہد کریں گے اور کسی ملامت کرنے والے کی ملامت سے نہ ڈریں گے ۔ یہ اللہ کا فضل ہے ‘ جسے چاہے عطا کرتا ہے ۔ اللہ وسیع ذرائع کا مالک ہے اور سب کچھ جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,55,'5:55','تمہارے رفیق تو حقیقت میں صرف اللہ اور اللہ کا رسول اور وہ اہل ایمان ہیں جو نماز قائم کرتے ہیں ‘ زکوۃ ادا کرتے ہیں اور اللہ کے آگے جھکنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(5,56,'5:56','اور جو اللہ اور اس کے رسول اور اہل ایمان کو اپنا رفیق بنا لے اسے معلوم ہو کہ اللہ کی جماعت ہی غالب ہونے والی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,57,'5:57','اے ایمان والو جن لوگون کو تم سے پہلے کتاب مل چکی ہے ان لوگوں میں سے جو ایسے ہیں کہ انہوں نے تمہارے دین کو ہنسی اور کھیل بنارکھا ہے ان کو اور دوسرے کفار کو دوست مت بناؤ۔ اور اللہ تعالیٰ سے ڈرو اگر تم ایمان دار ہو۔');
INSERT INTO "sayyid_qutb" VALUES(5,58,'5:58','اور جب تم نماز کے لیے اعلان کرتے ہو تو وہ لوگ اس کے ساتھ ہنسی اور کھیل کرتے ہیں۔ یہ اس سبب سے ہے کہ وہ ایسے لوگ ہیں کہ بالکل عقل نہیں رکھتے۔');
INSERT INTO "sayyid_qutb" VALUES(5,59,'5:59','ان سے کہو : اے اہل کتاب ‘ تم جس بات پر ہم سے بگڑے ہو وہ اس کے سوا اور کیا ہے کہ ہم اللہ پر اور دین کی اس تعلیم پر ایمان لائے ہیں جو ہماری طرف نازل ہوئی ہے اور ہم سے پہلے بھی نازل ہوئی تھی اور تم میں سے اکثر لوگ فاسق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,60,'5:60','پھر کہو : کیا میں ان لوگوں کی نشاندہی کر دوں جن کا انجام خدا کے ہاں فاسقوں کے انجام سے بدتر ہے ‘ وہ جن پر خدا نے لعنت کی ‘ جن پر اس کا غضب ٹوٹا ‘ جن میں سے بندر اور سور بنائے گئے ‘ جنہوں نے طاغوت کی بندگی کی۔ ان کا درجہ اور بھی زیادہ برا ہے اور وہ سواء السبیل سے بہت زیادہ بھٹکے ہوئے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,61,'5:61','جب یہ لوگ تم لوگوں کے پاس آتے ہیں تو کہتے ہیں کہ ہم ایمان لائے حالانکہ کفر لیے ہوئے آئے تھے اور کفر ہی لیے ہوئے واپس گئے اور اللہ خوب جانتا ہے جو کچھ یہ دلوں میں چھپائے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(5,62,'5:62','تم دیکھتے ہو کہ ان میں سے بکثرت لوگ گناہ اور ظلم و زیادتی کے کاموں پر دوڑ دھوپ کرتے پھرتے ہیں اور حرام کے مال کھاتے ہیں ۔ بہت ہی بری حرکات ہیں جو یہ کر رہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,63,'5:63','ان کے علماء ومشائخ انہیں گناہ پر زبان کھولنے اور حرام کھانے سے منع نہیں کرتے ۔ یقینا بہت ہی برا کارنامہ زندگی ہے جو یہ تیار کر رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(5,64,'5:64','یہودی کہتے ہیں کہ اللہ کے ہاتھ بندھے ہوئے ہیں ۔۔۔۔۔ باندھے گئے ہیں ان کے ہاتھ اور لعنت پڑی ان پر اس بکواس کی بدولت جو یہ کرتے ہیں اللہ کے ہاتھ تو کشادہ ہیں ‘ جس طرح چاہتا ہے خرچ کرتا ہے ۔ حقیقت یہ ہے کہ جو کلام تمہارے رب کی طرف سے تم پر نازل ہوا ہے وہ ان میں سے اکثر لوگوں کی سرکشی و باطل پرستی میں الٹا اضافہ کا موجب بن گیا ہے اور ہم نے ان کے درمیان قیامت تک کیلئے عداوت اور دشمنی ڈال دی ہے ۔ جب کبھی جنگ کی آگ بھڑکاتے ہیں اللہ اس کو ٹھنڈا کرتا ہے ۔ یہ زمین میں فساد پھیلانے کی سعی کر رہے ہیں مگر اللہ فساد پھیلانے والوں کو پسند نہیں کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,65,'5:65','اگر (اس سرکشی کے بجائے) یہ اہل کتاب ایمان لے آتے ‘ اور خدا ترسی کی روش اختیار کرتے ‘ تو ہم انکی برائیاں ان سے دور کردیتے ۔ اور ان کی نعمت بھری جنتوں میں پہنچاتے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,66,'5:66','کاش انہوں نے تورات اور انجیل اور ان دوسری کتابوں کو قائم کیا ہوتا جو ان کے رب کی طرف سے ان کے پاس بھیجی گئی تھیں ‘ ایسا کرتے تو ان کے لئے اوپر سے رزق برستا اور نیچے سے ابلتا ۔ اگرچہ ان میں کچھ لوگ راست رو بھی ہیں ‘ لیکن ان کی اکثریت سخت بدعمل ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,67,'5:67','اے پیغمبر صلى الله عليه وسلم جو کچھ تمہارے رب کی طرف سے تم پر نازل کیا گیا ہے وہ لوگوں تک پہنچا دو ‘ اگر تم نے ایسا نہ کیا تو اس کی پیغمبری کا حق ادا نہ کیا ‘ اللہ تم کو لوگوں کے شر سے بچانے والا ہے ، یقین رکھو کہ وہ کافروں کو ہدایت کی راہ ہرگز نہ دکھائے گا');
INSERT INTO "sayyid_qutb" VALUES(5,68,'5:68','صاف کہہ دو کہ ” اے اہل کتاب ‘ تم ہر گز کسی اصل پر نہیں ہو جب تک کہ تورات اور انجیل اور ان دوسری کتابوں کو قائم نہ کرو جو تمہاری طرف تمہارے رب کی طرف سے نازل کی گئی ہیں ، ضرور ہے کہ یہ فرمان جو تم پر نازل کیا گیا ہے ان میں سے اکثر کی سرکشی اور انکار کو اور زیادہ بڑھا دے گا ۔ مگر انکار کرنے والوں کے حال پر کچھ افسوس نہ کرو ۔ (یقین جانو کہ یہاں اجارہ کسی بھی نہیں ہے)');
INSERT INTO "sayyid_qutb" VALUES(5,69,'5:69','مسلمان ہوں یا یہودی ‘ صابی ہوں یا عیسائی ‘ جو بھی اللہ اور روز آخرت پر ایمان لائے گا اور نیک عمل کرے گا بیشک اس کے لئے نہ کسی خوف کا مقام ہے نہ رنج کا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,70,'5:70','ہم نے بنی اسرائیل سے پختہ عہد لیا اور ان کی طرف بہت سے رسول بھیجے ۔ مگر جب کبھی ان کے پاس کوئی رسول ‘ انکی خواہشات نفس کے خلاف کچھ لیکر آیا تو کسی کو انہوں نے جھٹلایا اور کو قتل کردیا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,71,'5:71','اور اپنے نزدیک یہ سمجھے کہ کوئی فتنہ رونما نہ ہوگا ۔ اس لئے اندھے اور بہرے بن گئے ، پھر اللہ نے انہیں معاف کیا تو ان میں سے اکثر لوگ اور زیادہ اندھے اور بہرے بنتے چلے گئے ، اللہ ان کی سب حرکات دیکھ رہا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,72,'5:72','یقینا کفر کیا ان لوگوں نے جنہوں نے کہا کہ اللہ مسیح ابن مریم ہی ہے حالانکہ مسیح نے کہا تھا کہ ” اے بنی اسرائیل ‘ اللہ کی بندگی کرو جو میرا رب بھی ہے اور تمہارا رب بھی ۔ “ جس نے اللہ کے ساتھ کسی کو شریک ٹھہرایا اس پر اللہ نے جنت حرام کردی اور اس کا ٹھکانا جہنم ہے اور ایسے ظالموں کا کوئی مددگار نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,73,'5:73','یقینا کفر کیا ان لوگوں نے جنہوں نے کہا کہ اللہ تین میں کا ایک ہے حالانکہ ایک خدا کے سوا کوئی خدا نہیں ہے ۔ اگر یہ لوگ ان باتوں سے باز نہ آئے تو ان میں سے جس جس نے کفر کیا ہے اس کو درد ناک سزا دی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(5,74,'5:74','پھر کیا یہ اللہ سے توبہ نہ کریں گے اور اس سے معافی نہ مانگیں گے ؟ اللہ بہت درگزر فرمانے والا اور رحم کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,75,'5:75','مسیح ابن مریم اس کے سوا کچھ نہیں کہ بس ایک رسول تھا ‘ اس سے پہلے اور بھی بہت سے رسول گزر چکے تھے ‘ اس کی ماں راست باز عورت تھی اور وہ دونوں کھانا کھاتے تھے ۔ دیکھو ہم کس طرح ان کے ساتھ حقیقت کی نشانیاں واضح کرتے ہیں ‘ پھر دیکھو یہ کدھرالٹے پھرے جاتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,76,'5:76','ان سے کہو تم اللہ کو چھوڑ کر اس کی پرستش کرتے ہو جو نہ تمہارے لئے نقصان کا اختیار رکھتا ہے نہ نفع کا ؟ حالانکہ سب کی سننے والا اور سب کچھ جاننے والا تو اللہ ہی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,77,'5:77','اے اہل کتاب ‘ اپنے دین میں غلو نہ کرو اور ان لوگوں کے تخیلات کی پیروی نہ کرو جو تم سے پہلے خود گمراہ ہوئے اور بہتوں کو گمراہ کیا اور ” سواء السبیل “ سے بھٹک گئے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,78,'5:78','بنی اسرائیل میں سے جن لوگوں نے کفر کی راہ اختیار کی ان پر داؤد اور عیسیٰ ابن مریم (علیہ السلام) کی زبان سے لعنت کی گئی کیونکہ وہ سرکش ہوگئے تھے اور زیادتیاں کرنے لگے تھے');
INSERT INTO "sayyid_qutb" VALUES(5,79,'5:79','انہوں نے ایک دوسرے کو برے افعال کے ارتکاب سے روکنا چھوڑ دیا تھا ‘ برا طرز عمل تھا جو انہوں نے اختیار کیا تھا');
INSERT INTO "sayyid_qutb" VALUES(5,80,'5:80','آج تم ان میں بکثرت ایسے لوگ دیکھتے ہو جو کفار کی حمایت ورفاقت کرتے ہیں ۔ یقینا بہت برا انجام ہے جس کی تیاری ان کے نفسوں نے ان کیلئے کی ہے اللہ ان پر غضبناک ہوگیا ہے اور وہ دائمی عذاب میں مبتلا ہونے والے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,81,'5:81','اگر فی الواقعہ یہ لوگ اللہ اور پیغمبر اور اس چیز کو ماننے والے ہوتے جو پیغمبر پر نازل ہوئی تھی تو کبھی کافروں کو اپنا رفیق نہ بناتے مگر ان میں سے بیشتر تو خدا کی اطاعت سے نکل چکے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,82,'5:82','تم اہل ایمان کی عداوت میں سب سے زیادہ سخت یہود اور مشرکین کو پاؤ گے اور ایمان لانے والوں کے لئے دوستی میں قریب تران لوگوں کو پاؤ گے جنہوں نے کہا تھا کہ ہم نصاری ہیں ۔ یہ اس وجہ سے کہ ان میں عبادت گزار عالم اور تارک الدنیا فقیر پائے جاتے ہیں اور ان میں غرور نفس نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,83,'5:83','’ جب وہ اس کلام کو سنتے ہیں جو رسول پر اترا ہے تو تم دیکھتے ہو کہ حق شناسی کے اثر سے ان کی آنکھیں آنسوؤں سے تر ہوجاتی ہیں ، وہ بول اٹھتے ہیں کہ ” پروردگار ‘ ہم ایمان لائے ‘ ہمارا نام گواہی دینے والوں میں لکھ لے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,84,'5:84','اور وہ کہتے ہیں کہ ” آخر کیوں نہ ہم اللہ پر ایمان لائیں اور جو حق ہمارے پاس آیا ہے اسے کیوں نہ مان لیں جب کہ ہم اس بات کی خواہش رکھتے ہیں کہ ہمارا رب ہمیں صالح لوگوں میں شامل کرے ؟');
INSERT INTO "sayyid_qutb" VALUES(5,85,'5:85','ان کے ان قول کی وجہ سے اللہ نے ان کو ایسی جنتیں عطا کیں جن کے نیچے نہریں بہتی ہیں اور وہ ان میں ہمیشہ رہیں گے یہ جزاء ہے نیک رویہ اختیار کرنے والوں کے لئے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,86,'5:86','رہے وہ لوگ جنہوں نے ہماری آیات کو ماننے سے انکار کیا اور انہیں جھٹلایا تو وہ جہنم کے مستحق ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,87,'5:87','” اے لوگو جو ایمان لائے ہو ‘ جو پاک چیزیں اللہ نے تمہارے لئے حلال کی ہیں انہیں حرام نہ کرلو اور حد سے تجاوز نہ کرو ‘ اللہ کو زیادتی کرنے والے سخت ناپسند ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,88,'5:88','جو کچھ حلال وطیب رزق اللہ نے تم کو دیا ہے اسے کھاؤ پیو اور اس اللہ کی نافرمانی سے بچتے رہو جس پر تم ایمان لائے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(5,89,'5:89','تم لوگ جو مہمل قسمیں کھالیتے ہو ‘ ان پر اللہ گرفت نہیں کرتا ‘ مگر جو قسمیں تم جان بوجھ کر کھاتے ہو اس پر وہ ضرور تم سے مواخذہ کرے گا ۔ (ایسی قسم توڑنے کا) کفارہ یہ ہے کہ دس مسکینوں کو وہ اوسط درجے کا کھانا کھلاؤ جو تم اپنے بال بچوں کو کھلاتے ہو ‘ یا انہیں کپڑے پہناؤ یا ایک غلام آزاد کرو ‘ اور جو اس کی استطاعت نہ رکھتا ہو ‘ وہ تین دن کے روزے رکھے ۔ یہ تمہاری قسموں کا کفارہ ہے جب کہ تم قسم کھا کر توڑ دو ۔ اپنی قسموں کی حفاظت کیا کرو ، اس طرح اللہ اپنے احکام تمہارے لئے واضح کرتا ہے شاید کہ تم شکر ادا کرو ۔ “');
INSERT INTO "sayyid_qutb" VALUES(5,90,'5:90','’ اے لوگو جو ایمان لائے ہو ‘ یہ شراب جوا اور یہ آستانے اور پانسے یہ سب گندے شیطانی کام ہیں ان سے پرہیز کرو ‘ امید ہے کہ تمہیں فلاح نصیب ہوگی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,91,'5:91','شیطان تو یہ چاہتا ہے کہ شراب اور جوئے کے ذریعے سے تمہارے درمیان عداوت اور بغض ڈال دے اور تمہیں خدا کی یاد سے اور نماز سے روک دے ، پھر کیا تم ان چیزوں سے باز رہو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(5,92,'5:92','اللہ اور اس کے رسول کی بات مانو اور باز آجاؤ لیکن اگر تم نے حکم عدولی کی تو جان لو کہ ہمارے رسول پر بس صاف صاف حکم پہنچا دینے کی ذمہ داری تھی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,93,'5:93','جو لوگ ایمان لے آئے اور نیک عمل کرنے لگے انہوں نے پہلے جو کچھ کھایا پیا تھا ‘ اس پر کوئی گرفت نہ ہوگی بشرطیکہ وہ آئندہ ان چیزوں سے بچے رہیں جو حرام کی گئی ہیں اور ایمان پر ثابت قدم رہیں اور اچھے کام کریں ‘ پھر جس چیز سے روکا جائے اس سے رکیں اور جو فرمان الہی ہو اسے مانیں ‘ پھر خدا ترسی کے ساتھ نیک رویہ رکھیں ۔ اللہ نیک کردار لوگوں کو پسند کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,94,'5:94','اے لوگو جو ایمان لائے ہو ‘ اللہ تمہیں اس شکار کے ذریعے سے سخت آزمائش میں ڈالے گا جو بالکل تمہارے ہاتھوں اور نیزوں کی زد میں ہوگا ۔ یہ دیکھنے کے لئے کہ تم میں سے کون اس سے غائبانہ ڈرتا ہے ‘ پھر جس نے اس تنبیہ کے بعد اللہ کی مقرر کی ہوئی حد سے تجاوز کیا اس کے لئے درد ناک سزا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,95,'5:95','اے لوگو جو ایمان لائے ہو ‘ احرام کی حالت میں شکار نہ مارو ‘ اور اگر تم میں سے کوئی جان بوجھ کر ایسا کر گزرے تو جو جانور اس نے مارا ہو اسی کے ہم پلہ ایک جانور اسے مویشیوں میں سے نذر دینا ہوگا جس کا فیصلہ تم میں سے دو عادل آدمی کریں گے اور یہ نذرانہ کعبہ پہنچایا جائے گا ‘ یا نہیں تو اسے گناہ کے کفارے میں چند مسکینوں کو کھانا کھلانا ہوگا ‘ یا اس کے بقدر روزے رکھنے ہوں گے ‘ تاکہ وہ اپنے کئے کا مزا چکھے ۔ پہلے جو کچھ ہوچکا اسے اللہ نے معاف کردیا ‘ لیکن اب اگر کسی نے اس حرکت کا اعادہ کیا تو اس سے اللہ بدلہ لے گا ‘ اللہ سب پر غالب ہے اور بدلہ لینے کی طاقت رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,96,'5:96','تمہارے لئے سمندر کا شکار اور اس کا کھانا حلال کردیا گیا ‘ جہاں تم ٹھہرو وہاں بھی اسے کھا سکتے ہو اور قافلے کے لئے زاد راہ بھی بنا سکتے ہو ۔ البتہ خشکی کا شکار ‘ جب تک تم احرام کی حالت میں ہو ‘ تم پر حرام کیا گیا ہے ۔ پس بچو اس خدا کی نافرمانی سے جس کی پیشی میں تم سب کو گھیر کر حاصل کیا جائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,97,'5:97','اللہ نے مکان محترم کعبہ کو لوگوں کے لئے (اجتماعی زندگی کے) قیام کا ذریعہ بنایا اور ماہ حرام اور قربانی کے جانوروں اور قلادوں کو بھی (اس کام میں معاون بنادیا) تاکہ تمہیں معلوم ہوجائے کہ اللہ آسمانوں اور زمین کے سب حالات سے باخبر ہے اور اسے ہر چیز کا علم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,98,'5:98','خبردار ہوجاؤ ۔ ! اللہ سزادینے میں بھی سخت ہے اور اسکے ساتھ بہت درگزر اور رحم بھی کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,99,'5:99','رسول پر تو پیغام پہنچا دینے کی ذمہ داری ہے ‘ آگے تمہارے کھلے اور چھپے سب حالات کا جاننے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,100,'5:100','اے پیغمبر ان سے کہہ دو کہ پاک اور ناپاک بہرحال یکساں نہیں ہیں ‘ خواہ ناپاک کی بہتات تمہیں کتنا ہی فریفتہ کرنے والی ہو ‘ پس اے لوگو جو عقل رکھتے ہو ‘ اللہ کی نافرمانی سے بچتے رہو ‘ امید ہے کہ تمہیں فلاح نصیب ہوگی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,101,'5:101','’ اے لوگو جو ایمان لائے ہو ‘ ایسی باتیں نہ پوچھا کرو جو تم پر ظاہر کردی جائیں تو تمہیں ناگوار ہوں ‘ لیکن اگر تم انہیں ایسے وقت پوچھو گے جب کہ قرآن نازل ہو رہا ہو تو وہ تم پر کھول دی جائیں گی ۔ اب تک جو کچھ تم نے کیا اسے اللہ نے معاف کردیا ہے ‘ وہ درگزر کرنے والا اور بردبار ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,102,'5:102','تم سے پہلے ایک گروہ نے اسی قسم کے سوالات کئے تھے ‘ پھر وہ لوگ انہی باتوں کی وجہ سے کفر میں مبتلا ہوگئے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,103,'5:103','اللہ نے نہ کوئی بحیرہ مقرر کیا ہے نہ سائبہ نہ وصیلہ اور نہ حام ۔ مگر یہ کافر اللہ پر جھوٹی تہمت لگاتے ہیں اور ان میں سے اکثر بےعقل ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,104,'5:104','کہ ایسے وہمیات کو مان رہے ہیں) اور جب ان سے کہا جاتا ہے کہ آؤ اس قانون کی طرف جو اللہ نے نازل کیا ہے اور آؤ پیغمبر کی طرف تو وہ جواب دیتے ہیں کہ ہمارے لئے تو بس وہی طریقہ کافی ہے جس پر ہم نے اپنے باپ دادا کو پایا ہے ۔ کیا یہ باپ دادا ہی کی تقلید کئے چلے جائیں گے خواہ وہ کچھ نہ جانتے ہوں اور صحیح راستہ کی انہیں خبر ہی نہ ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(5,105,'5:105','’ اے لوگو جو ایمان لائے ہو ‘ اپنی فکر کرو ‘ کسی دوسرے کی گمراہی سے تمہارا کچھ نہیں بگڑتا اگر تم خود راہ راست پر ہو ‘ اللہ کی طرف تم سب کو پلٹ کر جانا ہے ‘ پھر وہ تمہیں بتا دے گا کہ تم کیا کرے رہے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(5,106,'5:106','اے لوگوجو ایمان لائے ہو ‘ جب تم میں سے کسی کی موت کا وقت آجائے اور وہ وصیت کر رہا ہو تو اس کے لئے شہادت کا نصاب یہ ہے کہ تمہاری جماعت میں سے دو صاحب عدل آدمی گواہ بنائے جائیں ‘ یا اگر تم سفر کی حالت میں ہو اور وہاں موت کی مصیبت پیش آجائے تو غیر لوگوں میں سے سے دو گواہ لے لیے جائیں ۔ پھر اگر کوئی شک پڑجائے تو نماز کے بعد دونوں گواہوں کو (مسجد میں) روک لیا جائے اور وہ خدا کی قسم کھا کر کہیں کہ ” ہم کسی ذاتی فائدے کے عوض شہادت بیچنے والے نہیں ہیں ‘ اور خواہ کوئی ہمارا رشتہ دار ہی کیوں نہ ہو (ہم اس کی رعایت کرنے والے نہیں) اور نہ خدا واسطے کی گواہی کو ہم چھپانے والے ہیں ‘ اگر ہم نے ایسا کیا تو گناہ گاروں میں شمار ہوں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,107,'5:107','لیکن اگر پتہ چل جائے کہ ان دونوں نے اپنے آپ کو گناہ میں مبتلا کیا ہے تو پھر ان کی جگہ جو ان کی بہ نسبت شہادت دینے کے لئے اہل تر ہوں ان لوگوں میں سے کھڑے ہوں جن کی حق تلفی ہوئی ہو ‘ اور وہ خدا کی قسم کھا کر کہیں کہ ” ہماری شہادت ان شہادت سے زیادہ برحق ہے اور ہم نے اپنی گواہی میں کوئی زیادتی نہیں کی ہے ‘ اگر ہم ایسا کریں تو ظالموں میں سے ہوں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,108,'5:108','اس طریقے سے زیادہ توقع کی جاسکتی ہے کہ لوگ ٹھیک ٹھیک شہادت دیں گے یا کم از کم اس بات ہی کا خوف کریں گے کہ ان کی قسموں کے بعد دوسری قسموں سے کہیں ان کی تردید نہ ہوجائے ۔ اللہ سے ڈرو اور سنو اللہ نافرمانی کرنے والوں کو اپنی رہنمائی سے محروم کردیتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,109,'5:109','جس روز اللہ سب رسولوں کو جمع کرکے پوچھے گا کہ تمہیں کیا جواب دیا گیا تو وہ عرض کریں گے کہ ہمیں کچھ علم نہیں ‘ آپ ہی تمام پوشیدہ حقیقتوں کو جانتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,110,'5:110','پھر تصور کرو اس موقع کا جب اللہ فرمائے گا کہ ” اے مریم کے بیٹے عیسیٰ (علیہ السلام) یاد کر میری اس نعمت کو جو میں نے تجھے اور تیری ماں کو عطا کی تھی ۔ میں نے روح پاک سے تیری مدد کی ‘ تو گہوارے میں بھی لوگوں سے بات کرتا تھا اور بڑی عمر کو پہنچ کر بھی ‘ میں نے تجھ کو کتاب اور حکمت اور تورات اور انجیل کی تعلیم دی ‘ تو میرے حکم سے مٹی کا پتلا پرندے کی شکل کا بناتا اور اس میں پھونکتا تھا اور وہ میرے حکم سے پرندہ بن جاتا تھا ۔ تو مادر زاد اندھے اور کوڑھی کو میرے حکم سے اچھا کرتا تھا ‘ تو مردوں کو میرے حکم سے نکالتا تھا ۔ پھر جب تو بنی اسرائیل کے پاس صریح نشانیاں لے کر پہنچا اور جو لوگ ان میں سے منکر حق تھے انہوں نے کہا کہ یہ نشانیاں جادوگری کے سوا اور کچھ نہیں ہیں تو میں نے ہی تجھے ان سے بچایا ۔');
INSERT INTO "sayyid_qutb" VALUES(5,111,'5:111','اور جب میں نے حواریوں کو اشارہ کیا کہ مجھ پر اور میرے رسول پر ایمان لاؤ تب انہوں نے کہا ” ہم ایمان لائے اور گواہ روہ کہ ہم مسلم ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,112,'5:112','حواریوں کا سلسلے میں) یہ واقعہ بھی یاد رہے کہ جب حواریوں نے کہا ” اے عیسیٰ ابن مریم (علیہ السلام) کیا آپ کا رب ہم پر آسمان سے کھانے کا ایک خوان اتار سکتا ہے ؟ “ تو حضرت عیسیٰ (علیہ السلام) نے کہا اللہ سے ڈرو اگر تم مومن ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(5,113,'5:113','انہوں نے کہا ” ہم بس یہ چاہتے ہیں کہ ہم اس خوان سے کھانا کھائیں اور ہمارے دل مطمئن ہوں اور ہمیں معلوم ہوجائے کہ آپ نے جو کچھ ہم سے کہا ہے وہ سچ ہے اور ہم اس پر گواہ ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,114,'5:114','اس پر عیسیٰ ابن مریم (علیہ السلام) نے دعا کی ” خدایا ! ہمارے رب ‘ ہم پر آسمان سے ایک خوان نازل کر ‘ جو ہمارے لئے اور ہمارے اگلوں پچھلوں کے لئے خوشی کا موقع قرار پائے اور تیری طرف سے ایک نشانی ہو ہم کو رزق دے اور تو بہترین رازق ہے۔');
INSERT INTO "sayyid_qutb" VALUES(5,115,'5:115','اللہ نے جواب دیا ” میں اس کو تم پر نازل کرنے والا ہوں مگر اس کے بعد جو تم میں سے کفر کرے گا اسے میں ایسی سزا دوں گا جو میں نے کسی کو نہ دی ہوگی ۔');
INSERT INTO "sayyid_qutb" VALUES(5,116,'5:116','غرض جب (یہ احسانات یاد دلا کر) اللہ فرمائے گا کہ ” اے عیسیٰ ابن مریم ‘ کیا تو نے لوگوں سے کہا تھا کہ خدا کے سوا مجھے اور میری ماں کو بھی خدا بنا لو ؟ “ تو وہ جواب میں عرض کرے گا کہ ” سبحان اللہ ‘ میرا یہ کام نہ تھا کہ وہ بات کہتا جس کے کہنے کا مجھے حق نہ تھا ‘ اگر میں نے ایسی بات کہی ہوتی تو آپ کو ضرور علم ہوتا آپ جانتے ہیں جو کچھ میرے دل میں ہے اور میں نہیں جانتا جو کچھ آپ کے دل میں ہے ‘ آپ تو ساری پوشیدہ حقیقتوں کے عالم ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,117,'5:117','میں نے ان سے اس کے سوا کچھ نہیں کہا جس کا آپ نے حکم دیا تھا ‘ یہ کہ اللہ کی بندگی کرو جو میرا رب بھی ہے اور تمہارا رب بھی میں اسی وقت تک ان کا نگران تھا جب تک کہ میں ان کے درمیان تھا ۔ جب آپ نے مجھے واپس بلا لیا تو آپ ان پر نگران تھے اور آپ تو ساری ہی چیزوں پر نگران ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,118,'5:118','اب اگر آپ انہیں سزا دیں تو وہ آپ کے بندے ہیں اور اگر معاف کردیں تو آپ غالب اور دانا ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(5,119,'5:119','تب اللہ فرمائے گا ’ یہ وہ دن ہے جس میں سچوں کو ان کی سچائی نفع دیتی ہے ‘ ان کے لئے ایسے باغ ہیں جن کے نیچے نہریں بہہ رہی ہیں ‘ یہاں وہ ہمیشہ رہیں گے ‘ اللہ ان سے راضی ہو اور وہ اللہ سے ‘ یہی بڑی کامیابی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(5,120,'5:120','زمین اور آسمانوں اور تمام موجودات کی بادشاہی اللہ ہی کے لیے ہے اور وہ ہر چیز پر قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(6,1,'6:1','تعریف ہے اللہ کے لئے جس نے زمین و آسمان بنائے ‘ روشنیاں اور تاریکیاں پیدا ہیں ۔ پھر بھی وہ لوگ جنہوں نے دعوت حق کو ماننے سے انکار کردیا ہے دوسروں کو اپنے رب کے ہمسر ٹھہراتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,2,'6:2','وہی ہے جس نے تم کو مٹی سے پیدا کیا ‘ پھر تمہارے لئے زندگی کی ایک مدت مقرر کردی ‘ اور ایک دوسری مدت اور بھی ہے جو اس کے ہاں طے شدہ ہے ، مگر تم لوگ ہو جو شک میں پڑے ہوئے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,3,'6:3','وہی ایک خدا آسمانوں میں بھی اور زمین میں بھی ‘ تمہارے کھلے اور چھپے سب حال جانتا ہے اور جو برائی یا بھلائی تم کماتے ہو اس سے خوب واقف ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,4,'6:4','’ لوگوں کا حال یہ ہے کہ ان کے رب کی نشانیوں میں سے کوئی نشانی ایسی نہیں ہے جو ان کے سامنے آئی ہو ‘ اور انہوں نے اس سے منہ نہ موڑ لیا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(6,5,'6:5','چناچہ اب جو حق ان کے پاس آیا تو اسے بھی انہوں نے جھٹلا دیا ۔ اچھا ‘ جس چیز کا وہ اب تک مذاق اڑاتے رہے ہیں عنقریب اس کے متعلق کچھ چیزیں انہیں پہنچیں گی ۔');
INSERT INTO "sayyid_qutb" VALUES(6,6,'6:6','کیا انہوں نے دیکھا نہیں کہ ان سے پہلے کتنی ایسی قوموں کو ہم ہلاک کرچکے ہیں جن کا اپنے زمانے میں دور دورہ رہا ہے ؟ ان کو ہم نے زمین میں وہ اقتدار بخشا جو تمہیں نہیں بخشا ‘ ان پر ہم نے آسمان سے خوب بارشیں برسائیں اور ان کے نیچے نہریں بہا دیں (مگر انہوں نے کفران نعمت کیا) تو آخر کار ہم نے ان کے گناہوں کی پاداش میں انہیں تباہ کردیا اور ان کی جگہ دوسرے دور کی قوموں کو اٹھایا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,7,'6:7','اے پیغمبر صلى الله عليه وسلم اگر ہم تمہارے اوپر کوئی کاغذ پر لکھی ہوئی کتاب بھی اتار دیتے اور لوگ اسے اپنے ہاتھوں سے چھو کر بھی دیکھ لیتے تب بھی جنہوں نے حق کا انکار کیا وہ یہی کہتے کہ یہ تو صریح جادو ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,8,'6:8','کہتے ہیں کہ اس نبی پر کوئی فرشتہ کیوں نہیں اتارا گیا ۔ اگر کہیں ہم نے فرشتہ اتار دیا ہوتا تو اب تک کبھی کا فیصلہ ہوچکا ہوتا ‘ پھر انہیں کوئی مہلت نہ دی جاتی ۔');
INSERT INTO "sayyid_qutb" VALUES(6,9,'6:9','اور اگر ہم فرشتہ اتارتے تب بھی اسے انسانی شکل میں اتارتے اور اس طرح انہیں اسی شبہ میں مبتلا کردیتے جس میں اب یہ مبتلا ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,10,'6:10','اے نبی صلى الله عليه وسلم تم سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا جا چکا ہے ‘ مگر ان مذاق اڑانے والوں پر آخر کار وہی حقیقت مسلط ہو کر رہی جس کا وہ مذاق اڑاتے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,11,'6:11','ان سے کہو ‘ ذرا زمین پر چل پھر کر دیکھو جھٹلانے والوں کا کیا انجام ہوا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,12,'6:12','ان سے پوچھو ‘ آسمان اور زمین میں جو کچھ ہے وہ کس کا ہے کہو سب کچھ اللہ کا ہے ‘ اس نے رحم وکرم کا شیوہ اپنے اوپر لازم کرلیا ہے ۔ (اسی لئے وہ نافرمانیوں اور سرکشیوں پر تمہیں جلدی سے نہیں پکڑ لیتا) قیامت کے روز وہ تم سب کو ضرور جمع کرے گا ‘ یہ بالکل ایک غیر مشتبہ حقیقت ہے ۔ مگر جن لوگوں نے اپنے آپ کو خود تباہی کے خطرے میں مبتلا کرلیا ہے وہ اسے نہیں مانتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,13,'6:13','رات کے اندھیرے اور دن کے اجالے میں جو کچھ ٹھہرا ہوا ہے ‘ سب اللہ کا ہے اور وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(6,14,'6:14','کہو ‘ اللہ کو چھوڑ کر کیا میں کسی اور کو اپنا سرپرست بنا لوں ؟ اس خدا کو چھوڑ کر جو زمین وآسمانوں کا خالق ہے اور جو روزی دیتا ہے اور روزی لیتا نہیں ؟ کہو ‘ مجھے تو یہی حکم دیا گیا ہے کہ سب سے پہلے میں اس کے آگے سرتسلیم خم کروں اور (تاکید کی گئی ہے کہ کوئی شرک کرتا ہے تو کرے) تو بہر حال مشرکوں میں شامل نہ ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,15,'6:15','کہو ‘ اگر میں اپنے رب کی نافرمانی کروں تو ڈرتا ہوں کہ ایک بڑے (خوفناک) دن مجھے سزا بھگتنی پڑے گی ۔');
INSERT INTO "sayyid_qutb" VALUES(6,16,'6:16','اس دن جو سزا سے بچ گیا اس پر اللہ نے بڑا ہی رحم کیا اور یہی نمایاں کامیابی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,17,'6:17','اگر اللہ تمہیں کسی قسم کا نقصان پہنچائے تو اس کے سوا کوئی نہیں جو تمہیں اس نقصان سے بچا سکے ‘ اور اگر وہ تمہیں کسی بھلائی سے بہرہ مند کرے تو وہ ہر چیز پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,18,'6:18','وہ اپنے بندوں پر کامل اختیارات رکھتا ہے اور وہ دانا اور باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,19,'6:19','ان سے پوچھو کس کی گواہی بڑھ کر ہے ؟ کہو میرے اور تمہارے درمیان اللہ گواہ ہے ۔ یہ قرآن میری طرف بذریعہ وحی بھیجا گیا ہے تاکہ تمہیں اور جس جس کو یہ پہنچے ‘ سب کو متنبہ کر دوں ۔ کیا تم لوگ واقعی یہ شہادت دے سکتے ہو کہ اللہ کے ساتھ دوسرے خدا بھی شریک ہیں ؟ کہو میں تو اس کی شہادت ہر گز نہیں دے سکتا ۔ کہو خدا تو وہی ہے اور میں اس شرک سے قطعی بیزار ہوں جس میں تم مبتلا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(6,20,'6:20','جن لوگوں کو ہم نے کتاب دی ہے وہ اس بات کو اس طرح غیر مشتبہ طور پر پہچانتے ہیں جیسے ان کو اپنے بیٹوں کے پہچاننے میں کوئی اشتباہ پیش نہیں آتا ۔ مگر جنہوں نے اپنے آپ کو خود خسارے میں ڈال دیا ہے وہ اسے نہیں مانتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,21,'6:21','اور اس شخص سے بڑھ کر ظالم کون ہوگا جو اللہ جھوٹا بہتان لگائے ‘ یا اللہ کی نشانیوں کو جھٹلائے ؟ یقینا ایسے ظالم کبھی فلاح نہیں پاسکتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,22,'6:22','جس روز ہم ان کو اکٹھا کریں گے اور مشرکین سے پوچھیں گے کہ اب وہ تمہارے ٹھہرائے ہوئے شریک کہاں ہیں جن کو تم خدا سمجھتے تھے تو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,23,'6:23','وہ اس کے سوا کوئی فتنہ نہ اٹھا سکیں گے کہ اے ہمارے آقا ! تیری قسم ہم ہر گز مشرک نہ تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,24,'6:24','دیکھو اس وقت یہ کس طرح اپنے اوپر آپ جھوٹ گھڑیں گے اور وہاں ان کے سارے بناوٹی معبود گم ہوگئے۔');
INSERT INTO "sayyid_qutb" VALUES(6,25,'6:25','ان میں سے بعض لوگ ایسے ہیں جو کان لگا کر تمہاری بات سنتے ہیں مگر حال یہ ہے کہ ہم نے ان کے دلوں پردے ڈال رکھے ہیں جن کی وجہ سے وہ اس کو کچھ نہیں سمجھتے اور ان کے کانوں میں گرانی ڈال دی ہے ۔ (کہ سب کچھ سننے پر بھی کچھ نہیں سنتے) وہ خواہ کوئی نشانی دیکھ لیں ‘ اس پر ایمان لا کر نہ دیں گے ۔ حد یہ ہے کہ جب وہ تمہارے پاس آکر تم سے جھگڑتے ہیں تو ان میں سے جن لوگوں نے انکار کا فیصلہ کرلیا ہے وہ (ساری باتیں سننے کے بعد) یہی کہتے ہیں کہ یہ ایک داستان پارینہ کے سوا کچھ نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,26,'6:26','وہ اس امر حق کو قبول کرنے سے لوگوں کو روکتے ہیں اور خود بھی اس سے دور بھاگتے ہیں (وہ سمجھتے ہیں کہ اس حرکت سے وہ تمہارا کچھ بگاڑ رہے ہیں) حالانکہ دراصل وہ خود اپنی ہی تباہی کا سامان کر رہے ہیں مگر انہیں اس کا شعور نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,27,'6:27','کاش تم اس وقت ان کی حالت دیکھ سکتے کہ جب وہ دوزخ کے کنارے کھڑے کئے جائیں گے ۔ اس وقت وہ کہیں گے کہ کاش کوئی صورت ایسی ہو کہ ہم دنیا میں پھر واپس بھیجے جائیں اور اپنے رب کی نشانیوں کو نہ جھٹلائیں اور ایمان لانے والوں میں شامل ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,28,'6:28','درحقیقت یہ بات وہ محض اس وجہ سے کہیں گے کہ جس حقیقت پر انہوں نے پردہ ڈال رکھا تھا وہ اس وقت بےنقاب ہو کر ان کے سامنے آچکی ہوگی ‘ ورنہ اگر انہیں سابق زندگی کی طرف واپس بھیجا جائے تو پھر وہی سب کچھ کریں گے جس سے انہیں منع کیا گیا ہے ‘ وہ تو ہیں ہی جھوٹے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,29,'6:29','آج یہ لوگ کہتے ہیں کہ زندگی جو کچھ بھی ہے بس یہی دنیا کی زندگی ہے اور ہم مرنے کے بعد ہر گز دوبارہ نہ اٹھائے جائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,30,'6:30','کاش وہ منظر تم دیکھ سکو جب یہ اپنے رب کے سامنے کھڑے کئے جائیں گے ۔ اس وقت ان کا رب ان سے پوچھے گا ” کیا یہ حقیقت نہیں ہے ؟ “ یہ کہیں گے ” ہاں ہمارے رب ! یہ حقیقت ہی ہے ۔ “ وہ فرمائے گا ” اچھا تو اپنے انکار کی پاداش میں عذاب کا مزہ چکھو');
INSERT INTO "sayyid_qutb" VALUES(6,31,'6:31','نقصان میں پڑگئے وہ لوگ جنہوں نے اللہ سے اپنی ملاقات کی اطلاع کو جھوٹ قرار دیا ۔ جب اچانک وہ گھڑی آجائے گی تو یہی لوگ کہیں گے ” افسوس ! ہم سے اس معاملے میں کیسی تقصیر ہوئی “۔ اور ان کا حال یہ ہوگا کہ اپنی پیٹھوں پر اپنے گناہوں کا بوجھ لادے ہوئے ہوں گے ۔ دیکھو کیسا برا بوجھ ہے جو یہ اٹھا رہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,32,'6:32','دنیا کی زندگی تو ایک کھیل اور تماشا ہے ‘ حقیقت میں آخرت کا مقام ان لوگوں کے لئے بہتر ہے جو زیاں کاری سے بچنا چاہتے ہیں ۔ پھر کیا تم لوگ عقل سے کام نہ لوگے ؟۔');
INSERT INTO "sayyid_qutb" VALUES(6,33,'6:33','اے نبی صلى الله عليه وسلم ہمیں معلوم ہے کہ جو باتیں یہ لوگ بناتے ہیں ان سے تمہیں رنج ہوتا ہے لیکن یہ لوگ تمہیں نہیں جھٹلاتے بلکہ یہ ظالم دراصل اللہ کی آیات کا انکار کر رہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,34,'6:34','تم سے پہلے بھی بہت سے رسول جھٹلائے جا چکے ہیں مگر اس تکذیب پر اور ان اذیتوں پر جو انہیں پہنچائی گئیں انہوں نے صبر کیا ‘ یہاں تک کہ انہیں ہماری مدد پہنچ گئی ۔ اللہ کی باتوں کو بدلنے کی طاقت کسی میں نہیں ہے اور پچھلے رسولوں کے ساتھ جو کچھ پیش آیا اس کی خبریں تمہیں پہنچ چکی ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,35,'6:35','تاہم اگر ان لوگوں کی بےرخی تم سے برداشت نہیں ہوتی تو اگر تم میں کچھ زور ہے تو زمین میں کوئی سرنگ ڈھونڈو یا آسمان میں سیڑھی لگاؤ اور ان کے پاس کوئی نشانی لانے کی کوشش کرو۔ اگر اللہ چاہتا تو سب کو ہدایت پر جمع کرسکتا تھا لہذا نادان مت بنو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,36,'6:36','دعوت حق پر لبیک وہی کہتے ہیں جو سننے والے ہیں ‘ رہے مردے تو انہیں تو بس اللہ قبروں سے اٹھائے گا اور پھر وہ (اس کی عدالت میں پیش ہونے کی کے لئے) واپس لائے جائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,37,'6:37','یہ لوگ کہتے ہیں کہ اس نبی پر اس کے رب کی طرف سے کوئی نشانی کیوں نہ اتاری گئی ؟ کہو ‘ اللہ تعالیٰ نشانی اتارنے کی پوری قدرت رکھتا ہے ‘ مگر ان میں سے اکثر لوگ نادانی میں مبتلا ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,38,'6:38','زمین میں چلنے والے کسی جانور اور ہوا میں پروں سے اڑنے والے کسی پرندے کو دیکھ لو ‘ یہ سب تمہاری ہی طرح کی انواع ہیں ‘ ہم نے ان کی تقدیر کے نوشتے میں میں کوئی کسر نہیں چھوڑی ہے ‘ پھر یہ سب اپنے رب کی طرف سمیٹے جاتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,39,'6:39','مگر جو لوگ ہماری نشانیوں کو جھٹلاتے ہیں وہ بہرے اور گونگے ہیں ‘ تاریکیوں میں پڑے ہوئے ہیں ۔ اللہ جسے چاہتا ہے بھٹکا دیتا ہے اور جسے چاہتا ہے سیدھے رستے پر لگا دیتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,40,'6:40','ان سے کہو ‘ ذرا غور کرکے بتا ‘ اگر تم پر اللہ کی طرف سے کوئی بڑی مصیبت آجاتی یا آخری گھڑی آپہنچتی ہے تو کیا اس وقت تم اللہ کے سوا کسی اور کو پکارتے ہو ؟ بولو اگر تم سچے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,41,'6:41','اس وقت تم اللہ ہی کو پکارتے ہو ۔ پھر اگر وہ چاہتا ہے تو اس مصیبت کو تم پر سے ٹال دیتا ہے ۔ ایسے موقعوں پر تم اپنے ٹھہرائے ہوئے شریکوں کو بھول جاتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(6,42,'6:42','تم سے پہلے بہت سی قوموں کی طرف ہم نے رسول بھیجے اور ان قوموں کو مصائب وآلام میں مبتلا کیا تاکہ وہ عاجزی کے ساتھ جھک جائیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,43,'6:43','پس جب ہماری طرف سے ان پر سختی آئی تو کیوں نہ انہوں نے عاجزی اختیار کی ؟ مگر ان کے دل تو وہ سخت ہوگئے اور شیطان نے ان کو اطمینان دلایا کہ جو کچھ تم کر رہے ہو خوب کر رہے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,44,'6:44','پھر جب انہوں نے اس نصیحت کو جو انہیں کی گئی تھی ‘ بھلا دیا تو ہم نے ہر طرح کی خوشحالیوں کے دروازے ان کے لئے کھول دیئے ‘ یہاں تک کہ جب ان بخششوں میں جو انہیں عطا کی گئی تھیں وہ خوب مگن ہوگئے تو اچانک ہم نے انہیں پکڑ لیا اور اب حال یہ تھا کہ ہو ہر چیز سے مایوس تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,45,'6:45','اس طرح ان لوگوں کی جڑ کاٹ کر رکھ دی گئی جنہوں نے ظلم کیا تھا ، اور تعریف اللہ رب العالمین کے لئے ہے ۔ (کہ اس نے ان کی جڑ کاٹ دی )');
INSERT INTO "sayyid_qutb" VALUES(6,46,'6:46','اے نبی صلى الله عليه وسلم ان سے کہو کبھی تم نے یہ سوچا کہ اگر اللہ تعالیٰ تمہاری بینائی اور سماعت تم سے چھین لے اور تمہارے دلوں پر مہر کر دے تو اللہ کے سوا اور کون سا خدا ہے جو یہ قوتیں تمہیں واپس دلا سکتا ہے ۔ دیکھو کس طرح ہم بار بار اپنی نشانیاں ان کے سامنے پیش کرتے ہیں اور پھر یہ کس طرح ان سے نظر چراتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,47,'6:47','کہو کبھی تم نے سوچا کہ اگر اللہ کی طرف سے اچانک یا اعلانیہ تم پر عذاب آجائے تو کیا ظالم لوگوں کے سوا کوئی اور ہلاک ہوگا ؟ ۔');
INSERT INTO "sayyid_qutb" VALUES(6,48,'6:48','ہم جو رسول بھیجتے ہیں اسی لئے تو بھیجتے ہیں کہ وہ نیک کردار لوگوں کے لئے خوشخبری دینے والے اور بدکرداروں کے لئے ڈرانے والے ہوں ۔ پھر جو لوگ ان کی بات مان لیں اور اپنے طرز عمل کی اصلاح کرلیں ان کے لئے کسی خوف اور رنج کا موقع نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,49,'6:49','اور جو ہماری آیات کو جھٹلائیں وہ اپنی نافرمانیوں کی پاداش میں سزا بھگت کر رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,50,'6:50','ے نبی صلى الله عليه وسلم ان سے کہو ‘ میں تم سے یہ نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں ۔ نہ میں غیب کا علم رکھتا ہوں ‘ اور نہ یہ کہتا ہوں کہ میں فرشتہ ہوں ۔ میں تو صرف اس وحی کی پیروی کرتا ہوں جو مجھ پر نازل کی جاتی ہے ۔ “ پھر ان سے پوچھو ” کیا اندھا اور آنکھوں والا دونوں برابر ہوسکتے ہیں ؟ کیا تم غور نہیں کرتے ؟');
INSERT INTO "sayyid_qutb" VALUES(6,51,'6:51','اور اے نبی صلى الله عليه وسلم تم اس (علم وحی) کے ذریعے سے ان لوگوں کو نصیحت کرو جو اس کا خوف رکھتے ہیں کہ اپنے رب کے سامنے کبھی اس حال میں پیش کئے جائیں گے کہ اس کے سوا وہاں کوئی (ایسا ذی اقتدار) نہ ہوگا جو ان کا حامی و مددگار ہو یا ان کی سفارش کرے ‘ شاید کہ (اس نصیحت سے متنبہ ہو کر) وہ خدا ترسی کی روش اختیار کرلیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,52,'6:52','اور جو لوگ اپنے رب کو رات دن پکارتے رہتے ہیں اور اس کی خوشنودی کی طلب میں لگے ہوئے ہیں انہیں اپنے سے دور نہ پھینکو ۔ ان کے حساب میں سے کسی چیز کا بار تم پر نہیں ہے اور تمہارے حساب میں سے کی چیز کا بار ان پر نہیں ۔ اس پر بھی اگر تم انہیں دور پھینکو گے تو ظالموں میں شمار ہو گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,53,'6:53','دراصل ہم نے اس طرح ان لوگوں میں سے بعض کو بعض کے ذریعے آزمائش میں ڈالا ہے ۔ تاکہ وہ انہیں دیکھ کر کہیں ” کیا یہ ہیں وہ لوگ جن پر ہمارے درمیان اللہ کا فضل وکرم ہوا ہے ؟ “ ۔۔۔۔۔۔۔۔۔۔۔۔۔۔ ہاں ! کیا خدا اپنے شکر گزار بندوں کو ان سے زیادہ نہیں جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(6,54,'6:54','جب تمہارے پاس وہ لوگ آئیں جو ہماری آیات پر ایمان لاتے ہیں تو ان کو کہو ” تم پر سلامتی ہے ۔ تمہارے رب نے رحم وکرم کا شیوہ اپنے اوپر لازم کرلیا ہے ۔ (یہ اس کا رحم وکرم ہی ہے کہ) اگر تم میں سے کوئی نادانی کے ساتھ کسی برائی کا ارتکاب کر بیٹھا ہو پھر اس کے بعد توبہ کرے اور اصلاح کرلے تو وہ اسے معاف کردیتا ہے اور نرمی سے کام لیتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(6,55,'6:55','اور اس طرح ہم اپنی نشانیاں کھول کھول کر پیش کرتے ہیں تاکہ مجرموں کی راہ بالکل نمایاں ہوجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,56,'6:56','اے نبی صلى الله عليه وسلم ان سے کہو ” تم لوگ اللہ کے سوا جن دوسروں کو پکارتے ہو ان کی بندگی کرنے سے مجھے منع کیا گیا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,57,'6:57','کہو : ” میں تمہاری خواہشات کی پیروی نہیں کروں گا ‘ اگر میں نے ایسا کیا تو گمراہ ہوگیا ‘ راہ راست پانے والوں میں سے نہ رہا ۔ “ کہو ” میں اپنے رب کی طرف سے ایک دلیل روشن پر قائم ہوں اور تم نے اسے جھٹلا دیا ہے ۔ اب میرے اختیار میں وہ چیز ہے نہیں جس کے لئے تم جلدی مچا رہے ہو ‘ فیصلے کا سارا اختیار اللہ کو ہے ‘ وہی امر حق بیان کرتا ہے اور وہی بہترین فیصلہ کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(6,58,'6:58','کہو : اگر کہیں وہ چیز میرے اختیار میں ہوتی جس کے لئے تم جلدی مچا رہے ہو تو میرے اور تمہارے درمیان کبھی کا فیصلہ ہوچکا ہوتا ۔ مگر اللہ زیادہ بہتر جانتا ہے کہ ظالموں کے ساتھ کیا معاملہ کیا جانا چاہئے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,59,'6:59','اسی کے پاس غیب کی کنجیاں ہیں جنہیں اس کے سوا کوئی نہیں جانتا ۔ بحروبر میں جو کچھ ہے سب سے وہ واقف ہے ‘ درخت سے گرنے والا کوئی پتا ایسا نہیں ہے جس کا اسے علم نہ ہو ‘ زمین کے تاریک پردوں میں کوئی دانہ ایسا نہیں ہے جس سے وہ باخبر نہ ہو ‘ خشک وتر سب کچھ ایک کھلی کتاب میں لکھا ہوا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,60,'6:60','وہی ہے جو رات کو تمہاری روحیں قبض کرتا ہے اور دن کو جو کچھ تم کرتے ہو اسے جانتا ہے ۔ پھر دوسرے روز وہ تمہیں اسی کاروبار کے عالم میں واپس بھیج دیتا ہے تاکہ زندگی کی مقرر مدت پوری ہو ‘ آخر کار اسی کی طرف تمہاری واپسی ہے ‘ پھر وہ تمہیں بتا دے گا کہ تم کیا کرتے رہے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,61,'6:61','اپنے بندوں پر وہ پوری قدرت رکھتا ہے اور تم پر نگرانی کرنے والے مقرر کر کے بھیجتا ہے یہاں تک کہ جب تم میں سے کسی کی موت کا وقت آجاتا ہے تو اس کے بھیجے ہوئے فرشتے اس کی جان نکال لیتے ہیں اور اپنا فرض انجام دیتے ہیں اور ذرا کوتاہی نہیں کرتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,62,'6:62','پھر سب کے سب اللہ اپنے حقیقی آقا کی طرف واپس جاتے ہیں ۔ خبردار ہوجاؤ کہ سارے اختیارات اس کو حاصل ہیں اور وہ حساب لینے میں بہت تیز ہے');
INSERT INTO "sayyid_qutb" VALUES(6,63,'6:63','(اے نبی صلى الله عليه وسلم ان سے پوچھو ‘ صحرا اور سمندر کی تاریکیوں میں کون تمہیں خطرات سے بچاتا ہے ‘ کون ہے جس سے تم (مصیبت کے وقت) گڑ گڑا کر اور چپکے چپکے دعائیں مانگتے ہو ؟ کس سے کہتے ہو کہ اگر اس بلا سے اس نے ہم کو بچا لیا تو ہم ضرور شکر گزار ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(6,64,'6:64','کہو ‘ اللہ تمہیں اس سے اور ہر تکلیف سے نجات دیتا ہے ۔ پھر تم دوسروں کو اس کا شریک ٹھہراتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,65,'6:65','کہو ” وہ اس پر قادر ہے کہ تم پر کوئی عذاب اوپر سے نازل کر دے ‘ یا تمہارے قدموں کے نیچے سے برپا کر دے ‘ یا تمہیں گروہوں میں تقسیم کرکے ایک گروہ کو دوسرے گروہ کی طاقت کا مزہ چکھوا دے ۔ “ دیکھو ہم کس طرح بار بار مختلف طریقوں سے اپنی نشانیاں ان کے سامنے پیش کر رہے ہیں شاید کہ یہ حقیقت کو سمجھ لیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,66,'6:66','اور تمہاری قوم نے اس کا انکار کردیا ہے حالانکہ وہ حقیقت ہے ۔ ان سے کہہ دو کہ تم پر حوالہ دار نہیں بنایا گیا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,67,'6:67','ہر خبر کے ظہور میں آنے کا ایک وقت مقرر ہے ‘ عنقریب تم کو خود انجام معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(6,68,'6:68','اور اے نبی صلى الله عليه وسلم جب تم دیکھو کہ لوگ ہماری آیات پر نکتہ چینیاں کر رہے ہیں تو ان کے پاس سے ہٹ جاؤ ‘ یہاں تک کہ وہ اس گفتگو کر چھوڑ کر دوسری باتوں میں لگ جائیں ۔ اور اگر کبھی شیطان تمہیں بہلاوے میں ڈال دے تو جس وقت تمہیں اس غلطی کا احساس ہوجائے اس کے بعد پھر ایسے ظالم لوگوں کے پاس نہ بیٹھو');
INSERT INTO "sayyid_qutb" VALUES(6,69,'6:69','ان کے حساب میں سے کسی چیز کی ذمہ داری پرہیزگار لوگوں پر نہیں ہے ‘ البتہ نصیحت کرنا ان کا فرض ہے شاید کہ وہ غلط روی سے بچ جائیں');
INSERT INTO "sayyid_qutb" VALUES(6,70,'6:70','چھوڑو ان لوگوں کو جنہوں نے اپنے دین کو کھیل اور تماشابنا رکھا ہے اور جنہیں دنیا کی زندگی فریب میں مبتلا ہوئے ہے ۔ ہاں مگر یہ قرآن سنا کر نصیحت اور متنبہ کرتے رہو کہ کہیں کوئی شخص اپنے کئے ہوئے کرتوتوں کے وبال میں گرفتار نہ ہوجائے ‘ اور اگر گرفتار بھی اس حال میں ہو کہ اللہ سے بچانے والا کوئی حامی ومدد گار اور کوئی سفارشی اس کے لئے نہ ہو ‘ اور اگر وہ ہر ممکن چیز فدیہ میں دے کر چھوٹنا چاہے تو وہ بھی اس سے قبول نہ کی جائے ، کیونکہ ایسے لوگ تو خود اپنی کمائی کے نتیجے میں پکڑے جائیں گے ‘ ان کو تو اپنے انکار حق کے معاوضہ میں کھولتا ہوا پانی پینے کو اور دردناک عذاب بھگتنے کو ملے گا');
INSERT INTO "sayyid_qutb" VALUES(6,71,'6:71','اے نبی صلى الله عليه وسلم ان سے پوچھو کیا ہم اللہ کو چھوڑ کر ان کو پکاریں جو نہ ہمیں نفع دے سکتے ہیں نہ نقصان ؟ اور جب اللہ ہمیں سیدھا راستہ دکھا چکا ہے تو کیا اب ہم الٹے پاؤں پھرجائیں ؟ کیا ہم اپنا حال اس شخص کا سا کرلیں جسے شیطانوں نے صحرا میں بھٹکا دیا ہو اور وہ حیران و سرگرداں پھر رہا ہو ‘ درآں حالیکہ اس کے ساتھی اسے پکار رہے ہوں کہ ادھر آؤ ‘ سیدھی راہ موجود ہے ؟ کہو ” حقیقت میں صحیح راہنمائی تو صرف اللہ ہی کی رہنمائی ہے اور اس کی طرف سے ہمیں یہ حکم ملا ہے کہ مالک کائنات کے آگے سراطاعت خم کر دو “');
INSERT INTO "sayyid_qutb" VALUES(6,72,'6:72','نماز قائم کرو اور اس کی نافرمانی سے بچو۔ اسی کی طرف تم سمیٹے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(6,73,'6:73','وہی ہے جس نے آسمان و زمین کو برحق پیدا کیا ہے اور جس دن وہ کہے گا کہ حشر ہوجائے اسی دن وہ ہوجائے گا اس کا ارشاد عین حق ہے ۔ اور جس روز صور پھونکا جائے گا اس روز بادشاہی اسی کی ہوگی ‘ وہ غیب اور شہادت ہر چیز کا عالم ہے اور دانا اور باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,74,'6:74','اور ابراہیم کا واقعہ یاد کرو جب اس نے اپنے آزر سے کہا تھا : کیا تو بتوں کو خدا بناتا ہے ؟ میں تو تجھے اور تیری قوم کو کھلی گمرائی میں پاتا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,75,'6:75','ابراہیم کو ہم اسی طرح زمین و آسمان کا نظام سلطنت دکھاتے تھے اور اس لئے دکھاتے تھے کہ وہ یقین کرنے والوں میں سے ہوجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,76,'6:76','چناچہ جب رات اس پر طاری ہوئی تو اس نے ایک تارا دیکھا ۔ کہا یہ میرا رب ہے ؟ مگر وہ ڈوب گیا تو بولا ڈوب جانے والوں کا میں گرویدہ نہیں ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,77,'6:77','پھر جب چاند چمکتا نظر آیا تو کہا یہ میرا رب ہے ‘ مگر جب وہ بھی ڈوب گیا تو کہا اگر میرے رب نے میری راہنمائی نہ کی ہوتی تو میں بھی گمراہ لوگوں میں شامل ہوگیا ہوتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,78,'6:78','پھر جب سورج کو روشن دیکھا تو کہا یہ ہے میرا رب ہے یہ سب سے بڑا ہے ۔ مگر جب وہ بھی ڈوبا تو ابراہیم پکار اٹھا ” اے برادران قوم ! میں ان سب سے بیزار ہوں جنہیں تم خدا کا شریک ٹھہراتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,79,'6:79','میں یکسو ہو کر اپنا رخ اس ہستی کی طرف کرلیا جس نے زمین اور آسمان کو پیدا کیا ہے اور میں ہر گز شرک کرنے والوں میں سے نہیں ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(6,80,'6:80','اس کی قوم اس سے جھگڑنے لگی تو اس نے کہا ” کیا تم لوگ اللہ کے معاملے میں مجھ سے جھگڑتے ہو ؟ حالانکہ اس نے مجھے راہ راست دکھا دی ہے اور تمہارے ٹھہرائے ہوئے شریکوں سے نہیں ڈرتا ہاں اگر میرا رب کچھ چاہئے تو ضرور ہوسکتا ہے ، میرا رب کا علم ہر چیز پرچھایا ہوا ہے ‘ پھر کیا تم ہوش میں نہ آؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(6,81,'6:81','اور آخر میں تمہارے ٹھہرائے ہوئے شریکوں سے کیسے ڈرو جبکہ تم اللہ کے ساتھ ان چیزوں کو خدائی میں شریک بناتے ہوئے نہیں ڈرتے جن کے لئے اس نے تم پر کوئی سند نازل نہیں کی ؟ ہم دونوں فریقوں میں سے کون زیادہ بےخوفی اور اطمینان کا مستحق ہے بتاؤ اگر تم علم رکھتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,82,'6:82','حقیقت میں تو امن انہی کے لئے ہے اور راہ راست پر وہی ہیں جو ایمان لائے اور جنہوں نے اپنے ایمان کو ظلم کے ساتھ آلودہ نہیں کیا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,83,'6:83','یہ تھی ہماری وہ حجت جو ہم نے ابراہیم کو اس کی قوم کے مقابلہ میں عطا کی ۔ ہم جسے چاہتے ہیں بلند مرتبے عطا کرتے ہیں ۔ حق یہ ہے کہ تمہارا رب نہایت دانا اور علیم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,84,'6:84','پھر ہم نے ابراہیم (علیہ السلام) کو اسحاق (علیہ السلام) اور یعقوب (علیہ السلام) جیسی اولاد دی اور ہر ایک کو راہ راست دکھائی (وہی راہ راست جو) اس سے پہلے نوح (علیہ السلام) کو دکھائی تھی ۔ اور اس کی نسل سے ہم نے داؤد (علیہ السلام) ‘ سلیمان (علیہ السلام) ایوب (علیہ السلام) یوسف (علیہ السلام) موسیٰ (علیہ السلام) اور ہارون (علیہ السلام) کو (ہدایت بخشی) ۔ اسی طرح ہم نیکوکاروں کو ان کی نیکی کا بدلہ دیتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,85,'6:85','(اسی کی اولاد سے) زکریا (علیہ السلام) ‘ یحی (علیہ السلام) عیسیٰ (علیہ السلام) اور الیاس (علیہ السلام) کو (راہ یاب کیا) ہر ایک ان میں سے صالح تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,86,'6:86','(اسی کے خاندان سے) اسماعیل (علیہ السلام) ‘ الیسع (علیہ السلام) ‘ اور یونس (علیہ السلام) اور لوط (علیہ السلام) کو (راستہ دکھایا) ۔ ان میں سے ہر یک کو ہم نے تمام دیناوالوں پر فضیلت عطا کی ۔');
INSERT INTO "sayyid_qutb" VALUES(6,87,'6:87','نیز ان کے آباؤ اجداد اور انکی اولاد اور ان کے بھائی بندوں میں سے بہتوں کو ہم نے نوازا ۔ انہیں اپنی خدمت کے لئے چن لیا اور سیدھے راستے کی طرف ان کی رہنمائی کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,88,'6:88','لیکن اگر کہیں ان لوگوں نے شرک کیا ہوتا تو ان کا سب کیا کرایا غارت ہوجاتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,89,'6:89','یہ وہ لوگ تھے جن کو ہم نے کتاب اور حکم اور نبوت عطا کی تھی ۔ اب اگر یہ لوگ اس کو ماننے سے انکار کرتے ہیں تو (پرواہ نہیں) ہم نے کچھ اور لوگوں کو یہ نعمت سونپ دی ہے جو اس سے منکر نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,90,'6:90','اے نبی صلى الله عليه وسلم وہی لوگ اللہ کی طرف سے ہدایت یافتہ تھے ‘ انہی کے راستہ پر تم چلو اور کہہ دو کہ میں (اس تبلیغ وہدایت کے) کام پر تم سے کسی اجر کا طالب نہیں ہوں ‘ یہ تو ایک عام نصیحت ہے تمام دنیا والوں کے لئے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,91,'6:91','ان لوگوں نے اللہ کا بہت غلط اندازہ لگایا جب کہا کہ اللہ نے کسی بشر پر کچھ نازل نہیں کیا ۔ ان سے پوچھو ‘ پھر وہ کتاب جسے موسیٰ (علیہ السلام) لایا تھا ‘ جو تمام انسانوں کے لئے روشنی اور ہدایت تھی ‘ جسے تم پارہ پارہ کر کے رکھتے ہو ‘ کچھ دکھاتے ہو اور بہت کچھ چھپا جاتے ہو ‘ اور جس کے ذریعے سے تم کو وہ علم دیا گیا جو نہ تمہیں حاصل تھا اور نہ تمہارے باپ دادا کو ‘ آخر اس کا نازل کرنے والا کون تھا ؟ ۔۔۔۔۔۔۔۔۔۔۔ بس اتنا کہہ دو کہ اللہ ‘ پھر انہیں اپنی دلیل بازیوں سے کھیلنے کے لئے چھوڑ دو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,92,'6:92','(اسی کتاب کی طرح) یہ ایک کتاب ہے جسے ہم نے نازل کیا ہے ۔ بڑی خیر و برکت والی ہے ۔ اس چیز کی تصدیق کرتی ہے جو اس سے پہلے آئی تھی ۔ اور اس لئے نازل کی گئی ہے کہ اس کے ذریعہ سے تم بستیوں کے اس مرکز (یعنی مکہ) اور اس کے اطراف میں رہنے والوں کو متنبہ کرو ۔ جو لوگ آخرت کو مانتے ہیں وہ اس کتاب پر ایمان لاتے ہیں اور ان کا حال یہ ہے کہ اپنی نمازوں کی پابندی کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,93,'6:93','اور اس شخص سے بڑا ظالم اور کون ہوگا جو اللہ پر جھوٹا بہتان گھڑے ‘ کہے کہ مجھ پر وحی آئی درآں حالیکہ اس پر کوئی وحی نازل نہ کی گئی ہو ‘ یا جو اللہ کی نازل کردہ چیز کے مقابلے میں کہے کہ میں بھی ایسی چیز نازل کر کے دکھا دوں گا ؟ کاش تم ظالموں کو اس حالت میں دیکھ سکو جب کہ وہ سکرات موت میں ڈبکیاں کھا رہے ہوتے ہیں اور فرشتے ہاتھ بڑھا بڑھا کر کہہ رہے ہوتے ہیں کہ ” لاؤ“ نکالو اپنی جان ‘ آج تمہیں ان باتوں کی پاداش مین ذلت کا عذاب دیا جائے گا جو تم اللہ پر تہمت رکھ کر ناحق بکا کرتے تھے اور اس کی آیات کے مقابلے میں سرکشی دکھاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(6,94,'6:94','لو اب تم ایسے ہی تن تنہا ہمارے سامنے حاضر ہوگئے جیسا ہم نے تمہیں پہلی مرتبہ اکیلا پیدا کیا تھا ۔ جو کچھ ہم نے تمہیں دنیا میں دیا تھا ‘ وہ سب تم پیچھے چھوڑ آئے ہو ‘ اور اب ہم تمہارے ساتھ تمہارے ان سفارشیوں کو بھی نہیں دیکھتے جن کے متعلق تم سمجھتے تھے کہ تمہارے کام بنانے میں ان کا بھی کچھ حصہ ہے ۔ تمہارے آپس میں سب رابطے ٹوٹ گئے اور وہ سب تم سے گم ہوگئے جن کا تم زعم رکھتے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,95,'6:95','دانے اور گٹھلی کو پھاڑنے والا اللہ ہے ۔ وہی زندہ کو مردہ سے نکالتا ہے اور ہی مردہ کو زندہ سے خارج کرتا ہے ۔ یہ سارے کام کرنے والا تو اللہ ہے ‘ پھر تم کدھر بہکے چلے جا رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(6,96,'6:96','پردہ شب کو چاک کر کے وہی صبح نکالتا ہے ۔ اسی نے رات کو سکون کا وقت بنایا ہے ۔ اسی نے چاند اور سورج کے طلوع و غروب کا حساب مقرر کیا ہے ۔ یہ سب اسی زبر دست قدرت اور علم رکھنے والے کے ٹھہرائے ہوئے اندازے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,97,'6:97','اور وہی ہے جس نے تمہارے لئے تاروں کو صحرا اور سمندر کی تاریکیوں میں راستہ معلوم کرنے کا ذریعہ بنایا ۔ دیکھو ہم نے نشانیاں کھول کر بیان کردی ہیں ‘ ان لوگوں کے لئے جو علم رکھتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,98,'6:98','اور وہی ہے جس نے ایک متنفس سے تم کو پیدا کیا ۔ پھر ہر ایک کے لئے ایک جائے قرار ہے اور ایک اس کے سونپے جانے کی جگہ ۔ یہ نشانیاں ہم نے واضح کردی ہیں ان لوگوں کے لئے جو سمجھ رکھتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,99,'6:99','اور وہی ہے جس نے آسمان سے پانی برسایا ۔ پھر اس کے ذریعے سے ہر قسم کی نباتات اگائیں ۔ پھر اس سے ہرے بھرے کھیت اور درخت پیدا کئے پھر ان سے تہہ بہ تہہ چڑھے ہوئے دانے نکالے اور کھجور کے شگوفوں سے پھولوں کے گچھے پیدا کئے جو بوجھ کے مارے جھکے پڑتے ہیں اور انگور ‘ زیتوں اور انار کے باغ لگائے جن کے پھل ایک دوسرے سے ملتے جلتے بھی ہیں اور پھر ہر ایک کی خصوصیت جدا جدا بھی ہے ۔ یہ درخت جب پھلتے ہیں تو ان میں پھل آنے اور پھر ان کے پکنے کی کیفیت ذرا غور کی نظر سے دیکھو ‘ ان چیزوں میں نشانیاں ہیں ان لوگوں کے لئے جو ایمان لاتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,100,'6:100','اس پر بھی لوگوں نے جنوں کو اللہ کا شریک ٹھہرا دیا حالانکہ وہ خالق ہے اور بےجانے بوجھے اس کے لئے بیٹے اور بیٹیاں تصنیف کردیں ‘ حالانکہ وہ پاک اور بالاتر ہے ان باتوں سے جو یہ لوگ کہتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,101,'6:101','وہ تو آسمان اور زمین کا موجد ہے ‘ اس کا کوئی بیٹا کیسے ہو سکتا ہے جبکہ اس کی کوئی شریک زندگی ہی نہیں ہے ۔ اس نے ہر چیز کو پیدا کیا ہے اور وہ ہر چیز کا علم رکھتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,102,'6:102','یہ ہے اللہ تمہارا رب کوئی خدا ‘ اس کے سوا نہیں ہے ‘ ہر چیز کا خالق ‘ لہذا تم اسی کی بندگی کرو اور وہ ہر چیز کا کفیل ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,103,'6:103','نگاہیں اس کو نہیں پاسکتیں اور وہ نگاہوں کو پا لیتا ہے وہ نہایت باریک بین اور باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,104,'6:104','دیکھو تمہارے پاس تمہارے رب کی طرف سے بصیرت کی روشنیاں آگئی ہیں ‘ اب جو بینائی سے کام لے گا اپنا ہی بھلا کرے گا اور جو اندھا بنے گا خود نقصان اٹھائے گا اور میں تم پر کوئی پاسبان نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(6,105,'6:105','اس طرح ہم اپنی آیات کو بار بار مختلف طریقوں سے بیان کرتے ہیں اور اس لئے کرتے ہیں کہ یہ لوگ کہیں ” تم کسی سے پڑھ کر آئے ہو “ اور جو لوگ علم رکھتے ہیں ان پر ہم حقیقت کو روشن کردیں۔');
INSERT INTO "sayyid_qutb" VALUES(6,106,'6:106','اے نبی صلى الله عليه وسلم اس وحی کی پیروی کئے جاؤ ‘ جو تم پر تمہارے رب کی طرف سے نازل ہوئی ہے کیونکہ اس ایک رب کے سوا کوئی اور خدا نہیں ہے ۔ اور ان مشرکین کے نہ پڑو۔');
INSERT INTO "sayyid_qutb" VALUES(6,107,'6:107','اگر اللہ کی مشیت ہوتی تو (وہ خود ایسا بندوبست کرسکتا تھا کہ) یہ لوگ شرک نہ کرتے ۔ تم کو ہم نے ان پر پاسبان مقرر نہیں کیا ہے اور نہ تم ان پر حوالہ دار ہو');
INSERT INTO "sayyid_qutb" VALUES(6,108,'6:108','اور (اے مسلمانو ! ) یہ لوگ اللہ کے سوا جن کو پکارتے ہیں انہیں گالیاں نہ دو ‘ کہیں ایسا نہ ہو کہ یہ شرک سے آگے بڑھ کر جہالت کی بنا پر اللہ کو گالیاں دینے لگیں ۔ ہم نے تو اسی طرح ہر گروہ کے لئے اس کے عمل کو خوشنما بنا دیا ہے ‘ پھر انہیں اپنے رب ہی کی طرف پلٹ کر آنا ہے ‘ اس وقت وہ انہیں بتا دے گا کہ وہ کیا کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(6,109,'6:109','یہ لوگ کڑی کڑی قسمیں کھا کھا کر کہتے ہیں کہ اگر کوئی نشانی (یعنی معجزہ) ہمارے سامنے آجائے تو ہم اس پر ایمان لے آئیں گے ۔ اے نبی صلى الله عليه وسلم ان سے کہو کہ ” نشانیاں تو اللہ کے اختیار میں ہیں ۔ “ اور تمہیں کیسے سمجھایا جائے کہ اگر نشانیاں آ بھی جائیں تو یہ ایمان لانے والے نہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,110,'6:110','ہم اسی طرح ان کے دلوں اور نگاہوں کو پھیر رہے ہیں جس طرح یہ پہلی مرتبہ اس (کتاب) پر ایمان نہیں لائے تھے ۔ ہم انہیں ان کی سرکشی ہی میں بھٹکنے کے لئے چھوڑے دیتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,111,'6:111','اگر ہم فرشتے بھی ان پر نازل کردیتے اور مردے ان سے باتیں کرتے اور دنیا بھر کی چیزوں کو ہم ان کی آنکھوں کے سامنے جمع کردیتے تب بھی یہ ایمان لانے والے نہ تھے الا یہ کہ مشیت الہی یہی ہو (کہ یہ ایمان لائیں) مگر اکثر لوگ نادانی کی باتیں کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,112,'6:112','اور ہم نے تو اسی طرح ہمیشہ شیطان انسانوں اور شیطان جنوں کو ہر نبی کا دشمن بنایا ہے جو ایک دوسرے پر خوش آئند باتیں دھوکے اور فریب کے طور پر القا کرتے رہے ہیں ۔ اگر تمہارے رب کی مشیت یہ ہوتی کہ وہ ایسا نہ کریں تو وہ کبھی نہ کرتے ۔ پس تم انہیں ان کے حال پر چھوڑ دو کہ اپنی افترا پردازیاں کرتے رہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,113,'6:113','(یہ سب کچھ ہم انہیں اسی لئے کرنے دے رہیں کہ) جو لوگ آخرت پر ایمان نہیں رکھتے انکے دل اس خوشنما دھوکے کیطرف مائل ہوں اور وہ اس سے راضی ہوجائیں اور ان برائیوں کا اکتساب کریں جن کا اکتساب وہ کرنا چاہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(6,114,'6:114','پھر جب حال یہ ہے تو کیا میں اللہ کے سوا کوئی اور فیصلہ کرنے والا تلاش کروں ‘ حالانکہ اس نے پوری تفصیل کے ساتھ تمہاری طرف کتاب نازل کردی ہے ‘ اور جن لوگوں کو ہم نے تم سے پہلے کتاب دی تھی وہ جانتے تھے کہ یہ کتاب تمہارے رب ہی کی طرف سے حق کے ساتھ نازل ہوئی ہے لہذا تم شک کرنے والوں میں سے نہ ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,115,'6:115','تمہارے رب کی بات سچائی اور انصاف کے اعتبار سے کامل ہے ‘ کوئی اس کے فرامین کو تبدیل کرنے والا نہیں ہے اور وہ سب کچھ سنتا اور جانتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,116,'6:116','اور اے نبی صلى الله عليه وسلم اگر تم ان لوگوں کی اکثریت کے کہنے پر چلو جو زمین پر میں بستے ہیں تو وہ تمہیں اللہ کے راستے سے بھٹکا دیں گے ۔ وہ تو بس گمان پر چلتے اور قیاس آرائیاں کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,117,'6:117','درحقیقت تمہارا رب بہتر جانتا ہے کہ کون اس کے راستے سے ہٹا ہوا ہے اور کون سیدھی راہ پر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,118,'6:118','پھر اگر تم لوگ اللہ کی آیات پر ایمان رکھتے ہو تو جس جانور پر اللہ کا نام لیا گیا ہو اس کا گوشت کھاؤ ۔');
INSERT INTO "sayyid_qutb" VALUES(6,119,'6:119','آخر کیا وجہ ہے کہ تم وہ چیز نہ کھاؤ جس پر اللہ کا نام لیا گیا ہو ‘ حالانکہ جن چیزوں کا استعمال حالت اضطرار کے سوا دوسری تمام حالتوں میں اللہ نے حرام کردیا ہے ان کی تفصیل وہ تمہیں بتا چکا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,120,'6:120','تم کھلے گناہوں سے بھی بچو اور چھپنے والے گناہوں سے بھی ‘ جو لوگ گناہ کا اکتساب کرتے ہیں وہ اپنی کمائی کا بدلہ پا کر رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(6,121,'6:121','اور جس جانور کو اللہ کا نام لے کر ذبح نہ کیا گیا ہو اس کا گوشت نہ کھاؤ ‘ ایسا کرنا فسق ہے ۔ شیاطین اپنے ساتھیوں کے دلوں میں شکوک و اعتراضات القاء کرتے ہیں تاکہ وہ تم سے جھگڑا کریں ۔ لیکن اگر تم نے ان کی اطاعت قبول کرلی تو یقینا تم مشرک ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,122,'6:122','کیا وہ شخص جو پہلے مردہ تھا پھر ہم نے اسے زندگی بخشی اور اس کو وہ روشنی عطا کی جس کے اجالے میں وہ لوگوں کے درمیان زندگی کی راہ طے کرتا ہے اس شخص کی طرح ہو سکتا ہے جو تاریکیوں میں پڑا ہوا ہو اور کسی طرح اس سے نہ نکلتا ہو۔ کافروں کے لئے تو اسی طرح ان کے اعمال خوشنما بنادیئے گئے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,123,'6:123','اور اسی طرح ہم نے ہر بستی میں اس کے بڑے بڑے مجرموں کو لگا دیا ہے کہ وہاں اپنے مکروفریب کا جال پھیلائیں ۔ دراصل وہ اپنے فریب کے جال میں خود پھنسے ہیں مگر انہیں اس کا شعور نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,124,'6:124','جب ان کے سامنے کوئی آیت آتی ہے تو وہ کہتے ہی ہم نہ مانیں گے جب تک کہ وہ چیز خود ہم کو نہ دی جائے جو اللہ کے رسولوں کو دی گئی ہے ۔ اللہ زیادہ بہتر جانتا ہے کہ اپنی پیغامبری کا کام کس سے لے اور کس طرح لے ۔ قریب ہے وہ وقت کہ یہ مجرم اپنی مکاریوں کی پاداش میں اللہ کے ہاں ذلت اور سخت عذاب سے دو چار ہوں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,125,'6:125','پس حقیقت یہ ہے کہ اللہ جسے ہدایت بخشنے کا ارادہ کرتا ہے اس کا سینے کو تنگ کردیتا ہے اور ایسا بھینچتا کہ اسے یوں معلوم ہونے لگتا ہے کہ گویا اس کی روح آسمان کی طرف پرواز کر رہی ہے ۔ اس طرح اللہ ناپاکی ان لوگوں پر مسلط کردیتا ہے جو ایمان نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(6,126,'6:126','حالانکہ یہ راستہ تیرے رب کا سیدھا راستہ ہے اور اس کے نشانات ان لوگوں کے لئے واضح کردیئے گئے ہیں جو نصیحت قبول کرتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,127,'6:127','ان کے لئے ان کے رب کے پاس سلامتی کا گھر ہے اور وہ ان کا سرپرست ہے ‘ اس صحیح طرز عمل کی وجہ سے جو انہوں نے اختیار کیا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,128,'6:128','اور جس روز اللہ ان سب لوگوں کو گھیر کر جمع کرے گا ‘ اس روز جنوں سے خطاب کرکے فرمائے گا ” اے گروہ جن ! تم نے نوع انسانی پر خوب ہاتھ صاف کیا ۔ “ انسانوں میں سے جو ان کے رفیق تھے وہ عرض کریں گے پروردگار ! ہم میں سے ہر ایک نے دوسرے کو خوب استعمال کیا ہے ‘ اور اب ہم اس وقت آپہنچے ہیں جو تو نے ہمارے لئے مقرر کردیا ہے ۔ اللہ فرمائے گا ” اچھا اب آگ تمہارا ٹھکانا ہے ‘ اس میں تم ہمیشہ رہو گے ۔ “ اس سے بچیں گے صرف وہی جنہیں اللہ بچانا چاہے گا ‘ بیشک تمہارا رب دانا اور علیم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,129,'6:129','دیکھو اس طرح ہم ظالموں کو ایک دوسرے کا ساتھی بنائیں گے ۔ اس کمائی کی وجہ سے جو وہ کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(6,130,'6:130','اے گروہ جن وانس ! کیا تمہارے پاس خود تم ہی میں سے ہو پیغمبر نہیں آئے تھے جو تم کو میری آیات سناتے اور اس دن کے انجام سے ڈراتے تھے ۔ “ وہ کہیں گے ” ہاں ! ہم اپنے خلاف خود گواہی دیتے ہیں ۔ “ آج دنیا کی زندگی نے ان لوگوں کو دھوکے میں ڈال رکھا ہے ۔ مگر اس وقت وہ خود اپنے خلاف گواہی دیں گے کہ وہ کافر تھے');
INSERT INTO "sayyid_qutb" VALUES(6,131,'6:131','یہ شہادت ان سے اس لئے لی جائے گی کہ ثابت ہوجائے کہ تمہارا رب بستیوں کو ظلم کے ساتھ تباہ کرنے والا نہ تھا جب کہ ان کے باشندے حقیقت سے ناواقف ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,132,'6:132','ہر شخص کا درجہ اس کے عمل کے لحاظ سے ہے اور تمہارا رب لوگوں کے اعمال سے بیخبر نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(6,133,'6:133','تمہارا رب بےنیاز ہے اور مہربانی اس کا شیوہ ہے ۔ اگر وہ چاہے تو تم لوگوں کو لے جائے اور تمہاری جگہ دوسرے جن لوگوں کو چاہے لے آئے ‘ جس طرح اس نے تمہیں کچھ اور لوگوں کی نسل سے اٹھایا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,134,'6:134','تم سے جس چیز کا وعدہ کیا جا رہا ہے وہ یقینا آنے والی ہے اور تم خدا کو عاجز کرنے کی طاقت نہیں رکھتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,135,'6:135','اے نبی صلى الله عليه وسلم کہہ دو کہ لوگو تم اپنی جگہ عمل کرتے رہو اور میں بھی اپنی جگہ عمل کر رہا ہوں ‘ عنقریب تمہیں معلوم ہوجائے گا کہ انجام کار کس کے حق میں بہتر ہوتا ہے ۔ بہرحال یہ حقیقت ہے کہ ظالم کبھی فلاح نہیں پاسکتے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,136,'6:136','انہوں نے اللہ کے لئے خود اس کی پیدا کی ہوئی کھیتوں اور مویشیوں میں سے ایک حصہ مقرر کیا ہے ‘ اور کہتے ہیں یہ اللہ کیلئے ہے بزعم خود ‘ اور یہ ہمارے ٹھہرائے ہوئے شریکوں کے لئے ہے ۔ پھر جو حصہ ان کے ٹھہرائے ہوئے شریکوں کے لئے ہے وہ تو اللہ کو نہیں پہنچتا مگر جو اللہ کے لئے ہے وہ ان کے شریکوں کو پہنچ جاتا ہے کیسے برے فیصلے کرتے ہیں یہ لوگ ۔');
INSERT INTO "sayyid_qutb" VALUES(6,137,'6:137','اور اسی طرح بہت سے مشرکوں کے لئے ان کے شریکوں نے اولاد کے قتل کو خوشنما بنایا ہے تاکہ ان کو ہلاکت میں مبتلا کریں اور ان پر ان کے دین کو مشتبہ بنا دیں ۔ اگر اللہ چاہتا تو یہ ایسا نہ کرتے ‘ لہذا انہیں چھوڑ دو کہ یہ اپنی افترا پردازیوں میں لگے رہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,138,'6:138','کہتے ہیں یہ جانور اور یہ کھیت محفوظ ہیں ۔ انہیں صرف وہی لوگ کھا سکتے ہیں جنہیں ہم کھلانا چاہیں حالانکہ یہ پابندی ان کی خود ساختہ ہے ۔ پھر کچھ جانور ہیں جن پر سواری اور بار برداری حرام کردی گئی ہے اور کچھ جانور ہیں جن پر اللہ کا نام نہیں لیتے اور یہ سب کچھ انہوں نے اللہ پر افتراء کیا ہے ۔ عنقریب اللہ انہیں ان کی افتراء پر دازیوں کا بدلہ دے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,139,'6:139','اور کہتے ہیں کہ جو کچھ ان جانوروں کے پیٹ میں ہے یہ ہمارے مردوں کیلئے مخصوص ہے اور ہماری عورتوں پر حرام ‘ لیکن اگر وہ مردہ ہوں تو دونوں اس کھانے میں شریک ہو سکتے ہیں ۔ یہ باتیں جو انہوں نے گھڑ لی ہیں ان کا بدلہ اللہ انہیں دے کر رہے گا ۔ یقینا وہ حکیم ہے اور سب باتوں کی اسے خبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,140,'6:140','یقینا خسارے میں پڑگئے وہ لوگ جنہوں نے اپنی اولاد کو جہالت اور نادانی کی وجہ سے قتل کیا اور اللہ کے دیئے ہوئے رزق کو اللہ پر افتراء پردازی کر کے حرام ٹھہرا لیا ۔ یقینا وہ بھٹک گئے اور ہر گز وہ راہ راست پانے والوں میں سے نہ تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,141,'6:141','وہ اللہ ہی ہے جس نے طرح طرح کے باغ اور تاکستان اور نخلستان پیدا کئے ۔ کھیتیاں اگائیں جن سے قسم قسم کے ماکولات حاصل ہوتے ہیں ‘ زیتون اور انار کے درخت پیدا کئے جن کے پھل صورت میں مشابہ اور مزے میں مختلف ہوتے ہیں ۔ کھاؤ اس کی پیداوار جب کہ یہ پھلیں اور اللہ کا حق ادا کرو ‘ جب ان کی فصل کاٹو ‘ اور حد سے نہ گزرو کہ اللہ حد سے گزرنے والوں کو پسند نہیں کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,142,'6:142','پھر وہی ہے جس نے مویشیوں میں سے وہ جانور بھی پیدا کئے جن سے سواری وبار برداری کا کام لیا جاتا ہے اور وہ بھی جو کھانے اور بچھانے کے کام آتے ہیں ۔ کھاؤ ان چیزوں میں سے جو اللہ نے تمہیں بخشی ہیں اور شیطان کی پیروی نہ کرو کہ وہ تمہارا کھلا دشمن ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,143,'6:143','یہ آٹھ نرو مادہ ہیں ‘ دوبھیڑ کی قسم سے ‘ دو بکری کی قسم سے اے محمد صلى الله عليه وسلم ان سے پوچھو کہ اللہ نے ان کے نر حرام کئے ہیں یا مادہ ‘ یا وہ بچے جو بھیڑوں اور بکریوں کے پیٹ میں ہوں ؟ ٹھیک ٹھیک علم کے ساتھ مجھے بتاؤ اگر تم سچے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(6,144,'6:144','اور اسی طرح دو اونٹ کی قسم سے ہیں اور دو گائے کی قسم سے ۔ پوچھو ‘ ان کے نر حرام کئے ہیں یا مادہ یا وہ بچے جو اونٹنی اور گائے کے پیٹ میں ہوں ۔ کیا تم اس وقت حاضر تھے جب اللہ نے ان کے حرام ہونے کا حکم دیا تھا ؟ “ پھر اس شخص سے بڑھ کر ظالم اور کون ہوگا جو اللہ کی طرف منسوب کرکے جھوٹی بات کہے تاکہ علم کے بغیر لوگوں کی راہنمائی کرے یقینا اللہ ظالموں کو راہ راست نہیں دکھاتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,145,'6:145','اے نبی صلى الله عليه وسلم ان سے کہو کہ جو وحی میرے پاس آئی ہے اس میں تو میں کوئی چیز نہیں پاتا جو کسی کھانے والے پر حرام ہو ‘ الا یہ کہ وہ مردار ہو ‘ یابہایا ہوا خون ہو یا سور کا گوشت ہو ‘ کہ وہ ناپاک ہے ‘ یا فسق ہو کہ اللہ کے سوا کسی اور کے نام پر ذبح کیا گیا ہو۔ پھر جو شخص مجبور کی حالت میں بغیر اس کے کہ وہ نافرمانی کا ارادہ رکھتا ہو اور بغیر اس کے کہ وہ حد ضرورت سے تجاوز کرے ‘ یقینا تمہارا رب درگزر سے کام لینے والا اور رحم فرمانے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,146,'6:146','اور جن لوگوں نے یہودیت اختیار کی ان پر ہم نے سب ناخن والے جانور حرام کردیئے تھے اور گائے اور بکری کی چربی بھی بجر اس کے کہ جو ان کی پیتھ یا آنتوں سے لگی ہوئی ہو ‘ یا ہڈی سے لگی رہ جائے ۔ یہ ہم نے ان کی سرکشی کی سزا انہیں دی تھی ‘ اور یہ جو کچھ ہم کہہ رہے ہیں بالکل سچ کہہ رہے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,147,'6:147','اب اگر وہ تمہیں جھٹلائیں تو ان سے کہہ دو کہ تمہارے رب کا دامن رحمت وسیع ہے اور مجرموں سے اس کے عذاب کو پھیرا نہیں جاسکتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,148,'6:148','یہ مشرک لوگ ضرور کہیں گے کہ ” اگر اللہ چاہتا تو نہ ہم شرک کرتے اور نہ ہمارے باپ دادا اور نہ ہم کسی چیز کو حرام ٹھہراتے ۔ “ ایسی ہی باتیں بنا کر ان سے پہلے کے لوگوں نے بھی حق کو جھٹلایا یہاں تک کہ آخر کار ہمارے عذاب کا مزہ انہوں نے چکھ لیا ۔ ان سے کہو ’ ” کیا تمہارے پاس کوئی علم ہے جسے ہمارے سامنے پیش کرسکو ؟ تم تو محض گمان پر چل رہے ہو اور نری قیاس آرائیاں کرتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,149,'6:149','پھر کہو ” حقیقت رس حجت تو اللہ کے پاس ہے ‘ بیشک اللہ چاہتا تو تم سب کو ہدایت دے دیتا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,150,'6:150','ان سے کہو ” کہ لاؤ اپنے وہ گواہ جو اس بات کی شہادت دیں کہ اللہ ہی نے ان چیزوں کو حرام کیا ہے ۔ “ پھر اگر وہ شہادت دے دیں تو تم ان کے ساتھ شہادت نہ دینا اور ہر گز ان لوگوں کی خواہشات کے پیچھے نہ چلنا جنہوں نے ہماری آیات کو جھٹلایا ہے اور جو آخرت کے منکر ہیں اور جو دوسروں کو اپنے رب کا ہمسر بناتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,151,'6:151','اے نبی صلى الله عليه وسلم ان سے کہو کہ آؤ میں تمہیں سناؤں تمہارے رب نے تم کیا پابندیاں عائد کی ہیں : یہ کہ اس کے ساتھ کسی کو شریک نہ کرو۔ اور والدین کے ساتھ نیک سلوک کرو۔ اور اپنی اولاد کو مفلسی کے ڈر سے قتل نہ کرو ‘ ہم تمہیں بھی رزق دیتے ہیں اور ان کو بھی دیں گے اور بےشرمی کی باتوں کے قریب نہ جاؤ ۔ خواہ وہ کھلی ‘ یا چھپی ہوں۔ اور کسی جان کو جسے اللہ نے محترم ٹھہرایا ہے ہلاک نہ کرو مگر حق کے ساتھ ۔۔۔۔۔۔ یہ باتیں ہیں جس کی ہدایت تمہیں اس نے کی ہے شاید کہ تم سمجھ بوجھ سے کام لو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,152,'6:152','اور یہ کہ یتیم کے مال کے قریب نہ جاؤ مگر ایسے طریقے سے جو بہترین ہو ‘ یہاں تک کہ وہ اپنے سن رشد کو پہنچ جائے ۔ اور ناپ تول میں پورا انصاف کرو ‘ ہم ہر شخص پر ذمہ داریوں کا اتنا ہی بار رکھتے ہیں جتنا اس کے امکان میں ہے ۔ اور جب بات کہو انصاف کی کہو خواہ معاملہ اپنی رشتہ داری کا کیوں نہ ہو۔ اور اللہ کے عہد کو پورا کرو ۔۔۔۔۔ ان باتوں کی ہدایت اللہ نے تمہیں کی ہے شاید کہ تم نصیحت قبول کرو۔');
INSERT INTO "sayyid_qutb" VALUES(6,153,'6:153','نیز اللہ کی ہدایت یہ ہے کہ یہی میرا سیدھا راستہ ہے ‘ لہذا اسی پر چلو اور دوسرے راستوں پر نہ چلو کہ وہ اس کے راستے سے ہٹا کر تمہیں پراگندہ کردیں گے ۔ یہ ہے وہ ہدایت جو تمہارے رب نے تمہیں کی ہے شاید کہ تم کج روی سے بچو ۔');
INSERT INTO "sayyid_qutb" VALUES(6,154,'6:154','پھر ہم نے موسیٰ کو کتاب عطا کی تھی ‘ جو بھلائی کی روش اختیار کرنے والے انسان پر نعمت کی تکمیل اور ہر ضروری چیز کی تفصیل اور سراسر ہدایت اور رحمت تھی ۔ شاید کہ لوگ اپنے رب کی ملاقات پر ایمان لے آئیں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,155,'6:155','اور اسی طرح یہ کتاب ہم نے نازل کی ہے ۔ ایک برکت والی کتاب ‘ پس تم اس کی پیروی کرو اور تقوی کی روشن اختیار کرو ‘ بعید نہیں کہ تم پر رحم کیا جائے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,156,'6:156','اب تم یہ نہیں کہہ سکتے کہ کتاب تو ہم سے پہلے دو گروہوں کو دی گئی تھی اور ہم کو کوئی خبر نہیں کہ وہ کیا پڑھتے پڑھاتے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,157,'6:157','اب تم یہ بہانہ بھی نہیں کرسکتے کہ اگر ہم پر کتاب نازل کی گئی ہوتی تو ہم اس سے زیادہ راست رو ثابت ہوتے۔ تمہارے پاس تمہارے رب کی طرف سے ایک دلیل روشن اور ہدایت و رحمت (قرآن کی صورت میں) آگئی ہے ۔ اب اس سے بڑھ کر ظالم کون ہوگا ‘ جو اللہ کی آیات کو جھٹلائے اور ان سے منہ موڑے ۔ جو لوگ ہمارے آیات سے منہ موڑتے ہیں انہیں اس روگردانی کی پاداش میں ہم بدترین سزا دے کر رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,158,'6:158','’ کیا اب لوگ اس کے منتظر ہیں کہ ان کے سامنے فرشتے آکھڑے ہوں ‘ یا تمہارا رب خود آجائے ‘ یا تمہارے رب کی بعض صریح نشانیاں نمودار ہوجائیں ؟ جس روز تمہارے رب کی بعض مخصوص نشانیاں نمودار ہوجائیں گی پھر کسی ایسے شخص کو اس کا ایمان کچھ فائدہ نہ دے گا جو پہلے ایمان نہ لایا ہو یا جس نے اپنے ایمان میں کوئی بھلائی نہ کمائی ہو ۔ اے نبی صلى الله عليه وسلم ان سے کہہ دو کہ اچھا ‘ تم انتظار کرو ‘ ہم بھی انتظار کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(6,159,'6:159','جن لوگوں نے اپنے دین کو ٹکڑے ٹکڑے کردیا اور گروہ گروہ بن گئے ان سے تمہارا واسطہ کچھ نہیں ۔ ان کا معاملہ تو اللہ کے سپرد ہے ‘ وہی ان کو بتائے گا کہ انہوں نے کیا کچھ کیا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,160,'6:160','جو اللہ کے حضور نیکی لے کر آئے گا اس کے لئے دس گناہ اجر ہے اور جو بدی لے کر آئے گا اس کو اتنا ہی بدلہ دیاجائے گا جتنا اس نے قصور کیا اور کسی پر ظلم نہ کیا جائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,161,'6:161','اے نبی صلى الله عليه وسلم کہو میرے رب نے بالیقین مجھے سیدھا راستہ دکھایا ہے ‘ بالکل ٹھیک دین جس میں کوئی ٹیڑھ نہیں ‘ ابراہیم کا طریقہ جسے یکسو ہو کر اس نے اختیار کیا تھا اور وہ مشرکوں میں سے نہ تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,162,'6:162','کہو ‘ میری نماز ‘ میرے تمام مراسم عبودیت ‘ میرا جینا اور مرنا سب کچھ اللہ رب العالمین کے لئے ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(6,163,'6:163','جس کا کوئی شریک نہیں اس کا مجھے حکم دیا گیا ہے اور میں سب سے پہلے سر اطاعت جھکانے والا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(6,164,'6:164','کہو کیا میں میں اللہ کے سوا کوئی اور رب تلاش کروں حالانکہ وہی ہر چیز کا رب ہے ؟ ہر شخص جو کچھ کماتا ہے اس کا ذمہ دار وہ خود ہے ‘ کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھاتا ‘ پھر تم سب کو اپنے رب کی طرف پلٹنا ہے ‘ اس وقت وہ تمہارے اختلافات کی حقیقت تم پر کھول دے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(6,165,'6:165','وہی ہے جس نے تم کو زمین کا خلیفہ بنایا ‘ اور تم میں سے بعض کو بعض کے مقابلے میں بلند درجے دیئے تاکہ جو کچھ تم کو دیا ہے اس میں تمہاری آزمائش کرے ، بیشک تمہارا رب سزا دینے میں بھی بہت تیز ہے اور بہت درگزر کرنیو الا اور رحم فرمانے والا بھی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,1,'7:1','المص');
INSERT INTO "sayyid_qutb" VALUES(7,2,'7:2','یہ ایک کتاب ہے جو تمہاری طرف نازل کی گئی ہے ‘ پس اے محمد صلى الله عليه وسلم تمہارے دل میں اس سے کوئی جھجک نہ ہو ۔ اس کے اتارنے کی غرض یہ ہے کہ تم اس کے ذریعے سے (منکرین کو) ڈراؤ اور ایمان والوں کو نصیحت ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,3,'7:3','لوگوں جو کچھ تمہارے رب کی طرف سے تم پر نازل کیا گیا ہے اس کی پیروی کرو اور اپنے رب کو چھوڑ کر دوسرے سرپرستوں کی پیروی نہ کرو مگر تم نصیحت کم ہی پاتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,4,'7:4','کتنی ہی بستیاں ہیں جنہیں ہم نے ہلاک کردیا ۔ ان پر ہمارا عذاب اچانک رات کے وقت ٹوٹ پڑا ‘ یا دن دہاڑے ایسے وقت آیا جبکہ وہ آرام کر رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(7,5,'7:5','اور جب ہمارا عذاب ان پر آگیا تو ان کی زبان پر اس کے سوا کوئی صدا نہ تھی کہ واقعی ہم ظالم تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,6,'7:6','پس یہ ضرور ہو کر رہنا ہے کہ ہم ان لوگوں سے باز پرس کریں جن کی طرف ہم نے پیغمبر بھیجے ہیں اور پیغمبروں سے بھی پوچھیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,7,'7:7','پھر ہم خود پورے علم کے ساتھ ساری سرگزشت ان کے آگے پیش کردیں ‘ آخر ہم کہیں غائب تو نہیں تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,8,'7:8','اور وزن اس روز عین حق ہوگا جن کے پلڑے بھاری ہوں گے وہی فلاح پائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,9,'7:9','اور جن کے پلڑے ہلکے رہیں گے وہی اپنے آپ کو خسارے میں مبتلا کرنے والے ہوں گے کیونکہ وہ ہماری آیات کے ساتھ ظالمانہ برتاؤ کرتے رہے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,10,'7:10','ہم نے تمہیں زمین میں اختیارات کے ساتھ بسایا اور تمہارے لئے یہاں سامان زیست فراہم کیا ‘ مگر تم لوگ کم ہی شکر گزار ہوتے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,11,'7:11','بیشک ہم نے تمہاری تخلیق کی ابتداء کی ‘ پھر تمہاری صورت بنائی ‘ پھر فرشتوں سے کہا آدم کو سجدہ کرو ‘ اس حکم پر سب نے سجدہ کیا مگر ابلیس سجدہ کرنے والوں میں شامل نہ ہوا ۔');
INSERT INTO "sayyid_qutb" VALUES(7,12,'7:12','پوچھا ” تجھے کس چیز نے سجدہ کرنے سے روکا جب میں نے تجھ کو حکم دیا تھا ۔ بولا : ” میں اس سے بہتر ہوں ‘ تو نے مجھے آگ سے پیدا کیا اور اسے مٹی سے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,13,'7:13','فرمایا : ” تو یہاں سے نیچے اتر تجھے حق نہیں ہے کہ یہاں بڑائی کا گھمنڈ کرے ۔ نکل جا کہ درحقیقت تو ان لوگوں میں سے ہے جو خود اپنی ذلت چاہتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,14,'7:14','بولا : ” مجھے اس دن تک مہلت دے جبکہ یہ سب دوبارہ اٹھائے جائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,15,'7:15','فرمایا : ” تجھے مہلت ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,16,'7:16','بولا : ” اچھا تو جس طرح تو نے مجھے گمراہی میں مبتلا کیا ہے میں بھی اب تیری سیدھی راہ پر ان انسانوں کی گھات میں لگا رہوں گا');
INSERT INTO "sayyid_qutb" VALUES(7,17,'7:17','آگے اور پیچھے ‘ دائیں اور بائیں ہر طرف سے ان کو گھیروں گا اور تو ان میں سے اکثر کو شکر گزار نہ پائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(7,18,'7:18','“ فرمایا : ” نکل جا یہاں سے ذلیل اور ٹھکرایا ہوا ‘ یقین رکھ کہ ان میں سے جو تیری پیروی کریں گے تجھ سمیت ان سب سے جہنم کو بھر دوں گا');
INSERT INTO "sayyid_qutb" VALUES(7,19,'7:19','اور اے آدم ‘ تو اور تیری بیوی دونوں اس جنت میں رہو جہاں جس چیز کو تمہارا جی چاہے کھاؤ مگر اس درخت کے پاس نہ پھٹکنا ورنہ ظالموں میں سے ہوجاؤ گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,20,'7:20','پھر شیطان نے ان کو بہکایا تاکہ ان کی شرمگائیں جو ایک دوسرے سے چھپائی گئی تھیں ان کے سامنے کھول دے اس نے ان سے کہا ” تمہارے رب نے تمہیں جو اس درخت سے روکا ہے اس کی وجہ سے اس کے سوا کچھ نہیں ہے کہ کہیں فرشتے نہ بنا جاؤ یا تمہیں ہمیشگی کی زندگی حاصل نہ ہوجائے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,21,'7:21','اور اس نے قسم کھا کر ان سے کہا کہ میں تمہارا سچا خیر خواہ ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,22,'7:22','پس دھوکہ دے کر وہ ان دونوں کو رفتہ رفتہ اپنے ڈھب پر لے آیا ۔ آخر کار جب انہوں نے اس درخت کا مزہ چکھا تو ان کے ستر ایک دوسرے کے سامنے کھل گئے اور وہ اپنے جسموں کو جنت کے پتوں سے ڈھانکنے لگے ۔ تب ان کے رب نے انہیں پکارا ” کیا میں نے تمہیں اس درخت سے نہ روکا تھا ‘ اور نہ کہا تھا کہ شیطان تمہارا کھلا دشمن ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,23,'7:23','دونوں بول اٹھے ” اے ہمارے رب ہم نے اپنے اوپر ستم کیا ‘ اب اگر تو نے ہم سے درگزر نہ فرمایا اور رحم نہ کیا تو یقینا ہم تباہ ہوجائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,24,'7:24','فرمایا : ” اتر جاؤ ‘ تم ایک دوسرے کے دشمن ہو ‘ اور تمہارے لئے ایک خاص مدت تک زمین ہی میں جائے قرار اور سامان زیست ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,25,'7:25','اور فرمایا ” وہیں تم کو جینا اور وہی مرنا ہے اور اسی میں سے تم کو آخر کار نکالا جائے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(7,26,'7:26','اے اولاد آدم ‘ ہم نے تم پر لباس نازل کیا ہے کہ تمہارے جسم کے قابل شرم حصوں کو ڈھانکے اور تمہارے لئے جسم کی حفاظت اور زینت کا ذریعہ بھی ہو ‘ اور بہترین لباس تقوی کا لباس ہے ۔ یہ اللہ کی نشانیوں میں سے ایک نشانی ہے ‘ شاید کہ لوگ اس سے سبق لیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,27,'7:27','اے بنی آدم ایسا نہ ہو کہ شیطان تمہیں پھر اسی طرح فتنے میں مبتلا کردے جس نے اس سے پہلے تمہارے والدین کو اس نے جنت سے نکلوایا اور ان کے لباس ان پر سے اتروا دیئے تھے تاکہ ان کی شرمگائیں ایک دوسرے کے سامنے کھولے ۔ اور اس کے ساتھی تمہیں ایسی جگہ سے دیکھتے ہیں جہاں سے تم انہیں نہیں دیکھ سکتے ۔ ان شیاطین کو ہم نے ان لوگوں کا سرپرست بنا دیا ہے جو ایمان نہیں لاتے۔');
INSERT INTO "sayyid_qutb" VALUES(7,28,'7:28','یہ لوگ جب کوئی شرمناک کام کرتے ہیں تو کہتے ہیں کہ ہم نے اپنے باپ دادا کو اسی طریقے پر پایا ہے اور اللہ ہی نے ہمیں ایسا کرنے کا حکم دیا ہے ۔ ان سے کہو اللہ بےحیائی کا حکم کبھی نہیں دیا کرتا ۔ کیا تم اللہ کا نام لے کر وہ باتیں کہتے ہو جن کے متعلق تمہیں علم نہیں ہے کہ وہ اللہ کی طرف سے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(7,29,'7:29','اے نبی صلى الله عليه وسلم ان سے کہو ! میرے رب نے تو راستی و انصاف کا حکم دیا ہے ‘ اور ان کا حکم تو یہ ہے کہ ہر عبادت میں اپنا رخ ٹھیک رکھو اور اسی کو پکارو۔ اس کے دین کو اس کے لئے خالص رکھ کر ۔ جس طرح اس نے تمہیں اب پیدا کیا ہے ‘ اسی طرح تم پھر پیدا کئے جاؤ گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,30,'7:30','ایک گروہ کو تو اس نے سیدھا راستہ دکھا دیا ہے ‘ مگر دوسرے گروہ پر گمراہی چسپاں ہو کر رہ گئی ہے کیونکہ انہوں نے بجائے شیاطین کو اپنا سرپرست بنا لیا ہے اور وہ سمجھ رہے ہیں کہ ہم سیدھی راہ پر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(7,31,'7:31','اے بنی آدم ‘ ہر عبادت کے موقع پر اپنی زینت سے آراستہ رہو اور کھاؤ پیو اور حد سے تجاوز نہ کرو ‘ اللہ حد سے بڑھنے والوں کو پسند نہیں کرتا۔');
INSERT INTO "sayyid_qutb" VALUES(7,32,'7:32','اے نبی صلى الله عليه وسلم ان سے کہو کس نے اللہ کی اس زینت کو حرام کردیا جسے اللہ نے اپنے بندوں کیلئے نکالا تھا اور کس نے خدا کی بخشی ہوئی پاک چیزیں ممنوع کردیں ؟ کہو ‘ یہ ساری چیزیں دنیا کی زندگی میں بھی ایمان لانے والوں کے لئے ہیں ‘ اور قیامت کے روز تو خالصتا انہیں کے لئے ہوں گی ۔ اس طرح ہم اپنی باتیں صاف صاف بیان کرتے ہیں ان لوگوں کے لئے جو علم رکھنے والے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,33,'7:33','اے نبی صلى الله عليه وسلم ان سے کہو کہ میرے رب نے جو چیزیں حرام کی ہیں وہ تو یہ ہیں : بےشرمی کے کام ۔۔۔۔۔۔ خواہ کھلے ہوں یا چھپے ۔۔۔۔۔ اور گناہ اور حق کے خلاف زیادتی اور یہ کہ اللہ کے ساتھ تم کسی ایسے کو شریک کرو جس کے لئے اس نے کوئی سند نازل نہیں کی ‘ اور یہ کہ اللہ کے نام پر کوئی ایسی بات کہو جس کے متعلق تمہیں علم نہ ہو ۔ (کہ وہ حقیقت میں اسی نے فرمائی ہے)');
INSERT INTO "sayyid_qutb" VALUES(7,34,'7:34','ہر قوم کے لئے مہلت کی ایک مدت مقرر ہے ‘ پھر جب کسی قوم کی مدت آن پوری ہوتی ہے تو ایک گھڑی بھر کی تاخیر وتقدیم بھی نہیں ہوتی ۔');
INSERT INTO "sayyid_qutb" VALUES(7,35,'7:35','”(اور یہ بات اللہ نے آغاز تخلیق ہی میں صاف فرما دی تھی کہ ) ” اے بنی آدم “ یاد رکھو ! اگر تمہارے پاس خود تم میں سے ایسے رسول آئیں جو تمہیں میری آیات سنا رہے ہوں ‘ تو جو کوئی نافرمانی سے بچے گا اور اپنے رویے کی اصلاح کرے گا اس کے لئے کسی خوف ورنج کا موقع نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(7,36,'7:36','اور جو لوگ ہماری آیات کو جھٹلائیں گے اور ان کے مقابلے میں سرکشی برتیں گے وہی اہل دوزخ ہوں گے جہاں وہ ہمیشہ رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,37,'7:37','آخر اس سے بڑا ظالم اور کون ہوگا جو بالکل جھوٹی باتیں گھڑ کر اللہ کی طرف منسوب کرے یا اللہ کی سچی آیات کو جھٹلائے ؟ ایسے لوگ اپنے نوشتہ تقدیر کے مطابق اپنا حصہ پاتے رہیں گے ‘ یہاں تک کہ وہ گھڑی آجائے گی جب ہمارے بھیجے ہوئے فرشتے ان کی روحیں قبض کرنے کے لئے پہنچیں گے ۔ اس وقت وہ ان سے پوچھیں گے کہ ” بتاؤ اب کہاں ہیں تمہارے معبود جن کو تم خدا کے بجائے پکارتے تھے ؟ “ وہ کہیں گے کہ ” سب ہم سے گم ہوگئے “۔ اور وہ خود اپنے خلاف گواہی دیں گے کہ ہم واقعی منکر حق تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,38,'7:38','اللہ فرمائے گا جاؤ ‘ تم بھی اسی جہنم میں چلے جاؤ جس میں تم سے پہلے گزرے ہوئے گروہ جن وانس جا چکے ہیں ۔ ہر گروہ جب جہنم میں داخل ہوگا تو اپنے پیش رو گروہ پر لعنت کرتا ہوا داخل ہوگا ‘ حتی کہ جب سب وہاں جمع ہوجائیں گے تو ہر بعد والا گروہ پہلے گروہ کے حق میں کہے گا کہ اے رب ‘ یہ لوگ تھے جنہوں نے ہم کو گمراہ کیا ‘ لہذا انہیں آگ کا دوہرا عذاب دے ۔ جواب میں ارشاد ہوگا ۔ ہر ایک کے لئے دوہرا عذاب ہی ہے مگر تم جانتے نہیں ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,39,'7:39','اور پہلا گروہ دوسرے گروہ سے کہے گا کہ (اگر ہم قابل الزام تھے تو) تمہی کو ہم پر کون سی فضیلت حاصل تھی ‘ اب اپنی کمائی کے نتیجے میں عذاب کا مزا چکھو۔');
INSERT INTO "sayyid_qutb" VALUES(7,40,'7:40','یقین جانو ‘ جن لوگوں نے ہماری آیات کو جھٹلایا ہے اور انکے مقابلے میں سرکش رہے ان کے لئے آسمان کے دروازے ہرگز نہ کھولے جائیں گے ۔ ان کا جنت میں جانا اتنا ہی ناممکن ہے جتنا سوئی کے ناکے سے اونٹ کا گزرنا ۔ مجرموں کو ہمارے ہاں ایسا ہی بدلا ملا کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,41,'7:41','ان کے لئے تو جہنم کا بچھونا ہوگا اور جہنم ہی کا اوڑھنا ہے ۔ یہ ہے وہ جزا جو ہم ظالموں کو دیا کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(7,42,'7:42','بخلاف اس کے جن لوگوں نے ہماری آیات کو مان لیا ہے اور اچھے کام کئے ہیں ۔۔۔۔۔ اور اس باب میں ہم ہر ایک کو اس کی استطاعت ہی کے مطابق ذمہ دار ٹھہراتے ہیں ۔۔۔۔۔۔ وہ اہل جنت ہیں جہاں وہ ہمیشہ رہیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,43,'7:43','ان کے دلوں میں ایک دوسرے کے خلاف جو کچھ کدورت ہوگی اسے ہم نکال دیں گے ۔ ان کے نیچے نہریں بہتی ہوں گی اور وہ کہیں گے کہ ” تعریف خدا ہی کے لئے ہے جس نے ہمیں یہ راستہ دکھایا ‘ ہم خود راہ نہ پا سکتے تھے اگر خدا ہماری رہنمائی نہ کرتا ‘ ہمارے رب کے بھیجے ہوئے رسول حق ہی لے کر آئے تھے۔ “ اس وقت ندا آئے گی کہ ” یہ جنت جس کے تم وارث بنائے گئے ہو تمہیں ان اعمال کے بدلے مین ملی ہے جو تم کرتے رہے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,44,'7:44','پھر یہ جنت کے لوگ دوزخ والوں سے پکار کر کہیں گے ’‘ ہم نے ان سارے وعدوں کو ٹھیک پایا جو ہمارے رب نے ہم سے کئے تھے ‘ کیا تم نے بھی ان وعدوں کو ٹھیک پایا جو تمہارے رب نے کئے تھے ؟ “ وہ جواب دیں گے ۔ ” ہاں ۔ “ تب ایک پکارنے والا ان کے درمیان پکارے گا کہ ” خدا کی لعنت ان ظالموں پر۔');
INSERT INTO "sayyid_qutb" VALUES(7,45,'7:45','جو اللہ کے راستے سے لوگوں کو روکتے رہے اور اسے ٹیڑھا کرنا چاہتے تھے اور آخرت کے منکر تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,46,'7:46','ان دونوں گروہوں کے درمیان ایک اوٹ حائل ہوگی جس بلندیوں (اعراف) پر کچھ اور لوگ ہوں گے ۔ یہ ہر ایک کو اس کے قیافہ سے پہچانیں گے اور جنت والوں سے پکار کر کہیں گے کہ ” سلامتی ہو تم پر “ یہ لوگ جنت میں داخل تو نہیں ہوئے مگر اس کے امیدوار ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(7,47,'7:47','اور جب انکی نگائیں دوزخ والوں کی طرف پھریں گی تو کہیں گے ” اے رب ہمیں ان ظالم لوگوں میں شامل نہ کیجیو۔');
INSERT INTO "sayyid_qutb" VALUES(7,48,'7:48','پھر یہ اعراف کے لوگ دوزخ کی چند بڑی بڑی شخصیتوں کو ان کی علامتوں سے پہچان کر پکاریں گے کہ ” دیکھ لیا تم نے آج نہ تمہارے جتھے تمہارے کسی کام آئے اور نہ وہ سازوسامان جن کو تم بڑی چیز سمجھتے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,49,'7:49','اور کیا یہ اہل جنت وہی لوگ نہیں ہیں جن کے متعلق تم قسمیں کھا کھا کر کہتے تھے کہ ان کو تو خدا اپنی رحمت میں سے کچھ نہ دے گا ؟ آج انہی سے کہا گیا کہ داخل ہوجاؤ جنت میں ‘ تمہارے لئے نہ خوف ہے نہ رنج۔');
INSERT INTO "sayyid_qutb" VALUES(7,50,'7:50','اور دوزخ کے لوگ جنت والوں کو پکاریں گے کہ کچھ تھوڑا سا پانی ہم پر ڈال دو یا جو رزق اللہ نے تمہیں دیا ہے اسی میں سے کچھ پھینک دو ۔ وہ جواب دیں گے کہ ” اللہ نے یہ دونوں چیزیں ان منکرین حق پر حرام کردی ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,51,'7:51','جنہوں نے اپنے دین کو کھیل اور تفریح بنا لیا تھا اور جنہیں دنیا کی زندگی نے فریب میں مبتلا کر رکھا تھا ۔ اللہ فرماتا ہے کہ ” آج ہم بھی انہیں اسی طرح بھلا دیں گے جس طرح وہ اس دن کی ملاقات کو بھولے رہے اور ہماری آیات کا انکار کرتے رہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,52,'7:52','ہم ان لوگوں کے پاس ایک ایسی کتاب لے آئے ہیں جس کو ہم نے علم کی بناء پر مفصل بنایا ہے اور جو ایمان لانے والوں کے لئے ہدایت اور رحمت ہے۔');
INSERT INTO "sayyid_qutb" VALUES(7,53,'7:53','اب کیا یہ لوگ اس کے سوا کسی اور بات کے منتظر ہیں کہ وہ انجام سامنے آجائے جس کی یہ کتاب خبر دے رہی ہے ؟ جس روز وہ انجام سامنے آگیا تو وہی لوگ جنہوں نے پہلے اسے نظر انداز کردیا تھا کہیں گے کہ ” واقعی ہمارے رب کے رسول حق لے کر آئے تھے ‘ پھر کیا اب ہمیں کچھ سفارشی ملیں گے جو ہمارے حق میں سفارش کریں ؟ یا ہمیں دوبارہ واپس ہی بھیج دیا جائے تاکہ جو کچھ ہم پہلے کرتے تھے اس کے بجائے اب دوسرے طریقے پر کام کر کے دکھائیں ۔ “ ۔۔۔۔۔۔۔۔ انہوں نے اپنے آپ کو خسارے میں ڈال دیا اور وہ سارے جھوٹ جو انہوں نے تصنیف کر رکھے تھے آج ان سے گم ہوگئے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,54,'7:54','درحقیقت تمہارا رب اللہ ہی ہے جس نے آسمان و زمین کو چھ دنوں میں پیدا کیا پھر اپنے تخت سلطنت پر جلوہ فرما ہوا ۔ جو رات کو دن پر ڈھانک دیتا ہے اور پھر دن رات کے پیچھے دوڑا چلا آتا ہے جس نے سورج اور چاند اور تارے پیدا کئے ۔ سب اس کے فرمان کے تابع ہیں ۔ خبردار رہو اسی کی خلق ہے اور اسی کا امر ہے ۔ بڑا بابرکت ہے اللہ ‘ سارے جہانوں کا مالک و پروردگار ۔');
INSERT INTO "sayyid_qutb" VALUES(7,55,'7:55','اپنے رب کو پکارو گڑ گڑاتے ہوئے اور چپکے چپکے ‘ یقینا وہ حد سے گزرنے والوں کو پسند نہیں کرتا ۔');
INSERT INTO "sayyid_qutb" VALUES(7,56,'7:56','زمین میں فساد برپا نہ کرو ‘ جبکہ اس کی اصلاح ہوچکی ہے اور خدا ہی کو پکارو خوف کے ساتھ اور طمع کے ساتھ یقینا اللہ کی رحمت نیک کردار لوگوں سے قریب ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,57,'7:57','اور وہ اللہ ہی ہے جو ہواؤں کو اپنی رحمت سے آگے آگے خوشخبری لئے ہوئے بھیجتا ہے ۔ پھر جب وہ پانی سے لدے ہوئے بادل اٹھا لیتی ہیں تو انہیں کسی مردہ سرزمین کی طرف حرکت دیتا ہے اور وہاں مینہ برسا کر طرح طرح کے پھل نکال لاتا ہے ۔ دیکھو اس طرح ہم مردوں کو حالت موت سے نکالتے ہیں ‘ شاید کہ تم اس مشاہدے سے سبق لو۔');
INSERT INTO "sayyid_qutb" VALUES(7,58,'7:58','جو زمین اچھی ہوتی ہے وہ اپنے رب کے حکم سے خوب پھل پھول لاتی ہے اور جو زمین خراب ہوتی ہے اس سے ناقص پیداوار کے سوا کچھ نہیں نکلتا ۔ اس طرح ہم اپنی نشانیوں کو بار بار پیش کرتے ہیں ان لوگوں کے لئے شکر گزار ہونے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(7,59,'7:59','ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف بھیجا ۔ اس نے کہا ” اے برادران قوم ! اللہ کی بندگی کرو اس کے سوا تمہارا کوئی خدا نہیں ہے میں تمہارے حق میں ایک ہولناک دن کے عذاب سے ڈرتا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,60,'7:60','اس کی قوم کے سرداروں نے جواب دیا :” ہم کو تو یہ نظر آتا ہے کہ تم صریحر گمراہی مبتلا ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,61,'7:61','نوح (علیہ السلام) نے کہا ” اے برادران قوم ‘ میں کسی گمراہی میں نہیں پڑا ہوں بلکہ میں رب العالمین کا رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(7,62,'7:62','تمہیں اپنے رب کے پیغام پہنچاتا ہوں ‘ تمہارا خیر خواہ ہوں اور مجھے اللہ کی طرف سے وہ کچھ معلوم ہے جو تمہیں معلوم نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,63,'7:63','کیا تمہیں اس بات پر تعجب ہوا کہ تمہارے پاس خود تمہاری اپنی قوم کے ایک آدمی کے ذریعے سے تمہارے رب کی یاد دہانی آئی تاکہ تمہیں خبردار کرے اور تم غلط روی سے بچ جاؤ اور تم پر رحم کیا جائے ؟');
INSERT INTO "sayyid_qutb" VALUES(7,64,'7:64','مگر انہوں نے اس کو جھٹلایا ۔ آخر کار ہم نے اسے اور اس کے ساتھیوں کو ایک کشتی میں نجات دی اور ان لوگوں کو ڈبو دیا جنہوں نے ہماری آیات کو جھٹلایا تھا ‘ یقینا وہ اندھے لوگ تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,65,'7:65','اور عاد کی طرف ہم نے ان کے بھائی ہود کو بھیجا ۔ اس نے کہا ” اے برادران قوم ‘ اللہ کی بندگی کرو ‘ اس کے سوا تمہارا کوئی خدا نہیں ہے ۔ پھر کیا تم غلط روی سے پرہیز کرو گے ۔ ؟');
INSERT INTO "sayyid_qutb" VALUES(7,66,'7:66','اس کی قوم کے سرداروں نے ‘ جو اس کی بات ماننے سے انکار کر رہے تھے ‘ جواب میں کہا ” ہم تو تمہیں بےعقلی میں مبتلا سمجھتے تھے اور ہمیں گمان ہے کہ تم جھوٹے ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,67,'7:67','‘ اس نے کہا ” اے برادران قوم ‘ میں بےعقلی میں مبتلا نہیں ہوں بلکہ میں رب العالمین کا رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(7,68,'7:68','تم کو اپنے رب کے پیغامات پہنچاتا ہوں ‘ اور تمہارا خیر خواہ ہوں جس پر بھروسہ کیا جاسکتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,69,'7:69','کیا تمہیں اس بات پر تعجب ہوا کہ تمہارے پاس خود تمہاری اپنی قوم کے ایک آدمی کے ذریعے تمہارے رب کی یاد دہائی آئی تاکہ وہ تمہیں خبردار کرے ؟ بھول نہ جاؤ کہ تمہارے رب نے نوح (علیہ السلام) کی قوم کے بعد تم کو اس کا جانشین بنایا اور تمہیں خوب تنومند کیا ‘ پس اللہ کی قدرت کے کرشموں کو یاد رکھو ‘ امید ہے کہ فلاح پاؤ گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,70,'7:70','انہوں نے جواب دیا ” کیا تو ہمارے پاس اس لئے آیا ہے کہ ہم اکیلے اللہ ہی کی عبادت کریں اور انہیں چھوڑ دیں جن کی عبادت ہمارے باپ دادا کرتے آئے ہیں ؟ اچھا تو لے آ وہ عذاب جس کی تو ہمیں دھمکی دیتا ہے اگر تو سچا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,71,'7:71','‘ اس نے کہا ” تمہارے رب کی پھٹکار تم پر پڑگئی اور اس کا غضب ٹوٹ پڑا ۔ کیا تم مجھ سے ان ناموں پر جھگڑتے ہو جو تم نے اور تمہارے باپ دادا رکھ لئے ہیں ‘ جن کے لئے اللہ نے کوئی سند نازل نہیں کی ہے ؟ اچھا تو تم بھی انتظار کرو اور میں بھی تمہارے ساتھ انتظار کرتا ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,72,'7:72','آخر کار ہم نے اپنی مہربانی سے ہود اور اس کے ساتھیوں کو بچا لیا اور ان لوگوں کی جڑ کاٹ دی جو ہماری آیات کو جھٹلا چکے تھے اور ایمان لانے والے نہ تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,73,'7:73','اور ثمود کی طرف ہم نے ان کے بھائی صالح کو بھیجا ۔ اس نے کہا ” اے برادران قوم ‘ اللہ کی بندگی کرو ‘ اس کے سوا تمہارا کوئی خدا نہیں ہے ۔ تمہارے پاس تمہارے رب کی کھلی دلیل آگئی ہے ۔ یہ اللہ کی اونٹنی تمہارے لئے ایک نشانی کے طور پر ہے ‘ لہذا اسے چھوڑ دو کہ خدا کی زمین میں چرتی پھرے ۔ اس کو کسی برے ارادے سے ہاتھ نہ لگانا ورنہ ایک دردناک عذاب تمہیں آلے گا ۔');
INSERT INTO "sayyid_qutb" VALUES(7,74,'7:74','یاد کرو وہ وقت جب اللہ نے قوم عاد کے بعد تمہیں اس کا جانشین بنایا اور تم کو زمین یہ منزلت بخشی کہ آج تم ان کے ہموار میدانوں میں عالیشان محل بناتے اور اس کے پہاڑوں کو مکانات کی شکل میں تراشتے ہو ۔ پس اس کی قدرت کے کرشموں سے غافل نہ ہوجاؤ اور زمین میں فساد برپا نہ کرو۔');
INSERT INTO "sayyid_qutb" VALUES(7,75,'7:75','اس کی قوم کے سرداروں نے جو بڑے بنے ہوئے تھے ، کمزور طبقہ کے ان لوگوں سے جو ایمان لے آئے تھے ‘ کہا ” کیا واقعی یہ جانتے ہو کہ صالح (علیہ السلام) اپنے رب کا پیغمبر ہے ؟ “ انہوں نے جواب دیا ” بیشک جس پیغام کے ساتھ وہ بھیجا گیا ہے اسے ہم مانتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,76,'7:76','ان بڑائی کے مدعیوں نے کہا ” جس چیز کو تم نے مانا ہے ہم اس کے منکر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(7,77,'7:77','پھر انہوں نے اونٹنی کو مار ڈالا اور پورے تمرد کے ساتھ اپنے رب کے حکم کیخلاف ورزی کر گزرے اور صالح سے کہہ دیا کہ ” لے آ وہ عذاب جس کی تو ہمیں دھمکی دیتا ہے اگر تو واقعی پیغمبروں میں سے ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,78,'7:78','‘ آخر کار ایک دہلا دینے والی آفت نے انہیں آلیا اور وہ اپنے گھروں میں اوندھے پڑے کے پڑے رہ گئے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,79,'7:79','اور صالح یہ کہتا ہوا ان کی بستیوں سے نکل گیا کہ ” اے میری قوم ‘ میں نے اپنے رب کا پیغام تجھے پہنچا دیا اور میں نے تیری بہت خیرخواہی کی مگر میں کیا کروں کہ تجھے اپنے خیر خواہ پسند ہی نہیں ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,80,'7:80','اور لوط کو ہم نے پیغمبر بنا کر بھیجا ‘ پھر یاد کرو جب اس نے اپنی قوم سے کہا ” کیا تم ایسے بےحیا ہوگئے کہ وہ فحش کام کرتے ہو جو تم سے پہلے دنیا میں کسی نے نہیں کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(7,81,'7:81','تم عورتوں کو چھوڑ کر مردوں سے اپنی خواہش پوری کرتے ہو ۔ حقیقت یہ ہے کہ تم بالکل ہی حد سے گزر جانے والے لوگ ہو ۔');
INSERT INTO "sayyid_qutb" VALUES(7,82,'7:82','مگر اس کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ ” نکالو ان لوگوں کو اپنی بسیتوں سے ‘ بڑے پاکباز بنتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,83,'7:83','آخر کار ہم نے لوط اور اس کے گھر والوں کو نجات دی بجز اس کی بیوی کے جو پیچھے رہ جانے والوں میں تھی');
INSERT INTO "sayyid_qutb" VALUES(7,84,'7:84','بچا کر نکال دیا اور اس قوم پر برسائی ایک بارش پھر دیکھو کہ ان مجرموں کا کیا انجام ہوا ؟');
INSERT INTO "sayyid_qutb" VALUES(7,85,'7:85','اور مدین والوں کی طرف ہم نے ان کے بھائی شعیب کو بھیجا ‘ اس نے کہا : ’ اے برادران قوم ‘ اللہ کی بندگی کرو ‘ اس کے سوا تمہارا کوئی خدا نہیں ہے ۔ تمہارے پاس تمہارے رب کی صاف راہنمائی آگئی ہے ۔ لہذا وزن اور پیمانے پورے کرو ‘ لوگوں کو انکی چیزوں میں گھاٹا نہ دو ‘ اور زمین میں فساد برپا نہ کرو جب کہ اس کی اصلاح ہوچکی ہے ۔ اس میں تمہاری بھلائی ہے ۔ اگر تم واقعی مومن ہو');
INSERT INTO "sayyid_qutb" VALUES(7,86,'7:86','اور ہر راستے پر رہزن بن کر نہ بیٹھ جاؤ کہ لوگوں کو خوفزدہ کرنے اور ایمان لانے والوں کو خدا کے راستے سے روکنے لگو اور سیدھی راہ کو ٹیڑھا کرنے کے درپے ہوجاؤ ۔ یاد کرو وہ زمانہ جبکہ تم تھوڑے تھے پھر اللہ تمہیں بہت کردیا اور آنکھیں کھول کر دیکھو کہ دنیا میں مفسدوں کا کیا انجام ہوا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(7,87,'7:87','اگر تم میں ایک گروہ اس تعلیم پر جس کے ساتھ میں بھیجا گیا ہوں ‘ ایمان لاتا ہے اور دوسرا ایمان نہیں لاتا تو صبر کے ساتھ دیکھتے رہو یہاں تک کہ اللہ ہمارے درمیان فیصلہ کر دے اور وہی سب سے بہتر فیصلہ کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,88,'7:88','اس کی قوم کے سرداروں نے ‘ جو اپنی بڑائی کے گھمنڈ میں مبتلا تھے ‘ اس سے کہا ” اے شعیب ہم تجھے اور ان لوگوں کو جو تیرے ساتھ ایمان لائے ہیں اپنی بستی سے نکال دیں گے ورنہ تم لوگوں کو ہماری ملت میں واپس آنا ہوگا ۔ “ شعیب (علیہ السلام) نے جواب دیا ” کیا زبردستی ہمیں پھیرا جائے گا خواہ ہم راضی نہ ہوں ۔');
INSERT INTO "sayyid_qutb" VALUES(7,89,'7:89','ہم اللہ پر جھوٹ گھڑنے والے ہوں گے اگر تمہاری ملت میں پلٹ آئیں جبکہ اللہ ہمیں اس سے نجات دے چکا ہے ہمارے لئے تو اس طرف پلٹنا اب کسی طرح ممکن نہیں الا یہ کہ ہمارا رب ہی ایسا چاہے ہمارے رب کا علم ہر چیز پر حاوی ہے ۔ اس پر ہم نے اعتماد کرلیا۔ اے رب ‘ ہمارے اور ہماری قوم کے درمیان ٹھیک ٹھیک فیصلہ کر دے اور تو بہترین فیصلہ کرنے والا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,90,'7:90','اس کی قوم کے سرداروں نے ‘ جو اس کی بات ماننے سے انکار کرچکے تھے ‘ آپس میں کہا ” اگر تم نے شعیب کی پیروی قبول کرلی تو برباد ہوجاؤ گے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,91,'7:91','مگر ہوا یہ ایک دہلا دینے والی آفت نے ان کو آلیا اور وہ اپنے گھروں میں اوندھے پڑے کے پڑے رہ گئے ۔');
INSERT INTO "sayyid_qutb" VALUES(7,92,'7:92','جن لوگوں نے شعیب کو جھٹلایا وہ ایسے مٹے کہ گویا کبھی ان گھروں میں ہی نہ تھے ۔ شعیب کے جھٹلانے والے ہی آخرکار برباد ہوکر رہے');
INSERT INTO "sayyid_qutb" VALUES(7,93,'7:93','اور شعیب یہ کہہ کر ان کی بستیوں سے نکل گیا کہ ” اے برادران قوم میں نے اپنے رب کے پیغامات تمہیں پہنچا دیئے اور تمہاری خیرخواہی کا حق ادا کردیا ۔ اب میں اس قوم پر کیسے افسوس کروں جو قبول حق سے انکار کرتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(7,94,'7:94','کبھی ایسا نہیں ہوا کہ ہم نے کسی بستی میں نبی بھیجا ہو اور اس بستی کے لوگوں کو پہلے تنگی اور سختی میں مبتلا نہ کیا ہو اس خیال سے کہ شاید وہ عاجزی پر اتر آئیں');
INSERT INTO "sayyid_qutb" VALUES(7,95,'7:95','پھر ہم نے ان کی بدحالی کو خوش حالی سے بدل دیا یہاں تک کہ وہ خوب پھلے پھولے اور کہنے لگے کہ " ہمارے اسلاف پر بھی اچھے اور برے دن آتے ہی رہے ہیں " آخر کار ہم نے انہیں اچانک پکڑ لیا اور نہیں خبر تک نہ ہوئی');
INSERT INTO "sayyid_qutb" VALUES(7,96,'7:96','اگر بستیوں کے لوگ ایمان لاتے اور تقویٰ کی روش اختیار کرتے تو ہم ان پر آسمان اور زمین سے برکتوں کے دروازے کھول دیتے مگر انہوں نے تو جھٹلایا ، لہذا ہم نے اس بری کمائی کے حساب میں انہیں پکڑ لیا جو وہ سمیٹ رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(7,97,'7:97','پھر کیا بستیوں کے لوگ اب اس سے بےخوف ہوگئے ہیں کہ ہماری گرفت کبھی اچانک ان پر رات کے وقت نہ آجائے گی جب کہ وہ سوئے پڑے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(7,98,'7:98','یا انہیں اطمینان ہوگیا ہے کہ ہمارا مضبوط ہاتھ کبھی یکایک ان پر دن کے وقت نہ آپڑے گا جب کہ وہ کھیل رہے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(7,99,'7:99','کیا یہ لوگ اللہ کی چال سے بےخوف ہیں ؟ حالانکہ اللہ کی چال سے وہی قوم بےخوف ہوتی ہے جو تباہ ہونے والی ہو');
INSERT INTO "sayyid_qutb" VALUES(7,100,'7:100','اور کیا ان لوگوں کو جو سابق اہل زمین کے بعد زمین کے وارث ہوتے ہیں ، اس امر واقعی نے کچھ سبق نہیں دیا کہ اگر ہم چاہیں تو ان کے قصوروں پر انہیں پکڑ سکتے ہیں ؟ (مگر و سبق آموز حقائق سے تغافل برتتے ہیں) اور ہم ان کے دلوں پر مہر لگا دیتے ہیں ، پھر وہ کچھ نہیں سنتے');
INSERT INTO "sayyid_qutb" VALUES(7,101,'7:101','یہ قومیں جن کے قصے ہم تمہیں سنا رہے ہیں (تمہارے سامنے مثال کے طور پر موجود ہیں) ان کے رسول ان کے پاس کھلی کھلی نشانیاں لے کر آئے ، مگر جس چیز کو وہ ایک دفعہ جھٹلا چکے تھے پھر اسے وہ ماننے والے نہ تھے۔ دیکھو اس طرح ہم منکرین حق کے دلوں پر مہر لگا دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,102,'7:102','ہم نے ان میں سے اکثر میں کوئی پاس عہد نہ پایا بلکہ اکثر کو فاسق ہی پایا');
INSERT INTO "sayyid_qutb" VALUES(7,103,'7:103','پھر ان قوموں کے بعد (جن کا ذکر اوپر کیا گیا) ہم نے موسیٰ (علیہ السلام) کو اپنی نشانیوں کے ساتھ فرعون اور اس کی قوم کے سرداروں کے پاس بھیجا۔ مگر انہوں نے ہماری نشانیوں کے ساتھ ظلم کیا ، پس دیکھو کہ ان مفسدوں کا کیا انجام ہوا ؟ "');
INSERT INTO "sayyid_qutb" VALUES(7,104,'7:104','موسیٰ نے کہا " اے فرعون ، میں کائنات کے مالک کی طرف سے بھیجا ہوا آیا ہوں');
INSERT INTO "sayyid_qutb" VALUES(7,105,'7:105','میرا منصب یہی ہے کہ اللہ کا نام لے کر کوئی بات حق کے سوا نہ کہوں ، میں تم لوگوں کے پاس تمہارے رب کی طرف سے صریح دلیل ماموریت لے کر آیا ہوں ، لہذا تو بنی اسرائیل کو میرے ساتھ بھیج دے');
INSERT INTO "sayyid_qutb" VALUES(7,106,'7:106','فرعون نے کہا " اگر تو کوئی نشانی لایا ہے اور اپنے دعوے میں سچا ہے تو اسے پیش کر "');
INSERT INTO "sayyid_qutb" VALUES(7,107,'7:107','موسیٰ نے اپنا عصا پھینکا اور یکایک وہ ایک جیتا جاگتا اژدھا تھا');
INSERT INTO "sayyid_qutb" VALUES(7,108,'7:108','اور اس نے اپنی جیب سے ہاتھ نکالا اور سب دیکھنے والوں کے سامنے وہ چمک رہا تھا');
INSERT INTO "sayyid_qutb" VALUES(7,109,'7:109','اس پر فرعون کی قوم کے سرداروں نے آپس میں کہا کہ " یقینا یہ شخص بڑا ماہر جادوگر ہے "');
INSERT INTO "sayyid_qutb" VALUES(7,110,'7:110','تمہیں تمہاری زمین سے بےدخل کرنا چاہتا ہے۔ اب کہو کیا کہتے ہو ؟ "');
INSERT INTO "sayyid_qutb" VALUES(7,111,'7:111','پھر ان سب نے فرعون کو مشورہ دیا کہ اسے اور اس کے بھائی کو انتظار میں رکھیے اور تمام شہروں میں ہر کارے بھیج دیجئے');
INSERT INTO "sayyid_qutb" VALUES(7,112,'7:112','کہ ہر ماہر فن جادوگر کو آپ کے پاس لے آئیں');
INSERT INTO "sayyid_qutb" VALUES(7,113,'7:113','چناچہ جادوگر فرعون کے پاس آگئے۔ انہوں نے کہا " اگر ہم غالب رہے تو ہمیں اس کا صلہ تو ضرور ملے گا ؟ "');
INSERT INTO "sayyid_qutb" VALUES(7,114,'7:114','فرعون نے جواب دیا " ہاں ، اور تم مقرب بارگاہ ہوگے "۔');
INSERT INTO "sayyid_qutb" VALUES(7,115,'7:115','پھر انہوں نے موسیٰ (علیہ السلام) سے کہا تم پھینکتے ہو یا ہم پھینکیں ؟ موسیٰ نے جواب دیا۔ تم ہی پھینکو');
INSERT INTO "sayyid_qutb" VALUES(7,116,'7:116','انہوں نے جو اپنے آلچھر پھینکے تو نگاہوں کو مسحور اور دلوں کو خوفزدہ کردیا اور بڑٓ ہی زبردست جادو بنا لائے');
INSERT INTO "sayyid_qutb" VALUES(7,117,'7:117','ہم نے موسیٰ کو اشارہ کیا کہ پھینک اپنا عصا۔ اس کا پھینکنا تھا کہ آن کی آن میں وہ ان کے اس جھوٹے طلسم کو نکلتا چلا گیا');
INSERT INTO "sayyid_qutb" VALUES(7,118,'7:118','اس طرح جو حق تھا وہ حق ثابت ہوا اور جو کچھ انہوں نے بنا رکھا تھا وہ باطل ہوکر رہ گیا');
INSERT INTO "sayyid_qutb" VALUES(7,119,'7:119','فرعون اور اس کے ساتھی میدان مقابلہ میں مغلوب ہوئے اور (فتح مند ہونے کے بجائے) الٹے ذلیل ہوگے');
INSERT INTO "sayyid_qutb" VALUES(7,120,'7:120','اور جادوگروں کا حال یہ ہوا کہ گویا کسی چیز نے اندر سے انہیں سجدے میں گرا دیا');
INSERT INTO "sayyid_qutb" VALUES(7,121,'7:121','کہنے لگے ہم نے مان لیا رب العالمین کو');
INSERT INTO "sayyid_qutb" VALUES(7,122,'7:122','اس رب کو جسے موسیٰ اور ہارون مانتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(7,123,'7:123','فرعون نے کہا تم اس پر ایمان لے آئے قبل اس کے کہ میں تمہیں اجازت دوں ، یقینا یہ کوئی خفیہ سازش تھی جو تم لوگوں نے اس دار السلطنت میں کی تاکہ اس کے مالکوں کو اقتدار سے بےدخل کرو۔ اچھا تو اس کا نتیجہ اب تمہیں معلوم ہوا جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,124,'7:124','میں تمہارے ہاتھ پاؤں مخالف سمتوں سے کٹوا دوں گا اور اس کے بعد تم سب کو سولی پر چڑھاؤں گا');
INSERT INTO "sayyid_qutb" VALUES(7,125,'7:125','انہوں نے جواب دیا۔ بہرحال ہمیں پلٹنا اپنے رب ہی کی طرف ہے');
INSERT INTO "sayyid_qutb" VALUES(7,126,'7:126','تو جس بات پر ہم سے انتقام لینا چاہتا ہے وہ اس کے سوا کچھ نہیں کہ ہمارے رب کی نشانیاں جب ہمارے سامنے آگئیں تو ہم نے انہیں مان لیا۔ اے رب ! ہم پر صبر کا فیضان کر اور ہمیں دنیا سے اٹھا تو اس حال میں کہ ہم تیرے فرمان بردار ہوں');
INSERT INTO "sayyid_qutb" VALUES(7,127,'7:127','فرعون سے اس کی قوم کے سرداروں نے کہا " کیا تو موسیٰ اور اس کی قوم کو یوں ہی چھوڑ دے گا کہ ملک میں فساد پھیلائیں اور وہ تیری اور تیرے معبودوں کی بندگی چھوڑ بیٹھیں ؟ " فرعون نے جواب دیا " میں ان کے بیٹوں کو قتل کراؤں گا اور ان کی عورتوں کو جیتا رہنے دوں گا۔ ہمارے اقتدار کی گرفت ان پر مضبوط ہے "');
INSERT INTO "sayyid_qutb" VALUES(7,128,'7:128','موسیٰ نے اپنی قوم سے کہا " اللہ سے مدد مانگو اور صبر کرو ، زمین اللہ کی ہے ، اپنے بندوں میں سے جس کو چاہتا ہے اس کا وارث بنا دیتا ہے ، اور آخری کامیابی انہی کے لیے ہے جو اس سے ڈرتے ہوئے کام کریں "');
INSERT INTO "sayyid_qutb" VALUES(7,129,'7:129','اس کی قوم کے لوگوں نے کہا " تیرے آنے سے پہلے بھی ہم ستائے جاتے تھے اور اب تیرے آنے پر بھی ستائے جا رہے ہیں " اس نے جواب دیا " قریب ہے وہ وقت کہ تمہارا رب تمہارے دشمن کو ہلاک کردے اور تم کو زمین میں خلیفہ بنائے ، پھر دیکھے کہ تم کیسے عمل کرتے ہو "');
INSERT INTO "sayyid_qutb" VALUES(7,130,'7:130','ہم نے فرعون کے لوگوں کو کئی سال تک قحط اور پیداوار کی کمی میں مبتلا رکھا کہ شاید ان کو ہوش آئے');
INSERT INTO "sayyid_qutb" VALUES(7,131,'7:131','مگر ان کا حال یہ تھا کہ جب اچھا زمانہ آتا تو کہتے کہ ہم اسی کے مستحق ہیں ، اور جب برا زمانہ آتا تو موسیٰ اور اس کے ساتھیوں کو اپنے لیے فال بد ٹھہراتے ، حالانکہ در حقیقت ان کی فال بد تو اللہ کے پاس تھی ، مگر ان میں سے اکثر بےعلم تھے');
INSERT INTO "sayyid_qutb" VALUES(7,132,'7:132','انہوں نے موسیٰ سے کہا کہ " تو ہمیں مسحور کرنے کے لیے خواہ کوئی نشانی لے آئے ، ہم تو تیری بات ماننے والے نہیں ہیں "');
INSERT INTO "sayyid_qutb" VALUES(7,133,'7:133','آخر کار ہم نے ان پر طوفان بھیجا ، ٹڈی دل چھوڑے ، سرسریاں پھیلائیں ، مینڈک نکالے اور خون برسایا۔ یہ سب نشانیاں الگ الگ کرکے دکھائیں مگر وہ سرکشی کیے چلے گئے۔ اور وہ بڑے ہی مجرم لوگ تھے');
INSERT INTO "sayyid_qutb" VALUES(7,134,'7:134','جب کبھی ان پر بلا نازل ہوجاتی تو کہتے " اے موسیٰ ، تجھے اپنے رب کی طرف سے جو منصب حاصل ہے اس کی بناء پر ہمارے حق میں دعا کر ، اگر اب کے تو ہم پر سے یہ بلا ٹلوا دے تو ہم تیری بات مان لیں گے اور بنی اسرائیل کو تیرے ساتھ بھیج دیں گے "');
INSERT INTO "sayyid_qutb" VALUES(7,135,'7:135','مگر جب ہم ان پر سے اپنا عذاب ایک وقت مقرر تک کے لیے جس کو وہ بہرحال پہنچنے والے تھے ، ہٹا لیتے تھے تو وہ یککت اپنے عہد سے پھر جاتے');
INSERT INTO "sayyid_qutb" VALUES(7,136,'7:136','تب ہم نے ان سے انتقام لیا اور انہیں سمندر میں غرق کردیا کیونکہ انہوں نے ہماری نشانیوں کو جھٹلایا اور ان سے بےپرواہ ہوگئے تھے');
INSERT INTO "sayyid_qutb" VALUES(7,137,'7:137','اور ان کی جگہ ہم نے ان لوگوں کو جو کمزور بنا کر رکھے گئے تھے ، اس سرزمین کے مشرق و مغرب کا وارث بنا دیا جسے ہم نے برکتوں سے مالا مال کیا تھا۔ اس طرح بنی اسرائیل کے حق میں تیرے رب کا وعدہ خیر پورا ہوا کیونکہ انہوں نے صبر سے کام لیا تھا اور ہم نے فرعون اور اس کی قوم کا وہ سب کچھ برباد کردیا جو وہ بناتے اور چڑھاتے تھے "');
INSERT INTO "sayyid_qutb" VALUES(7,138,'7:138','بنی اسرائیل کو ہم نے سمندر سے گزار دیا ، پھر وہ چلے اور راستے میں ایک ایسی قوم پر ان کا گزر ہوا جو اپنے چند بتوں کی گرویدہ بنی ہوئی تھی۔ کہنے لگے " اے موسیٰ ، ہمارے لیے بھی کوئی ایسا معبود بنا دے جسیے ان لوگوں کے معبود ہیں۔ موسیٰ نے کہا " تم لوگ بڑی نادانی کی باتیں کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(7,139,'7:139','یہ لوگ جس طریقہ کی پیروی کر رہے ہیں وہ تو برباد ہونے والا ہے اور جو عمل وہ کر رہے ہیں وہ سراسر باطل ہے "');
INSERT INTO "sayyid_qutb" VALUES(7,140,'7:140','پھر موسیٰ نے کہا " کیا اللہ کے سوا کوئی اور معبود تمہارے لیے تلاش کروں ؟ حالانکہ وہ اللہ ہی ہے جس نے تمہیں دنیا بھر کی قومیں پر فضیلت بخشی ہے');
INSERT INTO "sayyid_qutb" VALUES(7,141,'7:141','اور (اللہ فرماتا ہے) وہ وقت یاد کرو جب ہم نے فرعون والوں سے تمہیں نجات دی ، جن کا حال یہ تھا کہ تمہیں سخت عذاب میں مبتلا رکھتے تھے ، تمہارے بیٹوں کو قتل کرتے اور تمہاری عورتوں کو زندہ رہنے دیتے تھے اور اس میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی');
INSERT INTO "sayyid_qutb" VALUES(7,142,'7:142','ہم نے موسیٰ کو تیس شب و روز کے لیے (کوہ سینا پر) طلب کیا اور بعد میں دس دن کا اور اضافہ کردیا ، اس طرح اس کے رب کی مقرر کردہ مدت پورے چالیس دن ہوگئی۔ موسیٰ نے چلتے ہوئے اپنے بھائی ہارون سے کہا کہ " میرے پیچھے تم میری قوم میں میری جانشینی کرنا اور ٹھیک کام کرتے رہنا اور بگاڑ پیدا کرنے والوں کے طریقے پر نہ چلنا "');
INSERT INTO "sayyid_qutb" VALUES(7,143,'7:143','جب وہ ہمارے مقرر کیے ہوئے وقت پر پہنچا اور اس کے رب نے اس سے کلام کیا تو اس نے التجا کی کہ " اے رب ! مجھے یارائے نظر دے کہ میں تجھے دیکھوں "۔ فرمایا " تو مجھے نہیں دیکھ سکتا۔ ہاں ذرا سامنے کے پہاڑ کی طرف دیکھ ، اگر وہ اپنی جگہ قائم رہ جائے تو البتہ تو مجھے دیکھ سکے گا " چناچہ اس کے رب نے جب پہاڑ پر تجلی کی تو اسے ریزہ ریزہ کردیا اور موسیٰ غش کھا کر گر پڑا۔ جب ہوش آیا تو بولا " پاک ہے تیری ذات ، میں تیرے حضور توبہ کرتا ہوں اور سب سے پہلا ایمان لانے والا میں ہوں "');
INSERT INTO "sayyid_qutb" VALUES(7,144,'7:144','فرمایا " اے موسیٰ ! میں نے تمام لوگوں پر ترجیح دے کر تجھے منتخب کیا کہ میری پیغمبری کرے اور مجھ سے ہم کلام ہو۔ پس جو کچھ میں تجھے دوں اسے لے اور شکر بجا لا');
INSERT INTO "sayyid_qutb" VALUES(7,145,'7:145','اس کے بعد ہم نے موسیٰ کو ہر شعبہ زندگی کے متلعق نصیحت اور ہر پہلو کے متعلق واضح ہدایت تختیوں پر لکھ کر دے دی اور اس سے کہا " ان ہدایات کو مضبوط ہاتھوں سے سنبھال اور اپنی قوم کو حکم دے کہ ان کے بہتر مفہوم کی پیروی کریں ، عنقریب میں تمہیں فاسقوں کے گھر دکھاؤں گا');
INSERT INTO "sayyid_qutb" VALUES(7,146,'7:146','میں اپنی نشانیوں سے ان لوگوں کی نگاہیں پھیر دوں گا جو بغیر کسی حق کے زمین میں بڑے بنتے ہیں ، وہ خواہ کوئی نشانی دیکھ لیں کبھی اس پر ایمان نہ لائیں گے ، اگر سیدھا راستہ ان کے سامنے آئے تو اسے اختیار نہ کریں گے اور اگر ٹیڑھا راستہ نظر آئے تو اس پر چل پڑیں گے ، اس لیے کہ انہوں نے ہماری نشانیوں کو جھٹلایا اور ان سے بےپروائی کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(7,147,'7:147','ہماری نشانیوں کو جس کسی نے جھٹلایا اور آخرت کی پیشی کی انکار کیا اس کے سارے اعمال ضائع ہوگئے۔ کیا لوگ اس کے سوا کچھ اور جزا پا سکتے ہیں کہ جیسا کریں ویسا بھریں');
INSERT INTO "sayyid_qutb" VALUES(7,148,'7:148','موسیٰ کے پیچھے اس کی قوم کے لوگوں نے اپنے زیوروں سے ایک بچھڑے کا پتلا بنایا جس میں بیل کی سی آواز نکلتی تھی۔ کیا انہیں نظر نہ آتا تھا کہ وہ نہ ان سے بولتا ہے نہ کسی معاملہ میں ان کی رہنمائی کرتا ہے ؟ مگر پھر بھی انہوں نے اسے معبود بنا لیا اور وہ سخت ظالم تھے');
INSERT INTO "sayyid_qutb" VALUES(7,149,'7:149','پھر جب ان کی فریب خوردگی کا طلسم ٹوٹ گیا اور انہوں نے دیکھ لیا کہ درحقیقت وہ گمراہ ہوگئے ہیں تو کہنے لگے کہ " اگر ہمارے رب نے ہم پر رحم نہ فرمایا اور ہم سے درگزر نہ کیا تو ہم برباد ہوجائیں گے "');
INSERT INTO "sayyid_qutb" VALUES(7,150,'7:150','ادھر سے موسیٰ غصے اور رنج میں بھرا ہوا اپنی قوم کی طرف پلٹا۔ آتے ہی اس نے کہا " بہت بری جانشینی کی تم لوگوں نے میرے بعد ! کیا تم سے اتنا صبر نہ ہوا کہ اپنے رب کے حکم کا انتظار کرلیتے ؟ " اور تختیاں پھینک دیں اور اپنے بھائی (ہارون) کے سر کے بال پکڑ کر اسے کھینچا۔ ہارون نے کہا " اے میری ماں کے بیٹے ، ان لوگوں نے مجھے دبا لیا اور قریب تھا کہ مجھے مار ڈالتے۔ پس تو دشمنوں کو مجھ پر ہنسنے کا موقع نہ دے اور اس ظالم گروہ کے ساتھ مجھے نہ شامل کر۔ "');
INSERT INTO "sayyid_qutb" VALUES(7,151,'7:151','تب موسیٰ نے کہا " اے رب ، مجھے اور میرے بھائی کو معاف کر اور ہمیں اپنی رحمت میں داخل فرما ، تو سب سے بڑھ کر رحیم ہے "');
INSERT INTO "sayyid_qutb" VALUES(7,152,'7:152','(جواب میں ارشاد ہوا) " کہ جن لوگوں نے بچھڑے کو معبود بنایا وہ ضرور اپنے رب کے غضب میں گرفتار ہو کر رہیں گے اور دنیا کی زندگی میں ذلیل ہوں گے۔ جھوٹ گھڑنے والوں کو ہم ایسی ہی سزا دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,153,'7:153','اور جو لوگ برے عمل کریں پھر توبہ کرلیں اور ایمان لے آئیں تو یقینا اس توبہ و ایمان کے بعد تیرا رب در گزر اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,154,'7:154','پھر جب موسیٰ کا غصہ ٹھنڈا ہوا تو اس نے وہ تختیاں اٹھا لیں جن کی تحریر میں ہدایت اور رحمت تھی ان لوگوں کے لیے جو اپنے رب سے ڈرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,155,'7:155','اور اس نے اپنی قوم کے ستر آدمیوں کو منتخب کیا تاکہ وہ (اس کے ساتھ) ہمارے مقرر کیے ہوئے وقت پر حاضر ہوں۔ جب ان لوگوں کو ایک سخت زلزلے نے آپکڑا تو موسیٰ نے عرض کیا " اے میرے سرکار ، آپ چاہتے تو پہلے ہی ان کو اور مجھے ہلاک کرسکتے تھے۔ کیا آپ اس قصور میں جو ہم میں سے چند نادانوں نے کیا تھا ہم سب کو ہلاک کردیں گے ؟ یہ تو آپ کی ڈالی ہوئی ایک آزمائش تھی جس کے ذریعہ سے آپ جسے چاہتے ہیں گمراہی میں مبتلا کردیتے ہیں اور جسے چاہتے ہیں ہدایت بخش دیتے ہیں۔ ہمارے سرپرست تو آپ ہی ہیں۔ پس ہمیں معاف کردیجئے اور ہم پر رحم فرمائیے ، آپ سب سے بڑھ کر معاف فرمانتے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,156,'7:156','اور ہمارے لیے اس دنیا کی بھلائی بھی لکھ دیجئے اور آخرت کی بھی ، ہم نے آپ کی طرف رجوع کرلیا۔ جواب ارشاد ہوا " سزا تو میں جسے چاہتا ہوں دیتا ہوں ، مگر میری رحمت ہر چیز پر چھائی ہوئی ہے۔ اور اسے میں ان لوگوں کے حق میں لکھوں گا جو نافرمانی سے پرہیز کریں گے ، زکوۃ دیں گے اور میری آیات پر ایمان لائیں گے');
INSERT INTO "sayyid_qutb" VALUES(7,157,'7:157','(پس آج یہ رحمت ان لوگوں کا حصہ ہے) جو اس پیغمبر ، نبی امی (صلى الله عليه وسلم) کی پیروی اختیار کریں۔ جس کا ذکر انہیں اپنے ہاں تورات اور انجیل میں لکھا ہوا ملتا ہے۔ وہ انہیں نیکی کا حکم دیتا ہے ، بدی سے روکتا ہے ، ان کے لیے پاک چیزیں حلال اور ناپاک چیزیں حرام کرتا ہے اور ان پر سے وہ بوجھ اتارتا ہے جو ان پر لدے ہوئے تھے اور وہ بندشیں کھولتا ہے جن میں وہ جکڑے ہوئے تھے۔ لہذا جو لوگ اس پر ایمان لائیں اور اس کی حمایت اور نصرت لڑیں اور اس روشنی کی پیروی اختیار کریں جو اس کے ساتھ نازل کی گئی ہے ، وہی فلاح پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,158,'7:158','اے محمد ان سے کہو کہ " اے انسانو ! میں تم سب کی طرف اس خدا کا پیغمبر ہوں جو زمین اور آسمانوں کی بادشاہی کا مالک ہے ، اس کے سوا کوئی خدا نہیں ہے ، وہی زندگی بخشتا ہے اور وہی موت دیتا ہے ، پس ایمان لاؤ اللہ پر اور اس کے بھیجے ہوئے نبی امی پر جو اللہ اور اس کے ارشادات کو مانتا ہے ، اور پیروی اختیار کرو اس کی ، امید ہے کہ تم راہ راست پا لوگے');
INSERT INTO "sayyid_qutb" VALUES(7,159,'7:159','موسیٰ کی قوم میں ایک گروہ ایسا بھی تھا جو حق کے مطابق ہدایت کرتا اور حق ہی کے مطابق انصاف کرتا تھا');
INSERT INTO "sayyid_qutb" VALUES(7,160,'7:160','اور ہم نے اس قوم کو بارہ گھرانوں میں تقسیم کرکے انہیں مستقل گروہوں کی شکل دے دی تھی۔ اور جب موسیٰ سے اس کی قوم نے پانی مانگا تو ہم نے اس کو اشارہ کیا کہ فلاں چٹان پر اپنی لاٹھی مارو۔ چناچہ اس چٹان سے یکایک بارہ چشمے پھوٹ نکلے اور ہر گروہ نے اپنے پانی لینے کی جگہ متعین کرلی۔ ہم نے ان پر بادل کا سایہ کیا اور ان پر من وسلوی اتارا۔ " کھاؤ وہ پاک چیزیں جو ہم نے تم کو بخشی ہیں " مگر اس کے بعد انہوں نے جو کچھ کیا تو ہم پر ظلم نہیں کیا بلکہ آپ اپنے اوپر ظلم کرتے رہے "');
INSERT INTO "sayyid_qutb" VALUES(7,161,'7:161','یاد کرو وہ وقت جب ان سے کہا گیا کہ " اس بستی میں جا کر بس جاؤ اور اس کی پیداوار سے اپنے حسب منشا روزی حاصل کرو اور حطۃ حطۃ کہتے جاؤ اور شہر کے دروازے میں سجدہ ریز ہوتے ہوئے داخل ہو ، ہم تمہاری خطائیں معاف کریں گے اور نیک رویہ رکھنے والوں کو مزید فضل سے نوازیں گے "');
INSERT INTO "sayyid_qutb" VALUES(7,162,'7:162','مگر جو لوگ ان میں سے ظالم تھے انہوں نے اس بات کو جو ان سے کہی گئی تھی ، بدل ڈالا اور نتیجہ یہ ہوا کہ ہم نے ان کے ظلم کی پاداش میں ان پر آسمان سے عذاب بھیج دیا');
INSERT INTO "sayyid_qutb" VALUES(7,163,'7:163','اور ذرا ان سے اس بستی کا حال بھی پوچھو جو سمندر کے کنارے واقع تھی۔ انہیں یاد دلاؤ وہ واقعہ کہ وہاں کے لوگ سبت (ہفتہ) کے دن احکام الٰہی کی خلاف ورزی کرتے تھے اور یہ کہ مچھلیاں سبت ہی کے دن ابھر ابھر کر سطح پر ان کے سامنے آتی تھیں اور سب کے سوا باقی دنوں میں نہیں آتی تھیں۔ یہ اس لیے ہوتا تھا کہ ہم ان کی نافرمانیوں کی وجہ سے ان کو آزمائش میں ڈال رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(7,164,'7:164','اور انہیں یہ بھی یاد دلاؤ کہ جب ان میں سے ایک گروہ نے دوسرے گروہ سے کہا تھا کہ " تم ایسے لوگوں کو کیوں نصیحت کرتے ہو جنہیں اللہ ہلاک کرنے والا یا سخت سزا دینے والا ہے " تو انہوں نے جواب دیا تھا کہ " ہم یہ سب کچھ تمہارے رب کے حضور اپنی معذرت پیش کرنے کے لیے کرتے ہیں اور اس امید پر کرتے ہیں کہ شاید یہ لوگ اس کی نافرمانی سے پرہیز کرنے لگیں "');
INSERT INTO "sayyid_qutb" VALUES(7,165,'7:165','آخر کار جب وہ ان ہدایات کو بالکل ہی فراموش کرگئے جو انہیں یاد کرائی گئی تھیں تو ہم نے ان لوگوں کو بچا لیا جو برائی سے روکتے تھے ، اور باقی سب لوگوں کو جو ظالم تھے ان کی نافرمانیوں پر سخت عذاب میں پکڑ لیا');
INSERT INTO "sayyid_qutb" VALUES(7,166,'7:166','پھر جب وہ پوری سرکشی کے ساتھ وہی کام کیے چلے گئے جس سے انہیں روکا گیا تھا ، تو ہم نے کہا بندر ہوجاؤ ذلیل اور خوار');
INSERT INTO "sayyid_qutb" VALUES(7,167,'7:167','اور یاد کرو جب کہ تمہارے رب نے اعلان کردیا کہ وہ قیامت تک برابر ایسے لوگ بنی اسرائیل پر مسلط کرتا رہے گا جو ان کو بدترین عذاب دیں گے " یقینا تمہارا رب سزا دینے میں تیز دست ہے اور یقینا وہ درگزر اور رحم سے بھی کام لینے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(7,168,'7:168','ہم نے ان کو زمین میں ٹکڑے ٹکڑے کرکے بہت سی قوموں میں تقسیم کردیا۔ کچھ لوگ ان میں نیک تھے اور کچھ اس سے مختلف۔ اور ہم ان کو اچھے اور برے حالات سے آزمائش میں مبتلا کرتے رہے کہ شاید یہ پلٹ آئیں');
INSERT INTO "sayyid_qutb" VALUES(7,169,'7:169','پھر اگلی نسلوں کے بعد ایسے ناخلف ان کے جانشین ہوئے کتاب الٰہی کے وارث ہو کر اسی دنیائے دنی کے فائدے سمیٹتے ہیں اور کہہ دیتے ہیں کہ توقع ہے ہمیں معاف کردیا جائے گا ، اور اگر وہی متاع دنیا سامنے آتی ہے تو پھر لپک کر اسے لے لیتے ہیں۔ کیا ان سے کتاب کا عہد نہیں لیا جا چکا ہے کہ اللہ کے نام پر وہی بات کہیں جو حق ہو ؟ اور یہ خود پڑھ چکے ہیں جو کتاب میں لکھا ہے۔ آخرت کی قیام گاہ تو خدا ترس لوگوں کے لیے ہی بہتر ہے ، کیا تم اتنی سی بات نہیں سمجھتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(7,170,'7:170','جو لوگ کتاب کی پابندی کرتے ہیں اور جنہوں نے نماز قائم کر رکھی ہے ، یقیناً ایسے نیک کردار لوگوں کا اجر ہم ضائع نہیں کریں گے');
INSERT INTO "sayyid_qutb" VALUES(7,171,'7:171','انہیں وہ وقت بھی کچھ یاد ہے جب کہ ہم نے پہاڑ کو بلا کر ان پر اس طرح چھا دیا تھا کہ گویا وہ چھتری ہے اور یہ گمان کر رہے تھے کہ وہ ان پر آپڑے گے اور اس وقت ہم نے ان سے کہا تھا کہ جو کتاب ہم تمہیں دے رہیں اسے مضبوطی کے ساتھ تھامو اور جو کچھ اس میں لکھا ہے اسے یاد رکھو ، توقع ہے کہ تم غلط روی سے بچے رہو گے');
INSERT INTO "sayyid_qutb" VALUES(7,172,'7:172','اور اے نبی لوگو کو یاد دلاؤ وہ وقت جب کہ تمہارے رب نے بنی آدم کی پشتوں سے ان کی نسل کو نکالا تھا اور انہیں خود ان کے اوپر گواہ بناتے ہوئے پوچھا تھا " کیا میں تمہارا رب نہیں ہوں ؟ " انہوں نے کہا " ضرور آپ ہی ہمارے رب ہیں ، ہم اس پر گواہی دیتے ہیں۔ یہ ہم نے اس لیے کیا کہ کہیں تم قیامت کے روز یہ نہ کہہ دو کہ " ہم تو اس بات سے بیخبر تھے "');
INSERT INTO "sayyid_qutb" VALUES(7,173,'7:173','یا یہ نہ کہنے لگو کہ " شرک کی ابتداء تو ہمارے باپ دادا نے ہم سے پہلے کی تھی اور ہم بعد کو ان کی نسل سے پیدا ہوئے ، پھر کیا آپ ہمیں اس قصور میں پکڑتے ہیں جو غلط کار لوگوں نے کیا تھا ؟ "');
INSERT INTO "sayyid_qutb" VALUES(7,174,'7:174','دیکھو اس طرح ہم نشانیاں واضح طور پر پیش کرتے ہیں۔ اور اس لیے کرتے ہیں کہ یہ لوگ پلٹ آئیں');
INSERT INTO "sayyid_qutb" VALUES(7,175,'7:175','اور اے نبی ان کے سامنے اس شخص کا حال بیان کرو جس کو ہم نے اپنی ٓیات کا علم عطا کیا تھا مگر وہ ان کی پابندے سے نکل بھاگا۔ آخر کار شیطان اس کے یچھے پڑگیا۔ یہاں تک کہ وہ بھٹکنے والوں میں شامل ہو کر رہا۔');
INSERT INTO "sayyid_qutb" VALUES(7,176,'7:176','اگر ہم چاہتے تو اسے ان آیتوں کے ذریعے سے بلندی عطا کرتے ، مگر وہ تو زمین ہی کی طرف جھک کر رہ گیا اور اپنی خواہش نفس ہی کے پیچھے پڑا رہا ، لہذا اس کی حالت کتے کی سی ہوگئی کہ تم اس پر حملہ کرو تب بھی زبان لٹکائے رہے اور اسے چھڑو تب بھی زبان لٹکائے رہے۔ یہی مثال ہے ان لوگوں کی جو ہماری آیات کو جھٹلاتے ہیں ، تم یہ حکایات ان کو سناتے رہو ، شاید کہ یہ کچھ غور و فکر کریں');
INSERT INTO "sayyid_qutb" VALUES(7,177,'7:177','بڑی ہی بری مثال ہے ایسے لوگوں کی جنہوں نے ہماری آیات کو جھٹلایا اور وہ آپ اپنے ہی اوپر ظلم کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,178,'7:178','جسے اللہ ہدایت بخشے بس وہی راہ راست پاتا ہے اور جس کو اللہ اپنی رہنمائی سے محروم کردے وہی ناکام و نامراد ہوکر رہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,179,'7:179','اور یہ حقیقت ہے کہ بہت سے جن اور انسان ایسے ہیں جن کو ہم نے جہنم ہی کے لیے پیدا کیا ہے۔ ان کے پاس دل ہیں مگر وہ ان سے سوچتے نہیں۔ ان کے پاس آنکھیں ہیں مگر وہ ان سے دیکھتے نہیں۔ ان کے پاس کان ہیں مگر وہ ان سے سنتے نہیں۔ وہ جانوروں کی طرح ہیں بلکہ ان سے بھی زیادہ گئے گزرے ، یہ وہ لوگ ہیں جو غفلت میں کھوئے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,180,'7:180','اللہ اچھے ناموں کا مستحق ہے اس کو اچھے ہی ناموں سے پکارو اور ان لوگوں کو چھوڑ دو جو اس کے نام رکھنے میں راستی سے منحرف ہوجاتے ہیں۔ جو کچھ وہ کرتے ہیں ، اس کا بدلہ وہ پاکر رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(7,181,'7:181','ہماری مخلوق میں ایک گروہ ایسا بھی ہے جو ٹھیک ٹھیک حق کے ساتھ ہدایت اور حق کے مطابق انصاف کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,182,'7:182','رہے وہ لوگ جنہوں نے ہماری آیات کو جھٹلا دیا ہے ، تو انہیں ہم بتدریج ایسے طریقہ سے تباہی کی طرف لے جائیں گے کہ انہیں خبر تک نہ ہوگی');
INSERT INTO "sayyid_qutb" VALUES(7,183,'7:183','میں ان کو ڈھیل دے رہا ہوں ، میری چال کا کوئی توڑ نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(7,184,'7:184','اور کیا ان لوگوں نے کبھی سوچا نہیں ؟ ان کے رفیق پر جنون کا کوئی اثر نہیں ہے۔ وہ تو ایک خبردار کرنے والا ہے (جو برا انجام سامنے ٓنے سے پہلے) صاف صاف متنبہ کر رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,185,'7:185','کیا ان لوگوں نے اسمان و زمین کے انتظام پر کبھی غور نہیں کیا اور کسی چیز کو بھی جو خدا نے پیدا کی ہے ، آنکھیں کھول کر نہیں دیکھا ؟ اور کیا یہ بھی انہوں نے نہیں سوچا کہ شاید ان کی مہلت زندگی پوری ہونے کا وقت قریب آ لگا ہو ؟ پھر آخر پیغمبر کی اس تنبیہ کے بعد اور کون سی بات ایسی ہوسکتی ہے جس پر یہ ایمان لائیں ؟');
INSERT INTO "sayyid_qutb" VALUES(7,186,'7:186','جس کو اللہ رہنمائی سے محروم کردے اس کے لیے پھر کوئی رہنما نہیں ہے ، اور اللہ انہیں ان کی سرکشی ہی میں بھٹکتا ہوا چھوڑ دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,187,'7:187','یہ لوگ تم سے پوچھتے ہیں کہ آخر وہ قیامت کی گھڑی کب نازل ہوگی ؟ کہو " اس کا علم میرے رب ہی کے پاس ہے۔ اسے اپنے وقت پر وہی ظاہر کرے گا۔ آسمانوں اور زمین میں وہ بڑا سخت وقت ہوگا۔ وہ تم پر اچانک آجائے گا " یہ لوگ اس کے متعلق تم سے اس طرح پوچھتے ہیں گویا کہ تم اس کی کھوج میں لگے ہوئے ہو۔ کہو " اس کا علم تو صرف اللہ کو ہے ، مگر اکثر لوگ اس حقیقت سے ناواقف ہیں "');
INSERT INTO "sayyid_qutb" VALUES(7,188,'7:188','اے نبی ان سے کہو کہ " میں اپنی ذات کے لیے کسی نفع اور نقصان کا اختیار نہیں رکھتا۔ اللہ ہی جو کچھ چاہتا ہے وہ ہوتا ہے۔ اور اگر مجھے غیب کا علم ہوتا تو میں بہت سے فادے اپنے لیے حاصل کرلیتا اور مجھے کوئی نقصان نہ پہنچتا۔ میں تو محض ایک خبردار کرنے والا اور خوشخبری سنانے والا ہوں ان لوگوں کے لیے جو میری بات مانیں');
INSERT INTO "sayyid_qutb" VALUES(7,189,'7:189','وہ اللہ ہی ہے جس نے تمہیں ایک جان سے پیدا کیا اور اسی کی جنس سے اس کا جوڑا بنایا تاکہ اس کے پاس سکون حاصل کرے۔ پھر جب مرد نے عورت کو ڈھانک لیا تو اسے ایک خفیف سا حمل رہ گیا جسے لیے لیے وہ چلتی پھرتی رہی۔ پھر جب وہ بوجھل ہوگئی تو دونوں نے مل کر اللہ اپنے رب سے دعا کی کہ اگر تونے ہم کو اچھا سا بچہ دیا تو ہم تیرے شکر گزار ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(7,190,'7:190','مگر جب اللہ نے ان کو ایک صیح وسالم بچہ دے دیا تو وہ اس کی بخشش و عنایت میں دوسروں کو اس کا شریک ٹھہرانے لگے۔ اللہ بہت بلندو برتر ہے۔ ان مشرکانہ باتوں سے جو یہ لوگ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,191,'7:191','کیسے نادان ہیں یہ لوگ کہ ان کو خدا کا شریک ٹھہراتے ہیں جو کسی چیز کو پیدا نہیں کرتے بلکہ خود پیدا کئے جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,192,'7:192','جو نہ ان کی مدد کرسکتے ہیں اور نہ آپ اپنی مدد ہی پر قادر ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,193,'7:193','اگر تم انہیں سیدھی راہ پر آنے کی دعوت دو تو وہ تمہارے پیچھے نہ آئیں۔ تم خواہ انہیں پکارو یا خاموش رہو ، دونوں صورتوں میں تمہارے لیے یکساں ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(7,194,'7:194','تم لوگ خدا کو چھوڑ کر جنہیں پکارتے ہو وہ تو محض بندے ہیں جیسے تم بندے ہو۔ ان سے دعائیں مانگ دیکھو ، یہ تمہاری دعاؤں کا جواب دیں اگر ان کے بارے میں تمہارے خیالات صحیح ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,195,'7:195','کیا یہ پاؤں رکھتے ہیں کہ ان سے چلیں ؟ کیا یہ ہاتھ رکھتے ہیں کہ ان سے پکڑیں ؟ کیا یہ آنکھیں رکھتے ہیں کہ ان سے دیکھیں ؟ کیا یہ کان رکھتے ہیں کہ ان سے چلیں ؟ کیا یہ ہاتھ رکھتے ہیں کہ ان سے پکڑیں ؟ کیا یہ آنکھیں رکھتے ہیں کہ ان سے دیکھیں ؟ کیا یہ کان رکھتے ہیں کہ ان سے سنیں ؟ اے نبی ان سے کہو کہ " بلا لو اپنے ٹھہرائے ہوئے شریکوں کو پھر تم سب مل کر میرے خلاف تدبیریں کرو اور مجھے ہرگز مہلت نہ دو');
INSERT INTO "sayyid_qutb" VALUES(7,196,'7:196','میرا حامی و ناصر وہ خدا ہے جس نے یہ کتاب نازل کی ہے اور وہ نیک آدمیوں کی حمایت کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,197,'7:197','بخلاف اس کے تم جنہیں خدا کو چھوڑ کر پکارتے ہو وہ نہ تمہاری مدد کرسکتے ہیں اور نہ خود اپنی مدد ہی کرنے کے قابل ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,198,'7:198','بلکہ اگر تم انہیں سیدھی راہ پر آنے کے لیے کہو تو وہ تمہاری بات سن بھی نہیں سکتے۔ بظاہر تم کو ایسا نظر آتا ہے کہ وہ تمہاری طرف دیکھ رہے ہیں مگر فی الواقع وہ کچھ بھی نہیں دیکھتے');
INSERT INTO "sayyid_qutb" VALUES(7,199,'7:199','اے نبی ! نرمی و درگزر کا طریقہ اختیار کرو ، معروف کی تلقین کیے جاؤ اور جاہلوں سے نہ الجھو');
INSERT INTO "sayyid_qutb" VALUES(7,200,'7:200','اگر کبھی شیطان تمہیں اکسائے تو اللہ کی پناہ مانگو ، وہ سب کچھ سننے والا اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,201,'7:201','حقیقت میں جو لوگ متقی ہیں ان کا حال تو یہ ہوتا ہے کہ کبھی شیطان کے اثر سے کوئی برا خیال اگر انہیں چھو بھی جاتا ہے تو فوراً چوکنے ہوجاتے ہیں اور پھر انہیں صاف نظر آنے لگتا ہے کہ ان کے لیے صحیح طریق کار کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(7,202,'7:202','رہے ان کے (یعنی شیاطین کے) بھائی بند ، تو وہ انہیں ان کی کج روی میں کھینچے لیے چلے جاتے ہیں اور بھٹکانے میں کوئی کسر اٹھا نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(7,203,'7:203','اے نبی جب تم ان لوگوں کے سامنے کوئی نشانی (یعنی معجزہ) پیش نہیں کرتے تو یہ کہتے ہیں کہ تم نے اپنے لیے کوئی نشانی کیوں نہ انتخاب کرلی ؟ ان سے کہو " میں تو صرف اس وحی کی پیروی کرتا ہوں جو میرے رب نے میری طرف بھیجی ہے۔ یہ بصیرت کی روشنیاں ہیں تمہارے رب کی طرف سے اور ہدایت اور حمت ہے ان لوگوں کے لیے جو اسے قبول کریں');
INSERT INTO "sayyid_qutb" VALUES(7,204,'7:204','جب قران تمہارے سامنے پڑھا جائے تو اسے توجہ سے سنو اور خاموش رہو ، شاید کہ تم پر بھی رحمت ہوجائے');
INSERT INTO "sayyid_qutb" VALUES(7,205,'7:205','اور اے نبی ! اپنے رب کو صبح و شام یاد کیا کرو دل ہی دل میں زاری اور خوف کے ساتھ اور زبان سے بھی ہلکی آواز کے ساتھ۔ تم ان لوگوں میں سے نہ ہوجاؤ جو غفلت میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(7,206,'7:206','جو فرشتے تمہارے رب کے حضور تقرب کا مقام رکھتے ہیں وہ کبھی انی بڑائی کے گھمنڈ میں آ کر اس کی عبادت سے منہ نہیں موڑتے اور اس کی تسبیح کرتے ہیں اور اس کے آگے جھکے رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,1,'8:1','لوگ تم سے انفال کے متعلق پوچھتے ہیں۔ کہو " یہ انفال تو اللہ اور اس کے رسول کے ہیں۔ پس تم لوگ اللہ سے ڈرو اور اپنے آپس کے تعلقات درست کرو اور اللہ اور اس کے رسول کی اطاعت کرو ، اگر تم مومن ہو "');
INSERT INTO "sayyid_qutb" VALUES(8,2,'8:2','سچے اہل ایمان تو وہ لوگ ہیں جن کے دل اللہ کا ذکر سن کر لرز جاتے ہیں اور جب اللہ کی آیات ان کے سامنے پڑھی جاتی ہیں تو ان کا ایمان بڑھ جاتا ہے اور وہ اپنے رب پر اعتماد رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,3,'8:3','جو نماز قائم کرتے ہیں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے (ہماری راہ میں) خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,4,'8:4','ایسے ہی لوگ حقیقی مومن ہیں۔ ان کے لیے ان کے رب پاس بڑے درجے ہیں (قصوروں سے درگذر ہے اور بہترین رزق ہے)');
INSERT INTO "sayyid_qutb" VALUES(8,5,'8:5','(اس مال غنیمت کے معاملہ میں بھی وسی ہی صورت پیش آرہی ہے جیسی اس وقت پیش آئی تھی جب کہ) تیرا رب تجھے حق کے ساتھ تیرے گھر سے نکال لایا تھا اور مومنوں میں سے ایک گروہ کو یہ ناگوار تھا');
INSERT INTO "sayyid_qutb" VALUES(8,6,'8:6','وہ اس حق کے معاملہ میں تجھ سے جھگڑ رہے تھے۔ در آں حالیکہ وہ صاف صاف نمایاں ہوچکا تھا۔ ان کا حال یہ تھا کہ گویا وہ آنکھوں دیکھتے موت کی طرف ہانکے جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,7,'8:7','یاد کرو وہ موقع جب کہ اللہ تم سے وعدہ کر رہا تھا کہ دونوں گروہوں میں سے ایک تمہیں مل جاے گا۔ تم چاہتے تھے کہ کمزور گروہ تمہیں ملے۔ مگر اللہ کا ارادہ یہ تھا کہ اپنے ارشادات سے حق کو حق کر دکھائے اور کافروں کی جڑ کاٹ دے');
INSERT INTO "sayyid_qutb" VALUES(8,8,'8:8','تاکہ حق حق ہو کر رہے اور باطل باطل ہوکر رہ جائے خواہ مجرموں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "sayyid_qutb" VALUES(8,9,'8:9','اور وہ موقع جب کہ تم اپنے رب سے فریاد کر رہے تھے ، جواب میں اس نے فرمایا کہ میں تمہاری مدد کے لیے پے در پے ایک ہزار فرشتے بھیج رہا ہوں');
INSERT INTO "sayyid_qutb" VALUES(8,10,'8:10','یہ بات اللہ نے تمہیں صرف اس لیے بتا دی کہ تمہیں خوشخبری ہو اور تمہارے دل اس سے مطمئن ہوجائیں ، ورنہ مدد تو جب بھی ہوتی ہے اللہ ہی کی طرف سے ہوتی ہے یقیناً اللہ زبردست اور دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,11,'8:11','اور وہ وقت جب کہ اللہ اپنی طرف سے غنودگی کی شکل میں تم پر اطمینان و بےخوفی کی کیفیت طاری کر رہا تھا اور آسمان سے تمہارے اوپر پانی برسا رہا تھا تاکہ تمہیں پاک کرے اور تم سے شیطان کی ڈالی ہوئی نجاست دور کرے اور تمہاری ہمت بندھائے اور اس کے ذریعے سے تمہارے قدم جما دے');
INSERT INTO "sayyid_qutb" VALUES(8,12,'8:12','اور وہ وقت جب کہ تمہارا رب فرشتوں کو اشارہ کر رہا تھا کہ " میں تمہارے ساتھ ہوں ، تم اہل ایمان کو ثابت قدم رکھو ، میں ابھی ان کافروں کے دلوں میں رعب ڈالے دیتا ہوں ، پس تم ان کی گردنوں پر ضرب اور جوڑ جوڑ پر چوٹ لگاؤ');
INSERT INTO "sayyid_qutb" VALUES(8,13,'8:13','یہ اس لیے کہ ان لوگوں نے اللہ اور اس کے رسول کا مقابلہ کیا اور جو اللہ اور اس کے رسول کا مقابلہ کرے اللہ اس کے لیے نہایت سخت گیر ہے');
INSERT INTO "sayyid_qutb" VALUES(8,14,'8:14','یہ ہے تم لوگوں کی سزا ، اب اس کا مزہ چکھو ، اور تمہیں معلوم ہو کہ حق کا انکار کرنے والوں کے لیے دوزخ کا عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(8,15,'8:15','اے لوگو جو ایمان لائے ہو ، جب تم ایک لشکر کی صورت میں کفار سے دو چار ہو تو ان کے مقابلہ میں پیٹھ نہ پھیرو');
INSERT INTO "sayyid_qutb" VALUES(8,16,'8:16','جس نے ایسے موقع پر پیٹھ پھیری۔ الا یہ کہ نگی چال کے طور پر ایسا کرے یا کسی دوسری فوج سے جا ملنے کے لیے۔ تو وہ اللہ کے غضب میں گھر جائے گا۔ اس کا ٹھکانا جہنم ہوگا اور وہ بہت بری جائے باز گشت ہے');
INSERT INTO "sayyid_qutb" VALUES(8,17,'8:17','پس حقیقت یہ ہے کہ تم نے انہیں قتل نہیں کیا بلکہ اللہ نے ان کو قتل کیا اور اے نبی تونے نہیں پھینکا بلکہ اللہ نے پھینکا (اور مومنوں کے ہاتھ جو اس کام میں استعمال کیے گئے) تو یہ اس لیے تھا کہ اللہ مومنوں کو ایک بہترین آزمائش سے کامیابی کے ساتھ گزار دے ، یقیناً اللہ سننے اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,18,'8:18','یہ معاملہ تو تمہارے ساتھ ہے اور کافروں کے ساتھ معاملہ یہ ہے کہ اللہ ان کی چالوں کو کمزور کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,19,'8:19','(ان کافروں سے کہہ دو ) ـاگر تم فیصلہ چاہتے تھے تو لو ، فیصلہ تمہارے سامنے آگیا۔ اب باز آجاآ ، تمہارے ہی لیے بہتر ہے ، ورنہ پھر پلٹ کر اسی حماقت کا اعادہ کروگے تو ہم بھی اسی سزا کا اعادہ کریں گے اور تمہاری جمعیت ، خواہ وہ کتنی ہی زیادہ ہو ، تمہارے کچھ کام نہ آسکے گی۔ اللہ مومنوں کے ساتھ ہے');
INSERT INTO "sayyid_qutb" VALUES(8,20,'8:20','اے لوگو جو ایمان لائے ہو ، اللہ اور رسول کے رسول کی اطاعت کرو اور حکم سننے کے بعد اس سے سرتابی نہ کرو');
INSERT INTO "sayyid_qutb" VALUES(8,21,'8:21','ان لوگوں کی طرح نہ ہوجاؤ جنہوں نے کہا کہ ہم نے سنا حالانکہ وہ نہیں سنتے تھے');
INSERT INTO "sayyid_qutb" VALUES(8,22,'8:22','یقیناً خدا کے نزدیک بد ترین قسم کے جانور وہ بہرے گونگے لوگ ہیں جو عقل سے کام نہیں لیتے');
INSERT INTO "sayyid_qutb" VALUES(8,23,'8:23','اگر اللہ کو معلوم ہوتا کہ ان میں کچھ بھی بھلائی ہے تو وہ ضرور انہیں سننے کی توفیق دیتا (لیکن بھلائی کے بغیر) اگر وہ ان کو سنواتا تو وہ بےرخی کے ساتھ منہ پھیر جاتے');
INSERT INTO "sayyid_qutb" VALUES(8,24,'8:24','اے لوگو جو ایمان لائے ہو ، اللہ اور اس کے رسول کی پکار پر لبیک کہو جب کہ رسول تمہیں اس چیز کی طرف بلائے تو تمہیں زندگی بخشنے والی ہے ، اور جان رکھو کہ اللہ آدمی اور اس کے دل کے درمیان حائل ہے اور اسی کی طرف تم سمیٹے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(8,25,'8:25','اور بچو اس فتنے سے جس کی شامت مخصوص طور پر صرف انہی لوگوں تک محدود نہ رہے گی جنہوں نے تم میں سے گناہ کیا ہو اور جان رکھو کہ اللہ سخت سزا دینے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,26,'8:26','یاد کرو وہ وقت جب کہ تم تھوڑے تھے ، زمین میں تم کو بےزور سمجھا جاتا تھا ، تم ڈرتے رہتے تھے کہ کہیں لوگ تمہیں مٹا نہ دیں۔ پھر اللہ نے تم کو جائے پناہ مہیا کردی ، اپنی مدد سے تمہارے ہاتھ مضبوط کیے اور تمہیں اچھا رزق پہنچایا ، شاید کہ تم شکر گزار ہو');
INSERT INTO "sayyid_qutb" VALUES(8,27,'8:27','اے لوگو جو ایمان لائے ہو ، جانتے بوجھتے اللہ اور اس کے رسول کے ساتھ خیانت نہ کرو ، اپنی امانتوں میں غداری کے مرتکب نہ ہو');
INSERT INTO "sayyid_qutb" VALUES(8,28,'8:28','اور جان رکھو کہ تمہارے مال اور تمہاری اولاد حقیقت میں سامان آزمائش ہیں اور اللہ کے پاس اجر دینے کے لیے بہت کچھ ہے');
INSERT INTO "sayyid_qutb" VALUES(8,29,'8:29','اے لوگو جو ایمان لائے ہو ، اگر تم خدا ترسی اختیار کروگے تو اللہ تمہارے لیے کسوٹی بہم پہنچا دے گا اور تمہاری برائیوں کو تم سے دور کرے گا اور تمہارے قصور معاف کرے گا۔ اللہ بڑا فضل فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,30,'8:30','وہ وقت بھی یاد کرنے کے قابل ہے جب کہ منکرین حق تیرے خلاف تدبیریں سوچ رہے تھے کہ تجھے قید کرلیں یا قتل کر ڈالیں یا جلا وطن کردیں۔ وہ اپنی چالیں چل رہے تھے اور اللہ اپنی چال چل رہا تھا اور اللہ سب سے بہتر چال چلنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,31,'8:31','جب ان کو ہماری آیات سنائی جاتی تھی تو کہتے تھے کہ ” ہاں سن لیا ہم نے ، ہم چاہیں تو ایسی ہی باتیں ہم بھی بنا سکتے ہیں ، یہ تو وہی پرانی کہانیاں ہیں جو پہلے سے لوگ کہتے چلے آ رہے ہیں “');
INSERT INTO "sayyid_qutb" VALUES(8,32,'8:32','اور وہ بات بھی یاد ہے جو انہوں نے کہی تھی ” خدایا ، اگر یہ واقعی حق ہے تیری طرف سے تو ہم پر آسمان سے پتھر برسا دے یا کوئی دردناک عذاب ہم پر لے آ “');
INSERT INTO "sayyid_qutb" VALUES(8,33,'8:33','اس وقت تو اللہ ان پر عذاب نازل کرنے والا نہ تھا جب کہ تو ان کے درمیان موجود تھا اور نہ اللہ کا یہ قاعدہ ہے کہ لوگ استغفار کر رہے ہوں اور وہ ان کو عذاب دے دے');
INSERT INTO "sayyid_qutb" VALUES(8,34,'8:34','لیکن اب کیوں نہ وہ ان پر عذاب نازل کرے جب کہ وہ مسجد حرام کا راستہ روک رہے ہیں ، حالانکہ کوہ اس مسجد کے جائز متولی نہیں ہیں۔ اس کے جائز متولی تو صرف اہل تقوی ہی ہوسکتے ہیں ، مگر اکثر لوگ اس بات کو نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(8,35,'8:35','بیت اللہ کے پاس ان لوگوں کی نماز کیا ہوتی ہے ؟ بس سیٹیاں بجاتے اور تالیاں پیٹتے ہیں۔ پس اب لو ، اس عذاب کا مزہ چکھو اپنے اس انکار حق کی پاداش میں جو تم کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(8,36,'8:36','جن لوگوں نے حق کو ماننے سے انکار کیا ہے وہ اپنے مال خدا کے راستے سے روکنے کے لیے صرف کر رہے ہیں اور ابھی اور خرچ کرتے رہیں گے ، مگر آخر کار یہی کوششیں ، ان کے لیے پچھتاوے کا سبب بنیں گی۔ پھر وہ مغلوب ہوں گے ، پھر یہ کافر جہنم کی طرف گھیر لیے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(8,37,'8:37','تاکہ اللہ گندگی کو پاکیزگی سے چھانٹ کر الگ کرے اور ہر قسم کی گندگی کو ملا کر اکٹھا کرے۔ پھر اس پلندے کو جہنم میں جھونک دے۔ یہی لوگ اصلی دیوالیے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,38,'8:38','اے نبی ! ان کافروں سے کہو کہ اگر اب بھی باز آجائیں تو جو کچھ پہلے ہوچکا ہے اس سے درگزر کرلیا جائے گا۔ لیکن اگر یہ اسی پچھلی روش کا اعادہ کریں گے تو گزشتہ قوموں کے ساتھ جو کچھ ہوچکا ہے ، وہ سب کو معلوم ہے');
INSERT INTO "sayyid_qutb" VALUES(8,39,'8:39','اے لوگو جو ایمان لائے ہو ، ان کافروں سے جنگ کرو یہاں تک کہ فتنہ باقی نہ رہے اور دین پورا کا پورا اللہ کے لیے ہوجائے۔ پھر اگر وہ فتنہ سے رک جائیں تو ان کے اعمال کا دیکھنے والا اللہ ہے');
INSERT INTO "sayyid_qutb" VALUES(8,40,'8:40','اور اگر وہ نہ مانیں تو جان رکھو کہ اللہ تمہارا سر پرست ہے اور وہ بہترین حامی و مددگار ہے');
INSERT INTO "sayyid_qutb" VALUES(8,41,'8:41','اور ت میں معلوم ہو کہ جو کچھ مال غنیمت تم نے حاصل کیا ہے اس کا پانچواں حصہ اللہ اور اس کے رسول اور رشتہ داروں اور یتیموں اور مسینوں اور مسافروں کے لیے ہے۔ اگر تم ایمان لائے ہو اللہ پر اور اس چیز پر جو فیصلے کے روز ، یعنی دونوں فوجوں کی مڈ بھیڑ کے دن ہم نے اپنے بندے پر نازل کی تھی ، (تو یہ حصہ بخوشی ادا کرو) اللہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(8,42,'8:42','یاد کرو وہ وقت جبکہ تم وادی کے اس جانب تھے اور وہ دوسری جانب پڑاؤ ڈالے ہوئے تھے۔ اور قافلہ تم سے نیچے (ساحل) کی طرف تھا۔ اگر کہیں پہلے سے تمہارے اور ان کے درمیان مقابلہ کی قرار داد ہوچکی ہوتی تو تم ضرور اس موقع پر پہلو تہی کرجاتے ، لیکن جو کچھ پیش آیا وہ اس لیے تھا کہ جس بات کا فیصلہ اللہ کرچکا تھا اسے ظہور میں لے آئے تاکہ جسے ہلاک ہونا ہے ، وہ دلیل روشن کے ساتھ ہلاک ہو اور جسے زندہ رہنا ہے وہ دلیل روشن کے ساتھ زندہ رہے ، یقیناً خدا سننے اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,43,'8:43','اور یاد کرو وہ وقت جب کہ اے نبی خدا ان کو تمہارے خواب میں تھوڑا دکھا رہا تھا۔ اگر کہیں وہ تمہیں ان کی تعداد زیادہ دکھا دیتا تو ضرور تم لوگ ہمت ہار جاتے اور لڑائی کے معاملہ میں جھگڑا شروع کردیتے ، لیکن اللہ ہی نے اس سے تمہیں بچایا ، یقیناً وہ سینوں کا حال تک جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,44,'8:44','اور یاد کرو جب کہ مقابلے کے وقت خدا نے تم لوگوں کی نگاہوں میں دشمنوں کو تھوڑا دکھایا اور ان کی نگاہوں میں تمہیں کم کرکے پیش کیا تاکہ جو بات ہونی تھی ، اسے اللہ ظہور میں لے آئے اور آخر کار سارے معاملات اللہ ہی کی طرف رجوع ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,45,'8:45','اے لوگو جو ایمان لائے ہو ، جب کسی گروہ سے تمہارا مقابلہ ہو تو ثابت قدم رہو اور اللہ کو کثرت دے یاد کرو ، توقع ہے کہ تمہیں کامیابی نصیب ہوگی');
INSERT INTO "sayyid_qutb" VALUES(8,46,'8:46','اور اللہ اور اس کے رسول کی اطاعت کرو اور آپس میں جھگڑو نہیں ورنہ تمہارے اندر کمزوری پیدا ہوجائے گی اور تمہاری ہوا اکھڑ جائے گی۔ صبر کام لو ، یقینا اللہ صبر کرنے والوں کے ساتھ ہے');
INSERT INTO "sayyid_qutb" VALUES(8,47,'8:47','اور ان لوگوں کے سے رنگ ڈھنگ نہ اختیار کرو جو اپنے گھروں سے اتراتے اور لوگوں کو اپنی شان دکھاتے ہوئے نکلے اور جن کی روش یہ ہے کہ اللہ کے راستے سے روکتے ہیں ، جو کچھ وہ کر رہے ہیں ، وہ اللہ کی گرفت سے باہر نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(8,48,'8:48','ذرا خیال کرو اس وقت کا جب کہ شیطان نے ان لوگوں کے کرتوت ان کی نگاہوں میں خوشنما بنا کر دکھائے تھے اور ان سے کہا تھا کہ آج کوئی تم پر غالب نہیں آسکتا اور یہ کہ میں تمہارے ساتھ ہوں۔ مگر جب دونوں گروہوں کا آمنا سامنا ہوا تو وہ الٹے پاؤں پھر گیا اور کہنے لگا کہ میرا تمہارا ساتھ نہیں ہے ، میں وہ کچھ دیکھ رہا ہوں جو تم لوگ نہیں دیکھتے۔ مجھے خدا سے ڈر لگتا ہے اور خدا سے ڈر لگتا ہے اور خدا بڑی سخت سزا دینے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,49,'8:49','جب کہ منافقین اور وہ سب لوگ جن کے دلوں کو روگ لگا ہوا ہے ، کہہ رہے تھے کہ ان لوگوں تو ان کے دین نے خبط میں مبتلا کر رکھا ہے ، حالانکہ اگر کوئی اللہ پر بھروسہ کرے تو یقینا اللہ بڑا زبردست اور دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,50,'8:50','کاش تم اس حالت کو دیکھ سکتے جب کہ فرشتے مقتول کافروں کی روحیں قبض کر رہے تھے۔ وہ ان کے چہروں ، اور ان کے کو لہوں پر ضربیں لگاتے جاتے تھے اور کہتے جاتے تھے " لو اب جلنے کی سزا بھگتو ،');
INSERT INTO "sayyid_qutb" VALUES(8,51,'8:51','یہ وہ جزا ہے جس کا سامان تمہارے اپنے ہاتھوں نے پیشگی مہیا کر رکھا تھا ، ورنہ اللہ تو اپنے بندوں پر ظلم کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,52,'8:52','یہ معاملہ ان کے ساتھ اسی طرح پیش آیا جس طرح آل فرعون اور ان سے پہلے کے دوسرے لوگوں کے ساتھ پیش آتا رہا ہے کہ انہوں نے اللہ کی آیات کو ماننے سے انکار کیا اور اللہ نے ان کے گناہوں پر انہیں پکڑ لیا۔ اللہ قوت رکھتا ہے اور سخت سزا دینے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,53,'8:53','یہ اللہ کی اس سنت کے مطابق ہوا کہ وہ کسی نعمت کو جو اس نے کسی قوم کو عطا کی اس وقت تک نہیں بدلتا جب تک کہ وہ قوم خود اپنے طرز عمل کو نہیں بدل دیتی۔ اللہ سب کچھ سنے والا اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,54,'8:54','آل فرعون اور ان سے پہلے کی قوموں کے ساتھ جو کچھ پیش آیا وہ اسی ضابطہ کے مطابق تھا۔ انہوں نے اپنے رب کی آیات کو جھٹلایا۔ تب ہم نے ان کے گناہوں کی پاداش میں انہیں ہلاک کیا اور آل فرعون کو غرق کردیا۔ یہ سب ظالم لوگ تھے');
INSERT INTO "sayyid_qutb" VALUES(8,55,'8:55','یقیناً اللہ کے نزدیک زمین پر چلنے والی مخلوق میں سب سے بدتر وہ لوگ ہیں جنہوں نے حق کو ماننے سے انکار کردیا۔ پھر کسی طرح وہ اسے قبول کرنے پر تیار نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,56,'8:56','(خصوصا) ان میں سے وہ لوگ جن کے ساتھ تونے معاہدہ کیا پھر وہ ہر موقع پر اس کو توڑتے ہیں اور ذرا خدا کا خوف نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(8,57,'8:57','پس اگر یہ لوگ تمہیں لڑائی میں مل جائیں تو ان کی ایسی خبر لو کہ ان کے بعد دوسرے جو لوگ ایسی روش اختیار کرنے والے ہوں ، ان کے حواس باختہ ہوجائیں توقع ہے کہ بد عہدوں کے اس انجام سے وہ سبق لیں گے');
INSERT INTO "sayyid_qutb" VALUES(8,58,'8:58','اور اگر کبھی تمہیں کسی قوم سے خیانت کا اندیشہ ہو تو اس کے معاہدے کو علانیہ اس کے آگے پھینک دو ، یقیناً اللہ خائنوں کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(8,59,'8:59','منکرین حق اس غلط فہمی میں نہ رہیں کہ وہ بازی لے گئے ، یقینا وہ ہم کو ہرا نہیں سکتے');
INSERT INTO "sayyid_qutb" VALUES(8,60,'8:60','اور تم لوگ ، جہاں تک تمہارا بس چلے ، زیادہ سے زیادہ طاقت اور تیار بندھے رہنے والے گھوڑے ان کے مقابلہ کے لیے مہیا رکھو ، تاکہ اس کے ذریعہ سے اللہ کے اور اپنے دشمنوں کو اور ان دوسرے اعداء کو خوفزدہ کردو جنہیں تم نہیں جانتے مگر اللہ جانتا ہے۔ اللہ کی راہ میں جو کچھ تم خرچ کروگے اس کا پورا پورا بدل تمہاری طرف پلٹایا جائے گا اور تمہارے ساتھ ہر گز ظلم نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(8,61,'8:61','اور اے نبی اگر دشمن صلح و سلامتی کی طرف مائل ہوں تو تم بھی اس کے لیے آمادہ ہوجاؤ اور اللہ پر بھروسہ کرو ، یقینا وہی سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,62,'8:62','اور اگر وہ دھوکے کی نیت رکھتے ہوں تو تمہارے لیے اللہ کافی ہے۔ وہی تو ہے جس نے اپنی مدد سے اور مومنوں کے ذریعہ سے تمہاری تائید کی');
INSERT INTO "sayyid_qutb" VALUES(8,63,'8:63','اور مومنوں کے دل ایک دوسرے کے ساتھ جوڑ دیے۔ تم روئے زمین کی ساری دولت بھی خرچ کر ڈالتے تو ان لوگوں کے دل نہ جوڑ سکتے تھے مگر وہ اللہ ہے جس نے ان لوگوں کے دل جوڑے ، یقیناً وہ بڑا زبرست اور دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,64,'8:64','اے نبی ! تمہارے لی اور تمہارے پیرو اہل ایمان کے لیے تو بس اللہ ہی کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(8,65,'8:65','اے نبی ! مومنوں کو جنگ پر ابھارو ، اگر تم میں سے بیس آدمی صابر ہوں تو وہ دو سو پر غالب آئیں گے اور اگر سو آدمی ایسے ہوں تو منکرین حق میں سے ہزار آدمیوں پر بھاری رہیں گے۔ کیونکہ وہ ایسے لوگ ہیں جو سمجھ نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(8,66,'8:66','اچھا ، اب اللہ نے تمہارا بوجھ ہلکا کیا اور اسے معلوم ہوا کہ ابھی تم میں کمزوری ہے ، پس اگر تم میں سے سو آدمی صابر ہوں تو وہ دو سو پر اور ہزار آدمی ایسے ہوں تو دو ہزار پر اللہ کے حکم سے غالب آئیں گے اور اللہ ان لوگوں کے ساتھ ہے جو صبر کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(8,67,'8:67','کسی نبی کے لیے یہ زیبا نہیں ہے کہ اس کے پاس قیدی ہوں جب تک کہ وہ زمین میں دشمنوں کو اچھی کچل نہ دے۔ تم لوگ دنیا کے فائدے چاہتے ہو ، حالانکہ اللہ کے پیش نظر آخرت ہے اور اللہ غالب اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(8,68,'8:68','گر اللہ کا نوشتہ پہلے نہ لکھا جا چکا ہوتا تو جو کچھ تم لوگوں نے لیا ہے اس کی پاداش میں تم کو بڑی سزا دی جاتی');
INSERT INTO "sayyid_qutb" VALUES(8,69,'8:69','پس جو کچھ تم نے مال حاصل کیا ہے اسے کھاؤ کہ وہ حلال اور پاک ہے اور اللہ سے ڈرتے رہو ، یقینا الہ در گزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,70,'8:70','اے نبی تم لوگوں کے قبضہ میں جو قیدی ہیں ان سے کہو اگر اللہ کو معلوم ہوا کہ تمہارے دلوں میں کچھ خیر ہے تو وہ تمہیں اس سے بڑھ چڑھ کر دے گا ، جو تم سے لیا گیا ہے اور تمہاری خطائیں معاف کرے گا ، اللہ درگزر کرنے والا ہے اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,71,'8:71','لیکن اگر وہ تیرے ساتھ خیانت کا ارادہ رکھتے ہیں تو اس سے پہلے وہ اللہ کے ساتھ خیانت کرچکے ہیں ، چناچہ اسی کی سزا اللہ نے انہیں دی کہ وہ تیرے قابو میں آگئے ، اللہ سب کچھ جانتا اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(8,72,'8:72','جن لوگوں نے ایمان قبول کیا اور ہجرت کی اور اللہ کی راہ میں اپنی جانیں لڑائیں اور اپنے مال کھپائے ، اور جن لوگوں نے ہجرت کرنے والوں کو جگہ دی اور ان کی مدد کی ، وہی در اصل ایک دوسرے کے ولی ہیں۔ رہے وہ لوگ جو ایمان تو لے آے مگر ہجرت کرکے (دار الاسلام مٰں) آ نہیں گئے تو ان سے تمہارا ولایت کا کوئی تعلق نہیں ہے ، جب تک کہ وہ ہجرت کرکے نہ آجائیں۔ ہاں اگر وہ دین کے معاملہ میں تم سے مدد مانگیں تو ان کی مدد کرنا تم پر فرض ہے ، لیکن کسی ایسی قوم کے خلاف نہیں جس سے تمہارا معاہدہ ہو۔ جو کچھ تم کرتے ہو اللہ اسے دیکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(8,73,'8:73','جو لوگ منکر حق ہیں ، وہ ایک دوسرے کی حمایت کرتے ہیں۔ اگر یہ نہ کروگے تو زمین میں فتنہ اور بڑا فساد برپا ہوگا');
INSERT INTO "sayyid_qutb" VALUES(8,74,'8:74','جو لوگ ایمان لائے اور جنہوں نے اللہ کی راہ میں گھر بار چھوڑے اور جدوجہد کی اور جنہوں نے پناہ دی اور مدد کی وہی سچے مومن ہیں۔ ان کے لیے خطاؤں سے درگزر ہے اور بہترین رزق ہے ،');
INSERT INTO "sayyid_qutb" VALUES(8,75,'8:75','اور جو لوگ بعد میں ایمان لائے اور ہجرت کرکے آگئے اور تمہارے ساتھ مل کر جدوجہد کرنے لگے وہ بھی تم ہی میں شامل ہیں مگر اللہ کی کتاب میں خون کے رشتہ دار ایک دوسرے کے زیادہ حقدار ہیں ، یقینا اللہ ہر چیز کو جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,1,'9:1','اعلان براءت ہے اللہ اور اس کے رسول کی طرف سے ان مشرکین کو جن سے تم معاہدے کیے تھے');
INSERT INTO "sayyid_qutb" VALUES(9,2,'9:2','پس تم لوگ ملک میں چار مہینے اور چل پھر لو اور جان رکھو کہ تم اللہ کو عاجز کرنے والے نہیں ہو۔ اور یہ کہ اللہ منکرین حق کو رسوا کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,3,'9:3','اطلاع عام ہے اللہ اور اس کے رسول کی طرف سے حج اکبر کے دن تمام لوگوں کے لیے کہ اللہ مشرکین سے بری الذمہ ہے اور اس کا رسول بھی۔ اب اگر تم لوگ توبہ کرلو تو تمہارے ہی لیے بہتر ہے اور جو منہ پھیرتے ہو تو خوب سمجھ لو کہ تم اللہ کو عاجز کرنے والے نہیں ہو۔ اور اے نبی انکار کرنے والوں کو سخت عذاب کی خوشخبری سنا دو');
INSERT INTO "sayyid_qutb" VALUES(9,4,'9:4','بجز ان مشرین کے جن سے تم نے معاہدے کیے ، پھر انہوں نے اپنے عہد کو پورا کرنے میں تمہارے ساتھ کوئی کمی نہیں کی اور نہ تمہارے خلاف کسی کی مدد کی ، تو ایسے لوگوں کے ساتھ تم بھی مدت معاہدہ تک وفا کرو کیونکہ اللہ متقیوں ہی کو پسند کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,5,'9:5','پس جب حرام مہینے گزر جائیں تو مشرکین کو قتل کرو جہاں پاؤ اور گھیرو اور ہر گھات میں ان کی خبر لینے کے لیے بیٹھو۔ پھر اگر وہ توبہ کرلیں اور نماز قائم کریں اور زکوۃ دیں تو انہیں چھوڑ دو ۔ اللہ درگزر فرمانے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,6,'9:6','اور اگر مشرکین میں سے کوئی شخص پناہ مانگ کر تمہارے پاس آنا چاہے (تاکہ اللہ کا کلام سنے) تو اسے پناہ دے دو یہاں تک کہ وہ اللہ کا کلام سن لے۔ پھر اسے اس کے مامن تک پہنچا دو ۔ یہ اس لیے کرنا چاہیے کہ یہ لوگ علم نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(9,7,'9:7','ان مشرکین کے لیے اللہ اور اس کے رسول کے نزدیک کوئی عہد آخر کیسے ہوسکتا ہے ؟ بجز ان لوگوں کے جن سے تم نے مسجد حرام کے پاس معاہدہ کیا تھا ، تو جب تک وہ تمہارے ساتھ سیدھے رہیں تم بھی ان کے ساتھ سیدھے رہو کیونکہ اللہ متقیوں کو پسند کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,8,'9:8','مگر ان کے سوا دوسرے مشرکین کے ساتھ کوئی عہد کیسے ہوسکتا ہے جبکہ ان کا حال یہ ہے کہ تم پر قابو پا جائیں تو نہ تمہارے معاملہ میں کسی قرابت کا لحاظ کریں نہ کسی معاہدہ کی ذمہ داری کا ؟ وہ اپنی زبانوں سے تم کو راضی کرنے کی کوشش کرتے ہیں مگر دل ان کے انکار کرتے ہیں اور ان میں سے اکثر فاسق ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,9,'9:9','انہوں نے اللہ کی آیات کے بدلے تھوڑی سی قیمت قبول کرلی۔ پھر اللہ کے راستے میں سد راہ بن کر کھڑے ہوگئے۔ بہت برے کرتوت تھے جو یہ کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(9,10,'9:10','کسی مومن کے معاملہ نہ یہ قرابت کا لحاظ کرتے ہیں اور نہ کسی عہد کی ذمہ داری کا۔ اور زیادہ ہمیشہ انہی کی طرف سے ہوئی ہے');
INSERT INTO "sayyid_qutb" VALUES(9,11,'9:11','پس اگر ہی توبہ کرلیں اور نماز قائم کریں اور زکوۃ دیں تو تمہارے دینی بھائی ہیں اور جاننے والوں کے لیے ہم اپنے احکام واضح کیے دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,12,'9:12','اور اگر عہد کرنے کے بعد یہ پھر اپنی قسموں کو توڑ ڈالیں اور تمہارے دین پر حملے کرنے شروع کردیں تو کفر کے علمبرداروں سے جنگ کرو کیونکہ ان کی قسموں کا کوئی اعتبار نہیں۔ شاید کہ (پھر تلوار ہی کے زور سے) وہ باز آئیں گے');
INSERT INTO "sayyid_qutb" VALUES(9,13,'9:13','کیا تم نہ لڑوگے ایسے لوگوں سے جو اپنے عہد توڑتے رہے ہیں اور جنہوں نے رسول کو ملک سے نکال دینے کا قصد کیا تھا اور زیادتی کی ابتدا کرنے والے وہی تھے ؟ کیا تم ان سے ڈرتے ہو ؟ اگر تم مومن ہو تو اللہ اس سے زیادہ مستحق ہے کہ اس سے ڈرو');
INSERT INTO "sayyid_qutb" VALUES(9,14,'9:14','ان سے لڑو ، اللہ تمہارے ہاتھوں سے ان کو سزا دلوائے گا اور انہیں ذلیل و خوار کرے گا اور ان کے مقابلہ میں تمہاری مدد کرے اور بہت سے مومنوں کے دل ٹھنڈے کرے گا');
INSERT INTO "sayyid_qutb" VALUES(9,15,'9:15','اور ان کے قلوب کی جلن مٹا دے گا اور جسے چاہے گا ، توبہ کی توفیق بھی گا۔ اللہ سب کچھ جاننے والا اور دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,16,'9:16','کیا تم لوگوں نے یہ سمئجھ رکھا ہے کہ یونی چھوڑ دیے جاؤ گے حالانکہ ابھی اللہ نے یہ تو دیکھا ہی نہیں کہ تم میں سے کون وہ لوگ ہیں جنہوں نے (اس کی راہ میں) جاں فشانی کی اور اللہ اور رسول اور مومنین کے سوا کسی کو جگر دوست نہ بنایا ، جو کچھ تم کرتے ہو ، اللہ اس سے باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(9,17,'9:17','مشرکین کا یہ کام نہیں ہے کہ وہ اللہ کی مسجدوں کے مجاور و خادم بنیں در آں حالیکہ اپنے اوپر وہ خود کفر کی شہادت دے رہے ہیں۔ ان کے تو سارے اعمال ضاعئ ہوگئے اور جہنم میں انہیں ہمیشہ رہنا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,18,'9:18','اللہ کی مسجدوں کے آباد کار (مجاور و خاد) تو وہی لوگ ہوسکتے ہیں جو اللہ اور روز آخر کو مانیں ، اور نماز قائم کریں ، زکوۃ دیں اور اللہ کے سوا کسی سے نہ ڈریں۔ انہی سے یہ توقع ہے کہ سیدھی راہ چلیں گے');
INSERT INTO "sayyid_qutb" VALUES(9,19,'9:19','کیا تم لوگوں نے حاجیوں کو پانی پلانے اور مسجد حرام کی مجاوری کرنے کو اس شخص کے کام کے برابر ٹھہرا لیا جو ایمان لایا اللہ پر اور روز آخر پر اور جس نے جانفشانی کی اللہ کی راہ میں ؟ اللہ کے نزدیک تو یہ دنوں برابر نہیں ہیں اور اللہ ظالموں کی رہنمائی نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(9,20,'9:20','اللہ کے ہاں تو انہی لوگوں کو ادرجہ بڑا ہے جو ایمان لائے اور جنہوں نے اس کی راہ میں گھر بار چھوڑے اور جان و مال سے جہاد کیا وہی کامیاب ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,21,'9:21','ان کا رب انہیں اپنی رحمت اور خوشنودی اور ایسی جنتوں کی بشارت دیتا ہے جہاں ان کے لیے پائیدار عیش کے سامان ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,22,'9:22','ان میں وہ ہمیشہ رہیں گے یقینا اللہ کے پاس خدمات کا صلہ دینے کو بہت کچھ ہے');
INSERT INTO "sayyid_qutb" VALUES(9,23,'9:23','اے لوگو جو ایمان لائے ہو ، اپنے باپوں اور بھائیوں کو بھی اپنا رفیق نہ بناؤ اگر وہ ایمان پر کفر کو ترجیح دیں۔ تم سے جو ان کو رفیق بنائیں گے وہی ظالم ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(9,24,'9:24','اے نبی ! کہہ دو کہ اگر تمہارے باپ اور بیٹے اور تمہارے بھائی اور تمہاری بیویاں اور تمہارے عزیز و اقارب اور تمہارے وہ مال جو تم نے کمائے ہیں اور تمہارے وہ کاروبار جن کے ماند پڑجانے کا تم کو خوف ہے ، اور تمہارے وہ گھر جو تم کو پسند ہیں ، تم کو اللہ اور اس کے رسول اور اس کی راہ میں جہاد سے عزیز تر ہیں تو انتظار کرو یہاں تک کہ اللہ اپنا فیصلہ تمہارے سامنے لے آئے ، اور اللہ فاسق لوگوں کی رہنمائی نہیں کیا کرتا');
INSERT INTO "sayyid_qutb" VALUES(9,25,'9:25','اللہ اس سے پہلے بہت سے مواقع پر تمہاری مدد کرچکا ہے۔ ابھی غزوہ حنین کے روز (اس کی دستگیری کی شان تم دیکھ چکے ہو) سا روز تمہیں اپنی کثرت تعداد کا غرہ تھا مگر وہ تمہارے کچھ کام نہ آئی اور زمین اپنی وسعت کے باوجود تم پر تنگ ہوگئی اور تم پیٹھ پھیر کر بھاگ نکلے');
INSERT INTO "sayyid_qutb" VALUES(9,26,'9:26','پھر اللہ نے اپنی سکینت اپنے رسول پر اور مومنین پر نازل فرمائی اور وہ لشکر اتارے جو تم کو نظر نہ آتے تھے اور منکرین حق کو سزا دی کہ یہی بدلہ ہے ان لوگوں کے لیے جو حق کا انکار کریں');
INSERT INTO "sayyid_qutb" VALUES(9,27,'9:27','پھر (تم یہ بھی دیکھ چکے ہو کہ) اس طرح سزا دینے کے بعد اللہ جس کو چاہتا ہے توبہ کی توفیق بھی بخش دیتا ہے ، اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,28,'9:28','اے لوگو جو ایمان لائے ہو ، مشرکین ناپاک ہیں ، لہذا اس سال کے بعد یہ مسجد حرام کے قریب نہ پھٹکنے پائیں۔ اور اگر تمہیں تنگ دستی کا خوف ہے تو بعید نہیں کہ اللہ چاہے تو تمہیں اپنے فضل سے غنی کردے ، اللہ علیم و حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(9,29,'9:29','جنگ کرو اہل کتاب میں سے ان لوگوں کے خلاف جو اللہ اور روز آخر پر ایمان نہیں لاتے اور جو کچھ اللہ اور اس کے رسول نے حرام قرار دیا ہے اسے حرام نہیں کرتے اور دین حق کو اپنا دین نہیں بناتے۔ (ان سے لڑو) یہاں تک کہ وہ اپنے ہاتھ سے جزیہ دیں اور چھوٹے بن کر رہیں');
INSERT INTO "sayyid_qutb" VALUES(9,30,'9:30','یہودی کہتے ہیں کہ عزیر اللہ کا بیٹا ہے۔ یہ بےحقیقت باتیں ہیں جو وہ اپنی زبانوں سے نکالتے ہیں۔ ان لوگوں کی دیکھا دیکھی جو ان سے پہلے کفر میں مبتلا ہوئے تھے۔ خدا کی مار ان پر ، یہ کہاں سے دھوکا کھا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,31,'9:31','انہوں نے اپنے علماء اور درویشوں کو اللہ کے سوا اپنا رب بنا لیا ہے اور اسی طرح مسیح ابن مریم کو بھی۔ حالانکہ ان کو ایک معبود کے سوا کسی کی بندگی کرنے کا حکم نہیں دیا گیا تھا ، وہ جس کے سوا کوئی مستحق عبادت نہیں ، پاک ہے وہ ان مشرکانہ باتوں سے جو یہ لوگ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,32,'9:32','یہ لوگ چاہتے ہیں کہ اللہ کی روشنی کو اپنی پھونکوں سے بجھا دیں۔ مگر اللہ اپنی روشنی کو مکمل کیے بغیر ماننے والا نہیں ہے خواہ کافروں کو یہ کتنا ہی ناگوار ہو۔ وہ اللہ ہی ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا ہے تاکہ اسے پوری جنس دین پر غالب کردے خواہ مشرکوں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "sayyid_qutb" VALUES(9,33,'9:33','وہ اللہ ہی ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا ہے تاکہ اسے پوری جنس دین پر غالب کردے خواہ مشرکوں کو یہ کتنا ہی ناگوار ہو');
INSERT INTO "sayyid_qutb" VALUES(9,34,'9:34','اے لوگو جو ایمان لائے ہو ، ان اہل کتاب کے اکثر علماء اور درویشوں کا حال یہ ہے کہ وہ لوگوں کے مال باطل طریقوں سے کھاتے ہیں اور انہیں اللہ کی راہ سے روکتے ہیں۔ دردناک سزا کی خوشخبری دو');
INSERT INTO "sayyid_qutb" VALUES(9,35,'9:35','ان کو جو سونے اور چاندی جمع کر کے رکھتے ہیں۔ اور انہیں خدا کی راہ میں خرچ نہیں کرتے۔ ایک دن آئے گا کہ اسی سونے چاندی پر جہنم کی آگ دہکائی جائے گی اور پھر اسی سے ان لوگوں کی پیشانیوں اور پہلوؤں اور پیٹھوں کو داغا جائے گا۔ یہ ہے وہ خزانہ جو تم نے اپنے لیے جمع کیا تھا ، لو اب اپنی سمیٹی ہوئی دولت کا مزہ چکھو');
INSERT INTO "sayyid_qutb" VALUES(9,36,'9:36','حقیقت یہ ہے کہ مہینوں کی تعداد جب سے اللہ نے آسمان و زمین کو پیدا کیا ہے اللہ کے نوشتے میں بارہ ہی ہے اور ان میں سے چار مہینے حرام ہیں۔ یہی ٹھیک ضابطہ ہے۔ ان چار مہینوں میں اپنے اوپر ظلم نہ کرو۔ اور مشرکوں سے سب سے مل کر لڑو جس طرح وہ سب مل کر تم سے لڑتے ہیں اور جان رکھو کہ اللہ متقیوں ہی کے ساتھ ہے');
INSERT INTO "sayyid_qutb" VALUES(9,37,'9:37','نسیئ تو کفر میں ایک مزید کافرانہ حرکت ہے جس سے یہ کافر لوگ گمراہی میں مبتلا کیے جاتے ہیں۔ کسی سال ایک مہینے کو حلال کرلیتے ہیں اور کسی سال اس کو حرام کردیتے ہیں تاکہ اللہ کے حرام کیے ہوئے مہینوں کی تعداد پوری بھی کردیں اور اللہ کا حرام کیا ہوا حلال بھی کرلیں ان کے برے اعمال ان کے لیے خوشنما بنا دیے گئے ہیں اور اللہ منکرین حق کو ہدایت نہیں دیا کرتا');
INSERT INTO "sayyid_qutb" VALUES(9,38,'9:38','اے لوگو جو ایمان لائے ہو ، تمہیں کیا ہوگیا کہ جب تم سے اللہ کی راہ میں نکلنے کے لیے کہا گیا تو تم زمین سے چمٹ کر رہ گئے ؟ کیا تم نے آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کرلیا ؟ ایسا ہے تو تمہیں معلوم کہ دنیوی زندگی کا یہ سب سروسامان آخرت میں بہت تھوڑا نکلے گا۔');
INSERT INTO "sayyid_qutb" VALUES(9,39,'9:39','تم نہ اٹھوگے تو خدا تمہیں دردناک سزا دے گا اور تمہاری جگہ کسی اور گروہ کو اٹھائے گا اور تم خدا کا کچھ بھی بگاڑ نہ سکوگے وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,40,'9:40','تم نے اگر نبی کی مدد نہ کی تو کچھ پروا نہیں ، اللہ اس کی مدد اس وقت کرچکا ہے جب کافروں نے اسے نکال دیا تھا ، جب وہ صرف دو میں کا دوسرا تھا ، جب وہ دونوں غار میں تھے ، جب وہ اپنے ساتھی سے کہہ رہا تھا کہ " غم نہ ، اللہ ہمارے ساتھ ہے " اس وقت اللہ نے اس پر اپنی طرف سے سکون قلب نازل کیا اور اس کی مدد ایسے لشکروں سے کی جو تم کو نظر نہ آتے تھے اور کافروں کو بول نیچا کردیا اور اللہ کا بول تو اونچا ہی ہے ، اللہ زبردست اور دانا و بینا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,41,'9:41','نکلو ، خواہ ہلکے ہو یا بوجھل ، اور جہاد کرو اللہ کی راہ میں اپنے مالو اور اپنی جانوں کے ساتھ ، یہ تمہارے لیے بہتر ہے ، اگر تم جانو');
INSERT INTO "sayyid_qutb" VALUES(9,42,'9:42','اے نبی اگر فائدہ سہل الحصول ہوتا اور سر ہلکا ہوتا تو وہ ضرور تمہارے پیچھے چلنے پر آمادہ ہوجاتے ، مگر ان پر تو یہ راستہ بہت کٹھن ہوگیا۔ اب وہ خدا کی قسم کھا کھا کر کہیں گے کہ اگر ہم چل سکتے تو یقینا تمہارے ساتھ چلتے۔ وہ اپنے آپ کو ہلاکت میں ڈال رہے ہیں۔ اللہ خوب جانتا ہے کہ وہ جھوٹے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,43,'9:43','اے نبی اللہ تمہیں معاف کرتے ، تم نے کیوں انہیں رخصت دے دی ؟ (تمہیں چاہئے تھا کہ خود رخصت نہ دیتے) تاکہ تم پر کھل جاتا کہ کون لوگ سچے ہیں اور جھوٹوں کو بھی تم جان لیتے ہو');
INSERT INTO "sayyid_qutb" VALUES(9,44,'9:44','جو لوگ اللہ اور روز آخر پر ایمان رکھتے ہیں وہ تو کبھی تم سے یہ درخواست نہ کریں گے کہ انہیں اپنی جان و مال کے ساتھ جہاد کرنے سے معاف رکھا جائے۔ اللہ متقیوں کو خوب جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,45,'9:45','ایسی درخواستیں تو صرف وہی لوگ کرتے ہیں جو اللہ اور روز آخر پر ایمان نہیں رکھتے ، جن کے دلوں میں شک ہے اور وہ اپنے شک ہی میں متردد ہو رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,46,'9:46','اگر واقعی ان کا ارادہ نکلنے کا ہوتا تو وہ اس کے لیے کچھ تیاری کرتے۔ لیکن اللہ کو ان کا اٹھنا پسند ہی نہ تھا۔ اس لیے اس نے انہیں سست کردیا اور کہہ دیا گیا کہ بیٹھ رہو بیٹھنے والوں کے ساتھ');
INSERT INTO "sayyid_qutb" VALUES(9,47,'9:47','اگر وہ تمہارے ساتھ نکلتے تو تمہارے اندر خرابی کے سوا کسی چیز کا اضافہ نہ کرتے۔ وہ تمہارے درمیان فتنہ پردازی کے لیے دوڑ دھوپ کرتے اور تمہارے گروہ کا حال یہ ہے کہ ابھی ان میں بہت سے ایسے لوگ موجود ہیں جو ان کی باتیں کان لگا کر سنتے ہیں ، اللہ ان ظالموں کو خوب جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,48,'9:48','اس سے پہلے بھی ان لوگوں نے فتنہ انگیزی کی کوششیں کی ہیں اور تمہیں ناکام کرنے کے یہ ہر طرح کی تدبیروں کا الٹ پھیر کرچکے ہیں یہاں تک کہ ان کی مرضی کے خلاف حق آگیا اور اللہ کا کام ہوکر رہا');
INSERT INTO "sayyid_qutb" VALUES(9,49,'9:49','ان میں سے کوئی ہے جو کہتا ہے کہ " مجھے رخصت دے دیجئے اور مجھ کو فتنے میں نہ ڈالیے " سن رکھو ! فتنے ہی میں تو یہ لوگ پڑے ہوئے ہیں اور جہنم نے ان کافروں کو گھیر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,50,'9:50','تمہارا بھلا ہوتا ہے تو انہیں رنج ہوتا ہے اور تم پر کوئی مصیبت آتی ہے تو یہ منہ پھیر کر خوش خوش پلٹتے ہیں اور کہتے جاتے ہیں کہ اچھا ہوا ہم نے پہلے ہی اپنا معاملہ ٹھیک کرلیا تھا');
INSERT INTO "sayyid_qutb" VALUES(9,51,'9:51','ان سے کہو " ہمیں ہرگز کوئی (برائی یا بھلائی) نہیں پہنچتی مگر وہ جو اللہ نے ہمارے لیے لکھ دی ہے ، اللہ ہی ہمرا مولی ہے اور اہل ایمان کو اسی پر بھروسہ کرنا چاہیے');
INSERT INTO "sayyid_qutb" VALUES(9,52,'9:52','ان سے کہو تم ہمارے معاملہ میں جس چیز کے منتظر ہو وہ اس کے سوا اور کیا ہے کہ دو بھلائیوں میں سے ایک بھلائی ہے۔ اور ہم تمہارے معاملہ میں جس چیز کے منتظر ہیں۔ وہ یہ ہے کہ اللہ خود تم کو سزا دیتا ہے یا ہمارے ساتھ دلواتا ہے ؟ اچھا تو اب تم بھی انتظار کرو اور ہم بھی تمہارے ساتھ منتظر ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,53,'9:53','ان سے کہو تم اپنے مال خواہ راضی خوشی خرچ کرو یا بکراہت ، بہرحال وہ قبول نہ کیے جائیں گے ۔ کیونکہ تم فاسق لوگ ہو');
INSERT INTO "sayyid_qutb" VALUES(9,54,'9:54','ان کے دئیے ہوئے مال قبول نہ ہونے کی کوئی وجہ اس کے سوا نہیں ہے کہ انہوں نے اللہ اور اس کے رسول سے کفر کیا ہے ، نماز کے لیے آتے ہیں تو کسمساتے ہوئے آتے ہیں اور راہ خدا می خرچ کرتے ہیں تو بادل ناخواستہ خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,55,'9:55','ان کے مال و دولت اور ان کی کثرت اولاد کو دیکھ کر دھوکا نہ کھاؤ، اللہ تو یہ اہتا ہے کہ انہی چیزوں');
INSERT INTO "sayyid_qutb" VALUES(9,56,'9:56','وہ خدا کی قسم کھا کھا کر کہتے ہیں کہ ہم تمہی میں سے ہیں ، حالانکہ وہ ہر گز تم میں سے نہیں ہیں۔ اصل میں تو وہ ایسے لوگ ہیں جو تم سے خوفزدہ ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,57,'9:57','اگر وہ کوئی جائے پناہ پا لیں یا کوئی کھوہ یا گھس بیٹھنے کی جگہ ، تو بھاگ کر اس میں جا چھپیں');
INSERT INTO "sayyid_qutb" VALUES(9,58,'9:58','اے نبی ان میں سے بعض لوگ صدقات کی تقسیم میں تم پر اعتراضات کتے ہیں ، اگر اس مال میں اسے انہیں کچھ دے دیا جائے تو خوش ہوجائیں ، اور نہ دیا جائے تو بگڑنے لگتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,59,'9:59','کیا اچھا ہوتا کہ اللہ اور رسول نے جو کچھ بھی انہیں دیا تھا اس پر وہ راضی رہتے اور کہتے کہ " اللہ ہمارے لیے کافی ہے ، وہ اپنے فضل سے ہمیں اور بہت کچھ دے گا اور اس کا رسول بھی ہم پر عنایت فرمائے گا ، ہم اللہ کی طرف نظر جمائے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,60,'9:60','یہ صدقات تو در اصل فقیروں اور مسکینوں کے لیے ہیں اور ان لوگوں کے لیے جو صدقات کے کام پر مامور ہوں ، اور ان کے لیے جن کی تالیف قلب مطلوب ہو۔ نیز یہ گردنوں کے چھڑانے اور قرضداروں کی مدد کرنے میں اور راہ کدا میں اور مسافر نوازی میں استعمال کرنے کے لیے ہیں۔ ایک فریضہ ہے اللہ کی طرف سے اور اللہ سب کچھ جاننے والا اور دانا و بینا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,61,'9:61','ان میں سے کچھ لوگ ہیں جو اپنی باتوں سے نبی کو دکھ دیتے ہیں اور کہتے ہیں کہ یہ شخص کانوں کا کچا ہے۔ کہو وہ تمہاری بھلائی کے لیے ایسا ہے ، اللہ پر ایمان رکھتا ہے اور اہل ایمان پر اعتماد کرتا ہے اور سراسر رحمت ہے ان لوگوں کے لیے جو تم میں سے ایماندار ہیں اور جو لوگ اللہ کے رسول کو دکھ دیتے ہیں ان کے لیے دردناک سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,62,'9:62','یہ لوگ تمہارے سامنے قسمیں کھاتے ہیں تاکہ تمہیں راضی کریں ، حالانک اگر یہ مومن ہیں تو اللہ اور رسول اس کے زیادہ حق دار ہیں کہ یہ ان کو راضی کرنے کی فکر کریں');
INSERT INTO "sayyid_qutb" VALUES(9,63,'9:63','کیا انہیں معلوم نہیں ہے کہ جو اللہ اور اس کے رسول کا مقابلہ کرتا ہے ، اس کے لیے دوزخ کی آگ ہے جس میں وہ ہمیشہ رہے گا ، یہ بہت بڑی رسوائی ہے');
INSERT INTO "sayyid_qutb" VALUES(9,64,'9:64','یہ منافق ڈر رہے ہیں کہ کہیں مسلمانوں پر کوئی ایسی سورت نازل نہ ہوجائے جو ان کے دلون کے بھید کھول کر رکھ دے۔ اے نبی ان سے کہو " اور مذاق اڑاؤ، اللہ اس چیز کو کھول دینے والا ہے جس کے کھل جانے سے تم ڈرتے ہو "');
INSERT INTO "sayyid_qutb" VALUES(9,65,'9:65','اگر ان سے پوچھو کہ تم کیا باتیں کر رہے تھے ، تو جھٹ کہہ دیں گے کہ ہم تو ہنسی مذاق اور دل لگی کر رہے تھے۔ ان سے کہو " کیا تمہاری ہنسی دل لگی اللہ اور اس کی آیات اور اس کے رسول ہی کے ساتھ تھی ؟');
INSERT INTO "sayyid_qutb" VALUES(9,66,'9:66','اب عذرات نہ تراشو۔ تم نے ایمان لانے کے بعد کفر کیا ہے۔ اگر ہم نے تم میں سے ایک گروہ کو معاف کر بھی دیا تو دوسرے گروہ کو تو ہم ضرور سزا دیں گے کیونکہ وہ مجرم ہے');
INSERT INTO "sayyid_qutb" VALUES(9,67,'9:67','منافق مرد اور منافق عورتیں سب ایک دوسرے کے ہم رنگ ہیں۔ برائی کا حکم دیتے ہیں اور بھلائی سے منع کرتے ہیں اور اپنے ہاتھ خیر سے روکتے ہیں۔ یہ اللہ کو بھول گئے تو اللہ نے بھی انہیں بھلا دیا۔ یقینا یہ منافق ہی فاسق ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,68,'9:68','ان منافق مردوں اور عورتوں اور کافروں کے لیے اللہ نے آتش دوزخ کا وعدہ کیا ہے جس میں وہ ہمیشہ رہیں گے ، وہی ان کے لیے موزوں ہے۔ ان پر اللہ کی پھٹکار ہے اور ان کے لیے قائم رہنے والا عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(9,69,'9:69','تم لوگوں کے رنگ ڈھنگ وہی ہیں جو تمہارے پیش رو وں کے تھے۔ وہ تم سے زیادہ زور آور اور تم سے بڑھ کر مال اور اولاد والے تھے۔ پھر انہوں نے دنیا میں اپنے حصہ کے مزے لوٹ لیے اور تم نے بھی اپنے حصے کے مزے اسی طرح لوٹے جیسے انہوں نے لوٹے تھے ، اور ایسی ہی بحثوں میں تم بھی پڑے جیسی بحثوں میں وہ پڑے تھے ، سو ان کا انجام یہ ہوا کہ دنیا اور آخرت میں ان کا سب کیا دھرا ضائع ہوگیا اور وہی خسارے میں ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,70,'9:70','کیا ان لوگوں کو اپنے پیش روؤں کی تاریخ نہیں پہنچی ؟ نوح کی قوم ، عاد ، ثمود ، ابراہیم کی قوم ، مدین کے لوگ اور وہ بستیاں جنہیں الٹ دیا گیا۔ ان کے رسول ان کے پاس کھلی کھلی نشانیاں لے کر آئے ، پھر یہ الہ کا کام نہ تھا کہ ان پر ظلم کرتا مگر وہ آپ ہی اپنے اوپر ظلم کرنے والے تے');
INSERT INTO "sayyid_qutb" VALUES(9,71,'9:71','مومن مرد اور مومن عورتیں ، یہ سب ایک دوسرے کے رفیق ہیں ، بھلائی کا حک دیتے اور برائی سے روکتے ہیں ، نماز قائم کرتے ہیں ، زکوۃ دیتے ہیں اور اللہ اور اس کے رسول کی اطاعت کرتے ہی۔ یہ وہ لوگ ہیں جن پر اللہ کی رحمت نازل ہوکر رہے گی ، یقیناً اللہ سب پر غالب اور حکیم و دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,72,'9:72','ان مومن مردوں اور عورتوں سے اللہ کا وعدہ ہے کہ انہیں ایسے باغ دے گا جن کے نیچے نہریں بہتی ہوں گی اور وہ ان میں ہمیشہ رہیں گے۔ ان سدا بہار باغوں میں ان کے لیے پاکیزہ قیام گاہیں ہوں گی اور سب سے بڑھ کر یہ کہ اللہ کی خوشنودی انہیں حاصل ہوگی۔ یہی بڑی کامیابی');
INSERT INTO "sayyid_qutb" VALUES(9,73,'9:73','اے نبی کفار اور منافقین دونوں کا پوری قوت سے مقابلہ کرو اور ان کے ساتھ سختی سے پیش آؤ۔ آخر کار ان کا ٹھکانا جہنم ہے اور وہ بدترین جائے قرار ہے');
INSERT INTO "sayyid_qutb" VALUES(9,74,'9:74','یہ لوگ خدا کی قسم کھا کھا کر کہتے ہیں کہ ہم نے وہ بات نہیں کہی ، حالانکہ انہوں نے ضرور وہ کافرانہ بات کہی ہے۔ وہ اسلام لانے کے بعد کفر کے مرتکب ہوئے اور انہوں نے وہ کچھ کرنے کا ارادہ کیا جسے کر نہ سکے۔ یہ ان کا سارا غصہ اسی بات پر ہے تاکہ اللہ اور اس کے رسول نے اپنے فضل سے ان کو غنی کردیا ہے ! اب اگر یہ اپنی اس روش سے باز آئیں تو انہی کے لی بہتر ہے ، اور اگر یہ باز نہ آئے تو اللہ ان کو نہایت دردناک سزا دے گا۔ دنیا میں بھی اور آخرت میں بھی ، اور زمین میں کوئی نہیں جو ان کا حمایتی اور مددگار ہو');
INSERT INTO "sayyid_qutb" VALUES(9,75,'9:75','ان میں سے بعض ایسے بھی ہیں جنہوں نے اللہ سے عہد کیا تھا کہ اگر اس نے اپنے فضل سے ہم کو نوازا تو ہم خیرات کریں گے اور صالح بن کر رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(9,76,'9:76','مگر جب اللہ نے اپنے فضل سے ان کو دولتمند کردیا تو وہ بخل پر اتر آئے اور اپنے عہد سے ایسے پھرے کہ انہیں اس کی پروا تک نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(9,77,'9:77','نتیجہ یہ نکلا کہ ان کی اس بد عہدی کی وجہ سے جو انہوں نے اللہ کے ساتھ کی ، اور اس جھوٹ کی وجہ سے جو وہ بولتے رہے ، اللہ نے ان کے دلوں میں نفاق بٹھا دیا ، جو اس کے حضور ان کی پیشی کے دن تک ان کا پیچھا نہ چھوڑے گا');
INSERT INTO "sayyid_qutb" VALUES(9,78,'9:78','کیا یہ لوگ جانتے نہیں ہیں کہ اللہ کو ان کے مخفی راز اور ان کی پوشیدہ سرگوشیاں تک معلوم ہیں اور وہ تمام غیب کی باتوں سے پوری طرح باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(9,79,'9:79','(وہ خوب جانتا ہے ان کنجوس دولت مندوں کو) جو برضا ورغبت دینے والے اہل ایمان کی مالی قربانیون پر باتیں چھانٹتے ہیں ان لوگوں کا مذاق اڑاتے ہیں جن کے پاس (راہ خدا میں دینے کے لیے) اس کے سوا کچھ نہیں ہے جو وہ اپنے اوپر مشقت برداشت کرکے دیتے ہیں۔ اللہ ان مذاق اڑانے والوں کا مذاق اڑاتا ہے اور ان کے لیے دردناک سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,80,'9:80','اے نبی تم خواہ ایسے لوگوں کے لیے معافی کی درخواست کرو یا نہ کرو ، اگر تم ستر مرتبہ بھی انہیں معاف کرنے کی درخواست کروگے تو اللہ انہیں ہرگز معاف نہ کرے گا۔ اس لیے کہ انہوں نے اللہ اور اس کے رسول کے ساتھ کفر کیا ہے ، اور اللہ فاسق لوگوں کو راہ نجات نہیں دکھاتا');
INSERT INTO "sayyid_qutb" VALUES(9,81,'9:81','جن لوگوں کو پیچھے رہ جانے کی اجازت دے دی گئی تھی وہ اللہ کے رسول کا ساتھ نہ دینے اور گھر بیٹھے رہنے پر خوش ہوئے اور انہیں گوارا نہ ہوا کہ اللہ کی راہ میں جان و مال سے جہاد کریں۔ انہوں نے لوگوں سے کہا کہ " اس سخت گرمی میں نہ نکلو " ان سے کہو کہ جہنم کی آگ اس سے زیادہ گرم ہے ، کاش انہیں اس کا شعور ہوتا');
INSERT INTO "sayyid_qutb" VALUES(9,82,'9:82','اب چاہئے کہ یہ لوگ ہنسنا کم کریں اور روئیں زیادہ ، اس لیے کہ جو بدی یہ کماتے رہے اس کی جزا ایسی ہی ہے (کہ انہیں اس پر رونا چاہئے)');
INSERT INTO "sayyid_qutb" VALUES(9,83,'9:83','اگر اللہ ان کے درمیان تمہیں واپس لے جائے اور آئندہ ان میں سے کوئی گروہ جہاد کے لیے نکلنے کی تم سے اجازت مانگے تو صاف کہہ دینا۔ اب تم میرے ساتھ ہر گز نہیں چل سکتے اور نہ میری معیت میں کسی دشمن سے لڑ سکتے ہو ، تم نے پہلے بیٹھ رہنے کو پسند کیا تھا تو اب گھر بیٹھنے والوں ہی کے ساتھ بیٹھے رہو');
INSERT INTO "sayyid_qutb" VALUES(9,84,'9:84','اور آئندہ ان میں سے جو کوئی مرے اس کی نماز جنازہ بھی تم ہرگز نہ پڑھنا اور نہ کبھی اس کی قبر پر کھڑے ہونا ، کیوں کہ انہوں نے اللہ اور اس کے رسول کے ساتھ کفر کیا ہے اور وہ مرے ہیں اس حال میں کہ وہ فاسق تھے');
INSERT INTO "sayyid_qutb" VALUES(9,85,'9:85','ان کی مالداری اور ان کی کثرت اولاد تم کو دھوکے میں نہ ڈالے۔ اللہ نے تو ارادہ کرلیا ہے کہ اس مال و اولاد کے ذریعہ سے ان کو اسی دنیا میں سزا دے اور ان کی جانیں اس حال میں نکلیں کہ وہ کافر ہوں');
INSERT INTO "sayyid_qutb" VALUES(9,86,'9:86','جب کبھی کوئی سورة اس مضمون کی نازل ہوئی کہ اللہ کو مانو اور اس کے رسول کے ساتھ مل کر جہاد کرو تو تم نے دیکھا کہ جو لوگ ان میں سے صاحب مقدرت تھے وہی تم سے درخواست کرنے لگے کہ انہیں جہاد کی شرکت سے معاف رکھا جائے اور انہوں نے کہا کہ ہمیں چھوڑ دیجئے کہ ہم بیٹھنے والوں کے ساتھ رہیں');
INSERT INTO "sayyid_qutb" VALUES(9,87,'9:87','ان لوگوں نے گھر بیٹھنے والیوں میں شامل ہونا پسند کیا اور ان کے دلوں پر ٹھپہ لگا دیا گیا ، اس لیے ان کی سمجھ میں اب کچھ نہیں آتا');
INSERT INTO "sayyid_qutb" VALUES(9,88,'9:88','بخلاف اس کے رسول نے اور ان لوگوں نے جو رسول کے ساتھ ایمان لائے تھے اپنی جان و مال سے جہاد کیا اور اب ساری بھلائیاں انہی کے لیے ہیں اور وہی فلاح پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(9,89,'9:89','اللہ نے ان کے لیے ایسے باغ تیار کر رکھے ہیں جن کے نیچے نہریں بہہ رہی ہیں ، ان میں وہ ہمیشہ رہیں گے۔ یہ ہے عطیم الشان کامیابی');
INSERT INTO "sayyid_qutb" VALUES(9,90,'9:90','بدوی عربوں میں سے بھی بہت سے لوگ آئے جنہوں نے عذر کیے تاکہ انہیں بھی پیچھے رہ جانے کی اجازت دی جائے۔ اس طرح بیٹھ رہے وہ لوگ جنہوں نے اللہ اور اس کے رسول سے ایمان کا جھوٹا عہد کیا تھا۔ ان بدویوں میں سے جن لوگوں نے کفر کا طریقہ اختیار کیا ہے عنقریب وہ دردناک سزا سے دوچار ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(9,91,'9:91','ضعیف ور بیمار لوگ اور وہ لوگ جو شرکت جہاد کے لیے زاد راہ نہیں پاتے ، اگر پیچھے رہ جائیں تو کوئی حرج نہیں جب کہ وہ خلوص دل کے ساتھ اللہ اور اس کے رسول کے وفادار ہوں۔ ایسے محسنین پر اعتراض کی کوئی گنجائش نہیں ہے ، اور اللہ درگزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,92,'9:92','اسی طرح ان لوگوں پر بھی کوئی اعتراض کا موقع نہیں ہے جنہوں نے خود اگر تم سے درخواست کی تھی کہ ہمارے لیے سواریاں بہم پہنچائی جائیں ، اور جب تم نے کہا کہ میں تمہارے لیے سواریوں کا انتطام نہیں کرسکتا تو وہ مجبوراً واپس گئے اور حال یہ تھا کہ ان کی آنکھوں سے آنسو جاری تھے اور انہیں اس بات کا بڑا رنج تھا کہ وہ اپنے خرچ پر شریک جہاد ہونے کی مقدرت نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(9,93,'9:93','البتہ اعتراض ان لوگوں پر ہے جو مالدار ہیں اور پھر بھی تم سے درخواستیں کرتے ہیں کہ انہیں شرکت جہاد سے معاف رکھا جائے۔ انہوں نے گھر بیٹھنے والوں میں رہنا پسند کیا اور اللہ نے ان کے دلوں پر ٹھپہ لگا دیا ، اس لیے اب یہ کچھ نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(9,94,'9:94','تم جب پلٹ کر ان کے پاس پہنچوگے تو یہ طرح طرح کے عذرات پیش کریں گے۔ مگر تم صاف کہہ دینا کہ " بہانے نہ کرو ہم تمہاری کسی بات کا اعتبار نہ کریں گے اللہ نے ہم کو تمہارے حالات بتا دئیے ہیں۔ اب اللہ اور اس کا رسول تمہارے طرز عمل کو دیکھے ، پھر تم اس کی طرف پلٹائے جاؤگے جو کھلے اور چھپے سب کا جاننے والا ہے۔ اور وہ تمہیں بتا دے گا کہ تم کیا کچھ کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(9,95,'9:95','تمہاری واپسی پر یہ تمہارے سامنے قسمیں کھائیں گے تاکہ تم ان سے صرف نظر کرو۔ تو بیشک تم ان سے صرف نظر ہی کرلو ، کیونکہ یہ گندگی ہیں اور ان کا اصلی مقام جہنم ہے جو ان کی کمائی کے بدلے میں انہیں نصیب ہوگی');
INSERT INTO "sayyid_qutb" VALUES(9,96,'9:96','یہ تمہارے سامنے قسمیں کھائیں گے تاکہ تم ان سے راضی ہوجاؤ۔ حالانکہ اگر تم ان سے راضی ہو بھی گئے تو اللہ ہر گز ایسے فاسق لوگوں سے راضی نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(9,97,'9:97','یہ بدوی عرب کفر ونفاق میں زیادہ سخت ہیں اور ان کے معاملہ میں اس امر کے امکانات زیادہ ہیں کہ اس دین کے حدود سے ناواقف رہیں جو اللہ نے اپنے رسول پر نازل کیا ہے۔ اللہ سب کچھ جانتا ہے۔ اور حکیم ودانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,98,'9:98','ان بدویوں میں ایسے لوگ موجود ہیں جو راہ خدا میں کچھ خرچ کرتے ہیں تو اسے اپنے اوپر زبردستی کی چٹی سمجھتے ہیں اور تمہارے حق میں زمانہ کی گردشوں کا انتظار کر رہے ہیں (کہ تم کسی چکر میں پھنسو تو وہ اپنی گردن سے اس نظام کی اطاعت کا قلادہ اتار پھینکیں جس میں تم نے انہیں کس دیا ہے۔ حالانکہ بدی کا چکر خود انہی پر مسلط ہے اور اللہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(9,99,'9:99','اور انہی بدویوں میں کچھ لوگ ایسے بھی ہیں جو اللہ اور روز آخرت پر ایمان رکھتے ہیں اور جو کچھ خرچ کرتے ہیں اسے اللہ کے ہاں تقرب کا اور رسول کی طرف سے رحمت کی دعائیں لینے کا ذریعہ بناتے ہیں۔ ہاں ! وہ ضرور ان کے لئے تقرب کا ذریعہ ہے اور اللہ ضرور ان کو اپنی رحمت میں داخل کرے گا ، یقینا اللہ درگزر کرنے والا اور رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,100,'9:100','وہ مہاجر و انصار جنہوں نے سب سے پہلے دعوت ایمان پر لبیک کہنے میں سبقت کی ، نیز وہ جو بعد میں راست بازی کے ساتھ ان کے پیچھے آئے ، اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے ، اللہ نے ان کے لئے ایسے باغ مہیا کر رکھے ہیں جن کے نیچے نہریں بہتی ہوں گی اور وہ ان میں ہمیشہ رہیں گے ، یہی عظیم الشان کامیابی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,101,'9:101','تمہارے گردوپیش میں جو بدوی رہتے ہیں ان میں بہت سے منافق ہیں اور اسی طرح خود مدینہ کے باشندوں میں بھی منافق موجود ہیں جو نفاق میں طاق ہوگئے ہیں۔ تم انہیں نہیں جانتے ، ہم انہیں جانتے ہیں۔ قریب ہے وہ وقت جب ہم ان کو دوہری سزادیں گے ، پھر وہ زیادہ بڑی سزا کے لئے واپس لائے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(9,102,'9:102','کچھ اور لوگ بھی ہیں جنہوں نے اپنے قصوروں کا اعتراف کیا ہے۔ ان کا عمل مخلوط ہے ، کچھ نیک ہے اور کچھ بد۔ بعید نہیں کہ اللہ ان پر پھر مہربان ہوجائے کیونکہ وہ درگزر کرنے والا اور رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,103,'9:103','اے نبی تم ان کے اموال میں سے صدقہ لے کر انہیں پاک کرو اور (نیکی کی راہ میں) انہیں بڑھاؤ اور ان کے حق میں دعائے رحمت کرو ، کیوں کہ تمہاری دعا ان کے لئے وجہ تسکین ہوگی ، اللہ سب کچھ سنتا اور جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,104,'9:104','کیا ان لوگوں کو معلوم نہیں ہے کہ وہ اللہ ہی ہے جو اپنے بندوں کی توبہ قبول کرتا ہے اور ان کی خیرات کو قبولیت عطا فرماتا ہے اور یہ کہ اللہ بہت معاف کرنے والا اور رحیم ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(9,105,'9:105','اور اے نبی تم ان لوگوں سے کہ دو کہ عمل کرو ، اللہ اور اس کا رسول اور مومنین سب دیکھیں گے کہ تمہارا طرز عمل اب کیا رہتا ہے ، پھر تم اس کی طرف پلٹائے جاؤگے جو کھلے اور چھپے سب کو جانتا ہے ، اور وہ تمہیں بتا دے گا کہ تم کیا کرتے رہے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(9,106,'9:106','کچھ دوسرے لوگ ہیں جن کا معاملہ ابھی خدا کے حکم پر ٹھرا ہوا ہے ، چاہے انہیں سزا دے اور چاہے ان پر ازسرنو مہربان ہوجائے۔ اللہ سب کچھ جانتا ہے اور حکیم ودانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,107,'9:107','کچھ اور لوگ ہیں جنہوں نے ایک مسجد بنائی اس غرض کے لئے کہ (دعوت حق کو) نقصاب پہنچائیں ، اور (خدا کی بندگی کرنے کے بجائے) کفر کریں ، اور اہل ایمان میں پھوٹ ڈالیں اور (اس بظاہر عبادت گاہ کو) اس شخص کے لئے کمین گاہ بنائیں جو اس سے پہلے خدا اور اس کے رسول کے خلاف برسر پیکار ہوچکا ہے۔ وہ ضرور قسمیں کھا کھا کر کہیں گے کہ ہمارا ارادہ تو بھلائی کے سوا کسی دوسری چیز کا نہ تھا مگر اللہ گواہ ہے کہ وہ قطعی جھوٹے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(9,108,'9:108','تم ہرگز اس عمارت میں کھڑے نہ ہونا۔ جو مسجد اول روز سے تقویٰ پر قائم کی تھی وہی اس کے لئے زیادہ موزوں ہے کہ تم اس میں (عبادت کے لئے) کھڑے ہو ، اور اس میں ایسے لوگ ہیں جو پاک رہنا پسند کرتے ہیں اور اللہ کو پاکیزگی اختیار کرنے والے ہی پسند ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(9,109,'9:109','پھر تمہارا کیا خیال ہے کہ بہتر انسان وہ ہے جس نے اپنی عمارت کی بنیاد خدا کے خوف اس کی رضا کی طلب پر رکھی ہو یا وہ جس نے اپنی عمارت ایک وادی کی کھوکھلی بےثبات کگر پر اٹھائی اور وہ اسے لے کر سیدھی جہنم کی آگ میں جاگری ؟ اور ایسے ظالم لوگوں کو اللہ کبھی سیدھی راہ نہیں دکھاتا۔');
INSERT INTO "sayyid_qutb" VALUES(9,110,'9:110','یہ عمارت جو انہوں نے بنائی ہے ، ہمیشہ ان کے دلوں میں بےیقینی کی جڑ بنی رہے گی (جس کے نکلنے کی اب کوئی صورت نہیں) بجز اس کے کہ ان کے دل ہی پارہ پارہ ہوجائیں۔ اللہ نہایت باخبر اور حکیم ودانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,111,'9:111','حقیقت یہ ہے کہ اللہ نے مومنوں سے ان کے نفس اور ان کے مال جنت کے بدلے خرید لئے ہیں۔ وہ اللہ کی راہ میں لڑتے اور مارتے اور مرتے ہیں۔ ان سے (جنت کا وعدہ) اللہ کے ذمے ایک پختہ وعدہ ہے ، تورات اور انجیل اور قرآن میں۔ اور کون ہے جو اللہ سے بڑھ کر اپنے عہد کا پورا کرنے والا ہو ؟ پس خوشیاں مناؤ اپنے اس سودے پر جو تم نے خدا سے چکا لیا ہے ، یہی سب سے بڑی کامیابی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,112,'9:112','اللہ کی طرف بار بار پلٹنے والے ، اس کی بندگی بجالانے والے ، اس کی تعریف کے گن گانے والے ، اس کی خاطر زمین میں گردش کرنے والے ، اس کے آگے رکوع اور سجدے کرنے والے ، نیکی کا حکم دینے والے ، بدی سے روکنے والے اور اللہ کی حدود کی حفاظت کرنے والے ، (اس کے شان کے ہوتے ہیں وہ مومن جو اللہ سے بیع کا یہ معاملہ طے کرتے ہیں) اور اے نبی ان مومنوں کو خوشخبری دے دو ۔');
INSERT INTO "sayyid_qutb" VALUES(9,113,'9:113','نبی کو اور ان لوگوں کو جو ایمان لائے ہیں ، زیبا نہیں ہے کہ مشرکوں کے لئے مغفرت کی دعا کریں ، چاہے وہ ان کے رشتہ دار ہی کیوں نہ ہوں ، جبکہ ان پر یہ بات کھل چکی ہے کہ وہ جہنم کے مستحق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(9,114,'9:114','ابراہیم نے اپنے باپ کے لئے جو دعائے مغفرت کی تھی وہ تو اس وعدے کی وجہ سے تھی جو اس نے اپنے باپ سے کیا تھا ، مگر جب اس پر یہ بات کھل گئی کہ اس کا باپ خدا کا دشمن ہے تو وہ اس سے بیزار ہوگیا ، حق یہ ہے کہ ابراہیم (علیہ السلام) بڑا رقیق القلب و خدا ترس اور بردبار آدمی تھا۔');
INSERT INTO "sayyid_qutb" VALUES(9,115,'9:115','اللہ کا یہ طریقہ نہیں ہے لوگوں کو یدایت دینے کے بعد پھر گمراہی میں مبتلا کرے۔ جب تک کہ انہیں صاف صاف بتا نہ دے کہ انہیں کب چیزوں سے بچنا چاہئے۔ در حقیقت اللہ ہر چیز کا علم رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,116,'9:116','اور یہ بھی واقعہ ہے کہ اللہ ہی کے قبضہ میں زمین و آسمان کی سلطنت ہے ، اسی کے اختیار میں زندگی وموت ہے اور تمہارا کوئی حامی و مددگار ایسا نہیں ہے کہ جو تمہیں اس سے بچا سکے۔');
INSERT INTO "sayyid_qutb" VALUES(9,117,'9:117','اللہ تعالیٰ نے معاف کردیا نبی کو اور ان مہاجرین وانصار کو جنہوں نے بڑی تنگی کے وقت میں نبی کا ساتھ دیا۔ اگرچہ ان میں سے کچھ لوگوں کے دل کجی کی طرف مائل ہوچلے تھے ، (مگر جب انہوں نے اس کجی کا اتباع نہ کیا بلکہ نبی کا ساتھ دیا تو) اللہ نے انہیں معاف کردیا ، بیشک اس کا معاملہ ان لوگوں کے ساتھ شفقت ومہربانی کا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,118,'9:118','اور ان تینوں کو بھی اس نے معاف کیا جن کے معاملہ کو ملتوی کردیا گیا تھا۔ جب زمین ان پر ساری وسعت کے باوجود ان پر تنگ ہوگئی اور ان کی اپنی جانیں بھی ان پر بار ہونے لگیں اور انہوں نے جان لیا کہ اللہ سے بچنے کے لئے کوئی جائے پناہ خود اللہ ہی کے دامن رحمت کے سوا نہیں ہے تو اللہ اپنی مہربانی سے ان کی طرف پلٹا تاکہ وہ اس کی طرف پلٹ آئیں ، یقینا وہ بڑا معاف کرنے والا اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,119,'9:119','اے لوگو جو ایمان لائے ہو ، اللہ سے ڈرو اور سچے لوگوں کا ساتھ دو ۔');
INSERT INTO "sayyid_qutb" VALUES(9,120,'9:120','مدینے کے باشندوں اور گردونواح کے بدویوں کو یہ ہرگز زیبا نہ تھا کہ اللہ کے رسول کو چھوڑ کر گھر بیٹھ رہتے اور اس کی طرف سے بےپروا ہوکر اپنے نفس کی فکر میں لگ جاتے۔ اس لئے کہ ایسا کبھی نہ ہوگا کہ اللہ کی راہ میں بھوک پیاس اور جسمانی مشقت کی کوئی تکلیف وہ جھیلیں ، اور منکرین حق کو جو راہ ناگوار ہے اس پر کوئی قدم وہ اٹھائیں ، اور کسی دشمن سے (عداوت حق کا) کوئی انتقام وہ لیں ، اور اس کے بدلے ان کے حق میں ایک عمل صالح نہ لکھا جائے۔ یقینا اللہ کے ہاں محسنوں کا حق الخدمت مارا نہیں جاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,121,'9:121','اسی طرح یہ کبھی بھی نہ ہوگا کہ وہ (راہ خدا میں) تھوڑا یا بہت کوئی خرچ اٹھائیں اور (سعی جہاد میں) کوئی وادی وہ پار کریں اور ان کے حق میں اسے لکھ نہ لیا جائے تاکہ اللہ ان کے اس اچھے کارنامے کا صلہ انہیں عطاکرے۔');
INSERT INTO "sayyid_qutb" VALUES(9,122,'9:122','اور یہ کچھ ضروری نہ تھا کہ اہل ایمان سارے کے سارے ہی نکل کھڑے ہوتے ، مگر ایسا کیوں نہ ہوا کہ ان کی آبادی کے ہر حصہ میں سے کچھ لوگ نکل کر آتے اور دین کی سمجھ پیدا کرتے اور واپس جاکر اپنے علاقے کے باشندوں کو خبردار کرتے تاکہ وہ (غیر مسلمانہ روش سے) پرہیز کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(9,123,'9:123','اے لوگوں جو ایمان لائے ہو ، جنگ کرو ان منکرین حق سے جو تمہارے پاس ہیں۔ اور چاہئے کہ وہ تمہارے اندر سختی پائیں ، اور جان لو کہ اللہ متقیوں کے ساتھ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,124,'9:124',''''' جب کوئی نئی سورت نازل ہوتی ہے تو ان میں سے بعض لوگ (مذاق کے طور پر مسلمانوں سے) کہ کہو ، تم میں سے کس کے ایمان میں اس سے اضافہ ہوا ؟ '''' جو لوگ ایمان لائے ہیں ان کے ایمان میں تو فی الواقع (ہر نازل ہونے والی سورت نے) اضافہ ہی کیا ہے اور وہ اس سے دلشاد ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(9,125,'9:125','البتہ جن لوگوں کے دلوں کو (نفاق) کا روگ لگا ہوا تھا ان کی سابق نجاست پر (ہر نئی سورت نے) ایک اور نجاست کا اضافہ کردیا اور وہ مرتے دم تک کفر ہی میں مبتلا رہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,126,'9:126','کیا یہ لوگ دیکھتے نہیں کہ ہر سال ایک دو مرتبہ یہ آزمائش میں ڈالے جاتے ہیں ؟ مگر اس پر بھی توبہ کرتے ہیں نہ کوئی سبق لیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(9,127,'9:127','جب کوئی سورت نازل ہوتی ہے تو یہ لوگ آنکھوں ہی آنکھوں میں ایک دوسرے سے باتیں کرتے ہیں کہ کہیں تم کو کوئی دیکھ تو نہیں رہا ہے ، پھر چپکے سے نکل بھاگتے ہیں۔ اللہ نے ان کے دل پھیر دیئے ہیں کیونکہ یہ ناسمجھ لوگ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(9,128,'9:128','دیکھو ! تم لوگوں کے پاس ایک رسول آیا ہے جو خود تم ہی میں سے ہے ، تمہارا نقصان میں پڑنا اس پر شاق ہے ، تمہاری فلاح کا وہ حریص ہے ، ایمان والوں کے لئے وہ شفیق اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(9,129,'9:129','اگر یہ لوگ تم سے منہ پھیرتے ہیں تو اے نبی ، ان سے کہ دو کہ '''' میرے لئے اللہ بس کرتا ہے ، کوئی معبود نہیں مگر وہ اسی پر میں نے بھروسہ کیا اور وہ مالک ہے عرش عظیم کا ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,1,'10:1','ا ل ر ، یہ اس کتاب کی آیات ہیں جو حکمت و دانش سے لبریز ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,2,'10:2',''''' کیا لوگوں کے یہ ایک عجیب بات ہوگئی کہ ہم نے خود انہی میں سے ایک آدمی پر وحی بھیجی کہ (غفلت میں پڑے ہوئے) لوگوں کو چونکا دے اور جو مان لیں ان کو خوشخبری دے دے کہ ان کے لئے ان کے رب کی موجودگی میں سچے قدم ہیں۔ منکرین نے کہا کہ یہ شخص تو کھلا جادوگر ہے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,3,'10:3',''''' حقیقت یہ ہے کہ تمہارا رب وہی خدا ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا ، پھر تخت سلطنت پر جلوہ گر ہوکر کائنات کا انتظام چلا رہا ہے۔ کوئی شفاعت '''' سفارش '''' کرنے والا نہیں ہے۔ الا یہ کہ اس کی اجازت کے بعد شفاعت کرے۔ یہی اللہ تمہارا رب ہے۔ لہذا تم اسی کی عبادت کرو۔ پھر کیا تم ہوش میں نہ آؤ گے۔');
INSERT INTO "sayyid_qutb" VALUES(10,4,'10:4','اسی کی طرف تم سب کو پلٹ کر جانا ہے۔ یہ اللہ کا پکا وعدہ ہے۔ بیشک پیدائش کی ابتدا وہی کرتا ہے ، پھر وہی دوبارہ پیدا کرے گا تاکہ جو لوگ ایمان لائے اور جنہوں نے نیک اعمال کیے ان کو پورے انصاف کے ساتھ جزا دے۔ اور جنہوں نے کفر کا طریقہ اختیار کیا ہو کھولتا ہوا پانی پئیں اور درد ناک سزا بھگتیں اس انکار حق کی پاداش میں جو وہ کرتے رہے ،');
INSERT INTO "sayyid_qutb" VALUES(10,5,'10:5','وہی ہے جس نے سورج کو اجیالا بنایا اور چاند کو چمک دی اور چاند کے گھٹنے بڑھنے کی منزلیں ٹھیک ٹھیک مقرر کردیں تاکہ تم اس سے برسوں اور تاریخوں کے حساب معلوم کرو۔ اللہ نے یہ سب کچھ برحق ہی پیدا کیا ہے۔ وہ اپنی نشانیوں کو کھول کھول کر پیش کر رہا ہے۔ ان لوگوں کے لئے جو علم رکھتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,6,'10:6','یقینا رات اور دن کے الٹ پھیر میں اور ہر اس چیز میں جو اللہ نے زمین اور آسمانوں میں پیدا کی ہے ، نشانیاں ہیں ان لوگوں کے لئے جو غلط بینی وغلط روی سے بچنا چاہتے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,7,'10:7',''''' حقیقت یہ ہے کہ جو لوگ ہم سے ملنے کی توقع نہیں رکھتے اور دنیا کی زندگی ہی پر راضی اور مطمئن ہوگئے ہیں ، اور جو لوگ ہماری نشانیوں سے غافل ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(10,8,'10:8','ان کا آخری ٹھکانا جہنم ہوگا ان برائیوں کی پاداش میں جن کا اکتساب وہ (اپنے اس غلط عقیدے اور غلط طرز عمل کی وجہ سے) کرتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,9,'10:9','اور یہ بھی حقیقت ہے کہ جو لوگ ایمان لائے (یعنی جنہوں نے ان صداقتوں کو قبول کرلیا جو اس کتاب میں پیش کی گئی ہیں) اور نیک اعمال کرتے رہے انہیں ان کا رب ان کے ایمان کی وجہ سے سیدھی راہ چلائے گا ، نعمت بھری جنتوں میں ان کے نیچے نہریں بہیں گی ،');
INSERT INTO "sayyid_qutb" VALUES(10,10,'10:10','وہاں ان کی صدا یہ ہوگی کہ '''' پاک ہے تو اے خدا ''''، ان کی دعا یہ ہوگی کہ '''' سلامتی ہو '''' اور ان کی ہر بات کا خاتمہ اس پر ہوگا کہ '''' ساری تعریف اللہ رب العالمین ہی کے لیے ہے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,11,'10:11',''''' اگر کہیں اللہ لوگوں کے ساتھ برا معاملہ کرنے میں بھی اتنی جلدی کرتا جتنی وہ دنیا کی بھلائی مانگنے میں جلدی کرتے ہیں تو ان کی مہلت عمل کبھی کی ختم کردی گئی ہوتی۔ (مگر ہمارا یہ طریقہ نہیں ہے) اس لئے ہم ان لوگوں کو جو ہم سے ملنے کی توقع نہیں رکھتے ان کی سرکشی میں بھٹکنے کے لئے چھوٹ دے دیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,12,'10:12','انسان کا حال یہ ہے کہ جب ان پر کوئی سخت وقت آتا ہے تو کھڑے اور بیٹھے اور لیٹے ہم کو پکارتا ہے ، مگر جب ہم اس کی مصیبت ٹال دیتے ہیں تو ایسا چل نکلتا ہے کہ گویا اس نے کبھی اپنے برے وقت پر ہم کو پکارا ہی نہ تھا۔ اس طرح حد سے گزر جانے والوں کے لئے ان کے کرتوت خوشنما بنا دیئے گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,13,'10:13','لوگو ، تم سے پہلے کی قوموں کو ہم نے ہلاک کردیا جب انہوں نے ظلم کی روش اختیار کی اور ان کے رسول ان کے پاس کھلی کھلی نشانیاں لے کر آئے اور انہوں نے ایمان لاکر ہی نہ دیا۔ اس طرح ہم مجرموں کو ان کے جرائم کا بدلہ دیا کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,14,'10:14','اب ان کے بعد ہم نے تم کو ان کی زمین میں جگہ دی ہے ، تاکہ دیکھیں تم کیسے عمل کرتے ہو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,15,'10:15',''''' جب انہیں ہماری صاف صاف باتیں سنائی جاتی ہیں تو وہ لوگ جو ہم سے ملنے کی توقع نہیں رکھتے ، کہتے ہیں کہ '''' اس کے بجائے کوئی اور قرآن لاؤ یا اس میں کچھ ترمیم کرو ''''۔ اے نبی ، ان سے کہو '''' میرا یہ کام نہیں ہے کہ اپنی طرف سے اس میں کوئی تغیر تبدل کرلوں۔ میں تو بس اس وحی کا پیرو ہوں جو میرے پاس بھیجی جاتی ہے۔ اگر میں اپنے رب کی نافرمانی کروں تو مجھے ایک بڑے ہولناک دن کے عذاب کا ڈر ہے ''''');
INSERT INTO "sayyid_qutb" VALUES(10,16,'10:16','اور کہو '''' اگر اللہ کی مشیت یہی ہوتی تو میں یہ قرآن تمہیں کبھی نہ سناتا اور اللہ تمہیں اس کی خبر تک نہ دیتا۔ آخر اس سے پہلے میں ایک عمر تمہارے درمیان گزارچکا ہوں ، کیا تم عقل سے کام نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(10,17,'10:17','پھر اس سے بڑھ کر ظالم اور کون ہوگا جو ایک جھوٹی بات گھڑ کر اللہ کی طرف منسوب کرے یا اللہ کی واقعی آیات کو جھوٹا قرار دے۔ یقینا مجرم کبھی فلاح نہیں پاسکتے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,18,'10:18',''''' یہ لوگ اللہ کے سوا ان کی پرستش کر رہے ہیں جو ان کو نہ نقصان پہنچا سکتے ہیں نہ نفع ، اور کہتے یہ ہیں کہ یہ اللہ کے ہاں ہمارے سفارشی ہیں۔ اے نبی ، ان سے کہو '''' کیا تم اللہ کو اس بات کی خبر دیتے ہو جسے وہ نہ آسمانوں میں جانتا ہے اور نہ زمین میں ؟ '''' پاک ہے وہ اور بالا و برتر ہے اس شرک سے جو یہ لوگ کرتے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,19,'10:19','ابتداء سارے انسان ایک ہی امت تھے ، بعد میں انہوں نے مختلف عقیدے اور مسلک بنا لیے اور اگر تیرے رب کی طرف سے پہلے ہی ایک بات طے نہ کرلی گئی ہوتی تو جس چیز میں وہ باہم اختلاف کر رہے ہیں اس کا فیصلہ کردیا جاتا');
INSERT INTO "sayyid_qutb" VALUES(10,20,'10:20',''''' اور یہ جو وہ کہتے ہیں کہ اس نبی پر اس کے رب کی طرف سے کوئی نشانی کیوں نہ اتاری گئی ، تو ان سے کہو '''' غیب کا مالک و مختار تو اللہ ہی ہے ، اچھا انتظار کرو ، میں بھی تمہارے ساتھ انتظار کرتا ہوں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,21,'10:21',''''' لوگوں کا حال یہ ہے کہ مصیبت کے بعد جب ہم ان کو رحمت کا مزا چکھاتے ہیں تو فورا ہی وہ ہماری نشانیوں کے معاملہ میں چالبازیاں شروع کردیتے ہیں۔ ان سے کہو '''' اللہ اپنی چال میں تم سے زیادہ تیز ہے ، اس کے فرشتے تمہاری سب مکاریوں کو قلم بند کر رہے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,22,'10:22','وہ اللہ ہی ہے جو تم کو خشکی اور تری میں چلاتا ہے۔ چناچہ جب تم کشتیوں میں سوار ہوکر باد موافق پر شاداں وفرحاں سفر کررہے ہوتے ہو اور اور پھر یکایک باد مخالف کا زور ہوتا ہے اور ہر طرف سے موجوں کے تھپیڑے لگتے ہیں اور مسافر سمجھ لیتے ہیں کہ طوفان میں گھر گئے ، اس وقت سب اپنے دین کو اللہ ہی کے لئے خالص کر کے اس سے دعائیں مانگتے ہیں کہ '''' اگر تو نے ہم کو اس بلا سے نجات دے دی تو ہم شکر گزار بندے بنیں گے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,23,'10:23','مگر جب وہ ان کو بچالیتا ہے تو پھر وہی لوگ حق سے منحرف ہوکر زمین میں بغاوت کرنے لگتے ہیں۔ لوگو ، تمہاری یہ بغاوت تمہارے ہی خلاف پڑ رہی ہے۔ دنیا کی زندگی کے چند روزہ مزے ہیں (لوٹ لو) ، پھر ہماری طرف تمہیں پلٹ کر آنا ہے ، اس وقت ہم تمہیں بتا دیں گے کہ تم کیا کچھ کرتے رہے ہو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,24,'10:24',''''' دنیا کی یہ زندگی (جس کے نشے میں مست ہوکر تم ہماری نشانیوں سے غفلت برت رہے ہو) اس کی مثال ایسی ہے جیسے آسمان سے ہم نے پانی برسایا تو زمین کی پیداوار ، جسے آدمی اور جانور سب کھاتے ہیں ، خوب گھنی ہوگئی ، پھر عین اس وقت جب کہ زمین اپنی بہار پر تھی اور کھیتیاں بنی سنوری کھڑی تھیں اور ان کے مالک سمجھ رہے تھے کہ اب ہم ان سے فائدہ اٹھا نے پر قادر ہیں ، یکا یک رات کو یا دن کو ہمارا حکم آگیا اور ہم نے اسے ایسا غارت کر کے رکھ دیا کہ گویا کل وہاں کچھ تھا ہی نہیں۔ اسی طرح ہم نشانیاں کھول کھول کر پیش کرتے ہیں ان لوگوں کے لئے جو سوچنے سمجھنے والے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,25,'10:25',''''' (تم اس ناپائیدار زندگی کے فریب میں مبتلا ہو رہے ہو) اور اللہ تمہیں دار السلام کی طرف دعوت دے رہا ہے۔ (ہدایت اس کے اختیار میں ہے) جس کو وہ چاہتا ہے سیدھا راستہ دکھا دیتا ہے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,26,'10:26',''''' جن لوگوں نے بھلائی کا طریقہ اختیار کیا ان کے لئے بھلائی ہے اور مزید فضل۔ ان کے چہروں پر رو سیاہی اور ذلت نہ چھائے گی۔ وہ جنت کے مستحق ہیں جہاں وہ ہمیشہ رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(10,27,'10:27','اور جن لوگوں نے برائیاں کمائیں ان کی برائی جیسی ہے ویسا ہی وہ بدلہ پائیں گے ، ذلت ان پر مسلط ہوگی ، کوئی اللہ سے ان کو بچانے والا نہ ہوگا ، ان کے چہروں پر ایسی تاریکی چھائی ہوئی ہوگی جیسے رات کے سیاہ پردے ان پر پڑے ہوئے ہوں ، وہ دوزخ کے مستحق ہیں جہاں وہ ہمیشہ رہیں گے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,28,'10:28',''''' جس روز ہم ان سب کو ایک ساتھ (اپنی عدالت میں) اکٹھا کریں گے ، پھر ان لوگوں جنہوں نے شرک کیا ہے ، ٹھر جاؤ تم بھی اور تمہارے شریک بھی ، پھر ہم ان کے درمیان اجنبیت کا پردہ ہٹادیں گے اور ان کے شریک کہیں گے کہ '''' تم ہماری عبادت تو نہیں کرتے تھے۔ ہمارے اور تمہارے درمیان اللہ کی گواہی کافی ہے کہ (تم اگر ہماری عبادت کرتے بھی تھے تو) ہم تمہاری اس عبادت سے بالکل نے خبر تھے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,29,'10:29','ہمارے اور تمہارے درمیان اللہ کی گواہی کافی ہے کہ (تم اگر ہماری عبادت کرتے بھی تھے تو) ہم تمہاری اس عبادت سے بالکل نے خبر تھے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,30,'10:30','اس وقت ہر شخص اپنے کئے کا مزہ چکھ لے گا ، سب اپنے حقیقی مالک کی طرف سے پھیر دیئے جائیں گے اور وہ سارے جھوٹ جو انہوں نے گھڑ رکھے تھے گم ہوجائیں گے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,31,'10:31','ان سے پوچھو ، کون تمہیں آسمان اور زمین سے رزق دیتا ہے ؟ یہ سماعت اور بینائی کی قوتیں کس کے اختیار میں ہیں ؟ کون بےجان میں سے جاندار کو اور جاندار میں سے بےجان کو نکالتا ہے ؟ کون اس نظم عالم کی تدبیر کر رہا ہے ؟ وہ ضرور کہیں گے کہ اللہ۔ کہو ، پھر (تم حقیقت کے خلاف چلنے سے) پرہیز نہیں کرتے ؟');
INSERT INTO "sayyid_qutb" VALUES(10,32,'10:32','تب تو یہی اللہ تمہارا حقیقی رب ہے۔ پھر حق کے بعد گمراہی کے سوا اور کیا باقی رہ گیا ؟ آخر یہ تم کدھر پھرائے جارہے ہو ؟ ''''');
INSERT INTO "sayyid_qutb" VALUES(10,33,'10:33','اسی طرح تیرے رب کا کلمہ ان لوگوں پر ثابت ہوگیا جو گناہ کرتے رہے ، کہ بیشک یہ ایمان نہیں لائیں گے');
INSERT INTO "sayyid_qutb" VALUES(10,34,'10:34','ان سے پوچھو ، تمہارے ٹھہرائے ہوئے شریکوں میں کوئی ہے جو تمہاری تخلیق کی ابتدا بھی کرتا ہو اور پھر اس کا اعادہ بھی کرے۔ کہو صرف اللہ ہی ہے جو تخلیق کی ابتدا بھی کرتا ہے اور اس کا اعادہ بھی۔ پھر تم کس الٹی راہ پر چلائے جا رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(10,35,'10:35','ان سے پوچھو تمہارے ٹھہرائے ہوئے شریکوں میں کوئی ایسا بھی ہے جو حق کی طرف راہنمائی کرتا ہو۔ کہو وہ صرف اللہ ہے جو حق کی طرف راہنمائی کرتا ہے۔ پھر بھلا بتاؤ جو حق کی طرف راہنمائی کرتا ہے وہ اس کا زیادہ مستحق ہے کہ اس کی پیروی کی جائے یا وہ جو خود راہ نہیں پاتا الا یہ کہ اس کی رہنمائی کی جائے۔ آخر تمہیں کیا ہوگیا ہے ، کیسے الٹے الٹے فیصلے کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(10,36,'10:36',''''' حقیقت یہ ہے کہ ان میں سے اکثر لوگ محض قیاس و گمان کے پیچھے چلے جارہے ہیں ، حالانکہ گمان حق کی ضرورت کو کچھ بھی پورا نہیں کرتا جو کچھ یہ کر رہے ہیں اللہ اس کو خوب جانتا ہے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,37,'10:37',''''' اور یہ قرآن وہ چیز نہیں ہے جو اللہ کی وحی کے بغیر تصنیف کرلیا جائے بلکہ یہ تو جو کچھ آچکا تھا اس کی تصدیق اور کتاب کی تفصیل ہے اس میں کو ئی شک نہیں کہ یہ فرمانروائے کائنات کی طرف سے ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,38,'10:38','کیا یہ لوگ کہتے ہیں کہ پیغمبر نے اسے خود تصنیف کرلیا ہے ؟ کہو '''' اگر تم اپنے اس الزام میں سچے ہو تو ایک سورت اس جیسی تصنیف کر لاؤ اس ایک خدا کو چھوڑ کر جس جس کو بلا سکتے ہو ، مدد کے لئے بلا لو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,39,'10:39','اصل یہ ہے کہ جو چیز ان کے علم کی گرفت میں نہیں آئی اور جس کا مآل بھی ان کے سامنے نہیں آیا ، اس کو انہوں نے خواہ مخوا اٹکل پچو جھٹلادیا۔ اسی طرح تو ان سے پہلے کے لوگ بھی جھٹلا چکے ہیں ، پھر دیکھ لو ان ظالموں کا انجام کیا ہوا ؟');
INSERT INTO "sayyid_qutb" VALUES(10,40,'10:40','ان میں سے کچھ لوگ ایمان لائیں گے اور کچھ نہیں لائیں گے اور تیرا رب ان مفسدوں کو خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,41,'10:41','اگر یہ تجھے جھٹلاتے ہیں تو کہ دے کہ '''' میرا عمل میرے لئے ہے اور تمہارا عمل تمہارے لئے ، جو کچھ میں کرتا ہوں اس کی ذمہ داری سے تم بری ہو اور جو کچھ تم کررہے ہو اس کی ذمہ داری سے میں بری ہوں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,42,'10:42','ان میں سے بہت سے لوگ ایسے ہیں جو تیری باتوں کو سنتے ہیں مگر کیا تو بہروں کو سنائے گا خواہ وہ کچھ ہی نہ سمجھتے ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(10,43,'10:43','ان میں سے بہت سے لوگ ہیں جو تجھے دیکھتے ہیں ، مگر کیا تو اندھوں کو راہ راست بتائے گا خواہ انہیں کچھ سوجھتا ہی نہ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(10,44,'10:44',''''' حقیقت یہ ہے کہ اللہ لوگوں پر ظلم نہیں کرتا ، لوگ خود ہی اپنے اوپر ظلم کرتے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,45,'10:45',''''' (آج یہ دنیا کی زندگی میں مست ہیں) اور جس روز اللہ ان کو اکٹھا کرے گا تو (یہی دنیا کی زندگی انہیں ایسی محسوس ہوگی) گویا یہ محض ایک گھڑی بھر آپس میں جان پہچان کرنے کو ٹھرے تھے (اس وقت تحقیق ہوجائے گا کہ) فی الواقع سکت گھاٹے میں رہے وہ لوگ جنوں نے اللہ کی ملاقات کو جھٹلایا اور ہرگز وہ راہ راست پر نہ تھے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,46,'10:46',''''' جن برے نتائج سے ہم انہیں ڈرا رہے ہیں ان کا کوئی حصہ ہم تیرے جیتے جی دکھاویں گے یا اس سے پہلے ہی تجھے اٹھالیں ، بہرحال انہیں آنا ہماری طرف ہی ہے اور جو کچھ یہ کر رہے ہیں اس پر اللہ گواہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,47,'10:47','ہر امت کے لئے ایک رسول ہے۔ پھر جب کسی امت کے پاس اس کا رسول آجاتا ہے تو اس کا فیصلہ پورے انصاف کے ساتھ چکادیا جاتا ہے اور اس پر ذرہ برابر ظلم نہیں کیا جاتا۔');
INSERT INTO "sayyid_qutb" VALUES(10,48,'10:48','کہتے ہیں اگر تمہاری یہ دھمکی سچی ہے تو آخر یہ کب پوری ہوگی ؟');
INSERT INTO "sayyid_qutb" VALUES(10,49,'10:49','کہو '''' میرے اختیار میں تو خود اپنا نفع و ضرر بھی نہیں ، سب کچھ اللہ کی مشیت پر موقوف ہے۔ ہر امت کے لئے مہلت کی ایک مدت ہے ، جب یہ مدت پوری ہوجاتی ہے تو گھڑی بھر کی تقدیم وتاخیر بھی نہیں ہوتی ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,50,'10:50','ان سے کہو ، کبھی تم نے یہ بھی سوچا کہ اگر اللہ کا عذاب اچانک رات کو یا دن کو آجائے (تو تم کیا کرسکتے ہو ؟ ) آخر یہ ایسی کون سی چیز ہے جس کے لئے مجرم جلدی مچائیں ؟');
INSERT INTO "sayyid_qutb" VALUES(10,51,'10:51','کیا جب وہ تم پر آپڑے اسی وقت تم اسے مانوگے ؟۔۔۔ اب بچنا چاہتے ہو ؟ حالانکہ تم خود ہی اس کے جلدی آنے کا تقاضا کر رہے تھے !');
INSERT INTO "sayyid_qutb" VALUES(10,52,'10:52','پھر ظالموں سے کہا جائے گا اب ہمیشہ کے عذاب کا مزہ چکھو ، جو کچھ تم کماتے رہے ہو ، اس کی پاداش کے سوا اور کیا تم کو بدلہ دیا جاسکتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(10,53,'10:53','پھر پوچھتے ہیں کیا واقعی یہ سچ ہے جو تم کہ رہے ہو ؟ کہو '''' میرے رب کی قسم ، یہ بالکل سچ ہے ، اور تم اتنا بل بوتا نہیں رکھتے کہ اسے ظہور میں آنے سے روک دو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,54,'10:54','اگر ہر اس شخص کے پاس جس نے ظلم کیا ہے ، روئے زمین کی دولت بھی ہو تو اس عذاب سے بچنے کے لئے وہ اسے فدیہ میں دینے پر آمادہ ہوجائے گا۔ جب یہ لوگ اس عذاب کو دیکھ لیں گے تو دل ہی دل میں پچھتائیں گے مگر ان کے درمیان پورے انصاف سے فیصلہ کیا جائے گا ، کوئی ظلم ان پر نہ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(10,55,'10:55',''''' سنو ، آسمانوں اور زمین میں جو کچھ ہے اللہ کا ہے۔ سن رکھو ! اللہ کا وعدہ سچا ہے مگر اکثر انسان جانتے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,56,'10:56','وہی زندگی بخشتا ہے اور وہی موت دیتا ہے اور اسی کی طرف تم سب کو پلٹنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,57,'10:57','لوگو تمہارے پاس تمہارے رب کی طرف سے نصیحت آگئی ہے۔ یہ وہ چیز ہے جو دلوں کے امراض کی شفا ہے اور جو اسے قبول کرلیں ان کے لیے رہنمائی اور رحمت ہے');
INSERT INTO "sayyid_qutb" VALUES(10,58,'10:58','اے نبی ، کہو کہ یہ اللہ کا فضل اور اس کی مہربانی ہے اس پر تو لوگوں کی تو خوشی منانی چاہئے ، یہ ان سب چیزوں سے بہتر ہے جنہیں لوگ سمیٹ رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(10,59,'10:59',''''' اے نبی ان سے کہو ، '''' تم لوگوں نے کبھی یہ بھی سوچا ہے کہ جو رزق اللہ نے تمہارے لئے اتارا تھا ، اس میں سے تم نے خود ہی کسی کو حرام اور کسی کو حلال ٹھرالیا ''''۔ ان سے پوچھو ، اللہ نے تم کو اس کی اجازت دی تھی ؟ یا تم اللہ پر افترا کر رہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(10,60,'10:60','جو لوگ اللہ پر یہ جھوٹا افترا باندھتے ہیں ان کا کیا گمان ہے کہ قیامت کے روز ان سے کیا معاملہ ہوگا ؟ اللہ تو لوگوں پر مہربانی کی نظر رکھتا ہے۔ مگر اکثر انسان ایسے ہیں جو شکر نہیں کرتے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,61,'10:61',''''' اے نبی ، تم جس حال میں بھی ہوتے ہو ، اور قرآن میں سے جو کچھ بھی سناتے ہو ، اور لوگو ، تم بھی جو کچھ کرتے ہو ، اس سب کے دوران میں ہم تم کو دیکھتے رہتے ہیں۔ کوئی ذرہ برابر چیز آسمان اور زمین میں ایسی نہیں ہے ، نہ چھوٹی نہ بڑی ، جو تیرے رب کی نظر سے پوشیدہ ہو اور ایک صاف دفتر میں درج نہ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(10,62,'10:62','سنو ! جو اللہ کے دوست ہیں ، ان کے لئے کسی خوف اور رنج کا موقع نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,63,'10:63','جو ایمان لائے اور جنہوں نے تقویٰ کا رویہ اختیار کیا ،');
INSERT INTO "sayyid_qutb" VALUES(10,64,'10:64','دنیا اور آخرت دونوں زندگیوں میں ان کے لئے بشارت ہی بشارت ہے۔ اللہ کی باتیں بدل نہیں سکتیں۔ یہی بڑی کامیابی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,65,'10:65','اے نبی ، جو باتیں یہ لوگ تجھ پر بناتے ہیں وہ تجھے رنجیدہ نہ کریں۔ عزت ساری کی ساری خدا کے اختیار میں ہے ، اور وہ سب کچھ سنتا اور جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,66,'10:66','آگاہ رہو ! آسمان کے بسنے والے ہوں یا زمین کے ، سب کے سب اللہ کے مملوک ہیں اور جو لوگ اللہ کے سوا کچھ (اپنے خود ساختہ) شریکوں کو پکار رہے ہیں وہ نرے وہم وہم و گمان کے پیرو ہیں اور محض قیاس آرائیاں کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,67,'10:67','وہ اللہ ہی جس نے تمہارے لئے رات بنائی کہ اس میں سکون حاصل کرو اور دن کو روشن بنایا۔ اس میں نشانیاں ہیں ان لوگوں کے لئے جو (کھلے کانوں سے پیغمبر کی دعوت) سنتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,68,'10:68',''''' لوگوں نے کہ دیا ہے کہ اللہ نے کسی کو بیٹا بنایا ہے۔ سبحان اللہ ! وہ تو بےنیاز ہے ، آسمانوں اور زمین میں جو کچھ ہے سب اس کی ملک ہے۔ تمہارے پاس اس قول کے لئے آخر دلیل کیا ہے ؟ کیا تم اللہ کے متعلق وہ باتیں کہتے ہو جو تمہارے علم میں نہیں ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(10,69,'10:69','اے نبی ، کہ دو کہ جو لوگ اللہ پر جھوٹے افترا باندھتے ہیں وہ ہرگز فلاح نہیں پاسکتے۔');
INSERT INTO "sayyid_qutb" VALUES(10,70,'10:70','دنیا کی چند روزہ زندگی میں مزے کرلیں ، پھر ہماری طرف ان کو پلٹنا ہے ، پھر ہم اس کفر کے بدلے جس کا ارتکاب وہ کر رہے ہیں ، ان کو سخت عذاب کا مزہ چکھائیں گے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,71,'10:71',''''' ان کو نوح کا قصہ سناؤ، اس وقت کا قصہ جب اس نے اپنے قوم سے کہا کہ '''' اے برادران قوم ، اگر میرا تمہارے درمیان رہنا ار اللہ کی آیات سنا سنا کر تمہیں غفلت سے بیدار کرنا تمہارے لئے ناقابل برداشت ہوگیا ہے تو میرا بھروسہ اللہ پر ہے۔ تم اپنے ٹھرائے ہوئے شریکوں کو ساتھ لے کر ایک متفقہ فیصلہ کرلو ، اور جو منصوبہ تمہارے پیش نظر ہو ، اس کو خوب سوچ سمجھ لو تاکہ اس کا کوئی پہلو تمہاری نگاہ سے پوشیدہ نہ رہے ، پھر میرے خلاف اس کو عمل میں لے آؤ اور مجھے ہرگز مہلت نہ دو ۔');
INSERT INTO "sayyid_qutb" VALUES(10,72,'10:72','تم نے میری نصیحت سے منہ موڑا (تو میرا کیا نقصان کیا) میں تم سے کسی اجر کا طلب گار نہ تھا ، میرا اجر تو اللہ کے ذمہ ہے۔ اور مجھے حکم دیا گیا ہے کہ (خواہ کوئی مانے یا نہ مانے) میں خود مسلم بن کر رہوں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,73,'10:73','انہوں نے اسے جھٹلایا اور نتیجہ یہ ہوا کہ ہم نے اسے اور ان لوگوں جو اس کے ساتھ کشتی میں تھے ، بچا لیا اور انہیں کو زمین میں جانشین بنایا اور ان سب لوگوں کو غرق کردیا جنہوں نے ہماری آیات کو جھٹلایا تھا۔ پس دیکھ لو جنہیں متنبہ کیا گیا تھا (اور پھر بھی انہوں نے مان کر نہ دیا) ان کا کیا انجام ہوا ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,74,'10:74',''''' پھر نوح کے بعد ہم نے مختلف پیغمبروں کو ان کی قوموں کی طرف بھیجا اور وہ ان کے پاس کھلی کھلی نشانیاں لے کر آئے ، مگر جس چیز کو انہوں نے پہلے جھٹلادیا تھا ، اسے پھر مان کر نہ دیا۔ اس طرح ہم حد سے گزر جانے والوں کے دلوں پر ٹھپہ لگا دیتے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,75,'10:75',''''' پھر ان کے بعد ہم نے موسیٰ اور ہارون کو اپنی نشانیوں کے ساتھ فرعون اور اس کے سرداروں کی طرف بھیجا ، مگر انہوں نے اپنی بڑائی کا گھمنڈ کیا اور وہ مجرم لوگ تھے۔');
INSERT INTO "sayyid_qutb" VALUES(10,76,'10:76','پس جب ہمارے پاس سے حق ان کے سامنے آیا تو انہوں نے کہ دیا کہ یہ تو کھلا جادو ہے۔');
INSERT INTO "sayyid_qutb" VALUES(10,77,'10:77','موسیٰ نے کہا '''' تم حق کو یہ کہتے ہو جب کہ وہ تمہارے سامنے آگیا ؟ کیا یہ جادو ہے ؟ حالانکہ جادوگر فلاح نہیں پایا کرتے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,78,'10:78','انہوں نے جواب میں کہا '''' کیا تو اس لئے آیا ہے کہ ہمیں اس طریقے سے پھیر دے جس پر ہم نے اپنے باپ دادا کو پایا ہے اور زمین میں بڑائی تم دونوں کی قائم ہوجائے ؟ تمہاری بات تو ہم ماننے والے نہیں ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,79,'10:79',''''' اور فرعون نے (اپنے آدمیوں سے) کہا کہ '''' ہر ماہر فن جادوگر کو میرے پاس حاضر کرو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,80,'10:80','جب جادوگر آگئے تو موسیٰ نے ان سے کہا ، جو کچھ تمہیں پھینکنا ہے ، پھینکو ''''۔ پھر جب انہوں نے اپنے انچھر پھینک دیئے');
INSERT INTO "sayyid_qutb" VALUES(10,81,'10:81','تو موسیٰ نے کہا '''' یہ جو کچھ تم نے پھینکا ہے یہ جادو ہے ، اللہ ابھی اسے باطل کیا دیتا ہے ، مفسدوں کے کام کو اللہ سدھرنے نہیں دیتا');
INSERT INTO "sayyid_qutb" VALUES(10,82,'10:82','اور اللہ اپنے فرمانوں سے حق کو حق کر دکھاتا ہے ، خواہ مجرموں کو وہ کتنا ہی ناگوار ہے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,83,'10:83','(پھر دیکھو کہ موسیٰ کو اس کی قوم میں چند نوجوانوں کے سوا کسی نے نہ مانا ، فرعون کے ڈر سے اور خود خود اپنی قوم کے سر بر آوردہ لوگوں کے ڈر سے (جنہیں خوف تھا) کہ فرعون ان کو عذاب میں مبتلا کرے گا اور واقعہ یہ ہے کہ فرعون زمین میں غلبہ رکھتا تھا اور وہ ان لوگوں میں سے تھا جو کسی حد پر رکتے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(10,84,'10:84','موسیٰ نے اپنی قوم سے کہا کہ '''' لوگو ، اگر تم واقعی اللہ پر ایمان رکھتے ہو تو اس پر بھروسہ کرو ، اگر مسلمان ہو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,85,'10:85','انہوں نے جواب دیا '''' ہم نے اللہ ہی پر بھروسہ کیا ، اے ہمارے رب ہمیں ظالم لوگوں کے لئے فتنہ نہ بنا');
INSERT INTO "sayyid_qutb" VALUES(10,86,'10:86','اور اپنی رحمت سے ہم کو کافروں سے نجات دے ''''');
INSERT INTO "sayyid_qutb" VALUES(10,87,'10:87','اور ہم نے موسیٰ اور اس کے بھائی کو اشارہ کیا کہ '''' مصر میں چند مکان اپنی قوم کے لئے مہیا کرو اور اپنے ان مکانوں کو قبلہ ٹھرا لو اور نماز قائم کرو ، اور اہل ایمان کو بشارت دے دو ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,88,'10:88',''''' موسیٰ نے دعا کی '''' اے ہمارے رب تونے فرعون اور اس کے سرداروں کو دنیا کی زندگی میں زینت اور اموال سے نواز رکھا ہے۔ اے رب کیا یہ اس لئے ہے کہ وہ لوگوں کو تیری راہ سے بھٹکائیں ؟ اے رب ، ان کے مال غارت کردے اور ان کے دلوں پر ایسی مہر کردے کہ ایمان نہ لائیں جب تک دردناک عذاب نہ دیکھ لیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,89,'10:89','اللہ تعالیٰ نے جواب دیا '''' تم دونوں کی دعا قبول کی گئی۔ ثابت قدم رہو اور لوگوں کے طریقے کی ہرگز پیروی نہ کرو جو علم نہیں رکھتے ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,90,'10:90',''''' اور ہم بنی اسرائیل کو سمندر سے گزار لے گئے۔ پھر فرعون اور اس کے لشکر ظلم اور زیادت کی غرض سے ان کے پیچھے چلے۔ حتیٰ کہ جب فرعون ڈوبنے لگا تو بول اٹھا '''' میں نے مان لیا کہ خداوند حقیقی اس کے سوا کوئی نہیں ہے جس پر بنی اسرائیل ایمان لائے ، اور میں بھی سر اطاعت جھکا دینے والوں میں سے ہوں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,91,'10:91','جواب دیا گیا اب ایمان لاتا ہے ! حالانکہ اس سے پہلے تک تو تو نافرمانی کرتا اور فساد کرنے والوں میں سے تھا۔');
INSERT INTO "sayyid_qutb" VALUES(10,92,'10:92','اب تو ہم صرف تیری لاش ہی کو بچائیں گے تاکہ بعد کی نسلوں کے لئے نشان عبرت بنے اگرچہ بہت سے انسان ایسے ہیں جو ہماری نشانیوں سے غفلت برتتے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,93,'10:93',''''' ہم نے بنی اسرائیل کو بہت اچھا ٹھکانا دیا اور نہایت عمدہ وسائل زندگی انہیں عطا کیے پھر انہوں نے باہم اختلاف نہیں کیا مگر اس وقت جب کہ علم ان کے پاس آچکا تھا۔ یقینہ تیرا رب قیامت کے روز ان کے درمیان اس چیز کا فیصلہ کر دے گا جس میں وہ اختلاف کرتے رہے ہیں ''''۔');
INSERT INTO "sayyid_qutb" VALUES(10,94,'10:94',''''' اب اگر تجھے اس ہدایت کی طرف سے کچھ بھی شک ہو جو ہم نے تجھ پر نازل کی ہے تو ان لوگوں سے پوچھ لے جو پہلے سے کتاب پڑھ رہے ہیں۔ فی الواقع یہ تیرے پاس حق ہی آیا ہے تیرے رب کی طرف سے لہذا تو شک کرنے والوں میں سے نہ ہو ،');
INSERT INTO "sayyid_qutb" VALUES(10,95,'10:95','اور ان لوگوں میں نہ شامل ہو جنہوں نے اللہ کی آیات کو جھٹلایا ہے ورنہ تو نقصان اٹھانے والوں میں سے ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(10,96,'10:96','حقیقت یہ ہے کہ جن لوگوں پر تیرے رب کا قول راست آگیا ہے ، وہ کبھی ایمان لاکر نہیں دیتے');
INSERT INTO "sayyid_qutb" VALUES(10,97,'10:97','ان کے سامنے خواہ کوئی نشانی آجائے جب تک کہ دردناک عذاب سامنے آتا نہ دیکھ لیں');
INSERT INTO "sayyid_qutb" VALUES(10,98,'10:98','پھر کیا ایسی کوئی مثال ہے کہ ایک بستی عذاب دیکھ کر ایمان لائی ہو اور اس کا ایمان اس کے لئے نفع بخش ثابت ہوا ہو ؟ یونس اور اس کی قوم کے سوا (اس کی کوئی نظیر نہیں) وہ قوم جب ایمان لے آئی تھی تو البتہ ہم نے اس پر دنیا کی زندگی میں رسوائی کا عذاب ٹال دیا تھا اور اس کو ایک مدت تک زندگی سے بہرہ مند ہونے کا موقع دے دیا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(10,99,'10:99','اگر تیرے رب کی مشیت یہ ہوتی (کہ زمین میں سب مومن و فرمانبردار ہی ہوں) تو سارے اہل زمین ایمان لے آئے ہوتے۔ پھر کیا تو لوگوں کو مجبور کرے گا کہ وہ مومن ہوجائیں ؟');
INSERT INTO "sayyid_qutb" VALUES(10,100,'10:100','کوئی متنفس اللہ کے اذن کے بغیر ایمان نہیں لاسکتا ، اور اللہ کا طریقہ یہ ہے کہ جو لوگ عقل سے کام نہیں لیتے');
INSERT INTO "sayyid_qutb" VALUES(10,101,'10:101','ان سے کہو زمین اور آسمانوں میں جو کچھ ہے ، اسے آنکھیں کھول کر دیکھو ، اور جو لوگ ایمان لانا ہی نہیں چاہتے ان کے لیے نشانیاں اور تنبی ہیں آخر کیا مفید ہوسکتی ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(10,102,'10:102','اب یہ لوگ اس کے سوا اور کس چیز کے منتظر ہیں کہ وہی برے دن دیکھیں جو ان سے پہلے گزرے ہوئے لوگ دیکھ چکے ہیں ؟ ان سے کہو " اچھا ، انتظار کرو ، میں بھی تمہارے ساتھ انتظار کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(10,103,'10:103','پھر جب ایسا وقت آتا ہے تو ہم اپنے رسولوں کو اور ان لوگوں کو بچا لیا کرتے ہیں جو ایمان لائے ہوں ہمارا یہی طریقہ ہے۔ ہم پر یہ حق ہے کہ مومنوں کو بچا لیں');
INSERT INTO "sayyid_qutb" VALUES(10,104,'10:104','اے نبی کہہ دو کہ لوگو ، اگر تابھی تک میرے دین کے متعلق کسی شک میں ہو تو سن لو کہ تم اللہ کے سوا جن کی بندگی کرتے ہو ، میں ان کی بندگی نہیں کرتا ، بلکہ صرف اسی خدا کی بندگی کرتا ہوں جس کے قبضے میں تمہاری موت ہے۔ مجھے حکم دیا گیا ہے کہ میں ایمان لانے والوں میں سے ہوں');
INSERT INTO "sayyid_qutb" VALUES(10,105,'10:105','اور مجھ سے فرمایا گیا ہے کہ یکسو ہو کر اپنے آپ کو ٹھیک ٹھیک اس دین پر قائم کردے اور ہرگز ہرگز مشرکوں میں سے نہ ہو');
INSERT INTO "sayyid_qutb" VALUES(10,106,'10:106','اور اللہ کو چھوڑ کر کسی ایسی ہستی کو نہ پکار جو تجھے نہ فائدہ پہنچا سکتی ہے اگر تو ایسا کرے گا تو ظالموں میں سے ہوگا');
INSERT INTO "sayyid_qutb" VALUES(10,107,'10:107','اگر اللہ تجھے کسی مصیبت میں ڈالے تو خود اس کے سوا کوئی نہیں جو اس مصیبت کو ٹال دے ، اور اگر وہ تیرے حق میں کسی بھلائی کا ارادہ کرے تو اس کے فضل کو پھیرنے والا بھی کوئی نہیں ہے ، وہ اپنے بندوں میں سے جس کو چاہتا ہے اپنے فضل سے نوازتا ہے ، اور وہ در گزر کرنے والا اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(10,108,'10:108','اے محمد کہہ دو کہ لوگو تمہارے پاس تمہارے رب کی طرف سے حق آچکا ہے۔ اب جو سیدھی راہ اختیار کرے اس کی راست روی اسی کے لیے مفید ہے اور جو گمراہ رہے اس کی گمراہی اسی کے لیے تباہ کن ہے اور میں تمہارے اوپر کوئی حوالہ دار نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(10,109,'10:109','اور اے نبی تم اس ہدایت کی پیروی کیے جاؤ جو تمہاری طرف بذریعہ وحی بھیجی جا رہی ہے ، اور صبر کرو یہاں تک کہ اللہ فیصلہ کردے ، اور وہی بہترین فیصلہ کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,1,'11:1','ال ر۔ فرمان ہے جس کی آیتیں پختہ اور مفصل ارشاد ہوتی ہیں ایک دانا اور باخبر ہستی کی طرف سے');
INSERT INTO "sayyid_qutb" VALUES(11,2,'11:2','کہ تم نہ بندگی کرو مگر صرف اللہ کی۔ میں اس کی طرف سے تم کو خبردار کرنے والا بھی ہوں ، اور بشارت دینے والا بھی');
INSERT INTO "sayyid_qutb" VALUES(11,3,'11:3','اور یہ کہ تم اپنے رب سے معافی چاہو اور اس کی طرف پلٹ آؤ تو وہ ایک مدت خاص تک تم کو اچھا سامان زندگی دے گا اور ہر صاحب فضل کو اس کا فضل عطا کرے گا لیکن اگر تم منہ پھیرتے ہو تو میں تمہارے حق میں ایک بڑے ہولناک دن کے عذاب سے ڈرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,4,'11:4','تم سب کو اللہ کی طرف پلٹنا ہے اور وہ سب کچھ کرسکتا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,5,'11:5','دیکھو ، یہ لوگ اپنے سینوں کو موڑتے ہیں تاکہ اس سے چھپ جائیں۔ خبردار ، جب یہ کپڑوں سے اپنے آپ کو ڈھانپتے ہیں ، اللہ ان کے چھپے کو بھی جانتا ہے اور کھلے کو بھی ، وہ تو ان بھیدوں سے بھی واقف ہے جو سینوں میں ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,6,'11:6','زمین میں چلنے والا کوئی جاندار ایسا نہیں ہے جس کا رزق اللہ کے ذمے نہ ہو اور جس کے متعلق وہ نہ جانتا ہو کہ کہاں وہ رہتا ہے ، اور کہا وہ سونپا جاتا ہے ، سب کچھ ایک صٖا دفتر میں درج ہے');
INSERT INTO "sayyid_qutb" VALUES(11,7,'11:7','اور وہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا۔ جب کہ اس سے پہلے اس کا عرش پانی پر تھا۔ تاکہ تم کو آزما کر دیکھے تم میں کون بہتر عمل کرنے والا ہے۔ اب اگر اے نبی ، تم کہتے ہو کہ لوگو ! مرنے کے بعد تم دوبارہ اٹھائے جاؤ گے ، تو منکرین فوراً بول اٹھتے ہیں کہ یہ تو صریح جادوگری ہے');
INSERT INTO "sayyid_qutb" VALUES(11,8,'11:8','اور اگر ہم ایک خاص مدت تک ان کی سزا کو ٹالتے ہیں تو وہ کہنے لگتے ہیں کہ یہ آخر کس چیز نے اسے روک رکھا ہے ؟ سنو ! جس روز اس سزا کا وقت آگیا تو وہ کسی کے پھیرے نہ پھر سکے گا اور وہی چیز ان کو آ گھیرے گی جس کا وہ مذاق اڑا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,9,'11:9','اگر کبھی ہم انسان کو اپنی رحمت سے نوازنے کے بعد پھر اس سے محروم کردیتے ہیں تو وہ مایوس ہوتا ہے اور ناشکری کرنے لگتا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,10,'11:10','اور اگر اس مصیبت کے بعد جو اس پر آئی تھی ، ہم اسے نعمت کا مزا چکھاتے ہیں تو وہ کہتا ہے میرے سارے دلدر پار ہوگئے ، پھر وہ پھولا نہیں سماتا اور اکڑنے لگتا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,11,'11:11','اس عیب سے پاک اگر کوئی ہیں تو بس وہ لوگ جو صبر کرنے والے اور نیکو کار ہیں اور وہی ہیں جن کے لیے درگزر بھی ہے اور بڑا اجر بھی');
INSERT INTO "sayyid_qutb" VALUES(11,12,'11:12','تو اے پیغمبر ، کہیں ایسا نہ ہو کہ تم ان چیزوں میں سے کسی چیز کو (بیان کرنے سے) چھوڑ دو جو تمہاری طرف وحی کی جا رہی ہیں اور اس بات پر دل تنگ ہوکر وہ کہیں گے " اس شخص پر کوئی خزانہ کیوں نہ اتارا گیا " یا یہ کہ " اس کے ساتھ کوئی فرشتہ کیوں نہ آیا " تم تو محض خبردار کرنے والے ہو ، آگے ہر چیز کا حوالہ دار اللہ ہے');
INSERT INTO "sayyid_qutb" VALUES(11,13,'11:13','کیا یہ کہتے ہیں کہ پیغمبر نے یہ کتاب خود گھڑ لی ہے ؟ کہو " اچھا یہ بات ہے تو اس جیسی گھڑی ہوئی دس سورتیں تم بنا لاؤ اور اللہ کے سوا اور جو جو (تمہارے معبود) ہیں ان کو مدد کے لیے بلا سکتے ہو تو بلا لو اگر تم (انہیں معبود سمجھنے میں) سچے ہو');
INSERT INTO "sayyid_qutb" VALUES(11,14,'11:14','فَاِنْ لَّمْ يَسْتَجِيْبُوْا لَكُمْ (اب اگر وہ (تمہارے معبود) تمہاری مدد کو نہیں پہنچتے) ۔ اور تم دس سورتیں گھڑ کر نہیں لاتے کیونکہ تمہارے معبود اس سلسلے میں تمہاری کچھ مدد نہٰں کرسکتے اور یہ تو کام بھی ایسا ہے جس کا ہونا ناممکن ہے ، اور تم خود بھی یہ کام بہرحال نہیں کرسکتے کیونکہ تم اپنے معبودوں کو تب ہی بلاتے ہو جب تم خود عاجز آجاؤ۔ فَاعْلَمُوْٓا اَنَّمَآ اُنْزِلَ بِعِلْمِ اللّٰهِ " تو جان لو کہ یہ اللہ کے علم سے نازل ہوئی ہے "۔ صرف اللہ ہی اس بات پر قدرت رکھتا ہے کہ وہ اسے نازل کرے۔ اور صرف اللہ کا علم ہی اس انداز میں اس کتاب کو نازل کرسکتا ہے اور اس قسم کے علوم اور دلائل دے سکتا ہے۔ اور کائنات کے احوال اور سنن بیان کرسکتا ہے اور انسانوں کے ماضی اور حال اور مستقبل کے لیے وہی یہ سب کچھ وضع کرسکتا ہے جو ان کی ذات و معاش کے لیے مفید ہے۔ وَاَنْ لَّآ اِلٰهَ اِلَّا هُوَ " اور یہ کہ اللہ کے سوا کوئی حقیقی معبود نہیں ہے " اگر ان کے الہ دس سورتیں نہیں لا سکتے تو پھر اس کا لازمی نتیجہ ہے کہ یہ کتاب منجانب اللہ ہے اور وہ جو دعوت دیتی ہے کہ اللہ کے سوا کوئی الہ نہیں وہ برحق ہے اور یہ کتاب اسی نے نازل کی ہے۔ فَهَلْ اَنْتُمْ مُّسْلِمُوْنَ " تو کیا تم سر تسلیم خم کرتے ہو " اس چیلنج اور پوری دنیا کی ناکامی کے بعد آخر کوئی معقول آدمی دعوت اسلامی کو تسلیم کرنے کے سوا اور کر کیا سکتا ہے ؟ لیکن یہ لوگ اس قدر ظالم ہیں کہ یہ اس عاجزی کے بعد بھی حق کا انکار کرتے ہیں۔ حقیقت یہ ہے کہ ان لوگوں پر سچائی تو بالکل واضح ہوگئی تھی لیکن اسلامی نظام کی وجہ سے ان کے مفادات ختم ہو رہے تھے۔ موجودہ جاہلی نظام ان کے لیے مفید تھا ، پھر اس نظام میں ان کو اقتدار اور سلطنت حاصل تھی۔ دعوت اسلامی تو آزادی ، انصاف اور ہر انسان کو عزت اور شرف عطا کرنے کی تحریک تھی۔ اور ان لوگوں نے دوسرے لوگوں کو اپنا غلام بنا رکھا تھا۔ لہذا لا الہ الا اللہ کی دعوت ان کے مفادات کے خلاف تھی۔ چناچہ اب (اگلی آیات میں) ان کے حسب حال یہ تبصرہ کیا جتا ہے جو ان کے حالات کی صحیح تصویر کشی کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(11,15,'11:15','جو لوگ بس اس دنیاوی زندگی اور اس کی خوشنمائیوں کے طالب ہوتے ہیں ان کی اکر گزاری کا سارا پھل ہم یہیں ان کو دے دیتے ہیں اور اس میں ان کے ساتھ کوئی کمی نہیں کی جاتی');
INSERT INTO "sayyid_qutb" VALUES(11,16,'11:16','مگر آخرت میں ایسے لوگوں کے لیے آگ کے سوا کچھ نہیں ہے۔ (وہاں معلوم ہوجائے گا کہ) جو کچھ انہوں ندے دنیا میں بنایا وہ سب ملیا میٹ ہوگیا اور اب ان کا سارا کیا دھرا محض باطل ہے');
INSERT INTO "sayyid_qutb" VALUES(11,17,'11:17','پھر بھلا وہ شخص جو اپنے رب کی طرف سے ایک صاف شہادت رکھتا تھا ، اس کے بعد ایک گواہ بھی پروردگار کی طرف سے (اس شہادت کی تائید میں) آگیا ، اور پہلے موسیٰ کی کتاب رہنما اور رحمت کے طور پر آئی ہوئی بھی موجود تھی (کیا وہ بھی دنیا پرستوں کی طرح اس سے انکار کرسکتا ہے ؟ ) ایسے لوگ تو اس پر ایمان ہی لائیں گے اور انسانی گروہوں میں سے جو کوئی اس کا انکار کرے تو اس کے لیے جس جگہ کو وعدہ ہے وہ دوزخ ہے۔ پس اے پیغمبر تم اس چیز کی طرف سے کسی شک میں نہ پڑنا ، یہ حق ہے تمہارے رب کی طرف سے مگر اکثر لوگ نہیں مانتے');
INSERT INTO "sayyid_qutb" VALUES(11,18,'11:18','اور اس شخص سے بڑھ کر ظالم اور کون ہوگا جو اللہ پر جھوٹ گھڑے ؟ ایسے لوگ اپنے رب کے حضور پیش ہوں گے اور گواہ شہادت دیں گے کہ یہ ہیں وہ لوگ جنہوں نے اپنے رب پر جھوٹ گھڑا تھا۔ سنو ! خدا کی لعنت ہے ظالموں پر');
INSERT INTO "sayyid_qutb" VALUES(11,19,'11:19','ان ظالموں پر جو خدا کے راستے سے لوگوں کو روکتے ہیں ، اس کے راستے کو ٹیڑھا کرنا چاہتے ہیں ، اور آخرت کا انکار کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,20,'11:20','وہ زمین میں اللہ کو بےبس کرنے والے نہ تھے اور نہ اللہ کے مقابلہ میں کوئی ان کا حامی تھا۔ انہیں اب دوہرا عذاب دیا جائے گا۔ وہ نہ کسی کی سن ہی سکتے تھے اور نہ خود ہی انہیں کچھ سوجھتا تھا');
INSERT INTO "sayyid_qutb" VALUES(11,21,'11:21','یہ وہ لوگ ہیں جنہوں نے اپنے آپ کو خود گھاٹے میں ڈالا اور وہ سب کچھ ان سے کھویا گیا جو انہوں نے گھڑ رکھا تھا');
INSERT INTO "sayyid_qutb" VALUES(11,22,'11:22','ناگزیر ہے کہ وہی آخرت میں سب سے بڑھ کر گھاٹے میں رہیں');
INSERT INTO "sayyid_qutb" VALUES(11,23,'11:23','رہے وہ لوگ جو ایمان لائے اور جنہوں نے نیک عمل کیے اور اپنے رب ہی کے ہو کر رہے ، تو یقیناً وہ جنتی لوگ ہیں اور جنت میں وہ ہمیشہ رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(11,24,'11:24','ان دونوں فریقوں کی مثال ایسی ہے جیسے ایک آدمی تو ہو اندھا بہرا اور دوسرا ہو دیکھنے اور سننے والا ، کیا یہ دونوں یکساں ہوسکتے ہیں ؟ کیا تم (اس مثال سے) کوئی سبق نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(11,25,'11:25','(اور ایسے ہی حالات تھے جب) ہم نے نوح کو اس کی قوم کی طرف بھیجا تھا۔ (اس نے کہا) " میں تم لوگوں کو صاف صاف خبردار کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,26,'11:26','کہ اللہ کے سوا کسی کی بندگی نہ کرو ورنہ مجھے اندیشہ ہے کہ تم پر ایک روز دردناک عذاب آئے گا');
INSERT INTO "sayyid_qutb" VALUES(11,27,'11:27','جواب میں اس کی قوم کے سردار ، جنہوں نے اس کی بات ماننے سے انکار کیا تھا ، بولے : ہماری نظر میں تو تم اس کے سوا کچھ نہیں ہو کہ بس ایک انسان ہو ہم جیسے۔ اور ہم دیکھ رہے ہیں کہ ہماری قوم میں سے بس ان لوگوں نے جو ہمارے ہاں ارذل تھے ، بےسوچے سمجھے تمہاری پیروی اختیار کرلی ہے۔ اور ہم کوئی چیز بھی ایسی نہیں پاتے جس میں تم لوگ ہم سے کچھ بڑھے ہوئے ہو ، بلکہ ہم تو تمہیں جھوٹا سمجھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,28,'11:28','اس نے کہا " اے برادران قوم ، ذرا سوچو تو سہی کہ اگر میں اپنے رب کی طرف سے ایک کھلی شہادت پر قائم تھا اور پھر اس نے مجھ کو اپنی خاص رحمت سے بھی نواز دیا مگر وہ تم کو نظر نہ آئی تو آخر ہمارے پاس کیا ذریعہ ہے کہ تم ماننا نہ چاہو اور ہم زبردستی اس کو تمہارے سر چپک دیں ؟');
INSERT INTO "sayyid_qutb" VALUES(11,29,'11:29','اور برادران قوم ، میں اس کام پر تم سے کوئی مال نہیں مانگتا ، میرا اجر تو اللہ کے ذمہ ہے ، اور میں ان لوگوں کو دھکے دینے سے بھی رہا جنہوں نے میری بات مانی ہے ، وہ آپ ہی اپنے رب کے حور جانے والے ہیں۔ مگر میں دیکھتا ہوں کہ تم لوگ جہالت برت رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(11,30,'11:30','اور اے قوم ، اگر میں ان لوگوں کو دھتکار دوں تو خدا کی پکڑ سے کون مجھے بچانے آئے گا ؟ تم لوگوں کی سمجھ میں کیا اتنی بات بھی نہیں آتی ؟');
INSERT INTO "sayyid_qutb" VALUES(11,31,'11:31','اور میں تم سے نہیں کہتا کہ میرے پاس اللہ کے خزانے ہیں ، نہ یہ کہتا ہوں کہ میں غیب کا علم رکھت ہوں ، نہ یہ میرا دعوی ہے کہ میں فرشتہ ہوں اور یہ بھی میں نہیں کہہ سکتا کہ جن لوگوں کو تمہاری آنکھیں حقارت سے دیکھتی ہیں انہیں اللہ نے کوئی بھلائی نہیں دی ، ان کے نفس کا حال اللہ ہی بہتر جانتا ہے ، اگر میں ایسا کہوں تو ظالم ہوں گا');
INSERT INTO "sayyid_qutb" VALUES(11,32,'11:32','آخر کار ان لوگوں نے کہا کہ " اے نوح ، تم نے ہم سے جھگڑا کیا اور بہت کرلیا۔ اب تو بس وہ عذاب لے آؤ جس کی تم ہمیں دھمکی دیتے ہو ، اگر سچے ہو');
INSERT INTO "sayyid_qutb" VALUES(11,33,'11:33','نوح نے جواب دیا " وہ تو اللہ ہی لائے گا ، اگر چاہے گا ، اور تم اتنا بل بوتا نہیں رکھتے کہ اسے روک دو');
INSERT INTO "sayyid_qutb" VALUES(11,34,'11:34','اب اگر میں تمہاری کچھ خیر خواہی کرنا بھی چاہوں ، تو میری خیر خواہی تمہیں کوئی فائدہ نہیں دے سکتی جب کہ اللہ ہی نے تمہیں بھٹکا دینے کا ارادہ کرلیا ہو ، وہی تمہارا رب ہے اور اسی کی طرف تمہیں پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,35,'11:35','اے نبی کیا یہ لوگ کہتے ہیں کہ اس شخص نے یہ سب کچھ خود گھڑ لیا ہے ؟ ان سے کہو : " اگر میں نے یہ خود ھڑا ہے تو مجھ پر اپنے جرم کی ذمہ داری ہے ، اور جو جرم تم کر رہے ہو ، اس کی ذمہ داری سے میں بری ہوں "');
INSERT INTO "sayyid_qutb" VALUES(11,36,'11:36','نوح پر وحی کی گئی کہ تمہاری قوم میں سے جو لوگ ایمان لاچکے ، بس وہ لاچکے ، اب کوئی ماننے والا نہیں ہے۔ ان کے کرتوتوں پر غم کھانا چھوڑو');
INSERT INTO "sayyid_qutb" VALUES(11,37,'11:37','اور ہماری نگرانی میں ہماری وحی کے مطابق ایک کشتی بنانی شروع کردو۔ اور دیکھو جن لوگوں نے ظلم کیا ہے ان کے حق میں مجھ سے کوئی سفارش نہ کرنا ، یہ سارے کے سارے اب ڈوبنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,38,'11:38','نوح کشتی بنا رہا تھا اور اس کی قوم کے سرداروں میں سے جو کوئی اس کے پاس سے گزرتا تھا وہ اس کا مذاق اڑتا تھا۔ اس نے کہا " اگر تم ہم پر ہنستے ہو تو ہم بھی تم پر ہنس رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,39,'11:39','عنقریب تمہیں خود معلوم ہوجائے گا کہ کس پر وہ عذاب آتا ہے جو اسے رسوا کردے گا اور کس پر وہ بلا ٹوٹ پڑتی ہے جو ٹالے نہ ٹلے گی');
INSERT INTO "sayyid_qutb" VALUES(11,40,'11:40','یہاں تک کہ جب ہمارا حکم آگیا اور وہ تنور ابل پڑا تو ہم نے کہا " ہر قسم کے جانوروں کا ایک ایک جوڑا کشتی میں رکھ لو ، اپنے گھر والوں کو بھی۔ سوائے ان اشخاص کے جن کی نشاندہی پہلے کی جا چکی ہے۔ اس میں سوار کرا دو اور ان لوگوں کو بھی بٹھا لو جو ایمان لائے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,41,'11:41','نوح نے کہا سوار ہوجاؤ اس میں ، اللہ ہی کے نام سے ہے اس کا چلنا بھی اور اسکا ٹھہرنا بھی ، میرا رب بڑا غفور و رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(11,42,'11:42','کشتی ان لوگوں کو لیے چلی جا رہی تھی اور ایک ایک موج پہاڑ کی طرح اٹھ رہی تھی۔ نوح کا بیٹا دور فاصلے پر تھا۔ نوح نے پکار کر کہا بیٹا ہمارے ساتھ سوار ہوجا ، کافروں کے ساتھ نہ رہ');
INSERT INTO "sayyid_qutb" VALUES(11,43,'11:43','اس نے پلٹ کر جواب دیا میں ابھی ایک پہاڑ پر چڑھ جاتا ہوں جو مجھے پانی سے بچا لے گا۔ نوح نے کہا آج کوئی چیز اللہ کے حکم سے بچانے والی نہیں ہے سوائے اس کے کہ اللہ ہی کسی پر رحم فرمائے۔ اتنے میں ایک موج دونوں کے درمیان حائل ہوگئی اور وہ بھی ڈوبنے والوں میں شامل ہوگیا');
INSERT INTO "sayyid_qutb" VALUES(11,44,'11:44','حکم ہوا " اے زمین اپنا سارا پانی نگل جا اور اے آسمان رک جا " چناچہ پانی زمین میں بیٹھ گیا ، فیصلہ چکا دیا گیا ، کشتی جودی پر ٹک گئی اور کہہ دیا گیا کہ دور ہوئی ظالموں کی قوم "');
INSERT INTO "sayyid_qutb" VALUES(11,45,'11:45','نوح نے اپنے رب کو پکارا ، کہا " اے رب ، میرا بیٹا میرے گھر والوں میں سے ہے اور تیرا وعدہ سچا ہے اور تو سب حاکموں سے بڑا اور بہتر حاکم ہے "');
INSERT INTO "sayyid_qutb" VALUES(11,46,'11:46','جواب میں ارشاد ہوا " اے نوح وہ تیرے گھر والوں میں سے نہیں ہے ، وہ تو ایک بگڑا ہوا کام ہے ، لہذا تو اس بات کی مجھ سے درخواست نہ کر جس کی حقیقت نہیں جانتا ، میں تجھے نصیحت کرتا ہوں کہ اپنے آپ کو جاہلوں کی طرح نہ بنا لے "');
INSERT INTO "sayyid_qutb" VALUES(11,47,'11:47','توح نے فورا عرض کیا اے میرے رب ، میں تیری پناہ مانگتا ہوں ، اس سے کہ وہ چیز تجھ سے مانگوں جس کا مجھے علم نہیں۔ اگر تونے مجھے معاف نہ کیا اور رحم نہ فرمایا تو میں برباد ہوجاؤں گا');
INSERT INTO "sayyid_qutb" VALUES(11,48,'11:48','حکم ہوا اے نوح ، اتر جا ، ہماری طرف سے سلامتی اور برکتیں ہیں تجھ پر اور ان گروہوں پر جو تیرے ساتھ ہیں اور کچھ گروہ ایسے بھی ہیں جن کو ہم کچھ مدت سامان زندگی بخشیں گے ، پھر انہیں ہماری طرف سے دردناک عذاب پہنچے گا');
INSERT INTO "sayyid_qutb" VALUES(11,49,'11:49','اے نبی یہ غیب کی خبریں ہیں جو ہم تمہاری طرف وحی کر رہے ہیں۔ اس سے پہلے نہ تم ان کو جانتے تھے اور نہ تمہاری قوم۔ پس صبر کرو ، انجام کار متقیوں ہی کے حق میں ہے');
INSERT INTO "sayyid_qutb" VALUES(11,50,'11:50','اور عاد کی طرف ہم نے ان کے بھائی ہود کو بھیجا۔ اس نے کہا " اے برادران قوم ، اللہ کی بندگی کرو ، تمہارا کوئی خدا اس کے سوا نہیں ہے تم نے محض جھوٹ گھڑ رکھے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,51,'11:51','اے برادران قوم ، اس کام پر میں تم سے کوئی اجر نہیں چاہتا ، میرا اجر تو اس کے ذمے ہے جس نے مجھے پیدا کیا ہے ، کیا تم عقل سے ذرا کام نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(11,52,'11:52','اور اے میری قوم کے لوگو ، اپنے رب سے معافی چاہو ، پھر اس کی طرف پلٹو وہ تم پر آسمان کے دھانے کھول دے گا اور تمہاری موجودہ قوت پر مزید قوت کا اضافہ کرے گا ، تم مجرم بن کر بندگی سے منہ نہ پھیرو');
INSERT INTO "sayyid_qutb" VALUES(11,53,'11:53','انہوں نے (جواب میں) کہا اے ہود تو ہمارے پاس کوئی واضح دلیل لے کر نہیں آیا ، اور ہم تیرے کہنے سے اپنے معبودوں کو چھوڑنے والے نہیں اور نہ ہی ہم تجھ کو ماننے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,54,'11:54','ہم تو یہ سمجھتے ہیں کہ تیرے اوپر ہمارے معبودوں میں سے کسی کی مار پڑگئی ہے ہود نے کہا " میں اللہ کی شہادت پیش کرتا ہوں ، اور تم گواہرہو کہ یہ جو اللہ کے سوا دوسروں کو تم نے خدائی میں شریک ٹھہرا رکھا ہے ، اس سے میں بیزار ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,55,'11:55','تم سب کے سب مل کر میرے خلاف اپنی کرنی میں کسر نہ اٹھا رکھو ، اور مجھ کو ذرا مہلت نہ دو');
INSERT INTO "sayyid_qutb" VALUES(11,56,'11:56','میرا بھروسہ اللہ پر ہے جو میرا رب بھی ہے اور تمہارا رب بھی۔ کوئی جاندار ایسا نہیں جس کی چوٹی اس کے ہاتھ میں نہ ہو۔ بیشک میرا رب سیدھی راہ پر ہے');
INSERT INTO "sayyid_qutb" VALUES(11,57,'11:57','اگر تم منہ پھیرتے ہو تو پھیر لو ، جو پیغام دے کر میں تمہارے پاس بھیجا گیا تھا وہ میں تم کو پہنچا چکا ہوں۔ اب میرا رب تمہاری جگہ دوسری قوم اٹھائے گا ، اور تم اس کا کا کچھ بگاڑ نہ سکوگے ، یقینا میرا رب ہر چیز پر نگران ہے');
INSERT INTO "sayyid_qutb" VALUES(11,58,'11:58','اور پھر جب ہمارا حکم آگیا تو ہم نے اپنی رحمت سے ہود کو اور ان لوگوں کو جو اس کے ساتھ ایمان لائے تھے ، نجات دے دی اور ایک سخت عذاب سے انہیں بچا لیا');
INSERT INTO "sayyid_qutb" VALUES(11,59,'11:59','یہ ہیں عاد ، اپنے رب کی آیات سے انہوں نے انکار کیا اور اس کے رسولوں کی بات انہوں نے نہ مانی اور ہر جبار دشمن حق کی پیروی کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(11,60,'11:60','آخر کار اس دنیا میں بھی ان پر پھٹکار پڑی اور قیامت کے روز بھی۔ عاد نے اپنے رب سے کفر کیا۔ سنو دور پھینک دئیے گئے عاد ہود کی قوم کے لوگ');
INSERT INTO "sayyid_qutb" VALUES(11,61,'11:61','اور ثمود کی طرف ہم نے ان کے بھائی صالح کو بھیجا۔ اس نے کہا اے میری قوم کے لوگ ، اللہ کی بندگی کرو ، اس کے سوا تمہارا کوئی خدا نہیں ، وہی ہے جس نے تم کو زمین سے پیدا کیا ہے اور یہاں تم کو بسایا ہے۔ لہذا تم اس سے معافی چاہو اور اس کی طرف پلٹ آؤ، یقینا میرا رب قریب ہے اور وہ دعاؤں کا جواب دینے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,62,'11:62','“ انہوں نے کہا ” “ اے صالح ” اس سے پہلے تو ہمارے درمیان ایسا شخص تھا جس سے بڑی توقعات وابستہ تھیں کیا تو ان معبودوں کی پرستش سے روکنا چاہتا ہے جن کی پرستش ہمارے باپ دادا کرتے تھے ؟ تو جس طریقے کی طرف ہمیں بلا رہا ہے اس کے بارے میں ہم کو سخت شبہ ہے جس نے ہمیں خلجان میں ڈال رکھا ہے۔ ”');
INSERT INTO "sayyid_qutb" VALUES(11,63,'11:63','“ صالح (علیہ السلام) نے کہا “ اے برادران قوم ، تم نے کچھ اس بات پر بھی غور کیا کہ اگر میں اپنے رب کی طرف سے ایک صاف شہادت رکھتا تھا ، اور پھر اس نے اپنی رحمت سے بھی مجھ کو نواز دیا تو اس کے بعد اللہ کی پکڑ سے مجھے کون بچائے گا ، اگر میں اس کی نافرمانی کروں ؟ تم میرے کس کام آسکتے ہو ، سوائے اس کے کہ مجھے اور زیادہ خسارے میں ڈال دو ”۔');
INSERT INTO "sayyid_qutb" VALUES(11,64,'11:64','“ اور اے میری قوم کے لوگو ، دیکھو یہ اللہ کی اونٹنی تمہارے لیے ایک نشانی ہے۔ اسے خدا کی زمین میں چرنے کے لیے آزاد چھوڑ دو ، اس سے ذرا تعرض نہ کرنا ورنہ کچھ زیادہ دیر نہ گزرے گی کہ تم پر خدا کا عذاب آجائے گا۔ ”');
INSERT INTO "sayyid_qutb" VALUES(11,65,'11:65','“ مگر انہوں نے اونٹنی کو مار ڈالا۔ اس پر صالح (علیہ السلام) نے ان کو خبردار کردیا کہ “ بس اب تین دن پانے گھروں میں اور رہ لو۔ یہ ایسی معیاد ہے جو جھوٹی نہ ثابت ہو گی');
INSERT INTO "sayyid_qutb" VALUES(11,66,'11:66','“ آخر کار جب ہمارے فیصلے کا وقت آگیا تو ہم نے اپنی رحمت سے صالح (علیہ السلام) کو اور ان لوگوں کو جو اس کے ساتھ ایمان لائے تھے ، بچا لیا اور اس دن کے رسوائی سے ان کو محفوظ رکھا۔ بیشک تیرا رب ہی دراصل طاقتور اور بالا دست ہے ،');
INSERT INTO "sayyid_qutb" VALUES(11,67,'11:67','رہے وہ لوگ جنہوں نے ظلم کیا تھا تو ایک سخت دھماکے نے ان کو دھر لیا اور اپنی بستیوں میں بےحس و حرکت پڑے کے پڑے رہ گئے');
INSERT INTO "sayyid_qutb" VALUES(11,68,'11:68','گویا وہاں کبھی بسے ہی نہ تھے ’ سنو ، ثمود نے اپنے رب سے کفر کیا ۔ سنو ، دور پھینک دیئے گئے ثمود');
INSERT INTO "sayyid_qutb" VALUES(11,69,'11:69','اور دیکھو ، ابراھیم (علیہ السلام) کے پاس ہمارے فرشتے خوشخبری لیے ہوئے پہنچے۔ “ کہا تم پر سلام ہو۔ ابراہیم (علیہ السلام) نے جواب دیا تم پر بھی سلام ہو۔ پھر کچھ دیر نہ گزری کہ ابراہیم (علیہ السلام) ایک بھنا ہوا بچھڑا (ان کی ضیافت کے لیے ) لے آیا۔');
INSERT INTO "sayyid_qutb" VALUES(11,70,'11:70','مگر جب دیکھا کہ ان کے ہاتھ کھانے پر نہیں بڑھتے تو وہ ان سے مشتبہ ہوگیا اور دل میں ان سے خوف محسوس کرنے لگا ‘ انہوں نے کہا ، ڈرو نہیں ، ہم تو لوط کی قوم کی طرف بھیجے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,71,'11:71','ابراہیم (علیہ السلام) کی بیوی بھی کھڑی ہوئی تھی۔ وہ یہ سن کر ہنس دی۔ پھر ہم نے اس کو اسحٰق (علیہ السلام) کی اور اسحاق (علیہ السلام) کے بعد یعقوب (علیہ السلام) کی خوشخبری دی');
INSERT INTO "sayyid_qutb" VALUES(11,72,'11:72','“ وہ بولی ’ “ ہائے میری کم بختی ، کیا اب میرے ہاں اولاد ہوگی جب کہ میں بڑھیا پھونس ہوگئی اور میرے میاں بھی بوڑھے ہوچکے ؟ یہ تو بڑی عجیب بات ہے');
INSERT INTO "sayyid_qutb" VALUES(11,73,'11:73','فرشتوں نے کہا ، “ اللہ کے حکم پر تعجب کرتی ہو ؟ ابراہیم (علیہ السلام) کے گھر والو ، تم لوگوں پر اللہ کی رحمت اور اس کی برکتیں ہیں اور یقیناً اللہ نہایت قابل تعریف اور بڑی شان والا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,74,'11:74','پھر جب ابراہیم (علیہ السلام) کی گھبراہٹ دور ہوگئی اور (اولاد کی بشارت سے) اس کا دل خوش ہوگیا تو اس نے قوم لوط کے معاملہ میں ہم سے جھگڑا شروع کیا');
INSERT INTO "sayyid_qutb" VALUES(11,75,'11:75','حقیقت میں ابراہیم بڑا حلیم اور نرم دل تھے');
INSERT INTO "sayyid_qutb" VALUES(11,76,'11:76','(آخر کار ہمارے فرشتوں نے اس سے کہا) “ اے ابراہیم (علیہ السلام) اس سے باز آجاؤ، تمہارے رب کا حکم ہوچکا ہے اور اب ان لوگوں پر وہ عذاب آ کر رہے گا جو کسی کے پھیر سے نہیں پھر سکتا');
INSERT INTO "sayyid_qutb" VALUES(11,77,'11:77','اور جب ہمارے فرشتے لوط (علیہ السلام) کے پاس پہنچے تو ان کی آمد سے وہ بہت گھبرایا اور دل تنگ ہوا اور کہنے لگا کہ آج بڑی مصیبت کا دن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(11,78,'11:78','( ان مہمانوں کا آنا تھا کہ ) اس کی قوم کے لوگ بےاختیار اس کے گھر کی طرف دوڑ بڑے۔ پہلے سے وہ ایسی ہی بد کاریوں کے خوگر تھے۔ لوط (علیہ السلام) نے ان سے کہا ، “ بھائیو ، یہ میری بیٹیاں موجود ہیں ، یہ تمہارے لیے پاکیزہ تر ہیں۔ کچھ خدا کا خوف کرو اور میرے مہمانوں کے معاملہ میں مجھے ذلیل نہ کرو۔ کیا تم میں کوئی بھلا آدمی نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(11,79,'11:79','انہوں نے جواب دیا ، “ تجھے تو معلوم ہی ہے کہ تیری بیٹیوں میں ہمارا کوئی حصہ نہیں ہے اور یہ تو بھی جانتا ہے کہ ہم چاہتے کیا ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(11,80,'11:80','لوط (علیہ السلام) کاش میرے پاس اتنی طاقت ہوتی کہ تمہیں سیدھا کردیتا ، یا کوئی مضبوط سہارا ہی ہوتا کہ اس کی پناہ لیتا');
INSERT INTO "sayyid_qutb" VALUES(11,81,'11:81','تب فرشتوں نے اس سے کہ کہ “ اے لوط ، ہم تیرے رب کے بھیجے ہوئے فرشتے ہیں ، یہ لوگ تیرا کچھ نہ بگاڑ سکیں گے بس تو کچھ رات رہے اپنے اہل و عیال کو لے کر نکل جا اور دیکھو تم میں سے کوئی شخص پیچھے پلٹ کر نہ دیکھے۔ مگر تیری بیوی (ساتھ نہیں چلے گی) کیونکہ اس رپ بھی وہی کچھ گزرنے والا ہے جو ان لوگوں پر گزرنا ہے۔ ان کی تباہی کے لیے صبح کا وقت مقرر ہے۔ صبح ہوتے اب دیر ہی کتنی ہے');
INSERT INTO "sayyid_qutb" VALUES(11,82,'11:82','پھر جب ہمارے فیصلہ کا وقت آپہنچا تو ہم نے اس بستی کو تلپٹ کردیا اور اس پر پکی ہوئی مٹی کے پتھر تا بڑ توڑ برسائے');
INSERT INTO "sayyid_qutb" VALUES(11,83,'11:83','جن میں سے ہر پتھر تیرے رب کے ہاں نشان زدہ تھا اور ظالموں سے یہ سزا کچھ دور نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(11,84,'11:84','اور مدین والوں کی طرف ہم نے ان کے بھائی شعیب (علیہ السلام) کو بھیجا۔ اس نے کہا “ اے میری قوم کے لوگو ، اللہ کی بندگی کرو ، اس کے سوا تمہارا کوئی خدا نہیں ہے اور ناپ تول میں کمی نہ کیا کرو۔ آج میں تم کو اچھے حال میں دیکھ رہا ہوں ، مگر مجھے ڈر ہے کہ کل تم پر ایسا دن آئے گا جس کا عذاب سب کو گھیر لے گا');
INSERT INTO "sayyid_qutb" VALUES(11,85,'11:85','اور اے برادران قوم ، ٹھیک ٹھیک انصاف کے ساتھ پورا ناپو اور تولو اور لوگوں کو ان کی چیزوں میں گھاٹا نہ دیا کرو اور زمین میں فساد نہ پھیلاتے پھر و');
INSERT INTO "sayyid_qutb" VALUES(11,86,'11:86','اللہ کی دی ہوئی بچت تمہارے لیے بہتر ہے اگر تم مومن ہو اور بہرحال میں تمہارے اوپر کوئی نگران کار نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,87,'11:87','انہوں نے جواب دیا ، “ اے شعیب (علیہ السلام) کیا تیری نماز تجھے یہ سکھاتی ہے کہ ہم ان سارے معبودوں کو چھوڑ دیں جن کی پرستش ہمارے باپ دادا کرتے تھے ؟ یا یہ کہ ہم کو اپنے مال میں اپنے منشاء کے مطابق تصرف کرنے کا اختیار نہ ہو ؟ بس تو ہی تو ایک عالی ظرف اور راست باز آدمی رہ گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,88,'11:88','شعیب (علیہ السلام) نے کہا “ تم خود ہی سوچو کہ اگر میں اپنے رب کی طرف سے ایک کھلی شہادت پر تھا اور پھر اس نے مجھے اپنے ہاں سے اچھا رزق بھی عطا کیا (تو اس کے بعد میں تمہاری گمراہیوں اور حرام خوریوں میں تمہارا شریک حال کیسے ہو سکتا ہوں ؟ ) اور میں ہر گز یہ نہیں چاہتا کہ جن باتوں سے میں تم کو روکتا ہوں ان کا خود ارتکاب کروں۔ میں تو اصلاح کرنا چاہتا ہوں جہاں تک بھی مریا بس چلے اور یہ جو کچھ میں کرنا چاہتا ہوں اس کا سارا انحصار اللہ کی توفیق پر ہے ، اسی پر میں نے بھروسہ کیا اور ہر معاملہ میں اسی کی طرف میں رجوع کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,89,'11:89','اور اے برادرن قوم ، میرے خلاف تمہاری ہٹ دھرمی کہیں یہ نوبت نہ پہنچا دے کہ آخر کار تم پر بھی وہی عذاب آ کر رہے جو نوح (علیہ السلام) یا صالح (علیہ السلام) کی قوم پر آیا تھا۔ اور لوط (علیہ السلام) کی قوم تو تم سے کچھ زیادہ دور بھی نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(11,90,'11:90','دیکھو ، اپنے رب سے معافی مانگو اور اس کی طرف پل آؤ، بیشک میرا رب رحیم ہے اور اپنی مخلوق سے محبت رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,91,'11:91','انہوں نے جواب دیا “ اے شعیب (علیہ السلام) تیری بہت سی باتیں تو ہماری سمجھ ہی میں نہیں آتیں اور ہم دیکھتے ہیں کہ تو ہمارے درمیان ایک بےزور آدمی ہے ، تیری برادری نہ ہوتی تو ہم کبھی کا تجھے سنگسار کرچکے ہوتے ، تیرا بل بوتا تو اتنا نہیں ہے کہ ہم پر بھاری ہو');
INSERT INTO "sayyid_qutb" VALUES(11,92,'11:92','شعیب (علیہ السلام) نے کہا “ بھائیو ، کیا میری براداری تم پر اللہ سے زیادہ بھاری ہے کہ تم نے (برادری کا تو خوف کیا اور ) اللہ کو بالکل پس پشت ڈال دیا ؟ جان رکھو کہ جو کچھ تم کر رہے ہو وہ اللہ کی گرفت سے باہر نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(11,93,'11:93','اے میری قوم کے لوگو ، تم اپنے طریقے پر کام کیے جاؤ اور میں اپنے طریقے پر کرتا رہوں گا ، جلدڈی ہی تمہیں معلوم ہو جاؤے گا کہ کس پر ذلت کا عذاب آتا ہے اور کون جھوٹا ہے۔ تم بھی انتظار کرو اور میں بھی تمہارے ساتھ چشم براہ ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,94,'11:94','آخر کار جب ہمارے فیصلے کا وقت آگیا تو ہم نے اپنی رحمت سے شعیب (علیہ السلام) اور اس کے ساتھ مومنوں کو بچا لیا اور جن لوگوں نے ظلم کیا تھا ، ان کو ایک سخت دھماکے نے ایسا پکڑا کہ وہ اپنی بستیوں میں بےحس و حرکت پڑے کے پڑے رہ گئے');
INSERT INTO "sayyid_qutb" VALUES(11,95,'11:95','گویا کبھی وہاں رہے بسے ہی نہ تھے۔ سنو ، میدن والے بھی دور پھینک دئیے گئے جس طرح ثمود پھینکے گئے تھے۔ ”');
INSERT INTO "sayyid_qutb" VALUES(11,96,'11:96','اور موسیٰ ” کو ہم نے اپنی نشانیوں اور کھلی سند ماموریت کے ساتھ');
INSERT INTO "sayyid_qutb" VALUES(11,97,'11:97','فرعون اور اس کے اعیان سلطنت کی طرف بھیجا مگر انہوں نے فرعون کے حکم کی پیروی کی ، حالانکہ فرعون کا حکم راستی پر نہ تھا');
INSERT INTO "sayyid_qutb" VALUES(11,98,'11:98','قیامت کے روز وہ اپنی قوم کے آگے آگے ہوگا اور اپنی پیشوائی میں انہیں دوزخ کی طرف لے جائے گا۔ کیسی بدتر جائے ورود ہے یہ جس پر کوئی پہنچے');
INSERT INTO "sayyid_qutb" VALUES(11,99,'11:99','اور ان لوگوں پر دنیا میں بھی لعنت پڑی اور قیامت کے روز بھی پڑے گی۔ کیسا برا صلہ ہے جو کسی کو ملے');
INSERT INTO "sayyid_qutb" VALUES(11,100,'11:100','یہ چند بستیوں کی سرگزشت ہے جو ہم تمہیں سنا رہے ہیں ، ان میں سے بعض اب بھی کھڑی ہیں اور بعض کی فصل کٹ چکی ہے');
INSERT INTO "sayyid_qutb" VALUES(11,101,'11:101','ہم نے ان پر ظلم نہیں کیا ، انہوں نے آپ اپنے اوپر ظلم کیا ، اور جب اللہ کا حکم آگیا تو ان کے وہ معبود جنہیں وہ اللہ کو چھوڑ کر پکارا کرتے تھے ، ان کے کچھ کام نہ آسکے اور انہوں نے ہلاکت و بربادی کے سوا انہیں کچھ فائدہ نہ دیا');
INSERT INTO "sayyid_qutb" VALUES(11,102,'11:102','اور تیرا رب جب کسی ظالم بستی کو پکڑتا ہے تو پھر اس کی پکڑ ایسی ہوتے ہے۔ فی الواقعہ اس کی پکڑ سخت اور دردناک ہوتی ہے');
INSERT INTO "sayyid_qutb" VALUES(11,103,'11:103','حقیقت یہ ہے کہ اس میں ایک نشانی ہے ، ہر اس شخص کے لیے جو عذاب آخرت کا خوف کرے۔ وہ ایک دن ہوگا جس میں سب لوگ جع ہوں گے اور پھر جو کچھ بھی اس ورز ہوگا سب کی آنکھوں کے سامنے ہوگا');
INSERT INTO "sayyid_qutb" VALUES(11,104,'11:104','ہم اس کے لانے میں کچھ زیادہ تاخیر نہیں کر رہے ہیں بس ایک گنی چنی مدت اسکے لیے مقرر ہے');
INSERT INTO "sayyid_qutb" VALUES(11,105,'11:105','جب وہ آئے گا تو کسی کو بات کرنے کی مجال نہ ہوگی الا یہ کہ خدا کی اجازت سے کچھ عرض کرے۔ پھر کچھ لوگ اس دن بدبخت ہوں گے اور کچھ نیک بخت');
INSERT INTO "sayyid_qutb" VALUES(11,106,'11:106','جو بدبخت ہ . ں گے وہ دوزخ میں جائیں گے ، جہاں وہ ہانپیں گے اور پھنکارے ماریں گے');
INSERT INTO "sayyid_qutb" VALUES(11,107,'11:107','اور اسی حالت میں وہ ہمیشہ رہیں گے ، جب تک کہ زمین و آسمان قائم ہیں الا یہ کہ تیرا رب کچھ اور چاہے۔ بیشک تیرا ربت پورا اختیار رکھتا ہے کہ جو چاہے ، کرے');
INSERT INTO "sayyid_qutb" VALUES(11,108,'11:108','رہے وہ لوگ جو نیک بخت نکلیں گے تو وہ جنت میں جائیں گے اور وہاں ہمیشہ ریھ گے جب تک زمین و آسمان قائم ہیں الا یہ کہ تیرا رب کجھ اور چٔہے۔ ایسی بخشش ان کو ملے گی کا سلسلہ کبھی منقطع نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(11,109,'11:109','پس اے نبی صلى الله عليه وسلم تو ان معبودوں کی طرف سے کسی شک میں نہ رہ ، جن کی یہ لوگ عبادت کر رہے ہیں۔ یہ تو (بس لکیر کے فقیر بنے ہوئے) اسی طرح پوجا پاٹ کیے جا رہے ہیں جس طرح پہلے ان کے باپ دادا کرتے تھے اور ہم ان کا حصہ انہیں بھر پور دیں گے بغیر اس کے کہ اس میں کچھ کاٹ کسر ہو');
INSERT INTO "sayyid_qutb" VALUES(11,110,'11:110','ہم اس سے پہلے موسیٰ ٰ (علیہ السلام) کو کتاب دے چکے ہیں اور اس کے بارے میں بھی اختلاف کیا گیا تھا (جس طرح آج اس کتاب کے بارے میں کیا جا رہا ہے جو تمہیں دی گئی ہے) اگر تیرے رب کی طرف سے ایک بات پہلے ہی طے نہ کردی گئی ہوتی تو ان اختلاف کرنے والوں کے درمیان کبھی کا فیصلہ چکا دیا ہوتا یہ واقعہ ہے کہ یہ لوگ اس کی طرف سے شک اور خلجان میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,111,'11:111','اور یہ بھی واقعہ ہے کہ تیر ارب انہیں ان کے اعمال کا پورا پورا بدلہ دے کر رہے گا ، یقینا وہ ان کی سب حرکتوں سے باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(11,112,'11:112','پس اے نبی صلى الله عليه وسلم تم اور تمہارے وہ ساتھی جو (کفر و بغاوت سے ایمان و اطاعت کی طرف) پلٹ آئے ہیں ، ٹھیک ٹھیک راہ راست پر ثابت قدم رہوں جیسا کہ تمہیں حکم دیا گیا ہے اور بندگی کی حد سے تجاوز نہ کرو ، جو کچھ تم کر رہے ہو ، اس پر تمہارا رب نگاہ رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(11,113,'11:113','ان ظالموں کی طرف ذرا نہ جھکنا ، ورنہ جہنم کی لپیٹ میں آجاؤ گے اور تمہیں کوئی ایسا ولی و سرپرست نہ ملے گا جو خدا سے تمہیں بچا سکے اور کہیں سے تم کو مدد نہ پہنچے گی');
INSERT INTO "sayyid_qutb" VALUES(11,114,'11:114','اور دیکھو ، نماز قائم کرودن کے دونوں سروں پر اور کچھ رات گزرنے پر۔ درحقیقت نیکیاں برائیوں کو دور کردیتی ہیں۔ یہ ایک یاد دہانی ہے ان لوگوں کے لیے جو خدا کو یاد رکھنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,115,'11:115','اور صبر کرو اللہ نیکی کرنے والوں کا اجر کبھی ضائع نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(11,116,'11:116','پھر کیوں نہ ان قوموں میں جو تم سے پہلے گزر چکی ہیں ایسے اہل خیر موجود رہے جو لوگوں کو زمین میں فساد برپا کرنے سے روکتے ؟ ایسے لوگ نکلے بھی تو بہت کم ، جن کو ہم نے ان قوموں میں سے بچا لیا ورنہ ظالم لوگ تو انہی مزوں کے پیچھے پڑے رہے جن کے سامان انہیں فراوانی کے ساتھ دیئے گئے تھے اور وہ مجرم بن کر رہے');
INSERT INTO "sayyid_qutb" VALUES(11,117,'11:117','تیرا رب ایسا نہیں ہے کہ بستیوں کو ناحق تباہ کر دے حالانکہ ان کے باشندے اصلاح کرنے والے ہوں');
INSERT INTO "sayyid_qutb" VALUES(11,118,'11:118','بیشک تیرا رب اگر چاہتا تو تمام انسانوں کو ایک گروہ بنا سکتا تھا ، مگر اب تو وہ مختلف طریقوں ہی پر چلتے رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(11,119,'11:119','اور بےراہ رویوں سے صرف وہ لوگ بچیں گے جن پر تیرے رب کی رحمت ہے۔ اسی (آزادی انتخاب و اختیار اور امتحان) کے لیے تو اس نے انہیں پیدا کیا تھا۔ اور تیرے رب کی وہ بات پوری ہوگئی جو اس نے کی تھی کہ میں جہنم کو جنوں اور انسانوں سب سے بھر دوں گا');
INSERT INTO "sayyid_qutb" VALUES(11,120,'11:120','اور اے نبی یہ پیغمبروں کے قصے جو ہم تمہیں سناتے ہیں ، یہ وہ چیزیں ہیں جن کے ذریعہ سے ہم تمہارے دل کو محضبوط کرتے ہیں ۔ ان کے اندر تم کو حقیقت کا علم ملا اور ایمان لانے والوں کو نصیحت اور بیداری نصیب ہوئی ہے');
INSERT INTO "sayyid_qutb" VALUES(11,121,'11:121','وہ لوگ جو ایمان نہیں لاتے ، تو ان سے کہہ دو کہ تم اپنے طریقے پر کام کرتے رہو اور ہم اپنے طریقے پر کیے جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,122,'11:122','انجام کار کا تم بھی انتظار کرو اور ہم بھی منتظر ہیں');
INSERT INTO "sayyid_qutb" VALUES(11,123,'11:123','آسمانوں اور زمین میں جو کچھ چھپا ہوا ہے سب اللہ کے قبضہ قدرت میں ہے اور سارا معاملہ اسی کی طرف رجوع کیا جاتا ہے ۔ پس اے نبی صلى الله عليه وسلم تو اس کی بندگی کر اور اسی پر بھروسہ رکھ ، جو کچھ تم لوگ کررہے ہو ، تیرا رب اس سے بیخبر نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(12,1,'12:1','ا ل ر۔ یہ اس کتاب کی آیات ہیں جو اپنا مدعا صاف صاف بیان کرتی ہے');
INSERT INTO "sayyid_qutb" VALUES(12,2,'12:2','ہم نے اسے نازل کیا ہے ، قرآن بنا کر ، عربی زبان میں تاکہ تم اسے اچھی طرح سمجھ سکو ،');
INSERT INTO "sayyid_qutb" VALUES(12,3,'12:3','اے محمد ہم اس قرآن کو تمہاری طرف وحی کرکے بہترین پیرائے میں واقعات و حقائق تم سے بیان کرتے ہیں ورنہ اس سے پہلے تو تم بالکل بیخبر تھے');
INSERT INTO "sayyid_qutb" VALUES(12,4,'12:4','یہ اس وقت کا ذکر ہے جب یوسف نے اپنے باپ سے کہا اباجان میں نے خواب میں دیکھا ہے کہ گیا ستارے ہیں اور سورج اور چاند ہیں اور وہ مجھے سجدہ کر رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,5,'12:5','جواب میں اس کے باپ نے کہا بیٹآ اپنا یہ خواب اپنے بھائیوں کو نہ سنانا ورنہ وہ تیرے درپے آزار ہوجائیں گے ، حقیقت یہ ہے کہ شیطان آدمی کا کھلا دشمن ہے');
INSERT INTO "sayyid_qutb" VALUES(12,6,'12:6','اور ایسا ہی ہوگا ، تیرا رب تجھے منتخب کرے گا ، اور تجھے باتوں کی تہہ تک پہنچنا سکھائے گا اور تیرے اوپر اور آل یعقوب پر اپنی نعمت اسی طرح پوری کرے گا جس طرح اس سے پہلے وہ تیرے بزرگوں ابراہیم اور اسحاق پر کرچکا ہے ، یقینا تیرا رب علیم و حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(12,7,'12:7','حقیقت یہ ہے کہ یوسف اور اس کے بھائیوں کے قصے میں ان پوچھنے والوں کے لیے بڑی نشانیاں ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,8,'12:8','یہ قصہ یوں شروع ہوتا ہے کہ اس کے بھائیوں نے آپس میں کہا " یہ یوسف اور اس کا بھائی دونوں ہمارے والد کو ہم سب سے زیادہ محبوب ہیں ، حالانک کہ ہم پورا تجھا ہیں۔ سچی بات یہ ہے کہ ہمارے اباجان بالکل بہک گئے ہیں "');
INSERT INTO "sayyid_qutb" VALUES(12,9,'12:9','چلو یوسف کو قتل کردو یا اسے کہیں پھینک دو تاکہ تمہارے والد کی توجہ صرف تمہاری ہی طرف ہوجائے۔ یہ کام کرلینے کے بعد نیک بن رہنا۔');
INSERT INTO "sayyid_qutb" VALUES(12,10,'12:10','اس پر ان میں سے ایک بولا۔ یوسف کو قتل نہ کرو ، اگر کچھ کرنا ہی ہے تو اسے کسی اندھے کنویں میں ڈال دو ۔ کوئی آتا جاتا قافلہ اسے نکال لے جائے گا');
INSERT INTO "sayyid_qutb" VALUES(12,11,'12:11','اس قرار داد پر انہوں نے اپنے باپ سے کہا : ابا جان کیا بات ہے کہ آپ یوسف کے معاملے میں ہم پر بھروسہ نہیں کرتے حالانکہ ہم اس کے سچے خیر خواہ ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,12,'12:12','کل اسے ہمارے ساتھ بھیج دیجئے ، کچھ چر چگ لے گا ، اور کھیل کود سے بھی دل بہلائے گا۔ ہم اس کی حفاظت کو موجود ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,13,'12:13','باپ نے کہا تمہارا اسے لے جانا مجھے شاق گزرتا ہے اور مجھے اندیشہ ہے کہ کہیں اسے بھیڑیا نہ پھاڑ کھائے جبکہ تم اس سے غافل ہو۔');
INSERT INTO "sayyid_qutb" VALUES(12,14,'12:14','انہوں نے جواب دیا اگر ہمارے ہوتے اسے بھیڑئیے نے کھالی تو ہم بڑے نکمے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(12,15,'12:15','اس طرح اصرار کرکے جب وہ اسے لے گئے اور انہوں نے طے کرلیا کہ اسے ایک اندھے کنوئیں میں چھوڑ دیں تو ہم نے یوسف کو وحی کی کہ ایک وقت آئے گا جب تو ان لوگوں کو ان کی یہ حرکت جتائے گا یہ اپنے فعل کے نتائج سے بیخبر ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,16,'12:16','شام کو وہ روتے پیٹتے اپنے باپ کے پاس آئے');
INSERT INTO "sayyid_qutb" VALUES(12,17,'12:17','اور کہا " ابا جان ہم دوڑ کا مقابلہ کرنے لگ گئے تھے اور یوسف کو ہم نے اپنے سامان کے پاس چھوڑ دیا تھا کہ اتنے میں بھیڑیا آکر اسے کھا گیا۔ آ ہماری بات کا یقین نہ کریں گے چاہے ہم سچے ہی ہوں');
INSERT INTO "sayyid_qutb" VALUES(12,18,'12:18','اور وہ یوسف کی قمیص پر جھوٹ موٹ کا خون لگا کرلے غائے تھے۔ یہ سن کر ان کے باپ نے کہا " بلکہ تمہارے نفس نے تمہارے لیے ایک بڑے کام کو آسان بنا دیا۔ اچھا صبروں کروں گا اور بخوبی صبر کروں گا ، جو بات تم بنا رہے ہو ، اس پر اللہ ہی سے مدد مانگی جاسکتی ہے');
INSERT INTO "sayyid_qutb" VALUES(12,19,'12:19','ادھر ایک قافلہ آیا اور اس نے اپنے سقے کو پانی لانے کے لیے بھیجا۔ سقے نے جو کنوئیں میں ڈول ڈالا تو (یوسف کو دیکھ کر) پکار اٹھا " مبارک ہو یہاں تو ایک لڑکا ہے " ان لوگوں نے مال تجارت سمجھ کر چھپا لیا حالانکہ جو کچھ وہ کر رہے تھے خدا اس سے باخبر تھا۔');
INSERT INTO "sayyid_qutb" VALUES(12,20,'12:20','آخر کار انہوں نے تھوڑی سی قیمت پر چند درہموں کے عوض اسے بیچ ڈالا اور وہ اس کی قیمت کے معاملہ میں کچھ زیادہ کے امیدوار نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(12,21,'12:21','مصر کے جس شخص نے اسے خریدا اس نے اپنی بیوی سے کہا " اس کو اچھی طرح رکھنا ، بعید نہیں کہ یہ ہمارے مفید ثابت ہو یا ہم اسے بیٹا بنا لیں۔ اس طرح ہم نے یوسف کے لیے اس سرزمین میں قدم جمانے کی صورت نکالی اور اسے معاملہ فہمی کی تعلیم دینے کا انتظام کیا۔ اللہ اپنا کام کر کے رہتا ہے۔ مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,22,'12:22','اور جب وہ اپنی پوری جوانی کو پہنچا تو ہم نے اسے قوت فیصلہ اور علم عطا کیا ، اس طرح ہم نیک لوگوں کو جزا دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,23,'12:23','جس عورت کے گھر میں وہ تھا وہ اس پر ڈورے ڈالنے لگی اور ایک روز دروازے بند کرکے بولی " آ جا " یوسف نے کہا : " خدا کی پناہ ، میرے رب نے تو مجھے اچھی منزلت بخشی (اور میں یہ کام کروں گا) ایسے ظالم کبھی فلاح نہیں پایا کرتے "');
INSERT INTO "sayyid_qutb" VALUES(12,24,'12:24','وہ اس کی طرف بڑھی اور یوسف بھی اس کی طرف بڑھتا اگر اپنے رب کی برہان نہ دیکھ لیتا۔ ایسا ہوا ، تاکہ اہم اس سے بدی اور بےحیائی کو دور کردیں ، در حقیقت وہ ہمارے چنے ہوئے بندوں میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(12,25,'12:25','آخر کار یوسف اور وہ آگے پیچھے دروازے کی طرف بھاگے اور اس نے پیچھے سے یوسف کی قمیص (کھینچ کر) پھاڑ دی۔ دروازے پر دونوں نے اس کے شوہر کو موجود پایا۔ اسے دیکھتے ہی عورت کہنے لگی : " کیا سزا ہے اس شخص کی جو تیری گھر والی پر نیت خراب کرے ؟ اس کے سوا اور کیا سزا ہوسکتی ہے کہ وہ قید کیا جائے یا اسے سخت عذاب دیا جائے "');
INSERT INTO "sayyid_qutb" VALUES(12,26,'12:26','یوسف نے کہا " یہی مجھے پھانسنے کی کوشش کر رہی تھی " اس عورت کے اپنے کنبہ والوں میں سے ایک شخص نے (قرینے کی) شہادت پیش کی کہ " اگر یوسف کی قمیص آگے سے پھٹی ہو تو عورت سچی ہے اور یہ جھوٹا');
INSERT INTO "sayyid_qutb" VALUES(12,27,'12:27','اور اگر اس کی قمیص پیچھے سے پھٹی ہو تو عورت جھوٹی ہے اور یہ سچا');
INSERT INTO "sayyid_qutb" VALUES(12,28,'12:28','جب شوہر نے دیکھا کہ یوسف کی قمیص پیچھے سے پھٹی ہے تو اس نے کہا " یہ تم عورتوں کی چالاکیاں ہیں ، واقعی بڑے غضب کی ہوتی ہیں تمہاری چالیں');
INSERT INTO "sayyid_qutb" VALUES(12,29,'12:29','یوسف اس معاملے سے درگزر کر۔ اور اے عورت تو اپنے قصور کی معافی مانگ ، تو ہی اصل میں خطا کار تھی');
INSERT INTO "sayyid_qutb" VALUES(12,30,'12:30','شہر کی عورتیں آپس میں چرچا کرنے لگیں کہ عزیز کی بیوی اپنے نوجوان غلام کے پیچھے پڑی ہوئی ہے ، محبت نے اس کو بےقابو کر رکھا ہے ، ہمارے نزدیک تو وہ صریح غلطی کر رہی ہے');
INSERT INTO "sayyid_qutb" VALUES(12,31,'12:31','اس نے جو ان کی یہ مکارانہ باتیں سنیں تو ان کو بلاوا بھیج دیا اور ان کے لیے تکیہ دار مجلس آراستہ کی اور ضیافت میں ہر ایک کے آگے ایک چھری رکھ دی (پھر عین اس وقت جبکہ وہ پھل کاٹ کاٹ کر کھا رہی تھیں) اس نے یوسف کو اشارہ کیا کہ ان کے سامنے نکل آجب ان عورتوں کی نگاہ اس پر پڑی تو وہ دنگ رہ گئیں اور اپنے ہاتھ کاٹ بیٹھیں اور بےساختہ پکار اٹھیں " حاشا للہ " یہ شخص انسان نہیں ہے ، یہ تو کوئی بزرگ فرشتہ ہے');
INSERT INTO "sayyid_qutb" VALUES(12,32,'12:32','عزیز کی بیوی نے کہا " دیکھ لیا ، یہ ہے وہ شخص جس کے معاملہ میں تم مجھ پر باتیں بناتی تھیں۔ بیشک میں نے اسے رجھانے کی کوشش کی تھی مگر یہ بچ نکلا ، اگر یہ میرا کہنا نہ مانے گا تو قید کیا جائے گا اور بہت ذلیل و خوار ہوگا "');
INSERT INTO "sayyid_qutb" VALUES(12,33,'12:33','یوسف نے کہا " اے میرے رب ! قید مجھے منظور ہے بہ نسبت اس کے کہ میں وہ کام کروں جو یہ مجھ سے چاہتی ہیں اور اگر تونے ان کی چالوں کو مجھ سے دفع نہ کیا تو میں ان کے دام میں پھنس جاؤں گا اور جاہلوں میں شامل ہو رہوں گا');
INSERT INTO "sayyid_qutb" VALUES(12,34,'12:34','اس کے رب نے اس کی دعا قبول کی اور ان عورتوں کی چالیں اس سے دفع کردیں ، بیشک وہی ہے جو سب کی سنتا اور سب کچھ جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(12,35,'12:35','پھر ان لوگوں کو یہ سوجھی کہ ایک مدت کے لیے اسے قید کردیں حالانکہ وہ اس (اس کی پاک دامنی اور خودا پنی عورتوں کے برے اطوار کی) صریح نشانیاں دیکھ چکے تھے');
INSERT INTO "sayyid_qutb" VALUES(12,36,'12:36','قید خانے میں دو غلام اور بھی اس کے ساتھ داخل ہوئے ، ایک روز ان میں سے ایک نے کہا میں نے خواب دیکھا ہے کہ میں شراب کشید کر رہا ہوں دوسرے نے کہا " میں نے دیکھا کہ میرے سر پر روٹیاں رکھی ہیں اور پرندے ان کو کھا رہے ہیں " دونوں نے کہا ہمیں اس کی تعبیر بتائیے ، ہم دیکھتے ہیں کہ آپ ایک نیک آدمی ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,37,'12:37','یوسف نے کہا یہاں جو کھانا تمہیں ملا کرتا ہے اس کے آنے سے پہلے میں تمہیں ان خوابوں کی تعبیر بتا دوں گا۔ یہ ان علوم میں سے ہے جو میرے رب نے مجھ عطا کیے ہیں۔ واقعہ یہ ہے کہ میں نے ان لوگوں کا طریقہ چھوڑ کر ، جو اللہ پر ایمان نہیں لاتے اور آخرت کا انکار کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,38,'12:38','میں نے اپنے بزرگوں ابراہیم ، اسحاق ، اور یعقوب کا طریقہ اختیار کیا ہے۔ ہمارا یہ کام نہیں ہے کہ اللہ کے سوا کسی کو شریک ٹھہرائیں ، در حقیقت یہ اللہ کا فضل ہے ہم پر اور تمام انسانوں پر (کہ اس نے اپنے سوا کسی کا بندہ ہمیں نہیں بنایا) مگر اکثر لوگ شکر نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(12,39,'12:39','اے زندان کے ساتھیو ، تم خود ہی سوچو کہ بہت سے متفرق رب بہتر ہیں یا وہ ایک اللہ جو سب پر غالب ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(12,40,'12:40','اس کو چھوڑ کر تم جن کی بندگی کر رہے ہو ، وہ اس کے سوا کچھ نہیں ہیں کہ بس چند نام ہیں جو تم نے اور تمہارے آباء و اجداد نے رکھ لیے ہیں ، اللہ نے ان کے لیے کوئی سند نازل نہیں کی۔ فرمانروائی کا اقتدار اللہ کے سوا کسی کے لیے نہیں ہے۔ اس کا حکم ہے کہ خود اس کے سوا تم کسی کی بندگی نہ کرو ، یہی ٹھیٹھ سیدھا طریق زندگی ہے ، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(12,41,'12:41','اے زنداں کے ساتھیو ، تمہارے خواب کی تعبیر یہ ہے کہ تم میں سے ایک تو اپنے رب (شاہ مصر) کو شراب پلائے گا ، رہا دوسرا تو اسے سولی پر چڑھا جائے گا اور پرندے اس کا سر نوچ نوچ کر کھائیں گے۔ فیصلہ ہوگیا اس بات کا جو تم پوچھ رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(12,42,'12:42','پھر ان میں سے جس کے متعلق خیال تھا کہ وہ رہا ہوجائے گا اس سے یوسف نے کہا کہ " اپنے رب (شاہ مصر) سے میرا ذکر کرنا مگ شیطان نے اسے ایسا غفلت میں ڈالا کہ وہ اپنے رب (شاہ مصر) سے اس کا ذکر کرنا بھول گیا اور یوسف کئی سال قید خانے میں پڑے رہے');
INSERT INTO "sayyid_qutb" VALUES(12,43,'12:43','ایک روز بادشاہ نے کہا " میں نے خواب میں دیکھا ہے کہ سات موٹی گائیں ہیں جن کو سات دبلی گائیں کھا رہی ہیں اور اناج کی سات بالیں ہری ہیں اور دوسری سات سوکھی۔ اے اہل دربار مجھے اس خوب کی تعبیر بتاؤ اگر تم خوابوں کا مطلب سمجھتے ہو');
INSERT INTO "sayyid_qutb" VALUES(12,44,'12:44','لوگوں نے کہا۔ یہ تو پریشان خوابوں کی باتیں ہیں اور ہم اس طرح کے خوابوں کا مطلب نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(12,45,'12:45','ان دو قیدیوں میں سے جو شخص بچ گیا تھا اور اسے ایک مدت دراز کے بعد اب بات یاد آئی اس نے کہا " میں آپ حضرات کو اس کی تاویل بتاتا ہو ، مجھے ذرا (قید خانے میں یوسف کے پاس) بھیج دیجئے');
INSERT INTO "sayyid_qutb" VALUES(12,46,'12:46','اس نے جا کر کہا : " یوسف ! اے سراپا راستی ، مجھے اس خواب کا مطلب بتا کہ سات موٹی گائیں ہیں جن کو سات دبلی گائیں کھا رہی ہیں اور سات بالیں ہری ہیں اور سات سوکھی ، شاید کہ میں ان لوگوں کے پاس واپس جاؤں اور شاید کہ وہ جان لیں "');
INSERT INTO "sayyid_qutb" VALUES(12,47,'12:47','یوسف نے کہا سات برس تک لگاتار تم کھیتی باڑی کرتے رہوگے ، اس دوران میں جو فصلیں تم کاٹو ان میں سے بس تھوڑا سا حصہ ، جو تمہاری خوراک کے کام آئے ، نکالو اور باقی کو اس کی بالیوں ہی میں رہنے دو');
INSERT INTO "sayyid_qutb" VALUES(12,48,'12:48','پھر سات برس بہت سخت آئیں گے۔ اس مانے میں وہ سب غلہ کھالیا جائے گا جو تم اس برے وقت کے لیے جمع کرو گے۔ اگر کچھ بچے گا تو بس وہی جو تم نے محفوظ کر رکھا ہو');
INSERT INTO "sayyid_qutb" VALUES(12,49,'12:49','اس کے بعد پھر ایک سال ایسا آئے گا جس میں باران رحمت سے لوگوں کی فریاد رسی کی جائے گی اور وہ رس نچوڑیں گے');
INSERT INTO "sayyid_qutb" VALUES(12,50,'12:50','بادشاہ نے کہا اسے میرے پاس لاؤ مگر جب شاہی فرستادہ یوسف کے پاس پہنچا تو اس نے کہا اپنے رب کے پاس واپس جا اور اس سے پوچھ کہ ان عورتوں کا کیا معاملہ ہے جنہوں نے اپنے ہاتھ کاٹ لیے تھے ؟ میرا رب تو ان کی مکاری سے واقف ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(12,51,'12:51','اس پر بادشاہ نے ان عورتوں سے دریافت کیا تمہارا کیا تجربہ ہے اس وقت کا جب تم نے یوسف کو رجھانے کی کوشش کی تھی ؟ سب نے یک زبان ہوکر کہا " حاشا للہ " ہم نے تو اس میں بدی کا شائبہ تک نہ پایا عزیز کی بیوی بول اٹھی اب حق کھل چکا ہے ، وہ میں ہی تھی جس نے اس کو پھسلانے کی وکشش کی تھی ، بیشک وہ بالکل سچا ہے');
INSERT INTO "sayyid_qutb" VALUES(12,52,'12:52','یہ اس لیے کہ عزیز مصر جان لے کہ میں نے درپردہ اس کی خیانت نہیں کی اور یہ کہ جو خیانت کرتے ہیں ، اللہ ان کی چالوں کو کامیابی کی راہ پر نہیں ڈالتا');
INSERT INTO "sayyid_qutb" VALUES(12,53,'12:53','“ میں کچھ اپنے نفس کی براءت نہیں کرتی۔ نفس بدی پر اکساتا ہی ہے الا یہ کہ کسی پر میرے رب کی رحمت ہو ، بیشک میرا رب بڑا غفور و رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(12,54,'12:54','بادشاہ نے کہا “ انہیں میرے پاس لاؤ تا کہ میں ان کو اپنے لئے مخصوص کرلوں “۔ جب یوسف نے اس سے گفتگو کی تو اس نے کہا “ اب آپ ہمارے ہاں قدرو منزلت رکھتے ہیں اور ہمیں آپ کی امانت پر پورا بھروسہ ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,55,'12:55','یوسف (علیہ السلام) نے کہا “ ملک کے خزانے میرے سپرد کیجئے میں حفاظت کرنے والا بھی ہوں اور علم بھی رکھتا ہوں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,56,'12:56','اس طرح ہم نے اس سر زمین میں یوسف کے لئے اقتدار کی راہ ہموار کی۔ وہ مختار تھا کہ اس میں جہاں چاہے اپنی جگہ بنائے۔ ہم اپنی رحمت سے جو کو چاہتے ہیں ، نوازتے ہیں ، نیک لوگوں کا اجر ہمارے ہاں مارا نہیں جاتا ،');
INSERT INTO "sayyid_qutb" VALUES(12,57,'12:57','اور آخرت کا اجر ان لوگوں کے لئے زیادہ بہتر ہے جو ایمان لے آئے اور خدا ترسی کے ساتھ کام کرتے رہے ”');
INSERT INTO "sayyid_qutb" VALUES(12,58,'12:58','“ یوسف (علیہ السلام) کے بھائی مصر آئے اور اس کے ہاں حاضر ہوئے۔ اس نے انہیں پہچان لیا مگر وہ اس سے نا آشنا تھے۔');
INSERT INTO "sayyid_qutb" VALUES(12,59,'12:59','پھر جب اس نے ان کا سامان تیار کروادیا تو چلتے وقت ان سے کہا “ اپنے سوتیلے بھائی کو میرے پاس لانا۔ دیکھتے نہیں ہو کہ میں کس طرح نہ بھر کردیتا ہوں اور کیسا اچھا مہمان نواز ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(12,60,'12:60','اگر تم اسے نہ لاؤ گے تو میرے پاس تمہارے لیے کوئی غلہ نہیں ہے ۔ بلکہ تم میرے قریب بھی نہ پھٹکنا ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,61,'12:61','انہوں نے کہا “ ہم کوشش کریں گے کہ والد صاحب اسے بھیجنے پر راضی ہوجائیں ، اور ہم ایسا ضرور کریں گے ”');
INSERT INTO "sayyid_qutb" VALUES(12,62,'12:62','یوسف (علیہ السلام) نے اپنے غلاموں کو اشارہ کیا کہ “ ان لوگوں نے غلے کے عوض جو مال دیا ہے وہ چپکے سے ان کے سامان ہی میں رکھ دو ”۔ یہ یوسف (علیہ السلام) نے اس امیر پر کیا کہ گھر پہنچ کر وہ اپنا واپس پایا ہوا مال پہچان جائیں گے (یا اس فیاضی پر احسان مند ہوں گے ) اور عجب نہیں کہ پھر پلٹیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,63,'12:63','“ جب وہ اپنے باپ کے پاس گئے تو کہا “ ابا جان ، آئندہ ہم کو غلہ دینے سے انکار کردیا گیا ہے ، لہٰذا آپ ہمارے بھائی کو ہمارے ساتھ بھیج دیجئے تا کہ ہم غلہ لے کر آئیں اور اس کی حفاظت کے ہم ذمہ دار ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,64,'12:64','باپ نے جواب دیا “ کیا میں اس کے معاملہ میں تم پر ویسا ہی بھروسہ کروں جیسا اس سے پہلے اس کے بھائی کے معاملہ میں کرچکا ہوں ؟ اللہ ہی بہتر محافظ ہے اور وہ سب سے بڑھ کر رحم فرمانے والا ہے ” ۔');
INSERT INTO "sayyid_qutb" VALUES(12,65,'12:65','پھر جب انہوں نے اپنا سامان کھولا تو دیکھا کہ ان کا مال بھی انہیں واپس کردیا گیا ہے۔ یہ دیکھ کر وہ پکار اٹھے “ ابا جان ، اور ہمیں کیا چاہئے ، دیکھئے یہ ہمارا مال بھی ہمیں واپس دے دیا گیا ہے۔ اب ہم جائیں گے اور اپنے اہل و عیال کے لئے رسد لے کر آئیں گے ، اپنے بھائی کی حفاظت بھی کریں گے اور ایک بار شتر اور زیادہ بھی لے آئیں گے ، اتنے غلہ کا اضافہ آسانی کے ساتھ ہوجائے گا ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,66,'12:66','ان کے باپ نے کہا “ میں اس کو ہرگز تمہارے ساتھ نہ بھیجوں گا۔ جب تک کہ تم اللہ کے نام سے مجھ کو نہ دے دو کہ اسے میرے پاس ضرور واپس لے کر آؤ گے الا یہ کہ تم گھیر ہی لیے جاؤ ”۔ جب انہوں نے اس کو اپنے اپنے ۔۔۔۔ دے دئیے تو اس نے کہا “ دیکھو ، ہمارے اس قول پر اللہ نگہبان ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,67,'12:67','پھر اس نے کہا “ میرے بچو ، مصر کے دار السلطنت میں ایک دروازے سے داخل نہ ہونا بلکہ مختلف دروازوں سے جانا۔ مگر میں اللہ کی مشیت سے تم کو نہیں بچا سکتا ، حکم اس کے سوا کسی کا بھی نہیں چلتا ، اسی پر میں نے بھروسہ کیا ، اور جس کو بھی بھروسہ کرنا ہو ، اسی پر کرے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,68,'12:68','“ اور واقعہ بھی یہی ہوا کہ جب وہ اپنے باپ کی ہدایت کے مطابق شہر میں ( متفرق دروازوں سے) داخل ہوئے تو اس کی یہ احتیاطی تدابیر اللہ کی مشیت کے مقابلے میں کچھ بھی کام نہ آسکی۔ ہاں پس یعقوب (علیہ السلام) کے دل میں جو ایک کھٹک تھی اسے دور کرنے کے لئے اس نے اپنی سی کوشش کرلی۔ بیشک وہ ہماری دی ہوئی تعلیم سے صاحب علم تھا مگر اکثر لوگ معاملہ کی حقیقت کو جانتے نہیں ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(12,69,'12:69','“ یہ لوگ یوسف (علیہ السلام) کے حضور پہنچے تو اس نے اپنے بھائی کو اپنے پاس الگ بلا لیا اور اسے بتا دیا کہ “ میں تیرا وہی بھائی ہوں (جو کھویا گیا تھا) اب تو ان باتوں کا غم نہ کر جو یہ لوگ کرتے رہے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,70,'12:70','“ جب یوسف (علیہ السلام) ان بھائیوں کا سامان لدوانے لگا تو اس نے اپنے بھائی کے سامان میں اپنا پیالہ رکھ دیا۔ پھر ایک پکارنے والے نے پکار کر کہا “ اے قافلے والو ، تم لوگ چور ہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,71,'12:71','انہوں نے پلٹ کر پوچھا “ تمہاری کیا چیز کھوئی گئی ؟');
INSERT INTO "sayyid_qutb" VALUES(12,72,'12:72','” سرکاری ملازموں نے کہا “ بادشاہ کا پیمانہ ہم کو نہیں ملتا ”۔ ( اور ان کے جمعدار نے کہا) “ جو شخص لا کر دے گا اس کے لئے ایک بار شتر انعام ہے ، اس کا میں ذمہ لیتا ہوں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,73,'12:73','ان بھائیوں نے کہا “ خدا کی قسم ، تم لوگ خوب جانتے ہو کہ ہم اس ملک میں فساد کرنے نہیں آئے ہیں اور ہم چوریاں کرنے والے نہیں ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,74,'12:74','انہوں نے کہا “ اچھا ، اگر تمہاری بات جھوٹی نکلی تو چور کی کیا سزا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(12,75,'12:75','انہوں نے کہا “ اس کی سزا ؟ جس کے سامان میں سے چیز نکلے وہ آپ ہی اپنی سزا میں رکھ لیا جائے ، ہمارے ہاں تو ایسے ظالموں کو سزا دینے کا یہی طریقہ ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,76,'12:76','تب یوسف (علیہ السلام) نے اپنے بھائی سے پہلے ان کی خرجیوں کی تلاشی لینی شروع کی ، پھر اپنے بھائی کی خرجی سے گم شدہ چیز برآمد کرلی۔ اس طرح ہم نے یوسف (علیہ السلام) کی تائید اپنی تدبیر سے کی۔ اس کا یہ کام نہ تھا کہ بادشاہ کے دین (یعنی مصر کے شاہی قانون ) میں اپنے بھائی کو پکڑتا الا یہ کہ اللہ ہی ایسا چاہے۔ ہم جس کے درجے چاہتے ہیں ، بلند کردیتے ہیں ، اور ایک علم رکھنے والا ایسا ہے جو ہر صاحب علم سے بالاتر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(12,77,'12:77','ان بھائیوں نے کہا “ یہ چوری کرے تو کچھ تعجب کی بات بھی نہیں ، اس سے پہلے اس کا بھائی (یوسف (علیہ السلام) بھی چوری کرچکا ہے ” ۔ یوسف (علیہ السلام) ان کی یہ بات سن کر پی گیا ، حقیقت ان پر نہ کھولی ، بس (زیر لب ) اتنا کہہ کر رہ گیا کہ “ بڑے ہی برے ہو تم لوگ ، (میرے منہ در منہ مجھ پر ) جو الزام تم لگا رہے ہو اس کی حقیقت خدا خوب جانتا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,78,'12:78','انہوں نے کہا “ اے سردار ذی اقتدار (عزیر) اس کا باپ بہت بوڑھا آدمی ہے ، اس کی جگہ آپ ہم میں سے کسی کو رکھ لیجئے ، ہم آپ کو بڑا ہی نیک نفس انسان پاتے ہیں ” ۔');
INSERT INTO "sayyid_qutb" VALUES(12,79,'12:79','یوسف نے کہا “ پناہ بخدا ، دوسرے کسی شخص کو ہم کیسے رکھ سکتے ہیں ، جس کے پاس ہم نے اپنا مال پایا ہے اس کو چھوڑ کر دوسرے کو رکھیں گے تو ہم ظالم ہوں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,80,'12:80','جب وہ یوسف (علیہ السلام) سے مایوس ہوگئے تو ایک گوشے میں جا کر آپس میں مشورہ کرنے لگے۔ ان میں جو سب سے بڑا تھا وہ بولا “ تم جانتے نہیں ہو کہ تمہارے والد تم سے خدا کے نام پر عہدو پیمان لے چکے ہیں ؟ اور اس سے پہلے یوسف (علیہ السلام) کے معاملہ میں جو تم کرچکے ہو وہ بھی تم کو معلوم ہے۔ اب میں تو یہاں سے ہرگز نہ جاؤں گا جب تک کہ میرے والد مجھے اجازت نہ دیں ، یا پھر اللہ ہی میرے حق میں کوئی فیصلہ فرما دے کہ وہ سب سے بہتر فیصلہ کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(12,81,'12:81','تم جا کر اپنے والد سے کہو کہ ابا جان ، آپ کے صاحبزادے نے چوری کی ہے۔ ہم نے اسے چوری کرتے ہوئے نہیں دیکھا۔ جو کچھ ہمیں معلوم ہوا ہے بس وہی ہم بیان کر رہے ہیں ، اور غیب کی نگہبانی تو ہم نہ کرسکتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(12,82,'12:82','آپ اس بستی کے لوگوں سے پوچھ لیجئے جہاں ہم تھے۔ اس قافلے سے دریافت کرلیجئے جس کے ساتھ ہم آئے ہیں۔ ہم اپنے بیان میں بالکل سچے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,83,'12:83','باپ نے یہ داستان سن کر کہا “ دراصل تمہارے نفس نے تمہارے لیے ایک اور بڑی بات کو سہل بنا دیا۔ اچھا میں اس پر بھی صبر کروں گا اور بخوبی کروں گا۔ کیا بعید کہ اللہ ان سب کو مجھ سے لا ملائے ، وہ سب کچھ جانتا ہے اور اس کے سب کام حکمت پر مبنی ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,84,'12:84','“ پھر وہ ان کی طرف سے منہ پھیر کر بیٹھ گیا اور کہنے لگا کہ “ ہائے یوسف ! ” اور اس کی آنکھیں سفید پڑگئی تھیں وہ دل ہی دل میں غم گھٹا جا رہا تھا ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,85,'12:85','بیٹوں نے کہا “ خدارا ! آپ تو بس یوسف ہی کو یاد کیے جاتے ہیں۔ نوبت یہ آگئی ہے کہ اس کے غم میں اپنے آپ کو گھلا دیں گے یا اپنی جان ہلاک کر ڈالیں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,86,'12:86','اس نے کہا “ میں اپنی پریشانی اور اپنے غم کی فریاد اللہ کے سوا کسی سے نہیں کرتا ، اور اللہ سے جیسا میں واقف ہوں ، تم نہیں ہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,87,'12:87','“ میرے بچو ، جا کر یوسف (علیہ السلام) اور اس کے بھائی کی کچھ ٹوہ لگاؤ، اللہ کی رحمت سے مایوس نہ ہو ، اس کی رحمت سے تو بس کافر ہی مایوس ہوا کرتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,88,'12:88','جب یہ لوگ مصر جا کر یوسف (علیہ السلام) کی پیشی میں داخل ہوئے تو انہوں نے عرض کیا کہ “ اے سردار بااقتدار ، ہم اور ہمارے اہل و عیال سخت مصیبت میں مبتلا ہیں ، اور ہم کچھ حقیر سی پونجی لے کر آئے ہیں ، آپ ہمیں بھر پور غلہ عنایت فرمائیں اور ہم کو خیرات دیں ، اللہ خیرات دینے والوں کو جزا دیتا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,89,'12:89','( “ یہ سن کر یوسف (علیہ السلام) سے نہ رہا گیا) اس نے کہا “ تمہیں کچھ یہ بھی معلوم ہے کہ تم نے یوسف (علیہ السلام) اور اس کے بھائی کے ساتھ کیا کیا تھا جب کہ تم نادان تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,90,'12:90','“ وہ چونک کر بولے ، “ ہائیں ! کیا تم یوسف (علیہ السلام) ہو ؟ ” اس نے کہا “ ہاں ، میں یوسف ہوں اور یہ میرا بھائی ہے۔ اللہ نے ہم پر احسان فرمایا ۔ حقیقت یہ ہے کہ اگر تم کوئی تقویٰ اور صبر سے کام لے تو اللہ کے ہاں ایسے نیک لوگوں کا اجر مارا نہیں جاتا ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,91,'12:91','انہوں نے کہا “ بخدا کہ تم کو اللہ نے ہم پر فضیلت بخشی اور واقعی ہم خطاکار تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,92,'12:92','“ اس نے جواب دیا “ آج تم پر کوئی گرفت نہیں ، اللہ تمہیں معاف کرے ، وہ ارحم الراحمین ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,93,'12:93','جاؤ ! میری یہ قمیض لے جاؤ اور میرے والد کے منہ پر ڈال دو ، ان کی بینائی پلٹ آئے گی اور اپنے سب اہل و عیال کو میرے پاس لے آؤ “۔');
INSERT INTO "sayyid_qutb" VALUES(12,94,'12:94','” جب یہ قافلہ (مصر سے) روانہ ہوا تو ان کے باپ نے (کنعان میں ) کہا ” میں یوسف کی خوشبو محسوس کر رہا ہوں ، تم لوگ کہیں یہ نہ کہنے لگو کہ میں بڑھاپے میں سٹھیا گیا ہوں “۔');
INSERT INTO "sayyid_qutb" VALUES(12,95,'12:95','گھر کے لوگ بولے ” خدا کی قسم آپ ابھی تک اپنے اسی پرانے خبط میں پڑے ہوئے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(12,96,'12:96','پھر جب خوشخبری لانے والا آیا تو اس نے یوسف کا قمیض یعقوب (علیہ السلام) کے منہ پر ڈال دیا اور یکایک اس کی بینائی عود کر آئی “۔ ” تب اس نے کہا ” میں تم سے کہتا نہ تھا ؟ میں اللہ کی طرف سے وہ کچھ جانتا ہوں جو تم نہیں جانتے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,97,'12:97','سب بول اٹھے ابا جان ، آپ ہمارے گناہوں کی بخشش کے لئے دعا کریں ، واقعی ہم خطاکار تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,98,'12:98','اس نے کہا ،” میں اپنے رب سے تمہارے لیے معافی کی درخواست کروں گا۔ وہ بڑا معاف کرنے والا اور رحیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,99,'12:99','پھر جب یہ لوگ یوسف (علیہ السلام) کے پاس پہنچے تو اس نے اپنے والدین کو اپنے ساتھ بٹھا لیا اور اپنے سب کنبے والوں سے کہا ” چلو ، اب شہر چلو ، اللہ نے چاہا تو امین چین سے رہو گے “');
INSERT INTO "sayyid_qutb" VALUES(12,100,'12:100','(شہر میں داخل ہونے کے بعد) اس نے اپنے والدین کو اٹھا کر اپنے پاس تخت پر بٹھایا اور سب اس کے آگے بےاختیار سجدے میں جھک گئے۔ یوسف (علیہ السلام) نے کہا ” اباجان ، یہ تعبیر ہے میرے اس خواب کی جو میں نے پہلے دیکھا تھا۔ میرے رب نے اسے حقیقت بنا دیا۔ اس کا احسان ہے کہ اس نے مجھے قید خانے سے نکالا ، اور آپ لوگوں کو صحرا سے لا کر مجھ سے ملایا ، حالانکہ شیطان میرے اور میرے بھائیوں کے درمیان فساد ڈال چکا تھا۔ واقعہ یہ ہے کہ میرا رب غیر محسوس تدبیروں سے اپنی مشیت پوری کرتا ہے ، بیشک وہ علیم اور حکیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(12,101,'12:101','اے میرے رب ، تو نے مجھے حکومت بخشی اور مجھ کو باتوں کی تہہ تک پہنچنا سکھایا۔ زمین و آسمان کے بنانے والے ، تو ہی دنیا اور آخرت میں میرا سر پرست ہے ، میرا خاتمہ اسلام پر کر اور انجام کار مجھے صالحین کے ساتھ ملا “۔');
INSERT INTO "sayyid_qutb" VALUES(12,102,'12:102','“ اے بی کہہ دو کہ یہ قصہ غیب کی خبروں سے ہے جو ہم تم پر وحی کررہے ہیں ، ورنہ تم اس وقت موجود نہ تھے جب یوسف (علیہ السلام) کے بھائیوں نے آپس میں اتفاق کر کے سازش کی تھی ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,103,'12:103','مگر تم خواہ کتنا ہی چاہو ، ان میں سے اکثر لوگ مان کردینے والے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(12,104,'12:104','حالانکہ تم اس خدمت پر ان سے کوئی اجرت بھی نہیں مانگتے ہو۔ یہ تو ایک نصیحت ہے جو دنیا والوں کے لئے عام ہے۔');
INSERT INTO "sayyid_qutb" VALUES(12,105,'12:105','زمین اور آسمانوں میں کتنی ہی نشانیاں ہیں جن پر سے یہ لوگ گزرتے رہتے ہیں اور ذرا توجہ نہیں کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(12,106,'12:106','ان میں سے اکثر اللہ کو مانتے ہیں مگر اس طرح کہ اس کے ساتھ دوسروں کو بھی شریک ٹھہراتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(12,107,'12:107','کیا یہ مطمئن ہیں کہ خدا کے عذاب کی کوئی بلا انہیں دبوچ نہ لے گی یا بیخبر ی میں قیامت کی گھڑی اچانک ان پر نہ آجائے گی ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,108,'12:108','تم ان سے صاف کہہ دو کہ “ میرا راستہ تو یہ ہے ، میں اللہ کی طرف بلاتا ہوں ، میں خود بھی پوری روشنی میں اپنا راستہ دیکھ رہا ہوں اور میرے ساتھی بھی ، اور اللہ پاک ہے اور شرک کرنے والوں سے میرا کوئی واسطہ نہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,109,'12:109','اے نبی صلى الله عليه وسلم ، تم سے پہلے ہم نے جو پیغمبر بھیجے تھے ، وہ سب بھی انسان ہی تھے اور انہی بستیوں کے رہنے والوں میں سے تھے ، اور انہی کی طرف ہم وحی بھیجتے رہے۔ پھر کیا یہ لوگ زمین میں چلے پھرے نہیں ہیں کہ ان قوموں کا انجام انہیں نظر نہ آیا جو ان سے پہلے گزر چکی ہیں ؟ یقیناً آخرت کا گھر ان لوگوں کے لئے اور زیادہ بہتر ہے جنہوں نے (پیغمبروں کی بات مان کر) تقویٰ کی روش اختیار کی۔ کیا اب بھی تم لوگ نہ سمجھو گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,110,'12:110','(پہلے پیغمبروں کے ساتھ بھی یہی ہوتا رہا ہے کہ وہ مدتوں نصیحت کرتے رہے اور لوگوں نے سن کر نہ دیا ) یہاں تک کہ جب پیغمبر لوگوں سے مایوس ہوگئے اور لوگوں نے بھی سمجھ لیا کہ ان سے جھوٹ بولا گیا تھا ، تو یکایک ہماری مدد پیغمبروں کو پہنچ گئی۔ پھر جب ایسا موقع آجاتا ہے تو ہمارا قاعدہ یہ ہے کہ جسے ہم چاہتے ہیں ، بچا لیتے ہیں اور مجرموں پر سے تو ہمارا عذاب ٹالا ہی نہیں جا سکتا ”۔');
INSERT INTO "sayyid_qutb" VALUES(12,111,'12:111','اگلے لوگوں کے ان قصوں میں عقل و ہوش رکھنے والوں کے لئے عبرت ہے۔ یہ جو کچھ قرآن میں بیان کیا جا رہا ہے یہ بناوٹی باتیں نہیں ہیں بلکہ جو کتابیں اس سے پہلے آئی ہوئی ہیں انہی کی تصدیق ہے اور ہر چیز کی تفصیل اور ایمان لانے والوں کے لئے ہدایت اور رحمت ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,1,'13:1','” ا۔ ل۔ م۔ ر۔ یہ کتاب الٰہی کی آیات ہیں ، اور جو کچھ تمہارے رب کی طرف سے تم پر نازل کیا گیا ہے وہ عین حق ہے ، مگر (تمہاری قوم کے ) اکثر لوگ مان نہیں رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(13,2,'13:2','وہ اللہ ہی ہے جس نے آسمانوں کو ایسے سہاروں کے بغیر قائم کیا جو تم کو نطر آتے ہوں ، پھر اپنے تخت سلطنت پر جلوہ فرما ہوا ، اور اس نے آفتاب و ماہتا کو ایک قانون کا پابند بنایا۔ اس سارے نظام کی ہر چیز ایک وقت مقرر تک لئے چل رہی ہے اور اللہ ہی اس سارے کام کی تدبیر فرمارہا ہے۔ وہ نشانیاں کھول کھول کر بیان کرتا ہے شاہد کہ تم اپنے رب کی ملاقات کا یقین کرو۔');
INSERT INTO "sayyid_qutb" VALUES(13,3,'13:3','اور وہی ہے جس نے یہ زمین پھیلا رکھی ہے ، اس میں پہاڑوں کے کھوٹنے گاڑ رکھے ہیں اور دریا بہا دئیے ہیں۔ اسی نے ہر طرح کے پھلوں کے جوڑے پیدا کیے ہیں اور وہی دن رات طاری کرتا ہے۔ ان ساری چیزوں میں بڑی نشانیاں ہیں ان لوگوں کے لئے جو غورو فکر سے کام لیتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(13,4,'13:4','اور دیکھو ، زمین میں الگ الگ خطے پائے جاتے ہیں جو ایک سے متصل واقع ہیں۔ انگور کے باغ ہیں ، کھیتیاں ہیں ، کھجور کے درخت ہیں جن میں سے کچھ اکہرے ہیں اور کچھ دوہرے۔ سب کو ایک ہی پانی سیراب کرتا ہے ، مگر مزے میں ہم کسی کو بہتر بنا دیتے ہیں اور کسی کو کمتر ۔ ان سب چیزوں میں بہت سی نشانیاں ہیں ان لوگوں کے لئے جو عقل سے کام لیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(13,5,'13:5','اگر تمہیں تعجب کرنا ہے ، تو تعجب کے قابل لوگوں کا یہ قول ہے کہ جب ہم مر کر مٹی ہوجائیں گے تو کیا ہم نئے سرے سے پیدا کئے جائیں گے ؟ یہ وہ لوگ ہیں جنہوں نے اپنے رب سے کفر کیا ہے۔ یہ وہ لوگ ہیں جن کی گردنوں میں طوق پڑے ہوئے ہیں۔ یہ جہنمی ہیں اور جہنم میں ہمیشہ رہیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(13,6,'13:6','یہ لوگ بھلائی سے پہلے برائی کے لئے جلدی مچا رہے ہیں حالانکہ ان سے پہلے (جو لوگ اس روش پر چلے ہیں ان پر خدا کے عذاب کی ) عبرت ناک مثالیں گزر چکی ہیں۔ حقیقت یہ ہے کہ تیرا رب لوگوں کی زیادتیوں کے باوجود ان کے ساتھ چشم پوشی سے کام لیتا ہے ، اور یہ بھی حقیقت ہے کہ تیرا رب سخت سزا دینے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,7,'13:7','یہ لوگ جنہوں نے تمہاری بات ماننے سے انکار کردیا ہے ، کہتے ہیں کہ “ اس شخص پر اس کے رب کی طرف سے کوئی نشانی کیوں نہ اتری ؟ ”۔۔۔۔ تم تو محض خبردار کردینے والے ہو ، اور ہر قوم کے لئے ایک رہنما ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,8,'13:8','اللہ ایک ایک حاملہ کے پیٹ سے واقف ہے ، جو کچھ اس میں بنتا ہے اسے بھی وہ جانتا ہے اور جو کچھ اس میں کمی یا بیشی ہوتی ہے اس سے بھی وہ باخبر رہتا ہے۔ ہر چیز کے لئے اس کے ہاں ایک مقدار مقرر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,9,'13:9','وہ پوشیدہ اور ظاہر ، ہر چیز کا عالم ہے۔ وہ بزرگ ہے اور ہر حال میں بالا تر رہنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,10,'13:10','تم میں سے کوئی شخص خواہ زور سے بات کرے یا آہستہ ، اور کوئی رات کی تاریکی میں چھپا ہوا ہو یا دن کی روشنی میں چل رہا ہو ، اس کے لئے سب یکساں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(13,11,'13:11','ہر شخص کے آگے اور پیچھے اس کے مقرر کئے ہوئے نگراں لگے ہوئے ہیں جو اللہ کے حکم سے اس کی دیکھ بھال کر رہے ہیں۔ حقیقت یہ ہے کہ اللہ کسی قوم کے حال کو نہیں بدلتا جب تک وہ خود اپنے اوصاف کو نہیں بدل لیتی۔ اور جب اللہ کسی قوم کی شامت لانے کا فیصلہ کرلے تو پھر وہ کسی کے ٹالے نہیں ٹل سکتی ، نہ اللہ کے مقابلے میں ایسی قوم کا کوئی حامی و مددگار ہوسکتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,12,'13:12','وہی ہے جو تمہارے سامنے بجلیاں چمکاتا ہے جنہیں دیکھ کر تمہیں اندیشے بھی لاحق ہوتے ہیں اور امیدیں بھی بندھتی ہیں۔ وہی ہے جو پانی سے لدے ہوئے بادل اٹھاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,13,'13:13','بادلوں کی گرج اس کی حمد کے ساتھ اس کی پاکی بیان کرتی ہے اور فرشتے اس کی ہیبت سے لرزتے ہوئے اس کی تسبیح کرتے ہیں۔ وہ کڑکتی ہوئی بجلیوں کو بھیجتا ہے اور (بسا اوقات) انہیں جس پر چاہتا ہے عین اس حالت میں گرا دیتا ہے جبکہ لوگ اللہ کے بارے میں جھگڑ رہے ہوتے ہیں۔ فی الواقع اس کی چال بڑی زبردست ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,14,'13:14','اسی کو پکارنابرحق ہے۔ رہیں وہ دوسری ہستیاں جنہیں اس کو چھوڑ کر یہ لوگ پکارتے ہیں ، وہ ان کی دعاؤں کا کوئی جواب نہیں دے سکتیں۔ انہیں پکارنا ایسا ہے جیسے کوئی شخص پانی کی طرف ہاتھ پھیلا کر اس سے درخواست کرے کہ تو میرے منہ تک پہنچ جا ، حالانکہ پانی اس تک پہنچنے والا نہیں۔ بس اسی طرح کافروں کی دعائیں بھی کچھ نہیں ہیں مگر ایک تیر بےہدف !');
INSERT INTO "sayyid_qutb" VALUES(13,15,'13:15','وہ تو اللہ ہی ہے جس کو زمین و آسمان کی ہر چیز طوعا و کرھا سجدہ کر رہی ہے اور سب چیزوں کے سائے صبح و شام اس کے آگے جھکتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(13,16,'13:16','ان سے پوچھو ، آسمان و زمین کا رب کون ہے ؟۔۔۔ کہو ، اللہ ۔ پھر ان سے کہو کہ حقیقت یہ ہے تو کیا تم نے اسے چھوڑ کر ایسے معبودوں کو اپنا کار ساز ٹھہرا لیا جو خود اپنے لیے بھی کسی نفع و نقصان کا اختیار نہیں رکھتے ؟ کہو ، کیا اندھا اور آنکھوں والا برابر ہوا کرتا ہے ؟ کیا روشنی اور تاریکیاں یکساں ہوتی ہیں ؟ اور اگر ایسا نہیں تو کیا ان کے ٹھہرائے ہوئے شریکوں نے بھی اللہ کی طرح کچھ پیدا کیا ہے کہ اس کی وجہ سے ان پر تخلیق کا معاملہ مشتبہ ہوگیا ؟۔۔۔۔ کہو ہر چیز کا خالق صرف اللہ ہے اور وہ یکتا ہے ، سب پر غالب ! “');
INSERT INTO "sayyid_qutb" VALUES(13,17,'13:17','اللہ نے آسمان سے پانی برسایا اور ہر ندی نالہ اپنے ظرف کے مطابق اسے لے کر چل نکلا۔ پھر جب سیلاب اٹھا تو سطح پر جھاگ بھی آگئے۔ اور ایسے ہی جھاگ ان دھاتوں پر بھی اٹھتے ہیں جنہیں زیور اور برتن وغیرہ بنانے کے لئے لوگ پگھلایا کرتے ہیں۔ اسی مثال سے اللہ حق اور باطل کے معاملے کو واضح کرتا ہے۔ جو جھاگ ہے وہ اڑجایا کرتا ہے اور جو چیز انسانوں کے لئے نافع ہے وہ زمین میں ٹھہر جاتی ہے۔ اس طرح اللہ مثالوں سے اپنی بات سمجھاتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,18,'13:18','جن لوگوں نے اپنے رب کی دعوت قبول کرلی ان کے لئے بھلائی ہے ، اور جنہوں نے اسے قبول نہ کیا وہ اگر زمین کی ساری دولت کے بھی مالک ہوں اور اتنی ہی اور فراہم کرلیں تو وہ خدا کی پکڑ سے بچنے کے لئے اس سب کو فدیہ میں دے ڈالنے پر تیار ہوجائیں گے۔ یہ وہ لوگ ہیں جن سے بری طرح حساب لیا جائے گا اور ان کا ٹھکانا جہنم ہے ، بہت ہی برا ٹھکانا ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,19,'13:19','بھلا یہ کس طرح ممکن ہے کہ وہ شخص جو تمہارے رب کی اس کتاب کو جو اس نے تم پر نازل کی ہے حق جانتا ہے اور وہ شخص جو اس حقیقت کی طرف سے اندھا ہے ، دونوں یکساں ہوجائیں ؟ نصیحت تو دانشمند لوگ ہی قبول کیا کرتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,20,'13:20','“ اور ان کا طرز عمل یہ ہوتا ہے کہ اللہ کے ساتھ اپنے عہد کو پورا کرتے ہیں ، اسے مضبوط باندھنے کے بعد توڑ نہیں ڈالتے ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,21,'13:21','ان کی روش یہ ہوتی ہے کہ اللہ نے جن جن روابط کو برقرار رکھنے کا حکم دیا ہے انہیں برقرار رکھتے ہیں ، اپنے رب سے ڈرتے ہیں اور اس بات کا خوف رکھتے ہیں کہ کہیں ان سے بری طرح حساب نہ لیا جائے۔');
INSERT INTO "sayyid_qutb" VALUES(13,22,'13:22','ان کا حال یہ ہوتا ہے کہ اپنے رب کی رضا کے لئے صبر سے کام لیتے ہیں ، نماز قائم کرتے ہیں ، ہمارے دئیے ہوئے رزق میں سے علانیہ اور پوشیدہ خرچ کرتے ہیں ، اور برائی کو بھلائی سے دفع کرتے ہیں۔ آخرت کا گھر انہی لوگوں کے لئے ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(13,23,'13:23','“ یعنی ایسے باغ جو ان کی ابدی قیام گاہ ہوں گے وہ خود بھی ان میں داخل ہوں گے اور ان کے آباؤ اجداد اور ان کی بیویوں اور ان کی اولاد میں سے جو جو صالح ہیں وہ بھی ان کے ساتھ وہاں جائیں گے۔ ملائکہ ہر طرف سے ان کے استقبال کے لئے آئیں گے اور ان سے کہیں گے');
INSERT INTO "sayyid_qutb" VALUES(13,24,'13:24','“ تم پر سلامتی ہے ، تم نے دنیا میں جس طرح صبر سے کام لیا اس کی بدولت آج تم اس کے مستحق ہوئے ہو ، پس کیا ہی خوب ہے یہ آخرت کا گھر ! ”');
INSERT INTO "sayyid_qutb" VALUES(13,25,'13:25','رہے وہ لوگ جو اللہ کے عہد کو مضبوط باندھ لینے کے بعد توڑ ڈالتے ہیں ، جو ان رابطوں کو کاٹتے ہیں جنہیں اللہ نے جوڑنے کا حکم دیا ہے ، اور جو زمین میں فساد پھیلاتے ہیں ، وہ لعنت کے مستحق ہوں گے اور ان کے لئے آخرت میں بہت برا ٹھکانہ ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,26,'13:26','” اللہ جس کو چاہتا ہے رزق کی فراخی بخشتا ہے اور جسے چاہتا ہے نپا تلا رزق دیتا ہے۔ یہ لوگ دنیوی زندگی میں مگن ہیں ، حالانکہ دنیا کی زندگی آخرت کے مقابلے میں ایک متاع قلیل کے سوا کچھ بھی نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(13,27,'13:27','” یہ لوگ جنہوں نے (رسالت محمدی صلى الله عليه وسلم کو ماننے سے ) انکار کردیا ہے ، کہتے ہیں : ” اس شخص پر اس کے رب کی طرف سے کوئی نشانی کیوں نہ اتری “۔۔۔ کہواللہ جسے چاہتا ہے گمراہ کردیتا ہے اور وہ اپنی طرف آنے کا راستہ اسی کو دکھاتا ہے جو اس کی طرف رجوع کرے۔');
INSERT INTO "sayyid_qutb" VALUES(13,28,'13:28','ایسے ہی لوگ ہیں جنہوں نے (اس نبی صلى الله عليه وسلم کی دعوت کو) مان لیا ہے اور ان کے دلوں کو اللہ کی یاد سے اطمینان نصیب ہوتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,29,'13:29','خبردار رہو ! اللہ کی یاد ہی وہ چیز ہے جس سے دلوں کو اطمینان نصیب ہوا کرتا ہے۔ پھر جن لوگوں نے دعوت حق کو مانا اور نیک عمل کئے وہ خوش نصیب ہیں اور ان کے لئے اچھا انجام ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(13,30,'13:30','اے نبی صلى الله عليه وسلم ، اسی شان سے ہم نے تم کو رسول بنا کر بھیجا ہے ایک ایسی قوم میں جس سے پہلے بہت سی قومیں گزر چکی ہیں ، تا کہ تم ان لوگوں کو وہ پیغام سناؤ جو ہم نے تم پر نازل کیا ہے۔ اس حال میں کہ یہ اپنے نہایت مہربان خدا کے کافر بنے ہوئے ہیں۔ ان سے کہو کہ وہی میرا رب ہے اس کے سوا کوئی معبود نہیں ہے ، اسی پر میں نے بھروسہ کیا اور وہی میرا طحاو ماویٰ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,31,'13:31','اور کیا ہوجاتا اگر کوئی ایسا قرآن اتار دیا جاتا جس کے زور سے پہاڑ چلنے لگتے یا زمین شق ہوجاتی ، یا مردے قبروں سے نکل کر بولنے لگتے ؟ (اس طرح کی نشانیاں دکھا دینا کچھ مشکل نہیں ہے ) بلکہ سارا اختیار ہی اللہ کے ہاتھ میں ہے۔ پھر کیا اہل ایمان (ابھی تک کفار کی طلب کے جواب میں کسی نشانی کے ظہور کی آس لگائے بیٹھے ہیں اور وہ یہ جان کر ) مایوس نہیں ہوگئے کہ اگر اللہ چاہتا تو سارے انسانوں کو ہدایت دے دیتا ؟ جن لوگوں نے خدا کے ساتھ کفر کا رویہ اختیار کر رکھا ہے ان پر ان کے کرتوتوں کی وجہ سے کوئی نہ کوئی آفت آتی ہی رہتی ہے ، یا ان کے گھر کے قریب کہیں نازل ہوتی ہے۔ یہ سلسلہ چلتا رہے گا یہاں تک کہ اللہ کا وعدہ آن پورا ہو۔ یقیناً اللہ اپنے وعدے کی خلاف ورزی نہیں کرتا۔');
INSERT INTO "sayyid_qutb" VALUES(13,32,'13:32','تم سے پہلے بھی بہت سے رسولوں کا مذاق اڑایا جا چکا ہے ، مگر میں نے ہمیشہ منکرین کو ڈھیل دی اور آخر کار ان کو پکڑ لیا ، پھر دیکھ لو کہ میری سزا کیسی سخت تھی “۔');
INSERT INTO "sayyid_qutb" VALUES(13,33,'13:33','” پھر کیا وہ جو ایک ایک متنفس کی کمائی پر نظر رکھتا ہے (اس کے مقابلے میں یہ جسارتیں کی جا رہی ہیں کہ ) لوگوں نے اس کے کچھ شریک ٹھہرا رکھے ہیں ؟ اے نبی ؐ ۔۔۔ ان سے کہو (اگر واقعی وہ خدا کے اپنے بنائے ہوئے شریک ہیں تو ) ذرا ان کے نام لو کہ وہ کون ہیں ؟ ۔۔۔ کیا تم اللہ کو ایک نئی بات کی خبر دے رہے ہو ، جسے وہ اپنی زمین میں نہیں جانتا ؟ یا تم لوگ بس یونہی جو منہ میں آتا ہے کہہ ڈالتے ہو ؟ حقیقت یہ ہے کہ جن لوگوں نے دعوت حق کو ماننے سے انکار کیا ہے ان کے لئے ان کی مکاریاں خوشنما بنا دی گئی ہیں اور وہ راہ راست سے روک دئیے گئے ہیں ، پھر جس کو اللہ گمراہی میں پھینک دے اسے کوئی راہ دکھانے والا نہیں ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(13,34,'13:34','ایسے لوگوں کے لئے دنیا کی زندگی ہی میں عذاب ہے ، اور آخرت کا عذاب اس سے بھی زیادہ سخت ہے۔ کوئی ایسا نہیں جو انہیں خدا سے بچانے والا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(13,35,'13:35','خدا ترس انسانوں کے لئے جس جنت کا وعدہ کیا گیا ہے اس کی شان یہ ہے کہ اس کے نیچے نہریں بہہ رہی ہیں ، اس کے پھل دائمی ہیں اور اس کا سایہ لازوال۔ یہ انجام ہے متقی لوگوں کا۔ اور منکرین حق کا انجام یہ ہے کہ ان کے لئے دوزخ کی آگ ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,36,'13:36','اے نبی صلى الله عليه وسلم ! جن لوگوں کو ہم نے پہلے کتاب دی تھی ، وہ اس کتاب سے جو ہم نے تم پر نازل کی ہے ، خوش ہیں اور مختلف گروہوں میں کچھ لوگ ایسے بھی ہیں جو اس کی بعض باتوں کو نہیں مانتے۔ تم صاف کہہ دو کہ ” مجھے تو صرف اللہ کی بندگی کا حکم دیا گیا ہے اور اس سے منع کیا گیا ہے کہ کسی کو اس کے ساتھ شریک ٹھہراؤں ، لہٰذا میں اسی کی طرف دعوت دیتا ہوں اور اسی کی طرف میرا رجوع ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,37,'13:37','اسی ہدایت کے ساتھ ہم نے یہ فرمان عربی تم پر نازل کیا ہے۔ اب اگر تم نے اس علم کے باوجود جو تمہارے پاس آچکا ہے لوگوں کی خواہشات کی پیروی کی تو اللہ کے مقابلے میں نہ کوئی تمہارا حامی و مددگار ہے اور نہ کوئی اس کی پکڑ سے تم کو بچا سکتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,38,'13:38','تم سے پہلے بھی ہم بہت سے رسول بھیج چکے ہیں اور ان کو ہم نے بیوی بچوں والا ہی بنایا تھا اور کسی رسول کی بھی یہ طاقت نہ تھی کہ اللہ کے اذن کے بغیر کوئی نشانی خود دکھاتا۔ ہر دور کے لئے ایک کتاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,39,'13:39','اللہ جو کچھ چاہتا ہے ، مٹا دیتا ہے اور جس چیز کو چاہتا ہے ، قائم رکھتا ہے۔ ام الکتاب اسی کے پاس ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,40,'13:40','اور اے نبی صلى الله عليه وسلم جس برے انجام کی دھمکی ہم ان لوگوں کو دے رہے ہیں اس کا کوئی حصہ خواہ ہم تمہارے جیتے جی دکھا دیں یا اس کے ظہور میں آنے سے پہلے ہم تمہیں اٹھا لیں ، بہرحال تمہارا کام صرف پیغام پہنچا دینا ہے اور حساب لینا ہمارا کام ہے۔');
INSERT INTO "sayyid_qutb" VALUES(13,41,'13:41','کیا یہ لوگ دیکھتے نہیں ہیں کہ ہم اس سر زمین پر چلے آرہے ہیں اور اس کا دائرہ ہر طرف سے تنگ کرتے چلے آتے ہیں ؟ اللہ حکومت کر رہا ہے۔ کوئی اس کے فیصلوں پر نظر ثانی کرنے والا نہیں ہے اور اسے حساب لیتے کچھ دیر نہیں لگتی “۔');
INSERT INTO "sayyid_qutb" VALUES(13,42,'13:42','ان سے پہلے جو لوگ ہو گزرے ہیں وہ بھی بڑی بڑی چالیں چل چکے ہیں ، مگر اصل فیصلہ کن چال تو پوری کی پوری اللہ ہی کے ہاتھ میں ہے۔ وہ جانتا ہے کہ کون کیا کچھ کمائی کر رہا ہے ، اور عنقریب یہ منکرین حق دیکھ لیں گے کہ انجام کس کا بخیر ہوتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(13,43,'13:43','یہ منکرین کہتے ہیں کہ تم خدا کے بھیجے ہوئے نہیں ہو۔ کہو ، ” میرے اور تمہارے درمیان اللہ کی گواہی کافی ہے اور پھر اس شخص کی گواہی جو کتاب آسمانی کا علم رکھتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,1,'14:1','ا۔ ل۔ ر۔ اے محمد صلى الله عليه وسلم ، یہ ایک کتاب ہے جس کو ہم نے تمہاری طرف نازل کیا ہے تا کہ تم لوگوں کو تاریکیوں سے نکال کر روشنی میں لاؤ، ان کے رب کی توفیق سے ، اس خدا کے راستے پر جو زبردست اور اپنی ذات میں آپ محمود ہے');
INSERT INTO "sayyid_qutb" VALUES(14,2,'14:2','اور زمین اور آسمانوں کی ساری موجودات کا مالک ہے۔ اور سخت تباہ کن سزا ہے قبول حق سے انکار کرنے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(14,3,'14:3','جو دنیا کی زندگی کو آخرت پر ترجیح دیتے ہیں۔ جو اللہ کے راستے سے لوگوں کو روک رہے ہیں اور چاہتے ہیں کہ یہ راستہ (ان کی خواہشات کے مطابق) ٹیڑھا ہوجائے۔ یہ لوگ گمراہی میں بہت دور نکل گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(14,4,'14:4','ہم نے اپنا پیغام دینے کے لئے جب بھی کوئی رسول بھیجا ہے اس نے اپنی قوم ہی کی زبان میں پیغام دیا ہے تا کہ وہ انہیں اچھی طرح کھول کر بات سمجھائے۔ پھر اللہ جسے چاہتا ہے بھٹکا دیتا ہے اور جسے چاہتا ہے ، ہدایت بخشتا ہے ، وہ بالادست اور حکیم ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,5,'14:5','ہم اس سے قبل موسیٰ (علیہ السلام) کو بھی اپنی نشانیوں کے ساتھ بھیج چکے ہیں۔ اسے بھی ہم نے حکم دیا تھا کہ اپنی قوم کو تاریکیوں سے نکال کر روشنی میں لا اور انہیں تاریخ الٰہی کے سبق آموز واقعات سنا کر نصیحت کر۔ ان واقعات میں بڑی نشانیاں ہیں ہر اس شخص کے لئے جو صبر اور شکر کرنے والا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(14,6,'14:6','یاد کرو جب موسیٰ (علیہ السلام) نے اپنی قوم سے کہا ” اللہ کے اس احسان کو یاد کرو جو اس نے تم پر کیا ہے۔ اس نے تم کو فرعون والوں سے چھڑایا جو تم سخت تکلیفیں دیتے تھے ، تمہارے لڑکوں کو قتل کر ڈالتے تھے اور تمہاری لڑکیوں کو زندہ بچا رکھتے تھے ، اس میں تمہارے رب کی طرف سے تمہاری بڑی آزمائش تھی۔');
INSERT INTO "sayyid_qutb" VALUES(14,7,'14:7','اور یاد رکھو ، تمہارے رب نے خبردار کردیا تھا کہ اگر شکر گزار بنو گے تو میں تم کو اور زیادہ نوازدوں گا اور اگر کفران نعمت کروگے تو میری سزا بہت سخت ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,8,'14:8','اور موسیٰ نے کہا کہ ” اگر تم کفر کرو اور زمین کے سارے رہنے والے بھی کافر ہوجائیں تو اللہ نے بےنیاز اور اپنی ذات میں آپ محمود ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,9,'14:9','” کیا تمہیں ان قوموں کے حالات نہیں پہنچے جو تم سے پہلے گزر چکی ہیں ؟ قوم نوح (علیہ السلام) ، عاد ، ثمود اور ان کے بعد آنے والی بہت سی قومیں جن کا شمار اللہ ہی کو معلوم ہے ؟ ان کے رسول جب ان کے پاس صاف صاف باتیں اور کھلی کھلی نشانیاں لیے ہوئے آئے تو انہوں نے اپنے منہ میں ہاتھ دبا لیے اور کہا کہ ” جس پیغام کے ساتھ تم بھیجے گئے ہو ہم اس کو نہیں مانتے اور جس چیز کی تم ہمیں دعوت دیتے ہو اس کی طرف سے ہم سخت خلجان آمیز شک میں پڑے ہوئے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(14,10,'14:10','ان کے رسولوں نے کہا ” کیا خدا کے بارے میں شک ہے جو آسمانوں اور زمین کا خالق ہے۔ وہ تمہیں بلا رہا ہے تا کہ تمہارے قصور معاف کرے اور تم کو ایک مدت مقرر تک مہلت دے۔ انہوں نے جواب دیا ” تم کچھ نہیں ہو مگر ویسے ہی انسان جیسے ہم ہیں۔ تم ہمیں ان ہستیوں کی بندگی سے روکنا چاہتے ہو ، جن کی بندگی ہمارے باپ دادا سے ہوتی چلی آرہی ہے ۔ اچھا تو لاؤ کوئی صریح سند “۔');
INSERT INTO "sayyid_qutb" VALUES(14,11,'14:11','ان کے رسولوں نے ان سے کہا ” واقعی ہم کچھ نہیں ہیں مگر تم ہی جیسے انسان۔ لیکن اللہ اپنے بندوں میں سے جس کو چاہتا ہے ، نوازتا ہے۔ اور یہ ہمارے اختیار میں نہیں ہے کہ تمہیں کوئی سند لا دیں۔ سند تو اللہ ہی کے اذن سے ہو سکتی ہے اور اللہ ہی پر اہل ایمان کو بھروسہ کرنا چاہیے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,12,'14:12','” اور ہم کیوں نہ اللہ پر بھروسہ کریں جب کہ ہماری زندگی کی راہوں میں اس نے ہماری رہنمائی کی ہے ؟ جو اذیتیں تم لوگ ہمیں دے رہے ہیں ان پر ہم صبر کریں گے اور بھروسہ کرنے والوں کا بھروسہ اللہ ہی پر ہونا چاہئے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,13,'14:13','” آخر کار منکرین نے اپنے رسولوں سے کہہ دیا کہ ” یا تو تمہیں ہماری ملت میں واپس آنا ہوگا ورنہ ہم تمہیں اپنے ملک سے نکال دیں گے “۔ ” تب ان کے رب نے ان پر وحی بھیجی کہ ” ہم ان ظالموں کو ہلاک کردیں گے');
INSERT INTO "sayyid_qutb" VALUES(14,14,'14:14','اور ان کے بعد تمہیں زمین میں آباد کریں گے۔ یہ انعام ہے اس کا جو میرے حضور جواب دہی کا خوف رکھتا ہو اور میری وعید سے ڈرتا ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(14,15,'14:15','“ انہوں نے فیصلہ چاہا تھا (تو یوں ان کا فیصلہ ہوا) اور ہر جبار دشمن حق نے منہ کی کھائی ،');
INSERT INTO "sayyid_qutb" VALUES(14,16,'14:16','پھر اس کے بعد آگے اس کے لئے جہنم ہے۔ وہاں اسے کچ لہو کا سا پانی پینے کو دیا جائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(14,17,'14:17','جسے وہ زبردستی حلق سے اتارنے کی کوشش کرے گا اور مشکل ہی سے اتار سکے گا۔ موت ہر طرف سے اس پر چھائی رہے گی مگر وہ مرنے نہ پائے گا اور آگے ایک سخت عذاب اس کی جان کا لاگو رہے گا ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,18,'14:18','“ جن لوگوں نے اپنے رب سے کفر کیا ہے ان کے اعمال کی مثال اسی اکھ کی سی ہے جسے ایک طوفانی دن کی آندھی نے اڑا دیا ہو۔ وہ اپنے کئے کا کچھ بھی پھل نہ پاسکیں گے ۔ یہی پرلے درجے کی گم گشتگی ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,19,'14:19','کیا تم دیکھتے نہیں ہو کہ اللہ نے آسمان و زمین کی تخلیق کو حق پر قائم کیا ہے ؟ وہ چاہے تو تم لوگوں کو لے جائے اور ایک نئی خلقت تمہاری جگہ لے آئے۔');
INSERT INTO "sayyid_qutb" VALUES(14,20,'14:20','ایسا کرنا اس پر کچھ بھی دشوار نہیں ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,21,'14:21','اور یہ لوگ جب اکٹھے اللہ کے سامنے بےنقاب ہوں گے تو اس وقت ان میں سے جو دنیا میں کمزور تھے ، وہ ان لوگوں سے جو بڑے بنے ہوئے تھے ، کہیں گے “ دنیا میں ہم تمہارے تابع تھے ، اب کیا تم اللہ کے عذاب سے ہم کو بچانے کے لئے بھی کچھ کرسکتے ہو ؟” وہ جواب دیں گے “ اگر اللہ نے ہمیں نجات کی کوئی راہ دکھائی ہوتی تو ہم ضرور تمہیں دکھا دیتے۔ اب تو یکساں ہے خواہ ہم جزع و فزع کریں یا صبر ، بہرحال ہمارے بچنے کی کوئی صورت نہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,22,'14:22','اور جب فیصلہ چکا دیا جائے گا تو شیطان کہے گا “ حقیقت یہ ہے کہ اللہ نے جو وعدے تم سے کئے تھے وہ سب سچے تھے اور میں نے جتنے وعدے کئے ان میں سے کوئی بھی پورا نہ کیا۔ میرا تم پر کوئی زور تو تھا نہیں ، میں نے اس کے سوا کچھ نہیں کیا کہ اپنے راستے کی طرف تم کو دعوت دی اور تم نے میری دعوت پر لبیک کہا۔ اب مجھے ملامت نہ کرو ، اپنے آپ ہی کو ملامت کرو۔ یہاں نہ میں تمہاری فریاد رسی کرسکتا ہوں اور نہ تم میری۔ اس سے پہلے جو تم نے مجھے خدائی میں شریک بنا رکھا تھا ، میں اس سے بری الذمہ ہوں ، ایسے ظالموں کے لئے تو دردناک سزا یقینی ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,23,'14:23','“ بخلاف اس کے جو لوگ دنیا میں ایمان لائے اور جنہوں نے نیک عمل کئے ہیں وہ ایسے باغوں میں داخل کئے جائیں گے جن کے نیچے نہریں بہتی ہوں گی۔ وہاں وہ اپنے رب کے اذن سے ہمیشہ رہیں گے ، اور وہاں ان کا استقبال سلامتی کی مبارکباد سے ہوگا ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,24,'14:24','کیا تم دیکھتے نہیں ہو کہ اللہ نے کلمہ طیبہ کو کس چیز سے مثال دی ہے ؟ اس کی مثال ایسی ہے جیسے ایک اچھی ذات کا درخت جس کی جڑ زمین میں گہری جمی ہوئی ہے اور شاخیں آسمان تک پہنچی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(14,25,'14:25','ہر آن وہ اپنے رب کے حکم سے اپنے پھل دے رہا ہے۔ یہ مثالیں اللہ اس لیے دیتا ہے کہ لوگ ان سے سبق لیں۔');
INSERT INTO "sayyid_qutb" VALUES(14,26,'14:26','اور کلمہ خبیثہ کی مثال ایک بدذات درخت کی سی ہے جو زمین کی سطح سے اکھاڑ پھینکا جاتا ہے ، اس کے لئے کوئی استحکام نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(14,27,'14:27','ایمان لانے والوں کو اللہ ایک قول ثابت کی بنیاد پر دنیا اور آخرت ، دونوں میں ثبات عطا کرتا ہے ، اور ظالموں کو اللہ بھٹکا دیتا ہے۔ اللہ کو اختیار ہے جو چاہے کرے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,28,'14:28','“ تم نے دیکھا ان لوگوں کو جنہوں نے اللہ کی نعمت پائی اور اسے کفران نعمت سے بدل ڈالا اور (اپنے ساتھ ) اپنی قوم کو بھی ہلاکت کے گھر میں جھونک دیا');
INSERT INTO "sayyid_qutb" VALUES(14,29,'14:29','یعنی جہنم ، جس میں ہو جھلسے جائیں گے اور وہ بدترین جائے قرار ہے');
INSERT INTO "sayyid_qutb" VALUES(14,30,'14:30','اور اللہ کے کچھ ہمسر تجویز کرلیے تا کہ وہ انہیں اللہ کے راستے سے بھٹکا دیں ؟ ان سے کہو ، اچھا مزے کرلو ، آخر کار تمہیں پلٹ کر جانا دوزخ ہی میں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(14,31,'14:31','“ اے نبی صلى الله عليه وسلم ، میرے جو بندے ایمان لائے ہیں ان سے کہہ دو کہ نماز قائم کریں اور جو کچھ ہم نے ان کو دیا ہے اس میں سے کھلے اور چھپے (راہ خیر میں ) خرچ کریں قبل اس کے کہ وہ دن آئے جس میں نہ خریدو فروخت ہوگی اور نہ دوست نوازی ہو سکے گی ”');
INSERT INTO "sayyid_qutb" VALUES(14,32,'14:32','” اللہ وہی تو ہے جس نے زمین اور آسمانوں کو پیدا کیا اور آسمان سے پانی برسایا ، پھر اس کے ذریعہ سے تمہاری رزق رسانی کے لئے طرح طرح کے پھل پیدا کئے۔ جس نے کشتی کو تمہارے لیے مسخر کیا کہ سمندر میں اس کے حکم سے چلے اور دریاؤں کو تمہارے لیے مسخر کیا۔');
INSERT INTO "sayyid_qutb" VALUES(14,33,'14:33','جس نے سورج اور چاند کو تمہارے لیے مسخر کیا کہ لگاتار چلے جا رہے ہیں اور رات اور دن کو تمہارے لیے مسخر کیا۔');
INSERT INTO "sayyid_qutb" VALUES(14,34,'14:34','جس نے وہ سب کچھ تمہیں دیا جو تم نے مانگا۔ اگر تم اللہ کی نعمتوں کا شمار کرنا چاہو تو نہیں کرسکتے۔ حقیقت یہ ہے کہ انسان بڑا ہی بےانصاف اور ناشکرا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(14,35,'14:35','” یاد کرو وہ وقت جب ابراہیم (علیہ السلام) نے دعا کی تھی کہ ” اس شہر (یعنی مکہ) کو امن کا شہر بنا اور مجھے اور میری اولاد کو بت پرستی سے بچا ۔');
INSERT INTO "sayyid_qutb" VALUES(14,36,'14:36','پروردگار ، ان بتوں نے بہتوں کو گمراہی میں ڈالا ہے (ممکن ہے کہ میری اولاد کو بھی یہ گمراہ کریں ، لہٰذا ان میں سے ) جو میرے طریقے پر چلے وہ میرا ہے اور جو میرے خلاف طریقہ اختیار کرے تو یقیناً درگزر کرنے والا مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(14,37,'14:37','پروردگار ، میں نے ایک بےآب وگیاہ وادی میں اپنی اولاد کے ایک حصے کو تیرے محترم گھر کے پاس لا بسایا ہے۔ پروردگار ، یہ میں نے اس لیے کیا ہے کہ یہ لوگ یہاں نماز قائم کریں ، لہٰذا تو لوگوں کے دلوں کو ان کا مشتاق بنا اور انہیں کھانے کو پھل دے ، شاید کہ یہ شکر گزار بنیں۔');
INSERT INTO "sayyid_qutb" VALUES(14,38,'14:38','پروردگار ، تو جانتا ہے جو کچھ ہم چھپاتے ہیں اور جو کچھ ظاہر کرتے ہیں “۔ اور واقعی اللہ سے کچھ بھی چھپا ہوا نہیں ہے ، نہ زمین میں نہ آسمانوں میں');
INSERT INTO "sayyid_qutb" VALUES(14,39,'14:39','” شکر ہے اس کا خدا کا جس نے مجھے اس بڑھاپے میں اسماعیل (علیہ السلام) اور اسحاق (علیہ السلام) جیسے بیٹے دئیے ، حقیقت یہ ہے کہ میرا رب ضرور دعا سنتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(14,40,'14:40','اے میرے پروردگار ، مجھے نماز قائم کرنے والا بنا اور میری اولاد میں سے بھی (ایسے لوگ اٹھا جو یہ کام کریں) ۔ پروردگار ، میری دعا قبول کر۔');
INSERT INTO "sayyid_qutb" VALUES(14,41,'14:41','پروردگار ، مجھے اور میرے والدین کو اور سب ایمان لانے والوں کو اس دن معاف کر دیجیو جبکہ حساب قائم ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(14,42,'14:42','“ اب یہ ظالم لوگ جو کچھ کر رہے ہیں ، اللہ کو تم اس سے غافل نہ سمجھو۔ اللہ تو انہیں ٹال رہا ہے اس دن کے لئے جب حال یہ ہوگا کہ آنکھیں پھٹی کی پھٹی رہ گئی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(14,43,'14:43','سر اٹھائے بھاگے چلے جا رہے ہیں ، نظریں اوپر جمی ہیں اور دل اڑے جاتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,44,'14:44','“ اے نبی ؐ، اس دن سے تم انہیں ڈرا دو جبکہ عذاب انہیں آلے گا۔ اس وقت یہ ظالم کہیں گے کہ “ اے ہمارے رب ہمیں تھوڑی سی مہلت اور دے دے ، ہم تیری دعوت کو لبیک کہیں گے اور رسولوں کی پیروی کریں گے ”۔ ( مگر انہیں صاف جواب دے دیا جائے گا کہ ) “ کیا تم وہی لوگ نہیں ہو جو اس سے پہلے قسمیں کھا کھا کر کہتے تھے کہ ہم پر تو کبھی زوال آنا ہی نہیں ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(14,45,'14:45','حالانکہ تم ان قوموں کی بستیوں میں رہ بس چکے تھے جنہوں نے اپنے اوپر آپ ظلم کیا تھا اور دیکھ چکے تھے کہ ہم نے ان سے کیا سلوک کیا اور ان کی مثالیں دے دے کر ہم تمہیں سمجھا بھی چکے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,46,'14:46','“ انہوں نے اپنی ساری ہی چالیں چل دیکھیں ، مگر ان کی ہر چال کا توڑ اللہ کے پاس تھا اگرچہ ان کی چالیں ایسی غضب کی تھیں کہ پہاڑ ان سے ٹل جائیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,47,'14:47','“ پس اے نبی صلى الله عليه وسلم ، تم ہرگز یہ گمان نہ کرو کہ اللہ کبھی اپنے رسولوں سے کئے ہوئے وعدوں کے خلاف کرے گا۔ اللہ زبردست ہے اور انتقام لینے والا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,48,'14:48','“ ڈراؤ انہیں اس دن سے جبکہ زمین اور آسمان بدل کر کچھ سے کچھ کر دئیے جائیں گے ”۔ “ اور سب کے سب اللہ واحد قہار کے سامنے بےنقاب حاضر ہوجائیں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,49,'14:49','“ اس روز تم مجرموں کو دیکھو گے کہ زنجیروں میں ہاتھ پاؤں جکڑے ہوئے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(14,50,'14:50','تارکول کے لباس پہنے ہوئے ہوں گے اور آگ کے شعلے ان کے چہروں پر چھائے جا رہے ہوں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,51,'14:51','یہ اس لیے ہوگا کہ اللہ ہر متنفس کو اس کے کئے کا بدلہ دے۔ اللہ کو حساب لیتے کچھ دیر نہیں لگتی ”۔');
INSERT INTO "sayyid_qutb" VALUES(14,52,'14:52','یہ ایک پیغام ہے سب انسانوں کے لئے ، اور یہ بھیجا گیا ہے اس لیے کہ ان کو اس کے ذریعہ سے خبردار کردیا جائے اور وہ جان لیں کہ حقیقت میں خدا بس ایک ہی ہے اور جو عقل رکھتے ہیں وہ ہوش میں آجائیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,1,'15:1','ا۔ ل۔ ر۔ یہ آیات ہیں کتاب الٰہی اور قرآن مبین کی۔');
INSERT INTO "sayyid_qutb" VALUES(15,2,'15:2','بعید نہیں کہ ایک وقت وہ آجائے جب وہی لوگ جنہوں نے آج انکار کردیا ہے پچھتا پچھتا کر کہیں گے کہ کاش ہم نے سر تسلیم خم کردیا ہوتا۔');
INSERT INTO "sayyid_qutb" VALUES(15,3,'15:3','چھوڑو انہیں ، کھائیں پئیں ، مزے کریں اور بھلاوے میں ڈالے رکھے ان کو جھوٹی امید۔ عنقریب انہیں معلوم ہوجائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(15,4,'15:4','ہم نے اس سے پہلے جس بستی کو بھی ہلاک کیا ہے اس کے لئے ایک خاص مہلت عمل لکھی جا چکی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(15,5,'15:5','کوئی قوم نہ اپنے وقت مقرر سے پہلے ہلاک ہو سکتی ہے ، نہ اس کے بعد چھوٹ سکتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,6,'15:6','یہ لوگ کہتے ہیں ” اے وہ شخص جس پر یہ ذکر نازل ہوا ہے ، تو یقیناً دیوانہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,7,'15:7','اگر تو سچا ہے تو ہمارے سامنے فرشتوں کو لے کیوں نہیں آتا ؟ “');
INSERT INTO "sayyid_qutb" VALUES(15,8,'15:8','ہم فرشتوں کو یوں ہی نہیں اتار دیا کرتے۔ وہ جب اترتے ہیں تو حق کے ساتھ اترتے ہیں ، اور پھر لوگوں کو مہلت نہیں دی جاتی۔');
INSERT INTO "sayyid_qutb" VALUES(15,9,'15:9','رہا یہ ذکر ، تو اس کو ہم نے نازل کیا ہے اور ہم خود اس کے نگہبان ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,10,'15:10','اے نبی صلى الله عليه وسلم ہم تم سے پہلے بہت سی گزری ہوئی قوموں میں رسول بھیج چکے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,11,'15:11','کبھی ایسا نہیں ہوا کہ ان کے پاس کوئی رسول آیا ہو اور انہوں نے اس کا مذاق نہ اڑایا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(15,12,'15:12','مجرمین کے دلوں میں تو ہم اس ذکر کو اسی طرح (سلاخ کے مانند) گزارتے ہیں۔ وہ اس پر ایمان نہیں لایا کرتے ۔');
INSERT INTO "sayyid_qutb" VALUES(15,13,'15:13','قدیم سے اس قماش کے لوگوں کا یہی طریقہ چلا آرہا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(15,14,'15:14','اگر ہم ان پر آسمان کا کوئی دروازہ کھول دیتے اور وہ دن دہاڑے اس میں چڑھنے بھی لگتے');
INSERT INTO "sayyid_qutb" VALUES(15,15,'15:15','تب بھی وہ یہی کہتے کہ ہماری آنکھوں کو دھوکا ہو رہا ہے ، بلکہ ہم پر جادو کردیا گیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,16,'15:16','“ یہ ہماری کارفرمائی ہے کہ آسمان میں ہم نے بہت سے مضبوط قلعے بنائے ، ان کو دیکھنے والوں کے لئے (ستاروں سے ) آراستہ کیا ،');
INSERT INTO "sayyid_qutb" VALUES(15,17,'15:17','اور ہر شیطان مردود سے ان کو محفوظ کردیا۔');
INSERT INTO "sayyid_qutb" VALUES(15,18,'15:18','کوئی شیطان ان میں راہ نہیں پا سکتا ، الا یہ کہ کچھ سن گن لے لے اور جب وہ سن گن لینے کی کوشش کرتا ہے تو ایک شعلہ روشن اس کا پیچھا کرتا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,19,'15:19','اور ہم نے زمین کو پھیلایا ، اس میں پہاڑ جمائے ، اس میں ہر نوع کی نباتات ٹھیک ٹھیک نپی تلی مقدار کے ساتھ اگائی');
INSERT INTO "sayyid_qutb" VALUES(15,20,'15:20','اور اس میں معیشت کے اسباب فراہم کئے تمہارے لیے اور ان بہت سی مخلوقات کے لئے بھی جن کے رازق تم نہیں ہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,21,'15:21','“ کوئی چیزایسی نہیں ہے جس کے خزانے ہمارے پاس نہ ہوں اور جس چیز کو بھی ہم نازل کرتے ہیں ایک مقرر مقدار میں نازل کرتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,22,'15:22','بار آور ہواؤں کو ہم ہی بھیجتے ہیں ، پھر آسمانوں سے پانی برساتے ہیں اور اس پانی سے تمہیں سیراب کرتے ہیں۔ اس دولت کے خزانے دار تم نہیں ہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,23,'15:23','“ زندگی اور موت ہم دیتے ہیں ، اور ہم ہی سب کے وارث ہونے والے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(15,24,'15:24','پہلے جو لوگ تم میں سے ہو گزرے ہیں ان کو بھی ہم نے دیکھ رکھا ہے اور بعد میں آنے والے بھی ہماری نگاہ میں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,25,'15:25','یقیناً تمہارا رب ان سب کو اکٹھا کرے گا وہ حکیم بھی ہے اور علیم بھی ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,26,'15:26','ہم نے انسان کو سڑی ہوئی مٹی کے سوکھے گارے سے بنایا');
INSERT INTO "sayyid_qutb" VALUES(15,27,'15:27','اور اس سے پہلے جنوں کو ہم آگ کی لپیٹ سے پیدا کرچکے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,28,'15:28','پھر یاد کرو اس موقع کو جب تمہارے رب نے فرشتوں سے کہا کہ “ میں سڑی ہوئی مٹی کے سوکھے گارے سے ایک بشر پیدا کر رہا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(15,29,'15:29','جب میں اسے پورا بنا چکوں اور اس میں اپنی روح سے کچھ پھونک دوں تو تم سب اس کے آگے سجدے میں گر جانا ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,30,'15:30','چناچہ تمام فرشتوں نے سجدہ کیا ،');
INSERT INTO "sayyid_qutb" VALUES(15,31,'15:31','سوائے ابلیس کے کہ اس نے سجدہ کرنے والوں کا ساتھ دینے سے انکار کر دیا ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,32,'15:32','رب نے پوچھا ” اے ابلیس ، تجھے کیا ہوا کہ تو نے سجدہ کرنے والوں کا ساتھ نہ دیا ؟');
INSERT INTO "sayyid_qutb" VALUES(15,33,'15:33','“ اس نے کہا ” میرا یہ کام نہیں ہے کہ میں اس بشر کو سجدہ کروں جسے تو نے سڑی ہوئی مٹی کے سوکھے گارے سے پیدا کیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,34,'15:34','رب نے فرمایا ” اچھا تو نکل جا یہاں سے کیونکہ تو مردود ہے ،');
INSERT INTO "sayyid_qutb" VALUES(15,35,'15:35','اور اب روزاجر تک تجھ پر لعنت ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,36,'15:36','اس نے عرض کیا ” میرے رب ، یہ بات ہے تو پھر مجھے اس روز تک کے لئے مہلت دے جب کہ سب انسان دوبارہ اٹھائے جائیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,37,'15:37','فرمایا ، ” اچھا ، تجھے مہلت ہے اس دن تک');
INSERT INTO "sayyid_qutb" VALUES(15,38,'15:38','جس کا وقت ہمیں معلوم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,39,'15:39','وہ بولا ، ” میرے رب ، جیسا تو نے مجھے بہکایا اسی طرح اب میں زمین میں ان کے لئے دل فریبیاں پیدا کر کے ان سب کو بہکا دوں گا ،');
INSERT INTO "sayyid_qutb" VALUES(15,40,'15:40','سوائے تیرے ان بندوں کے جنہیں تو نے ان میں سے خالص کرلیا ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(15,41,'15:41','فرمایا ” یہ راستہ ہے جو سیدھا مجھ تک پہنچتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,42,'15:42','بیشک جو میرے حقیقی بندے ہیں ان پر تیرا بس نہ چلے گا۔ تیرا بس تو صرف ان بہکے ہوئے لوگوں ہی پر چلے گا جو تیری پیروی کریں “۔');
INSERT INTO "sayyid_qutb" VALUES(15,43,'15:43','اور ان سب کے لئے جہنم کی وعید ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,44,'15:44','یہ جہنم (جس کی وعید پیروان ابلیس کے لئے کی گئی ہے ) اس کے سات دروازے ہیں ، ہر دروازے کے لئے ان میں سے ایک حصہ مخصوص کردیا گیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,45,'15:45','بخلاف اس کے متقی لوگ باغوں اور چشموں میں ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(15,46,'15:46','اور ان سے کہا جائے گا کہ داخل ہوجاؤ ان میں سلامتی کے ساتھ بےخوف و خطر۔');
INSERT INTO "sayyid_qutb" VALUES(15,47,'15:47','ان کے دلوں میں جو تھوڑی بہت کھوٹ کپٹ ہوگی اسے ہم نکال دیں گے ، وہ آپس میں بھائی بھائی بن کر آمنے سامنے تختوں پر بیٹھیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(15,48,'15:48','انہیں نہ وہاں کسی مشقت سے پالا پڑے گا اور نہ وہ وہاں سے نکالے جائیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,49,'15:49','اے نبی ؐ، میرے بندوں کو خبر دے دو کہ میں بہت درگزر کرنے والا اور رحیم ہوں');
INSERT INTO "sayyid_qutb" VALUES(15,50,'15:50','مگر اس کے ساتھ میرا عذاب بھی نہایت دردناک عذاب ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,51,'15:51','اور انہیں ذرا ابراہیم (علیہ السلام) کے مہمانوں کا قصہ سناؤ۔');
INSERT INTO "sayyid_qutb" VALUES(15,52,'15:52','جب وہ آئے اس کے ہاں اور کہا ” سلام ہو تم پر “۔ تو اس نے کہا ” ہمیں تم سے ڈر لگتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,53,'15:53','انہوں نے جواب دیا ۔ ” ڈرو نہیں ، ہم تمہیں ایک بڑے سیانے لڑکے کی بشارت دیتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(15,54,'15:54','ابراہیم نے کہا ” کیا تم اس بڑھاپے میں مجھے اولاد کی بشارت دیتے ہو ؟ ذرا سوچو تو سہی کہ یہ کیسی بشارت تم مجھے دے رہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(15,55,'15:55','“۔ انہوں نے جواب دیا ، ” ہم تمہیں برحق بشارت دے رہے ہیں ، تم مایوس نہ ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(15,56,'15:56','ابراہیم نے کہا ” اپنے رب کی رحمت سے مایوس تو گمراہ لوگ ہی ہوا کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(15,57,'15:57','پھر ابراہیم (علیہ السلام) نے پوچھا ” اے فرستاد گان الٰہی ، وہ مہم کیا ہے جس پر آپ حضرات تشریف لائے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(15,58,'15:58','“ وہ بولے ” ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,59,'15:59','صرف لوط (علیہ السلام) کے گھر والے مستثنیٰ ہیں ، ان سب کو ہم بچالیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(15,60,'15:60','سوائے اس کی بیوی کے جس کے لئے (اللہ فرماتا ہے کہ ) ہم نے مقدر کردیا ہے کہ وہ پیچھے رہ جانے والوں میں شامل رہے گی۔');
INSERT INTO "sayyid_qutb" VALUES(15,61,'15:61','پھر جب یہ فرستادے لوط (علیہ السلام) کے ہاں پہنچے');
INSERT INTO "sayyid_qutb" VALUES(15,62,'15:62','تو اس نے کہا ” آپ لوگ اجنبی معلوم ہوتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(15,63,'15:63','انہوں نے جواب دیا ” نہیں ، بلکہ ہم وہی چیز لے کر آئے ہیں جس کے آنے میں یہ لوگ شک کر رہے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(15,64,'15:64','ہم تم سے سچ کہتے ہیں کہ ہم حق کے ساتھ تمہارے پاس آئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,65,'15:65','لہٰذا اب تم کچھ رات رہے اپنے گھروالوں کو لے کر نکل جاؤ اور خود ان کے پیچھے پیچھے چلو۔ تم میں سے کوئی پلٹ نہ دیکھے ۔ بس سیدھے چلے جاؤ جدھر جانے کا تمہیں حکم دیا جا رہا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,66,'15:66','اور اسے ہم نے اپنا یہ فیصلہ پہنچا دیا کہ صبح ہوتے ہوتے ان لوگوں کی جڑ کاٹ دی جائے گی “۔');
INSERT INTO "sayyid_qutb" VALUES(15,67,'15:67','” اتنے میں شہر کے لوگ خوشی کے مارے بیتا ہو کر لوط (علیہ السلام) کے گھر چڑھ آئے “۔');
INSERT INTO "sayyid_qutb" VALUES(15,68,'15:68','لوط (علیہ السلام) نے کہا ” بھائیوں ، یہ میرے مہمان ہیں ، میری نصیحت نہ کرو ،');
INSERT INTO "sayyid_qutb" VALUES(15,69,'15:69','اللہ سے ڈرو ، مجھے رسوا نہ کرو “۔');
INSERT INTO "sayyid_qutb" VALUES(15,70,'15:70','وہ بولے ” کیا ہم بارہا تمہیں منع نہیں کرچکے کہ دنیا بھر کے ٹھیکے دار نہ بنو ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(15,71,'15:71','” لوط (علیہ السلام) نے (عاجز ہو کر) کہا ” اگر تمہیں کچھ کرنا ہی ہے تو یہ میری بیٹیاں موجود ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(15,72,'15:72','تیری جان کی قسم اے نبی صلى الله عليه وسلم ، اس وقت ان پر ایک نشہ سا چڑھا ہوا تھا جس میں وہ آپے سے باہر ہوئے جاتے تھے ”');
INSERT INTO "sayyid_qutb" VALUES(15,73,'15:73','آخر کار پو پھٹتے ہی ان کو ایک زبردست دھماکے نے آلیا');
INSERT INTO "sayyid_qutb" VALUES(15,74,'15:74','اور ہم نے اس بستی کو تلپٹ کر کے رکھ دیا اور ان پر پکی ہوئی مٹی کے پتھروں کی بارش برسا دی ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,75,'15:75','اس واقعے میں بڑی نشانیاں ہیں ان لوگوں کے لئے جو صاحب فراست ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,76,'15:76','اور وہ علاقہ (جہاں یہ واقعہ پیش آیا تھا ) گزرگاہ عام پر واقع ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,77,'15:77','اس میں سامان عبرت ہے ان لوگوں کے لئے جو صاحب ایمان ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,78,'15:78','اور ایکہ والے ظالم تھے ،');
INSERT INTO "sayyid_qutb" VALUES(15,79,'15:79','تو دیکھ لو ہم نے بھی ان سے انتقام لیا اور ان دونوں قوموں کے اجڑے ہوئے علاقے کھلے راستے پر واقع ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,80,'15:80','حجر کے لوگ بھی رسولوں کی تکذیب کرچکے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(15,81,'15:81','ہم نے اپنی آیات ان کے پاس بھیجیں ، اپنی نشانیاں ان کو دکھائیں ، مگر وہ سب کو نظر انداز ہی کرتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,82,'15:82','وہ پہاڑ تراش تراش کر مکان بناتے تھے اور اپنی جگہ بالکل بےخوف اور مطمئن تھے۔');
INSERT INTO "sayyid_qutb" VALUES(15,83,'15:83','آخر کار ایک زبردست دھماکے نے ان کو صبح ہوتے آلیا');
INSERT INTO "sayyid_qutb" VALUES(15,84,'15:84','اور ان کی کمائی ان کے کچھ کام نہ آئی ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,85,'15:85','“ ہم نے زمین اور آسمانوں کو اور ان کی سب موجودات کو حق کے سوا کسی اور بنیاد پر خلق نہیں کیا ہے ، اور فیصلے کی گھڑی یقیناً آنے والی ہے ، پس اے نبی صلى الله عليه وسلم (ان لوگوں کی بیہودگیوں پر ) شریفانہ درگذر سے کام لو۔');
INSERT INTO "sayyid_qutb" VALUES(15,86,'15:86','یقیناً تمہارا رب سب کا خالق ہے اور سب کچھ جانتا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,87,'15:87','ہم نے تم کو سات ایسی آیتیں دے رکھی ہیں جو بار بار دہرائی جانے کے لائق ہیں ، اور تمہیں قرآن عظیم عطا کیا ہے ”');
INSERT INTO "sayyid_qutb" VALUES(15,88,'15:88','تم اس متاع دنیا کی طرف آنکھ اٹھا کر نہ دیکھو جو ہم نے ان میں سے مختلف قسم کے لوگوں کو دے رکھی ہے اور نہ ان کے حال پر اپنا دل کڑھاؤ۔ انہیں چھوڑ کر ایمان لانے والوں کی طرف جھکو');
INSERT INTO "sayyid_qutb" VALUES(15,89,'15:89','اور (نہ ماننے والوں سے ) کہہ دو کہ “ میں تو صاف صاف تنبیہ کرنے والا ہوں ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,90,'15:90','یہ اسی طرح کی تنبیہ ہے جیسی ہم نے ان تفرقہ پردازوں کی طرف بھیجی تھی');
INSERT INTO "sayyid_qutb" VALUES(15,91,'15:91','جنہوں نے اپنے قرآن کو ٹکڑے ٹکڑے کر ڈالا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(15,92,'15:92','تو قسم ہے تیرے رب کی ، ہم ضرور ان سب سے پوچھیں گے کہ تم کیا کرتے رہے ہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,93,'15:93','پس اے نبی صلى الله عليه وسلم ، جس چیز کا تمہیں حکم دیا جا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(15,94,'15:94','اسے ہانکے پکارے کہہ دو اور شرک کرنے والوں کی ذرا پرواہ نہ کرو۔');
INSERT INTO "sayyid_qutb" VALUES(15,95,'15:95','تمہاری طرف سے ہم ان مذاق اڑانے والوں کی خبر لینے کے لئے کافی ہیں');
INSERT INTO "sayyid_qutb" VALUES(15,96,'15:96','جو اللہ کے ساتھ کسی اور کو بھی خدا قرار دیتے ہیں۔ عنقریب انہیں معلوم ہوجائے گا ”۔');
INSERT INTO "sayyid_qutb" VALUES(15,97,'15:97','ہمیں معلوم ہے کہ جو باتیں یہ لوگ تم پر بناتے ہیں ان سے تمہارے دل کو سخت کوفت ہوتی ہے');
INSERT INTO "sayyid_qutb" VALUES(15,98,'15:98','(اس کا علاج یہ ہے کہ) اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو ، اس کی جناب میں سجدہ بجا لاؤ،');
INSERT INTO "sayyid_qutb" VALUES(15,99,'15:99','اور اس آخری گھڑی تک اپنے رب کی بندگی کرتے رہو ، جس کا آنا یقینی ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,1,'16:1','“ آگیا اللہ کا فیصلہ ، اب اس کے لئے جلدی نہ مچاؤ۔ پاک ہے وہ اور بالا و برتر ہے اس شرک سے جو یہ لوگ کر رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,2,'16:2','وہ اس روح کو اپنے جس بندے پر چاہتا ہے اپنے حکم سے ملائکہ کے ذریعے نازل فرما دیتا ہے (اس ہدایت کے ساتھ کہ لوگوں کو) “ آگاہ کرو ، میرے سوا کوئی تمہارا معبود نہیں ہے ، لہٰذا تم مجھی سے ڈرو ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,3,'16:3','اس نے آسمان و زمین کو برحق پیدا کیا ہے ، وہ بہت بالا و برتر ہے اس شرک سے جو یہ لوگ کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,4,'16:4','اس نے انسان کو ایک ذرا سی بوند سے پیدا کیا اور دیکھتے دیکھتے صریحاً وہ ایک جھگڑا لو ہستی بن گیا ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,5,'16:5','اس نے جانور پیدا کئے ہیں جن میں تمہارے لیے پوشاک بھی ہے اور خوراک بھی ،');
INSERT INTO "sayyid_qutb" VALUES(16,6,'16:6','اور طرح طرح کے دوسرے فائدے بھی ۔ ان میں تمہارے لیے جمال ہے جب کہ صبح تم انہیں چرنے کے لئے بھیجتے ہو اور جب کہ شام انہیں واپس لاتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(16,7,'16:7','وہ تمہارے لیے بوجھ ڈھوکر ایسے ایسے مقامات تک لے جاتے ہیں جہاں تم سخت جانفشانی کے بغیر نہیں پہنچ سکتے۔ حقیقت یہ ہے کہ تمہارا رب بڑا ہی شفیق اور مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,8,'16:8','اس نے گھوڑے اور خچر اور گدھے پیدا کئے تا کہ تم ان پر سوار ہو اور وہ تمہاری زندگی کی رونق بنیں۔ وہ اور بہت سی چیزیں (تمہارے فائدے کے لئے ) پیدا کرتا ہے جن کا تمہیں علم تک نہیں ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,9,'16:9','“ اور اللہ ہی کے ذمہ ہے سیدھا راستہ بتانا جب کہ راستے ٹیڑھے بھی موجود ہیں۔ اگر وہ چاہتا تو تم سب کو ہدایت دے دیتا ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,10,'16:10','وہی ہے جس نے آسمان سے تمہارے لئے پانی برسایا جس سے تم خود بھی سیراب ہوتے ہو اور تمہارے جانوروں کے لئے بھی چارہ پیدا ہوتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,11,'16:11','وہ اس پانی کے ذریعہ سے کھیتیاں اگاتا ہے اور زیتون اور کھجور اور انگور اور طرح طرح کے دوسرے پھل پیدا کرتا ہے۔ اس میں ایک بڑی نشانی ہے ان لوگوں کے لئے جو غوروفکر کرتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,12,'16:12','اس نے تمہاری بھلائی کے لئے رات اور دن کو اور سورج اور چاند کو مسخر کر رکھا ہے اور سب تارے بھی اسی کے حکم سے مسخر ہیں۔ اس میں بہت نشانیاں ہیں ان لوگوں کے لئے جو عقل سے کام لیتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(16,13,'16:13','اور یہ جو بہت سی رنگ برنگ کی چیزیں اس نے تمہارے لیے زمین میں پیدا کر رکھی ہیں ، ان میں بھی ضرور نشانی ہے ان لوگوں کے لئے جو سبق حاصل کرنے والے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(16,14,'16:14','وہی ہے جس نے تمہارے لیے سمندر کو مسخر کر رکھا تا کہ تم اس سے تروتازہ گوشت لے کر کھاؤ اور اس سے زینت کی وہ چیزیں نکالو جنہیں تم پہنا کرتے ہو۔ تم دیکھتے ہو کہ کشتی سمندر کا سینہ چیرتی ہوئی چلتی ہے۔ یہ سب کچھ اس لیے ہے کہ تم اپنے رب کا فضل تلاش کرو اور اس کے شکر گزار بنو “۔');
INSERT INTO "sayyid_qutb" VALUES(16,15,'16:15','اس نے زمین میں پہاڑوں کی میخیں گاڑ دیں تا کہ زمین تم کو کر ڈھلک نہ جائے۔ اس نے دریا جاری کئے اور قدرتی راستے بنائے تا کہ تم ہدایت پاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(16,16,'16:16','اس نے زمین میں راستہ بتانے والی علامتیں رکھ دیں ، اور تاروں سے بھی لوگ ہدایت پاتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(16,17,'16:17','پھر کیا وہ جو پیدا کرتا ہے اور وہ جو کچھ بھی پیدا نہیں کرتے ، دونوں یکساں ہیں ؟ کیا تم ہوش میں نہیں آتے ؟');
INSERT INTO "sayyid_qutb" VALUES(16,18,'16:18','اگر تم اللہ کی نعمتوں کو گننا چاہو تو گن نہیں سکتے ، حقیقت یہ ہے کہ وہ بڑا ہی درگزر کرنے والا اور رحیم ہے ،');
INSERT INTO "sayyid_qutb" VALUES(16,19,'16:19','حالانکہ وہ تمہارے کھلے سے بھی واقف ہے اور چھپے سے بھی۔');
INSERT INTO "sayyid_qutb" VALUES(16,20,'16:20','” اور وہ دوسری ہستیاں جنہیں اللہ کو چھوڑ کر لوگ پکارتے ہیں ، وہ کسی چیز کی بھی خالق نہیں ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(16,21,'16:21','مردہ ہیں نہ کہ زندہ۔ اور ان کو کچھ معلوم نہیں کہ انہیں کب اٹھایا جائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(16,22,'16:22','“ تمہارا خدا ایک ہی خدا ہے مگر جو لوگ آخرت کو نہیں مانتے ان کے دلوں میں انکار بس کر رہ گیا ہے اور وہ گھمنڈ میں پڑگئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,23,'16:23','اللہ یقیناً ان کے سب کرتوت جانتا ہے ، چھپے ہوئے بھی اور کھلے ہوئے بھی۔ وہ ان لوگوں کو ہرگز پسند نہیں کرتا جو غرور نفس میں مبتلا ہوں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,24,'16:24','اور جب کوئی ان سے پوچھتا ہے کہ تمہارے رب نے یہ کیا چیز نازل کی ہے ، تو کہتے ہیں “ اجی وہ تو اگلے وقتوں کی فرسودہ کہانیاں ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,25,'16:25','یہ باتیں وہ اس لیے کرتے ہیں کہ قیامت کے روز اپنے بوجھ بھی پورے اٹھائیں اور ساتھ ساتھ کچھ ان لوگوں کے بوجھ بھی سمیٹیں جنہیں یہ بربنائے جہالت گمراہ کر رہے ہیں۔ دیکھو ! کیسی سخت ذمہ داری ہے جو یہ اپنے سرلے رہے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,26,'16:26','ان سے پہلے بھی بہت سے لوگ (حق کو نیچا دکھانے کے لئے ) ایسے ہی مکاریاں کرچکے ہیں ، تو دیکھ لو کہ اللہ نے ان کے مکر کی عمارت جڑ سے اکھاڑ پھینکی اور اس کی چھت اوپر سے ان کے سر پر آرہی اور ایسے رخ سے ان پر عذاب آیا جدھر سے اس کے آنے کا ان کو گمان تک نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(16,27,'16:27','پھر قیامت کے روز اللہ انہیں ذلیل و خوار کرے گا اور ان سے کہے گا “ بتاؤ اب کہاں ہیں میرے وہ شریک جن کے لئے تم (اہل حق سے ) جھگڑے کیا کرتے تھے ؟ ” جن لوگوں کو دنیا میں علم حاصل تھا وہ کہیں گے “ آج رسوائی اور بدبختی ہے کافروں کے لئے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,28,'16:28','ہاں ، انہی کافروں کے لئے جو اپنے نفس پر ظلم کرتے ہوئے جب ملائکہ کے ہاتھوں گرفتار ہوتے ہیں تو (سرکشی چھوڑ کر ) فوراً ڈگیں ڈال دیتے ہیں اور کہتے ہیں “ ہم تو کوئی قصور نہیں کر رہے تھے ”۔ ملائکہ جواب دیتے ہیں “ کر کیسے نہیں زہے تھے ! اللہ تمہارے کرتوتوں سے خوب واقف ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,29,'16:29','اب جاؤ، جہنم کے دروازوں میں گھس جاؤ۔ وہیں تم کو ہمیشہ رہنا ہے ”۔ پس حقیقت یہ ہے کہ بڑا ہی برا ٹھکانا ہے متکبروں کے لئے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,30,'16:30','دوسری طرف جب خدا ترس لوگوں سے پوچھا جاتا ہے کہ یہ کیا چیز ہے جو تمہارے رب کی طرف سے نازل ہوئی ہے ، تو وہ جواب دیتے ہیں کہ “ بہترین چیز اتری ہے ”۔ اس طرح کے نیکو کار لوگوں کے لئے اس دنیا میں بھی بھلائی ہے اور آخرت کا گھر تو ضرور ہی ان کے حق میں بہتر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,31,'16:31','بڑا ا چھا گھر ہے متقیوں کا ، دائمی قیام کی جنتیں ، جن میں وہ داخل ہوں گے ، نیچے نہریں بہہ رہی ہوں گی ، اور سب کچھ وہاں عین ان کی خواہش کے مطابق ہوگا۔ یہ جزا دیتا ہے اللہ متقویں کو۔');
INSERT INTO "sayyid_qutb" VALUES(16,32,'16:32','ان متقیوں کو جن کی روحیں پاکیزگی کی حالت میں جب ملائکہ قبض کرتے ہیں تو کہتے ہیں “ سلام ہو تم پر ، جاؤ جنت میں اپنے اعمال کے بدلے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,33,'16:33','اے نبی صلى الله عليه وسلم اب جو یہ لوگ انتظار کر رہے ہیں تو اس کے سوا اب اور کیا باقی رہ گیا ہے کہ ملائکہ ہی آپہنچیں یا تمہارے رب کا فیصلہ صادر ہوجائے ؟ اسی طرح کی ڈھٹائی ان سے پہلے بہت سے لوگ کرچکے ہیں۔ پھر جو کچھ ان کے ساتھ ہوا وہ ان پر اللہ کا ظلم نہ تھا بلکہ ان کا اپنا ظلم تھا جو انہوں نے خود اپنے اوپر کیا۔');
INSERT INTO "sayyid_qutb" VALUES(16,34,'16:34','ان کے کرتوتوں کی خرابیاں آخر کار ان کی دامن گیر ہوگئیں اور وہی ان پر مسلط ہو کر رہی جس کا وہ مذاق اڑایا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(16,35,'16:35','یہ مشرکین کہتے ہیں “ اگر اللہ چاہتا تو نہ ہم اور نہ ہمارے باپ دادا اس کے سوا کسی اور کی عبادت کرتے اور نہ اس کے حکم کے بغیر کسی چیز کو حرام ٹھہراتے ”۔ ایسے ہی بہانے ان سے پہلے کے لوگ بھی بناتے رہے ہیں۔ تو کیا رسولوں پر صاف صاف بات پہنچا دینے کے سوا اور بھی کوئی ذمہ داری ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(16,36,'16:36','ہم نے ہر امت میں ایک رسول بھیج دیا ، اور اس کے ذریعہ سے سب کو خبردار کردیا کہ “ اللہ کی بندگی کرو اور طاغوت کی بندگی سے بچو ”۔ اس کے بعد ان میں سے کسی کو اللہ نے ہدایت بخشی اور کسی پر ضلالت مسلط ہوگئی۔ پھر ذرا زمین میں چل پھر کر دیکھ لو کہ جھٹلانے والوں کا کیا انجام ہوچکا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,37,'16:37','” اے نبی صلى الله عليه وسلم تم چاہے ان کی ہدایت کے لئے کتنے ہی حریص ہو ، مگر اللہ جس کو بھٹکا دیتا ہے پھر اسے ہدایت نہیں دیا کرتا اور اس طرح کے لوگوں کی مدد کوئی نہیں کرسکتا “۔');
INSERT INTO "sayyid_qutb" VALUES(16,38,'16:38','” یہ لوگ اللہ کے نام سے کڑی کڑی قسمیں کھا کر کہتے ہیں کہ ” اللہ کسی مرنے والے کو پھر سے زندہ کر کے نہ اٹھائے گا “۔۔۔۔ اٹھائے گا کیوں نہیں ؟ یہ تو ایک وعدہ ہے جسے پورا کرنا اس نے اپنے اوپر واجب کرلیا ہے ، مگر اکثر لوگ جانتے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,39,'16:39','اور ایسا ہونا اس لیے ضروری ہے کہ اللہ ان کے سامنے اس حقیقت کو کھول دے جس کے بارے میں یہ اختلاف کر رہے ہیں ، اور منکرین حق کو معلوم ہوجائے کہ وہ جھوٹے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(16,40,'16:40','(رہا اس کا امکان تو ) ہمیں کسی چیز کو وجود میں لانے کے لئے اس سے زیادہ کچھ کرنا نہیں ہوتا کہ اسے حکم دیں ” ہوجا “ اور بس وہ ہوجاتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(16,41,'16:41','اور جو لوگ ظلم سہنے کے بعد اللہ کی خاطر ہجرت کر گئے ہیں ان کو ہم دنیا ہی میں اچھا ٹھکانا دیں گے اور آخرت کا اجر تو بہت بڑا ہے ۔ کاش جان لیں');
INSERT INTO "sayyid_qutb" VALUES(16,42,'16:42','وہ مظلوم جنہوں نے صبر کیا ہے اور جو اپنے رب کے بھروسے پر کام کر رہے ہیں۔ (کہ کیسا اچھا انجام ان کا منتظر ہے “ ) ۔');
INSERT INTO "sayyid_qutb" VALUES(16,43,'16:43','اے نبی صلى الله عليه وسلم ہم نے تم سے پہلے بھی جب کبھی رسول بھیجے ہیں ، آدمی ہی بھیجے ہیں جن کی طرف ہم اپنے پیغامات وحی کیا کرتے تھے ، اہل ذکر سے پوچھ لو ، اگر تم لوگ خود نہیں جانتے۔');
INSERT INTO "sayyid_qutb" VALUES(16,44,'16:44','پچھلے رسولوں کو بھی ہم نے روشن نشانیاں اور کتابیں دے کر بھیجا تھا وار اب یہ ذکر تم پر نازل کیا ہے تا کہ تم لوگوں کے سامنے اس تعلیم کی تشریح و توضیح کرتے جاؤ جو ان کے لئے اتاری گئی ہے اور تا کہ لوگ (خود بھی ) غورو فکر کریں “۔');
INSERT INTO "sayyid_qutb" VALUES(16,45,'16:45','پھر کیا وہ لوگ جو (دعوت پیغمبر کی مخالفت میں ) بدتر سے بدتر چالیں چل رہے ہیں ، اس بات سے بالکل ہی بےخوف ہوگئے ہیں کہ اللہ ان کو زمین میں دھنسا دے ، یا ایسے گوشے سے ان پر عذاب لے آئے جدھر سے اس کے آنے کا ان کو وہم و گمان تک نہ ہو ،');
INSERT INTO "sayyid_qutb" VALUES(16,46,'16:46','یا اچانک چلتے پھرتے ان کو پکڑ لے ، یا ایسی حالت میں اور وہ اس سے بچنے کی فکر میں چوکنے ہوں ؟ وہ جو کچھ بھی کرنا چاہے یہ لوگ اس کو عاجز کرنے کی طاقت نہیں رکھتے۔');
INSERT INTO "sayyid_qutb" VALUES(16,47,'16:47','انہیں پکڑے جب کہ انہیں خود آنے والی مصیبت کا کھٹکا لگا ہوا ہو حقیقت یہ ہے کہ تمہارا رب بڑا ہی نرم خو اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,48,'16:48','اور کیا یہ لوگ اللہ کی پیدا کی ہوئی کسی چیز کو بھی نہیں دیکھتے کہ اس کا سایہ کس طرح اللہ کے حضور سجدہ کرتے ہوئے دائیں اور بائیں گرتا ہے ؟ سب کے سب اسی طرح اظہار عجز کر رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,49,'16:49','زمین اور آسمانوں میں جس قدر جان دار مخلوقات ہیں اور جتنے ملائکہ ، سب اللہ کے آگے سربسجود ہیں۔ وہ ہرگز سرکشی نہیں کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(16,50,'16:50','اپنے رب سے جو ان کے اوپر ہے ، ڈرتے ہیں اور جو کچھ حکم دیا جاتا ہے اسی کے مطابق کام کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(16,51,'16:51','اللہ کا فرمان ہے کہ ” دو خدا نہ بنا لو ، خدا تو بس ایک ہی ہے ، لہٰذا تم مجھی سے ڈرو “۔');
INSERT INTO "sayyid_qutb" VALUES(16,52,'16:52','اس کا ہے وہ سب کچھ جو آسمانوں میں ہے اور جو کچھ زمین میں ہے ، اور خالصتاً اسی کا دین (ساری کائنات میں ) چل رہا ہے ۔ پھر کیا اللہ کو چھوڑ کر تم کسی اور سے تقویٰ کرو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(16,53,'16:53','تم کو جو نعمت بھی حاصل ہے اللہ ہی کی طرف سے ہے۔ پھر جب کوئی سخت وقت تم پر آتا ہے تو تم لوگ خود اپنی فریادیں لے کر اسی کی طرف دوڑتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(16,54,'16:54','مگر جب اللہ اس وقت کو ٹال دیتا ہے تو یکایک تم میں سے ایک گروہ اپنے رب کے ساتھ دوسروں کو (اس مہربانی کے شکریے میں ) شریک کرنے لگتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(16,55,'16:55','تا کہ اللہ کے احسان کی ناشکری کرے۔ اچھا ، مزے کرلو ، عنقریب تمہیں معلوم ہوجائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(16,56,'16:56','یہ لوگ جن کی حقیقت سے واقف نہیں ہیں ان کے حصے ہمارے دئیے ہوئے رزق میں سے مقرر کرتے ہیں ”۔ خدا کی قسم ، ضرور تم سے پوچھا جائے گا کہ یہ جھوٹ تم نے کیسے گھڑ لیے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,57,'16:57','یہ خدا کے لئے بیٹیاں تجویز کرتے ہیں۔ سبحان اللہ ! اور ان کے لئے وہ جو یہ خود چاہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(16,58,'16:58','جب ان میں سے کسی کو بیٹی کے پیدا ہونے کی خوشخبری دی جاتی ہے تو اس کے چہرے پر کلونس چھا جاتی ہے اور وہ بس خون کا سا گھونٹ پی کر رہ جاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,59,'16:59','لوگوں سے چھپتا پھرتا ہے کہ اس بری خبر کے بعد کیا کسی کو منہ دکھائے۔ سوچتا ہے کہ ذلت کے ساتھ بیٹی کو لیے رہے یا مٹی میں دبا دے ؟ دیکھو کیسے برے حکم ہیں جو یہ خدا کے بارے میں لگاتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,60,'16:60','“ بری صفات سے متصف کئے جانے کے لائق تو وہ لوگ ہیں جو آخرت کا یقین نہیں رکھتے۔ رہا اللہ تو اس کے لئے سب سے برتر صفات ہیں ، وہی تو سب پر غالب اور حکمت میں کامل ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,61,'16:61','“ اگر کہیں اللہ لوگوں کو ان کی زیادتی پر فوراً ہی پکڑ لیا کرتا تو روئے زمین پر کسی متنفس کو نہ چھوڑتا۔ لیکن وہ سب کو ایک وقت مقرر تک مہلت دیتا ہے ، پھر جب وہ وقت آجاتا ہے تو اس سے کوئی ایک گھڑی بھر بھی آگے پیچھے نہیں ہو سکتا ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,62,'16:62','آج یہ لوگ وہ چیزیں اللہ کے لئے تجویز کر رہے ہیں جو خود اپنے لیے انہیں ناپسند ہیں ، اور جھوٹ کہتی ہیں ان کی زبانیں کہ ان کے لئے بھلا ہی بھلا ہے۔ ان کے لئے تو ایک ہی چیز ہے اور وہ ہے دوزخ کی آگ۔ ضرور یہ سب سے پہلے اس میں پہنچائے جائیں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,63,'16:63','خدا کی قسم ، اے نبی صلى الله عليه وسلم تم سے پہلے بھی بہت سی قوموں میں ہم رسول بھیج چکے ہیں (اور پہلے بھی یہی ہوتا رہا ہے کہ ) شیطان نے ان کے برے کرتوت انہیں خوشنما بنا کر دکھائے ( رسولوں کی بات انہوں نے مان کر نہ دی ) ۔ وہی شیطان آج ان لوگوں کا بھی سر پرست بنا ہوا ہے اور یہ دردناک سزا کے مستحق بن رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,64,'16:64','ہم نے یہ کتاب تم پر اس لیے نازل کی ہے کہ تم ان اختلافات کی حقیقت ان پر کھول دو جن میں یہ پڑے ہوئے ہیں۔ یہ کتاب رہنمائی اور رحمت بن کر اتری ہے ان لوگوں کے لئے جو اسے مان لیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,65,'16:65','(تم ہر برسات میں دیکھتے ہو کہ ) اللہ نے آسمان سے پانی برسایا اور یکایک مردہ پڑی ہوئی زمین میں اس کی بدولت جان ڈال دی ۔ یقیناً اس میں ایک نشانی ہے سننے والوں کے لئے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,66,'16:66','اور تمہارے لیے مویشیوں میں بھی ایک سبق موجود ہے۔ ان کے پیٹ سے گوبر اور خون کے درمیان ہم ایک چیز تمہیں پلاتے ہیں ، یعنی خالص دودھ ، جو پینے والوں کے لئے نہایت خوشگوار ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,67,'16:67','(اسی طرح) کھجور کے درختوں اور انگور کی بیلوں سے بھی ہم ایک چیز تمہیں پلاتے ہیں جسے تم نشہ آور بھی بنا لیتے ہو اور پاک رزق بھی۔ یقیناً اس میں ایک نشانی ہے عقل سے کام لینے والوں کے لئے ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,68,'16:68','اور دیکھو ، تمہارے رب نے شہد کی مکھی پر یہ بات وحی کردی کہ پہاڑوں ، اور درختوں میں ، اور ٹٹیوں پر چڑھائی ہوئی بیلوں میں ،');
INSERT INTO "sayyid_qutb" VALUES(16,69,'16:69','اپنے چھتے بنا ، اور ہر طرح کے پھلوں کا رس چوس ، اور اپنے رب کی ہموار کی ہوئی راہوں پر چلتی رہ۔ اس مکھی کے اندر سے رنگ برنگ کا ایک شربت نکلتا ہے جس میں شفا ہے لوگوں کے لئے۔ یقیناً اس میں بھی ایک نشانی ہے ان لوگوں کے لئے جو غوروفکر کرتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,70,'16:70','“ اور دیکھو ، اللہ نے تم کو پیدا کیا ، پھر وہ تم کو موت دیتا ہے ، اور تم میں سے کوئی بدترین عمر کو پہنچا دیا جاتا ہے تا کہ سب کچھ جاننے کے بعد پھر کچھ نہ جانے۔ حق یہ ہے کہ اللہ ہی علم میں بھی کامل ہے اور قدرت میں بھی۔');
INSERT INTO "sayyid_qutb" VALUES(16,71,'16:71','اور دیکھو اللہ نے تم میں سے بعض کو بعض پر رزق میں فضیلت عطا کی ہے۔ پھر جن لوگوں کو یہ فضیلت دی گئی ہے وہ ایسے نہیں کہ اپنا رزق اپنے غلاموں کی طرف پھیر دیا کرتے ہوں تا کہ دونوں اس رزق میں برابر کے حصہ دار بن جائیں۔ تو کیا اللہ ہی کا احسان ماننے سے ان لوگوں کو انکار ہے ؟ ”');
INSERT INTO "sayyid_qutb" VALUES(16,72,'16:72','اور وہ اللہ ہی ہے جس نے تمہارے لئے تمہاری ہم جنس بیویاں بنائیں اور اسی نے ان بیویوں سے تمہیں بیٹے پوتے عطا کئے اور اچھی اچھی چیزیں تمہیں کھانے کو دیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,73,'16:73','پھر کیا یہ لوگ (یہ سب کچھ دیکھتے اور جانتے ہوئے بھی ) باطل کو مانتے ہیں اور اللہ کے احسان کا انکار کرتے ہیں اور اللہ کو چھوڑ کر ان کو پوجتے ہیں جن کے ہاتھ میں نہ آسمانوں سے انہیں کچھ بھی رزق دیتا ہے نہ زمین سے اور نہ یہ کام وہ کر ہی سکتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,74,'16:74','پس اللہ کے لئے مثالیں نہ گھڑو ، اللہ جانتا ہے ، تم نہیں جانتے ”۔ اللہ کی نہ کوئی مثال ہے اور نہ اس کے کوئی مماثل ہے کہ تم اس کی مثالیں دو ۔');
INSERT INTO "sayyid_qutb" VALUES(16,75,'16:75','اللہ ایک مثال دیتا ہے۔ ایک تو غلام ہے ، جو دوسری کا مملوک ہے اور خود کوئی اختیار نہیں رکھتا۔ دوسرا شخص ایسا ہے جسے ہم نے اپنی طرف سے اچھا رزق عطا کیا ہے اور وہ اس میں سے کھلے اور چھپے خوب خرچ کرتا ہے۔ بتاؤ، کیا یہ دونوں برابر ہیں ؟ ۔۔۔ الحمد للہ ، مگر اکثر لوگ (اس سیدھی بات کو ) نہیں جانتے۔');
INSERT INTO "sayyid_qutb" VALUES(16,76,'16:76','اللہ ایک اور مثال دیتا ہے ۔ دو آدمی ہیں۔ ایک گونگا بہرا ہے ، کوئی کام نہیں کرسکتا ، اپنے آقا پر بوجھ بنا ہوا ہے ، جدھر بھی وہ اسے بھیجے کوئی بھلا کام اس سے بن نہ آئے۔ دوسرا شخص ایسا ہے کہ انصاف کا حکم دیتا ہے اور خود راہ راست پر قائم ہے۔ بتاؤ کیا یہ دونوں یکساں ہیں ؟ ”۔');
INSERT INTO "sayyid_qutb" VALUES(16,77,'16:77','اور زمین و آسمان کے پوشیدہ حقائق کا علم تو اللہ ہی کو ہے اور قیامت کے برپا ہونے کا معاملہ کچھ دیر نہ لے گا مگر بس اتنی ہی جس میں آدمی کی پلک جھپک جائے ، بلکہ اس سے بھی کچھ کم۔ حقیقت یہ ہے کہ اللہ سب کچھ کرسکتا ہے');
INSERT INTO "sayyid_qutb" VALUES(16,78,'16:78','اللہ نے تم کو تمہاری مائوں کے پیٹوں سے نکالا اس حالت میں کہ تم کچھ نہ جانتے تھے اس نے تمہیں کان دئیے ، آنکھیں دیں اور سوچنے والے دل دئیے ، اس لئے کہ تم شکر گزار بنو');
INSERT INTO "sayyid_qutb" VALUES(16,79,'16:79','کیا ان لوگوں نے کبھی پرندوں کو نہیں دیکھا کہ فضائے آسمانی میں کس طرح مسخر ہیں ؟ اللہ کے سوا کس نے ان کو تھام رکھا ہے ؟ اس میں بہت سی نشانیاں ہیں ان لوگوں کے لئے جو ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(16,80,'16:80','اس نے جانوروں کی کھالوں سے تمہارے لئے ایسے مکان پیدا کیے جنہیں تم سفر اور قیام ، دونوں حالتوں میں ہلکا پاتے ہو۔ اس نے جانوروں کے صوف اور اون اور بالوں سے تمہارے لئے پہننے اور برتنے کی بہت سی چیزیں پیدا کردیں جو زندگی کی مدت مقررہ تک تمہارے کام آتی ہیں');
INSERT INTO "sayyid_qutb" VALUES(16,81,'16:81','” اللہ نے تمہارے لئے تمہارے گھروں کو جائے سکون بنایا۔ اس نے جانوروں کی کھالوں سے تمہارے لئے ایسے مکان پیدا کیے جنہیں تم سفر اور قیام ، دونوں حالتوں میں ہلکا پاتے ہو۔ اس نے جانوروں کے صوف اور اون اور بالوں سے تمہارے لئے پہننے اور برتنے کی بہت سی چیزیں پیدا کردیں جو زندگی کی مدت مقررہ تک تمہارے کام آتی ہیں۔ اس نے اپنی پیدا کی ہوئی بہت سی چیزوں سے تمہارے لئے سائے کا انتظام کیا ، پہاڑوں میں تمہارے لئے پناہ گاہیں بنائیں ، اور تمہیں ایسے پوشاکیں بخشیں جو تمہیں گرمی سے بچاتی ہیں۔ اور کچھ دوسری پوشاکیں جو آپس کی جنگ میں تمہاری حفاظت کرتی ہیں۔ اس طرح وہ تم پر اپنی نعمتوں کی تکمیل کرتا ہے شاید کہ تم فرمانبردار بنو');
INSERT INTO "sayyid_qutb" VALUES(16,82,'16:82','اب اگر یہ لوگ منہ موڑتے ہیں تو اے نبی صلى الله عليه وسلم تم پر صاف صاف پیغام حق پہنچا دینے کے سوا اور کوئی ذمہ داری نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,83,'16:83','یہ اللہ کے احسان کو پہنچانتے ہیں ، پھر اس کا انکار کرتے ہیں اور ان میں بیش تر لوگ ایسے ہیں جو حق ماننے کے لئے تیار نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(16,84,'16:84','انہیں کچھ ہوش بھی ہے کہ اس روز کیا بنے گا) جب کہ ہم ہر امت میں سے ایک گواہ کھڑا کریں گے ، پھر کافروں کو نہ حجتیں پیش کرنے کا موقع دیا جائے گا ، نہ ان سے توبہ و استغفار ہی کا مطالبہ کیا جائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(16,85,'16:85','ظالم لوگ جب ایک دفعہ عذاب دیکھ لیں گے تو اس کے بعد نہ ان کے عذاب میں کوئی تخفیف کی جائے گی اور نہ انہیں ایک لمحہ بھر مہلت دی جائے گی۔');
INSERT INTO "sayyid_qutb" VALUES(16,86,'16:86','اور جب وہ لوگ جنہوں نے دنیا میں شرک کیا تھا اپنے ٹھہرائے ہوئے شریکوں کو دیکھیں گے تو کہیں گے ” اے پروردگار ، یہی ہیں ہمارے وہ شریک جنہیں ہم تجھے چھوڑ کر پکارا کرتے تھے “۔ اس پر ان کے وہ معبود انہیں صاف جواب دیں گے کہ ” تم جھوٹے ہو “');
INSERT INTO "sayyid_qutb" VALUES(16,87,'16:87','(۔ اس وقت یہ سب اللہ کے آگے جھک جائیں گے اور ان کی وہ ساری افتراء پر دازیاں رفو چکر ہوجائیں گی جو یہ دنیا میں کرتے رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(16,88,'16:88','” جن لوگوں نے خود کفر کی راہ اختیار کی اور دوسروں کو اللہ کی راہ سے روکا انہیں ہم عذاب پر عذاب دیں گے۔ اس فساد کے بدلے میں جو وہ دنیا میں برپا کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(16,89,'16:89','اے نبی انہیں اس دن سے خبر دار کردو) جب کہ ہم ہر امت میں خود اسی کے ۔۔ سے ایک گواہ اٹھا کھڑا کریں گے جو اس کے مقابلہ میں شہادت دے گا ، اور ان لوگوں کے مقابلے میں شہادت دینے کے لئے ہم تمہیں لائیں گے۔ اور (یہ اسی شہادت کی تیاری ہے کہ ) ہم نے یہ کتاب تم پر نازل کردی ہے جو ہر چیز کی صاف صاف وضاحت کرنے والی ہے اور ہدایت و رحمت اور بشارت ہے ان لوگوں کے لئے جنہوں نے سر تسلیم خم کردیا ہے');
INSERT INTO "sayyid_qutb" VALUES(16,90,'16:90','اللہ عدل اور احسان اور صلہ رحمی کا حکم دیتا ہے اور بدی و بےحیائی اور ظلم و زیادتی سے منع کرتا ہے۔ وہ تمہیں نصیحت کرتا ہے تاکہ تم سبق لو۔');
INSERT INTO "sayyid_qutb" VALUES(16,91,'16:91','اللہ کے عہد کو پورا کرو جبکہ تم نے اس سے کوئی عہد باندھا ہو ، اور اپنی قسمیں پختہ کرنے کے بعد توڑ نہ ڈالو جبکہ تم اللہ کو اپنے اوپر گواہ بنا چکے ہو۔ اللہ تمہارے سب افعال سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,92,'16:92','تمہاری حالت اس عورت کی سی نہ ہوجائے جس نے آپ ہی محنت سے سوت کاتا اور پھر آپ ہی اس کو ٹکڑے ٹکڑے کر ڈالا۔ تم اپنی قسموں کو آپس کے معاملات میں مکر و فریب کا ہتھیار بناتے ہو تاکہ ایک قوم دوسری قوم سے بڑھ کر فائدے حاصل کرے۔ حالانکہ اللہ اس عہد و پیمان کے ذریعہ سے تم کو آزمائش میں ڈالتا ہے ، اور ضرور وہ قیامت کے روز تمہارے تمام اختلافات کی حقیقت تم پر کھول دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(16,93,'16:93','اگر اللہ کی مشیت یہ ہوتی (کہ تم میں کوئی اختلاف نہ ہو) تو وہ تم سب کو ایک ہی امت بنا دیتا ، مگر وہ جسے چاہتا ہے گمراہی میں ڈالتا ہے اور جسے چاہتا ہے راہ راست دکھا دیتا ہے ، اور پھر ضرور تم سے تمہارے اعمال کی باز پرس ہو کر رہے گی');
INSERT INTO "sayyid_qutb" VALUES(16,94,'16:94','”(اور اے مسلمانو) تم اپنی قسموں کو آپس میں ایک دوسرے کو دھوکہ دینے کا ذریعہ نہ بنا لینا ، کہیں ایسا نہ ہو کہ کوئی قدم جمنے کے بعد اکھڑ جائے اور تم اس جرم کی پاداش میں کہ تم نے لوگوں کو اللہ کی راہ سے روکا ، برا نتیجہ دیکھو اور سزا بھگتو۔');
INSERT INTO "sayyid_qutb" VALUES(16,95,'16:95','اللہ کے عہد کو تھوڑے سے فائدے کے بدلے نہ بیچ ڈالو ، جو کچ اللہ کے پاس ہے وہ تمہارے لئے زیادہ بہتر ہے اگر تم جانو۔');
INSERT INTO "sayyid_qutb" VALUES(16,96,'16:96','جو کچھ تمہارے پاس ہے وہ خرچ ہوجانے والا ہے اور جو کچھ اللہ کے پاس ہے وہی باقی رہنے والا ہے۔ اور ہم ضروری صبر سے کام لینے والوں کو ان کے اجر ان کے بہترین اعمال کے مطابق دیں گے');
INSERT INTO "sayyid_qutb" VALUES(16,97,'16:97','جو شخص بھی نیک عمل کرے گا ، خواہ وہ مرد ہو یا عورت ، بشرطیکہ ہو وہ مومن ، اسے ہم دنیا میں پاکیزہ زندگی بسر کرائیں گے اور (آخرت میں) ایسے لوگوں کو ان کے اجر ان کے بہترین اعمال کے مطابق بخشیں گے');
INSERT INTO "sayyid_qutb" VALUES(16,98,'16:98','پھر جب تم قرآن پڑھنے لگو تو شیطان رجیم سے خدا کی پناہ مانگ لیا کرو۔');
INSERT INTO "sayyid_qutb" VALUES(16,99,'16:99','اسے ان لوگوں پر تسلط حاصل نہیں ہوتا جو یمان لاتے اور اپنے رب پر بھروسہ کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,100,'16:100','اس کا زور تو انہی لوگوں پر چلتا ہے جو اس کو اپنا سر پرست بناتے اور اس کے بہکانے سے شرک کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,101,'16:101','جب ہم ایک آیت کی جگہ دوسری آیات نازل کرتے ہیں... اور اللہ بہتر جانتا ہے کہ وہ کیا نازل کرے... تو یہ لوگ کہتے ہیں کہ تم یہ قرآن خود گھڑتے ہو۔ اصل بات یہ ہے کہ ان میں سے اکثر لوگ حقیقت سے ناواقف ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,102,'16:102','ان سے کہو کہ اسے تو روح القدس نے ٹھیک ٹھیک میرے رب کی طرف سے بتدریج نازل کیا ہے تاکہ ایمان لانے والوں کے ایمان کو پختہ کرے اور فرماں برداروں کو زندگی کے معاملات میں سیدھی راہ بتائے اور انہیں فلاح وسعادت کی خوشخبری دے۔');
INSERT INTO "sayyid_qutb" VALUES(16,103,'16:103','ہمیں معلوم ہے یہ لوگ تمہارے متعلق کہتے ہیں کہ اس شخص کو ایک آدمی سکھاتا پڑھاتا ہے۔ حالانکہ ان کا اشارہ جس آدمی کی طرف ہے اس کی زبان عجمی ہے اور یہ صاف عربی زبان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,104,'16:104','حقیقت یہ ہے کہ جو لوگ اللہ کی آیات کو نہیں مانتے ، اللہ کبھی ان کو صحیح بات تک پہنچنے کی توفیق نہیں دیتا اور ایسے لوگوں کے لئے دردناک عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,105,'16:105','(جھوٹی باتیں نبی نہیں گھڑتا بلکہ) جھوٹ وہ لوگ گھڑ رہے ہیں جو اللہ کی آیات کو نہیں مانتے ، وہی حقیقت میں جھوٹے ہیں ‘');
INSERT INTO "sayyid_qutb" VALUES(16,106,'16:106','جو شخص ایمان لانے کے بعد کفر کرے ( وہ اگر ) مجبور کیا گہو ہو اور دل اس کا ایمان پر مطمئن ہو (تب تو خیر) مگر جس نے دل کی رضا مندی سے کفر کو قبول کرلیا اس پر اللہ کا غضب ہے اور ایسے سب لوگوں کے لئے بڑا عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,107,'16:107','یہ اس لئے کہ انہوں نے آخرت کے مقابلہ میں دنیا کی زندگی کو پسند کرلیا ، اور اللہ کا قاعدہ ہے کہ وہ ان لوگوں کو راہ نجات نہیں دکھاتا جو اس کی نعمت کا کفران کریں۔');
INSERT INTO "sayyid_qutb" VALUES(16,108,'16:108','یہ وہ لوگ ہیں جن کے دلوں اور کانوں اور آنکھوں پر اللہ نے مہر لگا دی ہے ، یہ غفلت میں ڈوب چکے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,109,'16:109','ضرور ہے کہ آخرت میں یہی خسارے میں رہیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,110,'16:110','بخلف اس کے جن لوگوں کا حال یہ ہے کہ جب (ایمان لانے کی وجہ سے ) وہ ساتئے گئے تو انہوں نے گھر بار چھوڑ دئیے۔ ہجرت کی ، راہ خدا میں سختیاں جھیلیں اور صبر سے کام کیا۔ ان کے لئے یقینا تیرا رب غفورو رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,111,'16:111','(ان سب کا فیصلہ اس دن ہوگا) جب کہ ہر متنفس اپنے ہی بچائو کی فکر میں لگا ہوا ہوگا اور ہر ایک کو اس کے کیے کا بدلہ پورا پورا دیا جائے گا اور کسی پر ذرہ برابر ظلم نہ ہونے پائے گا');
INSERT INTO "sayyid_qutb" VALUES(16,112,'16:112','اللہ ایک بستی کی مثال دیتا ہے وہ امن و اطمینان کی زندگی بسر کر رہی تھی اور ہر طرف سے اس کو بفراغت رزق پہنچ رہا تھا کہ اس نے اللہ کی نعمتوں کا کفران شروع کردیا۔ تب اللہ نے اس کے باشندوں کو ان کے کرتوتوں کو یہ مزا چکھایا کہ بھوک اور خوف کی مصیبیں ان پر چھا گئیں۔');
INSERT INTO "sayyid_qutb" VALUES(16,113,'16:113','ان کے پاس ان کی اپنی قوم میں سے ایک رسول آیا مگر انہوں نے اس کو جھٹلا دیا۔ آخر کار عذاب نے ان کو آلیا جبکہ وہ ظالم ہوچکے تھے');
INSERT INTO "sayyid_qutb" VALUES(16,114,'16:114','پس اے لوگو ، اللہ نے جو کچھ حلال اور پاک رزق تم کو بخشا ہے اسے کھائو اور اللہ کے احسان کا شکر ادا کرو اگر تم واقعی اس کی بندگی کرنے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(16,115,'16:115','اللہ نے جو کچھ تم پر حرام کیا ہے وہ ہے مردار ، خون ار سور کا گوشت اور وہ جانور جس پر اللہ کے سوا کسی اور کا نام لیا گیا ہو بھوک سے مجبور اور بےقرار ہو کر اگر کوئی ان چیزوں کو کھانے ، بغیر اس کے کہ وہ قانون الٰہی کی خلاف ورزی کا خواہش مند ہو یا حد ضرورت سے تجاوز کا مرتکب ہو ، تو یقینا اللہ معاف کرنے اور رحم فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(16,116,'16:116','اور یہ جو تمہاری زبانیں جھوٹے احکام لگایا کرتی ہیں کہ یہ چیز حلال ہے اور وہ حرام ، تو اس طرح کے حکم لگا کر اللہ پر جھوٹ نہ باندھو ، جو لوگ اللہ پر جھوٹے افتراء باندھتے ہیں وہ ہر گز فلاں نہیں پایا کرتے');
INSERT INTO "sayyid_qutb" VALUES(16,117,'16:117','دنیا کا عیش چند روزہ ہے آخر کار ان کے لئے دردناک سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(16,118,'16:118','وہ چیزیں ہم نے خاص طور پر یہودیوں کے لئے حرام کی تھیں جن کا ذکر اس سے پہلے ہم تم سے کرچکے ہیں۔ اور یہ ان پر ہمارا ظلم نہ تھا بلکہ ان کا اپنا ہی ظلم تھا جو وہ اپنے اوپر کر رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(16,119,'16:119','البتہ جن لوگوں نے جہالت کی بنا پر برا عمل کیا اور پھر توبہ کرکے اپنے عمل کی اصلاح کرلی تو یقینا توبہ و اصلاح کے بعد تیرا رب ان کے لئے غفور اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(16,120,'16:120','واقعہ یہ ہے کہ ابراہیم (علیہ السلام) اپنی ذات سے ایک پوری امت تھا ، اللہ کا مطیع فرمان اور یکسو۔ وہ کبھی مشرک نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(16,121,'16:121','اللہ کی نعمتوں کا شکر ادا کرنے والا تھا۔ اللہ نے اس کو منتخب کرلیا اور سیدھا راستہ دکھایا۔');
INSERT INTO "sayyid_qutb" VALUES(16,122,'16:122','دنیا میں اس کو بھلائی دی اور آخرت میں وہ یقینا صالحین میں سے ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(16,123,'16:123','پھر ہم نے تمہاری طرف یہ وحی بھیجی کہ یکسو ہو کر ابراہیم (علیہ السلام) کے طریقے پر چلو اور وہ مشرکوں میں سے نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(16,124,'16:124','رہاسبت ، تو وہ ہم نے ان لوگوں پر مسلط کیا تھا جنہوں نے اس کے احکام میں اختلاف کیا اور یقینا تیرا رب قیامت کے روز ان سب باتوں کو فیصلہ کردے گا جن میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(16,125,'16:125','اے نبی صلى الله عليه وسلم اپنے رب کے راستے کی طرف دعوت و حکمت اور عمدہ نصیحت کے ساتھ ، اور لوگوں سے مباحثہ کرو ایسے طریقہ پر جو بہترین ہو۔ تمہارا رب ہی زیادہ بہتر جانتا ہے کہ کون اس کی راہ سے بھٹکا ہوا ہے اور کون راہ راست پر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,126,'16:126','اور اگر لوگ بدلہ لو تو بس اسی قدر لے لو جس قدر تم پر زیادتی کی گئی ہو۔ لیکن اگر تم صبر کرو تو یقینا یہ صبر کرنے والوں ہی کے حق میں بہتر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(16,127,'16:127','اے نبی صلى الله عليه وسلم صبر سے کام کیے جائو...اور تمہارا یہ صبر اللہ ہی کی توفیق سے ہے...ان لوگوں کی حرکات پر رنج نہ کرو اور نہ ان کی چال بازیوں پر دل تنگ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(16,128,'16:128','اللہ ان لوگوں کے ساتھ ہے جو تقویٰ سے کام لیتے ہیں اور احسان پر عمل کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,1,'17:1','پاک ہے وہ جو لے گیا ایک رات اپنے بندے کو مسجد حرام سے دور کی اس مسجت تک جس کے ماحول کو اس نے برکت دی ہے تاکہ اسے کچھ نشانیوں کا مشاہدہ کرائے۔ حقیقت میں وہی ہے سب کچھ سننے اور دیکھنے والا');
INSERT INTO "sayyid_qutb" VALUES(17,2,'17:2','ہم نے اس سے پہلے موسیٰ (علیہ السلام) کو کتاب دی تھی اور اسے بنی اسرائیل کے لئے ذریعہ ہدایت بنایا تھا اس تاکید کے ساتھ کہ میرے سوا کسی کو اپنا وکیل نہ بنانا۔');
INSERT INTO "sayyid_qutb" VALUES(17,3,'17:3','تم ان لوگوں کی اولاد ہو جنہیں ہم نے نوح (علیہ السلام) کے ساتھ کشتی پر سوار کیا ، اور نوح (علیہ السلام) ایک شکر گزار بندہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(17,4,'17:4','پھر ہم نے اپنی کتاب میں بنی اسرائیل کو اس بات پر بھی متنبہ کردیا تھا کہ تم دو مرتبہ زمین میں فساد عظیم برپا کرو گے اور بڑی سرکشی دکھائو گے۔');
INSERT INTO "sayyid_qutb" VALUES(17,5,'17:5','آخر کار جب ان میں سے پہلی سرکشی کا موقعہ پیش آیا ، تو اے بنی اسرائیل ، ہم نے تمہارے مقابلے پر اپنے ایسے بندے اٹھائے جو نہایت زور آور تھے اور وہ تمہارے ملک میں گھس کر ہر طرف پھیل گئے۔ یہ ایک وعدہ تھا جسے پورا ہو کر ہی رہنا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(17,6,'17:6','اس کے بعد ہم نے تمہیں ان پر غلبے کا موقعہ دے دیا اور تمہیں مال اور اولاد سے مدد دی اور تمہاری تعداد پہلے سے بڑھا دی۔');
INSERT INTO "sayyid_qutb" VALUES(17,7,'17:7','دیکھو ! تم نے بھلائی کی تو وہ تمہارے اپنے ہی لیے بھلائی تھی ، اور برائی کی تو وہ تمہاری اپنی ذات کے لئے برائی ثابت ہوئی۔ پھر جب دوسرے وعدے کا وقت آیا تو ہم نے دوسرے دشمنوں کو تم پر مسلط کیا تاکہ وہ تمہارے چہرے بگاڑ دیں اور مسجد (بیت المقدس) میں اسی طرح گھس جائیں جس طرح پہلے دشمن گھسے تھے اور جس چیز پر ان کا ہاتھ پڑے اسے تباہ کرکے رکھ دیں۔');
INSERT INTO "sayyid_qutb" VALUES(17,8,'17:8','ہو سکتا ہے کہ اب تمہارا رب تم پر رحم کرے ، لیکن اگر تم نے پھر اپنی سابق روش کا اعادہ کیا تو ہم بھی پھر اپنی سزا کا اعادہ کریں گے ، اور کافر نعمت لوگوں کے لئے ہم نے جہنم کو قید خانہ بنا رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,9,'17:9','حقیقت یہ ہے کہ یہ قرآن وہ راہ دکھاتا ہے جو بالکل سیدھی ہے۔ جو لوگ اسے مان کر بھلے کام کرنے لگیں انہیں یہ بشارت دیتا ہے کہ ان کے لئے بڑا اجر ہے');
INSERT INTO "sayyid_qutb" VALUES(17,10,'17:10','اور جو لوگ آخرت کو نہ مانیں انہیں یہ خبر دیتا ہے کہ ان کے لئے ہم نے دردناک عذاب مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,11,'17:11','اور انسان شر اس طرح مانگتا ہے جس طرح خیر مانگنی چاہیے۔ انسان بڑا ہی جلد باز واقع ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,12,'17:12','دیکھو ، ہم نے رات سے اور دن کو دو نشانیاں بنایا ہے۔ رات کی نشانی کو ہم نے بےنور بنایا اور دن کی نشانی کو روشن کردیا تاکہ تم اپنے رب کا فضل تلاش کرسکو اور ماہ و سال کا حساب معلوم ہر سکو۔ اسی طرح ہم نے ہر چیز کو الگ الگ ممیز کرکے رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,13,'17:13','ہر انسان کا شگون ہم نے اس کے اپنے گلے میں لٹکا رکھا ہے ، اور قیامت کے روز ہم ایک نوشتہ اس کے لئے نکالیں گے جسے وہ کھلی کتاب کی طرح پائے گا');
INSERT INTO "sayyid_qutb" VALUES(17,14,'17:14','پڑھ اپنا نامہ اعمال ، آج اپنا حساب لگانے کے لئے تو خود ہی کافی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,15,'17:15','جو کوئی راہ راست اختیار کرکے اس کی راست روی اس کے اپنے ہی لئے مفید ہے ، اور جو گمراہ ہو اس کی گمراہی کا وبال اسی پر ہے۔ کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہ اٹھائے گا۔ اور ہم عذاب دینے والے نہیں ہیں جب تک کہ (لوگوں کو حق و باطل کا فرق سمجھانے کے لئے ) ایک پیغامبر نہ بھیج دیں۔');
INSERT INTO "sayyid_qutb" VALUES(17,16,'17:16','جب ہم کسی بستی کو ہلاک کرنے کا ارادہ کرتے ہیں تو اس کے خوشحال لوگوں کو حکم دیتے ہیں اور وہ اس میں نافرمانیاں کرنے لگتے ہیں ، تب عذاب کا فیصلہ اس بستی پر چسپاں ہوجاتا ہے اور ہم اسے برباد کرکے رکھ دیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(17,17,'17:17','دیکھ لو ، کتنی ہی نسلیں ہیں جو نوح (علیہ السلام) کے بعد ہمارے حکم سے ہلاک ہوئیں۔ تیرا رب اپنے بندوں کے گناہوں سے پوری طرح باخبر ہے اور سب کچھ دیکھ رہا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,18,'17:18','جو کوئی (اس دنیا میں) جلدی حاصل والے فائدوں کا خواہش مند ہو ، اسے یہیں ہم دے دیتے ہیں جو کچھ سبھی جسے دینا چاہیں ، پھر ا کے مقسوم میں جہنم لکھ دیتے ہیں جسے وہ تاپے گا ، ملامت زدہ اور رحمت سے محروم ہو کر۔');
INSERT INTO "sayyid_qutb" VALUES(17,19,'17:19','اور جو آخرت کا خواہش مند ہو اور اس کے لئے سعی کرے جیسی کہ اس کے لئے سعی کرنی چاہیے ، اور ہو وہ مومن ، تو ایسے ہر شخص کی سعی مشکور ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(17,20,'17:20','ان کو بھی اور ان کو بھی ، دونوں فریقوں کو ہم (دنیا میں) سامان زیست دیے جار ہے ہیں ، یہ تیرے رب کا عطیہ ہے ، اور تیرے رب کی عطا کو روکنے والا کوئی نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,21,'17:21','مگر دیکھ لو ، دنیا ہی میں ہم نے ایک گروہ کو دوسرے پر کیسی فضیلت دے رکھی ہے۔ اور آخرت میں اس کے درجے اور بھی زیادہ ہوں گے اور اس کی فضیلت اور بھی زیادہ بڑھ چڑھ کر ہوگی');
INSERT INTO "sayyid_qutb" VALUES(17,22,'17:22','تو اللہ کے ساتھ کوئی دوسرا معبود نہ بنا ورنہ ملامت زدہ اور بےیارومددگار بیٹھا رہ جائے گا');
INSERT INTO "sayyid_qutb" VALUES(17,23,'17:23','تیرے رب نے فیصلہ کردیا ہے کہ : تم لوگ کسی کی عبادت نہ کرو ، مگر صرف اس کی والدین کے ساتھ نیک سلوک کرو ، اگر تمہارے پاس ان میں سے کوئی ایک ، یا دونوں ، بوڑھے ہو کر رہیں تو انہیں اف تک نہ کہو ، نہ انہیں جھڑک کر جواب دو ، بلکہ ان سے احترام کے ساتھ بات کرو ، اور نرمی اور رحم کے ساتھ ان کے سامنے جھک کر رہو ، اور دعا کیا کرو کہ ” پروردگار ، ان پر رحم فرما جس طرح انہوں نے رحمت و شفقت کے ساتھ مجھے بچپن میں پالا تھا');
INSERT INTO "sayyid_qutb" VALUES(17,24,'17:24','اور نرمی اور رحم کے ساتھ ان کے سامنے جھک کر رہو اور دعا کیا کرو ، پروردگار ، ان پر رحم فرما جس طرح انہوں نے رحمت و شفقت کے ساتھ مجھے بچپن میں پالا تھا');
INSERT INTO "sayyid_qutb" VALUES(17,25,'17:25','تمہارا رب خوب جانتا ہے کہ تمہارے دلوں میں کیا ہے۔ اگر تم صالح بن کر رہو تو وہ ایسے سب لوگوں کے لئے درگزر کرنے والا ہے جو اپنے قصور پر متبہ ہو کر بندگی کے رویے کی طرف پلٹ آئیں');
INSERT INTO "sayyid_qutb" VALUES(17,26,'17:26','رشتہ دار کو اس کا حق دو اور مسکین اور مسافر کو اس کا حق۔ فضول خرچی نہ کرو۔');
INSERT INTO "sayyid_qutb" VALUES(17,27,'17:27','فضول خرچ لوگ شیطان کے بھائی ہیں ، اور شیطان اپنے رب کا ناشکرا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,28,'17:28','اگر ان سے (یعنی حاجت مند رشتہ داروں ، مسکینوں اور مسافروں سے) تمہیں کترانا ہو ، اس بنا پر کہ ابھی تم اللہ کی اس رحمت کو جس کے تم امیدوار ہو ، تلاش کر رہے ہو ، تو انہیں نرم جواب دے دو');
INSERT INTO "sayyid_qutb" VALUES(17,29,'17:29','نہ تو اپنا ہاتھ گردن سے باندھ رکھو اور نہ اسے بالکل ہی کھلا چھوڑو دو کہ ملامت زدہ اور عاجز بن کر رہ جائو');
INSERT INTO "sayyid_qutb" VALUES(17,30,'17:30','تیرا رب جس کے لئے چاہتا ہے رزق کشادہ کرتا ہے اور جس کے لئے چاہتا ہے تنگ کردیتا ہے۔ وہ اپنے بندوں کے حال سے باخبر ہے اور انہیں دیکھ رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,31,'17:31','اپنی اولاد کو افلاس کے اندیشے سے قتل نہ کرو۔ ہم انہیں بھی رزق دیں گے اور تمہیں بھی۔ درحقیقت ان کا قتل ایک بڑی خطا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,32,'17:32','اور زنا کے قریب نہ پھنکو ، وہ بہت برا فعل ہے اور بڑا ہی برا راستہ');
INSERT INTO "sayyid_qutb" VALUES(17,33,'17:33','قتل نفس کا ارتکاب نہ کرو جسے اللہ نے حرام کیا ہے مگر حق کے ساتھ۔ اور جو شخص مظلومانہ قتل کیا گیا ہو اس کے ولی کو ہم نے قصاص کے مطالبے کا حق عطا کیا ہے ، پس چاہیے کہ وہ قتل میں حد سے نہ گزرے ، اس کی مدد کی جائے گی۔');
INSERT INTO "sayyid_qutb" VALUES(17,34,'17:34','مال یتیم کے پاس نہ پھٹکو مگر احسن طریقے سے ، یہاں تک کہ وہ اپنے شباپ کو پہنچ جائے۔ عہد کی پابندی کرو ، بیشک عہدے کے بارے میں تم کو جواب دہی کرنی ہوگی');
INSERT INTO "sayyid_qutb" VALUES(17,35,'17:35','پیمانے سے دو تو پورا بھر کردو ، اور تولو تو ٹھیک ترازو سے تولو۔ یہ اچھا طریقہ ہے اور بلحاظ انجام بھی یہی بہتر ہے');
INSERT INTO "sayyid_qutb" VALUES(17,36,'17:36','کسی ایسی چیز کے پیچھے نہ لگو جس کا تمہیں علم نہ ہو ، یقینا آنکھ ، کان اور دل سب ہی کی باز پرس ہونی ہے');
INSERT INTO "sayyid_qutb" VALUES(17,37,'17:37','زمین میں اکڑ کر نہ چلو ، تم نہ زمین کو پھاڑ سکتے ہو ، نہ پہاڑوں کی بلندی کو پہنچ سکتے ہو');
INSERT INTO "sayyid_qutb" VALUES(17,38,'17:38','ان امور میں سے ہر ایک کا برا پہلو تیرے رب کے نزدیک ناپسندیدہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,39,'17:39','یہ وہ حکمت کی باتیں ہیں جو تیرے رب نے تجھ پر وحی کی ہیں اور دیکھ ، اللہ کے ساتھ کوئی دوسرا معبود نہ بنا بیٹھ ورنہ تو جہنم میں ڈال دیا جائے گا ملامت زدہ اور ہر بھلائی سے محروم ہو کر');
INSERT INTO "sayyid_qutb" VALUES(17,40,'17:40','کیسی عجیب بات ہے کہ تمہارے رب نے تمہیں تو بیٹوں سے نوازا اور خود اپنے لئے ملائکہ کو بیٹیا بنا لیا ؟ بڑی جھوٹی با ت ہے جو تم لوگ زبانوں سے نکالتے ہو');
INSERT INTO "sayyid_qutb" VALUES(17,41,'17:41','ہم نے اس قرآن میں طرح طرح سے لوگوں کو سمجھایا کہ ہوش میں آئیں ، مگر وہ حق سے اور زیادہ دور ہی بھاگے جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,42,'17:42','اے نبی صلى الله عليه وسلم ان سے کہو اگر اللہ کے ساتھ دوسرے خدا بھی ہوتے جیسا کہ یہ لوگ کہتے ہیں ، تو وہ مالک عرش کے مقام کو پہنچنے کی ضرور کوشش کرتے');
INSERT INTO "sayyid_qutb" VALUES(17,43,'17:43','پاک ہے وہ اور بہت بالا و برتر ہے ان باتوں سے جو یہ لوگ کہہ رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,44,'17:44','” اس کی پاکی تو ساتوں آسمان اور زمین اور وہ ساری چیزیں بیان کر ہی ہیں جو آسمان و زمین میں ہیں۔ کوئی چیز ایسی نہیں جو اس کی حمد کے ساتھ اس کی تسبیح نہ کر رہی ہو ، مگر تم ان کی تسبیح سمجھتے نہیں ہو۔ حقیقت یہ ہے کہ وہ بڑا ہی بردبار اور درگزر کرے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,45,'17:45','جب تم قرآن پڑھتے ہو تو ہم تمہارے اور آخرت پر ایمان نہ لانے والوں کے درمیان ایک پردہ حائل کردیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,46,'17:46','اور ان کے دلوں پر ایسا غلاف چڑھا دیتے ہیں کہ وہ کچھ نہیں سمجھتے ، اور ان کے کانوں میں گرانی پیدا کردیتے ہیں۔ اور جب تم قرآن میں اپنے ایک ہی رب کا ذکر کرتے ہو تو وہ نفرت سے منہ موڑ لیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(17,47,'17:47','ہمیں معلوم ہے کہ جب وہ کان لگا کر تمہاری بات سنتے ہیں تو دراصل کیا سنتے ہیں ، اور جب بیٹھ کر باہم سرگوشیاں کرتے ہیں تو کیا کہتے ہیں۔ یہ ظالم آپس میں کہتے ہیں کہ یہ تو ایک سحر زدہ آدمی ہے جس کے پیچھے تم لوگ جا رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(17,48,'17:48','دیکھو ، کیسی باتیں ہیں جو یہ لوگ تم پر چھانٹتے ہیں ، یہ بھٹک گئے ہیں ، انہیں راستہ نہیں ملتا');
INSERT INTO "sayyid_qutb" VALUES(17,49,'17:49','جب ہم صرف ہڈیاں اور خاک ہو کر رہ جائیں گے تو کیا ہم نئے سرے سے پیدا کرکے اٹھائے جائیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(17,50,'17:50','ان سے کہو ” تم پتھر یا لوہا بھی ہوجائو ،');
INSERT INTO "sayyid_qutb" VALUES(17,51,'17:51','یاس سے بھی زیادہ سخت کوئی چیز جو تمہارے ذہن میں قبول حیات سے بعید تر ہو “ (پھر بھی تم اٹھ کر رہو گے) ۔ وہ ضرور پوچھیں گے ” کون ہے وہ جو ہمیں پھر زندگی کی طرف پلٹا کر لائے گا ؟ “ جواب میں کہو :” وہی جس نے پہلی بات تم کو پیدا کیا “۔ وہ سر ہلا ہلا کر پوچھیں گے :” اچھا ، تو یہ ہوگا کب ؟ “ تم کہو :” کیا عجب کہ وہ وقت قریب ہی آلگا ہو۔');
INSERT INTO "sayyid_qutb" VALUES(17,52,'17:52','جس روز وہ تمہیں پکارے گا تو تم اس کی حمد کریت ہوئے اس کی پکار کے جواب میں نکل آئو گے اور تمہارا گمان اس وقت یہ ہوا کہ ہم بس تھوڑی دیر ہی اس حالت میں پڑے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,53,'17:53','اور اے نبی صلى الله عليه وسلم میرے بندوں (یعنی مومن بندوں) سے کہہ دو کہ زبان سے وہ بات نکالا کری جو بہترین ہو۔ دراصل یہ شیطان ہے جو انسانوں کے درمیان فساد ڈلوانے کی کوشش کرتا ہے۔ حقیت یہ ہے کہ شیطان انسان کا کھلا دشمن ہے');
INSERT INTO "sayyid_qutb" VALUES(17,54,'17:54','تمہارا رب حال سے خوب واقف ہے۔ وہ چاہے تو تم پر رحم کرے اور چاہے تو تمہیں عذاب دے دے۔ اور اے نبی صلى الله عليه وسلم ہم نے تم کو لوگوں پر حوالہ دار بنا کر نہیں بھیجا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,55,'17:55','تیرا رب زمین اور آسمانوں کی مخلوقات کو زیادہ جانتا ہے ہم نے بعض پیغمبروں کو بعض سے برھ کر مرتبے دئیے اور ہم نے ہی دائود کو زبور دی تھی');
INSERT INTO "sayyid_qutb" VALUES(17,56,'17:56','ان سے کہو ، پکار دیکھو ان معبودوں کو ، جن کو تم خدا کے سوا (اپنا کار ساز) سمجھتے ہو ، وہ کسی تکلیف کو تم سے نہ ہٹا سکتے ہیں نہ بدل سکتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,57,'17:57','جن کو یہ لوگ پکارتے ہیں وہ خود اپنے رب کے حضور رسائی حاصل کرنے کا وسیلہ تلاش کررہے ہیں کہ کون اس سے قریب تر ہوجائے اور وہ اس کی رحمت کے امیدوار اور اس کے عذاب سے خائف ہیں۔ حقیقت یہ ہے کہ تیرے رب کا عذاب ہے ہی ڈرانے کے لائق');
INSERT INTO "sayyid_qutb" VALUES(17,58,'17:58','اور کوئی بستی ایسی نہیں ہے جسے ہم قیامت سے پہلے ہلاک نہ کریں یا سخت عذاب نہ دیں ، یہ نوشتہ الٰہی میں لکھا ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,59,'17:59','اور ہم کو نشانیان بھیجنے سے نہیں روکا مگر اس بات نے کہ ان سے پہلے لوگ ان کو جھٹلا چکے ہیں (چنانچہ دیکھ لو) ثمود کو ہم نے علانیہ اونٹنی لاکر دی اور انہوں نے اس پر ظلم کیا۔ ہم نشانیاں اسی لئے تو بھیجتے ہیں کہ لوگ انہیں دیکھ کر ڈریں');
INSERT INTO "sayyid_qutb" VALUES(17,60,'17:60','یاد کرو اے نبی صلى الله عليه وسلم ‘ ہم نے تم سے کہہ دیا تھا کہ تیرے رب نے ان لوگوں کو گھیر رکھا ہے اور یہ جو کچھ ابھی ہم نے تمہیں دکھایا ہے ، اس کو اور اس درخت کو جس پر قرآن میں لعنت کی گئی ہے ، ہم نے ان لوگوں کے لئے بس ایک فتنہ بنا کر رکھ دیا۔ ہم انہیں تنبیہ پر کیے جا رہے ہیں ، مگر ہر تنبیہ ان کی سرکشی میں اضافہ کیے جاتی ہے');
INSERT INTO "sayyid_qutb" VALUES(17,61,'17:61','اور یاد کرو جبکہ ہم نے ملائکہ سے کہا کہ آدم کو سجدہ کرو ، تو سب نے سجدہ کیا ، مگر ابلیس نے نہ کیا۔ اس نے کہا :” کیا میں اس کو سجدہ کروں جسے تو نے مٹی سے بنایا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(17,62,'17:62','پھر بولا :” دیکھ تو سہی ، کیا یہ اس قابل تھا کہ تو نے اسے مجھ پر فضیلت دی ؟ اگر تو مجھے قیامت کے دن تک مہلت دے تو میں اس کی پوری نسل کی بیخ کنی کر ڈالوں ، بس تھوڑے ہی لوگ مجھ سے بچ سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(17,63,'17:63','اللہ تعالیٰ نے فرمایا :” اچھا تو جا ، ان میں سے جو بھی تیری پیروی کریں ، تجھ سمیت ان سب کے لئے جہنم ہی بھرپور جزا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,64,'17:64','تو جس جس کو اپنی دعوت سے پھسلا سکتا ہے پھسلالے ، ان پر اپنے سوار اور پیادے چڑھا لا ، مال اور اولاد میں ان کے ساتھ سا جھالگا ، اور ان کو وعدوں کے جال میں پھانس...اور شیطان کے وعدے ایک دھوکے کے سوا اور کچھ بھی نہیں');
INSERT INTO "sayyid_qutb" VALUES(17,65,'17:65','یقینا میرے بندوں پر تجھے کوئی اقتدار حاصل نہ ہوگا۔ اور تو کل کے لئے تیرا رب کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(17,66,'17:66','تمہارا (حقیقی) رب تو وہ ہے جو سمندر میں تمہاری کشتی چلاتا ہے تاکہ تم اس کا فضل تلاش کرو۔ حقیقت یہ ہے کہ وہ تمہارے حال پر نہایت مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(17,67,'17:67','جب سمندر میں تم پر مصیبت آتی ہے تو اس ایک کے سوا دوسرے جن جن کو تم پکارا کرتے ہو وہ سب گم ہوجاتے ہیں مگر جب وہ تم کو بچا کر خشکی پر پہنچا دیتا ہے تو تم اس سے منہ موڑ جاتے ہو۔ انسان واقعی بڑا ناشکرا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,68,'17:68','اچھا ، تو کیا تم اس بات سے بالکل بےخوف ہو کر خدا کبھی خشکی پر ہی تم کو زمین میں دھنسا دے ، یا تم پر پتھرائو کرنے والی آندھی بھیج دے اور تم اس سے بچانے والا کوئی حمایتی نہ پائو ؟');
INSERT INTO "sayyid_qutb" VALUES(17,69,'17:69','اور کیا تمہیں اس کا اندیشہ نہیں کہ خدا پھر کسی وقت سمندر میں تم کو لے جائے اور تمہاری ناشکری کے بدلے تم پر سخت طوفانی ہوا بھیج کر تمہیں غرق کردے اور تم کو ایسا کوئی نہ ملے جو اس سے تمہارے اس انجام کی پوچھ گچھ کرسکے ؟');
INSERT INTO "sayyid_qutb" VALUES(17,70,'17:70','یہ تو ہماری عنایت ہے کہ ہم نے بنی آدم کو بزرگی دی اور انہیں خشکی و تری میں سواریاں عطا کیں اور ان کو پاکیزہ چیزوں سے رزق دیا اور اپنی بہت سی مخلوقات پر نمایاں فوقیت بخشی');
INSERT INTO "sayyid_qutb" VALUES(17,71,'17:71','پھر خیال کرو اس دن کا جب کہ ہم ہر انسانی گروہ کو اس کے پیشوا کے ساتھ بلائیں گے۔ اس وقت جن لوگوں کو ان کا نامہ اعمال سیدھے ہاتھ میں دیا گیا۔ وہ اپنا کارنامہ پڑھیں گے اور ان پر ذرہ برابر ظلم نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(17,72,'17:72','اور جو اس دنیا میں اندھا بن کر رہا وہ آخرت میں بھی اندھا ہی رہے گا بلکہ راستہ پانے میں اندھے سے بھی زیادہ ناکام');
INSERT INTO "sayyid_qutb" VALUES(17,73,'17:73','اے نبی صلى الله عليه وسلم ان لوگوں نے اس کوشش میں کوء کسر اٹھا نہیں رکھی کہ تمہیں فتنے میں ڈال کر اس وحی سے پھیر دیں جو ہم نے تمہاری طرف بھیجی ہے تاکہ تم ہمارے نام پر اپنی طرف سے کوئی بات گھڑو۔ اگر تم ایسا کرتے تو وہ ضرور تمہیں اپنا دوست بنا لیتے۔');
INSERT INTO "sayyid_qutb" VALUES(17,74,'17:74','اور بعید نہ تھا کہ اگر تمہیں مضبوط نہ رکھتے تو تم ان کی طرف کچھ نہ کچھ جھک جاتے۔');
INSERT INTO "sayyid_qutb" VALUES(17,75,'17:75','لیکن اگر تم ایسا کرتے تو ہم تمہیں دنیا میں بھی دو ہرے عذاب کا مزہ چکھاتے اور آخرت میں بھی دوہرے عذاب کا ، پھر ہمارے مقابلے میں تم کوئی مددگار نہ پاتے۔');
INSERT INTO "sayyid_qutb" VALUES(17,76,'17:76','اور یہ لوگ اس بات پر بھی تلے رہے ہیں کہ تمہارے قدم اس سرزمین سے اکھاڑ دیں اور تمہیں یہاں سے نکال باہر کریں۔ لیکن اگر یہ ایسا کریں گے تو تمہارے بعد یہ خود یہاں کچھ زیادہ دیر نہ ٹھہر سکیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(17,77,'17:77','یہ ہمارا مستقبل طریق کار ہے جو ان سب رسولوں کے معاملے میں ہم نے برتا ہے جنہیں تم سے پہلے ہم نے بھیجا تھا اور ہمارے طریق کار میں تم کوئی تغیر نہ پائو گے');
INSERT INTO "sayyid_qutb" VALUES(17,78,'17:78','نماز قائم کرو غروب آفتاب سے لے کر رات کے اندھیرے تک او فجر کے قرا ان کا بھی التزام کرو کیونکہ قرآن فجر مشہور ہوتا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,79,'17:79','اور رات کو تہجد پڑھو یہ تمہارے لئے نفل ہے ، بعید نہیں کہ تمہارا رب تمہیں مقام محمود پر فائز کردے۔');
INSERT INTO "sayyid_qutb" VALUES(17,80,'17:80','اور دعا کرو کہ پروردگار ، مجھ کو جہاں بھی تو لے جا سچائی کے ساتھ لے جا اور جہاں سے بھی نکال سچائی کے ساتھ نکال اور اپنی طرف سے ایک اقتدار کو میرا مددگار بنا دے۔');
INSERT INTO "sayyid_qutb" VALUES(17,81,'17:81',': اور اعلان کر دو کہ ” حق آگیا اور باطل مٹ گیا ، باطل تو مٹنے ہی والا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,82,'17:82','ہم اس قرآن کے سلسلہ تنزیل میں وہ کچھ نازل کر رہے ہیں جو ماننے والوں کے لئے تو شفا اور رحمت ہے ، مگر ظالموں کے لئے خسارے کے سوا اور کسی چیز میں اضافہ نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(17,83,'17:83','انسان کا حال یہ ہے کہ جب ہم اس کو نعمت عطا کرتے ہیں تو وہ اینٹھتا اور پیٹھ موڑ لیتا ہے ، اور جب ذرا مصیبت سے دوچار ہوتا ہے تو مایوس ہونے لگتا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,84,'17:84','اے نبی صلى الله عليه وسلم لوگوں سے کہہ دو کہ ” ہر ایک اپنے طریقے پر عمل کر رہا ہے ، اب یہ تمہارا رب ہی بہترجانتا ہے کہ سیدھی راہ پر کون ہے');
INSERT INTO "sayyid_qutb" VALUES(17,85,'17:85','یہ لوگ تم سے روح کے متعلق پوچھتے ہیں ، کہو یہ روح میرے رب کے حکم سے آتی ہے ، مگر تم لوگوں نے علم سے کم ہی بہرہ پایا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,86,'17:86','اور اے نبی صلى الله عليه وسلم ہم چاہیں تو وہ سب کچھ تم سے چھین لیں جو ہم نے وحی کے ذریعے سے تم کو عطا کیا ہے ، پھر تم ہمارے مقابلے میں کوئی حمایتی نہ پائو گے جو اسے واپس دلا سکے۔');
INSERT INTO "sayyid_qutb" VALUES(17,87,'17:87','یہ تو جو کچھ تمہیں ملا ہے تمہارے رب کی رحمت سے ملا ہے ، حقیقت یہ ہے کہ اس کا فضل تم پر بہت بڑا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,88,'17:88','کہہ دو کہ اگر انسان اور جن سب کے سب مل کر اس قرآن جیسی کوئی چیز لانے کی کوشش کریں تو نہ لاسکیں گے ، چاہے وہ سب ایک دوسرے کے مدد گار ہی کیوں نہ ہوں');
INSERT INTO "sayyid_qutb" VALUES(17,89,'17:89','ہم نے اس قرآن میں لوگوں کو طرح طرح سے سمجھایا مگر اکثر لوگ انکار ہی پر جمے رہے');
INSERT INTO "sayyid_qutb" VALUES(17,90,'17:90','اور انہوں نے کہا ” ہم تیری بات نہ مانیں گے جب تک کہ تو ہمارے لئے زمین کو پھاڑ کر ایک چشمہ جاری نہ کر دے۔');
INSERT INTO "sayyid_qutb" VALUES(17,91,'17:91','یا تیرے لئے کھجوروں اور انگوروں کا ایک باغ پیدا ہو اور تو اس میں نہریں رواں کردے۔');
INSERT INTO "sayyid_qutb" VALUES(17,92,'17:92','یا تو آسمان کو ٹکڑے ٹکڑے کرکے ہمارے اوپر گرا دے جیسا کہ تیرا دعویٰ ہے۔ یا خدا اور فرشتوں کو ...ہمارے سامنے لے آئے۔');
INSERT INTO "sayyid_qutb" VALUES(17,93,'17:93','یا تیرے لئے سونے کا ایک گھر بن جائے یا تو آسمان پر چرھ جائے ، اور تیرے چرھنے کا بھی ہم یقین نہ کریں گے جب تک کہ تو ہمارے اوپر ایک ایسی تحریر اتار لائے جس ہم پڑھیں۔ اے نبی ، ان سے کہو ” پاک ہے میرا پروردگار ، کیا میں پیغام لانے والے انسان کے سوا اور بھی کچھ ہوں');
INSERT INTO "sayyid_qutb" VALUES(17,94,'17:94','لوگوں کے سامنے جب کبھی ہدایت آئی ، تو اس پر ایمان لانے سے ان کو کسی چیز نے نہیں روکا مگر ان کے اسی قول نے کہ ” کیا اللہ نے بشر کو پیغمبر بنا کر بھیج دیا');
INSERT INTO "sayyid_qutb" VALUES(17,95,'17:95','ان سے کہو ، اگر زمین میں فرشتے اطمینان سے چل پھر رہے ہوتے تو ہم ضرور آسمان سے کسی فرشتے ہی کو ان کے لئے پیغمبر بنا کر بھیجتے');
INSERT INTO "sayyid_qutb" VALUES(17,96,'17:96','اے نبی صلى الله عليه وسلم ان سے کہہ دو کہ میرے اور تمہارے درمیان بس ایک اللہ کی گواہی کافی ہے ، وہ اپنے بندوں کے حال سے باخبر ہے اور سب کچھ دیکھ رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,97,'17:97','جس کو اللہ ہدایت دے وہی ہدایت پانے والا ہے ، اور جسے وہ گمراہی میں ڈال دے تو اس کے سوا ایسے لوگوں کے لئے تو کوئی حامی و ناصر نہیں پاسکتا۔ ان لوگوں کو ہم قیامت کے روز اوندھے منہ کھینچ لائیں گے ، اندھے ، کوئلے اور بہرے۔ ان کا ٹھکانا جہنم ہے۔ جب کبھی اس کی آگ دھیمی ہونے لگے گی ہم اسے اور بھڑکا دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(17,98,'17:98',': یہ بدلہ ہے ان کی اس حرکت کا کہ انہوں نے ہماری آیات کا انکار کیا اور کہا ” کیا ہم صرف ہڈیاں اور خاک ہو کر رہ جائیں گے تو نئے سرے سے ہم کو پیدا کرکے اٹھا کر کھڑا کیا جائے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(17,99,'17:99','کیا ان کو یہ نہ سوچھا کہ جس خدا نے زمین اور آسمانوں کو پیدا کیا ہے وہ ان جیسوں کو پیدا کرنے کی ضرور قدرت رکھتا ہے ؟ اس نے ان کے حشر کے لئے ایک وقت مقرر کر رکھا ہے جس کا آنا یقینی ہے ، مگر ظالموں کو اصرار ہے کہ وہ اس کا انکار ہی کریں گے');
INSERT INTO "sayyid_qutb" VALUES(17,100,'17:100','اے نبی صلى الله عليه وسلم ان سے کہو ، اگر کہیں میرے رب کی رحمت کے خزانے تمہارے قبضے میں ہوتے تو تم خرچ ہوجانے اندیشے سے ضرور ان کو روک رکھتے۔ واقعی انسان بڑا تنگ دل واقع ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,101,'17:101','ہم نے موسیٰ (علیہ السلام) کو نشانیاں عطا کی تھیں جو صریح طور پر دکھائی دے رہی تھیں۔ اب یہ تم خود بنی اسرائیل سے پوچھ لو کہ جب موسیٰ (علیہ السلام) ان کے ہاں آئے تو فرعون نے یہی کہا تھا تاکہ ” اے موسیٰ میں سمجھتا ہوں کہ تو ضرور ایک سحر زدہ آدمی ہے');
INSERT INTO "sayyid_qutb" VALUES(17,102,'17:102','موسیٰ (علیہ السلام) نے اس کے جواب میں کہا ” تو خوب جانتا ہے کہ یہ بصیرت افروز نشانیاں زمین اور آسمانوں کے رب کے سوا کسی نے نازل نہیں کی ہیں ، اور میرا خیال یہ ہے کہ اے فرعون تو ضرور ایک شامت زدہ آدمی ہے');
INSERT INTO "sayyid_qutb" VALUES(17,103,'17:103','آخر کار فرعون نے ارادہ کیا کہ موسیٰ اور بنی اسرائیل کو زمین سے اکھاڑ پھینکے ، مگر ہم نے اس کو اور اس کے ساتھیوں کو اکٹھا غرق کردیا');
INSERT INTO "sayyid_qutb" VALUES(17,104,'17:104','اور اس کے بعد بنی اسرائیل سے کہا کہ اب تم زمین میں بسو ، پھر جب آخرت کے وعدے کا وقت آن پورا ہوگا ، تو ہم تم سب کو ایک ساتھ لاحاضر کریں گے');
INSERT INTO "sayyid_qutb" VALUES(17,105,'17:105','اس قرآن کو ہم نے حق کے ساتھ نازل کیا ہے اور حق ہی کے ساتھ یہ نازل ہوا ہے ، اور اے ہمارے نبی تمہیں ہم نے اس کے سوا اور کسی کام کے لئے نہیں بھیجا کہ (جو مان لے اسے) بشارت دے دو اور (جو نہ مانے اسے ) متنبہ کر دو ۔');
INSERT INTO "sayyid_qutb" VALUES(17,106,'17:106','اور اس قرآن کو ہم نے تھوڑا تھوڑا کرکے نازل کیا ہے تاکہ تم ٹھہر ٹھہر کر اسے لوگوں کو سنائو اور اسے ہم نے (موقع موقع سے) بتدریج اتارا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,107,'17:107','اے نبی صلى الله عليه وسلم ، ان لوگوں سے کہہ دو کہ تم اسے مانو یا نہ مانو ، جن لوگوں کو اس سے پہلے علم دیا گیا ہے ، انہیں جب یہ سنایا جاتا ہے تو وہ منہ کے بل سجدے میں گر جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(17,108,'17:108','اور پکار اٹھتے ہیں ” پاک ہے ہمارا رب “ اس کا وعدہ تو پورا ہونا ہی تھا');
INSERT INTO "sayyid_qutb" VALUES(17,109,'17:109','اور وہ منہ کے بل روتے ہوئے گر جاتے ہیں اور اسے سن کر ان کو خشوع اور بڑھ جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(17,110,'17:110','اے نبی صلى الله عليه وسلم ان سے کہو ” اللہ کہہ کر پکارو یا رحمان کہہ کر ، جس نام سے بھی پکارو ، اس کے لئے سب اچھے ہی نام ہیں اور اپنی نماز نہ بہت زیادہ بلند آواز سے پڑھو اور نہ بہت پست آواز سے ، ان دونوں کے درمیان اوسط درجے کا لہجہ اختیار کرو');
INSERT INTO "sayyid_qutb" VALUES(17,111,'17:111','اور کہو “ تعریف ہے اس خدا کے لئے جس نے کسی کو بنیا بنایا ، نہ کوئی بادشاہی میں اس کا شریک ہے ، اور نہ وہ عاجز ہے کہ کوئی اس کا پشتیبان ہو “۔ اور اس کی بڑائی بیان کرو ، کمال درجے کی بڑائی');
INSERT INTO "sayyid_qutb" VALUES(18,1,'18:1','تعریف اللہ کے لئے ہے جس نے اپنے بندے پر یہ کتاب نازل کی اور اس میں کوئی ٹیڑھ نہ رکھی۔');
INSERT INTO "sayyid_qutb" VALUES(18,2,'18:2','ٹھیک ٹھیک سیدھی بات کہنے والی کتاب ، تاکہ وہ لوگوں کو خدا کے سخت عذاب سے خبردار کر دے اور ایمان لا کر نیک عمل کرنے والوں کو خوشخبری دے دے کر ان کے لئے اچھا اجر ہے');
INSERT INTO "sayyid_qutb" VALUES(18,3,'18:3','جس میں ہمیشہ رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(18,4,'18:4','اور ان لوگوں کو ڈراوے جو کہتے ہیں کہ اللہ نے کسی کو بیٹا بنایا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(18,5,'18:5','اس بات کا نہ انہیں کوئی علم ہے اور نہ ان کے باپ دادا کو تھا۔ بڑی بات ہے جو ان کے منہ سے نکلتی ہے ، وہ محض جھوٹ بکتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,6,'18:6','اچھا ، تو اے نبی ، شاید تم ان کے پیچھے غم کے مارے اپنی جان کھو دینے والے ہو ، اگر یہ اس تعلیم پر ایمان نہ لائے۔');
INSERT INTO "sayyid_qutb" VALUES(18,7,'18:7','واقعہ یہ ہے کہ یہ جو کچھ سر و سامان بھی زمین پر ہے۔ اس کو ہم نے زمین کی زینت بنایا ہے تاکہ ان لوگوں کو آزمائیں۔ ان میں کون بہتر عمل کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,8,'18:8','آخر کار اس سب کو ہم ایک چٹیل میدان بنا دینے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,9,'18:9','کیا تم سمجھتے ہو کہ غار اور کتبے والے ہمارے کوئی بڑی عجیب نشانیوں میں سے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(18,10,'18:10','جب وہ چند نوجوان غار میں پناہ گزیں ہوئے اور انہوں نے کہا کہ ” اے پروردگار ، ہم کو اپنی رحمت خاص سے نواز اور ہمارا معاملہ درست کر دے');
INSERT INTO "sayyid_qutb" VALUES(18,11,'18:11','تو ہم نے انہیں اسی غار میں تھپک کر سالہا سال کے لئے گہری نیند سلا دیا ،');
INSERT INTO "sayyid_qutb" VALUES(18,12,'18:12','پھر ہم نے انہیں اٹھایا تاکہ دیکھیں ان کے دو گروہوں میں سے کون اپنی مدت قیام کا ٹھیک شمار کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,13,'18:13','ہم ان کا اصل قصہ تمہیں سناتے ہیں ، وہ چند نوجوان تھے جو اپنے رب پر ایمان لے آئے تھے اور ہم نے ان کو ہدایت میں ترقی بخشی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(18,14,'18:14','ہم نے ان کے دل اس وقت مضبوط کردیئے جب وہ اٹھے اور انہوں نے یہ اعلان کردیا کہ ” ہمارا رب تو بس وہی ہے جو آسمانوں اور زمین کا رب ہے۔ ہم اسے چھوڑ کر کسی دوسرے معبود کو نہ پکاریں گے۔ اگر ہم ایسا کریں تو بالکل بےجا بات کریں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,15,'18:15','(پھر انہوں نے آپس میں ایک دوسرے سے کہا) ” یہ ہماری قوم تو رب کائنات کو چھوڑ کر دور سے خدا بنا بیٹھی ہے۔ یہ لوگ ان کے معبود ہونے پر کوئی واضح دلیل کیوں نہیں لاتے ؟ آخر اس شخص سے بڑا ظالم اور کون ہو سکتا ہے جو اللہ پر جھوٹ باندھے ؟');
INSERT INTO "sayyid_qutb" VALUES(18,16,'18:16','اب جبکہ تم ان سے اور ان کے معبود ان غیر اللہ سے بےتعلق ہوچکے ہو تو چلو اب فلاں غار میں چل کر پناہ لو۔ تمہارا رب تم پر اپنی رحمت کا دامن وسیع کرے گا اور تمہارے کام کے لئے سروسامان مہیا کر دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,17,'18:17','تم انہیں غار میں دیکھتے تو تمہیں یوں نظر آتا کہ سورج جب نکلتا ہے تو ان کے غار کو چھوڑ کر دائیں جانب چڑھ جاتا ہے اور جب غروب ہوتا ہے تو ان سے بچ کر بائیں جانب اتر جاتا ہے اور وہ ہیں کہ غار کے اندر ایک وسیع جگہ میں پڑے ہیں۔ یہ اللہ کی نشانیوں میں سے ایک ہے ، جس کو اللہ ہدایت دے دی ہدایت پانے والا ہے اور جسے اللہ بھٹکا دے اس کے لئے تم کوئی ولی مرشد نہیں پاسکتے۔');
INSERT INTO "sayyid_qutb" VALUES(18,18,'18:18','تم انہیں دیکھ کر یہ سمجھتے کہ وہ ج اگر ہے ہیں ، حالانکہ وہ سو رہے تھے۔ ہم انہیں دائیں بائیں کروٹ دلواتے رہتے تھے اور ان کا کتا غار کے دہانے پر ہاتھ پھیلائے بیٹھا تھا۔ اگر تم کہیں جھانک کر انہیں دیکھتے تو الٹے پائوں بھاگ کھڑے ہوتے اور تم پر ان کے نظارے سے دہشت بیٹھ جاتی۔');
INSERT INTO "sayyid_qutb" VALUES(18,19,'18:19','” اور اسی عجیب کرشمے سے ہم نے انہیں اٹھا بٹھایا تاکہ ذرا آپس میں پوچھ گچھ کریں۔ ان میں سے ایک نے پوچھا ” کہو کتنی دیر اس حال میں رہے ؟ “ دوسروں نے کہا ’ د شاید دن بھر یا اس سے کچھ کم رہے ہوں گے۔ “ پھر وہ بولے ” اللہ ہی بہتر جانتا ہے کہ ہمارا کتنا وقت اس حالت میں گزرا۔ چلو ، اب اپنے میں سے کسی کو چاندی کا یہ مسئلہ دے کر شہر بھیجیں اور وہ دیکھے کہ سب سے اچھا کھانا کہاں ملتا ہے۔ وہاں وہ کچھ کھانے کے لئے لائے اور چاہئے کہ ذرا ہوشیاری سے کام کرے ، ایسا نہ ہو کہ وہ کسی کو ہمارے یہاں ہونے سے خبردار کر بیٹھے۔');
INSERT INTO "sayyid_qutb" VALUES(18,20,'18:20','اگر کہیں ان لوگوں کا ہاتھ ہم پر پڑگیا تو بس سگنسار ہی کر ڈالیں گے ، یا پھر زبردستی یہیں اپنی ملت میں واپس لے جائیں گے ، اور ایسا ہوا تو ہم کبھی فلاح نہ پا سکیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,21,'18:21','اس طرح ہم نے اہل شہر کو ان کے حال پر مطلع کیا تاکہ لوگ جان لیں کہ اللہ کا وعدہ سچا ہے اور یہ کہ قیامت کی گھڑی بیشک آکر رہے گی (مگر ذرا خیال کرو کہ جب سوچنے کی اصل بات یہ تھی) اس وقت وہ آپس میں اس بات پر جھگڑ رہے تھے کہ ان (اصحاب کہف) کے ساتھ کیا کیا جائے۔ کچھ لوگوں نے کہا ” ان پر ایک دیوار چن دو ، ان کا رب ہی ان کے معاملہ کو بہتر جانتا ہے “ مگر جو لوگ ان کے معاملات پر غالب تھے انہوں نے کہا ” ہم تو ان پر ایک عبادت گاہ بنائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,22,'18:22','کچھ لوگ کہیں گے کہ وہ تین تھے اور چوتھا ان کا کتا تھا اور کچھ دوسرے کہہ دیں گے کہ پانچ تھے اور چھٹا ان کا کتا تھا۔ یہ سب بےتکی ہانکتے ہیں۔ کچھ اور لوگ کہتے ہیں کہ سات تھے اور آٹھواں ان کا کتا تھا۔ کہو ، میرا رب ہی بہتر جانتا ہے کہ وہ کتنے تھے۔ کم ہی لوگ ان کی صحیح تعداد جانتے ہیں۔ پس سرسری بات سے بڑھ کر ان کی تعداد کے معاملے میں لووں سے بحث نہ کرو اور نہ ان کے متعلق کسی سے کچھ پوچھو');
INSERT INTO "sayyid_qutb" VALUES(18,23,'18:23','اور دیکھو ، کسی چیز کے بارے میں کبھی یہ نہ کہا کرو کہ میں کل یہ کام کر دوں گا');
INSERT INTO "sayyid_qutb" VALUES(18,24,'18:24','(تم کچھ نہیں کرسکتے) الایہ کہ اللہ چاہے۔ اگر بھولے سے ایسی بات زبان سے نکل جائے تو فوراً اپنے رب کو یاد کرو ، اور کہو امید ہے کہ میرا رب اس معاملے میں رشد سے قریب تر بات کی طرف میری رہنمائی فرما دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,25,'18:25','اور وہ اپنے غار میں تین سو سال رہے اور (کچھ لوگ مدت کے شمار میں) 9 سال اور بڑھ گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,26,'18:26','تم کہو اللہ ان کے قیام کی مدت زیادہ جانتا ہے ، آسمانوں اور زمین کے سب پوشیدہ احوال اسی کو معلوم ہیں ، کیا خوب ہے ، وہ دیکھنے والا اور سننے والا زمین و آسمان کی مخلوقات کا کوئی خبر گیر اس کے سوا نہیں اور وہ اپنی حکومت میں کسی کو شریک نہیں کرتا۔');
INSERT INTO "sayyid_qutb" VALUES(18,27,'18:27','اے نبی صلى الله عليه وسلم تمہارے رب کی کتاب میں سے جو کچھ تم پر وحی کیا گیا ہے اسے (جوں کا توں) سنا دو ، کوئی اس کے فرمودات کو بدل دینے کا مجاز نہیں ہے (اور اگر تم کسی کی خاطر اس میں ردو بدل کرو گے تو) اس سے بچ کر بھاگنے کے لئے کوئی جائے پناہ نہ پائو گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,28,'18:28','اور اپنے دل کو ان لوگوں کی معیت پر مطمئن کرو جو اپنے رب کی رضا کے طلبگار بن کر صبح و شام اسے پکارتے ہیں اور ان سے ہرگز نگاہ نہ پھیرو کیا تم دنیا کی زینت پسند کرتے ہو ؟ کسی ایسے شخص کی اطاعت نہ کرو ، جس کے دل کو ہم نے اپنی یاد سے غافل کردیا ہے اور جس نے اپنی خواہش نفس کی پیروی اختیار کرلی ہے اور جس کا طریق کا افراط وتفریط پر مبنی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,29,'18:29','صاف کہہ دو کہ یہ حق ہے تمہارے رب کی طرف سے ، اب جس کا جی چاہے مان لے اور جس کا جی چاہے انکار کر دے ہم نے (انکار کرنے والے) ظالموں کے لئے ایک آگ تیار کر رکھی ہے جس کی لپیٹیں انہیں گھیرے میں لے چکی ہیں۔ وہاں اگر وہ پانی مانگیں گے تو ایسے پانی سے ان کی تواضع کی جائے گی جو تیل کی تلچھٹ جیسا ہوگا اور ان کا منہ بھون ڈالے گا ، بدترین پینے کی چیز اور بہت بری آرام گاہ');
INSERT INTO "sayyid_qutb" VALUES(18,30,'18:30','رہے وہ لوگ جو مان لیں اور نیک عمل کریں ، تو یقیناً ہم نیکو کاروں کا اجر ضائع نہیں کیا کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(18,31,'18:31','ان کے لئے سدابہار جنتیں ہیں جن کے نیچے نہریں بہ رہی ہوں گی ، وہاں وہ سونے کے کنگنوں سے آراستہ کئے جائیں گے ، باریک ریشم اور اطلس و دیبا کے سبز کپڑے پہنیں گے اور اونچے مسندوں پر تکیے لگا کر بیٹھیں گے۔ بہترین اجر اور اعلیٰ درجے کی جائے قیام');
INSERT INTO "sayyid_qutb" VALUES(18,32,'18:32','اے نبی ؐ، ان کے سامنے مثال پیش کر دو ۔ دو شخص تھے۔ ان میں سے ایک کو ہم نے انگور کے دو باغ دیئے اور ان کے گرد کھجور کے درختوں کی باڑھ لگائی اور ان کے درمیان کاشت کی زمین رکھی۔');
INSERT INTO "sayyid_qutb" VALUES(18,33,'18:33','دونوں باغ خوب پھلے پھولے اور بار آور ہونے میں انہوں نے ذرا سی کسر بھی نہ چھوڑی۔ ان باغوں کے اندر ہم نے ایک نہر جاری کردی اور ات خوب نع حاصل ہوا');
INSERT INTO "sayyid_qutb" VALUES(18,34,'18:34','یہ کچھ پا کر ایک دن وہ اپنے ہمسائے سے بات کرتے ہوئے بولا ” میں تجھ سے زیادہ مالدار ہوں اور تجھ سے زیادہ طاقتور نفری رکھتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(18,35,'18:35','پھر وہ اپنی جنت میں داخل ہوا اور اپنے نفس کے حق میں ظالم بن کر کہنے لگا ” میں نہیں سمجھتا کہ یہ دولت کبھی فنا ہوجائے گی');
INSERT INTO "sayyid_qutb" VALUES(18,36,'18:36','اور مجھے توقع نہیں کہ قیامت کی گھڑی کب آئے گی۔ تاہم اگر کبھی مجھے توقع نہیں کہ قیامت کی گھڑی کب آئے گی۔ تاہم اگر کبھی مجھے اپنے رب کے حضور پلٹایا بھی گیا تو ضرور اس سے بھی زیادہ شاندار جگہ پائوں گا');
INSERT INTO "sayyid_qutb" VALUES(18,37,'18:37','اس کے ہمسائے نے گفتگو کرتے ہوئے اس سے کہا ” کیا تو کفر کرتا ہے اس ذلت سے جس نے تجھے مٹی اور پھر نطفے سے پیدا کیا اور تجھے ایک پورا آدمی بنا کر کھڑا کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(18,38,'18:38','رہا میں ، تو میرا رب تو وہی اللہ ہے اور میں اس کے ساتھ کسی کو شریک نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(18,39,'18:39','اور جب تو اپنی جنت میں داخل ہو رہا تھا تو اس وقت تیری زبان سے یہ کیوں نہ نکلا کہ ماشا اللہ ، فاقوہ الا باللہ ؟ اگر تو مجھے مال اور اولاد میں اپنے سے کمتر پا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(18,40,'18:40','تو بعید نہیں کہ میرا رب مجھے تیری جنت سے بہتر عطا فرما دے اور تیری جنت پر آسمان سے کوئی آفت بھیج دے جس سے وہ صاف میدان بن کر رہ جائے ،');
INSERT INTO "sayyid_qutb" VALUES(18,41,'18:41','یا اس کا پانی زمین میں اتر جائے اور پھر تو اسے کسی طرح نہ نکال سکے');
INSERT INTO "sayyid_qutb" VALUES(18,42,'18:42','آخر کار ہوا یہ کہ اس کا سارا ثمرہ مارا گیا اور وہ اپنے انگوروں کے باغ کو تنیوں پر الٹا پڑا دیکھ کر اپنی لگائی ہوئی لاگت پر ہاتھ ملتا رہ گیا اور کہنے لگا کہ ” کاش ! مَیں نے اپنے رب کے ساتھ کسی کو شریک نہ ٹھہرایا ہوتا');
INSERT INTO "sayyid_qutb" VALUES(18,43,'18:43','وہ ہوا اللہ کو چھوڑ کر اس کے پاس کوئی جتھا کہ اس کی مدد کرتا ، اور نہ کرسکا وہ آپ ہی اس آفت کا مقابلہ');
INSERT INTO "sayyid_qutb" VALUES(18,44,'18:44','اس وقت معلوم ہوا کہ کار سازی کا اختیار خدائے برحق کے لئے ہے ، انعام وہی بہتر ہے جو وہ بخشے اور انجام وہی بخیر ہے جو وہ دکھائے');
INSERT INTO "sayyid_qutb" VALUES(18,45,'18:45','اور اے نبی ‘ انہیں حیات دنیا کی حقیقت اس مثال سے سمجھائو کہ آج ہم نے آسمان سے پانی برسایا دیا تو زمین کی پود خوب گھنٹی ہوگئی ، اور کل وہی نباتات بھس بن کر رہ گئی جسے ہوائیں اڑائے لئے پھرتی ہیں۔ اللہ ہر چیز پر قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,46,'18:46','یہ مال اور یہ اولاد محض دنیوی زندگی کی ایک ہنگامی آرائش ہے۔ اصل میں تو باقی رہ جانے وال نیکیاں ہی تیرے رب کے نزدیک نتیجے کے لحاظ سے بہتر ہیں اور انہی سے اچھی امیدیں وابستہ کی جاسکتی ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,47,'18:47','فکر اس دن کی ہونی چاہئے جب کہ ہم پہاڑوں کو چلائیں گے ، اور تم زمین کو بالکل برہنہ پائو گے ، اور ہم تمام انسانوں کو اس طرح گھیر کر جمع کریں گے کہ (اگلوں پچھلوں میں سے) ایک بھی نہ چھوٹے گا');
INSERT INTO "sayyid_qutb" VALUES(18,48,'18:48','اور سب کے سب تمہارے رب کے حضور صف در صف پیش کئے جائیں گے ... لو دیکھ لو ، آگئے نا تم ہمارے پاس اسی طرح جیسا ہم نے تم کو پہلی بار یدا کیا تھا۔ تم نے تو یہ سمجھا تھا کہ ہم نے تمہارے لئے کوئی وعدے کا وقت مقرر ہی نہیں کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(18,49,'18:49','اور نامہ اعمال سامنے رکھ دیا جائے گا اس وقت تم دیکھو کے مجرم لوگ اپنی کتاب زندگی کے اندراجات سے ڈر رہے ہوں گے اور کہہ رہے ہوں گے کہ ” ہائے ہماری کم بختی ، یہ کیسی کتاب ہے کہ ہماری کوئی چھوٹی بڑی حرکت ایسی نہیں رہی جو اس میں درج نہ ہوگئی ہو۔ “ جو کچھ انہوں نے کہا تھا ، وہ سب اپنے سامنے حاضر پائیں گے اور تیرا رب کیسی پر ذرا ظلم نہ کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,50,'18:50','” یاد کرو ، جب ہم نے فرشتوں سے کہا کہ آدم کو سجدہ کرو تو انہوں نے سجدہ کیا مگر ابلیس نے نہ کیا۔ وہ جنوں میں سے تھا اس لئے اپنے رب کے حکم کی اطاعت سے نکل گیا۔ اب کیا تم مجھے چھوڑ کر اس کو اور اس کی ذریت کو اپنا سرپرست بناتے ہو ، حالانکہ وہ تمہارے دشمن ہیں ؟ بڑا ہی برا بدل ہے جسے ظالم لوگ اختیار کر رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,51,'18:51','میں نے آسمان و زمین پیدا کرتے وقت ان کو نہیں بلایا تھا اور نہ خود ان کی اپنی تخلیق میں انہیں شریک کیا تھا۔ میرا کام یہ نہیں ہے کہ گمراہ کرنے والوں کو اپنا مددگار بنایا کروں۔');
INSERT INTO "sayyid_qutb" VALUES(18,52,'18:52','پھر کیا کریں گے یہ لوگ اس روز جب کہ ان کا رب ان سے کہے گا کہ پکارو اب ان ہستیوں کو جنہیں تم میرا شریک سمجھ بیٹھے تھے۔ یہ ان کو پکاریں گے ، مگر وہ ان کی مدد کو نہ آئیں گے اور ہم ان کے درمیان ایک ہی ہلاکت کا گڑھا مشترک کردیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,53,'18:53','سارے مجرم اس روز آگ دیکھیں گے اور سمجھ لیں گے کہ اب انہیں اس میں گرنا ہے۔ اور وہ اس سے بچنے کے لئے کوئی جائے پناہ نہ پائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,54,'18:54','ہم نے اس قرآن میں لوگوں کو طرح طرح سے سمجھایا گیا مگر انسان بڑا ہی جھگڑا لو واقع ہوا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,55,'18:55','ان کے سامنے جب ہدایت آئی تو اسے ماننے اور اپنے رب کے حضور معانی چاہئے سے آخر ان کو کس چیز نے روک دیا ؟ اس کے وسا اور کچھ نہیں کہ وہ منتظر ہیں کہ ان کے ساتھ بھی وہی کچھ ہو جو پچھلی قوموں کے ساتھ ہوچکا ہے ، یا یہ کہ وہ عذاب کو سامنے آتے دیکھ لیں');
INSERT INTO "sayyid_qutb" VALUES(18,56,'18:56','رسولوں کو ہم اس کام کے سوا اور کسی غرض کے لئے نہیں بھیجتے کہ وہ بشارت اور تنبیہ کی خدمت انجام دے دیں۔ مگر کافروں کا یہ حال ہے کہ وہ باطل کے ہتھیار لے کر حق کو نیچا دکھانے کی کوشش کرتے ہیں اور انہوں نے میری آیات کو اور ان تنبیہات کو جو انہیں کی گئیں ، مذاق بنا لیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,57,'18:57','اور اس شخص سے بڑھ کر ظالم اور کون ہے جسے اس کے رب کی آیات سنا کر نصیحت کی جائے اور وہ ان سے منہ پھیرے اور اس برے انجام کو بھول جائے جس کا سروسامان اس نے اپنے لیے خود اپنے ہاتھوں کیا ہے ؟ (جن لوگوں نے یہ روش اختیار کی ہے) ان کے دلوں پر ہم نے غلاف چڑھا دیئے ہیں جو انہیں قرآن کی بات نہیں سمجھنے دیتے اور ان کے کانوں میں ہم نے گرانی پیدا کردی ہے۔ تم انہیں ہدایت کی طرف کتنا ہی بلائو ، وہ اس حالت میں کبھی ہدایت نہ پائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,58,'18:58','تیرا رب بڑا درگزر کرنے والا اور رحیم ہے۔ وہ ان کے کرتوتوں پر انہیں پکڑنا چاہتا تو جلدی ہی عذاب بھیج دیتا۔ مگر ان کے لئے وعدے کا ایک وقت مقرر ہے اور اس سے بچ کر بھاگ نکلنے کی یہ کوئی راہ نہ پائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,59,'18:59','یہ عذاب رسیدہ بستیاں تمہارے سامنے موجود ہیں۔ انہوں نے جب ظلم کیا تو ہم نے انہیں ہلاک کردیا اور ان میں سے ہر ایک کی ہلاکت کے لئے ہم نے وقت مقرر کر رکھا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(18,60,'18:60','(ذرا ان کو وہ قصہ سنائو جو موسیٰ کو پیش آیا تھا) جبکہ موسیٰ نے اپنے خادم سے کہا تھا کہ ” میں اپنا سفر ختم نہ کروں گا جب تک دونوں دریائوں کے سنگم پر نہ پہنچ جائوں ، ورنہ میں ایک زمانہ دراز تک چلتا ہی رہوں گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,61,'18:61','پس جب وہ ان کے سنگم پر پہنچے تو اپنی مچھلی سے غافل ہوگئے اور وہ نکل کر اس طرح دریا میں چلی گئی جیسے کہ کوئی سرنگ لگی ہو۔');
INSERT INTO "sayyid_qutb" VALUES(18,62,'18:62','آگے جا کر موسیٰ (علیہ السلام) نے اپنے خادم سے کہا ” لائو ہمارا ناشتہ ، آج کے سفر میں تو ہم بری طرح تھک گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,63,'18:63','خادم نے کہا ” آپ نے دیکھا ، یہ کیا ہوا ؟ جب ہم اس چٹان کے پاس ٹھہرے ہوئے تھے اس وقت مجھے مچھلی کا خیال نہ رہا اور شیطان نے مجھ کو ایسا غافل کردیا کہ وہ اس کا ذکر (آپ سے کرنا) بھول گیا۔ مچھلی تو عجیب طریقے سے نکل کر دریا میں چلی گئی۔');
INSERT INTO "sayyid_qutb" VALUES(18,64,'18:64','موسیٰ (علیہ السلام) نے کہا ” اسی کی تو ہمیں تلاش تھی۔ “ چناچہ وہ دونوں اپنے نقش قدم پر پھر واپس ہوئے');
INSERT INTO "sayyid_qutb" VALUES(18,65,'18:65','اور وہاں انہوں نے ہمارے بندوں میں سے ایک بندے کو پایا جسے ہم نے اپنی رحمت سے نوازا تھا اور اپنی طرف سے ایک خاص علم عطا کیا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(18,66,'18:66','موسیٰ نے اس سے کہا ” کیا میں آپ کے ساتھ رہ سکتا ہوں تاکہ آپ مجھے بھی اس دانش کی تعلیم دیں جو آپ کو سکھائی گئی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(18,67,'18:67','اس نے جواب دیا ” آپ میرے ساتھ صبر نہیں کرسکتے ،');
INSERT INTO "sayyid_qutb" VALUES(18,68,'18:68','اور جس چیز کی آپ کو خبر نہ ہو ، آپ اس پر صبر کر بھی کیسے سکتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,69,'18:69','موسیٰ نے کہا ” انشاء اللہ ، آپ مجھے صابر پائیں گے اور میں کسی معاملہ میں آپ کی نافرمانی نہ کروں گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,70,'18:70','اس نے کہا ” اچھا ، اگر آپ میرے ساتھ چلتے ہیں تو مجھ سے کوئی بات نہ پوچھیں جب تک کہ میں خود اس کا آپ سے ذکر نہ کروں۔');
INSERT INTO "sayyid_qutb" VALUES(18,71,'18:71','” اب وہ دونوں روانہ ہوئے ، یہاں تک کہ وہ ایک کشتی میں سوار ہوگئے تو اس شخص نے کشتی میں شگاف ڈال دیا۔ حضرت موسیٰ (علیہ السلام) ” آپ نے اس میں شگاف ڈال دیا تاکہ سب کشتی والوں کو ڈبو دیں ؟ یہ تو آپ نے ایک سخت حرکت کر ڈالی۔ “ یہ عبدصالح نہایت برد باری سے اور نہایت ہی سنجیدگی سے یاد دلاتے ہیں کہ ہمارے درمیان معاہدہ کیا طے ہوا تھا ؟');
INSERT INTO "sayyid_qutb" VALUES(18,72,'18:72','اس نے کہا ” میں نے تم سے کہا نہ تھا کہ تم میرے ساتھ صبر نہیں کرسکتے۔');
INSERT INTO "sayyid_qutb" VALUES(18,73,'18:73','موسیٰ نے کہا ” بھول چوک پر مجھے نہ پکڑیے میرے معاملے میں آپ ذرا سختی سے کام نہ لیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,74,'18:74','پھر وہ دونوں چلے ، یہاں تک کہ ان کو ایک لڑکا ملا اور اس شخص نے اسے قتل کردیا۔ حضرت موسیٰ (علیہ السلام) نے کہا ” آپ نے ایک بےگناہ کی جان لے لی حالانکہ اس نے کسی کا خون نہ کیا تھا ؟ یہ کام تو آپ نے بہت ہی برا کیا۔');
INSERT INTO "sayyid_qutb" VALUES(18,75,'18:75','اس نے کہا ” میں نے تم سے کہا نہ تھا کہ تم میرے ساتھ صبر نہیں کرسکتے۔');
INSERT INTO "sayyid_qutb" VALUES(18,76,'18:76','موسیٰ (علیہ السلام) نے کہا ” اس کے بعد اگر میں آپ سے کچھ پوچھوں تو آپ مجھے ساتھ نہ رکھیں۔ لیجیے اب تو میری طرف سے آپ کو عذر مل گیا۔');
INSERT INTO "sayyid_qutb" VALUES(18,77,'18:77','پھر وہ آگے چلے یہاں تک کہ ایک بستی میں پہنچے اور وہاں کے لوگوں سے کھانا مانگا تو انہوں نے ان دونوں کی ضیافت سے انکار کردیا۔ وہاں انہوں نے ایک دیوار دیکھی ، جو گرا چاہتی تھی۔ اس شخص نے اس دیوار کو پھر قائم کردیا۔ موسیٰ (علیہ السلام) نے کہا۔ اگر آپ چاہتے تو اس کام کی اجرت لے سکتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(18,78,'18:78','اس نے کہا۔ بس میرا تمہارا ساتھ ختم ہوا۔ اب میں تمہیں ان باتوں کی حقیقت بتاتا ہوں جن پر تم صبر نہ کرسکے۔');
INSERT INTO "sayyid_qutb" VALUES(18,79,'18:79','اس کشتی کا معاملہ یہ ہے کہ وہ چند غریب آدمیوں کی تھی جو دریا میں محنت مزدوری کرتے تھے۔ میں نے چاہا کہ اسے عیب دار کردوں ، کیونکہ آگے ایک ایسے بادشاہ کا علاقہ تھا جو ہر کشتی کو زبردستی چھین لیتا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(18,80,'18:80','رہا وہ لڑکا ، تو اس کے والدین مومن تھے ، ہمیں اندیشہ ہوا کہ یہ لڑکا اپنی سرکشی اور کفر سے ان کو تنگ کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,81,'18:81','اس لئے ہم نے چاہا کہ ان کا رب اس کے بدلے ان کو ایسی اولاد دے جو اخلاق میں بھی اس سے بہتر ہو اور جس سے صلہ رحمی بھی زیادہ متوقع ہو۔');
INSERT INTO "sayyid_qutb" VALUES(18,82,'18:82','اور اس دیوار کا معاملہ یہ ہے کہ یہ دو یتیم لڑکوں کی ہے جو اس شہر میں رہتے ہیں۔ اس دیوار کے نیچے ان بچوں کے لئے ایک خزانہ مدفون ہے اور ان کا باپ ایک نیک آدمی تھا اس لئے تمہارے رب نے چاہا کہ یہ دونوں بچے بالغ ہوں اور اپنا خزانہ نکال لیں۔ یہ تمہارے رب کی رحمت کی بنا پر کیا گیا ہے ، میں نے کچھ اپنے اختیار سے نہیں کردیا ہے۔ یہ ہے حقیقت ان باتوں کی جس پر تم صبر نہ سکے۔');
INSERT INTO "sayyid_qutb" VALUES(18,83,'18:83','اور اے نبی یہ لوگ تم سے ذوالقرنین کے بارے میں پوچھتے ہیں۔ ان سے کہو ، اس کا کچھ حال میں تم کو سناتا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(18,84,'18:84','” ہم نے اس کو زمین میں اقتدار عطا کر رکھا تھا اور اسے ہر قسم کے اسباب و وسائل بخشے۔');
INSERT INTO "sayyid_qutb" VALUES(18,85,'18:85','اس نے (پہلے مغرب کی طرف ایک مہم کا) سرو سامان کیا');
INSERT INTO "sayyid_qutb" VALUES(18,86,'18:86','حتیٰ کہ جب وہ غروب آفتاب کی حد تک پہنچ گیا تو اس نے سورج کو ایک کالے پانی میں ڈوبتے دیکھا۔ وہاں اسے ایک قوم ملی۔ ہم نے کہا ، ذوالقرنین ، تجھے یہ مقدرت بھی حاصل ہے کہ ان کو تکلیف پہنچائے اور یہ بھی کہ ان کے ساتھ نیک رویہ اختیار کرے۔');
INSERT INTO "sayyid_qutb" VALUES(18,87,'18:87','اس نے کہا ” جو ان میں سے ظلم کرے گا ہم اس کو سز دیں گے ، پھر وہ اپنے رب کی طرف بلایا جائے گا اور وہ اسے اور زیادہ سخت عذاب دے گا');
INSERT INTO "sayyid_qutb" VALUES(18,88,'18:88','اور جو ان میں سے ایمان لائے گا ، اور نیک عمل کرے گا ، اس کے لئے اچھی جزا ہے اور ہم اس کو نرم احکام دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,89,'18:89','” پھر اس نے (ایک دوسری مہم کی) تیاری کی ،');
INSERT INTO "sayyid_qutb" VALUES(18,90,'18:90','یہاں تک کہ وہ طلوع آفتاب کی حد تک جا پہنچا۔ وہاں اس نے دیکھا کہ سورج ایک ایسی قوم پر طلوع ہو رہا ہے جس کے لئے دھوپ سے بچنے کا کوئی سامان ہم نے نہیں کیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,91,'18:91','یہ حال تھا ان کا اور ذوالقرنین کے پاس جو کچھ تھا اسے ہم جانتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(18,92,'18:92','” پھر اس نے (ایک اور مہم کا) سامان کیا');
INSERT INTO "sayyid_qutb" VALUES(18,93,'18:93','یہاں تک کہ جب وہ دو پہاڑوں کے درمیان پہنچا تو اسے ان کے پاس ایک قوم ملی جو مشکل ہی سے کوئی بات سمجھتی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(18,94,'18:94','ان لوگوں نے کہا کہ اے ذوالقرنین ، یاجوج اور ماجوج اس سر زمین میں فساد پھیلاتے ہیں ، ہم اگر تجھے کوئی ٹیکس اس کام کے لئے دیں تو ہمارے اور ان کے درمیان ایک بند تعمیر کر دے ؟');
INSERT INTO "sayyid_qutb" VALUES(18,95,'18:95','اس نے کہا ” جو کچھ میرے رب نے مجھے دے رکھا ہے وہ بہت ہے۔ تم بس قوت سے میری مدد کرو۔ “ میں تمہارے اور ان کے درمیان بند بنائے دیتا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(18,96,'18:96','مجھے لوہے کی چادریں لا دو ۔ آخر جب دونوں پہاڑوں کے درمیانی خلا کو اس نے پاٹ دیا تو لوگوں سے کہا کہ اب آگ دہکائو حتی ٰ کہ (یہ آہنی دیوار) بالکل آگ کی طرح سرخ کردی تو اس نے کہا ” لائو ، اب میں اس پر پگھلا ہوا تانبا انڈیلوں گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(18,97,'18:97','(یہ بند ایسا تھا کہ) یاجوج و ماجوج اس پر چڑھ کر بھی نہ آسکتے تھے اور اس میں نقب لگانا ان کے لئے اور بھی مشکل تھا۔');
INSERT INTO "sayyid_qutb" VALUES(18,98,'18:98','ذوالقرنین نے کہا ” یہ میرے رب کی رحمت ہے مگر جب میرے رب کے وعدے کا وقت آئے گا تو وہ اس کو پیوند خاک کر دے گا ، اور میرے رب کا وعدہ برحق ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,99,'18:99','” اور اس روز ہم لوگوں کو چھوڑ دیں گے کہ (سمندر کی موجوں کی رطح) ایک دوسرے سے گتھم گتھا ہوں اور صور پھونکا جائے گا اور ہم سب انسانوں کو ایک ساتھ جمع کریں گے');
INSERT INTO "sayyid_qutb" VALUES(18,100,'18:100','اور وہ دن ہوگا جب ہم جہنم کو کافروں کے سامنے لائیں گے');
INSERT INTO "sayyid_qutb" VALUES(18,101,'18:101','ان کافروں کے سامنے جو میری نصیحت کی طرف سے اندھے بنے ہوئے تھے اور کچھ سننے کے لئے تایر ہی نہ تھے۔');
INSERT INTO "sayyid_qutb" VALUES(18,102,'18:102','تو کیا یہ لوگ ، جنہوں نے کفر اختیار کیا ہے ، یہ خیال رکھتے ہیں کہ مجھے چھوڑ کر میرے بندوں کو اپنا کار ساز بنا لیں ؟ ہم نے ایسے کافروں کی ضیافت کے لئے جنم تیار کر رکھی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,103,'18:103','” اے نبی ان سے کہو ، کیا ہم تمہیں بتائیں کہ اپنے اعمال میں سب سے زیادہ ناکام و نامراد کون لوگ ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(18,104,'18:104','وہ کہ دنیا کی زندگی میں جن کی ساری سعی و جہد راہ راست سے بھٹکی رہی اور وہ سمجھتے رہے کہ وہ سب کچھ ٹھیک کر رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(18,105,'18:105','یہ وہ لوگ ہیں جنہوں نے اپنے رب کی آیات کو ماننے سے انکار کیا اور اس کے حضور پیشی کا یقین نہ کیا۔ اس لئے ان کے سارے اعمال ضائع ہوگئے ، قیامت کے روز ہم انہیں کوئی وزن نہ دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,106,'18:106','” ان کی جزا جہنم ہے اس کفر کے بدلے جو انہوں نے کیا اور اس مذاق کی پاداش میں جو وہ میری آیات اور میرے رسولوں کے ساتھ کرتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(18,107,'18:107','” البتہ وہ لوگ جو ایمان لاے اور جنہوں نے نیک عمل کئے ، ان کی میزبانی کے لئے فردوس کے باغ ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(18,108,'18:108','وہ ان میں ہمیشہ رہیں گے اور کبھی اس جگہ سے نکل کر کہیں جانے کو ان کا جی نہ چاہے گا۔');
INSERT INTO "sayyid_qutb" VALUES(18,109,'18:109','” اے نبی کہو کہ اگر سمندر میرے رب کی باتیں لکھنے کے لئے روشنائی بن جائے تو وہ ختم ہوجائے ، مگر میرے رب کی باتیں ختم نہ ہوں ، بلکہ اگر اتنی ہی روشنائی ہم اور لے آئیں تو وہ بھی کفایت نہ کرے۔');
INSERT INTO "sayyid_qutb" VALUES(18,110,'18:110','اے نبی کہو کہ میں تو ایک انسان ہوں تم ہی جیسا میری طرف وحی کی جاتی ہے کہ تمہارا خدا بس ایک ہی خدا ہے ، پس جو کوئی اپنے رب کی ملاقات کا امیدوار ہو ، اسے چاہئے کہ نیک عمل کرے اور بندگی میں اپنے رب کے ساتھ کسی اور کو شریک نہ کرے۔');
INSERT INTO "sayyid_qutb" VALUES(19,1,'19:1','كۗهٰيٰعۗصۗ');
INSERT INTO "sayyid_qutb" VALUES(19,2,'19:2','ذکر ہے اس رحمت کا جو تیرے رب نے اپنے بندے زکریا پر کی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(19,3,'19:3','جبکہ اس نے اپنے رب کو چپکے چپکے پکارا۔');
INSERT INTO "sayyid_qutb" VALUES(19,4,'19:4','اس نے عرض کیا :” اے پروردگار ، میری ہڈیاں تک گھل گئی ہیں اور سر بڑھاپے سے بھڑک اٹھا ہے اے پروردگار میں کبھی تجھ سے دعا مانگ کرنا مراد نہیں رہا۔');
INSERT INTO "sayyid_qutb" VALUES(19,5,'19:5','مجھے اپنے پیچھے اپنے بھائی بندوں کی برائیوں کا خوف ہے اور میری بیوی بانجھ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,6,'19:6','تو مجھے اپنے فضل خاص سے ایک وارث عطا کر دے جو میرا وارث بھی ہو اور آل یعقوب کی مثراب بھی پائے اور اے پروردگار ، اس کو ایک پسندیدہ انسان بنا۔');
INSERT INTO "sayyid_qutb" VALUES(19,7,'19:7','(جواب دیا گیا) ” اے زکریا ، ہم تجھے ایک لڑکے کی بشارت دیتے ہیں جس کا نام یحییٰ ہوگا۔ ہم نے اس نام کا کوئی آدمی اس سے پہلے پیدا نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(19,8,'19:8','“ عرض کیا ” پروردگار ، بھلا میرے ہاں کیسے بیٹا ہوگا جبکہ میری بیوی بانجھ ہے اور میں بوڑھا ہو کر سوکھ چکا ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(19,9,'19:9','جواب ملا ” ایسا ہی ہوگا۔ تیرا رب فرماتا ہے کہ تہ یو تمیرے لئے ایک ذرا سی بات ہے ، آر اس سیپ ہلے میں تجھے پیدا کرچکا ہوں جب کہ تو کوئی چیز نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(19,10,'19:10','“ زکریا نے کہا ” پروردگار ، میرے لئے کوئی نشانی مقرر کر دے۔ “ فرمایا ” تیرے لئے نشانی یہ ہے کہ تو پیہم تین دن لوگوں سے بات نہ کرسکے گا۔');
INSERT INTO "sayyid_qutb" VALUES(19,11,'19:11','چناچہ وہ محراب سے نکل کر اپنی قوم کے سامنے آیا اور اس نے اشارے سے ان کو ہدایت کی کہ صبح و شام تسبیح کرو۔');
INSERT INTO "sayyid_qutb" VALUES(19,12,'19:12','اے یحییٰ کتاب الٰہی کو مضبوط تھام لے۔ ہم نے بچپن ہی میں ” حکم “ سے نوازا');
INSERT INTO "sayyid_qutb" VALUES(19,13,'19:13','اور اپنی طرف سے اس کو نرم دلی اور پاکیزگی عطا کی اور وہ بڑا پرہیز گار');
INSERT INTO "sayyid_qutb" VALUES(19,14,'19:14','اور اپنے والدین کا حق شناس تھا۔ وہ جبار نہ تھا اور نہ نافرمان۔');
INSERT INTO "sayyid_qutb" VALUES(19,15,'19:15','سلام اس پر جس روز کہ وہ پیدا ہوا اور جس دن وہ مرے اور جس روز وہ زندہ کر کے اٹھایا جائے۔');
INSERT INTO "sayyid_qutb" VALUES(19,16,'19:16','اور اے نبی اس کتاب میں مریم کا حال بیان کرو ، جبکہ وہ اپنے لوگوں سے الگ ہو کر شرقی جانب گوشہ نشین ہوگئی تھی');
INSERT INTO "sayyid_qutb" VALUES(19,17,'19:17','اور پردہ ڈال کر ان سے چھپ بیٹھی تھی۔ اس حلات میں ہم نے اس کے پاس اپنی روح کو (یعنی فرشتے کو) بھیجا اور وہ اس کے سامنے ایک پورے انسان کی شکل میں نمودار ہوگیا۔');
INSERT INTO "sayyid_qutb" VALUES(19,18,'19:18','مریم کی ایک بول اٹھی کہ ” اگر تو کوئی خدا ترس آدمی ہے تو میں تجھ سے خدائے رحمٰن کی پناہ مانگتی ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(19,19,'19:19','اس نے کہا ” میں تو تیرے رب کا فرستادہ ہوں اور اسلئے بھیجا گیا ہوں کہ تجھے ایک پاکیزہ لڑکا دوں۔');
INSERT INTO "sayyid_qutb" VALUES(19,20,'19:20','مریم نے کہا ” میرے ہاں کیسے لڑکا ہوگا جبکہ مجھے کسی بشر نے چھوا تک نہیں ہے اور میں کوئی بدکار عورت نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(19,21,'19:21','فرشتے نے کہا ” ایسا ہی ہوگا تیرا رب فرماتا ہے کہ ایسا کرنا میرے لئے بہت آسان ہے اور ہم یہ اس لئے کریں گے کہ اس لڑکے کو لوگوں کے لئے ایک نشانی بنائیں اور اپنی طرف سے ایک رحمت۔ اور یہ کام ہو کر رہنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,22,'19:22','مریم کو اس بچے کا حمل رہ گیا اور وہ اس حملے کو لئے ایک دور کے مقام پر چلی گیئ،');
INSERT INTO "sayyid_qutb" VALUES(19,23,'19:23','پھر زچگی کی تکلیف نے اسے ایک کھجور کے درخت کے نیچے پہنا دیا۔ وہ کہنے لگی ” کاش میں اس سے پہلے ہی مر جاتی ہے اور میرا نام و نشان نہ رہتا۔');
INSERT INTO "sayyid_qutb" VALUES(19,24,'19:24','بچے نے نیچے سے اس کو پکار کر کہا ” غم نہ کر تیرے رب نے تیرے نیچے ایک چشمہ رواں کردیا ہے');
INSERT INTO "sayyid_qutb" VALUES(19,25,'19:25','اور تو ذرا اس درخت کے تنے کو ہلاک ، تیرے اوپر تازہ کھجوریں ٹپک پڑیں گی۔');
INSERT INTO "sayyid_qutb" VALUES(19,26,'19:26','پس تو کھا اور اپنی آنکھیں ٹھنڈی کر۔ پھر اگر کوئی آدمی تھے نظر آئے تو اس سے کہہ دے کہ میں نے رحمن کے لئے روزے کی نذر مانی ہے ، اس لئے آج میں کسی سے نہ بولوں گی۔');
INSERT INTO "sayyid_qutb" VALUES(19,27,'19:27','پھر وہ اس بچے کو لئے ہوئے اپنی قوم میں آئی۔ لوگ کہنے لگے ” اے مریم ، یہ تو تو نے بڑا پاپ کر ڈالا۔');
INSERT INTO "sayyid_qutb" VALUES(19,28,'19:28','اے رہارون کی بہن ، نہ تیرا باپ کوئی برا آدمی تھا اور نہ تیرا ماں ہی کوئی بدکار عورت تھی۔');
INSERT INTO "sayyid_qutb" VALUES(19,29,'19:29','مریم نے بچے کی طرف اشارہ کیا۔ لوگوں نے کہا ” ہم اس سے کیا بات کریں جو گہوارے میں پڑا ہوا ایک بچہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,30,'19:30','بچہ بول اٹھا ” میں اللہ کا بندہ ہوں ، اس نے مجھے کتاب دی ، اور بنی بنایا ،');
INSERT INTO "sayyid_qutb" VALUES(19,31,'19:31','اور بابرکت کیا جہاں بھی میں رہوں ، اور نماز اور زکوۃ کی پابندی کا حکم دیا جب تک میں زندہ رہوں ،');
INSERT INTO "sayyid_qutb" VALUES(19,32,'19:32','اور اپنی والدہ کا حق ادا کرنے والا بنایا اور مجھ کو جبار اور شقی نہیں بنایا۔');
INSERT INTO "sayyid_qutb" VALUES(19,33,'19:33','سلام ہے مجھ پر جبکہ میں پیدا ہوا اور جبکہ میں مروں اور جبکہ زندہ کر کے اٹھایا جائوں۔');
INSERT INTO "sayyid_qutb" VALUES(19,34,'19:34','یہ ہے عیسٰی ابن مریم اور یہ ہے اس کے بارے میں وہ سچی بات جس میں لوگ شک کررہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(19,35,'19:35','اللہ کا یہ کام نہیں کہ وہ کسی کو بیٹا بنائے۔ وہ پاک ذات ہے وہ جب کسی بات کا فیصلہ کرتا ہے تو کہتا ہے کہ ہوجا ‘ اور بس وہ ہوجاتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,36,'19:36','(اور عیسیٰ نے کہا تھا کہ ) ” اللہ میرا رب بھی ہے اور تمہارا رب بھی ‘ پس تم اسی کی بندگی کرو ‘ یہی سیدھی راہ ہے');
INSERT INTO "sayyid_qutb" VALUES(19,37,'19:37','مگر پھر مختلف گروہ باہم اختلاف کرنے لگے۔ سو جن لوگوں نے کفر کیا ان کے لئے وہ وقت بڑی تباہی کا ہوگا جب کہ وہ ایک بڑا دن دیکھیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(19,38,'19:38','جب وہ ہمارے سامنے حاضر ہوں گے اس روز تو ان کے کان بھی خوب سن رہے ہوں گے اور ان کی آنکھیں بھی خوب دیکھتی ہوں گی ‘ مگر آج یہ ظالم کھلی گمراہی میں مبتلا ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(19,39,'19:39','اے نبی صلى الله عليه وسلم اس حالت میں جب کہ یہ لوگ غافل ہیں اور ایمان نہیں لارہے ہیں ‘ انہیں اس دن سے ڈرا دو جبکہ فیصلہ کردیا جائے گا اور پیچھتاوے کے سوا کوئی چارہ کار نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(19,40,'19:40','آخر کار ہم ہی زمین اور اس کی ساری چیزوں کے وارث ہوں گے اور سب ہماری طرف پلٹائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(19,41,'19:41','اور اس کتاب میں ابراہیم (علیہ السلام) کا قصہ بیان کرو ‘ بیشک وہ ایک راست باز انسان اور ایک نبی تھا');
INSERT INTO "sayyid_qutb" VALUES(19,42,'19:42','(انہیں ذرا اس موقع کی یاد دلائوض جبکہ اس نے اپنے باپ سے کہا کہ ” ابا جان ‘ آپ کیوں ان چیزوں کی عبادت کرتے ہیں جو نہ سنتی ہیں نہ دیکھتی ہیں اور نہ آپ کا کوئی کام بنا سکتی ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(19,43,'19:43','ابا جان ‘ میرے پاس ایک ایسا علم آیا ہے جو آپ کے پاس نہیں آیا ‘ آپ میرے پیچھے چلیں ‘ میں آپ کو سیدھا راستہ بتائوں گا۔');
INSERT INTO "sayyid_qutb" VALUES(19,44,'19:44','ابا جان ‘ آپ شیطان کی بندگی نہ کریں ‘ شیطان تو رحمن کا نافرمان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,45,'19:45','ابا جان ‘ مجھے ڈر ہے کہ کہیں آپ رحمن کے عذاب میں مبتلا نہ ہوجائیں اور شیطان کے ساتھی بن کر رہیں');
INSERT INTO "sayyid_qutb" VALUES(19,46,'19:46','باپ نے کہا ‘” ابراہیم ‘ کیا تو میرے معبودوں سے پھر گیا ہے ؟ اگر تو باز نہ آیا تو میں تجھے سنگسار کردوں گا۔ بس تو ہمیشہ کے لیے مجھ سے الگ ہوجا ؟');
INSERT INTO "sayyid_qutb" VALUES(19,47,'19:47','ابراہیم نے کہا ” سلام ہے آپ پر۔ میں اپنے رب سے دعا کروں گا کہ آپ کو معاف کردے ‘ میرا رب مجھ پر بڑا ہی مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,48,'19:48','میں آپ لوگوں کو بھی چھوڑتا ہوں اور ان ہستیوں کو بھی جنہیں آپ لوگ خدا کو چھوڑ کر پکارا کرتے ہیں۔ میں تو اپنے رب ہی کو پکاروں گا ‘ امید ہے کہ میں اپنے رب کو پکار کرنا مراد نہ رہوں گا');
INSERT INTO "sayyid_qutb" VALUES(19,49,'19:49','پس جب وہ ان لوگوں سے اور ان کے معبود ان غیر اللہ سے جدا ہوگیا تو ہم نے اس کو اسحاق اور یعقوب جیسی اولاد دی اور ہر ایک کو نبی بنایا');
INSERT INTO "sayyid_qutb" VALUES(19,50,'19:50','اور ان کو اپنی رحمت سے نوازا اور ان کو سچی ناموری عطا کی');
INSERT INTO "sayyid_qutb" VALUES(19,51,'19:51','اور ذکر کرو اس کتاب میں موسیٰ صلى الله عليه وسلم کا۔ وہ ایک چیدہ شخص تھا اور رسول نبی تھا۔');
INSERT INTO "sayyid_qutb" VALUES(19,52,'19:52','ہم نے اس کو طور کے داہنی جانب سے پکارا اور راز کی گفتگو سے اس کو تقریب عطا کیا');
INSERT INTO "sayyid_qutb" VALUES(19,53,'19:53','اور اپنی مہربانی سے اس کے بھائی ہارون کو نبی بنا کر اسے (مدد گار کے طور پر ) دیا');
INSERT INTO "sayyid_qutb" VALUES(19,54,'19:54','اور اس کتاب میں اسماعیل کا ذکر کرو۔ وہ وعدے کا سچا تھا اور رسول نبی تھا۔');
INSERT INTO "sayyid_qutb" VALUES(19,55,'19:55','وہ اپنے گھر والوں کو نماز اور زکوۃ کا حکم دیتا تھا اور اپنے رب کے نزدیک ایک پسندیدہ انسان تھا');
INSERT INTO "sayyid_qutb" VALUES(19,56,'19:56','اور کتاب میں ادریس کا ذکر کرو ‘ وہ ایک راست باز انسان اور ایک نبی تھے');
INSERT INTO "sayyid_qutb" VALUES(19,57,'19:57','اور اسے ہم نے بلند مقام پر اٹھایا تھا');
INSERT INTO "sayyid_qutb" VALUES(19,58,'19:58','یہ وہ پیغمبر ہیں جن پر اللہ نے انعام فرمایا ‘ آدم کی اولاد میں سے ‘ اور ان لوگوں کی نسل میں سے جنہیں ہم نے نوح کے ساتھ کشتی پر سوار کیا تھا ‘ اور ابراہیم کی نسل میں سے اور اسرائیل کی نسل میں سے اور یہ ان لوگوں میں سے تھے جن کو ہم نے ہدایت بخشی اور برگزیدہ کیا۔ ان کا حال یہ تھا کہ جب رحمن کی آیات ان کو سنائی جاتیں تو روتے ہوئے سجدے میں گر جاتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(19,59,'19:59','پھر ان کے بعد وہ نا خلف لوگ ان کے جانشین ہوئے جنہوں نے نماز کو ضائع کیا اور خواہشات نفس کی پیروی کی ‘ پس قریب ہے کہ وہ گمراہی کے انجام سے دو چار ہوں');
INSERT INTO "sayyid_qutb" VALUES(19,60,'19:60','البتہ جو توبہ کریں اور ایمان لے آئیں اور نیک عملی اختیار کرلیں ‘ وہ جنت میں داخل ہوں گے اور ان کی ذرہ برابر حق تلفی نہ ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(19,61,'19:61','ان کے لئے ہمیشہ رہنے والی جنتیں ہیں جن کا رحمن نے اپنے بندوں سے در پردہ وعدہ کر رکھا ہے اور یقیناً یہ وعدہ پورا ہو کر رہنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,62,'19:62','وہاں وہ کوئی بیہودہ بات نہ سنیں گے ‘ جو کچھ بھی سنیں گے ‘ ٹھیک ہی سنیں گے اور ان کا رزق انہیں پہیم صبح و شام ملتا رہے گا۔');
INSERT INTO "sayyid_qutb" VALUES(19,63,'19:63','یہ ہے وہ جنت جس کا وارث ہم اپنے بندوں میں سے اس کو بنائیں گے جو پرہیز گار رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(19,64,'19:64','اے نبی صلى الله عليه وسلم ‘ ہم تمہارے رب کے حکم کے بغیر نہیں اترا کرتے۔ جو کچھ ہمارے آگے ہے اور جو کچھ ہمارے پیچھے ہے اور جو کچھ اس کے درمیان ہے ‘ ہر چیز کا مالک وہی ہے اور تمہارا رب بھولنے والا نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,65,'19:65','وہ رب ہے آسمانوں اور زمین کا اور ان ساری چیزوں کا جو آسمانوں اور زمین کے درمیان ہیں ‘ پس تم اس کی بندگی کرو اور اسی کی بندگی پر ثابت قدم رہو۔ کیا ہے کوئی اور ہستی تمہارے علم میں اس کی ہم پایہ ؟');
INSERT INTO "sayyid_qutb" VALUES(19,66,'19:66','انسان کہتا ہے کیا واقعی جب میں مرچلوں گا تو پھر زندہ کرکے نکال لایا جائوں گا ؟');
INSERT INTO "sayyid_qutb" VALUES(19,67,'19:67','کیا انسان کو یاد نہیں آتا کہ ہم پہلے اس کو پیدا کرچکے ہیں جبکہ وہ کچھ بھی نہ تھا ؟');
INSERT INTO "sayyid_qutb" VALUES(19,68,'19:68','تیرے رب کی قسم ہم ضرور ان سب کو اور ان کے ساتھ شیاطین کو بھی گھیر لائیں گے۔ پھر جہنم کے گرد لاکر انہیں گھٹنوں کے بل گرادیں گے');
INSERT INTO "sayyid_qutb" VALUES(19,69,'19:69','” پھر ہر گروہ میں سے ہر اس شخص کو چھانٹ لیں گے جو رحمن کے مقابلے میں زیادہ سرکش بنا ہوا تھا');
INSERT INTO "sayyid_qutb" VALUES(19,70,'19:70','پھر یہ ہم جانتے ہیں کہ ان میں سے کون سب سے بڑھ کر جہنم میں جھونکے جانے کا مستحق ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,71,'19:71','تم میں سے کوئی ایسا نہیں ہے جو جہنم پر وارد نہ ہو ‘ یہ تو ایک طے شدہ بات ہے جسے پورا کرنا تیرے رب کے ذمے ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,72,'19:72','پھر ہم ان لوگوں کو بچا لیں گے جو (دنیا میں متقی تھے اور ظالموں کو اسی میں گرا ہوا چھوڑدیں گے');
INSERT INTO "sayyid_qutb" VALUES(19,73,'19:73','ان لوگوں کو جب ہماری کھلی کھلی آیات سنائی جاتی ہیں تو انکار کرنے والے ایمان لانے والوں سے کہتے ہیں ” بتائو ہم دونوں گروہوں میں سے کون بہتر حالت میں ہے اور کس کی مجلسیں زیادہ شاندار ہیں');
INSERT INTO "sayyid_qutb" VALUES(19,74,'19:74','حالانکہ ان سے پہلے ہم کتنی ہی ایسی قوموں کو ہلاک کرچکے ہیں جوان سے زیادہ سروسامان رکھتی تھیں اور ظاہری شاو شوکت میں ان سے بڑھی ہوئی تھیں');
INSERT INTO "sayyid_qutb" VALUES(19,75,'19:75','ان سے کہو ‘ جو شخص گمراہی میں مبتلا ہوتا ہے اسے رحمن ڈھیل دیتا ہے یہاں تک کہ جب ایسے لوگ وہ چیز دیکھ لیتے ہیں جس کا ان سے وعدہ کیا گیا ہے۔۔۔۔ خواہ وہ عذاب الہٰی ہو یا قیامت کی گھڑی۔۔۔ تب انہیں معلوم ہوجاتا ہے کہ کس کا حال خراب ہے اور کس کا جتھا کمزور !');
INSERT INTO "sayyid_qutb" VALUES(19,76,'19:76','اس کے برعکس جو لوگ راہ راست اختیار کرتے ہیں اللہ ان کو راست روی میں ترقی عظا فرماتا ہے اور باقی رہ جانے والی نیکیاں تیرے رب کے نزدیک جزاء اور انجام کے اعتبار سے بہتر ہیں');
INSERT INTO "sayyid_qutb" VALUES(19,77,'19:77','آیاتو نے دیکھا اس شخص کو جو ہماری آیات کو ماننے سے انکار کرتا ہے اور کہتا ہے کہ میں تو مال اور اولاد سے نوازا ہی جاتارہوں گا ؟');
INSERT INTO "sayyid_qutb" VALUES(19,78,'19:78','کیا اسے غیب کا پتہ چل گیا ہے یا اس نے رحن سے کوئی عہد لے رکھا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(19,79,'19:79','ہر گز نہیں ‘ جو کچھ یہ بکتا ہے اسے ہم لکھ لیں گے اور اس کے لئے سزا میں اور زیادہ اضافہ کریں گے۔');
INSERT INTO "sayyid_qutb" VALUES(19,80,'19:80','جس سرو سامان اور لائو لشکر کا یہ ذکر کررہا ہے وہ سب ہمارے پاس رہ جائے گا اور یہ اکیلا ہمارے سامنے حاضر ہو گا');
INSERT INTO "sayyid_qutb" VALUES(19,81,'19:81','ان لوگوں نے اللہ کو چھوڑ کر اپنے کچھ خدا بنارکھے ہیں کہ وہ ان کے پشیتبان ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(19,82,'19:82','کوئی پشتیبان نہ ہوگا۔ وہ سب ان کی عبادت کا انکار کریں گے اور الٹے ان کے مخالف بن جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(19,83,'19:83','کیا تم دیکھتے نہیں ہو کہ ہم نے منکرین حق پر شیاطین چھوڑ رکھے ہیں جو انہیں خوب خوب (مخالفت حق پر) اکسار رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(19,84,'19:84','اچھا ‘ تو اب ان پر نزول عذاب کے لئے بےتاب نہ ہو۔ ہم ان کے دن گن رہے ہیں وہ دن آنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,85,'19:85','جب متقی لوگوں کو ہم مہمانوں کی طرح رحمن کے حضور پیش کریں گے');
INSERT INTO "sayyid_qutb" VALUES(19,86,'19:86','اور مجرموں کو پیاسے جانوروں کی طرح جہنم کی طرف ہانک لے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(19,87,'19:87','اس وقت لوگ کوئی سفارش لانے پر قادر نہ ہوں کے بنجر اس کے جس نے رحمن کے حضور سے پروانہ حاصل کرلیا ہو');
INSERT INTO "sayyid_qutb" VALUES(19,88,'19:88','وہ کہتے ہیں کہ رحمن نے کسی کو بیٹا بنایا ہے');
INSERT INTO "sayyid_qutb" VALUES(19,89,'19:89','سخت بےہودہ بات ہے جو تم لوگ گھڑ لائے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(19,90,'19:90','قریب ہے کہ آسمان پھٹ پڑیں ‘ زمین شق ہوجائے اور پہاڑ گر جائیں');
INSERT INTO "sayyid_qutb" VALUES(19,91,'19:91','اس بات پر کہ لوگوں نے رحمن کے لئے اولاد ہونے کا دعویٰ کیا !');
INSERT INTO "sayyid_qutb" VALUES(19,92,'19:92','رحمن کی یہ شان نہیں ہے کہ وہ کسی کو بیٹا بنائے');
INSERT INTO "sayyid_qutb" VALUES(19,93,'19:93','زمین و آسمان کے سب لوگ اس کے حضور بندوں کی حیثیت سے پیش ہونے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(19,94,'19:94','سب پر وہ محیط ہے اور اس نے ان کو شمار کر رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(19,95,'19:95','سب قیامت کے روز فرداً فرادً اس کے سامنے حاضر ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(19,96,'19:96','یقیناً جو لوگ ایمان لے آئے ہیں اور عمل صالح کررہے ہیں ‘ عنقریب رحمن ان کے لئے دلوں میں محبت پیدا کر دے گا');
INSERT INTO "sayyid_qutb" VALUES(19,97,'19:97','پس اے نبی صلى الله عليه وسلم ‘ اس کلام کو ہم نے آسان کر کے تمہاری زبان میں اس لئے نازل کیا ہے کہ تم پرہیز گاروں کو خوشخبری دے دو اور ہٹ دھرم لوگوں کو ڈرادو');
INSERT INTO "sayyid_qutb" VALUES(19,98,'19:98','ان سے پہلے ہم کتنی ہی قوموں کو ہلاک کرچکے ہیں ‘ پھر آج کہیں تم ان کا نشان پاتے ہو یا ان کی بھنک بھی کہیں سنائی دیتی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(20,1,'20:1','طہ ‘');
INSERT INTO "sayyid_qutb" VALUES(20,2,'20:2','ہم نے یہ قرآن تم پر اس لئے نازل نہیں کیا ہے کہ تم مصیبت میں پڑجائو۔');
INSERT INTO "sayyid_qutb" VALUES(20,3,'20:3','یہ تو ایک یاد دہانی ہے ہر اس شخص کے لئے جو ڈرے۔');
INSERT INTO "sayyid_qutb" VALUES(20,4,'20:4','نازل کیا گیا ہے اس ذات کی طرف سے جس نے پیدا کیا ہے زمین کو اور بلند آسمانوں کو۔');
INSERT INTO "sayyid_qutb" VALUES(20,5,'20:5','وہ رحمن (کائنات کے ) تخت سلطنت پر جلوہ فرما ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,6,'20:6','مالک ہے ان سب چیزوں کا جو آسمانوں اور زمین میں ہیں اور جو زمین و آسمان کے درمیان ہیں اور جو مٹی کے نیچے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(20,7,'20:7','تم چاہے تو اپنی بات پکار کر کہو ‘ وہ تو چپکے سے کہی ہوئی بات بلکہ اس سے مخفی تر بات بھی جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,8,'20:8','وہ اللہ ہے ‘ اس کے سوا کوئی خدا نہیں ‘ اس کے لیے بہترین نام ہیں');
INSERT INTO "sayyid_qutb" VALUES(20,9,'20:9','کیا موسیٰ ؐ کا قصہ تمہیں معلوم ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(20,10,'20:10','جب دیکھا موسیٰ نے آگ کو تو کہا اپنے اہل سے ذرا ٹھہرو ‘ میں نے ایک آگے دیکھی ہے ‘ شاید میں تمہارے لئے ایک آدھ انگارا لے آئوں یا اس آگ پر مجھے کوئی راہنمائی مل جائے۔');
INSERT INTO "sayyid_qutb" VALUES(20,11,'20:11','’ وہاں پہنچا تو پکارا گیا ” اے موسیٰ !');
INSERT INTO "sayyid_qutb" VALUES(20,12,'20:12','میں ہی تیرا رب ہوں ‘ جو تیاں اتاردے ‘ تو وادی مقدس طویٰ میں ہے');
INSERT INTO "sayyid_qutb" VALUES(20,13,'20:13','اور میں نے تجھ کو چن لیا “ سن جو کچھ وحی کیا جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(20,14,'20:14','میں ہی اللہ ہوں ‘ میرے سوا کوئی خدا نہیں ہے۔ پس تو میری بندگی کر اور میری یاد کے لئے نماز قائم کر۔');
INSERT INTO "sayyid_qutb" VALUES(20,15,'20:15','قیامت کی گھڑی ضرور آنیوالی ہے۔ میں اس کا وقت مخفی رکھنا چاہتا ہوں تاک ہر متنفس اپنی سعی کے مطابق بدلہ پائے');
INSERT INTO "sayyid_qutb" VALUES(20,16,'20:16','پس کوئی ایسا شخص جو اس پر ایمان نہیں لاتا اور اپنی خواہش نفس کا بندہ بن گیا ‘ تجھ کو اس گھڑی کی فکر سے نہ روک دے ورنہ تو ہلاکت میں پڑجائے گا “');
INSERT INTO "sayyid_qutb" VALUES(20,17,'20:17','اور اے موسیٰ ‘ یہ تیرے ہاتھ میں کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(20,18,'20:18','’ اور اے موسیٰ ‘ یہ تیرے ہاتھ میں کیا ؟ “ موسیٰ نے جواب دیا ” یہ میری لاٹھی ہے ‘ اس پر ٹیک لگا کر چلتا ہوں ‘ اس سے اپنی بکریوں کے لیے پتے جھاڑتا ہوں ‘ اور بھی بہت سے کام ہیں جو میں اس سے لیتا ہوں “۔');
INSERT INTO "sayyid_qutb" VALUES(20,19,'20:19','فرمایا پھینک دے اس کو موسیٰ');
INSERT INTO "sayyid_qutb" VALUES(20,20,'20:20','اس نے پھینک دیا اور یکایک وہ ایک سانپ تھی جو دوڑ رہا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(20,21,'20:21','فرمایا ” پکڑلے اس کو اور ڈر نہیں ‘ ہم اسے پھر ویسا ہی کردیں گے جیسی یہ تھی “');
INSERT INTO "sayyid_qutb" VALUES(20,22,'20:22','اور ذرا اپنا ہاتھ اپنی بغل میں دبا ‘ چمکتا ہوا نکلے گا بغیر کسی تکلیف کے ‘ یہ دوسرے نشانی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(20,23,'20:23','اس لئے کہ ہم تجھے اپنی بڑی نشانیاں دکھانے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(20,24,'20:24','اب تو فرعون کے پاس جا ‘ وہ سرکش ہوگیا ہے');
INSERT INTO "sayyid_qutb" VALUES(20,25,'20:25','موسیٰ نے عرض کیا : ” پروردگار ‘ میرا سینہ کھول دے ‘');
INSERT INTO "sayyid_qutb" VALUES(20,26,'20:26','اور میرے کام کو میرے لئے آسان کر دے');
INSERT INTO "sayyid_qutb" VALUES(20,27,'20:27','اور میری زبان کی گرہ سلجھا دے');
INSERT INTO "sayyid_qutb" VALUES(20,28,'20:28','تاکہ لوگ میری بات سمجھ سکیں');
INSERT INTO "sayyid_qutb" VALUES(20,29,'20:29','اور میرے لئے میرے اپنے کنبے سے ایک وزیر مقرر کر دے۔');
INSERT INTO "sayyid_qutb" VALUES(20,30,'20:30','ہارون ‘ جو میرا بھائی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,31,'20:31','اس کے ذریعہ سے میرا ہاتھ مضبوط کر');
INSERT INTO "sayyid_qutb" VALUES(20,32,'20:32','اور اس کو میرے کام میں شریک کر دے ‘');
INSERT INTO "sayyid_qutb" VALUES(20,33,'20:33','تاکہ ہم خوب تیری پاکی بیان کریں');
INSERT INTO "sayyid_qutb" VALUES(20,34,'20:34','اور خوب تیرا چرچا کریں۔');
INSERT INTO "sayyid_qutb" VALUES(20,35,'20:35','تو ہمیشہ ہمارے حال پر نگراں رہا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(20,36,'20:36','فرمایا “ دیا گیا جو تو نے مانگا اے موسیٰ (علیہ السلام) ! ‘');
INSERT INTO "sayyid_qutb" VALUES(20,37,'20:37','ہم نے پھر ایک مرتبہ تجھ پر احسان کیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,38,'20:38','یاد کر وہ وقت جبکہ ہم نے تیری ماں کو اشارہ کیا ‘');
INSERT INTO "sayyid_qutb" VALUES(20,39,'20:39','ایسا اشارہ جو وحی کے ذریعہ سے ہی کیا جاتا ہے کہ اس بچے کو صندوق میں رکھ دے اور صندوق کو دریا میں چھوڑ دے۔ دریا اسے ساحل پر پھینک دے گا اور اسے میرا دشمن اور اس بچے کا دشمن اٹھا لے گا۔ میں نے اپنی طرف سے تجھ پر محبت طاری کردی اور ایسا انتظام کیا کہ تو میری نگرانی میں پالا جائے۔');
INSERT INTO "sayyid_qutb" VALUES(20,40,'20:40','یاد کر جبکہ تیری بہن چل رہی تھی ‘ پھر جاکر کہتی ہے ‘ میں تمہیں اس کا پتہ دوں جو اس بچے کی پرورش اچھی طرح کرے ؟ اس طرح ہم نے تجھے پھر تیری ماں کے پاس پہنچا دیا تاکہ اس کی آنکھ ٹھنڈی رہے اور وہ رنجیدہ نہ ہو۔ اور (یہ بھی یاد کر کہ) تو نے ایک شخص کو قتل کردیا تھا ‘ ہم نے تجھے اس پھندے سے نکالا اور تجھے مختلف آزمائشوں سے گزارا اور تو مدین کے لوگوں میں کئی سال ٹھہرا رہا۔ پھر اب ٹھیک اپنے وقت پر تو آگیا ہے اے موسیٰ');
INSERT INTO "sayyid_qutb" VALUES(20,41,'20:41','میں نے تجھ کو اپنے کا کام بنا لیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(20,42,'20:42','جا ‘ تو اور تیرا بھائی میری نشانیوں کے ساتھ۔ اور دیکھو ‘ تم میری یاد میں تقصیر نہ کرنا۔');
INSERT INTO "sayyid_qutb" VALUES(20,43,'20:43','جائو ‘ تم دونوں فرعون کے پاس کہ وہ سرکش ہوگیا ہے');
INSERT INTO "sayyid_qutb" VALUES(20,44,'20:44','اس سے نرمی کے ساتھ بات کرنا شاید کہ وہ نصیحت قبول کرے یا ڈر جائے “۔');
INSERT INTO "sayyid_qutb" VALUES(20,45,'20:45','دونوں نے عرض کیا ” پروردگار ‘ ہمیں اندیشہ ہے کہ وہ ہم پر زیادتی کرے گا ‘ یا پل پڑے گا “');
INSERT INTO "sayyid_qutb" VALUES(20,46,'20:46','فرمایا : ” ڈرو مت ‘ میں تمہارے ساتھ ہوں ‘ سب کچھ سن رہا ہوں اور دیکھ رہا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(20,47,'20:47','جائو اس کے پاس اور کہو کہ ہم تیرے رب کے فرستادے ہیں ‘ بنی اسرائیل کو ہمارے ساتھ جانے کے لئے چھوڑ دے اور ان کو تکلیف نہ دے۔ ہم تیرے پاس تیرے رب کی نشانی لے کر آئے اور سلامتی ہے اس کے لیے جو راہ راست کی پیروی کرے۔');
INSERT INTO "sayyid_qutb" VALUES(20,48,'20:48','ہم کو وحی سے بتایا گیا ہے کہ عذاب ہے اس کے لئے جو جھٹلائے اور منہ موڑے “۔');
INSERT INTO "sayyid_qutb" VALUES(20,49,'20:49','” فرعون نے کہا اچھا پھر تم دونوں کا رب کون ہے اے موسیٰ ؟');
INSERT INTO "sayyid_qutb" VALUES(20,50,'20:50','موسیٰ نے کہا ، ہمارا رب وہ ہے جس نے ہر چیز کو اس کی ساخت بخشی۔ پھر اس کو راستہ بتایا۔ ‘');
INSERT INTO "sayyid_qutb" VALUES(20,51,'20:51','” فرعون بولا ” اور پہلے جو نسلیں گزر چکی ہیں انکی پھر کیا حالت تھی ؟');
INSERT INTO "sayyid_qutb" VALUES(20,52,'20:52','’ موسیٰ نے کہا ” اس کا علم میرے رب کے پاس ایک نوشتے میں محفوظ ہے میرا رب نہ چوکتا ہے ، نہ بھولتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,53,'20:53','” وہی جس نے تمہارے لئے زمین کو گہوارہ بنایا اور اس میں تمہارے چلنے کو راستے بنائے اور اوپر سے پانی برسایا “ ‘ پھر اس کے ذریعہ سے مختلف اقسام کی پیداوار نکالی۔');
INSERT INTO "sayyid_qutb" VALUES(20,54,'20:54','کھائو اور اپنے جانوروں کو بھی چرائو۔ یقینا اس میں بہت سی نشانیاں ہیں عقل رکھنے والوں کے لئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,55,'20:55','” اسی زمین سے ہم نے تم کو پیدا کیا ہے ، اسی میں ہم تمہیں واپس لے جائیں گے ار اسی سے تم کو دوبارہ نکالیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,56,'20:56','ہم نے فرعون کو اپنی سب ہی نشانیاں دکھائیں مگر وہ جھٹلائے چلا گیا اور نہ مانا۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,57,'20:57','” کہنے لگا ” اے موسیٰ کیا تو ہمارے پاس اس لئے آیا ہے کہ اپنے جادو کے زور سے ہم کو ہمارے ملک سے نکال باہر کرے ؟');
INSERT INTO "sayyid_qutb" VALUES(20,58,'20:58','اچھا ہم بھی تیرے مقابلے میں ویسا ہی جادو لاتے ہیں۔ طے کرلے کب اور کہاں مقابلہ کرنا ہے۔ نہ ہم اس قرار داد سے پھریں گے نہ تو پھریو۔ کھلے میدان میں سامنے آ جا۔ ‘');
INSERT INTO "sayyid_qutb" VALUES(20,59,'20:59','‘ موسیٰ نے کہا ” جشن کا دن طے ہوا اور دن چڑھے لوگ جمع ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,60,'20:60','’ فرعون نے پلٹ کر اپنے سارے جھنڈے جمع کئے اور ہم میدان مقابلہ میں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(20,61,'20:61','” موسیٰ نے (عین موقع پر گروہ مقابل کو مخاطب کر کے ) کہا ” شامت کے مارو ، نہ جھوٹی تہمتیں باندھو اللہ پر ، ورنہ وہ ایک سخت عذاب سے تمہارا ستیاناس کر دے گا۔ جھوٹ جس نے بھی گھڑا وہ نامراد ہوا۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,62,'20:62','” یہ سن کر ان کے درمیان اختلاف رائے ہوگیا اور وہ چپکے چپکے باہم مشورہ کرنے لگے۔');
INSERT INTO "sayyid_qutb" VALUES(20,63,'20:63','آخر کار کچھ لوگوں نے کہا کہ ” یہ دونوں تو محض جادوگر ہیں۔ ان کا مقصد یہ ہے کہ اپنے جادو کے زور سے تم کو تمہاری زمین سے بےدخل کردیں اور تمہارے مثالی طریق کزندگی کا خاتمہ کردیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,64,'20:64','اپنی ساری تدبیریں آج اکٹھی کرلو اور ایکا کر کے میدان میں آ جائو ۔ بس یہ سمجھ لو کہ آج و غالت رہا وہ جیت گیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,65,'20:65','” جادو گر بولے ” موسیٰ تم پھینکتے ہو یا پہلے ہم پھینکیں “۔ یہ ان کی جانب سے میدان میں دعوت مقابلہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,66,'20:66','” موسیٰ نے کہا ” نہیں تم ہی پھینکو۔ ‘’ یکایک ان کی رسیاں اور ان کی لاٹھیاں ان کے جادو کے زور سے موسیٰ کو دوڑتی ہوئی محسوس ہونے لگیں ،');
INSERT INTO "sayyid_qutb" VALUES(20,67,'20:67','موسیٰ اپنے دل میں ڈر گیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,68,'20:68','” ہم نے کہا ” مت ڈر ، تو ہی غالب رہے گا ،');
INSERT INTO "sayyid_qutb" VALUES(20,69,'20:69','پھینک جو کچھ تیرے ہاتھ میں ہے ، ابھی ان کی ساری بناوٹی چیزوں کو نگلے جاتا ہے۔ یہ جو کچھ بنا کر لائے ہیں۔ یہ تو جادو گر کا فریب ہے۔ اور جادوگر کبھی کامیاب نہیں ہو سکتا ، خواہ کسی شان سے وہ آئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,70,'20:70','” آخر کو یہی ہوا کہ سارے جادوگر سجدے میں گرا دیئے گئے اور وہ پکار اٹھے “ مان لیا ہم نے ہارون اور موسیٰ کے رب کو۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,71,'20:71','’ فرعون نے کہا ” تم اس پر ایمان لے آئے قبل اس کے کہ میں تمہیں اس کی اجازت دیتا ؟ معلوم ہوگیا کہ یہ تمہارا گروہ ہے جس نے تمہیں جادو گری سکھائی تھی۔ اچھا ، اب میں تمہارے پائوں مخالف سمتوں سے کٹوایا ہوں اور کھجور کے تنوں پر تم کو سولی دیتا ہے۔ پھر تمہیں پتہ چل جائے گا کہ ہم دونوں میں سے کس کا عذاب زیادہ سخت اور دیرپا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,72,'20:72','’ یعنی میں تمہیں زیادہ سخت سزا دے سکتا ہوں یا موسیٰ ۔ جادوگروں نے جواب دیا۔ ” قسم ہے اس ذات کی جس نے ہمیں پیدا کیا ہے ، یہ ہرگز نہیں ہو سکتا کہ ہم روشن نشانیاں اسامنے آنے کے بعد بھی (صداقت پر) تجھے ترجیح دیں۔ تو جو کچھ کرنا چاہئے کرلے۔ تو زیادہ سے زیادہ بس اسی دنیا کی زنگدی کا فیصلہ کرسکتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,73,'20:73','ہم تو اپنے رب پر ایمان لے آئے تاکہ وہ ہماری خطائیں معاف کر دے اور اس جادوگری سے ، جس پر تو نے ہمیں مجبور کیا تھا ، درگزر فرمائے۔ اللہ ہی اچھا ہے اور وہی باقی رہنے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,74,'20:74','” یہ کہ جو مجرم بن کر اپنے رب کے حضور حاضر ہوگا اس کے لئے جہنم ہے جس میں وہ نہ جیے گا نہ مرے گا');
INSERT INTO "sayyid_qutb" VALUES(20,75,'20:75','اور جو اس کے حضور مومن کی حیثیت سے حاضر ہوگا جس نے نیک عمل کئے ہوں گے ، ایسے سب لوگوں کے لئے بلند درجے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(20,76,'20:76','سدا بہار باغ ہیں جن کے نیچے نہریں بہ رہی ہوں گی ، ان میں وہ ہمیشہ رہیں گے۔ یہ جزا ہے اس شخص کی جو پاکیزگی اختیار کرے۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,77,'20:77','” ہم نے موسیٰ پر وحی کی کہ اب راتوں رات میرے بندوں کو لے کر چل پڑ ، اور انکے لئے سمندر میں سے سوکھی سڑک بنا لے ، تجھے کسی کے تعاقب کا ذرا خوف نہ ہو اور نہ (سمندر کے بیچ سے گزرتے ہوئے) ڈر لگے۔');
INSERT INTO "sayyid_qutb" VALUES(20,78,'20:78','پیچھے سے فرعون اپنے لشکر لے کر پہنچا ، اور پھر سمندر ان پر چھا گیا جیسا کہ چھا جانے کا حق تھا۔');
INSERT INTO "sayyid_qutb" VALUES(20,79,'20:79','فرعون نے اپنی قوم کو گمراہ ہی کیا تھا ، کوئی صحیح رہنمائی نہیں کی تھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(20,80,'20:80','اے بنی اسرائیل ، ہم نے تم کو تمہارے دشمن سے نجات دی ، اور طور کے دائیں جانب تمہاری حاضری کے لئے وقت مقرر کیا اور تم پر من وسلویٰ اتارا');
INSERT INTO "sayyid_qutb" VALUES(20,81,'20:81','کھائو ہمارا دیا ہوا پاک رزق اور اسے کھا کر سرکشی نہ کرو ، ورنہ تم پر میرا غضب ٹوٹ پڑے گا۔ اور جس پر میرا غضب ٹوٹا وہ پھر گر کر ہی رہا۔');
INSERT INTO "sayyid_qutb" VALUES(20,82,'20:82','البتہ جو توبہ کرلے اور ایمان لائے اور نیک عمل کرے ، پھر سیدھا چلتا رہے اس کے لئے میں بہت درگزر کرنے والا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(20,83,'20:83','اور کیا چیز تمہیں اپنی قوم سے پہلے لے آئی اے موسیٰ ؟');
INSERT INTO "sayyid_qutb" VALUES(20,84,'20:84','اس نے عرض کیا ” وہ بس میرے پیچھے آ ہی رہے ہیں۔ میں جلدی کر کے تیرے حضور آگیا ہوں ، اے میرے رب ، تاکہ تو مجھ سے خوش ہوجائے ،');
INSERT INTO "sayyid_qutb" VALUES(20,85,'20:85','فرمایا ” اچھا تو سنو ، ہم نے تمہارے پیچھے تمہاری قوم کو آزمائش میں ڈال دیا اور سامری نے انہیں گمراہ کر ڈالا۔');
INSERT INTO "sayyid_qutb" VALUES(20,86,'20:86','موسیٰ سخت غصے اور رنج کی حالت میں اپنی قوم کی طرف پلٹا۔ جا کر اس نے کہا ” اے میری قوم کے لوگو ، کیا تمہارے رب نے تم سے اچھے وعدے نہیں کئے تھے ؟ کیا تمہیں دن لگ گئے ہیں ؟ یا تم اپنے رب کا غضب ہی اپنے اوپر لانا چاہتے تھے کہ تم نے مجھ سے وعدہ خلافی کی ؟');
INSERT INTO "sayyid_qutb" VALUES(20,87,'20:87','انہوں نے جواب دیا ” ہم نے آپ سے وعدہ خلافی کچھ اپنے اختیار سے نہیں کی ، معاملہ یہ ہوا کہ لوگوں کے زیورات کے بوجھ سے ہم لد گئے تھے اور ہم نے بس ان کو پھینک دیا تھا۔ “ پھر اسی طرح سامری نے بھی کچھ ڈالا');
INSERT INTO "sayyid_qutb" VALUES(20,88,'20:88','ور ان کے لئے ایک بچھڑے کی مورت بنا کر نکال لایا جس میں سے بیل کی سی آواز نکلتی تھی۔ لوگ پکار اٹھے ” یہی ہے تمہارا خدا اور موسیٰ کا خدا ، موسیٰ اسے بھول گیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,89,'20:89','کیا وہ دیکھتے نہ تھے کہ نہ وہ ان کی بات کا جواب دیتا ہے اور نہ ان کے نفع و نقصان کا کچھ اختیار رکھتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(20,90,'20:90','ہارون (موسی کے آنے سے) پہلے ہی ان سے کہ چکا تھا کہ لوگو تم اس کی وجہ سے فتنے میں پڑگئے ہو تمہارا رب تو حمن ہے پس تم میری پیروی کرو اور میری بات مانو۔');
INSERT INTO "sayyid_qutb" VALUES(20,91,'20:91','مگر انہوں نے اس سے کہہ دیا کہ ہم تو اسی کی پرستش کرتے رہیں گے جب تک کہ موسیٰ ہمارے پاس واپس نہ آجائے۔');
INSERT INTO "sayyid_qutb" VALUES(20,92,'20:92','موسیٰ (قوم کو ڈاٹنے کے بعد ہارون کی طرف پلٹے اور) بولے ” ہارون تم نے جب کہا تھا کہ یہ گمراہ ہو رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(20,93,'20:93','تو کس چیز نے تمہارا ہاتھ پکڑا تھا کہ میرے طریقے پر عمل نہ کرو ؟ کیا تم نے میرے حکم کی خلاف ورزی کی ؟');
INSERT INTO "sayyid_qutb" VALUES(20,94,'20:94','ہارون نے جواب دیا ” اے میری ماں کے بیٹے ، میری داڑھی نہ پکڑ ، نہ میرے سر کے بال کھینچ ، مجھے اس بات کا ڈر تھا کہ تو آ کر کہے گا تم نے بنی اسرائیل میں پھوٹ ڈال دی اور میری بات کا پاس نہ کیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,95,'20:95','موسیٰ نے کہا ” اور سامری ، تیرا کیا معاملہ ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(20,96,'20:96','اس نے جواب دیا ” میں نے وہ چیز دیکھی جو ان لوگوں کو نظر نہ آئی ، پس میں نے رسول کے نقش قدم سے ایک رسی اٹھا لی اور اس کو ڈال دیا۔ میرے نفس نے مجھے کچھ ایسا ہی سمجھایا۔');
INSERT INTO "sayyid_qutb" VALUES(20,97,'20:97','موسیٰ نے کہا ” اچھا تو جا ، اب زندگی بھر تجھے یہی پکاتے رہنا ہے کہ مجھے نہ چھونا اور تیرے لئے باز پرس کا ایک وقت مقرر ہے جو تجھ سے ہرگز نہ ٹلے گا اور دیکھ اپنے اس خدا کو جس پر تو ریجھا ہوا تھا ، اب ہم اسے جلا ڈالیں گے اور ریزہ ریزہ کر کے دریا میں بہا دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,98,'20:98','لوگو تمہارا خدا تو بس ایک ہی اللہ ہے ، جس کے سوا کوئی اور خدا نہیں ہے ، ہر چیز پر اس کا علم حاوی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,99,'20:99','اے نبی اس طر ہم پچھلے گزرے ہوئے حالات کی خبریں تم کو سناتے ہیں اور ہم نے خاص اپنے ہاں سے تم کو ایک ” ذکر “ (درس نصیحت) عطا کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(20,100,'20:100','جو کوئی اس سے منہ موڑے گا وہ قیامت یر وز سخت بار گناہ اٹھائے گا');
INSERT INTO "sayyid_qutb" VALUES(20,101,'20:101','اور ایسے سب لوگ ہمیشہ اس کے وبال میں گرفتار رہیں گے ، اور قیامت کے دن ان کے لئے (اس جرم کی ذمہ داری کا بوجھ) بڑا تکلیف وہ بوجھ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(20,102,'20:102','اس دن جبکہ صور پھونکا جائے گا اور ہم مجرموں کو اس حال میں گھیر لائیں گے کہ ان کی آنکھیں (دہشت کے مارے) پتھرائی ہوئی ہوں گی۔');
INSERT INTO "sayyid_qutb" VALUES(20,103,'20:103','آپس میں چپکے چپکے کہیں گے کہ ” دنیا میں مشکل ہی سے تم نے کوئی دس دن گزارے ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,104,'20:104','ہمیں خوب معلوم ہے کہ وہ کیا باتیں کر رہے ہوں گے۔ (ہم یہ بھی جانت یہیں کہ ) اس وقت ان میں سے جو زیادہ سے زیادہ محتاط اندازہ لگانے والا ہوگا وہ کہے گا کہ نہیں ، تمہاری دنیا کی زندگی بس ایک دن کی زندگی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(20,105,'20:105','یہ لوگ تم سے پوچھتے ہیں کہ آخر اس دن یہ پہاڑ کہاں چلے جائیں گے ؟ کہو کہ میرا رب ان کو دھول بنا کر اڑا دے گا');
INSERT INTO "sayyid_qutb" VALUES(20,106,'20:106','اور زمین کو ایسا ہموار چٹیل میدان بنا دے گا');
INSERT INTO "sayyid_qutb" VALUES(20,107,'20:107','کہ اس میں تم کوئی بل اور سلوٹ نہ دیکھو گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,108,'20:108','اس روز سب لوگ منادی کی پکار پر سیدھے چلے آئیں گے کوئی ذرا اکڑ نہ دکھا سکے گا اور آوازیں رحمٰن کے آگے دب جائیں گی ، ایک سراسر کے سوا تم کچھ نہ سنو گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,109,'20:109','اس روز شفاعت کارگر نہ ہوگی ، الایہ کہ کسی کو رحمٰن اس کی اجازت دے اور اس کی بات پسند کرے ۔');
INSERT INTO "sayyid_qutb" VALUES(20,110,'20:110','وہ لوگوں کا اگلا پچھلا سب حال جانتا ہے اور دوسروں کو اس کا پورا علم نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,111,'20:111','لوگوں کے سر اس حی وقیوم کے آگے جھک جائیں گے۔ نامراد ہوگا جو اس وقت کسی ظلم کا بار گناہ اٹھائے ہوئے ہوگا اور کسی ظلم یا حق تلفی کا خطرہ نہ ہو گا');
INSERT INTO "sayyid_qutb" VALUES(20,112,'20:112','اس شخص کو جو نیک عمل کرے اور اس کے ساتھ وہ مومن بھی ہو۔');
INSERT INTO "sayyid_qutb" VALUES(20,113,'20:113','اور اے نبی اسی طرح ہم نے اسے قرآن عربی بنا کر نازل کیا ہے اور اس میں طرح طرح سے تنبیہات ہیں ، شاید کہ یہ لوگ کج روی سے بچیں یا ان میں کچھ ہوش کے آثار اس کی بدولت پیدا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(20,114,'20:114','پس بالا و برتر ہے اللہ ، پادشاہ حقیقی ۔ اور دیکھو ، قرآن پڑھنے میں جلدی نہ کرو ، جب تک کہ تمہاری طرف اس کی وحی تکمیل کو نہ پہنچ جائے اور دعا کرو کہ اے پروردگار مجھے مزید علم عطا کر۔');
INSERT INTO "sayyid_qutb" VALUES(20,115,'20:115','ہم نے اس سے پہلے آدم کو ایک حکم دیا تھا ، مگر وہ بھول گیا اور ہم نے اس میں عزم نہ پایا۔');
INSERT INTO "sayyid_qutb" VALUES(20,116,'20:116','یاد کرو وہ وقت جبکہ ہم نے فرشتوں سے کہا تھا کہ آدم کو سجدہ کرو۔ وہ تو سب سجدہ کر گئے مگر ایک ابلیس تھا کہ انکار کر بیٹھا۔');
INSERT INTO "sayyid_qutb" VALUES(20,117,'20:117','اور اس پر ہم نے آدم سے کہا کہ دیکھو ، یہ تمہارا اور تمہاری بیوی کا دن ہے ایسا نہ ہو کہ یہ تمہیں جنت سے نکلوا دے اور تم مصیبت میں پڑ جائو۔');
INSERT INTO "sayyid_qutb" VALUES(20,118,'20:118','یہاں تو تمہیں یہ آسائشیں حاصل ہیں کہ نہ بھوکے ننگے رہتے ہو');
INSERT INTO "sayyid_qutb" VALUES(20,119,'20:119','نہ پیاس اور دھوپ تمہیں ستاتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,120,'20:120','لیکن شیطان نے اس کو پھسلایا ” کہنے لگا ، آدم ، بتائوں تمہیں وہ درخت جس سے ابدی زندگی اور لازوال سلطنت حاصل ہوتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,121,'20:121','آخر کار دونوں (میاں بیوی) اس درخت کا پھل کھا گئے۔ نتیجہ یہ ہوا کہف وراً ہی ان کے ستر ایک دوسرے کے آگے کھل گئے اور لگے دونوں اپنے آپ کو جنت کے پتوں سے ڈھانکنے ۔ آدم نے اپنے رب کی نافرمانی کی اور راہ راست سے بھٹک گیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,122,'20:122','پھر اس کے رب نے اسے برگزیدہ کیا اور اس کی توجہ قبول کرلی اور ہدایت بخشی۔');
INSERT INTO "sayyid_qutb" VALUES(20,123,'20:123','اور فرمایا تم دونوں (فریق ، یعنی انسان اور شیطان) یہاں سے اتر جائو۔ تم ایک دوسرے کے دشمن رہو گے۔ اب اگر میری طرف سے تمہیں کوئی ہدایت پہنچے تو جو کوئی میری اس ہدایت کی پیروی کرے گا وہ نہ بھٹکے گا نہ بدبختی میں مبتلا ہو گا');
INSERT INTO "sayyid_qutb" VALUES(20,124,'20:124','اور جو میرے ” ذکر “ (درس نصیحت) سے منہ موڑے گا اس کے لئے دنیا میں تنگ زندگی ہوگی اور قیامت کے روز ہم اسے اندھا اٹھائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(20,125,'20:125','“ و کہے گا ” پروردگار ، دنیا میں تو میں آنکھوں والا تھا ، یہاں مجھے اندھا کیوں اٹھایا ؟');
INSERT INTO "sayyid_qutb" VALUES(20,126,'20:126','اللہ تعالیٰ فرمائے گا ” ہاں اسی طرح تو ہماری آیات کو جبکہ وہ تیرے پاس آئی تھیں ، تو نے بھلا دیا تھا۔ اسی طر آج تو بھلایا جا رہا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,127,'20:127','“ اس طرح ہم حد سے گزرنے والے اور اپنے رب کی آیات نہ ماننے والے کو (دنیا میں) بدلہ دیتے ہیں اور آخرت کا عذاب زیادہ سخت اور زیادہ دیرپا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,128,'20:128','پھر کیا ان لوگوں کو (تاریخ کے اس سبق سے) کوئی ہدایت نہ ملی کہ ان سے پہلے کتنی ہی قوموں کو ہم ہلاک کرچکے ہیں جن کی (برباد دشہ) بستیوں میں آج یہ چلتے پھرتے ہیں ؟ درحقیقت اس میں بہت سی نشانیاں ہیں ان لوگوں کے لئے جو عقل سلیم رکھنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(20,129,'20:129','اگر تیرے رب کی طرف سے پہلے ایک بات طے نہ کردی گئی ہوتی اور مہلت کی ایک مدت مقرر نہ کی جا چکی ہوتی تو ضرور ان کا بھی فیصلہ چکا دیا جاتا۔');
INSERT INTO "sayyid_qutb" VALUES(20,130,'20:130','پس اے نبی ، جو باتیں یہ لوگ بتاتے ہیں ان پر صبر کرو ، اور پانے رب کی حمد و ثنا کے ساتھ ، اس کی تسبیح کرو ، سورج نکلنے سے پہلے اور غروب ہونے سے پہلے اور رتا کے اوقات میں بھی تسبیح کرو اور دن کے کناروں پر بھی شاید کہ تم راضی ہو جائو۔');
INSERT INTO "sayyid_qutb" VALUES(20,131,'20:131','نگاہ اٹھا کر بھی نہ دیکھو دنیوی زندگی کی اس شان و شوکت کو جو ہم نے ان میں مختلف قسم کے لوگوں کو دے رکھی ہے۔ وہ تو ہم نے انہیں آزمائش میں ڈالنے کے لئے دی ہے اور تیرے رب کا دیا ہوا رزق حلال ہی بہتر اور پائندہ تر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,132,'20:132','اپنے اہل و عیال کو نماز کی تلقین کرو اور خود بھی اس کے پابند رہو۔ ہم تم سے کوئی رزق نہیں چاہتے۔ رزق تو ہم ہی تمہیں دے رہے ہیں اور انجام کی بھلائی تقویٰ ہی کیلئے ہے۔');
INSERT INTO "sayyid_qutb" VALUES(20,133,'20:133','وہ کہتے ہیں کہ یہ شخص اپنے رب کی طرف سے کوئی نشانی (معجزہ) کیوں نہیں لاتا ؟ اور کیا ان کے پاس اگلے صحیفوں کی تمام تعلیمات کا بیان واضح نہیں آگیا۔');
INSERT INTO "sayyid_qutb" VALUES(20,134,'20:134','اگر ہم اس کے آنے سے پہلے ان کو کسی عذاب سے ہلاک کردیتے تو پھر یہی لوگ کہتے کہ اے پروردگار ، تو نے ہمارے پاس کوئی رسول کیوں نہ بھیجا کہ ذلیل و رسوا ہونے سے پہلے ہی ہم تیری آیات کی پیروی اختیار کرلیتے۔');
INSERT INTO "sayyid_qutb" VALUES(20,135,'20:135','اے نبی ان سے کہو ، ہر ایک انجام کار کے انتظار میں ہے ، پس اب منتظر رہو ، عنقریب تمہیں معلوم ہوجائے گا کہ کون سیدھی راہ چلنے والے ہیں اور کون ہدایت یافتہ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,1,'21:1','” قریب آگیا ہے لوگوں کے حساب کا وقت ‘ اور وہ ہیں کہ غفلت میں منہ موڑے ہوئے ہیں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,2,'21:2','ان کے پاس جو تازہ نصیحت بھی ان کے رب کی طرف سے آتی ہے اس کو بتکلف سنتے ہیں اور کھیل میں پڑے رہتے ہیں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,3,'21:3','دل ان کے (دوسری ہی فکروں میں) منھمک ہیں۔ اور ظالم آپس میں سرگوشیاں کرتے ہیں کہ یہ شخص آخر تم جیسا ایک بشر ہی تو ہے ‘ پھر کیا تم آنکھوں دیکھتے جادو کے پھندے میں پھنس جائو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(21,4,'21:4','“ رسول صلى الله عليه وسلم نے کہا میرا رب ہر اس بات کو جانتا ہے جو آسمان اور زمین میں کی جائے ‘ وہ سمیع اور علیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,5,'21:5','وہ کہتے ہیں ” بلکہ یہ پراگندہ خواب ہیں ‘ بلکہ یہ اس کی من گھڑت بات ہے ‘ بلکہ یہ شخص شاعر ہے ‘ ورنہ یہ لائے کوئی نشانی جس طرح پرانے زمانے کے رسول نشانیوں کے ساتھ بھیجے گئے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(21,6,'21:6','حالانکہ ان سے پہل کوئی بستی بھی ‘ جسے ہم نے ہلاک کیا ‘ ایمان نہ لائی۔ اب کیا یہ ایمان لائیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(21,7,'21:7','اور اے نبی صلى الله عليه وسلم تم سے پہلے بھی ہم نے انسانوں ہی کو رسول بنا رک بھیجا تھا جن پر ہم وحی کیا کرتے تھے۔ تم لوگ اگر علم نہیں رکھتے تو اہل کتاب سے پوچھ لو۔');
INSERT INTO "sayyid_qutb" VALUES(21,8,'21:8','ان رسولوں کو ہم نے کوئی ایسا جسم نہیں دیا تھا کہ وہ کھاتے نہ ہوں اور نہ وہ سدا جینے والے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(21,9,'21:9','پھر دیکھ لو کہ آخر کار ہم نے ان کے ساتھ اپنے وعدے پورے کیے اور انہیں اور جس جس جو ہم نے چاہا ‘ بچا لیا اور حد سے گزر جانے والوں کو ہلاک کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(21,10,'21:10','لوگو ‘ ہم نے تمہاری طرف ایک ایسی کتاب بھیجی ہے جس میں تمہارا ہی ذکر ہے ‘ کیا تم سمجھتے نہیں “');
INSERT INTO "sayyid_qutb" VALUES(21,11,'21:11','کتنی ہی ظالم بستیاں ہیں جن کو ہم نے پیس کر رکھ دیا اور ان کے بعد دوسری کسی قوم کو اٹھا یا۔');
INSERT INTO "sayyid_qutb" VALUES(21,12,'21:12','جب ان کو ہمارا عذاب محسوس ہوا تو لگے وہاں سے بھاگنے۔');
INSERT INTO "sayyid_qutb" VALUES(21,13,'21:13','(کہا گیا) ” ُ بھاگو نہیں ‘ جائو اپنے گھروں اور عیش کے سامانوں میں جن کے اندر تم چین کررہے تھے ‘ شاید کہ تم سے پوچھا جائے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,14,'21:14','کہنے لگے ” ہائے ہماری کم بختی ‘ بیشک ہم خطا وار تھے');
INSERT INTO "sayyid_qutb" VALUES(21,15,'21:15','اور وہ یہی پکارتے رہے ‘ یہاں تک کہ ہم نے ان کو کھلیان کردیا ‘ زندگی کا ایک شرارہ تک ان میں نہ رہا “۔');
INSERT INTO "sayyid_qutb" VALUES(21,16,'21:16','ہم نے اس آسمان اور زمین کو اور جو کچھ ان میں ہے ‘ کچھ کھیل کے طور پر نہیں بنایا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,17,'21:17','اگر ہم کوئی کھلونا بنانا چاہتے اور بس یہی کچھ ہمیں کرنا ہوتا تو اپنے ہی پاس سے کرلیتے۔');
INSERT INTO "sayyid_qutb" VALUES(21,18,'21:18','مگر ہم تو باطل پر حق کی چوٹ لگاتے ہیں جو اس کا سر توڑ دیتی ہے اور وہ دیکھتے دیکھتے مٹ جاتا ہے اور تمہارے لیے تباہی ہے ان باتوں کی وجہ سے جو تم بناتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(21,19,'21:19','زمین اور آسمانوں میں جو مخلوق بھی ہے اللہ کی ہے اور جو (فرشتے ) سا کے پاس ہیں وہ نہ اپنے آپ کو بڑا سمجھ کر اس کی بندگی سے سرتابی کرتے ہیں اور نہ طول ہوتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,20,'21:20','شب و روز اس کی تسبیح کرتے رہتے ہیں ‘ دم نہیں لیتے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,21,'21:21','کیا ان لوگوں کے بنائے ہوئے ارضی خدا ایسے ہیں کہ (بےجان کو جان بخش کر) اٹھا کھڑا کرتے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(21,22,'21:22','اگر آسمان و زمین میں ایک اللہ کے سوادوسرے خدا بھی ہوتے تو (زمین اور آسمان) دونوں کا نظام بگڑجاتا۔ پس پاک ہے اللہ رب العرش ان باتوں سے و یہ لوگ بنا رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,23,'21:23','وہ اپنے کاموں کے لیے (کسی کے آگے) جواب دہ نہیں ہے اور سب جواب دہ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,24,'21:24','” کیا اسے چھوڑ کر انہوں نے دوسرے خدا بنا لیے ہیں ؟ اے نبی صلى الله عليه وسلم ‘ ان سے کہو کہ ” لائو اپنی دلیل ‘ یہ کتاب بھی موجود ہے جس میں میرے دور کے لوگوں کے لیے نصیحت تھی “۔ یہ قرآن کریم موجود ہیں اور اس میں حضرت محمد صلى الله عليه وسلم کے دور کے لوگوں کے لیے نصیحت ہے۔ ’ مگر ان میں سے اکثر لوگ حقیقت سے بیخبر ہیں ‘ اس لیے منہ موڑے ہوئے ہیں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,25,'21:25','ہم نے تم سے پہلے جو رسول بھی بھیجا ہے اس کو یہی وحی کی ہے کہ میرے سوا کوئی خدا نہیں ہے ‘ پس تم لوگ میری ہی بندگی کرو “۔');
INSERT INTO "sayyid_qutb" VALUES(21,26,'21:26','یہ کہتے ہیں ‘” رحمن اولاد رکھتا ہے “ وہ (یعنی فرشتے ) تو بندے ہیں جنہیں عزت دی گئی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,27,'21:27','اس کے حضور بڑھ کر نہیں بولتے۔ اور بس اس کے حکم پر عمل کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(21,28,'21:28','جو کچھ ان کے سامنے ہے اسے بھی وہ جانتا ہے اور جو کچھ ان سے اوجھل ہے ‘ اس سے بھی وہ باخبر ہے۔ وہ کسی کی سفارش نہیں کرتے بجز اس کے جس کے حق میں سفارش سننے پر اللہ راضی ہو ‘ اور وہ اس کے خوف سے ڈرتے رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(21,29,'21:29','اور جو ان میں سے کوئی کہہ دے کہ اللہ کے سوا میں بھی ایک خدا ہوں ‘ تو اسے ہم جہنم کی سزادیں ‘ ہمارے ہاں ظالموں کا یہی بدلہ ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,30,'21:30','کیا وہ لوگ جنہوں نے (نبی صلى الله عليه وسلم کی بات ماننے سے) انکار کردیا ہے ‘ غور نہیں کرتے یہ سب آسمان اور زمین باہم ملے ہوئے تھے ‘ پھر ہم نے انہیں جدا کیا اور پانی سے ہر زندہ چیز پیدا کی ؟ کیا وہ (ہماری اس خلاقی کو ) نہیں مانتے ؟');
INSERT INTO "sayyid_qutb" VALUES(21,31,'21:31','اور ہم نے زمین میں پہاڑ جما دیئے تاکہ وہ انہیں لے کر ڈھلک نہ جائے اور اس میں کشادہ راہیں بنا دیں ‘ شاید کہ لوگ اپنا راستہ معلوم کرلیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,32,'21:32','اور ہم نے آسمان کو ایک محفوظ چھت بنا دیا مگر یہ ہیں کہ کائنات کی نشانیوں کی طرف توجہ ہی نہیں کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(21,33,'21:33','اور وہ اللہ ہی ہے جس نے رات اور دن بنائے اور سورج اور چاند کو پیدا کیا سب ایک ایک فلک میں تیر رہے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(21,34,'21:34','اور اے نبی صلى الله عليه وسلم ‘ ہمیشگی تو ہم نے تم سے پہلے بھی کسی انسان کے لیے نہیں رکھی ہے ‘ اگر تم مر گئے تو کیا یہ لوگ ہمیشہ جیتے رہیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(21,35,'21:35','ہر جاندار کو موت کا مزہ چکھنا ہے ‘ اور ہم اچھے اور برے حالات میں ڈال رک تم سب کی آزمائش کررہے ہیں ۔ آخر کار تمہیں ہماری ہی طرف پلٹنا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,36,'21:36','یہ منکرین حق جب تمہیں دیکھتے ہیں تو تمہارا مذاق بنا لیتے ہیں ۔ کہتے ہیں ” کیا یہ ہے وہ شخص جو تمہارے خدائوں کا ذکر کرتا ہے ؟ “ اور ان کا اپنا حال یہ ہے کہ وہ رحمن کے ذکر سے منکر ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(21,37,'21:37','انسان جلد باز مخلوق ہے۔ ابھی میں تم کو اپنی نشانیاں دکھائے دیتا ہوں ‘ مجھ سے جلدی نہ مچائو۔');
INSERT INTO "sayyid_qutb" VALUES(21,38,'21:38','یہ لوگ کہتے ہیں ” آخر یہ دھمکی پوری کب ہوگی اگر تم سچے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(21,39,'21:39','کاش ان کافروں کو اس وقت کا کچھ علم ہوتا جبکہ یہ نہ اپنے منہ آگ سے بچا سکیں گے نہ اپنی پیٹھیں ‘ اور نہ انکو کہیں سے مدد پہنچے گی۔');
INSERT INTO "sayyid_qutb" VALUES(21,40,'21:40','وہ بلا اچانک آئے گی اور انہیں اس طرح یک لخت دبوچ لے گی کہ یہ نہ اس کو دفع کرسکیں گے اور نہ ان کو لمحہ بھر کی مہلت ہی مل سکے گی۔');
INSERT INTO "sayyid_qutb" VALUES(21,41,'21:41','مذاق تم سے پہلے بھی رسولوں کا اڑایاجا چکا ہے ‘ مگر ان کا مذاق اڑانے والے اسی چیز کے پھیر میں آکر رہے جس کا وہ مذاق اڑاتے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,42,'21:42','’ اے نبی صلى الله عليه وسلم ‘ ان سے کہو ” کون ہے جو رات کو یا دن کو تمہیں رحمن سے بچا سکتا ہو ؟ “ مگر یہ اپنے رب کی نصیحت سے منہ موڑ رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(21,43,'21:43','کیا یہ کچھ ایسے خدا رکھتے ہیں جو ہمارے مقابلے میں ان کی حمایت کریں ؟ وہ تو نہ خود اپنی مدد کرسکتے ہیں اور نہ ہماری ہی تائید ان کو حاصل ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,44,'21:44','اصل بات یہ ہے کہ ان لوگوں کو اور ان کے آبائو اجداد کو ہم زندگی کا سروسامان دیئے چلے گئے یہاں تک کہ ان کو دن لگ گئے مگر کیا انہیں نظر نہیں آتا کہ ہم زمین کو مختلف سمتوں سے گھٹاتے چلے آرہے ہیں ؟ پھر کیا یہ غالب آجائیں گے “');
INSERT INTO "sayyid_qutb" VALUES(21,45,'21:45','ان سے کہہ دو کہ ” میں تو وحی کی بنا پر تمہیں متنبہ کررہا ہوں “۔ مگر بہرے پکار کو نہیں سنا کرتے جبکہ انہیں خبردار کیا جائے “۔ ”');
INSERT INTO "sayyid_qutb" VALUES(21,46,'21:46','اور اگر تیرے رب کا عذاب ذرا سا انہیں چھو جائے تو ابھی چیخ اٹھیں کہ ہائے ہمار کم بختی ‘ بیشک ہم خطاوار تھے “');
INSERT INTO "sayyid_qutb" VALUES(21,47,'21:47','قیامت کے روز ہم ٹھیک ٹھیک تولنے والے ترازو رکھ دیں گے ‘ پھر کسی شخص پر ذرہ برابر ظلم نہ ہوگا۔ جس کا رائی کے دانے برابر بھی کچھ کیا دھرا ہوگا وہ ہم سامنے لے آئیں گے اور حساب لگانے کے لیے ہم کافی ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(21,48,'21:48','پہلے ہم موسیٰ اور ہارون (علیہم السلام) کو فرقان اور روشنی اور ” ذکر “ عطا کرچکی ہیں ‘ ان متقی لوگوں کی بھلائی کے لیے');
INSERT INTO "sayyid_qutb" VALUES(21,49,'21:49','جو بےدیکھے اپنے رب سے ڈریں اور جن کو (حساب کی) اس گھڑی کا کھٹکا لگا ہوا');
INSERT INTO "sayyid_qutb" VALUES(21,50,'21:50','اور اب یہ بابرکت ” ذکر “ ہم نے (تمہارے لیے ) نازل کیا ہے۔ پھر کیا تم اس کو قبول کرنے سے انکاری ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(21,51,'21:51','اس سے بھی پہلے ہم نے ابراہیم کو اس کی ہوشمندی بخشی تھی اور ہم اس کو خوب جانتے تھے ‘');
INSERT INTO "sayyid_qutb" VALUES(21,52,'21:52','یاد کرو وہ موقع جبکہ اس نے اپنے باپ اور اپنی قوم سے کہا تھا کہ ” یہ مورتیں کیسی ہیں جن کے تم لوگ گرویدہ ہو رہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(21,53,'21:53','انہوں نے جواب دیا ” ہم نے اپنے باپ دادا کو ان کی عبادت کرتے پایا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,54,'21:54','’ اس نے کہا ” تم بھی گمراہ ہو اور تمہارے باپ دادا بھی صریح گمراہی میں پڑے ہوئے تھے ‘');
INSERT INTO "sayyid_qutb" VALUES(21,55,'21:55','انہوں نے کہا ” کیا تو ہمارے سامنے اپنی اصلی خیالات پیش کررہا ہے یا مذاق کرتا ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(21,56,'21:56','اس نے جواب دیا ” نہیں ‘ بلکہ فی الواقع تمہارا رب وہی ہے جو زمین اور آسمانوں کا رب ہے اور ان کا پیدا کرنے والا ہے۔ اس پر میں تمہارے سامنے گواہی دیتا ہوں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,57,'21:57','اور خدا کی قسم میں تمہاری غیر موجودگی میں ضرور تمہارے بتوں کی خبر لوں گا ‘');
INSERT INTO "sayyid_qutb" VALUES(21,58,'21:58','چناچہ اس نے ان کو ٹکڑے ٹکڑے کردیا اور صرف ان کے بڑے کو چھوڑ دیا تاکہ شاید ۔۔۔ ان کی طرف رجوع کریں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,59,'21:59','(انہوں نے آکر بتوں کا یہ حال دیکھا تو) کہنے لگے ” ہمارے خدائوں کا یہ حال کس نے کردیا ؟ وہ بڑا ہی کوئی ظالم تھا وہ “');
INSERT INTO "sayyid_qutb" VALUES(21,60,'21:60','(بعض لوگ) بولے ” ہم نے ایک نوجوان کو ان کا ذکر کرتے سنا تھا جس کا نام ابراہیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,61,'21:61','انہوں نے کہا ” تو پکڑ لائو اسے سب کے سامنے تاکہ دیکھ لیں اس کی کیسی خبر لی جاتی ہے “۔ اور سب کے سامنے کھلی عدالت میں ان کا مقدمہ پیش ہو۔');
INSERT INTO "sayyid_qutb" VALUES(21,62,'21:62','انہوں نے پوچھا ” کیوں ابراہیم ‘ آیا تو نے ہمارے خدائوں کے ساتھ یہ حرکت کی ہے “ ؟');
INSERT INTO "sayyid_qutb" VALUES(21,63,'21:63','اس نے جواب دیا ” بلکہ یہ سب کچھ ان کے اس سردار نے کیا ہے ‘ ان ہی سے پوچھ لو اگر یہ بولتے ہوں “');
INSERT INTO "sayyid_qutb" VALUES(21,64,'21:64','یہ سن کر وہ اپنے ضمیر کی طرف پلٹے اور (اپنے دلوں میں ) کہنے لگے ” واقعی تم خود ہی ظالم ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(21,65,'21:65','’ مگر پھر ان کی مت پلٹ گئی اور بولے ” تو جانتا ہے کہ یہ بولتے نہیں ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(21,66,'21:66','اس نے کہا ” پھر کیا تم اللہ کو چھوڑ کر ان چیزوں کو پوج رہے ہو جو نہ تمہیں نفع پہنچانے پر قادر ہیں نہ نقصان');
INSERT INTO "sayyid_qutb" VALUES(21,67,'21:67','تف ہے تم پر اور تمہارے ان معبودوں پر جن کی تم اللہ کو چھوڑ کر پوجا کررہے ہو۔ کیا تم کچھ بھی عقل نہیں رکھتے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,68,'21:68','انہوں نے کہا ” جلا ڈالو اس کو اور حمایت کرو اپنے خدائوں کی اگر تمہیں کچھ کرنا ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(21,69,'21:69','ہم نے کہا اے آگ ٹھنڈی ہوجا اور سلامتی بن جا ابراہیم کے لیے “');
INSERT INTO "sayyid_qutb" VALUES(21,70,'21:70','وہ چاہتے تھے کہ ابراہیم کے ساتھ برائی کریں مگر ہم نے ان کو بری طرح ناکام کردیا ‘');
INSERT INTO "sayyid_qutb" VALUES(21,71,'21:71','اور ہم اسے اور لوط (علیہ السلام) کو بچا کر اس سرزمین کی طرف نکال لے گئے جس میں ہم نے دنیا والوں کے لیے برکتیں رکھی ہیں ‘');
INSERT INTO "sayyid_qutb" VALUES(21,72,'21:72','اور ہم نے اسے اسحاق عطا کیا اور یعقوب اس پر مزید ‘ اور ہر ایک کو صالح بنایا');
INSERT INTO "sayyid_qutb" VALUES(21,73,'21:73','اور ہم نے ان کو امام بنا دیا جو ہمارے حکم سے رہنمائی کرتے تھے اور ہم نے انہیں وحی کے ذریعے نیک کاموں کی اور نماز قائم کرنے اور زکوۃ دینے کی ہدایت کی اور وہ ہمارے عبادت گزار تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,74,'21:74','اور لوط کو ہم نے حکم او علم بخشا اور اسے اس بستی سے بچا کر نکال دیا جو بدکاریاں کرتی تھی۔ درحقیقت وہ بڑی ہی بری فاسق قوم تھی۔');
INSERT INTO "sayyid_qutb" VALUES(21,75,'21:75','اور لوط کو ہم نے اپنی رحمت میں داخل کیا ‘ وہ صالح لوگوں میں سے تھا ‘');
INSERT INTO "sayyid_qutb" VALUES(21,76,'21:76','اور یہی نعمت ہم نے نوح کو دی۔ یاد کرو جبکہ ان سب سے پہلے اس نے ہمیں پکارا تھا۔ ہم نے اس کی دعا قبول کی اور اسے اور اس کے گھر والوں کو کرب عظیم سے نجات دی۔');
INSERT INTO "sayyid_qutb" VALUES(21,77,'21:77','اور اس قوم کے مقابلے میں اس کی مدد کی جس نے ہماری آیات کو جھٹلایا تھا۔ وہ بڑے برے لوگ تھے ‘ پس ہم نے ان سب کو غرق کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(21,78,'21:78','اور اسی نعمت سے ہم نے دائود و سلیمان کو سرفراز کیا۔ یاد کرو وہ موقع جبکہ وہ دونوں ایک کھیت کے مقدمے میں فیصلہ کررہے تھے جس میں رات کے وقت دوسرے لوگوں کی بکریاں پھیل گئی تھیں ‘ اور ہم ان کی عدالت خود دیکھ رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(21,79,'21:79','اس وقت ہم نے صحیح فیصلہ سلیمان کو سمجھا دیا ‘ حالانکہ حکم اور علم ہم نے دونوں ہی کو عطا کیا تھا۔ دائود کے ساتھ ہم نے پہاڑوں اور پرندوں کو مسخر کردیا تھا جو تسبیح کرتے تھے ‘ اس فعل کے کرنے والے ہم ہی تھے');
INSERT INTO "sayyid_qutb" VALUES(21,80,'21:80','اور ہم نے اس کو تمہارے فائدے کے لیے ذرہ بنانے کی صنعت سکھا دی تھی ‘ تاکہ تم کو ایک دوسرے کی مار سے بچائے ‘ پھر کیا تم شکر گزار ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(21,81,'21:81','اور سلیمان کے لیے ہم نے تیز ہوا کو مسخر کردیا تھا جو اس کی حکم سے اس سر زمین کی طرف چلتی تھی جس میں ہم نے برکتیں رکھیں ہیں ‘ ہم ہر چیز کا علم رکھنے والے تھے');
INSERT INTO "sayyid_qutb" VALUES(21,82,'21:82','اور شیاطین میں سے ہم نے ایسے بہت سوں کو اس کا تابع بنادیا تھا جو اس کے لیے غوطے لگاتے اور اس کے سوا دوسرے کام کرتے تھے ‘ ان سب کے نگران ہم ہی تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,83,'21:83','اور یہی (ہوشمندی اور علم وعلم کی نعمت ) ہم نے ایوب کو دی تھی۔ یاد کرو ‘ جب کہ اس نے اپنے رب کا پکارا کہ ” مجھے بیماری لگ گئی ہے اور تو ارحم الراحمین ہے “');
INSERT INTO "sayyid_qutb" VALUES(21,84,'21:84','ہم نے اس کی دعا قبول کی اور جو تکلیف اسے تھی اس کو دور کردیا ‘ اور صرف اس کے اہل و عیال ہی اس کو نہیں دیئے بلکہ ان کے ساتھ اتنے ہی اور بھی دیئے ‘ اپنی خاص رحمت کے طور پر ‘ اور اس لیے کہ یہ ایک سبق ہے عبادت گزاروں کے لیے “۔');
INSERT INTO "sayyid_qutb" VALUES(21,85,'21:85','اور یہی نعمت اسماعیل اور ادریس اور ذوالکفل (علیہم السلام) کو دی کہ یہ سب صابر لوگ تھے');
INSERT INTO "sayyid_qutb" VALUES(21,86,'21:86','اور ان کو ہم نے اپنی رحمت میں داخل کیا کہ وہ صالحوں میں سے تھے ‘');
INSERT INTO "sayyid_qutb" VALUES(21,87,'21:87','’ اور مچھلی والے کو بھی ہم نے روازا۔ یاد کرو جب کہ وہ بگڑ کر چلا گیا تھا اور سمجھا تھا کہ ہم اس پر تنگی نہ کریں گے۔ آخر کو اس نے تاریکیوں میں پکارا ” نہیں ہے کوئی خدا مگر تو ‘ پاک ہے تیری ذات ‘ بیشک میں نے قصور کیا “۔');
INSERT INTO "sayyid_qutb" VALUES(21,88,'21:88','تب ہم نے اس کی دعا قبول کی اور غم سے اس کو نجات بخشی ‘ اور اسی طرح ہم مومنوں کو بچا لیا کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(21,89,'21:89','” اور زکریا کو ، جبکہ اس نے اپنے رب کو پکارا کہ ” اے پروردگار ، مجھے اکیلا نہ چھوڑ اور بہترین وارث تو تو ہی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,90,'21:90','پس ہم نے اس کی دعا قبول کی اور اسے یحییٰ عطا کیا اور اس کی بیوی کو اس کے لئے درست کردیا ۔ یہ لوگ نیکی کے کاموں میں دوڑ دھوپ کرتے تھے اور ہمیں رغبت اور خوف کے ساتھ پکارتے تھے اور ہمارے آگے جھکے ہوئے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,91,'21:91','’ اور وہ خاتون جس نے اپنی عصمت کی حفاظت کی تھی۔ ہم نے اس کے اندر اپنی روح سے پھونکا اور اسے اور اس کے بیٹے کو دنیا بھر کے لئے نشانی با دیا۔');
INSERT INTO "sayyid_qutb" VALUES(21,92,'21:92','’ یہ تمہاری امت حقیقت میں ایک ہی امت ہے اور میں تمہارا رب ہوں ، پس تم میری عبادت کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,93,'21:93','’ مگر (یہ لوگوں کی کارستانی ہے کہ) انہوں نے آپس میں اپنے دین کو ٹکڑے ٹکڑے کر ڈالا۔ سب کو ہماری طرف پلٹنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,94,'21:94','پھر جو نیک عمل کرے گا ، اس حال میں کہ وہ مومن ہو ، تو اس کے کام کی ناقدری نہ ہوگی اور اسے ہم لکھ رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(21,95,'21:95','اور ممکن نہیں ہے کہ جس بستی کو ہم نے ہلاک کردیا ہو وہ پھر پلٹ نہ آئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,96,'21:96','” یہاں تک کہ جب یاجوج و ماجوج کھول دیئے جائیں گے اور ہر بلندی سے وہ نکل پڑیں گے');
INSERT INTO "sayyid_qutb" VALUES(21,97,'21:97','اور وعدہ برحق کے پورا ہونے کا وقت قریب آ لگے گا تو یکایک ان لوگوں کے دیدے پھٹے کے پھٹے رہ جائیں گے جنہوں نے کفر کیا تھا۔ کہیں گے ” ہائے ہماری کم بختی ، ہم اس چیز کی طرف سے غفلت میں پڑے ہوئے تھے ، بلکہ ہم خطاکار تھے۔');
INSERT INTO "sayyid_qutb" VALUES(21,98,'21:98','“ بیشک تم اور تمہارے وہ معبود جنہیں تم اللہ کو چھوڑ کر پوجتے ہو ، جہنم کا ایندھن ہیں ، وہیں تم کو جانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,99,'21:99','اگر یہ واقعی خدا ہوتے تو وہاں نہ جاتے۔ اب سب کو ہمیشہ اسی میں رہنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,100,'21:100','وہاں وہ پھنکارے ماریں گے اور حال یہ ہوگا کہ اس میں کان بڑی آواز نہ سنائی دے گی۔');
INSERT INTO "sayyid_qutb" VALUES(21,101,'21:101','’ رہے وہ لوگ جن کے بارے میں ہماری طرف سے بھلائی کا فیصلہ پہلے ہی ہوچکا ہے تو وہ یقینا اس سے دور رکھے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(21,102,'21:102','اس کی سرسراہٹ تک نہ سنیں گے اور وہ ہمیشہ ہمیشہ اپنی من بھاتی چیزوں کے درمیان رہیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(21,103,'21:103','وہ انتہائی گھبراہٹ کا وقت ان کو ذرا پریشان نہ کرے گا اور ملائکہ بڑھ کر ان کو ہاتھوں ہاتھ لیں گے کہ ” یہ تمہارا وہی دن ہے جس کا تم سے وعدہ کیا جاتا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(21,104,'21:104','وہ دن جب کہ آسمان کو ہم یوں لپیٹ کر رکھ دیں گے جیسے طومار میں اوراق لپیٹ دیئے جاتے ہیں۔ جس طرح پہلے ہم نے تخلیق کی ابتداء کی تھی اسی طر ہم پھر اس کا اعادہ کریں گے۔ یہ ایک وعدہ ہے ہمارے ذمے ، اور یہ کام ہمیں بہرحال کرنا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,105,'21:105','’ اور زبور میں ہم نصیحت کے بعد یہ لکھ چکے ہیں کہ زمین کے وارث ہمارے نیک بندے ہوں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(21,106,'21:106','” اس میں ایک بڑی خبر ہے عبادت گزار لوگوں کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(21,107,'21:107','اے نبی ؐ، ہم نے تو تم کو دنیا والوں کے لئے رحمت بنا کر بھیجا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(21,108,'21:108','ان سے کہو ” میرے پاس جو وحی آتی ہے وہ یہ ہے کہ تمہارا خدا صرف ایک خدا ہے ، پھر کیا تم سر اطاعت جھکاتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(21,109,'21:109','“ اگر وہ منہ پھیریں تو کہہ دو کہ ” میں نے علی الاعلان تم کو خبردار کردیا ہے۔ اب یہ میں نہیں جانتا کہ وہ چیز جس کا تم سے وعدہ کیا جا رہا ہے قریب ہے یا دور۔');
INSERT INTO "sayyid_qutb" VALUES(21,110,'21:110','اللہ وہ باتیں بھی جانتا ہے جو با آواز بلند کہی جاتی ہیں اور وہ بھی جو تم چھپا کر کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(21,111,'21:111','میں تو یہ سمجھتا ہوں کہ شاید یہ (دیر) تمہارے لئے ایک فتنہ ہے اور تمہیں ایک وقت خاص تک کے لئے مزے کرنے کا موقع دیا جا رہا ہے۔ ‘');
INSERT INTO "sayyid_qutb" VALUES(21,112,'21:112','‘(آخر کار) رسول صلى الله عليه وسلم نے کہا کہ ” اے میرے رب ، حق کے ساتھ فیصلہ کر دے اور لوگو ، تم جو باتیں بناتے ہو ، ان کے مقابلے میں ہمارا رب رحمٰن ہی ہمارے لئے مدد کا سہارا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,1,'22:1','” لوگو ، اپنے رب کے غضب سے بچو ، حقیقت یہ ہے کہ قیامت کا زلزلہ بڑی (ہولناک) چیز ہے۔');
INSERT INTO "sayyid_qutb" VALUES(22,2,'22:2','جس روز تم اسے دیکھو گے ، حال یہ ہوگا کہ ہر دودھ پلانے والی اپنے دودھ پیتے بچے سے غافل ہوجائے گی ، ہر حاملہ کا حمل گر جائے گا اور لوگ تم کو مدہوش نظر آئیں گے ، حالانکہ وہ نشے میں نہیں ہوں گے ، بلکہ اللہ کا عذاب ہی کچھ ایسا سخت ہو گا');
INSERT INTO "sayyid_qutb" VALUES(22,3,'22:3','بعض لوگ ایسے ہیں جو علم کے بغیر اللہ کے بارے میں بحثیں کرتے ہیں اور ہر شیطان سرکش کی پیروی کرنے لگتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,4,'22:4','حالانکہ اس کے تو نصیب ہی میں یہ لکھا ہے کہ جو اس کو دوست بنائے گا اسے وہ گمراہ کر کے چھوڑے گا اور عذاب جہنم کا راستہ دکھائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(22,5,'22:5','” لوگو ، اگر تمہیں زندگی بعد موت کے بارے میں کچھ شک ہے تو تمہیں معلوم ہو کہ ہم نے تم کو مٹی سے پیدا کیا ہے ، پھر نطفے سے ، پھر خلیہ سے ، پھر خون کے لوتھڑے سے جو شکل والی بھی ہوتی ہے اور بےشکل بھی (یہ ہم اس لئے بتا رہے ہیں) تاکہ تم پر حقیقت واضح کریں۔ ہم جس (نطفے) کو چاہتے ہیں ایک وقت خاص تک رحموں میں ٹھہرائیرکھتے ہیں ، پھر تم کو ایک بچے کی صورت میں نکال لاتے ہیں (پھر تمہیں پرورش کرتے ہیں) تاکہ تم اپنی جوانی کو پہنچو اور تم میں سے کوئی پہلے ہی واپس بلا لیا جاتا ہے اور کوئی بدترین عمر کی طرف پھیر دیا جاتا ہے تاکہ سب کچھ جاننے کے بعد پھر کچھ نہ جانے اور تم دیکھتے ہو کہ زمین سوکھی پڑی ہے ، پھر جہاں ہم نے اس پر مینہ برسایا کہ یکایک وہ پھبک اٹھی اور پھول گئی اور اس نے ہر قسم کی خوش منظر نباتات اگلنی شروع کردی۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,6,'22:6','یہ سب کچھ اس وجہ سے ہے کہ اللہ ہی حق ہے ، اور وہ مردوں کو زندہ کرتا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(22,7,'22:7','اور یہ (اس بات کی دلیل ہے) کہ قیامت کی گھڑی آ کر رہے گی ، اس میں کوئی شک کی گنجائش نہیں ، اور اللہ ضرور ان لوگوں کو اٹھائے گا جو قبروں میں جا چکے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,8,'22:8','بعض اور لوگ ایسے ہیں جو کسی علم اور ہدایت اور روشنی بخشنے والی کتاب کے بغیر خدا کے بارے میں جھگڑتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,9,'22:9','گردن اکڑائے ہوئے ، تاکہ لوگوں کو راہ خدا سے بھٹکا دیں۔ ایسے شخص کے لئے دنیا میں رسوائی ہے اور قیامت کے روز اس کو ہم آگ کے عذاب کا مزا چکھائیں گے');
INSERT INTO "sayyid_qutb" VALUES(22,10,'22:10','یہ ہے تیرا وہ مستقبل جو تیرے اپنے ہاتھوں نے تیرے لئے تیار کیا ہے ورنہ اللہ اپنے بندوں پر ظلم کرنے والا نہیں');
INSERT INTO "sayyid_qutb" VALUES(22,11,'22:11','” اور لوگوں میں کوئی ایسا ہے جو کنارے پر رہ کر اللہ کی بندگی کرتا ہے ، اگر فائدہ ہوا تو مطمئن ہوگیا اور جو کوئی مصیبت آگئی تو الٹا پھر گیا۔ اس کی دنیا بھی گئی اور آخرت بھی ۔ یہ ہے صریح خسارہ۔');
INSERT INTO "sayyid_qutb" VALUES(22,12,'22:12','پھر وہ اللہ کو چھوڑ کر ان کو پکارتا ہے جو نہ اس کو نقصان پہنچا سکتے ہیں نہ فائدہ۔ یہ ہے گمراہی کی انتہا');
INSERT INTO "sayyid_qutb" VALUES(22,13,'22:13','وہ ان کو پکارتا ہے جن کا نقصان ان کے نفع سے قریب تر ہے ، بدترین ہے اس کا مولیٰ اور بدترین ہے اس کا رفیق۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,14,'22:14','اس کے برعکس) اللہ ان لوگوں کو جو ایمان لائے اور جنہوں نے نیک عمل کئے ، یقینا ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہ رہی ہوں گی۔ اللہ کرتا ہے جو کچھ چاہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(22,15,'22:15','” جو شخص یہ گمان رکھتا ہو کہ اللہ دنیا اور آخرت میں اس کی کوئی مدد نہ کرے گا ، اسے چاہئے کہ ایک رسی کے ذریعے آسمان تک پہنچ کر شگاف لگائے پھر دیکھ لے کر آیا اس کی تدبیر کسی ایسی چیز کو رد کرسکتی ہے جو اس کو ناگوار ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,16,'22:16','” ایسی ہی کھلی کھلی باتوں کے ساتھ ہم نے اس قرآن کو نازل کیا ہے اور اللہ جسے چاہتا ہے ، ہدایت دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,17,'22:17','” جو لوگ ایمان لائے اور جو یہودی ہوئے اور صابئی اور نصاریٰ اور مجوس اور جن لوگوں نے شرک کیا ، ان سب کے درمیان اللہ قیامت کے روز فیصلہ کر دے گا ، ہر چیز اللہ کی نظر میں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,18,'22:18','” کیا تم دیکھتے نہیں ہو کہ اللہ کے آگے سربسجود ہیں وہ سب جو آسمانوں میں ہیں اور جو زمین میں ہیں ، سورج اور چاند اور تارے اور پہاڑ اور درخت اور جانور اور بہت سے انسان اور بہت سے وہ لوگ بھی جو عذاب کے مستحق ہوچکے ہیں ؟ اور جسے اللہ ذلیل و خوار کر دے اسے پھر کوئی عزت دینے والا نہیں ہے ، اللہ کرتا ہے جو کچھ چاہتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,19,'22:19','یہ دو فریق ہیں جن کے درمیان اپنے رب کے معاملے میں جھڑا ہے۔ ان میں سے وہ لوگ جنہوں نے کفر کیا ان کے لئے آگ کے لباس کاٹے جا چکے ہیں ، ان کے سروں پر کھولتا ہوا پانی ڈالا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(22,20,'22:20','جس سے ان کی کھالیں ہی نہیں پیٹ کے اندر کے حصے تک گل جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(22,21,'22:21','اور ان کی خبر لینے کے لئے لوہے کے گرز ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(22,22,'22:22','جب کبھی وہ گھبرا کر جہنم سے نکلنے کی کوشش کریں گے پھر سای میں دھکیل دیئے جائیں گے کہ چکھو اب جلنے کی سزا کا مزہ');
INSERT INTO "sayyid_qutb" VALUES(22,23,'22:23','(دوسری طرف) جو وگ ایمان لائے اور جنہوں نے نیک عمل کئے ان کو اللہ ایی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی۔ وہاں وہ سونے کے کنگنوں اور موتیوں سے آراستہ کئے جائیں گے اور ان کے لباس ریشم کے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(22,24,'22:24','وھدوا الی ......صراط الحمید (24) ترجمہ :۔ ” ان کو پاکیزہ بات قبول کرنے کی ہدایت بخشی گئی اور انہیں خدائے ستودہ صفات کا راستہ دکھایا گیا۔ “ اللہ کی ذات کے بارے میں دو فریق وجود میں آگئے ۔ ایک گروہ ایسا ہے ، جو بغیر ہدایت کے ، بغیر دلیل کے اور بغیر کتاب منیر کے اللہ کے بارے میں گفتگو کرتا ہے اور دوسرا گروہ جن کو آیات و ہدایات میں سے کوئی بات نظر نہیں آتی دونوں فریق اپنے انجام کو دیکھ لیں۔ جو لوگ اللہ کے بارے میں بغیر علم اور بغیر کتاب منیر کے بات کرتے ہیں وہ اپنا انجام دیکھ لیں۔');
INSERT INTO "sayyid_qutb" VALUES(22,25,'22:25','جن لوگوں نے کفر کیا اور جو (آج) اللہ کے راستے سے روک رہے ہیں اور اس مسجد حرام کی زیارت میں مانع ہیں جسے ہم نے سب لوگوں کے لئے بنایا ہے ، جس میں مقامی باشندوں اور باہر سے آنے والوں کے حقوق برابر ہیں (ان کی روش یقینا سزا کی مستحق ہے۔ ) اس (مسجد حرام) میں جو بھی راستی سے ہٹ کر ظلم کا طریقہ اختیار کرے گا اسے ہم درد ناک عذاب کا مزا چکھائیں گے');
INSERT INTO "sayyid_qutb" VALUES(22,26,'22:26','” یاد کرو وہ وقت جبکہ ہم نے ابراہیم کے لئے اس گھر (خانہ کعبہ) کی جگہ تجویز کی تھی (اس ہدایت کے ساتھ) کہ میرے ساتھ کسی چیز کو شریک نہ کرو ، اور میرے گھر کو طواف کرنے والوں اور قیام و رکوع و سجود کرنے والوں کے لئے پاک رکھو');
INSERT INTO "sayyid_qutb" VALUES(22,27,'22:27','اور لوگوں کو حج کے لئے اذن عام دے دو کہ وہ تمہارے پاس ہر دور دراز مقام سے پیدل اور اونٹوں پر سوار آئیں ،');
INSERT INTO "sayyid_qutb" VALUES(22,28,'22:28','تاکہ وہ فائدے دیکھیں جو یہاں ان کے لئے رکھے گئے ہیں اور چند مقرر دنوں میں ان جانوروں پر اللہ کا نام لیں جو اس نے انہیں بخشے ہیں ، خود بھی کھائیں اور تنگ دست محتاج کو بھی دیں');
INSERT INTO "sayyid_qutb" VALUES(22,29,'22:29','پھر اپنا میل کچیل دور کریں اور اپنی نذریں پوری کریں اور اس قدیم گھر کا طواف کریں۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,30,'22:30','” یہ تھا (تعمیر کعبہ کا مقصد) اور جو کوئی اللہ کی قائم کردہ حرمتوں کا احترام کرے تو یہ اس کے رب کے نزدیک خد اسی کے لئے بہتر ہے اور تمہارے لئے مویشی جانور حلال کئے گئے ، ماسوا ان چیزوں کے جو تمہیں بتائی جا چکی ہیں۔ پس بتوں کی گندگی سے بچو ، جھوٹی باتوں سے پرہیز کرو');
INSERT INTO "sayyid_qutb" VALUES(22,31,'22:31','یکسو ہو کر اللہ کے بندے بنو ، اس کے ساتھ کسی کو شریک نہ کرو اور جو کوئی اللہ کے ساتھ شریک کرے تو گویا وہ آسمان سے گر گیا ، اب یا تو اسے پرندے اچک لے جائیں گے یا ہوا اس کو ایسی جگہ لے جا کر پھینک دے گی جہاں اس کے چیتھڑے اڑ جائیں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,32,'22:32','یہ ہے اصل معاملہ (اسے سمجھ لو) اور جو اللہ کے مقرر کردہ شعائر کا احترام کرے تو یہ دلوں کے تقویٰ سے ہے');
INSERT INTO "sayyid_qutb" VALUES(22,33,'22:33','تمہیں ایک وقت مقرر تک ان (ہدی کے جانوروں) سے فائدہ اٹھانے کا حق ہے ، پر ان (کے قربان کرنے) کی جگہ اسی قدیم گھر کے پاس ہے');
INSERT INTO "sayyid_qutb" VALUES(22,34,'22:34','” ہر امت کے لئے ہم نے قربانی کا ایک قاعدہ مقرر کردیا ہے تاکہ (اس امت کے) لوگ ان جانوروں پر اللہ کا نام لیں جو اس نے ان کو بخشے ہیں۔ (ان مختلف طریقوں کے اندر مقصد ایک ہی ہے) پس تمہارا خدا ایک ہی خدا ہے اور اسی کے تم مطیع فرمان بنو اور اے نبی ؐ بشار دے دو عاجزانہ روش اختیار کرنے والوں کو');
INSERT INTO "sayyid_qutb" VALUES(22,35,'22:35','جن کا حال یہ ہے کہ اللہ کا ذکر سنتے ہیں تو ان کے دل کانپ اٹھتے ہیں ، جو مصیبت بھی ان پر آتی ہے اس پر صبر کرتے ہیں ، نماز قائم کرتے ہیں ، اور جو کچھ رزق ہم نے ان کو دیا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,36,'22:36','اور (قربانی کے ) اونٹوں کو ہم نے تمہارے لئے شعائر اللہ میں شامل کیا ہے ، تمہارے لئے ان میں بھلائی ہے ، پس انہیں کھڑا کر کے ان پر اللہ کا نام لو اور جب (قربانی کے بعد) ان کی پیٹھیں زمین پر ٹک جائیں تو ان میں سے خود بھی کھائو اور ان کو بھی کھلائو جو قناعت کئے بیٹھے ہیں اور ان کو بھی جو اپنی حاجت پیش کریں۔ ان جانوروں کو ہم نے اس طرح تمہارے لئے سخر کیا ہے تاکہ تم شکریہ ادا کرو');
INSERT INTO "sayyid_qutb" VALUES(22,37,'22:37','نہ ان کے گوشت اللہ کو پہنچتے ہیں نہ خون ، مگر اسے تمہارا تقویٰ پہنچتا ہے۔ اس نے ان کو تمہارے لئے اس طرح مسخر کیا ہے تاکہ اس کی بخشی ہوئی ہدایت پر تم اس کی تکبیر کرو اور اے نبی ؐ بشارت دے دے نیکو کار لوگوں کو');
INSERT INTO "sayyid_qutb" VALUES(22,38,'22:38','یقینا اللہ مدافعت کرتا ہے ان لوگوں کی طرف سے جو ایمان لائے ہیں۔ یقینا اللہ کسی خائن کافر نعمت کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(22,39,'22:39','اجازت دے دی گئی ان لوگوں کو جن کے خلاف جنگ کی جا رہی ہے ، کیونکہ وہ مظلوم ہیں اور اللہ یقینا ان کی مدد پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(22,40,'22:40','یہ وہ لوگ ہیں جو اپنے گھروں سے ناحق نکال دیئے گئے صرف اس قصور پر کہ وہ کہتے تھے ” ہمارا رب اللہ ہے۔ “ اگر اللہ لوگوں کو ایک دوسرے کے ذریعے دفع نہ کرتا رہے تو خانقاہیں اور گرجے اور معبد اور مسجدیں ، جن میں اللہ کا کثرت سے نام لیا جاتا ہے ، سب مسمار کر ڈالی جائیں۔ اللہ ضرور ان لوگوں کی مدد کرے گا جو اس کی مدد کریں گے۔ اللہ بڑا طاقتور اور زبردست ہے');
INSERT INTO "sayyid_qutb" VALUES(22,41,'22:41','یہ وہ لوگ ہیں جنہیں اگر ہم زمین میں اقتدار بخشیں تو وہ نماز قائم کریں گے۔ زکوۃ دیں گے ، نیکی کا حکم دیں گے اور برائی سے منع کریں گے اور تمام معاملات کا انجام کار اللہ کے ہاتھ میں ہے');
INSERT INTO "sayyid_qutb" VALUES(22,42,'22:42','” اے نبی صلى الله عليه وسلم اگر وہ (یعنی کفار) تمہیں جھٹلاتے ہیں تو ان سیپ ہلے قوم نوح اور عاد اور ثمود');
INSERT INTO "sayyid_qutb" VALUES(22,43,'22:43','اور قوم ابراہیم اور قوم لوط');
INSERT INTO "sayyid_qutb" VALUES(22,44,'22:44','اور اہل مدین بھی جھٹلا چکے ہیں اور موسیٰ بھی جھٹلائے جا چکے ہیں۔ ان سب منکرین حق کو میں نے پہلے مہلت دی پھر پکڑ لیا۔ اب دیکھ لو کہ میری عقوبت کیسی تھی');
INSERT INTO "sayyid_qutb" VALUES(22,45,'22:45','” کتنی ہی خطاکار بستیاں ہیں جن کو ہم نے تباہ کیا ہے اور آج وہ اپنی چھتوں پر الٹی پڑی ہیں ، کتنے ہی کنوئیں بیکار اور کتنے ہی قصر کھنڈر بنے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,46,'22:46','” کیا یہ لوگ زمین میں چلے پھرے نہیں ہیں کہ ان کے دل سمجھنے والے یا ان کے کان سننے والے ہوتے ؟ حقیقت یہ ہے کہ آنکھیں اندھی نہیں ہوتیں مگر وہ دل اندھے ہوجاتے ہیں جو سینوں میں ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,47,'22:47','” یہ لوگ عذاب کے لئے جلدی مچا رہے ہیں۔ اللہ ہرگز اپنے وعدے کے خلاف نہ کرے گا ، مگر تیرے رب کے ہاں کا ایک دن تمہارے شمار کے ہزار برس کے برابر وہا کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(22,48,'22:48','” کتنی ہی بستیاں ہیں جو ظالم تھیں ، میں نے ان کو پہلے مہلت دی ، پھر پکڑ لیا اور سب کو واپس تو میرے ہی پاس آنا ہے');
INSERT INTO "sayyid_qutb" VALUES(22,49,'22:49','” اے نبی صلى الله عليه وسلم کہہ دو کہ ” لوگو ، میں تو تمہارے لئے صرف وہ شخص ہوں جو (برا وقت آنے سے پہلے) صاف صاف خبردار کردینے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(22,50,'22:50','پھر جو ایمان لائیں گے اور نیک عمل کریں گے ان کے لئے مغفرت ہے اور عزت کی روزی');
INSERT INTO "sayyid_qutb" VALUES(22,51,'22:51','اور جو ہماری آیات کو نیچا دکھانے کی کوشش کریں گے وہ دوزخ کے یار ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,52,'22:52','” اور اے نبی ، تم سے پہلے ہم نے نہ کوئی رسول ایسا بھیجا ہے نہ نبی (جس کے ساتھ یہ معاملہ نہ پیش آیا ہو کہ) جب اس نے تمنا کی ، شیطان اس کی تمنا میں خلل انداز ہوگیا۔ اس طرح جو کچھ بھی شیطان خلل اندازیاں کرتا ہے ، اللہ ان کو مٹا دیتا ہے اور اپنی آیات کو پختہ کردیتا ہے ، اللہ علیم ہے اور حکیم');
INSERT INTO "sayyid_qutb" VALUES(22,53,'22:53','(وہ اس لئے ایسا ہونے دیتا ہے) تاکہ شیطان کی ڈالی ہوئی خرابی کو فتنہ بنا دے ان لوگوں کے لئے جن کے دلوں کو (نفاق کا) روگ لگا ہوا ہے اور جن کے دل کھوٹے ہیں۔ حقیقت یہ ہے کہ یہ ظالم لوگ عناد میں بہت دور نکل گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(22,54,'22:54','اور علم سے بہرہ مند لوگ جان لیں کہ یہ حق ہے تیرے رب کی طرف سے اور وہ اس پر ایمان لے آئیں اور ان کے دل اس کے آگے جھک جائیں ، یقینا اللہ ایمان لانے والوں کو ہمیشہ سیدھا راستہ دکھا دیتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,55,'22:55','” انکار کرنے والے تو اس کی طرف سے شک ہی میں پڑے رہیں گے یہاں تک کہ یا تو ان پر قیامت کی گھڑی اچانک آجائے ، یا ایک منحوس دن کا عذاب نازل ہو جائے');
INSERT INTO "sayyid_qutb" VALUES(22,56,'22:56','اس روز بادشاہی اللہ کی ہوگی ، اور وہ ان کے درمیان فیصلہ کر دے گا۔ جو ایمان رکھنے والے اور عمل صالح کرنے والے ہوں گے وہ نعمت بھری جنتوں میں جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(22,57,'22:57','اور جنہوں نے کفر کیا ہوگا اور ہماری آیات کو جھٹلایا ہوگا ان کے لئے رسوا کن عذاب ہوگا۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,58,'22:58','اور جن لوگوں نے اللہ کی راہ میں ہجرت کی ، پھر قتل کردیئے گئے یا مر گئے ، اللہ ان کو اچھا رزق دے گا اور یقینا اللہ ہی بہترین رازق ہے');
INSERT INTO "sayyid_qutb" VALUES(22,59,'22:59','” ۔ وہ انہیں ایسی جگہ پہنچائے گا جس سے وہ خوش ہوجائیں گے بیشک اللہ علیم اور حلیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,60,'22:60','” یہ تو ہے ان کا انجام اور جو کوئی بدلہ لے ، ویسا ہی جیسا اس کے ساتھ کیا گیا ، اور پھر اس پر زیادتی بھی کی گئی ہو ، تو اللہ اس کی مدد ضرور کرے گا۔ اللہ معاف کرنے والا اور درگزر کرنے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,61,'22:61','” یہ اس لئے کہ رات سے دن اور دن سے رات نکالنے والا اللہ ہی ہے اور وہ سمیع وبصیر ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,62,'22:62','” یہ اس لئے کہ اللہ ہی حق ہے اور وہ سب باطل ہیں جنہیں اللہ کو چھوڑ کر یہ لوگ پکارتے ہیں اور اللہ ہی بالا دست اور بزرگ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,63,'22:63','” کیا تم دیکھتے نہیں ہو کہ اللہ آسمان سے پانی برساتا ہے اور اس کی بدولت زمین سرسبز ہوجاتی ہے ؟ حقیقت یہ ہے کہ وہ لطیف وخبیر ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,64,'22:64','” اسی کا ہے جو کچھ آسمانوں میں ہے اور جو کچھ زمین میں ہے ، بیشک وہی غنی وحمید ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,65,'22:65','” کیا تم دیکھتے نہیں ہو کہ اس نے وہ سب کچھ تمہارے لئے مسخر کر رکھا ہے جو زمین میں ہے ، اور اسی نے کشتی کو قاعدے کا پابند بنایا ہے۔ وہ اس کے حکم سے سمندر میں چلتی ہے اور وہی آسمان کو اس طرح تھامے ہوئے کہ اس کے اذن کے بغیر وہ زمین پر نہیں گر سکتا۔ واقعہ یہ ہے کہ اللہ لوگوں کے حق میں بڑا شفیق اور رحیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,66,'22:66','” وہی ہے جس نے تمہیں زندگی بخشی ہے ، وہی تم کو موت دیتا ہے اور وہی پھر تم کو زندہ کرے گا۔ سچ یہ ہے کہ انسان بڑا ہی منکر حق ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,67,'22:67','” ہر امت کے لئے ہم نے ایک طریق عبادت مقرر کیا ہے جس کی وہ پیروی کرتی ہے ، پس اے نبی وہ اس معاملہ میں تم سے جھگڑا نہ کریں۔ تم اپنے رب کی طرف دعوت دو ۔ یقینا تم سیدھے پر ہو');
INSERT INTO "sayyid_qutb" VALUES(22,68,'22:68','اور اگر وہ تم سے جھگڑیں تو کہہ دو کہ ” جو کچھ تم کر رہے ہو ، اللہ کو خوب معلوم ہے');
INSERT INTO "sayyid_qutb" VALUES(22,69,'22:69','اللہ قیامت کے روز تمہارے درمیان ان سب باتوں کا فیصلہ کر دے گا جن میں تم اتخلاف کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(22,70,'22:70','کیا تم نہیں جانتے کہ آسمان و زمین کی ہر چیز اللہ کے علم میں ہے ؟ سب کچھ ایک کتاب میں درج ہے۔ اللہ کے لئے یہ کچھ بھی مشکل نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,71,'22:71','” یہ لوگ اللہ کو چھوڑ کر ان کی عبادت کر رہے ہیں جن کے لئے نہ تو اس نے کوئی سند نازل کی ہے اور نہ یہ خود ان کے بارے میں کوئی علم رکھتے ہیں۔ ان ظالموں کے لئے کوئی مددگار نہیں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,72,'22:72','” اور جب ان کو ہماری صاف صاف آیات سنائی جاتی ہیں تو تم دیکھتے ہو کہ منکرین حق کے چہرے بگڑنے لگتے ہیں ، اور ایسا محسوس ہوتا ہے کہ ابھی وہ ان لوگوں پر ٹوٹ پڑیں گے جو انہیں ہماری آیات سناتے ہیں۔ “ ” ان سے کہو میں بتائوں تمہیں کہ اس سے بد تر چیز ہے ؟ آگ ، اللہ نے اسی کا وعدہ ان لوگوں کے حق میں رکر رکھا ہے جو قبول حق سے انکار کریں اور وہ بہت ہی برا ٹھکانا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,73,'22:73','” لوگو ، ایک مثال دی جاتی ہے ، غور سے سنو جن معبودوں کو تم خدا کو چھوڑ کر پکارتے ہو وہ سب مل کر ایک مکھی بھی پیدا کرنا چاہیں تو نہیں کرسکتے۔ بلکہ اگر مکھی ان سے کوئی چیز چھین لے جائے تو وہ اسے چھڑا بھی نہیں سکتے۔ مدد چاہنے والے بھی کمزور اور جن سے مدد چاہی جاتی ہے وہ بھی کمزور۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,74,'22:74','” ان لوگوں نے اللہ کی قدرت ہی نہ پہنچانی ، جیسا کہ اس کے پہچاننے کا حق ہے۔ واقعہ یہ ہے کہ قوت اور عزت والا تو اللہ ہی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,75,'22:75','’ حقیقت یہ ہے کہ اللہ (اپنے فرامین کی ترسیل کے لئے) ملائکہ میں سے بھی پیغام رساں منتخب کرتا ہے اور انسانوں میں سے بھی۔ وہ سمیع اور بصیر ہے');
INSERT INTO "sayyid_qutb" VALUES(22,76,'22:76','’ جو کچھ لوگوں کے سامنے ہے اسے بھی وہ جانتا ہے اور جو کچھ ان سے اوجھل ہے اس سے بھی وہ واقف ہے اور سارے معاملات اسی کی طرف رجوع ہوتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(22,77,'22:77','” اے لوگو جو ایمان لائے ہو ، رکوع اور سجدہ کر ، اپنے رب کی بندگی کرو ، اور نیک کام کرو ، اسی سے توقع کی جاسکتی ہے کہ تم کو فلاح نصیب ہو');
INSERT INTO "sayyid_qutb" VALUES(22,78,'22:78','اللہ کی راہ میں جہاد کرو جیسا کہ جہاد کرنے کا حق ہے۔ اس نے تمہیں اپنے کام کے لئے چن لیا ہے اور دین میں تم پر کوئی تنگی نہیں رکھی۔ قائم ہو جائو اپنے باپ ابراہیم (علیہ السلام) کی ملت پر۔ اللہ نے پہلے بھی تمہارا نام ” مسلم “ رکھا تھا اور اس (قرآن) میں بھی (تمہارا یہی نام ہے) تاکہ رسول صلى الله عليه وسلم تم پر گواہ ہو اور تم لوگوں پر گواہ پس نماز قائم کرو ، زکوۃ دو اور اللہ سے وابستہ ہو جائو۔ وہ ہے تمہارا مولیٰ بہت ہی اچھا ہے وہ مولیٰ اور بہت ہی اچھا ہے وہ مددگار۔ “');
INSERT INTO "sayyid_qutb" VALUES(23,1,'23:1','” یقیناً فلاح پائے ہے ایمان لانے والوں نے');
INSERT INTO "sayyid_qutb" VALUES(23,2,'23:2','جو اپنی نماز میں خشوع اختیار کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,3,'23:3','لغویات سے دور رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,4,'23:4','زکوۃ کے طریقے پر عامل ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,5,'23:5','اپنی شرمگاہوں کی حفاظت کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,6,'23:6','سوائے اپنی بیویو کے اور ان عورتوں کے جو ان کی ملک کی یمین میں ہو کہ ان پر محفوظ نہ رکھنے میں وہ قابل ملامت نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,7,'23:7','البتہ جو اس کے علاوہ کچھ اور چاہیں وہ زیادتی کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,8,'23:8','اپنی امانتوں اور اپنے عہد ایمان کا پاس رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,9,'23:9','اور اپنی نمازوں کی محافظت کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,10,'23:10','یہی لوگ وہ وارث ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,11,'23:11','جو میراث میں فردوس پائیں گے اور اس میں ہمیشہ رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,12,'23:12','ہم نے انسان کو مٹی کے ست سے بنایا');
INSERT INTO "sayyid_qutb" VALUES(23,13,'23:13','پھر اسے ایک محفوظ جگہ ٹپکی ہوئی بوند میں تبدل کیا');
INSERT INTO "sayyid_qutb" VALUES(23,14,'23:14','پھر اس بوند کو لوتھڑے کی شکل دی ، پھر لوتھڑے کو بوٹی بنادیا ، پھر بوٹی کی ہڈیاں بنائیں ، پھر ہڈیوں کو گوشت چڑھایا ، پھر اس ایک دوسری ہی مخلوق بنا کھڑا کیا۔ پس بڑا ہی بابرکت ہے اللہ ، سب کاریگروں سے اچھا کاریگر۔');
INSERT INTO "sayyid_qutb" VALUES(23,15,'23:15','پھر اس کے بعد تم کو ضرور مرنا ہے ');
INSERT INTO "sayyid_qutb" VALUES(23,16,'23:16','پھر قیامت کے روز تم یقیناً اٹھائے جائو گے');
INSERT INTO "sayyid_qutb" VALUES(23,17,'23:17','ور تمہارے اوپر ہم نے سات راستے بنائے ، تخلیق کے کام سے ہم کچھ نا بلد نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(23,18,'23:18','” اور آسمان سے ہم نے ٹھیک حساب کے مطابق ایک خاص مقدار میں پانی اتارا اور اس کو زمین میں ٹھرا دیا ، ہم اسے جس طرح چاہیں غائب کرسکتے ہیں ۔');
INSERT INTO "sayyid_qutb" VALUES(23,19,'23:19','پھر اس پانی کے ذریعہ سے ہم نے تمہارے لیے کھجور اور انگور کے باغ پیدا کردیئے ، تمہارے لیے ان باغوں میں بہت سے لذیذ پھل ہیں اور ان سے تم روزی حاصل کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(23,20,'23:20','اور وہ درخت بھی ہم نے پیدا کیا جو طور سینا سے نکلتا ہے ، تیل بھی لیے ہوئے اگتا ہے اور کھانے والوں کے لیے سالن بھی');
INSERT INTO "sayyid_qutb" VALUES(23,21,'23:21','اور حقیقت یہ ہے کہ تمہارے لیے مویشیوں میں بھی ایک سبق ہے۔ ان کے بیٹوں میں جو کچھ ہے اسی میں سے ایک چیز (یعنی دودھ ) ہم تمہیں پلاتے ہیں ، اور تمہارے لیے ان میں بہت سے دوسرے فائدے بھی ہیں ۔ ان کو تمام کھاتے ہو');
INSERT INTO "sayyid_qutb" VALUES(23,22,'23:22','اور ان پر اور کشتیوں پر سوار بھی کیے جاتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(23,23,'23:23','ہم نے نوح کو اس قوم کی طرف بھیجا ۔ اس نے کہا اے میری قوم کے لوگو ، اللہ کی بندگی کرو ، اس کے سوا تمہارے لیے کوئی معبود نہیں ہے ، کیا تم ڈرتے نہیں ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(23,24,'23:24','” اس کی قوم کے جن سرداروں نے ماننے سے انکار کیا وہ کہنے لگے کہ ” یہ شخص کچھ نہیں ہے مگر ایک بشر تم ہی جیسا۔ اس کی غرض یہ ہے کہ تم پر برتری حاصل کرے۔ اللہ کو اگر بھیجنا ہوتا تو فرشتے بھیجتا۔ یہ بات تو ہم نے کبھی اپنے باپ دادا کے وقتوں میں سنی ہی نہیں (کہ بشر رسول بن کر آئے) ۔');
INSERT INTO "sayyid_qutb" VALUES(23,25,'23:25','کچھ نہیں ، بس اس آدمی کو ذرا جنون لاحق ہوگیا ہے ۔ کچھ مدت اور دیکھ لو (شاید افاقہ ہوجائے) ۔');
INSERT INTO "sayyid_qutb" VALUES(23,26,'23:26','نوح نے کہا ” پروردگار ، ان لوگوں نے جو میری تکذیب کی ہے ، اس پر اب تو ہی میری نصرت فرما “۔');
INSERT INTO "sayyid_qutb" VALUES(23,27,'23:27','ہم نے اس پر وحی کی کہ ” ہماری نگرانی میں اور ہماری وحی کے مطابق کشتی تیار کر۔ پھر جب ہمارا حکم آجائے اور ور تنور ایل پڑے تو ہر قسم کے جانوروں میں سے ایک ایک جوڑا لے کر اس میں سوار ہوجا ، اور اپنے اہل عیال کو بھی ساتھ لے ، سوائے ان کے جن کے خلاف پہلے ہی فیصلہ ہوچکا ہے ، اور ظالموں کے بارے میں مجھ سے کچھ نہ کہنا ، یہ اب غرق ہونے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(23,28,'23:28','پھر جب تو اپنے ساتھیوں سمیت کشتی پر سوار ہوجائے تو کہہ شکر ہے اس خدا کا جس نے ہمیں ظالم لوگوں سے نجات دی');
INSERT INTO "sayyid_qutb" VALUES(23,29,'23:29','اور کہہ ، پروردگار ، مجھ کو برکت والی جگہ انار اور تو بہترین جگہ دینے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(23,30,'23:30','” اس قصے میں بڑی نشانیاں ہیں ، اور آزمائش تو ہم کرکے ہی رہتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(23,31,'23:31','ان کے بعد ہم نے ایک دوسرے دور کی قوم اٹھائی');
INSERT INTO "sayyid_qutb" VALUES(23,32,'23:32','پھر ان میں خود انہی کی قوم کا ایک رسول بھیجا (جس نے انہیں ، دعوت دی) کہ اللہ کی بندگی کرو ، تمہارے لیے اس کے سوا کوئی اور معبود نہیں ہے ، کیا تم ڈرتے نہیں ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(23,33,'23:33','اس کی قوم کے جن سرداروں نے ماننے سے انکار کیا اور اخرت کی پیشی کو جھٹلایا ، جن کے ہم نے دنیا کی زندگی میں آسودہ کر رکھا تھا ، وہ کہنے لگے ” یہ شخص کچھ نہیں ہے ، مگر ایک بشر تم ہی جیسا');
INSERT INTO "sayyid_qutb" VALUES(23,34,'23:34','جو کچھ تم کھاتے ہو ، وہی یہ کھاتا ہے ، اور جو کچھ تم پیتے ہو ، وہی یہ پیتا ہے ۔ اب اگر تم انے اپنے ہی جیسے ایک بشر کی اطاعت قبول کرلی تو تم گھاٹے ہی میں ہی رہے');
INSERT INTO "sayyid_qutb" VALUES(23,35,'23:35','یہ تمہیں اطلاع دیتا ہے کہ جب تم مرکر مٹی ہو جائو گے اور ہڈیوں کا پنجر بن کر رہ جائو گے اس وقت تم ( قبروں سے) نکالے جائو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(23,36,'23:36','بعید ، بالکل بعید یہ وعدہ تم سے کیا جارہا ہے');
INSERT INTO "sayyid_qutb" VALUES(23,37,'23:37','زندگی کچھ نہیں ہے مگر بس یہی دنیا کی زندگی۔ یہیں ہم کو مرنا اور جینا ہے اور ہم ہر گز اٹھائے جانے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,38,'23:38','یہ شخص خدا کے نام پر محض جھوٹ گھڑ رہا ہے اور ہم کبھی اس کی ماننے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,39,'23:39','رسول صلى الله عليه وسلم نے کہاــ” پروردگار ، ان لوگوں نے جو میری تکذیب کی ہے اس پر اب تو ہی میری نصرت فرما');
INSERT INTO "sayyid_qutb" VALUES(23,40,'23:40','جواب میں ارشاد ہوا ” قریب ہے وہ وقت جب یہ اپنے کیے پر پچھتائیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,41,'23:41','آخر کار ٹھیک ٹھیک حق کے مطابق ایک ہنگامہ عظیم نے ان کو آلیا اور ہم نے ان کو کچرا بناکر پھینک دیا۔۔۔۔۔ دور ہو ظالم قوم ! “');
INSERT INTO "sayyid_qutb" VALUES(23,42,'23:42','پھر ہم نے ان کے بعد دوسری قومیں اٹھائیں');
INSERT INTO "sayyid_qutb" VALUES(23,43,'23:43','کوئی قوم نہ اپنے وقت سے پہلے ختم ہوئی اور نہ اس کے بعد ٹھہر سکی');
INSERT INTO "sayyid_qutb" VALUES(23,44,'23:44','پھر ہم نے پے درپے اپنے رسول بھیجے۔ جس قوم کے پاس بھی اس کا رسول آیا ، اس نے اسے جھٹلادیا ، اور ہم ایک کے بعد ایک قوم کو ہلاک کرتے چلے گئے ، حتیٰ کہ ان کو بس افسانہ ہی بنا کر چھوڑا۔۔۔ پھٹکار ہے ان لوگوں پر جو ایمان نہیں لاتے ! “');
INSERT INTO "sayyid_qutb" VALUES(23,45,'23:45','” پھر ہم نے موسیٰ اور اس کے بھائی ہارون کو اپنی نشانیوں اور کھلی سند کے ساتھ');
INSERT INTO "sayyid_qutb" VALUES(23,46,'23:46','فرعون اور اس کے اعیان سلطنت کی طرف بھیجا مگر انہوں نے تکبر کیا');
INSERT INTO "sayyid_qutb" VALUES(23,47,'23:47','اور ۔ کہنے لگے ” کیا ہم اپنے ہی جیسے دو آدمیوں پر ایمان لے آئیں ؟ اور آدمی بھی وہ جن کی قوم ہماری غلام ہے “');
INSERT INTO "sayyid_qutb" VALUES(23,48,'23:48','پس انہوں نے دونوں کو جھٹلادیا اور ہلادک ہونے والوں میں جاملے');
INSERT INTO "sayyid_qutb" VALUES(23,49,'23:49','اور موسیٰ کو ہم نے کتاب عطا فرمائی تاکہ لوگ اس سے رہنمائی حاصل کریں');
INSERT INTO "sayyid_qutb" VALUES(23,50,'23:50','اور ابن مریم اور اس کی ماں کو ہم نے ایک نشانی اور ان کو سطح مرتفع پر رکھا و اطمینان کی جگہ تھی اور چشمے اس میں جاری تھے');
INSERT INTO "sayyid_qutb" VALUES(23,51,'23:51','اے پغمبر کھائو پاک چیزیں اور عمل کرو صالح ، تم جو کچھ بھی کرتے ہو ، میں اس کو خوب جانتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(23,52,'23:52','اور یہ تمہاری امت ایک ہی امت ہے اور میں تمہارا رب ہوں ، پس مجھی سے ڈرو');
INSERT INTO "sayyid_qutb" VALUES(23,53,'23:53','مگر بعد میں لوگوں نے اپنے دین کو آپس میں ٹکڑے ٹکڑے کرلیا۔ ہر گروہ کے پاس جو کچھ ہے اسی میں وہ مگن ہے');
INSERT INTO "sayyid_qutb" VALUES(23,54,'23:54','اچھا تو چھوڑو انہیں ، ڈوبے رہیں اپنی غفلت میں ایک وقت خاص تک');
INSERT INTO "sayyid_qutb" VALUES(23,55,'23:55','کیا یہ سمجھتے ہیں کہ ہم جو انہیں مال و اولاد سے مدد دیئے جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,56,'23:56','تو گویا انہیں بھلائیاں دینے میں سر گرم ہیں ؟ نہیں اصل معاملے کا انہیں شعور نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(23,57,'23:57','حقیقت میں جو لوگ اپنے رب کے خوف سے ڈرنے والے ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,58,'23:58','جو اپنے رب کی آیات پر ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,59,'23:59','جو اپنے رب کے ساتھ کسی کو شریک نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(23,60,'23:60','اور جن کا حال یہ ہے کہ دیتے ہیں جو کچھ بھی دیتے ہیں اور دل ان کے اس خیال کا نپتے رہتے ہیں کہ ہمیں اپنے رب کی طرف پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(23,61,'23:61','وہی بھلائیوں کی طرف دوڑنے والے اور سبقت کرکے انہیں پالنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,62,'23:62','ہم کسی شخص کو اس کی مقدرت سے زیادہ تکلیف نہیں دیتے ، اور ہمارے پاس ایک کتاب ہے (ہر ایک کا حال) ٹھیک ٹھیک بتا دینے والی ہے اور لوگوں پر ظلم بہر حال نہیں کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(23,63,'23:63','مگر یہ لوگ اس معاملے سے بیخبر ہیں اور ان کے اعمال بھی اس طریقے سے (جس کا اوپر ذکر کیا گیا ہے) مختلف ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,64,'23:64','(وہ اپنے یہ کرتوت کیے چلے جائیں گے) یہاں تک کہ جب ہم ان سے عیاشوں کو عذاب میں پکڑ لیں گے تو پھر وہ ڈگرانا شروع کردیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,65,'23:65','اب بند کرو اپنی فریاد دفغان ، ہماری طرف سے اب کوئی مدد تمہیں نہیں ملنی');
INSERT INTO "sayyid_qutb" VALUES(23,66,'23:66','میری آیات سنائی جاتی تھیں تو تم ( رسول کی آواز سنتے ہی) الٹے پائوں بھاگ نکلتے تھے');
INSERT INTO "sayyid_qutb" VALUES(23,67,'23:67','اپنے گھمنڈ میں اس کو خاطر ہی میں نہ لاتے تھے ، اپنی چو پالیوں میں اس پر باتیں چھانٹتے اور بکواس کیا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(23,68,'23:68','’ تو کیا ان لوگوں نے کبھی اس بات پر غور نہیں کیا ؟ “ یا وہ کوئی ایسی بات لایا ہے جو کبھی ان کے اسلاف کے پاس نہ آئی تھی ؟');
INSERT INTO "sayyid_qutb" VALUES(23,69,'23:69','یا یہ اپنے رسول سے کبھی واقف نہ تھے کہ اس بد کتے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(23,70,'23:70','یا یہ اس بات کے قائل ہیں کہ وہ مجنون ہے ؟ نہیں بلکہ وہ حق لایا ہے اور حق ہی ان کی اکثریت کو ناگوار ہے');
INSERT INTO "sayyid_qutb" VALUES(23,71,'23:71','اور حق اگر کہیں ان کی خواہشات کے پیچھے چلتا تو زمین اور آسمان اور ان کی ساری آبادی کا نظام درہم برہم ہوجاتا نہیں بلکہ ہم ان کا اپنا ہی ذکر ان کے پاس لائے ہیں اور وہ اپنے ذکر سے منہ موڑ رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,72,'23:72','کیا تو ان سے کچھ مانگ رہا ہے ؟ تیرے لیے تو تیرے رب کا دیا ہی بہتر ہے اور وہ بہترین رازق ہے');
INSERT INTO "sayyid_qutb" VALUES(23,73,'23:73','تو ان سیدھے راستے کی طرف بلا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(23,74,'23:74','مگر جو لوگ آخرت کو نہیں مانتے وہ راہ راست سے ہٹ کر چلنا چاہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,75,'23:75','اگر ہم ان پر رحم کریں اور تکلیف جس میں آج کل یہ مبتلا ہیں ، دور کردیں تو یہ اپنی سرکشی میں بالکل ہی بہک جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,76,'23:76','ان کا حال تو یہ ہے کہ ہم نے انہیں تکلیف میں مبتلا کیا ، پھر بھی یہ اپنے رب کے آگے نہ جھے اور نہ عاجزی اختیار کرے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,77,'23:77','البتہ جب نوبت یہاں تک پینچ جائے گی کہ ہم ان پر سخت عذاب کا دروازہ کھول دیں گے تو یکا یک تم دیکھو گے کہ اس حالت میں وہ ہر خیر سے مایوس ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,78,'23:78','وہ اللہ ہی تو ہے جس نے تمہیں سننے اور دیکھنے کی قوتیں دیں اور سوچنے کی دل مگر تم لوگ کم ہی شکر گزار ہوتے ہو');
INSERT INTO "sayyid_qutb" VALUES(23,79,'23:79','وہی ہے جس نے تمہیں زمین مین پھیلایا اور اسی طرف تم سمیٹے جائو گے');
INSERT INTO "sayyid_qutb" VALUES(23,80,'23:80','وہی زندگی بخشتا ہے اور وہی موت دیتا ہے۔ گردش لیل و نہار اسی کے قبضہ میں ہے۔ کیا تمہاری سمجھ میں یہ بات نہیں آتی ؟ “');
INSERT INTO "sayyid_qutb" VALUES(23,81,'23:81','مگر یہ لوگ وہی کچھ کہتے ہیں جو ان کے پیش رو کہ چکے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,82,'23:82','یہ کہتے ہیں ” کیا جب ہم مر کر مٹی ہوجائیں گے اور ہڈیوں کا پنجر بن کر رہ جائیں گے تو ہم کو پھر زندہ کرکے اٹھایاجائے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(23,83,'23:83','ہم نے بھی یہ وعدے بہت سنے ہیں اور ہم سے پہلے ہمارے باپ دادا بھی سنتے رہے ہیں ۔ یہ محض افسا نہائے پارینہ ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,84,'23:84','ان سے کہو ، بتائو ، اگر تم جانتے ہو کہ یہ زمین اور اس کی ساری آبادی کس کی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(23,85,'23:85','یہ ضرور کہیں گے اللہ کی۔ کہو پھر تم ہوش میں کیوں نہیں آتے ؟');
INSERT INTO "sayyid_qutb" VALUES(23,86,'23:86','ان سے پوچھو ، ساتوں آسمانوں اور عرش عظیم کا مالک کون ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(23,87,'23:87','وہ ضرور کہیں گے اللہ۔ کہو ، پھر تم ڈرتے کیوں نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(23,88,'23:88','ان سے کہو ، بتائو اگر تم جانتے ہو کہ ہر چیز پر اقتدار کس کا ہے ؟ اور کون ہے وہ جو پناہ دیتا ہے اور اس کے مقابلے میں کوئی پناہ نہیں دے سکتا ؟');
INSERT INTO "sayyid_qutb" VALUES(23,89,'23:89','یہ ضرور کہیں گے کہ یہ بات تو اللہ ہی کے لیے ہے ۔ کہو ، پھر کہاں سے تم کو دھوکہ لگتا ہے');
INSERT INTO "sayyid_qutb" VALUES(23,90,'23:90','(جو امر حق ہے وہ ہم ان کے سامنے لے آئے ہیں ، اور کوئی شک نہیں کہ یہ لوگ جھوٹے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,91,'23:91','اللہ نے کسی کو اپنی اولاد نہیں بنایا ہے ، اور کوئی دوسرا خدا اس کے ساتھ نہیں ہے ۔ اگر ایسا ہوتا تو ہر خدا اپنی مخلوق کو لے کر الگ ہوجاتا اور پھر وہ ایک دوسرے پر چڑھ دوڑتے۔ پاک ہے اللہ ان باتوں سے جو یہ لوگ بناتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,92,'23:92','دیکھے اور چھپے کا جاننے والا ، دوبالا تر ، ہے اس شرک سے جو یہ لوگ تجویز کررہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,93,'23:93','اے نبی دعا کرو کہ ” اے پروردگار ‘ جس عذاب کی ان کو دھمکی دی جارہی ہے وہ اگر میری موجود گی میں تو لائے');
INSERT INTO "sayyid_qutb" VALUES(23,94,'23:94','تو اے میرے رب مجھے ان ظالموں میں شامل نہ کیجیو');
INSERT INTO "sayyid_qutb" VALUES(23,95,'23:95','اور حقیقت یہ ہے کہ ہم تمہاری آنکھوں کے سامنے ہی وہ چیز لے آنے کی پوری قدرت رکھتے ہیں جس کی دھمکی ہم انہیں دے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,96,'23:96','اے نبی ؐ برائی کو اس طریقہ سے دفع کر جو بہترین ہو۔ جو کچھ باتیں وہ تم پر بناتے ہیں وہ ہمیں معلوم ہیں');
INSERT INTO "sayyid_qutb" VALUES(23,97,'23:97','اور دعا کرو کہ ــــ” پروردگار میں شیاطین کی اکساہٹوں سے تیری پناہ مانگتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(23,98,'23:98','بلکہ اے میرے رب میں تو اس بھی تیری پناہ مانگتا ہوں کہ وہ میرے پاس آئیں');
INSERT INTO "sayyid_qutb" VALUES(23,99,'23:99','(یہ لوگ اپنی کرنی سے باز نہ آئیں گے) یہاں تک کہ جب ان میں سے کسی کو موت آجائے گی تو کہنا شروع کرے گا کہ ” اے میرے رب ‘ مجھے اسی دنیا میں واپس بھیج دیجئے');
INSERT INTO "sayyid_qutb" VALUES(23,100,'23:100','جسے میں چھوڑ آیا ہوں ؟ امید ہے کہ اب میں نیک عمل کروں گا اب ان سب (مرنے والوں) کے پیچھے ایک برزخ حائل ہے دوسری زندگی کے دن تک');
INSERT INTO "sayyid_qutb" VALUES(23,101,'23:101','پھر جو نہی کہ صور بھونک دیا گیا ‘ ان کے درمیان پھر کوئی رشتہ نہ رہے گا اور نہ وہ ایک دوسرے کو پوچھیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,102,'23:102','اس وقت جن کے پلڑے بھاری ہوں گے وہی فلاح پائیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,103,'23:103','اور جن کے پلڑے ہلکے ہوں گے وہی لوگ ہوں گے جنہوں نے اپنے آپ کو گھاٹے میں ڈال لیا۔ وہ جہنم میں ہمیشہ رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,104,'23:104','آگ ان کے چہروں کی کھال چاٹ جائے گی اور ان کے جبڑے باہر نکل آئیں گے');
INSERT INTO "sayyid_qutb" VALUES(23,105,'23:105','یا تم وہی لوگ نہیں ہو کہ میری آیات تمہیں سنائی جاتی تھیں تو تم انہیں جھٹلاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(23,106,'23:106','وہ کہیں گے ‘” اے ہمارے رب ہماری بدبختی ہم پر چھا گئی تھی۔ ہم واقعی گمراہ لوگ تھے');
INSERT INTO "sayyid_qutb" VALUES(23,107,'23:107','اے پروردگار ‘ اب ہمیں یہاں سے نکال دے۔ پھر ہم ایسا قصور کریں تو ظالم ہوں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(23,108,'23:108','اللہ تعالیٰ جواب دے گا ” دور ہو میرے سامنے سے ‘ پڑے رہو اسی میں اور جھ سے بات نہ کرو');
INSERT INTO "sayyid_qutb" VALUES(23,109,'23:109','تم وہی لوگ تو ہو کہ میرے کچھ بندے جب کہتے تھے کہ اے ہمارے پروردگار ‘ ہم ایمان لائے ‘ ہمیں معاف کردے ‘ ہم پر رحم کر ‘ تو سب رحیموں سے اچھا رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(23,110,'23:110','تو تم نے ان کا مذاق بنا لیا۔ یہاں تک کہ ان کی ضد نے تمہیں یہ بھی بھلا دیا کہ میں بھی کوئی ہوں ‘ اور تم ان پر ہنستے رہے “۔');
INSERT INTO "sayyid_qutb" VALUES(23,111,'23:111','آج ان کے اس صبر کا میں نے یہ پھل دیا ہے کہ وہی کامیاب ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(23,112,'23:112','پھر اللہ تعالیٰ ان سے پوچھے گا ‘ بتائو زمین میں تم کتنے سال رہے');
INSERT INTO "sayyid_qutb" VALUES(23,113,'23:113','وہ کہیں گے ” ایک دن یا دن کا بھی کچھ حصہ ہم ٹھہرے ‘ شمار کرنے والوں سے پوچھ لیجئے');
INSERT INTO "sayyid_qutb" VALUES(23,114,'23:114','ارشاد ہوگا تھوڑی ہی دیر ٹھہرے ہونا ‘ کاش تم نے یہ اس وقت جانا ہوتا');
INSERT INTO "sayyid_qutb" VALUES(23,115,'23:115','کیا تم نے یہ سمجھ رکھا تھا کہ ہم نے تمہیں فضول ہی پیدا کیا ہے اور تمہیں ہماری طرف کبھی پلٹنا ہی نہیں ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(23,116,'23:116','پس بالا و برتر ہے اللہ ‘ پادشاہ حقیقی ‘ کوئی خدا اس کے سوا نہیں ‘ مالک ہے عرش بزرگ کا');
INSERT INTO "sayyid_qutb" VALUES(23,117,'23:117','اور جو کوئی اللہ کے ساتھ کسی اور معبود کو پکارے ‘ جس کے لیے اس کے پاس کوئی دلیل نہیں ‘ تو اس کا حساب اس کے رب کی پاس ہے۔ ایسے کافر کبھی فلاح نہیں پاسکتے');
INSERT INTO "sayyid_qutb" VALUES(23,118,'23:118','اے نبی صلى الله عليه وسلم کہو ‘ میرے رب درگزرفرما اور رحم کر ‘ تو سب رحیموں سے اچھا رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,1,'24:1','” یہ ایک سورة ہے جس کو ہم نے نازل کیا ہے ‘ اور اسے ہم نے فرض کیا ہے اور اس میں ہم نے صاف صاف ہدایات نازل کی ہیں شاید کہ تم سبق لو “۔');
INSERT INTO "sayyid_qutb" VALUES(24,2,'24:2','زانیہ عورت اور زانی مرد ‘ دونوں میں سے ہر ایک کو سوکوڑے مارو اور ان پر ترس کھانے کا جذبہ اللہ کے دین کے معاملے میں تم کو دامن گیر نہ ہو اگر تم اللہ تعالیٰ اور روز آخر پر ایمان رکھتے ہو۔ اور ان کو سزا دیتے وقت اہل ایمان کا ایک گروہ مود رہے۔');
INSERT INTO "sayyid_qutb" VALUES(24,3,'24:3','زانی نکاح نہ کرے مگر زانیہ کے ساتھ یا مشرکہ کے ساتھ اور زانیہ کے ساتھ نکاح نہ کرے مگر زانی یا مشرک اور یہ حرام کردیا گیا ہے اہل ایمان پر');
INSERT INTO "sayyid_qutb" VALUES(24,4,'24:4','” اور جو لوگ پاک دامن عورتوں پر تہمت لگائیں ‘ پھر چار گواہ لے کر نہ آئیں ‘ ان کو اسی (80) کوڑے مارو اور ان کی شہادت کبھی قبول نہ کرو ‘ اور وہ خود ہی فاسق ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,5,'24:5','سوائے ان لوگوں کے جو اس حرکت کے بعد تائب ہوجائیں اور اصلاح کرلیں تو اللہ ضرور ( ان کے حق میں) غفور ورحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,6,'24:6','اور جو لوگ اپنی بیویوں پر الزام لگائیں اور ان کے پاس خود ان کے اپنے سوا دوسرے کوئی گواہ نہ ہوں تو ان میں سے ایک شخص کی شہادت (یہ ہے کہ وہ) چار مرتبہ اللہ کی قسم کھا کر گواہی دے کہ وہ ( اپنے الزام) میں سچا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,7,'24:7','اور پانچویں بار کہے کہ اس پر اللہ کی لعنت ہو اگر وہ (اپنے الزمم میں جھوٹا ہو');
INSERT INTO "sayyid_qutb" VALUES(24,8,'24:8','اور عورت سے سزا اس طرح ٹل سکتی ہے کہ وہ چار مرتبہ اللہ کی قسم کھا کر شہادت دے کہ یہ شخص (اپنے الزام میں) جھوٹا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,9,'24:9','اور پانچویں مرتبہ کہے کہ اس بندے پر اللہ کا غضب ٹوٹے اگر وہ (اپنے الزام میں) سچا ہو');
INSERT INTO "sayyid_qutb" VALUES(24,10,'24:10','تم لوگوں پر اللہ کا فضل اور اس کا رحم نہ ہوتا اور یہ بات نہ ہوتی کہ اللہ بڑا اتفات فرمانے والا اور حکیم ہے تو ( بیویوں پر الزام کا معاملہ تمہیں بڑی پیچیدگی میں ڈال دیتا)');
INSERT INTO "sayyid_qutb" VALUES(24,11,'24:11','جو لوگ یہ بہتان گھڑ لائے ہیں وہ تمہارے ہی اندر کا ایک ٹولہ ہیں۔ اس واقعے کو اپنے حق میں شر نہ سمجھو بلکہ یہ بھی تمہارے لیے خیر ہی ہے جس نے اس میں جتنا حصہ لیا اس نے اتنا ہی گناہ سمیٹا اور جس شخص نے اس کی ذمہ داری کا بڑا حصہ اپنے سر لیا اس کے لیے تو عذاب عظیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,12,'24:12','جس وقت تم لوگوں نے اسے سنا تھا اسی وقت کیوں نہ مومن مردوں اور مومن عورتوں نے اپنے آپ سے نیک گمان کیا اور کیوں نہ کہہ دیا کہ یہ صریح بہتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(24,13,'24:13','وہ لوگ (اپنے الزام کے ثبوت میں) چار گواہ کیوں نہ لائے ؟ اب کہ وہ گواہ نہیں لائے ہیں ‘ اللہ کے نزدیک وہی جھوٹے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(24,14,'24:14','اگر تم لوگوں پر دنیا اور آخرت میں اللہ کا فضل اور رحم و کرم نہ ہوتا تو جن باتوں میں تم پڑگئے تھے ان کی پاداش میں بڑا عذاب تمہیں آلیتا');
INSERT INTO "sayyid_qutb" VALUES(24,15,'24:15','(ذرا غور تو کرو ‘ اس وقت تم کیسی سخت غلطی کررہے تھے) جبکہ تمہاری ایک زبان سے دوسری زبان اس جھوٹ کو لیتی چلی جارہی تھی اور تم اپنے منہ سے وہ کچھ کہے جارہے تھے جس کے متعلق تمہیں کوئی علم نہ تھا۔ تم اسے ایک معمولی بات سمجھ رہے تھے ‘ حالانکہ اللہ کے نزدیک یہ بڑی بات تھی۔');
INSERT INTO "sayyid_qutb" VALUES(24,16,'24:16','کیوں نہ اسے سنتے ہی تم نے کہہ دیا کہ ” ہمیں ایسی بات زبان سے نکالنا زیب نہیں دیتا ‘ سبحان اللہ ‘ یہ تو ایک بہتان عظیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(24,17,'24:17','اللہ تم کو نصیحت کرتا ہے کہ آئندہ کبھی ایسی حرکت نہ کرنا اگر تم مومن ہو');
INSERT INTO "sayyid_qutb" VALUES(24,18,'24:18','اللہ تمہیں صاف صاف ہدایات دیتا ہے اور وہ علیم و حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,19,'24:19','جو لوگ چاہتے ہیں کہ ایمان لانے والوں کے گروہ میں فحش پھیلے وہ دنیا اور آخرت میں درد ناک سزا کے مستحق ہیں ‘ اللہ جانتا ہے اور تم نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(24,20,'24:20','اگر اللہ کا فضل اور اس کا رحم و کرم تم پر نہ ہوتا اور یہ بات نہ ہوتی کہ اللہ بڑا شفیق و رحیم ہے ‘( تو یہ چیز جو ابھی تمہارے اندر پھیلائی گئی تھی ‘ بدترین نتائج دکھا دیتی)');
INSERT INTO "sayyid_qutb" VALUES(24,21,'24:21','اے لوگو ‘ جو ایمان لائے ہو ‘ شیطان کے نقش قدم پر نہ چلو۔ اس کی پیروی کوئی کرے گا تو وہ تو اسے فحش اور بدی ہی کا حکم دے گا اگر اللہ کا فضل اور اس کا رحم و کرم تم پر نہ ہوتا تو تم میں سے کوئی شخص پاک نہ ہوسکتا مگر اللہ ہی جسے چاہتا ہے پاک کردیتا ہے اور اللہ سننے والا اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,22,'24:22','تم میں سے جو لوگ صاحب فضل اور صاحب مقدرت ہیں وہ اس بات کی قسم نہ کھا بیٹھیں کہ اپنے رشتہ دار ‘ مسکین اور مہاجر فی سبیل اللہ لوگوں کی مدد نہ کریں گے۔ انہیں معاف کردینا چاہیے اور درگزر کرنا چاہیے۔ کیا تم نہیں چاہتے کہ اللہ تمہیں معاف کرے ؟ اور اللہ کی صفت یہ ہے کہ وہ غفور اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(24,23,'24:23','جو لوگ پاک دامن ‘ بیخبر ‘ مومن عورتوں پر تہمتیں لگاتے ہیں ان پر دنیا اور آخرت میں لعنت کی گئی اور ان کے لیے بڑا عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(24,24,'24:24','وہ اس دن کو بھول نہ جائیں جبکہ ان کی اپنی زبانیں اور ان کے اپنے ہاتھ پائوں ان کے گرتوتوں کی گواہی دیں گے');
INSERT INTO "sayyid_qutb" VALUES(24,25,'24:25','اس دن اللہ وہ بدلہ انہیں پھر پور دے دے گا جس کے وہ مستحق ہیں اور انہیں معلوم ہوجائے گا کہ اللہ ہی حق ہے سچ کو سچ کر دکھانے والا');
INSERT INTO "sayyid_qutb" VALUES(24,26,'24:26','خبیث عورتیں خبیث مردوں کے لیے ہیں اور خبیث مرد خبیث عورتوں کے لیے۔ پاکیزہ عورتیں پاکیزہمردوں کے لیے ہیں اور پاکیزہ مرد پاکیزہ عورتوں کے لیے۔ ان کا دامن پاک ہے ان باتوں سے جو بنانے والے بناتے ہیں ‘ ان کے لیے مغفرت ہے اور رزق کریم');
INSERT INTO "sayyid_qutb" VALUES(24,27,'24:27','اے لوگو جو ایمان لائے ہو ‘ اپنے گھروں کے سوا دوسرے گھروں میں داخل نہ ہوا کرو جب تک کہ گھر والوں کی رضانہ لے لو اور گھر والوں پر سلام نہ بھیج لو ‘ یہ طریقہ تمہارے لیے بہتر ہے۔ توقع ہے کہ تم اس کا خیال رکھو گے');
INSERT INTO "sayyid_qutb" VALUES(24,28,'24:28','پھر اگر وہاں کسی کو نہ پائو گے تو داخل نہ ہو جب تک کہ تم کو اجازت نہ دے دی جائے ‘ اور اگر تم سے کہا جائے کہ واپس چلے جائو تو واپس ہو جائو ‘ یہ تمہارے لیے زیادہ پاکیزہ طریقہ ہے اور جو کچھ تم کرتے ہو ‘ اللہ اسے خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(24,29,'24:29','البتہ تمہارے لیے اس میں کوئی مضائقہ نہیں ہے کہ ایسے گھروں میں داخل ہو جائو جو کسی کے رہنے کی جگہ نہ ہوں اور جن میں تمہارے فائدے (یاکام) کی کوئی چیز ہو۔ تم جو کچھ ظاہر کرتے ہو اور جو کچھ چھپاتے ہو ‘ سب کی اللہ کو خبر ہے');
INSERT INTO "sayyid_qutb" VALUES(24,30,'24:30','اے نبی صلى الله عليه وسلم مومن مردوں سے کہو کہ اپنی نظریں بچا کر رکھیں اور اپنی شرمگاہوں کی حفاظت کریں ‘ یہ ان کے لیے زیادہ پاکیزہ طریقہ ہے ‘ جو کچھ وہ کرتے ہیں اللہ اس سے با خبر رہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,31,'24:31','اور اے نبی صلى الله عليه وسلم ‘ مومن عورتوں سے کہہ دو کہ اپنی نظریں بچا کر رکھیں ‘ اور اپنی شرم گاہوں کی حفاظت کریں ‘ اور اپنا بنائو سنگھار نہ دکھائیں۔ بجز اس کے جو خود ظاہر ہوجائے ‘ اور اپنے سینوں پر اپنی اوڑھنیوں کے آنچل ڈالے رہیں۔ وہ اپنا بنائو سنگھار ظاہر کریں مگر ان لوگوں کے سامنے : شوہر ‘ باپ ‘ شوہروں کے باپ ‘ اپنے بیٹے ‘ شوہروں کے بیٹے ‘ بھائی ‘ بھائیوں کے بیٹے ‘ بہنوں کے بیٹے ‘ اپنے میل جول کی عورتیں ‘ اپنے لونڈی غلام ‘ وہ زیر دست مرد جو کسی اور قسم کی غرض نہ رکھتے ہوں ‘ اور وہ بچے جو عورتوں کی پوشیدہ باتوں سے ابھی واقف نہ ہوئے ہوں۔ وہ اپنے پائوں زمین پر مارتی ہوئی نہ چلا کریں کہ اپنی جو زینت انہوں نے چھپارکھی ہو ‘ اس کا لوگوں کو علم ہوجائے۔ اے مومنو ‘ تم سب مل کر اللہ سے توبہ کرو ‘ توقع ہے کہ فلاح پائو گے “۔');
INSERT INTO "sayyid_qutb" VALUES(24,32,'24:32','تم میں سے جو لوگ مجرو ہوں اور تمہارے لونڈی غلاموں میں سے جو صالح ہوں ‘ ان کے نکاح کر دو ۔ اگر وہ غریب ہوں تو اللہ اپنے فضل سے ان کو غنی کر دے گا ‘ اللہ بڑی وسعت والا اور علیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,33,'24:33','اور جو نکاح کا موقع نہ پائیں انہیں چاہیے کہ عفت مآبی اختیار کریں ‘ یہاں تک کہ اللہ اپنے فضل سے ان کو غنی کر دے۔ اور تمہارے مملوکوں میں سے جو مکاتبت کی درخواست کریں ان سے مکاتبت کرلو اگر تمہیں معلوم ہو کہ ان کے اندر بھلائی ہے اور ان کو اس مال میں سے دو جو اللہ نے تمہیں دیا ہے۔ اور اپنی لونڈیوں کو اپنے دنیوی فائدوں کی خاطر قحبہ گری پر مجبور نہ کرو جبکہ وہ خود پاک دامن رہنا چاہتی ہوں اور جو کوئی ان کو مجبور کرے تو اس جبر کے بعد اللہ ان کے لیے غفورو رحیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(24,34,'24:34','ہم نے صاف صاف ہدایت دینے والی آیات تمہارے پاس بھیج دی ہیں ‘ اور ان قوموں کی عبرتناک مثالیں بھی ہم تمہارے سامنے پیش کرچکے ہیں جو تم سے پہلے ہو گزری ہیں اور وہ نصیحتیں ہم نے کردی ہیں جو ڈرنے والوں کے لیے ہوتی ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,35,'24:35','اللہ آسمانوں اور زمین کا نور ہے۔ (کائنات میں) اس کے نور کی مثال ایسی ہے جیسے ایک طاق میں چراغ رکھا ہوا ہو ‘ چراغ ایک فانوس میں ہو ‘ فانوس کا حال یہ ہو کہ جیسے موتی کی طرح چمکتا ہوا تارا ‘ اور وہ چراغ زیتون کے ایک ایسے مبارک درخت کے تیل سے روشن کیا جاتا ہو جو نہ شرقی ہو نہ غربی ‘ جس کا تیل آپ ہی آپ بھڑکا پڑتا ہو۔ چاہے آگ ہیں کو نہ لگے ‘(اس طرح) روشنی پر روشنی (بڑھنے کے تمام اسباب جمع ہوگئے ہوں) ۔ اللہ اپنے نور کی طرف جس کی چاہتا ہے ‘ رہنمائی فرماتا ہے ‘ وہ لوگوں کو مثالوں سے بات سمجھاتا ہے ‘ وہ ہر چیز سے خوب واقف ہے');
INSERT INTO "sayyid_qutb" VALUES(24,36,'24:36','اس کے نور کی طرف (ہدایت پانے والے) ان گھروں میں پائے جاتے ہیں جنہیں بلند کرنے کا ‘ اور جن میں اپنے نام کی یاد کا اللہ نے اذن دیا ہے۔ ان میں ایسے لوگ صبح و شام اس کی تسبیح کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,37,'24:37','جنہیں تجارت اور خریدو فروخت اللہ کی یاد سے اور اقامت نماز و ادائے زکوۃ سے غافل نہیں کردیتی۔ وہ اس دن سے ڈرتے رہتے ہیں جس میں دل الٹنے اور دیدے پتھراجانے کی نوبت آجائے گی');
INSERT INTO "sayyid_qutb" VALUES(24,38,'24:38','( اور وہ یہ سب کچھ اس لیے کرتے ہیں) تاکہ اللہ ان کے بہترین اعمال کی جزا ان کو دے اور مزید اپنے فضل سے نوازے ‘ اللہ جسے چاہتا ہے بےحساب دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,39,'24:39','(والذین کفروا۔۔۔۔۔۔۔۔ واللہ سریع الحساب اوکظلمت۔۔۔۔۔۔۔۔ من نور (39 : 40) ”(اس کے برعکس) جنہوں نے کفر کیا ان کے اعمال کی مثال ایسی ہے جیسے دشت بےآب میں سراب کہ پیاسا اس کو پانی سمجھے ہوئے تھا ‘ مگر جب وہاں پہنچا تو کچھ نہ پایا ‘ بلکہ وہاں اس نے اللہ کو موجود پایا ‘ جس نے اس کا پورا پورا حساب چکا دیا ‘ اور اللہ کو حساب لیتے دیر نہیں لگتی۔');
INSERT INTO "sayyid_qutb" VALUES(24,40,'24:40','یا پھر اس کی مثال ایسی ہے جیسے ایک گہرے سمندر میں اندھیرا ‘ کہ اوپر ایک موج چھائی ہوئی ہے ‘ اس پر ایک اور موج ‘ اور اس کے اوپر بادل ‘ تاریکی پر تاریکی مسلط ہے ‘ آدمی اپنا ہاتھ نکالے تو اسے بھی نہ دیکھنے پائے جسے اللہ نور نہ بخشے اس کے لیے پھر کوئی نور نہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(24,41,'24:41','کیا تم دیکھتے نہیں ہو کہ اللہ کی تسبیح کررہے ہیں ‘ وہ سب جو آسمانوں اور زمین میں ہیں اور وہ پرندے جو پر پھیلائے اڑ رہے ہیں ؟ ہر ایک اپنی نماز اور تسبیح کا طریقہ جانتا ہے ‘ اور یہ سب جو کچھ کرتے ہیں اللہ اس سے با خبر رہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,42,'24:42','آسمانوں اور زمین کی بادشاہی اللہ ہی کے لیے ہے اور اسی کی طرف سب کو پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,43,'24:43','وللہ ملک السموت والارض ج والی اللہ المصیر ” “۔ لہذا ہمارا فرض ہے کہ ہم اپنی تمام تر تو جہات اس کی طرف کردیں۔ اس کے سوا اور کوئی جائے پناہ نہیں ہے۔ اس سے بھاگنے کی کوئی جگہ نہیں ہے۔ اس کے عذاب سے بچنے کی کوئی جگہ نہیں ہے۔ اس کے مقابلے میں کوئی پناہ نہیں ہے اور سب نے اسی کی طرف جانا ہے۔ اب اس کائنات کے مناظر میں سے ایک دوسرا منظر۔ لوگ رات اور دن اس منظر کو دیکھتے ہیں اور غفلت کے ساتھ گزر جاتے ہیں ‘ حالانکہ یہ منظر دامن نظر کو پکڑ پکڑ کر کھینچتا ہے کہ ذرا دیکھو تو سہی ‘ دل میں سوچو تو سہی ‘ اللہ کی نشانیوں اور اس کی تخلیقات میں غور تو کرو۔ دلائل نور اور دلائل ہدایت پر غور تو کرو۔');
INSERT INTO "sayyid_qutb" VALUES(24,44,'24:44','(الم تر ان اللہ۔۔۔۔۔۔۔ بالابصار) (43) ” کیا تم دیکھتے نہیں ہو کہ اللہ بادل کو آہستہ آہستہ چلاتا ہے ‘ پھر اس کے ٹکڑوں کو باہم جوڑتا ہے ‘ پھر اسے سمیٹ کر ایک کثیف ابر بنا دیتا ہے ‘ پھر تم دیکھتے ہو کہ اس کے خول میں سے بارش کے قطرے ٹپکتے چلے آتے ہیں اور وہ آسمان سے ‘ ان پہاڑروں کی بدولت جو اس میں بلند ہیں اولے برساتا ہے ‘ پھر جسے چاہتا ہے ان کا نقصان پہنچتا ہے اور جسے چاہتا ہے ان سے بچا لیتا ہے۔ اس کی بجلی کی چمک نگاہوں کو خیرہ کیے دیتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(24,45,'24:45','اور اللہ نے ہر جاندار ایک طرح کے پانی سے پیدا کیا۔ کوئی پیٹ کے بل چل رہا ہے تو کوئی دو ٹانگوں پر اور کوئی چار ٹانگوں پر۔ جو کچھ وہ چاہتا ہے پیدا کرتا ہے ‘ وہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(24,46,'24:46','” ہم نے صاف صاف حقیقت بتانے والی آیات نازل کردی ہیں۔ آگے صراط مستقیم کی طرف ہدایت اللہ ہی جسے چاہتا ہے دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,47,'24:47','یہ لوگ کہتے ہیں کہ ہم ایمان لائے اللہ اور رسول صلى الله عليه وسلم پر اور ہم نے اطاعت کی ‘ مگر اس کے بعد ان میں سے ایک گروہ (اطاعت سے ) منہ موڑ جاتا ہے۔ ایسے لوگ ہر گز مومن نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,48,'24:48','جب ان کو بلایا جاتا ہے اللہ اور رسول صلى الله عليه وسلم کی طرف تاکہ رسول صلى الله عليه وسلم ان کے آپس کے مقدمے کا فیصلہ کرے تو ان میں سے ایک فریق کترا جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,49,'24:49','البتہ اگر حق ان کی موافقت میں ہو تو رسول صلى الله عليه وسلم کے پاس بڑے اطاعت کیش بنکر آجاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(24,50,'24:50','کیا ان کے دلوں کو (منافقت کا ) روگ لگا ہوا ہے ؟ یا یہ شک میں پڑے ہوئے ہیں ؟ یا ان کو یہ خوف ہے کہ اللہ اور اس کا رسول صلى الله عليه وسلم ان پر ظلم کرے گا ؟ اصل بات یہ ہے کہ ظالم تو یہ لوگ خود ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,51,'24:51','ایمان لانے والوں کا کام تو یہ ہے کہ جب وہ اللہ اور رسول صلى الله عليه وسلم کی طرف بلائے جائیں تاکہ رسول صلى الله عليه وسلم ان کے مقدمے کا فیصلہ کرے تو وہ کہیں کہ ہم نے سنا اور اطاعت کی۔ ایسے ہی لوگ فلاح پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,52,'24:52','اور کامیاب وہی ہیں جو اللہ اور رسول صلى الله عليه وسلم کی فرماں برداری کریں اور اللہ سے ڈریں اور اس کی نافرمانی سے بچیں۔ یہی لوگ کامیاب ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,53,'24:53','یہ (منافق) اللہ کے نام سے کڑی کڑی قسمیں کھاکر کہتے ہیں کہ ” آپ حکم دیں تو ہم گھروں سے نکل کھڑے ہوں “۔ ان سے کہو ” قسمیں نہ کھائو ‘ تمہاری اطاعت کا حال معلوم ہے۔ تمہارے کرتوتوں سے اللہ بیخبر نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(24,54,'24:54','“۔ کہو ” اللہ کے مطیع بنو اور رسول صلى الله عليه وسلم کے تابع فرمان بن کر رہو۔ لیکن اگر تم منہ پھیرتے ہو تو خوب سمجھ لو کہ رسول صلى الله عليه وسلم پر جس فرض کا بار رکھا گیا ہے اس کا ذمہ دار ہو ہے اور تم پر جس فرض کا بار ڈالا گیا ہے اس کے ذمہ دار تم ۔ اس کی اطاعت کرو گے تو خود ہی ہدایت پائو گے ورنہ رسول صلى الله عليه وسلم کی ذمہ داری اس سے زیادہ کچھ نہیں ہے کہ صاف صاف حکم پہنچا دے “۔');
INSERT INTO "sayyid_qutb" VALUES(24,55,'24:55','اس نے وعدہ فرمایا ہے تم میں سے ان لوگوں کے ساتھ جو ایمان لائیں اور نیک عمل کریں کہ وہ ان کو اسی طرح زمین میں خلیفہ بنائے گا جس طرح ان سے پہلے گزرے ہوئے لوگوں کو بنا چکا ہے ‘ ان کے لیے ان کے اس دین کو مضبوط بنیادوں پر قائم کر دے گا جسے اللہ تعالیٰ نے ان کے حق میں پسند کیا ہے ‘ اور ان کی (موجودہ ) حالت خوف کو امن سے بدل دے گا ‘ بس وہ میری بندگی کریں اور میرے ساتھ کسی کو شریک نہ کریں اور جو اس کے بعد کفر کرے تو ایسے ہی لوگ فاسق ہیں');
INSERT INTO "sayyid_qutb" VALUES(24,56,'24:56','نماز قائم کرو ‘ زکوۃ دو اور رسول صلى الله عليه وسلم کی اطاعت کرو ‘ امید ہے کہ تم پر رحم کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(24,57,'24:57','جو لوگ کفر کررہے ہیں ان کے متعلق اس غلط فہمی میں نہ رہو کہ وہ زمین میں اللہ کو عاجز کردیں گے۔ ان کا ٹھکانا دوزخ ہے اور وہ بڑا ہی برا ٹھکانا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,58,'24:58','اے لوگو جو ایمان لائے ہو ‘ لازم ہے کہ تمہارے لونڈی غلام اور تمہارے وہ بچے جو ابھی عقل کی حد کو نہیں پہنچے ہیں ‘ تین اوقات میں اجازت لے کر تمہارے پاس آیا کریں : صبح کی نماز سے پہلے اور دوپہر کو جب کہ تم کپڑے اتار کر رکھ دیتے ہو ‘ اور عشاء کی نماز کے عبد۔ یہ تین وقت تمہارے لیے پردے کے وقت ہیں۔ ان کے بعد وہ بلا اجازت آئیں تو نہ تم پر کوئی گناہ ہے ‘ نہ ان پر۔ تمہیں ایک دوسرے کے پاس بار بار آناہی ہوتا ہے ‘ اس طرح اللہ تمہارے لیے اپنے ارشادات کی توضیح کرتا ہے ‘ اور وہ علیم و حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,59,'24:59','اور جب تمہارے بچے عقل کی حد کو پہنچ جائیں تو چاہیے کہ اسی طرح اجازت لے کر آیا کریں جس طرح ان کے بڑے اجازت لیتے رہے ہیں ‘ اس طرح اللہ اپنی آیات تمہارے سامنے کھولتا ہے اور وہ علیم و حکیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(24,60,'24:60','” اور جو عورتیں جوانی سے گزری بیٹھی ہوں ‘ نکاح کی امید دار نہ ہوں ‘ وہ اگر اپنی چادریں اتار کر رکھ دیں تو ان پر کوئی گناہ نہیں ‘ بشرطیکہ زینت کی نمائش کرنے والی نہ ہوں۔ تاہم وہ بھی حیا داری ہی برتیں تو ان کے حق میں اچھا ہے اور اللہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(24,61,'24:61','کوئی حرج نہیں اگر کوئی اندھا ‘ یا لنگڑا یا مریض (کسی کے گھر سے کھالے ) اور نہ تمہارے اوپر اس میں کوئی مضائقہ ہے کہ اپنے گھروں سے کھائو یا اپنے باپ دادا کے گھروں سے ‘ یا اپنی ماں نانی کے گھروں سے ‘ یا اپنے بھائیوں کے گھروں سے ‘ یا اپنی بہنوں کے گھروں سے ‘ یا اپنے چچائوں کے گھروں سے ‘ یا اپنی پھوپھیوں کے گھروں سے ‘ یا اپنے ماموئوں کے گھروں سے ‘ یا اپنی خالائوں کے گھروں سے ‘ یا ان گھروں سے جن کی کنجیاں تمہاری سپرد گی میں ہوں ‘ یا اپنے دوستوں کے گھروں سے۔ اس میں بھی کوئی حرج نہیں کہ تم لوگ مل کر کھائو یا الگ الگ۔ البتہ جب گھروں میں داخل ہوا کرو تو اپنے لوگوں کو سلام کیا کرو ‘ دعائے خیر ‘ اللہ کی طرف سے مقرر فرمائی ہوئی ‘ بڑی بابرکت اور پاکیزہ۔ اس طرح اللہ تعالیٰ تمہارے سامنے آیات بیان کرتا ہے ‘ توقع ہے کہ تم سمجھ بوجھ سے کام لوگے “');
INSERT INTO "sayyid_qutb" VALUES(24,62,'24:62','مومن تو اصل میں وہی ہیں جو اللہ اور اس کے رسول صلى الله عليه وسلم کو دل سے مانیں اور جب کسی اجتماعی کام کے موقع پر رسول صلى الله عليه وسلم کے ساتھ ہوں۔ تو اس سے اجازت لیے بغیر نہ جائیں۔ اے نبی صلى الله عليه وسلم ‘ جو لوگ تم سے اجازت مانگتے ہیں وہی اللہ اور رسول صلى الله عليه وسلم کے ماننے والے ہیں ‘ پس جب وہ اپنے کسی کام سے اجازت مانگیں تو جسے تو چاہو ‘ اجازت دے دیا کرو۔ اور ایسے لوگوں کے حق میں اللہ سے دعائے مغفرت کیا کرو ‘ اللہ یقیناً غفورورحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(24,63,'24:63','مسلمانو ‘ اپنے درمیان رسول کے بلانے کو آپس میں ایک دوسرے کا سابلانا نہ سمجھ بیٹھو۔ اللہ ان لوگوں کو خوب جانتا ہے جو تم میں ایسے ہیں کہ ایک دوسرے کی آڑ لیتے ہوئے چپکے سے سٹک جاتے ہیں۔ رسول صلى الله عليه وسلم کے حکم کی خلاف ورزی کرنے والوں کو ڈرنا چاہیے کہ وہ کسی فتنے میں گرفتار نہ ہوجائیں یا ان پر دردناک عذاب نہ آجائے');
INSERT INTO "sayyid_qutb" VALUES(24,64,'24:64','خبر دار رہو ‘ آسمان و زمین میں جو کچھ ہے اللہ کا ہے۔ تم جس روش پر بھی ہو اللہ اس کو جانتا ہے۔ جس روز لوگ اس کی طرف پلٹائے جائیں گے وہ انہیں بتا دے گا کہ وہ کیا کچھ کر کے آئے ہیں۔ وہ ہر چیز کا علم رکھتا ہے ‘');
INSERT INTO "sayyid_qutb" VALUES(25,1,'25:1','نہایت متبرک ہے وہ جس نے یہ فرقان اپنے بندے پر نازل کیا تاکہ سارے جہان والوں کے لیے خبردار کردینے والاہو');
INSERT INTO "sayyid_qutb" VALUES(25,2,'25:2','وہ جو زمین اور آسمانوں کی بادشاہی کا مالک ہے ‘ جس نے کسی کو بیٹا نہیں بنایا ہے ‘ جس کے ساتھ بادشاہی میں کوئی شریک نہیں ہے ‘ جس نے ہر چیز کو پیدا کیا پھر اس کی ایک تقدیر مقرر کی');
INSERT INTO "sayyid_qutb" VALUES(25,3,'25:3','لوگوں نے اسے چھوڑ کر ایسے معبود بنا لیے جو کسی چیز کو پیدا نہیں کرتے بلکہ خود پیدا کیے جاتے ہیں ‘ جو خود اپنے لیے بھی کسی نفع یا نقصان کا اختیار نہیں رکھتے جو نہ مار سکتے ہیں ‘ نہ جلا سکتے ہیں ‘ نہ مرے ہوئے کو پھر اٹھا سکتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(25,4,'25:4','جن لوگوں نے نبی صلى الله عليه وسلم کی بات ماننے سے انکار کردیا ہے وہ کہتے ہیں کہ ” یہ فرقان ایک من گھڑت چیز ہے جسے اس شخص نے آپ ہی گھڑ لیا ہے اور کچھ دوسرے لوگوں نے اس کام میں اس کی مدد کی ہے “۔ بڑا ظلم اور سخت جھوٹ ہے');
INSERT INTO "sayyid_qutb" VALUES(25,5,'25:5','جس پر یہ لوگ اتر آئے ہیں۔ کہتے ہیں ” یہ پرانے لوگوں کی لکھی ہوئی چیزیں ہیں جنہیں یہ شخص نقل کراتا ہے اور وہ اسے صبح و شام سنائی جاتی ہیں');
INSERT INTO "sayyid_qutb" VALUES(25,6,'25:6','اے نبی صلى الله عليه وسلم ان سے کہو کہ اسے نازل کیا ہے ‘ اس نے جو زمین اور آسمانوں کا بھید جانتا ہے “۔ حقیقت یہ ہے کہ وہ بڑا غفورو رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(25,7,'25:7','” کہتے ہیں ” یہ کیسا رسول ہے جو کھانا کھاتا ہے اور بازاروں میں چلتا پھرتا ہے ؟ کیوں نہ اس کے پاس کوئی فرشتہ بھیجا گیا جو اس کے ساتھ رہتا اور (نہ ماننے والوں کو ) دھمکاتا ؟');
INSERT INTO "sayyid_qutb" VALUES(25,8,'25:8','یا اور کچھ نہیں تو اس کے لیے کوئی خزانہ ہی اتار دیا جاتا ‘ یا اس کے پاس کوئی باغ ہی ہوتا جس سے یہ (اطمینان کی ) روزی حاصل کرتا ‘ اور یہ ظالم کہتے ہیں ” تم لوگ تو ایک سحرزدہ آدمی کے پیچھے لگ گئے ہو');
INSERT INTO "sayyid_qutb" VALUES(25,9,'25:9','دیکھو کیسی کیسی حجتیں یہ لوگ تمہارے آگے پیش کررہے ہیں ‘ ایسے بہکے ہیں کہ کوئی ٹھکانے کی بات ان کو نہیں سوجھتی');
INSERT INTO "sayyid_qutb" VALUES(25,10,'25:10','بڑا بابرکت ہے وہ جو اگر چاہے تو ان کی تجویز کردہ چیزوں سے بھی زیادہ بڑھ چڑھ کر تم کو دے سکتا ہے ‘(ایک نہیں) بہت سے باغ جن کے نیچے نہریں بہتی ہیں اور بڑے بڑے محل');
INSERT INTO "sayyid_qutb" VALUES(25,11,'25:11','” اصل بات یہ ہے کہ یہ لوگ ” اس گھڑی “ کو جھٹلا چکے ہیں۔۔۔ اور جو اس گھڑی کو جھٹلائے اس کے لیے ہم نے بھڑکتی ہوئی آگ مہیا کر رکھی ہے');
INSERT INTO "sayyid_qutb" VALUES(25,12,'25:12','وہ جب دور سے ان کو دیکھے گی تو یہ اس کے غضب اور جوش کی آوازیں سن لیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,13,'25:13','اور جب یہ دست و پابستہ اس میں ایک تنگ جگہ ٹھونسے جائیں گے تو اپنی موت کو پکارنے لگیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,14,'25:14','(اس وقت ان سے کہا جائے گا) آج ایک موت کو نہیں بہت سی موتوں کو پکارو');
INSERT INTO "sayyid_qutb" VALUES(25,15,'25:15','ان سے پوچھو یہ انجام اچھا ہے یا وہ ابدی جنت جس کا وعدہ خدا ترس پرہیز گاروں سے کیا گیا ہے ‘ جو ان کے عمل کی جزا اور ان کے سفر کی آخری منزل ہو گی');
INSERT INTO "sayyid_qutb" VALUES(25,16,'25:16','جس میں ان کی پر خواہش پوری ہوگی ‘ جس میں وہ ہمیشہ ہمیشہ رہیں گے ‘ جس کا عطا کرنا تمہارے رب کے ذمے ایک واجب الادا وعدہ ہے');
INSERT INTO "sayyid_qutb" VALUES(25,17,'25:17','اور وہی دن ہو گا جب کہ (تمہارا رب) ان لوگوں کو بھی گھیر لائے گا اور ان کے ان معبودوں کو بھی بلالے گا جنہیں آج یہ اللہ کو چھوڑ کر پوج رہے ہیں ‘ پھر وہ ان سے پوچھے گا ” کیا تم نے میرے ان بندوں کو گمراہ کیا تھا ؟ یا یہ خود راہ راست سے بھٹک گئے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(25,18,'25:18','“ وہ عرض کریں گے ” پاک ہے آپ کی ذلت ‘ ہماری تو یہ بھی مجال نہ تھی کہ آپ کے سوا کسی کو پنا مولیٰ بنائیں۔ مگر آپ نے ان کو اور ان کے باپ دادا کو خوب سامان زندگی دیاحتیٰ کہ یہ سبق بھول گئے اور شامت زدہ ہو کر رہے');
INSERT INTO "sayyid_qutb" VALUES(25,19,'25:19','یوں جھٹلا دیں گے وہ (تمہارے معبود) تمہاری ان باتوں کو جو آج تم کہہ رہے ہو ‘ پھر تم نہ اپنی شامت کر ٹال سکو گے نہ کہیں سے مدد پاسکو گے اور جو بھی تم میں سے ظلم کرے اسے ہم سخت عذاب کا مزا چکھائیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,20,'25:20','اے نبی ‘ تم سے پہلے جو رسول بھی ہم نے بھیجے تھے وہ سب بھی کھانا کھانے والے اور بازاروں میں چلنے پھرنے والے لوگ ہی تھے۔ دراصل ہم نے تم لوگوں کو ایک دوسرے کے لیے آزمائش کا ذریعہ بنا دیا ہے۔ کیا تم صبر کرتے ہو ؟ تمہارا رب سب کچھ دیکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,21,'25:21','جو لوگ ہمارے حضور پیش ہونے کا اندیشہ نہیں رکھتے وہ کہتے ہیں ” کیوں نہ فرشتے ہمارے پاس بھیجے جائیں ؟ یا پھر ہم اپنے رب کو دیکھیں “۔ پڑاگھمنڈ لے بیٹھے یہ اپنے نفس میں اور حد سے گزر گئے اپنی سرکشی میں');
INSERT INTO "sayyid_qutb" VALUES(25,22,'25:22','جس روز یہ فرشتوں کو دیکھیں گے وہ مجرموں کے لیے کسی بشارت کا دن نہ ہوگا۔ چیخ اٹھیں گے کہ پناہ بخدا');
INSERT INTO "sayyid_qutb" VALUES(25,23,'25:23','اور جو کچھ بھی ان کا کیا دھرا ہے اسے لے کر ہم غبار کی طرح اڑا دیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,24,'25:24','بس وہی لوگ جو جنت کے مستحق ہیں اس دن اچھی جگہ ٹھہریں گے اور دوپر گزارنے کو عمدہ مقام پائیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,25,'25:25','آسمان کو چیرتا ہوا ایک بادل اس روز نمودار ہوگا اور فرشتوں کے پرے کے پرے اتار دیئے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(25,26,'25:26','اس روز حقیقی بادشاہی صرف رحمٰن کی ہوگی اور وہ منکرین کے لیے بڑا سخت دن ہو گا');
INSERT INTO "sayyid_qutb" VALUES(25,27,'25:27','ظالم انسان اپنے ہاتھ چبائے گا اور کہے گا ” کاش میں نے رسول کا ساتھ دیا ہوتا');
INSERT INTO "sayyid_qutb" VALUES(25,28,'25:28','ہائے میری کم بختی ‘ کاش میں نے فلاں شخص کو دوست نہ بنایا ہوتا');
INSERT INTO "sayyid_qutb" VALUES(25,29,'25:29','اس کے بہکائے میں آکر میں نے وہ نصیحت نہ مانی جو میرے پاس آئی تھی ‘ شیطان انسان کے حق میں بڑا ہی بےوفا نکلا');
INSERT INTO "sayyid_qutb" VALUES(25,30,'25:30','اور رسول کہے گا کہ اے میرے رب ‘ میری قوم کے لوگوں نے اس قرآن کو نشانہ تصحیک بنا لیا تھا');
INSERT INTO "sayyid_qutb" VALUES(25,31,'25:31','اے نبی صلى الله عليه وسلم ‘ ہم نے تو اسی طرح مجرموں کو ہر نبی کا دشمن بنایا ہے اور تمہارے لیے تمہارا رب ہی رہنمائی اور مدد کو کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(25,32,'25:32','منکرین کہتے ہیں اس شخص پر سارا قرآن ایک ہی وقت میں کیوں نہ اتار دیا گیا ؟ “۔ ہاں ایسا اس لیے کیا گیا ہے کہ اس کو اچھی طرح ہم تمہارے ذہن نشین کرتے ہیں اور (اسی غرض کے لیے) ہم نے اس کو ایک خاص ترتیب کے ساتھ الگ الگ اجزاء کی شکل دی ہے');
INSERT INTO "sayyid_qutb" VALUES(25,33,'25:33','اور (اس میں یہ مصلحت بھی ہے کہ جب کبھی وہ تمہارے سامنے کوئی نرالی بات (یا عجیب سوال) لے کر آئے ‘ اس کا ٹھیک جواب بروقت ہم نے تمہیں دے دیا اور بہترین طریقے سے بات کھول دی');
INSERT INTO "sayyid_qutb" VALUES(25,34,'25:34','جو لوگ اوندھے منہ جہنم کی طرف دھکیلے جانے والے ہیں ‘ ان کا موقف بہت برا ہے اور ان کی راہ حد درجہ غلط');
INSERT INTO "sayyid_qutb" VALUES(25,35,'25:35','ہم نے موسیٰ کتاب دی اور اس کے ساتھ اس کے بھائی ہارون کو مدد گار کے طور پر لگایا');
INSERT INTO "sayyid_qutb" VALUES(25,36,'25:36','اور ان سے کہا کہ جائو اس قوم کی طرف جس نے ہماری آیات کو جھٹلادیا ہے۔ آخر کار ان لوگوں کو ہم نے تباہ کر کے رکھ دیا');
INSERT INTO "sayyid_qutb" VALUES(25,37,'25:37','یہی حال قوم نوح کا ہوا۔ جب انہوں نے رسولوں کی تکذیب کی۔ ہم نے ان کو غرق کردیا اور دنیا بھر کے لوگوں کے لیے ایک نشان عبرت بنا دیا اور ان ظالموں کے لیے ایک درد ناک عذاب ہم نے مہیا کررکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,38,'25:38','اسی طرح عاد اور ثمود اور اصحاب الرس اور بیچ کی صدیوں کے بہت سے لوگ تباہ کیے گئے');
INSERT INTO "sayyid_qutb" VALUES(25,39,'25:39','ان میں سے ہر ایک کو ہم نے (پہلے تباہ ہونے والوں کی) مثالیں دے دے کر سمجھایا اور آخر کار ہر ایک کو غارت کر دیا');
INSERT INTO "sayyid_qutb" VALUES(25,40,'25:40','اور اس بستی پر تو ان کا گزر ہوچکا ہے جس پر بدترین بارش برسائی گئی تھی۔ کیا انہوں نے اس کا حال دیکھا نہ ہوگا ؟ مگر یہ موت کے بعد دوسری زندگی کی توقع ہی نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(25,41,'25:41','یہ لوگ جب تمہیں دیکھتے ہیں تو تمہارا مذاق بنا لیتے ہیں (کہتے ہیں) ” کیا یہ شخص ہے جسے خدا نے رسول بنا کر بھیجا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(25,42,'25:42','اس نے ہمیں گمراہ کرکے اپنے معبودوں سے برگشتہ کر ہی دیا ہوتا اگر ہم ان کی عقیدت پر جم نہ گئے ہوتے وہ وقت دور نہیں ہے جب عذاب دیکھ کر انہیں خود معلوم ہوجائے گا کہ کون گمراہی میں دور نکل گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,43,'25:43','کیا تم نے اس شخص کے حال پر غور کیا ہے جس نے اپنی خواہش نفس کو اپنا خدا بنا لیا ‘ کیا تم ایسے شخص کو راہ راست پر لے آنے کا ذمہ لے سکتے ہو');
INSERT INTO "sayyid_qutb" VALUES(25,44,'25:44','کیا تم سمجھتے ہو کہ ان میں سے اکثر لوگ سنتے اور سمجھتے ہیں ؟ یہ تو جانوروں کی طرح ہیں ‘ بلکہ ان سے بھی گئے گزرے');
INSERT INTO "sayyid_qutb" VALUES(25,45,'25:45','تم نے دیکھا نہیں کہ تمہارا رب کس طرح سایہ پھیلا دیتا ہے ؟ اگر وہ چاہتا تو اسے دائمی سایہ بنا دیتا ۔ ہم نے سورج کو اس پر دلیل بنایا');
INSERT INTO "sayyid_qutb" VALUES(25,46,'25:46','پھر (جیسے جیسے سورج اٹھتا جاتا ہے) ہم اس سائے کو رفتہ رفتہ اپنی طرف سمیٹتے چلے جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(25,47,'25:47','ور وہ اللہ ہی ہے جس نے رات کو تمہارے لئے لباس ، اور نیند کو سکون موت اور دن کو جی اٹھنے کا وقت بنایا');
INSERT INTO "sayyid_qutb" VALUES(25,48,'25:48','” اور وہی ہے جو اپنی رحمت کے آگے آگے ہوائوں کو بشارت بنا کر بھیجتا ہے۔ پھر آسمان سے پاک پانی نازل کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,49,'25:49','تاکہ ایک مردہ علاقے کو اس کے ذریعے زندگی بخشے اور اپنی مخلوق میں سے بہت سے جانوروں اور انسانوں کو سیراب کرے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,50,'25:50','” قرآن کو ہم بار بار ان کے سامنے لاتے ہیں تاکہ وہ کچھ سبق لیں ، مگر اکثر لوگ کفر اور ناشکری کے سوا کوئی دوسرا رویہ اختیار کرنے سے انکار کردیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(25,51,'25:51','اگر ہم چاہتے تو ایک ایک بستی میں ایک ایک خبردار کرنے والا اٹھا کھڑا کرتے');
INSERT INTO "sayyid_qutb" VALUES(25,52,'25:52','پس اے نبی صلى الله عليه وسلم کافروں کی بات ہرگز نہ مانو اور اس قرآن کو لے کر ان کے ساتھ زبردست جہاد کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,53,'25:53','” اور وہی ہے جس نے دو سمندروں کو ملا رکھا ہے۔ ایک لذیذ و شیریں ، دوسرا تلخ و شور اور دونوں کے درمیان ایک پردہ حائل ہے۔ ایک رکاوٹ ہے جو انہیں گڈ مڈ سے روکے ہوئے ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,54,'25:54','” اور وہی ہے جس نے پانی سے ایک بشر پیدا کیا ، پھر اس سے نسب اور سسرال کے دو الگ سلسلے چلائے۔ تیرا رب بڑا ہی قدرت والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,55,'25:55','” اس خدا کو چھوڑ کر لوگ ان کو پوج رہے ہیں جو نہ ان کو نفع پہنچا سکتے ہیں ، نہ نقصان اور اس سے مزید یہ کہ کافر اپنے رب کے مقابلے میں ہر باغی کا مددگار بنا ہوا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,56,'25:56','” اے نبی تم کو تو ہم نے بس ایک بشارت دینے الا اور خبردار کرنے والا بنا کر بھیجا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,57,'25:57','ان سے کہہ دو کہ ” میں اس کام پر تم سے کوئی اجرت نہیں مانگتا ، میری اجرت بس یہی ہے کہ جس کا جی چاہے اوہ اپنے رب کا راستہ اختیار کر لے');
INSERT INTO "sayyid_qutb" VALUES(25,58,'25:58','اے نبی صلى الله عليه وسلم اس خدا پر بھروسہ رکھو جو زندہ ہے اور کبھی مرنے والا نہیں۔ اس کی حمد کے ساتھ اس کی تسبیح کرو۔ اپنے بندوں کے گناہوں سے بس اسی کا باخبر ہونا کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(25,59,'25:59','وہجس نے چھ دنوں میں زمین اور آسمانوں کو اور ان ساری چیزوں کو بنا کر رکھ دیا جو آسمانوں اور زمین کے درمیان ہیں ، پھر آپ ہی ” عرش “ پر جلوہ فرما ہوا۔ رحمٰن ہے وہ اس سے پوچھو کہ وہ جاننے الا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,60,'25:60','” ان لوگوں سے جب کہا جاتا ہے کہ اس رحمن کو سجدہ کرو تو کہتے ہیں ” رحمٰن کیا ہوتا ہے ؟ کیا بس جسے تو کہہ دے اسی کو ہم سجدہ کرتے پھریں ؟ “ یہ دعوت ان کی نفرت میں آنا اور اضافہ کردیتی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,61,'25:61','” بڑا متبرک ہے وہ جس نے آسمان میں برج بنائے اور اس میں ایک چراغ اور ایک چمکتا چاند روشن کیا');
INSERT INTO "sayyid_qutb" VALUES(25,62,'25:62','وہی ہے جس نے رات اور دن کو ایک دوسرے کا جانشین بنایا ہر اس شخص کے لئے جو سبق لینا چاہے ، یا شکر گزار ہونا چاہئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,63,'25:63','” رحمٰن کے (اصلی) بندے وہ ہیں جو زمین پر نرم چال چلتے ہیں اور جاہل ان کے منہ آئیں تو کہہ دیتے ہیں کہ تم کو سلام');
INSERT INTO "sayyid_qutb" VALUES(25,64,'25:64','جو اپنے رب کے حضور سجدے اور قیام میں راتیں گزارتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(25,65,'25:65','” جو دعائیں کرتے ہیں کہ ” اے ہمارے رب جہنم کے عذاب سے ہم کو بچا لے ، اس کا عذاب تو جان کا لاگو ہے');
INSERT INTO "sayyid_qutb" VALUES(25,66,'25:66','وہ تو بڑا ہی برا مستقرد مقام ہے');
INSERT INTO "sayyid_qutb" VALUES(25,67,'25:67','جو خرچ کرتے ہیں تو نہ فضول خرچی کرتے ہیں نہ بخل ، بلکہ ان کا خرچ دونوں انتہائوں کے درمیان اعتدال پر قائم رہتا ہے');
INSERT INTO "sayyid_qutb" VALUES(25,68,'25:68','جو اللہ کے سوا کسی اور معبود کو نہیں پکارتے ، اللہ کی حرام کی ہوئی کسی جان کو ناحق ہلاک نہیں کرتے اور نہ زنا کے مرتکب ہوتے ہیں۔ یہ کام جو کوئی کرے وہ اپنے گناہ کا بدلہ پائے گا');
INSERT INTO "sayyid_qutb" VALUES(25,69,'25:69','قیامت کے روز اس کو مکرر عذاب دیا جائے گا اور اسی میں وہ ہمیشہ ذلت کے ساتھ پڑا رہے گا');
INSERT INTO "sayyid_qutb" VALUES(25,70,'25:70','الا یہ کہ کوئی (ان گناہوں کے بعد) توبہ کرچکا ہو اور ایمان لا کر عمل صالح کرنے لگا ہو۔ ایسے لوگوں کی برائیوں کو اللہ بھلائیوں سے بدل دے گا اور وہ بڑا غفور و رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(25,71,'25:71','جو شخص توبہ کر کے نیک عمل اختیار کرتا ہے وہ تو اللہ کی طرف پلٹ آتا ہے جیسا کہ پلٹنے کا حق ہے');
INSERT INTO "sayyid_qutb" VALUES(25,72,'25:72','(اور رحمٰن کے بندے وہ ہیں) جو جھوٹ کے گواہ نہیں بنتے اور سکی لغو چیز پر ان کا گزر ہوجائے تو شریف آدمیوں کی طرف گزر جاتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,73,'25:73','” جنہیں اگر ان کے رب کی آیات سنا کر نصیحت کی جاتی ہے تو وہ اس پر اندھے اور بہرے بن کر نہیں رہ جاتے۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,74,'25:74','” مانگا کرتے ہیں کہ ” اے ہمارے رب ، ہمیں اپنی بیویوں اور اپنی اولاد سے آنکھوں کی ٹھنڈک دے اور ہم کو پرہیز گاروں کا امام بنا۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,75,'25:75','” یہ ہیں وہ لوگ جو اپنے صبر کا پھل منزل بلند کی شکل میں پائیں گے۔ آداب و تسلیمات سے ان کا استقبال ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(25,76,'25:76','وہ ہمیشہ ہمیشہ وہاں رہیں گے۔ کیا ہی اچھا ہے وہ مستقر اور وہ مستقر اور وہ مقام۔ “');
INSERT INTO "sayyid_qutb" VALUES(25,77,'25:77','” اے نبی صلى الله عليه وسلم لوگوں سے کہو ” میرے رب کو تمہارے کیا حاجت پڑی ہے اگر تم اس کو نہ پکارو۔ اب کہ تم نے جھٹلا دیا ہے ، عنقریب وہ سزا پائو گے کہ جان چھڑانی محال ہوگی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,1,'26:1','” ط ۔ س ۔ م');
INSERT INTO "sayyid_qutb" VALUES(26,2,'26:2','یہ کتاب مبین کی آیات ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,3,'26:3','اے نبی شاید تم اس غم میں اپنی جان کھو دو گے کہ یہ لوگ ایمان نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(26,4,'26:4','ہم چاہیں تو آسمان سے ایسی نشانی نازل کرسکتے ہیں کہ ان کی گردنیں اس کے آگے جھک جائیں');
INSERT INTO "sayyid_qutb" VALUES(26,5,'26:5','” جن لوگوں کے پاس رحمٰن کے پاس رحمٰن کی طرف سے جو نئی نصیحت بھی آتی ہے ، یہ اس سے منہ موڑ لیتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,6,'26:6','” اب جب کہ یہ جھٹلا چکے ہیں ، عنقریب ان کو اس چیز کی حقیقت (مختلف طریقوں سے) معلوم ہوجائے گی جس کا یہ مذاق اڑاتے رہے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,7,'26:7','” اور کیا انہوں نے کبھی زمین پر نگاہ نہیں ڈالی کہ ہم نے کتنی کثیر مقدار میں ہر طرح کی عمدہ نباتات اس میں پیدا کی ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,8,'26:8','یقینا اس میں ایک نشانی ہے مگر ان میں سے اکثر ماننے والے نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(26,9,'26:9','” اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
INSERT INTO "sayyid_qutb" VALUES(26,10,'26:10','انہیں اس وقت کا قصہ سنائو جب کہ تمہارے رب نے موسیٰ کو پکارا ” ظالم قوم کے پاس جا');
INSERT INTO "sayyid_qutb" VALUES(26,11,'26:11','فرعون کی قوم کے پاس ...کیا وہ نہیں مارتے ؟');
INSERT INTO "sayyid_qutb" VALUES(26,12,'26:12','اس نے عرض کیا ” اے میرے رب ، مجھے خوف ہے کہ وہ مجھ کو جھٹلا دیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,13,'26:13','میرا سینہ گھٹتا ہے اور میری زبان نہیں چلتی۔ آپ ہارون کی طرف رسالت بھیجیں');
INSERT INTO "sayyid_qutb" VALUES(26,14,'26:14','اور مجھ پر ان کے ہاں ایک جرم کا الزام بھی ہے ، اس لئے میں ڈرتا ہوں کہ وہ مجھے قتل کردیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,15,'26:15','فرمایا ” ہرگز نہیں ، تم دونوں جائو ہماری نشانیاں لے کر ، ہم تمہارے ساتھ سب کچھ سنتے رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,16,'26:16','فرعون کے پاس جائو اور اس سے کہو ، ہم کو رب العالمین نے اس لئے بھیجا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,17,'26:17','کہ تو بنی اسرائیل کو ہمارے ساتھ جانے دے');
INSERT INTO "sayyid_qutb" VALUES(26,18,'26:18','فرعون نے کہا ” کیا ہم نے تجھ کو اپنے ہاں بچہ سا نہیں پالا تھا ؟ تو نے اپنی عمر کے کئی سال ہمارے ہاں گزارے');
INSERT INTO "sayyid_qutb" VALUES(26,19,'26:19','اور اس کے بعد کر گیا جو کچھ کر گیا تو بڑا احسان فراموش آدمی ہے');
INSERT INTO "sayyid_qutb" VALUES(26,20,'26:20','موسیٰ نے جواب دیا ” اس وقت وہ کام میں نے نادانستگی میں کردیا تھا');
INSERT INTO "sayyid_qutb" VALUES(26,21,'26:21','پھر میں تمہارے خوف سے بھاگ گیا۔ اس کے بعد میرے رب نے مجھ کو حکم عطا کیا اور مجھے رسولوں میں شامل فرمال یا');
INSERT INTO "sayyid_qutb" VALUES(26,22,'26:22','رہا تیرا احسان ، جو تو نے مجھ پر جتایا ہے تو اس کی حقیقت یہ ہے کہ تو نے بنی اسرائیل کو غلام بنا لیا تھا');
INSERT INTO "sayyid_qutb" VALUES(26,23,'26:23','فرعون نے کہا ” اور یہ رب العالمین کیا ہوتا ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(26,24,'26:24','موسیٰ نے جواب دیا ” آسمانوں اور زمین کا رب اور ان سب چیزوں کا رب جو آسمان و زمین کے درمیان ہیں ، اگر تم یقین لانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,25,'26:25','’ فرعون نے اپنے گرد و پیش کے لوگوں سے کہا ” سنتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,26,'26:26','’ موسیٰ نے کہا ” تمہارا رب بھی اور تمہارے ان آبائو اجداد کا رب بھی جو گزر چکے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,27,'26:27','” فرعون نے (حاضرین سے) کہا ” تمہارے یہ رسول صاحب جو تمہاری طرف بھیجے گئے ہیں ، بالکل ہی پاگل معلوم ہوتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,28,'26:28','” موسیٰ نے کہا ” مشرق و مغرب اور جو کچھ ان کے درمیان ہے سب کا رب ، اگر آپ لوگ کچھ عقل رکھتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,29,'26:29','” فرعون نے کہا “ ” اگر تو نے میرے سوا کسی اور کو معبود مانا تو تجھے بھی ان لوگوں میں شامل کر دوں گا جو قید خانوں میں پڑے سڑ رہے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,30,'26:30','’ موسیٰ نے کہا ” اگرچہ میں لے آئوں تیرے سامنے ایک صریح چیز بھی');
INSERT INTO "sayyid_qutb" VALUES(26,31,'26:31','’ فرعون نے کہا ” اچھا تو لے آ، اگر تو سچا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,32,'26:32','اس کی زبان سے یہ بات نکلتے ہی) موسیٰ نے اپنا عصا پھینکا اور یکایک وہ ایک صریح اژدھا تھا');
INSERT INTO "sayyid_qutb" VALUES(26,33,'26:33','پھر اس نے اپنا ہاتھ (بغل سے ) کھینچا اور وہ سب دیکھنے والوں کے سامنے چمک رہا تھا');
INSERT INTO "sayyid_qutb" VALUES(26,34,'26:34','’ فرعون اپنے گرد و پیش کے سرداروں سے بولا ” یہ شخص یقینا ایک ماہر جادوگر ہے');
INSERT INTO "sayyid_qutb" VALUES(26,35,'26:35','چاہتا ہے کہ اپنے جادو کے زور سے تم کو تمہارے ملک سے نکال دے۔ اب بتائو تم کیا حکم دیتے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,36,'26:36','” انہوں نے کہا ” اسے اور اس کے بھائی کو مہلت دیں اور شہروں میں ہر کارے بھیج دیجیے');
INSERT INTO "sayyid_qutb" VALUES(26,37,'26:37','کہ ہر سیانے جادوگر کو آپ کے پاس لے آئیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,38,'26:38','” چناچہ ایک روز مقرر وقت پر جادو گر اکٹھے کر لئے گئے');
INSERT INTO "sayyid_qutb" VALUES(26,39,'26:39','اور لوگوں سے کہا گیا ” تم اجتماع میں چلو گے');
INSERT INTO "sayyid_qutb" VALUES(26,40,'26:40','شاید کہ ہم جادوگروں کے دین ہی پر رہ جائیں اگر وہ غالب رہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,41,'26:41','” جب جادوگر میدان میں آئے تو انہوں نے فرعون سے کہا ” ہمیں انعام تو ملے گا اگر ہم غالب رہے ؟ ‘');
INSERT INTO "sayyid_qutb" VALUES(26,42,'26:42','‘ اس نے ہا ” ہاں ، اور تم تو اس وقت مقربین میں شامل ہو جائو گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,43,'26:43','” موسیٰ نے کہا ” پھینکو جو تمہیں پھینکنا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,44,'26:44','“ انہوں نے فوراً اپنی رسیاں اور لاٹھیاں پھینک دیں اور بولے ” فرعون کے اقبال سے ہم ہی غالب رہیں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,45,'26:45','پھر مسویٰ نے اپنا عصا پھینکا تو یکایک وہ ان کے جھوٹے کرشموں کو ہڑپ کرتا چلا جا رہا تھا');
INSERT INTO "sayyid_qutb" VALUES(26,46,'26:46','اس پر سارے جادوگر بےاختیار سجدے میں گر پڑے');
INSERT INTO "sayyid_qutb" VALUES(26,47,'26:47','اور بول اٹھے کہ مان گئے ہم رب العالمین کو');
INSERT INTO "sayyid_qutb" VALUES(26,48,'26:48','موسیٰ اور ہارون کے رب کو');
INSERT INTO "sayyid_qutb" VALUES(26,49,'26:49','فرعون نے کہا ، تم موسیٰ کی بات مان گئے قبل اس کے کہ میں تمہیں اجازت دیتا۔ ضرور یہ تمہارا بڑا ہے جس نے تمہیں جادو سکھایا ہے۔ اچھا ابھی تمہیں معلوم ہوجاتا ہے۔ میں تمہارے ہاتھ پائوں مخالف سمتوں سے کٹوائوں گا اور تم سب کو سولی پر چڑھا دوں گا');
INSERT INTO "sayyid_qutb" VALUES(26,50,'26:50','انہوں نے جواب دیا کچھ پروا نہیں ہم اپنے رب کے حضور پہنچ جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,51,'26:51','اور ہمیں توقع ہے کہ ہمارا رب ہمارے گناہ معاف کر دے گا کیونکہ سب سے پہلے ہم ایمان لائے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,52,'26:52','” ہم نے موسیٰ کو وحی بھیجی کہ ” راتوں رات میرے بندوں کو لے کر نکل جائو ، تمہارا پیچھا کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(26,53,'26:53','اس پر فرعون نے (فوجیں جمع کرنے کے لئے) شہروں میں نقیب بھیج دیئے');
INSERT INTO "sayyid_qutb" VALUES(26,54,'26:54','(اور کہلا بھیجا) کہ ” یہ کچھ مٹھی بھر لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,55,'26:55','اور انہوں نے ہم کو بہت ناراض کیا ہی');
INSERT INTO "sayyid_qutb" VALUES(26,56,'26:56','اور ہم ایک ایسی جماعت ہیں جس کا شیوہ ہر وقت چوکنا رہنا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,57,'26:57','’ اس طرح ہم انہیں ان کے باغوں اور چشموں');
INSERT INTO "sayyid_qutb" VALUES(26,58,'26:58','اور خزانوں اور ان کی بہترین قیام گاہوں سے نکال لائے');
INSERT INTO "sayyid_qutb" VALUES(26,59,'26:59','یہ تو ہوا ان کے ساتھ اور (دوسری طرف) بنی اسرائیل کو ہم نے ان سب چیزوں کا وارث کردیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,60,'26:60','” صبح ہوتے ہی یہ لوگ ان کے تعاقب میں چل پڑے۔');
INSERT INTO "sayyid_qutb" VALUES(26,61,'26:61','جب دونوں گروہوں کا آمنا سامنا ہوا تو موسیٰ کے ساتھ چیخ اٹھے کہ ” ہم تو پکڑے گئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,62,'26:62','موسیٰ نے کہا ” ہرگز نہیں۔ میرے ساتھ میرا رب ہے۔ وہ ضرور میری رہنمائی فرمائے گا');
INSERT INTO "sayyid_qutb" VALUES(26,63,'26:63','ہم نے موسیٰ کو وحی کے ذریعہ سے حکم دیا کہ مار اپنا عصا سمندر پر۔ “ یکایک سمندر پھٹ گیا اور اس کا ہر ٹکڑا ایک عظیم الشان پہاڑ کی طرح ہو گیا');
INSERT INTO "sayyid_qutb" VALUES(26,64,'26:64','اسی جگہ ہم دوسرے گروہ کو بھی قریب لے آئے');
INSERT INTO "sayyid_qutb" VALUES(26,65,'26:65','موسیٰ اور ان سب لوگوں کو جو اس کے ساتھ تھے ، ہم نے بچا لیا');
INSERT INTO "sayyid_qutb" VALUES(26,66,'26:66','اور دوسروں کو غرق کردیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,67,'26:67','” اس واقعہ میں ایک نشانی ہے مگر ان لوگوں میں سے اکثر ماننے والے نہیں ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,68,'26:68','’ اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
INSERT INTO "sayyid_qutb" VALUES(26,69,'26:69','” اور انہیں ابراہیم کا قصہ سنائو');
INSERT INTO "sayyid_qutb" VALUES(26,70,'26:70','جبکہ اس نے اپنے باپ اور اپنی قوم سے پوچھا تھا کہ ” یہ کیا چیزیں ہیں جن کو تم پوجتے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,71,'26:71','” انہوں نے جواب دیا ” کچھ بت ہیں جن کی ہم پوجا کرتے ہیں اور انہی کی سیوا میں ہم لگے رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,72,'26:72','” اس نے پوچھا ” کیا یہ تمہاری سنتے ہیں جب تم انہیں پکارتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(26,73,'26:73','یا یہ نہیں کچھ نفع یا نقصان پہنچاتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,74,'26:74','” انہوں نے جواب دیا ” بلکہ ہم نے اپنے باپ دادا کو ایسا ہی کرتے پایا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,75,'26:75','” اس پر ابراہیم نے کہا ” کبھی تم نے (آنکھیں کھول کر) ان چیزوں کو دیکھا بھی جن کی بندگی تم');
INSERT INTO "sayyid_qutb" VALUES(26,76,'26:76','اور تمہارے پچھلے باپ دادا بجا لاتے رہے ؟');
INSERT INTO "sayyid_qutb" VALUES(26,77,'26:77','میرے تو یہ سب دشمن ہیں ، بجز ایک رب العالمین کے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,78,'26:78','” جس نے مجھے پیدا کیا پھر وہی میری رہنمائی فرماتا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,79,'26:79','جو مجھے کھلاتا ہے اور پلاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,80,'26:80','اور جب بیمار ہوجاتا ہوں تو وہی مجھے شفا دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,81,'26:81','جو مجھے موت دے گا اور پھر دوبارہ مجھ کو زندگی بخشے گا');
INSERT INTO "sayyid_qutb" VALUES(26,82,'26:82','اور جس سے میں امید رکھتا ہوں کہ روز جزا میں وہ میری خطا معاف فرما دے گا');
INSERT INTO "sayyid_qutb" VALUES(26,83,'26:83','ابراہیم نے دعا کی ” اے میرے رب ، مجھے حکم عطا کر۔ اور مجھ کو صالحوں کے ساتھ ملا');
INSERT INTO "sayyid_qutb" VALUES(26,84,'26:84','اور بعد کے آنے والوں میں مجھ کو سچی ناموری عطا کر');
INSERT INTO "sayyid_qutb" VALUES(26,85,'26:85','اور مجھے جنت نعیم کے وارثوں میں شامل فرما');
INSERT INTO "sayyid_qutb" VALUES(26,86,'26:86','اور میرے باپ کو معاف کر دے کہ بیشک وہ گمراہ لوگوں میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(26,87,'26:87','اور مجھے اس دن رسوا نہ کر جبکہ سب لوگ زندہ کر کے اٹھائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,88,'26:88','جبکہ نہ مال کوئی فائدہ دے گانہ اولاد ،');
INSERT INTO "sayyid_qutb" VALUES(26,89,'26:89','بجز اس کے کہ کوئی شخص قلب سلیم لئے ہوئے اللہ کے حضور حاضر ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,90,'26:90','”(اس روز) پرہیز گاروں کیق ریب لے آئی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(26,91,'26:91','اور دوزخ بہکے ہوئے لوگوں کے سامنے کھول دی جائے گیا');
INSERT INTO "sayyid_qutb" VALUES(26,92,'26:92','ور ان سے پوچھا جائے گا کہ ” اب کہاں ہیں وہ جن کی تم خدا کو چھوڑ کر عبادت کرتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(26,93,'26:93','کیا وہ تمہاری کچھ مدد کر رہے ہیں یا خود اپنا بچائو کرسکتے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,94,'26:94','“ پھر وہ معبود اور یہ بہکے ہوئے لوگ ،');
INSERT INTO "sayyid_qutb" VALUES(26,95,'26:95','اور ابلیس کے لشکر سب کے سب اس میں اوپر تلے دھکیل دیئے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,96,'26:96','وہاں یہ سب آپس میں جھگڑیں گے');
INSERT INTO "sayyid_qutb" VALUES(26,97,'26:97','اور یہ بہکے ہوئے لوگ (اپنے معبودوں سے) کہیں گے کہ ” خدا کی قسم ، ہم تو صریح گمراہی میں مبتلا تھے');
INSERT INTO "sayyid_qutb" VALUES(26,98,'26:98','جب کہ تم کو رب العالمین کی برابری کا درجہ دے رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(26,99,'26:99','، اور وہ مجرم لوگ ہی تھے جنہوں نے ہم کو اس گمراہی میں ڈالا');
INSERT INTO "sayyid_qutb" VALUES(26,100,'26:100','اب نہ ہمارا کوئی سفارشی ہے');
INSERT INTO "sayyid_qutb" VALUES(26,101,'26:101','اور نہ کوئی جگری دوست');
INSERT INTO "sayyid_qutb" VALUES(26,102,'26:102','کاش ہمیں ایک دفعہ پھر پلٹنے کا موقع مل جائے تو ہم مومن ہوں');
INSERT INTO "sayyid_qutb" VALUES(26,103,'26:103','” یقینا اس میں ایکب ڑی نشانی ہے۔ مگر ان میں سے اکثر لوگ ایمان لانے والے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,104,'26:104','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,105,'26:105','” قوم نوح نے رسولوں کو جھٹلایا');
INSERT INTO "sayyid_qutb" VALUES(26,106,'26:106','یاد کرو جب کہ ان کے بھائی نوح (علیہ السلام) نے ان سے کہا تھا ” کیا تم ڈرتے نہیں ہو');
INSERT INTO "sayyid_qutb" VALUES(26,107,'26:107','میں تمہارے لئے ایک امانت دار رسول ہوں ،');
INSERT INTO "sayyid_qutb" VALUES(26,108,'26:108','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "sayyid_qutb" VALUES(26,109,'26:109','” میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں۔ میرا اجر تو رب العالمین کے ذمہ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,110,'26:110','” پس تم اللہ سے ڈرو اور (بےکھٹکے) میری اطاعت کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,111,'26:111','’ انہوں نے جواب دیا ” کیا ہم تجھے مان لیں حالانکہ تیری پیروی رذیل ترین لوگوں نے اتخیار کی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,112,'26:112','نوح نے کہا ” میں کیا جانوں کہ ان کے عمل کیسے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,113,'26:113','ان کا حساب تو میرے رب کے ذمہ ہے ، کاش تم کچھ شعور سے کام لو');
INSERT INTO "sayyid_qutb" VALUES(26,114,'26:114','میر ایہ کام نہیں ہے کہ جو ایمان لائیں ان کو میں دھتکاردوں');
INSERT INTO "sayyid_qutb" VALUES(26,115,'26:115','میں تو بس ایک صاف صاف متنبہ کردینے والا آدمی ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,116,'26:116','’ انہوں نے کہا ” اے نوح ، اگر تو باز نہ آیا تو پھٹکارے ہوئے لوگوں میں شامل ہو کر رہ گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,117,'26:117','نوح نے دعا کی اے میرے رب میری قوم نے مجھے جھٹلا دیا');
INSERT INTO "sayyid_qutb" VALUES(26,118,'26:118','اب میرے اور ان کے درمیان دو ٹوک فیصلہ کردے اور مجھے اور جو مومن میرے ساتھ ہیں ، ان کو نجات دے');
INSERT INTO "sayyid_qutb" VALUES(26,119,'26:119','آخر کار ہم نے اس کو اور اس کے ساتھیوں کو ایک بھری ہوئی کشتی میں بچا لیا');
INSERT INTO "sayyid_qutb" VALUES(26,120,'26:120','اور اس کے بعد باقی لوگوں کو غرق کردیا');
INSERT INTO "sayyid_qutb" VALUES(26,121,'26:121','یقینا اس میں ایک نشانی ہے ، مگر ان میں سے اکثر لوگ ماننے والے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,122,'26:122','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی');
INSERT INTO "sayyid_qutb" VALUES(26,123,'26:123','” عاد نے رسولوں کو جھٹلایا');
INSERT INTO "sayyid_qutb" VALUES(26,124,'26:124','یاد کرو جب کہ ان کے بھائی ہود نے ان سے کہا تھا ’ د کیا تم ڈرتے نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,125,'26:125','میں تمہارے لئے ایک امانت دار رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(26,126,'26:126','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو۔');
INSERT INTO "sayyid_qutb" VALUES(26,127,'26:127','میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں۔ میرا اجر تو رب العالمین کے ذمہ ہے');
INSERT INTO "sayyid_qutb" VALUES(26,128,'26:128','” یہ تمہارا کیا حال ہے کہ ہر اونچے مقام پر لا حاصل ایک یادگار عمارت بنا ڈالتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,129,'26:129','اور بڑے بڑے قصر تعمیر کرتے ہو گویا تمہیں ہمیشہ رہنا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,130,'26:130','’ اور جب کسی پر ہاتھ ڈالتے ہو جبار بن کر ڈالتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,131,'26:131','” پس تم لوگ اللہ سے ڈرو اور میری اطاعت کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,132,'26:132','’ ڈرو اس سے جس نے وہ کچھ تمہیں دیا ہے جو تم جانتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(26,133,'26:133','تمہیں جانور دیئے ، اولاد دی');
INSERT INTO "sayyid_qutb" VALUES(26,134,'26:134','باغ دیئے اور چشمے دیئے۔');
INSERT INTO "sayyid_qutb" VALUES(26,135,'26:135','مجھے تمہارے حق میں ایک بڑے دن کے عذاب کا ڈر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(26,136,'26:136','انہوں نے جواب دیا ” تو نصیحت کریانہ کر ، ہمارے لئے سب یکساں ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,137,'26:137','’ یہ باتیں یوں یوں ہی ہوتی چلی آئی ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,138,'26:138','’ اور ہم عذاب میں مبتلا ہونے والے نہیں ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,139,'26:139','” آخر کار انہوں نے اسے جھٹلا دیا اور ہم نے ان کو ہلاک کردیا۔ “ ’ یقینا اس میں ایک نشانی ہے ، مگر ان میں سے اکثر لوگ ماننے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,140,'26:140','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,141,'26:141','” ثمود نے رسولوں کو جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(26,142,'26:142','یاد کرو جب کہ ان کے بھائی صالح نے ان سے کہا ” کیا تم ڈرتے نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,143,'26:143','میں تمہارے لئے ایک امانت دار رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(26,144,'26:144','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو۔');
INSERT INTO "sayyid_qutb" VALUES(26,145,'26:145','میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں ، میرا اجر تو رب العالمین کے ذمہ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,146,'26:146','” کیا تم ان سب چیزوں کے درمیان ، جو یہاں ہیں ، بس یونہی اطمیان سے رہنے دیئے جائو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(26,147,'26:147','ان باغوں اور چشموں میں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,148,'26:148','ان کھیتوں اور نخلستانوں میں جن کے خوشے رس بھرے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,149,'26:149','تم پہاڑ کھود کھود کر فخریہ ان میں عمارتیں بناتے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,150,'26:150','” اللہ سے ڈرو اور میری اطاعت کرو۔');
INSERT INTO "sayyid_qutb" VALUES(26,151,'26:151','ان بےلگام لوگوں کی اطاعت نہ کرو');
INSERT INTO "sayyid_qutb" VALUES(26,152,'26:152','جو زمین میں فساد برپا کرتے ہیں اور کوئی اصلاح نہیں کرتے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,153,'26:153','انہوں نے جواب دیا ” تو محض ایک سحر زدہ آدمی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(26,154,'26:154','تو ہم جیسے ایک انسان کے سوا اور کیا ہے ؟ لا کوئی نشانی اگر تو سچا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,155,'26:155','صالح نے کہا ” یہ اونٹنی ہے ، ایک دن اس کے پینے کا ہے اور ایک دن تم سب کے پانی پینے کا');
INSERT INTO "sayyid_qutb" VALUES(26,156,'26:156','اس کو ہرگز نہ چھوڑنا ورنہ ایک بڑے دن کا عذاب تم کو آ لے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,157,'26:157','’ مگر انہوں نے اس کو چیں کاٹ دیں اور آخر کار پچھتاتے رہ گئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,158,'26:158','” عذاب نے انہیں آ لیا۔ “ ’ یقینا اس میں ایک نشانی ہے ، مگر ان میں سے اکثر لوگ ماننے والے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,159,'26:159','’ اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,160,'26:160','’ لوط کی قوم نے رسولوں کو جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(26,161,'26:161','یاد کرو جب کہ ان کے بھائی لوط نے ان سے کہا تھا ” کیا تم ڈرتے نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,162,'26:162','’ میں تمہارے لئے ایک امانت دار رسول ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,163,'26:163','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو۔');
INSERT INTO "sayyid_qutb" VALUES(26,164,'26:164','میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں ، میرا اجر تو رب العالمین کے ذمہ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,165,'26:165','” کیا تم دنیا کی مخلوق میں سے مردوں کے پاس جاتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,166,'26:166','اور تمہاری بیویوں میں تمہارے رب نے تمہارے لئے جو کچھ پیدا کیا ہے اسے چھوڑ دیتے ہو ؟ بلکہ تم لوگ تو حد سے ہی گزر گئے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,167,'26:167','” اگر تو ان باتوں سے باز نہ آیا تو جو لوگ ہماری بستیوں سے نکالے گئے ہیں ان میں تو بھی شامل ہو کر رہے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,168,'26:168','” اس نے کہا ” تمہارے کرتوتوں پر جو لوگ کڑھ رہے ہیں ، میں ان میں شامل ہوں');
INSERT INTO "sayyid_qutb" VALUES(26,169,'26:169','” اے پروردگار مجھے اور میرے اہل و عیال کو ان کی بدکرداریوں سے نجات دے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,170,'26:170','” آخر کار ہم نے اسے اور اس کے سب اہل و عیال کو بچا لیا ،');
INSERT INTO "sayyid_qutb" VALUES(26,171,'26:171','بجز ایک بڑھیا کے جو پیچھے رہ جانے والوں میں تھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,172,'26:172','” پھر باقی ماندہ لوگوں کو ہم نے تباہ کر دیا');
INSERT INTO "sayyid_qutb" VALUES(26,173,'26:173','اور ان پر برساتی ایک برسات بڑی ہی بری بارش تھی ، جو ان ذرائے جانے والوں پر نازل ہوئی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,174,'26:174','’ یقینا اس میں ایک نشانی ہے مگر ان میں سے اکثر لوگ ماننے الے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,175,'26:175','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,176,'26:176','” اصحاب الایکہ نے رسولوں کو جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(26,177,'26:177','یاد کرو جب کہ شعیب نے ان سے کہا تھا ” کیا تم ڈرتے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,178,'26:178','میں تمہارے لئے ایک امانت دار رسول ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(26,179,'26:179','لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو۔');
INSERT INTO "sayyid_qutb" VALUES(26,180,'26:180','میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں۔ میرا اجر تو رب العالمین کے ذمہ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,181,'26:181','” ٹھیک بھرو اور کسی کو گھاٹا نہ دو ۔');
INSERT INTO "sayyid_qutb" VALUES(26,182,'26:182','صحیح ترازو سے تولو');
INSERT INTO "sayyid_qutb" VALUES(26,183,'26:183','اور لوگوں کو ان کی چیزیں کم نہ دو ۔ زمین میں فساد نہ پھیلاتے پھرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,184,'26:184','’ اور اس ذات کا خوف کرو جس نے تمہیں اور گزشتہ نسلوں کو پیدا کیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,185,'26:185','’ انہوں نے کہا ” تو محض ایک سحر زدہ آدمی ہے');
INSERT INTO "sayyid_qutb" VALUES(26,186,'26:186','” اور تو کچھ نہیں ہے مگر ایک انسان ہم ہی جیسا ، اور ہم تو تجھے بالکل جھوٹا سمجھتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,187,'26:187','” اگر تو سچا ہے تو ہم پر آسمان کا کوئی ٹکڑا گرا دے');
INSERT INTO "sayyid_qutb" VALUES(26,188,'26:188','” شعیب (علیہ السلام) نے کہا ” میرا رب جانتا ہے جو کچھ تم کر رہے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,189,'26:189','’ انہوں نے اسے جھٹلا دیا آخر کار چھتری والے دن کا عذاب ان پر آگیا اور وہ بڑے ہی خوفناک دن کا عذاب تھا۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,190,'26:190','” یقینا اس میں ایک نشانی ہے ، مگر ان میں سے اکثر ماننے الے نہیں');
INSERT INTO "sayyid_qutb" VALUES(26,191,'26:191','اور حقیقت یہ ہے کہ تیرا رب زبردست بھی ہے اور رحیم بھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,192,'26:192','” یہ رب العالمین کی نازل کردہ چیز ہے');
INSERT INTO "sayyid_qutb" VALUES(26,193,'26:193','اس یلے کر امانت دار روح اتری ہے');
INSERT INTO "sayyid_qutb" VALUES(26,194,'26:194','تیرے دل پر ، تاکہ تو ان لوگوں میں شامل ہو ، جو (خدا کی طرف سے خلق خدا کو) متنبہ کرنے الے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,195,'26:195','، صاف صاف عربی زبان میں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,196,'26:196','” اور اگلے لوگوں کی کتابوں میں بھی یہ موجود ہے');
INSERT INTO "sayyid_qutb" VALUES(26,197,'26:197','کیا ان (اہل مکہ) کے لئے یہ کوئی نشانی نہیں ہے کہ اسے عملاء بنی اسرائیل جانتے ہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(26,198,'26:198','لیکن ان کی ہٹ دھرمی کا حال تو یہ ہے کہ) اگر ہم اسے کسی عجمی پر بھی نازل کر دیتے');
INSERT INTO "sayyid_qutb" VALUES(26,199,'26:199','”(اور یہ (فصیح عربی کلام) وہ ان کو پڑھ کر سناتا تب بھی یہ مان کر نہ دیتے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,200,'26:200','’ اسی طرح ہم نے اس (ذکر) کو مجرموں کے دلوں میں گزارا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(26,201,'26:201','’ وہ اس پر ایمان نہیں لاتے جب تک کہ عذاب الیم نہ دیکھ لیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,202,'26:202','” پھر جب وہ بیخبر ی میں ان پر آپڑتا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,203,'26:203','اس وقت وہ کہتے ہیں کہ ” کیا اب ہمیں کچھ مہلت مل سکتی ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(26,204,'26:204','” تو کیا یہ لوگ ہمارے عذاب کے لئے جلدی مچا رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,205,'26:205','تم نے کچھ غور کیا ، اگر ہم انہیں برسوں تک عیش کرنے کی مہلت بھی دے دیں');
INSERT INTO "sayyid_qutb" VALUES(26,206,'26:206','اور پھر وہی چیز ان پر آجائے جس سے انہیں ڈرایا جا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,207,'26:207','تو وہ سامان زیست جو ان کو ملا ہوا ہے ، ان کی کس کام آئے گا ؟ “');
INSERT INTO "sayyid_qutb" VALUES(26,208,'26:208','”(دیکھو) ہم نے کبھی کسی بستی کو اس کے بغیر ہلاک نہیں کیا کہ اس کے لئے خبردار کرنے والے');
INSERT INTO "sayyid_qutb" VALUES(26,209,'26:209','حق نصیحت ادا کرنے کو موجود تھے اور ہم ظالم نہ تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,210,'26:210','” اس (کتاب مبین) کو شیاطین لے کر نہیں اترے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,211,'26:211','نہ یہ کام ان کو سجتا ہے ، اور نہ وہ ایسا کر ہی سکتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,212,'26:212','وہ تو اس کی سماعت تک سے دور رکھے گئے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,213,'26:213','” پس اے نبی اللہ کے ساتھ کسی دور سے معبود کو نہ پکارو ، ورنہ تم بھی سزا پانے والوں میں شامل ہو جائو گے');
INSERT INTO "sayyid_qutb" VALUES(26,214,'26:214','اپنے قریب ترین رشتہ داروں کو ڈرائو');
INSERT INTO "sayyid_qutb" VALUES(26,215,'26:215','اور ایمان لانے والوں میں سے جو لوگ تمہاری پیروی اختیار کریں۔');
INSERT INTO "sayyid_qutb" VALUES(26,216,'26:216','اگر وہ تمہاری نافرمانی کریں تو کہہ دیں کہ میں ان باتوں سے بری الذمہ ہوں جو تم کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,217,'26:217','اور اس زبردست اور رحیم پر توکل کر');
INSERT INTO "sayyid_qutb" VALUES(26,218,'26:218','جو تمہیں اس وقت دیکھ رہا ہوتا ہے جب تم اٹھتے ہو');
INSERT INTO "sayyid_qutb" VALUES(26,219,'26:219','اور سجدہ گزار لوگوں میں تمہاری نقل و حرکت پر وہ نگاہ رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(26,220,'26:220','وہ سب کو سننے اور جاننے والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,221,'26:221','” لوگو ، کیا میں تمہیں بتائوں کہ شیاطین کس پر اترا کرتے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(26,222,'26:222','وہ ہر جعل ساز بدکار پر اترا کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(26,223,'26:223','سنی سنائی باتیں کانوں میں پھونکتے ہیں اور ان میں سے اکثر جھوٹے ہوتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,224,'26:224','” رہے شعراء تو ان کے پیچھے بہکے ہوئے لوگ چلا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(26,225,'26:225','کیا تم دیکھتے نہیں ہو کہ وہ ہر وادی میں بھٹکتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(26,226,'26:226','اور لکل باتیں کہتے ہیں جو کرتے نہیں ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(26,227,'26:227','والشعرآء ......مالایفعلون (802) یہ لوگ چونکہ خواہشات نفس اور اپنے مزاج کے پیچھے چلتے ہیں لہٰذا اکثر بہکے ہوئے لوگ شعراء کے پیچھے چلتے ہیں کیونکہ اکثربہکے ہوئے لوگ بھی ہوائے نفس کے بندے ہوتے ہیں اور ایسے لوگوں کا کوئی متعین نصب العین نہیں ہوتا۔ یہ ذہنی شعور اور تخیل کی وادیوں میں بھٹکتے رہتے ہیں۔ تخیلات تصورات اور ادہام کی وادیاں بہت ہی وسیع ہوتی ہیں ، یہ اپنے تاثرات کے مطابق ان میں سے کسی وادی میں پھٹکتے رہتے ہیں جس وقت ان کا دل جس وادی سے متاثر ہوئے ، اس میں پھرنے لگتے ہیں۔ پھر یہ جو باتیں کرتے ہیں وہ غیر عملی ہوتی ہیں کیونکہ یہ ایسی دنیا میں زندہ ہوتے ہیں جو انہوں نے خود پیدا کی ہوئی ہوتی ہے۔ یہ نحیفات کی دنیا ہوتی ہے اور ان کے شعور میں ہوتی ہے۔ اس دنیا کو یہ کرہ ارض پر عملی دنیا سے دور رکھتے ہیں کیونکہ عملی حقائق چونکہ تلخ ہوتے ہیں اس لئے وہ ان کو پسند نہیں ہوتے۔ اس لئے وہ بہت سی باتیں بناتے ہیں لیکن ایسا کرتے نہیں۔ کیونکہ ان کی دنیا عمل کی نہیں بلکہ وہم و گمان کی دنیا ہوتی ہے۔ اس نظر آنے الی دنیا میں ان کی دنیا کا نام و نشان بھی نہیں ہوتا۔ اب اسلام کو دیکھو کہ وہ تو ایک عملی نظام زنگدی ہے اور اس کے ہر حکم کے لئے حکم یہ ہے کہ اسے عملاً قائم کیا جائے ۔ اسلام گویا ایک عظیم تحریک ہے جو ضمیر و وجدان کی دنیا میں بھی ہے۔ عقائد و تصورات کی دنیا میں بھی ہے اور پھر یہ تحریک عملی دنیا میں بھی ہے۔ لہٰذا اسلام کی دنیا شعراء کی دنیا سے بالکل مختلف ہے کیونکہ شاعر اپنے ذہن میں ایک سوچ کی تخلیق کرتا ہے اور اس میں گم ہوجاتا ہے جبکہ اسلام جو عقیدہ اپناتا ہے اسے عملی شکل دیتا ہے۔ گویا اسلام اعلیٰ تصورات کو عملی شکل دیتا ہے اور اعلیٰ تصورات کو عمل اور اخلاق میں ظاہر کرتا ہے۔ اسلام لوگوں کو یہ تعلیم دیتا ہے کہ عملی حقائق سے فرار اختیار نہ کرو اور وہ موہوم خیالات کی طرف نہ بھاگو۔ اگر کوئی عملی حقیقت تمہیں پسند نہیں ہے تو اس سے بھاگ کر تخیلات کے قلعوں میں پناہ نہ لو۔ بلکہ اس پر حملہ آور ہو جائو اور حالات اور واقعات کو اپنی منشا کے مطابق بدل کر رکھ دو لیکن شاعر صاحب کا یہ کام نہیں ہوتا۔ اسلام انسانوں کی پوری قوت کو مجتمع کر کے اعلیٰ قدروں کو عملی دنیا تعمیر کرنے پر صرف کرتا ہے وہ اپنی کسی قوت کو بھی موہوم تخیلات کی دنیا میں رہ کر برباد نہیں کرتا۔ یہاں تک تو اسلام شعر و فن کو رد کرتا ہے اگر وہ عملی نہ ہوں۔ لیکن اسلام شعر و فن کا مطلقاً خلاف نہیں ہے۔ جو لوگ قرآن مجید کے ان الفاظ کو پڑھتے ہیں اور سرسری طور پر پڑھتے ہیں وہ شاید یہ سمجھیں کہ قرآن شعر و سخن کے مطلقاً خلاف ہے۔ دراصل قرآن کریم اس منہاج کے خلاف ہے جس پر ہمیشہ شعر اور فن چل نکلتا ہے یعنی ایسا تخیلاتی اور وہمی منہاج جس کے اندر عمل کی کوئی صورت نہ ہو۔ تخیل اور مبالغہ ہی ہو۔ ظاہر ہے کہ وہم و خیال کی دنیا تو پھر بہت ہی وسیع ہے ، انسان اس کے اندر غرق ہوجاتا ہے لیکن جب انسان کا شعور پختہ ہوجاتا ہے اور اس کے تاثرات شعر و فن پختہ ہوجاتے ہیں تو وہ شعوری اور تخیلاتی دنیا میں بھی کام کرتے ہیں اور عملی دنیا میں بھی کام کرتے ہیں۔ ایسے عملی تخیلات پھر اخلاقی لحاظ سے بھی پاک ہوتے ہیں۔ اور جب کوئی صاحب فن اسلامی رنگ میں رنگا جاتا ہے تو وہ محض ادہام و تخیلات کی دنیا میں قید نہیں ہوتا وہ عمل اور حکمت کی دنیا میں آتا ہے اور اس کے خیالات پھر بہت ہی قیمتی صاف اور روایتی شعرا سے مختلف ہوتے ہیں۔ جب کسی کی روح میں ایک مستقل منہاج رچ بس جاتا ہے۔ اس کا ایک اسلامی نصب العین قرار پاتا ہے۔ پھر وہ دنیا کو اسلام نقطہ نظر سے دیکھتا ہے۔ اسلام کی روشنی میں دیکھتا ہے ۔ اسلام کے زاویہ سے دیکھتا ہے تو اس کا فن شعر سن بھی مختلفہو جاتا ہے ۔ ایسے معیار پر اسلام شعور و سخن اور فن کے خلاف نہیں رہتا جیسا کہ قرآن مجید کے ظاہری الفاظ سے نظر آتا ہے۔ قرآن کریم نے خود انسانی عقل ، سوچ اور خیال کو اس دنیا کے عجیب و غریب مظاہر اور حقائق کی طرف متوجہ کیا ہے۔ خود نفس انسانی کے اندر جو عجائبات ہیں ، جن میں انسانی تحمیات اور تمام نفسیاتی افعال شامل ہیں ، یہ غور کرنے کی دعوت دی ہے۔ شعر و سخن کا مواد تو انسان کے نفسیای افعال ہی سے بنتا ہے اور قرآن کے اندر جگہ جگہ اس کائنات کی عجیب تخلیق اور نفس انسانی کے عجیب افعال پر غور کرنے کی دعوت دی گئی ہے اور قرآن نے یہ دعوت ایسے اسلوب میں دی ہے کہ آج تک کوئی شاعر اس کمال اور بنلدی تک نہیں پہنچ سکا۔ اپنی خوبصورتی اپنی فصاحت اور قدرت تصور کے اعتبار سے یہی وجہ ہے کہ قرآن کریم سابقہ عمومی تبصرے کے اندر استثناء پیدا کرتا ہے ۔ کیونکہ قرآن مطلقاً شعر و سخن او فن کے خلاف نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(27,1,'27:1','” ط ۔ س۔ یہ آیات ہیں قرآن اور کتاب مبین کی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,2,'27:2','” ہدایت اور بشارت ان ایمان لانے والوں کے لئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,3,'27:3','” جو نماز قائم کرتے اور زکوۃ دیتے ہیں اور پھر وہ ایسے لوگ ہیں جو آخرت پر پورا یقین رکھتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,4,'27:4','” حقیقت یہ ہے کہ جو لوگ آخرت کو نہیں مانتے ان کے لئے ہم نے ان کے کرتوتوں کو خوشنما بنا دیا ہے ، اس لئے وہ بھٹکتے پھرتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,5,'27:5','” یہ وہ لوگ ہیں جن کے لئے بڑی سزا ہے اور آخرت میں یہی سب سے زیادہ خسارے میں رہنے والے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,6,'27:6','” اور (اے نبی) بلاشبہ تم یہ قرآن ایک حکیم وعلیم ہستی کی طرف سے پا رہے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(27,7,'27:7','” انہیں اس وقت کا قصہ سنائوجب موسیٰ نے اپنے گھر والوں سے کہا کہ ” مجھے ایک آگ سی نظر آئی ہے ، میں ابھی یا تو وہاں سے کوئی خبر لے کر آتا ہوں تاکہ تم لوگ گرم ہو سکو۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,8,'27:8','” وہاں جو پہنچا تو ندا آئی کہ ” مبارک ہے وہ جو اس آگ میں ہے اور جو اس کے ماحول میں ہے۔ پاک ہے اللہ ، سب جہاں والوں کا پروردگار');
INSERT INTO "sayyid_qutb" VALUES(27,9,'27:9','، اے موسیٰ ، یہ میں ہوں اللہ ، زبردست اور دانا “۔');
INSERT INTO "sayyid_qutb" VALUES(27,10,'27:10','” تو ذرا اپنی لاٹھی اٹھا اور پھینک۔ “ ’ جونہی کہ موسیٰ نے دیکھا لاٹھی ، سانپ کی طرح بل کھا رہی ہے تو پیٹھ پھیر کر بھاگا اور پیچھے مڑ کر بھی نہ دیکھا۔ “ ’ اے موسیٰ ڈرو نہیں۔ میرے حضور رسول ڈرا نہیں کرتے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,11,'27:11','” الا یہ کہ کس نے قصور کیا ہو۔ پھر اگر برائی کے بعد اس نے بھلائی سے (اپنے فعل کو) بدل لیا تو میں معاف کرنے والا مہربان ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,12,'27:12','” اور ذرا اپنا ہاتھ اپنے گریبان میں تو ڈالو ، چمکتا ہوا نکلے گا بغیر کسی تکلیف کے۔ “ ” یہ (دو نشانیاں) نو نشانیوں میں سے ہیں فرعون اور اس کی قوم کی طرف (لے جانے کے لئے) وہ بڑے بدکردار لوگ ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,13,'27:13','” مگر جب ہماری کھلی کھلی نشانیاں ان لوگوں کے سامنے آئیں تو انہوں نے کہا کہ یہ تو کھلا جادو ہے');
INSERT INTO "sayyid_qutb" VALUES(27,14,'27:14','انہوں نے سراسر ظلم اور غرور کی راہ سے ان نشانیوں کا انکار کیا حالانکہ دل اس کے قائل ہوچکے تھے۔ اب دیکھ لو کہ ان مفسدوں کا انجام کیسا ہوا۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,15,'27:15','دوسری طرف) ہم نے دائود و سلیمان کو علم عطا کیا اور انہوں نے کہا کہ شکر ہے اس خدا کا جس نے ہم کو اپنے بہت سے مومن بندوں پر فضیلت عطا کی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,16,'27:16','” اور دائود کا وارث سلیمان ہوا اور اس نے کہا ” لوگو ، ہمیں پرندوں کی بولیاں سکھائی گئی ہیں اور ہمیں ہر طرح کی چیزیں دی گئی ہیں۔ بیشک یہ (اللہ کا) نمایاں فضل ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,17,'27:17','” سلیمان کے لئے جن اور انسانوں اور پرندوں کے لشکر جمع کئے گئے تھے اور وہ پورے ضبط میں رکھے جاتے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,18,'27:18','”(ایک مرتبہ وہ ان کے ساتھ کوچ کر رہا تھا) یہاں تک کہ جب یہ سب چیونٹیوں کی وادی میں پہنچے تو ایک چیونٹی نے کہا ” اے چیونٹیو ، اپنے بلوں میں گھس جائو “ کہیں ایسا نہ ہو کہ سلیمان اور اس کے لشکر تمہیں کچل ڈالیں اور انہیں خبر بھی نہ ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,19,'27:19','سلیمان اس کی بات پر مسکراتے ہوئے ہنس پڑا اور بولا ” اے میرے رب ، مجھے قابو میں رکھیں کہ میں تیرے اس احسان کا شکر ادا کرتا رہوں جو تو نے مجھ پر اور میرے والدین پر کیا ہے اور ایسا عمل صالح کروں جو تجھے پسند آئے اور اپنی رحمت سے مجھ کو اپنے صالح بندوں میں داخل کر۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,20,'27:20','”(ایک اور موقعہ پر) سلیمان السلام نے پرندوں کا جائزہ لیا اور کہا ” کیا بات ہے کہ میں فلاں ہد ہد کو نہیں دیکھ رہا ہوں ؟ کیا وہ کہیں غائب ہوگیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(27,21,'27:21','میں اسے سخت سزا دوں گا یا اسے ذبح کر دوں گا ، ورنہ اسے میرے سامنے معقول وجہ پیش کرنی ہوگی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,22,'27:22','’ کچھ زیادہ دیر نہ گزری تھی کہ اس نے آ کر کہا ” میں نے وہ معلومات حاصل کی ہیں جو آپ کے علم میں نہیں ہیں۔ میں سبا کے متعلق یقینی اطلاع لے کر آیا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(27,23,'27:23','میں نے وہاں ایک عورت دیکھی جو اس قوم کی حکمران ہے۔ اس کو ہر طرح کا سرو سامان بخشا گیا ہے اور اس کا تخت بڑا عظیم الشان ہے');
INSERT INTO "sayyid_qutb" VALUES(27,24,'27:24','میں نے دیکھا کہ وہ اور اس کی قوم اللہ کی بجائے سورج کے آگے سجدہ کرتی ہے۔ “ شیطان نے ان کے اعمال ان کے لئے خوشنما بنا دیئے اور انہیں شاہراہ سے روک دیا ، اس وجہ سے وہ یہ سیدھا راستہ نہیں پاتے');
INSERT INTO "sayyid_qutb" VALUES(27,25,'27:25','کہ اس خدا کو سجدہ کریں جو آسمانوں اور زمین کی پوشیدہ چیزیں نکالتا ہے اور وہ سب کچھ جانتا ہے جسے تم لوگ چھپاتے اور ظاہر کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(27,26,'27:26','اللہ کہ جس کے سوا کوئی مستحق عبادت نہیں ، جو عرش عظیم کا مالک ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,27,'27:27','سلیمان نے کہا ” ابھی ہم دیکھ لیتے ہیں کہ تو نے سچ کہا ہے یا تو جھوٹ بولنے والوں میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(27,28,'27:28','میرا یہ خط لے جا اور اسے ان لوگوں کی طرف ڈال دے ، پھر الگ ہٹ کر دیکھ کہ وہ کیا ردعمل ظاہر کرتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,29,'27:29','ملکہ بولی ” اے اہل دربار ، میری طرف ایک بڑا اہم خط پھینکا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(27,30,'27:30','وہ سلیمان کی جانب سے ہے اور اللہ رحمٰن و رحیم کے نام سے شروع کیا گیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(27,31,'27:31','مضمون یہ ہے کہ میرے مقابلے میں سرکشی نہ کرو اور مسلم ہو کر میرے پاس حاضر ہو جائو۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,32,'27:32','”(خط سنا کر) ملکہ نے کہا ” اے سرداران قوم ، میرے اس معاملے میں مجھ یمشورہ دو ، میں کسی معاملہ کا فیصلہ تمہارے بغیر نہیں کرتی ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,33,'27:33','” انہوں نے جواب دیا ” ہم طاقت ور اور لڑنے والے لوگ ہیں۔ آگے فیصلہ آپ کے ہاتھ میں ہے۔ آپ خود دیکھ لیں کہ آپ کو کیا حکم دینا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,34,'27:34','ملکہ نے کہا کہ ” بادشاہ جب کسی ملک میں گھس آتے ہیں تو اسے خراب اور اس کے عزت والوں کو ذلیل کردیتے ہیں ، یہی کچھ وہ کیا کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(27,35,'27:35','میں ان لوگوں کی طرف ایک ہد یہ بھیجتی ہوں ، پھر دیکھتی ہوں کہ میرے ایلچی کیا جواب لے کر پلٹتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(27,36,'27:36','” جب وہ (ملکہ کا سفیر) سلیمان کے ہاں پہنچا تو اس نے کہا ” کیا تم لوگ مال سے میری مدد کرنا چاہتے ہو ؟ جو کچھ خدا نے مجھے دے رکھا ہے وہ اس سے بہت زیادہ ہے جو تمہیں دیا ہے۔ تمہارا ہدیہ تم ہی کو مبارک رہے');
INSERT INTO "sayyid_qutb" VALUES(27,37,'27:37','(اے سفیر) واپس جا اپنے بھجینے والوں کی طرف۔ ہم ان پر ایسے لشکر لے کر آئیں گے جن کا مقابلہ وہ نہ کرسکیں گے اور ہم انہیں ایسی ذلت کے ساتھ وہاں سے نکالیں گے کہ وہ خوار ہو کر رہ جائیں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,38,'27:38','سلیمان نے کہا ” اے اہل دربار ، تم میں سے کون اس کا تخت میرے پاس لاتا ہے قبل اس کے کہ وہ لوگ مطیع ہو کہ میرے پاس حاضر ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(27,39,'27:39','جنوں میں سے ایک قوی ہیکل نے عرض کیا ” میں اسے حاضر کر دوں گا قبل اس کے کہ آپ اپنی جگہ سیاٹھیں میں اس کی طاقت رکھتا ہوں اور امانت دار ہوں');
INSERT INTO "sayyid_qutb" VALUES(27,40,'27:40','جس شخص کے پاس کتاب کا ایک علم تھا وہ بولا ” میں آپ کی پلک جھپکنے سے پہلے اسے لائے دیتا ہوں۔ “” جونہی کہ سلیمان (علیہ السلام) نے وہ تخت اپنے پاس رکھا ہوا دیکھا ، وہ پکار اٹھا ” یہ میرے رب کا فضل ہے تاکہ وہ مجھے آزمائے کہ میں شکر کرتا ہوں یا کافر نعمت بن جاتا ہے اور جو کوئی شکر کرتا ہے اس کا شکر اس کے اپنے ہی لئے مفید ہے ورنہ کوئی ناشکری کرے تو میرا رب بےنیاز اور اپنی ذات میں آپ بزرگ ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,41,'27:41','سلیمان نے کہا ” انجان طریقے سے اس کا تخت اس کے سامنے رکھ دو ، دیکھیں وہ صحیح بات تک پہنچتی ہے یا ان لوگوں میں سے ہے جو راست نہیں پاتے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,42,'27:42','” ملکہ جب حاضر ہوئی تو اس سے کہا گیا کہ تیرا تخت ایسا ہی ہے ؟ وہ کہنے لگی ” یہ تو گویا وہی ہے۔ “” ہم تو پہلے ہی جان گئے تھے اور ہم نے سر اطاعت جھکا دیا تھا۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,43,'27:43','” اس کو (ایمان لانے سے) جس چیز نے روک رکھا تھا وہ ان معبودوں کی عبادت تھی جنہیں وہ اللہ کے سوا پوجتی تھی ، کیوں کہ وہ ایک کافر قوم سے تھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,44,'27:44','اپنے پائنچے اٹھا لیے۔ سلیمان نے کہا ” یہ شیشے کا چکنا فرش ہے۔ “ اس پر وہ پکار اٹھی ” اے میرے رب (آج تک) میں اپنے نفس پر بڑا ظلم کرتی رہی ، اور اب میں نے سلیمان کے ساتھ اللہ رب العالمین کی اطاعت قبول کرلی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,45,'27:45','” اور ثمود کی طرف ہم نے ان کے بھائی صالح کو (یہ پیغام دے کر) بھیجا کہ اللہ بندگی کرو ، تو یکایک وہ دو متخاصم فریق بن گئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,46,'27:46','صالح نے کہا ” اے میری قوم کے لوگو ، بھلائی سے پہلے برائی کے لئے کیوں جلدی مچاتے ہو ؟ کیوں نہیں اللہ سے مغفرت طلب کرتے ؟ شاید کہ تم پر رحم فرمایا جائے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,47,'27:47','انہوں نے کہا ” ہم نے تو تم کو اور تمہارے ساتھیوں کو بدشگوفی کا نشان پایا ہے۔ “ ” تمہارے نیک و بدشگون کا سررشتہ تو اللہ کے پاس ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,48,'27:48','” اس شہر میں نوج تھے دار تھے جو ملک میں فساد پھیلاتے اور کوئی اصلاح کا کام نہ کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(27,49,'27:49','انہوں نے آپس میں کہا ” خدا کی قسم کھا کر عہد کرلو ہم صالح اور اس کے گھر والوں پر شیخون ماریں گے اور پھر اس کے ولی سے کہہ دیں گے کہ ہم اس کے خاندان کی ہلاکت کے موقع پر موجود نہ تھے ، ہم بالکل سچ کہتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,50,'27:50','” اور پھر ایک چال ہم نے چلی جس کی انہیں خبر نہ تھی۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,51,'27:51','” اب دیکھ لو کہ ان کی چال کا انجام کیا ہوا۔ ہم نے تباہ کر کے رکھ دیا ان کو اور ان کی پوری قوم کو۔');
INSERT INTO "sayyid_qutb" VALUES(27,52,'27:52','وہ ان کے گھر خالی پڑے ہیں اس ظلم کی پاداش میں جو وہ کرتے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,53,'27:53','اور ہم نے بچا لیا ایمان والوں اور ان لوگوں کو جو ڈرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(27,54,'27:54','” اور لوط کو ہم نے بھیجا۔ یاد کرو وہ وقت جب اس نے اپنی قوم سے کہا ” کیا تم آنکھوں دیکھتے بدکاری کرتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(27,55,'27:55','کیا تمہارا یہی چلن ہے کہ عورتوں کو چھوڑ کر مردوں کے پاس شہوت رانی کے لئے جاتے ہو ؟ حقیقت یہ ہے کہ تم لوگ سخت جہالت کا کام کرتے ہو۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,56,'27:56','” مگر اس کی قوم کا جواب کے سوا کچھ نہ تھا کہ انہوں نے کہا ” نکال دو لوط کے گھر والوں کو اپنی بستی سے یہ بڑے پاکباز بنتے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,57,'27:57','” آخر کار ہم نے بچا لیا اس کو اور اس کے گھر والوں کو ، بجز اس کی بیوی کے جس کا پیچھے رہ جانا ہم نے طے کردیا تھا');
INSERT INTO "sayyid_qutb" VALUES(27,58,'27:58','اور برسانی ان لوگوں پر ایک برسات بہت ہی بری برسات تھی۔ وہ ان لوگوں کے حق میں جو متنبہ کئے جا چکے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(27,59,'27:59','”(اے نبی صلى الله عليه وسلم کہو ، حمد ہے اللہ کے لئے اور سلام اس کے ان بندوں پر جنہیں اس نے برگزیدہ کیا۔ (ان سے پوچھو) اللہ بہتر ہے یو وہ معبور جنہیں یہ لوگ اس کا شریک بنا رہے ہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(27,60,'27:60','بھلا وہ کون ہے جس نے آسمانوں اور زمین کو پیدا کیا اور تمہارے لیے آسمان سے پانی برسایا پھر اس کے ذریعہ وہ خوشنما باغ اگائے جن کے درختوں کا اگانا تمہارے بس میں نہ تھا ؟ کیا اللہ کے ساتھ کوئی دوسرا خدا بھی (ان کاموں میں شریک) ہے ؟ (نہیں) بلکہ یہی لوگ راہ راست سے ہٹ کر چلے جا رہے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(27,61,'27:61','” اور وہ کون ہے جس نے زمین کو جائے قرار بنایا اور اس کے اندر دریا رواں کیے اور اس میں (پہاڑوں کی) میخیں گاڑ دیں اور پانی کے دو زخیروں کے درمیان پردے حائل کر دئیے ؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (ان کاموں میں شریک) ہے ؟ نہیں ، بلکہ ان میں سے اکثر لوگ نادان ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(27,62,'27:62','” کون ہے جو بےقرار کی دعا سنتا ہے جبکہ وہ اسے پکارے اور کون اس کی تکلیف رفع کرتا ہے ؟ اور (کون ہے جو) تمہیں زمین کا خلیفہ بناتا ہے ؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (یہ کام کرنے والا ) ہے ؟ تم لوگ کم ہی سوچتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(27,63,'27:63','” اور وہ کون ہے جو خشکی اور سمندر کی تاریکیوں میں تم کو راستہ دکھاتا ہے اور کون اپنی رحمت کے آگے ہواؤں کو خوشخبری لے کر بھیجتا ہے ؟ کیا اللہ کے ساتھ کوئی دوسرا خدا بھی (یہ کام کرتا) ہے ؟ بہت بالا و برتر ہے اللہ اس شرک سے جو یہ لوگ کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(27,64,'27:64','” اور وہ کون ہے جو خلق کی ابتدا کرتا اور پھر اس کا اعادہ کرتا ہے ؟ اور کون تم کو آسمانوں اور زمین سے رزق دیتا ہے ؟ کیا اللہ کے ساتھ کوئی اور خدا بھی (ان کاموں میں حصہ دار ) ہے ؟ کہو کہ لاؤ اپنی دلیل اگر تم سچے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(27,65,'27:65','ان سے کہو ، اللہ کے سوا آسمانوں اور زمین میں کوئی غیب کا علم نہیں رکھتا اور وہ (تمہارے معبود تو یہ بھی) نہیں جانتے کہ کب وہ اٹھائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(27,66,'27:66','بلکہ آخرت کا تو علم ہی ان لوگوں سے گم ہوگیا ہے ، بلکہ یہ اس کی طرف سے شک میں ہیں ، بلکہ یہ اس سے اندھے ہیں');
INSERT INTO "sayyid_qutb" VALUES(27,67,'27:67','” یہ منکر کہتے ہیں کیا جب ہم اور ہمارے باپ دادا مٹی ہوچکے ہوں گے تو ہمیں واقعی قبروں سے نکالا جائے گا “');
INSERT INTO "sayyid_qutb" VALUES(27,68,'27:68','” یہ خبریں ہم کو بھی دی گئی ہیں اور اس سے قبل ہمارے آباؤ اجداد کو بھی دی گئی ہیں لیکن یہ بس افسانے ہی افسانے ہیں جو اگلے وقتوں سے ہم سنتے چلے آ رہے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(27,69,'27:69','کہو ، ذرا زمین میں چل پھر کر دیکھو کہ مجرمین کا کیا انجام ہوچکا ہے');
INSERT INTO "sayyid_qutb" VALUES(27,70,'27:70','” اے نبی صلى الله عليه وسلم ان کے حال پر رنج نہ کرو ، اور نہ ان کی چالوں پر دل تنگ ہو “');
INSERT INTO "sayyid_qutb" VALUES(27,71,'27:71','” وہ کہتے ہیں یہ دھمکی کب پوری ہوگی اگر تم سچے ہو “');
INSERT INTO "sayyid_qutb" VALUES(27,72,'27:72','” کہو ، کیا عجیب ہے کہ جس عذاب کی تم جلدی مچا رہے ہو اس کا ایک حصہ تمہارے قریب ہی آلگا ہو “');
INSERT INTO "sayyid_qutb" VALUES(27,73,'27:73','” حقیقت یہ ہے کہ تیرا رب تو لوگوں پر بڑا فضل فرمانے والا ہے۔ مگر اکثر لوگ شکر نہیں کرتے “');
INSERT INTO "sayyid_qutb" VALUES(27,74,'27:74','بلاشبہ تیرا رب خوب جانتا ہے جو کچھ ان کے سینے اپنے اندر چھپائے ہوئے ہیں اور جو کچھ ظاہر کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(27,75,'27:75','” آسمان اور زمین کی کوئی پوشیدہ چیز ایسی نہیں ہے جو ایک واضح کتاب میں لکھی ہوئی نہ ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(27,76,'27:76','” یہ واقعہ ہے کہ یہ قرآن بنی اسرائیل کو اکثر ان باتوں کی حقیقت بتاتا ہے جن میں وہ اختلاف رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(27,77,'27:77','اور یہ ہدایت اور رحمت ہے ، ایمان لانے والوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(27,78,'27:78','۔ یقیناً (اسی طرح) تیرا رب ان لوگوں کے درمیان بھی اپنے حکم سے فیصلہ کر دے گا اور وہ زبردست اور سب کچھ جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(27,79,'27:79','پس اے نبی صلى الله عليه وسلم اللہ پر بھروسہ کرو ، یقینا تم صریح حق پر ہو');
INSERT INTO "sayyid_qutb" VALUES(27,80,'27:80','تم مردوں کو نہیں سنا سکتے ، نہ ان بہروں تک اپنی پکار پہنچا سکتے ہو ، جو پیٹھ پھیر کر بھاگے جا رہے ہوں');
INSERT INTO "sayyid_qutb" VALUES(27,81,'27:81','اور نہ اندھوں کو راستہ بتا کر بھٹکنے سے بچا سکتے ہو۔ تم تو اپنی بات انہی لوگوں کو سنا سکتے ہو جو ہماری آیات پر ایمان لاتے ہیں اور پھر فرماں بردار بن جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(27,82,'27:82','اور جب ہماری بات پوری ہونے کا وقت ان پر آپہنچے گا تو ہم ان کے لیے ایک جانور زمین سے نکالیں گے جو ان سے کلام کرے گا کہ لوگ ہماری آیات پر یقین نہیں کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(27,83,'27:83','اور ذرا تصور کرو اس دن کا جب ہم ہر امت میں سے ایک فوج کی فوج ان لوگوں کی گھیر لائیں گے جو ہماری آیات کو جھٹلایا کرتے تھے ، پھر ان کو (ان کی اقسام کی لحاظ سے درجہ بدرجہ) مرتب کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(27,84,'27:84','یہاں تک کہ جب سب آجائیں گے ، تو (ان کا رب ان سے) پوچھے گا کہ ” تم نے میری آیات کو جھٹلا دیا حالانکہ تم نے ان کا علمی احاطہ نہ کیا تھا ؟ اگر یہ نہیں تو اور تم کیا کر رہے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(27,85,'27:85','اور ان کے ظلم کی وجہ سے عذاب کا وعدہ ان پر پورا ہوجائے گا ، تب وہ کچھ بھی نہ بول سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(27,86,'27:86','کیا ان کو سجھائی نہ دیتا تھا کہ ہم نے رات ان کے لیے سکون حاصل کرنے کو بنائی تھی اور دن کو روشن کیا تھا ؟ اس میں بہت نشانیاں تھیں ان لوگوں کے لیے جو ایمان لاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(27,87,'27:87','اور کیا گزرے گی اس روز جب کہ صور پھونکا جائے گا اور ہول کھا جائیں گے وہ سب جو آسمانوں اور زمین میں ہیں ۔۔۔ سوائے ان لوگوں کے جنہیں اللہ اس ہول سے بچانا چاہے گا ۔۔۔ اور سب کان دبائے اس کے حضور حاضر ہوجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(27,88,'27:88','آج تو پہاڑوں کو دیکھتا ہے اور سمجھتا ہے کہ خوب جمے ہوئے ہیں ، مگر اس وقت یہ بادلوں کی طرح اڑ رہے ہوں گے ، یہ اللہ کی قدرت کا کرشمہ ہوگا جس نے ہر چیز کو حکمت کے ساتھ استوار کیا ہے۔ وہ خوب جانتا ہے کہ تم لوگ کیا کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(27,89,'27:89','جو شخص بھلائی لے کر آئے گا اسے اس سے زیادہ بہتر صلہ ملے گا اور ایسے لوگ اس دن کے ہول سے محفوظ ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(27,90,'27:90','اور جو برائی لیے ہوئے آئے گا ، ایسے سب لوگ اوندھے منہ آگ میں پھینکے جائیں گے۔ کیا تم لوگ اس کے سوا کوئی اور جزا پا سکتے ہو کہ جیسا کرو ویسا بھرو ؟');
INSERT INTO "sayyid_qutb" VALUES(27,91,'27:91','(اے نبی صلى الله عليه وسلم ، ان سے کہو) ” مجھے تو یہی حکم دیا گیا ہے کہ اس شہر (مکہ) کے رب کی بندگی کروں جس نے اسے جرم بنایا ہے اور جو ہر چیز کا مالک ہے۔ مجھے حکم دیا گیا ہے کہ میں مسلم بن کر رہوں');
INSERT INTO "sayyid_qutb" VALUES(27,92,'27:92','اور یہ قرآن پڑھ کر سناؤں “۔ اب جو ہدایت اختیار کرے گا وہ اپنے ہی بھلے کے لیے ہدایت اختیار کرے گا اور جو گمراہ ہو اس سے کہہ دو کہ ” میں تو بس خبردار کردینے والا ہوں “');
INSERT INTO "sayyid_qutb" VALUES(27,93,'27:93','ان سے کہو تعریف اللہ ہی کے لیے ہے ، عنقریب وہ تمہیں اپنی نشانیاں دکھا دے گا اور تم انہیں پہچان لوگے ْاور تیرا رب بیخبر نہیں ہےٗ ان اعمال سے جو تم لوگ کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(28,1,'28:1','ط۔ س۔ م ، یہ کتاب مبین کی آیات ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,2,'28:2','یہ کتاب مبین کی آیات ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,3,'28:3','ہم موسیٰ اور فرعون کا کچھ حال ٹھیک ٹھیک تمہیں سناتے ہیں ، ایسے اسے لوگوں کے فائدے کے لیے جو ایمان لائیں');
INSERT INTO "sayyid_qutb" VALUES(28,4,'28:4','اور ان سے فرعون اور ہامان اور ان کے لشکروں کی وہی کچھ دکھلا دیں جس کا انہیں ڈر تھا');
INSERT INTO "sayyid_qutb" VALUES(28,5,'28:5','اور ہم یہ ارادہ رکھتے تھے کہ مہربانی کریں ان لوگوں پر جو زمین میں ذلیل کرکے رکھے گئے تھے اور انہیں پیشوا بنا دیں اور انہی کو وارث بنائیں');
INSERT INTO "sayyid_qutb" VALUES(28,6,'28:6','اور زمین میں ان کو اقتدار بخشیں اور ان سے فرعون و ہامان اور ان کے لشکروں کو وہی کچھ دکھلا دیں جس کا انہیں ڈر تھا');
INSERT INTO "sayyid_qutb" VALUES(28,7,'28:7','ہم نے موسیٰ کی ماں کو اشارہ کیا کہ ” اس کو دودھ پلا “ پھر جب تجھے اس کی جان کا خطرہ ہو تو اسے دریا میں ڈال دے اور کچھ خوف اور غم نہ کر ہم اسے تیرے ہی پاس لے آئیں گے اور اس کو پیغمبروں میں شامل کریں گے');
INSERT INTO "sayyid_qutb" VALUES(28,8,'28:8','آخر کار فرعون کے گھر والوں نے اسے (دریا سے) نکال لیا تاکہ وہ ان کا دشمن اور ان کے لیے سبب رنج بنے واقعی فرعون اور ہامان اور ان کے لشکر (اپنی تدبیر میں) بڑے غلط کار تھے');
INSERT INTO "sayyid_qutb" VALUES(28,9,'28:9','فرعون کی بیوی نے (اس سے) کہا ، ” یہ میرے اور تیرے لیے آنکھوں کی ٹھنڈک ہے ، اسے قتل نہ کرو ، کیا عجب کہ یہ ہمارے لیے مفید ثابت ہو یا ہم اسے بیٹا ہی بنا لیں۔ اور وہ (انجام سے) بیخبر تھے');
INSERT INTO "sayyid_qutb" VALUES(28,10,'28:10','ادھر موسیٰ کی ماں کا دل اڑا جا رہا تھا ، وہ اس کا راز فاش کر بیٹھتی ، اگر ہم اس کی ڈھارس نہ بندھا دیتے تاکہ وہ (ہمارے وعدے پر) ایمان لانے والوں میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(28,11,'28:11','اس نے بچے کی بہن سے کہا اس کے پیچھے پیچھے جائے۔ چناچہ وہ الگ سے اس کو اس طرح دیکھتی رہی کہ (دشمنوں کو) اس کا پتہ نہ چلا');
INSERT INTO "sayyid_qutb" VALUES(28,12,'28:12','اور ہم نے بچے پر پہلے ہی دودھ پلانے والیوں کی چھاتیاں حرام کر رکھی تھیں۔ (یہ حالت دیکھ کر) اس لڑکی نے ان سے کہا ” میں تمہیں ایسے گھر کا پتہ بتاؤں جس کے لوگ اس کی پرورش کا ذمہ لیں اور خیر خواہی کے ساتھ اسے رکھیں');
INSERT INTO "sayyid_qutb" VALUES(28,13,'28:13','” اس طرح ہم موسیٰ کو اس کی ماں کے پاس پلٹا لائے تاکہ اس کی آنکھیں ٹھنڈی ہوں اور وہ غمگین نہ ہو اور جان لے کہ اللہ کا وعدہ سچا تھا ، مگر اکثر لوگ اس بات کو نہیں جانتے “۔');
INSERT INTO "sayyid_qutb" VALUES(28,14,'28:14','جب موسیٰ اپنی پوری جوانی کو پہنچ گیا اور اس کی نشوونما مکمل ہوگئی تو ہم نے اسے حکم اور علم عطا کیا ، ہم نیک لوگوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,15,'28:15','(ایک روز) وہ شہر میں ایسے وقت داخل ہوا جبکہ اہل شہر غفلت میں تھے۔ وہاں اس نے دیکھا کہ وہ آدمی لڑ رہے ہیں۔ ایک اس کی اپنی قوم کا تھا اور دوسرا اس کی دشمن قوم سے تعلق رکھتا تھا۔ اس کی قوم کے آدمی نے دشمن قوم والے کے خلاف اسے مدد کے لیے پکارا۔ موسیٰ نے اس کو ایک گھونسا مارا اور اس کا کام تمام کردیا۔ (یہ حرکت سرزد ہوتے ہی) موسیٰ نے کہا ، ” یہ شیطان کی کارفرمائی ہے ، وہ سخت دشمن اور کھلا گمراہ کن ہے');
INSERT INTO "sayyid_qutb" VALUES(28,16,'28:16','پھر وہ کہنے لگا ” سے میرے رب ، میں نے اپنے نفس پر ظلم کر ڈالا ، میری مغفرت فرما دے “۔ چناچہ اللہ نے اس کی مغفرت فرما دی');
INSERT INTO "sayyid_qutb" VALUES(28,17,'28:17','وہ غفور و رحیم ہے۔ موسیٰ نے عہد کیا کہ ” اے میرے رب ، یہ احسان جو تو نے مجھ پر کیا ہے ، اسکے بعد اب میں کبھی مجرموں کا مددگار نہ بنوں گا');
INSERT INTO "sayyid_qutb" VALUES(28,18,'28:18','دوسرے روز وہ صبح سویرے ڈرتا اور ہر طرف سے خطرہ پھانپتا ہوا شہر میں جا رہا تھا کہ یکایک کیا دیکھتا ہے کہ وہی شخص جس نے کل اسے مدد کے لیے پکارا تھا ، آج پھر اسے پکار رہا ہے۔ موسیٰ نے کہا ” تو تو بڑا ہی بہکا ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(28,19,'28:19','پھر جب موسیٰ نے ارادہ کیا کہ دشمن قوم کے آدمی پر حملہ کرے تو وہ پکار اٹھا ” اے موسیٰ ! کیا آج تو مجھے اسی طرح قتل کرنے لگا ہے جس طرح کل ایک شخص کو قتل کرچکا ہے تو اس ملک میں جبار بن کر رہنا چاہتا ہے ، اصلاح کرنا نہیں چاہتا');
INSERT INTO "sayyid_qutb" VALUES(28,20,'28:20','اس کے بعد ایک آدمی شہر کے پرلے سرے سے دوڑتا ہوا آیا اور بولا ” اے موسیٰ ، سرداروں میں تیرے قتل کے مشورے ہو رہے ہیں ، یہاں سے نکل جا ، میں تیرا خیر خواہ ہوں');
INSERT INTO "sayyid_qutb" VALUES(28,21,'28:21','یہ خبر سنتے ہی ڈرتا اور سہمتا نکل کھڑا ہوا اور اس نے دیا کی کہ ” اے میرے رب ، مجھے ظالموں سے بچا');
INSERT INTO "sayyid_qutb" VALUES(28,22,'28:22','(مصر سے نکل کر ) جب موسیٰ نے مدین کا رخ کیا تو اس نے کہا ” امید ہے کہ میرا رب ، مجھے ٹھیک راستے پر ڈال دے گا');
INSERT INTO "sayyid_qutb" VALUES(28,23,'28:23','اور جب وہ مدین کے کنویں پر پہنچا تو اس نے دیکھا کہ بہت سے لوگ اپنے جانوروں کو پانی پلا رہے ہیں اور ان سے الگ ایک طرف دو عورتیں اپنے جانوروں کو روک رہی ہیں۔ موسیٰ (علیہ السلام) نے ان عورتوں سے پوچھا ” تمہیں کیا پریشانی ہے ؟ “ انہوں نے کہا ” ہم اپنے جانوروں کو پانی نہیں پلا سکتیں جب تک یہ چروا ہے اپنے جانور نہ نکال نے جائیں ، اور ہمارے والد ایک بہت بوڑھے آدمی ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,24,'28:24','یہ سن کر موسیٰ (علیہ السلام) نے ان کے جانوروں کو پانی پلا دیا ، پھر ایک سائے کی جگہ جابیٹھے اور بولے ” پروردگار جو خیر بھی تو مجھ پر نازل کر دے میں اس کا محتاج ہوں');
INSERT INTO "sayyid_qutb" VALUES(28,25,'28:25','(کچھ دیر نہ گزری تھی) ان دونوں عورتوں میں سے ایک شرم و حیا کے ساتھ چلتی ہوئی اس کے پاس آئی اور کہنے لگی “۔ میرے والد آپ کو بلا رہے ہیں تاکہ آپ نے ہمارے لیے جانوروں کو پانی جو پلایا ہے اس کا اجر آپ کو دیں موسیٰ جب اس کے پاس پہنچا اور اپنا سارا قصہ اسے سنایا تو اس نے کہا ” کچھ خوف نہ کرو ، اب تم ظالم لوگوں سے بچ نکلے ہو');
INSERT INTO "sayyid_qutb" VALUES(28,26,'28:26','ان دونوں عورتوں میں سے ایک نے اپنے باپ سے کہا ” ابا جان ، اس شخص کو نوکر رکھ لیجئے ، بہترین آدمی ہے آپ ملازم رکھیں وہی ہو سکتا ہے جو مضبوط اور امانت دار ہو');
INSERT INTO "sayyid_qutb" VALUES(28,27,'28:27','اس کے باپ نے (موسیٰ سے) کہا ” میں چاہتا ہوں کہ اپنی ان دو بیٹیوں میں سے ایک کا نکاح تمہارے ساتھ کردوں بشرطیکہ تم آٹھ سال تک میرے ہاں ملازمت کرو ، اور اگر دس سال پورے کردو تو یہ تمہاری مرضی ہے۔ میں تم پر سختی نہیں کرنا چاہتا۔ تم انشاء اللہ مجھے نیک آدمی پاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(28,28,'28:28','موسیٰ نے جواب دیا ” یہ بات میرے اور آپ کے درمیان طے ہوگئی۔ ان دونوں مدتوں میں سے جو بھی پوری کر دوں اس کے بعد پھر کوئی زیادتی مجھ پر نہ ہو ، اور جو کچھ قول قرار ہم کر رہے ہیں ، اللہ اس پر نگہبان ہے');
INSERT INTO "sayyid_qutb" VALUES(28,29,'28:29','جب موسیٰ نے مدت پوری کردی اور وہ اپنے اہل و عیال کو لے کر چلا تو طور کی جانب اس کو ایک آگ نظر آئی۔ اس نے اپنے گھر والوں سے کہا ” ٹھہرو ، میں نے ایک آگ دیکھی ہے ، شاید میں وہاں سے کوئی خبر لے آؤں یا اس آگ سے کوئی انگارہ ہی اٹھا لاؤں جس سے تم تاپ سکو');
INSERT INTO "sayyid_qutb" VALUES(28,30,'28:30','وہاں پہنچا تو وادی کے دہنے کنارے پر مبارک خطے میں ایک درخت سے پکارا گیا کہ ” اے موسیٰ ، میں ہی اللہ ہوں ، سارے جہاں والوں کا مالک');
INSERT INTO "sayyid_qutb" VALUES(28,31,'28:31','اور (حکم دیا گیا کہ) پھینک دے اپنی لاٹھی۔ جوں ہی کہ موسیٰ نے دیکھا کہ وہ لاٹھی سانپ کی طرح بل کھا رہی ہے تو وہ پیٹھ پھیر کر بھاگا اور اس نے مڑ کر بھی نہ دیکھا۔ (ارشاد ہوا) ” موسیٰ ، پلٹ آؤ اور خوف نہ کرو ، تو بالکل محفوظ ہے');
INSERT INTO "sayyid_qutb" VALUES(28,32,'28:32','اپنا ہاتھ گریبان میں ڈال ، چمکتا ہوا نکلے گا بغیر کسی تکلیف کے ، اور خوف سے بچنے کے لیے اپنا بازو بھینچ لے۔ یہ دو روشن نشانیاں ہیں تیرے رب کی طرف سے فرعون اور اس کے درباریوں کے سامنے پیش کرنے کے لئے ، وہ بڑے نافرمان لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,33,'28:33','موسیٰ نے عرض کیا ” میرے آقا ، میں تو ان کا ایک آدمی قتل کرچکا ہوں ، ڈرتا ہوں کہ وہ مجھے مار ڈالیں گے');
INSERT INTO "sayyid_qutb" VALUES(28,34,'28:34','اور میرا بھائی ہارون مجھ سے زیادہ زبان آور ہے ، اسے میرے ساتھ مددگار کے طور پر بھیج تاکہ وہ میری تائید کرے ، مجھے اندیشہ ہے کہ وہ لوگ مجھے جھٹلائیں گے');
INSERT INTO "sayyid_qutb" VALUES(28,35,'28:35','فرمایا ” ہم تیرے بھائی کے ذریعہ سے تیرا ہاتھ مضبوط کریں گے اور تم دونوں کو ایسی سعادت بخشیں گے کہ وہ تمہارا کچھ نہ بگاڑ سکیں گے۔ ہماری نشانیوں کے زور سے غلبہ تمہارا اور تمہارے پیروؤں کا ہی ہوگا');
INSERT INTO "sayyid_qutb" VALUES(28,36,'28:36','پھر جب موسیٰ ۔۔۔۔ ان لوگوں کے پاس ہماری کھلی کھلی نشانیاں لے کر پہنچا تو انہوں نے کہا کہ ” یہ کچھ نہیں ہے مگر بناؤٹی جادو۔ اور یہ باتیں تو ہم نے اپنے باپ دادا کے زمانے میں کبھی سنیں ہی نہیں');
INSERT INTO "sayyid_qutb" VALUES(28,37,'28:37','موسیٰ نے جواب دیا ” میرا رب اس شخص کے حال سے خوف واقف ہے جو اس کی طرف سے ہدایت لے کر آیا ہے اور وہی بہتر جانتا ہے کہ آخری انجام کس کا اچھا ہوتا ہے ، حق یہ ہے کہ ظالم کبھی فلاح نہیں پاتے');
INSERT INTO "sayyid_qutb" VALUES(28,38,'28:38','اور فرعون نے کہا ” اے اہل دربار ، میں تو اپنے سوا تمہارے کسی خدا کو نہیں جانتا۔ ہامان ، ذرا اینٹیں پکوا کر میرے لیے ایک اونچی عمارت تو بنوا ، شاید کہ اس پر چڑھ کر میں موسٰٰ کے خدا کو دیکھ سکوں ، میں تو اسے جھوٹ سمجھتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(28,39,'28:39','اس نے اور اس کے لشکروں نے زمین میں بغیر کسی حق کے اپنی بڑائی کس گھمنڈ کیا اور سمجھے کہ انہیں کبھی ہماری طرف پلٹنا نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(28,40,'28:40','آخر کار ہم نے اسے اور اس کے لشکروں کو پکڑا اور سمندر میں پھینک دیا۔ اب دیکھ لو کہ ان ظالموں کا کیسا انجام ہوا');
INSERT INTO "sayyid_qutb" VALUES(28,41,'28:41','ہم نے انہیں جہنم کی طرف دعوت دینت والے پیش رو بنا دیا اور قیامت کے روز وہ کہیں سے کوئی مدد نہ پا سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(28,42,'28:42','ہم نے اس دنیا میں ان کے پیچھے لعنت لگا دی اور قیامت کے روز وہ بڑی قباحت میں مبتلا ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(28,43,'28:43','پچھلی نسلوں کو ہلاک کرنے کے بعد ہم نے موسیٰ کو کتاب عطا کی ، لوگوں کے لیے بصیرتوں کا سامان بنا کر ، ہدایت اور رحمت بنا کر ، تاکہ شاید لوگ سبق حاصل کریں');
INSERT INTO "sayyid_qutb" VALUES(28,44,'28:44','(اے نبی صلى الله عليه وسلم تم اس وقت مغربی گوشے میں موجود نہ تھے جب ہم نے موسیٰ کو یہ فرمان شریعت عطا کیا ، اور نہ تم شاہدین میں شامل تھے');
INSERT INTO "sayyid_qutb" VALUES(28,45,'28:45','بلکہ اس کے بعد (تمہارے زمانے تک) ہم بہت سی نسلیں اٹھا چکے ہیں اور ان پر بہت زمانہ گزر چکا ہے۔ تم اہل مدین کے درمیان بھی موجود نہ تھے کہ ان کو ہماری ایات سنا رہے ہوتے ، مگر (اس وقت کی یہ خبریں) بھیجنے والے ہم ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,46,'28:46','اور تم طور کے دامن میں بھی اس وقت موجود نہ تھے جب ہم نے (موسیٰ کو پہلی مرتبہ) پکارا تھا ، مگر یہ تمہارے رب کی رحمت ہے (کہ تم کو یہ معلوماے دی جار ہی ہیں) تاکہ تم ان لوگوں کو متنبہ کر دو جن کے پاس تم سے پہلے کوئی متنبہ کرنے والا نہیں آیا ، شاید کہ وہ ہوش میں آئیں');
INSERT INTO "sayyid_qutb" VALUES(28,47,'28:47','(اور یہ ہم نے اسلئے کیا کہ) کہیں ایسانہ ہو کہ انکے اپنے کیے کرتوتوں کی بدولت کوئی مصیبت جب ان پر آئے تو وہ کہیں ” اے پروردگار ، تو نے کیوں نہ ہماری طرف کوئی رسول بھیجا کہ ہم تیری آیات کی پیروی کرتے اور اہل ایمان میں سے ہوتے');
INSERT INTO "sayyid_qutb" VALUES(28,48,'28:48','مگر جب ہمارے ہاں سے حق ان کے پاس آگیا تو وہ کہنے لگے ” کیوں نہ دیا گیا اس کو وہی کچھ جو موسیٰ کو دیا گیا تھا ؟ “ کیا یہ لوگ اس کا انکار کرچکے ہیں جو اس سے پہلے موسیٰ کو دیا گیا تھا ؟ انہوں نے کہا ” دونوں جادو ہیں جو ایک دوسرے کی مدد کرتے ہیں اور کہا ” ہم کسی کو نہیں مانتے');
INSERT INTO "sayyid_qutb" VALUES(28,49,'28:49','(اے نبی صلى الله عليه وسلم ان سے کہو ، ” اچھا تو لاؤ اللہ کی طرف سے کوئی کتاب جو ان دونوں سے زیادہ ہدایت بخشنے والی ہو اگر تم سچے ہو میں اس کی پیروی اختیار کروں گا');
INSERT INTO "sayyid_qutb" VALUES(28,50,'28:50','اب اگر وہ تمہارا یہ مطالبہ پورا نہیں کرتے تو سمجھ لو کہ دراصل یہ اپنی خواہشات کے پیرو ہیں ، اور اس شخص سے بڑھ کر کون گمراہ ہوگا جو خدائی ہدایت کے بغیر بس اپنی خواہشات کی پیروی کرے ؟ اللہ ایسے ظالموں کو ہرگز ہدایت نہیں بخشا');
INSERT INTO "sayyid_qutb" VALUES(28,51,'28:51','اور (نصیحت کی) بات پے درپے ہم انہیں پہنچا چکے ہیں تاکہ وہ غفلت سے بیدار ہوں');
INSERT INTO "sayyid_qutb" VALUES(28,52,'28:52','جن لوگوں کو اس سے پہلے ہم نے کتاب دی تھی ، وہ اس (قرآن) پر ایمان لاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(28,53,'28:53','اور جب یہ ان کو سنایا جاتا ہے تو وہ کہتے ہیں کہ ” ہم اس پر ایمان لائے ، یہ واقعی حق ہے ہمارے رب کی طرف سے ہم تو پہلے ہی سے مسلم ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,54,'28:54','یہ وہ لوگ ہیں جنہیں ان کا اجر دوبارہ دیا جائے گا اس ثابت قدمی کے بدلے جو انہوں نے دکھائی۔ وہ برائی کو بھلائی سے دفع کرتے ہیں اور جو کچھ رزق ہم نے انہیں دیا ہے اس میں سے خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,55,'28:55','اور جب انہوں نے بیہودہ بات سنی تو یہ کہہ کر اس سے کنارہ کش ہوگئے کہ ” ہمارے اعمال ہمارے لیے اور تمہارے اعمال تمہارے لیے ، تم کو سلام ہے ، ہم جاہلوں کا سا طریقہ اختیار کرنا نہیں چاہتے');
INSERT INTO "sayyid_qutb" VALUES(28,56,'28:56','اے نبی صلى الله عليه وسلم ، تم جسے چاہو ، اسے ہدایت نہیں دے سکتے ، مگر اللہ جسے چاہتا ہے ، ہدایت دیتا ہے اور وہ ان لوگوں کو خوب جانتا ہے جو ہدایت قبول کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,57,'28:57','وہ کہتے ہیں ” اگر ہم تمہارے ساتھ اس ہدایت کی پیروی اختیار کرلیں تو اپنی زمین سے اچک لیے جائیں گے “۔ کیا یہ واقعہ نہیں ہے کہ ہم نے ایک پر امن حرم کو ان کے لیے جائے قیام بنا دیا جس کی طرف ہر طرح کے ثمرات کھچے چلے آتے ہیں ، ہماری طرف سے رزق کے طور پر ؟ مگر ان میں سے اکثر لوگ جانتے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(28,58,'28:58','اور کتنی ہی ایسی بستیاں ہم تباہ کرچکے ہیں جن کے لوگ اپنی معیشت پر اترا گئے تھے۔ سو دیکھ لو ، وہ ان کے مسکن پڑے ہوئے ہیں جن میں ان کے بعد کم ہی کوئی بسا ہے ؛ آخر کار ہم ہی وارث ہوکر رہے');
INSERT INTO "sayyid_qutb" VALUES(28,59,'28:59','اور تیرا رب بستیوں کو ہلاک کرنے والا نہ تھا جب تک کہ ان کے مرکز میں ایک رسول نہ بھیج دیتا جو ان کو ہماری آیات سناتا اور ہم بستیوں کو ہلاک کرنے والے نہ تھے جب تک کہ ان کے رہنے والے ظالم نہ ہوجاتے۔');
INSERT INTO "sayyid_qutb" VALUES(28,60,'28:60','تم لوگوں کو جو کچھ بھی دیا گیا ہے وہ محض دنیا کی زندگی کا سامان اور اس کی زینت ہے اور جو کچھ اللہ کے پاس ہے وہ اس سے بہتر اور باقی تر ہے۔ کیا تم لوگ عقل سے کام نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(28,61,'28:61','بھلا وہ شخص جس سے ہم نے اچھا وعدہ کیا ہو اور وہ اسے پانے والا ہو ، کبھی اس شخص کی طرح ہو سکتا ہے جسے ہم نے صرف حیات دنیا کا سروسامان دے دیا ہو اور پھر وہ قیامت کے روزسزا کیلئے پیش کیا جانے والا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(28,62,'28:62','اور (بھول نہ جائیں یہ لوگ ) اس دن کو جب کہ وہ ان کو پکارے گا اور پوچھے گا ” کہاں ہیں میرے وہ شریک جن کا تم گمان رکھتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(28,63,'28:63','یہ قول جن پر چسپاں ہوگا وہ کہیں گے ” اے ہمارے رب ، بیشک یہی لوگ ہیں جن کو ہم نے گمراہ کیا تھا۔ انہیں ہم نے اسی طرح گمراہ کیا جیسے ہم خود گمراہ ہوئے ۔ ہم آپ کے سامنے برات کا اظہار کرتے ہیں ۔ یہ ہماری توبندگی نہیں کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(28,64,'28:64','پھر ان سے کہا جائے گا کہ پکار دواب اپنے ٹھہرائے ہوئے شریکون کو یہ انہیں پکاریں گے مگر وہ ان کو کوئی جواب نہ دیں گے اور یہ لوگ عذاب دیکھ لیں گے۔ کاش ہدایت اختیار کرنے والے ہوتے');
INSERT INTO "sayyid_qutb" VALUES(28,65,'28:65','اور (فراموش نہ کریں یہ لوگ ) وہ دن جب کہ وہ ان کو پکارے گا اور پوچھے گا کہ ” جو رسول بھیجے گئے تھے انہیں تم نے کیا جواب دیا تھا ؟');
INSERT INTO "sayyid_qutb" VALUES(28,66,'28:66','اس وقت کوئی جواب ان کو نہ سوجھے گا اور نہ یہ آپس میں ایک دوسرے سے پوچھ ہی سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(28,67,'28:67','البتہ جس نے آج تو بہ کرلی اور ایمان لے آیہ اور نیک عمل کیے وہی یہ توقع کرسکتا ہے کہ وہاں فلاح پانے والوں میں سے ہوگا');
INSERT INTO "sayyid_qutb" VALUES(28,68,'28:68','تیرا رب پیدا کرتا ہے جو کچھ چاہتا ہے اور (وہ خود ہی اپنے کام کے لیے جسے چاہتا ہے) منتخب کرلیتا ہے ، یہ انتخاب ان لوگوں کے کرنے کا کام نہیں ہے۔ اللہ پاک ہے اور بہت بالا تر ہے اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,69,'28:69','تیرا رب جانتا ہے جو کچھ یہ دلوں میں چھپائے ہوئے ہیں اور جو کچھ یہ ظاہر کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(28,70,'28:70','وہی ایک اللہ ہے جس کے سوا کوئی عبادت کا مستحق نہیں۔ اسی کیلئے حمد ہے۔ دنیا میں بھی اور آخرت میں بھی ، فرماں روائی اس کی ہے اور اسی کی طرف تم سب پلٹائے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(28,71,'28:71','اے نبی صلى الله عليه وسلم ، ان سے کہو کبھی تم لوگوں نے غور کیا کہ اگر اللہ قیامت تک تم پر ہمیشہ کے لیے رات طاری کر دے تو اللہ کے سوا وہ کون سا معبود ہے جو تمہیں روشنی لا دے ؟ کیا تم سنتے نہیں ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(28,72,'28:72','ان سے پوچھو ، کبھی تم نے سوچا کہ اگر اللہ قیامت تک تم پر ہمیشہ کے لیے دن طاری کر دے تو اللہ کے سوا وہ کون سا معبود ہے جو تمہیں رات لا دے تاکہ تم اس میں سکون حاصل کرسکو ؟ کیا تم کو سوجھتا نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(28,73,'28:73','یہ اسی کی رحمت ہے کہ اس نے تمہارے لیے رات اور دن بنائے تاکہ تم (رات میں) سکون حاصل کرو اور (دن کو) اپنے رب کا فضل تلاش کرو ، شاید کہ تم شکر گزار بنو');
INSERT INTO "sayyid_qutb" VALUES(28,74,'28:74','(یاد رکھیں یہ لوگ) وہ دن جب کہ وہ انہیں پکارے گا ، پھر پوچھے گا ” کہاں ہیں میرے وہ شریک جن کا تم گمان رکھتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(28,75,'28:75','اور ہم ہر امت میں سے ایک گواہ نکال لائیں گے پھر کہیں گے کہ ” لاؤ اب اپنی دلیل “۔ اس وقت انہیں معلوم ہوجائے گا کہ حق اللہ کی طرف ہے ، اور گم ہوجائیں گے ان کے وہ سارے جھوٹ جو انہوں نے گھڑ رکھے تھے');
INSERT INTO "sayyid_qutb" VALUES(28,76,'28:76','یہ ایک واقعہ ہے کہ قارون موسیٰ (علیہ السلام) کی قوم کا ایک شخص تھا ، پھر وہ اپنی قوم کے خلاف سرکش ہوگیا۔ اور ہم نے اس کو اتنے خزانے دے رکھے تھے کہ ان کی کنجیاں طاقت ور آدمیوں کی ایک جماعت مشکل سے اٹھا سکتی تھی۔ ایک دفعہ جب اس کی قوم کے لوگوں نے اس سے کہا ” پھول نہ جا ، اللہ پھولنے والوں کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(28,77,'28:77','جو مال اللہ نے تجھے دیا ہے اس سے آخرت کا گھر بنانے کی فکر کر اور دنیا میں بھی اپنا حصہ فراموش نہ کر۔ احسان کر ، جس طرح اللہ نے تیرے ساتھ احسان کیا ہے ، اور زمین میں فساد برپا کرنے کی کوشش نہ کر ، اللہ مفسدوں کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(28,78,'28:78','تو اس نے کہا ” یہ سب کچھ تو مجھے اس علم کی بنا پر دیا گیا ہے جو مجھ کو حاصل ہے کیا اس کو یہ علم نہ تھا کہ اللہ اس سے پہلے بہت سے ایسے لوگوں کو ہلاک کرچکا ہے جو اس سے زیادہ قوت اور جمعیت رکھتے تھے ؟ مجرموں سے تو ان کے گناہ نہیں پوچھے جاتے');
INSERT INTO "sayyid_qutb" VALUES(28,79,'28:79','ایک روز وہ اپنی قوم کے سامنے اپنے پورے ٹھاٹھ میں نکلا۔ جو لوگ حیات دنیا کے طالب تھے وہ اسے ایکھ کر کہنے لگے ” کاش ہمیں بھی وہی کچھ ملتا جو قارون کو دیا گیا ہے ، یہ تو بڑا نصیب والا ہے');
INSERT INTO "sayyid_qutb" VALUES(28,80,'28:80','مگر جو لوگ علم رکھنے والے تھے وہ کہنے لگے ” افسوس تمہارے حال پر ، اللہ کا ثواب بہتر ہے اس شخص کے لیے جو ایمان لائے اور نیک عمل کرے اور یہ دولت نہیں ملتی مگر صبر کرنے والوں کو');
INSERT INTO "sayyid_qutb" VALUES(28,81,'28:81','آخر کار ہم نے اسے اور اس کے گھر کو زمین میں دھنسا دیا۔ پھر کوئی اس کے حامیوں کا گروہ نہ تھا جو اللہ کے مقابلے میں اس کی مدد کو آتا اور نہ وہ خود اپنی مدد آپ کر سکا');
INSERT INTO "sayyid_qutb" VALUES(28,82,'28:82','اب وہی لوگ جو کل اس کی منزلت کی تمنا کر رہے تھے ، کہنے لگے ” افسوس ، ہم بھول گئے تھے کہ اللہ اپنے بندوں میں سے جس کا رزق چاہتا ہے ، کشادہ کردیتا ہے اور جسے چاہتا ہے نپا تلا دیتا ہے۔ اگر اللہ نے ہم پر احسان نہ کیا ہوتا تو ہمیں بھی زمین میں دھنسا دیتا۔ افسوس ہم کو یاد نہ رہا کہ کافر فلاح نہیں پایا کرتے');
INSERT INTO "sayyid_qutb" VALUES(28,83,'28:83','وہ آخرت کا گھر تو ہم ان لوگوں کے لیے مخصوص کردیں گے جو زمین میں اپنی بڑائی نہیں چاہتے اور نہ فساد کرنا چاہتے ہیں اور انجام کی بھلائی متقین ہی کے لیے ہے');
INSERT INTO "sayyid_qutb" VALUES(28,84,'28:84','جو کوئی بھلائی لے کر آئے گا اس کے لیے اس سے بہتر بھلائی ہے ، اور جو برائی لے کر آئے تو برائیاں کرنے والوں کو ایسا ہی بدلہ ملے گا جیسے عمل وہ کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(28,85,'28:85','اے نبی ، یقین جانو کہ جس نے یہ قرآن تم پر فرض کیا ہے وہ تمہیں ایک بہترین انجام کو پہنچانے والا ہے “ (یعنی مکہ تک) ۔ ان لوگوں سے کہہ دو کہ “ میرا رب خوب جانتا ہے کہ ہدایت لے کر کون آیا ہے اور کھلی گمراہی میں کون مبتلا ہے');
INSERT INTO "sayyid_qutb" VALUES(28,86,'28:86','تم اس بات کے ہرگز امیدوار نہ تھے کہ تم پر کتاب نازل کی جائے گی ، یہ تو محض تمہارے رب کی مہربانی سے (تم پر نازل ہوئی ہے) پس تم کافروں کے مددگار نہ بنو');
INSERT INTO "sayyid_qutb" VALUES(28,87,'28:87','اور ایسا کبھی نہ ہونے پائے کہ اللہ کی آیات جب تم پر نازل ہوں تو کفار تمہیں ان سے باز رکھیں۔ اپنے رب کی طرف دعوت دو اور ہرگز مشرکوں میں شامل نہ ہو');
INSERT INTO "sayyid_qutb" VALUES(28,88,'28:88','اور اللہ کے ساتھ کسی دوسرے معبود کو نہ پکارو۔ اس کے سوا کوئی معبود نہیں ہے۔ ہر چیز ہلاک ہونے والی ہے سوائے اس کی ذات کے۔ فرمانروائی اسی کی ہے اور اسی کی طرف تم سب پلٹائے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(29,1,'29:1','ا۔ ل۔ م');
INSERT INTO "sayyid_qutb" VALUES(29,2,'29:2','کیا لوگوں نے یہ سمجھ رکھا ہے کہ وہ بس اتنا کہنے پر چھوڑ دئیے جائیں گے کہ ” ہم ایمان لائے “ اور ان کو آزمایا نہ جائے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(29,3,'29:3','حالانکہ ہم ان سب لوگوں کی آزمائش کرچکے ہیں جو ان سے پہلے گزرے ہیں۔ اللہ کو تو ضرور یہ دیکھنا ہے کہ سچے کون ہیں اور جھوٹے کون');
INSERT INTO "sayyid_qutb" VALUES(29,4,'29:4','اور کیا وہ لوگ جو بری حرکتیں کر رہے ہیں ، یہ سمجھے بیٹھے ہیں کہ وہ ہم سے بازی لے جائیں گے ؟ بڑا غلط حکم ہے جو وہ لگا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,5,'29:5','جو کوئی اللہ سے ملنے کی توقع رکھتا ہے (اسے معلوم ہونا چاہئے کہ) اللہ کا مقرر کیا ہوا وقت آنے ہی والا ہے ، اور اللہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,6,'29:6','جو شخص بھی جہاد کرے گا اپنے ہی بھلے کے لیے کرے گا۔ اللہ یقیناً دنیا جہان والوں سے بےنیاز ہے');
INSERT INTO "sayyid_qutb" VALUES(29,7,'29:7','اور جو لوگ ایمان لائیں گے اور نیک اعمال کریں گے ان کی برائیاں ہم ان سے دور کردیں گے اور انہیں ان کے بہترین اعمال کی جزا دیں گے');
INSERT INTO "sayyid_qutb" VALUES(29,8,'29:8','ہم نے انسان کو ہدایت کی کہ اپنے والدین کے ساتھ نیک سلوک کرے لیکن اگر وہ تجھ پر زور ڈالیں کہ تو میرے ساتھ کسی ایسے (معبود) کو شریک ٹھہرائے جسے تو (میرے شریک کی حیثیت سے) نہیں جانتا تو ان کی اطاعت نہ کر۔ میری ہی طرف تم سب کو پلٹ کر آنا ہے ، پھر میں تم کو بتا دوں گا کہ تم کیا کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(29,9,'29:9','اور جو لوگ ایمان لائے ہوں گے اور جنہوں نے نیک اعمال کیے ہوں گے ان کو ہم ضرور صالحین میں داخل کریں گے');
INSERT INTO "sayyid_qutb" VALUES(29,10,'29:10','لوگوں میں سے کوئی ایسا ہے جو کہتا ہے کہ ہم ایمان لائے اللہ پر ، مگر جب وہ اللہ کے معاملہ میں ستایا گیا تو اس نے لوگوں کی ڈالی ہوئی آزمائش کو اللہ کے عذاب کی طرف سمجھ لیا۔ اب اگر تیرے رب کی طرف سے فتح و نصرت آگئی تو یہی شخص کہے گا کہ ” ہم تو تمہارے ساتھ تھے “۔ کیا دنیا والوں کے دلوں کا حال ، اللہ کو بخوبی معلوم نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(29,11,'29:11','اور اللہ کو تو ضرور یہ دیکھنا ہی ہے کہ ایمان لانے والے کون ہیں اور منافق کون “۔');
INSERT INTO "sayyid_qutb" VALUES(29,12,'29:12','یہ کافر لوگ ایمان لانے والوں سے کہتے ہیں کہ تم ہمارے طریقے کی پیروی کرو اور تمہاری خطاؤں کو ہم اپنے اوپر لے لیں گے۔ حالانکہ ان کی خطاؤں میں سے کچھ بھی وہ اپنے اوپر لینے والے نہیں ہیں ، وہ قطعا جھوٹ کہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,13,'29:13','ہاں ضرور وہ اپنے بوجھ بھی اٹھائیں گے اور اپنے بوجھوں کے ساتھ دوسرے بہت سے بوجھ بھی۔ اور قیامت کے روز یقیناً ان نے ان افترا پردازیوں کی باز پرس ہوگی جو وہ کرتے رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,14,'29:14','ہم نے نوح کو اس کی قوم کی طرف بھیجا اور وہ پچاس کم ایک ہزار برس ان کے درمیان رہا۔ آخر کار ان لوگوں کو طوفان نے آگھیرا ، اس حال میں کہ وہ ظالم تھے');
INSERT INTO "sayyid_qutb" VALUES(29,15,'29:15','پھر نوح کو اور کشتی والوں کو ہم نے بچالیا اور اسے دنیا والوں کے لیے ایک نشان عبرت بنا کر رکھ دیا');
INSERT INTO "sayyid_qutb" VALUES(29,16,'29:16','اور ابراہیم کو بھیجا جب کہ اس نے اپنی قوم سے کہا : ” اللہ کی بندگی کرو اور اس سے ڈرو۔ یہ تمہارے لیے بہتر ہے اگر تم جانو');
INSERT INTO "sayyid_qutb" VALUES(29,17,'29:17','تم اللہ کو چھوڑ کر جنہیں پوج رہے ہو وہ تو محض بت ہیں اور تم ایک جھوٹ گھڑ رہے ہو۔ در حقیقت اللہ کے سوا جن کی تم پرستش کرتے ہو وہ تمہیں کوئی رزق بھی دینے کا اختیار نہیں رکھتے۔ اللہ سے رزق مانگو ، اور اسی کی بندگی کرو اور اس کا شکر ادا کرو ، اسی کی طرف تم پلٹائے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(29,18,'29:18','اور اگر تم جھٹلاتے ہو تو تم سے پہلے بہت سی قومیں جھٹلا چکی ہیں ، اور رسول صلى الله عليه وسلم پر صاف صاف پیغام پہنچا دینے کے سوا کوئی ذمہ داری نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(29,19,'29:19','کیا ان لوگوں نے کبھی دیکھا ہی نہیں ہے کہ کس طرح اللہ خلق کی ابتدا کرتا ہے ، پھر اس کا اعادہ کرتا ہے ؟ یقیناً یہ (اعادہ تو) اللہ کے لیے آسان تر ہے');
INSERT INTO "sayyid_qutb" VALUES(29,20,'29:20','ان سے کہو کہ زمین میں چلو پھرو اور دیکھو کہ اس نے کس طرح خلق کی ابتداء کی ہے ، پھر اللہ بار دیگر بھی زندگی بخشے گا۔ یہ یقیناً اللہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(29,21,'29:21','جسے چاہے سزا دے اور جس پر چاہے رحم فرمائے اسی کی طرف تم پھیرے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(29,22,'29:22','تم نہ زمین میں عاجز کرنے والے ہو ، نہ آسمان میں ، اور اللہ سے بچانے والا کوئی سرپرست اور مددگار تمہارے لیے نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(29,23,'29:23','جن لوگوں نے اللہ کی آیات کا اور اس سے ملاقات کا انکار کیا ہے وہ میری رحمت سے مایوس ہوچکے ہیں اور ان کے لیے دردناک سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,24,'29:24','پھر ابراہیم کی قوم کا جواب اس کے سوا کچھ نہ تھا کہ انہوں نے کہا ” قتل کر دو اسے یا جلا ڈالو اس کو “۔ آخر کار اللہ نے اسے آگ سے بچا لیا تھا ، یقیناً اس میں نشانیاں ہیں ، ان لوگوں کے لیے جو ایمان لانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,25,'29:25','اور اس نے کہا ، تم نے دنیا کی زندگی میں تو اللہ کو چھوڑ کر بتوں کو اپنے درمیان محبت کا ذریعہ بنا لیا ہے مگر قیامت کے روز تم ایک دوسرے کا انکار اور ایک دوسرے پر لعنت کرو گے اور آگ تمہارا ٹھکانا ہوگی اور کوئی تمہارا مددگار نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(29,26,'29:26','اس وقت لوط نے اس کو مانا اور ابراہیم نے کہا ” میں اپنے رب کی طرف ہجرت کرتا ہوں ، وہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(29,27,'29:27','اور ہم نے اسے اسحاق اور یعقوب (جیسی اولاد) عنایت فرمائی اور اس کی نسل میں نبوت اور کتاب رکھ دی ، اور اسے دنیا میں اس کا اجر عطا کیا اور آخرت میں وہ یقیناً صالحین میں سے ہوگا');
INSERT INTO "sayyid_qutb" VALUES(29,28,'29:28','اور ہم نے لوط کو بھیجا جب کہ اس نے اپنی قوم سے کہا : ” تم تو وہ فحش کام کرتے ہو ، جو تم سے پہلے دنیا والوں میں سے کسی نے نہیں کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,29,'29:29','کیا تمہارا حال یہ ہے کہ مردوں کے پاس جاتے ہو ، اور رہزنی کرتے ہو اور اپنی مجلسوں میں برے کام کرتے ہو ؟ “ پھر کوئی جواب اس کی قوم کے پاس اس کے سوا نہ تھا کہ انہوں نے کہا ” لے آ ، اللہ کا عذاب اگر تو سچا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,30,'29:30','لوط نے کہا ” اے میرے رب ، ان مفسدوں کے مقابلے میں میری مدد فرما');
INSERT INTO "sayyid_qutb" VALUES(29,31,'29:31','اور جب ہمارے فرستادے ابراہیم کے پاس بشارت لے کر پہنچے تو انہوں نے اس سے کہا ” ہم اس بستی کے لوگوں کو ہلاک کرنے والے ہیں ، اس کے لوگ سخت ظالم ہوچکے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,32,'29:32','ابراہیم نے کہا ” وہاں تو لوط موجود ہیں “۔ انہوں نے کہا ، ہم خوب جانتے ہیں کہ وہاں کون کون ہے۔ ہم اسے ، اور اس کی بیوی کے سوا ، اس کے باقی سب گھر والوں کو بچا لیں گے اس کی بیوی پیچھے رہ جانے والوں میں سے تھی');
INSERT INTO "sayyid_qutb" VALUES(29,33,'29:33','پھر جب ہمارے فرستادے لوط کے پاس پہنچے تو ان کی آمد پر وہ سخت پریشان اور تنگ دل ہوا انہوں نے کہا ، نہ ڈرو اور نہ رنج کرو۔ ہم تمہیں اور تمہارے گھر والوں کو بچالیں گے ، سوائے تمہاری بیوی کے جو پیچھے رہ جانے والوں میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(29,34,'29:34','ہم اس بستی کے لوگوں پر آسمان سے عذاب نازل کرنے والے ہیں اس فسق کی بدولت جو یہ کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,35,'29:35','اور ہم نے اس بستی کی ایک کھلی نشانی چھوڑ دی ہے ، ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,36,'29:36','اور مدین کی طرف ہم نے ان کے بھائی شعیب کو بھیجا۔ اس نے کہا ” اے میری قوم کے لوگو ، اللہ کی بندگی کرو اور روز آخر کے امیدوار رہو اور زمین میں مفسدبن کر زیادتیاں نہ کرتے پھرو');
INSERT INTO "sayyid_qutb" VALUES(29,37,'29:37','مگر انہوں نے اسے جھٹلا دیا۔ آخر کار ایک سخت زلزلے نے انہیں آلیا اور وہ اپنے گھروں میں پڑے کے پڑے رہ گئے');
INSERT INTO "sayyid_qutb" VALUES(29,38,'29:38','اور عاد وثمود کو ہم نے ہلاک کیا ، تم وہ مقامات دیکھ چکے ہو جہاں وہ رہتے تھے۔ ان کے اعمال کو شیطان نے ان کیلئے خوشنما بنا دیا اور انہیں راہ راست سے برگشتہ کردیا حالانکہ وہ ہوش گوش رکھتے تھے');
INSERT INTO "sayyid_qutb" VALUES(29,39,'29:39','اور قارون و فرعون و ہامان کو ہم نے ہلاک کیا اور موسیٰ ان کے پاس بینات لے کر آیا ، مگر انہوں نے زمین میں اپنی بڑائی کا زعم کیا ، حالانکہ وہ سبقت لے جانے والے نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(29,40,'29:40','آخر کار ہر ایک کو ہم نے اس کے گناہ میں پکڑا۔ پھر ان میں سے کسی پر ہم نے پتھراؤ کرنے والی ہوا بھیجی ، اور کسی کو ایک زبردست دھماکے نے آلیا ، اور کسی کو ہم نے زمین میں دھنسا دیا ، اور کسی کو غرق کردیا۔ اللہ ان پر ظلم کرنے والا نہ تھا ، مگر وہ خود ہی اپنے اوپر ظلم کر رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(29,41,'29:41','جن لوگوں نے اللہ کو چھوڑ کر دوسرے سرپرست بنا لیے ہیں ان کی مثال مکڑی جیسی ہے جو اپنا ایک گھر بناتی ہے اور سب گھروں سے زیادہ کمزور مکڑی کا گھر ہی ہوتا ہے۔ کاش یہ لوگ علم رکھتے');
INSERT INTO "sayyid_qutb" VALUES(29,42,'29:42','یہ لوگ اللہ کو چھوڑ کر جس چیز کو بھی پکارتے ہیں ، اللہ اسے خوب جانتا ہے اور وہی زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(29,43,'29:43','یہ مثالیں ہم لوگوں کی فہمائش کے لیے دیتے ہیں مگر ان کو وہی لوگ سمجھتے ہیں جو علم رکھنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,44,'29:44','اللہ نے آسمانوں اور زمین کو برحق پیدا کیا ہے ، در حقیقت اس میں ایک نشانی ہے اہل ایمان کے لیے');
INSERT INTO "sayyid_qutb" VALUES(29,45,'29:45','جو کتاب آپ پر وحی کی گئی ہے اسے پڑھئے اور نماز قائم کیجئے بیشک نماز فحاشی اور منکرات سے روکتی ہے۔ بیشک اللہ کا ذکر بڑی چیز ہے اور اللہ جانتا ہے جو کام تم کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(29,46,'29:46','اور اہل کتاب سے بحث نہ کرو مگر عمدہ طریقہ سے سوائے ان لوگوں کے جو ان میں سے ظالم ہوں اور ان سے کہو کہ ” ہم ایمان لائے ہیں اس چیز پر بھی جو ہماری طرف بھیجی گئی ہے اور اس چیز پر بھی جو تمہاری طرف بھیجی گئی تھی ، ہمارا خدا اور تمہارا خدا ایک ہی ہے اور ہم اسی کو مسلم (فرمانبردار ) ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,47,'29:47','(اے نبی صلى الله عليه وسلم ہم نے اسی طرح تمہاری طرف کتاب نازل کی ہے ، اس لیے وہ لوگ جن کو ہم نے پہلے کتاب دی تھی وہ اس پر ایمان لاتے ہیں ، اور ان لوگوں میں سے بھی بہت سے اس پر ایمان لا رہے ہیں ، اور ہماری آیات کا انکار صرف کافر ہی کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,48,'29:48','(اے نبی صلى الله عليه وسلم تم اس سے پہلے کوئی کتاب نہیں پڑھتے تھے اور نہ اپنے ہاتھ سے لکھتے تھے اگر ایسا ہوتا تو باطل پرست لوگ شک میں پڑ سکتے تھے');
INSERT INTO "sayyid_qutb" VALUES(29,49,'29:49','دراصل یہ روشن نشانیاں ہیں ان لوگوں کے دلوں میں جنہیں علم بخشا گیا ہے ، اور ہماری آیات کا انکار نہیں کرتے مگر وہ جو ظالم ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,50,'29:50','یہ لوگ کہتے ہیں کہ ” کیوں نہ اتاری گئیں اس شخص پر نشانیاں اس کے رب کی طرف سے ؟ “ کہو ” نشانیاں تو اللہ کے پاس ہیں اور میں صرف خبردار کرنے والا ہوں کھول کھول کر');
INSERT INTO "sayyid_qutb" VALUES(29,51,'29:51','اور کیا ان لوگوں کے لیے یہ (نشانی) کافی نہیں ہے کہ ہم نے تم پر کتاب نازل کی جو انہیں پڑھ کر سنائی جاتی ہے ؟ درحقیقت اس میں رحمت ہے اور نصیحت ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,52,'29:52','”(اے نبی صلى الله عليه وسلم ، کہو کہ ” میرے اور تمہارے درمیان اللہ گواہی کے لیے کافر ہے۔ وہ آسمانوں اور زمین میں سب کچھ جانتا ہے۔ جو لوگ باطل کو مانتے ہیں اور اللہ سے کفر کرتے ہیں وہی خسارے میں رہنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,53,'29:53','یہ لوگ تم سے عذاب جلدی لانے کا مطالبہ کرتے ہیں اگر ایک وقت مقرر نہ کردیا گیا ہوتا تو ان پر عذاب آچکا ہوتا۔ اور یقینا (اپنے وقت پر ) وہ آکر رہے گا ۔ اچا نک ، اس حال میں کہ انہیں خبر بھی نہ ہوگی');
INSERT INTO "sayyid_qutb" VALUES(29,54,'29:54','یہ تم سے عذاب جلدی لانے کا مطالبہ کرتے ہیں ، حالانکہ جہنم ان کافروں کو گھیرے میں لے چکی ہے');
INSERT INTO "sayyid_qutb" VALUES(29,55,'29:55','(اور انہیں پتہ چلے گا) اس روز جب کہ عذاب انہیں اوپر سے بھی ڈھانک لے گا اور پاؤں کے نیچے سے بھی اور کہے گا کہ اب چکھو مزا ان کرتوتوں کا جو تم کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(29,56,'29:56','اے میرے بندو جو ایمان لائے ہو ، میری زمین وسیع ہے ، پس تم میری ہی بندگی بجا لاؤ');
INSERT INTO "sayyid_qutb" VALUES(29,57,'29:57','ہر متنفس کو موت کا مزا چکھنا ہے ، پھر تو سب ہماری طرف ہی پلٹا کر لائے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(29,58,'29:58','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کو ہم جنت کی بلند وبالا عمارتوں میں رکھیں گے جن کے نیچے نہریں بہتی ہوں گی ، وہاں وہ ہمیشہ رہیں گے ، کیا ہی عمدہ اجر ہے عمل کرنے والوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(29,59,'29:59','ان لوگوں کے لیے جنہوں نے صبر کیا ہے اور جو اپنے رب پر بھروسہ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,60,'29:60','کتنے ہی جانور ہیں جو اپنا رزق اٹھائے نہیں پھرتے ، اللہ ان کو رزق دیتا ہے اور تمہارا رازق بھی وہی ہے ، وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,61,'29:61','اگر تم ان لوگوں سے پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے اور چاند اور سورج کو کس نے مسخر کر رکھا ہے تو ضرور کہیں گے کہ اللہ نے پھر یہ کدھر سے دھوکا کھا رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(29,62,'29:62','اللہ ہی ہے جو اپنے بندوں میں سے جس کا چاہتا ہے رزق کشادہ کرتا ہے اور جس کا چاہتا ہے تنگ کرتا ہے ، یقیناً اللہ ہر چیز کا جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(29,63,'29:63','اور اگر تم ان سے پوچھو کس نے آسمان سے پانی برسایا اور اس کے ذریعہ سے مردہ پڑی ہوئی زمین کو جلا اٹھایا تو وہ ضرور کہیں گے اللہ نے۔ کہو ، الحمدللہ ، مگر ان میں سے اکثر لوگ سمجھتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,64,'29:64','اور یہ دنیا کی زندگی کچھ نہیں ہے مگر ایک کھیل اور دل کا بہلاوا ۔ اصل زندگی کا گھر تو دار آخرت ہے ، کاش یہ لوگ جانتے');
INSERT INTO "sayyid_qutb" VALUES(29,65,'29:65','جب یہ لوگ کشتی پر سوار ہوتے ہیں تو اپنے دین کو اللہ کے لئے خالص کرکے اس سے دعا مانگتے ہیں۔ پھر جب وہ انہیں بچا کر خشکی پر لے جاتا ہے تو یکایک یہ شرک کرنے لگتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,66,'29:66','تاکہ اللہ کی دی ہوئی نجات پر ان کا کفران نعمت کریں اور حیات دنیا کے مزے لوٹیں۔ اچھا عنقریب انہیں معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(29,67,'29:67','کیا یہ دیکھتے ہیں کہ ہم نے ایک پرامن حرم بنایا ہے حالانکہ ان کے گردوپیش لوگ اچک لیے جاتے ہیں۔ پھر بھی یہ لوگ باطل کو مانتے ہیں اور اللہ کی نعمتوں کا کفران کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(29,68,'29:68','اس شخص سے بڑا ظالم کون ہوگا جو اللہ پر جھوٹ باندھے یا حق کو جھٹلائے جبکہ وہ اس کے سامنے آچکا ہو ، کیا ایسے کافروں کا ٹھکانہ جہنم نہیں ۔۔۔ ؟');
INSERT INTO "sayyid_qutb" VALUES(29,69,'29:69','جو لوگ ہماری خاطر مجاہدہ کریں گے انہیں ہم اپنے راستے دکھائیں گے ، اور یقیناً اللہ نیکوکاروں ہی کے ساتھ ہے');
INSERT INTO "sayyid_qutb" VALUES(30,1,'30:1','ا۔ ل۔ م');
INSERT INTO "sayyid_qutb" VALUES(30,2,'30:2','رومی قریب کی سرزمین میں مغلوب ہوگئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,3,'30:3','اور اپنی اس مغلوبیت کے بعد چند سال کے اندر وہ غالب ہوجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,4,'30:4','اللہ ہی کا اختیار ہے پہلے بھی اور بعد میں بھی۔ اور وہ دن ہو ہوگا جب کہ مسلمان خوشیاں منائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,5,'30:5','اللہ کی بخشی ہوئی فتح پر ۔ اللہ نصرت عطا فرماتا ہے جسے چاہتا ہے ، اور وہ زبردست اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(30,6,'30:6','یہ وعدہ اللہ نے کیا ہے ، اللہ کبھی اپنے وعدے کی خلاف ورزی نہیں کرتا ، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,7,'30:7','لوگ دنیا کی زندگی کا بس ظاہری پہلو جانتے ہیں اور آخرت سے وہ خود ہی غافل ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,8,'30:8','کیا انہوں نے کبھی اپنے آپ میں غوروفکر نہیں کیا ؟ اللہ نے زمین اور آسمانوں کو اور ان ساری چیزوں کو جو ان کے درمیان ہیں ، برحق اور ایک مدت مقرر ہی کے لیے پیدا کیا ہے مگر بہت سے لوگ اپنے رب کی ملاقات کے منکر ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,9,'30:9','اور کیا یہ لوگ کبھی زمین میں چلے پھرے نہیں ہیں کہ انہیں ان لوگوں کا انجام نظر آتا جو ان سے پہلے گزر چکے ہیں ؟ وہ ان سے زیادہ طاقت رکھتے تھے ، انہوں نے زمین کو خوب ادھیڑا تھا اور اسے اتنا آباد کیا تھا جتنا انہوں نے نہیں کیا ہے۔ ان کے پاس ان کے رسول روشن نشانیاں لے کر آئے۔ پھر اللہ ان پر ظلم کرنے والا نہ تھا ، مگر وہ خود ہی اپنے اوپر ظلم کر رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(30,10,'30:10','آخر کار جن لوگوں نے برائیاں کی تھیں ان کا انجام بہت برا ہوا اس لیے کہ انہوں نے اللہ کی آیات کو جھٹلایا تھا اور وہ ان کا مذاق اڑاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(30,11,'30:11','اللہ ہی خلق کی ابتداء کرتا ہے ، پھر وہی اس کا اعادہ کرے گا ، پھر اسی کی طرف تم پلٹائے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(30,12,'30:12','اور جب وہ ساعت برپا ہوگئی اس دن مجرم ہک دک رہ جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,13,'30:13','انکے ٹھہرائے ہوئے شریکوں میں سے کوئی ان کا سفارشی نہ ہوگا اور وہ اپنے شریکوں کے منکر ہوجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,14,'30:14','جس روز وہ ساعت برپا ہوگی ، اس دن (سب انسان) الگ گروہوں میں بٹ جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,15,'30:15','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں وہ ایک باغ میں شاداں وفرحاں رکھے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,16,'30:16','اور جنہوں نے کفر کیا ہے اور ہماری آیات کو اور آخرت کی ملاقات کو جھٹلایا ہے ، وہ عذاب میں حاضر رکھے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,17,'30:17','پس تسبیح کرو اللہ کی جب کہ تم شام کرتے ہو اور جب صبح کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(30,18,'30:18','آسمانوں اور زمین میں اسی کے لیے حمد ہے اور (تسبیح کرو اس کی) تیسرے پہر اور جبکہ تم پر ظہر کا وقت آتا ہے');
INSERT INTO "sayyid_qutb" VALUES(30,19,'30:19','وہ زندہ کو مردے میں سے نکالتا ہے اور مردے کو زندہ میں سے نکالتا ہے اور زمین کو اس کی موت کے بعد زندگی بخشتا ہے اور اسی طرح تم لوگ بھی نکالے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(30,20,'30:20','اور اس کی نشانیوں میں سے یہ ہے کہ اس نے تم کو مٹی سے پیدا کیا ، پھر یکایک تم بشر ہوکر پھیلتے جا رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(30,21,'30:21','اور اس کی نشانیوں میں سے یہ ہے کہ اس نے تمہارے لیے تمہاری ہی جنس سے بیویاں بنائیں تاکہ تم ان کے پاس سکون حاصل کرو اور تمہارے درمیان محبت اور رحمت پیدا کردی۔ یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو غور و فکر کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,22,'30:22','اور اس کی نشانیوں میں سے ہے آسمان و زمین کی پیدائش اور تمہاری زبانوں اور تمہارے رنگوں کا اختلاف ہے۔ یقیناً اس میں نشانیاں ہیں دانش مند لوگوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(30,23,'30:23','اور اس کی نشانیوں میں سے تمہارا رات اور دن کو سونا ، اور تمہارا اس کے فضل کو تلاش کرنا ہے۔ یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو غور سے سنتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,24,'30:24','اور اس کی نشانیوں میں سے یہ ہے کہ وہ تمہیں بجلی کی چمک دکھاتا ہے خوف کے ساتھ بھی اور طمع کے ساتھ بھی۔ اور آسمان سے پانی برساتا ہے ، پھر اس کے ذریعہ سے زمین کو اس کی موت کے بعد زندگی بخشا ہے ، یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,25,'30:25','اور اس کی نشانیوں میں سے یہ ہے کہ آسمان اور زمین اس کے حکم سے قائم ہیں۔ پھر جوں ہی کہ اس نے تمہیں زمین سے پکارا “ بس ایک ہی پکار میں اچانک تم نکل آؤ گے۔');
INSERT INTO "sayyid_qutb" VALUES(30,26,'30:26','آسمانوں اور زمین میں جو بھی ہیں ، اس کے بندے ہیں۔ سب کے سب اسی کے تابع فرمان ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,27,'30:27','وہی اللہ ہے جو تخلیق کی ابتدا کرتا ہے ، پھر وہی اس کا اعادہ کرے گا اور یہ اس کے لیے آسان تر ہے۔ آسمانوں اور زمین میں اس کی صفت سب سے برتر ہے اور وہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(30,28,'30:28','وہ تمہیں کود تمہاری اپنی ہی ذات سے ایک مثال دیتا ہے ۔ کیا تمہارے ان غلاموں میں سے جو تمہاری ملکیت میں ہیں ، کچھ غلام ایسے بھی ہیں جو ہمارے دئیے ہوئے مال و دولت میں تمہارے ساتھ برابر کے شریک ہوں اور تم ان سے اس طرح ڈرتے ہو جس طرح آپس میں اپنے ہمسروں سے ڈرتے ہو۔ اس طرح ہم آیات کھول کر پیش کرتے ہیں ان لوگوں کے لیے جو عقل سے کام لیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,29,'30:29','مگر یہ ظالم بےسمجھے بوجھے اپنے تخیلات کے پیچھے چل پڑے ہیں۔ اب کون اس شخص کو راستہ دکھا سکتا ہے جسے اللہ نے بھٹکا دیا ہو ، ایسے لوگوں کا تو کوئی مددگار نہیں ہوسکتا');
INSERT INTO "sayyid_qutb" VALUES(30,30,'30:30','پس (اے نبی صلى الله عليه وسلم ، اور نبی صلى الله عليه وسلم کے پیروو) یکسو ہوکر اپنا رخ اس دین کی سمت میں جما دو ، قائم ہوجاؤ اس فطرت پر جس طرح اللہ تعالیٰ نے انسانوں کو پیدا کیا ہے ، اللہ کی بنائی ہوئی ساخت بدلی نہیں جاسکتی ، یہی بالکل راست اور درست دین ہے ، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,31,'30:31','(قائم ہوجاؤ اس بات پر) اللہ کی طرف رجوع کرتے ہوئے ، اور ڈرو اس سے ، اور نماز قائم کرو ، اور نہ ہوجاؤ ان مشرکین میں سے');
INSERT INTO "sayyid_qutb" VALUES(30,32,'30:32','جنہوں نے اپنا اپنا دین الگ بنا لیا ہے اور گروہوں میں بٹ گئے ہیں ، ہر ایک گروہ کے پاس جو کچھ ہے اسی میں وہ مگن ہے');
INSERT INTO "sayyid_qutb" VALUES(30,33,'30:33','لوگوں کا حال یہ ہے کہ جب انہیں کوئی تکلیف پہنچتی ہے تو اپنے رب کی طرف رجوع کرکے اسے پکارتے ہیں ، پھر جب وہ کچھ اپنی رحمت کا ذائقہ انہیں چکھا دیتا ہے تو یکایک ان میں سے کچھ لوگ شرک کرنے لگتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,34,'30:34','تاکہ ہمارے کیے ہوئے احسان کی ناشکری کریں۔ اچھا ، مزے کرلو ، عنقریب تمہیں معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(30,35,'30:35','کیا ہم نے کوئی سند اور دلیل ان پر نازل کی ہے جو شہادت دیتی ہو ، اس شرک کی صداقت پر جو یہ کر رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(30,36,'30:36','جب ہم لوگوں کو رحمت کا ذائقہ چکھاتے ہیں تو وہ اس پر پھول جاتے ہیں اور جب ان کے اپنے کیے کرتوتوں سے ان پر کوئی مصیبت آتی ہے تو یکایک وہ مایوس ہونے لگتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,37,'30:37','کیا یہ لوگ دیکھتے نہیں کہ اللہ ہی رزق کشادہ کرتا ہے جس کا چاہتا ہے اور تنگ کرتا ہے (جس کا چاہتا ہے) یقیناً اس میں بہت سی نشانیاں ہیں ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,38,'30:38','پس (اے مومن) رشتہ دار کو اس کا حق دے اور مسکین و مسافر کو (اس کا حق) ۔ یہ طریقہ بہتر ہے ان لوگوں کے لیے جو اللہ کی خوشنودی چاہتے ہوں ، اور وہی فلاح پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,39,'30:39','جو سود تم دیتے ہو تاکہ لوگوں کے اموال میں شامل ہو کر وہ بڑھ جائے ، اللہ کے نزدیک وہ نہیں بڑھتا ، اور جو زکوٰۃ تم اللہ کی خوشنودی حاصل کرنے کے ارادے سے دیتے ہو ، اسی کے دینے والے درحقیقت اپنے مال بڑھاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,40,'30:40','اللہ ہی ہے جس نے تم کو پیدا کیا ، پھر تمہیں رزق دیا ، پھر وہ تمہیں موت دیتا ہے ، پھر وہ تمہیں زندہ کرے گا۔ کیا تمہارے ٹھہرائے ہوئے شریکوں میں کوئی ایسا ہے جو ان میں سے کوئی کام بھی کرتا ہو ؟ پاک ہے وہ اور بہت بالا و برتر ہے اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,41,'30:41','خشکی اور تری میں فساد برپا ہوگیا ہے۔ لوگوں کے پانے ہاتھوں کی کمائی سے تاکہ مزہ چکھائے ان کو ان کے بعض اعمال کا ، شاید کہ وہ باز آئیں');
INSERT INTO "sayyid_qutb" VALUES(30,42,'30:42','(اے نبی صلى الله عليه وسلم ان سے کہو کہ زمین میں چل پھر کر دیکھو۔ پہلے گزرے ہوئے لوگوں کا کیا انجام ہوچکا ہے ، ان میں سے اکثر مشرک ہی تھے');
INSERT INTO "sayyid_qutb" VALUES(30,43,'30:43','(اے نبی صلى الله عليه وسلم اپنا رخ مضبوطی کے ساتھ جما دو ، اس دین راست کی سمت میں قبل اس کے کہ وہ دن آئے جس کے ٹل جانے کی کوئی صورت اللہ کی طرف سے نہیں ہے۔ اس دن لوگ چھٹ کر ایک دوسرے سے الگ ہوجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(30,44,'30:44','جس نے کفر کیا ہے اس کے کفر کا وبال اسی پہ ہے ، اور جن لوگوں نے نیک عمل کیا ہے ، وہ اپنے ہی لیے (فلاح کا راستہ) صاف کر رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,45,'30:45','تاکہ اللہ ایمان لانے والوں اور عمل صالح کرنے والوں کو اپنے فضل سے جزا دے ، یقیناً وہ کافروں کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(30,46,'30:46','اس کی نشانیوں میں سے یہ ہے کہ وہ ہوائیں بھیجتا ہے ، بشارت دینے کے لیے اور تمہیں اپنی رحمت سے بہرہ مند کرنے کے لیے اور اس فرض کے لیے کہ کشتیاں اس کے حکم سے چلیں اور تم اس کا فضل تلاش کرو اور اس کے شکر گزار بنو');
INSERT INTO "sayyid_qutb" VALUES(30,47,'30:47','اور ہم نے تم سے پہلے رسولوں کو ان کی قوم کی طرف بھیجا اور وہ ان کے پاس روشن نشانیاں لے کر آئے ، پھر جنہوں نے جرم کیا ان سے ہم نے انتقام لیا اور ہم پر یہ حق تھا کہ ہم مومنوں کی مدد کریں');
INSERT INTO "sayyid_qutb" VALUES(30,48,'30:48','اللہ ہی ہے جو ہواؤں کو بھیجتا ہے اور وہ بادل اٹھاتی ہیں ، پھر وہ ان بادلوں کو آسمان میں پھیلاتا ہے جس طرح چاہتا ہے اور انہیں ٹکڑیوں میں تقسیم کرتا ہے ، پھر تو دیکھتا ہے کہ بارش کے قطرے بادل میں سے ٹپکے چلے آتے ہیں۔ یہ بارش جب وہ اپنے بندوں میں سے جن پر چاہتا ہے ، برساتا ہے تو یکایک وہ خوش و خرم ہوجاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,49,'30:49','حالانکہ اس کے نزول سے پہلے وہ مایوس ہو رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(30,50,'30:50','دیکھو اللہ کی رحمت کے اثرات کہ وہ مردہ پڑی ہوئی زمین کو وہ کس طرح جلا اٹھاتا ہے ، یقیناً وہ مردوں کو زندگی بخشنے والا ہے اور وہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(30,51,'30:51','اور اگر ہم ایک ایسی ہوا بھیج دیں جس کے اثر سے وہ اپنی کھیتی کو زرد پائیں تو وہ کفر کرتے رہ جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,52,'30:52','”(اے نبی صلى الله عليه وسلم تم مردوں کو نہیں سنا سکتے ، نہ ان بہروں کو اپنی پکار سنا سکتے ہو جو پیٹھ پھیرے چلے جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,53,'30:53','اور نہ تم اندھوں کو ان کی گمراہی سے نکال کر راہ راست دکھا سکتے ہو۔ تم تو انہی کو سنا سکتے ہو جو ہماری آیات پر ایمان لاتے اور سر تسلیم خم کردیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,54,'30:54','اللہ ہی تو ہے جس نے ضعف کی حالت سے تمہاری پیدائش کی۔ ابتدا کی ، پھر اس ضعف کے بعد تمہیں قوت بخشی ، پھر اس قوت کے بعد تمہیں ضعیف اور بوڑھا کردیا۔ وہ جو کچھ چاہتا ہے ، پیدا کرتا ہے۔ اور وہ سب کچھ جاننے والا ، ہر چیز پر قدرت رکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(30,55,'30:55','اور جب وہ ساعت برپا ہوگی تو مجرم قسمیں کھا کھا کر کہیں گے کہ ہم ایک گھڑی بھر سے زیادہ نہیں ٹھہرے ہیں ، اسی طرح وہ دنیا کی زندگی میں دھوکا کھایا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(30,56,'30:56','مگر جو علم اور ایمان سے بہر مند کیے گئے تھے وہ کہیں گے کہ خدا کے نوشتے میں تو تم روز حشر تک پڑے رہے ہو ، سو یہ وہی روز حشر ہے ، لیکن تم جانتے نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(30,57,'30:57','پس وہ دن ہوگا جس میں ظالموں کو ان کی معذرت کو ئی نفع نہ دے گی اور نہ ان سے معافی مانگنے کیلئے کہا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(30,58,'30:58','ہم نے اس قرآن میں لوگوں کو طرح طرح سے سمجھایا ہے۔ تم خواہ کوئی نشانی لے آؤ، جن لوگوں نے ماننے سے انکار کردیا ہے ، وہ یہی کہیں گے کہ تم باطل پر ہو');
INSERT INTO "sayyid_qutb" VALUES(30,59,'30:59','اس طرح ٹھپہ لگا دیتا ہے اللہ ان لوگوں کے دلوں پر جو بےعلم ہیں');
INSERT INTO "sayyid_qutb" VALUES(30,60,'30:60','پس (اے نبی صلى الله عليه وسلم صبر کرو ، یقیناً اللہ کا وعدہ سچا ہے ، اور ہرگز ہلکا نہ پائیں تم کو وہ لوگ جو یقین ، نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(31,1,'31:1','ا۔ ل۔ م');
INSERT INTO "sayyid_qutb" VALUES(31,2,'31:2','یہ کتاب حکیم کی آیات ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,3,'31:3','ہدایت اور رحمت نیکوکار لوگوں کیلئے');
INSERT INTO "sayyid_qutb" VALUES(31,4,'31:4','جو نماز قائم کرتے ہیں ، زکوۃ دیتے ہیں اور آخرت پر یقین رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,5,'31:5','یہی لوگ اپنے رب کی طرف سے راہ راست پر ہیں اور یہی فلاح پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,6,'31:6','اور انسانوں ہی میں سے کوئی ایسا بھی ہے جو کلام دلفریب خرید کر لاتا ہے تاکہ لوگوں کو اللہ کے راستہ سے علم کے بغیر بھٹکا دے اور اس راستے کی دعوت کو مذاق میں اڑا دے۔ ایسے لوگوں کے لیے سخت ذلیل کرنے والا عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(31,7,'31:7','اسے جب ہماری آیات سنائی جاتی ہیں تو وہ بڑے گھمنڈ کے ساتھ اس طرح رخ پھیر لیتا ہے گویا کہ اس نے انہیں سنا ہی نہیں ، گویا کہ اس کے کان بہرے ہیں۔ اچھا ، مژدہ سنا دو اسے ایک دردناک عذاب کا');
INSERT INTO "sayyid_qutb" VALUES(31,8,'31:8','البتہ جو لوگ ایمان لے آئیں اور نیک عمل کریں ، ان کے لئے نعمت بھری جنتیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,9,'31:9','جن میں وہ ہمیشہ رہیں گے۔ یہ اللہ کا پختہ وعدہ ہے اور وہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(31,10,'31:10','اس نے آسمانوں کو پیدا کیا بغیر ستونوں کے جو تم کو نظر آئیں۔ اس نے زمین میں پہاڑ جما دئیے تاکہ وہ تمہیں کے کر ڈھلک نہ جائے۔ اس نے ہر طرح کے جانور زمین میں پھیلا دئیے اور آسمان سے پانی برسایا اور زمین میں قسم قسم کی عمدہ چیزیں اگا دیں');
INSERT INTO "sayyid_qutb" VALUES(31,11,'31:11','تو یہ ہے اللہ کی تخلیق ، اب ذرا مجھے دکھاؤ، ان دوسروں نے کیا پیدا کیا ہے ؟ اصل بات یہ ہے کہ ظالم لوگ صریح گمراہی میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,12,'31:12','ہم نے لقمان کو حکمت عطا کی تھی کہ اللہ کا شکر گزار ہو۔ جو کوئی شکر کرے اس کا شکر اس کے اپنے ہی لیے مفید ہے اور جو کفر کرے تو حقیقت میں اللہ بےنیاز اور آپ سے آپ محمود ہے');
INSERT INTO "sayyid_qutb" VALUES(31,13,'31:13','یاد کرو جب لقمان اپنے بیٹے کو نصیحت کر رہا تھا تو اس نے کہا ” بیٹا ، خدا کے ساتھ کسی کو شریک نہ کرنا ، حق یہ ہے کہ شرک بہت بڑا ظلم ہے');
INSERT INTO "sayyid_qutb" VALUES(31,14,'31:14','اور یہ حقیقت ہے کہ ہم نے انسان کو اپنے والدین کا حق پہچاننے کی خود تاکید کی ہے۔ اس کی ماں نے ضعف پر ضعف اٹھا کر اسے پیٹ میں رکھا اور دو سال اس کا دودھ چھوٹنے میں لگے۔ (اسی لیے ہم نے اس کو نصیحت کی کہ) میرا شکر کر اور اپنے والدین کا شکر بجالا ، میری ہی طرف تجھے پلٹنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(31,15,'31:15','لیکن اگر وہ تجھ پر دباؤ ڈالیں کہ میرے ساتھ تو کسی ایسے کو شریک کرے جسے تو نہیں جانتا تو ان کی بات ہرگز نہ مان۔ دنیا میں ان کے ساتھ نیک برتاؤ کرتا رہ مگر پیروی اس شخص کے راستے کی کر جس نے میری طرف رجوع کیا ہے۔ پھر تم سب کو پلٹنا میری ہی طرف ہے ، اس وقت میں تمہیں بتا دوں گا کہ تم کیسے عمل کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(31,16,'31:16','(اور لقمان نے کہا تھا کہ) ” بیٹا ، کوئی چیز رانی کے دانہ برابر بھی ہو اور کسی چٹان میں یا آسمانوں یا زمین میں کہیں چھپی ہوئی ہو ، اللہ اسے نکال لائے گا۔ وہ باریک بیں اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(31,17,'31:17','بیٹا ، نماز قائم کر ، نیکی کا حکم دے ، بدی سے منع کر ، اور جو مصیبت بھی پڑے اس پر صبر کر۔ یہ وہ باتیں ہیں جن کی بڑی تاکید کی گئی ہے');
INSERT INTO "sayyid_qutb" VALUES(31,18,'31:18','اور لوگوں سے منہ پھیر کر بات نہ کر ، نہ زمین میں اکڑ کر چل ، اللہ کسی خود پسند اور فخر جتانے والے شخص کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(31,19,'31:19','اپنی چال میں اعتدال اختیار کر ، اور اپنی آواز ذرا پست رکھ ، سب آوازوں سے زیادہ بری آواز گدھوں کی آواز ہوتی ہے');
INSERT INTO "sayyid_qutb" VALUES(31,20,'31:20','کیا تم لوگ نہیں دیکھتے کہ اللہ نے زمین اور آسمانوں کی ساری چیزیں تمہارے مسخر کر رکھی ہیں اور اپنی کھلی اور چھپی نعمتیں تم پر تمام کردی ہیں ؟ اس پر حال یہ ہے کہ انسانوں میں سے کچھ لوگ ہیں جو ان کے بارے میں جھگڑتے ہیں بغیر اس کے کہ ان کے پاس کوئی علم ہو ، یا ہدایت ، یا کوئی روشنی دکھانے والی کتاب');
INSERT INTO "sayyid_qutb" VALUES(31,21,'31:21','اور جب ان سے کہا جاتا ہے کہ پیروی کرو اس چیز کی جو اللہ نے نازل کی ہے تو کہتے ہیں کہ ہم تو اس چیز کی پیروی کریں گے جس پر ہم نے اپنے باپ دادا کو پایا ہے۔ کیا یہ انہی کی پیروی کریں گے خواہ شیطان ان کو بھڑکتی ہوئی آگ ہی کی طرف کیون نہ بلاتا رہا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(31,22,'31:22','جو شخص اپنے آپ کو اللہ کے حوالے کردے اور عملاً وہ نیک ہو ، اس نے فی الواقع ایک بھروسے کے قابل سہارا تھام لیا اور سارے معاملات کا آخری فیصلہ اللہ ہی کے ہاتھ میں ہے');
INSERT INTO "sayyid_qutb" VALUES(31,23,'31:23','اب جو کفر کرتا ہے اس کا کفر تمہیں غم میں مبتلا نہ کرے ، انہیں پلٹ کر آنا تو ہماری ہی طرف ہے ، پھر ہم انہیں بتا دیں گے کہ وہ کیا کچھ کرکے آئے ہیں۔ یقیناً اللہ سینوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(31,24,'31:24','ہم تھوڑی مدت انہیں دنیا میں مزے کرنے کا موقع دے رہے ہیں ، پھر ان کو بےبس کرکے ایک سخت عذاب کی طرف کھینچ لے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(31,25,'31:25','اگر تم ان سے پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے ، تو یہ ضرور کہیں گے کہ اللہ نے۔ کہو الحمدللہ۔ مگر ان میں سے اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(31,26,'31:26','آسمانوں اور زمین میں جو کچھ ہے اللہ کا ہے۔ بیشک اللہ بےنیاز اور آپ سے آپ محمود ہے');
INSERT INTO "sayyid_qutb" VALUES(31,27,'31:27','زمین میں جتنے درخت ہیں اگر وہ سب کے سب قلم بن جائیں اور سمندر (دوات بن جائے) جسے سات مزید سمندر روشنائی مہیا کریں تب بھی اللہ کی باتیں (لکھنے سے) ختم نہ ہوں گی۔ بیشک اللہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(31,28,'31:28','تم سارے انسانوں کو پیدا کرنا اور پھر دوبارہ جلا اٹھانا تو (اس کے لئے) بس ایسا ہے جیسے ایک متنفس کو (پیدا کرنا اور جلا اٹھانا) ۔ حقیقت یہ ہے کہ اللہ سب کچھ سننے اور دیکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(31,29,'31:29','کیا تم دیکھتے نہیں ہو کہ اللہ رات کو دن میں پروتا ہوا لے آتا ہے اور دن کو رات میں اس نے سورج اور چاند کو مسخر کر رکھا ہے ، سب ایک وقت مقرر تک چلے جا رہے ہیں ، اور (کیا تم نہیں جانتے) کہ جو کچھ بھی تم کرتے ہو ، اللہ اس سے باخبر ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(31,30,'31:30','یہ سب کچھ اس وجہ سے ہے کہ اللہ ہی حق ہے اور اسے چھوڑ کر جن دوسری چیزوں کو یہ لوگ پکارتے ہیں ، وہ سب باطل ہیں ، اور (اس وجہ سے کہ) اللہ ہی بزرگ و برتر ہے');
INSERT INTO "sayyid_qutb" VALUES(31,31,'31:31','کیا تم دیکھتے نہیں ہو کہ کشتی سمندر میں اللہ کے فضل سے چلتی ہے تاکہ وہ تمہیں اپنی کچھ نشانیاں دکھائے ؟ درحقیقت اس میں بہت سی نشانیاں ہیں ہر اس شخص کے لیے جو صبر اور شکر کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(31,32,'31:32','اور جب (سمندر میں) ان لوگوں پر ایک موج سائبانوں کی طرح چھا جاتی ہے تو یہ اللہ کو پکارتے ہیں اپنے دین کو بالکل اسی کے لیے خالص کرکے۔ پھر جب وہ بچا کر انہیں خشکی تک پہنچا دیتا ہے تو ان میں سے کوئی اقتصاد برتتا ہے اور ہماری نشانیوں کا انکار نہیں کرتا مگر ہر وہ شخص جو غدار اور ناشکرا ہے');
INSERT INTO "sayyid_qutb" VALUES(31,33,'31:33','لوگو ، بچو اپنے رب کے غضب سے اور ڈرو اس دن سے جب کہ کوئی باپ اپنے بیٹے کی طرف سے بدلہ نہ دے گا اور نہ کوئی بیٹا ہی اپنے باپ کی طرف سے کچھ بدللہ دینے والا ہوگا۔ فی الواقعہ اللہ کا وعدہ سچا ہے۔ پس یہ دنیا کی زندگی تمہیں دھوکے میں نہ ڈالے اور نہ دھوکہ باز تم کو اللہ کے معاملے میں دھوکا دینے پائے');
INSERT INTO "sayyid_qutb" VALUES(31,34,'31:34','اس گھڑی کا علم اللہ ہی کے پاس ہے ، وہی بارش برساتا ہے ، وہی جانتا ہے کہ ماؤں کے پیٹوں میں کیا پرورش پا رہا ہے ، کوئی متنفس نہیں جانتا کہ وہ کیا کمائی کرنے والا ہے ، اور نہ کسی شخص کو یہ خبر ہے کہ کس سرزمین میں اس کو موت آنی ہے ، اللہ ہی سب کچھ جاننے والا اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(32,1,'32:1','ا۔ ل۔ م');
INSERT INTO "sayyid_qutb" VALUES(32,2,'32:2','اس کتاب کی تنزیل بلاشبہ رب العالمین کی طرف سے ہے');
INSERT INTO "sayyid_qutb" VALUES(32,3,'32:3','کیا یہ لوگ کہتے ہیں کہ اس شخص نے اسے خود گھڑ لیا ہے ؟ نہیں بلکہ یہ حق ہے تیرے رب کی طرف سے تاکہ تو متنبہ کرے ایک ایسی قوم کو جس کے پاس تجھ سے پہلے کوئی متنبہ کرنے والا نہیں آیا ، شاید کہ وہ ہدایت پاجائیں');
INSERT INTO "sayyid_qutb" VALUES(32,4,'32:4','وہ اللہ ہی ہے جس نے آسمانوں اور زمین کو اور ان ساری چیزوں کو جو ان کے درمیان ہیں چھ دنوں میں پیدا کیا اور اس کے بعد عرش پر جلوہ فرما ہوا ، اس کے سوا نہ تمہارا کوئی حامی و مددگار ہے اور نہ کوئی اس کے آگے سفارش کرنے والا ، پھر کیا تم ہوش میں نہ آؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(32,5,'32:5','وہ آسمان سے زمین تک دنیا کے معاملات کی تدبیر کرتا ہے اور اس تدبیر کی روداد اس کے حضور جاتی ہے ۔ ایک ایسے دن میں جس کی مقدار تمہارے شمار سے ایک ہزار سال ہے');
INSERT INTO "sayyid_qutb" VALUES(32,6,'32:6','وہی ہے ہر پوشیدہ اور ظاہر کا جاننے والا ، زبردست اور رحیم');
INSERT INTO "sayyid_qutb" VALUES(32,7,'32:7','جو چیز بھی اس نے بنائی خوب ہی بنائی۔ اس نے انسان کی تخلیق کی ابتدا گارے سے کی ،');
INSERT INTO "sayyid_qutb" VALUES(32,8,'32:8','پھر اس کی نسل ایک ایسے ست ست چلائی جو حقیر پانی کی طرح کا ہے');
INSERT INTO "sayyid_qutb" VALUES(32,9,'32:9','پھر اس کو نک سک سے درست کیا اور اس کے اندر اپنی روح پھونک دی اور تم کو کان دئیے ، آنکھیں دیں اور دل دئیے تم لوگ کم ہی شکر گزار ہوتے ہو');
INSERT INTO "sayyid_qutb" VALUES(32,10,'32:10','اور یہ لوگ کہتے ہیں : ” جب ہم مٹی میں رل مل چکے ہوں گے تو کیا ہم پھر نئے سرے سے پیدا کئے جائیں گے ؟ “ اصل بات یہ ہے کہ یہ اپنے رب کی ملاقات کے منکر ہیں');
INSERT INTO "sayyid_qutb" VALUES(32,11,'32:11','ان سے کہو ” موت کا وہ فرشتہ جو تم پر مقرر کیا گیا ہے تم کو پورا کا پورا اپنے قبضے میں لے لے گا اور پھر تم اپنے رب کی طرف پلٹا لائے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(32,12,'32:12','کاش تم دیکھو وہ وقت جب یہ مجرم سر جھکائے اپنے رب کے حضور کھڑے ہوں گے (اس وقت یہ کہہ رہے ہوں گے) ” اے ہمارے رب ، ہم نے خوب دیکھ لیا اور سن لیا ، اب ہمیں عاپس بھیج دے تاکہ ہم نیک عمل کریں ، ہمیں اب یقین آگیا ہے');
INSERT INTO "sayyid_qutb" VALUES(32,13,'32:13','(جواب میں ارشاد ہوگا) ” اگر ہم چاہتے تو پہلے ہی ہر نفس کو اس کی ہدایت دے دیتے مگر میری وہ بات پوری ہوگئی جو میں نے کہی تھی کہ میں جہنم کو جنوں اور انسانوں ، سب سے بھر دوں گا');
INSERT INTO "sayyid_qutb" VALUES(32,14,'32:14','پس اب چکھو مزا اپنی اس حرکت کا کہ تم نے اس دن کی ملاقات کو فراموش کردیا ، ہم نے بھی اب تمہیں فراموش کردیا ہے۔ چکھو ہمیشگی کے عذاب کا مزا اپنے کرتوتوں کی پاداش میں');
INSERT INTO "sayyid_qutb" VALUES(32,15,'32:15','ہماری آیات پر تو وہ لوگ ایمان لاتے ہیں جنہیں یہ آیات سنا کر جب نصیحت کی جاتی ہے تو سجدے میں گر پڑتے ہیں اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کرتے ہیں اور تکبر نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(32,16,'32:16','ان کی پیٹھیں بستروں سے الگ رہتی ہیں اپنے رب کو خوف اور طمع کے ساتھ پکارتے ہیں ، اور جو کچھ رزق ہم نے انہیں دیا ہے ، اس میں سے خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(32,17,'32:17','پھر جیسا کچھ آنکھوں کی ٹھنڈک کا سامان ان کے اعمال کی جزاء میں ان کے لیے چھپا رکھا گیا ہے اس کی کسی متنفس کو خبر نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(32,18,'32:18','بھلا کہیں یہ ہوسکتا ہے کہ جو شخص مومن ہو ، وہ اس شخص کی طرح ہوجائے جو فاسق ہو ؟ یہ دونوں برابر نہیں ہو سکتے');
INSERT INTO "sayyid_qutb" VALUES(32,19,'32:19','جو لوگ ایمان لائے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کے لیے تو جنتوں کی قیام گاہیں ہیں ، ضیافت کے طور پر ان کے اعمال کے بدلے میں');
INSERT INTO "sayyid_qutb" VALUES(32,20,'32:20','اور جنہوں نے فسق اختیار کیا ہے ، ان کا ٹھکانا دوزخ ہے۔ جب کبھی وہ اس سے نکلنا چاہیں گے اسی میں دھکیل دئیے جائیں گے اور ان سے کہا جائے گا کہ چکھو اب اسی آنے کے عذاب کا مزا جس کو تم جھٹلایا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(32,21,'32:21','اس بڑے عذاب سے پہلے ہم اسی دنیا میں (کسی نہ کسی چھوٹے) عذاب کا مزا انہیں چکھاتے رہیں گے ، شاید کہ یہ (اپنی باغیانہ روشن سے ) باز آجائیں');
INSERT INTO "sayyid_qutb" VALUES(32,22,'32:22','اور اس سے بڑا ظالم کون ہوگا جسے اس کے رب کی آیات کے ذریعہ سے نصیحت کی جائے اور پھر وہ ان سے منہ پھیرلے۔ ایسے مجرموں سے تو ہم انتقام لے کر رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(32,23,'32:23','اس سے پہلے ہم موسیٰ کو کتاب دے چکے ہیں ، لہٰذا اسی چیز کے ملنے پر تمہیں کوئی شک نہ ہونا چاہئے۔ اس کتاب کو ہم نے بنی اسرائیل کے لیے ہدایت بنایا تھا');
INSERT INTO "sayyid_qutb" VALUES(32,24,'32:24','اور جب انہوں نے صبر کیا اور ہماری آیات پر یقین لاتے رہے تو ان کے اندر ہم نے ایسے پیشوا پیدا کیے جو ہمارے حکم سے رہنمائی کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(32,25,'32:25','یقیناً تیرا رب ہی قیامت کے روز ان باتوں کا فیصلہ کرے گا جن میں (بنی اسرائیل) باہم اختلاف کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(32,26,'32:26','اور کیا ان لوگوں کو (ان تاریخی واقعات میں) کوئی ہدایت نہیں ملی کہ ان سے پہلے کتنی قوموں کو ہم ہلاک کرچکے ہیں ، جن کے رہنے کی جگہوں میں آج یہ چلتے پھرتے ہیں ؟ اس میں بڑی نشانیاں ہیں ، کیا یہ سنتے نہیں ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(32,27,'32:27','اور کیا ان لوگوں نے یہ منظر کبھی نہیں دیکھا کہ ہم ایک بےآب وگیاہ زمین کی طرف پانی بہا لاتے ہیں اور پھر اسی زمین سے وہ فصل اگاتے ہیں جس سے ان کے جانوروں کو بھی چارہ ملتا ہے اور یہ خود بھی کھاتے ہیں ؟ تو کیا انہیں کچھ نہیں سوجھتا');
INSERT INTO "sayyid_qutb" VALUES(32,28,'32:28','یہ لوگ کہتے ہیں کہ ” یہ فیصلہ کب ہوگا اگر تم سچے ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(32,29,'32:29','ان سے کہو ” فیصلے کے دن ایمان لانا ، ان لوگوں کے لیے کچھ بھی نافع نہ ہوگا جنہوں نے کفر کیا ہے اور پھر ان کو کوئی مہلت نہ ملے گی');
INSERT INTO "sayyid_qutb" VALUES(32,30,'32:30','انہیں ان کے حال پر چھوڑ دو اور انتظار کرو ، یہ بھی منتظر ہیں');
INSERT INTO "sayyid_qutb" VALUES(33,1,'33:1','اے نبی صلى الله عليه وسلم ، اللہ سے ڈرو اور کفار و منافقین کی اطاعت نہ کرو ، حقیقت میں علیم اور حکیم تو اللہ ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(33,2,'33:2','پیروی کرو اس بات کی جس کا اشارہ تمہارے رب کی طرف سے تمہیں کیا جا رہا ہے ، اللہ ہر اس بات سے باخبر ہے جو تم لوگ کرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(33,3,'33:3','اللہ پر توکل کرو ، اللہ ہی وکیل ہونے کے لیے کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(33,4,'33:4','اللہ نے کسی شخص کے دھڑ میں دو دل نہیں رکھے نہ اس نے تم لوگوں کو ان بیویوں کو جن سے تم ظہار کرتے ہو ، تمہاری ماں بنا دیا ہے ، اور نہ اس نے تمہارے منہ بولے بیٹوں کو تمہارا حقیقی بیٹا بنایا ہے۔ یہ تو وہ باتیں ہیں جو لوگ تم اپنے منہ سے نکال دیتے ہو ، مگر اللہ وہ بات کہتا ہے جو مبنی بر حقیقت ہے اور وہی صحیح طریقے کی طرف راہنمائی کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,5,'33:5','منہ بولے بیٹوں کو ان کے باپوں کی نسبت سے پکارو ، یہ اللہ کے نزدیک زیادہ منصفانہ بات ہے۔ اور اگر تمہیں معلوم نہ ہو کہ ان کے باپ کون ہیں تو وہ تمہارے دینی بھائی اور رفیق ہیں۔ نادانستہ جو بات تم کہو ، اس کے لیے تم پر کوئی گرفت نہیں ہے ، لیکن اس بات پر ضرور گرفت ہے جس کا تم دل سے ارادہ کرو ، اللہ درگزر کرنے والا اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,6,'33:6','بلاشبہ نبی تو اہل ایمان کے لئے ان کی اپنی ذات پر مقدم ہے ، اور نبی کی بیویاں ان کی مائیں ہیں ، مگر کتاب اللہ کی رو سے عام مومنین و مہاجرین کی بہ نسبت رشتہ دار ایک دوسرے کے زیادہ حقدار ہیں ، البتہ اپنے رفیقوں کے ساتھ تم کوئی بھلائی (کرنا چاہو تو) کرسکتے ہیں۔ یہ حکم کتاب الٰہی میں لکھا ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,7,'33:7','اور (اے نبی صلى الله عليه وسلم یاد رکھو اس عہد و پیمان کو جس ہم نے سب پیغمبروں سے لیا ہے ، تم سے بھی اور نوح اور ابراہیم اور موسیٰ اور عیسیٰ ابن مریم سے بھی۔ سب سے ہم پختہ عہد لے چکے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(33,8,'33:8','تاکہ سچے لوگوں سے (اب کا رب) ان کی سچائی کے بارے میں سوال کرے ، اور کافروں کے لیے تو اس نے دردناک عذاب مہیا کر ہی رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,9,'33:9','اے لوگو ، جو ایمان لائے ہو ، یاد کرو اللہ کے احسان کو جو (ابھی ابھی) اس نے تم پر کیا ہے۔ جب لشکر تم پر چڑھ آئے تو ہم نے ان پر ایک سخت آندھی بھیج دی اور ایسی فوجیں روانہ کیں جو تم کو نظر نہ آتی تھیں۔ اللہ وہ سب کچھ دیکھ رہا تھا جو تم لوگ اس وقت کر رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(33,10,'33:10','جب دشمن اوپر سے اور نیچے سے تم پر چڑھ آئے ، جب خوف کے مارے آنکھیں پتھرا گئیں ، کلیجے منہ کو آگئے اور تم لوگ اللہ کے بارے میں طرح طرح کے گمان کرنے لگے');
INSERT INTO "sayyid_qutb" VALUES(33,11,'33:11','اس وقت ایمان لا نے والے خوب آزمائے گئے اور بری طرح ہلا مارے گئے۔');
INSERT INTO "sayyid_qutb" VALUES(33,12,'33:12','یاد کرو وہ وقت جب منافقین اور وہ سب لوگ جن کے دلوں میں روگ تھا صاف صاف کہہ رہے تھے کہ اللہ اور اس کے رسول صلى الله عليه وسلم نے جو وعدے ہم سے کیے تھے ، وہ فریب کے سوا کچھ نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(33,13,'33:13','جب ان میں سے ایک گروہ نے کہا کہ ” اے یثرب کے لوگو ، تمہارے لیے اب ٹھہرنے کا کوئی موقع نہیں ہے ، پلٹ جاؤ “۔ جب ان کا ایک فریق یہ کہہ کر نبی صلى الله عليه وسلم سے رخصت طلب کر رہا تھا کہ ” ہمارے گھر خطرے میں ہیں ، حالانکہ وہ خطرے میں نہ تھے ، دراصل وہ (محاذ جنگ سے) بھاگنا چاہتے تھے');
INSERT INTO "sayyid_qutb" VALUES(33,14,'33:14','اگر شہر کے اطراف سے دشمن گھس آئے ہوتے اور اس وقت انہیں فتنے کی طرف دعوت دی جاتی تو یہ اس میں جا پڑتے اور مشکل ہی سے انہیں شریک فتنہ ہونے میں کوئی تامل ہوتا');
INSERT INTO "sayyid_qutb" VALUES(33,15,'33:15','ان لوگوں نے اس سے پہلے اللہ سے عہد کیا تھا کہ یہ پیٹھ نہ پھیریں گے ، اور اللہ سے کیے ہوئے عہد کی باز پرس تو ہونی ہی تھی');
INSERT INTO "sayyid_qutb" VALUES(33,16,'33:16','اے نبی صلى الله عليه وسلم ان سے کہو اگر تم موت یا قتل سے بھاگو تو یہ بھاگنا تمہارے لیے کچھ نفع بخش نہ ہوگا۔ اس کے بعد زندگی کے مزے لوٹنے کا تھوڑا ہی موقعہ تمہیں مل سکے گا');
INSERT INTO "sayyid_qutb" VALUES(33,17,'33:17','ان سے کہو کون ہے جو تمہیں اللہ سے بچا سکتا ہو۔ اگر وہ تمہیں نقصان پہنچانا چاہے اور کون اس کی رحمت کو روک سکتا ہے اگر تم پر مہربانی کرنا چاہے۔ اللہ کے مقابلے میں تو یہ لوگ کوئی حامی و مددگار نہیں پاسکتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(33,18,'33:18','اللہ تم میں سے ان لوگوں کو خوب جانتا ہے جو (جنگ کے کام میں) رکاوٹیں ڈالنے والے ہیں ، جو اپنے بھائیوں سے کہتے ہیں کہ ” آؤ ہماری طرف جو لڑائی میں حصہ لیتے بھی ہیں تو بس نام گنانے کو');
INSERT INTO "sayyid_qutb" VALUES(33,19,'33:19','یہ تمہارا ساتھ دینے میں بہت بخلیل ہیں خطرے کا وقت آجائے تو دیدے پھرا پھرا کر تمہاری طرف دیکھتے ہیں جیسے کسی مرنے والے پر غشی طاری ہو رہی ہو مگر جب خطرہ گزر جاتا ہے تو یہی تمہارے استقبال میں قینچی کی طرح تیز زبانوں کے ساتھ آجاتے ہیں مال کے لئے بہت لالچی ہیں یہ لوگ ہرگز ایمان نہیں لاتے اس لیے اللہ نے ان کے اعمال ضائع کر دئیے اور ایسا کرنا اللہ کے لیے بہت آسان ہے');
INSERT INTO "sayyid_qutb" VALUES(33,20,'33:20','سمجھ رہے ہیں کہ حملہ آور گروہ ابھی گئے نہیں ہیں اور اگر وہ پھر حملہ آور ہوجائیں تو ان کا جی چاہتا ہے کہ اس موقع پر کہیں صحرا میں بدوؤں کے درمیان جا بیٹھیں اور وہیں سے تمہارے حالات پوچھتے رہیں تاہم اگر یہ تمہارے درمیان رہے بھی تو لڑائی میں کم ہی حصہ لیں گے');
INSERT INTO "sayyid_qutb" VALUES(33,21,'33:21','در حقیقت تم لوگوں کے لیے اللہ کے رسول صلى الله عليه وسلم میں ایک بہترین نمونہ تھا ، ہر اس شخص کے لیے جو اللہ اور یوم آخر کا امیدوار ہو اور کثرت سے اللہ کو یاد کرے');
INSERT INTO "sayyid_qutb" VALUES(33,22,'33:22','اور سچے مومنوں (کا حال اس وقت یہ تھا) جب انہوں نے حملہ آور لشکروں کو دیکھا تو پکار اٹھے کہ ” یہ وہی چیز ہے جس کا اللہ اور اس کے رسول صلى الله عليه وسلم نے ہم سے وعدہ کیا تھا ، اللہ اور اس کے رسول صلى الله عليه وسلم کی بات بالکل سچی تھی “۔ اس واقعہ نے ان کے ایمان اور ان کی سپردگی کو اور زیادہ بڑھا دیا');
INSERT INTO "sayyid_qutb" VALUES(33,23,'33:23','ایمان لانے والوں میں ایسے لوگ موجود ہیں جنہوں نے اللہ سے کیے ہوئے عہد کو سچا کر دکھایا ہے۔ ان میں سے کوئی اپنی نذر پوری کرچکا اور کوئی وقت آنے کا منتظر ہے۔ انہوں نے اپنے رویے میں کوئی تبدیلی نہیں کی');
INSERT INTO "sayyid_qutb" VALUES(33,24,'33:24','(یہ سب کچھ اس لیے ہوا) ” تاکہ اللہ سچوں کو ان کی سچائی کی جزا دے اور منافقوں کو چاہے تو سزا دے اور چاہے تو ان کی توبہ قبول کرلے ، بیشک اللہ غفور و رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,25,'33:25','اللہ نے کفار کا منہ پھیر دیا ، وہ کوئی فائدہ حاصل کیے بغیر اپنے دل کی جلن لیے یونہی پلٹ گئے اور مومنین کی طرف سے اللہ ہی لڑنے کے لیے کافی ہوگیا ، اللہ بڑی قوت والا اور زبردست ہے');
INSERT INTO "sayyid_qutb" VALUES(33,26,'33:26','پھر اہل کتاب میں سے جن لوگوں نے ان حملہ آوروں کا ساتھ دیا تھا ، اللہ ان کی گڑھیوں سے انہیں اتار لایا اور ان کے دلوں میں اس نے ایسا رعب ڈال دیا کہ آج ان میں سے ایک گروہ کو تم قتل کر رہے ہو اور دوسرے گروہ کو قید کر رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(33,27,'33:27','اس نے تم کو ان کی زمین اور ان کے گھروں اور ان کے اموال کا وارث بنا دیا اور وہ علاقہ تمہیں دیا جسے تم نے کبھی پامال نہ کیا تھا۔ اللہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(33,28,'33:28','اے نبی صلى الله عليه وسلم ، اپنی بیویوں سے کہو ، اگر تم دنیا اور اس کی زینت چاہتی ہو تو آؤ، میں تمہیں کچھ دے دلا کر بھلے طریقے سے رخصت کر دوں۔');
INSERT INTO "sayyid_qutb" VALUES(33,29,'33:29','اور اگر تم اللہ اور اس کے رسول صلى الله عليه وسلم اور دار آخرت کی طالب ہو تو جان لو کہ تم میں سے جو نیکو کار ہیں اللہ نے ان کے لیے بڑا اجر مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,30,'33:30','نبی صلى الله عليه وسلم کی بیویو ، تم میں سے جو کسی صریح فحش حرکت کا ارتکاب کرے گی اسے دو ہرا عذاب دیا جائے گا ، اللہ کے لیے یہ بہت آسان کام ہے');
INSERT INTO "sayyid_qutb" VALUES(33,31,'33:31','اور تم میں سے جو اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت کرے گی اور نیک عمل کرے گی اس کو ہم دوہرا اجر دیں گے اور ہم نے اس کیلئے رزق کریم مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,32,'33:32','نبی صلى الله عليه وسلم کی بیویو ، تم عام عورتوں کی طرح نہیں ہو ، اگر تم اللہ سے ڈرنے والی ہو تو دبی زبان سے بات نہ کیا کرو کہ دل کی خرابی میں مبتلا کوئی شخص لالچ میں پڑجائے بلکہ صاف سیدھی بات کرو');
INSERT INTO "sayyid_qutb" VALUES(33,33,'33:33','اپنے گھروں میں ٹک کر رہو اور سابق دور جاہلیت کی سی سج دھج نہ دکھاتی پھرو۔ نماز قائم کرو ، زکوٰۃ دو اور اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت کرو۔ اللہ تو یہ چاہتا ہے کہ تم اہل بیت نبی صلى الله عليه وسلم سے گندگی کو دور کرے اور تمہیں پوری طرح پاک کر دے');
INSERT INTO "sayyid_qutb" VALUES(33,34,'33:34','یاد رکھو اللہ کی آیات اور حکمت کی ان باتوں کو جو تمہارے گھروں میں سنائی جاتی ہیں ، بیشک اللہ لطیف اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(33,35,'33:35','بالقین جو مرد اور جو عورتیں مسلم ہیں ، مومن ہیں ، مطیع فرمان ہیں ، راست باز ہیں ، صابر ہیں ، اللہ کے آگے جھکنے والے ہیں ، صدقہ دینے والے ہیں ، روزے رکھنے والے ہیں ، اپنی شرم گاہوں کی حفاظت کرنے والے ہیں ، اور اللہ کو کثرت سے یاد کرنے والے ہیں ، اللہ نے ان کے لیے مغفرت اور بڑا اجر مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,36,'33:36','کسی مومن مرد اور کسی مومن عورت کو یہ حق نہیں ہے کہ جب اللہ اور اس کا رسول صلى الله عليه وسلم کسی معاملے کا فیصلہ کر دے تو پھر اسے اپنے اس معاملے میں خود فیصلہ کرنے کا اختیار حاصل رہے اور جو کوئی اللہ اور اس کے رسول صلى الله عليه وسلم کی نافرمانی کرے تو وہ صریح گمراہی میں پڑ گیا');
INSERT INTO "sayyid_qutb" VALUES(33,37,'33:37','اے نبی صلى الله عليه وسلم ، یاد کرو وہ موقع جب تم اس شخص سے کہہ رہے تھے جس پر اللہ نے اور تم نے احسان کیا تھا کہ ” اپنی بیوی کو نہ چھوڑو اور اللہ سے ڈرو “۔ اس وقت تم اپنے دل میں وہ بات چھپائے ہوئے تھے جسے اللہ کھولنا چاہتا تھا ، تم لوگوں سے ڈر رہے تھے حالانکہ اللہ اس کا زیادہ حق دار ہے کہ تم اس سے ڈرو۔ پھر جب زید اس سے اپنی حاجت پوری کرچکا تو ہم نے اس (مطلقہ خاتون) کا تم سے نکاح کردیا تاکہ مومنوں پر اپنے منہ بولے بیٹوں کی بیویوں کے معاملہ میں کوئی تنگی نہ رہے جبکہ وہ ان سے اپنی حاجت پوری کرچکے ہوں اور اللہ کا حکم تو عمل میں آنا ہی چاہئے تھا');
INSERT INTO "sayyid_qutb" VALUES(33,38,'33:38','نبی پر کسی ایسے کام میں کوئی رکاوٹ نہیں ہے جو اللہ نے اس کے لیے مقرر کردیا ہو۔ یہی اللہ کی سنت ان سب انبیاء کے معاملہ میں رہی ہے جو پہلے گزر چکے ہیں اور اللہ کا حکم ایک قطعی طے شدہ فیصلہ ہوتا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,39,'33:39','(یہ اللہ کی سنت ہے ان لوگوں کے لیے) جو اللہ کے پیغامات پہنچاتے ہیں اور اسی سے ڈرتے ہیں اور ایک خدا کے سوا کسی سے نہیں ڈرتے اور محاسبہ کے لئے بس اللہ ہی کافی ہے');
INSERT INTO "sayyid_qutb" VALUES(33,40,'33:40','(لوگو) محمد صلى الله عليه وسلم تمہارے مردوں میں سے کسی کے باپ نہیں ہیں ، مگر وہ اللہ کے رسول اور خاتم النبین ہیں ، اور اللہ ہر چیز کا علم رکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,41,'33:41','اے لوگو ! جو ایمان لائے ہو ، اللہ کو کثرت سے یاد کرو');
INSERT INTO "sayyid_qutb" VALUES(33,42,'33:42','اور صبح و شام اس کی تسبیح کرتے رہو');
INSERT INTO "sayyid_qutb" VALUES(33,43,'33:43','وہی ہے جو تم پر رحمت فرماتا ہے اور اس کے ملائکہ تمہارے لیے دعائے رحمت کرتے ہیں تاکہ وہ تمہیں تاریکیوں سے روشنی میں نکال لائے ، وہ مومنوں پر بہت مہربان ہے');
INSERT INTO "sayyid_qutb" VALUES(33,44,'33:44','جس روز وہ اس سے ملیں گے ، ان کا استقبال سلام سے ہوگا اور ان کے لیے اللہ نے بڑا باعزت اجر مہیا کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,45,'33:45','اے نبی صلى الله عليه وسلم ، ہم نے تمہیں بھیجا ہے گواہ بنا کر ، بشارت دینے والا اور ڈرانے والا بنا کر');
INSERT INTO "sayyid_qutb" VALUES(33,46,'33:46','اللہ کی اجازت سے اس کی طرف دعوت دینے والا بنا کر اور روشن چراغ بنا کر');
INSERT INTO "sayyid_qutb" VALUES(33,47,'33:47','پشارت دے دو ان لوگوں کو جو (تم پر) ایمان لائے ہیں کہ ان کے لیے اللہ کی طرف سے بڑا فضل ہے');
INSERT INTO "sayyid_qutb" VALUES(33,48,'33:48','اور ہرگز نہ دبو کفارومنافقین سے ، کوئی پروانہ کرو ان کی اذیت رسانی کی اور بھروسہ کرلو اللہ پر ، اللہ ہی اس کے لئے کافی ہے کہ آدمی اپنے معاملات اس کے سپرد کر دے');
INSERT INTO "sayyid_qutb" VALUES(33,49,'33:49','اے لوگو جو ایمان لائے ہو ، جب تم مومن عورتوں سے نکاح کرو ، اور پھر انہیں ہاتھ لگانے سے پہلے طلاق دے دو تو تمہاری طرف سے ان پر کوئی عدت لازم نہیں ہے جس کے پورے ہونے کا تم مطالبہ کرسکو۔ لہٰذا انہیں کچھ مال دو اور بھلے طریقے سے رخصت کر دو');
INSERT INTO "sayyid_qutb" VALUES(33,50,'33:50','اے نبی صلى الله عليه وسلم ، ہم نے تمہارے لیے حلال کردیں تمہاری وہ بیویاں جن کے مہر تم نے ادا کیے ہیں ، اور وہ عورتیں جو اللہ کی عطا کردہ لونڈیوں میں سے تمہاری ملکیت میں آئیں ، اور تمہاری وہ چچا زاد اور پھوپھی زاد اور ماموں زاد اور خالہ زاد بہنیں جنہوں نے تمہارے ساتھ ہجرت کی ہے اور وہ مومن عورت جس نے اپنے آپ کو نبی صلى الله عليه وسلم کے لیے ہبہ کیا ہو اگر نبی اسے نکاح میں لینا چاہے۔ یہ رعایت خالصتا تمہارے لیے ہے۔ دوسرے مومنوں کے لیے نہیں ہے۔ ہم کو معلوم ہے کہ عام مومنوں پر ان کی بیویوں اور لونڈیوں کے بارے میں ہم نے کیا حدود عائد کیے ہیں۔ تمہیں ان حدود سے ہم نے اس لیے مستثنیٰ کیا ہے تاکہ تمہارے اوپر کوئی تنگی نہ رہے ، اور اللہ غفورورحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,51,'33:51','تم کو اختیار دیا جاتا ہے کہ اپنی بیویوں میں سے جس کو چاہو ، اپنے سے الگ رکھو ، جسے چاہو اپنے ساتھ رکھو اور جسے چاہو الگ رکھنے کے بعد اپنے پاس بلا لو۔ اس معاملہ میں تم پر کوئی مضائقہ نہیں ہے۔ اس طرح زیادہ متوقع ہے کہ ان کی آنکھیں ٹھنڈی رہیں گی اور وہ رنجیدہ نہ ہوں گی اور جو کچھ بھی تم ان کو دو گے اس پر وہ سب راضی رہیں گی۔ اللہ جانتا ہے کہ جو کچھ تم لوگوں کے دلوں میں ہے ، اور اللہ علیم و حلیم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,52,'33:52','اس کے بعد تمہارے لیے دوسری عورتیں حلال نہیں ہیں ، اور نہ اس کی اجازت ہے کہ ان کی جگہ اور بیویاں لے آؤ خواہ ان کا حسن تمہیں کتنا ہی پسند ہو ، البتہ لونڈیوں کی تمہیں اجازت ہے۔ اللہ ہر چیز پر نگران ہے');
INSERT INTO "sayyid_qutb" VALUES(33,53,'33:53','اے لوگو ، جو ایمان لائے ہو ، نبی کے گھروں میں بلا اجازت نہ چلے آیا کرو۔ نہ کھانے کا وقت تاکتے رہو۔ ہاں اگر تمہیں کھانے پر بلایا جائے تو ضرور آؤ۔ مگر جب کھانا کھالو تو منتشر ہوجاؤ۔ باتیں کرنے میں نہ لگے رہو۔ تمہاری یہ حرکتیں نبی صلى الله عليه وسلم کو تکلیف دیتی ہیں ، مگر وہ شرم کی وجہ سے کچھ نہیں کتے۔ اور اللہ حق بات کہنے میں نہیں شرماتا۔ نبی صلى الله عليه وسلم کی بیویوں سے اگر تمہیں کچھ مانگنا ہو تو پردے کے پیچھے سے مانگا کرو۔ یہ تمہارے اور ان کے دلوں کی پاکیزگی کے لیے زیادہ مناسب طریقہ ہے۔ تمہارے لیے یہ ہرگز جائز نہیں کہ اللہ کے رسول صلى الله عليه وسلم کو تکلیف دو ، اور نہ یہ جائز ہے کہ ان کے بعد ان کی بیویوں سے نکاح کرو ، یہ اللہ کے نزدیک بہت بڑا گناہ ہے ۔ تم خواہ کوئی بات ظاہر کرو یا چھپاؤ، اللہ کو ہر بات کا علم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,54,'33:54','تم خواہ کوئی بات ظاہر کرو یا چھپاؤ، اللہ کو ہر بات کا علم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,55,'33:55','ازواج نبی صلى الله عليه وسلم کے لیے اس میں کوئی مضائقہ نہیں ہے کہ ان کے باپ ، ان کے بیٹے ، ان کے بھائی۔ ان کے بھتیجے ، ان کے بھانجے ، ان کے میل جول کی عورتیں اور ان کے مملوک گھروں میں آئیں (اے عورتو) تمہیں اللہ کی نافرمانی سے پرہیز کرنا چاہئے اللہ ہر چیز پر نگاہ رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,56,'33:56','اللہ اور اس کے ملائکہ نبی صلى الله عليه وسلم پر درود بھیجے ہیں ، اے لوگو جو ایمان لائے ہو ، تم بھی ان پر دوروسلام بھیجو۔');
INSERT INTO "sayyid_qutb" VALUES(33,57,'33:57','جو لوگ اور اس کے رسول صلى الله عليه وسلم کو اذیت دیتے ہیں ان پر دنیا اور آخرت میں اللہ نے لعنت فرمائی ہے اور ان کے لیے رسوا کن عذاب مہیا کردیا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,58,'33:58','اور جو لوگ مومن مردوں اور عورتوں کو بےقصود اذیت دیتے ہیں انہوں نے ایک بڑے بہتان اور صریح گناہ کا وبال اپنے سر لے لیا ہے');
INSERT INTO "sayyid_qutb" VALUES(33,59,'33:59','اے نبی صلى الله عليه وسلم اپنی بیویوں اور بیٹیوں اور اہل ایمان کی عورتوں سے کہہ دو کہ اپنے اوپر اپنی چادروں کے پلو لٹکا لیا کریں۔ یہ زیادہ مناسب طریقہ ہے تاکہ وہ پہچان لی جائیں اور نہ ستائی جائیں۔ اللہ تعالیٰ غفور و رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(33,60,'33:60','اگر منافقین اور وہ لوگ جن کے دلوں میں خرابی ہے ، اور وہ جو مدینہ میں ہیجان انگیز افوائیں پھیلانے والے ہیں ، اپنی حرکتوں سے باز نہ آئے تو ہم ان کے خلاف کاروائی کرنے کے لیے تمہیں اٹھا کھڑا کریں گے ، پھر وہ اس شہر میں مشکل ہی سے تمہارے ساتھ رہ سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(33,61,'33:61','ان پر ہر طرف سے لعنت کی بوچھاڑ ہوگی ، جہاں کہیں پائے جائیں گے ، پکڑے جائیں گے اور بری طرح مارے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(33,62,'33:62','یہ اللہ کی سنت ہے جو ایسے لوگوں کے معاملے میں پہلے سے چلی آرہی ہے ، اور تم اللہ کی سنت میں کوئی تبدیلی نہ پاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(33,63,'33:63','لوگ تم سے پوچھتے ہیں کہ قیامت کی گھڑی کب آئے گی۔ کہو اس کا علم تو اللہ ہی کو ہے۔ تمہیں کیا خبر ، شاید کہ وہ قریب ہی آلگی ہو');
INSERT INTO "sayyid_qutb" VALUES(33,64,'33:64','بہرحال یہ امر یقینی ہے کہ اللہ نے کافروں پر لعنت کی ہے اور ان کے لئے بھڑکتی ہوئی آگ مہیا کردی ہے');
INSERT INTO "sayyid_qutb" VALUES(33,65,'33:65','جس میں وہ ہمیشہ رہیں گے ، کوئی حامی و مددگار نہ پاسکیں گے');
INSERT INTO "sayyid_qutb" VALUES(33,66,'33:66','جس روز ان کے چہرے آگ پر الٹ پلٹ کیے جائیں گے اس وقت وہ کہیں گے کہ ” کاش ہم نے اللہ اور رسول صلى الله عليه وسلم کی اطاعت کی ہوتی');
INSERT INTO "sayyid_qutb" VALUES(33,67,'33:67','اور کہیں گے ” اب رب ہمارے ، ہم نے اپنے سرداروں اور اپنے بڑوں کی اطاعت کی اور انہوں نے ہمیں راہ راست سے بےراہ کر دیا');
INSERT INTO "sayyid_qutb" VALUES(33,68,'33:68','اے رب ، ان کو دوہرا عذاب دے اور ان پر سخت لعنت کر');
INSERT INTO "sayyid_qutb" VALUES(33,69,'33:69','اے لوگو جو ایمان لائے ہو ، ان لوگوں کی طرح نہ بن جاؤ جنہوں نے موسیٰ کو اذیتیں دی تھیں ، پھر اللہ نے ان کو بنائی ہوئی باتوں سے اس کی براءت فرمائی اور وہ اللہ کے نزدیک باعزت تھا');
INSERT INTO "sayyid_qutb" VALUES(33,70,'33:70','اے ایمان لانے والو ، اللہ سے ڈرو اور ٹھیک بات کیا کرو');
INSERT INTO "sayyid_qutb" VALUES(33,71,'33:71','اللہ تمہارے اعمال درست کر دے گا اور تمہارے قصوروں سے درگزر فرمائے گا۔ جو شخص اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت کرے اس نے بڑی کامیابی حاصل کی');
INSERT INTO "sayyid_qutb" VALUES(33,72,'33:72','ہم نے اس امانت کو آسمانوں ، اور زمین اور پہاڑوں کے سامنے پیش کیا تو وہ اسے اٹھانے کے لیے تیار نہ ہوئے اور اس سے ڈر گئے ، مگر انسان نے اسے اٹھا لیا ، بیشک وہ بڑا ظالم اور جاہل ہے');
INSERT INTO "sayyid_qutb" VALUES(33,73,'33:73','اس بار امانت کو اٹھانے کا لازمی نتیجہ ہے تاکہ اللہ منافق مردوں اور عورتوں کی توبہ قبول کرے ، اللہ درگزر فرمانے والا اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(34,1,'34:1','حمد اس خدا کے لئے ہے جو آسمانوں اور زمین کی ہر چیز کا مالک ہے اور آخرت میں بھی اسی کے لیے حمد ہے۔ وہ دانا اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(34,2,'34:2','جو کچھ زمین میں جاتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے اترتا ہے اور جو کچھ اس میں چڑھتا ہے ، ہر چیز کو وہ جانتا ہے ، وہ رحیم اور غفور ہے');
INSERT INTO "sayyid_qutb" VALUES(34,3,'34:3','منکرین کہتے ہیں کیا بات ہے کہ قیامت ہم پر نہیں آرہی ہے ! کہو ” قسم ہے میرے عالم الغیب پروردگار کی ، وہ تم پر آکر رہے گی۔ اس سے ذرہ برابر کوئی چیز نہ آسمانوں میں چھپی ہوئی ہے نہ زمین میں۔ نہ ذرے سے بڑی اور نہ اس سے چھوٹی۔ سب کچھ ایک نمایاں دفتر میں درج ہے');
INSERT INTO "sayyid_qutb" VALUES(34,4,'34:4','اور یہ قیامت اس لیے آئے گی کہ جزا دے اللہ ان لوگوں کو جو ایمان لائے ہیں اور نیک عمل کرتے رہے ہیں۔ ان کے لئے مغفرت ہے اور رزق کریم');
INSERT INTO "sayyid_qutb" VALUES(34,5,'34:5','اور جن لوگوں نے ہماری آیات کو نیچا دکھانے کے لئے زور لگایا ہے ، ان کے لیے بدترین قسم کا دردناک عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(34,6,'34:6','اے نبی صلى الله عليه وسلم ، علم رکھنے والے خوب جانتے ہیں کہ جو کچھ تمہارے رب کی طرف سے تم پر نازل کیا گیا ہے وہ سراسر حق ہے اور خدائے عزیز وحمید کا راستہ دکھاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(34,7,'34:7','منکرین لوگوں سے کہتے ہیں ” ہم بتائیں تمہیں ایسا شخص جو خبر دیتا ہے کہ جب تمہارے جسم کا ذرہ ذرہ منتشر ہوچکا ہوگا اس وقت تم نئے سرے سے پیدا کر دئیے جاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(34,8,'34:8','نہ معلوم یہ شخص اللہ کے نام سے جھوٹ گھڑتا ہے یا اسے جنون لاحق ہے “۔ نہیں ، بلکہ جو لوگ آخرت کو نہیں مانتے وہ عذاب میں مبتلا ہونے والے ہیں اور وہی بری طرح بہکے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(34,9,'34:9','کیا انہوں نے کبھی اس آسمان و زمین کو نہیں دیکھا جو انہیں آگے اور پیچھے سے گھیرے ہوئے ہے ؟ ہم چاہیں تو انہیں زمین میں دھنسا دیں ، یا آسمان سے کچھ ٹکڑے ان پر گرا دیں۔ درحقیقت اس میں ایک نشانی ہے ہر اس بندے کے لیے جو خدا کی طرف رجوع کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(34,10,'34:10','ہم نے داؤد کو اپنے ہاں سے بڑا فضل عطا کیا تھا۔ (ہم نے حکم دیا کہ) اے پہاڑو ، اس کے ساتھ ہم آہنگی کرو۔ (اور یہی حکم ہم نے) پرندوں کو دیا۔ ہم نے لوہے کو اس کے لئے نرم کر دیا');
INSERT INTO "sayyid_qutb" VALUES(34,11,'34:11','اس ہدایت کے ساتھ کہ زر ہیں بنا اور اس کے حلقے ٹھیک اندازے سے رکھ اے آل داؤد نیک عمل کرو ، جو کچھ تم کرتے ہو ، میں دیکھ رہا ہوں');
INSERT INTO "sayyid_qutb" VALUES(34,12,'34:12','اور سلیمان کے لیے ہم نے ہوا کو مسخر کردیا ، صبح کے وقت اس کا چلنا ایک مہینے کی راہ تک اور شام کے وقت اس کا چلنا ایک مہینے کی راہ تک۔ ہم نے اس کے لئے پگھلے ہوئے تانبے کا چشمہ بہا دیا اور ایسے جن اس کے تابع کر دئیے جن ان کے تابع کر دئیے جو اپنے رب کے حکم سے اس کے آگے کام کرتے تھے ان میں سے جو ہمارے حکم سے سرتابی کرتا اس کو ہم بھڑکتی ہوئی آگ کا مزہ چکھاتے');
INSERT INTO "sayyid_qutb" VALUES(34,13,'34:13','وہ اس کے لیے بناتے تھے جو کچھ وہ چاہتا تھا۔ اونچی عمارات ، تصاویر ، بڑے بڑے حوض جیسے لگن اور اپنی جگہ سے نہ ہٹنے والی بڑی بڑی دیگیں اے آل داؤد عمل کرو شکر کے طریقے پر میرے بندوں میں سے کم ہی شکر گزار ہیں');
INSERT INTO "sayyid_qutb" VALUES(34,14,'34:14','پھر جب سلیمان (علیہ السلام) پر ہم نے موت کا فیصلہ نافذ کیا تو جنوں کو اس کی موت کا پتہ دینے والی کوئی چیز اس گھن کے سوا نہ تھی جو اس کے عصا کو کھا رہا تھا۔ اس طرح جب سلیمان گر پڑا تو جنوں پر یہ بات کھل گئی کہ اگر وہ غیب کے جاننے والے ہوتے تو اس ذلت کے عذاب میں مبتلا نہ رہتے');
INSERT INTO "sayyid_qutb" VALUES(34,15,'34:15','سبا کے لیے ان کے اپنے مسکن ہی میں ایک نشانی موجود تھے ، دو باغ دائیں اور بائیں۔ کھاؤ اپنے رب کا رزق اور شکر بجا لاؤ اس کا ، ملک ہے عمدہ و پاکیزہ اور پروردگار ہے بخشش فرمانے والا');
INSERT INTO "sayyid_qutb" VALUES(34,16,'34:16','مگر وہ منہ موڑ گئے۔ آخر کار ہم نے ان پر بند توڑ سیلاب بھیج دیا اور ان کے پچھلے دو باغوں کی جگہ دو اور باغ انہیں دئیے جن میں کڑوے کسیلے پھل اور جھاؤ کے درخت تھے اور کچھ تھوڑی سی بیریاں');
INSERT INTO "sayyid_qutb" VALUES(34,17,'34:17','یہ تھا ان کے کفر کا بدلہ جو ہم نے ان کو دیا ، اور ناشکرے انسان کے سوا ایسا بدلہ ہم اور کسی کو نہیں دیتے');
INSERT INTO "sayyid_qutb" VALUES(34,18,'34:18','اور ہم نے ان کے اور ان بستیوں کے درمیان ، جن کو ہم نے برکت عطا کی تھی ، نمایاں بستیاں بسا دی تھی اور ان میں سفر کی مسافتیں ایک اندازے پر رکھ دی تھیں۔ چلو پھرو ان راستوں میں رات دن پورے امن کے ساتھ');
INSERT INTO "sayyid_qutb" VALUES(34,19,'34:19','مگر انہوں نے کہا ” اے ہمارے رب ، ہمارے سفر کی مسافتیں لمبی کر دے انہوں نے اپنے اوپر آپ ظلم کیا آخر کار ہم نے انہیں افسانہ بنا کر رکھ دیا اور انہیں بالکل تتر بتر کر ڈالا یقیناً اس میں نشانیاں ہیں ہر اس شخص کے لیے جو بڑا صابر و شاکر ہو');
INSERT INTO "sayyid_qutb" VALUES(34,20,'34:20','ان کے معاملہ میں ابلیس نے اپنا گمان صحیح پایا اور انہوں نے اسی کی پیروی کی ، بجز ایک تھوڑے سے گروہ کے جو مومن تھا۔');
INSERT INTO "sayyid_qutb" VALUES(34,21,'34:21','ابلیس کو ان پر کوئی اقتدار حاصل نہ تھا مگر جو کچھ ہوا وہ اس لیے ہوا کہ ہم یہ دیکھنا چاہتے تھے کہ کون آخرت کا ماننے والا ہے اور کون اس کی طرف سے شک میں پڑا ہوا ہے۔ تیرا رب ہر چیز پر نگران ہے');
INSERT INTO "sayyid_qutb" VALUES(34,22,'34:22','(” اے نبی ؐ “ ان مشرکین سے ) کہو کہ ” پکار دیکھو اپنے ان معبود وں کو جنہیں تم اللہ کے سوا اپنا معبود سمجھتے بیٹھتے ہو ۔ وہ نہ آسمانوں میں کسی ذرہ برابر چیز کے مالک ہیں نہ زمین میں ۔ وہ آسمان و زمین کی ملکیت میں شریک بھی نہیں ہیں ۔ ان میں سے کوئی اللہ کا مددگار بھی نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(34,23,'34:23','اور اللہ کے حضور کوئی شفاعت بھی کسی کے لیے نافع نہیں ہوسکتی بجز اس شخص کے جس کے لیے اللہ نے سفارش کی اجازت دی ہو حتیٰ کہ جب لوگوں کے دلوں سے گھبراہٹ دور ہوگی تو وہ (سفارش کرنے والوں سے) پوچھیں گے کہ تمہارے رب نے کیا جواب دیا ، وہ کہیں گے کہ ٹھیک جواب ملا ہے اور وہ بزرگ و برتر ہے');
INSERT INTO "sayyid_qutb" VALUES(34,24,'34:24','(اے نبی ؐ) ان سے پوچھو ” کون تم کو آسمانوں اور زمین سے رزق دیتا ہے ؟ “ کہو ، اللہ ۔ اب لا محالہ ہم میں اور تم میں سے کوئی ایک ہی ہدایت پر ہے یا کھلی گمراہی میں پڑا ہوا ہے');
INSERT INTO "sayyid_qutb" VALUES(34,25,'34:25','ان سے کہو ، جو قصود ہم نے کیا ہو اس کی کوئی باز پرس تم سے نہ ہوگی اور جو کچھ تم کر رہے ہو ، اس کی کوئی جواب طلبی ہم سے نہیں کی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(34,26,'34:26','کہو ، ہمارا رب ہم کو جمع کرے گا ، پھر ہمارے درمیان ٹھیک ٹھیک فیصلہ کر دے گا۔ وہ ایسا زبردست حالم ہے جو سب کچھ جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(34,27,'34:27','ان سے کہو ، ذرا مجھے دکھاؤ تو سہی وہ کون ہستیاں ہیں جنہیں تم نے اس کے ساتھ شریک لگا رکھا ہے۔ ہرگز نہیں زبردست اور دانا تو بس وہ اللہ ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(34,28,'34:28','اور (اے نبی صلى الله عليه وسلم ہم نے تم کو تمام ہی انسانوں کے لئے بشیر و نذیر بنا کر بھیجا ہے ، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(34,29,'34:29','یہ لوگ تم سے کہتے ہیں کہ ” وہ (قیامت کا) وعدہ کب پورا ہوگا اگر تم سچے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(34,30,'34:30','کہو ، تمہارے لیے ایک ایسے دن کی میعاد مقرر ہے جس کے آنے میں نہ ایک گھڑی بھر کی تاخیر تم کرسکتے ہو اور نہ ایک گھڑی بھر پہلے اسے لاسکتے ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(34,31,'34:31','یہ کافر کہتے ہیں کہ ” ہم ہرگز اس قرآن کو نہ مانیں گے اور نہ اس سے پہلے آئی ہوئی کسی کتاب کو تسلیم کریں گے کاش تم دیکھو ان کا حال اس وقت جب یہ ظالم اپنے رب کے حضور کھڑے ہوں گے۔ اس وقت یہ ایک دوسرے پر الزام دھریں گے۔ جو لوگ دنیا میں دبا کر رکھے گئے تھے وہ بڑے بننے والوں سے کہیں گے کہ ” اگر تم نہ ہوتے تو ہم مومن ہوتے');
INSERT INTO "sayyid_qutb" VALUES(34,32,'34:32','وہ بڑے بننے والے ان دبے ہوئے لوگوں کو جواب دیں گے۔ ” کیا ہم نے تمہیں اس ہدایت سے روکا تھا جو تمہارے پاس آئی تھی ؟ نہیں ، بلکہ تم خود ہی مجرم تھے');
INSERT INTO "sayyid_qutb" VALUES(34,33,'34:33','وہ دبے ہوئے لوگ ان بڑے بننے والوں سے کہیں گے ، ” نہیں ، بلکہ شب و روز کی مکاری تھی جب تم ہم سے کہتے تھے کہ ہم اللہ سے کفر کریں اور دوسروں کو اس کا ہمسر ٹھہرائیں “۔ آخر کار جب یہ لوگ عذاب دیکھیں گے تو اپنے دلوں میں پچھتائیں گے اور ہم ان منکرین کے گلوں میں طوق ڈال دیں گے ۔ کیا لوگوں کو اس کے سوا اور کوئی بدلہ دیا جاسکتا ہے کہ جیسے اعمال ان کے تھے ، ویسے ہی جزا وہ پائیں');
INSERT INTO "sayyid_qutb" VALUES(34,34,'34:34','کبھی ایسا نہیں ہوا کہ ہم نے کسی بستی میں ایک خبردار کرنے والا بھیجا ہو اور اس بستی کے کھاتے پیتے لوگوں نے یہ نہ کہا ہو کہ ” جو پیغام تم لے کر آئے ہو ، اس کو ہم نہیں مانتے');
INSERT INTO "sayyid_qutb" VALUES(34,35,'34:35','انہوں نے ہمیشہ یہی کہا کہ ” ہم تم سے زیادہ مال اولاد رکھتے ہیں اور ہم ہرگز سزا پانے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(34,36,'34:36','اے نبی صلى الله عليه وسلم ان سے کہو ، ” میرا رب چاہتا ہے ، کشادہ رزق دیتا ہے اور جسے چاہتا ہے ، نپا تلا عطا کرتا ہے مگر اکثر لوگ اس کی حقیقت نہیں جانتے');
INSERT INTO "sayyid_qutb" VALUES(34,37,'34:37','یہ تمہاری دولت اور تمہاری اولاد نہیں ہے جو تمہیں ہم سے قریب کرتی ہو۔ ہاں مگر جو ایمان لائے اور نیک عمل کرے۔ یہی لوگ ہیں جن کے لئے ان کے عمل دہری جزا ہے۔ اور وہ بلند وبالا عمارتوں میں اطمینان سے رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(34,38,'34:38','رہے وہ لوگ جو ہماری آیات کو نیچا دکھانے کے لیے دوڑ دھوپ کرتے ہیں ، تو وہ عذاب میں مبتلا ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(34,39,'34:39','اے نبی صلى الله عليه وسلم ان سے کہو ” میرا رب اپنے بندوں میں سے جسے چاہتا ہے ، کھلا رزق دیتا ہے اور جسے چاہتا ہے ، نپا تلا دیتا ہے۔ جو کچھ تم خرچ کردیتے ہو ، اس کی جگہ وہی تم کو اور دیتا ہے ، وہ سب رازقوں سے بہتر رزاق ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(34,40,'34:40','اور جس دن وہ تمام انسانوں کو جمع کرے گا پھر فرشتوں سے پوچھے گا ” کیا یہ لوگ تمہاری ہی عبادت کیا کرتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(34,41,'34:41','تو وہ جواب دیں گے کہ ” پاک ہے آپ کی ذات ، ہمارا تعلق تو آپ سے ہے نہ کہ ان لوگوں سے۔ دراصل یہ ہماری نہیں بلکہ جنوں کی عبادت کرتے تھے ، ان میں سے اکثر انہی پر ایمان لائے تھے');
INSERT INTO "sayyid_qutb" VALUES(34,42,'34:42','(اس وقت ہم کہیں گے کہ) آج تم میں سے کوئی نہ کسی کو فائدہ پہنچا سکتا ہے نہ نقصان ، اور ظالموں سے ہم کہہ دیں گے کہ اب چکھو اس عذاب جہنم کا مزہ جسے تم جھٹلایا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(34,43,'34:43','ان لوگوں کو جب ہماری صاف صاف آیات سنائی جاتی ہیں تو یہ کہتے ہیں کہ یہ شخص تو بس یہ چاہتا ہے کہ تم کو ان معبودوں سے برگشتہ کر دے جن کی عبادت تمہارے باپ دادا کرتے آئے ہیں “۔ اور کہتے ہیں کہ ” یہ (قرآن) محض ایک جھوٹ ہے گھڑا ہوا “۔ ان کافروں کے سامنے جب حق آیا تو انہوں نے کہہ دیا کہ ” یہ تو صریح جادو ہے “');
INSERT INTO "sayyid_qutb" VALUES(34,44,'34:44','حالانکہ نہ ہم نے ان لوگوں کو پہلے کوئی کتاب دی تھی کہ یہ اسے پڑھتے ہوں اور نہ تم سے پہلے ان کی طرف کوئی متنبہ کرنے والا بھیجا تھا');
INSERT INTO "sayyid_qutb" VALUES(34,45,'34:45','ان سے پہلے گزرے ہوئے لوگ جھٹلا چکے ہیں۔ جو کچھ ہم نے انہیں دیا تھا اس کے عشر عشیر کو بھی یہ نہیں پہنچے ہیں ۔ مگر جب انہوں نے میرے رسولوں کو جھٹلایا تو دیکھ لو کہ میری سزا کیسی سخت تھی');
INSERT INTO "sayyid_qutb" VALUES(34,46,'34:46','اے نبی صلى الله عليه وسلم ان سے کہو کہ ” میں تمہیں بس ایک بات کی نصیحت کرتا ہوں۔ خدا کے لئے تم اکیلے اکیلے اور دو دو مل کر اپنا دماغ لڑاؤ اور سوچو ، تمہارے صاحب میں آخر کون سی بات ہے جو جنون کی ہو ؟ وہ تو ایک سخت عذاب کی آمد سے پہلے تم کو متنبہ کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(34,47,'34:47','ان سے کہو ، ” اگر میں نے تم سے کوئی اجر مانگا ہے تو وہ تم ہی کو مبارک رہے۔ میرا اجر تو اللہ کے ذمہ ہے اور وہ ہر چیز پر گواہ ہے');
INSERT INTO "sayyid_qutb" VALUES(34,48,'34:48','ان سے کہو ، میرا رب (مجھ پر) حق کا القار کرتا ہے اور وہ تمام پوشیدہ حقیقتوں کا جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(34,49,'34:49','کہو حق آگیا اور اب باطل کے لیے کچھ نہیں ہو سکتا');
INSERT INTO "sayyid_qutb" VALUES(34,50,'34:50','کہو اگر میں گمراہ ہوگیا ہوں تو میسری گمراہی کا وبال مجھ پر ہے ، اور اگر میں ہدایت پر ہوں تو اس وحی کی بناء پر ہوں جو میسرارب میرے اوپر نازل کرتا ہے ، وہ سب کچھ سنتا ہے اور قریب ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(34,51,'34:51','کاش تم دیکھو انہیں اس وقت جب یہ لوگ گھبرائے پھر رہے ہوں گے اور کہیں بچ کر نہ جاسکیں گے ، بلکہ قریب ہی سے پکڑ لیے جائیں گے ۔');
INSERT INTO "sayyid_qutb" VALUES(34,52,'34:52','اس وقت یہ کہیں گے کہ ہم اس پر ایمان لے آئے ۔ حالانکہ ان دور نکلی ہوئی چیز کہاں ہاتھ آسکتی ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(34,53,'34:53','اس سے پہلے یہ کفر کرچکے تھے اور بلا تحقیق دوردور کی کوڑیاں لایا کرتے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(34,54,'34:54','اس وقت جس چیز کی یہ تمنا کر رہے ہوں گے اس سے محروم کردیئے جائیں گے جس طرح ان کے پیش روہم مشرب محروم ہوچکے ہوں گے ۔ یہ بڑے گمراہ کن شک میں پڑے ہوئے تھے');
INSERT INTO "sayyid_qutb" VALUES(35,1,'35:1','” اللہ کے نام سے جو بےانتہا مہر بان اور رحم فرمانے والا ہے ۔” تعریف اللہ ہی کے لیے ہے جو آسمان اور زمین کا بنانے والا اور فرشتوں کو پیغام رساں مقرر کرنے والا ہے جن کے دودو ، تین تین اور چارچار بازو ہیں ۔ وہ اپنی مخلوق کی ساخت میں جیسا چاہتا ہے اضا فہ کرتا ہے ، یقیناً وہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(35,2,'35:2','اللہ جس رحمت کا دروازہ بھی لوگوں کے لیے کھول دے اسے کوئی روکنے والا نہیں ہے اور جسے وہ بند کر دے اسے اللہ کے بعد پھر کوئی دوسرا کھولنے والا نہیں۔ وہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(35,3,'35:3','” لوگو ، تم پر اللہ کے جو احسانات ہیں انہیں یاد رکھو۔ کیا اللہ کے سوا کوئی اور خالق بھی ہے جو تمہیں آسمان اور زمین سے رزق دیتا ہو ؟ ۔۔۔ کوئی معبود اس کے سوا نہیں ، آخر تم کہاں سے دھوکا کھا رہے ہو ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(35,4,'35:4','” اب اگر (اے نبی ؐ) یہ لوگ تمہیں جھٹلاتے ہیں ( تو یہ کوئی نئی بات نہیں ) ، تم سے پہلے بھی بہت سے رسول ؐجھٹلائے جاچکے ہیں اور سارے معاملات آخر کار اللہ ہی کی طرف رجوع ہونے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(35,5,'35:5','لو گو ، اللہ کا وعدہ یقینابر حق ہے ، لٰہذا دنیا کی ذندگی تمہیں دھو کے میں نہ ڈالے ، اور نہ وہ بڑا دھو کے باز تمہیں اللہ کے بارے میں دھوکہ دینے پا ئے');
INSERT INTO "sayyid_qutb" VALUES(35,6,'35:6','اور حقیقت شیطان تمہارا دشمن ہے اس لیے تم بھی اسے اپنا دشمن ہی سمجھو ۔ وہ تو اپنے پیرو وں کو اپنی راہ پر اس لیے بلا رہا ہے کہ وہ دوزخیوں میں شامل ہو جائیں');
INSERT INTO "sayyid_qutb" VALUES(35,7,'35:7','جو لوگ کفر کریں گے ان کے لیے دسخت عذاب ہے اور جو ایمان لائیں گے اور نیک عمل کریں گے ان کے لیے مغفرت اور بڑا اجر ہے');
INSERT INTO "sayyid_qutb" VALUES(35,8,'35:8',') بھلاکچھ ٹھکانا ہے اس شحص کی گمراہی کا) جس کے لیے اس کا براعمل خوشنما بنادیا گیا ہو اور وہ اسے اچھا سمجھ رہا ہو ؟ حقیقت یہ ہے کہ اللہ جسے چاہتا ہے ، گمراہی میں ڈال دیتا ہے اور جسے چاہتا ہے ، راہ راست دکھا دیتا ہے ۔ پس (اے نبی صلى الله عليه وسلم خواہ مخواہ تمہارے جان ان لوگوں کی خاطر غم وافسوس میں نہ کھلے ۔ جو کچھ یہ کر رہے ہیں ، اللہ اس کو خوب جانتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,9,'35:9','وہ اللہ ہی تو ہے جو ہواؤں کو بھیجتا ہے ، پھر وہ بادل اٹھاتی ہیں ، پھر ہم اسے ایک اجاڑ علاقے کی طرف کے جاتے ہیں اور اس کے ذریعہ سے اسی زمین کو جلا اٹھاتے ہیں جو مری پڑی تھی۔ مرے ہوئے انسانوں کا جی اٹھنا بھی اسی طرح ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(35,10,'35:10','جو کوئی عزت چاہتا ہو اسے معلوم ہونا چاہئے کہ عزت ساری کی ساری اللہ کی ہے۔ اس کے ہاں جو چیز اوپر چڑھتی ہے وہ صرف پاکیزہ قول ہے ، اور عمل صالح اس کو اوپر چڑھاتا ہے ۔ رہے وہ لوگ جو بےہودہ چال بازیاں کرتے ہیں ، ان کے لیے سخت عذاب ہے اور ان کا مکر خود ہی غارت ہونے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(35,11,'35:11','اللہ نے تم کو مٹی سے پیدا کیا ، پھر نطفہ سے ، پھر تمہارے جوڑے بنا دئیے (یعنی مرد اور عورت) کوئی عورت حاملہ نہیں ہوتی اور نہ بچہ جنتی ہے مگر یہ سب کچھ اللہ کے علم میں ہوتا ہے۔ کوئی عمر پانے والا عمر نہیں پاتا۔ اور نہ کسی کی عمر میں کچھ کمی ہوتی ہے۔ مگر یہ سب کچھ ایک کتاب میں لکھا ہوتا ہے ۔ اللہ کے لیے یہ بہت آسان کام ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,12,'35:12','” اور پانی کے دونوں ذخیرے یکساں نہیں ہیں ۔ ایک میٹھا اور پیاس بجھانے والا ہے ، پینے میں خوشگوار اور دوسرا سخت کھاری کہ حلق چھیل دے۔ مگر دونوں سے تم تروتازہ گوشت حاصل کرتے ہو ، پہننے کے لیے زینت کا سامان نکالتے ہو ، اور اسی پانی میں تم دیکھتے ہو کہ کشتیاں اس کا سینہ چیرتی چلی جا رہی ہیں تاکہ تم اللہ کا فضل تلاش کرو اور اس کے شکر گزار بنو “۔');
INSERT INTO "sayyid_qutb" VALUES(35,13,'35:13','وہ دن کے اندر رات اور رات کے اندر دن کو پروتا ہوا لے آتا ہے۔ چاند اور سورج کو اس نے مسخر کر رکھا ہے۔ یہ سب کچھ ایک وقت مقرر تک چلے جا رہا ہے “۔ وہی اللہ (جس کے یہ سارے کام ہیں) تمہارا رب ہے۔ بادشاہی اسی کی ہے۔ اسے چھوڑ کر جن دوسروں کو تم پکارتے ہو ، وہ ایک پرکاہ کے مالک بھی نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(35,14,'35:14','” انہیں پکارو ، تو وہ تمہاری دعائیں سن نہیں سکتے۔ اور سن لیں تو ان کا تمہیں کوئی جواب نہیں دے سکتے۔ اور قیامت کے روز وہ تمہارے شرک کا انکار کردیں گے۔ حقیقت حال کی ایسی صحیح خبر تمہیں ایک خبردار کے سوا کوئی نہیں دے سکتا “۔');
INSERT INTO "sayyid_qutb" VALUES(35,15,'35:15','لوگو ، تم ہی اللہ کے محتاج ہو اور اللہ تو غنی وحمید ہے۔');
INSERT INTO "sayyid_qutb" VALUES(35,16,'35:16','وہ چاہے تو تمہیں ہٹا کر کوئی نئی خلقت تمہاری جگہ لے آئے');
INSERT INTO "sayyid_qutb" VALUES(35,17,'35:17','اور ایسا کرنا اللہ کے لیے کچھ بھی دشوار نہیں');
INSERT INTO "sayyid_qutb" VALUES(35,18,'35:18','” کوئی بوجھ اٹھانے والا کسی دوسرے کا بوجھ نہ اٹھائے گا ، اور اگر کوئی لدا ہوا نفس اپنا بوجھ اٹھانے کے لیے پکارے گا تو اس کے بار کو ایک ادنیٰ حصہ بھی بنانے کے لئے کوئی نہ آئے گا چاہے وہ قریب ترین رشتہ دار ہی کیوں نہ ہو۔ (اے نبی صلى الله عليه وسلم تم صرف انہی لوگوں کو متنبہ کرسکتے ہو جو بےدیکھے اپنے رب سے ڈرتے ہیں اور نماز قائم کرتے ہیں ۔ جو شخص بھی پاکیزگی اختیار کرتا ہے اپنی ہی بھلائی کے لیے کرتا ہے اور پلٹنا سب کو اللہ ہی کی طرف ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,19,'35:19','” اندھا اور آنکھوں والا برابر نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(35,20,'35:20','نہ تاریکیاں اور روشنی یکساں ہیں');
INSERT INTO "sayyid_qutb" VALUES(35,21,'35:21','نہ ٹھنڈی چھاؤں اور دھوپ کی تپش ایک جیسی ہے');
INSERT INTO "sayyid_qutb" VALUES(35,22,'35:22','اور نہ زندے اور مردے مساوی ہیں “۔ اللہ جسے چاہتا ہے ، سنواتا ہے ، مگر (اے نبی صلى الله عليه وسلم تم ان لوگوں کو نہیں سنا سکتے جو قبروں میں مدفون ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(35,23,'35:23','تم تو بس ایک خبردار کرنے والے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(35,24,'35:24','ہم نے تم کو حق کے ساتھ بھیجا ہے۔ بشارت دینے والا اور ڈرانے والا بنا کر۔ اور کوئی امت ایسی نہیں گزری ہے جس میں کوئی متنبہ کرنے والا نہ آیا ہو');
INSERT INTO "sayyid_qutb" VALUES(35,25,'35:25','۔ اب اگر یہ لوگ تمہیں جھٹلاتے ہیں تو ان سے پہلے گزرے ہوئے لوگ بھی جھٹلا چکے ہیں۔ ان کے پاس ان کے رسول کھلے دلائل اور صحیفے اور روشن ہدایات دینے والی کتاب لے کر آئے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(35,26,'35:26','پھر جن لوگوں نے نہ مانا ان کو میں نے پکڑ لیا اور دیکھ لو کہ میری سزا کیسی سخت تھی “۔');
INSERT INTO "sayyid_qutb" VALUES(35,27,'35:27','کیا تم دیکھتے نہیں ہو کہ اللہ آسمان سے پانی برساتا ہے اور پھر اس کے ذریعہ سے ہم طرح طرح کے پھل نکال لاتے ہیں جن کے رنگ مختلف ہوتے ہیں۔ پہاڑوں میں بھی ، سفید ، سرخ اور گہری سیاہ دھاریاں پائی جاتی ہیں جن کے رنگ مختلف ہوتے ہیں پہاڑوں میں بھی ، سفید ، سرخ اور گہری سیاہ دھاریاں پائی جاتی ہیں جن کے رنگ مختلف ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(35,28,'35:28','اور اسی طرح انسانوں اور جانوروں اور مویشیوں کے رنگ بھی مختلف ہیں۔ حقیقت یہ ہے کہ اللہ کے بندوں میں سے صرف علم رکھنے والے لوگ ہی اس سے ڈرتے ہیں۔ بیشک اللہ زبردست اور درگزر فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(35,29,'35:29','جو لوگ کتاب اللہ کی تلاوت کرتے ہیں اور نماز قائم کرتے ہیں ، اور جو کچھ ہم نے انہیں رزق دیا ہے اس میں سے کھلے اور چھپے خرچ کرتے ہیں ، یقیناً وہی ایک ایسی تجارت کے متوقع ہیں جس میں ہرگز خسارہ نہ ہوگا۔ (اس تجارت میں انہوں نے اپنا سب کچھ اس لیے کھپایا ہے)');
INSERT INTO "sayyid_qutb" VALUES(35,30,'35:30','تاکہ اللہ ان کے اجر پورے کے پورے ان کو دے اور مزید اپنے فضل سے ان کو عطا فرمائے۔ بیشک اللہ بخشنے والا اور قدر دان ہے “');
INSERT INTO "sayyid_qutb" VALUES(35,31,'35:31','”(اے نبی صلى الله عليه وسلم جو کتاب ہم نے تمہاری طرف وحی کے ذریعہ سے بھیجی ہے وہی حق ہے ، تصدیق کرتی ہوئی آئی ہے ان کتابوں کی جو اس سے پہلے آئی تھی۔ بیشک اللہ اپنے بندوں کے حال سے باخبر ہے اور ہر چیز پر نگاہ رکھنے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,32,'35:32','پھر ہم نے اس کتاب کا وارث بنا دیا ان لوگوں کو جنہیں ہم نے (اس وراثت کے لیے) اپنے بندوں میں سے چن لیا ” اب کوئی تو ان میں سے اپنے نفس پر ظلم کرنے والا ہے ، اور کوئی بیچ کی راس ہے ، اور کوئی اللہ کے اذن سے نیکیوں میں سبقت کرنے والا ہے یہی بہت بڑا فضل ہے');
INSERT INTO "sayyid_qutb" VALUES(35,33,'35:33','” ہمیشہ رہنے والی جنتیں ہیں جن میں یہ لوگ داخل ہوں گے ، وہاں انہیں سونے کے کنگنوں اور موتیوں سے آراستہ کیا جائے گا ، وہاں ان کا لباس ریشم ہوگا');
INSERT INTO "sayyid_qutb" VALUES(35,34,'35:34','اور وہ کہیں گے کہ شکر ہے اس خدا کا جس نے ہم سے غم دور کردیا ، یقینا ہمارا رب معاف کرنے والا اور قدر فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(35,35,'35:35','جس نے ہمیں اپنے فضل سے ابدی قیام کی جگہ ٹھہرا دیا ، اب یہاں نہ ہمیں کوئی مشقت پیش آتی ہے اور نہ تکان لاحق ہوتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,36,'35:36','اور جن لوگوں نے کفر کیا ہے ان کے لیے جہنم کی آگ ہے۔ نہ ان کا قصہ پاک کردیا جائے گا کہ مرجائیں اور نہ ان کیلئے جہنم کے عذاب میں کوئی کمی کی جائے گی اس طرح ہم بدلہ دیتے ہیں ہر اس شخص کو جو کفر کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(35,37,'35:37','وہ وہاں چیخ چیخ کر کہیں گے کہ اے ہمارے رب ، ہمیں یہاں سے نکال لے تاکہ ہم نیک عمل کریں ان اعمال سے مختلف جو پہلے کرتے رہے تھے۔ (انہیں جواب دیا جائے گا) ، کیا ہم نے تم کو اتنی عمر نہ دی تھی جس میں کوئی سبق لینا چاہتا تو سبق لے سکتا تھا ؟ اور تمہارے پاس متنبہ کرنے والا بھی آچکا تھا۔ اب مزا چکھو۔ ظالموں کا یہاں کوئی مددگار نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(35,38,'35:38','بیشک ، اللہ آسمانوں اور زمین کی ہر پوشیدہ چیز سے واقف ہے۔ وہ تو سینوں کے چھپے ہوئے راز تک جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(35,39,'35:39','” وہی تو ہے جس نے تم کو زمین میں خلیفہ بنایا ہے۔ اب جو کوئی کفر کرتا ہے اس کے کفر کا وبال اسی پر ہے ، اور کافروں کو ان کا کفر اس کے سوا کوئی ترقی نہیں دیتا کہ ان کے رب کا غضب ان پر زیادہ سے زیادہ بھڑکتا چلا جاتا ہے۔ کافروں کے لیے خسارے میں اضافے کے سوا کوئی ترقی نہیں');
INSERT INTO "sayyid_qutb" VALUES(35,40,'35:40','”(اے نبی صلى الله عليه وسلم ان سے کہو ، ” کبھی تم نے دیکھا بھی ہے اپنے ان شریکوں کو جنہیں تم خدا کو چھوڑ کر پکارا کرتے ہو ؟ مجھے بتاؤ انہوں نے زمین میں کیا پیدا کیا ہے یا آسمانوں میں ان کی کیا شرکت ہے (اگر یہ نہیں بتا سکتے تو ان سے پوچھو) کیا ہم نے انہیں کوئی تحریر لکھ کردی ہے جس کی بنا پر یہ (اپنے اس شرک کیلئے) کوئی صاف دلیل رکھتے ہوں ؟ نہیں ، بلکہ یہ ظالم ایک دوسرے کو محض فریب کے جھانسے دیئے جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(35,41,'35:41','حقیقت یہ ہے کہ اللہ ہی ہے جو آسمانوں اور زمین کو ٹل جانے سے روکے ہوئے ہے ، اور اگر وہ ٹل جائیں تو اللہ کے بعد کوئی دوسرا انہیں تھامنے والا نہیں ہے۔ بیشک اللہ بڑا علیم اور درگزر فرمانے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(35,42,'35:42','یہ لوگ کڑی کڑی قسمیں کھا کر کہا کرتے تھے کہ اگر کوئی خبردار کرنے والا ان کے ہاں آگیا ہوتا تو یہ دنیا کی ہر دوسری قوم سے بڑھ کر راست رو ہوتے۔ مگر جب خبردار کرنے والا ان کے ہاں آگیا تو اس کی آمد نے ان کے اندر حق سے فرار کے سوا کسی چیز میں اضافہ نہ کیا۔');
INSERT INTO "sayyid_qutb" VALUES(35,43,'35:43','اب کیا یہ لوگ اس کا انتظار کر رہے ہیں کہ پچھلی قوموں کے ساتھ اللہ کا جو طریقہ رہا ہے وہی ان کے ساتھ بھی برتا جائے ؟ یہی بات تو تم اللہ کے طریقے میں ہرگز کوئی تبدیلی نہ پاؤ گے اور تم کبھی نہ دیکھو گے کہ اللہ کی سنت کو اس کے مقرر راستے سے کوئی طاقت پھیر سکتی ہے');
INSERT INTO "sayyid_qutb" VALUES(35,44,'35:44','یہ لوگ زمین میں کبھی چلے پھرے نہیں ہیں کہ انہیں ان لوگوں کا انجام نظر آتا جو ان سے پہلے گزر چکے ہیں اور ان سے بہت زیادہ طاقت ور تھے ؟ اللہ کو کوئی چیز عاجز کرنے والی نہیں ہے ، نہ آسمانوں میں اور نہ زمین میں۔ وہ سب کچھ جانتا ہے اور ہر چیز پر قدرت رکھتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(35,45,'35:45','” اگر کہیں وہ لوگوں کو ان کے لیے کرتوتوں پر پکڑتا تو زمین ہر کسی منٹسی کو جیتا نہ چھوڑتا۔ مگر وہ انہیں ایک مقرر وقت تک کے لیے مہلت دے رہا ہے۔ پھر جب ان کا وقت آن پورا ہوگا تو اللہ اپنے بندوں کو دیکھ لے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(36,1,'36:1','۔ یس');
INSERT INTO "sayyid_qutb" VALUES(36,2,'36:2','۔ قسم ہے قرآن حکیم کی');
INSERT INTO "sayyid_qutb" VALUES(36,3,'36:3','کہ تم یقیناً رسولوں میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(36,4,'36:4','سیدھے راستے پر');
INSERT INTO "sayyid_qutb" VALUES(36,5,'36:5','یہ قرآن) غالب اور رحیم ہستی کا نازل کردہ ہے');
INSERT INTO "sayyid_qutb" VALUES(36,6,'36:6','تاکہ تم خبردار کرو ایک ایسی قوم کو جس کے باپ دادا خبردار نہ کیے گئے تھے اور اس وجہ سے وہ غفلت میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,7,'36:7','۔ ان میں سے اکثر لوگ فیصلہ عذاب کے مستحق ہوچکے ہیں ، اسی لئے وہ ایمان نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(36,8,'36:8','۔ ہم نے ان کی گردنوں میں طوق ڈال دئیے ہیں جن سے وہ ٹھوڑیوں تک جکڑے گئے ہیں ، اس لیے وہ سر اٹھائے کھڑے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,9,'36:9','۔ ہم نے ایک دیوار ان کے آگے کھڑی کردی ہے اور ایک دیوار ان کے پیچھے۔ ہم نے انہیں ڈھانک دیا ہے ، انہیں سب کچھ نہیں سوجھتا۔');
INSERT INTO "sayyid_qutb" VALUES(36,10,'36:10','ان کے لیے یکساں ہے ، تم انہیں خبردار کردیا نہ کرو ، یہ نہ مانیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(36,11,'36:11','تم تو اسی شخص کو خبردار کرسکتے ہو جو نصیحت کی پیروی کرے اور بےدیکھے خدائے رحمان سے ڈرے۔ اسے مغفرت اور اجر کریم کی بشارت دے دو');
INSERT INTO "sayyid_qutb" VALUES(36,12,'36:12','۔ ہم یقیناً ایک روز مردوں کو زندہ کرنے والے ہیں ۔ جو کچھ افعال انہوں نے کیے ہیں وہ سب ہم لکھتے جا رہے ہیں۔ اور جو کچھ آثار انہوں نے پیچھے چھوڑے ہیں ، وہ بھی ہم ثبت کر رہے ہیں۔ ہر چیز کو ہم نے ایک کھلی کتاب میں درج کر رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(36,13,'36:13','انہیں مثال کے طور پر اس بستی والوں کا قصہ سناؤ جب کہ اس میں رسول آئے تھے۔ “۔');
INSERT INTO "sayyid_qutb" VALUES(36,14,'36:14','ہم نے ان کی طرف دو رسول بھیجے اور انہوں نے دونوں کو جھٹلا دیا۔ پھر ہم نے تیسرا مدد کے لیے بھیجا اور ان سب نے کہا ” ہم تمہاری طرف رسول کی حیثیت سے بھیجے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,15,'36:15','بستی والوں نے کہا ” تم کچھ نہیں ہو مگر ہم جیسے چند انسان اور خدائے رحمن نے ہرگز کوئی چیز نازل نہیں کی ہے ، تم محض جھوٹ بولتے ہو');
INSERT INTO "sayyid_qutb" VALUES(36,16,'36:16','رسولوں نے کہا ، ہمارا رب جانتا ہے کہ ہم ضرور تمہاری طرف رسول بنا کر بھیجے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,17,'36:17','اور ہم پر صاف صاف پیغام دینے کے سوا کوئی ذمہ داری نہیں ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,18,'36:18',') بستی والے کہنے لگے ہم تو تمہیں اپنے لیے فال بد سمجھتے ہیں۔ اگر تم باز نہ آئے تو ہم تم کو سنگسار کردیں گے اور ہم سے تم بڑی دردناک سزا پاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(36,19,'36:19','رسولوں نے کہا تمہاری فال بد تمہارے اپنے ساتھ لگی ہوئی ہے “ کیا یہ باتیں تم اس لیے کرتے ہو کہ تمہیں نصیحت کی گئی ؟ “ ” اصل بات یہ ہے کہ تم حد سے گزرے ہوئے لوگ ہو');
INSERT INTO "sayyid_qutb" VALUES(36,20,'36:20','اتنے میں شہر کے دور دراز گوشے سے ایک شخص دوڑتا ہوا آیا اور بولا ” اے میری قوم کے لوگو ، رسولوں کی پیروی اختیار کرلو۔');
INSERT INTO "sayyid_qutb" VALUES(36,21,'36:21','پیروی کرو ان لوگوں کی جو تم سے کوئی اجر نہیں نہیں چاہتے اور ٹھیک راستے پر ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,22,'36:22','آخر کیوں نہ میں اس ہستی کی بندگی کروں جس نے مجھے پیدا کیا ہے اور جس کی طرف تم سب کو پلٹ کر جانا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,23,'36:23','کیا میں اسے چھوڑ کر دوسرے معبود بنالوں ؟ حالانکہ اگر خدائے رحمن مجھے کوئی نقصان پہنچانا چاہے تو نہ ان کی شفاعت میرے کسی کام آسکتی ہے اور نہ وہ مجھے چھڑا ہی سکتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(36,24,'36:24','اگر میں ایسا کروں تو میں صریح گناہوں میں مبتلا ہوجاؤں گا۔');
INSERT INTO "sayyid_qutb" VALUES(36,25,'36:25','میں تو تمہارے رب پر ایمان لے آیا ، تم بھی میری بات مان لو');
INSERT INTO "sayyid_qutb" VALUES(36,26,'36:26','آخر کار ان لوگوں نے اسے قتل کردیا اور) اس شخص سے کہہ دیا گیا کہ ” داخل ہوجا جنت میں “۔ اس نے کہا ” کاش میری قوم کو یہ معلوم ہوتا');
INSERT INTO "sayyid_qutb" VALUES(36,27,'36:27','کہ میرے رب نے کس چیز کی بدولت میری مغفرت رما دی اور مجھے باعزت لوگوں میں داخل فرمایا');
INSERT INTO "sayyid_qutb" VALUES(36,28,'36:28','” اس کے بعد اس کی قوم پر ہم نے آسمان سے کوئی لشکر نہیں اتارا۔ ہمیں لشکر بھیجنے کی کوئی حاجت نہ تھی');
INSERT INTO "sayyid_qutb" VALUES(36,29,'36:29','بس ایک دھماکہ ہوا اور یکایک وہ سب بجھ کر رہ گئے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,30,'36:30','” افسوس بندوں کے حال پر ، جو رسول بھی ان کے پاس آیا اس کا وہ مذاق ہی اڑاتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(36,31,'36:31','کیا انہوں نے دیکھا نہیں کہ ان سے پہلے کتنی ہی قوموں کو ہم ہلاک کرچکے ہیں اور اس کے بعد وہ پھر کبھی ان کی طرف پلٹ کر نہ آئے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,32,'36:32','ان سب کو ایک روز ہمارے سامنے حاضر کیا جانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,33,'36:33',') ” ان لوگوں کے لیے بےجان زمین ایک نشانی ہے۔ ہم نے اس کو زندگی بخشی اور اس سے غلہ نکالا جسے یہ کھاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(36,34,'36:34','ہم نے اس میں کھجوروں اور انگوروں کے باغ پیدا کیے اور اس کے اندر سے چشمے پھوڑ نکالے');
INSERT INTO "sayyid_qutb" VALUES(36,35,'36:35','تاکہ یہ اس کے پھل کھائیں۔ یہ سب کچھ ان کے اپنے ہاتھوں کا پیدا کیا ہوا نہیں ہے۔ پھر کیا یہ شکر ادا نہیں کرتے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,36,'36:36','پاک ہے وہ ذات جس نے جملہ اقسام کے جوڑے پیدا کیے خواہ وہ زمین کی نباتات سے ہوں یا خود ان کی اپنی جنس میں سے یا ان اشیاء میں سے جن کو یہ جانتے تک نہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(36,37,'36:37','ان کے لیے ایک اور نشانی رات ہے ، ہم اس کے اوپر سے دن ہٹا دیتے ہیں تو ان پر اندھیرا چھا جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(36,38,'36:38','اور سورج ، وہ اپنے ٹھکانے کی طرف چلا جاتا ہے ۔ یہ زبردست علیم ہستی کا باندھا ہوا حساب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(36,39,'36:39','اور چاند ، اس کے لیے ہم نے منزلیں مقرر کر دی ہیں یہاں تک کہ ان سے گزرتا ہوا وہ پھر کھجور کی سوکھی شاخ کے مانند رہ جاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(36,40,'36:40','نہ سورج کے بس میں یہ ہے کہ وہ چاند کو جا پکڑے اور نہ رات دن پر سبقت لے جاسکتی ہے ۔ سب ایک ایک فلک میں تیر رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,41,'36:41','” ان کے لیے یہ بھی ایک نشانی ہے کہ ہم نے ان کی نسل کو بھری ہوئی کشتی میں سوار کردیا ،');
INSERT INTO "sayyid_qutb" VALUES(36,42,'36:42','اور پھر ان کے لیے ویسی ہی کشتیاں اور پیدا کیں ، جن پر یہ سوار ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,43,'36:43','ہم چاہیں تو ان کو غرق کردیں کوئی ان کی فریاد سننے والانہ ہو ، اور کسی طرح یہ نہ بچائے جاسکیں');
INSERT INTO "sayyid_qutb" VALUES(36,44,'36:44','۔ بس ہماری رحمت ہی ہے جو انہیں پار لگاتی اور ایک وقت خاص تک زندگی سے متمتع ہونے کا موقعہ دیتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,45,'36:45','ان لوگوں سے جب کہ جاتا ہے کہ بچو اس انجام سے جو تمہارے آگے آرہا ہے اور تمہارے پیچھے گزار چکا ہے ، شاید کہ تم پر رحم کیا جائے ( تو یہ سنی ان سنی کر جاتے ہیں ) ۔');
INSERT INTO "sayyid_qutb" VALUES(36,46,'36:46','ان کے سامنے ان کے رب کی آیات میں سے جو آیت بھی آتی ہے ، یہ اس کی طرف التفات نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(36,47,'36:47','اور جب ان سے کہا جاتا ہے کہ اللہ نے جو رزق تمہیں عطا کیا ہے ، اس میں سے کچھ اللہ کی راہ میں بھی خرچ کرو تو یہ لوگ جنہوں نے کفر کیا ہے ایمان لانے والوں کو جواب دیتے ہیں ” کیا ہم ان کو کھلائیں جنہیں اگر اللہ چاہتا تو خود کھلا دیتا ؟ تم تو بالکل ہی بہک گئے ہو');
INSERT INTO "sayyid_qutb" VALUES(36,48,'36:48','یہ لوگ کہتے ہیں کہ ” یہ قیامت کی دھمکی آخر کب پوری ہوگی “ ؟');
INSERT INTO "sayyid_qutb" VALUES(36,49,'36:49','دراصل یہ جس چیز کی راہ تک رہے ہیں وہ بس ایک دھماکہ ہے جو یکایک انہیں اس حالت میں دھرلے گا جب یہ (اپنے دنیوی معاملات میں) جھگڑ رہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(36,50,'36:50','اور اس وقت یہ وصیت تک نہ کرسکیں گے ، نہ اپنے گھروں کو پلٹ سکیں گے');
INSERT INTO "sayyid_qutb" VALUES(36,51,'36:51','۔ پھر ایک صور پھونکا جائے گا اور یکایک یہ اپنے رب کے حضور پیش ہونے کیلئے اپنی اپنی قبروں سے نکل پڑیں گے');
INSERT INTO "sayyid_qutb" VALUES(36,52,'36:52','۔ گھبرا کر کہیں گے : ” ارے ، یہ کس نے ہمیں ہماری خواب گاہ سے اٹھا کھڑا کیا ؟ “۔۔۔” یہ وہی چیز ہے جس کا خدائے رحمن نے وعدہ کیا تھا اور رسولوں کی بات سچی تھی');
INSERT INTO "sayyid_qutb" VALUES(36,53,'36:53','ایک ہی زور کی آواز ہوگی اور سب کے سب ہمارے سامنے حاضر کر دئیے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(36,54,'36:54','آج کسی پر ذرہ برابر ظلم نہ کیا جائے گا اور تمہیں ویسا ہی بدلہ دیا جائے گا جیسے تم عمل کرتے رہے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,55,'36:55','آج جنتی لوگ مزے کرنے میں مشغول ہیں۔ ،');
INSERT INTO "sayyid_qutb" VALUES(36,56,'36:56','وہ اور ان کی بیویاں گھنے سایوں میں ہیں۔ مسندوں پر تکیے لگائے ہوئے');
INSERT INTO "sayyid_qutb" VALUES(36,57,'36:57','ہر قسم کی لذیذ چیزیں کھانے پینے کو ان کے لیے وہاں موجود ہیں ، جو کچھ وہ طلب کریں ان کیلئے حاضر ہے');
INSERT INTO "sayyid_qutb" VALUES(36,58,'36:58','رب رحیم کی طرف سے ان کو سلام کہا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(36,59,'36:59','اور اے مجرمو ، آج تم چھٹ کر الگ ہو جاؤ');
INSERT INTO "sayyid_qutb" VALUES(36,60,'36:60','۔ آدم کے بچو ، کیا میں نے تم کو ہدایت نہ کی تھی کہ شیطان کی بندگی نہ کرو ، وہ تمہارا کھلا دشمن ہے');
INSERT INTO "sayyid_qutb" VALUES(36,61,'36:61','اور میری ہی بندگی کرو ، یہ سیدھا راستہ ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,62,'36:62','مگر اس کے باوجود اس نے تم میں سے ایک گروہ کثیر کو گمراہ کردیا۔ کیا تم عقل نہیں رکھتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,63,'36:63','” یہ وہی جہنم ہے جس سے تم کو ڈرایا جاتا رہا تھا');
INSERT INTO "sayyid_qutb" VALUES(36,64,'36:64','جو کفر تم دنیا میں کرتے رہے ہو ، اس کی پاداش میں اب اس کا ایندھن بنو');
INSERT INTO "sayyid_qutb" VALUES(36,65,'36:65','آج ہم ان کے منہ بند کیے دیتے ہیں ، ان کے ہاتھ ہم سے بولیں گے اور ان کے پاؤں گواہی دیں گے کہ یہ دنیا میں کیا کمائی کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,66,'36:66','” ہم چاہیں تو ان کی آنکھیں موند دیں ، پھر یہ راستے کی طرف لپک کر دیکھیں ، کہاں سے انہیں راستہ سجھائی دے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(36,67,'36:67','ہم چاہیں تو انہیں ان کی جگہ ہی پر اس طرح مسخ کرکے رکھ دیں کہ یہ نہ آگے چل سکیں نہ پیچھے پلٹ سکیں');
INSERT INTO "sayyid_qutb" VALUES(36,68,'36:68','جس شخص کو ہم لمبی عمر دیتے ہیں اس کی ساخت کو ہم الٹ ہی دیتے ہیں کیا (یہ حالات دیکھ کر) انہیں عقل نہیں آتی ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(36,69,'36:69','ہم نے اس (نبی صلى الله عليه وسلم کو شعر نہیں سکھایا ہے اور نہ شاعری اس کو زیب ہی دیتی ہے۔ یہ تو ایک نصیحت ہے اور صاف پڑھی جانے والے کتاب ،');
INSERT INTO "sayyid_qutb" VALUES(36,70,'36:70','تاکہ وہ ہر اس شخص کو خبردار کر دے جو زندہ ہو اور انکار کرنے والوں پر حجت قائم ہو جائے');
INSERT INTO "sayyid_qutb" VALUES(36,71,'36:71','کیا یہ لوگ دیکھتے نہیں ہیں کہ ہم نے اپنے ہاتھوں کی بنائی ہوئی چیزوں میں سے ان کے لیے مویشی پیدا کیے ہیں اور اب یہ ان کے مالک ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,72,'36:72','۔ ہم نے انہیں اس طرح ان کے بس میں کردیا ہے کہ ان میں سے کسی پر یہ سوار ہوتے ہیں ، کسی کا یہ گوشت کھاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(36,73,'36:73','اور ان کے اندر ان کے لیے طرح طرح کے فوائد اور مشروبات ہیں۔ پھر کیا یہ شکر گزار نہیں ہوتے ؟');
INSERT INTO "sayyid_qutb" VALUES(36,74,'36:74','، یہ سب کچھ ہوتے ہوئے انہوں نے اللہ کے سوا دوسرے خدا بنا لیے ہیں اور یہ امید رکھتے ہیں کہ ان کی مدد کی جائے گی۔');
INSERT INTO "sayyid_qutb" VALUES(36,75,'36:75','وہ ان کی کوئی مدد نہیں کرسکتے ” بلکہ یہ لوگ الٹے ان کے لیے حاضر باش لشکر بنے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(36,76,'36:76','اچھا ، جو باتیں یہ بنا رہے ہیں ، وہ تمہیں رنجیدہ نہ کریں ، ان کی چھپی اور کھلی سب باتوں کو ہم جانتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(36,77,'36:77','کیا انسان دیکھتا نہیں ہے کہ ہم نے اسے نطفہ سے پیدا کیا اور پھر وہ صریح جھگڑالوبن کر کھڑا ہوگیا ؟');
INSERT INTO "sayyid_qutb" VALUES(36,78,'36:78','” اب وہ ہم پر مثالیں چسپاں کرتا ہے اور اپنی پیدائش کو بھول جاتا ہے ۔ کہتا ہے ” کون ان ہڈیوں کو زندہ کرے گا جب کہ یہ بو سیدہ ہوچکی ہوں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(36,79,'36:79','اس سے کہو ، انہیں وہی زندہ کرے گا جس نے پہلے انہیں پیدا کیا تھا ، اور وہ تخلیق کا ہر کام جانتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(36,80,'36:80','وہی جس نے تمہارے لیے ہرے بھرے درخت سے آگ پیدا کردی اور تم اس سے اپنے چولہے روشن کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(36,81,'36:81','کیا وہ جس نے آسمانوں اور زمین کو پیدا کیا اس پر قادر نہیں ہے کہ ان جیسوں کو پیدا کرسکے ؟ کیوں نہیں ، جب کہ وہ ماہر خلاق ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(36,82,'36:82','وہ تو جب کسی چیز کا ارادہ کرتا ہے تو اس کا کام بس یہ ہے کہ اسے حکم دے کہ ہوجا اور وہ ہوجاتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(36,83,'36:83','پاک ہے وہ جس کے ہاتھ میں ہر چیز کا مکمل اقتدار ہے ، اور اسی کی طرف تم پلٹائے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,1,'37:1','قطار در قطار باندھنے والوں کی قسم');
INSERT INTO "sayyid_qutb" VALUES(37,2,'37:2','، پھر ان کی قسم جو ڈانٹنے پھٹکارنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,3,'37:3','پھر ان کی قسم جو کلام نصیحت سنانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,4,'37:4','، تمہارا معبود حقیقی بس ایک ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(37,5,'37:5','وہ جو زمین اور آسمانوں کا اور تمام ان چیزوں کا مالک ہے جو زمین و آسمان میں ہیں ، اور سارے مشرقوں کا مالک');
INSERT INTO "sayyid_qutb" VALUES(37,6,'37:6','ہم نے آسمان دنیا کو تاروں کی زینت سے آراستہ کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,7,'37:7',') ” اور ہر شیطان سرکش سے اس کو محفوظ کردیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(37,8,'37:8','10 یہ شیاطین ملاء اعلیٰ کی باتیں نہیں سن سکتے۔ ہر طرف سے مارے اور ہانکے جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,9,'37:9','اور ان کے لیے پیہم عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(37,10,'37:10','تاہم اگر کوئی ان میں سے کچھ لے اڑے تو ایک تیز شعلہ اس کا پیچھا کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,11,'37:11','اب ان سے پوچھو ، ان کی پیدائش زیادہ مشکل ہے یا ان چیزوں کی جو ہم نے پیدا کر رکھی ہیں ؟” ان کو تو ہم نے لیس دار گارے سے پیدا کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,12,'37:12','۔ تم (اللہ کی قدرت کے کرشموں پر) حیران ہو اور یہ اس کا مذاق اڑا رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(37,13,'37:13','سمجھانا جاتا ہے کہ تو سمجھ کر نہیں دیتے');
INSERT INTO "sayyid_qutb" VALUES(37,14,'37:14','کوئی نشانی دیکھتے ہیں تو اس ٹھٹھوں میں اڑاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,15,'37:15','اور کہتے ہیں ” یہ تو صریح جادو ہے ،');
INSERT INTO "sayyid_qutb" VALUES(37,16,'37:16','۔ بھلا کہیں ایسا ہو سکتا ہے کہ جب ہم مرچکے ہوں اور مٹی بن جائیں اور ہڈیوں کا پنجر رہ جائیں اس وقت ہم پھر زندہ کرکے اٹھا کھڑے کیے جائیں ؟');
INSERT INTO "sayyid_qutb" VALUES(37,17,'37:17','اور کیا ہمارے اگلے وقتوں کے آباو اجداد بھی اٹھائے جائیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(37,18,'37:18','ان سے کہو ہاں ، اور تم (خدا کے مقابلے میں ) بےبس ہو');
INSERT INTO "sayyid_qutb" VALUES(37,19,'37:19','” بس ایک ہی جھڑکی ہوگی اور یکایک یہ اپنی آنکھوں سے (وہ سب کچھ جس کی خبر دی جارہی ہے ) دیکھ رہے ہوں۔ گے “۔');
INSERT INTO "sayyid_qutb" VALUES(37,20,'37:20','اس وقت یہ کہیں گے ہائے ہماری کم بختی ، یہ تو یوم الجزا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,21,'37:21','یہ وہی فیصلے کا دن ہے جسے تم جھٹلایا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,22,'37:22','حکم ہوگا) ” گھیر لاؤ سب ظالموں اور ان کے ساتھیوں اور ان معبودوں کو');
INSERT INTO "sayyid_qutb" VALUES(37,23,'37:23','جن کی وہ خدا کو چھوڑ کر بندگی کیا کرتے تھے ، پھر ان سب کو جہنم کا راستہ دکھاؤ');
INSERT INTO "sayyid_qutb" VALUES(37,24,'37:24','اور ذرا انہیں ٹھیراؤ، ان سے کچھ پوچھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,25,'37:25','کیا ہوگیا تمہیں ، اب کیوں ایک دوسرے کی مدد نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(37,26,'37:26','ارے ، آج تو یہ اپنے آپ کو (اور ایک دوسرے کو) حوالے کیے دے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,27,'37:27','اس کے بعد یہ ایک دوسرے کی طرف مڑیں گے اور باہم تکرار شروع کردیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(37,28,'37:28','(پیروی کرنے والے اپنے پیشواؤں سے) کہیں گے ” تم ہمارے پاس سیدھے رخ سے آتے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,29,'37:29','وہ جواب دیں گے ” نہیں ، بلکہ تم خود ایمان لانے والے نہ تھے');
INSERT INTO "sayyid_qutb" VALUES(37,30,'37:30','” ہمارا تم پر کوئی زور نہ تھا “۔ تم خود ہی سرکش لوگ تھے');
INSERT INTO "sayyid_qutb" VALUES(37,31,'37:31','آخر کار ہم اپنے رب کے اس فرمان کے مستحق ہوگئے کہ ہم عذاب کا مزا چکھنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(37,32,'37:32','سو ہم نے تم کو بہکایا ، ہم خود بہکے ہوئے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(37,33,'37:33','” اس طرح وہ سب اس روز عذاب میں مشترک ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(37,34,'37:34','ہم مجرموں کے ساتھ یہی کچھ کیا کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,35,'37:35','یہ وہ لوگ تھے کہ جب ان سے کہا جاتا ” اللہ کے سوا کوئی معبود برحق نہیں ہے “۔ تو یہ گھمنڈ میں آجاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,36,'37:36','۔ اور کہتے تھے ” کیا ہم ایک شاعر مجنون کی خاطر اپنے معبودوں کو چھوڑ دیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,37,'37:37','حالانکہ وہ حق لے کر آیا تھا اور اس نے رسولوں کی تصدیق کی تھی');
INSERT INTO "sayyid_qutb" VALUES(37,38,'37:38','۔ (ان ان سے کہا جائے گا کہ) تم لازماً دردناک سزا کا مزا چکھنے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,39,'37:39','۔ اور تمہیں جو بدلہ بھی دیا جا رہا ہے انہیں اعمال کا دیا جا رہا ہے جو تم کرتے رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,40,'37:40','مگر اللہ کے چیدہ بندے (اس انجام بد سے) محفوظ ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(37,41,'37:41','ان کے لئے جانا بوجھا رزق ہے');
INSERT INTO "sayyid_qutb" VALUES(37,42,'37:42','، ہر طرح کی لذیذ چیزیں جن میں وہ عزت کے ساتھ رکھے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(37,43,'37:43','اور نعمت بھری جنتیں');
INSERT INTO "sayyid_qutb" VALUES(37,44,'37:44',') تختوں پر آمنے سامنے بیٹھیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,45,'37:45','۔ شراب کے چشموں سے ساغر بھر بھر کر ان کے درمیان پھرائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,46,'37:46','۔ چمکتی ہوئی شراب ، جو پینے والوں کے لیے لذت ہوگی');
INSERT INTO "sayyid_qutb" VALUES(37,47,'37:47','۔ نہ ان کے جسم کو اس سے کوئی ضرر ہوگا اور نہ ان کی عقل اس سے خراب ہوگی');
INSERT INTO "sayyid_qutb" VALUES(37,48,'37:48','اور ان کے پاس نگاہیں بچانے والی ، خوبصورت آنکھوں والی عورتیں ہوں گی');
INSERT INTO "sayyid_qutb" VALUES(37,49,'37:49','، ایسی نازک جیسے انڈے کے چھلکے کے نیچے چھپی ہوئی جھلی');
INSERT INTO "sayyid_qutb" VALUES(37,50,'37:50','پھر وہ ایک دوسرے کی طرف متوجہ ہوکر حالات پو چھیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,51,'37:51','۔ ان میں سے ایک کہے گا ،” دنیا میں میرا ایک ہم نشین تھا');
INSERT INTO "sayyid_qutb" VALUES(37,52,'37:52','جو مجھ سے کہا کرتا تھا کیا تم بھی تصدیق کرنے والوں میں سے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,53,'37:53','، کیا واقعی جب ہم مر چکے ہوں گے اور مٹی ہوجائیں گے اور ہڈیوں کا پنجربن کر رہ جائیں گے تو ہمیں جزاو سزادی جائے گی ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,54,'37:54','اب کی آپ لوگ دیکھتا چاہتے ہیں کہ وہ صاحب ان کہاں ہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,55,'37:55','یہ کہہ کر جو نہی وہ جھکاتو جہنم کی گہرائی میں اس کو دیکھ لیا');
INSERT INTO "sayyid_qutb" VALUES(37,56,'37:56','اور اس سے خطاب کرکے کعے گا “ خدا کی قسم ، تو تو مجھے تباہ ہی کردینے والا تھا');
INSERT INTO "sayyid_qutb" VALUES(37,57,'37:57','۔ میرے رب کا فضل شامل حال نہ ہوتا تو آج میں بھی ان لوگوں میں سے ہوتا جو پکڑے ہوئے آئے ہوں');
INSERT INTO "sayyid_qutb" VALUES(37,58,'37:58','اچھا تو کیا ان میں ہم مرنے والے نہیں ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(37,59,'37:59','موت جو ہمیں آنی تھی وہ بس پہلے آچکی ؟ ان ہمیں کوئی عذاب نہیں ہونا ؟');
INSERT INTO "sayyid_qutb" VALUES(37,60,'37:60','یقیناً یہی عظیم الشان کامیابی ہے');
INSERT INTO "sayyid_qutb" VALUES(37,61,'37:61','ایسی ہی کامیابی کے لیے عمل کرنے والوں کو عمل کرنا چاہئے');
INSERT INTO "sayyid_qutb" VALUES(37,62,'37:62','بولو ، یہ ضیافت اچھی ہے یا زقوم کا درخت ؟');
INSERT INTO "sayyid_qutb" VALUES(37,63,'37:63','ہم نے اس درخت کو ظالموں کے لیے فتنہ بنادیا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,64,'37:64','وہ ایک درخت ہے جو جہنم کی تمہ سے نکلتا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,65,'37:65','اس کے شگوفے ایسے ہیں جیسے شیطانوں کے سر');
INSERT INTO "sayyid_qutb" VALUES(37,66,'37:66','جہنم کے لوگ اسے کھائیں گے اور اس سے پیٹ بھریں گے');
INSERT INTO "sayyid_qutb" VALUES(37,67,'37:67','” پھر پینے کیلئے ان کو کھولتا ہوا غیر خالص پانی ملے گا');
INSERT INTO "sayyid_qutb" VALUES(37,68,'37:68','اس کے بعد ان کی واپسی اسی آتش دوزخ کی طرف ہوگی');
INSERT INTO "sayyid_qutb" VALUES(37,69,'37:69','یہ وہ لوگ ہیں جنہوں نے اپنے باپ دادا کو گمراہ پایا');
INSERT INTO "sayyid_qutb" VALUES(37,70,'37:70','” اور انہی کے نقش قدم پر دوڑ چلے');
INSERT INTO "sayyid_qutb" VALUES(37,71,'37:71','حالانکہ ان سے پہلے بہت سے لوگ گمراہ ہوچکے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,72,'37:72','۔ اور ان میں ہم نے تنبیہہ کرنے والے رسول بھیجے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,73,'37:73','۔ اب دیکھ لو کہ ان تنبیہہ کیے جانے والوں کا کیا انجام ہوا');
INSERT INTO "sayyid_qutb" VALUES(37,74,'37:74','اس بدانجامی سے بس اللہ کے وہی بندے بچے ہیں جنہیں ان ۔۔ لیے خالص کرلیا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,75,'37:75','ہم کو (اس سے پہلے) نوح نے پکارا تھا ، تو دیکھو کہ ہم کیسے اچھے جواب دینے والے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,76,'37:76','۔ ہم نے اس کو اور اس کے گھر والوں کو کرب عظیم سے بچا لیا');
INSERT INTO "sayyid_qutb" VALUES(37,77,'37:77','اور اسی کی نسل کو باقی رکھا ،');
INSERT INTO "sayyid_qutb" VALUES(37,78,'37:78','اور بعد کی نسلوں میں اس کی تعریف و توصیف چھوڑ دی');
INSERT INTO "sayyid_qutb" VALUES(37,79,'37:79','۔ سلام ہے نوح پر تمام دنیا والوں میں');
INSERT INTO "sayyid_qutb" VALUES(37,80,'37:80','ہم نیکی کرنے والوں کو ایسی ہی جزا دیا کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,81,'37:81','۔ در حقیقت وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(37,82,'37:82','پھر دوسرے گروہ کو ہم نے غرق کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(37,83,'37:83','اور نوح ہی کے طریقے پر چلنے والا ابراہیم تھا۔');
INSERT INTO "sayyid_qutb" VALUES(37,84,'37:84','” جب وہ اپنے رب کے حضور قلب سلیم لے کر آیا');
INSERT INTO "sayyid_qutb" VALUES(37,85,'37:85','۔ جب اس نے اپنے باپ اور اپنی قوم سے کہا ” یہ کیا چیزیں ہیں جن کی تم عبادت کر رہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,86,'37:86','کیا اللہ کو چھوڑ کر جھوٹ گھڑے ہوئے معبود چاہتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,87,'37:87','آخر رب العالمین کے بارے میں تمہارا کیا گمان ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,88,'37:88','پھر اس نے تاروں پر ایک نگاہ ڈالی');
INSERT INTO "sayyid_qutb" VALUES(37,89,'37:89','” اور کہا میری طبعیت خراب ہے');
INSERT INTO "sayyid_qutb" VALUES(37,90,'37:90','۔ چناچہ وہ لوگ اسے چھوڑ کر چلے گئے');
INSERT INTO "sayyid_qutb" VALUES(37,91,'37:91','۔ ان کے پیچھے وہ چپکے سے ان کے معبودوں کے مندر میں گھس گیا اور بولا ” آپ لوگ کھاتے کیوں نہیں ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(37,92,'37:92','کیا ہوگیا ، آپ لوگ بولتے بھی نہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,93,'37:93','اس کے بعد وہ ان پر پل پڑا اور سیدھے ہاتھ سے خوب ضربیں لگائیں');
INSERT INTO "sayyid_qutb" VALUES(37,94,'37:94','(واپس آکر) ” وہ لوگ بھاگے بھاگے اس کے پاس آئے');
INSERT INTO "sayyid_qutb" VALUES(37,95,'37:95','اس نے کہا ” کیا تم اپنی ہی تراشی ہوئی چیزوں کو پوجتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,96,'37:96','” حالانکہ اللہ ہی نے تم کو بھی پیدا کیا ہے اور ان چیزوں کو بھی جنہیں تم بناتے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,97,'37:97','انہوں نے کہا اس کے لیے ایک الاؤ تیار کرو اور اسے دہکتی ہوئی آگ کے ڈھیر میں پھینک دو');
INSERT INTO "sayyid_qutb" VALUES(37,98,'37:98','انہوں نے اس کے خلاف ایک کاروائی کرنی چاہی تھی ، مگر ہم نے انہی کو نیچا دکھا دیا');
INSERT INTO "sayyid_qutb" VALUES(37,99,'37:99','ابراہیم نے کہا ” میں اپنے رب کی طرف جاتا ہوں ، وہی میری راہنمائی کرے گا');
INSERT INTO "sayyid_qutb" VALUES(37,100,'37:100','اے پروردگار ، مجھے ایک بیٹا عطا کر جو صالحوں میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,101,'37:101','”(اس دعا کے جواب میں) ہم نے اس کو ایک علیم (بردبار) لڑکے کی بشارت دی');
INSERT INTO "sayyid_qutb" VALUES(37,102,'37:102','” وہ لڑکا جب اس کے ساتھ دوڑ دھوپ کرنے کی عمر کو پہنچ گیا تو (ایک روز) ابراہیم نے اس سے کہا ، ” بیٹا ، میں خواب میں دیکھتا ہوں کہ میں تجھے ذبح کر رہا ہوں ، اب تو بتا تیرا کیا خیال ہے ؟ “ اباجان ، جو کچھ آپ کو حکم دیا جا رہا ہے ، اسے کر ڈالیے ، آپ انشاء اللہ مجھے صابروں میں سے پائیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,103,'37:103','آخر کو جب دونوں نے سر تسلیم خم کردیا اور ابراہیم نے بیٹے کو ماتھے کے بل گرا دیا');
INSERT INTO "sayyid_qutb" VALUES(37,104,'37:104','اور ہم نے ندا دی کہ ” اے ابراہیم');
INSERT INTO "sayyid_qutb" VALUES(37,105,'37:105','، تو نے خواب سچ کر دکھایا۔ ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,106,'37:106','یقیناً یہ ایک کھلی آزمائش تھی');
INSERT INTO "sayyid_qutb" VALUES(37,107,'37:107','“۔ اور ہم نے ایک بڑی قربانی فدیے میں دے کر اس بچے کو چھڑا لیا');
INSERT INTO "sayyid_qutb" VALUES(37,108,'37:108','اور اس کی تعریف و توصیف ہمیشہ کے لیے بعد کی نسلوں میں چھوڑ دی');
INSERT INTO "sayyid_qutb" VALUES(37,109,'37:109','سلام ہے ابراہیم پر');
INSERT INTO "sayyid_qutb" VALUES(37,110,'37:110','');
INSERT INTO "sayyid_qutb" VALUES(37,111,'37:111','یقینا وہ ہمارے مومن بندوں میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(37,112,'37:112','اور ہم نے اسے اسحاق کی بشارت دی ، ایک نبی صالحین میں سے');
INSERT INTO "sayyid_qutb" VALUES(37,113,'37:113','اور اسے اور اسحاق کو برکت دی۔ اب ان دونوں کی ذریت میں سے کوئی محسن ہے اور کوئی اپنے نفس پر صریح ظلم کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,114,'37:114','اور ہم نے موسیٰ و ہارون پر احسان کیا');
INSERT INTO "sayyid_qutb" VALUES(37,115,'37:115','، ان کو اور ان کی قوم کو کرب عظیم سے نجات دی ،');
INSERT INTO "sayyid_qutb" VALUES(37,116,'37:116','انہیں نصرت بخشی جس کی جوہ سے وہی غالب رہے ،');
INSERT INTO "sayyid_qutb" VALUES(37,117,'37:117','ان کو نہایت واضح کتاب عطا کی');
INSERT INTO "sayyid_qutb" VALUES(37,118,'37:118','” ، انہیں راہ راست دکھائی');
INSERT INTO "sayyid_qutb" VALUES(37,119,'37:119','اور بعد کی نسلوں میں ان کا ذکر خیر باقی رکھا');
INSERT INTO "sayyid_qutb" VALUES(37,120,'37:120','سلام ہے موسیٰ اور ہارون پر۔');
INSERT INTO "sayyid_qutb" VALUES(37,121,'37:121','ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(37,122,'37:122','درحقیقت وہ ہمارے مومن بندوں میں سے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(37,123,'37:123','اور الیاس بھی یقیناً مرسلین میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(37,124,'37:124','۔ یاد کرو جب اس نے اپنی قوم سے کہا تھا کہ ” تم لوگ ڈرتے نہیں ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,125,'37:125','” کیا تم بعل کو پکارتے ہو اور احسن الخالقین کو چھوڑ دیتے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,126,'37:126','، اس اللہ کو جو تمہارا اور تمہارے اگلے پچھلے آباو اجداد کا رب ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(37,127,'37:127','مگر انہوں نے اسے جھٹلا دیاسو اب یقیناً وہ سزا کے لیے پیش کیے جانے والے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(37,128,'37:128','، بجران بندگان خدا کے جن کو خالص کرلیا گیا تھا');
INSERT INTO "sayyid_qutb" VALUES(37,129,'37:129','اور الیاس کا ذکر خیر ہم نے بعد کی نسلوں میں باقی رکھا');
INSERT INTO "sayyid_qutb" VALUES(37,130,'37:130','سلام ہے الیاس پر');
INSERT INTO "sayyid_qutb" VALUES(37,131,'37:131','۔ ہم نیکی کرنے والوں کو ایسی ہی جزا دیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(37,132,'37:132','۔ واقعی وہ ہمارے مومن بندوں میں سے تھا “۔');
INSERT INTO "sayyid_qutb" VALUES(37,133,'37:133','اور لوط بھی انہیں لوگوں میں سے تھا جو رسول بنا کر بھیجے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,134,'37:134','یاد کرو جب ہم نے اس کو اور اس کے سب گھر والوں کو نجات دی');
INSERT INTO "sayyid_qutb" VALUES(37,135,'37:135','، سوائے ایک بڑھیا کے جو پیچھے رہ جانے والوں میں سے تھی');
INSERT INTO "sayyid_qutb" VALUES(37,136,'37:136','پھر باقی سب کو تہس نہس کر دیا');
INSERT INTO "sayyid_qutb" VALUES(37,137,'37:137','آج تم شب و روز ان کے اجڑے دیار پر سے گزرتے ہو');
INSERT INTO "sayyid_qutb" VALUES(37,138,'37:138','کیا تم کو عقل نہیں آتی ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(37,139,'37:139','اور یقینا یونس بھی رسولوں میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(37,140,'37:140','۔ یاد کرو جب وہ ایک بھری کشتی میں عرب بھاگ نکلا');
INSERT INTO "sayyid_qutb" VALUES(37,141,'37:141','، پھر قرعہ اندازی میں شریک ہوا اور اس میں مات کھائی');
INSERT INTO "sayyid_qutb" VALUES(37,142,'37:142','آخر کار مچھلی نے اسے نگل لیا اور وہ ملامت زدہ تھا');
INSERT INTO "sayyid_qutb" VALUES(37,143,'37:143','۔ اب اگر وہ تسبیح کرنے والوں میں سے نہ ہوتا');
INSERT INTO "sayyid_qutb" VALUES(37,144,'37:144','تو روز قیامت تک اسی مچھلی کے پیٹ میں رہتا');
INSERT INTO "sayyid_qutb" VALUES(37,145,'37:145','آخر کار ہم نے اسے بڑی سقیم حالت میں ایک چٹیل زمین پر پھینک دیا');
INSERT INTO "sayyid_qutb" VALUES(37,146,'37:146','اور اس پر ایک بیلدار درخت اگا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(37,147,'37:147','۔ اس کے بعد ہم نے اسے ایک لاکھ ، یا اس سے زائد لوگوں کی طرف بھیجا۔');
INSERT INTO "sayyid_qutb" VALUES(37,148,'37:148','۔ وہ ایمان لائے ، اور ہم نے ایک وقت خاص تک انہیں باقی رکھا');
INSERT INTO "sayyid_qutb" VALUES(37,149,'37:149','پھر ذرا ان لوگوں سے پوچھو ، کیا (ان کے دل کو یہ بات لگتی ہے کہ) تمہارے رب کے لیے تو ہوں بیٹیاں اور ان کے لیے ہوں بیٹے ؟');
INSERT INTO "sayyid_qutb" VALUES(37,150,'37:150','” کیا واقعی ہم نے ملائکہ کو عورتیں ہی بنایا ہے اور یہ آنکھوں دیکھی بات کہہ رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(37,151,'37:151','خوب سن رکھو ، دراصل یہ لوگ اپنی من گھڑت سے یہ بات کہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,152,'37:152','کہ ” اللہ اولاد رکھتا ہے “ اور فی الواقع یہ جھوٹے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,153,'37:153','کیا اللہ نے بیٹوں کے بجائے بیٹیاں اپنے لیے پسند کرلیں ،');
INSERT INTO "sayyid_qutb" VALUES(37,154,'37:154','تمہیں کیا ہوگیا ہے ، کیسے حکم لگا رہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(37,155,'37:155','۔ کیا تمہیں ہوش نہیں آتا ؟');
INSERT INTO "sayyid_qutb" VALUES(37,156,'37:156','یا پھر تمہارے پاس اپنی ان باتوں کے لیے کوئی صاف سند ہے');
INSERT INTO "sayyid_qutb" VALUES(37,157,'37:157','، تو لاؤ اپنی وہ کتاب اگر تم سچے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(37,158,'37:158','انہوں نے اللہ اور جنوں کے درمیان نسب کا رشتہ بنا رکھا ہے حالانکہ جن خوب جانتے ہیں کہ یہ مجرم کی حیثیت سے پیش ہونے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,159,'37:159','اللہ پاک ہے ان باتوں سے جو یہ لوگ ان کی طرف منسوب کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(37,160,'37:160','” ماسوائے اللہ کے ان بندوں کئ جو مخلص ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(37,161,'37:161','پس تم اور تمہارے یہ معبود');
INSERT INTO "sayyid_qutb" VALUES(37,162,'37:162','اللہ سے کسی کو پھیر نہیں سکتے');
INSERT INTO "sayyid_qutb" VALUES(37,163,'37:163','” مگر صرف اس کو جو دوزخ کی بھڑکتی ہوئی آگ میں جھلسنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(37,164,'37:164','۔ اور ہمارا حال تو یہ ہے کہ ہم میں سے ہر ایک کا ایک مقام مقرر ہے');
INSERT INTO "sayyid_qutb" VALUES(37,165,'37:165','اور ہم صف بستہ خدمت گار ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,166,'37:166','ور تسبیح کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(37,167,'37:167','یہ لوگ پہلے تو کہا کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(37,168,'37:168','” کہ کاش ہمارے پاس وہ ” ذکر “ ہوتا جو پچھلی قوموں کو ملا تھا');
INSERT INTO "sayyid_qutb" VALUES(37,169,'37:169','تو ہم اللہ کے چیدہ بندے ہوتے');
INSERT INTO "sayyid_qutb" VALUES(37,170,'37:170','مگر (جب وہ آگیا) تو انہوں نے اس کا انکار کردیا۔ اب عنقریب انہیں (اس روش کا نتیجہ) معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(37,171,'37:171','اپنے بھیجے ہوئے بندوں سے ہم پہلے ہی وعدہ کرچکے ہیں کہ');
INSERT INTO "sayyid_qutb" VALUES(37,172,'37:172','یقیناً ان کی مدد کی جائے گی اور');
INSERT INTO "sayyid_qutb" VALUES(37,173,'37:173','ہمارا لشکر ہی غالب ہوکر رہے گا');
INSERT INTO "sayyid_qutb" VALUES(37,174,'37:174','پس اے نبی صلى الله عليه وسلم ، ذرا کچھ مدت تک انہیں ان کے چال پر چھوڑ دو');
INSERT INTO "sayyid_qutb" VALUES(37,175,'37:175','اور دیکھتے رہو ، عنقریب یہ خود بھی دیکھ لیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,176,'37:176','۔ کیا یہ ہمارے عذاب کے لیے جلدی مچا رہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(37,177,'37:177','جب وہ ان کے صحن میں اترے گا تو وہ دن ان لوگوں کے لیے بہت برا ہوگا جنہیں متنبہ کیا جاچکا ہے');
INSERT INTO "sayyid_qutb" VALUES(37,178,'37:178','،۔ پس ذرا انہیں کچھ مدت کے لئے چھوڑ دو');
INSERT INTO "sayyid_qutb" VALUES(37,179,'37:179','اور دیکھتے رہو ، عنقریب یہ خود دیکھ لیں گے');
INSERT INTO "sayyid_qutb" VALUES(37,180,'37:180','پاک ہے تیرا رب ، عزت کا مالک ، ان تمام باتوں سے جو یہ لوگ بنا رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(37,181,'37:181','اور سلام ہے رسولوں پر ،');
INSERT INTO "sayyid_qutb" VALUES(37,182,'37:182','اور ساری تعریف اللہ رب العالمین ہی کے لئے ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,1,'38:1','ص '' قسم ہے نصیحت بھرے قرآن کی ''');
INSERT INTO "sayyid_qutb" VALUES(38,2,'38:2','بلکہ یہی لوگ '' جنہوں نے ماننے سے انکار کیا ہے '' سخت تکبر اور ضد میں مبتلا ہیں');
INSERT INTO "sayyid_qutb" VALUES(38,3,'38:3','ان سے ہم کتنی ایسی ہی قوموں کو ہلاک کرچکے ہیں (اور جب ان کی شامت آئی ہے) تو وہ چیخ اٹھے ہیں '' مگر وہ وقت بچنے کا نہیں ہوتا “۔');
INSERT INTO "sayyid_qutb" VALUES(38,4,'38:4','ان لوگوں کو اس بات پر تعجب ہوا کہ ایک ذرانے والا خود انہی میں سے آگیا۔ منکرین کہنے لگے کہ ” یہ ساحر ہے '' سخت جھوٹا ہے ''');
INSERT INTO "sayyid_qutb" VALUES(38,5,'38:5','کیا اس نے سارے خداؤں کی جگہ بس ایک ہی خدا بنا ڈالا ؟ یہ تو بڑی عجیب بات ہے');
INSERT INTO "sayyid_qutb" VALUES(38,6,'38:6','اور سرداران قوم یہ کہتے ہوئے نکل گئے کہ ” چلو اور ڈٹے رہو اپنے معبودوں کی عبادت پر یہ بات تو کسی اور ہی غرض سے کہی جارہی ہے');
INSERT INTO "sayyid_qutb" VALUES(38,7,'38:7','یہ بات ہم نے زمانہ قریب کی ملت میں کسی سے نہیں سنی۔ یہ کچھ نہیں ہے مگر ایک من گھڑت بات “');
INSERT INTO "sayyid_qutb" VALUES(38,8,'38:8','کیا ہمارے درمیان بس یہی شخص رہ گیا تھا جس پر اللہ کا ذکر نازل کردیا گیا ؟ اصل بات یہ ہے کہ یہ میرے ” ذکر “ پر شک کررہے ہیں ، اور ساری باتیں اس لیے کررہے ہیں کہ انہوں نے میرے عذاب کا مزا چکھا نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,9,'38:9','کیا تیرے داتا اور غالب پروردگار کی رحمت کے خزانے ان کے قبضے میں ہیں');
INSERT INTO "sayyid_qutb" VALUES(38,10,'38:10','کیا یہ آسمان و زمین اور ان کے درمیان کی چیزوں کے مالک ہیں اچھا تو یہ عالم اسباب کی بلندیون پر چڑھ کر دیکھیں !');
INSERT INTO "sayyid_qutb" VALUES(38,11,'38:11','یہ تو جتھوں میں ایک چھوٹا سا جتھہ ہے جو اسی جگہ شکست کھانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,12,'38:12','ان سے پہلے نوح (علیہ السلام) کی قوم اور عاد '' اور میخوں والا فرعون');
INSERT INTO "sayyid_qutb" VALUES(38,13,'38:13','اور ثمود اور قوم لوط اور ایکہ والے جھٹلاچکے ہیں ۔ جتھے وہ تھے۔');
INSERT INTO "sayyid_qutb" VALUES(38,14,'38:14','ان میں سے ہر نے رسولوں کو جھٹلایا اور میری عقوبت کا فیصلہ اس پر چسپاں ہو کر رہا۔');
INSERT INTO "sayyid_qutb" VALUES(38,15,'38:15','وہ لوگ بھی بس ایک دھماکے کے منتظر ہیں جس کے بعد کوئی دوسرا دھماکہ نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(38,16,'38:16','اور یہ کہتے ہیں کہ اے ہمارے رب '' یوم الحساب سے پہلے ہی ہمارا حصہ ہمیں جلدی سے دے دے۔');
INSERT INTO "sayyid_qutb" VALUES(38,17,'38:17','اے نبی صبر کرو ان باتوں پر جو یہ لوگ بناتے ہیں ، اور ان کے سامنے ہمارے بندے داؤد کا قصہ بیان کرو جو بڑی قوتوں کا مالک تھا ہر معاملہ میں اللہ کی طرف رجوع کرنے والا تھا');
INSERT INTO "sayyid_qutb" VALUES(38,18,'38:18','۔ ہم نے پہاڑوں کو اس کے ساتھ مسخر کررکھا تھا کہ صبح وشام وہ اس کے ساتھ تسبیح کرتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(38,19,'38:19','پرندے سمٹ آتے ، سب کے سب اس کی تسبیح کی طرف متوجہ ہوجاتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(38,20,'38:20','ہم نے اس کی سلطنت مصبوط کردی تھی ، اس کو حکمت عطا کی تھی اور فیصلہ کن بات کہنے کی صلاحیت بخشی تھی “۔');
INSERT INTO "sayyid_qutb" VALUES(38,21,'38:21','پھر تمہیں کچھ خبر پہنچی ہے ان مقدمے والوں کی جو دیوار چڑھ کر اس کے بالاخانے میں گھس آئے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(38,22,'38:22','جب وہ داؤد کے پاس پہنچے تو وہ انہیں دیکھ کر گھبرا گیا۔ انہوں نے کہا ” ڈریئے نہیں “ ہم دوفریق مقدمہ ہیں جن میں سے ایک نے دوسرے پر زیادتی کی ہے۔ آپ ہمارے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ کردیجئے ، بےانصافی نہ کیجئے اور ہمیں راہ راست بتایئے');
INSERT INTO "sayyid_qutb" VALUES(38,23,'38:23','۔ یہ میرا بھائی ہے ، اس کے پاس نناوے دنبیاں ہیں اور میرے پاس صرف ایک ہی دنبی ہے۔ اس نے مجھ سے کہا کہ یہ ایک دنبی بھی میرے حوالے کردے اور اس نے گفتگو میں مجھے دبا کیا');
INSERT INTO "sayyid_qutb" VALUES(38,24,'38:24','داؤد نے جواب دیا۔” اس شخص نے اپنی دنبیوں کے ساتھ تیری دنبی ملالینے کا مطالبہ کرکے یقیناً تجھ پر ظلم کیا ، اور واقعہ یہ ہے کہ مل جل کر ساتھ رہنے والے لوگ اکثر ایک دوسرے پر زیادتیاں کرتے رہتے ہیں ، بس وہی لوگ اس سے بچے ہوئے ہیں جو ایمان رکھتے اور عمل صالح کرتے ہیں ، اور ایسے لوگ کم ہی ہیں “۔ داؤد سمجھ کیا کہ یہ تو ہم نے دراصل اس کی آزمائش کی ہے ، چناچہ اس نے اپنے رب سے معافی مانگی اور سجدے میں گر گیا اور رجوع کرلیا “۔');
INSERT INTO "sayyid_qutb" VALUES(38,25,'38:25','تب ہم نے اس کا وہ قصور معاف کیا اور یقیناً ہمارے ہاں اس کے لیے تقرب کا مقام اور بہتر انجام ہے');
INSERT INTO "sayyid_qutb" VALUES(38,26,'38:26',': (ہم نے اس سے کہا) ” اے داؤد '' ہم نے تجھے زمین میں خلیفہ بنایا ہے ، لہٰذا تو لوگوں کے درمیان حق کے ساتھ حکومت کر اور خواہش نفس کی پیروی نہ کر کہ وہ تجھے اللہ کی راہ سے بھٹکا دے گی۔ جو لوگ اللہ کی راہ سے بھٹکتے ہیں یقیناً ان کے لیے سخت سزا ہے کہ وہ یوم الحساب کو بھول گئے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,27,'38:27','ہم نے اس آسمان اور زمین کو اور اس دنیا کو جو ان کے درمیان ہے '' فضول پیدا نہیں کردیا یہ تو ان لوگوں کا گمان ہے جنہوں نے کفر کیا ہے ، اور ایسے کافروں کے لیے بربادی ہے جہنم کی آگ سے');
INSERT INTO "sayyid_qutb" VALUES(38,28,'38:28','کیا ہم ان لوگوں کو جو ایمان لاتے اور نیک عمل کرتے ہیں اور انکو جو زمین میں فساد کرنے والے ہیں ، یکساں کردیں ؟ کیا متقیوں کو ہم فاجروں جیسا کردیں ؟');
INSERT INTO "sayyid_qutb" VALUES(38,29,'38:29','یہ ایک بڑی برکت والی کتاب ہے جو (اے نبی صلى الله عليه وسلم ہم نے تمہاری طرف نازل کی ہے تاکہ یہ لوگ اسکی آیات پر غور کریں اور عقل و فکر رکھنے والے اس سے سبق لیں “');
INSERT INTO "sayyid_qutb" VALUES(38,30,'38:30','اور داؤد کو ہم نے سلیمان (جیسا بیٹا) عطا کیا ، بہترین بندہ ، کثرت سے اپنے رب کی طرف رجوع کرنے والا');
INSERT INTO "sayyid_qutb" VALUES(38,31,'38:31','قابل ذکر ہے وہ موقع جب شام کے وقت اس کے سامنے خوب سدھے ہوئے گھوڑے پیش کیے گئے');
INSERT INTO "sayyid_qutb" VALUES(38,32,'38:32','تو اس نے کہا ” میں نے اس مال کی محبت اپنے رب کی یاد سے اختیار کی ہے یہاں تک کہ جب وہ گھوڑے نگاہ سے اوجھل ہوگئے');
INSERT INTO "sayyid_qutb" VALUES(38,33,'38:33','تو (اس نے حکم دیا کہ) انہیں میرے پاس واپس لاؤ، پھر لگا ان کی پنڈلیوں اور گردن پر ہاتھ پھیرنے');
INSERT INTO "sayyid_qutb" VALUES(38,34,'38:34','اور (دیکھو کہ) سلیمان کو بھی ہم نے آزمائش میں ڈالا اور اس کی کرسی پر ایک جسد لاکر ڈال دیا۔ پھر اس نے رجوع کیا');
INSERT INTO "sayyid_qutb" VALUES(38,35,'38:35','اور کہا کہ ” اے میرے رب '' مجھے معاف کردے اور مجھے وہ بادشاہی دے جو میرے بعد کسی کے لیے سزاوار نہ ہو '' بیشک تو ہی اصل داتا ہے');
INSERT INTO "sayyid_qutb" VALUES(38,36,'38:36','تب ہم نے اس کے لیے ہوا کو مسخر کردیا جو اس کے حکم سے نرمی کے ساتھ چلتی جدھر وہ چاہتا تھا ۔');
INSERT INTO "sayyid_qutb" VALUES(38,37,'38:37','، اور شیاطین کو مسخر کردیا ، ہر طرح کے معمار اور غوطہ خور');
INSERT INTO "sayyid_qutb" VALUES(38,38,'38:38','اور دوسرے جو پابند سلاسل تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,39,'38:39','(ہم نے اس سے کہا) ” یہ ہماری بخشش ہے ، تجھے اختیار ہے جسے چاہے دے اور جس سے چاہے روک لے ، کوئی حساب نہیں');
INSERT INTO "sayyid_qutb" VALUES(38,40,'38:40','یقیناً اس کے لیے ہمارے ہاں تقریب کا مقام اور بہتر انجام ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,41,'38:41','اور ہمارے بندے ایوب کا ذکر کرو ، جب اس نے اپنے رب کو پکارا کہ شیطان نے مجھے تکلیف اور عذاب میں ڈال دیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,42,'38:42','(ہم نے اسے حکم دیا) اپنا پاؤں زمین پر مارا ، یہ ہے ٹھنڈا پانی نہانے کیلیے اور پینے کیلیے۔');
INSERT INTO "sayyid_qutb" VALUES(38,43,'38:43','ہم نے اسے اس کے اہل و عیال واپس دیئے اور انکے ساتھ اتنے ہی اور '' اپنی طرف سے رحمت کے طور پر ، اور عقل و فکر رکھنے والوں کیلیے درس کے طور پر۔');
INSERT INTO "sayyid_qutb" VALUES(38,44,'38:44','(اور ہم نے اس سے کہا) تنکوں کا ایک مٹھالے اور اس سے مار دے ، اپنی قسم نہ توڑ۔ ہم نے اسے صابر پایا ، اپنے رب کی طرف رجوع کرنے والا۔');
INSERT INTO "sayyid_qutb" VALUES(38,45,'38:45','اور ہمارے بندوں ابراہیم اور اسحاق اور یعقوب (علیہم السلام) کا ذکر کرو۔ بڑی قوت عمل رکھنے والے اور دیدہ ور لوگ تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(38,46,'38:46','ہم نے ان کو ایک خالص صفت کی بنا پر برگزیدہ کیا تھا ، اور وہ دار آخرت کی یاد تھی');
INSERT INTO "sayyid_qutb" VALUES(38,47,'38:47','اور یقیناً ہمارے ہاں ان کا شمار چنے ہوئے نیک اشخاص میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(38,48,'38:48','اور یاد کرو حضرت اسماعیل ، حضرت ذوالکفل اور حضرت السیع (علیہم السلام) اس کے برگزیدہ بندوں میں سے ہیں');
INSERT INTO "sayyid_qutb" VALUES(38,49,'38:49','یہ ایک ذکر تھا (اب سنو کہ) متقی لوگوں کے لیے یقیناً بہترین ٹھکانا ہے');
INSERT INTO "sayyid_qutb" VALUES(38,50,'38:50','، ہمیشہ رہنے والی جنتیں جن کے دروازے ان کے لیے کھلے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(38,51,'38:51','۔ ان میں وہ تکیے لگائے بیٹھے ہوں گے ، خوب خوب فواکہ اور مشروبات طلب کررہے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(38,52,'38:52','اور ان کے پاس شرمیلی ہم سن بیویاں ہوں گی');
INSERT INTO "sayyid_qutb" VALUES(38,53,'38:53','یہ وہ چیزیں ہیں جنہیں حساب کے دن عطا کرنے کا تم سے وعدہ کیا جارہا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,54,'38:54','یہ ہمارا رزق ہے جو کھبی ختم ہونے والا نہیں۔ یہ تو ہے متقیوں کا انجام');
INSERT INTO "sayyid_qutb" VALUES(38,55,'38:55','اور سرکشوں کے لیے بدترین ٹھکانا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(38,56,'38:56','جہنم جس میں وہ جھلسے جائیں گے بہت ہی بری قیام گاہ');
INSERT INTO "sayyid_qutb" VALUES(38,57,'38:57','۔ یہ ہے ان کے لیے ، پس وہ مزا چکھیں کھولتے ہوئے پانی اور پیپ ، لہو');
INSERT INTO "sayyid_qutb" VALUES(38,58,'38:58','اور اسی قسم کی دوسری تلخیوں کا');
INSERT INTO "sayyid_qutb" VALUES(38,59,'38:59','۔ (وہ جہنم کی طرف اپنے پیرو وں کو آتے دیکھ کر آپس میں کہیں گے) ” یہ ایک لشکر تمہارے پاس گھسا چلا آرہا ہے کوئی خوش آمدید ان کے لیے نہیں ہے ، یہ آگ میں جھلسنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(38,60,'38:60','وہ ان کو جواب دیں گے ” نہیں بلکہ تم ہی جھلسے جارہے ہو ، کوئی خیر مقدم تمہارے لیے نہیں ہے۔ تم ہی تو یہ انجام ہمارے آگے لائے ہو ، کیسی بری ہے یہ جائے قرار');
INSERT INTO "sayyid_qutb" VALUES(38,61,'38:61','پھر وہ کہیں گے ” اے ہمارے رب ، جس نے ہمیں اس انجام کو پہنچانے کا بندوبست کیا ، اس کو دوزخ کا دوہرا عذاب دے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,62,'38:62','اور وہ آپس میں کہیں گے ” کیا بات ہے '' ہم ان لوگوں کو کہیں ، نہیں دیکھتے جنہیں وہ دنیا میں برا سمجھتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(38,63,'38:63','ہم نے یونہی ان کا مذاق بنالیا یا وہ کہیں نظروں سے اوجھل ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(38,64,'38:64','“ بیشک یہ بات سچی ہے ، اہل دوذخ میں یہی کچھ جھگڑے ہونے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(38,65,'38:65',': (اے نبی صلى الله عليه وسلم ان سے کہو ” میں تو بس خبردار کردینے والا ہوں۔ کوئی حقیقی معبود نہیں۔ مگر اللہ جو یکتا ہے ، سب پر غالب ،');
INSERT INTO "sayyid_qutb" VALUES(38,66,'38:66','آسمانوں اور زمین کا مالک اور ان ساری چیزوں کا مالک جو ان کے درمیان ہیں ، زبردست اور درگزر کرنے والا “۔');
INSERT INTO "sayyid_qutb" VALUES(38,67,'38:67','ان سے کہو ” یہ ایک بڑی خبر ہے');
INSERT INTO "sayyid_qutb" VALUES(38,68,'38:68','جس کو سن کر تم منہ پھیرتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(38,69,'38:69','(ان سے کہو) ” مجھے اس وقت کی کوئی خبر نہ تھی جب ملاء اعلیٰ میں جھگڑا ہورہا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(38,70,'38:70','مجھ کو تو وحی کے ذریعہ سے یہ باتیں صرف اس لیے بتائی جاتی ہیں کہ میں کھلا کھلا خبردار کرنے والا ہوں');
INSERT INTO "sayyid_qutb" VALUES(38,71,'38:71','جب تیرے رب نے فرشتوں سے کہا ” میں مٹی سے ایک بشر بنانے والا ہوں ''');
INSERT INTO "sayyid_qutb" VALUES(38,72,'38:72','پھر جب میں اسے پوری طرح بنا دوں '' اور اس میں اپنی روح پھونک دوں تو تم اس کے آگے سجدے میں گرجاؤ “۔');
INSERT INTO "sayyid_qutb" VALUES(38,73,'38:73','حکم کے مطابق فرشتے سب کے سب سجدے میں گرگئے');
INSERT INTO "sayyid_qutb" VALUES(38,74,'38:74','مگر ابلیس نے اپنی بڑائی کا گھمنڈ کیا اور وہ کافروں میں سے ہوگیا۔');
INSERT INTO "sayyid_qutb" VALUES(38,75,'38:75','رب نے فرمایا ” اے ابلیس '' تجھے کیا چیز اس کو سجدہ کرنے سے مانع ہوئی جسے میں نے اپنے دونوں ہاتھوں سے بنایا ہے ؟ تو بڑا بن رہا ہے یا تو ہے ہی کچھ اونچے درجے کی ہستیوں میں سے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,76,'38:76','اس نے جواب دیا ” میں اس سے بہتر ہوں “ آپ نے مجھ کو آگ سے پیدا کیا ہے اور اس کو مٹی سے۔');
INSERT INTO "sayyid_qutb" VALUES(38,77,'38:77','فرمایا ” اچھا تو یہاں سے نکل جا ، تو مردود ہے ،');
INSERT INTO "sayyid_qutb" VALUES(38,78,'38:78','اور تیرے اوپر یوم الجزاء تک میری لعنت ہے۔');
INSERT INTO "sayyid_qutb" VALUES(38,79,'38:79','وہ بولا ” اے میرے رب ، یہ بات تو پھر اس وقت تک کے لیے مجھے مہلت دے دے جب یہ لوگ دوبارہ اٹھائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(38,80,'38:80','فرمایا ” اچھا ، تجھے اس روز تک مہلت ہے');
INSERT INTO "sayyid_qutb" VALUES(38,81,'38:81','جس کا وقت مجھے معلوم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,82,'38:82','اس نے کہا ” تیری عزت کی قسم '' میں سب لوگوں کو بہکا کررہوں گا');
INSERT INTO "sayyid_qutb" VALUES(38,83,'38:83','، بجز تیرے ان بندوں کے جنہیں تو نے خالص کرلیا ہے “');
INSERT INTO "sayyid_qutb" VALUES(38,84,'38:84','فرمایا ” تو حق یہ ہے '' اور میں حق ہی کہا کرتا ہوں ،');
INSERT INTO "sayyid_qutb" VALUES(38,85,'38:85','کہ میں جہنم کو تجھ سے اور ان سب لوگوں سے پھر دوں گا جو ان انسانوں میں سے تیری پیروی کریں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(38,86,'38:86',': (اے نبی صلى الله عليه وسلم ان سے کہہ دو کہ میں اس تبلغ پر تم سے کوئی اجر نہیں مانگتا ، اور نہ میں بناوٹی لوگوں میں سے ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(38,87,'38:87','ہ تو ایک نصیحت ہے تمام والوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(38,88,'38:88','اور تھوڑی مدت ہی گزرے گی کہ تمہیں اس کا حال خود معلوم ہوجائے گا “');
INSERT INTO "sayyid_qutb" VALUES(39,1,'39:1','اس کتاب کا نزول اللہ زبردست اور دانا کی طرف سے ہے');
INSERT INTO "sayyid_qutb" VALUES(39,2,'39:2','(اے نبی صلى الله عليه وسلم یہ کتاب ہم نے تمہاری طرف برحق نازل کی ہے ، لہٰذا تم اللہ ہی کی بندگی کرو ، دین کو اسی کے لیے خالص کرتے ہوئے');
INSERT INTO "sayyid_qutb" VALUES(39,3,'39:3','خبردار ، دین خالص اللہ کا حق ہے رہے وہ لوگ جنہوں نے اس کے سوا دوسرے سرپرست بنارکھے ہیں (اور اپنے اس فعل کی توجیہ یہ کرتے ہیں کہ) ہم تو ان کی عبادت اس لیے کرتے ہیں کہ وہ اللہ تک ہماری رسائی کرادیں '' اللہ یقیناً ان کے درمیان ان تمام باتوں کا فیصلہ کر دے گا جن میں وہ اختلاف کررہے ہیں۔ اللہ کسی ایسے شخص کو ہدایت نہیں دیتا ۔ جو جھوٹا اور منکرحق ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(39,4,'39:4','اگر اللہ کسی کو بیٹا بنانا چاہتا تو اپنی مخلوق میں سے جس کو چاہتا ، برگزیدہ کرلیتا ، پاک ہے وہ اس سے (کہ کوئی اس کا بیٹا ہو) ، وہ اکیلا اور سب پر غالب ۔');
INSERT INTO "sayyid_qutb" VALUES(39,5,'39:5','اس نے آسمانوں اور زمین کو برحق پیدا کیا ہے۔ وہی دن پر رات اور رات پر دن کو لپیٹتا ہے۔ اسی نے سورج اور چاند کو اس طرح مسخر کر رکھا ہے کہ ہر ایک ایک وقت مقررہ تک چلے جارہا ہے۔ جان رکھو ، وہ زبردست ہے اور درگزر کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(39,6,'39:6','اسی نے تم کو ایک جان سے پیدا کیا ، پھر وہی ہے جس نے اس جان سے اس کا جوڑا بنایا۔ اور اسی نے تمہارے لیے مویشیوں میں سے آٹھ نرومادہ پیدا کیے۔ وہ تمہاری ماؤں کے پیٹوں میں تین تین تاریک پردوں کے اندر تمہیں ایک کے بعد ایک شکل دیتا چلا جاتا ہے۔ یہی اللہ (جس کے یہ کام ہیں) تمہارا رب ہے ، بادشاہی اسی کی ہے ، کوئی معبود اس کے سوا نہیں ہے ، پھر تم کدھر سے پھرائے جارہے ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(39,7,'39:7','اگر تم کفر کرو تو اللہ تم سے بےنیاز ہے ، لیکن وہ اپنے بندوں کے لیے کفر کو پسند نہیں کرتا ، اور اگر تم شکر کرو تو اسے وہ تمہارے لیے پسند کرتا ہے۔ کوئی بوجھ اٹھانے والا کسی دوسرے کو بوجھ نہ اٹھائے گا۔ آخر کار تم سب کو اپنے رب کی طرف پلٹنا ہے ، پھر وہ تمہیں بتادے گا کہ تم کیا کرتے رہے ہو ، وہ تو دلوں کا حال جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(39,8,'39:8','انسان پر جب کوئی آفت آتی ہے تو وہ اپنے رب کی طرف رجوع کرکے اسے پکارتا ہے۔ پھر جب اس کا رب اسے اپنی نعمت سے نواز دیتا ہے تو وہ اس مصیبت کو بھول جاتا ہے جس پر وہ پہلے پکاررہا تھا اور دوسروں کو اللہ کا ہمسر ٹھہراتا ہے تاکہ اس کی راہ سے گمراہ کرے۔ (اے نبی صلى الله عليه وسلم اس سے کہو کہ تھوڑے دن اپنے کفر سے لطف اٹھالے ، یقینا تو دوزخ میں جانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(39,9,'39:9',': (کیا اس شخص کی روش بہتر ہے یا اس شخص کی) جو مطیع فرمان ہے ، رات کی گھڑیوں میں کھڑا رہتا اور سجدے کرتا ہے ، آخرت سے ڈرتا اور اپنے رب کی رحمت سے امید لگاتا ہے ؟ ان سے پوچھو کیا جاننے والے اور نہ جاننے والے دونوں کبھی یکساں ہوسکتے ہیں ؟ نصیحت تو عقل رکھنے والے ہی قبول کرتے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(39,10,'39:10',': (اے نبی صلى الله عليه وسلم کہو کہ اے میرے بندو جو ایمان لائے ہو ، اپنے رب سے ڈرو۔ جن لوگوں نے اس دنیا میں نیک رویہ اختیار کیا ہے ان کے لیے بھلائی ہے اور خدا کی زمین وسیع ہے ، صبر کرنے والوں کو تو ان کا اجر بےحساب دیا جائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(39,11,'39:11','(اے نبی صلى الله عليه وسلم ان سے کہو ، مجھے حکم دیا گیا ہے کہ دین کو اللہ کے لیے خالص کرکے اس کی بندگی کروں ، ا');
INSERT INTO "sayyid_qutb" VALUES(39,12,'39:12','ور مجھے حکم دیا گیا ہے کہ سب سے پہلے میں خود مسلم بنوں۔');
INSERT INTO "sayyid_qutb" VALUES(39,13,'39:13','کہو ، اگر میں اپنے رب کی نافرمانی کروں تو مجھے ایک بڑے دن کے عذاب کا خوف ہے۔');
INSERT INTO "sayyid_qutb" VALUES(39,14,'39:14','کہہ دو کہ میں تو اپنے دین کو اللہ کے لیے خالص کرکے اسی کی بندگی کروں گا ، خوب سن رکھو '' یہی کھلا دیوالیہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(39,15,'39:15','تم اس کے سوا جس جس کی بندگی کرنا چاہو کرتے رہو۔ کہو '' اصل دیوالیے تو وہی ہیں جنہوں نے قیامت کے روز اپنے آپ کو اور اپنے اہل عیال کو گھاٹے میں ڈال دیا۔');
INSERT INTO "sayyid_qutb" VALUES(39,16,'39:16','ان پر اگ کی چھتریاں اوپر سے بھی چھائی ہوں گی اور نیچے سے بھی۔ یہ وہ انجام ہے جس سے اللہ اپنے بندوں کو ڈراتا ہے '' پس اے میرے بندو '' میرے غضب سے بچو۔');
INSERT INTO "sayyid_qutb" VALUES(39,17,'39:17','بخلاف اس کے جن لوگوں نے طاغوت کی بندگی سے اجتناب کیا اور اللہ کی طرف رجوع کرلیا ان کے لیے خوشخبری ہے۔ پس (اے نبی صلى الله عليه وسلم بشارت دے دو');
INSERT INTO "sayyid_qutb" VALUES(39,18,'39:18','میرے ان بندوں کو جو بات کو غور سے سنتے ہیں اور اس کے بہترین پہلو کی پیروی کرتے ہیں۔ یہ وہ لوگ ہیں جن کو اللہ نے ہدایت بخشی ہے اور یہی دانش مند ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(39,19,'39:19','(اے نبی صلى الله عليه وسلم اس شخص کو کون بچاسکتا ہے جس پر عذاب کا فیصلہ چسپاں ہوچکا ہو ؟ تم اسے بچاسکتے ہو جو اگ میں گرچکا ہو');
INSERT INTO "sayyid_qutb" VALUES(39,20,'39:20','البتہ جو لوگ اپنے رب سے ڈرکر رہے ان کے لیے بلند عمارتیں ہیں منزل پر منزل نبی ہوئی ، جن کے نیچے نہریں بہ رہی ہوں گی۔ یہ اللہ کا وعدہ ہے ، اللہ کبھی اپنے وعدے کی خلاف ورزی نہیں کرتا “۔');
INSERT INTO "sayyid_qutb" VALUES(39,21,'39:21',':” کیا تم نہیں دیکھتے کہ اللہ نے آسمان سے پانی برسایا ، پھر اس کو سوتوں اور چشموں اور دریاؤں کی شکل میں زمین کے اندر جاری کیا ، پھر اس پانی کے ذریعہ سے وہ طرح طرح کی کھیتیاں نکالتا ہے جن کی قسمیں مختلف ہیں ، پھر وہ کھیتیاں پک کر سوکھ جاتی ہیں ، پھر تم دیکھتے ہو کہ وہ زرد پڑگئیں ، پھر آخر کار اللہ ان کو بھس بنا دیتا ہے۔ درحقیقت اس میں ایک سبق ہے عقل رکھنے والوں کیلیے “۔');
INSERT INTO "sayyid_qutb" VALUES(39,22,'39:22','اب کیا وہ شخص جس کا سینہ اللہ نے اسلام کے لیے کھول دیا اور وہ اپنے رب کی طرف سے ایک روشنی پر چل رہا ہے (اس شخص کی طرح ہوسکتا ہے جس نے ان باتوں سے کوئی سبق نہ لیا ؟ ) ۔ تباہی ہے ان لوگوں کے لیے جن کے دل اللہ کی نصیحت سے اور زیادہ سخت ہوگئے۔ وہ کھلی گمراہی میں پڑے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(39,23,'39:23',':” اللہ نے بہترین کلام اتارا ہے ، ایک ایسی کتاب جس کے تمام اجزاء ہم رنگ ہیں اور جس میں باربار مضامین دہرائے گئے ہیں۔ اسے سن کر ان لوگوں کے رونگٹے کھڑے ہوجاتے ہیں جو اپنے رب سے ڈرنے والے ہیں ، اور پھر ان کے جسم اور ان کے دل نرم ہوکر اللہ کے ذکر کی طرف راغب ہوجاتے ہیں۔ یہ اللہ کی ہدایت ہے جس سے وہ راہ راست پر لے آتا ہے جسے چاہتا ہے اور جسے اللہ ہی ہدایت نہ دے اس کے لیے پھر کوئی ہادی نہیں ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(39,24,'39:24','اب اس شخص کی بدحالی کا تم کیا اندازہ کرسکتے ہو جو قیامت کے روز عذاب کی سخت مار اپنے منہ پر لے گا ؟ ایسے ظالموں سے تو کہہ دیا جائے گا کہ اب چکھو مزہ ، اس کمائی کا جو تم کرتے رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(39,25,'39:25','ان سے پہلے بھی بہت سے لوگ اسی طرح جھٹلاچکے ہیں۔ آخر ان پر عذاب ایسے رخ سے آیا جدھر ان کو خیال بھی نہ جاسکتا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(39,26,'39:26','پھر اللہ نے ان کو دنیا ہی کی زندگی میں رسوائی کا مزہ چکھایا ، اور آخرت کا عذاب تو اس سے شدید تر ہے ، کاش یہ لوگ جانتے۔');
INSERT INTO "sayyid_qutb" VALUES(39,27,'39:27','ہم نے اس قرآن میں لوگوں کو طرح طرح کی مثالیں دی ہیں کہ یہ ہوش میں آئیں ۔');
INSERT INTO "sayyid_qutb" VALUES(39,28,'39:28','ایسا قرآن جو عربی زبان میں ہے ، جس میں کوئی ٹیڑھ نہیں ہے ، تاکہ یہ برے انجام سے بچیں۔');
INSERT INTO "sayyid_qutb" VALUES(39,29,'39:29','اللہ ایک مثال دیتا ہے۔ ایک شخص تو وہ ہے جس کے مالک ہونے میں بہت سے کج خلق آقا شریک ہیں جو اسے اپنی اپنی طرف کھینچتے ہیں اور دوسرا شخص پورا کا پورا ایک ہی آقا کا غلام ہے۔ کیا ان دونوں کا حال یکساں ہوسکتا ہے ؟ الحمد للہ ، مگر اکثر لوگ نادانی میں پڑے ہوئے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(39,30,'39:30','(اے نبی صلى الله عليه وسلم تمہیں بھی مرنا ہے اور ان لوگوں کو بھی مرنا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(39,31,'39:31','آخر کار قیامت کے روز تم سب اپنے رب کے حضور اپنا اپنا مقدمہ پیش کروگے “۔');
INSERT INTO "sayyid_qutb" VALUES(39,32,'39:32','” پھر اس شخص سے بڑا ظالم کون ہوگا جس نے اللہ پر جھوٹ باندھا اور جب سچائی اس کے سامنے آئی تو اسے جھٹلادیا۔ کیا ایسے لوگوں کے لیے جہنم میں کوئی ٹھکانا نہیں ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(39,33,'39:33',':” اور جو شخص سچائی لے کر آیا اور جنہوں نے اس کو سچ مانا ، وہی عذاب سے بچنے والے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(39,34,'39:34',''' انہیں اپنے رب کے ہاں وہ سب کچھ ملے گا جس کی وہ خواہش کریں گے۔ یہ ہے نیکی کرنے والوں کی جزا');
INSERT INTO "sayyid_qutb" VALUES(39,35,'39:35','تاکہ جو بدترین اعمال انہوں نے کیے تھے انہیں اللہ ان کے حساب سے ساقط کردے اور جو بہترین اعمال وہ کرتے رہے ان کے لحاظ سے ان کو اجر عطافرمائے');
INSERT INTO "sayyid_qutb" VALUES(39,36,'39:36','ا :”(اے نبی صلى الله عليه وسلم کیا اللہ اپنے بندے کے لیے کافی نہیں ہے ؟ یہ لوگ اس کے سوا دوسروں سے تم کو ڈراتے ہیں۔ حالانکہ اللہ جسے گمراہی میں ڈال دے اسے کوئی راستہ دکھانے والا نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(39,37,'39:37','اور جسے وہ ہدایت دے ، اسے بھٹکانے والا بھی کوئی نہیں ۔ کیا اللہ زبردست اور انتقام لینے والا نہیں ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(39,38,'39:38','ان لوگوں سے اگر تم پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا ہے ، تو یہ خود کہیں گے کہ اللہ نے۔ ان سے پوچھو ، جب حقیقت یہ ہے تو تمہارا کیا خیال ہے کہ اگر اللہ مجھے کوئی نقصان پہنچانا چاہے تو کیا تمہاری یہ دیویاں ، جنہیں تم اللہ کو چھوڑ کر پکارتے ہو ، مجھے اس کے پہنچائے ہوئے نقصان سے بچالیں گی ؟ یا اللہ مجھ پر مہربانی کرنا چاہے تو کیا یہ اس کی رحمت کو روک سکیں گی ؟ بس ان سے کہہ دو کہ میرے لیے اللہ ہی کافی ہے ، پھر وسہ کرنے والے اس پر پھر وسہ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,39,'39:39','ان سے صاف کہو کہ ” اے میری قوم کے لوگو ، تم اپنی جگہ اپنا کام کیے جاؤ، میں اپنا کام کرتا رہوں گا ، عنقریب تمہیں معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(39,40,'39:40','کہ کس پر رسواکن عذاب آتا ہے اور کسے وہ سزا ملتی ہے جو کبھی ٹلنے والی نہیں');
INSERT INTO "sayyid_qutb" VALUES(39,41,'39:41',': (اے نبی صلى الله عليه وسلم ہم نے سب انسانوں کے لیے یہ کتاب برحق تم پر نازل کردی ہے۔ اب جو سیدھا راستہ اختیار کرے گا اپنے لیے کرے گا) اور جو بھٹکے گا اس کے بھٹکنے کا وبال اسی پر ہوگا ، تم ان کے ذمہ دار نہیں ہو');
INSERT INTO "sayyid_qutb" VALUES(39,42,'39:42','وہ اللہ ہی ہے جو موت کے وقت روحیں قبض کرتا ہے اور جو ابھی نہیں مرا ہے اس کی روح نیند میں قبض کرلیتا ہے ، پھر جس پر وہ موت کا فیصلہ نافذ کرتا ہے ، اسے روک لیتا ہے اور دوسروں کی روحیں ایک وقت مقرر کے لیے واپس بھیج دیتا ہے۔ اس میں بڑی نشانیاں ہیں ان لوگوں کے لیے جو غور وفکر کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,43,'39:43','کیا اس خدا کو چھوڑ کر ان لوگوں نے دوسروں کو شفیع بنارکھا ہے ؟ ان سے کہو ، کیا وہ شفاعت کریں گے خواہ ان کے اختیار میں کچھ ہو نہ ہو اور وہ سمجھتے بھی نہ ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(39,44,'39:44','۔ کہو ، شفاعت ساری اللہ کے اختیار میں ہے۔ آسمانوں اور زمین کی بادشاہی کا وہی مالک ہے۔ پھر اسی کی طرف تم پلٹائے جانے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(39,45,'39:45','جب اکیلے اللہ کا ذکر کیا جاتا ہے تو آخرت پر ایمان نہ رکھنے والوں کے دل کڑھنے لگتے ہیں۔ اور جب اس کے سوا دوسروں کا ذکر ہوتا ہے تو یکایک وہ خوشی سے کھل اٹھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,46,'39:46','کہو ، خدایا ، آسمانوں اور زمین کے پیدا کرنے والے ، حاضر و غائب کے جاننے والے ، تو ہی اپنے بندوں کے درمیان اس چیز کا فیصلہ کرے گا ، جس میں وہ اختلاف کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,47,'39:47','اگر ان ظالموں کے پاس زمین کی ساری دولت بھی ہو ، اور اتنی ہی اور بھی ، تو یہ روز قیامت کے برے عذاب سے بچنے کیلیے فدیے میں دینے کیلیے تیار ہوجائیں گے۔ وہاں اللہ کی طرف سے انکے سامنے وہ کچھ آئے گا جسکا انہوں نے کبھی اندازہ ہی نہیں کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(39,48,'39:48','۔ وہاں اپنی کمائی کے سارے برے نتائج ان پر کھل جائیں گے اور وہی چیز ان پر مسلط ہوجائیگی جسکا یہ مذاق اڑاتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,49,'39:49','یہی انسان جب ذرا سی مصیبت اسے چھو جاتی ہے تو ہمیں پکارتا ہے ، اور جب ہم اسے اپنی طرف سے نعمت دے کر اپھار دیتے ہیں تو کہتا ہے کہ یہ تو مجھے علم کی بنا پر دیا گیا ہے ، نہیں بلکہ یہ آزمائش ہے ، مگر ان میں سے اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,50,'39:50','یہی بات ان سے پہلے گزرے ہوئے لوگ بھی کہہ چکے ہیں ، مگر جو کچھ وہ کماتے تھے وہ ان کے کسی کام نہ آیا');
INSERT INTO "sayyid_qutb" VALUES(39,51,'39:51','۔ پھر اپنی کمائی کے برے نتائج انہوں نے بھگتے ، اور ان میں سے بھی وہ جو ظالم ہیں وہ عنقریب اپنی کمائی کے برے نتائج بھگتیں گے ، یہ ہمیں عاجز کردینے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,52,'39:52','اور کیا انہیں معلوم نہیں ہے کہ اللہ جس کا چاہتا ہے ، رزق کشادہ کردیتا ہے اور جس کا چاہتا ہے ، تنگ کردیتا ہے ؟ اس میں نشانیاں ہیں ان لوگوں کے لیے جو ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,53,'39:53',':”(اے نبی صلى الله عليه وسلم کہہ دو کہ اے میرے بندو ، جنہوں نے اپنی جانوں پر زیادتی کی ہے ، اللہ کی رحمت سے مایوس نہ ہونا ، یقیناً اللہ سارے گناہ معاف کردیتا ہے ، وہ تو غفور ورحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(39,54,'39:54','پلٹ آؤ اپنے رب کی طرف اور مطیع بن جاؤ اسکے قبل اسکے کہ تم پر عذاب آجائے اور پھر کہیں تمہیں مدد نہ مل سکے');
INSERT INTO "sayyid_qutb" VALUES(39,55,'39:55','۔ اور پیروی اختیار کرلو اپنے رب کی بھیجی ہوئی کتاب کے بہترین پہلو کی۔ قبل اسکے کہ تم پر اچانک عذاب آجائے اور تمکو خبر بھی نہ ہو');
INSERT INTO "sayyid_qutb" VALUES(39,56,'39:56','کہیں ایسا نہ ہو کہ بعد میں کوئی شخص کہے ” افسوس میری اس تقصیر پر جو میں اللہ کی جناب میں کرتا رہا ، بلکہ میں تو الٹا مذاق اڑانے والوں میں شامل تھا');
INSERT INTO "sayyid_qutb" VALUES(39,57,'39:57','یا کہے ” کاش اللہ نے مجھے ہدایت بخشی ہوتی تو میں بھی متقیوں میں سے ہوتا');
INSERT INTO "sayyid_qutb" VALUES(39,58,'39:58','یا عذاب دیکھ کر کہے ” کاش مجھے ایک موقع اور مل جائے اور میں بھی نیک عمل کرنے والوں میں شامل ہوجاؤں');
INSERT INTO "sayyid_qutb" VALUES(39,59,'39:59',':”(اور اس وقت اسے یہ جواب ملے کہ) ” کیوں نہیں '' میری آیات تیرے پاس آچکی تھیں '' پھر تو نے انہیں جھٹلایا اور تکبر کیا اور تو کافروں میں سے تھا');
INSERT INTO "sayyid_qutb" VALUES(39,60,'39:60','آج جن لوگوں نے خدا پر جھوٹ باندھے ہیں قیامت کے روز تم دیکھو گے کہ ان کے منہ کالے ہوں گے۔ کیا جہنم میں متکبروں کے لیے کافی جگہ نہیں ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(39,61,'39:61','اس کے برعکس جن لوگوں نے یہاں تقویٰ کیا ہے ، ان کے اسباب کامیابی کی وجہ سے اللہ ان کو نجات دے گا ، ان کو نہ کوئی گزند پہنچے گا اور نہ وہ غمگین ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(39,62,'39:62','اللہ ہر چیز کا خالق ہے اور وہی ہر چیز پر نگہبان ہے');
INSERT INTO "sayyid_qutb" VALUES(39,63,'39:63','زمین اور آسمانوں کے خزانوں کی کنجیاں اسی کے پاس ہیں ، اور جو لوگ اللہ کی آیات سے کفر کرتے ہیں ۔ وہی گھاٹے میں رہنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,64,'39:64',':”(اے نبی صلى الله عليه وسلم ان سے کہو '' پھر کیا اے جاہلو '' تم اللہ کے سوا کسی اور کی بندگی کرنے کے لیے مجھ سے کہتے ہو');
INSERT INTO "sayyid_qutb" VALUES(39,65,'39:65',':”(یہ بات تمہیں ان سے صاف کہہ دینی چاہئے کیونکہ) تمہاری طرف اور تم سے پہلے گزرے ہوئے تمام انبیاء کی طرف یہ وحی بھیجی جاچکی ہے کہ اگر تم نے شرک کیا تو تمہارا عمل ضائع ہوجائے گا اور تم خسارے میں رہو گے');
INSERT INTO "sayyid_qutb" VALUES(39,66,'39:66','لہذا (اے نبی صلى الله عليه وسلم تم بس اللہ ہی کی بندگی کرو اور شکر گزار بندوں میں سے ہوجاؤ');
INSERT INTO "sayyid_qutb" VALUES(39,67,'39:67','ان لوگوں نے اللہ کی قدرہی نہ کی جیسا کہ اس کی قدر کرنے کا حق ہے اس کی قدرت کاملہ کا حال تو یہ ہے کہ قیامت کے روز پوری زمین اس کی مٹھی میں ہوگی ، اور آسمان اس کے دست راست میں لپٹے ہوئے ہوں گے۔ پاک اور بالاتر ہے وہ اس شرک سے جو یہ لوگ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(39,68,'39:68','اور اس روز صور پھونکا جائے گا اور وہ سب مرکرگر جائیں گے جو آسمانوں اور زمین میں ہیں ، سوائے ان کے جنہیں اللہ زندہ رکھنا چاہے۔ پھر ایک دوسرا صور پھونکا جائے گا اور یکایک سب کے سب اٹھ کر دیکھنے لگیں گے');
INSERT INTO "sayyid_qutb" VALUES(39,69,'39:69','زمین اپنے رب کے نور سے چمک اٹھے گی کتاب اعمال لاکر رکھ دی جائے گی '' انبیاء اور تمام گواہ حاضر کردیئے جائیں گے لوگوں کے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ کردیا جائے گا ، ان پر کوئی ظلم نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(39,70,'39:70','اور ہر متنفس کو جو کچھ بھی اسنے عمل کیا تھا ، اس کا پورا پورا بدلہ دے دیا جائے گا۔ لوگ جو کچھ بھی کرتے ہیں ، اللہ اس کو خوب جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(39,71,'39:71','(اس فیصلہ کے بعد) وہ لوگ جنہوں نے کفر کیا تھا ، جہنم کی طرف گروہ در گروہ ہانکے جائیں گے ، یہاں تک کہ جب وہ وہاں پہنچیں گے ، تو اس کے دروازے کھولے جائیں گے اور اس کے کارندے ان سے کہیں گے ” کیا تمہارے پاس تمہارے اپنے لوگوں میں سے ایسے رسول آئے تھے جنہوں نے تم کو تمہارے رب کی آیات سنائی ہوں اور تمہیں اس بات سے ڈرایا ہو کہ ایک وقت تمہیں یہ دن بھی دیکھنا ہوگا ؟ “ وہ جواب دیں گے۔” ہاں '' آئے تھے '' مگر عذاب کا فیصلہ کافروں پر چپک گیا');
INSERT INTO "sayyid_qutb" VALUES(39,72,'39:72','کہا جائے گا ، داخل ہوجاؤ، جہنم کے دروازوں میں ، یہاں اب تمہیں ہمیشہ رہنا ہے ، بڑا ہی برا ٹھکانہ ہے یہ متکبروں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(39,73,'39:73','اور جو لوگ اپنے رب کی نافرمانی سے پرہیز کرتے تھے انہیں گروہ درگروہ جنت کی طرف لے جایاجائے گا۔ یہاں تک کہ جب وہ وہاں پہنچیں گے ، اور اس کے دروازے پہلے ہی کھولے جاچکے ہوں گے ، تو اس کے منتظمین ان سے کہیں گے کہ ” سلام ہو تم پر '' بہت اچھے رہے '' داخل ہوجاؤ اس میں ہمیشہ کے لیے');
INSERT INTO "sayyid_qutb" VALUES(39,74,'39:74','اور وہ کہیں گے ” شکر ہے اس خدا کا جس نے ہمارے ساتھ اپنا وعدہ سچ کر دکھایا اور ہم کو زمین کا وارث بنادیا ، اب ہم جنت میں جہاں چاہیں اپنی جگہ بناسکتے ہیں پس بہترین اجر ہے عمل کرنے والوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(39,75,'39:75','اور تم دیکھو گے کہ فرشتے عرش کے گرد حلقہ بنائے ہوئے اپنے رب کی حمد وتسبیح کررہے ہوں گے اور لوگون کے درمیان ٹھیک ٹھیک حق کے ساتھ فیصلہ چکا دیا جائے گا ، اور پکاردیا جائے گا کہ حمد ہے ” اللہ رب العالمین کے لیے');
INSERT INTO "sayyid_qutb" VALUES(40,1,'40:1','ح '' م');
INSERT INTO "sayyid_qutb" VALUES(40,2,'40:2','اس کتاب کا نزول اللہ کی طرف سے ہے جو زبردست ہے');
INSERT INTO "sayyid_qutb" VALUES(40,3,'40:3',''' گناہ معاف کرنے والا اور توبہ قبول کرنے والا ہے ، سخت سزا دینے والا اور بڑا صاحب فضل ہے ۔ کوئی معبود اس کے سوا نہیں ، اسی کی طرف سب کو پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,4,'40:4','اللہ کی آیات میں جھگڑے نہیں کرتے مگر صرف وہ لوگ جنہوں نے کفر کیا ہے۔ اس کے بعد دنیا کے ملکوں میں ان کی چلت پھرت تمہیں کسی دھوکے میں نہ ڈالے');
INSERT INTO "sayyid_qutb" VALUES(40,5,'40:5','ان سے پہلے نوح کی قوم بھی جھٹلاچ کی ہے ، اور اس کے بعد بہت سے دوسرے جتھوں نے بھی یہ کام کیا ہے ، ہر قوم اپنے رسول پر جھپٹی تاکہ اسے گرفتار کرے۔ ان سب نے باطل کے ہتھیاروں سے حق کو نیچا دکھانے کی کوشش کی ، مگر آخر کار میں ان کو پکڑلیا ، پھر دیکھ لو کہ میری سزا کیسی سخت تھی');
INSERT INTO "sayyid_qutb" VALUES(40,6,'40:6','اسی طرح تیرے رب کا یہ فیصلہ بھی ان سب لوگوں پر چسپاں ہوچکا ہے ، جو کفر کے مرتکب ہوئے ہیں کہ وہ واصل بجہنم ہونے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(40,7,'40:7','عرش الہٰی کے حامل فرشتے ، اور وہ جو عرش کے گرد وپیش حاضر رہتے ہیں ، سب اپنے رحمت کی حمد کے ساتھ اس کی تسبیح کررہے ہیں۔ وہ اس پر ایمان رکھتے ہیں اور ایمان لانے والوں کے حق میں دعائے مغفرت کرتے ہیں وہ کہتے ہیں :” اے ہمارے رب ، تو اپنی رحمت اور اپنے علم کے ساتھ ہر چیز پر چھایا ہوا ہے ، پس معاف کردے اور عذاب دوزخ سے بچالے ، ان لوگوں کو جنہوں نے توبہ کی ہے اور تیرا راستہ اختیار کرلیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(40,8,'40:8','اے ہمارے رب ، اور داخل کر ان کو ہمیشہ رہنے والی ان جنتوں میں جن کا تو نے ان سے وعدہ کیا ہے ، اور ان کے والدین اور بیویوں اور اولاد میں سے صالح ہوں (ان کو بھی وہاں ان کے ساتھ ہی پہنچادے) ۔ تو بلاشبہ قادر مطلق اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(40,9,'40:9','اور بچادے ان کو برائیوں سے ، جس کو تو نے قیامت کے دن برائیوں سے بچا دیا اس پر تو نے بڑا رحم کیا ، یہی بڑی کامیابی ہے');
INSERT INTO "sayyid_qutb" VALUES(40,10,'40:10','جن لوگوں نے کفر کیا ہے ، قیامت کے روز ان کو پکار کر کہا جائے گا ،” آج تمہیں جتنا شدید غصہ اپنے اوپر آرہا ہے ، اللہ تم پر اس سے زیادہ غضبناک اس وقت ہوتا تھا جب تمہیں ایمان کی طرف بلایا جاتا تھا اور تم کفر کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(40,11,'40:11','وہ کہیں گے ” اے ہمارے رب ، تونے واقعی ہمیں دو دفعہ موت اور دو دفعہ زندگی دے دی ، اب ہم اپنے قصوروں کا اعتراف کرتے ہیں ، کیا اب یہاں سے نکلنے کی بھی کوئی سبیل ہے');
INSERT INTO "sayyid_qutb" VALUES(40,12,'40:12','“۔ ؟ “ (جواب ملے گا) ” یہ حالت جس میں تم مبتلا ہو ، اس وجہ سے ہے کہ جب اکیلے اللہ کی طرف بلایا جاتا تھا تو تم ماننے سے انکار کردیتے تھے اور جب اسکے ساتھ دوسروں کو ملایا جاتا تو تم مان لیتے تھے۔ اب فیصلہ اللہ بزرگ و برتر کے ہاتھ ہے');
INSERT INTO "sayyid_qutb" VALUES(40,13,'40:13','وہی ہے جو تم کو اپنی نشانیاں دکھاتا ہے اور آسمان سے تمہارے لیے رزق نازل کرتا ہے مگر (ان نشانیوں کے مشاہدے سے) سبق صرف وہی شخص لیتا ہے جو اللہ کی طرف رجوع کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(40,14,'40:14','(پس اے رجوع کرنے والو) اللہ ہی کو پکارو اپنے دین کو اس کے لیے خالص کرکے ، خواہ تمہارا یہ فعل کافروں کو کتنا ہی باگوار ہو');
INSERT INTO "sayyid_qutb" VALUES(40,15,'40:15','وہ بلند دوجوں والا ، مالک عرش ہے۔ اپنے بندوں میں سے جس پر چاہتا ہے ، اپنے حکم سے روح نازل کردیتا ہے تاکہ وہ ملاقات کے دن سے خبردار کردے');
INSERT INTO "sayyid_qutb" VALUES(40,16,'40:16','وہ دن جب کہ سب لوگ بےپردہ ہوں گے ، اللہ سے ان کی کوئی بات چھپی ہوئی نہ ہوگی۔ (اس روز پکار کر پوچھا جائے گا) آج بادشاہی کس کی ہے ؟ (ساراعالم پکار اٹھے گا) اللہ واحد قہار کی');
INSERT INTO "sayyid_qutb" VALUES(40,17,'40:17','۔ (کہا جائے گا) آج ہر متنفس کو اس کمائی بدلہ دیا جائے گا۔ جو اس نے کی تھی ، آج کسی پر کوئی ظلم نہ ہوگا اور اللہ حساب لینے میں بہت تیز ہے');
INSERT INTO "sayyid_qutb" VALUES(40,18,'40:18','اے نبی صلى الله عليه وسلم ڈراؤ ان لوگوں کو اس دن سے جو قریب آلگا ہے ، جب کلیجے منہ کو آرہے ہوں گے اور لوگ چپ چاپ غم کے گھونٹ پیئے کھڑے ہوں گے۔ ظالموں کا نہ کوئی مشفق دوست ہوگا اور نہ کوئی سفیع جس کی بات مانی جائے');
INSERT INTO "sayyid_qutb" VALUES(40,19,'40:19','اللہ نگاہوں کی چوری تک سے واقف ہے اور وہ راز تک جانتا ہے جو سینوں نے چھپارکھے ہیں');
INSERT INTO "sayyid_qutb" VALUES(40,20,'40:20','اور اللہ ٹھیک ٹھیک بےلاگ فیصلہ کرے گا۔ رہے وہ جن کو (یہ مشرکین) اللہ کو چھوڑ کر پکارتے ہیں ، وہ کسی چیز کا بھی فیصلہ کرنیوالے نہیں ہیں۔ بلاشبہ اللہ ہی سب کچھ سننے اور دیکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,21,'40:21','کیا یہ لوگ کبھی زمین میں چلے پھرے نہیں ہیں کہ انہیں ان لوگوں کا انجام نظر آتا جو ان سے پہلے گزرچکے ہیں ؟ وہ ان سے زیادہ طاقتور تھے اور ان سے زیادہ زبردست آثار زمین میں چھوڑگئے ہیں مگر اللہ نے ان کے گناہوں پر انہیں پکڑلیا اور ان کو اللہ سے بچانے والا کوئی نہ تھا');
INSERT INTO "sayyid_qutb" VALUES(40,22,'40:22','یہ ان کا انجام اس لیے ہوا کہ ان کے پاس ان کے رسوک بینات لے کر آئے اور انہوں نے ماننے سے انکار کردیا۔ آخر کار اللہ نے ان کو پکڑلیا یقیناً وہ بڑی قوت والا اور سزا دینے میں بہت سخت ہے');
INSERT INTO "sayyid_qutb" VALUES(40,23,'40:23','ہم نے موسیٰ کو اپنی نشانیاں اور نمایاں سند ماموریت کے ساتھ بھیجا ،');
INSERT INTO "sayyid_qutb" VALUES(40,24,'40:24','فرعون اور ہامان اور قارون کی طرف مگر انہوں نے کہا ” ساحر ہے ، کذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(40,25,'40:25','پھر جب وہ ہماری طرف سے حق ان کے سامنے لے آیا تو انہوں نے کہا ” جو لوگ ایمان لاکر اس کے ساتھ شامل ہوئے ہیں '' ان سب کے لڑکوں کو قتل کرو اور لڑکیوں کو جیتا چھوڑ دو “۔ مگر کافروں کی چال اکارت ہی گئی');
INSERT INTO "sayyid_qutb" VALUES(40,26,'40:26','ایک روز فرعون نے اپنے دربایوں سے کہا ” چھوڑو مجھے ، میں اس موسیٰ کو قتل کیے دیتا ہوں ، اور پکار دیکھے یہ اپنے رب کو۔ مجھے اندیشہ ہے کہ یہ تمہارا دین بدل ڈالے گا ، یا ملک میں فساد برپاکرے گا');
INSERT INTO "sayyid_qutb" VALUES(40,27,'40:27','موسیٰ نے کہا ” میں نے تو ہر اس متکبر کے مقابلے میں جو یوم الحساب پر ایمان نہیں رکھتا ، اپنے رب اور تمہارے رب کی پناہ لے لی ہے');
INSERT INTO "sayyid_qutb" VALUES(40,28,'40:28','اس موقع پر آل فرعون میں سے ایک مومن شخص ، جو اپنا ایمان چھپائے ہوئے تھا ، بول اٹھا :” کیا تم ایک شخص کو صرف اس بنا پر قتل کردوگے کہ وہ کہتا ہے میرا رب اللہ ہے ؟ حالانکہ وہ تمہارے رب کی طرف سے تمہارے پاس بینات لے آیا۔ اگر وہ جھوٹا ہے تو اس کا جھوٹ خود اسی پر پلٹ پڑے گا۔ لیکن اگر وہ سچا ہے تو جن ہولناک نتائج کا وہ تم کو خوف دلاتا ہے ان میں سے کچھ تو تم پر ضروری آجائیں گے۔ اللہ کسی ایسے شخص کو ہدایت نہیں دیتا جو حد سے گزر جانے والا اور کذاب ہو');
INSERT INTO "sayyid_qutb" VALUES(40,29,'40:29','اے میری قوم کے لوگو ، آج تمہیں بادشاہی حاصل ہے اور زمین میں تم غالب ہو ، لیکن اگر خدا کا عذاب ہم پر آگیا تو پھر کون ہے جو ہماری مدد کرسکے گا “۔ فرعون نے کہا ” میں تو تم لوگوں کو وہی رائے دے رہا ہوں جو مجھے مناسب نظر آتی ہے اور میں اسی راستے کی طرف تمہاری رہنمائی کرتا ہوں جو ٹھیک ہے');
INSERT INTO "sayyid_qutb" VALUES(40,30,'40:30','وہ شخص جو ایمان لایا تھا اس نے کہا ” اے قوم کے لوگو ، مجھے خوف ہے کہ کہیں تم پر بھی دن نہ آجائے جو اس سے پہلے بہت سے جتھوں پر آچکا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,31,'40:31','جیسا دن قوم نوح اور عاد اور ثمود اور ان کے بعد قوموں پر آیا تھا۔ اور یہ حقیقت ہے کہ اللہ اپنے بندوں پر ظلم کا کوئی ارادہ نہیں رکھتا');
INSERT INTO "sayyid_qutb" VALUES(40,32,'40:32','اے قوم ! مجھے ڈر ہے کہ کہیں تم پر فریادوفغاں کا دن نہ آجائے');
INSERT INTO "sayyid_qutb" VALUES(40,33,'40:33','جب تم ایک دوسرے کو پکاروگے اور بھاگے بھاگے پھروگے ، مگر اس وقت اللہ سے بچانے والا کوئی نہ ہوگا۔ سچ یہ ہے کہ جسے اللہ بھٹکادے اسے پھر کوئی راستہ دکھانے والا نہیں ہوتا');
INSERT INTO "sayyid_qutb" VALUES(40,34,'40:34','اس سے پہلے یوسف تمہارے پاس بینات لے کر آئے تھے مگر تم ان کی لائی ہوئی تعلیم کی طرف سے شک ہی میں پڑے رہے۔ پھر جب ان کا انتقال ہوگیا تو تم نے کہا اب ان کے بعد اللہ کوئی رسول ہرگز نہ بھیجے گا “۔ اسی طرح اللہ سب لوگوں کو گمراہی میں ڈال دیتا ہے جو حد سے گزرنے والے اور شکی ہوتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(40,35,'40:35','۔ اور اللہ کی آیات میں جھگڑے کرتے ہیں ، بغیر اس کے کہ ان کے پاس کوئی سند یا دلیل آئی ہو۔ یہ رویہ اللہ اور ایمان لانے والوں کے نزدیک سخت مبغوض ہے۔ اسی طرح ہر متکبروجبار کے دل پر ٹھپہ لگا دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,36,'40:36','فرعون نے کہا ” اے ہامان '' میرے لیے ایک بلند عمارت بناتا کہ میں راستوں تک پہنچ سکوں');
INSERT INTO "sayyid_qutb" VALUES(40,37,'40:37','آسمانوں کے راستوں تک '' اور موسیٰ کے خدا کو جھانک کردیکھوں۔ مجھے تو یہ موسیٰ جھوٹا ہی معلوم ہوتا ہے “۔۔۔۔ اسی طرح فرعون کے لیے اس کی بدعملی خوشنما بنادی گئی اور وہ راہ راست سے روک دیا گیا۔ فرعون کی ساری چال بازی (اس کی اپنی) تباہی کے راستہ ہی میں صرف ہوئی');
INSERT INTO "sayyid_qutb" VALUES(40,38,'40:38','وہ شخص جو ایمان لایا تھا ، بولا “ اے میری قوم کے لوگو ، میری بات مانو ، میں تمہیں صحیح راستہ بتاتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(40,39,'40:39','اے قوم ، یہ دنیا کی زندگی تو چند روزہ ہے ، ہمیشہ کے لیے قیام کی جگہ آخرت ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(40,40,'40:40','جو برائی کرے گا اس کو اتنا ہی بدلہ ملے گا جتنی اس نے برائی کی ہوگی۔ اور جو نیک عمل کرے گا ، خواہ وہ مرد ہو یا عورت ، بشرطیکہ ہو وہ مومن ، ایسے سب لوگ جنت میں داخل ہوں گے۔ جہاں ان کو بےحساب رزق دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(40,41,'40:41','اے قوم ، آخر یہ کیا ماجرا ہے ، کہ میں تو تم لوگوں کو نجات کی طرف بلاتا ہوں اور تم لوگ مجھے آگ کی طرف دعوت دیتے ہو !');
INSERT INTO "sayyid_qutb" VALUES(40,42,'40:42','تم مجھے اس بات کی دعوت دیتے ہو کہ میں اللہ سے کفر کروں اور اس کے ساتھ ان ہستیوں کو شریک ٹھہراؤں جنہیں میں نہیں جانتا ، حالانکہ کہ میں تمہیں اس زبردست مغفرت کرنے والے خدا کی طرف بلارہا ہوں');
INSERT INTO "sayyid_qutb" VALUES(40,43,'40:43','نہیں ، حق یہ ہے اور اس کے خلاف نہیں ہوسکتا کہ جن کی طرف تم مجھے بلا رہے ہو ، ، ان کی لیے نہ دنیا میں کوئی دعوت ہے ، اور ہم سب کو پلٹنا اللہ ہی کی طرف ہے ، اور حد سے گزرنے والے آگ میں جانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(40,44,'40:44','آج جو کچھ میں کہہ رہا ہوں ، عنقریب وہ وقت آئے گا ، جب تم اسے یاد کروگے۔ اور اپنا معاملہ میں اللہ کے سپرد کرتا ہوں ، وہ اپنے بندوں کا نگہبان ہے');
INSERT INTO "sayyid_qutb" VALUES(40,45,'40:45','آخر کار ان لوگوں نے جو بری سے بری چالیں اس مومن کے خلاف چلیں ، اللہ نے ان سب سے اس کو بچالیا ، اور فرعون کے ساتھ خود بدترین عذاب کے پھیر میں آگئے');
INSERT INTO "sayyid_qutb" VALUES(40,46,'40:46','۔ دوزخ کی آگ ہے جس کے سامنے صبح وشام وہ پیش کیے جاتے ہیں اور جب قیامت کی گھڑی آجائے گی تو حکم ہوگا کہ آل فرعون کو شدید تر عذاب میں داخل کرو');
INSERT INTO "sayyid_qutb" VALUES(40,47,'40:47','پھر ذراخیال کرو اس وقت کا جب یہ لوگ دوزخ میں ایک دوسرے سے جھگڑ رہے ہوں گے۔ دنیا میں جو لوگ کمزور تھے وہ بڑے بننے والوں سے کہیں گے کہ ” ہم تمہارے تابع تھے ، اب کیا یہاں تم نار جہنم کی تکلیف کے کچھ حصے سے ہم کو بچالوگے');
INSERT INTO "sayyid_qutb" VALUES(40,48,'40:48','وہ بڑے بننے والے جواب دیں گے ” ہم سب یہاں ایک حال میں ہیں ، اور اللہ بندوں کے درمیان فیصلہ کرچکا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,49,'40:49','پھر یہ دوزخ میں پڑے ہوئے لوگ جہنم کے اہل کاروں سے کہیں گے ” اپنے رب سے دعا کرو کہ ہمارے عذاب میں بس ایک دن کی تخفیف کردے');
INSERT INTO "sayyid_qutb" VALUES(40,50,'40:50','وہ پوچھیں گے ” کیا تمہارے پاس رسول بینات لے کر نہیں آتے رہے تھے ؟ “ وہ کہیں گے ” ہاں “۔ جہنم کے اہل کار بولیں گے :” پھر تو تم ہی دعا کرو ، اور کافروں کی دعا اکارت ہی جانے والی ہے');
INSERT INTO "sayyid_qutb" VALUES(40,51,'40:51','یقین جانو کہ ہم اپنے رسولوں اور ایمان لانے والوں کی مدد اس دنیا کی زندگی میں بھی لازما کرتے ہیں ، اور اس روز بھی کریں گے جب گواہ کھڑے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(40,52,'40:52','، کب ظالموں کو ان کی معذرت کچھ بھی فائدہ نہ دے گی اور ان پر لعنت پڑے گی اور بدترین ٹھکانہ ان کے حصے میں آئے گا');
INSERT INTO "sayyid_qutb" VALUES(40,53,'40:53','آخر دیکھ لو کہ موسیٰ کی ہم نے رہنمائی کی اور بنی اسرائیل کو اس کتاب کا وارث بنا دیا');
INSERT INTO "sayyid_qutb" VALUES(40,54,'40:54','۔ جو عقل و دانش رکھنے والوں کے لیے ہدایت ونصیحت تھی');
INSERT INTO "sayyid_qutb" VALUES(40,55,'40:55','۔ پس اے نبی ؐ صبر کرو ، اللہ کا وعدہ برحق ہے ، اپنے قصور کی معافی چاہو اور صبح وشام کی حمد کے ساتھ اس کی تسبیح کرتے رہو');
INSERT INTO "sayyid_qutb" VALUES(40,56,'40:56','حقیقت یہ ہے کہ جو لوگ کسی سند وحجت کے بغیر ، جو انکے پاس آئی ہو ، اللہ کی آیات میں جھگڑے رہے ہیں ، ان کے دلوں میں کبر بھرا ہوا ہے ، مگر وہ اس بڑائی کو پہنچنے والے نہیں ہیں جس کا وہ گھمنڈرکھتے ہیں۔ بس اللہ کی پناہ مانگ لو ، وہ سب کچھ دیکھتا اور سنتا ہے');
INSERT INTO "sayyid_qutb" VALUES(40,57,'40:57','آسمانوں اور زمین کا پیدا ا کرنا انسان کو پیدا کرنے کی بہ نسبت یقیناً زیادہ بڑا کام ہے ، مگر اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(40,58,'40:58','اور یہ نہیں ہوسکتا کہ اندھا اور بینا یکساں ہوجائیں اور ایماندار و صالح اور بدکار برابر ٹھہریں مگر تم لوگ کم ہی کچھ سمجھتے ہو۔ یقیناً قیامت کی گھڑی آنے والی ہے ، اسکے آنے میں کوئی شک نہیں ، مگر اکثر لوگ نہیں مانتے');
INSERT INTO "sayyid_qutb" VALUES(40,59,'40:59','یقیناً قیامت کی گھڑی آنے والی ہے ، اسکے آنے میں کوئی شک نہیں ، مگر اکثر لوگ نہیں مانتے');
INSERT INTO "sayyid_qutb" VALUES(40,60,'40:60','تمہارے رب کہتا ہے ” مجھے پکارو ، میں تمہاری دعائیں قبول کرونگا ، جو لوگ گھمنڈ میں آکر میری عبادت سے منہ موڑتے ہیں ، ضرور وہ ذلیل و خوار ہو کر جہنم میں داخل ہونگے');
INSERT INTO "sayyid_qutb" VALUES(40,61,'40:61','وہ اللہ ہی تو ہے جس نے تمہارے لیے رات بنائی تاکہ تم اس میں سکون حاصل کرو ، اور دن کو روشن کیا۔ حقیقت یہ ہے کہ اللہ لوگوں پر بڑا فضل فرمانے والا ہے۔ مگر اکثر لوگ شکرادا نہیں کرتے');
INSERT INTO "sayyid_qutb" VALUES(40,62,'40:62','وہی اللہ (جس نے تمہارے لیے یہ کچھ کیا ہے) تمہارا رب ہے ۔ ہر چیز کا خالق۔ اس کے سوا کوئی معبود نہیں۔ پھر تم کدھر سے بہکائے جارہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(40,63,'40:63','اسی طرح وہ سب لوگ بہکائے جاتے رہے ہیں جو اللہ کی آیات کا انکار کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(40,64,'40:64','وہ اللہ ہی تو ہے جس نے تمہارے لیے زمین کو جائے قرار بنایا اور اوپر آسمان کا گنبد بنادیا۔ جس نے تمہاری صورت بنائی اور بڑی ہی عمدہ بنائی۔ جس نے تمہیں پاکیزہ چیزوں کا رزق دیا۔ وہی اللہ (جس کے یہ کام ہیں) تمہارا رب ہے۔ بےحساب برکتوں والا ہے وہ کائنات کا رب ہے');
INSERT INTO "sayyid_qutb" VALUES(40,65,'40:65','وہی زندہ ہے اس کے سوا کوئی معبود نہیں۔ اسی کو تم پکارو اپنے دین کو اسی کے لیے خالص کرکے۔ ساری تعریف اللہ رب العالمین ہی کے لئے ہے');
INSERT INTO "sayyid_qutb" VALUES(40,66,'40:66','اے نبی صلى الله عليه وسلم ، ان لوگوں سے کہہ دو کہ مجھے تو ان ہستیوں کی عبادت سے منع کردیا گیا ہے۔ جنہیں تم اللہ کو چھوڑ کر پکارتے ہو۔ (میں یہ کام کیسے کرسکتا ہوں) جب کہ میرے پاس میرے رب کی طرف سے بینات آچکی ہیں۔ مجھے حکم دیا گیا ہے کہ میں رب العالمین کے آگے سر تسلیم خم کردوں');
INSERT INTO "sayyid_qutb" VALUES(40,67,'40:67','وہی تو ہے جس نے تم کو مٹی سے پیدا کیا ، پھر نطفے سے ، پھر خون کے لوتھڑے سے ، پھر وہ تمہیں بچے کی شکل میں نکالتا ہے ، پھر تمہیں بڑھاتا ہے تاکہ تم اپنی طاقت کو پہنچ جاؤ، پھر اور بڑھاتا ہے تاکہ تم بڑھاپے کو پہنچو اور تم میں سے کوئی پہلے ہی واپس بلالیا جاتا ہے۔ یہ سب کچھ اسلیے کیا جاتا ہے تاکہ تم اپنے مقررہ وقت تک پہنچ جاؤ اور اس لیے کہ تم حقیقت کو سمجھو');
INSERT INTO "sayyid_qutb" VALUES(40,68,'40:68','وہی ہے زندگی دینے والا ، اور وہی موت دینے والا ہے۔ وہ جس بات کا بھی فیصلہ کرتا ہے ، بس ایک حکم دیتا ہے کہ وہ ہوجائے اور وہ ہوجاتی ہے');
INSERT INTO "sayyid_qutb" VALUES(40,69,'40:69','تم نے دیکھا ان لوگوں کو جو اللہ کی آیات میں جھگڑے کرتے ہیں ، کہاں سے وہ پھرائے جارہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(40,70,'40:70','یہ لوگ جو اس کتاب کو اور ان ساری کتابوں کو جھٹلاتے ہیں جو ہم نے اپنے رسولوں کے ساتھ بھیجی تھیں ، عنقریب انہیں معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(40,71,'40:71','جب طوق ان کی گردنوں میں ہوں گے اور زنجیریں کھنچی جائیں گی ،');
INSERT INTO "sayyid_qutb" VALUES(40,72,'40:72','کھولتے ہوئے پانی میں اور دوزخ کی آگ میں جھونک دیئے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(40,73,'40:73','پھر ان سے پوچھا جائے گا کہ ” اب کہاں ہیں اللہ کے سوا وہ دوسرے خدا جن کو تم شریک کرتے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(40,74,'40:74','اللہ کے سوا وہ جواب دیں گے ” کھوئے گئے وہ ہم سے بلکہ ہم اس سے پہلے کسی چیز کو نہ پکارتے تھے “۔ اس طرح اللہ کافروں کو گمراہ ہونا متحقق کردے گا');
INSERT INTO "sayyid_qutb" VALUES(40,75,'40:75','ان سے کہا جائے گا ” یہ تمہارا انجام اس لیے ہوا ہے کہ تم زمین میں غیر حق پر مگن تھے۔ اور پھر اس پر اتراتے تھے');
INSERT INTO "sayyid_qutb" VALUES(40,76,'40:76','اب جاؤ، جہنم کے دروازوں میں داخل ہوجاؤ، ہمیشہ تم کو وہیں رہنا ہے ، بہت ہی برا ٹھکانا ہے متکبرین کا');
INSERT INTO "sayyid_qutb" VALUES(40,77,'40:77','پس اے نبی صلى الله عليه وسلم ، صبر کرو ، اللہ کا وعدہ برحق ہے ، اب خواہ ہم تمہارے سامنے ہی ان کو ان برے نتائج کا کوئی حصہ دکھا دیں جن سے ہم ! نہیں ڈرا رہے ہیں ، یا (اس سے پہلے) تمہیں دنیا سے اٹھالیں ، پلٹ کر آنا تو انہیں ہماری ہی طرف ہے');
INSERT INTO "sayyid_qutb" VALUES(40,78,'40:78','اے نبی صلى الله عليه وسلم ، تم سے پہلے ہم بہت سے رسول بھیج چکے ہیں جن میں سے بعض کے حالات ہم نے تم کو بتائے ہیں اور بعض کے نہیں بتائے۔ کسی رسول کی بھی یہ طاقت نہ تھی کہ اللہ کے اذن کے بغیر خود کوئی نشانی لے آتا ۔ پھر جب اللہ کا حکم آگیا تو حق کے مطابق فیصلہ کردیا گیا اور اس وقت غلط کار لوگ خسارے میں پڑ گئے');
INSERT INTO "sayyid_qutb" VALUES(40,79,'40:79','اللہ ہی نے تمہارے لیے مویشی جانور بنائے ہیں تاکہ ان میں سے کسی پر تم سوار ہو اور کسی کا گوشت کھاؤ');
INSERT INTO "sayyid_qutb" VALUES(40,80,'40:80','ان کے اندر تمہارے لیے اور بھی بہت سے منافع ہیں۔ وہ اس کام بھی آتے ہیں کہ تمہارے دلوں میں جہاں جانے کی حاجت ہو وہاں تم ان پر پہنچ سکو۔ ان پر بھی اور کشتیوں پر بھی تم سوار کیے جاتے ہو');
INSERT INTO "sayyid_qutb" VALUES(40,81,'40:81','اللہ اپنی یہ نشانیاں تمہیں دکھا رہا ہے ، آخر تم اس کی کن کن نشانیوں کا انکار کروگے');
INSERT INTO "sayyid_qutb" VALUES(40,82,'40:82','پھر کیا یہ زمین میں چلے پھرے نہیں ہیں کہ ان کو ان لوگوں کا انجام نظر آتا جو ان سے پہلے گزرچکے ہیں ؟ وہ ان سے تعداد میں زیادہ تھے ، ان سے بڑھ کر طاقتور تھے ، اور زمین میں ان سے زیادہ شاندار آثار چھوڑگئے ہیں۔ جو کچھ کمائی انہوں نے کی تھی ، آخر وہ ان کے کس کام آئی ؟');
INSERT INTO "sayyid_qutb" VALUES(40,83,'40:83',':” جب ان کے رسول ان کے پاس بنیات لے کر آئے تو وہ اسی علم میں مگن رہے جو ان کے اپنے پاس تھا ، اور اسی چیز کے پھیر میں آگئے جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(40,84,'40:84','جب انہوں نے ہمارا عذاب دیکھ لیا تو پکاراٹھے کہ ہم نے مان لیا اللہ وحدہ لاشریک کو اور ہم انکار کرتے ہیں ان سب معبودوں کا جنھیں ہم اس کا شریک ٹھیراتے تھے');
INSERT INTO "sayyid_qutb" VALUES(40,85,'40:85','۔ مگر ہمارا عذاب دیکھ لینے کے بعد ان کا ایمان ان کے لیے کچھ بھی نافع نہ ہوسکتا تھا کیوں کہ یہی اللہ کا مقرر ضابطہ ہے جو ہمیشہ اس کے بندوں میں جاری رہا ہے اور اس وقت کافر لوگ خسارے میں پڑگئے');
INSERT INTO "sayyid_qutb" VALUES(41,1,'41:1','ح م');
INSERT INTO "sayyid_qutb" VALUES(41,2,'41:2','یہ خدائے رحمٰن ورحیم کی طرف سے نازل کردہ چیز ہے');
INSERT INTO "sayyid_qutb" VALUES(41,3,'41:3','ایک ایسی کتاب جس کی آیات کھول کھول کر بیان کی گئی ہیں ، عربی زبان کا قرآن ، ان لوگوں کے لیے جو علم رکھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(41,4,'41:4','بشارت دینے والا اور ڈرادینے والا ہے۔ مگر ان لوگوں میں سے اکثر نے اس سے روگردانی کی اور وہ سب کر نہیں دیتے');
INSERT INTO "sayyid_qutb" VALUES(41,5,'41:5','کہتے ہیں جس چیز کی طرف تو ہمیں بلارہا ہے اس کے لیے ہمارے دلوں پر غلاف چڑھے ہیں۔ ہمارے کان بہرے ہوگئے ہیں۔ اور ہمارے اور تیرے درمیان ایک حجاب حائل ہوگیا ہے تو اپنا کام کر ، ہم اپنا کام کیے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(41,6,'41:6','اے نبی صلى الله عليه وسلم ، ان سے کہو ، میں تو ایک بشر ہوں تم جیسا۔ مجھے وحی کے ذریعہ سے بتایا جاتا ہے کہ تمہارا خدا تو بس ایک ہی ہے ، لہٰذا تم سیدھے اسی کا رخ اختیار کرو اور اس سے معافی چاہو۔ تباہی ان مشرکوں کے لیے');
INSERT INTO "sayyid_qutb" VALUES(41,7,'41:7','جو زکوٰۃ نہیں دیتے اور آخرت کے منکر ہیں');
INSERT INTO "sayyid_qutb" VALUES(41,8,'41:8','، رہے وہ لوگ جنہوں نے مان لیا اور نیک اعمال کیے ، ان کے لیے یقیناً ایسا اجر ہے جس کا سلسلہ کبھی ٹوٹنے والا نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(41,9,'41:9','اے نبی صلى الله عليه وسلم ، ان سے کہو کیا تم اس خدا سے کفر کرتے ہو اور دوسروں کو اس کا ہمسر ٹھیراتے ہو جس نے زمین کو دو دنوں میں بنادیا ؟ وہی تو سارے جہان والوں کا رب ہے');
INSERT INTO "sayyid_qutb" VALUES(41,10,'41:10','اس نے (زمین کو وجود میں لانے کے بعد) اوپر سے اس پر پہاڑ جما دیئے اور اس میں برکتیں رکھ دیں اور اس کے اندر سب مانگنے والوں کے لیے ہر ایک کی طلب و حاجت کے مطابق ٹھیک انداز سے خوراک کا سامان مہیا کردیا۔ یہ سب کام چار دن میں ہوگئے');
INSERT INTO "sayyid_qutb" VALUES(41,11,'41:11','پھر وہ آسمان کی طرف متوجہ ہوا ، جو اس وقت محض دھواں تھا۔ اس نے آسمان اور زمین سے کہا ” وجود میں آجاؤ، خواہ تم چاہو یا نہ چاہو “۔ دونوں نے کہا ” ہم آگئے فرمانبرداروں کی طرح');
INSERT INTO "sayyid_qutb" VALUES(41,12,'41:12','تب اس نے دودن کے اندر آسمان بنا دیئے اور ہر آسمان میں اس کا قانون وحی کردیا۔ اور آسمان دنیا کو ہم نے چراغوں سے آراستہ کیا اور اسے خوب محفوظ کردیا ۔ یہ سب کچھ ایک زبردست علیم ہستی کا منصوبہ ہے');
INSERT INTO "sayyid_qutb" VALUES(41,13,'41:13','اب اگر یہ لوگ منہ موڑتے ہیں تو ان سے کہہ دو کہ میں تم کو اسی طرح کے ایک اچانک ٹوٹ پڑنے والے عذاب سے ڈراتا ہوں ، جیسا عاد وثمود پر نازل ہوا تھا');
INSERT INTO "sayyid_qutb" VALUES(41,14,'41:14','۔ جب خدا کے رسول ان کے پاس آگے اور پیچھے ہر طرف سے آئے اور انہیں سمجھایا کہ اللہ کے سوا کسی کی بندگی نہ کرو ، تو انہوں نے کہا ” ہمارا رب چاہتا تو فرشتے بھیجتا ، لہٰذا ہم اس بات کو نہیں مانتے جس کے لیے تم بھیجے گئے ہو');
INSERT INTO "sayyid_qutb" VALUES(41,15,'41:15','عاد کا حال یہ تھا کہ وہ زمین میں کسی حق کے بغیر بڑے بن بیٹھے اور کہنے لگے ” کون ہے ہم سے زیادہ زور آوران کو یہ سوجھا کہ جس نے ان کو پیدا کیا ہے وہ ان سے زیادہ زور آور ہے ؟ وہ ہماری آیات کا انکار ہی کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(41,16,'41:16','“۔ ، آخر کار ہم نے چند منحوس دنوں میں سخت طوفانی ہوا ان پر بھیج دی تاکہ انہیں دنیا ہی کی زندگی میں ذلت ورسوائی کے عذاب مزہ چکھادیں ، اور آخرت کا عذاب تو اس سے بھی زیادہ رسواکن ہے ، وہاں کوئی ان کی مدد کرنے والا نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(41,17,'41:17','رہے ثمود ، تو ان کے سامنے ہم نے راہ راست پیش کی مگر انہوں نے راستہ دیکھنے کے بجائے اندھا بنا رہنا ہی پسند کیا ، آخر ان کے کرتوتوں کی بدولت ذلت کا عذاب ان پر ٹوٹ پڑا');
INSERT INTO "sayyid_qutb" VALUES(41,18,'41:18','اور ہم نے ان لوگوں کو بچالیا جو ایمان لائے تھے اور گمراہی و بدعملی سے پرہیز کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(41,19,'41:19','اور ذرا اس وقت کا خیال کرو جب اللہ کے یہ دشمن دوزخ کی طرف جانے کے لئے گھیر لائے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(41,20,'41:20','ان کے اگلوں کو پچھلوں کے آنے تک روک رکھا جائے ، پھر جب سب وہاں پہنچ جائیں گے تو ان کے کان اور ان کی آنکھیں اور ان کے جسم کی کھالیں ان پر گواہی دیں گی کہ وہ دنیا میں کیا کچھ کرتے رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(41,21,'41:21','وہ اپنے جسم کی کھالوں سے کہیں گے ” تم نے ہمارے خلاف کیوں گواہی دی ؟ “ وہ جواب دیں گی ” ہمیں اسی خدا نے گویائی دی ہے جس نے ہر چیز کو گویا کردیا ہے۔ اسی نے تم کو پہلی مرتبہ پیدا کیا تھا اور اب اسی کی طرف تم واپس لائے جارہے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(41,22,'41:22','تم دنیا میں جرائم کرتے وقت جب چھپتے تھے تو تمہیں یہ خیال نہ تھا کہ کبھی تمہارے اپنے کان اور تمہاری آنکھیں اور تمہارے جسم کی کھالیں تم گواہی دیں گی۔ بلکہ تم نے تو یہ سمجھا تھا کہ تمہارے بہت سے اعمال کی اللہ کو بھی خبر نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(41,23,'41:23','تمہارا یہی گمان جو تم نے اپنے رب کے ساتھ کیا تھا ، تمہیں لے ڈوبا اور اسی کی بدولت تم خسارے میں پڑگئے');
INSERT INTO "sayyid_qutb" VALUES(41,24,'41:24','اس حالت میں وہ صبر کریں (یا نہ کریں) آگ ہی ان کا ٹھکانا ہوگی ، اور اگر رجوع کا موقع چاہیں گے تو کوئی موقع نہیں نہ دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(41,25,'41:25','ہم نے ان پر ایسے ساتھ مسلط کر دئیے تھے جو انہیں آگے اور پیچھے ہر چیز خوشنما بنا کر دکھاتے تھے ، آخر کار ان پر بھی وہی فیصلہ عذاب چسپاں ہو کر رہا جو ان سے پہلے گزرے ہوئے جنوں اور انسانوں کے گروہوں پر چسپاں ہوچکا تھا ، یقیناً وہ خسارے میں رہ جانے والے تھے');
INSERT INTO "sayyid_qutb" VALUES(41,26,'41:26','یہ منکرین حق کہتے ہیں ” اس قرآن کو ہرگز نہ سنو اور جب یہ سنایا جائے تو اس میں خلل ڈالو ، شاید کہ اسی طرح تم غالب آجاؤ');
INSERT INTO "sayyid_qutb" VALUES(41,27,'41:27','ان کافروں کو ہم سخت عذاب کا مزہ چکھا کر رہیں گے اور جو بدترین حرکات یہ کرتے رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(41,28,'41:28','ان کا پورا پورا بدلہ انہیں دیں گے۔ وہ دوزخ ہے جو اللہ کے دشمنوں کو بدلے میں ملے گی۔ اسی میں ہمیشہ ہمیشہ کے لئے ان کا گھر ہوگا۔ یہ ہے سزا اس جرم کی کہ وہ ہماری آیات کا انکار کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(41,29,'41:29','وہاں یہ کافر کہیں گے کہ ” اے ہمارے رب ، ذرا ہمیں دکھادے ان جنوں اور انسانوں کو جنہوں نے ہمیں گمراہ کیا تھا ، ہم انہیں پاؤں تلے روند ڈالیں گے تا کہ وہ خوب ذلیل و خوار ہوں');
INSERT INTO "sayyid_qutb" VALUES(41,30,'41:30','جن لوگوں نے کہا کہ اللہ ہمارا رب ہے ، اور پھر وہ اس پر ثابت قدم رہے ، یقیناً ان پر فرشتے نازل ہوتے ہیں اور ان سے کہتے ہیں کہ ” نہ ڈرو ، نہ غم کرو ، اور خوش ہوجاؤ اس جنت کی بشارت سے جس کا تم سے وعدہ کیا گیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(41,31,'41:31','ہم اس دنیا کی زندگی میں بھی تمہارے ساتھ ہیں اور آخرت میں بھی۔ وہاں جو کچھ تم چاہو گے تمہیں ملے گا اور ہر چیز جس کی تم تمنا کرو گے وہ تمہاری ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(41,32,'41:32','یہ ہے سامان ضیافت اس ہستی کی طرف سے جو غفور اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(41,33,'41:33','اور اس شخص کی بات سے اچھی بات اور کس کی ہوگی جس نے اللہ کی طرف بلایا اور نیک عمل کیا اور کہا کہ میں مسلمان ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(41,34,'41:34','اور اے نبی صلى الله عليه وسلم نیکی اور بدی یکساں نہیں ہیں۔ تم بدی کو اس نیکی سے دفع کرو جو بہترین ہو۔ تم دیکھو گے کہ تمہارے ساتھ جس کی عداوت پڑی ہوئی تھی ، وہ جگری دوست بن گیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(41,35,'41:35','یہ صفت نصیب نہیں ہوتی مگر ان لوگوں کو جو صبر کرتے ہیں اور یہ مقام حاصل نہیں ہوتا ، مگر ان لوگوں کو جو بڑے نصیبے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(41,36,'41:36','اور اگر تم شیطان کی طرف سے کوئی اکساہٹ محسوس کرو تو اللہ کی پناہ مانگ لو ، وہ سب کچھ سنتا اور جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(41,37,'41:37','اللہ کی نشانیوں میں سے ہیں یہ رات اور دن اور سورج اور چاند۔ سورج اور چاند کو سجدہ نہ کرو بلکہ اس خدا کو سجدہ کرو جس نے انہیں پیدا کیا ہے۔ اگر فی الواقع تم اسی کی عبادت کرنے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(41,38,'41:38','لیکن اگر یہ لوگ غرور میں آکر اپنی ہی بات پر اڑے رہیں تو پرواہ نہیں ، جو فرشتے تیرے رب کے مقرب ہیں وہ شب و روز اس کی تسبیح کر رہے ہیں ، اور کبھی نہیں تھکتے');
INSERT INTO "sayyid_qutb" VALUES(41,39,'41:39','اور اللہ کی نشانیوں میں سے ایک یہ ہے کہ تم دیکھتے ہو زمین سونی پڑی ہوئی ہے ، پھر جونہی کہ ہم نے اس پر پانی برسایا ، یکایک وہ پھبک اٹھتی ہے اور پھول جاتی ہے۔ یقیناً جو خدا اس مری ہوئی زمین کو جلا اٹھاتا ہے وہ مردوں کو بھی زندگی بخشنے والا ہے۔ یقیناً وہ ہر چیز پر قدرت رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(41,40,'41:40','جو لوگ ہماری آیات کو الٹے معنی پہناتے ہیں وہ ہم سے کچھ چھپے ہوئے نہیں ہیں۔ خود ہی سوچ لو کہ آیا وہ شخص بہتر ہے جو آگ میں جھونکا جانے والا ہے یا وہ جو قیامت کے روز امن کی حالت میں حاضر ہوگا ؟ کرتے رہو جو کچھ تم چاہو ، تمہاری ساری حرکتوں کو اللہ دیکھ رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(41,41,'41:41','وہ لوگ جن کے سامنے کلام نصیحت آیا تو انہوں نے اسے ماننے سے انکار کردیا۔ مگر حقیقت یہ ہے کہ ایک زبردست کتاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(41,42,'41:42','باطل نہ سامنے سے اس پر آسکتا ہے نہ پیچھے سے ، یہ ایک حکیم وحمید کی نازل کردہ چیز ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(41,43,'41:43','اے نبی صلى الله عليه وسلم ، تم کو جو کچھ کہا جا رہا ہے ، اس میں کوئی چیز بھی ایسی نہیں ہے جو تم سے پہلے گزرے ہوئے رسولوں کو نہ کہی جا چکی ہو۔ بیشک تمہارے رب بڑا درگزر کرنے والا ہے ، اور اس کے ساتھ بڑی دردناک سزا دینے والا بھی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(41,44,'41:44','اگر ہم اس کو عجمی قرآن بنا کر بھیجتے تو یہ لوگ کہتے : “ کیوں نہ اس کی آیات کھول کر بیان کی گئیں ؟ کیا ہی عجیب بات ہے کہ کلام عجمی ہے اور مخاطب عربی ”۔ ان سے کہو ، یہ قرآن ایمان لانے والوں کے لئے تو ہدایت اور شفا ہے ، مگر جو لوگ ایمان نہیں لاتے ان کے لئے یہ کانوں کی ڈاٹ اور آنکھوں کی پٹی ہے۔ ان کا حال تو ایسا ہے جیسے ان کو دور سے پکارا جا رہا ہو');
INSERT INTO "sayyid_qutb" VALUES(41,45,'41:45','اس سے پہلے ہم نے موسیٰ کو کتاب دی تھی اور اس کے معاملے میں بھی یہی اختلاف ہوا تھا۔ اگر تیرے رب نے پہلے ایک بات طے نہ کردی ہوتی تو ان اختلاف کرنے والوں کے درمیان فیصلہ چکا دیا جاتا اور حقیقت یہ ہے کہ یہ لوگ اس کی طرف سے سخت اضطراب انگیز شک میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(41,46,'41:46','جو کوئی نیک عمل کرے گا اپنے ہی لیے اچھا کرے گا ، جو بدی کرے گا اس کا وبال اسی پر ہوگا ، اور تیرا رب اپنے بندوں کے حق میں ظالم نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(41,47,'41:47','اس ساعت کا علم اللہ ہی کی طرف راجع ہوتا ہے ، وہی ان سارے پھلوں کو جانتا ہے جو اپنے شگوفوں میں سے نکلتے ہیں ، اسی کو معلوم ہے کہ کون سی مادہ حاملہ ہوئی ہے اور کس نے بچہ جنا ہے ۔ پھر جس روزہ وہ ان لوگوں کو پکارنے گا کہ کہاں ہیں میرے وہ شریک ؟ یہ کہیں گے “ ہم عرض کرچکے ہیں ، آج ہم میں سے کوئی اس کی گواہی دینے والا نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(41,48,'41:48','اس وقت وہ سارے معبود ان سے گم ہوجائیں گے جنہیں یہ اس سے پہلے پکارتے تھے ، اور یہ لوگ سمجھ لیں گے کہ ان کے لئے اب کوئی جائے پناہ نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(41,49,'41:49','انسان کبھی بھلائی کی دعا مانگتے نہیں تھکتا ، اور جب کوئی آفت اس پر آجاتی ہے تو مایوس و دل شکستہ ہوجاتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(41,50,'41:50','مگر جونہی کہ سخت وقت گزر جانے کے بعد ہم اسے اپنی رحمت کا مزہ چکھاتے ہیں ، یہ کہتا ہے کہ “ میں اسی کا مستحق ہوں ، اور میں نہیں سمجھتا کہ قیامت کب آئے گی ، لیکن اگر واقعی میں اپنے رب کی طرف پلٹایا گیا تو وہاں بھی مزے کروں گا ”۔ حالانکہ کفر کرنے والوں کو لازماً ہم بتا کر رہیں گے کہ وہ کیا کر کے آئے ہیں اور انہیں ہم بڑے گندے عذاب کا مزہ چکھائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(41,51,'41:51','انسان کو جب ہم نعمت دیتے ہیں تو وہ منہ پھیرتا ہے اور اکڑ جاتا ہے اور جب اسے کوئی آفت چھو جاتی ہے تو لمبی چوڑی دعائیں کرنے لگتا ہے');
INSERT INTO "sayyid_qutb" VALUES(41,52,'41:52','اے نبی صلى الله عليه وسلم ، ان سے کہو ، کبھی تم نے یہ بھی سوچا کہ اگر واقعی یہ قرآن خدا ہی کی طرف سے ہوا اور تم اس کا انکار کرتے رہے تو اس شخص سے بڑھ کر بھٹکا ہوا اور کون ہوگا جو اس کی مخالفت میں دور تک نکل گیا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(41,53,'41:53','عنقریب ہم ان کو اپنی نشانیاں آفاق میں بھی دکھائیں گے ، اور ان کے اپنے نفس میں بھی یہاں تک کہ ان پر یہ بات کھل جائے گی کہ یہ قرآن واقعی برحق ہے۔ کیا یہ بات کافی نہیں ہے کہ تیرا رب ہر چیز پر گواہ ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(41,54,'41:54','آگاہ رہو ، یہ لوگ اپنے رب کی ملاقات میں شک رکھتے ہیں۔ سن رکھو ، وہ ہر چیز پر محیط ہے');
INSERT INTO "sayyid_qutb" VALUES(42,1,'42:1','ح م');
INSERT INTO "sayyid_qutb" VALUES(42,2,'42:2','ع س ، ق');
INSERT INTO "sayyid_qutb" VALUES(42,3,'42:3','اسی طرح اللہ غالب و حکیم تمہاری طرف اور تم سے پہلے گزرے ہوئے رسولوں ) کی طرف وحی کرتا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,4,'42:4','آسمانوں اور زمین میں جو کچھ بھی ہے اسی کا ہے ، وہ برتر اور عظیم ہے');
INSERT INTO "sayyid_qutb" VALUES(42,5,'42:5','قریب ہے کہ آسمان اوپر سے پھٹ پڑیں۔ فرشتے اپنے رب کی حمد کے ساتھ تسبیح کررہے ہیں ، اور زمین والوں کے حق میں درگزر کی درخواستیں کئے جاتے ہیں۔ آگاہ رہو ، حقیقت میں اللہ غفور ورحیم ہی ہے');
INSERT INTO "sayyid_qutb" VALUES(42,6,'42:6','جن لوگوں نے اس کو چھوڑ کر اپنے کچھ دوسرے سرپرست بنا رہے ہیں ، اللہ ہی ان پر نگراں ہے ، تم ان کے حوالہ دار نہیں ہو');
INSERT INTO "sayyid_qutb" VALUES(42,7,'42:7','ہاں اسی طرح اے نبی صلى الله عليه وسلم ، یہ قرآن عربی ہم نے تمہاری طرف وحی کیا ہے ، تا کہ تم بستیوں کے مرکز (شہر مکہ) اور اس کے گردو پیش رہنے والوں کو خبردار کر دو ، اور جمع ہونے کے دن سے ڈرا دو جس کے آنے میں کوئی شک نہیں۔ ایک گروہ کو جنت میں جاتا ہے اور دوسرے گروہ کو دوزخ میں');
INSERT INTO "sayyid_qutb" VALUES(42,8,'42:8','اگر اللہ چاہتا تو ان سب کو ایک ہی امت بنا دیتا ، مگر وہ جسے چاہتا ہے ، اپنی رحمت میں داخل کرتا ہے ، اور ظالموں کا نہ کوئی ولی ہے ، نہ مددگار');
INSERT INTO "sayyid_qutb" VALUES(42,9,'42:9','“۔۔ کیا یہ (ایسے نادان ہیں کہ ) انہوں نے اسے چھوڑ کر دوسرے ولی بنا رکھے ہیں ؟ ولی تو اللہ ہی ہے ، وہی مردوں کو زندہ کرتا ہے ، اور وہ ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(42,10,'42:10','تمہارے درمیان جس معاملہ میں بھی اختلاف ہو ، اس کا فیصلہ کرنا اللہ کا کام ہے وہی اللہ میرا رب ہے ، اسی پر میں نے بھروسہ کیا ، اور اسی کی طرف میں رجوع کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(42,11,'42:11','آسمانوں اور زمین کا بنانے والا ، جس نے تمہاری اپنی جنس سے تمہارے لیے جوڑے پیدا کئے ، اور اسی طرح جانوروں میں بھی (انہی کے ہم جنس ) جوڑے بنائے ، اور اس طریقہ سے وہ تمہاری نسلیں پھیلاتا ہے۔ کائنات کی کوئی چیز اس کے مشابہ نہیں ، وہ سب کچھ سننے اور دیکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,12,'42:12','آسمان اور زمین کے خزانوں کی کنجیاں اسی کے پاس ہیں ، جسے چاہتا ہے کھلا رزق دیتا ہے اور جسے چاہتا ہے نپا تلا دیتا ہے ، اسے ہر چیز کا علم ہے');
INSERT INTO "sayyid_qutb" VALUES(42,13,'42:13','اس نے تمہارے لیے دین کا دہی طریقہ مقرر کیا ہے جس کا حکم اس نے نوح کو دیا تھا اور جسے (اے محمد صلى الله عليه وسلم اب تمہاری طرف ہم نے وحی کے ذریعہ سے بھیجا ہے ، اور جس کی ہدایت ہم ابراہیم (علیہ السلام) اور موسیٰ (علیہ السلام) اور عیسیٰ (علیہ السلام) کو دے چکے ہیں ، اس تاکید کے ساتھ کہ قائم کرو اس دین کو اور اس میں متفرق نہ ہوجاؤ۔ یہی بات ان مشرکین کو سخت ناگوار ہوئی ہے جس کی طرف (اے محمد صلى الله عليه وسلم تم انہیں دے رہے ہو۔ اللہ جسے چاہتا ہے ، اپنا کرلیتا ہے اور وہ اپنی طرف آنے کا راستہ اسی کو دکھاتا ہے جو اس کی طرف رجوع کر ے');
INSERT INTO "sayyid_qutb" VALUES(42,14,'42:14','لوگوں میں جو تفرقہ رونما ہوا ، وہ اس کے بعد ہوا کہ ان کے پاس علم آچکا تھا ، اور اس بنا پر ہوا کہ وہ آپس میں ایک دوسرے پر زیادتی کرنا چاہتے تھے۔ اگر تیرا رب پہلے ہی نہ فرما چکا ہوتا کہ ایک وقت مقررہ تک فیصلہ ملتوی رکھا جائے گا تو ان کا قضیہ چکا دیا گیا ہوتا اور حقیقت یہ ہے کہ اگلوں کے بعد جو لوگ کتاب کے وارث بنائے گئے ، وہ اس کی طرف سے بڑے اضطراب انگیز شک میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,15,'42:15','(چونکہ یہ حالت پیدا ہوچکی ہے) اس لیے اے محمد صلى الله عليه وسلم اب تم اسی دین کی طرف دعوت دو ، اور جس طرح تمہیں حکم دیا گیا ہے اسی پر مضبوطی کے ساتھ قائم ہوجاؤ، اور ان لوگوں کی خواہشات کا اتباع نہ کرو ، اور ان سے کہہ دو کہ : “ اللہ نے جو کتاب بھی نازل کی ہے میں اس پر ایمان لایا ، مجھے حکم دیا گیا ہے کہ میں تمہارے درمیان انصاف کروں۔ اللہ ہی ہمارا رب بھی ہے اور تمہارا رب بھی۔ ہمارے اعمال ہمارے لیے ہیں اور تمہارے اعمال تمہارے لیے۔ ہمارے درمیان کوئی جھگڑا نہیں۔ اللہ ایک روز ہم سب کو جمع کرے گا اور اسی کی طرف سب کو جانا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,16,'42:16','اللہ کی دعوت پر لبیک کئے جانے کے بعد جو لوگ (لبیک کہنے والوں سے) اللہ کے معاملہ میں جھگڑے کرتے ہیں ، ان کی حجت بازی ان کے رب کے نزدیک باطل ہے ، اور ان پر اس کا غضب ہے اور ان کے لئے سخت عذاب ہے ”۔ اس سورت کے آغاز میں آیا تھا');
INSERT INTO "sayyid_qutb" VALUES(42,17,'42:17','وہ اللہ ہی ہے جس نے حق کے ساتھ یہ کتاب اور میزان نازل کی ہے اور تمہیں کیا خبر ، شاید کہ فیصلے کی گھڑی قریب ہی آلگی ہے');
INSERT INTO "sayyid_qutb" VALUES(42,18,'42:18','جو لوگ اس کے آنے پر ایمان نہیں رکھتے ، وہ تو اس کے لئے جلدی مچاتے ہیں ، مگر جو اس پر ایمان رکھتے ہیں وہ اس سے ڈرتے ہیں اور جانتے ہیں کہ یقیناً وہ آنے والی ہے۔ خوب سن لو ، جو لوگ اس گھڑی کے آنے میں شک ڈالنے والی بحثیں کرتے ہیں وہ گمراہی ہی بہت دور نکل گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,19,'42:19','اللہ اپنے بندوں پر بہت مہربان ہے جسے جو کچھ چاہتا ہے ، دیتا ہے اور وہ بڑی قوت والا اور زبردست ہے');
INSERT INTO "sayyid_qutb" VALUES(42,20,'42:20','جو کوئی آخرت کی کھیتی چاہتا ہے ، اس کی کھیتی کو ہم بڑھاتے ہیں ، اور جو دنیا کی کھیتی چاہتا ہے ، اسے دنیا ہی میں سے دیتے ہیں مگر آخرت میں اس کا کوئی حصہ نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(42,21,'42:21','کیا یہ لوگ کچھ ایسے شریک خدا رکھتے ہیں جنہوں نے ان کے لئے دین کی نوعیت رکھنے والا ایک ایسا طریقہ مقرر کردیا ہے جس کا اللہ نے اذن نہیں دیا ؟ اگر فیصلے کی بات طے ہوگئی ہوتی تو ان کا قضیہ چکا دیا گیا ہوتا۔ یقیناً ان ظالموں کے لئے دردناک عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(42,22,'42:22','تم دیکھو گے کہ یہ ظالم اس وقت اپنے کئے کے انجام سے ڈر رہے ہوں گے۔ اور وہ ان پر آکر رہے گا۔ بخلاف اس کے جو لوگ ایمان لے آئے ہیں اور جنہوں نے نیک عمل کئے ہیں ، وہ جنت کے گلستانوں میں ہوں گے ، جو کچھ بھی وہ چاہے گے اپنے رب کے ہاں پائیں گے ، یہی بڑ افضل ہے');
INSERT INTO "sayyid_qutb" VALUES(42,23,'42:23','یہ ہے وہ چیز جس کی خوشخبری اللہ اپنے ان بندوں کو دیتا ہے جنہوں نے مان لیا اور نیک عمل کئے۔ اے نبی صلى الله عليه وسلم ، ان لوگوں سے کہہ دو کہ ” میں اس کام پر تم سے کسی اجر کا طالب نہیں ہوں ، البتہ قرابت کی محبت ضرور چاہتا ہوں “۔ جو کوئی بھلائی کمائے گا ، ہم اس کے لئے اس بھلائی میں خوبی کا اضافہ کردیں گے۔ بیشک اللہ بڑا اور درگزر کرنے والا اور قدر دان ہے');
INSERT INTO "sayyid_qutb" VALUES(42,24,'42:24','کیا یہ لوگ کہتے ہیں کہ اس شخص نے اللہ پر جھوٹابہتان گھڑ لیا ہے ؟ اگر اللہ چاہے تو تمہارے دل پر مہر کر دے۔ وہ باطل کو مٹا دیتا ہے اور حق کو اپنے فرمانوں سے حق کر دکھاتا ہے ، وہ سینوں کے چھپے ہوئے راز جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,25,'42:25','وہی ہے جو اپنے بندوں سے توبہ قبول کرتا ہے اور برائیوں سے در گزر فرماتا ہے ، حالانکہ تم لوگوں کے سب افعال کا اسے علم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(42,26,'42:26','وہ ایمان لانے والوں اور نیک عمل کرنے والوں کی دعا قبول کرتا ہے اور اپنے فضل سے ان کو اور زیادہ دیتا ہے۔ رہے انکار کرنے والے ، تو ان کے لئے سخت سزا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,27,'42:27','اگر اللہ اپنے سب بندوں کو کھلا رزق دے دیتا تو وہ زمین میں سرکشی کا طوفان برپا کردیتے ، مگر وہ ایک حساب سے جتنا چاہتا ہے ، نازل کرتا ہے۔ یقیناً وہ اپنے بندوں سے باخبر ہے اور ان پر نگاہ رکھتا ہے “');
INSERT INTO "sayyid_qutb" VALUES(42,28,'42:28','” وہی ہے جو لوگوں کے مایوس ہوجانے کے بعد مینہ برساتا ہے اور اپنی رحمت پھیلا دیتا ہے ، اور وہی قابل تعریف ولی ہے');
INSERT INTO "sayyid_qutb" VALUES(42,29,'42:29','اس کی نشانیوں میں سے ہے۔ یہ زمین اور آسمانوں کی پیدائش ، اور یہ جاندار مخلوقات جو اس نے دونوں جگہ پھیلا رکھی ہیں۔ وہ جب چاہے انہیں اکٹھا کرسکتا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,30,'42:30','تم لوگوں پر جو مصیبت بھی آئی ہے ، تمہارے اپنے ہاتھوں کی کمائی سے آئی ہے۔ اور بہت سے قصوروں سے وہ ویسے ہی درگزر کرجاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,31,'42:31','تم زمین میں اپنے خدا کو عاجز کردینے والے نہیں ہو ، اور اللہ کے مقابلے میں تم کوئی حامی و ناصر نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(42,32,'42:32','اس کی نشانیوں میں سے ہیں یہ جہاز جو سمندر میں پہاڑوں کی طرح نظر آتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,33,'42:33','۔ اللہ جب چاہے ، ہوا کو ساکن کر دے اور یہ سمندر کی پیٹھ پر کھڑے کے کھڑے رہ جائیں۔ اس میں بڑی نشانیاں ہیں ہر اس شخص کے لئے جو کمال درجہ صبر و شکر کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(42,34,'42:34','یا (ان پر سوار ہونے والوں کے ) بہت سے گناہوں سے درگزر کرتے ہوئے ان کے چند ہی کرتوتوں کی پاداش میں انہیں ڈبو دے');
INSERT INTO "sayyid_qutb" VALUES(42,35,'42:35','اور اس وقت ہماری آیات میں جھگڑے کرنے والوں کو پتہ چل جائے کہ ان کے لئے کوئی جائے پناہ نہیں ہے');
INSERT INTO "sayyid_qutb" VALUES(42,36,'42:36','“ جو کچھ بھی تم لوگوں کو دیا گیا ہے وہ محض دنیا کی چند روزہ زندگی کا سرو ساماں ہے اور جو کچھ اللہ کے ہاں ہے وہ بہتر بھی ہے اور پائیدار بھی۔ وہ ان لوگوں کے لئے ہے جو ایمان لائے ہیں اور اپنے رب پر بھروسہ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,37,'42:37','جو بڑے بڑے گناہوں اور بےحیائی کے کاموں سے پرہیز کرتے ہیں اور اگر غصہ آجائے تو درگزر کر جاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,38,'42:38','، جو اپنے رب کا حکم مانتے ہیں ، نماز قائم کرتے ہیں ، اپنے معاملات آپس کے مشورے سے چلاتے ہیں ، ہم نے جو کچھ بھی رزق انہیں دیا ہے ، اس میں سے خرچ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,39,'42:39','، اور جب ان پر زیادتی کی جاتی ہے تو اس کا مقابلہ کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,40,'42:40','برائی کا بدلہ ویسی ہی برائی ہے ، پھر جو کوئی معاف کر دے اور اصلاح کرے اس کا اجر اللہ کے ذمہ ہے ، اللہ ظالموں کو پسند نہیں کرتا');
INSERT INTO "sayyid_qutb" VALUES(42,41,'42:41','اور جو لوگ ظلم ہونے کے بعد بدلہ لیں ان کو ملامت نہیں کی جاسکتی ،');
INSERT INTO "sayyid_qutb" VALUES(42,42,'42:42','ملامت کے مستحق تو وہ ہیں جو دوسروں پر ظلم کرتے ہیں اور زمین میں ناحق زیادتیاں کرتے ہیں۔ ایسے لوگوں کے لئے دردناک عذاب ہے');
INSERT INTO "sayyid_qutb" VALUES(42,43,'42:43','، البتہ جو شخص صبر سے کام لے اور درگزر کرے ، تو یہ بڑی اولو العزمی کے کاموں میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(42,44,'42:44','جس کو اللہ ہی گمراہی میں پھینک دے اس کا کوئی سنبھالنے والا اللہ کے بعد نہیں ہے۔ تم دیکھو گے کہ یہ ظالم جب عذاب دیکھیں گے تو کہیں گے اب پلٹنے کی بھی کوئی سبیل ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(42,45,'42:45','اور تم دیکھو گے کہ یہ جہنم کے سامنے جب لائے جائیں گے تو ذلت کے مارے جھکے جا رہے ہوں گے اور اس کو نظر بچا بچا کر کن اکھیوں سے دیکھیں گے۔ اس وقت وہ لوگ جو ایمان لائے تھے کہیں گے کہ واقعی اصل زیاں کار وہی ہیں جنہوں نے آج قیامت کے دن اپنے آپ کو اور اپنے متعلقین کو خسارے میں ڈال دیا۔ خبردار رہو ، ظالم لوگ مستقل عذاب میں ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(42,46,'42:46','اور ان کے کوئی حامی و سرپرست نہ ہوں گے۔ جو اللہ کے مقابلے میں ان کی مدد کو آئیں جسے اللہ گمراہی میں پھینک دے اس کے لئے بچاؤ کی کوئی سبیل نہیں ”');
INSERT INTO "sayyid_qutb" VALUES(42,47,'42:47','مان لو اپنے رب کی بات قبل اس کے کہ وہ دن آئے جس کے ٹلنے کی کوئی صورت اللہ کی طرف سے نہیں ہے۔ اس دن تمہارے لیے کوئی جائے پناہ نہ ہوگی اور نہ کوئی تمہارے حال کو بدلنے کی کوشش کرنے والا ہوگا');
INSERT INTO "sayyid_qutb" VALUES(42,48,'42:48','اب اگر یہ لوگ منہ موڑتے ہیں تو اے نبی ؐ ہم نے تم کو ان پر نگہبان بنا کر تو نہیں بھیجا ہے۔ تم پر تو صرف بات پہنچا دینے کی ذمہ داری ہے انسان کا حال یہ ہے کہ جب ہم اسے اپنی رحمت کا مزہ چکھاتے ہیں تو اس پر پھول جاتا ہے ، اور اگر اس کے اپنے ہاتھوں کا کیا دھرا کسی مصیبت کی شکل میں اس پر الٹ پڑتا ہے تو سخت ناشکرا بن جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,49,'42:49','اللہ زمین اور آسمانوں کی بادشاہی کا مالک ہے ، جو کچھ چاہتا ہے ، پیدا کرتا ہے ، جسے چاہتا ہے لڑکیاں دیتا ہے ، جسے چاہتا ہے ، لڑکے دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(42,50,'42:50','جسے چاہتا ہے لڑکے اور لڑکیاں ملا جلا کردیتا ہے ، اور جسے چاہتا ہے بانجھ کردیتا ہے۔ وہ سب کچھ جانتا اور ہر چیز پر قادر ہے');
INSERT INTO "sayyid_qutb" VALUES(42,51,'42:51','کسی بشر کا یہ مقام نہیں ہے کہ اللہ اس سے روبرو بات کرے۔ اس کی بات یا تو وحی (اشارے) کے طور پر ہوتی ہے ، یا پردے کے پیچھے سے ، یا پھر وہ کوئی پیغام بر (فرشتہ) بھیجتا ہے اور وہ اس کے حکم سے جو کچھ وہ چاہتا ہے وحی کرتا ہے ، وہ برتر اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(42,52,'42:52','اور اسی طرح (اے نبی صلى الله عليه وسلم ہم نے اپنے حکم سے ایک روح تمہاری طرف وحی کی ہے۔ تمہیں کچھ پتہ نہ تھا کہ کتاب کیا ہوتی ہے اور ایمان کیا ہوتا ہے ، مگر اس روح کو ہم نے ایک روشنی بنا دیا جس سے ہم راہ دکھاتے ہیں اپنے بندوں میں سے جسے چاہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(42,53,'42:53','یقیناً تم سیدھے راستے کی طرف رہنمائی کر رہے ہو ، اس خدا کے راستے کی طرف جو زمین اور آسمانوں کی ہر چیز کا مالک ہے۔ خبردار رہو ، سارے معاملات اللہ ہی کی طرف رجوع کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,1,'43:1','ح۔ م');
INSERT INTO "sayyid_qutb" VALUES(43,2,'43:2','قسم ہے اس واضح کتاب کی');
INSERT INTO "sayyid_qutb" VALUES(43,3,'43:3','کہ ہم نے اسے عربی زبان کا قرآن بنایا ہے تا کہ تم لوگ اسے سمجھو');
INSERT INTO "sayyid_qutb" VALUES(43,4,'43:4','“۔۔ اور در حقیقت یہ ام الکتب میں ثبت ہے ، ہمارے ہاں بڑی بلند مرتبہ اور حکمت سے لبریز کتاب');
INSERT INTO "sayyid_qutb" VALUES(43,5,'43:5','۔ اب کیا ہم تم سے بیزار ہو کر یہ درس نصیحت تمہارے ہاں بھیجنا چھوڑ دیں صرف اس لیے کہ تم حد سے گزرے ہوئے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(43,6,'43:6','پہلے گزری ہوئی قوموں میں بھی بار ہا ہم نے نبی بھیجے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,7,'43:7','کبھی ایسا نہیں ہوا کہ کوئی نبی ان کے ہاں آیا ہو اور انہوں نے اس کا مذاق نہ اڑایا ہو');
INSERT INTO "sayyid_qutb" VALUES(43,8,'43:8','پھر جو لوگ ان سے بدرجہا زیادہ طاقتور تھے ، انہیں ہم نے ہلاک کردیا ، پچھلی قوموں کی مثالیں گزر چکی ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,9,'43:9','اگر تم ان لوگوں سے پوچھو کہ زمین اور آسمانوں کو کس نے پیدا کیا تو یہ خود کہیں گے کہ “ انہیں اسی زبردست علیم ہستی نے پیدا کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(43,10,'43:10','وہی نا جس نے تمہارے لیے اس زمین کو گہوارہ بنایا اور اس میں تمہاری خاطر راستے بنا دئیے تا کہ تم اپنی منزل مقصود کی راہ پاسکو');
INSERT INTO "sayyid_qutb" VALUES(43,11,'43:11','“”۔۔ جس نے ایک خاص مقدار میں آسمان سے پانی اتارا اور اس کے ذریعے سے مردہ زمین کو جلا اٹھایا ، اسی طرح ایک روز تم زمین سے برآمد کئے جاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(43,12,'43:12','وہی جس نے یہ تمام جوڑے پیدا کئے ، اور جس نے تمہارے لیے کشتیوں اور جانوروں کو سواری بنایا');
INSERT INTO "sayyid_qutb" VALUES(43,13,'43:13','تا کہ ان کی پشت پر چڑھو اور جب ان پر بیٹھو تو اپنے رب کا احسان یاد کرو اور کہو کہ “ پاک ہے وہ جس نے ہمارے لیے ان چیزوں کو مسخر کردیا ورنہ ہم انہیں قابو میں لانے کی طاقت نہ رکھتے تھے');
INSERT INTO "sayyid_qutb" VALUES(43,14,'43:14','، اور ایک روز ہمیں اپنے رب کی طرف پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(43,15,'43:15','(یہ سب کچھ جانتے اور مانتے ہوئے بھی) ان لوگوں نے اس کے بندوں میں سے بعض کو اس کا جز بنا ڈالا۔ حقیقت یہ ہے کہ انسان کھلا احسان فراموش ہے');
INSERT INTO "sayyid_qutb" VALUES(43,16,'43:16','کیا اللہ نے اپنی مخلوق میں سے اپنے لیے بیٹیاں انتخاب کیں اور تمہیں بیٹوں سے نوازا');
INSERT INTO "sayyid_qutb" VALUES(43,17,'43:17','اور حال یہ ہے کہ جس اولاد کو یہ لوگ اس خدائے رحمٰن کی طرف منسوب کرتے ہیں اس کی ولادت کا مژدہ جب خود ان میں سے کسی کو دیا جاتا ہے تو اس کے منہ پر سیاہی چھا جاتی ہے اور وہ غم سے بھر جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(43,18,'43:18','کیا اللہ کے حصے میں وہ اولاد آئی جو زیوروں میں پالی جاتی ہے اور بحث وحجت میں اپنا مدعا پوری طرح واضح بھی نہیں کرسکتی ؟');
INSERT INTO "sayyid_qutb" VALUES(43,19,'43:19','انہوں نے فرشتوں کو ، جو خدائے رحمٰن کے خاص بندے ہیں ، عورتیں قرار دے لیا۔ کیا ان کے جسم کی ساخت انہوں نے دیکھی ہے ؟ ان کی گواہی لکھ لی جائے گی اور انہیں اس کی جواب دہی کرنی ہوگی');
INSERT INTO "sayyid_qutb" VALUES(43,20,'43:20','“۔ ؟۔۔ یہ کہتے ہیں “ اگر خدائے رحمٰن چاہتا (کہ ہم ان کی عبادت نہ کریں) تو ہم کبھی ان کو نہ پوجتے ”۔ یہ اس معاملے کی حقیقت کو قطعی نہیں جانتے ، محض تیر تکے لڑاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,21,'43:21','کیا ہم نے اس سے پہلے کوئی کتاب ان کو دی تھی جس کی سند (اپنی اس ملائکہ پرستی کے لئے ) یہ اپنے پاس رکھتے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(43,22,'43:22','نہیں ، بلکہ یہ کہتے ہیں کہ ہم نے اپنے باپ دادا کو ایک طریقے پر پایا ہے اور ہم انہیں کے نقش قدم پر چل رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,23,'43:23','۔ اسی طرح تم سے پہلے جس بستی میں بھی ہم نے کوئی نذیر بھیجا ، اس کے کھاتے پیتے لوگوں نے یہی کہا کہ ہم نے اپنے باپ دادا کو ایک طریقے پر پایا ہے اور ہم انہی کے نقش قدم کی پیروی کر رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,24,'43:24','ہر نبی نے ان سے پوچھا ، کیا تم اسی ڈگر پر چلے جاؤ گے خواہ میں تمہیں اس راستے سے زیادہ صحیح راستہ بتاؤں جس پر تم نے اپنے باپ دادا کو پایا ہے ؟ انہوں نے سارے رسولوں کو یہی جواب دیا کہ جس دین کی طرف بلانے کے لئے تم بھیجے گئے ہو ، ہم اس کے کافر ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,25,'43:25','۔ آخر کار ہم نے ان کی خبر لے ڈالی اور دیکھ لو کہ جھٹلانے والوں کا کیا انجام ہوا');
INSERT INTO "sayyid_qutb" VALUES(43,26,'43:26','یاد کرو وہ وقت جب ابراہیم نے اپنے باپ اور اپنی قوم سے کہا تھا کہ “ تم جن کی بندگی کرتے ہو ، میرا ان سے کوئی تعلق نہیں');
INSERT INTO "sayyid_qutb" VALUES(43,27,'43:27','میرا تعلق صرف اس سے ہے جس نے مجھے پیدا کیا ، وہی میری رہنمائی کرے گا ” ۔');
INSERT INTO "sayyid_qutb" VALUES(43,28,'43:28','اور ابراہیم یہی کلمہ اپنے پیچھے اپنی اولاد میں چھوڑ گیا تا کہ وہ اس کی طرف رجوع کریں ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,29,'43:29','“ ( اس کے باوجود جب یہ لوگ دوسروں کی بندگی کرنے لگے تو میں نے ان کو مٹا نہیں دیا ) بلکہ میں انہیں اور ان کے باپ دادا کو متاع حیات دیتا رہا ، یہاں تک کہ ان کے پاس حق ، اور کھول کھول کر بیان کرنے والا رسول صلى الله عليه وسلم آگیا۔');
INSERT INTO "sayyid_qutb" VALUES(43,30,'43:30','مگر جب وہ حق ان کے پاس آیا تو انہوں نے کہہ دیا کہ یہ تو جادو ہے اور ہم اس کو ماننے سے انکار کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,31,'43:31','۔ کہتے ہیں ، یہ قرآن دونوں شہروں کے بڑے آدمیوں میں سے کسی پر کیوں نہ نازل کیا گیا ؟');
INSERT INTO "sayyid_qutb" VALUES(43,32,'43:32','کیا تیرے رب کی رحمت یہ لوگ تقسیم کرتے ہیں ؟ دنیا کی زندگی میں ان کی گزر بسر کے ذرائع تو ہم نے ان کے درمیان تقسیم کئے ہیں ، اور ان میں سے کچھ لوگوں کو کچھ دوسرے لوگوں پر ہم نے بدرجہا فوقیت دی ہے تا کہ یہ ایک دوسرے سے خدمت لیں اور تیرے رب کی رحمت (یعنی نبوت) اس دولت سے زیادہ قیمتی ہے جو (ان کے رئیس) سمیٹ رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(43,33,'43:33','اگر یہ اندیشہ نہ ہوتا کہ سارے لوگ ایک ہی طریقے کے ہوجائیں گے تو ہم خدائے رحمٰن سے کفر کرنے والوں کے گھروں کی چھتیں ، اور ان کی سیڑھیاں جن سے وہ اپنے بالا خانوں پر چڑھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,34,'43:34','اور ان کے دروازے اور ان کے تخت جن پر وہ تکیے لگا کر بیٹھتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(43,35,'43:35','سب چاندی اور سونے کے بنا دیتے۔ یہ تو محض حیات دنیا کی متاع ہے اور آخرت تیرے رب کے ہاں صرف متبعین کے لئے ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,36,'43:36','اور جو شخص رحمٰن کے ذکر سے تغافل برتتا ہے ، ہم اس پر ایک شیطان مسلط کردیتے ہیں اور وہ اس کا رفیق بن جاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(43,37,'43:37','“ یہ شیاطین ایسے لوگوں کو راہ راست پر آنے سے روکتے ہیں ، اور وہ اپنی جگہ یہ سمجھتے ہیں کہ ہم ٹھیک جا رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(43,38,'43:38','آخر کار جب یہ شخص ہمارے ہاں پہنچے گا تو اپنے شیطان سے کہے گا “ کاش میرے اور تیرے درمیان مشرق و مغرب کا بعد ہوتا تو بدترین ساتھی ہے ”');
INSERT INTO "sayyid_qutb" VALUES(43,39,'43:39','اس وقت ان لوگوں سے کہا جائے گا کہ جب تم ظلم کرچکے تو آج یہ بات تمہارے لیے کچھ بھی نافع نہیں ہے کہ تم اور تمہارے شیاطین عذاب میں مشترک ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,40,'43:40','“ اب کیا اے نبی صلى الله عليه وسلم ، تم بہروں کو سناؤ گے ؟ یا اندھوں اور صریح گمراہی میں پڑے ہوئے لوگوں کو راہ دکھاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(43,41,'43:41','اب تو ہمیں ان کو سزا دینی ہے خواہ ہم تمہیں دنیا سے اٹھالیں ،');
INSERT INTO "sayyid_qutb" VALUES(43,42,'43:42','یا تم کو آنکھوں سے ان کا وہ انجام دکھا دیں جس کا ہم نے ان سے وعدہ کیا ہے ، ہمیں ان پر پوری قدرت حاصل ہے');
INSERT INTO "sayyid_qutb" VALUES(43,43,'43:43','تم بہرحال اس کتاب کو مضبوط سے تھامے رہو ، جو وحی کے ذریعہ سے تمہارے پاس بھیجی گئی ہے ، یقیناً تم سیدھے راستے پر ہو۔');
INSERT INTO "sayyid_qutb" VALUES(43,44,'43:44','حقیقت یہ ہے کہ یہ کتاب تمہارے لیے اور تمہاری قوم کے لئے ایک بہت بڑا شرف ہے اور عنقریب تم لوگوں کو اس کی جواب دہی کرنی ہوگی ۔');
INSERT INTO "sayyid_qutb" VALUES(43,45,'43:45','تم سے پہلے ہم نے جتنے رسول بھیجے تھے ان سب سے پوچھ دیکھو کیا ہم نے خدائے رحمٰن کے سوا کچھ دورے معبود بھی مقرر کیے تھے کہ ان کی بندگی کی جائے ؟”۔');
INSERT INTO "sayyid_qutb" VALUES(43,46,'43:46','“ ہم نے موسیٰ کو اپنی نشانیوں کے ساتھ فرعون اور اس کے اعیان سلطنت کے پاس بھیجا اور اس نے جا کر کہا کہ میں رب العالمین کا رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(43,47,'43:47','پھر جب اس نے ہماری نشانیاں ان کے سامنے پیش کیں تو وہ ٹھٹھے مارنے لگے');
INSERT INTO "sayyid_qutb" VALUES(43,48,'43:48','“ ہم ایک پر ایک ایسی نشانی ان کو دکھاتے چلے گئے جو پہلی سے بڑھ چڑھ کر تھی ، اور ہم نے ان کو عذاب میں دھر لیا کہ وہ اپنی روش سے باز آئیں۔');
INSERT INTO "sayyid_qutb" VALUES(43,49,'43:49','ہر عذاب کے موقع پر وہ کہتے ، اے ساحر ، اپنے رب کی طرف سے جو منصب تجھے حاصل ہے ، اس کی بنا پر ہمارے لیے اس سے دعا کر ، ہم ضرور راہ راست پر آجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(43,50,'43:50','مگر جوں ہی کہ ہم ان پر سے عذاب ہٹا دیتے وہ اپنی بات سے پھرجاتے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,51,'43:51','“ ایک روز فرعون نے اپنی قوم کے درمیان پکار کر کہا ، “ لوگو ، کیا مصر کی بادشاہی میری نہیں ہے ، اور یہ نہریں میرے نیچے نہیں بہہ رہی ہیں ؟ کیا تم لوگوں کو نظر نہیں آتا ؟');
INSERT INTO "sayyid_qutb" VALUES(43,52,'43:52','میں بہتر ہوں یا یہ شخص جو ذلیل و حقیر ہے اور اپنی بات بھی کھول کر بیان نہیں کرسکتا ؟');
INSERT INTO "sayyid_qutb" VALUES(43,53,'43:53','کیوں نہ اس پر سونے کے کنگن اتارے گئے ؟ یا فرشتوں کا ایک دستہ اس کی اردلی میں نہ آیا ؟”');
INSERT INTO "sayyid_qutb" VALUES(43,54,'43:54','“ اس نے اپنی قوم کو ہلکا سمجھا اور انہوں نے اس کی اطاعت کی ، در حقیقت وہ تھے ہی فاسق لوگ ” ۔');
INSERT INTO "sayyid_qutb" VALUES(43,55,'43:55','آخر کار جب انہوں نے ہمیں غضبناک کردیا تو ہم نے ان سے انتقام لیا اور ان کو اکٹھا غرق کر دیا');
INSERT INTO "sayyid_qutb" VALUES(43,56,'43:56','اور بعد والوں کے لئے پیش رو اور نمونہ عبرت بنا کر رکھ دیا ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,57,'43:57','اور جونہی کہ ابن مریم کی مثال دی گئی ، تمہاری قوم کے لوگوں نے اس پر غل مچادیا');
INSERT INTO "sayyid_qutb" VALUES(43,58,'43:58','اور لگے کہنے کہ ہمارے معبود اچھے ہیں یا وہ ؟ یہ مثال وہ تمہارے سامنے محض کج بحثی کے لئے لائے ہیں ، حقیقت یہ ہے کہ یہ ہیں ہی جھگڑالو لوگ۔');
INSERT INTO "sayyid_qutb" VALUES(43,59,'43:59','ابن مریم اس کے سوا کچھ نہ تھا کہ ایک بندہ تھا جس پر ہم نے انعام کیا اور بنی اسرائیل کے لئے اسے اپنی قدرت کا ایک نمونہ بنا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(43,60,'43:60','ہم چاہیں تو تم کو فرشتے بنا دیں جو زمین میں تمہارے جانشین ہوں');
INSERT INTO "sayyid_qutb" VALUES(43,61,'43:61','اور وہ (یعنی ابن مریم ) دراصل قیامت کی ایک نشانی ہے ، پس تم اس میں شک نہ کرو اور میری بات مان لو ، یہی سیدھا راستہ ہے');
INSERT INTO "sayyid_qutb" VALUES(43,62,'43:62','ایسا نہ ہو شیطان تم کو اس سے روک دے کہ وہ تمہارا کھلا دشمن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(43,63,'43:63','اور جب عیسیٰ صریح نشانیاں لیے ہوئے آیا تھا ، اس نے کہا تھا کہ ” میں تم لوگوں کے پاس حکمت لے کر آیا ہوں ، اور اس لیے آیا ہوں کہ تم پر بعض ان باتوں کی حقیقت کھول دوں جن میں تم اختلاف کر رہے ہو ، لہٰذا تم اللہ سے ڈرو اور میری اطاعت کرو');
INSERT INTO "sayyid_qutb" VALUES(43,64,'43:64','حقیقت یہ ہے کہ اللہ ہی میرا رب بھی ہے اور تمہارا رب بھی۔ اسی کی تم عبادت کرو ، یہی سیدھا راستہ ہے');
INSERT INTO "sayyid_qutb" VALUES(43,65,'43:65','مگر ( اس کی اس صاف تعلیم کے باوجود) گروہوں نے آپس میں اختلاف کیا ، پس تباہی ہے ان لوگوں کے لئے جنہوں نے ظلم کیا ایک دردناک دن کے عذاب سے “۔');
INSERT INTO "sayyid_qutb" VALUES(43,66,'43:66','” کیا یہ لوگ اب بس اسی چیز کے منتظر ہیں کہ اچانک ان پر قیامت آجائے اور انہیں خبر بھی نہ ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(43,67,'43:67','وہ دن جب آئے گا تو متقین کو چھوڑ کر باقی سب دوست ایک دوسرے کے دشمن ہوجائیں گے');
INSERT INTO "sayyid_qutb" VALUES(43,68,'43:68','کہا جائے گا کہ ” اے میرے بندو ، آج تمہارے لیے کوئی خوف نہیں اور نہ تمہیں کوئی غم لاحق ہوگا');
INSERT INTO "sayyid_qutb" VALUES(43,69,'43:69','اس روز ان لوگوں سے جو ہماری آیات پر ایمان لائے تھے اور مطیع فرمان بن کر رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(43,70,'43:70','داخل ہوجاؤ جنت میں تم اور تمہاری بیویاں ، تمہیں خوش کردیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(43,71,'43:71','ان کے آگی سونے کے تھال اور ساغر گردش کرائے جائیں گے اور ہر من بھاتی اور نگاہوں کو لذت دینے والی چیز وہاں موجود ہوگی۔ ان سے کہا جائے گا ” تم اب یہاں ہمیشہ رہو گے');
INSERT INTO "sayyid_qutb" VALUES(43,72,'43:72','تم اس جنت کے وارث اپنے ان اعمال کی وجہ سے ہوئے ہو جو تم دنیا میں کرتے رہے');
INSERT INTO "sayyid_qutb" VALUES(43,73,'43:73','تمہارے لیے یہاں بکثرت فواکہ موجود ہیں جنہیں تم کھاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(43,74,'43:74','رہے مجرمین ، تو وہ ہمیشہ جہنم کے عذاب میں مبتلا رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(43,75,'43:75','کبھی ان کے عذاب میں کمی نہ ہوگی ، اور وہ اس میں مایوس پڑے ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(43,76,'43:76','ان پر ہم نے ظلم نہیں کیا بلکہ وہ خود ہی اپنے اوپر ظلم کرتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(43,77,'43:77','وہ پکاریں گے ” اے مالک ، تیرا رب ہمارا کام ہی تمام کردے تو اچھا ہے “۔ وہ جواب دے گا ،” تم یوں ہی پڑے رہو گے “۔');
INSERT INTO "sayyid_qutb" VALUES(43,78,'43:78','ہم تمہارے پاس حق لے کر آئے تھے مگر تم میں سے اکثر کو حق ہی ناگوار تھا');
INSERT INTO "sayyid_qutb" VALUES(43,79,'43:79','کیا ان لوگوں نے کوئی اقدام کرنے کا فیصلہ کرلیا ہے ؟ اچھا تو ہم بھی پھر ایک فیصلہ کئے لیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(43,80,'43:80','کیا انہوں نے یہ سمجھ رکھا ہے کہ ہم ان کی راز کی باتیں اور ان کی سرگوشیاں سنتے نہیں ہیں ” ہم سب کچھ سن رہے ہیں اور ہمارے فرشتے ان کے پاس ہی لکھ رہے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,81,'43:81','ان سے کہو ، اگر واقعی رحمٰن کی کوئی اولاد ہوتی تو سب سے پہلے عبادت کرنے والا میں ہوتا ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,82,'43:82','پاک ہے آسمانوں اور زمین کا فرماں روا ، عرش کا مالک ، ان ساری باتوں سے جو یہ لوگ اس کی طرف منسوب کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(43,83,'43:83','اچھا ، انہیں اپنے باطل خیالات میں غرق اور اپنے کھیل میں منہمک رہنے دو ، یہاں تک کہ یہ اپنا وہ دن دیکھ لیں جس کا انہیں خوف دلایا جارہا ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,84,'43:84','وہی ایک آسمان میں بھی خدا ہے اور زمین میں بھی خدا ، اور وہی حکیم وعلیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(43,85,'43:85','بہت بالا و برتر ہے جس کے قبضے میں زمین اور آسمانوں اور ہر اس چیز کی بادشاہی ہے جو زمین و آسمان کے درمیان پائی جاتی ہے ، اور وہی قیامت کی گھڑی کا علم رکھتا ہے اور اسی کی طرف تم سب پلٹائے جانے والے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(43,86,'43:86','اس کو چھوڑ کر یہ لوگ جنہیں پکارتے ہیں وہ کسی شفاعت کا اختیار نہیں رکھتے ، الا یہ کہ کوئی علم کی بناء پر حق کی شہادت دے ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,87,'43:87','“ اور اگر تم ان سے پوچھو کہ انہیں کس نے پیدا کیا ہے تو یہ خود کہیں گے کہ اللہ نے ۔ پھر کہا سے یہ دھوکا کھا رہے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(43,88,'43:88','“ قسم ہے رسول صلى الله عليه وسلم کے اس قول کی کہ اے رب ، یہ وہ لوگ ہیں جو مان کر نہیں دیتے ،');
INSERT INTO "sayyid_qutb" VALUES(43,89,'43:89','اچھا ، اے نبی صلى الله عليه وسلم ان سے درگزر کرو اور کہہ دو کہ سلام ہے تمہیں ، عنقریب انہیں معلوم ہوجائے گا ”۔');
INSERT INTO "sayyid_qutb" VALUES(44,1,'44:1','ح۔ م ۔');
INSERT INTO "sayyid_qutb" VALUES(44,2,'44:2','قسم ہے اس کتاب مبین کی');
INSERT INTO "sayyid_qutb" VALUES(44,3,'44:3','کہ ہم نے اسے ایک بڑی خیروبرکت والی رات میں نازل کیا ہے ، کیونکہ ہم لوگوں کو متنبہ کرنے کا ارادہ رکھتے تھے');
INSERT INTO "sayyid_qutb" VALUES(44,4,'44:4','یہ وہ رات تھی جس میں ہر معاملہ کا حکیمانہ فیصلہ ہمارے حکم سے صادر کیا جاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(44,5,'44:5','ہم ایک رسول بھیجنے والے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(44,6,'44:6','تیرے رب کی رحمت کے طور پر ، یقیناً وہی سب کچھ سننے اور جاننے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(44,7,'44:7','آسمانوں اور زمین کا رب اور ہر اس چیز کا رب جو آسمان و زمین کے درمیان ہے ، اگر تم لوگ واقعی یقین رکھنے والے ہو');
INSERT INTO "sayyid_qutb" VALUES(44,8,'44:8','کوئی معبود اس کے سوا نہیں ہے ، وہی زندگی عطا کرتا ہے اور وہی موت دیتا ہے۔ تمہارا رب اور تمہارے ان اسلاف کا رب جو پہلے گزر چکے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(44,9,'44:9','مگر فی الواقع ان لوگوں کو یقین نہیں ہے بلکہ یہ اپنے شک میں پڑے کھیل رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,10,'44:10','اچھا ، انتظار کرو اس دن کا جب آسمان صریح دھواں لیے ہوئے آئے گا');
INSERT INTO "sayyid_qutb" VALUES(44,11,'44:11','اور وہ لوگوں پر چھا جائے گا ، یہ ہے درد ناک سزا');
INSERT INTO "sayyid_qutb" VALUES(44,12,'44:12','(اب کہتے ہیں کہ ) “ پروردگار ، ہم پر سے یہ عذاب ٹال دے ، ہم ایمان لاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,13,'44:13','ان کی غفلت کہاں دور ہوتی ہے ؟ ان کا حال تو یہ تھا کہ ان کے پاس رسول مبین آگیا');
INSERT INTO "sayyid_qutb" VALUES(44,14,'44:14','پھر بھی یہ اس کی طرف ملتفت نہ ہوئے اور کہا کہ “ یہ تو سکھایا پڑھایا باؤلا ہے');
INSERT INTO "sayyid_qutb" VALUES(44,15,'44:15','ہم ذرا عذاب ہٹانے والے ہیں (اگر ہٹا دیں تو) تم لوگ وہی کچھ کرو گے جو پہلے کر رہے تھے ۔');
INSERT INTO "sayyid_qutb" VALUES(44,16,'44:16','جس روز ہم بڑی پکڑ دھکڑ کریں گے وہ دن ہوگا جب ہم تم سے انتقام لیں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(44,17,'44:17','ہم ان سے پہلے فرعون کی قوم کو اسی آزمائش میں ڈال چکے ہیں ، ان کے پاس ایک نہایت شریف رسول آیا');
INSERT INTO "sayyid_qutb" VALUES(44,18,'44:18','اور اس نے کہا کہ “ اللہ کے بندو ! سب کچھ میرے حوالے کرو ” یا “ اللہ کے بندوں کو میرے حوالے کرو ، میں تمہارے لیے ایک امانت دار رسول ہوں');
INSERT INTO "sayyid_qutb" VALUES(44,19,'44:19','اللہ کے مقابلے میں سرکشی نہ کرو۔ میں تمہارے سامنے (اپنی ماموریت کی) صریح سند پیش کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(44,20,'44:20','اور میں اپنے رب اور تمہارے رب کی پناہ لے چکا ہوں ، اس سے کہ تم مجھ پر حملہ آور ہو');
INSERT INTO "sayyid_qutb" VALUES(44,21,'44:21','اگر تم میری بات نہیں مانتے تو مجھ پر ہاتھ ڈالنے سے باز رہو ”۔');
INSERT INTO "sayyid_qutb" VALUES(44,22,'44:22','آخر کار اس نے اپنے رب کو پکارا کہ یہ لوگ مجرم ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,23,'44:23','(جواب دیا گیا) “ اچھا تو راتوں رات میرے بندوں کو لے کر چل پڑا۔ تم لوگوں کا پیچھا کیا جائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(44,24,'44:24','سمندر کو اس کے حال پر کھلا چھوڑ دے۔ یہ سارا لشکر غرق ہونے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(44,25,'44:25','کتنے ہی باغ اور چشمے تھے جو وہ چھوڑ گئے');
INSERT INTO "sayyid_qutb" VALUES(44,26,'44:26','اور کھیت اور شاندار محل');
INSERT INTO "sayyid_qutb" VALUES(44,27,'44:27','کتنے ہی عیش کے سرو سامان ، جن میں وہ مزے کر رہے تھے ، ان کے پیچھے دھرے رہ گئے');
INSERT INTO "sayyid_qutb" VALUES(44,28,'44:28','یہ ہوا ان کا انجام ، اور ہم نے دوسروں کو ان چیزوں کا وارث بنا دیا');
INSERT INTO "sayyid_qutb" VALUES(44,29,'44:29','پھر نہ آسمان ان پر رویا ، نہ زمین اور ذرا سی مہلت بھی ان کو نہ دی گئی');
INSERT INTO "sayyid_qutb" VALUES(44,30,'44:30','۔ اس طرح بنی اسرائیل کو ہم نے سخت ذلت کے عذاب سے نجات دی ،');
INSERT INTO "sayyid_qutb" VALUES(44,31,'44:31','فرعون سے نجات دی جو حد سے گزر جانے والوں میں فی الواقع بڑے اونچے درجے کا آدمی تھا');
INSERT INTO "sayyid_qutb" VALUES(44,32,'44:32','اور ان کی حالت جانتے ہوئے ان کو دنیا کی دوسری قوموں پر ترجیح دی ،');
INSERT INTO "sayyid_qutb" VALUES(44,33,'44:33','اور انہیں ایسی نشانیاں دکھائیں جن میں صریح آزمائش تھی ”۔');
INSERT INTO "sayyid_qutb" VALUES(44,34,'44:34','” یہ لوگ کہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,35,'44:35','” ہماری پہلی موت کے سوا اور کچھ نہیں ، اس کے بعد ہم دوبارہ اٹھائے جانے والے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,36,'44:36','۔ اگر تم سچے ہو تو اٹھا لاؤ ہمارے باپ دادا کو');
INSERT INTO "sayyid_qutb" VALUES(44,37,'44:37','یہ بہتر ہیں یا تبع کی قوم اور اس سے پہلے کے لوگ ؟ ہم نے ان کو اسی بنا پر تباہ کیا کہ وہ مجرم ہوگئے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(44,38,'44:38','یہ آسمان و زمین اور ان کے درمیان کی چیزیں ہم نے کچھ کھیل کے طور پر نہیں بنا دی ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,39,'44:39','ان کو ہم نے برحق پیدا کیا ہے ، مگر ان میں سے اکثر لوگ جانتے نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(44,40,'44:40','ان سب لوگوں کے اٹھائے جانے کے لئے طے شدہ وقت فیصلے کا دن ہے ،');
INSERT INTO "sayyid_qutb" VALUES(44,41,'44:41','وہ دن جب کوئی عزیز قریب اپنے کسی عزیز قریب کے کچھ بھی کام نہ آئے گا اور نہ کہیں سے انہیں کوئی مدد پہنچے گی ،');
INSERT INTO "sayyid_qutb" VALUES(44,42,'44:42','سوائے اس کے کہ اللہ ہی کسی پر رحم کرے ، وہ زبردست اور رحیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(44,43,'44:43','زقوم کا درخت');
INSERT INTO "sayyid_qutb" VALUES(44,44,'44:44','گناہ گار کا کھانا ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(44,45,'44:45','” تیل کی تلچھٹ جیسا ، پیٹ میں وہ اس طرح جوش کھائے گا');
INSERT INTO "sayyid_qutb" VALUES(44,46,'44:46','جیسے کھولتا ہوا پانی جوش کھاتا ہے');
INSERT INTO "sayyid_qutb" VALUES(44,47,'44:47','پکڑو اسے اور رگیدتے ہوئے لے جاؤ اور اس کو جہنم کے بیچوں بیچ');
INSERT INTO "sayyid_qutb" VALUES(44,48,'44:48','اور انڈیل دو اس کے سر پر کھولتے پانی کا عذاب');
INSERT INTO "sayyid_qutb" VALUES(44,49,'44:49','چکھ اس کا مزا ، بڑا زبردست عزت در آدمی ہے تو');
INSERT INTO "sayyid_qutb" VALUES(44,50,'44:50','۔ یہ وہی چیز ہے جس کے آنے میں تم لوگ شک رکھتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(44,51,'44:51','خدا ترس لوگ امن کی جگہ میں ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(44,52,'44:52','باغوں اور چشموں میں ،');
INSERT INTO "sayyid_qutb" VALUES(44,53,'44:53','حریر و دیبا کے لباس پہنے ، آمنے سامنے بیٹھے ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(44,54,'44:54','یہ ہوگی ان کی شان۔ اور ہم گوری گوری آہو چشم عورتیں ان سے بیاہ دیں گے۔ وہاں موت کا مزہ وہ کبھی نہ چکھیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(44,55,'44:55','وہاں وہ اطمینان سے ہر طرح کی لذیذ چیزیں طلب کریں گے۔');
INSERT INTO "sayyid_qutb" VALUES(44,56,'44:56','بس دنیا میں جو موت آچکی ، سو آچکی۔ اور اللہ ان کو جہنم کے عذاب سے بچاوے گا ،');
INSERT INTO "sayyid_qutb" VALUES(44,57,'44:57','اپنے فضل سے یہی بڑی کامیابی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(44,58,'44:58','اے نبی صلى الله عليه وسلم ، ہم نے اس کتاب کو تمہاری زبان میں سہل بنا دیا ہے تا کہ یہ لوگ نصیحت حاصل کریں۔');
INSERT INTO "sayyid_qutb" VALUES(44,59,'44:59','اب تم بھی انتظار کرو ، یہ بھی منتظر ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(45,1,'45:1','ح م');
INSERT INTO "sayyid_qutb" VALUES(45,2,'45:2','اس کتاب کا نزول اللہ کی طرف سے ہے جو زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(45,3,'45:3','حقیقت یہ ہے کہ آسمانوں اور زمین میں بیشمار نشانیاں ہیں ایمان لانے والوں کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(45,4,'45:4','اور تمہاری اپنی پیدائش میں ، اور ان حیوانات میں جن کو اللہ (زمین میں ) پھیلا رہا ہے ، بڑی نشانیاں ہیں ان لوگوں کے لئے جو یقین لانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(45,5,'45:5','اور شب و روز کے فرق واختلاف میں ، اور اس رزق میں جسے اللہ آسمان سے نازل فرماتا ہے ، پھر اس کے ذریعہ سے مردہ زمین کو جلا اٹھاتا ہے ، اور ہواؤں کی گردش میں بہت سی نشانیاں ہیں ان لوگوں کے لئے جو عقل سے کام لیتے ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,6,'45:6','یہ اللہ کی نشانیاں ہیں جنہیں ہم تمہارے سامنے ٹھیک ٹھیک بیان کر رہے ہیں۔ اب آخر اللہ اور اس کی آیات کے بعد اور کون سی بات ہے جس پر یہ لوگ ایمان لائیں گے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,7,'45:7','تباہی ہے ہر اس جھوٹے بد اعمال شخص کے لئے');
INSERT INTO "sayyid_qutb" VALUES(45,8,'45:8','جس کے سامنے اللہ کی آیات پڑھی جاتی ہیں اور وہ ان کو سنتا ہے ، پھر پورے غرور کے ساتھ اپنے کفر پر اس طرح اڑا رہتا ہے کہ گویا اس نے ان کو سنا ہی نہیں۔ ایسے شخص کو درد ناک عذاب کا مژدہ سنا دو');
INSERT INTO "sayyid_qutb" VALUES(45,9,'45:9','ہماری آیات میں سے کوئی بات جب اس کے علم میں آتی ہے تو وہ ان کا مذاق بنا لیتا ہے۔ ایسے سب لوگوں کے لئے ذلت کا عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(45,10,'45:10','ان کے پیچھے جہنم ہے جو کچھ بھی انہوں نے دنیا میں کمایا ہے اس میں سے کوئی چیز ان کے کسی کام نہ آئے گی ، نہ ان کے وہ سرپرست ہی ان کے لئے کچھ کرسکیں گے جنہیں اللہ کو چھوڑ کر انہوں نے اپنا ولی بنا رکھا ہے ان کے لئے بڑا عذاب ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,11,'45:11','یہ قرآن سرا سر ہدایت ہے ، اور ان لوگوں کے لئے بلا کا دردناک عذاب ہے جنہوں نے اپنے رب کی آیات کو ماننے سے انکار کیا ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,12,'45:12','وہ اللہ ہی تو ہے جس نے تمہارے لیے سمندر کو مسخر کیا تا کہ اس کے حکم سے کشتیاں اس میں چلیں اور تم اس کا فضل تلاش کرو اور شکر گزار ہو');
INSERT INTO "sayyid_qutb" VALUES(45,13,'45:13','اس نے زمین اور آسمانوں کی ساری ہی چیزوں کو تمہارے لیے مسخر کردیا ، سب کچھ اپنے پاس سے۔۔۔ اس میں بڑی نشانیاں ہیں ان لوگوں کے لئے جو غوروفکر کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(45,14,'45:14','اے نبی ؐ، ایمان لانے والوں سے کہہ دو کہ جو لوگ اللہ کی طرف سے برن دن آنے کا کوئی اندیشہ نہیں رکھتے ، ان کی حرکتوں پر درگزر سے کام لیں تا کہ اللہ خود ایک گروہ کو اس کی کمائی کا بدلہ دے۔');
INSERT INTO "sayyid_qutb" VALUES(45,15,'45:15','جو کوئی نیک عمل کرے گا اپنے ہی لیے کرے گا ، اور جو برائی کرے گا وہ آپ ہی اس کا خمیازہ بھگتے گا۔ پھرجانا تو سب کو اپنے رب ہی کی طرف ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,16,'45:16','اس سے پہلے بنی اسرائیل کو ہم نے کتاب اور حکم اور نبوت عطا کی تھی ان کو ہم نے عمدہ سامان زیست سے نوازا ، دنیا بھر کے لوگوں پر انہیں فضیلت عطا کی');
INSERT INTO "sayyid_qutb" VALUES(45,17,'45:17','اور دین کے معاملہ میں انہیں واضح ہدایات دے دیں۔ پھر جو اختلاف ان کے درمیان رونما ہوا ، وہ (ناواقفیت کی وجہ سے نہیں بلکہ ) علم آجانے کے بعد ہوا اور اس بنا پر ہوا کہ وہ آپس میں ایک دوسرے پر زیادتی کرنا چاہتے تھے۔ اللہ قیامت کے روز ان معاملات کا فیصلہ فرما دے گا جن میں وہ اختلافات کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(45,18,'45:18','اس کے بعد اب اے نبی صلى الله عليه وسلم ، ہم نے تم کو دین کے معاملے میں ایک صاف شاہراہ (شریعت) پر قائم کیا ہے۔ لہٰذا تم اسی پر چلو اور ان لوگوں کی خواہشات کا اتباع نہ کرو جو علم نہیں رکھتے');
INSERT INTO "sayyid_qutb" VALUES(45,19,'45:19','۔ اللہ کے مقابلے میں وہ تمہارے کچھ بھی کام نہیں آسکتے۔ ظالم لوگ ایک دوسرے کے ساتھ ہیں اور متقیوں کا ساتھی اللہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(45,20,'45:20','یہ بصیرت کی روشنیاں ہیں ، سب لوگوں کے لئے اور ہدایت اور رحمت ان لوگوں کے لئے جو یقین لائیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,21,'45:21','کیا وہ لوگ جنہوں نے برائیوں کا ارتکاب کیا ہے یہ سمجھے بیٹھے ہیں کہ ہم انہیں اور ایمان لانے والوں اور نیک عمل کرنے والوں کو ایک جیسا کردیں گے کہ ان کا جینا اور مرنا یکساں ہوجائے ؟ بہت برے حکم ہیں جو یہ لوگ لگاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(45,22,'45:22','اللہ نے تو آسمانوں اور زمین کو برحق پیدا کیا ہے اور اس لیے کیا ہے کہ ہر متنفس کو اس کی کمائی کا بدلہ دیا جائے ، لوگوں پر ظلم ہرگز نہ کیا جائے گا ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,23,'45:23','پھر کیا تم نے کبھی اس شخص کے حال پر بھی غور کیا جس نے اپنی خواہش نفس کو اپنا خدا بنا لیا اور اللہ نے علم کے باوجود اسے گمراہی میں پھینک دیا اور اس کے دل اور کانوں پر مہر لگادی اور اس کی آنکھوں پر پردہ ڈال دیا ؟ اللہ کے بعد اب اور کون ہے جو اسے ہدایت دے ؟ کیا تم لوگ کوئی سبق نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(45,24,'45:24','یہ لوگ کہتے ہیں کہ “ زندگی بس یہی ہماری دنیا کی زندگی ہے ، یہیں ہمارا مرنا اور جینا ہے اور گردش ایام کے سوا کوئی چیز نہیں جو ہمیں ہلاک کرتی ہودرحقیقت اس معاملے میں ان کے پاس کوئی علم نہیں ہے۔ یہ محض گمان کی بنا پر یہ باتیں کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(45,25,'45:25','اور جب ہماری واضح آیات انہیں سنائی جاتی ہیں تو ان کے پاس کوئی حجت اس کے سوا نہیں ہوتی کہ اٹھا لاؤ ہمارے باپ دا دا کو اگر تم سچے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(45,26,'45:26','اے نبی صلى الله عليه وسلم ، ان سے کہو اللہ ہی تمہیں زندگی بخشتا ہے ، پھر وہی تمہیں موت دیتا ہے ، پھر وہی تم کو اس قیامت کے دن جمع کرے گا جس کے آنے میں کوئی شک نہیں ، مگر اکثر لوگ جانتے نہیں ہیں ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,27,'45:27','زمین اور آسمانوں کی بادشاہی اللہ ہی کی ہے ، اور جس روز قیامت کی گھڑی آکھڑی ہوگی اس دن باطل پرست خسارے میں پڑجائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(45,28,'45:28','اس وقت تم ہر گروہ کو گھٹنوں کے بل گرا دیکھو گے۔ ہر گروہ کو پکارا جائے گا کہ آئے اور اپنا نامہ اعمال دیکھے ان سے کہا جائے گا : “ آج تم لوگوں کو ان اعمال کا بدلہ دیا جائے گا جو تم کرتے رہے تھے');
INSERT INTO "sayyid_qutb" VALUES(45,29,'45:29','یہ ہمارا تیار کرایا ہوا اعمال نامہ ہے جو تمہارے اوپر ٹھیک ٹھیک شہادت دے رہا ہے ، جو کچھ بھی تم کرتے تھے اسے ہم لکھواتے جا رہے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,30,'45:30','“ پھر جو لوگ ایمان لائے تھے اور نیک عمل کرتے رہے تھے ، انہیں ان کا رب اپنی رحمت میں داخل کرے گا اور یہی صریح کامیابی ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,31,'45:31','“ اور جن لوگوں نے کفر کیا (ان سے کہا جائے گا) “ کیا میری آیات تم کو نہیں سنائی جاتی تھیں ؟ مگر تم نے تکبر کیا اور مجرم بن کر رہے');
INSERT INTO "sayyid_qutb" VALUES(45,32,'45:32','اور جب کہا جاتا تھا کہ اللہ کا وعدہ برحق ہے اور قیامت کے آنے میں کوئی شک نہیں تو تم کہتے تھے کہ ہم نہیں جانتے قیامت کیا ہوتی ہے ، ہم تو بس ایک گمان سا رکھتے ہیں ، یقین ہم کو نہیں ہے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,33,'45:33','اس وقت ان پر ان کے اعمال کی برائیاں کھل جائیں گی اور وہ اسی چیز کے پھیر میں آجائیں گے جس کا وہ مذاق اڑایا کرتے تھے ”۔');
INSERT INTO "sayyid_qutb" VALUES(45,34,'45:34','“ اور ان سے کہہ دیا جائے گا کہ “ آج ہم بھی اسی طرح تمہیں بھلائے دیتے ہیں جس طرح تم اس دن کی ملاقات کو بھول گئے تھے۔ تمہارا ٹھکانا اب دوزخ ہے اور کوئی تمہاری مدد کرنے والا نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(45,35,'45:35','یہ تمہارا انجام اس لئے ہوا ہے کہ تم نے اللہ کی آیات کا مذاق بنا لیا تھا اور تمہیں دنیا کی زندگی نے دھوکے میں ڈال دیا تھا۔ لہٰذا آج نہ یہ لوگ دوزخ سے نکالے جائیں گے اور نہ ان سے کہا جائے گا کہ معافی مانگ کر اپنے رب کو راضی کرو');
INSERT INTO "sayyid_qutb" VALUES(45,36,'45:36','پس تعریف اللہ ہی کے لئے ہے جو زمین اور آسمانوں کا مالک اور سارے جہان والوں کا پروردگار ہے۔');
INSERT INTO "sayyid_qutb" VALUES(45,37,'45:37','زمین اور آسمانوں میں بڑائی اسی کے لئے ہے اور وہی زبردست اور دانا ہے');
INSERT INTO "sayyid_qutb" VALUES(46,1,'46:1','“ ح ، م');
INSERT INTO "sayyid_qutb" VALUES(46,2,'46:2','، اس کتاب کا نزول اللہ زبردست اور دانا کی طرف سے ہے');
INSERT INTO "sayyid_qutb" VALUES(46,3,'46:3','ہم نے زمین اور آسمانوں کو اور ان ساری چیزوں کو جو ان کے درمیان ہیں ، برحق ، اور ایک مدت خاص کے تعین کے ساتھ پیدا کیا ہے۔ مگر یہ کافر لوگ اس حقیقت سے منہ موڑے ہوئے ہیں جس سے ان کو خبردار کیا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(46,4,'46:4','اے نبی صلى الله عليه وسلم ، ان سے کہو ، “ کبھی تم نے آنکھیں کھول کر دیکھا بھی کہ وہ ہستیاں ہیں کیا۔ جنہیں تم خدا کو چھوڑ کر پکارتے ہو ؟ ذرا مجھے دکھاؤ تو سہی کہ زمین میں انہوں نے کیا پیدا کیا ہے ؟ یا آسمانوں کی تخلیق و تدبیر میں ان کا کوئی حصہ ہے ؟ اس سے پہلے آئی ہوئی کتاب یا علم کا کوئی بقیہ (ان عقائد کے ثبوت میں ) تمہارے پاس ہو تو وہی لے آؤ اگر تم سچے ہو');
INSERT INTO "sayyid_qutb" VALUES(46,5,'46:5','آخر اس شخص سے زیادہ بہکا ہوا انسان اور کون ہوگا جو اللہ کو چھوڑ کر ان کو پکارے جو قیامت تک اسے جواب نہیں دے سکتے بلکہ اس سے بھی بیخبر ہیں کہ پکارنے والے ان کو پکار رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(46,6,'46:6','، اور جب تمام انسان جمع کئے جائیں گے اس وقت وہ اپنے پکارنے والوں کے دشمن اور ان کی عبادت کے منکر ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(46,7,'46:7','ان لوگوں کو جب ہماری صاف صاف آیات سنائی جاتی ہیں اور حق ان کے سامنے آجاتا ہے تو یہ کافر لوگ اس کے متعلق کہتے ہیں کہ یہ تو کھلا جادو ہے');
INSERT INTO "sayyid_qutb" VALUES(46,8,'46:8','کیا ان کا کہنا یہ ہے کہ رسول صلى الله عليه وسلم نے اسے خود گھڑ لیا ہے۔ ان سے کہو ، “ اگر میں نے اسے خود گھڑ لیا ہے ، تو تم مجھے خدا کی پکڑ سے کچھ بھی نہ بچا سکو گے ، جو باتیں تم بناتے ہو ، اللہ ان کو خوب جانتا ہے ، میرے اور تمہارے درمیان وہی گواہی دینے کے لئے کافی ہے ، اور وہ بڑا درگزر کرنے والا اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(46,9,'46:9','ان سے کہو ، “ میں کوئی نرالا رسول تو نہیں ہوں ، میں نہیں جانتا کہ کل تمہارے ساتھ کیا ہونا ہے اور میرے ساتھ کیا ، میں تو صرف اس وحی کی پیروی کرتا ہوں جو میرے پاس بھیجی جاتی ہے اور میں ایک صاف صاف خبردار کردینے والے کے سوا اور کچھ نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(46,10,'46:10','”۔ اے نبی صلى الله عليه وسلم ان سے کہو ، “ کبھی تم نے سوچا بھی کہ اگر یہ کلام اللہ ہی کی طرف سے ہو اور تم نے اس کا انکار کردیا (تو تمہارا کیا انجام ہوگا ؟ ) اور اس جیسے ایک کلام پر تو بنی اسرائیل کا ایک گواہ شہادت بھی دے چکا ہے۔ وہ ایمان لے آیا اور تم اپنے گھمنڈ میں پڑے رہے۔ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا');
INSERT INTO "sayyid_qutb" VALUES(46,11,'46:11','جن لوگوں نے ماننے سے انکار کردیا ہے وہ ایمان لانے والوں کے متعلق کہتے ہیں کہ اگر اس کتاب کو مان لینا کوئی اچھا کام ہوتا تو یہ لوگ اس معاملے میں ہم سے سبقت نہ لے جاسکتے تھے۔ چونکہ انہوں نے اس سے ہدایت نہ پائی اس لیے اب یہ ضرور کہیں گے کہ یہ تو پرانا جھوٹ ہے');
INSERT INTO "sayyid_qutb" VALUES(46,12,'46:12','۔ حالانکہ اس سے پہلے موسیٰ (علیہ السلام) کی کتاب رہنما اور رحمت بن کر آچکی ہے اور یہ کتاب اس کی تصدیق کرنے والی زبان عربی میں آئی تا کہ ظالموں کو متنبہ کر دے اور نیک روش اختیار کرنے والوں کو بشارت دے دے');
INSERT INTO "sayyid_qutb" VALUES(46,13,'46:13','یقیناً جن لوگوں نے کہہ دیا کہ اللہ ہی ہمارا رب ہے ، پھر اس پر جم گئے ، ان کے لئے نہ کوئی خوف ہے اور نہ وہ غمگین ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(46,14,'46:14','“۔ ایسے لوگ جنت میں جانے والے ہیں جہاں وہ ہمیشہ رہیں گے اپنے ان اعمال کے بدلے جو وہ دنیا میں کرتے رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(46,15,'46:15','ہم نے انسان کو ہدایت کی کہ وہ اپنے والدین کے ساتھ نیک برتاؤ کرے ۔ اس کی ماں نے مشقت اٹھا کر اسے پیٹ میں رکھا اور مشقت اٹھا کر ہی اس کو جنا ، اور اس کے حمل اور دودھ چھڑانے میں تیس مہینے لگ گئے۔ یہاں تک کہ جب وہ اپنی پوری طاقت کو پہنچا اور چالیس سال کا ہوگیا تو اس نے کہا “ اے میرے رب ، مجھے توفیق دے کہ میں تیری ان نعمتوں کا شکر ادا کروں جو تو نے مجھے اور میرے والدین کو عطا فرمائیں ، اور ایسا نیک عمل کروں جس سے تو راضی ہو ، اور میری اولاد کو بھی نیک بنا کر مجھے سکھ دے ، میں تیرے حضور توبہ کرتا ہوں اور تابع فرمان (مسلم) بندوں میں سے ہوں');
INSERT INTO "sayyid_qutb" VALUES(46,16,'46:16','“۔ اس طرح کے لوگوں سے ہم ان کے بہترین اعمال کو قبول کرتے ہیں اور ان کی برائیوں سے در گزر کر جاتے ہیں۔ یہ جنتی لوگوں میں شامل ہوں گے اس سچے وعدے کے مطابق جو ان سے کیا جاتا رہا ہے');
INSERT INTO "sayyid_qutb" VALUES(46,17,'46:17','اور جس شخص نے اپنے والدین سے کہا : “ اف ، تنگ کردیا تم نے ، کیا تم مجھے یہ خوف دلاتے ہو کہ میں مرنے کے بعد قبر سے نکالا جاؤں گا ؟ حالانکہ مجھ سے پہلے بہت سی نسلیں گزر چکی ہیں (ان میں میں سے تو کوئی اٹھ کر نہ آیا) ماں اور باپ اللہ کی دہائی دے کر کہتے ہیں “ اے بد نصیب ! مان جا ، اللہ وعدہ سچا ہے ” مگر وہ کہتا ہے “ یہ سب اگلے وقتوں کی فرسودہ کہانیاں ہیں');
INSERT INTO "sayyid_qutb" VALUES(46,18,'46:18','“۔ یہ لوگ ہیں جن پر عذاب کا فیصلہ چسپاں ہوچکا ہے۔ ان سے پہلے جنوں اور انسانوں کے جو ٹولے (اسی قماش کے ) ہو گزرے ہیں ، انہی میں یہ بھی جا شامل ہوں گے۔ بیشک یہ گھاٹے میں رہ جانے والے لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(46,19,'46:19','دونوں گروہوں میں سے ہر ایک کے درجے ان کے اعمال کے لحاظ سے ہیں تا کہ اللہ ان کے لئے کا پورا پورا بدلہ ان کو دے۔ ان پر ظلم ہرگز نہ کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(46,20,'46:20','“ پھر جب یہ کافر آگ کے سامنے لا کھڑے کئے جائیں گے تو ان سے کہا جائے گا :“ تم اپنے حصے کی نعمتیں اپنی دنیا کی زندگی میں ختم کرچکے اور ان کا لطف تم نے اٹھا لیا ، اب جو تکبر تم زمین میں سے کسی حق کے بغیر کرتے رہے اور جو نافرمانیاں تم نے کیں ، ان کی پاداش میں آج تم کو ذلت کا عذاب دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(46,21,'46:21','ذرا انہیں عاد کے بھائی (ہود) کا قصہ سناؤ جب کہ اس نے احقاف میں اپنی قوم کو خبردار کیا تھا اور ایسے خبردار کرنے والے اس سے پہلے بھی گزرچکے تھے اور اس کے بعد بھی آتے رہے۔۔۔ کہ “ اللہ کے سوا کسی کی بندگی نہ کرو ، مجھے تمہارے حق میں ایک بڑے ہولناک دن کے عذاب کا اندیشہ ہے');
INSERT INTO "sayyid_qutb" VALUES(46,22,'46:22','“۔ انہوں نے کہا “ کیا تو اس لیے آیا ہے کہ ہمیں بہکا کر ہمارے معبودوں سے برگشتہ کر دے ؟ اچھا تو لے آ ، اپنا وہ عذاب جس سے تو ہمیں ڈراتا ہے اگر واقعی تو سچا ہے');
INSERT INTO "sayyid_qutb" VALUES(46,23,'46:23','۔ اس نے کہا “ اس کا علم تو اللہ کو ہے ، میں صرف وہ پیغام تمہیں پہنچا رہا ہوں جسے دے کر مجھے بھیجا گیا ہے۔ مگر میں دیکھ رہا ہوں کہ تم لوگ جہالت پرت رہے ہو');
INSERT INTO "sayyid_qutb" VALUES(46,24,'46:24','۔ پھر جب انہوں نے اس عذاب کو اپنی وادیوں کی طرف آتے دیکھا تو کہنے لگے “ یہ بادل ہے جو ہم کو سیراب کر دے گا ”۔۔۔۔ “ نہیں ، بلکہ یہ وہی چیز ہے جس کے لئے تم جلدی مچا رہے تھے۔ یہ ہوا کا طوفان ہے جس میں دردناک عذاب چلا آرہا ہے');
INSERT INTO "sayyid_qutb" VALUES(46,25,'46:25','، اپنے رب کے حکم سے ہر چیز کو تباہ کر ڈالے گا ”۔ آخر کار ان کا حال یہ ہوا کہ ان کے رہنے کی جگہوں کے سوا وہاں کچھ نظر نہ آتا تھا۔ اس طرح ہم مجرموں کو بدلہ دیا کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(46,26,'46:26','۔ ان کو ہم نے وہ کچھ دیا تھا جو تم لوگوں کو نہیں دیا ہے۔ ان کو ہم نے کان ، آنکھیں اور دل ، سب کچھ دے رکھے تھے ، مگر نہ وہ کان ان کے کسی کام آئے ، نہ آنکھیں ، نہ دل۔ کیونکہ وہ اللہ کی آیات کا انکار کرتے تھے ، اور اسی چیز کے پھیر میں وہ آگئے جس کا وہ مذاق اڑاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(46,27,'46:27','۔ تمہارے گردوپیش کے علاقوں میں بہت سی بستیوں کو ہم ہلاک کرچکے ہیں۔ ہم نے اپنی آیات بھیج کر بار بار طرح طرح سے ان کو سمجھایا ، شاید کہ وہ باز آجائیں');
INSERT INTO "sayyid_qutb" VALUES(46,28,'46:28','۔ پھر کیوں نہ ان ہستیوں نے ان کی مدد کی جنہیں اللہ کو چھوڑ کر انہوں نے تقرب الی اللہ کا ذریعہ سمجھتے ہوئے معبود بنا لیا تھا ؟ بلکہ وہ تو ان سے کھوئے گئے ، اور یہ تھا ان کے جھوٹ اور ان بناؤنی عقیدوں کا انجام جو انہوں نے گھڑ رکھے تھے');
INSERT INTO "sayyid_qutb" VALUES(46,29,'46:29','اور وہ واقعہ بھی قابل ذکر ہے) جب ہم جنوں کے ایک گروہ کو تمہاری طرف لے آئے تھے تا کہ قرآن سنیں۔ جب وہ اس جگہ پہنچے (جہاں تم قرآن پڑھ رہے تھے) تو انہوں نے آپس میں کہا خاموش ہوجاؤ۔ پھر جب وہ پڑھا جا چکا تو وہ خبردار کرنے والے بن کر اپنی قوم کی طرف پلٹے۔');
INSERT INTO "sayyid_qutb" VALUES(46,30,'46:30','انہوں نے جا کر کہا ، “ اے ہماری قوم کے لوگو ، ہم نے ایک تاب سنی ہے جو موسیٰ کے بعد نازل کی گئی ہے ، تصدیق کرنے والی ہے اپنے سے پہلے آئی ہوئی کتابوں کی ، رہنمائی کرتی ہے حق اور راہ راست کی طرف۔');
INSERT INTO "sayyid_qutb" VALUES(46,31,'46:31','اے ہماری قوم کے لوگو ، اللہ کی طرف بلانے والے کی دعوت قبول کرلو اور اس پر ایمان لے آؤ، اللہ تمہارے گناہوں سے درگزر فرمائے گا اور تمہیں عذاب الیم سے بچا دے گا');
INSERT INTO "sayyid_qutb" VALUES(46,32,'46:32','اور جو کوئی اللہ کے داعی کی بات نہ مانے وہ نہ زمین میں خود کوئی بل بوتا رکھتا ہے کہ اللہ کو زچ کر دے ، اور نہ اس کے کوئی ایسے حامی و سرپرست ہیں کہ اللہ سے اس کو بچا لیں۔ ایسے لوگ کھلی گمراہی میں پڑے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(46,33,'46:33','اور کیا ان لوگوں کو یہ سجھائی نہیں دیتا کہ جس خدا نے یہ زمین اور آسمان پیدا کئے اور ان کو بناتے ہوئے وہ نہ تھکا ، وہ ضرور اس پر قادر ہے کہ مردوں کو جلا اٹھائے ؟ کیوں نہیں ، یقیناً وہ ہر چیز کی قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(46,34,'46:34','جس روز یہ کافر آگ کے سامنے لائے جائیں گے ، اس وقت ان سے پوچھا جائے گا “ کیا یہ حق نہیں ہے ؟ ” یہ کہیں گے “ ہاں ، ہمارے رب کی قسم (یہ واقعی حق ہے ) ”۔ اللہ فرمائے گا “ اچھا تو اب عذاب کا مزہ چکھو اپنے اس انکار کی پاداش میں جو تم کرتے رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(46,35,'46:35','پس اے نبی صلى الله عليه وسلم ، صبر کرو جس طرح اولو العزم رسولوں نے صبر کیا ہے ، اور ان کے معاملہ میں جلدی نہ کرو۔ جس روز یہ لوگ اس چیز کو دیکھ لیں گے جس کا انہیں خوف دلایا جارہا ہے تو انہیں معلوم ہوگا کہ جیسے دنیا میں دن کی ایک گھڑی بھر سے زیادہ نہیں رہے تھے۔ بات پہنچا دی گئی ، اب کیا نافرمان لوگوں کے سوا اور کوئی ہلاک ہوگا ؟');
INSERT INTO "sayyid_qutb" VALUES(47,1,'47:1','جن لوگوں نے کفر کیا اور اللہ کے راستے سے روکا ، اللہ نے ان کے اعمال کو رائیگاں کردیا۔');
INSERT INTO "sayyid_qutb" VALUES(47,2,'47:2','اور جو لوگ ایمان لائے اور نیک عمل کئے اور اس چیز کو مان لیا جو محمد صلى الله عليه وسلم پر نازل ہوئی ہے۔ اور ہے وہ سراسر حق ان کے رب کی طرف سے۔۔۔۔ اللہ نے ان کی برائیاں ان سے دور کردیں اور ان کا حال درست کردیا۔');
INSERT INTO "sayyid_qutb" VALUES(47,3,'47:3','یہ اس لیے کہ کفر کرنے والوں نے باطل کی پیروی کی اور ایمان لانے والوں نے حق کی پیروی کی جو ان کے رب کی طرف سے آیا ہے۔ اس طرح اللہ لوگوں کو ان کی ٹھیک ٹھیک حیثیت بتائے دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(47,4,'47:4','پس جب ان کافروں سے تمہاری مڈبھیڑ ہو تو پہلا کام گردنیں مارتا ہے ، یہاں تک کہ جب تم ان کو اچھی طرح کچل دو تب قیدیوں کو مضبوط باندھو ، اس کے بعد (تمہیں اختیار ہے) احسان کرو یا فدیے کا معاملہ کرلو ، تا آنکہ لڑائی اپنے ہتھیار ڈال دے۔ یہ ہے تمہارے کرنے کا کا۔ اللہ چاہتا تو خود ہی ان سے نمٹ لیتا ، مگر ( یہ طریقہ اس نے اس لیے اختیار کیا ہے ) تا کہ تم لوگوں کو ایک دوسرے کے ذریعہ سے آزمائے۔ اور جو لوگ اللہ کی راہ میں مارے جائیں گے۔ اللہ ان کے اعمال کو ہرگز ضائع نہ کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,5,'47:5','وہ ان کی رہنمائی فرمائے گا ، ان کا حال درست کر دے گا');
INSERT INTO "sayyid_qutb" VALUES(47,6,'47:6','اور ان کو اس جنت میں داخل کرے گا جس سے وہ ان کو واقف کرا چکا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,7,'47:7','اے لوگو ، جو ایمان لائے ہو ، اگر تم اللہ کی مدد کرو گے تو وہ تمہاری مدد کرے گا اور تمہارے قدم مضبوط جما دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,8,'47:8','رہے وہ لوگ جنہوں نے کفر کیا ہے ، تو ان کے لئے ہلاکت ہے اور اللہ نے ان کے اعمال کو بھٹکا دیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,9,'47:9','کیونکہ انہوں نے اس چیز کو ناپسند کیا جسے اللہ نے نازل کیا ہے ، لہٰذا اللہ نے ان کے اعمال ضائع کر دئیے۔');
INSERT INTO "sayyid_qutb" VALUES(47,10,'47:10','کیا وہ زمین میں چلے پھرے نہ تھے کہ ان لوگوں کا انجام دیکھتے جو ان سے پہلے گزر چکے ہیں ؟ اللہ نے ان کا سب کچھ ان پر الٹ دیا ، اور ایسے ہی نتائج ان کافروں کے لئے مقدر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(47,11,'47:11','یہ اس لیے کہ ایمان لانے والوں کا حامی و ناصر اللہ ہے اور کافروں کا حامی و ناصر کوئی نہیں');
INSERT INTO "sayyid_qutb" VALUES(47,12,'47:12','ایمان لانے والوں اور نیک عمل کرنے والوں کو اللہ ان جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہیں ، اور کفر کرنے والے بس دنیا کی چند روزہ زندگی کے مزے رہے ہیں ، جانوروں کی طرح کھا پی رہے ہیں اور ان کا آخری ٹھکانا جہنم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,13,'47:13','اے نبی صلى الله عليه وسلم ، کتنی ہی بستیاں ایسی گزر چکی ہیں جو تمہاری اس بستی سے بہت زیادہ زور آور تھیں جس نے تمہیں نکال دیا ہے۔ انہیں ہم نے اس طرح ہلاک کردیا کہ کوئی ان کا بچانے والا نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(47,14,'47:14','بھلا کہیں ایسا ہو سکتا ہے کہ جو اپنے رب کی طرف سے ایک صاف وصریح ہدایت پر ہو ، وہ ان لوگوں کی طرح ہوجائے جن کے لئے ان کا برا عمل خوشنما بنا دیا گیا ہے اور وہ اپنی خواہشات کے پیرو بن گئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(47,15,'47:15','پرہیز گار لوگوں کے لئے جس جنت کا وعدہ کیا گیا ہے اس کی شان تو یہ ہے کہ اس میں نہریں بہہ رہی ہوں گی ، نتھرے ہوئے پانی کی ، نہریں بہہ رہی ہوں گی ایسے دودھ کی جس کے مزے میں ذرا فرق نہ آیا ہوگا ، نہریں بہہ رہی ہوں گی ایسی شراب کی جو پینے والوں کے لئے لذیذ ہوگی ، نہریں بہہ رہی ہوں گی ایسی شراب کی جو پینے والوں کے لئے لذیذ ہوگی ، نہری بہہ رہی ہوں گی صاف شفاف شہد کی۔ اس میں ان کے لئے ہر طرح کے پھل ہوں گے اور ان کے رب کی طرف سے بخشش ۔ (کیا وہ شخص جس کے حصہ میں یہ جنت آنے والی ہے) ان لوگوں کی طرح ہو سکتا ہے جو جہنم میں ہمیشہ رہیں گے اور جنہیں ایسا گرم پانی پلایا جائے گا جو ان کی آنتیں تک کاٹ دے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(47,16,'47:16','ان میں سے کچھ لوگ ایسے ہیں جو کان لگا کر تمہاری بات سنتے ہیں اور پھر جب تمہارے پاس سے نکلتے ہیں تو ان لوگوں سے جنہیں علم کی نعمت بخشی گئی ہے۔ پوچھتے ہیں کہ ابھی ابھی انہوں نے کیا کہا تھا ؟ یہ وہ لوگ ہیں جن کے دلوں پر اللہ نے ٹھپہ لگادیا ہے اور یہ اپنی خواہشات کے پیرو بنے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(47,17,'47:17','رہے وہ لوگ جنہوں نے ہدایت پائی ہے ، اللہ ان کو اور زیادہ ہدایت دیتا ہے اور انہیں ان کے حصے کا تقویٰ عطا فرماتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,18,'47:18','اب کیا یہ لوگ بس قیامت ہی کے منتظر ہیں کہ وہ اچانک ان پر آجائے ؟ اس کی علامات تو آچکی ہیں ۔ جب وہ خود آجائے گی تو ان کے لئے نصیحت قبول کرنے کا کون سا موقع باقی رہ جائے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(47,19,'47:19','پس اے نبی صلى الله عليه وسلم خوب جان لو کہ اللہ کے سوا کوئی عبادت کا مستحق نہیں ہے ، اور معافی مانگو اپنے قصور کے لئے بھی اور مومن مردوں اور عورتوں کے لئے بھی۔ اللہ تمہاری سر گرمیوں کو بھی جانتا ہے اور تمہارے ٹھکانے سے بھی واقف ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,20,'47:20','جو لوگ ایمان لائے ہیں وہ کہہ رہے تھے کہ کوئی سورت کیوں نازل نہیں کی جاتی (جس میں جنگ کا حکم دیا جائے) مگر جب ایک پختہ سورت نازل کردی گئی جس میں جنگ کا ذکر تھا تو تم نے دیکھا کہ جن کے دلوں میں بیماری تھی وہ تمہاری طرف اس طرح دیکھ رہے ہیں جیسے کسی پر موت چھا گئی ہو۔');
INSERT INTO "sayyid_qutb" VALUES(47,21,'47:21','ان کے لئے بہتر یہ ہے کہ اطاعت کریں اور صحیح باتیں کریں۔ مگر قطعی حکم دے دیا گیا اس وقت وہ اللہ سے اپنے عہد میں سچے نکلتے تو انہی کے لئے اچھا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(47,22,'47:22','اب کیا تم لوگوں سے اس کے سوا کچھ اور توقع کی جاسکتی ہے کہ اگر تم الٹے منہ پھرگئے تو زمین میں پھر فساد برپا کرو گے اور آپس میں ایک دوسرے کے گلے کاٹو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(47,23,'47:23','یہ لوگ ہیں جن پر اللہ نے لعنت کی اور ان کو اندھا اور بہرہ بنا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(47,24,'47:24','کیا ان لوگوں نے قرآن پر غور نہیں کیا ، یا دلوں پر ان کے قفل چڑھے ہوئے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(47,25,'47:25','حقیقت یہ ہے کہ جو لوگ ہدایت واضح ہوجانے کے بعد اس سے پھرگئے ان کے لئے شیطان نے اس روش کو سہل بنا دیا ہے اور جھوٹی توقعات کا سلسلہ ان کے لئے دراز کر رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,26,'47:26','اسی لیے انہوں نے اللہ کے نازل کردہ دین کو ناپسند کرنے والوں سے کہہ دیا کہ بعض معاملات میں ہم تمہاری مانیں گے۔ اللہ ان کی یہ خفیہ باتیں خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,27,'47:27','پھر اس وقت کیا حال ہوگا جب فرشتے ان کی روحیں قبض کریں گے اور ان کے منہ اور پیٹھوں پر مارتے ہوئے انہیں لے جائیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(47,28,'47:28','یہ اسی تو ہوگا کہ انہوں نے اس طریقے کی پیروی کی جو اللہ کو ناراض کرنے والا ہے اور اس کی رضا کا راستہ اختیار کرنا پسند نہ کیا۔ اسی بنا پر اس نے ان کے سب اعمال ضائع کر دئیے۔');
INSERT INTO "sayyid_qutb" VALUES(47,29,'47:29','کیا وہ لوگ جن کے دلوں میں بیماری ہے ، یہ سمجھے بیٹھے ہیں کہ اللہ ان کے دلوں کے کھوٹ ظاہر نہیں کرے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(47,30,'47:30','ہم چاہیں تو انہیں تم کو آنکھوں سے دکھا دیں اور ان کے چہروں سے تم ان کو پہچان لو۔ مگر ان کے انداز کلام سے تو تم ان کو جان ہی لوگے۔ اللہ تم سب کے اعمال سے خوب واقف ہے۔');
INSERT INTO "sayyid_qutb" VALUES(47,31,'47:31','ہم ضرور تم لوگوں کو آزمائش میں ڈالیں گے تا کہ تمہارے حالات کی جانچ کریں اور دیکھ لیں کہ تم میں مجاہد اور ثابت قدم کون ہیں');
INSERT INTO "sayyid_qutb" VALUES(47,32,'47:32','جن لوگوں نے کفر کیا اور اللہ کی راہ سے روکا اور رسول سے جھگڑا کیا جبکہ ان پر راہ راست واضح ہوچکی تھی ، در حقیقت وہ اللہ کا کو ئی نقصان بھی نہیں کرسکتے ، بلکہ اللہ ہی ان کا سب کیا کرایا غارت کر دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,33,'47:33','اے لوگو ، جو ایمان لائے ہو ، تم اللہ کی اطاعت کرو اور رسول کی اطاعت کرو اور اپنے اعمال کو برباد نہ کرلو۔');
INSERT INTO "sayyid_qutb" VALUES(47,34,'47:34','کفر کرنے والوں اور راہ خدا سے روکنے والوں اور مرتے دم تک کفر پر جمے رہنے والوں کو تو اللہ ہرگز معاف نہ کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,35,'47:35','پس تم بودے نہ بنو اور صلح کی درخواست نہ کرو۔ تم ہی غالب رہنے والے ہو۔ اللہ تمہارے ساتھ ہے اور تمہارے اعمال کو وہ ہرگز ضائع نہ کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,36,'47:36','یہ دنیا کی زندگی تو ایک کھیل اور تماشا ہے۔ اگر تم ایمان رکھو اور تقویٰ کی روش پر چلتے رہو تو اللہ تمہارے اجر تم کو دے گا اور وہ تمہارے مال تم سے نہ مانگے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,37,'47:37','اگر کہیں وہ تمہارے مال تم سے مانگ لے اور سب کے سب تم سے طلب کرلے تو تم بخل کرو گے اور وہ تمہارے کھوٹ ابھار لائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(47,38,'47:38','دیکھو ، تم لوگوں کو دعوت دی جارہی ہے کہ اللہ کی راہ میں مال خرچ کرو۔ اس پر تم میں سے کچھ لوگ ہیں جو بخل کر رہے ہیں ، حالانکہ جو بخل کرتا ہے وہ درحقیقت اپنے آپ ہی سے بخل کر رہا ہے۔ اللہ تو غنی ہے ، تم ہی اس کے محتاج ہو۔ اگر تم منہ موڑوگے تو اللہ تمہاری جگہ کسی اور قوم کو لے آئے گا اور وہ تم جیسے نہ ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(48,1,'48:1','اے نبی صلى الله عليه وسلم ، ہم نے تم کو کھلی فتح عطا کر دی');
INSERT INTO "sayyid_qutb" VALUES(48,2,'48:2','تا کہ اللہ تمہاری اگلی پچھلی ہر کوتاہی سے درگزر فرمائے اور تم پر اپنی نعمت کی تکمیل کردے اور تمہیں سیدھا راستہ دکھائے');
INSERT INTO "sayyid_qutb" VALUES(48,3,'48:3','اور تم کو زبردست نصرت بخشے');
INSERT INTO "sayyid_qutb" VALUES(48,4,'48:4','وہی ہے جس نے مومنوں کے دلوں میں سکینت نازل فرمائی تا کہ اپنے ایمان کے ساتھ وہ ایک ایمان اور بڑھا لیں۔ زمین اور آسمانوں کے سب لشکر اللہ کے قبضہ قدرت میں ہیں اور وہ علیم و حکیم ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(48,5,'48:5','(اس نے یہ کام اس لیے کیا ہے) تا کہ مومن مردوں اور عورتوں کو ہمیشہ رہنے کے لئے ایسی جنتوں میں داخل فرمائے جن کے نیچے بہہ رہی ہوں گی اور ان کی برائیاں ان سے دور کر دے۔۔۔۔ اللہ کے نزدیک یہ بڑی کامیابی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,6,'48:6','اور ان منافق مردوں اور عورتوں اور مشرک مردوں اور عورتوں کو ۔۔۔۔ جو اللہ کے متعلق برے گمان رکھتے ہیں۔ برائی کے پھیر میں وہ خود ہی آگئے ، اللہ کا غضب ان پر ہوا اور اس نے ان پر لعنت کی اور ان کے لئے جہنم مہیا کردی جو بہت ہی برا ٹھکانہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,7,'48:7','زمین اور آسمان کے لشکر اللہ ہی کے قبضہ قدرت میں ہیں اور وہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(48,8,'48:8','اے نبی صلى الله عليه وسلم ، ہم نے تم کو شہادت دینے والا ، بشارت دینے والا اور خبردار کردینے والا بنا کر بھیجا ہے');
INSERT INTO "sayyid_qutb" VALUES(48,9,'48:9','تا کہ اے لوگو ، تم اللہ اور اس کے رسول پر ایمان لاؤ اور اس کا (یعنی رسول کا) ساتھ دو ، اس کی تعظیم و توقیر کرو اور صبح و شام اللہ کی تسبیح کرتے رہو۔');
INSERT INTO "sayyid_qutb" VALUES(48,10,'48:10','اے نبی صلى الله عليه وسلم بدوی عربوں میں سے جو لوگ پیچھے چھوڑ دئیے گئے تھے اب وہ آکر ضرور تم سے کہیں گے کہ ” ہمیں اپنے اموال اور بال بچوں کی فکر نے مشغول کر رکھا تھا ، آپ ہمارے لیے مغفرت کی دعا فرمائیں');
INSERT INTO "sayyid_qutb" VALUES(48,11,'48:11','“ یہ لوگ اپنی زبانوں سے وہ باتیں کہتے ہیں جو ان کے دلوں میں نہیں ہوتیں۔ ان سے کہنا ” اچھا ، یہی بات ہے تو کون تمہارے معاملہ میں اللہ کے فیصلے کو روک دینے کا کچھ بھی اختیار رکھتا ہے۔ اگر وہ تمہیں کوئی نقصان پہنچانا چاہے یا نفع بخشنا چاہے ؟ تمہارے اعمال سے تو اللہ ہی باخبر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(48,12,'48:12','(مگر اصل بات وہ نہیں ہے جو تم کہہ رہے ہو) بلکہ تم نے یوں سمجھا کہ رسول اور مومنین اپنے گھر والوں میں ہرگز پلٹ کر نہ آسکیں گے اور یہ خیال تمہارے دلوں کو بہت بھلا لگا اور تم نے بہت برے گمان کئے اور تم سخت بدباطن لوگ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(48,13,'48:13','اللہ اور اس کے رسول صلى الله عليه وسلم پر جو لوگ ایمان نہ رکھتے ہوں ایسے کافروں کے لے ہم نے بھڑکتی ہوئی آگ مہیا کر رکھی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,14,'48:14','آسمانوں اور زمین کی بادشاہی کا مالک اللہ ہی ہے ، جسے چاہے معاف کرے اور جسے چاہے سزا دے ، اور وہ غفورو رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,15,'48:15','جب تم مال غنیمت حاصل کرنے کے لئے جانے لگو گے تو یہ پیچھے چھوڑے جانے والے لوگ تم سے ضرور کہیں گے کہ ہمیں بھی اپنے ساتھ چلنے دو ۔ یہ چاہتے ہیں کہ اللہ کے فرمان کو بدل دیں۔ ان سے صاف کہہ دینا کہ ” تم ہرگز ہمارے ساتھ نہیں چل سکتے ، اللہ پہلے ہی یہ فرما چکا ہے “ ۔ یہ کہیں گے کہ ” نہیں ، بلکہ تم لوگ ہم سے حسد کر رہے ہو “۔ (حالانکہ بات حسد کی نہیں ہے) بلکہ یہ لوگ صحیح بات کو کم ہی سمجھتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(48,16,'48:16','ان پیچھے چھوڑے جانے والے بدوی عربوں سے کہنا کہ ” عنقریب تمہیں ایسے لوگوں سے لڑنے کے لئے بلایا جائے گا جو بڑے زور آور ہیں۔ تم کو ان سے جنگ کرنی ہوگی یا وہ مطیع ہوجائیں گے۔ اس وقت اگر تم نے حکم جہاد کی اطاعت کی تو اللہ تمہیں اچھا اجر دے گا ، اور اگر تم پھر اسی طرح منہ موڑ گئے جس طرح پہلے موڑ چکے ہو تو اللہ تم کو درد ناک سزا دے گا');
INSERT INTO "sayyid_qutb" VALUES(48,17,'48:17','ہاں اگر اندھا اور لنگڑا اور مریض جہاد کے لئے نہ آئے تو کوئی حرج نہیں ۔ جو کوئی اللہ اور اس کے رسول صلى الله عليه وسلم کی اطاعت کرے گا اللہ اسے ان جنتوں میں داخل کرے گا جن کے نیچے نہریں بہہ رہی ہوں گی اور جو منہ پھیرے گا اسے وہ دردناک عذاب دے گا');
INSERT INTO "sayyid_qutb" VALUES(48,18,'48:18','اللہ مومنوں سے خوش ہوگیا جب وہ درخت کے نیچے تم سے بیعت کر رہے تھے۔ ان کے دلوں کا حال اس کو معلوم تھا اس لیے اس نے ان پر سکینت نازل فرمائی ، ان کو انعام میں قریبی فتح بخشی');
INSERT INTO "sayyid_qutb" VALUES(48,19,'48:19','اور بہت سا مال غنیمت انہیں عطا کردیا جسے وہ (عنقریب) حاصل کریں گے۔ اللہ زبردست اور حکیم ہے');
INSERT INTO "sayyid_qutb" VALUES(48,20,'48:20','اللہ تم سے بکثرت اموال غنیمت کا وعدہ کرتا ہے جنہیں تم حاصل کرو گے۔ فوری طور پر تو یہ فتح اس نے تمہیں عطا کردی اور لوگوں کے ہاتھ تمہارے خلاف اٹھنے سے روک دئیے ، تا کہ یہ مومنوں کے لئے ایک نشانی بن جائے اور اللہ سیدھے راستے کی طرف تمہیں ہدایت بخشے۔');
INSERT INTO "sayyid_qutb" VALUES(48,21,'48:21','اس کے علاوہ دوسری اور غنیمتوں کا بھی وہ تم سے وعدہ کرتا ہے جن پر تم ابھی قادر نہیں ہوئے اور اللہ نے ان کو گھیر رکھا ہے ، اللہ ہر چیز پر قادر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,22,'48:22','یہ کافر لوگ اگر اس وقت تم سے لڑ گئے ہوتے تو یقیناً پیٹھ پھیر جاتے اور کوئی حامی و مددگار نہ پاتے۔');
INSERT INTO "sayyid_qutb" VALUES(48,23,'48:23','یہ اللہ کی سنت ہے۔ جو پہلے سے چلی آرہی ہے اور تم اللہ کی سنت میں کوئی تبدیلی نہ پاؤ گے');
INSERT INTO "sayyid_qutb" VALUES(48,24,'48:24','وہی ہے جس نے مکہ کی وادی میں ان کے ہاتھ تم سے اور تمہارے ہاتھ ان سے روک دئیے ، حالانکہ وہ ان پر تمہیں غلبہ عطا کرچکا تھا اور جو کچھ تم کر رہے تھے اللہ اسے دیکھ رہا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(48,25,'48:25','وہی لوگ تو ہیں جنہوں نے کفر کیا اور تم کو مسجد حرام سے روکا اور ہدی کے اونٹوں کو ان کی قربانی کی جگہ نہ پہنچنے دیا۔ اگر (مکہ میں ) ایسے مومن مرد و عورت موجود نہ ہوتے جنہیں تم نہیں جانتے ، اور یہ خطرہ نہ ہوتا کہ نادانستگی میں تم انہیں پامال کردو گے اور اس سے تم پر حرف آئے گا (تو جنگ نہ روکی جاتی۔ روکی وہ اس لیے گئی ) تا کہ اللہ اپنی رحمت میں جس کو چاہے داخل کرلے۔ وہ مومن الگ ہوگئے ہوتے تو (اہل مکہ میں) جو کافر تھے ان کو ہم ضرور سخت سزا دیتے۔');
INSERT INTO "sayyid_qutb" VALUES(48,26,'48:26','“ (یہی وجہ ہے کہ ) جب ان کافروں نے اپنے دلوں میں جاہلانہ حمیت بٹھا لی تو اللہ نے اپنے رسول صلى الله عليه وسلم اور مومنوں پر سکینت نازل فرمائی اور مومنوں کو تقویٰ کی بات کا پابند رکھا کہ وہی اس کے زیادہ حق دار اور اس کے اہل تھے۔ اللہ ہر چیز کا علم رکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(48,27,'48:27','فی الواقع اللہ نے اپنے رسول کو سچا خواب دکھا دیا جو ٹھیک ٹھیک حق کے مطابق تھا۔ انشاء اللہ تم ضرور مسجد حرام میں پورے امن کے ساتھ داخل ہو گے ، اپنے سر منڈواؤ گے اور بال ترشواؤ گے ، اور تمہیں کوئی خوف نہ ہوگا۔ وہ اس بات کو جانتا تھا جسے تم نہ جانتے تھے اس لیے وہ خواب پورا ہونے سے پہلے اس نے یہ قریبی فتح تم کو عطا فرما دی۔');
INSERT INTO "sayyid_qutb" VALUES(48,28,'48:28','وہ اللہ ہی ہے جس نے اپنے رسول صلى الله عليه وسلم کو ہدایت اور دین حق کے ساتھ بھیجا ہے تا کہ اس کو پوری جنس دین پر غالب کر دے اور اس حقیقت پر اللہ کی گواہی کافی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(48,29,'48:29','محمد صلى الله عليه وسلم اللہ کے رسول ہیں اور جو لوگ ان کے ساتھ ہیں وہ کفار پر سخت اور آپس میں رحیم ہیں ۔ تم جب دیکھو گے انہیں رکوع و سجود ، اور اللہ کے فضل اور اس کی خوشنودی کی طلب میں مشغول پاؤ گے۔ سجود کے اثرات ان کے چہروں پر موجود ہیں جن سے وہ الگ پہچانے جاتے ہیں۔ یہ ہے ان کی صفت تورات میں اور انجیل میں ان کی مثال یوں دی گئی ہے کہ گویا ایک کھیتی ہے جس نے پہلے کونپل نکالی ، پھر اس کو تقویت دی ، پھر وہ گدرائی ، پر اپنے تنے پر کھڑی ہوگئی۔ کاشت کرنے والوں کو وہ خوش کرتی ہے تا کہ کفار ان کے پھلنے پھولنے پر جلیں۔ اس گروہ کے لوگ جو ایمان لائے ہیں اور جنہوں نے نیک عمل کئے اللہ نے ان سے مغفرت اور بڑے اجر کا وعدہ فرمایا ہے');
INSERT INTO "sayyid_qutb" VALUES(49,1,'49:1','اے لوگو ، جو ایمان لائے ہو ، اللہ اور اس کے رسول صلى الله عليه وسلم کے آگے پیش قدمی نہ کرو اور اللہ سے ڈرو ، اللہ سب کچھ سننے اور جاننے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,2,'49:2','” اے لوگو ، جو ایمان لائے ہو ، اپنی آواز نبی کی آواز سے بلند نہ کرو ، اور نہ نبی صلى الله عليه وسلم کے ساتھ اونچی آواز میں بات کرو جس طرح تم آپس میں ایک دوسرے سے کرتے ہو۔ کہیں ایسا نہ ہو کہ تمہارا کیا کرایا سب غارت ہوجائے اور تمہیں خبر بھی نہ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(49,3,'49:3','جو لوگ رسول صلى الله عليه وسلم خدا کے حضور بات کرتے ہوئے اپنی آواز پست رکھتے ہیں وہ درحقیقت وہی لوگ ہیں جن کے دلوں کو اللہ نے تقویٰ کے لئے جانچ لیا ہے ، ان کے لئے مغفرت ہے اور اجر عظیم ۔');
INSERT INTO "sayyid_qutb" VALUES(49,4,'49:4','اے نبی صلى الله عليه وسلم ، جو لوگ تمہیں حجروں کے باہر سے پکارتے ہیں ان میں سے اکثر بےعقل ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(49,5,'49:5','اگر وہ تمہارے برآمد ہونے تک صبر کرتے تو انہی کے لئے بہتر تھا ، اللہ درگزر کرنے والا اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,6,'49:6','اے لوگوں ، جو ایمان لائے ہو ، اگر کوئی فاسق تمہارے پاس کوئی خبر لے کر آئے تو تحقیق کرلیا کرو ، کہیں ایسا نہ ہو کہ تم کسی گروہ کو نادانستہ نقصان پہنچا بیٹھو اور پھر اپنے کئے پر پشیمان ہو۔');
INSERT INTO "sayyid_qutb" VALUES(49,7,'49:7','خوب جان رکھو کہ تمہارے درمیان اللہ کا رسول موجود ہے ۔ اگر وہ بہت سے معاملات میں تمہاری بات مان لیا کرے تو تم ۔۔۔۔ ۔ مشکلات میں مبتلا ہوجاؤ۔ مگر اللہ نے تم کو ایمان کی محبت دی اور اس کو تمہارے لیے دل پسند بنا دیا اور کفر و فسق اور نافرمانی سے تم کو متنفر کردیا۔');
INSERT INTO "sayyid_qutb" VALUES(49,8,'49:8','ایسے ہی لوگ اللہ کے فضل و احسان سے راست رو ہیں اور اللہ علیم و حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,9,'49:9','اور اگر اہل ایمان میں سے دو گروہ آپس میں لڑ جائیں تو ان کے درمیان صلح کراؤ۔ پھر اگر ان میں سے ایک گروہ دوسرے گروہ پر زیادتی کرے تو زیادتی کرنے والے سے لڑو یہاں تک کہ وہ اللہ کے حکم کی طرف پلٹ آئے۔ پھر اگر وہ پلٹ آئے تو ان کے درمیان عدل کے ساتھ صلح کرا دو اور انصاف کرو کہ اللہ انصاف کرنے والوں کو پسند کرتا ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(49,10,'49:10','مومن تو ایک دوسرے کے بھائی ہیں ، لہٰذا اپنے بھائیوں کے درمیان تعلقات کو درست کرو اور اللہ سے ڈرو ، امید ہے کہ تم پر رحم کیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(49,11,'49:11','اے لوگوں ، جو ایمان لائے ہو ، نہ مرد دوسرے مردوں کا مذاق اڑائیں ، ہو سکتا ہے کہ وہ ان سے بہتر ہوں اور نہ عورتیں دوسری عورتوں کا مذاق اڑائیں۔ ہو سکتا ہے کہ وہ ان سے بہتر ہوں۔ آپس میں ایک دور سے پر طعن نہ کرو اور نہ ایک دوسرے کو برے القاب سے یاد کرو۔ ایمان لانے کے بعد فسق میں نام پیدا کرنا بہت بری بات ہے۔ جو لوگ اس روش سے باز نہ آئیں وہ ظالم ہیں');
INSERT INTO "sayyid_qutb" VALUES(49,12,'49:12','اے لوگو جو ایمان لائے ہو ، بہت گمان کرنے سے پرہیز کرو کہ بعض گمان گناہ ہوتے ہیں۔ تجسس نہ کرو۔ اور تم میں سے کوئی کسی کی غیبت نہ کرے۔ کیا تمہارے اندر کوئی ایسا ہے جو اپنے مرے ہوئے بھائی کا گوشت کھانا پسند کرے گا ؟ دیکھو ، تم خود اس سے گھن کھاتے ہو۔ اللہ سے ڈرو ، اللہ بڑا توبہ کرنے والا اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,13,'49:13','لوگو ، ہم نے تم کو ایک مرد اور ایک عورت سے پیدا کیا اور پھر تمہاری قومیں اور برادریاں بنا دیں تا کہ تم ایک دوسرے کو پہچانو۔ درحقیقت اللہ کے نزدیک تم میں سب سے زیادہ عزت والا وہ ہے جو تمہارے اندر سب سے زیادہ پرہیز گار ہے۔ یقیناً اللہ سب کچھ جاننے والا اور باخبر ہے');
INSERT INTO "sayyid_qutb" VALUES(49,14,'49:14','یہ بدوی کہتے ہیں کہ “ ہم ایمان لائے ”۔ ان سے کہو ، تم ایمان نہیں لائے ۔۔۔۔۔۔۔ ہم مطیع ہوگئے ”۔ ایمان ابھی تمہارے دلوں میں داخل نہیں ہوا ہے۔ اگر تم اللہ اور اس کے رسول صلى الله عليه وسلم کی فرمانبرداری اختیار کرلو تو وہ تمہارے اعمال کے اجر میں کوئی کمی نہ کرے گا ، یقیناً اللہ بڑا درگزر کرنے والا اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,15,'49:15','حقیقت میں تو مومن وہی ہیں جو اللہ اور اس کے رسول صلى الله عليه وسلم پر ایمان لائے پھر انہوں نے کوئی شک نہ کیا۔ اور اپنی جانوں اور مالوں سے اللہ کی راہ میں جہاد کیا۔ نہ ہی سچے لوگ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(49,16,'49:16','اے نبی صلى الله عليه وسلم ان (مدعیان ایمان ) سے کہو ، کیا تم اللہ کو اپنے دین کی اطلاع دے رہے ہو ؟ حالانکہ اللہ زمین اور آسمانوں کی ہر چیز کو جانتا ہے اور ہر چیز کا علم رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(49,17,'49:17','یہ لوگ تم پر احسان جتاتے ہیں کہ انہوں نے اسلام قبول کرلیا۔ ان سے کہو ، اپنے اسلام کا احسان مجھ پر نہ رکھو ، بلکہ اللہ تم پر اپنا احسان رکھتا ہے کہ اس نے تمہیں ایمان کی ہدایت دی اگر تم واقعی اپنے (دعوائے ایمان میں ) سچے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(49,18,'49:18','اللہ زمین اور آسمانوں کی ہر پوشیدہ چیز کا علم رکھتا ہے اور جو کچھ تم کہتے ہو وہ سب اس کی نگاہ میں ہے');
INSERT INTO "sayyid_qutb" VALUES(50,1,'50:1','ق ، قسم ہے قرآن مجید کی۔');
INSERT INTO "sayyid_qutb" VALUES(50,2,'50:2','بلکہ ان لوگوں کو تعجب اس بات پر ہوا کہ ایک خبردار کرنے والا خود انہی میں سے ان کے پاس آگیا۔ پھر منکرین کہنے لگے : “ یہ تو عجیب بات ہے');
INSERT INTO "sayyid_qutb" VALUES(50,3,'50:3','“ ، کیا جب ہم مرجائیں گے اور خاک ہوجائیں گے (تو دوبارہ اٹھائے جائیں گے) ؟ یہ واپسی تو عقل سے بعید ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,4,'50:4','(حالانکہ) زمین ان کے جسم میں سے جو کچھ کھاتی ہے وہ سب ہماری علم میں ہے اور ہمارے پاس ایک کتاب ہے جس میں سب کچھ محفوظ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,5,'50:5','بلکہ ان لوگوں نے تو جس وقت حق ان کے پاس آیا اسی وقت اسے صاف جھٹلا دیا۔ اسی وجہ سے اب یہ الجھن میں پڑے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(50,6,'50:6','اچھا ، تو کیا انہوں نے کبھی اپنے اوپر آسمان کی طرف نہیں دیکھا ؟ کس طرح ہم نے اسے بنایا اور آراستہ کیا ، اور اس میں کہیں کوئی رخنہ نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,7,'50:7','اور زمین کو ہم نے بچھایا اور اس میں پہاڑ جمائے اور اس کے اندر ہر طرح کی خوش منظر نباتات اگادیں۔');
INSERT INTO "sayyid_qutb" VALUES(50,8,'50:8','یہ ساری چیزیں آنکھیں کھولنے والی اور سبق دینے والی ہیں ہر اس بندے کے لئے جو (حق کی طرف) رجوع کرنے والا ہو');
INSERT INTO "sayyid_qutb" VALUES(50,9,'50:9','اور آسمان سے ہم نے برکت والا پانی نازل کیا ، پھر اس سے باغ اور فصل کے غلے اور بلند وبالا کھجور کے درخت پیدا کر دئیے');
INSERT INTO "sayyid_qutb" VALUES(50,10,'50:10','جن پر پھلوں سے لڈے ہوئے خوشے تہ بہ تہ لگتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(50,11,'50:11','یہ انتظام ہے بندوں کو رزق دینے کا ۔ اس پانی سے ہم ایک مردہ زمین کو زندگی بخش دیتے ہیں ۔ (مرے ہوئے انسانوں کا زمین سے ) نکلنا بھی اسی طرح ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(50,12,'50:12','ان سے پہلے نوح کی قوم ، اور اصحاب الرس اور ثمود');
INSERT INTO "sayyid_qutb" VALUES(50,13,'50:13','اور عاد ، اور فرعون ، اور لوط کے بھائی ،');
INSERT INTO "sayyid_qutb" VALUES(50,14,'50:14','اور ایکہ والے اور تبع کی قوم کے لوگ بھی جھٹلا چکے ہیں۔ ہر ایک نے رسولوں کو جھٹلایا اور آخر کار میری وعیدان پر چسپاں ہوگئی۔');
INSERT INTO "sayyid_qutb" VALUES(50,15,'50:15','کیا پہلی بار کی تخلیق سے ہم عاجز تھے ؟ مگر ایک نئی تخلیق کی طرف سے یہ لوگ شک میں پڑے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(50,16,'50:16','ہم نے انسان کو پیدا کیا ہے اور اس کے دل میں ابھرنے والے وسوسوں تک کو ہم جانتے ہیں۔ ہم اس کی رگ گردن سے بھی زیادہ اس سے قریب ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(50,17,'50:17','(اور ہمارے اس براہ راست علم کے علاوہ ) دو کاتب اس کے دائیں اور بائیں بیٹھے ہر چیز ثبت کر رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(50,18,'50:18','کوئی لفظ اس کی زبان سے نہیں نکلتا جسے محفوظ کرنے کے لے ایک حاضر باش نگراں موجود نہ ہو۔');
INSERT INTO "sayyid_qutb" VALUES(50,19,'50:19','پھر دیکھو ، وہ موت کی جان کنی حق لے کر آپہنچی ، یہ وہی چیز ہے جس سے تو بھاگتا تھا');
INSERT INTO "sayyid_qutb" VALUES(50,20,'50:20','اور پھر صور پھونکا گیا ، یہ ہے وہ دن جس کا تجھے خوف دلایا جاتا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(50,21,'50:21','ہر شخص اس حال میں آگیا کہ اس کے ساتھ ایک ہانک کر لانے والا ہے اور ایک گواہی دینے والا۔');
INSERT INTO "sayyid_qutb" VALUES(50,22,'50:22','اس چیز کی طرف تو غفلت میں تھا ، ہم نے وہ پردہ ہٹا دیا جو تیرے آگے پڑ اہوا تھا اور آج تیری نگاہ خوب تیز ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,23,'50:23','اس کے ساتھی نے عرض کیا یہ جو میری سپردگی میں تھا ، حاضر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,24,'50:24','حکم دیا گیا پھینک دو جہنم میں ہر کٹے کافر کو جو حق سے عناد رکھتا تھا ،');
INSERT INTO "sayyid_qutb" VALUES(50,25,'50:25','خیر کو روکنے والا اور حد سے تجاوز کرنے والا تھا ، شک میں پرا ہوا تھا');
INSERT INTO "sayyid_qutb" VALUES(50,26,'50:26','اور اللہ کے ساتھ کسی دوسرے کو خدا بنائے بیٹھا تھا۔ ڈال دو اسے سخت عذاب میں۔');
INSERT INTO "sayyid_qutb" VALUES(50,27,'50:27','اس کے ساتھی نے عرض کیا ” خداوندا ، میں نے اس کو سرکش نہیں بنایا بلکہ یہ خود ہی پرلے درجے کی گمراہی میں پڑا ہوا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(50,28,'50:28','جو اب میں ارشاد ہوا ” میرے حضور جھگڑا نہ کرو ، میں تم کو پہلے ہی انجام بد سے خبردار کرچکا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(50,29,'50:29','میرے ہاں بات پلٹی نہیں جاتی اور میں اپنے بندوں پر ظلم توڑنے والا نہیں ہوں');
INSERT INTO "sayyid_qutb" VALUES(50,30,'50:30','وہ دن جبکہ ہم جہنم سے پوچھیں گے کیا تو بھر گئی ؟ اور وہ کہے گی کیا اور کچھ ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(50,31,'50:31','اور جنت متقین کے قریب لے آئی جائے گی ، کچھ بھی دور نہ ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(50,32,'50:32','ارشاد ہوگا ” یہ ہے وہ چیز جس کا تم سے وعدہ کیا جاتا تھا ، ہر اس شخص کے لئے جو بہت رجوع کرنے والا ، اور بڑی نگہداشت کرنے والا تھا ،');
INSERT INTO "sayyid_qutb" VALUES(50,33,'50:33','جو بےدیکھے رحمٰن سے ڈرتا تھا ، اور جو دل گرویدہ لیے ہوئے آیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,34,'50:34','داخل ہوجاؤ جنت میں سلامتی کے ساتھ “۔ وہ دن حیات ابدی کا دن ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(50,35,'50:35','وہاں ان کے لئے وہ سب کچھ ہوگا جو وہ چاہیں گے اور ہمارے پاس اس سے زیادہ بھی بہت کچھ ان کے لئے ہے');
INSERT INTO "sayyid_qutb" VALUES(50,36,'50:36','ہم ان سے پہلے بہت سی قوموں کو ہلاک کرچکے ہیں جو ان سے بہت زیادہ طاقتور تھیں اور دنیا کے ملکوں کو انہوں نے چھان مارا تھا۔ پھر کی وہ کوئی جائے پناہ پا سکے ؟');
INSERT INTO "sayyid_qutb" VALUES(50,37,'50:37','اس تاریخ میں اس بات کا سبق ہے ہر اس شخص کے لئے جو دل رکھتا ہو ، یا جو توجہ سے بات کو سنے۔');
INSERT INTO "sayyid_qutb" VALUES(50,38,'50:38','ہم نے زمین اور آسمانوں کو اور ان کے درمیان کی ساری چیزوں کو چھ دنوں میں پیدا کردیا اور ہمیں کوئی تکان لاحق نہ ہوئی۔');
INSERT INTO "sayyid_qutb" VALUES(50,39,'50:39','پس اے نبی صلى الله عليه وسلم ، جو باتیں یہ لوگ بناتے ہیں ان پر صبر کرو ، اور اپنے رب کی حمد کے ساتھ اس کی تسبیح کرتے رہو۔ طلوع آفتاب اور غروب آفتاب سے پہلے');
INSERT INTO "sayyid_qutb" VALUES(50,40,'50:40','اور رات کے وقت پھر اس کی تسبیح کرو اور سجدہ ریزیوں سے فارغ ہونے کے بعد بھی۔');
INSERT INTO "sayyid_qutb" VALUES(50,41,'50:41','اور سنو ، جس دن منادی کرنے والا (ہر شخص کے) قریب ہی سے پکارے گا ،');
INSERT INTO "sayyid_qutb" VALUES(50,42,'50:42','جس دن سب لوگ آوازۂ حشر کو ٹھیک ٹھیک سن رہے ہوں گے ، وہ زمین سے مردوں کے نکلنے کا دن ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(50,43,'50:43','ہم ہی زندگی بخشتے ہیں اور ہم ہی موت دیتے ہیں اور ہماری طرف ہی اس دن سب کو پلٹنا ہے');
INSERT INTO "sayyid_qutb" VALUES(50,44,'50:44','جب زمین پھٹے گی اور لوگ اس کے اندر سے نکل کر تیز تیز بھاگے جا رہے ہوں گے۔ یہ حشر ہمارے لیے بہت آسان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(50,45,'50:45','اے نبی صلى الله عليه وسلم جو باتیں یہ لوگ بنا رہے ہیں انہیں ہم خوب جانتے ہیں ، اور تمہارا کام ان سے جبراً بات منوانا نہیں ہے۔ بس تم اس قرآن کے ذریعہ سے ہر اس شخص کو نصیحت کردو جو میری تنبیہہ سے ڈرے');
INSERT INTO "sayyid_qutb" VALUES(51,1,'51:1','قسم ہے ان ہواؤں کی جو گرد اڑانے والی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(51,2,'51:2','پھر پانی سے لدے ہوئے بادل اٹھانے والی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(51,3,'51:3','پھر قسم ہے سبک رفتاری کے ساتھ چلنے والی کشتیوں کی ،');
INSERT INTO "sayyid_qutb" VALUES(51,4,'51:4','پھر ایک بڑے کام (بارش) کی تقسیم کرنے والوں کی ،');
INSERT INTO "sayyid_qutb" VALUES(51,5,'51:5','حق یہ ہے کہ جس چیز کا تمہیں خوف دلایا جارہا ہے وہ سچی ہے');
INSERT INTO "sayyid_qutb" VALUES(51,6,'51:6','اور جزائے اعمال ضرور پیش آنی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,7,'51:7','قسم ہے بنے ہوئے آسمان کی ،');
INSERT INTO "sayyid_qutb" VALUES(51,8,'51:8','(آخرت کے بارے میں) تمہاری بات ایک دوسرے سے مختلف ہے');
INSERT INTO "sayyid_qutb" VALUES(51,9,'51:9','اس سے وہی برگشتہ ہوتا ہے جو حق سے پھرا ہوا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,10,'51:10','مارے گئے قیاس و گمان سے حکم لگانے والے');
INSERT INTO "sayyid_qutb" VALUES(51,11,'51:11','جو جہالت میں غرق اور غفلت میں مدہوش ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,12,'51:12','پوچھتے ہیں آخر وہ روز جزاء کب آئے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(51,13,'51:13','وہ اس روز آئے گا جب یہ لوگ آگ پر تپائے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(51,14,'51:14','(ان سے کہا جائے گا) اب چکھو مزا اپنے فتنے کا ، یہ وہی چیز ہے جس کے لئے تم جلدی مچا رہے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,15,'51:15','البتہ متقی لوگ اس روز باغوں اور چشموں میں ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(51,16,'51:16','جو کچھ ان کا رب انہیں دے گا اسے خوشی خوشی لے رہے ہوں گے۔ وہ اس دن کے آنے سے پہلے نیکوکار تھے ،');
INSERT INTO "sayyid_qutb" VALUES(51,17,'51:17','راتوں کو کم ہی سوتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(51,18,'51:18','پھر وہی رات کے پچھلے پہروں میں معافی مانگتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(51,19,'51:19','اور ان کے مالوں میں حق تھا سائل اور محروم کے لئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,20,'51:20','زمین میں بہت سی نشانیاں ہیں یقین لانے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(51,21,'51:21','اور خود تمہارے اپنے وجود میں ہیں۔ کیا تم کو سوجھتا نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(51,22,'51:22','آسمان ہی میں ہے تمہارا رزق اور وہ چیز بھی جس کا تم سے وعدہ کیا جارہا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(51,23,'51:23','پس قسم ہے آسمان اور زمین کے مالک کی ، یہ بات حق ہے۔ ایسی ہی یقینی جیسے تم بول رہے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(51,24,'51:24','اے نبی ! ابراہیم کے معزز مہمانوں کی حکایت بھی تمہیں پہنچی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(51,25,'51:25','جب وہ اس کے ہاں آئے تو کہا آپ کو سلام ہے۔ اس نے کہا آپ لوگوں کو بھی سلام ہے .... کچھ ناآشنا سے لوگ ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,26,'51:26','پھر وہ چپکے سے اپنے گھر والوں کے پاس گیا اور ایک بھنا ہوا “ موٹا تازہ بچھڑا');
INSERT INTO "sayyid_qutb" VALUES(51,27,'51:27','لاکر مہمانوں کے آگے پیش کیا۔ اس نے کہا آپ حضرات کھاتے نہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(51,28,'51:28','پھر وہ اپنے دل میں ان سے ارا۔ انہوں نے کہا ڈریئے نہیں اور اسے ایک ذی علم لڑکے کی پیدائش کا مژدہ سنایا۔');
INSERT INTO "sayyid_qutb" VALUES(51,29,'51:29','یہ سن کر اس کی بیوی چیختی ہوئی آگے بڑھی اور اس نے اپنا منہ پیٹ لیا اور کہنے لگی ، بوڑھی ، بانجھ ،');
INSERT INTO "sayyid_qutb" VALUES(51,30,'51:30','انہوں نے کہا یہی کچھ فرمایا ہے تیرے رب نے۔ وہ حکیم ہے اور سب کچھ جانتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,31,'51:31','ابراہیم نے کہا اے فرستاد گان الٰہی ، کیا مہم آپ کو درپیش ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(51,32,'51:32','انہوں نے کہا ہم ایک مجرم قوم کی طرف بھیجے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(51,33,'51:33','تاکہ اس پر پکی ہوئی مٹی کے پتھر برسا دیں');
INSERT INTO "sayyid_qutb" VALUES(51,34,'51:34','جو آپ کے رب کے ہاں حد سے گزر جانے والوں کے لئے نشان زدہ ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,35,'51:35','پھر ہم نے ان سب لوگوں کو نکال لیا جو اس بستی میں مومن تھے');
INSERT INTO "sayyid_qutb" VALUES(51,36,'51:36','اور وہاں ہم نے ایک گھر کے سوا مسلمانوں کا کوئی گھر نہ پایا۔');
INSERT INTO "sayyid_qutb" VALUES(51,37,'51:37','اسکے بعد ہم نے وہاں بس ایک نشانی ان لوگوں کے لئے چھوڑ دی جو درد ناک عذاب سے ڈرتے ہوں۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,38,'51:38','اور تمہارے لئے نشانی ہے موسیٰ کے قصے میں جب ہم نے اسے صریح سند کے ساتھ فرعون کے پاس بھیجا');
INSERT INTO "sayyid_qutb" VALUES(51,39,'51:39','تو وہ اپنے بل بوتے پر اکڑ گیا اور بولا یہ جادوگر ہے یا مجنون ہے۔');
INSERT INTO "sayyid_qutb" VALUES(51,40,'51:40','آخر کار ہم نے اس اور اس کے لشکروں کو پکڑا اور سب کو سمندر میں پھینک دیا اور وہ ملامت زدہ ہوکر رہ گیا۔');
INSERT INTO "sayyid_qutb" VALUES(51,41,'51:41','اور (تمہارے لئے نشانی ہے) عاد میں ، جبکہ ہم نے ان پر ایک ایسی بےخیر ہوا بھیج دی');
INSERT INTO "sayyid_qutb" VALUES(51,42,'51:42','کہ جس چیز پر بھی وہ گزر گئی اسے بوسیدہ کرکے رکھ دیا۔');
INSERT INTO "sayyid_qutb" VALUES(51,43,'51:43','اور (تمہارے لئے نشانی ہے) ثمود میں جب ان سے کہا گیا تھا کہ ایک خاص وقت تک مزے کرلو');
INSERT INTO "sayyid_qutb" VALUES(51,44,'51:44','مگر اس تنبیہ پر بھی انہوں نے اپنے رب کے حکم سے سرتابی کی۔ آخر کار ان کے دیکھتے دیکھتے اچانک ٹوٹ پڑنے والے عذاب نے ان کو آلیا۔');
INSERT INTO "sayyid_qutb" VALUES(51,45,'51:45','پھر نہ ان میں اٹھنے کی سکت تھی اور نہ وہ اپنا بچاؤ کرسکتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(51,46,'51:46','اور ان سب سے سے پہلے ہم نے نوح کی قوم کو ہلاک کیا کیونکہ وہ فاسق لوگ تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(51,47,'51:47','آسمان کو ہم نے اپنے زور سے بنایا ہے اور ہم اس کی وسعت رکھتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,48,'51:48','زمین کو ہم نے بچھایا ہے اور ہم بڑے اچھے ہموار کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(51,49,'51:49','اور ہر چیز کے ہم نے جوڑے بنائے ہیں۔ شاید کہ تم اس سے سبق لو۔');
INSERT INTO "sayyid_qutb" VALUES(51,50,'51:50','پس دوڑو اللہ کی طرف میں تمہارے لئے اس کی طرف سے صاف صاف خبردار کرنے والا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(51,51,'51:51','اور نہ بناؤ اللہ کے ساتھ کوئی دوسرا معبود ، میں تمہارے لئے اس کی طرف سے صاف صاف خبردار کرنے والوں ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(51,52,'51:52','یونہی ہوتا رہا ہے ان سے پہلے کی قوموں کے پاس بھی کوئی رسول ایسا نہیں آیا جسے انہوں نے یہ نہ کہا ہو کہ یہ ساحر ہے یا مجنون۔');
INSERT INTO "sayyid_qutb" VALUES(51,53,'51:53','کیا ان سب نے آپس میں اس پر کوئی سمجھوتہ کرلیا ہے ؟ نہیں بلکہ یہ سب سرکش لوگ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,54,'51:54','پس اے نبی ان سے رخ پھیر لو تم پر کچھ ملامت نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,55,'51:55','البتہ نصیحت کرتے رہو کیونکہ نصیحت ایمان لانے والوں کے لئے نافع ہے۔');
INSERT INTO "sayyid_qutb" VALUES(51,56,'51:56','میں نے جن اور انسانوں کو اس کے سوا کسی کام کے لئے پیدا نہیں کیا ہے کہ وہ میری بندگی کریں۔');
INSERT INTO "sayyid_qutb" VALUES(51,57,'51:57','میں ان سے کوئی رزق نہیں چاہتا اور نہ یہ چاہتا ہوں کہ وہ مجھے کھلائیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,58,'51:58','اللہ تو خود ہی رزاق ہے بڑی قوت والا اور زبردست ۔');
INSERT INTO "sayyid_qutb" VALUES(51,59,'51:59','پس جن لوگوں نے ظلم کیا ہے ان کے حصے کا بھی ویسا ہی عذاب تیار ہے جیسا انہی جیسے لوگوں کو ان کے حصے کا مل چکا ہے ، اس کے لئے یہ لوگ مجھ سے جلدی نہ مچائیں۔');
INSERT INTO "sayyid_qutb" VALUES(51,60,'51:60','آخر کو تباہی ہے کفر کرنے والوں کے لئے اس روز جس کا انہیں خوف دلایا جارہا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(52,1,'52:1','قسم ہے طور کی');
INSERT INTO "sayyid_qutb" VALUES(52,2,'52:2','اور ایک ایسی کھلی کتاب کی');
INSERT INTO "sayyid_qutb" VALUES(52,3,'52:3','جو رقیق جلد میں لکھی ہوئی ہے');
INSERT INTO "sayyid_qutb" VALUES(52,4,'52:4','اور آباد گھر کی');
INSERT INTO "sayyid_qutb" VALUES(52,5,'52:5','اور اونچی چھت کی');
INSERT INTO "sayyid_qutb" VALUES(52,6,'52:6','اور موجزن سمندر کی');
INSERT INTO "sayyid_qutb" VALUES(52,7,'52:7','کہ تیرے رب کا عذاب ضرور واقع ہونے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(52,8,'52:8','جسے کوئی دفع کرنے والا نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(52,9,'52:9','وہ اس روز واقع ہوگا جب آسمان بری طرح ڈگمگائے گا');
INSERT INTO "sayyid_qutb" VALUES(52,10,'52:10','اور پہاڑ اڑاے اڑے پھریں گے۔');
INSERT INTO "sayyid_qutb" VALUES(52,11,'52:11','تباہی ہے اس روز ان جھٹلانے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(52,12,'52:12','جو آج کھیل کے طور پر اپنی حجت بازیوں میں لگے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(52,13,'52:13','جس دن انہیں دھکے مار مار کر نار جہنم کی طرف لے چلاجائے گا');
INSERT INTO "sayyid_qutb" VALUES(52,14,'52:14','اس وقت ان سے کہا جائے گا کہ یہ وہی آگ ہے جسے تم جھٹلایا کرتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(52,15,'52:15','اب بتاؤ، یہ جادو ہے یا تمہیں سوجھ نہیں رہا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(52,16,'52:16','جاؤ اب جھلسو اس کے اندر تم خواہ صبر کرو یا نہ کرو ، تمہارے لئے یکساں ہے تمہیں ویسا ہی بدلہ دیا جارہا ہے جیسے تم عمل کررہے تھے۔ “');
INSERT INTO "sayyid_qutb" VALUES(52,17,'52:17','متقی لوگ وہاں باغوں اور نعمتوں میں ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(52,18,'52:18','لطف لے رہے ہوں گے ان چیزوں سے جو ان کا رب انہیں دے گا اور ان کا رب انہیں دوزخ کا عذاب سے بچا لے گا۔');
INSERT INTO "sayyid_qutb" VALUES(52,19,'52:19','ان سے کہا جائے گا کھاؤ اور پیو مزے سے اپنے ان اعمال کے صلے میں جو تم کرتے رہے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(52,20,'52:20','وہ آمنے سامنے بچھے ہوئے تختوں پر تکئے لگائے بیٹھے ہوں گے اور ہم خوبصورت آنکھوں والی حوریں ان سے بیاہ دیں گے');
INSERT INTO "sayyid_qutb" VALUES(52,21,'52:21','جو لوگ ایمان لائے ہیں اور ان کی اولاد بھی کسی درجہ ایمان میں ان کے نقش قدم پر چلی ہے ان کی اس اولاد کو بھی ہم (جنت میں) ان کے ساتھ ملادیں گے اور ان کے عمل میں کوئی گھاٹا ان کو نہ دیں گے۔ ہر شخص اپنے کسب کے عوض رہن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(52,22,'52:22','ہم ان کو ہر طرح کے پھل اور گوشت جس چیز کو بھی ان کا جی چاہے گا خوب دیئے چلے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(52,23,'52:23','وہ ایک دوسرے سے جام شراب لپک لپک کرلے رہے ہوں گے جس میں نہ یا وہ گوئی ہوگی نہ بدکرداری ا');
INSERT INTO "sayyid_qutb" VALUES(52,24,'52:24','ور ان کی خدمت میں وہ لڑکے دوڑتے پھر رہے ہوں گے جو انہی (کی خدمت) کے لئے مخصوص ہوں گے ایسے خوبصورت جیسے چھپا کر رکھے ہوئے موتی۔');
INSERT INTO "sayyid_qutb" VALUES(52,25,'52:25','یہ لوگ آپس میں ایک دوسرے سے (دنیا میں گزرے ہوئے) حالات پوچھیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(52,26,'52:26','یہ کہیں گے کہ ہم پہلے اپنے گھر والوں میں ڈرتے ہوئے زندگی بسر کرتے تھے');
INSERT INTO "sayyid_qutb" VALUES(52,27,'52:27','آخر کار اللہ نے ہم پر فضل فرمایا اور ہمیں جھلسا دینے والی ہوا کے عذاب سے بچا لیا۔');
INSERT INTO "sayyid_qutb" VALUES(52,28,'52:28','ہم پچھلی زندگی میں اسی سے دعائیں مانگتے تھے وہ واقعی بڑا ہی محسن اور رحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(52,29,'52:29','اے نبی تم نصیحت کئے جاؤ، اپنے رب کے فضل سے نہ تم کاہن ہو اور نہ مجنون۔');
INSERT INTO "sayyid_qutb" VALUES(52,30,'52:30','کیا یہ لوگ کہتے ہیں کہ یہ شخص شاعر ہے جس کے حق میں ہم گردش ایام کا انتظار کررہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(52,31,'52:31','ان سے کہو اچھا انتظار کرو میں بھی تمہارے ساتھ انتظار کرتا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(52,32,'52:32','کیا ان کی عقلیں انہیں ایسی ہی باتیں کرنے کے لئے کہتی ہیں ؟ یا درحقیقت یہ عناد میں حد سے گزرے ہوئے لوگ ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(52,33,'52:33','کیا یہ کہتے ہیں کہ اس شخص نے یہ قرآن خود گھڑ لیا ہے ؟ اصل بات یہ ہے کہ یہ ایمان نہیں لانا چاہتے');
INSERT INTO "sayyid_qutb" VALUES(52,34,'52:34','اگر یہ اپنے اس قول میں سچے ہیں تو اسی شان کا ایک کلام بنالائیں۔');
INSERT INTO "sayyid_qutb" VALUES(52,35,'52:35','کیا یہ کسی خالق کے بغیر خود پیدا ہوگئے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(52,36,'52:36','یا یہ خود اپنے خالق ہیں یا زمین اور آسمان کو انہوں نے پیدا کیا ہے ؟ اصل بات یہ ہے کہ یہ یقین نہیں رکھتے۔');
INSERT INTO "sayyid_qutb" VALUES(52,37,'52:37','کیا تیرے رب کے خزانے ان کے قبضے میں ہیں ؟ یا ان پر انہی کا حکم چلتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(52,38,'52:38','کیا ان کے پاس کوئی سیڑھی ہے جس پر چڑھ کر یہ عالم بالا کی سن گن لیتے ہیں ؟ ان میں سے جس نے سن گن لی ہو وہ لائے کوئی کھلی دلیل۔');
INSERT INTO "sayyid_qutb" VALUES(52,39,'52:39','کیا اللہ کے لئے تو ہیں بیٹیاں اور تم لوگوں کے ہیں بیٹے ؟');
INSERT INTO "sayyid_qutb" VALUES(52,40,'52:40','کیا تم ان سے کوئی اجر مانگتے ہو کہ یہ زبردستی پڑی ہوئی چٹی کے بوجھ تلے دبے جاتے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(52,41,'52:41','کیا ان کے پاس غیب کے حقائق کا علم ہے کہ اس کی بنا پر لکھ رہے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(52,42,'52:42','کیا یہ کوئی چال چلنا چاہتے ہیں ؟ اگر یہ بات ہے تو کفر کرنے والوں پر ان کی چال الٹی ہی پڑے گی۔');
INSERT INTO "sayyid_qutb" VALUES(52,43,'52:43','کیا اللہ کے سوا یہ کوئی اور معبود رکھتے ہیں ؟ اللہ پاک ہے اس شرک سے جو یہ لوگ کررہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(52,44,'52:44','یہ لوگ آسمان کے ٹکڑے بھی گرتے ہوئے دیکھ لیں تو کہیں گے یہ بادل ہیں جو امڈے چلے آرہے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(52,45,'52:45','پس اے نبی انہیں ان کے حال پر چھوڑ دو یہاں تک کہ یہ اپنے اس دن کو پہنچ جائیں جس میں یہ مار گرائے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(52,46,'52:46','جس میں یہ مار گرائے جائیں گے جس دن نہ ان کی اپنی کوئی چال ان کے کسی کام آئے گی نہ کوئی ان کی مدد کو آئے گا');
INSERT INTO "sayyid_qutb" VALUES(52,47,'52:47','اور اس وقت کے آنے سے پہلے بھی ظالموں کے لئے ایک عذاب ہے مگر ان میں سے اکثر جانتے نہیں ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(52,48,'52:48','اے نبی اپنے رب کا فیصلہ آنے تک صبر کرو ، تم ہماری نگاہ میں ہو تم جب اٹھو تو اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو ،');
INSERT INTO "sayyid_qutb" VALUES(52,49,'52:49','رات کو بھی اس کی تسبیح کیا کرو اور ستارے جب پلٹتے ہیں اس وقت بھی۔');
INSERT INTO "sayyid_qutb" VALUES(53,1,'53:1','قسم ہے تارے کی جب کہ وہ غروب ہوا۔');
INSERT INTO "sayyid_qutb" VALUES(53,2,'53:2','تمہارا رفیق نہ بھٹکا ہے نہ بہکا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,3,'53:3','وہ اپنی خواہش نفس سے نہیں بولتا۔');
INSERT INTO "sayyid_qutb" VALUES(53,4,'53:4','یہ تو ایک وحی ہے جو اس پر نازل کی جاتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,5,'53:5','اسے زبردست قوت والے نے تعلیم دی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,6,'53:6','جو بڑا صاحب حکمت ہے۔ وہ سامنے آکھڑا ہوا');
INSERT INTO "sayyid_qutb" VALUES(53,7,'53:7','جبکہ وہ بالائی افق پر تھا۔');
INSERT INTO "sayyid_qutb" VALUES(53,8,'53:8','پھر قریب آیا اور اوپر معلق ہوگیا۔');
INSERT INTO "sayyid_qutb" VALUES(53,9,'53:9','یہاں تک کہ دو کمانوں کے برابر یا اس سے کچھ کم فاصلہ رہ گیا۔');
INSERT INTO "sayyid_qutb" VALUES(53,10,'53:10','تب اس نے اللہ کے بندے کو وحی پہنچاتی جو وحی بھی اسے پہنچائی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(53,11,'53:11','نظر نے جو کچھ دیکھا ، دل نے اس میں جھوٹ نہ ملایا۔');
INSERT INTO "sayyid_qutb" VALUES(53,12,'53:12','اب کیا تم اس چیز پر اس سے جھگڑتے ہو جسے وہ آنکھوں سے دیکھتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(53,13,'53:13','اور ایک مرتبہ پھر اس نے اس کو اترتے دیکھا');
INSERT INTO "sayyid_qutb" VALUES(53,14,'53:14','سدرة المنتہیٰ کے پاس');
INSERT INTO "sayyid_qutb" VALUES(53,15,'53:15','جہاں پاس ہی جنت الماویٰ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,16,'53:16','اس وقت سدرہ پر چھارہا تھا جو کچھ کہ چھارہا تھا');
INSERT INTO "sayyid_qutb" VALUES(53,17,'53:17','نگاہ نہ چندھیائی نہ حد سے متجاوز ہوئی');
INSERT INTO "sayyid_qutb" VALUES(53,18,'53:18','اور اس نے اپنے رب کی بڑی بڑی نشانیاں دیکھیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(53,19,'53:19','اب ذرا بتاؤ تم نے کبھی اس لات اور اس عزی');
INSERT INTO "sayyid_qutb" VALUES(53,20,'53:20','اور تیسری ایک دیوی منات کی حقیقت پر کچھ غور بھی کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(53,21,'53:21','کیا بیٹے تمہارے لئے ہیں اور بیٹیاں خدا کے لئے ؟');
INSERT INTO "sayyid_qutb" VALUES(53,22,'53:22','یہ تو پھر بڑی دھاندلی کی تقسیم ہوئی۔');
INSERT INTO "sayyid_qutb" VALUES(53,23,'53:23','دراصل یہ کچھ نہیں ہیں مگر بس چند نام جو تم نے اور تمہارے باپ دادا نے رکھ لئے ہیں اللہ نے ان کے لئے کوئی سند نازل نہیں کی۔ حقیقت یہ ہے کہ لوگ محض وہم و گمان کی پیروی کررہے ہیں اور خواہشات نفس کے مرید بنے ہوئے ہیں۔ حالانکہ ان کے رب کی طرف سے ان کے پاس ہدایت آچکی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,24,'53:24','کیا انسان جو کچھ چاہے اس کے لئے وہی حق ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(53,25,'53:25','دنیا اور آخرت کا مالک تو اللہ ہی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,26,'53:26','آسمانوں میں کتنے ہی فرشتے موجود ہیں۔ ان کی شفاعت کچھ بھی کام نہیں آسکتی۔ جب تک کہ اللہ کسی ایسے شخص کے حق میں اس کی اجازت نہ دے جس کے لئے وہ کوئی عرض داشت سننا چاہے اور اس کو پسند کرے');
INSERT INTO "sayyid_qutb" VALUES(53,27,'53:27','مگر جو لوگ آخرت کو نہیں مانتے وہ فرشتوں کو دیویوں کے ناموں سے موسوم کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(53,28,'53:28','حالانکہ اس معاملہ کا کوئی علم انہیں حاصل نہیں ہے وہ محض گمان کی پیروی کررہے ہیں اور گمان حق کی جگہ کچھ بھی کام نہیں دے سکتا۔');
INSERT INTO "sayyid_qutb" VALUES(53,29,'53:29','پس اے نبی جو شخص ہمارے ذکر سے منہ پھیرتا ہے اور دنیا کی زندگی کے سوا جسے کچھ مطلوب نہیں ہے اسے اس کے حال پر چھوڑ دو ۔');
INSERT INTO "sayyid_qutb" VALUES(53,30,'53:30','ان لوگوں کا مبلغ علم بس یہی کچھ ہے۔ یہ بات تیرا رب ہی زیادہ جانتا ہے کہ اس کے راستے سے کون بھٹک گیا ہے اور کون سیدھے راستے پر ہے');
INSERT INTO "sayyid_qutb" VALUES(53,31,'53:31','اور زمین اور آسمانوں کی ہر چیز کا مالک اللہ ہی ہے۔ لا کہ اللہ برائی کرنے والوں کو ان کے عمل کا بدلہ دے اور ان لوگوں کو اچھی جزا سے نوازے جنہوں نے نیک رویہ اختیار کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(53,32,'53:32','جو بڑے بڑے گناہوں اور کھلے کھلے قبیح افعال سے پرہیز کرتے ہیں۔ الایہ کہ کچھ قصور ان سے سرزد ہوجائے۔ بلاشبہ تیرے رب کا دامن مغفرت بہت وسیع ہے۔ وہ تمہیں اس وقت سے خوب جانتا ہے جب اس نے زمین سے تمہیں پیدا کیا اور جب تم اپنی ماؤں کے پیٹوں میں ابھی جنین ہی تھے۔ پس اپنے نفس کی پاکی کے دعوے نہ کرو۔ وہی بہتر جانتا ہے کہ واقعی متقی کون ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(53,33,'53:33','پھر اے نبی تم نے اس شخص کو بھی دیکھا جو راہ خدا سے پھر گیا');
INSERT INTO "sayyid_qutb" VALUES(53,34,'53:34','اور تھوڑا سا دے کر رک گیا۔');
INSERT INTO "sayyid_qutb" VALUES(53,35,'53:35','کیا اس کے پاس غیب کا علم ہے کہ وہ حقیقت کو دیکھ رہا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(53,36,'53:36','کیا اسے ان باتوں کی کوئی خبر نہیں پہنچی جو موسیٰ کے صحیفوں');
INSERT INTO "sayyid_qutb" VALUES(53,37,'53:37','اور اس ابراہیم کے صحیفوں میں بیان ہوئی ہیں جس نے وفا کا حق ادا کردیا ؟');
INSERT INTO "sayyid_qutb" VALUES(53,38,'53:38','یہ کہ کوئی بوجھ اٹھانے والا دوسرے کا بوجھ نہیں اٹھائے گا');
INSERT INTO "sayyid_qutb" VALUES(53,39,'53:39','اور یہ کہ انسان کے لئے کچھ نہیں ہے مگر وہ جس کی اس نے سعی کی ہے');
INSERT INTO "sayyid_qutb" VALUES(53,40,'53:40','اور یہ کہ اس کی سعی عنقریب دیکھی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(53,41,'53:41','پھر اس کی پوری جزا اسے دی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(53,42,'53:42','اور یہ کہ آخر کار پہنچنا تیرے رب ہی کے پاس ہے');
INSERT INTO "sayyid_qutb" VALUES(53,43,'53:43','اور یہ کہ اسی نے ہنسایا اور اسی نے رلایا ہے');
INSERT INTO "sayyid_qutb" VALUES(53,44,'53:44','اور یہ کہ اسی نے موت دی اور اسی نے زندگی بخشی');
INSERT INTO "sayyid_qutb" VALUES(53,45,'53:45','اور یہ کہ اسی نے نر اور مادہ کا جوڑا پیدا کیا۔');
INSERT INTO "sayyid_qutb" VALUES(53,46,'53:46','ایک بوند سے جب وہ ٹپکائی جاتی ہے');
INSERT INTO "sayyid_qutb" VALUES(53,47,'53:47','اور یہ کہ دوسری زندگی بخشنا بھی اسی کے ذمہ ہے');
INSERT INTO "sayyid_qutb" VALUES(53,48,'53:48','اور یہ کہ اسی نے غنی کیا اور جائیداد بخشی');
INSERT INTO "sayyid_qutb" VALUES(53,49,'53:49','اور یہ کہ وہی شعریٰ کا رب ہے');
INSERT INTO "sayyid_qutb" VALUES(53,50,'53:50','اور یہ کہ اسی نے عاداولیٰ کو ہلاک کیا');
INSERT INTO "sayyid_qutb" VALUES(53,51,'53:51','اور ثمود کو ایسا مٹایا کہ ان میں سے کسی کو باقی نہ چھوڑا');
INSERT INTO "sayyid_qutb" VALUES(53,52,'53:52','اور ان سے پہلے قوم نوح کو تباہ کیا کیونکہ وہ تھے ہی سخت ظالم و سرکش لوگ');
INSERT INTO "sayyid_qutb" VALUES(53,53,'53:53','اور اوندھی گرنے والی بستیوں کو اٹھا پھینکا ،');
INSERT INTO "sayyid_qutb" VALUES(53,54,'53:54','پھر چھا دیا ان پر وہ کچھ جو (تم جانتے ہی ہو کہ) کیا چھا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(53,55,'53:55','پس اے انسان ، اپنے رب کی کن کن نعمتوں میں تو شک کرے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(53,56,'53:56','یہ ایک تنبیہ ہے پہلے آئی ہوئی تنبیات میں سے۔');
INSERT INTO "sayyid_qutb" VALUES(53,57,'53:57','آنے والی گھڑی قریب آلگی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(53,58,'53:58','اللہ کے سوا کوئی اس کو ہٹانے والا نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(53,59,'53:59','اب کیا یہی وہ باتیں ہیں جن پر تم اظہار تعجب کرتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(53,60,'53:60','ہنستے ہو اور روتے نہیں ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(53,61,'53:61','اور گا بجا کر انہیں ٹالتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(53,62,'53:62','جھک جاؤ اللہ کے آگے اور بندگی بجا لاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(54,1,'54:1','قیامت کی گھڑی قریب آگئی اور چاند پھٹ گیا');
INSERT INTO "sayyid_qutb" VALUES(54,2,'54:2','مگر ان لوگوں کا حال یہ ہے کہ خواہ کوئی نشانی دیکھ لیں منہ موڑ جاتے ہیں اور کہتے ہیں یہ تو چلتا ہوا جادو ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,3,'54:3','انہوں نے (اس کو بھی) جھٹلا دیا اور اپنی خواہشات نفس ہی کی پیروی کی۔ ہر معاملہ کو آخر کار ایک انجام پر پہنچ کر رہنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,4,'54:4','ان لوگوں کے سامنے (پچھلی قوموں کے) وہ حالات آچکے ہیں جن میں سرکشی سے باز رکھنے کے لئے کافی سامان عبرت ہے');
INSERT INTO "sayyid_qutb" VALUES(54,5,'54:5','اور ایسی حکمت جو نصیحت کے مقصد کو بدرجہ اتم پورا کرتی ہے مگر تنبیہات ان پر کارگر نہیں ہوتیں۔');
INSERT INTO "sayyid_qutb" VALUES(54,6,'54:6','پس اے نبی ان سے رخ پھیر لو جس روز پکارنے والا سخت ناگوار چیز کی طرف پکارے گا');
INSERT INTO "sayyid_qutb" VALUES(54,7,'54:7','لوگ سہمی ہوئی نگاہوں کے ساتھ اپنی قبروں سے اس طرح نکلیں گے گویا وہ بکھری ہوئی ٹڈیاں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(54,8,'54:8','پکارنے والے کی طرف دوڑے جارہے ہوں گے اور ہی منکرین (جو دنیا میں اس کا انکار کرتے تھے) اس وقت کہیں گے کہ یہ دن تو بڑا کٹھن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,9,'54:9','ان سے پہلے نوح کی قوم جھٹلاچ کی ہے۔ انہوں نے ہمارے بندے کو جھوٹا قرار دیا اور کہا کہ یہ دیوانہ ہے اور وہ بری طرح جھڑکا گیا۔');
INSERT INTO "sayyid_qutb" VALUES(54,10,'54:10','آخر کار اس نے اپنے رب کو پکارا کہ میں مغلوب ہوچکا اب تو ان سے انتقام لے۔');
INSERT INTO "sayyid_qutb" VALUES(54,11,'54:11','“ تب ہم نے موسلادھار بارش سے آسمان کے دروازے کھول دیئے');
INSERT INTO "sayyid_qutb" VALUES(54,12,'54:12','اور زمین کو پھاڑ کر چشموں میں تبدیل کردیا اور یہ سارا پانی اس کام کو پورا کرنے کے لئے مل گیا جو مقدر ہوچکا تھا');
INSERT INTO "sayyid_qutb" VALUES(54,13,'54:13','اور نوح کو ہم نے ایک تختوں اور کیلوں والی پر سوار کر دیا');
INSERT INTO "sayyid_qutb" VALUES(54,14,'54:14','جو ہماری نگرانی میں چل رہی تھی۔ یہ تھا بدلہ اس شخص کی خاطر جس کی ناقدری کی گئی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(54,15,'54:15','اس کشتی کو ہم نے ایک نشانی بنا کر چھوڑ دیا۔ پھر کوئی ہے نصیحت قبول کرنے والا ؟');
INSERT INTO "sayyid_qutb" VALUES(54,16,'54:16','دیکھ لو کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات۔');
INSERT INTO "sayyid_qutb" VALUES(54,17,'54:17','ہم نے اس قرآن کو نصیحت کے لئے آسان ذریعہ بنا دیا ہے۔ پھر کیا ہے کوئی نصیحت قبول کرنے والا ؟');
INSERT INTO "sayyid_qutb" VALUES(54,18,'54:18','عاد نے جھٹلایا تو دیکھ لو کہ کیسا تھا میرا عذاب اور کیسی تھیں میری تنبہیات۔');
INSERT INTO "sayyid_qutb" VALUES(54,19,'54:19','ہم نے ایک پیہم نحوست کے دن سخت طوفانی ہوا ان پر بھیج دی۔');
INSERT INTO "sayyid_qutb" VALUES(54,20,'54:20','جو لوگوں کو اٹھا اٹھا کر اس طرح پھینک رہی تھی جیسے وہ جڑ سے اکھڑے ہوئے کھجور کے تنے ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(54,21,'54:21','پس دیکھ لو کیسا تھا میرا عذاب اور کیسی تھی میری تنبیہات۔');
INSERT INTO "sayyid_qutb" VALUES(54,22,'54:22','ہم نے اس قرآن کو نصیحت کے لئے آسان ذریعہ بنا دیا ہے۔ پھر کیا ہے کوئی نصیحت قبول کرنے والا “ ؟');
INSERT INTO "sayyid_qutb" VALUES(54,23,'54:23','ثمود نے تنبیہات کو جھٹلایا');
INSERT INTO "sayyid_qutb" VALUES(54,24,'54:24','اور کہنے لگے۔ ایک اکیلا آدمی جو ہم ہی میں سے ہے ، کیا اب ہم اس کے پیچھے چلیں ؟ اس کا اتباع ہم قبول کرلیں تو اس کے معنی یہ ہوں گے کہ ہم بہک گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(54,25,'54:25','اور ہماری عقل ماری گئی ہے۔ کیا ہمارے درمیان بس یہی ایک شخص تھا جس پر خدا کا ذکر نازل کیا گیا ؟ نہیں بلکہ یہ پرلے درجے کا جھوٹا اور لالچی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,26,'54:26','(ہم نے اپنے پیغمبر سے کہا) کل ہی انہیں معلوم ہوا جاتا ہے کہ کون پرلے درجے کا جھوٹا اور لالچی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,27,'54:27','ہم اونٹنی کو ان کے لئے فتنہ بنا کر بھیج رہے ہیں۔ اب ذرا صبر کے ساتھ دیکھ کر ان کا کیا انجام ہوتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,28,'54:28','ان کو جتادے کہ پانی ان کے اور اونٹنی کے درمیان تقسیم ہوگا اور ہر ایک اپنی باری کے دن پانی پر آئے گا۔ “');
INSERT INTO "sayyid_qutb" VALUES(54,29,'54:29','آخر کار ان لوگوں نے اپنے آدمی کو پکارا اور اس نے اس کام کا بیڑا اٹھایا اور اونٹنی کو مار ڈالا۔');
INSERT INTO "sayyid_qutb" VALUES(54,30,'54:30','پھر دیکھ لو کہ کیسا تھا میرا عذاب اور کیسی تھیں میری تنبیہات۔');
INSERT INTO "sayyid_qutb" VALUES(54,31,'54:31','ہم نے ان پر بس ایک ہی دھماکہ چھوڑا اور وہ باڑے والے کی روندی ہوئی باڑھ کی طرح بھسن ہوکر رہ گئے۔');
INSERT INTO "sayyid_qutb" VALUES(54,32,'54:32','ہم نے اس قرآن کو نصیحت کے لئے آسان ذریعہ بنادیا ہے۔ اب ہے کوئی نصیحت قبول کرنے والا “ ؟');
INSERT INTO "sayyid_qutb" VALUES(54,33,'54:33','لوط کی قوم نے تنبیہات کو جھٹلایا');
INSERT INTO "sayyid_qutb" VALUES(54,34,'54:34','اور ہم نے پتھراؤ کرنے والی ہوا اس پر بھیج دی۔ صرف لوط کے گھر والے اس سے محفوظ رہے۔ ان کو ہم نے اپنے فضل سے رات کے پچھلے پہر بچا کر نکال دیا۔');
INSERT INTO "sayyid_qutb" VALUES(54,35,'54:35','یہ جزا دیتے ہیں ہم ہراس شخص کو جو شکر گزار ہوتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,36,'54:36','لوط نے اپنی قوم کے لوگوں کو ہماری پکڑ سے خبردار کیا مگر وہ ساری تنبیہات کو مشکوک سمجھ کر باتوں میں اڑاتے رہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,37,'54:37','پھر انہوں نے اسے اپنے مہمانوں کی حفاظت سے باز رکھنے کی کوشش کی۔ آخر کار ہم نے ان کی آنکھیں موند دیں کہ چکھو اب میرے عذاب اور میری تنبیہات کا۔');
INSERT INTO "sayyid_qutb" VALUES(54,38,'54:38','صبح سویرے ایک اٹل عذاب نے انکو آن لیا۔');
INSERT INTO "sayyid_qutb" VALUES(54,39,'54:39','چکھو اب میرے عذاب اور میری تنبیہات کا مزہ “');
INSERT INTO "sayyid_qutb" VALUES(54,40,'54:40','ہم نے اس قرآن کو نصیحت کے لئے آسان ذریعہ بنادیا ہے۔ پس ہے کوئی نصیحت قبول کرنے والا “ ؟');
INSERT INTO "sayyid_qutb" VALUES(54,41,'54:41','اور آل فرعون کے پاس بھی تنبیہات آئی تھیں');
INSERT INTO "sayyid_qutb" VALUES(54,42,'54:42','مگر انہوں نے ہماری ساری نشانیوں کو جھٹلا دیا۔ آخر کو ہم نے انہیں پکڑا جس طرح کوئی زبردست قدرت والا پکڑا کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,43,'54:43','کیا تمہارے کفار کچھ ان لوگوں سے بہتر ہیں ؟ یا آسمانی کتابوں میں تمہارے لئے کوئی معافی لکھی ہوئی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(54,44,'54:44','یا ان لوگوں کا کہنا یہ ہے کہ ہم ایک مضبوط جتھا ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(54,45,'54:45','اپنا بچاؤ کرلیں گے ؟ عنقریب یہ جتھا شکست کھا جائے گا اور یہ سب پیٹھ پھیر کر بھاگتے نظر آئیں گے');
INSERT INTO "sayyid_qutb" VALUES(54,46,'54:46','بلکہ ان سے نمٹنے کے لئے اصل وعدے کا وقت تو قیامت ہے اور وہ بڑی آفت اور زیادہ تلخ ساعت ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,47,'54:47','یہ مجرم لوگ در حقیقت غلط فہمی میں مبتلا ہیں اور ان کی عقل ماری گئی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,48,'54:48','جس روز یہ منہ کے بل آگ میں گھسیٹے جائیں گے ، اس روز ان سے کہا جائے گا کہ اب چکھو جہنم کی لپٹ کا مزا۔');
INSERT INTO "sayyid_qutb" VALUES(54,49,'54:49','ہم نے ہر چیز تقدیر کے ساتھ پیدا کی ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(54,50,'54:50','ہمارا حکم بس ایک ہی حکم ہوتا ہے اور پلک جھپکائے وہ عمل میں آجاتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,51,'54:51','اور تم جیسے بہت سوں کو ہم ہلاک کرچکے ہیں ، پھر ہے کوئی نصیحت قبول کرنے والا ؟');
INSERT INTO "sayyid_qutb" VALUES(54,52,'54:52','انہوں نے جو کچھ بھی کیا ہے دفتروں میں درج ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,53,'54:53','ہر چھوٹی بڑی بات لکھی ہوئی موجود ہے۔');
INSERT INTO "sayyid_qutb" VALUES(54,54,'54:54','نافرمانی سے پرہیز کرنے والے یقینا باغوں اور نہروں میں ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(54,55,'54:55','سچی عزت کی جگہ ، بڑے ذی اقتدار بادشاہ کے قریب۔ “');
INSERT INTO "sayyid_qutb" VALUES(55,1,'55:1','نہایت مہربان');
INSERT INTO "sayyid_qutb" VALUES(55,2,'55:2','(خدا) نے اس قرآن کی تعلیم دی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(55,3,'55:3','اسی نے انسان کو پیدا کیا');
INSERT INTO "sayyid_qutb" VALUES(55,4,'55:4','اور اسے بولنا سکھایا۔');
INSERT INTO "sayyid_qutb" VALUES(55,5,'55:5','سورج اور چاند ایک حساب کے پابند ہیں');
INSERT INTO "sayyid_qutb" VALUES(55,6,'55:6','اور تارے اور درخت سب سجدہ ریز ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(55,7,'55:7','آسمان کو اس نے بلند کیا اور میزان قائم کردی۔');
INSERT INTO "sayyid_qutb" VALUES(55,8,'55:8','اس کا تقاضا یہ ہے کہ تم میزان میں خلل نہ ڈالو ،');
INSERT INTO "sayyid_qutb" VALUES(55,9,'55:9','انصاف کے ساتھ ٹھیک ٹھیک تولو اور ترازو میں ڈنڈی نہ مارو۔');
INSERT INTO "sayyid_qutb" VALUES(55,10,'55:10','زمین کو اس نے سب مخلوقات کے لئے بنایا۔');
INSERT INTO "sayyid_qutb" VALUES(55,11,'55:11','اس میں ہر طرح کے بکثرت لذیذ پھل ہیں۔ کھجور کے درخت ہیں جن کے پھل غلافوں میں لپٹے ہوئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(55,12,'55:12','۔ طرح طرح کے غلے ہیں جن میں بھوسا بھی ہوتا ہے اور دانہ بھی۔');
INSERT INTO "sayyid_qutb" VALUES(55,13,'55:13','پس اے جن وانس ، تم اپنے رب کی کن کن نعمتوں کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,14,'55:14','انسان کو اس نے ٹھیکری جیسے سوکھے سڑے گارے سے بنایا');
INSERT INTO "sayyid_qutb" VALUES(55,15,'55:15','اور جن کو آگ کی لپٹ سے پیدا کیا۔');
INSERT INTO "sayyid_qutb" VALUES(55,16,'55:16','پس اے جن وانس ، تم اپنے رب کے کن کن عجائب قدرت کو جھٹلاؤ گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(55,17,'55:17','دونوں مشرق اور دونوں مغرب ، سب کا مالک و پروردگار وہی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(55,18,'55:18','پس اے جن وانس ، تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,19,'55:19','دو سمندروں کو اس نے چھوڑ دیا کہ بہم مل جائیں');
INSERT INTO "sayyid_qutb" VALUES(55,20,'55:20','پھر بھی ان کے درمیان ایک پردہ حائل ہے جس سے وہ تجاوز نہیں کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(55,21,'55:21','پس اے جن وانس ، تم اپنے رب کی قدرت کے کن کن کرشموں کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,22,'55:22','ان سمندروں سے موتی اور مونگے نکلتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(55,23,'55:23','پس اے جن وانس ، تم اپنے رب کی قدرت کے کن کن کمالات کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,24,'55:24','اور یہ جہاز اسی کے ہیں جو سمندر میں پہاڑوں کی طرح اونچے اٹھے ہوئے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(55,25,'55:25','پس اے جن وانس ، تم اپنے رب کے کن کن احسانات کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,26,'55:26','ہر چیز جو اس زمین پر ہے فنا ہوجانے والی ہے');
INSERT INTO "sayyid_qutb" VALUES(55,27,'55:27','اور صرف تیرے رب کی جلیل وکریم ذات ہی باقی رہنے والی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(55,28,'55:28','پس اے جن وانس ، تم اپنے رب کے کن کن کمالات کو جھٹلاؤ گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(55,29,'55:29','زمین اور آسمانوں میں جو بھی ہیں سب اپنی حاجتیں اسی سے مانگ رہے ہیں۔ ہر آن وہ نئی شان میں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(55,30,'55:30','پس اے جن وانس ، تم اپنے رب کی کن کن صفات حمیدہ کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,31,'55:31','اے زمین کے بوجھو ، عنقریب ہم تم سے باز پرس کرنے کے لئے فارغ ہوئے جاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(55,32,'55:32','(پھر دیکھ لیں گے کہ) تم اپنے رب کے کن کن احسانات کو جھٹلاتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(55,33,'55:33','اے گروہ جن وانس اگر تم زمین اور آسمانوں کی سرحدوں سے نکل کر بھاگ سکتے ہو تو بھاگ دیکھو۔ نہیں بھاگ سکتے۔ اس کے لئے بڑا زور چاہئے۔');
INSERT INTO "sayyid_qutb" VALUES(55,34,'55:34','اپنے رب کی کن کن قدرتوں کو تم جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,35,'55:35','(بھاگنے کی کوشش کرو گے تو) تم پر آگ کا شعلہ اور دھواں چھوڑ دیا جائے گا جس کا تم مقابلہ نہ کرسکوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(55,36,'55:36','اے جن وانس ، تم اپنے رب کی کن کن قدرتوں کا انکار کرو گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(55,37,'55:37','پھر (کیا بنے گا اس وقت) جب آسمان پھٹے گا اور سرخ ہوگا اور تیل کس طرح بہہ نکلے');
INSERT INTO "sayyid_qutb" VALUES(55,38,'55:38','تو اے جن وانس (اس وقت) تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,39,'55:39','اسی روز کسی انسان اور کسی جن سے ان کا گناہ پوچھنے کی ضرورت نہ ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(55,40,'55:40','پھر (دیکھ لیا جائے گا کہ) تم دونوں گروہ اپنے رب کے کن کن احسانات کا انکار کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(55,41,'55:41','مجرم وہاں اپنے چہروں سے پہچان لئے جائیں گے اور انہیں پیشانی کے بال اور پاؤں پکڑ پکڑ کر گھسیٹا جائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(55,42,'55:42','اس وقت تم اپنے رب کی کن کن قدرتوں کو جھٹلاؤ گے۔');
INSERT INTO "sayyid_qutb" VALUES(55,43,'55:43','اس وقت کہا جائے گا یہ وہی جہنم ہے جس کو مجرمین جھوٹ قرار دیا کرتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(55,44,'55:44','اسی جہنم اور کھولتے ہوئے پانی کے درمیان وہ گردش کرتے رہیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(55,45,'55:45','پھر اپنے رب کے کن کن قدرتوں کو تم جھٹلاؤ گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(55,46,'55:46','اور ہر اس شخص کے لئے جو اپنے رب کے حضور پیش ہونے کا خوف رکھتا ہو ، دو باغ ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(55,47,'55:47','اپنے رب کے کن کن انعمامات کو تم جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,48,'55:48','ہری بھری ڈالیوں سے بھر پور ،');
INSERT INTO "sayyid_qutb" VALUES(55,49,'55:49','اپنے رب کے کن کن انعامات کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,50,'55:50','دونوں باغوں میں دو چشمے رواں ،');
INSERT INTO "sayyid_qutb" VALUES(55,51,'55:51','اپنے رب کے کن کن انعامات کو جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,52,'55:52','دونوں باغوں میں ہر پھل کی دو قسمیں ،');
INSERT INTO "sayyid_qutb" VALUES(55,53,'55:53','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,54,'55:54','جنتی لوگ ایسے فرشوں پر تکئے لگا کے بیٹھیں گے جن کے استردبیزریشم کے ہوں گے اور باغوں کی ڈالیں پھلوں سے جھکی پڑ رہی ہوں گی۔');
INSERT INTO "sayyid_qutb" VALUES(55,55,'55:55','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,56,'55:56','ان نعمتوں کے درمیان شرمیلی نگاہوں والیاں ہوں گی جنہیں ان جنتیوں سے پہلے کبھی کسی انسان یا جن نے نہ چھوا ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(55,57,'55:57','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,58,'55:58','ایسی خوبصورت جیسے ہیرے اور موتی ،');
INSERT INTO "sayyid_qutb" VALUES(55,59,'55:59','اپنے رب کے کن کن انعامات کو تم جھٹلاؤ گے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(55,60,'55:60','نیکی کا بدلہ نیکی کے سوا اور کیا ہوسکتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(55,61,'55:61','پھر اے جن وانس اپنے رب کے کن کن اوصاف حمیدہ کا تم انکار کرو گے ؟');
INSERT INTO "sayyid_qutb" VALUES(55,62,'55:62','اور ان دو باغوں کے علاوہ دو باغ اور ہوں گے۔');
