INSERT INTO "sayyid_qutb" VALUES(56,5,'56:5','اور پہاڑ اس طرح ریزہ ریزہ کردیئے جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(56,6,'56:6','کہ پراگندہ غبار بن کر رہ جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,7,'56:7','تم لوگ اس وقت تین گروہوں میں تقسیم ہوجاؤ گے ،');
INSERT INTO "sayyid_qutb" VALUES(56,8,'56:8','دائیں بازو والے ، سودائیں بازو والوں (کی خوش نصیبی کا کیا کہنا');
INSERT INTO "sayyid_qutb" VALUES(56,9,'56:9','اور بائیں بازو والے ، تو بائیں بازو والوں (کی بدنصیبی) کا کیا ٹھکانا');
INSERT INTO "sayyid_qutb" VALUES(56,10,'56:10','اور آگے والے تو پھر آگے والے ہی ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(56,11,'56:11','وہی تو مقرب لوگ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(56,12,'56:12','نعمت بھری جنتوں میں رہیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,13,'56:13','اگلوں میں سے بہت ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(56,14,'56:14','اور پچھلوں میں سے کم۔');
INSERT INTO "sayyid_qutb" VALUES(56,15,'56:15','مرصع تختوں پر');
INSERT INTO "sayyid_qutb" VALUES(56,16,'56:16','تکئے لگائے آمنے سامنے بیٹھیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,17,'56:17','ان کی مجلسوں میں ابدی لڑکے');
INSERT INTO "sayyid_qutb" VALUES(56,18,'56:18','شراب چشمہ جاری سے لبریز پیالے ، اور کنٹر اور ساغر لئے دوڑتے پھرتے ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,19,'56:19','نہ ان کو اس سے جدا کیا جائے گا اور نہ وہ ختم ہوگا');
INSERT INTO "sayyid_qutb" VALUES(56,20,'56:20','اور وہ ان کے سامنے طرح طرح کے لذیذ پھل پیش کریں گے کہ جسے چاہیں چن لیں');
INSERT INTO "sayyid_qutb" VALUES(56,21,'56:21','اور پرندوں کے گوشت پیش کریں گے کہ جس پرندے کا چاہیں استعمال کریں');
INSERT INTO "sayyid_qutb" VALUES(56,22,'56:22','اور ان کے لئے خوبصورت آنکھوں والی حوریں ہوں گی ،');
INSERT INTO "sayyid_qutb" VALUES(56,23,'56:23','ایسی حسین جیسے چھپا کر رکھے ہوئے موتی۔');
INSERT INTO "sayyid_qutb" VALUES(56,24,'56:24','یہ سب کچھ ان اعمال کی جزا کے طور پر انہیں ملے گا جو وہ دنیا میں کرتے رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(56,25,'56:25','وہاں وہ کوئی بےہودہ کلام یا گناہ کی بات نہ سنیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,26,'56:26','جو بات بھی ہوگی ٹھیک ٹھیک ہوگی۔ “');
INSERT INTO "sayyid_qutb" VALUES(56,27,'56:27','اور دائیں بازو والے ، دائیں بازو والوں (کی خوش نصیبی) کا کیا کہنا ۔');
INSERT INTO "sayyid_qutb" VALUES(56,28,'56:28','وہ بےخا بیریوں');
INSERT INTO "sayyid_qutb" VALUES(56,29,'56:29','اور تہ بر تہ چڑھے ہوئے کیلوں');
INSERT INTO "sayyid_qutb" VALUES(56,30,'56:30','اور دور تک پھیلی ہوئی چھاؤں');
INSERT INTO "sayyid_qutb" VALUES(56,31,'56:31','اور ہر دم رواں پانی');
INSERT INTO "sayyid_qutb" VALUES(56,32,'56:32','اور کبھی ختم نہ ہونے والے');
INSERT INTO "sayyid_qutb" VALUES(56,33,'56:33','اور بےروک ٹوک ملنے والے بکثرت پھلوں');
INSERT INTO "sayyid_qutb" VALUES(56,34,'56:34','اور اونچی نشست گاہوں میں ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,35,'56:35','ان کی بیویوں کو ہم خاص طور پر نئے سرے سے پیدا کریں گے');
INSERT INTO "sayyid_qutb" VALUES(56,36,'56:36','اور انہیں باکرہ بنادیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(56,37,'56:37','اپنے شوہروں کی عاشق اور عمر میں ہم سن ،');
INSERT INTO "sayyid_qutb" VALUES(56,38,'56:38','یہ کچھ دائیں بازو والوں کے لئے ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,39,'56:39','وہ اگلوں میں سے بھی بہت ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(56,40,'56:40','وہ اگلوں میں سے بھی بہت ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(56,41,'56:41','اور بائیں بازو والے ، بائیں بازو والوں (کی بدنصیبی) کا کیا پوچھنا');
INSERT INTO "sayyid_qutb" VALUES(56,42,'56:42','وہ لو کی لپٹ اور کھولتے ہوئے پانی');
INSERT INTO "sayyid_qutb" VALUES(56,43,'56:43','اور کالے دھوئیں کے سائے میں ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(56,44,'56:44','جو نہ ٹھنڈا ہوگا نہ آرام دہ۔');
INSERT INTO "sayyid_qutb" VALUES(56,45,'56:45','یہ وہ لوگ ہوں گے جو اس انجام کو پہنچنے سے پہلے خوشحال تھے');
INSERT INTO "sayyid_qutb" VALUES(56,46,'56:46','اور گناہ عظیم پر اصرار کرتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(56,47,'56:47','کہتے تھے کیا جب ہم مرکر خاک ہوجائیں گے اور ہڈیوں کا پنجر رہ جائیں گے تو پھر اٹھا کھڑے کئے جائیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(56,48,'56:48','اور کیا ہمارے باپ دادا بھی اٹھائے جائیں گے جو پہلے گزر چکے ہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(56,49,'56:49','اے نبی ان لوگوں سے کہو ، یقینا اگلے اور پچھلے');
INSERT INTO "sayyid_qutb" VALUES(56,50,'56:50','سب ایک دن ضرور جمع کئے جانے والے ہیں جس کا وقت مقرر کیا جاچکا ہے');
INSERT INTO "sayyid_qutb" VALUES(56,51,'56:51','پھر اسے گمراہو اور جھٹلانے والو ،');
INSERT INTO "sayyid_qutb" VALUES(56,52,'56:52','تم زقوم کے درخت کی غذا کھانے والے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(56,53,'56:53','اسی سے تم پیٹ بھرو گے');
INSERT INTO "sayyid_qutb" VALUES(56,54,'56:54','اور اوپر سے کھولتا ہوا پانی');
INSERT INTO "sayyid_qutb" VALUES(56,55,'56:55','تو نس لگے ہوئے اونٹ کی طرح پیو گے۔');
INSERT INTO "sayyid_qutb" VALUES(56,56,'56:56','یہ ہے (ان بائیں بازو والوں) کی ضیافت کا سامان روز جزا میں۔ “');
INSERT INTO "sayyid_qutb" VALUES(56,57,'56:57','ہم نے تمہیں پیدا کیا ہے پھر کیوں تصدیق نہیں کرتے ؟');
INSERT INTO "sayyid_qutb" VALUES(56,58,'56:58','کبھی تم نے غور کیا ، یہ نطفہ جو تم ڈالتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(56,59,'56:59','اس سے بچہ تم بناتے ہو یا اس کے بنانے والے ہم ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(56,60,'56:60','ہم نے تمہارے درمیان موت کو تقسیم کیا ہے اور ہم اس سے عاجز نہیں ہیں');
INSERT INTO "sayyid_qutb" VALUES(56,61,'56:61','کہ تم جیسے اور لوگ لے آئیں اور کسی ایسے جہان میں تمہیں پیدا کردیں جس کو تم نہیں جانتے۔');
INSERT INTO "sayyid_qutb" VALUES(56,62,'56:62','اپنی پہلی پیدائش کو تو تم جانتے ہی ہو ، پھر کیوں سبق نہیں لیتے ؟');
INSERT INTO "sayyid_qutb" VALUES(56,63,'56:63','کبھی تم نے سوچا ، یہ بیج جو تم بوتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(56,64,'56:64','ان سے کھیتیاں تم اگاتے ہو یا ان کے اگانے والے ہم ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(56,65,'56:65','ہم چاہیں تو ان کھیتوں کو بھس بنا کر رکھ دیں اور تم طرح طرح کی باتیں بناتے رہ جاؤ');
INSERT INTO "sayyid_qutb" VALUES(56,66,'56:66','کہ ہم پر تو الٹی چٹی پڑ گئی');
INSERT INTO "sayyid_qutb" VALUES(56,67,'56:67','بلکہ ہمارے تو نصیب ہی پھوٹے ہوئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(56,68,'56:68','کبھی تم نے آنکھیں کھول کر دیکھا ، یہ پانی جو تم پیتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(56,69,'56:69','اسے تم نے بادل سے برسایا ہے یا اس کے برسانے والے ہم ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(56,70,'56:70','ہم چاہیں تو اسے سخت کھاری بنا کر رکھ دیں ، پھر کیوں تم شکرگزار نہیں ہوتے ؟');
INSERT INTO "sayyid_qutb" VALUES(56,71,'56:71','کبھی تم نے خیال کیا یہ آگ جو تم سلگاتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(56,72,'56:72','اس کا درخت تم نے پیدا کیا ہے یا اس کے پیدا کرنے والے ہم ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(56,73,'56:73','ہم نے اس کو یاددہانی کا ذریعہ اور حاجت مندوں کے لئے سامان زیست بنایا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,74,'56:74','پس اے نبی اپنے رب عظیم کے نام کی تسبیح کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(56,75,'56:75','پس میں قسم نہیں کھاتا تاروں کے مواقع کی');
INSERT INTO "sayyid_qutb" VALUES(56,76,'56:76','اور اگر تم سمجھو تو یہ بہت بڑی قسم ہے');
INSERT INTO "sayyid_qutb" VALUES(56,77,'56:77','کہ یہ ایک بلند پایہ قرآن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,78,'56:78','ایک محفوظ کتاب میں ثبت');
INSERT INTO "sayyid_qutb" VALUES(56,79,'56:79','جسے مطہرین کے سوا کوئی چھو نہیں سکتا۔');
INSERT INTO "sayyid_qutb" VALUES(56,80,'56:80','یہ رب العالمین کا نازل کردہ ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,81,'56:81','پھر کیا اس کلام کے ساتھ تم بےاعتنائی برتتے ہو');
INSERT INTO "sayyid_qutb" VALUES(56,82,'56:82','اور تم نے تکذیب کو اپنا رزق بنا لیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,83,'56:83','اب اگر تم کسی کے محکوم نہیں ہو اور اپنے اس خیال میں سچے ہو تو جب مرنے والے کی جان حلق تک پہنچ چکی ہوتی ہے');
INSERT INTO "sayyid_qutb" VALUES(56,84,'56:84','اور تم آنکھوں دیکھ رہے ہوتے ہو کہ وہ مررہا ہے');
INSERT INTO "sayyid_qutb" VALUES(56,85,'56:85','اس وقت تمہاری بہ نسبت ہم اس کے زیادہ قریب ہوتے ہیں مگر تم کو نظر نہیں آتے۔');
INSERT INTO "sayyid_qutb" VALUES(56,86,'56:86','اب اگر تم کسی کے محکوم نہیں');
INSERT INTO "sayyid_qutb" VALUES(56,87,'56:87','اس وقت اس کی نکلتی ہوئی جان کو واپس کیوں نہیں لے آتے ؟ اگر تم سچے ہو');
INSERT INTO "sayyid_qutb" VALUES(56,88,'56:88','پھر وہ مرنے والا اگر مقربین میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(56,89,'56:89','تو اس کے لئے راحت اور عمدہ رزق اور نعمت بھری جنت ہے');
INSERT INTO "sayyid_qutb" VALUES(56,90,'56:90','اور اگر وہ اصحاب یمین میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(56,91,'56:91','تو اس کا استقبال یوں ہوتا ہے کہ سلام ہے تجھے تو اصحاب الیمین میں سے ہے');
INSERT INTO "sayyid_qutb" VALUES(56,92,'56:92','اور اگر وہ جھٹلانے والے گمراہ لوگوں میں سے ہو');
INSERT INTO "sayyid_qutb" VALUES(56,93,'56:93','تو اس کی تواضع کے لئے کھولتا ہوا پانی ہے');
INSERT INTO "sayyid_qutb" VALUES(56,94,'56:94','اور جہنم میں جھونکا جانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(56,95,'56:95','یہ سب کچھ قطعی حق ہے ،');
INSERT INTO "sayyid_qutb" VALUES(56,96,'56:96','پس اے نبی اپنے رب عظیم کے نام کی تسبیح کرو۔ “');
INSERT INTO "sayyid_qutb" VALUES(57,1,'57:1','اللہ کی تسبیح کی ہے ہر اس چیز نے جو زمین اور آسمانوں میں ہے اور وہی زبردست اور دانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,2,'57:2','زمین اور آسمانوں کی سلطنت کا مالک وہی ہے ، زندگی بخشتا ہے اور موت دیتا ہے اور ہر چیز پر قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,3,'57:3','وہی اول بھی ہے اور آخر بھی ، اور ظاہر بھی ہے اور مخفی بھی اور وہ ہر چیز کا علم رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,4,'57:4','وہی ہے جس نے آسمانوں اور زمین کو چھ دنوں میں پیدا کیا اور پھر عرش پر جلوہ فرما ہوا۔ اس کے علم میں ہے جو کچھ زمین میں جاتا ہے اور جو کچھ اس سے نکلتا ہے اور جو کچھ آسمان سے اترتا ہے اور جو کچھ اس میں چڑھتا ہے۔ وہ تمہارے ساتھ ہے جہاں بھی تم ہو جو کام بھی تم کرتے ہو اسے وہ دیکھ رہا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,5,'57:5','وہی زمین اور آسمان کی بادشاہی کا مالک ہے اور تمام معاملات فیصلے کے لئے اس کی طرف رجوع کئے جاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(57,6,'57:6','وہی رات کو دن اور دن کو رات میں داخل کرتا ہے اور دلوں کے چھپے ہوئے راز تک جانتا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(57,7,'57:7','ایمان لاؤ اللہ اور اس کے رسول پر اور خرچ کرو ان چیزوں میں سے جن پر اس نے تم کو خلیفہ بنایا ہے۔ جو لوگ تم میں سے ایمان لائیں گے اور مال خرچ کریں گے ان کے لئے بڑا اجر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,8,'57:8','تمہیں کیا ہوگیا ہے کہ تم اللہ پر ایمان نہیں لاتے حالانکہ رسول تمہیں اپنے رب پر ایمان لانے کی دعوت دے رہا ہے اور وہ تم سے عہد لے چکا ہے۔ اگر تم واقعی ماننے والے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(57,9,'57:9','وہ اللہ ہی تو ہے جو اپنے بندے پر صاف صاف آیتیں نازل کررہا ہے تاکہ تمہیں تاریکیوں سے نکال کر روشنی میں لے آئے ، اور حقیقت یہ ہے کہ اللہ تم پر نہایت شفیق اور مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,10,'57:10','آخر کیا وجہ ہے کہ تم اللہ کی راہ میں خرچ نہیں کرتے حالانکہ زمین اور آسمانوں کی میراث اللہ ہی کے لئے ہے۔ تم میں سے جو لوگ فتح کے بعد خرچ اور جہاد کریں گے وہ کبھی ان لوگوں کے برابر نہیں ہوسکتے۔ جنہوں نے فتح سے پہلے خرچ اور جہاد کیا ہے۔ ان کا درجہ بعد میں خرچ اور جہاد کرنے والوں سے بڑھ کر ہے۔ اگرچہ اللہ نے دونوں ہی سے اچھے وعدے فرمائے ہیں۔ جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,11,'57:11','کون ہے جو اللہ کو قرض دے ؟ اچھا قرض ، تاکہ اللہ اسے کئی گنا بڑھا کر واپس دے اور اس کے لئے بہترین اجر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,12,'57:12','اس دن جب کہ تم مومن مردوں اور عورتوں کو دیکھو گے کہ ان کا نور ان کے آگے آگے اور ان کے دائیں جانب دوڑرہا ہوگا۔ (ان سے کہا جائے گا کہ ) آج بشارت ہے تمہارے لیے جنتیں ہوں گی جن کے نیچے نہریں بہ رہی ہوں گی ، جن میں وہ ہمیشہ رہیں گے۔ یہی ہے بڑی کامیابی۔');
INSERT INTO "sayyid_qutb" VALUES(57,13,'57:13','اس روز منافق مردوں اور عورتوں کا حال یہ ہوگا کہ وہ مومنوں سے کہیں گے ذرا ہماری طرف دیکھو تاکہ ہم تمہارے نور سے کچھ فائدہ اٹھائیں۔ مگر ان سے کہا جائے گا پیچھے ہٹ جاؤ ، اپنا نور کہیں اور تلاش کرو۔ “ پھر ان کے درمیان ایک دیوار حائل کردی جائے گی جس میں ایک دروازہ ہوگا۔ اس دروازے کے اندر رحمت ہوگی اور باہر عذاب۔');
INSERT INTO "sayyid_qutb" VALUES(57,14,'57:14','وہ مومنوں سے پکار پکار کر کہیں گے کیا ہم تمہارے ساتھ نہ تھے “ ؟ مومن جواب دیں گے۔ ہاں ، مگر تم نے اپنے آپ کو خود فتنے میں ڈالا ، موقع پرستی کی شک میں پڑے رہے ، اور جھوٹی توقعات تمہیں فریب دیتی رہیں ، یہاں تک کہ اللہ کا فیصلہ آگیا ، اور آخر وقت تک وہ بڑادھوکے باز (شیطان) تمہیں اللہ کے معاملہ میں دھوکا دیتا رہا۔');
INSERT INTO "sayyid_qutb" VALUES(57,15,'57:15','لہٰذا آج نہ تم سے کوئی فدیہ قبول کیا جائے گا اور نہ ان لوگوں سے جنہوں نے کھلا کھلا کفر کیا تھا۔ تمہارا ٹھکانا جہنم ہے ، وہی تمہاری خبر گیری کرنے والی ہے اور یہ بدترین انجام ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(57,16,'57:16','کیا ایمان لانے والوں کے لئے ابھی وہ وقت نہیں آیا کہ ان کے دل اللہ کے ذکر سے پگھلیں اور ان کے نازل کردہ حق کے آگے جھکیں اور وہ ان لوگوں کی طرح نہ ہوجائیں جنہیں پہلے کتاب دی گئی تھی ، پھر ایک لمبی مدت ان پر گزر گئی تو ان کے دل سخت ہوگئے اور آج ان میں سے اکثر فاسق بنے ہوئے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(57,17,'57:17','خوب جان لو کہ اللہ زمین کو اس کی موت کے بعد زندگی بخشتا ہے ، ہم نے نشانیاں تم کو صاف دکھا دی ہیں ، شاید کہ تم عقل سے کام لو۔ “');
INSERT INTO "sayyid_qutb" VALUES(57,18,'57:18','مردوں اور عورتوں میں سے جو لوگ صدقات دینے والے ہیں اور جنہوں نے اللہ کو قرض حسن دیا ہے ، ان کو یقینا کئی گنا بڑھا کردیا جائے گا اور ان کے لئے بہترین اجر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,19,'57:19','اور جو لوگ اللہ اور اس کے رسولوں پر ایمان لائے ہیں وہی اپنے رب کے نزدیک صدیق اور شہید ہیں ، ان کے لئے ان کا اجر اور ان کا نور ہے اور جن لوگوں نے کفر کیا اور ہماری آیات کو جھٹلایا ہے وہ دوزخی ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(57,20,'57:20','خوب جان لو یہ دنیا کی زندگی اس کے سو کچھ نہیں کہ ایک کھیل اور دل لگی اور ظاہری ٹیپ ٹاپ اور تمہارا آپس میں ایک دوسرے پر فخر جتانا اور مال واولاد میں ایک دوسری سے بڑھ جانے کی کوشش کرنا ہے۔ اس کی مثال ایسی ہے جیسے ایک بارش ہوگئی تو اس سے پیدا ہونے والی نباتات کو دیکھ کر کاشت کار خوش ہوگئے۔ پھر وہی کھیتی پک جاتی ہے اور تم دیکھتے ہو کہ وہ زرد ہوگئی۔ پھر وہ بھس بن کر رہ جاتی ہے۔ اس کی برعکس آخرت وہ جگہ ہے جہاں سخت عذاب ہے اور اللہ کی مغفرت اور اس کی خوشنودی ہے۔ دنیا کی زندگی ایک دھوکے کی ٹٹی کے سوا کچھ نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(57,21,'57:21','دوڑو اور ایک دوسرے سے آگے بڑھنے کی کوشش کرو اپنے رب کی مغفرت اور اس جنت کی طرف جس کی وسعت آسمان و زمین جیسی ہے ، جو مہیا کی گئی ہے ان لوگوں کے لئے جو اللہ اور اس کے رسولوں پر ایمان لائے ہوں۔ یہ اللہ کا فضل ہے ، جسے چاہتا ہے عطا فرماتا ہے ، اور اللہ بڑے فضل والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,22,'57:22','کوئی مصیبت ایسی نہیں جو زمین میں یا تمہارے اپنے نفس پر نازل ہوتی ہو اور ہم نے اس کو پیدا کرنے سے پہلے ایک کتاب یعنی نوشتہ تقدیر “ میں لکھ نہ رکھا ہو۔ ایسا کرنا اللہ کے لئے بہت آسان کام ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,23,'57:23','یہ سب کچھ اس لئے ہے تاکہ جو کچھ بھی نقصان تمہیں ہو اس پر تم دل شکستہ نہ ہو اور جو کچھ اللہ تمہیں عطا فرمائے اس پر پھول نہ جاؤ۔ اللہ ایسے لوگوں کو پسند نہیں کرتا جو اپنے آپ کو بڑی چیز سمجھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(57,24,'57:24','اور فخر جتاتے ہیں ، جو خود بخل کرتے ہیں اور دوسروں کو بخل کرنے پر اکساتے ہیں۔ اب اگر کوئی روگردانی کرتا ہے تو اللہ بےنیاز اور ستودہ صفات ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(57,25,'57:25','ہم نے اپنے رسولوں کو صاف صاف نشانیوں اور ہدایات کے ساتھ بھیجا ، اور ان کے ساتھ کتاب اور میزان نازل کی تاکہ لوگ انصاف پر قائم ہوں ، اور لوہا اتارا جس میں بڑا زور ہے اور لوگوں کے لئے منافع ہیں۔ یہ اس لئے کیا گیا ہے کہ اللہ کو معلوم ہوجائے کہ کون اس کو دیکھے بغیر اس کی اور اس کے رسولوں کی مدد کرتا ہے۔ یقینا اللہ بڑی قوت والا اور زبردست ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,26,'57:26','ہم نے نوح اور ابراہیم کو بھیجا اور ان دونوں کی نسل میں نبوت اور کتاب رکھ دی۔ پھر ان کی اولاد میں سے کسی نے ہدایت اختیار کی اور بہت سے فاسق ہوگئے۔');
INSERT INTO "sayyid_qutb" VALUES(57,27,'57:27','ان کے بعد ہم نے پے درپے اپنے رسول بھیجے اور ان سب کے بعد عیسیٰ ابن مریم کو مبعوث کیا اور اس کو انجیل عطا کی ، اور جن لوگوں نے اس کی پیروی اختیار کی ان کے دلوں میں ہم نے ترس اور رحم ڈال دیا۔ اور رہبانیت انہوں نے خود ایجاد کرلی ، ہم نے اسے ان پر فرض نہیں کیا تھا ، مگر اللہ کی خوشنودی کی طلب میں انہوں نے آپ ہی یہ بدعت نکالی اور پھر اس کی پابندی کرنے کا جو حق تھا اسے ادا نہ کیا۔ ان میں سے جو لوگ ایمان لائے ہوئے تھے ان کا اجر ہم نے ان کو عطا کیا ، مگر ان میں سے اکثر لوگ فاسق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(57,28,'57:28','اے لوگو جو ایمن لائے ہو ، اللہ سے ڈرو اور اس کے رسول (محمد صلى الله عليه وسلم) پر ایمان لاؤ، اللہ تمہیں رحمت کا دہرا حصہ عطا فرمائے گا اور تمہیں وہ نور بخشے گا جس کی روشنی میں تم چلو گے ، اور تمہارے قصور معاف کردے گا ، اللہ بڑا معاف کرنے والا اور مہربان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(57,29,'57:29','(تم کو یہ روش اختیار کرنی چاہئے) تاکہ اہل کتاب کو معلوم ہوجائے کہ اللہ کے فضل پر ان کا کوئی اجارہ نہیں ہے۔ اور یہ کہ اللہ کا فضل اس کے اپنے ہی ہاتھ میں ہے ، جسے چاہتا ہے عطا فرماتا ہے ، اور وہ بڑے فضل والا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(58,1,'58:1','اللہ نے سن لی اس عورت کی بات جو اپنے شوہر کے معاملہ میں تم سے تکرار کررہی ہے اور اللہ سے فریاد کیے جاتی ہے۔ اللہ تم دونوں کی گفتگو سن رہا ہے ، وہ سب کچھ سننے اور دیکھنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,2,'58:2','تم میں سے جو لوگ اپنی بیویوں سے اظہار کرتے ہیں کہ ان کی بیویاں ان کی مائیں بہنیں ہیں ، ان کی مائیں تو وہی ہیں جنہوں نے ان کو جنا ہے۔ یہ لوگ ایک سخت ناپسندیدہ اور جھوٹی بات کہتے ہیں ، اور حقیقت یہ ہے کہ اللہ بڑا معاف کرنے والا اور درگزر فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,3,'58:3','جو لوگ اپنی بیویوں سے اظہار کریں پھر اپنی اس بات سے رجوع کریں جو انہوں نے کہی تھی ، تو قبل اس کے کہ دونوں ایک دوسرے کو ہاتھ لگائیں ، ایک غلام آزاد کرنا ہوگا۔ اس سے تم کو نصیحت کی جاتی ہے ، اور جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,4,'58:4','اور جو شخص غلام نہ پائے وہ دو مہینے کے پے در پے روزے رکھے قبل اس کے کہ دونوں ایک دوسرے کو ہاتھ لگائیں۔ اور جو اس پر بھی قادر نہ ہو وہ ساٹھ مسکینوں کو کھانا کھلائے۔ یہ حکم اس لئے دیا جارہا ہے کہ تم اللہ اور اس کے رسول پر ایمان لاؤ۔ یہ اللہ کی مقرر کی گئی ہوئی حدیں ہیں ، اور کافروں کے لئے درد ناک سزا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(58,5,'58:5','جو لوگ اللہ اور اس کے رسول کی مخالفت کرتے ہیں وہ اسی طرح ذلیل و خوار کردیئے جائیں گے جس طرح ان سے پہلے کے لوگ ذلیل و خوار کیے جاچکے ہیں۔ ہم نے صاف صاف آیات نازل کردی ہیں ، اور کافروں کے لئے ذلت کا عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,6,'58:6','اس دن (یہ ذلت کا عذاب ہونا ہے) جب اللہ ان سب کو پھر سے زندہ کرکے اٹھائے گا۔ اور انہیں بتادے گا کہ وہ کیا کچھ کرکے آئے ہیں۔ وہ بھول گئے ہیں مگر اللہ نے ان کا سب کیا دھڑاگن گن کر محفوظ کررکھا ہے اور اللہ ایک ایک چیز پر شاہد ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(58,7,'58:7','کیا تم کو خبر نہیں ہے کہ زمین اور آسمانوں کی ہر چیز کا اللہ کا علم ہے ؟ کبھی ایسا نہیں ہوتا کہ تین آدمیوں میں کوئی سرگوشی ہو اور ان کے درمیان چوتھا اللہ نہ ہو ، یا پانچ آدمیوں میں سرگوشی ہو اور ان کے اندر چھٹا اللہ نہ ہو۔ خفیہ بات کرنے والے خواہ اس سے کم ہوں یا زیادہ ، جہاں کہیں بھی وہ ہیں ، اللہ ان کے ساتھ ہوتا ہے۔ پھر قیامت کے روز وہ ان کو بتا دے گا کہ انہوں نے کیا کچھ کیا ہے۔ اللہ ہر چیز کا علم رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,8,'58:8','کیا تم نے دیکھا نہیں ان لوگوں کو جنہیں سرگوشیاں کرنے سے منع کردیا گیا تھا پھر بھی وہ وہی حرکت کیے جاتے ہیں جس سے انہیں منع کیا گیا تھا ؟ یہ لوگ چھپ چھپ کر آپس میں گناہ اور زیادتی اور رسول کی نافرمانی کی باتیں کرتے ہیں ، اور جب تمہارے پاس آتے ہیں تو تمہیں اس طریقے سے سلام کرتے ہیں جس طرح اللہ نے تم پر سلام نہیں کیا ہے اور اپنے دلوں میں کہتے ہیں کہ ہماری ان باتوں پر اللہ ہمیں عذاب کیوں نہیں دیتا ؟ ان کے لئے جہنم ہی کافی ہے۔ اسی کا وہ ایندھن بنیں گے۔ بڑا ہی برا انجام ہے ان کا۔');
INSERT INTO "sayyid_qutb" VALUES(58,9,'58:9','اے لوگو جو ایمان لائے ہو ، جب تم آپس میں پوشیدہ بات کرو تو گناہ اور زیادتی اور رسول اللہ صلى الله عليه وسلم کی نافرمانی کی باتیں نہیں بلکہ نیکی اور تقویٰ کی باتیں کرو اور اس خدا سے ڈرتے رہو جس کے حضور تمہیں حشر میں پیش ہونا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,10,'58:10','کانا پھوسی تو ایک شیطانی کام ہے ، اور وہ اس لئے کی جاتی ہے کہ ایمان لانے والے لوگ اس سے رنجیدہ ہوں ، حالانکہ بےاذن خدا وہ انہیں کچھ بھی نقصان نہیں پہنچاسکتی ، اور مومنوں کو اللہ ہی پر بھروسہ رکھنا چاہئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(58,11,'58:11','اے لوگوجو ایمان لائے ہو ، جب تم سے کہا جائے کہ اپنی مجلس میں کشادگی پیدا کرو تو جگہ کشاد کردیا کرو ، اللہ تمہیں کشادگی بخشے گا اور جب تم سے کہا جائے کہ اٹھ جاؤ تو اٹھ جایا کرو۔ تم میں سے جو لوگ ایمان رکھنے والے ہیں اور جن کو علم بخشا گیا ہے ، اللہ ان کو بلند درجے عطا فرمائے گا ، اور جو کچھ تم کرتے ہو ، اللہ کو اس کی خبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,12,'58:12','اے لوگو جو ایمان لائے ہو ، جب تم رسول سے تخلیہ میں بات کرو تو بات کرنے سے پہلے کچھ صدقہ دو ، یہ تمہارے لئے بہتر اور پاکیزہ تر ہے ۔ البتہ اگر تم صدقہ دینے کے لئے کچھ نہ پاؤ تو اللہ غفور ورحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,13,'58:13','کیا تم ڈر گئے اس بات سے کہ تخلیہ میں گفتگو کرنے سے پہلے تمہیں صدقات دینے ہوں گے ؟ اچھا ، اگر تم ایسا نہ کرو ........ اور اللہ نے تم کو اس سے معاف کردیا ........ تو نماز قائم کرتے رہو ، زکوة دیتے رہو اور اللہ اور اس کے رسول کی اطاعت کرتے رہو ۔ تم جو کچھ کرتے ہو اللہ اس بےباخبر ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(58,14,'58:14','کیا تم نے دیکھا ان لوگوں کو جنہوں نے دوست بنایا ہے ایک ایسے گروہ کو جو اللہ کا مغضوب ہے ؟ وہ نہ تمہارے ہیں نہ ان کے ، اور وہ جان بوجھ کر جھوٹی بات پر قسمیں کھاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(58,15,'58:15','اللہ نے ان کے لئے سخت عذاب مہیا کررکھا ہے ، بڑے ہی برے کرتوت ہیں جو وہ کررہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(58,16,'58:16','انہوں نے اپنی قسموں کو ڈھال بنارکھا ہے جس کی آڑ میں وہ اللہ کی راہ سے لوگوں کو روکتے ہیں ، اس پر ان کے لئے ذلت کا عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,17,'58:17','اللہ سے بچانے کے لئے نہ ان کے مال کچھ کام آئیں گے نہ ان کی اولاد۔ وہ دوزخ کے یار ہیں ، اسی میں وہ ہمیشہ رہیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(58,18,'58:18','جس روز اللہ ان سب کو اٹھائے گا ، وہ اس کے سامنے بھی اسی طرح قسمیں کھائیں گے جس طرح تمہارے سامنے کھاتے ہیں اور اپنے نزدیک یہ سمجھیں گے کہ اس سے ان کا کچھ کام بن جائے گا۔ خوب جان لو ، وہ پرلے درجے کے جھوٹے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(58,19,'58:19','شیطان ان پر مسلط ہوچکا ہے اور اس نے خدا کی یاد ان کے دل سے بھلا دی ہے۔ وہ شیطان کی پارٹی کے لوگ ہیں۔ خبردار رہو ، شیطان کی پارٹی والے ہی خسارے میں رہنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(58,20,'58:20','یقینا ذلیل ترین مخلوقات میں سے ہیں وہ لوگ جو اللہ اور اس کے رسول کا مقابلہ کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(58,21,'58:21','اللہ نے لکھ دیا ہے کہ میں اور میرے رسول ہی غالب ہوکر رہیں گے۔ فی الواقع اللہ زبردست اور زور آور ہے۔');
INSERT INTO "sayyid_qutb" VALUES(58,22,'58:22','تم کبھی یہ نہ پاؤ گے کہ جو لوگ اللہ اور آخرت پر ایمان رکھنے والے ہیں وہ ان لوگوں سے محبت کرتے ہیں جنہوں نے اللہ اور اس کے رسول کی مخالفت کی ہے ، خواہ وہ ان کے باپ ہوں ، یا ان کے بیٹے ، یا ان کے بھائی یا ان کے اہل خاندان۔ یہ وہ لوگ ہیں جن کے دلوں میں اللہ نے ایمان ثبت کردیا ہے اور اپنی طرف سے ایک روح عطا کرکے ان کو قوت بخشی ہے۔ وہ ان کو ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ ان میں وہ ہمیشہ رہیں گے۔ اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے۔ وہ اللہ کی پارٹی کے لوگ ہیں۔ خبردار رہو ، اللہ کی پارٹی والے ہی فلاں پانے والے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(59,1,'59:1','اللہ ہی کی تسبیح کی ہے ہر اس چیز نے جو آسمانوں اور زمین میں ہے ، اور وہی غالب اور حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,2,'59:2','وہی ہے جس نے اہل کتاب کافروں کو ان کے گھروں سے نکال باہر کیا۔ تمہیں ہرگز یہ گمان نہ تھا کہ وہ نکل جائیں گے ، اور وہ بھی یہ سمجھے بیٹھے تھے کہ ان کی گڑھیاں انہیں اللہ سے بچا لیں گی۔ مگر ایسے رخ سے ان پر آیا جدھر ان کا خیال بھی نہ گیا تھا۔ اس نے ان کے دلوں میں رعب ڈال دیا۔ نتیجہ یہ ہوا کہ وہ خود اپنے ہاتھوں سے بھی اپنے گھروں کو برباد کررہے تھے اور مومنوں کے ہاتھوں بھی برباد کروا رہے تھے۔ پس عبرت حاصل کرو اے دیدہ بینار رکھنے والو !');
INSERT INTO "sayyid_qutb" VALUES(59,3,'59:3','اگر اللہ نے ان کے حق میں جلاوطنی نہ لکھ دی ہوتی تو دنیا ہی میں وہ انہیں عذاب دے ڈالتا ، اور آخرت میں تو ان کے لئے دوزخ کا عذاب ہے ہی۔');
INSERT INTO "sayyid_qutb" VALUES(59,4,'59:4','یہ سب کچھ اس لئے ہوا کہ انہوں نے اللہ اور اس کے رسول کا مقابلہ کیا ، اور جو بھی اللہ کا مقابلہ کرے اللہ اس کی سزا دینے میں بہت سخت ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(59,5,'59:5','تم لوگوں نے کھجوروں کے جو درخت کاٹے یا جن کو اپنی جڑوں پر کھڑا رہنے دیا ، یہ سب اللہ ہی کے اذن سے تھا۔ اور (اللہ نے یہ اذن اس لئے دیا) تاکہ فاسقوں کو ذلیل و خوار کرے۔');
INSERT INTO "sayyid_qutb" VALUES(59,6,'59:6','اور جو مال اللہ نے ان کے قبضے سے نکال کر اپنے رسول کی طرف پلٹا دیئے ، وہ ایسے مال ہیں جن پر تم نے اپنے گھوڑے اور اونٹ دوڑائے ہوں ، بلکہ اللہ اپنے رسولوں کو جس پر چاہتا ہے ، تسلط عطا فرما دیتا ہے ، اور اللہ ہر چیز پر قادر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,7,'59:7','جو کچھ بھی اللہ تعالیٰ بستیوں کے لوگوں سے اپنے رسول کی طرف پلٹا دے وہ اللہ اور رسول اور رشتہ داروں اور یتامی اور مساکین اور مسافروں کے لئے ہے تاکہ وہ تمہارے مالداروں ہی کے درمیان گردش نہ کرتا رہے۔ جو کچھ رسول تمہیں دے وہ لے لو اور جس چیز سے وہ تم کو روک دے اس سے رک جاؤ۔ اللہ سے ڈرو ، اللہ سخت سزا دینے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,8,'59:8','(نیز وہ مال) ان غریب مہاجرین کے لئے ہے جو اپنے گھروں اور جائیدادوں سے نکال باہر کیے گئے ہیں۔ یہ لوگ اللہ کا فضل اور اس کی خوشنودی چاہتے ہیں اور اللہ اور اس کے رسول کی حمایت پر کمر بستہ رہتے ہیں۔ یہی راست باز لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(59,9,'59:9','(اور وہ ان لوگوں کے لئے بھی ہے) جو ان مہاجرین کی آمد سے پہلے ہی ایمان لاکر دارالہجرت میں مقیم تھے۔ یہ ان لوگوں سے محبت کرتے ہیں جو ہجرت کرکے ان کے پاس آئے ہیں اور جو کچھ بھی ان (مہاجرین) کو دے دیا جائے اس کی کوئی حاجت تک یہ اپنے دلوں میں محسوس نہیں کرتے اور اپنی ذات پر دوسروں کو ترجیح دیتے ہیں۔ خواہ اپنی جگہ خود محتاج ہوں۔ حقیقت یہ ہے کہ جو لوگ اپنے دل کی تنگی سے بچا لیے گئے وہی فلاح پانے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(59,10,'59:10','(اور وہ ان لوگوں کے لئے بھی ہے) جو ان اگلوں کے بعد آئے ہیں ، جو کہتے ہیں کہ اے ہمارے رب ہمیں اور ہمارے ان سب بھائیوں کو بخش دے جو ہم سے پہلے ایمان لائے ہیں اور ہمارے دلوں میں اہل ایمان کے لئے کوئی بغض نہ رکھ ، ہمارے رب ، تو بڑا مہربان اور رحیم ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(59,11,'59:11','تم نے دیکھا نہیں ان لوگوں کو جنہوں نے منافقت کی روش اختیار کی ہے ؟ یہ اپنے کافر اہل کتاب بھائیوں سے کہتے ہیں اگر تمہیں نکالا گیا تو ہم تمہارے ساتھ نکلیں گے ، اور تمہارے معاملہ میں ہم کسی کی بات ہرگز نہ مانیں گے ، اور اگر تم سے جنگ کی گئی تو ہم تمہاری مدد کریں گے “۔ مگر اللہ گواہ ہے کہ یہ لوگ قطعی جھوٹے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(59,12,'59:12','اگر وہ نکالے گئے تو یہ ان کے ساتھ ہرگز نہ نکلیں گے ، اور اگر ان سے جنگ کی گئی تو یہ ان کی ہرگز مدد نہ کریں گے اور اگر یہ ان کی مدد کریں بھی تو پیٹھ پھیر جائیں گے اور پھر کہیں سے کوئی مدد نہ پائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(59,13,'59:13','ان کے دلوں میں اللہ سے بڑھ کر تمہارا خوف ہے ، اس لئے کہ یہ ایسے لوگ ہیں جو سمجھ بوجھ نہیں رکھتے۔');
INSERT INTO "sayyid_qutb" VALUES(59,14,'59:14','یہ کبھی اکٹھے ہوکر (کھلے میدان میں) تمہارا مقابلہ نہ کریں گے ، لڑیں گے بھی تو قلعہ بند بستیوں میں بیٹھ کر یا دیواروں کے پیچھے چھپ کر۔ یہ آپس میں مخالفت میں بڑے سخت ہیں۔ تم انہیں اکٹھا سمجھتے ہو مگر ان کے دل ایک دوسرے سے پھٹے ہوئے ہیں۔ ان کا یہ حال اس لئے ہے کہ یہ بےعقل لوگ ہیں');
INSERT INTO "sayyid_qutb" VALUES(59,15,'59:15','یہ انہی لوگوں کے مانند ہیں جو ان سے تھوڑی ہی مدت پہلے اپنے کیے کا مزا چکھ چکے ہیں۔ اور ان کے لئے درد ناک عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,16,'59:16','ان کی مثال شیطان کی سی ہے کہ پہلے وہ انسان سے کہتا ہے کہ کفر کر ، اور جب انسان کفر کر بیٹھتا ہے تو وہ کہتا ہے کہ میں تجھ سے بری الذمہ ہوں ، مجھے تو اللہ رب العالمین سے ڈر لگتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,17,'59:17','پھر دونوں کا انجام یہ ہونا ہے کہ ہمیشہ کے لئے جہنم میں جائیں ، اور ظالموں کی یہی جزا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(59,18,'59:18','اے لوگو جو ایمان لائے ہو ، اللہ سے ڈرو ، اور ہر شخص یہ دیکھے کہ اس نے کل کے لئے کیا سامان کیا ہے۔ اللہ سے ڈرتے رہو ، اللہ یقینا تمہارے ان سب اعمال سے باخبر ہے جو تم کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(59,19,'59:19','ان لوگوں کی طرح نہ ہوجاؤ جو اللہ کو بھول گئے تو اللہ نے انہیں خود اپنا نفس بھلا دیا ، یہی لوگ فاسق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(59,20,'59:20','دوزخ میں جانے والے اور جنت میں جانے والے کبھی یکساں نہیں ہوسکتے ۔ جنت میں جانے والے ہی اصل میں کامیاب ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(59,21,'59:21','اگر ہم نے یہ قرآن کسی پہاڑ پر بھی اتاردیا ہوتا تو تم دیکھتے کہ وہ اللہ کے خوف سے دبا جارہا ہے اور پھٹا پڑتا ہے۔ یہ مثالیں ہم لوگوں کے سامنے اس لئے بیان کرتے ہیں کہ وہ (اپنی حالت) پر غور کریں۔');
INSERT INTO "sayyid_qutb" VALUES(59,22,'59:22','وہ اللہ ہی ہے جس کے سوا کوئی معبود نہیں ، غائب اور ظاہر ہر چیز کا جاننے والا ، وہی رحمن اور رحیم ہے');
INSERT INTO "sayyid_qutb" VALUES(59,23,'59:23','وہ اللہ ہی ہے جس کے سوا کوئی معبود نہیں۔ وہ بادشاہ ہے نہایت مقدس ، سراسر سلامتی ، امن دینے والا ، نگہبان ، سب پر غالب ، اپنا حکم بزور نافذ کرنے والا ، اور بڑا ہی ہوکر رہنے والا۔ پاک ہے ، اللہ اس شرک سے جو لوگ کررہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(59,24,'59:24','وہ اللہ ہی ہے جو تخلیق کا منصوبہ بنانے والا اور اس کو نافذ کرنے والا اور اس کے مطابق صورت گری کرنے والا ہے۔ اس کے لئے بہترین نام ہیں۔ ہر چیز جو آسمانوں اور زمین میں ہے اس کی تسبیح کررہی ہے ، اور وہ زبردست اور حکیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(60,1,'60:1','اے لوگو جو ایمان لائے ہو ، اگر تم میری راہ میں جہاد کرنے کے لئے اور میری رضا جوئی کی خاطر (وطن چھوڑ کر گھروں سے) نکلے ہو تو میرے اور اپنے دشمنوں کو دوست نہ بناؤ۔ تم ان کے ساتھ دوستی کی طرح ڈالتے ہو ، حالانکہ جو حق تمہارے پاس آیا ہے اس کو ماننے سے وہ انکار کرچکے ہیں اور ان کی روش یہ ہے کہ رسول کو اور خود تم کو صرف اس قصور پر جلاوطن کرتے ہیں کہ تم اپنے رب ، اللہ پر ایمان لائے ہو ، تم چھپا کر ان کو دومستانہ پیغام بھیجتے ہو ، حالانکہ جو کچھ تم چھپا کر کرتے ہو اور جو علانیہ کرتے ہو ، ہر چیز کو میں خوب جانتا ہوں۔ جو شخص بھی تم میں سے ایسا کرے وہ یقینا راہ راست سے بھٹک گیا۔');
INSERT INTO "sayyid_qutb" VALUES(60,2,'60:2','ان کا رویہ تو یہ ہے کہ اگر تم پر قابو پاجائیں تو تمہارے ساتھ دشمنی کریں اور ہاتھ اور زبان سے تمہیں آزار دیں۔ وہ تو یہ چاہتے ہیں کہ تم کسی طرح کافر ہوجاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(60,3,'60:3','قیامت کے دن نہ تمہاری رشتہ داریاں کسی کام آئیں گی نہ تمہاری اولاد۔ اس روز اللہ تمہارے درمیان جدائی ڈال دے گا اور وہی تمہارے اعمال کا دیکھنے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(60,4,'60:4','تم لوگوں کے لئے ابراہیم (علیہ السلام) اور اس کے ساتھیوں میں اچھا نمونہ ہے کہ انہوں نے اپنی قوم سے صاف کہہ دیا ہم تم سے اور تمہارے ان معبودوں سے جن کو تم خدا کو چھوڑ کر پوجتے ہو ، قطعی بیزار ہیں۔ ہم نے تم سے کفر کیا اور ہمارے اور تمہارے درمیان ہمیشہ کے لئے عداوت ہوگئی اور بیر پڑگیا۔ جب تک تم اللہ واحد پر ایمان نہ لاؤ “۔ مگر ابراہیم (علیہ السلام) کا اپنے باپ سے یہ کہنا (اس سے مستثنیٰ ہے) کہ میں آپ کے لئے مغفرت کی درخواست ضرور کروں گا ، اور اللہ سے آپ کے لئے کچھ حاصل کرلینا میرے بس میں نہیں ہے “ (اور ابراہیم (علیہ السلام) واصحاب ابراہیم (علیہ السلام) کی دعا یہ تھی کہ) اے ہمارے رب ، تیرے ہی اوپر ہم نے بھروسہ کیا اور تیری ہی طرف ہم نے رجوع کرلیا اور تیرے ہی حضور ہمیں پلٹنا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(60,5,'60:5','اے ہمارے رب ہمیں کافروں کے لئے فتنہ نہ بنادے۔ اور اے ہمارے رب ، ہمارے قصوروں سے درگزر فرما ، بیشک تو ہی زبردست اور دانا ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(60,6,'60:6','انہی لوگوں کے طرز عمل میں تمہارے لئے اور ہر اس شخص کے لئے اچھا نمونہ ہے جو اللہ اور روز آخر کا امیدوار ہو۔ اس سے کوئی منحرف ہو تو اللہ بےنیاز اور اپنی ذات میں آپ محمود ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(60,7,'60:7','بعید نہیں کہ اللہ کبھی تمہارے اور ان لوگوں کے درمیان محبت ڈال دے جن سے آج تم نے دشمنی مول لی ہے۔ اللہ بڑی قدرت رکھتا ہے اور وہ غفور ورحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(60,8,'60:8','اللہ تمہیں اس بات سے نہیں روکتا کہ تم ان لوگوں کے ساتھ نیکی اور انصاف کا برتاؤ کرو جنہوں نے دین کے معاملہ میں تم سے جنگ نہیں کی ہے اور تمہیں تمہارے گھروں سے نہیں نکالا ہے۔ اللہ انصاف کرنے والوں کو پسند کرتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(60,9,'60:9','وہ تمہیں جس بات سے روکتا ہے وہ تو یہ ہے کہ تم ان لوگوں سے دوستی کرو جنہوں نے تم سے دین کے معاملہ میں جنگ کی ہے۔ اور تمہیں تمہارے گھروں سے نکالا ہے۔ اور تمہارے اخراج میں ایک دوسرے کی مدد کی ہے۔ ان سے جو لوگ دوستی کریں وہی ظالم ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(60,10,'60:10','اے لوگو جو ایمان لائے ہو ، جب مومن عورتیں ہجرت کرکے تمہارے پاس آئیں تو (ان کے مومن ہونے کی) جانچ پڑتال کرلو ، اور ان کے ایمان کی حقیقت تو اللہ ہی بہتر جانتا ہے۔ پھر جب تمہیں معلوم ہوجائے کہ وہ مومن ہیں تو انہیں کفار کی طرف واپس نہ کرو۔ نہ وہ کفار کے لئے حلال ہیں اور نہ کفار ان کے لئے حلال۔ ان کے کفار شوہروں نے جو مہران کو دیئے تھے وہ انہیں پھیر دو ۔ اور ان سے نکاح کر لنے میں تم پر کوئی گناہ نہیں جب کہ تم ان کے مہر ان کو ادا کردو۔ اور تم خود بھی کافر عورتوں کو اپنے نکاح میں نہ روکے رہو۔ جو مہر تم نے اپنی کافر بیویوں کو دیئے تھے وہ تم واپس مانگ لو۔ اور جو مہر کافروں نے اپنی مسلمان بیویوں کو دیئے تھے انہیں وہ واپس مانگ لیں۔ یہ الہل کا حکم ہے ، وہ تمہارے درمیان فیصلہ کرتا ہے اور وہ علیم و حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(60,11,'60:11','اور اگر تمہاری کافر بیویوں کے مہروں میں سے کچھ تمہیں کفار سے واپس نہ ملے اور پھر تمہاری نوبت آئے تو جن لوگوں کی بیویاں ادھر رہ گئی ہیں ان کو اتنی رقم ادا کر دو جو ان کے دیئے ہوئے مہروں کے برابر ہو۔ اور اس خدا سے ڈرتے رہو جس پر تم ایمان لائے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(60,12,'60:12','اے نبی جب تمہارے پاس مومن عورتیں بیعت کرنے کے لئے آئیں اور اس بات کا عہد کریں کہ وہ اللہ کے ساتھ کسی چیزکو شریک نہ کریں گی ، چوری نہ کریں گی ، زنانہ کریں گی ، اپنی اولاد کو قتل نہ کریں گی ، اپنے ہاتھ پاؤں کے آگے کوئی بہتان گھڑ کر نہ لائیں گی ، اور کسی امر معروف میں تمہاری نافرمانی نہ کریں گی ، تو ان سے بیعت لے لو اور ان کے حق میں دعائے مغفرت کرو ، یقینا اللہ درگزر فرمانے والا اور رحم کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(60,13,'60:13','اے لوگوں جو ایمان لائے ہو ، ان لوگوں کو دوست نہ بنایو ، جن پر اللہ نے غضب فرمایا ہے ، جو آخرت سے اسی طرح مایوس ہیں جس طرح قبروں میں پڑے ہوئے کافر مایوس ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(61,1,'61:1','اللہ کی تسبیح کی ہے ہر اس چیز نے جو آسمانوں اور زمین میں ہے ، اور وہ غالب اور حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(61,2,'61:2','اے لوگو جو ایمان لائے ہو ، تم کیوں وہ بات کہتے ہو جو کرتے نہیں ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(61,3,'61:3','اللہ کے نزدیک یہ سخت ناپسندیدہ حرکت ہے کہ تم کہو وہ بات جو کرتے نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(61,4,'61:4','اللہ کو تو پسند وہ لوگ ہیں جو اس کی راہ میں اس طرح صف بستہ ہوکر لڑتے ہیں گویا کہ وہ ایک سیسہ پلائی ہوئی دیوار ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(61,5,'61:5','اور یاد کرو موسیٰ.... کی وہ بات جو اس نے اپنی قوم سے کہی تھی کہ ” اے میری قوم کے لوگو ، تم کیوں مجھے اذیت دیتے ہو ، حالانکہ تم خوب جانتے کہ میں تمہاری طرف اللہ کا بھیجا ہوا رسول ہوں ؟ “ پھر جب انہوں نے ٹیڑھ اختیار کی تو اللہ نے بھی ان کے دل ٹیڑھے کردیئے ، اللہ فاسقوں کو ہدایت نہیں دیتا۔');
INSERT INTO "sayyid_qutb" VALUES(61,6,'61:6','اور یاد کرو عیسیٰ ابن مریم کی وہ بات جو اس نے کہی تھی کہ ” اے نبی اسرائیل ، میں تمہاری طرف اللہ کا بھیجا ہوا رسول ہوں ، تصدیق کرنے والا اس تورات کی جو مجھ سے پہلے آئی ہوئی موجود ہے ، اور بشارت دینے والا ہوں ایک رسول کی جو میرے بعد آئے گا جس کا نام احمد ہوگا۔ مگر جب وہ ان کے پاس کھلی کھلی نشانیاں لے کر آیا تو انہوں نے کہا یہ تو صریح دھوکا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(61,7,'61:7','اب بھلا اس شخص سے بڑا ظالم اور کون ہوگا جو اللہ پر جھوٹے بہتان باندھے ، حالانکہ اسے اسلام (اللہ کے آگے سراطاعت جھکا دینے) کی دعوت دی جارہی ہو ؟ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا');
INSERT INTO "sayyid_qutb" VALUES(61,8,'61:8','یہ لوگ اپنے منہ کی پھونکوں سے اللہ کے نور کو بجھانا چاہتے ہیں ، اور اللہ کا فیصلہ یہ ہے کہ وہ اپنے نور کو پورا پھیلا کر رہے گا خواہ کافروں کو یہ کتنا ہی ناگوار ہو۔');
INSERT INTO "sayyid_qutb" VALUES(61,9,'61:9','وہی تو ہے جس نے اپنے رسول کو ہدایت اور دین حق کے ساتھ بھیجا ہے تاکہ اسے پورے کے پورے دین پر غالب کردے خواہ مشرکین کو یہ کتنا ہی ناگوار ہو۔');
INSERT INTO "sayyid_qutb" VALUES(61,10,'61:10','اے لوگوں جو ایمان لائے ہو ، میں بتاؤں تم کو وہ تجارت جو تمہیں عذاب الیم سے بچادے ؟');
INSERT INTO "sayyid_qutb" VALUES(61,11,'61:11','ایمان لاؤ اللہ اور اس کے رسول پر ، اور جہاد کرو اللہ کی راہ میں اپنے مالوں سے اور اپنی جانوں سے۔ یہی تمہارے لئے بہتر ہے اگر تم جانو۔');
INSERT INTO "sayyid_qutb" VALUES(61,12,'61:12','اللہ تمہارے گناہ معاف کردے گا ، اور تم کو ایسے باغوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی ، اور ابدی قیام کی جنتوں میں بہترین گھر تمہیں عطا فرمائے گا۔ یہ ہے بڑی کامیابی۔');
INSERT INTO "sayyid_qutb" VALUES(61,13,'61:13','اور وہ دوسری چیز جو تم چاہتے ہو ، وہ بھی تمہیں دے گا ، اللہ کی طرف سے نصرت اور قریب ہی میں حاصل ہوجانے والی فتح۔ اے نبی اہل ایمان کو اس کی بشارت دے دو ۔');
INSERT INTO "sayyid_qutb" VALUES(61,14,'61:14','اے لوگو جو ایمان لائے ہو ، اللہ کی مددگار بنو ، جس طرح عیسیٰ ابن مریم نے حواریوں کو خطاب کرکے کہا تھا :” کون ہے اللہ کی طرف (بلانے) میں میرا مددگار ؟ “ اور حواریوں نے جواب دیا تھا : ” ہم ہیں اللہ کے مددگار “۔ اس وقت بنی اسرائیل کا ایک گروہ ایمان لایا اور دوسرے گروہ نے انکار کیا۔ پھر ہم نے ایمان لانے والوں کو ان کے دشمنوں کے مقابلے میں تائید کی اور وہی غالب ہوکر رہے “۔');
INSERT INTO "sayyid_qutb" VALUES(62,1,'62:1','” اللہ کی تسبیح کررہی ہے ہر وہ چیز جو آسمانوں میں ہے اور ہر وہ چیز جو زمین میں ہے .... بادشاہ ہے نہایت مقدس ، زبردست اور حکیم “۔');
INSERT INTO "sayyid_qutb" VALUES(62,2,'62:2','” وہی ہے جس نے امیوں کے اندر ایک رسول خود انہی میں سے اٹھای ، جو انہی اس کی آیات سناتا ہے ، ان کی زندگی سنوارتا ہے ، انکو کتاب اور حکمت کی تعلیم دیتا ہے ، حالانکہ اس سے پہلے وہ کھلی گمراہی میں پڑے ہوئے تھے');
INSERT INTO "sayyid_qutb" VALUES(62,3,'62:3','اور (اس رسول کی بعثت) ان دوسرے لوگوں کے لئے بھی ہے جو ابھی ان سے نہیں ملے ہیں۔ اللہ زبردست اور حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(62,4,'62:4','یہ اس کا فضل ہے ، جسے چاہتا ہے دیتا ہے ، اور وہ بڑا فضل فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(62,5,'62:5','جن لوگوں کو تورات کا حامل بنایا گیا تھا مگر انہوں نے اس کا بار نہ اٹھایا ، ان کی مثال اس گدھے کی سی ہے جس پر کتابیں لدی ہوئی ہیں۔ اس سے بھی زیادہ بری مثال ہے ان لوگوں کی جنہوں نے اللہ کی آیات کو جھٹلا دیا ہے۔ ایسے ظالموں کو اللہ ہدایت نہیں دیا کرتا۔');
INSERT INTO "sayyid_qutb" VALUES(62,6,'62:6','ان سے کہو ، ” اے لوگو جو یہودی بن گئے ہو ، اگر تمہیں یہ گھمنڈ ہے کہ باقی سب لوگوں کو چھوڑ کر بس تم ہی اللہ کے چہیتے ہو تو موت کی تمناکرو۔ اگر تم اپنے اس زعم میں سچے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(62,7,'62:7','لیکن یہ ہرگز اس کی تمنا نہ کریں گے اپنے ان کرتوتوں کی وجہ سے جو یہ کرچکے ہیں ، اور اللہ ان ظالموں کو خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(62,8,'62:8','ان سے کہو ” جس موت سے تم بھاگتے ہو ، وہ تو تمہیں آکر رہے گی۔ پھر تم اس کے سامنے پیش کیے جاؤ گے جو پوشیدہ وظاہر کا جاننے والا ہے ، اور وہ تمہیں بتادے گا کہ تم کیا کچھ کرتے رہے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(62,9,'62:9','اے لوگو جو ایمان لائے ہو ، جب پکارا جائے نماز کے لئے جمعہ کے دن تو اللہ کے ذکر کی طرف دوڑو اور خرید وفروخت چھوڑ دو ، یہ تمہارے لئے زیادہ بہتر ہے اگر تم جانو۔');
INSERT INTO "sayyid_qutb" VALUES(62,10,'62:10','پھر جب نماز پوری ہوجائے تو زمین میں پھیل جاؤ اور اللہ کا فضل تلاش کرو۔ اور اللہ کو کثرت سے یاد کرتے رہو ، شاید کہ تمہیں فلاح نصیب ہوجائے۔');
INSERT INTO "sayyid_qutb" VALUES(62,11,'62:11','” اور جب انہوں نے تجارت اور کھیل تماشا ہوتے دیکھا تو اس کی طرف لپک گئے اور تمہیں کھڑا چھوڑ دیا۔ ان سے کہو ، جو کچھ اللہ کے پاس ہے وہ کھیل تماشے اور تجارت سے بہتر ہے۔ اور اللہ سب سے بہتررزق دینے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(63,1,'63:1','” اے نبی ! جب یہ منافق تمہارے پاس آتے ہیں تو کہتے ہیں : ” ہم گواہی دیتے ہیں کہ آپ یقینا اللہ کے رسول ہیں “۔ اللہ جانتا ہے کہ تم ضرور اس کے رسول ہو ، مگر اللہ گواہی دیتا ہے کہ یہ منافق قطعی جھوٹے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(63,2,'63:2','انہوں نے اپنی قسموں کو ڈھال بنا رکھا ہے اور اس طرح یہ اللہ کے راستے سے خود رکتے اور دنیا کو روکتے ہیں۔ کیسی بری حرکتیں ہیں جو یہ لوگ کررہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(63,3,'63:3','یہ سب کچھ اس وجہ سے ہے کہ ان لوگوں نے ایمان لاکر پھر کفر کیا ، اس لئے ان کے دلوں پر مہر لگا دی گئی ، اب یہ کچھ نہیں سمجھتے۔');
INSERT INTO "sayyid_qutb" VALUES(63,4,'63:4','انہیں دیکھو تو ان کے جثے تمہیں بڑے شاندار نظر آئیں۔ بولیں تو تم ان کی باتیں سنتے ہو مگر اصل میں یہ گویا لکڑی کے کندے ہیں جو دیوار کے ساتھ چن کر رکھ دیئے گئے ہوں۔ ہر زور کی آواز کو یہ اپنے خلاف سمجھتے ہیں۔ یہ پکے دشمن ہیں ، ان سے بچ کر رہو ، اللہ کی مار ان پر ، یہ کدھر الٹے پھرائے جارہے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(63,5,'63:5','” اور جب ان سے کہا جاتا ہے کہ آگو تاکہ اللہ کا رسول تمہارے لئے مغفرت کی دعا کرے تو سر جھٹکتے ہیں اور تم دیکھتے ہو کہ وہ بڑے گھمنڈ کے ساتھ آنے سے رکتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(63,6,'63:6','اے نبی ! تم چاہے ان کے لئے مغفرت کی دعا کرو یا نہ کرو ، ان کے لئے یکساں ہے ، اللہ ہرگز انہیں معاف نہ کرے گا ، اللہ فاسق لوگوں کو ہرگز ہدایت نہیں دیتا۔');
INSERT INTO "sayyid_qutb" VALUES(63,7,'63:7','یہ وہی لوگ ہیں جو کہتے ہیں کہ رسول کے ساتھیوں پر خرچ کرنا بند کرو تاکہ یہ منتشر ہوجائیں۔ حالانکہ زمین اور آسمانوں کے خزانوں کا مالک اللہ ہے ، مگر یہ منافق سمجھتے نہیں ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(63,8,'63:8','یہ کہتے ہیں کہ ہم مدینہ واپس پہنچ جائیں تو جو عزت والا وہ ذلیل کو وہاں سے نکال باہر کرے گا۔ حالانکہ عزت تو اللہ اور اس کے رسول اور مومنین کے لئے ہے ، مگر یہ منافق جانتے نہیں ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(63,9,'63:9','” اے لوگوں جو ایمان لائے ہو ، تمہارے مال اور تمہاری اولادیں تم کو اللہ کی یاد سے غافل نہ کردیں۔ جو لوگ ایسا کریں وہی خسارے میں رہنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(63,10,'63:10','جو رزق ہم نے تمہیں دیا ہے اس میں سے خرچ کرو قبل اس کے کہ تم میں سے کسی کی موت کا وقت آجائے اور اس وقت وہ کہے کہ ” اے میرے رب ، کیوں نہ تونے مجھے تھوڑی سی مہلت اور دے دی کہ میں صدقہ دیتا اور صالح لوگوں میں شامل ہوجاتا “۔');
INSERT INTO "sayyid_qutb" VALUES(63,11,'63:11','حالانکہ جب کسی کی مہلت عمل پوری ہونے کا وقت آجاتا ہے تو اللہ کسی شخص کو ہرگز مزید مہلت نہیں دیتا ، اور جو کچھ تم کرتے ہو ، اللہ اس سے باخبر ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(64,1,'64:1','” اللہ کی تسبیح کررہی ہے ہر وہ چیز جو آسمانوں میں ہے اور ہر وہ چیز جو زمین میں ہے۔ اسی کی بادشاہی ہے اور اسی کے لئے تعریف ہے اور وہ ہر چیز پر قادر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,2,'64:2','” وہی ہے جس نے تم کو پیدا کیا ، پھر تم میں سے کوئی کافر ہے اور کوئی مومن “۔ اور اللہ تعالیٰ خوب جاننے والا دیکھنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(64,3,'64:3','” اس نے زمین اور آسمانوں کو برحق پیدا کیا ہے ، اور تمہاری صورت بنائی اور بڑی عمدہ بنائی ہے ، اور اسی کی طرف آخر کار تمہیں پلٹنا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(64,4,'64:4','زمین اور آسمانوں کی ہر چیز کا اسے علم ہے ، جو کچھ تم چھپاتے ہو ، اور جو کچھ تم ظاہر کرتے ہو ، سب اس کو معلوم ہے ، اور وہ دلوں کا حال تک جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,5,'64:5','کیا تمہیں ان لوگوں کی کوئی خبر نہیں پہنچی جنہوں نے اس سے پہلے کفر کیا اور پھر اپنی شامت اعمال کا مزہ چکھ لیا ؟ اور آگے ان کے لئے ایک درد ناک عذاب ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,6,'64:6','اس انجام کے مستحق وہ اس لئے ہوئے کہ ان کے پاس ان کے رسول کھلی کھلی دلیلیں اور نشانیاں لے کر آتے رہے ، مگر انہوں نے کہا ” کیا انسان ہمیں ہدایت دیں گے ؟ “ اس طرح انہوں نے ماننے سے انکار کردیا اور منہ پھیرلیا ، تب اللہ بھی ان سے بےپرواہ ہوگیا اور اللہ تو ہے ہی بےنیاز اور اپنی ذات میں آپ محمود۔ “');
INSERT INTO "sayyid_qutb" VALUES(64,7,'64:7','” منکرین نے بڑے دعوے سے کہا ہے کہ وہ مرنے کے بعد ہرگز دوبارہ نہ اٹھائے جائیں گے۔ ان سے کہو ” نہیں ، میرے رب کی قسم تم ضرور اٹھائے جاؤ گے ، پھر ضرور تمہیں بتایا جائے گا کہ تم نے (دنیا میں) کیا کچھ کیا ہے ، اور ایسا کرنا اللہ کے لئے بہت آسان ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,8,'64:8','پس ایمان لاؤ اللہ پر ، اور اس کے رسول پر ، اور اس روشنی پر جو ہم نے نازل کی ہے۔ جو کچھ تم کرتے ہو اللہ اس سے باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,9,'64:9','(اس کا پتہ تمہیں اس روز چل جائے گا) جب اجتماع کے دن وہ تم سب کو اکٹھا کرے گا۔ وہ دن ہوگا ایک دوسرے کے مقابلے میں لوگوں کی ہار جیت کا ، جو اللہ پر ایمان لایا ہے اور نیک عمل کرتا ہے ، اللہ اس کے گناہ جھاڑ دے گا اور اسے ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ لوگ ہمیشہ ہمیشہ ان میں رہیں گے۔ یہی بڑی کامیابی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,10,'64:10','اور جن لوگوں نے کفر کیا ہے اور ہماری آیات کو جھٹلایا ہے۔ وہ دوزخ کے باشندے ہوں گے جس میں وہ ہمیشہ رہیں گی اور وہ بدترین ٹھکانا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,11,'64:11','کوئی مصیبت کبھی نہیں آتی مگر اللہ کے اذن ہی سے آتی ہے۔ جو شخص اللہ پر ایمان رکھتا ہو ، اللہ اس کے دل کو ہدایت بخشتا ہے ، اللہ کو ہر چیز کا علم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,12,'64:12','اللہ کی اطاعت کرو اور رسول کی اطاعت کرو لیکن اگر تم اطاعت سے منہ موڑتے ہو تو ہمارے رسول پر صاف صاف حق پہنچا دینے کے سوا کوئی ذمہ داری نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,13,'64:13','اللہ وہ ہے جس کے سوا کوئی خدا نہیں ، لہٰذا ایمان لانے والوں کو اللہ ہی پر بھروسہ رکھنا چاہئے۔ “');
INSERT INTO "sayyid_qutb" VALUES(64,14,'64:14','” اے لوگو جو ایمان لائے ہو ، تمہاری بیویوں اور تمہاری اولاد میں سے بعض تمہارے دشمن ہیں ، ان سے ہوشیار رہو۔ اور اگر تم عفو و درگزر سے کام لو اور معاف کردو تو اللہ غفور ورحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,15,'64:15','تمہارے مال اور تمہاری اولاد تو ایک آزمائش ہیں ، اور اللہ ہی ہے جس کے پاس بڑا اجر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(64,16,'64:16','لہٰذا جہاں تک تمہارے بس میں ہو ، اللہ سے ڈرتے رہو ، اور سنو اور اطاعت کرو ، اور اپنے مال خرچ کرو ، یہ تمہارے لئے بہتر ہے۔ جو اپنے دل کی تنگی سے محفوظ رہ گئے بس وہی فلاں پانے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(64,17,'64:17','اگر تم اللہ کو قرض حسن دو تو وہ تمہیں کئی گنا بڑھا کردے گا اور تمہارے قصوروں سے درگزر فرمائے گا ، اللہ بڑا قدردان اور بردبار ہے ،');
INSERT INTO "sayyid_qutb" VALUES(64,18,'64:18','حاضر اور غائب ہر چیز کو جانتا ہے ، زبردست اور دانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(65,1,'65:1','” اے نبی ! جب تم لوگ عورتوں کو طلاق دو تو انہیں ان کی عدت کے لئے طلاق دیا کرو اور عدت کے زمانے کا ٹھیک ٹھیک شمار کرو ، اور للہ سے ڈرو ، جو تمہارے رب ہے۔ (زمانہ عدت میں) نہ تم انہیں ان کے گھروں سے نکالو ، اور نہ وہ خود نکلیں ، الایہ کہ وہ کسی صریح برائی کی مرتکب ہوں۔ یہ اللہ کی مقرر کردہ حدیں ہیں ، اور جو کوئی اللہ کی حدوں سے تجاوز کرے گا وہ اپنے اوپر خود ظلم کرے گا۔ تم نہیں جانتے ، شاید اس کے بعد اللہ (موافقت) کی کوئی صورت پیدا کردے۔');
INSERT INTO "sayyid_qutb" VALUES(65,2,'65:2','پھر جب وہ اپنی (عدت کی) مدت کے خاتمہ پر پہنچیں تو یا انہیں بھلے طریقے سے (اپنے نکاح میں) روک رکھو ، یا بھلے طریقے پر ان سے جدا ہوجاؤ اور دو ایسے آدمیوں کو گواہ بنالو جو تم میں سے صاحب عدل ہوں اور (اے گواہ بننے والو) گواہی ٹھیک ٹھیک اللہ کے لئے ادا کرو۔ یہ باتیں ہیں جن کی تم لوگوں کو نصیحت کی جاتی ہے ، ہر اس شخص کو جو اللہ اور آخرت کے دن پر ایمان رکھتا ہو۔ جو کوئی اللہ سے ڈرتے ہوئے کام کرے گا اللہ اس کے لئے مشکلات سے نکلنے کا کوئی راستہ پیدا کردے گا');
INSERT INTO "sayyid_qutb" VALUES(65,3,'65:3','اور اسے ایسے راستے سے رزق دے گا جدھر اس کا گمان بھی نہ جاتا ہو۔ جو اللہ پر بھروسہ کرے اس کے لئے وہ کافی ہے۔ اللہ اپنا کام پورا کرکے رہتا ہے۔ اللہ نے ہر چیز کے لئے ایک تقدیر مقرر کررکھی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(65,4,'65:4','اور تمہاری عورتوں میں سے جو حیض سے مایوس ہوچکی ہوں ان کے معاملہ میں اگر تم لوگوں کو کوئی شک لاحق ہے تو (تمہیں معلوم ہو کہ ) ان کی عدت تین مہینے ہے۔ اور یہی حکم ان کا ہے جنہیں ابھی حیض نہ آیا ہو۔ اور حاملہ عورتوں کی عدت کی حد یہ ہے کہ ان کا وضع حمل ہوجائے۔ جو شخص اللہ سے ڈرے اس کے معاملہ میں وہ سہولت پیدا کردیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(65,5,'65:5','یہ اللہ کا حکم ہے۔ جو اس نے تمہاری طرف نازل کیا ہے۔ جو اللہ سے ڈرے گا اللہ اس کی برائیوں کو اس سے دو رکردے گا اور اس کو بڑا اجر دے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(65,6,'65:6','” ان کو (زمانہ عدت میں) اسی جگہ رکھوجہاں تم رہتے ہو ، جیسی کچھ بھی جگہ تمہیں میسر ہو اور انہیں تنگ کرنے کے لئے ان کو نہ ستاؤ۔ اور اگر وہ معاملہ ہوں تو ان پر اس وقت تک خرچ کرتے رہو جب تک ان کا وضع حمل نہ ہوجائے۔ پھر اگر وہ تمہارے لئے (بچے کو) دودھ پلائیں تو ان کی اجرت انہیں دو ، اور بھلے طریقے سے (اجرت کا معاملہ) باہمی گفت و شنید سے طے کرلو۔ لیکن اگر تم نے (اجرت طے کرنے میں) ایک دوسرے کو تنگ کیا تو بچے کو کوئی اور عورت دودھ پلالے گی۔');
INSERT INTO "sayyid_qutb" VALUES(65,7,'65:7','خوشحال آدمی اپنی خوشحالی کے مطابق نفقہ دے ، اور جس کو رزق کم دیا گیا ہو وہ اسی مال میں سے خرچ کرے جو اللہ نے اسے دیا ہے۔ اللہ نے جس کو جتنا کچھ دیا ہے اس سے زیادہ کا وہ اسے مکلف نہیں کرتا۔ بعید نہیں کہ اللہ تنگ دستی کے بعد فراخ دستی بھی عطا فرمادے۔');
INSERT INTO "sayyid_qutb" VALUES(65,8,'65:8','کتنی ہی بستیاں ہیں جنہوں نے اپنے رب اور اس کے رسولوں کے حکم سے سرتابی کی تو ہم نے ان سے سخت محاسبہ کیا اور ان کو بری طرح سزا دی۔');
INSERT INTO "sayyid_qutb" VALUES(65,9,'65:9','انہوں نے اپنے کیے کا مزا چکھ لیا اور ان کا انجام کار گھاٹا ہی گھاٹا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(65,10,'65:10','اللہ نے (آخرت میں) ان کے لئے سخت عذاب مہیا کررکھا ہے۔ پس اللہ سے ڈرو۔ اے صاحب عقل لوگو ، جو ایمان لائے ہو۔ اللہ نے تمہاری طرف ایک نصیحت نازل کردی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(65,11,'65:11','ایک ایسا رسول جو تم کو اللہ کی صاف صاف ہدایت دینے والی آیات سناتا ہے تاکہ ایمان لانے والوں اور نیک عمل کرنے والوں کو تاریکیوں سے نکال کر روشنی میں لے آئے۔ جو کوئی اللہ پر ایمان لائے اور نیک عمل کرے ، اللہ اسے ایسی جنتوں میں داخل کرے گا جن کے نیچے نہریں بہتی ہوں گی۔ یہ لوگ ان میں ہمیشہ ہمیشہ رہیں گے۔ اللہ نے ایسے شخص کے لئے بہترین رزق رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(65,12,'65:12','اللہ وہ ہے جس نے سات آسمان بنائے اور زمین کی قسم سے بھی انہی کے مانند۔ ان کے درمیان حکم نازل ہوتا رہنا ہے۔ (یہ بات تمہیں اس لئے بتائی جارہی ہے) تاکہ تم جان لو کہ اللہ ہر چیز پر قدرت رکھتا ہے ، اور یہ کہ اللہ کا علم ہر چیز پر محیط ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(66,1,'66:1','” اے نبی ! تم کیوں اس چیز کو حرام کرتے ہو جو اللہ نے تمہارے لئے حلال کی ہے ؟ (کیا اس لئے کہ) تم اپنی بیویوں کی خوشی چاہتے ہو ؟.... اللہ معاف کرنے والا اور رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(66,2,'66:2','اللہ نے تم لوگوں کے لئے اپنی قسموں کی پابندی سے نکلنے کا طریقہ مقرر کردیا ہے۔ اللہ تمہارا مولیٰ ہے ، اور وہی علیم و حکیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(66,3,'66:3','پھر جب اس بیوی نے (کسی اور پر) وہ راز ظاہر کردیا ، اور اللہ نے نبی کو اس (افشائے راز) کی اطلاع دے دی ، تو نبی نے اس پر کسی حد تک (اس بیوی کو) خبردار کیا اور کسی حدتتک اس سے درگزر کیا۔ پھر جب نبی نے اسے (افشائے راز کی) یہ بات بتائی تو اس نے پوچھا آپ کو اسکی کس نے خبر دی ؟ نبی نے کہا :” مجھے اس نے خبر دی جو سب کچھ جانتا ہے اور خوب باخبر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(66,4,'66:4','اگر تم دونوں اللہ سے توبہ کرتی ہو (تو یہ تمہارے لئے بہتر ہے) کیونکہ تمہارے دل سیدھی راہ سے ہٹ گئے ہیں۔ اور اگر نبی کے مقابلے میں تم نے جتھ بندی کی تو جان رکھو کہ اللہ اس کا مولیٰ ہے اور اس کے بعد جبرائیل اور تمام صالح اہل ایمان اور سب ملائکہ اس کے ساتھی اور مددگار ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(66,5,'66:5','بعید نہیں کہ اگر نبی تم سب بیویوں کو طلاق دے دے تو اللہ اسے ایسی بیویاں تمہارے بدلے میں عطا فرمادے جو تم سے بہتر ہوں۔ سچی مسلمان ، باایمان ، اطاعت گزار ، توبہ گزار ، عبادت گزار اور روزہ دار ، خواہ شوہر دیدہ ہوں یا باکرہ “۔');
INSERT INTO "sayyid_qutb" VALUES(66,6,'66:6','” اے لوگو جو ایمان لائے ہو ! بچاؤ اپنے آپ کو اور اپنے اہل و عیال کو اس آگ سے جس کا ایندھن انسان اور پتھر ہوں گے ، جس پر نہایت تندوخو اور سخت گیر فرشتے مقرر ہوں گے جو کبھی اللہ کے حکم کی نافرمانی نہیں کرتے اور جو حکم بھی انہیں دیا جاتا ہے اسے بجا لاتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(66,7,'66:7','(اس وقت کہا جائے گا کہ) اے کافرو ! آج معذرتیں پیش نہ کرو ، تمہیں تو ویسا ہی بدلہ دیا جارہا ہے جیسے تم عمل کررہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(66,8,'66:8','اے لوگو ! جو ایمان لائے ہو ، اللہ سے توبہ کرو ، خالص توبہ ، بعید نہیں کہ اللہ تمہاری برائیاں دور کردے اور تمہیں ایسی جنتوں میں داخل فرمادے جن کے نیچے نہریں بہہ رہی ہوں گی۔ یہ وہ دن ہوگا جب اللہ نے اپنے نبی کو اور ان لوگوں کو جو اس کے ساتھ ایمان لائے ہیں ، رسوا نہ کرے گا۔ ان کا نور ان کے آگے آگے اور ان کے دائیں جانب دوڑرہا ہوگا اور وہ کہہ رہے ہوں گے کہ اے ہمارے رب ہمارا نور ہمارے لئے مکمل کردے اور ہم سے درگزر فرما ، تو ہر چیز پر قدرت رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(66,9,'66:9','اے نبی کفار اور منافقین سے جہاد کرو اور ان کے ساتھ سختی سے پیش آؤ۔ ان کا ٹھکانا جہنم ہے اور وہ بہت برا ٹھکانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(66,10,'66:10','” اللہ کافروں کے معاملہ میں نوح (علیہ السلام) .... اور لوط (علیہ السلام) .... کی بیویوں کو بطور مثال پیش کرتا ہے۔ وہ ہمارے دو صالح بندوں کی زوجیت میں تھیں ، مگر انہوں نے اپنے ان شوہروں سے خیانت کی اور وہ اللہ کے مقابلہ میں ان کے کچھ بھی نہ کام آسکے۔ دونوں سے کہہ دیا گیا کہ جاؤ آگ میں جانے والوں کے ساتھ تم بھی چلی جاؤ');
INSERT INTO "sayyid_qutb" VALUES(66,11,'66:11','اور اہل ایمان کے معاملہ میں اللہ فرعون کی بیوی کی مثال پیش کرتا ہے۔ جب کہ اس نے دعا کی ” اے میرے رب ! میرے لئے اپنے ہاں جنت میں ایک گھر بنادے اور مجھے فرعون اور اس کے عمل سے بچالے اور ظالم قوم سے مجھ کو نجات دے “۔');
INSERT INTO "sayyid_qutb" VALUES(66,12,'66:12','اور عمران کی بیٹی مریم کی مثال دیتا ہے جس نے اپنی شرمگاہ کی حفاظت کی تھی ، پھر ہم نے اس کے اندر اپنی طرف سے روح پھونک دی ، اور اس نے اپنے رب کے ارشادات اور اس کی کتابوں کی تصدیق کی اور وہ اطاعت گزار لوگوں میں سے تھی “۔');
INSERT INTO "sayyid_qutb" VALUES(67,1,'67:1','” نہایت بزرگ و برتر ہے وہ جس کے ہاتھ میں (کائنات کی) سلطنت ہے اور وہ ہر چیز پر قدرت رکھتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,2,'67:2','جس نے موت اور زندگی کو ایجاد کیا تاکہ تم لوگوں کو آزما کر دیکھے کہ تم میں سے کون بہتر عمل کرنے والا ہے ، اور وہ زبردست بھی ہے اور درگزر فرمانے والا بھی “۔');
INSERT INTO "sayyid_qutb" VALUES(67,3,'67:3','” جس نے تہہ بہ تہہ آسمان بنائے۔ تم رحمن کی تخلیق میں کسی قسم کی بےربطی نہ پاؤ گے۔ پھر پلٹ کر دیکھو ، کہیں تمہیں کوئی خلل نظر آتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(67,4,'67:4','بار بار نگاہ دوڑاؤ۔ تمہاری نگاہ تھک کرنا مراد پلٹ آئے گی۔');
INSERT INTO "sayyid_qutb" VALUES(67,5,'67:5','ہم نے تمہارے قریب کے آسمان کو عظیم الشان چراغوں سے آراستہ کیا ہے اور انہیں شیاطین کو مار بھگانے کا ذریعہ بنادیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,6,'67:6','اور جن لوگوں نے اپنے رب سے کفر کیا ہے ان کے لئے جہنم کا عذاب اور وہ بہت ہی برا ٹھکانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,7,'67:7','” جب وہ اس میں پھینکے جائیں گے تو اس کے دہاڑنے کی ہولناک آواز سنیں گے');
INSERT INTO "sayyid_qutb" VALUES(67,8,'67:8','اور وہ جوش کھارہی ہوگی ، شدت غضب سے پھٹی جاتی ہوگی “” ہر بار جب کوئی انبوہ اس میں ڈالا جائے گا ، اس کے کارندے ان لوگوں سے پوچھیں گے ” کیا تمہارے پاس کوئی خبردار کرنے والا نہیں آیا تھا ؟');
INSERT INTO "sayyid_qutb" VALUES(67,9,'67:9','وہ جواب دیں گے ” ہاں ! خبردار کرنے والا ہمارے پاس آیات تھا مگر ہم نے اسے جھٹلادیا اور کہا اللہ نے کچھ بھی نازل نہیں کیا ہے ، تم بڑی گمراہی میں پڑے ہوئے ہو');
INSERT INTO "sayyid_qutb" VALUES(67,10,'67:10','اور کہیں گے ” کاش ہم سنتے یا سمجھتے تو آج اس بھڑکتی ہوئی آگ کے سزاواروں میں نہ شامل ہوتے “');
INSERT INTO "sayyid_qutb" VALUES(67,11,'67:11','اس طرح وہ اپنے قصور کا خود اعتراف کرلیں گے ، لعنت ہے ان دوزخیوں پر “');
INSERT INTO "sayyid_qutb" VALUES(67,12,'67:12','جو لوگ بےدیکھے اپنے رب سے ڈرتے ہیں ، یقینا ان کے لئے مغفرت ہے اور بڑا اجر “۔');
INSERT INTO "sayyid_qutb" VALUES(67,13,'67:13','تم خواہ چپکے سے بات کرو یا اونچی آواز سے (اللہ کے لئے یکساں ہے) وہ تو دلوں کا حال تک جانتا ہے۔ کیا وہی نہ جانے گا جس نے پیدا کیا ہے ؟ حالانکہ وہ باریک بین اور باخبر ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,14,'67:14','کیا وہی نہ جانے جس نے پیدا کیا “ حالانکہ وہ باریک بین اور باخبر ہے “');
INSERT INTO "sayyid_qutb" VALUES(67,15,'67:15','وہی تو ہے جس نے تمہارے لئے زمین کو تابع رکھا ہے ، چلو اس کی چھاتی پر اور کھاؤ خدا کا رزق ، اسی کے حصور تمہیں دوبارہ زندہ ہوکر جانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,16,'67:16','کیا تم اس سے بےخوف ہو کہ وہ جو آسمان میں ہے ، تمہیں زمین میں دھنسادے اور یکایک یہ زمین جھکولے کھانے لگے ؟');
INSERT INTO "sayyid_qutb" VALUES(67,17,'67:17','کیا تم اس سے بےخوف ہو کہ وہ جو آسمان میں ہے ، تم پر پتھراؤ کرنے والی ہوا بھیج دے ؟ پھر تمہیں معلوم ہوجائے کہ میری تنبیہہ کیسی ہوتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(67,18,'67:18','ان سے پہلے گزرے ہوئے لوگ جھٹلا چکے ہیں۔ پھر دیکھ لو کہ میری گرفت کیسی سخت تھی “۔');
INSERT INTO "sayyid_qutb" VALUES(67,19,'67:19','” کیا یہ لوگ اپنے اوپر اڑنے والے پرندوں کو پر پھیلائے اور سکیڑتے نہیں دیکھتے ؟ رحمن کے سوا کوئی نہیں جو انہیں تھامے ہوئے ہو ؟ وہی ہر چیز کا نگہبان ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,20,'67:20','بتاؤ، آخروہ کون سا لشکر تمہارے پاس ہے جو رحمن کے مقابلے میں تمہاری مدد کرسکتا ہے ؟ حقیقت یہ ہے کہ یہ منکرین دھوکے میں پڑے ہوئے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(67,21,'67:21','یا پھر بتاؤ، کون ہے جو تمہیں رزق دے سکتا ہے۔ اگر رحمن اپنا رزق روک لے ؟ دراصل یہ لوگ سرکشی اور حق سے گریز پر اڑے ہوئے ہیں۔ “');
INSERT INTO "sayyid_qutb" VALUES(67,22,'67:22','بھلا یہ سوچو ، جو شخص منہ اوندھائے چل رہا ہو ، وہ زیادہ صحیح راہ پانے والا ہے یا وہ جو سر اٹھائے سیدھا ایک ہموار سڑک پر چل رہا ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(67,23,'67:23','ان سے کہو ، اللہ ہی ہے جس نے تمہیں پیدا کیا ، تم کو سننے اور دیکھنے کی طاقتیں دیں اور سوچنے سمجھنے والے دل دئیے ، مگر تم کم ہی شکر ادا کرتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(67,24,'67:24','ان سے کہو ، اللہ ہی ہے جس نے تمہیں زمین میں پھیلایا ہے اور اسی کی طرف تم سمیٹے جاؤ گے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,25,'67:25','یہ کہتے ہیں ” اگر تم سچے ہو تو بتاؤ یہ وعدہ کب پورا ہوگا ؟ “');
INSERT INTO "sayyid_qutb" VALUES(67,26,'67:26','کہو ،” اس کا علم تو اللہ کے پاس ہے ، میں تو بس صاف صاف خبر کردینے والا ہوں “۔');
INSERT INTO "sayyid_qutb" VALUES(67,27,'67:27','پھر جب یہ اس چیز کو قریب دیکھ لیں گے تو ان سب لوگوں کے چہرے بگڑ جائیں گے ، جنہوں نے انکار کیا ہے ، اور اس وقت ان سے کہا جائے گا کہ یہی ہے وہ چیز جس کے لئے تم تقاضا کررہے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,28,'67:28','ان سے کہو ، کبھی تم نے یہ بھی سوچا کہ اللہ خواہ مجھے اور میرے ساتھیوں کو ہلاک کردے یا ہم پر رحم کرے ، کافروں کو درد ناک عذاب سے کون بچالے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(67,29,'67:29','ان سے کہو ، وہ بڑا رحیم ہے ، اسی پر ہم ایمان لائے ہیں ، اور اسی پر ہمارا بھروسہ ہے ، عنقریب تمہیں معلوم ہوجائے گا کہ صریح گمراہی میں پڑا ہوا کون ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(67,30,'67:30','ان سے کہو ، کبھی تم نے یہ بھی سوچا کہ اگر تمہارے کنوؤں کا پانی زمین میں اتر جائے تو کون ہے جو اس پانی کی بہتی ہوئی سوتیں تمہیں نکال کر لادے گا ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(68,1,'68:1','” ن۔ قسم ہے قلم کی اور اس چیز کی جسے لکھنے والے لکھ رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(68,2,'68:2','تم اپنے رب کے فضل سے مجنون نہیں ہو۔');
INSERT INTO "sayyid_qutb" VALUES(68,3,'68:3','اور یقینا تمہارے لئے ایسا اجر ہے جس کا سلسلہ کبھی ختم ہونے والا نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(68,4,'68:4','اور بیشک تم اخلاق کے بڑے مرتبے پر ہو۔');
INSERT INTO "sayyid_qutb" VALUES(68,5,'68:5','عنقریب تم بھی دیکھ لوگے اور وہ بھی دیکھ لیں گے');
INSERT INTO "sayyid_qutb" VALUES(68,6,'68:6','کہ تم میں سے کون گمراہی میں مبتلا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(68,7,'68:7','تمہارا رب ان لوگوں کو بھی خوب جانتا ہے جو اس کی راہ سے بھٹکے ہوئے ہیں ، اور وہی ان کو بھی اچھی طرح جانتا ہے جو راہ راست پر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(68,8,'68:8','لہٰذا تم ان جھٹلانے والوں کے دباؤ میں ہرگز نہ آؤ۔');
INSERT INTO "sayyid_qutb" VALUES(68,9,'68:9','یہ تو چاہتے ہیں کہ کچھ تم مداہنت کرو تو یہ بھی مداہنت کریں۔');
INSERT INTO "sayyid_qutb" VALUES(68,10,'68:10','ہرگز نہ دبو کسی ایسے شخص سے جو بہت قسمیں کھانے والا بےوقعت آدمی ہے');
INSERT INTO "sayyid_qutb" VALUES(68,11,'68:11','، طعنے دیتا ہے ، چغلیاں کھاتا پھرتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(68,12,'68:12','بھلائی سے روکتا ہے ، ظلم و زیادتی میں حد سے گزر جانے والا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(68,13,'68:13','سخت بداعمال ہے ، جفاکار ہے اور ان سب عیوب کے ساتھ بداصل ہے');
INSERT INTO "sayyid_qutb" VALUES(68,14,'68:14','، اس بنا پر کہ وہ بہت مال اور اولاد رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(68,15,'68:15','جب ہماری آیات اس کو سنائی جاتی ہیں تو کہتا ہے یہ تو اگلے وقتوں کے افسانے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(68,16,'68:16','عنقریب ہم اس کی سونڈ پر داغ لگائیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(68,17,'68:17','ہم نے ان (اہل مکہ) کو اسی طرح آزمائش میں ڈالا ہے جس طرح ایک باغ کے مالکوں کو آزمائش میں ڈالا تھا ، جب انہوں نے قسم کھائی کہ صبح سویرے ضرور اپنے باغ کے پھل توڑیں گے');
INSERT INTO "sayyid_qutb" VALUES(68,18,'68:18','” اور وہ کوئی استثناء نہیں کررہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(68,19,'68:19','رات کو وہ سوئے پڑے تھے کہ تمہارے رب کی طرف سے ایک بلا اس باغ میں پھر گئی');
INSERT INTO "sayyid_qutb" VALUES(68,20,'68:20','اور اس کا ایسا حال ہوگیا جیسے کٹی ہوئی فصل ہو۔');
INSERT INTO "sayyid_qutb" VALUES(68,21,'68:21','صبح ان لوگوں نے ایک دوسرے کو پکارا');
INSERT INTO "sayyid_qutb" VALUES(68,22,'68:22','کہ اگر پھل توڑنے ہیں تو سویرے سویرے اپنی کھیتی کی طرف نکل چلو۔');
INSERT INTO "sayyid_qutb" VALUES(68,23,'68:23','چناچہ وہ چل پڑے اور آپس میں چپکے چپکے کہتے جاتے تھے');
INSERT INTO "sayyid_qutb" VALUES(68,24,'68:24','کہ آج کوئی مسکین تمہارے پاس باغ میں نہ آنے پائے۔');
INSERT INTO "sayyid_qutb" VALUES(68,25,'68:25','وہ کچھ نہ دینے کا فیصلہ کیے ہوئے صبح سویرے جلدی جلدی اس طرح وہاں گئے جیسے کہ وہ (پھل منع کرنے پر) قادر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(68,26,'68:26','مگر جب باغ کو دیکھا تو کہنے لگے ، ” ہم راستہ بھول گئے ہیں...');
INSERT INTO "sayyid_qutb" VALUES(68,27,'68:27','. نہیں ، بلکہ ہم محروم رہ گئے “۔');
INSERT INTO "sayyid_qutb" VALUES(68,28,'68:28','ان میں جو سب سے بہتر تھا اس نے کہا ” میں نے تم سے کہا نہ تھا کہ تم تسبیح کیوں نہیں کرے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(68,29,'68:29','وہ پکار اٹھے ” پاک ہے ہمارا رب ، واقعی ہم گناہ گار تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(68,30,'68:30','پھر ان میں سے ہر ایک دوسرے کو ملامت کرنے لگا۔');
INSERT INTO "sayyid_qutb" VALUES(68,31,'68:31','آخر کار انہوں نے کہا ” افسوس ہمارے حال پر ، بیشک ہم سرکش ہوگئے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(68,32,'68:32','بعید نہیں کہ ہمارا رب ہمیں بدلے میں اس سے بہتر باغ عطا فرمائے ، ہم اپنے رب کی طرف رجوع کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(68,33,'68:33','ایسا ہوتا ہے عذاب۔ اور آخرت کا عذاب اس سے بڑا ہے ، کاش یہ لوگ اس کو جانتے “۔');
INSERT INTO "sayyid_qutb" VALUES(68,34,'68:34','یقینا خدا ترس لوگوں کے لئے ان کے رب کے ہاں نعمت بھری جنتیں ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(68,35,'68:35','فرمانبرداروں کا حال مجرموں کا سا کردیں ؟');
INSERT INTO "sayyid_qutb" VALUES(68,36,'68:36','” تم لوگوں کو کیا ہوگیا ہے تم کیسے حکم لگاتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(68,37,'68:37','کیا تمہارے پاس کوئی کتاب ہے جس میں تم یہ پڑھتے ہو');
INSERT INTO "sayyid_qutb" VALUES(68,38,'68:38','کہ تمہارے لئے ضرور وہاں وہی کچھ ہے جو تم اپنے لئے پسند کرتے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(68,39,'68:39','یا پھر کیا تمہارے لئے روز قیامت تک ہم پر کچھ عہدوپیمان ثابت ہیں کہ تمہیں وہی کچھ ملے گا جس کا تم حکم لگاؤ؟');
INSERT INTO "sayyid_qutb" VALUES(68,40,'68:40','ان سے پوچھو تم میں سے کون اس کا ضامن ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(68,41,'68:41','یا پھر ان کے ٹھہرائے ہوئے کچھ شریک ہیں (جنہوں نے اس کا ذمہ لیا ہو) ؟ یہ بات ہے تو لائیں اپنے ان شریکوں کو اگر یہ سچے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(68,42,'68:42','جس روز سخت وقت آپڑے گا اور لوگوں کو سجدہ کرنے کے لئے بلایا جائے گا تو یہ لوگ سجدہ نہ کرسکیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(68,43,'68:43','ان کی نگاہیں نیچی ہوں گی ، ذلت ان پر چھارہی ہوگی۔ یہ جب صحیح وسالم تھے ، اس وقت انہیں سجدے کے لئے بلایا جاتا تھا (اور یہ انکار کرتے تھے) ۔');
INSERT INTO "sayyid_qutb" VALUES(68,44,'68:44','پس اے نبی ! تم اس کلام کے جھٹلانے والوں کا معاملہ مجھ پر چھوڑ دو ۔ ہم ایسے طریقہ سے ان کو بتدریج تباہی کی طرف سے جائیں گے کہ ان کو خبر بھی نہ ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(68,45,'68:45','میں ان کی رسی دراز کررہا ہوں ، میری چال بڑی زبردست ہے۔');
INSERT INTO "sayyid_qutb" VALUES(68,46,'68:46','کیا تم ان سے کوئی اجر طلب کررہے ہو کہ یہ اس چٹی کے بوجھ تلے دبے جارہے ہوں ؟');
INSERT INTO "sayyid_qutb" VALUES(68,47,'68:47','کیا ان کے پاس غیب کا علم ہے جسے یہ لکھ رہے ہوں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(68,48,'68:48','” اچھا ، اپنے رب کا فیصلہ صادر ہونے تک صبر کرو اور مچھلی والے (یونس علیہ السلام) کی طرح نہ ہوجاؤ، جب اس نے پکارا تھا اور وہ غم سے بھرا ہوا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(68,49,'68:49','اگر اس کے رب کی مہربانی اس کے شامل حال نہ ہوجاتی تو وہ مذموم ہوکر چٹیل میدان میں پھینک دیا جاتا۔');
INSERT INTO "sayyid_qutb" VALUES(68,50,'68:50','آخر کار اس کے رب نے اسے برگزیدہ فرما لیا اور اسے صالح بندوں میں شامل کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(68,51,'68:51','جب یہ کافر لوگ کلام نصیحت (قرآن) سنتے ہیں تو تمہیں ایسی نظروں سے دیکھتے ہیں کہ گویا تمہارے قدم اکھاڑ دیں گے ، اور کہتے ہیں کہ یہ ضرور دیوانہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(68,52,'68:52','حالانکہ یہ تو سارے جہان والوں کے لئے ایک نصیحت ہے۔ “');
INSERT INTO "sayyid_qutb" VALUES(69,1,'69:1','ہونی شدنی !');
INSERT INTO "sayyid_qutb" VALUES(69,2,'69:2','” کیا ہے وہ ہونی شدنی ؟');
INSERT INTO "sayyid_qutb" VALUES(69,3,'69:3','اور تم کیا جانو کہ وہ کیا ہے ہونی شدنی ؟ “');
INSERT INTO "sayyid_qutb" VALUES(69,4,'69:4','” ثمود اور عاد نے اس اچانک ٹوٹ پڑنے والی آفت کو جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(69,5,'69:5','تو ثمودایک سخت حادثہ سے ہلاک کیے گئے۔');
INSERT INTO "sayyid_qutb" VALUES(69,6,'69:6','اور عاد ایک بڑی شدید طوفانی آندھی سے تباہ کردیئے گئے۔');
INSERT INTO "sayyid_qutb" VALUES(69,7,'69:7','اللہ تعالیٰ نے مسلسل سات رات اور آٹھ دن ان پر مسلط رکھا ، تم دیکھتے کہ وہ وہاں اس طرح پچھڑے پڑے ہیں گویا وہ کھجور کے بوسیدہ تنے ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(69,8,'69:8','اب کیا ان میں سے کوئی باقی بچا نظر آتا ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(69,9,'69:9','” اور اسی خطائے عظیم کا ارتکاب فرعون اور اس سے پہلے کے لوگوں نے اور تلپٹ ہوجانے والی بستیوں نے کیا۔');
INSERT INTO "sayyid_qutb" VALUES(69,10,'69:10','ان سب نے اپنے رب کے رسول کی بات نہ مانی تو اس نے ان کو بڑی سختی کے ساتھ پکڑا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,11,'69:11','” جب پانی کا طوفان حد سے گزر گیا تو ہم نے تم کو کشتی میں سوار کردیا تھا');
INSERT INTO "sayyid_qutb" VALUES(69,12,'69:12','تاکہ اس واقعہ کو تمہارے لئے ایک سبق آموز یادگار بنادیں اور یاد رکھنے والے کان اس کی یاد محفوظ رکھیں “۔');
INSERT INTO "sayyid_qutb" VALUES(69,13,'69:13','” پھر جب ایک دفعہ صور پھونک ماردی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(69,14,'69:14','اور زمین اور پہاڑوں کو اٹھا کر ایک ہی چوٹ میں ریزہ ریزہ کردیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(69,15,'69:15','اس روز وہ ہونے والا واقعہ پیش آجائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(69,16,'69:16','اس دن آسمان پھٹے گا اور اس کی بندش ڈھیلی پڑجائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(69,17,'69:17','فرشتے ان کے اطراف و جوانب میں ہوں گے اور آٹھ فرشتے اس روز تیرے رب کا عرش اپنے اوپر اٹھائے ہوئے ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(69,18,'69:18','” وہ دن ہوگا جب تم لوگ پیش کیے جاؤ گے ، تمہارا کوئی راز بھی چھپا نہ رہ جائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,19,'69:19','” اس وقت جس کا نامہ اعمال اس کے سیدھے ہاتھ دیا جائے گا وہ کہے گا ” لو دیکھو “ پڑھو میرا نامہ اعمال ،');
INSERT INTO "sayyid_qutb" VALUES(69,20,'69:20','میں سمجھتا تھا کہ مجھے ضرور اپنا حساب ملنے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(69,21,'69:21','پس وہ دل پسند عیش میں ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(69,22,'69:22','عالی مقام جنت میں ،');
INSERT INTO "sayyid_qutb" VALUES(69,23,'69:23','جس کے پھلوں کے گچھے جھکے پڑ رہے ہوں گے۔');
INSERT INTO "sayyid_qutb" VALUES(69,24,'69:24','(ایسے لوگوں سے کہا جائے گا) مزے سے کھاؤ اور پیو اپنے ان اعمال کے بدلے جو تم نے گزرے ہوئے دنوں میں کیے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(69,25,'69:25','” اور جس کا نامہ اعمال اس کے بائیں ہاتھ میں دیا جائے گا وہ کہے گا ” کاش میرا اعمال نامہ مجھے نہ دیا گیا ہوتا');
INSERT INTO "sayyid_qutb" VALUES(69,26,'69:26','اور میں نہ جانتا کہ میرا حساب کیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(69,27,'69:27','کاش میری وہی موت (جو دنیا میں آئی تھی) فیصلہ کن ہوتی۔');
INSERT INTO "sayyid_qutb" VALUES(69,28,'69:28','آج میرا مال میرے کچھ کام نہ آیا۔');
INSERT INTO "sayyid_qutb" VALUES(69,29,'69:29','میرا سارا اقتدار ختم ہوگیا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,30,'69:30','”(حکم ہوگا) پکڑو اسے اور اس کی گردن میں طوق ڈال دو ،');
INSERT INTO "sayyid_qutb" VALUES(69,31,'69:31','پھر اسے جہنم میں جھونک دو ،');
INSERT INTO "sayyid_qutb" VALUES(69,32,'69:32','پھر اس کو ستر ہاتھ لمبی زنجیر میں جکڑ دو “۔');
INSERT INTO "sayyid_qutb" VALUES(69,33,'69:33','” یہ نہ اللہ بزرگ و برتر پر ایمان لاتا تھا');
INSERT INTO "sayyid_qutb" VALUES(69,34,'69:34','اور نہ مسکین کو کھانا کھلانے کی ترغیب دیتا تھا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,35,'69:35','” لہٰذا آج نہ یہاں اس کا کوئی یار غم خوار ہے');
INSERT INTO "sayyid_qutb" VALUES(69,36,'69:36','اور نہ زخموں کے دھو ون کے سوا اس کے لئے کوئی کھانا ،');
INSERT INTO "sayyid_qutb" VALUES(69,37,'69:37','جسے خطاکاروں کے سوا کوئی نہیں کھاتا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,38,'69:38','پس ، میں قسم نہیں کھاتا ہوں اور چیزوں کی بھی جو تم دیکھتے ہو');
INSERT INTO "sayyid_qutb" VALUES(69,39,'69:39','اور ان کی بھی جنہیں تم نہیں دیکھتے ،');
INSERT INTO "sayyid_qutb" VALUES(69,40,'69:40','یہ ایک رسول کریم کا قول ہے ،');
INSERT INTO "sayyid_qutb" VALUES(69,41,'69:41','کسی شاعر کا قول نہیں ہے ، تم لوگ کم ہی ایمان لاتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(69,42,'69:42','اور نہ یہ کسی کاہن کا قول ہے ، تم لوگ کم ہی غور کرتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(69,43,'69:43','یہ رب العالمین کی طرف سے نازل ہوا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(69,44,'69:44','” اور اگر اس (نبی) نے خود گھڑ کر کوئی بات ہماری طرف منسوب کی ہوتی');
INSERT INTO "sayyid_qutb" VALUES(69,45,'69:45','تو ہم اس کا دایاں ہاتھ پکڑ لیتے');
INSERT INTO "sayyid_qutb" VALUES(69,46,'69:46','اور اس کی رگ گردن کاٹ ڈالتے ،');
INSERT INTO "sayyid_qutb" VALUES(69,47,'69:47','پھر تم میں سے کوئی (ہمیں) اس کام سے روکنے والا نہ ہوتا “۔');
INSERT INTO "sayyid_qutb" VALUES(69,48,'69:48','در حقیقت یہ پرہیز گار لوگوں کے لئے ایک نصیحت ہے۔');
INSERT INTO "sayyid_qutb" VALUES(69,49,'69:49','اور ہم جانتے ہیں کہ تم میں سے کچھ لوگ جھٹلانے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(69,50,'69:50','ایسے کافروں کے لئے یقینا یہ موجب حسرت ہے');
INSERT INTO "sayyid_qutb" VALUES(69,51,'69:51','اور یہ بالکل یقینی حق ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(69,52,'69:52','پس اے نبی ! اپنے رب عظیم کے نام کی تسبیح کرو “');
INSERT INTO "sayyid_qutb" VALUES(70,1,'70:1','مانگنے والے نے عذاب مانگا ہے (وہ عذاب) جو ضرور واقع ہونے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(70,2,'70:2','، کافروں کے لئے ہے ، کوئی اسے دفع کرنے والا نہیں ،');
INSERT INTO "sayyid_qutb" VALUES(70,3,'70:3','اس خدا کی طرف سے ہے جو عروج کے زینوں کا مالک ہے۔');
INSERT INTO "sayyid_qutb" VALUES(70,4,'70:4','ملائکہ اور روح اس کے حضور صلى الله عليه وسلم چڑھ کر جاتے ہیں۔ ایک ایسے دن میں جس کی مقدار پچاس ہزار سال ہے۔');
INSERT INTO "sayyid_qutb" VALUES(70,5,'70:5','پس اے نبی صبر کرو ، شائستہ صبر۔');
INSERT INTO "sayyid_qutb" VALUES(70,6,'70:6','یہ لوگ اسے دور سمجھتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,7,'70:7','اور ہم اسے قریب دیکھ رہے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(70,8,'70:8','(وہ عذاب اس روز ہوگا) جس روز آسمان پگھلی ہوئی چاندی کی طرح ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(70,9,'70:9','اور پہاڑ رنگ برنگ کے دھنکے ہوئے اون جیسے ہوجائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(70,10,'70:10','اور کوئی جگری دوست اپنے جگری دوست کو نہ پوچھے گا');
INSERT INTO "sayyid_qutb" VALUES(70,11,'70:11','حالانکہ وہ ایک دوسرے کو دکھائے جائیں گے۔ مجرم چاہے گا کہ اس کے عذاب سے بچنے کے لئے اپنی اولاد کو ،');
INSERT INTO "sayyid_qutb" VALUES(70,12,'70:12','اپنی بیوی کو ، اپنے بھائی کو ،');
INSERT INTO "sayyid_qutb" VALUES(70,13,'70:13','اپنے قریب ترین خاندان کو جو اسے پناہ دینے والا تھا ،');
INSERT INTO "sayyid_qutb" VALUES(70,14,'70:14','اور روئے زمین کے سب لوگوں کو فدیہ میں دے دے اور یہ تدبیرا سے نجات دلادے۔');
INSERT INTO "sayyid_qutb" VALUES(70,15,'70:15','وہ تو بھڑکتی ہوئی آگ کی لپٹ ہوگی');
INSERT INTO "sayyid_qutb" VALUES(70,16,'70:16','جو گوشت پوست کو چاٹ جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(70,17,'70:17','پکار پکار کر اپنی طرف بلائے گی ہر اس شخص کو ، جس نے حق سے منہ موڑا اور پیٹھ پھیری');
INSERT INTO "sayyid_qutb" VALUES(70,18,'70:18','اور مال جمع کیا اور سینت سینت کر رکھا “۔');
INSERT INTO "sayyid_qutb" VALUES(70,19,'70:19','” انسان تھرڈلاپیدا کیا گیا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(70,20,'70:20','جب اس پر مصیبت آتی ہے تو گھبرا اٹھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(70,21,'70:21','اور جب اسے خوشحالی نصیب ہوتی ہے تو بخل کرنے لگتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(70,22,'70:22','مگر وہ لوگ (اس عیب سے بچے ہوئے ہیں) جو نماز پڑھنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,23,'70:23','جو اپنی نماز کی ہمیشہ پابندی کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(70,24,'70:24','جن کے مالوں میں ایک مقرر حق ہے ،');
INSERT INTO "sayyid_qutb" VALUES(70,25,'70:25','سائل اور محروم کا');
INSERT INTO "sayyid_qutb" VALUES(70,26,'70:26','جو روز جزاء کو برحق مانتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(70,27,'70:27','جو اپنے رب کے عذاب سے ڈرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,28,'70:28','کیونکہ ان کے رب کا عذاب ایسی چیز نہیں ہے جس سے کوئی بےخوف ہو ،');
INSERT INTO "sayyid_qutb" VALUES(70,29,'70:29','جو اپنی شرم گاہوں کی حفاظت کرتے ہیں .');
INSERT INTO "sayyid_qutb" VALUES(70,30,'70:30','بجز اپنی بیویوں یا اپنی مملو کہ عورتوں کے ، جن سے محفوظ نہ رکھنے میں ان پر کوئی ملامت نہیں ،');
INSERT INTO "sayyid_qutb" VALUES(70,31,'70:31','البتہ جو اس کے علاوہ کچھ اور چاہیں وہی حد سے تجاوز کرنے والے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,32,'70:32','جو اپنی امانتوں کی حفاظت اور اپنے عہد کا پاس کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,33,'70:33','جو اپنی گواہیوں میں راست بازی پر قائم رہتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,34,'70:34','اور جو اپنی نماز کی حفاظت کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(70,35,'70:35','یہ لوگ عزت کے ساتھ جنت کے باغوں میں رہیں گے');
INSERT INTO "sayyid_qutb" VALUES(70,36,'70:36','” پس اے نبی ! کیا بات ہے کہ یہ منکرین تمہاری طرف دوڑے چلے آرہے ہیں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(70,37,'70:37','دائیں اور بائیں سے گروہ درگروہ');
INSERT INTO "sayyid_qutb" VALUES(70,38,'70:38','کیا ان میں سے ہر ایک یہ لالچ رکھتا ہے کہ وہ نعمت بھری جنت میں داخل کردیا جائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(70,39,'70:39','” ہرگز نہیں ، ہم نے جس چیز سے ان کو پیدا کیا ہے اسے یہ خود جانتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(70,40,'70:40','” پس میں قسم نہیں کھاتا ہوں مشرقوں اور مغربوں کے مالک کی ، ہم اس پر قادر ہیں');
INSERT INTO "sayyid_qutb" VALUES(70,41,'70:41','کہ ان کی جگہ ان سے بہتر لوگ لے آئیں اور کوئی ہم سے بازی لے جانے والا نہیں ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(70,42,'70:42','” لہٰذا انہیں اپنی بیہودہ باتوں اور اپنے کھیل میں پڑا رہنے دو یہاں تک کہ یہ اپنے اس دن کو پہنچ جائیں جس کا ان سے وعدہ کیا جارہا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(70,43,'70:43','جب یہ اپنی قبروں سے نکل کر اس طرح دوڑے جارہے ہوں گے جیسے اپنے بتوں کے استھانوں کی طرف دوڑ رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(70,44,'70:44','ان کی نگاہیں جھکی ہوئی ہوں گی ، ذلت ان پر چھارہی ہوگی۔ وہ دن ہے جس کا ان سے وعدہ کیا جارہا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(71,1,'71:1','” ہم نے نوح (علیہ السلام) کو اس کی قوم کی طرف بھیجا (اس ہدایت کے ساتھ) کہ اپنی قوم کے لوگوں کو خبردار کردے قبل اس کے کہ ان پر ایک درد ناک عذاب آئے۔');
INSERT INTO "sayyid_qutb" VALUES(71,2,'71:2','اس نے کہا : ” اے میری قوم کے لوگو ! میں تمہارے لئے ایک صاف صاف خبردار کردینے والا (پیغمبر) ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(71,3,'71:3','(تم کو آگاہ کرتا ہوں) کہ اللہ کی بندگی کرو اور اس سے ڈرو اور میری اطاعت کرو ،');
INSERT INTO "sayyid_qutb" VALUES(71,4,'71:4','اللہ تمہارے گناہوں سے درگزر فرمائے گا اور تمہیں ایک وقت مقرر تک باقی رکھے گا۔ حقیقت یہ ہے کہ اللہ کا مقرر کیا ہوا وقت جب آجاتا ہے تو پھر ٹالا نہیں جاتا ، کاش تمہیں اس کا علم ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(71,5,'71:5','اس نے عرض کیا ” اے میرے رب ! میں نے اپنی قوم کے لوگوں کو شب وروز پکارا');
INSERT INTO "sayyid_qutb" VALUES(71,6,'71:6','مگر میری پکار نے ان کے فرار ہی میں اضافہ کیا۔');
INSERT INTO "sayyid_qutb" VALUES(71,7,'71:7','اور جب بھی میں ان نے ان کو بلایا تاکہ تو انہیں معاف کردے ، انہوں نے کانوں میں انگلیاں ٹھونس لیں اور اپنے کپڑوں سے منہ ڈھانک لئے اور اپنی روش پر اڑ گئے اور بڑا تکبر کیا۔');
INSERT INTO "sayyid_qutb" VALUES(71,8,'71:8','پھر میں نے ان کو ہانکے پکارے دعوت دی۔');
INSERT INTO "sayyid_qutb" VALUES(71,9,'71:9','پھر میں نے علانیہ بھی ان کو تبلیغ کی اور چپکے چپکے بھی سمجھایا۔');
INSERT INTO "sayyid_qutb" VALUES(71,10,'71:10','میں نے کہا : ” اپنے رب سے معافی مانگو ، بیشک وہ بڑا معاف کرنے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(71,11,'71:11','وہ تم پر آسمان سے خوب بارشیں برسائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(71,12,'71:12','تمہیں مال اور اولاد سے نوازے گا ، تمہارے لئے باغ پیدا کرے گا اور تمہارے لئے نہریں جاری کردے گا۔');
INSERT INTO "sayyid_qutb" VALUES(71,13,'71:13','تمہیں کیا ہوگیا ہے کہ اللہ کے لئے تم کسی وقار کی توقع نہیں رکھتے ؟');
INSERT INTO "sayyid_qutb" VALUES(71,14,'71:14','حالانکہ اس نے طرح طرح سے تمہیں بنایا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(71,15,'71:15','کیا دیکھتے نہیں ہو کہ اللہ نے کس طرح سات آسمان تہ بہ تہ بنائے');
INSERT INTO "sayyid_qutb" VALUES(71,16,'71:16','اور ان میں چاند کو نور اور سورج کو چراغ بنایا ؟');
INSERT INTO "sayyid_qutb" VALUES(71,17,'71:17','اور اللہ نے تم کو زمین سے عجیب طرح اگایا ،');
INSERT INTO "sayyid_qutb" VALUES(71,18,'71:18','پھر وہ تمہیں اسی زمین میں واپس لے جائے گا اور اس سے یکایک تم کو نکال کھڑا کرے گا۔');
INSERT INTO "sayyid_qutb" VALUES(71,19,'71:19','اور اللہ نے زمین کو تمہارے لئے فرش کی طرح بچھا دیا');
INSERT INTO "sayyid_qutb" VALUES(71,20,'71:20','تاکہ تم اس کے اندر کھلے راستوں میں چلو “۔');
INSERT INTO "sayyid_qutb" VALUES(71,21,'71:21','نوح نے کہا :” میرے رب ، انہوں نے میری بات رد کردی اور ان (رئیسوں) کی پیروی کی جو مال اور اولاد پاکر اور زیادہ نامراد ہوگئے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(71,22,'71:22','ان لوگوں نے بڑا بھاری مکر کا جال پھیلا رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(71,23,'71:23','انہوں نے کہا ہرگز نہ چھوڑ دو اپنے معبودوں کو ، اور چھوڑو ود اور سواع کو ، اور نہ یغوث اور یعوق اور نسر کو۔');
INSERT INTO "sayyid_qutb" VALUES(71,24,'71:24','انہوں نے بہت لوگوں کو گمراہ کیا ہے ، اور تو بھی ان ظالموں کو گمراہی کے سوا کسی چیز میں ترقی نہ دے “۔');
INSERT INTO "sayyid_qutb" VALUES(71,25,'71:25','” اپنی خطاؤں کی بنا پر ہی وہ غرق کئے گئے اور آگ میں جھونک دیئے گئے ، پھر انہوں نے اپنے لئے اللہ سے بچانے والا کو مددگار نہ پایا “۔');
INSERT INTO "sayyid_qutb" VALUES(71,26,'71:26','” اور نوح (علیہ السلام) نے کہا : ” میرے رب ، ان کافروں میں سے کوئی زمین پر بسنے والا نہ چھوڑ۔');
INSERT INTO "sayyid_qutb" VALUES(71,27,'71:27','اگر تونے ان کو چھوڑ دیا تو یہ تیرے بندوں کو گمراہ کریں گے اور ان کی نسل سے جو بھی پیدا ہوگا ، بدکار اور سخت کافر ہی ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(71,28,'71:28','میرے رب ، مجھے اور میرے والدین کو ، اور ہر اس شخص کو جو میرے گھر میں مومن کی حیثیت سے داخل ہوا ہے ، اور سب مومن مردوں اور عورتوں کو معاف فرمادے ، اور ظالموں کے لئے ہلاکت کے سوا کسی چیز میں اضافہ نہ کر “۔');
INSERT INTO "sayyid_qutb" VALUES(72,1,'72:1','” اے نبی کہو ، میری طرف وحی بھیجی گئی ہے کہ جنوں کے ایک گروہ نے غور سے سنا پھر جاکر اپنی قوم کے لوگوں سے کہا : ” ہم نے ایک بڑا عجیب قرآن سنا ہے');
INSERT INTO "sayyid_qutb" VALUES(72,2,'72:2','جو راہ راست کی طرف راہنمائی کرتا ہے اس لئے ہم اس پر ایمان لے آئے ہیں اور اب ہم ہرگز اپنے رب کے ساتھ کسی کو شریک نہیں کریں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,3,'72:3','” اور یہ کہ ہمارے رب کی شان بہت اعلیٰ وارفع ہے اس نے کسی کو بیوی یا بیٹا نہیں بنایا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,4,'72:4','” اور یہ کہ ہمارے نادان لوگ اللہ کے بارے میں بہت خلاف حق باتیں کہتے رہتے ہیں “');
INSERT INTO "sayyid_qutb" VALUES(72,5,'72:5','اور یہ کہ ” ہم نے سمجھا تھا کہ انسان اور جن کبھی خدا کے بارے میں جھوٹ نہیں بول سکتے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,6,'72:6','اور یہ کہ ” انسانوں میں سے کچھ لوگ جنوں میں سے کچھ لوگوں کی پناہ مانگا کرتے تھے ، اس طرح انہوں نے انسانوں کی بےچینی کو اور زیادہ بڑھا دیا “۔');
INSERT INTO "sayyid_qutb" VALUES(72,7,'72:7','اور یہ کہ ” انسانوں نے بھی وہی گمان کیا جیسا تمہارا گمان تھا کہ اللہ کسی کو رسول بنا کر نہ بھیجے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(72,8,'72:8','اور یہ کہ ” ہم نے آسمان کو ٹٹولا تو دیکھا کہ وہ پہریداروں سے پٹا پڑا ہے اور شہابوں کی بارش ہورہی ہے “');
INSERT INTO "sayyid_qutb" VALUES(72,9,'72:9','اور یہ کہ ” پہلے ہم سن گن لینے کے لئے آسمان میں بیٹھنے کی جگہ پالیتے تھے مگر اب جو چوری چھپے سننے کی کوشش کرتا ہے وہ اپنے لئے گھات میں ایک شہاب ثاقب لگا ہوا پاتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,10,'72:10','اور یہ کہ ” ہماری سمجھ میں نہ آتا تھا کہ آیا زمین والوں کے ساتھ کوئی برا معاملہ کرنے کا ارادہ کیا گیا ہے یا ان کا رب انہیں راہ راست دکھانا چاہتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,11,'72:11','” اور یہ کہ ” ہم میں سے کچھ لوگ صالح ہیں اور کچھ اس سے فروتر ہیں ، ہم مختلف طریقوں سے بنے ہوئے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(72,12,'72:12','اور یہ کہ ” ہم سمجھتے تھے کہ نہ زمین میں ہم اللہ کو عاجز کرسکتے ہیں اور نہ بھاگ کر اسے ہرا سکتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(72,13,'72:13','اور یہ کہ ” ہم نے جب ہدایت کی تعلیم سنی تو ہم اس پر ایمان لے آئے۔ اب جب کوئی بھی اپنے رب پر ایمان لے آئے گا اسے کسی حق تلفی یا ظلم کا خوف نہ ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(72,14,'72:14','اور یہ کہ ” ہم میں سے کچھ مسلم (اللہ کی اطاعت گزار) ہیں اور کچھ حق سے منحرف۔ تو جنہوں نے اسلام (اطاعت کا راستہ) اختیار کرلیا ” انہوں نے نجات کی راہ ڈھونڈلی');
INSERT INTO "sayyid_qutb" VALUES(72,15,'72:15','اور جو حق سے منحرف ہیں وہ جہنم کا ایندھن بننے والا ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(72,16,'72:16','اور لوگ اگر راہ راست پر ثابت قدمی سے چلتے تو ہم انہیں خوب سیراب کرتے');
INSERT INTO "sayyid_qutb" VALUES(72,17,'72:17','” تاکہ اس نعمت سے ان کی آزمائش کریں اور جو اپنے رب کے ذکر سے منہ موڑے گا اس کا رب اسے سخت عذاب میں مبتلا کردے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(72,18,'72:18','اور یہ کہ مسجدیں اللہ کے لئے ہیں ، لہٰذا ان میں اللہ کے ساتھ کسی اور کو نہ پکارو “۔');
INSERT INTO "sayyid_qutb" VALUES(72,19,'72:19','اور یہ کہ جب اللہ کا بندہ اس کو پکارنے کے لئے کھڑا ہو تو لوگ اس پر ٹوٹ پڑنے کے لئے تیار ہوگئے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,20,'72:20','اے نبی ! کہو کہ ” میں تو اپنے رب کو پکارتا ہوں اور اس کے ساتھ کسی کو شریک نہیں کرتا “۔');
INSERT INTO "sayyid_qutb" VALUES(72,21,'72:21','” کہو ، ” میں تم لوگوں کے لئے کسی نقصان کا اختیار رکھتا ہوں نہ کسی بھلائی کا “');
INSERT INTO "sayyid_qutb" VALUES(72,22,'72:22','۔ کہو ، ” مجھے اللہ کی گرفت سے کوئی بچا نہیں سکتا اور نہ میں اس کے دامن کے سوا کوئی جائے پناہ پاسکتا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(72,23,'72:23','میرا کام اس کے سوا کچھ نہیں ہے کہ اللہ کی بات اور اس کے پیغامات پہنچادوں۔ اب جو بھی اللہ اور اس کے رسول کی بات نہ مانے گا اس کے لئے جہنم کی آگ ہے اور ایسے لوگ اس میں ہمیشہ رہیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(72,24,'72:24','(یہ لوگ اپنی اس روش سے باز نہ آئیں گے) یہاں تک کہ جب اس چیز کو دیکھ لیں گے جس کا ان سے وعدہ کیا جارہا ہے تو انہیں معلوم ہوجائے گا کہ کس کے مددگار کمزور ہیں اور کس کا جتھا تعداد میں کم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(72,25,'72:25','کہو ، ” میں نہیں جانتا کہ جس چیز کا وعدہ تم سے کیا جارہا ہے وہ قریب ہے یا میرا رب اس کے لئے کوئی لمبی مدت مقرر فرماتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(72,26,'72:26','وہ عالم الغیب ہے ، اپنے غیب پر کسی کو مطلع نہیں کرتا ،');
INSERT INTO "sayyid_qutb" VALUES(72,27,'72:27','سوائے اس رسول کے جسے اس نے (غیب کا علم دینے کے لئے) پسند کرلیا ہو ، تو اس کے ٓگے اور پیچھے وہ محافظ لگا دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(72,28,'72:28','تاکہ وہ جان لے کہ انہوں نے اپنے رب کے پیغامات پہنچادیئے ، اور وہ ان کے پورے ماحول کا احاطہ کیے ہوئے ہے اور ایک ایک چیز کو اس نے گن رکھا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(73,1,'73:1','اے اوڑھ لپیٹ کر سونے والے ،');
INSERT INTO "sayyid_qutb" VALUES(73,2,'73:2','رات کو نماز میں کھڑے رہا کرو مگر کم ،');
INSERT INTO "sayyid_qutb" VALUES(73,3,'73:3','آدھی رات یا اس سے کچھ کم کرلو ،');
INSERT INTO "sayyid_qutb" VALUES(73,4,'73:4','یا اس سے کچھ زیادہ بڑھا دو ، اور قرآن کو خوب ٹھہر ٹھہر کر پڑھو۔');
INSERT INTO "sayyid_qutb" VALUES(73,5,'73:5','ہم تم پر ایک بھاری کلام نازل کرنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(73,6,'73:6','درحقیقت رات کا اٹھنا نفس پر قابو پانے کے لئے بہت کارگر اور قرآن ٹھیک پڑھنے کے لئے زیادہ موزوں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(73,7,'73:7','دن کے اوقات میں تو تمہارے لئے بہت مصروفیات ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(73,8,'73:8','اپنے رب کے نام کا ذکر کیا کرو اور سب سے کٹ کر اسی کے ہورہو۔');
INSERT INTO "sayyid_qutb" VALUES(73,9,'73:9','وہ مشرق ومغرب کا مالک ہے ، اس کے سوا کوئی خدا نہیں ہے ، لہٰذا اسی کو اپنا وکیل بنالو “۔');
INSERT INTO "sayyid_qutb" VALUES(73,10,'73:10','اور جو باتیں لوگ بنا رہے ہیں ان پر صبر کرو اور شرافت کے ساتھ ان سے الگ ہوجاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(73,11,'73:11','ان جھٹلانے والے خوشحال لوگوں سے نمٹنے کا کام تم مجھ پر چھوڑدو اور انہیں ذرا کچھ دیر اسی حالت پر رہنے دو ۔');
INSERT INTO "sayyid_qutb" VALUES(73,12,'73:12','ہمارے پاس (ان کے لئے) بھاری بیڑیاں ہیں اور بھرکٹی ہوئی آگ');
INSERT INTO "sayyid_qutb" VALUES(73,13,'73:13','اور حلق میں پھنسنے والا کھانا اور درد ناک عذاب “۔');
INSERT INTO "sayyid_qutb" VALUES(73,14,'73:14','یہ اس دن ہوگا جب زمین اور پہاڑ لرز اٹھیں گے اور پہاڑوں کا حال ایسا ہوجائے گا جیسے ریت کے ڈھیر ہیں جو بکھرے جارہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(73,15,'73:15','تم لوگوں کے پاس ہم نے اسی طرح ایک رسول تم پر گواہ بنا کر بھیجا ہے جس طرح ہم نے فرعون کی طرف ایک رسول بھیجا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(73,16,'73:16','(پھر دیکھ لو جب) فرعون نے اس رسول کی بات نہ مانی تو ہم نے اس کو بڑی سختی کے ساتھ پکڑ لیا۔');
INSERT INTO "sayyid_qutb" VALUES(73,17,'73:17','اگر تم ماننے سے انکار کرو گے تو اس دن کیسے بچ جاؤ گے جو بچوں کو بوڑھا کردے گا');
INSERT INTO "sayyid_qutb" VALUES(73,18,'73:18','اور جس کی سختی سے آسمان پھٹا جارہا ہوگا ؟ اللہ کا وعدہ تو پورا ہوکر ہی رہتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(73,19,'73:19','یہ ایک نصیحت ہے ، اب جس کا جی چاہے ، اپنے رب کی طرف جانے کا راستہ اختیار کرلے');
INSERT INTO "sayyid_qutb" VALUES(73,20,'73:20','اے نبی ! تمہارا رب جانتا ہے کہ تم کبھی دوتہائی رات کے قریب اور کبھی آدھی رات اور کبھی ایک تہائی رات عبادل میں کھڑے رہتے ہو ، اور تمہارے ساتھیوں میں سے بھی ایک گروہ یہ عمل کرتا ہے۔ اللہ ہی رات اور دن کے اوقات کا حساب رکھتا ہے ، اسے معلوم ہے کہ تم لوگ اوقات کا ٹھیک شمار نہیں کرسکتے ، لہٰذا اس نے تم پر مہربانی فرمائی ، اب جتنا قرآن آسانی سے پڑھ سکتے ہو ، پڑھ لیا کرو۔ اسے معلوم ہے کہ تم میں کچھ مریض ہوں گے ، کچھ دوسرے لوگ اللہ کے فضل کی تلاش میں سفر کرتے ہیں ، اور کچھ اور لوگ اللہ کی راہ میں جنگ کرتے ہیں۔ پس جتنا قرآن بآسانی پڑھا جاسکے ، پڑھ لیا کرو ، نماز قائم کرو ، زکوٰة دو اور اللہ کو اچھا قرض دیتے رہو۔ جو کچھ بھلائی تم اپنے لئے آگے بھیجو گے اسے اللہ کے ہاں موجود پاؤ گے ، وہی زیادہ بہتر ہے اور اس کا اجر بہت بڑا ہے۔ اللہ سے مغفرت مانگتے رہو ، بیشک اللہ بڑا غفور ورحیم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(74,1,'74:1','” اے اوڑھ لپیٹ کرلیٹنے والے ،');
INSERT INTO "sayyid_qutb" VALUES(74,2,'74:2','اٹھو اور خبردار کرو۔');
INSERT INTO "sayyid_qutb" VALUES(74,3,'74:3','اور اپنے رب کی بڑائی کا اعلان کرو ،');
INSERT INTO "sayyid_qutb" VALUES(74,4,'74:4','اور اپنے کپڑے پاک رکھو ،');
INSERT INTO "sayyid_qutb" VALUES(74,5,'74:5','اور گندگی سے دور رہو۔');
INSERT INTO "sayyid_qutb" VALUES(74,6,'74:6','اور احسان نہ کرو زیادہ حاصل کرنے کے لئے');
INSERT INTO "sayyid_qutb" VALUES(74,7,'74:7','اور اپنے رب کی خاطر صبر کرو۔');
INSERT INTO "sayyid_qutb" VALUES(74,8,'74:8','اچھا ، جب صور میں پھونک ماری جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(74,9,'74:9','وہ دن بڑا ہی سخت دن ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(74,10,'74:10','کافروں کے لئے ہلکانہ ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(74,11,'74:11','چھوڑ دو مجھے اور شخص کو جسے میں نے اکیلا پیدا کیا ،');
INSERT INTO "sayyid_qutb" VALUES(74,12,'74:12','بہت سا مال اس کو دیا ،');
INSERT INTO "sayyid_qutb" VALUES(74,13,'74:13','اس کے ساتھ حاضر رہنے والے بیٹے دیئے ،');
INSERT INTO "sayyid_qutb" VALUES(74,14,'74:14','اور اس کے لئے ریاست کی راہ ہموار کی ،');
INSERT INTO "sayyid_qutb" VALUES(74,15,'74:15','پھر وہ طمع رکھتا ہے کہ میں اسے اور زیادہ دوں۔');
INSERT INTO "sayyid_qutb" VALUES(74,16,'74:16','ہرگز نہیں ، وہ ہماری آیات سے عناد رکھتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(74,17,'74:17','میں تو اسے عنقریب ایک کٹھن چڑھائی چڑھواؤں گا۔');
INSERT INTO "sayyid_qutb" VALUES(74,18,'74:18','اس نے سوچا اور کچھ بات بنانے کی کوشش کی');
INSERT INTO "sayyid_qutb" VALUES(74,19,'74:19','تو خدا کی مار اس پر ، کیسی بات بنانے کی کوشش کی۔');
INSERT INTO "sayyid_qutb" VALUES(74,20,'74:20','ہاں ، خدا کی مار اس پر ، کیسی بات بنانے کی کوشش کی۔');
INSERT INTO "sayyid_qutb" VALUES(74,21,'74:21','پھر (لوگوں کی طرف) دیکھا۔');
INSERT INTO "sayyid_qutb" VALUES(74,22,'74:22','پھر پیشانی سیکڑی اور منہ بنایا۔');
INSERT INTO "sayyid_qutb" VALUES(74,23,'74:23','پھر پلٹا اور تکبر میں پڑگیا۔');
INSERT INTO "sayyid_qutb" VALUES(74,24,'74:24','آخر کار بولا کہ یہ کچھ نہیں ہے مگر ایک جادو جو پہلے سے چلاآرہا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(74,25,'74:25','یہ تو ایک انسانی کلام ہے۔');
INSERT INTO "sayyid_qutb" VALUES(74,26,'74:26','عنقریب میں اسے دوزخ میں جھونک دوں گا');
INSERT INTO "sayyid_qutb" VALUES(74,27,'74:27','اور تم کیا جانو کہ کیا ہے وہ دوزخ ؟');
INSERT INTO "sayyid_qutb" VALUES(74,28,'74:28','نہ باقی رکھے نہ چھوڑے۔');
INSERT INTO "sayyid_qutb" VALUES(74,29,'74:29','کھال جھلس دینے والی۔');
INSERT INTO "sayyid_qutb" VALUES(74,30,'74:30','انیس کارکن اس پر مقرر ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(74,31,'74:31','ہم نے دوزخ کے یہ کارکن فرشتے بنائے ہیں ، اور ان کی تعداد کو کافروں کے لئے فتنہ بنادیا ہے ، تاکہ اہل کتاب کو یقین آجائے اور ایمان لانے والوں کا ایمان بڑھے ، اور اہل کتاب اور مومنین کسی شک میں نہ رہیں ، اور دل کے بیمار اور کفار یہ کہیں کہ بھلا اللہ کا اس عجیب بات سے کیا مطلب ہوسکتا ہے۔ اس طرح اللہ جسے چاہتا ہے گمراہ کردیتا ہے اور جسے چاہتا ہے ، ہدایت بخش دیتا ہے اور تیرے رب کے لشکروں کو خود اس کے سوا کوئی نہیں جانتا.... اور اس دوزخ کا ذکر اس کے سوا کسی غرض کے لئے نہیں کیا گیا ہے کہ لوگوں کو اس سے نصیحت ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(74,32,'74:32','ہرگز نہیں ، قسم ہے چاند کی ،');
INSERT INTO "sayyid_qutb" VALUES(74,33,'74:33','اور ات کی جبکہ وہ پلٹتی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(74,34,'74:34','اور صبح کی جبکہ وہ روشن ہوتی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(74,35,'74:35','یہ دوزخ بھی بڑی چیزوں میں سے ایک ہے ،');
INSERT INTO "sayyid_qutb" VALUES(74,36,'74:36','انسانوں کے لئے ڈراوا “۔');
INSERT INTO "sayyid_qutb" VALUES(74,37,'74:37','تم میں سے ہر اس شخص کے لئے ڈراوا جو آگے بڑھنا چاہے یا پیچھے رہ جانا چاہے۔');
INSERT INTO "sayyid_qutb" VALUES(74,38,'74:38','ہر شخص اپنے کسب کے بدلے رہن ہے۔');
INSERT INTO "sayyid_qutb" VALUES(74,39,'74:39','دائیں بازو والوں کے سوا ،');
INSERT INTO "sayyid_qutb" VALUES(74,40,'74:40','جو جنتوں میں ہوں گی ، وہ پوچھیں گے ”');
INSERT INTO "sayyid_qutb" VALUES(74,41,'74:41','مجرموں سے');
INSERT INTO "sayyid_qutb" VALUES(74,42,'74:42','تمہیں کیا چیز دوزخ میں لے گئی ؟ “');
INSERT INTO "sayyid_qutb" VALUES(74,43,'74:43','وہ کہیں گے ” ہم نماز پڑھنے والوں میں سے نہ تھے ،');
INSERT INTO "sayyid_qutb" VALUES(74,44,'74:44','اور مسکین کو کھانا نہیں کھلاتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(74,45,'74:45','اور حق کے خلاف باتیں بنانے والوں کے ساتھ مل کر ہم بھی باتیں بنانے لگتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(74,46,'74:46','اور روز جزاء کو جھوٹ قرار دیتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(74,47,'74:47','یہاں تک کہ ہمیں اس یقینی چیز سے سابقہ پیش آگیا “۔');
INSERT INTO "sayyid_qutb" VALUES(74,48,'74:48','اس وقت سفارش کرنے والوں کی کوئی سفارش ان کے کسی کام نہ آئے گی');
INSERT INTO "sayyid_qutb" VALUES(74,49,'74:49','آخر ان لوگوں کو کیا ہوگیا ہے کہ یہ اس نصیحت سے منہ موڑ رہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(74,50,'74:50','گویا یہ جنگلی گدھے ہیں');
INSERT INTO "sayyid_qutb" VALUES(74,51,'74:51','جو شیر سے ڈر کر بھاگ پڑے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(74,52,'74:52','بلکہ ان میں سے تو ہر ایک یہ چاہتا ہے کہ اس کے نام کھلے خط بھیجے جائیں “۔');
INSERT INTO "sayyid_qutb" VALUES(74,53,'74:53','ہرگز نہیں ، اصل بات یہ ہے کہ یہ آخرت کا خوف نہیں رکھتے۔');
INSERT INTO "sayyid_qutb" VALUES(74,54,'74:54','ہرگز نہیں ، یہ تو ایک نصیحت ہے ،');
INSERT INTO "sayyid_qutb" VALUES(74,55,'74:55','اب جس کا جی چاہے اس سے سبق حاصل کرلے “۔');
INSERT INTO "sayyid_qutb" VALUES(74,56,'74:56','اور یہ کوئی سبق حاصل نہ کریں گے الایہ کہ اللہ ہی ایسا چاہے۔ وہ اس کا حقدار ہے کہ اس سے تقویٰ کیا جائے اور وہ اس کا اہل ہے کہ (تقویٰ کرنے والوں کو) بخش دے “۔');
INSERT INTO "sayyid_qutb" VALUES(75,1,'75:1','میں قسم نہیں کھاتا ہوں قیامت کے دن کی ،');
INSERT INTO "sayyid_qutb" VALUES(75,2,'75:2','اور میں قسم نہیں کھاتا ہوں ملامت کرنے والے نفس کی ،');
INSERT INTO "sayyid_qutb" VALUES(75,3,'75:3','کیا انسان یہ سمجھ رہا ہے کہ ہم اس کی ہڈیوں کو جمع نہ کرسکیں گے ؟');
INSERT INTO "sayyid_qutb" VALUES(75,4,'75:4','کیوں نہیں ؟ ہم تو اس کی انگلیوں کی پور پور تک ٹھیک بنادینے پر قادر ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(75,5,'75:5','مگر انسان چاہتا ہے کہ آگے بھی بداعمالیاں کرتا رہے۔');
INSERT INTO "sayyid_qutb" VALUES(75,6,'75:6','پوچھتا ہے : ” آخر کب آنا ہے وہ قیامت کا دن ؟ “');
INSERT INTO "sayyid_qutb" VALUES(75,7,'75:7','پھر جب دیدے پتھرا جائیں گے');
INSERT INTO "sayyid_qutb" VALUES(75,8,'75:8','اور چاند بےنور ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(75,9,'75:9','اور چاند سورج ملا کر ایک کردیئے جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(75,10,'75:10','اس وقت یہی انسان کہے گا ” کہاں بھاگ کر جاؤں ؟ “');
INSERT INTO "sayyid_qutb" VALUES(75,11,'75:11','ہرگز نہیں ، وہاں کوئی جائے پناہ نہ ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(75,12,'75:12','اس روز تیرے رب ہی کے سامنے جاکر ٹھہرنا ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(75,13,'75:13','اس روز انسان کو اس کا سب اگلا پچھلا کیا کرایا بتادیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(75,14,'75:14','بلکہ انسان خود ہی اپنے آپ کو خوب جانتا ہے');
INSERT INTO "sayyid_qutb" VALUES(75,15,'75:15','چاہے وہ کتنی ہی معذرتیں پیش کرے “۔');
INSERT INTO "sayyid_qutb" VALUES(75,16,'75:16','اے نبی ! اس وحی کو جلدی جلدی یاد کرنے کے لئے اپنی زبان کو حرکت نہ دو ،');
INSERT INTO "sayyid_qutb" VALUES(75,17,'75:17','اس کو یاد کرادینا اور پڑھوادینا ہمارے ذمہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(75,18,'75:18','لہٰذا جب ہم اسے پڑھ رہے ہوں ، اس وقت تم اس کی قرات کو غور سے سنتے رہو ،');
INSERT INTO "sayyid_qutb" VALUES(75,19,'75:19','پھر اس کا مطلب سمجھا دینا بھی ہمارے ہی ذمہ ہے');
INSERT INTO "sayyid_qutb" VALUES(75,20,'75:20','” ہرگز نہیں ، اصل بات یہ ہے کہ تم لوگ جلد حاصل ہونے والی چیز (یعنی دنیا) سے محبت رکھتے ہو');
INSERT INTO "sayyid_qutb" VALUES(75,21,'75:21','اور آخرت کو چھوڑ دیتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(75,22,'75:22','اس روز کچھ چہرے تروتازہ ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(75,23,'75:23','اپنے رب کی طرف دیکھ رہے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(75,24,'75:24','اور کچھ چہرے اداس ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(75,25,'75:25','اور سمجھ رہے ہوں گے کہ ان کے ساتھ کمر توڑ برتاؤ ہونے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(75,26,'75:26','” ہرگز نہیں ، جان حلق تک پہنچ جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(75,27,'75:27','اور کہا جائے گا کہ ہے کوئی جھاڑ پھونک کرنے والا ،');
INSERT INTO "sayyid_qutb" VALUES(75,28,'75:28','اور آدمی سمجھے لے گا کہ یہ دنیا سے جدائی کا وقت ہے');
INSERT INTO "sayyid_qutb" VALUES(75,29,'75:29','اور پنڈلی سے پنڈلی جڑ جائے گی۔');
INSERT INTO "sayyid_qutb" VALUES(75,30,'75:30','وہ دن ہوگا تیرے رب کی طرف روانگی کا “۔');
INSERT INTO "sayyid_qutb" VALUES(75,31,'75:31','مگر اس نے نہ سچ مانا اور نہ نماز پڑھی');
INSERT INTO "sayyid_qutb" VALUES(75,32,'75:32','بلکہ جھٹلایا اور پلٹ گیا ،');
INSERT INTO "sayyid_qutb" VALUES(75,33,'75:33','پھر اکڑتا ہوا اپنے گھر والوں کی طرف چل دیا۔ “');
INSERT INTO "sayyid_qutb" VALUES(75,34,'75:34','” یہ روش تیرے ہی لئے سزاوار ہے اور تجھی کو زیب دیتی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(75,35,'75:35','ہاں یہ روش تیرے ہی لئے سزاوار ہے اور تجھی کو زیب دیتی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(75,36,'75:36','” کیا انسان نے یہ سمجھ رکھا ہے کہ وہ یونہی مہمل چھو ڑدیا جائے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(75,37,'75:37','کیا وہ ایک حقیر پانی کا نطفہ نہ تھا جو (رحم مادر میں) ٹپکایا جاتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(75,38,'75:38','پھر وہ ایک لو تھڑا بنا ، پھر اللہ نے اس کا جسم بنایا اور اس کے اعضا درست کیے ،');
INSERT INTO "sayyid_qutb" VALUES(75,39,'75:39','پھر اس سے مرد اور عورت کی دو قسمیں بنائیں۔');
INSERT INTO "sayyid_qutb" VALUES(75,40,'75:40','کیا وہ اس پر قادر نہیں ہے کہ مرنے والوں کو پھر سے زندہ کردے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,1,'76:1','” کیا انسان پر لامتناہی زمانے کا ایک وقت ایسا بھی گزرا ہے جبکہ وہ کوئی قابل ذکر چیز نہ تھا۔');
INSERT INTO "sayyid_qutb" VALUES(76,2,'76:2','ہم نے انسان کو ایک مخلوط نطفے سے پیدا کیا تاکہ اس کا امتحان لیں اور اس غرض کے لئے ہم نے اسے سننے اور دیکھنے والا بنایا۔');
INSERT INTO "sayyid_qutb" VALUES(76,3,'76:3','ہم نے اسے راستہ دکھادیا ، خواہ شکر کرنے والا بنے یا کفر کرنے والا “۔');
INSERT INTO "sayyid_qutb" VALUES(76,4,'76:4','” کفر کرنے والوں کے لئے ہم نے زنجیریں اور طوق اور بھڑکتی ہوئی آگ مہیا کررکھی ہے');
INSERT INTO "sayyid_qutb" VALUES(76,5,'76:5','نیک لوگ (جنت میں) شراب کے ایسے ساغر پئیں گے جن میں آب کافور کی آمیزش ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(76,6,'76:6','یہ ایک بہتا چشمہ ہوگا جس کے پانی کے ساتھ اللہ کے بندے شراب پئیں گے اور جہاں چاہیں گے بسہولت اس کی شاخیں نکال لیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,7,'76:7','” یہ وہ لوگ ہوں گے جو (دنیا میں) نذر پوری کرتے ہیں ، اور اس دن سے ڈرتے ہیں جس کی آفت ہر طرف پھیلی ہوئی ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(76,8,'76:8','اور اللہ کی محبت میں مسکین اور یتیم اور قیدکو کھانا کھلاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(76,9,'76:9','(اور ان سے کہتے ہیں کہ) ” ہم تمہیں صرف اللہ کی خاطر کھلا رہے ہیں ، ہم تم سے نہ کوئی بدلہ چاہتے ہیں نہ شکریہ ،');
INSERT INTO "sayyid_qutb" VALUES(76,10,'76:10','ہمیں تو اپنے رب سے اس دن کے عذاب کا خوف لاحق ہے جو سخت مصیبت کا انتہائی طویل دن ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(76,11,'76:11','” پس اللہ تعالیٰ انہیں اس دن کے شر سے بچا لے گا اور انہیں تازگی اور سرور بخشے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(76,12,'76:12','اور ان کے صبر کے بدلے میں انہیں جنت اور ریشمی لباس عطا کرے گا “۔ اس جنت میں ان کی مستقل رہائش ہوگی اور ریشمی لباس ہوگا جسے وہ پہنیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(76,13,'76:13','” وہاں وہ اونچی مسندوں پر تکیے لگائے بیٹھے ہوں گے۔ نہ انہیں دھوپ کی گرمی ستائے گی ، نہ جاڑے کی ٹھر “۔');
INSERT INTO "sayyid_qutb" VALUES(76,14,'76:14','” جنت کی چھاؤں ان پر جھکی ہوئی سایہ کررہی ہوگی ، اور اس کے پھل ہر وقت ان کے بس میں ہوں گے (کہ جس طرح چاہیں انہیں توڑڈالیں) “۔');
INSERT INTO "sayyid_qutb" VALUES(76,15,'76:15','” ان کے آگے چاندی کے برتن اور شیشے کے پیالے گردش کرائے جارہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(76,16,'76:16','شیشے بھی وہ چاندی کی قسم کے ہوں گے ، اور ان کو (منتظمین جنت نے) ٹھیک اندازے کے مطابق بھرا ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(76,17,'76:17','ان کو وہاں ایسی شراب کے جام پلائے جائیں گے جس میں سونٹھ کی آمیزش ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(76,18,'76:18','یہ جنت کا ایک چشمہ ہوگا جسے سلسبیل کہا جاتا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,19,'76:19','” ان کی خدمت کے لئے ایسے لڑکے دوڑتے پھر رہے ہوں گے جو ہمیشہ لڑکے ہی رہیں گے۔ تم انہیں دیکھو تو سمجھو کہ موتی ہیں جو بکھیر دیئے گئے ہیں');
INSERT INTO "sayyid_qutb" VALUES(76,20,'76:20','” وہاں جدھر بھی تم نگاہ ڈالوگے نعمتیں ہی نعمتیں اور ایک بڑی سلطنت کا سروسامان تمہیں نظر آئے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(76,21,'76:21','” ان کی اوپر باریک ریشم کے سبز لباس اور اطلس ودیبا کے کپڑے ہوں گے ، ان کو چاندی کے کنگن پہنائے جائیں گے ، اور ان کا رب ان کو نہایت پاکیزہ شراب پلائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(76,22,'76:22','یہ ہے تمہاری جزا اور تمہاری کارگزاری قابل قدر ٹھہری ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,23,'76:23','اے نبی ! ہم نے ہی تم پر یہ قرآن تھوڑا تھوڑا کرکے نازل کیا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(76,24,'76:24','لہٰذا تم اپنے رب کے حکم پر صبر کرو ، اور ان میں سے کسی بدعمل یا منکر حق کی بات نہ مانو۔');
INSERT INTO "sayyid_qutb" VALUES(76,25,'76:25','اپنے رب کا نام صبح وشام یاد کرو ،');
INSERT INTO "sayyid_qutb" VALUES(76,26,'76:26','رات کو بھی اس کے حضور سجدہ ریز ہو اور رات کے طویل اوقات میں اس کی تسبیح کرتے رہو “۔');
INSERT INTO "sayyid_qutb" VALUES(76,27,'76:27','یہ لوگ تو جلدی حاصل کرنے والی چیز (دنیا) سے محبت رکھتے ہیں اور آگے جو بھاری دن آنے والا ہے اسے نظر انداز کردیتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(76,28,'76:28','” ہم نے ہی ان کو پیدا کیا ہے اور ان کے جوڑ بند مضبوط کیے ہیں ، اور ہم جب چاہیں ان کو بدل کر رکھ دیں “۔');
INSERT INTO "sayyid_qutb" VALUES(76,29,'76:29','” یہ ایک نصیحت ہے ، اب جس کا جی چاہے اپنے رب کی طرف جانے کا راستہ اختیار کرلے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,30,'76:30','اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک اللہ نہ چاہے ، یقینا اللہ بڑا علیم و حکیم ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(76,31,'76:31','اپنی رحمت میں جس کو چاہتا ہے داخل کرتا ہے ، اور ظالموں کے لئے اس نے درد ناک عذاب تیار کر رکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(77,1,'77:1','قسم ہے ان ہواؤں کی جو پے در پے بھیجی جاتی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(77,2,'77:2','پھر طوفانی رفتار سے چلتی ہیں');
INSERT INTO "sayyid_qutb" VALUES(77,3,'77:3','اور بادلوں کو اٹھا کر پھیلاتی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(77,4,'77:4','پھر ان کو پھاڑ کر جدا کرتی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(77,5,'77:5','پھر دلوں میں خدا کی یاد ڈالتی ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(77,6,'77:6','عذر کے طور پر یا ڈراوے کے طور پر ،');
INSERT INTO "sayyid_qutb" VALUES(77,7,'77:7','جس چیز کا تم سے وعدہ کیا جارہا ہے وہ ضرور واقع ہونے والی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,8,'77:8','پھر جب ستارے ماند پڑجائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(77,9,'77:9','اور آسمان پھاڑ دیا جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(77,10,'77:10','اور پہاڑ دھنک ڈالے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(77,11,'77:11','اور رسولوں کی حاضری کا وقت آپہنچے گا (اس روز وہ چیز واقع ہوجائے گی)');
INSERT INTO "sayyid_qutb" VALUES(77,12,'77:12','کس روز کے لئے یہ کام اٹھا رکھا گیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(77,13,'77:13','فیصلے کے روز کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(77,14,'77:14','اور تمہیں کیا خبر کہ وہ فیصلے کا دن کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(77,15,'77:15','تباہی ہے اس دن جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,16,'77:16','” کیا ہم نے اگلوں کو ہلاک نہیں کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(77,17,'77:17','پھر انہی کے پیچھے ہم بعد والوں کو چلتا کریں گے۔');
INSERT INTO "sayyid_qutb" VALUES(77,18,'77:18','مجرموں کے ساتھ ہم یہی کچھ کیا کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(77,19,'77:19','تباہی ہے ان دن جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,20,'77:20','کیا ہم نے ایک حقیر پانی سے تمہیں پیدا نہیں کیا');
INSERT INTO "sayyid_qutb" VALUES(77,21,'77:21','اسے ایک محفوظ جگہ ٹھیرائے رکھا ؟');
INSERT INTO "sayyid_qutb" VALUES(77,22,'77:22','( ایک مقررہ مدت تک)');
INSERT INTO "sayyid_qutb" VALUES(77,23,'77:23','تو دیکھو ، ہم اس پر قادر تھے ، پس ہم بہت اچھی قدرت رکھنے والے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(77,24,'77:24','تباہی ہے اس روز جھٹلانے والوں کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(77,25,'77:25','کیا ہم نے زمین کو سمیٹ کر رکھنے والی نہیں بنایا ،');
INSERT INTO "sayyid_qutb" VALUES(77,26,'77:26','زندوں کے لئے بھی اور مردوں کے لئے بھی');
INSERT INTO "sayyid_qutb" VALUES(77,27,'77:27','اور اس میں بلندو بالا پہاڑ جمائے اور تمہیں میٹھا پانی پلایا ؟');
INSERT INTO "sayyid_qutb" VALUES(77,28,'77:28','تباہی ہے اس دن جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,29,'77:29','” چلو اب اسی چیز کی طرف جسے تم جھٹلایا کرتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(77,30,'77:30','چلو اس سائے کی طرف جو تین شاخوں والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(77,31,'77:31','نہ ٹھنڈک پہنچانے والا اور نہ آگ کی لپٹ سے بچانے والا۔');
INSERT INTO "sayyid_qutb" VALUES(77,32,'77:32','وہ آگ محل جیسی بڑی چنگاریوں کی (جو اچھلتی ہوئی یوں محسوس ہوں گی)');
INSERT INTO "sayyid_qutb" VALUES(77,33,'77:33','گویا کہ وہ زرد اونٹ ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(77,34,'77:34','تباہی ہے اس دن جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,35,'77:35','یہ وہ دن ہے جس میں وہ نہ کچھ بولیں گے');
INSERT INTO "sayyid_qutb" VALUES(77,36,'77:36','اور نہ انہیں موقع دیا جائے گا کہ کوئی عذر پیش کریں۔');
INSERT INTO "sayyid_qutb" VALUES(77,37,'77:37','تباہی ہے اس دن جھٹلانے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(77,38,'77:38','” یہ فیصلے کا دن ہے۔ ہم نے تمہیں اور تم سے پہلے گزرے ہوئے لوگوں کو جمع کردیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(77,39,'77:39','اب اگر کوئی چال تم چل سکتے ہو تو میرے مقابلہ میں چل دیکھو۔');
INSERT INTO "sayyid_qutb" VALUES(77,40,'77:40','تباہی ہے اس دن جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,41,'77:41','متقی لوگ آج سایوں اور چشموں میں ہیں');
INSERT INTO "sayyid_qutb" VALUES(77,42,'77:42','اور جو پھل وہ چاہئیں (ان کے لئے حاضر ہیں)');
INSERT INTO "sayyid_qutb" VALUES(77,43,'77:43','کھاؤ اور پیو مزے سے اپنے ان اعمال کے صلے میں جو تم کرتے رہے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(77,44,'77:44','ہم نیک لوگوں کو ایسی ہی جزا دیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(77,45,'77:45','تباہی ہے اس روز جھٹلانے والوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(77,46,'77:46','کھالو اور مزے لے کرلو تھوڑے دن۔ حقیقت میں تم لوگ مجرم ہو۔');
INSERT INTO "sayyid_qutb" VALUES(77,47,'77:47','تباہی ہے اس دن جھٹلانے والوں کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(77,48,'77:48','جب ان سے کہا جاتا ہے کہ (اللہ کے آگے) جھکو تو نہیں جھکتے۔');
INSERT INTO "sayyid_qutb" VALUES(77,49,'77:49','تباہی ہے اس دن جھٹلانے والوں کے لئے۔');
INSERT INTO "sayyid_qutb" VALUES(77,50,'77:50','اب اس (قرآن) کے بعد اور کو کن سا کلام ایسا ہوسکتا ہے جس پر یہ ایمان لائیں “۔');
INSERT INTO "sayyid_qutb" VALUES(78,1,'78:1','” یہ لوگ کس چیز کے بارے میں پوچھ گچھ کررہے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(78,2,'78:2','کیا اس بڑی خبر کے سارے میں');
INSERT INTO "sayyid_qutb" VALUES(78,3,'78:3','جس کے متعلق یہ مختلف چہ میگوئیاں کرنے میں لگے ہوئے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(78,4,'78:4','ہرگز نہیں ! عنقریب انہیں معلوم ہوجائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(78,5,'78:5','ہاں ، ہرگز نہیں ، عنقریب انہیں معلوم ہوجائے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(78,6,'78:6','” کیا یہ واقعہ نہیں ہے کہ ہم نے زمین کو فرش بنایا ،');
INSERT INTO "sayyid_qutb" VALUES(78,7,'78:7','اور پہاڑوں کو میخوں کی طرح گاڑ دیا۔');
INSERT INTO "sayyid_qutb" VALUES(78,8,'78:8','اور تمہیں جوڑوں کی شکل میں پیدا کیا ،');
INSERT INTO "sayyid_qutb" VALUES(78,9,'78:9','اور تمہاری نیند کو باعث سکون بنایا ،');
INSERT INTO "sayyid_qutb" VALUES(78,10,'78:10','اور رات کو پردہ پوش');
INSERT INTO "sayyid_qutb" VALUES(78,11,'78:11','اور دن کو معاش کا وقت بنایا');
INSERT INTO "sayyid_qutb" VALUES(78,12,'78:12','اور تمہارے اوپر سات مضبوط آسمان قائم کیے');
INSERT INTO "sayyid_qutb" VALUES(78,13,'78:13','اور ایک نہایت روشن اور گرم چراغ پیدا کیا ،');
INSERT INTO "sayyid_qutb" VALUES(78,14,'78:14','اور بادلوں سے لگاتار بارش برسائی');
INSERT INTO "sayyid_qutb" VALUES(78,15,'78:15','تاکہ اس کے ذریعے سے غلہ سبزی');
INSERT INTO "sayyid_qutb" VALUES(78,16,'78:16','اور گھنے باغ اگائیں “۔');
INSERT INTO "sayyid_qutb" VALUES(78,17,'78:17','” بیشک فیصلے کا دن ایک مقرر وقت ہے');
INSERT INTO "sayyid_qutb" VALUES(78,18,'78:18','جس روز صور میں پھونک مار دی جائے گی ، تم فوج در فوج نکل آؤ گے۔');
INSERT INTO "sayyid_qutb" VALUES(78,19,'78:19','اور آسمان کھول دیا جائے گا ، حتیٰ کہ وہ دروازے ہی دروازے بن کر رہ جائے گا');
INSERT INTO "sayyid_qutb" VALUES(78,20,'78:20','اور پہاڑ چلائے جائیں گے یہاں تک کہ وہ سراب ہوجائیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(78,21,'78:21','” درحقیقت جہنم ایک گھات ہے ،');
INSERT INTO "sayyid_qutb" VALUES(78,22,'78:22','سرکشوں کا ٹھکانا ،');
INSERT INTO "sayyid_qutb" VALUES(78,23,'78:23','جس میں وہ مدتوں پڑے رہیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(78,24,'78:24','اس کے اندر کسی ٹھنڈک اور پینے کے قابل کسی چیز کا مزہ نہ چکھیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(78,25,'78:25','کچھ ملے گا تو بس گرم پانی اور زخموں کا دھوؤن ،');
INSERT INTO "sayyid_qutb" VALUES(78,26,'78:26','(ان کے کرتوتوں) کا بھرپور بدلہ۔');
INSERT INTO "sayyid_qutb" VALUES(78,27,'78:27','وہ کسی حساب کی توقع نہ رکھتے تھے');
INSERT INTO "sayyid_qutb" VALUES(78,28,'78:28','اور ہماری آیات کو انہوں نے بالکل جھٹلادیا تھا ،');
INSERT INTO "sayyid_qutb" VALUES(78,29,'78:29','اور حال یہ تھا کہ ہم ہر چیز گن گن کر لکھ رکھی تھی۔');
INSERT INTO "sayyid_qutb" VALUES(78,30,'78:30','اب چکھو مزہ ، ہم تمہارے لئے عذاب کے سوا کسی چیز میں ہرگز اضافہ نہ کریں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(78,31,'78:31','” یقینا متقیوں کے لئے کامرانی کا ایک مقام ہے ،');
INSERT INTO "sayyid_qutb" VALUES(78,32,'78:32','باغ اور انگور ،');
INSERT INTO "sayyid_qutb" VALUES(78,33,'78:33','اور نوخیز ہم سن لڑکیاں');
INSERT INTO "sayyid_qutb" VALUES(78,34,'78:34','اور چھلکتے ہوئے جام۔');
INSERT INTO "sayyid_qutb" VALUES(78,35,'78:35','وہاں کوئی لغو اور جھوٹی بات وہ نہ سنیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(78,36,'78:36','جزاء اور کافی انعام تمہارے رب کی طرف سے “۔');
INSERT INTO "sayyid_qutb" VALUES(78,37,'78:37','” اس نہایت مہربان خدا کی طرف سے جو زمین اور آسمانوں اور ان کے درمیان کی ہر چیز کا مالک ، جس کے سامنے کسی کو بولنے کا یارا نہیں۔');
INSERT INTO "sayyid_qutb" VALUES(78,38,'78:38','جس روز روح اور ملائکہ صف بستہ کھڑے ہوں گے ، کوئی نہ بولے گا سوائے اس کے جسے رحمن اجازت دے اور جو ٹھیک بات کہے “۔');
INSERT INTO "sayyid_qutb" VALUES(78,39,'78:39','” وہ دن برحق ہے ، اب جس کا جی چاہے اپنے رب کی طرف پلٹنے کا راستہ اختیار کرلے۔');
INSERT INTO "sayyid_qutb" VALUES(78,40,'78:40','ہم نے تم لوگوں کو اس عذاب سے ڈرادیا ہے جو قریب آلگا ہے۔ جس روز آدمی وہ سب کچھ دیکھ لے گا جو اس کے ہاتھوں نے آگے بھیجا ہے ، اور کافر پکار اٹھے گا کہ کاش وہ خاک ہوتا “۔');
INSERT INTO "sayyid_qutb" VALUES(79,1,'79:1','” قسم ہے ان کی جو ڈوب کر کھینچتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(79,2,'79:2','اور آہستگی سے نکال لے جاتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(79,3,'79:3','اور تیزی سے تیرتے پھرتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(79,4,'79:4','پھر سبقت کرتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(79,5,'79:5','پھر معاملات کا انتظام چلاتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(79,6,'79:6','” جس روز ہلا مارے گا ، زلزلے کا جھٹکا');
INSERT INTO "sayyid_qutb" VALUES(79,7,'79:7','اور اس کے پیچھے ایک اور جھٹکا پڑے گا ،');
INSERT INTO "sayyid_qutb" VALUES(79,8,'79:8','کچھ دل ہوں گے جو اس روز خوف سے کانپ رہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(79,9,'79:9','نگاہیں ان کی سہمی ہوئی ہوں گی۔');
INSERT INTO "sayyid_qutb" VALUES(79,10,'79:10','یہ لوگ کہیں گے ” کیا واقعی ہم پلٹا کر پھر واپس لائے گئے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(79,11,'79:11','جب کہ ہم کھوکھلی بوسیدہ ہڈیاں بن چکے تھے ؟');
INSERT INTO "sayyid_qutb" VALUES(79,12,'79:12','“ کہیں گے ” یہ واپسی تو پھر بڑے گھاٹے کی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(79,13,'79:13','حالانکہ یہ بس اتنا کام ہے کہ ایک زور کی ڈانٹ پڑے گی');
INSERT INTO "sayyid_qutb" VALUES(79,14,'79:14','اور یکایک یہ کھلے میدان میں موجود ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(79,15,'79:15','” کیا تمہیں موسیٰ کے قصے کی خبر پہنچی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(79,16,'79:16','جب اس کے رب نے اسے طویٰ کی مقدس وادی میں پکارا تھا کہ ”');
INSERT INTO "sayyid_qutb" VALUES(79,17,'79:17','فرعون کے پاس جا وہ سرکش ہوگیا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(79,18,'79:18','اور اس سے کہہ کیا تو اس کے لئے تیار ہے کہ پاکیزگی اختیار کرے');
INSERT INTO "sayyid_qutb" VALUES(79,19,'79:19','اور میں تیرے رب کی طرف تیری رہنمائی کروں تو (اس کا) خوف تیرے اندر پیدا ہو ؟ “');
INSERT INTO "sayyid_qutb" VALUES(79,20,'79:20','پھر موسیٰ نے (فرعون کے پاس جاکر) اس کو بڑی نشانی دکھائی ،');
INSERT INTO "sayyid_qutb" VALUES(79,21,'79:21','مگر اس نے جھٹلا دیا اور نہ مانا ؟');
INSERT INTO "sayyid_qutb" VALUES(79,22,'79:22','پھر چال بازیاں کرنے کے لئے پلٹا');
INSERT INTO "sayyid_qutb" VALUES(79,23,'79:23','اور لوگوں کو جمع کرکے اس نے پکار کر');
INSERT INTO "sayyid_qutb" VALUES(79,24,'79:24','کہا ” میں تمہارا سب سے بڑا رب ہوں “۔');
INSERT INTO "sayyid_qutb" VALUES(79,25,'79:25','آخر کار اللہ نے اسے آخرت اور دنیا کے عذاب میں پکڑ لیا۔');
INSERT INTO "sayyid_qutb" VALUES(79,26,'79:26','درحقیقت اس میں بڑی عبرت ہے ہر اس شخص کے لئے جو ڈرے “۔');
INSERT INTO "sayyid_qutb" VALUES(79,27,'79:27','” کیا تم لوگوں کی تخلیق زیادہ سخت کام ہے یا آسمان کی ؟');
INSERT INTO "sayyid_qutb" VALUES(79,28,'79:28','اللہ نے اس کو بنایا ، اس کی چھت خوب اونچی اٹھائی');
INSERT INTO "sayyid_qutb" VALUES(79,29,'79:29','پھر اس کا توازن قائم کیا ، اور اس کی رات ڈھانکی اور اس کا دن نکالا۔');
INSERT INTO "sayyid_qutb" VALUES(79,30,'79:30','اس کے بعد زمین کو اس نے بچھایا ،');
INSERT INTO "sayyid_qutb" VALUES(79,31,'79:31','اس کے اندر سے اس کا پانی اور چارہ نکالا');
INSERT INTO "sayyid_qutb" VALUES(79,32,'79:32','اور پہاڑ اس میں گاڑ دیئے۔');
INSERT INTO "sayyid_qutb" VALUES(79,33,'79:33','سامان زیست کے طور پر تمہارے لئے اور تمہارے مویشیوں کے لئے “۔');
INSERT INTO "sayyid_qutb" VALUES(79,34,'79:34','” پھر جب وہ ہنگامہ عظیم برپا ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(79,35,'79:35','جس روز انسان اپنا سب کیا دھرا یاد کرے گا ،');
INSERT INTO "sayyid_qutb" VALUES(79,36,'79:36','اور ہر دیکھنے والے کے سامنے دوزخ کھول کر رکھ دی جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(79,37,'79:37','تو جس نے سرکشی کی تھی');
INSERT INTO "sayyid_qutb" VALUES(79,38,'79:38','اور دنیا کی زندگی کو ترجیح دی تھی ،');
INSERT INTO "sayyid_qutb" VALUES(79,39,'79:39','دوزخ ہی اس کا ٹھکانا ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(79,40,'79:40','اور جس نے اپنے رب کے سامنے کھڑے ہونے کا خوف کیا تھا اور نفس کو بری خواہشات سے بازرکھا تھا ،');
INSERT INTO "sayyid_qutb" VALUES(79,41,'79:41','جنت اس کا ٹھکانہ ہوگی۔ “');
INSERT INTO "sayyid_qutb" VALUES(79,42,'79:42','” یہ لوگ تم سے پوچھتے ہیں کہ ” آخر وہ گھڑی کب آکر ٹھہرے گی ؟ “');
INSERT INTO "sayyid_qutb" VALUES(79,43,'79:43','تمہارا کیا کام کر کہ اس کا وقت بتاؤ۔');
INSERT INTO "sayyid_qutb" VALUES(79,44,'79:44','اس کا علم تو اللہ پر ختم ہے۔');
INSERT INTO "sayyid_qutb" VALUES(79,45,'79:45','تم صرف خبردار کرنے والے ہو۔ ہر اس شخص کو جو اس کا خوف کرے۔');
INSERT INTO "sayyid_qutb" VALUES(79,46,'79:46','جس روز یہ لوگ اسے دیکھ لیں گے تو انہیں یوں محسوس ہوگا کہ (دنیا میں یا حالت موت میں) یہ بس ایک دن کے پچھلے پہر یا اگلے پہر تک ٹھہرے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(80,1,'80:1','” ترش رو ہوا اور بےرخی برتی');
INSERT INTO "sayyid_qutb" VALUES(80,2,'80:2','اس بات پر کہ وہ اندھا اس کے پاس آگیا۔');
INSERT INTO "sayyid_qutb" VALUES(80,3,'80:3','تمہیں کیا خبر ، شاید وہ سدھر جائے');
INSERT INTO "sayyid_qutb" VALUES(80,4,'80:4','یا نصیحت پر دھیان دے اور نصیحت کرنا اس کے لئے نافع ہو ،');
INSERT INTO "sayyid_qutb" VALUES(80,5,'80:5','جو شخص بےپروائی برتتا ہے');
INSERT INTO "sayyid_qutb" VALUES(80,6,'80:6','اس کی طرف تو تم توجہ کرتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(80,7,'80:7','حالانکہ اگر وہ نہ سدھرے تو تم پر اس کی کیا ذمہ داری ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(80,8,'80:8','اور جو خود تمہارے پاس دوڑا آتا ہے');
INSERT INTO "sayyid_qutb" VALUES(80,9,'80:9','اور ڈر رہا ہوتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(80,10,'80:10','اس سے تم بےرخی برتتے ہو۔');
INSERT INTO "sayyid_qutb" VALUES(80,11,'80:11','ہرگز نہیں ، یہ تو ایک نصیحت ہے ،');
INSERT INTO "sayyid_qutb" VALUES(80,12,'80:12','جس کا جی چاہے اسے قبول کرے۔');
INSERT INTO "sayyid_qutb" VALUES(80,13,'80:13','یہ ایسے صحیفوں میں درج ہے جو مکرم ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(80,14,'80:14','بلند مرتبہ ہیں ، پاکیزہ ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(80,15,'80:15','معزز ہاتھوں میں رہتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(80,16,'80:16','اور نیک کاتبوں کے');
INSERT INTO "sayyid_qutb" VALUES(80,17,'80:17','” قتل ہو انسان ، کیسا ناشکرا ہے یہ ۔');
INSERT INTO "sayyid_qutb" VALUES(80,18,'80:18','کس چیز سے اللہ نے اسے پیدا کیا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(80,19,'80:19','نطفہ کی ایک بوند سے۔ اللہ نے اسے پیدا کیا ، پھر اس کی تقدیر مقرر کی ،');
INSERT INTO "sayyid_qutb" VALUES(80,20,'80:20','پھر اس کے لئے زندگی کی راہ آسان کی ،');
INSERT INTO "sayyid_qutb" VALUES(80,21,'80:21','پھر اسے موت دی اور قبر میں پہنچایا۔');
INSERT INTO "sayyid_qutb" VALUES(80,22,'80:22','پھر جب چاہے وہ اسے دوبارہ کھڑا کردے۔');
INSERT INTO "sayyid_qutb" VALUES(80,23,'80:23','ہرگز نہیں ، اس نے وہ فرض ادا نہیں کیا جس کا اللہ نے اسے حکم دیا تھا “۔');
INSERT INTO "sayyid_qutb" VALUES(80,24,'80:24','پھر ذرا انسان اپنی خوراک کو دیکھے۔');
INSERT INTO "sayyid_qutb" VALUES(80,25,'80:25','ہم نے خوب پانی لنڈھایا ،');
INSERT INTO "sayyid_qutb" VALUES(80,26,'80:26','پھر زمین کو عجیب طرح پھاڑا ،');
INSERT INTO "sayyid_qutb" VALUES(80,27,'80:27','پھر اس کے اندر اگائے غلے');
INSERT INTO "sayyid_qutb" VALUES(80,28,'80:28','اور انگور اور ترکاریاں ۔');
INSERT INTO "sayyid_qutb" VALUES(80,29,'80:29','اور زیتون اور کھجوریں');
INSERT INTO "sayyid_qutb" VALUES(80,30,'80:30','اور گھنے باغ');
INSERT INTO "sayyid_qutb" VALUES(80,31,'80:31','اور طرح طرح کے پھل');
INSERT INTO "sayyid_qutb" VALUES(80,32,'80:32','اور چارے تمہارے لئے اور تمہارے مویشیوں کے لئے سامان زیست کے طور پر “۔');
INSERT INTO "sayyid_qutb" VALUES(80,33,'80:33','آخر کار جب وہ کان بہرے کردینے والی آواز بلندہوگی ....');
INSERT INTO "sayyid_qutb" VALUES(80,34,'80:34','اس روز آدمی اپنے بھائی سے بھاگے گا');
INSERT INTO "sayyid_qutb" VALUES(80,35,'80:35','اور اپنی ماں اور اپنے باپ');
INSERT INTO "sayyid_qutb" VALUES(80,36,'80:36','اور اپنی بیوی اور اپنی اولاد سے بھاگے گا۔');
INSERT INTO "sayyid_qutb" VALUES(80,37,'80:37','ان میں سے ہر شخص پر اس دن ایسا وقت آپڑے گا کہ اسے اپنے سوا کسی کا ہوش نہ ہوگا۔');
INSERT INTO "sayyid_qutb" VALUES(80,38,'80:38','کچھ چہرے اس روز دمک رہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(80,39,'80:39','ہشاش بشاش اور خوش وخرم ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(80,40,'80:40','اور کچھ چہروں پر اس روز خاک اڑارہی ہوگی');
INSERT INTO "sayyid_qutb" VALUES(80,41,'80:41','اور کلونس چھائی ہوئی ہوگی۔');
INSERT INTO "sayyid_qutb" VALUES(80,42,'80:42','یہی کافروفاجر لوگ ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(81,1,'81:1','جب سورج لپیٹ دیا جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(81,2,'81:2','اور جب تارے بکھر جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(81,3,'81:3','اور جب پہاڑ چلائے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(81,4,'81:4','اور جب دس مہینے کی حاملہ اونٹنیاں اپنے حال پر چھوڑ دی جائیں گی ،');
INSERT INTO "sayyid_qutb" VALUES(81,5,'81:5','اور جب جنگلی جانور سمیٹ کر اکٹھے کردیئے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(81,6,'81:6','اور جب سمندر بھرکا دیئے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(81,7,'81:7','اور جب جانیں (جسموں سے) جوڑ دی جائیں گی ،');
INSERT INTO "sayyid_qutb" VALUES(81,8,'81:8','اور جب زندہ گاڑی ہوئی لڑکی سے پوچھا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(81,9,'81:9','کہ وہ کسی قصور میں ماری گئی ؟');
INSERT INTO "sayyid_qutb" VALUES(81,10,'81:10','اور جب اعمال نامے کھولے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(81,11,'81:11','اور جب آسمان کا پردہ ہٹا دیا جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(81,12,'81:12','اور جب جہنم دہکائی جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(81,13,'81:13','اور جب جنت قریب لے آئی جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(81,14,'81:14','اس وقت ہر شخص کو معلوم ہوجائے گا کہ وہ کیا لے کر آیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(81,15,'81:15','پس نہیں ، میں قسم کھاتا ہوں پلٹنے والے');
INSERT INTO "sayyid_qutb" VALUES(81,16,'81:16','اور چھپ جانے والے تاروں کی ،');
INSERT INTO "sayyid_qutb" VALUES(81,17,'81:17','اور رات کی جبکہ وہ تاریک ہوئی');
INSERT INTO "sayyid_qutb" VALUES(81,18,'81:18','اور صبح کی جبکہ اس نے سانس لیا ،');
INSERT INTO "sayyid_qutb" VALUES(81,19,'81:19','یہ فی الواقع ایک بزرگ پیغمبر کا قول ہے ،');
INSERT INTO "sayyid_qutb" VALUES(81,20,'81:20','جو بڑی توانائی رکھتا ہے ، عرش والے کے ہاں بلند مرتبہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(81,21,'81:21','وہاں اس کا حکم مانا جاتا ہے ، وہ بااعتماد ہے ،');
INSERT INTO "sayyid_qutb" VALUES(81,22,'81:22','اور (اے اہل مکہ) تمہارا رفیق مجنون نہیں ہے ،');
INSERT INTO "sayyid_qutb" VALUES(81,23,'81:23','اس نے اس پیغمبر کو روشن افق پر دیکھا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(81,24,'81:24','اور وہ غیب (کے اس علم کو لوگوں تک پہنچانے) کے معاملہ میں بخیل نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(81,25,'81:25','اور یہ کسی شیطان مردود کا قول نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(81,26,'81:26','پھر تم لوگ کدھر چلے جارہے ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(81,27,'81:27','یہ تو سارے جہان والوں کے لئے ایک نصیحت ہے ،');
INSERT INTO "sayyid_qutb" VALUES(81,28,'81:28','تم میں سے ہر اس شخص کے لئے جو راہ راست پر چلنا چاہتا ہو');
INSERT INTO "sayyid_qutb" VALUES(81,29,'81:29','اور تمہارے چاہنے سے کچھ نہیں ہوتا جب تک اللہ رب العالمین نہ چاہے۔');
INSERT INTO "sayyid_qutb" VALUES(82,1,'82:1','جب آسمان پھٹ جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(82,2,'82:2','اور جب تارے بکھر جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(82,3,'82:3','اور جب سمندر پھاڑ دیئے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(82,4,'82:4','اور جب قبریں کھول دی جائیں گی ،');
INSERT INTO "sayyid_qutb" VALUES(82,5,'82:5','اس وقت ہر شخص کو اس کا اگلا پچھلا سب کیا دھرا معلوم ہوجائے گا');
INSERT INTO "sayyid_qutb" VALUES(82,6,'82:6','اے انسان ! کس چیز نے تجھے اپنے اس رب کریم کی طرف سے دھوکے میں ڈال دیا ،');
INSERT INTO "sayyid_qutb" VALUES(82,7,'82:7','جس نے تجھے پیدا کیا ، تجھے تک سک سے درست کیا ، تجھے متناسب بنایا ،');
INSERT INTO "sayyid_qutb" VALUES(82,8,'82:8','اور جس صورت میں چاہا ، تجھ کو جوڑ کر تیار کیا۔');
INSERT INTO "sayyid_qutb" VALUES(82,9,'82:9','ہرگز نہیں ! بلکہ (اصل بات یہ ہے کہ) تم لوگ جزا وسزا کو جھٹلاتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(82,10,'82:10','حالانکہ تم پر نگران مقرر ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(82,11,'82:11','ایسے معزز کاتب');
INSERT INTO "sayyid_qutb" VALUES(82,12,'82:12','جو تمہارے ہر فعل کو جانتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(82,13,'82:13','یقینا نیک لوگ مزے میں ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(82,14,'82:14','اور بیشک بدکار لوگ جہنم میں جائیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(82,15,'82:15','جزا کے دن وہ اس میں داخل ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(82,16,'82:16','اور اس سے ہرگز غائب نہ ہوسکیں گے');
INSERT INTO "sayyid_qutb" VALUES(82,17,'82:17','اور تم کیا جانتے ہو کہ وہ جزا کا دن کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(82,18,'82:18','ہاں ، تمہیں کیا خبر کہ وہ جزا کا دن کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(82,19,'82:19','یہ وہ دن ہے جب کسی شخص کے لئے کچھ کرنا کسی کے بس میں نہ ہوگا ، فیصلہ اس دن بالکل اللہ کے اختیار میں ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(83,1,'83:1','تباہی ہے ڈنڈی مارنے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(83,2,'83:2','جن کا حال یہ ہے کہ جب لوگوں سے لیتے ہیں تو اپنے زور سے پورا پورا لیتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(83,3,'83:3','اور جب ان کو ناپ کر یا تول کردیتے ہیں تو انہیں گھاٹا دیتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(83,4,'83:4','کیا یہ لوگ نہیں سمجھتے کہ یہ اٹھا کر لائے جانے والے ہیں ؟');
INSERT INTO "sayyid_qutb" VALUES(83,5,'83:5','ایک بڑے دن');
INSERT INTO "sayyid_qutb" VALUES(83,6,'83:6','اس دن جبکہ سب لوگ رب العالمین کے سامنے کھڑے ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(83,7,'83:7','ہرگز نہیں ، یقینا بدکاروں کا نامہ اعمال قید خانے کے دفتر میں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(83,8,'83:8','اور تمہیں کیا معلوم کہ کیا ہے وہ قید خانے کا دفتر ؟');
INSERT INTO "sayyid_qutb" VALUES(83,9,'83:9','وہ ایک کتاب ہے لکھی ہوئی۔');
INSERT INTO "sayyid_qutb" VALUES(83,10,'83:10','تباہی ہے اس روز جھٹلانے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(83,11,'83:11','جو روز جزا کو جھٹلاتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(83,12,'83:12','اور اسے نہیں جھٹلاتا مگر ہر وہ شخص جو حد سے گزر جانے والا بد عمل ہے۔');
INSERT INTO "sayyid_qutb" VALUES(83,13,'83:13','اسے جب ہماری آیات سنائی جاتی ہیں تو کہتا ہے ، ” یہ تو اگلے وقتوں کی کہانیاں ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(83,14,'83:14','ہرگز نہیں ، بلکہ دراصل ان لوگوں کے دلوں پر ان کے برے اعمال کا زنگ چڑھ گیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(83,15,'83:15','ہرگز نہیں ، بالیقین اس روز یہ اپنے رب کی دید سے محروم رکھے جائیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(83,16,'83:16','پھر یہ جہنم میں جاپڑیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(83,17,'83:17','پھر ان سے کہا جائے گا کہ یہ وہی چیز ہے جسے تم جھٹلایا کرتے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(83,18,'83:18','ہرگز نہیں ، بیشک نیک آدمیوں کا نامہ اعمال بلند پایہ لوگوں کے دفتر میں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(83,19,'83:19','اور تمہیں کیا خبر کہ کیا ہے وہ بلند پایہ لوگوں کا دفتر ؟');
INSERT INTO "sayyid_qutb" VALUES(83,20,'83:20','ایک لکھی ہوئی کتاب ،');
INSERT INTO "sayyid_qutb" VALUES(83,21,'83:21','جس کی نگہداشت مقرب فرشتے کرتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(83,22,'83:22','بیشک نیک لوگ بڑے مزے میں ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(83,23,'83:23','اونچی مسندوں پر بیٹھے نظارے کررہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(83,24,'83:24','ان کے چہروں پر تم خوشحالی کی رونق محسوس کرو گے۔');
INSERT INTO "sayyid_qutb" VALUES(83,25,'83:25','ان کو نفیس ترین سربند شراب پلائی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(83,26,'83:26','جس پر مشک کی مہر لگی ہوگی۔ جو لوگ دوسروں پر بازی لے جانا چاہتے ہوں وہ اس چیز کو حاصل کرنے میں بازی لے جانے کی کوشش کریں۔');
INSERT INTO "sayyid_qutb" VALUES(83,27,'83:27','اس شراب میں تسنیم کی آمیزش ہوگی ،');
INSERT INTO "sayyid_qutb" VALUES(83,28,'83:28','یہ ایک چشمہ ہے جس کے پانی کے ساتھ مقرب لوگ شراب پئیں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(83,29,'83:29','مجرم لوگ دنیا میں ایمان لانے والوں کا مذاق اڑاتے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(83,30,'83:30','جب ان کے پاس سے گزرتے تو آنکھیں مار مار کر ان کی طرف اشارے کرتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(83,31,'83:31','اپنے گھر والوں کی طرف پلٹتے تو مزے لیتے ہوئے پلٹتے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(83,32,'83:32','اور جب انہیں دیکھتے تھے کہ یہ بہکے ہوئے لوگ ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(83,33,'83:33','حالانکہ وہ ان پر نگران بنا کر نہیں بھیجے گئے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(83,34,'83:34','آج ایمان لانے والے کفار پر ہنس رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(83,35,'83:35','مسندوں پر بیٹھے ہوئے ان کا حال دیکھ رہے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(83,36,'83:36','مل گیا نا کافروں کو ان حرکتوں کا ثواب جو وہ کیا کرتے تھے “۔');
INSERT INTO "sayyid_qutb" VALUES(84,1,'84:1','جب آسمان پھٹ جائے گا');
INSERT INTO "sayyid_qutb" VALUES(84,2,'84:2','اور اپنے رب کے فرمان کی تعمیل کرے گا اور اس کے لئے حق یہی ہے (کہ اپنے رب کے حکم مانے) ۔');
INSERT INTO "sayyid_qutb" VALUES(84,3,'84:3','اور جب زمین پھیلا دی جائے گی');
INSERT INTO "sayyid_qutb" VALUES(84,4,'84:4','اور جو کچھ اس کے اندر ہے اسے باہر پھینک کر خالی ہوجائے گی');
INSERT INTO "sayyid_qutb" VALUES(84,5,'84:5','اور اپنے رب کے حکم کی تعمیل کرے گی اور اس کے لئے حق یہی ہے (کہ اس کی تعمیل کرئے) “۔');
INSERT INTO "sayyid_qutb" VALUES(84,6,'84:6','” اے انسان ! تو کشاں کشاں اپنے رب کی طرف چلا جارہا ہے اور اس سے ملنے والا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(84,7,'84:7','پھر جس کا نامہ اعمال اس کے سیدھے ہاتھ میں دیا گیا ،');
INSERT INTO "sayyid_qutb" VALUES(84,8,'84:8','اس سے ہلکا حساب لیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(84,9,'84:9','اور وہ اپنے لوگوں کی طرف خوش خوش پلٹے گا۔');
INSERT INTO "sayyid_qutb" VALUES(84,10,'84:10','رہا وہ شخص جس کا نامہ اعمال اس کی پیٹھ کے پیچھے دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(84,11,'84:11','تو وہ موت کو پکارے گا');
INSERT INTO "sayyid_qutb" VALUES(84,12,'84:12','اور بھڑکتی ہوئی آگ میں جاپڑے گا۔');
INSERT INTO "sayyid_qutb" VALUES(84,13,'84:13','وہ اپنے گھر والوں میں مگن تھا۔');
INSERT INTO "sayyid_qutb" VALUES(84,14,'84:14','اس نے سمجھا تھا کہ اسے کبھی پلٹنا نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(84,15,'84:15','پلٹنا کیسے نہ تھا ، اس کا رب اس کے کرتوت دیکھ رہا تھا “۔');
INSERT INTO "sayyid_qutb" VALUES(84,16,'84:16','پس نہیں ، میں قسم کھاتا ہوں شفق کی ،');
INSERT INTO "sayyid_qutb" VALUES(84,17,'84:17','اور رات کی اور جو کچھ وہ سمیٹ لیتی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(84,18,'84:18','اور چاند کی جب کہ وہ ماہ کامل ہوجاتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(84,19,'84:19','تم کو ضرور درجہ بدرجہ ایک مشکل حالت سے دوسری حالت کی طرف گزرتے چلے جانا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(84,20,'84:20','” پھر ان لوگوں کو کیا ہوگیا ہے کہ وہ ایمان نہیں لاتے');
INSERT INTO "sayyid_qutb" VALUES(84,21,'84:21','اور جب قرآن ان کے سامنے پڑھا جائے تو سجدہ نہیں کرتے۔');
INSERT INTO "sayyid_qutb" VALUES(84,22,'84:22','بلکہ یہ منکرین تو الٹا جھٹلاتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(84,23,'84:23','حالانکہ جو کچھ یہ (اپنے نامہ اعمال میں) جمع کررہے ہیں اللہ اسے خوب جانتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(84,24,'84:24','لہٰذا ان کو درد ناک عذاب کی بشارت دے دو “۔');
INSERT INTO "sayyid_qutb" VALUES(84,25,'84:25','البتہ جو لوگ ایمان لے آئے ہیں اور جنہوں نے نیک عمل کیے ہیں ان کے لئے کبھی ختم نہ ہونے والا اجر ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(85,1,'85:1','قسم ہے مضبوط قلعوں والے آسمان کی ،');
INSERT INTO "sayyid_qutb" VALUES(85,2,'85:2','اور اس دن کی جس کا وعدہ کیا گیا ہے یعنی قیامت) ،');
INSERT INTO "sayyid_qutb" VALUES(85,3,'85:3','اور دیکھنے والے اور دیکھی جانے والی چیز کی “۔');
INSERT INTO "sayyid_qutb" VALUES(85,4,'85:4','مارے گئے گڑھے والے۔');
INSERT INTO "sayyid_qutb" VALUES(85,5,'85:5','(اس گڑھے والے) جس میں خوب بھڑکتے ہوئے ایندھن کی آگ تھی۔');
INSERT INTO "sayyid_qutb" VALUES(85,6,'85:6','جبکہ وہ اس گڑھے کے کنارے پر بیٹھے ہوئے تھے');
INSERT INTO "sayyid_qutb" VALUES(85,7,'85:7','اور جو کچھ وہ ایمان لانے والوں کے ساتھ کررہے تھے اسے دیکھ رہے تھے۔');
INSERT INTO "sayyid_qutb" VALUES(85,8,'85:8','اور ان اہل ایمان سے ان کی دشمنی اس کے سوا کسی وجہ سے نہ تھی کہ وہ اس خدا پر ایمان لے آئے تھے جو زبردست اور اپنی ذات میں آپ محمود ہے ،');
INSERT INTO "sayyid_qutb" VALUES(85,9,'85:9','جو آسمانوں اور زمین کی سلطنت کا مالک ہے ، اور وہ خدا سب کچھ دیکھ رہا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(85,10,'85:10','جن لوگوں نے مومن مردوں اور عورتوں پر ستم توڑا اور پھر اس سے تائب نہ ہوئے یقینا ان کے لئے جہنم کا عذاب ہے اور ان کے لئے جلائے جانے کی سزا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(85,11,'85:11','جو لوگ ایمان لائے اور انہوں نے نیک عمل کیے ، یقینا ان کے لئے جنت کے باغ ہیں جن کے نیچے نہریں بہتی ہوں گی ، یہ ہے بڑی کامیابی “۔');
INSERT INTO "sayyid_qutb" VALUES(85,12,'85:12','درحقیقت تمہارے رب کی پکڑ بڑی سخت ہے');
INSERT INTO "sayyid_qutb" VALUES(85,13,'85:13','وہی پہلی بار پیدا کرتا ہے ، وہی دوبارہ پیدا کرے گا');
INSERT INTO "sayyid_qutb" VALUES(85,14,'85:14','وہ اللہ بخشنے والا اور محبت کرنے والا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(85,15,'85:15','عرش کا مالک ہے اور بزرگ و برتر ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(85,16,'85:16','اور جو کچھ چاہے کر ڈالنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(85,17,'85:17','کیا تمہیں لشکروں کی خبر پہنچی ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(85,18,'85:18','فرعون اور ثمود (کے لشکروں کی !');
INSERT INTO "sayyid_qutb" VALUES(85,19,'85:19','مگر جنہوں نے کفر کیا ہے وہ جھٹلانے میں لگے ہوئے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(85,20,'85:20','حالانکہ اللہ نے انہیں گھیرے میں لے رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(85,21,'85:21','(ان جھٹلانے سے اس قرآن کا کچھ نہیں بگڑتا) بلکہ یہ قرآن بلند پایہ ہے');
INSERT INTO "sayyid_qutb" VALUES(85,22,'85:22','اور لوح میں (نقش ہے) جو محفوظ ہے');
INSERT INTO "sayyid_qutb" VALUES(86,1,'86:1','اللہ کے نام سے جو بےانتہا مہربان اور رحم فرمانے والا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(86,2,'86:2','اور تم کیا جانو کہ وہ رات کو نمودار ہونے والا کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(86,3,'86:3','چمکتا ہوا تارا۔');
INSERT INTO "sayyid_qutb" VALUES(86,4,'86:4','کوئی جان ایسی نہیں جس کے اوپر کوئی نگہبان نہ ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(86,5,'86:5','پھر ذرا انسان ہی دیکھ لے کہ وہ کس چیز سے پیدا کیا گیا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(86,6,'86:6','ایک اچھلنے والے پانی سے پیدا کیا گیا ہے');
INSERT INTO "sayyid_qutb" VALUES(86,7,'86:7','جو پیٹھ اور سینے کی ہڈیوں کے درمیان سے نکلتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(86,8,'86:8','یقینا وہ (خالق ( اسے دوبارہ پیدا کرنے پر قادر ہے ۔');
INSERT INTO "sayyid_qutb" VALUES(86,9,'86:9','جس روز پوشیدہ اسرار کی جانچ پڑتال ہوگی');
INSERT INTO "sayyid_qutb" VALUES(86,10,'86:10','اس وقت انسان کے پاس نہ خود اپنا کوئی زور ہوگا اور نہ کوئی اس کی مدد کرنے والا ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(86,11,'86:11','قسم ہے بارش برسانے والے آسمان کی');
INSERT INTO "sayyid_qutb" VALUES(86,12,'86:12','اور (نباتات اگتے وقت) پھٹ جانے والی زمین کی ،');
INSERT INTO "sayyid_qutb" VALUES(86,13,'86:13','یہ ایک جچی تلی بات ہے ،');
INSERT INTO "sayyid_qutb" VALUES(86,14,'86:14','ہنسی مذاق نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(86,15,'86:15','” یہ لوگ (یعنی کفار مکہ) کچھ چالیں چل رہے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(86,16,'86:16','میں بھی ایک چال چل رہا ہوں ،');
INSERT INTO "sayyid_qutb" VALUES(86,17,'86:17','پس چھوڑ دو اے نبی ان کافروں کو ایک ذرا کی ذرا ان کے حال پر چھوڑ دو “۔');
INSERT INTO "sayyid_qutb" VALUES(87,1,'87:1','”(اے نبی ! ) اپنے رب برتر کے نام کی تسبیح کرو');
INSERT INTO "sayyid_qutb" VALUES(87,2,'87:2','جس نے پیدا کیا اور تناسب قائم کیا ،');
INSERT INTO "sayyid_qutb" VALUES(87,3,'87:3','جس نے تقدیر بنائی پھر راہ دکھائی ،');
INSERT INTO "sayyid_qutb" VALUES(87,4,'87:4','جس نے نباتات اگائیں');
INSERT INTO "sayyid_qutb" VALUES(87,5,'87:5','پھر ان کو سیاہ کوڑا کرکٹ بنادیا “۔');
INSERT INTO "sayyid_qutb" VALUES(87,6,'87:6','ہم تمہیں پڑھوا دیں گے ، پھر تم نہیں بھولوگے ،');
INSERT INTO "sayyid_qutb" VALUES(87,7,'87:7','سوائے اس کے جو اللہ چاہے ، وہ ظاہر کو بھی جانتا ہے اور جو کچھ پوشیدہ ہے اس کو بھی۔');
INSERT INTO "sayyid_qutb" VALUES(87,8,'87:8','اور ہم تمہیں آسان طریقے کی سہولت دیتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(87,9,'87:9','لہٰذا تم نصیحت کرو اگر نصیحت نافع ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(87,10,'87:10','جو شخص ڈرتا ہے وہ نصیحت قبول کرلے گا ،');
INSERT INTO "sayyid_qutb" VALUES(87,11,'87:11','اور اس سے گریز کرے گا وہ انتہائی بدبخت');
INSERT INTO "sayyid_qutb" VALUES(87,12,'87:12','جو بڑی آگ میں جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(87,13,'87:13','پھر نہ اس میں مرے گا نہ جئے گا۔');
INSERT INTO "sayyid_qutb" VALUES(87,14,'87:14','فلاح پا گیا وہ جس نے پاکیزگی اختیار کی');
INSERT INTO "sayyid_qutb" VALUES(87,15,'87:15','” اور اپنے رب کا نام یاد کیا ، پھر نماز پڑھی “۔');
INSERT INTO "sayyid_qutb" VALUES(87,16,'87:16','مگر تم لوگ دنیا کی زندگی کو ترجیح دیتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(87,17,'87:17','” حالانکہ آخرت سے بہتر ہے اور باقی رہنے والی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(87,18,'87:18','” یہی بات پہلے آئے ہوئے صحیفوں میں بھی کہی گئی تھی');
INSERT INTO "sayyid_qutb" VALUES(87,19,'87:19','، ابراہیم اور موسیٰ کے صحیفوں میں “۔');
INSERT INTO "sayyid_qutb" VALUES(88,1,'88:1','کیا تمہیں اس چھاجانے والی آفت (یعنی قیامت) کی خبر پہنچی ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(88,2,'88:2','کچھ چہرے اس روز خوفزدہ ہوگے ،');
INSERT INTO "sayyid_qutb" VALUES(88,3,'88:3','” سخت مشقت کر رے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,4,'88:4','تھکے جاتے ہوں گے ، شدید آگ میں جھلس رہے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,5,'88:5','کھولتے ہوئے چشمے کا پانی انہیں پینے کو دیا جائے گا ،');
INSERT INTO "sayyid_qutb" VALUES(88,6,'88:6','خاردار سوکھی گھاس کے سوا کوئی کھانا ان کے لئے نہ ہوگا');
INSERT INTO "sayyid_qutb" VALUES(88,7,'88:7','جو نہ موٹا کرے نہ بھوک مٹائے۔ “');
INSERT INTO "sayyid_qutb" VALUES(88,8,'88:8','” کچھ چہرے اس روز بارونق ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,9,'88:9','اپنی کا گزاری پر خوش ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,10,'88:10','عالی مقام جنت میں ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,11,'88:11','کوئی بیہودہ بات وہ وہاں نہ سنیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,12,'88:12','اس میں چشمے رواں ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,13,'88:13','اس کے اندر اونچی مسندیں ہوں گی ،');
INSERT INTO "sayyid_qutb" VALUES(88,14,'88:14','ساغررکھے ہوئے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(88,15,'88:15','گاؤ تکیوں کی قطاریں لگی ہوں گی');
INSERT INTO "sayyid_qutb" VALUES(88,16,'88:16','اور نفیس فرش بچھے ہوئے ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(88,17,'88:17','”(یہ لوگ نہیں مانتے) تو کیا یہ اونٹوں کو نہیں دیکھتے کہ کیسے بنائے گئے ؟');
INSERT INTO "sayyid_qutb" VALUES(88,18,'88:18','آسمان کو نہیں دیکھتے کہ کیسے اٹھایا گیا ؟');
INSERT INTO "sayyid_qutb" VALUES(88,19,'88:19','پہاڑوں کو نہیں دیکھتے کہ کیسے جمائے گئے ؟');
INSERT INTO "sayyid_qutb" VALUES(88,20,'88:20','اور زمین کو نہیں دیکھتے کہ کیسے بچھائی گئی ؟');
INSERT INTO "sayyid_qutb" VALUES(88,21,'88:21','” اچھا تو (اے نبی) نصیحت کیے جاؤ، تم بس نصیحت ہی کرنے والے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(88,22,'88:22','کچھ ان پر جبر کرنے والے نہیں ہو۔');
INSERT INTO "sayyid_qutb" VALUES(88,23,'88:23','البتہ جو شخص منہ موڑے گا ، اور انکار کرے گا');
INSERT INTO "sayyid_qutb" VALUES(88,24,'88:24','تو اللہ اس کو بھاری سزا دے گا۔');
INSERT INTO "sayyid_qutb" VALUES(88,25,'88:25','ان لوگوں کو پلٹنا ہماری طرف ہی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(88,26,'88:26','پھر ان کا حساب لینا ہمارے ہی ذمہ ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(89,1,'89:1','” قسم ہے فجر کی ،');
INSERT INTO "sayyid_qutb" VALUES(89,2,'89:2','اور دس راتوں کی ،');
INSERT INTO "sayyid_qutb" VALUES(89,3,'89:3','اور جفت اور طاق کی ،');
INSERT INTO "sayyid_qutb" VALUES(89,4,'89:4','اور رات کی جب رخصت ہورہی ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(89,5,'89:5','کیا اس میں صاحب عقل کے لئے کوئی قسم ہے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(89,6,'89:6','تم نے دیکھا نہیں کہ تمہارے رب نے کیا برتاؤ کیا۔');
INSERT INTO "sayyid_qutb" VALUES(89,7,'89:7','اونچے ستونوں والے عادارم کے ساتھ');
INSERT INTO "sayyid_qutb" VALUES(89,8,'89:8','جن کے مانند کوئی قوم دنیا کے ملکوں میں پیدا نہیں کی گئی تھی ؟');
INSERT INTO "sayyid_qutb" VALUES(89,9,'89:9','” اور ثمود کے ساتھ جنہوں نے وادی میں چٹانیں تراشی تھیں ؟');
INSERT INTO "sayyid_qutb" VALUES(89,10,'89:10','اور میخوں والے فرعون کے ساتھ ؟');
INSERT INTO "sayyid_qutb" VALUES(89,11,'89:11','یہ وہ لوگ تھے جنہوں نے دنیا کے ملکوں میں بڑی سرکشی کی تھی');
INSERT INTO "sayyid_qutb" VALUES(89,12,'89:12','اور ان میں فساد پھیلایا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(89,13,'89:13','آخر کار تمہارے رب نے ان پر عذاب کا کوڑا برسا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(89,14,'89:14','حقیقت یہ ہے کہ تمہارا رب گھات لگائے ہوئے ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(89,15,'89:15','مگر انسان کا حال یہ ہے کہ اس کا رب جب اس کو آزمائش میں ڈالتا ہے اور اسے عزت اور نعمت دیتا ہے تو وہ کہتا ہے کہ میرے رب نے مجھے عزت دار بنادیا۔');
INSERT INTO "sayyid_qutb" VALUES(89,16,'89:16','اور جب وہ اس کو آزمائش میں ڈالتا ہے اور اس کا رزق اس پر تنگ کردیتا ہے تو وہ کہتا ہے میرے رب نے مجھے ذلیل کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(89,17,'89:17','ہر گز نہیں بلکہ تم یتیم سے عزت کا سلوک نہیں کرتے ،');
INSERT INTO "sayyid_qutb" VALUES(89,18,'89:18','اور مسکین کو کھانا کھلانے پر ایک دوسرے کو نہیں اکساتے ،');
INSERT INTO "sayyid_qutb" VALUES(89,19,'89:19','اور میراث کا سرا مال سمیٹ کر کھاجاتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(89,20,'89:20','اور مال کی محبت میں بری طرح گرفتار ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(89,21,'89:21','ہرگز نہیں ، جب زمین پے درپے کوٹ کوٹ کر ریگ زار بنادی جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(89,22,'89:22','اور تمہارا رب جلوہ فرما ہوگا اس حال میں کہ فرشتے صف در صف کھڑے ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(89,23,'89:23','اور جہنم اس روز سامنے لے آئی جائے گی ، اس دن انسان کو سمجھ آئے گی اس وقت اس کے سمجھنے کا کیا حاصل ؟');
INSERT INTO "sayyid_qutb" VALUES(89,24,'89:24','وہ کہے گا کہ کاش میں نے اپنی اس زندگی کے لئے کچھ پیشگی سامان کیا ہوتا !');
INSERT INTO "sayyid_qutb" VALUES(89,25,'89:25','پھر اس دن اللہ جو عذاب دے گا ویسا عذاب دینے والا کوئی نہیں ،');
INSERT INTO "sayyid_qutb" VALUES(89,26,'89:26','اور اللہ جیسا باندھے گا ویسا باندھنے والا کوئی نہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(89,27,'89:27','”(دوسری طرف اشارہ ہوگا) اے نفس مطمئن');
INSERT INTO "sayyid_qutb" VALUES(89,28,'89:28','چل اپنے رب کی طرف اس حال میں کہ تو خوش اور پسندیدہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(89,29,'89:29','شامل ہوجا میرے بندوں میں');
INSERT INTO "sayyid_qutb" VALUES(89,30,'89:30','اور داخل ہوجا میری جنت میں “۔');
INSERT INTO "sayyid_qutb" VALUES(90,1,'90:1','نہیں ! میں قسم کھاتا ہوں اس شہر کی');
INSERT INTO "sayyid_qutb" VALUES(90,2,'90:2','” اور حال یہ ہے کہ تم اس شہر میں مقیم ہو ،');
INSERT INTO "sayyid_qutb" VALUES(90,3,'90:3','اور قسم کھاتا ہوں باپ کی اور اس اولاد کی جو اس سے پیدا ہوئی ،');
INSERT INTO "sayyid_qutb" VALUES(90,4,'90:4','درحقیقت ہم نے انسان کو مشقت میں پیدا کیا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(90,5,'90:5','” کیا اس نے یہ سمجھ رکھا ہے کہ اس پر کوئی قابونہ پاسکے گا ؟');
INSERT INTO "sayyid_qutb" VALUES(90,6,'90:6','کہتا ہے کہ میں نے ڈھیروں مال اڑا دیا۔');
INSERT INTO "sayyid_qutb" VALUES(90,7,'90:7','کیا وہ سمجھتا ہے کہ کسی نے اس کو نہیں دیکھا ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(90,8,'90:8','کی اہم نے اسے دو آنکھیں نہیں دئیے ؟');
INSERT INTO "sayyid_qutb" VALUES(90,9,'90:9','” اور ایک زبان اور دو ہونٹ');
INSERT INTO "sayyid_qutb" VALUES(90,10,'90:10','اور (نیکی اور بدی کے) دونوں نمایاں راستے اسے (نہیں) دکھا دیئے ؟ “');
INSERT INTO "sayyid_qutb" VALUES(90,11,'90:11','” مگر اس نے دشوار گزار گھاٹی سے گزرنے کی ہمت نہ کی');
INSERT INTO "sayyid_qutb" VALUES(90,12,'90:12','اور تم کیا جانو کہ کیا ہے وہ دشوار گزار گھاٹی ؟');
INSERT INTO "sayyid_qutb" VALUES(90,13,'90:13','کسی گردن کو غلامی سے چھڑانا ،');
INSERT INTO "sayyid_qutb" VALUES(90,14,'90:14','یا فاقے کے دن کو کھانا کھلانا۔');
INSERT INTO "sayyid_qutb" VALUES(90,15,'90:15','کسی قریبی یتیم');
INSERT INTO "sayyid_qutb" VALUES(90,16,'90:16','یا خاک نشین مسکین کو');
INSERT INTO "sayyid_qutb" VALUES(90,17,'90:17','پھر (اس کے ساتھ یہ کہ) آدمی ان لوگوں میں شامل ہو جو ایمان لائے اور جنہوں نے ایک دوسرے کو صبر اور (خلق خدا پر) رحم کی تلقین کی۔');
INSERT INTO "sayyid_qutb" VALUES(90,18,'90:18','یہ لوگ ہیں دائیں بازو والے “۔');
INSERT INTO "sayyid_qutb" VALUES(90,19,'90:19','اور جنہوں نے ہماری آیات کو ماننے سے انکار کیا وہ بائیں بازو والے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(90,20,'90:20','ان پر آگ چھائی ہوئی ہوگی “۔');
INSERT INTO "sayyid_qutb" VALUES(91,1,'91:1','” سورج اور اس کی دھوپ کی قسم ،');
INSERT INTO "sayyid_qutb" VALUES(91,2,'91:2','اور چاند کی قسم جب کہ وہ اس کے پیچھے آتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(91,3,'91:3','اور دن کی قسم جب کہ وہ (سورج کو) نمایاں کردیتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(91,4,'91:4','اور رات کی قسم جبکہ وہ (سورج کو) ڈھانک لیتی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(91,5,'91:5','اور آسمان کی اور اس ذات کی قسم جس نے اسے قائم کیا ،');
INSERT INTO "sayyid_qutb" VALUES(91,6,'91:6','اور زمین کی اور اس ذات کی قسم جس نے اسے بچھایا ،');
INSERT INTO "sayyid_qutb" VALUES(91,7,'91:7','اور نفس انسانی کی اور اس ذات کی قسم جس نے اسے ہموار کیا ،');
INSERT INTO "sayyid_qutb" VALUES(91,8,'91:8','پھر اس کی بدی اور اس کی پرہیز گاری اس پر الہام کردی ،');
INSERT INTO "sayyid_qutb" VALUES(91,9,'91:9','یقینا فلاح پا گیا وہ جس نے نفس کا تزکیہ کیا');
INSERT INTO "sayyid_qutb" VALUES(91,10,'91:10','اور نامراد ہوا وہ جس نے اس کو دبادیا “۔');
INSERT INTO "sayyid_qutb" VALUES(91,11,'91:11','ثمود نے اپنی سرکشی کی بنا پر جھٹلایا۔');
INSERT INTO "sayyid_qutb" VALUES(91,12,'91:12','جب اس قوم کا سب سے زیادہ شقی آدمی بپھر کر اٹھا');
INSERT INTO "sayyid_qutb" VALUES(91,13,'91:13','تو اللہ کے رسول نے ان لوگوں سے کہا کہ خبردار ، اللہ کی اونٹنی کو (ہاتھ نہ لگانا) اور اس کے پانی پینے (میں مانع نہ ہونا)');
INSERT INTO "sayyid_qutb" VALUES(91,14,'91:14','مگر انہوں نے اس کی بات کو جھوٹا قرار دیا اور اونٹنی کو مار ڈالا۔ آخر کار ان کے گناہ کی پاداش میں ان کے رب نے ان پر ایسی آفت توڑی کہ ایک ساتھ سب کو پیوند خاک کردیا ،');
INSERT INTO "sayyid_qutb" VALUES(91,15,'91:15','اور اسے (اپنے اس فعل کے) کسی برے نتیجے کا کوئی خوف نہیں ہے۔');
INSERT INTO "sayyid_qutb" VALUES(92,1,'92:1','” قسم ہے رات کی جب وہ چھا جائے');
INSERT INTO "sayyid_qutb" VALUES(92,2,'92:2','اور دن کی جب وہ روشن ہو');
INSERT INTO "sayyid_qutb" VALUES(92,3,'92:3','اور اس ذات کی جس نے نر اور مادہ کی تخلیق کی قسم “۔');
INSERT INTO "sayyid_qutb" VALUES(92,4,'92:4','در حقیقت تم لوگوں کی کوششیں مختلف قسم کی ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(92,5,'92:5','تو جس نے (راہ خدا میں) مال دیا اور (خدا کی نافرمانی سے) پرہیز کیا ،');
INSERT INTO "sayyid_qutb" VALUES(92,6,'92:6','اور بھلائی کو سچ مانا ،');
INSERT INTO "sayyid_qutb" VALUES(92,7,'92:7','اس کو ہم آسان راستے کے لئے سہولت دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(92,8,'92:8','اور جس نے بخل کیا اور (اپنے خدا سے) بےنیازی برتی');
INSERT INTO "sayyid_qutb" VALUES(92,9,'92:9','اور بھلائی کو جھٹلایا ،');
INSERT INTO "sayyid_qutb" VALUES(92,10,'92:10','اس کو ہم سخت راستے کے لئے سہولت دیں گے۔');
INSERT INTO "sayyid_qutb" VALUES(92,11,'92:11','اور اس کا مال آخر اس کے کس کام آئے گا جبکہ وہ ہلاک ہوجائے گا ؟ “۔');
INSERT INTO "sayyid_qutb" VALUES(92,12,'92:12','بیشک راستہ بتانا ہمارے ذمہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(92,13,'92:13','اور درحقیقت آخرت اور دنیا ، دونوں کے ہم مالک ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(92,14,'92:14','پس میں نے تم کو خبردار کردیا ہے بھڑکتی ہوئی آگ سے');
INSERT INTO "sayyid_qutb" VALUES(92,15,'92:15','۔ اس میں نہیں جھلسے گا مگر وہ انتہائی بدبخت');
INSERT INTO "sayyid_qutb" VALUES(92,16,'92:16','جس نے جھٹلایا اور منہ پھیرا۔');
INSERT INTO "sayyid_qutb" VALUES(92,17,'92:17','اور اس سے دور رکھا جائے گا وہ نہایت پرہیز گار');
INSERT INTO "sayyid_qutb" VALUES(92,18,'92:18','جو پاکیزہ ہونے کی خاطر اپنا مال دیتا ہے۔');
INSERT INTO "sayyid_qutb" VALUES(92,19,'92:19','اس پر کسی کا کوئی احسان نہیں ہے ،');
INSERT INTO "sayyid_qutb" VALUES(92,20,'92:20','جس کا بدلہ اسے دینا ہو وہ تو صرف اپنے رب برتر کی رضاجوئی کے لئے یہ کام کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(92,21,'92:21','اور ضرور وہ خوش ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(93,1,'93:1','” قسم ہے روز روشن کی');
INSERT INTO "sayyid_qutb" VALUES(93,2,'93:2','اور رات کی جبکہ وہ سکون کے ساتھ طاری ہوجائے ،');
INSERT INTO "sayyid_qutb" VALUES(93,3,'93:3','(اے نبی) تمہارے رب نے تم کو ہرگز نہیں چھوڑا ، اور نہ وہ ناراض ہوا ۔');
INSERT INTO "sayyid_qutb" VALUES(93,4,'93:4','اور یقینا تمہارے لئے بعد کا دور پہلے سے بہتر ہے');
INSERT INTO "sayyid_qutb" VALUES(93,5,'93:5','اور عنقریب تمہارا رب تم کو اتنا دے گا کہ تم خوش ہوجاؤ گے “۔');
INSERT INTO "sayyid_qutb" VALUES(93,6,'93:6','” کیا اس نے تم کو یتیم نہیں پایا اور پھر ٹھکانہ فراہم کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(93,7,'93:7','اور تمہیں ناواقف راہ پایا اور پھر ہدایت بخشی');
INSERT INTO "sayyid_qutb" VALUES(93,8,'93:8','اور تمہیں نادار پایا اور پھر مالدار کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(93,9,'93:9','لہٰذا یتیم پر سختی نہ کرو ،');
INSERT INTO "sayyid_qutb" VALUES(93,10,'93:10','اور سائل کو نہ جھڑکو ،');
INSERT INTO "sayyid_qutb" VALUES(93,11,'93:11','اور اپنے رب کی نعمت کا اظہار کرو “۔');
INSERT INTO "sayyid_qutb" VALUES(94,1,'94:1','”(اے نبی) کیا ہم نے تمہارا سینہ تمہارے لئے کھول نہیں دیا ؟');
INSERT INTO "sayyid_qutb" VALUES(94,2,'94:2','اور تم پر سے وہ باری بوجھ اتاردیا');
INSERT INTO "sayyid_qutb" VALUES(94,3,'94:3','جو تمہاری کمر توڑے ڈال رہا تھا۔');
INSERT INTO "sayyid_qutb" VALUES(94,4,'94:4','اور تمہاری خاطر تمہارے ذکر کا آواز بلند کردیا “۔');
INSERT INTO "sayyid_qutb" VALUES(94,5,'94:5','پس حقیقت یہ ہے کہ تنگی کے ساتھ فراخی بھی ہے۔');
INSERT INTO "sayyid_qutb" VALUES(94,6,'94:6','بیشک تنگی کے ساتھ فراخی بھی ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(94,7,'94:7','لہٰذا جب تم فارغ ہو تو عبادت کی مشقت میں لگ جاؤ');
INSERT INTO "sayyid_qutb" VALUES(94,8,'94:8','اور اپنے رب ہی کی طرف راغب ہوجاؤ“');
INSERT INTO "sayyid_qutb" VALUES(95,1,'95:1','قسم ہے انجیر اور زیتون کی');
INSERT INTO "sayyid_qutb" VALUES(95,2,'95:2','اور طور سینا');
INSERT INTO "sayyid_qutb" VALUES(95,3,'95:3','اور اس پر امن شہر (مکہ) کی “۔');
INSERT INTO "sayyid_qutb" VALUES(95,4,'95:4','ہم نے انسان کو بہترین ساخت پر پیدا کیا ،');
INSERT INTO "sayyid_qutb" VALUES(95,5,'95:5','پھر اسے الٹا پھیر کر ہم نے سب نیچوں سے نیچ کردیا ،');
INSERT INTO "sayyid_qutb" VALUES(95,6,'95:6','سوائے ان لوگوں کے جو ایمان لائے اور نیک عمل کرتے رہے کہ ان کے لئے کبھی ختم نہ ہونے والا اجر ہے “');
INSERT INTO "sayyid_qutb" VALUES(95,7,'95:7','پس (اے نبی) اس کے بعد کون جزا وسزا کے معاملہ میں تم کو جھٹلا سکتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(95,8,'95:8','کیا اللہ سب حاکموں سے بڑا حاکم نہیں ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(96,1,'96:1','پڑھو (اے نبی) اپنے رب کے نام کے ساتھ جس نے پیدا کیا ،');
INSERT INTO "sayyid_qutb" VALUES(96,2,'96:2','جمے ہوئے خون کے ایک لوتھڑے سے انسان کی تخلیق کی۔');
INSERT INTO "sayyid_qutb" VALUES(96,3,'96:3','پڑھو ، اور تمہارا رب بڑا کریم ہے');
INSERT INTO "sayyid_qutb" VALUES(96,4,'96:4','جس نے قلم کے ذریعہ سے علم سکھایا ،');
INSERT INTO "sayyid_qutb" VALUES(96,5,'96:5','انسان کو وہ علم دیا جسے وہ نہ جانتا تھا “۔');
INSERT INTO "sayyid_qutb" VALUES(96,6,'96:6','ہرگز نہیں ، انسان سرکشی کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(96,7,'96:7','اس بنا پر کہ وہ اپنے آپ کو بےنیاز دیکھتا ہے');
INSERT INTO "sayyid_qutb" VALUES(96,8,'96:8','(حالانکہ) پلٹنا یقینا تیرے رب ہی کی طرف ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(96,9,'96:9','تم نے دیکھا اس شخص جو ایک بندے کو منع کرتا ہے');
INSERT INTO "sayyid_qutb" VALUES(96,10,'96:10','جبکہ وہ نماز پڑھتا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(96,11,'96:11','تمہارا کیا خیال ہے اگر (وہ بندہ ) راہ راست پر ہو');
INSERT INTO "sayyid_qutb" VALUES(96,12,'96:12','یا پرہیزگاری کی تلقین کرتا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(96,13,'96:13','تمہارا کیا خیال ہے اگر (یہ منع کرنے والا شخص حق کو) جھٹلاتا اور منہ موڑتا ہو ؟');
INSERT INTO "sayyid_qutb" VALUES(96,14,'96:14','کیا وہ نہیں جانتا کہ اللہ دیکھ رہا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(96,15,'96:15','ہرگز نہیں ! اگر وہ باز نہ آیا تو ہم اس کی پیشانی کے بال پکڑ کر کھینچیں گے ،');
INSERT INTO "sayyid_qutb" VALUES(96,16,'96:16','اس پیشانی کو جو جھوٹی اور سخت خطاکار ہے۔');
INSERT INTO "sayyid_qutb" VALUES(96,17,'96:17','وہ بلالے اپنے حامیوں کی ٹولی کو ،');
INSERT INTO "sayyid_qutb" VALUES(96,18,'96:18','ہم بھی عذاب کے فرشتوں کو بلالیں گے۔ “');
INSERT INTO "sayyid_qutb" VALUES(96,19,'96:19','ہرگز نہیں ، اس کی بات نہ مانو اور سجدہ کرو اور (اپنے رب کا) قرب حاصل کرو۔');
INSERT INTO "sayyid_qutb" VALUES(97,1,'97:1','ہم نے اس کو لیلة القدر میں نازل کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(97,2,'97:2','اور تم کیا جانو کہ شب قدر کیا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(97,3,'97:3','شب قدر ایک ہزار مہینوں سے زیادہ بہتر ہے۔');
INSERT INTO "sayyid_qutb" VALUES(97,4,'97:4','فرشتے اور روح ، اس میں اپنے رب کے اذن سے ہر حکم لے کر اترتے ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(97,5,'97:5','یہ رات سراسر سلامتی ہے طلوع فجر تک “۔');
INSERT INTO "sayyid_qutb" VALUES(98,1,'98:1','اہل کتاب اور مشرکین میں سے جو لوگ کافر تھے ، (وہ اپنے کفر سے) باز آنے والے نہ تھے جب تک کہ ان میں پاس دلیل روشن نہ آجائے');
INSERT INTO "sayyid_qutb" VALUES(98,2,'98:2','(یعنی) اللہ کی طرف سے ایک رسول جو پاک صحیفے پڑھ کر سنائے');
INSERT INTO "sayyid_qutb" VALUES(98,3,'98:3','جن میں بالکل راست اور درست تحریریں لکھی ہوئی ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(98,4,'98:4','پہلے جن لوگوں کو کتاب دی گئی تھی ، ان میں تفرقہ برپا نہیں ہوا مگر اس کے بعد کہ ان کے پاس واضح بیان آچکا تھا “');
INSERT INTO "sayyid_qutb" VALUES(98,5,'98:5','” اور ان کو اس کے سوا کوئی حکم نہیں دیا گیا تھا کہ اللہ کی بندگی کریں ، اپنے دن کو اس کے لئے خالص کرکے ، بالکل یک سو ہوکر ، اور نماز قائم کریں اور زکوٰة دیں۔ یہی نہایت صحیح و درست دین ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(98,6,'98:6','” اہل کتاب اور مشرکین میں سے جن لوگوں نے کفر کیا ہے وہ یقینا جہنم کی آگ میں جائیں گے اور ہمیشہ اس میں رہیں گے ، یہ لوگ بدترین خلائق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(98,7,'98:7','جو لوگ ایمان لے آئے اور جنہوں نے نیک عمل کیے ، وہ یقینا بہترین خلائق ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(98,8,'98:8','ان کی جزا ان کے رب کے ہاں دائمی قیام کی جنتیں ہیں جن کے نیچے نہریں بہہ رہی ہوں گی۔ وہ ان میں ہمیشہ ہمیشہ رہیں گے۔ اللہ ان سے راضی ہوا اور وہ اللہ سے راضی ہوئے ۔ یہ کچھ ہے اس شخص کے لئے جس نے اپنے رب کا خوف کیا ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(99,1,'99:1','جب زمین اپنی پوری شدت کے ساتھ ہلاڈالی جائے گی ،');
INSERT INTO "sayyid_qutb" VALUES(99,2,'99:2','اور زمین اپنے اندر کے سارے بوجھ نکال کر باہر ڈال دے گی ،');
INSERT INTO "sayyid_qutb" VALUES(99,3,'99:3','اور انسان کہے گا کہ یہ اس کو کیا ہورہا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(99,4,'99:4','اس روز وہ اپنے (اوپر گزرے ہوئے) حالات بیان کرے گی');
INSERT INTO "sayyid_qutb" VALUES(99,5,'99:5','کیونکہ تیرے رب نے اسے (ایسا کرنے کا) حکم دیا ہوگا “۔');
INSERT INTO "sayyid_qutb" VALUES(99,6,'99:6','اس روز لوگ متفرق حالت میں پلٹیں گے تاکہ ان کے اعمال ان کو دکھائے جائیں۔');
INSERT INTO "sayyid_qutb" VALUES(99,7,'99:7','پھر جس نے ذرا برابر نیکی کی ہوگی وہ اس کو دیکھ لے گا');
INSERT INTO "sayyid_qutb" VALUES(99,8,'99:8','اور جس نے ذرہ برابر بدی کی ہوگی وہ اس کو دیکھ لے گا “۔');
INSERT INTO "sayyid_qutb" VALUES(100,1,'100:1','قسم ہے ان (گھوڑوں) کی جو پھنکارے مارتے ہوئے دوڑتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(100,2,'100:2','پھر (اپنی ٹاپوں سے) چنگاریاں جھاڑتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(100,3,'100:3','پھر صبح سویرے چھاپہ مارتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(100,4,'100:4','پھر اس موقع پر گردوغبار اڑاتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(100,5,'100:5','پھر اسی حالت میں کسی مجمع کے اندر جاگھستے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(100,6,'100:6','حقیقت یہ ہے کہ انسان اپنے رب کا بڑا ناشکرا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(100,7,'100:7','اور وہ خود اس پر گواہ ہے ،');
INSERT INTO "sayyid_qutb" VALUES(100,8,'100:8','اور وہ مال و دولت کی محبت میں بری طرح مبتلا ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(100,9,'100:9','تو کیا وہ اس وقت کو نہیں جانتا جب قبروں میں جو کچھ (مدفون) ہے اسے نکال لیاجائے ،');
INSERT INTO "sayyid_qutb" VALUES(100,10,'100:10','اور سینوں میں جو کچھ (مخفی) ہے اسے برآمد کرکے اس کی جانچ پڑتال کی جائے گی “۔');
INSERT INTO "sayyid_qutb" VALUES(100,11,'100:11','یقینا ان کا رب اس روز ان سے خوب باخبر ہوگا');
INSERT INTO "sayyid_qutb" VALUES(101,1,'101:1','عظیم حادثہ !');
INSERT INTO "sayyid_qutb" VALUES(101,2,'101:2','کیا ہے وہ عظیم حادثہ ؟');
INSERT INTO "sayyid_qutb" VALUES(101,3,'101:3','تم کیا جانو کہ وہ عظیم حادثہ کیا ہے');
INSERT INTO "sayyid_qutb" VALUES(101,4,'101:4','” وہ دن جب لوگ بکھرے ہوئے پروانوں کی طرح');
INSERT INTO "sayyid_qutb" VALUES(101,5,'101:5','اور پہاڑ رنگ برنگ کے دھنکے ہوئے اون کی طرح ہوں گے “۔');
INSERT INTO "sayyid_qutb" VALUES(101,6,'101:6','پھر جس کے پلڑے بھاری ہوں گے ،');
INSERT INTO "sayyid_qutb" VALUES(101,7,'101:7','وہ دل پسند عیش میں ہوگا ،');
INSERT INTO "sayyid_qutb" VALUES(101,8,'101:8','اور جس کے پلڑے ہلکے ہوں گے');
INSERT INTO "sayyid_qutb" VALUES(101,9,'101:9','اس کی جائے قرار گہری کھائی ہوگی');
INSERT INTO "sayyid_qutb" VALUES(101,10,'101:10','اور تمہیں کیا خبر کہ وہ کیا چیز ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(101,11,'101:11','بھڑکتی ہوئی آگ “۔');
INSERT INTO "sayyid_qutb" VALUES(102,1,'102:1','تم لوگوں کو زیادہ سے زیادہ اور ایک دوسرے سے بڑھ کر دنیا حاصل کرنے کی دھن نے غفلت میں ڈال رکھا ہے');
INSERT INTO "sayyid_qutb" VALUES(102,2,'102:2','یہاں تک کہ (اسی فکر میں) تم لب گور تک پہنچ جاتے ہو “۔');
INSERT INTO "sayyid_qutb" VALUES(102,3,'102:3','ہرگز نہیں ، عنقریب تم کو معلوم ہوجائے گا۔');
INSERT INTO "sayyid_qutb" VALUES(102,4,'102:4','پھر (سن لو کہ) ہرگز نہیں ، عنقریب تم کو معلوم ہوجائے گا “');
INSERT INTO "sayyid_qutb" VALUES(102,5,'102:5','” ہرگز نہیں ! اگر تم یقینی علم کی حیثیت سے (اس روش کے انجام کو) جانتے ہوتے (تو تمہارا یہ طرز عمل نہ ہوتا) “۔');
INSERT INTO "sayyid_qutb" VALUES(102,6,'102:6','” تم دوزخ دیکھ کر رہوگے “۔');
INSERT INTO "sayyid_qutb" VALUES(102,7,'102:7','پھر (سن لو کہ) تم بالکل یقین کے ساتھ اسے دیکھ لوگے “');
INSERT INTO "sayyid_qutb" VALUES(102,8,'102:8','پھر ضرور اس روز تم سے ان نعمتوں کے بارے میں جواب طلبی کی جائے گی۔');
INSERT INTO "sayyid_qutb" VALUES(103,1,'103:1','زمانے کی قسم ،');
INSERT INTO "sayyid_qutb" VALUES(103,2,'103:2','انسان درحقیقت خسارے میں ہے ،');
INSERT INTO "sayyid_qutb" VALUES(103,3,'103:3','سوائے ان لوگوں کے جو ایمان لائے ، اور نیک اعمال کرتے رہے ، اور ایک دوسرے کو حق کی نصیحت اور صبر کی تلقین کرتے رہے “۔');
INSERT INTO "sayyid_qutb" VALUES(104,1,'104:1','تباہی ہے ہر اس شخص کے لئے جو (منہ در منہ) لوگوں پر طعن اور (پیٹھ پیچھے) برائیاں کرنے کا خوگر ہے ،');
INSERT INTO "sayyid_qutb" VALUES(104,2,'104:2','جس نے مال جمع کیا اور اسے گن گن کر رکھا۔');
INSERT INTO "sayyid_qutb" VALUES(104,3,'104:3','وہ سمجھتا ہے کہ اس کا مال ہمیشہ اس کے پاس رہے گا۔');
INSERT INTO "sayyid_qutb" VALUES(104,4,'104:4','ہرگز نہیں ، وہ شخص تو چکنا چور کردینے والی جگہ میں پھینک دیا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(104,5,'104:5','اور تم کیا جانو کہ کیا ہے وہ چکنا چور کردینے والی جگہ ؟');
INSERT INTO "sayyid_qutb" VALUES(104,6,'104:6','اللہ کی آگ ، خوب بھڑکائی ہوئی ،');
INSERT INTO "sayyid_qutb" VALUES(104,7,'104:7','جو دلوں تک پہنچے گی۔');
INSERT INTO "sayyid_qutb" VALUES(104,8,'104:8','وہ ان پر ڈھانک کر بند کردی جائے گی (اس حالت میں کہ وہ)');
INSERT INTO "sayyid_qutb" VALUES(104,9,'104:9','اونچے اونچے ستونوں میں (گھرے ہوئے ہوں گے) “۔');
INSERT INTO "sayyid_qutb" VALUES(105,1,'105:1','تم نے دیکھا نہیں کہ تمہارے رب نے ہاتھی والوں کے ساتھ کیا کیا ؟');
INSERT INTO "sayyid_qutb" VALUES(105,2,'105:2','کیا اس نے ان کی تدبیر کو اکارت نہیں کردیا ؟');
INSERT INTO "sayyid_qutb" VALUES(105,3,'105:3','اور ان پر پرندوں کے جھنڈ کے جھنڈ بھیج دیئے');
INSERT INTO "sayyid_qutb" VALUES(105,4,'105:4','جو ان کے اوپر پکی ہوئی مٹی کے پتھر پھینک رہے تھے ،');
INSERT INTO "sayyid_qutb" VALUES(105,5,'105:5','پھر ان کا یہ حال کردی جیسے (جانوروں کا ) کھایا ہوا بھوسا۔');
INSERT INTO "sayyid_qutb" VALUES(106,1,'106:1','” چوں کہ قریش مانوس ہوئے');
INSERT INTO "sayyid_qutb" VALUES(106,2,'106:2','(یعنی جاڑے اور گرمی کے سفروں سے مانوس ،');
INSERT INTO "sayyid_qutb" VALUES(106,3,'106:3','لہٰذا ان کو چاہئے کہ اس گھر کے رب کی عبادت کریں');
INSERT INTO "sayyid_qutb" VALUES(106,4,'106:4','جس نے انہیں بھوک سے بچا کر کھانے کو دیا اور خوف سے بچا کر امن عطا کیا “۔');
INSERT INTO "sayyid_qutb" VALUES(107,1,'107:1','تم نے دیکھا اس شخص کو جو آخرت کی جزاوسزا کو جھٹلاتا ہے ؟');
INSERT INTO "sayyid_qutb" VALUES(107,2,'107:2','وہی تو ہے جو یتیم کو دھکے دیتا ہے');
INSERT INTO "sayyid_qutb" VALUES(107,3,'107:3','اور مسکین کا کھانا دینے پر نہیں اکساتا۔');
INSERT INTO "sayyid_qutb" VALUES(107,4,'107:4','پھر تباہی ہے ان نماز پڑھنے والوں کے لئے');
INSERT INTO "sayyid_qutb" VALUES(107,5,'107:5','جو اپنی نماز سے غفلت برتتے ہیں ،');
INSERT INTO "sayyid_qutb" VALUES(107,6,'107:6','جو ریاکاری کرتے ہیں');
INSERT INTO "sayyid_qutb" VALUES(107,7,'107:7','اور معمولی ضرورت کی چیزیں (لوگوں کو) دینے سے گریز کرتے ہیں “۔');
INSERT INTO "sayyid_qutb" VALUES(108,1,'108:1','(اے نبی) ہم نے تمہیں کوثر عطا کردیا');
INSERT INTO "sayyid_qutb" VALUES(108,2,'108:2','پس تم اپنے رب ہی کے لئے نماز پڑھو اور قربانی کرو ، ۔');
INSERT INTO "sayyid_qutb" VALUES(108,3,'108:3','تمہارا دشمن ہی جڑ کٹا ہے “');
INSERT INTO "sayyid_qutb" VALUES(109,1,'109:1','” کہہ دو کہ اے کافرو ،');
INSERT INTO "sayyid_qutb" VALUES(109,2,'109:2','میں ان کی عبادت نہیں کرتا جن کی عبادت تم کرتے ہو ،');
INSERT INTO "sayyid_qutb" VALUES(109,3,'109:3','نہ تم اس کی عبادت کرنے والے ہو ، جس کی عبادت میں کرتا ہوں');
INSERT INTO "sayyid_qutb" VALUES(109,4,'109:4','اور نہ میں ان کی عبادت کرنے والا ہوں جنکی عبادت تم نے کی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(109,5,'109:5','اور نہ تم اس کی عبادت کرنے والے ہو جس کی عبادت میں کرتا ہوں۔');
INSERT INTO "sayyid_qutb" VALUES(109,6,'109:6','تمہارے لئے تمہارا دین ہے اور میرے لئے میرا دین “۔');
INSERT INTO "sayyid_qutb" VALUES(110,1,'110:1','جب اللہ کی مدد آجائے اور فتح نصیب ہوجائے');
INSERT INTO "sayyid_qutb" VALUES(110,2,'110:2','اور (اے نبی) تم دیکھ لو کہ لوگ فوج درفوج اللہ کے دین میں داخل ہورہے ہیں');
INSERT INTO "sayyid_qutb" VALUES(110,3,'110:3','تو اپنے رب کی حمد کے ساتھ اس کی تسبیح کرو ، اور اس سے مغفرت کی دعامانگو ، بیشک وہ بڑا توبہ قبول کرنے والا ہے');
INSERT INTO "sayyid_qutb" VALUES(111,1,'111:1','” ٹوٹ گئے ابولہب کے ہاتھ اور نامراد ہوگیا وہ۔');
INSERT INTO "sayyid_qutb" VALUES(111,2,'111:2','اس کا مال اور جو کچھ اس نے کمایا وہ اس کے کسی کام نہ آیا۔');
INSERT INTO "sayyid_qutb" VALUES(111,3,'111:3','ضرور وہ شعلہ زن آگ میں ڈالا جائے گا');
INSERT INTO "sayyid_qutb" VALUES(111,4,'111:4','اور (اس کے ساتھ) اس کی جورو بھی ، لگائی بجھائی کرنے والی ،');
INSERT INTO "sayyid_qutb" VALUES(111,5,'111:5','اس کی گردن میں مونچھ کی رسی ہوگی “۔');
INSERT INTO "sayyid_qutb" VALUES(112,1,'112:1','کہو ، وہ اللہ ہے ، یکتا۔');
INSERT INTO "sayyid_qutb" VALUES(112,2,'112:2','اللہ سب سے بےنیاز ہے اور سب اس کے محتاج ہیں۔');
INSERT INTO "sayyid_qutb" VALUES(112,3,'112:3','نہ اس کی کوئی اولاد ہے اور نہ وہ کسی کی اولاد ۔');
INSERT INTO "sayyid_qutb" VALUES(112,4,'112:4','اور کوئی اس کا ہمسر نہیں ہے “۔');
INSERT INTO "sayyid_qutb" VALUES(113,1,'113:1','” کہو ، میں پناہ مانگتا ہوں صبح کے رب کی ،');
INSERT INTO "sayyid_qutb" VALUES(113,2,'113:2','ہر اس چیز کے شر سے جو اس نے پیدا کی ہے ،');
INSERT INTO "sayyid_qutb" VALUES(113,3,'113:3','اور رات کی تاریکی کے شر سے جب کہ وہ چھا جائے ،');
INSERT INTO "sayyid_qutb" VALUES(113,4,'113:4','اور گرہوں میں پھونکنے والوں (یا والیوں) کے شر سے ،');
INSERT INTO "sayyid_qutb" VALUES(113,5,'113:5','اور حاسد کے شر سے جب کہ وہ حسد کرے “۔');
INSERT INTO "sayyid_qutb" VALUES(114,1,'114:1','” کہو ، میں پناہ مانگتا ہوں انسانوں کے رب ،');
INSERT INTO "sayyid_qutb" VALUES(114,2,'114:2','انسانوں کے بادشاہ ،');
INSERT INTO "sayyid_qutb" VALUES(114,3,'114:3','انسانوں کے حقیقی معبود کی ،');
INSERT INTO "sayyid_qutb" VALUES(114,4,'114:4','اس وسوسہ ڈالنے والے کے شر سے جو بارہا پلٹ کر آتا ہے ،');
INSERT INTO "sayyid_qutb" VALUES(114,5,'114:5','جو لوگوں کے دلوں میں وسوسے ڈالتا ہے');
INSERT INTO "sayyid_qutb" VALUES(114,6,'114:6','خواہ وہ جنوں میں سے ہو یا انسانوں میں سے “۔');
