INSERT INTO "silika" VALUES(1,1,'1:1','Mulina lya Allah, Jwaukoto wejinji, Jwachanasa channope.');
INSERT INTO "silika" VALUES(1,2,'1:2','Lumbili losope lwene alapikwe Allah, M''mbuje jwa iwumbe yosope. ');
INSERT INTO "silika" VALUES(1,3,'1:3','Jwaukoto wejinji, Jwachanasa channope.');
INSERT INTO "silika" VALUES(1,4,'1:4','Nsyene Lisiku Lyamalipilo.');
INSERT INTO "silika" VALUES(1,5,'1:5','Kwele Jikape tukwagalagatila, soni Kwele Jikape tukwaŵenda chikamuchisyo.');
INSERT INTO "silika" VALUES(1,6,'1:6','Tujongolelani kwitala lyagoloka. ');
INSERT INTO "silika" VALUES(1,7,'1:7','Litala lya aŵala ŵanya ŵamwapele chindimba, ngaŵaga ŵaŵatumbililidwe (aŵala ŵaŵaimanyi yakuona nikuileka), kapena ŵaŵasokonechele (aŵala ŵaŵailesile yakuona ligongo lya ujinga ni kupotela).');
INSERT INTO "silika" VALUES(2,1,'2:1','Alif-Lâm-Mîm. ');
INSERT INTO "silika" VALUES(2,2,'2:2','Achi ni Chitabu changali kaikasya nkati mwakwe, chongolo cha ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(2,3,'2:3','Aŵala ŵakasakulupililaga yangaiona, ni akwimikaga Swala mwakolosya, soni mu ichindu yatwapele akasatolaga.');
INSERT INTO "silika" VALUES(2,4,'2:4','Ni aŵala ŵakasakulupililaga yayatulusyidwe kukwenu, ni yayatulusyidwe paujo penu (mmwe nkanim’biche), ni ya Akhera jamanjao akasakulupilila kusyesyene.');
INSERT INTO "silika" VALUES(2,5,'2:5','Ŵele ŵanganyao ali pachongoko chakuumila kwa M’mbuje gwao, soni ŵanyao niŵali ŵakupunda.');
INSERT INTO "silika" VALUES(2,6,'2:6','Chisimu aŵala ŵakanile; yalumope kwa jamanjao mwatetele kapena nkatetela ngakulupililape.');
INSERT INTO "silika" VALUES(2,7,'2:7','Allah ŵadindile chidindo m’mitima mwao ni m’mawiwi mwao, soni m’meso mwao mwana chisiŵilo, basi tachipata jamanjao ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(2,8,'2:8','Soni chilikati cha ŵandu pana (achinakunaku) ŵakutiji: “Tukulupilile mwa Allah ni Lisiku Lyambesi.” Kutendaga nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(2,9,'2:9','Akuntenda chinyengo Allah ni aŵala ŵakulupilile, ni pangali ŵaakwanyenga ikaŵeje achimisyene, ni ngakulimanyilila.');
INSERT INTO "silika" VALUES(2,10,'2:10','M’mitima mwao mwana ulwele; sano Allah niŵajonjechesye ulweleo, basi tachipata jamanjao ilagasyo yakupoteka nnope ligongo lyakuŵechetaŵecheta kwao yaunami.');
INSERT INTO "silika" VALUES(2,11,'2:11','Soni naga asalilidwemo kuti: “Nkawatanganyaga pachilambo.” Akasatiji: “Chisimutu uwwe niŵakwilanya.”');
INSERT INTO "silika" VALUES(2,12,'2:12','Manyililani yanti ŵanganyao chisimu chene niŵali ŵakuwatanganya, nambo ngakulimanyilila.');
INSERT INTO "silika" VALUES(2,13,'2:13','Soni naga asalilidwemo kuti: “Kulupililani yatite pakulupilila ŵandu ŵane.” Akasatiji: “Ana tukulupilile yatite pakulupilila ŵawawate?” Manyililani yanti ŵanganyao chisimu chene niŵali ŵakuwawata, nambo ngakupata kumanyilila.');
INSERT INTO "silika" VALUES(2,14,'2:14','Soni naga asimene ni aŵala ŵakulupilile, akasatiji: “Tukulupilile,” nambo pati apite pa kasyepela ku mashetani gajao, akasatiji: “Chisimu uwwe tulinamwe, uwwetu tukuŵa tuli nkwatenda chipongwe.”');
INSERT INTO "silika" VALUES(2,15,'2:15','Allah chachalipila pa chipongwe chaocho, ni chiŵalechelele nkusokonechela mwaomo achiyumbagayumbaga.');
INSERT INTO "silika" VALUES(2,16,'2:16','Jamanjao ni aŵalaŵala ŵasagwile kusokonechela pakusumanya ni chongoko, nambo kusumanya kwaoko nganikwajonjechesya (chilichose), soni nganaŵa ŵakongoka.');
INSERT INTO "silika" VALUES(2,17,'2:17','Chilandanyo chao chili chisawu ajulajula jwana kolesyaga moto (ndema jajwaliji nkwenda nchipi), sano katema kawalangwisye yayaliji mungulugulu mwakwe, Allah ŵatyochesye lilanguka lyaolyo, nikwaleka ali nchipi ŵangaŵa nkulola.');
INSERT INTO "silika" VALUES(2,18,'2:18','Ŵangapikana, ŵangaŵecheta, ŵangalola soni nganaŵa awujile (kuchongoko).');
INSERT INTO "silika" VALUES(2,19,'2:19','Kapena mpela wula janneso jakuuma kwinani, nikuŵa mwana chipi, kulindima ni kumesya, akuŵikaga yala yao m’mawiwi mwao ligongo lya chikokomo pakogopa kuwa, nambo Allah agasyungulile makafili (pagamanya chenene).');
INSERT INTO "silika" VALUES(2,20,'2:20','Kumesya kukusigalaga panandi kuti kusombole meso gao, ndaŵi ni katema jakukwamulichila (kumesyako) akwendaga mwilangukamo, nambo naga kwasimilemo akasajima. Mwanti Allah angasache angatyochesye kupikana kwao ni kulola kwao, chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(2,21,'2:21','E jenumanja achinaŵandu! Mun''galagatileje M''mbuje gwenu juŵan''gumbile jemanjammwe ni ŵakala ŵenu, kuti m''be ŵakuligosa (ku ipotesi).');
INSERT INTO "silika" VALUES(2,22,'2:22','Ajula jwana kuntendelaga litaka kuŵa chakutandika, liwunde nikuŵa nsakasa, nijwaatulwisye kuumila kwinani mesi (gawula), nijwaamesisye ni mesigo isogosi kuti iŵe yakulya yenu, ningasimum''bichilaga Allah milungu kutendaga jenumanja nchimanyililaga (kuti jwangali ŵakamusyangana Najo).');
INSERT INTO "silika" VALUES(2,23,'2:23','Sano naga n''di ŵakaika pa ichindu yatuntuluchisye kapolo Jwetu, ikani najo sura jimpepe jakulandana ni ja m’Qur’an, soni mwaŵilanje ŵakamusyangana nao ŵangaŵaga Allah, naga jenumanja nkuŵecheta yakuona.');
INSERT INTO "silika" VALUES(2,24,'2:24','Nambo naga ngantenda nikuti ngasintenda soni, basi ujogopani Moto wauli sasu syakwe ŵandu ni maganga, wauŵichidwe kuŵa wamakafili.');
INSERT INTO "silika" VALUES(2,25,'2:25','Mwatagulilani aŵala ŵakulupilile nikutendaga itendo yambone abali jakusengwasya janti akwete kupata ŵanganyao Matimbe ga ku Mbepo, gasikujilima cha pasi pakwe sulo, ndaŵi ni katema kachakapedweje isogosi mwalakwemo kuŵa yakulya (yao), takajileje: “Ayi ni ayilayila itwapedwaga kala yila.” Ni takapedweje ichindu yakulandana (kaoneche kakwe nambo yakulekangana kanonye), soni akwete kupata ŵanganyao mwalakwemo achiŵammakwao ŵaswejesyedwe, soni jamanjao mwalakwemo takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,26,'2:26','Chisimu Allah jwangantesya soni kupeleka chilandanyo cha njenjema kapena chakupunda pele. Sano aŵala ŵakulupilile niakumanyililaga kuti chilandanyochi chakuonaonape chakuumila kwa M’mbuje gwao, nambo aŵala ŵakanile niakasatiji: “Ana Allah akusaka chichi pa chilandanyo chalakwechi?” (Allah) akwalechelelaga ŵajinji kusokonechela nacho, nambo soni akwajongolaga nacho ŵajinji. Nambotu jwaangaalechelela kusokonechela nacho ikaŵeje ŵakunyosya.');
INSERT INTO "silika" VALUES(2,27,'2:27','Aŵala ŵaakugopolanya chilanga cha Allah panyuma pakuchitaŵa, ni akasakataga yalamwile Allah kuti ilumbikanyisidwe, soni akasawatanganyaga pa chilambo, jamanjao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(2,28,'2:28','Ana chimunkane chinauli Allah kutendaga mwaliji ŵawe niŵampele umi (wa duniya), kaneka champe chiwa, kaneka nichachin''jimusya (m’malembe), kaneko Kukwakwe tinchiujila.');
INSERT INTO "silika" VALUES(2,29,'2:29','Jwalakwe ni Ajula juŵan''guumbiile yosope yaili pa chilambo, kaneka ŵasachilile gumba liwunde, niŵataŵile mawunde nsano nagaŵili, soni Jwalakwe chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(2,30,'2:30','Soni (kumbuchilani) katema kaŵaŵechete Ambuje ŵenu kwa Achimalaika kuti: “Chisimu Une ngusaka kuŵika ku duniya (achinaŵandu) ŵatakaŵeje alinkupisyangana (pakupitisya malamusi mmalo Mwangune).” Ŵanganyao ŵatite: “Ana nkusaka kuŵika mwalakwemo ŵatachiŵaga nkuwatanganya ni kusulusyaga myasi, kutendaga uwwe tukuŵa tuli nkunnapilila ni lumbili Lwenu soni tukunswejesyaga?” Jwalakwe jwatite: “Chisimu Une ngumanyilila yangankuimanyilila jenumanja.”');
INSERT INTO "silika" VALUES(2,31,'2:31','Niŵan''jiganyisye Adam mena ga indu yosope, kaneka ŵailosisye kwa Achimalaika niŵaatesile kuti: “Mundagulilani mena ga ayi induyi naga nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(2,32,'2:32','Ŵanganyao ŵatite: “Kuswejela ni Kwenu! Uwwe ŵangali umanyilisi (pa iliyose) ikaŵeje yantumanyisye, chisimutu M''mwe ni ŵan''di Ŵakumanyilila kwannope, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(2,33,'2:33','Jwalakwe jwatite: “E mmwe Adam! Mwatagulilani mena gakwe.” Sano katema kajwatagulile mena gakwe (Allah) jwatite: “Ana nganimbecheta kukwenu yanti chisimu Une ngumanyilila yakusisika ya kumawunde ni petaka, soni nguimanyilila yankulosya palangulangu niyam''bele jenumanja n''di nkusisa?”');
INSERT INTO "silika" VALUES(2,34,'2:34','Soni (kumbuchilani) katema katwasalile Achimalaika kuti: “Mwasujudilani Adam.” Basi wosope niŵasujudu kusigala Ibulisu; ŵakanile niŵalikwesyisye, mwanti ŵasyuchile kuŵa jumpepe mwa makafili.');
INSERT INTO "silika" VALUES(2,35,'2:35','Soni twatite: “E mmwe Adam! Tamani mmwejo ni ŵankwenu mwitimbe lya ku Mbepo, ni n’dyeje mwalakwemo mwagopoka palipose pansachile, nambo ngasinchiŵandichila achi chitelachi ntakuŵa mu ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(2,36,'2:36','Nambo shetani ŵatelesyasisye ŵanaŵaŵilio mwalakwemo nikwakopokasya mu ŵaliji mula, ni twatite: “Tulukani, chilikati chenu chili chana utinda, soni nkwete jenumanja ku duniya ndamo jenu ni kusengwa kwa ndema jannono.”');
INSERT INTO "silika" VALUES(2,37,'2:37','Sano Adam jwapochele maloŵe (gakutendela toba) kuumila kwa M''mbujegwe (nijwatesile toba) niŵampochele toba jakwejo, chisimu Jwalakwe ni Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,38,'2:38','Twatite: “Tulukani mwalakwemo ŵanaose; naga chikanyyichilile chongoko chakuumila Kukwangu, basi ŵatakakuyeje chongoko Changucho ngaaja kuŵa niwoga soni ngaaja kuŵa ŵakudandaula.”');
INSERT INTO "silika" VALUES(2,39,'2:39','“Nambo ŵandu ŵaakufuulu nikanila ma Ȃya Getu ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.”');
INSERT INTO "silika" VALUES(2,40,'2:40','E jenumanja ŵanache ŵa Israila! Kumbuchilani chindimba Changu chanampele, soni kwanisyani chilanga Changu None chingwanisye chilanga chenu, soni Jikape Une basi njogopani.');
INSERT INTO "silika" VALUES(2,41,'2:41','Soni ikulupililani yandulwisye (yayili m''Qur’an) yayikwitichisya yan''di nayo, ni nkaŵa ŵandanda kuikana; soni kasinsagulaga (ya duniya) ya ntengo wannono pa kusumanya ni ma Ȃya Gangu, soni Jikape Une basi njogopani.');
INSERT INTO "silika" VALUES(2,42,'2:42','Soni nkawanganyaga yakuona ni yaunami ni kusisaga yakuona kutendaga jenumanja nchimanyililaga. ');
INSERT INTO "silika" VALUES(2,43,'2:43','Soni jimikani Swala mwakolosya nikutolaga Zaka, ni n’jinameje pamo ni akwinama.');
INSERT INTO "silika" VALUES(2,44,'2:44','Ana nkwachisya ŵandu ŵane yambone nikuliliŵalila mwachimisyene posepo jenumanja nkusomaga chitabu, ana ŵangali lunda?');
INSERT INTO "silika" VALUES(2,45,'2:45','Soni likamuchisyani pa kupilila ni pa (kuswali) Swala, soni chisimu jalakwejo jiŵele jausito ikaŵeje kwa ŵakulinandiya.');
INSERT INTO "silika" VALUES(2,46,'2:46','Aŵala ŵaakusimichisya kuti tachisimangana ni M’mbuje gwao (Allah), soni kuti ŵanganyao Kukwakwe chachiujila.');
INSERT INTO "silika" VALUES(2,47,'2:47','E jenumanja ŵanache ŵa Israila! Kumbuchilani chindimba Changu chanampele, soni nanchimbichisye kupunda iwumbe yosope (ya ndaŵi jenu).');
INSERT INTO "silika" VALUES(2,48,'2:48','Ni lijogopani Lisiku lyangakomboleka mundu kunkamuchisya mundu jwine chilichose, soni ngasikukundidwa kutyochela kwa jwalakwejo kuchondelela, atamuno chaliwombolela chakuuma kukwakwe ngasichipocheledwa, soni ŵanganyao ngasaŵa ŵakulupusyidwa.');
INSERT INTO "silika" VALUES(2,49,'2:49','Soni (kumbuchilani) katema katwan''jokwele ku ŵandu ŵa Firiauna ŵaŵaliji nkumpa ilagasyo yakusakala nnope, ŵaliji nkusikita ŵanache ŵenu ŵachilume nikwalekaga wumi ŵanache ŵenu ŵachikongwe, sano mu yalakweyo mwaliji mwana kulinjidwa kwekulungwa nnope kwakutyochela kwa M''mbuje gwenu.');
INSERT INTO "silika" VALUES(2,50,'2:50','Soni (kumbuchilani) katema katwannekanganichisye mbwani, mwanti twan''jokwele ŵanganyammwe ni kwamisya ŵandu ŵa Firiauna, kutendaga jenumanja n''di nkulola.');
INSERT INTO "silika" VALUES(2,51,'2:51','Soni (kumbuchilani) katema katwampele chilanga Musa cha ilo makumi ncheche (kuti atende ibada nkumalamo tumpe Taurati), kaneka nimwakatesile kang''ombe (kuŵa kannungu kenu) panyuma pakwe (jwele ali apite kwitumbi kupochela Tauratijo). Nimwaliji ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,52,'2:52','Kaneka nitwankululuchile panyuma pa yele kuti m''be ŵakutogolela.');
INSERT INTO "silika" VALUES(2,53,'2:53','Soni (kumbuchilani) katema katwampele Musa Chitabu ni chakulekanganya (chilikati cha yakuona ni yaunami) kuti m''be ŵakongoka.');
INSERT INTO "silika" VALUES(2,54,'2:54','Soni (kumbuchilani) katema kajwaŵechete Musa kwa ŵandu ŵakwe kuti: “E ŵandu ŵangu! Chisimu chene ŵanganyammwe n''ditendele lupuso mwachimisyene paakatenda kwenu kan''gombe (kuŵa kannungu kenu), sano tendani toba kwa Akun''gumba ŵenu soni ulaganani mwachimisyenepe, yalakweyo niyayili yambone kukwenu pameso pa Akun''gumba ŵenu.” Kaneka niŵapochele toba jenu, chisimu Jwalakwe ni Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,55,'2:55','Soni (kumbuchilani) katema kamwatite: “E mmwe Musa! Nganituŵa tunkulupilile mmwe mpaka tukam''bone Allah mesonkulola.” Nicham''buleje chikokomo cha moto (wa njasi), kutendaga jenumanja n''di nkulola.');
INSERT INTO "silika" VALUES(2,56,'2:56','Kaneka twansyusisye panyuma pa kuwa kwenu kuti m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(2,57,'2:57','Soni twam''bunichile ni m''bulili wa liwunde nikuntuluchisya manna ni salwa, (nikuntenda kuti): “Lyagani mu yambone yatumpeleyi.” Ninganatutenda lupuso Uwwe, nambo achimisyene ni ŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(2,58,'2:58','Soni (kumbuchilani) katema katwatite: “Jinjilani awu musiwu, ni n’dyeje mwalakwemo palipose pansachile mwagopoka, soni jinjililani pannango chiinamile ni m''becheteje kuti: ‘Tukululuchilani ileŵi yetu.’ Uwwe chitunkululuchile ileŵi yenuyo ni kwajonjechesya (yambone) ŵakolosya.”');
INSERT INTO "silika" VALUES(2,59,'2:59','Nambo ŵaŵalitesile lupuso ŵala ŵagalawisye (lilamusi lila mwanti ŵajinjile mwakokona, nikuŵecheta) maloŵe gane gangaŵaga gaŵasalilidwe gala, sano ni twatuluchisye ŵaalitenda lupuso ŵala ipotesi kutyochela kwinani ligongo lya kunyosya kwao.');
INSERT INTO "silika" VALUES(2,60,'2:60','Soni (kumbuchilani) katema kajwaŵendele Musa ŵandu ŵakwe mesi, nitwantesile kuti: “Putasyani simbo jenujo peganga.” Basi nisyaumile mwalakwemo sulo likumi kwisa siŵili, mwanti lukosyo lulilose lwamanyilile pakumwela pakwe. (Twasalile kuti): “Lyagani soni mwagani mu lisiki lyampele Allah, nambo nkalapwaga pa chilambo pakuwatanganya.');
INSERT INTO "silika" VALUES(2,61,'2:61','Soni (kumbuchilani) katema kamwatite: “E mmwe Musa! Ngatukombola kupililila pa yakulya impepe! Sano tuŵendelani kwa Ambuje ŵenu (Allah) kuti atukopochesye yaikusamesyaga litaka, kutandila ya mboga-mboga yakwe (mpela ngunde, mbwanda, ikuya ni sugama), kwisa soni masakaka gakwe, adiyo jwakwe, dengu jwakwe (chana) ni anyesi jwakwe. Jwalakwe jwatite: “Ana nkutindanya yangalondeka ni yambone? Tuluchilani ku chimisi, basi chisimu chinkapateje yam''bendileyo.” Mwanti kwaŵichidwe pa jamanjao kunyosyeka ni kulaga (kwangalekangana nako), niŵaŵajilwe kupata gasabu ja kuumila kwa Allah, yalakweyo ligongo lyanti ŵanganyao ŵaliji nkukanila ma Ȃya ga Allah, nikuŵaga soni ali nkuulaga Achimitume mwangali ligongo. Basi yeleyo ni ligongo lya kunyosya kwao soni ŵaliji ali nkusumba mipika.');
INSERT INTO "silika" VALUES(2,62,'2:62','Chisimu aŵala ŵakulupilile (mitume jakala) ni ŵali mu Chiyuda, Akilisito ni Asabayi; jwalijose mwa jamanjao juchakulupilile mwa Allah ni Lisiku Lyambesi (pakunda Usilamu), ni kutendaga yambone (mu Usilamu mwaomo), nikuti akwete kupata malipilo gao kwa M’mbuje gwao, soni ngasaja kuŵa nioga, ngasaja kuŵa soni ŵakudandaula .');
INSERT INTO "silika" VALUES(2,63,'2:63','Soni (kumbuchilani) katema katwajigele chilanga chenu, ni twalinyakwile litumbi pachanya penu (nikuntenda kuti): “Kamulisyani yatumpeleyi mwamachili, soni ikumbuchilani yaili mwalakwemu kuti m''be ni woga (wakun''jogopa Allah).');
INSERT INTO "silika" VALUES(2,64,'2:64','Nambo panyuma pa yalakweyo mwagalawiche (ni kusyulula), ukaŵaga umbone wa Allah pa jamanjammwe ni chanasa Chakwe nkaliji mwa ŵakwasika.');
INSERT INTO "silika" VALUES(2,65,'2:65','Soni mmasile kwamanyilila chenene aŵala ŵana kusumbaga mpika mwa jamanja pa (kasa uchimbichimbi wa lisiku) Lyakuŵeluka, basi ni twasalile kuti: “Syukani majani gakunyosyeka.”');
INSERT INTO "silika" VALUES(2,66,'2:66','Ni twakutesile (kusyuka majani kwaoko) kuŵa chitetelo cha kwatetela ŵaŵaliji pajele ndemajo ni ŵapanyuma pao, ni chamuko kwa ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(2,67,'2:67','Soni (kumbuchilani) katema kajwaŵechete Musa kwa ŵandu ŵakwe kuti: “Allah chisimu chene akunnamula kuti nsikite ng''ombe.” Ŵanganyao ŵatite: “Ana nkututenda chipongwe?” Jwalakwe jwatite: “Ngulijuya mwa Allah ya kuŵa mwa (ŵandu) ajinga (ŵakutenda chanache pa dini).”');
INSERT INTO "silika" VALUES(2,68,'2:68','Ŵanganyao ŵatite: “Tuŵendelani kwa Ambuje ŵenu kuti atulondechesye ya ng''ombe jakwejo.” Jwalakwe jwatite: “Chisimu Jwalakwe akuŵecheta yanti chisimu jalakwejo ni ng''ombe janganijiŵa jekulungwa nnope kapena kamwana, nambo jachilikati cha siŵilisi, basi tendani yannamulidwe.”');
INSERT INTO "silika" VALUES(2,69,'2:69','Ŵanganyao ŵatite: “Tuŵendelani kwa Ambuje ŵenu kuti atulondechesye ya ntundu wakwe.” Jwalakwe jwatite: “Chisimu Jwalakwe akuŵecheta yanti chisimu jalakwejo ni ng''ombe jam''bilichila wakucha, jakwasengwasya ŵajilola.”');
INSERT INTO "silika" VALUES(2,70,'2:70','Ŵanganyao ŵatite: “Tuŵendelani kwa Ambuje ŵenu kuti atulondechesye ya ng''ombe jakwejo chenene, chisimu ng''ombe (syatulondechesyesyo) siŵele syakulandana-landana kukwetu, sonitu uwwe chisimu chene naga Allah akusaka, chitujongoleledwe (ku jalakwejo mpaka chitujipate).”');
INSERT INTO "silika" VALUES(2,71,'2:71','(Musa) jwatite: “Chisimu Jwalakwe akuŵecheta yanti chisimu jalakwejo ni ng''ombe jangajijiganya masengo gaguguda litaka, soni jangajitila mbeju, jambonembonepe mwanti jangali liŵala.” Ŵanganyao ŵatite: “Sambano nim''biche nago maloŵe gakuona-onape.” Basi niŵajisikite (mwalikanganichisya), mwanti ŵasigele panandi kuti akatenda.');
INSERT INTO "silika" VALUES(2,72,'2:72','Soni (kumbuchilani) katema kamwauleje mundu, basi nimwaliji nkututilana mu yalakweyo, (ŵane kutiji: “Chenono niŵauleje,” ŵane kutiji: “Nambo chenono),” Allah ni nkumanyukula yamwaliji nkusisa.');
INSERT INTO "silika" VALUES(2,73,'2:73','Basi nitwatite: “Munnyesyani (jwakuwajo) ni chipande chine chang''ombejo (ni chasyuche nikunnanjila nkuulaga).” Iyyoyopeyo (yaŵatite pakunsyusya jwele) nimutachisyuchisya ŵawe (pa Kiyama), ni akunnosyaga imanyilo Yakwe kuti m''be ni lunda.');
INSERT INTO "silika" VALUES(2,74,'2:74','Kaneka mitima jenu panyuma pa yalakweyo ni jajumwile kuti gwa! Ni kuŵa mpela liganga kapena kumulisya nnope (kulipunda ligangalyo). Chisimu chene pana maganga gane sulo sikasatulukaga kuumila mwalakwemo, pana gane soni gaganjikaga nikuumagamo mesi, ni pana gane soni gakugwaga ligongo lya kun''jogopa Allah, sanotu Allah nganaŵa jwanti ni ngaimanya yankutenda.');
INSERT INTO "silika" VALUES(2,75,'2:75','Ana (jenumanja Asilamu) nkukolaga tama janti ni kunkulupilila ŵanganyao, kutendaga likuga line mwa jamanjao lyaliji nkupikanila maloŵe ga Allah, kaneka nikugapitikusya panyuma pagamanya chenene aku achimanyililaga (kuti akulemwa)?');
INSERT INTO "silika" VALUES(2,76,'2:76','Soni naga asimene ni aŵala ŵaakulupilile, akasatiji: “Tukulupilile (yanti Muhammadi صلى الله عليه وسلم Ntume nsyesyene soni jukolanjidwe mu itabu yetu.)” Nambo pati atemi achimisyenepe pakasyepela akasatiji: “Ana nkwatagulila ŵanganyao yammanyisye Allah (mu Taurat) kuti akan''gomeche nayo kwa M''mbuje gwenu, ana ŵangali lunda?”');
INSERT INTO "silika" VALUES(2,77,'2:77','Ana ngakumanyilila yanti Allah jukuimanya yaakusisa ni yaakulosya?');
INSERT INTO "silika" VALUES(2,78,'2:78','Soni mwa jamanjao mwana ajinga ŵangachimanya chitabu nambo kusepelela unami, niŵangali ine ikaŵeje liganichisya basi.');
INSERT INTO "silika" VALUES(2,79,'2:79','Basi ipotesi takaipate aŵala ŵaakulemba chitabu nimakono gao, kaneka ni akutiji: “Achichi chityochele kwa Allah.” (Akuŵecheta wele unamiu) kuti apate (ya duniya) yantengo wannono pakusumanya ni chalakwecho, basi ipotesi niyao ligongo lya yagalembile makono gao, soni ipotesi niyao ligongo lya yaakupatayo.');
INSERT INTO "silika" VALUES(2,80,'2:80','Soni (Ayudao) akasatiji: “Moto ngauja kutukwaya ikaŵeje moŵa gakuŵalanjika pe.” Jilani: “Ana mwatekulangana najo Allah mwanti Allah ningaŵa nkasa chilanga Chakwe, kapena n''gamba kum''bechetela Allah ya ngankuimanyilila?”');
INSERT INTO "silika" VALUES(2,81,'2:81','Elo! Jwaapanganyisye chakusakala, soni nikunsyungula ilemwa yakwe, basi ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,82,'2:82','Sano aŵala ŵaakulupilile nikutendaga yambone, ŵanganyao niŵandu ŵa ku Mbepo, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,83,'2:83','Soni (kumbuchilani) katema katwajigele chilanga chaŵanache ŵa Israila chanti: “Ngamun''galagatilaga jwalijose ikaŵeje Allah, ni mwatendeleje yambone achinangolo, ŵapaulongo, ŵawina ni masikini, soni m''becheteje ni ŵandu yambone chenene, soni jimikani Swala mwakolosya nikutolaga Zaka.” Kaneka mwagalawiche (nikuchitundumalila chilangachi) ikaŵeje ŵannono mwa jenumanja, aku nchisyululaga.');
INSERT INTO "silika" VALUES(2,84,'2:84','Soni (kumbuchilani) katema katwajigele chilanga chenu chanti: “Ngansulusyaga myasi jenu, soni ngankoposyanaga mwachimisyenepe mmisi jenu.” Sano jenumanja ni mwakundile aku nchitendelaga umboni (kuti chilangacho chakuona nambo nganinchikuya).');
INSERT INTO "silika" VALUES(2,85,'2:85','Yeti jemanjapemmwe niŵankuwulagana soni mwachimisyenepe, nikulikoposyaga likuga line mwa jamanja mmisi jao, nkukamusyangana (ni amagongo) pa (kwatendela yangalumbana) ŵanganyao mwasambi ni mwakusumba mpika. Nambo naga mateeka (akayidi ŵakamulwa kungondo) ganyyichilile nkugaombolaga, kutendaga ili haraam kukwenu kwakoposya ŵanganyao. Ana nkukulupilila mbali jine ja chitabu, mbali jine nikujikana? Basi pangali malipilo gane kwa nkutenda yalakwei, ni ikaŵeje kwaluka pa umi wa duniya, soni lisiku lya Kiyama chakapelechedwe ku ilagasyo yakupoteka nnope, sanotu Allah nganaŵa jwanti ni ngaimanya yankutenda.');
INSERT INTO "silika" VALUES(2,86,'2:86','Jamanjao niaŵalaŵala ŵasagwile umi wa duniya pakusumanya ni Akhera, niligongo lyakwe ilagasyo ngaija kupungusyidwa kwa jamanjao soni ngaja kuŵa ŵaakulupusidwa.');
INSERT INTO "silika" VALUES(2,87,'2:87','Soni yakuonaonape, Musa twampele Chitabu ni twakuichisye panyuma pakwepo achimitenga (ŵane). Soni Isa mwanache jwa Maryam twampele isimosimo yakuonechela ni twan''dimbangenye ni Nsimu Weswela (Jibulilu). Ana ngaŵa ndaŵi ni katema kaŵanyyichililaga ntenga ni yangaikunonyelwa mitima jenu mwalikwesyaga? Mwanti likuga line (lya achimitenga) mwalikaaniile, likuga line soni nkuliulaga.');
INSERT INTO "silika" VALUES(2,88,'2:88','Soni ŵanganyao ŵatite: “Mitima jetu jeunichilidwe (kwanti ikulepeleka kwinjila yankulalichila mmwe Muhammadi صلى الله عليه وسلم).” (Yeletu yaunami) nambo Allah ŵalwesile ŵanganyao ligongo lyaukafili wao mwanti yannonope ni yaakukulupilila.');
INSERT INTO "silika" VALUES(2,89,'2:89','Soni ndema jachaichilile chitabu chakutyochela kwa Allah chakwitichisya (itabu) yalinayo (ŵachikanile), kutendaga kala (nkanaiche Muhammadi صلى الله عليه وسلم ni Qur''an) ŵaliji nkuŵenda chikulupusyo (kwa Allah kupitila mu kwika kwa jwele Ntume jwachachakulupusya) kwa ŵandu makafili. Nambo ndema jayaichilile ya ŵaliji nkuimanyililayo ŵaikanile, basi malweso ga Allah gali pa achimakafili.');
INSERT INTO "silika" VALUES(2,90,'2:90','Kaje kusakala yasagwile pakusumanya ni (ntendele wa) mitima jao, kwakuli kanila kwao yatulwisye Allah ligongo lya litima lyanti Allah antuluchisye ukoto Wakwe jwansachile mwa achikapolo Ŵakwe (jwanganaŵa Myuda), basi ni aŵajilwe kupata gasabu pachanya pa gasabu (jakala). Soni akwete kupata achimakafili ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(2,91,'2:91','Soni naga asalilidwemo kuti: “Kulupililani yatulwisye Allah.” Akasatiji: “Tukulupilila yayatulusyidwe kukwetu.” Ni akukanila yayaiche panyuma pakwe kutendaga yili yakuonaonape yayikwitichisya yalinayo. Jilani: “Nambi uli mwaulagaga Mitume ja Allah kalako, naga mwaliji ŵakulupilila (kusyene)?”');
INSERT INTO "silika" VALUES(2,92,'2:92','Soni (Ayudammwe) pamasile paŵam’bichilile Musa ni ilosyo yakuonechela (palangulangu), kaneka ni mwakatesile kang''ombe (kuŵa kannungu kenu) panyuma pa (kutyoka) jwalakwejo (kuja kwitumbi kuupochela malamusi), ni mwaliji ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,93,'2:93','Soni (kumbuchilani) katema katwajigele chilanga chenu, ni twalinyakwile litumbi pachanya penu, (nikuntenda kuti): “Kamulisyani yatumpeleyi mwamachili soni pikanani.” Ŵanganyao ŵatite: “Tupikene nambo tunyosyisye.” Ni ŵamwesyedwe m’mitima mwao (chinonyelo cha kukagalagatila) kang''ombe ligongo lya ukafili wao. Jilani: “Kaje kusakala yaikunchisya chikulupi chenu, naga n''di ŵakulupilia (kusyene).”');
INSERT INTO "silika" VALUES(2,94,'2:94','Jilani (pakwasalila Ayudao): “Naga nyumba jambesi ja kwa Allah jili jenujenupe pangali ŵandu ŵane, basi chilajilani chiwa naga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(2,95,'2:95','Nambo nganaŵa achilajile ata panandi ligongo lya (yasambi) yagalongwesye makono gao, sanotu Allah ni jwaakwamanya chenene ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,96,'2:96','Sonitu chimwasimane kwene (Ayudao) kuti niŵali ŵakupapambila nnope wumi wa duniya kwapunda ŵandu ŵane, kwapunda soni ŵakuwanganya (Allah ni isanamu), jwalijose mwa jamanjao akasasachililaga ali apedwile wumi welewu wakwana yaka sausande, kutendaga kupedwa kwakwe wumi welewu nganikuŵa kum''bisile kwanaula ni ilagasyo. Sanotu Allah ni Jwakuilolechesya yaakutenda.');
INSERT INTO "silika" VALUES(2,97,'2:97','Jilani: “Mundu juchaŵe mmagongo jwa Jibulilu (ligongo lyakwika najo Qur''an,) basi (amanyilile kuti) chisimu jwalakwejo ajituluchisye (Qur''anijo) mu ntima mwenu kwa lisosa lya Allah, kwisa kuijitichisya (itabu) yaili paujo pakwe, nikuŵa chongolo ni abali jakusengwasya kwa ŵakulupilila.”');
INSERT INTO "silika" VALUES(2,98,'2:98','Mundu jwali mmagongo jwa Allah, Malaika Gakwe, Mitenga Jakwe, Jibulilu ni Mikailu (basi niyakwe), chisimutu Allah ni Mmagongo jwa makafili.');
INSERT INTO "silika" VALUES(2,99,'2:99','Soni yakuonaonape, twatulwisye kukwenu ma Ȃya gakusalichisya, nipangali ŵaakugakana ikaŵeje ŵakunyosya.');
INSERT INTO "silika" VALUES(2,100,'2:100','Ana nganiiŵa yanti ndaŵi ni katema kaakutaŵanaga chilanga, likuga line mwa jamanjao akasachijasaga? Nambo kuti ŵajinji mwa jamanjao ngakukulupilila.');
INSERT INTO "silika" VALUES(2,101,'2:101','Soni ndema ja jwaichilile Ntenga jwakutyochela kwa Allah jwakwitichisya yalinayo ŵanganyao, likuga lya lyapedwile Chitabu lyaponyisye Chitabu cha Allah kunyuma kwa migongo jao mpela ngakuchimanyilila.');
INSERT INTO "silika" VALUES(2,102,'2:102','Niŵaikuyiye (itabu yausaŵi) yagaliji nkwasoomela mashetani (ni kwalambusyaga kuti wele usaŵiu waliji) mu uchimwene wa Sulaimana, ninganakufuluji Sulaimana nambo mashetani ni gagaliji nkufulu, ngati gajiganyaga ŵandu usaŵi (wagaumanyi kala Sulaimana nkanaiche), ni (usaŵi) wawatulusyidwe kwa malaika gaŵili Haruta ni Maruta ku Babila, nambo (achimalaikao) nganaŵaga ali nkun''jiganya jwalijose ikaŵeje ali ansalile kaje kuti: “Uwwetu tuŵele fitina (jakun''dinga chikulupi chenu), sano nkakufulu.” Ni ŵaliji nkulijiganya kwa ŵaŵilio (chisamya) chakulekanichisya chilikati cha mundu ni ŵankwakwe. Ni nganaŵa ŵanti ni kunnagachisya nayo yalakweyo jwalijose ikaŵeje kwa lisosa lya Allah, niakulijiganyaga yakwajonanga, yangaŵa nkwakamuchisya, soni amasile kumanyilila kuti jwayiche mwa kuusagula (usaŵio), nganakola liunjili lililyose (lya yambone) ku Akhera. Kaje kusakala yasagwile pakusumanya ni (ntendele wa) mitima jao, ingaŵe ŵamanyililaga.');
INSERT INTO "silika" VALUES(2,103,'2:103','Soni ŵanganyao ingaŵe ŵakulupilile ni kuligosa, chisimu chene, malipilo gakuumila kwa Allah gakaliji gambone (kukwao) ingaŵe ŵamanyililaga.');
INSERT INTO "silika" VALUES(2,104,'2:104','E jenumanja ŵankulupilile! Kasintiji (pakunsalila Ntume صلى الله عليه وسلم): “Tuchiingaani.” Nambo n''jileje: “Ntukosoŵalile.” Soni pikanilani (malamusi), mwanti akwete kupata achimakafili ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(2,105,'2:105','Ngaakunonyelwa aŵala ŵanya ŵakanile; kutandila achinsyene itabu (Ayuda ni Akilisito) ni ŵakuwanganya (Allah ni isanamu) kuti intuluchile jenumanja (Asilamummwe) yambone iliyose kutyochela kwa M''mbuje gwenu. Nambotu Allah akunsagulilaga ukoto Wakwe jwansachile, soni Allah ni nsyene umbone wekulungwa.');
INSERT INTO "silika" VALUES(2,106,'2:106','Ngatukufutatu Ȃya jilijose kapena kujiliŵasya, ikaŵeje tukwikanajo jine jambone kupunda jalakwejo kapena jakulandana najo, ana ngankumanyilila kuti Allah pa chindu chilichose aŵele Jwakombola?');
INSERT INTO "silika" VALUES(2,107,'2:107','Ana ngankumanyilila kuti Allah akwete uchimwene wa kumawunde ni petaka? Soni nganinkola jenumanja kaligoselo ata nkulupusyo jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(2,108,'2:108','Kapena nkusaka (Asilamummwe) kuti mum''busyeje Ntenga jwenuصلى الله عليه وسلمmpela yaŵatiji pakuwusyidwa Musa kalakala (iwusyo yachalu)? Soni jwachasagule ukafili pakusumanya ni chikulupi, nikuti amasile mwasokonechele kuleka litala lyagoloka.');
INSERT INTO "silika" VALUES(2,109,'2:109','Ŵajinji mwa achinsyene itabu (Ayuda ni Akilisito) aŵele ali nkusachilila yanti ali am''buchisye (Asilamummwe) ku ukafili panyuma pakulupilila kwenu, ligongo lya litima lyakutyochela kwa ŵanganyao achimisyene, panyuma pakuonechela kwa jamanjao chakuona (jwali Muhammadi صلى الله عليه وسلم), basi mwakululuchilani nikwaleka mpaka Allah ayichenalyo lilamusi Lyakwe, chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(2,110,'2:110','Soni jimikani Swala mwakolosya ni ntoleje Zaka, soni mu chambone chilichose chinkulilongochesya chinkachisimane kwa Allah, chisimu Allah ichindu yankutenda aŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(2,111,'2:111','Soni ŵatite: “Ngaŵa nkuja kwinjila ku Mbepo ni ikaŵeje jwali Myuda kapena Nkilisito.” Yele ni isako yao. Jilani: “Ikani nao umboni wenu naga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(2,112,'2:112','(Ngwamba!) Nambo kuti mundu jwachajipeleche ngope jakwe kwa Allah (pakunda Usilamu), ni ali jwakolosya (pa Usilamu wakwe), jwele nijuchakapate malipilo gakwe kwa M''mbujegwe, soni ngasaja kuŵa nioga, ngasaja kuŵa soni ŵakudandaula.');
INSERT INTO "silika" VALUES(2,113,'2:113','Soni Ayuda akasatiji: “Akilisito nganaŵa pa chilichose (ŵangali dini jaakukuya).” Nombe Akilisito akasatiji: “Ayuda nganaŵa pa chilichose (ŵangali dini jaakukuya).” Kutendaga ŵanganyao akusoomaga Chitabu, mmomopemo mwa kulandana nimaloŵe ga ŵanganyao nimwaakuŵechetela ŵandu ŵangali chachimanyi (ŵagalagatila isanamu). Sano Allah ni jwatachijilanya chilikati chao lisiku lya Kiyama pa ichindu yaŵaliji nkutindana pa yalakweyo.');
INSERT INTO "silika" VALUES(2,114,'2:114','Ana soni nduni jwali jwalupuso nnope kumpunda mundu jwaakukanya misikiti ja Allah kuti Lina Lyakwe lisalidweje mwalakwemo, ni akulimbangana pakujitendekasya chisaame (pakwalekasya ŵandu kutendelamo ibada). Ŵanganyao nganikuŵa kwakuŵajilwa kukwao kuti ajijinjile (misikitijo) ikaŵeje mwawoga. Kwaluka ni kwao pa duniya, soni ku Akhera chakapate ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(2,115,'2:115','Soni kungopoko ni kungapililo nsyene jwakwe Allah, sano kulikose kuchin''galauchile (pakuswali naga Allah annamulile), nikuti kwalakweko nikuchili chibula cha Allah, chisimu Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,116,'2:116','Soni akasatiji: “Allah alitendele mwanache.” Kuswejela ni Kwakwe (Allah aŵambalene nayo yele), nambo kuti yosope ya kumawunde ni petaka iŵele Yakwe Jwalakwejo, yosope yene ikwitika Kukwakwe (mwa ukapolo).');
INSERT INTO "silika" VALUES(2,117,'2:117','(Jwalakwe ni) Nkugumba mawunde ni mataka, sano naga asachile kuti chindu chipatikane, basi agambaga kuchilamula kuti: “M''be!” ni chikasaŵaga.');
INSERT INTO "silika" VALUES(2,118,'2:118','Soni ŵandu ŵangali chachimanyi akasatiji: “Ligongo chichi Allah jwangatuŵechetekasya uwwe, kapena kutuichilila chisimosimo?” Mmomopemo mwakulandana nimaloŵe ga ŵanganyao nimwaŵaŵechetele ŵandu ŵakala jamanjao nkanapagwe, mitima jao jilandene, pamasile patusalichisye ma Ȃya kwa ŵandu ŵaakukulupilila kusyesyene.');
INSERT INTO "silika" VALUES(2,119,'2:119','Chisimutu Uwwe tuntumisye (mmwejo Muhammadi صلى الله عليه وسلم) ni yakuona kuti m''be ŵakutagulila abali syakusengwasya ni ŵakutetela, soni ngan''ja kuwusyidwa ya ŵandu ŵa ku Moto Waukali.');
INSERT INTO "silika" VALUES(2,120,'2:120','Soni nganaŵa anchiniile mmwejo Ayuda ni Akilisito mpaka n''di nkuyiye dini syao. Jilani: “Chisimu chongoko cha Allah ni chachili chongoko chisyesyene.” Nambotu naga nkuye yakusaka yao (chachili Chiyuda kapena Chikilisito chaocho) panyuma pa yainyyichilile yaumanyilisiyi (jajili Qur’an), chim''be ŵangali ŵakun''gosa ata ŵakunkulupusya (pa ipotesi yakuumila) kwa Allah.');
INSERT INTO "silika" VALUES(2,121,'2:121','Aŵala ŵatwapele chitabu nikuchisoomaga kusyesyene (mwangali konjechesya kapena kupungula), ŵanganyao niŵaakuchikulupilila (chitabu cha Qur’an), nambo ŵachachikane chele, ŵanganyao niŵachaŵe ŵakwasika.');
INSERT INTO "silika" VALUES(2,122,'2:122','E jenumanja ŵanache ŵa Israila! Kumbuchilani chindimba Changu chanampele, soni nanchimbichisye kupunda iwumbe yosope (yandaŵi jenu).');
INSERT INTO "silika" VALUES(2,123,'2:123','Ni lijogopani lisiku lyangakomboleka mundu kunkamuchisya mundu jwine chilichose, soni ngasichikundidwa kuumila kwa jwalakwejo chaliombolela, atamuno chondechonde (cha akuchondelela) ngasichinkamuchisya, soni ŵanganyao ngasaŵa ŵakulupusidwa.');
INSERT INTO "silika" VALUES(2,124,'2:124','Soni (kumbuchilani) katema kajwalinjidwe Ibrahima ni Ambujegwe pakumpa malamusi, niŵagakwanisye. (Allah) jwatite: “Chisimutu Une chinantende mmwejo kuŵa nnongola jwa ŵandu.” (Ibrahima) jwatite: “Ana ni achiŵanangu ŵakwe?” (Allah) jwatite: “(Elo, nambo kuti) chilanga changu ngaŵa nkwapata ŵakulitenda lupuso (mu ŵanache ŵenuo.)”');
INSERT INTO "silika" VALUES(2,125,'2:125','Soni (kumbuchilani) katema katwakutesile ku nyumba (ja Likaaba) kuŵa kwakuja kwendelajendela ŵandu nikwantunjelele, soni patendani pamalo paŵaliji nkwima Ibrahima (ndema jaŵataŵaga Likaabalyo) kuŵa pakuswalila. Nitwannamwile Ibrahima ni Ismaila kuti: “Jiswejesyani Nyumba Jangu ligongo lya ŵakujisyungula, ni ŵakutama itikafu ni ŵakwinama ni kusujudu (pakuswali).”');
INSERT INTO "silika" VALUES(2,126,'2:126','Soni (kumbuchilani) katema kajwaŵechete Ibrahima kuti: “E Ambuje ŵangu! Utendani awu Musi (wa Maaka) kuŵa ŵantunjelele, nimwape achinsyeneo isogosi, nambo ŵaakulupililepe mwa jamanjao mwa Allah ni Lisiku Lyambesi.” (Allah) jwatite: “Nijwakufulu jwake, basi chinansengwasye panandi kaneka chinjintutila ku ilagasyo ya ku Moto, kaje kusakala malo gakuujila gakwe!”');
INSERT INTO "silika" VALUES(2,127,'2:127','Soni (kumbuchilani) katema kaŵaliji nkukwesya Ibrahima ni Ismaila chitepo chanyumba (ja Likaaba aku achiŵendaga kuti): “Ambuje ŵetu! Pochelani kutyochela kukwetu (masengo getuga), chisimutu Mmwe ni ŵan''di Ŵakupikanichisya, Ŵakumanyilila nnope.”');
INSERT INTO "silika" VALUES(2,128,'2:128','“Ambuje ŵetu! Tutendani kuŵa ŵakulipeleka Kukwenu, ni mu ŵanache ŵetu (muŵe mwana soni) nkutula wakulipeleka Kukwenu, soni tulosyani (matala ga katende ka) ibada yetu (ya Hija ni Umra), ni ntupochele toba jetu, chisimu Mmwejo ni ŵan''di Ŵakupochela nnope toba, Ŵachanasa channope.”');
INSERT INTO "silika" VALUES(2,129,'2:129','“Ambuje ŵetu! Mwatumichisyani Ntenga jwakutyochela mu ŵanganya peo, kuti ŵasoomeleje ma Ȃya Genu ni kwajiganyaga Chitabu ni lunda lwakusokoka, soni kuti ŵaswejesyeje, chisimutu Mmwe ni ŵan''di Ŵamachili gakupunda, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(2,130,'2:130','Ana nduni jwatatende ngajisaka dini ja Ibrahima (wauli Usilamu) pangaŵaga jwaauwawatisye ntima ŵakwe? Sonitu pamasile patwansagwile (Ibrahimajo) pa duniya, chisimu jwalakwe soni ku Akhera takaŵe mwa ŵandu ŵambone.');
INSERT INTO "silika" VALUES(2,131,'2:131','(Kumbuchilani) katema kaŵasalilidwe ni Ambujegwe (Allah) kuti: “Lipelekani (pakuŵa Nsilamu).” Jwalakwe jwatite: “Nalipeleche kwa M''mbuje jwa iwumbe yosope (mbele Nsilamu.)”');
INSERT INTO "silika" VALUES(2,132,'2:132','Soni Ibrahima ŵaagombelechesye yalakwei (yakuya dini ja Usilamu) ŵanache ŵakwe, nombe Yaakubu (jwaagombelechesye ŵanache ŵakwe kuti): “E achiŵanangu! Chisimu Allah ansagulile Dini, ningasim''bwa ikaŵeje n''di Asilamu.”');
INSERT INTO "silika" VALUES(2,133,'2:133','Ana jenumanja mwapali ndema jachanyyichilile Yaakubu chiwa? Katema kaŵawusisye ŵanache ŵakwe kuti: “Ana chin''galagatileje chichi panyuma pangu?” Ŵanganyao ŵatite: “Tugalagatileje N''nungu jwenu, (jujojopejo) ni N''nungu jwa achinangolo ŵenu; Ibrahima, Ismaila ni Isihaka, N''nungu Jumpepe. Sonitu uwwe tukulipeleka Kukwakwe (pakuŵa Asilamu).”');
INSERT INTO "silika" VALUES(2,134,'2:134','Walakweo ni nkutula wawapite kala, chiuchipata yawapanganyisye, nombe ŵanganyammwe tinchipata yampanganyisye, soni ngasin''ja kuwusyidwa mu ichindu yaŵaliji nkutenda ŵanganyao.');
INSERT INTO "silika" VALUES(2,135,'2:135','Soni (Ayuda ni Akilisito) akasatiji (pakwasalila Asilamu): “Naga kuti n''jongoche nim''be Ayuda kapena Akilisito.” Jilani: “Nambo (uwwe tukuya) dini ja Ibrahima jwakongoka (jwa ŵalekangene nasyo dini syepombotale), soni nganaŵa mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(2,136,'2:136','Ŵechetani (jenumanja Asilamu pakwasalila Ayuda ni Akilisito) kuti: “Uwwe tukulupilile mwa Allah, ni yaitulusyidwe kukwetu (jajili Qur’an), ni yayatulusyidwe kwa Ibrahima, Ismaila, Isihaka, Yaakubu ni isukulu yakwe, ni yaŵapedwile Musa ni Isa, ni yaŵapedwile Achimitume kuumila kwa M’mbuje gwao, ngatukulekanganya chilikati cha jumpepe mwa ŵanganyao, soni uwwe Kukwakwe tukulipeleka (pakuŵa Asilamu).”');
INSERT INTO "silika" VALUES(2,137,'2:137','Sano naga ŵanganyao akulupilile yantite paakulupilila jenumanja, nikuti chajongoche, nambo naga akugalauka (kuleka chele chikulupichi), nikuti ŵanganyao agambile kuŵa mu ukangani, basi Allah chan''gose jenumanja (ku yakusakala yakutyochela) kwa ŵanganyao, soni Jwalakwejo ni Jwakupikanichisya Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,138,'2:138','(Kuyani) dini ja Allah (wauli Usilamu), ana niŵani ŵakwete dini jambone kupunda ja Allah? Sonitu uwwe Jwelepejo nijwatukun''galagatila.');
INSERT INTO "silika" VALUES(2,139,'2:139','Jilani (ku Ayuda ni Akilisito): “Ana nkusisyana nowe pa ya Allah kutendaga Jwalakwejo ni M''mbuje gwetu soni M''mbuje gwenu? Soni uwwe tukwete yakutenda yetu, nombe jenumanja nkwete yakutenda yenu, soni uwwe (yosope yatukutendaga) tukunswejechesyaga Jwalakwe.”');
INSERT INTO "silika" VALUES(2,140,'2:140','Kapena (jenumanja Ayuda ni Akilisito) nkuŵechetaga kuti: “Kutandila Ibrahima, Ismaila, Isihaka, Yaakubu ni isukulu yakwe, ŵaliji Ayuda kapena Akilisito?” Jilani: “Ana ŵanganyammwe ni ŵan''di ŵakumanyilila nnope kapena Allah (jwaakuti ŵanyao ŵaliji Asilamu)?” Ana nduni jwali jwalupuso nnope kumpunda mundu jwaakusisa umboni wakwete wakutyochela kwa Allah! Sanotu Allah nganaŵa jwanti ni ngaimanya yankutenda.');
INSERT INTO "silika" VALUES(2,141,'2:141','Walakweo ni nkutula wawapite kala, chiuchipata yawapanganyisye, nombe ŵanganyammwe tinchipata yampanganyisye, soni ngasin''ja kuwusyidwa mu ichindu yaŵaliji nkutenda ŵanganyao.');
INSERT INTO "silika" VALUES(2,142,'2:142','Pangakaŵapa ŵawawate mwa ŵanduŵa chaŵeje alinkuŵecheta kuti: “Ana nichichi chachagalawisye (Asilamu) kuleka ku chibula kwao kuŵaliji nkulola (pakuswali)?” Jilani: “Kungopoko ni kundiŵilo nsyene jwakwe Allah, akasan''jongolela jwaakunsaka kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(2,143,'2:143','Soni iyyoyopeyo tuntesile ŵanganyammwe kuŵa nkutula wachilikati kuti m''be mboni pa ŵandu, nombenajo Ntenga (Muhammadi صلى الله عليه وسلم) aŵe mboni pa jamanja, ninganitukutenda ku chibula (cha Baitul Muqaddas) kwamwaliji nkulola, ikaŵeje kuti tummanyukule mundu jwaakunkuya Ntenga pakunnekanganya nimundu jwaakugalauchila ku indende yakwe, soni chisimu chene (yagalausya chibulayo) yaliji yakusausya kwekulungwa nnope ikaŵeje kwa ŵandu ŵaŵajongwele Allah, ninganaŵa Allah jwanti nikonanga chikulupi chenu (pa swala syenu sim''bele n''di nkuswali pakulola ku Baitul Muqaddas), chisimu Allah kwa ŵandu ni Mpole kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,144,'2:144','Pamasile patukuweni galausyagalausya kwa ngope jenu (mmwe Muhammadi صلى الله عليه وسلم kulola) kwinani, sano chitun''galauchisye ku chibula chankuchisakacho, mwanti sano galauchisyani ngope jenu upande wa Nsikiti Wakuchimbichika (wa Maaka), soni palipose pachim''be ŵanganyammwe galauchisyani ngope syenu ku wele upandeo, soni chisimu chene aŵala ŵaŵapedwile Chitabu akumanyilila yanti yalakweyi yakuonaonape yakuumila kwa M’mbuje gwao, sano Allah nganaŵa jwanti ni ngaimanya yaakutenda ŵanganyao.');
INSERT INTO "silika" VALUES(2,145,'2:145','Naatamuno m’biche nacho kwa aŵala ŵaŵapedwile chitabu chilosyo chantundu uliose nganaŵa akuyiye chibula chenu, nombe mmwe nganim''ba nkuyiye chibula chao, soni atamose ŵane mwa jamanjao nganaŵa akuyiye chibula chaŵane, nambo naga nkuye yaakwisaka ŵanganyao panyuma pa umanyilisi wawunyyichilileu, chisimu mmwe chim''be pa jele ndemajo mwa ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,146,'2:146','Ŵandu ŵatwapele chitabu akuimanyilila yalakweyi yaakuti pakwamanyilila ŵanache ŵao, nambo chisimu likuga line mwa jamanjao likasasisaga yakuona lichiimanyililaga.');
INSERT INTO "silika" VALUES(2,147,'2:147','(Yalakweyi ni) yakuona yakuumila kwa Ambuje ŵenu, basi ngasim''ba ata panandi mwa ŵakaichila.');
INSERT INTO "silika" VALUES(2,148,'2:148','Soni uliose (mwa mikutula jakala) wakwete upande wakwe wawalolaga (pakuswali), basi ijanguilani yambone, palipose pachim''be Allah tachiika namwe ŵanaose (lisiku lya Kiyama), chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(2,149,'2:149','Soni kulikose kumpite basi galauchisyani ngope jenu ku Nsikiti Wakuchimbichika, soni chisimu yalakweyi niyakuona yakuumila kwa Ambuje ŵenu, sano Allah nganaŵa jwanti ni ngaimanya yankutenda ŵanganyammwe.');
INSERT INTO "silika" VALUES(2,150,'2:150','Soni kulikose kumpite (mmwe Muhammadi صلى الله عليه وسلم) basi galauchisyani ngope jenu ku Nsikiti Wakuchimbichika, nombe palipose pachim''be ŵanganyammwe basi galauchisyani ngope syenu ku upandeo, kuti ŵandu akasakola maloŵe gakutataŵana namwe, ikaŵeje ŵawowopeo ŵalitendele lupuso mwa jamanjao, basi ngasimwajogopaga ŵanganyao nambo munjogopeje Une, ni kuti ngwanilisye chindimba Changu pa jenumanja kuti n''jongoche.');
INSERT INTO "silika" VALUES(2,151,'2:151','Mpela mutuntumichisye kukwenu jenumanja Ntenga jwakutyochela mwa ŵanganyapemmwe jwaakunsoomela ma Ȃya Getu, nikunswejesyaga, ni akun''jiganyaga Chitabu ni lunda lwakusokoka, ni akun''jiganyaga soni yamwalekaga ngaimanya.');
INSERT INTO "silika" VALUES(2,152,'2:152','Basi mungumbuchilani, None chinankumbuchile, soni mundogolelani, ni nkanganaga.');
INSERT INTO "silika" VALUES(2,153,'2:153','E jenumanja ŵankulupilile! Likamuchisyani (naga yimpatile ipato) pakupilila ni kuswali, chisimu Allah ali pamo ni akupilila.');
INSERT INTO "silika" VALUES(2,154,'2:154','Ni ngasim''bechetaga pa kwamba ya ŵandu ŵaakuwulajidwa petala lya Allah kuti: “Ŵawe.” Nambo ŵachijumi gamba kuti ngankupata kumanyilila.');
INSERT INTO "silika" VALUES(2,155,'2:155','Soni chitun''dinje ni chindu chakogoya (mpela ngondo), kwisa soni sala, kunandipa kwa ipanje, kwasika kwa maumi ni konasika kwa isogosi, ni mwatagulilani ŵakupilila abali jakusengwasya.');
INSERT INTO "silika" VALUES(2,156,'2:156','Aŵala ŵanti naga ipato yapatilemo akasatiji: “Chisimu uwwe ŵa Allah, soni chisimu uwwe Kukwakwe tuuchiujila.”');
INSERT INTO "silika" VALUES(2,157,'2:157','Ŵanganyao ukoto wakuumila kwa M’mbuje gwao uli pa jamanjao ni chanasa, soni ŵanyao niŵali ŵakongoka.');
INSERT INTO "silika" VALUES(2,158,'2:158','Chisimu (matumbi ga) Swafa ni Maruwa gaŵele mu imanyilo ya (Dini ja) Allah, sano jwalijose jwapite kutenda Hija kapena Umra, paŵe pangali ulemwa kwa jwalakwejo kuusyausya kwenda chilikati chakwecho, mwanti jwachapanganye masengo mwa gamba kulipeleka (nikuti tachilipidwa), chisimu Allah ni Jwakutogolela kusyene, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,159,'2:159','Chisimu aŵala ŵaakusisa yatutulwisye yaili ilosyo yakuonechela palangulangu soni yaili chongoko, panyuma panti tuisalichisye kwa ŵandu m’Chitabu, ŵanganyao niŵaakuŵa ali nkulwesedwa ni Allah, soni ni akuŵa ali nkulwesedwa ni akulwesa.');
INSERT INTO "silika" VALUES(2,160,'2:160','Ikaŵeje aŵala ŵatesile toba ni kwilanya (yaŵawatanganyagayo) ni kusalichisya (yaŵasisagayo), basi ŵanganyao ni ŵachinapochele toba jao, soni Une ni Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,161,'2:161','Chisimu aŵala ŵakanile nikwikanila kuwa ali makafili, ŵanganyao malweso ga Allah, Achimalaika ni ŵandu wosope gali pa jamanjao.');
INSERT INTO "silika" VALUES(2,162,'2:162','Mwele (mmalweso ga ku Motogo) chakaŵe ŵandamo, ilagasyo ngaija kupungusyidwa kwa jamanjao, soni ŵanyao ngaaja kupedwa lipesa.');
INSERT INTO "silika" VALUES(2,163,'2:163','Soni Nnungu jwenu ni Nnungu Jumpepe basi, pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Jwaukoto wejinji, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,164,'2:164','Chisimu chene mu kagumbidwe ka mawunde ni mataka, ni kupisyangana kwa chilo ni muusi, ni yombo yekulungwakulungwa yaikwenda m''mbwani ili ipachile ichindu yakwakamuchisya ŵandu (nambo ngamilwa), ni mesi gaatulwisye Allah kuumila kwinani nikusyuchisya nago litaka panyuma pakuwa kwakwe, ni kwenesya palakwepo mitundu josope ja inyama, ni kujigalausyagalausya mbungo ni mawunde gawula gagakwendesyedwa chilikati cha kwinani ni pasi, muŵele mwana ilosyo kwa ŵandu ŵakwete lunda.');
INSERT INTO "silika" VALUES(2,165,'2:165','Soni chilikati cha ŵandu pana ŵaakulitendelaga (isanamu kuŵa) milungu (jao) kunneka Allah, ni akwinonyelaga yaakatite pakunnonyela Allah, nambo ŵandu ŵakulupilile niŵali ŵakunnonyela Allah mwakupelenganya. Mwanti ŵandu ŵalitendele lupusowo angajione ndema jachakaioneje ilagasyo (akaiweni yakogoya kusyene), ligongo machili gosope gachiŵa ga Allah, soni Allah ni Jwaukali pakupeleka ilagasyo.');
INSERT INTO "silika" VALUES(2,166,'2:166','(Mwakumbusyani) katema katachiliipaata achinnongola ŵaŵaliji nkukuyidwa (mu ukafili) pakwakana ŵaŵaliji nkukuya, aku ali ayiweni ilagasyo, mwanti nkamulano wa chilikati chao uchikatika.');
INSERT INTO "silika" VALUES(2,167,'2:167','Nitachiŵecheta akukuya kuti: “Kungaŵe kwakomboleka uwwe kuujila (ku duniya) tungajile kuliipaata pakwakana ŵanganyao mpela yatite pakuliipaata pakutukana uwwe.” Iyyoyopeyo niichakajileje Allah pakwalosya ŵanganyao itendo yao kuŵa madandausi gao, soni ŵanganyao ngasaŵa ŵakopoka ku Moto.');
INSERT INTO "silika" VALUES(2,168,'2:168','E jenumanja achinaŵandu! Mu ichindu yaili pa chilambo lyagani ya halaal peyo yayili yambone, soni ngasinkuyaga sajo sya shetani, chisimu jwalakwe kukwenu aŵele mmagongo jwakuonechela.');
INSERT INTO "silika" VALUES(2,169,'2:169','Chisimu jwalakwejo akunchisya yangalumbana ni yakunyalaya, soni nikuti munnambuchisyeje Allah yangankuimanyilila.');
INSERT INTO "silika" VALUES(2,170,'2:170','Soni naga ili iŵechetedwe kukwao (yanti): “Kuyani yaatulwisye Allah.” Akasatiji: “Nambo tukuya yatwasimene nayo achatati ŵetu.” Ana nantimuno kuti achatati ŵawowo nganakola umanyilisi wa chilichose soni nganaŵa ŵakongoka (chiŵakuyejepe?)');
INSERT INTO "silika" VALUES(2,171,'2:171','Soni chilandanyo cha achimakafili chili chisawu (m''busa) jwaakuigumilila (ilango yakwe) mwa kuikalipila, kutendaga yalakwe ikaakupikana ikaŵeje kuŵilangape ni gumila, ŵangapikana, ŵangaŵecheta soni ŵangalola, mwanti ŵangali umanyilisi.');
INSERT INTO "silika" VALUES(2,172,'2:172','E jenumanja ŵankulupilile! Lyagani mu yambone yatumpele, soni muntogolelani Allah nam’baga jenumanja nkun''galagatila Jikape Jwele.');
INSERT INTO "silika" VALUES(2,173,'2:173','Chisimu (Allah) ayitesile haraam kukwenu (kulya) chibudu, myasi, nyama jeguluŵe ni chachisikitidwe pakolanjidwa (lina linepe) lyangaŵaga lya Allah (kuŵa mbepesi ja akumasoka), nambo jwachasingalilwe (nikulya cha haraamucho) mwangali kuchilumba kapena kusumba mpika, basi chipaŵe pangali ulemwa kukwakwe, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,174,'2:174','Chisimu aŵala ŵaakusisa yaŵatulwisye Allah m’Chitabu (cha Baibulo) nikuisumanyaga ni (ya duniya) yantengo ŵannono, ŵanganyao ngakulya chinepe m’matumbo mwao changaŵaga moto, soni Allah ngaaja kwaŵechetekasya lisiku lya Kiyama, soni ngaaja kwaswejesya, soni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(2,175,'2:175','Jamanjao niaŵalaŵala ŵasagwile kusokonechela pakusumanya ni chongoko, ni (kusagula soni) ilagasyo pakusumanya ni chikululuko, ana nichichi chikwatendekasya kupilila ni Moto?');
INSERT INTO "silika" VALUES(2,176,'2:176','Yalakweyo niligongo lyanti Allah atulwisye Chitabu mwakuonaonape, nambo ŵandu ŵatindene m’Chitabumo ali mu ukangani wakutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(2,177,'2:177','Umbone nganiuŵa pagalauchisya ngope syenu upande wa kungopoko ni kundiŵilo, nambo umbone (usyesyene) ni wa mundu jwankulupilile Allah, Lisiku Lyambesi, Malaika, Chitabu ni Achimitume, achipelekaga soni chipanje achichisakaga kwa ŵapaulongo, ŵawina, masikini, jwapaulendo, ŵakuŵenda, ni kwaapela ulukosyo achikapolo, achiswaliji soni Swala nikutolaga Zaka, ni ŵakwanilisya ilanga yao pati ataŵenemo, ni ŵakupilila pakulaga, pakulwala ni pandema jangondo, ŵanganyao niŵaasimichisye (Usilamu wao), soni ŵanyao niŵali ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(2,178,'2:178','E jenumanja ŵankulupilile! Silamulidwe kukwenu mbusyo syakulandana pa akuulajidwa, nnukosyo kwa nnukosyo, kapolo kwa kapolo, jwankongwe kwa jwankongwe, nambo (nkuulaga) jwakululuchilidwe ni nchimwenegwe (nkuulajidwa) pa chindu chinechakwe (mpela kuti apeleche mbiya mmalo mwakuulajidwa) nikuti (nkukululukajo) asikuyekuye (ni kuŵilasya) mwa umbone, nombenajo (nkuulagajo) alipile (mbiyasyo) kukwakwe mwa umbone, kwalakweko ni kwepepasya kwakutyochele kwa M''mbuje gwenu soni (chiŵele) chanasa, sano juchasumbe mpika panyuma pa yele, nikuti akwete kupata ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(2,179,'2:179','Soni nkwete jenumanja (kusunga) umi mu mbusyo syakulandana E jenumanja ŵankwete lunda! Kuti n''digose (ku nteto wakuulagana).');
INSERT INTO "silika" VALUES(2,180,'2:180','Kulamulidwe kukwenu pati chiwa chili chim’bichilile jumo jwenu naga achilekaga chipanje; kuleka wasiya (pa chipanjecho) waachinangolo ŵaŵili ni ŵapaulongo mwa kwilanila (ni malamusi ga Dini), lyele liŵele lilamusi kwa ŵaoga (ŵakun''jogopa Allah). ');
INSERT INTO "silika" VALUES(2,181,'2:181','Sano jwachaligalausye (lilamusili) panyuma pakulipikana; nikuti ulemwa wakwe chiuŵe pa ŵaakuligalausyao, chisimu Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,182,'2:182','Sano mundu jwan''jogopele nkuleka wasiyajo kuti atakwika mwapendechele ku upande wepombotale kapena ku ulemwa nikwakolochesya chilikati chao, chaŵe jwangali ulemwa, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,183,'2:183','E jenumanja ŵankulupilile! Kulamulidwe kukwenu kutaŵa (Namasani) mpela yakwatite pakulamulidwa kwa ŵandu ŵaŵaliji paujo penu (jenumanja nkanim’biche) kuti n''digose (ku ilemwa).');
INSERT INTO "silika" VALUES(2,184,'2:184','Moŵa gakuŵalanjikape (29 kapena 30), nambo jwachaŵe nkulwala mwa jaomanja kapena pa ulendo (nigopola) akwanisye chiŵalanjilochi ni moŵa gane (ga kuuchisya), sano ŵandu ŵakutaŵa mwakulajilila (ligongo lyauchekulu kapena ulwele wan''gona-n''gona), chakomboleche (kuleka kutaŵako ni) kupeleka malipilo gayakulya yakwanila masikini (jumo pa lisiku lililyose lyakataŵile), sano jwachapanganye masengo mwagamba kulipeleka yele tiiŵe yambone kukwakwe, nambo naga nkutaŵa (iyyoyo mwakulajilila) tiiŵe yambone kukwenu naga n''di ŵakumanyilila (kusosekwa kwakutaŵako).');
INSERT INTO "silika" VALUES(2,185,'2:185','Mwesi wa Namasani ni awula wajatulusyidwe Qur’an nkati mwakwe kuŵa chongolo cha ŵandu ni ilosyo ya kuonechela palangulangu ya chongoko, soni kuŵa chilekanganyo (cha yakuona ni yaunami), sano jwauweni mwa jaomanja mwesi (ali chitamile pamusi) ataŵe Namasanijo, nambo jwachaŵe nkulwala kapena paulendo (nigopola), akwanisye chiŵalanjilo ni moŵa gane (ga kuuchisya), Allah jukunsoselaga yakwepepala ŵanganyammwe soni jwangansosela yakunonopa, soni (jukusaka) kuti nkwanisye chiŵalanjilocho ni kunkusyaga Allah (pakutenda Takbirah) ligongo lya kun''jongola kwenu soni kuti m''be ŵakutogolela. ');
INSERT INTO "silika" VALUES(2,186,'2:186','Soni naga ali am''busisye achikapolo Ŵangu Yangune, basi (mwasalilani yanti): Chisimu Une ndili pakuŵandika, ngwangaga duwa jankuomba pati amombile, basi anjitiche (kuŵilanga Kwangu) ni angulupilile kuti ajongoche.');
INSERT INTO "silika" VALUES(2,187,'2:187','Kuŵele kwakundisyidwa kukwenu chilo chakwimuchila kutaŵa kutendanao ulombela achimmasyeto ŵenu, ŵanganyao ni iwaalo yenu nombe ŵanganyammwe ni iwaalo yao, Allah amanyiiliile kuti ŵanganyammwe mwaliji nkuliijiiŵa mwachimisyene, basi apocheele toba jenu ni ankululuchiile, sambano mwakwayagani ni nsosejesoseje yannembele Allah (ŵali ŵanache), soni lyagani ni kumwaga mpaka umanyukuche kukwenu usi weswela kutyochela mu usi wepiliwu kumasikusiku (kwakuli kombola kulekanganya chilikati cha kuswejela kwa kumasikusiku ni chipi chachilo), kaneko kwanilisyani kutaŵa mpaka chilo (kutiŵila kwa lyuŵa), soni ngasimwakwayaga ŵanganyao kutendaga jenumanja n''di nkutama itikafu m’misikiti, jele ni mipingu ja Allah, basi kasin''jiŵandichilaga, iyyoyopeyo niyakasatiji Allah pakusalichisya ma Ȃya Gakwe kwa ŵandu kuti aŵe nioga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(2,188,'2:188','Soni kasillyaganilaga chipanje chenu mwa ukoleko nikuchipelekaga kwa akulamula kuti mpate kulya liwunjili lya chipanje cha ŵandu mwasambi kutendaga jenumanja nchimanyililaga (kuti ŵa ukoleko).');
INSERT INTO "silika" VALUES(2,189,'2:189','Akum''busya ya myesi, jilani: “Jelejo ni ipiimo ya ndaŵi sya ŵandu (yakumanyililila ndema jakutendela indu yao) ni Hija, sano umbone nganiuŵa pakwinjila kwenu m’majumba kupitila ku chituupa kwakwe, nambo umbone ni wa mundu jwaoga (wakun’jogopa Allah), ni gajinjilagani majumba kupitila m’milango mwakwe, soni mun''jogopeje Allah kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(2,190,'2:190','Soni mwaputani ngondo petala lya Allah aŵala ŵaakumputa, nambo ngasinsumba mpika (pakwaputa ŵangantanda), chisimu Allah ngaakwanonyela ŵakusumba mpika.');
INSERT INTO "silika" VALUES(2,191,'2:191','Soni mwaulagani ŵanganyao palipose pachimwasimane, soni mwakoposyani palipose paŵankopwesye, ligongo fitina (ja kwawatanganyichisya ŵandu dini jao) jiŵele jakusakala nnope kumpunda kuwulaga, nambo nkaapuuta ngondo mungulugulu mwa Nsikiti Wakuchimbichika (wa Maaka) ni ikaŵeje naga ali apatandite ŵanganyao kumputila palakwepo, sano naga achintandaga kumputa ngondo, basi mwaulagani, mwele ni mugaŵelele malipilo ga makafili.');
INSERT INTO "silika" VALUES(2,192,'2:192','Nambo naga akuleka, basi chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,193,'2:193','Soni mwaputani ngondo mpaka fitina (jagalagatila isanamu) jiikane mwakumala, soni Dini nijiŵe ja Allah pe, nambo naga akuleka, basi paŵe pangali umagongo ikaŵeje kwa ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,194,'2:194','Mwesi wakuchimbichika kwa mwesi wakuchimbichika, soni ichindu yakuchimbichika (naga utyosyedwe uchimbichimbi wakweo) iŵichilidwe mbusyo, sano jwantendele uwwanga nombe jenumanja mum''buchisyani uwwangao mwakulandana ni mwantendele ŵanganyammwe uwwanga, soni mun''jogopani Allah, ni manyililani yanti Allah ali pamo ni ŵaoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(2,195,'2:195','Soni tolani petala lya Allah, ningasin''diponyaga ni makono genu kuchonasiko, soni kolosyani, chisimu Allah akwanonyela ŵakolosya.');
INSERT INTO "silika" VALUES(2,196,'2:196','Soni kwanilisyani Hija ni Umra ligongo lya Allah, nambo naga nsiŵilwemo ni gambani kusikita chinyama cha mbepesi changasausya kuchipata (mpela mbusi), ni ngasimmyola mitwe jenu mpaka chili chiiche chinyama chambepesicho pamalo pakwe (pakusikitila), sano juchaŵe nkulwala mwa jenumanja kapena mu ntwe mwakwe kuŵa mwana yakunsausya (nikutenda yakanyisyidwa mpela kumyola ndema jakwe nkanijikwane), chatole malipilo gakutaŵa kapena sadaka kapena kusikita chinyama, sano naga n''di pantunjelele; basi jwachalisengwasye pakutenda Umra nkumalamo nikutenda Hija asikite chinyama chambepesi changasausya kuchipata, sano jwachatende ngachipata ataŵe moŵa gatatu ku Hija peko ni moŵa nsano nagaŵili pati m''bujiile (nsimangwenu), gele ni moŵa likumi gakwanila, yeleyi tiiŵe kwa mundu jwanganiliŵa liŵasa lyakwe dusi ni Nsikiti Wakuchimbichika, soni mun''jogopani Allah, ni manyililani yanti Allah aŵele Jwaukali pakupeleka ipotesi. ');
INSERT INTO "silika" VALUES(2,197,'2:197','Hija jikwete myesi jakumanyika (jajili: Mfungomosi, Mfungopili ni Mfungotatu), sano mundu jwachanuye Hija mujele (myesiji) akatenda ni kuŵecheta ya nsese, kapena kunyosya (pakutenda yasambi), soni akasakangana mu Hijamo, soni iliyose yachintende ŵanganyammwe Allah chaimanye, ni jigalani poso (jakwana kuti nkapita nchiŵenda), nambo poso jambone nnope nioga (wakun''jogopa Allah), ni njogopagani Une E jenumanja ŵankwete lunda!');
INSERT INTO "silika" VALUES(2,198,'2:198','Chipaŵe pangali ulemwa kukwenu naga nchisosagasosaga umbone wakuuma kwa M''mbuje gwenu (pakusumisyana indu ku Hija), basi pati n''gungumwiche kutyochela ku Arafâti munkolangani Allah pa Mashi’aril Harâm (papali pa Muzdalifah), ni munkumbuchileje yatite pakun''jongola, soni chisimu kala mwaliji mu ŵakusokonechela.');
INSERT INTO "silika" VALUES(2,199,'2:199','Kaneka gungumukani kutyochela pamalo paakugungumuka ŵandu wosope ni mum''bendeje Allah chikululuko, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,200,'2:200','Basi pati mmalisyisye ibada yenu (ya Hija) munkolangagani Allah yamwatiji pakwakolanga achatati ŵenu kapena kunkolanjisya nnope. Sano chilikati chaŵandu pana ŵakutiji: “Ambuje ŵetu! Tupani (chiilepe) pa duniya pano.” Ni nganakola ŵalakweo ku Akhera liunjili lililyose (lya yambone).');
INSERT INTO "silika" VALUES(2,201,'2:201','Sano ŵane mwa jamanjao ni akutiji: “Ambuje ŵetu! Tupani yambone pa duniya pano, soni nkatupe yambone ku Akhera ni nkatuŵambasye ilagasyo ya Moto.');
INSERT INTO "silika" VALUES(2,202,'2:202','Ŵele ŵanganyao ni ŵatachiŵa ŵana liunjili lya yaŵapanganyisye, sanotu Allah ni Jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(2,203,'2:203','Ni munkolangagani Allah m’moŵa gakuŵalanjika (gagali moŵa gatatu panyuma pa lisiku lya idi ya mfungotatu), nambo jwachachelenje pa (gamba kutama) moŵa gaŵilipe (ni kuŵigulaga) chaŵe jwangali ulemwa, nombe jwachachelewele (pakumalisya moŵa ganagatatu) chaŵe soni jwangali ulemwa, kwa mundu jwachaŵe ni woga, ni mun''jogopani Allah, soni manyililani yanti ŵanganyammwe Kukwakwe chinchisonganganyisyidwa.');
INSERT INTO "silika" VALUES(2,204,'2:204','Soni chilikati cha ŵandu pana ŵayikunsengwasyaga (mmwe Muhammadi صلى الله عليه وسلم) yakuŵecheta yakwe pa umi wa duniya uno, ni akumpaga umboni Allah pa yaili mu ntima mwakwe, kutendaga jwalakwejo ni jwapundile nnope makani.');
INSERT INTO "silika" VALUES(2,205,'2:205','Sano naga agalawiche (kutyochela kukwenu), niya jakwe jikuŵaga kupita nchiwatanganya pa chilambo nikonanga mmmela ni uŵelesi (wailango), nambo Allah jwangannonyela kuwatanganya.');
INSERT INTO "silika" VALUES(2,206,'2:206','Soni naga asalilidwemo kuti: “Mun''jogope Allah.” Chibuli chikuntendekasyaga kupanganya ya sambi, basi Jahannama jichinkwana jwalakwejo, kaje kusakala nnope liuto lyagusama (lyalitandichile)!');
INSERT INTO "silika" VALUES(2,207,'2:207','Soni chilikati cha ŵandu pana jwine jwaakusumisya ntima wakwe ligongo lyakusaka chinonyelo cha Allah, sano Allah ni Mpole kwa achikapolo Ŵakwe.');
INSERT INTO "silika" VALUES(2,208,'2:208','E jenumanja ŵankulupilile! Jinjilani mu Usilamu wosope wene, ni ngasinkuyaga sajo sya shetani, chisimu jwalakwejo kukwenu aŵele mmagongo jwakuonechela palangulangu.');
INSERT INTO "silika" VALUES(2,209,'2:209','Nambo naga ntelesye panyuma pakum’bichilila ilosyo yakuonechela palangulangu, basi manyililani yanti Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(2,210,'2:210','Ana pana ine yaakwembecheya ŵanganyao pangaŵaga kwaichilila Allah m’miwulili ja mawunde, ni (kwaichilila soni) Malaika, nikulamulidwa chilamusi (chakonasika kwao)? Sano kwa Allah ni kwakuujila indu yosope.');
INSERT INTO "silika" VALUES(2,211,'2:211','Mwawusyani ŵanache ŵa Israila kuti ana ilosyo ilingwa yatwapele yakuonechela palangulangu? Nambo jwachagalausye chindimba cha Allah panyuma pakum’bichilila (basi niyakwe), chisimutu Allah ni Jwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(2,212,'2:212','Usalalisidwe ku ŵandu ŵakanila umi wa duniya, soni niakwatendaga chipongwe ŵandu ŵakulupilile, nambo ŵandu ŵaŵele ali ni woga (wakun’jogopa Allah), chachiŵa pachanya pao lisiku lya Kiyama, sano Allah niakumpaga lisiki jwansachile lyangaŵalanjika.');
INSERT INTO "silika" VALUES(2,213,'2:213','Ŵandu wosope ŵaliji nkutula umo (wa Usilamu kaneka ni ŵatandite kulekangana), sano Allah nijwatumisye mitume jakutagulila abali syakusengwasya ni ja kutetela, ni jwatulwisye pamo ni ŵanganyao itabu mwakuonaonape, kuti ajilanyichisye chilikati cha ŵandu mu ichindu yaŵalekangene pa yalakweyo, ni nganalekangana pa yalakweyo ikaŵeje ŵaŵapedwile itabuo, panyuma pakwaichilila ilosyo yakuonechela palangulangu, ligongo lya kutendelana litima chilikati chao, sano Allah ni jwajongolele aŵala ŵaŵakulupilile ku yakuona (isyesyene) mu ichindu yaŵalekangeneyo, mwa lisosa Lyakwe, sano Allah ni akun’jongolelaga jwansachile kwitala lyagoloka.');
INSERT INTO "silika" VALUES(2,214,'2:214','Ana nkuganichisyaga yanti chinkajinjile ku Mbepo, kutendaga nkaniyim’bichilile (ilagasyo) yakulandana ni ya aŵala ŵaŵapite kala jenumanja nkanimpagwe? Kwakwayiyetu kulagasika ni kulwalika, ni ŵatetemesyedwe kusyene mpaka kwikana mwakuŵecheta Ntenga niŵandu ŵaŵakulupilile najo imo kuti: “Ana chikulupusyo cha Allah chichiiche chakachi?” Manyililani yanti chisimu chene chikulupusyo cha Allah chili duusi.');
INSERT INTO "silika" VALUES(2,215,'2:215','Akum''busya (mmwe Muhammadi صلى الله عليه وسلم) kuti ana nichichi chachatole? Jilani: “Yambone iliyose yachintole mwapani achinangolo ŵaŵili, ŵapaulongo, ŵawina, masikini ni jwapaulendo, sano chambone chilichose chachimpanganye, chisimu chalakwecho Allah akuchimanya chenene.”');
INSERT INTO "silika" VALUES(2,216,'2:216','Kulamulidwe kukwenu jenumanja kuputa ngondo kutendaga jalakwejo jili jakuŵenjedwa kukwenu, nambotu komboleka kuchiŵenga chindu kutendaga chili chambone kukwenu, soni nikomboleka kuchinonyela chindu kutendaga chili chakusakala kukwenu, sano Allah ni jwaakumanyilila, nambo ŵanganyammwe ngankumanyilila.');
INSERT INTO "silika" VALUES(2,217,'2:217','Akum''busya (mmwe Muhammadi صلى الله عليه وسلم) ya myesi jakuchimbichika (kuti iliuli) kumenyana ngondo mwalakwemo? Jilani: “Kumenyana ngondo mwalakwemo uŵele ulemwa wekulungwa. Nambotu kwasiŵilila (ŵandu) ku dini ja Allah, ni kunkana Jwalakwejo, ni (kwakanya ŵandu) ku Nsikiti Wakuchimbichika (wa Maaka), ni kwakoposya achinsyene mwalakwemo uŵele ulemwa wekulungwa nnope kwa Allah, sano fitina (jakwawatanganyichisya ŵandu dini jao), jiŵele jakusakala nnope kumpunda kuulaga.” Ni ngaaleka kuŵa ali nkumputa ngondo mpakana ali ankopwesye mu dini jenu (ja Usilamu) naga ali apakombwele, nambo jwachakopoche mwa jawomanja kuleka dini jakwe ni kwikanila kuwa ali kafili, basi ŵele ŵanganyao masengo gao gajonasiche pa duniya pano ni ku Akhera kwakwe, soni ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,218,'2:218','Chisimu aŵala ŵakulupilile, ni aŵala ŵasamile ni kumenyana petala lya Allah, ŵele ŵanganyao ni ŵali ni chikulupi chakupata ukoto wa Allah, sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,219,'2:219','Akum''busya (mmwe Muhammadi صلى الله عليه وسلم) ya ukana ni juga. Jilani: “Mu yalakwemo mwana ulemwa wekulungwa kwisa soni (ine) yakamuchisya ku ŵandu, nambo ulemwa wakweo uŵele wekulungwa nnope kupunda yakamuchisya yakweyo.” Soni niakum''busya kuti ana chichi chachatole? Jilani: “Chakupunda (pa yakulajila yenu).” Iyyoyopeyo niyakutiji Allah pakusalichisya kukwenu ma Ȃya (ga malamusi) kuti m''be ŵaganisya.');
INSERT INTO "silika" VALUES(2,220,'2:220','(Mu ichindu ya) pa duniya ni ku Akhera. Soni niakum''busya ya ŵawina. Jilani: “Kwajilanyichisya ŵanganyao iŵele yambone, nambo naga n''di m''bwanganyichilene nao (mpaka iŵe yambonepe) ngati achalongo achimijenu, soni Allah akummanyilila nkuwatanganya ni nkwilanya, sano Allah angasache akankandapachisye (malamusi), chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(2,221,'2:221','Soni nkalombela (jenumanja Asilamu ŵachilume) achakongwe ŵagalagatila isanamu mpaka ali akulupilile, chisimu kapolo jwachikongwe jwakulupilila juŵele jwambone kumpunda (nnukosyo) jwagalagatila isanamu atamuno juli junnonyelesye. Nombe soni (Asilamu ŵachikongwe) nkaalombekasya ku achalume ŵagalagatila isanamu mpaka ali akulupilile, chisimu kapolo jwachilume jwakulupilila juŵele jwambone kumpunda (nnukosyo) jwagalagatila isanamu atamuno juli junnonyelesye. Ŵanganyao akuŵilanjila ku Moto, nambo Allah akuŵilanjila ku Mbepo ni chikululuko mwa lisosa Lyakwe, ni akusalichisyaga ma Ȃya Gakwe ku ŵandu kuti aŵe ŵakumbuchila.');
INSERT INTO "silika" VALUES(2,222,'2:222','Soni akum''busya ya ulwele wa kuwesi. Jilani: “Walakweo uŵele usakwa wakuulasya, ni mwaŵambalani achimmasyeto pati ali kuwesi, soni ngasimwaŵandichila mpakana aswejele (pakumala kulwalako ni koga), sano pati aswejelemo, basi mwaichililani pamalo pakwe yatite pakunnamulila Allah, chisimu Allah jukwanonyela ŵakutenda toba nnope ni jukwanonyela soni ŵakuliswejesya.”');
INSERT INTO "silika" VALUES(2,223,'2:223','Achimmasyeto ŵenuo aŵele migunda jenu, basi ni n''jiichilileje migunda jenujo yankuti kusaka, soni lilongochesyani (yambone) mwachimisyene, ni mun''jogopeje Allah, soni manyililani yanti ŵanganyammwe chinchisimangana Najo, ni mwatagulilani ŵakulupilila abali jakusengwasya.');
INSERT INTO "silika" VALUES(2,224,'2:224','Ngasinkutenda kulumbila kwenu lina lya Allah kuŵa chakunnekasya kutenda yaukoto ni yakun''jogopa Allah ni kwilanya chilikati cha ŵandu (naga n''di nnumbile kuti ngantenda yeleyo), sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,225,'2:225','Allah ngasampa ilagasyo pakulumbila kwenu kwangali mate, nambo chachimpa ilagasyo pa yajitesile mitima jenu (pakulumbila mwakusimichisya), sano Allah ni Jwakululuka kusyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(2,226,'2:226','Kwa aŵala ŵakwalumbililaga achimmasyeto ŵao (kuti nganaŵa ŵakwayiye), ajembecheye myesi ncheche, sano naga akuujila (pajele ndemaji), basi Allah chisimu chene ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(2,227,'2:227','Nambo naga ali asimichisye kulekana ulombela, basi Allah chisimu chene ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,228,'2:228','Soni achakongwe ŵalechedwe malombela ajembecheye (atame eda) ni kulitalichisya achimisyene (yakulombedwa) mpaka kuswejela kutatu (kwa ulwele wa kuwesi).Ni ngaikundisidwa kwa ŵele ŵanyao kusisa yagumbile Allah mu itumbo yao, naŵaga ŵanganyao akunkulupilila Allah ni Lisiku Lyambesi, sano achiŵammakwao akwete machili gakomboleka kwaujila mu jele ndemaji naga akusaka kwilana, ni akwete kutendeledwa achakongweo (ni achiŵammakwao) yakulandana ni yaakulamulidwa ŵanganyao kuti atende (kwa achalume ŵao) mwakwilanila (ni malamusi ga Dini), nambo kuti achalume akwete uchimbichimbi pa ŵanganyao (ŵalume ni ntwe weŵasa), sano Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(2,229,'2:229','Maloŵe gakulekanila ulombela (ganti ŵalume nkuŵechetamo komboleka kuujila soni) gaŵechetedwe kaŵilipe, basi ni antamiche jwankongwejo mwakwilanila (ni malamusi ga Dini naga achim''bujilaga), kapena alekangane najo mwaumbone. Ni ngaikundisyidwa kukwenu kujigala chilichose mu ichindu yamwapele achakongweo, ikaŵeje naga ŵanaŵaŵilio (ŵalume ni ŵankwao) akogopa kuti ngakombola gosa mipingu ja Allah (gagali malamusi ga Allah gakwendechesya liŵasa), nambo naga nkogopa kuti ngakombola gosa mipingu ja Allah, basi chipaŵe pangali ulemwa kwa ŵaŵilio pa chindu chaliombolele nacho ŵakongweo (ŵakongwe chapeleche chipanje kwa ŵankwao kuti alechedwe, nombe ŵalume chapochele chipanjecho nikwaleka). Jele ni mipingu ja Allah basi kasin''jigulukaga, sano ŵachaguluche mipingu ja Allah, ŵanganyao niŵali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,230,'2:230','Nambo naga akwika mwakunneka jwankongwejo (pakum''bechetela maloŵe gakulekanila ulombela kaataatu), ngakuŵa kwakundisyidwa kwa ŵalumeo (kum''bujila) panyuma pakwe, mpaka ali alombedwe niŵalume ŵane (nombenao nichaikane mwakunneka). Sano naga ali annesilemo, basi paŵe pangali ulemwa pa ŵaŵiliŵala (ŵakongweo ni ŵankwao ŵandandaŵala) kuujilana naga akuona kuti chakombole gosa mipingu ja Allah. Sano jele ni mipingu ja Allah jaakuŵa ali nkujisalichisya ku ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(2,231,'2:231','Sano pati mwalesile ulombela achimmasyeto ni akwanisye ndema jawo (jakutamila eda), mwatamikani mwakwilanila (ni malamusi ga Dini naga nchaujilaga), kapena lekanganani nao mwakwilanila (ni malamusi ga Dini soni), nambo ngasimwatamika mwa kwalagasya kuti n''gulucheje mipingu (pangakuya malamusi ga liŵasa), sano jwachaiche mwakutenda yele, nikuti amasile mwalitendele lupuso nsyene. Soni ma Ȃya ga Allah ngasin''gatendelaga chipongwe, ni kumbuchilani chindimba cha Allah chachili pa jenumanja ni yaŵatulwisye kukwenu ya m’chitabu (cha Qur’an), ni yalunda lwakusokoka (gagali mahadisi ga Ntume صلى الله عليه وسلم) yaakum''bunda nayo. Ni mun''jogopani Allah soni manyililani yanti Allah chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(2,232,'2:232','Sano pati mwalesile ulombela achimmasyeto niakwaniisye ndema jao (jakutamila eda), basi (jenumanja ŵakuchikongwe) nkasimwakanya ŵanganyao kulombedwa soni ni achalume ŵao naga ali apikanganene chilikati chao mwakwilanila (ni malamusi ga Dini), yalakweyi akuundidwa nayo mundu jwali nkunkulupilila Allah mwa jenumanja ni Lisiku Lyambesi, yalakweyi niyaili yambone nnope kukwenu ni ya kuswejelesya, sano Allah ni akumanyilila nambo ŵanganyammwe ngankumanyilila.');
INSERT INTO "silika" VALUES(2,233,'2:233','Sano achamao ajonjesyeje ŵanache ŵao yaka iŵili yakwanila kwa jwakusaka kwanisya konjesya, ni liŵele lilamusi kwa atatigwe mwanache kwapa yakulya achamawowo ni kwawecha mwakwilanila (ni malamusi ga Dini), ntima wa jwalijose ukakanganisyidwa ikaŵeje yampaka ukombole. Akasalagasyidwa amao ligongo lya mwanagwao, nombe atati (akalagasidwa) ligongo lya mwanagwao, lilamusili lili yalumope soni kwa akuchengeta (mwanachejo atatigwe ali awile). Nambo naga ali asachile ŵanaŵaŵilio kunnekasya konga mwakunonyelwa kwao ni kupikanganana, basi chipaŵe pangali ulemwa pa ŵanganyao. Sano naga nsachile kwasosela ŵanache ŵenuo akonjesya ŵane (ŵangaŵaga achikulu ŵao), chipaŵe pangali ulemwa kukwenu naga n''di mwapele chimwakamulene kutola (pa konjesyako) mwakwilanila (ni malamusi ga Dini), ni mun''jogopeje Allah, soni manyililani yanti Allah ichindu yankutenda juŵele Jwakwiilolechesya.');
INSERT INTO "silika" VALUES(2,234,'2:234','Sano ŵachaweje mwa jenumanja ni kulekaga achiŵammakwao, ajembecheye (achiŵammakwaowo ni kulitalichisya) achimisyene (yakulombedwa), mpaka myesi ncheche ni moŵa likumi, sano naga akwaniisye ndema jao (jakutamila eda), chipaŵe pangali ulemwa kukwenu mu ichindu yalitendele achimisyene (mpela kulisalalisya nikulilosya kwa akuwusya ulombela), nambo iŵeje mwakwilanila (ni malamusi ga Dini), sano Allah abali syosope sya yankutenda aŵele Jwasimanya chenene.');
INSERT INTO "silika" VALUES(2,235,'2:235','Soni chipaŵe pangali ulemwa kukwenu mu kulochesya kwenu yakusaka kwausya ulombela achimmasyetowo (nambo mwangali kulumukula, naga ndema jao ja eda nkanijimale), kapena n''di n''gambile kusisa m’mitima mwenu (yakwalomba pajichimala edapo), Allah akumanyilila yanti jemanja chinchakumbuchilape ŵanganyao, nambo ngasintaŵana nao chilanga mwaasili (cha kwalomba kutendaga eda nkanijimale) pangaŵaga kuŵecheta nao maloŵe gakwilanila (ni malamusi ga Dini). Ni ngasinsimichisya kutaŵikanya ulombela mpaka ndema jajilembedwe (jakutamila eda jila) jiiche pantepa pakwe. Ni manyililani yanti Allah akumanyilila yaili m’mitima mwenu, mwanti mun''jogopani, ni manyililani soni yanti Allah ni Jwakululuka kusyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(2,236,'2:236','Chipaŵe pangali ulemwa kukwenu naga n''di mwalesile ulombela achimmasyeto nkanim’biche mwakwakwaya (gona ulombela), soni nkanimwamanyisye liunjili lyao lya mbiya syandowa, nambo mwasengwasyani pakwapa liŵale (mbiya syakulekanila), jwakupata chapeleche mwakulandana ni kupata kwakwe, nombe jwakulaga chapeleche mwakulandana ni kulaga kwakwe, liŵale lyakwilanila (ni malamusi ga Dini), lyeleli liŵele lilamusi kwa ŵakolosya.');
INSERT INTO "silika" VALUES(2,237,'2:237','Nambo naga n''di mwalesile ulombela ŵanganyao nkanim’biche mwakwakwaya (gona ulombela) kutendaga n''di mwamanyisye liunjili lyao lyambiya syandowa, mwapani litika lya mbiya syamwakamulenesyo ni ikaŵeje (achakongweo) ali akululwiche (kuti: “Basi akamba chilichose.”) Kapena akululuche ajula jwachili chiundo cha ulombela mu nkono mwakwe (nkulombela, pakwalechela ŵakongwe mbiya syosope). Nambo naga nkukululuchilana, tiiŵe yakum''bandichisya ku woga (wakun''jogopa Allah). Ni ngasin''diŵalila kutendelana yambone chilikati chenu (ata n''di nkulekana), chisimu Allah ichindu yankutenda juŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(2,238,'2:238','Chengetani Swala syosope (pakusiswali mu ndema syakwe), makamaka Swala jachilikati, soni jimagani pameso pa Allah mwa kulinandiya.');
INSERT INTO "silika" VALUES(2,239,'2:239','Sano naga n''di ni woga (mpela pa ngondo), chinkomboleche kuswali n''di nkwenda kapena n''di nkwesile chakwela, nambo naga n''di pa ntunjelele, basi munkumbuchilani Allah (pakuswali) yatite pakun''jiganya yamwalekaga ngaimanya.');
INSERT INTO "silika" VALUES(2,240,'2:240','Sano ŵachaweje mwa jenumanja ni kulekaga achiŵammakwao, aleche wasiya wakwamba achiŵammakwaowo wanti apedwe ichindu yakwakamuchisya yakwana chaka chantundu mwangali koposyedwa (mmajumba ga achiŵammakwao), nambo naga akukopoka (achimisyene), chipaŵe pangali ulemwa kukwenu mu ichindu yalitendele achimisyene yakwilanila (ni malamusi ga Dini), sano Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(2,241,'2:241','Sano achakongwe ŵaakulechedwa ulombela, apedwe liŵale lyakwasengwasya lyakwilanila (ni malamusi ga Dini), lyeleli liŵele lilamusi kwa ŵaoga (wakun''jogopa Allah).');
INSERT INTO "silika" VALUES(2,242,'2:242','Iyyoyopeyo niyakutiji Allah pakusalichisya kukwenu ma Ȃya Gakwe kuti m''be ni lunda.');
INSERT INTO "silika" VALUES(2,243,'2:243','Ana nganinyyimanya ya aŵala ŵanya ŵana kopokanganaga m’majumba mwao ali masausande sausande pakogopa kuwa? Sano Allah niŵasalile kuti: “Wani!” Kaneka ni ŵasyusisye (kuti amanyilile yanti chiwa changatilika). Chisimu Allah ni Nsyene umbone wakwatendela ŵandu, nambo ŵandu ŵajinji ŵangatogolela.');
INSERT INTO "silika" VALUES(2,244,'2:244','Ni putani ngondo petala lya Allah, soni manyililani yanti Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,245,'2:245','Ana ni nduni jwachankongosye Allah ngongole jambone (pakupeleka chipanje chakwe petala lya Allah), kuti ajijonjechesye kwa jwalakwejo ni yakonjechesya yejinji? Sano Allah ni akuumbataga (magasa pakupeleka mwakunusila), soni ni akusapangulaga (magasago pakupeleka mwakutupiya), soni Kukwakwe chinchiuchisidwa.');
INSERT INTO "silika" VALUES(2,246,'2:246','Ana nganinyyimanya ya achakulungwakulungwa mu ŵanache ŵa Israila panyuma pa (kuwa) Musa, katema kaŵansalile Ntume jwao (jwali Samuyele) kuti: “Ntusagulile nchimwene kuti tupute ngondo petala lya Allah?” Jwalakwe ŵatite: “Mwinetu ŵanganyammwe ngaŵa nkuputa ngondojo pati jilamulidwe kukwenu.” Ŵanganyao ŵatite: “Chantiuli tukaputa ngondo petala lya Allah kutendaga tukoposyedwe mmajumba mwetu, ni ŵanache ŵetu (akamwilwe ni amagongo)?” Nambo ndema jakwalamulidwe kwa jamanjao kuputa ngondo ŵagalawiche ikaŵeje ŵannono mwa ŵanganyao, sano Allah ni jwaakwamanya chenene ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,247,'2:247','Sano ntume jwao ŵasalile kuti: “Chisimu Allah ansagulile Twaluta (Sawelo) kuŵa nchimwene.” Ŵanganyao ŵatite: “Chawale chantiuli jwalakwe uchimwene wakutulamulila uwwe, kutendaga uwwejo niŵatuli ŵakuŵajilwa ni uchimwene kumpunda jwalakwe, soni nganapedwa chipanje chejinji?” Jwalakwe jwatite: “Chisimu Allah ansagwile jwalakwejo mwa jemanja ni an''jonjechesye kusapanguka mu umanyilisi ni chilu, sano Allah akasam''bwechaga uchimwene Wakwe jwansachile, soni Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(2,248,'2:248','Soni ntume jwaojo ŵasalile kuti: “Chisimu chimanyilo cha uchimwene wakwe nikum’bichilila kwa libokosi lyalili nkati mwakwe mwana kutulala kwakuumila kwa M''mbuje gwenu, ni sigasiga syanakusilekaga ŵandu ŵa Musa ni ŵandu ŵa Haruna, (chilim’bichilile libokosilyo) ali alitwichile Achimalaika, chisimu mu yalakweyo mwana chimanyilo kukwenu naga jenumanja n''di ŵakulupilila (kusyene).”');
INSERT INTO "silika" VALUES(2,249,'2:249','Sano katema kajwaŵigwile Twalutu pamo ni asilikali, jwatite: “Chisimu Allah tan''dinje ni lusulo, basi jwachaikane mwakumwa mwalakwemo nikuti nganaŵa none (akasanguya), nambo jwachatende ngagapasya chisimu chene jwele nijwachaŵe none, ikaŵeje jwachagambe kuteka kampepe ni ligasa lyakwe (ni kumwa nombenajo tuli umo).” Niŵamwele mwalakwemo ikaŵeje ŵannono mwa ŵanganyao. Sano katema ka jwalujombweche (lusulo lula) jwalakwe ni ŵandu ŵaŵakulupilile najo imo, ŵanganyao ŵatite: “Ŵangali machili uwwe lelo gakunkombolela Jaluta (Goliyati) ni asilikali ŵakwe.” Nambo ŵandu ŵaŵaliji ni chikulupi chakusimangana ni Allah ŵatite: “Ana makuga galingwa gakunandipilwa gagaamalene nago makuga gapawinji mu lisosa lya Allah! Sano Allah ali pamo ni akupilila.”');
INSERT INTO "silika" VALUES(2,250,'2:250','Sano katema kaŵasimangene najo Jaluta ni asilikali ŵakwe, (asilikali ŵa Twalutu) ŵatite: “E Ambuje ŵetu! Tupungulilani uwwe kupilila, soni n''galimbanganye makawu getu ni ntukulupusye ku ŵandu achimakafili.”');
INSERT INTO "silika" VALUES(2,251,'2:251','Basi ni ŵapwilingenye mu lisosa lya Allah, mwanti Daudi jwauleje Jaluta, sano Allah niŵampele (Daudijo) uchimwene ni lunda lwakusokoka (utume), niŵan''jiganyisye iliyose yaŵasachile. Soni kungaŵaga Allah kwatuta ŵandu (amagongo) kupitila mwa ŵandu ŵane (mpela asilikali), chilambo chingawatanganyiche, nambo kuti Allah ni Nsyene umbone wakwiitendela iwumbe yosope.');
INSERT INTO "silika" VALUES(2,252,'2:252','Gele ni ma Ȃya ga Allah gatukunsoomela mwakuonaonape, soni mmwejo (Muhammadi صلى الله عليه وسلم) n''di jumpepe mwa Achimitenga.');
INSERT INTO "silika" VALUES(2,253,'2:253','Ŵele Achimitengao twalekangenye pakwapa uchimbichimbi ŵane kwapunda ŵane. Ŵane mwa jamanjao Allah ŵaŵechetekasisye, niŵanyakulile ŵane mwa jamanjao uchimbichimbi (wao). Soni Isa mwanache jwa Maryam twampele isimosimo yakuonechela ni twan''dimbangenye ni Nsimu Ŵeswela (Jibulilu), sano Allah angasache nga nganamenyana aŵala ŵaŵaiche munyuma mwao panyuma pakwaichilila ilosyo yakuonechela palangulangu, nambo kuti ŵalekangene, mwanti ŵane mwa jamanjao ŵakulupilile ŵane soni mwa ŵanganyao ŵakanile, sano Allah angasache nga nganamenyana, nambo kuti Allah akasatendaga yaakusaka.');
INSERT INTO "silika" VALUES(2,254,'2:254','E jenumanja ŵankulupilile! Tolani mu ichindu yatumpele nkaniliiche Lisiku lyangali kusuma (chaalikulupuchisya) mu lyele Lisikulyo, atamuno usyoŵe ni chondechonde (ngaŵa nkukamuchisya), sanotu makafili niŵali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,255,'2:255','Allah pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo. Jwaumi wandamo, Jwachiimilecho (pakuchengeta iwumbe Yakwe). Jwangampata gwesela atamuno lugono. Iŵele Yakwe Jwalakwejo yosope yaili kumawunde ni yaili petaka. Ana nduni jwampaka akombole kuchondelela Kukwakwe pangaŵaga mu lisosa Lyakwe? Jukumanyilila ya paujo pao ni ya panyuma pao. Sano ŵanganyao ngaŵa nkuchisyungula (kwaachimanya chenene) chilichose mu ya umanyilisi Wakwe ikaŵeje chasachile. Chitengu Chakwe cha uchimbichimbi chitenjele kumawunde ni petaka. Ni jwangansitopela kuichengeta yalakweyo. Soni Jwalakwe ni Jwapenani kusyene, Jwakuchimbichika nnope. ');
INSERT INTO "silika" VALUES(2,256,'2:256','Pangali kanganichisyana mu Dini. Pamasile pachimanyukwiche chongoko pakulekangana ni kusokonechela, basi jwachankane shetani ni kunkulupilila Allah nikuti pamasile paakamulisye nkonjo wakulimbangana wauli wangatemeka. Sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,257,'2:257','Allah ni Nkwaagosa ŵandu ŵakulupilile, akwakoposyaga m’chipi nikwajinjisya mwilanguka, nambo ŵandu ŵakanile akwagosa ŵao ni shetani, akwakoposyaga mwilanguka ni kwajinjisya nchipi, ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,258,'2:258','Ana nganinyyimanya ya ajula jwana kutataŵanaga ni Ibrahima pa ya Ambujegwe, ligongo lyanti Allah ŵampele uchimwene? Pandema jaŵaŵechete Ibrahima kuti: “Ambuje ŵangu ni ŵaakupelekaga umi ni chiwa,” (Anamuruda) ŵatite: “Une none ngupelekaga umi ni chiwa.” Ibrahima jwatite: “Chisimu Allah ni jwaakwikaga nalyo lyuŵa kuumila ku ngopoko, sano mmwe m''biche nalyo kuumila ku ngapililo, basi niŵanjendengele ajula jwaŵakanile (kusoŵa yakuŵecheta), sano Allah jwangaŵa nkwajongola ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(2,259,'2:259','Kapena mpela ajula jwana kuupitaga musi uli ugumuchile (mapupa gakwe) pa misakasa jakwe. Jwalakwe jwatite: “Ana Allah chachiujimusya chantiuli walakwewu panyuma pakuwa kwakwe?” Basi Allah niŵampele chiwa yaka yakwana lichila, kaneka niŵansyusisye, niŵam’busisye kuti: “Nnonjele ndema jantiuli (n''di chiwile)?” Jwalakwe jwatite: “Nonjele usiku umo kapena chipande cha usiku.” (Allah) jwatite: “Nambo nnonjele yaka yakwana lichila, sano ilolani yakulya yenu ni yakumwa yenu, nganiijonasika (nganiichesuka), soni jilolani mbunda (bulu) jenu (jajili maupaupape), ni (tunsyusisye) kuti tum''biiche kuŵa chilosyo kwa ŵandu, soni galolani maupa (ga mbunda jenujo) yatujile pakugasyusya kaneka ni kugawecha minowu.” Basi katema kayamanyukwiche kukwakwe, jwatite: “(Sano) ngumanyilila yanti Allah pa chindu chilichose aŵele Jwakombola.”');
INSERT INTO "silika" VALUES(2,260,'2:260','Soni (kumbuchilani) katema kajwaŵechete Ibrahima kuti: “E Ambuje ŵangu! Munosyani yachinchiti pakwasyusya ŵawe.” (Allah) jwatite: “Ana nganinkulupililepe?” Jwalakwe jwatite: “Ngaŵa (kuti nganingulupilile), nambo kuti utamilichiche ntima wangu.” (Allah) jwatite: “Basi jigalani ijuni ncheche ni nyyisonganganye kukwenu mpaka nyyisyoŵele chenene, (kaneka ni nyyikatanyekatanye ni kuiwanganya iŵalo yakwe), kaneka ni nkaŵiche petumbi lililyose liwunjili lya yalakweyo, kumala pele ni nyyiŵilanje chiyim’bichilile mwakangamala, soni manyililani yanti Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(2,261,'2:261','Chilandanyo cha aŵala ŵaakutolaga ipanje yao petala lya Allah, chili chisawu lusongolo lumpepe lwalukopwesye masache nsano nagaŵili, mwisache lililyose nikuŵa mwana songolo syakwana lichila, Allah ni akun''jonjechesyaga jwansachile, soni Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,262,'2:262','Ŵandu ŵaakutola ipanje yao petala lya Allah, soni ni ngaakukuyichisya pa yaatosileyo kuumba kapena kupeleka ilagasyo, akwete kupata malipilo gao kwa M’mbuje gwao, soni ngasaja kuŵa niwoga, ngasaja kuŵa soni ŵakudandaula.');
INSERT INTO "silika" VALUES(2,263,'2:263','Maloŵe gambone ni kululuka, ili chenene kumpunda sadaka jaakuyichisya ilagasyo, sano Allah ni Jwakupata kwejinji, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(2,264,'2:264','E jenumanja ŵankulupilile! Ngasin''jonangaga sadaka syenu paakuumba ni kupeleka ilagasyo, mpela ajula jwaakutola chipanje chakwe mwakulilochesya ku ŵandu, ni ngaakunkulupilila Allah ni Lisiku Lyambesi. Sano chilandanyo chakwe chili chisawu lwala lwaluli lwana litaka pachanya pakwe, nikumputila wula jejinji (nikutyosya litaka lyosopelila) nikupaleka pali mbee! Ngasakombola kupata chilichose mu yaŵapanganyisye. Sano Allah ngaakwajongolaga ŵandu makafili.');
INSERT INTO "silika" VALUES(2,265,'2:265','Sano chilandanyo cha ŵandu ŵaakutolaga ipanje yao pakusachilila chinonyelo cha Allah, ni kusimichisya kwakutyochela m’mitima mwao (kuti Allah chachalipila), chili chisawu n''gunda wauli pa chiŵata chakunyakuchila nikumputila wula jejinji nikoposya isogosi yakwe mwa chitupyo chakusogola kaŵili, nambo naga ngamputila wula jejinji, nikuti jamawawa (jikunkwanagape nikusogola mujikusogolelaga migunda jine). Sano Allah ichindu yankutenda aŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(2,266,'2:266','Ana mpaka anonyelwe jumo mwa jenumanja kola n''gunda wa itela ya tende ni zabibu wasikujilima cha pasi pakwe sulo, akwete jwalakwejo mwelemo isogosi yantundu uliwose, nikumpata uchekulu ali akwete ŵanache ŵangalikombolela, nikumpata n''gundao chimbunga chana moto nikutinika (mpaka ayinonyele yele)? Iyyoyopeyo niyakutiji Allah pakusalichisya kukwenu ma Ȃya kuti m''be ŵakuchetelela. ');
INSERT INTO "silika" VALUES(2,267,'2:267','E jenumanja ŵankulupilile! Tolani mu yambone yampatile ni yatunkopochesye kuumila mwitaka, ni ngasinsachililaga kutola chakusakala kutendaga jenumanja nganim''ba nchipochele (chanti iyyoyocho pati nchipedwaga) ikaŵeje mwaachikupilila (ni kupochelela kusoni. Ana Allah ni mpaka apochele chakusakalacho?) Soni manyililani yanti Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(2,268,'2:268','Shetani akun''jogoyechesyaga kulaga ni akunnamulaga yakunyalaya (mpela ubayili), sano Allah ni akutaŵana namwe ya chikululuko chakuumila Kukwakwe ni umbone (naga nchitolaga), sano Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(2,269,'2:269','Akumpaga lunda lwakusokoka jwansachile, sano jwapedwile lunda lwakusokoka, nikuti pamasile papedwile yambone yejinji, ni pangali ŵaakukumbuchilaga (ni kwakamuchisya kumbuchilako), ikaŵeje ŵandu ŵakwete lunda.');
INSERT INTO "silika" VALUES(2,270,'2:270','Soni chilichose chachintole kapena nasili jilijose jachintaŵe, chisimu chene Allah chaimanye, soni ŵandu ŵalupuso ngasakola ŵakwakulupusya.');
INSERT INTO "silika" VALUES(2,271,'2:271','Naga sadaka syenu nkusilosya pakutola chisiŵe syambone, nambo naga nsitole mwakusisa nikwapaga ŵakulaga, basi yele niyachiiŵe yambone nnope kukwenu, soni chansimachisye yakusakala yenu, sano Allah abali syosope sya yankutenda aŵele Jwasimanya chenene.');
INSERT INTO "silika" VALUES(2,272,'2:272','Nganigaŵa masengo genu kwajongola ŵanganyao, nambo kuti Allah ni jwaakun’jongolaga jwansachile. Sano mu chambone chilichose chachintole basi (ukoto wakwe) wenu mwachimisyene, ni ngankutolaga ikaŵeje pakusachilila Ngope ja Allah, sano mu chambone chilichose chachintole, chichilipidwa kukwenu mwakwanila, soni ŵanganyammwe ngasin''ja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(2,273,'2:273','(Sadakasyo sipelechedwe) ku ŵakulaga ŵasiŵilidwe petala lya Allah, ŵangaŵa nkombola kwenda pa chilambo (kupita nchiliisosela lisiki). N''jinga (jwangamanya mwaŵelele) akwaganichisyaga kuti ŵakuliipatila ligongo lyakuliisiŵilila (kuŵenda). Mpaka mwamanyilile (kuti ŵakulaga) ni imanyilo yao. Ngakwaŵendaga ŵandu mwakwekweteka. Soni chambone chilichose chachintole, chisimu chalakwecho Allah akuchimanya chenene.');
INSERT INTO "silika" VALUES(2,274,'2:274','Ŵandu ŵaakutolaga ipanje yao chilo ni muusi, mwakusisa ni mwakuonechela, nikuti akwete kupata malipilo gao kwa M’mbuje gwao, soni ngasaja kuŵa niwoga, ngasaja kuŵa soni ŵakudandaula.');
INSERT INTO "silika" VALUES(2,275,'2:275','Ŵandu ŵaakulya (chipanje cha) katapila, ngasajima (pakutyochela m’malembe mwao), ikaŵeje yakutiji pakwima mundu jwagakumputaga masoka gachishetani nikuntendekasya mangwengwe, yalakweyo ni ligongo lyanti ŵanganyao akutiji: “Chisimu kusumana kukulandanaga ni katapila.” Kutendaga Allah akundisye kusumana ni akanyisye katapila. Sano jwachim’biiche chamuko chakutyochela kwa M’mbujegwe (chakun''jamuka ya katapila) nikuleka (katapilajo), basi yayapite kala chiiŵe yakwe (ngaawusya chipanje chakatapila jakalakala), mwanti nganya jakwe jikaŵe kwa Allah (ni jwachakamanyilile yakutenda najo), nambo ŵachaujile (soni ku katapila), basi ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(2,276,'2:276','Allah akasatyosyaga upile (pachipanje cha) katapila ni akasasijonjechesyaga upile sadaka, sano Allah jwangannonyela kafili jwalijose jwasambi syejinji.');
INSERT INTO "silika" VALUES(2,277,'2:277','Chisimu ŵandu ŵakulupilile ni kutendaga yambone, ni kwimikaga Swala mwakolosya, ni kutolaga Zaka, akwete kupata malipilo gao kwa M’mbuje gwao, soni ngasaja kuŵa niwoga, ngasaja kuŵa soni ŵakudandaula.');
INSERT INTO "silika" VALUES(2,278,'2:278','E jenumanja ŵankulupilile! Mun''jogopani Allah ni nneche yayasigalile mu katapila naga n''di ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(2,279,'2:279','Nambo naga ngankutenda (yakuleka katapilayo), manyililani kusyene ya (kuŵa pa) ngondo jakutyochela kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم, nambo naga ntende toba nikuti itepo ya ipanje yenu chiiŵe yenu. Ngasintenda lupuso (pakuŵilasya yakupunda pa chitepo cha chipanje chenu), nambo soni nkatendedwa lupuso (pakum''buchisya yakunandipila pa chitepocho).');
INSERT INTO "silika" VALUES(2,280,'2:280','Sano naga (nkukongola) inkanganiiche, basi (nkukongosya) an''jembechele mpakana chiyin''jepepalile, nambo naga nkujisyusya (ngongolejo) kuŵa sadaka chiiŵe yambone kukwenu naga n''di ŵakumanyilila.');
INSERT INTO "silika" VALUES(2,281,'2:281','Ni lijogopani Lisiku lyakum''buchisya ŵanganyammwe kwa Allah, kaneka mundu jwalijose chachilipidwa mwakwanila pa yaŵapanganyisye soni ŵanyao ngasaja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(2,282,'2:282','E jenumanja ŵankulupilile! Naga n''di nkukongosyana ngongole jakuusya pandema jekolanjidwe, basi jilembani. Ni alembe nkulemba chilikati chenu mwakulungamika. Sano nkulemba ngasakana kulemba mpela yatite pakun''jiganya Allah, alembepe basi. Soni alembesyeje jwajili kukwakwe ngongolejo (nkukongola), ni an''jogope Allah M’mbujegwe, soni akapungusya chilichose mu ngongolejo. Sano naga jwajili kukwakwe ngongolejo (nkukongola) juli jwawwawate (jwangakombola kwendesya chipanje ligongo lya ulyakasi), kapena jwangali machili (gakulembechesya mpela mwanache), kapena ngakukombola kulembesya nsyene (ligongo lya chimeme kapena ujinga), basi annembechesye nkunchengeta jwakwe mwakulungamika. Soni soselani mboni siŵili mwa achalume ŵenu (syakuŵichila umboni ngongolejo). Nambo naga nganaŵa (apatikene) achalume ŵaŵili, ni aŵe jwannume jumo ni achakongwe ŵaŵili mwa ŵankasamwakundaga kuŵa mboni, kutendela kuti naga jumo jwao (mwa achakongweo) juli juliŵalile, jwinejo chankumbusye n''jakwejo. Soni mboni sikakana pati siŵilanjidwemo. Ni ngasimpela kujilemba (ngongolejo mwachijiŵele), jamwana kapena jekulungwa kwikanila ndema jakwe (jakuuchisya). Yele (yakulemba ngongoleyo) niyayili yakulungamika nnope kwa Allah soni yakulimbanganya umboni ni yakuŵandichila kuntendekasya kuti nkakaichila, ikaŵeje naga gali malonda ga pameso gankusumana chilikati chenu ni chipaŵe pangali ulemwa kukwenu naga ngankugalemba, nambo sosani ŵakutendela umboni ndema jankusumana. Nambotu akalagasyidwa nkulemba kapena nkuŵichila umboni, nambo naga ntende (yele yakwalagasyayo) kwele nichikuŵe kunyosya kwenu (kwakunnyosya Allah). Ni mun''jogopani Allah, soni Allah akun''jiganya ŵanganyammwe, soni Allah chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(2,283,'2:283','Sano naga n''di pa ulendo ni nganimumpata nkulemba, basi paŵe pana pinyolo jakupocheledwa (ni nkukongosya), nambo naga ŵane (akukongosya) mwa jenumanja ŵakulupilile ŵane (akukongola pakwakongosya mwangalajila pinyolo), basi jwakongosyedwejo awuchisye ngongole jakwejo, soni an''jogope Allah M’mbujegwe. Ni ngasinsisa umboni, sano jwachausise (umbonio), chisimu jwalakwejo chiuŵe wasambi ntima wakwe, sonitu Allah ichindu yankutenda aŵele Jwaimanya chenene.');
INSERT INTO "silika" VALUES(2,284,'2:284','Yosope yaili kumawunde ni yaili petaka nsyene jwakwe Allah, ni kanga chinyyilochesye papaswela yaili m’mitima mwenu kapena chinyyisise Allah chachim''balanga nayope, ni chachinkululuchila jwansachile ni kumpa ilagasyo jwansachile. Sano Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(2,285,'2:285','Ntenga صلى الله عليه وسلم jukulupilile yayitulusyidwe kukwakwe kuumila kwa M’mbujegwe, iyyoyopeyo soni ŵakulupilila. Jwalijose mwa jamanjao ankulupilile Allah, Malaika Gakwe, Itabu Yakwe ni Achimitenga Ŵakwe. (Akutiji): “Ngatukulekanganya chilikati cha jumpepe mwa Achimitenga Ŵakwe (wosope tukwakulupililaga).” Soni ni akutiji: “Tupikene soni tukundile, (tukuŵenda) chikululuko Chenu (Mmwe) Ambuje ŵetu! Soni Kukwenu pe ni kwakuujila.”');
INSERT INTO "silika" VALUES(2,286,'2:286','Allah jwangankanganika mundu (kutenda) ikaŵeje yampaka ayikombole, chakalipidwe pa yambone yaŵapanganyisye ni chakapedwe ipotesi pa yakusakala yaŵapanganyisye. “Ambuje ŵetu! Ngasintupa ipotesi pati tuliŵalilemo kapena kulemwa. Ambuje ŵetu! Ngasintutwika uwwe nsigo (wamalamusi) yamwatite pakwatwika ŵakala ŵetu (Ayuda ni Akilisito). Ambuje ŵetu! Ngasintutwika uwwe yanganitukola nayo machili (ganti nikuikwanisya).Ni tusimachisyani (sambi syetu), soni tukululuchilani (ilemwa yetu), tutendelani soni chanasa, Mmwejo ni Nkutugosa jwetu, basi tukulupusyani ku ŵandu makafili.” ');
INSERT INTO "silika" VALUES(3,1,'3:1','Alif - Lam – Mim. ');
INSERT INTO "silika" VALUES(3,2,'3:2','Allah pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo. Jwaumi wandamo, Jwachiimilecho (pakuchengeta iwumbe Yakwe).');
INSERT INTO "silika" VALUES(3,3,'3:3','Antuluchiisye mmwejo Chitabu mwakuonaonape, chachikwitichisya yayaliji paujo pakwe, niŵatulwisye soni Taurat ni Injil.');
INSERT INTO "silika" VALUES(3,4,'3:4','(Ŵaitulwisye) kala kuti iŵe (itabuyo) chongolo kwa ŵandu, ni ŵatulwisye soni chilekanganyo (chachikulekanganya chilikati cha yakuona ni yaunami jajili Qur’an). Chisimu ŵandu ŵakanile ma Ȃya ga Allah akwete kupata ilagasyo yaukali nnope, sano Allah ni Jwamachili gakupunda, Nkuusya mbusyo syakupoteka kusyene.');
INSERT INTO "silika" VALUES(3,5,'3:5','Chisimu kwa Allah ngachikasasisika chindu chilichose chapasi ni kwinani.');
INSERT INTO "silika" VALUES(3,6,'3:6','Jwalakwe ni Ajula jwaakumpanjilaga kaoneche kenu n''di n’nyitumbo yaakutiji pakusaka (kuti m''be ŵalume kapena ŵakongwe, ŵaswela kapena ŵapiliu, ŵalewu kapena ŵajipi, ŵakwimbala kapena ŵachilenje). Pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(3,7,'3:7','Jwalakwe ni Ajula jwantuluchiisye chitabu (cha Qur’ani), mwalakwemo mwana ma Ȃya gakupikanika chenene, gele ni chitepo chachitabu (mugagonile malamusi), nambo (ma Ȃya) gane gangamanyika mate gakwe gasyesyene (mpela kundanda kwa sura sine), sano ŵandu ŵamuli m’mitima mwao mwana kupombotala (kwakuleka yakuona), ni akugakuyaga (ma Ȃya) gangamanyika mate gakwego, pakusachilila fitina (jakwawatanganyichisya ŵandu nganisyo) ni kusachilila kumanyilila mate gakwe gasyesyene. Nipangali ŵaakumanyililaga mate gakwe chenene ikaŵeje Allah. Sano ŵatiŵiile mu umanyilisi akuŵechetaga kuti: “Tujikulupilile (Qur’aniji), josope jene jityochele kwa M’mbuje gwetu (Allah). Ni pangali ŵaakukumbuchilaga (ni kwakamuchisya kumbuchilako), ikaŵeje ŵandu ŵaakwete lunda.”');
INSERT INTO "silika" VALUES(3,8,'3:8','(Ŵatiŵiile mu umanyilisio akasatiji): “Ambuje ŵetu! Ngasin''jipombotasya mitima jetu panyuma pakutujongola, tupani ukoto wakuumila Kukwenu, chisimu Mmwejo ni ŵan''di Ŵakupeleka kwannope.”');
INSERT INTO "silika" VALUES(3,9,'3:9','“Ambuje ŵetu! Chisimutu Mmwe ni Nkwasonganganya ŵandu pa Lisiku lyangali kaichila ya lyalakwelyo, chisimu Allah jwangakasa chilanga.”');
INSERT INTO "silika" VALUES(3,10,'3:10','Chisimu ŵandu ŵakanile, ngasichija kwakamuchisya chipanje chao atamose ŵanache ŵao chilichose mu (ipotesi) yakuumila kwa Allah, soni ŵanganyao ni sasu sya ku Moto.');
INSERT INTO "silika" VALUES(3,11,'3:11','(Nyendele ja makafili galeloga jili) chisawu nyendele ja ŵandu ŵa Firiauna ni aŵala ŵapaujo pao, ŵakaaniile imanyilo Yetu, basi Allah ŵaakwembekenye ligongo lya sambi syao, sano Allah ni Jwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(3,12,'3:12','Mwatagulilani aŵala ŵakanile kuti: “Pangakaŵapa chinkomboledwe (pa ngondo), ni chinchisonganganyisidwa wa ku Jahannama, kaje kusakala liuto lyagusama (lyan''ditandichile)!”');
INSERT INTO "silika" VALUES(3,13,'3:13','Yakuonaonape, kwaliji kwana kukwenu chimanyilo (cha chikulupusyo cha Allah) m’mikutula jiŵili jajasimangene (pa ngondo ja Badri), nkutula ŵine waputaga ngondo petala lya Allah sano wineo waliji wachikafili, (makafiligo) galiji nkwaona (Asilamuo kuti ali ŵajinji) pakulandanya ni muŵaŵelele ŵanganyao winji wakaŵili pa kulola kwameso (kutendaga nambo Asilamuo ali ŵapanandi). Sano Allah akun''dimbanganyaga ni chikulupusyo Chakwe jwansachile, chisimu mu yalakweyo mwana kuchetelela kusyene kwa ŵakwete meso (gakulolechesya mwa umanyilisi).');
INSERT INTO "silika" VALUES(3,14,'3:14','Kusalalisidwe ku ŵandu kuinonyela isako (yaumi wa duniya) kutandila achimmasyeto, ŵanache, ni maunjili-maunjili ga golodi ni silifa, ni mahachi geŵichidwe imanyilo yanti gamanyicheje (kululila), ni ilango, kwisa soni mbeju. Yalakweyo ni yakusengwasya umi wa duniya, sano Allah ni jwakwete kwakuwujila kwambone.');
INSERT INTO "silika" VALUES(3,15,'3:15','Jilani: “Ana nansalile yambone nnope kumpunda yalakweyo? Ŵandu ŵan’jogwepe (Allah), akwete kupata kwa M’mbuje gwao (Allah) Matimbe ga ku Mbepo, gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, ni (kupata) achiŵammakwao ŵaswejesyedwe, ni chinonyelo chakuumila kwa Allah, sano Allah ni Jwakwalolechesya achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(3,16,'3:16','Aŵala ŵakutiji: “Ambuje ŵetu! Chisimutu uwwe tukulupilile, basi tukululuchilani sambi syetu ni ntuŵambasye ku ilagasyo ya Moto.”');
INSERT INTO "silika" VALUES(3,17,'3:17','Ŵakupilila, ŵakuŵecheta yakuona, ŵakunda malamusi, ŵakutola, soni ŵakuŵenda chikululuko kumasikusiku.');
INSERT INTO "silika" VALUES(3,18,'3:18','Allah akuŵichila umboni wanti pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, soni Achimalaika ni (ŵandu) ŵakwete umanyilisi (akuŵichila nao wele umboniwu). (Jwalakwe ndaŵi syosope ni) Nkusimika chilungamiko. Pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(3,19,'3:19','Chisimu naga dini (jakuona) pameso pa Allah ni Usilamu. Ni nganatindana aŵala ŵana kupedwaga chitabu (Ayuda ni Akilisito pa dini syao), ikaŵeje panyuma pakwaichilila umanyilisi (wa dini jakuona), (ŵatindene) ligongo lya litima lyalyaliji chilikati chao, sano jwachakane ma Ȃya ga Allah, basi (tachim''balanga ni kun''dipila), chisimu Allah ni Jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(3,20,'3:20','Sano naga achikanganaga namwe, basi jilani: “Najipeleche ngope jangu kwa Allah pamo ni ŵandu ŵanguyiye.” Soni jilani kwa aŵala ŵana kupedwaga chitabu (Ayuda ni Akilisito) kwisa soni ŵangapakombola kulemba ni kuŵalanga (Ŵarabu) kuti: “Ana nsingwiche (ni kwinjila mu Usilamu)?” Sano naga asingwiche nikuti pamasile paajongweche, nambo naga akugalauka (kuleka Usilamu), basi masengo genu ni kulungusya (utenga), sano Allah ni Jwakwalolechesya achikapolo (Ŵakwe). ');
INSERT INTO "silika" VALUES(3,21,'3:21','Chisimu aŵala ŵaakukanila ma Ȃya ga Allah, ni kuulagaga Achimitume mwangali ligongo, ni kuulagaga soni mu ŵanduŵa aŵala ŵaakulamulaga yakulungamika, mwatagulilani ya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(3,22,'3:22','Ŵele ŵanganyao ni ŵagajonasiche masengo gao pa duniya pano ni ku Akhera kwakwe, soni ngasakola ŵakwakamuchisya.');
INSERT INTO "silika" VALUES(3,23,'3:23','Ana ngankwaona aŵala ŵapedwile chipande cha chitabu ŵaakuŵilanjidwa ku chitabu cha Allah kuti chaajilanye chilikati chao, kaneka likuga line mwa jamanjao liigalauchila kumbali aku lichisyululaga!');
INSERT INTO "silika" VALUES(3,24,'3:24','Yalakweyotu ligongo lyanti ŵanganyao akasatiji: “Moto ngauja kutukwaya ikaŵeje moŵa gakuŵalanjikape.” Soni yanyenjile mu dini jaomo (yaunami) yaaŵele ali nkusepelela.');
INSERT INTO "silika" VALUES(3,25,'3:25','Ana ichiŵa uli ndema jituchasonganganya pa Lisiku lyangali kaichila ya lyalakwelyo? Soni mundu jwalijose tachilipidwa mwakwana pa yawapanganyisye, soni ŵanyao ngasatendedwa lupuso.');
INSERT INTO "silika" VALUES(3,26,'3:26','Jilani: “E Allah! Mwasyene uchimwene wosope, nkasimum''bwecha uchimwene jwamunsachile ni kum''bula uchimweneo jwamunsachile, soni nkunchimbichisyaga jwamunsachile ni kunchembulusya jwamunsachile, umbone wosope uli M’myala Mwenu, chisimu Mmwe pa chindu chilichose m''bele Ŵakombola.”');
INSERT INTO "silika" VALUES(3,27,'3:27','“Nkasachijinjisyaga chilo m''muusi (ni kuleupa muusiwo kwipipa chilo mpela ndema jachitukuta), ni nkuujinjisyaga soni muusi m''chilo (nikuleupa chilocho kwipipa muusi mpela ndema jakusisima). Soni nkasakoposyaga chaumi kuumila mu chachiwe, nikoposya chachiwe kuumila mu chaumi (mpela nguku kopoka mwindanda lindanda kopoka mu nguku). Ni nkumpaga lisiki jwamunsachile lyangaŵalanjika.”');
INSERT INTO "silika" VALUES(3,28,'3:28','Ŵakulupilila akatomana nago ulongo makafili kwaleka ŵakulupilila (achin''jao), basi jwachatende yele nikuti ngasakola chilichose chakuuma kwa Allah, ikaŵeje (kutomana nago ulongo wapachanyape) pakuliigosa ŵanganyammwe ku yakusakala yao yankwiijogopa, sano Allah ni akuntetela ya Nsyenejo (pa ilagasyo Yakwe), soni kwa Allah pe ni kwakuujila.');
INSERT INTO "silika" VALUES(3,29,'3:29','Jilani: “Kanga chinyyisise yaili mu idali mwenu kapena kuilochesya papaswela Allah tayimanyepe, soni akumanyilila yaili kumawunde ni yaili petaka, sano Allah pa chindu chilichose aŵele Jwakombola.”');
INSERT INTO "silika" VALUES(3,30,'3:30','Lisiku lyatachiisimana mundu jwalijose yambone yajwapanganyisye ili isonganganyisyidwe, ni yakusakala yakwe yajwapanganyisye, ni juchilajila yanti pakaŵe pana nkuuli welewu chilikati cha yakusakalayo ni jwalakwejo. Sano Allah ni akuntetela ya Nsyenejo (pa ilagasyo Yakwe), soni Allah ni Mpole kwa achikapolo (Ŵakwe).');
INSERT INTO "silika" VALUES(3,31,'3:31','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Nam’baga jenumanja nkunnonyela Allah, basi munguyani; Allah channonyele soni chankululuchile sambi syenu, sano Allah ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(3,32,'3:32','Jilani: “Mumpikanilani Allah ni Ntenga صلى الله عليه وسلم.” Nambo naga akugalauka, basi (chachipedwa ipotesi) chisimu Allah jwangaganonyela makafili.');
INSERT INTO "silika" VALUES(3,33,'3:33','Chisimu Allah ŵansagwile Adam, Nuhu, liŵasa lya Ibrahima ni liŵasa lya Imrani pa iwumbe yosope.');
INSERT INTO "silika" VALUES(3,34,'3:34','(Ŵaliji) ŵanache; (ŵakupagwana) ŵane kuumila mwa ŵane chilikati chao, sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(3,35,'3:35','(Kumbuchilani) katema kaŵaŵechete ŵankwao Imrani (ŵali mamaŵao Maryam jwali mamagwe Isa) kuti: “Ambuje ŵangu! Chisimu une nantaŵiile nasili janti; (mwanache) jwali nchitumbo changuchi chachiŵa pepesa (lyakutumichila kunsikiti wa Baitul Muqaddas) basi mbochelani (nasili janguji), chisimu Mmwejo niŵan''di Ŵakupikanichisya, Ŵakumanyilila nnope.”');
INSERT INTO "silika" VALUES(3,36,'3:36','Basi ndema jaŵam''beleche ŵatite: “Ambuje ŵangu! Unetu mbeleche jwankongwe!” - Sano Allah ni Nkuchimanya chenene chaŵaŵelechecho - “Nambo jwannume (junyembecheeyeeje kuti chimbeleche) nganijuŵa mpela jwankongwe (jwambeleche, ngati jwankongwe jwakuujilila machili soni ngaŵa nkutumichila mwakwanila ku nsikiti). Sano une nampele lina lyakuti ‘Maryam’, sonitu une ngun''juya jwalakweju ni ŵanache ŵakwe Kukwenu kutyochela ku shetani jwakuŵinjidwa.”');
INSERT INTO "silika" VALUES(3,37,'3:37','Basi (Allah) M’mbujegwe ŵampochele kapochele kambone, soni ni ŵankusisye kakusye kambone, ni ŵantesile Zakariya kuŵa nkunchengeta jwakwe. Ndaŵi ni katema kaŵaliji Zakariya ali nkun''jinjilila (Maryamujo) ku chigunguli cha mu nsikitimo, ŵaliji nkuisimana kwa jwalakwejo yakulya. Ŵatiji (pakum''busya): “E Maryam! Kwapi nkuipata ayi?” Jwalakwe jwatiji: “Yalakweyi yikutyochela kwa Allah. Chisimu Allah akumpaga lisiki jwansachile mwangaŵalanjila.”');
INSERT INTO "silika" VALUES(3,38,'3:38','Papopo Zakariya jwaaŵendile Ambujegwe (Allah) kuti: “Ambuje ŵangu! Mumbani kuumila Kukwenu mwanache jwambone, chisimu Mmwejo ni Ŵakupikana kuŵenda.”');
INSERT INTO "silika" VALUES(3,39,'3:39','Sambano jwalakwe ali ajimi kuswali ku chigunguli cha mu nsikiti, Achimalaika ŵam''bilasile (pakunsalila kuti): “Chisimu Allah akumpa abali jakusengwasya ja (kuti chim''beleche mwanache lina lyakwe) Yahaya, juchachigombelechesya (ya ntume juchachipagwa ni) liloŵe lyakuumila kwa Allah (lyakuti ‘M''be! Nikuŵa’ jwali Isa) ni chachiŵa soni (Yahayajo) jwakuchimbichika, jwakuligosa ku yansese soni (chachiŵa) Ntume jwa mwa ŵandu ŵambone.”');
INSERT INTO "silika" VALUES(3,40,'3:40','Jwalakwe jwatite: “Ambuje ŵangu! Chingole chantiuli mwanache kutendaga pamasile paumbichilile uchekulu, nombe soni ŵankwangu ŵapalale ?” (Lilaika) lyatite: “Mwele (nimuchiiŵele); Allah akutendaga yasachile.”');
INSERT INTO "silika" VALUES(3,41,'3:41','Jwalakwe jwatite: “Ambuje ŵangu! Mumbichilani chimanyilo (chachiimanyisye yanti ŵankwangu ajigele chilu)!” (Lilaika) lyatite: “Chimanyilo chenu nichanti ngasinkombola kwaŵechetekasya ŵandu moŵa gakwana gatatu ikaŵeje mwagamba kulanjilape basi. Ni mwaakolanjeje Ambuje ŵenu kwejinji soni mwaaswejesyeje (pakuswali) kwigulo ni kundaŵi.”');
INSERT INTO "silika" VALUES(3,42,'3:42','Soni (kumbuchilani) katema kaŵaŵechete Achimalaika kuti: “E mmwe Maryam! Chisimutu Allah ansagwile mmwejo, ni answejesye, soni ansagwile pa uchimbichimbi kwaapunda achakongwe ŵamitundu josope jaŵandu.”');
INSERT INTO "silika" VALUES(3,43,'3:43','“E mmwe Maryam! Linandiyani kwa Ambuje ŵenu, soni nsujuduji ni kwinamaga (kuswali) pamo ni akwinama.”');
INSERT INTO "silika" VALUES(3,44,'3:44','Syalakwesi ni sine mwa abali syakusisika syatukun''juwulila, ni nganim''ba nao ŵanganyao ndema jaŵaponyaga yakulembela yao (m''mesi kuŵa ndoto janti amanyilile) kuti ŵani mwa jamanjao ŵachanchengete Maryam, soni nganim''ba nao ndema jaŵaliji nkukangana.');
INSERT INTO "silika" VALUES(3,45,'3:45','(Kumbuchilani) katema kaŵaŵechete Achimalaika kuti: “E mmwe Maryam! Chisimutu Allah akuntagulila abali jakusengwasya ja (kuti chim''beleche mwanache ni) liloŵe lyakuumila Kukwakwe (mwangali kusimangana ni ŵalume, nambo pagamba kunsalila kuti: ‘ŵelekani’ mmwe ni kuŵeleka), lina lyakwe chachiŵa Masihi Isa mwanache jwa Maryam, jwakuchimbichika pa duniya ni ku Akhera kwakwe, soni ni jumpepe mu ŵakuŵandichisyidwa (kwa Allah).”');
INSERT INTO "silika" VALUES(3,46,'3:46','“Soni chachiŵecheta ni ŵandu ali nnuuli kwisa soni ali akusile, ni chachiŵa mu ŵandu ŵambone.”');
INSERT INTO "silika" VALUES(3,47,'3:47','(Maryam) jwatite: “Ambuje ŵangu! Chingole chantiuli mwanache kutendaga nganangwayeje mundu jwalijose (jwannume)?” (Lilaika) lyatite: “Mwele (nimuchiiŵele), Allah akasagumba chasachile. Pati asachile chilichose (kuti chipatikane), basi agambaga kuchisalila kuti ‘M''be!’ nichikasaŵaga.”');
INSERT INTO "silika" VALUES(3,48,'3:48','“Soni chachin''jiganya kulemba ni lunda lwakusokoka, kwisa soni Taurat ni Injil.”');
INSERT INTO "silika" VALUES(3,49,'3:49','“Soni (chachiŵa) Ntenga (juchachitumisyidwa) ku ŵanache ŵa Israila (ni chachiŵaga ali nkwasalila yanti): ‘Chisimutu une nam’bichilile ni chisimosimo kuumila kwa M''mbuje gwenu, mwanti chingombolecheje kun''gumbila kutyochela mwilongo chitusitusi cha chijuni nikuchipepelela, basi nikusyuka chijuni chisyesyene mu lisosa lya Allah, ni chimbosyeje ŵangalola kwachipago, ŵachinaŵa, ni kusyusyaga ŵawe mu lisosa lya Allah, chinansalileje soni yachin''dyeje ni yachinsunjeje m’majumba mwenu. Chisimu mu yalakweyi muŵele mwana chimanyilo kukwenu nam''baga jenumanja ŵakulupilila (kusyene).’');
INSERT INTO "silika" VALUES(3,50,'3:50','‘Soni (chimbe) jwakwitichisya yaili paujo pangu yakutyochela m''Taurat, nikuti soni nankundisye yine yayakanyisyidwe kukwenu, ni nam’bichiiliile ni chisimosimo kuumila kwa M''mbuje gwenu (kuŵa chimanyilo cha utume wangu), basi mun''jogopani Allah ni kungunda une.’');
INSERT INTO "silika" VALUES(3,51,'3:51','‘Chisimu Allah ni M''mbuje gwangu soni M''mbuje gwenu, basi mun''galagatilani Jwalakwejo, lyalakweli nilitala lyagoloka.”');
INSERT INTO "silika" VALUES(3,52,'3:52','Nambo ndema jaŵauweni Isa mwa jamanjao ukafili (kuti ukwendelechela) ŵatite: “Ana ŵani ŵachaŵe akungamuchisya ŵangu (pa kuŵilanjila) kwa Allah?” Ŵakulijiganya ŵakwe ŵatite: “Uwwetu ni ŵatuli ŵakunkamuchisya Allah, tunkulupilile Allah, soni ŵichilani umboni wanti uwwe chisimu chene tuli Asilamu.”');
INSERT INTO "silika" VALUES(3,53,'3:53','“Ambuje ŵetu! Uwwe tuikulupilile yantulwisye, ni tunkuyiye Ntenga, basi tulembani pamo ni akuŵichila umboni (pa ya ujika Wenu).”');
INSERT INTO "silika" VALUES(3,54,'3:54','Ni ŵatesile malindi (Ayuda gakusaka kum''bulaga Isa), nambo Allah ŵalepelekasyisye malindigo (pakun''jigala Isa kujanajo kwinani nkanaiche mwakunkamula), sano Allah ni Jwambone nnope pakulepelekasya malindi ga amalindi.');
INSERT INTO "silika" VALUES(3,55,'3:55','(Kumbuchilani) katema kajwaŵechete Allah kuti: “E mmwe Isa! Chisimutu Une chinan''jigale ni kunkwesya Kukwangu, soni chinanswejesye ku ŵandu makafili, ni chinaatende ŵandu ŵankuyiye kuŵa pachanya pa ŵandu makafili mpaka lisiku lya Kiyama. Kaneko Kukwangu nikwakuujila kwenu ni tinjijiilanya chilikati chenu mu ichindu yamwaliji nkulekangana.”');
INSERT INTO "silika" VALUES(3,56,'3:56','“Sano aŵala ŵaakanile, basi chinalagasye ni ilagasyo yaukali nnope pa duniya pano ni ku Akhera kwakwe, soni ngasakola ŵakwakamuchisya.”');
INSERT INTO "silika" VALUES(3,57,'3:57','Nambo aŵala ŵaakulupilile ni kutendaga yambone, basi chachalipila malipilo gao mwakwana, sano Allah ni jwangaanonyela ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,58,'3:58','Ayi yatukunsoomelayi (mmwe Muhammadi صلى الله عليه وسلم) gaŵele ma Ȃya ni chikumbusyo chalunda lwakusokoka.');
INSERT INTO "silika" VALUES(3,59,'3:59','Chisimu chilandanyo cha Isa kwa Allah chiŵele chisawu Adam; ŵan''gumbile kuumila mwitaka kaneko niŵantesile kuti: “Ŵani (mundu!)” Basi niŵaŵele.');
INSERT INTO "silika" VALUES(3,60,'3:60','(Yalakweyi ni) yakuona yakutyochela kwa Ambuje ŵenu (Allah), sano ngasim''ba mwa ŵakaichila.');
INSERT INTO "silika" VALUES(3,61,'3:61','Sano ŵachakangane nomwe mu jele nganiji panyuma pa umanyilisi waum’bichililewu, basi jilani: “Ikani, twaŵilanje ŵanache ŵetu ni ŵanache ŵenu, achimmasyeto ŵetu ni achimmasyeto ŵenu, twachimisyewe ni mwachimisyene ŵakwe, kaneko ni tuŵende (kwa Allah) mwakuteteŵala, basi ni tugatende malweso ga Allah kuti gaŵe kwa ŵali ŵaunami (mwetuwe).”');
INSERT INTO "silika" VALUES(3,62,'3:62','Chisimu jalakweji ni ngani jakuonaonape, ni pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Allah, soni chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(3,63,'3:63','Nambo naga akugalauka (kuleka yele yakuonayi), basi (tachapa ipotesi), chisimu Allah ni Jwaakwamanya chenene akuwatanganya.');
INSERT INTO "silika" VALUES(3,64,'3:64','Jilani: “E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Ikani kuliloŵe lyakulungamika (lyachitulandane) chilikati chetuwe ni jenumanja, lyanti ‘Tukan''galagatilaga (jwinepe) ikaŵeje Allah, ni tukam''bwanganyaga ni chilichose, soni ŵane mwetuwe akatenda ŵane kuŵa milungu kunneka Allah.” Nambo naga akugalauka (kuleka lyele liloŵeli), basi jilani: “Ŵichilani umboni wanti uwwe chisimu chene tuli Asilamu (ŵakulipeleka kwa Allah).”');
INSERT INTO "silika" VALUES(3,65,'3:65','E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Anauli nkukanilana pa ya Ibrahima, kutendaga (itabu ya) Taurat ni Injil nganiitulusyidwa ikaŵeje panyuma pakwe, ana ŵangali lunda?');
INSERT INTO "silika" VALUES(3,66,'3:66','Nnole! Ŵanganyammwe ni aŵalaŵala ŵamwakaanilene pa ichindu yamwakwete nayo umanyilisi, nambi uli (sano) nkukaanilana pa ichindu yanganinkola nayo umanyilisi, sano Allah ni Jwaakumanyilila nambo ŵanganyammwe ngankumanyilila.');
INSERT INTO "silika" VALUES(3,67,'3:67','Ibrahima nganaŵa Myuda kapena Mkilisito, nambo ŵaliji jwakongoka (pakulekangana nasyo dini syepombotale, ŵaliji soni) Nsilamu (jwakulipeleka); soni nganaŵa mwa akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(3,68,'3:68','Chisimu ŵandu ŵali ŵakuŵajilwa kulumbikana najo Ibrahima, ni aŵala ŵaŵankuyiye (pa ndema ja utume wakwe), ni aju Ntumeju (Muhammadi صلى الله عليه وسلم), kwisa soni ŵandu ŵaakulupilile (pakunda Usilamu), sano Allah ni Nkwaagosa ŵakulupilila.');
INSERT INTO "silika" VALUES(3,69,'3:69','Likuga line lya ŵandu ŵaŵapedwile chitabu (Ayuda ni Akilisito), likasasachililaga lili linsokonesye (pakunkoposya mu Usilamu), ni pangali ŵampaka ŵasokonesye ikaŵeje achimisyene, nambo ngakumanyilila.');
INSERT INTO "silika" VALUES(3,70,'3:70','E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Ligongo chichi nkugakana ma Ȃya ga Allah (Qur’an) kutendaga jenumanja nkutendelaga umboni (kuti gakuonaonape)?');
INSERT INTO "silika" VALUES(3,71,'3:71','E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Ligongo chichi nkuwanganyaga yakuona ni yaunami ni kusisaga yakuona kutendaga jenumanja nchimanyililaga?');
INSERT INTO "silika" VALUES(3,72,'3:72','Soni likuga line lya ŵandu ŵaŵapedwile chitabu lyatite (pakwasalila achimijao): “Ikulupililani yaitulusyidwe kwa ŵandu ŵakulupilila (Asilamu) kundanda kwa muusi (kundaŵi) ni n’nyikane kumbesi kwakwe (kwigulo) kuti mwine ŵanganyao chaujile (munyuma kuleka Usilamu).”');
INSERT INTO "silika" VALUES(3,73,'3:73','“Soni nkasimunkulupilila jwalijose ikaŵeje jwaakuyiye dini jenu (ja Chiyuda kapena Chikilisito).” Jilani: “Chisimu naga chongoko chisyesyene ni chongoko cha Allah basi.” (Ayuda ni Akilisitowo akasatiji sikati jao: “Nkasinkulupilila) ataakwika mwapedwile jwalijose yakulandana ni yampedwile ŵanganyammwe, kapena kuti ataakwika mwan''gomeche kwa M''mbuje gwenu.” Jilani: “Chisimu umbone wosope uli m’Myala mwa Allah akasampaga jwansachile, sano Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(3,74,'3:74','Akunsagulilaga ukoto Wakwe jwansachile, soni Allah ni Nsyene umbone wekulungwa.');
INSERT INTO "silika" VALUES(3,75,'3:75','Soni mu ŵandu ŵaŵapedwile chitabu mwana ŵanti naga n''di mwasunjisye maunjili-maunjili ga chipanje mpaka am''buchisye, ni mwana ŵane mwa jamanjao ŵanti naga n''di mwasunjisye Dinari (mbiya jimpepe), nganaŵa am''buchisye ni ikaŵeje n''di mwaajimilile (pa kwaŵilasya mwa kwakwekweteka). Yalakweyo ligongo lyanti ŵanganyao akasatiji: “Pangali litala lililyose lyakutugambila uwwe ligongo lya (kwaaliila) ajinga (ŵanganituŵa nao imo mu dini ligongo Allah atukundisye).” Ni akunnambuchisyaga Allah aku achimanyililaga (kuti akulambusya).');
INSERT INTO "silika" VALUES(3,76,'3:76','(Ngwamba!) Nambo jwaakukwanisya chilanga chakwe, ni kuŵaga ali nkun''jogopa (Allah pakuligosa ku ilemwa, jwele nijuchaŵe jwakunonyeledwa ni Allah), basi Allah chisimu chene akwanonyela ŵakun''jogopa (Jwalakwe).');
INSERT INTO "silika" VALUES(3,77,'3:77','Chisimu ŵandu ŵaakusagula (ya duniya) yantengo wannono pakusumanya ni chilanga cha Allah kwisa soni kulumbila kwao, ŵanyao ngasakola liunjili lililyose (lya yambone) ku Akhera, soni Allah ngaaja kwaŵechetekasya, ata nikwalola kose ngaaja kwalola lisiku lya Kiyama, soni ngaaja kwaswejesya. Ni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(3,78,'3:78','Soni mwa jamanjao chisimu chene mwana likuga line likasapindaga ndimi syao pakusooma chitabu kuti nyyiganichisyeje (yaakusoomayo) kuti ya m’chitabu, kutendaga nganiiŵa ya m’chitabu. Ni akutiji: “Yalakweyi iwumile kwa Allah.” Kutendaga nganiyiuma kwa Allah. Ni akunnambuchisyaga Allah aku achimanyililaga (kuti akulambusya).');
INSERT INTO "silika" VALUES(3,79,'3:79','Nganiiŵa yakuŵajilwa kwa mundu (mpela Isa mwanache jwa Maryam) juŵapedwile chitabu, umanyilisi wakwilanyichisya ni utume, panyuma pakwe ni kwatendaga ŵandu kuti: “M''beje ŵakungalagatila une mmalo mwa Allah.” Nambo (yaŵasalilaga yakwe niyanti): “M''be jenumanja ŵakutumichila Ambuje Nnungu (mwalunda ni umanyilisi) ligongo lyanti nkwiganyaga chitabu soni ligongo lyanti nkuchisoomaga (chitabucho).” ');
INSERT INTO "silika" VALUES(3,80,'3:80','Soni nganaŵa annamulile yanti mwatendeje Achimalaika ni Achimitume kuŵa milungu. Ana mpaka annamulile ya ukafili panyuma panti n''di Asilamu? ');
INSERT INTO "silika" VALUES(3,81,'3:81','Soni (kumbuchilani) katema kajwataŵene Allah chilanga ni Achimitume (pakwatenda kuti): “Chisimu mulimose mwachinampele chitabu ni lunda lwakusokoka (nikuŵa ni umanyilisi kwisa soni uchimbichimbi wakupunda), kaneko ni kwisa kum’bichilila Ntenga jwakwitichisya yan''di nayo, ichiŵajilwa kukwenu kunkulupilila ni kunkamuchisya.” (Jwalakwe) jwaawusisye kuti: “Ana nkundile ni kujigala pa yalakweyi chilanga Changuchi?” Ŵanganyao ŵatite: “Tukundile.” (Jwalakwe) jwatite: “Basi tendelani umboni (yachilangachi), nombe Une ndinamwe pakutendela umboni.”');
INSERT INTO "silika" VALUES(3,82,'3:82','Nambo ŵachagalauche panyuma pa yele, basi ŵanyao niŵali ŵakunyosya.');
INSERT INTO "silika" VALUES(3,83,'3:83','Ana akusaka dini jinepe jangaŵaga ja Allah, kutendaga chilichose cha kumawunde ni petaka chikwitika Kukwakwe mwakusaka ni mwangasaka? Soni Kukwakwe chachiuchisidwa.');
INSERT INTO "silika" VALUES(3,84,'3:84','Jilani: “Tukulupilile mwa Allah, ni yaitulusyidwe kukwetu (jajili Qur’an) ni yayatulusyidwe kwa Ibrahima, Ismaila, Isihaka, Yaakubu ni isukulu yakwe, ni yaŵapedwile Musa ni Isa, ni Achimitume kuumila kwa Ambuje ŵao, ngatukulekanganya chilikati cha jumpepe mwa ŵanganyao, soni uwwe Kukwakwe tukulipeleka.”');
INSERT INTO "silika" VALUES(3,85,'3:85','Soni jwachasache dini jinepe jangaŵaga ja Usilamu, nikuti ngasijipocheledwa kuumila kukwakwe, soni jwalakwejo ku Akhera tachiŵa mu ŵakwasika. ');
INSERT INTO "silika" VALUES(3,86,'3:86','Ana Allah mpaka ŵajongole chinauli ŵandu ŵakanile panyuma paakulupilila kwao ni kutendela umboni yanti chisimu Ntenga (Muhammadi صلى الله عليه وسلم) jwakuonaonape, ni kwaichilila soni ilosyo yakuonechela palangulangu? Sano Allah jwangaŵa nkwajongola ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,87,'3:87','Ŵanganyao malipilo gao niganti; chisimu malweso ga Allah, Achimalaika ni ŵandu wosope gali pa jamanjao.');
INSERT INTO "silika" VALUES(3,88,'3:88','Mwele (mmalweso ga ku Motogo) chakaŵe ŵandamo, ilagasyo ngaija kupungusyidwa kwa jamanjao, soni ŵanyao ngaaja kupedwa lipesa.');
INSERT INTO "silika" VALUES(3,89,'3:89','Ikaŵeje aŵala ŵatesile toba panyuma pa yalakweyo, ni kwilanya (itendo yao), basi chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(3,90,'3:90','Chisimu aŵala ŵakanile panyuma pa chikulupi chao, kaneko nikonjechesya soni kanila, toba jao ngasijipocheledwa ata panandi, soni ŵanyao niŵali ŵakusokonechela.');
INSERT INTO "silika" VALUES(3,91,'3:91','Chisimu aŵala ŵakanile ni kwikanila kuwa ali makafili, nikuti ngasijipocheledwa kutyochela kwa jwalijose mwa jamanjao golodi jagumbala chilambo cha pasi atamuno ali aliwombolele najo, ŵanganyao akwete kupata ilagasyo yakupoteka nnope, soni ngasakola ŵakwakamuchisya. ');
INSERT INTO "silika" VALUES(3,92,'3:92','Nganim''ba mpatile umbone (usyesyene) mpaka nkaatoole mu yankuinonyela, sano mu chilichose chachintole, basi chisimu chalakwecho Allah akuchimanya chenene.');
INSERT INTO "silika" VALUES(3,93,'3:93','Yakulya yosope yaliji ya halaal ku ŵanache ŵa Israila, ikaŵeje yaŵalitendele kuŵa ya haraam Israilajo nsyene Taurat nkanijitulusyidwe. Jilani: “Basi ikani najo Taurat ni n''jisoome naga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(3,94,'3:94','Sano juchansepele Allah unami panyuma pa yalakweyi, basi ŵanganyao niŵachaŵe ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,95,'3:95','Jilani: “Allah aŵechete yakuona, basi kuyani dini ja Ibrahima jwakongoka soni nganaŵa mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(3,96,'3:96','Chisimu naga nyumba jandanda jajaŵichidwe kwa ŵandu (kuŵa jagalagatilamo Allah) ni jajili pa Bakkah (ku Maaka), jaupile soni (jiŵele) chongolo ku iwumbe yosope (ŵandu ni majini).');
INSERT INTO "silika" VALUES(3,97,'3:97','Mwalakwemo mwana imanyilo yakuonechela (yakulosya kuchimbichika kwakwe ni uchikala wakwe), mpela pamalo paŵaliji nkwima Ibrahima (ndema jaŵaliji nkutaŵa Likaabalyo), soni jwachajijinjile (jele nyumbaji) chaŵe mu ntunjelele. Sano kwa lilamusi lya Allah iŵele yakusosekwa kwa ŵandu kuja kutenda Hija ku nyumbajo ŵampaka akombole kupata litala lyakwaulila kwele, nambo jwatakane (kujakutenda Hija mwangali chipaka chisyesyene nikuti chaŵe kafili) basi chisimu Allah ni Jwankwanekwane jwangalajila iwumbe Yakwe.');
INSERT INTO "silika" VALUES(3,98,'3:98','Jilani: “E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Ligongo chichi nkukanila ma Ȃya ga Allah kutendaga Allah aŵele Mboni pa ichindu yankutenda?”');
INSERT INTO "silika" VALUES(3,99,'3:99','Jilani: “E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Ligongo chichi nkwasiŵilila ŵandu ŵakulupilile kuti akakuya litala lya Allah (wauli Usilamu)? Nkulisosela lyalakwelyo kupombotala (kuti lionecheje mpela lyakusakala) kutendaga jenumanja nkuŵichila umboni (wanti Usilamu ni litala lyagoloka)? Sanotu Allah nganaŵa jwanti ni ngaimanya yankutenda.”');
INSERT INTO "silika" VALUES(3,100,'3:100','E jenumanja ŵankulupilile! Naga nkwika mwakulipikanila likuga line mu ŵandu ŵaŵapedwile chitabu, cham''buchisye ku ukafili panyuma pakulupilila kwenu.');
INSERT INTO "silika" VALUES(3,101,'3:101','Ana chim''be makafili chanti uli kutendaga ma Ȃya ga Allah gakusoomedwa kukwenu nombenajo Ntenga صلى الله عليه وسلم ali chilikati chenu? Sano jwachalikamulile chenene mwa Allah, nikuti pamasile paajongoleledwe kwitala lyagoloka.');
INSERT INTO "silika" VALUES(3,102,'3:102','E jenumanja ŵankulupilile! Mun''jogopani Allah kun''jogopa kusyesyene, soni nkaika mwa kuwa ikaŵeje n''di Asilamu.');
INSERT INTO "silika" VALUES(3,103,'3:103','Soni kamulisyani ŵanaose lukonji lwa Allah (jajili Qur’an) ni ngasinnekangana. Soni kumbuchilani chindimba cha Allah pa jenumanja; pandema jamwaliji ŵakuŵengana, basi ni jwalumbikenye chilikati cha mitima jenu mwanti mwa chindimba Chakwe mwasyuchile kuŵa ŵaulongo. Soni mwaliji mungulugulu mwalisimbo lya Moto, ni ŵan''jokwele mwalakwemo. Iyyoyopeyo ni yakutiji Allah pakusalichisya kukwenu ma Ȃya Gakwe kuti n''jongoche.');
INSERT INTO "silika" VALUES(3,104,'3:104','Soni lipatikane mwa jenumanja likuga lyakuŵilanjila ku umbone (Usilamu), ni lilamuleje (kutenda) yambone ni kanyaga yakusakala, sano ŵanganyao niŵachaŵe ŵakupunda.');
INSERT INTO "silika" VALUES(3,105,'3:105','Ni ngasim''ba chisawu aŵala ŵaŵagaŵikene ni kulekangana panyuma pa kwaichilila ilosyo yakuonechela palangulangu, soni ŵanganyao akwete kupata ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(3,106,'3:106','Lisiku lya sichiswejela ngope sine ni kupiilila ngope sine, sano aŵala ŵasichipiilila ngope syao (chachatenda kuti): “Ana mwakaaniile panyuma pakulupilila kwenu? Basi pasyani ilagasyo ligongo lya ayila yamwaliji nkuikana.”');
INSERT INTO "silika" VALUES(3,107,'3:107','Sano nambo aŵala ŵasiichiswejela ngope syao, basi tachiŵa mu ukoto wa Allah, ŵanyao mwalakwemo tachiŵa ŵandamo.');
INSERT INTO "silika" VALUES(3,108,'3:108','Gele ni ma Ȃya ga Allah gatukunsoomela mwakuonaonape, ni nganaŵa Allah jwakusaka kuitenda lupuso iwumbe (Yakwe).');
INSERT INTO "silika" VALUES(3,109,'3:109','Soni yosope yaili kumawunde ni yaili petaka nsyene jwakwe Allah, soni kwa Allah ni kwakuujila indu yosope.');
INSERT INTO "silika" VALUES(3,110,'3:110','Jenumanja (Asilamu) m''bele nkutula wambone nnope waukoposyedwe (pakusagulidwa chilikati cha mikutula josope kuti uŵe wakamuchisya) ku ŵandu, nkulamulaga (kutenda) yambone ni nkukanyaga yakusakala, soni ni nkukulupililaga mwa Allah. Mwanti ŵaŵapedwile chitabu (Ayuda ni Akilisito) ingaŵe ŵakulupilile (pakunda Usilamu), ingaliji yambone kukwao, ŵane mwa jamanjao ŵana chikulupi, nambo ŵajinjio ŵakunyosya.');
INSERT INTO "silika" VALUES(3,111,'3:111','Nganaŵa annagasisye ŵanganyao ikaŵeje ni ilagasyo (yannonope), soni naga ali atite ampute ngondo mpaka annosye migongo (kutiila), soni ŵanyao ngaŵa nkukulupusyidwa.');
INSERT INTO "silika" VALUES(3,112,'3:112','Kuŵichidwe pa jamanjao kuchembulusyidwa palipose paakupatikana ikaŵeje (naga ali alikamulile) ni lukonji lwa Allah (pakuya dini jakwe ja Usilamu) kapena lukonji lwa ŵandu, (pakamuchisyidwa ni ŵandu ŵane mu yakutenda yao, nambo pajika ngaŵa nkola machili). Ni aŵajilwe kupata gasabu ja kuumila kwa Allah, soni ni kuŵichiidwe pa jamanjao kulaga (kwangalekangana nako), yalakweyo ligongo lyanti ŵanganyao ŵaliji nkukanila ma Ȃya ga Allah, nikuŵaga soni ali nkuulaga Achimitume mwangali ligongo. Basi yeleyo ni ligongo lya kunyosya kwao soni ŵaliji ali nkusumba mipika.');
INSERT INTO "silika" VALUES(3,113,'3:113','Nganaŵa wosope ŵene kuti ŵakulandana, nkutula wine mu ŵaŵapedwile chitabu (Ayuda ni Akilisito) uŵele wakwimilichika (pa yakuona yakuya Usilamu panyuma pakusinguka kwao), akasasoomaga ma Ȃya ga Allah ndaŵi syaachilo aku achisujuduji (kuswali).');
INSERT INTO "silika" VALUES(3,114,'3:114','Akasakulupililaga mwa Allah ni Lisiku Lyambesi, ni akasalamulaga (kutenda) yambone nikanyaga yakusakala, soni ni akasatendaga chitema pa masengo gambone. Sano ŵanganyao ni ŵali mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(3,115,'3:115','Soni chilichose chachapanganye mu ichindu yambone basi ngaaja kwakana nacho (nambo chaakaalipile malipilo gambone). Sano Allah ni Jwaakwamanya chenene ŵakun''jogopa (Jwalakwe).');
INSERT INTO "silika" VALUES(3,116,'3:116','Chisimu aŵala ŵakanile, ngaŵa nkwakamuchisya chipanje chao atamuno ŵanache ŵao chilichose mu (ipotesi) yakuumila kwa Allah, soni ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(3,117,'3:117','Chilandanyo cha indu yaakutola pa awu umi wa duniya uno, chili chisawu mbungo jajili nkati mwakwe mwana kusisima kwakogoya jajipujiile n''gunda wa ŵandu ŵaalitendele lupuso achimisyene ni kuujonanga, ni nganiŵatenda Allah lupuso nambo achimisyene ni ŵaakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,118,'3:118','E jenumanja ŵankulupilile! Ngasimwatenda ŵanganaŵa mwa jenumanja kuŵa isyoŵe yakwisalila asili syenu, ngati ŵanyao ngaŵa nkuleka kulimbichila kutenda yakun''jonanga (naakailochesye pameso), ikwanonyelaga yaikunnagasyaga, umasile pauwonechele umagongo kuumila pakamwa pao, nambo soni yayikusisa mitima jao (nganisyo syakusakala) iŵele yekulungwa nnope. Pamasile patusalichisye ma Ȃya kukwenu nam''baga jenumanja ŵakumanyililaga.');
INSERT INTO "silika" VALUES(3,119,'3:119','Ha! Ana jenumanja ni ŵankwanonyela ŵanganyao kutendaga ŵanyao ali ngaakunnonyela atamuno kuti jenumanja nkuikulupililaga itabu yosope? Soni naga ali asimangene namwe akasatiji: “Tukulupilile.” Nambo naga ali pakasyepela akasannumilaga songa sya yala ligongo lya ukali. Jilani: “Wani ni ukali wenuo, chisimu Allah ni Nkuimanya chenene yayili m’mitima.”');
INSERT INTO "silika" VALUES(3,120,'3:120','Naga chili chim’bichilile (chindu) chambone chikasachasakalisyaga, nambo naga chili chimpatile chakusakala akasasengwaga nacho, nambo naga mpilile nikuŵa niwoga (wakun''jogopa Allah), nikuti malindi gao ngaŵa nkuntenda chilichose. Chisimu Allah ichindu yaakutenda ŵanganyao juŵele Jwaisyungula (kwaimanya chenene).');
INSERT INTO "silika" VALUES(3,121,'3:121','Soni (kumbuchilani mmwe Muhammadi صلى الله عليه وسلم) katema kamwaalaaŵiile kutyochela kwiŵasa lyenu kuti nkaalinganyichisye ŵakulupilila mauto gakumenyanila (ngondo ja Uhudi), sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(3,122,'3:122','Katema kagasachilile makuga gaŵili mwa jenumanja kuti ganyambe (ligongo lya woga nikutila muŵatilile achinakunaku), nambo Allah nijuŵaliji Jwaagagosa (makuga) ganagaŵiligo (mwanti ŵagasunjile kuti gakatila), sano ŵakulupilila ajegamileje kwa Allah pe basi.');
INSERT INTO "silika" VALUES(3,123,'3:123','Soni Allah chisimu chene ŵankulupwisye pa (ngondo ja) Badri kutendaga jenumanja n''di ŵangali machili (ligongo lya unandi wenu ni kupelembelwa ida yangondo), basi mun''jogopani Allah kuti m''be ŵakutogolela.');
INSERT INTO "silika" VALUES(3,124,'3:124','Katema kamwasalilaga ŵakulupilila kuti: “Ana ngaiŵa yakunkwana jenumanja kuti M''mbuje gwenu junkamuchisye ni Achimalaika ŵakwana masausande gatatu ŵakutulusyidwa?”');
INSERT INTO "silika" VALUES(3,125,'3:125','“Elo! Naga mpilile ni kuligosa (ku ilemwa) soni (amagongo ŵenuwo) ni kunyyichilila mwa chitema chao chalakwechi, M''mbuje gwenu junkamuchisye ni Achimalaika ŵakwana masausande nsano ŵana imanyilo.”');
INSERT INTO "silika" VALUES(3,126,'3:126','Ni nganaitenda yalakweyi Allah ikaŵeje kuti iŵe abali jakusengwasya kukwenu, ni kuti soni jipate kutulala mitima jenu ni yalakweyo, sano chikulupusyo nganichiŵa chityochele kwinepe ikaŵeje kwa Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(3,127,'3:127','(Ŵatesile yalakweyi) kuti akate chipande cha ŵandu makafili (pakwaulaga ŵane mwa ŵanganyao), kapena kwajigasya nchesela (ŵakusigalao) ni galauka yalayalape (pangapata yasachileje).');
INSERT INTO "silika" VALUES(3,128,'3:128','Nganinkola (mmwe Muhammadi صلى الله عليه وسلم) chilamusi chilichose (pa ŵanganyao), mwine (Allah) komboleka kwapochela toba jao (ni kwajongola panyuma pa ukafili wao) kapena kwapa ilagasyo, pakuti ŵanganyao chisimu chene ali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,129,'3:129','Soni yosope yaili kumawunde ni yaili petaka nsyene jwakwe Allah, akunkululuchilaga jwansachile ni kumpa ilagasyo jwansachile, sano Allah ni Jwaakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(3,130,'3:130','E jenumanja ŵankulupilile! Ngasin''dyaga (chipanje cha) katapila mwakonjechesya-jonjechesya, ni mun''jogopani Allah kuti m''be ŵakupunda. ');
INSERT INTO "silika" VALUES(3,131,'3:131','Soni ujogopani Moto waulinganyisyidwe kuŵa ŵa makafili.');
INSERT INTO "silika" VALUES(3,132,'3:132','Ni mumpikanilani Allah kwisa soni Ntenga صلى الله عليه وسلم kuti ntendeledwe chanasa.');
INSERT INTO "silika" VALUES(3,133,'3:133','Soni kangamalani ku (masengo gagantendekasye kupata) chikululuko chakuumila kwa M''mbuje gwenu, ni Litimbe (lya ku Mbepo) lyalili kusapanguka kwa nchijiipi mwakwe kuli chisawu kwinani ni pasi, lyalilinganyisyidwe kuŵa lya ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(3,134,'3:134','Aŵala ŵaakutolaga (ipanje yao) ili nkwajendela chenene atamuno ili yakanganiche, ni ŵaakusisaga ukali wao ni ŵaakwakululuchilaga ŵandu, sano Allah ni akwanonyelaga ŵakolosya.');
INSERT INTO "silika" VALUES(3,135,'3:135','Ni aŵala ŵanti naga atesile chakunyalaya (chachili chilemwa chekulungwa mpela chikululu), kapena kulitenda lupuso achimisyene (pakutenda ilemwa yamwanamwana), akasankumbuchilaga Allah ni kuŵenda chikululuko pa sambi syao – Ana ni nduni jwampaka akululuche sambi pangaŵaga Allah? - Soni ni ngakasapitilisyaga pa yatesilejeyo kutendaga achimanyililaga. ');
INSERT INTO "silika" VALUES(3,136,'3:136','Ŵanganyao malipilo gao nichikululuko chakuumila kwa M’mbuje gwao kwisa soni Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, soni kaje kusalala malipilo ga akupanganya (masengo gambone)!');
INSERT INTO "silika" VALUES(3,137,'3:137','Paamasile pagaapiite kalakala jenumanja nkanimpagwe matala (gakulekangana-lekangana gaipotesi), basi jendagani pa chilambo kupita nchilola mujaŵelele mbesi ja akukanila.');
INSERT INTO "silika" VALUES(3,138,'3:138','Aku kuŵele gombelesya ku ŵandu (wosope), ni chongolo kwisa soni chamuko kwa ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(3,139,'3:139','Ni ngasimpela (pakumenyana ni amagongo ŵenu), soni ngasin''dandaula (pa ipato yayimpatile), soni jenumanja niŵachim''be ŵapenani naga n''di ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(3,140,'3:140','Nagaŵaga gampatile maŵanga, nikuti nombe ŵandu ŵaneo pakwete pagapatile maŵanga ganti chisawu gelega, sano galakwego ni masiku gatukugagalausyagalausya chilikati cha ŵandu (lelo kwajendela ichindu chenene ŵane malaŵi nikwisa kwajendela chenene ŵane), soni Allah (atesile yalakweyi) kuti ŵaamanyukule ŵandu ŵakulupilile (kusyesyene, pangatila pa ngondojo), nikuti soni ŵatende ŵane mwa jemanja kuŵa ŵakuwila pa ngondo ja dini, sano Allah jwangaanonyela ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(3,141,'3:141','Soni kuti Allah ŵaswejesye aŵala ŵakulupilile nikugamala makafili (pakugajonanga).');
INSERT INTO "silika" VALUES(3,142,'3:142','Ana nkuganichisya yanti chinkajinjile ku Mbepo, kutendaga Allah nkaniŵamanyukule ŵandu ŵaamenyene ngondo (petala Lyakwe) mwa jenumanja, ni kwamanyukula soni ŵakupilila (pa ngondojo)?');
INSERT INTO "silika" VALUES(3,143,'3:143','Soni chisimu chene ŵanganyammwe mwaliji nkuchilajila chiwa (chakuwila pa ngondo ja Allah) nkaninsimangane nacho, sano ni nchiweni kusyene (pa kuulajidwa achimijenu) aku n''di nkulola.');
INSERT INTO "silika" VALUES(3,144,'3:144','Ni nganaŵa Muhammadi صلى الله عليه وسلم ikaŵeje Ntenga basi, Achimitenga (ŵajinji) ŵapite paujo pakwe. Ana naga ali awile kapena kuulajidwa mpaka n''galauchile ku indende yenu (kuujila ku ukafili)? Sano jwampaka agalauchile ku indende yakwe nganaŵa apeleche chisausyo chilichose kwa Allah, sano Allah ni chachalipila ŵakutogolela.');
INSERT INTO "silika" VALUES(3,145,'3:145','Soni nganiiŵa yanti mundu jwalijose ni komboleka kuwa, ikaŵeje mu lisosa lya Allah pandema jakwe jelembedwe, sano jwaakusaka malipilo ga pa duniya chitumpele papopo, nombe soni jwaakusaka malipilo ga ku Akhera chitukampele kukoko, ni chitwalipila (yambone) ŵakutogolela.');
INSERT INTO "silika" VALUES(3,146,'3:146','Ana soni ŵalingwaŵalingwa mwa Achimitume ŵaŵamenyene ngondo ali imo ni ŵandu ŵajinji ŵaumanyilisi (wa dini)! Nambotu nganatenguka ligongo lya ipato yayapatile petala lya Allah, soni nganatenda ulesi, nambo soni nganalitulusya (pameso pa amagongo ŵao), sano Allah ni akwanonyela ŵakupilila.');
INSERT INTO "silika" VALUES(3,147,'3:147','Ni nganiiŵaga yakuŵecheta yao ikaŵeje ŵatiji: “Ambuje ŵetu! Tukululuchilani ileŵi yetu ni kupelenganya kwetu mpika pa indu yetu, soni n''galimbanganye makawu getu, ni ntukulupusye ku ŵandu achimakafili.”');
INSERT INTO "silika" VALUES(3,148,'3:148','Basi Allah niŵapele malipilo ga pa duniya ni malipilo gambone nnope ga ku Akhera, sano Allah ni akwanonyela ŵakolosya.');
INSERT INTO "silika" VALUES(3,149,'3:149','E jenumanja ŵankulupilile! Naga nkwiika mwakwapikanila aŵala ŵakanile, cham''buchisye ku indende yenu (ku ukafili), ni chin''galauche (kuumila ku chikulupi) n''di ŵakwasika.');
INSERT INTO "silika" VALUES(3,150,'3:150','Nambo Allah ni M''mbuje gwenu (jwakunkamuchisya), soni Jwalakwe ni Jwambone nnope kwapunda akukamuchisya (wosope).');
INSERT INTO "silika" VALUES(3,151,'3:151','Chituponye woga m’mitima mwa aŵala ŵakanile ligongo lyakum''bwanganya kwao Allah ni ichindu yanganaituluchisya umboni (wakulosya ulungu wakwe). Sano mauto gao (gakutamilichika) chigakaŵe ku Moto, kaje kusakala mauto ga ŵakulitenda lupuso!');
INSERT INTO "silika" VALUES(3,152,'3:152','Soni Allah pamasile paŵankwanilichisye mwakuonaonape chilanga Chakwe (cha kwakombola amagongo ŵenu) ndema jamwaliji nkwaulaga mu lisosa Lyakwe, kwikanila ndema jamwalepele ni kulekangana nganisyo palilamusi (lya Ntume صلى الله عليه وسلم lyangalekangana nalyo litumbi) ni mwanyochesye panyuma pakunnosya yamwaliji nkuinonyela, ŵane mwa jenumanja pana ŵaakunonyelwa ni duniya soni ŵane mwa jenumanja pana ŵaakunonyelwa ni Akhera, kaneka (Allah) ŵantyosyisye ku ŵanganyao (pakutiila) kuti an''dinje (kupilila kwenu), sano pamasile pankululuchile, soni Allah ni Nsyene umbone wakwatendela ŵakulupilila.');
INSERT INTO "silika" VALUES(3,153,'3:153','(Kumbuchilani) katema kamwaliji nkukwela (mwitumbi pakwatiila amagongo ŵenu), soni n''di ngankugalauka kuti mpikane ya jwalijose, kutendaga Ntenga صلى الله عليه وسلم ali nkum''bilanga munyuma mwenu, (Allah) niŵan''dipile pakumpa madandausi pachanya pa madandausi, (nambo ŵankululuchile) kuti nkadandaula pa (yambone) yayimpite soni atamuno pa (ipato) yayimpatile, sano Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(3,154,'3:154','Kaneko panyuma pa madandausi ŵantuluchisye ntunjelele walugono lwana kulikupulaga likuga line mwa jenumanja, kutendaga likuga line nganisyo sya m’mitima mwao syapele mbumu kusyene (mwanti ata lugono nganilwakupula), ŵaliji nkun''ganichisya Allah yangaŵa yakuona, nganisyo syachijinga; ŵaliji nkuŵecheta kuti: “Ana mpela tukwete uwwe mu aji nganiji chilichose (chagundana ni ulamusi)?” Jilani: “Ichindu yosope yene nsyene jwakwe Allah.” Akusisa m’mitima mwao yangaakwilosya kukwenu, pakuŵecheta akuti: “Tungakole mu aji nganiji chilichose (chagundana ni ulamusi) nga nganituulajidwa pepano.” Jilani: “Atamuno nkaŵe n''di m’majumba mwenu, angakopweche ŵandu ŵalembedwe kuti awulajidwe kwaula m’mauto mwao mwakuwila, nambo Allah (jwatesile yalakweyi) kuti juimanyukule yayili mu idali mwenu, ni kuti soni juiswejesye yaili m’mitima mwenu, sano Allah ni Nkuimanya chenene yaili mu idali.');
INSERT INTO "silika" VALUES(3,155,'3:155','Chisimu ŵandu ŵaŵagalawiche (kutiila) mwa jenumanja lisiku lyagaasimangene makuga gaŵili (pa ngondo ja Uhudi), chisimu shetani ni juŵatelesyasisye ligongo lya ine (mwa ilemwa) yaŵapanganyisye, sano Allah pamasile paŵakululuchile ŵanganyao, chisimu Allah ni Jwakululuka kusyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(3,156,'3:156','E jenumanja ŵankulupilile! Ngasim''ba chisawu aŵala ŵakanile (achinakunaku), soni ŵakutiji pakwagamba achimijao pati ajesilejesile pa chilambo kapena kuja ku ngondo (nikuwila kukoko): “Angaŵe ŵaliji nowe, nga nganawa kapena kuulajidwa.” Allah (jwapele syele nganisyosyo) kuti jusitende kuŵa syakutendekasya kuligamba m’mitima mwao, sano Allah ni jwaakupelekaga umi ni chiwa, soni Allah ichindu yankutenda juŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(3,157,'3:157','Sano naga n''di m''bulajidwe petala lya Allah kapena kuwa chisimu chikululuko chakuumila kwa Allah ni chanasa iŵele yambone nnope ku ipunda yaakusonganganya ŵanganyao (pa umi wa duniya).');
INSERT INTO "silika" VALUES(3,158,'3:158','Soni naga n''di m''bwile kapena kuulajidwa, chisimu kwa Allah chinchisonganganyisyidwa.');
INSERT INTO "silika" VALUES(3,159,'3:159','Sano ligongo lya ukoto wakuumila kwa Allah m''bele (mmwe Muhammadi صلى الله عليه وسلم) ŵakupepa ntima kwa ŵanganyao, nambo nkaŵe ŵaukali, ŵakalalisya ntima, akapwilingene mumbali mwenu, basi mwalekani ni kwaŵendela chikululuko, soni mwawusyani nganisyo pa ichindu, nambo naga n''di nsimichisye, basi jegamilani kwa Allah (ni kutenda yansimichisyeyo), chisimu Allah jukwanonyela ŵakwegamila (Kukwakwe).');
INSERT INTO "silika" VALUES(3,160,'3:160','Naga Allah ali atite ankamuchisye basi pangali ŵampaka ankombole, nambo naga ali atite anneche (ngankamuchisya) ana niŵani ŵampaka ankamuchisye panyuma pa (kunneka) Jwalakwe? Sano kwa Allah pe ajegamile ŵakulupilila.');
INSERT INTO "silika" VALUES(3,161,'3:161','Soni nganiiŵa yakomboleka kwa ntume jwalijose gwasya (chindu chakupata kungondo), mwanti jwalijose jwachagwasye (chindu) tachiikanacho chaŵagwasyisyecho lisiku lya Kiyama, kaneka mundu jwalijose tachilipidwa mwakwanila pa yaŵapanganyisye, soni ŵanyao ngasaja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(3,162,'3:162','Ana jwaakukuya chinonyelo cha Allah mpaka alandane ni jwajim''bajile gasabu jakuumila kwa Allah, soni liuto lyakwe nikuŵa ku Jahannama? Kaje kusakala malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(3,163,'3:163','Ŵanganyao akwete mauchimbichimbi (gakulekangana) kwa Allah, sano Allah ni Jwakuilolechesya yaakutenda.');
INSERT INTO "silika" VALUES(3,164,'3:164','Chisimu Allah pamasile paŵatendele ŵakulupilila ukoto wekulungwa nnope pakwatumichisya Ntenga jwakuumila mu ŵanganyapeo, jwaakuŵa nkwasoomela ma Ȃya Gakwe, ni kwaswejesyaga, ni kwajiganyaga soni Chitabu ni lunda lwakusokoka, kutendaga kala ŵaliji mu kusokonechela kwakuonechela.');
INSERT INTO "silika" VALUES(3,165,'3:165','Ana uli ndema jachampatile chipato chanti pakwete pamwapatisye (amagongo ŵenu) chanti iyyoyocho mwa winji wakaŵili mwatite: “Chityochele kwapi achi (chipatochi)?” Jilani: “Chalakwecho chityochele kwa jenumanja mwachimisyene (ligongo lyakunyosya kwenu). Chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(3,166,'3:166','Soni yayampatile (pa ngondo ja Uhudi) lisiku lyagasimangene makuga gaŵili ga asilikali, yaliji mu lisosa lya Allah nikuti ŵaamanyukule ŵakulupilila.');
INSERT INTO "silika" VALUES(3,167,'3:167','Nikuti ŵamanyukule soni ŵandu ŵaŵele ali nkutenda unakunaku. Ni ŵasalilidwe (yanti): “Ikani mmenyane (ngondo) petala lya Allah kapena mwaasiŵilile (amagongo kukwetu).” Ŵanganyao ŵatite: “Tungaimanye yanti kwana kumenyana, chisimu chene tungankuyiye (nambo ngati kuulajidwape tukukuona kwalakweko)!” Ŵanganyao ku ukafili lyele lisikulyo ŵaŵandichile nnope kulekangana ni ku chikulupi. Akuŵechetaga ni ngamwa syao yanganiiŵa m’mitima mwao, sano Allah ni jukuimanya chenene ichindu yaakusisa.');
INSERT INTO "silika" VALUES(3,168,'3:168','(Ŵanganyao ni) aŵala ŵana kuŵechetaga yakwamba achimijao (ŵaŵaulajidwe pa ngondo ja Uhudi) kutendaga (akuŵechetao) ŵatemi (ngajaula kungondoko): “Angaŵe ŵatukundile (yakuti akajaula) nga nganaulajidwa.” Jilani: “Basi lityochesyani mwachimisyene chiwa (kuti nkawa) naga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(3,169,'3:169','Soni ngasimwaganichisya ata panandi aŵala ŵaaulajidwe petala lya Allah kuti ŵawe, nambo ŵachijumi, akulisyidwa kwa Ambuje ŵao.');
INSERT INTO "silika" VALUES(3,170,'3:170','Aku achisengwaga ni yaŵapele Allah kuumila mu ukoto Wakwe, ni akwasangalalilaga ŵandu ŵanganasimangane nao ŵali munyuma mwao (ŵaakusaka nombe kuwila petala lya Allah), kuti ŵanganyao ngasaja kuŵa ni woga soni ngasaja kuŵa ŵakudandaula.');
INSERT INTO "silika" VALUES(3,171,'3:171','Akuchisangalalila chindimba chakuumila kwa Allah ni umbone, chisimu soni Allah jwangasokonesya malipilo ga ŵakulupilila.');
INSERT INTO "silika" VALUES(3,172,'3:172','Aŵala ŵaŵan''jitiche Allah ni Ntenga صلى الله عليه وسلم panyuma pakwapata maŵanga, sano ŵakolosisye (itendo yao) mwa ŵanganyao ni kun''jogopa Allah, akwete kupata malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(3,173,'3:173','Aŵala ŵana kusalilidwaga ni ŵandu (achinakunaku) kuti: “Chisimu ŵandu (makafili) ansonganganyichisye (asilikali ŵajinji) basi mwajogopani.” Nambo (gele maloŵego) gajonjechesye chikulupi, ni ŵatite: “Allah akutukwanila (pakutugosa) soni ni Nkun''jegama Jwambone nnope. ”');
INSERT INTO "silika" VALUES(3,174,'3:174','Basi ŵagalawiche ni chindimba chakuumila kwa Allah ni umbone, nganichakwaya chakusakala (chilichose), ni ŵakuyiye chinonyelo cha Allah. Sano Allah ni Nsyene umbone wekulungwa nnope.');
INSERT INTO "silika" VALUES(3,175,'3:175','Chisimu jwalakwe (jwana kun''jogoyagajo) nishetani jwaakuntendekasya kuti mwajogopeje achimijakwe, basi ngasimwajogopaga ŵanganyao nambo munjogopeje Une naga n''di ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(3,176,'3:176','Soni akan''dandaulikasya aŵala ŵaakuutuchila ku ukafili, chisimu ŵanganyao nganaŵa apeleche chisausyo chilichose kwa Allah. Allah akusaka kuti akaaŵiichila ŵanganyao liunjili lililyose (lya yambone) ku Akhera, soni akwete kupata ŵanganyao ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(3,177,'3:177','Chisimu aŵala ŵasagwile ukafili pakusumanya ni chikulupi, nganaŵa apeleche chisausyo chilichose kwa Allah, soni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(3,178,'3:178','Soni aŵala ŵakanile akaganichisya ata panandi kuti ndaŵi jelewu jatukwapaji jili jambone kukwao, chisimu chene tukwapa ndaŵi jelewuji kuti ajonjechesyeje ulemwa, soni akwete kupata ŵanganyao ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(3,179,'3:179','Nganaŵa Allah jwanti ni gamba kwaleka ŵakulupilila mu kaŵe kan''diika mpaka ali alekangenye (chilikati cha) ŵakusakala ni ŵambone. Soni nganaŵa Allah jwanti ni gamba kummanyisya yakusisika, nambo kuti Allah akasasagulaga mwa Achimitenga Ŵakwe jwansachile (nikummanyisya ine mwa yalakweyo). Basi munkulupililani Allah ni Mitenga Jakwe. Sano naga nkulupilile ni kun''jogopa (Allah), nikuti nkwete kupata malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(3,180,'3:180','Soni aŵala ŵaakutendela ubaili ichindu yaŵapele Allah kuumila mu umbone Wakwe (ni ngapelekaga Zaka) akaganichisya ata panandi kuti kutenda yele kuŵele kwambone kukwao, nambo kwalakweko kuŵele kwakusakala kukwao, chachitaŵidwa makongwa (mu ngosi mwao) gaichindu yaŵaliji nkutendela ubailiyo lisiku lya Kiyama, sano Allah ni Nsyene kutawalila ya kumawunde ni petaka, soni Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(3,181,'3:181','Chisimu Allah pamasile paalipikene liloŵe (lyachalu) lya ŵandu (Ayuda) ŵatite: “Chisimu Allah jwakulaga nambo uwwe niŵatuli ŵakusichila.” Chituilembe yaaŵecheteyo ni kuulaga kwao Achimitume mwangali ligongo, soni tuchiti: “Pasyani ilagasyo ya (Moto wa) koocha.”');
INSERT INTO "silika" VALUES(3,182,'3:182','“Yalakweyi ni ligongo lya (yakusakala) yagaalongwesye makono genu, soni chisimu Allah nganijuŵa jwakwaatenda lupuso ata panandi achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(3,183,'3:183','Aŵala (Ayuda) ŵaŵechete kuti: “Chisimu Allah ŵatulamwile uwwe yanti; tukankulupilila ntenga jwalijose mpakana ali ayiche najo mbepesi jakutinisyidwa ni moto.” Jilani: “Pamasile paŵam’bichilile achimitenga paujo pangu ni ilosyo yakuonechela palangulangu kwisa soni yankwambayo, nambi uli mwaawuleje, naga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(3,184,'3:184','Nambo naga achinkanilaga (mmwe Muhammadi صلى الله عليه وسلم ningaŵa yachilendo), ngati ŵakanilidwe achimitenga paujo penu, ŵaŵaiche ni ilosyo yakuonechela palangulangu ni itabu yalunda lwakusokoka kwisa soni itabu yakulanguka.');
INSERT INTO "silika" VALUES(3,185,'3:185','Mundu jwalijose tachipasya chiwa, nambo kuti tinchipedwa malipilo genu mwakwana lisiku lya Kiyama. Sano jwatakaŵambasyidwe ni Moto ni kwinjisyidwa ku Mbepo, basi tachiŵa pamasile papundile (kusyene), sano umi wa duniya nganiuŵa (chilichose) nambo chisengwasyo chakuloŵesya.');
INSERT INTO "silika" VALUES(3,186,'3:186','Chisimu ŵanganyammwe chin''dinjidwe mu ipanje yenu ni maumi genu, soni ni timpikane kwene ilagasyo yayijinji kuumila kwa aŵala ŵaŵapedwile chitabu paujo penu (Ayuda ni Akilisito), ni kuumila soni kwa akuwanganya Allah ni isanamu, nambo naga mpilile ni kun''jogopa (Allah chim''be ntesile yambone), basi chisimu yalakweyo ni yaili mu ichindu yakusosekwa kuŵichila ntima.');
INSERT INTO "silika" VALUES(3,187,'3:187','Soni (kumbuchilani) katema kajwataŵene Allah chilanga ni aŵala ŵana kupedwaga chitabu (cha Baibulo pakwatenda kuti): “Chisimu chene chinchisalichisye ŵanganyammwe (chitabucho) ku ŵandu soni ngam''ba ŵakuchisisa.” Nambo ŵachiponyisye kunyuma kwa migongo jao, ni ŵasagwile (ya duniya) yantengo wannono pakusumanya ni chalakwecho, kaje kusakala yaakusagula yakwe!');
INSERT INTO "silika" VALUES(3,188,'3:188','Nkaganichisya ata panandi kuti ŵandu ŵaakuŵa ali nkupoochela pa ichindu (yakusakala) yatesile, nikuŵaga soni ali nkunonyelwa kuti alapilidweje pa ichindu yanganapanganya, ngasimwaganichisya ŵanganyao ata panandi kuti chakakulupuche ku ilagasyo, nambo akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(3,189,'3:189','Soni Allah ni Nsyene uchimwene wa kumawunde ni petaka, soni Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(3,190,'3:190','Chisimu chene mu kagumbidwe ka mawunde ni mataka, ni kupisyangana kwa chilo ni muusi, muŵele mwana ilosyo kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(3,191,'3:191','Aŵala ŵaakunkumbuchilaga Allah (pakunkolanga) chiimile, chitamile kwisa soni (ali chigonele) cha mu mbalati mwao, ni akuchetelelaga mu kagumbidwe ka mawunde ni mataka, (ni akutiji): “Ambuje ŵetu! Nganin''gamba gumba yalakweyi mwa masanje, kuswejela ni Kwenu, basi tuŵambasyani ku ilagasyo ya Moto.”');
INSERT INTO "silika" VALUES(3,192,'3:192','“Ambuje ŵetu! Chisimutu Mmwe mundu jwachinkan''jinjisye ku Moto nikuti pamasile pamun''jalwisye, soni ŵandu ŵalupuso ngasakola ŵakwakulupusya.”');
INSERT INTO "silika" VALUES(3,193,'3:193','“Ambuje ŵetu! Chisimutu uwwe tumpikene nkuŵilanga ali nkuŵilanjila ku chikulupi achitiji: ‘Munkulupililani M’mbuje gwenu.’ Basi uwwe ni tukulupilile. Ambuje ŵetu! Ni tukululuchilani sambi syetu, soni ntusimachisye yakusakala yetu, ni ntupe chiwa pamo ni atenda ngoto.”');
INSERT INTO "silika" VALUES(3,194,'3:194','“Ambuje ŵetu! Tupani soni ayila yamwatupele chilanga kupitila mwa Achimitenga Ŵenu (kuti chinchitupa), ni ngasin''ja kutujalusya lisiku lya Kiyama, chisimu Mmwejo ŵangakasa chilanga.”');
INSERT INTO "silika" VALUES(3,195,'3:195','Basi Ambuje ŵao ni ŵaajiitiiche ŵanganyao (duwa syawosyo pakwatenda kuti): “Chisimu Une ngaanja kusokonesya masengo gankupanganya jwalijose mwa jenumanja kutandila jwannume atamose jwankongwe, (pakuti jenumanjammwe ŵanakajumo) ŵane mwa jenumanja akuumila mwa ŵane. Basi ŵandu ŵasamile, ni koposyedwa m’misi jao, ni kulagasyidwa petala Lyangu, ni kumenyana ngondo, ni kuulajidwa, chisimu ŵanganyao chinasimachisye yakusakala yao, ni kuja kwajinjisya ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, (galakwego ni) malipilo gakuumila kwa Allah, sano Allah Kukwakwe nikugali malipilo gambone nnope.');
INSERT INTO "silika" VALUES(3,196,'3:196','Chisimu kukannoŵesya mmwejo kwendajenda kwa ŵandu makafili pa chilambo.');
INSERT INTO "silika" VALUES(3,197,'3:197','(Kweleko kuŵele) kusengwa kwannonope, kaneko liuto lyao ni ku Jahannama, kaje kusakala liuto lyagusama (lyalitandichile)!');
INSERT INTO "silika" VALUES(3,198,'3:198','Nambo ŵandu ŵan’jogwepe M’mbuje gwao, akwete kupata Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, (jalakwejo ni) mbiiko jakuumila kwa Allah, sano ichindu yaili kwa Allah ni yambone nnope kwa atenda ngoto.');
INSERT INTO "silika" VALUES(3,199,'3:199','Soni chisimu chene, mwa ŵandu ŵaŵapedwile chitabu (Ayuda ni Akilisito), mwana ŵaakunkulupililaga Allah ni yaitulusyidwe kukwenu, kwisa soni yayatulusyidwe kukwao, achilinandiyaga kwa Allah, ngakusagulaga (ya duniya) yantengo wannono pakusumanya ni ma Ȃya ga Allah, ŵanganyao akwete kupata malipilo gao kwa M’mbuje gwao, chisimu Allah ni Jwachitema pa kuŵalanjila.');
INSERT INTO "silika" VALUES(3,200,'3:200','E jenumanja ŵankulupilile! Pililani (pakutenda ibada), soni mwapundani amagongo ŵenu pakupilila, soni gosani mipika jenu (kuti amagongo akan''jinjilila), ni mun''jogopeje Allah kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(4,1,'4:1','E jenumanja achinaŵandu! Mun''jogopani M''mbuje gwenu jwaŵan''gumbile kuumila mwa mundu jumpempe (Adamu), ni jwagumbile kuumila mwa jwalakwejo ŵankwakwe (Hawa), ni jwajenesye kuumila mu ŵaŵiliwo achalume ŵajinji kwisa soni achakongwe, ni mun''jogopani Allah Jwankasaŵendanaga kupitila mu Jwalakwejo, ni (jogopani soni kata) maulongo ga kupagwana. Chisimu Allah pa jenumanja aŵele Nkung''unulila kusyene (jwaakuŵa ali nkukalolechesya kalikose kankutenda).');
INSERT INTO "silika" VALUES(4,2,'4:2','Soni mwapani ŵawina chipanje chao, ni ngasintindanya yakusakala (yenu) ni yambone (yao), soni ngasin''dya chipanje chao (pakuchiwanganya) ku chipanje chenu, chisimu wele uli ulemwa wekulungwa.');
INSERT INTO "silika" VALUES(4,3,'4:3','Nambo naga nkogopa kuti ngannungamika pa ŵawina (ŵachikongwe ŵankutama nao ŵankusaka kwalomba mwalekani), basi lombelani (ŵanepe) mwa achakongwe ŵamwanonyele; ŵaŵili, kapena ŵatatu, kapena ncheche. Nambo naga nkogopa kuti ngampakombola kwilanya, basi (gambani kulombela) jumpepe, kapena (mateeka) gagapatile magasa genu gakun''dyo. Yalakweyo ni yakuŵandichila (kuntendekasya) kuti nkapendechela (ku ungajilanya).');
INSERT INTO "silika" VALUES(4,4,'4:4','Soni mwapani achimmasyeto mahari gao (mbiya syandowa kapena chilichose chakwalombelela) kuŵa ntuuka, nambo naga ali anonyelwe m’mitima mwao yakumpa chinechakwe m''maharigo, basi chilyaani mwakusengwa ni mwangachijogopela (ngati Allah ankundisye)!');
INSERT INTO "silika" VALUES(4,5,'4:5','Ni nkaapa ŵawawate chipanje chenu chaachitesile Allah kuŵa chakun''dimbanganya, nambo mwaaliichisyani nacho ni kwawecha, ni mwaŵecheteleje maloŵe gambone.');
INSERT INTO "silika" VALUES(4,6,'4:6','Soni mwalingani ŵawina (pa lunda lwao lwakwendechesya chipanje) kwikanila ali ayiche ndema ja (kusaka) ulombela, sano naga n''di nkuweni kwa ŵanganyao komangala kwa lunda, basi mwapani chipanje chao. Ni ngasinchilya (chipanjecho) mwakonanga ni mwachitema pakogopela kuti atakwika mwaakusile. Sano mundu jwali jwakuliipatila basi aliŵambasye (kujigala malipilo mu chipanjecho pakuchigosa kwakwe), nambo mundu jwali jwakulaga, basi chakomboleche kulya (chipanjecho) mwakwilanila (ni mwaakuchigosela), sano ndema jankwapa chipanje chao basi mwasoselani mboni pameso pao (syakutendela umboni wanti apocheele chipanje chakwana, kogopela atakwisa mwaakaaniile panyuma pakwe), sano Allah jukwanila kuŵa Nkuŵalanjila chenene (chipanjecho kuti chigosedwe chenene kapena chepungule).');
INSERT INTO "silika" VALUES(4,7,'4:7','Akwete kupata achalume liunjili mu chipanje chalesile achinangolo ni ŵaulongo wakuŵandichila, nombe nao achakongwe akwete kupata liunjili mu chipanje chalesile achinangolo ni ŵaulongo wakuŵandichila, muchichiŵele (chipanjecho) chapanandi kapena chejinji, (gaŵele) maunjili gepanjepanje (Allah).');
INSERT INTO "silika" VALUES(4,8,'4:8','Sano ndema jagaŵanya (chipanjecho) naga akwika ŵapaulongo, ni ŵawina, kwisa soni masikini (ŵanganakola liwunjili lyao), basi mwapani (kachinandipamuno) kuumila mu chipanjecho, nimwaŵecheteleje maloŵe gambone.');
INSERT INTO "silika" VALUES(4,9,'4:9','Ni ajogope aŵala (ŵalecheledwe chipanje kwakalamusya ŵawinao, ligongo nombe nao ali) ŵanti ingaŵe ŵalesile munyuma mwao ŵanache ŵangalikombolela akajogopele (kalamusyidwa), basi an''jogopeje Allah ni aŵecheteje maloŵe gagoloka.');
INSERT INTO "silika" VALUES(4,10,'4:10','Chisimu ŵandu ŵaakulya chipanje cha ŵanache ŵawina mwa lupuso, chisimu chene ŵanganyao akulya (ni kutaga) moto m’matumbo mwao, soni chakajinjile ku Moto Wakulapuka.');
INSERT INTO "silika" VALUES(4,11,'4:11','Allah akunnamula (ya kagaŵanye ka chipanje chamalekwa) kuŵanache ŵenu, kuti mwanache jwannume akwete kupata liunjili lyakulandana ni liunjili lya ŵanache ŵachikongwe ŵaŵili (2:1). Sano naga ali ŵachikongwepe (ŵaŵili kapena) kupunda paŵaŵili, nikuti chapate maunjili gaŵili m’maunjili gatatu(2⁄3) gachipanje chalesile (nkuwa), nambo naga (mwanache jwankongwejo) ali jumpepe, nikuti chapate litika (1⁄2 lya chipanje chosope). Sano achinangolo ŵa nkuwa jwalijose mu ŵaŵiliwo chapate liunjili limo m’maunjili nsano na limo (1⁄6) nchipanje chalesile (nkuwajo) naga julesile mwanache (kapena chisukulu), nambo naga nganijuleka mwanache (kapena chisukulu) nantawaliile (chipanje chakwecho) achinangolo ŵakwe pe, nikuti mamagwe chapate liunjili limo m’maunjili gatatu (gachipanje chosope 1⁄3, sano babagwe nichajigale maunjili gaŵili gakusigalago 2⁄3), nambo sano naga julesile achachimwenegwe (kapena achalumbugwe), nikuti mamagweŵala chapate liunjili limo m’maunjili nsano na limo (1⁄6 gachipanje chosope, gaŵanya kwalakweku kutendekwe) panyuma pakupitisya wasiya wachaleche (nkuwa wanti chindu chanti apedwe chenono) kapena (kun''dipilila) ngongole (syakwe). Achinangolo ŵenu ni ŵanache ŵenu ngankupata kumanyilila kuti ana ŵani mwa ŵanganyao ŵali ŵakuŵandichila nnope kukwenu pa chikamuchisyo. (Awu ni) mpango (wa kagaŵanye) waupanjidwe ni Allah, chisimu Allah aŵele Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,12,'4:12','Sano jenumanja (achalume) nkwete kupata litika (1⁄2) lyachipanje chalesile achiŵammakwenu naga nganaleka ŵanganyao mwanache (kapena chisukulu), nambo naga alesile mwanache (kapena chisukulu), nikuti chimpate liunjili limo m’maunjili ncheche (1⁄4) nchipanje chalesile ŵanganyao, (gaŵanya kwalakweku kutendekwe) panyuma pakupitisya wasiya wachaleche (achiŵammakwenuo) kapena (kwalipilila) ngongole (syao). Nombe nao (achiŵammakwenu) akwete kupata liunjili limo m’maunjili ncheche (1⁄4) nchipanje channesile ŵanganyammwe naga nganinneka mwanache (kapena chisukulu), nambo naga nnesile mwanache (kapena chisukulu), nikuti chapate liunjili limo m’maunjili nsano na gatatu (1⁄8) mu chipanje channesile, (gaŵanya kwalakweku kutendekwe) panyuma pakupitisya wasiya wachinneche ŵanganyammwe kapena (kun''dipilila) ngongole (syenu). Sano naŵaga ŵalume kapena ŵakongwe akutawalwa (chipanje chao) kutendaga ali ŵangali mwanache (kapena chisukulu) ni achinangolo ŵakwe ŵanaŵaŵili, nambo ni alesile nchimwenegwe (jwakuchimama) kapena nnumbugwe (jwakuchimama), nikuti jwalijose mu ŵaŵiliwo chapate liunjili limo m’maunjili nsano na limo (1⁄6 gachipanje chosope), nambo naŵaga ali ŵakutupila kupunda pele, nikuti chagaŵane liunjili limo m’maunjili gatatu (1⁄3 gachipanje chosope), (gaŵanya kwalakweku kutendekwe) panyuma pakupitisya wasiya wachiulechedwe kapena (kulipilila) ngongole (sya akuwa), mwangali kupeleka isausyo, achi ni chilamusi chakuumila kwa Allah, sanotu Allah ni Jwakumanyilila kusyesyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(4,13,'4:13','Jalakwejo ni mipika ja Allah, sano jwachampikanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, nikuti chachin''jinjisya ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, sano kwalakweko ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(4,14,'4:14','Nambo jwachannyosye Allah ni Ntenga Jwakwe صلى الله عليه وسلم ni gulukaga mipika Jakwe, chakan''jinjisye ku Moto, chijukaŵe jwandamo mwalakwemo, soni akwete kupata jwalakwejo ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(4,15,'4:15','Sano ŵataiche mwakutenda chakunyalaya (chigwagwa) mwa achimmasyeto ŵenu, mwaŵilanjilani mboni ncheche mwa jenumanja, sano naga sipeleche umboni (wanti ŵaweni ali nkutenda chigwagwacho), basi mwaugalilani m’majumba mpaka tichasimane chiwa, kapena Allah chiŵaŵichile litala line. ');
INSERT INTO "silika" VALUES(4,16,'4:16','Nombe nao achalume ŵaŵili mwa jenumanja ŵataiche mwakuchitenda (chausakwacho mpela matanyula) basi mwalagasyani, nambo naga akutenda toba ni kolosya (ndamo syao) basi mwalekani, chisimu Allah aŵele Jwakupochela nnope toba Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,17,'4:17','Chisimu toba jakundidwa ni Allah ni ja aŵala ŵaakutendaga yakusakala mwaujinga, kaneka ni akutendaga toba mwachitema-chitema, basi ŵanganyao niŵaakwapochelaga Allah toba jao, ni aŵele Allah Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,18,'4:18','Ni nganijiŵa (jikundidwe) toba ja ŵandu ŵaakutendaga yakusakala kwikanila chiwa chili chim’bichilile jumo jwao nikutiji: “Chisimu une ndesile toba sano,” atamose aŵala ŵaakuwa ali makafili. Ŵanganyao twalinganyichisye ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(4,19,'4:19','E jenumanja ŵankulupilile! Ngayiikundisyidwa kwa jenumanja (ŵakuchilume) kuti mwatawalile achimmasyeto mwakwakanganichisya (panyuma pakuwa achiŵammakwao). Ni nkasimwalagasyaga (achiŵammakwenu pandamo naga n''di ngankwasaka, kuti mpaka ayikane mwakuliombola) pakusaka kwasumula ichindu ine yamwapele, (nteto walakweu uli wakanyisyidwa), ikaŵeje naga ayikene mwakutenda chakunyalaya chakuonechela (mpela chigwagwa). Soni tamagani nao (achiŵammakwenuwo) mwaumbone, natamuno n''di mwaŵenjile (nkautuchila kwaleka), mwinetu tinkomboleche kuchiŵenga chindu chaaŵisile Allah nkati mwakwe umbone wejinji.');
INSERT INTO "silika" VALUES(4,20,'4:20','Sano naga mkusaka kunnomba jwankongwe jwine pakunneka jwankongwe (jwakala) kutendaga jumpepe mwa jamanjao (jwankunnekajo) n''di mumpele liunjili (lyambiya syandowa) kasin''jigala kutyochela mwalakwemo chilichose, ana nkuchijigala mwalupuso ni mwaulemwa wakuonechela?');
INSERT INTO "silika" VALUES(4,21,'4:21','Ana soni chinchijigale chinauli kutendaga pamasile pamwajinjilene ŵane mwa ŵane (mwaikene mwagona ulombela), soni (achakongwewo) ŵajigele kuumila kukwenu chilanga chakulimbangana (pandowa chakuti chintame nao chenene)?');
INSERT INTO "silika" VALUES(4,22,'4:22','Soni nkalombela achakongwe ŵalombedweje ni achatati ŵenu, ikaŵeje yayapite kala (ninkaiwilisya soni kuitenda), chisimu wele (ntetowu) uŵele wakunyalaya ni wakuŵenjedwa kusyene soni ni litala lyakusakala nnope.');
INSERT INTO "silika" VALUES(4,23,'4:23','Iŵele haraam kukwenu (kwalomba) achimama ŵenu (ŵakum''beleka), ŵanache ŵenu (ŵakuliŵelechela), achalumbu ŵenu (ŵakupagwana), achibaba ŵenu ŵachikongwe, achimama ŵenu ŵanandi kapena ŵakulungwa, ŵanache ŵanchimwene gwenu, ŵanache ŵannumbu gwenu, achimama ŵenu ŵaŵan''jonjesye, achalumbu ŵenu ŵamwajonjile liŵele limo, achakwegwe ŵaŵaŵeleche achiŵammakwenu, ŵanache ŵakwasimana ŵankwalela ŵakupagwa kutyochela mwa achiŵammakwenu ŵan''jinjilene nao (pagona ulombela), nambo naga nganin''jinjilana nao, basi chipaŵe pangali ulemwa kukwenu (kwalomba ŵanache ŵaowo), achiŵammakwao ŵanache ŵenu ŵachilume ŵakutyochela m’migongo mwenu, soni nikuti mpwatikanye chilikati cha mundu ni mpwakwe (ŵakupagwana), ikaŵeje yayapite kala (ninkaiwilisya soni kuitenda). Chisimu Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,24,'4:24','Soni ni (iŵele haraam kukwenu kwalomba) achimmasyeto ŵalombedwe, ikaŵeje (mateeka) gagapatile magasa genu gakun''dyo, achi ni chilamusi cha Allah chachili pa jenumanja. Ni iŵele halaal (kwalomba achakongwe) ŵanganaŵa ŵele (ŵasalidwewo). Mwasosagani (achakongwewo) ni chipanje chenu mwakwalomba ngaŵaga mwachikululu, sano ŵam''biche mwakulisengwasya nao mwa ŵanganyao (pakwalomba), basi mwapani mahari gao yagatite pakulamulidwa, soni chipaŵe pangali ulemwa kukwenu pa yampikanganene (kutola) panyuma pa kulamulidwa (mahari), chisimu Allah aŵele Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,25,'4:25','Sano jwachatende ngapakombola mwa jenumanja kupata chipanje chakulombelela alukosyo ŵakulupilila (ŵachisilamu), basi (gambani kulombela) mwa aŵala ŵagapatile magasa genu gakun''dyo mu ŵesichana ŵenu ŵakulupilila (ŵachisilamu). Soni Allah ni jwaakumanyilila nnope ya chikulupi chenu, (pakuti jenumanjammwe ŵanakajumo) ŵane mwa jenumanja akuumila mwa ŵane, basi mwalombani kwa lusa lwa mabwana gao soni mwapani mahari gao (mbiya syandowa kapena chilichose chakwalombelela) mwakwilanila, (mwalombani) ŵakuligosa ngaŵaga ŵachigwagwa kapena ŵakutenda maganja. Sano pati aligosile (achimateekao pakulombedwa) nikwisa kutenda chakunyalaya (chigwagwa), basi chapedwe litika lya ilagasyo yaakupedwaga nnukosyo (channyesye ikoti makumi nsano), yalakwe (yakulombela mateekayo) ili yaajula jwaakogopa kuti atakwisa mwatesile chikululu mwa jenumanja, nambo naga n''di mpilile (nikuleka kugalomba mateekago) mpaka iŵe yambone kukwenu, sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,26,'4:26','Allah akusaka kunsalichisya ŵanganyammwe ni kun''jongolela ku matala ga aŵala ŵaŵaliji paujo penu, ni kumpochela toba jenu, sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,27,'4:27','Soni Allah akusachilila kuti ampochele toba jenu, nambo ŵandu ŵaakukuya isako (yao) akusachililaga n''di mpombotele kupombotala kwekulungwa nnope.');
INSERT INTO "silika" VALUES(4,28,'4:28','Allah akusachilila kuti an''jepepachisye, pakuti mundu jwagumbidwe jwangali machili.');
INSERT INTO "silika" VALUES(4,29,'4:29','E jenumanja ŵankulupilile! Kasillyaganilana chipanje chenu mwaukoleko, ikaŵeje gali malonda (gankusumana) mwakupikanganana chilikati chenu, soni ngasilliulaga mwachimisyene, chisimu Allah aŵele Jwachanasa kwa jemanja. ');
INSERT INTO "silika" VALUES(4,30,'4:30','Soni jwachaiche mwakutenda yele mwakusumba mpika ni mwalupuso basi tuchija kun''jinjisya ku Moto, soni yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(4,31,'4:31','Naga n''diŵambasyeje (ilemwa) yekulungwakulungwa yankulekasyidwa, chitunsimachisye yakusakala yenu (yamwanamwana) ni tuchin''jinjisya kumalo kwauchimbichimbi (ku Mbepo).');
INSERT INTO "silika" VALUES(4,32,'4:32','Soni ngasinyyilupataga ichindu yaapeleche Allah mwawinji kwa ŵane mwa jenumanja kwapunda ŵane, akwete kupata achalume liunjili lya (malipilo ga) yapanganyisye, nombe nao achakongwe akwete kupata liunjili lya (malipililo ga) yapanganyisye, ni mum''bendeje Allah ukoto Wakwe, chisimu Allah chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(4,33,'4:33','Soni kwa jwalijose (mwa jenumanja) tutomeche nkuntawalila (jwakwe) pa chipanje chalesile achinangolo ŵaŵili ni ŵaulongo, sano aŵala ŵamwataŵene nao chilanga (chakuntawalila chipanje chenu), mwapani liunjili lyao (kupitila mu wasiya wachimwalechele), chisimu Allah pa chindu chilichose aŵele Jwakutendela umboni.');
INSERT INTO "silika" VALUES(4,34,'4:34','Achalume niŵali ŵakwajimila achakongwe (ŵalume ni ntwe weŵasa), ligongo lyanti Allah ŵachimbichisye ŵane mwa jamanjao kwapunda ŵane (achalume kwapunda achakongwe), soni ligongo lya chipanje chao chaakuŵa ali nkutola. Sano achimmasyeto ŵambone niŵali ŵakumanya, ŵakuligosa (yilu yao nigosa yipanje yaliŵasa) atamuno achiŵammakwao pali pangali, ligongo lyanti Allah ŵagosile (achimmasyetowo kupitila mwa achiŵammakwao ŵaakwapaga yaakulajila). Sano aŵala (achimmasyeto) ŵankukujogopa kuliikwesya kwao basi mwajamukani, (naga ngaakupikana) mwawunduchilani pagona, (naga kuunduka ngakukamuchisya) mwaputani (kwangaulasya), sano naga ali nkumpikanila, basi ngasimwasosela matala (gakwalagachisya). Chisimu Allah ni jwaali Jwapenani kusyene, Jwankulungwa nnope.');
INSERT INTO "silika" VALUES(4,35,'4:35','Sano naga (jenumanja akwiilanya) nkuujogopa ukangani wauli chilikati chao (ŵalume ni ŵankwao), basi tumisyani namangoswe jwakuchilume ni namangoswe jwakuchikongwe (kuti akachengulanye), naga ŵanaŵaŵiliwo ali asachile kwilanya, Allah tiŵakombolasye, chisimu Allah aŵele Jwakumanyilila nnope, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(4,36,'4:36','Soni mun''galagatilani Allah ni nkam''bwanganyaga ni chilichose, nombe achinangolo ŵanaŵaŵili mwatendelani yambone, kwisa soni ŵaulongo, ŵawina, masikini, jwakuŵandikana najo nyumba niali jwaulongo, jwakuŵandikana najo nyumba niali mundu nsape, n''jenu jwakutama najo imo, jwapaulendo ni (mateeka) gagapatile magasa genu gakun''dyo. Chisimu Allah ngakasaŵanonyela aŵala ŵali ŵakulikwesya ni kuliiti.');
INSERT INTO "silika" VALUES(4,37,'4:37','Aŵala ŵaakutenda ubayili ni kwachisyaga soni ŵandu kutenda ubayili, ni akusisaga yaŵapele Allah kuumila mu ukoto Wakwe. Ni twalinganyichiisye achimakafili ilagasyo yakwalusya. ');
INSERT INTO "silika" VALUES(4,38,'4:38','Ni aŵala ŵaakutolaga ipanje yao mwakulilochesya ku ŵandu, ni ngakunkulupilila Allah ni Lisiku Lyambesi. Sano mundu jwachaŵe shetani n''jakwe, nikuti taŵe akwete n''jakwe jwakusakala nnope.');
INSERT INTO "silika" VALUES(4,39,'4:39','Ana soni chichi chikaasawisye ŵanganyao ingaŵe ŵankulupilile Allah ni Lisiku Lyambesi, nikutolaga mu ichindu yaŵapele Allah? Sanotu Allah ni jwali Jwakwamanya chenene ŵanganyao.');
INSERT INTO "silika" VALUES(4,40,'4:40','Chisimu Allah jwangatenda lupuso (ku iwumbe Yakwe atamuno pachitendo chamwana chanti chisawu) usito wa lunyilili, sano nachiŵaga chambone, akasachitupiyaga nikupeleka kuumila Kukwakwe malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,41,'4:41','Nambi ichiŵa uli ndema jatuchiika najo mboni kutyochela mu nkutula uliwose (jakuutendela umboni nkutulao), nikwika namwe (mmwe Muhammadi صلى الله عليه وسلم) kuŵa mboni pa ŵanganyaŵa?');
INSERT INTO "silika" VALUES(4,42,'4:42','Lyele lisikulyo aŵala ŵaŵakufuulu, ni kunnyosya Ntenga صلى الله عليه وسلم chachilajila yanti: Bolaga likagambe kwasimasya litaka (ningasyuka soni), ni ngasakombola kunsisa Allah liloŵe lililyose (lyakwamba yaŵapanganyisye).');
INSERT INTO "silika" VALUES(4,43,'4:43','E jenumanja ŵankulupilile! Ngasin''jiŵandichila Swala kutendaga n''di nkolelwe, mpaka mmanyilile kaje yankuŵecheta, atamuno soni n''di ni janaba (ngasin''jinjila mu nsikiti kukuswaali) mpaka nkajoje, ikaŵeje jwagamba kutumbulukanya litala (mu nsikitimo pakwinjilila aku ni kopochela aku). Sano naga n''di ŵakulwala, kapena pa ulendo, kapena jumo mwa jenumanja ayiiche kutyochela kukaŵenduka (kuchimbusi), kapena nkwayanile ni achimmasyeto (pakutenda nsangu), ni nganin''gapata mesi, basi gambani kutenda Tayammamu ni litaka lyambone, basi pakaani kungope kwenu ni m’makono mwenu. Chisimu Allah aŵele Nkusiimaasya (ilemwa), Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(4,44,'4:44','Ana ngankwaona aŵala ŵapedwile chipande cha chitabu ŵaakulisagulila kusokonechela, nikusachililaga soni kuti n''di nsokonechele ŵanganyammwe (kuleka) litala (lyagoloka)!');
INSERT INTO "silika" VALUES(4,45,'4:45','Soni Allah akumanyilila chenene ya amagongo ŵenu, ni akukwanila Allah kuŵa Nkugosa, soni Allah akukwanila kuŵa Nkukamuchisya.');
INSERT INTO "silika" VALUES(4,46,'4:46','Mwa ŵandu ŵali mu chiyuda, pana ŵaakugapitikusyaga maloŵe pakugatyosya mmangwakwe, ni akutiji (pakunsalila Muhammadi صلى الله عليه وسلم): “Tupikene nambo tunyosyisye, soni pikanani n''di ŵangapikana.” (Kwakuli kulwesa). Soni (akuntendaga kuti): “Tuchiingaani.” (Achambaga nkuchiinga jwakwe jwangali lunda, akuŵecheta gele maloŵega) mwakupinda ndimi syao (kuti Muhammadi صلى الله عليه وسلم ajileje: “Akunjimbichisya,” kutendaga ali nkunchindimbula), ni kusaka kujinyosya Dini (ja Usilamu). Nambo chisimu ŵanganyao ingaŵe ŵaliji ali nkuti: “Tupikene soni tukundile, ni pikanani soni ntukosoŵalile, (mmalo mwakuti ‘Tuchiingaani),” ingaliji yambone kukwao ni yagoloka nnope, nambo kuti Allah ate kwalwesa ligongo lya ukafili wao, ni ngaakukulupilila ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(4,47,'4:47','E jenumanja ŵamwapedwile chitabu (cha Baibulo Ayuda ni Akilisito)! Ikulupililani yatutulwisye (kwa Muhammadi صلى الله عليه وسلم) yaikwitichisya yan''di nayo, nkanituiche mwakusigalausya ngope (kuŵa syangali meso, ngwimbi, mbula, pakamwa ata chipundi), kaneka nikusipombotachisya kunyuma kwakwe (kundungu), kapena nkanitwalwese ŵanganyao yatwatite pakwalwesa achinsyene (kasa uchimbichimbi wa lisiku) Lyakuŵeluka. Sano chilamusi cha Allah ni chiŵele chakutendekwape basi. ');
INSERT INTO "silika" VALUES(4,48,'4:48','Chisimu Allah nganaŵa akululwiche (sambi sya) kum''bwanganya (ni indu ine), nambo akasakululukaga (sambi sine) syanganisiŵa syalakwesi kwa mundu jwansachile. Sano jwachaiche mwakum''bwanganya Allah (ni indu ine), nikuti chaŵe pamasile papanganyisye chikansambi chekulungwa.');
INSERT INTO "silika" VALUES(4,49,'4:49','Ana ngankwaona aŵala (Ayuda ni Akilisito) ŵaakuliswejesyaga achimisyene? Nambo Allah ni jwaakunswejesya jwansachile, soni ngasaja kutendedwa lupuso atamose pa (kachindu kamwana kanti chisawu) kawuusi kankati mwa lupele lwatende.');
INSERT INTO "silika" VALUES(4,50,'4:50','Nnole, yaakuti pakunsepela Allah unami! Sanotu ayi ikwanila kuŵa sambi syakuonechela (syakuja kwajinjichisya ku Moto).');
INSERT INTO "silika" VALUES(4,51,'4:51','Ana ngankwaona aŵala ŵapedwile chipande chachitabu, ŵaakuŵa ali nkukulupilila isanamu ni shetani, ni akuŵechetaga ni achimakafili kuti: ŵanganyao (achimakafiliwo) ni ŵali petala lyagoloka nnope kwapunda ŵandu ŵakulupilile (Asilamu).');
INSERT INTO "silika" VALUES(4,52,'4:52','Ŵanganyao ni aŵala ŵaŵalwesile Allah. Sano jwaakwika mwakulwesedwa ni Allah, nikuti nganim''ba mumpatiile nkulupusyo.');
INSERT INTO "silika" VALUES(4,53,'4:53','Kapena akwete ŵanganyao chipande cha uchimwene (wa Allah)? Ingaŵetu iyyo, nikuti nga nganiŵapa ŵandu (ata kachindu kannono kanti chisawu) chisowu cha lupele lwa tende.');
INSERT INTO "silika" VALUES(4,54,'4:54','Kapena akwatendela litima ŵandu (Asilamu) pa ichindu yaŵapele Allah (wauli utume) kuumila mu okoto Wakwe? Basi pamasile patwalipele liŵasa lya Ibrahima Chitabu ni lunda lwakusokoka, ni twapele soni ŵanganyao uchimwene wekulungwa nnope.');
INSERT INTO "silika" VALUES(4,55,'4:55','Basi ŵane mwa ŵanganyao pana ŵaikulupilile yalakweyi, nambo soni ŵane mwa ŵanganyao pana ŵaliŵambesye nayo, ni jikwanila Jahannama kuŵa jakwachomela.');
INSERT INTO "silika" VALUES(4,56,'4:56','Chisimu ŵandu ŵakaaniile ma Ȃya Getu, tuchija kwajinjisya ku Moto. Ndaŵi ni katema kachigakatinicheje mapende gao, chitukaatindanichisyeje ni mapende gane kuti akapasyejepe ilagasyo. Chisimu Allah aŵele Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,57,'4:57','Sano ŵandu ŵaakulupilile nikutendaga yambone, chitukaajinjisye ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo kwayaka ni yaka. Akwete kupata ŵanganyao mwalakwemo achiŵammakwao ŵaswejesyedwe, nitukaajinjisye soni kumiwulili jakusisimila chenene. ');
INSERT INTO "silika" VALUES(4,58,'4:58','Chisimu Allah akunnamula yanti, mwauchisyeje achimisyene ichindu yampele kusalasya, soni ndema jankwilanya chilikati cha ŵandu, jilanyani mwagolosya, chisimu Allah (misyungu) jaakuŵa ali nkum''bunda najo jiŵele jambone nnope, chisimu Allah aŵele Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(4,59,'4:59','E jenumanja ŵankulupilile! Mumpikanilani Allah soni ni mumpikanile Ntenga صلى الله عليه وسلم, kwisa soni ŵaakwete ulamusi mwa jenumanja (Asilamu). Sano naga ntindene pa chindu chilichose, basi chiuchisyani kwa Allah ni kwa Ntenga صلى الله عليه وسلم nam''baga ŵanganyammwe nkunkulupilila Allah ni Lisiku Lyambesi, yalakweyo ni yaili chenene (pakumasya ukangani) soni ni yaikwete yakuichisya yambone nnope.');
INSERT INTO "silika" VALUES(4,60,'4:60','Ana ngankwaona aŵala (achinakunaku) ŵaagamba kuŵecheta mwaukoleko kuti ŵanganyao ayikulupilile yaitulusyidwe kukwenu, ni yayatulusyidwe paujo penu? Akusaka ŵanganyao kuti akalamulidwe (magambo gao) ni malamusi gangajilana ni Sharia, kutendaga pamasile palamulidwe yagakana gele (malamusigo), sanotu shetani ni akusaka kuti ŵasokonesye ŵangayao kusokonechela kwanaula.');
INSERT INTO "silika" VALUES(4,61,'4:61','Soni pati asalilidwemo kuti: “Ikani kuyatulwisye Allah, soni (ikani) kwa Ntenga صلى الله عليه وسلم.” Chimwaone achinakunaku ali nkuntundumalila kusyesyene.');
INSERT INTO "silika" VALUES(4,62,'4:62','Ana mpaka iŵe uli ndema jakwapata ipato ligongo lya yagalongwesye makono gao, (nikuŵa pangali ŵane ŵakwajilanyichisya ikaŵeje mmwejo Muhammadi صلى الله عليه وسلم)? Kaneko nikum’bichilila aku achilumbilaga (mwakulipembechela) kuti: “Tukunnumbila Allah, nganitusakaga (kuujitichisya ulamusi wa ŵanganyao pakuja kwalaŵila kunneka mwwe), nambo (twagambaga kusaka kulosya) umbone ni kupikanganana!”');
INSERT INTO "silika" VALUES(4,63,'4:63','Ŵanganyao ni aŵala ŵaakuimanya Allah yaili m’mitima mwao, basi mwalekani (nkaapa ilagasyo), nambo mwajamukani ni kwaŵechetela maloŵe gakwinjilisya m’mitima mwao.');
INSERT INTO "silika" VALUES(4,64,'4:64','Ni nganitutumisyeje ntenga jwalijose ikaaŵeje kuti akundidweje kwa lisosa lya Allah. Sano ŵanganyao pandema jaŵalitendele lupuso achimisyene, ingaŵe ŵam’bichilile (mmwe Muhammadi صلى الله عليه وسلم) ni kum''benda Allah chikululuko, nombe najo Ntenga صلى الله عليه وسلم nikwaŵendela chikululuko, chisimu chene angansimene Allah ali Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,65,'4:65','Nambo ngaŵa (mwaakuŵechetelamo kuti akulupiliile), ngwalumbila Ambuje ŵenu! Ngaŵa nkuŵa ŵakulupilila ŵanganyao ni ikaŵeje ali antesile (mmwe Muhammadi صلى الله عليه وسلم) kuŵa nkwaajilanya jwao pa ichindu yalekangene chilikati chao, soni ni akasimana usito m’mitima mwao pa ulamusi wannamwile, ni alipeleche kusyesyene (paakunda ulamusi wenuwo).');
INSERT INTO "silika" VALUES(4,66,'4:66','Soni Uwwe tungaŵe twalamwile ŵanganyao yanti: “Ulaganani mwachimisyenepe,” kapena: “Kopokani m’majumba mwenu,” nga nganatenda yalakweyo ikaŵeje ŵannonope mwa jamanjao. Nambo ingaŵe ŵanganyao ŵatesile yaŵaliji nkusalilidwa, ingaliji yambone kukwao ni yakupundanganya nnope kulimbanganya (chikulupi chao).');
INSERT INTO "silika" VALUES(4,67,'4:67','Soni pa jele ndemajo, chisimu chene tungapele ŵanganyao kuumila Kukwetu malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,68,'4:68','Soni tungajongolele kwitala lyagoloka (lyakuja kwaikasya ku Mbepo).');
INSERT INTO "silika" VALUES(4,69,'4:69','Sano ŵachampikanile Allah ni Ntenga صلى الله عليه وسلم, basi ŵanganyao ni ŵachachiŵa pamo ni ŵandu ŵaŵapele Allah chindimba, kutandila Achimitume, ni ŵakulungamika kusyene (pachikulupi), ni ŵakuwila kungondo (jakumenyana ni makafili), kwisa soni (ŵandu) ŵambone, kaje kusalala kuŵa nao imo ŵanganyao!');
INSERT INTO "silika" VALUES(4,70,'4:70','Walakweo ni ukoto wakuumila kwa Allah, sano Allah akukwanila kuŵa Nkumanyilila Nsyesyene.');
INSERT INTO "silika" VALUES(4,71,'4:71','E jenumanja ŵankulupilile! Jigalani yakuligosela (ku amagongo ŵenu), basi ni m’biguleje (wa kungondo) makuga-makuga kapena ŵigulani ŵanaose nakamo.');
INSERT INTO "silika" VALUES(4,72,'4:72','Soni chisimu mu ŵanganyammwe, pana jwaakusigalila munyuma (ngajaula kungondoko nikwagwisyaga soni ulesi ŵane), sano pati yimpatile ipato ŵanganyammwe akasatiji: “Chisimu Allah ambele chindimba pangapatikana une (kwalakweko) imo ni ŵanganyao.”');
INSERT INTO "silika" VALUES(4,73,'4:73','Nambo naga umpatile umbone wakuumila kwa Allah (mpela kupata chipanje chakuchileka amagongo), chisimu akasaŵechetaga mpela paliji pangali chinonyelo chilikati chenu ni jwalakwejo: “Nkongwene! Ngaŵe imo ni ŵanganyao ngati ngapundile kupunda kwekulungwa nnope (pakungaŵila chipanjecho).”');
INSERT INTO "silika" VALUES(4,74,'4:74','Basi (jwapite kungondoko), apute ngondo petala lya Allah (kwaputa) ŵandu ŵaakusagula wumi wa duniya pakusumanya ni Akhera (ligongo lya ukafili wao), sano jwataiche mwakuputa ngondo petala lya Allah, nikuulajidwa kapena kupunda, basi tuchijisa kumpa malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,75,'4:75','Ana chichi chisawisye kukwenu (chakuntendekasya) kuti nkaputa ngondo petala lya Allah, ni (kwakulupusya) ŵali ŵangalikombolela mwa achalume, achakongwe ni ŵanache, ŵakasatiji: “Ambuje ŵetu! Tukoposyani m''musiwu (wa Maaka) wauli achinsyene ŵakwe ŵalupuso, soni tuŵichilani kuumila Kukwenu nkutugosa, soni tuŵichilani kuumila Kukwenu nkutukamuchisya.”');
INSERT INTO "silika" VALUES(4,76,'4:76','Ŵandu ŵakulupilile akuputaga ngondo petala lya Allah, nambo achimakafili akuputaga ngondo petala lya shetani, basi iputani ngondo isyoŵe ya shetani, chisimu malindi ga shetani gaŵele gangali machili.');
INSERT INTO "silika" VALUES(4,77,'4:77','Ana ngankwaona aŵala ŵana kusalilidwaga kuti: “Gakanyani makono genu (kuti gakaaputa achimakafili mpaka chalamule kaje Allah), nambo gambagani kwimika Swala mwakolosya ni kutolaga Zaka?” Sano ndema jakwalamulidwe kwa ŵanganyao kuputa ngondo, yeti likuga line mwa jamanjao lyaliji nkwajogopa ŵandu (makafili) yalikutiji pakun’jogopa Allah kapena woga wakupunda pele. Ni ŵatite: “Ambuje ŵetu! Ligongo chichi ntulamwile kuputa ngondo? Ngati bola nkaatuchelewasye ndema jannono (ikaliji yambone)!” Jilani: “Kusengwa kwa pa duniya kuŵele kwannono, nambo Akhera ni jajili jambone nnope kwa mundu jwali jwawoga (wakun''jogopa Allah), soni ngasin''ja kutendedwa lupuso atamose pa (kachindu kamwana kanti chisawu) kawuusi kankati mwa lupele lwa tende.”');
INSERT INTO "silika" VALUES(4,78,'4:78','Palipose pachim''be chiwa chichimpatepe, atamose n''di m’makome geliye mwa kunyang''amasya, naga umbone uli wapatile (achimakafili ni achinakunaku) akasatiji: “Walakweu ukumila kwa Allah.” Nambo naga chapatile chakusakala ni akasatiji: “Chalakwechi chikuumila kukwenu (mmwe Muhammadi صلى الله عليه وسلم). Jilani: “Yosope ikuumila kwa Allah (mu lisosa Lyakwe).” Ana kwa ŵele ŵanduŵa chipali chichi chikwatendekasya kuti alepele kupikanichisya abali (jilijose)?');
INSERT INTO "silika" VALUES(4,79,'4:79','Umbone uliose waumpatile nikuti ukuumila kwa Allah (ligongo lya ukoto Wakwe), nambo chakusakala chilichose chachimpatile nikuti chikuumila kukwenu mwasyene (ligongo lya itendo yenu yakusakala). Ni tuntumisye (mmwe Muhammadi صلى الله عليه وسلم) kuŵa ntenga kwa ŵandu, sano Allah akukwanila kuŵa Mboni.');
INSERT INTO "silika" VALUES(4,80,'4:80','Jwatampikanile Ntenga صلى الله عليه وسلم nikuti taŵe pamasile pampikanile Allah, nambo jwatagalauche (kuleka yele), basi nganituntumisya (mmwejo Muhammadi صلى الله عليه وسلم) kuŵa jwakwalolela ŵanganyao.');
INSERT INTO "silika" VALUES(4,81,'4:81','Ni akasatiji: “Tuukuunda.” Nambo naga atyosile kukwenu likuga line mwa jamanjao likasachesyaga kutaŵana (inepe) yangaŵaga yankuŵecheta, sano Allah ni akulemba yaakuchesya kutaŵanayo. Basi mwalekani (nkaapa ilagasyo) ni n''jegamile kwa Allah, sano Allah akukwanila kuŵa Nkun''jegama.');
INSERT INTO "silika" VALUES(4,82,'4:82','Ana ni ngaakujichetelela Qur’an? Mwanti jingaŵe jikuumila kwangaŵaga kwa Allah, angausimene mwalakwemo nkanikani wejinji (mwanti jikaliji nkukanilana jisyenepe, nambo nganijiŵa iyyo ata panandi).');
INSERT INTO "silika" VALUES(4,83,'4:83','Soni pati chaichilile chindu chakwayana ni ntunjelele kapena woga, akasachinadiji (kwa ŵandu), mwanti akajiuchisye (abalijo) kwa Ntenga صلى الله عليه وسلم ni kwa aŵala ŵaakwete ulamusi mwa jamanjao, akajimanyi aŵala ŵaakujiungunyaga (abali jilijose) mwa jamanjao (kuti aji jakuŵajilwa kujinadi kapena iyayi). Soni ungaŵaga umbone wa Allah kuti uli pa jenumanja kwisa soni chanasa Chakwe, nkankuyiye shetani, ikaŵeje ŵannono (mwa jenumanja).');
INSERT INTO "silika" VALUES(4,84,'4:84','Basi putani ngondo (mmwe Muhammadi صلى الله عليه وسلم) petala lya Allah, ngankukanganisyidwa (ya ŵandu ŵane) ikaŵeje mwasyene, soni mwachisyani ŵakulupilila, mwine Allah kuŵa nkugachiga machili ga ngondo ga aŵala ŵakanile, sano Allah ni Jwakupundanganya nnope machili, soni Jwakupundanganya nnope pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(4,85,'4:85','Jwatam''bombele (jwine) duwa jambone (janti inkombolechele ichindu yambone), takole liunjili lya (yambone) yakuumila mu jalakwejo, nombe soni jwatam''bombele (jwine) duwa jakusakala, takole liunjili lya (yakusakala) yakuumila mu jalakwejo, sano Allah pa chindu chilichose aŵele Jwakombola (kuchitendela umboni ni kuchisunga).');
INSERT INTO "silika" VALUES(4,86,'4:86','Soni naga nkomasyidwe ni ukomasye uliose, basi jitikani ni ukomasye wambone nnope kuupunda wele, kapena gambani kuwusya wanti chisawu uwwowo, chisimu Allah pachindu chilichose aŵele Nkuŵalanjila mwakosoŵalila.');
INSERT INTO "silika" VALUES(4,87,'4:87','Allah pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, tachinsonganganya kusyene pa lisiku lya Kiyama lyangali kaichila ya lyalakwelyo, ana ni nduni jwali jwakuona nnope pa kaŵechete kumpunda Allah?');
INSERT INTO "silika" VALUES(4,88,'4:88','Ana nkwete chichi chikuntendekasya kuti m''be mikutula jiŵili pa ya achinakunaku, kutendaga Allah ŵausisye ŵanganyao (ni kwagwichisya mu ukafili) ligongo lya yapanganyisye? Ana nkusaka kun''jongola mundu jwannechelele Allah kusokonechela? Sano jwaakwika Allah mwakunnechelela kusokonechela, basi nganim''ba mumpatiile litala (lyakongochela).');
INSERT INTO "silika" VALUES(4,89,'4:89','Akasasachililaga n''di nkufuulu ŵanganyammwe yatite pakufulu ŵanyao ni m''be yalumo (kakufulu kakwe), basi ngasimwatenda mwa ŵanganyao kuŵa isyoŵe mpaka akasame petala lya Allah, nambo naga akugalauka (kuleka yakusamayo), basi mwakamulani ni kwaulaga palipose pa mwasimene, soni ngasimuntenda (jwalijose) mwa ŵanganyao kuŵa nsyoŵe kapena nkukamuchisya.');
INSERT INTO "silika" VALUES(4,90,'4:90','Akaŵeje aŵala ŵaakuliwanga ku ŵandu ŵapali pachilikati chenu ni jemanjao pana chilanga (chantunjelele), kapena ŵaakum’bichilila idali yao ili yakutaŵika (ngasaka) kumenyana namwe kapena kumenyana ni ŵandu ŵao, (ŵele nkaputa ngondo), mwanti Allah angasache angapele machili gakunkombolela ŵanganyammwe, basi ni akamenyene namwe, sano naga ali am''bambele ni ngamenyana namwe soni ni kumpa ntunjelele, basi Allah nganam''bichila pa jamanjao litala (lyakumenyana nao).');
INSERT INTO "silika" VALUES(4,91,'4:91','Chimwasimane ŵane ŵaakusaka kupata ntunjelele kukwenu ni kupata soni ntunjelele ku ŵandu ŵao, ndaŵi ni katema jaakwawuchisya (achimijao) ku ukafili akasagwilagamo chose, nambo naga ngaakum''bambala jenumanja, ni ngampa ntunjelele, soni ni ngagachiga makono gao, basi mwakamulani ni kwaulaga palipose pamwasimene, soni ŵanyao ni aŵala ŵatum''bichile pa ŵanganyao umboni wakuonechela (wakunkundisya kwaputa ngondo).');
INSERT INTO "silika" VALUES(4,92,'4:92','Jwakulupilila nganaŵa am''buleje jwakulupilila (n''jakwe melepe) ni ikaŵeje mwakulemwa, sano jwam''buleje jwakulupilila mwakulemwa, basi agopole kapolo jwakulupilila kwisa soni malipilo gagapelechedwe kwa akuwilwa, ni ikaŵeje ali akululwiche (akuwilwao) mwasadaka. Sano naŵaga (nkuulajidwajo) jukutyochela ku ŵandu ŵali amagongo ŵenu, nambo ni juli jwakulupilila, basi tin''gambe gopola kapolo jwakulupilila (mwangali kutola malipilo gane), nambo naga (nkuulajidwajo) juli jwakutyochela ku ŵandu ŵapali pachilikati chenu ni jemanjao pana chilanga (changamenyana ngondo), basi gapatikane malipilo gakupelechedwa kwa akuwilwa ni gopola kapolo jwakulupilila, sano jwanganapata (yalakwei), basi chataŵe myesi jiŵili jakulondongana, (aji ni) toba jakuumila kwa Allah, ni aŵele Allah Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,93,'4:93','Sano jwacham''bulaje jwakulupilila melepe, basi malipilo gakwe ni Jahannama, chijukaŵe jwandamo mwalakwemo, soni Allah antumbilile ni kunnwesa, soni an''dinganyichisye ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(4,94,'4:94','E jenumanja ŵankulupilile! Pati n''jesile petala lya Allah (kukupuuta ngondo) basi lolechesyani (nkanintende chilichose), soni ngasim''becheta kwa mundu jwampele salâmu (yanti): “Nganim''ba ŵakulupilila mmwejo,” (ni kum''bulaga). Nkusachilila ichindu yakumala ya umi wa duniya! Nambotu kwa Allah ni kwaili yakupata yejinji. Mmomopemo ni mwamwaŵelele ŵanganyammwe kalakala, basi Allah niŵantendele ukoto (pakun''jinjisya mu Usilamu), basi lolechesyani. Chisimu Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(4,95,'4:95','Nganaŵa alandene ŵakulupilila ŵagamba kutama (ngajaula ku ngondo) kwatyosya ŵakwete ipaka (mpela kuulala, ungalola ni kulemala), (ngaŵa nkulandana) ni akuputa ngondo petala lya Allah ni chipanje chao ni achimisyene ŵakwe, Allah ŵanyakwile pa uchimbichimbi akuputa ngondo petala lya Allah ni chipanje chao ni achimisyene ŵakwe kwapunda ŵagamba kutama, sano ŵanaose Allah ŵapele chilanga cha yambone, nambo kuti Allah ŵajonjechesye malipilo gamakulungwa nnope akuputa ngondo kwapunda ŵagamba kutama.');
INSERT INTO "silika" VALUES(4,96,'4:96','(Chachipata) mauchimbichimbi gakuumila Kukwakwe kwisa soni chikululuko ni chanasa, ni aŵele Allah Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,97,'4:97','Chisimu ŵandu ŵaakwatyosyaga Achimalaika mitima jao (pa kuwa) ali ŵalitenda lupuso achimisyene (pangasama pa Maaka), akwaŵechetaga kuti: “Mukaŵechi mwaliji?” Ŵanganyao akutiji: “Twaliji wangalikombolela (ni ŵakuŵanidwa) pa chilambo.” (Achimalaika) akutiji: “Ana chilambo cha Allah nganichiŵa chakusapanguka mwanti jenumanja nikomboleka kusamila (kwine) mwalakwemo?” Basi ŵanganyao mauto gao ni Jahannama, kaje kunyalaya malo gakuwujila gakwe!');
INSERT INTO "silika" VALUES(4,98,'4:98','Ikaŵeje ŵangalikombolela (kusyene) mwa achalume, achakongwe ni ŵanache ŵangaŵa nkupakombola kutenda ulaamba (uliose), soni ŵangaŵa nkulilungama litala (lyakuja ku Madina).');
INSERT INTO "silika" VALUES(4,99,'4:99','Basi ŵanganyao chisimu chene Allah chiŵasimachisye (ilemwa yao), ni aŵele Allah Jwakusiimaasya (ilemwa), Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(4,100,'4:100','Sano jwatasame petala lya Allah tapate pa chilambo malo gakusamila gamajinji ni kusimana chenene, sano jwataŵigule pa nyumba jakwe kusama wa kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم, kaneka chiwa ni kunsimana (petala), basi pamasile pagapatikene malipilo gakwe kwa Allah, ni aŵele Allah Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,101,'4:101','Sano pati nchijendaga pa chilambo, basi tipaŵe pangali ulemwa kukwenu kupungula swala, naga n''di nkogopa kum''bwatanganya aŵala ŵakanile, chisimu achimakafili kukwenu aŵele amagongo ŵakuonechela.');
INSERT INTO "silika" VALUES(4,102,'4:102','Sano naga n''di nao imo (Asilamuo pa ngondo) ni kusaka kwaswalisya swala, basi lijime namwe likuga line mwa jamanjao soni ajigale yida yao, sano pati amasile kusujudu, basi aŵe (nkutenda ulinda) kunyuma kwenu, ni liiche likuga line lyanganiliswali, basi ni liswali namwe, nombe nao akalamuche nikujigala yida yao (achiswaliji), aŵala ŵakanile akasasachililaga yanti n''di nyyipwanyile yida yenu ni ndundu syenu, basi ni an''gungumuchile mwanakamo (tichi!) Sano chipaŵe pangali ulemwa kukwenu kutuula yida yenu naga n''di nkwete isausyo ya wula kapena n''di ŵakulwala, nambo kalamukani. Chisimu Allah ŵalinganyichisye achimakafili ilagasyo yakwalusya. ');
INSERT INTO "silika" VALUES(4,103,'4:103','Sano pati mmasile kuswali, basi munkumbuchilagani Allah chiimile, chitamile kapena n''di n''gonele mbalati syenu, sano naga n''di pa ntunjelele basi jimikani Swala mwakolosya, chisimu Swala kwa ŵakulupilila jiŵele chilamusi chepanjile ndaŵi.');
INSERT INTO "silika" VALUES(4,104,'4:104','Soni ngasimpela pa (kupita nchikonangona) kwakuya ŵandu (amagongo). Nam''baga nkupikana kupoteka, basi nombenao chisimu chene akupikana kupoteka yankuti pakupikana kupoteka ŵanganyammwe, nambo jenumanja nkwembecheya (kupata) kwa Allah yangakwembecheya ŵanganyao, ni aŵele Allah Jwakumanyilila kusyene, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,105,'4:105','Chisimu chene Uwwe tutulwisye kukwenu Chitabu mwakuonaonape kuti n''jilanye chilikati cha ŵandu ni yannosisye Allah (gagali malamusi ga Dini), ni ngasim''ba mmagambo jwa ŵakanyengano (ngasim’ba ŵakwajimila).');
INSERT INTO "silika" VALUES(4,106,'4:106','Ni mum''bendeje Allah chikululuko, chisimu Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,107,'4:107','Ngasimwakagulila (ngasimwajimila) ŵandu ŵaakulinyenga achimisyene, chisimu Allah jwangannonyela mundu jwali jwakanyengano nnope, jwasambi syejinji.');
INSERT INTO "silika" VALUES(4,108,'4:108','Akulisisaga kwa ŵandu (pa yakusakala yao) kutendaga nganaŵa akombwele kulisisa kwa Allah, ngati Jwalakwe akasaŵa nao imo (ni umanyilisi Wakwe) pandema jaakulanganaga chilo pa maloŵe gangannonyela (Jwalakwe), ni aŵele Allah pa ichindu yaakutenda Jwakuisyungula (kwaimanya chenene).');
INSERT INTO "silika" VALUES(4,109,'4:109','Ha! Jenumanja niaŵala ŵankuŵa nkwakagulila ŵanganyao pa umi wa duniya, ana ni nduni jwataakakagulile ŵanganyao kwa Allah lisiku lya Kiyama? Kapena nduni jwatakaŵe nkwachenjela jwao?');
INSERT INTO "silika" VALUES(4,110,'4:110','Sano jwachatende chakusakala chilichose kapena kulitenda lupuso nsyene, kaneka nikum''benda chikululuko Allah, chansimane Allah ali Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,111,'4:111','Sano jwachapanganye chasambi chilichose nikuti akulipanganyichisya nsyene, ni aŵele Allah Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,112,'4:112','Sano jwachapanganye chilemwa kapena chasambi chilichose kaneka nikunnambuchisya nkotokotope, basi pamasile patwichile n’gogolo (unami wekulungwa) ni sambi syakuonechela.');
INSERT INTO "silika" VALUES(4,113,'4:113','Sano ungaŵaga umbone wa Allah kuti uli penumwe (Muhammadi صلى الله عليه وسلم) kwisa soni chanasa Chakwe, chisimu likuga line mwa jamanjao lingasachilile kuti linsokonesye, ni ngakunsokonesya (jwalijose) ikaŵeje achimisyene, soni nganaŵa ampele ŵanganyao chisausyo chilichose, ni antuluchisye Allah Chitabu kwisa soni lunda lwakusokoka, soni an''jiganyisye yamwaliji nkakuimanyilila, ni uŵele umbone wa Allah wauli penumwe wekulungwa kusyene.');
INSERT INTO "silika" VALUES(4,114,'4:114','Mwangali yambone m’mitemela jao jejinji, ikaŵeje jwaakuchisya kutola sadaka kapena kutenda yambone kapena kwilanya chilikati cha ŵandu, sano jwachatende yele pakusachilila chinonyelo cha Allah, basi tuchijisa kumpa malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,115,'4:115','Sano jwachansiisye Ntenga صلى الله عليه وسلم panyuma pakumanyukuka kukwakwe chongoko, niikuya linepe lyangaŵaga litala lya ŵakulupilila, chitun''galauchisye kwaagalauchile (nsyeneko), ni tukan''jinjisye ku Jahannama, kaje kusakala nnope malo gakuwujila gakwe! ');
INSERT INTO "silika" VALUES(4,116,'4:116','Chisimu Allah nganaŵa akululwiche (sambi sya) kum''bwanganya (ni indu ine), nambo akasakululukaga (sambi sine) syanganisiŵa syalakwesi kwa jwansachile. Sano jwakwika mwakum''bwanganya Allah (ni indu ine), nikuti amasile mwapotele kupotela kwakutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(4,117,'4:117','Ngaakuilumbanjila inepe yangaŵaga Jwalakwe (Allah) ikaŵeje isanamu yachikongwe, ni ngaakunnumbanjila jwinepe ikaŵeje shetani jwakuchalila pa unampitiku.');
INSERT INTO "silika" VALUES(4,118,'4:118','Allah ŵannwesile (shetanijo). Ni jwatite: “Chisimu chingalisapulile mwa achikapolo Ŵenu liunjili lyakumanyika.”');
INSERT INTO "silika" VALUES(4,119,'4:119','“Ni tingaasokonesyeje kusyene, soni tingaatandikasyeje isako yaunami, ni tingaachisyeje kuti akasijeje mawiwi ga ilango, ni tingaachisyeje soni kuti akagalausyeje chipago chagumbidwa ni Allah.” Sano jwachantende shetani kuŵa nkun''gosa jwakwe kunneka Allah, nikuti pamasile paapelelwe kupelelwa kwakuonechela. ');
INSERT INTO "silika" VALUES(4,120,'4:120','Akwapaga ilanga ni kwatandikasya isako yaunami, ni ngakwapaga shetani ilanga ikaŵeje yaulaamba.');
INSERT INTO "silika" VALUES(4,121,'4:121','Ŵanganyao liuto lyao ni ku Jahannama, soni ngasapata pakutilila kopoka mwalakwemo.');
INSERT INTO "silika" VALUES(4,122,'4:122','Sano aŵala ŵakulupilile ni kutendaga yambone chitukaajinjisye ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka, (achi ni) chilanga cha Allah chakuonaonape, ana ni maloŵe gacheni gampaka gaŵe gakuona nnope kugapunda ga Allah?');
INSERT INTO "silika" VALUES(4,123,'4:123','(Kwinjila ku Mbepo) nganikuŵa pa chisako chenu kapena chisako cha achinsyene Chitabu (Ayuda ni Akilisito, nambo kuti) jwachatende chakusakala chachilipidwa nacho, soni ngasampata nkun''gosa namuno nkunkamuchisya jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(4,124,'4:124','Soni jwachapanganye masengo gambone kutandila jwannume kapena jwankongwe kutendaga jwalakwejo ali jwakulupilila, basi ŵanganyao niŵatakajinjile ku Mbepo, soni ngasaja kutendedwa lupuso atamose pa (kachindu kamwana kanti chisawu) chisou cha lupele lwa tende.');
INSERT INTO "silika" VALUES(4,125,'4:125','Ana ni nduni jwali ni Dini jambone nnope kwa kumpunda mundu jwaajipeleche ngope jakwe kwa Allah (pakunda Usilamu) aku ali jwakolosya (pa Usilamu wakwe), soni achikuyaga Dini ja Ibrahima jwakongoka, sano Allah ni ŵantesile Ibrahima kuŵa nsyoŵe (Jwakwe).');
INSERT INTO "silika" VALUES(4,126,'4:126','Soni yaili kumawunde ni yaili petaka nsyene jwakwe Allah, ni aŵele Allah pa chindu chilichose Jwaachisyungula (kwachimanya chenene).');
INSERT INTO "silika" VALUES(4,127,'4:127','Soni akum''busya ya achakongwe, jilani: “Allah akusaka kuntagulila ya ŵanganyao, ni yaikusoomedwa kukwenu m''Chitabu (cha Qur’an), ya ŵawina ŵachikongwe aŵala ŵangankwapaga yailembedwe kukwao (kuti apochele mpela mahari), nambo ni nkusachililaga kwalomba, kwisa soni ya ŵanache ŵangalikombolela, ni kuti n''jimilichiche pa (kwagolochesya) ŵawina mwakulungamika. Soni chambone chilichose chankutenda chisimu chene Allah aŵele Jwakuchimanya chenene.');
INSERT INTO "silika" VALUES(4,128,'4:128','Sano naga ŵammasyeto akogopa kwa ŵankwao uchimwa kapena kutimbuka, basi chipaŵe pangali ulemwa kwa ŵanaŵaŵilio kutaŵana chilikati chao ya nkamulano, sano nkamulano ni wambone (kulekangana ni kulekana ulombela), ngati mitima jitawelwe ni usuchi (nambo soni kokomochela ya jikusaka), nambo naga n''di nkolosisye (masengo) ni kuŵa ni woga (wakun''jogopa Allah), basi (ni yambone), chisimu Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(4,129,'4:129','Ni nganim''ba mpakombwele kwilanya (kusyesyene) chilikati cha achimmasyeto ata nkaalimbichiile chantiuli, nambo ni kasimpendechela (ku ŵamo) kupendechela kwa chose, mwanti ni kunneka jwinejo nti chisawu jwankolechedwe (ngamanyika kuti jwannombedwe kapena jwewuli), nambo naga n''di nkamulene ni kuŵa ni woga (wakun''jogopa Allah), basi (ni yambone), chisimu Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,130,'4:130','Nambo naga akulekangana (pakulekana ulombela), Allah tansichilisye jwalijose (mwa ŵanaŵaŵiliwo) kuumila muukoto Wakwe wakusapanguka, ni aŵele Allah Jwakwanila kusyene (ku iwumbe Yakwe), Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,131,'4:131','Soni yaili kumawunde ni yaili petaka nsyene jwakwe Allah, soni chisimu chene pamasile pa twaachisyisye aŵala ŵana kupedwaga chitabu paujo penu (Ayuda ni Akilisito) ni jenumanja ŵakwe yanti mun''jogopani Allah, nambo naga nkufulu, basi (ni yenu), chisimu Allah iŵele Yakwe yaili kumawunde ni yaili petaka, ni aŵele Allah Jwakupata kwejinji Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(4,132,'4:132','Soni yaili kumawunde ni yaili petaka nsyene jwakwe Allah, sano Allah akukwanila kuŵa Nkun''jegama.');
INSERT INTO "silika" VALUES(4,133,'4:133','Naasakaga Jwalakwe mpaka antyosye jenumanja achinaŵandu ni kwika nao ŵane, ni aŵele Allah pa yalakweyo Jwakombola kusyene.');
INSERT INTO "silika" VALUES(4,134,'4:134','Jwaakuŵa ali nkusaka malipilo ga duniya, basi kwa Allah nikwakuli malipilo ga duniya ni Akhera, ni aŵele Allah Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(4,135,'4:135','E jenumanja ŵankulupilile! Ŵani ŵakwimilichika pa chilungamiko, ŵakupeleka umboni ligongo lya Allah namuno uli wakun''galauchila mwachimisyene, kapena achinangolo ŵaŵili ni ŵaulongo, namuno ali jwakupata kapena jwakulaga (mmwe kasinkosoŵalila yele), ngati Allah ni jwali jwakuŵajilwa kosoŵalila ya ŵanaŵaŵilio, basi kasinkuya isako ni kuleka chilungamiko, nambo naga mpombotasye (umboni) kapena kana (kupeleka umbonio), basi (ni yenu), chisimu Allah abali syosope sya yankutenda aŵele Jwasimanya chenene.');
INSERT INTO "silika" VALUES(4,136,'4:136','E jenumanja ŵankulupilile! Munkulupililani Allah ni Ntenga Jwakwe صلى الله عليه وسلم kwisa soni Chitabu chaatulwisye kwa Ntenga Jwakwe صلى الله عليه وسلم ni Chitabu chaŵatulwisye kalakala, sano jwaakwika mwakunkana Allah, malaika Gakwe, itabu Yakwe, achimitenga Ŵakwe ni Lisiku Lyambesi, nikuti amasile mwapotele kupotela kwakutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(4,137,'4:137','Chisimu aŵala ŵakulupilile kaneka nikufulu, soni nikwisa kulupilila kaneka ni kufulu soni, kaneko nikwisa konjechesya nnope kufulu, Allah nganaŵa ŵakululuchile ŵanganyao namuno kwajongolela kwitala (lyagoloka).');
INSERT INTO "silika" VALUES(4,138,'4:138','Mwatagulilani achinakunaku yanti akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(4,139,'4:139','Aŵala ŵaakwatenda achimakafili kuŵa isyoŵe yao kwaleka ŵakulupilila. Ana akusakaga kwa ŵanganyao uchimbichimbi? Basi uchimbichimbi wosopetu nsyene jwakwe ni Allah.');
INSERT INTO "silika" VALUES(4,140,'4:140','Soni pamasile paitulusyidwe kukwenu m’Chitabu (Qur’an) yanti: “Ndema jankupikana ma Ȃya ga Allah gali nkukanidwa ni kutendedwa chipongwe, basi ngasintama nao imo mpaka ajinjile mu ngani jine jangaŵaga jele, (nambo kwika mwakutama nao imo), nikuti pajele ndemajo mpaka m''be chisawu ŵanganyao.” Chisimu Allah ni Nkwasonganganya ku Moto achinakunaku ni achimakafili wosope ŵene.');
INSERT INTO "silika" VALUES(4,141,'4:141','Aŵala ŵaakuŵa ali nkun''jembecheya ŵanganyammwe (kuti impate ipato), basi naga kupunda kwakuumila kwa Allah kuli kukwenu akasatiji: “Ana nganituŵa namwe imo?” Nambo naga achimakafili apatile kaupile (panandi ka kwakombola Asilamu, achinakunakuo) akutiji: “Ana nganitusigala panandi kuti tun''guŵatile (pa ndema jatwaliji ni Asilamu), soni ni twaliji nkunsiŵilila ku ŵakulupilila (kuti akaika mwankombwele)!” Basi Allah tachijilanya chilikati chenu lisiku lya Kiyama, soni Allah nganaŵa aŵisile litala kwa achimakafili lyakwapundila ŵakulupilila.');
INSERT INTO "silika" VALUES(4,142,'4:142','Chisimu achinakunaku akuntenda chinyengo Allah, sano Jwalakwe ni tachalipila pa chinyengo chaocho, soni pati ajimi kuswali akwimaga mwaulesi, akutenda ndola kwa ŵandu soni ni ŵangankolanga Allah ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(4,143,'4:143','Akuŵa nkutogandoga chilikati cha yalakweyi (ya chikulupi ni ukafili), nganaŵa kwa ŵanganyaŵa namuno kwa ŵanganyao, sano jwannechelele Allah kuti asokonechele, basi nganim''ba mumpatiile litala (lyagoloka).');
INSERT INTO "silika" VALUES(4,144,'4:144','E jenumanja ŵankulupilile! Ngasimwatenda achimakafili kuŵa isyoŵe yenu kwaleka ŵakulupilila, ana nkusaka kuti mum''bichile Allah umboni wakuonechela pa jenumanja (kuti ŵakusakala)?');
INSERT INTO "silika" VALUES(4,145,'4:145','Chisimu achinakunaku chachiŵa pasi pasyesyene pa Moto, soni nganim''ba mwapatiile ŵanganyao nkwajokola. ');
INSERT INTO "silika" VALUES(4,146,'4:146','Ikaŵeje aŵala ŵatesile toba (pa kuleka unakunaku wao), ni kwilanya (ndamo syao), ni kulikamulichisya mwa Allah, ni kunswejechesya Allah Dini jao, basi ŵanganyao ni ŵali pamo ni ŵakulupilila, sano Allah ni tachijisa kwapa ŵakulupilila malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,147,'4:147','Ana Allah mpaka annagachisye ya, naga n''di ntogolele ni kulupilila? Ni aŵele Allah Jwakutogolela kusyene, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(4,148,'4:148','Allah jwangannonyela kuinadi (mundu) yakusakala (ya jwine) ikaŵeje jwatendeledwe lupuso, ni aŵele Allah Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(4,149,'4:149','Kanga tinchilosye chambone chilichose (pakutenda) kapena tinchisise, kapena tinsiimaasye chakusakala chilichose (chantendele jwine), basi (yosope chiiŵe yambone), chisimu Allah aŵele Jwakusiimaasya (ilemwa), Jwakombola kusyene.');
INSERT INTO "silika" VALUES(4,150,'4:150','Chisimu aŵala ŵaakunkana Allah ni achimitenga Ŵakwe, ni kusakaga ali nkulekanganya chilikati cha Allah ni achimitenga Ŵakwe, nikuŵechetaga (yanti): “Ŵane tukwakulupilila nambo ŵane tukwakana,” ni kusakaga kujigala litala lya chilikati cha yalakweyi (ngaŵa lya Usilamu usyesyene kapena lya ukafili),');
INSERT INTO "silika" VALUES(4,151,'4:151','Ŵanganyao ni ŵali makafili gasyesyene, ni twalinganyichisye achimakafili ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(4,152,'4:152','Sano aŵala ŵankulupilile Allah ni achimitenga Ŵakwe, ni ngalekanganyaga chilikati cha jwalijose mwa ŵanganyao, jamanjao tachija kwapa malipilo gao, soni ni aŵele Allah Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(4,153,'4:153','Achinsyene chitabu (Ayuda) akum''benda (mmwe Muhammadi صلى الله عليه وسلم) yanti mwatuluchisye chitabu kuumila kwinani, chisimu chene ŵanganyao pakwete pam''bendile Musa yekulungwa kupunda yele, pandema jaŵatite: “Tulosyani Allah mesonkulola.” Basi nichakwembekenye chikokomo cha moto (wanjasi) ligongo lya lupuso lwao, kaneka ni ŵakatesile kang''ombe (kuŵa kannungu kao) panyuma pakwaichilila ilosyo yakuonechela, nambo twakululwichepe yalakweyo, ni twampele Musa umboni wa ulamusi wakuonechela.');
INSERT INTO "silika" VALUES(4,154,'4:154','Ni twalinyakwile litumbi pachanya pao pakutaŵana nao chilanga (chaakuya Taurat). Ni twasalile kuti: “Jinjililani pannango (wa chilambo cha Shami) chiinamile.” Nambo soni twasalile kuti: “Ngasinsumbaga mpika pa (kasa uchimbichimbi wa lisiku) Lyakuŵeluka.” Ni twajigele kutyochela kukwao chilanga chakulimbangana. ');
INSERT INTO "silika" VALUES(4,155,'4:155','Basi (twalwesile) ligongo lyakasa kwao chilanga ni kanila kwao ma Ȃya ga Allah, ni kuulaga kwao achimitume mwangali ligongo, ni kuŵecheta kwao kwanti: “Mitima jetu jewunichilidwe (kwanti ikulepeleka kwinjila yankulalichila mmwe Muhammadi صلى الله عليه وسلم).” (Ngaŵa jewunichile,) nambo Allah atekudinda idindo pa jalakwejo ligongo lya ukafili wao, ni ngakukulupilila ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(4,156,'4:156','Soni ligongo lyaukafili wao ni kunnambuchisya kwao Maryam unami wekulungwa nnope (wakuti ŵakwete mwanache jwapetinji - achambaga Isa).');
INSERT INTO "silika" VALUES(4,157,'4:157','Ni kuŵecheta kwao (mwakutwanga kuti): “Uwwe twam''buleje Masihi Isa mwanache jwa Maryam ntenga jwa Allah,” kutendaga nganam''bulaga soni nganankomela pansalaba, nambo kuti ŵagambile kupupuliganyisyidwa (pakuwulaga mundu jwinepe kupelaga Isa ligongo lya kulandana), soni chisimu aŵala ŵatindene mu ngani (ja kuwulajidwa kwa Isa) ali mu liwaniwani ni yalakweyi. Nganakola ŵanganyao pa yalakweyi umanyilisi (usyesyene) ikaŵeje agamba kuya liganichisya (basi), soni chisimu chene nganam''bulaga (Isajo).');
INSERT INTO "silika" VALUES(4,158,'4:158','Nambo Allah ŵankwesisye Kukwakwe, ni aŵele Allah Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,159,'4:159','Soni pangali jwalijose mwa achinsyene chitabu (Ayuda ni Akilisito) ikaŵeje chachinkulupilila kusyene (Isajo kuti jwaliji ntume jwa Allah ngaŵa mwanache Jwakwe soni kuti ŵaliji mundu ngaŵa Nnungu, chachikulupilila yalakweyi) chiwa chakwe chili chisigele panandi (nambo ngasichinkamuchisya chikulupicho), sano lisiku lya Kiyama tachiŵa (Isajo) mboni pa ŵanganyao (pakwakana ŵaŵankaaniile ni kwajitika ŵaŵan''jiitiiche).');
INSERT INTO "silika" VALUES(4,160,'4:160','Sano ligongo lya lupuso lwakuliitenda aŵala Ayuda, twaitesile kuŵa ya haraam kukwao yambone yayaliji ya halaal kwa ŵanyao, ni ligongo lyakwasiŵilila kwao (ŵandu) ŵajinji kwitala lya Allah.');
INSERT INTO "silika" VALUES(4,161,'4:161','Ni ligongo lya kujigala kwao katapila kutendaga ŵakanyisyidwe yalakweyo, ni kulya kwao soni chipanje cha ŵandu mwaukoleko, ni twalinganyichisye achimakafili mwa ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(4,162,'4:162','Nambo ŵatiŵiile mu umanyilisi mwa ŵanganyao, ni ŵakulupilila ŵakwe (Asilamu), akukulupilila yayitulusyidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) ni yayatulusyidwe paujo penu (mmwe nkanim’biche). Sano ŵaakwimika Swala mwakolosya, ni kutolaga Zaka, ni ŵaakunkulupilila Allah ni Lisiku Lyambesi, ŵanganyao tuchapa malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(4,163,'4:163','Chisimu Uwwe tujuuwiile kukwenu (mmwe Muhammadi صلى الله عليه وسلم maloŵe gakusisika), yatwatite pakuuwula kwa Nuhu ni achimitume ŵaŵaiche panyuma pakwe, twaajuuwiile soni kwa Ibrahima, Ismaila, Isihaka, Yaakubu pamo ni isukulu yakwe, (soni kwa) Isa, Ayyubu, Yunusu, Haruna ni Sulaimana, ni twampele Daudi Zaburi.');
INSERT INTO "silika" VALUES(4,164,'4:164','Ni achimitenga ŵatuntagulile kala kwisa soni achimitenga ŵanganituntagulila, ni ŵam''bechetekasisye Allah Musa mwachindonda (mwangali jwakulungusya maloŵe).');
INSERT INTO "silika" VALUES(4,165,'4:165','Achimitenga ŵakutagulila abali syakusengwasya, soni ŵakutetela kuti kasaja kola ŵandu maloŵe gakuliŵalanga kwa Allah panyuma pa (kwika) achimitenga, ni aŵele Allah Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,166,'4:166','Nambo Allah akutendela umboni yatulwisye kukwenu (mmwe Muhammadi صلى الله عليه وسلم kuti yakuonaonape) ayitulwisye mwa umanyilisi Wakwe, nombe nao Achimalaika akuŵichila umboni, sano Allah akukwanila kuŵa Mboni.');
INSERT INTO "silika" VALUES(4,167,'4:167','Chisimu aŵala ŵakanile ni kusiŵililaga (ŵandu) kwitala lya Allah, nikuti amasile mwapotele ŵanganyao kupotela kwakutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(4,168,'4:168','Chisimu aŵala ŵakanile ni kutendaga lupuso, Allah nganaŵa ŵakululuchile kapena kwajongolela kwitala lililyose.');
INSERT INTO "silika" VALUES(4,169,'4:169','Ikaŵeje litala lya ku Jahannama, chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka, soni yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(4,170,'4:170','E jenumanja achinaŵandu! Pamasile pam’bichilile Ntenga (Muhammadi صلى الله عليه وسلم) ni yakuona kuumila kwa M''mbuje gwenu, basi kulupililani; chiyiŵe yambone kukwenu, nambo naga nkane, basi (niyenu), chisimu Allah iŵele Yakwe yosope yaili kumawunde ni petaka, ni aŵele Allah Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(4,171,'4:171','E jenumanja achinsyene chitabu (Ayuda ni Akilisito)! Ngasinsumbanga mpika pa dini jenu, soni ngasimum''bechetelaga Allah ikaŵeje yakuonaonape. Chisimu Masihi Isa mwanache jwa Maryam ntenga jwa Allah, soni (juŵele chiumbe chagumbidwa ni) liloŵe Lyakwe (lya Allah lyakuti: “M''be!”) lyaŵaliponyisye kwa Maryam, soni (jukwete) nsimu wakuumila Kukwakwe (mujiŵelele misimu jine josope). Basi munkulupililani Allah ni achimitenga Ŵakwe, soni nkatiji: “Ŵatatu,” lekani (chikulupi cha utatu wa Allah) chiiŵe yambone kukwenu, chisimu Allah ni Nnungu Jumope, kuswejela ni Kwakwe kwakuŵambalana nayo yakola mwanache, iŵele Yakwe yosope yaili kumawunde ni yaili petaka, soni Allah akukwanila kuŵa Nkun''jegama. ');
INSERT INTO "silika" VALUES(4,172,'4:172','Masihi nganaŵa alikwesisye kwakana kuŵa kapolo jwa Allah, namuno Achimalaika ŵaŵandichisyidwe (kwa Allah), sano ŵataiche mwakana kun''galagatila Jwalakwe ni kulikwesya, basi tachasonganganya Kukwakwe ŵanawose.');
INSERT INTO "silika" VALUES(4,173,'4:173','Sano nambo aŵala ŵakulupilile ni kutendaga yambone, basi chachalipila malipilo gao mwakwana ni kwajonjechesya mu ukoto Wakwe, nambo aŵala ŵakanile (kun''galagatila Allah) ni kulikwesya, basi tachalagasya ni ilagasyo yakupoteka nnope, soni ngasampata nkwagosa namuno nkwakamuchisya jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(4,174,'4:174','E jenumanja achinaŵandu! Pamasile paum’bichilile umboni wakusimichika (Muhammadi صلى الله عليه وسلم) kuumila kwa M''mbuje gwenu, ni tuntuluchisye lilanguka lyakuonechela (Qur’an).');
INSERT INTO "silika" VALUES(4,175,'4:175','Sano nambo aŵala ŵankulupilile Allah ni kulikamulichisya Kukwakwe, basi tachajinjisya mu chanasa chakuumila Kukwakwe ni ukoto, soni tiŵajongolele Kukwakwe ni litala lyagoloka.');
INSERT INTO "silika" VALUES(4,176,'4:176','Akum''buusya (mmwe Muhammadi صلى الله عليه وسلم ya kagaŵanye ka chipanje chamalekwa), jilani: “Allah akumpa lilamusi lya (kagaŵanye ka chipanje cha) mundu jwaakuwa ali jwangali mwanache (kapena chisukulu) namuno achinangolo ŵakwe ŵanaŵaŵili, (kuti) naga mundu awile jwangali mwanache nambo akwete nnumbugwe (jwakuchibaba), nikuti akwete kupata (nnumbugwejo) litika (1⁄2) lya yalesile (nchimwenegwejo). Nombenajo (nchimwene naga nkuwajo juli nnumbugwe jwakuchibaba), chajigale chipanje chosope cha nnumbugwejo naga jwangali mwanache. Sano naga ŵaliji (akusigalao achalumbugwepe) ŵaŵili, nikuti akwete kupata maunjili gaŵili mmaunjili gatatu (2⁄3) ga yalesile (nchimwenegwaojo). Nambo naga ŵaliji (akusigalao) ŵachilumbu (ŵawanganyichilane) achalume ni achakongwe, nikuti jwannume akwete kupata liunjili lyakulandana ni liunjili lya achakongwe ŵaŵili (2:1). Allah akunsalichisya (malamusi Gakwe) ntakwika munsokonechele, soni Allah chindu chilichose aŵele Jwakuchimanya chenene.”');
INSERT INTO "silika" VALUES(5,1,'5:1','E jenumanja ŵankulupilile! Kwanilisyani ilanga. Iŵele halaal kukwenu (kulya) inyama yakulanga (mpela ngamiya, ng''ombe, ngondolo ni mbusi) ikaŵeje yayikusoomedwa kukwenu (mu Qur’an kuti ya haraam). Nganigaŵa gakundisyidwa maani kutendaga n''di nnuyiye (ibada ya Hija kapena Umra), chisimu Allah akulamula yaakusaka.');
INSERT INTO "silika" VALUES(5,2,'5:2','E jenumanja ŵankulupilile! Ngasinkuchembulusya kuchimbichika kwa imanyilo ya (Dini ja) Allah, namuno mwesi wakuchimbichika, namuno (kuchimbichika kwa inyama yaitumisyidwe ku Maaka kuŵa) mbepesi, namuno makosa (gaikuwechedwaga inyamayo gakulosya kuti mbepesi sya ku Maaka), namuno (kuchimbichika kwa ŵandu) ŵaakunuya ya kwaula ku Nyumba Jakuchimbichika achisachililaga umbone wakuumila kwa M’mbuje gwao ni chinonyelo, sano pati nkopweche (mu ibada ya Hija kapena Umra), basi lupatani (naga nkusaka), soni kwaŵenga ŵandu ligongo lyanti ansiŵiiliile kuti nkaika ku Nsikiti Wakuchimbichika (wa ku Maaka), kukantendekasya kuti nsumbe mpika (pakwauchisya ukoleko wao), nambo kamusyanganani pa yaukoto ni yawoga (wakun''jogopa Allah), ni ngasinkamusyangana pa yasambi ni yaumagongo, soni mun''jogopani Allah, chisimu Allah ni Jwaukali nnope pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(5,3,'5:3','Iŵele haraam kukwenu (kulya) chibudu, myasi, nyama jeguluŵe, ni chachikolanjilidwe (lina linepe) lyangaŵaga lya Allah pakuchisikita (kuŵa mbepesi ja akumasoka), ni chachiwile ligongo lya kutilinyisyidwa (kupotedwa), ni chachiwile ligongo lya kuputwa, ni chachiwile ligongo lya kusenjelemukuka, ni chachiwile pakutotedwa ni misengo (ja chijakwe), ni chachiwile ligongo lya kuliidwa ni chinyama cha mwitinji - ikaaŵeje chansikite (nkanichimalichiche kuwa), ni chachisikitilidwe peganga (kapena pansolo pakupelechela mbepesi kwa akumasoka), soni (kuŵele kwakanyisyidwa kukwenu) kusaka kuimanya yakusisika pakulondola isango, yosope yeneyi iŵele ulemwa. Lelo akatile tama aŵala ŵakanile pa (yakujimasya) dini jenu (ja Usilamu), basi ngasimwajogopaga ŵanganyao nambo munjogopeje Une. Lelo nankwanilichisye dini jenu ni ngwanisye chindimba Changu pa jenumanja ni nansagulile Usilamu kuŵa dini jenu. Nambo jwaasingaliilwe ni sala (ni kulya ya haraamuyo), mwangali kupendechela ku ulemwa, basi (ngapata sambi), chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(5,4,'5:4','Akum''buusya (mmwe Muhammadi صلى الله عليه وسلم) kuti ana ichichi yayili halaal kukwao (kulya)? Jilani: “Iŵele halaal kukwenu (kulya) yambone (yosope), kwisa soni (nyama jammikuuli jajikamwilwe ni) inyama kapena ijuni yakusakulila yanyyijiganyisye kasakule ka ikwembelechesya, nkwijiganyaga (matala gakusakulila) gan''jiganyisye Allah, basi lyani yayinkamulile ni nllikolanjeje lina lya Allah pa ikwembelechesya, soni mun''jogopani Allah. Chisimu Allah ni Jwachitema nnope pakuŵalanjila.”');
INSERT INTO "silika" VALUES(5,5,'5:5','Lelo iŵele halaal kukwenu (kulya) yambone (yosope), niyakulya ya aŵala ŵaŵapedwile Chitabu (Ayuda ni Akilisito) iŵele halaal kukwenu, nombe yakulya yenu iŵele halaal kukwao, ni (nkundisyidwe soni kwalomba) achakongwe ŵambone mu ŵakulupilila (ŵachisilamu) kwisa soni achakongwe ŵambone mwa aŵala ŵanakupedwaga Chitabu paujo penu (Ayuda ni Akilisito) naga n''di mwapele mahari gao (mbiya syandowa kapena chilichose chakwalombelela), mwakwalomba ngaŵaga mwachikululu kapena kwatenda maganja, sano jwatakane chikulupi, nikuti pamasile pagajonasiche masengo gakwe, soni jwalakwejo ku Akhera tachiŵa mu ŵakwasika.');
INSERT INTO "silika" VALUES(5,6,'5:6','E jenumanja ŵankulupilile! Pandema jankwima kuja ku swali, basi josyani ngope syenu kwisa soni mikono jenu mpaka mu isukusuku, soni pakani (mesi) pa mitwe jenu, ni (kosya) sajo syenu mpaka m’misomali, soni naga n''di ni janaba, basi liswejesyani (pakoga chilu chosope), Sano naga n''di ŵakulwala, kapena pa ulendo, kapena jumo mwa jenumanja ayiiche kutyochela kukaŵenduka (kuchimbusi), kapena nkwayanile ni achimmasyeto (pakutenda nsangu), ni nganin''gapata mesi, basi gambani kutenda Tayammamu ni litaka lyambone, lipakaani kungope kwenu ni mmakono mwenu ni lyalakwelyo, Allah ngakusaka kuŵika pa jenumanja chakunonopa chilichose, nambo akusaka kuti answejesye ni akwanisye chindimba Chakwe pa jenumanja kuti m''be ŵakutogolela.');
INSERT INTO "silika" VALUES(5,7,'5:7','Soni kumbuchilani chindimba cha Allah chachili pa jenumanja, ni chilanga Chakwe chajwataŵene namwe chila, pandema jamwatite: “Tupikene soni tukundile.” Soni mun''jogopani Allah, chisimu Allah ni Nkuimanya chenene yayili m’mitima (mwenu).');
INSERT INTO "silika" VALUES(5,8,'5:8','E jenumanja ŵankulupilile! Ŵani ŵakwimilichika (pakupitisya yakuona) ligongo lya Allah, ŵakupeleka umboni mwakulungamika, soni kwaŵenga ŵandu kukantendekasya kuti nkalungamika, lungamikani, kutenda iyyoyoko nikwakuli kwakuŵandichila nnope ku woga (wakun''jogopa Allah), soni mun''jogopani Allah, chisimu Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(5,9,'5:9','Allah ŵapele chilanga aŵala ŵakulupilile nikutendaga yambone (chanti), akwete kupata ŵanganyao chikululuko ni malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(5,10,'5:10','Nambo ŵandu ŵaakufuulu ni kanila ma Ȃya Getu, ŵanganyao niŵandu ŵa ku Moto Waukali.');
INSERT INTO "silika" VALUES(5,11,'5:11','E jenumanja ŵankulupilile! Kumbuchilani chindimba cha Allah pa jenumanja, pandema jaŵasachilile ŵandu kuti ajongole makono gao kukwenu (kusaka kumputa ngondo), basi (Allah) ni ŵagachijile makono gaogo kuti gakanyyichila ŵanganyammwe, soni mun''jogopani Allah, soni kwa Allah pe basi ajegamile ŵakulupilila.');
INSERT INTO "silika" VALUES(5,12,'5:12','Soni pamasile paŵajigele Allah chilanga cha ŵanache ŵa Israila, ni twatomeche mwa ŵanganyao achinnongola likumi kwisa ŵaŵili, sano Allah ni jwatite: “Chisimu Une ndinamwe, naga nchiimikaga Swala mwakolosya ni kutolaga Zaka ni kwakulupililaga achimitenga Ŵangu, ni kwakamuchisyaga, ni kunkongosyaga Allah ngongole jambone, chisimu chene tinansiimachisye yakusakala yenu, soni tinjin''jinjisya kwene ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, nambo jwachakane panyuma pa chele (chilangachi) mwa jenumanja, nikuti amasile mwasokonechele (kuleka) litala lyagoloka.”');
INSERT INTO "silika" VALUES(5,13,'5:13','Basi ligongo lyakasa chilanga chao twalwesile, ni twajitesile mitima jao kuŵa jakuumula, akuŵa ali nkupitikusya maloŵe pakugatyosya m’mangwakwe, soni ni aliŵalile liunjili lya (utenga wakunkuya Muhammadi صلى الله عليه وسلم) waŵakumbusyidwe (mu Taurat), ni nganneka kuŵa n''di nkuukopochela ulaamba mwa ŵanganyao ikaŵeje ŵannono mwa jamanjao, basi mwakululuchilani ni kwikwanjila (yangalumbana yao), chisimu Allah akwanonyela ŵakolosya.');
INSERT INTO "silika" VALUES(5,14,'5:14','Nombe soni kuumila kwa aŵala ŵakutiji: “Chisimu uwwe Akilisito,” twajigele chilanga chao, basi ni ŵaliŵalile liunjili lya (utenga wakunkuya Muhammadi صلى الله عليه وسلم) waŵakumbusyidwe (mu Injil), basi ni tupandile chilikati chao utinda ni uŵengani mpaka lisiku lya Kiyama, ni tachiika kwatagulila Allah yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(5,15,'5:15','E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Pamasile pam''bichilile Ntenga Jwetu (Muhammadi صلى الله عليه وسلم) jwaakunsalichisya palangulangu yaijinji mu yamwaliji nkusisa ya m’chitabu (cha Baibulo), soni ni akuileka yaijinji, pamasile palim''bichilile kuumila kwa Allah lilanguka (Muhammadi صلى الله عليه وسلم) kwisa soni Chitabu chakusalichisya kusyesyene (Qur’an).');
INSERT INTO "silika" VALUES(5,16,'5:16','Allah akwajongolela nacho ŵandu ŵaakukuya chinonyelo Chakwe kumatala gantendele, ni akwakoposya m’chipi ni kwajinjisya mwilanguka mwa lisosa Lyakwe, soni ni akwajongolela kwitala lyagoloka.');
INSERT INTO "silika" VALUES(5,17,'5:17','Pamasile paakufuulu aŵala ŵaakutiji: “Chisimu Allah ni Masihi mwanache jwa Maryam.” Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana nduni jwampaka akakwete chilichose chakumpinga Allah angasache Jwalakwe kun''jonanga Masihi (Isa) mwanache jwa Maryamujo, kwisa soni mamagwe ni wosope ŵali pachilambo? Soni Allah ni Nsyene uchimwene wa kumawunde ni petaka ni yaili chilikati chakwe, akugumbaga chaakusaka, soni Allah pachindu chilichose aŵele Jwakombola.”');
INSERT INTO "silika" VALUES(5,18,'5:18','Soni Ayuda ni Akilisito akutiji: “Uwwe niŵanache ŵa Allah soni isyoŵe yakwe.” Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Nambi uli akumpaga ilagasyo pa sambi syenu? Nambo jenumanja ŵanduŵandupe mu iwumbe yaagumbile, akunkululuchilaga jwansachile ni kumpa ilagasyo jwansachile, soni Allah ni Nsyene uchimwene wa kumawunde ni petaka ni yaili chilikati chakwe, soni Kukwakwe ni kwakuujila.”');
INSERT INTO "silika" VALUES(5,19,'5:19','E jenumanja achimisyene chitabu (Ayuda ni Akilisito)! Pamasile pam''bichilile Ntenga Jwetu (Muhammadi صلى الله عليه وسلم) jwaakunsalichisya ŵanganyammwe pandema jakulechesya (kwika) achimitenga, kuti kasin''ja kuŵecheta (yanti): “Nganatuichilila nkutagulila abali jakusengwasya namuno nkutetela.” Sano pamasile pam''bichilile nkutagulila abali jakusengwasya soni nkutetela, soni Allah pachindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(5,20,'5:20','Soni (kumbuchilani) katema kajwaŵechete Musa kwa ŵandu ŵakwe (kuti): “E ŵandu ŵangu! Kumbuchilani chindimba cha Allah pa jenumanja pandema jaŵatesile ŵane mwa jenumanja kuŵa achimitume, niŵantesile kuŵa achiŵenye, ni ŵampele jenumanja yanganampeje jwalijose mu iwumbe (yakala).”');
INSERT INTO "silika" VALUES(5,21,'5:21','“E ŵandu ŵangu! Jinjilani m’chilambo chakuswejesyedwa (cha Falastini) channembele Allah, ni ngasim''bujila munyuma mwenu (kutila) ntakwika mun''galawiche kuwa ŵakwasika.”');
INSERT INTO "silika" VALUES(5,22,'5:22','Ŵanganyao ŵatite: “E mmwe Musa! Chisimu mwalakwemo mwana ŵandu ŵamachili nnope, soni chisimu uwwe nganituŵa tujinjilemo mpaka akakopochemo, nambo naga akopochemo, basi uwwe chisimu chene tuŵe nkwinjila.”');
INSERT INTO "silika" VALUES(5,23,'5:23','Ŵandu ŵaŵili (Yoshwa ni Kaalab) mu ŵandu ŵaŵaliji ni woga (wakun''jogopa Allah, aŵala) ŵaŵapele Allah ukoto ŵatite: “Mwaajinjililani ŵanganyao pannango, sano naga nkwika mwakuchijinjila (chilambocho), basi chisimu ŵanganyammwe tim''be ŵakupunda, soni kwa Allah pe basi jegamilani nam''baga jenumanja ŵakulupilila (kusyene).”');
INSERT INTO "silika" VALUES(5,24,'5:24','Ŵanganyao ŵatite: “E mmwe Musa! Uwwe nganituŵa tujinjilemo atapanandi muchalonjelele ŵanganyao ali mwalakwemo, basi jaulani mmwejo ni Ambuje ŵenu (Allah) ni nkamenyane nao, chisimu uwwe ŵachitamile panopano.”');
INSERT INTO "silika" VALUES(5,25,'5:25','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu une jwangali ukombosi (wakunkanganichisya jwalijose kukuya malamusi Genu), ikaŵeje nansyene ni nkulugwangu, basi lekanganyani chilikati chetuwe ni ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(5,26,'5:26','(Allah) jwatite: “Basi nikuti chalakwe (chilambo cha Falastinacho) chiŵele chakanyisyidwa kukwao (kuchijinjila), kwayaka makumi ncheche, chagambeje kwendajenda pa chilambo, basi ngasimwadandaulila ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(5,27,'5:27','Soni mwasoomelani (mmwe Muhammadi صلى الله عليه وسلم) abali ja ŵanache ŵaŵili ŵa Adam (Haabiil ni Qaabiil) mwakuonaonape, pandema jaŵapeleche mbepesi (kwa Allah), basi ni japocheledwe ja jumo jwao (Haabiil) nambo ja jwinejo nganijipocheledwa, (Qaabiil) jwatite: “Chisimu chene une chinam''bulaje.” (Haabiil) jwatite: “Chisimu Allah akasapochela (mbepesi) ja ŵakun''jogopa (Jwalakwe).”');
INSERT INTO "silika" VALUES(5,28,'5:28','“Chisimu naga nkongola nkono wenu kukwangu kuti mumbulaje, une ngamba jwakongola nkono wangu kukwenu kuti nam''bulaje, chisimu une ngun''jogopa Allah M''mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(5,29,'5:29','“Chisimu une ngusaka kuti m''buje n''di ntwichile sambi syangu ni sambi syenu, ni tin''jise kuŵa mu ŵandu ŵa ku Moto, soni galakwego ni malipilo ga ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(5,30,'5:30','Basi (Qaabiil) ntima wakwe wanchisisye yakum''bulaga mpwakwe ni ŵam''buleje, basi ni jwasyuchile kuŵa mu ŵakwasika.');
INSERT INTO "silika" VALUES(5,31,'5:31','Basi Allah ŵatumisye likungulu lyalyaliji nkusokola pasi kuti linnosye yatajile pakusika malilo ga mpwakwe. (Qaabiil) jwatite: “Kutapana kwangune! Yeti nepeleje kuti mbe mpela ali likungululi, kuti siiche malilo gampwanga.” Basi ni jwasyuchile kuŵa mu ŵakuligamba.');
INSERT INTO "silika" VALUES(5,32,'5:32','Paligongo lya yalakweyi twalamwile ŵanache ŵa Israila yanti: jwaawuleje mundu mwangali (kuti nkuwulajidwajo jwate kuwulaga) mundu jwine kapena kuwatanganya pa chilambo, basi iŵele mpela awuleje ŵandu wosope, nombe soni jwaakulupwisye umi wa mundu, basi iŵele mpela akulupwisye umi wa ŵandu wosope. Soni pamasile paŵayichilile achimitenga Ŵetu ni ilosyo yakuonechela, kaneko chisimu ŵajinji mwa ŵanganyao panyuma pa yalakweyo ŵapelenganyagape konanga pachilambo.');
INSERT INTO "silika" VALUES(5,33,'5:33','Chisimu malipilo ga aŵala ŵaakumenyana ngondo ni Allah ni Ntenga Jwakwe صلى الله عليه وسلم nikupitaga nchiwatanganya pachilambo, awulajidwe kapena akomeledwe pansalaba kapena gakatidwe makono gao ni ngongolo syao mwakutindanya (ligasa lyakun''dyo ni likau lyakunchiji, kapena ligasa lyakunchiji ni likau lyakun''dyo), kapena atopoledwe m’chilambo, kwalakweku ni kwaluka kwao pa duniya, soni akwete kupata ŵanganyao ku Akhera ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(5,34,'5:34','Ikaŵeje aŵala ŵatesile toba nkanim''biche mwakwakamula, basi manyililani yanti Allah ni Jwakululuka kusyene Jwachanasa channope.');
INSERT INTO "silika" VALUES(5,35,'5:35','E jenumanja ŵankulupilile! Mun''jogopani Allah, ni nsosejesoseje litala lyakum''bikasya Kukwakwe, soni putani ngondo petala Lyakwe kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(5,36,'5:36','Chisimu aŵala ŵakanile ingaŵe ŵakwete yosope yaili pa chilambo, pamo ni ine soni yanti chisawu yele, kuti aliwombolele nayo ku ilagasyo ya lisiku lya Kiyama, nga nganiyikundidwa kukwao, soni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(5,37,'5:37','Chakasachilileje kuti akopoche ku Moto, nambo ŵanganyao ngaja kuŵa ŵakopoka mwalakwemo, soni akwete kupata ŵanganyao ilagasyo yandamo.');
INSERT INTO "silika" VALUES(5,38,'5:38','Soni jwawiyi jwachilume ni jwawiyi jwachikongwe mwakatani makono gao kuŵa malipilo ga yapanganyisye, (ayi ni) ipotesi yakuumila kwa Allah, soni Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(5,39,'5:39','Nambo jwatesile toba panyuma pa lupuso lwakwe, ni kwilanya (itendo yakwe), basi chisimu Allah tapochele toba jakwe, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(5,40,'5:40','Ana ngankumanyilila yanti Allah akwete uchimwene wa kumawunde ni petaka? Akumpaga ilagasyo jwakunsaka ni kunkululuchila jwakunsaka, soni Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(5,41,'5:41','E mmwe Ntenga صلى الله عليه وسلم! Akasan''dandaulikasya aŵala ŵaakutenda chitema (gwila) mu ukafili mu aŵala ŵaakuŵecheta ni pakamwa paope kuti: “Tukulupilile,” kutendaga mitima jao nganijikulupilila, nombe mwa Ayuda mwana ŵaakupikanichisyaga (yankuŵecheta) kuti akalambusyeje, akupikanichisyaga (kuti akasaleje) kwa ŵandu ŵane ŵanganam''bichilila (ŵali achakulungwa ŵao, aŵala) ŵaakusembendula maloŵe (ga mu Taurat) pagatyosya m’mangwakwe. (Aŵala) ŵaakutiji: “Naga n''di mpedwile (ni Muhammadijo) ayi (yatukunsalilayi) kaipochelani, nambo naga nganimpedwa yele nkakalamuche.” Soni jwansachilile Allah kumpa fitina (jakunsokonesya ni kun’jonanga), nganim''ba mumpatiile chilichose chakumpinga Allah, ŵanganyao ni aŵalaŵala ŵanganasaka Allah kuti ajiswejesye mitima jao, akwete kupata ŵanganyao pa duniya kwaluka, ni akwete kupata soni ku Akhera ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(5,42,'5:42','Ŵakunonyelwa kupikanila unami, ŵangatenda ndetemo pakulya ya haraam, sano naga ali am''bichilile, basi jilanyani chilikati chao kapena mwatundumalile, soni naga n''di mwatundumaliile, nganaŵa ampele chisausyo chilichose, nambo naga ntite n''jilanye, basi jilanyani chilikati chao mwagolosya, chisimu Allah akwanonyela ŵagolosya (magambo).');
INSERT INTO "silika" VALUES(5,43,'5:43','Ana soni chantiwuli cham''biiche kuŵa nkwilanya kutendaga akwete Taurat, mwalakwemo mwana ulamusi wa Allah, kaneka ni akugalauka (mwakunyosya) panyuma pa kwele (kwilanya kwenuko), soni ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(5,44,'5:44','Chisimu Uwwe twatulwisye Taurat, mwalakwemo mwaliji mwana chongoko ni lilanguka, Achimitume ŵaŵalipeleche (kwa Allah) ŵaliji nkwilanyichisya najo Ayuda, nombe nao ŵandu ŵaulungu ŵachiyuda kwisa soni achalijiganye ŵao (ŵajilanyichisye soni Tauratijo Ayuda), pakuti ŵanganyao ŵachisyidwe kusunga chitabu cha Allah (Taurat) soni ŵaliji mboni pa chalakwecho, basi ngasimwajogopa ŵandu nambo munjogope Une, soni kasinsagulaga (ya duniya) yantengo wannono pakusumanya ni ma Ȃya Gangu, soni ŵangakwilanyichisya yaatulwisye Allah, basi ŵanganyao niŵali achimakafili.');
INSERT INTO "silika" VALUES(5,45,'5:45','Soni mwalakwemo twalamwile ŵanganyao yanti: “Mundu awulajidwe ligongo lya kuwulaga mundu, liso likolotoledwe ligongo lya kolotola liso, lupula lukatidwe ligongo lyakata lupula, liwiwi likatidwe ligongo lyakata liwiwi, lino likuulidwe ligongo lyakuula lino, nombe maŵanga gana mbusyo (syakwe).” Nambo jwaakululwiche yalakweyo mwasadaka, basi yele (yakululukayo) tiyintendekasye kululuchilidwa sambi syakwe, soni ŵangakwilanyichisya yaatulwisye Allah, basi ŵanganyao niŵali ŵalupuso.');
INSERT INTO "silika" VALUES(5,46,'5:46','Ni twakuuyichisye mu sajo syao (Achimitumewo) ni Isa mwanache jwa Maryam, kwisa kuijitichisya yayaliji paujo pakwe ya mu Taurat, ni twampele soni Injil, mwalakwemo mwaliji mwana chongoko ni lilanguka, soni jaliji nkwitichisya yayaliji paujo pakwe ya mu Taurat, soni jaliji chongolo ni chamuko kwa ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(5,47,'5:47','Soni achinsyene Injil (Akilisito), ajilanyichisyeje yatulwisye Allah mwalakwemo, soni ŵangakwilanyichisya yaatulwisye Allah, basi ŵanganyao niŵali ŵakunyosya.');
INSERT INTO "silika" VALUES(5,48,'5:48','Soni tuntuluchiisye (mmwe Muhammadi صلى الله عليه وسلم) chitabu (Qur’an) mwakuonaonape, chachikwitichisya yaili paujo pakwe ya m’chitabu (cha Baibulo), ni kuchigosa (pakuitendela umboni yaili mwalakwemo kuti: “Ayi yakuona, nambo ayi yaunami,”) basi jilanyani chilikati chao ni yatulwisye Allah, soni ngasinkuya isako yao kuleka yakuona yaim''bichilile, ŵaliose mwa jenumanja twapele chilamusi ni litala lyakulanguka, soni Allah angasache angantesile kuŵa nkutula umpepe, nambo asachile kun''dinga pa yampele, basi pikisanani pa yambone, kwa Allah ni kwakuujila kwenu ŵanaose, basi nitachintagulila ichindu yamwaliji nkulekangana mu yalakweyo.');
INSERT INTO "silika" VALUES(5,49,'5:49','Soni jilanyani chilikati chao ni yatulwisye Allah, ni ngasinkuya isako yao, soni mwakalamukani atakwika mwansokonesye kwakuleka ine yatulwisye Allah kukwenu, nambo naga akugalauka (kuleka chiilanyo chenu), basi manyililani yanti Allah akusaka kwapa ipotesi ya sine mwa sambi syao, soni chisimu ŵandu ŵajinji ali ŵakunyosya.');
INSERT INTO "silika" VALUES(5,50,'5:50','Ana kwilanya kwa chijinga ni kwakukusaka? Ana nduni jwali jwambone nnope pa kwilanya kumpunda Allah? (Ayi iŵele yakumanyika) kwa ŵandu ŵaakukulupilila kusyesyene.');
INSERT INTO "silika" VALUES(5,51,'5:51','E jenumanja ŵankulupilile! Ngasimwatenda Ayuda ni Akilisito kuŵa isyoŵe. Ŵane mwa jamanjao aŵele isyoŵe ya ŵane, soni jwatasyoŵekane nao mwa jenumanja, basi nikuti jwalakwejo ni jumo jwao. Chisimu Allah jwaangaajongola ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(5,52,'5:52','Basi timwaone aŵala ŵamuli m’mitima mwao mwana ulwele (wa unakunaku), achikangamalilaga kukwao achitiji: “Tukogopa kuti litakwika mwalitupatile (nowe) likungu (lyampaka lyapate Asilamuŵa)”. Nambo mwine Allah kuŵa nkwika nako kuwugula (nsinda wa Maaka) kapena chindu chine chakuuma Kukwakwe (mpela kusijuwula nganisyo syasili m’mitima mwa achinakunakuo), basi nikwisa kusyuka kuŵa ŵakudandaula pa yaŵasisile m’mitima mwao.');
INSERT INTO "silika" VALUES(5,53,'5:53','Nikuŵecheta aŵala ŵakulupilile (kuti): “Ana ŵanganyaŵa ngaŵa ni aŵalaŵala ŵana kulumbilaga mwa Allah mu kulumbila kwao kwamachili kuti: chisimu ŵanganyao ali nomwe (Asilamu)?” Itendo yao ijonasiche, basi ni asyuchile kuŵa ŵakwasika.');
INSERT INTO "silika" VALUES(5,54,'5:54','E jenumanja ŵankulupilile! Jwatagalauche mwa jenumanja kuleka dini jakwe (ja Usilamu), basi Allah sampano taiche nao ŵandu ŵatiŵanonyele nombe nao soni tannonyele, ŵakulinandiya kwa ŵakulupilila, ŵamachili pa makafili, ŵataŵeje ali nkuputa ngondo petala lya Allah, soni ngajogopaga mpachepache wa akupacha, walakweu ni umbone wa Allah waakumpaga jwansachile, soni Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(5,55,'5:55','Chisimu nsyoŵe jwenu ni Allah ni Ntenga Jwakwe صلى الله عليه وسلم, kwisa soni aŵala ŵaakulupilile, aŵala ŵakwimikaga Swala mwakolosya ni kutolaga Zaka aku achilinandiyaga pakwinama (kuswali).');
INSERT INTO "silika" VALUES(5,56,'5:56','Sano jwatasyoŵekane ni Allah, Ntenga Jwakwe صلى الله عليه وسلم, kwisa soni ŵandu ŵaakulupilile, basi chisimu nkutula wa Allah ni wauli wakupunda.');
INSERT INTO "silika" VALUES(5,57,'5:57','E jenumanja ŵankulupilile! Ngasimwatenda ŵandu ŵaajitendele dini jenu chipongwe ni chanache mwa ŵandu ŵaŵapedwile chitabu paujo penu, kwisa soni achimakafili kuŵa isyoŵe, soni mun''jogopani Allah naga n''di ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(5,58,'5:58','Soni pati m''bilanjiile ku Swala, akuutendela (nsanao) chipongwe ni chanache, yalakweyo niligongo lyanti ŵanganyao ŵangali lunda.');
INSERT INTO "silika" VALUES(5,59,'5:59','Jilani: “E jenumanja achinsyene chitabu (Ayuda ni Akilisito) ana nkutuona ulemwa uwwe ligongo lyanti tunkulupilile Allah ni yayitulusyidwe kukwetu kwisa soni yayatulusyidwe kala? Sonitu ŵajinji mwa jenumanja ŵakunyosya.”');
INSERT INTO "silika" VALUES(5,60,'5:60','Jilani: “Ana nantagulile ya (nkupata) malipilo gakusakala kwa Allah kulekangana niyele (yankutuganichisyayo)? Jwalakwetu ni ajula jwannwesile Allah ni kuntumbilila ni kwasyusya ŵane mwa ŵanganyao kuŵa majani ni maguluŵe, ni kun''galagatila shetani, ŵanganyao ni achinsyene malo gakusakala nnope (ku Moto), soni ni ŵakusokonechela nnope kuleka litala lyagoloka.”');
INSERT INTO "silika" VALUES(5,61,'5:61','Soni pati anyyichilile ŵanganyammwe akasatiji: “Tukulupilile.” Kutendaga ali ajinjile (kukwenu) ni ukafili, soni ni kopoka nao (ukafilio). Nambo Allah ni jwaakuimanya chenene yaŵele ŵanganyao ali nkusisa.');
INSERT INTO "silika" VALUES(5,62,'5:62','Timwaone ŵajinji mwa jamanjao achikangamalilaga ku ilemwa ni umagongo ni kulya kwao ya haraam, kaje kusakala nnope yaaŵele ŵanganyao ali nkutenda!');
INSERT INTO "silika" VALUES(5,63,'5:63','Ligongo chichi ŵandu ŵaulungu ŵachiyuda kwisa soni achalijiganye ŵao ngaakwakanya yakuŵecheta yao yaulemwa ni kulya kwao ya haraam? Kaje kusakala nnope yaaŵele ŵanganyao ali nkupanganya!');
INSERT INTO "silika" VALUES(5,64,'5:64','Soni Ayuda akasatiji: “Ligasa lya Allah lyeumbate (ngatukupata chipanje mpela kala).” (Ngwamba,) magasa gao nigagali gamaumbate (ligongo lya kulyalyaganya kwao), soni ni alwesedwe ligongo lya yaakuŵecheta. Nambo magasa Gakwe (Allah) ganagaŵili gachongolele akutolaga mwaakusachila, soni yaitulusyidwe kukwenu kuumila kwa Ambuje ŵenu chisimu chene chiyajonjechesye ŵajinji mwa jamanjao kusumba mpika ni ukafili, ni tuponyisye chilikati chao utinda ni uŵengani mpaka lisiku lya Kiyama, ndaŵi ni katema kaakukolesyaga moto wangondo, Allah akasausimisyaga, ni akasajendaga ŵanganyao pa chilambo kupita nchiwatanganya, soni Allah jwanganonyela akuwatanganya.');
INSERT INTO "silika" VALUES(5,65,'5:65','Soni ingaŵe kuti achinsyene chitabu (Ayuda ni Akilisito) ŵaakulupilile (mwa Muhammadi صلى الله عليه وسلم) ni kuŵa niwoga (wakun''jogopa Allah), chisimu tukaasiimaachiisye yakusakala yao, soni tukajile kwajinjisya ku Matimbe Gachindimba.');
INSERT INTO "silika" VALUES(5,66,'5:66','Soni ingaŵe ŵanganyao ŵajikamulichisye masengo Taurat ni Injil, kwisa soni yaitulusyidwe kukwao kuumila kwa M’mbuje gwao (jajili Qur’an), chisimu akapatile yakulya kutyochela pachanya pao ni pasi pangongolo syao, mwa jamanjao mwana likuga lyalili petala lyagoloka (paakuya ya mu itabu ni kunkulupilala Muhammadi صلى الله عليه وسلم, mpela Abdullah bin Salâm ), nambo ŵajinji mwa jamanjao iŵele yakusakala nnope yaakutenda.');
INSERT INTO "silika" VALUES(5,67,'5:67','E mmwe Ntenga صلى الله عليه وسلم! Lungusyani yaitulusyidwe kukwenu kuumila kwa Ambuje ŵenu (Allah). Nambo naga ngankutenda, nikuti nganinnungusya utenga Wakwe. Soni Allah tam’bambasye ku ŵandu, chisimu Allah ngaakwaajongolaga ŵandu makafili.');
INSERT INTO "silika" VALUES(5,68,'5:68','Jilani: “E jenumanja achinsyene chitabu (Ayuda ni Akilisito)! Nganim''ba pa chilichose mpaka nkakamulichisye masengo Taurat ni Injil, kwisa soni yaitulusyidwe kukwenu kuumila kwa M''mbuje gwenu (jajili Qur’an).” Sano yaitulusyidwe kukwenu kuumila kwa Ambuje ŵenu, chisimu chene chiyajonjechesye ŵajinji mwa jamanjao kusumba mpika ni ukafili, basi ngasimwadandaulila ŵandu makafili.');
INSERT INTO "silika" VALUES(5,69,'5:69','Chisimu aŵala ŵakulupilile (mitume jakala) ni ŵali mu Chiyuda, Asabayi ni Akilisito; ŵaliose mwajamanjao ŵatakulupilile mwa Allah ni Lisiku Lyambesi (pakunda Usilamu), ni kutendaga yambone (mu Usilamu mwaomo), basi ngaaja kuŵa ni woga, soni ngaaja kuŵa ŵakudandaula. ');
INSERT INTO "silika" VALUES(5,70,'5:70','Yakuonaonape, twajigele chilanga cha ŵanache ŵa Israila ni twatumisye kukwao achimitenga, ndaŵi ni katema kajwaichililaga ntenga ni yangaikunonyelwa mitima jao likuga line (lya achimitenga) ŵaalikaaniile, likuga line soni akuliulaga.');
INSERT INTO "silika" VALUES(5,71,'5:71','Ni ŵaganichisyaga yanti ngapapatikana ilagasyo; basi ni ŵaliji ŵangalola soni ŵangapikana, kaneka (ali atesile toba) Allah ŵaapochele toba jao, yeti soni ŵajinji mwa jamanjao, ŵapitilisye ungalola ni ungapikana, sano Allah ni Nkuilolechesya yaakutenda.');
INSERT INTO "silika" VALUES(5,72,'5:72','Pamasile paakufuulu aŵala ŵaakutiji: “Chisimu Allah ni Masihi mwanache jwa Maryam.” Kutendaga Masihi jwatite: “E jenumanja ŵanache ŵa Israila! Mun''galagatilani Allah M''mbuje gwangu soni M''mbuje gwenu.” Chisimu jwaakum''bwanganya Allah (ni indu ine), nikuti Allah pamasile pankanyisye jwalakwejo kwinjila ku Mbepo, mwanti liuto lyakwe ni ku Moto, soni ŵandu ŵalupuso ngasakola ŵakwakulupusya.');
INSERT INTO "silika" VALUES(5,73,'5:73','Pamasile paakufuulu aŵala ŵaakutiji: “Chisimu Allah ni jumpepe mwa (milungu) jitatu,” kutendaga pangali nnungu jwine ikaŵeje Nnungu Jumo. Nambo naga ngaakuleka yaakuŵechetayo, chisimu ŵandu ŵakanile mwa ŵanganyao chiyapate ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(5,74,'5:74','Ana ngakutenda toba kwa Allah ni kum''bendaga chikululuko? Nambotu Allah ni Jwakululuka kusyene, Jwachanasa channope. ');
INSERT INTO "silika" VALUES(5,75,'5:75','Masihi (Isa) mwanache jwa Maryam nganaŵa jwinepe ikaŵeje Ntenga basi, pamasile papite paujo pakwe achimitenga (ŵane), nombenao mamagwe ŵaliji ŵakuonaonape, ŵanaŵaŵilio ŵaliji nkulya yakulya (mwaakulilaga jwalijose, ana Nnungu kuŵa nkulya?) Nnole yatukuti pakusalichisya kukwao ma Ȃya, soni nnole yaakuti pakugalausyidwa (kuleka yakuona).');
INSERT INTO "silika" VALUES(5,76,'5:76','Jilani: “Ana nkuchigalagatila changanichikola (machili) gakumpela ilagasyo namuno chikamuchisyo kunneka Allah? Nambotu Allah nijwali Jwakupikanichisya, Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(5,77,'5:77','Jilani: “E jenumanja achinsyene chitabu (Ayuda ni Akilisito)! Ngasinsumbaga mpika pa dini jenu (paakulupilila inepe) yangaŵaga yakuona, soni ngasinkuya isako yaŵandu ŵaŵamasile paŵasokonechele kala, ni kwaasokonesya ŵajinji, niŵasokonechele kuleka litala lyagoloka.”');
INSERT INTO "silika" VALUES(5,78,'5:78','Ŵalwesedwe aŵala ŵaŵakanile mu ŵanache ŵa Israila ni lulimi lwa Daudi ni Isa mwanache jwa Maryam, yalakweyo ligongo lya kunyosya kwao, soni ŵaliji ali nkusumba mpika.');
INSERT INTO "silika" VALUES(5,79,'5:79','Nganaŵaga nkukanyana pa yakusakala yaaitesile, kaje kusakala nnope yaŵaliji nkutenda!');
INSERT INTO "silika" VALUES(5,80,'5:80','Timwaone ŵajinji mwa jamanjao (Ayuda) ali nkusyoŵekana nao aŵala ŵakanile (makafili gacharabu, kuti awupute ngondo Usilamu), kaje kusakala nnope yaajilongochesye mitima jao! Mwanti Allah ŵakasilichile, soni mu ilagasyo tachiŵa ŵandamo.');
INSERT INTO "silika" VALUES(5,81,'5:81','Mwanti angaŵe ali nkunkulupilila Allah ni Ntume (Muhammadi صلى الله عليه وسلم) kwisa soni yaitulusyidwe kukwakwe, nga nganiŵatenda kuŵa asyoŵe, nambo kuti ŵajinji mwa jamanjao ŵakunyosya.');
INSERT INTO "silika" VALUES(5,82,'5:82','Chisimu chene timwasimane ŵandu ŵali ŵakupundanganya nnope utinda kwa ŵandu ŵaakulupilile; ni Ayuda kwisa soni ŵakuwanganya (Allah ni isanamu), soni chisimu chene timwasimane ŵali ŵakuŵandichila ŵao nnope ku chinonyelo chakwanonyela ŵandu ŵakulupilila; ni aŵala ŵakutiji: “Chisimu uwwe ni Akilisito.” Yalakweyo ligongo lyanti mwa ŵanganyao mwana achalijiganye ya dini kwisa soni ŵakun’jogopa Nnungu, soni nikuti ŵanganyao ŵangalikwesya.');
INSERT INTO "silika" VALUES(5,83,'5:83','Soni pati apikene (Akilisitowo) yaitulusyidwe kwa Ntenga (Muhammadi صلى الله عليه وسلم ), tin''gaone meso gao gali nkulakatisya misosi ligongo lya yakuona yaimanyi, achitiji: “Ambuje ŵetu! Tukulupilile. Basi tulembani pamo ni akuŵichila umboni (yakuonayi).”');
INSERT INTO "silika" VALUES(5,84,'5:84','Ni (ŵatite pakwajanga akwagamba ligongo lyakunda Usilamu): “Chantiuli tukankulupilila Allah ni yakuona yaituichilile, kutendaga tunankun’jembecheya M’mbuje gwetu kuti akatujinjisye (ku Mbepo) pamo ni ŵandu ŵambone?”');
INSERT INTO "silika" VALUES(5,85,'5:85','Basi ligongo lya yaŵaŵechete, Allah ŵalipile Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, soni galakwego nimalipilo ga ŵakolosya.');
INSERT INTO "silika" VALUES(5,86,'5:86','Nambo ŵandu ŵaakufuulu ni kanila ma Ȃya Getu, ŵanganyao niŵandu ŵa ku Moto Waukali.');
INSERT INTO "silika" VALUES(5,87,'5:87','E jenumanja ŵankulupilile! Ngasinyyitenda kuŵa haraam ichindu yambone yayitesile Allah kuŵa halaal kukwenu, soni ngasinsumba mpika, chisimu Allah jwangaanonyela ŵakusumba mpika.');
INSERT INTO "silika" VALUES(5,88,'5:88','Soni lyagani mu ichindu yampele Allah, yaili ya halaal nambo soni yambone, ni mun''jogopeje Allah Ajula jwankuŵa ŵanganyammwe n''di nkunkulupilila.');
INSERT INTO "silika" VALUES(5,89,'5:89','Allah ngasampa ilagasyo pakulumbila kwenu kwangali mate, nambo tachimpa ilagasyo pakulumbila kunnumbile mwakusimichisya, basi malipilo gakwe (naga m''bwesesye yannumbileyo), nikugalisya masikini likumi ni yakulya yachipiimo chakatikati chankuliichisya maŵasa genu, kapena kwawecha, kapena gopola kapolo, nambo jwanganapata (yalakweyi), basi chataŵe moŵa gatatu. Galakwego ni malipilo ga kulumbila kwenu pati nnumbile (ni kuwesesya yannumbileyo), soni sungani kulumbila kwenu (nkawesesyaga-wesesyaga yannumbile kuti chintende), iyyoyopeyo niyaakuti Allah pakusalichisya kukwenu ma Ȃya Gakwe kuti m''be ŵakutogolela.');
INSERT INTO "silika" VALUES(5,90,'5:90','E jenumanja ŵankulupilile! Chisimu ukana, juga, isanamu kwisa soni isango (yosopeyi) iŵele usakwa, (ili) mmasengo ga Shetani, basi liŵambasyani kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(5,91,'5:91','Chisimu Shetani akusaka kutandisya chilikati chenu utinda ni uŵengani kupitila mu ukana ni juga, ni kuti ansiŵilile kunkumbuchila Allah ni kuswali, ana jenumanja n''di ŵakuleka?');
INSERT INTO "silika" VALUES(5,92,'5:92','Soni mumpikanilani Allah ni mumpikanile Ntenga صلى الله عليه وسلم, ni kalamukani (panganyosya malamusi), nambo naga nkugalauka (kuleka yele), basi manyililani yanti masengo ga Ntenga Jwetu ni kulungusya (utenga) mwakupikanika.');
INSERT INTO "silika" VALUES(5,93,'5:93','Pangali ulemwa kwa aŵala ŵaakulupilile ni kutendaga yambone pa yaŵalile (kala nkaniyikanyisyidwe) naga ali ni woga (wa kuiŵambala yakanyisyidwayo) ni kulupilila (yalamulidwe) ni kutendaga yambone, soni nikwendelechela kuŵa ni woga ni kulupilila kaneko ni kuŵa ni woga ni kolosya, soni Allah akwanonyela ŵakolosya.');
INSERT INTO "silika" VALUES(5,94,'5:94','E jenumanja ŵankulupilile! Allah tan''dinje mayeso ni sine mwa nyama sya m’maani sichigaichile makono genu ni mapanga genu (n''di nnuyiye ibada ya Hija kapena Umra) kuti Allah ammanyukule mundu jwaakun''jogopaga Jwalakwe mwangali kum''bona, sano juchasumbe mpika panyuma pa yele (yasalilidweyo ni kulupata), basi akwete kupata ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(5,95,'5:95','E jenumanja ŵankulupilile! Ngasim''bulaga nyama ja m’maani kutendaga n''di nnuyiye (ibada ya Hija kapena Umra), soni jwataiche mwakujiulaga mwa jenumanja melepe, basi malipilo (gakwe chigaŵe pakusikita) chakulandana ni chaulejecho mu nyama syakulanga, yampaka ajile pakulamula ŵakulungamika ŵaŵili mwa jenumanja, kuŵa mbepesi jakupelechedwa kuLikaaba (pakwapeleganya nyamajo ŵakulaga ŵakweleko), kapena malipilo ga yakulya yakwaaliisya masikini, kapena kutaŵa mwakulandana ni yakulyayo, kuti apasye makungu ga itendo yakwe, Allah ayisiimaasiisye yayapite kala, nambo jwataujauje soni, basi Allah tam''buchisye mbusyo syakupoteka syakuuma Kukwakwe, soni Allah ni Jwamachili gakupunda, Nkuusya mbusyo syakupoteka kusyene.');
INSERT INTO "silika" VALUES(5,96,'5:96','Gaŵele gaakundisyidwa kukwenu maani ga m''mbwani ni yakulya yakwe (yaisimanikwe m''mbwanimo ili iwile isyenepe ni ili ya halali) jiŵele posyo jenu kwisa soni ŵapaulendo, ni gakanyisyidwe kukwenu maani ga m’mikuli muchinnonjelele n''di muniya (ja Hija kapena Umra). Soni mun''jogopani Allah Ajula jwa chinchisonganganyisyidwa Kukwakwe.');
INSERT INTO "silika" VALUES(5,97,'5:97','Allah alitesile Likaaba - Nyumba Jakuchimbichika - kuŵa lyakwajimila ŵandu (kuti apatende ibada ya Hija ni kupata yakulajila yao ya duniya mwantunjelele), nimwesi wakuchimbichika (awutesile nombe kuŵa wakwajimila ŵandu pangaputigwa ngondo mwalakwemo), kwisa soni inyama yambepesi (ja ku Maaka), ni makosa (gaikuwechedwaga, ayitesile yosopeyo kuŵa yakwajimila ŵandu ŵaijigele kuti akatendedwa uwwanga), yalakweyi (ayitesile) kuti mmanyilile yanti Allah akwimanya yaili kumawunde ni yaili petaka, ni kuti soni Allah chindu chilichose aŵele Jwachimanya chenene.');
INSERT INTO "silika" VALUES(5,98,'5:98','Manyililani yanti Allah ni Jwaukali pakupeleka ipotesi, ni kuti soni Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(5,99,'5:99','Pangali masengo gane kwa Ntenga صلى الله عليه وسلم ikaŵeje kulungusya utenga, soni Allah akwimanya yankulosya palangulangu ni yankusisa.');
INSERT INTO "silika" VALUES(5,100,'5:100','Jilani: “Yakusakala ni yambone ngaŵa nkulandana, namuno kuli kunsangalesye kutupa kwa yakusakalayo (nkapendechela ku yalakweyo).” Basi mun''jogopani Allah, E jenumanja ŵankwete lunda! Kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(5,101,'5:101','E jenumanja ŵankulupilile! Ngasim''buusyaga indu yanti ili ilosyedwe kukwenu mpaka insakalile, soni naga nyyiwusyeje ndema jakutulusyidwa Qur’an chiyilosyedwe kukwenu. Allah ayisiimaasiisye yeleyo (yakuwuusyawuusya yenuyo ni ngasim''bujauja soni), soni Allah ni Jwakululuka kusyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(5,102,'5:102','Chisimu ŵawuusiisye yalakweyo ŵandu ŵaŵaliji paujo penu, kaneko ligongo lya yalakweyo ŵasyuchile kuŵa ŵakanila.');
INSERT INTO "silika" VALUES(5,103,'5:103','Allah nganaŵika (mpingu uliose) pa nyama jakuŵilanjikwa Bahiira , kapena Saaiba . kapena Waswiila , kapena Haamu , (aji ni mitundu ja ngamiya syaŵaliji ŵandu nkuchimbichisya nasyo isanamu kala Usilamu nkaniuyiche), nambo ŵandu ŵaakanile akunsepela Allah unami, soni ŵajinji mwa jamanjao ŵangali umanyilisi.');
INSERT INTO "silika" VALUES(5,104,'5:104','Soni naga ili iŵechetedwe kukwao (yanti): “Ikaani ku yaatulwisye Allah ni (m''biche soni) kwa Ntenga صلى الله عليه وسلم.” Akutiji: “Ikutukwanila yatwasimene nayo achatati ŵetu.” Ana nantimuno kuti achatati ŵawowo nganamanyililaga chilichose soni nganaŵa ŵakongoka (chiŵakuyejepe?)');
INSERT INTO "silika" VALUES(5,105,'5:105','E jenumanja ŵankulupilile! Likolosyani mitima jenu. Ŵandu ŵasokonechele nganaŵa ampele ilagasyo naga n''di n''jongweche, kwa Allah ni kwakuwujila kwenu ŵanaose, basi ni tachintagulila ichindu yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(5,106,'5:106','E jenumanja ŵankulupilile! Ŵikani mboni chilikati chenu ndema jakum’bichilila jumo jwenu chiwa ni akusaka kuleka wasiya, (siŵe mboni) siŵili syakulungamika mwa jenumanja (Asilamu syakusilechela wasiyao), kapena sinepe siŵili syanganisiŵa mwa jenumanja (Asilamu) naga n''jesilejesile pa chilambo ni chimpatile chipato cha chiwa (Asilamu achin''jenu ali kwanaula). Naga n''di nkaichile (yasikuŵecheta mboni siŵilisyo), sijimikani panyuma pa swala, basi ni silumbile kwa Allah (pakuŵecheta kuti): “Ngatukusumanya kulumbilaku ni chindu chilichose (cha duniya kuti tulumbile yaunami tupate chakupata), naatamuno chili chakunkamuchisya jwapaulongo (petu), soni ngatukusisa umboni wa Allah (waatulamwile kuti tupeleche), uwwetu (kwisa mwakusisa umboni), nikuti pajele ndemajo chituŵe mu ŵandu ŵakulemwa.”');
INSERT INTO "silika" VALUES(5,107,'5:107','Nambo naga ili iwonechele yanti (mboni) siŵilisila siŵajilwe kupata sambi (pakupeleka umboni waunami kapena kusisa kachindu ka mu wasiyao), basi mmalo mwakwe chajime ŵane ŵaŵili mwa ŵandu ŵachikwaŵandika (chipanjecho ŵali achalongo achin’jakwe nkuwa), ŵaukwagunda nnope (wasiyao), basi ni chalumbile kwa Allah (pakusikanila mboni siŵili sila kuti): “Chisimu umboni wetu uŵele wakuona nnope kulekangana ni umboni wao, soni nganitusumba mpika (pakwasepela unami ŵanaŵaŵiliŵa), uwwetu (kwisa mwakusumba mpika), nikuti pajele ndemajo chituŵe mu ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(5,108,'5:108','Chele (chilamusichi) chiŵele chakuŵandichila (kusitendekasya mboni) kuti siyiche nao umboni mwaukusosekwela, kapena kuti sijogope yanti kutakwika mwakupelechedwe kulumbila (kwine) panyuma pa kulumbila kwao (ni kwaluka), soni mun''jogopani Allah ni kupikanila (malamusi), soni Allah jwangaajongola ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(5,109,'5:109','Pa lisiku lyatachisonganganya Allah achimitenga nikwausya kuti: “Ana mwajanjidwe ichichi?” Ŵanganyao tachiti: “Uwe ŵangali umanyilisi, chisimu Mmwejo ni Ŵakumanyilila nnope yakusisika.”');
INSERT INTO "silika" VALUES(5,110,'5:110','(Kumbuchilani) katema katachiŵecheta Allah (lisiku lya Kiyama kunsalila Isa) kuti: “E mmwe Isa mwanache jwa Maryam! Kumbuchilani chindimba Changu chachaliji kukwenu ni kwa mama ŵenu, ndema janan''dimbangenye ni Nsimu Weswela (Jibulilu), mwanti mwaŵechete ni ŵandu n''di nnuuli kwisa soni n''di nkusile, ni ndema janan''jiganyisye kulemba, lunda lwakusokoka, kwisa soni Taurat ni Injil, ni ndema jamwaliji nkugumba kutyochela mwilongo chitusitusi cha chijuni mu lisosa Lyangu, ni kuchipepelela basi ni kusyuka chijuni chisyesyene mu lisosa Lyangu, ni mwaliji nkwaaposya ŵangalola kwachipago kwisa soni ŵachinaŵa mu lisosa Lyangu, ni ndema jamwaliji nkwakoposya ŵawe (m’malembe nikuŵa ŵawumi) mu lisosa Lyangu, ni ndema janaasiŵilile ŵanache ŵa Israila kukwenu (katema kaŵakamulene yakum''bulaga) pandema jamwaichilile ni ilosyo yakuonechela, basi aŵala ŵaŵakanile mwa jamanjao ŵatite: ‘Ayitu nganiyiŵa inepe ikaŵeje usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(5,111,'5:111','(Kumbuchilani) soni katema kanajuwulile ŵakulijiganya (ŵa Isa) yanti: “Mungulupililani Une ni Ntenga Jwangu (Isa).” Ŵanganyao ŵatite: “Tukulupilile, soni ŵichilani umboni kuti chisimu uwwe tuli Asilamu.”');
INSERT INTO "silika" VALUES(5,112,'5:112','(Kumbuchilani) katema kaŵaŵechete ŵakulijiganya (ŵa Isa kuti): “E mmwe Isa mwanache jwa Maryam! Ana Ambuje ŵenu mpaka apakombole kututuluchisya tebulo jejale yakulya kuumila kwinani?” (Isa) jwatite: “Mun''jogopani Allah naga n''di ŵakulupilila (kusyene).”');
INSERT INTO "silika" VALUES(5,113,'5:113','Ŵanganyao ŵatite: “Tukusaka kuti tulye yalakweyo ni jitulalile mitima jetu, soni tumanyilile yanti chisimu ntusaaliile yakuona, soni kuti tuŵe mu ŵakutendela umboni pa yalakweyo.”');
INSERT INTO "silika" VALUES(5,114,'5:114','Isa mwanache jwa Maryam jwatite: “E Allah Ambuje ŵetu! Tutuluchisyani tebulo jejale (yakulya) kuumila kwinani kuti jiŵe yidi (pwando) kukwetu ja ŵandanda ŵetu ni ŵambesi ŵetu, soni (kuti chiŵe) chisimosimo chakutyochela Kukwenu, ni tupani soni lisiki, soni Mmwejo ni Ŵambone nnope mwa akupeleka lisiki.”');
INSERT INTO "silika" VALUES(5,115,'5:115','Allah jwatite: “Chisimu Une tinajitulusye kukwenu, nambo jwachakane panyuma pakwe mwa jenumanja, basitu Une chinallagasye ni ilagasyo ya nganinallagasyeje nayo jwalijose mu iwumbe yosope.”');
INSERT INTO "silika" VALUES(5,116,'5:116','Soni (kumbuchilani) katema katachiŵecheta Allah (lisiku lya Kiyama pa kum’buusya Isa) kuti: “E mmwe Isa mwanache jwa Maryam! Ana mmwe mwasalile ŵandu yanti: ‘Mundendani une ni mama ŵangu kuŵa milungu jiŵili mmalo mwa Allah?” Jwalakwe tachiti: “Kuswejela ni Kwenu! Nganiyiŵa yakundisidwa kukwangu kuti mbechete yanganiyiŵajilwa kukwangu, ingaŵe naiche mwakuŵecheta yalakweyo, basi chisimu nkayimanyi. Nkumanyilila yaili mu ntima mwangu, nambo une ngangumanyilila yaili mu ntima Mwenu, chisimu Mmwejo ni Ŵakumanyilila nnope yakusisika.”');
INSERT INTO "silika" VALUES(5,117,'5:117','“Nganimbecheta kukwao ikaŵeje yamwanamwile yanti: ‘Mun''galagatileje Allah, M’mbuje gwangu soni M''mbuje gwenu.’ Soni naliji mboni pa ŵanganyao pa ndema janalonjele chilikati chao. Nambo katema kamwanjigele, Mmwejo ni ŵamwaliji ŵakwalolela ŵanganyao, soni Mmwe pa chindu chilichose m''bele Mboni.”');
INSERT INTO "silika" VALUES(5,118,'5:118','“Naga nkwapa ilagasyo, basi chisimu ŵanganyao achikapolo Ŵenu, soni naga nkwakululuchila, basi nikuti chisimu Mmwejo ni Ŵamachili gakupunda, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(5,119,'5:119','Allah tachiti: “Ali ni lisiku lyakwakamuchisya ŵakuŵecheta yakuona ukuona wao, akwete kupata ŵanganyao Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chaŵe ŵandamo mwalakwemo kwa yaka ni yaka. Allah tiŵanonyele (pakwapochela masengo gao gambone), nombe nao chanonyelwe Najo (pa malipilo gachijwaape). Kwalakweko ni kupunda kwekulungwa nnope.”');
INSERT INTO "silika" VALUES(5,120,'5:120','Allah ni Nsyene uchimwene wa kumawunde ni petaka ni yaili mwalakwemo, soni Jwalakwe pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(6,1,'6:1','Lumbili losope lwene alapikwe Allah, Ajula jwagumbile mawunde ni mataka ni aŵisile chipi ni lilanguka, yeti kaneka aŵala ŵakanile, ni akuŵika chakulandanyichisya (ni Jwalakwe pakuchigalagatila)!');
INSERT INTO "silika" VALUES(6,2,'6:2','Jwalakwe ni Ajula jwaŵan''gumbile ŵanganyammwe kuumila mwilongo, kaneka ni jwaŵisile ndaŵi (jakuwila) ni ndaŵi jekolanjidwe Kukwakwe (jakwimuchila m’malembe), yeti jenumanja ni nkukaichilape (yakwimukayo)!');
INSERT INTO "silika" VALUES(6,3,'6:3','Soni Jwalakwe ni Allah (Jwakuŵajilwa galagatilwa) kumawunde ni petaka, jukumanyilila asili syenu ni yakunyanyika yenu, ni jukumanyilila soni yankupanganya.');
INSERT INTO "silika" VALUES(6,4,'6:4','Soni changaaichilila ŵanganyao chilosyo chilichose mu ilosyo ya M’mbuje gwao, ikaŵeje akasaŵaga ali nkuchitundumalila.');
INSERT INTO "silika" VALUES(6,5,'6:5','Basi pamasile paayikaaniile yakuona ndema ja yaichilile, nambo chisyaichilile sampano abali sya ayila (ipotesi) yaaŵele ali nkuitendela chipongwe.');
INSERT INTO "silika" VALUES(6,6,'6:6','Ana ngakuiona yanti jilingwa mwa mikutula jatwajijonasile paujo pao, twalimbangenye ŵanganyao pa chilambo yanganitujila pakun''dimbanganya ŵanganyammwe, ni twatuluchisye wula jejinji, ni twasitesile sulo kuŵa syakujilima cha pasi pao, nambo twajonasilepe ligongo lya sambi syao, ni twagumbile panyuma pao mikutula jine.');
INSERT INTO "silika" VALUES(6,7,'6:7','Soni namuno ingaŵe kuti twantuluchisye (mmwe Muhammadi صلى الله عليه وسلم) chitabu (chelembedwe) nchikalakala nikuchikwaya ŵanganyao ni makono gao, chisimu aŵala ŵakanile angatite: “Ayitu nganiiŵa inepe ikaŵeje usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(6,8,'6:8','Soni ŵanganyao akasatiji: “Uli ngantuluchisya jwalakweju lilaika (kuti lintendeleje umboni ya utume wakwe)?” Sano ingaŵe twatulwisye lilaika (ŵanyao ni kanilape), chisimu chilamusi (chakonasika kwao) chingapitisyidwe (mwanakamo), soni nga nganapedwa lipesa (lyakutendela toba).');
INSERT INTO "silika" VALUES(6,9,'6:9','Soni tungantende kuŵa lilaika (jwalakwejo) chisimu tungalitesile kuŵa mpela mundu (kuti akombolecheje kuliona ni kuŵecheta nalyo), soni (tungalitende kuŵa mpela mundu) chisimu tungapupuligenye ŵanganyao (mwanti angapelaga mundu n''jao ni nga nganalikundape) yaakuti pakulipupuliganya (achimisyene pangakunda utume wa Muhammadi صلى الله عليه وسلم ni kuŵecheta kwao kwanti: “Aju ngati mundu mpela uwwewe ni tunkunde?”)');
INSERT INTO "silika" VALUES(6,10,'6:10','Soni chisimu chene achimitenga (ŵajinji) ŵatendedwe chipongwe paujo penu (mmwe Muhammadi صلى الله عليه وسلم nkanim’biche), nambo (mbesi jakwe) aŵala ŵaŵaliji nkutenda chipongwe mwa jamanjao (achimakafiliwo), yasyungwile (ipato) yaŵaliji nkuitendela chipongweyo.');
INSERT INTO "silika" VALUES(6,11,'6:11','Jilani: “Jendagani pa chilambo ni mpiteje nchilola mujaŵelele mbesi ja akukanila.”');
INSERT INTO "silika" VALUES(6,12,'6:12','Jilani (mwakwawusya): “Ana yacheni yaili kumawunde ni petaka?” Jilani (naga ngakwanga): “Ya Allah.” Alichisisye Nsyene (kutenda) chanasa. Tachinsonganganya kusyene pa lisiku lya Kiyama lyangali kaichila ya lyalakwelyo. Aŵala ŵaalipelelwekasisye achimisyene, basi nganaŵa akulupilile (ya Kiyamayo).');
INSERT INTO "silika" VALUES(6,13,'6:13','Soni iŵele Yakwe Jwalakwejo (Allah) yaikusimanikwa nchilo ni muusi, soni Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(6,14,'6:14','Jilani: “Ana nantende kuŵa kaligoselo jwangaŵaga Allah Nkugumba mawunde ni mataka? Soni Jwalakwe ni jwaakuliisya nambo jwangaliisyidwa.” Jilani: “Chisimu une namulidwe yakuti mbe jwandanda mwa ŵaalipeleeche (pakukunda Usilamu).” Soni (salilidwe yanti): “Nkasim''ba ata panandi mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(6,15,'6:15','Jilani: “Chisimu une ngogopa ilagasyo ya Lisiku Lyekulungwa naga naanyosye Ambuje ŵangu (Allah).”');
INSERT INTO "silika" VALUES(6,16,'6:16','Jwatakantyochesye (ilagasyo) lyele lisikulyo, basi (chakaŵe kuti) pamasile pantendele chanasa, soni kwalakweko ni kupunda kwakuonechela.');
INSERT INTO "silika" VALUES(6,17,'6:17','Soni naga Allah ali ankwayikasisye masausyo, basi pangali ŵagatyosya ikaŵeje Jwalakwe, soni naga ali ankwayikasisye yambone, basi Jwalakwe pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(6,18,'6:18','Soni Jwalakwe ni Jwakupunda ni kutawala pa iwumbe Yakwe, soni Jwalakwe ni Jwalunda lwakusokoka, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(6,19,'6:19','Jilani (pakwawusya): “Ana chinduchi chachili chekulungwa nnope umboni wakwe?” Jilani (naga ngakwanga): “Allah. (Jwalakwe ni) Mboni chilikati changu ni jenumanja. Soni aji Qur’an jijuwulidwe kukwangu kuti nantetele najo ni jwalijose jwajim’biiche. Ana jenumanja nkuŵichila umboni wanti pana milungu jine kupwatika pa Allah?” Jilani: “Une ni nganguŵichila umboni.” Jilani: “Chisimu Jwalakwe ni Nnungu Jumpepe, soni chisimu une ndalikangene ni yankum''bwanganya nayo.”');
INSERT INTO "silika" VALUES(6,20,'6:20','Aŵala ŵatwapele chitabu (Ayuda ni Akilisito), akummanyilila (Muhammadi صلى الله عليه وسلم chenene) yaakuti pakwamanyilila ŵanache ŵao. Aŵala ŵalipelelwekasisye achimisyene, basi nganaŵa akulupilile.');
INSERT INTO "silika" VALUES(6,21,'6:21','Ana nduni jwali jwalupuso nnope kumpunda mundu jwansepele Allah unami kapena kanila ma Ȃya Gakwe? Chisimu ŵalupuso ngaŵa nkupunda.');
INSERT INTO "silika" VALUES(6,22,'6:22','Ni (mwakumbusyani) lisiku lituchasonganganya ŵanaose, kaneka ni tuchiti kwa aŵala ŵaŵaliji nkuwanganya (Allah ni isanamu): “Ali kwapi akwawanganya ŵenuŵala ŵamwaliji nkulitwanjila (kuti ŵakamusyangana najo Allah)?”');
INSERT INTO "silika" VALUES(6,23,'6:23','Ni ngasikuŵa kuliŵalanga kwao ikaŵeje tachiti: “Tukunnumbila Allah M''mbuje gwetu! Nganituŵatu uwwe akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(6,24,'6:24','Nnole! Yatachiti pakulilambusya achimisyene! Soni (isanamu) yaŵaliji nkuisepelela (kuti chiyichajokola kwa Allah) ichasokonechela.');
INSERT INTO "silika" VALUES(6,25,'6:25','Soni mwa ŵanganyao mwana ŵaakumpikanilaga (nchisoomaga Qur’an), nambo tuŵisile isiŵilo pachanya pa mitima jao kuti akaimanyilila (yankusoomayo ligongo lya sambi syao), ni m’mawiwi mwao mwana usiiwa, soni namuno achione chilosyo chilichose nganaŵa achikulupilile, kwikanila ndema janti ali am''bichilile kukangana namwe, aŵala ŵakanile akasatiji: “Nganijiŵatu jalakwe (Qur’aniji) ikaŵeje adisiadisi sya ŵandu ŵakala.”');
INSERT INTO "silika" VALUES(6,26,'6:26','Soni ŵanganyao akwakanya (ŵane) yalakweyi (yakunkuya Muhammadi صلى الله عليه وسلم ni Qur’an), nombe nao (achimisyene) akuliŵambasya ku yalakweyo, ni ngakwajonanga (ŵaliose) ikaŵeje achimisyene, ni ngakulimanyilila.');
INSERT INTO "silika" VALUES(6,27,'6:27','Soni nkaione pa ndema jatachajimika ku Moto! (Nkakuweni kuyumbayumba kwao). Basi tachiti: “Ha! Nabolaga uwwe akatuwuchisye (ku duniya), nga nganituja kanila soni ilosyo ya M’mbuje gwetu, soni tungajile kuŵa mu ŵakulupilila.”');
INSERT INTO "silika" VALUES(6,28,'6:28','(Ngwamba)! Nambo kuti iwonechele palangulangu kukwao yaŵaliji nkusisa kala. Soni ingaŵe ŵawuchisye (ku duniya), chisimu angawujawije kutenda yaŵakanyisyidwe, soni chisimu ŵanganyao ŵaunami kusyene.');
INSERT INTO "silika" VALUES(6,29,'6:29','Soni akutiji: “Pangali (umi) wine ikaŵeje umi wetu wa duniya pewu, soni nganituŵatu uwwe ŵakwimusyidwa (m’malembe).”');
INSERT INTO "silika" VALUES(6,30,'6:30','Soni nkaione pa ndema jachakajimichidwe (pameso) pa M’mbuje gwao! (Nkajile kuiona yakogoya kusyene). Jwalakwe takajile (pakwawusya): “Ana ayi (yakwimusyidwa ni kuŵalanjidwayi) nganiiŵa yakuona?” Ŵanganyao takajile (pakwanga): “Yakuonaonape, tukunnumbila M’mbuje gwetu.” Jwalakwe takajile: “Basi pasyani ilagasyo ligongo lya ayila yamwaliji nkuikanila.”');
INSERT INTO "silika" VALUES(6,31,'6:31','Pamasile paapelelwe aŵala ŵaaŵele nkukanila ya kusimangana ni Allah, mpaka Kiyama jili jaichilile mwa chisupuchisya chachitiji: “E Masausyo getuwe! Ligongo lya ayila yatwailechelele pa duniya,” soni ŵanganyao chakaŵelechele misigo jao (jasambi) ku migongo kwao, kaje kusakala nnope yachakaŵelechele yakwe!');
INSERT INTO "silika" VALUES(6,32,'6:32','Ni nganiuŵa umi wa duniya ikaŵeje masanje ni chanache, nambo Nyumba Jambesi ni jambone nnope kwa aŵala ŵaakun''jogopa (Allah), ana jenumanja ŵangali lunda?');
INSERT INTO "silika" VALUES(6,33,'6:33','Tukumanyilila kusyene yanti chisimu ikun''dandaulikasya yaakuŵecheta, chisimutu ŵanganyao ngaakunkanila mmwejo (Muhammadi صلى الله عليه وسلم), nambo ŵalupuso akuŵa nkugakanila ma Ȃya ga Allah.');
INSERT INTO "silika" VALUES(6,34,'6:34','Soni pamasile paŵakanilidwe achimitenga paujo penu, basi ni ŵapilile pa yaŵakanilidweyo, soni ŵasausyidwe mpaka chaichilile chikulupusyo Chetu, pangali jwagalausya maloŵe ga Allah, soni pamasile pa sim''bichilile abali sya achimitenga.');
INSERT INTO "silika" VALUES(6,35,'6:35','Sano naga kutundumala kwao (kwakuntundumalila mmwe Muhammadi صلى الله عليه وسلم ni yam''biche nayo) kukunkulila (mwanti ngaŵa nkombola kupilila), basi naga n''di ŵakombola kusosa lisomwa (lyakwinjilila) mwitaka kapena makwelelo (gakwelela kwinani) ni kwaichilila nacho chisimosimo (basi tendani, nambo naga ngankombola pililani). Soni Allah angasache angasongangenye (wosope ŵene) pa chongoko, basi ngasim''ba ata panandi mwa ŵandu ajinga.');
INSERT INTO "silika" VALUES(6,36,'6:36','Chisimu ŵaakwitikaga ni aŵala ŵaakupikanagape, nambo ŵawe (achimakafili) Allah tachajimusya (m’malembe) kaneko Kukwakwe tachiuchisyidwa.');
INSERT INTO "silika" VALUES(6,37,'6:37','Ni akutiji: “Ligongo chichi ngantuluchila jwalakweju chisimosimo kuumila kwa M''mbujegwe?” Jilani: “Chisimu Allah ali Jwakombola kutulusya chisimosimo, nambo kuti ŵajinji mwa jamanjao ngakumanyilila.”');
INSERT INTO "silika" VALUES(6,38,'6:38','Ni pangali chinyama chilichose (chachikwenda) petaka, namuno chijuni chachiguluka ni mapapiko gakwe ganagaŵili ikaŵeje jili mikutula (ja iwumbe) chisawu ŵanganyammwe, nganituleka (kuchisalichisya) chilichose m’Chitabu, kaneko kwa Ambuje ŵao chachisonganganyisyidwa.');
INSERT INTO "silika" VALUES(6,39,'6:39','Sano aŵala ŵaakukanila ma Ȃya Getu, ali ŵangapikana, ŵangaŵecheta soni ali m’chipi, jwansachile Allah (kuti asokonechele) akunnechelelaga kusokonechela, soni jwansachile (kuti ajongoche) akum''bikaga petala lyagoloka.');
INSERT INTO "silika" VALUES(6,40,'6:40','Jilani: “Ana nkuyiona uli, naga ilagasyo ya Allah ili im''bichilile, kapena kum''bichilila Kiyama, ana mpaka mum''bilanje jwinepe jwangaŵaga Allah (kuti an''jokole), nam''baga jenumanja ŵakuonaonape?”');
INSERT INTO "silika" VALUES(6,41,'6:41','(Ngwamba)! Nambo kuti Jwelepejo nijwampaka mum''bilanje, basi ni mpaka antyochesye yampaka mum''bilanjile (kuti antyochesyeyo) naga ali asachile, soni ni mpaka nyyiliŵalile yankuŵa nkuiwanganya (ni Allah).');
INSERT INTO "silika" VALUES(6,42,'6:42','Soni chisimu chene twatumisye (achimitenga) ku mikutula ja paujo penu, basi ni twajikwembekenye (mikutulajo) ni ulanda kwisa soni makungu kuti jilinandiye.');
INSERT INTO "silika" VALUES(6,43,'6:43','Nambi ligongo chichi nganalinandiyaga pandema ja yaichilile ipotesi Yetu? Nambo kuti mitima jao jaliji jejumu, soni shetani ŵasalalichisye yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,44,'6:44','Sano katema kaŵaapwaanyiile ayila yaŵaliji nkukumbusyidwa, twaawugulile milango ja chindu chilichose (chambone chaŵachilajilaga), kwikanila ndema jaŵasangalele nayo ayila yaŵapedwile, twakwembekenye mwa chisupuchisya, basi papopo ŵaliji ŵakata tama.');
INSERT INTO "silika" VALUES(6,45,'6:45','Basi ni syakatidwe njiga sya aŵala ŵaŵatesile lupuso (pakuwa wosope ŵene), soni lumbili losope lwene alapikwe Allah M''mbuje jwa iumbe yosope.');
INSERT INTO "silika" VALUES(6,46,'6:46','Jilani: “Ana nkuyiona uli, naga ali ajigele Allah kupikana kwenu ni kulola kwenu, ni kudinda (chidindo) m’mitima mwenu, ana nnunguchi jwangaŵaga Allah jwampaka am''buchisye yalakweyo?” Nnole yatukuti pakusalichisya nnope ma Ȃya, kaneka ŵanganyao ni akutundumala kusyene!');
INSERT INTO "silika" VALUES(6,47,'6:47','Jilani: “Ana nkuiona uli, naga ilagasyo ya Allah ili im''bichilile mwa chisupuchisya (lwachilo) kapena mwakuonechela (musinapula), ana mpaka ajonasiche (ŵanepe) ŵangaŵaga ŵandu ŵalupuso?”');
INSERT INTO "silika" VALUES(6,48,'6:48','Soni ngatukutumisyaga Achimitenga ikaŵeje kuti aŵe ŵakutagulila abali syakusengwasya ni ŵakutetela, nambo ŵaakulupilile ni kwilanya (itendo yao), basi ngaaja kuŵa ni woga soni ngaaja kuŵa ŵakudandaula.');
INSERT INTO "silika" VALUES(6,49,'6:49','Sano aŵala ŵakaaniile ma Ȃya Getu, tiyichakwaya ilagasyo ligongo lya kunyosya kwao.');
INSERT INTO "silika" VALUES(6,50,'6:50','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Une ngangunsalila yanti ngwete imatiilo ya Allah, soni nganguimanyilila yakusisika, soni ngangunsalila yanti une lilaika, ngangukuya ikaŵeje yaikujuwulidwa kukwangu.” Jilani: “Ana jwangalola mpaka alandane ni jwakulolaga? Nambi uli ngankuchetelela?”');
INSERT INTO "silika" VALUES(6,51,'6:51','Ni mwatetele najo (Qur’aniji) aŵala ŵaakogopa yanti, chachisonganganyisyidwa kwa M’mbuje gwao (Allah) ali nganakola ŵakwagosa ata ŵakwajokola jwangaŵaga Jwalakwe, (mwatetelani) kuti aŵe ni woga (wakun''jogopa Allah).');
INSERT INTO "silika" VALUES(6,52,'6:52','Soni nkaaŵiinga aŵala ŵaakum’bombaga M''mbuje gwao (Allah) kundaŵi ni kwigulo achisachililaga Ngope Jakwe, kuŵalanjidwa kwao ngakukum’bandika ata panandi nombe soni kuŵalanjidwa kwenu ngakukwaŵandika ŵanganyao ata panandi mwanti ni kwaaŵiinga, (naga nchaaŵingaga,) basi tim''be mu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(6,53,'6:53','Soni iyyoyopeyo ni yatutite pakwalinga mayeso ŵane mwa jamanjao (ŵali ŵakupata) kupitila mwa ŵane (ŵali ŵakulaga pakwajongola ŵakulagao ŵakupata nkanaŵe) kuti (ŵakupata) ajile: “Ana ŵanganyaŵa ni ŵaŵatendele Allah ukoto (pakwajongola) chilikati chetu (kutuleka uwwe? Nganijiŵa dini jakuona, ngati nitukatandite kunda uwwe ŵanganyao nkanaŵe!)” Ana Allah nganaŵa Jwakwamanya chenene ŵakutogolela?');
INSERT INTO "silika" VALUES(6,54,'6:54','Soni pati am''bichilile aŵala ŵaakukulupilila ma Ȃya Getu, basi jilani: “Ntendele uŵe kukwenu! M''mbuje gwenu alichisisye chanasa, kuti jwatesile mwa jenumanja chakusakala mwaujinga kaneka nikutenda toba panyuma pakwe, ni kwilanya (itendo yakwe), basi Jwalakwe (Allah) ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(6,55,'6:55','Soni iyyoyopeyo ni yatukuti pagopolanya ma Ȃya, soni kuti lionechele papaswela litala lya akuleŵa.');
INSERT INTO "silika" VALUES(6,56,'6:56','Jilani: “Chisimu une nganyisyidwe kwaagalagatila aŵala ŵankwalumbanjila jwangaŵaga Allah.” Jilani: “Nganguya isako yenu, mwanti ngajile nguye ngaliji pamasile pasokonechele, soni nga nganimba mwa ŵakongoka.”');
INSERT INTO "silika" VALUES(6,57,'6:57','Jilani: “Chisimu une ndili pa chimanyilo chakumanyukuka chakuumila kwa Ambuje ŵangu (jajili Tauhidi), sano jenumanja ni nkuchikanila chalakwecho (pakunkana Allah), une nganingola yele (ipotesi) yankuichelenjelayo, pangali ulamusi ikaŵeje wa Allah pe, akusimbaga yakuona, soni Jwalakwe ni Jwambone nnope mwa akwilanya (magambo).”');
INSERT INTO "silika" VALUES(6,58,'6:58','Jilani: “Ingaŵe nakwete yele yankuichelenjelayo chisimu chilamusi (chakumpa ipotesi) chikapitisyidwe chilikati changu ni jenumanja (mwanakamo), sano Allah ni Nkwaamanya chenene ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(6,59,'6:59','Soni Kukwakwe ni kugali makiyi ga yakusisika pangali ŵaakugamanya ikaŵeje Jwalakwe, ni akuimanya yaili pankuli ni m''mbwani, soni ngalikwikaga mwakupakatika lisamba lililyose ikaŵeje akulimanyaga, kapena lusongolo lwalukuŵa nchipi chetaka, kapena chachiŵisi chilichose namuno chachijumu, ikaŵeje (yosope) ili m’chitabu chakusalichisya (chilichose Al-Lauh-Al-Mahfûz).');
INSERT INTO "silika" VALUES(6,60,'6:60','Soni Jwalakwe ni Ajula jwaakumpaga chiwa (chalugono) chilo, soni ni jukumanyililaga yampanganyisye muusi, kaneka ni akun''jimusyaga m’muusimo kuti jikwanile ndema jekolanjidwe (jakutamila ni umi), kaneka Kukwakwe ni kwakuujila kwenu, kaneko ni tachintagulila mu ichindu yamwaliji nkutenda.');
INSERT INTO "silika" VALUES(6,61,'6:61','Soni Jwalakwe ni Jwakupunda ni kutawala pa iwumbe Yakwe, ni akuntumichisyaga ŵanganyammwe (Achimalaika) ŵakusunga (ni kulemba itendo yenu), kwikanila ndema ja kum''bichilila jumo jwenu chiwa Achimitenga Ŵetu (Achimalaika) akasantyosyaga umi, soni ŵanganyao ŵangakwanjila (masengo gao).');
INSERT INTO "silika" VALUES(6,62,'6:62','Kaneko taachiuchisyidwa kwa Allah Bwana jwao Jwakuona, manyililani yanti ulamusi ni Wakwe, soni Jwalakwe ni Jwachitema nnope (pakuŵalanjila) mwa akuŵalanjila (wosope).');
INSERT INTO "silika" VALUES(6,63,'6:63','Jilani: “Ana nduni jwaakun’jokolaga m’chipi (cha ipotesi) ya pankuli ni m''mbwani (pa ndema ja) nkum’bombaga mwakulinandiya ni mwakusongona (kuti): ‘Naga akutujokola mu yele (ipotesiyi) chisimu chituŵe mu ŵakutogolela.”');
INSERT INTO "silika" VALUES(6,64,'6:64','Jilani: “Allah ni jwaakun’jokolaga mu yalakweyo ni m’matabu galigose, yeti kaneka jenumanja ni nkuwanganya (Allah ni isanamu!)”');
INSERT INTO "silika" VALUES(6,65,'6:65','Jilani: “Jwalakwe jukwete ukombosi wakuntumichisya ŵanganyammwe ilagasyo kuumila pachanya penu kapena pasi pa ngongolo syenu, kapena kum''bwatanganyaŵatanganya (nikuŵa) mikutulamikutula (jakutindana nganisyo), ni kwapa kupasya ŵane mwa jenumanja njalale sya ŵane (uwwanga).” Nnole yatukuti pakusalichisya nnope ma Ȃya kuti ŵanganyao amanyilile.');
INSERT INTO "silika" VALUES(6,66,'6:66','Sano ŵandu ŵenu (mmwe Muhammadi صلى الله عليه وسلم) ajikaaniile jalakwe (Qur''aniji) kutendaga jili jakuonaonape. Jilani: “Une nganimba jwakunnolela (ni kusunga itendo yenu).”');
INSERT INTO "silika" VALUES(6,67,'6:67','Abali jilijose jikwete ndema jakwe jisyesyene (jakutendechela), ni tinchimanyilila.');
INSERT INTO "silika" VALUES(6,68,'6:68','Soni pati mwaweni aŵala ŵaakuweweta yakwayana ni ma Ȃya Getu (pagatenda chipongwe), basi mwaŵambalani mpaka ajinjile mu ngani jine. Sano naga shetani ali an''diŵesye (ni gamba kulisisimuchila n''dinao), basi panyuma pakumbuchila, ngasintama nao ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(6,69,'6:69','Soni aŵala ŵali ni woga (wakun''jogopa Allah), ngakukwakwaya kuŵalanjidwa kwao (ŵalupusowo) ata panandi, nambo (masengo gao ni) kwakumbusya (ŵalupusowo) kuti mwine chaŵe ni woga (wakun''jogopa Allah ni kuleka chipongwecho).');
INSERT INTO "silika" VALUES(6,70,'6:70','Soni mwalekani aŵala ŵaajitesile dini jao kuŵa masanje ni chanache, ni wanyenjile umi wa duniya. Nambo mwakumbusyani najo (Qur’aniji kuti akalamuche), utakuja kupelechedwa ntima (wa mundu jwalijose) ku chonasiko ligongo lya yawapanganyisye, uli nganiukola nkuugosa namuno nkuujokola jwangaŵaga Allah, soni nantimuno chiulinjilile kupeleka chaaliombolela chantundu uliose ngasichipocheledwa kuumila kukwakwe, ŵanganyao ni aŵala ŵapelechedwe ku chonasiko ligongo lya yaŵapanganyisye, akwete kupata yakumwa yamesi gamasemule kwisa soni ilagasyo yakupoteka nnope ligongo lya yaukafili yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,71,'6:71','Jilani: “Ana tuilumbanjileje (inepe) yangaŵaga Allah ichindu yangaŵa nkutupa chikamuchisyo kapena masausyo, ni tuwuchisyidwe ku indende yetu (kuujila ku ukafili) panyuma pakutujongola Allah? (Tuŵe) chisawu mundu jwasochelesyedwe ni mashetani pa chilambo nikuŵa jwapupulipupuli? Ali akwete achimijakwe ŵaakum''bilanjila ku chongoko (kuti): ‘Yikani kukwetu!’ (Nambo jwalakwe syantipe! Uwwe ngaŵa nkuŵa mpela jwele.)” Jilani: “Chisimu chongolo cha Allah ni chachili chongolo chisyesyene, soni tulamuliidwe kuti tulipeleche kwa M''mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(6,72,'6:72','Soni (atuchisisye yanti): “Jimikani Swala mwakolosya ni kun''jogopaga Jwalakwe (Allah), soni Jwalakwe ni Ajula jwatinchisonganganyisyidwa Kukwakwe.”');
INSERT INTO "silika" VALUES(6,73,'6:73','Soni Jwalakwe ni Ajula jwaagumbile mawunde ni mataka mwakuonaonape, ni (jwagumbile soni) lisiku (lya Kiyama) lyatachiti (pakuchisalila chindu chilichose chachawile): “Syukani!” Basi ni chichisyuka. Maloŵe Gakwe nigagali gakuonaonape. Soni uchimwene uchiŵa Wakwewakwepe lisiku lya lichigombedwa lipenga, Jwakumanyilila yakusisika ni yakuonechela, soni Jwalakwe ni Jwalunda lwakusokoka, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(6,74,'6:74','Soni (kumbuchilani) katema kajwaŵechete Ibrahima kwasalila babagwe Azara (kuti): “Ana nkuitenda isanamu kuŵa milungu? Chisimu une ngum''bona mmwejo ni ŵandu ŵenu kuti n''di nkupotela kwakuonechela.”');
INSERT INTO "silika" VALUES(6,75,'6:75','Soni iyyoyopeyo (yatwatite pakunnosya kupotela kwa babagwe), twannosisye Ibrahimajo uchimwene wa kumawunde ni petaka kuti juŵe soni mu ŵakusimichisya (chikulupi chao).');
INSERT INTO "silika" VALUES(6,76,'6:76','Pandema jakwanswelele jwajiweni ndondwa, ni jwatite (melemelepe kusaka kwimusya lunda lwa makafili): “Aji (ndondwaji) ni m''mbuje gwangu.” Nambo ndema jajatiŵile jwatite: “Jwanganonyelwa (milungu) jakutiŵilaga.”');
INSERT INTO "silika" VALUES(6,77,'6:77','Pandema jajwauweni mwesi uchikopokaga jwatite: “Aju ni m''mbuje gwangu.” Nambo ndema jawatiŵile jwatite: “Naga ngaakunjongola Ambuje ŵangu, chisimu timbe mu ŵandu ŵakupotela.”');
INSERT INTO "silika" VALUES(6,78,'6:78','Pandema jajwaliweni lyuŵa lichikopokaga jwatite: “Aju ni m''mbuje gwangu, aju ni jwankulungwa kwannope.” Nambo ndema jalyakapile jwatite: “E ŵandu wangu! Chisimu une ndalikangene ni yankum''bwanganya nayo (Allah).”');
INSERT INTO "silika" VALUES(6,79,'6:79','“Chisimu une nunjiche ngope jangu kwa Ajula jwaagumbile mawunde ni mataka, ndili jwakongoka (pakulekangana nasyo dini syepombotale), soni une nganimba mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(6,80,'6:80','Niŵatataŵene najo ŵandu ŵakwe (pakun''jogoya kuti chatapane ligongo lyakulekangana ni isanamu). Jwalakwe jwatite: “Ana nkutataŵana none pa ya Allah kutendaga pamasile paanjongwele? Soni nganimba naijogwepe yankum''bwanganya nayo (Allah), (ngaŵa nkuumbata chilichose) ikaŵeje Ambuje ŵangu ali asachile chinechakwe (kuti chimbate), Ambuje ŵangu atenjele pa umanyilisi wakuchimanya chilichose, ana ngankukumbuchila (yangun''jamukaga ni kwamuchika)?”');
INSERT INTO "silika" VALUES(6,81,'6:81','“Ana soni chinaijogope chantiuli yamum''bwanganyisye (nayo Allah), kutendaga jenumanja ngankogopa yanti mum''bwanganyisye Allah ni (isanamu) yanganantuluchisya nayo umboni (wakundisya kuigalagatila), ana ni nkutulachi pa jiŵiliji (wangune kapena wajenumanja) wauli wakuŵajilwa kuŵa pa ntunjelele, naga n''di ŵakumanyilila?”');
INSERT INTO "silika" VALUES(6,82,'6:82','Aŵala ŵaakulupilile ni nganawanganya chikulupi chao ni lupuso (lwagalagatila isanamu), ŵanganyao ni ŵachachipata ntunjelele soni ni ŵali ŵakongoka. ');
INSERT INTO "silika" VALUES(6,83,'6:83','Soni walakweo ni umboni Wetu watwampele Ibrahima wakwagomechela ŵandu ŵakwe, tukunnyakulilaga uchimbichimbi (wakwe) jwatunsachile, chisimu Ambuje ŵenu ni Ŵalunda lwakusokoka, Ŵakumanyilila nnope.');
INSERT INTO "silika" VALUES(6,84,'6:84','Ni twampele (Ibrahimajo mwanache jwakuŵilanjikwa) Isihaka kwisa soni (chisukulu chakuŵilanjikwa) Yaakubu, jwalijose mwa jamanjao twan''jongwele, nombe najo Nuhu twan''jongwele kala (Ibrahima nkanaiche), soni mu ŵanache ŵakwe (ŵa Nuhu twan''jongwele) Daudi, Sulaimana, Ayyubu, Yusufu, Musa ni Haruna, soni iyyoyopeyo niyatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(6,85,'6:85','Kwisa soni Zakariya, Yahaya, Isa ni Iliyasa, jwalijose mwa jamanjao jwaliji mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(6,86,'6:86','Kwisa soni Ismaila, Al-yasa‘a (Eliya), Yunusu ni Lutwi, soni jwalijose mwa jamanjao twanchimbichisye kupunda iwumbe yosope (yandaŵi jakwe).');
INSERT INTO "silika" VALUES(6,87,'6:87','Kwisa soni ŵane mwa achatati ŵao, ŵanache ŵao ni achalongo achimijao, soni twasagwile ŵanganyao ni kwajongolela kwitala lyagoloka.');
INSERT INTO "silika" VALUES(6,88,'6:88','Chele ni chongolo cha Allah, akun’jongolaga nacho jwansachile mwa achikapolo Ŵakwe, nambo akam''bwanganye (Allah ni isanamu), ikajonasichile yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,89,'6:89','Ŵanganyao ni aŵala ŵatwapele chitabu, umanyilisi (wamalamusi ga dini) ni utume, nambo naga aga (makafiliga) gayikaane yalakweyi, basi pamasile patuiŵiichiile ŵandu ŵanganaŵa ayikanile.');
INSERT INTO "silika" VALUES(6,90,'6:90','Ŵanganyao ni aŵala ŵaŵajongwele Allah, sano kuyani chongoko chaocho. Jilani: “Une ngangun’juga malipilo pa aji (Qur''aniji), nganijiŵa jalakweji ikaŵeje chikumbusyo ku iwumbe yosope.”');
INSERT INTO "silika" VALUES(6,91,'6:91','Soni (Ayuda) ngananchimbichisya Allah kunchimbichisya kusyesyene pandema jaŵatite: “Allah nganatulusyeje chilichose kwa mundu jwalijose.” Jilani: “Ana nduni juŵatulwisye chitabu chaŵaiche nacho Musa chachaliji lilanguka ni chongolo kwa ŵandu? Chankuchitenda kuŵa ikalakala (yalekane-lekane), nkuilosyaga (yansachile kuilosya), nambo yaijinji nkuisisaga (yaikutindana ni nganisyo syenu), soni ni n''jiganyisyidwe (ni aji Qur’aniji) yanganinyyimanyaga jenumanja namuno achatati ŵenu?” Jilani: “Allah (ni jwaŵatulwisye).” Kaneko mwalekani aching''andaga nkuweweta mwaomo.');
INSERT INTO "silika" VALUES(6,92,'6:92','Sano aji (Qur’aniji), ni chitabu chatuchitulwisye chaupile chagombelechesya yaili paujo pakwe, soni kuti mwaatetele (ŵandu) ŵa pa chitepo cha misi (Maaka) ni ŵali mungulugulu mwakwe, sano aŵala ŵaakukulupilila ya Akhera akuchikulupililaga chele (chitabuchi), soni ŵanyao akuŵa nkwendelechela kusigosa Swala syao (pakusiswaliswali).');
INSERT INTO "silika" VALUES(6,93,'6:93','Ana nduni jwali jwalupuso nnope kumpunda mundu jwansepele Allah unami, kapena jwatite: “Gajuwulidwe kukwangu (maloŵe),” kutendaga nganigajuwulidwa kukwakwe (maloŵe) galigose! Ni mundu jwatite: “Chindulusye yakulandana ni yaatulwisye Allah!” Soni nkaione pa ndema janti ŵandu ŵalupuso ali nkuputaputa kwa chiwa, ni Achimalaika ali ajongwele makono gao (pakwanyekula, achitiji): “Likoposyani mitima jenu! Lelo chillipidwe ilagasyo yakunyosyeka ligongo lya yamwaliji nkum''bechetela Allah yangaŵa yakuona, soni ni mwaliji nkulikwesya pakuleka ma Ȃya Gakwe (nkaiweni yakogoya kusyene). ”');
INSERT INTO "silika" VALUES(6,94,'6:94','Soni (pa Kiyama takasalile kuti): “Pamasile pantuichilile jikajika mpela yatwatite pakun''gumba pandanda, soni ni iyyilesile munyuma mwenu yosope yatwampele, ni ngatukwaona nomwe akun''jokola ŵenu ŵamwaliji nkulitwanjila kuti ŵanganyao ni akuwanganya ŵenu (ni Allah). Chisimu (nkamulano) chilikati chenu (ni ŵanganyao) pamasile paukatiche, soni insokonechele yamwaliji nkulitwanjila (kuti nombe nayo milungu).”');
INSERT INTO "silika" VALUES(6,95,'6:95','Chisimu Allah ni Nkuganga lusongolo ni lupele (kuti lumele), akasakoposyaga chawumi kutyochela mu chachiwe, soni ni Nkukoposya chachiwe kutyochela mu chawumi, Jwalakwejo ni Allah, nambi nkugalausyidwa chinauli (kuleka yakuona)?');
INSERT INTO "silika" VALUES(6,96,'6:96','(Jwalakwe ni) Nkutaatanukula lipananga lya kumasikusiku, ni achitesile chilo kuŵa chakupumula, nombe nalyo lyuŵa ni mwesi kuŵa yakuŵalanjila (moŵa ni yaka), walakweo ni mpango wa (Allah) Jwamachili gakupunda, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(6,97,'6:97','Soni Jwalakwe ni Ajula jwam''bichile ndondwa kuti n''jongoche nasyo m’chipi chapankuuli ni m''mbwani (pakulungama ku upande wankuja). Pamasile patugopolenye ma Ȃya kwa ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(6,98,'6:98','Soni Jwalakwe ni Ajula juŵan''gumbile kuumila mwa mundu jumo (Adamu), mwanti (kukwenu) kwana liwuto lyakutamilichika (papali pa duniya kapena m’chitumbo cha mama), kwisa soni liwuto lyakusunjila (mwamuli m’malembe kapena mu n''gongo mwa baba), pamasile patugopolenye ma Ȃya kwa ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(6,99,'6:99','Soni Jwalakwe ni Ajula jwaatulwisye mesi (ga wula) kuumila kwinani, basi ni galakwego tukopwesye mmela wa chilichose, basi mu walakweo tukopwesye ikole ni mipesi jakubiliŵita, tukoposyaga mwalakwemo songolo syakupambikana mwakutitigana, nombe mu itela ya tende kuumila mu itungatunga yakwe (mukasakopoka) mikonga jagoyika (ni isogosi), soni (ni mesigo tumesisye) migunda ja zabibu, nzaituni ni makomamanga, yakulandana (masamba) nambo yakulekangana (isogosi ni kanonye kakwe), ilolani isogosi yakwe ndema jaikusogola kwisa soni kajiule kakwe. Chisimu mu yalakweyo mwana imanyilo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(6,100,'6:100','Yeti ni am''biichiile Allah achimijakwe ŵachijini kutendaga Jwalakwe ni juŵagagumbile (majinigo), soni ni ansepele (yanti akwete) ŵanache ŵachilume ni ŵachikongwe mwangali umanyilisi, kuswejela ni Kwakwe soni ali kwanaula ni yaakusimba (ŵanganyao).');
INSERT INTO "silika" VALUES(6,101,'6:101','(Jwalakwe ni) Nkugumba mawunde ni mataka, chantiuli chakole mwanache kutendaga jwangali ŵankwakwe? Soni ni juŵagumbile chilichose, mwanti Jwalakwe chindu chilichose aŵele Jwakuchimanya chenene. ');
INSERT INTO "silika" VALUES(6,102,'6:102','Jwalakwejotu ni Allah M''mbuje gwenu, pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Nkugumba chilichose, basi mun''galagatilani, soni Jwalakwe pa chindu chilichose aŵele Nkuchengeta.');
INSERT INTO "silika" VALUES(6,103,'6:103','Meso nganigaŵa gam''biiche (kuti gam''bone) nambo Jwalakwe ni jwaakugayichilaga meso (pakugaona), soni Jwalakwe ni Jwakumanyilila yakunyilila nnope, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(6,104,'6:104','(Jilani mmwe Muhammadi صلى الله عليه وسلم): “Pamasile pagam''bichilile maumboni gakuloleka kutyochela kwa M''mbuje gwenu, sano jwakulola (maumbonigo ni kugakulupilila), basi uli kukwakwe (ukoto wakwe), nambo jwachatende ngalola, basi ni yakwe (ipotesi), soni une (Ntume) nganimba nkunnolela jwenu.”');
INSERT INTO "silika" VALUES(6,105,'6:105','Soni iyyoyopeyo ni yatukuti pakusalichisya nnope ma Ȃya, mwanti (achimakafili) chajileje: “Ntekusooma (mu itabu yaakala ni ntunjile Qur’aniji kuumila mwalakwemo),” soni tukuti tujisalichisye kwa ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(6,106,'6:106','Kuyani yaijuwulidwe kukwenu kuumila kwa Ambuje ŵenu, pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, soni mwaŵambalani akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(6,107,'6:107','Soni Allah angasache nga nganawwanganya ŵanganyao (Allah ni isanamu), soni nganitum''bika (mmwe Muhammadi صلى الله عليه وسلم) kuŵa nkwalolela jwao, soni mmwe nganim''ba nkwachengeta jwao.');
INSERT INTO "silika" VALUES(6,108,'6:108','Soni ngasimwatukana aŵala ŵaakuilumbanjila inepe yangaŵaga Allah, kogopela kuti atakwika mwantukene Allah mwaumagongo mwangali umanyilisi, iyyoyopeyo tugasalalisye ku nkutula uliose masengo gakwe, kaneka kwa M''mbuje gwao ni kwakuujila kwao, basi ni tachatagulila yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,109,'6:109','Soni (achimakafili) ŵaaluumbiile mwa Allah kulumbila kwao kwamachili kuti: Kwika mwakwaichilila chisimosimo mpaka achikulupilile kusyene. Jilani: “Chisimu isimosimo ili kwa Allah, ana ni chichi champaka chimmanyisye (jenumanja Asilamu) yanti namuno chili chiyiche (chisimosimocho) nganaŵa akulupilile?”');
INSERT INTO "silika" VALUES(6,110,'6:110','Soni chitugalausye mitima jao ni meso gao (mwanti ngaakombola kuchikulupilila chakuona namuno ali achiweni), mpela yaŵatite pangachikulupilila pandanda, soni ni chitwaleche nkusokonechela mwaomo achiyumbagayumbaga.');
INSERT INTO "silika" VALUES(6,111,'6:111','Soni namuno (achimakafilio) tungaatuluchisye Malaika (kwisa kuŵichila umboni yautume wenu), nombe ŵawe nikwaŵechetekasya (pakwagumbalichisya umboni), ni kwasonganganyichisya chindu chilichose pameso pao, nganganaŵa ŵanti nikulupilila ikaŵeje Allah ali asachile, nambo kuti ŵajinji mwa ŵanganyao ali muujinga.');
INSERT INTO "silika" VALUES(6,112,'6:112','Soni iyyoyopeyo tum''biichiile Ntume jwalijose amagongo (gagali) mashetani gachiŵandu ni gachijini, akasamanyisyanaga ŵane ni ŵane maloŵe gepete pakusaka kunyenga, nambo Ambuje ŵenu angasache nganganaitenda yalakweyo ŵanganyao, basi mwalekani ni yaakuŵa nkusepelela (yaoyo).');
INSERT INTO "silika" VALUES(6,113,'6:113','Soni kuti jipendechele ku gele (maloŵe gaunamigo) mitima ja aŵala ŵangakukulupililaga ya Akhera ni kuti soni aganonyele (maloŵego), soni ni kuti apitilisye kupanganya (yasambi) yaakuŵa nkupanganya.');
INSERT INTO "silika" VALUES(6,114,'6:114','(Jilani Mmwe Muhammadi صلى الله عليه وسلم): “Ana sosesose jwine nkwilanya jwangaŵaga Allah, kutendaga Jwalakwe ni Ajula jwaantuluchisye chitabu (cha Qur’an) chegopolanye mwakusalichisya?” Soni aŵala ŵatwapele chitabu (cha Taurat ni Injil), akumanyililaga yanti jalakwe (Qur’aniji) jitulusyidwe kutyochela kwa Ambuje ŵenu mwakuonaonape, basi ngasim''ba ata panandi mwa ŵakaichila.');
INSERT INTO "silika" VALUES(6,115,'6:115','Soni maloŵe ga Ambuje ŵenu gakwaniile pakuona ni pagoloka, pangali nkupitikusya maloŵe Gakwe, soni Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(6,116,'6:116','Soni naga nkwaapikanila ŵajinji ŵali pachilambo, chansokonesye pakuntalichisya kwitala lya Allah, pangali yaakukuya ikaŵeje liganichisyape, soni pangali yaakutenda ikaŵeje unami basi.');
INSERT INTO "silika" VALUES(6,117,'6:117','Chisimu Ambuje ŵenu ni ŵaakummanyilila chenene jwaasokonechele kuleka litala Lyakwe, soni Jwalakwe ni jwaakwamanyilila chenene ŵakongoka.');
INSERT INTO "silika" VALUES(6,118,'6:118','Basi lyagani (nyama) jajikolanjiliidwe lina lya Allah (pa kujisikita), nam''baga jenumanja ŵakulupilila ma Ȃya Gakwe.');
INSERT INTO "silika" VALUES(6,119,'6:119','Soni ana chichi chachinnechele kulya (nyama) jajikolanjiliidwe lina lya Allah (pa kujisikita), kutendaga pamasile pantagulile mwakalula (nyama) syaasitesile haraam kukwenu (kulya), ikaŵeje (syan''diile) mwakusingalilwa (ligongo lyasala)? Soni chisimu ŵajinji akuŵa nkwasokonesya (ŵane) ni isako yao mwangali umanyilisi, chisimu Ambuje ŵenu ni ŵaakwamanya chene ŵakusumba mpika.');
INSERT INTO "silika" VALUES(6,120,'6:120','Soni lekani ilemwa yakuonechela ni yakusisika yakwe, chisimu aŵala ŵaakupanganya ilemwa chachilipidwa pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(6,121,'6:121','Soni kasin''dya (nyama) janganijikolanjilidwa lina lya Allah (pa kujisikita), soni kutenda iyyo (yakulya chibuduyo) chisimu uŵele ulemwa, soni chisimu mashetani (gachijini) gakwasongonelaga achimijao (ŵali mashetani gachiŵandu) kuti akanganeje namwe (pangani jakusikita), nambo naga nkwajitika (yakulya chibudu) nikuti chim''be ŵakuwanganya (Allah ni indu ine). ');
INSERT INTO "silika" VALUES(6,122,'6:122','Ana jwaŵaliji jwam''bwe (niujinga kwisa soni ukafili) basi Uwwe ni kunsyusya (pakumpa umanyilisi ni chikulupi) ni kum''bichila soni lilanguka (jajili Qur’an) ni kwendaga nalyo mwa ŵandu, mpaka alandane ni jwajili nyendele jakwe m’chipi (chaujinga) mwanti nganaŵa akopwechemo? Iyyoyopeyo isalalisyidwe kwa makafili yaaŵele ali nkupanganya.');
INSERT INTO "silika" VALUES(6,123,'6:123','Soni iyyoyopeyo twaaŵisile m’musi uliose achinkulungwa mwa akusakasya ŵao kuti atendejemo malindi (gakwalekasya ŵandu kukuya Usilamu), ningaakwaatendela malindi (ŵanepe) ikaŵeje achimisyene ni ngaakulimanyilila.');
INSERT INTO "silika" VALUES(6,124,'6:124','Soni pati chayichilile chisimosimo akasatiji: “Ngatukulupilila mpaka tupedwe chisawu yaŵapedwile Achimitenga ŵa Allah.” Allah ni jwaakumanyilila chenene pamalo paakuŵika utenga Wakwe (kuti aju jwakuŵajilwa kuŵa ntenga, ngaŵa jwalijose). Sampano chiwapate aŵala ŵaaleŵile nchesela wakuumila kwa Allah kwisa soni ilagasyo yaukali nnope ligongo lya malindi gaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,125,'6:125','Basi jwaakusaka Allah kuti an''jongole, akasansapangulilaga chidali chakwe ku Usilamu, sano jwaakusaka kunnechelela kusokonechela, akasachitenda chidali chakwe kuŵa chakangani chaning''aning''a, gambakuŵa mpela akulikanganichisya kwela kwinani. Iyyoyopeyo ni yaakutiji Allah pakuŵika ilagasyo yakunyalaya kwa aŵala ŵangakukulupilila.');
INSERT INTO "silika" VALUES(6,126,'6:126','Sano ali ni litala lya Ambuje ŵenu lyalili lyagoloka, pamasile patugopolenye ma Ȃya kwa ŵandu ŵakumbuchila.');
INSERT INTO "silika" VALUES(6,127,'6:127','Akwete kupata ŵanganyao Nyumba Jantendele kwa Ambuje ŵao, soni Jwalakwe ni jutachiŵa Nkwagosa jwao ligongo lya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(6,128,'6:128','Soni (mwakumbusyani) lisiku lyatachasonganganya ŵanaose (nikwasalila yanti): “E jenumanja likuga lya achimajini! Chisimu mwatupiye achinaŵandu (ŵakwasochelesya).” Sano achimijao (majinio) mwa achinaŵandu chachiti: “Ambuje ŵetu! Twapatisyanaga ukoto sikati jetu, ni twaajiyiche ndema jetu ajila jamwatuŵichile (jakuwila).” (Allah) tachiti: “Moto ni liwuto lyenu lyakutamilichika, chim''be ŵandamo mwalakwemo ikaŵeje mwachasachile Allah (kuti ŵandu ŵasambi nambo ŵaliji ni chikulupi cha Tauhidi akopoche), chisimu Ambuje ŵenu ni Ŵalunda lwakusokoka, Ŵakumanyilila nnope.”');
INSERT INTO "silika" VALUES(6,129,'6:129','Soni iyyoyopeyo niyatukutiji pa kwasyoŵekanya ŵalupuso ŵane ni ŵane ligongo lya (sambi) syaaŵele ali nkupata.');
INSERT INTO "silika" VALUES(6,130,'6:130','“E jenumanja makuga ga achimajini ni achinaŵandu! Ana nganam''bichilila achimitenga ŵakuumila mwa jenumanja, ŵaŵaliji nkunsimulila ma Ȃya Gangu ni kuntetelaga ya kusimangana nalyo lisiku lyenu lyeleli?” Ŵanganyao chachiti: “Tulitendele umboni twaachimisyewe.” Ni wanyenjile umi wa duniya, soni chachilitendela umboni achimisyene kuti ŵanganyao ŵaliji makafili.');
INSERT INTO "silika" VALUES(6,131,'6:131','Yalakweyi ni ligongo lyanti Ambuje ŵenu nganaŵa ŵajijonanga misi ligongo lya lupuso kutendaga achimisyene misijo akakumanyilila.');
INSERT INTO "silika" VALUES(6,132,'6:132','Soni jwalijose akwete kupata uchimbichimbi kuumila mu yaŵapanganyisye, sanotu Ambuje ŵenu nganaŵa ŵanti ni ngaimanya yaakutenda ŵanganyao.');
INSERT INTO "silika" VALUES(6,133,'6:133','Soni Ambuje ŵenu ni Ŵakupata kwejinji, Asyene chanasa. Naasakaga mpaka antyosye ŵanganyammwe (mwakunsabalisya), sano mmalo mwenu nikwaŵika ŵaakwasaka kuŵa ŵawinjila, mpela yatite pakun''gumba jenumanja kuumila mum''beleko wa ŵandu ŵane.');
INSERT INTO "silika" VALUES(6,134,'6:134','Chisimu yankuŵa nkupedwa chilanga chiyiyichepe basi, soni nganim''ba ŵanganyammwe ŵanti ni kuilepelekasya.');
INSERT INTO "silika" VALUES(6,135,'6:135','Jilani (mmwe Muhammadir): “E ŵandu ŵangu! Kamulani masengo yatin''jile paakamula (ni ukombosi wenu wosope), une none ngukamula (mwangukamulila), basi tinchiyika kumanyilila kuti nduni jwachijiŵa mbesi jakwe (jambone) pachilambo (chaduniya chino ni ku Akhera kwakwe). Chisimu chene ŵalupuso ngaŵa nkupunda.”');
INSERT INTO "silika" VALUES(6,136,'6:136','Soni ŵanganyao am''bichile Allah liunjili lya mbeju ni ilango mu yaŵagumbile (Nsyene), ni akutiji: “Ayi ya Allah, - mukulitwanjila kwao - nambo ayi ya akwawanganya ŵetu (ni Allah yaili isanamu).” Sano yaili yaakwawanganya ŵao basi ngaikwikaga kwa Allah (pati ipelembele peunjili Lyakwe), nambo yaili ya Allah basi ikwikaga ku akwawanganya ŵao (pati ipelembele peunjili lyao), kaje kusakala nnope chiilanyo chao!');
INSERT INTO "silika" VALUES(6,137,'6:137','Soni iyyoyopeyo ni yatite akwawanganya ŵao (ni Allah gagali mashetani) pakwasalalichisya ŵajinji mwa akuwanganya ya kuulaga ŵanache ŵao (pakogopa nchesela ni kulaga), kuti ŵajawusye kuchonasiko ni kwawatanganyichisya dini jao, soni Allah angasache nganganatenda ŵanganyao yalakweyo, basi mwalekani ni yaakuŵa nkusepelela (yaoyo).');
INSERT INTO "silika" VALUES(6,138,'6:138','Soni akutiji: “Ayi ilangoyi ni asi mbejusi iŵele yaakanyisyidwa, pangali ŵatalye yalakweyi ikaŵeje ŵatukwasaka,” – mukulitwanjila kwao. Soni ilango ine ikanyisyidwe migongo jakwe (kujikwela kapena kutwichilila misigo). Sano ilango ine ngaakukolangaga lina lya Allah pakuisikita, gamba kunsepela unami Jwalakwe. Chachalipila pa yaŵaliji nkusepelela.');
INSERT INTO "silika" VALUES(6,139,'6:139','Ni akutiji soni: “Yaili mu ndumbo sya ayi ilangoyi iŵele yaachalume ŵetupe basi, soni iŵele haraam kwa achakongwe ŵetu.” Nambo naga kupagwa chili chachiwe, basi niakuŵa ŵakamusyangana (kulya) chalakwecho (ŵanaose achalume ni achakongwe). Tachalipila (pakwapa ilagasyo) ligongo lya yakusimba yao (yaunami), chisimu Jwalakwe ni Jwalunda lwakusokoka, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(6,140,'6:140','Pamasile paŵapelelwe aŵala ŵaŵawuleje ŵanache ŵao mwakuwawata mwangali umanyilisi, nikuitenda kuŵa haraam ya ŵapele Allah, gamba kunsepela Allah unami, pamasile paŵasokonechele soni nganaŵa ŵakongoka.');
INSERT INTO "silika" VALUES(6,141,'6:141','Soni Jwalakwe ni Ajula jwagumbile matimbe gakuchinjidwa (itela yakwe ligongo lyakutambala mpela matikiti), ni gangachinjidwa (itela yakwe ligongo lyakwimilichika mpela mindimu), ni (agumbile soni) mitende ni mimela jakulekangana yakulidwa yakwe (mukaoneche ni kanonye), kwisa soni nzaituni (wakwenga mauta), ni makomamanga yakulandana (kaoneche) ni kulekangana (kanonye), lyagani isogosi yakwe pati isogwele (ni kwiula), soni pelekani liunjili lyakwe (lya kwapa ŵakulaga) lisiku lyagungula, nambo ngasintenda ulyakasi, chisimu Jwalakwe (Allah) jwanganonyela ŵaulyakasi.');
INSERT INTO "silika" VALUES(6,142,'6:142','Soni muilango (agumbile) yakutwichilila misigo (mpela ngamiya), ni yakupanganyichisya chakutandika (manyunya gakwe mpela ngondolo), lyagani muyampele Allah, ni ngasinkuya sajo sya shetani, chisimu jwalakwe kukwenu ni mmagongo jwakuonechela.');
INSERT INTO "silika" VALUES(6,143,'6:143','(Agumbile) mitundu nsano na jitatu (ja ilango), kumbali ja ngondolo (agumbile) siŵili (tonde ni nkolo), ni kumbali ja mbusi (agumbile) siŵili (tonde ni nkolo), jilani: “Ana Jwalakwe ŵankanyisye achitonde ŵanaŵaŵili (jwangondolo ni jwambusi), kapena mikolo jinajiŵili (wangondolo ni wambusi), kapena (tuŵanache) twatusimaniche muiŵelechelo ya mikolo jiŵiliji? Mumanyisyani mwaumanyilisi nam''baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(6,144,'6:144','Nombe kumbali ja ngamiya (agumbile) siŵili (tonde ni nkolo), ni kumbali ja ng''ombe (agumbile) siŵili (tonde ni nkolo), jilani: “Ana Jwalakwe ŵankanyisye achitonde ŵanaŵaŵili (jwangamiya ni jwang''ombe), kapena mikolo jinajiŵili (wangamiya ni wang''ombe), kapena (tuŵanache) twatusimaniche muiŵelechelo ya mikolo jiŵiliji? Kapena jenumanja mwapali pandema jaŵannamulaga Allah yalakweyi? Ana nduni jwali jwalupuso nnope kumpunda jwaakunsepela Allah unami kuti ŵasochelesye ŵandu mwangali umanyilisi? Chisimu Allah ngaakwaajongolaga ŵandu ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(6,145,'6:145','Jilani: “Nganguchipata muyaijuwulidwe kukwangu chachili haraam (kulya) kwa jwaakusaka kulya, ikaŵeje naga chili chibudu, kapena myasi jakusulusyidwa (ndema ja kusikita), kapena nyama jeguluŵe, chisimu jalakwejo jiŵele jausakwa, kapena (chinyama chakutendekasya) kopoka (mmalamusi ga Allah) pakolanjilidwa (lina linepe) lyangaŵaga lya Allah pakuchisikita (kuŵa mbepesi ja akumasoka).” Nambo jwachasingalilwe (nikulya chaharaamucho) mwangali kuchisachilila nnope kapena kusumba mpika, basi (ngaapata sambi), chisimu Ambuje ŵenu (Allah) ni Ŵakululuka kusyene, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(6,146,'6:146','Sano aŵala ŵaŵaliji Ayuda twakanyisye (kulya) chilichose chachikwete ikotwa yangagaŵikana (mpela liŵata ni hachi), soni kumbali ja ng''ombe kwisa soni mbusi ni ngondolo twakanyisye (kulya) mauta gakwe ikaŵeje gaganyambatile m’migongo jakwe kapena m’matumbo kapena gagawanganyichilene ni maupa, yalakweyo twalipile ŵanganyao ligongo lya unampitiku wao, soni chisimu Uwwe niŵakuŵecheta yakuona.');
INSERT INTO "silika" VALUES(6,147,'6:147','Sano naga akwisa mwakunkanila, basi jilani: “M''mbuje gwenu ni Nsyene chanasa chakwenela, nambo ilagasyo Yakwe yangaŵa nkuyiusya kwa ŵandu ŵakuleŵa.”');
INSERT INTO "silika" VALUES(6,148,'6:148','Chaŵecheteje aŵala ŵawwanganyisye (Allah ni isanamu) kuti: “Allah angasache nganganitum''bwanganya (uwwe) namuno achatati ŵetu, soni nganganituchitenda kuŵa haraam chilichose (chaachitesile Jwalakwe kuŵa halaal, nambo kuti akuinonyela yatukutendayi).” Iyyoyopeyo ni iŵatite pakanila aŵala ŵaŵaliji paujo pao mpaka ŵapasisye ipotesi Yetu. Jilani: “Ana nkwete umanyilisi uliose (wanti Allah akuinonyela yankutendayo) mwanti mpaka ntukopochesye (wele umanyilisio)? Ngankukuya ikaŵeje liganichisyape, soni nganim''ba jenumanja kuti nkuŵecheta inepe ikaŵeje yaunami.”');
INSERT INTO "silika" VALUES(6,149,'6:149','Jilani: “Sano Allah ni jwaakwete umboni wakupesya makani (pakutumisya Achimitume ni kutulusya itabu), mwanti angasache angan’jongwele ŵanaose.”');
INSERT INTO "silika" VALUES(6,150,'6:150','Jilani: “Ikani nasyo mboni syenu syasipeleche umboni kuti Allah ŵayitesile kuŵa haraam yalakweyi.” Sano naga sipelecheje umboni, basi mmwe ngasintendela nasyo umboni, soni nkakuya yaakuisaka aŵala ŵaagakaaniile ma Ȃya Getu, ni aŵala ŵangakukulupilila ya Akhera, soni ŵanganyao akuŵika chakulandanyichisya ni M''mbuje gwao (pakuchigalagatila).');
INSERT INTO "silika" VALUES(6,151,'6:151','Jilani: “Ikaani nansoomele yaayitesile haraam kukwenu M''mbuje gwenu. Ngasimum''bwanganyaga Jwalakwe ni chilichose, ni mwatendeleje yambone achinangolo ŵanaŵaŵili, soni nkaulagaga ŵanache ŵenu ligongo lya kulaga, Uwwe chitumpe lisiki jenumanja ni ŵanganyao, soni ngasiyyiŵandichila yakunyalaya (mpela chikululu) yakuonechela ni yakusisika, soni ngasim''bulaga mundu jwaakanyisye Allah (kum''bulaga) ikaŵeje pa ligongo lyakwitichika (ni malamusi ga Dini), yalakweyi anchisisye jenumanja kuti m''be ni lunda.”');
INSERT INTO "silika" VALUES(6,152,'6:152','“Soni ngasinchiŵandichila chipanje cha mwanache jwaŵina ikaŵeje mwitala lyalili lyambone (mpela kuchitupiya ni malonda), mpaka akuyiche komangala kwakwe (ni kumpaga), soni gumbasyani chipiimo cha mbale kwisa soni sikelo mwachilungamiko, ngatukunkanganika mundu (kutenda) ikaŵeje yampaka ayikombole, soni pati nchiŵechetaga (pakwimba magambo kapena kupeleka umboni), basi lungamikani namuno (jwakun''gomelajo) ali jwaulongo, soni chilanga cha Allah kwanilisyani, yalakweyi anchisisye jenumanja kuti m''be ŵakumbuchila.”');
INSERT INTO "silika" VALUES(6,153,'6:153','“Soni ali ni litala lyangu lyalili lyagoloka basi likuyani, ni ngasinkuya matala (gane) gatakwika mwagannekangenye ni litala Lyakwe (Allah), yalakweyi anchisisye jenumanja kuti m''be ni woga (wakun''jogopa Jwalakwe).”');
INSERT INTO "silika" VALUES(6,154,'6:154','Kaneka twampele Musa chitabu pakusaka kwanilisya (ukoto Wetu) kwa jwampaka akolosye (masengo), ni (kuŵa soni) chakusalichisya chindu chilichose, ni chongolo kwisa soni chanasa, kuti ŵanganyao akulupilile yakusimangana ni M''mbuje gwao.');
INSERT INTO "silika" VALUES(6,155,'6:155','Sano aji (Qur’an) ni chitabu chatutulwisye chachili chaupile, basi chikuyani, ni kuŵa ni woga, kuti jenumanja ntendeledwe chanasa.');
INSERT INTO "silika" VALUES(6,156,'6:156','Kuti kasin''ja kuŵecheta (yanti): “Chisimu chitabu chatulusyidwe ku makuga gaŵili ga m''bujo mwetu (Ayuda ni Akilisito), soni uwwe twaliji tukakumanyilila yaŵaliji nkusooma.”');
INSERT INTO "silika" VALUES(6,157,'6:157','Kapena kasin''ja kuti: “Tungaŵe uwwepe ni ŵachatutuluchile chitabu, tungaliji ŵakongoka nnope kwapunda ŵanganyao.” Basi pamasile pachim''bichilile chimanyilo (chakumanyukuka) chakuumila kwa M’mbuje gwenu (jajili Qur’an) soni chongolo kwisa soni chanasa, ana nduni jwali jwalupuso nnope kumpunda jwaagakaaniile ma Ȃya ga Allah ni kugatundumalila? Tuchalipila aŵala ŵaakugatundumalila ma Ȃya Getu ilagasyo yakusakala nnope ligongo lyakutundumala kwao.');
INSERT INTO "silika" VALUES(6,158,'6:158','Ana pana yaakwembecheya pangaŵaga kwayichilila malaika (kukwatyosya umi), kapena kwika Ambuje ŵenu (lisiku lya Kiyama kukwilanya magambo), kapena kwika ine muimanyilo ya Ambuje ŵenu (yakulosya kuŵandichila Kiyama)? Lisiku lya kwika ine mu imanyilo ya Ambuje ŵenu, ngasichinkamuchisya mundu chikulupi chakwe (chakulupilila pa jele ndemajo) naga nganakulupilila kala, kapena kupanganya yambone m’chikulupi mwakwemo. Jilani: “Jembecheyani, uwwe nowe tukwembecheya. ”');
INSERT INTO "silika" VALUES(6,159,'6:159','Chisimu aŵala ŵaagaŵenye dini jao nikuŵa ŵanganyao mikutula-mikutula, mmwe ngachikum''bandika chilichose cha ŵanganyao, chisimu nganya jao jili kwa Allah (ni jutachimanyilila yakutenda nao), kaneka ni tachatagulila yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(6,160,'6:160','Jwatachiyika nacho chambone (chimo), basi tachipata yambone likumi yakulandana nacho, nambo jwatachiyika nacho chakusakala (chimo), ngasalipidwa ikaŵeje chanti mpela chichocho, soni ŵanganyao ngasaja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(6,161,'6:161','Jilani: “Chisimu une anjongoleele Ambuje ŵangu kwitala lyakongoka (lya Usilamu), jajili dini jagoloka, dini ja Ibrahima juŵaliji jwakongoka, soni nganaŵa mwaakuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(6,162,'6:162','Jilani: “Chisimu swala jangu, ni mbepesi jangu, kwisa soni umi wangu ni chiwa changu, (yosopeyo) ya Allah M''mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(6,163,'6:163','Jwangali ŵakamusyangana Najo, soni yalakweyi ni yanamulidwe, soni une ni jwandanda mwa Asilamu.”');
INSERT INTO "silika" VALUES(6,164,'6:164','Jilani: “Ana sose m''mbuje jwine jwangaŵaga Allah, kutendaga Jwalakwe ni M''mbuje jwa chindu chilichose?” Ni ngakupanganyaga mundu jwalijose (yasambi) ikaŵeje akulipanganyichisya nsyene (ipotesi yakwe), soni jwaatwiichiile nsigo (wasambi) ngasaja kutwichila nsigo wajwine, kaneka kwa M''mbuje gwenu ni kwakuujila kwenu, basi nitachintagulila ichindu yamwaliji nkulekangana muyalakweyo.');
INSERT INTO "silika" VALUES(6,165,'6:165','Soni Jwalakwe ni Ajula jwantesile ŵanganyammwe kuŵa ŵawinjila pachilambo (panyuma pakwajonanga ŵakala), ni ŵanyakwile pa uchimbichimbi ŵane mwajenumanja pachanya pa ŵane kuti an''dije mayeso pa ichindu yampele, chisimu Ambuje ŵenu ni ŵachitema pakupeleka ipotesi, nambo soni Ŵalakweo ni Ŵakululuka kusyene, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(7,1,'7:1','Alif-Lâm-Mîm-Swâd. ');
INSERT INTO "silika" VALUES(7,2,'7:2','(Achi ni) chitabu (cha Qur’an) chachitulusyidwe kukwenu, basi muntima mwenu mukaŵa mwana kutaŵika pa (kuchilungusya) chalakwechi (pakogopa kunkanila), kuti mwatetele nacho (achimakafili), ni kuŵa chikumbusyo kwa ŵakulupilila.');
INSERT INTO "silika" VALUES(7,3,'7:3','Kuyani yaitulusyidwe kukwenu jenumanja kuumila kwa M''mbuje gwenu (Allah), ni ngasimwakuya akugosa (ŵanepe) ŵangaŵaga Jwalakwejo, yannonope ni yankukumbuchila.');
INSERT INTO "silika" VALUES(7,4,'7:4','Ana soni jilingwa mwa misi jatwajijonasile, ni yajiyichilile ipotesi Yetu lwaachilo kapena ŵanganyao ali n''gusama (muusi)!');
INSERT INTO "silika" VALUES(7,5,'7:5','Ni nganikuŵa kulilasika kwao pa ndema jayaichilile ipotesi Yetu ikaŵeje ŵatiji: “Chisimu uwwe twaliji ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(7,6,'7:6','Basi chisimu chene tuchawusya aŵala ŵaŵatumisyilidwe (achimitenga), nombenao achimitenga tuchawusya.');
INSERT INTO "silika" VALUES(7,7,'7:7','Basi chisimu chene tuchasimbila (ngani syao syosope) mwaumanyilisi, soni Uwwe nganituŵa ŵakusoŵa (pa yaŵatendaga).');
INSERT INTO "silika" VALUES(7,8,'7:8','Soni chipiimo (chakupimila masengo ga ŵandu) lyele lisikulyo chichiŵa chakuonaonape (changali chinyengo), sano ŵayikasitope ipiimo yao (yamasengo gambone), basi ŵanganyao ni ŵachakaŵe ŵakupunda.');
INSERT INTO "silika" VALUES(7,9,'7:9','Nambo ŵayikapepuche ipiimo yao (yamasengo gambone), basi ŵanganyao ni aŵala ŵalipelelwekasisye achimisyene ligongo lya lupuso lwaŵaliji nkugatenda ma Ȃya Getu.');
INSERT INTO "silika" VALUES(7,10,'7:10','Soni pamasile patuntamilichikasisye ŵanganyammwe pachilambo, ni tum''bichilepo yakulamila, nambo kutogolela kwenu kwannonope.');
INSERT INTO "silika" VALUES(7,11,'7:11','Soni chisimu twan''gumbile ŵanganyammwe (babaŵenu Adam), kaneka ni kunkolosya (pakumpa kaoneche kachimundu), kaneko ni twatite kwa Achimalaika: “Mwasujudilani Adam.” Basi wosope ni ŵasujudu kusigala Ibulisu, nganaŵa mwa akusujudu.');
INSERT INTO "silika" VALUES(7,12,'7:12','(Allah) jwatite: “Chichi chinsiŵilile kuti nkasujudu ndema janallamwile?” Jwalakwe jwatite: “Une ni jwambone nnope kumpunda jwalakweju, mwangumbile une kuumila m’moto, ni mun''gumbile jwalakweju kuumila mwilongo.”');
INSERT INTO "silika" VALUES(7,13,'7:13','(Allah) jwatite: “Basi tulukani mwele (mu Mbepomo), mwanti ngayikuŵajilwa kukwenu kuti n''dikwesye mwalakwemo, basi kopokani chisimu mmwejo n''di muŵakuchembuluka.”');
INSERT INTO "silika" VALUES(7,14,'7:14','Jwalakwe jwatite: “Mumbani lipesa (lyakuti ngawa) mpaka lisiku lyakwimusyidwa (ŵandu m’malembe).”');
INSERT INTO "silika" VALUES(7,15,'7:15','(Allah) jwatite: “Chisimu mmwejo n''di muŵakupedwa lipesa.”');
INSERT INTO "silika" VALUES(7,16,'7:16','Jwalakwe jwatite: “Basi ligongo lyanti mundesile kuŵa jwakupombotala, chinatamilile kwene ŵanganyao petala Lyenu lyagoloka (kuti nasokonesye).”');
INSERT INTO "silika" VALUES(7,17,'7:17','“Kaneka chinaayichilileje kwene cham''bujo mwao ni chamunyuma mwao, kwisa soni chakun''dyo kwao ni chakunchiji kwao, soni ngamwasimana ŵajinji mwajamanjao ali ŵakutogolela.”');
INSERT INTO "silika" VALUES(7,18,'7:18','(Allah) jwatite: “Kopokani mwalakwemo n''di ŵakunyosyedwa ni kuŵinjidwa, chisimu ŵataiche mwakunkuya mmwe mwaŵanganyao, nikuti tingajigumbasye kwene Jahannama ni jenumanja wosope.”');
INSERT INTO "silika" VALUES(7,19,'7:19','Kaneko (Allah jwatite kwa Adam): “E mmwe Adam! Tamani mmwejo ni ŵankwenu mu Litimbe lya ku Mbepo, basi n’dyeje palipose pansachile, nambo ngasinchiŵandichila achi chitelachi ntakuŵa muŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(7,20,'7:20','Basi shetani jwaasongonele (yakusakala Adam ni Hawa), kuti iwonechele kukwao yayasisiche kwa ŵanganyao mu umakonope wao, (shetani) ni jwatite: “M''mbuje gwenu nganankanya achi chitelachi ikaŵeje kuti ntakwika mwansyuchile malaika gaŵili, kapena kuŵa muŵandamo (ŵangawa).”');
INSERT INTO "silika" VALUES(7,21,'7:21','Ni jwalumbile pameso pao (juchitiji): “Chisimu une ndili (jumpepe) mwa akum''bunda misyungu.”');
INSERT INTO "silika" VALUES(7,22,'7:22','Basi ni jwaatulwisye (pa uchimbichimbi wao) mwakwanyenjelela. Basi pandema jaŵapasyisye (isogosi ya) chitelacho, waonechele kukwao umakonope wao, ni ŵatandite kulinyambatika masamba ga mu Litimbemo, ni jwaaŵilasile M''mbuje gwao (juchitiji): “Ana nganinankanya ŵanaŵaŵilimmwe achi chitelachi, ni kunsalila kuti chisimu shetani kukwenu ni mmagongo jwakuonechela?”');
INSERT INTO "silika" VALUES(7,23,'7:23','Ŵanaŵaŵilio ŵatite: “E Ambuje ŵetu! Tulitendele lupuso twachimisyewe, nambo naga ngankutukululuchila ni kututendela chanasa chisimu chituŵe mu ŵakwasika.”');
INSERT INTO "silika" VALUES(7,24,'7:24','(Allah) jwatite: “Tulukani, chilikati chenu chili chana utinda, soni nkwete jenumanja kuduniya ndamo jenu ni kusengwa kwa ndema jannono.”');
INSERT INTO "silika" VALUES(7,25,'7:25','(Allah) jwatite: “Tinkaŵeje umi mwele (m’duniyamo), soni mwalakwemo tinkaweje, soni kuumila mwele (mwitakamo) tinchikoposyedwa.”');
INSERT INTO "silika" VALUES(7,26,'7:26','E jenumanja ŵanache ŵa Adam! Chisimu tuntuluchisye iwaalo yaikusisa umakonope wenu nikuŵa soni ipeto (yakulipetela nayo), nambo iwaalo ya woga (wakun''jogopa Allah mpela chikulupi ni masengo gambone), yele ni yaili yambone nnope. Yalakweyo ili mu imanyisyo ya Allah kuti ŵanganyao aŵe ŵakumbuchila.');
INSERT INTO "silika" VALUES(7,27,'7:27','E jenumanja ŵanache ŵa Adam! Akansokonesya shetani yaŵatite pakwakopokasya achinangolo ŵenu ŵanaŵaŵili mu Litimbe lya ku Mbepo, achaawuulaga yakuwala yao kuti ŵaalosye umakonope wao, chisimu jwalakwejo akasam''bonaga ŵanganyammwe - jwele ni lukosyo lwakwe (lwachijini) – mukaŵe kanti jenumanja ngankwaonaga. Chisimu Uwwe tugatesile mashetani kuŵa isyoŵe ya aŵala ŵangakukulupilila.');
INSERT INTO "silika" VALUES(7,28,'7:28','Soni naga atesile chakunyalaya akasatiji: “Twasimene achatati ŵetu achitenga chalakwechi, nombenajo Allah atulamwile chelechi.” Jilani: “Chisimu Allah jwangalamula yakunyalaya. Ana mkum''bechetela Allah yangankuimanyilila?”');
INSERT INTO "silika" VALUES(7,29,'7:29','Jilani: “Ambuje ŵangu alamwile chilungamiko, ni (akuti soni) lungamisyani ngope syenu (Kukwakwe) pamalo palipose pakusujudila (ndema jakuswali), ni munnumbanjileje Jwalakwe mwakunswejechesya dini Jakwe, mpela yaŵatite Jwalakwe pakuntanda (kun''gumba, iyyoyopeyo soni) chinchiujila (Kukwakwe).”');
INSERT INTO "silika" VALUES(7,30,'7:30','Likuga line alijongwele, nambo likuga line kuŵajilwe kukwao kusokonechela, chisimu ŵanganyao agatesile mashetani kuŵa akwagosa ŵao kunneka Allah, ni akuganichisya yanti ŵanganyao ali ŵakongoka.');
INSERT INTO "silika" VALUES(7,31,'7:31','E jenumanja ŵanache ŵa Adam! Jigalani yakulisalalichisya yenu (pakuwala yakuwala yachasa) pamalo palipose pakuswalila, ni lyagani soni mwagani nambo ngasintenda ulyakasi, chisimu Jwalakwe (Allah) jwaanganonyela ŵaulyakasi.');
INSERT INTO "silika" VALUES(7,32,'7:32','Jilani: “Ana nduni jwaayitesile kuŵa haraam ipeto ya Allah (mpela yakuwala yakusalala) yaŵakopochesye achikapolo Ŵakwe, kwisa soni lisiki lyambone?” Jilani: “Yalakweyo pa umi wa duniya (uno) achinsyene ŵakwe ni ŵakulupilila (namuno kuti makafili akupasyaga nao, nambo) lisiku lya Kiyama ichiŵa yaoyaope (ŵakulupililao).” Iyyoyopeyo ni yatukuti pagopolanya ma Ȃya kwa ŵandu ŵaakumanyilila.');
INSERT INTO "silika" VALUES(7,33,'7:33','Jilani: “Chisimu Ambuje ŵangu ayitesile kuŵa haraam yakunyalaya (yaili ilemwa yayikulungwa mpela chikululu), yaiwonechele muyalakweyo ni yaisisiche yakwe, ni yasambi (yantundu uliose), kwisa soni uwwanga (wakutenda) mwangali ligongo, ni kuti mum''bwanganyeje Allah ni ichindu yanganaituluchisya umboni (wakundisya kuigalagatila), soni nikuti mum''becheteleje Allah yangankuimanyilila.”');
INSERT INTO "silika" VALUES(7,34,'7:34','Soni nkutula uliose wana ndema jakwe (jakuwila), basi pati jiyiche ndema jaojo, nganaŵa ajichelewasisye kwakandaŵi namuno kujijanguya.');
INSERT INTO "silika" VALUES(7,35,'7:35','E jenumanja ŵanache ŵa Adam! Ndema jilijose jakum''bichilila achimitenga ŵakuumila mwa jenumanja, ni kunsimbilaga ma Ȃya Gangu (mwajitikani), sano ŵaligosile (kuilemwa) ni kwilanya (itendo yao), basi ngaaja kuŵa ni woga soni ngaaja kuŵa ŵakudandaula.');
INSERT INTO "silika" VALUES(7,36,'7:36','Nambo aŵala ŵaakaaniile ma Ȃya Getu ni kulikwesya nago, ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(7,37,'7:37','Ana nduni jwali jwalupuso nnope kumpunda mundu jwansepele Allah unami, kapena kanila ma Ȃya Gakwe? Ŵanganyao chilyapate liunjili lyao (lya yambone ya umi wa duniya lya ŵalembeledwe) m’chitabu (cha lisosa lya Allah namuno kuti ali makafili), kwikanila ndema jakwaichilila achimitenga Ŵetu (ŵali Achimalaika) kwisa kwatyosya umi, tachiti (pakwawusya): “Ili kwapi ayila yamwaliji nkuilumbanjila kunneka Allah?” Ŵanganyao tachiti: “Itusokonechele.” Ni chachilitendela umboni achimisyene wanti ŵanganyao ŵaliji makafili.');
INSERT INTO "silika" VALUES(7,38,'7:38','(Allah) tachiti: “Jinjilani ku Moto m’mikutula jajapite kala jenumanja nkanimpagwe ja majini ni ŵandu. Ndema jilijose jaukajinjileje nkutula tiukaulweseje (nkutula) ujakwe (wakala), kwikanila ndema jatakasongangane mwalakwemo ŵanaose, ŵakumalichisya ŵao (kwinjila) chachiti pakwamba ya ŵakutandilila ŵao: “Ambuje ŵetu! Ŵanganyaŵa ŵatusokonesye, basi mwapani ilagasyo yakonjechesya ya Moto.” Jwalakwe tachiti: “Jwalijose akwete (ilagasyo) ya konjochesya nambo kuti ngankumanyilila.”');
INSERT INTO "silika" VALUES(7,39,'7:39','Sano ŵakutandilila ŵao tachiti kwa ŵakumalichisya ŵao: “Nganiupagwatu kukwenu ukoto uliose ŵakutupunda uwwe, basi pasyani ilagasyo ligongo lya (sambi) syamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(7,40,'7:40','Chisimu aŵala ŵaakaaniile ma Ȃya Getu ni kulikwesya nago, ngasaugulilidwa milango ja kwinani, soni ngasajinjila ku Mbepo mpaka jikajinjile ngamiya pebowo lyasingano (yaili yangakomboleka), soni iyyoyopeyo ni yatukutiji pakwalipila akuleŵa.');
INSERT INTO "silika" VALUES(7,41,'7:41','Akwete ŵanganyao kuumila mu Jahannama ugono (ŵamoto), ni pachanya pao chakwaunichila (chamoto), soni iyyoyopeyo ni yatukutiji pakwalipila ŵalupuso.');
INSERT INTO "silika" VALUES(7,42,'7:42','Sano aŵala ŵaakulupilile nikutendaga yambone - ngatukunkanganichisyaga mundu (kutenda) ikaŵeje yampaka ayikombole - ŵanganyao niŵandu ŵa ku Mbepo, ŵanyao mwalakwemo takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(7,43,'7:43','Ni tuchityosya yayaliji m’mitima mwao ya utinda (waŵatindanaga pa duniya), sulo sinankujilima cha pasi pao, ni tachiti: “Lumbili losope lwene alapikwe Allah Ajula jwaŵatujongolele kuyalakweyi, soni nganganituŵa ŵanti nikongoka ingaŵe yanti Allah nganatujongola. Chisimu achimitenga ŵa M’mbuje gwetu pamasile paŵayiche ni yakuona.” Ni chachiŵilanjidwa (pakwasalila yanti): “Jalakweji ni Mbepo jan''jinjisyidwe majumba ligongo lya yamwaliji nkutenda.”');
INSERT INTO "silika" VALUES(7,44,'7:44','Soni ŵandu ŵa ku Mbepo tachaŵilanga ŵandu ŵa ku Moto (achitiji): “Chisimu uwwe tuisimene yaŵatupaga chilanga Ambuje ŵetu kuti yakuona, ana nombe ŵanganyammwe iyyisimene yaŵampaga chilanga Ambuje ŵenu kuti yakuona?” Ŵanganyao tachiti (pakwanga): “Elo.” Basi ni tachijenesya nkwenesya chilikati chao yanti: “Malweso ga Allah gali pa ŵalupuso.”');
INSERT INTO "silika" VALUES(7,45,'7:45','“Aŵala ŵaŵaliji nkusiŵilila (ŵandu kuti akajenda) petala lya Allah ni kusachililaga ali alipombotesye (litalalyo), soni ŵanganyao ŵaliji ali nkukanila ya Akhera.”');
INSERT INTO "silika" VALUES(7,46,'7:46','Soni chilikati chao (cha ŵandu ŵa ku Mbepo ni ŵa ku Moto), chipakaŵe pana uŵigo (chachili chikwesya), soni pachanya pa chikwesya, paachiŵa pana ŵandu (ŵakulandana itendo yao yambone ni yakusakala) ŵachachiŵa ali nkwamanyilila wosope (ŵandu ŵa ku Mbepo ni ŵa ku Moto) ni imanyilo yao. Ni tachaŵilanga ŵandu ŵa ku Mbepo (achitiji): “Ntendele uŵe kukwenu.” Chachiŵa nkanajijinjile (Mbepojo) nambo ali akwete tama (janti tajijinjile).');
INSERT INTO "silika" VALUES(7,47,'7:47','Sano ndema jachigakagalausyidweje meso gao (kulola) upande wa ŵandu ŵa ku Moto takajileje: “Ambuje ŵetu! Ngasintuŵika uwwe pamo ni ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(7,48,'7:48','Soni ŵandu ŵa pa chikwesya chachaŵilanga ŵandu (ŵa ku Moto) ŵachachamanyilila ni imanyilo yao achitiji: “Nganiunkamuchisya winji wenu ula namuno ayila yamwaliji nkulikwechesya nayo.”');
INSERT INTO "silika" VALUES(7,49,'7:49','“Ana ŵanyaŵa ngaŵa ni aŵala ŵamwalumbililaga yanti Allah nganaŵa ŵatendele chanasa? (Nnole! Iŵechetedwetu kukwao yanti): ‘Jinjilani ku Mbepo, ngam''ba ni woga soni ngam''ba ŵakudandaula.”');
INSERT INTO "silika" VALUES(7,50,'7:50','Nombe ŵandu ŵa ku Moto tachaŵilanga ŵandu ŵa ku Mbepo (achitiji): “Tupungulilani mesi kapena lisiki lyampele Allah.” Ŵanganyao tachiti: “Chisimu Allah ayitesile inayiŵiliyo kuŵa yakanyisyidwa kwa achimakafili.”');
INSERT INTO "silika" VALUES(7,51,'7:51','Aŵala ŵaŵajitesile dini jao kuŵa chanache ni masanje, ni wanyenjile umi wa duniya. Basi lelo chitwaliŵalile yaŵatite pakukuliŵalila kusimana nalyo lisiku lyao lyeleli, ni yaŵatiji pakanila ma Ȃya Getu.');
INSERT INTO "silika" VALUES(7,52,'7:52','Soni chisimu pamasile patuiche nacho kukwao chitabu (cha Qur’an) chatuchigopolenye mwa umanyilisi, kuŵa chongolo kwisa soni chanasa kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(7,53,'7:53','Ana pana chaakwembecheya pangaŵaga kalulila kwakwe (kwa yayikusalidwa m’chitabumo kuti ichitendekwa)? Lisiku lyakwika kalulila kwakwe (lisiku lya Kiyama) tachiŵecheta aŵala ŵaŵachiliŵalile kala (chitabucho kuti): “Chisimu achimitenga ŵa M’mbuje gwetu (Allah) ŵayiche ni yakuona (lelo tukwitichisya). Ana ni tukwete uwwe akutujokola ŵanti ni atujokole, kapena mpaka ikomboleche kutuwusya kuti tukatende (yambone) yangaŵaga ayila (yakusakala) yatwaliji nkutenda?” Pamasile paŵalipelelwekasyisye achimisyene, ni yaasokonechele ŵanganyao (isanamu) yaŵaliji nkuisepelela (kuti chiyichajokola kwa Allah).');
INSERT INTO "silika" VALUES(7,54,'7:54','Chisimu M''mbuje gwenu ni Allah Ajula juŵagumbile mawunde ni mataka m''moŵa nsano nalimo, kaneka ni jwatenjele pa Arishi (Chindanda Chaulungu). Akuchitendaga chilo kuŵa chakuwuunichila muusi ni chikasaukuyaga mwa chitemachitema (iyyopeyo soni muusi kuchiunichila chilo ni ukasachikuyaga mwa chitemachitema), soni (agumbile) lyuŵa ni mwesi kwisa soni ndondwa, (yosope) ijepepasyidwe kwa (kuya) chilamusi Chakwe. Manyililani yanti gumba ni Kwakwe kwisa soni ulamusi, anyakwiche Allah (pa uchimbichimbi ni upile) M''mbuje jwa iwumbe (yosope).');
INSERT INTO "silika" VALUES(7,55,'7:55','Mum’bombani M''mbuje gwenu mwakulinandiya ni mwakusisa, chisimu Jwalakwe jwangaanonyela ŵakusumba mpika.');
INSERT INTO "silika" VALUES(7,56,'7:56','Ni ngasim''bwatanganyaga pachilambo panyuma pa kupajilanya, ni mum’bombeje mwaoga ni mwakola tama, chisimu chanasa cha Allah chili dusi kwa ŵakolosya.');
INSERT INTO "silika" VALUES(7,57,'7:57','Soni Jwalakwe ni Ajula jwaakujitumisyaga mbungo kuŵa chisengwasyo paujo pa ukoto Wakwe (wula nkanijigwe), mpaka pati jili jitwichile (mbungojo) maunde Gausito, tukasajitumisyaga ku chilambo chachiwe. Basi ni tukasatulusyaga mesi (gawula) kwalakweko, basi ni galakwego tukasakoposyaga isogosi yantundu uliose, iyyoyopeyo ni yatuchiti pakwakoposya ŵawe (m’malembe). (Yosopeyi) kuti jenumanja M''be ŵakumbuchila.');
INSERT INTO "silika" VALUES(7,58,'7:58','Sano chilambo chambone (chachajila) mmela wakwe ukasakopoka (mwagapugapu) mulisosa lya Ambujegwe, nambo (chilambo) chachisakele (changali chajila, mmela wakwe) wangakopoka ikaŵeje mwakulajilila. Iyyoyopeyo ni yatukuti pagopolanya ma Ȃya kwa ŵandu ŵaakutogolela.');
INSERT INTO "silika" VALUES(7,59,'7:59','Chisimu chene twantumisye Nuhu kuŵandu ŵakwe nijwatite: “E ŵandu ŵangu! Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe, chisimu une ngun''jogopela ilagasyo ya lisiku lyekulungwa nnope.”');
INSERT INTO "silika" VALUES(7,60,'7:60','Achinkulungwa mwa ŵandu ŵakwe ŵatite: “Chisimu uwwe tukum''bona kuti n''di mukusokonechela kwakuonechela.”');
INSERT INTO "silika" VALUES(7,61,'7:61','Jwalakwe jwatite: “E ŵandu ŵangu! Kwaune kwangali kusokonechela, nambo kuti uneji Ntenga jwakuumila kwa M''mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(7,62,'7:62','“Ngulungusya kukwenu utenga wa Ambuje ŵangu, soni ngum''bunda misyungu, ni ngumanyilila kuumila kwa Allah yangankuimanyilila.”');
INSERT INTO "silika" VALUES(7,63,'7:63','“Ana nkusimonga kum''bichilila kwenu chikumbusyo kuumila kwa M''mbuje gwenu kupitila mwa mundu jwakutyochela mwa jenumanja, kuti antetele, ni kuti n''jogope, soni nikuti ŵanganyammwe ntendeledwe chanasa?”');
INSERT INTO "silika" VALUES(7,64,'7:64','Nambo ŵankaaniile, basi ni twankulupwisye jwalakwe ni aŵala ŵaŵaliji najo imo m’chombo, ni twamisyisye aŵala ŵaŵaakaaniile ma Ȃya Getu. Chisimu ŵanganyao ŵaliji ŵandu ŵangalola.');
INSERT INTO "silika" VALUES(7,65,'7:65','Sano kwa achina Adi (twantumisye) nnongo n’jao (jwali) Hudu. Jwalakwe jwatite: “E ŵandu ŵangu! Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe, ana ngankogopa?”');
INSERT INTO "silika" VALUES(7,66,'7:66','Anchinkulungwa aŵala ŵaŵakanile mwa ŵandu ŵakwe ŵatite: “Chisimu uwwe tukum''bona kuti n''di nkupulika, soni uwwe tukun''ganichisya kuti mmwejo jumpepe mwa ŵaunami.”');
INSERT INTO "silika" VALUES(7,67,'7:67','Jwalakwe jwatite: “E ŵandu ŵangu! Kwaune kwangali kupulika, nambo kuti uneji Ntenga jwakuumila kwa M''mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(7,68,'7:68','Ngulungusya kukwenu utenga wa Ambuje ŵangu, soni une kukwenu ni nkuwunda misyungu jwakulupichika.');
INSERT INTO "silika" VALUES(7,69,'7:69','Ana nkusimonga kum''bichilila kwenu chikumbusyo kuumila kwa M''mbuje gwenu kupitila mwa mundu jwakutyochela mwa jenumanja kuti antetele? Soni kumbuchilani ndema jaŵam''bisile jenumanja kuŵa ŵawinjila panyuma pa ŵandu ŵa Nuhu, ni an''jonjechesye kusapanguka mu kagumbidwe (pakumpa ilu yamachili, yekulungwakulungwa soni yeleuleu), basi kumbuchilani indimba ya Allah kuti m''be ŵakupunda.”');
INSERT INTO "silika" VALUES(7,70,'7:70','Ŵanganyao ŵatite: “Ana ntuyichilile kuti tun''galagatileje Allah Jikape ni tuleche yaŵaliji nkugalagatila achatati ŵetu? Basi ikani nayo kukwetu yankutupa chilangayo naga n''di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(7,71,'7:71','Jwalakwe jwatite: “Pamasile pa in''gwilile ilagasyo kwisa soni gasabu kuumila kwa M''mbuje gwenu. Ana nkukangana none pa mena gamwagambile kugakolanga ŵanganyammwe ni achatatiŵenu (pa isanamu yankuigalagatila), kutendaga Allah nganatulusya pa galakwego umboni uliose (wakulosya kuti gelego mena ga milungu jakuŵajilwa kujigalagatila)? Basi jembecheyani, chisimu une ndili mwa akwembecheya namwe.”');
INSERT INTO "silika" VALUES(7,72,'7:72','Basi ni twankulupwisye jwalakwe ni aŵala ŵaŵaliji najo imo ni chanasa chakuumila Kukwetu, ni twakatile njiga sya aŵala ŵaŵaakaaniile ma Ȃya Getu (pakuwa wosope ŵene), soni nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(7,73,'7:73','Sano kwa achina Samuda (twantumisye) nnongo n’jao (jwali) Swaleh. Jwalakwe jwatite: “E ŵandu ŵangu! Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe, pamasile pachim''bichilile chilosyo chakuonechela palangulangu kuumila kwa M''mbuje gwenu. Aji ngamiya ja Allah jiŵele chilosyo kukwenu, basi jilekani jilyeje petaka lya Allah, ni ngasin''jikwaya ni yakusakala iliyose, itakwika mwainkwembekenye ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(7,74,'7:74','“Soni kumbuchilani ndema jaŵam''bisile jenumanja kuŵa ŵawinjila panyuma pa achina Adi, ni antaamiiche ndamo jambone pachilambo, nkulitaŵila majumba gamakulungwakulungwa muiŵata yakwe, soni ni nkusepa m’matumbi majumba, basi kumbuchilani indimba ya Allah, ni nkalapwaga pachilambo pakuwatanganya.”');
INSERT INTO "silika" VALUES(7,75,'7:75','Anchinkulungwa aŵala ŵaŵalikwesyisye mwa ŵandu ŵakwe ŵatite pakwasalila aŵala ŵaŵaŵalanjidwaga kuti ŵangalikombolela mu ŵandu ŵaŵakulupilile mwajamanjao: “Ana ni nkumanyilila kusyene yanti Swaleh juli jwakutumisyidwa kuumila kwa M''mbujegwe?” Ŵanganyao ŵatite: “Chisimu uwwe ichindu yaatumisyidwe nayo jwalakweju tuli ŵakuikulupilila.”');
INSERT INTO "silika" VALUES(7,76,'7:76','Aŵala ŵaŵalikwesyisye ŵatite: “Chisimu uwwe ayi yanyyikulupilileyi tuli ŵakuikana.”');
INSERT INTO "silika" VALUES(7,77,'7:77','Basi niŵajisinjile ngamiya,ni kutenda chalu pakuleka (kuya) lilamusi lya M’mbuje gwao, ni kuŵecheta kuti: “E mmwe Swaleh! Ikaani nayo kukwetu yankutupa chilangayo naga n’di mwa achimitenga.”');
INSERT INTO "silika" VALUES(7,78,'7:78','Basi nichakwembekenye chisikinya, mwanti ŵasimanikwe m’majumba mwao (ali awile) chitindiŵalile.');
INSERT INTO "silika" VALUES(7,79,'7:79','Basi jwalakwe nijwaatyochele aku juchitiji: “E ŵandu ŵangu! Chisimu pamasile panalungwisye utenga wa Ambuje ŵangu kukwenu, soni ni nam''bundile misyungu (jambone), nambo kuti jenumanja ngankwanonyelaga akuwunda misyungu.”');
INSERT INTO "silika" VALUES(7,80,'7:80','Nombenajo Lutwi (twampele utume. Kumbuchilani) katema kajwatite kwaŵandu ŵakwe: “Ana nkutenda yakunyalaya yanganannongoleleje kuitenda yalakweyo jwalijose mu iwumbe yosope (yakala)?”');
INSERT INTO "silika" VALUES(7,81,'7:81','“Chisimu ŵanganyammwe nkwatenda achalume matanyula kuleka (kwalomba) achakongwe, nambo ŵanganyammwe niŵandu ŵakupundanganya malile (pa kupanganya sambi syekulungwa nnope).”');
INSERT INTO "silika" VALUES(7,82,'7:82','Ni nganiuŵa wanje wa ŵandu ŵakwe (kuti waliji wambone), ikaŵeje kuŵecheta yanti: “Mwaŵingani m’musi mwenu, chisimu ŵanganyao ni ŵandu ŵaakuliiswejesya (chatamile yapi m’musi wausakwa!)”');
INSERT INTO "silika" VALUES(7,83,'7:83','Basi ni twankulupwisye jwalakwe pamo ni liŵasa lyakwe, ikaŵeje ŵankwakwepe; ŵaliji mwa akusigalila munyuma (ni ŵajonasiche).');
INSERT INTO "silika" VALUES(7,84,'7:84','Ni twatuluchisye wula (ja maganga), basi nnole mujaŵelele mbesi ja akuleŵa.');
INSERT INTO "silika" VALUES(7,85,'7:85','Sano (kuŵandu ŵa) ku Madiyana (twantumisye) nnongo n’jao (jwali) Shuaibu. Jwalakwe jwatite: “E ŵandu ŵangu! Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe, pamasile pachim''bichilile chilosyo chakuonechela palangulangu kuumila kwa M''mbuje gwenu. Basi gumbasyani chipiimo chambale kwisa soni sikelo, soni ngasimwapunguchisyaga ŵandu ichindu yao, ni ngasim''bwatanganyaga pachilambo panyuma pa kupakolosya. Yalakweyi ni yaili yambone kukwenu nam''baga jenumanja ŵakulupilila (kusyene).”');
INSERT INTO "silika" VALUES(7,86,'7:86','“Soni ngasintamaga petala palipose nikwajogoyaga (ŵandu), soni ni kwasiŵililaga kwitala lya Allah ŵandu ŵankulupilile Jwalakwe, ni kusachililaga n''di nlipombotesye (litalalyo), soni kumbuchilani ndema jamwaliji ŵansakato kaneka niŵantupiye. Soni nnole mujaŵelele mbesi ja akuwatanganya.” ');
INSERT INTO "silika" VALUES(7,87,'7:87','“Soni naliŵaga likuga line mwa jenumanja liukulupilile (utenga) wandumisyidwe nao, sano likuga line ni nganilikulupilila, basi pililani mpaka Allah ajilanye chilikati chetu, soni Jwalakwe ni Jwambone nnope mwa akwilanya (magambo).”');
INSERT INTO "silika" VALUES(7,88,'7:88','Achinkulungwa aŵala ŵaŵalikwesyisye mwa ŵandu ŵakwe ŵatite: “Chisimu uwwe chitum’binje mmwe Shuaibu m’musi mwetu pamo ni aŵala ŵaakulupilile imo nimmwe, ayo iyayi nikuti chim’bujile kwene ku dini jetu.” Jwalakwe jwatite: “Namuno kuti tuli nkujiŵenga?”');
INSERT INTO "silika" VALUES(7,89,'7:89','“Chisimu chituŵe tunsepele Allah unami naga tuli tuwujile ku dini jenu panyuma panti Allah ali atujokweleko, soni ngaŵa nkuŵa kukwetu yakuwujila kwalakweko ikaŵeje naga Allah Ambuje ŵetu ali asachile, Ambuje ŵetu atenjele paumanyilisi wakuchimanya chilichose, kwa Allah pe ni kwatujeegamiile, Ambuje ŵetu! Jilanyani chilikati chetuwe ni ŵandu ŵetu mwakuonaonape, soni Mmwejo ni Ŵambone nnope mwa akwilanya.”');
INSERT INTO "silika" VALUES(7,90,'7:90','Achinkulungwa aŵala ŵaŵakanile mwa ŵandu ŵakwe ni ŵatite: “Nambo naga munkuye Shuaibu chisimu ŵanganyammwe pa jele ndemajo chim''be ŵakwasika.”');
INSERT INTO "silika" VALUES(7,91,'7:91','Basi ni chakwembekenye chisikinya, mwanti ŵachelelwe (ali ŵawe) m’majumba mwao chitindiŵalile.');
INSERT INTO "silika" VALUES(7,92,'7:92','Aŵala ŵaŵankaaniile Shuaibu ŵaliji nti chisawu nganatamejepo (pamalopo), aŵala ŵaŵankaaniile Shuaibu ni ŵaŵaliji ŵakwasika.');
INSERT INTO "silika" VALUES(7,93,'7:93','Basi jwalakwe nijwatyochele aku juchitiji: “E ŵandu ŵangu! Chisimu pamasile panalungwisye mautenga ga Ambuje ŵangu kukwenu, soni ni nam''bundile misyungu (jambone), sano chinadandaulile chinauli ŵandu makafili?”');
INSERT INTO "silika" VALUES(7,94,'7:94','Soni nganitutumisyeje m’musi (uliose) ntume jwalijose (ni kunkana) ikaŵeje twakwembekanyaga achimisyeneo ni ulanda kwisa soni makungu kuti ateteŵale.');
INSERT INTO "silika" VALUES(7,95,'7:95','Kaneka twagalausyaga pamalo pa yakusakala ni kuŵikapo yambone (twatyochesyaga ulanda ni makungugo nkanakulupililepe), mpaka ŵatupaga (ni kutupa soni ipanje yao), ni ŵaikanaga mwakuŵecheta yanti: “(Ata kalakala ni iyyiyipeyi) makungu ni ntendele yapataga achatati ŵetu (makunguga ngaŵa ligongo lya ukafili.)” Basi ni twakwembekanyaga mwachisupuchisya akakumanyilila.');
INSERT INTO "silika" VALUES(7,96,'7:96','Soni ingaŵe yanti achimisyene misio ŵakulupilile ni kuŵa ni woga (wakun''jogopa Allah), chisimu tungaugulile upile kuumila kwinani ni pasi, nambo kuti ŵakaaniile, basi ni twakwembekenye ligongo lya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(7,97,'7:97','Ana achimisyene misio akuliona kuti ali pantunjelele kwanti ngaŵa nkwaichilila ipotesi Yetu lwachilo ali nnugono?');
INSERT INTO "silika" VALUES(7,98,'7:98','Kapena achimisyene misio akuliona kuti ali pantunjelele kwanti ngaŵa nkwaichilila ipotesi Yetu lwansana ali nkung''anda?');
INSERT INTO "silika" VALUES(7,99,'7:99','Ana akuliona kuti ali pa ntunjelele ku mitego ja Allah? Basi ngaŵa nkuliona kuti ali pa ntunjelele ku mitego ja Allah ikaŵeje ŵandu ŵali ŵakwasika.');
INSERT INTO "silika" VALUES(7,100,'7:100','Ana nganiyiŵa yakumanyika kwa aŵala ŵaachitawele chilambo panyuma pa achimisyene (ŵaŵatamagamo kala) yanti; Uwwe tungasache tungapatisye ipotesi ligongo lya sambi syao, ni kwadinda m’mitima mwao kwanti nganaŵa apikene (chilichose)?');
INSERT INTO "silika" VALUES(7,101,'7:101','Jalakwejo nimisi jatukunsimbila sine mwa ngani syakwe, soni chisimu chene achimitenga ŵao ŵaichilile ni ilosyo yakuonechela palangulangu, basi ni nganaŵa ŵanti ni kuikulupilila yaŵaikanile kala, iyyoyopeyo niyakasatiji Allah pakudinda m’mitima ja achimakafili.');
INSERT INTO "silika" VALUES(7,102,'7:102','Soni nganituchisimana kwa ŵajinji mwa ŵanganyao chilanga chilichose (chaŵaliji nkuchikwanisya), nambo twaasimene ŵajinji mwa ŵanganyao ali ŵakunyosya.');
INSERT INTO "silika" VALUES(7,103,'7:103','Kaneka panyuma pao (achimitenga ŵasalidweo), twantumisye Musa ni isimosimo Yetu kwa Firiauna ni nduna syakwe, basi ni ŵaitesile lupuso pakuikana, basi nnole mujaŵelele mbesi ja akuwatanganya.');
INSERT INTO "silika" VALUES(7,104,'7:104','Sano Musa jwatite: “E mmwe Firiauna! Chisimu une ni Ntenga jwakuumila kwa M''mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(7,105,'7:105','“Ikusosekwa kukwangu kuti ngasinam''bechetela Allah ikaŵeje yakuonaonape, chisimu nam''bichiiliile ŵanganyammwe ni chisimosimo chakuonechela chakuumila kwa M’mbuje gwenu, basi mwalekani mbigule nao ŵanache ŵa Israila.”');
INSERT INTO "silika" VALUES(7,106,'7:106','(Firiauna) jwatite: “Nam''baga m''biiche ni chimanyisyo basi ikaani nacho naga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(7,107,'7:107','Basi (Musa) ni jwaponyisye simbo jakwe (pasi). Nalole! Jasyuchile lijoka lyakuonechela.');
INSERT INTO "silika" VALUES(7,108,'7:108','Soni ni jwakopwesye ligasa lyakwe. Nalole! Lyasyuchile lyeswela (lyakumelemenda) kwa akulola.');
INSERT INTO "silika" VALUES(7,109,'7:109','Achinkulungwa ŵa mwa ŵandu ŵa Firiauna (pakwitichisyana ni Firiaunajo) ŵatite: “Chisimu jwalakweju ni nsaŵi jwakumanyilila nnope (usaŵi).”');
INSERT INTO "silika" VALUES(7,110,'7:110','“Jukusaka kuti junkopokasye m’chilambo mwenu, ana nitinnamule yantiuli?”');
INSERT INTO "silika" VALUES(7,111,'7:111','Ŵanganyao ŵatite (pakumpanga lunda Firiauna): “Munnechelesyani panandi jwalakwe ni nkulugwejo (nkaawulaga chitema), nintumisye m’misinda akusonganganya.”');
INSERT INTO "silika" VALUES(7,112,'7:112','“Kuti ayiche najo kukwenu nsaŵi jwalijose jwali jwakumanyilila nnope (usaŵi).”');
INSERT INTO "silika" VALUES(7,113,'7:113','Ni ŵam''bichilile asaŵi Firiauna. Ŵanganyao ŵatite: “Chisimu kukwetu chikuŵe kwana malipilo (gambone) natuŵaga uwwejo ni ŵakupunda.”');
INSERT INTO "silika" VALUES(7,114,'7:114','Jwalakwe jwatite: “Elo, konjechesya soni pelepo, chisimu ŵanganyammwe chim''be mwa akuŵandichisyidwa (kukwangu).”');
INSERT INTO "silika" VALUES(7,115,'7:115','Ŵanganyao ŵatite: “E mmwe Musa! Mwine chimponye kaje, kapena uwwe niŵachituŵe akuponya (mwakutandilila)?”');
INSERT INTO "silika" VALUES(7,116,'7:116','Jwalakwe jwatite: “Ponyani.” Basi ndema jaŵaponyisye ŵagalojile meso ga ŵandu ni kwataga woga, ni ŵaiche ni usaŵi wekulungwa nnope.');
INSERT INTO "silika" VALUES(7,117,'7:117','Ni twajuuwiile (maloŵe) kwa Musa ganti: “Ponyani simbo jenu.” Nalole! Jalakwe jamisile mwanakamo yaunami yaŵaliji nkulosya.');
INSERT INTO "silika" VALUES(7,118,'7:118','Basi chasimichiche chakuona ni yaweseche yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(7,119,'7:119','Basi niŵapundidwe palakwepo niŵasyuchile kuŵa ŵakunyosyeka.');
INSERT INTO "silika" VALUES(7,120,'7:120','Asaŵi ni ŵaligwisyisye pasi kusujudu.');
INSERT INTO "silika" VALUES(7,121,'7:121','Ŵatite: “Tunkulupilile M''mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(7,122,'7:122','“M''mbuje jwa Musa ni Haruna.”');
INSERT INTO "silika" VALUES(7,123,'7:123','Firiauna jwatite: “Ana munkulupilile jwalakweju une nkaninampe lusa? Chisimu aga ni malindi gantesile ŵanganyammwe mu nsindamu kuti mwakoposyemo achinsyene (nintawale jemanja), basi pangakaŵapa chiyyimanye (yachinantende).”');
INSERT INTO "silika" VALUES(7,124,'7:124','“Chisimu chingate makono genu ni ngongolo syenu mwakutindanya (ligasa lyakun''dyo ni likau lyakunchiji, kapena ligasa lyakunchiji ni likau lyakun''dyo), kaneka ni chinankomele pansalaba ŵanaose.”');
INSERT INTO "silika" VALUES(7,125,'7:125','Ŵanganyao ŵatite: “Chisimu uwwe kwa M''mbuje gwetu tukuujila.”');
INSERT INTO "silika" VALUES(7,126,'7:126','“Soni pangali ulemwa uliose wankuuona kukwetu ikaŵeje kuti tuikulupilile ilosyo ya M''mbuje gwetu ndema jaituyichilile. Ambuje ŵetu! Tupungulilani kupilila ni ntupe chiwa tuli Asilamu.”');
INSERT INTO "silika" VALUES(7,127,'7:127','Achinkulungwa ŵa mwa ŵandu ŵa Firiauna ni ŵatite (pa kunchoonga Firiauna): “Ana nitin''gambe kunnechelesya Musa ni ŵandu ŵakwe kuti awatanganyeje pa chilambo, ni antundumalile mmwe ni milungu jenu?” Jwalakwe jwatite: “Chituwulaje ŵanache ŵao ŵachilume nikwaleka umi ŵanache ŵao ŵachikongwe, soni chisimu uwwe pa ŵanganyao tukwete machili gakwakombolela.”');
INSERT INTO "silika" VALUES(7,128,'7:128','Musa jwatite kwa ŵandu ŵakwe: “Ŵendani chikamuchisyo kwa Allah soni pililani, chisimu chilambochino cha Allah chaachipeleche kwa ŵaakwasaka mwa achikapolo Ŵakwe kuŵa ŵawinjila. Soni mbesi (jambone) jili kwa ŵandu ŵawoga (wakun''jogopa Allah).”');
INSERT INTO "silika" VALUES(7,129,'7:129','Ŵanganyao ŵatite: “Tuŵele tuli nkulagasyidwa mmwe nkanintuyichilile, ni (sano jakwe) panyuma pakutuyichilila!” Jwalakwe jwatite: “Mwinetu M''mbuje gwenu kuŵa nkun''jonanga mmagongo jwenu, ni kumpisya kutama ŵanganyammwe pachilambo kuti alole mwachintendeleje (itendo yenu).”');
INSERT INTO "silika" VALUES(7,130,'7:130','Soni chisimu twakwembekenye kusyene ŵandu ŵa Firiauna ni yaka ya chilala, kwisa soni kupunguka kwa isogosi, kuti ŵanganyao aŵe ŵakumbuchila.');
INSERT INTO "silika" VALUES(7,131,'7:131','Basi (ŵaliji ŵanti) naga umbone waichililemo ŵatiji: “Yetuwe ni yeleyayi.” Nambo naga kwapata yakusakala, niŵantutilaga chitapani Musa ni ŵali najo imo (kuti ni ŵaakwatapanya). Manyililani yanti chitapani chao chili kwa Allah (ligongo lya itendo yao yangalumbana), nambo kuti ŵajinji mwa ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(7,132,'7:132','Soni ŵatite (pakunsalila Musa): “Chilosyo chilichose chachim''biche nacho kukwetu kuti ntulojele nacho (chin''gambe kulilagasya), uwwetu nganituŵa tunkulupilile mmwe.”');
INSERT INTO "silika" VALUES(7,133,'7:133','Basi ni twatumichisye ŵanganyao chigumula, sombe, njipi, mola kwisa soni myasi (kuŵa malundelunde ga) ilosyo yakuonechela, basi ni ŵalikwesyisye ni ŵaliji soni ŵandu ŵakuleŵa.');
INSERT INTO "silika" VALUES(7,134,'7:134','Soni pandema jayaliji nkwagwila ilagasyo ŵatiji: “E mmwe Musa! Tuŵendelani kwa Ambuje ŵenu ligongo lya chilanga Chakwe chaŵataŵene namwe (chanti chatutyochesye ilagasyo naga tukulupilile), chisimu naga nkututyochesya ilagasyo, chitunkulupilile kwene soni chitwaleche kwene kuŵigula nomwe ŵanache ŵa Israila.”');
INSERT INTO "silika" VALUES(7,135,'7:135','Nambo ndema jatwaliji nkwatyochesya ilagasyoyo kwakwikanila ndema jaŵaliji nkuŵajilwa kujiyika, papopo ŵanganyao ŵaliji nkukasa chilanga mwanakamo.');
INSERT INTO "silika" VALUES(7,136,'7:136','Basi ni twauchisye mbusyo ŵanganyao (syakupoteka nnope), mwanti twamisyisye m''mbwani ligongo lyanti ŵanganyao ŵakaaniile ilosyo Yetu, niŵaliji soni ali nkuikwanjila.');
INSERT INTO "silika" VALUES(7,137,'7:137','Ni twatesile ŵandu aŵala ŵaŵaliji nkuonedwa mpela ŵangalikombolela (ligongo lya ukapolo ŵali ŵanache ŵa Israila) kuŵa ŵawinjila kungopoko kwa chilambo (cha Shami) ni kungapililo kwakwe akula kwatwatasileko upile, ni lyakalulile liloŵe lya Ambuje ŵenu lyalili lyambone kwa ŵanache ŵa Israila ligongo lya kupilila kwao, soni ni twagajonasile masengo gaŵaliji nkupanganya Firiauna ni ŵandu ŵakwe (mpela ga unamalima) kwisa soni majumba gaŵaliji nkwimika njo!');
INSERT INTO "silika" VALUES(7,138,'7:138','Ni twajombwesye ŵanache ŵa Israila mbwani, basi ni ŵasimene ŵandu ali nkubindichila ku isanamu yao (pa kuigalagatila). Ŵanganyao ŵatite: “E mmwe Musa! Tutendelani nnungu jwetu yatite pakola ŵanganyao milungu jao.” Jwalakwe jwatite: “Chisimu ŵanganyammwe ni ŵandu ajinga.”');
INSERT INTO "silika" VALUES(7,139,'7:139','“Chisimu ŵanganyaŵa tachiŵa ŵakonanjidwa pa yalitomecheyi, soni ni iŵele yantega yaŵele ali nkupanganya.”');
INSERT INTO "silika" VALUES(7,140,'7:140','Jwalakwe jwatite: “Ana jwangaŵaga Allah nansosele jenumanja kuŵa nnungu, kutendaga Jwalakwe anchimbichisye kupunda iwumbe yosope (yandaŵi jenu)?”');
INSERT INTO "silika" VALUES(7,141,'7:141','Soni (kumbuchilani) katema katwan''jokwele ku ŵandu ŵa Firiauna ŵaŵaliji nkumpa ilagasyo yakusakala nnope, ŵaliji nkuwulaga ŵanache ŵenu ŵachilume ni kwalekaga umi ŵanache ŵenu ŵachikongwe, sano mu yalakweyo mwaliji mwana kulinjidwa kwekulungwa nnope kwakutyochela kwa M''mbuje gwenu.');
INSERT INTO "silika" VALUES(7,142,'7:142','Ni twampele chilanga Musa cha ilo makumi gatatu (kuti atende ibada kaneko tumpe Taurat), ni twaikwanilisye ni (ilo ine) likumi, ni jakwanile ndema jachilanga cha Ambujegwe (chakuja kumpa Tauratijo) mu ilo makumi ncheche. Musa ni jwatite kwa nchimwenegwe Haruna: “Tamilani chitengu changu pa (kwalongolela) ŵandu ŵangu soni kolosyani, ni ngasinkuya litala lya akuwatanganya.”');
INSERT INTO "silika" VALUES(7,143,'7:143','Sano ndema ja jwaiche Musa pamalo gachilanga Chetu, Ambujegwe nikuŵecheta najo, jwatite: “Ambuje ŵangu! Lilosyani kukwangu kuti nam''bone.” Jwalakwe jwatite: “Nganim''ba mumbweni, nambo lilolani litumbi, naga litamilichiche pamalo pakwe chinkomboleche kumbona.” Basi ndema ja ŵalionesye Ambujegwe kwitumbi ŵalitesile kuŵa lyakusyajika, sano Musa ni jwagwile kwakomoka. Basi ndema ja jwasisimwiche jwatite: “Kuswejela ni Kwenu! Ndesile toba kukwenu, soni une ni jwandanda mwa akukulupilila.”');
INSERT INTO "silika" VALUES(7,144,'7:144','(Allah) jwatite: “E mmwe Musa! Chisimu Une nansagwile mmwejo mwa ŵandu (wosope), kunsagulila utenga Wangu ni kuŵecheta Kwangu (nomwe), basi pochelani yanampeleyi ni m''be mu ŵakutogolela.”');
INSERT INTO "silika" VALUES(7,145,'7:145','Ni twannembele jwalakwejo m’maubawu kuumila mu chindu chilichose chiigaanyo, ni kusalichisya kwa chindu chilichose, (ni kuntenda kuti): “Basi gakamulisyani mwamachili, ni mwalamuleje ŵandu ŵenu kuti agakamulisyeje mwaumbone wakwe wannope. Pangakaŵapa chinannosye misi ja ŵakunyosya.”');
INSERT INTO "silika" VALUES(7,146,'7:146','Chinaŵambasye ku ma Ȃya Gangu aŵala ŵaakulikwesya pa chilambo mwangali ligongo lyakuona, mwanti naga ali achiweni chilosyo chilichose ngakasachikulupililaga, soni naga ali aliweni litala lyachongoko ngakulitenda kuŵa litala, nambo naga aliweni litala lyakusokonechela akulitendaga kuŵa litala, yalakweyo ni ligongo lyanti ŵanganyao ŵakaaniile ilosyo Yetu, niŵaliji soni ali nkuikwanjila.');
INSERT INTO "silika" VALUES(7,147,'7:147','Sano aŵala ŵaakaaniile ma Ȃya Getu ni kusimangana kwa ku Akhera, gajonasiche masengo gao. Ana chachilipidwa (inepe) yangaŵaga yaŵaliji nkupanganya?');
INSERT INTO "silika" VALUES(7,148,'7:148','Ni ŵaalitendele ŵandu ŵa Musa panyuma pakwe (jwele ali apite kwitumbi kuupochela Taurat) kuumila mu ipeto yao (ya golodi ni silifa) kang''ombe (kuŵa kannungu kao), kakaakwete chiŵiliŵili chachakoposyaga liloŵe (mpela chawumi), ana nganayionaga ŵanganyao yanti kalakweko nganikaŵechetekasyaga soni nganikaajoongolelaga kwitala (lililyose)? Ŵalitendele kalakweko (kuŵa kannungu kao) ni ŵaliji ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(7,149,'7:149','Sano ndema ja ŵaaligambile ni kuyiona yanti ŵanganyao pamasile paasokonechele ŵatite: “Naga ngaakututendela chanasa Ambuje ŵetu, ni kutukululuchila chisimu chene chituŵe mu ŵakwasika.”');
INSERT INTO "silika" VALUES(7,150,'7:150','Sano ndema ja jwaujile Musa ku ŵandu ŵakwe juli jugasabiche ni kupwelembwesuka, jwatite: “Kaje kusakala yamwasigele nchitendaga panyuma pangu! Ana nkulumisye chilamusi cha M’mbuje gwenu (pakulitendela yenuyenupe nkaniyyimanye yatannamule)?” Ni jwaponyisye (pasi) maubawu, kaneka nijwakamwile ntwe wa nchimwenegwe ni kuuwutila kukwakwe. (Haruna) jwatite: “Mwanache jwa mama! Chisimu ŵandu ŵambweni ungali machili, mwanti ŵasigele panandi kuti ambulaje, basi nkaasengwachisya amagongo pa une (pakumba ipotesi), soni nkambika pamo ni ŵandu ŵakulitenda lupuso.”');
INSERT INTO "silika" VALUES(7,151,'7:151','(Musa) jwatite: “Ambuje! Mungululuchilani une ni achimwene ŵangu, ni ntujinjisye mu chanasa Chenu, soni Mmwe ni Ŵachanasa channope kwapunda ŵachanasa wosope.”');
INSERT INTO "silika" VALUES(7,152,'7:152','Chisimu aŵala ŵalitendele kang''ombe (kuŵa kannungu kao), chijapate gasabu jakuumila kwa M’mbuje gwao kwisa soni kunyosyeka pa umi wa duniya, soni iyyoyopeyo niyatukutiji pakwalipila ŵakusepelela (unami mu Dini).');
INSERT INTO "silika" VALUES(7,153,'7:153','Soni aŵala ŵatesile yakusakala kaneka ni kutenda toba panyuma pakwe ni kulupilila, chisimu Ambuje ŵenu panyuma pakwe aŵele Ŵakululuka kusyene, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(7,154,'7:154','Sano ndema ja jatulele kwa Musa gasabu, jwagalokwete maubawu gala, soni m’malemba mwakwe mwaliji mwana chongolo kwisa soni chanasa kwa aŵala ŵali ni woga wakun''jogopa Mmbuje gwao.');
INSERT INTO "silika" VALUES(7,155,'7:155','Ni jwasagwile Musa mu ŵandu ŵakwe achalume makumi nsano na gaŵili (70 ŵaŵaliji ŵambone) kuti ajaule kumalo gachilanga Chetu (chatwapangene najo Musajo kuti akaiche nao kwitumbi kuti akaaŵendele chikululuko achimijao ŵana galagatilaga kang''ombe ŵala), basi ndema jachakwembekenye chisikinya chamachili (Musa) jwatite: “Ambuje! Nkasache nkaajonasile ŵanganyaŵa nonejwakwe kalape (pameso pa achimijao wosope kuti amanyilile yanti ngaŵa ndekwaulaga une). Ana nkutujonanga pa yatesile ŵawawate mwetuwe? Nganijiŵatu jalakweji ikaŵeje fitina Jenu jankwalechelela najo kusokonechela ŵankwasaka nikwajongola ŵankwasaka, Mmwe ni Nkutugosa jwetu, basi tukululuchilani uwwe ni kututendela chanasa, soni Mmwe ni Ŵambone nnope mwa akukululuka wosope.”');
INSERT INTO "silika" VALUES(7,156,'7:156','“Soni tulembelani yambone pa duniya pano ni ku Akhera kwakwe, chisimu uwwe tuwujiile Kukwenu.” (Allah) jwatite: “Ilagasyo Yangu chinansausye nayo jwangunsaka (mwa ŵandu ŵakusakala), sano chanasa Changu ni chin''jeeneele chindu chilichose (pa duniya), nambo (ku Akhera) chinjalembela chalakwe (chanasacho) kuti chikaapate aŵala ŵali ni woga (wakun''jogopa Allah) ni akutolaga Zaka, kwisa soni aŵala ŵaakuŵa ali nkugakulupilila ma Ȃya Getu.”');
INSERT INTO "silika" VALUES(7,157,'7:157','“Aŵala ŵaakunkuya Ntenga, Ntume jwangapakombola kulemba ni kuŵalanga, ajula jwaakunsimana juli julembedwe kukwao mu Taurat kwisa soni mu Injil, akwalamulaga kutenda yaukoto ni kwalekasyaga yangalumbana, ni akwakundisyaga yambone ni kwakanyaga yausakwa, ni akwatula misigo jao ni makongwa gagaliji pa ŵanganyao (malamusi gakusausya kugakuya), basi aŵala ŵankulupilile jwalakwe (Muhammadi صلى الله عليه وسلم) ni kunchimbichisya ni kunkamuchisya ni kuya lilanguka (Qur’an) lyalyatulusyidwe imo ni jwalakwe, ŵanganyao niŵali ŵakupunda. ”');
INSERT INTO "silika" VALUES(7,158,'7:158','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “E jenumanja ŵandu! Chisimu une ni Ntenga jwa Allah kukwenu ŵanaose, Ajula jwauli Wakwe uchimwene wa kumawunde ni petaka, pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, akupelekaga umi ni chiwa, basi munkulupililani Allah ni Ntenga Jwakwe, Ntume jwangapakombola kulemba ni kuŵalanga, ajula jwaakunkulupilila Allah ni maloŵe Gakwe, ni munkuyani kuti n''jongoche.”');
INSERT INTO "silika" VALUES(7,159,'7:159','Soni mu ŵandu ŵa Musa mwana likuga lyalikwaajongolela (achimijao) ku yakuona (Usilamu), soni ni yakuonayo akutendaga chilungamiko.');
INSERT INTO "silika" VALUES(7,160,'7:160','Ni twaagaŵenye ŵanganyao ngosyo likumi kwisa siŵili kuŵa makuga (gekulungwakulungwa), ni twampele maloŵe Musa ndema jaŵam''bendile ŵandu ŵakwe mesi, kuti: “Putasyani simbo jenujo peganga.” Basi nisyaumile mwalakwemo sulo likumi kwisa siŵili, mwanti lukosyo lulilose lwamanyilile pakumwela pakwe. Soni twaaunichile ni m''bulili wa liunde, ni kwatuluchisya manna ni salwa, (nikwatenda kuti): “Lyagani mu yambone yatumpeleyi.” Ni nganatutenda lupuso Uwwe, nambo achimisyene ni ŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(7,161,'7:161','Soni (mwakumbusyani) katema kayaŵechetedwe kukwao yanti: “Tamani m''musi awu (wa Baitul Muqaddas), ni n’dyeje mwalakwemu palipose pansachile, ni m''becheteje kuti: ‘Tukululuchilani ileŵi yetu.’ Soni jinjililani pannango chiinamile. Chitunkululuchile ileŵi yenu, chitwajonjechesye (yambone) ŵakolosya.”');
INSERT INTO "silika" VALUES(7,162,'7:162','Nambo ŵaŵalitesile lupuso mwa ŵanganyao ŵagalawisye (lilamusi lila mwanti ŵaŵechete) maloŵe gane gangaŵaga gaŵasalilidwe gala, sano ni twatumisye pa ŵanganyao ipotesi kutyochela kwinani ligongo lyakulitenda kwao lupuso.');
INSERT INTO "silika" VALUES(7,163,'7:163','Soni mwawusyani ŵanganyao ya musi (wa Ayilah) wawaliji mungulugulu mbwani, ndema jaŵaliji (achinsyene musio) nkuguluka mpingu wa (lisiku) Lyakuŵeluka (wangaja kuwulaga somba nambo kutenda ibadape), pandema jasyaichililaga somba syao mwakwela lisiku lyakuŵeluka lyao, nambo lisiku lyangaŵa lyakuŵeluka lyao nganisyaichililaga, yele niyatwatite pakwalinga ligongo lya kunyosya kwao.');
INSERT INTO "silika" VALUES(7,164,'7:164','Soni (mwakumbusyani) katema kalyaŵechete likuga line mwa ŵanganyao (pakwawusya ŵandu ŵaŵaliji nkwajamuka aŵala ŵana gulukaga mpingu wa lisiku lyakuŵeluka): “Nkulilagachisya chichi kwajamuka ŵandu ŵanti Allah asigele panandi kwajonanga kapena kwapa ilagasyo yakupoteka nnope?” Ŵanganyao ŵatite: “Tupate pakuliŵalanjila kwa M''mbuje gwenu (kuti twajamukaga nambo nganatupikanilaga), ni kuti mwine ŵanganyao chaŵe ni woga (wakun''jogopa Allah).”');
INSERT INTO "silika" VALUES(7,165,'7:165','Sano katema kaŵaapwaanyiile ayila yaŵaliji nkukumbusyidwa, twajokwele aŵala ŵaŵakanyaga yakusakala, ni twakwembekenye aŵala ŵaŵalitendaga lupuso ni ilagasyo yakusakala nnope ligongo lya kunyosya kwao.');
INSERT INTO "silika" VALUES(7,166,'7:166','Basi ndema ja ŵalikwesyisye pangaleka yaŵakanyisyidweyo, twasalile kuti: “Syukani majani gakunyosyeka.”');
INSERT INTO "silika" VALUES(7,167,'7:167','Soni (mwakumbusyani) ndema ja ŵagombelesye Ambuje ŵenu yanti; chisimu chene taŵeje ali nkwatumichisya (Ayudao) mpaka lisiku lya Kiyama ŵachaŵeje ali nkwapa ilagasyo yakusakala, chisimu Ambuje ŵenu ni Ŵachitema pakupeleka ipotesi, soni chisimu Ŵalakweo ni Ŵakululuka kusyene Ŵachanasa channope.');
INSERT INTO "silika" VALUES(7,168,'7:168','Ni twalekangenye (Ayudao) pa chilambo kuŵa mikutula-mikutula, ŵane mwa ŵanganyao ŵambone, nambo ŵane nganaŵa ŵanti iyyo, soni ni twalinjile ni yambone kwisa soni yakusakala kuti awujile (kwa Allah).');
INSERT INTO "silika" VALUES(7,169,'7:169','Basi ni ŵalundite panyuma pao akulunda (ŵakusakala) ŵaŵachitawele chitabu (cha Taurat nambo mwangali kuchikuuya), ŵaliji nkulisagulila ichindu (yakusakala) ya auno (umi) wapasi, ni kuŵechetaga (mwakuliwimba): “Ichikululuchidwa kukwetu (kuŵa mpela akutenda toba).” Nambo soni yeti naga kwaichilila ichindu ine (yakusakala) yanti chisawu iyyoyo ŵaigundumilaga pakuijigala (kutendaga ali ŵatende toba). Ana nganichijigalidwa chilanga kukwao cha m''Chitabu chanti ngasam''bechetela Allah ikaŵeje yakuonaonape? Kutendaga ŵanganyao asoomile yaili mwalakwemo? Sanotu Nyumba Jambesi jichiŵa jambone nnope kwa ŵandu ŵali ni woga (wakun''jogopa Allah), ana jenumanja ŵangali lunda?');
INSERT INTO "silika" VALUES(7,170,'7:170','Sano aŵala ŵaakukamulisya chitabu (pa kuikuya yaili mwalakwemo), ni kwimikaga Swala mwakolosya (tuchalipila yambone), chisimu Uwwe ŵangajonanga malipilo ga akwilanya.');
INSERT INTO "silika" VALUES(7,171,'7:171','Soni (mwakumbusyani) katema katwalinyakwile litumbi pachanya pao ni kuŵa mpela nsakasa, ni ŵasimichisye yanti chilyagwile, (twaasalile yanti): “Kamulisyani yatumpeleyi (Taurat) mwamachili, soni kumbuchilani (malamusi) gagali mwalakwemo kuti jenumanja m''be ni woga (wakun''jogopa Allah).”');
INSERT INTO "silika" VALUES(7,172,'7:172','Soni (kumbuchilani) katema kaŵakopwesye Ambuje ŵenu mu ŵanache ŵa Adam kuumila m’migongo jao ŵanache ŵao (ŵane kuumila m’migongo ja ŵane muchachiŵelekanilana, soni wosope nikwakoposya mun''gongo wa Adam), ni kwatendekasya kuŵa mboni syakulitendela achimisyene (yakunda Tauhidi, pakwatenda kuti): “Ana nganimba Nam''mbuje gwenu?” Ŵanganyao ŵatite: “Elo! Tuteendeele umboni (kuti Mmwejo ni Ambuje ŵetu).” Ntaakwiisa kuŵecheta lisiku lya Kiyama yanti: “Chisimu uwwe pangali chatwachimaanyi chakwayana ni yele (ya Tauhidiyi).”');
INSERT INTO "silika" VALUES(7,173,'7:173','Kapena ntaakwiisa kuŵecheta kuti: “Chisimu achatati ŵetu ni ŵaŵawanganyisye (Allah ni isanamu) kala, sano uwwe twagambile kuŵa ŵanache (ŵatwaiche) panyuma pao (ni kwaakuuyaaga), ana ni chintujonanjeje ligongo lya yaŵatesile ŵakuya yaunami?”');
INSERT INTO "silika" VALUES(7,174,'7:174','Soni iyyoyopeyo ni yatukuti pagopolanya ma Ȃya, soni kuti ŵanganyao awujile (kwa Allah).');
INSERT INTO "silika" VALUES(7,175,'7:175','Mwaasoomelaani ŵanganyao abali ja ajula jwatwampele ma Ȃya Getu, basi jwele nikuliiwuulaamo, sano shetani niŵankuyiye (nikuŵa najo), basi ni jwaliji mu ŵakusokonechela.');
INSERT INTO "silika" VALUES(7,176,'7:176','Soni tungasache tunganyyakwile (pa uchimbichimbi) ni gele (ma Ȃyago), nambo kuti jwalakwe jwapendechele ku duniya ni kuya isako yakwe, basi chilandanyo chakwe chili chisawu mbwa janti nkujikalipilamo jikasapumasika (pakutiila) mwakoposya lulimi, natamuno kujileka (ngajikalipila) jikasapumasikape mwakoposya lulimi, chele ni chilandanyo cha ŵandu aŵala ŵaakaaniile ma Ȃya Getu, basi siimbaani ngani kuti ŵanganyao achetelele.');
INSERT INTO "silika" VALUES(7,177,'7:177','Kaje kusakala chilandanyo cha ŵandu aŵala ŵaakaaniile ma Ȃya Getu! Soni achimisyene niŵaŵele ali nkulitenda lupuso.');
INSERT INTO "silika" VALUES(7,178,'7:178','Mundu jwan''jongwele Allah, basi nijwali jwakongoka, nambo ŵaŵalechelele kusokonechela, basi ŵanganyao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(7,179,'7:179','Soni chisimu chene twajiguumbiile Jahannama majini gamajinji kwisa soni ŵandu (ŵatakajinjileko), ŵanganyao akwete mitima nambo ŵangamanyilila najo, meso soni akwete nambo ŵangalolela nago, ni mawiwi gakwe akwete nambo ŵangapikanila nago, ŵanganyao ali chisawu inyama, gambakuti ŵanganyao ni ŵali ŵakusokonechela nnope (kumpunda inyama), ŵanganyao ni ŵali ŵaakwaanjila (malamusi ga Allah).');
INSERT INTO "silika" VALUES(7,180,'7:180','Soni Allah jukwete mena gambone nnope, basi mum’bombani nago, ni mwaleche aŵala ŵaakugapombotasya mena Gakwe, chachilipidwa pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(7,181,'7:181','Soni mu ŵandu ŵatwagumbile, mwana likuga lyalikwaajongolela (achimijao) ku yakuona, soni ni yakuonayo akutendaga chilungamiko.');
INSERT INTO "silika" VALUES(7,182,'7:182','Soni aŵala ŵaakaaniile ma Ȃya Getu, chitwajigale mbolembole wa ku ipotesi kupitila m’matala gangaakugamanyilila.');
INSERT INTO "silika" VALUES(7,183,'7:183','Soni ngamba kwalechelela ŵanganyao, chisimu mpango Wangu uŵele wakulimbangana.');
INSERT INTO "silika" VALUES(7,184,'7:184','Ana ngaakuchetelela? Mwa n''jaomotu mwangali masoka, nganaŵa jwalakwe (Muhammadi صلى الله عليه وسلم) ikaŵeje nkutetela jwakuonechela.');
INSERT INTO "silika" VALUES(7,185,'7:185','Ana ngaakuuona uchimwene wa kumawunde ni petaka, ni ichindu yosope yaagumbile Allah, soni yanti mwine komboleka kuŵa ndaŵi jao jakuwila jiŵandichile? Ana ni abalichi panyuma paaji (Quraniji) jatachijikulupilila?');
INSERT INTO "silika" VALUES(7,186,'7:186','Mundu jwannechelele Allah kusokonechela, basi nganakola nkun''jongola, ni akwalekaga nkusokonechela mwaomo achiyumbagayumbaga.');
INSERT INTO "silika" VALUES(7,187,'7:187','Akum''buusya (mmwe Muhammadi صلى الله عليه وسلم) ya ndema (jakwichila Kiyama kuti): “Jichiŵa chakachi?” Jilani: “Chisimu umanyilisi wakwe uli kwa Ambuje ŵangu pe. Pangali ŵampaka ajionesye papaswela ndaŵi jakwe ikaŵeje Jwalakwe. Jisitwepe kumawunde ni petaka. Ngasijim''bichilila ikaŵeje mwachisupuchisya.” Akum''buusya nti chisawu mmwejo m''bele ŵakuwuchilichisya ya jalakwejo (ni mpaka n''jimaanyi ndaŵi jakwe). Jilani: “Chisimu umanyilisi wakwe uli kwa Allah, nambo ŵandu ŵajinji ngaakumanyilila.” ');
INSERT INTO "silika" VALUES(7,188,'7:188','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Jwangali machili gakulikamuchisya nansyene kapena kulipa isausyo ikaŵeje yasachile Allah, soni ngaaŵe nguimanyilila yakusisika ngalichuluichisye yambone, soni nga nganiyimbata yakusakala. Nganimbatu une ikaŵeje nkwaatetela ni kwatagulila abali jakusengwasya ŵandu ŵaakukulupilila.”');
INSERT INTO "silika" VALUES(7,189,'7:189','Jwalakwe ni Ajula jwaŵan''gumbile ŵanganyammwe kuumila mwa mundu jumpepe (Adam), nijwaaŵisile kuumila mwa jwalakwejo ŵankwakwe (Hawa), kuti jutulalileje (jwannume jwalijose) kwa ŵele (ŵankwakweo). Sano ndema jaakwaakumbatilaga, akasajigalaga (ŵammasyetowo) nsigo wakwepepala, ni akasajendagajendaga nao, nambo pati upesile (nsigowo nikusigala panandi kuŵeleka) ŵanaŵaŵili (ŵalume ni ŵankwao) akasam’bombaga Allah M''mbuje gwao (kuti): “Naga nkutupa mwanache jwambone (jwalungalunga) chisimu chituŵe mu ŵakutogolela.”');
INSERT INTO "silika" VALUES(7,190,'7:190','Basi ndema jaakwaapaaga mwanache jwambone (jwalungalunga) akasam''biichilaga Jwalakwe (Allah) ŵakamusyangana najo pa yajwapele, basi Allah jutalikangene kusyene ni yaakum''bwanganya nayo.');
INSERT INTO "silika" VALUES(7,191,'7:191','Ana akuiwanganya (ni Allah) ichindu yangaŵa nkugumba chilichose kutendaga yalakweyo ni yayigumbidwa?');
INSERT INTO "silika" VALUES(7,192,'7:192','Soni nganiyiŵa ipakombwele kwaakamuchisya ŵanganyao namuno kulikamuchisya isyeneyo.');
INSERT INTO "silika" VALUES(7,193,'7:193','Soni naga n''di iyyiŵilaanjiile ku chongoko nganiiŵa inkuyiye (ngati yangali umanyilisi), yalumope kwa jemanja nkuiŵilanga kapena gamba kuŵa jii.');
INSERT INTO "silika" VALUES(7,194,'7:194','Chisimu ayila yankuilumbanjila kunneka Allah iŵele achikapolo mpela ŵanganyapemmwe, basi ilumbanjilaani soni ni in''janje (pakumpa yankusakayo) nanga nkuŵecheta yakuona (yakuti jelejo milungu jisyesyene).');
INSERT INTO "silika" VALUES(7,195,'7:195','Ana ikwete yalakweyo ngongolo syaikwendela? Kapena ikwete makono gaikamulila? Kapena ikwete meso gaikulolela? Kapena ikwete mawiwi gaikupikanila? Jilani: “Mwaŵilangani akwawanganya ŵenu (ni Allah), kaneko ni mundendele malindi mwanti ngasimumba lipesa.”');
INSERT INTO "silika" VALUES(7,196,'7:196','“Chisimu kaligoselo kangu ni Allah Ajula jwaatulwisye Chitabu (Qur’an), soni Jwalakwe akwagosaga akutenda yambone.”');
INSERT INTO "silika" VALUES(7,197,'7:197','“Sano ayila yankuilumbanjila kunneka Jwalakwe nganiyiŵa ipakombwele kunkamuchisyaŵanganyammwe namuno kulikamuchisya isyeneyo.”');
INSERT INTO "silika" VALUES(7,198,'7:198','Soni naga n''di iyyiŵilaanjiile ku chongoko nganiiŵa ipikene, soni chiyyione ili tananaa kukwenu kutendaga ili ngaikulola.');
INSERT INTO "silika" VALUES(7,199,'7:199','Kamulisyani chikululuko, soni lamulani yambone, ni mwatundumalile ajinga.');
INSERT INTO "silika" VALUES(7,200,'7:200','Soni naga gali gam''bichilile masongono gakusakala kuumila ku shetani, basi lijuyani mwa Allah, chisimu Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(7,201,'7:201','Chisimu aŵala ŵali ni woga (wakun''jogopa Allah) pati syakwaiye nganisyo syakusakala kuumila ku shetani, akasankumbuchilaga (Allah), basi ndaŵi jijojo ni akasasisinukukaga.');
INSERT INTO "silika" VALUES(7,202,'7:202','Nambo achambusanga ŵao (makafili gagali mashetani gachijini) akwalimbikasyaga (achimakafilio) m’kusokonechela (ni kwajonjechesya ya sambi), soni ŵangapumulila.');
INSERT INTO "silika" VALUES(7,203,'7:203','Soni naga nganimwaichilila (achimakafilio) ni chisimosimo akasatiji: “Uli ngalipanganyichisya chalakwe?” Jilani: “Chisimu une ngukuya yaikuuwulidwa kukwangu kuumila kwa Ambuje ŵangu, aji (Qur’aniji) jiŵele muli syakuumila kwa M’mbuje gwenu (Allah), soni chongolo ni chanasa kwa ŵandu ŵaakukulupilila.”');
INSERT INTO "silika" VALUES(7,204,'7:204','Soni pati jichisoomedwaaga Qur’an, basi jipikanilani ni kujila jii! Kuti ntendeledwe chanasa.');
INSERT INTO "silika" VALUES(7,205,'7:205','Soni mwakolanjeje Ambuje ŵenu (Allah) mu ntima mwenu, mwakulinandiya, mwa woga ni mwangakwesya maloŵe, kundaŵi ni kwigulo, soni ngasim''ba mu ŵaakwaanjila. ');
INSERT INTO "silika" VALUES(7,206,'7:206','Chisimu aŵala (Achimalaika) ŵali kwa Ambuje ŵenu ŵangalikwesya pakuleka kun''galagatila Jwalakwe, ni akunswejesyaga, soni akunsujudilaga Jwalakwe.');
INSERT INTO "silika" VALUES(8,1,'8:1','Akum''buusya ya chipanje chakupalagula (pa ngondo ja Badri kuti chigaŵidwe uli)? Jilani: “Chipanje chakupalagula chiŵele cha Allah ni Ntenga صلى الله عليه وسلم (ni ŵachagaŵanye). Basi mun''jogopani Allah ni kolosya yaili chilikati chenu (pakumasya utinda). Ni mumpikanilani Allah ni Ntenga Jwakwe nam''baga jenumanja n’di ŵakulupilila (kusyene).” ');
INSERT INTO "silika" VALUES(8,2,'8:2','Chisimu ŵakulupilila (kusyene) ni aŵala ŵaakutiji naga Allah achikolanjidwaga mitima jao jikasan''gumbalaga woga, soni pati achisoomeledwaga ma Ȃya Gakwe gakwajonjechesyaga chikulupi, soni kwa M''mbuje gwao pe ni kwaakwegamilaga.');
INSERT INTO "silika" VALUES(8,3,'8:3','Aŵala ŵaakwimikaga Swala mwakolosya, soni mu ichindu yatwapele akasatolaga.');
INSERT INTO "silika" VALUES(8,4,'8:4','Ŵanganyao niŵali ŵakulupilila kusyesyene. Akwete kupata ŵanganyao uchimbichimbi wakulekanganalekangana kwa M''mbuje gwao, ni chikululuko kwisa soni lisiki lyakuchimbichika (lya ku Mbepo).');
INSERT INTO "silika" VALUES(8,5,'8:5','(Kagaŵanye ka chipanje chakupalagula kaachimile ŵane mu ŵakulupilila pangaumanya ukoto wakwe), mpela yaŵatite pakunkoposya Ambuje ŵenu pannango penu (kuja kungondo ja Badri) mwakuonaonape, mwanti chisimu likuga line mu ŵaakulupilila yachimile (pangaumanya ukoto wangondojo).');
INSERT INTO "silika" VALUES(8,6,'8:6','Akukanilana namwe pa yakuona (gagali magongo gangondojo) panyuma pakuonechela papaswela, (kajichime kakwe ngondojo) mpela atenda kuchinjilidwa kuchiwa aku ali nkulola.');
INSERT INTO "silika" VALUES(8,7,'8:7','Soni (kumbuchilani) katema kaŵampaga chilanga Allah cha umo mwa mikutula jiŵili kuti uŵe wenu (mmenyane nao soni chiwwukombole, wauli nkutula wa asilikali), sano jemanja ni kusachililaga yanti (nkutula) wangali machili (wauli wamalonda) uŵe wenu (wachimmenyane nao), kutendaga Allah akusaka kuchisimika chakuona ni maloŵe Gakwe, ni kata njiga sya achimakafili.');
INSERT INTO "silika" VALUES(8,8,'8:8','Kuti achisimiche chakuona ni kuiwesesya yaukoleko, namuno yachime akuleŵa.');
INSERT INTO "silika" VALUES(8,9,'8:9','(Kumbuchilani) katema kamwam''bendaga M''buje gwenu chikamuchisyo, basi niŵan''janjile yanti: “Chisimu Une tinankamuchisye ni sausande jimo (1000) ja achimalaika ŵakutuluka mwakuyana.”');
INSERT INTO "silika" VALUES(8,10,'8:10','Ni nganaitenda yalakweyi Allah ikaŵeje kuti iŵe abali jakusengwasya, ni kuti soni jipate kutulala ni yalakweyo mitima jenu, sano chikulupusyo nganichiŵa chityochele kwinepe ikaŵeje kwa Allah, chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(8,11,'8:11','(Kumbuchilani) katema kaŵankupwile (Allah) ni lugono kuŵa ntunjelele wakuuma Kukwakwe, ni kuntuluchisya ŵanganyammwe kuumila kwinani mesi (ga wula) kuti answejesye nago ni kuntyochesya masongono ga shetani, nikuti amulimbanganye m’mitima mwenu (kuti mukaŵa ni woga), ni kulimbanganya nago makau (kuti gakawowochela munsanga).');
INSERT INTO "silika" VALUES(8,12,'8:12','(Kumbuchilani) katema kaŵajuwile Ambuje ŵenu kwa Achimalaika yanti: “Chisimu Une ndinamwe, basi mwalimbanganyani aŵala ŵaakulupilile. Chindaje woga m’mitima ja aŵala ŵaakanile, basi kunulaani pachanya pa ngosi, ni nkateje mwa ŵanganyao yala yosope (yammagasa ni mmakau).');
INSERT INTO "silika" VALUES(8,13,'8:13','Yalakweyo paligongo lyanti ŵanganyao ŵatindene najo Allah ni Ntenga Jwakwe. Sano jwatatindane najo Allah ni Ntenga Jwakwe, basi (ni yakwe), chisimu Allah nijwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(8,14,'8:14','Yalakweyi (ni ilagasyo), basi ipasyani (jenumanja achimakafili pa duniya pano), soni chisimu akwete kupata achimakafili ilagasyo ya Moto (ku Akhera).');
INSERT INTO "silika" VALUES(8,15,'8:15','E junumanja ŵankulupilile! Pati nsimangene nao mwakuŵandikana aŵala ŵakanile (pa ngondo), basi ngasimwalochesya migongo (kutila).');
INSERT INTO "silika" VALUES(8,16,'8:16','Sano jwataiche mwakwalochesya lyele lisikulyo n''gongo wakwe (kutila) - pangaŵaga galauka kwa itagu ya ngondo, kapena kusaka kuliwanga mu nkutula wine (wa Asilamu peo) - nikuti pamasile paaŵajilwe kupata gasabu jakuumila kwa Allah, soni liwuto lyakwe ni ku Jahannama. Kaje kusakala malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(8,17,'8:17','Mwanti jenumanja nganimwaulaga ŵanganyao nambo Allah ni jwaŵauleje, soni mmwe (Muhammadi صلى الله عليه وسلم) nganimwasoma ŵanganyao ndema jamwaamiisiile (nsanga), nambo Allah ni jwaŵasomile (pakwalonda nao mmeso), ni kuti ŵalinje ŵakulupilila kuumila Kukwakwe mayeso gambone. Chisimu Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(8,18,'8:18','Yeleyi (ni yakuona yakwe), soni chisimu Allah ni Nkugatondoya malindi ga achimakafili.');
INSERT INTO "silika" VALUES(8,19,'8:19','Naga mwaŵendaga chilamusi (jenumanja achimakafili chakupunda pangondo), basi pamasile pachim''bichilile chilamusi (chakumpunda Asilamu), sano naga nkuleka (kwatanda Asilamu), basi chiiŵe yambone kukwenu, nambo naga nkuujauja (kwatanda), chituujauje (kwakamuchisya), soni likuga lyenu ngalinkamuchisya chilichose natamuno litupe chantiuli, soni chisimu Allah ali pamo ni ŵakulupilila.');
INSERT INTO "silika" VALUES(8,20,'8:20','E jenumanja ŵankulupilile! Mumpikanilani Allah ni Ntenga Jwakwe, ni ngasimuntundumalila kutendaga jenumanja nchipikanaga.');
INSERT INTO "silika" VALUES(8,21,'8:21','Soni kasim''ba nti chisawu aŵala (achinakunaku) ŵakutiji: “Tupikene.” Kutendaga akakupikana (kwakunda).');
INSERT INTO "silika" VALUES(8,22,'8:22','Chisimu inyama yakusakala nnope pameso pa Allah ni asiiwa (ŵangapikana yakuona) kwisa soni abubu (ŵangajitichisya yakuona) ŵali ŵangali lunda.');
INSERT INTO "silika" VALUES(8,23,'8:23','Soni Allah angaumanye umbone uliose mwa ŵanganyao chisimu angaapiikaniisye, soni natamuno angaapiikanisye akatundumele aku achisyululaga.');
INSERT INTO "silika" VALUES(8,24,'8:24','E jenumanja ŵankulupilile! Mun''jitikani Allah ni Ntenga صلى الله عليه وسلم pati achim''bilanjilaga ku yampaka impe umi (wambone), soni manyililani yanti Allah jukusiŵilikanganyaga chilikati cha mundu ni ntima wakwe (kuti akatenda yaukusaka ntimao), soni (manyililani) yanti Kukwakwe chinchisonganganyisyidwa.');
INSERT INTO "silika" VALUES(8,25,'8:25','Soni ijogopani ipotesi yangaŵa nkwapata ŵawowopeo ŵakulitenda lupuso mwa jenumanja, (nambo mpaka yapate ni ŵambone ŵakwe naga ngakanya yakusakala), soni manyililani yanti Allah ni Jwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(8,26,'8:26','Soni kumbuchilani katema kamwaliji ŵakuŵalanjika, ŵangalikombolela pa chilambo, nchijogopaga kunswamba ŵandu, basi niŵan''juyiye (pamalo pantunjelele), niŵan''dimbangenye ni chikamuchisyo Chakwe, nikumpa ichindu yambone kuti m''be ŵakutogolela.');
INSERT INTO "silika" VALUES(8,27,'8:27','E jenumanja ŵankulupilile! Ngasimuntendela lupuso Allah ni Ntenga صلى الله عليه وسلم ni kusitendela lupuso amana syenu (ichindu yansunjisyidwe), aku nchimanyililaga (kuti nkulemwa).');
INSERT INTO "silika" VALUES(8,28,'8:28','Soni manyililani yanti chisimu ipanje yenu kwisa soni ŵanache ŵenu jiŵele fitina (jakun''dinga chikulupi chenu), soni yanti Allah Kukwakwe nikwagali malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(8,29,'8:29','E jenumanja ŵankulupilile! Naga mun''jogopeje Allah, tam''biichile chilekanganyo (chakulekanganyichisya chilikati cha yakuona ni yaunami), ni tansiimaachisye yakusakala yenu, ni kunkululuchila. Soni Allah ni Nsyene umbone wekulungwa nnope.');
INSERT INTO "silika" VALUES(8,30,'8:30','Soni (kumbuchilani mmwe Muhammadi صلى الله عليه وسلم) ndema jaŵaliji nkuntendela malindi aŵala ŵakanile gakuti antaŵe, kapena am''bulaje, kapena ankoposye (m’musi wenu wa Maaka), ŵaliji nkutenda malindi, sano Allah ni jwaliji nkuwesesya malindigo, soni Allah ni Jwambone nnope mwa akuwesesya malindi.');
INSERT INTO "silika" VALUES(8,31,'8:31','Soni pati gachisoomedwaaga kukwao ma Ȃya Getu akasatiji: “Pakwete patupikene (yeleyi kala), tungaŵe tukusaka tungaŵechete yanti chisawu yeleyi, nganiyiŵatu yeleyi ikaŵeje adisiadisi syaŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(8,32,'8:32','Soni (kumbuchilani) katema kaŵatite: “E Allah! Naiŵaga ayi (yaayiche nayo Muhammadi صلى الله عليه وسلم) ili yakuona ya kuumila Kukwenu, basi tutuluchisyani wula jamaganga kuumila kwinani, kapena m''biche nayo kukwetu ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(8,33,'8:33','Ni nganaŵa Allah jwanti ni kwalagasya ŵanganyao kutendaga mmwe (Muhammadi صلى الله عليه وسلم) n''di nao, soni Allah nganaŵa Nkwalagasya ŵanganyao kutendaga ali nkuŵenda chikululuko.');
INSERT INTO "silika" VALUES(8,34,'8:34','Soni chichi akwete ŵanganyao chaangaalagaachisya Allah, kutendaga ŵanganyao ali nkusiŵilila (ŵandu) kwinjila mu Nsikiti Wakuchimbichika (wa Maaka), soni nganaŵa akuuchengeta ŵakwe? Pangali ŵampaka aŵe akuuchengeta ŵakwe ni ikaŵeje ŵakun''jogopa (Allah), nambo ŵajinji mwa ŵanganyao ngaakumanyilila.');
INSERT INTO "silika" VALUES(8,35,'8:35','Ni nganijiŵa swala jao pa Nyumba (ja Kaaba) ikaŵeje kwimba lulusi ni kwata m''myala. Basi pasyani ilagasyo ligongo lya ungakulupilila wenu.');
INSERT INTO "silika" VALUES(8,36,'8:36','Chisimu aŵala ŵakanile akutolaga ipanje yao pakusiŵilila (ŵandu) kwitala lya Allah. Basi taŵeje ali nkuitolape, nambo pambesi pakwe chigagambe kuŵa madandausi kwa jemanjao, kaneka ni chaapundidwe, soni aŵala ŵaakufuulu chachisonganganyisyidwa wa ku Jahannama.');
INSERT INTO "silika" VALUES(8,37,'8:37','(Akwapaga machili achimakafili gakulimbana ni Asilamu), kuti Allah ŵalekanganye ŵakusakala ni ŵambone, ni kwaŵika ŵakusakala ŵane mwa jamanjao pachanya pa ŵane, ni kwaunjika wosope ŵene basi ni kwaponya ku Jahannama, ŵanganyao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(8,38,'8:38','Mwasalilani aŵala ŵaakufuulu (yanti); naga akuleka (ukafili) chakululuchilidwe yayalongolele, nambo naga akuujila soni (ku ukafili), basi matala ga (kaape ilagasyo ŵandu) ŵandanda pamasile pa gapite (kuŵa chitetelo chao).');
INSERT INTO "silika" VALUES(8,39,'8:39','Soni mwaputani ngondo kwikanila fitina (ja galagatila isanamu) jikapatikana, ni jiŵe dini josopejene jagalagatila Allah pe. Nambo naga akuleka (yagalagatila isanamuyo), basi chisimu Allah ichindu yaakutenda juŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(8,40,'8:40','Nambo naga akugalauka, basi manyililani yanti Allah ni Akun''gosa ŵenu, Kaligoselo kambone nnope ni Nkukamuchisya jwambone kusyene.');
INSERT INTO "silika" VALUES(8,41,'8:41','Soni manyililani yanti iliyose yampatile mu ipanje yakupalagula pangondo, basi chisimu liunjili limo m''maunjili nsano lipelechedwe kwa Allah ni Ntenga صلى الله عليه وسلم kwisa soni ŵaulongo (wa Ntenga صلى الله عليه وسلم), ŵawina, masikini ni jwapaulendo, nam''baga jenumanja munkulupilile Allah ni yatwatulwisye kwa kapolo Jwetu (Muhammadi صلى الله عليه وسلم) lisiku lyakulekanganya (yakuona ni yaunami), lisiku lyagaasimangene makuga gaŵili (likuga lya Asilamu ni lya makafili pa ngondo ja Badri). Sano Allah pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(8,42,'8:42','(Kumbuchilani) katema kamwaliji jenumanja (likuga lya Asilamu) ku upande wachiŵata wakuŵandichila (ku Madina), kutendaga ŵanganyao (likuga lya makafili) ali ku upande wachiŵata wakutalichila, sano nkutula wamalonda niuli chamwiŵanda mwenu (kungulugulu mbwani). Ingaŵe mwataŵene chilanga (chakusimanganilana), chisimu nkalekangene pa chilangacho. (Nambo mwasimangene yangagonela), kuti Allah akwanilisye kupitisya chilamusi chachaliji chelamulelamule, kuti ajonasiche ŵachajonasiche (pakusagula ukafili) panyuma pa chilosyo chakuonechela, ni aŵe ni umi ŵachaŵe umi (pakusagula chikulupi) panyuma pa chilosyo chakuonechela. Soni chisimu Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(8,43,'8:43','(Kumbuchilani) katema kaŵannosyisye Allah ŵanganyao kulugono kwenu (mmwe Muhammadi صلى الله عليه وسلم) ali ŵannono, mwanti akannosye ali ŵajinji, chisimu nkatengwiche ŵanganyammwe ni nkakanganile pakutaŵana nganisyo (syakumenyana nao), nambo kuti Allah ŵan''goosile (jenumanja). Chisimu Jwalakwe ni Jwakumanyilila yaili m’mitima.');
INSERT INTO "silika" VALUES(8,44,'8:44','Soni (kumbuchilani) katema kamwasimangene (pa luŵala lwangondo ja Badri, Allah) jwannosisye ŵanganyao m’meso mwenu kuŵa ŵannono (kuti nkole ntau wakumenyana nao), ni ŵannaandiiye m’meso mwao (kuti akalichiliganya nnope, yoseyo) kuti akwanilisye Allah kupitisya chilamusi chachaliji chelamulelamule (chakwakombolasya Asilamu kwa amagongo ŵao), soni kwa Allah pe ni kwakuujila ichindu (yosope).');
INSERT INTO "silika" VALUES(8,45,'8:45','E jenumanja ŵankulupilile! Pati nsimangene nalyo likuga (lya amagongo), basi jimilichikani, ni munkolanjeje Allah kwannope kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(8,46,'8:46','Soni mumpikanilani Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni ngasinkangana ntakwika muntengwiche ni kutyoka machili genu. Soni pililani, chisimu Allah juli pamo ni akupilila.');
INSERT INTO "silika" VALUES(8,47,'8:47','Soni kasim''ba nti chisawu aŵala ŵana kopokaga m’milango mwao mwakutwanga ni kulionya kwa ŵandu, ni kusiŵililaga (ŵandu) kwitala lya Allah. Sano Allah ichindu yaakutenda ŵanganyao juŵele Jwaisyungula (kwaimanya chenene).');
INSERT INTO "silika" VALUES(8,48,'8:48','Soni (kumbuchilani) katema kajwasalalichisye shetani (achimakafili) itendo yao, ni jwatite: “Pangali mu ŵanduŵa ŵampaka ankombole jenumanja lelo, soni chisimu une ni mnaasi jwenu (pachikamuchisyo chilichose).” Nambo ndema jagaonegene makuga gaŵili, jwaujile ku indende yakwe (kutila) ni jwatite: “Chisimu une mambalene nomwe, unetu ngwiona yangankuiona jenumanja (gagali malaika), unetu ngun''jogopa Allah, soni Allah ni Jwaukali pakupeleka ipotesi.”');
INSERT INTO "silika" VALUES(8,49,'8:49','(Kumbuchilani) katema kaŵaŵechetaga achinakunaku ni aŵala ŵamuli m’mitima mwao ulwele (waukafili) kuti: “Ŵanganyaŵa (Asilamu) janyenjile dini jao.” Sano jwatajegamile kwa Allah, basi chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(8,50,'8:50','Soni ingaŵe mwaiweni pandema jaakutyosyaga Achimalaika umi wa aŵala ŵaakufuulu (pakuwa), achaaputaga ku ngope syao ni ku migongo jao, soni (achasalilaga yanti): “Pasyani ilagasyo ya (Moto wa) koocha. (Nkaiweni yakogoya kusyene).”');
INSERT INTO "silika" VALUES(8,51,'8:51','“Yalakweyi niligongo lya (yakusakala) yagaalongwesye makono genu, soni chisimu Allah nganijuŵa jwakwaatenda lupuso ata panandi achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(8,52,'8:52','(Nyendele ja makafili galeloga jili) chisawu nyendele ja ŵandu ŵa Firiauna ni aŵala ŵapaujo pao, ŵakanile imanyilo ya Allah, basi Allah ni ŵakwembekenye ligongo lya sambi syao, chisimu Allah ni Jwakulimbangana nnope, Jwaukali kusyene pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(8,53,'8:53','Yalakweyi niligongo lyanti Allah nganaŵa jwanti ni kuchigalausya chindimba chaŵapele ŵandu mpaka ali agalawisye (ŵanduo) umbone wauli m’mitima mwao, soni chisimu Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(8,54,'8:54','(Nyendele ja makafili galeloga jili) chisawu nyendele ja ŵandu ŵa Firiauna ni aŵala ŵapaujo pao, ŵakaaniile imanyilo ya M’mbuje gwao (Allah), basi ni twaajonasile ligongo lya sambi syao, ni twamisyisye ŵandu ŵa Firiauna, soni wosope ŵaliji ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(8,55,'8:55','Chisimu inyama yakusakala nnope pameso pa Allah ni aŵala ŵaakufuulu, basi ŵanganyao ngaakukulupilila.');
INSERT INTO "silika" VALUES(8,56,'8:56','Aŵala ŵamwataŵene nao chilanga, kaneka ni akukasaga chilanga chaocho ndaŵi ni katema, soni ŵanganyao ngaakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(8,57,'8:57','Mwanti naga n''di mwapatikenye pa ngondo, basi mwabalalikanganyani ni ŵanganyaŵa ŵamunyuma mwao (pakwapa ilagasyo ŵanganyaŵa mwakwakalalichisya kuti aujilile ŵane ŵaakasachilile kwakuya ŵanganyaŵa pakwalagasya Asilamu) kuti ŵanganyao aliwunde.');
INSERT INTO "silika" VALUES(8,58,'8:58','Soni naga nkogopa chinyengo (chakasa chilanga) kutyochela kwa ŵandu, basi mwaponyelani (chilanga chao) mwakulandana (kuti paŵe pangali chilanga chilikati chenu). Chisimu Allah jwaangaanonyela ŵachinyengo.');
INSERT INTO "silika" VALUES(8,59,'8:59','Soni aŵala ŵaakufuulu akaganichisya ata panandi yanti ampelete (Allah mwanti ngaŵa nkwatenda yakwatenda). Chisimu ŵanganyao nganaŵa annepelekasisye (Allah kwapa ipotesi).');
INSERT INTO "silika" VALUES(8,60,'8:60','Soni mwakosechelani (amagongowo) m’machili (gakuputila ngondo) mwachinkombolele, kwisa soni mahachi geŵichidwe tayali, kuti mwajogoyechesye nago amagongo ŵa Allah ni amagongo ŵenu (achimakafili ŵa pa Maaka), ni ŵane ŵakwe ŵangaŵaga ŵanganyao, jenumanja ngankwamanyilila nambo Allah akwamanyilila, soni chilichose chachintole petala lya Allah tichikwanilisidwe kukwenu (malipilo gakwe), soni ngam''ba ŵakutendedwa lupuso.');
INSERT INTO "silika" VALUES(8,61,'8:61','Sano naga achipendechelaga kuntunjelele, basi pendechelani kukoko nomwe ni n''jegamile kwa Allah. Chisimu Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(8,62,'8:62','Soni naga achisakaga kuntenda chinyengo ŵanganyao, basi chisimu Allah chankwanile (kuŵa akun''gosa ŵenu). Jwalakwe ni Ajula juŵan''dimbangenye ni chikamuchisyo Chakwe, ni ŵakulupilila.');
INSERT INTO "silika" VALUES(8,63,'8:63','Ni jwalumbikenye chilikati cha mitima jao (pakuŵika chinonyelo sikati jao). Nantimuno nkaatoole yosope yaili pa chilambo (pakusachilila kulumbikanya mitima jao mwa jikajenu), nga nganimpakombola kulumbikanya chilikati cha mitima jao, nambo Allah jwalumbikenye chilikati chao. Chisimu Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(8,64,'8:64','E mmwe Ntume صلى الله عليه وسلم! Allah akunkwanila (kuŵa akun''gosa ŵenu) pamo ni ŵankuyiye mu ŵakulupilila.');
INSERT INTO "silika" VALUES(8,65,'8:65','E mmwe Ntume صلى الله عليه وسلم! Mwalimbikasyani ŵakulupilila ya kumenyana ngondo (ni makafili). Naga ali apali mwa jenumanja (ŵandu) makumi gaŵili (20) ŵakupililila, mpaka ŵaakombole (amagongo) machila gaŵili (200), soni naga ali apali mwa jenumanja (ŵandu ŵakwana) lichila (100), mpaka ŵakombole (amagongo) ŵakwana sausande jimo (1000) mwa aŵala ŵaakanile, ligongo lyanti ŵanganyao ni ŵandu ŵangali umanyilisi.');
INSERT INTO "silika" VALUES(8,66,'8:66','Sambano Allah an''jepepachisye ŵanganyammwe, ni ayimanyi yanti mwa jenumanja mwana kutochomala, basi naga ali apali mwa jenumanja (ŵandu ŵakwana) lichila (100) ŵakupililila mpaka ŵakombole (amagongo) machila gaŵili (200), soni naga ali apali mwa jenumanja (ŵandu) sausande jimo (1000) mpaka ŵakombole (amagongo) ŵakwana masausande gaŵili (2000) mwa lisosa lya Allah, soni Allah juli pamo ni akupilila.');
INSERT INTO "silika" VALUES(8,67,'8:67','Nganiyiŵajilwa kwa Ntume صلى الله عليه وسلم kola mateeka ni mpakana ali atupiye kaje kuulaga (amagongo ŵakwe) pa chilambo. Nkusachilila ichindu ya duniya (mbote syaliombolela mateeka) kutendaga Allah jukusachilila (kuti mpate malipilo ga) Akhera! Sano Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(8,68,'8:68','Chingaŵaga chilamusi chachalongolele chakuumila kwa Allah (chakunkundisya chipanje chakupalagula pa ngondo), chisimu ikankwayiye ilagasyo yekulungwa pa (mbote syaliombolela mateeka) syan''jigele.');
INSERT INTO "silika" VALUES(8,69,'8:69','Basi lyagani mu yampalagwile pangondo (yaili) ya halaal nambo soni yambone, ni mun''jogopeje Allah. Chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(8,70,'8:70','E mmwe Ntume صلى الله عليه وسلم! Mwasalilani achimateeka ŵali m’makono mwenu (kuti): “Naga Allah aumanye umbone uliose m’mitima mwenu (wakunda Usilamu), champe yambone nnope kumpunda yaijigalidwe kuumila kukwenu, soni ni tankululuchile, soni Allah ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(8,71,'8:71','Sano naga akusaka kuntendela ulaamba (chiuŵe wangali masengo kukwenu), ngati pamasile paŵantendele Allah ulaamba kalakala, basi ni jwapeleche machili (kukwenu) gakwakombolela ŵanganyao, soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(8,72,'8:72','Chisimu aŵala ŵaakulupilile, ni kusama (kuja ku Madina), ni kumenyana ngondo ni chipanje chao ni achimisyene ŵakwe petala lya Allah (Amuhajirina), kwisa soni aŵala (Answari) ŵaŵapele (ŵanganyao) pakutama ni kwakamuchisya, ŵele ŵanganyao ni asyoŵe (ŵakuchenjelana) ŵane ni ŵane.Sano aŵala ŵaakulupilile nambo ni ngasama (kuja ku Madina), nganinkola kwachenjela kulikose mpaka akaasaame, nambo naga ali am''bendile chikamuchisyo pa Dini, basi mwakamuchisyani, ni ikaŵeje (naga ali atandene) ni ŵandu ŵachipali chilanga (chantunjelele chamwataŵene) chilikati chenu ni ŵanganyao, soni Allah ichindu yankutenda aŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(8,73,'8:73','Nombe aŵala ŵaakufuulu aŵele asyoŵe (ŵakuchenjelana) ŵane ni ŵane. Naga jenumanja (Asilamu) ngankutenda yele (yakuchenjelanayo) chipaŵe pana fitina (jangondo) pa chilambo ni kuwatanganya kwekulungwa. ');
INSERT INTO "silika" VALUES(8,74,'8:74','Sano aŵala ŵaakulupilile, ni kusama (kuja ku Madina), ni kumenyana ngondo petala lya Allah (Amuhajirina), kwisa soni aŵala (Answari) ŵaŵapele (ŵanganyao) pakutama ni kwakamuchisya, ŵele ŵanyao ni ŵali ŵakulupilila kusyesyene, akwete kupata chikululuko kwisa soni lisiki lyakuchimbichika (lya ku Mbepo).');
INSERT INTO "silika" VALUES(8,75,'8:75','Sano aŵala ŵaakulupilile panyuma (pa Amuhajirina ni Answari) ni kusama (kuja ku Madina), ni kumenyana ngondo pamo ni jenumanja, basi ŵanganyao ni ŵali mwa jenumanja. Nambo ŵaulongo wakupagwana ni ŵali ŵakuŵajilwa (kwinjilana chipanje) ŵane ni ŵane (mwaiŵelele) m’chitabu cha Allah. Chisimu Allah chindu chilichose aŵele Jwakuchimanya chenene.');
INSERT INTO "silika" VALUES(9,1,'9:1','(Aku ni) kuliiwuula (ku ilanga ya ntunjelele) kwakuumila kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم kuja kwa aŵala ŵamwataŵene nao ilanga (kaneka ni kuikasa) mwa akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(9,2,'9:2','Basi jendagani pa chilambo myesi nchechepe (mwantunjelele kaneko paŵe pangali chiteteyo chilikati chenu nowe), soni manyililani yanti ŵanganyammwe nganim''ba munnepelekasisye Allah (kumpa ipotesi), ni kuti soni Allah juŵele Jwakwajalusya achimakafili.');
INSERT INTO "silika" VALUES(9,3,'9:3','Soni (aku ni) kwenesya kwakuumila kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم kwa (kwamanyisya) ŵandu lisiku lya Hija jekulungwa yanti; chisimu Allah ni Ntenga Jwakwe صلى الله عليه وسلم aŵambalene ni akuwanganya (Allah ni isanamu, nganaŵa asunjile ilanga yao). Sano naga ntende toba, basi chiyiŵe yambone kukwenu, nambo naga nkusyulula, basi manyililani yanti ŵanganyammwe nganim''ba munnepelekasisye Allah (kumpa ipotesi), soni mwatagulilani aŵala ŵaakanile ya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(9,4,'9:4','Ikaŵeje aŵala ŵamwataŵene nao ilanga (yantunjelele) mwa akuwanganya (Allah ni isanamu) kaneka ni nganampunguchisya chilichose (mu ilangayo) soni ni nganankamuchisya jwalijose pa (kumputa) jenumanja, basi mwakwanilichisyani ilanga yao mpaka mu ndaŵi jao. Chisimu Allah akwanonyela ŵawoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(9,5,'9:5','Sano pati jipite myesi jakuchimbichika, basi mwaulagani ŵakuwanganya (Allah ni isanamu) palipose pamwasimene, soni mwakamulani ni kwaŵiga uŵigo, ni kwaalindilila m’matala gosope gene. Nambo naga ali atesile toba ni kwimikaga Swala mwakolosya, ni kutolaga Zaka, basi lilekani litala lyao (kuti ajendeje mwagopoka). Chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,6,'9:6','Soni naga jumpepe mwa akuwanganya (Allah ni isanamu) ali am''bendile kuti munchiinje (aŵe nchiteteyo chenu), basi munchiingaani kuti mpaka apikane maloŵe ga Allah, kaneka ni mumpechesye kumalo gakwe gantunjelele, yalakweyi ni ligongo lyanti ŵanganyao ni ŵandu ŵangamanyilila.');
INSERT INTO "silika" VALUES(9,7,'9:7','Chantiuli chakole chilanga (chantunjelele) kwa Allah ni kwa Ntenga Jwakwe صلى الله عليه وسلم akuwanganya (Allah ni isanamu, kutendaga ŵanganyao ali ŵakasa ilanga)? Ikaŵeje aŵala ŵamwataŵene nao chilanga (chantunjelele) pa Nsikiti Wakuchimbichika (wa Maaka). Basi mwatalonjelele ali nkunnungamichila nombe jenumanja mwalungamichilani, chisimu Allah akwanonyela ŵawoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(9,8,'9:8','Chantiuli (chakole chilanga chantunjelele ŵanganyao) kutendaga naga ali an''guŵatile ŵangakosya kukwenu ulongo namuno chilanga? Akunsengwasyaga ni ngamwa syao kutendaga mitima jao jichikanaga (kunnonyela), soni ŵajinji mwa ŵanganyao ŵakunyosya.');
INSERT INTO "silika" VALUES(9,9,'9:9','Asagwile (ya duniya) ya ntengo wannono pakusumanya ni ma Ȃya ga Allah, basi ni asiŵilile (ŵandu) kwitala Lyakwe. Chisimu ŵanganyao iŵele yakusakala nnope yaaŵele nkupanganya.');
INSERT INTO "silika" VALUES(9,10,'9:10','Ŵangakosya kwa jwakulupilila ulongo namuno chilanga, soni ŵanyao ni ŵali ŵakusumba mpika.');
INSERT INTO "silika" VALUES(9,11,'9:11','Nambo naga ali atesile toba ni kwimikaga Swala mwakolosya ni kutolaga Zaka, nikuti achalongoachimijenu mu Dini ŵeleo, ni tugopolanya ma Ȃya kwa ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(9,12,'9:12','Soni naga akugopola kulumbila kwao panyuma pa chilanga chao, ni kutaga nchesela mu Dini jenu, basi mwaputani ngondo achinnongola ŵa ukafili - chisimu ŵanganyao kulumbila kwao kuŵele kwangali mate - (mwaputani ngondo) kuti aleche (yakusakalayo).');
INSERT INTO "silika" VALUES(9,13,'9:13','Ana ngankwaputa ngondo ŵandu ŵaagopwele kulumbila kwao ni kusachilila ya kunkoposya Ntenga r, kutendaga soni ŵanganyao ni ŵaŵantandite jenumanja kandandape? Ana nkwajogopa ŵanganyao? Basi Allah ni jwali Jwakuŵajilwa kuti mun''jogope naga n''di ŵaakulupilila (kusyene).');
INSERT INTO "silika" VALUES(9,14,'9:14','Mwaputani ngondo ŵanganyao, Allah tiŵape ilagasyo ni makono genu ni kwajigasya nchesela, ni tanchenjele jenumanja pa ŵanganyao, ni kuposya mitima ja ŵandu ŵakulupilila.');
INSERT INTO "silika" VALUES(9,15,'9:15','Ni kutyosya gasabu ja mitima jao. Soni Allah tampochele toba jwansachile (mwa achimakafili panyuma pakunda Usilamu). Soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,16,'9:16','Ana nkuganichisya yanti chin''gambe kulechedwa kutendaga Allah nkaniŵamanyukule aŵala ŵaapuutiile ngondo (Dini Jakwe) mwa jenumanja, ni ŵanganiŵaatenda ŵanepe ŵangaŵaga Allah kwisa soni Ntenga Jwakwe صلى الله عليه وسلم ni ŵaakulupilile kuŵa isyoŵe yapantima. Sano Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(9,17,'9:17','Nganiiŵajilwa kwa akuwanganya (Allah ni isanamu) kuti ajigose misikiti ja Allah achilitendelaga umboni achimisyene ya ukafili. Ŵanganyao ni ŵagajonasiche masengo gao (gambone), soni ku Moto chakaŵe ŵandamo.');
INSERT INTO "silika" VALUES(9,18,'9:18','Chisimu ŵakuŵajilwa kujigosa misikiti ja Allah ni aŵala ŵaakulupilile mwa Allah ni Lisiku Lyambesi ni kwimikaga Swala mwakolosya ni kutolaga Zaka, ni ngaakun’jogopa jwinepe jwangaŵaga Allah. Basi ŵanganyao kuŵa nkuŵa mwa akongoka.');
INSERT INTO "silika" VALUES(9,19,'9:19','Ana nkutesile kwaamwesya mesi ŵandu ŵa ku Hija ni kuugosa Nsikiti Wakuchimbichika (wa Maaka) kuŵa yalumo ni ajula jwaakulupilile mwa Allah ni Lisiku Lyambesi ni kumenyana ngondo petala lya Allah? Ngaŵa nkulandana pameso pa Allah. Soni Allah ngaakwajongolaga ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(9,20,'9:20','Aŵala ŵaakulupilile ni kusama (kuja ku Madina), ni kumenyana ngondo petala lya Allah ni ipanje yao ni achimisyene ŵakwe, akwete uchimbichimbi wekulungwa pameso pa Allah, soni ŵanganyao niŵali ŵakupunda. ');
INSERT INTO "silika" VALUES(9,21,'9:21','Ambuje ŵao akwapa abali jakusengwasya ja chanasa chakuumila Kukwakwe kwisa soni chinonyelo, ni Matimbe ga ku Mbepo, akwete kupata ŵanganyao mwalakwemo chindimba chakwendelechela.');
INSERT INTO "silika" VALUES(9,22,'9:22','Chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka. Chisimu Allah Kukwakwe kwana malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(9,23,'9:23','E jenumanja ŵankulupilile! Ngasimwatenda achatati ŵenu ni achachimwene ŵenu kuŵa isyoŵe naga ali awunonyele ukafili m’malo mwa chikulupi. Sano jwachiŵatende kuŵa isyoŵe yakwe mwa jenumanja, basi ŵanganyao ni ŵali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(9,24,'9:24','Jilani: “Naŵaga achatati ŵenu, ŵanache ŵenu, achachimwene ŵenu, achiŵammakwenu, achanasi ŵenu, kwisa soni ipanje yanyyipatile, ni malonda gankugajogopela konasika, kwisa soni majumba gankuganonyela, (naiŵaga yalakweyi) yakunonyeledwa nnope kukwenu kumpunda Allah ni Ntenga Jwakwe صلى الله عليه وسلم ni kumenyana ngondo petala Lyakwe; basi jembecheyani kwikanila Allah ayiche ni chilamusi Chakwe (cha ipotesi), soni Allah ngaakwajongolaga ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(9,25,'9:25','Chisimu Allah aŵele ali nkunchenjela mu mbala syejinji syangondo, ni lisiku lya (ngondo ja) Hunaini, ndema ja wannyenjile winji wenu, ninganiunkamuchisya chilichose, chilambo ni cham''banile kutendaga chili chakusapanguka, kaneka ni mwagalawiche kutila.');
INSERT INTO "silika" VALUES(9,26,'9:26','Kaneka Allah jwatulwisye kutulala Kwakwe pa Ntenga Jwakwe صلى الله عليه وسلم ni pa ŵakulupilila, ni jwatulwisye soni asilikali (ŵachilaika) ŵanganimwaona, ni jwapele ilagasyo aŵala ŵaŵakanile, soni gele ni malipilo ga achimakafili.');
INSERT INTO "silika" VALUES(9,27,'9:27','Soni panyuma pa yalakweyi Allah tapochele toba ja jwansachile (mwa achimakafilio panyuma pakunda Usilamu), soni Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,28,'9:28','E jenumanja ŵankulupilile! Chisimu akuwanganya (Allah ni isanamu) aŵele najasun (ŵausakwa) , basi akauŵandichila Nsikiti Wakuchimbichika (wa Maaka) panyuma pa chaka chao chechino (cha 9 chisamile Ntume صلى الله عليه وسلم), sano naga nkogopa kulaga, basi pangakaŵapa Allah tansichilisye ni umbone Wakwe naasakaga. Chisimu Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,29,'9:29','Mwaputani ngondo aŵala ŵangakukulupilila mwa Allah ni Lisiku Lyambesi, ni ngakuiŵambala yaakanyisye Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni ngakukuya Dini jakuona (ja Usilamu) mwa aŵala ŵaŵapedwile chitabu (Ayuda ni Akilisito), mpaka akapeleche nsongo kwa makono gao aku ali ŵakunyosyeka.');
INSERT INTO "silika" VALUES(9,30,'9:30','Soni Ayuda akasatiji: “Uzairu (Ezara) mwanache jwa Allah.” Nombe Akilisito akasatiji: “Masihi (Isa) mwanache jwa Allah.” Kwele nikuŵecheta kwao ni ngamwa syao, akusyachisya yakuŵecheta ya aŵala ŵaŵakanile kalakala. Allah ŵalwesile ŵanganyao, ana akusokonesedwa chinauli (kuleka yakuona)? ');
INSERT INTO "silika" VALUES(9,31,'9:31','Ŵatesile achalijiganye ŵao (ilimu ya dini) ni akun''jogopa Nnungu ŵao kuŵa milungu kunneka Allah, kwisa soni Masihi (Isa) mwanache jwa Maryam, kutendaga nganalamulidwa ikaŵeje yanti an''galagatileje Nnungu Jumpepe, pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwele pejo, kuswejela ni Kwakwe pakuŵambalana ni yaakum''bwanganya nayo. ');
INSERT INTO "silika" VALUES(9,32,'9:32','Akusachililaga kuti asimisye lilanguka lya Allah (Usilamu) ni ngamwa syao, sano Allah ni akukana ikaŵeje kwanilisya lilanguka Lyakwe namuno achimakafili yachime.');
INSERT INTO "silika" VALUES(9,33,'9:33','Jwalakwe ni Ajula jwaatumisye Ntenga Jwakwe (Muhammadi صلى الله عليه وسلم) ni chongolo kwisa soni Dini jakuona kuti ajikwesye pachanya pa dini syosope, namuno ŵagalagatila isanamu yachime.');
INSERT INTO "silika" VALUES(9,34,'9:34','E jenumanja ŵankulupilile! Chisimu ŵajinji mwa achalijiganye (ilimu ya dini ŵachiyuda) ni ŵakun''jogopa Nnungu (ŵachikilisito) akulyaga ipanje yaŵandu mwaukoleko, ni akusiŵilila (ŵandu) kwitala lya Allah. Sano aŵala ŵaakukusanya golodi ni silifa (mbiya) ni ngasitolaga petala lya Allah, basi mwatagulilani ya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(9,35,'9:35','Lisiku lyasichitukutisidwa (mbiyasyo) ku moto wa Jahannama, basi nikuchomedwa nasyo usyo syao, mbalati syao ni migongo jao, (achisalilidwaga yanti): “Ayi ni yamwalikusanyichisye mwachimisyene, basi pasyani yamwaliji nkukusanya.”');
INSERT INTO "silika" VALUES(9,36,'9:36','Chisimu chiŵalanjilo cha myesi kwa Allah (pa chaka) nimyesi likumi kwisa jiŵili, (muyalembedwele) m’chitabu cha Allah lisiku lyaŵagumbile mawunde ni mataka, mu jalakwejo mwana (myesi) ncheche jakuchimbichika (Mfungopili, Mfungotatu, Mfungonne ni Mfungokumi). Jele ni Dini jagoloka, basi ngasillitendaga lupuso mwachimisyene mwele (m’myesi jakuchimbichikamo). Soni jenumanja ŵanaose mwaputani ngondo akuwanganya (Allah ni isanamu) yaakuti ŵanganyao ŵanaose pakumputa ngondo jenumanja, soni manyililani yanti Allah ali pamo ni ŵawoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(9,37,'9:37','Chisimu kuchelewasya (mwesi wakuchimbichika kuti uŵe wamalaŵi m’malo mwaunouno) kuŵele konjechesya pa ukafili, akusochelesyedwa nayo aŵala ŵaakanile, (mwesi umopeo) akuutenda kuŵa mwesi wamba chaka chine, chaka chine ni kuutenda kuŵa ŵakuchimbichika, kuti awutile chiŵalanjilo cha (myesi) jaajitesile Allah kuŵa jakuchimbichika (kuti jikwanile), basi ni akujitenda (myesi) jakuchimbichika jila kuŵa (myesi) wamba. Isalalisidwe kukwao itendo yao yangalumbana, soni Allah ngakwajongolaga ŵandu achimakafili.');
INSERT INTO "silika" VALUES(9,38,'9:38','E jenumanja ŵamkulupilile! Chipali chichi kukwenu kwanti naga nsalilidwe (yanti): “Ŵigulani (wa kuputa ngondo) petala lya Allah,” nkukanilila mwausito pa chilambo? Ana nnonyelwe ni umi wa duniya kumpunda Akhera? Nganiyiŵatu yakusengwasya ya umi wa duniya pakulandanya ni Akhera ikaŵeje yannono.');
INSERT INTO "silika" VALUES(9,39,'9:39','Naga ngankuŵigula (wa kungondo), Allah champe ilagasyo yakupoteka nnope, ni tayiche nao ŵandu ŵane mmalo mwa jenumanja, soni nganim''ba mumpele chisausyo chilichose, soni Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(9,40,'9:40','Naga ngankunkamuchisya (Ntumejoصلى الله عليه وسلمngali kandu), basi pamasile paŵankamuchiisye Allah ndema jaŵankopwesye aŵala ŵaŵakanile, ali jwaŵili mwa ŵaŵili (jumo jwakwe Abubakri t), pandema jaŵaliji ŵanaŵaŵilio kumbanga, ndema jaŵansalilaga n''jakwe (kuti): “Ngasin''dandaula, chisimu Allah ali nowe.” Basi Allah ni ŵaatulwisye kutulala Kwakwe pa jwalakwejo, ni ŵan''dimbangenye ni asilikali (ŵachilaika) ŵanganimwaona jenumanja, niŵalitesile liloŵe lya aŵala ŵaŵakanile kuŵa lyapasi, sano liloŵe lya Allah nikuŵa lyapenani, soni Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,41,'9:41','Ŵigulani (wa kungondo) n''di ŵakupepuchilwa namuno ŵakusitopelwa, ni mmenyane ngondo ni chipanje chenu ni mwachimisyene ŵakwe petala lya Allah, yalakweyo ni yambone kukwenu nam''baga jenumanja ŵakumanyilila. ');
INSERT INTO "silika" VALUES(9,42,'9:42','Ingaŵe (kuŵigulako) wa kuchipanje chakuŵandika (chakuchipata mwakwepepala), ulendowo nikuŵa ŵejipi chisimu angankuyiye, nambo kuti lutando (lwakuja ku Tabuki) lwatalichile ŵanganyao. Ni channumbileje Allah (achitiji): “Tungaŵe twakwete ukombosi chisimu tungakopweche namwe.” Akulijonanga achimisyene, soni Allah akumanyilila yanti chisimu ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(9,43,'9:43','Allah ankululuchile (mmwe Muhammadi صلى الله عليه وسلم), ligongo chichi kwapa lusa ŵanganyao (lwakutama ngajaula kungondo? Ngati nkajembechele kaje) mpaka amanyukuche kukwenu aŵala ŵaŵechete yakuona ni mwamanyilile ŵaunami.');
INSERT INTO "silika" VALUES(9,44,'9:44','Nganaŵa am''bendile lusa aŵala ŵaakukulupilila mwa Allah ni Lisiku Lyambesi lwakuti akaja kumenyana ngondo ni ipanje yao ni achimisyene ŵakwe, soni Allah ni Jwaakwamanya chenene ŵawoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(9,45,'9:45','Chisimu ŵaakum''benda lusa mmwe niŵawowopeo ŵangakukulupilila mwa Allah ni Lisiku Lyambesi, soni ŵajikaichiile mitima jao, basi ŵanganyao mu kaichila kwaoko ali ŵapendapenda.');
INSERT INTO "silika" VALUES(9,46,'9:46','Soni ingaŵe ŵasachilile yakopoka (kuja kungondo) angakukosechele yakosechela yakwe, nambo kuti kwanchimile Allah kopoka kwao, basi ni ŵaateengwiisye, soni ni yaŵechetedwe yanti: “Tamani imo ni akutama.”');
INSERT INTO "silika" VALUES(9,47,'9:47','Ingaŵe ŵakopweche namwe nga nganan''jonjechesya ikaŵeje kuwatanganya, soni angaliji nkusemasema chilikati chenu (kupita nchitenda unakunaku) pakunsachilila mpumbulima, kutendaga soni mwa ŵanganyammwe muli mwana ŵaakwapikanilaga ŵanganyao, soni Allah ni Jwaakwamanya chenene ŵaalitenda lupuso.');
INSERT INTO "silika" VALUES(9,48,'9:48','Chisimu pamasile paŵasachilile yampumbulima kutandila kala (mmwe Muhammadi صلى الله عليه وسلم n''di naikape m’Madina), ni aŵele ali nkun''galauchisyagalauchisya ichindu mpaka iyiiche yakuona (kupunda pa ngondo), ni kuonechela papaswela chilamusi cha Allah (Usilamu) kutendaga ŵanganyao ili nkwachima.');
INSERT INTO "silika" VALUES(9,49,'9:49','Soni mwa ŵanganyao mwana jwaakutiji: “Mumbani lusa une (lwangajaula kungondo), soni ngasimumbika pa kuliingwa (kwakwaona achakongwe ŵa ku Roma ndakuja kulemwa).” Manyililani yanti ŵanganyao agwilile kala nkuliingwa, soni chisimu Jahannama jichasyungula achimakafili.');
INSERT INTO "silika" VALUES(9,50,'9:50','Naga ili impatile yambone ikwasakalisyaga, nambo naga gali gampatile makungu akutiji: “Twalikalamuchile kalape (niligongo lyakwe makungu gatuŵambele).” Ni akugalaukaga ulendo aku ali nkusengwa.');
INSERT INTO "silika" VALUES(9,51,'9:51','Jilani: “Nganichiŵa chitupatile uwwe ikaŵeje chaatulembele Allah, Jwalakwe ni Nkutugosa jwetu, soni kwa Allah pe basi ajegamile ŵakulupilila.”');
INSERT INTO "silika" VALUES(9,52,'9:52','Jilani: “Ana nkwembecheya kuti itupate uwwe (inepe) yangaŵaga chimo mu yambone iŵili (kupunda pa ngondo ni kupalagula chipanje, kapena kuulajidwa ni kuja kwinjila ku Mbepo). Sano uwwe ni tukwembecheya kukwenu kuti Allah ampe ilagasyo kuuma Kukwakwe kapena kupitila m’makono mwetu, basi jembecheyagani, chisimu uwwe tukwembecheya namwe.”');
INSERT INTO "silika" VALUES(9,53,'9:53','Jilani: “Tolagani (chipanje chenu petala lya Allah) mwakusachilila kapena mwakulikanganichisya (yalumope), nganichiŵa chipocheledwe kuumila kukwenu, chisimu ŵanganyammwe m''bele ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(9,54,'9:54','Soni pangali chachikwasiŵilila ŵanganyao kuti yakutola yao ikapocheledwa kuumila kukwao, ikaŵeje kuti ŵanganyao ankanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni ngakujiyichilila Swala ikaŵeje mwaulesi, soni ngaakutola ikaŵeje mwakulikanganichisya.');
INSERT INTO "silika" VALUES(9,55,'9:55','Basi ikansengwasya ipanje yao namuno ŵanache ŵao, chisimu Allah akusaka kuti ŵalagasye nayo yalakweyo pa umi wa duniya, ni jityoche mitima jao (pakuwa) ali achimakafili.');
INSERT INTO "silika" VALUES(9,56,'9:56','Soni akunnumbila Allah kuti chisimu ŵanganyao ali mwa jenumanja, kutendaga ŵanganyao nganaŵa mwa jenumanja, nambo ŵanganyao ni ŵandu (achinakunaku) ŵaakogopa (kuti ntaakwaulaga).');
INSERT INTO "silika" VALUES(9,57,'9:57','Ingaŵe akupata malo gakutilila kapena mbaanga (syakuti nikuuŵaamo) kapena malo gakulisisa, angalungeme kwalakweko mwakangamala.');
INSERT INTO "silika" VALUES(9,58,'9:58','Soni mwa ŵanganyao mwana ŵaakun''gamba mu (kagaŵanye ka) sadaka, basi naga ali apedwile kachindu mwalakwemo akasanonyelwa, nambo naga nganapedwa kalikose mwalakwemo akasaŵa ŵakasilika.');
INSERT INTO "silika" VALUES(9,59,'9:59','Sano ingaŵe ŵanganyao ŵajikutile pa yaŵapele Allah ni Ntenga Jwakwe صلى الله عليه وسلم ni kuŵechetaga kuti: “Allah akutukwanila, Allah chaatupe kuumila mu ukoto Wakwe, ni Ntenga Jwakwe صلى الله عليه وسلم soni, chisimu uwwe kwa Allah tukusachilila (ukoto Wakwe).” (Ingaliji yambone kukwao).');
INSERT INTO "silika" VALUES(9,60,'9:60','Chisimu sadaka (sya Zaka) sipelechedweje kwa ŵakulaga, ni masikini, kwisa soni akusisonganganya, ni ŵakujisyoŵechesya mitima jao (ku Usilamu), ni pa kwapela ulokosyo achikapolo, ni (kwalipilila) ŵasyakwembekenye ngongole, ni petala lya Allah (mpela pa Jihadi), kwisa soni jwaakatichilwe pa ulendo. Ali ni lilamusi lyakuumila kwa Allah, soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,61,'9:61','Soni mwa ŵanganyao mwana ŵane ŵaakunsausya Ntume صلى الله عليه وسلم ni kuŵechetaga kuti: “Jwalakweju (jugamba kutejela) liwiwi (ku iliyose ni kuijitichisya).” Jilani: “Liwiwi lyakutejela chenene kukwenu, (ngati jukwamanya ŵaakuŵecheta yakuona ni ŵaakulambusya), jukunkulupilila Allah ni kwajitichisya ŵakulupilila, soni juŵele chanasa kwa aŵala ŵaakulupilile mwa jenumanja.” Nambo aŵala ŵaakunsausya Ntenga jwa Allah صلى الله عليه وسلم, akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(9,62,'9:62','Akunnumbila Allah kukwenu kuti annonyelesye, kutendaga Allah ni Ntenga Jwakwe صلى الله عليه وسلم ni jwali jwakuŵajilwa kuti annonyelesye naŵaga ŵanganyao ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(9,63,'9:63','Ana ngakumanyilila yanti chisimu jwaakunsisya Allah ni Ntenga Jwakwe صلى الله عليه وسلم nikuti akwete kupata jwalakwejo moto wa Jahannama, chakaŵe jwandamo mwalakwemo? Kwalakweko ni kwaluka kwekulungwa nnope.');
INSERT INTO "silika" VALUES(9,64,'9:64','Achinakunaku akogopa yakwatuluchila Sura jakwaajuwula yaili m’mitima mwao. Jilani: “Tendagani chipongwe, chisimu Allah ni Nkuikoposya yankuijogopayo.”');
INSERT INTO "silika" VALUES(9,65,'9:65','Soni naga n''di ntite mwawusye (chaŵecheteleje maloŵe gachalu), chisimu mpaka ajile: “Chisimutu uwwe twaliji nkutenda yambenje ni yakunguluka.” Jilani: “Ana mwaliji nkuntenda chipongwe Allah, ma Ȃya Gakwe, kwisa soni Ntenga Jwakwe صلى الله عليه وسلم?”');
INSERT INTO "silika" VALUES(9,66,'9:66','Kasin''diŵalanga, pamasile pankufuulu panyuma pachikulupi chenu. Naga tulikululuchile likuga line mwa jenumanja, likuga line chitulipe ilagasyo ligongo lyanti ŵanganyao ŵaliji ŵakuleŵa.');
INSERT INTO "silika" VALUES(9,67,'9:67','Achinakunaku ŵachilume ni achinakunaku ŵachikongwe ali (ŵanakaajumo) ŵane kwa ŵane, akuchisyaga yakusakala ni kanyaga yambone, ni akasaumbataga soni magasa gao (pangatola petala lya Allah), an''diŵalile Allah basi nombenajo ni jwaliŵalile, chisimu achinakunaku niŵali ŵakunyosya.');
INSERT INTO "silika" VALUES(9,68,'9:68','Allah ŵapele achinakunaku ŵachilume ni achinakunaku ŵachikongwe kwisa soni achimakafili chilanga cha moto wa Jahannama, chakaŵe ŵandamo mwalakwemo, walakweo chiuchakwana ŵanganyao, soni Allah ŵalwesile ŵanganyao, ni akwete kupata ŵanyao ilagasyo yakwendelechela.');
INSERT INTO "silika" VALUES(9,69,'9:69','(Jenumanja n''di) chisawu aŵala ŵaŵaliji paujo penu, (chitukomboleche kun''jonanga mwatwajonanjile ŵanganyao, kutendaga) ŵanganyao ŵaliji ŵakulimbangana nnope kumpunda ŵanganyammwe pamachili, niŵakwete ipanje yejinji nnope kwisa soni ŵanache, basi ni ŵasangalalile liunjili lyao (lya duniya ndema jannono), basi nombe jenumanja ni nkuŵa nkusangalalila liunjili lyenu (lya duniya kwandema jannono), yaŵatite pakusangalalila aŵala ŵaŵaliji paujo penu liunjili lyao, ni nkuŵa nkutiŵila (mmaloŵe gangali mate) yaŵatite pakutiŵila ŵanganyao, ŵele ŵanyao ni ŵagajonasiche masengo gao pa duniya pano ni ku Akhera kwakwe, soni ŵanyao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(9,70,'9:70','Ana nganijaichilila abali ja ŵapaujo pao, ŵandu ŵa Nuhu, A''di ni Samuda, kwisa soni ŵandu ŵa Ibrahima, ni achinsyene Madiyana, kwisa soni misi jajapitikusyidwe (kwinani kuŵa pasi, pasi kuŵa kwinani jajili Sodomu ni Gomora)? Achimitenga ŵao ŵaichilile ni ilosyo yakuonechela palangulangu, basi nganaŵa Allah jwanti nikwatenda lupuso ŵanganyao, nambo achimisyene niŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(9,71,'9:71','Sano ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe ŵane mwa jamanjao aŵele isyoŵe ya ŵane, akuchisyaga yambone ni akukanyaga yakusakala, ni akwimikaga Swala mwakolosya, ni kutolaga Zaka, ni akumpikanilaga Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ŵanganyao ni ŵatachatendela Allah chanasa. Chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,72,'9:72','Allah jwapele chilanga ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe cha Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, ni mauto gambone m’Matimbe Gandamo, sano chinonyelo chakuumila kwa Allah ni chachili chekulungwa kusyene. Kwalakweko nikwakuli kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(9,73,'9:73','E mmwe Ntume صلى الله عليه وسلم! Limbanani nao achimakafili kwisa soni achinakunaku, ni mwajumulile ntima, soni mauto gao ni ku Jahannama, kaje kusakala malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(9,74,'9:74','Akunnumbila Allah kuti nganaŵecheta (maloŵe gachalu), kutendaga ŵaŵechete maloŵe gaukafili, ni ŵakufuulu panyuma pa Usilamu wao, ni ŵasachilile (kutenda malindi) gaŵalepele kugaichila, ni nganachiona chakusakala chilichose (pakwika kwa Usilamu chachatendekasisye ganisya yamalindi), ikaŵeje kuti Allah ni Ntenga Jwakwe صلى الله عليه وسلم ŵasichilisye ŵanganyao ni ukoto Wakwe. Basi naga atende toba chiiŵe yambone kukwao, nambo naga anyosye Allah tiŵape ilagasyo yakupoteka nnope pa duniya pano ni ku Akhera kwakwe, soni nganakola ŵanganyao pa chilambo kaligoselo ata nkulupusyo.');
INSERT INTO "silika" VALUES(9,75,'9:75','Soni mwa ŵanganyao mwana ŵaŵataŵene najo Allah chilanga chanti: “Naga atupe ukoto Wakwe chisimu chitupelecheje sadaka nikuŵa kwene mwa atenda ngoto.”');
INSERT INTO "silika" VALUES(9,76,'9:76','Nambo katema kaŵapele ukoto Wakwe, ŵautendele ubaili ni ŵanyosyisye aku achisyululaga.');
INSERT INTO "silika" VALUES(9,77,'9:77','Basi yakuichisya yakwe nikwaliji kwaŵika unakunaku m’mitima mwao kwikanila lisiku lyatachisimangana Najo, ligongo lyakuwwesechesya Allah ya ŵataŵene Najo, soni ligongo lya unami waŵaliji nkulambusya ndaŵi ni katema.');
INSERT INTO "silika" VALUES(9,78,'9:78','Ana ngakumanyilila yanti Allah jukusimanya asili syao ni mitemela jao, soni yanti Allah ni Jwakumanyilila nnope yakusisika?');
INSERT INTO "silika" VALUES(9,79,'9:79','Aŵala ŵaakwasogoda ŵandu ŵakulipeleka pakutola sadaka syejinji mwa ŵandu ŵakulupilila, ni (kwasogoda soni) aŵala ŵangakupata (chakutola) ikaŵeje (channono cha) ukombosi wao, basi ni akuŵa ali nkwaatenda chipongwe ŵanganyao; Allah chiŵalipile pa chipongwe chaocho, soni ni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(9,80,'9:80','Mwaŵendelani chikululuko ŵanganyao kapena nneche ngaŵendela chikululuko (yalumope), namuno mwaŵendele chikululuko katema kakwana makumi nsano nagaŵili (70), basi Allah nganaŵa ŵakululuchile ŵanganyao, yalakweyo ni ligongo lyanti ŵanganyao ankanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, sano Allah ni jwangaajongola ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(9,81,'9:81','Aŵala ŵaŵasigalile munyuma (ngajaula kungondo ja Tabuki), ŵasangalele pakutama kwao panyuma pa (kuŵigula) Ntenga jwa Allah صلى الله عليه وسلم, ni yachimile yakuputa ngondo ni chipanje chao ni achimisyene ŵakwe petala lya Allah, ni ŵatite: “Nkaŵigula (wa kungondo) mu lyoto (chitukuta chejinji).” Jilani: “Moto wa Jahannama ni waupundile nnope lyoto ingaŵe ŵaliji ŵana umanyilisi.”');
INSERT INTO "silika" VALUES(9,82,'9:82','Basi asecheje panandipe (pa duniya pano) nambo chachilila kwannope (ku Akhera) kuŵa malipilo pa yaŵaliji ali nkupanganya.');
INSERT INTO "silika" VALUES(9,83,'9:83','Sano nam’busyaaga Allah (kungondo ja Tabuki) kuja ku likuga line mwa jamanjao ni kum’bendaga lusa lwakopoka (kuja kungondo sine syasipatikaneje panyuma pakwe), basi jilani: “Nganim’ba nkopweche none mpaka kalakala, soni nganim’ba mumputile ngondo none mmagongo, chisimu ŵanganyammwe mwanonyelwe kutama (ngajaula kungondo) kandanda, basi tamagani pamo ni ŵakusigala munyuma.”');
INSERT INTO "silika" VALUES(9,84,'9:84','Soni ngasimunswalila ata panandi (mmwe Muhammadi صلى الله عليه وسلم) jwalijose jwaawile mwa jamanjao, soni ngasin’jima pelembe lyakwe (kum’bombela dua), chisimu ŵanganyao ankanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni awile ali ŵakunyosya.');
INSERT INTO "silika" VALUES(9,85,'9:85','Soni ikansengwasya ipanje yao namuno ŵanache ŵao, chisimu Allah akusaka kuti ŵalagasye nayo yalakweyo pa duniya pano, nikuti mitima jao jityoche (pakuwa) ali achimakafili.');
INSERT INTO "silika" VALUES(9,86,'9:86','Soni pati jitulusyidwe Sura jakuchisya yanti: Munkulupililani Allah soni ni mputeje ngondo pamo ni Ntenga Jwakwe صلى الله عليه وسلم, ayipanje mwa jamanjao akum’bendaga lusa (lwangajaula nao kungondo), ni akutiji: “Tulekani tuŵe pamo ni akutama (pannango).”');
INSERT INTO "silika" VALUES(9,87,'9:87','Yanonyele yakuŵa pamo ni ŵakusigala munyuma, ni mudindidwe chidindo m’mitima mwao, mwanti ŵanganyao ngaakupata umanyilisi.');
INSERT INTO "silika" VALUES(9,88,'9:88','Nambo Ntenga صلى الله عليه وسلم ni aŵala ŵakulupilile najo imo, aputile ngondo ni chipanje chao ni achimisyene ŵakwe, soni ŵanganyao akwete kupata yambone, soni ŵanyao ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(9,89,'9:89','Allah jwalinganyichisye ŵanganyao Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, kwele ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(9,90,'9:90','Soni akutola ipaka (yakuona) mu ŵarabu ŵanchimisi ŵayiche (kwa Ntenga صلى الله عليه وسلم) kuti apedwe lusa (lwangajaula kungondo), nambo aŵala (achinakunaku) ŵaŵannambwisye Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ŵagambile kutama (mwangali kuŵenda lusa kwa Ntenga صلى الله عليه وسلم), chiyapate aŵala ŵakanile mwa jamanjao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(9,91,'9:91','Pangali ulemwa (pa ngajaula kungondo) kwa (ŵandu) ŵangali machili, kapena ŵakulwala, kapena aŵala ŵangaakupata yakuti atole (yakwakamuchisya pa ulendo kapena yakwalechela ŵakusigala pannango), naga achikulupichikaga kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم. Pangali litala lililyose lya (kwagambila) ŵakolosya, soni Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,92,'9:92','Nambo soni (pangali ulemwa) kwa aŵala ŵanti ndema jaŵam’bichilile kuti mwasosele chakwela (pakuja ku ngondo), mmwe nikuti: “Nganguchipata chilichose chanti ni kunkwesyapo,” ŵagalawiche aku meso gao galinkulakatisya misosi jamadandausi ligongo lyangachipata chilichose chachatole (chakamuchisya pa ulendowo).');
INSERT INTO "silika" VALUES(9,93,'9:93','Nambo kuti litala (lyachigambilo) lili pa aŵala ŵaakum’benda lusa (lwangajaula ku ngondo) kutendaga ali ŵakupata, yanonyele yakuŵa pamo ni ŵakusigala munyuma, ni amudindile Allah chidindo m’mitima mwao, mwanti ŵanganyao ngakumanyilila (ukoto waakwasa).');
INSERT INTO "silika" VALUES(9,94,'9:94','Chantolele ipaka (yaunami yangajaulila kungondo) pandema jachim''bujile kukwao. Jilani: “Nkatola ipaka, nganituŵa tunkulupilile jenumanja, Allah pamasile paatutagulile ya abali syenu, soni Allah chagaone masengo genu pamo ni Ntenga Jwakwe صلى الله عليه وسلم, kaneka (panyuma pa kuwa kwenu) ni chachim''buchisya kwa Nkumanyilila yakusisika ni yakuonechela, basi ni taachintagulila pa ichindu yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(9,95,'9:95','Pachin''galauche kuja kukwao channumbile Allah kukwenu kuti mwaleche (nkatenda chilichose), basi mwalekani, chisimu ŵanganyao aŵele usakwa (ligongo lya itendo yao yangalumbana), soni mauto gao ni ku Jahannama kuŵa malipilo ga yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(9,96,'9:96','Akulumbila kukwenu kuti mwasangalalile, basi nantimuno mwasangalalile nambo chisimu Allah ngaŵa nkwasangalalila ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(9,97,'9:97','Ŵarabu ŵanchimisi ni ŵapundile nnope ukafili ni unakunaku, soni ni ŵaŵajilwe kuti akajimanya mipingu ja yatulwisye Allah kwa Ntenga Jwakwe صلى الله عليه وسلم, sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,98,'9:98','Soni mwa ŵarabu ŵanchimisi mwana ŵaakuionaga yaakutola (petala lya Allah) nti chisawu mbote syalulele, ni akuujembechelaga mpupulusi kuti umpate ŵanganyammwe, paŵanganyao ni pauli mpupulusi wakusakala (chiwapate achinsyene), sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(9,99,'9:99','Soni mwa ŵarabu ŵanchimisi mwana ŵane ŵaakunkulupilila Allah ni Lisiku Lyambesi, ni akuiona yaakutola (petala lya Allah) kuŵa chakuliŵandichilisya kwa Allah ni (chakwatendekasya kupata) maduwa ga Ntenga صلى الله عليه وسلم. Manyililani yanti! Chisimu yalakweyo niyakwaŵandichisya ŵanganyao (kwa Allah), Allah tachajinjisya ŵanganyao ku Ukoto Wakwe, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,100,'9:100','Sano ŵaŵalongolele ni kuŵa ŵandanda (mu Usilamu) mwa Akusama (ŵaŵasamile ku Maaka), ni Akukamuchisya (ŵali alukosyo ŵa m’Madina), kwisa soni aŵala ŵaŵaakuyiye ŵanganyao mwa umbone, Allah tachanonyela (pakwapochela masengo gao gambone), nombe nao tachinonyelwa Najo (pa malipilo gatachaalipila), soni ŵalinganyichisye ŵanganyao Matimbe ga ku Mbepo gasikujilima pasi pakwe sulo. Chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka. Kwalakweko ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(9,101,'9:101','Soni mwa aŵala ŵali mungulugulu mwenu (dusi ni nsinda wa Madina) mu ŵarabu ŵanchimisi mwana achinakunaku, nambo soni achinsyene nsinda wa Madina apimiiliile pa unakunaku (mwanti) ngankwamanyilila, Uwwe tukwamanyilila, chitwape ilagasyo kaŵili kose (pa duniya pano), kaneka ni tachiuchisyidwa ku ilagasyo yekulungwa nnope (pa Kiyama).');
INSERT INTO "silika" VALUES(9,102,'9:102','Sano ŵane ajitichisye ilemwa yao (ni kutenda toba), awanganyisye masengo gambone ni gane gakusakala, mwanti Allah chiŵapochele toba jao, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,103,'9:103','Jigalani sadaka m’chipanje chao kuti mwatakatisye (ku sambi) ni kwaswejesya najo (ku ubaili), soni mwaombeleje duwa, chisimu duwa jenu jiŵele jakwatulasya ŵanganyao, sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(9,104,'9:104','Ana ngaakumanyilila yanti Allah ni jwaakupochela toba kuumila kwa achikapolo Ŵakwe ni kusipochela sadaka, soni yanti Allah ni Jwakupochela nnope toba, Jwachanasa channope?');
INSERT INTO "silika" VALUES(9,105,'9:105','Soni jilani: “Panganyani (yankusaka), basi Allah tagaone masengo genu pamo ni Ntenga Jwakwe صلى الله عليه وسلم kwisa soni ŵakulupilila, kaneka (panyuma pa kuwa kwenu) ni chachim''buchisya kwa Nkumanyilila yakusisika niyakuonechela, basi ni taachintagulila pa ichindu yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(9,106,'9:106','Sano ŵane ŵatesile kuti ajembecheyeje chilamusi cha Allah (toba jao nganijipocheledwe), mwine chiŵape ilagasyo kapena chiŵapochele toba jao, sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,107,'9:107','Ni (pana achinakunaku ŵane) aŵala ŵaŵataŵile nsikiti pakupeleka ilagasyo (kwa Asilamu), ni (kulimbikasya) ukafili, ni gaŵanya chilikati cha ŵakulupilila, ni kuutenda kuŵa chiliindo cha kwalindilila aŵala ŵana kumputaga ngondo Allah ni Ntenga Jwakwe صلى الله عليه وسلم kalakala, soni chisimu chene chaŵeje nkulumbila (kuti): “Nganitusaka (pakutaŵa nsikitiu inepe) ikaŵeje umbone.” Sano Allah ni akuŵichila umboni kuti chisimu ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(9,108,'9:108','Ngasin’jima (kuswali) mwalakwemo ata panandi, chisimu nsikiti wawalembeleledwe kutandila lisiku lyandanda pakun’jogopa (Allah), niwauli wakuŵajilwa kuti n’jime (kuswali) mwalakwemo, mwelemo mwana ŵandu ŵakunonyelwa kuliswejesya, sano Allah ni akwanonyela ŵakuliswejesya.');
INSERT INTO "silika" VALUES(9,109,'9:109','Ana ajula jwana kulembelelaga chakutaŵa chakwe pa woga wakun''jogopa Allah ni (pa kusachilila) chinonyelo (Chakwe) ngaŵa ni jwali jwambone, kapena ajula jwana kulembelelaga chakutaŵa chakwe munjengwe mwa lisimbo lyaagumuchila ni gumuchila najo m’moto wa Jahannama? Sano Allah jwangaajongola ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(9,110,'9:110','Chakutaŵa chao achila chaŵataŵile ngachileka kuŵa chakutaga chikaiko m’mitima mwao ni ikaŵeje mitima jao jili jikatichekatiche (pa kuwa), sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(9,111,'9:111','Chisimu Allah jusumile kwa ŵakulupilila mitima jao ni chipanje chao pantengo wanti akwete kupata ŵanganyao Litimbe lya ku Mbepo, akuputa ngondo petala lya Allah, basi ni akuwulagaga (makafli) nombe nao ni kuwulajidwaga. Achi ni chilanga chakuonaonape chaalichisisye Nsyene mu Taurat, mu Injil kwisa soni mu Qur''an. Ana ni nduni jwali jwakwanisya nnope chilanga chakwe kumpunda Allah? Basi sengwani ni malonda genu gansumisyene Najo, soni kwalakweko ni kupunda kwekulungwa nnope. ');
INSERT INTO "silika" VALUES(9,112,'9:112','(Ŵakulupilila ŵakwe aŵala) ŵakutendatenda toba (pati alemwisye), ŵakutenda ibada nnope, ŵakunnapilila (Allah), ŵakuchuluya kutaŵa (Swaum), ŵakwinamajinama (kuswali), ŵakusujudusujudu, ŵakuchisya yambone soni ŵakanya yakusakala, soni ŵagosa mipingu ja Allah, sano mwapani abali jakusengwasya ŵakulupilila.');
INSERT INTO "silika" VALUES(9,113,'9:113','Nganiyiŵa (yakuŵajilwa) kwa Ntume صلى الله عليه وسلم ni aŵala ŵaakulupilile kwaŵendela chikululuko ŵagalagatila isanamu, namuno akaliji ŵaulongo, panyuma pakumanyukuka kukwao kuti ŵanganyao niŵandu ŵa ku Moto Waukali (pakuwa ali makafili).');
INSERT INTO "silika" VALUES(9,114,'9:114','Soni nganikuŵa kuŵenda kwa Ibrahima chikululuko kwaŵendela babagwe ikaŵeje ligongo lya chilanga chajwataŵene nao. Nambo ndema ja yamanyukwiche kukwakwe yanti ŵalakweo mmagongo jwa Allah jwaŵambalene nao. Chisimu Ibrahima jwaliji jwakum''benda nnope Allah mwakulinandiya, jwakutulala ntima.');
INSERT INTO "silika" VALUES(9,115,'9:115','Soni Allah nganaŵa jwanti ni kwasochelesya ŵandu panyuma pa kwajongola, ni ikaŵeje ali ŵatagulile kaje yakuŵajilwa kuiŵambala. Chisimu Allah chindu chilichose aŵele Jwakuchimanya chenene.');
INSERT INTO "silika" VALUES(9,116,'9:116','Chisimu Allah uŵele Wakwe uchimwene wa kumawunde ni petaka, akupelekaga umi ni chiwa, soni nganinkola ŵanganyammwe jwangaŵaga Allah akun’gosa ŵaliose namuno akunkamuchisya.');
INSERT INTO "silika" VALUES(9,117,'9:117','Chisimu Allah jwajendelechele kupochela toba ja Ntume صلى الله عليه وسلم, Akusama (ŵaŵasamile ku Maaka) ni Akukamuchisya (ŵali alukosyo ŵa m’Madina) aŵala ŵaŵankuyiye (Ntumejo(صلى الله عليه وسلم pa ndema ja masausyo (pakuja kumenyana ni Aroma ku Tabuki), panyuma panti mitima ja likuga line mwa jamanjao jasigele panandi kuti jipombotale (kuleka litala lyagoloka), kaneka (Allah) jwapochele toba jao, chisimu Jwalakwe kwa jamanjao juŵele Mpole kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,118,'9:118','Ni (jwapochele soni toba) kwa aŵala ŵatatu ŵana kulechedwaga (toba jao ngapocheledwa chitema) kwikanila ndema ja chaaŵanile chilambo mwachisapanguchilemu, ni ŵaŵanikwe soni m’mitima mwao, ni ŵasimichisye yanti pangali kwakuntilila Allah ikaŵeje Kukwakwe pe. Kaneka ni jwakombolasyisye ŵanganyao kutenda toba kuti atende tobajo, chisimu Allah ni Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(9,119,'9:119','E jenumanja ŵankulupilile! Mun’jogopani Allah ni m’be pamo ni akuŵecheta yakuona.');
INSERT INTO "silika" VALUES(9,120,'9:120','Nganiyiŵajilwa kwa achinsyene nsinda wa Madina ni ŵali mungulugulu mwao mu ŵarabu ŵanchimisi kusigala munyuma kunneka Ntenga jwa Allah صلى الله عليه وسلم(pangalongana najo ku ngondo), soni (nganiyiŵajilwa) kulongosya umi wao kuleka umi wakwe. Yalakweyo ligongo lyanti ŵanganyao ngaŵa nkwakola njota, kapena kwapata matabu, kapena kwakola sala petala lya Allah, soni ngaŵa nkuliŵata pa malo pa kwapikasya ntima achimakafili, soni ngaŵa nkwapatisya amagongo chipato chilichose, ni ikaŵeje gakulembedwa kukwao masengo gambone pa yalakweyo. Chisimu Allah jwangajonanga malipilo ga ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(9,121,'9:121','Soni ngaŵa nkutola ŵanganyao chakutola chamwana namuno chekulungwa, soni ngaŵa nkutumbulukanya chiŵata, ikaŵeje ikulembedwa kukwao kuti Allah chachalipila (malipilo) gambone nnope ga yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(9,122,'9:122','Soni ŵakulupilila nganaŵa ŵanti ni akopocheje ŵanaose (wakungondo kunneka Ntume صلى الله عليه وسلم jikape), anauli ngakopoka kuumila mwiikuga lililyose lya jamanjao kalikuga (wakungondo), kuti (ŵakusigalao) alijiganyeje ya Dini (kwa Ntume صلى الله عليه وسلم), ni kuti ŵatetele ŵandu ŵao pataujile kukwao kuti ŵanganyao aligose (ku ilemwa).');
INSERT INTO "silika" VALUES(9,123,'9:123','E jenumanja ŵankulupilile! Mwaputani ngondo aŵala achimakafili ŵam’bandichile, ni akusimane kalalisya mwa jenumanja, soni manyililani yanti Allah ali pamo ni ŵakun’jogopa (Jwalakwe).');
INSERT INTO "silika" VALUES(9,124,'9:124','Soni ndema jilijose jajikutulusyidwa Sura, basi ŵane mwa jamanjao (achinakunaku) pana ŵaakutiji: “Ana nduni mwa jamanja jwajin’jonjechesye jalakweji chikulupi?” Basi kwa aŵala ŵaakulupilile ni jajonjechesye chikulupi soni akuŵa ali nkusengwa.');
INSERT INTO "silika" VALUES(9,125,'9:125','Nambo kwa aŵala ŵamuli m’mitima mwao mwana ulwele (waunakunaku) ni jajonjechesye yakunyalaya (ukafili ni unakunaku) ku yakunyalaya yao (yakalape), ni akuwaga ali makafili.');
INSERT INTO "silika" VALUES(9,126,'9:126','Ana ngaakuiona yanti ŵanganyao akulinjidwa pa chaka chilichose kampepe kapena kaŵilikose (ni makungu gakulekangana-lekangana)? Nambo kaneka ŵangatenda toba (kwa Allah) namuno kuliunda.');
INSERT INTO "silika" VALUES(9,127,'9:127','Soni ndema jilijose jajikutulusyidwa Sura (jakwajuwula ungalumbana wao), akasaloleganaga (kuti agwale aku achitiji): “Ana pana jwalijose jwakum’bona?” Kaneka ni akasapwilingana (mwagwala), Allah ajipwilingenye mitima jao (kwilanguka lyachikulupi) ligongo lyanti ŵanganyao ni ŵandu ŵangali umanyilisi.');
INSERT INTO "silika" VALUES(9,128,'9:128','Chisimu chene pamasile pam’bichilile ŵanganyammwe Ntenga صلى الله عليه وسلم jwakuumila mwa jenumanja, ikasain’dandaulikasya jwalakwe yayinnagasisye ŵanganyammwe, jwakuŵichila ntima pa jenumanja (kuti n’jongoche ni nkajinjile ku Mbepo), kwa ŵakulupilila juŵele mpole kusyene, jwachanasa channope.');
INSERT INTO "silika" VALUES(9,129,'9:129','Nambo naga akwendelechela ŵanganyao kunyosya, basi jilani: “Allah akungwanila (kuŵa Nkungosa jwangu), pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵe Jwalakwe, kwa Jwalakwe pe njegamiile, soni Jwalakwe ni M’mbuje jwa Arishi (Chindanda Chaulungu) jekulungwa nnope.”');
INSERT INTO "silika" VALUES(10,1,'10:1','Alif–Lâm–Râ . Aga ni ma Ȃya ga m’Chitabu chegumbale lunda lwakusokoka.');
INSERT INTO "silika" VALUES(10,2,'10:2','Ana iŵele yakusimonjeka kwa ŵandu kun’juwulila Uwwe mundu (jumpepe) mwa jamanjao (jwali Muhammadi صلى الله عليه وسلم) yanti: “Mwatetelani achinaŵandu, ni kwapa abali jakusengwasya aŵala ŵaakulupilile janti: Akwete kupata ŵanganyao kwa M’mbuje gwao malipilo gambone ga itendo yaŵalongwesye?” Achimakafili akutiji: “Chisimu aju ni nsaŵi jwakuonechela.”');
INSERT INTO "silika" VALUES(10,3,'10:3','Chisimu M’mbuje gwenu ni Allah Ajula juŵagumbile mawunde ni mataka m’masiku nsano nalimo, kaneka ni jwatenjele pa Arishi (Chindanda Chaulungu), jukulondesyaga ichindu (ya iwumbe Yakwe). Pangali nkuchondelela (jwanti nikuchondelela Kukwakwe) ikaŵeje panyuma pa lisosa Lyakwe. Jwalakwejo ni Allah M’mbuje gwenu, basi mun’galagatilani (Jwelepejo), ana ngankukumbuchila?');
INSERT INTO "silika" VALUES(10,4,'10:4','Kukwakwe nikwakuujila kwenu ŵanaose, (achi ni) chilanga cha Allah chakuonaonape. Chisimu Jwalakwe nijwaakutandisyaga gumba iwumbe, kaneka chachiyiusyausya (kuigumba panyuma pakuwa) kuti akaalipile mwa chilungamiko aŵala ŵaakulupilile nikutendaga yambone, sano aŵala ŵaakanile akwete kupata yakumwa ya mesi gamasemule nnope ni ilagasyo yakupoteka nnope ligongo lya ukafili wao uŵaliji nkutenda.');
INSERT INTO "silika" VALUES(10,5,'10:5','Jwalakwe ni Ajula jwaalitesile lyuŵa kuŵa lyakuŵala, mwesi nikuŵa wakumulika, ni awupimiile (mwesio) mauto kuti mmanyilileje winji wa yaka ni chiŵalanjilo, Allah nganagamba gumba yalakweyo ikaŵeje mwakuonaonape (ngaŵa mwachanache), akugopolanya ma Ȃya kwa ŵandu ŵakumanyilila.');
INSERT INTO "silika" VALUES(10,6,'10:6','Chisimu chene mu kapisyangane ka chilo ni muusi, ni yaagumbile Allah kumawunde ni petaka iŵele ilosyo kwa ŵandu ŵakun’jogopa (Allah).');
INSERT INTO "silika" VALUES(10,7,'10:7','Chisimu aŵala ŵangakwembecheya ya kusimangana Nowe, ni kunonyelwa nao umi wa duniya, ni kutulalila nao, ni aŵala ŵaakuŵa ali nkugakwanjila ma Ȃya Getu,');
INSERT INTO "silika" VALUES(10,8,'10:8','Ŵanganyao mauto gao ni ku Moto ligongo lya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(10,9,'10:9','Chisimu aŵala ŵaakulupilile ni kutendaga yambone, M’mbuje gwao tiŵajongole ligongo lya chikulupi chao, chisikajilimeje cha pasi pao sulo, ku Matimbe Gachindimba (ku Mbepo).');
INSERT INTO "silika" VALUES(10,10,'10:10','Kawombe ka maduwa gao mwalakwemo (takajileje): “Kuswejela ni Kwenu E Mmwe Ambuje ŵetu!” Soni kakomasyane kao mwalakwemo (takajileje): “Mtendele!” Sano kumbesi kwa kuwomba kwao takajileje: “Lumbili losope lwene alapikwe Allah M’mbuje jwa iumbe yosope.”');
INSERT INTO "silika" VALUES(10,11,'10:11','Soni ingaŵe yanti Allah akwajanguichisyaga ŵandu yakusakala (yaakuliŵendelaga mwaukali) yaakutiji pakwajanguichisya yambone (pati am’bendilemo), jingaliji jilamulidwe kukwao ndema jao (jakonasichila), basi ni tukwalekaga aŵala ŵangaakwembecheya ya kusimangana Nowe mkusokonechela mwao achiyumbagayumbaga.');
INSERT INTO "silika" VALUES(10,12,'10:12','Soni naga mundu chili chinkwayiye chakusausya akasatuŵendaga ali agonile chamumbalati mwakwe, kapena chitamile, kapena chiimile, basi ndema jatukuntyochesyaga chakusausya chakwecho akwendelechelaga (mu ukafili wakwe) nti chisawu nganatuŵendeje pa chakusausya chachankwayiye, iyyoyopeyo ni yaitite pakunonyelesyedwa kwa akupelenganya malile (pa sambi) ya aŵele ali nkupanganya.');
INSERT INTO "silika" VALUES(10,13,'10:13','Soni pamasile patwajijonasile mikutula paujo penu ndema jajasakesye, kutendaga achimitenga ŵao ŵaichilile ni ilosyo yakuonechela, nambo nganaŵa ŵanti nikulupilila, iyyoyopeyo niyampaka tujile pakwalipila ŵandu akuleŵa.');
INSERT INTO "silika" VALUES(10,14,'10:14','Kaneka tuntesile jenumanja kuŵa ŵawinjila pa chilambo panyuma pa ŵanganyao kuti tulole yatin’jileje pakupanganya masengo.');
INSERT INTO "silika" VALUES(10,15,'10:15','Soni pati gasoomedwe kukwao ma Ȃya Getu gakupikanika chenene, aŵala ŵangaakwembecheya ya kusimangana Nowe akasatiji: “Ikani najo Qur’an jangaŵaga aji, kapena n’jigalausye.” Jilani: “Nganiyiŵa kukwangu yanti mpaka najigalausye mwakusaka kwangu, ngangukuya ikaŵeje yayikuuwulidwa kukwangu, chisimu une ngogopa ilagasyo ya lisiku lyekulungwa naga nanyosye Ambuje ŵangu.”');
INSERT INTO "silika" VALUES(10,16,'10:16','Jilani: “Allah angasache nga nganinajisooma kukwenu, soni nga nganammanyisya ya jalakweji, mwanti natemi chilikati chenu ndema ja umi welewu nkanijiŵe jalakweji, ana ŵangali lunda?”');
INSERT INTO "silika" VALUES(10,17,'10:17','Ana nduni jwali jwalupuso nnope kumpunda jwansepele Allah unami, kapena kanila ma Ȃya Gakwe? Akuleŵa chisimu chene ngaŵa nkupunda.');
INSERT INTO "silika" VALUES(10,18,'10:18','Soni ni akuigalagatilaga kunneka Allah ichindu yangaŵa nkwapa ipotesi namuno kwakamuchisya, ni akutiji: “Ŵalakweŵa ni akutuŵendela ŵetu kwa Allah.” Jilani: “Ana nkuntagulila Allah yangaakuimanyilila kumawunde namuno petaka? Kuswejela ni Kwakwe soni jutalikangene kusyene ni yaakum’bwanganya nayo.”');
INSERT INTO "silika" VALUES(10,19,'10:19','Soni ŵandu nganaŵa (kalakala pandaŵi ja Adam) ikaŵeje nkutula umpepe (wagalagatila Allah), basi ni ŵalekangene (panyuma pakwe), mwanti likaŵaga liloŵe lyalyalongolele lyakuumila kwa M''buje gwenu (lyangapa ilagasyo yekulungwa nnope ikaŵeje ku Kiyama), chisimu kungajilanyidwe chilikati chao pa ichindu yaŵatindanaga pa yalakweyo.');
INSERT INTO "silika" VALUES(10,20,'10:20','Soni ŵanganyao akutiji: “Nambi uli ngachikutulusyidwa kukwakwe chimanyilo kuumila kwa M’mbujegwe?” Basi jilani: “Chisimu yakusisika nsyene jwakwe ni Allah, sano jembecheyagani, chisimu none jwakwe ndili mu akwembecheya pamo ni jenumanja.”');
INSERT INTO "silika" VALUES(10,21,'10:21','Sano pati twapele kupasya ŵandu ukoto panyuma pa masausyo gagakwayiye, jele ndemajo ŵanganyao akasatanda kugatendela malindi ma Ȃya Getu. Jilani: “Allah nijwachitema pakuwesesya malindi.” Chisimu achimitenga Ŵetu (Achimalaika) akulemba malindi gankutaŵana.”');
INSERT INTO "silika" VALUES(10,22,'10:22','Jwalakwe ni Ajula jwaakunkombolasya ŵanganyammwe kwenda pankuli ni pambwani, kwikanila ndema jankuŵaga mu yombo, nikwendaga nao (yomboyo) ni mbungo jambone, ŵanganyao nikusengwa nayo, (mwatwitu!) mbungo jachimbunga nikuiŵichilila (yomboyo), nikwaŵichilila soni ŵanganyao matumbela kuumila mbali ni mbali, kaneka ŵanganyao nikulupilila yanti asyungulilidwe (ni chonasiko); akasam’bendaga Allah mwakunswejechesya dini Jakwe (pakum’benda Jwelepejo, achitiji): “Naga nkwika mwakutujokola mu yalakweyi chisimu chene chituŵe mu ŵakutogolela.”');
INSERT INTO "silika" VALUES(10,23,'10:23','Nambo pati ŵajokwelemo, papopo akutandaga unampitiku (wakumpitikuchila Allah) pa chilambo mwangaŵajilwa. E achinaŵandu! Chisimu unampitiku wenuo chiun’jonanje mwachimisyene. Kusengwa kwaumi wa duniya (kuli kwannono), kaneka Kukwetu nikwakuujila kwenu, basi nituchintagulila yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(10,24,'10:24','Chisimu chilandanyo cha umi wa duniya chili chisawu mesi (gawula) gatutulwisye kuumila kwinani, basi nikuwanganyichilana ni galakwego mmela wapetaka mu yaakulyaga ŵandu ni ilango, kwikanila ndema jalikujigalaga litaka ipeto yakwe (pakuŵisikala ni mmela) ni kuliisalalisya (ni uluŵa), mwanti achimisyene (litakalyo) niganisya yanti ŵana ukombosi pa lyalakwelyo (wagowola yaapandileyo), chilamusi Chetu chikasayiŵichililaga lwachilo kapena lwamuusi nikuitenda nti chisawu yegungule, mpela nganiyiŵa konyokonyo liso, iyyoyopeyo niyatukuti pakusalichisya ma Ȃya kwa ŵandu ŵakuchetelela.');
INSERT INTO "silika" VALUES(10,25,'10:25','Sano Allah jukuŵilanjila ku Nyumba Jantendele (ku Mbepo) ni akun’jongolela jwansachile kwitala lyagoloka.');
INSERT INTO "silika" VALUES(10,26,'10:26','Akwete kupata aŵala ŵaakolosyisye (chikulupi nimasengo gao malipilo) gambone nnope (Mbepo) kwisa soni yakonjechesya (kum’bona Allah), soni chipi ngasichiunichila ngope syao namuno kunyosyeka, ŵanganyao niŵandu ŵa ku Mbepo, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(10,27,'10:27','Sano aŵala ŵapanganyisye yakusakala, malipilo ga chakusakala gakulandana nichalakwecho, soni kunyosyeka kuchaunichila, ngasakola nkwagosa (kuipotesi yakuumila) kwa Allah, gambakuŵa ngope syao nti chisawu siunichilidwe ni ipande yachilo chachipi, ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(10,28,'10:28','Soni (mwakumbusyani ya) lisiku lituchasonganganya ŵanaose, nituchiti ku aŵala ŵaŵawanganyisye (Allah ni isanamu): “Jimani papopo jenumanja ni akwawanganya ŵenuwo (pakwagalagatila pamo ni Allah), kaneko ni tuchalekanganya chilikati chao (ni Asilamu), sano akwawanganya ŵao ŵala tachiti: “Ŵanganyammwe nganim’ba nkutugalagatila uwwe.”');
INSERT INTO "silika" VALUES(10,29,'10:29','“Basi Allah akukwanila kuŵa Mboni chilikati chetuwe ni jenumanja yanti uwwe chisimu pangali yatwayimanyaga yakutugalagatila kwenu.”');
INSERT INTO "silika" VALUES(10,30,'10:30','Kwalakweko mundu jwalijose tachimanyilila kusyene yajwalongwesye, ni tachiuchisyidwa kwa Allah M’mbuje gwao Jwakuonaonape, ni ichasokonechela ŵanganyao ayila yaŵaliji nkuyisepelela (kuŵa milungu jao).');
INSERT INTO "silika" VALUES(10,31,'10:31','Jilani: “Ana nduni jwaakumpaga lisiki kuumila kwinani ni pasi? Kapena nduni jwakamulile kupikana ni kulola? Soni nduni jwaakukoposyaga chawumi kuumila mu chachiwe nikoposya chachiwe kuumila mu chawumi? Soni nduni jwaakulondesyaga indu yosope?” Tajile: “Allah.” Basi jilani: “Nambi uli jenumanja ngan’jogopa (Jwalakwe)?”');
INSERT INTO "silika" VALUES(10,32,'10:32','Basi Jwalakwejo ni Allah M’mbuje gwenu Jwakuonaonape, ana panyuma pa yakuona pana ine pangaŵaga kusokonechela? Ana nkugalausyidwa chinauli (kuleka kun’galagatila Jwalakwe)?');
INSERT INTO "silika" VALUES(10,33,'10:33','Iyyoyopeyo niyalitite pakusimichika liloŵe lya Ambuje ŵenu pa aŵala ŵannyosyisye (Allah) lyanti ŵanganyao nganaŵa akulupilile.');
INSERT INTO "silika" VALUES(10,34,'10:34','Jilani: “Ana mu akwawanganya ŵenu (ni Allah) pana jwaakutandisyaga gumba iwumbe; kaneko (ili iwile) ni kuiwusyawusya?” Jilani: “Allah ni jwaakutandisyaga gumba iwumbe kaneko ni kuiwusyawusya. Ana nkugalausyidwa chinauli (kuleka litala lyagoloka ni kuya lyesokonechele)?”');
INSERT INTO "silika" VALUES(10,35,'10:35','Jilani: “Ana mu akwawanganya ŵenu (ni Allah) pana jwaakongolelaga ku yakuona?” Jilani: “Allah ni jwaakongolelaga ku yakuona. Ana jwaakongolela ku yakuona ngaŵa nijwali jwakuŵajilwa kunkuya, kapena ajula jwangaŵa nkulipatila chongoko niikaŵeje akajongoledwe? Ana chipali chichi kukwenu? Nkulamula chinauli?”');
INSERT INTO "silika" VALUES(10,36,'10:36','Soni ŵajinji mwa ŵanganyao ngaakukuya ikaŵeje liganichisya pe, chisimu liganichisya ngaŵa nkukamuchisya chilichose pa yakuona. Chisimu Allah ni Nkuyimanya chenene yaakutenda.');
INSERT INTO "silika" VALUES(10,37,'10:37','Soni nganijiŵa Qur’aniji janti nikutungwa ni jwinepe jwangaŵaga Allah, nambo kuti jiyiche kwitichisya ayila yayaliji paujo pakwe (Taurat ni Injil), ni kusalichisya chitabu (malamusi gagalembedwe), jangali kaikasya nkati mwakwe, jityochele kwa M’mbuje jwa iwumbe yosope. ');
INSERT INTO "silika" VALUES(10,38,'10:38','Kapena ŵanganyao akuti: “Jwalakwe (Muhammadi صلى الله عليه وسلم) ni jwaajitunjile?” Jilani: “Basi ikani najo sura jimpepe jakulandana najo (Qur’an), ni mwaŵilanje ŵatinkombole (kwaŵilanga kuti ankamuchisye) ŵangaŵaga Allah nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(10,39,'10:39','Nambo ayikaaniile yanganaisyungula umanyilisi wakwe, namuno kalulila kwakwe nganikwapate. Iyyoyopeyo ni yaŵatite pakanila aŵala ŵapaujo pao. Basi nnole yajatite pakuŵa mbesi jaŵalupuso!');
INSERT INTO "silika" VALUES(10,40,'10:40','Sano ŵane mwa ŵanganyao pana ŵaakujikulupilila jalakweji (Qur’an), nambo soni ŵane mwa ŵanyao pana ŵangakujikulupilila, sano Ambuje ŵenu ni ŵaakwamanya chenene akuwatanganya.');
INSERT INTO "silika" VALUES(10,41,'10:41','Sano naga achinkanilaga jilani: “Une ngwete masengo gangu, nombe jenumanja nkwete masengo genu. Jenumanja ntalikangene nayo yangupanganya, une none ndalikangene nayo yankupanganya.”');
INSERT INTO "silika" VALUES(10,42,'10:42','Soni mwa ŵanganyao mwana ŵaakupikanilaga kukwenu (mwaukoleko ndema jankusooma Qur’an), ana mmwe mpaka mwaapikanasye asiiwa namuno ali ŵangali lunda?');
INSERT INTO "silika" VALUES(10,43,'10:43','Soni mwa ŵanganyao mwana ŵaakunkolondochesyaga meso (mwachalu), ana mmwe mpaka mwaalondolele ŵatatale meso namuno ali ŵangalola?');
INSERT INTO "silika" VALUES(10,44,'10:44','Chisimu Allah jwaangaateenda lupuso lulilose ŵandu, nambo kuti ŵandu achimisyene ni ŵaakulitenda lupuso.');
INSERT INTO "silika" VALUES(10,45,'10:45','Sano lisiku lyatachasonganganya ŵanganyao, (ichiŵa) nti chisawu nganatama (pa duniya) ikaŵeje ndaŵi jimpepe jamuusi. Tachimanyigana chilikati chao (nambo pangali jwatachinkosoŵalila n’jakwe). Pamasile paajonasiche aŵala ŵaŵakaaniile yakusimangana ni Allah, soni nganaŵa ŵakongoka.');
INSERT INTO "silika" VALUES(10,46,'10:46','Soni kanga chitunnosye (mmwe Muhammadi صلى الله عليه وسلم pa duniya pepano) ine mwa ayila (ipotesi) yatukwatetela kapena kumpa chiwa (nkanitunnosye tiyapatepe), basi Kukwetu ni kwakuujila kwao, soni Allah ni Mboni pa yaakutenda.');
INSERT INTO "silika" VALUES(10,47,'10:47','Soni nkutula uliose ukwete ntenga, basi pandema jatachiyika ntenga jwao (ku Kiyama) kuchilamulidwa chilikati chao mwa chilungamiko, soni ŵanganyao ngasatendedwa lupuso.');
INSERT INTO "silika" VALUES(10,48,'10:48','Sano ni akutiji: “Ana chele chilangachi chiyiche chakachi nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(10,49,'10:49','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Une jwangali machili gakulipela isausyo nansyene kapena kulikamuchisya ikaŵeje yasachile Allah. Nkutula uliose ukwete ndema jakwe (jakuwila), pati jiyiche ndema jaojo, basi nganaŵa ajichelewasisye kwakandaŵi namuno kujijanguya.”');
INSERT INTO "silika" VALUES(10,50,'10:50','Jilani: “Ana nkuyiona uli naga ili im’bichilile ilagasyo Yakwe (ya Allah) lwachilo kapena lwamuusi, ana niyapi mu yele (ilagasyoyo) yaakuikulumisya akuleŵa (kuti iyicheje mwachitema)?”');
INSERT INTO "silika" VALUES(10,51,'10:51','“Ana pandema jatiichigwa (ilagasyoyo) ni tinchiikulupilila? (Tinchiwusyidwa kuti): Ana sano (ni nkuikulupilila) kutendaga mwaliji jenumanja n’di kuikulumisya (mwachalu)?”');
INSERT INTO "silika" VALUES(10,52,'10:52','Kaneko kuchiŵechetedwa kwa aŵala ŵaŵalitesile lupuso kuti: “Pasyani ilagasyo yandamo, ana nkulipidwa (inepe) yangaŵaga imwaliji nkupanganya!”');
INSERT INTO "silika" VALUES(10,53,'10:53','Sano ŵanganyao akum’busyaga (mmwe Muhammadi صلى الله عليه وسلم) kuti: “Nambo yalakweyi yakuona?” Jilani: “Eee! Ngwalumbila Ambuje ŵangu, chisimu yalakweyi yakuona, soni jenumanja nganim’ba iyyilepelekasyisye.”');
INSERT INTO "silika" VALUES(10,54,'10:54','Soni ingaŵe kuti mundu jwalijose juŵatesile lupuso jwaakwete yosope ya pa chilambo, jukasachilile kuja kuliombolela nayo, soni tachiligamba chamuntima ndema jatachiyiona ilagasyo, ni kuchilamulidwa chilikati chao mwachilungamiko, soni ŵanganyao ngasatendedwa lupuso.');
INSERT INTO "silika" VALUES(10,55,'10:55','Manyililani yanti chisimu Allah ni Nsyene yosope yaili kumawunde ni petaka. Manyililani yanti chisimu chilanga cha Allah ni chakuonaonape, nambo kuti ŵajinji mwa ŵanganyao ngaakumanyilila.');
INSERT INTO "silika" VALUES(10,56,'10:56','Jwalakwe akupeleka umi ni kupeleka chiwa, soni Kukwakwe chinchiuchisyidwa.');
INSERT INTO "silika" VALUES(10,57,'10:57','E jenumanja achinaŵandu! Pamasile pachim’bichilile chamuko (Qur’an) kuumila kwa M’mbuje gwenu, ni chakuposya (ulwele) wauli m’mitima mwenu, nambo soni chongolo ni chanasa ku ŵakulupilila.');
INSERT INTO "silika" VALUES(10,58,'10:58','Jilani: “Mwaukoto wa Allah ni chanasa Chakwe (apatile yalakweyi), basi kwaligongo lyele asangalale.” Yalakweyo niyambone nnope kupunda (ipanje) yaakusonganganya.');
INSERT INTO "silika" VALUES(10,59,'10:59','Jilani: “Ana nkuyiona uli mu lisiki lyaatulwisye Allah kukwenu, basi jenumanja nillitesile line mu lyalakwelyo kuŵa lya haraam line ni kuŵa lya halaal.” Jilani: “Ana Allah ni jwanchisisye (yalakweyi) kapena nkunsepela Allah unami?”');
INSERT INTO "silika" VALUES(10,60,'10:60','Ana nganisyo sya aŵala ŵaakunsepela Allah unami sili syantiuli ya lisiku lya Kiyama (akuganisya yanti tachikulupuka ku ipotesi)? Chisimu Allah akwete ukoto kwa ŵandu, nambo kuti ŵajinji mwa ŵanganyao ŵangatogolela.');
INSERT INTO "silika" VALUES(10,61,'10:61','Ni ngankuŵaga (mmwe Muhammadi صلى الله عليه وسلم) m’masengo galigose, soni ngankusoomaga mwalakwemo chilichose cha m’Qur’an, soni (jenumanja achinaŵandu) ngankupanganyaga masengo galigose, ikaŵeje Uwwe tukuŵaga Mboni pa jenumanja ndema jankujijilika nago, soni ngachikasasisika kwa Ambuje ŵenu (chindu) chausito wanti mpela lunyilili pasi kapena kwinani, soni namuno chamwana nnope kupunda chalakwecho kapena chakulilapo niikaŵeje chili m’chitabu chakusalichisya (chilichose Al-Lauh-Al-Mahfûz).');
INSERT INTO "silika" VALUES(10,62,'10:62','Manyililani yanti chisimu isyoŵe ya Allah ngasiyiŵa ni woga soni ngasiyiŵa yakudandaula. ');
INSERT INTO "silika" VALUES(10,63,'10:63','Aŵala ŵaŵakulupilile ni kuŵa ni woga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(10,64,'10:64','Akwete kupata ŵanganyao abali jakusengwasya pa umi wa duniya (kupitila mu sagamisi syambone), ni ku Akhera (chakapate yambone), pangali galausya maloŵe ga Allah, kwalakweko ni kupunda kwekulungwa nnope. ');
INSERT INTO "silika" VALUES(10,65,'10:65','Sano gakan’dandaulikasya maloŵe gao (mmwe Muhammadi صلى الله عليه وسلم), chisimu machili ni uchimbichimbi yosope yene ya Allah, Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(10,66,'10:66','Manyililani yanti chisimu Allah ni Nsyene (iwumbe yosope) yaili kumawunde ni yaili petaka, sano aŵala ŵaakuiŵendaga yinepe yangaŵaga Allah ngaakuikuyaga yakuiwanganya (ni Allah), ngakukuyaga ikaŵeje liganichisyape, soni nganaŵa ŵanganyao ikaŵeje nkusepelela unami basi.');
INSERT INTO "silika" VALUES(10,67,'10:67','Jwalakwe ni Ajula jwam’bichile chilo kuti ntulalilejemo, soni muusi (autesile) kuŵa wakuloleka (yindu kukwenu), chisimu mu yalakweyo mwana ilosyo kwa ŵandu ŵakupikanichisya.');
INSERT INTO "silika" VALUES(10,68,'10:68','Ŵanganyao akutiji: “Allah alitendele mwanache.” Kuswejela ni Kwakwe, Jwalakwe ni Jwakusichila kusyene, iŵele Yakwe (yosope) yaili kumawunde ni yaili petaka. Nganinkola ŵanganyammwe umboni uliose pa yalakweyi. Ana nkum’bechetela Allah yangankuimanyilila?');
INSERT INTO "silika" VALUES(10,69,'10:69','Jilani: “Chisimu aŵala ŵaakunsepela Allah unami nganaŵa apundile.”');
INSERT INTO "silika" VALUES(10,70,'10:70','Kusengwa (kwandema jannono chakupate) pa duniya, kaneko Kukwetu ni kwakuujila kwao, soni ni tuchapa kupasya ilagasyo yakupoteka nnope ligongo lya ukafili wao uŵaliji nkutenda.');
INSERT INTO "silika" VALUES(10,71,'10:71','Soni mwasoomelani abali ja Nuhu, ndema jajwatite kwa ŵandu ŵakwe: “E ŵandu ŵangu! Nakuŵaga kukunsakalisya kutama kwangu (nomwe) ni kumbusya kwangu ma Ȃya ga Allah, basi kwa Allah njegamiile, basi songanganyani malindi genu pamo ni akwawanganya ŵenu (ni Allah), soni malindi genugo gakaŵa gakusisika kukwenu, kaneko ni mpitisye chilamusi chenu kukwangu soni nkamba lipesa.”');
INSERT INTO "silika" VALUES(10,72,'10:72','“Nambo naga nkugalauka (kuleka utenga wangu niyenu), basi une ngangun’juga malipilo galigose, malipilo gangu nganigaŵa kwinepe ikaŵeje kwa Allah, soni une namulidwe kuti mbe mwa Asilamu.”');
INSERT INTO "silika" VALUES(10,73,'10:73','Nambo ŵankaaniile. Basi nitwan’jokwele pamo ni ŵaŵaliji najo imo m’chombo, ni twatesile ŵanganyao kuŵa ŵawinjila, ni twamisyisye aŵala ŵaŵakaaniile ma Ȃya Getu, basi nnole mujaŵelele mbesi jaakuteteledwa!');
INSERT INTO "silika" VALUES(10,74,'10:74','Kaneko panyuma pakwe twatumisye achimitenga ku ŵandu ŵao, ni ŵayichilile ni ilosyo yakuonechela, nambo nganaŵa ŵanti ni kuikulupilila yaŵaikanile (ŵane) kala. Iyyoyopeyo ni yatukutiji pakudinda idindo m’mitima mwa akusumba mpika.');
INSERT INTO "silika" VALUES(10,75,'10:75','Kaneko panyuma pao twantumisye Musa ni Haruna kwa Firiauna ni nduna syakwe pamo ni ilosyo Yetu, nambo ŵalikwesyisye nikuŵa ŵandu ŵakuleŵa.');
INSERT INTO "silika" VALUES(10,76,'10:76','Basi ndema jayaichilile yakuona kuumila Kukwetu ŵatite: “Chisimu awu ni usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(10,77,'10:77','Musa jwatite: “Ana jenumanja nkuti iyyo pakuigamba yakuona pati im’bichililemo? Ana awu usaŵi? Soni asaŵi nganaŵa apundile.”');
INSERT INTO "silika" VALUES(10,78,'10:78','Ŵanganyao ŵatite: “Ana mmwe ntuyichilile kuti ntugalausye ku (chikulupi) chatwasimene nacho achatati ŵetu, sano ukulu ni uŵe wajenumanja ŵaŵili m’chilambo (cha Misra chino)? Uwwetu nganituŵa tunkulupilile jenumanja.”');
INSERT INTO "silika" VALUES(10,79,'10:79','Sano Firiauna jwatite: “Ikaninajo kukwangu nsaŵi jwalijose jwakumanyilila chenene (usaŵi).”');
INSERT INTO "silika" VALUES(10,80,'10:80','Basi ndema jaŵaikangene asaŵi, Musa jwaatesile kuti: “Ponyani yankusaka kuponya.”');
INSERT INTO "silika" VALUES(10,81,'10:81','Basi ndema jaŵaponyisye, Musa jwatite: “Yam’biche nayoyitu uŵele usaŵi, chisimu Allah chauwesesye sampano. Chisimu Allah jwangakolosya masengo gaakuwatanganya.”');
INSERT INTO "silika" VALUES(10,82,'10:82','“Soni Allah chailimbikasye yakuona ni maloŵe Gakwe, namuno akuleŵa yachime.”');
INSERT INTO "silika" VALUES(10,83,'10:83','Nambo pangali ŵaŵankulupilile Musa niikaŵeje achachanda ŵa mu ŵandu ŵakwe pakun’jogopa Firiauna ni nduna syao kuti ata kwalagasya. Soni chisimu Firiauna jwaliji jwakulikwesya pa chilambo, soni chisimu jwalakwe jwaliji mu akupunda malile pa konanga.');
INSERT INTO "silika" VALUES(10,84,'10:84','Sano Musa jwatite: “E ŵandu ŵangu! Nam’baga jenumanja munkulupilile Allah, basi Kukwakwe pe jegamilani nam’baga jenumanja Asilamu (ŵakulipeleka kwa Allah).”');
INSERT INTO "silika" VALUES(10,85,'10:85','Basi ŵanganyao ŵatite: “Tujegamiile kwa Allah. E Ambuje ŵetu! Ngasintutenda uwwe kuŵa akulagasyidwa ni ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(10,86,'10:86','“Soni tujokolani ni chanasa Chenu ku ŵandu achimakafili.”');
INSERT INTO "silika" VALUES(10,87,'10:87','Ni twan’juwulile Musa ni nkulugwe (Haruna) yanti: “Mwakolochesyani ŵandu ŵenu m’Misra majumba, nin’gatende majumba genugo kuŵa mwakuswalila (pakuti nganim’ba nkombwele kuswalila m’misikiti), soni jimikani Swala mwakolosya, ni mwape abali jakusengwasya ŵakulupilila.”');
INSERT INTO "silika" VALUES(10,88,'10:88','Sano Musa jwatite: “Ambuje ŵetu! Chisimu Mmwe mumpele Firiauna ni nduna syakwe ipeto ni ipanje pa umi wa duniya, Ambuje ŵetu! (Ŵanganyao akuikamulisya masengo yalakweyo) kuti asochelesye kwitala Lyenu. Ambuje ŵetu! Ijonangani ipanje yao, ni n’jijumusye mitima jao mwanti akakulupilila mpaka ayione ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(10,89,'10:89','Jwalakwe jwaatite: “Duwa jenu ŵanaŵaŵilimmwe (Musa ni Haruna) pamasile pajijanjidwe, basi lungamikani, soni ngasinkuya litala lya aŵala ŵangali chachimanyi.”');
INSERT INTO "silika" VALUES(10,90,'10:90','Nitwajombwesye mbwani ŵanache ŵa Israila, basi Firiauna ni asilikali ŵakwe ŵakuyiye ŵanganyao mwa uwwanga ni umagongo, kwikanila ndema jakunsimene (Firiauna) kumilwa jwatite: “Ngulupilile yanti: Pangali jwakuŵajilwa kun’galagatila ikaŵeje Ajula jwankulupilile ŵanache ŵa Israila, soni une ndili mwa Asilamu.”');
INSERT INTO "silika" VALUES(10,91,'10:91','Ana sano (ninkukulupilila) kutendaga pamasile pamwanyosyisye kala, soni ni mwaliji mu akuwatanganya!');
INSERT INTO "silika" VALUES(10,92,'10:92','Basi lelo chitun’jokole (pakuchichuwula m’mbwani) chiŵiliŵili chenu kuti m’be chilosyo kwa ŵapanyuma penu. Soni chisimu ŵandu ŵajinji akuikwanjila ilosyo Yetu.');
INSERT INTO "silika" VALUES(10,93,'10:93','Soni chisimu chene twakolochesye ŵanache ŵa Israila mauto gakuchimbichika (Shami ni Misra), ni twapele lisiki lyambone, basi ni nganatindana kwikanila ndema jawayichilile umanyilisi. Chisimu M’mbuje gwenu chachijilanya chilikati chao lisiku lya Kiyama pa ichindu yaŵalij nkutindana pa yalakweyo.');
INSERT INTO "silika" VALUES(10,94,'10:94','Sano naga (mmwe Muhammadi صلى الله عليه وسلم) n’di pakaika mu ichindu yatuntuluchisye, basi mwawusyani aŵala ŵaakusooma chitabu (chachatulusyidwe) paujo penu (Taurat ni Injil). Chisimu yakuona pamasile payim’bichilile kuumila kwa Ambuje ŵenu (Allah), basi ngasim’ba mu ŵakaichila.');
INSERT INTO "silika" VALUES(10,95,'10:95','Soni ngasim’ba ata panandi mwa aŵala ŵaakaaniile ma Ȃya ga Allah ntakwika kuŵa mu ŵakwasika.');
INSERT INTO "silika" VALUES(10,96,'10:96','Chisimu aŵala ŵalisimichiche liloŵe lya Ambuje ŵenu (lya ilagasyo) pa ŵanganyao, nganaŵa akulupilile.');
INSERT INTO "silika" VALUES(10,97,'10:97','Namuno chili chaichilile chilosyo chilichose, mpaka ayione ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(10,98,'10:98','Ana wapali nsinda wawakulupilile (panyuma pakuyiona ipotesi) mwanti chikulupi chakwe (pajele ndemajo) ni kuukamuchisya (kuutyosya mu ipotesiyo) pangaŵaga ŵandu ŵa Yunusu? Pandema jaŵakulupilile twatyochesye ilagasyo yakwalusya pa umi wa duniya, ni twasengwasisye kwikanila ndema (jao jakuwila).');
INSERT INTO "silika" VALUES(10,99,'10:99','Soni M’mbuje gwenu angasache wosope ŵali pa chilambo angakulupilile ŵanaose, ana mmwe ni mpaka mwakanganichisye ŵandu kuti mpaka aŵe ŵakulupilila?');
INSERT INTO "silika" VALUES(10,100,'10:100','Soni nganiyiŵa yanti mundu jwalijose ni kulupilila ikaŵeje kwa lisosa lya Allah, ni akuŵikaga ilagasyo yakunyalaya ku aŵala ŵangali lunda (lwakuchetelela ilosyo ya Allah).');
INSERT INTO "silika" VALUES(10,101,'10:101','Jilani: “Nnole, ana ichichi yaili kumawunde ni petaka!” Nambo ilosyo kwisa soni akutetela nganaŵa akamuchisye ku ŵandu ŵangaakukulupilila.');
INSERT INTO "silika" VALUES(10,102,'10:102','Ana ŵanganyao akwembecheya (inepe) yangaŵaga (chonasiko) chakulandana nti chisawu moŵa ga aŵala ŵaŵapite paujo pao? Jilani: “Basi jembecheyani, chisimu none jwakwe ndinamwe mu akwembecheya.”');
INSERT INTO "silika" VALUES(10,103,'10:103','Kaneko chitwakulupusye achimitenga Ŵetu ni aŵala ŵaakulupilile, iyyoyopeyo ili yakuŵajilwa Kukwetu kwakulupusya ŵakulupilila.');
INSERT INTO "silika" VALUES(10,104,'10:104','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “E jenumanja achinaŵandu! Nam’baga jenumanja n’di ŵakaika pa dini jangu (ja Usilamu), basi (manyililani yanti) nganimba nagalagatile aŵala ŵankwagalagatila ŵangaŵaga Allah, nambo une ngun’galagatila Allah Ajula jwaakumpaga chiwa, soni namulidwe kuti mbe mwa akukulupilila.”');
INSERT INTO "silika" VALUES(10,105,'10:105','Soni (salilidwe yanti): “Lungamisyani ngope jenu ku Dini mwakupendechela ku Uujika Wakwe, soni ngasim’ba ata panandi mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(10,106,'10:106','“Soni ngasinchilumbanjila chindu changaŵa nkunkamuchisya namuno kumpa isausyo kunneka Allah, sano naga nkuika mwakutenda (yele), basi chisimu mmwejo chim’be mwa akulitenda lupuso.”');
INSERT INTO "silika" VALUES(10,107,'10:107','Sano Allah naga ali atite ankwayikasye masausyo, basi pangali ŵampaka agakungunukule ikaŵeje Jwalakwe, soni naga ansachilile yambone, basi pangali jwampaka awuwusye ukoto Wakwe, akuwuyikasyaga kwa jwansachile mwa achikapolo Ŵakwe, soni Jwalakwe ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(10,108,'10:108','Jilani: “E jenumanja achinaŵandu! Pamasile paim’bichilile yakuona kuumila kwa M’mbuje gwenu, sano jwaajongweche, basi ni kuti akongoka kwakulikamuchisya nsyene, soni jwasochelele, basi ni kuti akusochelela kwakulikonya nsyene, soni une nganimba nkunnolela jwenu”');
INSERT INTO "silika" VALUES(10,109,'10:109','Soni (salilidwe yanti): “Kuyani yayikuuwulidwa kukwenu, soni pililani mpaka Allah ajilanye, soni Jwalakwe ni Jwambone nnope mu akwilanya (magambo).”');
INSERT INTO "silika" VALUES(11,1,'11:1','Alif–Lâm–Râ . (Achi ni) Chitabu chagachisidwe chenene ma Ȃya gakwe, kaneka ni kugasalichisya chenene kuumila kwa (Allah) Jwalunda lwakusokoka, Jwakusimanya abali syosope.');
INSERT INTO "silika" VALUES(11,2,'11:2','(Atesile yalakweyi) kuti nkagalagatila ikaŵeje Allah, chisimu une kukwenu kuumila Kukwakwe ndili nkutetela soni nkutagulila abali syakusengwasya.');
INSERT INTO "silika" VALUES(11,3,'11:3','Soni ni kuti mum’bendeje chikululuko M’mbuje gwenu kaneko ni kutenda toba Kukwakwe, chansengwasye ni yakusengwasya yambone kwikanila ndema jekolanjidwe (jakuwila), ni tachimpa (ku Kiyama) nsyene ukoto jwalijose ukoto wakwe, nambo naga nkugalauka, basi chisimu une ngun’jogopela ilagasyo ya Lisiku Lyekulungwa.');
INSERT INTO "silika" VALUES(11,4,'11:4','Kwa Allah ni kwakuujila kwenu, soni Jwalakwe pachindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(11,5,'11:5','Manyililani yanti chisimu ŵanganyao akukwinya idali yao kuti an’juŵe Jwalakwe (Allah pakusisa yaili m’mitima mwao), manyililani yanti pandema jaakuliunichilaga nguo syao, Jwalakwe jukumanyililaga yaakusisa ni yaakulosya. Chisimu Jwalakwe ni Nkumanyilila chenene yaili m’mitima.');
INSERT INTO "silika" VALUES(11,6,'11:6','Soni pangali chiumbe chilichose chakuulukutaga petaka ikaŵeje lisiki lyakwe lili kwa Allah, nijukumanyilila liuto lyakwe lyakutamilichika (mun’gongo mwaatatigwe kapena pa duniya) kwisa soni malo gakuchisunjila (m’chitumbo chaachikulugwe kapena m’malembe), yosope ili m’chitabu chakusalichisya (chilichose Al-Lauh-Al-Mahfûz).');
INSERT INTO "silika" VALUES(11,7,'11:7','Soni Jwalakwe ni Ajula juŵagumbile mawunde ni mataka m’masiku nsano nalimo, soni Arishi Jakwe (Chindanda Chaulungu) jaliji pachanya pa mesi, (jwaatesile yalakweyi) kuti an’diije ŵanganyammwe yanti ŵani mwa jenumanja ŵatapanganye masengo gambone nnope. Nambo naga n’di mwasalile yanti: “Chisimu ŵanganyammwe chinchiukulidwa panyuma pakuwa.” Aŵala ŵakufuulu akutiji: “Nganiuŵatu walakweu ikaŵeje usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(11,8,'11:8','Soni naga tuli twaachelewachisye ŵanganyao ilagasyo kwikanila ndema jeŵalanjile, chisimu akutiji: “Chichi chikuntaŵa (ilagasyoyo kuti ikaika)?” Manyililani yanti lisiku lyakwaichilila (ilagasyoyo) nganiyiŵa iŵambasyidwe kukwao, ni ichasyungula ayila (ilagasyo) yaŵaliji nkuyitendela chipongwe.');
INSERT INTO "silika" VALUES(11,9,'11:9','Soni naga tuli tumpele kupasya mundu ukoto kuumila Kukwetu, kaneka nikuutyosya kukwakwe, chisimu jwalakwe akuŵaga jwakwasa ntima, jwakufulu.');
INSERT INTO "silika" VALUES(11,10,'11:10','Soni naga tuli tumpele kupasya ntendele panyuma pa masausyo gagankwayiye, chisimu akasatiji: “Yakusakala indyosile.” Chisimu jwalakwe jukasaŵa jwakusimbwa, jwakuliiti.');
INSERT INTO "silika" VALUES(11,11,'11:11','Ikaŵeje aŵala ŵaapilile nikutendaga yambone, ŵanganyao akwete kupata chikululuko ni malipilo gekulungwa.');
INSERT INTO "silika" VALUES(11,12,'11:12','Sano mmwe (Muhammadi صلى الله عليه وسلم) mwine kuŵa nkuleka ine mu yaikuuwulidwa kukwenu, ni kuŵanidwa nayo chidali chenu ligongo lyanti ŵanganyao akutiji: “Ligongo chichi nganiyitulusyidwa kukwakwe imatiilo ya chipanje, kapena lilaika kwika najo imo?” Chisimutu mmwe ninkutetelape, sano Allah pachindu chilichose juŵele Nkulolela.');
INSERT INTO "silika" VALUES(11,13,'11:13','Kapena ŵanganyao akuti: “Jwalakwe (Muhammadi صلى الله عليه وسلم) nijwaajitunjile (Qur’aniji)?” Jilani: “Basi ikani nasyo sura likumi syakulandana najo nisili syakutungwa, nimwaŵilanje ŵatinkombole (kwaŵilanga kuti ankamuchisye) ŵangaŵaga Allah nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(11,14,'11:14','“Nambo naga akan’jitika ŵanganyammwe (ŵakwaŵilanga kuti ankamuchisyeo), basi manyililani yanti (Qur’anijo) jitulusyidwe mwa umanyilisi wa Allah, ni kuti pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo, ana nambi jenumanja chim’be Asilamu?”');
INSERT INTO "silika" VALUES(11,15,'11:15','Ŵaliose ŵaakusaka umi wa duniya ni ipeto yakwe, chitwakwanichisye (malipilo ga) itendo yao pa duniya pepo, soni ŵanganyao palakwepo ngaaŵa ŵakupungusyidwa (kalikose).');
INSERT INTO "silika" VALUES(11,16,'11:16','Ŵanganyao ni aŵala ŵatakatende ngakola ku Akhera ikaŵeje Moto, soni ijonasiche yaapanganyisye pa duniyapo, ni iŵele soni yakuweseka yaaŵele ali nkutenda.');
INSERT INTO "silika" VALUES(11,17,'11:17','Ana (Muhammadi صلى الله عليه وسلم kapena ŵakulupilila) ŵali pachilosyo chakuonechela (Qur’an) chakuumila kwa Ambuje ŵao, soni mboni jakuumila Kukwakwe (jwali Jibulilu) jikujikuya (Qur’anijo kwisa kujijitichisya, mpaka alandane ni achimakafili)? Kutendaga paujo pakwe (chaiche) chitabu cha Musa (Taurat, chachaliji soni) nnongola ni chanasa (pajele ndemajo)! Ŵanganyao (ŵaali pachilosyo chakuonechelao) akuchikulupilila chalakwe (chitabu cha Qur’anichi, ni tachipata Mbepo), nambo ŵatachikane chele m’mikutula (ja achimakafili), basi Moto nichilanga chao. Sano mmwe ngasim’ba nkukaichila ya chalakwechi, chisimu chalakwechi ni chakuonaonape chakuumila kwa Ambuje ŵenu, nambo kuti ŵandu ŵajinji ngaakukulupilila.');
INSERT INTO "silika" VALUES(11,18,'11:18','Ana soni nduni jwaali jwalupuso nnope kumpunda jwaakunsepela Allah unami? Ŵanganyao taachiyika nao kwa M’mbuje gwao, soni mboni (Achimalaika) sichiti: “Ŵanganyaŵa ni aŵala ŵaŵannambuchisye M’mbuje gwao.” Manyililani yanti! Malweso ga Allah gali pa ŵalupuso.');
INSERT INTO "silika" VALUES(11,19,'11:19','Aŵala ŵaakusiŵilila (ŵandu) petala lya Allah ni kulisachililaga kupombotala, soni ŵanganyao Akhera akuŵa nkujikanila.');
INSERT INTO "silika" VALUES(11,20,'11:20','Ŵanganyao nganaŵa ŵanti nikunnepelekasya (Allah kwapa ipotesi) pachilambo, soni nganakola nkwaagosa jwalijose jwangaŵaga Allah, ilagasyo ichijonjechesyedwa kukwao (ligongo lyakwasochelesya achimijao), nganaŵa nkukombola kupikana (yakuona) soni nganaŵa nkuiwona.');
INSERT INTO "silika" VALUES(11,21,'11:21','Ŵanganyao ni aŵala ŵaalijonasile achimisyene (pakulijinjisya ku Moto), soni ni ichasokonechela ayila yaŵaliji nkuisepelela (kuŵa milungu jao).');
INSERT INTO "silika" VALUES(11,22,'11:22','Pangali kaichila yanti ŵanganyao ku Akhera tachiŵa ŵakwasika nnope.');
INSERT INTO "silika" VALUES(11,23,'11:23','Chisimu aŵala ŵaakulupilile ni kutendaga yambone ni kuujila mwakulinandiya kwa M’mbuje gwao, ŵanganyao niŵandu ŵa ku Mbepo, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(11,24,'11:24','Chilandanyo chamikutula jiŵiliji (makafili ni Asilamu) chili chisawu (mundu) jwangalola soni jwangapikana, ni jwakulolaga soni jwakupikanaga, ana ŵaŵiliŵa mpaka aŵe yalumo (kaŵe kao) nkwalandanyamo? Ana ngankuchetelela?');
INSERT INTO "silika" VALUES(11,25,'11:25','Soni chisimu chene twantumisye Nuhu ku ŵandu ŵakwe (nijwatite): “Chisimu une kukwenu ni nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(11,26,'11:26','“Mwanti ngasin’galagatila (chinepe) ikaŵeje Allah. Chisimu une ngun’jogopela ilagasyo ya Lisiku Lyakupoteka nnope.”');
INSERT INTO "silika" VALUES(11,27,'11:27','Basi achinkulungwa aŵala ŵaŵakanile mwa ŵandu ŵakwe ŵatite: “Ngatukum’bonatu mmwe ikaŵeje mundu mpela uwwewe, soni ngatukum’bonatu kuti ankuyiye ikaŵeje aŵala ŵali ŵapasi petu, (soni ankuyiye) naganisyape (nkanachetelele yenumwe), soni ngatukuuona kukwenu uchimbichimbi ni umbone uliose wakutupunda uwwe, nambo tukun’ganichisya jenumanja kuti ŵaunami.”');
INSERT INTO "silika" VALUES(11,28,'11:28','Jwalakwe jwatite: “E ŵandu ŵangu! Nkuiona uli, nimbaga une ndili pachilosyo chakuonechela chakuumila kwa Ambuje ŵangu, soni ni ambele ukoto (utume) kuumila Kukwakwe, basi ni upupuliganyisyidwe kukwenu (ukotowo), ana uwwe nimpaka tunkanganichisye walakwe (ukotowo) kutendaga jenumanja n’di nkuuchima?”');
INSERT INTO "silika" VALUES(11,29,'11:29','“Soni E ŵandu ŵangu! Ngangun’juga chipanje pa wele (utengau), malipilo gangu nganigaŵa kwinepe ikaŵeje kwa Allah, soni une nganimba naaŵinjile aŵala ŵaakulupilile, chisimu ŵanganyao tachisimangana ni M’mbuje gwao (nikwalipila yambone yao), nambo une ngum’bona jenumanja kuti n’di ŵandu ajinga.”');
INSERT INTO "silika" VALUES(11,30,'11:30','“Soni E ŵandu ŵangu! Ana ŵani ŵampaka anjenjele kwa Allah ndili naaŵinjile ŵanganyao? Ana ngankuganisya?”');
INSERT INTO "silika" VALUES(11,31,'11:31','“Soni une ngangunsalila yanti ngwete imatiilo ya ipanje ya Allah, soni ngangumanyilila yakusisika, soni nganguti chisimu uneji lilaika, soni nganguti kwa aŵala ŵagakwachembulusya meso genu: ‘Allah ngaŵa nkwaapa yambone.’ Allah ni jwaakumanyilila chenene yaili m’mitima mwao. Chisimu une (kwikana mwakuŵecheta yele) pajele ndemajo chimbe mu ŵalupuso.”');
INSERT INTO "silika" VALUES(11,32,'11:32','Ŵanganyao ŵaatite: “E mmwe Nuhu! Chisimu nkanganile nowe, soni ni nchulwiye ukangani waakangana noweo, basi gambani kwikanayo yankutujogoyechesyao nam’baga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(11,33,'11:33','Jwalakwe jwaatite: “Chisimu Allah ni jwampaka ayichenayo kukwenu naga ali asachile, soni jenumanja nganim’ba munnepelekasyisye.”');
INSERT INTO "silika" VALUES(11,34,'11:34','“Soni chamuko changu nganichiŵa chinkamuchisye nantimuno ndili sachile kun’jamuka naaŵaga Allah akusaka kunnechelela kusochelela, Jwalakwe ni M’mbuje gwenu, soni Kukwakwe tinchiuchisyidwa.”');
INSERT INTO "silika" VALUES(11,35,'11:35','Kapena ŵanganyao (achimakafili ŵa pa Maaka) akuti: “Jwalakwe (Muhammadi صلى الله عليه وسلم) ni jwaajitunjile (Qur’aniji)?” Jilani: “Naiŵaga najitunjile une, basi ileŵi yangu (yakutungako) imbate nansyene, nambo une ndalikangene nayo ileŵi yankuleŵa ŵanganyammwe.”');
INSERT INTO "silika" VALUES(11,36,'11:36','Ni yaajuwulidwe kwa Nuhu yanti: “Nganaŵa akulupilile mu ŵandu ŵenu ikaŵeje ŵawowopeo ŵaŵakulupilile kala, basi ngasin’dandaula pa yaaŵele nkutenda.”');
INSERT INTO "silika" VALUES(11,37,'11:37','“Sano panganyani chombo (mwakuloleledwa) ni meso Getu soni mwachiiganyo Chetu, soni ngasimumbechetekasya pa (kwajimila) aŵala ŵalitesile lupuso (yakuleka kwaapa ipotesi), chisimu ŵanganyao ali ŵakumisyidwa.”');
INSERT INTO "silika" VALUES(11,38,'11:38','Nijwaliji nkupanganya chombochila, sano ndaŵi ni katema kaŵaliji achinkulungwa ŵa mu ŵandu ŵakwe ali nkumpitila ŵaliji nkuntenda chipongwe. Jwalakwe jwatiji: “Naiŵaga jenumanja nkututenda chipongwe, basi uwwe nowe chituntende chipongwe mpela yankuti pakutenda chipongwe.”');
INSERT INTO "silika" VALUES(11,39,'11:39','“Basi pangakaŵapa timmanyilile (yanti) nduni jwainyyichilile ilagasyo yatiin’jalusye, nikun’gwila ilagasyo yakwendelechela.”');
INSERT INTO "silika" VALUES(11,40,'11:40','(Yaliji nkutendekwa yalakweyi) kwikanila ndema jalyaiche lilamusi lyetu (lyakwajonanga) ni kuulika pachisisa (mesi gatutumule, chachaliji chilosyo kwa Nuhu chakuŵandichila chigumula), twatite: “Kwesyani mwalakwemo (m’chombo) ntundu uliose (wainyama) iŵili iŵili (chelume ni chekongwe) kwisa soni liŵasa lyenu – ikaŵeje jwalyannongolele liloŵe (lyakuti tachijonasika nikuti ngakwela) – ni (mwakwesye soni) aŵala ŵaakulupilile.” Ninganakulupilila najo imo ikaŵeje ŵannono.');
INSERT INTO "silika" VALUES(11,41,'11:41','Sano (Nuhu) jwatite: “Kwelani mwalakwemo, mu Lina lya Allah kuŵe kwenda kwakwe ni kochela kwakwe. Chisimu Ambuje ŵangu ni Ŵakululuka kusyene, Ŵachanasa channope.”');
INSERT INTO "silika" VALUES(11,42,'11:42','Sambano chalakwe (chombo) chaliji nkwenda nao m’matumbela ganti mpela matumbi. Sano Nuhu ŵam’bilasile mwanache jwakwe jwajwaliji pajikajikape: “E mwanangu! Kwelani nowe, soni ngasim’ba pamo ni achimakafili.”');
INSERT INTO "silika" VALUES(11,43,'11:43','(Mwanache) jwatite: “Chindilile kwitumbi, lingose kumesi.” (Nuhu) jwatite: “Pangali jwagosedwa lelo kuchilamusi cha Allah ikaŵeje jwantendele chanasa.” Sano matumbela nigasiŵilikangenye chilikati chao, basi ni jwaliji mwa akumisyidwa.');
INSERT INTO "silika" VALUES(11,44,'11:44','Nikwaŵechetedwe (yanti): “E mmwe antaka! Milani mesi genu, nombe mmwe aliwunde! Jitaŵani (wula jenu), nigapwele mesi, ni chakwanilisyidwe chilamusi (chachigumula), ni chakochele (chombochila) pe (tumbi lya) Judi, ni kwaŵechetedwe (kuti): “Chonasiko chakunaula chili kwa ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(11,45,'11:45','Sano Nuhu ŵan’gumilile M’mbujegwe achitiji: “Ambuje ŵangu! Chisimu mwanangu juli mu ŵapeŵasa pangu, soni chisimu chilanga Chenu (chakwagosa ŵapeŵasa pangu) chakuonaonape, soni Mmwe ni Ŵakwilanya mwachilungamiko kwaapunda akwilanya wosope.”');
INSERT INTO "silika" VALUES(11,46,'11:46','Jwalakwe jwatite: “E mmwe Nuhu! Chisimu jwalakwe nganijuŵa jwapeŵasa penu, chisimu jwalakwe masengo (gakwe) nganigaŵa gambone, basi ngasimumbuusya Une yanganinkola nayo umanyilisi, chisimu Une ngun’jamuka kuti ntakwika mum’bele mu ŵandu ajinga.”');
INSERT INTO "silika" VALUES(11,47,'11:47','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu une ngulijuya Kukwenu yakum’buusya yanganingola nayo umanyilisi. Sano naga nkangululuchila ni kundendela chanasa chimbe mu ŵakwasika.”');
INSERT INTO "silika" VALUES(11,48,'11:48','Kwaŵechetedwe (yanti): “E mmwe Nuhu! Tulukani mwantendele wakuumila Kukwetu soni upile wejinji uŵe kukwenu ni ku mikutula ja aŵala ŵaali namwe, sano mikutula jine (jakusakala) tuchijisengwasya kaneko jichinkwaya kuumila Kukwetu ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(11,49,'11:49','Syalakwesi (nisine) mu abali syakusisika syatukun’juwulila, nganim’ba nkusimanya mmwejo namuno ŵandu ŵenu nkanijiŵe aji (Qur’an), basi pililani, chisimu mbesi (jambone) jili kwa ŵandu ŵawoga (wakun''jogopa Allah).');
INSERT INTO "silika" VALUES(11,50,'11:50','Sano kwa achina Adi (twantumisye) nnongo n’jao (jwali) Hudu. Jwalakwe jwatite: “E ŵandu ŵangu! Mun’galagatilani Allah, nganinkola ŵanganyammwe nnungu jwine jwangaŵaga Jwalakwe, nganim’ba jenumanja ikaŵeje nkusepelela unami (wakuitenda isanamu kuŵa milungu jenu).”');
INSERT INTO "silika" VALUES(11,51,'11:51','“E ŵandu ŵangu! Une ngangun’juga malipilo pa wele (utengau), malipilo gangu nganigaŵa kwinepe ikaŵeje kwa Ajula Jwaangumbile, ana jenumanja ŵangali lunda?”');
INSERT INTO "silika" VALUES(11,52,'11:52','“Soni E ŵandu ŵangu! Mum’bendani chikululuko M’mbuje gwenu soni tendani toba Kukwakwe, chantuluchisye wula mwawinji, soni chan’jonjechesye machili ku machili genu (gakala), ningasin’galauka soni kuŵa ŵakuleŵa.”');
INSERT INTO "silika" VALUES(11,53,'11:53','Ŵanganyao ŵatite: “E mmwe Hudu! Nganintuichilila ni chilosyo chilichose chakuonechela (chakulosya ya utume wenu), soni uwwe nganituŵa ŵakuleka milungu jetu ligongo lya maloŵe genu, soni uwwe nganituŵa tunkulupilile mmwe.”');
INSERT INTO "silika" VALUES(11,54,'11:54','“Ngatukuŵecheta inepe ikaŵeje yanti jine mwa milungu jetu jinkwembekenye nago mangwengwe.” Jwalakwe jwatite: “Chisimu une ngum’bika Allah kuŵa Mboni, nombe jenumanja soni tendelani umboni yanti une mambalene nayo yankuiwanganya (ni Allah pakuigalagatila),”');
INSERT INTO "silika" VALUES(11,55,'11:55','“Kunneka Jwalakwe. Basi mundendelani malindi ŵanaose (jenumanja pamo ni isanamu yenu), soni nkaamba lipesa (ligongo ngangun’jogopa).”');
INSERT INTO "silika" VALUES(11,56,'11:56','“Chisimu une njegamile kwa Allah M’mbuje gwangu soni M’mbuje gwenu, pangali chinyama chilichose chakuulukutaga ikaŵeje Jwalakwe achikwembekenye liwuo lyakwe (akuchijendesya mwaakusachila), chisimu Ambuje ŵangu ali petala lyagoloka (lyachilungamiko).”');
INSERT INTO "silika" VALUES(11,57,'11:57','“Nambo naga nkugalauka (kuleka yangunsalilayi), basi (niyenu), une pamasile panannunguchisye yandumichisyidwe kukwenu, soni Ambuje ŵangu mpaka ŵaŵiche ŵandu ŵane kuŵa ŵawinjila mmalo mwa jenumanja, soni jenumanja nganim’ba munsawisye Jwalakwe ata pandi, chisimu Ambuje ŵangu chindu chilichose aŵele Nkuchilolela.”');
INSERT INTO "silika" VALUES(11,58,'11:58','Sano ndema jachayiche chilamusi chetu (chakwajonanga achina Adi) twan’jokwele Hudu ni aŵala ŵaŵakulupilile najo imo mwachanasa chakuumila Kukwetu, ni twaajokwele ŵanganyao kuilagasyo yakaka.');
INSERT INTO "silika" VALUES(11,59,'11:59','Sano ŵele ni achina Adi. Ŵakaaniile ma Ȃya ga M’mbuje gwao, ni kunyosya mitenga Jakwe, ni ŵakuyiye chilamusi cha jwalijose jwakulikwesya, jwamakani.');
INSERT INTO "silika" VALUES(11,60,'11:60','Soni ŵakuyisyidwe ni malweso pa duniya pano, ni Lisiku lya Kiyama (tachikuisyidwa nago soni), manyililani yanti chisimu achina Adi ŵankaaniile M’mbuje gwao, manyililani yanti chonasiko chakunaula chaliji kwa achina Adi ŵandu ŵa Hudu.');
INSERT INTO "silika" VALUES(11,61,'11:61','Sano kwa achina Samuda (twantumisye) nnongo n’jao (jwali) Swaleh. Jwalakwe jwatite: “E ŵandu ŵangu! Mun’galagatilani Allah, nganinkola ŵanganyammwe nnungu jwine jwangaŵaga Jwalakwe, Jwalakwe ni juŵan’gumbile jenumanja kuumila mwitaka, ni kuntamika palakwepo, basi mum’bendagani chikululuko kaneko ni kutenda toba Kukwakwe, chisimu Ambuje ŵangu ali pakuŵandika (ni umanyilisi Wakwe), Ŵakwanga (maduwa).');
INSERT INTO "silika" VALUES(11,62,'11:62','Ŵanganyao ŵatite: “E mmwe Swaleh! Chisimu mwaliji ŵakwembecheledwa mwetuwe (kuti m’be mwenye ŵetu) nkaniyiŵe ayi. Ana nkutukanya kwagalagatila aŵala ŵaŵele achatati ŵetu ali nkwagalagatila? Soni chisimu uwwe tuli nchikaiko chekulungwa chakaichila yankutuŵilanjilayi.”');
INSERT INTO "silika" VALUES(11,63,'11:63','Jwalakwe jwatite: “E ŵandu ŵangu! Nkuiona uli, nimbaga une ndili pa chilosyo chakuonechela chakuumila kwa Ambuje ŵangu, soni ni ambele ukoto (utume) kuumila Kukwakwe, ana niŵani ŵampaka anjenjele kwa Allah naga ndili nannyosyisyemo? Basi jenumanja ngankunyonjechesya ikaŵeje chasiko.”');
INSERT INTO "silika" VALUES(11,64,'11:64','“Soni E ŵandu ŵangu! Aji ngamiya ja Allah jiŵele chilosyo kukwenu, basi jilekani jilyeje petaka lya Allah, soni ngasin’jikwaya niyakusakala iliyose itakwika mwayinkwembekenye ilagasyo yakuŵandika.”');
INSERT INTO "silika" VALUES(11,65,'11:65','Basi niŵajisikite. Basi jwalakwe jwatite: “Sengwagani m’milango mwenu kwamoŵa gatatupe. Chele ni chilanga changaŵa nkuchikanila.”');
INSERT INTO "silika" VALUES(11,66,'11:66','Basi ndema jachaiche chilamusi Chetu (chakwajonanga ŵanganyao) twan’jokwele Swaleh ni aŵala ŵaŵakulupilile najo imo kwachanasa chakuumila Kukwetu, ni (twaŵambesye soni) kunchesela wa lyele lisikulyo. Chisimu Ambuje ŵenu ni Ŵakulimbangana Nnope, Ŵamachili gakupunda.');
INSERT INTO "silika" VALUES(11,67,'11:67','Ni wakwembekenye aŵala ŵaŵalitesile lupuso n’gumilo (waipotesi), mwanti ŵachelelwe m’majumba mwao (ali awile) chitindiŵalile.');
INSERT INTO "silika" VALUES(11,68,'11:68','Gamba kuŵa nti chisawu ŵanganyao nganatamejemo. Manyililani yanti chisimu achina Samuda ŵankaaniile M’mbuje gwao, manyililani yanti chonasiko chakunaula chaliji kwa achina Samuda.');
INSERT INTO "silika" VALUES(11,69,'11:69','Soni chisimu chene Achimitenga Ŵetu (ŵachilaika) ŵam’bichilile Ibrahima ni abali jakusengwasya. Ŵanganyao ŵatite: “Ntendele!” Jwalakwe jwatite (pakwanga): “Ntendele!” Basi jwalakwe nganijutamilisya nambo (mwachitemachitema) jwaiche nako kang’ombe kejoche.');
INSERT INTO "silika" VALUES(11,70,'11:70','Nambo ndema jajwagaweni magasa gao gali ngagakuwumbutukuchila kuyakulyao jwaasimonjile ŵanganyao, niwampatile woga wakwajogopa ŵele ŵanyao. Ŵanganyao ŵatite: “Nkajogopa, chisimu uwwe tutumisyidwe ku ŵandu ŵa Lutwi (kukwajonanga).”');
INSERT INTO "silika" VALUES(11,71,'11:71','Sano ŵankwao (Ibrahima ŵali Sara) ŵaliji ali chiimile, basi niŵasechile (pakupikana yachonasiko cha ŵandu ŵa Lutwi ligongo lyakusakasya kwao nnope), basi nitwampele abali jakusengwasya (janti chakole mwanache lina lyakwe) Isihaka, soni panyuma pa Isihaka (chachipata chisukulu chakuŵilanjikwa) Yaakubu.');
INSERT INTO "silika" VALUES(11,72,'11:72','Jwalakwe jwatite: “Kutapana kwangune! Ana timbeleche kutendaga ndili jwanchekulu, nombe ŵankwanguŵa ŵachekulupe? Chisimu ayi niichindu yakusimosya.”');
INSERT INTO "silika" VALUES(11,73,'11:73','(Achimalaikao) ŵatite: “Ana nkulisimonga lilamusi lya Allah? Chanasa cha Allah ni upile Wakwe ili pa jenumanja achinsyene nyumbaji, chisimu Jwalakwe (Allah) ni Jwakulapilidwa kusyesyene, Jwakuchimbichika nnope.”');
INSERT INTO "silika" VALUES(11,74,'11:74','Basi ndema jawantyosile woga Ibrahima ni kum’bichilila abali jakusengwasya, jwatandite kutuchondelela ya ŵandu ŵa Lutwi (kuti tukaajonanga).');
INSERT INTO "silika" VALUES(11,75,'11:75','Chisimu Ibrahima jwaliji jwakutulala ntima, jwakum''benda nnope Allah mwakulinandiya soni jwakuujilaujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(11,76,'11:76','(Ndema jajwachulwiye kuchondelela twansalile kuti): “E Ibrahima! Ilekani yalakweyi, chisimu pamasile pachiyiche chilamusi cha Ambuje ŵenu (chakwajonanga ŵanyao), soni chisimu ŵanganyao chiyayichilile ilagasyo yangaŵa nkuyiusya.”');
INSERT INTO "silika" VALUES(11,77,'11:77','Sano ndema jaŵam’bichilile Lutwi Achimitenga Ŵetu, jwadandawile ya ŵaganyao nikuŵanikwa nao chidali (pakwaajogopela kuti atakutendedwa matanyula, nikusoŵa yakuti kaachenjele, ligongo nganiŵamanya kuti Achimalaika). Jwalakwe ni jwatite: “Lelo nilisiku lyakusausya nnope.”');
INSERT INTO "silika" VALUES(11,78,'11:78','Sano ŵandu ŵakwe ŵam’bichilile ali nkutugala (kwisa kwatenda matanyula achalendo ŵakwe ŵala), kutendaga kala ŵaliji nkutenda soni yakusakala (yanti iyyiyi). Jwalakwe jwatite: “E ŵandu ŵangu! Aŵa ŵanache ŵangu ŵachikongwe (achakongwe ŵam’chilambo muno), ŵanganyaŵa niŵali ŵakuswejela nnope kukwenu (ŵakuŵajilwa kwalomba), basi mun’jogopani Allah, ni ngasimunjigasya nchesela pa achalendo ŵanguŵa, ana mwa jenumanja mwangali mundu jwanganisyo syagoloka?”');
INSERT INTO "silika" VALUES(11,79,'11:79','Ŵanganyao ŵatite: “Chisimu mmwe nkumanyilila kuti uwwe nganitukola chisako chilichose ku ŵanache ŵenu ŵachikongweo, soni chisimu mmwe nkumanyilila yatukusaka.”');
INSERT INTO "silika" VALUES(11,80,'11:80','Jwalakwe jwatite: “Ngaaŵe nakwete machili pa jenumanja, kapena ngajegame palusichi lwakulimbangana (ngakole ŵakungamuchisya, ngalimbene namwe kuti nkaatenda yausakwa achalendoŵa).”');
INSERT INTO "silika" VALUES(11,81,'11:81','(Achimalaika) ŵatite: “E mmwe Lutwi! Chisimu uwwe Achimitenga ŵa Ambuje ŵenu, nganyyichila ŵanganyao (niyakusakala iliyose), basi ŵigulani niliŵasa lyenu mu upande ŵachilo, ni ngasagalauka jwalijose mwa jenumanja (kulola munyuma ndema jakupikana kulindima kwaipotesi), ikaŵeje ŵankwenu, chisimu chiyaapate ŵalakweo yachiyapate ŵanganyao, chisimu chilanga chao kumasikusiku, ana kumasikusiku nganikuŵa dusi?”');
INSERT INTO "silika" VALUES(11,82,'11:82','Basi ndema jachayiche chilamusi Chetu (chakwajonanga) twajipitikwisye (misi ja Sodomu ni Gomora) kwinani kuŵa pasi, pasi kuŵa kwinani, nitwalakatichisye ŵanganyao sakalawe syelongo lyejoche, syakugwa mwakuyana chenene.');
INSERT INTO "silika" VALUES(11,83,'11:83','(Syaliji) syeŵichidwe chimanyilo kwa Ambuje ŵenu (lusakalawe lulilose lwaliji lwana nsyene), ni nganiyiŵa yele (ilagasyoyo) kuti ili kwanaula ni ŵalupuso.');
INSERT INTO "silika" VALUES(11,84,'11:84','Sano (ku ŵandu ŵa) ku Madiyana (twantumisye) nnongo n’jao (jwali) Shuaibu. Jwalakwe jwatite: “E ŵandu ŵangu! Mun’galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe, soni ngasimpungusyaga ipiimo yambale kwisa soni sikelo. Chisimu une ngum’bona ŵanganyammwe kuti n’di pambone, soni chisimu une ngun’jogopela ŵanganyammwe ilagasyo ya Lisiku Lyakunsyungulila (nayo).”');
INSERT INTO "silika" VALUES(11,85,'11:85','“Soni E ŵandu ŵangu! Gumbasyani ipiimo yambale kwisa soni sikelo mwachilungamiko, ni ngasimwapunguchisyaga ŵandu indu yao, soni nkalapwaga pa chilambo pakuwatanganya.”');
INSERT INTO "silika" VALUES(11,86,'11:86','“Sigasiga sya Allah (syannechele panyuma pakwagumbachisya ŵandu ipiimo yao) sili syambone kukwenu nam’baga jenumanja ŵakulupilila (kusyene), soni une nganimba nkunnolela jwenu.”');
INSERT INTO "silika" VALUES(11,87,'11:87','Ŵanganyao ŵatite: “E mmwe Shuaibu! Ana swala syenu nisyasikunnamula yanti uwwe tuyileche yaaŵele nkuigalagataila achatati ŵetu, nikuleka kutenda yatukusaka pa ipanje yetu? Chisimutu mmwe niŵan’di ŵalunda soni ŵanganisyo syagoloka.” (Ŵaŵechete yalakweyi mwakuchindimbula).');
INSERT INTO "silika" VALUES(11,88,'11:88','Jwalakwe jwatite: “E ŵandu ŵangu! Nkuiona uli, nimbaga une ndili pa chilosyo chakuonechela chakuumila kwa Ambuje ŵangu, ni ambele kuumila Kukwakwe lisiki lyambone (ana nalisakasye pakuwanganya ni chipanje chakanyisyidwa)? Soni ngangusaka kutindana namwe pakutenda une yangunkanya jenumanja, ngapagwa chingusaka ikaŵeje kwilanya mwachingombolele, soni nganiuŵa ukombosi wangu utyochele kwinepe ikaŵeje kwa Allah, Kukwakwe njegamiile soni Kukwakwe nguujila (pakutenda toba).”');
INSERT INTO "silika" VALUES(11,89,'11:89','“Soni E ŵandu ŵangu! Kukantendekasya kutindana kwenu none kumpata yanti chisawu yayapatile ŵandu ŵa Nuhu, kapena ŵandu ŵa Hudu, kapena ŵandu ŵa Swaleh, soni ŵandu ŵa Lutwi nganaŵa kwanaula ni jenumanja.”');
INSERT INTO "silika" VALUES(11,90,'11:90','“Ni mum’bendeje chikululuko M’mbuje gwenu soni tendani toba Kukwakwe, chisimu Ambuje ŵangu Ŵachanasa channope, Ŵachinonyelo chejinji.”');
INSERT INTO "silika" VALUES(11,91,'11:91','Ŵanganyao ŵatite: “E mmwe Shuaibu! Uwwe ngatukupikanichisya mu yaijinji yankuŵecheta, soni chisimu uwwe tukum’bona mmwejo kuti nimundu jwangali machili mwetuwe, soni ingaŵaga ŵandu ŵapeŵasa penu chisimu tungansomile maganga, soni mmwe nganim’ba ŵakuchimbichika ata panandi kukwetu.”');
INSERT INTO "silika" VALUES(11,92,'11:92','Jwalakwe jwatite: “E ŵandu ŵangu! Ana ŵandu ŵapeŵasa pangu niŵali ŵakuchimbichika nnope kukwenu kumpunda Allah? Nimumponyisye kunyuma kwa migongo jenu. Chisimu Ambuje ŵangu ichindu yankutenda aŵele Ŵaisyungula (kwaimanya chenene).”');
INSERT INTO "silika" VALUES(11,93,'11:93','“Soni E ŵandu ŵangu! Kamulani masengo yatin''jile paakamula (ni ukombosi wenu wosope), none jwakwe ngukamula (mwangukamulila), sampano timmanyilile kuti nduni jwatiyim’bichilile ilagasyo yatiyin’jalusye, nikuti nduni jwali jwaunami, soni jembecheyani, none jwakwe ngwembecheya namwe imo.”');
INSERT INTO "silika" VALUES(11,94,'11:94','Sano ndema jachaiche chilamusi Chetu (chakwajonanga ŵanganyao) twan’jokwele Shuaibu ni aŵala ŵaŵakulupilile najo imo kwachanasa chakuumila Kukwetu, ni wakwembekenye aŵala ŵaŵalitesile lupuso n’gumilo (waipotesi), mwanti ŵachelelwe m’majumba mwao (ali awile) chitindiŵalile.');
INSERT INTO "silika" VALUES(11,95,'11:95','Gamba kuŵa nti chisawu ŵanganyao nganatamejemo. Manyililani yanti chonasiko chakunaula chaliji (kuŵandu ŵa) ku Madiyana mpela yaŵatite pakuŵa nacho achina Samuda.');
INSERT INTO "silika" VALUES(11,96,'11:96','Soni chisimu chene twantumisye Musa ni isimosimo Yetu kwisa soni maumboni gakuonechela.');
INSERT INTO "silika" VALUES(11,97,'11:97','Kwa Firiauna ni nduna syakwe, basi ŵanganyao ŵakuyiye lilamusi lya Firiauna, sano lilamusi lya Firiauna nganiliŵa lyakongoka.');
INSERT INTO "silika" VALUES(11,98,'11:98','Tachalongolela ŵandu ŵakwe lisiku lya Kiyama ni kwajinjisya ku Moto, kaje kusakala malo gakwinjisyidwa gakwe!');
INSERT INTO "silika" VALUES(11,99,'11:99','Soni ŵakuyisyidwe pa awuno (umi ŵaduniya) ni malweso, soni lisiku lya Kiyama (tachikuyisyidwa soni ni malweso), kaje kusakala ntuka wakupedwa wakwe!');
INSERT INTO "silika" VALUES(11,100,'11:100','Syalakwesi nisine mu abali syamisinda syatukunsimbila (mmwe Muhammadi صلى الله عليه وسلم), jine mwa jele (misindajo) jachiŵela chiimile, nambo jine jasimasiche.');
INSERT INTO "silika" VALUES(11,101,'11:101','Soni nganitwatenda Uwwe lupuso (pakwajonanga) nambo kuti ŵalitesile lupuso achimisyene (pakanila kwao), soni milungu jao ajila jaŵaliji nkujilumbanjila kunneka Allah nganijakamuchisya chilichose ndema jachaichilile chilamusi cha Ambuje ŵenu (chakwajonanga), soni nganijajonjechesya ikaŵeje chonasiko.');
INSERT INTO "silika" VALUES(11,102,'11:102','Soni mwele ni mwaakukwembekanyichisyaga Ambuje ŵenu pati atite ajikwembekanye misinda kutendaga jili jalupuso, chisimu kakwembekanye Kakwe kakasaŵaga kakupoteka nnope, kaukali. ');
INSERT INTO "silika" VALUES(11,103,'11:103','Chisimu mu yalakweyi mwana chichetelelo kwa aŵala ŵaakogopa ilagasyo ya Akhera, lyele nilisiku lyatachisonganganyisyidwa ku lyalakwelyo ŵandu, soni lyele nilisiku lyakutendeledwa umboni (niiwumbe yosope, mwanti ngasichisoŵa anta chimo).');
INSERT INTO "silika" VALUES(11,104,'11:104','Ningatukulichelewasya ikaŵeje kwandema jeŵalanjile (kalape).');
INSERT INTO "silika" VALUES(11,105,'11:105','Lyuŵa lyatilichiyika (lisiku lya Kiyamalyo) ngasaŵecheta mundu jwalijose ikaŵeje kwa lisosa Lyakwe (Allah), basi mu ŵanganyao muchiŵa mwana ŵangalumbana ni ŵambone.');
INSERT INTO "silika" VALUES(11,106,'11:106','Basi aŵala ŵatachiŵa ŵangalumbana nikuti tachiŵa m’Moto, tachikola ŵanganyao mwalakwemo koloma ni kutenda ngwikwi.');
INSERT INTO "silika" VALUES(11,107,'11:107','Tachilonjela mwalakwemo mugachilonjelela mawunde ni mataka, ikaŵeje mutachisachila Ambuje ŵenu. Chisimu M’mbuje gwenu ni Nkupanganya ya jukusaka.');
INSERT INTO "silika" VALUES(11,108,'11:108','Sano aŵala ŵatachiŵa ŵambone nikuti tachiŵa mu Litimbe lya ku Mbepo, tachilonjela mwalakwemo mugachilonjelela mawunde ni mataka, ikaŵeje mutachisachila Ambuje ŵenu; (walakweo ni) ntuka wangamala.');
INSERT INTO "silika" VALUES(11,109,'11:109','Basi mmwe nkaŵa mu kaichila pa yaakuigalagatila ŵanganyao (yanti nganijiŵa milungu jakuona), ngaakugalagatila ikaŵeje mpela yaŵatiji pagalagatila achatatiŵao kala, soni chisimu Uwwe chitwalipile liunjili lyao mwakwana mwangali kupungusya.');
INSERT INTO "silika" VALUES(11,110,'11:110','Soni chisimu chene Musa twampele chitabu, basi ni wapatikene mwalakwemo ukangani, mwanti likaŵaga liloŵe lyalyalongolele lyakuumila kwa Ambuje ŵenu (lyangapa ilagasyo yekulungwa nnope ikaŵeje ku Kiyama) chisimu kungajilanyidwe chilikati chao, soni ŵanganyao ali nchikaiko chekulungwa chakuchikaichila chalakwecho.');
INSERT INTO "silika" VALUES(11,111,'11:111','Soni chisimu Ambuje ŵenu tachalipila ŵaliose mwa ŵanganyao masengo gao mwakwana, chisimu Jwalakwe abali syosope sya yaakutenda aŵele Jwasimanya chenene.');
INSERT INTO "silika" VALUES(11,112,'11:112','Basi lungamikani (mmwe Muhammadi صلى الله عليه وسلم) yantite pakulamulidwa pamo ni aŵala ŵaatesile toba nomwe, soni jenumanja nkasumba mpika. Chisimu Jwalakwe ichindu yankutenda juŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(11,113,'11:113','Ningasimpendechela kwa aŵala ŵaalitesile lupuso utakwika mwaunkwayiye Moto, kutendaga jenumanja n’di nganinkola akun’gosa jwangaŵaga Allah, kaneko jenumanja ningakulupusyidwa.');
INSERT INTO "silika" VALUES(11,114,'11:114','Soni jimikani Swala mwakolosya songa siŵili syamuusi (kundawi ni kwigulo), kwisa soni ndema sine syachilo, chisimu yambone ikutyosyaga yakusakala, chele nichikumbusyo kwa ŵakumbuchila.');
INSERT INTO "silika" VALUES(11,115,'11:115','Soni pililani, chisimutu Allah jwangajonanga malipilo ga ŵakolosya.');
INSERT INTO "silika" VALUES(11,116,'11:116','Anauli nganapatikana m’mikutula jaŵakala ŵenu achinsyene sigasiga syalunda, ŵakanya kuwatanganya pa chilambo, ikaŵeje ŵannonope mwa aŵala ŵatwajokwele mu ŵanganyao? Sano aŵala ŵaŵalitesile lupuso ŵajendelechele mu (yasambi) yaŵaliji nkusengwa nayo, niŵaliji ŵakuleŵa.');
INSERT INTO "silika" VALUES(11,117,'11:117','Sano Ambuje ŵenu nganaŵa ŵanti nikujijonanga misi mwalupuso, kutendaga achinsyene misijo ali ŵakutenda yambone.');
INSERT INTO "silika" VALUES(11,118,'11:118','Soni Ambuje ŵenu angasache angatesile achinaŵandu kuŵa nkutula umpepe (wakuya Usilamu), nambo ngaaleka kuŵa ali nkutindana.');
INSERT INTO "silika" VALUES(11,119,'11:119','Ikaŵeje ŵaŵatendele chanasa Ambuje ŵenu, sano kwaligongo lyele (lya chanasacho) ŵagumbile, soni maloŵe ga Ambuje ŵenu gamasile pagakwanilisyidwe (yanti): “Chisimu tinjijigumbasya Jahannama ni achimajini kwisa soni achinaŵandu wosopeŵene (ŵaŵaliji ŵakusakasya).”');
INSERT INTO "silika" VALUES(11,120,'11:120','Sano yosope yatukunsimbila (mmwe Muhammadi صلى الله عليه وسلم) mu abali sya Achimitenga ili yanti tun’dimbikasye nayo ntima wenu, sano mu aji (Sura ja Qur’aniji) im’bichilile yakuona, chamuko kwisa soni chikumbusyo ku ŵakulupilila.');
INSERT INTO "silika" VALUES(11,121,'11:121','Sano jilani kwa aŵala ŵangakukulupilila: “Panganyani (masengo genu) mwachimpanganyichisye, nombe uwwe tupanganye (mwatupanganyichisye).”');
INSERT INTO "silika" VALUES(11,122,'11:122','“Soni jembecheyani, nombe uwwe tukwembecheya.”');
INSERT INTO "silika" VALUES(11,123,'11:123','Soni Allah ni Nsyene yakusisika ya kumawunde ni petaka, soni Kukwakwe nikwakuujila ichindu yosope, basi mun’galagatilani ni kwegamila Kukwakwe, sano Ambuje ŵenu nganaŵa ŵanti ningaimanya yankutenda ŵanganyammwe.');
INSERT INTO "silika" VALUES(12,1,'12:1','Alif-Lâm-Râ. Aga ni ma Ȃya ga m’Chitabu chakusalichisya.');
INSERT INTO "silika" VALUES(12,2,'12:2','Chisimu Uwwe tujitulwisye jalakwejo kuŵa Qur’an jacharabu kuti jenumanja mpate kumanyilila.');
INSERT INTO "silika" VALUES(12,3,'12:3','Uwwe tukunsimulila (mmwe Muhammadi صلى الله عليه وسلم) abali syambone nnope pakun’juwulila aji Qur’an, ni mwaliji ŵangachimanya chilichose (chakwayana ni Qur’anijo) nkanijiŵe (kuntuluchila).');
INSERT INTO "silika" VALUES(12,4,'12:4','(Kumbuchilani) katema kajwaŵechete Yusufu kwababagwe kuti: “E baba ŵangu! Chisimu une nasiweni kulugono ndondwa likumi kwisa jimo, lyuŵa ni mwesi, naiweni yosopeyo inankusujudila une.”');
INSERT INTO "silika" VALUES(12,5,'12:5','Jwalakwe jwatite: “E mwanangu! Nkaasagamukulila sagamisi syenusyo achakuluŵenu, atakwisa mwantendele malindi, chisimu shetani kwa mundu aŵele mmagongo jwakuonechela.”');
INSERT INTO "silika" VALUES(12,6,'12:6','“Soni iyyoyopeyo Ambuje ŵenu tachinsagula nikun’jiganya gopolela sagamisi, nikwanilisya ukoto Wakwe kukwenu kwisa soni kwiŵasa lya Yaakubu, mpela yaŵatite pakuukwanilisya kalakala kwa achatatiŵenu ŵaŵili Ibrahima ni Isihaka, chisimu Ambuje ŵenu ni Ŵakumanyilila nnope, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(12,7,'12:7','Chisimu mwa Yusufu ni achakulugwe mwaliji mwana ichetelelo kwa akuwusya (ya abali syao).');
INSERT INTO "silika" VALUES(12,8,'12:8','(Kumbuchilani) katema kaŵaŵechete (achakulugwe Yusufu) kuti: “Chisimu Yusufu ni mpwakwe (Binyamini) ali ŵakunonyeledwa nnope kwa baba ŵetu kutupunda uwwe, kutendaga uwwe nilikuga lyamachili. Chisimu baba ŵetu ali nkusoya kwakuonechela.”');
INSERT INTO "silika" VALUES(12,9,'12:9','“Mum’bulagani Yusufu, kapena nkamponye ku chilambo (chakwanaula), kuti jiŵe jenujenupe ngope jababa ŵenu, soni panyuma pa yalakweyo tim’be ŵandu ŵambone (pakutenda toba).”');
INSERT INTO "silika" VALUES(12,10,'12:10','Jwaŵechete nkuŵecheta mwa ŵanganyao kuti: “Kasimum’bulaga Yusufu, nambo nansakaga kutendape chinechakwe gambani kumponya pasi pachisima chakusokoka, channokote ŵane mu ŵandu ŵapaulendo.”');
INSERT INTO "silika" VALUES(12,11,'12:11','Ŵanganyao ŵatite: “E baba ŵetu! Ligongo chichi ngankutukulupilila uwwe pa Yusufu, kutendaga uwwe tukunsachililaga yambone jwalakwe?”');
INSERT INTO "silika" VALUES(12,12,'12:12','“Muŵŵigusyani nowe malaŵi, kuti akasengwe soni akan’gande, soni chisimu uwwe chitukan’gose jwalakwe.”');
INSERT INTO "silika" VALUES(12,13,'12:13','Jwalakwe (Yaakubu) jwatite: “Chisimu une ikundandaulikasya yanti n’jaule najo, soni ngogopa kuti litakuja kun’dya lisogo kutendaga jenumanja n’di munkwaanjiile.”');
INSERT INTO "silika" VALUES(12,14,'12:14','Ŵanganyao ŵatite: “Naliyikaga mwakun’dya lisogo kutendaga uwwe tuli likuga lyamachili, nikuti uwwe pa jele ndemajo tuchiŵa ŵakulepela (ngasituŵa achalume ŵasyene).”');
INSERT INTO "silika" VALUES(12,15,'12:15','Basi ndema jaŵapite najo, ni kamulana yakumponya pasi pachisima chakusokoka (ŵamponyisye), sano Uwwe nitwan’juwulile (Yusufujo maloŵe ganti): “Chisimu mmwe chinchatagulila yachitendo chao chalakwechi, kutendaga ŵanganyao akakumanyilila (yanti mmwejo ni Yusufu jula).”');
INSERT INTO "silika" VALUES(12,16,'12:16','Niŵayikangene kwa baba ŵao lwegulo ali nkulila.');
INSERT INTO "silika" VALUES(12,17,'12:17','Ŵanganyao ŵatite: “E baba ŵetu! Chisimu uwwe twapite kuutenda chilindasi (mpikisano wakuutuka), nitwannesile Yusufu pasyaliji ndundu syetu, basi nilin’dile lisogo; nambo mmwe nganim’ba ntukulupilile namuno kuti tukuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(12,18,'12:18','Niŵaiche najo kansu jakwe jili jepakasye myasi jaunami. Jwalakwe (Yaakubu ndema jajwajiweni kansu kuti jangapapuka) jwatite: “(Ngwamba!) Nambo kuti mitima jenu jinsalalichisye chitendo (chamuntesile n’jenujo). Basi (chatindende une) nikupilila kwambone, soni Allah ni Nkuŵendedwa chikamuchisyo pa yankutangayi.”');
INSERT INTO "silika" VALUES(12,19,'12:19','Ni ŵaiche ŵandu ŵapaulendo, basi ni ŵantumisye nkwatechela mesi jwao, basi ni jwaponyisye ndowa jakwe. Jwalakwe jwatite: “E kusengwa kwangu! Male nchanda!” Niŵansisile mpela katundu jwamalonda, sano Allah jwaimanyaga chenene yaŵaliji nkupanganya ŵanganyao.');
INSERT INTO "silika" VALUES(12,20,'12:20','Niŵansumisye pantengo wannono – makobili gakuŵalanjika - soni ŵaliji ŵakwanjila ya jwalakwe.');
INSERT INTO "silika" VALUES(12,21,'12:21','Sano ajula (Potifala) jwa ku Misra jwaŵansumile (Yusufu) ŵasalile ŵankwakwe kuti: “Munkolochesyani ndamo jakwe, mwine chachitukamuchisya, kapena tugambe kuntenda kuŵa mwanache (jwetu).” Soni iyyoyopeyo niyatwatite pakuntamilichikasya Yusufu m’chilambo (cha Misra), soni kuti tun’jiganye gopolela abali (sya sagamisi). Sano Allah ni Jwakupunda pa itendo Yakwe, nambo ŵandu ŵajinji ngakumanyilila.');
INSERT INTO "silika" VALUES(12,22,'12:22','Sano ndema jaŵauŵiche (Yusufu) ukulu wakwe wakwanila, twampele lunda lwakusokoka ni umanyilisi; soni iyyoyopeyo niyatukutiji pakwalipila ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(12,23,'12:23','Sano aŵala (ŵammasyeto) ŵajwaliji (Yusufu) m’nyumba mwao, ŵannyenjile jwalakwe akakusaka, mwanti ŵaugele milango, nikuŵecheta kuti: “Jisani akuno.” Jwalakwe jwatite: “Ngulijuya mwa Allah (kuti ngatenda yakusakalayo)! Chisimu ŵalakweo (ŵankwenuo) ni abwana ŵangu. Angolochesye ndamo jangu (mwanti nganimba naatesile lupuso), chisimu chene ŵalupuso nganaŵa apundile.”');
INSERT INTO "silika" VALUES(12,24,'12:24','Soni chisimu (ŵammasyetowo) pamasile paŵansachilile (Yusufujo), nombenajo akaŵa kuchiona chilosyo cha Ambujegwe (chakunsiŵilikanganya) akaasachilile (ŵammasyetowo, nambo nganaika mwakwasachilila), mwele nimuyaŵelele, kuti tuntyochesye jwalakwe yakusakala ni yakunyalaya, chisimu jwalakwe juli mwa achikapolo Ŵetu ŵakuswejesyedwa.');
INSERT INTO "silika" VALUES(12,25,'12:25','Sano ni ŵatoposyanile ŵanaŵaŵili wa kunnango, basi (ŵammasyeto) ŵampapwile (Yusufu) kansu jakwe chakunyuma, niŵansimene ŵanaŵaŵilio pannango bwana jwao (ŵammasyetowo - ŵali ŵankwao). (Ŵammasyeto) ŵatite: “Malipilochi kwa mundu jwaasachileje kutenda yakusakala ku ŵankwenu, pangaŵaga kutaŵidwa kapena kupedwa ilagasyo yakupoteka nnope?”');
INSERT INTO "silika" VALUES(12,26,'12:26','(Yusufu) jwatite: “Ŵalakweŵa niŵanyenjileje une ndili ngangusaka.” Sano mboni jakwiŵasa kwao (ŵammasyetowo) jaŵichile umboni ŵanti: “Najiŵaga kansu jakwe jipapulidwe chakumeso, nikuti (ŵammasyetowo) akuŵecheta yakuona, sano jwalakwe (Yusufu) juli mwa ŵandu ŵaunami.”');
INSERT INTO "silika" VALUES(12,27,'12:27','“Nambo najiŵaga kansu jakwe jipapulidwe chakunyuma, nikuti (ŵammasyetowo) akulambusya, sano jwalakwe (Yusufu) juli mwa ŵandu ŵakuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(12,28,'12:28','Basi ndema jaŵajiweni kansu jakwe kuti jipapulidwe chakunyuma ŵatite: “Chisimu yalakweyi ni itagu yenu (jenumanja achimmasyeto), chisimu itagu yenu ili yekulungwa nnope.”');
INSERT INTO "silika" VALUES(12,29,'12:29','“Yusufu! Ikwanjilani yalakweyi. Sano mmwe (ŵankwangu)! Ŵendani chikululuko pa sambi syenu, chisimu mmwe n’di mwa ŵandu ŵakulemwa.”');
INSERT INTO "silika" VALUES(12,30,'12:30','Sano achimmasyeto ŵa mu nsinda ŵatite: “Ŵankwao ŵakuchimbichika (anduna) akunnyenga nchanda jwao jwalakwe akakusaka, chisimu chisako chagumbele nnope, chisimu uwwe tukwaona kuti ali nkusoya kwa kuonechela.”');
INSERT INTO "silika" VALUES(12,31,'12:31','Sano ndema jaŵapikene (ŵankwao anduna) gamba kwao, ŵatumichisye mitenga (kuti ayiche kukuona kusalala kwa Yusufu), ni ŵalinganyichisye malo gakutama chakwegama ni yakulya (yakata ni ipula), niŵampele jwalijose mwa ŵanganyao chipula (kuti akatileje yakulyayo), ni ŵatite (ŵammasyeto ŵala pakunsalila Yusufu): “Kopokani kuja kukwao.” Basi ndema jaŵam’bweni ŵankusisye nnope pa uchimbichimbi (ligongo lyakasalale kakwe), sano (ligongo lyausimongo nnope) ŵaliji nkulikata makono gao (nambo ngapikana kupoteka), niŵatite: “Allah ayitalichisye (yakuŵa jwalakweju mundu)! Aju ngaŵa mundu, aju nganaŵa chinepe ikaŵeje lilaika lyakuchimbichika.”');
INSERT INTO "silika" VALUES(12,32,'12:32','(Ŵankwao anduna) ŵatite: “Basitu aju jenumanja nijwejula jwamungaambiileje jula, chisimu chene une nannyenjile jwele akakusaka, basi ni jwaligosile. Nambo sano naga jukatenda yangunnamulayo, chisimu chene chijuponyedwe muukaidi, soni chijuŵe mwa ŵakunyosyeka.”');
INSERT INTO "silika" VALUES(12,33,'12:33','Jwalakwe jwatite: “Ambuje ŵangu! Ukaidi uli wakunonyeledwa nnope kukwangu kulekangana ni yaakumilanjilayo, naga ngankumambasya itagu yao, chimbendechele kukwao, ni timbe mu ŵandu ajinga.”');
INSERT INTO "silika" VALUES(12,34,'12:34','Basi Ambujegwe ŵan’janjile (duwa jakwe), ni ŵam’bambesye itagu yao, chisimu Jwalakwe (Allah) ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(12,35,'12:35','Kaneka panyuma pakuiona ŵanganyao (anduna ni ŵandu ŵao) ilosyo (yakuswejela kwa Yusufu), syaichilile nganisyo syanti agambe kumponya (Yusufu) mu ukaidi kwandema jannono (iŵe mpela nijuŵaleŵile, pakusaka kwatyosya nchesela ŵammasyeto ŵala).');
INSERT INTO "silika" VALUES(12,36,'12:36','Niŵajinjile najo (Yusufujo) mu ukaidi achachanda ŵaŵili. Jumo jwao jwatite: “Chisimu une sagamile ndili nkuminya ukana.” Sano jwine nijwatite: “Chisimu une sagamile ndili ndwichile mikate pantwe pangu, jinankulidwa ni ijuni. Tutagulilani ngopolelo syakwe, chisimu uwwe tukum’bona kuti n’di jumpepe mu ŵakolosya.”');
INSERT INTO "silika" VALUES(12,37,'12:37','Jwalakwe jwatite: “Nganiiŵa im’bichilile ŵanganyammwe yakulya kuŵa lisiki lyenu, ikaŵeje chimbe ndili nantagulile ngopolelo syakwe nkaniim’bichilile. Ayi niyine mu yanjiganyisye Ambuje ŵangu (Allah). Chisimu une nesile dini jaŵandu ŵangakukulupilila mwa Allah, soni ŵanganyao ya Akhera akuŵa ali nkuikana.”');
INSERT INTO "silika" VALUES(12,38,'12:38','“Ninguyiye dini ja achatati ŵangu, - Ibrahima, Isihaka ni Yaakubu, nganiyiŵa yakuŵajilwa kukwetu kuti tum’bwanganye Allah ni chilichose, yalakweyo (yagalagatila Allah pe) ili mu ukoto wa Allah wauli kukwetu ni kwa ŵandu ŵane, nambo kuti ŵandu ŵajinji ngakutogolela.”');
INSERT INTO "silika" VALUES(12,39,'12:39','“Achimijangu ŵa mu ukaidi! Ana mabwana (milungu) gakulekangana gali chenene, kapena Allah jwali Jumpepe, Jwamachili gangalimbana nago?”');
INSERT INTO "silika" VALUES(12,40,'12:40','“Ngankugalagatila inepe kunneka Jwalakwe ikaŵeje mena gampeleche - jenumanja ni achatati ŵenu - ganganatulusya pa galakwego Allah umboni uliose (wakulosya kuti gelego mena ga milungu jakuŵajilwa kujigalagatila), pangali ulamusi wine ikaŵeje wa Allah pe, (Jwalakwe) alamwile yanti nkasin’galagatila chinepe ikaŵeje Jwalakwe. Jele ni Dini jagoloka, nambo ŵandu ŵajinji ngakumanyilila.”');
INSERT INTO "silika" VALUES(12,41,'12:41','“Achimijangu ŵa mu ukaidi! Sano jumo mwa jenumanja ŵaŵili chaakaamweesyeeje abwana ŵao ukana, nambo jwinejo chaulajidwe mwakomeledwa (pansalaba), ijuni nichiyilyeje pantwe pakwe, chilamusi chilamulidwe kala cha ichindu yankuwusyayi.”');
INSERT INTO "silika" VALUES(12,42,'12:42','Sano jwalakwe (Yusufu) ŵansalile ajula jwaŵammanyi kuti chajokoche mwa ŵaŵilio (yanti): “Nkakumbuchile kungolanga kwa abwana ŵenu (Afiriauna kuti angoposye mu ukaidi muno).” Basi shetani ŵan’diŵesye jwalakwejo kolanga (ya Yusufu) kwa abwana ŵakwe. Basi ni ŵatemi (Yusufu) mu ukaidi yaka igala.');
INSERT INTO "silika" VALUES(12,43,'12:43','Sano nchimwene (Firiauna) jwatite: “Chisimu une sagamile ng’ombe nsano nasiŵili syakwimbala sinankuliidwa ni (ng’ombe) nsano nasiŵili syaganda, ni (nagaweni soni) masache nsano nagaŵili (gatiligu) gamaŵisi, ni gane (nsano nagaŵili) gamajumu. E achinkulungwa! Mungopolelani sagamisi syangusi, nam’baga jenumanja ŵanti nigopolela sagamisi.”');
INSERT INTO "silika" VALUES(12,44,'12:44','Ŵanganyao ŵatite: “Asi ni sagamisi syewanganyichilane syangali mate, soni uwwe yagopolela sagamisi nganituŵa tuimanyi chenene.”');
INSERT INTO "silika" VALUES(12,45,'12:45','Sano ajula juŵajokweche mu ŵaŵili ŵala (ŵanakuŵaga mu ukaidi) jwakumbuchile (chiŵendo cha Yusufu) panyuma pakupita ndema jelewu, ni jwatite: “Une tinansalile ngopolelo syakwe, basi mundumani (kuti ngansosele ngopolelo syakwesyo).”');
INSERT INTO "silika" VALUES(12,46,'12:46','(Jwalakwe jwatite): “Yusufu, mmwe ŵakuonaonape! Tugopolelani yang’ombe nsano nasiŵili syakwimbala syakuliidwa ni (ng’ombe) nsano nasiŵili syaganda, ni masache nsano nagaŵili (gatiligu) gamaŵisi, ni gane (nsano nagaŵili) gamajumu; kuti une mbate kuujila kwa ŵandu, kuti mwine ŵanganyao akapate kumanyilila (ngopolelo syakwe).”');
INSERT INTO "silika" VALUES(12,47,'12:47','(Yusufu) jwatite: “Timpande yaka nsano naiŵili yakuyana, sano yan’gowele basi ilekani m’masache mwakwe pemo, ikaŵeje yannonope yatin’dyeje.”');
INSERT INTO "silika" VALUES(12,48,'12:48','“Kaneka panyuma payalakweyo ichiika (yaka) nsano naiŵili yakunonopa, yatiichiŵaga nkulya yamwailongochesye, ikaŵeje yannonope yatinchiigosaga (kuŵa yambeju).”');
INSERT INTO "silika" VALUES(12,49,'12:49','“Kaneko panyuma payele chichiika chaka chachachipedwa ŵandu mwalakwemo ula jejinji, soni mwalakwemo tachiminyaga (isogosi ya ukana ni mauta).”');
INSERT INTO "silika" VALUES(12,50,'12:50','Sano nchimwene jwatite: “Ikani najo kukwangu.” Nambo jwantenga paŵam’bichilile, (Yusufu) jwatite: “Ujilani kwa abwana ŵenu, ni nkaawusye kuti: ‘Kali uli kaŵe ka achimmasyeto aŵala ŵanakulikataga makono gao?’ Chisimu Ambuje ŵangu yaitagu yao akuimanya chenene.”');
INSERT INTO "silika" VALUES(12,51,'12:51','(Nchimwene) jwatite (kwa achimmasyeto): “Ana abali jenu jaliji uli pandema jamwannyengaga Yusufu jwalakwe akakusaka?” Ŵanganyao ŵatite: “Allah ayitalichisye (yaulemwa wa Yusufu)! Pangali yakusakala iliyose yatwaimanyi kukwakwe.” Ŵankwao ŵakuchimbika (anduna) ŵatite: “Sambano nipaimanyukwiche kusyene yakuona. Une nijunannyengaga jwalakwe akakusaka, soni jwalakwe chisimu chene juli mwa ŵandu ŵakuonaonape.”');
INSERT INTO "silika" VALUES(12,52,'12:52','(Yusufu jwatite): “Yalakweyi (yakwawusya kaje achimmasyetowo, ndendele) kuti (anduna) amanyilile yanti une nganinatenda lupuso pakasyepela, nikuti Allah jwangaijongolela itagu ya ŵalupuso.”');
INSERT INTO "silika" VALUES(12,53,'12:53','“Soni nganguliswejesya ntima, chisimu ntima uliose uli wakuchisyachisya yakusakala, ikaŵeje waawutendele chanasa Ambuje ŵangu. Chisimu Ambuje ŵangu ni Ŵakululuka kusyene, Ŵachanasa channope.”');
INSERT INTO "silika" VALUES(12,54,'12:54','Sano nchimwene jwatite: “Ikani najo kukwangu kuti nantende kuŵa jwangujwangupe.” Basi paŵaŵechetene najo, (nchimwene) jwatite: “Chisimu mmwe lelo kukwetu n’di ŵakuchimbichika nnope soni ŵakulupichika kusyene.”');
INSERT INTO "silika" VALUES(12,55,'12:55','(Yusufu) jwatite: “Mumbikani kuŵa nkulolela jwa imatiilo ya chilambo (cha Misra), chisimu une ni nkugosa chenene, jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(12,56,'12:56','Soni iyyoyopeyo twampele Yusuf uchimbichimbi m’chilambo (cha Misra), jwaliji nkutama mwalakwemo palipose pajusachile, tukumpaga ukoto Wetu jwatunsachile, soni ngatukasajonanga malipilo gaŵakolosya (masengo).');
INSERT INTO "silika" VALUES(12,57,'12:57','Soni chisimu malipilo ga ku Akhera nigagali gambone nnope kwa aŵala ŵaakulupilile nikuŵaga ali niwoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(12,58,'12:58','Ni ŵaiche achakulugwe Yusufu (kuupala yakulya), basi ni ŵajinjile kukwakwe, mwanti jwalakwe ŵamanyilile kutendaga ŵanganyao akakummanyilila.');
INSERT INTO "silika" VALUES(12,59,'12:59','Sano pajwaapimiile mwakwana chenene yakulya yao, jwatite: “Nkaiche najo kukwangu mpwenu jwa kuchibaba kwenu (Binyamin), ana ngankuiwona yanti une ngugumbasya chipiimo, soni kuti une nijwambone nnope mwa akupochela achalendo?”');
INSERT INTO "silika" VALUES(12,60,'12:60','“Nambo naga kasim’bika najo kukwangu, nikuti ngasinkola chipiimo chilichose (cha yakulya) kukwangu, soni ngasin’jisa kumandichila.”');
INSERT INTO "silika" VALUES(12,61,'12:61','Ŵanganyao ŵatite: “Chitukalinjelinje kwanyenjelela babagwe ya jwalakwejo, chisimu uwwe chitukatende (yalakweyo).”');
INSERT INTO "silika" VALUES(12,62,'12:62','Sano jwalakwe jwasalile achiboi ŵakwe kuti: “Ŵiikaani chipanje chao (chaasuumiile yakulyayo) m’misigo jao kuti akapate kuchimanyilila pataujile kumaŵasa gao, kuti mwine takauje soni.”');
INSERT INTO "silika" VALUES(12,63,'12:63','Basi ndema jaŵaujiile kwa baba ŵao ŵatite: “E baba ŵetu! Tukajimidwe chipiimo (chayakulya ikaŵeje tuli tupite ni mpwetuju), basi muŵŵigusyani nowe mpwetu, kuti tukapate kupimilidwa (chipiimo chakwana), soni chisimu uwwe chitukan’gose jwalakwe.”');
INSERT INTO "silika" VALUES(12,64,'12:64','(Yaakubu) jwatite: “Ana mpaka nankulupilile jenumanja pa jwalakwe kupunda yanatite pakunkulupilila pa nkulugwe kalakala (jwamwansoŵesye)? Nambo Allah nijwali Jwambone nnope paagosa, soni Jwalakwe ni Jwachanasa channope kwapunda ŵachanasa wosope.”');
INSERT INTO "silika" VALUES(12,65,'12:65','Sano katema kaŵagopwele misigo jao, ŵachisimene chipanje chao (chaŵaasuumiile yakulya ila) kuti chiusyidwe kukwao. Ŵanganyao ŵatite: “E baba ŵetu! Tulajile soni chichi (paukoto wa mwenye kupunda awu)? Achi chipanje chetu chila chiusyidwe kukwetu; mwanti chitukagapalile maŵasa getu, soni mpwetujo chitukan’gose, ni chitukajonjechesye soni chipiimo chansigo wangamiya jantundu, chalakwecho chiŵele chipiimo chakwepepala (kwa mwenyeo).”');
INSERT INTO "silika" VALUES(12,66,'12:66','(Yaakubu) jwatite: “Nganimba naŵŵigwisye nomwe, mpaka nkaambe kaje chilanga chakulumbilila mwa Allah kuti chisimu tinchim’busya kukwangu, ikaŵeje naga n’di nsyungulilidwe (ni amagongo).” Basi paŵampele chilanga chao, jwatite: “Allah ni Mboni pa yatukuŵechetayi.”');
INSERT INTO "silika" VALUES(12,67,'12:67','Nijwatite: “E achiŵanangu! Nkaja kwinjilila nnango umpepe, nambo nkajinjilile milango jakulekangana, soni nganimba nankamuchisye ku chilichose chakuumila kwa Allah, pangali ulamusi wine ikaŵeje wa Allah, Kukwakwe pe njegamiile, soni Kukwakwe pe ajegamile akwegamila.”');
INSERT INTO "silika" VALUES(12,68,'12:68','Sano ndema jaŵajinjile ŵanganyao yaŵatite pakwapanga baba ŵao, nganiiŵa yakwakamuchisya ŵanganyao ku chilichose chakuumila kwa Allah, pangaŵaga chisako chachaliji mu ntima mwa Yaakubu nichiŵachikwanilisye (Yaakubu chakusaka kwaŵambasya ku makungu). Soni chisimu jwalakwe (Yaakubu) jwaliji ni umanyilisi ligongo lyakun’jiganya Kwetu, nambo kuti ŵandu ŵajinji ngakumanyilila.');
INSERT INTO "silika" VALUES(12,69,'12:69','Sano katema kaŵajinjile kwa Yusufu, jwalakwe ŵankumbatile mpwakwe (ni) jwatite: “Chisimu une ninankulugwenu, basi ngasin’dandaula pa yaŵele ali nkutenda ŵanganyaŵa.”');
INSERT INTO "silika" VALUES(12,70,'12:70','Basi pajwaapimiile mwakwana chenene yakulya yao, jwaŵisile mbiisi (chipiimo chakupimila yakulya) mu nsigo wampwakwe, kaneka nijwagumisile nkugumila kuti: “E jampingo! Chisimu ŵanganyammwe ŵawiyi.”');
INSERT INTO "silika" VALUES(12,71,'12:71','Ŵanganyao ŵatite – aku achigalauchilaga kukwao: “Ana chichi chikusoŵa kukwenu?”');
INSERT INTO "silika" VALUES(12,72,'12:72','Ŵanganyao ŵatite: “Jikutusoŵa mbiisi jamwenye, sano jwataiche najo chapate (malipilo ga) nsigo wangamiya jantundu, (Yusufu jwatite): ‘Soni une ninkwimilila pa yalakweyi (yakupeleka gele malipilogo).”');
INSERT INTO "silika" VALUES(12,73,'12:73','Ŵanganyao ŵatite: “Tukunnumbila Allah! Chisimu pamasile panyyimanyi kuti uwwe nganituika kukuwatanganya m’chilambo, soni nganituŵa ŵawiyi.”');
INSERT INTO "silika" VALUES(12,74,'12:74','Ŵanganyao ŵatite: “Ana malipilo gakwe (jwawiyijo) chigaŵe gapi, nam’baga jenumanja ŵaunami?”');
INSERT INTO "silika" VALUES(12,75,'12:75','Ŵanganyao ŵatite: “Malipilo gakwe tigaŵe kuti; jwajisimaniiche (mbiisijo) mu nsigo wakwe, basi jwele nijuchaŵe malipilo gakwe (pakujigalidwa kuŵa kapolo ni kulagasidwa pa wiyi wakwe), iyyoyopeyo niyatukutiji pakwalipila ŵalupuso.”');
INSERT INTO "silika" VALUES(12,76,'12:76','Basi jwalakwe (Yusufu) jwatandite (kuungunya-ungunya) m’misaku jao nkaniuŵe nsaku wampwakwe, kaneka nijwajikopwesye munsaku wampwakwe, mwele nimutwatite pakun’dinganyichisya itagu Yusufu (yakun’jigalila mpwakwe). Mwachilamusi cha nchimwene (jwachilambocho) nga nganan’jigala mpwakwejo (mpela kapolo), ikaŵeje mwalisosa lya Allah (pakwatendekasya achakulugwe kukuya chilamusi chakumangwao chakun’jigala ukapolo jwawiyi). Tukunnyakulaga pa uchimbichimbi jwatunsachile, soni pachanya pa jwalijose jwaakwete umanyilisi pana jwakumanyilila nnope (kumpunda jwalakwejo).');
INSERT INTO "silika" VALUES(12,77,'12:77','Ŵanganyao ŵatite: “Naajiŵaga jwalakweju, basi (nganiiŵa yachimamo) ngati pakwete pajiŵile nkulugwe kala.” Basi Yusufu jwaisisile yalakweyi mu ntima mwakwe ninganailochesya kukwao. Jwatite: “Jenumanja nkwete ndamo jakusakala nnope, soni Allah ni Nkuimanya chenene yankusimulilayi.”');
INSERT INTO "silika" VALUES(12,78,'12:78','Ŵanganyao ŵatite: “E wao ŵakuchimbichika (anduna)! Chisimu jwalakweju akwete atatigwe ŵachekulupe nnope (ŵatakan’dandaulile nnope); basi jigalani jumo mwetuwe mmalo mwa jwalakwejo; chisimu uwwe tukwaona wao kuti ali mwa ŵakolosya.”');
INSERT INTO "silika" VALUES(12,79,'12:79','Jwalakwe jwatite: “Tukulijuya mwa Allah ku yanti tun’jigale jwinepe jwangaŵaga jwatuchisimene chipanje chetu kwa jwalakwejo, chisimu uwwe pajele ndemajo (tuchijigalaga jwinepe) chituŵe ŵalupuso.”');
INSERT INTO "silika" VALUES(12,80,'12:80','Basi katema kaŵakatile tama ya jwalakwe, ŵapite pakasyepela kuntemela. Jwankulungwa jwao jwatite: “Ana ngankuimanyilila yanti baba ŵenu ŵajigele kwa jenumanja chilanga chakulumbilila mwa Allah (chakuti tinchim’busya jwalakwejo kukwakwe)? Soni nkaniiŵe ayi mwalepele kunchengeta Yusufu. Basi ngangopoka m’chilambo muno mpakana akambe lusa baba, kapena Allah anamule (yakopoka pakungombolasya kun’jigala mpwangajo), soni Jwalakwe ni Jwambone nnope mwa akulamula.”');
INSERT INTO "silika" VALUES(12,81,'12:81','“Ujilani kwa baba ŵenu, ninkajile: ‘E baba ŵetu! Chisimu mwanagwenu ajiŵile, soni uwwe nganitutendela umboni ikaŵeje pa yatwaimanyi (yakuti mwetuwe mwangali jwawiyi), soni nganituŵa uwwe ŵakuimanya yakusisika (yanti Binyamin kuŵa nkwiŵa).”');
INSERT INTO "silika" VALUES(12,82,'12:82','“Soni uwusyani musi (wa Misra) watwalijimo, ni nkutula watwaliji nao pakuwuja, soni chisimu uwwe tukuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(12,83,'12:83','(Yaakubu) jwatite: “(Ngwamba!) Nambo kuti mitima jenu jinsalalichisye chitendo chinechakwe. Basi (chatindende une) nikupilila kwambone, mwine Allah kuŵa nkwikanao kukwangu wosopeŵene, chisimu Jwalakwe ni Jwakumanyilila nnope, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(12,84,'12:84','Sano jwalakwe jwatundumalile ŵanganyao nijwatite: “Ha, kudandaula kwangune ya Yusufu!” Nigatatele meso gakwe ligongo lyamadandausi, basi nijwaliji jwakusisa nnope (madandausi gakwego).');
INSERT INTO "silika" VALUES(12,85,'12:85','Ŵanganyao ŵatite: “Tukunnumbila Allah, mmwe nganneka kuŵa n’di nkunkumbuchila Yusufu mpaka chim’be ŵangali machili ligongo lyakulwalika ni uchekulu, kapena kuŵa mu ŵakonasika (pakuwa).”');
INSERT INTO "silika" VALUES(12,86,'12:86','Jwalakwe jwatite: “Chisimu ngupacha madandausi gangu ni kulikusika kwangu kwa Allah, soni ningumanyilila kuumila kwa Allah yangankuimanyilila ŵanganyammwe.”');
INSERT INTO "silika" VALUES(12,87,'12:87','“E achiŵanangu! Jaulani kupita nchiwusyawusya ya Yusufu ni mpwakwe, ningasin’jasa ntima pachanasa cha Allah, chisimu chene pangali jwaakwasaga ntima pachanasa cha Allah ikaŵeje ŵandu makafili.”');
INSERT INTO "silika" VALUES(12,88,'12:88','Basi katema kaŵajinjile ŵanganyao kwa jwalakwe (Yusufu), ŵatite: “E wao ŵakuchimbichika (anduna)! Masausyo gatukwaiye uwwe ni maŵasa getu, nituyiche ni chipanje changali mate, basi tugumbachisyani chipiimo (namuno kuti chipanje chetucho chili changali mate), soni tupani sadaka, chisimu Allah akwalipilaga akutola sadaka.”');
INSERT INTO "silika" VALUES(12,89,'12:89','Jwalakwe jwatite: “Ana nkuimanyilila yamwantendele Yusufu ni mpwakwe pandema janti ŵanganyammwe n’di mu ujinga?”');
INSERT INTO "silika" VALUES(12,90,'12:90','Ŵanganyao ŵatite: “Ana mmweji yakuona niYusufu jula!” Jwalakwe jwatite: “Une niYusufu jula, soni aju mpwanga jula. Chisimu Allah atutendele ukoto uwwe. Chisimu jwataŵe ni woga (wakun’jogopa Allah) ni kupilila, basi chisimu Allah jwangajonanga malipilo gaŵakolosya (masengo).”');
INSERT INTO "silika" VALUES(12,91,'12:91','Ŵanganyao ŵatite: “Tukunnumbila Allah! Chisimu Allah pamasile pansagwile mmwe kuŵa pachanya petu, soni chisimu uwwe twaliji ŵakulemwa kusyene.”');
INSERT INTO "silika" VALUES(12,92,'12:92','Jwalakwe jwatite: “Pangali kun’jamuka lelo, Allah chankululuchile, soni Jwalakwe ni Jwachanasa channope kwapunda ŵachanasa wosope.”');
INSERT INTO "silika" VALUES(12,93,'12:93','“Jaulani ni kansu janguji basi ninkajiponye pangope jababa ŵangu ni chakatande kulola, ni nkambichilile ni maŵasa genu gosopegene.”');
INSERT INTO "silika" VALUES(12,94,'12:94','Sano ndema jawaŵigwile nkutula (kuujila kumangwao), baba ŵao (ŵali Yaakubu ŵaŵaliji pa jele ndemajo ku Shami) ŵatite: “Chisimu une ngupikana luumba lwa Yusufu, ingaŵaga kuti nkuunganichisya yakusokonechela lunda (nkangulupilile).”');
INSERT INTO "silika" VALUES(12,95,'12:95','Ŵanganyao ŵatite: “Tukunnumbila Allah! Chisimutu mmwe n’di mu kusoya kwenu kwakala (kwakunnonyela nnope Yusufu).”');
INSERT INTO "silika" VALUES(12,96,'12:96','Basi ndema jajwaiche nkujigala abali jakusengwasya, jwajiponyisye (kansu ja Yusufu) pangope jakwe (ja Yaakubu), basi papopo ŵatandite soni kulola. Ŵatite: “Ana nganimbecheta kukwenu yanti une ngumanyilila kuumila kwa Allah yangankuimanyilila jenumanja?”');
INSERT INTO "silika" VALUES(12,97,'12:97','Ŵanganyao ŵatite: “E baba ŵetu! Tuŵendelani chikululuko pa sambi syetu. Chisimu uwwe twaliji ŵakulemwa.”');
INSERT INTO "silika" VALUES(12,98,'12:98','Jwalakwe jwatite: “Chinam’bendele kanyumakano chikululuko kwa Ambuje ŵangu. Chisimu Jwalakwe ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(12,99,'12:99','Basi ndema jaŵajinjile ŵanganyao kwa Yusufu, jwalakwe jwaakumbatile achinangologwe ni jwatite: “Jinjilani m’Misra, kwalisosa lya Allah, mwantunjelele.”');
INSERT INTO "silika" VALUES(12,100,'12:100','Ni ŵakwesisye achinangologwe pa chindanda (chakwe chaumwenye), basi wosope niŵasenjelemukwiche kunsujudila. Sano (Yusufu) jwatite: “E baba ŵangu! Asi ni ngopolelo sya sagamisi syangu syakalakala, chisimu Ambuje ŵangu asitesile kuŵa syakuona, soni chisimu Jwalakwe jwandendele une yambone, mwanti jwangopwesye mu ukaidi, ni kwikanamwe kuumila kuchimisi, panyuma panti shetani jwawatanganyisye (pakupanda utinda) chilikati changune ni achakuluŵangu. Chisimu Ambuje ŵangu ni Akupanga mipango ja ichindu yaakuisaka. Chisimu Jwalakwe ni Jwakumanyilila nnope, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(12,101,'12:101','“Ambuje ŵangu! Chisimu mumbele une umwenye, ni kunjiganya gopolela sagamisi, (E Mmwe) Nkugumba mawunde ni mataka! Mmwe ni Nkungosa jwangu pa duniya (pano) ni ku Akhera kwakwe, mumbe chiwa ndili Nsilamu, soni ni nkaasimanganye ni ŵandu ŵambone.”');
INSERT INTO "silika" VALUES(12,102,'12:102','Asi ni sine mu abali syakusisika syatukun’juwulila (mmwe Muhammadi صلى الله عليه وسلم). Soni nganim’ba nao imo pandema jaŵasimichisyaga chitendo chao (chakumponya Yusufu m’chisima) aku ali nkutenda malindi.');
INSERT INTO "silika" VALUES(12,103,'12:103','Soni ŵandu ŵajinji nganaŵa akulupilile namuno nkasachilile chantiuli.');
INSERT INTO "silika" VALUES(12,104,'12:104','Soni ngankwajuga pa yalakweyi malipilo galigose, nganiyiŵatu yalakwe (yam’biche nayoyi) ikaŵeje chikumbusyo ku iwumbe yosope.');
INSERT INTO "silika" VALUES(12,105,'12:105','Ana soni ilingwa-ilingwa mu ilosyo (yakulosya kuti Allah apali) yaili kumawunde ni petaka yaakuŵa ali nkuipita, kutendaga ŵanganyao yalakweyo ali nkuikwanjila!');
INSERT INTO "silika" VALUES(12,106,'12:106','Soni ŵajinji mwa ŵanganyao ngaakunkulupilila Allah ikaŵeje aku ali nkuwanganya (ni isanamu).');
INSERT INTO "silika" VALUES(12,107,'12:107','Ana akuliŵika pantunjelele wanti ngaŵa nkwayichilila chakwaunichila kuumila mu ilagasyo ya Allah, kapena kwaichilila Kiyama mwachisupuchisya kutendaga ŵanganyao akakumanyilila?');
INSERT INTO "silika" VALUES(12,108,'12:108','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ali ni litala lyangu; nguŵilanjila kwa Allah mwa umanyilisi usyesyene; une ni ŵanguyiye, soni kuswejela ni Kwakwe Allah, soni une nganimba mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(12,109,'12:109','Soni nganitutumisyeje (ntume jwalijose) paujo penu ikaŵeje achalume ŵatwaliji nkwajuwulila (utenga) mwa achimisyene misinda. Ana ngakwendajenda (achimakafilio) pachilambo nikupitaga nchilola yajatite kuŵa mbesi ja aŵala (ŵangalumbana ŵaŵaliji) paujo pao? Sano nyumba ja Akhera nijajili jambone nnope kwa aŵala ŵali ni woga (wakun’jogopa Allah). Ana jenumanja ngankuitaga mulunda?');
INSERT INTO "silika" VALUES(12,110,'12:110','Kwikanila ndema jaŵakataga tama Achimitenga (yachikulupi cha makafili), ni ganisya yanti ŵanganyao akanidwe (ni ŵandu ŵao), chikamuchisyo Chetu ni pachayichililaga. Basi ni ŵajokoledwaga ŵatwasachile. Soni ilagasyo Yetu ngaikasausyidwaga kwa ŵandu akuleŵa.');
INSERT INTO "silika" VALUES(12,111,'12:111','Chisimu mwa abali syaosi mwana misyungu kwa ŵakwete lunda. Nganijiŵa (Qur’aniji) adisiadisi jakulukanya, nambo jiŵele jakwitichisya (itabu ya Allah) ayila yaili paujo pakwe (mpela Taurat ni Injil), ni kusalichisya mwagopolanya chenene chilichose (chakusosekwa mu Dini), ni (jiŵele soni) chongolo ni chanasa kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(13,1,'13:1','Alif-Lâm-Mîm-Râ. Aga ni ma Ȃya ga m’Chitabu (cha Qur’an). Sano ayila yaitulusyidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) kuumila kwa Ambuje ŵenu niyakuonaonape; nambo kuti ŵandu ŵajinji ngaakukulupilila.');
INSERT INTO "silika" VALUES(13,2,'13:2','Allah ni Ajula juŵanyakwile mawunde mwangali misati jankujiona; kaneka ni jwatenjele pa Arishi (Chindanda Chaulungu). Ni alijepepesye lyuŵa ni mwesi, chilichose (mwa yalakweyi) chikwenda (mwakwendelechela) mpaka pandema jekolanjidwe. Jwalakwe jukulondesyaga ichindu (ya iwumbe Yakwe). Jugopolanya ma Ȃya kuti jenumanja nkulupilile kusyesyene yakusimangana ni M’mbuje gwenu.');
INSERT INTO "silika" VALUES(13,3,'13:3','Soni Jwalakwe ni Ajula jwaalijasesye litaka, ni kuŵika palakwepo yakunyichisya (matumbi) kwisa soni sulo. Soni mu isogosi iliyose juŵisile mwalakwemo mitundu jiŵili jiŵili. Akuchitendaga chilo kuŵa chakuwuunichila muusi. Chisimu mu yalakweyi mwana ilosyo kwa ŵandu ŵaakuchetelela.');
INSERT INTO "silika" VALUES(13,4,'13:4','Sano petaka pana ipande yagundana (nambo yajila yakwe yakulekangana), ni (mwana soni) migunda ja zabibu ni mimela (jine), kwisa soni mitende jana mapanda ni jangali mapanda; (nambo yoseyo) ikwitilidwa ni mesi gampepe (gakulandana), ni tukuitenda ine kuŵa yambone nnope kuipunda ine mu kanonye. Chisimu mu yalakweyi mwana ilosyo kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(13,5,'13:5','Sano naga n’di ŵakusimonga (mmwe Muhammadi صلى الله عليه وسلم ya kakanile ka makafili), basi chakusimosya nnope nikuŵecheta kwao (kwanti): “Ana tuli tusyuchile litaka, nambo yakuona uwwejo tuchiŵa soni mu kagumbidwe kasambano?” Ŵanganyao ni aŵala ŵanganankulupilila M’mbuje gwao, soni ŵanganyao makongwa tigakaŵe mu ngosi mwao, soni ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(13,6,'13:6','Ni akunkulumisya kuti m’biche nayo yakusakala (yaili ilagasyo yao) nkaniyiŵe yambone; kutendaga pamasile paipite paujo pao ilagasyo yakulandana (ni yaakuikulumisyayo kumikutula jine, yayaŵajilwe kuti aliunde nayo). Soni chisimu Ambuje ŵenu ni ŵaakwete chikululuko kwa ŵandu pa lupuso lwao. Soni chisimu Ambuje ŵenu ni Ŵaukali nnope pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(13,7,'13:7','Sano aŵala ŵakanile ni akutiji: “Nambi uli ngantuluchila jwalakwe chisimosimo kuumila kwa M’mbujegwe (mpela simbo ja Musa ni ngamiya ja Swaleh)?” Chisimutu mmwe n’gambile kuŵa nkutetela (ngaŵa jwakwika nayo isimosimo mwankusachila). Sano ŵandu ŵaliose akwete nkwajongola (jwao ni isimosimo yaŵampele Allah).');
INSERT INTO "silika" VALUES(13,8,'13:8','Allah akumanyilila chaakujigala ŵakongwe ŵaliose (m’chitumbo mwao), ni yaikupungusya iŵelechelo ni yaikonjechesya yakwe, soni chindu chilichose Kukwakwe chili ni chipiimo (chakwe).');
INSERT INTO "silika" VALUES(13,9,'13:9','(Jwalakwe ni) Nkumanyilila yakusisika ni yakuonechela, Jwankulungwa nnope, Jwapenani kusyene.');
INSERT INTO "silika" VALUES(13,10,'13:10','Yalumope (kwa Allah) jwaakusisa maloŵe mwa jenumanja ni jwaakuganyanyisya, ni mundu jwaakulisisa chilo ni jwaakwenda kalolekape muusi.');
INSERT INTO "silika" VALUES(13,11,'13:11','(Mundu jwalijose) jukwete akupisyangana (ŵachilaika) m’bujo mwakwe ni munyuma mwakwe ŵaakunchengeta mu lilamusi lya Allah. Chisimu Allah nganaŵa augalawisye ntendele wauli kwa ŵandu mpaka ali agalawisye (ŵanduo) umbone wauli m’mitima mwao. Sano naga Allah ali asachile kwapa ŵandu ilagasyo, basi pangali kuiusya, soni nganakola ŵanganyao kaligoselo kalikose kangaŵaga Jwalakwe.');
INSERT INTO "silika" VALUES(13,12,'13:12','Jwalakwe ni Ajula jwaakunnosyaga jenumanja kumesya mwa (kuntaga) woga (wakusalidwa ni njasi) ni tama (jakupata wula), ni akugapatikanisyaga mawunde gakusitopa (ni mesi gawula).');
INSERT INTO "silika" VALUES(13,13,'13:13','Soni kulindima kukunswejesyaga Jwalakwe mwakuntogolela, nombenao Achimalaika (akasanswejesyaga) pakun’jogopa, ni akasatumisyaga njasi nikunsala najo jwansachile. Sano ŵanganyao (achimakafili) akusisyana ya Allah (kuti apali kapena pangali kutendaga ali apali), soni Jwalakwe ni Jwaukali nnope pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(13,14,'13:14','Jiŵele Jakwe duwa jakuonaonape (ni Jwakuŵajilwa kum’bomba ligongo ni jwaakwangaga duwajo), sano aŵala ŵaakuilumbanjila inepe yangaŵaga Jwalakwe nganiyiŵa yajanjile ŵanganyao pa chilichose, pangaŵaga nti chisawu ajula jwaakusapangula magasa gakwe ganagaŵili kulochesya kumesi kuti gaiche (mesigo) m’kamwa mwakwe, kutendaga nganigaŵa gayiche, ni nganijiŵa duwa jamakafili ikaŵeje mu kusokonechela.');
INSERT INTO "silika" VALUES(13,15,'13:15','Soni akunsujudilaga Allah ŵaali kumawunde ni petaka mwakusaka ni mwakanganisyidwa, nambo soni iwulili yao (ikunsujudilaga) kundaŵi ni kwigulo.');
INSERT INTO "silika" VALUES(13,16,'13:16','Jilani: “Ana ninduni m’mbuje jwa kumawunde ni petaka?” Jilani: “Allah.” Jilani: “Ana mwatesile (ŵanepe) ŵangaŵaga Jwalakwe kuŵa akun’gosa, ŵali ŵangali ukombosi ŵakulikamuchisya achimisyene namuno kulipa isausyo?” Jilani: “Ana mpaka alandane jwangalola ni jwakulolaga? Ana kapena mpaka chilandane chipi ni lilanguka? Kapena am’biichiile Allah ŵakamusyangana nao, ŵagumbile yaŵatite pagumba Jwalakwe, mwanti iwumbe (yambali siŵilisi) ikulandana kukwao?” Jilani: “Allah ni Nkugumba chilichose. Soni Jwalakwe ni Jumpepe, Jwamachili gangalimbana nago.”');
INSERT INTO "silika" VALUES(13,17,'13:17','(Jwalakwe) akasatulusyaga mesi (gawula) kuumila kwinani, basi iŵata niikujilimaga mwachipiimo chakwe, nigakasajigala mesi gachikuumba chiulo chachikasatutumila mpaka pachanya, nambo soni muyaakasaiwukutaga (ŵachipala) pamoto (mpela mtapo wagolodi ni silifa) pakusaka (koposya) ipeto kapena iteleko, mukasatutuma chiulo chakulandana nichalakwecho. Iyyoyopeyo niyaakuti Allah pakupeleka chilandanyo cha yakuona ni yaunami. Sano chiulo nichigambaga kututumila nikwasichila, nambo yakwakamuchisya ŵandu niikasatamilichikaga mwitaka, mwele nimwaakasapelechelaga Allah ilandanyo.');
INSERT INTO "silika" VALUES(13,18,'13:18','Akwete kupata aŵala ŵan’jitiche M’mbuje gwao Ukoto (Mbepo), nambo aŵala ŵanganan’jitika Jwalakwe, namuno angaje kola (pa Kiyama) yosope yaili pachilambo ni yanti mpela yeleyo soni yalumo, angajile kusaka kuliombolela nayo (nambo nga nganiyija kwakamuchisya), ŵanganyao takakole chiŵalanjilo chakusakala nnope, soni mauto gao ni ku Jahannama, kaje kusakala malo gagusama (galitandichile)!');
INSERT INTO "silika" VALUES(13,19,'13:19','Ana jwaakumanyilila yanti yaitulusyidwe kukwenu kuumila kwa Ambuje ŵenu niyakuona, mpaka alandane ni jwangalola? Chisimu ŵaakwete lundape ni ŵaakukumbuchilaga.');
INSERT INTO "silika" VALUES(13,20,'13:20','Aŵala ŵaakukwanilisya chilanga cha Allah, ni ŵangawesesya soni chilanga (chakutaŵana ni ŵandu achimijao).');
INSERT INTO "silika" VALUES(13,21,'13:21','Ni aŵala ŵaakulumbikanya ichindu yalamwile Allah kuti ilumbikanyisyidwe, ni akuŵa ali nkun’jogopa M’mbuje gwao, ni kuchijogopaga soni chiŵalanjilo chakusakala nnope (cha pa Kiyama).');
INSERT INTO "silika" VALUES(13,22,'13:22','Ni aŵala ŵaapilile pakusachilila Ngope ja M’mbuje gwao, ni kwimikaga Swala mwakolosya, ni kutolaga mu ichindu yatwapele mwakusisa ni mwakuonechela, ni kutyosyaga niyambone yakusakala, ŵanganyao niŵaakwete mbesi (jambone) ja Nyumba (ja Akhera).');
INSERT INTO "silika" VALUES(13,23,'13:23','Matimbe Gandamo, gatakagajinjile ŵanganyao kwisa soni ŵaŵatesile yaukoto mwa achinangolo ŵao, achiŵammakwao ni ŵanache ŵao, nambo soni Achimalaika taakaajinjilileje ŵanganyao pannango uliose.');
INSERT INTO "silika" VALUES(13,24,'13:24','(Achasalilaga kuti): “Ntendele uŵe kukwenu, ligongo lya kupilila kwenu.” Kaje kusalala yakuichisya ya Nyumba (ja Akhera)!');
INSERT INTO "silika" VALUES(13,25,'13:25','Sano aŵala ŵaakugopola chilanga cha Allah panyuma pakuchitaŵa, ni kataga yalamwile Allah kuti ilumbikanyisyidwe, ni kuwatanganyaga pa chilambo, ŵanganyao niŵakwete kupata malweso, soni akwete kupata ŵanganyao Nyumba jakusakala nnope (Jahannama).');
INSERT INTO "silika" VALUES(13,26,'13:26','Allah akasansapangulila lisiki jwansachile, ni kunandiya (lisikilyo kwa jwansachile), ni akusengwa nao (achimakafili) umi wa duniya, ni nganiuŵa umi wa duniya pakulandanya ni Akhera ikaŵeje kusengwa kwannonope.');
INSERT INTO "silika" VALUES(13,27,'13:27','Achimakafili niakutiji: “Nambi uli ngantuluchila jwalakweju chisimosimo kuumila kwa Ambujegwe?” Jilani: “Chisimu Allah akunnechelelaga kusokonechela jwansachile, ni kun’jongolela Kukwakwe jwaujile (kwa Allah pa kutenda toba).”');
INSERT INTO "silika" VALUES(13,28,'13:28','Aŵala ŵaakulupilile, soni mitima jao ni jikutulalilaga pakunkolanga Allah. Manyililani yanti; pakunkolanga Allah mitima jikasatulalila. ');
INSERT INTO "silika" VALUES(13,29,'13:29','Aŵala ŵaakulupilile ni kutendaga yambone, kusengwa ni kwao, kwisa soni malo gakuujila gambone nnope (ku Mbepo).');
INSERT INTO "silika" VALUES(13,30,'13:30','Iyyoyopeyo tuntumisye mmwejo (Muhammadi صلى الله عليه وسلم) ku nkutula wajapite paujo pakwe mikutula jine, kuti mwaasoomele yatun’juwulile, nambo ŵanganyao akunkana Ar-Rahmân (Jwaukoto wejinji). Jilani: “Jwalakwe ni M’mbuje gwangu, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo. Kukwakwe njegamiile, soni Kukwakwe ni kwakuujila kwangu pakutenda toba.”');
INSERT INTO "silika" VALUES(13,31,'13:31','Soni ingaŵe kuti kwaliji kwana Qur’an janti ni jalakwejo matumbi nikwendesyedwa (pandema jakusoomwa), kapena ni jalakwejo litaka niganjidwa, kapena ni jalakwejo ŵawe nikwaŵechetekasya (jikaliji aji Qur’aniji, nambo masengo ga Qur’an ngaŵa gele). Nambo ulamusi wa chilichose uŵele wa Allah. Ana nganamanyililepe aŵala ŵaakulupilile yanti Allah angasache angajongwele ŵandu wosope? Sano chitapani ngachileka kwapata aŵala ŵaakanile ligongo lya ayila (yakusakala) yatesile, kapena chituule (chitapanicho) dusi ni musi ŵao mpaka chiyiche chilanga cha Allah, chisimu Allah jwangakasa chilanga.');
INSERT INTO "silika" VALUES(13,32,'13:32','Soni chisimu pamasile paŵatendedwe chipongwe Achimitenga paujo penu (mmwe Muhammadi صلى الله عليه وسلم nkanim’biche), basi ni napeleche lipesa kwa aŵala ŵaŵakufuulu, kaneko ni nakwembekenye (ni ilagasyo), ana ilagasyo Yangu yaliji uli (kapoteche kakwe)!');
INSERT INTO "silika" VALUES(13,33,'13:33','Ana kwali Ajula (Allah) jwaakuujiimila ntima uliose pa yaupanganyisye (kuti taachiulipila, ngaŵa nijwali jwakuŵajilwa kun’galagatila)? Ni am’biichiile Allah ŵakamusyangana nao. Jilani: “Mwakolangani (ŵakamusyangana naowo).” Kapena nkuntagulila Jwalakwe yangakuimanyilila pa chilambo, kapena (n’gamba kuŵecheta) maloŵe gapachanyape (ngaŵa gakuumila pasi pa ntima)? Nambo kuti gasalalisyidwe kwa aŵala ŵakanile malindi gao, ni asiŵilidwe kwitala (lyagoloka). Sano jwannechelele Allah kupotela, basi nganakola nkun’jongola.');
INSERT INTO "silika" VALUES(13,34,'13:34','Akwete kupata ŵanganyao ilagasyo mu umi wa duniya, sano chisimu ipotesi ya Akhera niyakupoteka nnope, soni ngasakola nkwagosa jwalijose kwa Allah.');
INSERT INTO "silika" VALUES(13,35,'13:35','Chilandanyo cha Mbepo jaapedwile chilanga ŵawoga (wakun’jogopa Allah, chili nyyi): Sulo sikujilima cha pasi pakwe, isogosi yakwe yandamo kwisa soni miulili jakwe. Jele nimbesi ja aŵala ŵan’jogwepe (Allah). Sano mbesi ja achimakafili ni Moto.');
INSERT INTO "silika" VALUES(13,36,'13:36','Sano aŵala ŵatwapele chitabu (mpela Abdullah bin Salâm ni Ayuda ŵane ŵaasingwiche) akuisengwela ayila yaitulusyidwe kukwenu (jajili Qur’an), nambo m’mikutula jine (jamakafili) mwana ŵaakuikana ine mwa yalakweyo. Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une namulidwe kuti nan’galagatileje Allah ni ngawwanganyaga Jwalakwe (ni chilichose). Kukwakwe nguŵilanjila, soni Kukwakwe nikwakuujila kwangu.”');
INSERT INTO "silika" VALUES(13,37,'13:37','Soni iyyoyopeyo tujitulwisye jalakwe (Qur’aniji) kuŵa chilamusi cha m’Charabu, nambo naga nkuye isako yao panyuma pa umanyilisi waum’bichilile, ngasinkola kwa Allah nsyoŵe jwalijose namuno akun’gosa.');
INSERT INTO "silika" VALUES(13,38,'13:38','Soni chisimu pamasile patwatumisye Achimitenga paujo penu (mmwe Muhammadi صلى الله عليه وسلم), ni twaŵichile achiŵammakwao kwisa soni ŵanache, ni nganiyiŵa kwa ntenga julijose yanti nikwikanacho chisimosimo ikaŵeje mu lisosa lya Allah, ndema jilijose jana lilamusi lyakwe lyalilembedwe, (nkwika ndemajo lilamusi likasaika).');
INSERT INTO "silika" VALUES(13,39,'13:39','Allah akasaifutaga yaisachile (kuti ayifute), ni kuilimbikasya (kuti ikatyoka yaisachile), soni Kukwakwe kwana Mama jwa Chitabu (Al-Lauh Al-Mahfûz).');
INSERT INTO "silika" VALUES(13,40,'13:40','Soni kanga chitunnosye (mmwe Muhammadi صلى الله عليه وسلم pa duniya pepano) ine mwa ayila (ipotesi) yatukwatetela kapena kumpa chiwa (nkanitunnosye tiyapatepe), chisimu masengo genu nikulungusya (utenga) basi, sano Kukwetu ni kwachili chiŵalanjilo (cha kwaŵalanjila itendo yao).');
INSERT INTO "silika" VALUES(13,41,'13:41','Ana ngaakuiwona yanti Uwwe tukuchiichilila chilambo (chamakafili) nikuchipungulaga (panandi panandi) kuumila m’malile mwakwe (pakuchipeleka kwa Asilamu pakupunda pangondo)? Sano Allah akasalamulaga, pangali jwakuwusyanukula ulamusi Wakwe, soni Jwalakwe ni Jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(13,42,'13:42','Soni chisimu aŵala ŵapaujo pao ŵatesile malindi, basi Allah ni nsyene kuwesesya malindi gosope, jukumanyilila yaakupanganya mundu jwalijose, soni achimakafili taachimanyilila yanti: Mbesi (jambone) ja Nyumba (ja Akhera) jichiŵa jacheni!');
INSERT INTO "silika" VALUES(13,43,'13:43','Ni akuŵechetaga aŵala ŵakanile kuti: “Mmwe (Muhammadi صلى الله عليه وسلم) nganim’ba ntenga.” Jilani: “Allah akukwanila kuŵa Mboni chilikati changune ni jenumanja (yakuti une ntenga), kwisa soni aŵala ŵauli kukwao umanyilisi wa m’chitabu (mpela Abdullah bin Salâm ni Ayuda ŵane kwisa soni Akilisito ŵaujitiche Usilamu).”');
INSERT INTO "silika" VALUES(14,1,'14:1','Alif-Lâm-Râ. (Achi ni) Chitabu chatuchitulwisye kukwenu (mmwe Muhammadi صلى الله عليه وسلم) kuti mwakoposye ŵandu m’chipi ni kwajinjisya mwilanguka – kwa lisosa lya Ambuje ŵao – (ni mwajausye) kwitala lya (Allah) Jwamachili gakupunda, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(14,2,'14:2','Allah, Ajula jwaili Yakwe yosope yaili kumawunde ni petaka, soni ipotesi niyao achimakafili kuumila mu ilagasyo yaukali nnope.');
INSERT INTO "silika" VALUES(14,3,'14:3','Aŵala ŵaakunonyela nnope umi wa duniya kumpunda Akhera, ni akusiŵililaga (ŵandu) petala lya Allah, ni kulisachililaga kupombotala, ŵanganyao niŵali nkupotela kwakutalikangana nnope (ni yakuona).');
INSERT INTO "silika" VALUES(14,4,'14:4','Soni nganitutumisyeje ntenga jwalijose ikaŵeje ni chiŵecheto cha ŵandu ŵakwe kuti ŵasalichisye ŵanganyao (utenga), basi Allah ni akunnechelelaga kusokonechela jwansachile, ni kun’jongola jwansachile, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(14,5,'14:5','Soni chisimu chene pamasile patwantumisye Musa ni isimosimo Yetu (ni kuntenda kuti): “Mwakoposyani ŵandu ŵenu m’chipi ni kwajinjisya mwilanguka, soni mwakumbusyani ya moŵa ga Allah (ga chindimba).” Chisimu mu yalakweyo muŵele mwana imanyisyo kwa jwalijose jwali jwakupilila nnope, jwakutogolela kwejinji.');
INSERT INTO "silika" VALUES(14,6,'14:6','Soni (kumbuchilani) katema kajwaŵechete Musa ku ŵandu ŵakwe kuti: “Kumbuchilani chindimba cha Allah pa jenumanja, ndema jaŵan’jokwele ŵanganyammwe ku ŵandu ŵa Firiauna ŵaŵaliji nkumpa ilagasyo yakusakala nnope, ŵaliji nkusikita ŵanache ŵenu ŵachilume ni kwalekaga umi ŵanache ŵenu ŵachikongwe, sano mu yalakweyo mwaliji mwana kulinjidwa kwekulungwa nnope kwakutyochela kwa M’mbuje gwenu.”');
INSERT INTO "silika" VALUES(14,7,'14:7','Soni (kumbuchilani) katema kajwajenesye M’mbuje gwenu yanti: “Chisimu naga nkutogolela Une tinan’jonjechesye, nambo naga nkane (manyililani yanti): Chisimu ilagasyo Yangu niyaukali nnope.”');
INSERT INTO "silika" VALUES(14,8,'14:8','Soni Musa jwatite (ku ŵandu ŵakwe): “Naga nkane jenumanja ni wosope ŵali pa chilambo, basi (niyenu), chisimu Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.”');
INSERT INTO "silika" VALUES(14,9,'14:9','Ana nganijim’bichilila ŵanganyammwe abali ja aŵala ŵapaujo penu? Ŵandu ŵa Nuhu, ŵa Adi ni ŵa Samuda, kwisa soni aŵala ŵamunyuma mwao, ŵangaakwamanyilila ikaŵeje Allah? Ŵaichilile Achimitenga ŵao ni ilosyo yakuonechela, basi ni ŵauchisye magasa gao kukamwa kwao (paagaluma ligongo lyaukali), ni ŵatite: “Chisimu uwwe tukaaniile yantumisyidwe nayoyi, soni chisimu uwwe tuli nchikaiko chekulungwa chakaichila yankutuŵilanjilayi.”');
INSERT INTO "silika" VALUES(14,10,'14:10','Achimitenga ŵao ŵatite: “Chichi! Allah kuŵa nkunkaichila, Nkugumba mawunde ni mataka? Akum’bilanga jenumanja kuti ankululuchile sambi syenu, ni anchelewasye (ngampa ipotesi) kwikanila ndema jekolanjidwe (jakuwila).” Ŵanganyao ŵatite: “Nganim’ba jenumanja ikaŵeje ŵandu mpela uwwewe. N’gamba kusaka kuti ntusiŵilile ku yaŵaliji nkuigalagatila achatati ŵetu, basi ikani nao kukwetu umboni wakuonechela.”');
INSERT INTO "silika" VALUES(14,11,'14:11','Achimitenga ŵao ŵaasalile kuti: “Uwwe nganituŵa ikaŵeje ŵandu mpela ŵanganyammwe, nambo kuti Allah akasampaga ukoto jwansachile mwa achikapolo Ŵakwe. Soni nganiyiŵa kukwetu yanti ni tum’bichilile nacho chilosyo ikaŵeje mu lisosa lya Allah, sano kwa Allah pe basi ajegamile ŵakulupilila.”');
INSERT INTO "silika" VALUES(14,12,'14:12','“Soni chipali chichi kukwetu chakututendekasya kuti tukajegamila kwa Allah kutendaga pamasile paatulosisye matala getu? Soni chitupilile kusyene pa iliyose yampaka ntusausye nayo, soni kwa Allah pe basi ajegamile akwegamila.”');
INSERT INTO "silika" VALUES(14,13,'14:13','Sano aŵala ŵaŵakanile ŵasalile Achimitenga ŵao kuti: “Chisimu chene chitunkoposye ŵanganyammwe m’chilambo mwetu, kapena chim’bujile ku dini jetu.” Basi Ambuje ŵao (Allah) ni ŵajuwulile ŵanganyao maloŵe ganti: “Chisimu chene Uwwe chitwajonanje ŵalupuso.”');
INSERT INTO "silika" VALUES(14,14,'14:14','“Soni chisimu chene chituntamiche ŵanganyammwe m’chilambomo panyuma pa ŵanganyao. Yalakweyo yakuipata jwaakogopa kwima pameso Pangu (lisiku lya Kiyama), ni kogopaga soni chitetelo Changu.”');
INSERT INTO "silika" VALUES(14,15,'14:15','Sano (Achimitenga ŵala) ni ŵaŵendile chikamuchisyo (kwa Allah chaagapundila makafili), ni jwapelelwe jwakulikwesya jwalijose, jwamakani.');
INSERT INTO "silika" VALUES(14,16,'14:16','M’bujo mwakwe mwana Jahannama, ni takamwesyedweje mesi ga uwowu wachimyasi.');
INSERT INTO "silika" VALUES(14,17,'14:17','Tachiŵa ali nkugakukumila mwachikasakasa, ni kulagaga nago nnope kugamila, soni chiwa chikam’bichilileje mbali ni mbali nambo nganaŵa awile, soni paujo pakwe chipakaŵe pana ilagasyo (ine soni) yakandapala.');
INSERT INTO "silika" VALUES(14,18,'14:18','Chilandanyo cha aŵala ŵankaaniile M’mbuje gwao (chili nyyi): Itendo yao (yambone yatakatende ngalipidwa nayo ligongo lyanti nganantendelaga Allah), ili chisawu liwu lyalikumpujila mbungo mwamachili palisiku lya chimbunga, ngasakombola kupata chilichose mu yaŵapanganyisye. Kwalakweko ni kupotela kwakutalikangana nnope (ni yakuona).');
INSERT INTO "silika" VALUES(14,19,'14:19','Ana ngankuiona yanti Allah agumbile mawunde ni mataka mwakuonaonape? Mwanti ali asachile mpaka antyosye jenumanja (mwanakamo) ni kwika nayo iwumbe yasambano.');
INSERT INTO "silika" VALUES(14,20,'14:20','Soni yalakweyo kwa Allah nganiyiŵa yakusausya ata panandi.');
INSERT INTO "silika" VALUES(14,21,'14:21','Soni ŵanganyao tachionechela kwa Allah ŵanaose (lisiku lya Kiyama). Basi ni tachiŵecheta ŵangalikombolela kwa aŵala ŵaŵalikwesyaga (ŵaali achinnongola ŵao ŵaŵagambaga kwakuya) kuti: “Chisimu uwwe twaliji nkunkuya jenumanja; ana chintutyochesye chilichose mu ilagasyo ya Allah?” (Achinnongolao) tachiti: “Ingaŵe kuti Allah ŵatujongwele, nikuti uwwe nowe tukan’jongwele ŵanganyammwe. (Sano) yalumope kukwetu tuŵe jasajasa (ni ipotesiyi) kapena tupilile (nayo); nganitukola kwakutilila.”');
INSERT INTO "silika" VALUES(14,22,'14:22','Sano Shetani pandema jachichilamulidwa chilamusi (ni kwinjila ŵambone ku Mbepo, ŵakusakala ni kwinjila ku Moto pamo ni Shetanijo); juchiŵecheta kuti: “Chisimu Allah ŵataŵene namwe chilanga chakuonaonape (ni akwanilisye chilanga Chakwe), nombe une nataŵene namwe chilanga nambo nankaasiile. Nambotu une nganingola machili kukwenu (gakunkanganichisya kuti munguye) ikaŵeje nagambaga kum’bilanga basi jenumanja nimwanjitikaga. Basi ngasimundandaula une, nambo nllidandaule mwachimisyene. Une nganimba ngombwele kunkamuchisya, nombe jenumanja soni nganim’ba nkombwele kungamuchisya. Chisimu une ngukukana kumwanganya kwenu (kwamwamwanganyaga ni Allah) kalakala (ku duniya pakunjitika malamusi gangu). Chisimu ŵalupuso akwete kupata ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(14,23,'14:23','Sano aŵala ŵaŵakulupilile ni kutendaga yambone, takajinjisyidwe ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, takaŵe ŵandamo mwalakwemo kwa lisosa lya M’mbuje gwao. Komasyana kwao mwalakwemo tikukaŵeje: Ntendele!');
INSERT INTO "silika" VALUES(14,24,'14:24','Ana ngankuiona yatite Allah pakupeleka chilandanyo? Liloŵe lyambone lili chisawu chitela chambone, njiga syakwe syesimiche mwakulimbangana, sano nyambi syakwe siyiche kwinani.');
INSERT INTO "silika" VALUES(14,25,'14:25','Chikupelekaga isogosi yakwe ndema ni katema mwa lisosa lya Ambujegwe, sano Allah ni akasapelekaga ilandanyo kwa ŵandu kuti ŵanganyao akumbuchile.');
INSERT INTO "silika" VALUES(14,26,'14:26','Sano chilandanyo cha liloŵe lyakusakala, nimpela chitela chakusakala chachichuŵidwe pachanya petaka, changanichikola kutamilichika.');
INSERT INTO "silika" VALUES(14,27,'14:27','Allah akwalimbanganyaga aŵala ŵaakulupilile ni liloŵe lyakulimbangana pa umi wa duniya ni ku Akhera kwakwe, soni Allah akwalechelelaga kusokonechela ŵalupuso, soni Allah akasapanganyaga yaakusaka.');
INSERT INTO "silika" VALUES(14,28,'14:28','Ana nganimwaona aŵala ŵaatindenye ukoto wa Allah ni ukafili, ni kwayikasya ŵandu ŵao (ŵaŵakuyaga payakusakala) ku nyumba ja chonasiko?');
INSERT INTO "silika" VALUES(14,29,'14:29','(Jajili) Jahannama; tachijijinjila; kaje kusakala malo gakutamilichika gakwe!');
INSERT INTO "silika" VALUES(14,30,'14:30','Ni am’bichile Allah milungu, kuti ŵaasokonesye (ŵandu) kwitala Lyakwe. Jilani: “Sengwani (ni umi wenu wannono wa duniyau), nambo chisimu kwakuwujila kwenu ni ku Moto!”');
INSERT INTO "silika" VALUES(14,31,'14:31','Mwasalilani achikapolo Ŵangu aŵala ŵaakulupilile kuti ajimicheje Swala mwakolosya nikutolaga mu ayila yatwapele, mwakusisa ni mwakuonechela, nkaniliyiche lisiku lyangali malonda nkati mwakwe namuno usyoŵe.');
INSERT INTO "silika" VALUES(14,32,'14:32','Allah ni Ajula jwaagumbile mawunde ni mataka, ni jutulwisye kuumila kwinani mesi (gawula), ni jumesisye ni galakwego isogosi kuŵa yakulya yenu, ni an’jepepachisye ŵanganyammwe yombo kuti ijendeje m’mbwani mwa lilamusi Lyakwe, ni an’jepepachisye soni sulo.');
INSERT INTO "silika" VALUES(14,33,'14:33','Ni an’jepepachisye soni lyuŵa ni mwesi yaikwenda mwangapela, ni an’jepepachisye soni chilo ni muusi.');
INSERT INTO "silika" VALUES(14,34,'14:34','Ni ampele jenumanja yosope yamum’bendile, soni ntite m’buŵalanje ukoto wa Allah, nganim’ba uwwukwanisye. Chisimu mundu juŵele jwalupuso nnope, jwakanila kusyene.');
INSERT INTO "silika" VALUES(14,35,'14:35','Soni (kumbuchilani) katema kajwaŵechete Ibrahima kuti: “Ambuje ŵangu! Utendani awu musi (wa Maaka) kuŵa wantunjelele, soni mumambasyani une ni achiŵanangu ku yagalagatila isanamu.”');
INSERT INTO "silika" VALUES(14,36,'14:36','“Ambuje ŵangu! Chisimu yalakwe (isanamuyo) yasokonesye ŵandu ŵajinji. Basi jwanguyiye une nikuti chisimu jwalakwejo jukuumila mwa une (jwa mu dini jangu), nambo jwanyosyisye une, basi M’mwejo ni Ŵakululuka kusyene, Ŵachanasa channope.”');
INSERT INTO "silika" VALUES(14,37,'14:37','“Ambuje ŵetu! Chisimu une nataamiiche ŵane mwa ŵanache ŵangu pachiŵata changali mmela (cha Maaka) pa Nyumba Jenu Jakuchimbichika (ja Likaaba), Ambuje ŵetu! (Nataamiiche ŵanganyao) kuti ajimicheje Swala mwakolosya. Basi jitendani mitima ja ŵandu kuŵa jakupendechela kukwao (anonyelweje kuja kutama nao), soni mwapani isogosi kuti aŵe ŵakutogolela.”');
INSERT INTO "silika" VALUES(14,38,'14:38','“Ambuje ŵetu! Chisimu Mmwe nkumanyilila yatukusisa ni yatukulosya palangulangu, ni pangali chilichose chachikasasisika kwa Allah petaka atamuno kwinani.”');
INSERT INTO "silika" VALUES(14,39,'14:39','“Lumbili losope lwene alapikwe Allah Ajula jwaambele Une ku uchekulu Ismaila ni Isihaka. Chisimu Ambuje ŵangu ni Ŵakupikana kuŵenda.”');
INSERT INTO "silika" VALUES(14,40,'14:40','“Ambuje ŵangu! Mundendani une kuŵa jwakwimika Swala mwakolosya, nambo soni ni achiŵanangu ŵakwe. Ambuje ŵetu! Pochelani soni duwa jangu.”');
INSERT INTO "silika" VALUES(14,41,'14:41','“Ambuje ŵetu! Timungululuchile une ni achinangolo ŵangu ŵaŵili, kwisa soni ŵakulupilila, lisiku lya chichisimichika chiŵalanjilo.”');
INSERT INTO "silika" VALUES(14,42,'14:42','Soni ngasin’ganichisya ata panandi yanti Allah ngajukuimanya yaakutenda ŵalupuso, nambo kuti jugamba kwachelewasya kwikanila lisiku lyachigakolondoka mu lyele (lisikulyo) meso.');
INSERT INTO "silika" VALUES(14,43,'14:43','Achikangamalaga aku mitwe jao ali anyakwile, meso gao gali ngagakuujila kukwao (gali palaanda kulepela kupila), m’mitima mwao muli mwangali chachipali (chaganisya ligongo lyaoga nnope).');
INSERT INTO "silika" VALUES(14,44,'14:44','Soni mwatetelani ŵandu ya lisiku lya ichaichilila ilagasyo, basi nikuŵecheta aŵala ŵaŵalitesile lupuso kuti: “Ambuje ŵetu! Tuchelewasyani kandaŵi kannono (pakutuuchisya soni ku duniya), tukajanje kuŵilanga Kwenu ni tukaakuuye Achimitenga.” (Kuchiŵechetedwa kukwao kuti): “Ana ngaŵa mwaliji nkulumbila kala kuti ngasintyoka (pa duniya)?”');
INSERT INTO "silika" VALUES(14,45,'14:45','“Soni jenumanja mwatemi m’malo mwanakutamaga aŵala ŵaŵalitesile lupuso achimisyene, soni yamanyukwiche kukwenu mwatwatendele ŵanganyao (pakwajonanga), soni ni twampele jenumanja ilandanyo (yakulekanganalekangana nambo mwaikaaniile).”');
INSERT INTO "silika" VALUES(14,46,'14:46','Soni chisimu ŵatesile malindi gao, nambo malindi gaogo gali kwa Allah (akugamanya chenene), soni namuno gakaliji malindi gaogo ganti nikuchuŵika nago matumbi (nganigaŵa gatyosisye Usilamu).');
INSERT INTO "silika" VALUES(14,47,'14:47','Soni ngasin’ganisya ata panandi yanti Allah kuŵa nkwakasila chilanga Chakwe Achimitenga Ŵakwe. Chisimu Allah ni Jwamachili gakupunda, Nkuusya mbusyo (syakupoteka kusyene).');
INSERT INTO "silika" VALUES(14,48,'14:48','(Chilangachi chichikwanilana pa) lisiku lyalichigalausyidwa litaka kuŵa litaka line (lyachilendo), ni mawunde soni (iyyoyopeyo), ni chachionechela ŵanganyao kwa Allah Jumpepe, Jwamachili gangalimbana nago.');
INSERT INTO "silika" VALUES(14,49,'14:49','Ni tinchaona akuleŵa lyele lisikulyo ali ataŵidwe m’maunyolo.');
INSERT INTO "silika" VALUES(14,50,'14:50','Yakuwala yao ichiŵa yalipula, soni Moto uchiunichila ngope syao.');
INSERT INTO "silika" VALUES(14,51,'14:51','Kuti Allah an’dipile mundu jwalijose yajwapanganyisye. Chisimu Allah ni Jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(14,52,'14:52','Aku kuŵele gombelesya ku ŵandu, ni kuti alitetele nako, soni kuti amanyilile yanti Jwalakwe (Allah) ni Nnungu Jumpepe, soni kuti ŵakwete lunda akumbuchile.');
INSERT INTO "silika" VALUES(15,1,'15:1','Alif-Lâm-Râ. Aga ni ma Ȃya ga m’Chitabu (cha Qur’an) soni ni Chisoomo chakupikanika.');
INSERT INTO "silika" VALUES(15,2,'15:2','Kakajinji katakaŵe achimakafili (ndema jatakaŵe ku Moto) ali nkulajila kuti bola angaŵe Asilamu (paŵaliji ku duniya).');
INSERT INTO "silika" VALUES(15,3,'15:3','Mwalekani agambeje kulya ni kusengwa, ni kwaluyaluya liganichisya (lyakupata yambone kwa Allah), basi taachiika kumanyilila (mbesi ja ukafili wao).');
INSERT INTO "silika" VALUES(15,4,'15:4','Ni nganituujonanjeje musi uliose ikaŵeje waliji wana chilamusi chakwe chakumanyika (cha ndema jakuujonanjila).');
INSERT INTO "silika" VALUES(15,5,'15:5','Nkutula uliose nganiuŵa ujilongolele ndema jakwe (jakuwila) kapena kujichelewasya (jili jikwanile).');
INSERT INTO "silika" VALUES(15,6,'15:6','(Achimakafili) ni ŵatite (pakun’gamba Muhammadi صلى الله عليه وسلم): “E mmwe aŵala ŵachintuluchile Chikumbusyo (Qur’an)! Chisimu mmwejo ŵamasoka.”');
INSERT INTO "silika" VALUES(15,7,'15:7','“Uli ngaikanago kukwetu Malaika naga n’di mwa akuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(15,8,'15:8','(Allah akwasalila kuti): Uwwe ŵangatulusya Malaika ikaŵeje mwakuonaonape (kwisa kupeleka ipotesi), soni pajele ndemajo ngaakasaŵaga (achimakafilio) ŵakupedwa lipesa.');
INSERT INTO "silika" VALUES(15,9,'15:9','Chisimu Uwwe ni Aŵala ŵatutulwisye Chikumbusyo (Qur’an) soni Uwwe chisimu chene tuchigose.');
INSERT INTO "silika" VALUES(15,10,'15:10','Soni chisimu chene twaatumisye (Achimitume) paujo penu (mmwe Muhammadi صلى الله عليه وسلم) m’mikutula (jaŵandu) ŵandanda.');
INSERT INTO "silika" VALUES(15,11,'15:11','Ni nganijwaichililaga ntenga jwalijose ikaŵeje ŵaliji nkuntenda chipongwe.');
INSERT INTO "silika" VALUES(15,12,'15:12','Iyyoyopeyo tukuupitisyaga (nteto wachipongwewo) m’mitima mwa akuleŵa.');
INSERT INTO "silika" VALUES(15,13,'15:13','Ngakasajikulupililaga jalakwe (Qur’an) kutendaga pamasile payapite ilandanyo ya (ŵandu) ŵandanda (yaŵatite kapedwe ipotesi).');
INSERT INTO "silika" VALUES(15,14,'15:14','Soni tungaaugulile (achimakafilio) nnango wakwinani, ni kuŵa ŵanganyao ali nkukwela kwalakweko chitetete (mwaakusachila, kuti chiŵe chisimosimo cha Muhammadi صلى الله عليه وسلم).');
INSERT INTO "silika" VALUES(15,15,'15:15','Basi angatite: “Chisimu meso getu gantesile ndandagasi. (Ngwamba), nambo kuti uwwe ni ŵandu ŵakulogwa (tukuiona ikaliyo).”');
INSERT INTO "silika" VALUES(15,16,'15:16','Soni chisimu chene tuŵisile kwinani ndondwa syekulungwakulungwa ni mwakwenda mwakwe, ni tukusalalisye kwa ŵaakuona.');
INSERT INTO "silika" VALUES(15,17,'15:17','Ni tukugosile ku shetani jwalijose jwakuŵinjidwa.');
INSERT INTO "silika" VALUES(15,18,'15:18','Ikaŵeje jwaakupikanilaga mwawiyi, basi papopo chisinga (cha moto) chakuonechela ni kunkuya.');
INSERT INTO "silika" VALUES(15,19,'15:19','Sano litaka twalijasesye ni kuŵika mwalakwemo yakunyichisya (gagali matumbi gausito), ni tumesisye mwalakwemo chindu chilichose chepimepime.');
INSERT INTO "silika" VALUES(15,20,'15:20','Ni tum’bichile mwalakwemo yakulamila ŵanganyammwe ni ichindu yanganim’ba jenumanja kuti mpaka nkombole kuipatila lisiki (mpela inyama ni ilango ni ine yosope yaumi).');
INSERT INTO "silika" VALUES(15,21,'15:21','Ni pangali chindu chilichose ikaŵeje imatiilo yakwe ili Kukwetu, ni ngatukasachitulusyaga ikaŵeje kwa chipiimo chakumanyika.');
INSERT INTO "silika" VALUES(15,22,'15:22','Soni tukasasitumisyaga mbungo syakuŵelekasya (mawunde mesi gawula), basi ni tukasagwisyaga kuumila kwinani mesi (gawula), ni tukasampaga kumwa ŵanganyammwe galakwe, sano jenumanja ngaŵa ni ŵankugasungaga galakwe (mesigo).');
INSERT INTO "silika" VALUES(15,23,'15:23','Soni chisimu Uwwe ni Aŵala ŵatukupeleka umi ni chiwa, soni Uwwe ni Ŵawinjila.');
INSERT INTO "silika" VALUES(15,24,'15:24','Soni pamasile patwamanyi kusyene ŵaŵalongolele mwa jenumanja (ŵaŵawile kala), soni pamasile patwamanyi kusyene ŵaasigalile (ŵaali chijumi kwisa soni ŵanganapagwe).');
INSERT INTO "silika" VALUES(15,25,'15:25','Soni chisimu Ambuje ŵenu Ŵalakwe ni ŵatachasonganganya ŵanganyao, chisimu Ŵalakweo ni Ŵalunda lwakusokoka, Ŵakumanyilila nnope.');
INSERT INTO "silika" VALUES(15,26,'15:26','Soni chisimu chene mundu twan’gumbile ni lilongo lyejumu lyakulilaga (nkuligombamo) lyakuumila m’matope gakupiililila geunde.');
INSERT INTO "silika" VALUES(15,27,'15:27','Sano lijini twaligumbile kala kuumila ku malamba ga moto gangali lyosi.');
INSERT INTO "silika" VALUES(15,28,'15:28','Soni (kumbuchilani) katema kaŵaŵechete Ambuje ŵenu kwa Achimalaika kuti: “Chisimu Une tingumbe mundu ni lilongo lyejumu lyakulilaga (nkuligombamo) lyakuumila m’matope gakupiililila geunde.”');
INSERT INTO "silika" VALUES(15,29,'15:29','“Sano patimale kunkolosya ni kumpepelela nsimu wakuuma Kukwangu, basi munsenjelemukuchile pakunsujudila.”');
INSERT INTO "silika" VALUES(15,30,'15:30','Basi ni ŵasujudu Achimalaika wosope ŵene nakamo.');
INSERT INTO "silika" VALUES(15,31,'15:31','Pangaŵaga Ibulisu; jwakanile kuti aŵe pamo ni akusujudu.');
INSERT INTO "silika" VALUES(15,32,'15:32','(Allah) jwatite: “E mmwe Ibulisu! Chipali chichi kukwenu chintendekasisye kuti nkaŵa pamo ni akusujudu?”');
INSERT INTO "silika" VALUES(15,33,'15:33','Jwalakwe jwatite: “Une nganimba jwanti nikunsujudila mundu jwamun’gumbile ni lilongo lyejumu lyakulilaga (nkuligombamo) lyakuumila m’matope gakupiililila geunde.”');
INSERT INTO "silika" VALUES(15,34,'15:34','(Allah) jwatite: “Basi kopokani mwalakwemo, mwanti chisimu mmwe (sano) n’di ŵakuŵinjidwa.”');
INSERT INTO "silika" VALUES(15,35,'15:35','“Soni chisimu pachanya penu pana malweso mpaka Lisiku Lyamalipilo.”');
INSERT INTO "silika" VALUES(15,36,'15:36','Jwalakwe jwatite: “Ambuje ŵangu! Mumbanitu lipesa (lyakuti ngawa) mpaka lisiku lyatachiukulidwa (m’malembe achiiumbe wosope).”');
INSERT INTO "silika" VALUES(15,37,'15:37','(Allah) jwatite: “Basi chisimu mmwe n’di mwa akupedwa lipesa.”');
INSERT INTO "silika" VALUES(15,38,'15:38','“Kwikanila lisiku lya ndema jakumanyika (jagombedwa lipenga lyandanda, pele ni patinchiwila).”');
INSERT INTO "silika" VALUES(15,39,'15:39','Jwalakwe jwatite: “Ambuje ŵangu! Ligongo lyanti munechelele kusokonechela, basi chinasalalichisye kwene ŵanganyao pa chilambo (litala lyesokonechele), ni chinasokonesye kwene ŵanaose.”');
INSERT INTO "silika" VALUES(15,40,'15:40','“Pangaŵaga achikapolo Ŵenu ŵakuswejesyedwa mwa ŵanganyao.”');
INSERT INTO "silika" VALUES(15,41,'15:41','(Allah) jwatite: “Ali nilitala lya Kukwangu lyalili lyagoloka.”');
INSERT INTO "silika" VALUES(15,42,'15:42','“Chisimu achikapolo Ŵangu nganinkola mmwejo pa ŵanganyao machili, ikaŵeje aŵala ŵatakankuyeje (mwakusaka kwao) mwa akusokonechela.”');
INSERT INTO "silika" VALUES(15,43,'15:43','Soni chisimu Jahannama ni malo gao gaakupedwa chilanga ŵanaose.');
INSERT INTO "silika" VALUES(15,44,'15:44','Jikwete milango nsano najiŵili, nnango uliose ukwete liunjili mwa ŵanganyao lyegaŵidwegaŵidwe.');
INSERT INTO "silika" VALUES(15,45,'15:45','Chisimu ŵaoga (wakun’jogopa Allah) tachiŵa m’Matimbe ga ku Mbepo ni sulo (syasichijilimaga chapasi pakwe).');
INSERT INTO "silika" VALUES(15,46,'15:46','(Tachisalilidwa kuti) gajinjilani mwantendele n’di ŵagosedwa.');
INSERT INTO "silika" VALUES(15,47,'15:47','Ni tuchityosya iwundo (ya uchimwa ni litima) yayaliji m’mitima mwao, tachiŵa (ŵakunonyelana) mwaulongo ali (atemi) pa itengu yauchimbichimbi mwakulolegana.');
INSERT INTO "silika" VALUES(15,48,'15:48','Ngagaja kwakwaya mwalakwemo masausyo galigose, soni ŵanganyao ngaaja koposyedwamo.');
INSERT INTO "silika" VALUES(15,49,'15:49','Mwatagulilani achikapolo Ŵangu yanti chisimu Une ni jwandi Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(15,50,'15:50','Soni kuti chisimu ilagasyo Yangu ni yaili ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(15,51,'15:51','Soni mwatagulilani ya achalendo ŵa Ibrahima.');
INSERT INTO "silika" VALUES(15,52,'15:52','Pandema jaŵajinjile kukwakwe ni kuŵecheta kuti: “Ntendele!” Jwalakwe (panyuma pakwajanga ni kwaona kuti ngakulya yakulya yajwaŵichile) jwatite: “Chisimu uwwe tukun’jogopa ŵanganyammwe.”');
INSERT INTO "silika" VALUES(15,53,'15:53','Ŵanganyao ŵatite: “Nkajogopa! Chisimu uwwe tukumpa abali jakusengwasya janti chinkole mwanache jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(15,54,'15:54','Jwalakwe jwatite: “Ana nkundagulila jele abali jakusengwasyaji kutendaga uchekulu uli ungwayiye kala? Ana abali jamboneji nkundagulila m’matalachi?”');
INSERT INTO "silika" VALUES(15,55,'15:55','Ŵanganyao ŵatite: “Tukuntagulila abali jamboneji mwakuonaonape; basi ngasim’ba mwa ŵakata tama.”');
INSERT INTO "silika" VALUES(15,56,'15:56','Jwalakwe jwatite: “Ana ninduni jwampaka akate tama kuchanasa cha Ambujegwe, pangaŵaga ŵakupotela?”');
INSERT INTO "silika" VALUES(15,57,'15:57','(Kaneka) jwatite: “Abali jao (jine) jaichile nijapi jaomanja akutumidwa?”');
INSERT INTO "silika" VALUES(15,58,'15:58','Ŵanganyao ŵatite: “Chisimu uwwe tutumisyidwe ku ŵandu ŵakuleŵa.”');
INSERT INTO "silika" VALUES(15,59,'15:59','“Ikaŵeje ŵaliŵasa lya Lutwi, chisimu uwwe chitukajokole ŵanawose.”');
INSERT INTO "silika" VALUES(15,60,'15:60','“Pangaŵanga ŵankwao, twapimichisye yanti ŵalakweo chaŵe mwa akusigalila munyuma (chajonanjidwe).”');
INSERT INTO "silika" VALUES(15,61,'15:61','Basi ndema jaŵaiche akutumidwa kwiŵasa lya Lutwi.');
INSERT INTO "silika" VALUES(15,62,'15:62','Jwalakwe jwatite: “Chisimu ŵanganyammwe ni ŵandu ŵangamanyika (kukwangu).”');
INSERT INTO "silika" VALUES(15,63,'15:63','Ŵanganyao ŵatite: “Basi tum’bichilile ni ayila (ilagasyo) yaŵaliji nkuikaichila.”');
INSERT INTO "silika" VALUES(15,64,'15:64','“Soni tun’jigalile (abali) jakuonaonape, soni chisimu uwwe ŵakuonaonape.”');
INSERT INTO "silika" VALUES(15,65,'15:65','“Basi ŵigulani niliŵasa lyenu mu upande wachilo, sano mmwe mwakuyeje munyuma mwao, nambo akasagalauka jwalijose mwa jenumanja (kulola munyuma ndema jakupikana kulindima kwaipotesi yakuuma kwinani, atakwisa mwaawile), ni n’jendelechele (mwakangamala) kuja kunnamulidwe.”');
INSERT INTO "silika" VALUES(15,66,'15:66','Ni twammanyisye jwalakwe (Lutwi) lilamusi lyalakweli lyanti: Chisimu njinga sya ŵanganyao (achimakafilio) chisikatidwe (tajonanjidwe) kumasikusiku.');
INSERT INTO "silika" VALUES(15,67,'15:67','Niŵaiche (kwa Lutwi) ŵandu ŵa mu nsinda ali ŵakusengwa (pakupikana kuti ayiche achachanda ŵakusalala kuti ŵatende yausakwa).');
INSERT INTO "silika" VALUES(15,68,'15:68','(Lutwi) jwatite: “Chisimu ŵanganyaŵa achalendo ŵangu, basi nkandesya soni (pakwatenda yakusakala).”');
INSERT INTO "silika" VALUES(15,69,'15:69','“Ni mun’jogope Allah soni nkanjigasya nchesela (pameso pao).”');
INSERT INTO "silika" VALUES(15,70,'15:70','Ŵanganyao ŵatite: “Ana nganitunkanya kupochela ŵandu (ŵachilendo)?”');
INSERT INTO "silika" VALUES(15,71,'15:71','Jwalakwe jwatite: “Aŵa ŵanache ŵangu ŵachikongwe (achakongwe ŵa m’chilambo muno mwalombani), naga jenumanja n’di ŵakutenda (yannamulidwe).”');
INSERT INTO "silika" VALUES(15,72,'15:72','Nguulumbila umi wenu (mmwe Muhammadi صلى الله عليه وسلم), chisimu ŵanganyao nkukolelwa mwao (ni yakusakala) ŵaliji pupulipupuli.');
INSERT INTO "silika" VALUES(15,73,'15:73','Basi ni wakwembekenye n’gumilo (waipotesi) ndema jakopoka lyuŵa.');
INSERT INTO "silika" VALUES(15,74,'15:74','Basi ni twajitesile (misi ja Sodomu ni Gomora kuŵa jagalausyidwa) kwinani kwakwe kuŵa pasi, ni twagwichisye ŵanganyao wula jamaganga gelongo lya ku Moto.');
INSERT INTO "silika" VALUES(15,75,'15:75','Chisimu mu yalakweyi mwana ilosyo kwa ŵakulola ni kuchetelela (mu ilosyo ya Allah).');
INSERT INTO "silika" VALUES(15,76,'15:76','Soni chisimu jalakwe (misijo) jatamilichiche mwitalatalape (lyakutyochela ku Maaka kuja ku Shami mwanti ŵanganyao akuionaga imanyilo yakwe).');
INSERT INTO "silika" VALUES(15,77,'15:77','Chisimu mu yalakweyo mwana chilosyo kwa ŵakulupilila.');
INSERT INTO "silika" VALUES(15,78,'15:78','Soni chisimu ŵandu ŵa mu itekete (ku Madiyana) ŵaliji ŵalupuso (pakunkana Ntume jwao Shuaibu).');
INSERT INTO "silika" VALUES(15,79,'15:79','Basi ni twauchisye mbusyo ŵanganyao (pakwapa ipotesi), soni chisimu yalakwe (ilambo) iŵiliyi (chilambo chaŵandu ŵa Lutwi ni chaŵandu ŵa Shuaibu) yaliji mwitalatalape lyakuonechela (kwa Makuraish).');
INSERT INTO "silika" VALUES(15,80,'15:80','Soni chisimu chene achinsyene chiŵata cha Hijri (chamaganga ŵali achina Samuda) ŵakaaniile achimitenga.');
INSERT INTO "silika" VALUES(15,81,'15:81','Ni twapele ŵanganyao ilosyo Yetu (mpela ngamiya jana kopokaga pa lwala), basi ni ŵaliji ŵakuitundumalila.');
INSERT INTO "silika" VALUES(15,82,'15:82','Soni ŵaliji nkusepa m’matumbi majumba mwantunjelele.');
INSERT INTO "silika" VALUES(15,83,'15:83','Basi n’gumilo (waipotesi) wakwembekenye ŵanganyao kumasikusiku.');
INSERT INTO "silika" VALUES(15,84,'15:84','Mwanti nganiyakamuchisya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(15,85,'15:85','Ni nganitugumba mawunde ni mataka ni yaili chilikati chakwe ikaŵeje mwakuonaonape, soni chisimu chene Kiyama jikwika. Basi kululukani (mmwe Muhammadi صلى الله عليه وسلم) kululuka kwambone.');
INSERT INTO "silika" VALUES(15,86,'15:86','Chisimu Ambuje ŵenu ni ŵali Akugumba kusyene, Ŵakumanyilila nnope.');
INSERT INTO "silika" VALUES(15,87,'15:87','Soni chisimu chene tumpele mmwejo (ma Ȃya) nsano nagaŵili gakusoomedwasoomedwa (Sûrat Al-Fâtihah) kwisa soni Qur’an jakuchimbichika.');
INSERT INTO "silika" VALUES(15,88,'15:88','Ngasinyyikolondochesya ata panandi meso genu yatujisengwachisye nayo mikutula jakulekanganalekangana ja ŵanganyao (achimakafilio), soni ngasimwadandaulila. Ni mwatuluchisyeje lipapiko lyenu ŵakulupilila (pakwatendela chanasa).');
INSERT INTO "silika" VALUES(15,89,'15:89','Soni jilani: “Chisimu une ni nkutetela jwakuonechela (ngangusisa chilichose kogopela kuti itakwisa muimpatile ilagasyo).”');
INSERT INTO "silika" VALUES(15,90,'15:90','Mpela yatwatite pakutulusya (ilagasyo) kwa akugaŵanya.');
INSERT INTO "silika" VALUES(15,91,'15:91','Aŵala ŵaajitesile Qur’an kuŵa ipande-ipande (ine kuikunda ine ni kuikana).');
INSERT INTO "silika" VALUES(15,92,'15:92','Basi ngwalumbila Ambuje ŵenu, tuchawusya kwene wosope ŵene (lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(15,93,'15:93','Pa yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(15,94,'15:94','Basi jenesyani mwakunyanyisya yannamulidwe, ni mwatundumalile ŵakuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(15,95,'15:95','Uwwe tukwanila kun’gosa (ku yakusakala ya) ŵachipongwe.');
INSERT INTO "silika" VALUES(15,96,'15:96','Aŵala ŵaakuŵika pamo ni Allah nnungu jwine, basi sampano taimanye (yakuyichisya yakwe).');
INSERT INTO "silika" VALUES(15,97,'15:97','Soni chisimu chene tukumanyilila yanti chidali chenu chikuŵanikwa ni yaakuŵecheta.');
INSERT INTO "silika" VALUES(15,98,'15:98','Basi luswejesyani lumbili lwa Ambuje ŵenu, ni m’beje mwa akusujudu (kuswali). ');
INSERT INTO "silika" VALUES(15,99,'15:99','Ni mwagalagatileje Ambuje ŵenu mpaka chim’bichilile chiwa.');
INSERT INTO "silika" VALUES(16,1,'16:1','Chilamusi cha Allah pamasile pachiyiche (Kiyama jili dusi), basi ngasinchijanguichisya. Kuswejela ni Kwakwe (Allah) soni atukwiche ku yaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(16,2,'16:2','Akasagatulusyaga Malaika ni utenga kwa ulamusi Wakwe kwa jwansachile mwa achikapolo Ŵakwe, (pakwasalila) kuti: “Mwatetelani (ŵandu) yanti: Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Unepe, basi njogopani.”');
INSERT INTO "silika" VALUES(16,3,'16:3','Agumbile mawunde ni mataka mwakuonaonape, atukwiche kuyaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(16,4,'16:4','Agumbile mundu kuumila mu lindondwa lya mesi (ga ŵalume ni ŵakongwe), yeti kaneko jwalakwe (mundujo) ni jukuŵaga jwamakaani jwakuonechela (jwakunkanila Allah).');
INSERT INTO "silika" VALUES(16,5,'16:5','Soni ilango an’guumbiile jenumanja, mwalakwemo mwana kutukutila (mpela kupanganyichisya manyunya gakwe yakuliunichila ni iwaalo yakutukutila,) ni (mwana soni) yakamuchisya (ine yejinji), soni ine mu yalakweyo nkuilyaga.');
INSERT INTO "silika" VALUES(16,6,'16:6','Soni nkwete (kuona) jenumanja mu yalakweyo kusalalila ndema jankuuja nayo ligulo kwichinga (ili ijikwite) ni ndema jankwaula nayo kululiila kundaŵi (ili nkwenda mwakangamala).');
INSERT INTO "silika" VALUES(16,7,'16:7','Soni ikutwichilaga misigo jenu jausito kuja kujiikasya ku chilambo changanim’bajilwa kuchiika ikaŵeje mwakulagasika nnope mwaachimisyene. Chisimu M’mbuje gwenu ni Mpole kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(16,8,'16:8','Ni (ŵagumbile soni) mahachi, nyumbu ni mbunda kuti nyyikweleje ni kuŵa soni ipeto (yakunsengwasya), soni ni akugumba (ine yakwela) yangankuimanyilila.');
INSERT INTO "silika" VALUES(16,9,'16:9','Ni gali kwa Allah masengo ga (kusalichisya) litala lyagoloka (lyakunyyikasya mundu ku Mbepo), nambo kwana matala gane soni gepombotale. Sano (Allah) akasache akan’jongwele ŵanaose.');
INSERT INTO "silika" VALUES(16,10,'16:10','Jwalakwe ni Ajula jwaakutulusyaga kuumila kwinani mesi (gawula), nkwete kumwa jenumanja mu galakwego, soni kuumila mu galakwego itela (ikasamelaga) yankasalichisyaga ilango.');
INSERT INTO "silika" VALUES(16,11,'16:11','Nigalakwego akasammechesyaga mbeju, mizaituni (jakwenga mauta), mitende ni mizabibu, kwisa soni mitundu josope ja isogosi. Chisimu mu yalakweyo mwana chilosyo kwa ŵandu ŵakuchetelela.');
INSERT INTO "silika" VALUES(16,12,'16:12','Ni an’jepepachisye chilo ni muusi, lyuŵa ni mwesi, nombe nasyo ndondwa sijepepasyidwe mwachilamusi Chakwe. Chisimu mu yalakweyo mwana ilosyo kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(16,13,'16:13','Ni iliyose yan’guumbiile petaka (kutandila mitapo jambiya, yakumelamela kwisa soni inyama), yakulekangana mitundu jakwe (ni kaoneche kakwe), chisimu mu yalakweyo mwana chilosyo kwa ŵandu ŵakumbuchila.');
INSERT INTO "silika" VALUES(16,14,'16:14','Soni Jwalakwe ni Ajula jwaajijepepesye mbwani, kuti n’dyeje kuumila mwalakwemo nyama ja matumbi (somba), soni kuti mkoposyeje mwalakwemo ipeto yankasawalaga. Ni nkasaionaga soni yombo ili nkupapula matumbela mwalakwemo, kuti nsosejesoseje umbone Wakwe (wa Allah pakujigala misigo jenu jamalonda mu yombomo), ni kuti soni jenumanja m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(16,15,'16:15','Ni aŵisile petaka yakunyichisya (matumbi gausito) kuti litakwika mulin’gwedanyichile ŵanganyammwe (litakalyo), ni (aŵisile) sulo kwisa soni matala kuti jenumanja n’dijongolele (mwachimisyene kwakuja).');
INSERT INTO "silika" VALUES(16,16,'16:16','Ni (aŵisile soni) imanyisyo (ine yakwajongolela muusi), sano ni ndondwa ŵanganyao akasalijongolela (achimisyene chilo).');
INSERT INTO "silika" VALUES(16,17,'16:17','Ana Ajula jwaakasagumbaga (jwali Allah) mpaka alandane ni aŵala ŵangaakasagumba (yaili isanamu)? Ana ngankukumbuchila?');
INSERT INTO "silika" VALUES(16,18,'16:18','Soni ntite m’buŵalanje ukoto wa Allah nganim’ba uwwukwanisye. Chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(16,19,'16:19','Soni Allah akuimanyilila yankuisisa ni yankuilosya papaswela.');
INSERT INTO "silika" VALUES(16,20,'16:20','Sano ayila yaakuilumbanjila yangaŵaga Allah nganiyiŵa igumbile chilichose, nambo yalakweyo ni yaili yagumbidwa.');
INSERT INTO "silika" VALUES(16,21,'16:21','Ŵawe nganaŵa ŵachijumi, soni ngaakumanyilila kuti tachiukulidwa chakachi (m’malembe).');
INSERT INTO "silika" VALUES(16,22,'16:22','Nnungu jwenu (jwakuŵajilwa kun’galagatila) ni Nnungu jumpepe (Allah). Nambo aŵala ŵangaakukulupilila ya Akhera mitima jao jikana (ya Uujika wa Allah) soni ŵanganyao akuŵa ali nkulikwesya.');
INSERT INTO "silika" VALUES(16,23,'16:23','Pangali kaichila yanti Allah akuimanyilila yaakuisisa ni yaakuilosya papaswela. Chisimu Jwalakwe jwangaanonyela ŵakulikwesya.');
INSERT INTO "silika" VALUES(16,24,'16:24','Soni naga kuli kuŵechetedwe kukwao yanti: “Ana M’mbuje gwenu jutulwisye chichi (kwa Muhammadi صلى الله عليه وسلم)?” Ŵanganyao akasatiji: “Adisiadisi sya ŵandu ŵakala.” (Akuŵechetaga yalakweyi mwakwasokonesya ŵandu).');
INSERT INTO "silika" VALUES(16,25,'16:25','Kuti akatwichile misigo jao (jasambi) jakwanila lisiku lya Kiyama, kwisa soni misigo (jasambi) ja aŵala ŵaakwasokonesya mwangali umanyilisi (wa akusokonesedwao). Naalole, kaje kusakala misigo jachakatwichilejejo!');
INSERT INTO "silika" VALUES(16,26,'16:26','Chisimu ŵaatesile malindi aŵala ŵaŵaliji paujo pao (ŵanya Anamuruda), nambo Allah ŵagumulile chakutaŵa chao kutandila pachitepo, basi nsakasa ni wagwilile pachanya pao, soni ilagasyo ni yaichilile kuumila upande wanganaumanyilila.');
INSERT INTO "silika" VALUES(16,27,'16:27','Kaneko lisiku lya Kiyama (Allah) tachajigasya nchesela ni tachiti: “Ali kwapi akumwanganya nao, aŵala ŵamwaliji nkukangana (ni Achimitume) yakwamba ŵanganyao?” Aŵala ŵaŵapedwile umanyilisi tachiti: “Chisimu kwaluka lelo ni ulanda ili pa achimakafili.”');
INSERT INTO "silika" VALUES(16,28,'16:28','“Aŵala ŵaakwatyosyaga Achimalaika mitima jao (pa kuwa) ali ŵalitenda lupuso achimisyene (ligongo lyaukafili wao).” Basi tachilipeleka (aku achitiji): “Uwwe nganituŵa nkutenda yakusakala iliyose.” (Achimalaika tachiti): “Yakuona, chisimu chene Allah ni Nkumanyilila kusyene yamwaliji nkutenda ŵanganyammwe.”');
INSERT INTO "silika" VALUES(16,29,'16:29','“Basi jinjilani milango ja ku Jahannama, chim’be ŵandamo kwalakweko.” Basi kaje kusakala nnope mauto ga ŵakulikwesya!');
INSERT INTO "silika" VALUES(16,30,'16:30','Sano (naga kuli) kuŵechetedwe kwa aŵala ŵaakun’jogopa (Allah) kuti: “Ana M’mbuje gwenu jutulwisye chichi (kwa Muhammadi صلى الله عليه وسلم)?” Akasatiji: “(Atulwisye) yambone.” Kwa aŵala ŵaakutenda yambone pa achino chilambo cha duniya akwete kupata yambone, sano chisimu nyumba ja Akhera ni jajili jambone nnope. Soni kaje kusalala nnope nyumba ja ŵandu ŵaoga (wakun’jogopa Allah)!');
INSERT INTO "silika" VALUES(16,31,'16:31','Matimbe Gandamo gatakagajinjile, sulo sikujilima cha pasi pakwe, akwete kupata ŵanganyao mwalakwemo iliyose yaakuisaka. Iyyoyopeyo ni yaakutiji Allah pakwalipila ŵaoga (wakun’jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(16,32,'16:32','Aŵala ŵaakwatyosyaga Achimalaika mitima jao (pa kuwa) ali ŵambone, achasalilaga kuti: “Ntendele uŵe kukwenu! Jinjilani ku Mbepo ligongo lya ayila (yambone) yamwaliji nkutenda.”');
INSERT INTO "silika" VALUES(16,33,'16:33','Ana akwembecheya (achimakafilio inepe) pangaŵaga kuti gaichilile Malaika (kukwatyosya mitima), kapena kuti chaichilile chilamusi cha Ambuje ŵenu (Allah cha ilagasyo kapena Kiyama)? Mwele ni muŵatendele aŵala ŵa paujo pao. Ni nganiŵatenda Allah lupuso, nambo achimisyene ni ŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(16,34,'16:34','Basi yapatile yakusakala ya ŵapanganyisye, niyasyungwile ayila (ilagasyo) yaŵaliji nkuitendela chipongwe.');
INSERT INTO "silika" VALUES(16,35,'16:35','Sano aŵala ŵawanganyisye (Allah ni isanamu) akutiji: “Allah angasache, nga nganitugalagatila uwwe namuno achatati ŵetu chinepe changaŵaga Jwalakwe, soni nga nganituchitenda chindu chilichose kuŵa cha haraam mwangali (lilamusi lya) Jwalakwe.” Mwele ni muŵatendele aŵala ŵapaujo pao. Ana nambi kwa Achimitenga kwana masengo gane gangaŵaga kulungusya utenga mwakupikanika?');
INSERT INTO "silika" VALUES(16,36,'16:36','Soni chisimu chene twatumisye ku nkutula uliose Ntenga (juŵausalilaga) kuti: “Mun’galagatilani Allah ni kuŵambalana najo shetani.” Basi ŵane mwa ŵanganyao pana ŵaŵajongwele Allah, ni pana ŵane mwa ŵanganyao ŵakwasimichiche pa ŵanganyao kusokonechela. Basi jendagani pa chilambo kupita nchilola mujaŵelele mbesi ja akukanila.');
INSERT INTO "silika" VALUES(16,37,'16:37','Naga nkusachilila nnope yakwajongola ŵanganyao (mmwe Muhammadi صلى الله عليه وسلم), basi (manyililani yanti): Chisimu Allah jwangan’jongola jwannechelele kusokonechela, soni nganakola ŵanganyao akwakamuchisya.');
INSERT INTO "silika" VALUES(16,38,'16:38','Ni akunnumbilaga Allah kulumbila kwao kwamachili, kuti Allah ngasiŵajimusya ŵandu ŵaakuwa. Yakuona (tachajimusya, achi ni) chilanga chaalikanganiche Nsyene mwakuonaonape, nambo ŵandu ŵajinji ngakumanyilila.');
INSERT INTO "silika" VALUES(16,39,'16:39','(Tachajimusya) kuti ŵalondechesye ayila yaakutindana pa yalakweyo, ni kuti amanyilile aŵala ŵaŵakanile kuti ŵanganyao ŵaliji ŵaunami.');
INSERT INTO "silika" VALUES(16,40,'16:40','Chisimu liloŵe Lyetu pa chindu chilichose pati tuchisachile (kuti chipatikane), tugambaga kuchisalila kuti: “Chiŵe.” Basi ni chikisaŵaga.');
INSERT INTO "silika" VALUES(16,41,'16:41','Sano aŵala ŵaasamile (nsimangwao) ligongo lya Allah panyuma pakutendeledwa lupuso, chisimu chitwatamiche pa duniya mwa umbone, soni malipilo ga ku Akhera (gagakwajembecheya) ni gagali gekulungwa nnope ingaŵe ŵamanyililaga (ŵakusakalaŵa).');
INSERT INTO "silika" VALUES(16,42,'16:42','(Ŵatakaipate yalakweyi ni) aŵala ŵaŵapilile ni kuŵa ali nkwegamila kwa M’mbuje gwaope.');
INSERT INTO "silika" VALUES(16,43,'16:43','Ni nganitutumisyeje paujo penu (kuŵa Achimitume) ikaŵeje achalume ŵatwaliji nkwajuwulila maloŵe. Basi mwawusyani achinsyene lukumbukumbu (lwa itabu yakala) nam’baga jenumanja ngankumanyilila.');
INSERT INTO "silika" VALUES(16,44,'16:44','(Twatumisye Achimitume) ni ilosyo yakuonechela kwisa soni itabu. Nituntuluchisye mmwejo (Muhammadi صلى الله عليه وسلم) Chikumbusyo (Qur’an) kuti mwasalichisye ŵandu yaitulusyidwe kukwao, ni kuti soni ŵanganyao achetelele.');
INSERT INTO "silika" VALUES(16,45,'16:45','Ana aliŵisile pantunjelele aŵala ŵaakutenda malindi ga yakusakala kuti Allah nganaŵa ŵatitimisye nalyo litaka, kapena kwaichilila ilagasyo kuumila upande wangakuumanyilila?');
INSERT INTO "silika" VALUES(16,46,'16:46','Kapena kwakwembekanya ŵanganyao mu kwendajenda kwao, kutendaga ŵanganyao nganaŵa ŵanti nikunnepelekasya (Allah kwapa ipotesi)?');
INSERT INTO "silika" VALUES(16,47,'16:47','Kapena kwakwembekanya aku ali ni woga (wakwembecheya kwapata ilagasyo pakwapungula jumojumo mpaka kumala wosope)? Basi chisimu M’mbuje gwenu ni Mpole kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(16,48,'16:48','Ana nganaiona ichindu yaagumbile Allah yaikupendama iwulili yakwe chakun’dyo ni kunchiji kunsujudila Allah aku ili yakulinandiya?');
INSERT INTO "silika" VALUES(16,49,'16:49','Soni yosope yaili kumawunde ni yaili petaka ikunsujudilaga Allah, kutandila chinyama chilichose chakuulukutaga ni Achimalaika ŵakwe, soni ŵanganyao ŵangalikwesya.');
INSERT INTO "silika" VALUES(16,50,'16:50','Akasan’jogopaga M’mbuje gwao jwali pachanya pao, soni akasatenda (yosope) yaakulamulidwaga.');
INSERT INTO "silika" VALUES(16,51,'16:51','Soni Allah atite: “Ngasillitendela milungu jiŵili, chisimu Jwalakwe ni Nnungu Jumpepe. Basi Jikape Une njogopani.”');
INSERT INTO "silika" VALUES(16,52,'16:52','Soni ni Yakwe yosope yaili kumawunde ni petaka, soni ni Kwakwe-kwakwepe galagatilwa ndaŵi syosope. Ana timun’jogope (jwinepe) jwangaŵaga Allah?');
INSERT INTO "silika" VALUES(16,53,'16:53','Soni ukoto uliose wankwete basi utyochele kwa Allah (nambo jenumanja wangatogolela), kaneka naga isausyo inkwayiye basi ni nkulililaga Kukwakwe.');
INSERT INTO "silika" VALUES(16,54,'16:54','Nambo pati ankungunukulile isausyo, papopo likuga line mwa jenumanja likum’bwanganyaga M’mbuje gwao (ni isanamu).');
INSERT INTO "silika" VALUES(16,55,'16:55','Kuti akanile ukoto watwapele, basi sengwagani (panandipe), mwanti tinchiika kumanyilila (mbesi jaitendo yenu).');
INSERT INTO "silika" VALUES(16,56,'16:56','Ni akuiŵichila ichindu yangakuimanyilila (kuti yakupelekaga ipotesi kapena yakamuchisyaga, yaili isanamu) liunjili mu ichindu yatwapele (mpela mbeju ni ilango, pakuŵecheta kuti: “Ayi ya Allah nambo ayi ya akwawanganya ŵetu ni Allah.”) Ngunnumbila Allah! Chisimu chene chinchiwusyidwa ŵanganyammwe yamwaliji nkusepelela.');
INSERT INTO "silika" VALUES(16,57,'16:57','Ni akum’bichila Allah ŵanache ŵachikongwe (kuti jwaŵeleche ŵanache ŵachikongwe ŵali Achimalaika), kuswejela ni Kwakwe (juŵambalene nayo yaakunnambuchisyao)! Sano ŵanganyao ni akulisagulila ŵaakwanonyela (ŵali ŵanache ŵachilume)!');
INSERT INTO "silika" VALUES(16,58,'16:58','Soni jumo jwao ndema jaakupedwa abali ja (kupagwa kukwakwe) mwanache jwankongwe, ngope jakwe jikasaŵa jakupiilila, soni jwalakwe gakasan’gumbala madandausi.');
INSERT INTO "silika" VALUES(16,59,'16:59','Akasalisisa ku ŵandu ligongo lya kusakala kwa abali jaasalilildwe. (Akasatanda kuliwusya yanti): Ana atame najo pamo ni kwaluka (pameso pa ŵandu), kapena agambe kunsilila mwitaka (jwachijumi)? Naalole, kaje kusakala nnope chiilanyo chao!');
INSERT INTO "silika" VALUES(16,60,'16:60','Kwa aŵala ŵangakukulupilila ya Akhera kwana chilandanyo chakusakala (chakwalandanyichisya), sano Allah ni jukwete lumbili lwapenani nnope (kwanti pangali chakunnandanyichisya nacho), soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(16,61,'16:61','Soni ingaŵe kuti Allah akwakwembekanyaga ŵandu (pakwapa ilagasyo mwachitema) ligongo lya lupuso lwao, nga nganachileka palakwe (petakapo) chinyama chilichose chakuulukutaga, nambo kuti akwachelewasyaga mpaka pandema jekolanjidwe, basi pati jiyiche ndema jaojo nganaŵa ajichelewasisye kwakandaŵi namuno kujijanguya.');
INSERT INTO "silika" VALUES(16,62,'16:62','Ni akum’bichila Allah yaakuichima ŵanganyao (ŵali ŵanache ŵachikongwe), sano ndimi syao ni sikusimulila unami wanti: Akwete kupata ŵanganyao umbone (kwa Allah). Pangali kaichila yanti Moto ni wao, soni yanti ŵanganyao tachilechedwa (mwalakwemo).');
INSERT INTO "silika" VALUES(16,63,'16:63','Ngunnumbila Allah! Chisimu chene twatumisye (Achimitume) ku mikutula ja paujo penu (mmwe Muhammadi صلى الله عليه وسلم), basi Shetani ni jwasalalichisye itendo yao, sano jwalakwe ni nsyoŵe jwao lelo (pa duniya pano, nambo ku Akhera tachiŵengana najo), soni tachipata ŵanganyao ilagasyo yakupoteka kusyene.');
INSERT INTO "silika" VALUES(16,64,'16:64','Ni nganituntuluchisya (mmwe Muhammadi صلى الله عليه وسلم) Chitabu (cha Qur’an) ikaŵeje kuti mwatagulile mwakupikanika ŵanganyao ayila yaatindene pa yalakweyo, ni kuti (chiŵe) chongolo kwisa soni chanasa kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(16,65,'16:65','Sano Allah atulwisye mesi (gawula) kuumila kwinani, basi ni galakwego alisyusyisye litaka panyuma pakuwa kwakwe. Chisimu mu yalakweyo mwana chilosyo (chakwasyusya ŵawe pa Kiyama) kwa ŵandu ŵaakupikanaga (ni kuchetelela).');
INSERT INTO "silika" VALUES(16,66,'16:66','Soni chisimu nkwete ŵanganyammwe ichetelelo mu ilango, tukummwesyaga yaili m’matumbo mwakwe, yaikopokaga chilikati chandoŵe ni myasi, (wauli) nkaka wakulenjela wakumwedwa chenene kwa akumwa.');
INSERT INTO "silika" VALUES(16,67,'16:67','Ni mu isogosi yatende ni zabibu nkasalinganyaga kuumila mwalakwemo yakumwa yakolelwasya kwisa soni lisiki lyambone. Chisimu mu yalakweyo mwana chilosyo (chakulosya ukombosi wa Allah) kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(16,68,'16:68','Soni Ambuje ŵenu ŵasijuwulile nyuchi kuti: “Lilinganyichisyani majumba m’matumbi kwisa soni mu itela ni (m’misinga) jaakukolekaga (ŵandu).”');
INSERT INTO "silika" VALUES(16,69,'16:69','“Kaneko nillyeje isogosi yantundu uliose, basi ni n’jendeje m’matala ga Ambuje ŵenu gagajepepasyidwe (kukwenu).” Ikasakopokaga mu ndumbo syakwe yakumwa yakulekangana ntundu wakwe (wauli uchi), mwalakwemo mwana ntela waŵandu (wakupolela ilwele yaijinji). Chisimu mu yalakweyo mwana chilosyo (chakulosya ukombosi wa Allah) kwa ŵandu ŵaakuchetelela.');
INSERT INTO "silika" VALUES(16,70,'16:70','Sano Allah ŵan’gumbile ŵanganyammwe, kaneko champe chiwa, sano ŵane mwa jenumanja pana ŵaakutendesyedwa kuujila ku umi wakusakala (wauchekulu wannope) mwanti ngaakumanyililaga chilichose panyuma pa umanyilisi (waŵakwete). Chisimu Allah ni Jwakumanyilila nnope, Jwakombola kusyene.');
INSERT INTO "silika" VALUES(16,71,'16:71','Soni Allah ŵaatupiye ŵane mwa jenumanja palisiki kwapunda ŵane, nambo aŵala ŵapedwile lyejinji nganaŵa agaŵile lisiki lyaolyo kwa aŵala (achikapolo) ŵagapatile makono gao gan’dyo; kuti ŵanganyao ni aŵe ŵakulandana pa lisikilyo. (Nambi uli nkunnandanya Allah ni achikapolo Ŵakwe kutendaga jenumanja nganim’ba nkundile kulandana ni achikapolo ŵenu?) Ana nambi akuukana ukoto wa Allah?');
INSERT INTO "silika" VALUES(16,72,'16:72','Soni Allah an’guumbiile ŵanganyammwe achimmasyeto ŵantundu wenu, ni am’biichiile kuumila mwa achimmasyeto ŵenuo ŵanache kwisa soni isukulu, ni ampele soni ichindu yambonembone. Ana yaukoleko ni yaakuikulupilila, sano ukoto wa Allah ŵanganyao ni akuukana?');
INSERT INTO "silika" VALUES(16,73,'16:73','Ni akuigalagatila (inepe) yangaŵaga Allah, yangaŵa nkwapa lisiki lililyose kuumila kumawunde ni petaka, soni yangapakombola (chilichose).');
INSERT INTO "silika" VALUES(16,74,'16:74','Basi ngasimunnandanyichisyaga Allah ni ilandanyo iliyose, chisimu Allah akumanyilila (yanti jwangali ŵakulandana nao), sano ŵanganyammwe ngankumanyilila.');
INSERT INTO "silika" VALUES(16,75,'16:75','Allah apeleche chilandanyo chakapolo jwajupatidwe, jwangalikombolela pa chilichose, ni mundu jwatumpele kuumila Kukwetu lisiki lyambone, basi jwalakwe ni akutola mu lisikilyo mwakusisa nimwakuonechela. Ana mpaka alandane? (Nambi uli nkunnandanya Allah ni isanamu?) Lumbili losope lwene alapikwe Allah. Nambo kuti ŵajinji mu ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(16,76,'16:76','Ni apeleche soni Allah chilandanyo (chine) chaŵandu ŵaŵili. Jumo mwa ŵaŵilio bubu, jwangalikombolela pachilichose, soni jwalakwe jugambile kuŵa nsigo ŵakwasitopesya abwana ŵakwe, kulikose kuchannungamisye jwangaujako ni yambone (achi ni chilandanyo cha kafili). Ana mpaka alandane jwalakweju ni ajula jwaakulamula mwa chilungamiko, soni jwalakwe ali petala lyagoloka (jwali Nsilamu)?');
INSERT INTO "silika" VALUES(16,77,'16:77','Soni iŵele Yakwe Allah yakusisika ya kumawunde ni petaka. Ninganijiŵa ngani ja (kaiche ka) Kiyama ikaŵeje mpela kupila kwa liso kapena jele kuŵandichila nnope. Chisimu Allah pachindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(16,78,'16:78','Sano Allah ŵankopwesye ŵanganyammwe m’matumbo mwa achikulu ŵenu nkakumanyilila chilichose, ni ŵam’bichile kupikana ni kulola kwisa soni mitima kuti m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(16,79,'16:79','Ana ngaakuiona ijuni yaijepepasyidwe (paguluka) munnengalenga wa kwinani, pangali jwaakuijangata ikaŵeje Allah. Chisimu mu yalakweyo mwana ilosyo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(16,80,'16:80','Soni Allah am’bichile jenumanja kutulalila m’majumba mwenu, ni am’bichile soni majumba kuumila m’mapende ga ilango (gagali matenti), gankasagaonaga kuŵa gakwepepala (kuganyakula) palisiku lyakwenda kwenu ulendo ni lisiku lyakutamilichika kwenu (pamalo). Soni kuumila mu wenjewenje wakwe ni manyunya gakwe kwisa soni umbo syakwe (nkasapanganyaga) ikolelo (mpela yakuwala, yakutamila ni yagonela) ni yakusengwasya kwa kandaŵi.');
INSERT INTO "silika" VALUES(16,81,'16:81','Soni Allah am’bichile ŵanganyammwe miulili mu ichindu yaagumbile, soni ni am’bichile mwakuuŵa m’matumbi (mpela mbanga syekulungwakulungwa), ni am’bichile soni yakuwala (yetonji ni wenjewenje) yakun’gosa kuchitukuta (ni kusisima), ni yakuwala (yachisyano) yakun’gosa kungondo syenu. Mwele ni mwaakukwanilichisya ukoto Wakwe kukwenu kuti m’be ŵakulipeleka (Kukwakwe pakunda Usilamu).');
INSERT INTO "silika" VALUES(16,82,'16:82','Nambo naga akugalauka (kuleka Usilamu), basi masengo genu (mmwe Muhammadi صلى الله عليه وسلم) ni kulungusya (utenga) mwakupikanika.');
INSERT INTO "silika" VALUES(16,83,'16:83','Akuumanyililaga ukoto wa Allah kaneka nikuukaana, soni ŵajinji mwa ŵanganyao makafili.');
INSERT INTO "silika" VALUES(16,84,'16:84','Soni (kumbuchilani) lisiku lyatuchitumisya kuumila mu nkutula uliose mboni (jakuutendela umboni nkutulao jwali ntume jwao), kaneka aŵala ŵaŵakanile ngasapedwa lusa (lwakuliŵalanga), soni ŵanganyao ngasakundidwa (kuujila ku duniya) kuti akatende toba ni kuŵenda chikululuko (kwa Allah).');
INSERT INTO "silika" VALUES(16,85,'16:85','Sano ndema jatakaione ilagasyo aŵala ŵaŵalitesile lupuso (ni kutanda kulila ni kudandaula); basi ngaija kupungusyidwa kukwao, soni ngaja kupedwa lipesa.');
INSERT INTO "silika" VALUES(16,86,'16:86','Soni aŵala ŵaŵawanganyaga (Allah ni isanamu) ndema jatakaaone akwawanganya ŵaowo takajile: “Ambuje ŵetu! Ŵanganyaŵa ni akwawanganya ŵetu aŵala ŵatwaliji nkwalumbanjila kunneka Mmwe.” Nambo ŵanganyao takaponyele maloŵe (gaogo achitiji): “Chisimu ŵanganyammwe ŵaunami.”');
INSERT INTO "silika" VALUES(16,87,'16:87','Ni takalipeleche kwa Allah mwakuligaasya pa lyele lisikuli, Soni (isanamu) yaŵaliji nkuisepelela (kuti tiyichajokola kwa Allah) ichasokonechela.');
INSERT INTO "silika" VALUES(16,88,'16:88','Aŵala ŵakanile ni kwasiŵililaga (ŵandu) kwitala lya Allah, chitukajonjechesye ilagasyo pachanya pa ilagasyo ligongo lyakuwatanganya kuŵaliji nkuwatanganya.');
INSERT INTO "silika" VALUES(16,89,'16:89','Soni (kumbuchilani) lisiku lyatuchijitumisya mboni ku nkutula uliose jakuumila mu ŵanganyapeo jichijachonjela, ni tuchiyika namwe (mmwe Muhammadi صلى الله عليه وسلم) kuŵa mboni pa ŵanganyaŵa. Soni tuntuluchisye Chitabu (Qur’an) chachikusalichisya chindu chilichose, chachili soni chongolo, chanasa ni abali jakusengwasya kwa Asilamu.');
INSERT INTO "silika" VALUES(16,90,'16:90','Chisimu Allah akuchisya chilungamiko, kolosya (masengo) ni kwapa ŵapaulongo (yakuŵajilwa kwapa), ni akukanya yakunyalaya (yaili ilemwa yekulungwakulungwa mpela chikululu) ni yakusakala kwisa soni uwwanga, akum’bunda ŵanganyammwe kuti m’be ŵakumbuchila.');
INSERT INTO "silika" VALUES(16,91,'16:91','Soni kwanilisyani chilanga cha Allah pati ntaŵene (Najo), ni ngasin’gopola kulumbila kwenu panyuma pakukuliiya chenene, kutendaga pamasile pamum’bisile Allah kuŵa Mboni pa jenumanja. Chisimu Allah akumanyilila yankutenda.');
INSERT INTO "silika" VALUES(16,92,'16:92','Soni ngasim’ba nti chisawu ajula (jwammasyeto) juŵakululenye-kululenye usi wakwe panyuma pakuuluka mwamachili, pakukutenda kulumbila kwenu kuŵa litala lyakanyengano chilikati chenu ligongo lyakuŵa likuga line lyejinji kupunda likuga line (pakwaleka ŵamwataŵene nao chilanga ligongo lyakwaona unandi, ni kuja kamulana nao ŵanganintaŵana nao chilanga ligongo lyakwaona winji). Chisimu Allah akun’dinga jenumanja ni yalakweyi. Soni chisimu chene chachintagulila ŵanganyammwe lisiku lya Kiyama yamwaliji nkutindana mu yalakweyo.');
INSERT INTO "silika" VALUES(16,93,'16:93','Sano Allah angasache angantesile kuŵa nkutula umpepe, nambo kuti akunnechelelaga kusokonechela jwansachile ni kun’jongola jwansachile, soni chisimu chene tinchiwusyidwa jenumanja yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(16,94,'16:94','Soni ngasinkutenda kulumbila kwenu kuŵa litala lyakanyengano chilikati chenu, litakwika mwalitelesye likau (petala lya Usilamu) panyuma pakwimilichika, ni kupasya ŵanganyammwe yakusakala (pa duniya pano) ligongo lyakusiŵilila kwenu (ŵandu) kwitala lya Allah, ni kwisa kupata soni ŵanganyammwe ilagasyo yakupoteka nnope (ku Akhera).');
INSERT INTO "silika" VALUES(16,95,'16:95','Soni nkasumanyaga chilanga cha Allah ni ntengo wannono (wa duniya), chisimu yaili kwa Allah ni yaili yambone nnope kukwenu nam’baga jenumanja ŵakumanyililaga.');
INSERT INTO "silika" VALUES(16,96,'16:96','Iliyose yaili kukwenu ichimala, nambo iliyose yaili kwa Allah (yakun’dipila pamasengo genu gambone) ni yaili yangamala, soni chisimu chene aŵala ŵaŵapilile tuchapa malipilo gao (gamakulungwa nnope) mwakulandanila ni yambone nnope yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(16,97,'16:97','Jwalijose jwatatende yambone – chinga jwannume kapena jwankongwe – ni ali jwakulupilila; basi chisimu chene Uwwe chituntamiche jwalakwejo umi wambone (pa dunia pano), soni chisimu (ku Akhera) tuchapa malipilo gao (gamakulungwa nnope) mwakulandanila ni yambone nnope yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(16,98,'16:98','Sano pandema jankusaka kusooma Qur’an, basi lijuyaani mwa Allah ku shetani jwakuŵinjidwa.');
INSERT INTO "silika" VALUES(16,99,'16:99','Chisimu jwalakwe nganakola machili ku aŵala ŵaakulupilile, ni kuŵaga soni ali nkwegamila kwa M’mbuje gwao.');
INSERT INTO "silika" VALUES(16,100,'16:100','Nambo machili gakwe gali pa aŵala ŵaakusyoŵekana najo (pakunkuya ni kunkunda malamusi), ni aŵala ŵaakuŵa ali nkum’bwanganya najo (ni Allah).');
INSERT INTO "silika" VALUES(16,101,'16:101','Sano naga tuli tutindenye (pakuŵika) Ȃya pamalo pa Ȃya (jine) – kutendaga Allah ni Nkuimanya chenene yaakutulusya – (achimakafili) akasatiji: “Chisimu mmwejo (Muhammadi صلى الله عليه وسلم) ŵakusepelela unami.” (Ngwamba), nambo ŵajinji mu ŵanganyao ngaakumanyilila.');
INSERT INTO "silika" VALUES(16,102,'16:102','Jilani: “Ajitulwisye Nsimu Weswela (Jibulilu) kuumila kwa Ambuje ŵenu mwakuonaonape, kuti ŵalimbikasye aŵala ŵaakulupilile, ni kuŵa chongolo kwisa soni abali jakusengwasya kwa Asilamu.”');
INSERT INTO "silika" VALUES(16,103,'16:103','Soni chisimu chene Uwwe tukumanyilila yanti ŵanganyao akuŵecheta yanti: “Chisimu mundu ni jwaakun’jiganya jwalakwe (Muhammadi صلى الله عليه وسلم).” (Nambo) chiŵecheto cha ajula jwaakun’ganichisya (kuti ni jwaakun’jiganya Muhammadi صلى الله عليه وسلم) chili chachilendo, sano achi (chiŵecheto cha Qur’an) ni chiŵecheto cha Charabu chakupikanika.');
INSERT INTO "silika" VALUES(16,104,'16:104','Chisimu aŵala ŵangakukulupilila ma Ȃya ga Allah, Allah ngasiŵajongola, soni akwete kupata ŵanganyao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(16,105,'16:105','Chisimu ŵaakulukanyaga unami ni aŵala ŵangakukulupililaga ma Ȃya ga Allah, soni ŵanyao ni ŵali ŵaunami.');
INSERT INTO "silika" VALUES(16,106,'16:106','Mundu jwatankane Allah panyuma pachikulupi chakwe (ilagasyo yekulungwa nnope ikun’jembecheya), ikaŵeje jwakanganisyidwe (kulumukula maloŵe ga ukafili) kutendaga ntima wakwe uli wakutamilichika pachikulupi (jwele ngakopoka mu Usilamu), nambo jwatausapangulile ukafili chidali (ni kunnonyela ukafilio), basi kwa ŵanganyao chikuŵe kwana gasabu jakuumila kwa Allah, soni akwete kupata ŵanganyao ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(16,107,'16:107','Yalakweyo ni ligongo lyanti ŵanganyao awunonyele nnope umi wa duniya kujipunda Akhera, ni ligongo lyanti Allah jwangaajongola ŵandu makafili.');
INSERT INTO "silika" VALUES(16,108,'16:108','Ŵanganyao ni aŵala ŵaŵadindile Allah chidindo m’mitima mwao ni m’mawiwi mwao kwisa soni m’meso mwao, soni ŵanyao ni ŵali ŵakwanjila (malamusi ga Allah).');
INSERT INTO "silika" VALUES(16,109,'16:109','Pangali kaichila yanti ŵanganyao ku Akhera ni ŵatakaŵe ŵakwasika.');
INSERT INTO "silika" VALUES(16,110,'16:110','Kaneko chisimu Ambuje ŵenu kwa aŵala ŵasamile panyuma pakulingwa (kwikanila mwakuŵecheta yangaŵajilwa), kaneko ni kulimbangana (pakuputa ngondo petala lya Allah) ni kupilila, chisimu Ambuje ŵenu panyuma pa yalakweyo aŵele Ŵakululuka kusyene, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(16,111,'16:111','(Kumbuchilani) lyuŵa lyataachiika mundu jwalijose ali nkuliichenjela nsyene, ni tachilipidwa mundu jwalijose mwakwanila pa yawapanganyisye, soni ŵanyao ngasaŵa ŵakutendedwa lupuso.');
INSERT INTO "silika" VALUES(16,112,'16:112','Soni Allah apeleche chilandanyo cha Musi (wa Maaka) wawaliji wantunjelele soni wakutamilichika, lyauichililaga lisiki lyakwe mwawinji kuumila malo galigose, nambo wakaaniile indimba ya Allah, basi Allah ŵaupele kupasya chiwaalo chasala ni woga ligongo lya yaŵaliji (achinsyene musio) ali nkutenda.');
INSERT INTO "silika" VALUES(16,113,'16:113','Soni chisimu chene jwaichilile Ntenga (Muhammadi صلى الله عليه وسلم) jwakuumila mu ŵanganyapeo, nambo ŵankaaniile, basi ni yakwembekenye ilagasyo kutendaga ŵanganyao ali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(16,114,'16:114','Basi lyagani mu yampele Allah yaili ya halaal nambo soni yambone, ni ntogoleleje ukoto wa Allah nam’baga jenumanja nkun’galagatila Jwelepejo.');
INSERT INTO "silika" VALUES(16,115,'16:115','Chisimu (Allah) ayitesile haraam kukwenu (kulya) chibudu, myasi, nyama jeguluŵe ni chachikolanjilidwe (lina linepe) lyangaŵaga lya Allah pakuchisikita (kuŵa mbepesi ja akumasoka), nambo jwachasingalilwe (nikulya cha haraamucho) mwangali kuchiluumba kapena kusumba mpika, basi (ngaapata sambi) chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(16,116,'16:116','Ni ngasim’bechetaga yakwayana ni ayila yasikusimulila ndimi syenu mwaunami (kuti): “Ayi ya halaal, ayi ya haraam,” kuti munsepelele Allah unami. Chisimu aŵala ŵaakunsepelela Allah unami nganaŵa apundile.');
INSERT INTO "silika" VALUES(16,117,'16:117','Kusengwa kwannono (chikuŵe kwao pa duniya pano), nambo akwete kupata ŵanganyao ilagasyo yakupoteka nnope (ku Akhera).');
INSERT INTO "silika" VALUES(16,118,'16:118','Ni ku aŵala ŵaŵaliji Ayuda twakanyisye (kulya) yatwansimbile kala, ni nganitwatenda Uwwe lupuso, nambo achimisyene ni ŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(16,119,'16:119','Kaneko chisimu Ambuje ŵenu ku aŵala ŵatesile yakusakala mwaujinga, kaneko ni kutenda toba panyuma pa yalakweyo, ni kolosya (masengo), chisimu Ambuje ŵenu (Allah) panyuma pa yalakweyo aŵele Ŵakululuka kusyene, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(16,120,'16:120','Chisimu Ibrahima jwaliji nnongola (juŵasonganganyisye mbili syosope syambone), jwakunda malamusi ga Allah mwakulinandiya, jwakupendechela ku Uujika wa Allah (pakun’galagatila Jwelepejo) soni nganaŵa mwa akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(16,121,'16:121','(Jwaliji) jwakutogolela ukoto Wakwe (wa Allah), ŵansagwile ni kun’jongolela kwitala lyagoloka.');
INSERT INTO "silika" VALUES(16,122,'16:122','Ni twampele yambone pa duniya pano, soni chisimu jwalakwe ku Akhera chakaŵe mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(16,123,'16:123','Kaneka twan’juwulile mmwe (Muhammadi صلى الله عليه وسلم) yanti: “Kuyani dini ja Ibrahima (juŵaliji) jwakupendechela ku Uujika wa Allah, soni nganaŵa mwa akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(16,124,'16:124','Chisimu Lyakuŵeluka lyaŵichidwe ku aŵala ŵaŵatindene pa ya lyalakwelyo, soni chisimu Ambuje ŵenu tachijilanya chilikati chao lisiku lya Kiyama pa ichindu yaŵaliji nkutindana.');
INSERT INTO "silika" VALUES(16,125,'16:125','Ŵilanjilani (mmwe Muhammadi صلى الله عليه وسلم) kwitala lya Ambuje ŵenu mwa lunda lwakusokoka ni kaŵilanjile kambone, soni ponyanganani nao maloŵe kaponyangane kambone, chisimu Ambuje ŵenu ni ŵaakwamanya chenene ŵaasokonecheele kuleka litala Lyakwe, soni Jwalakwe ni jwaakwamanya chenene ŵali ŵakongoka.');
INSERT INTO "silika" VALUES(16,126,'16:126','Sano pati nchiusyaga mbusyo sya ilagasyo, basi usyani mbusyo syakulandana ni ilagasyo yaŵantendele, nambo naga mpilile (pakuleka kuuchisya), chisimu yalakweyo tiiŵe yambone kwa akupilila.');
INSERT INTO "silika" VALUES(16,127,'16:127','Soni pililani, nganikuŵatu kupilila kwenuko ikaŵeje mwa Allah (mu ukombosi Ŵakwe ni lisosa Lyakwe), ngasin’dandaula ya ŵanganyao, soni ngasim’ba pakangani m’malindi gaakutenda.');
INSERT INTO "silika" VALUES(16,128,'16:128','Chisimu Allah ali pamo ni aŵala ŵaakun’jogopa (Jwalakwe) kwisa soni aŵala atenda ngoto.');
INSERT INTO "silika" VALUES(17,1,'17:1','Kuswejela ni Kwakwe Ajula (Allah) juŵan’jesyisye kapolo Jwakwe (Muhammadi صلى الله عليه وسلم) chilo kutyochela ku Nsikiti Wakuchimbichika (wa Maaka) kuja ku Nsikiti Wakunaula (wa Baitul-Muqaddas) awula watwaipele upile yayaliji mungulugulu mwakwe, kuti tunnosye jwalakwe (Muhammadi صلى الله عليه وسلم) ine mu isimosimo Yetu, Chisimu Jwalakwe (Allah) ni Jwakupikanichisya, Jwakulolechesya. ');
INSERT INTO "silika" VALUES(17,2,'17:2','Ni twampele Musa chitabu (cha Taurat), ni twachitesile kuŵa chongolo ku ŵanache ŵa Israila. (Allah ŵasalile kuti): “Ngasimuntenda (jwinepe) jwangaŵaga Une kuŵa nkun’jegama.”');
INSERT INTO "silika" VALUES(17,3,'17:3','(Ŵasalile soni kuti: “E jenumanja) isukulu ya aŵala ŵatwajigele pampepe ni Nuhu (m’chombo)! (M’beje ŵakutogolela mujwatendelaga nangolo jwenu.) Chisimu jwalakwe (Nuhujo) jwaliji kapolo jwakutogolela kusyene.”');
INSERT INTO "silika" VALUES(17,4,'17:4','Ni twajuwulile ŵanache ŵa Israila m’Chitabu (cha Taurat kuti): “Chisimu ŵanganyammwe tim’bwatanganye pa chilambo kaŵilikose, ni tin’dikwesye kulikwesya kwekulungwa nnope.”');
INSERT INTO "silika" VALUES(17,5,'17:5','“Basi pachayiche chilanga cha (kulipidwa ligongo lya kuwatanganya) kwandanda mwa kuŵiliko, twantumichisye ŵanganyammwe achikapolo Ŵetu achinsyene machili gakuputila ngondo mwaukali, basi ni ŵasyungwilesyungwile nkatikati mwa majumba (kupita nchim’bulaga). Nichaliji chilanga chakutendedwa.”');
INSERT INTO "silika" VALUES(17,6,'17:6','“Kaneka ni twam’buchisye machili gakwakombolela ŵanganyao, ni twan’jonjechesye chipanje ni ŵanache, ni twantesile kuŵa likuga lyejinji.”');
INSERT INTO "silika" VALUES(17,7,'17:7','(Nitwatite): “Naga ntende yambone, nikuti nkulitendela yamboneyo mwachimisyene, nambo naga nsakasye, nikuti nkulisakachisya mwachimisyene. Sano pachayiche chilanga (chakulipidwa ligongo lya kuwatanganya) kwambesi, (twalechelele amagongo ŵenu) kuti asijigasye nchesela ngope syenu, ni kuujinjila Nsikiti (wa Baitul-Muqaddas) muŵaujinjilile kandanda, ni konanga chilichose chaachitawalile konanga kwekulungwa nnope.”');
INSERT INTO "silika" VALUES(17,8,'17:8','(Twasalile m’Chitabu cha Taurat kuti: “Naga nnondeche mu yakutenda yenu), chisimu M’mbuje gwenu tantendele chanasa, nambo naga m’bujauje (kutenda yangalumbana), chituujauje (kumpa ipotesi), soni tujitesile Jahannama kwa achimakafili kuŵa kolokoni (ndende).”');
INSERT INTO "silika" VALUES(17,9,'17:9','Chisimu aji Qur’an jikongolela ku alila (litala) lyalili lyagoloka nnope, ni jikwapa abali jakusengwasya aŵala ŵaakutenda yambone kuti akwete kupata ŵanganyao malipilo gamakulungwa.');
INSERT INTO "silika" VALUES(17,10,'17:10','Ni kuti aŵala ŵangakukulupilila ya Akhera twalinganyichisye ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(17,11,'17:11','Soni mundu akasaŵendaga yakusakala (mwakupupuluma) yakasatiji pakuŵenda yambone, sano mundu aŵele jwakupupuluma.');
INSERT INTO "silika" VALUES(17,12,'17:12','Ni tuŵisile chilo ni muusi kuŵa ilosyo iŵili, ni tuchifufwite chilosyo chachilo (ni chipi), ni tuchitesile chilosyo cha muusi kuŵa chakuŵala (ni lilanguka lyakwe) kuti nsosejesoseje umbone wakuumila kwa M’mbuje gwenu, soni kuti mmanyilileje winji wa yaka ni chiŵalanjilo (cha masiku, ijuma ni myesi). Soni chindu chilichose tuchilondesye mwakuchisalichisya chenene.');
INSERT INTO "silika" VALUES(17,13,'17:13','Soni mundu jwalijose tuntaŵilile itendo yakwe nnukosi mwakwe, ni chitukankopochesye lisiku lya Kiyama chitabu chachakachisimane chili cheunukuleunukule.');
INSERT INTO "silika" VALUES(17,14,'17:14','(Chakasalilidwe kuti): “Soomaani chitabu chenu, mmwejo mwasyene lelo nkukwanila kuliŵalanjila (itendo yenu).”');
INSERT INTO "silika" VALUES(17,15,'17:15','Mundu jwaajongweche, basi nikuti akongoka kwakulikamuchisya nsyene, soni jwasochelele, basi nikuti akusochelela kwaalikonya nsyene, soni jwaatwiichiile nsigo (wasambi) ngasaja kutwichila nsigo wajwine, soni Uwwe nganituŵa ŵakupeleka ilagasyo mpaka tukatumisye (kaje) ntenga.');
INSERT INTO "silika" VALUES(17,16,'17:16','Soni tuli tusachile kuujonanga musi (achinsyene musio ali nkutenda yangalumbana) tukwalamulaga (kaje) ŵakulipatila ŵao (yakun’jitika Allah ni kuŵa atenda ngoto), basi ni akasapitikukaga (kumpitikuchila Allah) mwalakwemo, pele ni palikasasimichikaga liloŵe (lyailagasyo) pa wele (musio), basi ni tukasaujonangaga kusyene.');
INSERT INTO "silika" VALUES(17,17,'17:17','Ana soni jilingwajilingwa mwa mikutula jatwajijonasile panyuma pa Nuhu! Sano Ambuje ŵenu akwanile pasambi sya achikapolo Ŵakwe kuŵa Nkumanyilila chenene, Nkulolechesya.');
INSERT INTO "silika" VALUES(17,18,'17:18','Mundu jwaakuŵa nkusachilila umi wachitema (wa duniya), chitun’janguichisye (kumpa) mwalakwemo yatukusaka (kupeleka) kwa jwatukunsaka, kaneka Uwwe tum’biichiile jwalakwe (ku Akhera) Jahannama, takajijinjile ali jwakunyosyedwa soni jwakuŵinjidwa (kuchanasa cha Allah).');
INSERT INTO "silika" VALUES(17,19,'17:19','Sano jwatasache Akhera ni kulimbichila kujitendela masengo gakwe, aku ali jwakulupilila, basi ŵanganyao kulimbichila kwaoko kuchiŵa kwakutogoleledwa.');
INSERT INTO "silika" VALUES(17,20,'17:20','Ŵaliose – ŵanganyaŵa ni ŵanganyao (ŵambone ni ŵakusakala) – tukwapaga mu yakupeleka ya Ambuje ŵenu (Allah), ni nganiiŵa yakupeleka ya Ambuje ŵenu yanti ni kusiŵiilidwa (kuti ikampata chiumbe Chakwe).');
INSERT INTO "silika" VALUES(17,21,'17:21','Nnole yatutite pakwachimbichisya (pa duniya pano) ŵane kwapunda ŵane, sano chisimu Akhera ni jichiŵa jekulungwa nnope pa mauchimbichimbi soni jekulungwa nnope pa kapundane.');
INSERT INTO "silika" VALUES(17,22,'17:22','Ngasim’biika pamo ni Allah nnungu jwine, ntakwisa mum’bele ŵagambidwa ni kulechedwa (ku Moto ŵangali ŵakun’jokola).');
INSERT INTO "silika" VALUES(17,23,'17:23','Soni Ambuje ŵenu (Allah) alamwile yanti ngasin’galagatila ŵanganyammwe (jwinepe) ikaŵeje Jwele pejo, ni kwatendela yambone achinangolo ŵaŵili. Naga jumo jwao akuuyika uchekulu (juli) kukwenu kapena ŵanaŵaŵili, basi ngasimwaŵechetela (maloŵe gachipongwe atamose kuti) ah! Soni ngasimwakalipila, ni mwaŵecheteleje maloŵe gauchimbichimbi.');
INSERT INTO "silika" VALUES(17,24,'17:24','Soni mwagoyechesyeje lipapiko lyakulinandiya pakwatendela chanasa, ni m’becheteje kuti: “Ambuje ŵangu! Mwatendelani chanasa ŵanaŵaŵilio mpela muŵanelelaga une kuwanache.”');
INSERT INTO "silika" VALUES(17,25,'17:25','M’mbuje gwenu jukuimanya yaili m’mitima mwenu, nam’baga jenumanja ŵambone (kwa achinangolo ŵenu ndaŵi ni katema, kaneka ni kusoya panandi), basi chisimu Jwalakwe juŵele Jwakululuka kusyene kwa ŵakuujilaujila (Kukwakwe pakutenda toba).');
INSERT INTO "silika" VALUES(17,26,'17:26','Soni jwapaulongo mumpani liunjili lyakwe (kuumila mu chipanje chenu), kwisa soni masikini ni jwapaulendo, ni ngasimmisanga (chipanje chenu) namisangape.');
INSERT INTO "silika" VALUES(17,27,'17:27','Chisimu akumisanga aŵele achin’jao mashetani, sano shetani kwa Ambujegwe (Allah) juŵele jwakanila kusyene (chindimba champele).');
INSERT INTO "silika" VALUES(17,28,'17:28','Sano naga nkwatundumalila ŵanganyao (pangapa liunjili lyao ligongo lyanti nganinkola), nambo nchisosagasosaga ukoto ŵakuumila kwa Ambuje ŵenu (Allah) wankuujembecheya, basi mwaŵechetelani maloŵe gakwalukangana (ni kutulasya ntima, mpela kuti: Naga mbatemo chinan’gaŵile).');
INSERT INTO "silika" VALUES(17,29,'17:29','Soni ngasim’buŵika nkono wenu kuŵa (mpela) utaŵidwe nnukosi mwenu (pakuleka kutola), soni ngasiwwujongola kuujongola kosope kwene (pakutola mwakumisanga) ntakwisa mum’bele ŵagambidwa (ligongo lyaubaili) ni kulaga nnope (ligongo lyakumisanga).');
INSERT INTO "silika" VALUES(17,30,'17:30','Chisimu Ambuje ŵenu akunsapangulilaga lisiki jwansachile, ni kunnandiyichisya (jwansachile). Chisimu Jwalakwe kwa achikapolo Ŵakwe juŵele Jwakumanyilila chenene, Jwakulolechesya (akummanya jwakuŵajilwa utajili ni jwakuŵajilwa kulaga).');
INSERT INTO "silika" VALUES(17,31,'17:31','Soni ngasim’bulaga ŵanache ŵenu pakogopa kulaga, Uwwe chitwape lisiki ŵanganyao ni jenumanja ŵakwe. Chisimu kwaulaga ŵanganyao chiŵele chilemwa chekulungwa.');
INSERT INTO "silika" VALUES(17,32,'17:32','Soni ngasinchiŵandichila chikululu, chisimu chalakwecho chiŵele usakwa, soni (liŵele) litala lyangalumbana.');
INSERT INTO "silika" VALUES(17,33,'17:33','Soni ngasim’bulaga mundu jwaakanyisye Allah (kum’bulaga) ikaŵeje paligongo lyakwitichika (ni malamusi ga Dini), sano mundu jwaulajidwe mwalupuso, basi pamasile patum’bichile machili jwawinjila jwakwe (gakuusya mbusyo, kapena kupochela malipilo, kapena kululuka), ningasajonanga (jwawinjilajo) pa kuulaga, chisimu jwalakwe juŵele jwakamuchisyidwa (ni malamusi ga Dini).');
INSERT INTO "silika" VALUES(17,34,'17:34','Soni ngasinchiŵandichila chipanje cha mwanache jwaŵina ikaŵeje mwitala lyalili lyambone (mpela kuchitupiya ni malonda), mpaka akuyiche komangala kwakwe (ni kumpaga), soni kwanilisyani chilanga, chisimu chilanga chichiŵa chakuwusyidwa (pa Kiyama).');
INSERT INTO "silika" VALUES(17,35,'17:35','Soni gumbasyani chipiimo chambale ndema jankupima, soni n’dinjeje nisikelo jakulungamika, yalakweyo ni yaili yambone (kukwenu) soni ni yaikwete mbesi jambone nnope (ku Akhera).');
INSERT INTO "silika" VALUES(17,36,'17:36','Soni ngasinyyikuyakuya (pakuiŵecheta kapena kuitenda) ichindu yanganinkola nayo umanyilisi. Chisimu mawiwi, meso ni ntima ya chilichose cha yalakweyo yichiŵa yakuwusyidwa (ni Allah).');
INSERT INTO "silika" VALUES(17,37,'17:37','Soni ngasin’jenda pa chilambo mwakutwanga, chisimu mmwe ngaŵa nkupowola litaka soni ngaŵa nkugaika matumbi pakaleupe.');
INSERT INTO "silika" VALUES(17,38,'17:38','Yosope yeneyi kusakala kwakwe kuŵele kwakuŵenjedwa nnope kwa Ambuje ŵenu (Allah).');
INSERT INTO "silika" VALUES(17,39,'17:39','Yalakweyi ni (ine) mu yalunda lwakusokoka yan’juwulile Ambuje ŵenu (mmwe Muhammadi صلى الله عليه وسلم), ni ngasim’biika pamo ni Allah nnungu jwine ntakwika mumponyedwe ku Jahannama n’di ŵagambidwa soni ŵakuŵinjidwa (kuchanasa cha Allah).');
INSERT INTO "silika" VALUES(17,40,'17:40','Ana M’mbuje gwenu ansagulile jenumanja (achimakafili ŵa pa Maaka) ŵanache ŵachilume, sano Jwalakwe ni alitendele mwa Achimalaika ŵanache ŵachikongwe? Chisimu ŵanganyammwe nkuŵecheta liloŵe lyekulungwa nnope.');
INSERT INTO "silika" VALUES(17,41,'17:41','Soni pamasile patusalichisye nnope (chilichose) mu aji Qur’an kuti (achimakafili) apate kumbuchila, ni ngajikwajonjechesya ŵanganyao ikaŵeje kujichima ni kujitalichila.');
INSERT INTO "silika" VALUES(17,42,'17:42','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ingaŵe japali pamo ni Jwalakwe (Allah) milungu jine mpela mwaakuŵechetela (achimakafilio), basi jingasosilesosile (milungujo) litala lyakwaulila kwa Nsyene Arishi (Chindanda Chaulungu kuti akamenyane Najo).”');
INSERT INTO "silika" VALUES(17,43,'17:43','Kuswejela ni Kwakwe soni jutalikangene nayo yaakuŵechetayo kutalikangana kwekulungwa nnope.');
INSERT INTO "silika" VALUES(17,44,'17:44','Gakunswejesyaga Jwalakwe mawunde nsano nagaŵili kwisa soni litaka ni yaili mwalakwemo, ni pangali chindu chilichose ikaŵeje chikunswejesyaga ni lumbili Lwakwe, nambo kuti ŵanganyammwe ngankumanyilila kaswejesye kao. Chisimu Jwalakwe juŵele Jwakutulala nnope ntima, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(17,45,'17:45','Sano pati nchisoomaga (mmwe Muhammadi صلى الله عليه وسلم) Qur’an tukasaŵika chilikati chenumwe ni aŵala ŵangakukulupilila ya Akhera chakusiŵilila changaonechela. ');
INSERT INTO "silika" VALUES(17,46,'17:46','Soni tuŵisile iwuniko m’mitima mwao kuti akajimanyilila aji (Qur’an), ni m’mawiwi mwao (tutasile) usito (wa usiiwa kuti akajipikana), soni pati nchasalaga Ambuje ŵenu (Allah) Jikape mu Qur’an, akasagalauchila ku migongo jao kutila (mwakuchimidwa kusyene ni yakuntenda Nnungu kuŵa Jumope).');
INSERT INTO "silika" VALUES(17,47,'17:47','Uwwe tukumanyilila kusyene yaakupikanila ŵanganyao ndema jaakupikanila kukwenu (mmwe Muhammadi صلى الله عليه وسلم nchisoomaga), ni ndema jaakusongonelana. Ndema jaakuŵecheta ŵalupuso (kwasalila Asilamu pakusaka kwasokonesya): “Jenumanja ngankunkuya ikaŵeje mundu jwannojedwe.”');
INSERT INTO "silika" VALUES(17,48,'17:48','Nnole yatite pakunnandanyichisya ni ilandanyo yaukoleko (pane kuti ŵakulogwa pane nsaŵi pane ŵamasoka), basi ni asokonechele, mwanti nganaŵa apakombwele kupata litala (lya chongoko).');
INSERT INTO "silika" VALUES(17,49,'17:49','Ni akasatiji: “Ana uwwe patuchiŵa maupa ni ipande yekatichekatiche, nambo yakuona uwwe tuchiukulidwa nikuŵa yagumbidwa yasambano?”');
INSERT INTO "silika" VALUES(17,50,'17:50','Jilani: “Ŵani maganga kapena isyano,”');
INSERT INTO "silika" VALUES(17,51,'17:51','“Kapena yagumbidwa ine yaikula m’mitima mwenu (yankuiona kuti yakusausya kuwukulidwa, ata n’di yalakweyo chinchiukulidwape).” Pele ni tajile: “Ana ŵani ŵatachituuchisya (ku umi)?” Jilani: “Ajula juŵan’gumbile kandanda.” Pele ni tampukunyichisye mitwe jao ni kuti: “Yele tiichiŵa chakachi?” Jilani: “Kuŵa-nkuŵa pakuŵandika!”');
INSERT INTO "silika" VALUES(17,52,'17:52','“Lisiku lyatachim’bilanga ŵanganyammwe (Allah), ni tinchin’janga mwakunnapilila lumbili Lwakwe, soni ni tinchiganichisya kuti nganintama (pa duniya) ikaŵeje panandipe.”');
INSERT INTO "silika" VALUES(17,53,'17:53','Soni mwasalilani achikapolo Ŵangu kuti (ndaŵi syosope) aŵecheteje yaili yambone, (ligongo) shetani chisimu chene akasakwembelechesyaga maukangani chilikati chao, chisimu shetani kwa mundu aŵele mmagongo jwakuonechela.');
INSERT INTO "silika" VALUES(17,54,'17:54','M’mbuje gwenu ni jwaakummanya chenene ŵanganyammwe, nasakaga chantendele chanasa kapena nasakaga champe ilagasyo. Ni nganituntumisya mmwe (Muhammadi صلى الله عليه وسلم) kuŵa jwakwalolela ŵanganyao.');
INSERT INTO "silika" VALUES(17,55,'17:55','Soni Ambuje ŵenu ni ŵaakwamanya chenene ŵali kumawunde ni petaka. Soni chisimu chene twaachimbichisye Achimitume ŵane kwapunda ŵane, ni twampele Daudi (chitabu cha) Zaburi.');
INSERT INTO "silika" VALUES(17,56,'17:56','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Mwaŵilangani aŵala ŵankulitwanjila (kuŵa milungu mpela Isa, Uzair, Achimalaika ni ŵane) kunneka Jwalakwe (Allah, kuti antyochesye masausyo gagakumpata), basi nganaŵa akwete machili gakuntyochesya chisausyo namuno kuchigalauchisya (kwa ŵane).');
INSERT INTO "silika" VALUES(17,57,'17:57','Aŵala ŵanya ŵakwaŵilanga (kuti ŵatyochesye masausyo, nombenao) akusosagasosaga litala lyakuliŵandichisyila kwa M’mbuje gwao (Allah), kuti ana ŵani ŵataŵe ŵakuŵandichila nnope; ni akwembecheya ukoto Wakwe ni kogopa ilagasyo Yakwe. Chisimu ilagasyo ya Ambuje ŵenu iŵele yakogopedwa.');
INSERT INTO "silika" VALUES(17,58,'17:58','Soni pangali musi uliose ikaŵeje Uwwe tuchiujonanga nkaniliŵe lisiku lya Kiyama, kapena kuulagasya ni ilagasyo yakupoteka nnope (naga uli wakuŵajilwa ni yalakweyo), yalakweyi yaliji yelembelembe m’Chitabu (chailembedwe ichindu yosope).');
INSERT INTO "silika" VALUES(17,59,'17:59','Soni pangali chachikutulekasya kutumisya isimosimo ikaŵeje kuti (ŵandu) ŵandanda ŵaikaaniile. Sano ŵandu ŵa Samuda twapele ngamiya jajikongwe kuŵa chimanyisyo chakuonechela, nambo ŵajitesile lupuso. Ni ngatukutumisyaga isimosimo ikaŵeje pakogoya (ni kutetela).');
INSERT INTO "silika" VALUES(17,60,'17:60','Soni (kumbuchilani) katema katwansalile (mmwe Muhammadi صلى الله عليه وسلم) kuti: “Chisimu Ambuje ŵenu (Allah) ŵasyungulile ŵandu (ali mwigasa Lyakwe ni nganaŵa antesile mmwejo iliyose).” Ni nganituitenda yakuiona yatwannosisye (mmwe Muhammadi صلى الله عليه وسلم chilo cha Isrâi ni Mi‘râji yamwaiweni mesope ngaŵa kulugono) ikaŵeje kuŵa mayeso kwa ŵandu (gakwalinga chikulupi chao), nombe soni chitela chelwesyedwe (cha Zaqqûm chachisalidwe) m’Qur’an (chiŵele mayeso kwa ŵandu kuti ikomboleka uli chitela kumela m’moto)! Ni tukwajogoyaga (pakwatetela), nambo ngakukwajonjechesyaga ikaŵeje kusumba mpika kwekulungwa.');
INSERT INTO "silika" VALUES(17,61,'17:61','Soni (kumbuchilani) katema katwasalile Achimalaika kuti: “Mwasujudilani Adam.” Basi wosope niŵasujudu kusigala Ibulisu, jwalakwe jwatite: “Ana nansujudile jwamun’gumbile kuumila mwilongo?”');
INSERT INTO "silika" VALUES(17,62,'17:62','(Ibulisu) jwatite (soni pakunsalila Allah): “Ana nyyiweni! Aju jwamunchimbichisye kumbunda une, naga mumbe lipesa (lyakuuneka ndili umipe) mpaka lisiku lya Kiyama, chisimu chene chinaajonanje ŵanache ŵakwe pakwapulikasya ikaŵeje ŵannonope.”');
INSERT INTO "silika" VALUES(17,63,'17:63','(Allah) jwatite: “Tyokani! Sano ŵataiche mwakunkuya mmwe mwa ŵanganyao, basi chisimu Jahannama nimalipilo genu malipilo gakwana.”');
INSERT INTO "silika" VALUES(17,64,'17:64','“Soni mwaloŵesyani mwapanandipanandi ŵatinkombole (kwaloŵesya) mwa ŵanganyao ni liloŵe lyenu (mpela ing’wenyeng’wenye, ngoma ni yakwimbajimba iliyose yakanyisyidwa), soni mwasonganganyichisyani (asilikali ŵenu) ŵakwela mahachi ni ŵakwenda wapasi, soni kamusyanganani nao mu ipanje (pakwatendekasya kupata ipanje m’matala gangalumbana mpela wiyi, juga ni kotosya), ni (nkamusyangane nao soni) mu ŵanache (pakwatendekasya kupata ŵanache ŵapetinji), soni ni mwapeje ilanga.” Sano shetani ni jwangapa ŵanganyao ikaŵeje ilanga yaulamba.');
INSERT INTO "silika" VALUES(17,65,'17:65','“Chisimu achikapolo Ŵangu, mmwe nganinkola machili pa ŵanganyao (gakwapulikasya), soni Ambuje ŵenu (Allah) akukwanila kuŵa Nkugosa.”');
INSERT INTO "silika" VALUES(17,66,'17:66','M’mbuje gwenu (Allah) ni Ajula jwaakun’jendechesya yombo m’mbwani kuti mpiteje nchisosasosa ukoto Wakwe. Chisimu Jwalakwe kwa jenumanja juŵele Jwachanasa channope.');
INSERT INTO "silika" VALUES(17,67,'17:67','Sano pati gampatile masausyo m’mbwani, (wosope) ŵankwalumbanjilaga akasasoŵaga kusigala Jwalakwepe (Allah), nambo pati ankulupwisye pakunyyikasya pankuuli, nkasagalauka (kunneka Allah). Sano mundu aŵele jwakanila (ukoto waakumpa, jwangatogolela.) ');
INSERT INTO "silika" VALUES(17,68,'17:68','Ana nkuliŵika pantunjelele wanti Jwalakwe (Allah) ngaŵa nkuntitimisya nao upande wapankuuli, kapena kuntumichisya chimbunga chasakalawe? Kaneko jenumanja ngasimwapata akunchenjela.');
INSERT INTO "silika" VALUES(17,69,'17:69','Kapena nkuliŵika pantunjelele wanti Jwalakwe (Allah) ngaŵa nkum’buchisya jenumanja mwalakwe (m’mbwanimo) kaaŵiili ni kuntumichisya mbungo jaukali ni kummisya ligongo lyaukafili wenu? Kaneko jenumanja ngasimwapata akumputila ngondo Kukwetu pa yalakweyi.');
INSERT INTO "silika" VALUES(17,70,'17:70','Soni chisimu chene pamasile patwachimbichisye ŵanache ŵa Adam, ni twapele yakwela pankuli ni pambwani, ni twapele soni lisiki lyambone, nambo soni twachimbichisye ni uchimbichimbi wekulungwa nnope kuipunda yaijinji mu iwumbe yatugumbile.');
INSERT INTO "silika" VALUES(17,71,'17:71','(Kumbuchilani) Lisiku lyatuchaŵilanga ŵandu wosope (nkutula uliose) ni nnongola jwao. Sano ŵatakapedwe chitabu chao ni nkono wao wan’dyo, basi ŵanganyao chakasoome chitabu chao (mwakusengwa), soni ngasaja kutendedwa lupuso atamose pa (kachindu kamwana kanti chisawu) kawuusi kankati mwa lupele lwa tende.');
INSERT INTO "silika" VALUES(17,72,'17:72','Sano jwali jwangalola apano (pa duniya kulola kwakuchetelela ilosyo ya Allah ni ngankulupilila Jwalakwe), basi jwalakwejo ku Akhera takaŵe jwangalola (kwene) soni takapotele nnope kuleka litala (lyachokoko).');
INSERT INTO "silika" VALUES(17,73,'17:73','Soni chisimu chene ŵanganyao ŵasigele panandi kuti am’bwatanganye (mmwe Muhammadi صلى الله عليه وسلم) kwakuleka ayila yatun’juwulile kuti ntusepelele Uwwe ikaliyo, sano pajele ndemajo akantesile kuŵa chisyoŵe chapantima.');
INSERT INTO "silika" VALUES(17,74,'17:74','Soni ingaŵaga kuti Uwwe twan’dimbangenye, chisimu nkaliji dusi kuti mpendechele kukwao panandi pajakwe.');
INSERT INTO "silika" VALUES(17,75,'17:75','Mwanti (nkapendechele), chisimu tukampele kupasya (ilagasyo) yakonjechesya pa awuno umi uno (kwapunda ŵandu ŵane), ni (ilagasyo) yakonjechesya panyuma pakuwa, kaneko nga nganimwapata akunkulupusya Kukwetu.');
INSERT INTO "silika" VALUES(17,76,'17:76','Soni chisimu chene ŵanganyao ŵasigele panandi kuti an’jogoye nnope m’chilambo achi (cha Maaka) kuti ankoposyemo, sano ingaŵe iyyo, nga nganalonjela panyuma penu ikaŵeje kandaŵi kannono.');
INSERT INTO "silika" VALUES(17,77,'17:77','(Jele nijaliji) nyendele (Jetu kwa) aŵala ŵatwamasile kwatumisya paujo penu (mmwe Muhammadi صلى الله عليه وسلم) mwa achimitenga Ŵetu, soni ngankusimana galausya kulikose ku nyendele Jetu.');
INSERT INTO "silika" VALUES(17,78,'17:78','Jimikani Swala mwakolosya pakupendama kwalyuŵa kwikanila chipi chachilo (syasili swala sya Dhuhr, Asr, Magrib ni Isha), soni (soomani) Qur’an ja kumasikusiku (pa swala ja Fajr). Chisimu Qur’an ja kumasikusiku jiŵele jakutendeledwa umboni (ni Achimalaika).');
INSERT INTO "silika" VALUES(17,79,'17:79','Soni mu ipande ine yachilo basi jimani kuswali pa (kusooma) jalakwe (Qur’aniji), kuŵa swala jakonjechesya kukwenu (mmwe Muhammadi صلى الله عليه وسلم jajili swala ja Tahajjudi), kuti Ambuje ŵenu akan’jimiche pamalo pakulapilidwa (ni iwumbe yosope Lisiku lya Kiyama). ');
INSERT INTO "silika" VALUES(17,80,'17:80','Soni jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ambuje ŵangu! Kutendani kwinjila kwangu (mu nsinda wa Madina) kuŵa kwinjila kwambone, ni nkutende soni kopoka kwangu (mu nsinda wa Maaka) kuŵa kopoka kwambone, ni mumbani kuumila Kukwenu machili gakungamuchisya (kwakombolela amagongo ŵangu).”');
INSERT INTO "silika" VALUES(17,81,'17:81','Soni jilani: “Yakuona iŵiiche, ni imasile paityosile yaukoleko, chisimu yaukoleko (mbesi jakwe) ikasaŵaga yakutyoka.”');
INSERT INTO "silika" VALUES(17,82,'17:82','Soni tukutulusya mu Qur’an wauli ntela ni chanasa kwa ŵakulupilila, ni ngajikwajonjechesya (Qur’anijo) ŵalupuso ikaŵeje chasiko.');
INSERT INTO "silika" VALUES(17,83,'17:83','Soni naga tuli tumpele mundu (kafili) chindimba jukasagalauka (kuleka kutukumbuchila ni kutuŵenda), ni jukasalitalichisyaga (ni litala lyagoloka pakulikwesya), nambo pati inkwaiye yakusakala jukasaŵa jwakwasa ntima kusyene.');
INSERT INTO "silika" VALUES(17,84,'17:84','Jilani (mmwe Muhammadi صلى الله عليه وسلم paagasalila makafiligo): “Jwalijose akupanganya masengo mwaliŵelele litala lyakwe (dini jakwe), sano M’mbuje gwenu nijwaakummanyilila kusyene jwalili litala lyakwe lyakongoka nnope.”');
INSERT INTO "silika" VALUES(17,85,'17:85','Niakum’busya (mmwe Muhammadi صلى الله عليه وسلم) yansimu. Jilani: “Nsimu uli chimpepe mu ichindu yakuimanyilila Ambuje ŵangupe (Allah). Ninganimpedwa ŵanganyammwe umanyilisi ikaŵeje panandipe.”');
INSERT INTO "silika" VALUES(17,86,'17:86','Soni tungasache chisimu tungaityosisye ayila yatunjuwulile mmwe (Muhammadi صلى الله عليه وسلم jajili Qur’an pakujifuta mu ntima mwenu), kaneko nga nganinkola ŵakunchenjela Kukwetu pa yalakweyo.');
INSERT INTO "silika" VALUES(17,87,'17:87','Ikaŵeje ligongo lya chanasa chakuumila kwa Ambuje ŵenu (Allah, ni ligongo lyakwe tujilesile Qur’anijo mu ntima mwenu). Chisimu ukoto Wakwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) uŵele wekulungwa.');
INSERT INTO "silika" VALUES(17,88,'17:88','Jilani: “Namuno angasongangane ŵandu ni majini kuti aiche najo janti mpela aji Qur’an, nga nganaika najo janti mpela jalakweji, namuno angakamusyangane jwine ni n’jakwe.”');
INSERT INTO "silika" VALUES(17,89,'17:89','Soni chisimu chene pamasile patusalichisye nnope kwa ŵandu mu aji Qur’an chilandanyo chilichose, nambo ŵandu ŵajinji akukana ikaŵeje ukafili basi.');
INSERT INTO "silika" VALUES(17,90,'17:90','Ŵanganyao ni ŵatite: “Nganituŵa tunkulupilile mmwe mpaka nkatuwumyichisye tutumule (jwangapwela) mwitaka (lyetu lino lya Maaka).”');
INSERT INTO "silika" VALUES(17,91,'17:91','“Kapena nkole n’gunda wa tende ni zabibu, ni m’bumye sulo chilikati chakwecho mwangalechesya.”');
INSERT INTO "silika" VALUES(17,92,'17:92','“Kapena ntugwichisye pachanya petu liwunde ipandeipande mpela yankuti pakulitwanjila, kapena m’biche najo Allah ni Achimalaika (kuti tuonegane nao) meso kwa meso.”');
INSERT INTO "silika" VALUES(17,93,'17:93','“Kapena nkole nyumba ja golodi, kapena nkwele kwinani, ni nganituŵa tukukulupilile kukwela kwenuko mpaka n’di ntutuluchiisye chitabu chakuti tuchisoomeje.” Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Kuswejela ni Kwao Ambuje ŵangu! Ana une ndili chilichose, pangaŵaga mundu (jwandumisyidwe kuŵa) ntenga?”');
INSERT INTO "silika" VALUES(17,94,'17:94','Ni pangali chachasiŵilikangenye ŵandu kuti akulupilile ndema jachaichilile chongoko pangaŵaga kuŵecheta kwao kwanti: “Ana Allah atumiisye mundu kuŵa ntenga?”');
INSERT INTO "silika" VALUES(17,95,'17:95','Jilani: “Ingaŵe kuti petaka pana Achimalaika ŵaakwendajenda mwantunjelele, tungatuluchisye ŵanganyao kuumila kwinani Lilaika kuŵa ntenga.”');
INSERT INTO "silika" VALUES(17,96,'17:96','Jilani: “Allah akukwanila kuŵa Mboni chilikati changune ni jenumanja, chisimu Jwalakwe kwa achikapolo Ŵakwe juŵele Jwaasimanya abali syosope, Jwakulolechesya.”');
INSERT INTO "silika" VALUES(17,97,'17:97','Sano jwaakun’jongolaga Allah, basi jwalakwe ni jwakongoka, nambo ŵaakwalechelela kusokonechela, basi mmwe ngam’ba mwapatiile akukamuchisya ni gosa jwangaŵaga Jwalakwe, ni tuchasonganganya Lisiku lya Kiyama chamakuku ali ŵangalola, ŵangaŵecheta soni ŵangapikana, mauto gao ni ku Jahannama. Ndema jilijose jaukaliteje (Motowo panandi), tukaujonjechesyeje kukwao soni kulapuka.');
INSERT INTO "silika" VALUES(17,98,'17:98','Galakwego ni malipilo gao ligongo lyanti ŵanganyao ŵakaaniile ma Ȃya Getu, ni kuŵecheta kuti: “Ana tuli tuŵele uwwejo maupa ni ipande yetikitiche-tikitiche nambo yakuona tuchiukulidwa ni kuŵa iwumbe yasambano?”');
INSERT INTO "silika" VALUES(17,99,'17:99','Ana ngakuiona ŵanganyao yanti Allah juŵagumbile mawunde ni mataka, ali Jwakombola kusyene kwagumba ŵanti chisawu ŵanganyao? Soni ŵaaŵiichiile ndema (jakuwila ni kwimuchila m’malembe) jangali chikaiko. Basi ŵalupuso akukana (yosopeyi) ikaŵeje ukafili basi.');
INSERT INTO "silika" VALUES(17,100,'17:100','Jilani (pakwasalila achimakafilio): “Ingaŵe ŵanganyammwe mwakamulile imatiilo ya ukoto wa Ambuje ŵangu (Allah), nikuti nkawumbete (kuleka kupeleka) pakogopa kutola (kuti itakumala), sano mundu aŵele jwaugaga.”');
INSERT INTO "silika" VALUES(17,101,'17:101','Soni chisimu pamasile patwampele Musa ilosyo nsano nancheche yakuonechela. Basi mwawusyani ŵanache ŵa Israila, ndema jajwaichilile (Musajo), kaneka Firiauna ni ŵansalile kuti: “Chisimu une ngun’ganichisya mmwe Musa kuti ŵalojedwe.”');
INSERT INTO "silika" VALUES(17,102,'17:102','(Musa) jwatite: “Chisimu pamasile panyyimanyi kuti ayi (ilosyoyi) nganaitulusya ikaŵeje M’mbuje jwa kumawunde ni petaka kuŵa ichetelelo. Soni chisimu une ngun’ganichisya mmwe Firiauna kuti ŵajonasiche.”');
INSERT INTO "silika" VALUES(17,103,'17:103','Basi jwalakwe (Firiauna) jwasachilile kwakoposya ŵanganyao (Musa ni ŵanache ŵa Israila) m’chilambo (cha Misra), basi nitwammisyisye (mmesi Firiaunajo) ni wosope ŵaŵaliji najo imo.');
INSERT INTO "silika" VALUES(17,104,'17:104','Sano panyuma pa (kummisya) jwalakwe; twasalile ŵanache ŵa Israila kuti: “Tamagani m’chilambo (cha Shami), mwanti pachichiika chilanga chambesi (Kiyama), tuchinsonganganya wosopeŵene mwakuwanganyichilana (jenumanja ni amagongo ŵenu).”');
INSERT INTO "silika" VALUES(17,105,'17:105','Ni yakuona tujitulwisye Uwwe (Qur’anijo), nambo soni mwakuonaonape jitulwiche, ni nganituntumisya mmwe (Muhammadi صلى الله عليه وسلم) ikaŵeje kuti m’be ŵakutagulila abali jakusengwasya ni ŵakutetela.');
INSERT INTO "silika" VALUES(17,106,'17:106','oni (jalakweji ni) Qur’an jatujigaŵenye (ma Ȃya ni ma Sura) kuti mwasoomele ŵandu mwantimatuusi, ni tujitulwisye panandipanandi (mu yaka makumi gaŵili kwisa itatu 23).');
INSERT INTO "silika" VALUES(17,107,'17:107','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Jikulupililani jalakwe (Qur’aniji) kapena nneche ngajikulupilila. Chisimu aŵala ŵaŵapedwile umanyilisi paujo pakwe (Qur’an nkanijituluche Ayuda ni Akilisito) pati jichisoomedwaga kukwao (Qur’an) akasagwa niipundi yao kusujudu.”');
INSERT INTO "silika" VALUES(17,108,'17:108','Ni akasatiji: “Aswejele M’mbuje gwetu! Chisimu chilanga cha M’mbuje gwetu chiŵele chakwanilisyidwa mwangalepeleka.”');
INSERT INTO "silika" VALUES(17,109,'17:109','Ni akasagwa niipundi yao achililaga, ni jikwajonjechesyaga (Qur’anijo) kulinandiya.');
INSERT INTO "silika" VALUES(17,110,'17:110','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Mum’bendani Allah kapena mum’bendani Ar-Rahmân, nilina lililyose lyachimum’bendele (yalumope), ligongo Jwalakwe jukwete mena gambonembone.” Soni nkanyanyikaga nnope ni (Qur’an ja pa) swala jenu kapena kutulusya nnope maloŵe, nambo kuyani litala lya chilikati chayalakweyo.');
INSERT INTO "silika" VALUES(17,111,'17:111','Ni m’becheteje kuti: “Lumbili losope lwene alapikwe Allah jwanganalitendela mwanache, soni nganakola jwagaŵana najo pa Uchimwene (Wakwe), soni nganaŵa jwaulesi kwanti nikola jwakunkamuchisya, soni munkusyani kunkusya kwannope!”');
INSERT INTO "silika" VALUES(18,1,'18:1','Lumbili losope lwene alapikwe Allah Ajula jwantuluchisye kapolo Jwakwe (Muhammadi صلى الله عليه وسلم) Chitabu (cha Qur’an), ni nganaŵika nkati mwakwe kupombotala kulikose.');
INSERT INTO "silika" VALUES(18,2,'18:2','(Achitesile kuŵa) chagoloka kuti chitetele (makafili) ya ipotesi yakandapala yakuuma Kukwakwe, ni chasengwasye ŵakulupilila aŵala ŵaakutenda yambone (pakwapa abali janti), akwete kupata ŵanganyao malipilo gambone.');
INSERT INTO "silika" VALUES(18,3,'18:3','Tachitama mwalakwe (m’malipilo gambonemo) yakayaka.');
INSERT INTO "silika" VALUES(18,4,'18:4','Ni kuti chatetele soni aŵala ŵaakuti: “Allah alitendele mwanache.”');
INSERT INTO "silika" VALUES(18,5,'18:5','Ŵanganyao nganakola payalakweyo umanyilisi uliose ni achatati ŵao ŵakwe. Likusile nnope liloŵe lyalikopoka mu ngamwa syao. Ngakuŵecheta inepe ikaŵeje unami.');
INSERT INTO "silika" VALUES(18,6,'18:6','Mwinetu mmwe kuŵa nkulijonanga mwasyene ni madandausi (gakwadandaulila) panyuma pao (ali agalawiche kunneka mmwe), ligongo lyanti ngaakujikulupilila aji abali (ja Qur’aniji, mmwe ngasin’dandaula chanti iyyo, naga ngaakukulupilila niyao).');
INSERT INTO "silika" VALUES(18,7,'18:7','Chisimu Uwwe tuitesile ichindu yaili pachanya petaka kuŵa yakulisalalichisya litakalyo, kuti twalinje ŵanganyao kuti ana ŵani mwa ŵanganyao ŵataŵe ŵakolosya nnope masengo.');
INSERT INTO "silika" VALUES(18,8,'18:8','Soni chisimu Uwwe tuchiyiteenda yosope yaili palakwepo kuŵa litaka lyakumula lyangali chilichose.');
INSERT INTO "silika" VALUES(18,9,'18:9','Ana nkuganichisya yanti achimisyene mbanga ni ubawu (wawalembedwepo mena gao ni ngosyo syao) yaliji mu imanyilo Yetu yakusimosya nnope? (Iyayi pana yakusimosya nnope kumpunda yalakweyo).');
INSERT INTO "silika" VALUES(18,10,'18:10','(Kumbuchilani) katema kaŵajile kuuŵa achachanda kumbanga ni ŵatite: “Ambuje ŵetu! Tupani ukoto kuumila Kukwenu, ni ntukolochesye ichindu yetu mwachongoko.”');
INSERT INTO "silika" VALUES(18,11,'18:11','Ni twasiŵile m’mawiwi mwao (kwanti ŵagonile lugono lwangapikana nalo chilichose) yaka yakwana igala kumbanga.');
INSERT INTO "silika" VALUES(18,12,'18:12','Kaneko twajimwisye, kuti twalinje kuti ana ŵani m’mikutula jiŵili (jajikangana pa yaŵanganyao) ŵaakumanyilila chenene kuŵalanjila ndema jaŵatemi (achachandao mu mbangamo).');
INSERT INTO "silika" VALUES(18,13,'18:13','Chisimu Uwwe tukunsimulila (mmwe Muhammadi صلى الله عليه وسلم) abali jao mwakuonaonape, chisimu ŵanganyao ŵaliji achachanda ŵaŵankulupilile M’mbuje gwao (Allah), ni twajonjechesye chongoko.');
INSERT INTO "silika" VALUES(18,14,'18:14','Ni twajilimbangenye mitima jao (pa chikulupi) pandema jaŵajimi (pameso pa nchimwene jwao jwachikafili) ni kuti: “M’mbuje gwetu ni M’mbuje jwa kumawunde ni petaka (Allah), nganituŵa tunnumbanjile nnungu jwinepe jwangaŵaga Jwalakwe, pajele ndemajo (tuchitendaga yele yakunnumbanjila jwinepeyo), chisimu chene chituŵe tuŵechete yaukafili wakunaula.”');
INSERT INTO "silika" VALUES(18,15,'18:15','“Aŵa ŵandu ŵetu alitendele milungu jine kunneka Jwalakwe, nambi uli ngaakuika nao umboni wakuonechela pa jalakwejo (wakulosya kuti jalakwejo milungu)? Ana nduni jwali jwalupuso nnope kumpunda mundu jwaakunsepela Allah unami?”');
INSERT INTO "silika" VALUES(18,16,'18:16','(Achachanda ŵasalilene yanti): “Sano ndema jachinsache kwatundumalila ŵanganyaŵa ni ayila yaakuigalagatila yangaŵaga Allah, basi kajuŵeni kumbanga, M’mbuje gwenu chansapangulile chanasa Chakwe, ni chankolochesye ichindu yenu mwakun’jepepachisya.”');
INSERT INTO "silika" VALUES(18,17,'18:17','Soni nkaliweni lyuŵa ndema jalikopoka (ni kwelelaga nsana) lilinkujiŵambala mbanga jaojo (ni kuŵala kwakwe) chakun’dyo, sano ndema jalikutiŵila (nkaliweni) lilinkwaŵambala (ni kuŵala kwakwe) chakunchiji, kutendaga ŵanganyao ŵaliji pantetete mwalakwemo. Yalakweyo ni ine mu ilosyo ya Allah (yakulosya ukombosi Wakwe). Mundu jwan’jongwele Allah, basi jwalakwe ni jwali jwakongoka, nambo jwannechelele kusokonechela nganim’ba mumpatiile nsyoŵe jwakun’jongola.');
INSERT INTO "silika" VALUES(18,18,'18:18','Soni nkaaganiichiisye ŵanganyao kuti ali mesope (ligongo meso gao galiji tanana!) kutendaga ali nnugono, aku tuli nkwagalauchisya chakun’dyo ni chakunchiji (kuti litaka likalya ilu yao), soni mbwa jao jili chongolele ngongolo syakwe (syam’bujo) pannango (wa mbangajo nambo chakuusa, kuŵa londa jwao), ingaŵe mwaweni ŵanganyao nkagalawiche kutila, soni ungan’gumbele woga wakwajogopa ŵanganyao.');
INSERT INTO "silika" VALUES(18,19,'18:19','Soni iyyoyopeyo twajimwisye ŵanganyao (nnugono lwao lwayaka ni yaka) kuti awusyane chilikati chao (yandema jalonjele ali chigonele). Jwaŵechete nkuŵecheta mwa ŵanganyao kuti: “Nnonjele ndema jantiuli?” Ŵanganyao ŵatite: “Tulonjele usiku umo kapena chipande cha usiku.” (Ŵane mwa) ŵanganyao ŵatite: “Ambuje ŵenu (Allah) ni ŵaakumanyilila chenene munnonjelele. Basi muntumani jumo jwenu ni mbiya syenu syasilifasi, ajaule ku nsinda, ni akalole kuti niyapi yakulya yaili yambone nnope, basi ni akaiche nayo kukwenu yakulya ine mwa yalakweyo. Nambo akatende mwakalamuka, mwanti akaja kummanyisya jwalijose ya jenumanja.”');
INSERT INTO "silika" VALUES(18,20,'18:20','“Ligongo naga ŵanganyao ammanye pan’di jenumanja chansome maganga (mpaka kuwa), kapena cham’buchisye ku dini jao (jepotoche), sano pajele ndemajo (naga m’bujile ku dini jao), ngasim’ba ŵakupunda mpaka kalakala.”');
INSERT INTO "silika" VALUES(18,21,'18:21','Soni iyyoyopeyo twamanyisye ŵanganyao (kwa ŵandu kupitila mu mbiya syao syasilifa sila) kuti amanyilile (ŵanduo) yanti, chilanga cha Allah (chakwimuka m’malembe) chakuonaonape, nambo soni kuti Kiyama nganijiŵa jakaikasya. (Kumbuchilani) katema kaŵaliji (achinsyene nsinda) ali nkukangana chilikati chao ya ngani jaojo. Basi (ŵane mwa) ŵanganyao ŵatite: “Taŵani chakutaŵa pa ŵanganyao (kuti ŵandu akaaliingulaga), Ambuje ŵao (Allah) ni ŵaakumanyilila chenene ya ŵanganyao.” Ŵandu ŵaŵapundile panganisyo syao ŵatite: “Chisimu chene uwe chitutaŵe pa ŵanganyaŵa nsikiti.”');
INSERT INTO "silika" VALUES(18,22,'18:22','(Ŵane) akutiji: “Ŵaliji ŵandu ŵatatu, jwancheche jwao jaliji mbwa jao.” (Ŵane) ni akutiji: “Ŵaliji ŵandu nsano, jwansano najumo jwao jaliji mbwa jao.” (Akuŵecheta) mwagamba ganichisya yangaiona. (Ŵane) soni ni akutiji: “Ŵaliji ŵandu nsano naŵaŵili, jwansano naŵatatu jwao jaliji mbwa jao.” Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ambuje ŵangu ni ŵaakumanyilila chenene yachiŵalanjilo chao, ngakwamanyilila ŵanganyao ikaŵeje ŵannonope.” Sano nkakangana pa yaŵanganyao (yachiŵalanjilo chao), ikaŵeje kangana kwapachanyape (ligongo kwangali mate), soni ngasimum’buusya jwalijose mwa ŵanganyao (Ayuda ni Akilisito) ya ŵanganyao (achinsyene mbanga).');
INSERT INTO "silika" VALUES(18,23,'18:23','Soni ngasinnogwa kuŵecheta yakwamba chilichose kuti: “Chisimu une chinachipanganye chalakwecho malaŵi.”');
INSERT INTO "silika" VALUES(18,24,'18:24','Ikaŵeje (pakuŵecheta maloŵe ganti): “Nasakaga Allah.” Soni mwakumbuchilani Ambuje ŵenu pati n’diŵalile, ni kuŵecheta kuti: “Mwine Ambuje ŵangu kuŵa nkuunjongolela litala lyakuŵandika nnope pachongoko kumpunda lyalakweli.”');
INSERT INTO "silika" VALUES(18,25,'18:25','Ni ŵatemi mu mbanga mwaomo yaka machila gatatu (300) (pakuŵalanjila kajende kalyuŵa) ni ŵajonjechesye ine nsano nancheche (9) (pakuŵalanjila kajende kamwesi).');
INSERT INTO "silika" VALUES(18,26,'18:26','Jilani: “Allah ni jwaakumanyilila chenene muŵaataamiile ŵanganyao. Iŵele Yakwe yakusisika ya kumawunde ni petaka. Kaje kulolechesya nnope Jwalakwe ni kupikanichisya! Nganakola ŵanganyao nkwagosa jwangaŵaga Jwalakwe. Soni Jwalakwe jwangan’gaŵila jwalijose ya ulamusi Ŵakwe.”');
INSERT INTO "silika" VALUES(18,27,'18:27','Soni soomaani yaijuwulidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) ya m’Chitabu cha Ambuje ŵenu (Qur’an), pangali nkutindanya maloŵe Gakwe. Soni Mmwe nganim’ba mpatile kwakutilila pangaŵaga Kukwakwe.');
INSERT INTO "silika" VALUES(18,28,'18:28','Litaŵani ntima wenu (mmwe Muhammadi صلى الله عليه وسلم) pakuŵa pamo ni aŵala ŵaakum’bombanga M’mbuje gwao kundaŵi ni kwigulo achisachililaga ngope Jakwe, soni meso genu gakaapeleta ŵanganyao (ni kwalola ŵane) nchisachililaga ipeto yaumi wa duniya, soni ngasimunkunda ajula jwatuuliŵesye ntima wakwe yaatukumbuchila Uwwe, ni jukuyiye isako yakwe, soni ichindu yakwe iŵele yakwasika.');
INSERT INTO "silika" VALUES(18,29,'18:29','Soni jilani: “Yakuona ikuumila kwa M’mbuje gwenu (Allah).” Sano jwaakusaka (kulupilila) basi akulupilile, soni jwaakusaka (kanila) basi akanile. Chisimu Uwwe twalinganyichisye ŵalupuso Moto wachiuchasyungula uŵigo wakwe. Sano naga akaŵendeje chikamuchisyo (ligongo lya njota), chakakamuchisyidweje ni mesi (gamasemule nnope) ganti mpela ntofu wenyelenyenduche, gagakagwaguleje ngope. Kaje kusakala nnope yakumwa (yakwe)! Soni kaje kusakala nnope pakwegama ni kupumulila (pakwe)!');
INSERT INTO "silika" VALUES(18,30,'18:30','Chisimu aŵala ŵaakulupilile ni kutendaga yambone (tuchalipila pa umbone wao), chisimu Uwwe ŵangawatanganya malipilo ga mundu jwaakolosyisye masengo.');
INSERT INTO "silika" VALUES(18,31,'18:31','Ŵanganyao akwete kupata Matimbe Gandamo, kwasikujilima sulo cha pasi pao, chakapetedwe kwalakweko ni makosa gagolodi, ni chakawaleje nguwo syachisamba sya silika jwakuŵeŵela ni silika jwakandapalila, ali ajegeme kwalakweko nyyindanda yetandiche chenene. Kaje kusalala nnope malipilo (gakwe)! Ni kutama chenene nnope pakwegama ni kupumulila (pakwe)!');
INSERT INTO "silika" VALUES(18,32,'18:32','Soni mwapani ŵanganyao chilandanyo cha ŵandu ŵaŵili; jumo jwao (juŵaliji kafili) twam’biichiile migunda jiŵili ja zabibu, ni twajisyungulichisye ni mitende, ni twaŵisile chilikati chakwecho mmela (wine).');
INSERT INTO "silika" VALUES(18,33,'18:33','Migunda jinajiŵilijo jasogwele isogosi yakwe, soni nganijipungusyamo chilichose (mu kasogole kakwe), ni twapitisye nkatikaati mwakwemo sulo.');
INSERT INTO "silika" VALUES(18,34,'18:34','Ni jwakwete soni isogosi (ine mpela ipanje ni ŵanache), ni ŵansalile n’jakwe (juŵaliji jwakulupilila) aku ali nkuntwanjila kuti: “Une ni jungwete chipanje chejinji nnope kumpunda mmwe, nambo soni (ngwete) machili gamajinji ligongo lya ŵandu ŵakunguya.”');
INSERT INTO "silika" VALUES(18,35,'18:35','Ni jwajinjile mun’gunda mwakwe (mwakutwanga) juli nkuliitenda lupuso nsyene (ligongo lyaukafili wakwe). Jwatite (pakubwasya): “Nganguganichisya yanti wele n’gundau nikonasika ata panandi.”');
INSERT INTO "silika" VALUES(18,36,'18:36','“Soni nganguganichisya yanti Kiyama jichiika. Nambo naga (chijiiche, une ni) kumbuchisya kwa Ambuje ŵangu (Allah), chisimu chene chingagasimane malo gakuujila gambone nnope kupunda galakwega.”');
INSERT INTO "silika" VALUES(18,37,'18:37','N’jakwe (juŵaliji jwakulupilila) ŵansalile mwakun’janga kuti: “Ana mmwe nkunkaana Ajula juŵan’gumbile kuumila mwitaka, kaneka kuumila mu lindondwa lyamesi, kaneko ni ŵankolosisye kuŵa mundu?”');
INSERT INTO "silika" VALUES(18,38,'18:38','“Nambo kumbali jangu, (ni ngukulupilila kuti) Jwalakwejo ni Allah, M’mbuje gwangu, soni ngangum’bwanganya ni M’mbuje gwangujo jwalijose.”');
INSERT INTO "silika" VALUES(18,39,'18:39','“Soni bola nkaŵechete ndema jakwinjila mu n’gunda mwenu kuti: ‘(Ayi ni) yasachile Allah (kuti naipate). Machili ngaŵa nkupatikana ikaŵeje (pakupedwa) ni Allah.’ Naga nkumbona une kuti ndili jwakunandipilwa kukwenu pa chipanje ni ŵanache.”');
INSERT INTO "silika" VALUES(18,40,'18:40','“Soni mwine Ambuje ŵangu kuŵa nkuumba yambone nnope kupunda n’gunda wenuo, ni kuutumichisya walakweo makungu (gawula jakokota chajila) kuumila kwinani, ni kusyuka kuŵa litaka lyakutelesya.”');
INSERT INTO "silika" VALUES(18,41,'18:41','“Kapena mesi gakwe (gamun’gundamo) gambaga kupwa, mwanti mmwe nganim’ba nkombwele kugasosasosa nikugapata.”');
INSERT INTO "silika" VALUES(18,42,'18:42','Sano isogosi yakwe yasyungulilidwe (ni makungu), mwanti jwatandite kupitikusya-pitikusya magasa gakwe (mwakudandaula) ligongo lya yajuijonanjile (migundajo), soni itela yakwe ili igwilile pa masajili gakwe, ni jwaliji nkuŵecheta kuti: “Nkongwene! Nabola ngatende ngaawanganya Ambuje ŵangu ni jwalijose.”');
INSERT INTO "silika" VALUES(18,43,'18:43','Soni nganijukola likuga lyaŵandu ŵakunkamuchisya panyuma pakun’jasa Allah, namuno jwalakwe nsyene nganijuŵa jwaalikamuchisya.');
INSERT INTO "silika" VALUES(18,44,'18:44','Kwalakweko (ku Kiyama) chikulupusyo ni uchimwene ichiŵa ya Allah pe, Jwakuona. Jwalakwe ni Jwambone nnope pakalipile, soni ni Jwambone nnope pakaiche ni mbesi jambone.');
INSERT INTO "silika" VALUES(18,45,'18:45','Soni mwapani ŵanganyao chilandanyo cha umi ŵa duniya, uli chisawu mesi (gawula) gatukugatulusyaga kuumila kwinani, kaneko ni gakasawanganyichilana ni mmela wamwitaka (ni kutanda kumela mmelao mwakusalalila), nambo (kaneko) ukasasyuka wejumu soni wakuteketuka waukasaululusyidwaga ni mbungo. Sano Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(18,46,'18:46','Chipanje ni ŵanache iŵele ipeto ya umi wa duniya, nambo masengo gambone gakwendelechela ni gagali gambone nnope kwa Ambuje ŵenu pamalipilo, soni niyakwembecheya yambone (yakuja kuipata kwa Allah).');
INSERT INTO "silika" VALUES(18,47,'18:47','Soni (kumbuchilani) katema kituchigajendesya matumbi, ni tinchiliona litaka lili mbembembe, ni tuchasonganganya ŵanganyao mwanti ngasitunneka ata jumo mwaŵanganyao munyuma.');
INSERT INTO "silika" VALUES(18,48,'18:48','Ni tachionesyedwa kwa Ambuje ŵenu ali m’miselamisela, (nitachasalila Allah kuti): “Chisimu chene ntuichilile itwatite pakun’gumba kandanda. Nambo mwaganichisyaga yanti nganituŵa tum’biichiile chilanga (chakusimangana Nowe).');
INSERT INTO "silika" VALUES(18,49,'18:49','Ni chichiŵichidwa chitabu (chamasengo gao), ni chinchaona akuleŵa ali ŵawoga ligongo lya yaili mwalakwemo, ni tachitiji: “Kutapana kwetuwe! Ana chele chitabuchi chanti uli! Changaleka (chindu) chamwana namuno chekulungwa ikaŵeje kuchikusanya (pakuchilemba ni manambala gakwe)!” Ni tachiisimana yosope yaŵapanganyisye ili iyiche (ni kuŵichidwa paujo pao). Soni Ambuje ŵenu ŵangantenda lupuso jwalijose.');
INSERT INTO "silika" VALUES(18,50,'18:50','Soni (kumbuchilani) katema katwasalile Achimalaika kuti: “Mwasujudilani Adam.” Basi wosope ni ŵasujudu, kusigala Ibulisu, jwaliji jwakuumila m’majini. Basi ni jwalikanile lilamusi lya Ambujegwe. Ana ni nkuntenda jwalakwejo ni ŵanache ŵakwe kuŵa isyoŵe kuuneka Une, kutendaga ŵanganyao kukwenu aŵele amagongo? Kaje kusakala katindanye kaŵandu ŵalupuso!');
INSERT INTO "silika" VALUES(18,51,'18:51','Nganinatenda ŵanganyao kuŵa mboni pakagumbe ka mawunde ni mataka, namuno pakagumbidwe kaachimisyeneo, soni nganimba jwanti ni kwatenda akusochelesya kuŵa akungamuchisya (Ŵangu).');
INSERT INTO "silika" VALUES(18,52,'18:52','Soni (kumbuchilani) katema katachiti (Allah): “Mwaŵilangani akwawanganya ŵenu None, aŵala ŵamwaliji nkulilambusya.” Basi ni tachaŵilanga, nambo ngasiŵajanga. Ni tuchiŵika chilikati chao chakusiŵilikanganya (malo gachonasiko mwanti ngasasimangana soni).');
INSERT INTO "silika" VALUES(18,53,'18:53','Ni tachiuona akuleŵa Moto, basi ni tachisimichisya yanti ŵanganyao taujinjile. Soni ngasapata pakuugwalila.');
INSERT INTO "silika" VALUES(18,54,'18:54','Soni chisimu chene pamasile patusalichisye nnope kwa ŵandu mu aji Qur’an chilandanyo chilichose, sano mundu ni aŵele jwamakani nnope kupunda chilichose.');
INSERT INTO "silika" VALUES(18,55,'18:55','Ni pangali chachikwasiŵilila ŵandu kulupilila (sano) panyuma pakwaichilila chongolo (Qur’an), ni kum’benda chikululuko M’mbuje gwao, ikaŵeje (akwembecheya) kuti syaichilile nyendele sya (ŵandu) ŵandanda (sya kapate kailagasyo), kapena kuti yaichilile ilagasyo meso kwa meso.');
INSERT INTO "silika" VALUES(18,56,'18:56','Ni ngatukutumisyaga Achimitenga ikaŵeje kuti aŵe ŵakutagulila abali syakusengwasya ni ŵakutetela. Nambo aŵala ŵakanile ni akutendela makani yaunami, kuti ni yalakweyo awesesye yakuona, ni akugatendela ma Ȃya Gangu chipongwe kwisa soni yaakuteteledwa nayo.');
INSERT INTO "silika" VALUES(18,57,'18:57','Ana soni nduni jwali jwalupuso nnope kwakumpunda mundu jwakumbusyidwe ma Ȃya ga M’mbujegwe, nambo jwalakwe ni ganyosyela, ni kuliŵalila yagaaloongwesye makono gakwe? Chisimu Uwwe tuŵisile isiŵilo pachanya pa mitima jao kuti akajimanyilila jalakwe (Qur’anji), ni m’mawiwi mwao mwana usiiwa. Soni namuno mwaŵilanjile ku chongoko, nganaŵa ajongweche ata panandi.');
INSERT INTO "silika" VALUES(18,58,'18:58','Soni Ambuje ŵenu ni Ŵakululuka kusyene, Asyene chanasa, angajile ŵakwembekanye pa yakusakala yapanganyisye, nikuti angajanguyichiyisye kwapa ilagasyo (pa duniya pepano), nambo ŵanganyao akwete chilanga (chakupedwela ilagasyo pa Kiyama), ngasapata pakutilila palipose pakutalikanganila nacho.');
INSERT INTO "silika" VALUES(18,59,'18:59','Soni jalakwejo ni misi (ja achina Adi ni Samuda) ŵatwajonasile ndema jaŵatesile lupuso, ni twaŵichile chilanga chachonasiko chao.');
INSERT INTO "silika" VALUES(18,60,'18:60','Soni (kumbuchilani) katema kajwaŵechete Musa kwa nchanda jwakwe (Yushau Nuni) kuti: “Nganeka (kwenda) mpaka ngaiche pandago ja mbwani siŵili, kapena chingambeje kwenda yaka ni yaka (mpaka simane najo jwangunsosajo).”');
INSERT INTO "silika" VALUES(18,61,'18:61','Nambo paŵaiche ŵanaŵaŵilio pandago ja mbwani siŵili pala ŵaliŵalile somba jao, basi ni jalitendele litala lyakwe m’mbwani nti chisawu mbugu.');
INSERT INTO "silika" VALUES(18,62,'18:62','Basi ndema jaŵajendelechele paujo, (Musa) ŵansalile nchanda jwakwe kuti: “Tupani yakulya yetu yakundaŵi, chisimu chene pa ulendo wetu walakweu tusimangene nayo yakupesya kusyene.”');
INSERT INTO "silika" VALUES(18,63,'18:63','(Nchanda) jwatite: “Ana nyyiweni ndema jatwapumulile palwala pala! Basitu une naajiliŵalile somba jila. Ni nganandiŵasya yajalakwejo ikaŵeje shetani, kuti ngajikumbuchila, nijilitendele litala m’mbwani mwakusimosya.”');
INSERT INTO "silika" VALUES(18,64,'18:64','(Musa) jwatite: “Pele ni patupasachileje.” Basi ni ŵaujile munyuma ali nkupita nchikuya sajo syao.');
INSERT INTO "silika" VALUES(18,65,'18:65','Basi ni ŵansimene jumpepe mwa achikapolo Ŵetu (Khidr), jwatwampele ukoto ŵakuumila Kukwetu, soni twan’jiganyisye ilimu kuumila Kukwetu.');
INSERT INTO "silika" VALUES(18,66,'18:66','Musa jwatite kwa jwalakwe: “Ana komboleka kunkuya kuti munjiganye yachongoko chin’jiganyisyidwe?”');
INSERT INTO "silika" VALUES(18,67,'18:67','(Khidr) jwatite: “Chisimu mmwe nganim’ba nkombwele kupilila none.”');
INSERT INTO "silika" VALUES(18,68,'18:68','“Ana chimpilile chinauli pa ichindu yangankuimanyilila chenene yaikwamba?”');
INSERT INTO "silika" VALUES(18,69,'18:69','(Musa) jwatite: “Nasakaga Allah timusimane kuŵa jwakupilila, soni nganyosya lilamusi lyenu.”');
INSERT INTO "silika" VALUES(18,70,'18:70','(Khidr) jwatite: “Sano naga nkunguya, basi ngasimumbusya ya chilichose, mpaka ndande nansyene kunsalila ya chalakwecho.”');
INSERT INTO "silika" VALUES(18,71,'18:71','Basi ni ŵaŵigwile ŵanaŵaŵili (Khidr ni Musa achijendaga mungulugulu mbwani), mpaka pandema jaŵakwesile m’chombo; (Khidr) jwachipowele. (Musa) jwatite: “Ana nchipowele (chombochi) kuti mwamisye achimisyene? Chisimu pamasile pantesile chindu chekulungwa changalumbana.”');
INSERT INTO "silika" VALUES(18,72,'18:72','(Khidr) jwatite: “Ana nganimbecheta yanti chisimu mmwejo nganim’ba nkombwele kupilila none?”');
INSERT INTO "silika" VALUES(18,73,'18:73','(Musa) jwatite: “Nkamba ilagasyo ligongo lyakuliŵalila kwangu, soni ngasimunganganika ni isausyo yekulungwa nnope pa chisako changu (chakusa kunkuya mmwe).”');
INSERT INTO "silika" VALUES(18,74,'18:74','Basi ni ŵajendelechele ŵanaŵaŵili (pa ulendo wao panyuma pakutuluka m’chombo mula), mpaka pandema jaŵakasimene kanchanda; basi (Khidr) ŵakauleje. (Musa) jwatite: “Ana m’buleje mundu jwangali chaleŵile, jwanganaulaga mundu? Chisimu pamasile pantesile chindu chakunyalaya kusyene.”');
INSERT INTO "silika" VALUES(18,75,'18:75','(Khidr) jwatite: “Ana nganinansalila yanti chisimu mmwejo nganim’ba nkombwele kupilila none?”');
INSERT INTO "silika" VALUES(18,76,'18:76','(Musa) jwatite: “Naga nam’busye yachindu chine panyuma pachalakwechi, basi ngasimungunda soni kulongana namwe, pamasile pampochele usulu wakwanila kuumila kukwangu.”');
INSERT INTO "silika" VALUES(18,77,'18:77','Basi ni ŵajendelechele ŵanaŵaŵili (pa ulendo wao), mpaka pandema jaŵaichilile ŵandu ŵa m’musi (wa Antwâkia), ŵaŵendile yakulya achimisyene musio, nambo ŵakanile kwakalibuchisya. Basi ni ŵalisimene mwalakwemo lipupa linankusaka kugwa, basi (Khidr) ni jwalijimiche. (Musa) jwatite: “Nkasache, chisimu chene nkagapochelele malipilo (masengo galakwega nitukaasuumiile yakulya).”');
INSERT INTO "silika" VALUES(18,78,'18:78','(Khidr) jwatite: “Aku ni kulekangana kwa chilikati cha une nomwe. Sano chinantagulile ngopolelo sya ichindu yanganinkombola kuipililila ata panandi.”');
INSERT INTO "silika" VALUES(18,79,'18:79','“Sano chombochila, basi chaliji chaŵandu ŵakulaga ŵakamula masengo m’mbwani, ninasachile kuti nachipe ayibu, sano paujo pao paliji pana mwenye juŵaliji nkujigala chombo chilichose (chambone) mwakotosya.”');
INSERT INTO "silika" VALUES(18,80,'18:80','“Sano kanchanda kala, nikuti achinangolo ŵakwe ŵaliji ŵakulupilila, basi nitwajogwepe kuti atakwakanganichisya kutenda yakusumba mpika ni yaukafili.”');
INSERT INTO "silika" VALUES(18,81,'18:81','“Nitwasachile kuti Ambuje ŵao ŵatindanichisye ni jwambone kumpunda jwele pakutakata ni jwakuŵandichila nnope ku chanasa (chakwatendela achinangolo ŵakwe).”');
INSERT INTO "silika" VALUES(18,82,'18:82','“Sano lipupa lila, nilyaliji lya ŵanache ŵaŵili ŵawina mu nsindamo; sano pasi pakwe paliji pana chipanje chao (chaŵasalachisye malemu baba ŵao); sano baba ŵao ŵaliji ntenda ngoto. Basi Ambuje ŵenu niŵasachile kuti akuyiche ŵanaŵaŵilio komangala kwao, mwanti chalikopochesye chipanje chaocho, (awu) kuŵa ukoto wakuumila kwa Ambuje ŵenu. Ni nganindenda yalakweyi mwaulamusi ŵangu. Asi ni ngopolelo sya ichindu yanganinkombola kuipililila.”');
INSERT INTO "silika" VALUES(18,83,'18:83','Soni akum’busya (mmwe Muhammadi صلى الله عليه وسلم) ya Dhul-Qarnain. Jilani: “Tinansoomele sine mwa abali syakwe.”');
INSERT INTO "silika" VALUES(18,84,'18:84','Chisimu Uwwe twampele machili gakutamila pa chilambo, ni twampele soni litala lyakutendela chilichose.');
INSERT INTO "silika" VALUES(18,85,'18:85','Ni jwakuyiye litala (lyakulungama ku ilambo yakwigulo).');
INSERT INTO "silika" VALUES(18,86,'18:86','Mpaka pajwaiche pakutiŵilila lyuŵa (mukalole kameso), ŵalisimene linankutiŵila petanda lyamatope gamapiliu, ni ŵasimene palakwepo ŵandu (makafili). Twatite: “E mmwe a Dhul- Qarnain! Naga nkusaka komboleka kwapa ilagasyo kapena kwatendela yambone.”');
INSERT INTO "silika" VALUES(18,87,'18:87','Jwalakwe jwatite: “Sano jwatesile lupuso (pakun’galagatila jwinepe jwangaŵaga Allah), basi chitumpe ilagasyo, kaneko tachiuchisyidwa kwa Ambujegwe, ni tachija kunnagasya ni ilagasyo yakunyalaya kusyene.”');
INSERT INTO "silika" VALUES(18,88,'18:88','“Nambo jwaakulupilile ni kutendaga yambone, nikuti akwete kupata jwalakwejo malipilo gambone, soni chitunsalile yakwepepala mu ichindu yetu (yakun’jongolela).”');
INSERT INTO "silika" VALUES(18,89,'18:89','Kaneka jwakuyiye soni litala (lyakulungama ku ilambo yakundaŵi).');
INSERT INTO "silika" VALUES(18,90,'18:90','Mpaka pajwaiche pakopochela lyuŵa (mukalole kameso), ŵalisimene linankwakopochela ŵandu ŵanganitwaŵichila chakwasiŵilila ku lyalakwelyo.');
INSERT INTO "silika" VALUES(18,91,'18:91','Mwele nimuyaŵelele, chisimu soni Uwwe twaisyungwile yosope yayaliji kukwakwe (Dhul-Qarnain) paaimanya chenene.');
INSERT INTO "silika" VALUES(18,92,'18:92','Kaneko jwakuyiye soni litala (line).');
INSERT INTO "silika" VALUES(18,93,'18:93','Mpaka pajwaiche chilikati chamatumbi gaŵili, ŵasimene chamungulugulu mwakwemo ŵandu ŵakuchelewa kupikana chiŵecheto.');
INSERT INTO "silika" VALUES(18,94,'18:94','Ŵanganyao ŵatite: “E mmwe a Dhul-Qarnain! Chisimu Yaajuju ni Maajuju akuwatanganya pa chilambo. Ana ngakomboleka kuti tumpe malipilo kuti m’biiche chilikati chetuwe ni ŵanganyao chakusiŵilila?”');
INSERT INTO "silika" VALUES(18,95,'18:95','Jwalakwe jwatite: “Ichindu yangombolasyisye une Ambuje ŵangu, iŵele yambone nnope (kupunda yankusaka kumbayo), basi mungamuchisyani mwamachili, timbiche chilikati chenu ni ŵanganyao chakusiŵilila chakulimbangana.”');
INSERT INTO "silika" VALUES(18,96,'18:96','“Mumbani ipande ya isyano.” Mpaka pandema jajwagumbesye mpata wa matumbi gaŵiligo (ni isyanoyo), jwatite: “Pepelelani (moto).” Kwikanila ndema jajwaitesile (isyanoyo kuŵa yechejeu mpela) moto, jwatite: “Mumbani ntofu wenyelenyenduche naupungulile pachanya pakwe.”');
INSERT INTO "silika" VALUES(18,97,'18:97','Basi (Yaajuju ni Maajuju) nganakombola kwela pachanya pakwe, nambo soni nganakombola kuchipowola.');
INSERT INTO "silika" VALUES(18,98,'18:98','(Dhul-Qarnain) jwatite: “Awu ni ukoto wakuumila kwa Ambuje ŵangu. Basi pachichiika chilangacha Ambuje ŵangu (chakopochela Yaajuju ni Maajuju Kiyama jili jisigele panandi), tachichitenda (chakusiŵililachi) kuŵa chakusyajidwa. Sano chilanga cha Ambuje ŵangu chiŵele chakuonaonape.”');
INSERT INTO "silika" VALUES(18,99,'18:99','Ni tuchaleka lyele lisikulyo (lyakopochela Yaajuju ni Maajuju) ŵane mwa ŵanganyao (ŵakusakala) achajinjililaga ŵane, ni lichigombedwa lipenga, pele ni tuchasonganganya wosope ŵene pamo.');
INSERT INTO "silika" VALUES(18,100,'18:100','Ni tuchijionesya palangulangu Jahannama lyele lisikulyo kwa achimakafili.');
INSERT INTO "silika" VALUES(18,101,'18:101','Aŵala ŵagaliji meso gao m’chisiŵilo kuleka (kulola) chikumbusyo Changu (Qur’an), ni ŵaliji soni akakukombola kupikana (Qur’anijo).');
INSERT INTO "silika" VALUES(18,102,'18:102','Ana aŵala ŵakanile, akuganichisya yanti mpaka ŵasyusye achikapolo Ŵangu (Achimalaika ni Achimitume) kuŵa akugosa kuuneka Une? Chisimu Uwwe twalinganyichisye achimakafili Jahannama kuŵa malo gakwichila.');
INSERT INTO "silika" VALUES(18,103,'18:103','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana tuntagulile ya ŵaali ŵakwasika nnope pa itendo (yao)?”');
INSERT INTO "silika" VALUES(18,104,'18:104','“Aŵala ŵagasokonechele masengo gao pa umi wa duniya (ligongo lya ukafili), kutendaga ŵanganyao achiganichisyaga yanti ŵanyao akukolosya itendo.”');
INSERT INTO "silika" VALUES(18,105,'18:105','Ŵanganyao ni aŵala ŵaayikanile ilosyo ya Ambuje ŵao kwisa soni yakusimangana ni Jwalake, basi ni gajonasiche masengo gao, mwanti ngasitwaŵichila ŵanganyao lisiku lya Kiyama sikelo (jakwapimila masengo gao).');
INSERT INTO "silika" VALUES(18,106,'18:106','Galakwego ni malipilo gao; (jajili) Jahannama, ligongo lya ukafili wao, ni ŵagatesile ma Ȃya Gangu kwisa soni Achimitenga Ŵangu chipongwe.');
INSERT INTO "silika" VALUES(18,107,'18:107','Chisimu aŵala ŵaakulupilile ni kutendaga yambone, malo gao gakwichila kuchiŵa ku Matimbe ga Mbepo ja Firdaus.');
INSERT INTO "silika" VALUES(18,108,'18:108','Chakaŵe ŵandamo mwalakwemo, ngaja kusaka kutyokamo.');
INSERT INTO "silika" VALUES(18,109,'18:109','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Atamose mbwani jikaliji wino wakulembela maloŵe ga Ambuje ŵangu, mbwanijo jikamasile, maloŵe ga Ambuje ŵangu nkanigamale, namuno tungaiche najo (mbwani jine) janti mpela jijojo ni konjechesya.”');
INSERT INTO "silika" VALUES(18,110,'18:110','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une mundu mpele jenumanja. Ikujuwulidwa kukwangu yanti: Chisimu nnungu jwenu ni Nnungu Jumpepe. Sano jwaakusachilila kusimangana ni Ambujegwe, basi apanganye masengo gambone, ni akasawanganya pa ibada yakwagalagatila Ambujegwe ni jwalijose.”');
INSERT INTO "silika" VALUES(19,1,'19:1','Kâf-Hâ-Yâ-‘Aîn-Swâd. ');
INSERT INTO "silika" VALUES(19,2,'19:2','(Achi ni) chikumbusyo cha ukoto wa Ambuje ŵenu (Allah) pa kapolo Jwakwe Zakariya.');
INSERT INTO "silika" VALUES(19,3,'19:3','Pandema jajwaŵilasile Ambujegwe (Allah) ni m’bilango wakusongona.');
INSERT INTO "silika" VALUES(19,4,'19:4','Jwatite: “E Ambuje ŵangu! Maupa gangu gatochomele, ni ntwe wangu ujenele uli, ni nganimbeje jwantapane pakum’benda Mmwe Ambuje ŵangu (kwanti ni nganjanga).”');
INSERT INTO "silika" VALUES(19,5,'19:5','“Chisimu une ngwajogopa achalongo achimijangu (kuti atakonanga Dini) panyuma pangu, sano ŵankwangu ni ali ŵapalale, basi mumbani (mwanache) jwawinjila kuumila Kukwenu.”');
INSERT INTO "silika" VALUES(19,6,'19:6','“Tanjinjile une, ni talijinjile soni liŵasa lya Yaakubu (pa ilimu ya Dini ni utume), soni chimuntende jwalakwejo Mmwe Ambuje ŵangu kuŵa jwakunonyeledwa.”');
INSERT INTO "silika" VALUES(19,7,'19:7','(Jwasalilidwe kuti): “E mmwe Zakariya! Chisimu Uwwe tukumpa abali jakusengwasya ja mwanache lina lyakwe Yahaya, nganitumpeje jwalijose lyele linali kala kose.”');
INSERT INTO "silika" VALUES(19,8,'19:8','(Zakariya) jwatite: “Ambuje ŵangu! Chingole chantiuli mwanache kutendaga ŵankwangu ŵapalale, ni une pamasile pambiche pambesi pa uchekulu?”');
INSERT INTO "silika" VALUES(19,9,'19:9','(Lilaika) lyatite: “Mwele (ni muchiiŵele). Ambuje ŵenu akuti: ‘Yalakweyo iŵele yakwepepala Kukwangu, soni chisimu nan’gumbile mmwejo kalakala nkanim’be kuŵa chilichose.”');
INSERT INTO "silika" VALUES(19,10,'19:10','(Zakariya) jwatite: “Ambuje ŵangu! Mumbichilani chimanyilo (chachiimanyisye yanti ŵankwangu ajigele chilu)?” Lyalakwe (Lilaika) lyatite: “Chimanyilo chenu ni chanti; ngasinkombola kwaŵechetekasya ŵandu ilo itatu kutendaga n’di ŵambonembonepe.”');
INSERT INTO "silika" VALUES(19,11,'19:11','Basi ni jwakopweche m’chigunguli chakuswalila ni kuonechela ku ŵandu ŵakwe, ni jwasalile ŵanganyao ni makono kuti: “Munswejesyani (Allah) kundaŵi ni kwigulo.”');
INSERT INTO "silika" VALUES(19,12,'19:12','(Allah ŵansalile Yahaya kuti): “E mmwe Yahaya! Jigalani Chitabu (cha Taurat) mwamachili.” Ni twampele jwalakwe lunda (lwa utume) ali jwannandi.');
INSERT INTO "silika" VALUES(19,13,'19:13','Ni (twampele soni) chinonyelo (chakuwanganyichilana ni chanasa) kuumila Kukwetu, ni kuswejela (kusambi syantundu uliose), ni jwaliji soni jwawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(19,14,'19:14','Soni jwaliji jwakwatendela yambone achinangolo ŵakwe, soni nganaŵa jwakulikulungusya kapena jwakunyosya.');
INSERT INTO "silika" VALUES(19,15,'19:15','Soni ntendele waliji pa jwalakwe kutandila lisiku lyajwapali, ni lisiku lyakuwa nambo soni (uchiŵa pajwalakwe) lisiku lyajuchiukulidwa m’malembe ni kuŵa ni umi soni.');
INSERT INTO "silika" VALUES(19,16,'19:16','Soni munsalani Maryam m’Chitabu (achi cha Qur’an), pandema jaŵaatukwiche achalongo achimijakwe (ni kulungama) kuupande wakundaŵi (wansikiti wa Baitul Muqaddas).');
INSERT INTO "silika" VALUES(19,17,'19:17','Ni jwaŵisile chakusiŵilila ku ŵanganyao. Basi ni twantumichisye Nsimu Wetu (Jibulilu), ni walilandenye kukwakwe nti chisawu mundu jusyesyene.');
INSERT INTO "silika" VALUES(19,18,'19:18','(Maryam) jwatite: “Chisimu une ngulijuya mwa (Allah) Jwaukoto wejinji kukwenu, nam’baga mmwejo ŵawoga (wakun’jogopa Allah).”');
INSERT INTO "silika" VALUES(19,19,'19:19','(Lilaika) lyatite: “Chisimu une ntenga jwa Ambuje ŵenu, (mbiche) kukumpa ntuka wa mwanache jwanswela.”');
INSERT INTO "silika" VALUES(19,20,'19:20','(Maryam) jwatite: “Chingole chantiuli mwanache kutendaga nganangwayeje mundu jwalijose (jwannume), soni nganimba jwachigwagwa?”');
INSERT INTO "silika" VALUES(19,21,'19:21','(Lilaika) lyatite: “Mwele (ni muchiiŵele), Ambuje ŵenu akuti: ‘Yalakweyo iŵele yakwepepala Kukwangu, soni kuti tuntende jwalakwejo kuŵa chisimosimo kwa ŵandu, ni ukoto wakuuma Kukwetu, soni (achi) ni chiŵele chilamusi chelamulelamule.”');
INSERT INTO "silika" VALUES(19,22,'19:22','Basi (Maryam) ni ŵajigele chilu cha jwalakwe (Isa), basi (Maryam) ni jwatukwiche nacho (chilucho kulungama) kuupande wakunaula (wa ku Baitulaham).');
INSERT INTO "silika" VALUES(19,23,'19:23','Sano ulwele wakunkuuli wanyyikasyisye ku chisichi cha ntende. Jwalakwe jwatite: “Nkongwene! Nabola ningaawe nkaniiŵe yalakweyi, ngati ngaliji jwakuliŵalichika kusyene.”');
INSERT INTO "silika" VALUES(19,24,'19:24','Basi (Lilaika) lyam’bilasile (Maryam) chamwiŵanda mwakwe, (lichitiji): “Ngasin’dandaula. Chisimu Ambuje ŵenu antendele mwiŵanda mwenumo kapwapwa.”');
INSERT INTO "silika" VALUES(19,25,'19:25','“Soni ligwedanyilani chisichi chantendecho, tijumpatuchile tende jwan’jiwu.”');
INSERT INTO "silika" VALUES(19,26,'19:26','“Basi lyani, mwani ni ntuulasye liso (lyenu). Sano naga mum’bone mundu jwalijose, basi n’jile (ni makono) kuti: ‘Chisimu une ndaŵile nasili kwa (Allah) Jwaukoto wejinji jakuleka kuŵecheta, mwanti lelo nganam’bechetekasya mundu jwalijose.”');
INSERT INTO "silika" VALUES(19,27,'19:27','Basi ni jwaiche najo (mwanachejo) ku ŵandu ŵakwe ali am’beleche. Ŵanganyao ŵatite: “E mmwe Maryam! Chisimu pamasile pam’biche ni chindu chekulungwa nnope (chakusimosya).”');
INSERT INTO "silika" VALUES(19,28,'19:28','“E mmwe alumbuŵao Haruna! Atati ŵenu nganaŵa mundu jwangalumbana, namuno achikulu ŵenu soni nganaŵa ŵachigwagwa.”');
INSERT INTO "silika" VALUES(19,29,'19:29','Basi (Maryam) ŵannaanjiile (mwanache kuti aŵechetane najo). Ŵanganyao ŵatite: “Chitum’bechetekasye chinauli mwanache jwanti ali nnuuli?”');
INSERT INTO "silika" VALUES(19,30,'19:30','(Mwanache) jwatite: “Chisimu une kapolo jwa Allah, ambele Chitabu, ni andesile kuŵa Ntume.”');
INSERT INTO "silika" VALUES(19,31,'19:31','“Soni andesile kuŵa jwaupile palipose pandili, ni anjisyisye kuswali ni kutola Zaka muchinonjelele ndili chijumi.”');
INSERT INTO "silika" VALUES(19,32,'19:32','“Ni kwatendela yaukoto mama ŵangu, ni nganandenda kuŵa jwakulikwesya, kapena jwantapane.”');
INSERT INTO "silika" VALUES(19,33,'19:33','“Soni ntendele uli kukwangu kutandila lisiku lyanapali, ni lisiku lyatinjiwa, kwikanila soni lisiku lyatinjiukulidwa m’malembe ni kuŵa ni umi soni.”');
INSERT INTO "silika" VALUES(19,34,'19:34','Jwalakwe ni Isa mwanache jwa Maryam. (Aga ni) maloŵe gakuona gaakuŵa (Akilisito) ali nkugakaichila.');
INSERT INTO "silika" VALUES(19,35,'19:35','Nganiiŵa yakuŵajilwa kwa Allah kulitendela mwanache jwalijose, kuswejela ni Kwakwe. Pati asachile chilichose (kuti chipatikane), basi agambaga kuchisalila kuti “M''be!” Ni chikasaŵaga.');
INSERT INTO "silika" VALUES(19,36,'19:36','(Isa jwatite) “Soni chisimu Allah ni M’mbuje gwangu soni M’mbuje gwenu, basi mun’galagatilani. Ali ni litala lyagoloka.”');
INSERT INTO "silika" VALUES(19,37,'19:37','Basi ni gaatiindeene makuga (ga Akilisito) chilikati chao (pakwamba ya Isa, ŵane kutiji mwanache jwa Nnungu, ŵane nnungu jwene, ŵane jumpepe mwa milungu jitatu), basi ipotesi tiikaŵe ya aŵala ŵaŵakanile yakuja kuonechela pansongano wa lisiku lyekulungwa.');
INSERT INTO "silika" VALUES(19,38,'19:38','Kaje kupikanichisya ŵanganyao (achimakafilio) ni kulolechesya lisiku lyatachituichilila! Nambo ŵalupuso lelo (pa duniya pano), ali mu kupotela kwakuonechela.');
INSERT INTO "silika" VALUES(19,39,'19:39','Soni mwatetelani ŵanganyao ya lisiku lyamadandausi, ndema jachichilamulidwa chilamusi (chakwinjila ku Mbepo ni ku Moto), kutendaga ŵanganyao (pa duniya pano), ali nkulilechelela soni ngakukulupilila.');
INSERT INTO "silika" VALUES(19,40,'19:40','Chisimu Uwwe ni ŵatuchitawalila litaka ni yaili palakwepo (yosope ichiŵa Yetuwe panyuma pakuwa iwumbe yosope), soni Kukwetu tachiuchisyidwa.');
INSERT INTO "silika" VALUES(19,41,'19:41','Soni munsalani Ibrahima m’Chitabu (achi cha Qur’an), chisimu jwalakwe jwaliji jwakuŵecheta yakuona soni Ntume.');
INSERT INTO "silika" VALUES(19,42,'19:42','Pandema jaŵatite kwa babagwe: “E baba ŵangu! Ligongo chichi nkugalagatila chindu changapikana, changalola soni changaŵa nkunkamuchisya chilichose?”');
INSERT INTO "silika" VALUES(19,43,'19:43','“E baba ŵangu! Chisimu une umbichilile umanyilisi wanganium’bichilila mmwe, basi munguyaani, tinan’jongolele kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(19,44,'19:44','“E baba ŵangu! Ngasimun’galagatilaga shetani, chisimu shetani kwa (Allah) Jwaukoto wejinji juŵele jwakunyosya kusyene.”');
INSERT INTO "silika" VALUES(19,45,'19:45','“E baba ŵangu! Chisimu une ngogopa kuti itakwika mwainkwayiye ilagasyo kuumila kwa (Allah) Jwaukoto wejinji, ni kuŵa mmwejo nsyoŵe jwa shetani.”');
INSERT INTO "silika" VALUES(19,46,'19:46','(Babagwe) ŵatite: “Ana nkujiŵenga milungu jangu mmwe Ibrahima (niligongo lyakwe nkujigamba)! Chisimu naga ngankuleka (yakujigambayo) chinansome kwene (maganga), soni mundyochelani ndaŵi jeleu (tukaonegana).”');
INSERT INTO "silika" VALUES(19,47,'19:47','(Ibrahima) jwatite: “Ntendele uŵe kukwenu! Chinam’bendele chikululuko kwa Ambuje ŵangu, chisimu Ŵalakweo aŵele ŵakundendela nnope une ukoto.”');
INSERT INTO "silika" VALUES(19,48,'19:48','“Soni chinantundumalile ŵanganyammwe ni yankuiŵenda kunneka Allah, une ni chinam’bendeje Mmbuje gwangu. Ngwete chikulupi chanti ngamba jwantapane pakum’benda Mmbuje gwangu (kwanti ni ngajanjidwa duwa jangu).”');
INSERT INTO "silika" VALUES(19,49,'19:49','Basi ndema jaŵatundumalile ŵanganyao ni yaŵaliji nkuigalagatila kunneka Allah, twampele jwalakwejo Isihaka ni Yaakuubu, soni jwalijose (mwa ŵanganyao) twantesile kuŵa Ntume.');
INSERT INTO "silika" VALUES(19,50,'19:50','Ni twapele soni ŵanganyao kuumila mu ukoto Ŵetu (chipanje ni ŵanache), ni twaŵichile kusalidwa kwambone soni kwapenani (ni ŵandu ŵadini syosope).');
INSERT INTO "silika" VALUES(19,51,'19:51','Soni munsalani Musa m’Chitabu (achi cha Qur’an), chisimu jwalakwe jwaliji jwakusagulidwa ni kuswejesyedwa, soni jwaliji Ntenga soni Ntume.');
INSERT INTO "silika" VALUES(19,52,'19:52','Ni twam’bilasile kuumila kun’dyo kwetumbi, ni twam’bandichisye kuti tuŵechetane najo mwakusongona.');
INSERT INTO "silika" VALUES(19,53,'19:53','Ni twampele (Musa) kuumila mu ukoto Ŵetu nchimwenegwe Haruna kuŵa Ntume.');
INSERT INTO "silika" VALUES(19,54,'19:54','Soni munsalani Ismaila m’Chitabu (achi cha Qur’an), chisimu jwalakwe jwaliji jwakuona pachilanga, soni jwaliji Ntenga soni Ntume.');
INSERT INTO "silika" VALUES(19,55,'19:55','Soni jwaliji nkulichisya liŵasa lyakwe kuswali ni kutola Zaka, soni jwaliji jwakunonyeledwa kwa Ambujegwe (Allah).');
INSERT INTO "silika" VALUES(19,56,'19:56','Soni munsalani Idrisa m’Chitabu (achi cha Qur’an), chisimu jwalakwe jwaliji jwakuŵecheta yakuona soni Ntume.');
INSERT INTO "silika" VALUES(19,57,'19:57','Ni twannyakwile (ni kum’bika) malo gapenani kusyene.');
INSERT INTO "silika" VALUES(19,58,'19:58','Ŵanganyao ni aŵala ŵaŵapele Allah chindimba, mwa achimitume, mu ŵanache ŵa Adam, ni mu aŵala ŵatwajigele pamo ni Nuhu (m’chombo), ni mu ŵanache ŵa Ibrahima ni Israila, ni mu aŵala ŵatwajongwele ni kwasagula. Pati gachisoomedwaga kukwao ma Ȃya ga (Allah) Jwaukoto wejinji, akasasenjelemukukaga kusujudu aku anankulila.');
INSERT INTO "silika" VALUES(19,59,'19:59','Basi ni yasipwiche panyuma pao isipuka (ŵanache ni isukulu yakusakala), ŵasijonasile swala (pangasilondesya chenene kasiswali kakwe kapena kusileka kwene), ni ŵakuyiye isako (yao), ni tachija kusimangana nao Moto.');
INSERT INTO "silika" VALUES(19,60,'19:60','Ikaŵeje aŵala ŵatesile toba, ni kulupilila, ni kutendaga yambone, basi ŵanganyao takajinjile ku Mbepo, soni ngaja kutendedwa lupuso pa chilichose.');
INSERT INTO "silika" VALUES(19,61,'19:61','(Takagajinjile) Matimbe Gandamo, agala gataŵene nao chilanga (Allah) Jwaukoto wejinji achikapolo Ŵakwe mwangali kugaona. Chisimu Jwalakwe chilanga Chakwe chiŵele chakwika.');
INSERT INTO "silika" VALUES(19,62,'19:62','Ngaaja kupikana mwalakwemo maloŵe gangali mate, ikaŵeje salâm (kulamuchila kwantendele), soni akwete kupata lisiki lyao mwalakwemo kundaŵi ni kwigulo.');
INSERT INTO "silika" VALUES(19,63,'19:63','Jalakwejo ni Mbepo jatukaape kujitawala mwa achikapolo Ŵetu ŵaŵaliji ŵawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(19,64,'19:64','Ni ngatukasatulukaga (uwwe Achimalaika) ikaŵeje kwalilamusi lya Ambuje ŵenu (mmwe Muhammadi صلى الله عليه وسلم), iŵele Yakwe yaili paujo petu ni yapanyuma petu kwisa soni yaili chilikati chayalakweyo, ni nganaŵa Ambuje ŵenu ŵanti ni kuliŵalila.');
INSERT INTO "silika" VALUES(19,65,'19:65','M’mbuje jwa kumawunde ni petaka ni chilikati cha iŵiliyi, basi mun’galagatileje (Jwele pejo), soni jendelechelani kupilila pakun’galagatila Jwalakwe. Ana pana jwankummanyilila jwakulandana Najo pakolanjidwa?');
INSERT INTO "silika" VALUES(19,66,'19:66','Sano mundu (kafili) ni akutiji: “Ana ndili mbwile, nambo yakuona chinjikoposyedwa (m’malembe) ni kuŵa ni umi?”');
INSERT INTO "silika" VALUES(19,67,'19:67','Ana mundu ngaakukumbuchila kuti chisimu Uwwe twan’gumbile jwalakwejo kalakala kutendaga nganaŵa chilichose?');
INSERT INTO "silika" VALUES(19,68,'19:68','Basi ngwalumbila Ambuje ŵenu! Chisimu Uwwe tuchasonganganya ŵanganyao pampepe ni mashetani, kaneka ni tuchiika nao kusyene mungulugulu mwa Jahannama ali chitindiŵalile.');
INSERT INTO "silika" VALUES(19,69,'19:69','Kaneko ni tuchikoposya kusyene kuumila mwikuga lililyose jwalijose mwa ŵanganyao jwaŵaliji jwakupelenganya nnope pakunnyosya (Allah) Jwaukoto wejinji.');
INSERT INTO "silika" VALUES(19,70,'19:70','Soni chisimu Uwwe, ni ŵatukwamanya chenene ŵali ŵakuŵajilwa kuujinjila walakwe (Motowo).');
INSERT INTO "silika" VALUES(19,71,'19:71','Soni pangali jwalijose mwa jenumanja ikaŵeje chakaujomboche (Motowo), chiŵele (chalakwechi) kwa Ambuje ŵenu chilamusi chelamulelamule.');
INSERT INTO "silika" VALUES(19,72,'19:72','Kaneko ni chitukaajokole aŵala ŵaŵaliji ni woga (wakun’jogopa Allah), ni chitukaaleche mwalakwemo ŵalupuso ali chitindiŵalile.');
INSERT INTO "silika" VALUES(19,73,'19:73','Sano naga gachisoomedwaga kukwao ma Ȃya Getu gakupikanika chenene, aŵala ŵaakanile akasatiji kwa aŵala ŵaakulupilile: “Ana lyapi mwa makuga gaŵili (lyajenumanja kapena lyetuwe) lyalili ni malo gambone nnope gakutama soni lyalili nganya jambone nnope (jakuchimbichika)?”');
INSERT INTO "silika" VALUES(19,74,'19:74','Ana jilingwa mwa mikutula jatwajijonasile paujo pao, jajakwete ndundu syambone nnope kwisa soni kaoneche kambone?');
INSERT INTO "silika" VALUES(19,75,'19:75','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Mundu jwali nkusokonechela, basi (Allah) Jwaukoto wejinji channundile jwalakwejo ndaŵi (jaumi wakwe), kwikanila pandema jataachiiona yaakuŵa nkupedwa chilanga – mwine ilagasyo kapena ndaŵi (jakwichila Kiyama) – basi tachimanyilila kuti nduni jwakwete malo gakusakala ni jwaakwete asilikali ŵangali machilili.”');
INSERT INTO "silika" VALUES(19,76,'19:76','Sano Allah ni akwajonjechesyaga chongoko ŵandu ŵaajongweche, soni masengo gambone gakwendelechela ni gagali gambone nnope kwa Ambuje ŵenu pa malipilo, soni ni kwakuujila kwambone (ngaŵa ya duniya yaakutwanjila achimakafiliyo).');
INSERT INTO "silika" VALUES(19,77,'19:77','Ana mum’bweni ajula jwaagakaaniile ma Ȃya Getu, ni kuŵecheta kuti: “Chisimu chene une (naga kumbuchisya soni ku umi pa Kiyama), chinjipedwa (soni) chipanje ni ŵanache (yaatite pakuumba pa duniya pano).”');
INSERT INTO "silika" VALUES(19,78,'19:78','Ana jutekuilingula yakusisika, kapena jutaŵene chilanga ni (Allah) Jwaukoto wejinji?');
INSERT INTO "silika" VALUES(19,79,'19:79','Ngwamba! Uwwe chitulembe yajukuŵecheta, ni tuchin’jonjechesya ndaŵi jelewu nnope jailagasyo.');
INSERT INTO "silika" VALUES(19,80,'19:80','Ni tuchintawalila (pajuchiwapo) yosope yajukwambayo (chipanje ni ŵanache ŵakweo), ni juchituichilila (ku Kiyama) juli jikajikape.');
INSERT INTO "silika" VALUES(19,81,'19:81','Eti ŵanganyao alitendele milungu jinepe kunneka Allah, kuti jape ŵanganyao machili ni uchimbichimbi.');
INSERT INTO "silika" VALUES(19,82,'19:82','Ngwamba! (Ŵaakuti achimilungu ŵaowo) tachikukana kwagalagatila kwaoko, ni tachisyuka kuŵa atinda ŵao.');
INSERT INTO "silika" VALUES(19,83,'19:83','Ana ngankuiona yanti Uwwe tukugatumisyaga mashetani pa achimakafili gagakwakwembelechesyaga kusyene (kutenda yakusakala)?');
INSERT INTO "silika" VALUES(19,84,'19:84','Basi ngasintenda chitema pa ŵanganyao (kuti apedwe ilagasyo mwachitema), chisimu Uwwe tukuŵa tulinkwaŵalanjila ŵanganyao chiŵalanjilo (chamasiku gao, pachigakwanapo tuchapa ilagasyo).');
INSERT INTO "silika" VALUES(19,85,'19:85','Lisiku lyatuchasonganganya ŵandu ŵawoga (wakun’jogopa Allah) ulendo wa kwa (Allah) Jwaukoto wejinji, ali m’mikutula jekwele mu yakwela.');
INSERT INTO "silika" VALUES(19,86,'19:86','Ni tuchasakula akuleŵa wa ku Jahannama ali ni njota (mpela yaikasatiji pakusakulidwa ilango yanjota wakukumwa mesi).');
INSERT INTO "silika" VALUES(19,87,'19:87','Ngasakola machili gakuchondelela ikaŵeje jwapochele chilanga (cha lusa) kuumila kwa (Allah) Jwaukoto wejinji.');
INSERT INTO "silika" VALUES(19,88,'19:88','Ŵanganyao ni akutiji: “(Allah) Jwaukoto wejinji alitendele mwanache.”');
INSERT INTO "silika" VALUES(19,89,'19:89','Chisimu pamasile pam’biche nacho chindu chakunyalaya kusyene (pa yankuŵechetayi).');
INSERT INTO "silika" VALUES(19,90,'19:90','Mawunde gakusigalaga panandi kuti gapasuchepasuche ni maloŵego, nombe litaka ganjikaganjika, nombe matumbi kugwa ni katikakatika.');
INSERT INTO "silika" VALUES(19,91,'19:91','Ligongo lyakunnambuchisya (Allah) Jwaukoto wejinji kuti jwana mwanache.');
INSERT INTO "silika" VALUES(19,92,'19:92','Soni nganiiŵa yakuŵajilwa kwa (Allah) Jwaukoto wejinji kulitendela mwanache.');
INSERT INTO "silika" VALUES(19,93,'19:93','Pangali jwalijose jwaali kumawunde ni petaka ikaŵeje chijuchiyika kwa (Allah) Jwaukoto wejinji juli kapolo.');
INSERT INTO "silika" VALUES(19,94,'19:94','Chisimu pamasile paŵakusenye ŵanganyao pakwamanya chenene, ni ŵaŵalasile chiŵalanjilo chakwana.');
INSERT INTO "silika" VALUES(19,95,'19:95','Soni ŵosope ŵeneo tachim’bichilila (Allah) lisiku lya Kiyama jikajika.');
INSERT INTO "silika" VALUES(19,96,'19:96','Chisimu aŵala ŵaakulupilile ni kutendaga yambone, (Allah) Jwaukoto wejinji tachaŵichila ŵanganyao chinonyelo.');
INSERT INTO "silika" VALUES(19,97,'19:97','Basi chisimu tujijepepesye jele (Qur’aniji) m’chiŵecheto chenu (mmwe Muhammadi صلى الله عليه وسلم), kuti mwasengwasye najo ŵandu ŵawoga (wakun’jogopa Allah), ni kwatetela najo ŵandu ŵamakani.');
INSERT INTO "silika" VALUES(19,98,'19:98','Ana jilingwa mwa mikutula jatwajijonasile paujo pao? Ana pana jwalijose jwankum’bona (mmwe Muhammadi صلى الله عليه وسلم) mwa jamanjao kapena kupikana nswakanyo wakwe?');
INSERT INTO "silika" VALUES(20,1,'20:1','Twâ-Hâ. ');
INSERT INTO "silika" VALUES(20,2,'20:2','Nganituntuluchisya mmwe (Muhammadi صلى الله عليه وسلم) Qur’an kuti nnagasiche.');
INSERT INTO "silika" VALUES(20,3,'20:3','Nambo kuti jiŵe chikumbusyo kwa mundu jwaakun’jogopa (Allah).');
INSERT INTO "silika" VALUES(20,4,'20:4','Chakutulusyidwa chakuumila kwa Ajula jwaŵagumbile litaka ni mawunde gagali kwinani nnope.');
INSERT INTO "silika" VALUES(20,5,'20:5','(Jwalakwe ni Allah) Jwaukoto wejinji, jwatenjele pa Arishi (Chindanda Chaulungu katenjele kakulandana ni uchimbichimbi Ŵakwe).');
INSERT INTO "silika" VALUES(20,6,'20:6','Iŵele Yakwe yosope yaili kumawunde ni yaili petaka ni yaili chilikati chakwe, kwika soni yaili pasi petaka.');
INSERT INTO "silika" VALUES(20,7,'20:7','Sano kanga chinnyanyiche liloŵe (pakuomba duwa kapena kutenda mwakusongona; yalumope), ngati chisimu Jwalakwe (Allah) jukumanyilila yaasili ni yakusisika nnope.');
INSERT INTO "silika" VALUES(20,8,'20:8','Allah! Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwele pejo, Jwalakwe jukwete Mena gambone nnope.');
INSERT INTO "silika" VALUES(20,9,'20:9','Soni ana jinyyiche abali ja Musa?');
INSERT INTO "silika" VALUES(20,10,'20:10','Ndema jajwauweni moto, basi ni jwalisalile liŵasa lyakwe kuti: “Jembechelani! Chisimu une nauweni moto; mwine une kuŵa nkwika nacho kukwenu chisinga kuumila mu walakweo, kapena chingapate pa motopo jwakunondolela (litala).”');
INSERT INTO "silika" VALUES(20,11,'20:11','Basi ndema jajwauŵichilile (motowo) jwaŵilanjidwe (kuti): “E mmwe Musa!”');
INSERT INTO "silika" VALUES(20,12,'20:12','“Chisimu Une ni Nam’mbuje gwenu! Basi wulani ilatu yenu; chisimutu mmwe n’di pachiŵata chakutakata (chakuŵilanjikwa) Tuwaa.”');
INSERT INTO "silika" VALUES(20,13,'20:13','“Soni Une nansagwile (kuŵa Ntume), basi pikanilani yaikujuwulidwa.”');
INSERT INTO "silika" VALUES(20,14,'20:14','“Chisimu Une ni Allah! Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Unepe, basi mungalagatilani, ni n’jimicheje Swala mwakolosya pakungumbuchila Une.”');
INSERT INTO "silika" VALUES(20,15,'20:15','“Chisimu Kiyama jikwisa, melepe ngujisisa, kuti mundu jwalijose akalipidwe pa yaakupanganya.”');
INSERT INTO "silika" VALUES(20,16,'20:16','“Sano akasansiŵilikanganya ata panandi kuleka (kulupilila) ya jalakwejo ajula jwangakujikulupilila, ni jugamba kuya isako yakwe; ntakwisa mwan’jonasiche.”');
INSERT INTO "silika" VALUES(20,17,'20:17','“Ana nichichi chele chachili mu nkono wenu wan’dyo mmwe Musa?”');
INSERT INTO "silika" VALUES(20,18,'20:18','Jwalakwe jwatite: “Jalakweji nisimbo jangu, ngujijegamilaga jalakweji (pakwenda), ni ngupakatichisyaga masamba gakulichisya ngondolo syangu, nambo soni une ngwete mu jalakweji ipaka ine.”');
INSERT INTO "silika" VALUES(20,19,'20:19','(Allah) jwatite: “Jiponyani mmwe Musa!”');
INSERT INTO "silika" VALUES(20,20,'20:20','Ni ŵajiponyisye; basi ajile sai ŵajiweni jisyuchile lijoka lyakwenda mwaluŵilo.');
INSERT INTO "silika" VALUES(20,21,'20:21','(Allah) jwatite: “Likamulani, soni nkajogopa. Chituliuchisye mu kaŵe kakwe kandanda (kuŵa simbo).”');
INSERT INTO "silika" VALUES(20,22,'20:22','“Soni kwapatisyani ligasa lyenu kungwapa kwenu; chilikopoche lili lyeswela (lyakumelemenda) ngaŵaga mwaulwele, (chele nikuŵa) chisimosimo chine.”');
INSERT INTO "silika" VALUES(20,23,'20:23','“Kuti tunnosye ine mu isimosimo Yetu yekulungwakulungwa.”');
INSERT INTO "silika" VALUES(20,24,'20:24','“Jaawulani kwa Firiauna, chisimu jwalakwejo jusumbile mpika (pakulikwesya).”');
INSERT INTO "silika" VALUES(20,25,'20:25','(Musa) jwatite: “Ambuje ŵangu! Sapangulilani chidali changu.”');
INSERT INTO "silika" VALUES(20,26,'20:26','“Nimunjepepachisye masengo gangu.”');
INSERT INTO "silika" VALUES(20,27,'20:27','“Soni gopolaani chiundo palulimi pangu (ngaŵechetaga mwachimeme).”');
INSERT INTO "silika" VALUES(20,28,'20:28','“Kuti (ŵandu) akapikanichisyeje yakuŵecheta yangu.”');
INSERT INTO "silika" VALUES(20,29,'20:29','“Soni mbichilaani nkungamuchisya jwakuumila kwiŵasa kwangu.”');
INSERT INTO "silika" VALUES(20,30,'20:30','“(Jwali) Haruna, nkulugwangu.”');
INSERT INTO "silika" VALUES(20,31,'20:31','“Limbisyani ni jwalakwejo machili gangu.”');
INSERT INTO "silika" VALUES(20,32,'20:32','“Soni mum’bwangani mmasengo gangu (gakulungusya utenga).”');
INSERT INTO "silika" VALUES(20,33,'20:33','“Kuti tunswejesyeje Mmwejo (Allah) kwejinji.”');
INSERT INTO "silika" VALUES(20,34,'20:34','“Ni tunkumbuchileje kwejinji.”');
INSERT INTO "silika" VALUES(20,35,'20:35','“Chisimu Mmwe m’bele ŵakutulolechesya uwwe.”');
INSERT INTO "silika" VALUES(20,36,'20:36','(Allah) jwatite: “Pamasile pampedwile iŵendo yenuyo, E mmwe Musa!”');
INSERT INTO "silika" VALUES(20,37,'20:37','“Soni chisimu chene Uwwe twampele mmwe ukoto ndaŵi jine (kalakala).”');
INSERT INTO "silika" VALUES(20,38,'20:38','“Ndema jatwajuwulile mama ŵenu yatwajuwulile.”');
INSERT INTO "silika" VALUES(20,39,'20:39','“(Yanti) mum’biikaani jwalakwe (mwanachejo) mwibokosi, ni nkaliŵiche nnusulo, basi lusulo chilumponye munjengwe, (sano palakwepo) amagongo Ŵangu ŵali soni amagongo ŵakwe tan’jigale. Ni naŵisile chinonyelo penumwe chakuuma Kukwangu (kuti m’be ŵakunonyeledwa ni ŵandu wosope), nambo soni kuti nneledwe kwa Liso Lyangu (mwakuloledwa ni Une).”');
INSERT INTO "silika" VALUES(20,40,'20:40','“(Kumbuchilani) katema kaŵajendaga alumbu ŵenu (kuja kwiŵasa lya Firiauna) ni kuja kuti: ‘Ana nannanjile kwa mundu jwampaka akombole kunnela jwalakweju?’ Basi ni twam’buchisye kwa achikulu ŵenu, kuti gatulale meso gao, nambo soni kuti akadandaula. Kaneka ni mwauleje mundu (mwakuluchisya), basi ni twan’jokwele ku madandausi gamakulungwa, ni twan’dinjile mmwejo ni mayeso gausito nnope. Kaneka mwatemi yaka igala ni ŵandu ŵa ku Madyana. Kaneko nim’biche (apa) mwampango (Ŵangu wakumpa utume n’di niyaka makumi ncheche) E mmwe Musa!”');
INSERT INTO "silika" VALUES(20,41,'20:41','“Ni nansagwile mmwejo kulisagulila Nansyene (kuti m’be ntume Jwangu).”');
INSERT INTO "silika" VALUES(20,42,'20:42','“Jaulani mmwe ni akuluŵenu pamo ni isimosimo Yangu, soni ngasimpela pakungumbuchila Une.”');
INSERT INTO "silika" VALUES(20,43,'20:43','“Jaawulani ŵanaŵaŵilimmwe kwa Firiauna, chisimu jwalakwejo jusumbile mpika (pakulikwesya).”');
INSERT INTO "silika" VALUES(20,44,'20:44','“Basi ni nkansalile maloŵe gakoloŵa kuti mwine jwalakwejo chijukachetelele kapena chijukajogope.”');
INSERT INTO "silika" VALUES(20,45,'20:45','Ŵanganyao ŵatite: “Ambuje ŵetu! Chisimu uwwe tukogopa kuti chakatuguŵatile (nkanituyiche mwakuŵecheta chilichose) kapena chakasumbe mpika (wakutulagachisya ni kulikwesya).”');
INSERT INTO "silika" VALUES(20,46,'20:46','(Allah) jwatite: “Nkajogopa, chisimu Une ndinamwe, ngupikana soni ngulola.”');
INSERT INTO "silika" VALUES(20,47,'20:47','“Basi mun’jaulilani, ni nkajile: ‘Chisimu uwwe mitenga jiŵili ja Ambuje ŵenu, basi mwalekani aŵigule nowe ŵanache ŵa Israila, soni ngasimwalagasya. Chisimu tum’bichilile ni chisimosimo chakuumila kwa Ambuje ŵenu. Sano ntendele chiuŵe kwa mundu jwaakukuya chongoko.’');
INSERT INTO "silika" VALUES(20,48,'20:48','‘Chisimu uwwe ijuwulidwe kukwetu yanti: Chisimu ilagasyo chiiŵe kwa mundu jwaakukanila (yalakweyi) ni galauchila kumbali.”');
INSERT INTO "silika" VALUES(20,49,'20:49','(Firiauna) jwatite: “Ana M’mbuje gwenujo ninduni, E mmwe Musa?”');
INSERT INTO "silika" VALUES(20,50,'20:50','(Musa) jwatite: “M’mbuje gwetu ni Ajula jwaachipele chindu chilichose kagumbidwe kakwe, kaneka ni kuchijongolela (ku yaachiguumbiile).”');
INSERT INTO "silika" VALUES(20,51,'20:51','(Firiauna) jwatite: “Nambi mikutula jandanda jili uli (jajapite mmwe nkanim’biche)?”');
INSERT INTO "silika" VALUES(20,52,'20:52','(Musa) jwatite: “Umanyilisi waajimanya jalakwejo uli kwa Ambuje ŵangu m’chitabu (chailembedwemo ichindu yosope), Ambuje ŵangu ŵangaŵa nkusoya namuno kuliŵalila.”');
INSERT INTO "silika" VALUES(20,53,'20:53','Ajula jwam’biichiile litaka kuŵa chakutandika, ni ampaanjiile mwalakwemo matala, ni atulwisye kuumila kwinani mesi (gawula), ni tukopwesye ni galakwego mitundu jakulekanganalekangana ja mmela.');
INSERT INTO "silika" VALUES(20,54,'20:54','Lyagani soni illichisyeje ilango yenu. Chisimu mu yalakweyo mwana ilosyo kwa achinsyene lunda.');
INSERT INTO "silika" VALUES(20,55,'20:55','Kuumila mwalakwe (mwitakamo) tun’gumbile, soni mwalakwemo chitum’buchisye, nambo soni kuumila mwalakwemo tuchinkoposya ndema jine (pa Kiyama).');
INSERT INTO "silika" VALUES(20,56,'20:56','Soni chisimu chene twannosisye jwalakwe (Firiauna) isimosimo Yetu yosope yene, nambo jwasisisye ni kana.');
INSERT INTO "silika" VALUES(20,57,'20:57','(Firiauna) jwatite: “Ana ntuŵichiiliile kuti ntukoposye m’chilambo mwetu ni usaŵi wenu, E mmwe Musa!”');
INSERT INTO "silika" VALUES(20,58,'20:58','“Chisimu nowe ŵakwe chitum’bichilile ni usaŵi wanti mpela uwwuwu, basi ŵiikaani chilikati chetuwe nomwe chilanga (chansongangano) changaŵa nkuchikulusya uwwe kapena mmwe, (chitusimanganile) pamalo pakuŵajilwa.”');
INSERT INTO "silika" VALUES(20,59,'20:59','(Musa) jwatite: “Chilanga chenu (chiŵe) lisiku lyakulipeta (lyuŵa lyaung’ando), ni tasonganganyisidwe ŵandu ndaŵi jakwela nsana.”');
INSERT INTO "silika" VALUES(20,60,'20:60','Basi Firiauna jwagalawiche ni kuja kusonganganya matipwi gakwe, kaneka jwaiche (ni asaŵi lisiku lyachilanga).');
INSERT INTO "silika" VALUES(20,61,'20:61','Musa jwasalile ŵanganyao kuti: “Kutapana kwenu ŵanyamwe! Ngasimunsepelela Allah unami, atakwika mwanteketulenye ni ilagasyo, soni pamasile paajonasiche mundu jwaasepelele unami.”');
INSERT INTO "silika" VALUES(20,62,'20:62','Basi (asaŵi) ŵaakanganile chilikati chao pa ichindu yao (yakwamba Musa ni nkulugwe), ni ŵasongonelene mwantemela.');
INSERT INTO "silika" VALUES(20,63,'20:63','Ŵanganyao ŵatite (mukusongonelana mwao): “Chisimu ŵanyaŵatu asaŵi (ŵamanye chenene), ni usaŵi waowo akusaka kuti ankoposye jenumanja m’chilambo mwenu, ni kumasya nyendele jenu jajili jambone nnope.”');
INSERT INTO "silika" VALUES(20,64,'20:64','“Basi kusanyani matipwi genu, kaneka ni m’biche n’di m’miselamisela, soni chisimu lelo chaŵe jwakupunda mundu jwataŵe jwapenani (pakulongolela matipwi gakwe).”');
INSERT INTO "silika" VALUES(20,65,'20:65','Ŵanganyao ŵatite: “E mmwe Musa! Ana chimponye mmwe (kundanda), kapena uwwe ni ŵachituŵe ŵandanda kuponya?”');
INSERT INTO "silika" VALUES(20,66,'20:66','(Musa) jwatite: “Nambo ponyani (kaje) jenumanja.” Basi ajile sai, ngonji syao (asaŵi ŵala) ni simbo syao syaonekaga kukwakwe (Musa) ligongo lyausaŵi wao nti chisawu sikwenda mwaluŵilo.');
INSERT INTO "silika" VALUES(20,67,'20:67','Basi ni wampatile woga mu ntima mwakwe Musa.');
INSERT INTO "silika" VALUES(20,68,'20:68','Twatite: “Nkajogopa, chisimu mmwejo ni ŵatim’be ŵapenani nnope.”');
INSERT INTO "silika" VALUES(20,69,'20:69','“Sano ponyani chachili kunkono wenu wan’dyo, chichimile yaalinganyisye, chisimu ŵanganyao alinganyisye matipwi gansaŵi, sano nsaŵi nganaŵa apundile palipose patayiche (ni ukombosi wakwe).”');
INSERT INTO "silika" VALUES(20,70,'20:70','Basi asaŵi ŵasenjelemukwiche kusujudu, ni ŵatite: “Tunkulupilile M’mbuje jwa Haruna ni Musa.”');
INSERT INTO "silika" VALUES(20,71,'20:71','(Firiauna) jwatite: “Aa! Jenumanja munkulupilile jwalakwe nkaninampe luusa? Nikuti jwalakweju chisimu chene ni jwankulungwa jwenu jwaŵan’jiganyisye usaŵi. Basi chingate kwene makono genu ni ngongolo syenu mwakutindanya (ligasa lyakun''dyo ni likau lyakunchiji, kapena ligasa lyakunchiji ni likau lyakun''dyo), soni ni chinankomele kwene pa ipika ya mitende, ni timmanyilile kwene kuti ŵani mwetuwe (une Firiauna kapena Nnungu jwa Musa) ŵaali ŵakupeleka ilagasyo yakupoteka nnope ni yakwendelechela.”');
INSERT INTO "silika" VALUES(20,72,'20:72','Ŵanganyao ŵatite: “Uwwe nganituŵa tunsagwile mmwe kuileka ayi (yakuona) yaituichilile yakuonechela palangulangu, ni kunneka Ajula (Allah) juŵatugumbile. Basi pitisyani chilamusi chankusaka kupitisya, chisimu mmwe nkwete ukombosi wakupitisya chilamusi chakwayana ni auno umi wa duniya pe.”');
INSERT INTO "silika" VALUES(20,73,'20:73','“Chisimu uwwe tunkulupilile M’mbuje gwetu kuti atukululuchile ileŵi yetu, kwisa soni usaŵi wantukanganichisye (kutenda), soni Allah ni jwali Jwambone nnope (pamalipilo kumpunda mmwe naga kun’jitika), soni Jwakwendelechela nnope (pakupeleka ilagasyo kumpunda mmwe naga kunnyosya).”');
INSERT INTO "silika" VALUES(20,74,'20:74','Chisimu jwatiŵaichilile Ambujegwe juli jwakuleŵa, basi chisimu jukwete kupata jwalakwejo Jahannama, ngajuja kuwa kwalakweko soni ngajuja kola umi (wambone).');
INSERT INTO "silika" VALUES(20,75,'20:75','Sano jwatiŵaichilile juli jwakulupilila, juli jumasile pajutesile yambone, basi ŵanganyao akwete kupata mauchimbichimbi gapenani kusyene.');
INSERT INTO "silika" VALUES(20,76,'20:76','Matimbe Gandamo, sulo sikujilima cha pasi pakwe, chakaŵe ŵandamo mwalakwemo, soni galakwega ni malipilo gamundu jwaliswejesye.');
INSERT INTO "silika" VALUES(20,77,'20:77','Soni chisimu chene Uwwe twan’juwulile Musa (maloŵe ganti): “Ŵigulani lwachilo ni achikapolo Ŵangu, ni mwapanjile litala lyejumu m’mbwani, nkajogopa kamulwa (ni Firiauna) soni nkaŵa ni woga (uliose mpela kumilwa).”');
INSERT INTO "silika" VALUES(20,78,'20:78','Sano Firiauna ni asilikali ŵakwe ŵakuyiye ŵanganyao, basi ni gaunichiile m’mbwanimo (mesi) gagaakutiiche.');
INSERT INTO "silika" VALUES(20,79,'20:79','Sano Firiauna ŵapotelesye ŵandu ŵakwe, soni nganiŵajongola.');
INSERT INTO "silika" VALUES(20,80,'20:80','E jenumanja ŵanache ŵa Israila! Pamasile patwan’jokwele kwa mmagongo jwenu, ni twampele chilanga (chakwiisa ku) upande wakun’dyo wetumbi, ni twantuluchisye manna ni saliwa.');
INSERT INTO "silika" VALUES(20,81,'20:81','(Ni kuntenda kuti): “Lyagani mu yambone yatumpeleyi, ni nkasumba mpika pa yalakweyi, jitakwika mujintuluchile gasabu Jangu. Sano jwajikuntuluchilaga gasabu Jangu nikuti pamasile pajujonasiche.”');
INSERT INTO "silika" VALUES(20,82,'20:82','“Soni chisimu Une ni Jwakululuka kusyene kwa mundu jwatesile toba ni kulupilila, soni ni kutendaga yambone, kaneka ni kwendelechela kukuya chongoko (mpaka kuwa).”');
INSERT INTO "silika" VALUES(20,83,'20:83','(Ndema jaŵakwesile Musa petumbi, Allah jwatite): “Ana nichichi chachintendekasisye kwanguya kwakwikanila kwaleka ŵandu ŵenu, E mmwe Musa?”');
INSERT INTO "silika" VALUES(20,84,'20:84','Jwalakwe jwatite: “Ŵanganyao ali panyuma pangupa akunguya. Ni ndesile chitema kwika Kukwenu Ambuje ŵangu, kuti mpunde kunonyela.”');
INSERT INTO "silika" VALUES(20,85,'20:85','(Allah) jwatite: “Basitu Uwwe pamasile patwalinjile mayeso ŵandu ŵenu panyuma penu, ni ŵapotelesye Saamiriyyu.”');
INSERT INTO "silika" VALUES(20,86,'20:86','Basi Musa jwaujiile ku ŵandu ŵakwe juli ni gasabu soni juli jwakudandaula. Ni jwatite: “E ŵandu ŵangu! Ana M’mbuje gwenu nganijutaŵana namwe chilanga chambone? Kapena ndaŵi jachilangacho jalewipe kukwenu? Kapena nsachile kuti jintuluchile gasabu kuumila kwa M’mbuje gwenu, basi ni nkasile chilanga changu?”');
INSERT INTO "silika" VALUES(20,87,'20:87','Ŵanganyao ŵatite: “Uwwe nganitukasa chilanga chenu mwachisako chetu, nambo kuti uwwe twatwichidwe misigo ja ipeto yaŵandu (ŵa Firiauna), basi ni twaiponyisye (pamoto ni kunyelenyenduka nigumbika kang’ombe), basi iyyoyopeyo soni ni ijwatite pakuponya Saamiriyyu.”');
INSERT INTO "silika" VALUES(20,88,'20:88','Ni ŵakopochesye (Saamiriyyu kuumila pamoto pala kasanamu ka) kang’ombe kakaliji kana chiŵiliŵili chakwanila, kakatendaga mpela kakutwamula. Basi ŵanganyao ni ŵatite: “Aju ni nnungu jwenu soni ni nnungu jwa Musa, gamba kuti (Musa) juliŵalile (niligongo lyakwe jujile kunsosa kwitumbi).”');
INSERT INTO "silika" VALUES(20,89,'20:89','Ana nganayionaga yanti (kasanamuko) nganikauchisyaga maloŵe ŵanganyao (nganikaajangaga), soni nganikakola (machili) gakwapela ilagasyo namuno chikamuchisyo?');
INSERT INTO "silika" VALUES(20,90,'20:90','Sano chisimu Haruna ŵaliji ali ŵasalile kala ŵanganyao yanti: “E ŵandu ŵangu! Chisimu ŵanganyammwe m’bwatanganyisyidwe (chikulupi chenu) ni kalakweka. Sano chisimu M’mbuje gwenu ni (Allah) Jwaukoto wejinji; basi munguyani une (pakun’galagatila Allah), ni nkunde lilamusi lyangu (lyakunnekasya kukagalagatila kasanamuka).”');
INSERT INTO "silika" VALUES(20,91,'20:91','Ŵanganyao ŵatite: “Nganituŵa tulesile kukagalagatila kalakweka ata panandi mpaka awujile kaje kukwetu Musa.”');
INSERT INTO "silika" VALUES(20,92,'20:92','(Ndema jaŵausile Musa) ŵatite: “E mmwe Haruna! Chichi chankanyisye ndema jamwaweni ŵanganyao kuti apotele,”');
INSERT INTO "silika" VALUES(20,93,'20:93','“Kuti nkanguya? Ana mwanyosyisye lilamusi lyangu?”');
INSERT INTO "silika" VALUES(20,94,'20:94','(Haruna) jwatite: “E mmwe mwanache jwa mama! Ngasimungamula ndeu syangu kapena ntwe wangu. Chisimu une najogwepe kuti (migule ni ŵane ni kwaleka ŵane) ntakwika muntite: ‘Ntandisye gaŵanyikana chilikati cha ŵanache ŵa Israila, ni nganin’dindilila maloŵe gangu.”');
INSERT INTO "silika" VALUES(20,95,'20:95','(Musa) jwatite: “Chichi chantendekasyisye yalakweyi mmwe Saamiriyyu?”');
INSERT INTO "silika" VALUES(20,96,'20:96','Jwalakwe jwatite: “Une naiweni yanganaiona ŵanganyao, ninasapwile panandi (litaka lyakuumila) mu sajo sya (hachi ja) Ntenga (Jibulilu), ninaliponyisye (pa moto payaliji ipeto ya ŵandu ŵa Firiauna yayagumbiche kang’ombe), mwanti yalakweyi ni yayambanjile ntima wangu.”');
INSERT INTO "silika" VALUES(20,97,'20:97','(Musa) jwatite: “Basi tyokani, soni chisimu mmwe nkwete (ilagasyo) mu awuno umi (wa duniya) yanti chin’gambeje kuti: ‘Nkangwaya,’ (chim’be ŵajika ŵangam’bandichila ŵandu), soni chisimu nkwete chilanga changaŵa nkuchikasa mmwejo (chakuukulidwa m’malembe ni kuja kulipidwa ku Akhera). Ni munnole nnungu jwenu jum’bele n’di nkwendelechela kun’galagatila, chisimu chitunchome moto, kaneko liwu lyakwe chitukalimwasanye m’mbwani mwakuliululusya.”');
INSERT INTO "silika" VALUES(20,98,'20:98','Chisimu Nnungu jwenu ni Allah, Ajula jwaali pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwele pejo, soni jutenjele pa umanyilisi waachimanya chilichose.');
INSERT INTO "silika" VALUES(20,99,'20:99','Mwele ni mwatukuti pakunsimbila mmwe (Muhammadi صلى الله عليه وسلم) mu abali sya indu yayalongolele. Soni pamasile patumpele mmwe kuumila Kukwetu Chikumbusyo (Qur’an).');
INSERT INTO "silika" VALUES(20,100,'20:100','Jwatachitundumalile chele (Chikumbusyocho), basi chisimu jwalakwe tachitwichila lisiku lya Kiyama nsigo (wasambi).');
INSERT INTO "silika" VALUES(20,101,'20:101','Tachiŵa ŵandamo mwalakwe (mu ilagasyomo). Kaje kusakala nnope ku ŵanganyao nsigo wakutwichila wakwe lisiku lya Kiyama!');
INSERT INTO "silika" VALUES(20,102,'20:102','Lisiku lyachiligombedwa lipenga, ni tuchasonganganya akuleŵa lyele lisikulyo meso gao gali gabuluwu (gamatatale ligongo lyawoga).');
INSERT INTO "silika" VALUES(20,103,'20:103','Tachiŵa ali nkusongonelana chilikati chao (achitiji): “Nganintama (pa duniya) ikaŵeje (moŵa) likumi pe.”');
INSERT INTO "silika" VALUES(20,104,'20:104','Uwwe tukuimanya chenene yatachiŵechetaga, ndema jatachiŵechetaga ŵambone ŵao panyendele (jalunda ni kupimila chenene ndaŵi kuti): “Nganintama ŵanganyammwe (pa duniya) ikaŵeje lyuŵa limo (pakulandanya ni ulewu wamoŵa ga ku Moto).”');
INSERT INTO "silika" VALUES(20,105,'20:105','Soni akum’busya ya matumbi (kuti gachiŵa uli lisiku lya Kiyama)? Jilani: “Ambuje ŵangu tachigasyaga-syaga ni kugaululusya nti chisawu luwundu.”');
INSERT INTO "silika" VALUES(20,106,'20:106','“Nitachilileka (litaka) lili chiŵata chetilisye tyatyatya!”');
INSERT INTO "silika" VALUES(20,107,'20:107','“Ngasilliona palakwepo litiŵi namuno chikwesya.”');
INSERT INTO "silika" VALUES(20,108,'20:108','Lyele lisikulyo tachinkuuya ŵanganyao nkuŵilanga (jwa Allah) mwangali kunsembenduka jwalakwejo, sano maloŵe ni gaachintulalila (Allah) Jwaukoto wejinji, ni ngasimpikana ikaŵeje kusongona ni misindo ja sajo.');
INSERT INTO "silika" VALUES(20,109,'20:109','Lyele lisikulyo ngasichikamuchisya chondechonde (cha jwalijose) ikaŵeje jwapedwile lusa ni (Allah) Jwaukoto wejinji, ni kunonyelwa najo yakuŵecheta yakwe.');
INSERT INTO "silika" VALUES(20,110,'20:110','(Allah) jukumanyilila yaili paujo pao (ya umi auno wa duniya) ni yaili munyuma mwao (ya ku Akhera), sano ŵanganyao nganaŵa ansyungwile Jwalakwe pakummanya chenene (mwajuŵelele).');
INSERT INTO "silika" VALUES(20,111,'20:111','Soni ngope (syosope lyele lisikulyo) sichinteteŵalila (Allah) Jwaumi wandamo, Jwachimilecho (pakuchengeta iwumbe Yakwe), soni tachiŵa jwakwasika kusyene mundu jwaatwichile (nsigo wa) lupuso (pakum’bwanganya Allah ni isanamu).');
INSERT INTO "silika" VALUES(20,112,'20:112','Sano jwaakupanganya masengo gambone kutendaga ali jwakulupilila, basi ngasajogopa kutendedwa lupuso (pakun’jonjechesya yakusakala), namuno kupungusyidwa (malipilo gakwe gambone).');
INSERT INTO "silika" VALUES(20,113,'20:113','Soni iyyoyopeyo tujitulwisye jalakweji kuŵa Qur’an ja Charabu, ni tusalichisye mwalakwemo ya itetelo (yamitundumitundu), kuti ŵanganyao ajogope, kapena kuti jatendekasye (ndaŵi ni katema) kupata chikumbusyo chasambano.');
INSERT INTO "silika" VALUES(20,114,'20:114','Basi atukwiche Allah, Nchimwene Jwakuona. Ningasin’jitendelaga chitema Qur’an (paajisooma) nkanigamalichiche kutuluka kukwenu maloŵe gakwe. Ni m’becheteje kuti: “Ambuje ŵangu! Munyonjechesyani umanyilisi.”');
INSERT INTO "silika" VALUES(20,115,'20:115','Soni pamasile patwapele chilanga Adam kala (chakuti akalya isogosi ya m’chitela chakanyisyidwa) nambo ŵaliŵalile, ni nganituchisimana kukwao chisako chakulimbangana (chakuleka kutenda yaŵakanyisyidweyo).');
INSERT INTO "silika" VALUES(20,116,'20:116','Soni (kumbuchilani) katema katwasalile Achimalaika kuti: “Mwasujudilani Adam.” Basi wosope ni ŵasujudu kusigala Ibulisu; ŵakanile.');
INSERT INTO "silika" VALUES(20,117,'20:117','Basi ni twatite: “E mmwe Adam! Chisimu aju ni mmagongo jwenumwe ni ŵankwenu. Basi akankopokasya mu Mbepo ntakwika mwannagasyiche.”');
INSERT INTO "silika" VALUES(20,118,'20:118','“Chisimu mmwe nkwete (chilanga Kukwetu chanti), ngajinkolaga sala mwalakwemo soni ngam’baga makonope.”');
INSERT INTO "silika" VALUES(20,119,'20:119','“Soni chisimu mmwe ngajinkolaga njota mwalakwemo soni ngachinkolaga chitukuta.”');
INSERT INTO "silika" VALUES(20,120,'20:120','Basi shetani ni ŵansongonele (pakunnyenjelela yakusakala) ŵatite: “E mmwe Adam! Ana nannanjile chitela cha (kuntendekasya kuŵa ŵa) ndamo ni (ŵa) uchimwene wangamala?”');
INSERT INTO "silika" VALUES(20,121,'20:121','Ni ŵalile ŵanaŵaŵili (Adam ni ŵankwao isogosi) kuumila mwalakwe (m’chitelamo), basi ni waonechele kukwao utukani wao, ni ŵatandite kulinyambatika masamba ga mu Mbepo. Sano Adam ŵannyosyisye M’mbujegwe basi ni ŵapotele.');
INSERT INTO "silika" VALUES(20,122,'20:122','Kaneka Ambujegwe ŵansagwile pakumpochela toba jakwe ni kun’jongola.');
INSERT INTO "silika" VALUES(20,123,'20:123','(Allah) jwatite: “Tulukani mwalakwemo ŵanaose chilikati chenu chili chana utinda. Naga chikam’bichilile chongoko chakuumila Kukwangu, basi ŵatakakuyeje chongoko Changucho, ngaja kusokonechela soni ngaja kulagasika.”');
INSERT INTO "silika" VALUES(20,124,'20:124','“Nambo ŵatakanyosye kuleka chikumbusyo Changu, nikuti chisimu ŵalakweo chakakole umi wakanganika (ku duniya), ni tuchajimusya (m’malembe) lisiku lya Kiyama ali ŵangalola.”');
INSERT INTO "silika" VALUES(20,125,'20:125','Tachiti (pakusimonga): “Ambuje ŵangu! Chantiuli kunjimusya (m’malembe) ndili jwangalola, kutendaga naliji jwakulolaga?”');
INSERT INTO "silika" VALUES(20,126,'20:126','(Allah) tachiti: “Iyyoyopeyo (ni yamwatite mmwe, ndema ja) gam’bichilile ma Ȃya Getu basi mwagaliŵalile, iyyoyopeyo soni lelo chin’diŵalidwe.”');
INSERT INTO "silika" VALUES(20,127,'20:127','Soni mwele ni mwatukan’dipilile (pa Kiyama) mundu jwaŵapelenganyisye malile (pakutenda yasambi), ni ngakulupilila ma Ȃya ga M’mbujegwe, sano ilagasyo ya ku Akhera ni yakupoteka nnope soni yakwendelechela.');
INSERT INTO "silika" VALUES(20,128,'20:128','Ana nganiyimanyukuchepe kukwao yanti mikutula jilingwa jatwajijonasile paujo pao? Soni ŵanganyaŵa (achimakafili ŵaleloŵa) akwendaga muŵatamaga ŵanganyao (ana ngaakuionaga imanyilo yakonanjidwa kwao)? Chisimu mu yalakweyo mwana ilosyo kwa ŵandu ŵakwete lunda.');
INSERT INTO "silika" VALUES(20,129,'20:129','Mwanti likaŵaga liloŵe lyalyalongolele lyakuumila kwa Ambuje ŵenu (lyangapa ilagasyo yekulungwa nnope ikaŵeje ku Kiyama), ni ndema (jao jakuwila kuti jaliji) jetomechedwe, chisimu (ilagasyo yao) ikaliji yakusosekwa (kuti yapate sampanojino).');
INSERT INTO "silika" VALUES(20,130,'20:130','Basi pililani (mmwe Muhammadi صلى الله عليه وسلم) pa yaakuŵecheta ŵanganyao, ni nlluswejesyeje Lumbili lwa Ambuje ŵenu lyuŵa nkanilikopoche (pakuswali swala ja Fajr), nambo soni nkanilitiŵile (pakuswali swala ja Asr), nambo soni ndaŵi sine syachilo basi nswejesyeje (pakuswali swala ja Magrib ni Isha), kwisa soni pasonga sya muusi (katikati ja muusi pakuswali swala ja Dhuhr), kuti tim’be ŵakusengwa (ni malipilo gatachimpa Allah pa Kiyama).');
INSERT INTO "silika" VALUES(20,131,'20:131','Soni ngasinyyikolondochesya meso genu yatujisengwachisye nayo mikutula jakulekanganalekangana ja ŵanganyao (achimakafilio), (yalakweyo iŵele) ipeto ya umi wa duniya, kuti twalinje ŵanganyao pa yalakweyo. Sano lisiki lya Ambuje ŵenu (ku Akhera) ni lyalili lyambone nnope soni lyakwendelechela.');
INSERT INTO "silika" VALUES(20,132,'20:132','Soni nllichisyeje liŵasa lyenu kuswali Swala, ni mpililile mmwejo pa (kujiswaliswali) jalakwe. Ngatukum’benda mmwe lisiki, Uwwe ni ŵatukumpa lisiki mmwe. Soni mbesi jambone jili mu woga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(20,133,'20:133','Soni (achimakafili) ŵatite: “Nambi uli ngajukutuichilila (Muhammadi صلى الله عليه وسلم) ni chisimosimo kuumila kwa M’mbujegwe?” Ana nganiwaichilila umboni wakuonechela wa ayila yaili mu ikalakala yandanda (Taurat ni Injil yakwika kwa Ntume Muhammadi صلى الله عليه وسلم)?');
INSERT INTO "silika" VALUES(20,134,'20:134','Soni ingaŵe kuti Uwwe twajonasile ŵanganyao ni ilagasyo nkanaiche jwalakwe (Muhammadi صلى الله عليه وسلم), angatite: “Ambuje ŵetu! Uli ngatutumichisya kaje ntenga kuti tukuye ma Ȃya Genu, nkanituiche mwakunyosyeka (chanti iyyi) ni kwaluka?”');
INSERT INTO "silika" VALUES(20,135,'20:135','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Jwalijose (mwetuwe) jukwembecheya (malipilo gakwe), basi nombe jenumanja jembecheyani, mwanti sampano tiyyimanye yanti ana ŵani (chilikati chetuwe ni jenumanja) ŵali petala lyagoloka, soni kuti ana ŵani ŵali ŵakongoka.”');
INSERT INTO "silika" VALUES(21,1,'21:1','Chiŵandichile ku ŵandu chiŵalanjilo chao (chakwaŵalanga pa Kiyama), kutendaga ŵanganyao ali nkulilechelela ni kunyosya.');
INSERT INTO "silika" VALUES(21,2,'21:2','Ngachikwaichililaga chikumbusyo chilichose chasambano chakuumila kwa M’mbuje gwao, ikaŵeje kuti akasachipikanilaga aku alinkutenda chanache.');
INSERT INTO "silika" VALUES(21,3,'21:3','Mitima jao jichinyosyelaga, ni kusongonelanaga mwaasili aŵala ŵalitendele lupuso kuti: “Ana aju (Muhammadi صلى الله عليه وسلم) ninduni, pangaŵaga mundu mpele jenumanja? Ana nkuuŵichilila usaŵi kutendaga jenumanja n’di nkulola?”');
INSERT INTO "silika" VALUES(21,4,'21:4','Jwalakwe (Muhammadi صلى الله عليه وسلم) jwatite: “Ambuje ŵangu akumanyilila liloŵe (lililyose lyalikuŵechetedwa) kumawunde ni petaka, soni Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(21,5,'21:5','Nambo ŵanganyao ŵatite: “(Ayi yaakuŵecheta Muhammadi صلى الله عليه وسلم iŵele) sagamisi syewanganyichilane.” (Ndaŵi jine akutiji): “Nambo jute kuilukanya (nsyene).” (Pane ni akutiji): “Nambo jwalakweju nkwimba ilaanga, basi ayiche nacho kukwetu chisimosimo mpela yaŵatite pakutumisyilidwa (achimitume) ŵandanda.”');
INSERT INTO "silika" VALUES(21,6,'21:6','Nganiukulupilileje paujo pao musi uliose m’misi jatwajijonasile (namuno kuti twajitumichisye isimosimo), ana ŵanganyao ni takulupilile?');
INSERT INTO "silika" VALUES(21,7,'21:7','Ni nganitutumisyeje paujo penu (mmwe Muhammadi صلى الله عليه وسلم) ikaŵeje ŵandu (ngaŵaga malaika) ŵatwaliji nkwajuwulila (maloŵe), basi mwawusyani achimisyene lukumbukumbu (lwaindu yakala), nam’baga jenumanja ngankumanyilila.');
INSERT INTO "silika" VALUES(21,8,'21:8','Soni nganitwatenda (achimitumeo) kuŵa ilu yangalya yakulya, nambo soni nganaŵa ŵandamo.');
INSERT INTO "silika" VALUES(21,9,'21:9','Kaneko twasimichisye chilanga chao (chakwakamuchisya kwa amagongo ŵao), basi ni twajokwele (achimitumeo) ni ŵatwasachile (mwa akwakuya ŵao), ni twajonasile ŵakupelenganya malile (pasambi).');
INSERT INTO "silika" VALUES(21,10,'21:10','Chisimu pamasile patuntuluchisye ŵanganyammwe Chitabu (Qur’an), mwalakwemo mwana chikumbusyo chenu, ana ŵangali lunda?');
INSERT INTO "silika" VALUES(21,11,'21:11','Ana misi jilingwa ja twajijonasile jajaliji nkutenda lupuso, ni twagumbile panyuma pao ŵandu ŵane!');
INSERT INTO "silika" VALUES(21,12,'21:12','Basi ndema jaŵaiweni ipotesi Yetu (kuti ikwika), jele ndemajo ŵanganyao ŵalinjilile kuitila.');
INSERT INTO "silika" VALUES(21,13,'21:13','Nkatila, nambo m’bujile ku ayila (yasambi) yamwaliji nkusengwa nayo, ni m’malo mwenu, kuti m’buusyidwe (yachindimba champele Allah).');
INSERT INTO "silika" VALUES(21,14,'21:14','Ŵanganyao ŵatite (pagumila): “Ipotesi yetuwe! Chisimu uwwe twaliji ŵalupuso.”');
INSERT INTO "silika" VALUES(21,15,'21:15','Ni nganikuleka gumila kwaoko mpaka twatesile chisawu n’gunda wegungule, (soni mpaka) ŵalitile (mwaukasalitilaga moto, ŵawile).');
INSERT INTO "silika" VALUES(21,16,'21:16','Soni Uwwe nganitugumba mawunde ni mataka kwisa soni yaili chilikati chakwe mwamasanje.');
INSERT INTO "silika" VALUES(21,17,'21:17','Tungasache kulitendela chakunguluchila (mpela ŵakongwe ni mwanache), tungalitendele chalakwecho kuumila Kukwetu (mpela achakongwe ŵaku Mbepo kapena achimalaika) tungaŵe ŵakuti tutende (yalakweyo).');
INSERT INTO "silika" VALUES(21,18,'21:18','Nambo tukasachiponyaga chakuona pa chaunami ni kuchikasa wongo, basi pajele ndemajo chalakwe (chaunamicho) chikasatyoka. Soni ipotesi ni yenu ligongo lya yankusimbayo (yakunnambuchisya Allah kuti akwete ŵakongwe ni mwanache).');
INSERT INTO "silika" VALUES(21,19,'21:19','Soni aŵele Ŵakwe (Allah) wosope ŵali kumawunde ni petaka, soni ŵali Kukwakwe (Achimalaika), ŵangalikwesya kuleka kun’galagatila soni ŵangapela (ni ibada).');
INSERT INTO "silika" VALUES(21,20,'21:20','Akunswejesyaga chilo ni muusi ŵangatondoŵela.');
INSERT INTO "silika" VALUES(21,21,'21:21','Kapena (achimakafilio) alitendele milungu kuumila mwitaka, jalakwejo ni jikwimusya (ŵandu ŵawe)?');
INSERT INTO "silika" VALUES(21,22,'21:22','Ingaŵe mwalakwemo (kwinani ni pasi) mwana milungu jine jwangaŵaga Allah; mukajonasiche. Basi aswejele Allah M’mbuje jwa Arishi (Chindanda Chaulungu) ku (yakusakala) yaakusimba (achimakafili yakwayana ni Jwalakwe).');
INSERT INTO "silika" VALUES(21,23,'21:23','Jwalakwe jwangaŵa nkuwusyidwa yajukutenda, nambo ŵanganyao ni ŵatachiwusyidwa.');
INSERT INTO "silika" VALUES(21,24,'21:24','Kapena ŵanganyao alitendele milungu jinepe jwangaŵaga Jwalakwe? Jilani: “Ikani nao umboni wenu (payankuŵechetayo). Achi ni chikumbusyo kwa ŵandinao (jajili Qur’an mwangalimo yankuŵechetayo), nambo soni chikumbusyo kwa ŵa m’bujo mwangu (jajili Taurat, Zabur kwisa soni Injil mwangali soni yankuŵechetayo).” Nambo kuti ŵajinji mwa ŵanganyao ngaakuchimanya chakuona, basi ŵanganyao ni akuŵa ŵakunyosya.');
INSERT INTO "silika" VALUES(21,25,'21:25','Soni nganitutumisyeje ntenga jwalijose paujo penu (mmwe Muhammadi صلى الله عليه وسلم) ikaŵeje kuti twan’juwulilaga yanti: “Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Une pe, basi ngalagatilani.”');
INSERT INTO "silika" VALUES(21,26,'21:26','Soni ŵanganyao ŵatite: “(Allah) Jwaukoto wejinji alitendele mwanache.” Kuswejela ni Kwakwe. Nambo (aŵala ŵakwagalagatila mpela Achimalaika, Isa ni Uzair aŵele) achikapolo ŵakuchimbichikwa.');
INSERT INTO "silika" VALUES(21,27,'21:27','Ŵanganyao ŵangannongolela Jwalakwe (Allah) kuŵecheta, ni akupanganyaga (chilichose) kwa lilamusi Lyakwe.');
INSERT INTO "silika" VALUES(21,28,'21:28','Jwalakwe jukuimanya yaili paujo pao ni yamunyuma mwao, soni ŵanganyao ŵangachondelela (pajwalijose) ikaŵeje pamundu jwannonyele (Allah), soni ŵanganyao akuŵa ŵakuligandasya ligongo lyakun’jogopa Jwalakwe.');
INSERT INTO "silika" VALUES(21,29,'21:29','Sano jwataŵechete mwa ŵanganyao kuti: “Chisimu une ni nnungu ngaŵaga Jwalakwe (Allah).” Basi jwalakwe chitun’dipile Jahannama, iyyoyopeyo ni yatukasatiji pakwalipila ŵalupuso.');
INSERT INTO "silika" VALUES(21,30,'21:30','Ana nganaiona aŵala achimakafili yanti mawunde ni mataka galiji genyambatikane, basi ni twaganyambatukulenye? Soni tugumbile kuumila m’mesi chaumi chilichose. Ana ngakukulupilila?');
INSERT INTO "silika" VALUES(21,31,'21:31','Ni tuŵisile petaka yakunyichisya (gagali matumbi gausito), kuti litakwika mulyagwedanyichile ŵanganyao (litakalyo), nituŵisile soni palakwepo matala gakusapanguka kuti ŵanganyao alijongolele (achimisyene kwakuja).');
INSERT INTO "silika" VALUES(21,32,'21:32','Soni tuliŵisile liwunde kuŵa nsakasa wagosedwa, sano ŵanganyao akuitundumalila ilosyo yakwe (mpela lyuŵa mwesi ni ndondwa pangaichetelela).');
INSERT INTO "silika" VALUES(21,33,'21:33','Soni Jwalakwe ni Ajula juŵagumbile chilo ni muusi, kwisa soni lyuŵa ni mwesi, chilichose ching’ambila mwitala lyasyungulilo.');
INSERT INTO "silika" VALUES(21,34,'21:34','Soni nganitum’bicheje mundu jwalijose paujo penu (mmwe Muhammadi صلى الله عليه وسلم) kuŵa jwandamo, ana naga mmwe kuwa, ŵanganyao nichaŵe ŵandamo?');
INSERT INTO "silika" VALUES(21,35,'21:35','Ntima uliose uchipasya chiwa, ni tukun’dingaga ni yakusakala nambo soni ni yambone kuŵa mayeso (gakun’dinjila kupilila kwenu ni kutogolela kwenu), soni Kukwetu tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(21,36,'21:36','Soni pati am’bweni mmwe (Muhammadi صلى الله عليه وسلم) aŵala ŵakanile, ngaakuntendelaga inepe ikaŵeje chipongwe (pakuŵecheta kuti): “Ana aju ni jwaakwakolanga achimilungu ŵenu (mwachalu)?” Kutendaga ŵanganyao yakunkolanga (Allah) Jwaukoto wejinji ŵanyao akukana.');
INSERT INTO "silika" VALUES(21,37,'21:37','Mundu jugumbidwe kuŵa jwakupupuluma. Sampano chinannosye ŵanganyammwe ilosyo Yangu (yakaiche ka ipotesi), basi ngasimunyanguya.');
INSERT INTO "silika" VALUES(21,38,'21:38','(Achimakafili) ni akasatiji: “Ana chele chilangachi chiyiche chakachi nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(21,39,'21:39','Ingaŵe kuti akumanyilila aŵala ŵakanile ndema jatachitenda ngauchiga Moto kungope kwao, namuno kunyuma kwao (nganganaŵecheta yalakweyo), soni ŵanganyao ngasaŵa ŵakamuchisyidwa.');
INSERT INTO "silika" VALUES(21,40,'21:40','Nambo (Motowo) uchaichilila mwachisupuchisya ni uchamasya lunda, basi ngasakombola kuuwusya, nambo soni ŵanganyao ngasapedwa lipesa.');
INSERT INTO "silika" VALUES(21,41,'21:41','Soni chisimu chene achimitenga (ŵajinji) ŵatendedwe chipongwe paujo penu (mmwe Muhammadi صلى الله عليه وسلم), nambo (mbesi jakwe) aŵala ŵaŵaliji nkutenda chipongwe mwa jamanjao, yasyungwile (ipato) yaŵaliji nkuitendela chipongwe.');
INSERT INTO "silika" VALUES(21,42,'21:42','Jilani: “Ana nduni jwampaka an’gose jenumanja chilo ni muusi ku (ilagasyo ya Allah) Jwaukoto wejinji?” Nambo ŵanganyao akuŵa ŵakukutundumalila kunkumbuchila M’mbuje gwao.');
INSERT INTO "silika" VALUES(21,43,'21:43','Kapena akwete ŵanganyao milungu jakwasiŵilila (kuilagasyo) ŵangaŵaga Uwwe? Jalakwe (milungu jaojo), jangaŵa nkupakombola kulikamuchisya jisyene (nimpaka jikombole kwakamuchisya ŵane)? Soni jalakwejo nganijiŵa jigosedwe Kukwetu.');
INSERT INTO "silika" VALUES(21,44,'21:44','Nambo kuti twasengwasisye ŵanganyao ni achatati ŵao mpaka jalewipe kukwao ndaŵi (jaumi wao). Ana ngaakuiona yanti Uwwe tukuliyichilila litaka ni kulinandiyaga mpika wakwe (wa ulamusi wao waukafili)? Ana ŵanganyao ni ŵataŵe ŵakupunda?');
INSERT INTO "silika" VALUES(21,45,'21:45','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une nguntetela ŵanganyammwe ni maloŵe (ga Allah).” Nambo asiiwa nganaŵa apikene m’bilango, (namuno) achiteteledwaga.');
INSERT INTO "silika" VALUES(21,46,'21:46','Nambo kalikatite kaakwaye kambungo kaipotesi ya Ambuje ŵenu, chisimu chene mpaka ajile: “Ipotesi yetuwe! Chisimu uwwe twaliji ŵaalitenda lupuso.”');
INSERT INTO "silika" VALUES(21,47,'21:47','Ni tuchiŵika masikelo gachilungamiko pa lisiku lya Kiyama, basi mundu jwalijose ngasatendedwa lupuso pachilichose, natamuno (chinducho) chichiŵe chausito wati mpela lupele lwa mbumbi (tanaposi), tuchiika nacho; soni Uwwe tukwanila kuŵa Akuŵalanjila.');
INSERT INTO "silika" VALUES(21,48,'21:48','Soni chisimu chene Uwwe twampele Musa ni Haruna (Chitabu), chakulekanganya (chilikati cha yakuona ni yaunami, chachaliji) soni lumuli ni chikumbusyo kwa ŵandu ŵawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(21,49,'21:49','Aŵala ŵaakun’jogopa M’mbuje gwao mwangali kum’bona, soni ŵanyao Kiyama akasajijogopaga.');
INSERT INTO "silika" VALUES(21,50,'21:50','Sano aji (Qur’an) ni chikumbusyo chaupile chatuchitulwisye; ana jenumanja chalakwechi ni nkuchikana?');
INSERT INTO "silika" VALUES(21,51,'21:51','Soni chisimu chene Uwwe twampele Ibrahima chongoko chakwe kala (kuuchanda), nitwaliji nkummanya chenene.');
INSERT INTO "silika" VALUES(21,52,'21:52','Pandema jaŵaŵechete kwa babagwe ni ŵandu ŵakwe kuti: “Ana niyapi ayi isanamu yankuŵa jenumanja nchijendelechelaga kuigalagatila?”');
INSERT INTO "silika" VALUES(21,53,'21:53','Ŵanganyao ŵatite: “Twasimene achatati ŵetu achiigalagatilaga.”');
INSERT INTO "silika" VALUES(21,54,'21:54','Jwalakwe jwatite: “Chisimu chene jenumanja ni achatati ŵenu m’bele mukusokonechela kwakuonechela.”');
INSERT INTO "silika" VALUES(21,55,'21:55','Ŵanganyao ŵatite: “Ana ntuŵichilile niyakuona kapena n’di mwa akutenda chanache?”');
INSERT INTO "silika" VALUES(21,56,'21:56','Jwalakwe jwatite: “(Ngwamba), nambo m’mbuje gwenu ni M’mbuje jwa kumawunde ni petaka Ajula juŵaigumbile yalakweyo, soni une payalakweyo ndili mwa akutendela umboni.”');
INSERT INTO "silika" VALUES(21,57,'21:57','“Soni ngunnumbila Allah! Chisimu une chindende malindi (gakuijonanga) isanamu yenu panyuma pagalauka ni kulosya migongo jenu (kwaulaga kwakuja kwenu).”');
INSERT INTO "silika" VALUES(21,58,'21:58','Basi ni ŵaitesile (isanamu ila) kuŵa ipande-ipande ni kusigasya chachikulungwa chakwe, kuti ŵanganyao awujile ku chalakwecho.');
INSERT INTO "silika" VALUES(21,59,'21:59','Ŵanganyao ŵatite: “Ana nduni jwatesile yalakweyi ku milungu jetu? Chisimu jwalakwejo juŵele mwa akutenda lupuso kusyene.”');
INSERT INTO "silika" VALUES(21,60,'21:60','(Ŵane) ŵatite: “Tumpikene nchanda jwinejwakwe juli nkwapakana ŵanganyao, jukuŵilanjikwa Ibrahima.”');
INSERT INTO "silika" VALUES(21,61,'21:61','Ŵanganyao ŵatite: “Ikani najo pameso pa ŵandu kuti atendele umboni.”');
INSERT INTO "silika" VALUES(21,62,'21:62','Ŵanganyao ŵatite: “Ana ni mmwe ŵantesile yalakweyi ku milungu jetu, E mmwe Ibrahima?”');
INSERT INTO "silika" VALUES(21,63,'21:63','Jwalakwe jwatite: “(Ngaŵa une), nambo aju jwankulungwa jwaoju ni jwatesile yalakweyi, basi mwawusyani ŵanganyaŵa naŵaga ŵakuŵechetaga.”');
INSERT INTO "silika" VALUES(21,64,'21:64','Basi ŵagalauchilene achimisyene niŵatite: “Chisimu ŵanganyammwe ni ŵan’di ŵalupuso (pangajigosa milunguji).”');
INSERT INTO "silika" VALUES(21,65,'21:65','Kaneko ŵajinamisye mitwe jao (nikuujila kunganisyo syao syachijinga) ni ŵatite: “Chisimu chene mmwe nkumanyilila yanti ŵanganyaŵa nganaŵa aŵechete (uli kututenda chipongwe chanti iyyo)?”');
INSERT INTO "silika" VALUES(21,66,'21:66','Jwalakwe jwatite: “Ana ni nkuigalagatila ichindu yangaŵa nkunkamuchisya chilichose kunneka Allah, soni yangaŵa nkumpa ilagasyo?”');
INSERT INTO "silika" VALUES(21,67,'21:67','“Kunyosyeka ni kwenu pamo ni ayila yankuigalagatila kunneka Allah, ana ŵangali lunda?”');
INSERT INTO "silika" VALUES(21,68,'21:68','Ŵanganyao ŵatite: “Munchomani moto, ni nkulupusye milungu jenu, naga jenumanja nkusaka kutenda (chitendo basi tendani yele).”');
INSERT INTO "silika" VALUES(21,69,'21:69','Uwwe twatite: “E mmwe amoto! Um’be ŵakusisima ni ŵantendele kwa Ibrahima.”');
INSERT INTO "silika" VALUES(21,70,'21:70','Ni ŵansosele jwalakwe yakusakala, nambo Uwwe twatesile ŵanganyao kuŵa ŵakwasika kusyene.');
INSERT INTO "silika" VALUES(21,71,'21:71','Ni twan’jokwele jwalakwe ni Lutwi (ni kwapeleka) ku chilambo achila (cha Shami) chatwachipele upile ligongo lya iwumbe (yakutama mwalakwemo).');
INSERT INTO "silika" VALUES(21,72,'21:72','Ni twampele jwalakwe Isihaka, ni ntuka wakonjechesya (chachili chisukulu chakwe) Yaakuubu, soni wosope ŵene twatesile kuŵa ŵambone.');
INSERT INTO "silika" VALUES(21,73,'21:73','Ni twatesile kuŵa achinnongola, ŵaŵaliji nkwajongola (ŵandu) ni ulamusi Ŵetu, ni twajuwulile ŵanganyao yakupanganya yambone, ni kwimika Swala mwakolosya, ni kutola Zaka, nambo soni ŵaliji nkutugalagatila Uwwe pe.');
INSERT INTO "silika" VALUES(21,74,'21:74','Nambo soni Lutwi twampele lunda lwakusokoka kwisa soni umanyilisi, nitwan’jokwele ku Musi wawaliji nkutenda yausakwa, chisimu ŵanganyao ŵaliji ŵandu ŵakusakala, ŵakunyosya.');
INSERT INTO "silika" VALUES(21,75,'21:75','Nitwan’jinjisye jwalakwe mu ukoto Ŵetu, chisimu jwalakwe jwaliji muŵandu ŵambone.');
INSERT INTO "silika" VALUES(21,76,'21:76','Soni (munkumbuchilani) Nuhu, ndema ja jwaŵilasile kalakala (pakutuŵenda), basi Uwwe ni twan’jitiche, soni ni twan’jokwele jwalakwe ni ŵapeŵasa ŵakwe ku taabu jekulungwa.');
INSERT INTO "silika" VALUES(21,77,'21:77','Ni twanchenjele kuŵandu ŵaŵaliji nkukanila ma Ȃya Getu, chisimu ŵanganyao ŵaliji ŵandu ŵakusakala. Basi ni twamisisye wosope ŵene.');
INSERT INTO "silika" VALUES(21,78,'21:78','Soni (munkumbuchilani) Daudi ni Sulaimana, pandema jaŵaliji nkwilanya magambo ga n’gunda. Ndema jasyalilemo chilo mbusi sya ŵandu ŵane. Sano Uwwe nitwaliji Mboni pachiilanyo chaocho.');
INSERT INTO "silika" VALUES(21,79,'21:79','Ni twantesile Sulaimana kuŵa jwagamanya chenene (magambogo kajilanye kakwe kwapunda babagwe), sano jwalijose mwa ŵanganyao twampele chiilanyo chambone ni umanyilisi. Ni twagajepepesye matumbi ni ijuni kuti iŵeje inankunswejesya (Allah) pamo ni Daudi, ni twalijitu Uwwe Akutenda (yosope yeneyi).');
INSERT INTO "silika" VALUES(21,80,'21:80','Ni twan’jiganyisye (Daudi) kapanganye ka iwaalo yachisyano (yapangondo) ligongo lya jenumanja, kuti iŵe inankun’gosa pa kumenyana kwenu, ana jenumanja n’di ŵakutogolela?');
INSERT INTO "silika" VALUES(21,81,'21:81','Sano nombe najo Sulaimana twan’jepepachisye mbungo jachimbunga, jajaliji nkwenda kwa ulamusi wakwe kuja kuchilambo chatwachipele upile (chilambo cha Shami), nitwalijitu Uwwe pachindu chilichose Ŵachimanya chenene.');
INSERT INTO "silika" VALUES(21,82,'21:82','Nambo soni m’mashetani mwaliji mwana ŵaŵaliji nkuntiŵilila (Sulaimanajo m’mbwani kuja kunsosela ngalekale), ni ŵaliji nkupanganya soni masengo gane gangaŵaga gele (mpela yakutaŵataŵa). Ni twaliji Uwwe tuli nkwalonda ŵanganyao.');
INSERT INTO "silika" VALUES(21,83,'21:83','Soni (munkumbuchilani) Ayyubu, ndema jajwaŵilasile Ambujegwe (pakwaŵenda) kuti: “Chisimu une gangwayiye masausyo, sano Mmwe ni Ŵachanasa channope kwapunda ŵachanasa wosope.”');
INSERT INTO "silika" VALUES(21,84,'21:84','Basi ni twan’jitiche, ni twantyochesye masausyo gajwaliji nago, ni twampele ŵapeŵasa ŵakwe, ni konjechesya soni paŵanganyao ŵane ŵanti mpela ŵawowo, (twantendele yosope yeneyi kuŵa) ukoto wakuumila Kukwetu, ni (kuti chiŵe) chikumbusyo kwa akutenda ibada.');
INSERT INTO "silika" VALUES(21,85,'21:85','Soni (munkumbuchilani) Ismaila, Idrisa ni Dhul-Kifli (Yesaya), wosope ŵeneŵa ŵaliji mwa akupilila.');
INSERT INTO "silika" VALUES(21,86,'21:86','Soni twajinjisye mu ukoto Ŵetu, chisimu ŵanganyao ŵaliji mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(21,87,'21:87','Soni (munkumbuchilani) Dhun-Nun (Yunusu), ndema jajwauŵigwisye ulendo juli jugasabiche, basi ni jwaganichisyaga yanti Uwwe ngatumpa ipotesi iliyose, ni jwagumisile m’chipi (cha m’matumbo mwasomba jajammisile) juchitiji: “Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Mmwe, kuswejela ni Kwenu. Chisimu une mbele mwa akulitenda lupuso.”');
INSERT INTO "silika" VALUES(21,88,'21:88','Basi ni twan’jitiche, ni twan’jokwele kumadandausi, soni iyyoyopeyo ni yatukasatiji pakwajokola ŵakulupilila.');
INSERT INTO "silika" VALUES(21,89,'21:89','Soni (munkumbuchilani) Zakariya, ndema jajwaŵilasile Ambujegwe (pakwaŵenda) kuti: “Ambuje ŵangu! Ngasimuneka jikape (jwangali mwanache), soni Mmwe ni Ŵambone nnope kwapunda ŵawinjila wosope.”');
INSERT INTO "silika" VALUES(21,90,'21:90','Basi ni twan’jitiche, ni twampele Yahaya, ni twankolochesye soni ŵankwakwe (kuti aŵisikale). Chisimu ŵanganyao ŵaliji ŵachitema pakutenda yambone, soni ŵaliji nkutuŵenda mwakusachilila umbone nambo soni mwawoga, ni ŵaliji soni ŵakulinandiya Kukwetu.');
INSERT INTO "silika" VALUES(21,91,'21:91','Soni (munkumbuchilani Maryam) ajula jwaŵaugosile utukani wakwe, ni twapepelele mwa jwalakwe (umi) kuumila kwa Nsimu Ŵetu (Jibulilu ni kupagwa Isa jwangali babagwe), ni twantesile jwalakwe ni mwanagwe kuŵa chilosyo kwa iwumbe yosope.');
INSERT INTO "silika" VALUES(21,92,'21:92','Chisimu awu nkutula wenu (wa Usilamu), ni nkutula umpepe, soni Une ni M’mbuje gwenu, basi mungalagatilani Une (Jikape).');
INSERT INTO "silika" VALUES(21,93,'21:93','Ni ŵatindene ŵanganyao (ŵandu ŵakwakuya achimitume) yakwayana ni dini jao (ni kuŵa mikutula-mikutula). Wosope ŵeneo Kukwetu tachiujila.');
INSERT INTO "silika" VALUES(21,94,'21:94','Sano jwachatende itendo yambone kutendaga juli jwakulupilila, nikuti masengo gakwego ngasigakanidwa, soni chisimu Uwwe tukunnembela jwalakwe (masengo gakwego m’chitabu mwakwe).');
INSERT INTO "silika" VALUES(21,95,'21:95','Soni ikanyisyidwe ku (ŵandu ŵa) musi uliose watwaujonasile kuti ŵanganyao nganaŵa awujiile (ku duniya Kiyama nkanijiyiche).');
INSERT INTO "silika" VALUES(21,96,'21:96','Kwikanila ndema jatachiugulidwa Yaajuju ni Maajuju, soni ŵanganyao tachiŵa ali nkutuluka mwakangamala pachikwesya chilichose (wakukuwatanganya).');
INSERT INTO "silika" VALUES(21,97,'21:97','Ni chichiŵandichila chilanga chakuona (Kiyama), basi pajele ndemajo ichiŵa yanti meso ga aŵala ŵaŵakanile gachikolondoka aku achitiji: “Ipotesi yetuwe! Chisimu uwwe twaliji nkuikwanjila yalakweyi, nambo soni twaliji ŵaalitenda lupuso.”');
INSERT INTO "silika" VALUES(21,98,'21:98','Chisimu ŵanganyammwe ni ayila yankuigalagatila kunneka Allah tinchiŵa sasu sya ku Jahannama, ŵanganyammwe jalakwejo tinchijijinjila. ');
INSERT INTO "silika" VALUES(21,99,'21:99','Ingaŵe yalakwe (isanamuyo) kuti ili milungu (jisyesyene) nga nganiyija kuwujinjila (Motowo), soni wosope ŵene (isanamu ni akuigalagatila ŵakwe) takaŵe ŵandamo mwalakwemo.');
INSERT INTO "silika" VALUES(21,100,'21:100','Tachikola ŵanganyao mwalakwemo koloma, soni ŵanganyao mwalakwemo ngasajaga kupikana (tachiŵa asiiwa). ');
INSERT INTO "silika" VALUES(21,101,'21:101','Chisimu aŵala ŵawalongolele umbone ku ŵanganyao wakuumila Kukwetu (mpela Isa, Maryam ni Uzair), ŵanyao tachitalichisyidwa ni walakweo (Moto).');
INSERT INTO "silika" VALUES(21,102,'21:102','Ngaja kupikana kulilima kwakwe kwalwapasipasi (kwa Motowo), soni ŵanyao takaŵe ŵandamo mu ichindu yajisachile mitima jao.');
INSERT INTO "silika" VALUES(21,103,'21:103','Ngasichadandaulikasya ŵanganyao chogoyo chekulungwa (cha lisiku lya Kiyama), ni tachachingamila Achimalaika (achasalilaga kuti): “Ali ni lisiku lyenu alila lyamwaliji nkupedwa chilanga (chakwika kwakwe).”');
INSERT INTO "silika" VALUES(21,104,'21:104','Lisiku lyatuchiŵiliga liwunde chisawu yaikasatiji kaŵilije ikalakala ilembo, mpela yatwatite katande gumba kwandanda tuchikuwilisya soni. (Chalakwechi ni) chilanga chachili Petuwe. Chisimu Uwwe tuŵele ŵakutenda (yalakweyo).');
INSERT INTO "silika" VALUES(21,105,'21:105','Soni chisimu Uwwe twalembile mu Zabur panyuma pakulemba mu Chikumbusyo (Al-Lauh Al-Mahfûz) yanti litaka tachilitawalila achikapolo Ŵangu ŵambone.');
INSERT INTO "silika" VALUES(21,106,'21:106','Chisimu mu aji (Qur’an) mwana utenga wakupikanika kwaŵandu ŵagalagatila (Allah).');
INSERT INTO "silika" VALUES(21,107,'21:107','Ni nganituntumisya mmwe (Muhammadi صلى الله عليه وسلم) ikaŵeje kuŵa ukoto ku iwumbe yosope.');
INSERT INTO "silika" VALUES(21,108,'21:108','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu ikujuwulidwa kukwangu yanti nnungu jwenu (jwakuŵajilwa kun’galagatila) ni Nnungu Jumo. Ana jenumanja chim’be ŵakulipeleka (pakuŵa Asilamu ŵakun’galagatila Jwele pejo)?”');
INSERT INTO "silika" VALUES(21,109,'21:109','Sano naga akugalauka (kuleka Usilamu), basi jilani: “Nannaaŵiile m’myala pakulungusya utenga mwakulandana (mwangali lusagu), soni ngangumanyilila kuti yankupedwa chilangayo (ipotesi kapena Kiyama) ili dusi kapena kwanaula.”');
INSERT INTO "silika" VALUES(21,110,'21:110','“Chisimu Jwalakwe (Allah) jukumanyilila maloŵe gakunyanyika, ni jukumanyilila soni yankusisa.”');
INSERT INTO "silika" VALUES(21,111,'21:111','“Soni ngangumanyilila, mwine kwele (kunchelewachisya ipotesiko) kuŵele mayeso gakun’dinga ŵanganyammwe, ni kusengwa kwandema jannono.”');
INSERT INTO "silika" VALUES(21,112,'21:112','(Muhammadi صلى الله عليه وسلم) jwatite: “Ambuje ŵangu! Jilanyani mwakuonaonape, soni M’mbuje gwetu ni (Allah) Jwaukoto wejinji, (soni Jwalakwe ni) Nkuŵendedwa chikamuchisyo pa yankusimbayo.”');
INSERT INTO "silika" VALUES(22,1,'22:1','E jenumanja ŵandu! Mun’jogopani M’mbuje gwenu, chisimu chisikinya cha Kiyama chiŵele chindu chekulungwa kusyene.');
INSERT INTO "silika" VALUES(22,2,'22:2','Lisiku lyatinchijiona ŵanganyammwe (Kiyamajo), tachiliŵalila nkonjesya jwalijose chaŵaliji nkuchijonjesya, soni jwalijose juŵakwete nsigo tachijasanga nsigo wakwe, ni tinchaona ŵandu nti chisawu ŵakolelwe, kutendaga ŵanganyao nganaŵa ŵakolelwe, nambo kuti ipotesi ya Allah ni yaili yaukali (yayatendekasyisye yalakweyo).');
INSERT INTO "silika" VALUES(22,3,'22:3','Soni chilikati cha ŵandu pana ŵaakukanganaga pa ya Allah mwangali umanyilisi, ni akunkuyaga shetani jwalijose jwachibuli nnope.');
INSERT INTO "silika" VALUES(22,4,'22:4','Ilembedwe pa jwalakwe (shetanijo) yanti jwatantende jwalakwejo kuŵa nsyoŵe, basi chisimu jwalakwejo chansochelesye, ni kunnongolela kuilagasyo ya Moto Wakulapuka.');
INSERT INTO "silika" VALUES(22,5,'22:5','E jenumanja ŵandu! Naga n’di ŵakaika pa yakwimuka m’malembe, basi chisimu Uwwe twan’gumbile (pandanda) kuumila mwitaka, kaneko (tukun’gumbaga) kuumila mu lindondwa (lya ulume ni ukongwe), kaneko (ni likusyukaga) chipande chamyasi, kaneko (ni chikusyukaga) chipande channou chegumbiche (kuŵa mwanache jwankwane iŵalo) kapena changagumbika (mbundumuko), kuti tuntagulile jenumanja (ya ukombosi Ŵetu wakutenda chilichose chatukusaka). Ni tukasantamikaga m’chiŵelechelo jwatunsachile (kuti atamilichiche) kwikanila ndema jekolanjidwe (jakupagwila), kaneko ni tukasankoposyaga jenumanja n’di makandi, kaneko (ni tukunkusyaaga) kuti nkuŵiche komangala kwenu. Sano ŵane mwa jenumanja pana ŵaakuwaga (nkanakule), soni ŵane mwajenumanja pana ŵaakuwuchisyidwa kuumi wakusakala (wauchekulu wannope) mwanti ngaakumanyililaga chilichose panyuma pa umanyilisi (waŵakwete). Soni nkasalionaga litaka lili litalele, nambo pati tutulwisye palyalakwelyo mesi (gawula) likasatenganyika ni kutulumbala soni ni likasamesyaga ntundu uliose (wammela) wakusalala.');
INSERT INTO "silika" VALUES(22,6,'22:6','Yalakwe (yaisalidweyo ikutendekwaga) ligongo lyanti Allah ni jwali Jwakuonaonape, soni kuti Jwalakwe ni jwaakwimusyaga ŵawe, nambo soni kuti Jwalakwe pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(22,7,'22:7','Ni (kuŵa umboni wakuti) chisimu Kiyama jichiyika mwangali kaichila pa jalakwejo, ni kuti chisimu Allah tachajimusya ŵali m’malembe.');
INSERT INTO "silika" VALUES(22,8,'22:8','Soni chilikati cha ŵandu pana jwaakukanganaga pa ya Allah mwangali umanyilisi, namuno chongoko, namuno soni chitabu chakupeleka lilanguka (kuumila kwa Allah).');
INSERT INTO "silika" VALUES(22,9,'22:9','Juchipombotasyaga lukosi lwakwe (pakanila yakuona mwakulikwesya) kuti jusochelesye (ŵandu) kwitala lya Allah. Jukwete kupata jwalakwejo paduniya kwaluka, sano lisiku lya Kiyama chitukampe kupasya ilagasyo ya Moto wakutinisya.');
INSERT INTO "silika" VALUES(22,10,'22:10','(Chijukasalilidwe yanti): Yalakweyi ni ligongo lya ayila yagalongwesye makono genu, nambo soni kuti Allah nganaŵa jwakwatenda lupuso ata panandi achikapolo (Ŵakwe).');
INSERT INTO "silika" VALUES(22,11,'22:11','Soni chilikati cha ŵandu pana jwaakun’galagatilaga Allah chanchisegambali (mwakayichila), basi naga yimpatile yambone, jukasatulalila nayo (ni kuŵa mpela nsilamu nsyesyene), nambo naga gampatile masausyo, jukasagalauka ni ngope jakwe (jukasaujilaga ku ukafili wakwe), jupelelwe duniya ni Akhera, kwalakweko ni kupelelwa kwakuonechela.');
INSERT INTO "silika" VALUES(22,12,'22:12','Jukasaŵaŵendaga, mmalo mwa Allah, aŵala ŵangaŵa nkumpa masausyo (naga nganiŵagalagatila), nambo soni ŵangaŵa nkumpa ikamuchisyo (naga ŵagalagatile), kwalakweko ni kupotela kwakutalikangana nnope (ni yakuona).');
INSERT INTO "silika" VALUES(22,13,'22:13','Jukasaŵaŵendaga aŵala ŵagali masausyo gao gakuŵandichila nnope kulekangana ni ikamuchisyo yao, kaje kusakala nnope kaligoselo kakwe, soni kaje kusakala nnope nsyoŵe jwakwe!');
INSERT INTO "silika" VALUES(22,14,'22:14','Chisimu Allah tachajinjisya aŵala ŵaakulupilile ni kutendaga yambone, ku Matimbe ga ku Mbepo gasikujilima sulo cha pasi pakwe. Chisimu Allah akasatendaga yaakusaka.');
INSERT INTO "silika" VALUES(22,15,'22:15','Jwalijose jwaakuŵa nkuganichisya yanti Allah nganaŵa ankamuchisye (Muhammadi صلى الله عليه وسلم) paduniya ni ku Akhera, basi akoleche lukonji kunsakasa kaneko ni alitaŵilile, ni alole kuti ana chitagu chakwecho chichityosye yaikumpikasya ntimayo (ana kuliulaga kwakweko chikutendekasye kuti Usilamu ukajenela)?');
INSERT INTO "silika" VALUES(22,16,'22:16','Soni iyyoyopeyo tujitulwisye (Qur’an) kuŵa ma Ȃya gakupikanika, soni chisimu Allah akun’jongolaga jwansachile.');
INSERT INTO "silika" VALUES(22,17,'22:17','Chisimu aŵala ŵakulupilile (mwa Allah ni Ntume Jwakwe Muhammadi صلى الله عليه وسلم) ni ŵali mu Chiyuda, Asabayi ni Akilisito, kwisa soni Amajuusi ni aŵala ŵaawanganyisye (Allah ni isanamu), chisimu Allah tachijilanya chilikati chao lisiku lya Kiyama. Chisimu Allah pachindu chilichose aŵele Mboni.');
INSERT INTO "silika" VALUES(22,18,'22:18','Ana ngankuiona yanti chisimu Allah ikunsujudilaga Jwalakwe yosope yaili kumawunde ni yaili petaka, lyuŵa, mwesi, ndondwa, matumbi, itela, inyama kwisa soni ŵandu ŵajinji? Nambo soni ŵajinji (ŵane mwa ŵanduo) ilagasyo isimichiche pa ŵanganyao. Sano jwatite Allah an’jigasye nchesela, basi ngaŵa nkukola jwakunchimbichisya. Chisimu Allah akasatendaga yaakusaka.');
INSERT INTO "silika" VALUES(22,19,'22:19','Aŵa ni akukangana ŵaŵili (ŵakulupilila ni ŵakanila), akanganile pa ya M’mbuje gwao, sano aŵala ŵaakanile, chachikatilidwa nguwo sya Moto (ni kuwechedwa), pachanya pa mitwe jao pachipungulilidwa mesi gamasemule kusyene.');
INSERT INTO "silika" VALUES(22,20,'22:20','Ni gele (mesigo), ichinyelenyenduka yaili m’matumbo mwao kwisa soni mapende.');
INSERT INTO "silika" VALUES(22,21,'22:21','Soni takakole ŵanganyao nyundo syachisyano (syakwakasila).');
INSERT INTO "silika" VALUES(22,22,'22:22','Ndaŵi ni katema katakasacheje kuti akopoche mwalakwemo ligongo lya kulagasika nnope takawuchisyeje mmomo, soni (takasalileje kuti): “Pasyani ilagasyo yakutinisya.”');
INSERT INTO "silika" VALUES(22,23,'22:23','Chisimu Allah tachajinjisya aŵala ŵaakulupilile ni kutendaga yambone, ku Matimbe ga ku Mbepo gasikujilima sulo cha pasi pakwe. Takawechedweje kwalakweko makosa gagolodi ni gangalekale syakumelemenda nnope, soni yakuwala yao kwalakweko tiyikaŵe ya silika.');
INSERT INTO "silika" VALUES(22,24,'22:24','Soni ŵanganyao ajongoleledwe (paduniya pano) ku maloŵe gambone, soni ajongoleledwe kwitala (lya Usilamu) lya (Allah) Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(22,25,'22:25','Chisimu aŵala ŵaakanile ni kuŵaga ali nkwasiŵilila (ŵandu) kwitala lya Allah, ni (kwasiŵilikanganyaga soni kuti akajinjila mu) Nsikiti Wakuchimbichika (wa ku Maaka), awula watuutesile kuŵa wa ŵandu wosope (pakutendelamo ibada ya Hija ni Umra) mwakulandana, kutandila akutama mwalakwemo ni achalendo ŵakwe, (basi ŵanganyao tuchapa ilagasyo yakupoteka kusyene). Sano jwalijose jwatasache kutenda yangalumbana mwalakwemo mwalupuso, chitumpe kupasya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(22,26,'22:26','Soni (kumbuchilani) katema katwannanjile Ibrahima malo ga (kutaŵa) Nyumba (jakuchimbichika ja Likaaba), (ni kuntenda kuti): “Nkasimumwanganya Une ni chilichose, soni jiswejesyani Nyumba Jangu kwaswejechesya akujisyungula, ni akwiima, akwiinama kwisa soni akusujudu (pa kuswali).”');
INSERT INTO "silika" VALUES(22,27,'22:27','“Soni jenesyani ku ŵandu ya Hija. Tam’bichilile ŵakwenda wapasi ni (ŵakwela) pa ngamiya jilijose jaganda (ligongo lya matabu gaulendo), taiche kuumila kwitala lililyose lyakunaula.”');
INSERT INTO "silika" VALUES(22,28,'22:28','“Kuti ajise kulola yakwakamuchisya yao, ni kulikolanga Lina lya Allah m’moŵa gakumanyika, pa yaŵapele ŵanganyao mu inyama yakulanga, basi lyagani jine mujalakwe (nyamajo), nikwalisya soni ŵayakanganiche ŵaali ŵakulaga.”');
INSERT INTO "silika" VALUES(22,29,'22:29','“Kaneko aliswejesye ku usakwa wao (pakuwula nguwo sya Ihrâm ni kumyola umbo, kulengula ngose, kupakala mauta gakununjila ni ine), ni kwanilisya nasili syao, nikujisyungula Nyumba jakalakala (Likaaba).”');
INSERT INTO "silika" VALUES(22,30,'22:30','Yalakweyo (ni ibada ya Hija yakuŵajilwa ŵandu kuntendela Allah), sano jwataichimbichisye ichindu yakuchimbichika ya Allah, basi (kutenda) yalakweyo tiiŵe yambone kukwakwe pameso pa M’mbujegwe. Ni iŵele halaal kukwenu (kulya) ilango, ikaŵeje yaikusoomedwa kukwenu (mu Qur’an kuti yaharaam). Basi uŵambalani usakwa (wagalagatila) isanamu, soni gaŵambalani maloŵe gaunami.');
INSERT INTO "silika" VALUES(22,31,'22:31','Pakupendechela kwa Allah (pakun’galagatila Jwele pejo), mwangali kum’bwanganya Jwalakwe (ni isanamu). Sano jwaakum’bwanganya Allah (ni isanamu), basi akuŵa nti chisawu apatwiche kwinani ni inkwakwile ijuni, kapena mbungo jijile kumponya kumalo kwakutalika nnope.');
INSERT INTO "silika" VALUES(22,32,'22:32','Yalakweyo (ni ibada ya Hija yakuŵajilwa ŵandu kuntendela Allah), sano jwataichimbichisye imanyilo ya (dini ja) Allah (mpela kuijimbasya inyama yakupeleka mbepesi ku Nyumba jakuchimbichika ja Likaaba), basi yalakweyo tiilosye woga (wakun’jogopa Allah) wauli m’mitima.');
INSERT INTO "silika" VALUES(22,33,'22:33','Nkwete ŵanganyammwe mu yalakwe (inyama yambepesiyo) ikamuchisyo (mpela kuikwela, kuitwika misigo nikuikama nkaka), mpaka pa ndema jekolanjidwe (ja kuisikitila), kaneko malo gakwe gakuisikitila ni ku Nyumba jakalakala (ku Maaka).');
INSERT INTO "silika" VALUES(22,34,'22:34','Soni nkutula uliose twauŵichile malo gakusikitila inyama ya mbepesi, kuti alikolanjeje Lina lya Allah pa yaŵapele ŵanganyao mu inyama yakulanga. Basi nnungu jwenu ni Nnungu Jumpepe, basi Kukwakwe lipelekani (pakuŵa Asilamu), soni mwapani abali jakusengwasya ŵakunda malamusi mwakulinandiya.');
INSERT INTO "silika" VALUES(22,35,'22:35','Aŵala ŵaakutiji naga Allah achikolanjidwaga mitima jao jikasan’gumbalaga woga, soni akasaŵaga ŵakupilila pa yayapatile, ni akasaŵaga soni ŵakwimika Swala mwakolosya, nambo soni mu ichindu yatwapele akasatolaga.');
INSERT INTO "silika" VALUES(22,36,'22:36','Sano ngamiya (syasikupelechedwa ku Nyumba jakuchimbichika ja Likaaba kuŵa mbepesi), tusitesile kukwenu kuŵa mu imanyilo ya (Dini ja) Allah, nkwete ŵanganyammwe mu syalakwesyo yambone (yejinji). Basi likolangani Lina lya Allah pa syalakwesyo ndema jasikwima m’misela (pa kusisikita), sano pati sigwile chamumbalati mwakwe (panyuma pakusisikita), basi lyagani mu syalakwesyo, ni kun’diisya nkwikutila (jwangaaŵendaŵenda ŵandu), ni nkuŵendaŵenda jwakwe. Iyyoyopeyo tusijepepesye kukwenu syalakwesyo kuti m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(22,37,'22:37','Nganijiŵa jim’biiche Allah minou jakwe namuno myasi jakwe, nambo woga wakuumila kukwenu ni waukasaum’bikaga Jwalakwe. Iyyoyopeyo ayijepepesye yalakwe (inyamayo) kukwenu kuti munkusyeje Allah ligongo lya kun’jongola kwenu, soni mwapani abali jakusengwasya ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(22,38,'22:38','Chisimu Allah akasaŵachenjela aŵala ŵaakulupilile. Chisimu Allah ŵangannonyela jwalijose jwachinyengo nnope, jwakanila kusyene (ukoto wa Allah).');
INSERT INTO "silika" VALUES(22,39,'22:39','Apedwile lusa (lwakuputa ngondo) aŵala ŵaakuŵa nkuputwa ngondo; ligongo lyanti ŵanganyao atendedwe lupuso, soni chisimu Allah pa yakwakamuchisya ŵanganyao juŵele Jwakombola kusyene.');
INSERT INTO "silika" VALUES(22,40,'22:40','Aŵala ŵakoposyedwe nsimangwao mwangali chilungamiko ni ikaŵeje ligongo lyanti ŵanganyao akutiji: “M’mbuje gwetu ni Allah.” Soni kungaŵaga Allah kwatuta ŵandu (amagongo) kupitila mwa ŵandu ŵane (mpela asilikali), gakagumulidwe masunagogi, yalichi, nyumba sine syakutendelamo ibada kwisa soni misikiti jalikolanjidwamo Lina lya Allah mwa winji, soni chisimu Allah chankamuchisye kusyene mundu jwaakunkamuchisya Jwalakwe (paajikamuchisya Dini Jakwe), chisimu Allah ni Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(22,41,'22:41','Aŵala ŵati naga tuli twapele machili pachilambo (gakwakombolela amagongo ŵao), akwimikaga Swala mwakolosya ni kutolaga Zaka, ni kuchisyaga yambone, ni kanyaga yakusakala, sano kwa Allah ni kujili mbesi ja ichindu (yosope).');
INSERT INTO "silika" VALUES(22,42,'22:42','Sano naga akunkanila mmwe (Muhammadi صلى الله عليه وسلم), basi (nganiiŵa yachilendo) chisimu ŵakaaniile paujo pao ŵandu ŵa Nuhu, achina Adi ni achina Samuda.');
INSERT INTO "silika" VALUES(22,43,'22:43','Kwisa soni ŵandu ŵa Ibrahima ni ŵandu ŵa Lutwi.');
INSERT INTO "silika" VALUES(22,44,'22:44','Ni kwisa soni achimisyene Madiyana, nombe najo Musa jwakaaniidwe. Basi nalechelele achimakafili, kaneko ni nakwembekenye (ni ilagasyo). Nambi kwaliji uli kasilika Kwangu (ngati najonasile wosope)?');
INSERT INTO "silika" VALUES(22,45,'22:45','Ana jilingwajilingwa mwa misi jatwajijonasile jili jalupuso, basi mapupa gakwe gali gagwilile pa misakasa jakwe? Nambo soni (ilingwailingwa) isima yayalechedwe kwisa soni makome geliye mwa kunyang’amasya?');
INSERT INTO "silika" VALUES(22,46,'22:46','Ana ŵanganyao ngakwendajenda pa chilambo kuti jipatikane kukwao mitima jaganichisya mwalunda, kapena mawiwi gakupikanila? Basi chisimu chene meso ngagakasatatala (kwakonanga Dini) nambo mitima jajili mu idali ni jajikasatatala (kwakonanga Dini).');
INSERT INTO "silika" VALUES(22,47,'22:47','Ni akunkulumisya kuti m’biche nayo ilagasyo, nambotu Allah nganaŵa akasile chilanga Chakwe (chaŵachiŵisile chanti ilagasyo yakandapala ichiŵa ku Akhera). Soni chisimu lisiku limpepe kwa Ambuje ŵenu lili chisawu yaka yakwana sausande (1000) muyankuti kaŵalanjile ŵanganyammwe.');
INSERT INTO "silika" VALUES(22,48,'22:48','Ana soni jilingwajilingwa mwa misi janajilechelele kutendaga jili jalupuso, kaneko ni najikwembekenye (ni ilagasyo)? Soni Kukwangu ni kwakuujila.');
INSERT INTO "silika" VALUES(22,49,'22:49','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “E jenumanja ŵandu! Chisimu une kukwenu ni nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(22,50,'22:50','“Basi aŵala ŵaakulupilile ni kutendaga yambone, akwete kupata ŵanganyao chikululuko kwisa soni lisiki lyakuchimbichika (lya ku Mbepo).”');
INSERT INTO "silika" VALUES(22,51,'22:51','“Sano aŵala ŵaakulimbichila kulimbana ni ma Ȃya Getu aku ali nkulepelekasya (ŵandu kunkuya Muhammadi صلى الله عليه وسلم), ŵanganyao niŵandu ŵa ku Moto Waukali.”');
INSERT INTO "silika" VALUES(22,52,'22:52','Soni nganitutumisyeje paujo penu (mmwe Muhammadi صلى الله عليه وسلم) ntenga jwalijose namuno ntume, ni ikaŵeje kuti juchisoomaga, shetani ŵaponyaga (maloŵe gaunami) mu kusooma kwakweko, basi Allah ni ŵafutaga yaŵaponyaga shetaniyo, kaneka Allah ni ŵagalimbanganyaga ma Ȃya Gakwe, sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(22,53,'22:53','(Ayi yatendekwaga) kuti ayitende yaŵaponyaga shetani kuŵa mayeso kwa aŵala ŵamuli m’mitima mwao mwana ulwele (wa ukafili ni unakunaku) ni ŵajijumwile mitima jao (pangakunda yakuona), soni ŵandu ŵalupuso ali mu ukangani wakutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(22,54,'22:54','Soni kuti amanyilile aŵala ŵapedwile umanyilisi kuti chisimu jele (Qur’aniji) jakuona jakuumila kwa Ambuje ŵenu, basi ni ajikulupilile jalakwejo ni kuti soni mitima jao jilinandiye pakujikunda jalakwe, soni chisimu Allah ni Nkwajongolela aŵala ŵaakulupilile kwitala lyagoloka.');
INSERT INTO "silika" VALUES(22,55,'22:55','Soni aŵala ŵaakanile ngaleka kuŵa nchikaiko chakujikaichila jele (Qur’anijo) mpakana chijaichilile ndaŵi (jao jakuwila) mwachisupuchisya, kapena kwaichilila ilagasyo ya lisiku lyangali chilo (lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(22,56,'22:56','Uchimwene lyele lisikulyo uchiŵa wa Allah pe, Jwalakwe tachijilanya chilikati chao. Basi aŵala ŵaŵakulupilile ni kutendaga yambone, takaŵe m’Matimbe Gachindimba (ku Mbepo).');
INSERT INTO "silika" VALUES(22,57,'22:57','Sano aŵala ŵakufuulu ni kaanila ma Ȃya Getu, basi ŵanganyao akwete kupata ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(22,58,'22:58','Sano aŵala ŵaŵasamile petala lya Allah, kaneko ni kuulajidwa kapena kuwa, chisimu Allah tachapa lisiki lyambone, soni chisimu Allah ni jwaali Jwambone nnope mwa akupeleka lisiki.');
INSERT INTO "silika" VALUES(22,59,'22:59','Chisimu ŵanganyao tachajinjisya pakwinjila patachipanonyela (ku Mbepo). Soni chisimu Allah ni Jwakumanyilila kusyesyene, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(22,60,'22:60','Yalakweyo ili iyyo. Sano juchauchisye ni ipotesi yakulandana ni ayila yajwapedwile jwalakwe kaneko ni kutendeledwa uwwanga, chisimu Allah tankamuchisye. Chisimu Allah ni Nkusiimaasya (ilemwa), Jwakuluka kusyene.');
INSERT INTO "silika" VALUES(22,61,'22:61','Yalakweyo ni ligongo lyanti Allah akwinjisyaga chilo m’muusi (ni kuleupa muusi kwipipa chilo mpela ndaŵi jachitukuta), ni akwinjisyaga soni muusi nchilo (ni kuleupa chilo kwipipa muusi mpela ndaŵi jakusisima), soni ligongo lyanti Allah ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(22,62,'22:62','Yalakweyo ni ligongo lyanti Allah ni jwali Jwakuona, soni kuti ayila yaakuiŵendaga yangaŵaga Jwalakwe ni yaili yaukoleko, soni kuti Allah ni jwali Jwapenani kusyene, Jwankulungwa kwannope.');
INSERT INTO "silika" VALUES(22,63,'22:63','Ana ngankuiona yanti Allah akutulusyaga kuumila kwinani mesi (gawula), basi litaka ni likasasyuka kuŵa lyakubiliŵitala? Chisimu Allah ni Jwakumanyilila yakunyilila nnope, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(22,64,'22:64','Iŵele Yakwe yosope yaili kumawunde ni yaili petaka. Soni chisimu Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(22,65,'22:65','Ana ngankuiona yanti Allah an’jepepachisye jenumanja yosope yaili petaka, kwisa soni yombo yaikwenda m’mbwani kwa ulamusi Wakwe? Ni akulichiinga liwunde kuti likagwila petaka ni ikaŵeje kwa lusa Lwakwe. Chisimu Allah ku ŵandu ni Mpole nnope, Jwachanasa channope.');
INSERT INTO "silika" VALUES(22,66,'22:66','Soni Jwalakwe ni Ajula juŵampele umi (waduniya), kaneka ni tampe chiwa, kaneko ni tachin’jimusya soni (m’malembe). Chisimu mundu juŵele jwakanila kusyene.');
INSERT INTO "silika" VALUES(22,67,'22:67','Nkutula uliose twauŵichile litala lyakatende ka ibada lyaakuŵa nkukuya ŵanganyao (mpela kusikita inyama masiku gatatu gakutama pa Mina ku Maaka ndema ja Hija), basi ni akakangana namwe pa ngani (jakusikita; pakuŵecheta kuti: Inyama yaayiuleje Allah pakuwa chibudu ni yaili yakuŵajilwa kulya kulekangana ni yansikite jenumanja). Soni ŵilanjilani kwa Ambuje ŵenu. Chisimu mmwejo (Muhammadi صلى الله عليه وسلم) n’di pachongoko chagoloka.');
INSERT INTO "silika" VALUES(22,68,'22:68','Sano naga akanganeje namwe, basi jilani: “Allah ni jwaakumanyilila chenene yankutenda ŵanganyammwe.”');
INSERT INTO "silika" VALUES(22,69,'22:69','“Allah tachijilanya chilikati chenu lisiku lya Kiyama pa ichindu yamwaliji ŵanganyammwe muyalakweyo nkutindana.”');
INSERT INTO "silika" VALUES(22,70,'22:70','Ana ngankuimanyilila yanti Allah jukuimanya yosope yaili kwinani ni pasi? Chisimu yalakweyo ili m’Chitabu (cha Al-Lauh-al-Mahfûz). Chisimu yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(22,71,'22:71','Ni akuŵa ŵanganyao nkuigalagatila (inepe) yangaŵaga Allah, ayila yanganaituluchisya umboni (wakundisya kuigalagatila), soni yanganakola nayo umanyilisi, sano ŵalupuso nganakola nkwakamuchisya.');
INSERT INTO "silika" VALUES(22,72,'22:72','Soni ndema jakusoomedwa kukwao ma Ȃya Getu gakupikanika, tinkuone kungope kwa aŵala ŵakanile kunyalaisidwa (kwakanila), akusigalaga panandi kwatenda uwanga aŵala ŵaakwaasoomela ma Ȃya Getu. Jilani: “Ana nantagulile yakusakala nnope kumpunda yalakweyo? Moto (wa Jahannama) waataŵene nao Allah chilanga aŵala ŵaakanile. Kaje kusakala nnope malo gakuujila gakwe!”');
INSERT INTO "silika" VALUES(22,73,'22:73','E jenumanja ŵandu! Chipelechedwe chilandanyo, basi chipikanilani. Chisimu aŵala ŵankwalumbanjilaga ŵangaŵaga Allah nganaŵa apakombwele gumba membe namuno ali atite asongangane pa jalakwejo. Soni naga membejo jili jaasombwele chindu nganaŵa achipokasisye kujalakwejo. Ali jwangali machili nkuŵenda ni nkuŵendedwa jwakwe.');
INSERT INTO "silika" VALUES(22,74,'22:74','Ngananchimbichisya Allah ni uchimbichimbi Wakwe wakum’bajila. Chisimu Allah ni Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(22,75,'22:75','Allah akasasagulaga mitenga kuumila mwa achimalaika ni mwa ŵandu. Chisimu Allah ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(22,76,'22:76','Jukuimanya yaili paujo pao ni yapanyuma pao, soni kwa Allah pe nikwakuujila ichindu yosope.');
INSERT INTO "silika" VALUES(22,77,'22:77','E jenumanja ŵankulupilile! Jinamani soni sujuduni ni mun’galagatileje M’mbuje gwenu, soni tendagani yambone kuti m’be ŵakupunda.');
INSERT INTO "silika" VALUES(22,78,'22:78','Soni putani petala lya Allah ngondo jisyesyene. Jwalakwe ni jwansagwile ŵanganyammwe (kuŵa nkutula wambone nnope), ni nganam’bichila ŵanganyammwe mu Dini yakusausya iliyose. (Jalakweji ni) Dini ja atati ŵenu Ibrahima. Jwalakwe (Allah) ni juŵankolasile jenumanja ni lina lyakuti Asilamu, kuumila kalakose ni mu aji (Qur’an), kuti Ntenga (Muhammadi صلى الله عليه وسلم) aŵe mboni pa jenumanja, nombe ŵanganyammwe m’be mboni pa ŵandu. Basi jimikagani Swala mwakolosya ni kutolaga Zaka, soni munkamulisyani Allah (pakola chikulupi mwa Jwalakwe, ni kun’jegama Jwelepejo). Jwalakwe ni M’mbuje gwenu. Kaje umbone nnope Ambuje ŵakwe! Soni kaje umbone nnope Akukamuchisya ŵakwe!');
INSERT INTO "silika" VALUES(23,1,'23:1','Chisimu pamasile paapundile ŵakulupilila.');
INSERT INTO "silika" VALUES(23,2,'23:2','Aŵala ŵaakuŵa ŵakuteteŵala pa Swala syao.');
INSERT INTO "silika" VALUES(23,3,'23:3','Ni aŵala ŵaakuŵa nkuitundumalila yangali mate.');
INSERT INTO "silika" VALUES(23,4,'23:4','Ni aŵala ŵaakuŵa nkutenda yakutola Zaka.');
INSERT INTO "silika" VALUES(23,5,'23:5','Ni aŵala ŵaakuŵa nkuugosa utukani wao.');
INSERT INTO "silika" VALUES(23,6,'23:6','Ikaŵeje (akuukamulichisya masengo) pa achiŵammakwao kapena yagapatile makono gao gakun’dyo (mateka), basi chisimu ŵanganyao nganaŵa ŵagambidwa.');
INSERT INTO "silika" VALUES(23,7,'23:7','Sano ŵatasache yakuipunda yele, basi ŵanyao ni ŵakusumba mpika.');
INSERT INTO "silika" VALUES(23,8,'23:8','Ni aŵala ŵaakuŵa nkusunga ichindu yaasunjisyidwe kwisa soni ilanga yao.');
INSERT INTO "silika" VALUES(23,9,'23:9','Ni aŵala ŵaakuŵa nkusigosa Swala syao (pakusiswali chenene mundaŵi syakwe).');
INSERT INTO "silika" VALUES(23,10,'23:10','Ŵanganyao ni akutawala.');
INSERT INTO "silika" VALUES(23,11,'23:11','Aŵala ŵatakajitawale Mbepo ja Firdausi, ŵanyao mwalakwemo takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(23,12,'23:12','Soni chisimu Uwwe twan’gumbile mundu (Adam) kuumila mwilongo lyesaguche.');
INSERT INTO "silika" VALUES(23,13,'23:13','Kaneko twantesile (mundu jwalijose kuŵa jwagumbidwa ni) lindondwa (lya ulume ni ukongwe, lyalikuŵichidwaga) pa malo pakutamilichika ni kusunjika chenene (mu chiŵelechelo).');
INSERT INTO "silika" VALUES(23,14,'23:14','Kaneka ni tukasaligalausyaga lindondwalila kuŵa chipande chamyasi, ni tukasachigalausyaga chipande chamyasichila kuŵa chipande channou, ni tukasachigalausyaga chipande channouchila kuŵa maupa, ni tukasagawechaga maupagala minou, kaneko ni tukasantendaga kuŵa chiumbe chine (chaumi pakumpepelela nsimu). Basi atukwiche (pa uchimbichimbi ni upile) Allah, jwali Jwambone nnope mwa akugumba.');
INSERT INTO "silika" VALUES(23,15,'23:15','Soni chisimu ŵanganyammwe panyuma payalakweyo tim’bwe.');
INSERT INTO "silika" VALUES(23,16,'23:16','Soni chisimu ŵanganyammwe lisiku lya Kiyama tinchiukulidwa m’malembe.');
INSERT INTO "silika" VALUES(23,17,'23:17','Soni chisimu Uwwe twagumbile pachanya penu matala nsano nagaŵili (mawunde), ni nganituŵa Uwwe ŵanti ningaimanya iwumbe.');
INSERT INTO "silika" VALUES(23,18,'23:18','Ni tutulwise kuumila kwinani mesi (gawula) mwachipiimo, basi ni tugatesile kuŵa gakutamilichika mwitaka, soni chisimu Uwwe yagatyosya galakwego tuŵele Ŵakombola.');
INSERT INTO "silika" VALUES(23,19,'23:19','Basi ni galakwego twantendele ŵanganyammwe migunda ja mitende ni mizabibu, nkwete jenumanja mwalakwemo isogosi yejinji, soni mwa yalakweyo nkasalyaga.');
INSERT INTO "silika" VALUES(23,20,'23:20','Ni (twantendele soni) chitela (chanzaituni) chachikasamelaga kuumila petumbi lya Sinai, chachikasajenjekaga mauta, ni kuŵa soni mboga kwa akulya.');
INSERT INTO "silika" VALUES(23,21,'23:21','Soni chisimu nkwete ŵanganyammwe mu ilango chichetelelo. Tukumpaga kumwa (nkaka) wauli m’matumbo mwakwe. Soni nkwete jenumanja muyalakweyo ikamuchisyo yaijinji, nambo soni ine muyalakweyo nkasalyaga.');
INSERT INTO "silika" VALUES(23,22,'23:22','Nambo soni pa yalakweyo kwisa soni pa yombo nkasanyakulidwaga.');
INSERT INTO "silika" VALUES(23,23,'23:23','Soni chisimu chene twantumisye Nuhu kuŵandu ŵakwe ni jwatite: “E ŵandu ŵangu! Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe. Ana ngankogopa (ipotesi Yakwe)?”');
INSERT INTO "silika" VALUES(23,24,'23:24','Basi achinkulungwa aŵala ŵaŵakanile mu ŵandu ŵakwe ŵatite: “Nganijuŵatu aju ikaŵeje mundu mpele jenumanja. Jukusaka kuliŵika kuŵa jwakuchimbichika ni jwambone nnope kumpunda jenumanja. Soni Allah angasache angatulwisye malaika. Nganituipikaneje uwwe ayi kwa achatatiŵetu ŵandanda.”');
INSERT INTO "silika" VALUES(23,25,'23:25','“Nganijuŵa jwalakweju ikaŵeje mundu jwamasoka, basi mun’jembecheyani kwikanila ndema (jakwe jakuwila).”');
INSERT INTO "silika" VALUES(23,26,'23:26','(Nuhu) jwatite: “Ambuje ŵangu! Munjenjelani ligongo lyakunganila.”');
INSERT INTO "silika" VALUES(23,27,'23:27','Basi ni twan’juwulile (maloŵe ganti): “Panganyani chombo (mwakuloleledwa) ni meso Getu soni mwachiiganyo Chetu. Sano patiliiche lilamusi Lyetu (lyakwajonanga) ni kuulika pachisisa (mesi gatutumule, chachiŵe chilosyo kukwenu chakuŵandichila chigumula), jinjisyani mwalakwe (m’chombomo) ntundu uliose (wainyama) iŵili iŵili (chelume ni chekongwe) kwisa soni liŵasa lyenu – ikaŵeje jwalyannongolele liloŵe mwa ŵanganyao (lyakuti tachijonasika nikuti ngakwela), soni ngasimumbechetekasya pa (kwajimila) aŵala ŵalitesile lupuso (yakuleka kwaapa ipotesi), chisimu ŵanganyao ali ŵakumisyidwa.”');
INSERT INTO "silika" VALUES(23,28,'23:28','Sano patintamilichiche mmwejo nchombo ni ŵandu ŵan’di nao, basi jilani: “Lumbili losope lwene alapikwe Allah Ajula jwaatujokwele ku ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(23,29,'23:29','Soni (pakutuluka) jilani: “Ambuje! Munduluchisyani pamalo paupile, soni Mmwejo ni ŵan’di Ŵambone nnope mwa akutulusya.”');
INSERT INTO "silika" VALUES(23,30,'23:30','Chisimu muyalakweyi mwana ilosyo. Soni chisimu Uwwe tuli ŵakwalinga (ŵandu).');
INSERT INTO "silika" VALUES(23,31,'23:31','Kaneko twagumbile panyuma pao nkutula wine (achina Adi).');
INSERT INTO "silika" VALUES(23,32,'23:32','Basi ni twatumisye ku ŵanganyao ntenga (Hudu) jwakuumila mu ŵanyapeo (juŵasalilaga kuti): “Mun''galagatilani Allah, nganinkola nnungu jwine jwangaŵaga Jwalakwe. Ana ngankogopa (ipotesi Yakwe)?”');
INSERT INTO "silika" VALUES(23,33,'23:33','Sano achinkulungwa mu ŵandu ŵakwe aŵala ŵaŵakufuulu ni kanila yakusimangana ni Akhera soni ni twasichilisye pa umi waduniya, ŵatite: “Nganijuŵa aju ikaŵeje mundu mpela jenumanja, jukulyaga mu ichindu yankuilyaga jenumanja, soni jukumwaga mu ichindu yankumwaga.”');
INSERT INTO "silika" VALUES(23,34,'23:34','“Soni chisimu naga nkwika mwakun’jitika mundu mpela jenumanja, chisimu ŵanganyammwe pa jele ndemajo tim’be ŵakwasika.”');
INSERT INTO "silika" VALUES(23,35,'23:35','“Ana akumpa chilanga chanti chisimu ŵanganyammwe patinchiwapo ni kusyuka litaka ni maupa, kuti chisimu jenumanja chinchikoposyedwa m’malembe?”');
INSERT INTO "silika" VALUES(23,36,'23:36','“Ili kwanaula, ili kwanaula yankupedwa chilangayo (nganiiŵa itendekwe ata panandi).”');
INSERT INTO "silika" VALUES(23,37,'23:37','“Pangali (umi) wine ikaŵeje umi wetu waduniyapeuno, tukuwaga, nambo soni tukulamaga, soni nganituŵa uwwe ŵanti ni kuukulidwa m’malembe.”');
INSERT INTO "silika" VALUES(23,38,'23:38','“Nganijuŵa aju (jwaakuliŵilanjila utumeju) ikaŵeje mundu jwansepele Allah unami, soni uwwe nganituŵa tunkulupilile jwalakwe.”');
INSERT INTO "silika" VALUES(23,39,'23:39','(Hudu) jwatite: “Ambuje ŵangu! Munjenjelani ligongo lyakunganila.”');
INSERT INTO "silika" VALUES(23,40,'23:40','(Allah) jwatite: “Pangakaŵapa taŵe ŵakuligamba.”');
INSERT INTO "silika" VALUES(23,41,'23:41','Basi ni wakwembekenye n’gumilo (waipotesi) mwakuonaonape, basi ni twatesile kuŵa mpela iswani (yakujigalwa ni mesi), basi chonasiko chakunaula chaliji kwa ŵandu ŵalupuso.');
INSERT INTO "silika" VALUES(23,42,'23:42','Kaneko twagumbile panyuma pao mikutula jine.');
INSERT INTO "silika" VALUES(23,43,'23:43','Pangali umati wampaka ujijanguye kapena kujichelewasya ndema jakwe (jakuwila).');
INSERT INTO "silika" VALUES(23,44,'23:44','Kaneko twatumisye achimitenga Ŵetu mwakuyana. Ndema ni katema kajwauŵichililaga umati uliose ntenga jwakwe ŵankanilaga, ni twaakuyichisyaga ŵane panyuma pa ŵane (wa kuchonasiko), ni twatesile ŵanganyao kuŵa ngani (sisyesyene syaŵandu ŵakala syakwajamuka nasyo ŵandu ŵasambano), basi chonasiko chakunaula chaliji kwa ŵandu ŵangakulupilila.');
INSERT INTO "silika" VALUES(23,45,'23:45','Kaneko twantumisye Musa ni nkulugwe Haruna, pamo ni isimosimo Yetu kwisa soni umboni wakuonechela.');
INSERT INTO "silika" VALUES(23,46,'23:46','Kwa Firiauna ni nduna syakwe, nambo ŵanganyao ŵalikwesyisye ni ŵaliji soni ŵandu ŵakulinyakula.');
INSERT INTO "silika" VALUES(23,47,'23:47','Basi ŵanganyao ŵatite: “Ana twakulupilile ŵandu ŵaŵili ŵanti mpela uwwewe, kutendaga ŵandu ŵao kukwetu ali achikapolo!”');
INSERT INTO "silika" VALUES(23,48,'23:48','Mwanti ŵaakaaniile ŵanaŵaŵilio, basi ni ŵaliji (akukanilao) mwa akonanjidwa.');
INSERT INTO "silika" VALUES(23,49,'23:49','Soni chisimu Uwwe twampele Musa Chitabu kuti ŵanganyao ajongoche.');
INSERT INTO "silika" VALUES(23,50,'23:50','Soni twantesile Mwanache jwa Maryam kwisa soni Mamagwe kuŵa chisimosimo (chakulosya ukombosi Wetu, wakomboleka ŵakongwe kuŵeleka mwanache mwangali kusimana ni ŵalume), ni twajuyiye ŵanaŵaŵilio pachikwesya chakutamilichika ni ntunjelele, chachakwete soni achitutumule (pa Baitul Muqaddas).');
INSERT INTO "silika" VALUES(23,51,'23:51','E jenumanja achimitenga! Lyagani yakulya yambone ni mpanganyeje ichindu yambone. Chisimu Une ichindu yankutenda mbele Jwaimanya chenene.');
INSERT INTO "silika" VALUES(23,52,'23:52','Soni chisimu awu nkutula wenu (wa Usilamu) ni nkutula umpepe, soni Une ni Nam’mbuje gwenu, basi njogopani.');
INSERT INTO "silika" VALUES(23,53,'23:53','Nambo (akwakuya achimitengao) ŵanupulanilene dini jao chilikati chao ni kuŵa mikutulamikutula, likuga lililyose pachikulupi chalinacho akasachisengwelaga.');
INSERT INTO "silika" VALUES(23,54,'23:54','Basi mwalekani ŵanganyao mu kusokonechela mwaomo, kwikanila ndema (jao jakuwila).');
INSERT INTO "silika" VALUES(23,55,'23:55','Ana akuganichisya yanti chipanje ni ŵanache yatukwapa mwawinji,');
INSERT INTO "silika" VALUES(23,56,'23:56','Kuti tukwaŵilinganya pakwapa yambone? (Ngwamba, kweleko kwalechelesya pa duniya pano, kuti akaja kupata yambone iliyose ku Akhera), nambo kuti ngakulimanyilila.');
INSERT INTO "silika" VALUES(23,57,'23:57','Chisimu aŵala ŵaakuŵa nkuligandasya ligongo lyakun’jogopa M’mbuje gwao;');
INSERT INTO "silika" VALUES(23,58,'23:58','Ni aŵala ŵaakuŵa nkugakulupilila ma Ȃya ga M’mbuje gwao;');
INSERT INTO "silika" VALUES(23,59,'23:59','Ni aŵala ŵangaakum’bwanganya M’mbuje gwao (ni isanamu);');
INSERT INTO "silika" VALUES(23,60,'23:60','Ni aŵala ŵaakutolaga (Zaka ni sadaka syao), syaakupelekaga aku mitima jao jili ni woga (wanti itendo yaoyo mwine ipocheledwe kapena iyayi), ligongo lyanti ŵanganyao ali ni chikulupi chanti kwa M’mbuje gwao tachiujila (ni kuja kuŵalanjidwa itendo yao);');
INSERT INTO "silika" VALUES(23,61,'23:61','Ŵanganyao ni ŵaakutenda chitema pa ichindu yambone, soni ŵanganyao pa yalakweyo akuŵa paujo (mpela kuswali ipindi kundanda kwandaŵi syakwe).');
INSERT INTO "silika" VALUES(23,62,'23:62','Soni ngatukunkanganikaga mundu (kutenda) ikaŵeje yampaka ayikombole, soni Kukwetu kwana Chitabu chakuŵecheta yakuonaonape (Al-Lauh Al-Mahfūz), soni ŵanganyao ngasaja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(23,63,'23:63','Nambo mitima jao (achimakafilio) jili mupupulipupuli wa aji (Qur’an), soni akwete ŵanganyao itendo yangaŵaga yalakwe (yaisalidwe kwa ŵakulupilila) yataŵe ŵanganyao ali nkuipanganya.');
INSERT INTO "silika" VALUES(23,64,'23:64','Kwikanila ndema jatuchakwembekanya ŵakulipatila ŵao ni ilagasyo, jele ndemajo ŵanganyao tachigumila pakuŵenda chikamuchisyo.');
INSERT INTO "silika" VALUES(23,65,'23:65','(Tachisalilidwa yanti): “Ngasin’gumila pakuŵenda chikamuchisyo lelo, chisimu ŵanganyammwe nganchenjeledwa Nowe.”');
INSERT INTO "silika" VALUES(23,66,'23:66','“Chisimu ma Ȃya Gangu galiji nkusoomedwa kukwenu, nambo mwaliji nkugalauchila ku indende yenu (pakugakanila galakwe).”');
INSERT INTO "silika" VALUES(23,67,'23:67','“Aku n’di nkulikwesya (ligongo lyanti ŵakutama pa Nsikiti Wakuchimbichika wa Maaka), n’di nkuŵechetana chilo yangalumbana yaajigamba jalakwe (Qur’an).”');
INSERT INTO "silika" VALUES(23,68,'23:68','Ana nganalicheteleleje ŵanganyao liloŵe (lya Allah lyalitulusyidwe kwa Muhammadi صلى الله عليه وسلم jajili Qur’an), kapena yaichilile yanganiyaichilileje achatati ŵao ŵandanda?');
INSERT INTO "silika" VALUES(23,69,'23:69','Kapena nganammanya ntenga jwao (ndamo syakwe syambone), basi ŵanganyao jwalakwe ni akunkana?');
INSERT INTO "silika" VALUES(23,70,'23:70','Kapena akutiji: “Mwajwalakwe mwana masoka!” (Ngwamba), nambo kuti jwaichilile ni yakuona, soni ŵajinji mwa ŵanganyao yakuona akuichimaga.Sano ingaŵe kuti yakuona yakuyiye isako yao, nikuti gakajonasiche mawunde ni mataka kwisa soni yaili mwalakwemo, nambo kuti Uwwe twaichilile ŵanganyao ni chikumbusyo chao, sano ŵanganyao yachikumbusyo chaocho akunyosyela.');
INSERT INTO "silika" VALUES(23,71,'23:71','Sano ingaŵe kuti yakuona yakuyiye isako yao, nikuti gakajonasiche mawunde ni mataka kwisa soni yaili mwalakwemo, nambo kuti Uwwe twaichilile ŵanganyao ni chikumbusyo chao, sano ŵanganyao yachikumbusyo chaocho akunyosyela.');
INSERT INTO "silika" VALUES(23,72,'23:72','Kapena mmwe (Muhammadi صلى الله عليه وسلم) nkwajuga ŵanganyao malipilo (ni ligongo lyakwe akujikana Diniji)? Nambotu malipilo ga Ambuje ŵenu ni gagali gambone nnope, soni Jwalakwe ni Jwambone nnope mwa akupeleka lisiki.');
INSERT INTO "silika" VALUES(23,73,'23:73','Soni chisimu mmwe (Muhammadi صلى الله عليه وسلم) nkwaŵilanjila ŵanganyao kwitala lyagoloka.');
INSERT INTO "silika" VALUES(23,74,'23:74','Soni chisimu aŵala ŵangakukulupilila ya Akhera akusepuchila kwanaula kuleka litala (lyagolokalyo).');
INSERT INTO "silika" VALUES(23,75,'23:75','Soni namuno tungatendele chanasa ni kwatyochesya makungu gaali nago, angajendelechelepe mu kusumba mpika kwao ali pupulipupuli.');
INSERT INTO "silika" VALUES(23,76,'23:76','Soni chisimu Uwwe twakwembekenye ŵanganyao ni ilagasyo, nambo nganalitulusya kwa Ambuje ŵao, nambo soni nganaŵenda mwakulinandiya.');
INSERT INTO "silika" VALUES(23,77,'23:77','Kwikanila ndema jatuchaugulila nnango wana ilagasyo yaukali nnope, pajele ndemajo ŵanganyao tachiŵa ŵakata tama.');
INSERT INTO "silika" VALUES(23,78,'23:78','Soni Jwalakwe ni Ajula juŵan’guumbiile kupikana, kulola ni mitima (jaganichisya). Nambo kutogolela kwenu kuŵele kwannonope.');
INSERT INTO "silika" VALUES(23,79,'23:79','Soni Jwalakwe ni Ajula jwan’jenesye petaka, soni Kukwakwe ni kutinchisonganganyisidwa.');
INSERT INTO "silika" VALUES(23,80,'23:80','Soni Jwalakwe ni Ajula jwaakupelekaga umi ni chiwa, soni kuŵele Kwakwe kutindana kwa chilo ni muusi, ana jenumanja ŵangali lunda?');
INSERT INTO "silika" VALUES(23,81,'23:81','Nambo kuti ŵanganyao akuŵechetaga muŵaŵechetelaga (ŵandu) ŵandanda.');
INSERT INTO "silika" VALUES(23,82,'23:82','Ŵanganyao akutiji: “Ana patuchiwapo ni kusyuka litaka ni maupaupape nambo yakuona uwwe tuchiukulidwa m’malembe?”');
INSERT INTO "silika" VALUES(23,83,'23:83','“Chisimu tuŵele tuli nkupedwa chilanga uwwe ni achatatiŵetu chakwamba yalakweyi kuumila kalakala, nganiiŵa yalakweyi ikaŵeje adisiadisi syaŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(23,84,'23:84','Jilani: “Ana litaka ni yaili mwalakwemo yacheni nam’baga jenumanja ŵakumanyililaga?”');
INSERT INTO "silika" VALUES(23,85,'23:85','(Pakwanga) tajile: “Ya Allah.” Jilani: “Ana ni ngankukumbuchila (kuti Jwalakwe jwana ukombosi wachilichose)?”');
INSERT INTO "silika" VALUES(23,86,'23:86','Jilani: “Ana ni nduni M’mbuje jwa mawunde nsano nagaŵili ni M’mbuje jwa Arishi Jekulungwa (Chindanda Chaulungu)?”');
INSERT INTO "silika" VALUES(23,87,'23:87','(Pakwanga) tajile: “Allah.” Jilani: “Ana ni ngankogopa (kuchigalagatila chinepe changaŵaga Jwalakwe)?”');
INSERT INTO "silika" VALUES(23,88,'23:88','Jilani: “Ana ni nduni jwamuli munkono Mwakwe mwana Umwenye wachilichose, soni Jwalakwe ni jwaakugosa (indu yosope) nambo pangali champaka chigosedwe pa ilagasyo Yakwe, nam’baga jenumanja ŵakumanyililaga?”');
INSERT INTO "silika" VALUES(23,89,'23:89','(Pakwanga) tajile: “Allah.” Jilani: “Nambi mkulojedwa chinauli?”');
INSERT INTO "silika" VALUES(23,90,'23:90','Nambo twaichilile ni yakuona, nambo soni chisimu ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(23,91,'23:91','Allah nganalitendela mwanache jwalijose, soni pangali nnungu jwine pamo ni Jwalakwe, ingaŵe iyyo (kuti paliji pana nnungujwine) nikuti nnungu jwalijose jungajigalaga yajugumbile (ni kwakanya achimilungu ŵane kuituma), soni ŵane mwa ŵanganyao angalinjilile kuŵa pachanya pa ŵane (pakanganilana umwenye). Allah aŵambalene nayo kusyene yaakusimba ŵanganyao.');
INSERT INTO "silika" VALUES(23,92,'23:92','Nkumanyilila yakusisika ni yakuonechela, basi Jwalakwe jutukwiche kwannope ku yaakum’bwanganya nayoyo.');
INSERT INTO "silika" VALUES(23,93,'23:93','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ambuje ŵangu! Naga timunosye (ilagasyo) yaakuŵa nkupedwa chilanga ŵanganyao,”');
INSERT INTO "silika" VALUES(23,94,'23:94','“Ambuje ŵangu! Basi ngasimundenda kuŵa muŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(23,95,'23:95','Sano Uwwe chisimu chene pa yakunnosya (ilagasyo) yaakuŵa nkupedwa chilanga ŵanganyao tuŵele ŵakombola kusyene.');
INSERT INTO "silika" VALUES(23,96,'23:96','Tyosyani yakusakala (yanteendeele) pa (kwateendela) ayila yaili yambone. Uwwe tukumanyilila chenene yaakusimbayo.');
INSERT INTO "silika" VALUES(23,97,'23:97','Soni jilani: “Ambuje ŵangu! Ngulijuya Kukwenu kumasongono ga shetani.”');
INSERT INTO "silika" VALUES(23,98,'23:98','“Ni ngulijuya soni Kukwenu Ambuje ŵangu kuti atakwika mwambichilile.”');
INSERT INTO "silika" VALUES(23,99,'23:99','Kwikanila ndema janti jumo jwao (mwa akuwanganya Allah ni isanamu) chili chim’bichilile chiwa jukasatiji: “Ambuje ŵangu! Mumbusyani.”');
INSERT INTO "silika" VALUES(23,100,'23:100','“Kuti mwine une ngatende yambone pa ayila yanaailesile.” Ngwamba! Chisimu lyalakwelyo ligambile kuŵa liloŵe lyajukuŵecheta jwalakwejo, soni munyuma mwaomo mwana chisiŵililo (chakwasiŵilila kuti akaujila soni ku duniya), kwikanila lisiku lyatachiukulidwa (m’malembe).');
INSERT INTO "silika" VALUES(23,101,'23:101','Sano pandema jalichigombedwa lipenga, basi pachiŵa pangali ulongo chilikati chao (watachitwanjilaga), nambo soni ngasawusyana (ligongo lyaipotesi nnope).');
INSERT INTO "silika" VALUES(23,102,'23:102','Sano ŵatiukasitope n’dingo wao (wa yambone, ni kwepepala yakusakala), basi ŵanganyao ni ŵatachiŵa ŵakupunda.');
INSERT INTO "silika" VALUES(23,103,'23:103','Nambo ŵatiukajepepale n’dingo wao (wa yambone, ni kusitopa yakusakala), basi ŵanganyao ni aŵala ŵalipelelwekasyisye achimisyene, takaŵe ŵandamo mu Jahannama.');
INSERT INTO "silika" VALUES(23,104,'23:104','Moto tiukachome ngope syao soni ŵanyao mwalakwemo takaŵe chechena! (Aku sili sipotweche ngomo syao lugomo lwakwinani gunda chilikati chantwe, lwapasi gunda nchisou).');
INSERT INTO "silika" VALUES(23,105,'23:105','(Takawusyidweje kuti): “Ana ma Ȃya Gangu nganigasoomedwaga kukwenu, basi jenumanja ni mwaliji nkugakanila?”');
INSERT INTO "silika" VALUES(23,106,'23:106','Ŵanganyao takajile: “Ambuje ŵetu! Ungalumbana wetu watukombwele mwanti twaliji ŵandu ŵakusokonechela.”');
INSERT INTO "silika" VALUES(23,107,'23:107','“Ambuje ŵetu! Tukoposyani kwalakwe (ku motokuno nituujile ku duniya kuti tukapanganye yambone), sano naga tukaujile soni (kupanganya yangalumbana), basi chisimu uwwe chitukaŵe ŵaalitenda lupuso.”');
INSERT INTO "silika" VALUES(23,108,'23:108','(Allah) takajile: “Tamani mwalakwemo mwakunyosyeka, soni nkasimumbechetekasya.”');
INSERT INTO "silika" VALUES(23,109,'23:109','“Chisimu chene lyapali likuga mwa achikapolo Ŵangu lyalyaliji nkuŵecheta kuti: ‘Ambuje ŵetu! Uwwe tukulupilile, basi tukululuchilani, ni kututendala chanasa, soni Mmwe ni Ŵambone nnope kwapunda akutenda chanasa wosope.”');
INSERT INTO "silika" VALUES(23,110,'23:110','“(Nambo) basi jenumanja mwaliji nkwatenda chipongwe ŵanganyao mwanti mpaka ŵan’diŵesye kungumbuchila Une, soni ŵanyao jenumanja mwaliji nkwaseka.”');
INSERT INTO "silika" VALUES(23,111,'23:111','“Chisimu Une lelo nalipile ŵanganyao (Mbepo) ligongo lyakupilila kwao; chisimu ŵanyao ni ŵali ŵakupunda.”');
INSERT INTO "silika" VALUES(23,112,'23:112','(Allah) takajile: “Ana yaka ilingwa yamwatemi ŵanganyammwe pachilambo (cha duniya)?”');
INSERT INTO "silika" VALUES(23,113,'23:113','Ŵanganyao takajile: “Twatemi usiku umo kapena chipande cha usiku, basi mwawusyani akuŵalanjila.”');
INSERT INTO "silika" VALUES(23,114,'23:114','(Allah) takajile: “Nganintama jenumanja ikaŵeje panandipe, ingaŵe kuti ŵanganyammwe mwaliji nkumanyilila (ya ilagasyo ya ku Akhera, nganganimpanganya yangalumbana mukandaŵi kannono kamwatemi ku duniya).”');
INSERT INTO "silika" VALUES(23,115,'23:115','“Ana nkuganichisya ŵanganyammwe kuti Uwwe tun’gumbile wambape (ŵangali masengo gakututendela Uwwe), soni kuti jenumanja Kukwetu ngasim’bujila?”');
INSERT INTO "silika" VALUES(23,116,'23:116','Basi atukwiche kusyene Allah, Nchimwene Jwakuonaonape. Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo, M’mbuje jwa Arishi Jakuchimbichika kwannope (Chindanda Chaulungu).');
INSERT INTO "silika" VALUES(23,117,'23:117','Soni jwatam’bende pampepe ni Allah nnungu jwine, jwanganakola najo umboni (wakuŵajilwa kum’benda), basi nikuti chiŵalanjilo chakwe chili kwa M’mbujegwe. Chisimu makafili nganigaŵa gapundile.');
INSERT INTO "silika" VALUES(23,118,'23:118','Soni jilani mmwe (Muhammadi صلى الله عليه وسلم): “Ambuje ŵangu! Kululukani soni tendani chanasa, soni Mmwe ni Ŵambone nnope kwapunda akutenda chanasa wosope.”');
INSERT INTO "silika" VALUES(24,1,'24:1','(Jalakweji ni) Sura jatujitulwisye ni kuŵikamo malamusi, soni tutulwisyemo ma Ȃya gakupikanika kuti jenumanja m’be ŵakumbuchila.');
INSERT INTO "silika" VALUES(24,2,'24:2','Jwankongwe jwachigwagwa ni jwannume jwachikululu basi jenumanja munnyesyani jwalijose mwa jamanjao mipichi jakwana lichila (100), soni chikantesya chanasa ligongo lyaŵaŵilio pa (kupitisya) dini ja Allah (malamusi galamwile pa ŵanyao), nam’baga jenumanja nkunkulupililaga Allah ni Lisiku Lyambesi. Soni likuga lya ŵakulupilila lilolele ilagasyo yaoyo. ');
INSERT INTO "silika" VALUES(24,3,'24:3','Jwannume jwachikululu jwangalombela ikaŵeje jwankongwe jwachigwagwa (n’jakwe) kapena jwakuwanganya (Allah ni isanamu), nombenajo jwankongwe jwachigwagwa jwangalombedwa ikaŵeje ni jwannume jwachikululu (n’jakwe) kapena jwakuwanganya (Allah ni isanamu), ni iŵele haraam yalakweyo kwa ŵakulupilila. ');
INSERT INTO "silika" VALUES(24,4,'24:4','Sano aŵala ŵaakwalambuchisya ugoni achimmasyeto ŵakuligosa, kaneko ni nganaika nasyo mboni ncheche, basi mwanyesyani mipichi makumi nsano nagatatu (80), soni ngasiwwukunda umboni wao mpaka kalakala, soni ŵanganyao niŵali ŵakunyosya.');
INSERT INTO "silika" VALUES(24,5,'24:5','Ikaŵeje aŵala ŵatesile toba panyuma pa yalakweyo ni kolosya (itendo yao), basi chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(24,6,'24:6','Sano aŵala ŵaakwalambuchisyaga ugoni achiŵammakwao, ni nganakola mboni ikaŵeje achimisyene, basi umboni wa jumojwao ni maumboni gakwana ncheche (gatapeleche) pakunnumbila Allah, kuti chisimu jwalakwejo juli mwa akuŵecheta yakuona.');
INSERT INTO "silika" VALUES(24,7,'24:7','Sano (umboni) wansano (julumbile) kuti malweso ga Allah gaŵe pa jwalakwejo najuŵaga juli mwa akulambusya.');
INSERT INTO "silika" VALUES(24,8,'24:8','Sano (jwammasyeto) chijutyosyeledwe ilagasyo (yakusomedwa maganga mpaka kuwa) naga juli jupeleche maumboni gakwana ncheche pakunnumbila Allah kuti chisimu ŵalakweo (ŵankwakweo) ali mwa akulambusya.');
INSERT INTO "silika" VALUES(24,9,'24:9','Sano (umboni) wansano (julumbile) kuti gasabu ja Allah jiŵe pa jwalakwe (jwammasyetojo) naŵaga (ŵankwakweo) ali mwa akuŵecheta yakuona.');
INSERT INTO "silika" VALUES(24,10,'24:10','Soni (jenumanja nkalasile) ungaŵaga umbone wa Allah pa jenumanja ni chanasa Chakwe, ni kuti soni Allah ni Jwakupochela nnope toba, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(24,11,'24:11','Chisimu aŵala ŵaiche nao unami (wakwalambuchisya ugoni mama Aisha رضي الله عنهم ŵankwakwe Muhammadi صلى الله عليه وسلم) uli nkutula wa mwa jenumanja. Ngasiyyiganichisya yalakweyo kuti ili yakusakala kukwenu, nambo yalakweyo ili yambone kukwenu. Akwete kupata mundu jwalijose mwajamanjao sambi syapanganyisye. Sano ajula jwalitwichile ntambala wekulungwa wayalakweyo mwa ŵanyao, yakwe ichiŵa ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(24,12,'24:12','Nambi uli pandema jamwajipikene (abaliji) ŵakulupilila ŵachilume ni ŵachikongwe nganiŵaganichisya achimijao yambone, ni kuŵecheta kuti: “Walakweu ni unami wakuonechela?”');
INSERT INTO "silika" VALUES(24,13,'24:13','Nambi uli nganaika nasyo mboni ncheche? Sano pandema janti nganaika nasyo mboni, basi nikuti ŵanganyao kwa Allah ali ŵaunami.');
INSERT INTO "silika" VALUES(24,14,'24:14','Soni ungaŵaga umbone wa Allah pa jenumanja ni chanasa Chakwe pa duniya ni ku Akhera, ingankwaiye ilagasyo yekulungwa nnope pa yamwaliji nkuijijilichila.');
INSERT INTO "silika" VALUES(24,15,'24:15','Ndema jamwaliji nkuupochelangana (unamio) ni ndimi syenu, ni kuŵechetaga ni ngamwa syenu yanganinkola nayo umanyilisi, ni mwaiganichisyaga kuti yamwana kutendaga yalakweyo kwa Allah ili yekulungwa nnope.');
INSERT INTO "silika" VALUES(24,16,'24:16','Nambi uli soni pandema jamwajipikene (abaliji) nganim’becheta kuti: “Ngaikuŵa yakuŵajilwa kukwetu kuti tuŵecheteje yalakweyi, kuswejela ni Kwenu (Mmwe Ambuje)! Awu ni unami wekulungwa nnope.”');
INSERT INTO "silika" VALUES(24,17,'24:17','Allah akun’jamuka kuti ngasim’bujauja soni kutenda yanti mpela yele mpaka kalakala nam’baga jenumanja n’di ŵakulupilila (kusyene).');
INSERT INTO "silika" VALUES(24,18,'24:18','Soni Allah akusalichisya kukwenu ma Ȃya, soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(24,19,'24:19','Chisimu aŵala ŵaakunonyelwa kuti yausakwa ijenele mu ŵandu ŵakulupilile, akwete kupata ŵanganyao ilagasyo yakupoteka nnope paduniya ni ku Akhera, sano Allah ni jwaakumanyilila, nambo ŵanganyammwe ngankumanyilila.');
INSERT INTO "silika" VALUES(24,20,'24:20','Soni (angampele ilagasyo mwachitema) ungaŵaga umbone wa Allah pa jenumanja ni chanasa Chakwe, ni kuti Allah ni Mpole nnope, Jwachanasa channope.');
INSERT INTO "silika" VALUES(24,21,'24:21','E jenumanja ŵankulupilile! Ngasinkuyaga sajo sya shetani, sano jwatakuye sajo sya shetani, basi (tasokonechele), chisimu jwalakwe akuchisyaga yausakwa ni yangalumbana. Soni ungaŵaga umbone wa Allah pa jenumanja ni chanasa Chakwe, nga nganaswejela mwa jenumanja jwalijose mpaka kalakala, nambo kuti Allah akunswejesyaga jwansachile, soni Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(24,22,'24:22','Soni achimisyene ukoto mwa jenumanja ni kusimana chenene (pa chipanje) akalumbila yakuleka kwapa (chikamuchisyo) ŵaulongo, ŵakulaga ni akusama petala lya Allah, nambo akululuche ni kuipwanyila (yalakweyo). Ana ngankunonyelwa yanti Allah ankululuchile ŵanganyammwe? Sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(24,23,'24:23','Chisimu aŵala ŵaakwalambuchisya ugoni achimmasyeto ŵakuligosa, ŵangali nganisyo (syaugoni), ŵakulupilila, alweeseedwe pa duniya ni ku Akhera kwakwe, soni akwete kupata ŵanganyao ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(24,24,'24:24','Lisiku lyasichitendela umboni ndimi syao, makono gao ni ngongolo syao pa yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(24,25,'24:25','Lyele lisikulyo Allah tachalipila ŵanganyao mwakwana malipilo gao gasyesyene, ni tachimanyilila soni kuti Allah ni Jwakuona kwakuonechela.');
INSERT INTO "silika" VALUES(24,26,'24:26','Achakongwe ŵangalumbana akuŵajilwa kuŵa ŵa achalume ŵangalumbana, nombe achalume ŵangalumbana akuŵajilwa kola achakongwe ŵangalumbana, sano achakongwe ŵambone akuŵajilwa kuŵa ŵa achalume ŵambone, nombe achalume ŵambone akuŵajilwa kola achakongwe ŵambone, ŵele ŵanyao (achalume ni achakongwe ŵamboneo) aŵambasidwe ku agala (maloŵe gakunyalaya) gaakuŵecheta (ŵandu). Akwete kupata ŵanganyao chikululuko kwisa soni lisiki lyakuchimbichika (lya ku Mbepo).');
INSERT INTO "silika" VALUES(24,27,'24:27','E jenumanja ŵankulupilile! Kasin’jinjilaga m’majumba ganganigaŵa majumba genu, ni ikaŵeje n’di mpoposisye kaje ni kwapa salamu achimisyene majumbago, yalakweyo ni yaili yambone kukwenu kuti m’be ŵakumbuchila (umbone wa yalakweyo ni nyyitendeje).');
INSERT INTO "silika" VALUES(24,28,'24:28','Sano naga nkansimana jwalijose mwalakwemo basi nkajinjila mpaka n’di mpedwile lusa. Soni naga nsalilidwe kuti: “Ujilani.” Basi ujaani, yalakweyo ni yakuswejela nnope kukwenu. Soni Allah ichindu yankutenda aŵele Jwaimanya chenene.');
INSERT INTO "silika" VALUES(24,29,'24:29','Chipaŵe pangali ulemwa kukwenu kwinjila majumba gangali ŵaakutama, mwalakwemo nimuli mwana yakunkamuchisya yenu. Sano Allah ni akumanyilila yankulosya palangulangu ni yankusisa.');
INSERT INTO "silika" VALUES(24,30,'24:30','Mwasalilani ŵakulupilila ŵachilume kuti ateteŵasyeje meso gao (pangalola yakanyisyidwa), ni kuti soni augoseje utukani wao. Yalakweyo niyaili yakuswejela nnope kukwao. Chisimu Allah ni Nkusimanya abali syosope sya yaakutenda.');
INSERT INTO "silika" VALUES(24,31,'24:31','Mwasalilani soni ŵakulupilila ŵachikongwe kuti ateteŵasyeje meso gao (pangalola yakanyisyidwa), ni kuti soni augoseje utukani wao. Soni ngasalosyaga (papaswela) ipeto yao ikaŵeje yaionechele muyalakweyo (mpela ngope ni magasa), ni aliunichileje mipango jao pachanya pa mitwe, ngosi ni idali yao, soni ngasalosya ipeto yao ikaŵeje kwa achiŵammakwao, kapena achatati ŵao, kapena achakwegwao ŵachilume, kapena ŵanache ŵao ŵachilume, kapena ŵanache ŵachilume ŵa achiŵammakwao, kapena achachimwene ŵao, kapena ŵanache ŵachilume ŵa achachimwene ŵao, kapena ŵanache ŵachilume ŵa achalumbu ŵao, kapena achakongwe achimijao (ŵachisilamu), kapena ŵaapatidwe ni makono gao gakun’dyo (mateeka), kapena ŵakuya (ŵakwatumichila achakongwe) ŵali achalume (ŵachekulupe) ŵangali chisako (chakwasaka achakongweo), kapena ŵanache ŵachilume ŵanganaukopochele utukani wa achakongwe. Soni akasaputasyaga (pasi) ngongolo syao kuti imanyukuche yaakusisa mu ipeto yao. Sano tendani toba kwa Allah wosopeŵene jenumanja ŵakulupilila kuti m’be ŵakupunda.');
INSERT INTO "silika" VALUES(24,32,'24:32','Soni galombesyani mauli ga mwa jenumanja (alukosyo) kwisa soni ŵali ŵambone mwa achikapolo ŵenu ŵachilume ni achikapolo ŵenu ŵachikongwe, naaŵaga ŵakulaga Allah tiŵasichilisye kuumila mu ukoto Wakwe. Sano Allah ni Jwakwanila kusyene (ku iwumbe Yakwe), Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(24,33,'24:33','Soni aligose (ku yansese) aŵala ŵangakupata yakulombelela mpaka Allah ŵasichilisye kuumila mu ukoto Wakwe. Sano aŵala ŵaakusachilila kulembeledwa (ulukosyo) mwa aŵala ŵagapatile makono genu gakun’dyo (achikapolo ŵachilume ni ŵachikongwe), basi mwalembelani (kuti aliombole) nam’baga jenumanja nkuumanyilila umbone uliose mwa ŵanganyao, soni mwapani ŵanganyao mu chipanje cha Allah achila champele. Soni ngasin’gakanganichisya mateeka genu kutenda chigwagwa galakwe gali nkusaka kuligosa kuti mpate ichindu yakumala ya umi waduniya. Sano jwatagakanganichisye (mateekago), basi chisimu Allah panyuma pagakanganichisya juli Jwakululuka kusyene (mwanti tagakululuchile matekago ligongo gatekanganisyidwa), Jwachanasa channope (tagatendele chanasa).');
INSERT INTO "silika" VALUES(24,34,'24:34','Soni chisimu pamasile patutulwisye kukwenu ma Ȃya gakusalichisya, ni chilandanyo cha aŵala ŵaŵapite paujo penu, kwisa soni chamuko kwa ŵakun’jogopa (Allah).');
INSERT INTO "silika" VALUES(24,35,'24:35','Allah ni Nkulangusya kumawunde ni petaka. Chilandanyo cha lilanguka Lyakwe (muntima mwa mundu jwakulupilila) chili chisawu chango (chipowo cha pepupa) chachiŵichidwe nkati mwakwemo nyali, nyalijo jili mwigalasi, ligalasilyo lili mpela ndondwa jakumelemenda, (nyalijo ni) jikolesyedwa (ni mauta gakuumila) ku chitela chaupile cha nzaituni, wanganiuŵa upande wakundaŵi kapena ŵakwigulo (ukum’balilaga lyuŵa nnope ndaŵi syosope kutandila pakopoka mpaka pakapila, kwaligongo lyele mauta gakwe gali gambone nnope), mauta gakwe gakusigalaga panandi kuti gakoposye lilanguka namuno moto nkaniugakwaye. Lilanguka pachanya pa lilanguka! Allah akun’jongolelaga ku lilanguka Lyakwe jwansachile. Sano Allah ni akupeleka ilandanyo kwa ŵandu. Soni Allah chindu chilichose aŵele Jwachimanya chenene.');
INSERT INTO "silika" VALUES(24,36,'24:36','M’majumba (misikiti) gaachisisye Allah kuti ganyakulidwe (pakugaswejesya ni kugachimbichisya) ni kuti likolanjidwejemo Lina Lyakwe; akunswejesyaga Jwalakwe (Allah) mwalakwemo kundaŵi ni kwigulo,');
INSERT INTO "silika" VALUES(24,37,'24:37','Achalume, ŵangakukwapaga chipaka kusuma ni kusumisya (malonda) chakulechela kunkumbuchila Allah (muntima ni palulimi), ni kwimika Swala mwakolosya nambo soni kutola Zaka, akasalijogopaga Lisiku lyajichigalaukagalauka mitima mulyalakwelyo ni meso, (ligongo lyakogoya nnope kwa ipotesi yakwe).');
INSERT INTO "silika" VALUES(24,38,'24:38','Kuti akaalipile ŵanganyao Allah yambone yaŵapanganyisye ni kuja kwajonjechesya soni kuumila mu ukoto Wakwe. Soni Allah akumpaga lisiki jwansachile mwangaŵalanjila.');
INSERT INTO "silika" VALUES(24,39,'24:39','Sano aŵala ŵaakanile, itendo yao (yaakuionaga mpela yambone) ili chisawu lunyisinyisi lwaluli mulipululu (nkuli wambembembe). Jwanjota akasaluganichisyaga kuti mesi. Mpakana pati ali aluichilile ngakasachisimanapo chilichose (nombe najo kafili pajuchiiŵichilila itendo yakwe yambone pa Kiyama ngasiyinkamuchisya), ni tachinsimana Allah kwalakweko jwatachinkwanilichisya chiŵalanjilo chakwe. Soni Allah ni jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(24,40,'24:40','Kapena mpela chipi chachili m’mbwani jakusokoka jajikum’bunichila matumbela, pachanya pakwepo nikuŵa pana matumbela gane, pachanya pakwe (pamatumbela gaŵiligo) nikuŵa pana lipundugulu. (Nigamba kuŵa) chipi chine pachanya pachijakwe! Mwanti (mundu) pati akopwesye nkono wakwe nganaŵa aŵandichile kuuwona (ligongo lyakutotekana kwachipicho). Sano jwanganam’bichila Allah lilanguka (lyachongoko), basi jwalakwejo ngaŵa nkola lilanguka (ngaŵa nkongoka).');
INSERT INTO "silika" VALUES(24,41,'24:41','Ana ngankuiona yanti Allah ikunswejesya Jwalakwe yosope yaili kumawunde ni petaka kwisa soni ijuni ili chisapangulile (mapapiko gakwe paguluka)? Chilichose pamasile pachimanyilile Swala jakwe ni kaswejesye kakwe. Soni Allah ni Nkumanyilila chenene yaakutenda.');
INSERT INTO "silika" VALUES(24,42,'24:42','Soni uŵele ŵa Allah uchimwene wa kumawunde ni petaka, soni kwa Allah pe ni kwakuujila.');
INSERT INTO "silika" VALUES(24,43,'24:43','Ana ngakuiona yanti Allah akwendesya mawunde gawula mwambolembole kaneka ni kugalumbikanya yalumo, kaneko ni kugaŵika maunjilimaunjili, basi ni nkujionaga wula jili nkukopoka chilikati chakwecho, soni akutulusyaga kuumila kwinani - m’matumbi (gamatalila) - mwelemo (akutulusyaga) matalila, basi ni akasamputa nago jwansachile ni kugaŵambasya ku jwansachile. Kumulika kwa kumesya kwakwe kukusigalaga panandi kuti kutyosye kulola.');
INSERT INTO "silika" VALUES(24,44,'24:44','Allah akuchigalusyaga chilo ni muusi (panyuma pa chilo ukwikaga muusi, soni panyuma pa muusi chikwikaga chilo). Chisimu mu yalakweyo mwana kuchetelela kusyene kwa ŵakwete meso (gakulolechesya mwa umanyilisi).');
INSERT INTO "silika" VALUES(24,45,'24:45','Soni Allah agumbile chiumbe chilichose chakuulukutaga kuumila m’mesi. Sano ine mwa yalakweyo ikwenda chamakuku, soni ine mwa yalakweyo pana yaikwenda ni ngongolo siŵili, nambo soni ine mwa yalakweyo pana yaikwenda ni ngongolo ncheche. Allah akasagumba chaakusaka. Chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(24,46,'24:46','Chisimu pamasile patutulwisye ma Ȃya gakusalichisya. Soni Allah akasan’jongolelaga jwansachile kwitala lyagoloka.');
INSERT INTO "silika" VALUES(24,47,'24:47','(Achinakunaku) ni akutiji: “Tukulupilile mwa Allah ni Ntenga صلى الله عليه وسلم soni tukundile.” Kaneka ni likasagalauka likuga line mwa ŵanganyao panyuma pa yalakweyo. Sano ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(24,48,'24:48','Soni pati aŵilanjidwe kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم kuti ŵajilanye chilikati chao, pajele ndemajo likuga line mwa ŵanganyao likasaŵa lyakusyulula (naga alimanyi achimisyene kuti ŵakulemwa).');
INSERT INTO "silika" VALUES(24,49,'24:49','Nambo naga yakuona (panganijo) ili ni ŵanganyao, akasam’bichililaga (Ntengajoصلى الله عليه وسلمmwachitema) aku ali ŵakumanya.');
INSERT INTO "silika" VALUES(24,50,'24:50','Ana m’mitima mwao mutekuŵa mwana ulwele, kapena akukaichila, kapena akogopa kuti Allah ni Ntenga Jwakwe صلى الله عليه وسلم atakwatenda lupuso (ndema jakwajilanya)? (Ngwamba), nambo ŵanganyao ni ŵali ŵaalitenda lupuso.');
INSERT INTO "silika" VALUES(24,51,'24:51','Chisimu kuŵecheta kwa ŵakulupilila pati aŵilanjidwe kwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم kuti ŵajilanye chilikati chao; kuŵeje kwanti ajileje: “Tupikene soni tukundile.” Soni ŵanganyao ni ŵataŵe ŵakupunda.');
INSERT INTO "silika" VALUES(24,52,'24:52','Sano jwatankunde Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni kun’jogopaga Allah ni kuligosaga Kukwakwe, basi ŵanganyao ni ŵataŵe ŵakupunda.');
INSERT INTO "silika" VALUES(24,53,'24:53','Sano (achinakunakuo) ni akuŵa ali nkunnumbila Allah kulumbila kwao kwamachili kuti chisimu naga n’di mwachisyisye (kopoka wakungondo) mpaka akopoche kwene. Jilani: “Nkalumbila. Kunda kwenu kwachimanyichile (kuti kwaunami). Chisimu Allah ni Nkusimanya abali syosope sya yankutenda.”');
INSERT INTO "silika" VALUES(24,54,'24:54','Jilani: “Mumpikanilani Allah ni mumpikanile soni Ntenga صلى الله عليه وسلم, nambo naga nkugalauka (kuleka yele), basi nikuti jwalakwe (Ntengajo) tiyinkwaye yaatwichidwe (yakulungusya utenga), nombe jenumanja tiyinkwaye yantwichidwe (yakunda utenga), nambo naga nkunkunda jwalakwe nikuti tin’jongoche. Sano kwa Ntenga صلى الله عليه وسلم kwangali iliyose ikaŵeje kulungusya (utenga) mwakupikanika.');
INSERT INTO "silika" VALUES(24,55,'24:55','Allah ŵaapele chilanga aŵala ŵaakulupilile mwa jenumanja ni kutendaga yambone chanti; chisimu chene chiŵatende kuŵa ŵawinjila pachilambo (m’malo mwa akulamula ŵaleloŵa) mpela yaŵatite pakwatenda kuŵa ŵawinjila aŵala ŵapaujo pao, soni chisimu chene tiŵalimbanganyichisye dini jao ajila jaŵasagulile (Usilamu), ni tiŵatindanichisye kwene panyuma pa woga wao pakwapa ntunjelele, (mutalonjelele kuŵa) ali nkungalagatila Une, ni ngaamwanganyaga ni chilichose. Sano ŵatakane panyuma pa yele; basi ŵanganyao ni ŵataŵe ŵakunyosya.');
INSERT INTO "silika" VALUES(24,56,'24:56','Soni jimikani Swala mwakolosya ni kutolaga Zaka, soni mumpikanile Ntenga صلى الله عليه وسلم kuti ntendeledwe chanasa.');
INSERT INTO "silika" VALUES(24,57,'24:57','Ngasimwaganichisya ata panandi aŵala ŵaakanile kuti mpaka alepelekasye pachilambo (kunnepelekasya Allah kwapa ipotesi), soni mauto gao ni ku Moto. Soni kaje kusakala nnope malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(24,58,'24:58','E jenumanja ŵankulupilile! Ampoposyeje aŵala ŵagapatile makono genu gakun’dyo (achikapolo ŵachilume ni ŵachikongwe ŵakwe) kwisa soni aŵala ŵanganaiche pakulisagamilila mwa jenumanja (alukosyo pa) ndema sitatu; nkanijiŵe swala ja Fajr, ni pandema jankuwulaga iwaalo yenu muusi (kuti nlijongole), kwisa soni panyuma pa swala ja Isha, (asi ni) ndema sitatu sya chiŵako chenu. Pangali ulemwa kukwenu kapena kukwao panyuma (pandema) syalakwesi (kwinjila mwangali kupoposya). (Ŵanganyao ali) ŵakunsyungulasyungula ŵanganyammwe, ŵane mwa jenumanja pa (kwatumichila) ŵane. Iyyoyopeyo Allah akusalichisya kukwenu ma Ȃya. Soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(24,59,'24:59','Soni ŵanache mu jenumanja (achingosyo) pati ayiche ndema jakulisagamilila, basi apoposyeje (ndema syosope) yaakutiji pakupoposya aŵala ŵapaujo pao (ŵachikulile ŵao). Iyyoyopeyo Allah akusalichisya kukwenu ma Ȃya Gakwe. Soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(24,60,'24:60','Soni achakongwe ŵajumu (ligongo lya uchekulu) aŵala ŵangakwembecheya kulombedwa, basi chipaŵe pangali ulemwa kukwao kuliunukula nguo syao (syapachanya) nambo mwangali kulosya ipeto (yankati mpela nkufu ibangili ni ine). Nambo naga ali atite aligose (ni kuleka kuliunukula nguo syao syapachanya) mpaka iŵe yambone nnope kukwao. Sano Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(24,61,'24:61','Pangali chakanya pa jwangalola, pangali chakanya soni pa jwannemale, pangali chakanya soni pa jwakulwala, kapena soni pa mwachimisyene kuti n’dyeje m’majumba mwenu, kapena m’majumba ga achatati ŵenu, kapena m’majumba ga achachikulu ŵenu, kapena m’majumba ga achachimwene ŵenu, kapena m’majumba ga achachemwali ŵenu, kapena m’majumba ga achibaba ŵenu ŵanandi kapena ŵakulungwa, kapena m’majumba ga achibaba ŵenu ŵachikongwe, kapena m’majumba ga achin’jomba ŵenu, kapena m’majumba ga achimama ŵenu ŵanandi kapena ŵakulungwa, kapena ŵamwasunjile makiyi gao, kapena ajenu. Pangali chakanya pa jenumanja kuti n’dyeje nganya kapena jikajika. Sano pati nchijinjilaga m’majumba, basi peleganani salamu mwachimisyene (pakuŵecheta kuti: As-Salâm ‘Alaikum – Ntendele uŵe kukwenu) chilamuchilo chakuumila kwa Allah chachili chaupile soni chambone. Iyyoyopeyo Allah akusalichisya kukwenu ma Ȃya kuti jenumanja mmanyilile.');
INSERT INTO "silika" VALUES(24,62,'24:62','Chisimu ŵakulupilila (ŵasyesyene), ni aŵala ŵaakulupilile mwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم, soni pandema jaakuŵa najo imo (Ntengajo) pa chindu chakwaya wosope ŵene, ŵangatyoka mpaka akam’bende kaje lusa. Chisimu aŵala ŵaakum’benda lusa (mmwe Muhammadi صلى الله عليه وسلم), ŵanganyao ni aŵala ŵaakukulupilila (kusyene) mwa Allah ni Ntenga Jwakwe صلى الله عليه وسلم. Sano pati am’bendile lusa lwa (kuja kutenda) ine mu ipaka yao, basi mumpani lusa jumunsachile mwa ŵanganyao, ni mwaŵendeleje ŵanganyao chikululuko kwa Allah, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(24,63,'24:63','Kam’bilanje Ntenga صلى الله عليه وسلم chilikati chenu ngasinkatenda mpela yankuti pakuŵilangana mwachimisyenepe. Chisimu Allah akwamanya aŵala ŵaakugwala mwa jenumanja mwakulijuya (kuja kutenda ipaka yao mwangali kuŵenda lusa kwa Ntenga صلى الله عليه وسلم). Basi alijogopele aŵala ŵaakutindana ni chilamusi chakwe (sunna sya Ntenga صلى الله عليه وسلم) kuti gatakwika mugapatile makungu (pa duniya pano) kapena kuja kwapata ilagasyo yakupoteka nnope (ku Akhera).');
INSERT INTO "silika" VALUES(24,64,'24:64','Manyililani yanti! Chisimu yosope yaili kumawunde ni petaka ili ya Allah. Chisimu Jwalakwe jukumanyilila kaŵe kenu, ni (jukumanyilila soni) Lisiku lyatachiuchisyidwa ŵanganyao Kukwakwe, basi ni tachatagulila yaŵapanganyisye, soni Allah chindu chilichose aŵele Jwachimanya chenene.');
INSERT INTO "silika" VALUES(25,1,'25:1','Anyakwiche (pa uchimbichimbi ni upile) Ajula (Allah) jwaatulwisye chilekanganyo (cha yakuona ni yaunami jajili Qur’an) kwa kapolo Jwakwe (Muhammadi صلى الله عليه وسلم) kuti juŵe nkuitetela iwumbe yosope.');
INSERT INTO "silika" VALUES(25,2,'25:2','(Allah) Ajula jwauli Ŵakwe uchimwene wa kumawunde ni petaka, soni nganalitendela mwanache, ni nganakola soni jwakamusyangana najo mu uchimwene, soni agumbile chindu chilichose ni kuchipimila mwakulandanila ni chipiimo chakwe.');
INSERT INTO "silika" VALUES(25,3,'25:3','Yeti ŵanganyao (achimakafili) ni alitendele mmalo mwa Jwalakwe (Allah) milungu jangaŵa nkugumba chilichose kutendaga jalakwe ni jajigumbidwaga, soni jangali ukombosi wakulityochesya jisyene isausyo namuno kulikamuchisya, soni jangali ukombosi ŵakutendekasya chiwa namuno kupeleka umi kapena kwimusya ŵawe m’malembe.');
INSERT INTO "silika" VALUES(25,4,'25:4','Aŵala ŵaakanile ni akutiji: “Nganijiŵatu aji (Qur’an) ikaŵeje unami waawusepelele (Muhammadi صلى الله عليه وسلم), ni ankamuchisye pa wele (unamiu) ŵandu ŵane.” Basi ŵanganyao pamasile paayiche nalo lupuso ni unami (pa yakuŵecheta yaoyo).');
INSERT INTO "silika" VALUES(25,5,'25:5','Soni ŵanganyao ni akutiji: “(Aji Qur’an jiŵele) adisiadisi sya ŵandu ŵandanda sya asilembesye, basi syalakwe ni sikuŵa sinankusyoomwa kukwakwe kundaŵi ni kwigulo (kuti asijinjisye chimoyo).”');
INSERT INTO "silika" VALUES(25,6,'25:6','Jilani: “Jele (Qur’aniji) ajitulwisye Ajula (Allah) jwaakumanyilila asili syakumawunde ni petaka. Chisimu Jwalakwe juŵele Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(25,7,'25:7','Soni ŵanganyao ni akutiji: “Ligongo chichi aju ntengaju jukulyaga yakulya ni kwendagajendaga m’misika (mutukutendela uwwe)? Ligongo chichi ngantuluchila lilaika kuti liŵe lyakutetela pamo ni jwalakwe?”');
INSERT INTO "silika" VALUES(25,8,'25:8','“Kapena (ligongo chichi) ngachikuponyedwa kukwakwe chimatiilo (chambiya) kapena kola litimbe ni alyeje mwalakwemo?” Sano ŵandu ŵalupuso ni akutiji: “Jenumanja ngankunkuya ikaŵeje mundu jwannojedwe.”');
INSERT INTO "silika" VALUES(25,9,'25:9','Nnole yaakuti pakunnandanyichisya mmwe ilandanyo, basi ni asokonechele, mwanti nganaŵa apakombwele (kupata) litala (lyagoloka).');
INSERT INTO "silika" VALUES(25,10,'25:10','Anyakwiche (pa uchimbichimbi ni upile) Ajula (Allah jwanti) naga ali asachile, mpaka am’bichile yambone nnope kumpunda yalakweyo – (tachim’bichila) Matimbe gasikujilima cha pasi pakwe sulo, ni tachim’bichila soni Majumba gamakulungwakulungwa (ku Mbepo).');
INSERT INTO "silika" VALUES(25,11,'25:11','Nambo ŵanganyao akuŵa nkukanila ya Kiyama, ni twalinganyichisye ŵakanila ya Kiyama Moto Wakulapuka.');
INSERT INTO "silika" VALUES(25,12,'25:12','Ndema jatiukaaone ŵanganyao (Motowo) kuumila pamalo pakutalichila, takapikane kutokota kwakwe kwaukali kwisa soni kulilima.');
INSERT INTO "silika" VALUES(25,13,'25:13','Soni ndema jatachiponyedwaga mwalakwemo pamalo pakanganika ali ataŵidwe m’makongwa, palakwepo takachiŵilanjeje chonasiko (kuti bola agambe kuwa apumule).');
INSERT INTO "silika" VALUES(25,14,'25:14','(Takasalilidweje kuti): Ngasinchiŵilanga lelo chonasiko chimo, nambo nyyiŵilanje yonasiko yejinji.');
INSERT INTO "silika" VALUES(25,15,'25:15','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana yele (ilagasyoyo) ni yaili yambone kapena Litimbe Lyandamo lya ku Mbepo lyapedwile chilanga ŵandu ŵawoga (wakun’jogopa Allah)?” Chilikaŵe malipilo kwa ŵanganyao nambo soni malo gakuujila.');
INSERT INTO "silika" VALUES(25,16,'25:16','Chakakoleje ŵanganyao mwalakwemo chilichose chatakachisacheje aku ali ŵandamo. Achi ni chiŵele chilanga chakusimichika kwa Ambuje ŵenu chachikuŵendedwa (kuti achikwanisye, ni tachichikwanisya mwangalepeleka).');
INSERT INTO "silika" VALUES(25,17,'25:17','Soni lisiku lyatachasonganganya (achimakafili) ni aŵala ŵaŵaliji nkwagalagatila kunneka Allah (mpela Isa, Uzair ni Malaika), basi tachawusya kuti: “Ana jenumanja ni ŵamwasokonesye achikapolo Ŵanguŵa, kapena ŵanganyaŵa ŵasokonechele (achimisyenepe kuleka) litala (lyagoloka)?”');
INSERT INTO "silika" VALUES(25,18,'25:18','Ŵanganyao takajile: “Kuswejela ni Kwenu. Nganiyiŵa yakuŵajilwa kukwetu kuti tulitendele akutugosa ŵaliose ŵangaŵaga Mmwe (kuli uli kwachisya ŵandu yakutugalagatila)! Nambo kuti Mmwe mwasengwasisye ŵanganyao ni achatati ŵao mpaka ŵaliŵalile chamuko, mwanti ŵaliji ŵandu ŵakonasika.”');
INSERT INTO "silika" VALUES(25,19,'25:19','Basi mwele ni muchachinkaanila (akwagalagatila ŵenu) pa yankuŵecheta (kuti ŵanganyao milungu), basi ŵanganyammwe ngasinkombola kulityochesya (ilagasyo) namuno kupata chikamuchisyo. Sano ŵatatende lupuso mwa jenumanja (lwagalagatila chinepe changaŵaga Allah), tuuchimpa kupasya ilagasyo yekulungwa (ku Akhera).');
INSERT INTO "silika" VALUES(25,20,'25:20','Soni Uwwe nganitutumisyeje achimitenga ŵaliose paujo penu (mmwe Muhammadi صلى الله عليه وسلم), ikaŵeje chisimu ŵanganyao ŵaliji nkulya yakulya ni kwendagajendaga m’misika. Ni twatesile ŵane mwa jenumanja kuŵa mayeso kwa ŵane (mpela ŵakupata kuŵa mayeso kwa ŵakulaga) kuti ana chimpilile? Sano Ambuje ŵenu aŵele Ŵakulolechesya.');
INSERT INTO "silika" VALUES(25,21,'25:21','Sano aŵala ŵangakwembecheya yakusimangana Nowe ni akuti: “Ligongo chichi ngatutuluchila malaika (kuti gaŵe mitume kukwetu), kapena tum’bone M’mbuje gwetu (juchitendelaga umboni ya utume wa Muhammadi صلى الله عليه وسلم)?” Chisimu pamasile paalikwesyisye m’mitima mwao, ni anyosisye kunyosya kwekulungwa.');
INSERT INTO "silika" VALUES(25,22,'25:22','Lisiku lyatachigaona malaika kuchiŵa kwangali kusengwa lyele lisikulyo kwaakuleŵa, ni gachiŵecheta (malaikago kuti): “Kuŵele kwakusiŵilidwa kusyene (kwa jenumanja lelo kupata yambone).”');
INSERT INTO "silika" VALUES(25,23,'25:23','Ni tuuchiiŵichilila itendo (yambone) yaŵapanganyisye ni tuuchiitenda kuŵa luwundu lwakuwululusyidwa.');
INSERT INTO "silika" VALUES(25,24,'25:24','Ŵandu ŵa ku Mbepo lyele lisikulyo tachikola malo gambone nnope gakutamilichika ni malo gambone nnope gagusama.');
INSERT INTO "silika" VALUES(25,25,'25:25','Soni (kumbuchilani) lisiku lyalichipapuka liwunde ni kutendekasya lipundugulu, nambo soni achimalaika ni kutulusyidwa mwawinji.');
INSERT INTO "silika" VALUES(25,26,'25:26','Uchimwene wakuonaonape lyele lisikulyo uchiŵa wa (Allah) Jwaukoto wejinji, ni lichiŵa lisiku lyakusausya nnope kwa achimakafili.');
INSERT INTO "silika" VALUES(25,27,'25:27','Soni (kumbuchilani) lisiku lyatachililuma jwalupuso magasa gakwe ganagaŵili achitiji: “E nkongwene! Ningajigale litala imo ni Ntenga صلى الله عليه وسلم.”');
INSERT INTO "silika" VALUES(25,28,'25:28','“Ah kutapana kwangune! Nabolaga nga nganinantenda jwantijwanti kuŵa nsyoŵe.”');
INSERT INTO "silika" VALUES(25,29,'25:29','“Chisimu pamasile paasochelesye une kwakuleka Chikumbusyo (Qur’an) panyuma pakumbichilila, soni shetani aŵele jwakunneka mundu (ngankamuchisya pa ipotesi).”');
INSERT INTO "silika" VALUES(25,30,'25:30','Sano Ntenga صلى الله عليه وسلم ni jwatite: “E Ambuje ŵangu! Chisimu ŵandu ŵangu ajitesile aji Qur’an kuŵa chindu chakulechedwa (pangajisooma ni ngakuuya malamusi gakwe).”');
INSERT INTO "silika" VALUES(25,31,'25:31','Soni iyyoyopeyo twam’bichile ntume jwalijose m’magongo kuumila mwa akuleŵa, sano Ambuje ŵenu akukwanila kuŵa Nkongola nambo soni Nkukamuchisya.');
INSERT INTO "silika" VALUES(25,32,'25:32','Soni aŵala ŵaakanile ni akutiji: “Ligongo chichi Qur’an nganijintuluchila jwalakwe (Muhammadi صلى الله عليه وسلم) mwanakamo?” Iyyoyopeyo (tukujitulusya panandipanandi) kuti tuulimbanganye najo ntima wenu, ni tujitulwisye (kukwenu) mwakosoŵalila (pakujitulusya mwambolembole mu ipande yepanje chenene).');
INSERT INTO "silika" VALUES(25,33,'25:33','Soni ŵanganyao nganaŵa ayiche nacho kukwenu chitagu chilichose (chakumpinga nacho), ikaŵeje Uwwe chituŵiche nacho kukwenu chakuona (chakwanga chitagucho), ni kagopolanye kakwe kambone.');
INSERT INTO "silika" VALUES(25,34,'25:34','Aŵala ŵatachisonganganyisidwa (alinkuutidwa) chamakuku wa ku Jahannama, ŵanganyao ni ŵatachiŵa pamalo pakusakala nnope, nambo soni ni ŵakusokonechela nnope kwitala (lyagoloka).');
INSERT INTO "silika" VALUES(25,35,'25:35','Soni chisimu pamasile patwampele Musa Chitabu (Taurat), ni twam’bichile nkulugwe Haruna kuŵa nkunkamuchisya.');
INSERT INTO "silika" VALUES(25,36,'25:36','Ni twatite: “Jawulani ŵanaŵaŵilimmwe ku ŵandu aŵala ŵaayikaaniile isimosimo Yetu.” Basi ni twajonasile kusyene (panyuma pakwakanila achimitengao).');
INSERT INTO "silika" VALUES(25,37,'25:37','Nombe nao ŵandu ŵa Nuhu; ndema jaŵaakaaniile achimitenga, twamisisye (ni chigumula), ni twatesile kuŵa chilosyo kwa ŵandu. Sano ŵalupuso twalinganyichisye ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(25,38,'25:38','Nambo soni achina Adi, achina Samuda, achimisyene chisima kwisa soni mikutula jejinji ja chilikati chao.');
INSERT INTO "silika" VALUES(25,39,'25:39','Soni jwalijose (mwa ŵanganyao), twampele ilandanyo (kuŵa maumboni ni ijiiganyo), soni ŵaliose (mwa ŵanganyao) twajonasile kusyene (panyuma pakwakanila achimitenga ŵao).');
INSERT INTO "silika" VALUES(25,40,'25:40','Soni chisimu ŵanganyao (achimakafili ŵa pa Maaka m’maulendo gao gakuja ku Shami), akasaupitaga musi (wa Sodomu) awula wana kumputilaga ula jakusakala (jamaganga), ana ngaakasaŵaga ali nkuuona? Nambo kuti akuŵaga akakwembecheya yakwimuka m’malembe, (niligongo lyakwe ngaakukulupililaga).');
INSERT INTO "silika" VALUES(25,41,'25:41','Soni ndema jaakum’bona mmwe (Muhammadi صلى الله عليه وسلم) ngaakuntenda inepe ikaŵeje chipongwe (pakuŵecheta kuti): “Ana aju ni jwantumisye Allah kuŵa ntenga?”');
INSERT INTO "silika" VALUES(25,42,'25:42','“Jwaasigele panandi kuti atusochelesye kuleka milungu jetu, ingaŵaga kuti twapililile pa jalakwejo.” Sano ŵanganyao tachiika kumanyilila ndema jatachiiona ilagasyo kuti nduni jwakusokonechela nnope kwitala (lyagoloka).');
INSERT INTO "silika" VALUES(25,43,'25:43','Ana mum’bweni ajula jwaachitesile chisako chakwe kuŵa nnungu jwakwe? Ana mmwe ni mpaka mbe nkun’gosa jwakwe (kuti jukakuya isako yakwe).');
INSERT INTO "silika" VALUES(25,44,'25:44','Kapena nkuganichisya yanti ŵanganyao akupikana kapena kuitaga mulunda (yankwasalila)? Nganaŵatu ŵanganyao ikaŵeje ali mpela inyama yakulanga, nambo soni ŵanganyao ni ŵasokonechele nnope kwitala (lyagoloka kuipunda inyama yakulanga).');
INSERT INTO "silika" VALUES(25,45,'25:45','Ana nganimwaona Ambuje ŵenu yatite pakutambasya m’bulili (kutandila kutiŵila mpaka kopoka kwa lyuŵa)? Angasache angautesile kuŵa wakutamilichika (wangasukuna, ni kukaliji chilope kwangali muusi). Soni tulitesile lyuŵa kuŵa nkuulondolela (m’bulilio, panyuma pakopoka lyuŵa m’bulili ukasatanda kwipipa).');
INSERT INTO "silika" VALUES(25,46,'25:46','Kaneko ni tukasauwumbataga Kukwetu kuwumbata kwa panandipanandi (mpaka ukasamalaga lyuŵa lili liyiche pewuwo).');
INSERT INTO "silika" VALUES(25,47,'25:47','Soni Jwalakwe ni Ajula jwam’biichiile ŵanganyammwe chilo kuŵa chiwaalo (pakusiŵilila ilu yenu ni chipi chakwe), soni (ni am’biichiile) lugono kuŵa kupumula (kwa ilu yenu), ni awuŵisile muusi kuŵa ndema jakubalalikangana (kupita nchisosa lisiki).');
INSERT INTO "silika" VALUES(25,48,'25:48','Soni Jwalakwe ni Ajula jwaakasatumisyaga mbungo kuŵa chisengwasyo paujo pa ukoto Wakwe (wula nkanijigwe), ni tukasatulusya kuumila kwinani mesi gakulenjela.');
INSERT INTO "silika" VALUES(25,49,'25:49','Kuti ni galakwego tusyuchisye chilambo chachiwe, ni tumwechesye ine (mu iwumbe) yatwaigumbile (mpela) ilango kwisa soni ŵandu ŵajinji.');
INSERT INTO "silika" VALUES(25,50,'25:50','Soni chisimu Uwwe tukujigaŵanyaga (wulajo) chilikati chao kuti aŵeje ŵakumbuchila (ukoto Wetu), nambo ŵandu ŵajinji akukana (chikulupi) ikaŵeje ukafili (basi).');
INSERT INTO "silika" VALUES(25,51,'25:51','Soni tungasache tungatumisye m’musi uliose nkutetela (jwaojwaope).');
INSERT INTO "silika" VALUES(25,52,'25:52','Basi ngasimwajitika achimakafili (pa isako yao), nambo n’dimbane nao nijele (Qur’aniji) kulimbana kwekulungwa.');
INSERT INTO "silika" VALUES(25,53,'25:53','Soni Jwalakwe ni Ajula jwaasisimangenye mbwani siŵili, aji jamesi gakunon’ga gakumasya njota, najiji jamesi gachijete gakuŵaŵa. Ni juŵisile chilikati chakwecho uŵigo, ni chilekanganyo chakulekanganya kusyene.');
INSERT INTO "silika" VALUES(25,54,'25:54','Soni Jwalakwe ni Ajula jwaagumbile mundu kuumila m’mesi, ni am’biichiile ulongo ŵakupagwana ni waulombela. Soni Ambuje ŵenu aŵele Ŵakombola kusyene.');
INSERT INTO "silika" VALUES(25,55,'25:55','Soni ŵanganyao (achimakafili) akuŵa ali nkuchigalagatila chinepe changaŵaga Allah changaŵa nkwakamuchisya (naga ali achigalagatile) kapena kwapa ilagasyo (naga ali nganachigalagatila). Sano kafili ni akuŵaga nkunkamuchisya (shetani) pa (kwapitikuchila) Ambujegwe.');
INSERT INTO "silika" VALUES(25,56,'25:56','Ni nganituntumisya mmwe (Muhammadi صلى الله عليه وسلم) ikaŵeje kuti m’be nkutagulila abali jakusengwasya nambo soni nkutetela.');
INSERT INTO "silika" VALUES(25,57,'25:57','Jilani: “Une ngangun’juga malipilo galigose pa yalakwe (yakulungusya utengayi), nambo jwasachile kujigala litala lyakuja kwa M’mbujegwe (pakutola chipanje chakwe basi ngangunkanya).”');
INSERT INTO "silika" VALUES(25,58,'25:58','Soni jegamilani kwa (Allah) Jwaumi wandamo, Ajula jwaali Jwangawa, ni nluswejesyeje Lumbili Lwakwe. Soni jukwanile Jwalakwe pa sambi sya achikapolo Ŵakwe kuŵa Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(25,59,'25:59','Ajula juŵagumbile mawunde ni mataka ni yaili chilikati chakwe m’moŵa nsano nalimo, kaneka nijwatenjele pa Arishi (Chindanda Chaulungu). Jwaukoto wejinji, basi mum’busyani ya Jwalakwe nkumanyilila chenene (yambili Syakwe).');
INSERT INTO "silika" VALUES(25,60,'25:60','Soni naga ali asalilidwe (yanti): “Munsujudilani Jwaukoto wejinji.” Akasatiji: “Ana Jwaukoto wejinji ninduni? Ana tunsujudile jwankutuchisya mmwe?” Sano (gele maloŵega) gaajonjechesye ŵanganyao kutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(25,61,'25:61','Anyakwiche (pa uchimbichimbi ni upile) Ajula (Allah) jwaŵisile kwinani ndondwa syekulungwa-kulungwa ni mwakwenda mwakwe, ni aŵisile soni kwalakweko nyali (lyuŵa) kwisa soni mwesi wakumulika.');
INSERT INTO "silika" VALUES(25,62,'25:62','Soni Jwalakwe ni Ajula jwaŵisile chilo ni muusi kuŵa yaakuyaana ni kuchengana-chengana kwa jwaakusaka kumbuchila kapena kusaka kutogolela.');
INSERT INTO "silika" VALUES(25,63,'25:63','Sano achikapolo ŵa (Allah) Jwaukoto wejinji ni aŵala ŵaakwenda pachilambo mwakulinandiya, nambo soni ajinga ali aŵechete kukwao (maloŵe gangalumbana), akasajangaga ni maloŵe gantima tusya.');
INSERT INTO "silika" VALUES(25,64,'25:64','Ni aŵala ŵaakupitisyaga ndaŵi ja chilo achin’galagatilaga M’mbuje gwao mwakusujudu ni kwima (kuswali). ');
INSERT INTO "silika" VALUES(25,65,'25:65','Ni aŵala ŵaakutiji: “Ambuje ŵetu! Tutyochesyani ilagasyo ya Jahannama, chisimu ilagasyo yakwe iŵele yangalekangana nayo.”');
INSERT INTO "silika" VALUES(25,66,'25:66','“Chisimu jalakwe (Jahannama) gaŵele malo gakusakala nnope kutamilichika ni kwimilichika.”');
INSERT INTO "silika" VALUES(25,67,'25:67','Ni aŵala ŵati achitolaga ngakasamisangaga soni ŵangatenda ugagadi, nambo akasaŵa ŵakwimilichika chilikati cha yalakweyo.');
INSERT INTO "silika" VALUES(25,68,'25:68','Ni aŵala ŵangaakum’bendaga nnungu jwine pampepe ni Allah, soni ni ngaakuwulagaga mundu jwaakanyisye Allah (kum’bulaga) ikaŵeje paligongo lyakwitichika (ni malamusi ga Dini), nambo soni ŵangatenda chikululu. Sano jwachatende yele nikuti chasimangane ni ilagasyo (ya pa duniya).');
INSERT INTO "silika" VALUES(25,69,'25:69','Ilagasyo tiikajonjechesyedwe kukwakwe lisiku lya Kiyama, ni chijukalonjele mwalakwemo aku juli jwakunyosyedwa.');
INSERT INTO "silika" VALUES(25,70,'25:70','Ikaŵeje ŵatesile toba, ni kulupilila, ni kupanganyaga masengo gambone; basi ŵanganyao Allah chakatindanichisye yakusakala yao ni yambone, soni Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(25,71,'25:71','Sano jwaakutenda toba ni kupanganyaga yambone, nikuti chisimu jwalakwejo jukulamba kwa Allah pakutenda toba jisyesyene.');
INSERT INTO "silika" VALUES(25,72,'25:72','Ni aŵala ŵangatendela umboni yaunami, nambo soni naga achipitaga malo ga yachanache akasapitaga mwa adabu (ja kuitundumalila yalakweyo).');
INSERT INTO "silika" VALUES(25,73,'25:73','Ni aŵala ŵaakutiji naga ali akumbusyidwe ni ma Ȃya ga M’mbuje gwao ŵangagagwila mwa usiiwa ni ungalola.');
INSERT INTO "silika" VALUES(25,74,'25:74','Ni aŵala ŵaakutiji: “Ambuje ŵetu! Tupani kuumila mu achiŵammakwetu ni ŵanache ŵetu chakuteteŵachisya meso, ni ntutende uwwe kuŵa achinnongola ŵa ŵandu ŵawoga (wakun’jogopa Allah).”');
INSERT INTO "silika" VALUES(25,75,'25:75','Ŵanganyao takalipidwe yumba yapenani (ku Mbepo), ni takasimangane nao mwalakwemo uchimbichimbi ni ntendele.');
INSERT INTO "silika" VALUES(25,76,'25:76','Takaŵe ŵandamo mwalakwemo – Kaje kusalala malo gakutamilichika ni kwimilichika gakwe!');
INSERT INTO "silika" VALUES(25,77,'25:77','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ambuje ŵangu nga nganankochesyaga ŵanganyammwe kungaŵaga kuomba kwenu (kwankum’bombaga naga ili impatile ipato). Nambo sano ni pamasile pamunkanile (Jwalakwe). Basi sano (ipotesi) ni tiiŵe yangalekangana nayo ndema ni katema.');
INSERT INTO "silika" VALUES(26,1,'26:1','Twâ-Sîn-Mîm. ');
INSERT INTO "silika" VALUES(26,2,'26:2','Aga ni ma Ȃya ga m’Chitabu chakusalichisya.');
INSERT INTO "silika" VALUES(26,3,'26:3','Mwinetu mmwe kuŵa nkulijonanga mwasyene (ni madandausi) ligongo lyanti ŵanganyao ngakuŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,4,'26:4','Tungasache tungatuluchisye ŵanganyao kuumila kwinani chisimosimo, basi ngosi syao ni sikaliji syakuteteŵalila kuchalakwecho (mwanti akaujitiche Usilamu mwakanganisyidwa, nambo ngatukusasaka kwakanganichisya ŵandu ku Usilamu).');
INSERT INTO "silika" VALUES(26,5,'26:5','Ni ngachikwaichililaga chikumbusyo chasambano chakuumila kwa (Allah) Jwaukoto wejinji, ikaŵeje akasaŵaga ŵanganyao ŵaachitundumalila chalakwecho.');
INSERT INTO "silika" VALUES(26,6,'26:6','Sano pamasile pakanile, basi sampano tisyaichilile abali sya ayila yaaŵele ali nkuitendela chipongwe.');
INSERT INTO "silika" VALUES(26,7,'26:7','Ana nganaliona litaka – kuti mmela ulingwa watuumesisye mwalakwemo wa ntundu uliose wakusalala chenene?');
INSERT INTO "silika" VALUES(26,8,'26:8','Chisimu mu yalakweyo mwana chilosyo (cha ukombosi wa Allah), nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,9,'26:9','Soni chisimu Ambuje ŵenu, ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,10,'26:10','Soni (kumbuchilani) katema kaŵam’bilasile Ambuje ŵenu Musa (ni kuntenda kuti): “Jaulani ku ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(26,11,'26:11','“Ŵandu ŵa Firiauna; ana ngakogopa?”');
INSERT INTO "silika" VALUES(26,12,'26:12','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu une ngogopa kuti chakanganile.”');
INSERT INTO "silika" VALUES(26,13,'26:13','“Ni chikaŵanikwe chidali changu (ligongo lyakunganila), nambo soni lulimi lwangu ngalukuchachukaga chenene, basi tumisyani (soni utengau) kwa Haruna (kuti akangamusyeje).”');
INSERT INTO "silika" VALUES(26,14,'26:14','“Soni ŵanganyao akwete magambo ganaleŵele, ni ngogopa kuti takambulaje.”');
INSERT INTO "silika" VALUES(26,15,'26:15','Allah) jwatite: “Ngwamba! Basi jaulani ŵanaŵaŵilimmwe ni isimosimo Yetu. Chisimu Uwwe chitukaŵe namwe imo (pakunkamuchisya) aku tuchipikanilaga (yatakaŵecheteje).”');
INSERT INTO "silika" VALUES(26,16,'26:16','“Basi mun’jaulilani Firiauna ni kuja kuti: ‘Chisimu uwwe (jwalijose mwetuwe) ntenga jwa M’mbuje jwa iwumbe yosospe.’');
INSERT INTO "silika" VALUES(26,17,'26:17','‘(Tutumidwe) kuti mwaleche tuŵigule nao ŵanache ŵa Israila (tujauleje nao ku Shami).”');
INSERT INTO "silika" VALUES(26,18,'26:18','(Firiauna) jwatite (kwa Musa): “Ana nganitunnela kukwetu n’di mwanache, soni ni kutama nowe pa umi wenu yaka yejinji?”');
INSERT INTO "silika" VALUES(26,19,'26:19','“Ni mwatesile chitendo chenu chamwatesile chila (chakuwulaga mundu) aku n’di ŵakanila (ukoto wangu kukwenu).”');
INSERT INTO "silika" VALUES(26,20,'26:20','(Musa) jwatite: “Natesile yalakweyo pajele ndemajo kutendaga une ndili jumpepe mwa ŵangamanyilila (yakwayana ni Ambuje ŵangu ni Utume).”');
INSERT INTO "silika" VALUES(26,21,'26:21','“Basi ni nantisile ŵanganyammwe ndema janan’jogwepe, basi Ambuje ŵangu ni ambele lunda lwaulamusi, soni ni andesile kuŵa mwa achimitenga.”');
INSERT INTO "silika" VALUES(26,22,'26:22','“Ana soni weleu ni ukoto wakungumba nao kutendaga mwaŵisile mu ukapolo ŵanache ŵa Israila!”');
INSERT INTO "silika" VALUES(26,23,'26:23','Firiauna jwatite: “Ana M’mbuje jwa iwumbe yosopejo ni nduni?”');
INSERT INTO "silika" VALUES(26,24,'26:24','(Musa) jwatite: “M’mbuje jwa kumawunde ni petaka ni yaili chilikati chakwe, nam’baga jenumanja nkwete ntima wakusaka kusimichisya (tinsimichisye yele).”');
INSERT INTO "silika" VALUES(26,25,'26:25','(Firiauna) jwasalile ŵaŵaliji mungulugulu mwakwe (mwachalu) kuti: “Ana ngankupikana (yawanache yajukuŵechetayi)?”');
INSERT INTO "silika" VALUES(26,26,'26:26','(Musa) jwatite: “M’mbuje gwenu jenumanja nambo soni M’mbuje jwa achatati ŵenu ŵandanda.”');
INSERT INTO "silika" VALUES(26,27,'26:27','(Firiauna) jwatite: “Chisimu ntenga jwenu jwatumisyidwe kukwenuju jwamasoka.”');
INSERT INTO "silika" VALUES(26,28,'26:28','(Musa) jwatite: “M’mbuje jwa kungopoko ni kungapililo ni yaili chilikati chakwe, nam’baga jenumanja ŵana lunda (timmanyilile yele).”');
INSERT INTO "silika" VALUES(26,29,'26:29','(Firiauna) jwatite: “Naga nkulitendela nnungu jwinepe jwangaŵaga une, chisimu chene chinantende kuŵa jumpepe mwa akutaŵidwa.”');
INSERT INTO "silika" VALUES(26,30,'26:30','(Musa) jwatite: “Atamose ndili nam’bichilile nacho chindu chakuonechela palangulangu?”');
INSERT INTO "silika" VALUES(26,31,'26:31','(Firiauna) jwatite: “Ŵikani nacho, naga n’di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(26,32,'26:32','Basi (Musa) ni jwaponyisye simbo jakwe (pasi). Naalole! Jasyuchile lijoka lyakuonechela.');
INSERT INTO "silika" VALUES(26,33,'26:33','Soni ni jwakopwesye ligasa lyakwe. Naalole! Lyasyuchile lyeswela (lyakumelemenda) kwa akulola.');
INSERT INTO "silika" VALUES(26,34,'26:34','(Firiauna) jwasalile achinkulungwa ŵaŵaliji mungulugulu mwakwe kuti: “Chisimu jwalakweju nsaŵi jwakumanyilila nnope (usaŵi).”');
INSERT INTO "silika" VALUES(26,35,'26:35','“Jukusaka kuti junkopokasye m’chilambo mwenu ni usaŵi wakweo, ana ni tinnamule yantiuli?”');
INSERT INTO "silika" VALUES(26,36,'26:36','Ŵanganyao ŵatite (pakwapanga lunda Afiriauna): “Munnechelesyani panandi jwalakwe ni nkulugwejo (nkaawulaga chitema) ni ntumisye m’misinda akusonganganya.”');
INSERT INTO "silika" VALUES(26,37,'26:37','“Kuti ayiche najo kukwenu nsaŵi jwalijose jwali jwakumanyilila nnope (usaŵi).”');
INSERT INTO "silika" VALUES(26,38,'26:38','Basi asaŵi ni ŵasonganganyisidwe pa ndema jisyesyene ja lisiku lyakumanyika.');
INSERT INTO "silika" VALUES(26,39,'26:39','Ŵandu nombe ni ŵawusyidwe kuti: “Ana jenumanja tinsongangane?”');
INSERT INTO "silika" VALUES(26,40,'26:40','“Kuti mwine uwwejo twakuye asaŵi naga ŵanganyao aŵe ŵakupunda.”');
INSERT INTO "silika" VALUES(26,41,'26:41','Basi ndema jaŵaiche asaŵi ŵatite kwa Firiauna: “Ana chisimu kukwetu chikuŵe kwana malipilo (gambone) natuŵaga uwwejo ni ŵakupunda?”');
INSERT INTO "silika" VALUES(26,42,'26:42','(Firiauna) jwatite: “Elo, konjechesya soni pelepo chisimu ŵanganyammwe pajele ndemajo chim’be mwa akuŵandichisyidwa (kukwangu).” ');
INSERT INTO "silika" VALUES(26,43,'26:43','Musa jwasalile ŵanganyao kuti: “Ponyani jenumanja yankusaka kuponyayo.”');
INSERT INTO "silika" VALUES(26,44,'26:44','Basi ni ŵaponyisye ngonji syao ni simbo syao, niŵatite: “Kwa machili ga Firiauna chisimu uwwe ni ŵachituŵe ŵakupunda.”');
INSERT INTO "silika" VALUES(26,45,'26:45','Sano Musa ni jwaponyisye simbo jakwe. Naalole! Jalakwe jamisile mwanakamo yaunami yaŵaliji nkulosya.');
INSERT INTO "silika" VALUES(26,46,'26:46','Basi asaŵi ŵasenjelemukwiche kusujudu.');
INSERT INTO "silika" VALUES(26,47,'26:47','Ŵatite: “Tunkulupilile M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,48,'26:48','“M’mbuje jwa Musa ni Haruna.”');
INSERT INTO "silika" VALUES(26,49,'26:49','(Firiauna) jwatite: “Munkulupilile jwalakweju une nkaninampe lusa? Chisimu jwalakweju ni jwankulungwa jwenu jwan’jiganyisye usaŵi. Basi chisimu pangakaŵapa chiyyimanye (yachinantende). Chisimu chingate makono genu ni ngongolo syenu mwakutindanya (ligasa lyakun''dyo ni likau lyakunchiji, kapena ligasa lyakunchiji ni likau lyakun''dyo), kaneka ni chinankomele pansalaba ŵanaose.”');
INSERT INTO "silika" VALUES(26,50,'26:50','Ŵanganyao ŵatite: “Yangali kandu, chisimu uwwe kwa M’mbuje gwetu tukuujila.”');
INSERT INTO "silika" VALUES(26,51,'26:51','“Chisimu uwwe tukwete chikulupi chanti, M’mbuje gwetu jutukululuchile sambi syetu ligongo lyakuŵa ŵandanda mwa akukulupilila.”');
INSERT INTO "silika" VALUES(26,52,'26:52','Ni twan’juwulile Musa (maloŵe ganti): “Ŵigulani lwachilo ni achikapolo Ŵangu, chisimu ŵanganyammwe tinkuuyidwe.”');
INSERT INTO "silika" VALUES(26,53,'26:53','Basi Firiauna ni jwatumisye m’misinda (josope) akusonganganya.');
INSERT INTO "silika" VALUES(26,54,'26:54','(Ŵaŵapitaga nchigombelesya yanti): “Chisimu ŵanganyaŵa (ŵanache ŵa Israila) ali kwene kankutula kannono.”');
INSERT INTO "silika" VALUES(26,55,'26:55','“Soni chisimu ŵanganyao akutupikasya ntima.”');
INSERT INTO "silika" VALUES(26,56,'26:56','“Soni chisimu uwwe tuli wosope (ŵajinji nambo soni) tuli mesope (tukwalolechesya ŵanganyao kuti akatupatisya ipato iliyose).”');
INSERT INTO "silika" VALUES(26,57,'26:57','Basi Uwwe ni twakopwesye ŵanganyao (ŵanya Afiriauna) m’matimbe ni achitutumule.');
INSERT INTO "silika" VALUES(26,58,'26:58','Kwisa soni mu imatiilo yachipanje ni m’malo mwakuchimbichika.');
INSERT INTO "silika" VALUES(26,59,'26:59','Mwele (ni mutwatendele), ni twaitesile kuŵa yawinjilwa ni ŵanache ŵa Israila.');
INSERT INTO "silika" VALUES(26,60,'26:60','Basi ŵanganyao ni ŵakuyiye (ŵanache ŵa Israilao) pakopoka kwa lyuŵa.');
INSERT INTO "silika" VALUES(26,61,'26:61','Basi ndema jagaonegene makuga ganagaŵili (likuga lya Firiauna ni lya Musa), ŵandu ŵa Musa ŵatite: “Chisimu uwwe chitukamulwe.”');
INSERT INTO "silika" VALUES(26,62,'26:62','Jwalakwe jwatite: “Ngwamba! Chisimu Ambuje ŵangu ali none, chanjongolele (yakutenda kuti mpaka tujokoche).”');
INSERT INTO "silika" VALUES(26,63,'26:63','Basi ni twan’juwulile Musa (maloŵe ganti): “Putani mbwani ni simbo jenujo.” Ni jagaŵanyiche, upande uliose waliji nti chisawu litumbi lyekulungwa.');
INSERT INTO "silika" VALUES(26,64,'26:64','Ni twaŵandichisye palakwepo ŵaneo (Firiauna ni ŵandu ŵakwe).');
INSERT INTO "silika" VALUES(26,65,'26:65','Ni twan’jokwele Musa ni ŵaŵaliji najo imo wosopeŵene.');
INSERT INTO "silika" VALUES(26,66,'26:66','Kaneka ni twamisisye ŵaneo (Firiauna ni ŵandu ŵakwe).');
INSERT INTO "silika" VALUES(26,67,'26:67','Chisimu mu yalakweyi mwana chimanyisyo (cha ukombosi wa Allah). Nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,68,'26:68','Soni chisimu Ambuje ŵenu niŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,69,'26:69','Soni mwasoomelani ŵanganyao abali ja Ibrahima.');
INSERT INTO "silika" VALUES(26,70,'26:70','Pandema jajwawusisye babagwe ni ŵandu ŵakwe kuti: “Chichi nkuchigalagatila?”');
INSERT INTO "silika" VALUES(26,71,'26:71','Ŵanganyao ŵatite: “Tukugalagatila isanamu, basi ni tukwendelechela kuigalagatila ndema ni katema.”');
INSERT INTO "silika" VALUES(26,72,'26:72','(Ibrahima) jwatite: “Ana akasampikanaga pati nchaŵilangaga?”');
INSERT INTO "silika" VALUES(26,73,'26:73','“Kapena akasampaga chikamuchisyo (pati nchagalagatilaga) kapena kupeleka masausyo (naga ngaagalagatila)?”');
INSERT INTO "silika" VALUES(26,74,'26:74','Ŵanganyao ŵatite: “(Ngwamba,) Nambo twasimene achatati ŵetu achitendaga mwatukutendelamu.”');
INSERT INTO "silika" VALUES(26,75,'26:75','(Ibrahima) jwatite: “Ana nkwaona aŵa ŵam’bele n’di nkwagalagatila,”');
INSERT INTO "silika" VALUES(26,76,'26:76','“Jenumanja ni achatati ŵenu ŵaŵalongolele?”');
INSERT INTO "silika" VALUES(26,77,'26:77','“Basi chisimu ŵanganyao ni amagongo ŵangu, ikaŵeje M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,78,'26:78','“Ajula juŵangumbile, nambo soni Jwalakwe ni jwaakunjongolela (kwitala lyagoloka).”');
INSERT INTO "silika" VALUES(26,79,'26:79','“Nambo soni Jwalakwe ni Ajula jwaakundisyaga ni kumwesya.”');
INSERT INTO "silika" VALUES(26,80,'26:80','“Soni pati nwasile basi Jwalakwe ni jwaakumbosyaga.”');
INSERT INTO "silika" VALUES(26,81,'26:81','“Nambo soni Ajula jwatambe chiwa, nikwisa kumba umi soni (ku Kiyama).”');
INSERT INTO "silika" VALUES(26,82,'26:82','“Nambo soni Ajula jwangukolela tama janti tachingululuchila ileŵi yangu Lisiku Lyamalipilo.”');
INSERT INTO "silika" VALUES(26,83,'26:83','“Ambuje ŵangu! Mumbani lunda lwakwilanyichisya (ichindu), ni musimanganye ni ŵandu ŵambone.”');
INSERT INTO "silika" VALUES(26,84,'26:84','“Soni mumbani kusaalidwa kwambone m’mikutula jamunyuma.”');
INSERT INTO "silika" VALUES(26,85,'26:85','“Ni mundende kuŵa mwa akujitawala Mbepo Jachindimba.”');
INSERT INTO "silika" VALUES(26,86,'26:86','“Ni mwakululuchile atati ŵangu, chisimu ŵalakweo aŵele ŵampepe mwa akusokonechela.”');
INSERT INTO "silika" VALUES(26,87,'26:87','“Soni nkaja kunjalusya pa Lisiku lyatachijimusyidwaga ŵandu m’malembe.”');
INSERT INTO "silika" VALUES(26,88,'26:88','“Lisiku lyangakamuchisya chipanje namuno ŵanache,”');
INSERT INTO "silika" VALUES(26,89,'26:89','“Ikaŵeje jwatachiika kwa Allah ni ntima weswela.”');
INSERT INTO "silika" VALUES(26,90,'26:90','Ni jichiŵandichisyidwa Mbepo kwa ŵakun’jogopa (Allah).');
INSERT INTO "silika" VALUES(26,91,'26:91','Ni uchionesyedwa Moto Waukali kwa ŵapombotale.');
INSERT INTO "silika" VALUES(26,92,'26:92','Ni kuchiŵechetedwa kukwao: “Ali kwapi aŵala ŵamwaliji nkwagalagatila,”');
INSERT INTO "silika" VALUES(26,93,'26:93','“Kunneka Allah? Ana mpaka akombole kunkamuchisya ŵanganyammwe kapena kulikamuchisya achimisyene?”');
INSERT INTO "silika" VALUES(26,94,'26:94','Basi ni tachiponyedwa chamakuku mwalakwemo ŵanganyao ni akupombotala (ŵane).');
INSERT INTO "silika" VALUES(26,95,'26:95','Kwisa soni asilikali ŵa Ibulisu wosopeŵene.');
INSERT INTO "silika" VALUES(26,96,'26:96','Tachiti, aku ŵanganyao achikanganaga mwalakwemo:');
INSERT INTO "silika" VALUES(26,97,'26:97','“Tukunnumbila Allah! Chisimu uwwe twaliji nkusokonechela kwakuonechela.”');
INSERT INTO "silika" VALUES(26,98,'26:98','“Pandema jatwannandanyaga ŵanganyammwe ni M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,99,'26:99','“Ni nganatusokonesya ikaŵeje akuleŵa.”');
INSERT INTO "silika" VALUES(26,100,'26:100','“Ni nganitukola akutuombola.”');
INSERT INTO "silika" VALUES(26,101,'26:101','“Namuno nsyoŵe jwapantima (jwanti ni kutukamuchisya.)”');
INSERT INTO "silika" VALUES(26,102,'26:102','“Mwanti tungakole upile wakuujila (ku duniya), basi tungajiile kuŵa mwa akukulupilila.”');
INSERT INTO "silika" VALUES(26,103,'26:103','Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,104,'26:104','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,105,'26:105','Ŵandu ŵa Nuhu ŵaakaaniile achimitenga.');
INSERT INTO "silika" VALUES(26,106,'26:106','Ndema jaŵasalile nnongo n’jao Nuhu kuti: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(26,107,'26:107','“Chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(26,108,'26:108','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,109,'26:109','“Soni ngangun’juga pa yalakweyi malipilo, nganigaŵatu malipilo gangu ikaŵeje kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,110,'26:110','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,111,'26:111','Ŵanganyao ŵatite: “Ana tunkulupilile mmwe kutendaga ŵaakunkuya ni ŵandu ŵapasi?”');
INSERT INTO "silika" VALUES(26,112,'26:112','Jwalakwe jwatite: “Ana umanyilisichi ungwete pa yaŵaliji nkutenda ŵanganyao?”');
INSERT INTO "silika" VALUES(26,113,'26:113','“Nganichiŵatu chiŵalanjilo chao ikaŵeje kwa Ambuje ŵangu, ingaŵe mwamanyililaga.”');
INSERT INTO "silika" VALUES(26,114,'26:114','“Soni nganimba une jwanti ni kwaŵinga ŵakulupilila.”');
INSERT INTO "silika" VALUES(26,115,'26:115','“Nganimba une ikaŵeje nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(26,116,'26:116','Ŵanganyao ŵatite: “Naga ngankuleka E mmwe Nuhu! Chisimu chene chim’be mwa akusomedwa maganga.”');
INSERT INTO "silika" VALUES(26,117,'26:117','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu ŵandu ŵangu angaaniile.”');
INSERT INTO "silika" VALUES(26,118,'26:118','“Basi jilanyani chilikati changune ni ŵanganyao ni chilanyo (chambone), ni munjokole une ni ŵandu ŵali none mu ŵakulupilila.”');
INSERT INTO "silika" VALUES(26,119,'26:119','Basi ni twan’jokwele jwalakwe pamo ni ŵaŵaliji najo m’chombo chepachile.');
INSERT INTO "silika" VALUES(26,120,'26:120','Kaneko ni twamisisye ŵakusigalao panyuma pakwe.');
INSERT INTO "silika" VALUES(26,121,'26:121','Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,122,'26:122','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,123,'26:123','Achina Adi ŵakaaniile achimitenga.');
INSERT INTO "silika" VALUES(26,124,'26:124','Ndema jaŵasalile nnongo n’jao Hudu kuti: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(26,125,'26:125','“Chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(26,126,'26:126','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,127,'26:127','“Soni ngangun’juga pa yalakweyi malipilo, nganigaŵatu malipilo gangu ikaŵeje kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,128,'26:128','“Ana nkutaŵa pachikwesya palipose pakusalala nyumba syeleuleu mwamasanje (pagamba kusaka kulosya machili ngaŵa syagonamo)?”');
INSERT INTO "silika" VALUES(26,129,'26:129','“Ni nkulitaŵila makome gamakulungwakulungwa nti chisawu tinnonjele?”');
INSERT INTO "silika" VALUES(26,130,'26:130','“Soni ntite mpute ngondo nkasaputa mwakulikwesya.”');
INSERT INTO "silika" VALUES(26,131,'26:131','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,132,'26:132','“Soni mun’jogopani Ajula jwampele ichindu (yambonembone) yankuimanyilila.”');
INSERT INTO "silika" VALUES(26,133,'26:133','“Ampele ilango ni ŵanache.”');
INSERT INTO "silika" VALUES(26,134,'26:134','“Kwisa soni migunda ni sulo syatutumule.”');
INSERT INTO "silika" VALUES(26,135,'26:135','“Chisimu une ngun’jogopela ŵanganyammwe ilagasyo ya lisiku lyekulungwa.”');
INSERT INTO "silika" VALUES(26,136,'26:136','Ŵanganyao ŵatite: “Yalumope kukwetu, n’jamuche kapena nkaŵa mwa akwamuka.”');
INSERT INTO "silika" VALUES(26,137,'26:137','“Nganiyiŵatu ayi (yatukutendayi) ikaŵeje nyendele ja ŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(26,138,'26:138','“Soni nganituŵatu uwwe ŵanti ni kupedwa ilagasyo.”');
INSERT INTO "silika" VALUES(26,139,'26:139','Basi ŵankaaniile, ni twajonasile. Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,140,'26:140','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,141,'26:141','Achina Samuda ŵakaaniile achimitenga.');
INSERT INTO "silika" VALUES(26,142,'26:142','Ndema jaŵasalile nnongo n’jao Swaleh kuti: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(26,143,'26:143','“Chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(26,144,'26:144','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,145,'26:145','“Soni ngangun’juga pa yalakweyi malipilo, nganigaŵa malipilo gangu ikaŵeje kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,146,'26:146','“Ana (nkuganichisya kuti) tin’gambe kulechedwa mwantunjelele pa yaili pepanoyi.”');
INSERT INTO "silika" VALUES(26,147,'26:147','“M’migunda ni sulo syatutumule,”');
INSERT INTO "silika" VALUES(26,148,'26:148','“Ni mmela kwisa soni mitende itungatunga yakwe yakutependala?”');
INSERT INTO "silika" VALUES(26,149,'26:149','“Soni nkusepa m’matumbi majumba mwa ufundi.”');
INSERT INTO "silika" VALUES(26,150,'26:150','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,151,'26:151','“Ni ngasimpikanila chilamusi cha akupunda malile (pasambi).”');
INSERT INTO "silika" VALUES(26,152,'26:152','“Aŵala ŵaakuwatanganya pa chilambo, soni ŵangajilanya.”');
INSERT INTO "silika" VALUES(26,153,'26:153','Ŵanganyao ŵatite: “Chisimu mmwe n’di mwa akulojedwa.”');
INSERT INTO "silika" VALUES(26,154,'26:154','“Nganim’ba mmwe ikaŵeje mundu mpela uwwewe, basi ikani nacho chisimosimo naga n’di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(26,155,'26:155','Jwalakwe jwatite: “Aji ngamiya jajikongwe; jikwete kumwa (mesi), nombe jenumanja nkwete kumwa (mesi) palisiku lyakumanyika (ngamiya lisiku lyakwe nombe jenumanja lisiku lyenu).”');
INSERT INTO "silika" VALUES(26,156,'26:156','“Ni ngasin’jikwaya ni yakusakala iliyose itakwisa mwainkwembekenye ilagasyo yalisiku lyekulungwa.”');
INSERT INTO "silika" VALUES(26,157,'26:157','Nambo ŵanganyao ŵajisikite, mwanti ŵasyuchile kuŵa ŵakuligamba.');
INSERT INTO "silika" VALUES(26,158,'26:158','Basi ni yakwembekenye ilagasyo. Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,159,'26:159','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,160,'26:160','Ŵandu ŵa Lutwi ŵakaaniile achimitenga.');
INSERT INTO "silika" VALUES(26,161,'26:161','Ndema jaŵasalile nnongo n’jao Lutwi kuti: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(26,162,'26:162','“Chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(26,163,'26:163','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,164,'26:164','“Soni ngangun’juga pa yalakweyi malipilo, nganigaŵa malipilo gangu ikaŵeje kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,165,'26:165','“Ana nkwaichilila achalume (achimijenu pakwatenda matanyula) mu iwumbe,”');
INSERT INTO "silika" VALUES(26,166,'26:166','“Ni nkwaleka aŵala ŵan’guumbiile Ambuje ŵenu kuti aŵeje achiŵammakwenu? Nambo jenumanja ni ŵandu ŵakusumba mpika.”');
INSERT INTO "silika" VALUES(26,167,'26:167','Ŵanganyao ŵatite: “Naga ngankuleka (yakutukanya matanyula) mmwe Lutwi! Chisimu chene chim’be mwa akukoposyedwa.”');
INSERT INTO "silika" VALUES(26,168,'26:168','Jwalakwe jwatite: “Chisimu une ni jumpepe mwa akuiŵenga mwaukali itendo yenu (yamatanyulai).”');
INSERT INTO "silika" VALUES(26,169,'26:169','“Ambuje ŵangu! Munjokolani une ni liŵasa lyangu ku yaakupanganya ŵanganyaŵa.”');
INSERT INTO "silika" VALUES(26,170,'26:170','Ni twan’jokwele jwalakwe ni liŵasa lyakwe lyosopelyene,');
INSERT INTO "silika" VALUES(26,171,'26:171','Ikaŵeje kangalamba kachikongwe (ŵankwao Lutwi ŵaŵaliji) mwa akusigalila munyuma.');
INSERT INTO "silika" VALUES(26,172,'26:172','Kaneko ni twajonasile ŵaneo.');
INSERT INTO "silika" VALUES(26,173,'26:173','Ni twagwichisye ŵanganyao wula (jamaganga). Kaje kusakala wula ja akuteteledwa!');
INSERT INTO "silika" VALUES(26,174,'26:174','Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,175,'26:175','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,176,'26:176','Achinsyene itekete (ŵandu ŵa ku Madiyana) ŵakaaniile achimitenga.');
INSERT INTO "silika" VALUES(26,177,'26:177','Ndema jaŵasalile Shuaibu kuti: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(26,178,'26:178','“Chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(26,179,'26:179','“Basi mun’jogopani Allah ni kumbikanila une.”');
INSERT INTO "silika" VALUES(26,180,'26:180','“Soni ngangun’juga pa yalakweyi malipilo, nganigaŵa malipilo gangu ikaŵeje kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(26,181,'26:181','“Gumbasyani chipiimo chambale ni ngasim’ba mwa akupungusya.”');
INSERT INTO "silika" VALUES(26,182,'26:182','“Ni mpimeje ni sikelo jakulungamika.”');
INSERT INTO "silika" VALUES(26,183,'26:183','“Soni ngasimwapunguchisyaga ŵandu indu yao, ni ngasin’jendagajendaga pa chilambo kupita nchiwatanganya.”');
INSERT INTO "silika" VALUES(26,184,'26:184','“Soni mun’jogopani Ajula juŵan’gumbile ŵanganyammwe kwisa soni mikutula ja ŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(26,185,'26:185','Ŵanganyao ŵatite: “Chisimu mmwe n’di mwa akulojedwa.”');
INSERT INTO "silika" VALUES(26,186,'26:186','“Soni nganim’ba mmwe ikaŵeje mundu mpela uwwewe. Soni uwwe tukun’ganichisya kwene kuti n’di jumpepe mwa ŵandu ŵaunami.”');
INSERT INTO "silika" VALUES(26,187,'26:187','“Basi tugwichisyani uwwe chipande cha liwunde (kuti chitujonanje) nam’baga mmwejo n’di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(26,188,'26:188','Jwalakwe jwatite: “Ambuje ŵangu ni ŵaakumanyilila chenene yankupanganya.”');
INSERT INTO "silika" VALUES(26,189,'26:189','Ni ŵankaaniile, basi ni yakwembekenye ilagasyo yalisiku lyam’bulili (waliwunde lyaipotesi), chisimu yalakweyo yaliji ilagasyo ya lisiku lyekulungwa.');
INSERT INTO "silika" VALUES(26,190,'26:190','Chisimu mu yalakweyi mwana chilosyo, nambo ŵajinji mwa ŵanganyao nganaŵa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,191,'26:191','Soni chisimu Ambuje ŵenu ni ŵali Ŵamachili gakupunda, Ŵachanasa channope.');
INSERT INTO "silika" VALUES(26,192,'26:192','Soni chisimu jele (Qur’aniji) jiŵele jakutulusyidwa kuumila kwa M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(26,193,'26:193','Watulwiche najo Nsiimu (Jibulilu) wakulupichika,');
INSERT INTO "silika" VALUES(26,194,'26:194','Pantima penu (mmwe Muhammadi صلى الله عليه وسلم), kuti m’be mwa akutetela,');
INSERT INTO "silika" VALUES(26,195,'26:195','Mu chiŵecheto cha Charabu chakupikanika chenene.');
INSERT INTO "silika" VALUES(26,196,'26:196','Soni yalakweyi yili mu itabu ya (ŵandu) ŵandanda.');
INSERT INTO "silika" VALUES(26,197,'26:197','Ana nganichiŵa chilosyo kukwao chanti ŵalijiganye ŵa ŵanache ŵa Israila (mpela Abdullah bin Salâm ŵaŵaujitiche Usilamu) ni kujimanya jalakwe (Qur’aniji kuti jakuona)? ');
INSERT INTO "silika" VALUES(26,198,'26:198','Soni tungajitulusye (Qur’aniji) kwa ŵanepe ŵangaŵa ŵarabu,');
INSERT INTO "silika" VALUES(26,199,'26:199','Basi ni kujisooma kukwao, nga nganaŵa ŵakujikulupilila jalakwe.');
INSERT INTO "silika" VALUES(26,200,'26:200','Iyyoyopeyo tukupitisye (kujikanila Qur’an) m’mitima mwa akuleŵa (atamose kuti jukwasoomela mwarabu n’jao).');
INSERT INTO "silika" VALUES(26,201,'26:201','Ngaajikulupilila jalakwe mpaka akaiwone ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(26,202,'26:202','Basi ni tiichaichilila mwachisupuchisya ŵanganyao akakumanyilila.');
INSERT INTO "silika" VALUES(26,203,'26:203','Ni tachiti: “Ana uwwe ngakomboleka kupedwa lipesa (kuti tutende toba)?”');
INSERT INTO "silika" VALUES(26,204,'26:204','Ana akuijanguya ilagasyo Yetu?');
INSERT INTO "silika" VALUES(26,205,'26:205','Ana nkuiona uli, naga tuli twasengwasisye ŵanganyao kwa yaka (yejinji),');
INSERT INTO "silika" VALUES(26,206,'26:206','Kaneka ni kwaichilila (ipotesi) yaŵaliji nkupedwa chilanga,');
INSERT INTO "silika" VALUES(26,207,'26:207','Ngasiyakamuchisya ayila yaŵele ali nkusengwasyidwa nayo.');
INSERT INTO "silika" VALUES(26,208,'26:208','Soni Uwwe nganituujonanjeje musi uliose ikaŵeje wakolaga akutetela.');
INSERT INTO "silika" VALUES(26,209,'26:209','Kuŵa chikumbusyo, soni Uwwe nganituŵa ŵalupuso (pakwajonanga ŵandu nkanitwatetele).');
INSERT INTO "silika" VALUES(26,210,'26:210','Soni mashetani ngaŵa ni gagatulwisye jalakwe (Qur’aniji mwaakuŵechetela achimakafili).');
INSERT INTO "silika" VALUES(26,211,'26:211','Soni ngaikuŵajilwa kukwao, nambo soni nganaŵa akombwele (kujitulusya).');
INSERT INTO "silika" VALUES(26,212,'26:212','Chisimu ŵanganyao yakupikanila (Qur’an kumawunde jichitulukaga) asiŵiiliidwe.');
INSERT INTO "silika" VALUES(26,213,'26:213','Basi ngasimunnumbanjila pampepe ni Allah nnungu jwine, ntakwika mum’bele mwa akulagasyidwa.');
INSERT INTO "silika" VALUES(26,214,'26:214','Ni mwatetelani ŵalukosyo lwenu ŵali ŵakuŵandichila. ');
INSERT INTO "silika" VALUES(26,215,'26:215','Nimwagoyechesye lipapiko lyenu (lya chanasa) aŵala ŵankuyiye mwa ŵakulupilila.');
INSERT INTO "silika" VALUES(26,216,'26:216','Sano naga akunnyosya basi jilani: “Chisimu une mambalene nayo ichindu yankutenda.”');
INSERT INTO "silika" VALUES(26,217,'26:217','Nin’jegamile kwa (Allah) Jwamachili gakupunda, Jwachanasa channope.');
INSERT INTO "silika" VALUES(26,218,'26:218','Ajula jwaakum’bona ndema jankwima (jikape kuswali patahajjudi).');
INSERT INTO "silika" VALUES(26,219,'26:219','Ni kutenganyikatenganyika kwenu (pakuswali ipindi nsano) pamo ni akusujudu (akuswali ŵane).');
INSERT INTO "silika" VALUES(26,220,'26:220','Chisimu Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(26,221,'26:221','Ana nantagulile ŵanganyammwe jwagakuntuluchilaga mashetani?');
INSERT INTO "silika" VALUES(26,222,'26:222','Gakuntuluchilaga jwalijose jwaunami wejinji, jwasambi nnope.');
INSERT INTO "silika" VALUES(26,223,'26:223','Gakuponyaga yagapikene (kwinani kwaponyela achibimbi) nambo ŵajinji mwa ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(26,224,'26:224','Akwimba chilaanga ŵaliose akasaŵakuyaga ŵasokonechele.');
INSERT INTO "silika" VALUES(26,225,'26:225','Ana ngankuiona yanti ŵanganyao m’chiŵata chilichose (chamaloŵe) akuŵa nkupojola?');
INSERT INTO "silika" VALUES(26,226,'26:226','Soni kuti ŵanganyao akasaŵechetaga yangakutendaga.');
INSERT INTO "silika" VALUES(26,227,'26:227','Ikaŵeje aŵala ŵaakulupilile ni kutendaga yambone, ni kunkolangaga Allah kwejinji, ni kulikulupusya achimisyene panyuma pakutendeledwa lupuso (pakwajanga achimakafili ni ilaanga pa ilaanga yao yaakwimba yakwagamba Asilamu). Sano pangakaŵapa tamanyilile aŵala ŵatesile lupuso kwagalauchila kutagalauchile.');
INSERT INTO "silika" VALUES(27,1,'27:1','Twâ-Sîn . Aga ni ma Ȃya ga Qur’an nambo soni (chiŵele) Chitabu chakusalichisya.');
INSERT INTO "silika" VALUES(27,2,'27:2','Chongolo nambo soni chisengwasyo kwa ŵakulupilila.');
INSERT INTO "silika" VALUES(27,3,'27:3','Aŵala ŵaakwimikaga Swala mwakolosya ni kutolaga Zaka, soni ŵanganyao ya Akhera akasaŵa ŵakulupilila kusyesyene.');
INSERT INTO "silika" VALUES(27,4,'27:4','Chisimu aŵala ŵangakukulupilila ya Akhera twasalalichisye itendo yao mwanti ŵanyao agamba kuŵa pupulipupuli (nkusokonechela mwao).');
INSERT INTO "silika" VALUES(27,5,'27:5','Ŵanganyao ni aŵala ŵaakwete kupata ilagasyo yakunyalaya (pa duniya pano), nambo soni ŵanganyao ku Akhera tachiŵa ŵakwasika kusyene.');
INSERT INTO "silika" VALUES(27,6,'27:6','Soni chisimu mmwejo (Muhammadi صلى الله عليه وسلم) nkusoomesyedwa Qur’an kuumila kwa (Allah) Jwalunda lwakusokoka, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(27,7,'27:7','(Kumbuchilani) katema kajwalisalile Musa liŵasa lyakwe kuti: “Chisimu une nauweni moto. Chinam’bichilile najo abali kuumila ku walakweo, kapena chinam’bichilile nacho chisinga chamoto kuti jenumanja n’jote.”');
INSERT INTO "silika" VALUES(27,8,'27:8','Basi ndema jajwauŵichilile jwaŵilanjidwe kuti: “Ajaliiwe jwali pamoto ni jwali mungulugulu mwakwe. Nambo soni aswejele Allah M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(27,9,'27:9','“E mmwe Musa! Chisimu Jwalakwejotu ni Une Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(27,10,'27:10','“Soni ponyani simbo jenu.” Basi ndema jajwajiweni jinankutenganyika nti chisawu lijoka, jwagalawiche kutila nambo soni nganijulola munyuma. (Allah jwatite): “E mmwe Musa! Nkajogopa. Chisimu Une Achimitenga ŵangajogopa Kukwangu.”');
INSERT INTO "silika" VALUES(27,11,'27:11','“Nambo jwalitendele lupuso kaneko ni galausya (pakupanganya) yambone panyuma pa yakusakala basi (ngunkululuchilaga), chisimu Une ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(27,12,'27:12','“Soni jinjisyani ligasa lyenu pa mitima pa kansu jenu chilikopoche lili lyeswela ngaŵaga mwaulwele. (Ilosyo iŵiliyi ili) mu ilosyo nsano nancheche. (Jaulani nayo) kwa Firiauna ni ŵandu ŵakwe, chisimu ŵanganyao aŵele ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(27,13,'27:13','Basi ndema jayaichilile isimosimo Yetu yakuloleka ŵanganyao ŵatite: “Awu ni usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(27,14,'27:14','Ni ŵaikaaniile (isimosimoyo) mwalupuso nambo soni mwakulikwesya kutendaga mitima jao jaikulupilile kusyesyene. Basi nnole mujaŵelele mbesi ja akuwatanganya!');
INSERT INTO "silika" VALUES(27,15,'27:15','Soni chisimu chene pamasile patwampele Daudi ni Sulaimana umanyilisi. Ni ŵatite ŵanaŵaŵili: “Lumbili losope lwene alapikwe Allah Ajula jwaatulongwesye uwwe (pakutupa ukoto) kwapunda ŵajinji mwa achikapolo Ŵakwe ŵakulupilila.”');
INSERT INTO "silika" VALUES(27,16,'27:16','Sano Sulaimana jwatamile chitengu cha Daudi. Ni jwatite: “E jenumanja ŵandu! Uwwe tujiganyisyidwe chiŵecheto cha ijuni, ni tupedwile chindu chilichose. Chisimu walakweu ni ukoto wakuonechela.”');
INSERT INTO "silika" VALUES(27,17,'27:17','Ni ŵasonganganyisyidwe kwa Sulaimana asilikali ŵakwe kuumila m’majini, ŵandu ni ijuni. Basi ni ŵaliji ali nkwaalidwa m’mikutulamikutula (pakajende kakwe).');
INSERT INTO "silika" VALUES(27,18,'27:18','Kwikanila ndema jaŵaiche ŵanganyao pachiŵata chambamba, lupamba lwatite (pakusisalila mbamba sijakwe): “E jenumanja achimbamba! Jinjilani m’majumba mwenu atakwika mwan’diŵete Sulaimana ni asilikali ŵakwe kutendaga ŵanganyao akakumanyilila.”');
INSERT INTO "silika" VALUES(27,19,'27:19','Basi ni jwamwetulile (Sulaimana) mwakusechelela ligongo lya maloŵe gakwe (ga lupamba). Ni jwatite: “Ambuje ŵangu! Mumbani machili ganti ngomboleje kutogolela ukoto Ŵenu wamumbele une ni achinangolo ŵangu, nambo soni napakomboleje kutenda yambone yampaka iyyinonyele. Ni munyinjisye une mwa ukoto Ŵenu mu achikapolo Ŵenu ŵambone.”');
INSERT INTO "silika" VALUES(27,20,'27:20','Ni jwapite nchendela ijuni, basi ni jwatite: “Itite uli kukwangu mbona ngangujiona ngukuku, kapena jili mwa akusoŵa?”');
INSERT INTO "silika" VALUES(27,21,'27:21','“Chisimu chene chinajilagasye ni ilagasyo yakandapala, kapena chingambe kujisikita, ikaŵeje jiiche nalyo ligongo lyakupikanika (lyajisoŵele).”');
INSERT INTO "silika" VALUES(27,22,'27:22','Nambo (ngukuku) nganijitama ndema jelewu, basi (nijaiche kwa Sulaimana ni) jatite: “Naikopocheele yanganiyyikopochela mmwe, ni nam’bichilile kuumila ku Sabai ni abali jakuonaonape.”');
INSERT INTO "silika" VALUES(27,23,'27:23','“Chisimu une naasimene ŵakongwe ali nkwalamulila (ŵandu ŵa ku Sabai), ni apedwile chindu chilichose (chakuŵajilwa kola nchimwene), nambo soni akwete chindanda (cha uchimwene) chekulungwa nnope.”');
INSERT INTO "silika" VALUES(27,24,'27:24','“Nasimene ŵalakweo ni ŵandu ŵao alinkulisujudila lyuŵa kunneka Allah, soni shetani jwasalalichisye itendo yao, ni jwasiŵilile kwitala (lyagoloka), basi ŵanganyao ngakupata chongoko,”');
INSERT INTO "silika" VALUES(27,25,'27:25','“Mwanti ngaakunsujudila Allah Ajula jwaakukoposyaga yakusisika ya kumawunde ni petaka, soni ni akuimanyilila yankusisa ni yankulosya.”');
INSERT INTO "silika" VALUES(27,26,'27:26','“Allah pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo. M’mbuje jwa Arishi (Chindanda Chaulungu) Jekulungwa nnope.”');
INSERT INTO "silika" VALUES(27,27,'27:27','(Sulaimana) jwatite: “Chitulole naga nkuŵecheta yakuona kapena n’di mwa akulambusya.”');
INSERT INTO "silika" VALUES(27,28,'27:28','“Jaulani ni baluwa janguji ni nkajiponye kukwao, kaneko ni nkaatundumalile (nkatame pakutalichila) ni nkalole mwachigakaŵele majibu gao.”');
INSERT INTO "silika" VALUES(27,29,'27:29','(Nyakongwe) ŵatite: “E jenumanja achinduna! Chisimu une jiponyedwe kukwangu baluwa jakuchimbichika.”');
INSERT INTO "silika" VALUES(27,30,'27:30','“Chisimu jalakwe jikuumila kwa Sulaimana, soni chisimu jalakwe (jikuti): “”');
INSERT INTO "silika" VALUES(27,31,'27:31','“Ngasin’dikwesya kukwangu, nambo mumbichilile n’di Asilamu.”');
INSERT INTO "silika" VALUES(27,32,'27:32','(Nyakongwe) ŵatite: “E jenumanja achinduna! Mumbangani yakujila pangani janguji, une nganimba namwile chilichose mpaka nkambichilile (ni kumba nganisyo syenu).”');
INSERT INTO "silika" VALUES(27,33,'27:33','Ŵanganyao ŵatite: “Uwwe ni achinsyene machili, nambo soni achinsyene kuputa ngondo mwaukali, nambo yosope ili kukwenu, basi nnole muchinnamulile.”');
INSERT INTO "silika" VALUES(27,34,'27:34','(Nyakongwe) ŵatite: “Chisimu achiŵenye naga ali aujinjile musi, akasaujonangaga ni kwatenda ŵakuchimbichika mwa ŵandu ŵakwe kuŵa ŵakunyosyeka, mwele ni mwaakasatendela (ndema syose).”');
INSERT INTO "silika" VALUES(27,35,'27:35','“Basi chisimu une chindumisye kukwao ntuuka, basi ninole kuti ana tisiuje nayo yantiuli ndumetume.”');
INSERT INTO "silika" VALUES(27,36,'27:36','Basi ndema jasyaiche (ndumetume sila) kwa Sulaimana, jwalakwe jwatite: “Ana nkungamuchisya ni chipanje? Yambele une Allah ili yambone nnope kupunda yampele ŵanganyammwe, nambo kuti jenumanja nkuchina ni ntuuka wenu.”');
INSERT INTO "silika" VALUES(27,37,'27:37','(Kaneka Sulaimana ŵansalile nnongola jwa ndumetume kuti): “Ujilani kukwao, basi chisimu chene chitwaichilile ni asilikali ŵangaŵa nkombola ŵanganyao kulimbana nao, soni chisimu chene chitwakoposye ŵanganyao mwalakwemo ali ŵakunyosyeka, nambo soni ali atengwiche.”');
INSERT INTO "silika" VALUES(27,38,'27:38','(Sulaimana) jwatite: “E jenumanja achinduna! Nduni mwa jenumanja jwataichenacho kukwangu chindanda chakwe (cha uchimwene) nkanaŵe kumbichilila ŵanganyao ali Asilamu?”');
INSERT INTO "silika" VALUES(27,39,'27:39','Jwaŵechete jwamachili nnope mwa majini kuti: “Une chimbiche nacho kukwenu nkanin’jiime kutyoka pamalo penu (panganya jenu), soni chisimu une pa chalakwecho ndili jwamachili kusyene, soni jwakulupichika.”');
INSERT INTO "silika" VALUES(27,40,'27:40','Jwaŵechete ajula juŵakwete umanyilisi wa m’chitabu kuti: “Une chinam’bichilile nacho nkaniliusyaausye kukwenu liso lyenu.” Basi ndema jajwachiweni (Sulaimana chindanda cha uchimwene chila) chili chitamilichiche kukwakwe jwatite: “Ayi ili mu ukoto wa Ambuje ŵangu kuti aninje yanti; ana tindogolele kapena tingane! Sano jwatatogolele nikuti akutogolela kwaalikamuchisya nsyene, nambo soni jwatakane basi (akukana kwaalikonya nsyene), chisimu Ambuje ŵangu ni Ŵakupata kwejinji (ŵangalajila chilichose), Ŵantuuka wannope.”');
INSERT INTO "silika" VALUES(27,41,'27:41','(Sulaimana) jwatite: “Muntindanyichisye (kaoneche ka) chindanda chakwe (cha uchimwene), tulole kuti ana tachimanyilile kapena chaŵe mwa aŵala ŵangamanyilila (ichindu).”');
INSERT INTO "silika" VALUES(27,42,'27:42','Basi ndema jaŵaiche (nyakongwe) yaŵechetedwe yanti: “Ana chindanda chenu (cha uchimwene) chikuŵa mpela achichi?” Jwalakwe jwatite: “Chikuŵa mpela nichichichi.” (Sulaimana jwatite): “Sano uwwe twapedwile umanyilisi nkanaŵe jwalakwe, soni twaŵele Asilamu.”');
INSERT INTO "silika" VALUES(27,43,'27:43','Sano aila yajwaigalagatilaga kunneka Allah yansiŵililaga (nyakongwejo ku Usilamu), chisimu jwalakwe jwaliji jwakuumila ku ŵandu achimakafili.');
INSERT INTO "silika" VALUES(27,44,'27:44','(Nyakongwe) ŵasalilidwe kuti: “Jinjilani mwipenu lyanyumba.” Basi ndema jaŵaliweni ŵaliganichisyaga kuti litanda, ni ŵakwindile (nguwo mpaka) kulosya sonjo syakwe. (Sulaimana) jwatite: “Chisimu lyalakwelitu lipenu lyanyumba lyesyasyajile ni magalasi.” (Nyakongwe) ŵatite: “Ambuje ŵangu! Chisimu une naalitendele lupuso nansyene (paagalagatila inepe yangaŵaga Mmwe), sano nalipeleche pamo ni Sulaimana kwa Allah, M’mbuje jwa iwumbe yosope (ndili Nsilamu).”');
INSERT INTO "silika" VALUES(27,45,'27:45','Soni chisimu chene Uwwe twatumisye kwa achina Samuda nnongo n’jao (jwali) Swaleh (juŵasalile kuti): “Mun’galagatilani Allah.” Basi naalole! Ŵanganyao ŵasyuchile kuŵa makuga gaŵili (lyakulupilila ni lyakanila) gagaliji nkukangana.');
INSERT INTO "silika" VALUES(27,46,'27:46','Jwalakwe jwatite: “E ŵandu ŵangu! Ligongo chichi nkuijanguichisya yakusakala (kuti impate) nkaniiŵe yambone? Uli ngam’benda Allah chikuluko kuti ntendeledwe chanasa?”');
INSERT INTO "silika" VALUES(27,47,'27:47','Ŵanganyao ŵatite: “Uwwe tutapene ligongo lyenumwe ni ŵan’di namwe.” Jwalakwe jwatite: “Chitapani chenu chili kwa Allah (ligongo lya itendo yenu yangalumbana), nambo jenumanja ni ŵandu ŵankuliingwa (pakumpa yambone ni yakusakala).”');
INSERT INTO "silika" VALUES(27,48,'27:48','Sano mu nsinda (wa Samuda) mwaliji mwana ŵandu nsano nancheche ŵaŵaliji nkuwatanganya pa chilambo, soni nganakolosyaga.');
INSERT INTO "silika" VALUES(27,49,'27:49','Ŵanganyao ŵatite: “Nnumbililane kwa Allah kuti chisimu chitukampute ngondo chilo jwalakwe ni liŵasa lyakwe, kaneko chisimu chitunsalile jwawinjila jwakwe kuti: Uwwe nganituŵandika pa chonasiko cha liŵasa lyakwe, nambo soni chisimu uwwe tukuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(27,50,'27:50','Ni ŵapanjile malindi, nombe Uwwe nitwapanjile mipango jakonanga malindi gao kutendaga ŵanganyao akakumanyilila.');
INSERT INTO "silika" VALUES(27,51,'27:51','Basi nnole mujaŵelele mbesi ja malindi gao! Chisimu Uwwe twajonasile ŵanganyao ni ŵandu ŵao wosopeŵene.');
INSERT INTO "silika" VALUES(27,52,'27:52','Basi asila ni nyumba syao (syasyasyuchile kuŵa) mawuucha, ligongo lya lupuso lwao. Chisimu mu yalakwei mwana chilosyo kwa ŵandu ŵaakumanyilila.');
INSERT INTO "silika" VALUES(27,53,'27:53','Ni twajokwele aŵala ŵaŵakulupilile nikuŵa ni woga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(27,54,'27:54','Soni (munkumbuchilani) Lutwi pandema jaŵatite kwa ŵandu ŵakwe: “Ana nkuyiichilila yausakwa (matanyula) kutendaga jenumanja n’di nkuloleela?”');
INSERT INTO "silika" VALUES(27,55,'27:55','“Nambo yakuona jenumanja nkwaichilila achalume pakwanilisya isako yenu kwaleka achakongwe? Nambo jenumanja niŵandu ajinga.”');
INSERT INTO "silika" VALUES(27,56,'27:56','Basi nganiuŵa wanje wa ŵandu ŵakwe (kuti waliji wambone,) ikaŵeje kuŵecheta yanti: “Mwaŵingani ŵaliŵasa lya Lutwi m’musi mwenu, chisimu ŵanganyao ni ŵandu ŵaakuliiswejesya (chatamile syapi m’musi wausakwa!)”');
INSERT INTO "silika" VALUES(27,57,'27:57','Basi ni twankulupwisye jwalakwe pamo ni liŵasa lyakwe, ikaŵeje ŵankwakwepe; twapimichisye (yakuŵa) mwa akusigalila munyuma (ni ŵajonasiche).');
INSERT INTO "silika" VALUES(27,58,'27:58','Ni twatuluchisye wula (ja maganga), kaje kusakala wula ja akuteteledwa!');
INSERT INTO "silika" VALUES(27,59,'27:59','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Lumbili losope lwene alapikwe Allah, soni ntendele uŵe pa achikapolo Ŵakwe aŵala ŵaŵasagwile. Ana Allah nijwali Jwambone kapena ayila yaakuiwanganya (ni Allah pakuigalagatila)?”');
INSERT INTO "silika" VALUES(27,60,'27:60','Kapena (jwambone ni) Ajula jwaagumbile mawunde ni mataka, ni kuntuluchisya kuumila kwinani mesi (gawula), ni tumesisye ni galakwego matimbe gana uŵigo gagakwete kusalalila? Nganiuŵa kukwenu (ukombosi) wanti nikumesya itela yakwe. Ana pana nnungu jwine pampepe ni Allah? (Ngwamba!) Nambo ŵanganyao niŵandu ŵaakuŵika chakulandanyichisya (ni Jwalakwe pakuchigalagatila).');
INSERT INTO "silika" VALUES(27,61,'27:61','Kapena (jwambone ni) Ajula jwaalitesile litaka kuŵa lyakutamilichika, ni kuŵika nkatikati mwakwemo sulo, ni kuliŵichila matumbi gakunyichisya, ni kuŵika chakusiŵilila chilikati cha mbwani siŵili (ja mesi gachijete ni jamesi gakumweka)? Ana pana nnungu jwine pampepe ni Allah? (Ngwamba!) Nambo ŵajinji mwa ŵanganyao ŵangali chachimanyi.');
INSERT INTO "silika" VALUES(27,62,'27:62','Kapena (jwambone ni) Ajula jwaakun’jangaga jwainkanganiche pati am’bombilemo, ni jukungunukulaga masausyo, ni akum’bika ŵanganyammwe kuŵa ŵawinjila pachilambo (panyuma pa ŵandu ŵane)? Ana pana nnungu jwine pampepe ni Allah? (Ngwamba!) Yannonope niyankukumbuchilaga.');
INSERT INTO "silika" VALUES(27,63,'27:63','Kapena (jwambone ni) Ajula jwaakun’jongolelaga mu chipi cha pankuuli ni pambwani, nambo soni Ajula jwaakutumisyaga mbungo kuŵa chisengwasyo paujo pa ukoto Wakwe (wula nkanijigwe)? Ana pana nnungu jwine pampepe ni Allah? (Ngwamba!) Atukwiche Allah ku yaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(27,64,'27:64','Kapena (jwambone ni) Ajula jwaakutandisyaga gumba iwumbe kaneko ni kuiwusyawusya (panyuma pa kuwa), nambo soni Ajula jwaakumpaga lisiki kuumila kwinani ni pasi? Ana pana nnungu jwine pampepe ni Allah? (Ngwamba!) Jilani: “Ikani nao umboni wenu nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(27,65,'27:65','Jilani: “Ŵali kumawunde ni petaka ngakumanyilila yakusisika ikaŵeje Allah, ni ngakumanyilila soni kuti chakachi chachiukulidwa m’malembe.”');
INSERT INTO "silika" VALUES(27,66,'27:66','Ana umanyilisi ŵao uyiche pakomangala pakuimanya ya Akhera (kwanti ni kuwusyaga yandema jakwichila)? (Ngwamba!) Nambo ŵanganyao ali mu kaichila yajalakwejo, nambo soni ŵanganyao ali mu ungalola usyesyene wa yajalakwejo.');
INSERT INTO "silika" VALUES(27,67,'27:67','Ni ŵaŵechete aŵala ŵaŵakanile kuti: “Ana tuli tusyuchile litakatakape uwwe ni achatati ŵetu, nambo yakuona tuchikoposyedwa (m’malembe nikuŵa niumi soni)?”');
INSERT INTO "silika" VALUES(27,68,'27:68','“Chisimu tuŵele tuli nkupedwa chilanga cha yalakweyi uwwe mpaka ni achatati ŵetu kalakala (uwwe nkanitupagwe), nganiyiŵatu yalakweyi ikaŵeje adisiadisi syaŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(27,69,'27:69','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Jendagani pa chilambo ni kupita nchilola mujaŵelele mbesi ja akuleŵa.”');
INSERT INTO "silika" VALUES(27,70,'27:70','Ni ngasimwadandaulila ŵanganyao, soni ngasim’ba pakangani ligongo lya malindi gaakutenda.');
INSERT INTO "silika" VALUES(27,71,'27:71','Ŵanganyao ni akutiji: “Ana chele chilangachi chiyiche chakachi nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(27,72,'27:72','Jilani: “Kuŵankuŵa ili dusi munyuma mwenumo ine mwa ayila yankuŵa nkuikulumisya”');
INSERT INTO "silika" VALUES(27,73,'27:73','Soni chisimu Ambuje ŵenu ni Asyene umbone wakwatendela ŵandu, nambo ŵajinji mwa ŵanganyao ŵangatogolela.');
INSERT INTO "silika" VALUES(27,74,'27:74','Soni chisimu Ambuje ŵenu akumanyilila yaikusisa mitima jao ni yaakuilosya papaswela.');
INSERT INTO "silika" VALUES(27,75,'27:75','Soni pangali chakusisika chilichose kwinani ni pasi, ikaŵeje chili m’Chitabu chakusalichisya (chilichose Al-Lauh Al-Mahfȗz).');
INSERT INTO "silika" VALUES(27,76,'27:76','Chisimu aji Qur’an jikusimulila ku ŵanache ŵa Israila yaijinji mwa ayila yaakuŵa ŵanganyao ali nkutindana.');
INSERT INTO "silika" VALUES(27,77,'27:77','Soni chisimu jele (Qur’aniji) jiŵele chongolo ni chanasa kwa ŵakulupilila.');
INSERT INTO "silika" VALUES(27,78,'27:78','Chisimu Ambuje ŵenu tachijilanya chilikati chao ni ulamusi Wakwe (wakuona), soni Jwalakwe ni Jwamachili gakupunda, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(27,79,'27:79','Basi jegamilani kwa Allah, chisimu mmwejo (Muhammadi صلى الله عليه وسلم) n’di pa yakuona yakuonechela.');
INSERT INTO "silika" VALUES(27,80,'27:80','Chisimu mmwe nganim’ba mwapikanisye ŵawe, soni nganim’ba mwapikanisye asiiwa m’bilango (kupikana kwakwakamuchisya, niyalumo soni achimakafili) ndema jaakutiila achilosyaga migongo jao (kuti akapikana yankwasalila).');
INSERT INTO "silika" VALUES(27,81,'27:81','Soni nganim’ba mmwe ŵanti ni kwajongolela ŵangalola pakwatyosya mu kusokonechela kwao, nganim’ba mwapikanisye ikaŵeje aŵala ŵaakukulupilila ma Ȃya Getu, ni kuŵa ŵanganyao ŵakulipeleka (kwa Allah pakuŵa Asilamu).');
INSERT INTO "silika" VALUES(27,82,'27:82','Sano ndema jalichisimichika liloŵe (lya ilagasyo) pa ŵanganyao, (Kiyama jili jiŵandichile), tuchakopochesya chinyama kuumila mwitaka, chachichiŵa chili nkwaŵechetekasya, ligongo ŵandu ŵaliji akakugakulupilila kusyesyene ma Ȃya Getu.');
INSERT INTO "silika" VALUES(27,83,'27:83','Soni (kumbuchilani) lisiku lituchisonganganya kuumila mu umati uliose likuga mwa aŵala ŵaakukanila ma Ȃya Getu, basi ni tachiŵa ali nkwaalidwa m’mikutula-mikutula (nikusakulidwaga wa kuchiŵalanjilo).');
INSERT INTO "silika" VALUES(27,84,'27:84','Kwikanila ndema jatachiikangana (pameso pa Allah Jwalakwe) tachiti: “Ana mwagakanilaga ma Ȃya Gangu kutendaga nganin’gasyungula kwagamanya chenene, kapena nambi chichi (chine) chamwaliji nkutenda?”');
INSERT INTO "silika" VALUES(27,85,'27:85','Ni lichisimichika liloŵe (lyachonasiko) pa ŵanganyao ligongo lya lupuso lwao, mwanti ngasakombola kuŵecheta (kwakuliŵalanga).');
INSERT INTO "silika" VALUES(27,86,'27:86','Ana nganayiona ŵanganyao yanti Uwwe twatendele chilo kuti apumulejemo, muusi nikuŵa ŵakuloleka? Chisimu mu yalakweyo muŵele mwana ilosyo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(27,87,'27:87','Soni (kumbuchilani) lisiku lyachiligombedwa lipenga nikogopa (kwakuwanako) yosope yaili kumawunde ni yaili petaka, ikaŵeje yataachiisaka Allah (kuti ikajogopa), soni yosope ichim’bichilila Jwalakwe ili yakulinandiya.');
INSERT INTO "silika" VALUES(27,88,'27:88','Ni tinchigaonaga matumbi ni kugaganichisyaga kuti gali chilimbanganile (m’malo mwakwe), nambo kutendaga gali nkwenda mugakwendelaga mawunde gawula. (Gele) nimasengo ga Allah Ajula jwaachikolosisye chenene chindu chilichose. Chisimu Jwalakwe ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(27,89,'27:89','Jwalijose jwatachiika ni (chitendo) chambone, basi tachipata (malipilo) gambone nnope kupunda chalakwecho, nambo soni ŵanganyao tachiŵa pantunjelele ku chogoyo cha lyele lisikulyo.');
INSERT INTO "silika" VALUES(27,90,'27:90','Sano jwalijose jwatachiika ni (chitendo) chakusakala, basi tachiponyedwa ngope syao chamakuku ku Moto, (ni kuchiŵechetedwa kukwao kuti): “Ana nkulipidwa (inepe) pangaŵaga yamwaliji nkupanganya?”');
INSERT INTO "silika" VALUES(27,91,'27:91','(Jilani mmwe Muhammadi صلى الله عليه وسلم): “Chisimu namulidwe yakuti nan’galagatileje (Allah) M’mbuje jwa awu Nsinda (wa Maaka), Ajula jwaŵautesile kuŵa wakuchimbichika, soni chiŵele Chakwe Jwalakwe chindu chilichose, soni ni namulidwe kuti mbe mwa Asilamu.”');
INSERT INTO "silika" VALUES(27,92,'27:92','“Soni ni kuti soomeje Qur’an.” Sano mundu jwaajongweche, basi nikuti akongoka kwalikamuchisya nsyene, nombe najo mundu jwasokonechele, basi (akusokonechela kwalikonya nsyene), jilani: “Chisimu une ndili mwa akutetela.”');
INSERT INTO "silika" VALUES(27,93,'27:93','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Lumbili losope lwene alapikwe Allah. Sampano tannosye imanyilo Yakwe basi ni tiyyimanyilile. Soni Ambuje ŵenu nganaŵa ŵanti ni ngaimanya yankutenda ŵanganyammwe.”');
INSERT INTO "silika" VALUES(28,1,'28:1','Twâ-Sîn-Mîm. ');
INSERT INTO "silika" VALUES(28,2,'28:2','Aga ni ma Ȃya ga m’Chitabu chakusalichisya.');
INSERT INTO "silika" VALUES(28,3,'28:3','Tukunsoomela mmwe sine mwa abali sya Musa ni Firiauna mwakuonaonape, kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(28,4,'28:4','Chisimu Firiauna jwalikwesisye pa chilambo, ni ŵatesile achinsyene (chilambocho) kuŵa mikutula-mikutula, likuga line mwa jamanjao ŵalitondweye machili (ŵanache ŵa Israila ŵatesile kuŵa achikapolo), jwaliji nkusikita ŵanache ŵao ŵachilume ni kwalekaga umi ŵanache ŵao ŵachikongwe, chisimu jwalakwe jwaliji mwa akuwatanganya.');
INSERT INTO "silika" VALUES(28,5,'28:5','Ni twasachile kwatendela yaukoto aŵala ŵaŵatondoyedwe machili pa chilambo, ni kwatenda kuŵa achinnongola, ni kwatenda soni kuŵa ŵawinjila.');
INSERT INTO "silika" VALUES(28,6,'28:6','Ni kuti twalimbanganye ŵanganyao pa chilambo, ni kunnosya Firiauna ni Hamana kwisa soni asilikali ŵao kuumila kwa jamanjao (ŵanache ŵa Israilao) yaŵaliji nkuijogopa (yakwasumula uchimwene wao).');
INSERT INTO "silika" VALUES(28,7,'28:7','Ni twan’juwulile mamagwe Musa yanti: “Mun’jonjesyeje jwalakwe (Musa). Nambo naga mun’jogopele (kuti atakwisa mwankopocheele Firiauna), basi mumponye nnusulo; nkajogopa soni nkadandaula, chisimu Uwwe chitum’buchisye jwalakwe kukwenu, ni kwisa kuntenda kuŵa mwa achimitenga.”');
INSERT INTO "silika" VALUES(28,8,'28:8','Basi ni ŵannokwete ŵandu ŵa Firiauna kuti chasyuche kuŵa mmagongo jwao ni chakudandaulisya chao. Chisimu Firiauna ni Hamana kwisa soni asilikali ŵao ŵaliji ŵakulemwa.');
INSERT INTO "silika" VALUES(28,9,'28:9','Ŵankwao Firiauna ni ŵatite: “Chakuteteŵalila liso lyangune ni lyenumwe! Nkam’bulaga jwalakweju mwine kuŵa nkutukamuchisya, kapena tugambe kuntenda kuŵa mwana (gwetu).” Ni (ŵatesile yalakweyi) ŵanganyao akakumanyilila (yakuichisya yakwe).');
INSERT INTO "silika" VALUES(28,10,'28:10','Sano mu ntima mwa mamagwe Musa mwasyuchile kuŵa mwangali chachipali (ikaŵeje nganisyo syakun’ganisya mwanagwe), mwanti jwasigele panandi kuti ayilochesye ya jwalakwejo (kuti mwanagwe), tungaŵaga kuulimbanganya ntima wakwe (ni chikulupi), kuti aŵe mwa akukulupilila (chilanga cha Allah).');
INSERT INTO "silika" VALUES(28,11,'28:11','Sano (mama) ni ŵansalile nnumbugwe (Musa) kuti: “Munkuyeje jwalakwe (kwagakuja najo mesigo).” Basi jwalakwe ni ŵannolaga chapakutalichila kutendaga ŵanganyao akakumanyilila.');
INSERT INTO "silika" VALUES(28,12,'28:12','Soni Uwwe twaliji tuli tunkanyisye kala jwalakwe (liŵele lililyose lya) akonjesya, (kwikanila ndema jajwaiche nnumbugwe) ni jwatite: “Ana nannanjile kwa achinsyene nyumba ŵatannelele jwalakweju, ni kutama najo soni ŵanganyao mwakunchengeta chenene?”');
INSERT INTO "silika" VALUES(28,13,'28:13','Basi ni twam’buchisye kwa mamagwe kuti litulalile liso lyao, nambo soni kuti akadandaula, ni kuti amanyilile yanti chilanga cha Allah nichakuonaonape. Nambo ŵajinji mwa ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(28,14,'28:14','Sano ndema jaŵakuiche (Musa) komangala kwakwe ni kuliingaana (ulumelume wakwe), twampele lunda lwakusokoka ni umanyilisi; soni iyyoyopeyo niyatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(28,15,'28:15','Ni jwajinjile mu nsinda pa ndema janti achinsyene nsindao akakumanyilila, basi ni ŵasimene mwalakwemo ŵandu ŵaŵili ali nkumenyana – jwinejo jwakuumila mwikuga lyakwe (mu ŵanache ŵa Israila), sano jwinejo jwakuumila mwa amagongo ŵakwe. Sano ajula jwakuumila mwikuga lyakwe jwam’bendile chikamuchisyo pa ajula jwakuumila mwa amagongo ŵakwe, basi Musa ni ŵantwanjile suŵa ni kum’bulaga. Jwalakwe jwatite: “Ayi (yandesileyi) nimasengo gashetani; chisimu jwalakwe nimmagongo jwakusochelesya, jwakuonechela.”');
INSERT INTO "silika" VALUES(28,16,'28:16','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu une nalitendele lupuso nansyene, basi ngululuchilani.” Basi (Allah) ni ŵankululuchile. Chisimu Jwalakwe ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(28,17,'28:17','Jwalakwe jwatite: “Ambuje ŵangu! Ligongo lya ukoto ŵamumbele, basi ngasimba jwakwakamuchisya akuleŵa.”');
INSERT INTO "silika" VALUES(28,18,'28:18','Basi ni kwanchelele mu nsinda juli jwawoga aku ali nkwembecheya (yakuichisya ya chileŵi chakwe), basi ajile sayi, (ampikene) ajula jwana kum’bendaga chikamuchisyo liso ali nkun’gumilila (pakum’bilanga kuti ankamuchisye soni pa mmagongo jwakwe jwine). Musa ŵansalile jwalakwe kuti: “Chisimu mmwe ni nkusokonesya jwakuonechela.”');
INSERT INTO "silika" VALUES(28,19,'28:19','Basi pandema jajwasachile (Musa) kuntwanga ajula mmagongo jwa ŵanaŵaŵilio, (nkuŵenda chikamuchisyo jula jwapelaga kuti Musa akusaka kuntwanga jwele ni) jwatite: “Ana nkusaka kumulaga mpela yamwatite pakuulaga mundu liso? Mmwe ngankusaka inepe, ikaŵeje kuti m’be ŵakulikwesya pa chilambo, nambo soni ngankusaka kuŵa mwa akwilanya.”');
INSERT INTO "silika" VALUES(28,20,'28:20','Ni jwaiche mundu kuumila kumbesi kwa nsinda ali nkutugala. Jwalakwe jwatite: “E mmwe Musa! Chisimu achinkulungwa akunnanganila yakum’bulaga, basi kopokani (m’chilambo muno), chisimu une ndili mwa akunsachilila mmwe yambone.”');
INSERT INTO "silika" VALUES(28,21,'28:21','Basi ni jwakopweche mwalakwemo juli ni woga, aku juli nkwembecheya (kumpata yakusakala kuumila kwa amagongo ŵakwe). Jwatite: “Ambuje ŵangu! Munyokolani ku ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(28,22,'28:22','Sano ndema jajwalungeme ku upande wa Madiyana jwatite: “Mwine Ambuje ŵangu kuŵa nkunjongolela kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(28,23,'28:23','Sano ndema jajwagayiche mesi ga ku Madiyana, jwalisimene palakwepo likuga lya ŵandu linankumwesya (ilango yao), ni jwasimene mungulugulu mwao achakongwe ŵaŵili ali nkuisiŵilila (ilango yao kuti ikamwa nao mesi). Jwalakwe jwatite: “Chichi chipali kukwenu?” Ŵanganyao ŵatite: “Nganituŵa tumwesisye (ilango yetu) mpaka akatyosye kaje akuchinga (ilango yao ligongo nganituŵa tukombwele kulimbana nao), baba ŵetu soni ŵachekulupe nnope.”');
INSERT INTO "silika" VALUES(28,24,'28:24','Basi jwalakwe ni ŵamwechesye ŵanganyao (ilango yao), kaneko ni jwagalauchile pa m’bulili ni jwatite: “Ambuje ŵangu! Chisimu une ngulajila chambone chilichose champaka munduluchisye.”');
INSERT INTO "silika" VALUES(28,25,'28:25','Basi ni ŵam’bichilile jumpepe mwa (achakongwe) ŵaŵili ŵala, junankwenda mwasonisoni. (Jwankongwe) jwatite: “Chisimu atati ŵangu akum’bilanga kuti akan’dipile malipilo ga kutumwechesya uwwe (ilango yetu).” Basi ndema ja ŵam’bichilile (Shuaibu) ni kunsimbila abali, jwalakwe jwatite: “Nkajogopa, n’jokweche ku ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(28,26,'28:26','Jumpepe mwa (achakongwe) ŵaŵili ŵala jwatite: “E atati ŵangu! Munnembani jwalakweju masengo (gakuchinga ilango mmalo mwetuwe), chisimu jwambone kunnemba mmwe masengo ni jwamachili nambo soni jwakulupichika, (sano inaiŵiliyo mwa jwalakweju ipali).”');
INSERT INTO "silika" VALUES(28,27,'28:27','(Shuaibu) jwatite: “Chisimu une ngusaka kuti nannombesye jumpepe mwa asikana ŵangu ŵaŵiliŵa, pakundumichila yaka nsano naitatu, nambo naga tinkwanisye likumi basi tikuŵe kusaka kwenu. Soni ngangusaka kunnonopeyechesya (pakuntupiya yaka). Timusimane – nasakaga Allah – kuti ndili mwa ŵandu ŵambone (ŵakwanisya chilanga).”');
INSERT INTO "silika" VALUES(28,28,'28:28','(Musa) jwatite: “Chalakwe (chilangachi) chili chilikati changune ni alakwe. Ndema jilijose mu siŵilisi jachingwanilisye, basi ngatendeledwa lupuso, soni Allah ni Nkulolela pa yatukuŵechetayi.”');
INSERT INTO "silika" VALUES(28,29,'28:29','Basi katema kajwakwanilisye Musa ndema (jila) ni kuŵigula achijendaga ni liŵasa lyakwe jwauweni moto ku upande wetumbi. Jwalakwe jwalisalile liŵasa lyakwe kuti: “Jembechelani, chisimi une nauweni moto; mwine une kuŵa nkum’bichilila najo abali jinejakwe kuumila kwalakweko, kapena chisinga chamoto kuti jenumanja n’joote.”');
INSERT INTO "silika" VALUES(28,30,'28:30','Basi ndema ja jwauŵiche (motowo), jwaŵilanjidwe kuumila ku upande wakun’dyo kwa chiŵata, pewuto lyaupile kuumila m’chitela: “E mmwe Musa! Chisimu Une ni Allah, M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(28,31,'28:31','“Soni ponyani pasi simbo jenu.” Basi ndema jajwajiweni jinankutenganyika nti chisawu lijoka, jwagalawiche kutila soni nganijulola munyuma. (Allah jwatite): “E mmwe Musa! Ŵandichilani soni nkajogopa. Chisimu mmwejo n’di mwa akuŵa pantunjelele.”');
INSERT INTO "silika" VALUES(28,32,'28:32','“Jinjisyani ligasa lyenu pa mitima pa kansu jenu chilikopoche lili lyeswela (lyakumelemenda) ngaŵaga mwaulwele, soni umbatisyani ligasa lyenu chakungwapa kwenu ligongo lya woga (waumpatile pakuliona lijoka basi ni tiumale wogao, nambo soni ligasalyo tiliujile mulyaŵelele), basi yalakweyi ni ilosyo iŵili yakuumila kwa Ambuje ŵenu kuja kwa Firiauna ni nduna syakwe. Chisimu ŵanganyao aŵele ŵandu ŵakunyosya.”');
INSERT INTO "silika" VALUES(28,33,'28:33','(Musa) jwatite: “Ambuje ŵangu! Chisimu une nawuleje mundu mwa ŵanganyao, ni ngogopa kuti takambulaje.”');
INSERT INTO "silika" VALUES(28,34,'28:34','“Soni achimwene wangu Haruna niwali wakuchachuka lulimi kumbunda une; basi mwatumisyani none kuŵa akungamuchisya kuti akandwatwajileje (akanjitichisyeje yatingaŵecheteje). Chisimu une ngogopa kuti ŵanganyao ata kuja kwisa mwanganile.”');
INSERT INTO "silika" VALUES(28,35,'28:35','(Allah) jwatite: “Chitun’dimbanganye nkono wenu ni nkulugwenujo, ni chitum’biichile ŵanaŵaŵilimmwe machili, mwanti ŵanganyao nganaŵa akuyiche kukwenu (pakumpa isausyo); ni isimosimo Yetu, ŵanaŵaŵilimmwe kwisa soni ŵandu ŵakunkuya ni ŵatim’be ŵakupunda.”');
INSERT INTO "silika" VALUES(28,36,'28:36','Basi ndema jaŵaichilile Musa ŵanganyao ni isimosimo Yetu yakuonechela papaswela, ŵanganyao ŵatite: “Nganiyiŵatu ayi ikaŵeje usaŵi wakutungwa, soni nganitupikaneje ya yalakweyi kwa achatati ŵetu ŵandanda.”');
INSERT INTO "silika" VALUES(28,37,'28:37','Musa ni jwatite: “Ambuje ŵangu ni ŵaakummanya chenene jwaiche ni chongolo kuumila Kukwakwe, ni jwachijiŵe mbesi jakwe (jambone) pa chilambo (cha duniya chino ni ku Akhera kwakwe). Chisimu chene ŵalupuso ngaŵa nkupunda.”');
INSERT INTO "silika" VALUES(28,38,'28:38','Firiauna ni jwatite: “E jenumanja achinduna! Ngangummanyilila kukwenu nnungu jwinejwakwe jwangaŵaga une, basi mungolechesyani mmwe Hamana (moto wakochela njelwa) syelongo, ni mundaŵile chipilala kuti mwine une ngan’dingule nnungu jwa Musa, soni chisimu une ngun’ganichisya jwalakwe kuti juli mwa akulambusya.”');
INSERT INTO "silika" VALUES(28,39,'28:39','Ni jwalikwesyisye jwalakwe ni asilikali ŵakwe pa chilambo mwangaŵajilwa, ni ŵaganichisyaga kuti ŵanganyao Kukwetu ngasauchisyidwa.');
INSERT INTO "silika" VALUES(28,40,'28:40','Ni twankwembekenye jwalakwe ni asilikali ŵakwe ni twaaponyisye m’mbwani. Basi nnole mujaŵelele mbesi ja ŵalupuso.');
INSERT INTO "silika" VALUES(28,41,'28:41','Ni twatesile ŵanganyao kuŵa achinnongola ŵakuŵilanjila ku Moto, soni lisiku lya Kiyama ngasakamuchisyidwa.');
INSERT INTO "silika" VALUES(28,42,'28:42','Ni twakuyichisye ŵanganyao pa duniya pano ni malweso, sano lisiku lya Kiyama ŵanganyao tachiŵa mwa akunyalaya kusyene, (ŵakutalichisyidwa ku yambone iliyose)');
INSERT INTO "silika" VALUES(28,43,'28:43','Soni chisimu pamasile patwampele Musa Chitabu panyuma pakujijonanga mikutula jandanda, kuŵa muli kwa ŵandu, chongolo nambo soni chanasa kuti ŵanganyao akumbuchile.');
INSERT INTO "silika" VALUES(28,44,'28:44','Ni nganim’ba mmwe (Muhammadi صلى الله عليه وسلم) kuupande wa kwigulo (kwetumbi) pandema jatwampaga Musa lilamusi (lya utume), nambo soni nganim’ba mwa akutendela umboni.');
INSERT INTO "silika" VALUES(28,45,'28:45','Nambo Uwwe twagumbile mikutula (jejinji panyuma pa Musa), ni galewipe maumi gao (mwanti mpaka syaliŵaliche abali sya achimitume). Soni mmwe (Muhammadi صلى الله عليه وسلم) nganintameje ku ŵandu ŵa ku Madiyana ni kwasoomelaga ma Ȃya Getu, (kwanti mpaka ni nsimanye abali syao), nambo Uwwe ni ŵatuŵele Akutumisya (achimitume ni kutumisya abali syao kukwenu).');
INSERT INTO "silika" VALUES(28,46,'28:46','Soni mmwe (Muhammadi صلى الله عليه وسلم) nganim’ba ku upande wetumbi ndema jatwam’bilangaga (Musa), nambo (mmwe tuntumisye kuŵa) ukoto wakuumila kwa Ambuje ŵenu kuti mwatetele ŵandu ŵanganijwaichilileje nkutetela mmwe nkanim’biche, kuti ŵanganyao akumbuchile.');
INSERT INTO "silika" VALUES(28,47,'28:47','Soni ingaŵaga kuti nkwapata ipato ligongo lya yagalongwesye makono gao ni akutiji: “Ambuje ŵetu! Uli ngatumisya kukwetu ntenga kuti tukuye ma Ȃya Genu ni kuŵa mwa akukulupilila?” (Nga nganituntumisya kukwao, nambo tuntumisye kuti asoŵe chakulambuchisya).');
INSERT INTO "silika" VALUES(28,48,'28:48','Basi ndema jachaichilile chakuona (Muhammadi صلى الله عليه وسلم) kuumila Kukwetu akutiji: “Nambi uli nganapedwa yanti mpela yaŵapedwile Musa?” Ana ŵanganyao nganaikana yaŵapedwile Musa kala? Ni akutiji (pakwamba ya Taurat ni Qur’an): “Usaŵi uŵili ŵaukamusyana.” Ni akutiji: “Chisimu uwwe tukuukana woseo.”');
INSERT INTO "silika" VALUES(28,49,'28:49','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Basi ikani nacho chitabu kuumila kwa Allah chachili chakongoka nnope kumpunda iŵiliyi, kuti une nachikuyeje nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(28,50,'28:50','Nambo naga ŵanganyao akan’janga mmwe, basi manyililani yanti ŵanganyao agamba kuya isako yao. Ana ninduni jwali jwakusokonechela nnope kwakumpunda mundu jwagamba kuya isako yakwe mwangali chongoko chakuumila kwa Allah? Chisimu Allah ngakwajongolaga ŵandu ŵalupuso.');
INSERT INTO "silika" VALUES(28,51,'28:51','Soni chisimu pamasile pa twalunguchisye ŵanganyao maloŵe (gachamuko jajili Qur’an), kuti mwine takumbuchile (nambo kwapi)!');
INSERT INTO "silika" VALUES(28,52,'28:52','Aŵala ŵanakwapaga chitabu kala jalakwe (Qur’an) nkanijiyiche, ŵanganyao jalakwe akujikulupilila (mpela ‘Abdullâh bin Salâm ni Salmân Al-Fârisî).');
INSERT INTO "silika" VALUES(28,53,'28:53','Soni pati jichisoomedwaga kukwao akasatiji: “Uwwe tujikulupilile jalakwe, yalakweyi niyakuona yakuumila kwa M’mbuje gwetu, chisimu uwwe namuno nkanijiŵe jalakweji, twaliji Asilamu (ŵakuya Musa ni Isa).”');
INSERT INTO "silika" VALUES(28,54,'28:54','Ŵanganyao tachipedwa malipilo gao kaŵilikose ligongo lya kupilila kwao, ni akutyosyaga ni yambone yakusakala, nambo soni mu ichindu yatwapele akasatolaga. ');
INSERT INTO "silika" VALUES(28,55,'28:55','Soni pati apikene yangali mate akasaiŵambalaga ni akasatiji: “Uwwe tukwete masengo getu, nombe jenumanja nkwete masengo genu. Ntendele uŵe kukwenu. Uwwe ŵangasachilila (kwakuya) ajinga.”');
INSERT INTO "silika" VALUES(28,56,'28:56','Chisimu mmwe nganim’ba mun’jongwele jwamunnonyele, nambo Allah akun’jongolaga jwakunsaka, soni Jwalakwe ni jwaakwamanya chenene akongoka.');
INSERT INTO "silika" VALUES(28,57,'28:57','Sano ŵanganyao ni ŵatite: “Naga tukuye chongoko chan’di nacho chituswambidwe m’chilambo mwetu.” Ana nganitwatamilikachisya ŵanganyao malo gakuchimbichika gantunjelele (Maaka), kwaikwika mwalulele isogosi yamitundumitundu kuŵa lisiki lyakuuma Kukwetu? Nambo ŵajinji mwa ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(28,58,'28:58','Soni jilingwajilingwa mwa misi jatwajijonasile, janakutwanjilaga ndamo jao (jambone)! Sanotu amo nimmalo mwao mwanganimutamidweje panyuma pao ikaŵeje panandipe, soni Uwwe niŵatwaliji Ŵawinjila.');
INSERT INTO "silika" VALUES(28,59,'28:59','Ni nganaŵa Ambuje ŵenu (Allah) ŵanti nikonanga misi mpaka akatumisye mu nsinda wakwe wekulungwa ntenga jwakwasoomela ŵanganyao ma Ȃya Getu (nkugakanamo nikwajonangaga), soni nganituŵa ŵakujijonanga misi ikaŵeje achimisyeneo ali ŵalupuso.');
INSERT INTO "silika" VALUES(28,60,'28:60','Soni ichindu iliyose yampedwile iŵele yakusengwasya umi wa duniya ni ipeto yakwe, nambo yaili kwa Allah niyambone nnope soni yakwendelechela, ana ngankuitaga mulunda?');
INSERT INTO "silika" VALUES(28,61,'28:61','Ana mundu jwatutaŵene najo chilanga chambone (chakuja kwinjila ku Mbepo) mwanti jwalakwe takasimangane nacho, mpaka julandane ni mundu jwatunsengwasisye ni yakusengwasya ya umi wa duniya, kaneka jwalakwe lisiku lya Kiyama nikuja kuŵa mwa akwika nao (kuti aponyedwe m’Moto)?');
INSERT INTO "silika" VALUES(28,62,'28:62','Soni (kumbuchilani) lisiku lyatachaŵilanga ŵanganyao (Allah) basi ni tachiti: “Ali kwapi akumwanganya nao aŵala ŵamwaliji nkulitwanjila?”');
INSERT INTO "silika" VALUES(28,63,'28:63','Tachiŵecheta aŵala ŵalichisimichika pa ŵanganyao liloŵe (lyakupedwa ilagasyo) kuti: “Ambuje ŵetu! Aŵa ni aŵala ŵatwasochelesye. Twasochelesye ŵanganyaŵa mpela yatwatite pakusochelela (twachimisyewe), tukuliipaata Kukwenu; nganaŵa ŵanganyao nkutugalagatila uwwe.”');
INSERT INTO "silika" VALUES(28,64,'28:64','Ni ichiŵechetedwa yanti: “Mwaŵilangani akwawanganya ŵenu.” Basi ni tachaŵilanga nambo ngasiŵajanga, ni tachiyiona ilagasyo. (Pa jele ndemajo tachisachilila yanti): Bola ŵanganyao angaŵe ŵajongokaga (pa duniya nikuti nga nganaiona ilagasyoyo).');
INSERT INTO "silika" VALUES(28,65,'28:65','Soni (kumbuchilani) lisiku lyatachaŵilanga ŵanganyao (Allah) basi ni tachiti: “Ana ichichi mwajanjile achimitenga?”');
INSERT INTO "silika" VALUES(28,66,'28:66','Ni sichasoŵa ŵanganyao abali (sya kwanga kwakwajokola) lyele lisikulyo, soni ŵanganyao ngasawusyana.');
INSERT INTO "silika" VALUES(28,67,'28:67','Nambo mundu jwatesile toba ni kulupilila ni kutendaga soni yambone, basi jwalakwe tachiŵa mwa ŵandu ŵakupunda.');
INSERT INTO "silika" VALUES(28,68,'28:68','Sano Ambuje ŵenu akasagumba chaakusaka ni kusagula (chaakusaka). Nganakola ŵanganyao kusagula kulikose. Aswejele Allah, soni atukwiche kwannope ku yaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(28,69,'28:69','Soni Ambuje ŵenu akuimanyilila yaikusisa mitima jao ni yaakuilochesya papaswela.');
INSERT INTO "silika" VALUES(28,70,'28:70','Soni Jwalakwe ni Allah; pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo. Jukwete kulapilidwa kosope kwene pandanda (pa duniya pano) ni pambesi pakwe (ku Mbepo), soni jukwete ulamusi, soni Kukwakwe tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(28,71,'28:71','Jilani: “Ana nkuiona uli naga Allah ali am’biichiile jenumanja chilo kuŵa chakwendelechela mpaka lisiku lya Kiyama, ana ninnunguchi jwangaŵaga Allah jwampaka am’bichilile nalyo lilanguka? Ana jenumanja ŵangapikana?”');
INSERT INTO "silika" VALUES(28,72,'28:72','Jilani: “Ana nkuiona uli naga Allah ali am’biichiile jenumanja muusi kuŵa wakwendelechela mpaka lisiku lya Kiyama, ana ninnunguchi jwangaŵaga Allah jwampaka am’bichilile nacho chilo chakuti ntulalilejemo? Ana jenumanja ŵangalola?”');
INSERT INTO "silika" VALUES(28,73,'28:73','Soni mwa ukoto Wakwe, ŵam’biichiile jenumanja chilo ni muusi kuti ntulalilejemo (nchilomo), ni kuti nsosejesoseje umbone Wakwe (muusi), ni kuti jenumanja m’beje ŵakutogolela.');
INSERT INTO "silika" VALUES(28,74,'28:74','Soni (kumbuchilani) lisiku lyatachaŵilanga ŵanganyao (Allah) basi ni tachiti: “Ali kwapi akumwanganya nao aŵala ŵamwaliji nkulitwanjila?”');
INSERT INTO "silika" VALUES(28,75,'28:75','Ni tuchikoposya kuumila mu nkutula uliose mboni (jakuutendela umboni nkutulao) ni tuchiti: “Ikani nao umboni wenu.” Basi ni tachimanyilila yanti: Yakuona ni ya Allah (Jikape), ni ichasokonechela ŵanganyao (isanamu) yaŵaliji nkuisepelela.');
INSERT INTO "silika" VALUES(28,76,'28:76','Chisimu Qaruna jwaliji mu ŵandu ŵa Musa, nambo jwalikwesyisye kukwao. Ni twampele imatiilo yachipanje yanti; makiyi gakwe ganti nikusitopelwa nago likuga lya ŵandu ŵamachili. (Kumbuchilani) katema kaŵansalile ŵandu ŵakwe kuti: “Nkatwangaga, chisimu Allah jwangaanonyela ŵakutwanga.”');
INSERT INTO "silika" VALUES(28,77,'28:77','“Ni nsosejesoseje ni yampele Allah Nyumba Jambesi, ni ngasin’diŵalila liunjili lyenu lya duniya, soni kolosyani (masengo) yatite Allah pakunkolochesya mmwe (ichindu yenu), ni ngasinsachilila kuwatanganya pa chilambo, chisimu Allah jwangaanonyela akuwatanganya.”');
INSERT INTO "silika" VALUES(28,78,'28:78','Jwalakwe jwatite: “Chisimu mbedwile yalakweyi ligongo lya umanyilisi wangwete.” Ana ngajukumanyilila yanti Allah pamasile paŵajijonasile m’bujo mwakwe mikutula jajaliji jakulimbangana nnope kumpunda jwalakwejo, nambo soni jakutupiya nnope kusonganganya (chipanje)? Sano akuleŵa ngasaja kuwusyidwa ya sambi syao.');
INSERT INTO "silika" VALUES(28,79,'28:79','Basi ni jwakopweche (mwakutwanga) ku ŵandu ŵakwe juli julipetilepetile. Aŵala ŵaŵaliji nkusachilila umi wa duniya ŵatite (pakumbila): “Ah, nabola uwwe tungakole yanti mpela yapedwile Qaruna! Chisimu jwalakwe jukwete liunjili lyekulungwa nnope.”');
INSERT INTO "silika" VALUES(28,80,'28:80','Nambo aŵala ŵaŵapedwile ilimu (ya dini) ŵatite: “Kutapana kwenu ŵanganyammwe! (Yele yangakumbila), malipilo ga Allah (ku Akhera) ni gagachiŵa gambone nnope kwamundu jwaakulupilile ni kutendaga yambone. Ni ngasaja kupedwa yalakweyo ikaŵeje akupilila.”');
INSERT INTO "silika" VALUES(28,81,'28:81','Basi ni twantitimisye nalyo litaka jwalakwe ni nyumba jakwe, ni nganijukola likuga line lililyose lyakunkamuchisya kulimbana ni Allah, soni nganijuŵa mwa akulikamuchisya achimisyene.');
INSERT INTO "silika" VALUES(28,82,'28:82','Sano aŵala ŵaŵakumbilaga uchimbichimbi wakwe mwaliso ŵatandite kuŵecheta kuti: “E yakuona! Allah akunsapangulilaga lisiki jwansachile (namuno juli jwakuŵenjedwa Kukwakwe), ni kunnandiyichisya (lisikilyo jwansachile namuno juli jwakunonyeledwa Kukwakwe). Ingaŵaga kuti Allah jwatutendele ukoto uwwe, angatutitimisye. E yakuona! Achimakafili nganaŵa apundile.”');
INSERT INTO "silika" VALUES(28,83,'28:83','Ajila nyumba ja Akhera (Mbepo) tujiŵisile kuŵa ja aŵala ŵangaakusaka kulikwesya pa chilambo namuno kuwatanganya. Sano mbesi jambone jili kwa ŵawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(28,84,'28:84','Jwalijose jwatachiika ni (chitendo) chambone basi tachipata (malipilo) gambone nnope kumpunda chalakwecho, nombe soni jwalijose jwatachiika ni (chitendo) chakusakala, basi ngasalipidwa aŵala ŵaŵatendaga yakusakala ikaŵeje yaŵaliji nkutenda.');
INSERT INTO "silika" VALUES(28,85,'28:85','Chisimu Ajula jwantuluchisye mmwe (Muhammadi صلى الله عليه وسلم) Qura’an tachim’buchisya kwene kumalo kwakuwujila (ku Maaka kapena ku Mbepo). Jilani: “Ambuje ŵangu niŵaakummanyilila chenene jwaiche ni chongoko, ni jwali nkupotela kwakuonechela.”');
INSERT INTO "silika" VALUES(28,86,'28:86','Soni mmwe (Muhammadi صلى الله عليه وسلم), nganin’jembecheyaga yanti Chitabu (Qur’an) ni kutumisyidwa kukwenu, ikaŵeje (chitumisyidwe kwa) ukoto wakuumila kwa Ambuje ŵenu, basi ngasim’ba ata panandi nkwakamuchisya achimakafili (pa dini jao jaakum’bilanjila).');
INSERT INTO "silika" VALUES(28,87,'28:87','Soni akasansiŵilikanganya ŵanganyao ata panandi ku ma Ȃya ga Allah panyuma panti gali gantuluchile, soni ŵilanjilani kwa Ambuje ŵenu, ni ngasim’ba ata panandi mwa akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(28,88,'28:88','Ni ngasimunnumbanjila pamo ni Allah nnungu jwine. Pangali nnungu jwine (jwakuŵajilwa kunnumbanjila) ikaŵeje Jwelepejo. Chindu chilichose chichijonasika kusigala Ngope Jakwe pe. Ulamusi ni Wakwe, soni Kukwakwe tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(29,1,'29:1','Alif-Lâm-Mîm. ');
INSERT INTO "silika" VALUES(29,2,'29:2','Ana ŵandu akuganichisya yanti tagambe kulechedwa achiŵechetaga yanti: “Tukulupilile.” Kutendaga ŵanganyao akakulinjidwa (pachikulupi chao)?');
INSERT INTO "silika" VALUES(29,3,'29:3','Soni chisimu pamasile patwalinjile aŵala ŵapaujo pao. Basi Allah tiŵamanyukule kusyene aŵala ŵaakulupichiche (pachikulupi chao), ni tiŵamanyukule soni kusyene ŵaunami.');
INSERT INTO "silika" VALUES(29,4,'29:4','Kapena akuganichisya aŵala ŵaakupanganya yakusakala kuti kuŵa nkutupunda Uwwe (pakutugwala mwanti ningaapa ilagasyo)? Kaje kusakala nnope chiilanyo chao!');
INSERT INTO "silika" VALUES(29,5,'29:5','Mundu jwaakwembecheya kusimangana ni Allah, basi chisimu ndema ja Allah (jakusimanganila Najo) jikwisa kwene (mwangalepecheka), soni Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(29,6,'29:6','Sano jwaakulimbichila (pakulimbana ni amagongo kapena ni ntima wakwe), nikuti akulimbichila kwaalikamuchisya nsyene, chisimu Allah ni Jwakusichila nnope kwangalajila iwumbe (iliyose).');
INSERT INTO "silika" VALUES(29,7,'29:7','Sano aŵala ŵakulupilile ni kutendaga yambone, tuchasimachisya kwene yakusakala yao ni kwalipila kwene pa yambone nnope yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(29,8,'29:8','Sano mundu tunchisisye kwatendela yambone achinangologwe ŵanaŵaŵili. Nambo naga achinkanganichisyaga kuti mumwanganye Une ni yanganinkola nayo umanyilisi, basi ngasimwakunda. Kukwangu ni kwakuujila kwenu, basi ni tinjintagulila yamwaliji nkutenda.');
INSERT INTO "silika" VALUES(29,9,'29:9','Sano aŵala ŵakulupilile ni kutendaga yambone, chiitwajinjisya kwene mu (ŵandu) ŵambone.');
INSERT INTO "silika" VALUES(29,10,'29:10','Soni chilikati cha ŵandu pana ŵakutiji: “Tukulupilile mwa Allah.” Nambo naga ali apedwile masausyo ligongo lya Allah, akasagatenda masausyo ga ŵandu nti chisawu ilagasyo ya Allah, sano naga chili chiyiche chikulupusyo chakuumila kwa Ambuje ŵenu chisimu (achinakunaku) akutiji: “Chisimu uwwe twaliji namwe imo,” (nambo chitamilecho ali nkulambusya). Ana nganaŵa Allah kuti ni Jwakumanyilila nnope yaili m’mitima mwa iwumbe yosope?');
INSERT INTO "silika" VALUES(29,11,'29:11','Sano Allah tiŵamanyukule kusyene aŵala ŵakulupilile ni tiŵamanyukule soni kusyene achinakunaku.');
INSERT INTO "silika" VALUES(29,12,'29:12','Sano aŵala ŵakanile ni akutiji pakwasalila aŵala ŵakulupilile: “Kuyani litala lyetu; ni chitukantwichilile ileŵi yenu.” Kutendaga ŵanganyao nganaŵa ajile kwatwichilila mu ileŵi yao chilichose. Chisimu ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(29,13,'29:13','Ni tachitwichila ŵanganyao misigo jao (jasambi ligongo lya ukafili wao) ni misigo jine pakupwatika pamisigo jao (ligongo lyakwasochelesya ŵane), ni tachiwusyidwa kusyene lisiku lya Kiyama pa unami waŵaliji nkusepelela.');
INSERT INTO "silika" VALUES(29,14,'29:14','Soni chisimu pamasile patwantumisye Nuhu ku ŵandu ŵakwe, ni jwatemi kukwao yaka sausande kusapula makumi nsano (950), basi ni chakwembekenye ŵanganyao chigumula aku ali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(29,15,'29:15','Basi ni twan’jokwele jwalakwe ni ŵaŵaliji m’chombo, ni twachitesile (chombocho) kuŵa chisimosimo ku iwumbe yosope.');
INSERT INTO "silika" VALUES(29,16,'29:16','Soni (munkumbuchilani) Ibrahima pandema jaŵasalile ŵandu ŵakwe kuti: “Mun’galagatilani Allah ni kun’jogopa Jwalakwe. Yalakweyo niyaili yambone kukwenu nam’baga jenumanja ŵakumanyililaga.”');
INSERT INTO "silika" VALUES(29,17,'29:17','“Chisimu jenumanja nkugalagatila isanamu kunneka Allah, ni n’gamba kulisepela unami. Chisimu aŵala ŵankwagalagatila kunneka Allah, nganaŵa ampatiile jenumanja lisiki, basi sosagani lisiki kwa Allah pe, ni mun’galagatileje (Jwelepejo), soni muntogoleleje, soni Kukwakwe tinchiuchisyidwa.”');
INSERT INTO "silika" VALUES(29,18,'29:18','Sano naga jenumanja nkanile basi (ngaiŵa yachimamo, ngati) pamasile pajakaaniile mikutula ja paujo penu, soni nganigapagwa kwa Ntenga صلى الله عليه وسلم (masengo gane), ikaŵeje kulungusya (utenga) mwakupikanika.');
INSERT INTO "silika" VALUES(29,19,'29:19','Ana ngakuiona ŵanganyao yaakuti Allah pakatandisye gumba iwumbe, kaneko ni kuiwusyawusya? Chisimu yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(29,20,'29:20','Jilani: “Jendagani pa chilambo ni kupita nchilola yaŵatite pakatandisye gumba iwumbe, kaneko Allah ni tachigumba kagumbe ka kwa Akhera (kwakuli kwimusya ŵawe m’malembe). Chisimu Allah, pachindu chilichose juŵele Jwakombola kusyene.');
INSERT INTO "silika" VALUES(29,21,'29:21','Akumpaga ilagasyo jwansachile ni kuntendela chanasa jwansachile, soni Kukwakwe tinchigalauchisyidwa.');
INSERT INTO "silika" VALUES(29,22,'29:22','Soni nganim’ba jenumanja ŵanti ni kulepelekasya pa chilambo (cha pasi), kapena kwinani (kunnepelekasya Allah kumpa ipotesi), soni nganinkola jwangaŵaga Allah akun’gosa namuno akunkamuchisya.');
INSERT INTO "silika" VALUES(29,23,'29:23','Sano aŵala ŵaakanile ma Ȃya ga Allah ni yakusimangana Najo, ŵanganyao niŵakatile tama ku chanasa Changu, soni ŵanganyao akwete kupata ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(29,24,'29:24','Basi ni nganiuŵa wanje wa ŵandu ŵakwe (ŵa Ibrahima) ikaŵeje ŵagambile kuti: “Mum’bulagani kapena munchome moto.” Basi Allah ni ŵan’jokwele ku moto. Chisimu mu yalakweyo muŵele mwana ilosyo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(29,25,'29:25','Sano (Ibrahima) jwatite: “Chisimu jenumanja n’ditendele isanamu (kuŵa yakuigalagatila) kunneka Allah. Chinonyelo chilikati chenu chiŵele cha umi wa pa duniya peuno, nambo lisiku lya Kiyama tinchikanana jwine ni n’jakwe, nambo soni tinchilwesana jwine ni n’jakwe, soni mauto genu ni ku Moto, soni ngasinkola akunkulupusya.”');
INSERT INTO "silika" VALUES(29,26,'29:26','Basi Lutwi ŵankulupilile jwalakwe. (Ibrahima) ni jwatite: “Chisimu une ngusama wa kwa Ambuje ŵangu (Allah). Chisimu Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(29,27,'29:27','Ni twampele jwalakwe Isihaka ni Yaakubu, ni twaŵisile mu ŵanache ŵakwe utume ni chitabu, ni twampele malipilo gakwe pa duniya, soni chisimu jwalakwe ku Akhera tachiŵa mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(29,28,'29:28','Soni (munkumbuchilani) Lutwi pandema jajwatite ku ŵandu ŵakwe: “Chisimu ŵanganyammwe nkuiŵichilila yausakwa yanganannongoleleje ku (itenda) yalakweyo jwalijose mu iwumbe yosope.”');
INSERT INTO "silika" VALUES(29,29,'29:29','“Ana chisimu ŵanganyammwe nkwaichilila achalume (pakwatenda matanyula), ni kutendaga ukachikwanje m’matala, ni kwikaga nayo itendo yangalumbana mu nganya syenu?” Basi ni nganiuŵa wanje wa ŵandu ŵakwe ikaŵeje ŵagambile kuti: “Ikani nayo kukwetu ilagasyo ya Allah, nam’baga n’di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(29,30,'29:30','Jwalakwe jwatite: “Ambuje ŵangu! Munjenjelani ku ŵandu ŵakuwatanganya.”');
INSERT INTO "silika" VALUES(29,31,'29:31','Sano katema kaŵaiche achimitenga Ŵetu kwa Ibrahima ni abali jakusengwasya ŵatite: “Chisimu uwwe chitwajonanje achinsyene awu musi (wa Sodomu), chisimu achinsyene ŵakwe aŵele ŵalupuso.”');
INSERT INTO "silika" VALUES(29,32,'29:32','(Ibrahima) jwatite: “Chisimu mwalakwemo mwana Lutwi.” Ŵanganyao ŵatite: “Uwwe tukwamanya chenene ŵali mwalakwemo. Chisimu uwwe chitun’jokole jwalakwe ni liŵasa lyakwe ikaŵeje ŵankwakwepe, chaŵe mwa akusigalila munyuma (chajonanjidwe).”');
INSERT INTO "silika" VALUES(29,33,'29:33','Sano paŵagambile kum’bichilila Lutwi achimitenga Ŵetu, jwadandawile yaŵaganyao nikuŵanikwa nao chidali (pakwaajogopela kuti atakutendedwa matanyula, ni kusoŵa yakuti kaachenjele, ligongo nganiŵamanya kuti achimalaika). Ŵanganyao ni ŵatite: “Nkajogopa soni nkadandaula, chisimu uwwe chitun’jokole mmwe ni liŵasa lyenu ikaŵeje ŵankwenupe, chaŵe mwa akusigalila munyuma (chajonanjidwe).”');
INSERT INTO "silika" VALUES(29,34,'29:34','“Chisimu uwwe tusigele panandi kwatuluchisya achinsyene awu musi (wa Sodomu) ipotesi kutyochela kwinani ligongo lya kunyosya kwao.”');
INSERT INTO "silika" VALUES(29,35,'29:35','Soni chisimu pamasile patwalesile mwalakwemo chimanyilo chakuonechela kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(29,36,'29:36','Sano (kuŵandu ŵa) ku Madiyana (twantumisye) nnongo n’jao (jwali) Shuaibu. Basi jwatite: “E ŵandu ŵangu! Mun’galagatilani Allah, ni llijogope Lisiku Lyambesi, soni nkajendagajendaga pa chilambo kupita nchiwatanganya.”');
INSERT INTO "silika" VALUES(29,37,'29:37','Ni ŵankaaniile, basi ni chakwembekenye chisikinya, mwanti ŵachelelwe (ali ŵawe) m’majumba mwao chitindiŵalile.');
INSERT INTO "silika" VALUES(29,38,'29:38','Nombe nao soni achina Adi ni achina Samuda (twajonasile), soni pamasile pakuonechele kukwenu (konasika kwao) kuumila m’malo mwao muŵatamaga (mwamusyuchile kuŵa m’masame). Soni shetani ni jwasalalichisye ŵanganyao itendo yao (yangalumbana), basi ni jwasiŵilile ŵanganyao kwitala (lyagoloka) namuno kuti ŵaliji ŵakulolechesya (pakutenda ichindu).');
INSERT INTO "silika" VALUES(29,39,'29:39','Nombe najo soni Qaruna, Firiauna ni Hamana (twajonasile). Soni chisimu chene pamasile pajwaichilile ŵanganyao Musa ni isimosimo yakuonechela, basi ni ŵalikwesyisye pachilambo, kutendaga nganaŵa ŵanti ni kumpunda (Allah, mwanti ni kunnepelekasya kwapa ilagasyo).');
INSERT INTO "silika" VALUES(29,40,'29:40','Basi jwalijose (mwa ŵanganyao) twankwembekenye ni sambi syakwe. Basi ŵane mwa jamanjao pana ŵatwatumichisye chimbunga cha sakalawe, ŵane mwa ŵanganyao pana ŵawakwembekenye n’gumilo (waipotesi), ŵane mwa ŵanganyao pana ŵatwatitimisye nalyo litaka, soni ŵane mwa ŵanganyao pana ŵatwamisyisye. Sano Allah nganijuŵa jwanti ni kwatenda lupuso ŵanganyao, nambo kuti ŵanyao ni ŵaŵaliji nkulitenda lupuso achimisyene.');
INSERT INTO "silika" VALUES(29,41,'29:41','Chilandanyo cha aŵala ŵalitendele jwinepe jwangaŵaga Allah kuŵa kaligoselo, chili chisawu nyangata jwalitendele nyumba (ja lundandambuli jangaŵa nkun’gosa kuchitukuta namuno mbepo), soni chisimu naga nyumba jangali machili ninyumba ja nyangata, ingaŵe ŵanganyao ŵamanyililaga.');
INSERT INTO "silika" VALUES(29,42,'29:42','Chisimu Allah jukuimanya ichindu iliyose yaakuilumbanjila ŵanganyao kunneka Jwalakwe, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(29,43,'29:43','Soni yalakweyo ni ilandanyo yatukupeleka ku ŵandu, ni ngakuitaga mu lunda ikaŵeje ŵakumanyililape.');
INSERT INTO "silika" VALUES(29,44,'29:44','Allah jwagumbile mawunde ni mataka mwakuonaonape, chisimu mu yalakweyo mwana chilosyo kwa ŵakulupilila.');
INSERT INTO "silika" VALUES(29,45,'29:45','Soomani (mmwe Muhammadi صلى الله عليه وسلم) yaijuwulidwe kukwenu ya m’Chitabu (cha Qur’an), ni n’jimicheje Swala mwakolosya, chisimu Swala jikasakanyaga yausakwa ni yangalumbana, soni kumbuchila kwa Allah (kwakunkumbuchila jenumanja pameso pa Achimalaika) ni kwakuli kwekulungwa nnope (kumpunda kumbuchila kwenu kwa kunkumbuchila Jwalakwe pa Swala syenu), soni Allah jukumanyilila yankupanganya.');
INSERT INTO "silika" VALUES(29,46,'29:46','Soni nkakangana nao achimisyene Chitabu (Ayuda ni Akilisito) ikaŵeje ni (kakangane) akala kakali kambone (kakulosyana itabu), ni ikaŵeje aŵala ŵali ŵalupuso mwa ŵanganyao. Soni jilani (kwa ŵanganyao): “Uwwe tukulupilile yaitulusyidwe kukwetu ni yaitulusyidwe kukwenu, soni Nnungu jwetu ni Nnungu jwenu Jumpepe, soni uwwe Kukwakwe tukulipeleka (pakuŵa Asilamu).”');
INSERT INTO "silika" VALUES(29,47,'29:47','Soni iyyoyopeyo (yatwatite pakwatuluchisya itabu achimitume ŵakala) tuntuluchisye (mmwe Muhammadi صلى الله عليه وسلم) Chitabu (cha Qur’an), sano aŵala ŵatwapele chitabu (Ayuda ni Akilisito) akuchikulupilila chalakwechi (Qur’an), nambo soni mwa ŵanganyaŵa (ŵarabu ŵanganitwatumichisyeje chitabu chilichose) mwana ŵaakuchikulupilila chalakwechi, ni pangali ŵagakana ma Ȃya Getu ikaŵeje achimakafili.');
INSERT INTO "silika" VALUES(29,48,'29:48','Soni mmwe (Muhammadi صلى الله عليه وسلم) nganinsoomeje chitabu chilichose achi nkanichiyiche, nambo soni nganinchilembeje ni nkono wenu wan’dyo, ingaŵe iyyo nikuti angakayichile ŵakuya yaunami.');
INSERT INTO "silika" VALUES(29,49,'29:49','Nambo jalakwe (Qur’aniji) jiŵele ma Ȃya gakusalichisya gagali m’mitima ja aŵala ŵapedwile umanyilisi, ni pangali ŵagakana ma Ȃya Getu ikaŵeje ŵalupuso.');
INSERT INTO "silika" VALUES(29,50,'29:50','Sano ŵanganyao ni akutiji: “Ligongo chichi ngantuluchila jwalakwe isimosimo kuumila kwa M’mbujegwe?” Jilani: “Chisimu isimosimo ili kwa Allah, sano une ngambile kuŵa nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(29,51,'29:51','Ana ngaikwakwanila ŵanganyao yanti Uwwe tuntuluchisye mmwe (Muhammadi صلى الله عليه وسلم) Chitabu (Qur’an) chachikusoomedwa kukwao? Chisimu mu chalakwecho mwana chanasa nambo soni chikumbusyo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(29,52,'29:52','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Allah akukwanila kuŵa Mboni chilikati changune ni jenumanja, akumanyilila yaili kumawunde ni petaka.” Sano aŵala ŵaakulupilile yaunami ni kunkana Allah, ŵanganyao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(29,53,'29:53','Ni akum’benda kuti ilagasyo iyiche mwachitema. Sano ingaŵaga (kuti pana) ndema jekolanjidwe (jakwichila ilagasyo) ingaliji yaichilile ŵanganyao ilagasyoyo, soni chisimu chene ichaichilila ŵanganyao mwachisupuchisya aku ŵanganyao akakumanyilila.');
INSERT INTO "silika" VALUES(29,54,'29:54','Akum’benda kuti ilagasyo iyiche mwachitema. Sano chisimu Jahannama jichasyungula kwene achimakafili.');
INSERT INTO "silika" VALUES(29,55,'29:55','Lisiku lyaichaunichila ilagasyo kutandila pachanya pao mpaka pasi pangongolo syao, ni tachiŵecheta (Allah kuti): “Pasyani yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(29,56,'29:56','E achikapolo Ŵangu aŵala ŵankulupilile! Chisimu litaka Lyangu liŵele lyakusapanguka (tinkomboleche kwaula chilambo chine naga m’chilambo mwenumo nkulepela kungalagatila mwagopoka), sano Jikape Une basi ngalagatilani.');
INSERT INTO "silika" VALUES(29,57,'29:57','Mundu jwalijose tachipasya chiwa, kaneka Kukwetu tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(29,58,'29:58','Sano aŵala ŵaakulupilile ni kutendaga yambone, chisimu Uwwe chitukatamiche mu yumba ya ku Mbepo, yasikujilima chapasi pakwe sulo, chakaŵe ŵandamo mwalakwemo, kaje kusalala malipilo ga akupanganya (masengo gambone)!');
INSERT INTO "silika" VALUES(29,59,'29:59','Aŵala ŵaapilile ni kwegamilaga kwa M’mbuje gwao pe.');
INSERT INTO "silika" VALUES(29,60,'29:60','Ilingwa-ilingwa mwa iwumbe yakuulukutaga yangaikulisoselaga lisiki lyakwe? Allah ni jwakuipa lisiki yalakweyo ni jenumanja ŵakwe, soni Jwalake ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(29,61,'29:61','Soni naga n’di mwawusisye ŵanganyao (kuti): “Ana nduni juŵagumbile mawunde ni mataka, ni kulijepepasya lyuŵa ni mwesi?” Chisimu chene tajile: “Allah.” Nambi akugalausyidwa chinauli (kwakuleka kun’galagatila Jwelepejo)?');
INSERT INTO "silika" VALUES(29,62,'29:62','Allah akasansapangulilaga lisiki jwaakunsaka mwa achikapolo Ŵakwe, ni kunnandiyichisya (jwaakunsaka), chisimu Allah chindu chilichose aŵele Jwachimanya chenene.');
INSERT INTO "silika" VALUES(29,63,'29:63','Soni naga n’di mwawusisye ŵanganyao (kuti): “Ana nduni jwaakasatulusyaga mesi kuumila kwinani, ni kusyuchisya nago litaka panyuma pakuwa kwakwe?” Chisimu chene tajile: “Allah.” Jilani: “Lumbili losope lwene alapikwe Allah.” Nambo ŵajinji mwa ŵanganyao ŵangali lunda.');
INSERT INTO "silika" VALUES(29,64,'29:64','Ni nganiuŵa awu umi wa duniyau ikaŵeje chanache ni masanje, sano chisimu Nyumba Jambesi niwauli umi usyesyene, ingaŵe ŵanganyao ŵamanyililaga. ');
INSERT INTO "silika" VALUES(29,65,'29:65','Sano pati akwesile ŵanganyao m’chombo (ni kusimana nayo yakogoya) akasam’bombaga Allah mwakunswejechesya dini Jakwe. Nambo naga ŵajokwelemo ni kwaikasya pankuuli, pajele ndemajo ŵanganyao akasawanganyaga (pakum’bomba Allah ni isanamu).');
INSERT INTO "silika" VALUES(29,66,'29:66','Akanileje yatwapeleyo (pangatogolela), nambo soni asengweje (pagalagatila isanamu yaoyo), basi sampano tamanyilile (yakuichisya yakwe).');
INSERT INTO "silika" VALUES(29,67,'29:67','Ana ŵanganyao ngakuiona yanti Uwwe tugatesile (Maaka) kuŵa gakuchimbichika soni gantunjelele, kutendaga ŵandu (ŵane) akuswambidwa mungulugulu mwaomo! Ana yaunami ni yaakuikulupilila, nambo ukoto wa Allah ni kuukaana?');
INSERT INTO "silika" VALUES(29,68,'29:68','Ana nduni jwali jwalupuso nnope kwakumpunda mundu jwansepele Allah unami, kapena kuikanila yakuona pati im’bichililemo? Ana ku Jahannama nganikuŵa kwiuto lya achimakafili?');
INSERT INTO "silika" VALUES(29,69,'29:69','Sano aŵala ŵaakulimbichila ligongo Lyetuwe, chisimu Uwwe chitwajongolele ku matala Getu. Soni chisimu Allah ali pampepe ni ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(30,1,'30:1','Alif-Lâm-Mîm. ');
INSERT INTO "silika" VALUES(30,2,'30:2','Aroma apundidwe (ni Afaarisi).');
INSERT INTO "silika" VALUES(30,3,'30:3','M’chilambo chakuŵandika (cha Shami), nambo nombe nao (Aroma) panyuma pakupundidwa tachipunda (kwapunda Afaarisio).');
INSERT INTO "silika" VALUES(30,4,'30:4','Mu yaka igala (yapachilikati cha itatu ni nsano nancheche), chilamusi ni cha Allah paujo ni panyuma (pakupundako), soni lyele lisikulyo (lyatachipunda Aroma kwapunda Afaarisi) ŵakulupilila (Asilamu) tachisengwa.');
INSERT INTO "silika" VALUES(30,5,'30:5','Ni chikamuchisyo cha Allah. Akunkamuchisyaga jwaakunsaka, soni Jwalakwe ni Jwamachili gakupunda, Jwachanasa channope.');
INSERT INTO "silika" VALUES(30,6,'30:6','(Achi ni) chilanga cha Allah. Allah jwangakasa chilanga Chakwe, nambo ŵandu ŵajinji ngakumanyilila.');
INSERT INTO "silika" VALUES(30,7,'30:7','Akumanyilila yakuonechelape ya umi wa duniya (mpela yamalonda, yaunamalima, yakutaŵataŵa ni ine), sano ŵanganyao ya Akhera akuŵa nkuikwanjila.');
INSERT INTO "silika" VALUES(30,8,'30:8','Ana ŵanganyao ngakuchetelela (m’mitima mwao) yakwamba achimisyene (yajwatite Allah pa kwagumba kuumila mungapagwa, kuti iyyoyopeyo soni juchajimusya m’malembe)? Allah nganijugumba mawunde ni mataka ni yaili chilikati chakwe ikaŵeje mwakuonaonape ni ndema jekolanjidwe, soni chisimu ŵandu ŵajinji yakusimangana ni M’mbuje gwao akukanila.');
INSERT INTO "silika" VALUES(30,9,'30:9','Ana ngakwendajenda pa chilambo ni kupitaga nchilola mujaŵelele mbesi ja aŵala ŵapaujo pao? Ŵaliji ŵamachili nnope kwapunda ŵanganyao, ni ŵagugudaga litaka nambo soni kutaŵapo yakutaŵataŵa ni kutamapo mwawinji kupunda mwataŵile ni kutamapo ŵanganyao, ni ŵaichilile achimitenga ŵao ni ilosyo yakuonechela. Basi ni nganijuŵa Allah jwanti ni kwatenda lupuso ŵanganyao, nambo kuti achimisyene ni ŵaŵaliji ali nkulitenda lupuso.');
INSERT INTO "silika" VALUES(30,10,'30:10','Kaneka yakusakala niyaliji mbesi ja aŵala ŵaŵasakesye, ligongo lyanti ŵakaaniile ma Ȃya ga Allah ni ŵaliji ali nkugatenda chipongwe.');
INSERT INTO "silika" VALUES(30,11,'30:11','Allah ni jwaakutandisyaga gumba iwumbe, kaneka juchiiwusyawusya (kuigumba panyuma pakuwa), kaneko Kukwakwe tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(30,12,'30:12','Sano lisiku lyajichijima Kiyama, akuleŵa tachikata tama.');
INSERT INTO "silika" VALUES(30,13,'30:13','Ni ngasakola ŵanganyao akuchondelela kuumila mwa akwawanganya ŵao (ni Allah), ni tachiŵa (akwawanganya ŵaowo yaili isanamu) ŵakwakana akuwanganya ŵao.');
INSERT INTO "silika" VALUES(30,14,'30:14','Sano lisiku lyajichijima Kiyama, lyele lisikulyo ŵanganyao tachilekangana (ŵane wa ku Mbepo ŵane wa ku Moto).');
INSERT INTO "silika" VALUES(30,15,'30:15','Basi aŵala ŵaakulupilile ni kutendaga yambone tachiŵa mu Litimbe lyakusangalasya (lya ku Mbepo) ali nkusengwasyidwa.');
INSERT INTO "silika" VALUES(30,16,'30:16','Sano aŵala ŵaakufuulu ni kanila ma Ȃya Getu ni yakusimangana ni Akhera, basi ŵanganyao ku ilagasyo tachiika nao.');
INSERT INTO "silika" VALUES(30,17,'30:17','Basi munswejesyani Allah ndema jankuswelwa (pakuswali swala ja Magrib ni Isha), ni ndema jankuchelwa (pakuswali swala ja Fajr).');
INSERT INTO "silika" VALUES(30,18,'30:18','Soni Jwalakwe jukwete lumbili losope lwene kumawunde ni petaka, ni (munswejesyeje soni) kwigulo (pakuswali swala ja Asr), ni ndema jankuutanda muusi (pakuswali swala ja Dhuhr).');
INSERT INTO "silika" VALUES(30,19,'30:19','Jwalakwe jukasakoposyaga chaumi kuumila mu chachiwe, ni koposya chachiwe kuumila mu chaumi, ni kulisyusya litaka panyuma pakuwa kwakwe, soni iyyoyopeyo tinchikoposyedwa ŵanganyammwe (m’malembe ni kuŵa ni umi soni).');
INSERT INTO "silika" VALUES(30,20,'30:20','Soni mu imanyilo Yakwe (yakulosya uchimbichimbi ni ukombosi Wakwe) ni yanti, ŵan’gumbile jenumanja kuumila mwitaka, kaneka jenumanja ni kuŵa ŵandu ŵankwenela (posepose).');
INSERT INTO "silika" VALUES(30,21,'30:21','Soni mu imanyilo Yakwe (yakulosya ukoto Wakwe) ni yanti, an’guumbiile ŵanganyammwe kuumila mwa jenumanja achimisyene achiŵammakwenu kuti ntulalileje kwa jamanjao, ni aŵisile chilikati chenu chinonyelo kwisa soni chanasa. Chisimu mu yalakweyo mwana imanyilo kwa ŵandu ŵaakuchetelelaga.');
INSERT INTO "silika" VALUES(30,22,'30:22','Soni mu imanyilo Yakwe (yakulosya ukombosi Wakwe) gumba mawunde ni mataka, ni kutindana kwa iŵecheto yenu kwisa soni mitundu jenu. Chisimu mu yalakweyo mwana imanyilo kwa ŵaakumanyilila.');
INSERT INTO "silika" VALUES(30,23,'30:23','Soni mu imanyilo Yakwe (yakulosya ukoto ni ukombosi Wakwe) gona kwenu kwa chilo ni muusi (kuti mpapumuleje), ni kupita nchisosasosa kwenu umbone Wakwe. Chisimu mu yalakweyo mwana imanyilo kwa ŵandu ŵaakupikana.');
INSERT INTO "silika" VALUES(30,24,'30:24','Soni mu imanyilo Yakwe (yakulosya machili Gakwe gakupunda ni yanti), akunnosyaga kumesya mwakuntesya woga ni kumpa tama (jakwika kwa wula), ni akutulusyaga kuumila kwinani mesi (gawula), basi ni akasasyuchisyaga nago litaka panyuma pakuwa kwakwe. Chisimu mu yalakweyo mwana imanyilo kwa ŵandu ŵakwete lunda.');
INSERT INTO "silika" VALUES(30,25,'30:25','Soni mu imanyilo Yakwe (yakulosya umbuje Wakwe) ni kwima kwa mawunde ni mataka kwa ulamusi Wakwe. Kaneko ndema jatachim’bilanga ni m’bilango umpepe kuumila mwitaka, jele ndemajo jenumanja tinchikopokanganaga.');
INSERT INTO "silika" VALUES(30,26,'30:26','Soni aŵele Ŵakwe Jwalakwe wosope ŵali kumawunde ni petaka, soni wosopeŵene akwitika Kukwakwe mwakulinandiya.');
INSERT INTO "silika" VALUES(30,27,'30:27','Soni Jwalakwe ni Ajula jwaakutandisyaga gumba iwumbe kaneka ni tachiiwusyawusya (kuigumba panyuma pakuwa) soni yalakweyo (yakuiwusyawusyayo) ni yaili yakwepepala nnope Kukwakwe, soni Jwalakwe jukwete lumbili lwapenani nnope kumawunde ni petaka (kwanti pangali chakunnandanyichisya nacho), soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(30,28,'30:28','Jwalakwe ampele jenumanja chilandanyo kuumila mu jenumanja mwachimisyene: Ana jenumanja nkwete ŵakamusyangana nao mwa (achikapolo) ŵagapatile magasa genu gakun’dyo pa (ipanje) yatumpele, mwanti jenumanja payalakweyo nkulandana (kagaŵane), ni kwajogopaga (achikapolowo) yankuti pakogopana mwachimisyene (alukosyo? Nambi uli nkum’bichila Allah akukamusyangana nao mwa achikapolo Ŵakwe)? Iyyoyopeyo ni yatukuti pagopolanya ma Ȃya kwa ŵandu ŵakwete lunda.');
INSERT INTO "silika" VALUES(30,29,'30:29','Nambo aŵala ŵalitesile lupuso akuyiye isako yao mwangali umanyilisi. Ana ninduni jwampaka an’jongole mundu jwannechelele Allah kusokonechela? Soni nganakola ŵanganyao akwajokola ŵaliose.');
INSERT INTO "silika" VALUES(30,30,'30:30','Basi lungamisyani ngope jenu (mmwe Muhammadi صلى الله عليه وسلم) mwakulungamika chenene ku Dini - chipago cha Allah achila chiŵaapagwisye ŵandu pa chalakwecho, pangali galausya kagumbe ka Allah, jalakwejo ni Dini jagoloka, nambo ŵandu ŵajinji ngaakumanyilila. ');
INSERT INTO "silika" VALUES(30,31,'30:31','(Soni ndema ni katema m’beje jenumanja) ŵakuujila Kukwakwe pakutenda toba, ni mun’jogopeje, nambo soni n’jimicheje Swala mwakolosya, ni ngasim’ba mwa akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(30,32,'30:32','Mwa aŵala ŵagaŵenye dini jao ni kuŵa mikutula-mikutula, likuga lililyose likusengwela yalili nayo.');
INSERT INTO "silika" VALUES(30,33,'30:33','Sano ŵandu ndema jagakwakwayaga masausyo akasam’bombaga M’mbuje gwao mwakuujila Kukwakwe pakutenda toba, kaneko nkwapa kupasya kuumila Kukwakwe ukoto, pajele ndemajo likuga line mwa jamanjao likasam’bwanganyaga M’mbuje gwao (ni isanamu).');
INSERT INTO "silika" VALUES(30,34,'30:34','Pangatogolela yatwapele. Basi sengwagani (ni umi wenu wejipio); mwanti pangakaŵapa timmanyilile (kusakala kwakwe).');
INSERT INTO "silika" VALUES(30,35,'30:35','Ana kapena twatuluchiisye ŵanganyao umboni (chitabu), basi walakweo ni ukuŵecheta yakwayana ni yaaŵele ŵanganyao ali nkum’bwanganya nayo (Allah)?');
INSERT INTO "silika" VALUES(30,36,'30:36','Soni naga tuli twapele kupasya ŵandu ukoto akasasengwaga nao, nambo naga chili chapatile chakusakala chilichose ligongo lya yagalongwesye makono gao, pajele ndemajo ŵanganyao akasaŵa ŵakata tama.');
INSERT INTO "silika" VALUES(30,37,'30:37','Ana ŵanganyao ngakuiona yanti Allah akasansapangulila lisiki jwaakunsaka, ni kunandiya (lisikilyo kwa jwaakunsaka). Chisimu mu yalakweyo mwana imanyilo kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(30,38,'30:38','Basi jwaulongo mumpani liunjili lyakwe, kwisa soni masikini ni jwapaulendo. Yalakweyo ni yaili yambone nnope kwa aŵala ŵaakusachilila ngope ja Allah, soni ŵanyao ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(30,39,'30:39','Sano ntuuka uliose wantosile (kwapa ŵane) kuti utupiye (chipanje chenu pakwembecheya kum’buchisya yejinji kuumila) m’chipanje cha ŵandu, basi ngaukasatupaga kwa Allah. Nambo Zaka jilijose janŵanganyao ni ŵatachijonjechesyedwa (malipilo gao).tosile nchisachililaga ngope ja Allah (ni jajikasatupaga kwa Allah), basi');
INSERT INTO "silika" VALUES(30,40,'30:40','Allah ni Ajula juŵan’gumbile jenumanja, kaneko ni kumpa lisiki, kaneko tachimpa chiwa, kaneko ni tachimpa umi (soni pakun’jimusya m’malembe). Ana mwa akwawanganya ŵenu (ni Allah) pana jwaakutendaga chilichose mu yalakweyi? Kuswejela ni Kwakwe soni jutalikangene kusyene ni yaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(30,41,'30:41','Chiwatanganyo chionechele pankuuli ni pambwani ligongo lya (yangalumbana) yagapanganyisye makono ga ŵandu, kuti (Allah) ŵape kupasya ŵanganyao (ilagasyo ya) ine mwa ayila yapanganyisye, kuti ŵanganyao awujile (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(30,42,'30:42','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Jendagani pa chilambo ni kupita nchilola mujaŵelele mbesi ja aŵala ŵakala. Ŵajinji mwa jamanjao ŵaliji akuwanganya (Allah ni isanamu).”');
INSERT INTO "silika" VALUES(30,43,'30:43','Basi lungamisyani (mmwe Muhammadi صلى الله عليه وسلم) ngope jenu ku Dini jagoloka, nkaniliyiche lisiku lyakuumila kwa Allah lyangali jwakuliusya. Lyele lisikulyo (ŵandu) tachigaŵikana (ni kuŵa mikutula jiŵili; wa ku Mbepo ni wa ku Moto).');
INSERT INTO "silika" VALUES(30,44,'30:44','Mundu jwaakufuulu, basi gali kukwakwe (makungu ga) ukafili wakweo, sano ŵatesile yambone, basi ni kuti akulitandichila achimisyene mauto gagusama (ku Mbepo).');
INSERT INTO "silika" VALUES(30,45,'30:45','(Tachigaŵikana ŵandu pa Kiyama) kuti akalipile aŵala ŵaakulupilile ni kutendaga yambone kuumila mu umbone Wakwe. Chisimu Jwalakwe jwangaganonyela makafili.');
INSERT INTO "silika" VALUES(30,46,'30:46','Soni mu imanyilo Yakwe (yakulosya ukombosi ni ukoto Wakwe) ni yanti, akutumisyaga mbungo kuŵa jakupeleka abali jakusengwasya (ja kwisa kwa wula) kuti ampe kupasya ukoto Wakwe, ni kuti yombo ijendeje mwa ulamusi Wakwe, ni kuti nsosejesoseje umbone Wakwe, ni kuti soni jenumanja m’beje ŵakutogolela.');
INSERT INTO "silika" VALUES(30,47,'30:47','Soni chisimu pamasile patwatumisye paujo penu (mmwe Muhammadi صلى الله عليه وسلم nkanim’biche) achimitenga ku ŵandu ŵao, ni ŵaichilile ni ilosyo yakuonechela (yakulosya ukuona wao, nambo ŵaikaaniile). Basi ni twauchisye mbusyo syakusakala aŵala ŵaŵaleŵile. Ni kwaliji kwakuŵajilwa Kukwetu kwakulupusya ŵakulupilila.');
INSERT INTO "silika" VALUES(30,48,'30:48','Allah ni Ajula jwaakutumisyaga mbungo, basi ni jikasawunga mawunde gawula (kuumila nnuwundu lwamesi ga m’mbwani ni sulo), basi ni akasagajasasyaga kwinani yaakuti pakusaka, ni akasagagaŵanyaga kuŵa ipande-ipande, mpaka mmwe nkasajionaga wula jinankopoka mkatikati mwakwe. Sano naga ali ŵaatuluchisye jalakwejo ŵaakwasaka mwa achikapolo Ŵakwe, pajele ndemajo ŵanganyao akasaŵa ŵakusengwa.');
INSERT INTO "silika" VALUES(30,49,'30:49','Namuno kuti ŵaliji ŵanganyao paujo pakwe nkanijatuluchile (wulajo) ŵakata tama.');
INSERT INTO "silika" VALUES(30,50,'30:50','Basi ilolani yakuichisya ya ukoto wa Allah yaakuti pakulisyusya litaka panyuma pa kuwa kwakwe. Chisimu Jwalakwe ni jutachijimusya ŵawe, soni Jwalakwe pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(30,51,'30:51','Soni chisimu naga tuli tutite tutumisye mbungo (jakwisa kuujonanga mmela wao), basi ni kuuwona (mmelao) uli wam’bilichila; chisimu ŵanganyao mpaka aŵe panyuma pakwe ŵakufulu.');
INSERT INTO "silika" VALUES(30,52,'30:52','Basi chisimu mmwe nganim’ba mwapikanisye ŵawe (achimakafili), nambo soni nganim’ba mwapikanisye asiiwa m’bilango ndema jaakugalauka kulosya migongo jao (pangasaka kupikana yankwasalila).');
INSERT INTO "silika" VALUES(30,53,'30:53','Nambo soni mmwe nganim’ba mwajongolele ŵangalola mu kusokonechela kwao. Nganim’ba mwapikanisye ikaŵeje aŵala ŵaakukulupilila ma Ȃya Getu aku ali ŵakulipeleka (kwa Allah pakuŵa Asilamu).');
INSERT INTO "silika" VALUES(30,54,'30:54','Allah ni Ajula juŵan’gumbile ŵanganyammwe mu kaŵe kakulepetala (kangali machili), kaneka ni akasampaga machili panyuma pakulepetala, kaneka ni akasampaga kutochomala ni wuuli panyuma pakola machili. Akasagumbaga yaakusaka, soni Jwalakwe ni Jwakumanyilila nnope, Jwakombola kusyene.');
INSERT INTO "silika" VALUES(30,55,'30:55','Sano lisiku lyajichijima Kiyama, akuleŵa tachilumbila yanti: Nganatama (pa duniya) ikaŵeje ndaŵi jimpepe, iyyoyopeyo ni yaŵatiji pagalausyidwa (kuleka yakuona).');
INSERT INTO "silika" VALUES(30,56,'30:56','Ni tachiŵecheta aŵala ŵaŵapedwile umanyilisi ni chikulupi kuti: “Chisimu ŵanganyammwe mwatemi m’chilamusi cha Allah kwikanila lisiku lyakuwukulidwa (m’malembe), sambano ali ni lisiku lyakuwukulidwa (m’malembe alila lyamwalikanilaga), nambo ŵanganyammwe mwaliji nkakumanyilila (yakupatikana kwakwe).”');
INSERT INTO "silika" VALUES(30,57,'30:57','Basi lyele lisikulyo ngasikwakamuchisya aŵala ŵaŵalitesile lupuso kuliŵalanga kwao, soni ŵanganyao ngasakundidwa (kuujila ku duniya) kuti akatende toba ni kuŵenda chikululuko (kwa Allah).');
INSERT INTO "silika" VALUES(30,58,'30:58','Soni pamasile patupeleche ku ŵandu mu aji Qur’an ntundu uliose wachilandanyo. Nambo naga n’di ntite mwaichilile nacho (mmwe Muhammadi صلى الله عليه وسلم) chisimosimo chilichose (chakulosya ukuona wa utume wenu), aŵala ŵaakufuulu chisimu chene mpaka ajile (pakwasalila ŵakulupilila): “Nganim’ba jenumanja ikaŵeje ŵakuya yaunami.”');
INSERT INTO "silika" VALUES(30,59,'30:59','Iyyoyopeyo ni yaakutiji Allah pakudinda (chidindo) m’mitima mwa aŵala ŵangali chachimanyi.');
INSERT INTO "silika" VALUES(30,60,'30:60','Basi pililani (mmwe Muhammadi صلى الله عليه وسلم), chisimu chilanga cha Allah (chakunkamuchisya mmwejo) chiŵele chakuonaonape. Soni akantesya ulesi (wakuleka kulungusya utenga) aŵala ŵangakukulupilila kusyesyene.');
INSERT INTO "silika" VALUES(31,1,'31:1','Alif-Lâm-Mîm. ');
INSERT INTO "silika" VALUES(31,2,'31:2','Aga ni ma Ȃya ga m’Chitabu chegumbale lunda lwakusokoka.');
INSERT INTO "silika" VALUES(31,3,'31:3','Chongolo nambo soni chanasa kwa ŵakolosya.');
INSERT INTO "silika" VALUES(31,4,'31:4','Aŵala ŵaakwimikaga Swala mwakolosya ni kutolaga Zaka, nambo soni ya Akhera jamanjao akasakulupilila kusyesyene.');
INSERT INTO "silika" VALUES(31,5,'31:5','Ŵele ŵanganyao ali pachongoko chakuumila kwa M’mbuje gwao, soni ŵanyao ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(31,6,'31:6','Soni chilikati cha ŵandu pana jwaakusumaga ngani syachanache kuti jusokonesye (nasyo ŵandu) kwitala lya Allah mwangali umanyilisi, ni jukulitendaga soni chipongwe (litala lya Allah kwisa soni ma Ȃya Gakwe). Ŵanganyao akwete kupata ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(31,7,'31:7','Soni pati gachisoomedwaga kukwakwe ma Ȃya Getu, jukasagalauka mwakulikwesya kuŵa nti chisawu nganijugapikana, ni kuŵa nti chisawu soni m’mawiwi mwakwe mwana usiiwa. Basi muntagulilani ya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(31,8,'31:8','Chisimu aŵala ŵakulupilile ni kutendaga yambone, akwete kupata Matimbe Gachindimba (Mbepo).');
INSERT INTO "silika" VALUES(31,9,'31:9','Takaŵe ŵandamo mwalakwemo, (achi ni) chilanga cha Allah chakuonaonape, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(31,10,'31:10','Jugumbile mawunde mwangali misati jankujiona, ni juŵisile petaka yakunyichisya (matumbi gausito), kuti litakwika mulin’gwedanyichile ŵanganyammwe (litakalyo), ni jujenesye palakwepo mitundu josope ja inyama, ni tutulwisye kuumila kwinani mesi (ga wula), basi ni tumesisye palakwepo ntundu uliose (wa mmela) wakusalala chenene.');
INSERT INTO "silika" VALUES(31,11,'31:11','Aku nigumba kwa Allah. Basi munosyani, ana niyapi yaagumbile aŵala ŵanganaŵa Jwalakwe? (Ngwamba, pangali chilichose chagumbile)! Nambo kuti ŵakulitenda lupuso ali mkusokonechela kwakuonechela.');
INSERT INTO "silika" VALUES(31,12,'31:12','Soni chisimu chene pamasile patwampele Luqman lunda lwakusokoka (ni twansalile kuti): “Muntogolelani Allah.” Sano jwaakutogolela, nikuti akutogolela kwakulikamuchisya nsyene, nambo jwakanile, basi (akanile kwakulijonanga nsyene), chisimu Allah ni Jwakupata kwejinji (jwangalajila chilichose), Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(31,13,'31:13','Soni (kumbuchilani) katema kajwaŵechete Luqman kwa mwanagwe aku achim’bundaga kuti: “E mwanangu! Ngasimum’bwanganya Allah (ni isanamu). Chisimu kuwanganya (Allah ni isanamu) luŵele lupuso lwekulungwa nnope.” ');
INSERT INTO "silika" VALUES(31,14,'31:14','Soni tunchisyisye mundu kwa achinangolo ŵakwe (kwatendela yambone). - Ŵan’jigele (chilu chakwe) mamagwe mwakutochomala ni kulagasika pachanya pakutochomala ni kulagasika. (Ŵan’jonjesye) ni kunnekasya konga pa yaka iŵili. – Mundogolelani Une ni achinangolo ŵenu ŵanaŵaŵili. Kukwangu ni kwakuujila.');
INSERT INTO "silika" VALUES(31,15,'31:15','Nambo naga alimbane namwe pa yanti mmwejo mumwanganyeje Une ni yanganinkola nayo umanyilisi; basi ngasimwajitika, nambo ntameje nao pa duniya mwaumbone, ni nkuyeje litala lya ajula jwagalauchile Kukwangu pakutenda toba. Kaneka Kukwangu ni kwakuujila kwenu, basi ni tinjintagulila jenumanja yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(31,16,'31:16','“E mwanangu! Chisimu chalakwe (chitendo changalumbana) namuno chili chausito wati mpela lupele lwa mbumbi (tanaposi), ni kuŵa chalakwecho nkati mwa lwala kapena nkati mwa mawunde kapena nkati mwetaka; Allah tachiika nachope. Chisimu Allah ni Jwakumanyilila yakunyilila nnope, Jwaasimanya abali syosope.”');
INSERT INTO "silika" VALUES(31,17,'31:17','“E mwanangu! Jimikani Swala mwakolosya, ni nchisyeje yambone, ni nkanyeje yakusakala, nambo soni mpilileje pa ipato yaimpatile. Chisimu yalakweyo ni yaili mu ichindu yakusosekwa kuŵichila ntima.”');
INSERT INTO "silika" VALUES(31,18,'31:18','“Ngasimwasyulula ŵandu ni lijeje lyenu, nambo soni nkajenda pa chilambo mwakutwanga, chisimu Allah ŵangannonyela jwakulikwesya jwalijose, jwakuliiti.”');
INSERT INTO "silika" VALUES(31,19,'31:19','“N’jendeje wende wenu wachilikati (wakulitimalika), ni kutondoyaga liloŵe lyenu. Chisimu lyakunyalaya nnope mmaloŵe gosope ni liloŵe lyambunda (bulu).”');
INSERT INTO "silika" VALUES(31,20,'31:20','Ana ngankuiona yanti Allah an’jepepachisye jenumanja yosope yaili kumawunde ni yaili petaka, ni an’gumbalichisyeindimba Yakwe yakuonechela ni yakusisika? Soni chilikati cha ŵandu pana ŵaakukanganaga pa ya Allah mwangali umanyilisi, namuno chongoko, namuno soni chitabu chakupeleka lilanguka (kuumila kwa Allah).');
INSERT INTO "silika" VALUES(31,21,'31:21','Soni naga ili iŵechetedwe kukwao (yanti): “Kuyani yaatulwisye Allah.” Akasatiji: “Nambo tukuya yatwasimene nayo achatati ŵetu.” Ana nantimuno kuti shetani jukwaŵilanjila ku ilagasyo ya Moto Wakulapuka (tiŵakuyejepe)?');
INSERT INTO "silika" VALUES(31,22,'31:22','Sano jwaakujipeleka ngope jakwe kwa Allah aku ali jwakolosya (masengo), nikuti pamasile paakamulisye nkonjo wakulimbangana wauli wangatemeka. Sano kwa Allah ni kwajili mbesi ja ichindu yosope.');
INSERT INTO "silika" VALUES(31,23,'31:23','Sano jwaakufuulu, basi kukan’dandaulikasya kufulu kwakweko, Kukwetu ni kwakuujila kwao, basi ni tuchatagulila yaŵapanganyisye, chisimu Allah ni Nkuimanya chenene yayili m’mitima.');
INSERT INTO "silika" VALUES(31,24,'31:24','Chitwasengwasye panandi, kaneko tuuchatutila ku ilagasyo yakandapala.');
INSERT INTO "silika" VALUES(31,25,'31:25','Soni naga n’di mwawusisye ŵanganyao (kuti): “Ana nduni juŵagumbile mawunde ni mataka?” Chisimu chene tajile: “Allah.” Jilani: “Lumbili losope lwene alapikwe Allah.” Nambo ŵajinji mwa ŵanganyao ŵangali chachimanyi.');
INSERT INTO "silika" VALUES(31,26,'31:26','Iŵele Yakwe Allah yosope yaili kumawunde ni petaka, chisimu Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(31,27,'31:27','Soni ingaŵe kuti itela yosope yaili pa chilambo yaliji mapensulo, sano mbwani (nikuŵa wino wakulembela), ni kujijonjechesya panyuma pakwe ni mbwani sine nsano nasiŵili; maloŵe ga Allah nga nganigamala. Chisimu Allah ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(31,28,'31:28','Nganikuŵa gumbidwa kwenu namuno kuukulidwa kwenu (m’malembe) ikaŵeje mpela (gumbidwa ni kuukulidwa kwa) mundu jumpepe. Chisimu Allah ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(31,29,'31:29','Ana ngankuiona yanti Allah akwinjisyaga chilo m’muusi (ni kuleupa muusi kwipipa chilo mpela ndaŵi jachitukuta), ni akwinjisyaga soni muusi nchilo (ni kuleupa chilo kwipipa muusi mpela ndaŵi jakusisima), ni alijepepesye lyuŵa ni mwesi chilichose chikwenda kwandaŵi jekolanjidwe, nambo soni yanti Allah abali syosope sya yankutenda aŵele Jwasimanya chenene?');
INSERT INTO "silika" VALUES(31,30,'31:30','Yalakweyo ni ligongo lyanti Allah ni jwali Jwakuona, soni kuti ayila yaakuiŵendaga yangaŵaga Jwalakwe yaukoleko, soni kuti Allah ni jwali Jwapenani kusyene, Jwankulungwa kwannope.');
INSERT INTO "silika" VALUES(31,31,'31:31','Ana ngankuiona yanti yombo ikwenda pa mbwani mwa ukoto wa Allah, kuti annosye jenumanja mu imanyilo Yakwe (yakulosya ukombosi Wakwe)? Chisimu mu yalakweyo muŵele mwana imanyisyo kwa jwalijose jwali jwakupilila nnope, jwakutogolela kwejinji.');
INSERT INTO "silika" VALUES(31,32,'31:32','Sano pati gaawunichile ŵanganyao matumbela ganti chisawu miulili (ja mawunde gawula ni matumbi), akasam’bombaga Allah mwakunswejechesya duwa (syao pakum’bomba Jwele pejo), nambo nkwakulupuchisya pankuuli, basi ŵane mwa jamanjao pana ŵaakuŵa chilikati (cha chikulupi ni ukafili). Ni pangali ŵaakasaikanilaga imanyilo Yetu ikaŵeje nkukasa nnope ilanga jwalijose, jwakufulu nnope.');
INSERT INTO "silika" VALUES(31,33,'31:33','E jenumanja achinaŵandu! Mun’jogopani M’mbuje gwenu (Allah), ni llijogope soni Lisiku lyangakomboleka nangolo kunkamuchisya mwanagwe, namuno mwanache kwakamuchisya ŵanangolo ŵakwe chilichose. Chisimu chilanga cha Allah chakuonaonape. Basi ukannyenga ŵanganyammwe ata panandi umi wa duniya, nambo soni akannyenga jenumanja ata panandi nkunyenga (shetani) pa ya Allah.');
INSERT INTO "silika" VALUES(31,34,'31:34','Chisimu Allah, Kukwakwe pe ni kuuli umanyilisi wa ndaŵi (jakwichila Kiyama), Jwalakwe ni jwaakutulusyaga wula, soni ni jwaakumanyililaga yaili mu itumbo. Ni ngakasamanyilila mundu jwalijose ichapanganye malaŵi, nambo soni mundu jwalijose ngakasamanyilila kuti chilambochi chachiwila. Chisimu Allah ni Jwakumanyilila nnope (ichindu), Jwasimanya abali syosope.');
INSERT INTO "silika" VALUES(32,1,'32:1','Alif-Lâm-Mîm. ');
INSERT INTO "silika" VALUES(32,2,'32:2','Kutulusya kwa Chitabu (Qur’an) changali kaikasya nkati mwakwe kutyochele kwa M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(32,3,'32:3','Kapena ŵanganyao akuti: “Jwalakwe (Muhammadi صلى الله عليه وسلم) ni jwaachitunjile?” (Ngwamba)! Nambo chalakwecho chiŵele chakuona chakuumila kwa Ambuje ŵenu (Allah), kuti mwatetele ŵandu ŵanganijwaichilileje nkutetela paujo penu, kuti ŵanganyao ajongoche.');
INSERT INTO "silika" VALUES(32,4,'32:4','Allah ni Ajula juŵagumbile mawunde ni mataka ni yaili chilikati chakwe m''moŵa nsano nalimo, kaneka ni jwatenjele pa Arishi (Chindanda Chaulungu). Nganinkola jenumanja jwangaŵaga Jwalakwe akun’gosa ŵaliose namuno akun’jokola, ana ngankukumbuchila?');
INSERT INTO "silika" VALUES(32,5,'32:5','(Jwalakwe) jukasalondesyaga ichindu (yosope) kutyochela kwinani mpaka pasi, kaneka ni ikasakwelaga Kukwakwe, pa lisiku limpepe lyachiŵele chipiimo chauleu wakwe yaka sausande (1000) yankuti pakuŵalanjila jenumanja (ni yaka yenu ya pa duniya).');
INSERT INTO "silika" VALUES(32,6,'32:6','Jwalakwejo ni Nkumanyilila yakusisika ni yakuonechela, Jwamachili gakupunda, Jwachanasa channope.');
INSERT INTO "silika" VALUES(32,7,'32:7','Ajula juŵachikolosyisye chindu chilichose kagumbidwe kakwe, ni jwakutandite gumbidwa kwa mundu kuumila mwilongo.');
INSERT INTO "silika" VALUES(32,8,'32:8','Kaneko jwautesile uŵelesi wakwe (mundujo) kuŵa wakuumila m’mesi gasyesyene gakunyosyeka.');
INSERT INTO "silika" VALUES(32,9,'32:9','Kaneko ni ŵankolosyisye ni kumpepelela nsimu wakuumila Kukwakwe. Ni am’biichiile jenumanja mawiwi (gakupikanila), meso (gakulolela) kwisa soni mitima (jaganichisya), nambo kutogolela kwenu kuli kwannonope.');
INSERT INTO "silika" VALUES(32,10,'32:10','Sano ŵanganyao ni akutiji: “Ana patuchipotela mwitaka, (panyuma pakuwa ni kuwanganyichilana ni litaka), nikuti chisimu uwwe mpaka tuchiŵa soni mu kagumbidwe kasambano?” (Elo tachiŵa), nambo ŵanganyao yakusimangana ni M’mbuje gwao (Allah) akuikanila.');
INSERT INTO "silika" VALUES(32,11,'32:11','Jilani: “Lilaika lya chiwa lyaliŵichidwe kukwenu lichityosya umi wenu, kaneko kwa M’mbuje gwenu tinchiuchisyidwa.”');
INSERT INTO "silika" VALUES(32,12,'32:12','Ingaŵe mwajile kwaona akuleŵa achijinamisyaga mitwe jao kwa M’mbuje gwao (Allah aku achitiji): “Ambuje ŵetu! Sano tulolite nambo soni tupikene, basi tuwuchisyani (ku duniya kuti) tukapanganye yambone, chisimu uwwe (sano) tukulupilila kusyesyene.” (Nkajile kuiona yanchesela kusyene).');
INSERT INTO "silika" VALUES(32,13,'32:13','Soni tungasache tungampele mundu jwalijose chongoko chakwe, nambo kuti lyasimichiche liloŵe Kukwangu lyanti: Chisimu chinjigumbasya Jahannama ni majini kwisa soni ŵandu wosope (ŵali ŵakusakala).');
INSERT INTO "silika" VALUES(32,14,'32:14','(Tachisalilidwa yanti): Basi pasyani (ilagasyo) ligongo lyakuliŵalila kwenu yakusimangana ni lisiku lyenu lyelino. Uwwe Nowe tun’diŵaliile. Sano pasyani ilagasyo yandamo, ligongo lya yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(32,15,'32:15','Chisimu ŵakasagakulupilila ma Ȃya Getu, ni aŵala ŵanti naga ali akumbusyidwe nago akasasenjelemukukaga kusujudu, ni akasaluswejesyaga lumbili lwa M’mbuje gwao (Allah), soni ŵanganyao ŵangalikwesya.');
INSERT INTO "silika" VALUES(32,16,'32:16','Mbalati syao sikasatalikanganaga ni maugono (ndema jakwimuchila kuswali Tahajjudi), achim’bombaga M’mbuje gwao mwaoga (wakun’jogopa Jwalakwe), nambo soni mwakola tama (ja ukoto Wakwe), nambo soni mu ichindu yatwapele akasatolaga.');
INSERT INTO "silika" VALUES(32,17,'32:17','Ni ngakumanyilila mundu jwalijose yaŵaasisiile ŵanganyao (ku Mbepo) mu yakuteteŵachisya meso, kuŵa malipilo ga yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(32,18,'32:18','Ana jwali jwakulupilila mpaka aŵe mpela jwakunyosya? Nganaŵa alandene.');
INSERT INTO "silika" VALUES(32,19,'32:19','Sano aŵala ŵakulupilile ni kutendaga yambone, basi akwete kupata ŵanganyao Matimbe Gakutamilichikamo (ku Mbepo), kuŵa mbiiko pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(32,20,'32:20','Sano soni aŵala ŵaŵanyosyisye, basi mauto gao ni ku Moto. Ndaŵi ni katema katakasacheje kuti akopochemo, takawuchisyidweje mmomo, ni takasalilidweje yanti: “Pasyani ilagasyo ya Moto ayila yamwaliji nkuikanila.”');
INSERT INTO "silika" VALUES(32,21,'32:21','Soni chisimu chitwape kupasya ŵanganyao ilagasyo yakuŵandika (ya pa duniya), nkaniyiŵe ilagasyo yekulungwa (ya ku Akhera), kuti ŵanganyao awujile (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(32,22,'32:22','Ana nduni jwali jwalupuso nnope kwakumpunda mundu jwakumbusyidwe ni ma Ȃya ga Ambujegwe, kaneka ni kugatundumalila? Chisimu Uwwe kwa akuleŵa tuŵele Ŵakuusya mbusyo.');
INSERT INTO "silika" VALUES(32,23,'32:23','Soni chisimu pamasile patwampele Musa Chitabu (cha Taurat), basi ngasim’ba ŵakaichila yakusimangana najo (Musa kwa mwasimangene najo chilo cha Al-Isrâ’ ni Al- Mi‘râj). Ni twachitesile (Chitabucho) kuŵa chongolo ku ŵanache ŵa Israila.');
INSERT INTO "silika" VALUES(32,24,'32:24','Ni twatesile ŵane mwa ŵanganyao kuŵa achinnongola ŵaŵaliji nkwajongolela (ŵandu) ni ulamusi Wetu, pandema ja ŵapilile ni kuŵaga soni ali nkugakulupilila kusyesyene ma Ȃya Getu.');
INSERT INTO "silika" VALUES(32,25,'32:25','Chisimu Ambuje ŵenu (Allah) ni ŵatachijilanya chilikati chao lisiku lya Kiyama, pa ichindu yaŵaliji ŵanganyao ali nkutindana pa yalakweyo.');
INSERT INTO "silika" VALUES(32,26,'32:26','Ana nganiyimanyukuchepe kukwao yanti mikutula jilingwa jatwajijonasile paujo pao? Soni ŵanganyaŵa (achimakafili ŵaleloŵa), akwendaga mu ŵatamaga ŵanganyao, (ana ngaakuionaga imanyilo yakonanjidwa kwao)? Chisimu mu yalakweyo mwana ilosyo. Ana ngakupikana?');
INSERT INTO "silika" VALUES(32,27,'32:27','Ana ngakuiona yanti Uwwe tukugapelekaga mesi kwitaka lyejumu, ni tukasakoposyaga ni galakwego mmela waikulyaga ilango yao mpaka ni achimisyene ŵakweo, ana ngakulola?');
INSERT INTO "silika" VALUES(32,28,'32:28','Sano ŵanganyao ni akutiji: “Ana kwele kulamulidwa (kwakuti uwwe tupate ipotesi) kuchiŵa chakachi, nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(32,29,'32:29','Jilani: “Lisiku lyakulamulidwa (kupata ipotesi yenu), ngasichakamuchisya aŵala ŵaŵakufuulu chikulupi chao, nambo soni ngasapedwa lipesa (lyakutendela toba).”');
INSERT INTO "silika" VALUES(32,30,'32:30','Basi mwalekani ŵanganyao (mmwe Muhammadi صلى الله عليه وسلم), ni n’jembecheyeje (yachiŵatende ŵanganyao Allah), chisimu ŵanganyao akwembecheya (yakuti gampate jenumanja makungu).');
INSERT INTO "silika" VALUES(33,1,'33:1','E mmwe Ntume (Muhammadi صلى الله عليه وسلم)! Jendelechelani kun’jogopa Allah, soni ngasimwapikanila achimakafili kwisa soni achinakunaku. Chisimu Allah aŵele Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(33,2,'33:2','Soni kuyani yaikujuwulidwa kukwenu kuumila kwa Ambuje ŵenu. Chisimu Allah abali syosope sya yankutenda ŵanganyammwe aŵele Jwasimanya chenene.');
INSERT INTO "silika" VALUES(33,3,'33:3','Soni jegamilani kwa Allah, soni Allah akukwanila kuŵa Nkun''jegama.');
INSERT INTO "silika" VALUES(33,4,'33:4','Allah nganam’bichila mundu mitima jiŵili nkati mwakwe, soni nganiŵatenda achiŵammakwenu aŵala ŵankwalandanya mwa ŵanganyao ni migongo (ja achimama ŵenu pa chiŵako changaŵa nkutenda nao soni ulombela) kuŵa achimama ŵenu ŵasyesyene, soni nganiŵatenda ŵanache ŵenu ŵakulela mpela ŵakuliŵelechela. Yalakweyo igambile kuŵa yakuŵecheta yenu (yankuŵechetaga) ni ngamwa syenu. Nambo Allah ni jwaakuŵecheta yakuona, nambo soni akongolela kwitala (lyagoloka).');
INSERT INTO "silika" VALUES(33,5,'33:5','Mwakolanjileje (ŵanache ŵakuleledwao) ni (mena) gaachatati ŵao, yalakweyo ni yaili yakulungamika nnope kwa Allah. Sano naga ngankwamanyilila achatati ŵao, basi (mwakolanjeje kuti) achalongo achin’jenu pa dini ni akupedwa ulokosyo ŵenu. Sano chipaŵe pangali ulemwa kukwenu pa yannemwisye, nambo (chipaŵe pana ulemwa) pa yajitesile melepe mitima jenu. Soni Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(33,6,'33:6','Ntume صلى الله عليه وسلم ni jwali jwakusosekwa nnope kwa ŵakulupilila kupunda achimisyeneo, (kupunda kusosekwa kwa ilu yao ni ipanje yao kukwao), soni achiŵammakwakwe (Ntume صلى الله عليه وسلم) ali achimama ŵao (ŵakulupililao pachiŵako chauchimbichimbi ni changalomba panyuma pa Ntumejo). Sano ŵapaulongo ni ŵali ŵakuŵajilwa (kwinjilana chipanje chamalekwa) ŵane ni ŵane (kupitila m’malamusi gagali) m’Chitabu cha Allah, kupunda (ya kwapa kwinjila chipanje chamalekwa) ŵakulupilila kwisa soni akusama (ŵanganaŵa ŵaulongo wa akuwa), ikaŵeje naga nsachile kwatendela achimijenuo yambone (ngali kandu, mpela kwalechela wasiya wanti chapate panandi mu chipanje chenu chamalekwa). Yalakweyi yaliji yelembedwelembedwe m’Chitabu (cha Al-Lauh Al-Mahfȗz).');
INSERT INTO "silika" VALUES(33,7,'33:7','Soni (kumbuchilani) katema katwajigele kuumila kwa achimitume chilanga chao (chakulungusya utenga mwakulupichika), nambo soni kuumila kukwenu (mmwe Muhammadi صلى الله عليه وسلم), ni kuumila soni kwa Nuhu, Ibrahima, Musa ni Isa mwanache jwa Maryam, ni twajigele Uwwe kuumila kwa ŵanganyao chilanga chakulimbangana.');
INSERT INTO "silika" VALUES(33,8,'33:8','Kuti (Allah) akaawuusye ŵakuona (achimitume ni achimitenga) pa ukuona wao (pa kalungusye kao utenga ni yaŵatite ŵandu ŵao pakwajanga). Ni ŵalinganyichisye achimakafili ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(33,9,'33:9','E jenumanja ŵankulupilile! Kumbuchilani chindimba cha Allah pa jenumanja, pandema jajam’bichilile mikutula ja asilikali (ŵachikafili). Basi ni twatumichisye ŵanganyao mbungo kwisa soni mikutula ja asilikali (ŵachilaika pa ngondo ja Al-Ahzâb) ŵanganimwaona, ni juŵele Allah pa ichindu yankutenda Jwakulolechesya.');
INSERT INTO "silika" VALUES(33,10,'33:10','Pandema jaŵam’bichilile kuumila chakulutando kwenu ni chakwiŵanda kwenu, ni pandema jagapombotele meso (pakulola kwa amagongope), mitima ni kwika kungulo (ligongo lya woga ni kutetemela), aku nchin’ganichisyaga Allah nganisyo syakulekanganalekangana.');
INSERT INTO "silika" VALUES(33,11,'33:11','Palakwepo ŵakulupilila ŵalinjidwe (mayeso gekulungwa), ni ŵatetemesyedwe kutetemesyedwa kwamachili nnope.');
INSERT INTO "silika" VALUES(33,12,'33:12','Ni ndema jaŵaŵechetaga achinakunaku kwisa soni aŵala ŵamwaliji m’mitima mwao mwana ulwele (wa chikaiko) kuti: “Nganatupa uwwe Allah ni Ntenga Jwakwe chilanga chinepe ikaŵeje cha chinyengo.”');
INSERT INTO "silika" VALUES(33,13,'33:13','Ni ndema jalyaŵechete likuga line mwa jamanjao kuti: “E jenumanja achinsyene Yathiriba (Madina)! Pangali kwimilichika kukwenu (kwanti ni kwakombola amagongo ŵenu) basi ujaani.” Sano likuga line mwa jamanjao lyaliji nkum’benda lusa Ntume صلى الله عليه وسلم (lwa kuujila ku Madina) pakuŵecheta kuti: “Chisimu majumba getu gali wasikape (gangali gosedwa kwa amagongo).” Kutendaga galakwego nganigaŵa wasikape, ngaakusaka inepe ikaŵeje kutila.');
INSERT INTO "silika" VALUES(33,14,'33:14','Soni ingaŵe ŵajinjilile ŵanganyao (amagongo ŵao) kuumila mbali syakwe syosope (sya nsinda wa Madina) kaneka ni kwachisya yakuleka Usilamu, angaitesile kwene yalakweyo, soni nga nganaijembechela ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(33,15,'33:15','Soni chisimu ŵanganyao ŵaliji ali ataŵene najo chilanga Allah kala, chanti ngasalosya migongo jao (kutiila). Sano chilanga cha Allah chiŵele chakuwusyidwa.');
INSERT INTO "silika" VALUES(33,16,'33:16','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Nganikuŵa kunkamuchiisye kutiila kwenu pati nchichitiilaga chiwa kapena kuulajidwa. Sano pajele ndemajo (pati ntisilemo, kutiila kwenuko) nganim’ba nsengwasyidwe (nako) ikaŵeje panandipe (nkumala ndaŵi ja umi wenu tim’bwepe).”');
INSERT INTO "silika" VALUES(33,17,'33:17','Jilani: “Ana ni nduni ajula jwampaka an’gose kwa Allah naga ali ansachiiliile jenumanja yakusakala, kapena (jwampaka ampe jenumanja yakusakala) naga (Allah) ali asachile kuntendela chanasa?” Soni nganaŵa alipatiile ŵanganyao jwangaŵaga Allah nkwagosa namuno nkwakamuchisya.');
INSERT INTO "silika" VALUES(33,18,'33:18','Allah akwamanyilila chenene akutengusya (ŵandu kuti akajaula kungondo pamo ni Ntume صلى الله عليه وسلم) mwa jenumanja, ni ŵaakutiji pakwasalila achimijao: “Ikani kukwetu (nkaliwanga ni Muhammadi صلى الله عليه وسلم ntakwika mun’jonasiche najo imo).” Nambo soni ŵanganyao (achimisyene) ŵangaika kungondo ikaŵeje panandipe (mwandola ni kogopa nchesela).');
INSERT INTO "silika" VALUES(33,19,'33:19','Ŵaumbombo kukwenu (pa chipanje, chinonyelo kwisa soni chikamuchisyo chilichose cha petala lya Allah). Sano naga woga (wakujijogopa ngondo) uli waichililemo, timwaone ali nkunnola mmwe (pakogopa kuulajidwa), meso gao gali nkugalaukagalauka nti chisawu ajula jwaakukomoka nacho chiwa. Nambo naga woga uli utyosile akasanchingamilaga ni ndimi syakutema (pakulilapilila machili mwa ukoleko aku achisakaga mbote). Ŵaumbombo pa yambone iliyose (mpela mbotesyo akasasakaga ali ajigele jika ŵanganyao). Ŵanganyao nganakulupilila (kusyene). Basi Allah agajonasile masengo gao, soni yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(33,20,'33:20','Akuganichisya kuti mikutula ja asilikali (ŵachikafili) nganijijaulepe (ligongo lyakujijogopa nnope). Sano naga jili jitite jiyiche (soni) mikutula ja asilikalijo, mpaka asachilile (achinakunakuo) yanti ali ajile kutama imo ni ŵarabu ŵanchimisi, ni akawusyeje ya abali syenu (ali kwanaula). Mwanti (pajele ndemajo) ali ŵanti alinamwe imo, nganaŵa aputile ngondo ikaŵeje panandipe (mwandola ni kogopa nchesela).');
INSERT INTO "silika" VALUES(33,21,'33:21','Chisimu pamasile paluŵele kukwenu lusyasyo lwambone pa Ntenga jwa Allah صلى الله عليه وسلم, kwa mundu jwali nkuŵichila ntima pa Allah ni Lisiku Lyambesi ni kunkumbuchilaga Allah kwejinji.');
INSERT INTO "silika" VALUES(33,22,'33:22','Sano ndema jaŵajiweni ŵakulupilila mikutula ja asilikali ŵatite: “Ayi ni yaŵatupele chilanga Allah ni Ntenga Jwakwe صلى الله عليه وسلم (chakupedwa ipato kaneko ni kulupusyidwa), soni Allah ni Ntenga Jwakwe صلى الله عليه وسلم ŵaŵechete yakuona.” Ni nganiyajonjechesya ŵanganyao ikaŵeje chikulupi ni kulipeleka.');
INSERT INTO "silika" VALUES(33,23,'33:23','Mu ŵakulupilila mwana achalume ŵatesile ukweli pa chilanga chaŵataŵene najo Allah (chakuputa ngondo pamo ni Ntume صلى الله عليه وسلم ningalosya migongo kutiila). Basi ŵane mwa jamanjao pana ŵajimasile ndaŵi jao (pakuwa kapena kuulajidwa petala lya Allah), sano ŵane mwa jamanjao pana ŵaakwembecheya (kuulajidwa kapena kupunda pa ngondo), ni nganagalausya ata panandi (chilanga chao).');
INSERT INTO "silika" VALUES(33,24,'33:24','Kuti Allah ŵalipile ŵakuona pa kuona kwao, ni ŵape ilagasyo achinakunaku nasakaga, kapena kwapochela toba jao. Chisimu Allah aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(33,25,'33:25','Sano Allah ni jwausyisye aŵala ŵaŵakufuulu ni ukali wao, nganapata yambone iliyose. Soni Allah ŵaapuutiile ŵakulupilila ngondo (pakwatumichisya amagongo ŵao mbungo ni asilikali ŵachilaika). Ni aŵele Allah Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(33,26,'33:26','Sano aŵala ŵaŵakamuchisye (achimakafilio) mu achimisyene chitabu (ayuda ŵalukosyo lwa Quraidwa, Allah) ŵaatulwisye m’makome mwao, ni ŵaponyisye m’mitima mwao woga. Likuga line mwaliulagaga, ni kulikamulaga ukapolo likuga line.');
INSERT INTO "silika" VALUES(33,27,'33:27','Ni ŵantendekasyisye kutawalila litaka lyao, nyumba syao ni chipanje chao, kwisa soni litaka lyanganimpondepo (lya Khaibara). Ni aŵele Allah pa chindu chilichose Jwakombola kusyene.');
INSERT INTO "silika" VALUES(33,28,'33:28','E mmwe Ntume (Muhammadi صلى الله عليه وسلم)! Mwasalilani achiŵammakwenu kuti: “Nam’baga jenumanja nkusaka umi wa duniya ni ipeto yakwe, basi ikaani nampe liŵale ni nanneche ulombela kaleche kambone.” ');
INSERT INTO "silika" VALUES(33,29,'33:29','“Nambo naga nkunsaka Allah ni Ntenga Jwakwe صلى الله عليه وسلم kwisa soni Nyumba ja Akhera, basi chisimu Allah ŵalinganyichisye ŵakolosya (masengo) mwa jenumanja malipilo gamakulungwa nnope.”');
INSERT INTO "silika" VALUES(33,30,'33:30','E jenumja achiŵammakwakwe Ntume! Jwachatende mwa jenumanja chausakwa chakuonechela (mpela chigwagwa), tajonjechesyedwe ilagasyo kaŵilikose, soni yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(33,31,'33:31','Sano jwatamanye mwa jenumanja kummanyila Allah ni Ntenga Jwakwe صلى الله عليه وسلم ni kupanganyaga yambone, chitumpe malipilo gakwe kaŵilikose, ni tun’dinganyichisye jwalakwejo lisiki lyambone nnope (ku Mbepo).');
INSERT INTO "silika" VALUES(33,32,'33:32','E jenumanja achiŵammakwakwe Ntume! Nganim’ba jenumanja nti chisawu jumpepe mwa achakongwe (wamba ŵanganaŵa achiŵammakwakwe Ntume), naga n’di ni woga (wakun’jogopa Allah). Basi ngasinsalalisyaga maloŵe (nkaŵechetaga mwakunema ni achalume), atakwika mwakwete tama (jakutenda namwe yausakwa) ajula jwamuli mu ntima mwakwe ulwele (wa chikululu ni unakunaku). Nambo m’becheteje maloŵe gakulitimalika.');
INSERT INTO "silika" VALUES(33,33,'33:33','Tamilichikagani m’majumba mwenu, soni ngasinnosyaga yakulisalalisya yenu yayatiji pakulosyedwa ndema ja ujinga jandanda, ni n’jimicheje Swala mwakolosya ni kutolaga Zaka, ni mumpikanileje Allah ni Ntenga Jwakwe r. Chisimu Allah akusaka kuti antyochesye usakwa (wasambi), E jenumanja achinsyene nyumba (ja Ntume صلى الله عليه وسلم), ni answejesye kunswejesya kusyesyene.');
INSERT INTO "silika" VALUES(33,34,'33:34','Soni kumbuchilani (chindimba cha Allah chachili pa jenumanja ligongo lya) yaikusoomedwa m’majumba mwenu ya ma Ȃya ga Allah ni yalunda lwakusokoka (Sunna sya Ntume صلى الله عليه وسلم), chisimu Allah aŵele Jwakuimanyilila yakunyilila nnope, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(33,35,'33:35','Chisimu Asilamu ŵachilume ni ŵachikongwe, ŵakulupilila ŵachilume ni ŵachikongwe, ŵakunda malamusi ŵachilume ni ŵachikongwe, ŵakuŵecheta yakuona ŵachilume ni ŵachikongwe, ŵakupilila ŵachilume ni ŵachikongwe, ŵakuteteŵala (pameso pa Allah) ŵachilume ni ŵachikongwe, ŵakutola sadaka ŵachilume ni ŵachikongwe, ŵakutaŵa (swaumu) ŵachilume ni ŵachikongwe, ŵagosa utukani wao ŵachilume ni ŵachikongwe, ŵakunkumbuchila Allah kwejinji ŵachilume ni ŵachikongwe, Allah ŵalinganyichisye ŵanganyao chikululuko kwisa soni malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(33,36,'33:36','Soni nganiyiŵa ku jwakulupilila julijose jwachilume kapena jwachikongwe, pati alamwile Allah ni Ntenga Jwakwe صلى الله عليه وسلم chilamusi chilichose, kola ŵanganyao kusagula kulikose mu nganisyo syao. Sano jwatannyosye Allah ni Ntenga Jwakwe صلى الله عليه وسلم, basi pamasile papotele kupotela kwakuonechela.');
INSERT INTO "silika" VALUES(33,37,'33:37','Soni (kumbuchilani mmwe Muhammadi صلى الله عليه وسلم) katema kamwansalile ajula jwa ŵampele Allah ukoto (pakun’jongolela ku Usilamu), nombe mmwe ni kuntendela ukoto (pakumpa ulukosyo, jwajuli Zaid bin Hârithah t, ndema jamwansalile kuti): “Kamulanani ni ŵankwenu (nkaaleka ulombela), ni mun’jogope Allah.” Ni mwaliji nkusisa mu ntima mwenu ayila yasachile Allah kuilosya palangulangu (yakunnombekasya ŵakongweo panyuma pakulechedwa ni ŵankwao, kuti sityoche nganisyo syanti: Mwanache jwakulela juli nti chisawu jwakuliŵelechela), ni mwaliji nkwajogopa ŵandu (kuti atakuŵecheta yanti: “Muhammadi صلى الله عليه وسلم julombele ŵakongwe ŵakulechedwa ni mwanache jwakwe jwakulela juŵaliji soni kapolo jwakwe),” kutendaga Allah ni jwali Jwakuŵajilwa kuti mun’jogopeje. Sano ndema ja chammalile chisako Zaidu رضى الله عنه pa ŵele (ŵakongweo ŵali Zainab binti Jahashi رضي الله عنهم ni kwaleka ulombela); twannombesye mmwe (Muhammadi صلى الله عليه وسلم) ŵele (ŵakongweo), kuti pakaŵaga pana chipaka chilichose ku ŵakulupilila pa (yakwalomba) achiŵammakwao ŵanache ŵao ŵakulela, naga ŵanganyao (achalume ŵawowo) chili chamalile chisako pa ŵanyao (achakongweo ni kwaleka ulombela). Ni chiŵele chilamusi cha Allah chakutendekwape (basi).');
INSERT INTO "silika" VALUES(33,38,'33:38','Pangali gambidwa kulikose kwa Ntume صلى الله عليه وسلم pa (kutenda) ichindu yannamwile Allah. (Aji ni) nyendele ja Allah pa aŵala ŵaŵapite kala. Sano chilamusi cha Allah chiŵele mpango wepanje chenene.');
INSERT INTO "silika" VALUES(33,39,'33:39','Aŵala ŵaakulungusya mautenga ga Allah ni kun’jogopaga Jwalakwe, ni ngaakun’jogopaga jwalijose ikaŵeje Allah. Sano Allah ni akukwanila kuŵa Nkuŵalanjila.');
INSERT INTO "silika" VALUES(33,40,'33:40','Muhammadi صلى الله عليه وسلم nganaŵa baba jwa jwalijose mwa achalume ŵenu, nambo (jwalakwe ni) Ntenga jwa Allah nambo soni jwambesi kwa Achimitume. Sano Allah chindu chilichose aŵele Jwakuchimanya chenene.');
INSERT INTO "silika" VALUES(33,41,'33:41','E jenumanja ŵankulupilile! Munkumbuchilagani Allah kunkumbuchila kwejinji.');
INSERT INTO "silika" VALUES(33,42,'33:42','Ni munswejesyeje kundaŵi ni kwigulo.');
INSERT INTO "silika" VALUES(33,43,'33:43','Jwalakwe ni Ajula jwaakuntendela chanasa ŵanganyammwe, ni Achimalaika Ŵakwe (akum’bombelaga ŵanganyammwe chanasa kwa Allah), kuti ankoposye m’chipi ni kun’jinjisya mwilanguka, ni juŵele Jwalakwe (Allah) Jwachanasa nnope ku ŵakulupilila.');
INSERT INTO "silika" VALUES(33,44,'33:44','Kakomasyane kao lisiku lyatachisimangana Najo (Allah) kachiŵa: “Ntendele!” Ni ŵalinganyichisye ŵanganyao malipilo gakuchimbichika (Mbepo).');
INSERT INTO "silika" VALUES(33,45,'33:45','E mmwe Ntume صلى الله عليه وسلم! Chisimu Uwwe tuntumisye mmwejo kuŵa mboni, jwakutagulila abali jakusengwasya nambo soni jwakutetela.');
INSERT INTO "silika" VALUES(33,46,'33:46','Ni (kuti m’be) jwakuŵilanjila (ŵandu) kwa Allah mwa lisosa Lyakwe, nambo soni (kuti m’be) nyali jakumulika.');
INSERT INTO "silika" VALUES(33,47,'33:47','Soni mwatagulilani ŵakulupilila yanti akwete kupata ŵanganyao kuumila kwa Allah umbone wekulumgwa.');
INSERT INTO "silika" VALUES(33,48,'33:48','Soni ngasimwajitika achimakafili ni achinakunaku, ni nneche isausyo yao (pangaauchisya uwwanga wao mpaka nkalamulidwe kwauchisya), ni n’jegamileje kwa Allah, sano Allah akukwanila kuŵa Nkun’jegama.');
INSERT INTO "silika" VALUES(33,49,'33:49','E jenumanja ŵankulupilile! Naga n’di mwalombile achakongwe ŵakulupilila, kaneka ni kwaleka ulombela nkanim’biche mwakwakwaya (gona ulombela), basi nganinkola jenumanja pa ŵanganyao eda (lilinda lya pakumala kwa ulombela) jilijose jatim’balanjile. Sano mwapani liŵale, ni mwaleche kwaleka kwambone.');
INSERT INTO "silika" VALUES(33,50,'33:50','E mmwe Ntume صلى الله عليه وسلم! Chisimu Uwwe tunkundisye mmwejo achiŵammakwenu aŵala ŵamwapele mahari gao, kwisa soni (mateeka) gagapatile nkono wenu wakun’dyo, mwa agala (mateeka) gampele Allah, ni ŵanache ŵa achibaba ŵenu ŵaakulungwa kapena ŵanandi, ni ŵanache ŵa achibaba ŵenu ŵachikongwe, ni ŵanache ŵa n’jomba ŵenu, ni ŵanache ŵa achimama ŵenu ŵaakulungwa kapena ŵanandi aŵala ŵaasamile nomwe, ni jwankongwe jwakulupilila pati alipeleche nsyene kwa Ntume صلى الله عليه وسلم, najuŵaga Ntume صلى الله عليه وسلم jukusaka kunnomba –iŵele yakundisyidwa kukwenupe (mmwe Ntume صلى الله عليه وسلم kunnomba mwangali kutola chilichose) – ngaŵa ku ŵakulupilila (ŵane). Pamasile patuimanyi yatwalamwile ŵanganyao pa achiŵammakwao kwisa soni (mateeka) gagapatile makono gao ga kun’dyo. (Tunkundisye yalakweyi mmwe Ntume صلى الله عليه وسلم jikape) kuti pakaŵa pana chipaka chilichose kukwenu, sano Allah ni aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(33,51,'33:51','Tinkomboleche kunchelewasya jwamunsachile mwa ŵanganyao, ni kunkumbatisya kukwenu jwamunsachile. Sano naga n’di munsachilile ajula jwamum’bisile pambali tipaŵe pangali ulemwa kukwenu (kunkumbatisya kukwenu). Yalakweyo ni yakuŵandichila kuti meso gao gateteŵale, nambo soni kuti akadandaula, ni ayisengwele yamwapele ŵanganyao ŵanaose. Sano Allah akumanyilila yaili m’mitima mwenu, ni aŵele Allah Jwakumanyilila nnope, Jwakutulala nnope ntima.');
INSERT INTO "silika" VALUES(33,52,'33:52','Panyuma (pa achimmasyeto ŵan’di nao), nganaŵa ŵakundisyidwa kukwenu (kwalomba) achakongwe (ŵane), namuno kuti mwatindanye ŵanganyao ni achimmasyeto (ŵane), nantimuno kukansengwasyisye kusalala kwao, ikaŵeje (mateeka) gagapatile nkono wenu wakun’dyo. Ni aŵele Allah pa chindu chilichose Nkung’unulila kusyene.');
INSERT INTO "silika" VALUES(33,53,'33:53','Chisimu pamasile paluŵele kukwenu lusyasyo lwambone pa Ntenga jwa Allah صلى الله عليه وسلم, kwa mundu jwali nkuŵichila ntima pa Allah ni Lisiku Lyambesi ni kunkumbuchilaga Allah kwejinji.');
INSERT INTO "silika" VALUES(33,54,'33:54','Kanga tinchilosye chindu chilichose kapena tinchisise basi (yalumope), chisimu Allah chindu chilichose aŵele Jwakuchimanya chenene.');
INSERT INTO "silika" VALUES(33,55,'33:55','Pangali ulemwa kwa ŵanganyao (achiŵammakwakwe Ntumeo, kuonechela nkanawale buyibuyi) kwa achibaba ŵao, namuno kwa achiŵanache ŵao, namuno kwa achachimwene ŵao, namuno kwa ŵanache ŵa achachimwene ŵao, namuno kwa ŵanache ŵa achachemwali ŵao, namuno kwa achakongwe achin’jao, namuno kwa aŵala (mateeka) ŵagapatile makono gao gakun’dyo. Ni mun’jogopeje Allah jenumanja (achakongwe). Chisimu Allah pa chindu chilichose aŵele Mboni.');
INSERT INTO "silika" VALUES(33,56,'33:56','Chisimu Allah akupeleka chanasa kwa Ntume (Muhammadi صلى الله عليه وسلم), nombe nao soni Achimalaika (akum’bombelaga chanasa Ntumejo)صلى الله عليه وسلم. E jenumanja ŵankulupilile! Mum’bombelani chanasa jwalakwe ni kum’bombela soni ntendele. ');
INSERT INTO "silika" VALUES(33,57,'33:57','Chisimu aŵala ŵaakunsausya Allah ni Ntenga Jwakwe صلى الله عليه وسلم, Allah ŵalwesile ŵanganyao pa duniya ni ku Akhera kwakwe, ni ŵalinganyichisye soni ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(33,58,'33:58','Soni aŵala ŵaakwasausya ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe pa ichindu yanganapanganya, basi nikuti pamasile patwichile n’gogolo (unami wekulungwa) ni ulemwa wakuonechela.');
INSERT INTO "silika" VALUES(33,59,'33:59','E mmwe Ntume صلى الله عليه وسلم! Mwasalilani achiŵammakwenu, ni ŵanache ŵenu ŵachikongwe, kwisa soni achakongwe ŵa ŵakulupilila kuti: Aliunichileje chilu chosope ni nguwo syao. Yalakweyo ni yakwaŵandichisya kuti amanyicheje (kuti ŵandu ŵakuchimbichika), basi ni ngalagasyidwaga. Sano Allah ni aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(33,60,'33:60','Naga ngakuleka achinakunaku ni aŵala ŵamuli m’mitima mwao mwana ulwele (wachikululu) ni akwenesya abali syaunami (syakwisya woga) m’Madina, chisimu chene chitun’guŵatisye (mmwe Muhammadi صلى الله عليه وسلم) ŵanganyao, kaneko ngaataama namwe pakuŵandika mwalakwemo ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(33,61,'33:61','(Ŵanganyao) ŵalaniwe, palipose paasimanikwe akwembekanyisidwe ni awulajidwe kwanneso.');
INSERT INTO "silika" VALUES(33,62,'33:62','(Aji ni) nyendele ja Allah pa aŵala ŵaŵapite kala, ni ngankusimana kupitikuka kulikose pa nyendele ja Allah.');
INSERT INTO "silika" VALUES(33,63,'33:63','Ŵandu akum’buusya (mmwe Muhammadi صلى الله عليه وسلم) ya ndema (jakwichila Kiyama)? Jilani: “Chisimu umanyilisi wakwe uli kwa Allah pe.” Ana ni chichi chikummanyisya kuti mwine ndemajo kuŵa nkuŵa pakuŵandika?');
INSERT INTO "silika" VALUES(33,64,'33:64','Chisimu Allah ŵalwesile achimakafili, ni ŵalinganyichisye Moto Wakulapuka.');
INSERT INTO "silika" VALUES(33,65,'33:65','Takaŵe ŵandamo mwalakwemo kwa yaka ni yaka, ngasaja kumpata nkugosa jwalijose namuno nkukamuchisya.');
INSERT INTO "silika" VALUES(33,66,'33:66','Lisiku lyachisigalausyidwa ngope syao m’Moto, achitiji: “E nabolaga uwwe tungampikanile Allah ni kumpikanila soni Ntenga (Muhammadi صلى الله عليه وسلم).”');
INSERT INTO "silika" VALUES(33,67,'33:67','Ni tachiti soni: “Ambuje ŵetu! Chisimu uwwe twapikanile mabwana ŵetu kwisa soni achinkulungwa ŵetu, basi ni ŵatusochelesye litala.”');
INSERT INTO "silika" VALUES(33,68,'33:68','“Ambuje ŵetu! Mwapani ŵanganyao ilagasyo yakaŵili, ni mwalwese kwalwesa kwekulungwa.”');
INSERT INTO "silika" VALUES(33,69,'33:69','E jenumanja ŵankulupilile! Ngasim’ba nti chisawu aŵala ŵana kunnagasyaga Musa, basi Allah ni ŵanswejesye ku yaŵaŵechete ŵanganyao, ni jwaliji (Musa) jwakuchimbichika pameso pa Allah. ');
INSERT INTO "silika" VALUES(33,70,'33:70','E jenumanja ŵankulupilile! Mun’jogopani Allah, ni m’becheteje maloŵe gakulungamika.');
INSERT INTO "silika" VALUES(33,71,'33:71','Tankolochesye itendu yenu, ni tankululuchile sambi syenu, sano jwatampikanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, basi ni kuti pamasile paapundile kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(33,72,'33:72','Chisimu Uwwe twagalosisye malamusi ku mawunde, mataka ni matumbi, basi yalakwe ni yakanile kugatwichila (malamusigo), ni yalijogopele nago. Nambo mundu ni jwagatwiichiile. Chisimu jwalakwe jwaliji jwakulitenda nnope lupuso n’jiga kusyene.');
INSERT INTO "silika" VALUES(33,73,'33:73','Kuti Allah ŵape ilagasyo (ŵatanyosye malamusigo ŵali) achinakunaku ŵachilume ni ŵachikongwe, kwisa soni akuwanganya (Allah ni isanamu) ŵachilume ni ŵachikongwe, ni kuti soni Allah ŵapochele toba (ŵatakunde malamusigo ŵali) ŵakulupilila ŵachilume ni ŵachikongwe. Sano Allah ni aŵele Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(34,1,'34:1','Lumbili losope lwene alapikwe Allah Ajula jwaili Yakwe (yosope) yaili kumawunde ni yaili petaka. Soni jukwete lumbili losope lwene ku Akhera, soni Jwalakwe ni Jwalunda lwakusokoka, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(34,2,'34:2','Akuimanyilila yaikwinjila mwitaka, ni yaikopoka mwalakwemo, ni yaikutuluka kuumila kwinani, ni yaikwela kwalakweko, soni Jwalakwe ni Jwachanasa channope, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(34,3,'34:3','Ni ŵaŵechete aŵala ŵaakufuulu kuti: “Ngasijituichilila Kiyama.” Jilani: “Elo, ngwalumbila Ambuje ŵangu! Jichim’bichilila kwene. (Jwalakwe ni) Nkumanyilila yakusisika. Ngachikasasisika Kukwakwe (chindu) chausito wanti mpela lunyilili kumawunde kapena petaka, soni namuno chamwana nnope kupunda chalakwecho kapena chaakulilaapo, niikaŵeje chili m’chitabu chakusalichisya (chilichose Al-Lauh-Al-Mahfûz). ” ');
INSERT INTO "silika" VALUES(34,4,'34:4','Kuti akaalipile aŵala ŵakulupilile ni kutendaga yambone, akwete kupata ŵanganyao chikululuko kwisa soni lisiki lyakuchimbichika (lya ku Mbepo).');
INSERT INTO "silika" VALUES(34,5,'34:5','Sano aŵala ŵaakulimbichila kulimbana ni ma Ȃya Getu aku ali nkulepelekasya (ŵandu kwitala lya Allah), ŵanganyao akwete kupata ilagasyo yakunyalaya ni kupoteka kusyene.');
INSERT INTO "silika" VALUES(34,6,'34:6','Sano aŵala ŵapedwile umanyilisi akuiona ayila yaitulusyidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) kuumila kwa Ambuje ŵenu kuti ni yakuona, soni ikongolela kwitala lya (Allah) Jwamachili gakupunda, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(34,7,'34:7','Sano aŵala ŵaakufuulu ni akutiji (pakusalilana achimisyenepe mwachipongwe): “Ana tunnanjile kwa mundu (achambanga Muhammadi صلى الله عليه وسلم) jwachantagulile (yanti): Patinchikatika-katika ni kuŵa ipande-ipande (m’malembe panyuma pakuwa), kuti chisimu jenumanja tinchiŵa mukagumbidwe kasambano?”');
INSERT INTO "silika" VALUES(34,8,'34:8','Ana jwalakwe (Muhammadi صلى الله عليه وسلم) ansepele Allah unami, kapena mwa jwalakwe mwana masoka? (Ngwamba)! Nambo aŵala ŵangakukulupilila ya Akhera ali mu ilagasyo ni kupotela kwakutalikangana nnope (ni yakuona).');
INSERT INTO "silika" VALUES(34,9,'34:9','Ana ŵanganyao ngakuiona yaili m’bujo mwao ni yaili munyuma mwao, ya liwunde ni litaka? Tuli tusachile mpaka twatitimisye nalyo litaka, kapena kwagwichisya pachanya pao chipande cha liwunde. Chisimu mu yalakweyo muŵele mwana chimanyisyo kwa kapolo jwalijose jwakuwujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(34,10,'34:10','Soni chisimu pamasile patwampele Daudi kuumila Kukwetu ukoto. (Twatite): “E jenumanja achimatumbi! Mun’jitichisyani jwalakwe (Daudi achinswejesyaga Allah), nombe ni jenumanja achiyijuni (ŵakwe).” Ni twantependekachisye jwalakwe chisyano.');
INSERT INTO "silika" VALUES(34,11,'34:11','(Twansalile kuti): “Panganyani (yakuwala yachisyano) yagumbalila (chilu chosope yakulichijila kungondo), ni mpimileje chenene mu kawunje kambonji (syakwe). Soni panganyani ŵanganyammwe yambone. Chisimu Une ichindu yankutenda mbele Jwakuilolechesya.”');
INSERT INTO "silika" VALUES(34,12,'34:12','Nombe kwa Sulaimana (twajijepepesye) mbungo, (jajaliji nkwenda) ulendo wa kundaŵi (lutando lwa) mwesi wantundu, nombe soni ulendo wa kwigulo (lutando lwa) mwesi wantundu. Ni twam’bumichisye tutumule jwa chikungu (ntofu wenyelenyenduche), nombe soni m’majini mwaliji mwana gagakamulaga masengo pameso pakwe, mwa lisosa lya M’mbujegwe (Allah). Sano jwatagalauche mwa ŵanganyao kuleka lilamusi Lyetu, tuchimpa kupasya mu ilagasyo ya Moto Wakulapuka.');
INSERT INTO "silika" VALUES(34,13,'34:13','Ŵantendelaga yajukusaka, kutandila yumba yammakome gakunyang’amala, itusitusi, mataalo (mabeseni) gamakulungwa ganti mpela matanda, kwisa soni majanje gachitamile (pachisisa ligongo lya ukulu nnope). (Twasalile kuti): “Panganyani, E ŵaliŵasa lya Daudi! (Masengo gambone gakulosya) kutogolela (ukoto wampedwile).” Sano ŵannonope mwa achikapolo Ŵangu ni ŵali ŵakutogolela.');
INSERT INTO "silika" VALUES(34,14,'34:14','Basi ndema jatwalamwile kukwakwe (Sulaimana) ya chiwa, pangali chachamanyisye ŵanganyao (achimajinio) ya chiwa chakwe, ikaŵeje inyama ya petaka (ucheche), yayaliji nkulya (mwambolembole) simbo jakwe. Basi ndema jajwagwile, yamanyukwiche kwa majini yanti: Angaŵe ŵamanyililaga yakusisika, nga nganatama mu ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(34,15,'34:15','Chisimu chene ku (ŵandu ŵa ku) Saba’ kwaliji kwana chimanyilo (chakulosya ukombosi wa Allah) m’malo mwao mwakutama - Migunda jiŵili wa chakun’dyo ni wa chakunchiji. (Yaŵechetedwe kukwao yanti): “Lyagani mu lisiki lya M’mbuje gwenu, ni muntogoleleje Jwalakwe. Nsinda wambone, nambo soni M’mbuje Jwakululuka kusyene.”');
INSERT INTO "silika" VALUES(34,16,'34:16','Nambo ŵanyosyisye (lilamusi lya Allah), basi ni twatumichisye mesi gachikumba gana kuchinjidwaga ni uŵigo (gagajonasile migunda jila), ni twatindanyichisye migunda jao jiŵilijila ni migunda jine jiŵili jajakwete isogosi yakuŵaŵa nnope, itela ya ukwesu, ni ine mu itela ya masawu yapanandi.');
INSERT INTO "silika" VALUES(34,17,'34:17','Yalakweyi twalipile ligongo lya kufulu kwao. Ana ni tukwalipilaga (wele ulipilewu) ŵangaŵaga ŵakufulu!');
INSERT INTO "silika" VALUES(34,18,'34:18','Ni twaŵisile chilikati chao (ŵandu ŵa ku Saba’) ni chilikati cha misi ajila jatwajipele upile (ja ku Shami), (twaŵisile) misi jakuonechela, ni twapimile mwalakwemo igono ya ulendo. (Ni twasalile kuti): “Jendagani mwalakwemo chilo ni muusi mwantunjelele.”');
INSERT INTO "silika" VALUES(34,19,'34:19','Nambo ŵanganyao ŵatite: “Ambuje ŵetu! Talikanganyani igono ya ulendo yetu.” Ni ŵalitesile lupuso achimisyene, basi ni twatesile ŵanganyao kuŵa ngani (sisyesyene syaŵandu ŵakala syakwajamuka nasyo ŵandu ŵasambano), ni twapwilingenye kwapwilinganya kosope kwene. Chisimu mu yalakweyo muŵele mwana imanyisyo kwa jwalijose jwali jwakupilila nnope, jwakutogolela kwejinji.');
INSERT INTO "silika" VALUES(34,20,'34:20','Soni chisimu Ibulisu ŵasitendekasyisye nganisyo syakwe kuŵa syakuona kwa ŵanganyao, basi ni ŵankuyiye, ikaŵeje likuga lya ŵakulupilila.');
INSERT INTO "silika" VALUES(34,21,'34:21','Ni nganigapagwa kukwakwe pa ŵanganyao machili galigose, ikaŵeje kuti tummanyukule ajula jwaakukulupilila ya Akhera pakunnekanganya ni ajula jwaakuŵa nkukaichila ya jalakwe. Sano Ambuje ŵenu pa chindu chilichose aŵele Nkusunga chenene.');
INSERT INTO "silika" VALUES(34,22,'34:22','Jilani: “Mwaŵilangani aŵala ŵankulitwanjila (kuti nombe nao milungu) kunneka Allah (kuti ankamuchisye, nambo nganaŵa ankamuchisye chilichose), nganakola ŵanganyao (namuno chindu cha) usito wanti chisawu lunyilili ku mawunde namuno petaka, soni nganakola ŵanganyao mwalakwemo liwunjili lililyose (lyakamusyangana ni Allah), soni nganakola Jwalakwe mwa ŵanganyao nkukamuchisya jwalijose.”');
INSERT INTO "silika" VALUES(34,23,'34:23','Soni chondechonde ngachikasakamuchisya pameso Pakwe (Allah) ikaŵeje kwa jwampele luusa (lwakuti achondelele). Mpaka pati woga uli utyosile m’mitima mwao (Achimalaika) akasatiji: “Ana M’mbuje gwenu juŵechete ichichi?” (Achimalaika ŵane) akutiji: “(Jwalakwe juŵechete) yakuonaonape, soni Jwalakwe ni Jwapenani kusyene, Jwankulungwa kwannope.”');
INSERT INTO "silika" VALUES(34,24,'34:24','Jilani: “Ana nduni jwaakumpaga lisiki kuumila kumawunde ni petaka?” Jilani: “Allah. Soni mwine uwwe kapena jenumanja tuli pachongoko kapena pakupotela kwakuonechela.”');
INSERT INTO "silika" VALUES(34,25,'34:25','Jilani: “Jenumanja ngasim’buwusyidwa pa yatuleŵile uwwe, nambo soni uwwe ngasituwusyidwa pa yankupanganya jenumanja.”');
INSERT INTO "silika" VALUES(34,26,'34:26','Jilani: “Tachitusonganganya chilikati chetu Ambuje ŵetu (ku Kiyama), kaneka ni tachijilanya chilikati chetu mwa chilungamiko, soni Jwalakwe ni Nkwilanya chenene, Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(34,27,'34:27','Jilani: “Munosyani aŵala ŵamwasimangenye Najo (Allah) kuŵa akunkamuchisya (Ŵakwe). Ngwamba (Jwalakwe nganakola akunkamuchisya ŵaliose)! Nambo Jwalakwe ni Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(34,28,'34:28','Ni nganituntumisya mmwe (Muhammadi صلى الله عليه وسلم) ikaŵeje ku ŵandu wosope, (kuti m’be) ŵakupeleka abali jakusengwasya nambo soni ŵakutetela, nambo ŵandu ŵajinji ngakumanyilila.');
INSERT INTO "silika" VALUES(34,29,'34:29','Sano ni akutiji: “Ana chele chilangachi chiyiche chakachi nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(34,30,'34:30','Jilani: “Jenumanja nkwete chilanga cha Lisiku (lya Kiyama) lyangaŵa nkulichelewachisya jenumanja ndaŵi jimpepe namuno kulijanguyichisya.”');
INSERT INTO "silika" VALUES(34,31,'34:31','Sano aŵala ŵaakufuulu ni akasatiji: “Nganituŵa tujikulupilile aji Qur’aniji, namuno ayila (itabu) yayaliji paujo pakwe.” Soni ingaŵe mwajile kwaona ŵalupuso patakajimichidwe pameso pa M’mbuje gwao (Allah), achiusyanganilanaga maloŵe (gakudandaulana) ŵane kwa ŵane, (nkajile kuiona yakogoya kusyene)! Aŵala ŵangalikombolela tachasalila aŵala ŵaŵalikwesisye kuti: “Ingaŵaga ŵanganyammwe, chisimu chene tungaliji ŵakulupilila.”');
INSERT INTO "silika" VALUES(34,32,'34:32','Aŵala ŵaŵalikwesisye tachajanga aŵala ŵangalikombolela kuti: “Ana uwwe ni ŵatwansiŵilile ku chongoko panyuma pakuti chili chim’bichilile? (Ngwamba), nambo jenumanja mwaliji ŵakuleŵa.”');
INSERT INTO "silika" VALUES(34,33,'34:33','Aŵala ŵangalikombolela ni tachiti pakwajanga aŵala ŵaŵalikwesisye: “(Ngwamba), nambo (jenumanja mwaliji nkutenda) malindi ga chilo ni muusi (gakutusiŵilila ku Usilamu), pa ndema jamwatulamulaga kuti tunkane Allah, ni kuti tum’bichileje Jwalakwe milungu (jine).” Ni tachisisa ŵanganyao madandawusi pandema jatachiyiona ilagasyo, ni tuchiŵika makongwa mungosi sya aŵala ŵaŵakufuulu. Ana akulipidwa inepe pangaŵaga ayila yaŵaliji nkupanganya?');
INSERT INTO "silika" VALUES(34,34,'34:34','Ni nganituntumisyeje nkutetela jwalijose ku musi uliose ikaŵeje ŵakulipatila ŵakwe ŵatiji: “Chisimu uwwe yantumisyidwe nayo ŵanganyammwe tukuyikana.”');
INSERT INTO "silika" VALUES(34,35,'34:35','Soni ŵatiji: “Uwwe ni ŵatukwete chipanje chejinji kwisa soni ŵanache ŵajinji, soni uwwe nganituŵa tupedwile ilagasyo.”');
INSERT INTO "silika" VALUES(34,36,'34:36','Jilani: “Chisimu Ambuje ŵangu akasansapangulila lisiki jwansachile, ni kunandiya (lisikilyo kwa jwansachile), nambo ŵandu ŵajinji ngakumanyilila.”');
INSERT INTO "silika" VALUES(34,37,'34:37','Ni nganichiŵa chipanje chenu namuno ŵanache ŵenu kuti ni yaim’bandichisye Kukwetu pa dalaja (ja ku Mbepo), pangaŵaga jwakulupilile ni kutendaga yambone. Basi ŵanganyao tachipata malipilo gakonjechesya ligongo lya yaŵapanganyisye, soni ŵanganyao mu yumba (yapenani ya ku Mbepo) takaŵe ŵantunjelele.');
INSERT INTO "silika" VALUES(34,38,'34:38','Sano aŵala ŵaakulimbichila kulimbana ni ma Ȃya Getu aku ali nkulepelekasya (ŵandu kwitala lya Allah), ŵanganyao ku ilagasyo tachiika nao.');
INSERT INTO "silika" VALUES(34,39,'34:39','Jilani: “Chisimu Ambuje ŵangu akasansapangulilaga lisiki jwansachile mwa achikapolo Ŵakwe, ni kunnandiyichisya (lisikilyo jwansachile). Sano chilichose chachintole (petala lya Allah), basi Jwalakwe talipisye chalakwecho, soni Jwalakwe ni Jwambone nnope mwa akupeleka lisiki.”');
INSERT INTO "silika" VALUES(34,40,'34:40','Soni (kumbuchilani) lisiku lyatachasonganganya ŵanaose, kaneka ni tachiŵecheta kwa Achimalaika kuti: “Ana ŵanganyaŵa ŵaliji nkun’galagatila jenumanja?”');
INSERT INTO "silika" VALUES(34,41,'34:41','Ŵanganyao tachiti: “Kuswejela ni Kwenu! Mmwe ni Nsyoŵe jwetu ngaŵa ŵanganyao. (Ngwamba), nambo ŵaliji nkugagalagatila majini, ŵajinji ŵao ŵaliji nkugakulupilila galakwego.”');
INSERT INTO "silika" VALUES(34,42,'34:42','Basi lelo ngaakola jwine mwa jenumanja chikamuchisyo chakunkamuchisya jwine namuno masausyo gakunsausya jwine. Ni tuchiti ku aŵala ŵaŵalitesile lupuso: “Pasyani ilagasyo ya Moto wamwaliji nkuwukaanila.”');
INSERT INTO "silika" VALUES(34,43,'34:43','Sano pati gachisoomedwaga kukwao ma Ȃya Getu gakupikanika, akasatiji: “Nganijuŵa aju (Muhammadi صلى الله عليه وسلم) ikaŵeje mundu jwaakusaka kunsiŵilila ku yaŵaliji nkuigalagatila achatati ŵenu.” Ni akasatiji soni: “Nganijiŵa aji (Qur’an) ikaŵeje unami waukusepedwa.” Ni akasaŵechetaga aŵala ŵaakufuulu pakuigamba yakuona ndema jakwaichilila: “Nganiyiŵa ayi ikaŵeje usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(34,44,'34:44','Ni nganitwapeje ŵanganyaŵa (ŵarabu) itabu iliyose yaŵaliji nkuisooma (nkanijiŵe Qur’an), nambo soni nganitutumisyeje kukwao m’bujo mwenu (mmwe Muhammadi صلى الله عليه وسلم nkanim’biche) nkutetela jwalijose.');
INSERT INTO "silika" VALUES(34,45,'34:45','Ni ŵakaaniile aŵala ŵapaujo pao, ni nganaliyika (aŵa ŵarabu) liunjili limpepe m’maunjili likumi lya yatwapele ŵanganyao, basi ŵanganyao ŵakaaniile achimitenga Ŵangu, nambi kwaliji uli kasilika Kwangu (ngati najonasile wosope)?');
INSERT INTO "silika" VALUES(34,46,'34:46','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une ngun’jamuka chindu chimpepe: Chakuti n’jiime ligongo lya Allah, ŵaŵili-ŵaŵili ni jumo-jumo, kaneka ni nchetelele; (tinyyione yanti) n’jenuju (Muhammadi صلى الله عليه وسلم) nganakola masoka, nganijuŵa jwalakwe ikaŵeje nkuntetela jwenu, nkaniyiyiche ilagasyo yaukali nnope.”');
INSERT INTO "silika" VALUES(34,47,'34:47','Jilani: “Malipilo galigose ganan’jujile (naga pakwete panan’jujile), basi gaŵele genu (gajigalani), nganigaŵa malipilo gangu ikaŵeje kwa Allah pe, soni Jwalakwe pa chindu chilichose aŵele Mboni.”');
INSERT INTO "silika" VALUES(34,48,'34:48','Jilani: “Chisimu Ambuje ŵangu akuiponyaga yakuona (kwa Achimitume mpela ayi yaimbichilile uneyi, nambo soni Jwalakwe ni) Nkumanyilila chenene yakusisika.”');
INSERT INTO "silika" VALUES(34,49,'34:49','Jilani: “Yakuona (Usilamu) iŵiche, sano yaukoleko (ukafili) nganiyiŵa iŵiche ni yasambano kapena kuuchisya (machili gakwe).”');
INSERT INTO "silika" VALUES(34,50,'34:50','Jilani: “Naga ngupotela, nikuti ngupotela kwaalikonya nansyene, nambo naga ngongoka, basi niligongo lya yaakunjuwulila Ambuje ŵangu, chisimu Jwalakwe ni Jwakupikanichisya, Jwakuŵandichila nnope (kukwetu ni umanyilisi Wakwe).”');
INSERT INTO "silika" VALUES(34,51,'34:51','Soni ingaŵe mwajile kuiona ndema jatakajogope ŵanganyao, kutendaga pali pangali pagwalila, ni kwembekanyisyidwa (mwanakamo) kuumila pamalo pakuŵandika (m’malembe, nkajile kuiona yakogoya kusyene).');
INSERT INTO "silika" VALUES(34,52,'34:52','Sano ŵanganyao tachiti: “Tujikulupilile (sano dini ja Usilamu).” Nambo takakombole chantiuli kuupata (Usilamu ku Akhera) kwakuli kumalo kwakutalichila (ni kwakuupatila Usilamuo kwakuli ku duniya)?');
INSERT INTO "silika" VALUES(34,53,'34:53','Soni kutendaga pamasile pa ŵaukanile (Usilamuo) kala (ku duniya), ni ŵaliji nkuiponya yakusisika kuumila pamalo pakutalichila, (ŵaliji nkuikaichila yosope yayasisikaga mulunda lwao mpela Utume, Qur’an, Akhera kaichila kwakutalichila nnope ni yakuona).');
INSERT INTO "silika" VALUES(34,54,'34:54','Ni tachisiŵilikanganywa chilikati chao ni chilikati cha yaakuisaka (yakunda Usilamu ni kupocheledwa toba jao), mpelayayatite pakutendedwa ku (makafili) gajao kala, chisimu ŵanganyao ŵaliji m’chikaiko chakwakaikasya (utenga wa Allah).');
INSERT INTO "silika" VALUES(35,1,'35:1','Lumbili losope lwene alapikwe Allah, Nkugumba mawunde ni mataka, Nkugatenda Malaika kuŵa Achimitenga ŵakwete mapapiko gaŵili-gaŵili, gatatu-gatatu, kapena ncheche-ncheche, akonjechesyaga mu kagumbe yaakusaka. Chisimu Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(35,2,'35:2','Ukoto uliose waakwaugulilaga Allah ŵandu, basi pangali jwampaka awusiŵilile, soni waakuwusiŵilila, basi pangali jwampaka awupeleche panyuma pakwe, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(35,3,'35:3','E jenumanja achinaŵandu! Kumbuchilani chindimba cha Allah pa jenumanja. Ana pana nkugumba jwangaŵaga Allah jwaakumpaga lisiki kuumila kwinani ni pasi? Pangali nnungujwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwalakwe. Nambi nkugalausyidwa chinauli (kuleka yakuona)?');
INSERT INTO "silika" VALUES(35,4,'35:4','Sano naga achinkaanilaga (mmwe Muhammadi صلى الله عليه وسلم), basi (ngayiŵa yakusimosya) ngati pamasile paŵakanilidwe Achimitenga paujo penu (mmwe nkanim’biche). Soni kwa Allah ni kwakuujila ichindu yosope.');
INSERT INTO "silika" VALUES(35,5,'35:5','E jenumanja achinaŵandu! Chisimu chilanga cha Allah chakuonaonape. Basi ukannyenga ŵanganyammwe ata panandi umi wa duniya, nambo soni akannyega jenumanja ata panandi nkunyenga (shetani) pa ya Allah.');
INSERT INTO "silika" VALUES(35,6,'35:6','Chisimu shetani kukwenu juŵele mmagongo, basi muntendani kuŵa mmagongo (nkan’jitikaga yajukusaka), chisimu jukuliŵilanjila likuga lyakwe kuti likaŵe mu ŵandu ŵa ku Moto Wakulapuka.');
INSERT INTO "silika" VALUES(35,7,'35:7','Aŵala ŵaakufuulu akwete kupata ilagasyo yaukali nnope, sano aŵala ŵaakulupilile ni kutendaga yambone, akwete kupata chikululuko ni malipilo gamakulungwa.');
INSERT INTO "silika" VALUES(35,8,'35:8','Ana ajula jwaisalalisyidwe kukwakwe itendo yakwe yangalumbana, ni jukuiona kuti yambone (chichi champaka munsalile kuti apikane)? Basi chisimu Allah akasannechelelaga kusokonechela jwansachile, ni akasan’jongolaga jwansachile. Basi ntima wenu ukatyoka ligongo lya kwadandaulila ŵanganyao. Chisimu Allah ni Nkumanyilila chenene yaakupanganya ŵanganyao.');
INSERT INTO "silika" VALUES(35,9,'35:9','Soni Allah ni Ajula jwaakutumisyaga mbungo, ni jikasagatenganyaga mawunde gawula, basi ni tukasagajausyaga ku chilambo chachiwe, basi ni tukasasyuchisyaga nago litaka panyuma pakuwa kwakwe. Iyyoyopeyo (ni mukuchiŵela soni) kwimuka (m’malembe).');
INSERT INTO "silika" VALUES(35,10,'35:10','Mundu jwaakuŵa ali nkusaka uchimbichimbi, basi (an’jitiche Allah), ngati Allah ni Nsyene uchimbichimbi wosope. Kukwakwe (Allah) gaakwela maloŵe gambone, sano masengo gambone ni gakugakwesya (maloŵe gambonego, mwanti maloŵe gambonepe pangali masengo gambone ngaŵa nkukamuchisya). Sano aŵala ŵaakutenda malindi ga yakusakala, akwete kupata ilagasyo yaukali nnope, soni malindi ga ŵanganyao gaŵele gakonasika.');
INSERT INTO "silika" VALUES(35,11,'35:11','Soni Allah an’gumbile jenumanja kuumila mwitaka, kaneka kuumila mwindondwa lya mesi (gaŵalume ni ŵakongwe), kaneka am’bisile kuŵa ŵamitundu jiŵili (achalume ni achakongwe). Jwankongwe jwalijose jwangajigala chiilu kapena kuŵeleka, ikaŵeje kwa umanyilisi Ŵakwe (Allah). Ni ngakasapedwaga umi welewu nkupedwa umi welewu, kapena (jwine) kupungusyidwa mu umi wakwe, ikaŵeje (ipali yosopeyo) m’Chitabu (Cha Al-Lauh Al-Mahfȗz), chisimu yalakweyo kwa Allah iŵele yakwepepala kusyene.');
INSERT INTO "silika" VALUES(35,12,'35:12','Soni nganisiŵa silandene mbwani siŵili; aji ja (mesi ga) kunong’a, gakulyolyopela nambo soni gakumweleka chenene, sano jineji ja (mesi ga) chijete gakuŵaŵa, sano kuumila mu sinasiŵilisi nkulyaga nyama jamatumbi (somba syeŵisi), ni nkukoposyaga ipeto yankuwalaga (ngalekale), ni nkuionaga yombo mwalakwemo inankatanganya kuti nsosejesoseje umbone Wakwe (wa Allah), ni kuti jenumanja m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(35,13,'35:13','Akasachijinjisyaga chilo m’muusi (ni kuleupa muusi kwipipa chilo mpela ndaŵi jachitukuta), ni akwinjisyaga soni muusi m’chilo (ni kuleupa chilo kwipipa muusi mpela ndaŵi jakusisima), ni alijepepesye lyuŵa ni mwesi, yosope ikwenda kwandema jakwe jekolanjidwe, Jwalakwe ni Allah M’mbuje gwenu, uchimwene ni Wakwe. Sano aŵala ŵankwaŵenda kunneka Jwalakwe nganakola (chindu chilichose namuno chamwana nti chisawu) chikoŵa cha lupele lwa tende.');
INSERT INTO "silika" VALUES(35,14,'35:14','Naga nkwaŵenda ŵanganyao nganaŵa apikene kuŵenda kwenu, soni namuno angapikane nga nganan’janga, soni lisiku lya Kiyama tachikana kuwanganya kwenu (kwamwaliji nkwawanganya ni Allah), ni pangali jwampaka ansalile mwakulandana (ni mwaakunsalilila Allah) Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(35,15,'35:15','E jenumanja achinaŵandu! Jenumanja ni ŵan’di ŵakulajila kwa Allah, sano Allah ni jwali Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(35,16,'35:16','Nasakaga mpaka antyosye jenumanja, ni kwika nayo iwumbe yasambano.');
INSERT INTO "silika" VALUES(35,17,'35:17','Soni yalakweyo kwa Allah nganiyiŵa yakusausya ata panandi.');
INSERT INTO "silika" VALUES(35,18,'35:18','Soni jwaatwiichiile nsigo (wasambi) ngasaja kutwichila nsigo wajwine, soni jwaasitopelwe ali am’bilasile (jwine) ku nsigo wakwe (kuti ampochele), nganiuŵa upocheledwe ata panandi (ni jwalakwejo) namuno juli jwa paulongo. Chisimu mmwe (Muhammadi صلى الله عليه وسلم) nkwatetela aŵala ŵaakun’jogopa M’mbuje gwao (Allah) mwangali kum’bona, ni akwimikaga Swala mwakolosya. Sano jwaakuliswejesya, basi nikuti akuliswejesya kwaalikamuchisya nsyene. Soni kwa Allah pe ni kwakuujila (iwumbe yosospe).');
INSERT INTO "silika" VALUES(35,19,'35:19','Soni nganaŵa alandene jwangalola ni jwakulolaga.');
INSERT INTO "silika" VALUES(35,20,'35:20','Namuno chipi ni lilanguka.');
INSERT INTO "silika" VALUES(35,21,'35:21','Namuno m’bulili ni lyoto.');
INSERT INTO "silika" VALUES(35,22,'35:22','Soni nganaŵa alandene ŵachijumi ni ŵawe. Chisimu Allah akumpikanisyaga jwansachile, sano mmwe nganim’ba mwapikanisye wali m’malembe.');
INSERT INTO "silika" VALUES(35,23,'35:23','Nganim’ba mmwe ikaŵeje nkutetela.');
INSERT INTO "silika" VALUES(35,24,'35:24','Chisimu Uwwe tumtumisye mmwe (Muhammadi صلى الله عليه وسلم) mwakuonaonape kuŵa nkupeleka abali jakusengwasya ni nkutetela, ni pangali nkutula uliose ikaŵeje jwapite mwa walakweo nkutetela.');
INSERT INTO "silika" VALUES(35,25,'35:25','Sano naga achinkanilaga (ngaiŵa yakusimosya), basi pamasile paŵaakaaniile aŵala ŵaŵaliji paujo pao, ŵayichilile achimitenga ŵao ni (ilosyo) yakuonechela palangulangu, ikalakala kwisa soni chitabu chakupeleka lilanguka.');
INSERT INTO "silika" VALUES(35,26,'35:26','Kaneka nakwembekenye aŵala ŵaŵakufuulu, nambi kwaliji uli kasilika Kwangu (ngati najonasile wosope)?');
INSERT INTO "silika" VALUES(35,27,'35:27','Ana nganinyyiona yanti Allah atulwisye kuumila kwinani mesi (gawula), basi ni tukopochesye isogosi yakulekangana mitundu jakwe, nambo soni m’matumbi mwana tumisela tweswela ni twechejeu twakulekangana mitundu jakwe, ni twine twepiliu nnope?');
INSERT INTO "silika" VALUES(35,28,'35:28','Nambo soni mu ŵandu, inyama ni ilango, iyyoyopeyo soni mitundu jakwe jakulekangana. Chisimu ŵaakun’jogopa Allah mwa achikapolo Ŵakwe ni Maulama. Chisimu Allah ni Jwamachili gakupunda, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(35,29,'35:29','Chisimu aŵala ŵaakusoomaga chitabu cha Allah (Qur’an), ni kwimikaga Swala mwakolosya, ni kutolaga mu ichindu yatwapele mwakusisa ni mwakuonechela; ŵanganyao akusachililaga malonda gangajonasika.');
INSERT INTO "silika" VALUES(35,30,'35:30','Kuti akalipile malipilo gao mwakwana, ni kwajonjechesya mu umbone Wakwe, chisimu Jwalakwe ni Jwakululuka kusyene, Jwakutogolela nnope.');
INSERT INTO "silika" VALUES(35,31,'35:31','Sano ayi yatun’juwulile mmwe (Muhammadi صلى الله عليه وسلم) ya m’Chitabu (cha Qur’an), ni yaili yakuonaonape, yaikwitichisya yaili paujo pakwe. Chisimu Allah kwa achikapolo Ŵakwe juŵele Jwakwamanya chenene, Jwakwalolechesya.');
INSERT INTO "silika" VALUES(35,32,'35:32','Kaneka twapele Chitabu (Qur’an) aŵala ŵatwasagwile mwa achikapolo Ŵetu (ŵakunkuya Muhammadi صلى الله عليه وسلم), basi ŵane mwa jamanjao ali ŵakulitenda lupuso achimisyene (pakola yakusakala yejinji kuipunda yambone), ŵane soni mwa jamanjao ali ŵachilikati (ŵakulandana yambone ni yakusakala yao), ni pana ŵane soni mwa jamanjao ŵakulongolela pa yambone mu lisosa lya Allah, walakweo ni wauli umbone wekulungwa.');
INSERT INTO "silika" VALUES(35,33,'35:33','Matimbe Gandamo takagajinjile, takapetedwe mwalakwemo ni makosa gagolodi ni ngalekale, soni nguwo syao mwalakwemo tisikaŵe sya silika.');
INSERT INTO "silika" VALUES(35,34,'35:34','Ni takajile: “Lumbili losope lwene alapikwe Allah Ajula jwatutyochesye uwwe madandausi, chisimu Ambuje ŵetu ni Ŵakululuka kusyene, Ŵakutogolela nnope.”');
INSERT INTO "silika" VALUES(35,35,'35:35','“Ajula jwatutamiche uwwe m’Nyumba Jandamo kwa umbone Wakwe, taabu ngajitukwaya mwalakwemo, nambo soni ngakutukwaya mwalakwemo kupela.”');
INSERT INTO "silika" VALUES(35,36,'35:36','Sano aŵala ŵaakufuulu akwete kupata moto wa Jahannama, ngasikulamulidwa kukwao (yakuti awe) basi nikuwa, nambo soni ilagasyo yakwe ngasiipungusyidwa pa ŵanganyao. Mwele ni mwatukun’dipililaga kafili jwalijose.');
INSERT INTO "silika" VALUES(35,37,'35:37','Soni ŵanganyao takaŵeje ali nkugumila mwalakwemo (pa kun’dilila Allah kuti): “Ambuje ŵetu! Tukoposyani, tukatende yambone yangaŵaga ayila yatwaliji nkutenda.” (Allah takajile): “Ana nganitumpa umi welewu wakomboleka kumbuchila mu walakweo jwakumbuchila? Nambo soni ŵam’bichilile nkutetela. Basi pasyani (ilagasyo), sano ŵakulitenda lupuso nganakola nkwakamuchisya.”');
INSERT INTO "silika" VALUES(35,38,'35:38','Chisimu Allah ni Nkumanyilila yakusisika ya kumawunde ni petaka, chisimu Jwalakwe ni Nkumanyilila yaili m’mitima.');
INSERT INTO "silika" VALUES(35,39,'35:39','Jwalakwe ni Ajula jwam’bisile ŵanganyammwe kuŵa ŵakupisyangana pa chilambo (kuwa ŵane ni kupagwa ŵane), sano jwatakufulu basi ukafili wakweo tiwun’jonanje nsyene, ni ngaukwajonjechesyaga achimakafili ukafili waowo kwa Ambuje ŵao (Allah) ikaŵeje kuŵenjedwa, nambo soni ngaukwajonjechesyaga achimakafili ukafili waowo ikaŵeje chasiko.');
INSERT INTO "silika" VALUES(35,40,'35:40','Jilani: “Ana nkwaona akwawanganya ŵenu (ni Allah) aŵala ŵankwalumbanjila kunneka Allah? Munosyani, ana ni upandechi wetaka waagumbile? Kapena akwete ŵanganyao liunjili lyakamusyangana (najo Allah) ku mawunde?” Kapena twapele ŵanganyao (achimakafilio) chitabu, basi ŵanganyao ni ali ni umboni wakuonechela kuumila mu chalakwecho (wakwakundisya ukafili waowo)? (Ngwamba), nambo ŵalupuso ngakulanganaga ŵane ni ŵane ikaŵeje ya chinyengo.');
INSERT INTO "silika" VALUES(35,41,'35:41','Chisimu Allah ni jwaakugakamula mawunde ni mataka kuti gakatyoka mmangwakwe, soni naga gali gatite gatyoche mmangwakwe pangali anta jumo jwampaka agakamule panyuma pa Jwalakwe, chisimu Jwalakwe juŵele Jwakutulala nnope ntima, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(35,42,'35:42','Ni ŵalumbile ŵanganyaŵa (achimakafili) mwa Allah kulumbila kwao kwamachili kuti: Naga tiŵaichilile nkutetela, chisimu chene tachiŵa ŵakongoka nnope kujipunda mikutula jine jilijose. Basi ndema jajwaichilile nkutetela nganiyajonjechesya ikaŵeje kutalikangana (ni yakuona).');
INSERT INTO "silika" VALUES(35,43,'35:43','Pakulikwesya pa chilambo ni kutenda malindi gakusakala (gakwasiŵilikanganya ŵandu ku chikulupi), ni gangaasyungula malindi gakusakala ikaŵeje achimisyene (ŵaagatesile), ana akwembecheya ŵanganyao (inepe) pangaŵaga nyendele ja (kapedwe ilagasyo ka ŵandu) ŵandanda! Basi ni ngankusimana kupitikuka kulikose pa nyendele ja Allah, nambo soni ngankusimana galauka kulikose pa nyendele ja Allah.');
INSERT INTO "silika" VALUES(35,44,'35:44','Ana ŵanganyaŵa ngakwendajenda pa chilambo ni kupitaga nchilola mujaŵelele mbesi ja aŵala ŵapaujo pao, ŵaŵaliji soni ŵamachili nnope kwapunda ŵanganyaŵa? Sano Allah nganaŵa jwanti ni kunkulupuka chindu chilichose ku mawunde namuno petaka, chisimu Jwalakwe aŵele Jwakumanyilila nnope, Jwakombola kusyene.');
INSERT INTO "silika" VALUES(35,45,'35:45','Soni ingaŵe kuti Allah akwapaga ilagasyo ŵandu pa yapanganyisye, nga nganaleka pachanya pakwe (petaka) chinyama chilichose chakuulukutaga. Nambo kuti akwachelewasyaga mpaka pa ndema jakwe jekolanjidwe. Basi pajichiŵika ndema jao (tachilipidwa), basi chisimu Allah aŵele Jwakwalolechesya achikapolo Ŵakwe.');
INSERT INTO "silika" VALUES(36,1,'36:1','Yâ-Sîn. ');
INSERT INTO "silika" VALUES(36,2,'36:2','Ngujilumbila (aji) Qur’an jegumbale lunda lwakusokoka.');
INSERT INTO "silika" VALUES(36,3,'36:3','Chisimu mmwe (Muhammadi صلى الله عليه وسلم) n’di mwa Achimitenga.');
INSERT INTO "silika" VALUES(36,4,'36:4','(Soni n’di) pachanya petala lyagoloka.');
INSERT INTO "silika" VALUES(36,5,'36:5','(Aji ni Qur’an) jajitulusyidwe ni (Allah) Jwamachili gakupunda, Jwachanasa channope.');
INSERT INTO "silika" VALUES(36,6,'36:6','Kuti mwatetele ŵandu ŵanganateteledweje achatati ŵao, basi ŵanganyao ali ŵakwanjila (chikulupi).Chisimu liloŵe (lya ipotesi) pamasile palisimichiche pa ŵajinji mwa ŵanganyao, mwanti ŵanganyao ngaakulupilila.');
INSERT INTO "silika" VALUES(36,7,'36:7','Chisimu liloŵe (lya ipotesi) pamasile palisimichiche pa ŵajinji mwa ŵanganyao, mwanti ŵanganyao ngaakulupilila.');
INSERT INTO "silika" VALUES(36,8,'36:8','Chisimu Uwwe tuŵisile mu ngosi mwao makongwa ni gayiche mpaka mu ipundi, mwanti mitwe jao jinyakulidwe (ŵangakombola galausya kapena kutulusya mitwe jao kuti achione chikulupi).');
INSERT INTO "silika" VALUES(36,9,'36:9','Ni tuŵisile m’bujo mwao chakusiŵilila ni munyuma mwao chakusiŵilila, ni twaunichile, mwanti ŵangaŵa nkulola.');
INSERT INTO "silika" VALUES(36,10,'36:10','Soni yalumope kwa jamanjao mwatetele kapena nkatetela, ngaakulupililape.');
INSERT INTO "silika" VALUES(36,11,'36:11','Chisimu mmwe mpaka muntetele (ni kunkamuchisya kutetelako) ajula jwaakukuya chikumbusyo (Qur’an), ni kun’jogopaga (Allah) Jwaukoto wejinji mwangali kum’bona, basi munsengwasyani ya (kupata) chikululuko kwisa soni malipilo gakuchimbichika.');
INSERT INTO "silika" VALUES(36,12,'36:12','Chisimu Uwwe ni ŵatuchijimusya ŵawe, ni tukulembaga yaŵalongwesye (kupanganya ni katika malipilo gakwe), ni sajo syao (syaŵaliji nkwenda kuja kutenda ibada, ni masengo gaŵapanganyisye gagakwendelechela malipilo gakwe), sano chindu chilichose tuchisunjile (pakuchilemba) m’chitabu chakusalichisya (chilichose Al-Lauh Al-Mahfȗz).');
INSERT INTO "silika" VALUES(36,13,'36:13','Mwapani ŵanganyao chilandanyo cha achinsyene musi (wa Antwâkia), pandema jaŵauŵichilile Achimitenga.');
INSERT INTO "silika" VALUES(36,14,'36:14','Pandema jatwatumichisye (Achimitenga) ŵaŵili, ni ŵaakaaniile, basi ni twalimbangenye pa (kwatumichisya Ntenga) jwatatu, basi (ŵanaŵatatu) ni ŵatite: “Chisimu uwwe tutumidwe kukwenu.”');
INSERT INTO "silika" VALUES(36,15,'36:15','(Achinsyene musi) ŵatite: “Nganim’batu jenumanja ikaŵeje ŵandu mpela uwwewe, soni (Allah) Jwaukoto wejinji nganatumisya chilichose, nganim’ba jenumanja ikaŵeje kuti nkulambusya.”');
INSERT INTO "silika" VALUES(36,16,'36:16','(Achimitenga) ŵatite: “Ambuje ŵetu akumanyilila yanti chisimu uwwe tutumisyidwe kukwenu.”');
INSERT INTO "silika" VALUES(36,17,'36:17','“Ni nganiyiŵa kukwetu ikaŵeje kulungusya (utenga) mwakupikanika.”');
INSERT INTO "silika" VALUES(36,18,'36:18','(Achinsyene musi) ŵatite: “Chisimu uwwe tutapene ligongo lya jenumanja, nambo naga ngankuleka, chisimu chene chitunsome maganga, soni tiyinkwaye kwene kuumila kukwetu ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(36,19,'36:19','(Achimitenga) ŵatite: “Chitapani chenu chili ni mwachimisyene (ligongo lya ukafili wenu), ana ligongo lyanti nkumbusyidwe (ni nkuti tuntapenye ni kutujogoyaga)? (Ngwamba), nambo jenumanja ni ŵandu ŵakupundanganya malile (pa ukafili).”');
INSERT INTO "silika" VALUES(36,20,'36:20','Ni jwayiche mundu anankutugala kuumila ku upande wakutalichila wa nsinda. Jwalakwe jwatite: “E ŵandu ŵangu! Mwakuyani Achimitenga.”');
INSERT INTO "silika" VALUES(36,21,'36:21','“Mwakuyani ŵandu ŵangaakun’juga malipilo, nambo soni ŵanganyao ali ŵakongoka.”');
INSERT INTO "silika" VALUES(36,22,'36:22','“Ana chipali chichi kukwangu chachindendekasye kuti ngan’galagatila Ajula (Allah) juŵangumbile une, soni Kukwakwe jenumanja tinchiuchisyidwa?”');
INSERT INTO "silika" VALUES(36,23,'36:23','“Ana nalitendele milungu jine kunneka Jwalakwe? Naga ali asachilile (Allah) Jwaukoto wejinji kumba isausyo chondechonde jao (achiisanamuo) nganijiŵa jingamuchisye une chilichose, soni ŵanganyao nganaŵa anjokwele.”');
INSERT INTO "silika" VALUES(36,24,'36:24','“Chisimu une njiyikaga mwakutenda yele timbe mukusokonechela kwakuonechela.”');
INSERT INTO "silika" VALUES(36,25,'36:25','“Chisimu une nankulupilile M’mbuje gwenu (Allah), basi mbikanilani.” (Ni nganampikanila nambo ŵam’buleje).');
INSERT INTO "silika" VALUES(36,26,'36:26','Yaŵechetedwe yanti: “Jinjilani ku Mbepo.” Jwalakwe jwatite: “E nabola ŵandu ŵangu angamanyilile,”');
INSERT INTO "silika" VALUES(36,27,'36:27','“Yatite kaangululuchile kakwe Ambuje ŵangu (Allah) ni kundenda kuŵa mwa ŵakuchimbichika!”');
INSERT INTO "silika" VALUES(36,28,'36:28','Soni nganitutulusya pa ŵandu ŵakwe panyuma pa jwalakwejo asilikali kuumila kwinani (ŵakwisa kwajonanga), soni nganikuŵa kwakusosekwa Kukwetu kuti mpaka tutulusye (asilikali).');
INSERT INTO "silika" VALUES(36,29,'36:29','Nganikuŵa (konanjidwa kwao) ikaŵeje ni n’gumiilo umpepe (uŵagumisile Jibulilu), basi papopo ŵaliji ŵakulita (ŵawile).');
INSERT INTO "silika" VALUES(36,30,'36:30','Ha! Yakudandaulisya kwa achikapolo (ŵa Allah)! Nganiŵaichilileje ntenga jwalijose, ikaŵeje ŵaliji nkuntenda chipongwe.');
INSERT INTO "silika" VALUES(36,31,'36:31','Ana ngakulola (yanti) mikutula jilingwa jatujijonasile paujo pao? (Ana ngakulola) yanti ŵanganyao ngaakuujila kukwao?');
INSERT INTO "silika" VALUES(36,32,'36:32','Soni pangali (iwumbe) iliyose ikaŵeje yosope yene ichiyika pameso Petu.');
INSERT INTO "silika" VALUES(36,33,'36:33','Soni chilosyo (chachili) kwa ŵanganyao (chakulosya ukombosi wa Allah wakwajimusya ŵawe) ni litaka lyaliwe, tukasalisyusyaga, ni koposyamo songolo, basi sine mu syalakwesyo akasalyaga.');
INSERT INTO "silika" VALUES(36,34,'36:34','Ni tukasaŵikaga mwalakwemo migunda ja mitende ni mizabibu, ni tukasapitisyaga soni mwalakwemo achitutumule.');
INSERT INTO "silika" VALUES(36,35,'36:35','Kuti alyeje isogosi yakwe, kutendaga nganiyipanganyidwa ni makono gao, ana ngakutogolela?');
INSERT INTO "silika" VALUES(36,36,'36:36','Kuswejela ni Kwakwe Ajula (Allah) jwagumbile chachilume ni chachikongwe mu indu yosope, kutandila yaikumesya litaka, ni mwa achimisyene ŵakweo (ŵandu), ni mu ichindu yangakuimanyilila.');
INSERT INTO "silika" VALUES(36,37,'36:37','Soni chilosyo (chine chachili) kwa ŵanganyao (chakulosya ukombosi wa Allah) ni chilo, tukasawuwulaga mwalakwemo muusi, basi papopo ŵanganyao ni akasaŵaga m’chipi.');
INSERT INTO "silika" VALUES(36,38,'36:38','Sano lyuŵa likasajendaga mpaka pakutulalila pakwe. Walakweo ni mpango wa (Allah) Jwamachili gakupunda, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(36,39,'36:39','Nombe nao mwesi twaupimile mauto (makumi gaŵili kwisa nsano nagatatu), mpaka ukasaujaga kuŵa mpela chikole cha tende chakalakala (chepindiche).');
INSERT INTO "silika" VALUES(36,40,'36:40','Nganiyiŵa yakuŵajilwa lyuŵa kuwuyika mwesi, namuno chilo kuwulongolela muusi. Soni chilichose ching’ambila mwitala lyasyungulilo.');
INSERT INTO "silika" VALUES(36,41,'36:41','Soni chilosyo (chine chachili) kwa ŵanganyao (chakulosya ukombosi wa Allah) ni chanti: Uwwe twajigele achatati ŵao m’chombo chepachile (cha ndaŵi ja Nuhu).');
INSERT INTO "silika" VALUES(36,42,'36:42','Ni twaguumbiile ŵanganyao (yombo) yakulandana mpela chalakwecho yaakukwela.');
INSERT INTO "silika" VALUES(36,43,'36:43','Sano Uwwe naga tuli tusachile mpaka twamisye, ni pangali nkwakamuchisya ŵanganyao (jwampaka apikane gumila kwao), soni ŵanganyao ngaŵa nkokoledwa.');
INSERT INTO "silika" VALUES(36,44,'36:44','Ikaŵeje kwa chanasa chakuuma Kukwetu, ni kusengwa kwa ndema jannono.');
INSERT INTO "silika" VALUES(36,45,'36:45','Sano pati iŵechetedwe kukwao yanti: “Jogopani yaili paujo penu ni yaili panyuma penu kuti ntendeledwe chanasa;” (ngakasapikana).');
INSERT INTO "silika" VALUES(36,46,'36:46','Ni changaichilila chilosyo chilichose mu ilosyo ya M’mbuje gwao (Allah), ikaŵeje ŵanganyao akasaŵaga ŵakuitundumalila yalakweyo.');
INSERT INTO "silika" VALUES(36,47,'36:47','Soni pati iŵechetedwe kukwao yanti: “Tolani mu yampele Allah.” Aŵala ŵaakufuulu akasatiji pakwasalila (mwachipongwe) aŵala ŵaakulupilile: “Ana twalisye aŵala ŵanti Allah angasache angalisyisye (Nsyene)? Nganim’ba jenumanja ikaŵeje mu kusokonechela kwakuonechela.”');
INSERT INTO "silika" VALUES(36,48,'36:48','Ŵanganyao ni akasatiji: “Ana chele chilanga (chakwika Kiyamachi) chiyiche chakachi nam’baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(36,49,'36:49','Ngakwembecheya ikaŵeje n’gumilo umpepe (lipenga lyandanda) wauchakwembekanya (mwachisupuchisya) aku ali nkukangana.');
INSERT INTO "silika" VALUES(36,50,'36:50','Basi ngasakombola kuleka wasiya, namuno kumaŵasa kwao ngasaujila.');
INSERT INTO "silika" VALUES(36,51,'36:51','Ni lichigombedwa lipenga (lyaŵili), basi papopo ŵanganyao kuumila m’malembe tachikopokangana mwachitemachitema wa kwa M’mbuje gwao (Allah).');
INSERT INTO "silika" VALUES(36,52,'36:52','(Aku) achitiji: “Ipotesi yetuwe! Ana ŵani ŵaatujimwisye pagona petu?” (Tachisalilidwa yanti): “Ayi ni ayila yajwapeleche chilanga (Allah) Jwaukoto wejinji ni kuŵecheta mwakuona Achimitenga.”');
INSERT INTO "silika" VALUES(36,53,'36:53','Ngasiyiŵa ikaŵeje n’gumilo umpepe, basi ajile sayi wosopeŵene tachisimanikwa kuti ayiche pameso Petu.');
INSERT INTO "silika" VALUES(36,54,'36:54','(Ni tachisalilidwa yanti): “Basi lelo (lisiku lya Kiyama) ngatendedwa lupuso mundu jwalijose pa chilichose, soni ngan’dipidwa ikaŵeje yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(36,55,'36:55','Chisimu ŵandu ŵa ku Mbepo lelo taŵe mu shuguli (syao) achisengwaga.');
INSERT INTO "silika" VALUES(36,56,'36:56','Ŵanganyao ni achiŵammakwao taŵe m’miwulili, ali ajegeme mu indanda yetandiche chenene.');
INSERT INTO "silika" VALUES(36,57,'36:57','Akwete kupata ŵanganyao mwalakwemo isogosi (yantundu uliose), soni akwete kupata chilichose chaakuchisaka.');
INSERT INTO "silika" VALUES(36,58,'36:58','(Tachisalilidwa yanti): “Salâm (Ntendele uŵe kwa jaomanja!)” – Liloŵe lyakuumila kwa (Allah) M’mbuje Jwachanasa channope.');
INSERT INTO "silika" VALUES(36,59,'36:59','Sano (ŵangalumbana ni tachisalilidwa kuti): “Litukusyani lelo jenumanja akuleŵa (ku ŵakulupilila)!”');
INSERT INTO "silika" VALUES(36,60,'36:60','“Ana nganinan’jamuka, E Jenumanja ŵanache ŵa Adam! Yanti ngasimun’galagatilaga shetani, chisimu jwalakwejo kukwenu juŵele mmagongo jwakuonechela?”');
INSERT INTO "silika" VALUES(36,61,'36:61','“Ni kuti mungalagatileje Une (Jikape), lyalakweli ni litala lyagoloka?”');
INSERT INTO "silika" VALUES(36,62,'36:62','“Soni chisimu pamasile pajwaisochelesye mu jenumanja iwumbe yejinji, ana nganim’baga ŵakola lunda?”');
INSERT INTO "silika" VALUES(36,63,'36:63','“Aji ni Jahannama ajila jamwaliji nkupedwa chilanga.”');
INSERT INTO "silika" VALUES(36,64,'36:64','“Jijinjilani lelo, ligongo lya ukafili wamwaliji nkutenda.”');
INSERT INTO "silika" VALUES(36,65,'36:65','Lelo chitusiŵe ngamwa syao, ni gatuŵechetekasye makono gao, ni kuŵichila umboni ngongolo syao pa ayila yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(36,66,'36:66','Sano tungasache chisimu tungafufwite meso gao, mwanti angalimbanilene litala, nambo angalolite chantiuli?');
INSERT INTO "silika" VALUES(36,67,'36:67','Soni tungasache tungasyusisye (kuŵa inyama kapena maganga) pamalo pao pepo, ni nganganakombola kuja m’bujo namuno kuwujila munyuma.');
INSERT INTO "silika" VALUES(36,68,'36:68','Soni jwatukumpaga umi (welewu) tukum’buchisyaga munyuma mukagumbidwe (kakwe, ni jukasaŵa nti chisawu mwanache). Ana ngakuitagaga mulunda (yalakweyi)?');
INSERT INTO "silika" VALUES(36,69,'36:69','Soni nganitun’jiganya jwalakwe (Muhammadi صلى الله عليه وسلم) kwimba ilaanga, nambo soni ngaikuŵajilwa kukwakwe (kuŵa nkutunga ilaanga). Nganiyiŵatu ayi (yayiche nayo Muhammadi صلى الله عليه وسلم) ikaŵeje chikumbusyo kwisa soni Qur’an jakusalichisya (chilichose).');
INSERT INTO "silika" VALUES(36,70,'36:70','Kuti antetele jwali chijumi (jwakulupila), ni lisimichiche liloŵe (lya ilagasyo) pa achimakafili.');
INSERT INTO "silika" VALUES(36,71,'36:71','Ana ngakuiona ŵanganyao yanti Uwwe twaguumbiile ilaango, mu ayila yaipanganyisye makono Getu, basi ŵanganyao ni akuŵa ali nkuitenda kuŵa yao.');
INSERT INTO "silika" VALUES(36,72,'36:72','Ni twajepepachisye yalakweyo ŵanganyao, basi ine mu yalakweyo iŵele yakwela yao, soni ine mu yalakweyo akasailyaga.');
INSERT INTO "silika" VALUES(36,73,'36:73','Soni akwete ŵanganyao mwalakwemo yakamuchisya (ine yejinji), kwisa soni yakumwa (mpela nkaka). Ana ngakutogolela?');
INSERT INTO "silika" VALUES(36,74,'36:74','Ni alitendele ŵanganyao milungu (jaunami) kunneka Allah kuti mwine ŵanganyao takamuchisyidwe (ni jalakwejo).');
INSERT INTO "silika" VALUES(36,75,'36:75','(Nambo) nganijiŵa jikombwele (milungujo) kwakamuchisya ŵanganyao, soni jalakwejo jichisonganganyisyidwa ku ŵanganyao (ndema jakwaŵalanga itendo yao) kuŵa asilikali (ŵakwachonjela).');
INSERT INTO "silika" VALUES(36,76,'36:76','Basi ikan’dandaulikasya mmwe (Muhammadi صلى الله عليه وسلم) yakuŵecheta yao. Chisimu Uwwe tukuimanya yaakusisa ni yaakulosya.');
INSERT INTO "silika" VALUES(36,77,'36:77','Ana mundu ngajukuiona yanti Uwwe tun’gumbile kuumila mwindondwa lya mesi (ga ŵalume ni ŵakongwe), yeti kaneko jwalakwe ni jukuŵaga mmakaani jwakuonechela (jwakunkanila Allah).');
INSERT INTO "silika" VALUES(36,78,'36:78','Ni akutupa Uwwe chilandanyo, ni kuliŵalila kagumbidwe kakwe (kakuumila m’mesi gangali mate). Akutiji: “Ŵani ŵatachigapa umi maupa kutendaga gali gepukuswepukuswe?”');
INSERT INTO "silika" VALUES(36,79,'36:79','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Tachigapa umi Ajula juŵagagumbile kandanda, soni Jwalakwe chiumbe chilichose juŵele Jwakuchimanya chenene.”');
INSERT INTO "silika" VALUES(36,80,'36:80','Ajula jwam’biichiile jenumanja moto m’chitela cheŵisi, basi jenumanja ni nkasakolesyaga (motowo) kuumila muchalakwecho.');
INSERT INTO "silika" VALUES(36,81,'36:81','Ana nganaŵa Ajula (Allah) juŵagumbile mawunde ni mataka jwakombola kuti ŵagumbe (soni ŵandu) ŵanti mpela ŵanganyao? Elo (juŵele Jwakombola kusyene), soni Jwalakwe ni Nkugumba kusyene, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(36,82,'36:82','Chisimu chilamusi Chakwe pati asachile chindu (kuti chipatikane), agambaga kuchisalila kuti: “M’be.” Basi ni chikasaŵaga.');
INSERT INTO "silika" VALUES(36,83,'36:83','Basi kuswejela ni Kwakwe Ajula jwamuli mu Nkono Mwakwe mwana uchimwene wakuchilamulila chindu chilichose, soni Kukwakwe tinchiuchisyidwa (wosope ŵene).');
INSERT INTO "silika" VALUES(37,1,'37:1','Ngwalumbila aŵala (Achimalaika) ŵaakwima m’miselamisela (pakuswali).');
INSERT INTO "silika" VALUES(37,2,'37:2','Kwisa soni aŵala (Achimalaika) ŵaakasakalipilaga mwamachili (kugakalipila mawunde gawula pakugajendesya).');
INSERT INTO "silika" VALUES(37,3,'37:3','Kwisa soni aŵala (Achimalaika) ŵaakusoomaga chikumbusyo (chitabu pakwikanacho kwa Achimitume kuumila kwa Allah).');
INSERT INTO "silika" VALUES(37,4,'37:4','Chisimu Nnungu jwenu ni Jumpepe.');
INSERT INTO "silika" VALUES(37,5,'37:5','M’mbuje jwa kumawunde ni petaka ni yaili chilikati chakwe, soni M’mbuje jwa kungopoko kosope. ');
INSERT INTO "silika" VALUES(37,6,'37:6','Chisimu Uwwe tulisalalisye liwunde lya duniya ni isalalisyo yandondwa.');
INSERT INTO "silika" VALUES(37,7,'37:7','Ni kuligosa ku shetani jwalijose jwakunyosya.');
INSERT INTO "silika" VALUES(37,8,'37:8','Kuti akapikanila ya nkutula wa kwinani (wa Achimalaika), ni akasasomedwaga (ni yenje yandondwa) kuumila upande wa mbali ni mbali.');
INSERT INTO "silika" VALUES(37,9,'37:9','Kuŵinjidwa mwamachili, soni akwete kupata ŵanganyao ilagasyo yakupoteka nnope ni kwendelechela.');
INSERT INTO "silika" VALUES(37,10,'37:10','Ikaŵeje jwasombwele liloŵe mwawiyi, basi ni kunkuya chenje chakuŵala nnope.');
INSERT INTO "silika" VALUES(37,11,'37:11','Basi mwawusyani (achimakafilio kuti): “Ana ŵanganyao ni ŵali ŵakusausya nnope kagumbidwe, kapena ayila (ine mpela mawunde, mataka ni matumbi) yatwaigumbile?” Chisimu Uwwe twagumbile jamanjao kuumila mwilongo lyakunyata.');
INSERT INTO "silika" VALUES(37,12,'37:12','(Ngwamba), nambo mmwe (Muhammadi صلى الله عليه وسلم) nkusimonga (kanila kwao yakwimuka m’malembe), sano ŵanganyao ni akutenda chipongwe (kuntenda mmwe Muhammadi صلى الله عليه وسلم ni Qur’an).');
INSERT INTO "silika" VALUES(37,13,'37:13','Soni naga ali akumbusyidwe ŵangakumbuchila.');
INSERT INTO "silika" VALUES(37,14,'37:14','Soni naga ali achiweni chisimosimo akasachitenda chipongwe.');
INSERT INTO "silika" VALUES(37,15,'37:15','Ni akasatiji: “Nganichiŵatu achi ikaŵeje usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(37,16,'37:16','“Ana patuchiwapo ni kusyuka uwwejo litaka ni maupa, nambo yakuona kuti uwwe tuchiukulidwa (m’malembe)?”');
INSERT INTO "silika" VALUES(37,17,'37:17','“Mpaka ni achatati ŵetu ŵandanda (nombenao tachiukulidwa)?”');
INSERT INTO "silika" VALUES(37,18,'37:18','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Elo (tinchiukulidwa), soni jenumanja n’di ŵakunyosyeka.”');
INSERT INTO "silika" VALUES(37,19,'37:19','Basi chisimu kwalakwe (kuukulidwako) kuchiŵa ni n’gumilo umpepe (walipenga lyaŵili), basi papopo ŵanganyao tachiŵa ali nkulola.');
INSERT INTO "silika" VALUES(37,20,'37:20','Ni tachiti: “Ipotesi yetuwe! Ali ni Lisiku Lyamalipilo.”');
INSERT INTO "silika" VALUES(37,21,'37:21','(Tachisalilidwa yanti): “Ali ni Lisiku Lyachiilanyo alila lyamwaliji jenumanja n’di nkulikanila.”');
INSERT INTO "silika" VALUES(37,22,'37:22','(Achimalaika tachisalilidwa yanti): “Mwasonganganyani aŵala ŵaŵalitesile lupuso, pamo ni achimijao (ŵachijini), kwisa soni (isanamu) ayila yaŵaliji nkuigalagatila.”');
INSERT INTO "silika" VALUES(37,23,'37:23','“Kunneka Allah; basi mwajongolelani kwitala lya ku Moto Waukali.”');
INSERT INTO "silika" VALUES(37,24,'37:24','“Soni mwajimikani, chisimu ŵanganyao ali ŵakuwusyidwa.”');
INSERT INTO "silika" VALUES(37,25,'37:25','“Chipali chichi kukwenu, uli ngankukamusyangana?”');
INSERT INTO "silika" VALUES(37,26,'37:26','(Ngwamba), nambo ŵanganyao lyele lisikulyo tachiŵa ŵakulipeleka (kuchilamusi cha Allah).');
INSERT INTO "silika" VALUES(37,27,'37:27','Ni tachigalauchilana ŵane kwa ŵane achiwusyanaga.');
INSERT INTO "silika" VALUES(37,28,'37:28','(Ŵangalikombolela) tachiti (pakwadandaula achinkulungwa ŵao): “Chisimu ŵanganyammwe mwaliji nkutuyichilila chakun’dyo (mukayiche kanti mpela kambone, kwekula kwaliji kutuloŵesya)!”');
INSERT INTO "silika" VALUES(37,29,'37:29','(Achinkulungwa) tachiti: “(Ngwamba), nambo jenumanja nganim’ba ŵakulupilila.”');
INSERT INTO "silika" VALUES(37,30,'37:30','“Soni uwwe nganitukola machili galigose pa jenumanja (gakunkanganichisya ukafili), nambo kuti jenumanja mwaliji ŵandu ŵakusumba mpika (pa ukafili mpela uwwewe).”');
INSERT INTO "silika" VALUES(37,31,'37:31','“Basi liloŵe lya Ambuje ŵetu (Allah lyakupedwa ilagasyo) lisimichiche petuwe, chisimu uwwe chitupasye (ilagasyoyo).”');
INSERT INTO "silika" VALUES(37,32,'37:32','“Basi twansochelesye (ligongo lyanti nombe) uwwe chisimu chene twaliji ŵasochelele.”');
INSERT INTO "silika" VALUES(37,33,'37:33','Basi chisimu ŵanganyao lyele lisikulyo tachiŵa yalumo mu ilagasyo.');
INSERT INTO "silika" VALUES(37,34,'37:34','Chisimu Uwwe mwele ni mutukwatendelaga ŵakuleŵa.');
INSERT INTO "silika" VALUES(37,35,'37:35','Chisimu ŵanganyao ndema jakuŵechetedwa kukwao yanti: Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Allah; ŵaliji nkulikwesya.');
INSERT INTO "silika" VALUES(37,36,'37:36','Ni kuŵechetaga yanti: “Ana chisimu uwwe tuleche milungu jetu ligongo lya nkwimba ilaanga, jwamasoka?”');
INSERT INTO "silika" VALUES(37,37,'37:37','(Ngwamba), nambo juyiche ni yakuona, soni jwajitichisye Achimitenga (ŵaŵalongolele).');
INSERT INTO "silika" VALUES(37,38,'37:38','Chisimu ŵanganyammwe (achimakafili ŵa pa Maaka), timpasye ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(37,39,'37:39','Soni ngallipidwa ikaŵeje yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(37,40,'37:40','Ikaŵeje achikapolo ŵa Allah ŵakuswejesyedwa.');
INSERT INTO "silika" VALUES(37,41,'37:41','Ŵanganyao akwete kupata lisiki lyakumanyika.');
INSERT INTO "silika" VALUES(37,42,'37:42','Isogosi (yantundu uliose), soni ŵanyao takachimbichisyidwe.');
INSERT INTO "silika" VALUES(37,43,'37:43','Ku Matimbe Gachindimba (ku Mbepo).');
INSERT INTO "silika" VALUES(37,44,'37:44','Ali (atemi) pa itengu yauchimbichimbi mwakulolegana.');
INSERT INTO "silika" VALUES(37,45,'37:45','Tiikaŵeje ili nkwasyungula ipanda ya ukana ŵakulenjela.');
INSERT INTO "silika" VALUES(37,46,'37:46','Weswela wakulyolyopela kwa akumwa.');
INSERT INTO "silika" VALUES(37,47,'37:47','Mwangali mwele (mu ukanamo) yakupotekasya (ntwe namuno m’matumbo), nambo soni ŵanganyao ngasakolelwa nao.');
INSERT INTO "silika" VALUES(37,48,'37:48','Soni kukwao tikukaŵe kwana (achiŵammakwao) ŵakulola pejipi (ŵakwalola achalume ŵaope ŵangatenda meso mwamwamwa kwa achalume ŵane), achakulungwa meso mwakusalalila.');
INSERT INTO "silika" VALUES(37,49,'37:49','Gamba kuŵa (kaswejele kao) ŵanganyao nti chisawu mandanda gagasunjidwe (m’mapapiko).');
INSERT INTO "silika" VALUES(37,50,'37:50','Basi tachigalauchilana ŵane kwa ŵane achiwusyanaga.');
INSERT INTO "silika" VALUES(37,51,'37:51','Nkuŵecheta jumpepe mwa ŵanganyao tachiti: “Chisimu une nakwete n’jangu (kuduniya).”');
INSERT INTO "silika" VALUES(37,52,'37:52','“Juŵaliji nkuti: ‘Ana mmwe chisimu chene n’di mwa akwitichisya?’');
INSERT INTO "silika" VALUES(37,53,'37:53','‘(Yanti) patuchiwapo ni kusyuka litaka ni maupa, nambo yakuona uwwe tuchilipidwa (pa itendo yetu)?”');
INSERT INTO "silika" VALUES(37,54,'37:54','(Nkuŵecheta jula) tachiti: “Ana jenumanja chin’dingule (none ku Moto kuti mum’bone yajukuti pakulagasika?” Ŵanganyao tachiti: “Iyayi.”)');
INSERT INTO "silika" VALUES(37,55,'37:55','Basi jwalakwe ni juchilingula, basi ni tachim’bona (n’jakwejula) juli nkatikati mwa Moto Waukali.');
INSERT INTO "silika" VALUES(37,56,'37:56','Juchiti: “Ngunnumbila Allah! Mmwetu mwasigele panandi kuti munyonanje (ngampikanile).”');
INSERT INTO "silika" VALUES(37,57,'37:57','“Soni ungaŵaga ukoto wa Ambuje ŵangu (Allah), ningaliji mwa akwika nao (ku Moto).”');
INSERT INTO "silika" VALUES(37,58,'37:58','(Ŵandu ŵa ku Mbepo tachiti): “Ana uwweji nikuti ngatuwa (soni)?”');
INSERT INTO "silika" VALUES(37,59,'37:59','“Ikaŵeje chiwa chetu chandandapechila, soni uwwe ngatulagasyidwa?”');
INSERT INTO "silika" VALUES(37,60,'37:60','Chisimu kwalakweku ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(37,61,'37:61','Pa (kusaka kupata) yanti mpela ayi, basi apanganye masengo (gambone) akupanganya.');
INSERT INTO "silika" VALUES(37,62,'37:62','Ana yalakweyi ni mbiko jambone, kapena chitela cha Zakkum?');
INSERT INTO "silika" VALUES(37,63,'37:63','Chisimu Uwwe tuchitesile chele (chitelacho) kuŵa mayeso ku ŵalupuso (pakuŵecheta ŵanganyao kuti: “Mpaka ikomboleche uli chitela kumela m’moto)?”');
INSERT INTO "silika" VALUES(37,64,'37:64','Chisimu chalakwecho ni chitela chachikopoka kuumila pasi pasyesyene pa Moto Waukali.');
INSERT INTO "silika" VALUES(37,65,'37:65','Soso sya isogosi yakwe (siŵele syakunyalaya nnope) mpela mitwe ja mashetani.');
INSERT INTO "silika" VALUES(37,66,'37:66','Basi chisimu ŵanganyao takalyeje chele (chitelacho), ni chalakwecho takagumbasyeje ndumbo syao.');
INSERT INTO "silika" VALUES(37,67,'37:67','Kaneko chisimu akwete kupata ŵanganyao kupwatika pa chele (chitelacho) chakuwanganyichisya gagali mesi gamasemule nnope.');
INSERT INTO "silika" VALUES(37,68,'37:68','Kaneko chisimu kwakuwujila kwao ni ku Moto Waukali.');
INSERT INTO "silika" VALUES(37,69,'37:69','Chisimu ŵanganyao ŵasimene achatati ŵao ali ŵapotele.');
INSERT INTO "silika" VALUES(37,70,'37:70','Sano ŵanganyao mu sajo syao akutugala (pakwakuya mwachitemachitema mwangali kusosa kaje umboni).');
INSERT INTO "silika" VALUES(37,71,'37:71','Soni chisimu ŵapotele paujo pao ŵajinji (mu ŵandu) ŵandanda.');
INSERT INTO "silika" VALUES(37,72,'37:72','Soni chisimu twatumisye mwa ŵanganyao akutetela.');
INSERT INTO "silika" VALUES(37,73,'37:73','Basi nnole mujaŵelele mbesi ja akuteteledwa.');
INSERT INTO "silika" VALUES(37,74,'37:74','Ikaŵeje achikapolo ŵa Allah ŵakuswejesyedwa.');
INSERT INTO "silika" VALUES(37,75,'37:75','Soni chisimu Nuhu jwatuŵilasile (kuti tun’jokole), basi ni Akwanga Ŵambone nnope (twaliji Uwwe).');
INSERT INTO "silika" VALUES(37,76,'37:76','Ni twan’jokwele jwalakwe ni ŵapeŵasa ŵakwe ku taabu jekulungwa.');
INSERT INTO "silika" VALUES(37,77,'37:77','Ni twatesile ŵanache ŵakwe kuŵa sigasiga (ŵaŵasigele ali nkuŵelekana paduniya).');
INSERT INTO "silika" VALUES(37,78,'37:78','Ni twannechele (mbili syambone) kwa (ŵandu wosope) ŵakwika munyuma.');
INSERT INTO "silika" VALUES(37,79,'37:79','Ntendele uŵe kwa Nuhu mu iwumbe yosope.');
INSERT INTO "silika" VALUES(37,80,'37:80','Chisimu Uwwe iyyoyopeyo ni yatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(37,81,'37:81','Chisimu jwalakwe (Nuhu) jwaliji mwa achikapolo Ŵetu ŵakulupilila.');
INSERT INTO "silika" VALUES(37,82,'37:82','Kaneko ni twamisyisye ŵane (wosope mwa ŵangakulupilila).');
INSERT INTO "silika" VALUES(37,83,'37:83','Soni chisimu mwa aŵala ŵaŵakuyiye litala lyakwe (lya Nuhu lya Tauhidi) jwaliji Ibrahima.');
INSERT INTO "silika" VALUES(37,84,'37:84','Ndema ja ŵaichilile Ambujegwe (Allah) ni ntima wambonembonepe.');
INSERT INTO "silika" VALUES(37,85,'37:85','Ndema ja jwatite kwa babagwe ni ŵandu ŵakwe: “Ana nkugalagatila chichi?”');
INSERT INTO "silika" VALUES(37,86,'37:86','“Ana mwaunaminamipe milungu jangaŵaga Allah ni jankujisaka?”');
INSERT INTO "silika" VALUES(37,87,'37:87','“Ana nganisyo syenu sili uli pa ya M’mbuje jwa iwumbe yosope?”');
INSERT INTO "silika" VALUES(37,88,'37:88','Kaneka ni jwalolite kulola (kwakuchetelela) ku ndondwa.');
INSERT INTO "silika" VALUES(37,89,'37:89','Ni jwatite: “Chisimu une jwakulwala.”');
INSERT INTO "silika" VALUES(37,90,'37:90','Basi ŵanganyao ŵantundumalile jwalakwe mwakunnochesya migongo.');
INSERT INTO "silika" VALUES(37,91,'37:91','Basi jwalakwe ni jwapite mwagwala ku milungu jao, ni jwatite: “Uli ngankulya (yakulya yam’bichileyi)?”');
INSERT INTO "silika" VALUES(37,92,'37:92','“Chipali chichi kukwenu, uli ngankuŵecheta?”');
INSERT INTO "silika" VALUES(37,93,'37:93','Kaneka ni jwajigalauchile pakujiputa ni nkono wakwe wan’dyo (kwamachili gakwe gosope).');
INSERT INTO "silika" VALUES(37,94,'37:94','Basi (paŵapikene achinsyene isanamuyo) ŵam’bichilile ali nkutugala.');
INSERT INTO "silika" VALUES(37,95,'37:95','(Jwalakwe) jwatite: “Ana nkuigalagatila yankuisepa (mwachimisyene)?”');
INSERT INTO "silika" VALUES(37,96,'37:96','“Kutendaga Allah ni juŵan’gumbile jemanjammwe ni yankupanganya!”');
INSERT INTO "silika" VALUES(37,97,'37:97','Ŵanganyao ŵatite: “Muntaŵilani chakutaŵa (chiwukuta) ni mumponye m’moto waukali.”');
INSERT INTO "silika" VALUES(37,98,'37:98','Basi ŵasachile kuntendela jwalakwe malindi, ni twatesile kuŵa ŵapasi kusyene.');
INSERT INTO "silika" VALUES(37,99,'37:99','(Ibrahima) ni jwatite: “Chisimu une ngwaula kwa Ambuje ŵangu (Allah), takanjongole.”');
INSERT INTO "silika" VALUES(37,100,'37:100','“Ambuje ŵangu! Mumbani (mwanache jwa) mu ŵandu ŵambone.”');
INSERT INTO "silika" VALUES(37,101,'37:101','Basi ni twansengwasyisye ni abali ja (kola) mwanache jwakulitimalika (jwali Ismaila).');
INSERT INTO "silika" VALUES(37,102,'37:102','Basi ndema jajwayiche (mwanachejo) pa nsingu wakwenda najo imo, (Ibrahima) jwatite: “E mwanangu! Chisimu une naiweni kulugono yanti une ngunsikita mmwe (kuŵa mbepesi jakupeleka kwa Allah), basi nnole (nganisyo syenu), nkuiona uli?” (Mwanache) jwatite: “E baba ŵangu! Tendani yannamulidwe, timusimane une nasakaga Allah ndili mwa akupilila.”');
INSERT INTO "silika" VALUES(37,103,'37:103','Basi ndema jaŵalipeleche ŵanaŵaŵili (ku chilamusi cha Allah), (Ibrahima) ni ŵan’goneche chamakuku (Ismaila kuti ansikite).');
INSERT INTO "silika" VALUES(37,104,'37:104','Uwwe ni twam’bilasile kuti: “E mmwe Ibrahima!”');
INSERT INTO "silika" VALUES(37,105,'37:105','“Chisimu pamasile pankwanilisye sagamisi.” Chisimu Uwwe iyyoyopeyo ni yatukasatiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(37,106,'37:106','Chisimu galakwega ni mayeso gakuonechela.');
INSERT INTO "silika" VALUES(37,107,'37:107','Ni twam’bombwele (nkusikitidwajo) ni chakusikitidwa chekulungwa (ngondolo).');
INSERT INTO "silika" VALUES(37,108,'37:108','Ni twannechele (mbili syambone) kwa (ŵandu wosope) ŵakwika munyuma.');
INSERT INTO "silika" VALUES(37,109,'37:109','Ntendele uŵe kwa Ibrahima.');
INSERT INTO "silika" VALUES(37,110,'37:110','Iyyoyopeyo ni yatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(37,111,'37:111','Chisimu jwalakwe jwaliji mwa achikapolo Ŵetu ŵakulupilila.');
INSERT INTO "silika" VALUES(37,112,'37:112','Ni twansengwasyisye ni abali ja (kum’beleka) Isihaka, Ntume jwa mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(37,113,'37:113','Ni twampele upile jwalakwe ni Isihaka jwakwe. Sano mu ŵanache ŵao mwana nkukolosya (itendo yakwe) ni nkulitenda lupuso nsyene mwakuonechela.');
INSERT INTO "silika" VALUES(37,114,'37:114','Soni chisimu chene twampele ukoto Musa ni Haruna.');
INSERT INTO "silika" VALUES(37,115,'37:115','Ni twajokwele ŵanaŵaŵilio ni ŵandu ŵao ku taabu jekulungwa.');
INSERT INTO "silika" VALUES(37,116,'37:116','Ni twakamuchisye, basi ni ŵaliji ŵanganyao ŵakupunda.');
INSERT INTO "silika" VALUES(37,117,'37:117','Ni twapele ŵanaŵaŵilio chitabu chakusalichisya.');
INSERT INTO "silika" VALUES(37,118,'37:118','Ni twajongolele kwitala lyagoloka.');
INSERT INTO "silika" VALUES(37,119,'37:119','Ni twalechele (mbili syambone) kwa (ŵandu wosope) ŵakwika munyuma.');
INSERT INTO "silika" VALUES(37,120,'37:120','Ntendele uŵe kwa Musa ni Haruna.');
INSERT INTO "silika" VALUES(37,121,'37:121','Chisimu Uwwe iyyoyopeyo ni yatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(37,122,'37:122','Chisimu ŵanaŵaŵilio ŵaliji mwa achikapolo Ŵetu ŵakulupilila.');
INSERT INTO "silika" VALUES(37,123,'37:123','Soni chisimu Iliyasa jwaliji mwa Achimitenga.');
INSERT INTO "silika" VALUES(37,124,'37:124','(Kumbuchilani) ndema jajwatite ku ŵandu ŵakwe: “Ana ngankogopa?”');
INSERT INTO "silika" VALUES(37,125,'37:125','“Ana nkum’bomba Ba’la ni kunneka jwali Jwambone nnope mwa akugumba,”');
INSERT INTO "silika" VALUES(37,126,'37:126','“Allah, M’mbuje gwenu nambo soni M’mbuje jwa achatati ŵenu ŵandanda?”');
INSERT INTO "silika" VALUES(37,127,'37:127','Basi ŵankaaniile, basi chisimu ŵanganyao tachiika nao (ku ilagasyo).');
INSERT INTO "silika" VALUES(37,128,'37:128','Ikaŵeje achikapolo ŵa Allah ŵakuswejesyedwa.');
INSERT INTO "silika" VALUES(37,129,'37:129','Ni twannechele (mbili syambone) kwa (ŵandu wosope) ŵakwika munyuma.');
INSERT INTO "silika" VALUES(37,130,'37:130','Ntendele uŵe kwa Ilyaasiin.');
INSERT INTO "silika" VALUES(37,131,'37:131','Chisimu Uwwe iyyoyopeyo ni yatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(37,132,'37:132','Chisimu jwalakwe jwaliji mwa achikapolo Ŵetu ŵakulupilila.');
INSERT INTO "silika" VALUES(37,133,'37:133','Soni chisimu Lutwi jwaliji mwa Achimitenga.');
INSERT INTO "silika" VALUES(37,134,'37:134','(Kumbuchilani) ndema jatwan’jokwele jwalakwe ni liŵasa lyakwe lyosopelyene.');
INSERT INTO "silika" VALUES(37,135,'37:135','Ikaŵeje kangalamba kachikongwe (ŵankwao Lutwi ŵaŵaliji) mwa akusigalila munyuma.');
INSERT INTO "silika" VALUES(37,136,'37:136','Kaneko ni twajonasile ŵaneo.');
INSERT INTO "silika" VALUES(37,137,'37:137','Soni chisimu ŵanganyammwe nkwapitaga ŵanganyao (misi jao) kundaŵi.');
INSERT INTO "silika" VALUES(37,138,'37:138','Ni chilo chakwe, ana ŵangali lunda?');
INSERT INTO "silika" VALUES(37,139,'37:139','Soni chisimu Yunusu jwaliji mwa Achimitenga.');
INSERT INTO "silika" VALUES(37,140,'37:140','(Kumbuchilani) ndema ja jwatilile m’chombo chepachile.');
INSERT INTO "silika" VALUES(37,141,'37:141','Basi ni ŵatesile ndoto (kuti ŵatijagwile ndotojo aponyedwe m’mesi), basi ni jwaliji (Yunusu) mwa akwagwila (ndoto).');
INSERT INTO "silika" VALUES(37,142,'37:142','Basi ni chammisile chinsomba aku juli jwagambidwa.');
INSERT INTO "silika" VALUES(37,143,'37:143','Basi ingaŵa kuti jwalakwe jwaliji mwa akunswejesya (Allah).');
INSERT INTO "silika" VALUES(37,144,'37:144','Chisimu jungatemi m’matumbo mwakwe kwikanila lisiku lyakuwukulidwa (ŵandu m’malembe).');
INSERT INTO "silika" VALUES(37,145,'37:145','Basi twamponyisye munjengwe jambembembe juli jwakulwala.');
INSERT INTO "silika" VALUES(37,146,'37:146','Ni twammechesye chitela cha mongumbondu (mbonda syekulungwa syati mpela mongu). ');
INSERT INTO "silika" VALUES(37,147,'37:147','Ni twantumisye ku (ŵandu) masausande gakwana lichila (100,000) nambo soni ŵakupunda pele.');
INSERT INTO "silika" VALUES(37,148,'37:148','Ni ŵakulupilile, basi ni twasengwasyisye kwikanila ndema (jao jakuwila).');
INSERT INTO "silika" VALUES(37,149,'37:149','Basi mwawusyani (achimakafili ŵa pa Maaka mmwe Muhammadi صلى الله عليه وسلم kuti): “Ana Ambuje ŵenu ni akwete ŵanache ŵachikongwe, sano ŵanganyao ni akwete ŵanache ŵachilume?”');
INSERT INTO "silika" VALUES(37,150,'37:150','“Kapena twagumbile Achimalaika kuŵa achakongwe aku ŵanganyao achitendelaga umboni?”');
INSERT INTO "silika" VALUES(37,151,'37:151','Manyililani yanti, ŵanganyao ni unami ŵao akuŵechetaga yanti:');
INSERT INTO "silika" VALUES(37,152,'37:152','“Allah aŵeleche (ŵanache ŵachikongwe ŵali Achimalaika).” Soni chisimu ŵanganyao ŵaunami kusyene.');
INSERT INTO "silika" VALUES(37,153,'37:153','Ana (Allah) jusagwile ŵanache ŵachikongwe kwaleka ŵachilume?');
INSERT INTO "silika" VALUES(37,154,'37:154','Chipali chichi kukwenu, ana nkulamula chinauli?');
INSERT INTO "silika" VALUES(37,155,'37:155','Ana ngankukumbuchila (yanti Allah juŵambalene nayo yakola mwanache)?');
INSERT INTO "silika" VALUES(37,156,'37:156','Kapena jenumanja nkwete umboni wakuonechela (wa yankuŵechetayi)?');
INSERT INTO "silika" VALUES(37,157,'37:157','Basi ikani nacho chitabu chenu nam’baga jenumanja nkuŵecheta yakuona.');
INSERT INTO "silika" VALUES(37,158,'37:158','Ni asepelele ŵanganyao ulongo chilikati cha Jwalakwe (Allah) ni majini. Nambo majini ni gamanyiliile kusyene yanti chisimu ŵanganyao (achimakafilio) tachija kuonechela (kwa Allah kuti akajiilanye magambo ga unami wao).');
INSERT INTO "silika" VALUES(37,159,'37:159','Allah aŵambalene nayo kusyene yaakusimba ŵanganyao.');
INSERT INTO "silika" VALUES(37,160,'37:160','Ikaŵeje achikapolo ŵa Allah ŵakuswejesyedwa (ŵanganyao ni ŵaakunswejesya Allah ku yaakunnambuchisya achimakafili).');
INSERT INTO "silika" VALUES(37,161,'37:161','Sano chisimu jenumanja (achimakafili) ni yankuigalagatila (isanamu),');
INSERT INTO "silika" VALUES(37,162,'37:162','Nganim’ba jenumanja ŵanti ni kunsochelesyanayo (jwalijose mu ŵakulupilila kuti anneche Allah).');
INSERT INTO "silika" VALUES(37,163,'37:163','Ikaŵeje ajula jwatakajinjile ku Moto Waukali.');
INSERT INTO "silika" VALUES(37,164,'37:164','Soni pangali jwalijose mwetuwe (Achimalaika) ikaŵeje jukwete malo gakumanyika (kwinani gakutendela ibada).');
INSERT INTO "silika" VALUES(37,165,'37:165','Soni chisimu uwwe (Achimalaika) tukwimaga m’misela (pakuswali).');
INSERT INTO "silika" VALUES(37,166,'37:166','Soni chisimu uwwe (Achimalaika) tukunswejesyaga (Allah pa kuswali).');
INSERT INTO "silika" VALUES(37,167,'37:167','Soni chisimu ŵanganyaŵa (achimakafili ŵacharabu) ŵatiji:');
INSERT INTO "silika" VALUES(37,168,'37:168','“Ingaŵe twakwete uwwe chikumbusyo muŵakolele ŵandu ŵandanda,”');
INSERT INTO "silika" VALUES(37,169,'37:169','“Chisimu tungaliji achikapolo ŵa Allah ŵakuswejesyedwa.”');
INSERT INTO "silika" VALUES(37,170,'37:170','Basi (pachaichilile chikumbusyo) ŵachikanile, basi tachiyika kumanyilila (yatinjatenda).');
INSERT INTO "silika" VALUES(37,171,'37:171','Soni chisimu chene pamasile pa lyalongolele liloŵe Lyetu kwa achikapolo Ŵetu Achimitenga.');
INSERT INTO "silika" VALUES(37,172,'37:172','(Lyanti) chisimu ŵanganyao ni ŵatakamuchisyidwe.');
INSERT INTO "silika" VALUES(37,173,'37:173','Nambo soni kuti asilikali Ŵetu chisimu ni ŵataŵe ŵakupunda.');
INSERT INTO "silika" VALUES(37,174,'37:174','Basi mwatundumalilani kwa kandaŵi kannono.');
INSERT INTO "silika" VALUES(37,175,'37:175','Soni gambagani kwalola; basi sampano taione.');
INSERT INTO "silika" VALUES(37,176,'37:176','Ana akuijanguya ilagasyo Yetu?');
INSERT INTO "silika" VALUES(37,177,'37:177','Basi (ilagasyo) patiichituluchila paluŵala pao (tachijonanjidwa), ni kuchiŵa kundaŵi kwakusakala nnope kwa akuteteledwa.');
INSERT INTO "silika" VALUES(37,178,'37:178','Soni mwatundumalilani kwa kandaŵi kannono.');
INSERT INTO "silika" VALUES(37,179,'37:179','Soni gambagani kwalola; basi sampano taione.');
INSERT INTO "silika" VALUES(37,180,'37:180','Aŵambalene kusyene Ambuje ŵenu (Allah), Ambuje ŵamachili gakupunda ku yaakwasimba nayo (achimakafili).');
INSERT INTO "silika" VALUES(37,181,'37:181','Soni ntendele uŵe kwa Achimitenga.');
INSERT INTO "silika" VALUES(37,182,'37:182','Soni lumbili losope lwene alapikwe Allah M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(38,1,'38:1','Swâd, ngujilumbila Qur’an jajikwete chikumbusyo.');
INSERT INTO "silika" VALUES(38,2,'38:2','Nambo aŵala ŵaakufuulu ali nkulikwesya ni kusisya.');
INSERT INTO "silika" VALUES(38,3,'38:3','Ana mikutula jilingwa jatwajijonasile paujo pao! Ni ŵagumisile, kutendaga ndema jakokochela jili jipitene.');
INSERT INTO "silika" VALUES(38,4,'38:4','Ni akusimonga ya kwaichilila nkutetela kuumila mwa jamanjao, achimakafili ni akutiji: “Aju ni nsaŵi, jwaunami kusyene.”');
INSERT INTO "silika" VALUES(38,5,'38:5','“Ana jujitesile milungu (josope) kuŵa nnungu jumo! Chisimu chalakwechi ni chindu chakusimosya.”');
INSERT INTO "silika" VALUES(38,6,'38:6','Ni ŵaŵigwile achinkulungwa mwa ŵanganyao (kupita nchigombelesya yanti): “Jendelechelani (pa dini jenu), soni jipililililani milungu jenu (pa kujigalagatila), chisimu chalakwechi ni chindu (chakusakala) chachikusachikwa (kukwetu, chakuti tuleche dini jetu).”');
INSERT INTO "silika" VALUES(38,7,'38:7','“Nganitupikaneje yalakweyi (yakuŵa nnungu jumo namuno) mu dini jambesiji (ja chikilisito), nganiyiŵatu ayi ikaŵeje unami.”');
INSERT INTO "silika" VALUES(38,8,'38:8','“Ana pa chilikati petu chikumbusyo ni chitulusyiidwe kukwakwe (pe)?” Nambo ŵanganyao ali mu kaichila ya chikumbusyo Changu, nambo nganapasye ilagasyo (Yangu).');
INSERT INTO "silika" VALUES(38,9,'38:9','Kapena akwete ŵanganyao imatiilo ya ukoto ŵa Ambuje ŵenu (Allah) Ŵamachili gakupunda, Ŵakupeleka kwannope?');
INSERT INTO "silika" VALUES(38,10,'38:10','Kapena akwete ŵanganyao uchimwene wa kumawunde ni petaka ni yaili chilikati chakwe? Basi (naga ili iyyo), akwele m’makwelelo (kuja kwinani).');
INSERT INTO "silika" VALUES(38,11,'38:11','(Aga makafiliga pamo ni kulikwesya kwao) kaŵele kankutula kangali machili mwanti tikakomboledwe yagatite pakomboledwa makuga (ga makafili) kalakala (gagakanilaga utenga wa Achimitume.)');
INSERT INTO "silika" VALUES(38,12,'38:12','Ŵakaaniile paujo pao ŵandu ŵa Nuhu, Adi ni Firiauna nsyene ichili (yakulimbanganyichisya uchimwene wakwe; ŵali asilikali).');
INSERT INTO "silika" VALUES(38,13,'38:13','Kwisa soni Samuda, ŵandu ŵa Lutwi ni akutama nyyitekete (ŵandu ŵa Shuaibu). Jalakwejo ni mikutula (ja makafili jatwajijonasile).');
INSERT INTO "silika" VALUES(38,14,'38:14','Pangali jwalijose mwa jamanjao ikaŵeje jwakaaniile Achimitenga, basi ni yasimichiche ipotesi Yangu.');
INSERT INTO "silika" VALUES(38,15,'38:15','Ni ngakwembecheya ŵanganyaŵa ikaŵeje n’gumilo umpepe (wa lipenga lya Kiyama) wangali kuusyaausya.');
INSERT INTO "silika" VALUES(38,16,'38:16','Ŵanganyao ni akutiji (mwachipongwe): “Ambuje ŵetu! Tupani mwachitema chipande chetu (cha malipilo ga masengo getu gambone ni gakusakala) nkaniliyiche Lisiku Lyachiŵalanjilo.”');
INSERT INTO "silika" VALUES(38,17,'38:17','Pililani (mmwe Muhammadi صلى الله عليه وسلم) pa yaakuŵecheta, ni munkumbuchile kapolo Jwetu Daudi nsyene machili (gakwendechesya utume ni uchimwene), chisimu jwalakwe jwaliji jwakuujilaujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(38,18,'38:18','Chisimu Uwwe twagajepepesye matumbi kuti gaswejesyeje (Lumbili Lwetu) pamo ni jwalakwe (Daudi) kwigulo ni kundaŵi.');
INSERT INTO "silika" VALUES(38,19,'38:19','Nombe nayo ijuni (yaliji nkuswejesya najo) ili isonganganyisyidwe, yosope yene (matumbi ni ijuni) yaliji yakumpikanila (Daudi).');
INSERT INTO "silika" VALUES(38,20,'38:20','Ni twaulimbangenye uchimwene wakwe, ni twampele lunda lwakusokoka nambo soni kwilanya chenene magambo (ni chine chilichose).');
INSERT INTO "silika" VALUES(38,21,'38:21','Ana jinyyiche abali ja akukangana ndema ja ŵakwesile lipupa mpaka kwinjila ku chigunguli (chakuswalila Daudi)?');
INSERT INTO "silika" VALUES(38,22,'38:22','Ndema ja ŵan’jinjilile Daudi, basi jwalakwe ni ŵajogwepe ŵanganyao. Ŵanganyao ŵatite: “Nkajogopa, (uwwe ni) akukangana ŵaŵili, jumo jwetu ankotwesye n’jakwe, basi jilanyani chilikati chetu mwachilungamiko ni nkajimila mbali, soni tujongolelani kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(38,23,'38:23','“Chisimu aju nnongo n’jangu; jukwete ngondolo syekongwe makumi nsano nancheche kwisa nsano nancheche (99), sano une ni ngwete ngondolo jekongwe jimope, sano ni jukuti: ‘Mumbani jimojo nansunjile.’ (Sano une nansyene ni ngangusaka), ni jumbundile pa kaŵechete.”');
INSERT INTO "silika" VALUES(38,24,'38:24','(Daudi) jwatite: “Chisimu antesile lupuso pakuŵenda ngondolo jenu jekongwe kuti ajiwanganye ku ngondolo syakwe syekongwe, chisimu ŵajinji mwa akuwanganya nao ichindu akasakotosyanaga ŵane ni ŵane, ikaŵeje aŵala ŵaakulupilile ni kutendaga yambone, soni ŵanti iyyoyowo nganatupa.” Ni jwasimichisye Daudi kuti chisimu tun’dinjile mayeso, basi ni jwaaŵendile chikululuko Ambujegwe (Allah), ni jwasenjelemukwiche kwinama (kuswali) ni jwaujile (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(38,25,'38:25','Basi twankululuchile yalakweyo, soni chisimu jwalakwe jukwete Kukwetu uchimbichimbi kwisa soni malo gambone nnope gakuujila (ku Mbepo).');
INSERT INTO "silika" VALUES(38,26,'38:26','“E mmwe Daudi! Chisimu Uwwe tuntesile mmwe kuŵa ŵakulolela pachilambo, basi jilanyani chilikati cha ŵandu mwa chilungamiko, soni ngasinkuya isako (yantima wenu) itakwisa mwainsokonesye kuleka litala lya Allah. Chisimu aŵala ŵaakusokonechela kuleka litala lya Allah, akwete kupata ŵanganyao ilagasyo yaukali nnope ligongo lyakuliŵalila kwao Lisiku Lyachiŵalanjilo.”');
INSERT INTO "silika" VALUES(38,27,'38:27','Ni nganitugumba mawunde ni mataka ni yaili chilikati chakwe mwamasanje. Syele ni nganisyo sya aŵala ŵaakufuulu. Basi ipotesi yakuumila ku Moto takaipate aŵala ŵaakufuulu.');
INSERT INTO "silika" VALUES(38,28,'38:28','Ana twatende aŵala ŵaakulupilile ni kutendaga yambone nti chisawu akuwatanganya pachilambo? Kapena twatende ŵawoga (wakun’jogopa Allah) nti chisawu akuleŵa?');
INSERT INTO "silika" VALUES(38,29,'38:29','(Achi ni) Chitabu (cha Qur’an) chatuchitulwisye kukwenu (mmwe Muhammadi صلى الله عليه وسلم) chaupile wejinji kuti agachetelele (ŵandu) ma Ȃya gakwe ni kuti ŵakwete lunda akumbuchile.');
INSERT INTO "silika" VALUES(38,30,'38:30','Ni twampele Daudi (mwanache lina lyakwe) Sulaimana. Kaje umbone nnope kapolo jwakwe! Chisimu jwalakwe (Sulaimana) jwaliji jwakuujilaujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(38,31,'38:31','(Kumbuchilani) ndema ja syaonesyedwe kukwakwe kwigulo hachi syakutulala nnope pati sijimi, syakuutuka nnope.');
INSERT INTO "silika" VALUES(38,32,'38:32','Basi jwatite: “Chisimu une nainonyele nnope yambone (hachi), kwakuleka kwakumbuchila Ambuje ŵangu (Allah pangaswali swala ja Asr) mpakana (lyuŵa) lisiŵiliche mu chakusiŵilila (pakutiŵila).”');
INSERT INTO "silika" VALUES(38,33,'38:33','“Siuchisyani kukwangu (hachisyo).” Basi ni jwatandite kusipukuta sonjo ni ngosi.');
INSERT INTO "silika" VALUES(38,34,'38:34','Soni chisimu twan’dinjile Sulaimana mayeso, ni twaponyisye pa chitengu chakwe (cha uchimwene) chiŵiliŵili (lijini lyalyasumwile uchimwene wakwe kwa kandaŵi), kaneka ni jwaujile (ku uchimwene wakwe).');
INSERT INTO "silika" VALUES(38,35,'38:35','Jwalakwe jwatite: “Ambuje ŵangu! Mungululuchilani, ni mumbe uchimwene ŵangam’bajila jwalijose panyuma pangune, chisimu Mmwejo ni ŵan’di Ŵakupeleka kwannope.”');
INSERT INTO "silika" VALUES(38,36,'38:36','Basi twan’jepepachisye mbungo jajajendaga mwambolembole mwa lilalumusi lyakwe (kuja) kulikose kwajukusaka.');
INSERT INTO "silika" VALUES(38,37,'38:37','Ni (twan’jepepachisye soni) mashetani, ŵaliose ŵakutaŵa (yakutaŵataŵa) ni ŵakutiŵila (m’mbwani kukusosa ngalekale).');
INSERT INTO "silika" VALUES(38,38,'38:38','Sano ŵane ŵaliji ŵagamba kutaŵidwa m’maunyolo (ŵakunyosya malamusi gakwe).');
INSERT INTO "silika" VALUES(38,39,'38:39','(Allah ŵansalile kuti): “Awu ni ntuka Wetu (watumpele), basi tolani (kumpa jwankunsaka) kapena umbatani, mwangali chiŵalanjilo.”');
INSERT INTO "silika" VALUES(38,40,'38:40','Soni chisimu jwalakwe jukwete Kukwetu uchimbichimbi kwisa soni malo gambone nnope gakuujila (ku Mbepo).');
INSERT INTO "silika" VALUES(38,41,'38:41','Soni munkumbuchilani kapolo Jwetu Ayyubu, ndema ja jwaŵilasile Ambujegwe (Allah ni jwatite): “Chisimu une jungwayiye shetani ni masausyo kwisa soni ipotesi.”');
INSERT INTO "silika" VALUES(38,42,'38:42','(Allah ŵansalile kuti): “Putisyani lukongolo lwenu (petaka, tipakopoche mesi). Aga (mesi) gakoga gakusisima (basi jogani) nambo soni gakumwa (basi mwaani).”');
INSERT INTO "silika" VALUES(38,43,'38:43','Ni twampele ŵapeŵasa ŵakwe ni ŵane ŵanti mpela ŵawowo soni, kuŵa ukoto wakuuma Kukwetu, ni chikumbusyo ku ŵaakwete lunda.');
INSERT INTO "silika" VALUES(38,44,'38:44','“Soni jigalani munkono mwenu katenje (ka tutela twaŵanaŵana) ni mwapute nako (ŵankwenu pa yangalumbana yaŵatesile), ni nkawesesya kulumbila kwenu.” Chisimu Uwwe twansimene jwalakwe kuti jwaliji jwakupilila. Kaje umbone nnope kapolo jwakwe! Chisimu jwalakwe jwaliji jwakuujilaujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(38,45,'38:45','Soni mwakumbuchilani achikapolo Ŵetu Ibrahima, Isihaka ni Yaakubu achinsyene machili (gakwendechesya dini) ni kulola (kwaumanyilisi).');
INSERT INTO "silika" VALUES(38,46,'38:46','Chisimu Uwwe twasagwile ŵanganyao pakwapa ndamo jambone ja kumbuchila Nyumba Jambesi (ja Akhera).');
INSERT INTO "silika" VALUES(38,47,'38:47','Soni chisimu ŵanganyao Kukwetu ŵaliji ŵakusagulidwa soni ŵambone.');
INSERT INTO "silika" VALUES(38,48,'38:48','Soni munkumbuchilani Ismaila, Al-yasa‘a (Eliya) ni Dhul-Kifli (Yesaya), soni ŵosope ŵene ŵaliji mu ŵandu ŵambone.');
INSERT INTO "silika" VALUES(38,49,'38:49','Achi ni chikumbusyo. Sano chisimu ŵawoga (wakun’jogopa Allah) akwete kupata malo gakuujila gambone nnope.');
INSERT INTO "silika" VALUES(38,50,'38:50','Matimbe Gandamo, gatigakaŵe geugulidwe milango kwaugulila ŵanganyao.');
INSERT INTO "silika" VALUES(38,51,'38:51','Takaŵe ali ajegeme (mwakwegama) kwalakweko, achiŵilasyaga kwalakweko isogosi yejinji ni yakumwa.');
INSERT INTO "silika" VALUES(38,52,'38:52','Soni kukwao tikukaŵe kwana (achiŵammakwao) ŵakulola pejipi (ŵakwalola achalume ŵaope ŵangatenda meso mwamwamwa kwa achalume ŵane), ŵakulandana misingu.');
INSERT INTO "silika" VALUES(38,53,'38:53','Ayi ni yankupedwa chilanga ya Lisiku Lyachiŵalanjilo.');
INSERT INTO "silika" VALUES(38,54,'38:54','Chisimu ali ni lisiki Lyetu lyangamala.');
INSERT INTO "silika" VALUES(38,55,'38:55','Ayi (ichiŵa iyyo). Sano chisimu ŵakusumba mpika (pakunnyosya Allah) akwete kupata malo gakuujila gakusakala nnope.');
INSERT INTO "silika" VALUES(38,56,'38:56','Jahannama, takajijinjile. Basi kaje kusakala liuto lyagusama (lyalitandichile).');
INSERT INTO "silika" VALUES(38,57,'38:57','Ayi (ichiŵa iyyo). Basi agapasye ŵanganyao galakwe - mesi gamasemule nnope ni uwowu.');
INSERT INTO "silika" VALUES(38,58,'38:58','Ni (ipotesi) yine yanti chisawu iyyiyi yamitundumitundu.');
INSERT INTO "silika" VALUES(38,59,'38:59','(Tachisalilidwa akukuyidwa pakuponyedwa kumoto pamo ni akukuya): “Awu ni nkutula waukwinjila pamo ni jenumanja (ku Moto).” (Akukuyidwa tachiti): “Ŵangali pakwichila pambone ŵanganyao. Chisimu ŵanganyao tapye Moto.”');
INSERT INTO "silika" VALUES(38,60,'38:60','(Akukuya) tachiti: “Nambo jenumanja ni ŵankuŵajilwa ngakola pakwichila pambone, jenumanja ni ŵantutendekasyisye uwwe kupata ayi (ipotesiyi pakutusochelesya ku duniya), basi kaje kusakala malo gakutamilichika gakwe!”');
INSERT INTO "silika" VALUES(38,61,'38:61','Ŵanganyao takajile: “Ambuje ŵetu! Ŵatutendekasyisye uwwe kupata yalakweyi, basi mwajonjechesyani ilagasyo yakaŵili m’Moto.”');
INSERT INTO "silika" VALUES(38,62,'38:62','Ŵanganyao ni takajile: “Chipali chichi kukwetu, uli ngatukwaona achalume ŵatwaliji nkwaganichisya kuti ali mu ŵandu ŵakusakala (ligongo lyakunkuya Muhammadi صلى الله عليه وسلم)?”');
INSERT INTO "silika" VALUES(38,63,'38:63','“Ana twatesile chipongwe (ku duniya kutendaga ali ŵambone ŵakuŵajilwa kwinjila ku Mbepo), kapena gatekwakulusya meso (getu nambo tuli nao imo m’Moto muno)?”');
INSERT INTO "silika" VALUES(38,64,'38:64','Chisimu ayi ni yakuona yakangana ŵandu ŵa ku Moto.');
INSERT INTO "silika" VALUES(38,65,'38:65','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une ni nkutetela pe, soni pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Allah Jumpepe, Jwamachili gangalimbana nago.”');
INSERT INTO "silika" VALUES(38,66,'38:66','“M’mbuje jwa mawunde ni mataka ni yaili chilikati chakwe, Jwamachili gakupunda, Jwakululuka kusyene.”');
INSERT INTO "silika" VALUES(38,67,'38:67','Jilani: “Aji (Qur’an) jiŵele abali jekulungwa nnope.”');
INSERT INTO "silika" VALUES(38,68,'38:68','“Jankujitundumalila ŵanganyammwe.”');
INSERT INTO "silika" VALUES(38,69,'38:69','“Nganingola umanyilisi wakwayana ni nkutula wakwinani (Achimalaika) ndema jawaliji nkukangana (pa yagumbidwa kwa Adam).”');
INSERT INTO "silika" VALUES(38,70,'38:70','“Nganiyijuwulidwa kukwangu, ikaŵeje yanti une ni nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(38,71,'38:71','(Kumbuchilani) ndema jaŵaŵechete Ambuje ŵenu (Allah) kwa Achimalaika kuti: “Chisimu Une tingumbe mundu kuumila mwilongo.”');
INSERT INTO "silika" VALUES(38,72,'38:72','“Sano patimale kunkolosya ni kumpepelela nsimu wakuuma Kukwangu, basi munsenjelemuchile pakunsujudila.”');
INSERT INTO "silika" VALUES(38,73,'38:73','Basi ni ŵasujudu Achimalaika wosope ŵene nakamo.');
INSERT INTO "silika" VALUES(38,74,'38:74','Ikaŵeje Ibulisu, jwalikwesyisye ni jwaliji mwa makafili.');
INSERT INTO "silika" VALUES(38,75,'38:75','(Allah) jwatite: “E mmwe Ibulisu! Chichi chinnekasyisye kunsujudila ajula junan’gumbile ni Makono Gangu ganagaŵili? Ana n’dikwesyisye kapena n’di mwa ŵapenani kusyene?”');
INSERT INTO "silika" VALUES(38,76,'38:76','Jwalakwe jwatite: “Une ni jwambone nnope kumpunda jwalakweju, mwangumbile une kuumila m’moto, ni mun''gumbile jwalakweju kuumila mwilongo.');
INSERT INTO "silika" VALUES(38,77,'38:77','(Allah) jwatite: “Basi kopokani mwalakwemo, mwanti chisimu mmwe (sano) n’di ŵakuŵinjidwa.”');
INSERT INTO "silika" VALUES(38,78,'38:78','“Soni chisimu pachanya penu pana malweso Gangu mpaka Lisiku Lyamalipilo.”');
INSERT INTO "silika" VALUES(38,79,'38:79','Jwalakwe jwatite: “Ambuje ŵangu! Mumbanitu lipesa (lyakuti ngawa) mpaka lisiku lyatachiukulidwa (m’malembe achiiumbe wosope).”');
INSERT INTO "silika" VALUES(38,80,'38:80','(Allah) jwatite: “Basi chisimu mmwe n’di mwa akupedwa lipesa.”');
INSERT INTO "silika" VALUES(38,81,'38:81','“Kwikanila lisiku lya ndema jakumanyika (jagombedwa lipenga lyandanda, pele ni patinchiwila).”');
INSERT INTO "silika" VALUES(38,82,'38:82','Jwalakwe jwatite: “Ngugalumbila machili Genu gakupunda! Tinasokonesye kwene ŵanaose.”');
INSERT INTO "silika" VALUES(38,83,'38:83','“Pangaŵaga achikapolo Ŵenu ŵakuswejesyedwa mwa ŵanganyao.”');
INSERT INTO "silika" VALUES(38,84,'38:84','(Allah) jwatite: “Ngulumbila kusyene, soni yakuona nguŵecheta.”');
INSERT INTO "silika" VALUES(38,85,'38:85','“Tingajigumbasye kwene Jahannama ni mmwe pamo ni ŵataachinkuuya mwa ŵanganyao wosope ŵene.”');
INSERT INTO "silika" VALUES(38,86,'38:86','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ngangun’juga pa yalakweyi malipilo galigose, soni nganimba une mwa akulikanganichisya (kutenda yangaŵa nkuikombola).”');
INSERT INTO "silika" VALUES(38,87,'38:87','“Nganijiŵatu aji (Qur’an) ikaŵeje chikumbusyo ku iwumbe yosope.”');
INSERT INTO "silika" VALUES(38,88,'38:88','“Soni timmanyilile kwene (kalulila kwa) abali jakwe pangakaŵapa.”');
INSERT INTO "silika" VALUES(39,1,'39:1','Kutulusyidwa kwa Chitabu (Qur’an) kukuumila kwa Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(39,2,'39:2','Chisimu Uwwe tuntuluchisye (mmwe Muhammadi صلى الله عليه وسلم) Chitabu mwakuonaonape, basi mun’galagatilani Allah mwakunswejechesya dini Jakwe.');
INSERT INTO "silika" VALUES(39,3,'39:3','Manyililani yanti Allah akasapochelaga dini jeswela (jangawanganya ni isanamu). Sano aŵala ŵalitendele akwakamuchisya ŵanepe kunneka Jwalakwe (Allah, akasatiji): “Ngatukwagalagatila ŵanganyaŵa ikaŵeje kuti atuŵandichisye kwa Allah nnope.” Chisimu Allah tachijilanya chilikati chao pa ichindu yaŵaliji nkutindana pa yalakweyo. Chisimu Allah ŵangan’jongola mundu jwali jwaunami soni jwakufulu nnope.');
INSERT INTO "silika" VALUES(39,4,'39:4','Allah angasache kulitendela mwanache angasagwile mu ichindu yaagumbile chaakuchisaka. Kuswejela ni Kwakwe, Jwalakwe ni Allah, Jumpepe, Jwamachili gangalimbana nago.');
INSERT INTO "silika" VALUES(39,5,'39:5','Jwagumbile mawunde ni mataka mwakuonaonape, akasachiŵiligaga chilo m’muusi ni kuuŵiliga muusi m’chilo, ni alijepepesye lyuŵa ni mwesi, yosope yene ikwenda mpaka pandema jekolanjidwe, manyililani yanti Jwalakwe (Allah) ni Jwamachili gakupunda, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(39,6,'39:6','Ŵan’gumbile jenumanja kuumila mwa mundu jumpepe (Adam), ni ŵagumbile kuumila mwa jwalakwejo ŵankwakwe (Hawa), ni antuluchisye ŵanganyammwe kuumila mu ilango mitundu nsano najitatu (ngamiya, ng’ombe, ngondolo ni mbusi chilichose chelume ni chekongwe). Akasan’gumbaga jenumanja mu itumbo ya achimama ŵenu kagumbidwe kane panyuma pa kagumbidwe kane, mu ipi itatu. Jwalakwejotu ni Allah M’mbuje gwenu, Uchimwene ni Wakwe, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwalakwe, basi ana nkugalausyidwa chinauli (kuleka yakuona)?');
INSERT INTO "silika" VALUES(39,7,'39:7','Naga nkufulu, basi chisimu Allah ngaakunnajila jenumanja, soni ngaakuunonyela ukafili wa achikapolo Ŵakwe. Sano naga nkutogolela taisengwele yalakweyo kukwenu. Soni jwaatwiichiile nsigo (wasambi) ngasaja kutwichila nsigo wajwine, kaneka kwa M''mbuje gwenu (Allah) ni kwakuujila kwenu, basi ni tachintagulila ichindu yamwaliji nkupanganya. Chisimu Jwalakwe ni Nkumanyilila yaili m’mitima.');
INSERT INTO "silika" VALUES(39,8,'39:8','Sano naga mundu chili chinkwayiye chakusausya chilichose akasam’bendaga M’mbujegwe (Allah) mwakuujila Kukwakwe (pakutenda toba). Kaneko nkumpa ukoto wakuuma Kukwakwe, basi jukasaliŵalilaga ayila yajwaliji nkwaŵilanjila ku yalakweyo (Ambujegwe) kalakala (kuti antyochesye). Ni akum’bichilaga Allah milungu kuti ŵasokonesye (najo ŵandu) kuleka litala Lyakwe. Jilani: “Sengwagani ni ukafili wenuo panandipe, chisimu mmwejo n’di mu ŵandu ŵa ku Moto.”');
INSERT INTO "silika" VALUES(39,9,'39:9','Ana jwaakulinandiya (kwa Allah ni kuswaliji) nkati mwa chilo pa kusujudu ni kwima ali nkujijogopa Akhera, ni kwembecheya chanasa cha Ambujegwe (mpaka julandane ni jwakunyosya)? Jilani: “Ana mpaka alandane aŵala ŵaakumanyilila ni aŵala ŵangaakumanyilila?” Chisimu ŵaakwete lundape ni ŵaakukumbuchilaga.');
INSERT INTO "silika" VALUES(39,10,'39:10','Jilani: “E achikapolo Ŵangu aŵala ŵaakulupilile! Mun’jogopani M’mbuje gwenu (Allah). Akwete kupata yambone aŵala ŵaakolosyisye (itendo yao) pa duniya pano, sano chilambo cha Allah chiŵele chakusapanguka (naga nkulepela kutenda ibada upande wine, basi jawulani upande wine). Chisimu akupilila tachipedwa mwakwana malipilo gao mwangaŵalanjila.”');
INSERT INTO "silika" VALUES(39,11,'39:11','Jilani: “Chisimu une namulidwe kuti nan’galagatileje Allah mwakunswejechesya dini Jakwe.”');
INSERT INTO "silika" VALUES(39,12,'39:12','“Ni namulidwe soni kuti mbe jwandanda mwa ŵakulipeleka (pakunda Usilamu).”');
INSERT INTO "silika" VALUES(39,13,'39:13','Jilani: “Chisimu une ngogopa ilagasyo ya Lisiku Lyekulungwa naga naanyosye Ambuje ŵangu (Allah).”');
INSERT INTO "silika" VALUES(39,14,'39:14','Jilani: “Allah pe ni jungun’galagatila mwakunswejechesya dini jangu.”');
INSERT INTO "silika" VALUES(39,15,'39:15','“Basi jenumanja galagatilagani chilichose chanchisachile changaŵaga Jwalakwe.” Jilani: “Chisimu ŵakwasika ni aŵala ŵatachilijasikasya (Mbepo) achimisyene kwisa soni maŵasa gao lisiku lya Kiyama (ligongo lya itendo yao yangalumbana yaŵaliji nkutenda kuduniya), manyililani yanti kwalakweko ni kwasika kwakuonechela.”');
INSERT INTO "silika" VALUES(39,16,'39:16','Takakole ŵanganyao pachanya pao yakusajikanya ya moto, ni pasi pao yakusajikanya (ya moto). (Ni ilagasyo) yalakweyi Allah akwajogoyechesya nayo achikapolo Ŵakwe. “E achikapolo Ŵangu! Basi njogopani.”');
INSERT INTO "silika" VALUES(39,17,'39:17','Sano aŵala ŵaaliŵambesye ku shetani kuti akan’galagatila, ni galauchila kwa Allah (pakutenda toba), akwete kupata ŵanganyao abali jakusengwasya (ja kuja kwinjila ku Mbepo), basi mwapani abali jakusengwasya achikapolo Ŵangu.');
INSERT INTO "silika" VALUES(39,18,'39:18','Aŵala ŵaakupikanilaga maloŵe, ni kugakuyaga gagali gambone nnope. Ŵanganyao ni aŵala ŵaŵajongwele Allah, soni ŵanyao ni ŵaakwete lunda.');
INSERT INTO "silika" VALUES(39,19,'39:19','Ana ajula jwalisimichiche pa jwalakwejo liloŵe lya ilagasyo, ana mmwe mpaka mun’jokole jwali m’Moto?');
INSERT INTO "silika" VALUES(39,20,'39:20','Nambo aŵala ŵaakun’jogopa M’mbuje gwao (Allah), akwete kupata yumba yapenani yaitaŵidwe mwakusajikanya ine pachanya pa ijakwe, sulo sikajilimeje chapasi pakwe. (Achi ni) chilanga cha Allah, Allah jwangakasa chilanga.');
INSERT INTO "silika" VALUES(39,21,'39:21','Ana ngankuiona yanti Allah akasatulusyaga mesi (gawula) kuumila kwinani, ni akugajinjisyaga mu achitutumule nkati mwetaka, kaneka ni akasakopochesyaga nago mmela wakulekangana ntundu wakwe, kaneka ni ukasajumulaga ni nkuuwonaga uli wam’bilichila, kaneko ni akasautenda kuŵa wakuteketuka. Chisimu muyalakweyo muŵele mwana chikumbusyo ku ŵaakwete lunda.');
INSERT INTO "silika" VALUES(39,22,'39:22','Ana mundu jwansapangulile Allah ntima wakwe ku Usilamu, basi jwalakwe ni juli pelanguka lyakuumila kwa M’mbujegwe (mpaka julandane ni kafili)? Ipotesi ili kwa aŵala ŵajijumwile mitima jao pa yakunkumbuchila Allah, ŵanganyao ali mu kusokonechela kwakuonechela.');
INSERT INTO "silika" VALUES(39,23,'39:23','Allah atulwisye ngani jambone nnope (jajili) Chitabu (cha Qur’an) chakulandana-landana soni chakuwilisya-wilisya. Mapende ga aŵala ŵaakun’jogopaga M’mbuje gwao (Allah) gakasajilijitikaga najo, kaneka ni gakasatependalaga mapende gao ni mitima jao ku yakunkumbuchila Allah, chele ni chongoko cha Allah akasan’jongolaga nacho jwansachile. Sano jwaakunnechelelaga Allah kusokonechela, basi nganakola nkun’jongola.');
INSERT INTO "silika" VALUES(39,24,'39:24','Ana ajula jwatakaichijeje ni ngope jakwe ilagasyo yakusakala nnope lisiku lya Kiyama (takaŵe nti chisawu jwatakajinjile ku Mbepo)? Ni takasalilidwe ŵalupuso kuti: “Pasyani ayila yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(39,25,'39:25','Ŵaakaaniile aŵala ŵapaujo pao, basi ni yaichilile ilagasyo kuumila upande wanganaumanyilila.');
INSERT INTO "silika" VALUES(39,26,'39:26','Basi Allah ŵapele kupasya kwaluka pa umi waduniya, sano ilagasyo ya ku Akhera ni yaikulungwa nnope ingaŵe ŵamanyililaga.');
INSERT INTO "silika" VALUES(39,27,'39:27','Soni chisimu pamasile patupeleche ku ŵandu mu aji Qur’an chilandanyo chilichose kuti ŵanganyao akumbuchile.');
INSERT INTO "silika" VALUES(39,28,'39:28','Qur’an ja Charabu jangali kupombotala, kuti ŵanganyao aligose (ku ilemwa).');
INSERT INTO "silika" VALUES(39,29,'39:29','Allah apeleche chilandanyo cha mundu jwajukwete mabwana gakamusyangana gagakangana (mpela mundu jwagalagatila isanamu), ni mundu jwaagamba kuntumichila bwana jumpepe (mpela mundu jwagalagatila Allah pe), ana ŵaŵiliŵa mpaka aŵe yalumo (kaŵe kao) nkwalandanyamo? Lumbili losope lwene alapikwe Allah, nambo ŵajinji mwa ŵanganyao ngaakumanyilila.');
INSERT INTO "silika" VALUES(39,30,'39:30','Chisimu mmwejo ŵakuwa, nombe nao soni chisimu ŵakuwa.');
INSERT INTO "silika" VALUES(39,31,'39:31','Kaneka chisimu ŵanganyammwe lisiku lya Kiyama kwa M’mbuje gwenu (Allah) tinchiŵa nkukangana.');
INSERT INTO "silika" VALUES(39,32,'39:32','Ana nduni jwali jwalupuso nnope kwakumpunda mundu jwansepele Allah unami, ni kuikanila yakuona pati im’bichililemo? Ana ku Jahannama nganikuŵa kwiuto lya achimakafili?');
INSERT INTO "silika" VALUES(39,33,'39:33','Sano ajula (Muhammadi صلى الله عليه وسلم) jwaayiche ni yakuona ni ŵaijitichisye yalakweyo (maswahaba ni ŵamunyuma mwao), ŵanganyao ni ŵali ŵawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(39,34,'39:34','Akwete kupata ŵanganyao iliyose yatakaisacheje kwa M’mbuje gwao (Allah), gele ni malipilo ga ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(39,35,'39:35','Kuti Allah akaasiimaachisye ŵanganyao yakusakala yaŵapanganyisye, ni kwalipila malipilo gao pa yambone nnope yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(39,36,'39:36','Ana Allah nganaŵa Jwakunkwanila kapolo Jwakwe? Ni akun’jogoyechesya ŵanganyao ayila (yaakuigalagatila) yangaŵaga Jwalakwe (Allah). Sano jwannechelele Allah kusokonechela, basi nganijukola nkun’jongola.');
INSERT INTO "silika" VALUES(39,37,'39:37','Soni jwatite Allah an’jongole, basi nganapagwa kukwakwe jwanti nikunsochelesya. Ana Allah nganaŵa Jwamachili gakupunda, Nkuusya mbusyo syakupoteka kusyene?');
INSERT INTO "silika" VALUES(39,38,'39:38','Soni naga n’di mwawusisye ŵanganyao (kuti): “Ana nduni juŵagumbile mawunde ni mataka?” Chisimu chene tajile: “Allah.” Jilani: “Ana nkuiona uli (isanamu) yankuilumbanjila kunneka Allah, naga Allah ali asachilile kumba masausyo, ana yalakweyo mpaka ikungunukule masausyo Gakwego? Kapena ali asachilile kumba ukoto, ana yalakweyo mpaka isiŵilile ukoto Wakweo?” Jilani: “Allah akungwanila, Kukwakwe pe ajegamile akwegamila.”');
INSERT INTO "silika" VALUES(39,39,'39:39','Jilani: “E ŵandu ŵangu! Kamulani masengo yatin''jile paakamula (ni ukombosi wenu wosope), none jwakwe ngukamula (mwangukamulila), basi sampano timmanyilile,”');
INSERT INTO "silika" VALUES(39,40,'39:40','“Kuti nduni jwatiyim’bichilile ilagasyo yatiyin’jalusye, ni kuntuluchila ilagasyo yakwendelechela.”');
INSERT INTO "silika" VALUES(39,41,'39:41','Chisimu Uwwe tuntuluchiisye mmwe (Muhammadi صلى الله عليه وسلم) Chitabu (Qur’an) ligongo lyaŵandu mwakuonaonape. Basi jwaajongweche nikuti akongoka kwaalikamuchisya nsyene, ni jwaapoteele nikuti akupotela kwalikonya nsyene, soni mmwe nganim''ba nkwachengeta jwao.');
INSERT INTO "silika" VALUES(39,42,'39:42','Allah ni jwaakujityosyaga misimu ndema jakuwa kwakwe. Sano ajila (misimu) janganijim’biche chiwa (akasajityosyagape) ku lugono kwakwe. Ni akasajisiŵililaga ajila jaajilamulile chiwa (kulugono peko kuti jikaujila soni ku yiiilu yakwe, mundu ni akasawilaga kulugono), ni akasajitumisyaga jine josope (ku yiilu yakwe) mpaka pandema jekolanjidwe (jakuwiila). Chisimu mu yalakweyo muŵele mwana ilosyo (yakulosya ukombosi wa Allah) kwa ŵandu ŵaakuchetelela.');
INSERT INTO "silika" VALUES(39,43,'39:43','Ana kapena alitendele (inepe) yangaŵaga Allah kuŵa yakwachondelela (kwa Allah)? Jilani: “Ana nantimuno ili yangali ulamusi pa chilichose soni yangali lunda?”');
INSERT INTO "silika" VALUES(39,44,'39:44','Jilani: “Chondechonde chosope chili kwa (malamusi ga) Allah, uŵele Wakwe uchimwene wa kumawunde ni petaka, kaneka Kukwakwe tinchiuchisyidwa.”');
INSERT INTO "silika" VALUES(39,45,'39:45','Sano pati achikolanjidwaga Allah Jikape jikasanyalaisyidwaga mitima ja aŵala ŵangakukulupilila ya Akhera, nambo pati achikolanjidwaga aŵala ŵanganaŵa Jwalakwe, jele ndemajo ŵanganyao akasasengwaga.');
INSERT INTO "silika" VALUES(39,46,'39:46','Jilani: “E Allah! Nkugumba mawunde ni mataka! Nkumanyilila yakusisika ni yakuonechela! Mmwe tinchijiilanya chilikati cha achikapolo Ŵenu pa ichindu yaŵaliji ŵanganyao pa yalakweyo ali nkutindana.”');
INSERT INTO "silika" VALUES(39,47,'39:47','Namuno ŵandu ŵalupuso angaje kola (pa Kiyama) yosope yaili pa chilambo ni yanti mpela yeleyo soni yalumo, angajile kusaka kuliombolela nayo ku ilagasyo yakusakala ya lisiku lya Kiyama (nambo nga nganiyija kwakamuchisya), ni tiyikaonechele kwa ŵanganyao kuumila kwa Allah yanganayiganichisyaga.');
INSERT INTO "silika" VALUES(39,48,'39:48','Ni kuchionechela kukwao kusakala kwa ayila yaŵapanganyisye, ni ichasyungula ŵanganyao ayila yaŵaliji nkuitendela chipongwe.');
INSERT INTO "silika" VALUES(39,49,'39:49','Basi naga mundu ili inkwayiye ipotesi akasatuŵilangaga (kuti tuntyochesye), kaneko naga tuli tumpele ukoto wakuuma Kukwetu, jukasatiji: “Chisimu mbedwile yalakweyi ligongo lya umanyilisi (wangu, mwanti pangali ligongo lyakuntogolelela Allah).” (Ngwamba), nambo galakwego gagambile kuŵa mayeso, nambo ŵajinji mwa jamanjao ngakumanyilila.');
INSERT INTO "silika" VALUES(39,50,'39:50','Chisimu ŵaŵechete yalakweyi aŵala ŵapaujo pao, basi ni nganiyakamuchisya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(39,51,'39:51','Basi ni yapatile yakusala ya yaŵapanganyisye, soni aŵala ŵalitesile lupuso mwa ŵanganyaŵa (ŵantumisyidwe kukwao mmwe Muhammadi صلى الله عليه وسلم) tiyapate yakusakala ya yaapanganyisye, soni nganaŵa ŵanganyao ŵanti nikunnepelekasya (Allah kwapa ilagasyo).');
INSERT INTO "silika" VALUES(39,52,'39:52','Ana nganamanyilila yanti Allah akasansapangulila lisiki jwansachile, ni kunandiya (lisikilyo kwa jwansachile)? Chisimu mu yalakweyo muŵele mwana ilosyo (yakulosya ukombosi Wakwe) kwa ŵandu ŵaakukulupilila.');
INSERT INTO "silika" VALUES(39,53,'39:53','Jilani: “E achikapolo Ŵangu ŵan’ditendele yakusakala mwachimisyene! Ngasinkata tama ku chanasa cha Allah, chisimu Allah akasakululukaga sambi syosope syene, chisimu Jwalakwe ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(39,54,'39:54','“Soni ujilaani kwa M’mbuje gwenu (Allah pakutenda toba), soni lipelekaani Kukwakwe, nkaniyim’bichilile ilagasyo kaneka ningajokoledwa.”');
INSERT INTO "silika" VALUES(39,55,'39:55','“Soni kuyani yambone nnope yayitulusyidwe kukwenu kuumila kwa M’mbuje gwenu (Allah), nkaniyim’bichilile ilagasyo mwachisupuchisya kutendaga jenumanja nkakumanyilila.”');
INSERT INTO "silika" VALUES(39,56,'39:56','Kuti ngasaja kuŵecheta mundu jwalijose yanti: “Kudandaula kwangune! Ligongo lya ayila yanapungwisye ku mbali ja Allah, soni chisimu naliji mwa akutenda chipongwe.”');
INSERT INTO "silika" VALUES(39,57,'39:57','Kapena kuja kuŵecheta yanti: “Ingaŵe kuti Allah ŵanjongwele, ningaliji mwa ŵandu ŵawoga (wakun’jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(39,58,'39:58','Kapena kuja kuŵecheta ndema jakuiona ilagasyo kuti: “Kungaŵe kwakomboleka une kuujila (ku duniya), basi ngajile kuŵa mwa akukolosya (masengo).”');
INSERT INTO "silika" VALUES(39,59,'39:59','(Tachisalilidwa yanti): “Elo, chisimu ma Ȃya Gangu gam’bichilile, nambo mwagakaaniile, ni mwalikwesyisye, soni mwaliji mwa achimakafili.”');
INSERT INTO "silika" VALUES(39,60,'39:60','Soni lisiku lya Kiyama tinchaona aŵala ŵaŵannambuchisye Allah (yanti akwete mwanache kapena ŵakamusyangana nao) ngope syao sili syakupiilila. Ana ku Jahannama nganikuŵa kwiuto lya ŵakulikwesya?');
INSERT INTO "silika" VALUES(39,61,'39:61','Ni tachajokola Allah aŵala ŵaŵaliji ni woga (wakun’jogopa Jwalakwe) ligongo lyakupunda kwao (kwakupata ntendele wa ku Mbepo), yakusakala ngasiyakwaya, nambo soni ŵanganyao ngasaŵa ŵakudandaula.');
INSERT INTO "silika" VALUES(39,62,'39:62','Allah ni Nkugumba chilichose, soni Jwalakwe pa chindu chilichose juŵele Nkuchengeta.');
INSERT INTO "silika" VALUES(39,63,'39:63','Gaŵele Gakwe makiyi ga kumawunde ni petaka. Sano aŵala ŵaakaaniile ma Ȃya ga Allah, ŵanganyao niŵali ŵakwasika.');
INSERT INTO "silika" VALUES(39,64,'39:64','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana jwangaŵaga Allah ni jwankuunamula kuti nan’galagatileje? E jenumanja ajinga?”');
INSERT INTO "silika" VALUES(39,65,'39:65','Soni chisimu pamasile paijuwulidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم) ni kwa aŵala (Achimitenga) ŵa paujo penu (yanti): “Chisimu naga nkuwanganya (Allah ni isanamu), masengo genu tigajonasiche kwene, soni tim’be kwene mu ŵakwasika.”');
INSERT INTO "silika" VALUES(39,66,'39:66','Nambo basi mun’galagatileje Allah pe, ni m’beje mwa akutogolela.');
INSERT INTO "silika" VALUES(39,67,'39:67','Ni ngananchimbichisya Allah uchimbichimbi Wakwe usyesyene. Sano litaka lyosope lyene lichiumbatidwa mwigasa Lyakwe lisiku lya Kiyama, ni mawunde gachiŵilijidwa ni Nkono Wakwe wan’dyo, kuswejela ni Kwakwe soni jutalikangene kusyene ni yaakum’bwanganya nayo.');
INSERT INTO "silika" VALUES(39,68,'39:68','Ni lichigombedwa lipenga, basi tachiwa wosope ŵali kumawunde ni ŵali petaka, ikaŵeje ŵatachasaka Allah (kuti akawa). Kaneka lichigombedwa soni line, basi ajilesayi ŵanganyao tachiŵa ali ajimi aku ali nkulola. ');
INSERT INTO "silika" VALUES(39,69,'39:69','Sano litaka (lyele lisikuli) lichiŵala ni lilanguka lya M’mbujegwe (Allah, ndema jatachiyika kukwiilanya magambo), ni chichiŵichidwa chitabu (chamasengo ga ŵandu), ni taachiyika nao Achimitume kwisa soni mboni, ni kuchijilanyisyidwa chilikati chao mwakuonaonape, soni ŵanganyao ngasatendedwa lupuso.');
INSERT INTO "silika" VALUES(39,70,'39:70','Soni mundu jwalijose tachilipidwa mwakwana chenene ya jwapanganyisye, soni Jwalakwe (Allah) ni jwaakumanyilila chenene yaakutenda ŵanganyao.');
INSERT INTO "silika" VALUES(39,71,'39:71','Ni tachichinjidwa aŵala ŵaŵakufuulu wa ku Jahannama ali mikutula-mikutula, mpaka pandema jatachijiyika, jichiugulidwa milango jakwe (mwachitemachitema), ni tachawusya ŵanganyao akujichengeta ŵakwe kuti: “Ana nganam’bichilila Achimitenga ŵakuumila mwa jenumanja pemmwe, ŵaŵaliji nkunsoomela ma Ȃya ga M’mbuje gwenu (Allah), ni kuntetelaga ya kusimangana ni lisiku lyenu lyelino?” Ŵanganyao tachiti: “Elo (ŵatuyichilile), nambo kuti lyasimichiche liloŵe lya ilagasyo pa achimakafili.”');
INSERT INTO "silika" VALUES(39,72,'39:72','Ichiŵechetedwa yanti: “Jijinjilani milango ja Jahannama, tim’be ŵandamo mwalakwemo.” Kaje kusakala nnope mauto ga ŵakulikwesya!');
INSERT INTO "silika" VALUES(39,73,'39:73','Ni tachichinjidwa aŵala ŵaŵan’jogwepe M’mbuje gwao (Allah) wa ku Mbepo ali mikutula-mikutula, mpaka pandema jatachijiyika, ni jichiugulidwa milango jakwe (ŵanganyao nkanakopochele), ni tachasalila ŵanganyao akujichengeta ŵakwe (mwakwakalibuchisya): “Ntendele uŵe kukwenu! M’bele ŵambone (pa itendo ni iŵecheto yenu, mwanti sengwagani), basi jijinjilani (Mbepo), tim’be ŵandamo (mwalakwemo).”');
INSERT INTO "silika" VALUES(39,74,'39:74','Ŵanganyao ni tachiti: “Lumbili losope lwene alapikwe Allah Ajula jwaatukwanilichisye chilanga Chakwe, ni kututawalikasya litaka (lya mu Mbepo muno), chitukombolecheje kutama mu Mbepo palipose patusachile.” Kaje kusalala nnope malipilo ga akupanganya (masengo gambone)!');
INSERT INTO "silika" VALUES(39,75,'39:75','Ni tinchaona Achimalaika achisyungulaga mungulugulu mwa Arishi (Chindanda Chaulungu) aku ali nkuluswejesya lumbili lwa M’mbuje gwao (Allah). Ni kuchijilanyisyidwa chilikati chao mwakuonaonape, ni kuchiŵechetedwa yanti: “Lumbili losope lwene alapikwe Allah M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(40,1,'40:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(40,2,'40:2','Kutulusyidwa kwa Chitabu (Qur’an) kukuumila kwa Allah, Jwamachili gakupunda, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(40,3,'40:3','Jwakululuka sambi ni Jwakupochela toba, Jwaukali pakupeleka ipotesi, Nsyene kupeleka mituka jejinji, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwalakwe, soni Kukwakwe pe ni kwakuujila.');
INSERT INTO "silika" VALUES(40,4,'40:4','Ngaakasakanganaga pa ma Ȃya ga Allah ikaŵeje aŵala ŵaakufuulu, basi kukannyenga kwendajenda kwao mu ilambo (kupita nchisosa yaakusaka mwantunjelele, chisimu mbesi jao ni ku Moto).');
INSERT INTO "silika" VALUES(40,5,'40:5','Ŵaakaaniile paujo pao ŵandu ŵa Nuhu kwisa soni mikutula (jine) panyuma pao, sano umati uliose wasachilile (yangalumbana) kwa ntenga jwao kuti ankwembekanye (am’bulaje), ni ŵakanganile ni yaunami kuti atyochesye nayo yakuona, basi nakwembekenye. Ana ipotesi Yangu yaliji uli!');
INSERT INTO "silika" VALUES(40,6,'40:6','Soni iyyoyopeyo lisimichiche liloŵe lya Ambuje ŵenu pa aŵala ŵakanile lyanti ŵanganyao ni ŵandu ŵa ku Moto.');
INSERT INTO "silika" VALUES(40,7,'40:7','Aŵala (Achimalaika) ŵaakutwichila Arishi (Chindanda Chaulungu) ni ŵali mungulugulu mwakwe, akuluswejesyaga lumbili lwa M’mbuje gwao (Allah), ni akunkulupililaga Jwalakwe, ni akwaŵendelaga chikululuko aŵala ŵaakulupilile (achitiji): “Ambuje ŵetu! Nchijenele chindu chilichose pa ukoto ni umanyilisi, basi mwakululuchilani aŵala ŵatesile toba ni kuya litala Lyenu, ni mwagosani ku ilagasyo ya Moto Waukali.”');
INSERT INTO "silika" VALUES(40,8,'40:8','“Ambuje ŵetu! Soni mwajinjisyani ŵanganyao ku Matimbe Gandamo agala gamwapele chilanga, kwisa soni aŵala ŵaŵatesile yambone mwa achatati ŵao, achiŵammakwao ni ŵanache ŵao, chisimu Mmwe ni Ŵamachili gakupunda, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(40,9,'40:9','“Ni mwagose ku yakusakala, sano jwatimun’gose ku yakusakala lyele lisikuli, chisimu tim’be muntendele chanasa, soni kwalakweko ni kupunda kwekulungwa nnope.”');
INSERT INTO "silika" VALUES(40,10,'40:10','Chisimu aŵala ŵaŵakufuulu takaŵilanjidwe (ni kusalilidwa kuti): “Chisimu kunchima kwa Allah (kumwanchimaga jenumanja ndema jamwankanaga ku duniya) kwaliji kwekulungwa kupunda kulichima kwenu (lelo) mwachimisyene (m’Motomo). (Kumbuchilani) ndema jamwaŵilanjidwaga ku chikulupi basi jenumanja nikanaga.”');
INSERT INTO "silika" VALUES(40,11,'40:11','Ŵanganyao tachiti: “Ambuje ŵetu! Mwatupele chiwa kaŵilikose (m’migongo ja achatati ŵetu ni panyuma pa umi wa duniya), ni ntupele umi kaŵilikose (pa duniya ni ku Akhera), basi tujitichiisye sambi syetu. Ana nipana litala lyakopochela (ku Moto kuno?” Tachisalilidwa kuti: “Pangali.)”');
INSERT INTO "silika" VALUES(40,12,'40:12','(Tachisalilidwa kuti): “Yalakweyi niligongo lyanti ndema jajwalumbanjidwaga Allah Jikape mwakanilaga, nambo naga nkum’bwanganya Jwalakwe (ni isanamu) ni mwakulupililaga, basi chiilanyo chosope chili cha Allah Jwapenani kusyene, Jwankulungwa nnope.”');
INSERT INTO "silika" VALUES(40,13,'40:13','Jwalakwe ni Ajula jwaakunnosya ŵanganyammwe ilosyo Yakwe (yakulosya ukombosi Wakwe), ni akuntuluchisya lisiki (wula) kuumila kwinani, ni ngaakukumbuchila (kusyesyene) ikaŵeje ajula jwaakuwujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(40,14,'40:14','Basi munnumbanjileje Allah mwakunswejechesya dini Jakwe, namuno achimakafili yachime.');
INSERT INTO "silika" VALUES(40,15,'40:15','(Jwalakwe Allah ni) Nsyene mauchimbichimbi gapenani kusyene, Nsyene Arishi (Chindanda Chaulungu), jukupelekaga maloŵe kwa lilamusi Lyakwe ku jwansachile mwa achikapolo Ŵakwe, kuti jutetele ya lisiku lyakusimangana (lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(40,16,'40:16','Lisiku lyatachionechela papaswela ŵanganyao, chindu chilichose chakuumila kukwao ngasichisisika kwa Allah. (Allah tachiwusya kuti): “Ana lelo uchimwene wosope wacheni?” (Ni tachilijanga Nsyene kuti): “Wa Allah Jumpepe, Jwamachili gangalimbana nago.”');
INSERT INTO "silika" VALUES(40,17,'40:17','Lelo mundu jwalijose tijulipidwe yajwapanganyisye, pangali lupuso lelo, chisimu Allah ni Jwachitema pakuŵalanjila.');
INSERT INTO "silika" VALUES(40,18,'40:18','Soni mwatetelani ya lisiku (lya Kiyama) lyalikuŵandichila, pandema jajichiyika mitima kungulo (ligongo lya woga) jili jin’gumbele madandausi, ŵalupuso ngasakola nsyoŵe jwa pantima (jwanti nikwakamuchisya lyele lisikulyo), namuno nkuchondelela jwaakundidwa (kuchondelela kwakwe).');
INSERT INTO "silika" VALUES(40,19,'40:19','(Allah) akumanyilila chinyengo cha meso, ni yajikusisa mitima.');
INSERT INTO "silika" VALUES(40,20,'40:20','Soni Allah akwiilanyaaga mwakuonaonape, sano aŵala ŵaakwalumbanjila kunneka Jwalakwe nganaŵa apakombwele kwiilanya chilichose. Chisimu Allah ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(40,21,'40:21','Ana ngakwendajenda pachilambo ni kupitaga nchilola mujaŵelele mbesi ja aŵala ŵaŵaliji paujo pao? Ŵaliji ŵamachili nnope kwapunda ŵanganyao, ni (ŵalesile) ipasipasi (yejinji) pachilambo (mpela yakutaŵataŵa kwapunda Makuraishi), basi Allah ŵakwembekenye ligongo lyasambi syao, ni nganakola nkwagosa jwalijose kwa Allah.');
INSERT INTO "silika" VALUES(40,22,'40:22','Yalakweyi nilogongo lyanti ŵanganyao ŵaliji nkwaichilila Achimitenga ŵao ni ilosyo yakuonechela ni ŵaakaaniile, basi Allah niŵakwembekenye. Chisimu Jwalakwe ni Jwakulimbangana nnope, Jwaukali kusyene pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(40,23,'40:23','Soni chisimu chene twantumisye Musa ni isimosimo Yetu kwisa soni maumboni gakuonechela.');
INSERT INTO "silika" VALUES(40,24,'40:24','Kwa Firiauna, Hamana ni Karuna, ŵanganyao ni ŵatite: “(Aju ni) nsaŵi, jwaunami kusyene.”');
INSERT INTO "silika" VALUES(40,25,'40:25','Basi ndema jajwaichilile ni yakuona kuumila Kukwetu, ŵatite: “Mwaulagani ŵanache ŵachilume ŵa aŵala ŵaakulupilile najo imo, ni mwalecheje umi ŵanache ŵao ŵachikongwe.” Ni nganigaŵa malindi ga achimakafili ikaŵeje mu kusokonechela.');
INSERT INTO "silika" VALUES(40,26,'40:26','Ni jwaŵechete Firiauna kuti: “Munekani kuti nam’bulaje Musa, ni ŵaŵilanje Ambujegwe (kuti anchenjele). Chisimu une ngogopa kuti atakwika mwaagalawisye dini jenu, kapena atakutandikasya kuwatanganya kwa kuonechela pa chilambo.”');
INSERT INTO "silika" VALUES(40,27,'40:27','Musa jwatite: “Chisimu une ngulijuya mwa M’mbuje gwangu nambo soni M’mbuje gwenu (Allah), ku jwakulikwesya jwalijose jwangakukulupilila ya Lisiku Lyachiŵalanjilo.”');
INSERT INTO "silika" VALUES(40,28,'40:28','Ni jwaŵechete mundu jwakulupilila jwa kwiŵasa lya Firiauna juŵasisaga chikulupi chakwe: “Ana nkusaka kum’bulaga mundu ligongo lyakuŵecheta kuti: ‘M’mbuje gwangu ni Allah?’ Kutendaga soni jwalakwe pamasile pam’bichilile ni ilosyo yakuonechela kuumila kwa M’mbuje gwenu? Sano najuŵaga jwaunami, basi unami wakweo tiun’jonanje nsyene, nambo naga juli jwakuona nikuti tiyimpate ine mu yaakumpa chilanga (kuti tiyimpate pa duniya pepano). Chisimu Allah jwangan’jongola mundu jwali jwakupelenganya malile (pakonanga), jwaunami kusyene.”');
INSERT INTO "silika" VALUES(40,29,'40:29','“E ŵandu ŵangu! Uchimwene ni wenu lelo, n’di ŵakupunda pa chilambo. Ana niŵani ŵampaka atujokole ku ipotesi ya Allah naga ili ituyichilile?” Firiauna jwatite: “Ngangumpanga ikaŵeje yanguiona (kuti yambone), soni ngangun’jongolela ikaŵeje kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(40,30,'40:30','Ajula juŵakulupilile ni jwatite: “E ŵandu ŵangu! Chisimu une ngun’jogopela ŵanganyammwe chisawu lisiku (lya ipotesi yanakumpataga) mikutula (ja makafili gakalakala).”');
INSERT INTO "silika" VALUES(40,31,'40:31','“Chisawu nyendele (jakapedwe ka ilagasyo) ja ŵandu wa Nuhu, Adi ni Samuda kwisa soni aŵala ŵamunyuma mwao, ni nganaŵa Allah jwakusaka kwatenda lupuso achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(40,32,'40:32','“E ŵandu ŵangu! Chisimu une ngun’jogopela ŵanganyammwe lisiku lyakuŵilangana (lisiku lya Kiyama).”');
INSERT INTO "silika" VALUES(40,33,'40:33','“Lisiku lyatinchilosya migongo kutila, n’di nganinkola ŵakunchenjela kwa Allah, sano jwannechelele Allah kupotela, basi nganijukola nkun’jongola jwalijose.”');
INSERT INTO "silika" VALUES(40,34,'40:34','“Soni chisimu chene ŵam’bichilile Yusufu kalakala ni ilosyo yakuonechela, basi ninganinneka kuŵa mu kaichila pa yajwaiche nayo kukwenu, kwikanila ndema jajwawile mwatite: ‘Allah nganaŵa atumisye panyuma pakwe ntenga jwalijose.” Iyyoyopeyo niyaakutiji Allah pakunnechelela kupotela mundu jwali jwakupelenganya malile (pakonanga), jwakaichila.');
INSERT INTO "silika" VALUES(40,35,'40:35','Aŵala ŵaakukangana pa ma Ȃya ga Allah mwangali umboni uliose wawaichilile, (kangana kwaoko) kukusile nnope kuŵenjedwa (kwakwe) pameso pa Allah kwisa soni pameso pa aŵala ŵaakulupilile. Iyyoyopeyo ni yakasatiji Allah pakudinda chidindo pa ntima uliose wa jwakulikulungusya, jwakulikwesya.');
INSERT INTO "silika" VALUES(40,36,'40:36','Firiauna ni jwatite: “E mmwe Hamana! Mundaŵilani chipilala, kuti mwine une tinagaiche matala.”');
INSERT INTO "silika" VALUES(40,37,'40:37','“Matala ga kumawunde, kuti ngan’dingule Nnungu jwa Musa, soni chisimu une ngun’ganichisya jwalakwe kuti jwaunami.” Iyyoyopeyo yasalalisyidwe kwa Firiauna itendo yakwe yangalumbana, ni jwasiŵilidwe kwitala (lyagoloka), ni nganigaŵa malindi ga Firiauna ikaŵeje mu konasika.');
INSERT INTO "silika" VALUES(40,38,'40:38','Ajula juŵakulupilile ni jwatite: “E ŵandu ŵangu! Munguyani une nan’jongolele kwitala lyachongoko.”');
INSERT INTO "silika" VALUES(40,39,'40:39','“E ŵandu ŵangu! Chisimu awu umi wa duniya uŵele chisengwasyo (chandema jannono), sano chisimu Akhera nijajili Nyumba Jakutamilichika.”');
INSERT INTO "silika" VALUES(40,40,'40:40','“Jwatapanganye chakusakala chilichose, basi ngaalipidwa ikaŵeje chakulandana mpela chichocho, soni juchapanganye chambone chilichose, kwali taŵe jwannume kapena jwankongwe aku juli tachipedwaga lisiki kwalakweko mwangaŵalanjila.”jwakulupilila, basi ŵanganyao tachijinjila ku Mbepo,');
INSERT INTO "silika" VALUES(40,41,'40:41','“Soni E ŵandu ŵangu! Ligongo chichi une ngum’bilanjila jenumanja ku chokoko, sano jenumanja ni nkumilanjila une ku Moto!”');
INSERT INTO "silika" VALUES(40,42,'40:42','“Nkumilanjila une kuti nankaane Allah ni nam’bwanganye Jwalakwe ni ichindu yanganingola nayo umanyilisi, kutendaga une ngum’bilanjila ku (Allah) Jwamachili gakupunda, Jwakululuka kusyene!”');
INSERT INTO "silika" VALUES(40,43,'40:43','“Pangali kaichila yanti ŵankumilan’jila kuti nagalagatilejeo, nganakola duwa (janti mpaka ajijanje) pa duniya namuno ku Akhera, soni kuti kwakuujila kwetu ni kwa Allah, soni kuti ŵakupelenganya malile (pakonanga) ni ŵatachiŵa ŵandu ŵa ku Moto.”');
INSERT INTO "silika" VALUES(40,44,'40:44','“Basi tinchiyikumbuchila yangunsalilayi, ni ngwegamisya indu yangu kwa Allah. Chisimu Allah ni Jwakwalolechesya achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(40,45,'40:45','Basi Allah ni ŵan’gosile ku yakusakala ya malindi gaŵantendele, ni yasyungwile ŵandu ŵa Firiauna ilagasyo yakusakala nnope.');
INSERT INTO "silika" VALUES(40,46,'40:46','Moto, akulosyedwa ŵanganyao ku walakweo kundaŵi ni kwigulo (m’malembe mwao). Sano lisiku lyajichijima Kiyama (kuchiŵechetedwa yanti): “Mwajinjisyani ŵandu ŵa Firiauna ku ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(40,47,'40:47','Soni (mwakumbusyani) katema katachikanganaga m’Moto. Basi nitachiti ŵangalikombolela kwa aŵala ŵaŵalikwesyisye: “Chisimu uwwe twaliji akunkuya ŵenu, ana jenumanja ni chintutyochesye upande wine wa Moto?”');
INSERT INTO "silika" VALUES(40,48,'40:48','Aŵala ŵaŵalikwesyisye ni tachiti: “Chisimu uwwe wosope ŵene tuli mwemuno, chisimu Allah pamasile paajiileenye chilikati cha achikapolo (Ŵakwe).”');
INSERT INTO "silika" VALUES(40,49,'40:49','Aŵala ŵatachiŵa m’Moto tachiti pakwasalila akujisunga Jahannama (Achimalaika): “Mum’bendani M’mbuje gwenu (Allah) kuti atupunguchisye uwwe ilagasyo lisiku limpepe.”');
INSERT INTO "silika" VALUES(40,50,'40:50','(Akujisunga Jahannama) tachiti: “Ana achimitenga ŵenu nganaŵa nkum’bichilila ni ilosyo yakuonechela?” Ŵanganyao tachiti: “Elo.” (Akujisunga Jahannama) tachiti: “Basi wombani.” Ni nganijiŵa duwa jamakafili ikaŵeje mu kusokonechela.');
INSERT INTO "silika" VALUES(40,51,'40:51','Chisimu Uwwe chitwachenjele Achimitenga Ŵetu kwisa soni aŵala ŵakulupilile pa umi wa duniya ni lisiku lyasichijima mboni (lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(40,52,'40:52','Lisiku lyaangaakamuchisya ŵalupuso kuliŵalanga kwao, ni tachikola ŵanganyao malweso, ni tachikola soni nyumba jakusakala nnope (Jahannama).');
INSERT INTO "silika" VALUES(40,53,'40:53','Soni chisimu chene Musa twampele chongoko, ni twapele ŵanache ŵa Israila kuchitawalila Chitabu (Taurat).');
INSERT INTO "silika" VALUES(40,54,'40:54','(Chachaliji) chongolo kwisa soni chikumbusyo kwa ŵakwete lunda.');
INSERT INTO "silika" VALUES(40,55,'40:55','Basi pililani (mmwe Muhammadi صلى الله عليه وسلم), chisimu chilanga cha Allah chakuonaonape, soni ŵendani chikululuko ku sambi syenu, ni nnuswejesyeje lumbili lwa Ambuje ŵenu (Allah) kwigulo ni kundaŵi.');
INSERT INTO "silika" VALUES(40,56,'40:56','Chisimu aŵala ŵaakukangana pa ma Ȃya ga Allah mwangali umboni uliose wawayichilile, m’mitima mwao mwangali ikaŵeje kulikwesya, (akusosa ukulu wakumpunda Muhammadi صلى الله عليه وسلم) nambo nganaŵa aŵuŵiche ŵanganyao, basi lijuyani mwa Allah, chisimu Jwalakwe ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(40,57,'40:57','Chisimu gumba mawunde ni mataka kuŵele kwekulungwa nnope kupunda gumba ŵandu, nambo ŵandu ŵajinji ngaakumanyilila.');
INSERT INTO "silika" VALUES(40,58,'40:58','Soni nganaŵa alandene jwangalola ni jwakulolaga, namuno aŵala ŵaakulupilile ni kutendaga yambone (nganaŵa alandene) ni akusakasya. Yannonope ni yankukumbuchilaga.');
INSERT INTO "silika" VALUES(40,59,'40:59','Chisimu Kiyama jikwisa kwene, jangali kaikasya (yakwika kwakwe), nambo ŵandu ŵajinji ngaakukulupilila.');
INSERT INTO "silika" VALUES(40,60,'40:60','Ambuje ŵenu (Allah) ni akuti: “Mumombani Une tinan’janje. Chisimu aŵala ŵaakulikwesya pakuleka kungalagatila Une, tachijinjila ku Jahannama ali ŵakunyosyeka.”');
INSERT INTO "silika" VALUES(40,61,'40:61','Allah ni Ajula jwam’bichile chilo kuti ntulalilejemo, muusi nikuŵa wakuloleka, chisimu Allah ni Nsyene umbone wakwatendela ŵandu, nambo ŵandu ŵajinji ŵangatogolela.');
INSERT INTO "silika" VALUES(40,62,'40:62','Jwalakwe ni Allah M’mbuje gwenu, Nkugumba chindu chilichose, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo, ana nkugalausyidwa chinauli (kuleka yakuona)?');
INSERT INTO "silika" VALUES(40,63,'40:63','Iyyoyopeyo ni yaŵatite pakugalausyidwa aŵala ŵaŵaliji nkugakanila ma Ȃya ga Allah (kalakala).');
INSERT INTO "silika" VALUES(40,64,'40:64','Allah ni Ajula jwantendele litaka kuŵa lyakutamilichika, liwunde nikuŵa nsakasa, ni ampaanjiile kaoneche kenu, basi ni akakolosyisye kaoneche kenuko, ni ampele ichindu yambone, Jwalakwe ni Allah M’mbuje gwenu, basi anyakwiche (pa uchimbichimbi ni upile) Allah, M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(40,65,'40:65','Jwalakwe ni Jwaumi wandamo, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo, basi mum’bombani Jwalakwe mwakunswejechesya dini Jakwe. Lumbili losope lwene alapikwe Allah, M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(40,66,'40:66','Jilani: “Chisimu une nganyisyidwe kwagalagatila aŵala ŵankwalumbanjila kunneka Allah payimbichilile ilosyo yakuonechela kuumila kwa Ambuje ŵangu, ni namulidwe kuti nalipeleche kwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(40,67,'40:67','Jwalakwe ni Ajula juŵan’gumbile jenumanja kuumila mwitaka (baba ŵenu Adam), kaneka (akun’gumbaga) kuumila mwindondwa lya mesi (ga ŵalume ni ŵakongwe), kaneka kuumila m’chipande cha myasi jekamatane, kaneka akunkoposyaga n’di makandi, kaneka (akunnechelelaga) kuti m’biche ndema jaakomangala kwenu, kaneko kuti m’be ŵachekulupe, ni pana ŵane mwa jenumanja ŵaakuwaga nkanayiche (ndema jauchekulu, ŵane namuno ndema jakomangala), ni (antendele yalakweyi) kuti n’jiyiche ndema jekolanjidwe (jakuwila jaŵannembele), nambo soni kuti jenumanja nkole lunda (lwakumanyililila kuti Allah jwapakombwele kutenda yele mpaka akombole kwajimusya ŵandu ŵawe).');
INSERT INTO "silika" VALUES(40,68,'40:68','Jwalakwe ni Ajula jwaakupelekaga umi ni chiwa, sano pati asachile chilichose (kuti chipatikane), basi agambaga kuchisalila kuti “M''be!” Ni chikasaŵaga.');
INSERT INTO "silika" VALUES(40,69,'40:69','Ana ngankwaona aŵala ŵaakukangana pa ma Ȃya ga Allah? Ana akugalausyidwa chinauli (kuleka yakuona)?');
INSERT INTO "silika" VALUES(40,70,'40:70','Aŵala ŵaachikaaniile Chitabu (cha Qur’an), ni ayila yatwaatumiisye nayo Achimitenga Ŵetu (ya chikulupi cha Tauhidi ni yakwimuka m’malembe). Basi tachiika kumanyilila (ipotesi yakanila kwao).');
INSERT INTO "silika" VALUES(40,71,'40:71','Ndema jagachiŵa makongwa mu ngosi mwao, kwisa soni unyolo (mu ngongolo mwao) ali nkuwutidwa.');
INSERT INTO "silika" VALUES(40,72,'40:72','M’mesi gamasemule kusyene, kaneko tachiŵa nkuchomedwa m’Moto.');
INSERT INTO "silika" VALUES(40,73,'40:73','Kaneko ichiŵechetedwa kukwao yanti: “Ili kwapi ayila yamwaliji nkuiwanganya (pa kuigalagatila)?”');
INSERT INTO "silika" VALUES(40,74,'40:74','“Kunneka Allah?” Ŵanganyao takajile: “Itusokonechele. (Ngwamba), nambo kutandila kalakose nganituŵeje tuli nkuchilumbanjila chindu chilichose (changaŵaga Mmwe Ambuje).” Iyyoyopeyo niyaakutiji Allah pakwalechelela kusokonechela achimakafili.');
INSERT INTO "silika" VALUES(40,75,'40:75','“Yalakweyi (yaimpatile lelo m’Moto) niligongo lyanti mwaliji nkutwanga pa chilambo mwangaŵajilwa (pagalagatila isanamu ni kanila ya kwimuka m’malembe), ni ligongo lyanti mwaliji nkuchina.”');
INSERT INTO "silika" VALUES(40,76,'40:76','“Jinjilani milango ja ku Jahannama, tim’be ŵandamo kwalakweko. Kaje kusakala nnope mauto ga ŵakulikwesya!”');
INSERT INTO "silika" VALUES(40,77,'40:77','Basi pililani (mmwe Muhammadi صلى الله عليه وسلم), chisimu chilanga cha Allah chakuonaonape. Soni kanga chitunnosye (mmwe Muhammadi صلى الله عليه وسلم pa duniya pepano) ine mwa ayila (ipotesi) yatukwatetela kapena kumpa chiwa (nkanitunnosye; tiyapatepe), basi Kukwetu taachiuchisyidwa.');
INSERT INTO "silika" VALUES(40,78,'40:78','Soni chisimu chene twatumisye Achimitenga paujo penu (mmwe Muhammadi صلى الله عليه وسلم). Ŵane mwa jamanjao twansimbile (ngani syao), ŵane soni mwa jamanjao nganitunsimbila (ngani syao). Ni nganiyiŵa kwa ntenga julijose yanti nikwikanacho chisimosimo ikaŵeje mu lisosa lya Allah. Sano patichiyika chilamusi cha Allah (chakwapa ipotesi ŵakanila); kuchilamulidwa mwakuonaonape, ni tachipelelwa jele ndemajo ŵakuya yaunami.');
INSERT INTO "silika" VALUES(40,79,'40:79','Allah ni Ajula jwam’bichile jenumanja ilango kuti ine mu yalakweyo nyyikweleje, ni kuti ine muyalakweyo nyyilyeje.');
INSERT INTO "silika" VALUES(40,80,'40:80','Soni nkwete jenumanja mu yalakweyo ikamuchisyo (yaijinji mpela kuikama nkaka), nambo soni kupitila mu yalakweyo nkombolecheje kuyiyika isako yaili m’mitima mwenu (mpela kutwichilila misigo jenu), nambo soni pa yalakweyo kwisa soni pa yombo nkasanyakulidwaga.');
INSERT INTO "silika" VALUES(40,81,'40:81','Ni akunnosyaga (Allah) ilosyo Yakwe (yakulosya ukombosi Wakwe). Ana niyapi mu ilosyo ya Allah yankuikana?');
INSERT INTO "silika" VALUES(40,82,'40:82','Ana ngakwendajenda pa chilambo ni kupitaga nchilola mujaŵelele mbesi ja aŵala ŵaŵaliji paujo pao? Ŵaliji ŵajinji kwapunda ŵanganyaŵa (Makuraishi), nambo soni ŵamachili nnope, ni (ŵalesile) ipasipasi (yejinji) pachilambo (mpela yakutaŵataŵa), nambo nganiyakamuchisya ŵanganyao ya ŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(40,83,'40:83','Basi ndema jaŵaichilile ŵanganyao Achimitenga ŵao ni ilosyo yakuonechela, ŵatwanjile ni umanyilisi wawaliji kukwao (wakumanyilila ya duniya, ni kuichembulusya yaŵaiche nayo Achimitenga), ni yasyungwile ŵanganyao (ipato) ayila yaŵaliji nkuitenda chipongwe.');
INSERT INTO "silika" VALUES(40,84,'40:84','Sano ndema jaŵaiweni ipotesi Yetu ŵatite: “(Sano) tunkulipilile Allah Jumpepe, ni tuikaaniile ayila yatwaliji nkuiwanganya ni Jwalakwe (pa kuigalagatila).”');
INSERT INTO "silika" VALUES(40,85,'40:85','Basi nganichiŵa chanti nikwakamuchisya chikulupi chao ndema ja ŵaiweni ipotesi Yetu. (Aji ni) nyendele ja Allah jajapite pa achikapolo Ŵakwe (janti naga ayikene mwa kuiona ipotesi toba jao nganijipocheledwaga). Ni ŵapelelwaga jele ndemajo achimakafili.');
INSERT INTO "silika" VALUES(41,1,'41:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(41,2,'41:2','Chakutulusyidwa (chalakwechi) chityochele kwa (Allah) Jwaukoto wejinji, Jwachanasa channope.');
INSERT INTO "silika" VALUES(41,3,'41:3','(Chalakwechi ni) Chitabu chagasalichisyidwe ma Ȃya gakwe, Qur’an jacharabu, (gasalichisyidwe ma Ȃya gakwe) ku ŵandu ŵaakumanyilila (Maulama).');
INSERT INTO "silika" VALUES(41,4,'41:4','Chakupeleka abali jakusengwasya ni chakutetela, nambo ŵajinji mwa ŵanganyao (achimakafili) agalaŵiche (kujileka Qur’aniji), basi ŵanganyao ni ngaakupikana.');
INSERT INTO "silika" VALUES(41,5,'41:5','Ni akutiji: “Mitima jetu jili mu isiŵilo ku yankutuŵilanjilayi, ni m’mawiwi mwetu mwana usiiwa, soni chilikati chetuwe nomwe pana chakusiŵilila, basi panganyagani (yenu), nowe tukupanganya (yetu, pangali jwatan’jinjilile n’jakwe).”');
INSERT INTO "silika" VALUES(41,6,'41:6','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu une mundu mpela jenumanja. Ikujuwulidwa kukwangu yanti: Chisimu nnungu jwenu ni Nnungu Jumpepe. Basi jimilichikaani Kukwakwe, ni mum’bendeje chikululuko.” Sano ipotesi tiyapate akuwanganya (Allah ni isanamu).');
INSERT INTO "silika" VALUES(41,7,'41:7','Aŵala ŵangaakutola Zaka, soni ŵanganyao Akhera akujikaana.');
INSERT INTO "silika" VALUES(41,8,'41:8','Chisimu aŵala ŵaakulupilile ni kutendaga yambone, akwete kupata ŵanganyao malipilo gangamala.');
INSERT INTO "silika" VALUES(41,9,'41:9','Jilani: “Ana chisimu jenumanja nkunkaana Ajula (Allah) juŵagumbile litaka m’moŵa gaŵili, ni nkum’bichila Jwalakwe milungu (jine pakujigalagatila)? Jwalakwe ni M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(41,10,'41:10','Ni jwaŵisile mwalakwemo yakunyichisya pachanya pakwe (matumbi), ni ŵalipele upile (litakalyo pakuŵikapo mesi, m’mela ni inyama), ni jwapimilepo yakulya yakwe (yakwakwanila ŵakutama palakwepo, jwatesile yalakweyi) m’moŵa ncheche gakuliingaana. (Yalakweyi ni yakwanila) kwa akuwusya (ya kagumbidwe ketaka ni yaili palakwepo).');
INSERT INTO "silika" VALUES(41,11,'41:11','Kaneka jwasachilile gumba liwunde kutendaga lili lyosilyosipe. Basi niŵalisalile lyalakwe kwisa soni litaka kuti: “Jisani mwakusaka kapena mwangasaka (kundani lilamusi Lyangu).” Inayiŵiliyo yatite: “Tuŵiiche mwaakuunda (lilamusi Lyenu).”');
INSERT INTO "silika" VALUES(41,12,'41:12','Basi jwagakwanisye kagumbidwe kakwe kuŵa mawunde nsano nagaŵili m’moŵa gaŵili. Ni jwalimanyisye liwunde lililyose masengo gakwe. Ni twalisalalisye liwunde lya duniya ni nyali (ndondwa) ni kuligosa (nasyo ndondwasyo pakugasomela mashetani ndema jagakusaka kuja kupikanila maloŵe kwinani). Walakweo ni mpango wa (Allah) Jwamachili gakupunda, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(41,13,'41:13','Nambo naga akugalauka (kuleka chikulupi) basi jilani: “Nguntetela ya ipotesi yanti chisawu ipotesi ya ŵanya Adi ni ŵanya Samuda.”');
INSERT INTO "silika" VALUES(41,14,'41:14','Ndema jaŵaichilile ŵanganyao Achimitenga paujo pao ni panyuma pao, (ni kwasalilaga yanti): “Ngasimun’galagatilaga jwinepe ikaŵeje Allah.” Ŵanganyao ŵatiji: “Ambuje ŵetu angasache angatumisye Malaika (ngaŵa jenumanja ŵandu achimijetu), basi chisimu uwwe tukuikana yantumisyidwe nayoyi.”');
INSERT INTO "silika" VALUES(41,15,'41:15','Sano ŵanya Adi ni ŵalikwesyisye pa chilambo mwangaŵajilwa, ni ŵatite: “Ŵani ŵakutupunda uwwe machili?” Ana nganaiona yanti Allah Ajula juŵagumbile ŵanganyao nijwali Jwakwapunda ŵanganyao machili? Ni ŵaliji ŵanganyao ali nkugakanila ma Ȃya Getu.');
INSERT INTO "silika" VALUES(41,16,'41:16','Basi twatumichisye ŵanganyao mbungo jakusisima nnope m’moŵa gamakungu, kuti twape kupasya ilagasyo yakwalusya pa umi wa duniya, sano ilagasyo ya ku Akhera niyaili yakwalusya nnope, soni ŵanganyao ngasakamuchisyidwa.');
INSERT INTO "silika" VALUES(41,17,'41:17','Sano ŵanya Asamuda basi twajongwele, nambo ŵaunonyele ungalola kulekangana ni chongoko, basi chakwembekenye chikokomo cha moto wa ilagasyo yakwalusya ligongo lya yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(41,18,'41:18','Ni twajokwele aŵala ŵaŵakulupilile nikuŵaga ni woga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(41,19,'41:19','Ni (mwakumbusyani) lisiku lyatachisonganganyisyidwa amagongo ŵa Allah wa ku Moto, basi ni tachiŵa ali nkwalidwa m’mikutula-mikutula.');
INSERT INTO "silika" VALUES(41,20,'41:20','Kwikanila ndema jatachiuŵichilila (Motowo) gachachonjela mawiwi gao, meso gao, kwisa soni mapende gao pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(41,21,'41:21','Ni tachiti ku mapende gao: “Ligongo chichi jenumanja kutuchonjela uwwe?” Galakwe gachiti: “Atuŵechetekasyisye Allah Ajula jwaakuchiŵechetesyaga chindu chilichose, Jwalakwe ni juŵan’gumbile jenumanja kundanda kwene, soni Kukwakwe (sano) nkuuchisyidwa.”');
INSERT INTO "silika" VALUES(41,22,'41:22','“Ni nganim’baga nkulisiŵilila kuti gakanchonjela mawiwi genu, meso genu, namuno mapende genu, nambo mwaganichisyaga yanti Allah ngaakumanyilila yejinji mu yankutenda.”');
INSERT INTO "silika" VALUES(41,23,'41:23','“Soni syalakwesyo ni nganisyo syenu syamwaliji nkun’ganichisya M’mbuje gwenu (Allah) sin’jonasile, basi ni nsyuchile kuŵa ŵakupelelwa.”');
INSERT INTO "silika" VALUES(41,24,'41:24','Basi kanga tapilile, Moto tiuŵepe mauto gao, soni kanga taŵende chinonyelo (kwa Allah kuti ŵakoposye ku Motoko ni kwauchisya ku duniya), basi nganaŵa ŵanti ni kunonyeledwa.');
INSERT INTO "silika" VALUES(41,25,'41:25','Ni twaŵichile ŵanganyao (pa duniya pano) achimijao ŵangalekangana nao (ŵachishetani), basi ni ŵasalalichisye yaili m’bujo mwao ni yaili munyuma mwao, basi ni lisimichiche kukwao liloŵe (lyakupata ilagasyo, yalyatite pakusimichika) ku mikutula jajapite paujo pao ja majini ni ŵandu. Chisimu ŵanganyao ŵaliji ŵakwasika.');
INSERT INTO "silika" VALUES(41,26,'41:26','Sano aŵala ŵaakufuulu ŵatite: “Ngasin’jipikanila aji Qur’aniji, soni n’jitendeleje masegwe (ndema jajikusoomedwa) kuti mwine m’be ŵakupunda.” Basi chisimu chene chitwape kupasya aŵala ŵaakufuulu ilagasyo yaukali nnope, ni chitwalipile kwene malipilo gakusakala nnope, ligongo lya itendo yao yangalumbana yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(41,27,'41:27','Basi chisimu chene chitwape kupasya aŵala ŵaakufuulu ilagasyo yaukali nnope, ni chitwalipile kwene malipilo gakusakala nnope, ligongo lya itendo yao yangalumbana yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(41,28,'41:28','Galakwego nimalipilo ga amagongo ŵa Allah; wauli Moto, akwete kupata ŵanganyao mwalakwemo nyumba jandamo, kuŵa malipilo ga yaaŵele ali nkugakanila ma Ȃya Getu.');
INSERT INTO "silika" VALUES(41,29,'41:29','Ni tachiŵecheta aŵala ŵaŵakufuulu kuti: “Ambuje ŵetu! Tulosyani agala (makuga gaŵili) gagatusochelesye ga m’majini ni ŵandu, kuti tugaŵiche pasi pa makau getu, kuti gaŵe gapasi kusyene.”');
INSERT INTO "silika" VALUES(41,30,'41:30','Chisimu aŵala ŵaakutiji: “M’mbuje gwetu ni Allah.” Kaneko nikwimilichika (pa Dini), Achimalaika akwatuluchilaga (ndema jakuwa ni kwasalilaga kuti): “Nkajogopa soni nkadandaula, ni nsengweje ya Mbepo jamwaliji nkupedwa chilanga.”');
INSERT INTO "silika" VALUES(41,31,'41:31','“Uwwe ni isyoŵe yenu pa umi wa duniya ni ku Akhera kwakwe, ni nkwete kupata jenumanja kwalakweko yatiikalaluchileje mitima jenu, soni nkwete kupata kwalakweko yatinkaisacheje.”');
INSERT INTO "silika" VALUES(41,32,'41:32','(Jalakweji ni) mbiiko jakuumila kwa (Allah) Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(41,33,'41:33','Ana ninduni jwakuŵecheta maloŵe gambone nnope kumpunda ajula jwaakuŵilanjila kwa Allah, ni kutendaga yambone, ni kuŵechetaga kuti: “Chisimu une ndili mwa Asilamu!”');
INSERT INTO "silika" VALUES(41,34,'41:34','Yambone ni yakusakala ngaŵa nkulandana. Tyosyani (yakusakala) ni ayila (itendo) yaili yambone (jwantendele yakusakala mum’buchisyani yambone), basi n’jile sayi ajula jwapali pachilikati penumwe ni jwalakwe pana utinda, taŵe nti chisawu nsyoŵe jwapantima.');
INSERT INTO "silika" VALUES(41,35,'41:35','Nambo nganaŵa apedwile yalakweyo ikaŵeje aŵala ŵaapiliile, soni nganaŵa apedwile yalakweyo ikaŵeje ajula jwaakwete liunjili lyekulungwa (lya umbone).');
INSERT INTO "silika" VALUES(41,36,'41:36','Sano naga achintyosyaga kwene (ku masengo gambone) nkutyosya jwalijose jwachishetani, basi lijuuyaani mwa Allah, chisimu Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(41,37,'41:37','Soni mu ilosyo Yakwe (Allah yakulosya ukombosi Wakwe); chilo ni muusi, kwisa soni lyuŵa ni mwesi. Ngasillisujudilaga lyuŵa namuno mwesi, nambo munsujudileje Allah Ajula juŵaigumbile yalakweyo nam’baga jenumanja nkun’galagatila Jwelepejo.');
INSERT INTO "silika" VALUES(41,38,'41:38','Sano naga ŵanganyao akulikwesya, basi (Allah nganaŵa ŵalajile ŵanganyao), aŵala (Achimalaika) ŵali kwa Ambuje ŵenu akasanswejesyaga Jwalakwe chilo ni muusi, soni ŵanganyao ŵangapela.');
INSERT INTO "silika" VALUES(41,39,'41:39','Soni mu ilosyo Yakwe (Allah yakulosya ukombosi Wakwe); niyanti mmwe nkasalionaga litaka lili litalele, nambo pati tutulwisye pa lyalakwelyo mesi (gawula) likasatenganyika ni kutulumbala, chisimu Ajula jwaalijimwisye ni Jwatachijimusya kwene ŵawe. Chisimu Jwalakwe pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(41,40,'41:40','Chisimu aŵala ŵaakugapombotasya ma Ȃya Getu nganaŵa asisiche Kukwetu. Ana ajula jwatakaponyedwe ku Moto ni jwajuli jwambone, kapena ajula jwatakaiche mwantunjelele lisiku lya Kiyama? Panganyani yankusaka, chisimu Jwalakwe (Allah) ichindu yankutenda juŵele Jwakwilolechesya.');
INSERT INTO "silika" VALUES(41,41,'41:41','Chisimu aŵala ŵaachikaaniile chikumbusyo (Qur’an) ndema jachaichilile (chitwape ilagasyo), soni chisimu chalakwecho ni Chitabu chakuchimbichika.');
INSERT INTO "silika" VALUES(41,42,'41:42','Changam’bichilila unami paujo pakwe namuno panyuma pakwe, chakutulusyidwa kuumila kwa (Allah) Jwalunda lwakusokoka, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(41,43,'41:43','Ngaikuŵechetedwa kukwenu (mmwe Muhammadi صلى الله عليه وسلم) ikaŵeje ilayiila yanakuŵechetedwaga kwa Achimitenga paujo penu, chisimu Ambuje ŵenu ni Asyene chikululuko, soni ni Asyene ipotesi yakupoteka nnope.');
INSERT INTO "silika" VALUES(41,44,'41:44','Soni tungajitende kuŵa Qur’an jangaŵa jacharabu, chisimu angatite: “Ligongo chichi ma Ȃya gakwe ngagakusalichisyidwa chenene? Ana (Chitabu nikuŵa) changaŵa chacharabu, sano (Ntume nikuŵa) mwarabu?” Jilani: “Chalakwecho (Chitabucho) kwa aŵala ŵaakulupilile chiŵele chongolo ni chiposyo. Sano aŵala ŵangakukulupilila m’mawiwi mwao mwana usiiwa, nambo soni jalakwe (Qur’anijo) kukwao jiŵele ungalola, ŵanganyao (ngaakupikana, agambile kuŵa nti chisawu) akuŵilanjidwa kuumila pamalo pakutalichila.”');
INSERT INTO "silika" VALUES(41,45,'41:45','Soni chisimu chene Musa twampele chitabu, basi ni wapatikene mwalakwemo ukangani, mwanti lingaŵaga liloŵe lyalyalongolele lyakuumila kwa Ambuje ŵenu (Allah lyangapa ilagasyo yekulungwa nnope ikaŵeje ku Kiyama), chisimu kungajilanyidwe chilikati chao, soni chisimu ŵanganyao ali m’chikaiko chekulungwa chakuchikaichila chalakwecho.');
INSERT INTO "silika" VALUES(41,46,'41:46','Jwatapanganye yambone nikuti akupanganya kwaalikamuchisya nsyene, soni jwatasakasye nikuti akusakasya kwalikonya nsyene, ni nganaŵa Ambuje ŵenu (Allah) ŵanti nikwatenda lupuso ata panandi achikapolo (Ŵakwe).');
INSERT INTO "silika" VALUES(41,47,'41:47','Kukwakwepe (Allah) ni kwaukuwuchisyidwa umanyilisi wa Kiyama. Soni isogosi ngaikasakopoka mu itungatunga mwakwe, ni ngachikasajigala chiilu chachikongwe chilichose kapena kuŵeleka, ikaŵeje kwa umanyilisi Wakwe (Allah), sano lisiku lyataakaaŵilanje ŵanganyao (Allah ni kwawusya kuti): “Ali kwapi ŵamwamwanganyaga nao ŵala?” Ŵanganyao takajile: “Tukummanyisya yanti pangali jwalijose mwetuwe jwaakuŵichila umboni (wanti Mmwe nkwete ŵakum’bwanganya nao).”');
INSERT INTO "silika" VALUES(41,48,'41:48','Ni tiyichasokonechela yaŵaliji nkuilumbanjila kalakala, ni tachisimichisya yanti nganakola ŵanganyao kwakutilila.');
INSERT INTO "silika" VALUES(41,49,'41:49','Mundu (kafili) jwangapela kuŵenda yambone (kwa Allah, Allah ni akumpaga nambo jwangatogolela), sano naga inkwayiye yakusakala basi jukasaŵa jwakwasa ntima, jwakata tama.');
INSERT INTO "silika" VALUES(41,50,'41:50','Soni chisimu naga tuli tumpele kupasya ukoto wakuuma Kukwetu panyuma pa masausyo gagankwayiye, chisimu akasaŵechetaga kuti: “Ayi niyangune (ni jwaanguŵajilwa kupata yeleyi ligongo lyaumanyilisi wangu), soni nganguganichisya yanti Kiyama kuŵa nkwiima. Nambo naga timbuchisyidwe kwa Ambuje ŵangu, nikuti chisimu une tinjipata yambone Kukwakwe.” Basi tuchatagulila kwene aŵala ŵaakufuulu yaŵapanganyisye, ni chitukape kupasya kwene mu ilagasyo yakandapala.');
INSERT INTO "silika" VALUES(41,51,'41:51','Soni naga tuli tumpele ukoto mundu, jukasanyosyaga ni galauchila kumbali, nambo naga inkwayiye yakusakala, basi jukasaŵa ni maduwa gamalewulewu.');
INSERT INTO "silika" VALUES(41,52,'41:52','Jilani: “Ana nkuiona uli najiŵaga (Qur’aniji) jakuumila kwa Allah, kaneka jenumanja ni n’jikaanile (ana ngaŵa nimpotele)? Ana nduni jwali jwakupotela nnope kumpunda ajula jwali mu ukangani wakutalikangana nnope (ni yakuona)?”');
INSERT INTO "silika" VALUES(41,53,'41:53','Chitwalosye imanyilo Yetu ku maupande (gakulekangana-lekangana ga kumawunde ni petaka), kwisa soni mu ŵanganyao achimisyene, mpaka imanyukuche kukwao yanti jele (Qur’aniji) jakuona. Ana ngaikwanila kwa M’mbuje gwenu (Allah) yanti Jwalakwejo pa chindu chilichose juŵele Mboni?');
INSERT INTO "silika" VALUES(41,54,'41:54','Manyililani yanti, chisimu ŵanganyao ali m’chikaiko pa yakusimangana ni M’mbuje gwao (Allah). Manyililani yanti; chisimu Jwalakwe (Allah) chindu chilichose juŵele Nkuchisyungula (kwaachimanya chenene).');
INSERT INTO "silika" VALUES(42,1,'42:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(42,2,'42:2','‘Aîn Sîn Qâf. ');
INSERT INTO "silika" VALUES(42,3,'42:3','Mwele ni mwaakuuwulila (maloŵe) Allah Jwamachili gakupunda, Jwalunda lwakusokoka kukwenu (mmwe Muhammadi صلى الله عليه وسلم) ni (yaŵatite pakuuwula) kwa aŵala (Achimitenga) ŵa paujo penu.');
INSERT INTO "silika" VALUES(42,4,'42:4','Iŵele Yakwe (yosope) yaili kumawunde ni yaili petaka, soni Jwalakwe ni Jwapenani kusyene, Jwankulungwa nnope.');
INSERT INTO "silika" VALUES(42,5,'42:5','Mawunde gakusigalaga panandi kuti gapasuchepasuche pachanya pakwe (ligongo lyakun’jogopa Allah), ni Achimalaika akuluswejesyaga lumbili lwa M’mbuje gwao (Allah), ni kwaŵendelaga chikululuko ŵali petaka. Manyililani yanti chisimu Allah ni Jwali Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(42,6,'42:6','Sano aŵala ŵaalitendele (inepe) yangaŵaga Jwalakwe (Allah) kuŵa akwagosa, Allah ni Nkwaasuunga ŵanganyao (pa itendo yao yangalumbana kuti chiŵalipile), soni mmwe nganim''ba nkwachengeta jwao.');
INSERT INTO "silika" VALUES(42,7,'42:7','Iyyoyopeyo soni tun’juwulile mmwe (Muhammadi صلى الله عليه وسلم) Qur’an jacharabu kuti muntetele Mama jwa misinda (Maaka) ni ŵali mungulugulu mwakwe, ni ntetele ya lisiku lyansongangano lyangali kayikasya ya lyalakwelyo. Likuga line lichiŵa ku Mbepo sano likuga line lichiŵa ku Moto Wakulapuka.');
INSERT INTO "silika" VALUES(42,8,'42:8','Soni Allah angasache angatesile ŵanganyao kuŵa nkutula umpepe, nambo akun’jinjisyaga jwansachile mu ukoto Wakwe, sano ŵalupuso nganakola nkwagosa jwalijose namuno nkwakamuchisya.');
INSERT INTO "silika" VALUES(42,9,'42:9','Ana kapena alitendele ŵanganyao (ŵanepe) ŵangaŵaga Jwalakwe (Allah) kuŵa akwagosa? Nambotu Allah ni Nkugosa nsyesyene, soni Jwalakwe nijwaakwimusyaga ŵawe, soni Jwalakwe pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(42,10,'42:10','Sano mu chindu chilichose chankanganile, basi chiilanyo chakwe chili kwa Allah. (Jilani mmwe Muhammadi صلى الله عليه وسلم): Jwalakwejotu ni Allah M’mbuje gwangu, Kukwakwe njegamiile, soni Kukwakwe nguwujila (pa kutenda toba).');
INSERT INTO "silika" VALUES(42,11,'42:11','(Jwalakwe ni) Nkugumba mawunde ni mataka. Am’biichiile achiŵammakwenu kuumila mu ntundu wenu (wachiŵaandu), nombe ilango (ayiŵiichiile soni) achiŵammakwao (kuumila mu ntundu wao wachinyama), akasan’gumbaga mwakuntupiya mwalakwemo (kupitila mu kusimangana kwa chachilume ni chachikongwe). Pangali chilichose chakulandana ni Jwalakwe (Allah), soni Jwalakwe ni Jwakupikanichisya, Jwakulolechesya.');
INSERT INTO "silika" VALUES(42,12,'42:12','Gaŵele Gakwe makiyi ga kumawunde ni petaka, akasansapangulilaga lisiki jwansachile, ni kunandiya (lisikilyo kwa jwansachile), chisimu Jwalakwe chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(42,13,'42:13','Annamwile jenumanja mu dini yaŵanchisyisye Nuhu, ni ayila yatun’juwulile (mmwe Muhammadi صلى الله عليه وسلم), ni ayila yatwanchisyisye Ibrahima, Musa ni Isa, yanti: “Jimikaani dini (pakuya malamusi gakwe gosope), ni ngasinnekangana mwalakwemo.” Chikusile nnope kwa akuwanganya (Allah ni isanamu chikulupi cha Tauhidi) chankwaŵilanjila ku chalakwecho. Allah akunsagulilaga Kukwakwe jwaakunsaka, ni akun’jongolela Kukwakwe jwaakuwujila (Kukwakwe pakutenda toba).');
INSERT INTO "silika" VALUES(42,14,'42:14','Ni nganalekangana ŵanganyao (Ayuda ni Akilisito pa dini syao) ikaŵeje panyuma pakwaichilila umanyilisi (wa chikulupi cha Tauhidi), (ŵalekangene) ligongo lya litima lyalyaliji chilikati chao, mwanti lingaŵaga liloŵe lyalyalongolele lyakuumila kwa Ambuje ŵenu (lyangapa ilagasyo yekulungwa) kwikanila ndema jekolanjidwe (ja Kiyama), chisimu kungajilanyidwe chilikati chao. Soni chisimu aŵala ŵaŵapedwile kuchitawala Chitabu (Taurat ni Injil) panyuma pao ali m’chikaiko chekulungwa chakuchikaichila chalakwe (chikulupi cha Tauhidi ni Usilamu).');
INSERT INTO "silika" VALUES(42,15,'42:15','Sano ku yalakweyo (yakuya Usilamu) basi ŵilanjilani, ni n’jimilichiche (pakuya malamusi gosope ga Usilamu) mpela yantite pakulamulidwa, soni ngasinkuuya isako yao, ni n’jile: “Nachikulupilile chitabu chilichose chaatulwisye Allah, ni namulidwe kutenda chilungamiko chilikati chenu. Allah ni M’mbuje gwetu nambo soni M’mbuje gwenu. Uwwe tukwete masengo getu, nombe jenumanja nkwete masengo genu. Pangali ukangani chilikati chetuwe ni jenumanja. Allah tachitusonganganya sikati jetu, soni Kukwakwe ni kwakuujila.”');
INSERT INTO "silika" VALUES(42,16,'42:16','Sano aŵala ŵaakukangana pa ya (dini ja) Allah panyuma paakundidwa (dinijo ni ŵandu), ukangani waowo uli wangali mate kwa M’mbuje gwao (Allah), soni pa ŵanganyao pana gasabu, ni akwete kupata ŵanganyao ilagasyo yaukali nnope (lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(42,17,'42:17','Allah ni Ajula jwaatulwisye Chitabu (Qur’an) mwakuonaonape, ni (atulwisye soni) chilungamiko. Sano nichichi chachimmanyisye kuti Kiyama kuŵa nkuŵa jiŵandichile?');
INSERT INTO "silika" VALUES(42,18,'42:18','Akujijanguya (Kiyamajo mwachipongwe) aŵala ŵangakujikulupilila, nambo aŵala ŵaakulupilile ni akujijogopaga, ni akumanyililaga kuti chisimu jalakwejo jakuonaonape. Manyililani yanti: Chisimu aŵala ŵaakukangana pa ya Kiyama ali nkupotela kwakutalikangana nnope (ni yakuona).');
INSERT INTO "silika" VALUES(42,19,'42:19','Allah ni Mpole nnope kwa achikapolo Ŵakwe, akumpaga lisiki jwansachile, soni Jwalakwe ni Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(42,20,'42:20','Jwaŵele ali nkusachilila (ni itendo yakwe) malipilo ga ku Akhera, chitukan’jonjechesye pa malipilo gakwe, ni jwaŵele ali nkusachilila (ni itendo yakwe) malipilo ga pa duniya, chitumpe palakwepo (yayannembele kuti impate), ni ngasakola jwalakwejo ku Akhera liunjili lililyose (lya yambone).');
INSERT INTO "silika" VALUES(42,21,'42:21','Kapena akwete ŵanganyao akwawanganya (ni Allah), ŵaŵalamulile mu dini yanganachisya Allah, soni lingaŵaga liloŵe lya chilekanganyo (lyalyalongolele kala lyangapa ilagasyo yekulungwa ikaŵeje ku Kiyama), kungaajiilanyidwe chilikati chao, soni chisimu ŵalupuso tachipata ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(42,22,'42:22','Tinchaona ŵalupuso (lisiku lya Kiyama) ali niwoga (wakuijogopa ilagasyo) ligongo lya ayila (yangalumbana) yaŵapanganyisye, sano yalakwe (ilagasyoyo) ichapata kwene ŵanganyao. Sano aŵala ŵaakulupilile ni kutendaga yambone, tachiŵa m’Matimbe Gakusengwasya ga ku Mbepo, akwete kupata ŵanganyao yatakaisacheje kwa M’mbuje gwao (Allah), walakweo ni ukoto wekulungwa.');
INSERT INTO "silika" VALUES(42,23,'42:23','Aji ni abali jambone jaakwasengwasya najo Allah achikapolo Ŵakwe aŵala ŵaakulupilile ni kutendaga yambone. Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Une ngangun’juga pa wele (utengau) malipilo galigose, ikaŵeje (ngun’juga) chinonyelo (chakuunonyela une ni nganagasyaga pa masengo ganguga) ligongo lya ulongo (waupali chilikati changu ni jenumanja).” Sano jwatapanganye chambone chilichose, chitun’jonjechesye pa chalakwecho umbone (pa kun’dipila yejinji). Chisimu Allah ni Jwakululuka kusyene, Jwakutogolela nnope (pakulipila yejinji pa itendo yannono).');
INSERT INTO "silika" VALUES(42,24,'42:24','Kapena ŵanganyao akutiji: “(Muhammadi صلى الله عليه وسلم) ansepele Allah unami?” Basi Allah angasache angasiŵile mu ntima mwenu (pakun’diŵasya yosope yanyyimanyi ya m’Qur’an ingaŵe mwansepele unami). Allah ni akufutaga yaukoleko ni kuilimbikasya yakuona ni maloŵe Gakwe. Chisimu Jwalakwe ni Jwakumanyilila yaili m’mitima.');
INSERT INTO "silika" VALUES(42,25,'42:25','Soni Jwalakwe ni jwaakupochelaga toba kuumila kwa achikapolo Ŵakwe, ni jukusimasyaga ilemwa, soni jukumanyililaga yankutenda.');
INSERT INTO "silika" VALUES(42,26,'42:26','Soni akwajangaga (maduwa) aŵala ŵaakulupilile ni kutendaga yambone, ni akwajonjechesyaga mu ukoto Ŵakwe. Sano achimakafili akwete kupata ilagasyo yaukali nnope.');
INSERT INTO "silika" VALUES(42,27,'42:27','Soni ingaŵe kuti Allah jwasapangulile lisiki achikapolo Ŵakwe, angasumbile mpika pachilambo, nambo kuti jukutulusyaga mu chipiimo chaachisachile. Chisimu Jwalakwe kwa achikapolo Ŵakwe juŵele Jwaasimanya abali syosope, Jwakulolechesya.');
INSERT INTO "silika" VALUES(42,28,'42:28','Soni Jwalakwe ni Ajula jwaakutulusyaga wula panyuma panti ŵanganyao ali akatile tama, ni jukwenesyaga ukoto Wakwe, soni Jwalakwe ni Nkugosa nsyesyene, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(42,29,'42:29','Soni mu ilosyo Yakwe (yakulosya ukombosi Ŵakwe), gumba kwa mawunde ni mataka, ni yaumi iliyose yakuulukutaga yaayijenesye munamuŵilimo, soni Jwalakwe ya kuisonganganya yalakweyo patachisakapo juŵele Jwakombola kusyene.');
INSERT INTO "silika" VALUES(42,30,'42:30','Soni chipato chilichose chachimpatile, basi ligongo lya yagapanginyisye makono genu, ni jukuisimasyaga yaijinji.');
INSERT INTO "silika" VALUES(42,31,'42:31','Soni jenumanja (achimakafili) nganim’ba munnepelekasyisye (Allah kumpa ipotesi) pa chilambo, soni nganinkola jenumanja kaligoselo ata nkulupusyo jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(42,32,'42:32','Soni mu ilosyo Yakwe (ya kulosya chindimba Chakwe kwa jenumanja), yombo yaikwenda m’mbwani yanti mpela matumbi (ukulu wakwe).');
INSERT INTO "silika" VALUES(42,33,'42:33','Naga juli jusachile mpaka ajituulasye mbungo, ni mpaka igambe kuŵa (yomboyo) chiimile pachanya pakwe (pa mbwani). Chisimu mu yalakweyo muŵele mwana imanyisyo kwa jwalijose jwali jwakupilila nnope, jwakutogolela kwejinji.');
INSERT INTO "silika" VALUES(42,34,'42:34','Kapena kuijonanga (yomboyo nikwamisya achinsyeneo) ligongo lya (yangalumbana) yapanganyisye, ni jukuisimasyaga yaijinji.');
INSERT INTO "silika" VALUES(42,35,'42:35','Ni akumanyilila aŵala ŵaakukangana pa ma Ȃya Getu (yanti): Nganakola ŵanganyao kwakutilila.');
INSERT INTO "silika" VALUES(42,36,'42:36','Sano chindu chilichose chim’bele nkupedwa, basi chigambile kuŵa chisengwasyo (channono) cha umi wa duniya, nambo yaili kwa Allah ni yambone nnope soni yakwendelechela kwa aŵala ŵaakulupilile, soni kwa M’mbuje gwao pe (Allah) akwegamilaga.');
INSERT INTO "silika" VALUES(42,37,'42:37','Ni aŵala ŵaakuliŵambasyaga sambi syekulungwakulungwa (mpela kuwulaga mundu mwangali ligongo lyakwitichika ni Dini), kwisa soni yakunyalaya (mpela chikululu), soni naga ali agasabiche basi akasakululukaga.');
INSERT INTO "silika" VALUES(42,38,'42:38','Ni aŵala ŵan’jitiche M’mbuje gwao (Allah pa chilichose chaŵalamwile), ni kwimikaga Swala mwakolosya, soni indu yao ikasaŵaga yakutaŵana mashauli sikati jao, ni ichindu yatwapele akasatolaga.');
INSERT INTO "silika" VALUES(42,39,'42:39','Ni aŵala ŵanti naga uli ŵapatile uwwanga akasaliuchisyaga.');
INSERT INTO "silika" VALUES(42,40,'42:40','Sano malipilo ga chakusakala ni chakusakala chanti mpela chichocho, nambo jwaachisimasyisye (chakusakalacho) ni kupikanganana (najo nkuchitendajo), basi malipilo gakwe gali kwa Allah. Chisimu Jwalakwe (Allah) ngaakwanonyelaga ŵalupuso.');
INSERT INTO "silika" VALUES(42,41,'42:41','Sano chisimu jwaaliuchisye panyuma pa uwwanga waatendeledwe, basi pa ŵanganyao pangali litala (lyakwagambila).');
INSERT INTO "silika" VALUES(42,42,'42:42','Nambo litala (lyachigambilo) lili pa aŵala ŵaakwatenda lupuso ŵandu ni kutendaga uwwanga pa chilambo mwangaŵajilwa, ŵanganyao akwete kupata ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(42,43,'42:43','Nambo chisimu jwaapilile, ni kululuka, chisimu yalakweyo ni yaili kwene mu ichindu yakusosekwa kuŵichila ntima.');
INSERT INTO "silika" VALUES(42,44,'42:44','Sano jwaakunnechelela Allah kusokonechela, basi nganakola nkun’gosa panyuma pakwe. Ni tinchaona ŵalupuso ndema jatachiiona ilagasyo achitiji: “Ana pana litala lyakuujila (ku duniya)?”');
INSERT INTO "silika" VALUES(42,45,'42:45','Ni tinchaona ŵanganyao ali nkupelechedwa ku walakwe (Motowo), ali ateteŵele ligongo lya kunyosyeka, ali nkulola ni liso lyakusisa (chamunjenje meso). Sano aŵala ŵaŵakulupilile tachiti: “Chisimu ŵakwasika ni aŵala ŵaalijasikasyisye (Mbepo) achimisyene kwisa soni maŵasa gao (lelo) lisiku lya Kiyama (ligongo lya itendo yao yangalumbana yaŵaliji nkutenda ku duniya), manyililani yanti; chisimu ŵalupuso taŵe mu ilagasyo yakwendelechela.”');
INSERT INTO "silika" VALUES(42,46,'42:46','Soni ngasakola ŵanganyao akwagosa ŵaliose ŵatachachenjela jwangaŵaga Allah. Sano jwannechelele Allah kusokonechela, basi nganakola litala lililyose (lyakongochela pa duniya ni kokochela ku Akhera).');
INSERT INTO "silika" VALUES(42,47,'42:47','Mun’jitikani M’mbuje gwenu (Allah), nkaniliyiche lisiku lyakuumila kwa Allah lyangali jwakuliusya. Ngasinkola ŵanganyammwe kwakutilila lyele lisikulyo, soni ngasinkola kaanila kulikose (pa yamwapanganyisye).');
INSERT INTO "silika" VALUES(42,48,'42:48','Sano naga akugalauka (kuleka chikulupi cha Tauhidi), basi nganituntumisya (mmwejo Muhammadi صلى الله عليه وسلم) kuŵa jwakwalolela ŵanganyao, nganiyiŵa kukwenu ikaŵeje kulungusya utenga. Soni chisimu Uwwe tuli tumpele mundu kupasya ukoto kuumila Kukwetu, akasausengwelaga (mwangali kutogolela), nambo naga chapatile chakusakala chilichose ligongo lya yagalongwesye makono gao, basi chisimu mundu jukasaŵa jwakufulu.');
INSERT INTO "silika" VALUES(42,49,'42:49','Uŵele wakwe Allah uchimwene wa kumawunde ni petaka. Jukasagumba yajukusaka. Akumpaga jwansachile ŵanache ŵachikongwe, ni akumpaga jwansachile ŵanache ŵachilume.');
INSERT INTO "silika" VALUES(42,50,'42:50','Kapena kwawanganya ŵanache ŵachilume ni ŵachikongwe (kwa mundu jumo), ni akuntendaga jwansachile kuŵa jwangaŵeleka. Chisimu Jwalakwe ni Jwakumanyilila nnope, Jwakombola kusyene.');
INSERT INTO "silika" VALUES(42,51,'42:51','Soni nganiyiŵa kwa mundu yanti ni kum’bechetekasya jwalakwejo Allah, ikaŵeje ni maloŵe gakuuwulidwa (mu ntima mwakwe), kapena kusyeto kwa chakusiŵilila (gambaga kupikana maloŵe ga Allah mwangali kum’bona), kapena kuntumisya Ntenga (Jibulilu) basi ni kun’juwulila yaakuisaka (Allah) kwa lisosa Lyakwe. Chisimu Jwalakwe ni Jwapenani kusyene, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(42,52,'42:52','Iyyoyopeyo soni tun’juwulile mmwe (Muhammadi صلى الله عليه وسلم) maloŵe (Qur’an) kwa ulamusi Wetu. Nganimmanyililaga kuti Chitabu ni chichi namuno chikulupi. Nambo tujitesile jalakwe (Qur’an) kuŵa lilanguka lyatukun’jongolela nalyo jwatukunsaka mwa achikapolo Ŵetu. Soni chisimu mmwe (Muhammadi صلى الله عليه وسلم) nkongolelaga kwene kwitala lyagoloka.');
INSERT INTO "silika" VALUES(42,53,'42:53','Litala lya Allah Ajula jwaili Yakwe (Jikape yosope) yaili kumawunde ni yaili petaka. Manyililani yanti kwa Allah ikuujila ichindu yosope.');
INSERT INTO "silika" VALUES(43,1,'43:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(43,2,'43:2','Nguchilumbila Chitabu (cha Qur’an) chakusalichisya.');
INSERT INTO "silika" VALUES(43,3,'43:3','Chisimu Uwwe tujitesile jalakweji kuŵa Qur’an jacharabu kuti jenumanja mpate kugataga mu lunda (mate gakwe chenene).');
INSERT INTO "silika" VALUES(43,4,'43:4','Soni chisimu jalakwe (Qur’an) m’Mama jwa chitabu (Al-Lauh Al-Mahfȗz) jwali Kukwetu jili japenani kusyene, jegumbale lunda lwakusokoka.');
INSERT INTO "silika" VALUES(43,5,'43:5','Ana tuntyochesye ŵanganyammwe chikumbusyo (Qur’an) kutyosya kwene (kwakuleka kunnamula ni kunkanya najo) ligongo lyanti n’di ŵakusumba mpika (pa ungalumbana)?');
INSERT INTO "silika" VALUES(43,6,'43:6','Ana Achimitume ŵalingwa ŵatwatumiisye mu (ŵandu) ŵandanda!');
INSERT INTO "silika" VALUES(43,7,'43:7','Soni nganiŵaichililaga ntume jwalijose ikaŵeje ŵaliji nkuntenda chipongwe.');
INSERT INTO "silika" VALUES(43,8,'43:8','Basi ni twajonasile (ŵandu ŵakala) ŵakupelenganya nnope machili kwapunda ŵanganyao (Makuraishi), ni ipite ilandanyo ya (ŵandu) ŵandanda (yaŵatite kapedwe ilagasyo nambo Makuraishi ngagakukulupilila).');
INSERT INTO "silika" VALUES(43,9,'43:9','Soni naga n’di mwawusyisye kuti: “Ana nduni juŵagumbile mawunde ni mataka?” Chisimu tajile: “Jwagagumbile (Allah) Jwamachili gakupunda, Jwakumanyilila nnope.”');
INSERT INTO "silika" VALUES(43,10,'43:10','Ajula jwantendele litaka kuŵa chakutandika, ni am’bichile palakwepo matala kuti jenumanja n’dijongolele kwakuja.');
INSERT INTO "silika" VALUES(43,11,'43:11','Ni Ajula jwaatulwisye kuumila kwinani mesi mwan’dingo (wakuŵajilwa), basi ni tujimwisye nago chilambo chachiwe, iyyoyopeyo soni tinchikoposyedwa (m’malembe).');
INSERT INTO "silika" VALUES(43,12,'43:12','Ni Ajula jwaagumbile chachilume ni chachikongwe mu ichindu yosope, ni am’bichile yombo ni ilango yankuikwela.');
INSERT INTO "silika" VALUES(43,13,'43:13','Kuti ntamilichicheje pa migongo pakwe, kaneka ni nkumbuchileje chindimba cha M’mbuje gwenu (Allah) pati ntamilichiche pachanya pakwepo, ni kuŵechetaga kuti: “Kuswejela ni Kwakwe Ajula (Allah) jwaatujepepachisye achi, soni uwwe nga nganitukombola kulumbikana nacho (mwamachili getu).”');
INSERT INTO "silika" VALUES(43,14,'43:14','“Soni chisimu uwwe kwa Ambuje ŵetu (Allah) chituwujile kwene.”');
INSERT INTO "silika" VALUES(43,15,'43:15','Ni am’bichile (Allah) mwa achikapolo Ŵakwe chipande (pakuŵecheta kuti: Achimalaika ŵanache ŵa Allah). Chisimu mundu ni mmakaani jwakuonechela.');
INSERT INTO "silika" VALUES(43,16,'43:16','Kapena (Allah) alitendele ŵanache ŵachikongwe mu yaagumbile, sano jenumanja ni ansagulile ŵanache ŵachilume?');
INSERT INTO "silika" VALUES(43,17,'43:17','Soni naga jumo jwao juli jupedwile abali ja ayila yannandanyichisye (Allah) Jwaukoto wejinji (yakola mwanache jwankongwe), basi ngope jakwe jikasapiilila, soni gakasan’gumbala madandausi.');
INSERT INTO "silika" VALUES(43,18,'43:18','Ha! Ana jwaaleledwe mu ipeto, soni jwalakwe pa ukangani nganijuŵa juŵechete mwakupikanika (ni jwankumpa Allah, ŵali ŵanache ŵachikongwe)?');
INSERT INTO "silika" VALUES(43,19,'43:19','Ni agatesile Malaika gagali achikapolo ŵa (Allah) Jwaukoto wejinji kuŵa achakongwe. Ana ŵakuweni gumbidwa kwao? Umboni wao tiulembedwe, ni tachiwusyidwa.');
INSERT INTO "silika" VALUES(43,20,'43:20','Ni akutiji: “(Allah) Jwaukoto wejinji angasache (kuti tukagalagatila isanamu) nga nganituigalagatila.” Nganakola ŵanganyao umanyilisi uliose pa yele (yaakuŵechetayo), nganaŵa ŵanganyao ikaŵeje agamba kusepelela unami basi.');
INSERT INTO "silika" VALUES(43,21,'43:21','Kapena twapele ŵanganyao chitabu nkanichiŵe achi (cha Qur’an), sano ŵanganyao ni akulikamulichisya mu chalakwecho (akuipata mwalakwemo yaakuŵechetayo)?');
INSERT INTO "silika" VALUES(43,22,'43:22','(Ngwamba), nambo akutiji: “Chisimu uwwe twasimene achatati ŵetu ali pa nyendele ja dini (jagalagatila isanamu), sano uwwe nowe tukulondola sajo syao.”');
INSERT INTO "silika" VALUES(43,23,'43:23','Soni iyyoyopeyo nganitutumisyeje paujo penu (mmwe Muhammadi صلى الله عليه وسلم) m’musi uliose nkutetela jwalijose, ikaŵeje ŵaŵechetaga ŵakulipatila ŵao kuti: “Chisimu uwwe twasimene achatati ŵetu ali pa nyendele ja dini (jagalagatila isanamu), sano uwwe nowe tukuya sajo syao.”');
INSERT INTO "silika" VALUES(43,24,'43:24','(Nkutetela jwao) jwatite: “Namuno kuti nam’bichilile ni (nyendele) jakongoka nnope kupunda jamwasimene nao achatati ŵenu (tinkuyejepe nyendele ja achatati ŵenujo)?” Ŵanganyao ŵatite: “Chisimu uwwe yantumisyidwe nayoyi tukuikaana.”');
INSERT INTO "silika" VALUES(43,25,'43:25','Basi ni twapele ipotesi ŵanganyao. Basi nnole mujaŵelele mbesi ja akukanila!');
INSERT INTO "silika" VALUES(43,26,'43:26','Soni (kumbuchilani) katema kajwaŵechete Ibrahima kwa babagwe ni ŵandu ŵakwe kuti: “Chisimu une ndalikangene nayo yankuigalagatila jenumanja.”');
INSERT INTO "silika" VALUES(43,27,'43:27','“Ikaŵeje Ajula (Allah) juŵangumbile, basi chisimu Jwalakwe tanjongole.”');
INSERT INTO "silika" VALUES(43,28,'43:28','Ni jwalitesile lyele (liloŵe lya Tauhidilyo) kuŵa liloŵe lyakwendelechela mu uŵelesi wakwe kuti ŵanganyao awujileje (ku lyele liloŵelyo ndema ni katema).');
INSERT INTO "silika" VALUES(43,29,'43:29','Nambo nasengwasyisye ŵanganyaŵa ni achatati ŵao (pangapa ilagasyo chitema), mpaka yaichiiliile yakuona (Qur’an) ni Ntenga jwakusalichisya (Muhammadi صلى الله عليه وسلم).');
INSERT INTO "silika" VALUES(43,30,'43:30','Sano ndema ja yaichiiliile yakuona (Qur’an) ŵatite: “Awu ni usaŵi, soni chisimu uwwe walakwewu tukuukaana.”');
INSERT INTO "silika" VALUES(43,31,'43:31','Ni ŵatite: “Ligongo chichi ngatulusyidwa aji Qur’an kwa mundu jwankulungwa jwa m’misi jiŵili (Maaka kapena Twaif)?”');
INSERT INTO "silika" VALUES(43,32,'43:32','Ana ŵanganyao ni ŵaakugaŵanya ukoto wa Ambuje ŵenu? Uwwe tugaŵenye sikati jao yakulamila yao pa umi wa duniya, ni twanyakwile ŵane mwa jamanjao mauchimbichimbi pachanya pa ŵane, kuti ŵane mwa jamanjao ŵatende ŵane kuŵa ŵamasengo ŵao. Sano ukoto wa Ambuje ŵenu (Allah jajili Mbepo) niwauli wambone nnope kupunda ya (duniya) yaakusonganganya.');
INSERT INTO "silika" VALUES(43,33,'43:33','Soni ingaŵaga kuti ŵandu mpaka aŵe nkutula umpepe (wachikafili), ŵandu ŵaakunkaana (Allah) Jwaukoto wejinji tungaŵichile misakasa ja silifa ja majumba gao, kwisa soni makwelelo gaakasakwelelaga (nombe nago kuŵa gasilifa).');
INSERT INTO "silika" VALUES(43,34,'43:34','Ni majumba gao gakakwete matanga (gasilifa), kwisa soni itengu yauchimbichimbi yaakwegamagapo (ikaliji yasilifa).');
INSERT INTO "silika" VALUES(43,35,'43:35','Ni yakusalalisya yagolodi. Nambo yosope yeneyo nga nganiyiŵa chilichose ikaŵeje chisengwasyo cha umi wa duniya, sano Akhera jajili kwa Ambuje ŵenu ni jaŵakun’jogopa (Allah pe).');
INSERT INTO "silika" VALUES(43,36,'43:36','Sano jwatagalauche kuleka kunkumbuchila (Allah) Jwaukoto wejinji (pakujikwanjila Qur’an), chitum’bichile shetani jwataŵe n’jakwe jwangalekangana najo.');
INSERT INTO "silika" VALUES(43,37,'43:37','Soni chisimu galakwe (mashetani) gakwasiŵililaga jamanjao kwitala (lyagoloka), ni akuganichisyaga kuti chisimu ŵanganyao ali ŵakongoka.');
INSERT INTO "silika" VALUES(43,38,'43:38','Kwikanila ndema jatachituichilila (lisiku lya Kiyama, nikwinjisyidwaga ku Moto) tachiti (pakunsalila shetani): “Ha! Nabolaga chilikati changune nomwe ungapagwe nkuuli wa chilikati cha kungopoko ni kungapililo.” Kaje kusakala n’jakwe jwangalekangana najo jwakwe!');
INSERT INTO "silika" VALUES(43,39,'43:39','Soni ngakunkamuchisya lelo kuŵa kwenu yalumo mu ilagasyo (jenumanja ni mashetani genu), ligongo lya lupuso lwamwalitendele (pagalagatila isanamu).');
INSERT INTO "silika" VALUES(43,40,'43:40','Ana mmwe (Muhammadi صلى الله عليه وسلم) mpaka mwapikanasye asiiwa, kapena kwajongolela ŵangalola ni ŵali mkupotela kwakuonechela?');
INSERT INTO "silika" VALUES(43,41,'43:41','Sano naga tuntyosye kwene (m’duniya muno nkanitunnosye ilagasyo yao), basi (nkakayichila ya kwalagasya kwao), chisimu Uwwe ŵanganyao tuchauchisya mbusyo (syakupoteka nnope).');
INSERT INTO "silika" VALUES(43,42,'43:42','Kapena chitunnosye kwene (ni meso genu nkanim’bwe) ayila (ilagasyo) yatwapele chilanga, basi chisimu Uwwe tukwete ukombosi pa ŵanganyao.');
INSERT INTO "silika" VALUES(43,43,'43:43','Basi ikamulisyani ayila yayijuwulidwe kukwenu (mmwe Muhammadi صلى الله عليه وسلم), chisimu mmwejo n’di petala lyagoloka.');
INSERT INTO "silika" VALUES(43,44,'43:44','Soni chisimu jalakwe (Qur’an) jiŵele chikumbusyo kukwenu ni kwa ŵandu ŵenu, ni tinchiyika kuwusyidwa (ya jalakwe).');
INSERT INTO "silika" VALUES(43,45,'43:45','Soni mwawusyani (mmwe Muhammadi صلى الله عليه وسلم) ŵatwatumisye paujo penu mwa Achimitenga Ŵetu kuti: “Ana twaŵisile milungu jine jangaŵaga (Allah) Jwaukoto wejinji kuti jigalagatilweje?”');
INSERT INTO "silika" VALUES(43,46,'43:46','Soni chisimu twantumisye Musa ni ilosyo Yetu kwa Firiauna ni nduna syakwe, basi ni jwatite: “Chisimu une ni Ntenga jwa M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(43,47,'43:47','Basi katema kajwaichilile ni ilosyo Yetu, jele ndemajo ŵanganyao ŵaliji nkuiseka.');
INSERT INTO "silika" VALUES(43,48,'43:48','Ni nganitwalosyaga chilosyo chilichose ikaŵeje chaliji chekulungwa kupunda chijakwe (chapaujo pakwe), ni twakwembekenye ni ilagasyo kuti awujile (Kukwetu).');
INSERT INTO "silika" VALUES(43,49,'43:49','Ni ŵatite: “E wao chensaŵi! Tuŵendelani kwa Ambuje ŵenu ligongo lya chilanga Chakwe chaŵataŵene namwe (chanti chatutyochesye ilagasyo naga tukulupilile), chisimu uwwe chitujongoche.”');
INSERT INTO "silika" VALUES(43,50,'43:50','Nambo ndema jatwaliji nkwatyochesya ilagasyoyo, papopo ŵanganyao ŵaliji nkukasa chilanga mwanakamo.');
INSERT INTO "silika" VALUES(43,51,'43:51','Ni jwagombelesye Firiauna ku ŵandu ŵakwe juchitiji: “E ŵandu ŵangu! Ana nganiuŵa wangu uchimwene wa Misra, ni asi sulo syasikujilima chapasi pangu (nganisiŵa syangu)? Ana ngankulola?”');
INSERT INTO "silika" VALUES(43,52,'43:52','“Kapena une ni jwandi jwambone nnope kumpunda aju (Musa) jwakunyosyeka, soni jwangaŵa nkupakombola kuŵecheta mwakupikanika chenene?”');
INSERT INTO "silika" VALUES(43,53,'43:53','“Nambi uli ngaponyeledwa jwalakwe ibangili yagolodi, kapena Malaika kwika pamo ni jwalakwe ni kunkuyaga (palipose pajukwenda naga juli ntume nsyesyene)?”');
INSERT INTO "silika" VALUES(43,54,'43:54','Basi ŵaloŵesye ŵandu ŵakwe, nombe nao ni ŵan’jiitiiche, chisimu ŵanganyao ŵaliji ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(43,55,'43:55','Basi ndema jaŵatupikasyisye ntima, twapele ilagasyo, ni twamisyisye wosope ŵene.');
INSERT INTO "silika" VALUES(43,56,'43:56','Basi ni twatesile kuŵa akulongolela (kupedwa ipotesi, kuti aŵe chamuko) nambo soni chilandanyo ku ŵakwisa munyuma.');
INSERT INTO "silika" VALUES(43,57,'43:57','Sano ndema ja chapelechedwe chilandanyo cha (Isa) mwanache jwa Maryam, papopo ŵandu ŵenu ŵachisechile mwakwesya maloŵe.');
INSERT INTO "silika" VALUES(43,58,'43:58','Ni ŵatite: “Ana milungu jetu ni jajili jambone kapena jwalakwe (Isa)?” Nganapeleka chele chilandanyocho kukwenu ikaŵeje mwakutataŵanape, nambo ŵanganyao ni ŵandu amakaani.');
INSERT INTO "silika" VALUES(43,59,'43:59','Nganaŵa jwalakwe (Isa) ikaŵeje kapolo jwatwampele ukoto (wa Utume), ni twantesile kuŵa chilandanyo ku ŵanache ŵa Israila (chakulosya ukombosi Wetu, pakupagwa jwangali babagwe).');
INSERT INTO "silika" VALUES(43,60,'43:60','Soni tungasache tungatesile Achimalaika kuŵa ŵawinjila pa chilambo mmalo mwa jenumanja (panyuma pakun’jonanga).');
INSERT INTO "silika" VALUES(43,61,'43:61','Soni chisimu jwalakwe (Isa) ni chimanyilo cha Kiyama (kwika kwa Isa kumbesi kwa duniya kuchilosya kuti Kiyama jiŵandichile), basi ngasinkaichila ata panandi ya yalakweyo, ni munguye Une (Allah), ali ni litala lyagoloka.');
INSERT INTO "silika" VALUES(43,62,'43:62','Soni akansiŵilikanganya ata panandi shetani, chisimu jwalakwe kukwenu ni mmagongo jwakuonechela.');
INSERT INTO "silika" VALUES(43,63,'43:63','Sano ndema jajwaiche Isa ni ilosyo yakuonechela, jwatite: “Nam’bichilile ni lunda lwakusokoka (utume), nambo soni kuti nannondechesye ine mwa ayila yankutindana pa yalakweyo, basi mun’jogopani Allah ni mumbikanile une.”');
INSERT INTO "silika" VALUES(43,64,'43:64','“Chisimu Allah ni M’mbuje gwangu soni M’mbuje gwenu, basi mun’galagatilani, ali ni litala lyagoloka.”');
INSERT INTO "silika" VALUES(43,65,'43:65','Sano mikutula (jachikilisito) jatindene chilikati chao (pa ya Isa, ŵane kutiji: nnungu, ŵane: mwanache jwa Nnungu, ŵane: jumpepe mwa milungu jitatu). Basi ipotesi niyao aŵala ŵalitesile lupuso (pakunnambuchisya Isa ikaliyo, takapate) ilagasyo ya lisiku lyakupoteka kusyene.');
INSERT INTO "silika" VALUES(43,66,'43:66','Ana akwembecheya inepe yangaŵaga Kiyama kuti jaichilile mwachisupuchisya ŵanganyao akakumanyilila?');
INSERT INTO "silika" VALUES(43,67,'43:67','Asyoŵe lyele lisikulyo tachiŵengana ŵane ni ŵane, ikaŵeje ŵawoga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(43,68,'43:68','(Ichiŵechetedwa ku ŵakulupilila kuti): E achikapolo Ŵangu! Ngam’ba ni woga lelo, soni jenumanja ngan’dandaula.');
INSERT INTO "silika" VALUES(43,69,'43:69','Aŵala ŵaŵakulupilile ma Ȃya Getu ni kuŵa Asilamu.');
INSERT INTO "silika" VALUES(43,70,'43:70','Jinjilani ku Mbepo, jenumanja ni achiŵammakwenu ŵakwe tim’be nkusengwasyidwa.');
INSERT INTO "silika" VALUES(43,71,'43:71','Tisikaŵeje silinkwasyungula mbale syagolodi kwisa soni migao (jagolodi), ni timukaŵe mwana mwalakwemo yatiyikalaluchileje mitima, ni kusengwasya meso, soni jenumanja mwalakwemo tinkaŵe ŵandamo.');
INSERT INTO "silika" VALUES(43,72,'43:72','Soni jalakweji ni Mbepo ajila jampedwile kujitawala ligongo lya ayila yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(43,73,'43:73','Nkwete kupata jenumanja mwalakwemo isogosi yejinji yatin’dyeje.');
INSERT INTO "silika" VALUES(43,74,'43:74','Chisimu akuleŵa tachiŵa ŵandamo mu ilagasyo ya Jahannama.');
INSERT INTO "silika" VALUES(43,75,'43:75','Ngasapedwa lipesa lyakupumula, nambo soni ŵanganyao tachiŵa ŵakata tama mwalakwemo (jakupata yambone iliyose).');
INSERT INTO "silika" VALUES(43,76,'43:76','Ni nganitwatenda lupuso Uwwe, nambo ŵanganyao ni ŵaŵaliji nkulitenda lupuso.');
INSERT INTO "silika" VALUES(43,77,'43:77','Ni tachim’bilanga (nkuwusunga Moto kuti): “E Malik! Ambuje ŵenu (Allah) agambe kutupa chiwa (kuti tupumule ku ilagasyoyi).” (Malik) tachiti (pakwanga panyuma pakupita yaka sausande): “Chisimu jenumanja tintame (m’mumu).”');
INSERT INTO "silika" VALUES(43,78,'43:78','Chisimu pamasile patum’bichilile ni yakuona, nambo ŵajinji mwa jenumanja yakuonayo akuichima.');
INSERT INTO "silika" VALUES(43,79,'43:79','Kapena ŵanganyao ataŵikenye malindi (gakusaka kum’bulaga Muhammadi صلى الله عليه وسلم)? Basi chisimu Uwwe nowe tutaŵikenye (yakuwesesya malindi gaogo).');
INSERT INTO "silika" VALUES(43,80,'43:80','Kapena akuganichisya yanti Uwwe ngatukupikana asili syao ni mitemela jao? Kwapi! (Tukupikana twachimisyewe), ni Achimitenga Ŵetu (Achimalaika) ali kukwao, akulemba.');
INSERT INTO "silika" VALUES(43,81,'43:81','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ingaŵe kuti (Allah) Jwaukoto wejinji jwana mwanache, basi une ngaliji jwandanda kun’galagatila.”');
INSERT INTO "silika" VALUES(43,82,'43:82','Aswejele M’mbuje jwa kumawunde ni petaka, M’mbuje jwa Arishi (Chindanda Chaulungu) ku yaakusimba (achimakafili yanti Allah jwana mwanache).');
INSERT INTO "silika" VALUES(43,83,'43:83','Basi mwalekani agambeje kuweweta yangali mate ni kupita nching''anda mpaka chasimangane nalyo Lisiku lyao alila lyaakuŵa ali nkupedwa chilanga (cha kwisa kwakwe).');
INSERT INTO "silika" VALUES(43,84,'43:84','Soni Jwalakwe (Allah) ni Ajula jwali Jwagalagatidwa kwinani, ni Jwagalagatidwa petaka, soni Jwalakwe ni Jwalunda lwakusokoka, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(43,85,'43:85','Soni anyakwiche (pa uchimbichimbi ni upile) Ajula (Allah) jwauli Wakwe uchimwene wa kumawunde ni petaka ni yaili chilikati chakwe, soni Kukwakwe ni kwauli umanyilisi wa Kiyama, soni Kukwakwe tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(43,86,'43:86','Soni aŵala ŵakwalumbanjila ŵangaŵaga Jwalakwe (Allah) nganakola machili gakuchondelela, ikaŵeje jwaatendele umboni yakuona aku achimanyililaga.');
INSERT INTO "silika" VALUES(43,87,'43:87','Soni naga n’di mwawusyisye kuti: Nduni juŵagumbile ŵanganyao? Chisimu chene tajile: “Allah.” Nambi akugalausyidwa chinauli (kwakuleka kun’galagatila Juŵaagumbile)?');
INSERT INTO "silika" VALUES(43,88,'43:88','Soni uŵechete wakwe (Muhammadi صلى الله عليه وسلم ndaŵi ni katema ukuŵa wanti): “E Ambuje ŵangu! Chisimu ŵanganyaŵa ni ŵandu ŵangaakukulupilila (kwali ndende nao chichi)!”');
INSERT INTO "silika" VALUES(43,89,'43:89','Basi mwalekani, ni m’becheteje (maloŵe ga) ntendele. Basi sampano tayimanye (yakuyichisya ya ukafili wao). ');
INSERT INTO "silika" VALUES(44,1,'44:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(44,2,'44:2','Nguchilumbila Chitabu (cha Qur’an) chakusalichisya.');
INSERT INTO "silika" VALUES(44,3,'44:3','Chisimu Uwwe tujitulwisye jalakwe (Qur’an) m’Chilo Chaupile, chisimu Uwwe tuŵele Akutetela.');
INSERT INTO "silika" VALUES(44,4,'44:4','Mu chele (chilochi) chikasaŵichidwaga m’mangwakwe chindu chilichose chalunda lwakusokoka. ');
INSERT INTO "silika" VALUES(44,5,'44:5','Kwa ulamusi wakuuma Kukwetu, chisimu Uwwe tuŵele Ŵakutumisya (Achimitenga).');
INSERT INTO "silika" VALUES(44,6,'44:6','Kuŵa ukoto wakuumila kwa Ambuje ŵenu (Allah), chisimu Jwalakwe ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(44,7,'44:7','M’mbuje jwa kumawunde ni petaka ni yaili chilikati chakwe, nam’baga jenumanja n’di ŵakusimichisya (chikulupi chenu tinsimichisye yele).');
INSERT INTO "silika" VALUES(44,8,'44:8','Pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwelepejo, jukupelekaga umi ni chiwa, M’mbuje gwenu nambo soni M’mbuje jwa achatati ŵenu ŵandanda.');
INSERT INTO "silika" VALUES(44,9,'44:9','Nambo ŵanganyao (achimakafili) ali m’chikaiko, agamba ng’anda (pakuya isako yao yakusakala).');
INSERT INTO "silika" VALUES(44,10,'44:10','Basi lijembecheyani lisiku lyalichiyika liwunde ni lyosi lyakuloleka.');
INSERT INTO "silika" VALUES(44,11,'44:11','Lyalichawunichila ŵandu, (ni tachiti): “Ayi ni ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(44,12,'44:12','“Ambuje ŵetu! Tukungunukulilani ilagasyo, chisimu uwwe chituŵe ŵakulupilila.”');
INSERT INTO "silika" VALUES(44,13,'44:13','(Ana lelo) kumbuchila chikwakamuchisye chichi ŵanganyao, kutendaga pamasile pajwaichilile Ntenga jwakusalichisya!');
INSERT INTO "silika" VALUES(44,14,'44:14','Kaneka ŵantundumalile ni ŵatite: “Jutekwiganyisyidwa (yalakweyi, ngaŵa yakutyochela kwa Allah, nambo soni) jwamasoka.”');
INSERT INTO "silika" VALUES(44,15,'44:15','Chisimu Uwwe tuli tutite tunkungunukulile ilagasyo panandipe, chisimu ŵanganyammwe mpaka m’bujile (ku ukafili wenu).');
INSERT INTO "silika" VALUES(44,16,'44:16','(Mwakumbusyani) lisiku lyatuchikwembekanya kwembekanya kwekulungwa (pakupeleka ipotesi kwa achimakafili), chisimu Uwwe ni ŵakuusya mbusyo (syakupoteka kusyene).');
INSERT INTO "silika" VALUES(44,17,'44:17','Soni chisimu chene nkanayiche ŵanganyao (Makuraishi) twalinjile mayeso ŵandu ŵa Firiauna, ni jwaichilile Ntenga jwakuchimbichika (Musa).');
INSERT INTO "silika" VALUES(44,18,'44:18','(Ni jwatite): “Mumbani achikapolo ŵa Allah (ŵanache ŵa Israila), chisimu une kukwenu ni Ntenga jwakulupichika.”');
INSERT INTO "silika" VALUES(44,19,'44:19','“Soni ngasin’dikwesya kwa Allah, chisimu une tinam’bichilile nao umboni wakuonechela.”');
INSERT INTO "silika" VALUES(44,20,'44:20','“Soni chisimu une nalijuyiye mwa (Allah) M’mbuje gwangu jwali soni M’mbuje gwenu kuti ntakwika mwamusomile maganga.”');
INSERT INTO "silika" VALUES(44,21,'44:21','“Nambo naga ngankungulupilila, basi mumambalani (ngasimunagasya).”');
INSERT INTO "silika" VALUES(44,22,'44:22','(Nambo ŵantendele uwwanga), basi ni jwaŵilasile Ambujegwe (Allah juchitiji): “Chisimu ŵanganyaŵa ni ŵandu ŵakuleŵa, (ndende nao chichi)?”');
INSERT INTO "silika" VALUES(44,23,'44:23','(Allah ŵansalile kuti): “Basi ŵigulani ni achikapolo Ŵangu chilo, chisimu ŵanganyammwe tinkuuyidwe.”');
INSERT INTO "silika" VALUES(44,24,'44:24','“Soni jilekani mbwani jili mujiŵelelepemo (duu chilekanganile), chisimu ŵanganyao ni asilikali ŵatamisyidwe.”');
INSERT INTO "silika" VALUES(44,25,'44:25','Ana migunda jilingwa jaŵajilesile ni achitutumule!');
INSERT INTO "silika" VALUES(44,26,'44:26','Ni mimela ni pamalo pambone!');
INSERT INTO "silika" VALUES(44,27,'44:27','Ni chindimba (chekulungwa) chaŵaliji nkulisengwachisya nacho!');
INSERT INTO "silika" VALUES(44,28,'44:28','Mwele (ni muyaŵelele), ni twapele kuitawalila yalakweyi ŵandu ŵane (ŵanache ŵa Israila).');
INSERT INTO "silika" VALUES(44,29,'44:29','Ni nganikwalilila ŵanganyao kwinani ni pasi, nambo soni nganaŵa ŵakupedwa lipesa (lyakutendela toba).');
INSERT INTO "silika" VALUES(44,30,'44:30','Soni chisimu twajokwele ŵanache ŵa Israila ku ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(44,31,'44:31','Yakuumila kwa Firiauna, chisimu jwalakwe jwaliji jwakulikwesya, (soni jumpepe) mwa akusumba mpika.');
INSERT INTO "silika" VALUES(44,32,'44:32','Soni twasagwile ŵanganyao (ŵanache ŵa Israila) pa umanyilisi kuipunda iwumbe yosope (yandaŵi jao).');
INSERT INTO "silika" VALUES(44,33,'44:33','Ni twapele ilosyo yayaliji nkati mwakwe mayeso gakuonechela.');
INSERT INTO "silika" VALUES(44,34,'44:34','Chisimu ŵanganyaŵa (Makuraishi) akutiji:');
INSERT INTO "silika" VALUES(44,35,'44:35','“Pangali chinechakwe ikaŵeje chiwa chetu chandanda (pechi chatukuwaga chachili chipelela chetu), soni nganituŵa uwwe ŵakwimusyidwa (m’malembe).”');
INSERT INTO "silika" VALUES(44,36,'44:36','“Basi ikani nao achatati ŵetu (ŵaŵawile kalakala) nam’baga jenumanja nkuŵecheta yakuona (kuti kwana kwimuka m’malembe).”');
INSERT INTO "silika" VALUES(44,37,'44:37','Ana ŵanganyao (Makuraishi) niŵali ŵambone, kapena ŵandu ŵa Tubba‘i (ŵa ku Yemeni), ni aŵala ŵapaujo pao? Twajonasile (wosope ŵeneo), chisimu ŵanganyao ŵaliji ŵakuleŵa.');
INSERT INTO "silika" VALUES(44,38,'44:38','Soni Uwwe nganitugumba mawunde ni mataka kwisa soni yaili chilikati chakwe mwamasanje.');
INSERT INTO "silika" VALUES(44,39,'44:39','Nganituigumba yele iŵiliyi ikaŵeje mwakuonaonape, nambo ŵajinji mwa ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(44,40,'44:40','Chisimu lisiku lyachiilanyo, ni ndema jao wosope ŵene (jajiŵichidwe jakwapela ipotesi).');
INSERT INTO "silika" VALUES(44,41,'44:41','Lisiku lyangakamuchisya jwaulongo kwa nnongo n’jakwe chindu chilichose, nambo soni ŵanganyao ngasakulupusyidwa.');
INSERT INTO "silika" VALUES(44,42,'44:42','Ikaŵeje ŵatachatendela chanasa Allah, chisimu Jwalakwe ni jwali Jwamachili gakupunda, Jwachanasa channope.');
INSERT INTO "silika" VALUES(44,43,'44:43','Chisimu chitela cha Zakkuum,');
INSERT INTO "silika" VALUES(44,44,'44:44','Niyakulya ya ŵakulemwa.');
INSERT INTO "silika" VALUES(44,45,'44:45','Yanti mpela ntofu wenyelenyenduche, yaikasemuleje m’matumbo.');
INSERT INTO "silika" VALUES(44,46,'44:46','Chisawu kasemule ka mesi gamoto nnope.');
INSERT INTO "silika" VALUES(44,47,'44:47','(Kuchiŵechetedwa yanti): “Munkwembekanye, ni munkwekwelemechesye pakatikati pa Moto Waukali.”');
INSERT INTO "silika" VALUES(44,48,'44:48','“Kaneka mpungulile pachanya pa ntwe wakwe ilagasyo ya mesi gamasemule nnope.”');
INSERT INTO "silika" VALUES(44,49,'44:49','(Tachisalilidwa mwakuchindimbulidwa kuti): “Pasyani, chisimu mmwe ni ŵan’di ŵamachili gakupunda, ŵakuchimbichika nnope.”');
INSERT INTO "silika" VALUES(44,50,'44:50','“Chisimu ayi ni ayila yamwaliji nkuikaichila.”');
INSERT INTO "silika" VALUES(44,51,'44:51','Chisimu ŵawoga (wakun’jogopa Allah) tachiŵa pamalo gantunjelele.');
INSERT INTO "silika" VALUES(44,52,'44:52','Ku Matimbe ga ku Mbepo, ni achitutumule.');
INSERT INTO "silika" VALUES(44,53,'44:53','Takawaleje (nguwo sya) silika jwakuŵeŵela ni silika jwakandapalila, (ali atemi) mwakulolegana.');
INSERT INTO "silika" VALUES(44,54,'44:54','Mwele (ni muyichiŵela), ni tuchalombekasya ni achimmasyeto ŵameso gakulanguka soni gekulungwa mwakusalalila.');
INSERT INTO "silika" VALUES(44,55,'44:55','Takaŵilasyeje mwalakwemo ntundu uliose wa isogosi mwantunjelele.');
INSERT INTO "silika" VALUES(44,56,'44:56','Ngasaja kupasya chiwa mwalakwemo ikaŵeje chiwa chao chandanda (cha pa duniya), ni taakaagoose ku ilagasyo ya Moto Waukali.');
INSERT INTO "silika" VALUES(44,57,'44:57','Kwa ukoto wakuumila kwa Ambuje ŵenu (Allah), chisimu kwalakweko ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(44,58,'44:58','Basi chisimu tujijepepesye (Qur’aniji) mu chiŵecheto chenu (mmwe Muhammadi صلى الله عليه وسلم) kuti ŵanganyao akumbuchile.');
INSERT INTO "silika" VALUES(44,59,'44:59','Basi jembecheyani, chisimu nombe nao (achimakafilio) akwembecheya (sampano tamanyukuche jwakonasika).');
INSERT INTO "silika" VALUES(45,1,'45:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(45,2,'45:2','Kutulusyidwa kwa Chitabu (Qur’an) kukuumila kwa Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(45,3,'45:3','Chisimu kumawunde ni petaka kwana ilosyo kwa ŵandu ŵakulupilila (yakulosya kuti Allah apali).');
INSERT INTO "silika" VALUES(45,4,'45:4','Ni mu kagumbidwe kenu, ni yawumi iliyose yakuulukutaga yaakuijenesya (Allah) iŵele ilosyo kwa ŵandu ŵaakusimichisya (chikulupi chao).');
INSERT INTO "silika" VALUES(45,5,'45:5','Ni kupisyangana kwa chilo ni muuusi, ni lisiki (wula) lyatulwisye Allah kuumila kwinani basi ni kusyuchisya nalyo litaka panyuma pakuwa kwakwe, ni kujigalausyagalausya mbungo, iŵele ilosyo kwa ŵandu ŵaakwete lunda.');
INSERT INTO "silika" VALUES(45,6,'45:6','Galakwega ni ma Ȃya ga Allah gatukunsoomela (mmwe Muhammadi صلى الله عليه وسلم) mwakuonaonape, ana ni nganichi jatajikulupilile panyuma pa (kunkanila) Allah ni ma Ȃya Gakwe?');
INSERT INTO "silika" VALUES(45,7,'45:7','Ipotesi niyakwe jwalijose jwakulukanya unami nnope, jwasambi syejinji.');
INSERT INTO "silika" VALUES(45,8,'45:8','Jwaakupikanaga ma Ȃya ga Allah gali nkusoomedwa kukwakwe, kaneka ni jukanganichilaga (kupanganya yakanyisyidwa) mwakulikwesya nti chisawu nganijugapikana (ma Ȃyago), basi mumpani abali ja ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(45,9,'45:9','Soni naga ali achimanyi chindu chinechakwe chakuumila m’ma Ȃya Getu, akasachitendelaga chipongwe, ŵanganyao akwete kupata ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(45,10,'45:10','Paujo pao pana Jahannama, soni yaŵapanganyisye ngaija kwakamuchisya chilichose, namuno ayila yaŵalitendele kuŵa yakwagosa kunneka Allah, soni akwete kupata ŵanganyao ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(45,11,'45:11','Aji (Qur’an) jiŵele chongolo, sano aŵala ŵaagakaanile ma Ȃya ga M’mbuje gwao akwete kupata ilagasyo yakunyalaya ni kupoteka kusyene.');
INSERT INTO "silika" VALUES(45,12,'45:12','Allah ni Ajula jwan’jepepachisye mbwani, kuti yombo ijendeje mwalakwemo kwa ulamusi Wakwe, ni kuti nsosejesoseje ukoto Wakwe, ni kuti jenumanja m’be ŵakutogolela.');
INSERT INTO "silika" VALUES(45,13,'45:13','Ni an’jepepachisye yaili kumawunde ni yaili petaka yosope yene ikuumila Kukwakwe, chisimu mu yalakweyo muŵele mwana ilosyo kwa ŵandu ŵaakuchetelela.');
INSERT INTO "silika" VALUES(45,14,'45:14','Mwasalilani aŵala ŵaakulupilile kuti ŵaakululuchile aŵala (achimakafili ŵaakwatanda) ŵangakwembechela ya moŵa ga Allah (gakwapela ipotesi), kuti (Allah) akalipile ŵandu pa yaŵapanganyisye. ');
INSERT INTO "silika" VALUES(45,15,'45:15','Jwatapanganye yambone nikuti akulipanganyichisya nsyene, soni jwatasakasye nikuti akulisakachisya nsyene, kaneka kwa M’mbuje gwenu (Allah) tinchiuchisyidwa.');
INSERT INTO "silika" VALUES(45,16,'45:16','Soni chisimu pamasile patwapele ŵanache ŵa Israila Chitabu, malamusi kwisa soni utume, ni twapele ichindu yambonembone, ni twachimbichisye kupunda iwumbe yosope (yandaŵi jao).');
INSERT INTO "silika" VALUES(45,17,'45:17','Ni twapele maumboni gakuonechela pa ichindu (ya dini), ni nganatindana ikaŵeje panyuma pakwaichilila umanyilisi ligongo lya litima lyalyaliji sikati jao, chisimu Ambuje ŵenu (Allah) tachijiilanya chilikati chao lisiku lya Kiyama pa ichindu yaŵaliji nkutindana pa yalakweyo.');
INSERT INTO "silika" VALUES(45,18,'45:18','Kaneka tum’bisile (mmwe Muhammadi صلى الله عليه وسلم) pachanya petala lya chilamusi (cha dini), basi likuyaani, ni nkakuuya isako ya aŵala ŵangali chachimanyi.');
INSERT INTO "silika" VALUES(45,19,'45:19','Chisimu ŵanganyao nganaŵa ankamuchisye chilichose (pa ilagasyo ya) kuumila kwa Allah. Sano chisimu ŵalupuso aŵele asyoŵe (ŵakuchenjelana) ŵane ni ŵane, sano Allah ni Nkwachenjela ŵawoga (wakun’jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(45,20,'45:20','Aji (Qur’aniji) jiŵele muli syakwamulichila ŵandu, nambo soni chongolo ni chanasa kwa ŵandu ŵaakusimichisya (chikulupi chao).');
INSERT INTO "silika" VALUES(45,21,'45:21','Ana akuganichisya aŵala ŵaapanganyisye yakusakala kuti chitwatende nti chisawu aŵala ŵaakulupilile nikutendaga yambone, kuŵa yalumo pa umi wao ni chiwa chao? Kaje kusakala nnope chiilanyo chao!');
INSERT INTO "silika" VALUES(45,22,'45:22','Ni agumbile Allah mawunde ni mataka mwakuonaonape, soni kuti mundu jwalijose akalipidwe pa yajwapanganyisye, soni ŵanganyao ngaja kutendedwa lupuso.');
INSERT INTO "silika" VALUES(45,23,'45:23','Ana mum’bweni ajula jwaitesile isako yakwe kuŵa nnungu jwakwe, Allah ni annechelele kusokonechela panyuma pa umanyilisi (waum’bichilile mundujo kuti ayi yakusakala ni kuitendagape), ni adindile chidindo mmawiwi mwakwe, ni mu ntima mwakwe, ni aŵisile m’meso mwakwe chisiŵilo, ana ni nduni jwampaka an’jongole jwalakwejo panyuma pa (kunnechelela) Allah? Ana ngankukumbuchila?');
INSERT INTO "silika" VALUES(45,24,'45:24','(Achimakafili) ni ŵatite: “Pangali (umi) wine ikaŵeje umi wetu wa duniya peuno, tukuwaga, nambo soni tukulamaga, ni ngajikutujonangaga ikaŵeje ndaŵi basi.” Ni nganakola ŵanganyao umanyilisi uliose pa yele (yaakuŵechetayo), nganaŵa ŵanganyao ikaŵeje agamba ganichisyape basi.');
INSERT INTO "silika" VALUES(45,25,'45:25','Sano pati gachisoomedwaga kukwao ma Ȃya Getu gakusalichisya, ngagakasaŵaga makani gao ikaŵeje kuŵecheta kwao kwanti: “Ŵikaani nao achatati ŵetu (ŵaŵawile kalakala) nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(45,26,'45:26','Jilani: “Allah ni jwaakumpa umi jenumanja, kaneka ni akumpaga chiwa, kaneko taachinsonganganya lisiku lya Kiyama lyangali kaichila ya lyalakwelyo, nambo ŵandu ŵajinji ngaakumanyilila.”');
INSERT INTO "silika" VALUES(45,27,'45:27','Soni uŵele wakwe Allah uchimwene wa kumawunde ni petaka, sano lisiku lyajichijima Kiyama lyele lisikulyo ŵakuya yaunami tachipelelwa (yambone yosope).');
INSERT INTO "silika" VALUES(45,28,'45:28','Ni tinchiuona nkutula uliose uli utindiŵele, nkutula uliose uchiŵilanjidwa wa ku (kusooma) chitabu chakwe (cha masengo gakwe), (achisalilidwaga yanti): “Lelo tillipidwe jenumanja yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(45,29,'45:29','“Achi chitabu Chetu chikuŵecheta yakwamba jenumanja mwakuonaonape, chisimu Uwwe twaliji nkulemba yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(45,30,'45:30','Sano aŵala ŵaŵakulupilile ni kutendaga yambone, basi M’mbuje gwao (Allah) takajinjisye ku chanasa Chakwe, kwalakweko ni kupunda kwakuonechela.');
INSERT INTO "silika" VALUES(45,31,'45:31','Nambo aŵala ŵaŵakufuulu (tiikaŵechetedwe kukwao yanti): “Ana nganigaŵa ma Ȃya Gangu gali nkusoomedwa kukwenu? Nambo mwalikulungwisye, ni mwaliji ŵandu ŵakuleŵa.”');
INSERT INTO "silika" VALUES(45,32,'45:32','Soni pati iŵechetedwe (yanti): “Chisimu chilanga cha Allah chakuonaonape, nambo soni Kiyama nganijiŵa jakaikasya.” Mwatiji: “Ngatukumanyilila kuti Kiyama nichichi, nganitukola nganisyo silisyose ikaŵeje liganichisyape, soni nganituŵa mwa akusimichisya (yakwika kwa Kiyama).”');
INSERT INTO "silika" VALUES(45,33,'45:33','Ni ichionechela kukwao yakusakala yaŵapanganyisye, ni ichasyungula ayila yaŵaliji nkuitenda chipongwe.');
INSERT INTO "silika" VALUES(45,34,'45:34','Ni ichiŵechetedwa yanti: “Lelo chitun’diŵalile (m’Moto) mpela yamwatite pakuliŵalila yakusimangana ni lisiku lyenu lyelino, mauto genu ni ku Moto, soni nganinkola jenumanja akunkamuchisya ŵaliose.”');
INSERT INTO "silika" VALUES(45,35,'45:35','“Yalakweyo (yaimpatileyo) ni ligongo lyanti jenumanja mwagatendele chipongwe ma Ȃya ga Allah, ni wannyenjile umi wa duniya.” Basi lelo ngakoposyedwa mwalakwemo (m’Moto), soni ŵanganyao ngaakundidwa (kuujila ku duniya) kuti akatende toba ni kuŵenda chikululuko (kwa Allah).');
INSERT INTO "silika" VALUES(45,36,'45:36','Basi Allah ni Nsyene lumbili losope lwene, M’mbuje jwa kumawunde, soni M’mbuje jwa petaka, M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(45,37,'45:37','Soni ukulungwa ni Wakwe kumawunde ni petaka, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(46,1,'46:1','Hâ. Mîm. ');
INSERT INTO "silika" VALUES(46,2,'46:2','Kutulusyidwa kwa Chitabu (Qur’an) kukuumila kwa Allah, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(46,3,'46:3','Nganitugumba mawunde ni mataka ni yaili chilikati chakwe ikaŵeje mwakuonaonape ni ndema jekolanjidwe, sano aŵala ŵaakufuulu akuitundumalila yaateteledwe nayo.');
INSERT INTO "silika" VALUES(46,4,'46:4','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana nkuiona uli ayila yankuiwomba kunneka Allah? Munosyani, ana ni upandechi wetaka waigumbile? Kapena ikwete yalakweyo liunjili lyakamusyangana (najo Allah) ku mawunde? Mumbani chitabu chachapali nkanichiŵe achi (cha Qur’an), kapena sigasiga sya umanyilisyi (syakutendela umboni yagalagatila isanamu), nam’baga jenumanja nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(46,5,'46:5','Ana ni nduni jwali jwakupotela nnope kumpunda mundu jwaakuiwomba inepe yangaŵaga Allah, ayila yangaŵa nkwajanga mpaka lisiku lya Kiyama, soni yalakwe ngaikumanyilila ya maduwa gao (kuti akuiwomba).');
INSERT INTO "silika" VALUES(46,6,'46:6','Soni patachisonganganyisyidwa ŵandu tiyichiŵa (isanamuyo) amagongo ŵao, ni tiyichiŵa ili nkuikana ibada yao (yaŵaaliji nkuigalagatila).');
INSERT INTO "silika" VALUES(46,7,'46:7','Sano ndema jakusoomedwa kukwao ma Ȃya Getu gakupikanika, akasaŵecheta aŵala ŵaakufuulu pakwamba ya yakuona (Qur’an) ndema jakwaichilila: “Awu ni usaŵi wakuonechela.”');
INSERT INTO "silika" VALUES(46,8,'46:8','Kapena akutiji: “Jwalakwe (Muhammadi صلى الله عليه وسلم) ni jwaajitunjile (Qur’an)?” Jilani: “Nambaga ni junajitunjile, basi nganinkola ukombosi uliose wakungamuchisya kwa Allah (pa ilagasyo Yakwe), Jwalakwe ni Nkumanyilila chenene yankuŵechetana yakwamba jalakwe (Qur’an), Jwalakwe jukwanila kuŵa Mboni chilikati changune ni jenumanja, soni Jwalakwe ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(46,9,'46:9','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Une nganimba ndanda ja achimitenga, soni ngangumanyilila yatiyichitendekwa kukwangu kapena kukwenu, unetu ngangukuya ikaŵeje yayikuuwulidwa kukwangu, soni nganimba une ikaŵeje nkutetela jwakuonechela.”');
INSERT INTO "silika" VALUES(46,10,'46:10','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ana nkuiona uli najiŵaga (Qur’aniji) jakutyochela kwa Allah, sano jenumanja ni n’jikaanile, sano nkutendela umboni jwakuumila mu ŵanache ŵa Israila (Abdullah bin Salâm t) ni jutendele umboni pa jalakweji (kuti jakuona mpela mujaŵelele Taurat), ni jukulupilile, sano jenumanja ni n’dikwesyisye (pangajikulupilila, ana ngam’ba ŵalupuso?)” Chisimu Allah jwaangaajongola ŵandu ŵalupuso.');
INSERT INTO "silika" VALUES(46,11,'46:11','Sano aŵala ŵaakufuulu (achinsyene ipanje ni machili) ni akutiji pakwamba ya aŵala ŵaakulupilile (ŵali ŵakulaga soni ŵangali machili): “Ingaŵe kuti (dini jalakweji) jili jambone, nga nganatulongolela ŵanganyaŵa ku jalakwejo.” Sano pandema janti nganajongoka najo (Qur’anijo), basi akutiji: “Awu ni unami wakalakala.”');
INSERT INTO "silika" VALUES(46,12,'46:12','Soni paujo pakwe chapali chitabu cha Musa chachaliji nnongola ni chanasa, sano nombe achi ni chitabu chachikwitichisya (itabu ya Allah yayalongolele), (chachiŵiche) m’chiŵecheto cha Charabu, kuti chatetele aŵala ŵaalitesile lupuso, ni kwapa abali jakusengwasya ŵakolosya.');
INSERT INTO "silika" VALUES(46,13,'46:13','Chisimu aŵala ŵaakutiji: “M’mbuje gwetu ni Allah.” Kaneko nikwimilichika (pa Dini), basi ngasaŵa ni woga (ndaŵi jakuwa namuno panyuma pakwe) nambo soni ngasadandaula.');
INSERT INTO "silika" VALUES(46,14,'46:14','Ŵanganyao niŵandu ŵa ku Mbepo, takaŵe ŵandamo kwalakweko, kuŵa malipilo pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(46,15,'46:15','Soni tunchisyisye mundu kwa achinangolo ŵakwe kwatendela yambone. - Ŵan’jigele chilu chakwe mamagwe mwakulagasika, ni ŵam’beleche mwakulagasika. Kujigala chilu chakwe ni kunnekasya konga (japite) myesi makumi gatatu. Kwikanila ndema jaakukuyika komangala kwakwe, ni kwika yaka makumi ncheche ajileje: “Ambuje ŵangu! Mumbani machili ganti ngomboleje kutogolela ukoto Ŵenu wamumbele une ni achinangolo ŵangu, nambo soni napakomboleje kutenda yambone yampaka iyyinonyele. Ni mungolochesye achiŵanangu, chisimu une ndesile toba Kukwenu, soni chisimu une ndili mwa Asilamu.”');
INSERT INTO "silika" VALUES(46,16,'46:16','Ŵanganyao ni aŵala ŵatukwapochelaga yambone yaapanganyisye, ni kwakululuchila ileŵi yao, (tachiŵa) mu ŵandu ŵa ku Mbepo, chilanga chakuona achila chaŵaliji nkupedwa.');
INSERT INTO "silika" VALUES(46,17,'46:17','Sano ajula jwaakwasalilaga achinangolo ŵakwe kuti: “Kusakala nikwenu! Ana nkumba chilanga chanti tinjikoposyedwa (m’malembe), kutendaga pamasile pajipite mikutula paujo pangu (nambo ngajikukoposyedwa m’malembe mpaka lelo)?” Aku (achinangolo) ŵanaŵaŵilio achiŵendaga chikamuchisyo kwa Allah (chakuti an’jongole mwanachejo), (aku achin’jamukaga mwanachejo kuti): “Ipotesi niyenu! Kulupililani. Chisimu chilanga cha Allah ni chakuonaonape.” Nambo jwalakwe jukasati: “Nganiyiŵatu ayi (yankuŵecheta) ikaŵeje adisiadisi sya (ŵandu) ŵandanda.”');
INSERT INTO "silika" VALUES(46,18,'46:18','Ŵanganyao ni aŵala ŵalisimichiche pa ŵanganyao liloŵe (lya kuponyedwa ku Moto) pamo ni mikutula jajapite paujo pao ja majini ni ŵandu, chisimu ŵanganyao niŵali ŵakupelelwa.');
INSERT INTO "silika" VALUES(46,19,'46:19','Soni jwalijose tachikola dalaja (syakulekangana ni jwine) mu yaŵapanganyisye, ni kuti (Allah) akalipile mwakwana itendo yao, soni ŵanganyao ngasatendedwa lupuso.');
INSERT INTO "silika" VALUES(46,20,'46:20','Soni (mwakumbusyani) lisiku lyatachilosyedwa aŵala ŵaŵakufuulu ku Moto (ni kusalilidwa yanti): “Mwaijonasile yambone yenu pa umi wenu wa duniya, ni mwalisengwasyisye nayo (ku duniyako, basi lelo nganyyipata soni). Basi lelo tillipidwe ilagasyo yakwalusya ligongo lya kulikwesya kwenu kwamwaliji nkulikwesya pa chilambo mwangaŵajilwa, ni ligongo lya kunyosya kwamwaliji nkunyosya.”');
INSERT INTO "silika" VALUES(46,21,'46:21','Soni munkumbuchilani (Hudu) nnongo n’jao achina Adi; ndema jaŵatetele ŵandu ŵakwe pa Al-Ahqâf (chiŵata chamasugulu ga nsanga kumwela kwa chilumba cha Ŵarabu), soni chisimu ŵaapiite akutetela ŵajinji paujo pakwe ni panyuma pakwe; (ni jwasalile kuti): “Ngasin’galagatila (jwinepe) akaŵeje Allah, chisimu une ngun’jogopela jenumanja ilagasyo ya lisiku lyekulungwa nnope.”');
INSERT INTO "silika" VALUES(46,22,'46:22','Ŵanganyao ŵatite: “Ana ntuyichilile kuti ntugalausye kuleka milungu jetu? Basi ikani nayo kukwetu yankutujogoyechesyao nam’baga n’di mwa akuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(46,23,'46:23','(Hudu) jwatite: “Chisimu umanyilisi (waajimanya ndema jakwichila ilagasyoyo) uli kwa Allah pe, ni ngunnunguchisya yandumisyidwe nayo, nambo une ngum’bona jenumanja kuti n’di ŵandu ŵakutenda yachijinga.”');
INSERT INTO "silika" VALUES(46,24,'46:24','Basi ndema jaŵaiweni yele (ipotesi) ili mpela liwunde lyawula lyalyalungamaga ku iŵata yao, ŵatite: “Ali ni liwunde lyalitugwichisye wula.” (Ŵasalilidwe kuti: “Ngwamba,) nambo yalakweyi ni ayila yamwaisachililaga kuti iyiche mwachitema, (jalakweji ni) mbungo nkatimwakwemo mwana ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(46,25,'46:25','“Jajikonanga chilichose kwa ulamusi wa M’mbujegwe (Allah).” Ni ŵasyuchile kuŵa ŵangaŵa nkuoneka soni ikaŵeje majumba gaope (ni gagasigalile). Iyyoyopeyo niyatukutiji pakwalipila ŵandu ŵakuleŵa.');
INSERT INTO "silika" VALUES(46,26,'46:26','Soni twalimbangenye ŵanganyao ni ichindu yanganitun’dimbanganyichisya jenumanja (Makuraishi) ni yalakweyo (mpela machili ni chipanje), ni twapele mawiwi, meso ni mitima; nambo nganigakamuchisya mawiwi gao, namuno meso gao, namuno mitima jao chilichose pandema jaŵaliji nkukanila ma Ȃya ga Allah, ni yasyungwile ŵanganyao ayila yaŵaliji nkuitenda chipongwe.');
INSERT INTO "silika" VALUES(46,27,'46:27','Soni chisimu chene twajijonasile misi ja mungulugulu mwenu, ni twasalichisye ma Ȃya kuti ŵanganyao awujile (ku chikulupi cha Tauhidi).');
INSERT INTO "silika" VALUES(46,28,'46:28','Nambi uli (pandema jayayichilile ilagasyo) nganiyakamuchisya ayila yaŵalitendele yangaŵaga Allah kuŵa milungu jakuliŵandichilisya Kukwakwe? Nambo yasokonechele ŵanganyao, soni walakweo (wakuitenda isanamu kuŵa milungu jakwaŵandichisya kwa Allah) ni unami wao, ni (yaukoleko) yaaŵele ŵanganyao ali nkutunga.');
INSERT INTO "silika" VALUES(46,29,'46:29','Soni (kumbuchilani) katema katwatumisye kukwenu (mmwe Muhammadi صلى الله عليه وسلم) likuga lya majini kwisa kupikanila Qur’an. Basi ndema jagajiyichilile, gatite: “Jilani jii.” Sano pajamasile (kusoomwa), gagalawiche kuja ku majini gajao kukugatetela.');
INSERT INTO "silika" VALUES(46,30,'46:30','Gaatite: “E majini gajetu! Chisimu uwwe tuchipikene chitabu chachitulusyidwe panyuma pa Musa, chachikwitichisya yayaliji m’bujo mwakwe, chikongolela ku yakuona, ni kwitala lyagoloka.”');
INSERT INTO "silika" VALUES(46,31,'46:31','“E majini gajetu! Mun’jitikani nkuŵilanga jwa Allah (Muhammadi صلى الله عليه وسلم), ni munkulupilile jwalakwe, (Allah) tankululuchile sambi syenu, ni tam’bambasye ku ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(46,32,'46:32','“Sano jwatatende ngan’jitika nkuŵilanga jwa Allah; nikuti nganijuŵa junnepelekasyisye (Allah kumpa ipotesi) pa chilambo, soni nganijukola jwalakwe jwangaŵaga Allah akun’gosa ŵaliose. Ŵanganyao ali nkupotela kwakuonechela.”');
INSERT INTO "silika" VALUES(46,33,'46:33','Ana ngaakuiona yanti Allah Ajula juŵagumbile mawunde ni mataka ni nganijupela pakuigumba yalakweyo, kuti juŵele Jwakombola kwajimusya ŵawe? Elo, chisimu Jwalakwe pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(46,34,'46:34','Sano lisiku lyatachionesyedwa ku Moto aŵala ŵaŵakufuulu (tachiwusyidwa kuti): “Ana ayi nganiyiŵa yakuona?” Ŵanganyao tachiti: “Elo (yakuona) tukwalumbila Ambuje ŵetu.” (Allah) tachiti: “Basi pasyani ilagasyo ligongo lya ayila yamwaliji nkuikana.”');
INSERT INTO "silika" VALUES(46,35,'46:35','Basi pililani (mmwe Muhammadi صلى الله عليه وسلم) yaŵatite pakupilila Achimitenga ŵakulimbangana nnope (pa dini, mpela Nuhu, Ibrahima, Musa ni Isa), ni ngasimwatendela chitema ŵanganyao (achimakafilio kuti yapate ilagasyo mwachitemachitema). Lisiku lyatachiyiona (ilagasyo) yaakupedwa chilanga (chakwika kwakwe), tachiŵa nti chisawu ŵanganyao nganatama (pa duniya) ikaŵeje ndaŵi jimpepe ja muusi. (Aji Qur’an jiŵele) utenga wakulungusyidwa (mwakwanila), ana (nkwika ilagasyoyo) mpaka ajonasiche (ŵanepe) pangaŵaga ŵandu ŵakunyosya?');
INSERT INTO "silika" VALUES(47,1,'47:1','Aŵala ŵaakufuulu, nikusiŵililaga (ŵandu) kwitala lya Allah, Jwalakwe (Allah) juitesile kuŵa yantega itendo yao.');
INSERT INTO "silika" VALUES(47,2,'47:2','Nambo aŵala ŵaakulupilile ni kutendaga yambone, ni kulupilila yaitulusyidwe kwa Muhammadi صلى الله عليه وسلم yaili yakuona yakuumila kwa M’mbuje gwao (Allah), Jwalakwe (Allah) juisimasisye ileŵi yao, ni kwakolochesya kaŵe kao. ');
INSERT INTO "silika" VALUES(47,3,'47:3','Yalakweyo ni ligongo lyanti aŵala ŵaakufuulu akuyiye yaunami, sano aŵala ŵaakulupilile ni akuyiye yakuona yakuumila kwa M’mbuje gwao (Allah), iyyoyopeyo ni yaakuti Allah pakupeleka kwa ŵandu ilandanyo yao.');
INSERT INTO "silika" VALUES(47,4,'47:4','Sano naga nsimangene nao (pangondo) aŵala ŵaakufuulu, basi mwakunulani ngosi (mwaulagani mwanakamo), mpaka pati ntupiye kwaulaga ŵanganyao, basi liiyaani matondolo (gakwataŵila, mwakamulani kuŵa mateeka genu), sano panyuma pakwe komboleka gamba kwaleka (mwangali kutola mbote), kapena (kwaleka) pa kuliombola (pakutola mbote), (ntendeje iyyo) mpaka ngondo jiyikane mwakutula misigo jakwe (jiyikane mwakumala). Chalakwecho (ni chilamusi cha Allah). Sano Allah angasache nikuti angamalene nao (Nsyene mwangali jenumanja kumenyana nao), nambo (akunnamula kuputa ngondo) kuti ŵalinje mayeso ŵane mwa jenumanja pa ŵane (ŵakulupilila pa ŵakanila). Sano aŵala ŵaaulajidwe petala lya Allah, basi Jwalakwe ngasagajonanga masengo gao. ');
INSERT INTO "silika" VALUES(47,5,'47:5','Tiŵajongole, ni kwakolochesya kaŵe kao.');
INSERT INTO "silika" VALUES(47,6,'47:6','Ni tachajinjisya ku Mbepo jaŵamanyisye. ');
INSERT INTO "silika" VALUES(47,7,'47:7','E jenumanja ŵankulupilile! Naga nkunkamuchisya Allah (pakamuchisya petala Lyakwe), Jwalakwe tankamuchisye, ni tagalimbanganye makawu genu.');
INSERT INTO "silika" VALUES(47,8,'47:8','Sano aŵala ŵaakufuulu, chonasiko nichao, soni (Allah) ayitesile kuŵa yantega itendo yao.');
INSERT INTO "silika" VALUES(47,9,'47:9','Yalakweyo ni ligongo lyanti ŵanganyao ayiŵenjile yaatulwisye Allah, basi (Allah) ni agajonasile masengo gao.');
INSERT INTO "silika" VALUES(47,10,'47:10','Ana ngaakwendajenda pa chilambo nikupitaga nchilola mujaŵelele mbesi ja aŵala ŵapaujo pao? Allah ŵajonasile; sano akwete kupata achimakafili (ŵaleloŵa ilagasyo) yanti mpela yalakweyo.');
INSERT INTO "silika" VALUES(47,11,'47:11','Yalakweyo ni ligongo lyanti Allah ni Nkwachengeta aŵala ŵaakulupilile, sano achimakafili nganakola nkwachengeta jwao.');
INSERT INTO "silika" VALUES(47,12,'47:12','Chisimu Allah tachajinjisya aŵala ŵaakulupilile ni kutendaga yambone ku Matimbe ga ku Mbepo gasikujilima chapasi pakwe sulo. Nambo aŵala ŵaakufuulu akusengwa (pa umi wa duniya) ni kulyaga mwaikulila ilango, sano Moto ni mauto gao.');
INSERT INTO "silika" VALUES(47,13,'47:13','Ana jilingwajilingwa mwa misi (jatwajijonasile) jajaliji jamachili nnope kuwupunda musi wenu awu (wa Maaka) waunkopwesye? Twaajonasile ŵanganyao, ni nganakola jwakwakamuchisya jwalijose.');
INSERT INTO "silika" VALUES(47,14,'47:14','Ana mundu jwali pa umboni wakuonechela wakuumila kwa M’mbujegwe (Allah) mpaka alandane ni mundu jwaisalalisyidwe itendo yakwe yangalumbana, aku ali nkukuya ŵanganyao isako yao?');
INSERT INTO "silika" VALUES(47,15,'47:15','Kasimbidwe ka Mbepo jaapedwile chilanga ŵawoga (wakun’jogopa Allah, kali nyyi); kwalakweko kwana sulo syamesi gangaunda, ni sulo syankaka wangachesuka kanonye kakwe, ni sulo syaukana wakulyolyopela kwa akumwa, ni sulo syauchi weswejesye (wangali mbotole), soni akwete kupata ŵanganyao kwalakweko ntundu uliose wa isogosi, kwisa soni chikululuko chakuumila kwa M’mbuje gwao (Allah). (Ana ŵanganyao) mpaka alandane ni ajula jwatakaŵe jwandamo m’Moto, ni kumwesyedwa mesi gamasemule nnope gachigakakatanye matumbo gao?');
INSERT INTO "silika" VALUES(47,16,'47:16','Soni ŵane mwa ŵanganyao (achinakunaku) pana ŵaakupikanilaga kukwenu (mmwe Muhammadi صلى الله عليه وسلم nchipelekaga ilimu), mpaka pati atyosile kukwenu akutiji (mwachipongwe) kwa aŵala ŵaapedwile umanyilisi: “Ana (Muhammadi صلى الله عليه وسلم) juŵechete chichi sampano jino?” Ŵanganyao ni aŵala ŵaŵadindile Allah chidindo m’mitima mwao, ni akuyiye isako yao.');
INSERT INTO "silika" VALUES(47,17,'47:17','Sano aŵala ŵaachijitiche chongoko, (Allah) ŵajonjecheesye chongoko, ni kwapa woga wao (wakun’jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(47,18,'47:18','Ana akwembecheya (inepe) yangaŵaga Kiyama kuti jaichilile mwachisupuchisya? Basi pamasile paiyiche imanyilo yakwe. Ana kuchakamuchisya chinauli kumbuchila kwao ndema jajichaichilila (Kiyamajo)? ');
INSERT INTO "silika" VALUES(47,19,'47:19','Basi manyililani yanti pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Allah, ni m’bendeje chikululuko pa sambi syenu ni (sambi) sya ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe. Soni Allah akumanyilila kwendajenda kwenu, ni malo genu gakutulalila. ');
INSERT INTO "silika" VALUES(47,20,'47:20','Sano aŵala ŵaakulupilile ni akutiji: “Ligongo chichi ngatulusyidwa Sura (kukwetu jakutukundisya kwauchisya achimakafili pa uwwanga waakututenda)?” Nambo nkutulusyidwa Sura jakutamilichika (chilamusi chakwe), nikusalidwamo ngani jangondo, timwaone aŵala ŵamuli m’mitima mwao ulwele (wa chikaiko ni unakunaku) achinnolaga ni kalole kamundu jwaakukomoka nacho chiwa. Nambo yaliji yambone kukwao (achinakunakuo kumpikanila Allah ni kunkunda).');
INSERT INTO "silika" VALUES(47,21,'47:21','(Yambone kukwao ni) kukunda ni (kuŵecheta) maloŵe gambone. Sano naga ngondo jili jisimichiche (achinakunaku ikasayachima), nambo akansimichisye Allah (niya syao) ikaliji yambone kukwao.');
INSERT INTO "silika" VALUES(47,22,'47:22','Mwinetu jenumanja patin’galauche (kuleka chikulupi ni kuleka kumenyana ngondo ni makafili), tim’be nkuwatanganya pachilambo nikataga ulongo wenu (mwamwatendelaga kala ndema jaujinga).');
INSERT INTO "silika" VALUES(47,23,'47:23','Ŵanganyao ni aŵala ŵaŵalwesile Allah, niŵatesile kuŵa ŵangapikana, ni kugatenda kuŵa gangalola meso gao.');
INSERT INTO "silika" VALUES(47,24,'47:24','Ana ngaakujichetelela Qur’an, kapena m’mitima mwao mwana achinampatiika ŵakwe (mwanti ni jiwugalidwe kwangajipikana Qur’anijo)?');
INSERT INTO "silika" VALUES(47,25,'47:25','Chisimu aŵala ŵaagalauchile ku migongo jao (ku ukafili) panyuma pakumanyukuka kukwao chongoko, shetani jwasalalichisye (itendo yao yangalumbana), soni (Allah) nijwapele ndaŵi jelewu.');
INSERT INTO "silika" VALUES(47,26,'47:26','Yalakweyo ni ligongo lyanti ŵanganyao (achinakunaku) ŵatite pakwasalila aŵala (achimakafili) ŵaŵaichimile ayila yaŵatulwisye Allah: “Chitunkunde ine mu ichindu (yenu, mpela yakum’benga Muhammadi صلى الله عليه وسلم, ni kwagwisya ulesi ŵakusaka kuja kumenyana namwe ngondo).” Sano Allah ni akumanyilila asili syao.');
INSERT INTO "silika" VALUES(47,27,'47:27','Ana ichiŵa uli ndema jatachatyosyaga Achimalaika maumi gao pakuwa, achaputaga ngope syao ni migongo jao?');
INSERT INTO "silika" VALUES(47,28,'47:28','Yalakweyo ni ligongo lyanti ŵanganyao ŵaakuyiye yayampikasyisye Allah ntima, nikuchiŵenga chinonyelo Chakwe, basi (Allah) ni agajonasile masengo gao.');
INSERT INTO "silika" VALUES(47,29,'47:29','Ana akuganichisya aŵala ŵamuli m’mitima mwao ulwele (wa unakunaku) kuti Allah ngaŵa nkukukopochesya papaswela kusakalilwa kwao?');
INSERT INTO "silika" VALUES(47,30,'47:30','Soni tungasache tungannosisye ŵanganyao, basi nkaamanyilile ni imanyilo yao, nambo tinkomboleche kwamanya kwene mu kaŵechete (kao) kakunonyelesya, soni Allah akumanyilila itendo yenu.');
INSERT INTO "silika" VALUES(47,31,'47:31','Soni chitun’dinje kwene mayeso jenumanja mpaka twamanyukule akuputa ngondo (ligongo lya dini) mwa jenumanja, ni ŵakupilila, ni tusilochesye papaswela abali syenu.');
INSERT INTO "silika" VALUES(47,32,'47:32','Chisimu aŵala ŵaakufuulu ni kusiŵililaga (ŵandu) kwitala lya Allah, ni kunsisyaga Ntenga صلى الله عليه وسلم panyuma pakumanyukuka kukwao chongoko; nganaŵa apeleche chisausyo chilichose kwa Allah, ni tachigajonanga (Allah) masengo gao.');
INSERT INTO "silika" VALUES(47,33,'47:33','E jenumanja ŵankulupilile! Mumpikanilani Allah ni mumpikanile soni Ntenga صلى الله عليه وسلم, ni nkajonanga masengo genu.');
INSERT INTO "silika" VALUES(47,34,'47:34','Chisimu aŵala ŵaakufuulu, ni kusiŵililaga (ŵandu) kwitala lya Allah, kaneko nikuwa ali makafili, basi Allah ngasiŵakululuchila ŵanganyao.');
INSERT INTO "silika" VALUES(47,35,'47:35','Basi ngasimpela, nikuŵenda ntunjelele (kwa amagongo ŵenu), kutendaga jenumanja ni ŵakupunda, (nambo n’jendelechele kwakunula) soni Allah ali namwe, soni ngasampunguchisya (malipilo ga) itendo yenu.');
INSERT INTO "silika" VALUES(47,36,'47:36','Chisimu umi wa duniya uŵele masanje ni chanache, nambo naga nkulupilile, ni kuŵa ni woga (wakun’jogopa Allah pakuligosa ku ilemwa), (Allah) tampe malipilo genu, soni ngaakun’juga chipanje chenu (chosope kuti nchipeleche petala lya Allah).');
INSERT INTO "silika" VALUES(47,37,'47:37','Ali atite an’juje chalakwecho, ni kunkanganika, mpaka ntende ubayili, ni mpaka akukopocheyse papaswela kusakalilwa kwenu.');
INSERT INTO "silika" VALUES(47,38,'47:38','Ha! Jenumanja ni aŵala ŵankuŵilanjidwa kuti ntole petala lya Allah, basi ŵane mwa jenumanja pana ŵaakutenda ubayili. Sano jwaakutenda ubayili nikuti akutenda ubayili waalikonya nsyene, sano Allah ni Jwakupata kwejinji, nambo jenumanja ni ŵakulaga. Nambo naga nkugalauka (kuleka Usilamu, Allah) tantindanye niŵandu ŵane ŵangaŵaga jenumanja, soni ngasaŵa ŵanti mpela jenumanja (tachiŵa ŵambone).');
INSERT INTO "silika" VALUES(48,1,'48:1','Chisimu Uwwe tum’buguliile mmwe (Muhammadi صلى الله عليه وسلم) kuwugula kwakuonechela (wauli nkamulano wa pa Hudayibiya wawatendekasisye kuti Maaka gaugulidwe).');
INSERT INTO "silika" VALUES(48,2,'48:2','Kuti Allah ankululuchile sambi syenu syam’bujo ni syamunyuma, ni kunkwanilichisya chindimba Chakwe, ni kun’jongolela kwitala lyagoloka. ');
INSERT INTO "silika" VALUES(48,3,'48:3','Nikuti Allah ankamuchisye kamuchisya kwamachili.');
INSERT INTO "silika" VALUES(48,4,'48:4','Jwalakwe ni Ajula juŵatulwisye kutulala m’mitima mwa ŵakulupilila kuti ajonjechesye chikulupi pa chikulupi chao (chakalape). Sano Allah ni Nsyene asilikali ŵa kumawunde ni petaka, ni aŵele Allah Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(48,5,'48:5','(Allah atesile yalakweyi) kuti akaajinjisye ŵakulupilila ŵachilume ni ŵachikongwe ku Matimbe ga ku Mbepo, gasikujilima chapasi pakwe sulo, takaŵe ŵandamo mwalakwemo, nikuti ŵasimachisye ileŵi yao. Ni kuŵele kwalakweko pameso pa Allah kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(48,6,'48:6','Nikuti ŵape ilagasyo achinakunaku ŵachilume ni ŵachikongwe, kwisa soni ŵakuwanganya (Allah ni isanamu) ŵachilume ni ŵachikongwe, ŵaakun’ganichisyaga Allah nganisyo syangalumbana, pa ŵanganyao pana mpupulusi wakusakala, soni Allah ŵakasilichile, ŵalwesile, ni ŵalinganyichisye Jahannama, kaje kusakala malo gakuwujila gakwe!');
INSERT INTO "silika" VALUES(48,7,'48:7','Soni Allah ni Nsyene asilikali ŵa kumawunde ni petaka, ni aŵele Allah Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(48,8,'48:8','Chisimu Uwwe tuntumisye mmwe (Muhammadi صلى الله عليه وسلم) kuŵa mboni, ni jwakutagulila abali jakusengwasya, nambo soni jwakutetela.');
INSERT INTO "silika" VALUES(48,9,'48:9','Kuti jenumanja munkulupilile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni kunkamuchisya (Ntengajo) nambo soni kunchimbichisya, nikuti munswejesyeje (Allah) kundaŵi ni kwigulo.');
INSERT INTO "silika" VALUES(48,10,'48:10','Chisimu aŵala ŵaakutaŵana namwe chilanga (mmwe Muhammadi صلى الله عليه وسلم), chisimu akutaŵana chilanga ni Allah, nkono wa Allah uli pachanya pa makono gao, sano jwatakase (chilanga chakwecho), nikuti akukasa kwalikonya nsyene, sano jwatakwanilisye yajwataŵene najo Allah, basi (Allah) tachimpa malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(48,11,'48:11','Tansalile (mmwe Muhammadi صلى الله عليه وسلم) akusigalila munyuma (pangalongana namwe wa ku Maaka) mu ŵarabu ŵanchimisi: “Yatuŵijikenye ipanje yetu kwisa soni maŵasa getu, basi tuŵendelani chikululuko (kwa Allah).” Akuŵecheta ni ndimi syao yanganiyiŵa m’mitima mwao. Jilani: “Ana ni nduni jwampaka akombole kunkamuchisya chilichose kwa Allah nasakaga kumpa masausyo kapena nasakaga kunkamuchisya? Nambo Allah aŵele Jwaasimanya chenene abali syosope sya yankutenda.”');
INSERT INTO "silika" VALUES(48,12,'48:12','“Nambo mwaganichisyaga yanti Ntenga صلى الله عليه وسلم ni ŵakulupilila ngaaujila kumaŵasa gao ata panandi, ni yasalalisyidwe yalakweyo m’mitima mwenu, ni mwaganisyisye nganisyo syangalumbana, ni m’bele ŵandu ŵakonasika.”');
INSERT INTO "silika" VALUES(48,13,'48:13','Sano jwangaakunkulupilila Allah ni Ntenga Jwakwe صلى الله عليه وسلم, basi chisimu Uwwe tugalinganyichisye makafili Moto Wakulapuka.');
INSERT INTO "silika" VALUES(48,14,'48:14','Soni Allah ni Nsyene uchimwene wa kumawunde ni petaka, akunkululuchilaga jwansachile, ni kumpa ilagasyo jwansachile, ni aŵele Allah Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(48,15,'48:15','Taŵechete akusigalila munyuma pandema jatim’bigule kuja kuchipanje chakupalagula (cha ku Khaibara) kuti nkachijigale: “Tulekaani tunkuuye.” Akusaka agalawusye maloŵe ga Allah. Jilani: “Nganim’ba ntukuyiye. Mwele ni muŵaŵechetele kalape Allah.” Basi ŵanganyao tajile: “(Ngwamba), nambo nkututendela litima.” (Ngwamba), nambo aŵele akakumanyilila ikaŵeje panandipe.');
INSERT INTO "silika" VALUES(48,16,'48:16','Mwasalilani akusigalila munyuma mu ŵarabu ŵanchimisi (kuti): “Sampano tim’bilanjidwe wa (kumenyana nao) ŵandu ŵaakwete machili gakuputila ngondo mwaukali, tinkamenyane nao kapena takagambe kulipeleka. Basi naga nkunde, Allah tampe malipilo gambone, nambo naga n’galauche (kuleka kumenyana nao) yamwatite paagalauka kalakala, tannagasye ni ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(48,17,'48:17','Pangali gambidwa kwa jwangalola, soni pangali gambidwa kwa jwannemale, nambo soni pangali gambidwa kwa jwakulwala (naga ngaakwaula kungondo). Soni jwatampikanile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, takan’jinjisye ku Matimbe ga ku Mbepo gasikujilima sulo chapasi pakwe. Sano jwatagalauche, nikuti tannagasye ni ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(48,18,'48:18','Chisimu pamasile paŵasengwele Allah ŵakulupilila ndema jaŵataŵanaga namwe (mmwe Muhammadi صلى الله عليه وسلم) chilanga pasi pachitela, basi ni jwamanyilile yayaliji m’mitima mwao, ni ŵatuluchisye kutulala, ni ŵalipile kuwugula kwakuŵandika (kwakuwugula nsinda wa Khaibara ni Maaka).');
INSERT INTO "silika" VALUES(48,19,'48:19','Ni ipanje yakupalagula yaijinji yatachiyijigala, ni aŵele Allah Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(48,20,'48:20','Ampele chilanga Allah cha ipanje yakupalagula yaijinji yatinchiyijigala, basi ni an’janguyichisye kaje ayi (ya ku Khaibara), ni agachijile makono ga ŵandu kuti gakanyyichila, nikuti chiŵe chilosyo kwa ŵakulupilila (chakulosya chikamuchisyo Chakwe), soni kuti an’jongolele kwitala lyagoloka.');
INSERT INTO "silika" VALUES(48,21,'48:21','Ni yine (mu ipanje yakupalagula) yanganinyyikombola (kuipata), chisimu Allah pamasile paayisyungwile yalakweyo, ni aŵele Allah pa chindu chilichose Jwakombola kusyene.');
INSERT INTO "silika" VALUES(48,22,'48:22','Soni angajile ampute ngondo aŵala ŵaakufuulu, angalosisye migongo (kutila), kaneka nga nganampata nkugosa jwalijose namuno nkukamuchisya.');
INSERT INTO "silika" VALUES(48,23,'48:23','(Jalakwejo ni) nyendele ja Allah ajila jajalongolele kala, ni ngankusimana kupitikuka kulikose pa nyendele ja Allah.');
INSERT INTO "silika" VALUES(48,24,'48:24','Jwalakwe ni Ajula juŵagachijile makono gao kukwenu, ni makono genu kukwao nkati mwa (chiŵata cha) Maaka, panyuma pakumpundikasya pa jamanjao, ni juŵele Allah pa ichindu yankutenda Jwakuilolechesya.');
INSERT INTO "silika" VALUES(48,25,'48:25','Ŵanganyao ni aŵala ŵaakufuulu, ni kunsiŵilila ŵanganyammwe ku Nsikiti Wakuchimbichika (wa Maaka), ni kuisiŵilila inyama yambepesi kuti ikayika pamalo pakwe (pakusikitilidwa). Sano angaŵaga achalume ŵakulupilila ni achakongwe ŵakulupilila ŵanganimwamanya, kuti ntakwika mwamwawuleje nikupata jenumanja ligongo lya ŵanganyao sambi nkakumanyilila (nga nganagachiga makono genu pa wanganyao), (nambo jwatesile yalakweyo) kuti Allah an’jinjisye jwaakunsaka mu ukoto Wakwe. Ingaŵe kuti ŵanganyao (Asilamu ni makafili) ŵatalikangene, tungaapele aŵala ŵaŵakufuulu mwa jamanjao ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(48,26,'48:26','Pandema jaŵaŵisile aŵala ŵaŵakufuulu m’mitima mwao mbapata syagasabu, mbapata syagasabu syachijinga, basi Allah ni ŵatulwisye kutulala Kwakwe pa Ntenga Jwakwe صلى الله عليه وسلم ni pa ŵakulupilila, ni ŵatendekasyisye kulikanganichila liloŵe lya woga (wakun’jogopa Allah lya Lâ ilâha illallâh [Pangali nnungu jwine jwakuŵajilwa kun’galagatila ikaŵeje Allah]), ni ŵaliji ŵakuŵajilwa ni lyalakwelyo nambo soni ni achinsyene (liloŵelyo), ni aŵele Allah chindu chilichose Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(48,27,'48:27','Chisimu pamasile pansimichisye Allah Ntenga Jwakwe (Muhammadi صلى الله عليه وسلم) sagamisi mwakuonaonape, chisimu ŵanganyammwe tinchijinjila mu Nsikiti Wakuchimbichika (wa Maaka) nasakaga Allah mwantunjelele, n’di mmyosile mitwe jenu ni kupungusya (wumbo syenu), nkaakogopa. Basi Jwalakwe (Allah) jukumanyilila yangankuimanyilila jenumanja, basi ni juŵisile nkaniyiŵe yalakweyo (yakuujinjila Nsikiti Wakuchimbichika wa Maaka) kuwugula kwakuŵandika (kwa nsinda wa Khaibara).');
INSERT INTO "silika" VALUES(48,28,'48:28','Jwalakwe ni Ajula jwaatumisye Ntenga Jwakwe (Muhammadi صلى الله عليه وسلم) ni chongolo kwisa soni Dini jakuona kuti ajikwesye pachanya pa dini syosope, sano Allah ni akukwanila kuŵa Mboni.');
INSERT INTO "silika" VALUES(48,29,'48:29','Muhammadi صلى الله عليه وسلم ni Ntenga jwa Allah. Sano aŵala ŵali pamo ni jwalakwe ali ŵamachili nnope kwa achimakafili, (nambo) ŵakutendelana chanasa sikati jao. Timwaone achijinamaga ni kusujudu (kuswali), achisachililaga umbone wakuumila kwa Allah ni chinonyelo (Chakwe). Imanyilo yao ili kungope kwao ligongo lya kusujudu (pa kuswali). Ayi ni yatite kasimbidwe mu Taurat. Sano yatite kasimbidwe mu In’jil; (ŵanganyao) ali chisawu mmela waukopwesye nyambi syakwe, sano (nyambisyo) ni kuwulimbanganya (mmelao), ni komangala, ni kwimilichika pepata lyakwe, ni kwasengwasya akuupaanda, kuti agapikasye ntima makafili ligongo lya ŵanganyao. Allah ŵapele chilanga aŵala ŵaakulupilile ni kutendaga yambone mwa ŵanganyao cha chikululuko ni malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(49,1,'49:1','E jenumanja ŵankulupilile! Ngasinnongolela (kupeleka nganisyo syenu) pameso pa Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni mun’jogopeje Allah, chisimu Allah ni Jwakupikanichisya, Jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(49,2,'49:2','E jenumanja ŵankulupilile! Ngasinnyakulaga maloŵe genu pachanya pa liloŵe lya Ntume صلى الله عليه وسلم, nambo soni ngasimunnyanyichilaga jwalakwe liloŵe yankuti pakunyanyichilana ŵane kwa ŵane, gatakwika mugajonasiche masengo genu kutendaga jenumanja nkakumanyilila.');
INSERT INTO "silika" VALUES(49,3,'49:3','Chisimu aŵala ŵaakutondoya maloŵe gao kwa Ntenga jwa Allah صلى الله عليه وسلم, ŵanganyao ni aŵala ŵaajilinjile Allah mitima jao pa woga (wakun’jogopa Jwalakwe), akwete kupata ŵanganyao chikululuko ni malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(49,4,'49:4','Chisimu aŵala ŵaakum’bilanga (mmwe Muhammadi صلى الله عليه وسلم n’di) kusyeto kwa yumba, ŵajinji ŵao ŵangali lunda.');
INSERT INTO "silika" VALUES(49,5,'49:5','Soni ingaŵe ŵanganyao ŵapilile kuti mpaka nkopoche kuja kukwao, ingaliji yambone kwa jamanjao. Sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(49,6,'49:6','E junumanja ŵankulupilile! Naga juli jum’bichilile jwangakulupichika ni abali jilijose (ngasimunkulupilila), nambo n’jiwungunye (kaje), ntakwika mwamwalagasyisye ŵandu mwa ujinga, basi ni kusyuka kuŵa ŵakuligamba pa yampanganyisye.');
INSERT INTO "silika" VALUES(49,7,'49:7','Soni manyililani yanti mwa jenumanja mwana Ntenga jwa Allahصلى الله عليه وسلم(basi mumpikanilani yaakunsalila). Ingaŵe ŵankundilile mu ichindu yaijinji (yankuŵecheta) chisimu nkaliji m’matabu. Nambo Allah achinonyelesye kukwenu chikulupi, ni kuchisalalisya m’mitima mwenu, ni awutesile kuŵa wakuchimidwa kukwenu ukafili, unampitiku ni kunyosya. Ŵanganyao ni ŵali ŵagoloka.');
INSERT INTO "silika" VALUES(49,8,'49:8','(Awu ni) umbone wakuumila kwa Allah ni ukoto, soni Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(49,9,'49:9','Sano naga makuga gaŵili ga ŵakulupilila gakumenyana, basi jilanyani chilikati chao. Sano naga limo mu gaŵiligo likulitendela uwwanga linelyo, basi liputani alila lyalikutenda uwwanga mpaka liujile ku chilamusi cha Allah. Sano naga lili liujiile, basi jilanyani chilikati chao mwachilungamiko, soni golosyani (magambo), chisimu Allah akwanonyela ŵagolosya (magambo).');
INSERT INTO "silika" VALUES(49,10,'49:10','Chisimu ŵakulupilila wosope ali paulongo; basi jilanyani chilikati cha achalongo achimijenu, ni mun’jogopeje Allah kuti ntendeledwe chanasa.');
INSERT INTO "silika" VALUES(49,11,'49:11','E jenumanja ŵankulupilile! Ŵandu akaanyosya ŵandu achimijao, mwine kuŵa nkuŵa (akunyosyedwao) ni ŵali ŵambone (pameso pa Allah) kwapunda ŵanganyao, nombe nao achakongwe (akaanyosya) achakongwe achimijao, mwine kuŵa nkuŵa (akunyosyedwao) ni ŵali ŵambone (pameso pa Allah) kwapunda ŵanganyao. Ni ngasimpelegana ayibu mwachimisyene (pakumbana utundu), soni nkakolangana mena gachipongwe, kaje kusakala nnope (kolanjilidwa mundu) lina lyakuti nampitiku panyuma pakulupilila! Sano ŵatatende ngatenda toba, basi ŵanganyao ni ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(49,12,'49:12','E jenumanja ŵankulupilile! Liŵambasyani liganichisya lyejinji, chisimu line mwa liganichisya lili sambi, soni ngasimwajendaga ŵandu m’masajo, soni ŵane mwa jenumanja akaasogodaga ŵane. Ana jumo jwenu mpaka anonyelwe kulya nyama ja n’jakwe jwam’bwe? Mpaka iyyiŵenjetu yele; (basi kuŵengani nombe kusogodana), ni mun’jogopeje Allah. Chisimu Allah ni Jwakupochela nnope toba, Jwachanasa channope.');
INSERT INTO "silika" VALUES(49,13,'49:13','E jenumanja ŵandu! Chisimu Uwwe tun’gumbile kuumila mwa jwannume ni jwankongwe, ni tuntesile kuŵa mitundu ni ngosyo (syakulekangana-lekangana) kuti mmanyiganeje. Chisimu ŵakuchimbichika nnope ŵenu pameso pa Allah ni ŵawoga nnope ŵenu (wakun’jogopa Allah). Chisimu Allah ni Jwakumanyilila nnope, Jwaasimanya abali syosope.');
INSERT INTO "silika" VALUES(49,14,'49:14','Ŵarabu ŵanchimisi ŵatite: “Tukulupilile.” Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Nganinkulupilile. Nambo n’jileje: ‘Tulipeleeche (pakuŵa Asilamu).’ Ligongo mpaka sano chikulupi nganichijinjile m’mitima mwenu (ni kutamilichika). Sano naga nkunkunda Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ngampunguchisya chilichose mu (malipilo ga) itendo yenu. Chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(49,15,'49:15','Chisimu ŵakulupilila ni aŵala ŵankulupilile Allah ni Ntenga Jwakwe صلى الله عليه وسلم, kaneka ni nganakaichila, ni kuputa ngondo ni chipanje chao ni achimisyene ŵakwe petala lya Allah, ŵanganyao ni ŵali ŵakuona (pa chikulupi chao).');
INSERT INTO "silika" VALUES(49,16,'49:16','Jilani: “Ana jenumanja nkummanyisya Allah dini jenu, kutendaga Allah jukumanyilila ya kumawunde ni petaka? Soni Allah chindu chilichose aŵele Jwaachimanya chenene.”');
INSERT INTO "silika" VALUES(49,17,'49:17','Akunkuumba ligongo lyanti ajiinjiile mu Usilamu (mpela kuti antendele ukoto mmwe Muhammadi صلى الله عليه وسلم). Jilani: “Nkaanguumba nako kwinjila kwenu mu Usilamu. Nambo Allah ni jwantendele ukoto jenumanja ligongo lyanti an’jongolele ku chikulupi, nam’baga jenumanja ŵakuona (pa chikulupi chenu).”');
INSERT INTO "silika" VALUES(49,18,'49:18','Chisimu Allah akumanyilila yakusisika ya kumawunde ni petaka, soni Allah ni Nkuilolechesya yankutenda.');
INSERT INTO "silika" VALUES(50,1,'50:1','Qâf. Ngujilumbila Qur’an jakuchimbichika (kuti tinchiukulidwa m’malembe).');
INSERT INTO "silika" VALUES(50,2,'50:2','Nambo akusimonga ya kwaichilila nkutetela (Muhammadi صلى الله عليه وسلم) jwakuumila mu ŵanyapeo. Basi achimakafili ni akutiji: “Achi ni chindu chakusimosya.”');
INSERT INTO "silika" VALUES(50,3,'50:3','“Ana patuchiwapo, ni kusyuka litaka (chisimu uwwe tuchiukulidwa)? Kwalakweko ni kuwuja kwakutalichila (kwangaŵa nkupatikana).”');
INSERT INTO "silika" VALUES(50,4,'50:4','Chisimu Uwwe tukumanyilila yaikupungusya litaka mwajamanjao (mu ilu yao ndema jaakuola m’malembe), soni Kukwetu kwana Chitabu chakusunga (chilichose Al-Lauh Al-Mahfȗz).');
INSERT INTO "silika" VALUES(50,5,'50:5','Nambo ayikaaniile yakuona ndema ja yaichilile, ni ali mu kaŵe kecholoŵane (akulepela kulekanganya sikati ja yakuona ni yaunami).');
INSERT INTO "silika" VALUES(50,6,'50:6','Ana ngaakuliona liwunde pachanya pao yatutite kalitaŵe kakwe ni kalisalalisye kakwe! Soni nganilikola mapesa.');
INSERT INTO "silika" VALUES(50,7,'50:7','Ni litaka tulijasesye, ni tuŵisile palakwepo yakunyichisya (matumbi), ni tumesisye palakwepo ntundu uliose (wa mmela) wakusalala.');
INSERT INTO "silika" VALUES(50,8,'50:8','(Kuti iŵe yalakweyi) yakuwugula meso ni yakunkumbusya kapolo jwalijose jwakuwujila (kwa Allah pakutenda toba).');
INSERT INTO "silika" VALUES(50,9,'50:9','Ni tutulwisye kuumila kwinani mesi gaupile (wula), ni tumesyisye ni galakwego migunda kwisa soni songolo syaagungulidwa.');
INSERT INTO "silika" VALUES(50,10,'50:10','Ni mitende jelewulewu jajikwete itungatunga ya isogosi yakonyokonyo.');
INSERT INTO "silika" VALUES(50,11,'50:11','(Kuŵa) lisiki lya achikapolo (Ŵetu), ni tukuchisyusyaga nago (mesigo) chilambo chachiwe, iyyoyopeyo ni mukuchiŵela kopoka (kwa m’malembe).');
INSERT INTO "silika" VALUES(50,12,'50:12','Ŵaakaaniile paujo pao (Makuraishi nkanigaiche) ŵandu ŵa Nuhu, achimisyene chisima ni achina Samuda.');
INSERT INTO "silika" VALUES(50,13,'50:13','Ni achina Adi, Firiauna ni achalongo achimijakwe Lutwi.');
INSERT INTO "silika" VALUES(50,14,'50:14','Ni akutama nyyitekete (ŵandu ŵa Shuaibu), ni ŵandu ŵa Tubba‘i (ŵa ku Yemeni), ŵaliose mwa jamanjao ŵaakaaniile Achimitenga, basi ni chasimichiche chitetelo Changu (pa ŵanganyao).');
INSERT INTO "silika" VALUES(50,15,'50:15','Ana ni twapesile ni gumba kwandanda? (Ngwamba), nambo ŵanganyao ali mu kaichila ya gumba kwasambano.');
INSERT INTO "silika" VALUES(50,16,'50:16','Soni chisimu Uwwe tun’gumbile mundu, ni tukumanyilila yaikunsongonela ntima wakwe, soni Uwwe tuli dusi nnope kwa jwalakwe (ni umanyilisi Wetu) kupunda ntasi ŵakuuta myasi (wannukosi mwakwe).');
INSERT INTO "silika" VALUES(50,17,'50:17','(Kumbuchilani) katema kaakupochela (itendo ya mundu ni kuilemba) akupochela ŵaŵili (Achimalaika) ŵaakutama chakun’dyo ni chakunchiji.');
INSERT INTO "silika" VALUES(50,18,'50:18','Ngaakulumukulaga (mundu) liloŵe lililyose, ikaŵeje kukwakwe kukuŵa kwana nkwembecheya jwatayali (kulemba).');
INSERT INTO "silika" VALUES(50,19,'50:19','Ni kuuchiyika kolelwa kwa chiwa mwakuonaonape, (ni tachisalilidwa mundu yanti): “Ayi ni ayila yamwaliji nkuiŵambala.”');
INSERT INTO "silika" VALUES(50,20,'50:20','Ni lichigombedwa lipenga, lyele ni lisiku lyaipotesi.');
INSERT INTO "silika" VALUES(50,21,'50:21','Ni tachiyika mundu jwalijose ni akunchiinga ni akuntendela umboni (ŵachilaika).');
INSERT INTO "silika" VALUES(50,22,'50:22','(Tachisalilidwa nkuleeŵa kuti): “Chisimu mmwe mwaliji nkukwanjila ya yalakweyi, basi tum’bunukwile chisiŵilo chenu, mwanti liso lyenu lelo lili lyakutema.”');
INSERT INTO "silika" VALUES(50,23,'50:23','Ni tachiŵecheta n’jakwe jwangalekangana najo (lilaika lyalyalembaga masengo gakwe): “Ayi ni yaili kukwangu tayali (yamasengo gakwe gangalumbana).”');
INSERT INTO "silika" VALUES(50,24,'50:24','(Allah tachalamula Achimalaika ŵaŵili ŵala lyakuchiinga ni lyakutendela umboni kuti): “Mumponyaani ku Jahannama jwalijose jwakufulu nnope, jwamakani.”');
INSERT INTO "silika" VALUES(50,25,'50:25','“Jwakusiŵilila yambone, jwakusumba mpika, jwakaichila.”');
INSERT INTO "silika" VALUES(50,26,'50:26','“Ajula juŵaŵisile nnungu jwine (jwakun’galagatila) pampepe ni Allah, basi mumponyani mu ilagasyo yawukali.”');
INSERT INTO "silika" VALUES(50,27,'50:27','Ni tachiŵecheta n’jakwe jwangalekangana najo (jwachishetani): “Ambuje ŵetu! Une nganinansochelesya jwalakweju, nambo kuti jwaliji nkupotela kwakutalikangana nnope (ni yakuona).”');
INSERT INTO "silika" VALUES(50,28,'50:28','(Allah) tachiti: “Ngasinkangana pameso Pangu, soni namasile panannongochesye chitetelo Changu.”');
INSERT INTO "silika" VALUES(50,29,'50:29','“Ngalikasagalausyidwaga liloŵe Kukwangu, soni Une nganimba jwakwatenda lupuso ata panandi achikapolo (Ŵangu).”');
INSERT INTO "silika" VALUES(50,30,'50:30','Lisiku lyatuchijiwusya Jahannama kuti: “Ana n’gumbele?” Basi jalakwe ni jichiti: “Ana pana yakonjechesya?” ');
INSERT INTO "silika" VALUES(50,31,'50:31','Ni jichiŵandichisyidwa Mbepo kwa ŵaoga (wakun’jogopa Allah), ngasijiŵa pakutalichila.');
INSERT INTO "silika" VALUES(50,32,'50:32','(Tachisalilidwa yanti): “Ayi ni yamwapedwaga chilanga, ya jwalijose jwakuujilaujila (kwa Allah pakutenda toba), jwakusunga (malamusi Gakwe).”');
INSERT INTO "silika" VALUES(50,33,'50:33','“Juŵan’jogwepe Allah mwangali kum’bona, ni jwaiche ni ntima wagalauchila (Kukwakwe pakutenda toba).”');
INSERT INTO "silika" VALUES(50,34,'50:34','(Tachisalilidwa yanti): “Jijinjilani (Mbepojo) mwantendele. Ali ni lisiku lya umi wandamo.”');
INSERT INTO "silika" VALUES(50,35,'50:35','Akwete kupata ŵanganyao chilichose chatakachisacheje mwalakwemo, soni Kukwetu kwana yakonjechesya (kum’bona Allah).');
INSERT INTO "silika" VALUES(50,36,'50:36','Ana mikutula jilingwa jatwajijonasile paujo pao, ŵaŵaliji ŵamachili nnope kwapunda ŵanganyaŵa, nambo soni ŵasyungwile-syungwile mu ilambo, ana ni ŵapapatile pakutilila (ndema ja yaichilile ipotesi, ngati ŵajonasiche)?');
INSERT INTO "silika" VALUES(50,37,'50:37','Chisimu mu yalakweyo muŵele mwana chikumbusyo kwa mundu jwakwete ntima (waganisya), kapena jwaakutejela liwiwi ni nsyenejo juli papopo (ni nganisyo syakwe).');
INSERT INTO "silika" VALUES(50,38,'50:38','Soni chisimu chene twagumbile mawunde ni mataka ni yaili chilikati chakwe m’moŵa nsano nalimo, ni nganikutukwaya kupela.');
INSERT INTO "silika" VALUES(50,39,'50:39','Basi pililani pa yaakuŵecheta, ni nnuswejesyeje lumbili lwa Ambuje ŵenu (Allah) lyuŵa nkanilikopoche (pakuswali swala ja Fajr), nambo soni nkanilitiŵile (pakuswali swala ja Dhuhr ni Asr).');
INSERT INTO "silika" VALUES(50,40,'50:40','Nambo soni chilo luswejesyani (lumbili lwa Ambuje ŵenu pakuswali swala ja Maghrib ni Isha), ni panyuma pa kusujudu (panyuma pakuswali swala sya Fard nswaliji soni swala sya sunna, nikutendaga Subhânallâh, Alhamdulillâh ni Allâh-Akbar).');
INSERT INTO "silika" VALUES(50,41,'50:41','Soni pikanani (ya) lisiku lyatachiŵilanga nkuŵilanga kuumila pamalo pakuŵandika.');
INSERT INTO "silika" VALUES(50,42,'50:42','Lisiku lyatachiupikana n’gumilo (wa lipenga) mwakuonaonape, lyele ni lisiku lyakopoka (m’malembe).');
INSERT INTO "silika" VALUES(50,43,'50:43','Chisimu Uwwe ni Ŵatukupelekaga umi ni chiwa, soni Kukwetu ni kwakuwujila.');
INSERT INTO "silika" VALUES(50,44,'50:44','Lisiku lyaliichaaganjichila litaka (kuti akopochemo) mwachitema-chitema, wele ni nsonganganyo wakwepepala nnope Kukwetu.');
INSERT INTO "silika" VALUES(50,45,'50:45','Uwwe tukumanyilila chenene yaakuŵecheta. Soni mmwe (Muhammadi صلى الله عليه وسلم) nganim’ba ŵakwakanganichisya ŵanganyao (ku chikulupi). Basi munkumbusyani ni Qur’an jwaakogopa chitetelo Changu.');
INSERT INTO "silika" VALUES(51,1,'51:1','Ngujilumbila (mbungo) jajikututumya luwundu (lwamesi ni kuŵelekasya mawunde gawula).');
INSERT INTO "silika" VALUES(51,2,'51:2','Ni (ngugalumbila soni mawunde) gagakutwichila mesi gausito.');
INSERT INTO "silika" VALUES(51,3,'51:3','Ni (yombo) yaikwenda (m’mbwani) mwakutulalila. ');
INSERT INTO "silika" VALUES(51,4,'51:4','Ni (Achimalaika) ŵaakugaŵanya chilamusi (cha Allah, mpela wula ni malisiki gane).');
INSERT INTO "silika" VALUES(51,5,'51:5','Chisimu yankupedwa chilanga iŵele yakuonaonape.');
INSERT INTO "silika" VALUES(51,6,'51:6','Soni chisimu malipilo gaŵele gakupatikana.');
INSERT INTO "silika" VALUES(51,7,'51:7','Ngulilumbila liwunde lyalikwete kagumbidwe kambone.');
INSERT INTO "silika" VALUES(51,8,'51:8','Chisimu ŵanganyammwe n’di pa maloŵe gakutindana (gakwamba Qur’an ni Muhammadi صلى الله عليه وسلم).');
INSERT INTO "silika" VALUES(51,9,'51:9','Akugalawusyidwa ni galakwego (kuleka kujikulupilila Qur’an ni Muhammadi صلى الله عليه وسلم) jwagalawusyidwe (kuleka chikulupi cha Tauhidi).');
INSERT INTO "silika" VALUES(51,10,'51:10','Pamasile palaniwe ŵaunami.');
INSERT INTO "silika" VALUES(51,11,'51:11','Aŵala ŵaatiŵiile mu ujinga, achikwanjilaga (ya Akhera).');
INSERT INTO "silika" VALUES(51,12,'51:12','Akuwusyaga (mwachipongwe): “Ni chakachi (kwika kwakwe lyele) Lisiku Lyamalipilo?”');
INSERT INTO "silika" VALUES(51,13,'51:13','(Lyele ni) lisiku lyatachilagasyidwa (pakuchomedwa) ku Moto.');
INSERT INTO "silika" VALUES(51,14,'51:14','(Tachisalilidwa kuti): “Pasyani kulagasyidwa kwenu, ayi ni ayila yamwaliji nkuijanguya (kuti iyicheje, sano ni iyiche).”');
INSERT INTO "silika" VALUES(51,15,'51:15','Chisimu ŵawoga (wakun’jogopa Allah) tachiŵa m’Matimbe ga ku Mbepo ni achitutumule.');
INSERT INTO "silika" VALUES(51,16,'51:16','Achipochelaga yaŵapele Ambuje ŵao (Allah). Chisimu ŵanganyao nkaniyiŵe yalakweyo (ku duniya) ŵaliji ŵakolosya (masengo).');
INSERT INTO "silika" VALUES(51,17,'51:17','Ŵaliji nkugona panandipe chilo.');
INSERT INTO "silika" VALUES(51,18,'51:18','Soni kumasikusiku ŵanganyao ŵaliji nkuŵenda chikululuko (kwa Allah).');
INSERT INTO "silika" VALUES(51,19,'51:19','Ni m’chipanje mwao mwaliji mwana liwunjili lyakumpa nkuŵenda ni nkwimidwa (ligongo lya kulisiŵilila kuŵenda).');
INSERT INTO "silika" VALUES(51,20,'51:20','Ni petaka pana ilosyo (yakulosya ukombosi wa Allah) kwa ŵakusimichisya (chikulupi chao).');
INSERT INTO "silika" VALUES(51,21,'51:21','Ni mu (kagumbidwe ka) jenumanja ŵakwe, ana ngankulola?');
INSERT INTO "silika" VALUES(51,22,'51:22','Soni kwinani ni kwalili lisiki lyenu ni yankupedwa chilanga.');
INSERT INTO "silika" VALUES(51,23,'51:23','Basi ngunnumbila M’mbuje jwa kwinani ni pasi, chisimu yalakweyi yakuona, mpela muyiŵelele (yakuona) yanti jenumanja nkuŵechetaga.');
INSERT INTO "silika" VALUES(51,24,'51:24','Ana jinyyiche abali ja achalendo ŵa Ibrahima ŵakuchimbichikwa?');
INSERT INTO "silika" VALUES(51,25,'51:25','Pa ndema jaŵajinjile kukwakwe ni ŵatite: “Ntendele!” Jwalakwe jwatite: “Ntendele!” (Ni jwatite soni chamuntima: “Jenumanja ni) ŵandu ŵangamanyika (kukwangu).”');
INSERT INTO "silika" VALUES(51,26,'51:26','Ni ŵagalauchile ku ŵapeŵasa ŵakwe (mwangalolechela), ni ŵayiche nako kan’gombe kakunakana (kejoche).');
INSERT INTO "silika" VALUES(51,27,'51:27','Ni jwakaŵandichisye kukwao. (Pajwagaweni magasa gao kuti ngagakujigala kulya) jwatite: “Ana uli ngankulya?”');
INSERT INTO "silika" VALUES(51,28,'51:28','Basi ni wampatile woga wakwajogopa ŵanganyao. Ŵanyao ŵatite: “Nkajogopa.” Ni ŵampele abali jakusengwasya janti chakole mwanache jwakumanyilila nnope.');
INSERT INTO "silika" VALUES(51,29,'51:29','Ni ŵayiche ŵankwakwe (ŵali Sara) ni masegwe, aku ali nkuliputa kungope (ligongo lya usimongo), ni ŵatite: “Jwanchekulupe, jwampalale (timbeleche chinauli)!”');
INSERT INTO "silika" VALUES(51,30,'51:30','Ŵanganyao ŵatite: “Iyyoyopeyo (tim’beleche mwam’belelemo), Ambuje ŵenu (Allah) niyatite, chisimu Ŵalakweo ni Ŵalunda lwakusokoka, Ŵakumanyilila nnope.”');
INSERT INTO "silika" VALUES(51,31,'51:31','(Kaneka Ibrahima) jwatite: “Abali jao (jine) jaichile nijapi jaomanja akutumidwa?”');
INSERT INTO "silika" VALUES(51,32,'51:32','Ŵanganyao ŵatite: “Chisimu uwwe tutumisyidwe ku ŵandu ŵakuleŵa.”');
INSERT INTO "silika" VALUES(51,33,'51:33','“Kuti tukaatumichisye sakalawe syelongo (lya ku Moto).”');
INSERT INTO "silika" VALUES(51,34,'51:34','“Syeŵichidwe chimanyilo kwa Ambuje ŵenu (Allah) kwaŵichila akusumba mpika (pasambi).”');
INSERT INTO "silika" VALUES(51,35,'51:35','Basi ni twaakopwesye ŵaŵaliji mwalakwemo mwa akukulupilila.');
INSERT INTO "silika" VALUES(51,36,'51:36','Nambo nganitujisimana mwalakwemo ikaŵeje nyumba jimpepe ja Asilamu (Lutwi ni ŵanache ŵakwe ŵaŵili ŵachikongwe).');
INSERT INTO "silika" VALUES(51,37,'51:37','Ni twalesile mwalakwemo chilosyo (cha ipotesi jajili mbwani jajiwe) kwa ŵandu ŵaakuijogopa ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(51,38,'51:38','Nombe mu (abali ja) Musa (muŵele mwana chilosyo cha ipotesi), pandema jatwantumisye kwa Firiauna ni umboni wakuonechela.');
INSERT INTO "silika" VALUES(51,39,'51:39','Basi (Firiauna) jwanyosyisye ligongo lya machili gakwe, ni jwatite: “(Aju ni) nsaŵi kapena jwamasoka.”');
INSERT INTO "silika" VALUES(51,40,'51:40','Ni twankwembekenye jwalakwe ni asilikali ŵakwe ni twaaponyisye m’mbwani, jwalakwe juli jwagambidwa.');
INSERT INTO "silika" VALUES(51,41,'51:41','Nombe mu (abali ja) achina Adi (muŵele mwana chilosyo cha ipotesi), pandema jatwatumichisye mbungo (jachimbunga) jakonanga.');
INSERT INTO "silika" VALUES(51,42,'51:42','Nganijichilekaga chindu chilichose chajichipujiile ikaŵeje kuchitenda mpela chepukuswe.');
INSERT INTO "silika" VALUES(51,43,'51:43','Nombe mu (abali ja) achina Samuda (muŵele mwana chilosyo cha ipotesi), pandema jaŵasalilidwe kuti: “Sengwagani kwakandaŵi kannono.”');
INSERT INTO "silika" VALUES(51,44,'51:44','Ni ŵalikwesisye kuleka lilamusi lya M’mbuje gwao (Allah), ni wakwembekenye n’gumilo (wa ipotesi) aku ali nkulola.');
INSERT INTO "silika" VALUES(51,45,'51:45','Ni nganakombola kwima (kuti atile), soni nganaŵa ŵanti nikulikulupusya (achimisyene).');
INSERT INTO "silika" VALUES(51,46,'51:46','Ni (twajonasile) ŵandu ŵa Nuhu kalakala (nkanaŵe ŵasalidweo), chisimu ŵanganyao ŵaliji ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(51,47,'51:47','Sano liwunde twalitaŵile mwamachili, soni chisimu Uwwe tuŵele Ŵakulisapangula (liwundelyo).');
INSERT INTO "silika" VALUES(51,48,'51:48','Ni litaka twalitandiche, basi ni Ŵambone nnope mwa akwasasya (twaliji Uwwe)!');
INSERT INTO "silika" VALUES(51,49,'51:49','Ni mu chindu chilichose tugumbile iŵili-iŵili (mpela chachilume ni chachikongwe), kuti jenumanja nkumbuchile (ukoto wa Allah).');
INSERT INTO "silika" VALUES(51,50,'51:50','Basi (mwasalilani yanti): “Tililani kwa Allah, chisimu une (Muhammadi صلى الله عليه وسلم) kukwenu ni nkutetela jwakuonechela jwakuuma Kukwakwe.”');
INSERT INTO "silika" VALUES(51,51,'51:51','“Soni ngasim’biika pamo ni Allah nnungu jwine, chisimu une (Muhammadi صلى الله عليه وسلم) kukwenu ni nkutetela jwakuonechela jwakuuma Kukwakwe.”');
INSERT INTO "silika" VALUES(51,52,'51:52','Iyyoyopeyo (yaakuti pakuntenda mmwe Muhammadi صلى الله عليه وسلم kuti nsaŵi kapena jwamasoka), nganijwaichilileje aŵala ŵapaujo pao ntenga jwalijose ikaŵeje ŵanganyao ŵatiji: “(Aju ni) nsaŵi kapena jwamasoka.”');
INSERT INTO "silika" VALUES(51,53,'51:53','Ana ate kulechelana gele (maloŵega)? (Ngwamba), nambo ŵanganyao ni ŵandu ŵakusumba mpika (pa ukafili).');
INSERT INTO "silika" VALUES(51,54,'51:54','Basi mwatundumalilani ŵanganyao (mmwe Muhammadi صلى الله عليه وسلم), soni mmwe ngam’ba ŵagambidwa (nkwatundumalilamo).');
INSERT INTO "silika" VALUES(51,55,'51:55','Soni kumbusyani, basi chisimu kumbusya tikwakamuchisye ŵakulupilila.');
INSERT INTO "silika" VALUES(51,56,'51:56','Soni nganingamba gumba majini ni ŵandu ikaŵeje kuti angalagatileje Une (Jikape).');
INSERT INTO "silika" VALUES(51,57,'51:57','Ngangusaka kuumila kukwao lisiki, soni ngangusaka kuti andiisyeeje (kapena kuliisya iwumbe Yangu).');
INSERT INTO "silika" VALUES(51,58,'51:58','Chisimu Allah ni Nkupeleka lisiki nnope, Nsyene machili gakulimbangana.');
INSERT INTO "silika" VALUES(51,59,'51:59','Sano chisimu akwete kupata aŵala ŵalitendele lupuso chipande cha ilagasyo chanti mpela chipande chailagasyo cha achimijao (ŵakala), basi ngasanjanguya (kuti napeje ilagasyoyo ndaŵi jakwe nkanijikwane).');
INSERT INTO "silika" VALUES(51,60,'51:60','Basi ipotesi tiyichiŵa kwa aŵala ŵaakufuulu pa lisiku lyao alila lyaakupedwa chilanga (cha kwika kwakwe).');
INSERT INTO "silika" VALUES(52,1,'52:1','Ngulilumbila litumbi (lyaŵam’bechetakasyisye Allah Musa lyalili ku Sinayi).');
INSERT INTO "silika" VALUES(52,2,'52:2','Ni chitabu chachilembeedwe (Qur’an).');
INSERT INTO "silika" VALUES(52,3,'52:3','Mu ikalakala yepende yewunukulidwe.');
INSERT INTO "silika" VALUES(52,4,'52:4','Ni Al-Bait-ul-Ma‘mȗr (Nyumba jajili kumawunde mwakulondana ni Likaaba, jaakujijaulilaga Achimalaika mwakuŵilinganya).');
INSERT INTO "silika" VALUES(52,5,'52:5','Ni nsakasa wenyakulidwe (liwunde).');
INSERT INTO "silika" VALUES(52,6,'52:6','Ni mbwani syegumbasye (mesi).');
INSERT INTO "silika" VALUES(52,7,'52:7','Chisimu ilagasyo ya Ambuje ŵenu (Allah) iŵele yakupatikana kwene.');
INSERT INTO "silika" VALUES(52,8,'52:8','Pangali jwakuisiŵilila.');
INSERT INTO "silika" VALUES(52,9,'52:9','Lisiku lyalichitenganyika liwunde ni ntenganyiko wamachili.');
INSERT INTO "silika" VALUES(52,10,'52:10','Ni gachijenda matumbi ni kajende kaluŵilo.');
INSERT INTO "silika" VALUES(52,11,'52:11','Basi ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(52,12,'52:12','Aŵala ŵaakuŵa nkung’anda mu yangali mate.');
INSERT INTO "silika" VALUES(52,13,'52:13','Lisiku lyatachitutilidwa ku moto wa Jahannama katutilidwe kamachili.');
INSERT INTO "silika" VALUES(52,14,'52:14','(Ni tachisalilidwa yanti): “Awu ni Moto awula wamwaliji nkuwukaanila.”');
INSERT INTO "silika" VALUES(52,15,'52:15','“Ana weleu usaŵi, kapena jenumanja ngankulola?”');
INSERT INTO "silika" VALUES(52,16,'52:16','“Ujinjilani (ni kupasya kupoteka kwakwe), pililani kapena nkapilila yalumope kukwenu, chisimu nkulipidwa yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(52,17,'52:17','Chisimu ŵawoga (wakun’jogopa Allah) tachiŵa m’Matimbe ga ku Mbepo, ni chindimba.');
INSERT INTO "silika" VALUES(52,18,'52:18','Achiyisengwelaga yaŵapele M’mbuje gwao (Allah), aku M’mbuje gwao ali ŵagosile ku ilagasyo ya Moto Waukali.');
INSERT INTO "silika" VALUES(52,19,'52:19','(Tachasalila kuti): “Lyagani soni mwagani mwakusengwa ligongo lya ayila (yambone) yamwaliji nkutenda.”');
INSERT INTO "silika" VALUES(52,20,'52:20','Ali ajegeme mu itengu yauchimbichimbi yejalidwe chenene, ni tuchalombekasya ni achimmasyeto ŵameso gakulanguka soni gekulungwa mwakusalalila.');
INSERT INTO "silika" VALUES(52,21,'52:21','Sano aŵala ŵaakulupilile ni kwaakuuya ŵanache ŵao pa chikulupi, chitukasimanganye ŵanganyao ni ŵanache ŵao, soni ngatuja kwapunguchisya (malipilo ga) masengo gao chilichose. Mundu jwalijose tachiŵa uwombosi wakuliombolela ku (yakusakala) yaŵapanganyisye (pakutaŵidwa ku Moto).');
INSERT INTO "silika" VALUES(52,22,'52:22','Ni tukaajonjechesyeje (ŵakulupililao ndaŵi ni katema) isogosi kwisa soni nyama, mu yataakaisacheje.');
INSERT INTO "silika" VALUES(52,23,'52:23','Takapochelanganeje kwalakweko ipanda (yankologo) wangatendekasya kuŵecheta yangali mate namuno yasambi.');
INSERT INTO "silika" VALUES(52,24,'52:24','Ni taakaasyuunguleje ŵanganyao achiboyi ŵao (ŵakwatumichila), gamba kuŵa ŵanganyao (kamelemende kakwe) nti chisawu ngalekale syesunjidwe.');
INSERT INTO "silika" VALUES(52,25,'52:25','Ni tachigalauchilana ŵane kwa ŵane achiwusyanaga (yatite kawupate ntendele wa ku Mbepo).');
INSERT INTO "silika" VALUES(52,26,'52:26','Tachiti: “Chisimu uwwe twaliji kala ku maŵasa getu tuli nkogopa (ipotesi ya Allah).”');
INSERT INTO "silika" VALUES(52,27,'52:27','“Basi Allah atutendele ukoto uwwe ni atugosile ku ilagasyo ya Moto.”');
INSERT INTO "silika" VALUES(52,28,'52:28','“Chisimu uwwe twaliji kala (ku duniya) tuli nkum’bomba (Jwele Jikape), chisimu Jwalakwe ni Jwambone nnope, Jwachanasa channope.”');
INSERT INTO "silika" VALUES(52,29,'52:29','Basi kumbusyani (mmwe Muhammadi صلى الله عليه وسلم), soni mmwe nganim’ba abimbi kapena ŵamasoka pa chindimba cha Ambuje ŵenu (champele, wauli utume).');
INSERT INTO "silika" VALUES(52,30,'52:30','Kapena ŵanganyao akutiji: “(Jwalakweju ni) nkwiimba ilaanga, tugamba kun’jembechela kuti chimpate chiwa (yachatite pakwapata akwiimba ŵane ŵaakala)?”');
INSERT INTO "silika" VALUES(52,31,'52:31','Jilani: “Jembechelaani, chisimu une none ndinamwe mwa akwembecheya.”');
INSERT INTO "silika" VALUES(52,32,'52:32','Kapena lunda lwao ni lwalukwalamulila yalakweyi, kapena agambile kuŵa ŵandu ŵakusumba mpika?');
INSERT INTO "silika" VALUES(52,33,'52:33','Kapena akutiji: “Jwalakwe (Muhammadi صلى الله عليه وسلم) ni jwaajituunjile (Qur’aniji)?” (Ngwamba), nambo kuti ŵanganyao ngaakukulupilila.');
INSERT INTO "silika" VALUES(52,34,'52:34','Basi ayiche najo abali janti mpela jalakweji (Qur’an), naŵaga jamanjao akuŵecheta yakuona.');
INSERT INTO "silika" VALUES(52,35,'52:35','Kapena ŵagumbidwe mwangali chilichose (chakwagumba), kapena ŵanganyao ni ŵaŵaligumbile (achimisyene)?');
INSERT INTO "silika" VALUES(52,36,'52:36','Kapena ŵanganyao ni ŵaŵagumbile mawunde ni mataka? (Ngwamba), nambo kuti ngakusimichisya (ya ilagasyo ya Allah).');
INSERT INTO "silika" VALUES(52,37,'52:37','Kapena akwete ŵanganyao imatiilo ya Ambuje ŵenu (Allah), kapena ŵanganyao ni ŵaakwete machili gakutendela yaakusaka?');
INSERT INTO "silika" VALUES(52,38,'52:38','Kapena akwete makwelelo (gakwelela kwinani) ni akupikanilaga pa galakwego (yakuŵecheta Achimalaika kwinaniko)? Basi ayiche naotu akupikanila ŵaowo umboni wakuonechela.');
INSERT INTO "silika" VALUES(52,39,'52:39','Kapena Jwalakwe (Allah) ni jukwete ŵanache ŵachikongwe, sano jenumanja ni nkwete ŵanache ŵachilume?');
INSERT INTO "silika" VALUES(52,40,'52:40','Kapena mmwe (Muhammadi صلى الله عليه وسلم) nkwajuga ŵanganyao malipilo, sano jamanjao ni akusitopelwa nasyo ngongole?');
INSERT INTO "silika" VALUES(52,41,'52:41','Kapena akwete ŵanganyao (ilimu) yakusisika, basi ŵanganyao ni akulemba (yalakweyo).');
INSERT INTO "silika" VALUES(52,42,'52:42','Kapena akusaka kutenda maliindi? Basi aŵala ŵaakufuulu ni ŵali ŵakwaujila malindi gao.');
INSERT INTO "silika" VALUES(52,43,'52:43','Kapena akwete ŵanganyao nnungu jwine jwangaŵaga Allah? Aswejele Allah ku yaakum''bwanganya nayo.');
INSERT INTO "silika" VALUES(52,44,'52:44','Namuno angachione chipande cha liwunde chinankugwa (kukwao kwakuli kwika kwa ipotesi), angatite: “(Aga ni) mawunde gawula gagakamatene (ngaŵa kwika kwa ipotesi).”');
INSERT INTO "silika" VALUES(52,45,'52:45','Basi mwalekaani, mpaka tasimangane nalyo lisiku lyao alila lyatachikomoka kwa kuwa mu lyalakwelyo.');
INSERT INTO "silika" VALUES(52,46,'52:46','Lisiku lyaangaakamuchisya malindi gao chilichose, soni ŵanganyao ngasakulupusyidwa.');
INSERT INTO "silika" VALUES(52,47,'52:47','Soni chisimu akwete kupata aŵala ŵaatesile lupuso (ligongo lya ukafili wao) ilagasyo (ine pa duniya pano) nkaniyiŵe yalakweyo (ya ku Akhera), nambo ŵajinji mwa ŵanganyao ngakumanyilila.');
INSERT INTO "silika" VALUES(52,48,'52:48','Basi chijembecheyaani mwakupilila (mmwe Muhammadi صلى الله عليه وسلم) chilamusi cha Ambuje ŵenu (Allah), basi chisimu mmwe nkuloleledwa ni meso Getu, ni lluswejesyeje lumbili lwa Ambuje ŵenu (Allah) ndema jankwiimuka (kuumila llugono kapena pamalo palipose).');
INSERT INTO "silika" VALUES(52,49,'52:49','Nombe soni chilo lluswejesyeje (lumbili lwa Ambuje ŵenu), ni pakutiŵila ndondwa (kumasikusiku).');
INSERT INTO "silika" VALUES(53,1,'53:1','Ngujilumbila ndondwa ndema ja jikutiŵila.');
INSERT INTO "silika" VALUES(53,2,'53:2','Nganapotela n’jenuju (Muhammadi صلى الله عليه وسلم), soni nganapombotala.');
INSERT INTO "silika" VALUES(53,3,'53:3','Soni ngajukuŵecheta ya chisako (chakwe).');
INSERT INTO "silika" VALUES(53,4,'53:4','Nganigaŵa galakwego ikaŵeje maloŵe gakuuwula gagakuuwulidwa.');
INSERT INTO "silika" VALUES(53,5,'53:5','An’jiganyisye (Qur’anijo Jibulilu) jwamachili gamajinji.');
INSERT INTO "silika" VALUES(53,6,'53:6','Jwajukwete kaoneche kambone. Basi ni jwatamilichiche (pakaoneche kakwe kasyesyene kaŵan’guumbiile Allah).');
INSERT INTO "silika" VALUES(53,7,'53:7','Jwalakwe (Jibulilu) juli pachipelela cha meso chamwinani nnope.');
INSERT INTO "silika" VALUES(53,8,'53:8','Kaneka ni jwaŵandichile (Jibulilu), basi ni konjechesya soni nnope kuŵandichila.');
INSERT INTO "silika" VALUES(53,9,'53:9','Ni waliji (uŵandikane wao) mpela sikati ja songa siŵili sya ukunje, kapena kuŵandichila nnope (kupunda pele).');
INSERT INTO "silika" VALUES(53,10,'53:10','Basi (Allah) ni jwaajuuwiile kwa kapolo Jwakwe (Muhammadi صلى الله عليه وسلم) ya jwaajuuwiile (kupitila mwa Jibulilu).');
INSERT INTO "silika" VALUES(53,11,'53:11','Ntima (wa Muhammadi صلى الله عليه وسلم) nganiulambusya pa yajwaiweni (ni meso gakwe).');
INSERT INTO "silika" VALUES(53,12,'53:12','Ana ni nkukangana najo pa yajukuiona?');
INSERT INTO "silika" VALUES(53,13,'53:13','Soni chisimu (Muhammadi صلى الله عليه وسلم) ŵam’bweni (Jibulilu) ndema jine (chilo cha Al-Mi‘râj).');
INSERT INTO "silika" VALUES(53,14,'53:14','Pa chitela chansaawu chakupelelapo (indu yakwela kwinani kapena kutuluka pasi, chachili kusyeto kwa mawunde nsano nagaŵili).');
INSERT INTO "silika" VALUES(53,15,'53:15','Palakwepo ni pajili Mbepo Jakutamilichikamo.');
INSERT INTO "silika" VALUES(53,16,'53:16','Ndema jayachiunichilaga chitela chansaawucho yayachiunichilaga. ');
INSERT INTO "silika" VALUES(53,17,'53:17','Liso (lya Muhammadi صلى الله عليه وسلم) nganilisembenduka, soni nganilisumba mpika.');
INSERT INTO "silika" VALUES(53,18,'53:18','Chisimu chene jwalakwe (Muhammadi صلى الله عليه وسلم) jwaiweni ilosyo yekulungwa-kulungwa ya Ambuje ŵakwe (Allah). ');
INSERT INTO "silika" VALUES(53,19,'53:19','Ana mwaweni Al-Lâta ni Al-‘Uzzâ,');
INSERT INTO "silika" VALUES(53,20,'53:20','Kwisa soni Manâta jwatatu jwinejo (kuŵa milungu jenu mmalo mwa Allah)?');
INSERT INTO "silika" VALUES(53,21,'53:21','Ana jenumanja ni nkwete ŵanache ŵachilume, sano Jwalakwe (Allah) ni jukwete ŵanache ŵachikongwe?');
INSERT INTO "silika" VALUES(53,22,'53:22','Kwele ni gaŵanya kwalupuso (kwakuntenda lupuso Allah).');
INSERT INTO "silika" VALUES(53,23,'53:23','Nganigaŵatu galakwego ikaŵeje menape (ga isanamu) gan’gakolasile jenumanja ni achatati ŵenu, Allah nganatulusya pa yalakweyo umboni uliose (wakundisya kuigalagatila), ngakukuya ŵanganyao ikaŵeje liganichisya, ni yayikusaka mitima, kutendaga pamasile pachaichilile kuumila kwa M’mbuje gwao (Allah) chongoko.');
INSERT INTO "silika" VALUES(53,24,'53:24','Kapena mundu mpaka akombole kupata chilichose chaakuchiluumba? (Ngwamba!)');
INSERT INTO "silika" VALUES(53,25,'53:25','Basi Allah ni Nsyene Akhera ni Duniya.');
INSERT INTO "silika" VALUES(53,26,'53:26','Ana soni Achimalaika ŵalingwa kumawunde ŵangaŵa nkukamuchisya chondechonde jao chilichose, ikaŵeje panyuma pakupeleka lusa Allah kwa jwaankunsaka (mwa ŵanganyao kuti awombe duwa ja chondechonde), ni kunonyelwa najo (nkuwombeledwajo)?');
INSERT INTO "silika" VALUES(53,27,'53:27','Chisimu aŵala ŵangakukulupilila ya Akhera akwakolanga Achimalaika ni mena gachikongwe (pakuŵecheta kuti ŵanganyao ŵanache ŵachikongwe ŵa Allah).');
INSERT INTO "silika" VALUES(53,28,'53:28','Ni nganakola ŵanganyao pa yalakweyo umanyilisi uliose, ngakukuya ikaŵeje liganichisya, sano liganichisya lyangakamuchisya chilichose pa yakuona.');
INSERT INTO "silika" VALUES(53,29,'53:29','Basi munnekaani ajula jwaachitundumalile chikumbusyo Chetu (Qur’an), ni ngajukusaka ikaŵeje umi wa duniya.');
INSERT INTO "silika" VALUES(53,30,'53:30','Chele ni chipelela chao cha umanyilisi. Chisimu Ambuje ŵenu (Allah) ni ŵaakummanyilila jwaapoteele kuleka litala Lyakwe, nambo soni ni ŵaakummanyilila jwajongweche.');
INSERT INTO "silika" VALUES(53,31,'53:31','Soni Allah ni Nsyene yosope yaili ku mawunde ni yaili petaka, kuti akaalipile aŵala ŵasakesye pa yaŵapanganyisye, ni kwalipila yambone aŵala ŵaakolosisye (masengo).');
INSERT INTO "silika" VALUES(53,32,'53:32','Aŵala ŵaakuliŵambasya ilemwa yekulungwa-kulungwa, ni yakunyalaya, ikaŵeje yamwanamwana. Chisimu Ambuje ŵenu (Allah) ni Ŵachikululuko chakusapanguka. Ŵalakwe ni ŵaakummanya chenene jenumanja, kutandila paŵan’gumbaga kuumila mwitaka, ni pandema jankuŵaaga misigo mu itumbo ya achimama ŵenu. Basi ngasilliswejesyaga mwachimisyene, Jwalakwe ni Jwaakummanya chenene jwaali ni woga (wakun’jogopa Allah).');
INSERT INTO "silika" VALUES(53,33,'53:33','Ana mum’bweni (mmwe Muhammadi صلى الله عليه وسلم) ajula jwaagalawiche (kuleka kuunda malamusi ga Allah)?');
INSERT INTO "silika" VALUES(53,34,'53:34','Ni jutosile panandi (mu chipanje chakwe) kaneka ni kuleka (kutolako)?');
INSERT INTO "silika" VALUES(53,35,'53:35','Ana kukwakwe kwana umanyilisi wakusisika, basi jwalakwe ni jukuiona (yanti chipanje chakwecho chichimale ligongo lyakutola)?');
INSERT INTO "silika" VALUES(53,36,'53:36','Ana nganijusalilidwa yaili mu ikalakala ya Musa?');
INSERT INTO "silika" VALUES(53,37,'53:37','Ni Ibrahima ajula juŵakwanilisye (chilanga cha Allah)?');
INSERT INTO "silika" VALUES(53,38,'53:38','Yanti jwantwiichile nsigo (wasambi) ngasaja kutwichila nsigo (wasambi) wajwine.');
INSERT INTO "silika" VALUES(53,39,'53:39','Niyanti mundu nganijukola ikaŵeje yajupanganyisye. ');
INSERT INTO "silika" VALUES(53,40,'53:40','Nambo soni kuti masengo gakwe gaachionedwa.');
INSERT INTO "silika" VALUES(53,41,'53:41','Kaneko ni tachilipidwa malipilo gakwana.');
INSERT INTO "silika" VALUES(53,42,'53:42','Ni yanti kwa Ambuje ŵenu (Allah) ni kuchipelela (kwakuwujila indu yosope).');
INSERT INTO "silika" VALUES(53,43,'53:43','Ni yanti Jwalakwe (Allah) ni Jwaakutendekaasyaga kuseka ni Jwaakutendekasyaga soni kulila.');
INSERT INTO "silika" VALUES(53,44,'53:44','Ni yanti Jwalakwe (Allah) ni Jwaakupeleka chiwa ni Jwaakupeleka soni umi.');
INSERT INTO "silika" VALUES(53,45,'53:45','Ni yanti Jwalakwe (Allah) ni jwaagumbile mitundu jiŵili; chachilume ni chachikongwe.');
INSERT INTO "silika" VALUES(53,46,'53:46','Kuumila mu lindondwa (lya ulume) pati litajidwe (m’chiŵelechelo).');
INSERT INTO "silika" VALUES(53,47,'53:47','Ni yanti kwa Jwalakwe (Allah) kwana guumba kwine (kwakwimusya ŵawe m’malembe).');
INSERT INTO "silika" VALUES(53,48,'53:48','Ni yanti Jwalakwe (Allah) ni Jwaakupeleka yakwanila (kuikamulichisya masengo), ni Jwaakupeleka soni yakusalasya.');
INSERT INTO "silika" VALUES(53,49,'53:49','Ni yanti Jwalakwe (Allah) ni M’mbuje jwa ndondwa ja chilimila (jaŵajigalagatilaga ŵarabu ŵane).');
INSERT INTO "silika" VALUES(53,50,'53:50','Ni yanti Jwalakwe (Allah) ni juŵajonasile achina Adi ŵandanda.');
INSERT INTO "silika" VALUES(53,51,'53:51','Ni achina Samuda, ni nganansigasya (ata jumo).');
INSERT INTO "silika" VALUES(53,52,'53:52','Kwisa soni ŵandu ŵa Nuhu kalakala, chisimu ŵanganyao ŵaliji ŵalupuso nnope, ni ŵakusumba mpika nnope (pa sambi).');
INSERT INTO "silika" VALUES(53,53,'53:53','Ni misi jakupitikusyidwa (ja ŵandu ŵa Lutwi) ŵajigwisyisye.');
INSERT INTO "silika" VALUES(53,54,'53:54','Ni yam’bunichiile (misijo) yayam’bunichiile (yaili ilagasyo ya sakalawe syelongo lyejoche ni moto wa Jahannama).');
INSERT INTO "silika" VALUES(53,55,'53:55','Ana ni chapi mu chindimba cha Ambuje ŵenu (Allah mmwe amundu) cha nkuchikaichila (kuti mwine nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(53,56,'53:56','Aju (Muhammadi صلى الله عليه وسلم) ni nkutetela jumpepe mwa akutetela ŵandanda.');
INSERT INTO "silika" VALUES(53,57,'53:57','Chikusejelela chakusejelela (Kiyama jikuŵandichila).');
INSERT INTO "silika" VALUES(53,58,'53:58','Pangali jwampaka achiunukule (kuti chimanyiche ndema jakwe jakwichila) jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(53,59,'53:59','Ana jenumanja nkujisimonga aji abaliji (Qur’an)?');
INSERT INTO "silika" VALUES(53,60,'53:60','Ni nkuseka (mwachipongwe) ni ngalilaga (pati nchipikanaga yogoyo ya Allah)!');
INSERT INTO "silika" VALUES(53,61,'53:61','Ni n’gamba kwanjila pe!');
INSERT INTO "silika" VALUES(53,62,'53:62','Basi munsujudilaani Allah ni mun’galagatileje (Jwele Jikape).');
INSERT INTO "silika" VALUES(54,1,'54:1','Ndaŵi (jakwichila Kiyama) jiŵandichile, ni ugaanjiiche mwesi. ');
INSERT INTO "silika" VALUES(54,2,'54:2','Soni naga ali achiweni chisimosimo akasanyosyaga, ni akutiji: “(Awu ni) usaŵi wakwendelechela.”');
INSERT INTO "silika" VALUES(54,3,'54:3','Ni ŵaakaaniile, ni kuya isako yao, sano chindu chilichose (chambone kapena chakusakala chaŵapanganyisye mundu) chiŵele chakutamilichika (malipilo gakwe lisiku lya Kiyama).');
INSERT INTO "silika" VALUES(54,4,'54:4','Soni chisimu chene pamasile pasyaayiiche ŵanganyao ngani (mu aji Qur’an) syasikwete nkati mwakwe ikaanyo yaukali (yakwakanya kutenda yakusakala).');
INSERT INTO "silika" VALUES(54,5,'54:5','(Aji Qur’an jiŵele jegumbale) lunda lwakusokoka nnope, nambo itetelo ya akutetela nganiyiŵa ikamuchisye (kwa ŵandu ŵaajitundumalile).');
INSERT INTO "silika" VALUES(54,6,'54:6','Basi mwalekani. (Mwakumbusyani) lisiku lyatachiŵilanga nkuŵilanga (Israfil) ku chindu chakunyalaya kusyene (chiŵalanjilo cha pa Kiyama).');
INSERT INTO "silika" VALUES(54,7,'54:7','Gali gateteŵele meso gao, achikopokanganaga m’malembe, nti chisawu ŵanganyao sombe syakubalalikangana.');
INSERT INTO "silika" VALUES(54,8,'54:8','Achikangamalaga (aku ali anyakwile ngosi, mwanja) wa kwankuŵilanga. Achimakafili tachiti: “Ali ni lisiku lyakusausya kusyene.”');
INSERT INTO "silika" VALUES(54,9,'54:9','Ŵaakaaniile paujo pao (Makuraishi nkanigaiche) ŵandu ŵa Nuhu, basi ŵansisisye kapolo Jwetu (Nuhu), ni ŵatite: “Jwalakweju jwamasoka.” Ni jwakalipilidwe (ni kogoyesyedwa).');
INSERT INTO "silika" VALUES(54,10,'54:10','Basi jwaaŵendile Ambujegwe (Allah juchitiji): “Chisimu une ngomboleedwe, basi njokolaani.”');
INSERT INTO "silika" VALUES(54,11,'54:11','Basi ni twawugwile milango ja kwinani ni mesi gakusuka kwanneso.');
INSERT INTO "silika" VALUES(54,12,'54:12','Ni twaliganjile litaka ni achitutumule, ni gasimene mesi (gakuuma kwinani ni gakopoka pasi) pa chindu chachaliji chepanjepanje (chigumula chakwajonanjila ŵandu ŵa Nuhu).');
INSERT INTO "silika" VALUES(54,13,'54:13','Ni twan’jigele (Nuhujo) pa chombo chamatabwa chachakomedwe ni misomali (jaitela).');
INSERT INTO "silika" VALUES(54,14,'54:14','Chaliji nkwenda (mwakuloleledwa) ni meso Getu, kuŵa malipilo kwa ajula juŵaliji ali akaniliidwe (Nuhu).');
INSERT INTO "silika" VALUES(54,15,'54:15','Soni chisimu twachilesile (chombocho) kuŵa chilosyo, ana pana ŵaachikumbuchila (ni kuliwunda nacho)?');
INSERT INTO "silika" VALUES(54,16,'54:16','Ana ilagasyo Yangu yaliji uli kwisa soni itetelo Yangu?');
INSERT INTO "silika" VALUES(54,17,'54:17','Soni chisimu chene tujijepepesye Qur’an kuti jiŵe chikumbusyo, ana pana ŵaajikumbuchila (nikuliwunda najo)?');
INSERT INTO "silika" VALUES(54,18,'54:18','Achina Adi (nombe) ŵaakaaniile. Ana ilagasyo Yangu yaliji uli kwisa soni itetelo Yangu?');
INSERT INTO "silika" VALUES(54,19,'54:19','Chisimu Uwwe twatumichisye mbungo jakusisima nnope mu lisiku lyachitapani chakwendelechela.');
INSERT INTO "silika" VALUES(54,20,'54:20','Jaliji nkwachuŵa ŵandu (kuja nao mwinani, kaneka nikwagwichisya pasi mwakwapitikusya) nti chisawu ŵanganyao mapata ga itela ya tende yechuŵidwe.');
INSERT INTO "silika" VALUES(54,21,'54:21','Ana ilagasyo Yangu yaliji uli kwisa soni itetelo Yangu?');
INSERT INTO "silika" VALUES(54,22,'54:22','Soni chisimu chene tujijepepesye Qur’an kuti jiŵe chikumbusyo, ana pana ŵaajikumbuchila (nikuliwunda najo)?');
INSERT INTO "silika" VALUES(54,23,'54:23','Achina Samuda ŵaakaaniile akutetela.');
INSERT INTO "silika" VALUES(54,24,'54:24','Ni ŵatite: “Ana mundu jwakuumila mwetuwe jumpepe ni tunkuyeje? Chisimu uwwe pa jele ndemajo chituŵe mu kupotela ni masoka.”');
INSERT INTO "silika" VALUES(54,25,'54:25','“Ana chikumbusyo chipelecheedwe kukwakwe (Swaleh jikape) sikati jetu? (Ngwamba), nambo jwalakwe ni jwaunami kusyene, (soni) jwakulikulungusya.”');
INSERT INTO "silika" VALUES(54,26,'54:26','Tachimanyilila malaŵi kuti ana nduni jwali jwaunami kusyene, (soni) jwakulikulungusya.');
INSERT INTO "silika" VALUES(54,27,'54:27','(Twansalile Swaleh kuti): “Chisimu Uwwe tukutumisya ngamiya jekongwe kuŵa mayeso kukwao, basi mwajembechelani ni mpilile.”');
INSERT INTO "silika" VALUES(54,28,'54:28','“Ni mwatagulile yanti mesi gagaŵiidwe sikati jao (ni ngamiya, lisiku amweje ŵandu, lisiku line jimweje ngamiya), kumwa kulikose akuŵichilileje (jwakukum’bandika).”');
INSERT INTO "silika" VALUES(54,29,'54:29','Nambo ŵam’bilasile n’jao (Qudâr bin Sâlif juŵaliji jwakusakalilwa nnope) ni jwajigele (upanga) ni jwajiuleje (ngamiya jila).');
INSERT INTO "silika" VALUES(54,30,'54:30','Ana ilagasyo Yangu yaliji uli kwisa soni itetelo Yangu?');
INSERT INTO "silika" VALUES(54,31,'54:31','Chisimu Uwwe twatumichisye ŵanganyao n’gumilo umpepe, basi niŵaliji nti chisawu mbelenje sya nkutaŵa lichinga (ni gumuka nikupondedwapondedwa).');
INSERT INTO "silika" VALUES(54,32,'54:32','Soni chisimu chene tujijepepesye Qur’an kuti jiŵe chikumbusyo, ana pana ŵaajikumbuchila (nikuliwunda najo)?');
INSERT INTO "silika" VALUES(54,33,'54:33','Ŵandu ŵa Lutwi ŵaakaaniile akutetela.');
INSERT INTO "silika" VALUES(54,34,'54:34','Chisimu Uwwe twatumichisye chimbunga cha sakalawe (syasyajonasile wosope ŵene), ikaŵeje ŵaliŵasa lya Lutwi, twajokwele kumasikusiku.');
INSERT INTO "silika" VALUES(54,35,'54:35','Kuŵa ukoto wakuuma Kukwetu, iyyoyopeyo niyatukutiji pakun’dipila jwaatogolele.');
INSERT INTO "silika" VALUES(54,36,'54:36','Soni chisimu jwalakwe (Lutwi) jwaatetele ŵanganyao ilagasyo Yetu, nambo ŵaikaichile itetelo.');
INSERT INTO "silika" VALUES(54,37,'54:37','Soni chisimu ŵannyenjelele (Lutwi kuti ŵape) achalendo ŵakwe (atende nao matanyula), basi ni twatatalesye meso gao, (ni twasalile kuti): “Pasyani ilagasyo Yangu ni itetelo Yangu.”');
INSERT INTO "silika" VALUES(54,38,'54:38','Ni yaalaaŵiile ŵanganyao kundaŵi ilagasyo yakutamilichika.');
INSERT INTO "silika" VALUES(54,39,'54:39','(Twasalile kuti): “Pasyani ilagasyo Yangu ni itetelo Yangu.”');
INSERT INTO "silika" VALUES(54,40,'54:40','Soni chisimu chene tujijepepesye Qur’an kuti jiŵe chikumbusyo, ana pana ŵaajikumbuchila (nikuliwunda najo)?');
INSERT INTO "silika" VALUES(54,41,'54:41','Soni chisimu chene ŵandu ŵa Firiauna yaichilile itetelo.');
INSERT INTO "silika" VALUES(54,42,'54:42','Ŵaakaaniile ilosyo Yetu yosopeyene (nsano nancheche), basi nitwaakwembekeenye kakwembekanye ka Jwamachili gakupunda, Jwakombola (chilichose).');
INSERT INTO "silika" VALUES(54,43,'54:43','Ana makafili genu (jenumanja Makuraishi) ni gagali gambone kwapunda ŵanganyao (ŵaŵajonanjidwe kala, mwanti jenumanja ni ngan’jonanjidwa), kapena nkwete chilanga chantunjelele (chachilembedwe) mu itabu (chanti ngasin’jonanjidwa)?');
INSERT INTO "silika" VALUES(54,44,'54:44','Kapena akutiji: “Uwwe tuli likuga lyejinji mwanti mpaka tukombole kulichenjela (pangali jwampaka atukombole)!');
INSERT INTO "silika" VALUES(54,45,'54:45','Lichikomboledwa likuga (lyaolyo), ni tachilosya migongo (kutila. Yatendekwe yalakweyi pangondo ja Badr).');
INSERT INTO "silika" VALUES(54,46,'54:46','Nambo Kiyama ni chilanga chao (chakwapela ilagasyo), soni Kiyama ni jamakungu gamakulungwa soni jakuŵaŵa nnope.');
INSERT INTO "silika" VALUES(54,47,'54:47','Chisimu akuleŵa ali nkupotela ni m’masoka.');
INSERT INTO "silika" VALUES(54,48,'54:48','(Mwakumbusyani) lisiku lyatachiwutidwaga ku Moto chamakuku, (achisalilidwaga kuti): “Pasyani kakwaye ka Moto.”');
INSERT INTO "silika" VALUES(54,49,'54:49','Chisimu Uwwe chindu chilichose tuchigumbile kwa mpango (wakwe wawapanjidwe kalape).');
INSERT INTO "silika" VALUES(54,50,'54:50','Ni nganiuŵa ulamusi Wetu ikaŵeje mwanakamo mpela kupila kwa liso.');
INSERT INTO "silika" VALUES(54,51,'54:51','Soni chisimu chene twajonasile ŵakulandana namwe (m’makafili gakalakala), ana pana jwampaka akumbuchile (ni kuliwunda)?');
INSERT INTO "silika" VALUES(54,52,'54:52','Soni chilichose chaŵapanginyisye chili mu itabu (yakulembedwamo masengo gao).');
INSERT INTO "silika" VALUES(54,53,'54:53','Soni chilichose chamwana ni chekulungwa chileembeedwe.');
INSERT INTO "silika" VALUES(54,54,'54:54','Chisimu ŵawoga (wakun’jogopa Allah) tachiŵa m’Matimbe ni sulo (sya ku Mbepo).');
INSERT INTO "silika" VALUES(54,55,'54:55','Pakutama pambone kusyesyene, kwa Nchimwene (Allah) Jwakombola (chilichose).');
INSERT INTO "silika" VALUES(55,1,'55:1','(Allah) Jwaukoto wejinji.');
INSERT INTO "silika" VALUES(55,2,'55:2','Jujiganyisye Qur’an.');
INSERT INTO "silika" VALUES(55,3,'55:3','Jwagumbile mundu.');
INSERT INTO "silika" VALUES(55,4,'55:4','Ŵan’jiganyisye kuŵecheta.');
INSERT INTO "silika" VALUES(55,5,'55:5','Lyuŵa ni mwesi (ikwenda) kwa chiŵalanjilo (chakwe).');
INSERT INTO "silika" VALUES(55,6,'55:6','Itela yakutambala ni itela yakwimilichika ikunsujudilaga (Allah).');
INSERT INTO "silika" VALUES(55,7,'55:7','Soni liwunde ŵalinyakwile, ni ŵaŵisile chilungamiko.');
INSERT INTO "silika" VALUES(55,8,'55:8','Kuti mkapungusyaga pa kupima.');
INSERT INTO "silika" VALUES(55,9,'55:9','Ni n’jimicheje chipiimo mwachilungamiko, soni nkapungusyaga yakupimidwa.');
INSERT INTO "silika" VALUES(55,10,'55:10','Soni litaka ŵaliŵisile (mwakulitaandika) ligongo lya iwumbe.');
INSERT INTO "silika" VALUES(55,11,'55:11','Mwalakwemo mwana isogosi, ni mitende jajikwete itungatunga.');
INSERT INTO "silika" VALUES(55,12,'55:12','Ni songolo syasikwete masapi, ni masamba gakunuunjiila.');
INSERT INTO "silika" VALUES(55,13,'55:13','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,14,'55:14','Jwagumbile mundu (Adam) kuumila mwilongo lyakulilaga mpela liigeleesya.');
INSERT INTO "silika" VALUES(55,15,'55:15','Sano majini ni ŵagagumbile kuumila m’malamba ga moto gangali lyosi.');
INSERT INTO "silika" VALUES(55,16,'55:16','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,17,'55:17','(Allah ni) M’mbuje jwa kungopoko kuŵili (kwa lyuŵa chawu ni chuku), soni ni M’mbuje jwa kungapililo kuŵili (kwa lyuŵa chawu ni chuku).');
INSERT INTO "silika" VALUES(55,18,'55:18','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,19,'55:19','Aŵisile mbwani siŵili syasikusimana (jamesi gakunong’a ni jamesi gachijete).');
INSERT INTO "silika" VALUES(55,20,'55:20','Sikati jakwe jana uŵigo, mwanti syangajinjililana.');
INSERT INTO "silika" VALUES(55,21,'55:21','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,22,'55:22','Sikasakopokaga mu (mbwani) sinasiŵilisyo ngalekale syekulungwa-kulungwa ni ngalekale syamwana-mwana.');
INSERT INTO "silika" VALUES(55,23,'55:23','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,24,'55:24','Ni jukwete yombo yayinyakuliidwe (tanga syakwe) yaikwenda m’mbwani (yekulungwa-kulungwa) mpela matumbi.');
INSERT INTO "silika" VALUES(55,25,'55:25','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,26,'55:26','Chilichose chachili palakwepo (petaka) chichijonasika.');
INSERT INTO "silika" VALUES(55,27,'55:27','Ni jichisigala ngope ja Ambuje ŵenu (Allah) jajikwete uchimbichimbi ni ukoto.');
INSERT INTO "silika" VALUES(55,28,'55:28','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,29,'55:29','Akasam’bendaga Jwalakwe wosope ŵali ku mawunde ni petaka, lisiku lililyose Jwalakwe juli pa masengo (mpela kwakwesya ni kwaatulusya ŵandu pa uchimbichimbi nambo soni kupeleka umi ni chiwa, ni ine).');
INSERT INTO "silika" VALUES(55,30,'55:30','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,31,'55:31','Sampano chitulipe lipesa Uwwe kwa jenumanja jampingo ŵaŵili (ŵandu ni majini lyakum’balanga itendo yenu pa Kiyama).');
INSERT INTO "silika" VALUES(55,32,'55:32','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,33,'55:33','E jenumanja makuga ga majini ni ŵandu! Naga tinkombole kupeleta upande wa kumawunde ni petaka (kuti mundiile ngampata), basi peletani. Nganim’ba mpeleete ikaŵeje ni machili (gakuuma Kukwangu).');
INSERT INTO "silika" VALUES(55,34,'55:34','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,35,'55:35','Chigatumisyidwe kukwenu malamba gamoto ni ntofu wenyelenyenduche, ni ngankombola kulupusyana.');
INSERT INTO "silika" VALUES(55,36,'55:36','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,37,'55:37','Basi palichipapuka liwunde nikuchejela mpela lipende lyechejewu.');
INSERT INTO "silika" VALUES(55,38,'55:38','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,39,'55:39','Basi lyele lisikulyo mundu namuno lijini ngasawusyidwa ya sambi syakwe.');
INSERT INTO "silika" VALUES(55,40,'55:40','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,41,'55:41','Akuleŵa tachimanyika ni imanyilo yao, basi ni tachikwembekanyisyidwa ni wumbo sya pewuwo pakupwatikanya ni makawu (kaneka ni kuponyedwa ku Moto).');
INSERT INTO "silika" VALUES(55,42,'55:42','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,43,'55:43','(Ichiŵechetedwa kwa akuleŵa mwa kwanyosya kuti): “Aji ni Jahannama ajila jaŵajikanilaga akuleŵa.”');
INSERT INTO "silika" VALUES(55,44,'55:44','“Taŵeje ali nkusyungula sikati ja jalakweji ni mesi gamoto gamasemule nnope.”');
INSERT INTO "silika" VALUES(55,45,'55:45','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,46,'55:46','Sano jwaakujogwepe kwimichidwa pameso pa Ambujegwe (Allah, ni kupanganyaga yalamulidwe, ni kuiŵambalaga yakanyisyidwe), takapate matimbe gaŵili ga ku Mbepo.');
INSERT INTO "silika" VALUES(55,47,'55:47','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,48,'55:48','(Matimbe) gagakwete nyambi syakutenjela mwakusalalila.');
INSERT INTO "silika" VALUES(55,49,'55:49','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,50,'55:50','Nkatikati mwa (matimbe) ganagaŵiliga mwana achitutumule ŵaŵili ŵaakujilimaga.');
INSERT INTO "silika" VALUES(55,51,'55:51','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,52,'55:52','Mwalakwemo ntundu uliose wa isogosi uli patundu paŵili.');
INSERT INTO "silika" VALUES(55,53,'55:53','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,54,'55:54','Tachiŵa ali ajegeme mu yakutandika (pa itengu yauchimbichimbi) nkati mwakwe mwekunje nguo ja silika jwakandapalila, ni isogosi ya m’matimbe ganagaŵili ili yakuŵandichila nnope (yaati konyola chitamile).');
INSERT INTO "silika" VALUES(55,55,'55:55','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,56,'55:56','Mwalakwemo mwana (achiŵammakwao) ŵakulola pejipi (ŵakwalola achalume ŵaope ŵangatenda meso mwamwamwa kwa achalume ŵane), nganagoneje nao mundu jwalijose namuno lijini nkanaŵe ŵanganyao (achalume ŵao).');
INSERT INTO "silika" VALUES(55,57,'55:57','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,58,'55:58','(Kasalale kao achimmasyetowo) gamba kuŵa nti chisawu ngalekale syakuswejelngo (mpela kwesya ni kutulusya a nnope ni ngalekale syakuchejelela.');
INSERT INTO "silika" VALUES(55,59,'55:59','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,60,'55:60','Ana pana malipilo ga umbone pangaŵaga kulipa umbone (soni)!');
INSERT INTO "silika" VALUES(55,61,'55:61','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,62,'55:62','Konjechesa pa (matimbe) gaŵili gala, pana matimbe gane gaŵili soni.');
INSERT INTO "silika" VALUES(55,63,'55:63','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,64,'55:64','Gakubiliŵita nnope.');
INSERT INTO "silika" VALUES(55,65,'55:65','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,66,'55:66','Nkatikati mwa matimbe ganagaŵiligo mwana achitutumule ŵaŵili ŵakoposya mesi mwamachili (kuumila pasi).');
INSERT INTO "silika" VALUES(55,67,'55:67','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,68,'55:68','Mwalakwemo mwana isogosi, kwisasoni mitende ni mikomamanga.');
INSERT INTO "silika" VALUES(55,69,'55:69','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,70,'55:70','Mwalakwemo mwana achimmasyeto achambonembone, soni ŵakusalala.');
INSERT INTO "silika" VALUES(55,71,'55:71','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,72,'55:72','Ŵameso gakulanguka, ŵachigosedwele mu yumba.');
INSERT INTO "silika" VALUES(55,73,'55:73','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,74,'55:74','Nganagoneje nao mundu jwalijose namuno lijini nkanaŵe ŵanganyao (achalume ŵao).');
INSERT INTO "silika" VALUES(55,75,'55:75','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,76,'55:76','Tachiŵa ali ajegeme m’misago jachisamba, ni (kutamila) makondolo (matelesi) gakusalala nnope.');
INSERT INTO "silika" VALUES(55,77,'55:77','Ana nichapi mu chindimba cha M’mbuje gwenu (ŵanaŵaŵili mmwe ŵandu ni majini) chankuchikaana (kuti nganichiŵa Chakwe)?');
INSERT INTO "silika" VALUES(55,78,'55:78','Linyakwiche (pa uchimbichimbi ni upile) lina lya Ambuje ŵenu (Allah) Asyene uchimbichimbi ni ukoto.');
INSERT INTO "silika" VALUES(56,1,'56:1','Patichitendekwa chakutendekwa (Kiyama).');
INSERT INTO "silika" VALUES(56,2,'56:2','Pangali jwakanila yakutendekwa kwakwe.');
INSERT INTO "silika" VALUES(56,3,'56:3','(Chalakwe ni) chakwaatulusya (achimakafili pakwajinjisya ku Moto) ni kwakwesya (Asilamu pakwajinjisya ku Mbepo).');
INSERT INTO "silika" VALUES(56,4,'56:4','Ndema jalichitenganyika litaka kutenganyika kwamachili.');
INSERT INTO "silika" VALUES(56,5,'56:5','Matumbi ni kusyajidwa-syajidwa.');
INSERT INTO "silika" VALUES(56,6,'56:6','Ni kuŵa luwundu lyakuwululusyidwa.');
INSERT INTO "silika" VALUES(56,7,'56:7','Ni tinchiŵa jenumanja (pa Kiyama) mikutula jitatu.');
INSERT INTO "silika" VALUES(56,8,'56:8','Basi (pachiŵa pana) ŵandu ŵa kunkono wan’dyo, ana ŵandu ŵa kunkono wan’dyo ni ŵapi (uchimbichimbi wao uchiŵa uli)!');
INSERT INTO "silika" VALUES(56,9,'56:9','Ni (pachiŵa pana soni) ŵandu ŵa kunkono wanchiji, ana ŵandu ŵa kunkono wanchiji ni ŵapi (kusakala kwao kuchiŵa uli)!');
INSERT INTO "silika" VALUES(56,10,'56:10','Ni (pachiŵa pana soni) ŵaŵalongolele (pakutenda yambone ku duniya) ŵatachiŵa ŵakulongolela (ku Mbepo).');
INSERT INTO "silika" VALUES(56,11,'56:11','Ŵanganyao ni ŵatachiŵa ŵakuŵandichisyidwa (kwa Allah).');
INSERT INTO "silika" VALUES(56,12,'56:12','Tachiŵa m’Matimbe Gachindimba (ku Mbepo).');
INSERT INTO "silika" VALUES(56,13,'56:13','Likuga lyejinji (lya akulongolela ku yambone) lichiŵa lyakutyochela mu ŵandu ŵandanda.');
INSERT INTO "silika" VALUES(56,14,'56:14','Sano (likuga) lyannono (lya akulongolela ku yambone) lichiŵa lyakutyochela mu ŵandu ŵakumalichisya.');
INSERT INTO "silika" VALUES(56,15,'56:15','Ali pa itengu yauchimbichimbi yeluche (ni golodi ni mitapo jine).');
INSERT INTO "silika" VALUES(56,16,'56:16','Tachiyijegamaga mwakulolegana.');
INSERT INTO "silika" VALUES(56,17,'56:17','Tachasyungulaga (pa kwatumichila) achachanda ŵakutamilichika (pa uchanda).');
INSERT INTO "silika" VALUES(56,18,'56:18','Ni migao kwisa soni mabilika (ga yakumwa), ni ipanda ya ukana wakuumila mu tutumule jwakujilimaga.');
INSERT INTO "silika" VALUES(56,19,'56:19','Ngasalwala nao ntwe namuno kutyoka lunda.');
INSERT INTO "silika" VALUES(56,20,'56:20','Ni isogosi iliyose yatakaisaguleje.');
INSERT INTO "silika" VALUES(56,21,'56:21','Ni nyama ja ijuni jatakajilaluchileje.');
INSERT INTO "silika" VALUES(56,22,'56:22','Ni achimmasyeto ŵameso gakulanguka soni gekulungwa mwakusalalila.');
INSERT INTO "silika" VALUES(56,23,'56:23','(Ŵakuswejela) nti chisawu ngalekale syesunjidwe.');
INSERT INTO "silika" VALUES(56,24,'56:24','Kuŵa malipilo pa yaŵaliji nkupanganya.');
INSERT INTO "silika" VALUES(56,25,'56:25','Ngaaja kupikana mwalakwemo maloŵe gangali mate, namuno gakutendekasya kupata sambi.');
INSERT INTO "silika" VALUES(56,26,'56:26','Ikaŵeje maloŵe ganti: Ntendele, ntendele.');
INSERT INTO "silika" VALUES(56,27,'56:27','Sano (pachiŵa pana) ŵandu ŵa kunkono wan’dyo, ana ŵandu ŵa kunkono wan’dyo ni ŵapi (uchimbichimbi wao uchiŵa uli)!');
INSERT INTO "silika" VALUES(56,28,'56:28','(Tachiŵa) mu itela ya masaawu yetyosye miŵa.');
INSERT INTO "silika" VALUES(56,29,'56:29','Ni itela ya magombo yesogole magombo gakwe mwakusajikana chenene.');
INSERT INTO "silika" VALUES(56,30,'56:30','Ni miwulili jakwendelechela.');
INSERT INTO "silika" VALUES(56,31,'56:31','Ni mesi gakujilima ndaŵi ni katema.');
INSERT INTO "silika" VALUES(56,32,'56:32','Ni isogosi yejinji.');
INSERT INTO "silika" VALUES(56,33,'56:33','Yangakatichisya kasogole nambo soni yangasiŵiilidwa.');
INSERT INTO "silika" VALUES(56,34,'56:34','Ni yakutandika yenyakulidwe (pa itengu yauchimbichimbi).');
INSERT INTO "silika" VALUES(56,35,'56:35','Chisimu Uwwe chitukaaguumbe ŵanganyao (achakongwe ŵakuumila ku duniya) kagumbidwe kane (kakusalala nnope kulekangana ni muŵaŵelele ku duniya).');
INSERT INTO "silika" VALUES(56,36,'56:36','Basi chitukatende kuŵa ŵaali.');
INSERT INTO "silika" VALUES(56,37,'56:37','Ŵakulinonyelesya kwa achalume ŵao, ŵakulandana misingu.');
INSERT INTO "silika" VALUES(56,38,'56:38','(Tachiŵa) ŵa (achalume) ŵa kunkono wan’dyo.');
INSERT INTO "silika" VALUES(56,39,'56:39','Likuga lyejinji (lya ŵa kunkono wan’dyo) lichityochela mu ŵandu ŵandanda.');
INSERT INTO "silika" VALUES(56,40,'56:40','Nambo soni likuga lyejinji (lya ŵa kunkono wan’dyo) lichityochela mu ŵandu ŵakumalichisya.');
INSERT INTO "silika" VALUES(56,41,'56:41','Ni (pachiŵa pana) ŵandu ŵa kunkono wanchiji, ana ŵandu ŵa kunkono wanchiji ni ŵapi (kusakala kwao kuchiŵa uli)!');
INSERT INTO "silika" VALUES(56,42,'56:42','(Tachiŵa) mu chimbunga chamoto, ni mesi gamasemule nnope.');
INSERT INTO "silika" VALUES(56,43,'56:43','Ni m’bulili wa lyosi lyepiliwu nnope.');
INSERT INTO "silika" VALUES(56,44,'56:44','Wangapeleka kusisimila namuno kutulalila.');
INSERT INTO "silika" VALUES(56,45,'56:45','Chisimu ŵanganyao nkaniyiŵe yalakweyi (ku duniya) ŵaliji nkutama umi wakusangalala (ni yakanyisyidwa).');
INSERT INTO "silika" VALUES(56,46,'56:46','Ni ŵaliji nkwendelechela (kutenda) sambi syekulungwa.');
INSERT INTO "silika" VALUES(56,47,'56:47','Ni ŵaliji nkuŵecheta kuti: “Ana patuchiwapo ni kusyuka litaka ni maupa, nambo yakuona uwwe tuchiukulidwa (m’malembe)?”');
INSERT INTO "silika" VALUES(56,48,'56:48','“Ni achatati ŵetu ŵakwe ŵandanda?”');
INSERT INTO "silika" VALUES(56,49,'56:49','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Chisimu ŵandanda ni ŵambesi ŵakwe.”');
INSERT INTO "silika" VALUES(56,50,'56:50','“Tachisonganganyisyidwa wa kunsongangano wetomechedwe wa lisiku lyakumanyika.”');
INSERT INTO "silika" VALUES(56,51,'56:51','“Kaneka chisimu ŵanganyammwe, E jenumanja ŵalupuso, (soni) ŵakanila!”');
INSERT INTO "silika" VALUES(56,52,'56:52','“Tinkalye kwene chitela cha Zakkum.”');
INSERT INTO "silika" VALUES(56,53,'56:53','“Ni tinkachigumbasyeje ndumbo (syenu).”');
INSERT INTO "silika" VALUES(56,54,'56:54','“Ni tinkachimweleje mesi gamasemule nnope.”');
INSERT INTO "silika" VALUES(56,55,'56:55','“Ni tinkamweje mwasikumwelaga ngamiya syanjota nnope.”');
INSERT INTO "silika" VALUES(56,56,'56:56','Aji ni mbiiko jao Lisiku Lyamalipilo.');
INSERT INTO "silika" VALUES(56,57,'56:57','Uwwe twan’gumbile jenumanja, nambi ligongo chichi ngankukulupilila (ya kwimuka m’malembe panyuma pakuwa)?');
INSERT INTO "silika" VALUES(56,58,'56:58','Ana nkuliona uli lindondwa lya ulume lyankudondechesyaga (m’chiŵelechelo)?');
INSERT INTO "silika" VALUES(56,59,'56:59','Ana jenumanja niŵankuligumbaga (kuti liŵe mundu), kapena Uwwe niŵatukuligumbaga?');
INSERT INTO "silika" VALUES(56,60,'56:60','Uwwe tupimiile chilikati chenu chiwa (kuti jwalijose mwa jenumanja tachiwa), ni nganituŵa Uwwe ŵakulepela,');
INSERT INTO "silika" VALUES(56,61,'56:61','Kuti chitun’galawusye kagumbidwe kenu (lisiku lya Kiyama), nikun’gumba mu kaŵe kangankukamanyilila.');
INSERT INTO "silika" VALUES(56,62,'56:62','Soni chisimu chene pamasile pankumanyi gumba kwandanda (kuti Allah ni juŵagumbile), nambi uli ngankukumbuchila (yanti Jwalakwe mpaka jukombole gumba kwaŵili)?');
INSERT INTO "silika" VALUES(56,63,'56:63','Ana nkujiona uli mbeju jankupandaga?');
INSERT INTO "silika" VALUES(56,64,'56:64','Ana jenumanja niŵankujimesyaga, kapena Uwwe niŵatukumesyaga?');
INSERT INTO "silika" VALUES(56,65,'56:65','Tungasache tungajitesile kuŵa jakuteketuka, ni nkaliji ŵakusimonga (chachintendesye kuti jiteketuche).');
INSERT INTO "silika" VALUES(56,66,'56:66','(Ni nkatiji): “Chisimu uwwe tugambile kwasa chipanje chetu.”');
INSERT INTO "silika" VALUES(56,67,'56:67','“Nambo uwwe tujimidwe.”');
INSERT INTO "silika" VALUES(56,68,'56:68','Ana nkugaona uli mesi gankumwaga?');
INSERT INTO "silika" VALUES(56,69,'56:69','Ana jenumanja ni ŵan’gatulwisye kuumila kumawunde gawula, kapena Uwwe ni Akutulsya?');
INSERT INTO "silika" VALUES(56,70,'56:70','Tungasache tungagatesile kuŵa gachijete nnope, nambi ligongo chichi ngankutogolela?');
INSERT INTO "silika" VALUES(56,71,'56:71','Ana nkuuwona uli moto wankukolesyaga?');
INSERT INTO "silika" VALUES(56,72,'56:72','Ana jenumanja ni ŵamwagumbile itela yakwe, kapena Uwwe ni Akugumba?');
INSERT INTO "silika" VALUES(56,73,'56:73','Uwwe tuwutesile walakwe (motowo) kuŵa chikumbusyo (cha Moto wa ku Akhera), ni wakwakamuchisya ŵa paulendo.');
INSERT INTO "silika" VALUES(56,74,'56:74','Basi liswejesyani lina lya Ambuje ŵenu Ŵakulungwa nnope (Allah).');
INSERT INTO "silika" VALUES(56,75,'56:75','Ngumulumbila mwagwila ndondwa (mwakutiŵilila mwakwe kwinani).');
INSERT INTO "silika" VALUES(56,76,'56:76','Soni chisimu chalakwechi ni chilumbilo chekulungwa nnope ingaŵe mwamanyililaga.');
INSERT INTO "silika" VALUES(56,77,'56:77','Chisimu jalakweji ni Qur’an jakuchimbichika.');
INSERT INTO "silika" VALUES(56,78,'56:78','Jili m’Chitabu chesunjidwe chenene (Al-Lauh Al- Mahfȗz).');
INSERT INTO "silika" VALUES(56,79,'56:79','Ngakuchikwayaga ikaŵeje ŵaswejesyedwe.');
INSERT INTO "silika" VALUES(56,80,'56:80','Chakutulusyidwa kuumila kwa M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(56,81,'56:81','Ana aji abaliji (Qur’an) jenumanja (achimakafili) nkujikaanila?');
INSERT INTO "silika" VALUES(56,82,'56:82','Sano kanila kwenu (lisiki lyampele Allah) nikwankukutenda kuŵa kutogolela kwenu.');
INSERT INTO "silika" VALUES(56,83,'56:83','Nambi uli ntima uli uyiche pekolongo (ndema jakuwa jumo jwenu ngankuuwuchisyaga m’chilu mwakwe)?');
INSERT INTO "silika" VALUES(56,84,'56:84','Kutendaga jenumanja jele ndemajo nkasaŵaga n’di nkulola.');
INSERT INTO "silika" VALUES(56,85,'56:85','Soni Uwwe (Achimalaika ŵakutyosya ntima) tukuŵaga dusi kwa jwalakwe kumpunda jenumanja, nambo ngankutuonaga.');
INSERT INTO "silika" VALUES(56,86,'56:86','Nambi uli naga nganim’ba ŵanti nikuja kulipidwa (itendo yenu, soni ngasin’jimuka m’malembe),');
INSERT INTO "silika" VALUES(56,87,'56:87','(Uli) ngankuuwuchisyaga (ntimao m’chilu mwakwe kuti akawa mundujo), naga jenumanja nkuŵecheta yakuona (kuti kwangali kwimuka m’malembe, ngati nichiwa chakwe chikapagwa)!');
INSERT INTO "silika" VALUES(56,88,'56:88','Sano naga juli (nkuwajo) mwa akuŵandichisyidwa (kwa Allah).');
INSERT INTO "silika" VALUES(56,89,'56:89','Basi jukasapata ntendele ni lisiki lyakununjila, kwisa soni Litimbe Lyachindimba lya ku Mbepo.');
INSERT INTO "silika" VALUES(56,90,'56:90','Sano naga juli (nkuwajo) mwa ŵandu ŵakunkono wan’dyo.');
INSERT INTO "silika" VALUES(56,91,'56:91','Basi (jukasasalilidwaga kuti): “Ntendele uli kukwenu, (ligongo lyakuŵa kwenu) mu ŵandu ŵakunkono wan’dyo.”');
INSERT INTO "silika" VALUES(56,92,'56:92','Nambo naga juli (nkuwajo) mwa akukanila, ŵakupotela.');
INSERT INTO "silika" VALUES(56,93,'56:93','Mbiiko jakwe jikasaŵa mesi gamasemule nnope.');
INSERT INTO "silika" VALUES(56,94,'56:94','Ni kupya m’Moto Waukali.');
INSERT INTO "silika" VALUES(56,95,'56:95','Chisimu ayi (yatunsimbile mmwe Muhammadi صلى الله عليه وسلم) ni yakuona yakusimichika.');
INSERT INTO "silika" VALUES(56,96,'56:96','Basi liswejesyani lina lya Ambuje ŵenu Ŵakulungwa nnope (Allah).');
INSERT INTO "silika" VALUES(57,1,'57:1','Iŵele ili nkunswejesya Allah Yosope yaili kumawunde ni petaka, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(57,2,'57:2','Uŵele Ŵakwe uchimwene wa kumawunde ni petaka, jukupelekaga umi ni chiwa, soni Jwalakwe pa chindu chilichose juŵele Jwakombola.');
INSERT INTO "silika" VALUES(57,3,'57:3','Jwalakwe ni Jwandanda (pangali chilichose paujo Pakwe), soni Jwambesi (pangali chilichose panyuma Pakwe), soni Jwapenani kusyene (pangali chilichose pachanya Pakwe), soni Jwakuŵandika nnope (ni umanyilisyi Wakwe, pangali chakuŵandika nnope kupunda Jwalakwe), soni Jwalakwe chindu chilichose juŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(57,4,'57:4','Jwalakwe ni Ajula juŵagumbile mawunde ni mataka m’moŵa nsano nalimo, kaneka ni jwatenjele pa Arishi (Chindanda Chaulungu). Jukuimanyilila yaikwinjila mwitaka, ni yaikopoka mwalakwemo, ni yaikutuluka kuumila kwinani, ni yaikwela kwalakweko, soni Jwalakwe juli pampepe ni jenumanja (ni umanyilisyi Wakwe) palipose pan’di, soni Allah ichindu yankutenda juŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(57,5,'57:5','Uŵele Ŵakwe uchimwene wa kumawunde ni petaka, soni kwa Allah ni kwakuuchisyidwa ichindu yosope.');
INSERT INTO "silika" VALUES(57,6,'57:6','Akasachijinjisyaga chilo m’muusi (ni kuleupa muusi kwipipa chilo mpela ndaŵi jachitukuta), ni akwinjisyaga soni muusi m’chilo (ni kuleupa chilo kwipipa muusi mpela ndaŵi jakusisima), soni Jwalakwe ni Nkumanyilila chenene yaili m’mitima.');
INSERT INTO "silika" VALUES(57,7,'57:7','Munkulupililani Allah ni Ntenga Jwakwe صلى الله عليه وسلم, ni ntoleje mu ayila (ipanje) yantesile jenumanja kuŵa ŵakuipochelangana (ŵane kuumila kwa ŵane), basi aŵala ŵaakulupilile mwa jenumanja, ni kutola (ipanje yao), akwete kupata malipilo gamakulungwa.');
INSERT INTO "silika" VALUES(57,8,'57:8','Chipali chichi kukwenu chikuntendekasya kuti nkankulupilila Allah, kutendaga Ntenga (Muhammadi صلى الله عليه وسلم) akum’bilanga kuti munkulupilile M’mbuje gwenu, soni (Allah) pamasile paajigele chilanga chenu? Nam’baga jenumanja ŵanti nikulupilila (basi kulupililani).');
INSERT INTO "silika" VALUES(57,9,'57:9','Jwalakwe ni Ajula jwaakuntuluchisya kapolo Jwakwe (Muhammadi صلى الله عليه وسلم) ma Ȃya gakusalichisya, kuti ankoposye m’chipi (chaukafili) ni kun’jinjisya mu lilanguka (lya Usilamu), soni chisimu Allah kukwenu ni Mpole kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(57,10,'57:10','Soni chipali chichi kukwenu chikuntendekasya kuti nkatola petala lya Allah, kutendaga Allah ni Nsyene yakulechedwa (yosope) ya kumawunde ni petaka? Nganaŵa alandene mwa jenumanja aŵala ŵaŵatosile nkanikuŵe kuwugula (Maaka) ni kuputa ngondo, ŵanganyao niŵaakwete uchimbichimbi wekulungwa nnope ngalandana ni aŵala ŵatosile panyuma (pakuwugula Maaka) ni kuputa ngondo, nambo ŵosope ŵeneo Allah ŵaapele chilanga cha umbone, soni Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(57,11,'57:11','Ana ni nduni jwatankongosye Allah ngongole jambone (pakupeleka chipanje chakwe petala lya Allah) kuti ajijonjechesye kwa jwalakwejo (ni yakonjechesya yejinji, pakun’jonjechesya chipanje chakwe pa duniya pano)? Ni jukwete kupata soni jwalakwejo malipilo gakuchimbichika (ku Akhera).');
INSERT INTO "silika" VALUES(57,12,'57:12','Lisiku litinchaona ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe lilanguka lyao linankwenda paujo pao ni kunkono wao wan’dyo, (achisalilidwaga yanti): “Kusengwa kwenu lelo! (Nikugajinjila) Matimbe ga ku Mbepo, gasikujilima chapasi pakwe sulo! Tim’be ŵandamo mwalakwemo! Kwalakweko ni kupunda kwekulungwa nnope!”');
INSERT INTO "silika" VALUES(57,13,'57:13','Lisiku lyatachiŵecheta achinakunaku ŵachilume ni achinakunaku ŵachikongwe kwasalila aŵala ŵaŵakulupilile: “Tulindani kuti tupale nao lilanguka lyenulyo.” Kuchiŵechetedwa (kukwao mwachipongwe): “Ujilaani munyuma mwenu, ni nkasosesose lilanguka.” Basi ni uchiŵichidwa sikati jao uŵigo wana nnango, nkati mwakwe (upande wa ŵakulupilila) muchiŵa mwana chanasa, nambo kusa kwakwe cha upande ŵakwe (wa achinakunaku) kuchiŵa kwana ipotesi.');
INSERT INTO "silika" VALUES(57,14,'57:14','(Achinakunaku) tachaŵilanga ŵanganyao (ŵakulupilila), (achitiji): “Ana nganituŵaga imo ni jenumanja (ku duniya pakutenda ibada)?” (Ŵakulupilila) takajile: “Elo (mwaŵaga nowe pachanyape), nambo jenumanja mwalijonasile mwachimisyene (ni unakunaku), ni mwajembechele (kuti yapate ipato Asilamu), ni mwakayichile (ya kwimuka m’malembe), ni yannyenjile isako yenu yaunami mpaka chayiiche chilamusi cha Allah (chiwa), ni ŵannyenjile jenumanja pa ya Allah nkunyenga (Ibulisu).”');
INSERT INTO "silika" VALUES(57,15,'57:15','“Basi lelo ngachipocheledwa chaaliwombolela kuumila kukwenu namuno kwa aŵala ŵaŵakufuulu, mauto genu ni ku Moto, gele ni malo genu gakum’bajila, kaje kusakala malo gakuwujila gakwe!”');
INSERT INTO "silika" VALUES(57,16,'57:16','Ana nganijikwane ndaŵi kwa aŵala ŵaakulupilile janti mitima jao jiteteŵaleje pa kunkumbuchila Allah, ni (pakuipikana) ayila yakuona yaitulwiche (Qur’an). Soni ngasaŵa nti chisawu aŵala ŵaŵapedwile Chitabu kalakala (Ayuda ni Akilisito), basi jalewipe kukwao ndaŵi (jakulekangana ni achimitume ŵao), ni jajumwile mitima jao, soni ŵajinji mwa ŵanganyao ŵakunyosya.');
INSERT INTO "silika" VALUES(57,17,'57:17','Manyililani yanti Allah akulisyusyaga litaka panyuma pakuwa kwakwe, chisimu chene tunsalichisye jenumanja ilosyo (ya ukombosi Wetu) kuti nkole lunda.');
INSERT INTO "silika" VALUES(57,18,'57:18','Chisimu akutola sadaka ŵachilume ni akutola sadaka ŵachikongwe, ni kunkongosyaga Allah ngongole jambone (pakupeleka chipanje chao petala lya Allah), tiyijonjechesyedwe kukwao (ipanje yao pa duniya pano), ni akwete kupata soni ŵanganyao malipilo gakuchimbichika (ku Akhera).');
INSERT INTO "silika" VALUES(57,19,'57:19','Sano aŵala ŵankulupilile Allah ni Achimitenga Ŵakwe, ŵanganyao kwa M’mbuje gwao ni (ŵaakuŵalanjidwa kuti) ŵakulungamika kusyene (pachikulupi) soni ŵakuwila pa ngondo (jakumenyana ni makafili), akwete kupata ŵanganyao malipilo gao ni lilanguka lyao. Nambo aŵala ŵaakufuulu ni kanila ma Ȃya Getu ŵanganyao niŵandu ŵa ku Moto Waukali.');
INSERT INTO "silika" VALUES(57,20,'57:20','Manyililani yanti umi wa duniya uŵele ŵamasanje ni wangali mate, nambo soni chipeto, ni kupoochesyaana sikati jenu, ni kutupiya kusonganganya chipanje ni ŵanache. Ili chisawu wula jaukunsengwasyaga nnamalima mmela wakwe, kaneka ni kumula ni nkuuwonaga uli wam’bilichila, kaneka ni ukasaŵaga wakuteketuka (wangali chilichose). Sano ku Akhera tikukaŵe kwana ilagasyo yaukali nnope (kwa achimakafili), nambo soni chikululuko chakuumila kwa Allah ni chinonyelo (kwa ŵakulupilila). Ni nganiuŵa umi wa duniya ikaŵeje chisengwasyo chakunyenga.');
INSERT INTO "silika" VALUES(57,21,'57:21','Kangamalani wa ku chikululuko chakuumila kwa M’mbuje gwenu (Allah), ni Mbepo kusapanguka kwakwe kuli mpela kusapanguka kwa kwinani ni pasi, jiŵichidwe tayali kwa aŵala ŵankulupilile Allah ni Achimetenga Ŵakwe. Walakweo ni ukoto wa Allah waakumpaga jwansachile, soni Allah ni Nsyene ukoto wekulungwa nnope.');
INSERT INTO "silika" VALUES(57,22,'57:22','Nganichipatikaneje chipato petaka, namuno mu mwachimisyene, ikaŵeje (chalembedwe) m’Chitabu (cha Al-Lauh Al-Mahfȗz) nkanituchiguumbe, chisimu yalakweyo kwa Allah iŵele yakwepepala kusyene. ');
INSERT INTO "silika" VALUES(57,23,'57:23','Kuti nkadandaula pa yaimpiite, nambo soni nkatwanga pa yampeele, soni Allah ŵangannonyela jwakulikwesya jwalijose jwakuliiti.');
INSERT INTO "silika" VALUES(57,24,'57:24','Aŵala ŵaakutenda ubayili, ni kwachisyaga ŵandu kutenda ubayili. Sano jwatagalauche (kuleka yalamulidwe), basi (niyakwe), chisimu Allah ni Jwakupata kwejinji Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(57,25,'57:25','Chisimu twaatumisye Achimitenga Ŵetu ni ilosyo yakuonechela, ni twatumisye pampepe ni ŵanganyao chitabu kwisa soni chilungamiko, kuti ŵandu ajime (pakutendelana ichindu yao) mwachilungamiko, ni tutulwisye chisyano, mwalakwemo mwana machili gamajinji (gakuputila ngondo ), kwisa soni yakwakamuchisya ŵandu (yakulekangana-lekangana), soni kuti Allah ammanyukule mundu jwaakunkamuchisya Jwalakwe (paajikamuchisya dini Jakwe), ni (kwakamuchisya) Achimitenga Ŵakwe mwangali kum’bona (Allah jo). Chisimu Allah ni Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(57,26,'57:26','Soni chisimu chene twantumisye Nuhu ni Ibrahima, ni twaŵisile mu ŵanache ŵao Utume ni Chitabu, basi ŵane mwa ŵanganyao ŵaliji ŵakongoka, nambo ŵajinji mwa ŵanganyao ŵaliji ŵakunyosya.');
INSERT INTO "silika" VALUES(57,27,'57:27','Kaneko twakuyichisye mu sajo syao Achimitenga Ŵetu (ŵane), ni twakuyichisye soni ni Isa mwanache jwa Maryam, ni twampele Injil, ni twaŵisile m’mitima mwa aŵala ŵaŵankuyiye upole ni chanasa. Sano ungalombela ŵautandisye achimisyene, Uwwe nganitwalamulila yele. (Achimisyene ni ŵaŵaitandisye yalakweyo) pakusachilila chinonyelo cha Allah, ni nganaugosa (ungalombelao) kaugose kakwe kasyesyene. Basi ni twapele aŵala ŵaŵakulupilile (Allah ni Achimitenga Ŵakwe) mwa ŵanganyao malipilo gao, nambo ŵajinji mwa ŵanganyao ali ŵakunyosya.');
INSERT INTO "silika" VALUES(57,28,'57:28','E jenumanja ŵankulupilile! Mun’jogopani Allah, ni munkulupilile Ntenga Jwakwe (Muhammadi صلى الله عليه وسلم), tampe mitambala jiŵili ja chanasa Chakwe, ni tam’bichile lilanguka lyatin’jendeje nalyo, soni tankululuchile, sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(57,29,'57:29','Kuti ŵandu ŵaŵapedwile Chitabu (Ayuda ni Akilisito ŵangaakunkulupilila Muhammadi صلى الله عليه وسلم) amanyilile yanti ŵanganyao nganakola ukombosi uliose pa ukoto wa Allah, ni kuti ukoto (wosope) uli m’makono mwa Allah akumpaga jwaakunsaka, soni Allah ni Nsyene ukoto wekulungwa nnope.');
INSERT INTO "silika" VALUES(58,1,'58:1','Chisimu pamasile paapikene Allah liloŵe lya ajula (jwammasyeto) jwaakusisyana namwe (maloŵe) pa ya ŵankwakwe (ŵantuumbiliile), ni jukulaŵila (magambo) kwa Allah, mwanti Allah akuŵa ali nkupikana kuŵechetana kwenu, chisimu Allah ni Jwakupikanichisya Jwakulolechesya.');
INSERT INTO "silika" VALUES(58,2,'58:2','Ŵandu ŵaakuŵa mu jenumanja ali nkwalandanya achimmasyeto ŵao ni migongo (ja achimama ŵao pa chiŵako), ngaŵa nkuŵa ŵanyao achimama ŵao (pa chiŵako changaŵa nkutenda nao soni ulombela), nganaŵa achimama ŵao ni ikaŵeje aŵala ŵaŵaŵeleche, soni chisimu ŵanganyao akuŵecheta maloŵe gakusakala soni gaunami, soni chisimu Allah ni Nkusiimaasya (ilemwa) Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(58,3,'58:3','Sano ŵandu ŵaakuŵa ali nkwalandanya achimmasyeto ŵao ni migongo (ja achimama ŵao pa chiŵako changaŵa nkutenda nao soni ulombela), kaneko ni kuusyausya yaŵaŵecheteyo; basi agopole kapolo nkanaiche mwakwayana (ni achiŵammakwaowo), jalakweji ni (misyungu ja Allah) jankuwundidwa najo, sano Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(58,4,'58:4','Sano jwanganapata (kapolo) basi chataŵe myesi jiŵili jakuyana nkanaiche mwakwayana (ni ŵankwakwe), nambo jwanganaŵa apakombwele (kutaŵa) basi chiŵape yakulya masikini makumi nsano nalimo (60). Yalakweyo (ilamulidwe) kuti munkulupilile Allah ni Ntenga Jwakwe صلى الله عليه وسلم. Soni jalakweji ni mipingu ja Allah, ni akwete kupata achimakafili ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(58,5,'58:5','Chisimu aŵala ŵaakunsiisya Allah ni Ntenga Jwakwe صلى الله عليه وسلم, anyosyedwe mpela yaŵatite pakunyosyedwa ŵakala ŵao, soni pamasile patutulwisye ma Ȃya gakusalichisya, ni akwete kupata achimakafili ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(58,6,'58:6','Lisiku lyatachasyusya Allah ŵanaose ni kwatagulila yaŵapanganyisye, Allah jwaisonganganyisye (ni kuisunga) nambo ŵanganyao ŵailiŵalile, sano Allah pa chindu chilichose aŵele Mboni.');
INSERT INTO "silika" VALUES(58,7,'58:7','Ana ngankuiona yanti Allah jukuimanya yaili kumawunde ni yaili petaka? Ngaukasatamidwaga ntemela wa ŵandu ŵatatu ikaŵeje Jwalakwe jukasaŵa Jwancheche jwao, naatamuno wa ŵandu nsano ikaŵeje Jwalakwe jukasaŵa Jwansano najumo jwao, naatamuno wa ŵandu ŵakunandipila pele kapena ŵakutupila ikaŵejepe Jwalakwe jukasaŵa nao palipose pachaŵe (ni umanyilisi Wakwe wangasisika chilichose), kaneka ichindu yaŵapanginyisye tachatagulila lisiku lya Kiyama, chisimu Allah chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(58,8,'58:8','Ana ngankwaona aŵala ŵanya ŵaŵakanyisyidwe kutama ntemela (wamalindi), kaneka ni akuwujauja kutenda yaŵakanyisyidweyo? Ni akusongonelanaga yasambi ni yaumagongo kwisa soni yakunnyosya Ntenga صلى الله عليه وسلم. Soni pati am’bichililemo akasankomasyaga ni komasya (kwakuchindimbula) kwanganankomasyeje nako Allah, m’mitima mwao niakutiji: “Nambi uli Allah ngatupa ilagasyo pa yatukuŵechetayi?” Jahannama jichakwana ŵanganyao, chachijijinjila. Kaje kusakala malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(58,9,'58:9','E jenumanja ŵankulupilile! Pati nchitamaga ntemela basi ngasinsongonelanaga yasambi ni yaumagongo kwisa soni yakunnyosya Ntenga صلى الله عليه وسلم, nambo nsongonelaneje yaukoto ni woga (wakun''jogopa Allah), soni mun''jogopani Allah Ajula jwachinchisonganganyisyidwa Kukwakwe.');
INSERT INTO "silika" VALUES(58,10,'58:10','Chisimu mitemela (jamalindi) jikuumilaga ku shetani kuti ŵaadandaulikasye aŵala ŵaakulupilile, nambo nganaŵa ŵaapoteche nao chilichose ikaŵeje kwa lisosa lya Allah, sano ŵakulupilila ajegamileje kwa Allah pe basi.');
INSERT INTO "silika" VALUES(58,11,'58:11','E jenumanja ŵankulupilile! Pati nsalilidwemo kuti pelekani lipesa panganya basi pelekani lipesalyo, Allah chansapangulile (ukoto Wakwe), soni pati iŵechetedwe yanti: “Jimani (kuja kutenda masengo ga Dini),” basi jimani. Allah tiŵanyakule pa uchimbichimbi aŵala ŵaakulupilile mwa jenumanja kwisa soni aŵala ŵaapedwile umanyilisi, sano Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(58,12,'58:12','E jenumanja ŵankulupilile! Ndema jankusaka kuŵecheta najo Ntenga صلى الله عليه وسلم yantemela, basi longosyani (kutola) sadaka (jakwapa masikini) nkaniuŵe ntemela wenuo, yalakweyo ni yaili yambone nnope kukwenu ni yakuswejelesya, nambo naga nganimpata (sadakajo ngalikandu), basi chisimu Allah ni Jwakululuka kusyene Jwachanasa channope.');
INSERT INTO "silika" VALUES(58,13,'58:13','Ana n''jogwepe kulongosya (kutola) sadaka nkaniuŵe ntemela wenu (wakuŵechetana najo Ntenga صلى الله عليه وسلم)? Sano naga nganim’biche mwakutenda, basi Allah an''jepepachisye, sano gambagani kwimika Swala mwakolosya ni kutolaga Zaka, soni mumpikanilani Allah ni Ntenga Jwakwe صلى الله عليه وسلم, sano Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(58,14,'58:14','Ana ngankwaona aŵala (achinakunaku) ŵatomene nao ulongo ŵandu ŵaŵatumbilile Allah? Ŵanganyao nganaŵa mwa jenumanja nambo soni nganaŵa mwa ŵanganyao, soni akulumbilaga yaunami aku achimanyililaga (kuti akulambusya).');
INSERT INTO "silika" VALUES(58,15,'58:15','Allah ŵalinganyichisye ŵanganyao ilagasyo yaukali nnope, chisimu ŵanganyao iŵele yakusakala nnope yaaŵele nkupanganya.');
INSERT INTO "silika" VALUES(58,16,'58:16','Akutesile kulumbila kwao (kwaunami) kuŵa chakusiŵilila (itendo yao yangalumbana), basi ni ŵasiŵilile (ŵandu) kwitala lya Allah; ni akwete kupata ŵanganyao ilagasyo yakwalusya.');
INSERT INTO "silika" VALUES(58,17,'58:17','Ngasichija kwakamuchisya ŵanganyao chipanje chao atamose ŵanache ŵao chilichose mu (ipotesi) yakuumila kwa Allah, ŵanganyao niŵandu ŵa ku Moto, ŵanyao kwalakweko takaŵe ŵandamo.');
INSERT INTO "silika" VALUES(58,18,'58:18','Lisiku lyatachasyusya Allah ŵanaose, basi ni chachiŵa nkulumbila Kukwakwe (Allah, mwaunami) yaŵatiji pakulumbila kukwenu, aku ali nkuganichisya yanti chapate chilichose (pakulumbila kwaoko), manyililani yanti ŵanganyao chisimu chene niŵali ŵaunami.');
INSERT INTO "silika" VALUES(58,19,'58:19','Shetani jwaguŵatiile ŵanganyao ni jwaliŵeesye kunkumbuchila Allah, ŵanganyao ni likuga lya shetani, manyililani yanti chisimu chene likuga lya shetani nilyalili lyakulepela.');
INSERT INTO "silika" VALUES(58,20,'58:20','Chisimu aŵala ŵaakunsiisya Allah ni Ntenga Jwakwe صلى الله عليه وسلم ŵanganyao chachiŵa mu ŵakunyosyeka nnope.');
INSERT INTO "silika" VALUES(58,21,'58:21','Allah jwaalembile yanti: “Chisimu Une ni Achimitenga Ŵangu niŵachituŵe Ŵakupunda.” Chisimu Allah ni Jwakulimbangana nnope, Jwamachili gakupunda.');
INSERT INTO "silika" VALUES(58,22,'58:22','Ngamwasimana ŵandu ŵaakunkulupilila Allah ni Lisiku Lyambesi ali nkwanonyela ŵandu ŵaakunsisya Allah ni Ntenga Jwakwe صلى الله عليه وسلم, namuno akaliji achatati ŵao, kapena ŵanache ŵao, kapena achachimwene ŵao, kapena ŵalukosyo lwao. Ŵanganyao (Allah) alembile m’mitima mwao chikulupi (mwakuchisimika kusyene), ni ŵalimbangenye ni Nsimu (Jibulilu) wakuumila Kukwakwe, ni tachajinjisya ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo. Allah tachanonyela (pakwapochela masengo gao gambone), nombe nao chachinonyelwa Najo (pamalipilo gataachaapa). Ŵanganyao ni likuga lya Allah, manyililani yanti chisimu chene likuga lya Allah ni lyalili lyakupunda.');
INSERT INTO "silika" VALUES(59,1,'59:1','Iŵele ili nkunswejesya Allah yosope yaili kumawunde ni yaili petaka, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(59,2,'59:2','Jwalakwe ni Ajula jwana kwakoposyaga ŵandu ŵaŵakanile mwa anchinsyene chitabu (Ayuda ŵalukosyo lwa Nadhîr) kutyochela m’majumba mwao mu kusonganganyisidwa kwandanda (kwausami). Nganin''ganichisyaga kuti mpaka akopoche, nombe nao ŵaganichisyaga yanti chisimu ŵanganyao gasiŵilile makome gao kwa Allah, basi Allah ni jwaapele (ipotesi) kuumila upande wanganaganichisyaga, ni jwaponyisye woga m’mitima mwao, (mwanti) ŵaliji nkugumula majumba gao ni makono gao kwisa soni makono ga ŵakulupilila. Basi chetelelani jenumanja ŵankwete meso (gakulolechesya mwaumanyilisi).');
INSERT INTO "silika" VALUES(59,3,'59:3','Soni ingaŵaaga kuti Allah jwaalembele (pandandape) yakusama, jukaapele ilagasyo pa duniya, nambo akwete kupata ŵanganyao ku Akhera ilagasyo ya Moto.');
INSERT INTO "silika" VALUES(59,4,'59:4','Yalakweyo paligongo lyanti ŵanganyao ŵatindene najo Allah ni Ntenga Jwakwe صلى الله عليه وسلم, sano jwatatindane najo Allah, basi chisimu Allah ni Jwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(59,5,'59:5','Muitela ya tende ya mwaikatile (jenumanja Asilamu) kapena ya mwailesile ili chiimile m’mapata mwakwe, basi (yatendekwe) mu lisosa lya Allah ni kuti ŵajalusye ŵakunyosya.');
INSERT INTO "silika" VALUES(59,6,'59:6','Soni ipanje yakupalagula yaapeleche Allah kwa Ntenga Jwakwe صلى الله عليه وسلم kuumila kwa ŵanganyao, basi pa (kuipata) yalakweyo nganinnajila kuutusya hachi kapena ngamiya, nambo kuti Allah akwapaga machili Achimitenga Ŵakwe gakwakombolela ŵaakwasaka, sano Allah pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(59,7,'59:7','Ipanje yakupalagula yaapeleche Allah kwa Ntenga Jwakwe صلى الله عليه وسلم kuumila kwa achinsyene misi, basi iŵele ya Allah ni Ntenga صلى الله عليه وسلم ni ŵaulongo (wa Ntenga صلى الله عليه وسلم), kwisa soni ŵawina, masikini ni jwapaulendo; kuti ikaŵa yagamba kusyungula chilikati cha ŵakusichila mwa jenumanja. Sano champele Ntenga صلى الله عليه وسلم basi chipochelani, nambo chankanyisye basi chilekani. Ni mun''jogopani Allah, chisimu Allah ni Jwaukali pakupeleka ipotesi.');
INSERT INTO "silika" VALUES(59,8,'59:8','(Ipelechedweje) kuŵakulaga ŵaasamile, aŵala ŵakoposyedwe nsimangwao kwisa soni ipanje yao achisachililaga umbone wa kuumila kwa Allah kwisa soni chinonyelo, ni akuŵa ali nkunkamuchisya Allah (paajikamuchisya Dini Jakwe), ni (akuŵa soni nkunkamuchisya) Ntenga Jwakwe صلى الله عليه وسلم, ŵanganyao niŵali ŵakuonaonape.');
INSERT INTO "silika" VALUES(59,9,'59:9','Sano aŵala ŵaŵatemi mu nsinda (wa Madina) ni kulupilila nkanaiche ŵanganyao akuŵa ali nkwanonyela ŵandu ŵaasaamiile kukwao, soni ngaakuchisimana m’mitima mwao chipaka chilichose mu ichindu yaapedwile (akusamao), soni akulongosyaga (isako ya achimijao pa chindu chilichose) kupunda isako yao, namuno angaliji ali nkuchisachilila (chinducho) kwanneso. Sano ŵaakuŵambasyidwa ku ubaili wa ntima wao basi ŵanganyao ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(59,10,'59:10','Sano aŵala ŵaiche panyuma pao akasatiji: “Ambuje ŵetu! Tukululuchilani uwwe ni achimijetu aŵala ŵaŵatulongolele pa chikulupi, ni ngasim''biika m’mitima mwetu uchimwa wakwatendela aŵala ŵaakulupilile, Ambuje ŵetu! Chisimu Mmwejo ni Mpole kusyene, Jwachanasa cha nnope.”');
INSERT INTO "silika" VALUES(59,11,'59:11','Ana nganimwaona aŵala akutenda unakunaku ŵaakutiji pakwasalila achimijao aŵala ŵakanile mwa achinsyene chitabu (Ayuda): “Chisimu naga n''di nkoposyedwe jenumanja (m’Madina muno) tuchikopoka namwe kwene, soni ngasitun''jiitika jwalijose mpaka kalakala pa (yakunsausya) jenumanja, soni naga n''di mputidwe ngondo (ni Asilamu) tuchinkamuchisya kwene.” Nambo Allah akutendela umboni kuti chisimu ŵanganyao ŵaunami.');
INSERT INTO "silika" VALUES(59,12,'59:12','Mwanti naga ali akoposyedwe (Ayudao) nganaŵa akopweche nao imo (achinakunakuo), soni naga ali aputidwe ngondo nganaŵa ŵakamuchisye, soni mwanti ali atite ŵakamuchisye mpaka alosye kwene migongo (kutila), kaneka ni ngasakulupusyidwa.');
INSERT INTO "silika" VALUES(59,13,'59:13','Chisimu ŵanganyammwe (Asilamu) niŵan''di ŵakogopedwa nnope m’mitima mwao kupunda Allah, yalakweyo niligongo lyanti ŵanganyao niŵandu ŵangali chachimanyi.');
INSERT INTO "silika" VALUES(59,14,'59:14','Nganaŵa amputile ngondo (Ayudao atamose ali) wosope, ni ikaŵeje m’misi jegosedwe ni makome, kapena kusyeto kwa mapupa. Ngondo jao (ja utinda) chilikati chao jiŵele jekulungwa nnope, mpaka mwaganichisye kuti ali imo kutendaga mitima jao jelekangane, yalakweyo niligongo lyanti ŵanganyao niŵandu ŵangali lunda.');
INSERT INTO "silika" VALUES(59,15,'59:15','(Kaŵe ka Ayuda ŵalukosyo lwa Nadhîr) kali chisawu aŵala (Ayuda ŵalukosyo lwa Kainukai) ŵaŵalongolele ŵanganyao (kupata ilagasyo) pangakaŵapa, agapasisye makungu ga itendo yao, ni akwete soni kupata ŵanganyao ilagasyo yakupoteka nnope (ku Akhera).');
INSERT INTO "silika" VALUES(59,16,'59:16','(Chilandanyo cha achinakunaku pakwanyenga Ayuda ŵalukosyo lwa Nadhîr ya kumpitikuchila Muhammadi (صلى الله عليه وسلم chili chisawu shetani ndema jaŵansalile mundu kuti: “Munkanani (Allah).” Basi ndema ja ŵakanile, (shetani) jwatite: “Chisimu une ndalikangene namwe, unetu ngun''jogopa Allah M’mbuje jwa iwumbe yosope.”');
INSERT INTO "silika" VALUES(59,17,'59:17','Basi ni jaliji mbesi ja ŵanaŵaŵiliŵa kuja kwinjila ku Moto ni kuŵa ŵandamo mwalakwemo, sano galakwego ni malipilo ga ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(59,18,'59:18','E jenumanja ŵankulupilile! Mun''jogopani Allah, soni mundu jwalijose ayilole yaalongwesye ya malaŵi (ku Akhera), ni mun''jogopani Allah, chisimu Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(59,19,'59:19','Ni ngasim''ba mpela aŵala ŵanya ŵan''diŵalile Allah, nombe Najo ni ŵaliŵeesye mitima jao. Ŵanganyao niŵali ŵakunyosya.');
INSERT INTO "silika" VALUES(59,20,'59:20','Ngaŵa nkulandana ŵandu ŵa ku Moto ni ŵandu ŵa ku Mbepo, ŵandu ŵa ku Mbepo ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(59,21,'59:21','Ingaŵe kuti twajitulwisye aji Qur’aniji pachanya petumbi, chisimu nkaliweni lili lyakuteteŵala ni ganjika ligongo lya kun''jogopa Allah, yalakweyo ni ilandanyo ya tukupeleka kwa ŵandu kuti achetelele.');
INSERT INTO "silika" VALUES(59,22,'59:22','Jwalakwe ni Allah Ajula jwaali pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Jwakumanyilila yakusisika ni yakuonechela, Jwalakwe ni Jwaukoto wejinji, Jwachanasa channope.');
INSERT INTO "silika" VALUES(59,23,'59:23','Jwalakwe ni Allah Ajula jwali pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, Nchimwene, Jwakuswejela, Nsyene ntendele, Nkupeleka ntunjelele, Nkung''unulila ni goosa chilichose (mu itendo ya achikapolo Ŵakwe), Jwamachili gakupunda, Nkuteteŵasya iwumbe Yakwe, Jwakulikusya kusyene. Aswejele Allah ku yaakum''bwanganya nayo.');
INSERT INTO "silika" VALUES(59,24,'59:24','Jwalakwe ni Allah, Nkugumba, Nkusipusya chilichose pandanda, Nkupaanga kaoneche ka chindu. Jukwete mena gambone nnope. Ikunswejesyaga Jwalakwe yosope yaili kumawunde ni petaka, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(60,1,'60:1','E jenumanja ŵankulupilile! Ngasimwatenda amagongo Ŵangu ni amagongo ŵenu kuŵa isyoŵe ni kwatulilaga (abali syenu) mwachinonyelo, kutendaga pamasile paayikanile yakuona yaim’bichilile. Akunkoposya Ntenga صلى الله عليه وسلم ni jenumanja ŵakwe (nsimangwenu) ligongo lyanti nkunkulupilila Allah M’mbuje gwenu. Nam''baga jenumanja nkopweche kuumenyana ngondo petala Lyangu ni kusachilila chinonyelo Changu (ngasimwatenda kuŵa isyoŵe yenu), nkwatumichisya asili (syenu syangondo) mwachinonyelo kutendaga Une nguimanyilila yansisile ni yannosisye (palangulangu)! Sano jwataitende yele mwa jenumanja nikuti amasile mwapotele kuleka litala lyagoloka.');
INSERT INTO "silika" VALUES(60,2,'60:2','Ali asimangene namwe (pangondo) akuŵaga amagongo ŵenu, soni akasan''jongolelaga makono gao ni ndimi syao mwakusakala (pakumputa ni kuntukana), ni akasasachililaga n''di nkufuulu (pakuleka Usilamu).');
INSERT INTO "silika" VALUES(60,3,'60:3','Ngasaŵa ankamuchisye achalongo achin''jenu namuno ŵanache ŵenu lisiku lya Kiyama, (Allah) tachijilanya chilikati chenu, sano Allah ichindu yankutenda aŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(60,4,'60:4','Pamasile paluŵele kukwenu lusyasyo lwambone pa Ibrahima ni aŵala ŵaŵaliji najo, ndema ja ŵasalile ŵandu ŵao kuti: “Chisimu uwwe tuŵambalene namwe kwisa soni ayila yankuŵa nkuigalagatila kunneka Allah, tunkanile ŵanganyammwe, soni uonechele chilikati chetuwe ni jenumanja utinda ni uŵengani wa mpaka kalakala mpaka nkankulupilile Allah Jikape.” (Nombe jenumanja umati wa Muhammadi صلى الله عليه وسلم syasyani yele yagakana makafiliyo,) ikaŵeje maloŵe (gajwaŵechete) Ibrahima kwasalila babagwe (ŵaŵaliji kafili kuti): “Chisimu chene chinjim''bendela chikululuko, nambo nganingola chilichose chanti ni kun''gosela nacho kwa Allah.” (Yagaŵendela chikululuko makafiliyo nkasyasya, nambo n''jileje): “Ambuje ŵetu! Kukwenu pe tujeegamiile, soni Kukwenu pe tugalauchiile pakutenda toba, soni Kukwenu pe ni kwakuujila.”');
INSERT INTO "silika" VALUES(60,5,'60:5','“Ambuje ŵetu! Ngasintuŵika uwwe pa kulingwa ni achimakafili (pakwakombolasya kutuwatanganyichisya chikulupi), soni tukululuchilani Ambuje ŵetu! Chisimu Mmwe niŵan''di Ŵamachili gakupunda, Ŵalunda lwakusokoka.”');
INSERT INTO "silika" VALUES(60,6,'60:6','Chisimu pamasile paluŵele kukwenu pa jamanjao (Ibrahima ni umati wakwe), lusyasyo lwambone kwa mundu jwaali nkuŵichila ntima pa Allah ni lisiku lyambesi, nambo mundu jwatanyosye (yaalamwile Allah), basi chisimu Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(60,7,'60:7','Mwine Allah kuŵa nkuŵika usyoŵe chilikati chaŵanganyammwe ni ŵane mwa aŵala ŵamwatesile kuŵa amagongo ŵenu (pa kwajongolela ku Usilamu), soni Allah ni Jwakombola kusyesyene, soni Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(60,8,'60:8','Allah ngaakunkanya ŵanganyammwe kwaatendela yaukoto ni yagoloka aŵala ŵanganamputa ngondo pa Dini soni nganankoposya nsimangwenu, chisimu Allah akwanonyela ŵagolosya (ichindu).');
INSERT INTO "silika" VALUES(60,9,'60:9','Chisimu Allah akunkanya ŵanganyammwe kusyoŵekana nao aŵala ŵamputile ngondo pa Dini ni kunkoposya nsimangwenu, ni akamuchiisye pa yakoposyedwa kwenu, sano ŵatasyoŵekane nao ŵanganyao, basi ŵanyao ni ŵali ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(60,10,'60:10','E jenumanja ŵankulupilile! Pati am’bichiiliile ŵakulupilila ŵachikongwe ŵakusama basi mwalingani (kuti nchimanye chikulupi chao), Allah ni Jwaakumanyilila nnope ya chikulupi chao. Sano naga n''di mwamanyi kuti ŵakulupilila, basi ngasimwauchisya ku makafili, nganaŵa ŵanyao (achimmasyeto) ŵakundisyidwa kwa ŵanganyao, nombenao (achimakafili) nganaŵa (achikunganya) ŵakundisyidwa kwa ŵanyao, nambo mwauchisyani (achimakafilio) yaŵatosile (pakwalomba achimmasyetowo), ni chikuŵe kwangali ulemwa kukwenu kwalomba (achimmasyetowo) pati mwapele mahari gao (namuno kuti ŵalesile achalume ŵao ŵachikafili ku Maaka), soni ngasinkanganichila malombela ni achimmasyeto ŵachikafili (ŵaŵasigele ku Maaka), ŵilasyani ya mwatosile (pakwalomba achimmasyeto ŵachikafilio), nombe nao (achimakafili) aŵilasye yaŵatosile (pakwalomba achimmasyeto ŵaaujitiche Usilamu). Wele ni ulamusi wa Allah, Jwalakwe akulamula chilikati chenu. Sano Allah ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(60,11,'60:11','Sano naga jwalijose mwa achiŵammakwenu ali antisile kuja kumakafili (ni m''bilasisye mahari genu nambo akanyisye), kaneka ni mpatile chipanje (cha makafilio chachaŵajilwe kwauchisya pa achiŵammakwao ŵaŵatisile kapena chinchipatile petala lyangondo), basi mwapani aŵala ŵaatisile achiŵammakwao yakulandana ni yaŵatosile (pakulombela). Soni mun''jogopani Allah Ajula jwankuŵa nkunkulupilila.');
INSERT INTO "silika" VALUES(60,12,'60:12','E mmwe Ntume صلى الله عليه وسلم! Pati am’bichiiliile ŵakulupilila ŵachikongwe kuutaŵana namwe chilanga chanti; ngawwanganyaga Allah ni chilichose, soni ngajiŵaga, soni ngatendaga chigwagwa, soni ngaulagaga ŵanache ŵao, soni ngaikaga nao ukapanga-panga (unami) watausepeje chilikati chamakono gao ni ngongolo syao (kwakuli kwalambuchisya achiŵammakwao ŵanache ŵangaŵandika), soni ngannyosyaga pa ichindu yambone (yatimwalamuleje), basi taŵanani nao chilangacho, ni mwaŵendelani chikululuko kwa Allah, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(60,13,'60:13','E jenumanja ŵankulupilile! Ngasinsyoŵekana nao ŵandu ŵaŵatumbilile Allah, pamasile paakatile tama (jakupata malipilo gambone) ku Akhera (ligongo lya kusakasya nnope), yaatite pa kata tama (jakupata yambone iliyose) achimakafili ŵaali m’malembe.');
INSERT INTO "silika" VALUES(61,1,'61:1','Iŵele ili nkunswejesya Allah yosope yaili kumawunde ni yaili petaka, soni Jwalakwe ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(61,2,'61:2','E jenumanja ŵankulupilile! Ligongo chichi nkuŵechetaga yangankutendaga?');
INSERT INTO "silika" VALUES(61,3,'61:3','Ikunchimaga kwekulungwa nnope Allah yakuti m''becheteje yangankutendaga.');
INSERT INTO "silika" VALUES(61,4,'61:4','Chisimu Allah akwanonyela aŵala ŵaakumenyanaga ngondo petala Lyakwe ali chilili pansela nti chisawu ŵanganyao lipupa lyekamulane chenene. ');
INSERT INTO "silika" VALUES(61,5,'61:5','Soni (kumbuchilani) katema kajwaŵechete Musa ku ŵandu ŵakwe kuti: “E ŵandu ŵangu! Ligongo chichi nkuusausya, kutendaga nkumanyilila kusyene yanti une ni Ntenga jwa Allah kukwenu?” Basi ndema jaŵapombotele (kuleka litala lya Allah), Allah ŵajipombotesye mitima jao, sano Allah ngakwajongolaga ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(61,6,'61:6','Soni (kumbuchilani) katema kajwaŵechete Isa mwanache jwa Maryam kuti: “E jenumanja ŵanache ŵa Israila! Chisimu une ni Ntenga jwa Allah kukwenu jungwitichisya yaili paujo pangu yakuumila mu Taurat, soni ni ngutagulila ya abali jakusengwasya ja Ntenga jwatachiika panyuma pangu lina lyakwe Ahmadu.” Nambo ndema jajwaichilile ni ilosyo yakuonechela papaswela, ŵatite: “Awu ni usaŵi ŵakuonechela.”');
INSERT INTO "silika" VALUES(61,7,'61:7','Ana soni nduni jwali jwalupuso nnope kumpunda mundu jwansepele Allah unami kutendaga jwalakwe ali nkuŵilanjidwa ku Usilamu? Sano Allah ngaakwajongolaga ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(61,8,'61:8','Akasasachililaga ali asimisye lilanguka lya Allah (Usilamu) ni ngamwa syao, sano Allah ni Jwakwanilisya lilanguka Lyakwe namuno achimakafili yachime.');
INSERT INTO "silika" VALUES(61,9,'61:9','Jwalakwe ni Ajula jwaatumisye Ntenga Jwakwe (Muhammadi صلى الله عليه وسلم) ni chongolo kwisa soni Dini jakuona kuti ajikwesye pachanya pa dini syosope, namuno ŵakuwanganya (Allah ni isanamu) yachime.');
INSERT INTO "silika" VALUES(61,10,'61:10','E jenumanja ŵankulupilile! Ana nammanyisye malonda gampaka gan''jokole ku ilagasyo yakupoteka nnope?');
INSERT INTO "silika" VALUES(61,11,'61:11','Munkulupilileje Allah ni Ntenga Jwakwe صلى الله عليه وسلم, soni mputeje ngondo petala lya Allah ni chipanje chenu ni mwachimisyene ŵakwe, yalakweyo ni yaili yambone kukwenu naga n''di ŵakumanyilila.');
INSERT INTO "silika" VALUES(61,12,'61:12','Tankululuchile sambi syenu ni kun''jinjisya ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, ni malo gambone nnope ku Matimbe Gandamo. Kwalakweko ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(61,13,'61:13','Ni (ukoto) wine wachiwwunonyele (chachili) chikamuchisyo chakuumila kwa Allah kwisa soni kuugula (Maaka) kwakuli pakuŵandika, ni mwatagulilani abali jakusengwasya ŵakulupilila.');
INSERT INTO "silika" VALUES(61,14,'61:14','E jenumanja ŵankulupilile! Ŵaagaani ŵakamuchisya (pakwenesya Dini ja) Allah, mpela yajwatite kuŵecheta Isa mwanache jwa Maryam pakwawusya achinafunsi (ŵakwe kuti): “Ana akungamuchisya ŵangu niŵani kwa Allah?” Achinafunsi (ŵakwe) ŵatite: “Uwwe ni akukamuchisya (pakwenesya Dini ja) Allah.” Basi likuga line mu ŵanache ŵa Israila lyakulupilile, nambo likuga line lyakanile. Basi ni twaalimbangenye aŵala ŵaŵakulupilile pa amagongo ŵao ni ŵasyuchile kuŵa ŵapachanya.');
INSERT INTO "silika" VALUES(62,1,'62:1','Yosope yaili kumawunde ni yaili petaka ikunswejesyaga Allah, Nchimwene, Jwakuswejela, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(62,2,'62:2','Jwalakwe ni Ajula jwaatumisye ku ŵangapakombola kulemba ni kuŵalanga (Ŵarabu) Ntenga صلى الله عليه وسلم jwakuumila mu ŵanganyapeo jwaakuŵa nkwasoomela ma Ȃya Gakwe, ni kwaswejesyaga, ni kwajiganyaga soni Chitabu ni lunda lwakusokoka, kutendaga kala ŵaliji m’kusokonechela kwakuonechela.');
INSERT INTO "silika" VALUES(62,3,'62:3','Ni (antumiisye soni ku) ŵane mwa ŵanganyao ŵanganasimangane nao. Sano Jwalakwe (Allah) ni Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(62,4,'62:4','Walakweo niumbone wa Allah waakumpaga jwaakunsaka, sano Allah ni Nsyene umbone wekulungwa.');
INSERT INTO "silika" VALUES(62,5,'62:5','Chilandanyo cha aŵala (Ayuda) ŵana kupedwaga Taurat kaneka ni nganajitendela masengo, chili chisawu mbunda (bulu) jajikutwichila itabu yekulungwa-kulungwa (nambo ngali yakunkamuchisya). Kaje kusakala chilandanyo cha ŵandu aŵala ŵaakaaniile ma Ȃya ga Allah, Sano Allah ngaakwajongolaga ŵandu ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(62,6,'62:6','Jilani: “E jenumanja ŵan''di m''chiyuda! Nam''baga nkuliitwanjila yanti ŵanganyammwe ni asyoŵe ŵa Allah pangali ŵandu ŵane soni, basi chilajilani chiwa naga nkuŵecheta yakuona.”');
INSERT INTO "silika" VALUES(62,7,'62:7','Nambo ngaachilajila ata panandi ligongo lya (yasambi) yagalongwesye makono gao, sano Allah ni jwaakwamanya chenene ŵakulitenda lupuso.');
INSERT INTO "silika" VALUES(62,8,'62:8','Jilani: “Chisimu chiwa chankuchitilacho mwannami tichimpatepe, kaneka ni tinchiuchisyidwa kwa (Allah) Jwakumanyilila yakusisika ni yakuonechela, basi ni tachintagulila ichindu yamwaliji nkupanganya.”');
INSERT INTO "silika" VALUES(62,9,'62:9','E jenumanja ŵankulupilile! Pati ukomedwe nsana wa Swala (ja Ijuma) Lisiku Lyaijuma basi kangamalani kuja kunkumbuchila Allah, soni lekani malonda, yalakweyo ni yaili yambone kukwenu naga n''di ŵakumanyilila.');
INSERT INTO "silika" VALUES(62,10,'62:10','Sano pati Swala (ja Ijuma) jimasile basi balalikanganani pa chilambo ni kupitaga nchisosa umbone wa Allah, soni munkumbuchileje Allah kwejinji kuti m''be ŵakupunda.');
INSERT INTO "silika" VALUES(62,11,'62:11','Soni pati agaweni malonda kapena yang''anda-ng''anda akuigungumuchila yalakweyo ni kunneka mmwe n''di chiimile. Jilani: “Ichindu yaili kwa Allah ni yambone nnope kupunda yang''anda-ng''anda kwisa soni malonda, sano Allah ni Jwambone nnope mwa akupeleka lisiki.”');
INSERT INTO "silika" VALUES(63,1,'63:1','Pati am’bichiiliile achinakunaku akasatiji: “Tukutendela umboni yanti chisimu mmwe (Muhammadi صلى الله عليه وسلم) Ntenga jwa Allah.” Sano Allah akumanyilila kuti chisimu mmwejo Ntenga Jwakwe, soni Allah akutendela umboni yanti chisimu achinakunaku ŵaunami kusyene.');
INSERT INTO "silika" VALUES(63,2,'63:2','Akutesile kulumbila kwao (kwaunami) kuŵa chakusiŵilila (itendo yao yangalumbana), basi ni ŵaasiŵilile (ŵandu) kwitala lya Allah; chisimu ŵanganyao iŵele yakusakala nnope yaaŵele nkupanganya.');
INSERT INTO "silika" VALUES(63,3,'63:3','Yalakweyo ligongo lyanti ŵanganyao ŵaakulupilile kaneka ni kanila, basi ni jaugalidwe mitima jao mwanti nganaŵa amanyilile (chilichose chakwajokola).');
INSERT INTO "silika" VALUES(63,4,'63:4','Sano ndema jankwaonaga ikunsengwasyaga ilu yao, soni atite aŵechete nkasagatejelaga liwiwi maloŵe gao (ligongo lyakakolosye kao pakuŵecheta, nambo maloŵe gao gali gangali mate), ŵanganyao agambile kuŵa nti chisawu ipika yaijegamisyidwe (kwipupa, agambile kuŵa ilu-ilupe yangali lunda), akuganichisyaga n''gumilo uliose (mpela wakuŵilanjila ku ngondo) kuti ukwamba ŵanganyao (ligongo lya woga), ŵanganyao ni amagongo, mwakalamukani, pamasile pa ŵalwesile Allah! Ana akugalausyidwa chinauli (kuleka yakuona ni kulupilila unami)?');
INSERT INTO "silika" VALUES(63,5,'63:5','Soni pati iŵechetedwe kukwao (yanti): “Ikanganani kuti am''bendele chikululuko Ntenga jwa Allah,” akasapombotasyaga mitwe jao, soni mpaka mwaone achisyululaga aku ali nkulikwesya.');
INSERT INTO "silika" VALUES(63,6,'63:6','Yalumope kwa jamanjao, mwaŵendele chikululuko kapena nkaŵendela, Allah nganaŵa ŵakululuchile. Chisimu Allah jwangaŵa nkwajongola ŵandu ŵakunyosya.');
INSERT INTO "silika" VALUES(63,7,'63:7','Jamanjao ni aŵala ŵaakutiji (pa kwasalila Answari): “Ngasintola (chipanje chenu) kwapa aŵala (Akusama) ŵali kwa Ntenga jwa Allahصلى الله عليه وسلمmpaka atimbuche.” Kutendaga Allah ni Nsyene imatiilo (yosope ya ipanje) ya kumawunde ni petaka, nambo kuti achinakunaku ngakupata kumanyilila kusyesyene.');
INSERT INTO "silika" VALUES(63,8,'63:8','Akutiji: “Naga tuujile ku Madina, chisimu jwakuchimbichika nnope (achambaga Abdullah bin Ubayi bin Salulu nnongola jwa achinakunaku), chaakankoposye mwalakwemo jwakunyosyeka nnope (achambaga Muhammadi صلى الله عليه وسلم).” Kutendaga Allah ni Nsyene uchimbichimbi, kwisa soni Ntenga Jwakwe صلى الله عليه وسلم ni ŵakulupilila, nambo kuti achinakunaku ngakumanyilila.');
INSERT INTO "silika" VALUES(63,9,'63:9','E jenumanja ŵankulupilile! Ikalluyaluya ipanje yenu ni ŵanache ŵenu kwakuleka kunkumbuchila Allah, sano ŵachaiche mwakutenda yele, basi ŵanganyao niŵataŵe ŵakwasika.');
INSERT INTO "silika" VALUES(63,10,'63:10','Soni tolani mu ichindu yatumpele nkanichim’bichilile jumo jwenu chiwa, ni kwisa kuŵecheta (mwakudandaula kuti): “Ambuje ŵangu! Uli nganjelewasya kandaŵi kannono kuti ndole sadaka ni mbe mu ŵandu ŵambone!”');
INSERT INTO "silika" VALUES(63,11,'63:11','Sano Allah nganaŵa auchelewasisye ntima uliose pati jiiche ndema jakwe (jakuwila), soni Allah ni Nkusimanya chenene abali syosope sya yankutenda.');
INSERT INTO "silika" VALUES(64,1,'64:1','Yosope yaili kumawunde ni yaili petaka ikunswejesyaga Allah, uchimwene wosope wene ni Wakwe soni lumbili losope lwene ni Lwakwe, soni Jwalakwe pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(64,2,'64:2','Jwalakwe ni Ajula jwaŵan''gumbile ŵanganyammwe, basi ŵane mwa jenumanja makafili, ŵane mwa jenumanja soni ŵakulupilila, sano Allah ichindu yankutenda aŵele Jwakuilolechesya.');
INSERT INTO "silika" VALUES(64,3,'64:3','Agumbile mawunde ni mataka mwakuonaonape, ni ampaanjiile kaoneche kenu, basi ni akakolosyisye kaoneche kenuko, soni Kukwakwe pe ni kwakuujila.');
INSERT INTO "silika" VALUES(64,4,'64:4','Akumanyilila yaili ku mawunde ni petaka, ni akuimanyilila soni yankuisisa ni yankuijuwula, soni Allah ni Nkuimanya chenene yaili m’mitima.');
INSERT INTO "silika" VALUES(64,5,'64:5','Ana nganijinyyika ŵanganyammwe abali jaaŵala ŵaŵakanile kalakala? Basi ŵapasyisye makungu ga itendo yao (ya ukafili pa duniya pepano), ni akwete kupata soni ŵanganyao ilagasyo yakupoteka nnope (ku Akhera).');
INSERT INTO "silika" VALUES(64,6,'64:6','Yalakweyo niligongo lyanti Achimitenga ŵaliji nkwaichilila ni ilosyo yakuonechela, basi niŵatiji (pakuŵecheta mwachalu): “Ana ŵandu (mpela uwwewe) ni mpaka atujongole?” Basi ŵakanile ni kusyulula, nambo Allah nganaŵa pausauchi (wakulajila chikulupi chao), soni Allah ni Jwakupata kwejinji, Jwakulapilidwa kusyesyene.');
INSERT INTO "silika" VALUES(64,7,'64:7','Ŵandu ŵaakanile akulitwanjilaga yanti ngasaukulidwa (m’malembe). Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Elo! Ngwalumbila Ambuje ŵangu! Chisimu chene chinchiukulidwa, kaneka nikwisa kusalilidwa kwene yamwaliji nkutenda (ku duniya), soni yalakweyo kwa Allah iŵele yakwepepala kusyene.”');
INSERT INTO "silika" VALUES(64,8,'64:8','Basi munkulupililani Allah ni Ntenga Jwakwe صلى الله عليه وسلم kwisa soni Lilanguka lyatutulwisye (Qur’an). Sano Allah abali syosope sya yankutenda aŵele Jwaasimanya chenene.');
INSERT INTO "silika" VALUES(64,9,'64:9','(Kumbuchilani) lyuŵa lya tachinsonganganya pa Lisiku Lyansongano, lyele ni Lisiku lya kalamusyana (ŵandu ŵa ku Mbepo chakakalamusye ŵa ku Moto majumba gao gaakajile kwinjila akakulupilile). Sano jwachankulupilile Allah ni kutendaga yambone tansiimaachisye ilemwa yakwe, soni takan''jinjisye ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka. Kwele ni kupunda kwekulungwa nnope.');
INSERT INTO "silika" VALUES(64,10,'64:10','Nambo ŵandu ŵakufuulu ni kanila ma Ȃya Getu ŵanganyao niŵandu ŵa ku Moto, takaŵe ŵandamo mwalakwemo, kaje kusakala nnope malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(64,11,'64:11','Nganichipatikaneje chipato chilichose ikaŵeje mu lisosa lya Allah, sano jwachankulupilile Allah tan''jongolele ntima wakwe (ku chikulupi chanti: yaimpatileyo yaliji yelembedwe kala ni Allah kuti impate), sano Allah chindu chilichose aŵele Jwaachimanya chenene.');
INSERT INTO "silika" VALUES(64,12,'64:12','Soni mumpikanilani Allah ni mumpikanile soni Ntenga صلى الله عليه وسلم, nambo naga nkugalauka (kuleka yele), basi masengo ga Ntenga Jwetuصلى الله عليه وسلمni kulungusya (utenga) mwakupikanika.');
INSERT INTO "silika" VALUES(64,13,'64:13','Allah pangali nnungu jwine (jwakuŵajilwa kun''galagatila) ikaŵeje Jwelepejo, soni basi kwa Allah pe ajegamile ŵakulupilila.');
INSERT INTO "silika" VALUES(64,14,'64:14','E jenumanja ŵankulupilile! Chisimu ŵane mwa achiŵammakwenu ni ŵanache ŵenu ali amagongo ŵenu (ligongo akunnuyaluyaga mpaka kuleka ya Dini), basi mwakalamukani, nambo naga nkwakululuchila, ni kuipwanyila (yayapite) ni kuisiimaasya (kwene m’mitima mwenu Allah tasengwe), basi chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(64,15,'64:15','Chisimu ipanje yenu ni ŵanache ŵenu iŵele mayeso (gakun''dinga chikulupi chenu), sano Allah Kukwakwe nikwagali malipilo gamakulungwa nnope.');
INSERT INTO "silika" VALUES(64,16,'64:16','Basi mun''jogopani Allah mwachinkombolele, soni pikanilani (malamusi) ni nkunde (yankusalilidwa), soni tolani; chiiŵe yambone kukwenu mwachimisyene. Sano ŵaakuŵambasyidwa ku ubaili wa ntima wao, basi ŵanganyao ni ŵali ŵakupunda.');
INSERT INTO "silika" VALUES(64,17,'64:17','Naga nkunkongosya Allah ngongole jambone (pakutola ipanje yenu petala Lyakwe) tajijonjechesye kukwenu (malipilo gakwe) ni kunkululuchila (ilemwa yenu), sano Allah ni Jwakutogolela kusyene (pakulipila yejinji ku itendo yannono), Mpole nnope.');
INSERT INTO "silika" VALUES(64,18,'64:18','Jwakumanyilila yakusisika ni yakuonechela, Jwamachili gakupunda, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(65,1,'65:1','E mmwe Ntume صلى الله عليه وسلم! Pati nchalekaga achakongwe (ulombela), basi mwalekani pa eda jao (jajili ndema jakwembecheya panyuma pakulechedwa ulombela, jajikutandila ŵakongwe ali aswejele ku wesi nikwalekaga nkanimwakwaye), soni ŵalanjilani ya (masiku ga) eda. Ni mun''jogopeje Allah M''mbuje gwenu. Nkasimwakoposya m’majumba mwao, nombenao soni akasakopoka (achimisyenepe kwikanila ndema ja eda jili jimasile), ikaŵeje naga ali atesile chakusakala chakuonechela (mpela chigwagwa, apo chinkomboleche kwakoposya). Sano jele ni mipingu ja Allah, mwanti jwataguluche mipingu ja Allah nikuti pamasile paalitendele lupuso nsyene. Ngankumanyilila (chaaŵiichiile Allah jele mipinguji), mwine Allah kuŵa nkuchipatikanisya chindu chine panyuma payele (yakulekanayo chakwilanya chilikati chenu, nikuujilana eda nkanijimale).');
INSERT INTO "silika" VALUES(65,2,'65:2','Basi pati jiŵandichile ndema jao (jakumalila eda) mwaujilani mwaumbone, kapena lekanganani nao mwaumbone, soni soselani mboni siŵili syakulungamika syakutyochela mwa jenumanja (Asilamu).Ni mpelecheje umboni chenene ligongo lya Allah. Yalakweyo akuundidwa nayo mundu jwaaŵele ali nkunkulupilila Allah ni Lisiku Lyambesi. Sano mundu jwatan''jogope Allah tam''bichile litala lyakopochela (mu ipato).');
INSERT INTO "silika" VALUES(65,3,'65:3','Soni tampe lisiki m’matala gangaakuganichisya, soni jwatajegamile kwa Allah, basi Jwalakwe tankwanile (kuŵa nkunkamuchisya jwakwe), chisimu Allah ni Jwakwanilisya chisako Chakwe, Allah pamasile paaŵisile chipiimo cha chindu chilichose.');
INSERT INTO "silika" VALUES(65,4,'65:4','Sano aŵala ŵaakatile tama jakulwala wesi mwa achimmasyeto ŵenu naga nkukaichila (ya ndema jao ja eda), basi eda jao ni myesi jitatu, ni aŵala ŵanganatande kulwala wesi (eda jao iyyoyo soni). Sano aŵala ŵaakwete yiilu ndema jao (ja kumalila eda) ni muchachiŵelechela yiilu yaoyo. Sano mundu jwatan''jogope Allah tan''jepepachisye ichindu yakwe.');
INSERT INTO "silika" VALUES(65,5,'65:5','Lyele ni lilamusi lya Allah lyaalitulwisye kukwenu, sano jwatan''jogope Allah tansiimaachisye yakusakala yakwe ni kunkuchisya malipilo gakwe.');
INSERT INTO "silika" VALUES(65,6,'65:6','Mwatamikani (achimmasyeto ŵamwalesileo) mwankutama pemo, yankuti pakulipatila (namuno kuti mwalesile ulombela naga eda jao nkanijimale), soni nkasimwalagasya ŵanganyao kuti mwaaŵane (niaganisye yakutila achimisyene). Soni naaŵaga ŵana yiilu basi mwapani (yosope yakusosekwa pa umi) mpaka aŵeleche yiilu yaoyo. Sano naga ali an''jonjecheesye (ŵanache ŵenu), basi mwapani malipilo gao, soni tamani ntemela wakupikanganana (ya malipilogo) chilikati chenu mwaumbone. Nambo naga nkunonopesyana basi (mwanachejo) tiŵajonjechesye (babao ŵakongwe) ŵane.');
INSERT INTO "silika" VALUES(65,7,'65:7','Jwakupata atole mwakulandana ni kupata kwakwe, nambo jwaanaandiyisyiidwe lisiki lyakwe, basi atole (kachinandipamuno) mu yampele Allah, Allah jwangankanganika mundu (kutola) ikaŵeje yampeele, Allah taaŵiiche kwepepala panyuma pakunonopa.');
INSERT INTO "silika" VALUES(65,8,'65:8','Ana soni ŵalingwa-ŵalingwa mwa achimisyene misi ŵaŵanyosyisye lilamusi lya Ambuje ŵao kwisa soni Achimitenga Ŵakwe! Basi ni twaŵalasile chiŵalanjilo chaukali nnope, ni twapele ilagasyo yakunyalaya kusyene.');
INSERT INTO "silika" VALUES(65,9,'65:9','Basi ni ŵapasisye makungu ga itendo yao, soni kupelelwa ni jaliji mbesi ja itendo yao.');
INSERT INTO "silika" VALUES(65,10,'65:10','Allah ŵaalinganyichisye ŵanganyao ilagasyo yaukali nnope, basi mun''jogopani Allah, E jenumanja ŵankwete lunda! Aŵala ŵankulupilile! Pamasile paatulwisye Allah kukwenu Chikumbusyo.');
INSERT INTO "silika" VALUES(65,11,'65:11','Ntenga صلى الله عليه وسلم jwaakunsoomela ma Ȃya ga Allah gakusalichisya, kuti ŵakoposye aŵala ŵaakulupilile ni kutendaga yambone kutyochela m’chipi ni kwajinjisya mwilanguka. Sano jwatankulupilile Allah ni kutendaga yambone takan''jinjisye ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka, Allah pamasile pankolochesye jwalakwejo lisiki lyambone nnope.');
INSERT INTO "silika" VALUES(65,12,'65:12','Allah ni Ajula jwaagumbile mawunde nsano nagaŵili, nombe nalyo litaka yalumope (mataka nsano nagaŵili). Malamusi gakuŵa gali nkutuluka chilikati chakwe (cha mawunde ni mataka) kuti mmanyilile ŵanganyammwe yanti Allah pa chindu chilichose aŵele Jwakombola kusyene, ni yanti Allah amasile paachisyungwile chindu chilichose ni umanyilisi (waachimanya chenene).');
INSERT INTO "silika" VALUES(66,1,'66:1','E mmwe Ntume صلى الله عليه وسلم! Ligongo chichi nkuchitenda kuŵa haraam chindu chaachitesile Allah kuŵa halaal kukwenu? Nkusachilila kwasengwasya achiŵammakwenu (ni ligongo lyakwe ntesile yalakweyi)? Sano Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(66,2,'66:2','Allah pamasile pam''biichiile ŵanganyammwe lilamusi lya kagopole ka kulumbila kwenu, soni Allah ni Akun''gosa ŵenu, soni Jwalakwe ni Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(66,3,'66:3','Soni (kumbuchilani) katema kajwasunjisye ngani Ntume صلى الله عليه وسلم kwa ŵamo mwa achiŵammakwakwe (Hafswa رضي الله عنهم), basi ndema ja ŵaajijuwile (asilijo kwa Aisha رضي الله عنهم), Allah ni kummanyisya (Ntume صلى الله عليه وسلم) yalakweyo, (Ntume صلى الله عليه وسلم) jwatagulile (Hafswa رضي الله عنهم) upande wine wanganijo nambo upande wine jwaulesile, basi ndema jajwatagulile yalakweyo (Hafswa رضي الله عنهم) ŵatite: “Ŵani ŵantagulile yele?” (Ntume صلى الله عليه وسلم) jwatite: “Andagulile (Allah) Jwakumanyilila nnope, Jwaasimanya abali syosope.”');
INSERT INTO "silika" VALUES(66,4,'66:4','Naga ŵanaŵaŵilimmwe (Hafswa ni Aisha رضي الله عنهم) ntende toba kwa Allah (pakuleka kunnagasya Ntume صلى الله عليه وسلم ni wiwu basi tiiŵe yambone kukwenu, soni tendani chitema) ligongo mitima jenu jipombotele panandi (kusaka kunkanila Ntume صلى الله عليه وسلم yajukusaka), nambo naga nkamusyangane pa yakunnagasya Ntumejo r, basi Allah chisimu chene ni Nkunchengeta jwakwe, kwisa soni Jibulilu ni ŵali ŵambone mu (ŵandu) ŵakulupilila, ni Achimalaika konjechesya pele tankamuchisye.');
INSERT INTO "silika" VALUES(66,5,'66:5','Naga (Ntume صلى الله عليه وسلم) ali annesile jenumanja (ulombela), chisimu Ambujegwe mpaka ampe mmalo mwa jenumanja achiŵammakwakwe ŵane ŵambone nnope kumpunda ŵanganyammwe, Asilamu (ŵakulipeleka kwa Allah), ŵakulupilila, ŵakunda malamusi mwakulinandiya, ŵakutenda toba, ŵakutenda ibada nnope, ŵakutaŵataŵa (swaumu), mauli ni ŵali ŵakwe.');
INSERT INTO "silika" VALUES(66,6,'66:6','E jenumanja ŵankulupilile! Lichingani mwachimisyene kwisa soni maŵasa genu ku Moto wauli sasu syakwe ŵandu ni maganga, pa walakweo pana Achimalaika (ŵakuulolela) ŵakalalisya, ŵamachili, ŵangannyosya Allah pa yaŵalamwile, soni akasatenda (yosope) yaakulamulidwaga.');
INSERT INTO "silika" VALUES(66,7,'66:7','E jenumanja achimakafili! Ngasin''diŵalanga lelo, chisimu nkulipidwa pa yamwaliji nkupanganya.');
INSERT INTO "silika" VALUES(66,8,'66:8','E jenumanja ŵankulupilile! Galauchilaani kwa Allah pakutenda toba jisyesyene, chisimu M''mbuje gwenu tansiimaachisye yakusakala yenu, ni kuja kun''jinjisya ku Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo, Lisiku lyangan''jigasya Allah nchesela Ntume (Muhammadi صلى الله عليه وسلم) ni ŵandu ŵaŵakulupilile najo imo. Lilanguka lyao lichiŵaga nkwenda paujo pao ni kunkono wao wan''dyo, achiŵechetaga (kuti): “Ambuje ŵetu! Tukwanilichisyani lilanguka lyetu, soni ntukululuchile. Chisimu Mmwejo pa chindu chilichose m''bele Ŵakombola.”');
INSERT INTO "silika" VALUES(66,9,'66:9','E mmwe Ntume صلى الله عليه وسلم! Limbanani nao achimakafili ni achinakunaku, ni mwajumulile ntima, mauto gao ni ku Jahannama, kaje kusakala malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(66,10,'66:10','Allah apeleeche chilandanyo chaŵandu makafili pa ŵankwao Nuhu ni ŵankwao Lutwi, ŵanaŵaŵiliŵa ŵaliji pasi pa achikapolo ŵaŵili mwa achikapolo Ŵetu ŵambone, nambo ŵaatesile chinyengo (achiŵammakwaowo pakusisa ukafili mu ntima ni kamulana ni amagongo), basi (Achimitumeo) nganiŵakamuchisya (achiŵammakwaowo) chilichose mu (ipotesi) yakuumila kwa Allah, ni yaŵechetedwe (yanti): “Jinjilani ku Moto pamo ni akwinjila.”');
INSERT INTO "silika" VALUES(66,11,'66:11','Ni apeleeche soni Allah chilandanyo cha ŵandu ŵakulupilila pa ŵankwakwe Firiauna ndema jaŵatite: “Ambuje ŵangu! Mundaŵilani Kukwenu nyumba ku Mbepo, ni munjokole kwa Firiauna kwisa soni itendo yakwe, ni munjokole ku ŵandu ŵalupuso.”');
INSERT INTO "silika" VALUES(66,12,'66:12','Ni Maryam mwanache jwa Imrani ajula juŵaugosile utukani wakwe, ni twapepelele mwalakwemo (umi) kuumila kwa Nsimu Wetu (Jibulilu ni kupagwa Isa jwangali babagwe), ni jwajitichisye (Maryamujo) maloŵe ga M''mbujegwe ni itabu Yakwe, ni jwaliji soni mu ŵakunda malamusi mwakulinandiya. ');
INSERT INTO "silika" VALUES(67,1,'67:1','Anyakwiche (pa uchimbichimbi ni upile) Ajula (Allah) jwauli mu Nkono Mwakwe uchimwene, soni Jwalakwe pa chindu chilichose aŵele Jwakombola.');
INSERT INTO "silika" VALUES(67,2,'67:2','Ajula jwaagumbile chiwa ni umi kuti an''dinje (yanti) ana ŵani mwa jenumanja ŵali ŵakolosya kusyene masengo. Soni Jwalakwe ni Jwamachili gakupunda, Jwakululuka kusyene.');
INSERT INTO "silika" VALUES(67,3,'67:3','Ajula jwaagumbile mawunde nsano nagaŵili mwakusajikanya (line pachanya pa lijakwe, natamuno nnole kwinaniko) ngankuona kutindana kulikose mu kagumbe ka (Allah) Jwaukoto wejinji, basi wilisyani kulola, ana nkujiona midala jilijose?');
INSERT INTO "silika" VALUES(67,4,'67:4','Soni wilisyani kulola kaŵilikose, chikum''bujile mwasyene kulolako (meso gali) nkuchenga soni galakwe gali pambesi pakupela (nambo so! Nganim''ba nchiweni chilema chilichose).');
INSERT INTO "silika" VALUES(67,5,'67:5','Soni chisimu chene pamasile patulisalalisye liwunde lya duniya ni nyali (ndondwa), ni tusitesile kuŵa (mwakuumila yenje) yagasomela mashetani. Soni twalinganyichisye ŵangayao ilagasyo ya Moto Wakulapuka.');
INSERT INTO "silika" VALUES(67,6,'67:6','Soni akwete kupata ŵandu ŵankanile M''mbuje gwao ilagasyo ya Jahannama, kaje kusakala nnope malo gakuujila gakwe!');
INSERT INTO "silika" VALUES(67,7,'67:7','Ndema jatachiponyedwa mwalakwemo tachikupikana kulilima kwakwe soni aku uli nkutokota kusyene.');
INSERT INTO "silika" VALUES(67,8,'67:8','Ukusigalaga panandi kuti ukatichekatiche ligongo lya ukali. Ndema jilijose jalikaponyedweje mwalakwemo likuga (lya achimakafili), chaakaawusyeje akuwusunga (Motowo ŵali Achimalaika kuti): “Ana nganam’bichilila nkutetela?”');
INSERT INTO "silika" VALUES(67,9,'67:9','Takajanjeje (kuti): “Elo, chisimu jwatuichilile nkutetela, nambo twakaaniile, ni twatite: ‘Allah nganatumisya chilichose, nganim''batu jenumanja ikaŵeje mu kupotela kwekulungwa.”');
INSERT INTO "silika" VALUES(67,10,'67:10','Soni takajileje: “Tungaŵe twapikanile (yaŵatusalilaga ila) kapena kuiganichisya mwalunda, nga nganituŵa mu ŵandu ŵa ku Moto Wakulapuka.”');
INSERT INTO "silika" VALUES(67,11,'67:11','Basi takajitichisye ileŵi yao (nambo kwitichisyako ngakuja kwakamuchisya), mwanti kutalichila nikwao ŵandu ŵa ku Moto Wakulapuka (kutyochela ku chanasa cha Allah)!');
INSERT INTO "silika" VALUES(67,12,'67:12','Chisimu aŵala ŵaakun’jogopa M’mbuje gwao akakum’bona, akwete kupata chikululuko ni malipilo gekulungwa.');
INSERT INTO "silika" VALUES(67,13,'67:13','Soni (kanga) chin''gasise maloŵe genu (gakun''gaamba Muhammadi صلى الله عليه وسلم) kapena chin''galochesye papaswela (yalumope), chisimu Jwalakwe (Allah) ni Nkumanyilila yayili m’mitima.');
INSERT INTO "silika" VALUES(67,14,'67:14','Ana jukamanyilila Jwagumbile? Kutendaga Jwalakwe ni Jwakumanyilila yakunyilila nnope, Jwaasimanya abali syosope!');
INSERT INTO "silika" VALUES(67,15,'67:15','Jwalakwe ni Ajula jwam''biichiile litaka kuŵa lyakutumika (pa chilichose chankusaka, mpela kwendajenda, kulima ni kutaŵapo yakutaŵataŵa), basi jendani mbali ni mbali syetakalyo soni lyagani mu lisiki Lyakwe (Allah), soni Kukwakwe pe ni kwakuujila (kwenu pa Kiyama).');
INSERT INTO "silika" VALUES(67,16,'67:16','Ana nkuiona kuti n''di pantunjelele mwanti (Allah) Jwaali kwinani ngaŵa nkuntitimisya nalyo litaka, basi nikuŵa lyalakwelyo lina nkutenganyika (pakuwowochelwa)?');
INSERT INTO "silika" VALUES(67,17,'67:17','Kapena nkuiona kuti n''di pantunjelele mwanti (Allah) Jwaali kwinani ngaŵa nkuntumichisya chimbunga chasakalawe (nikun''jonanga nasyo sakalawesyo)? Basi ni tinchimanyilila mwachikasaŵelaga chitetelo Changu.');
INSERT INTO "silika" VALUES(67,18,'67:18','Soni yakuonaonape pamasile paŵaakaaniile aŵala ŵapaujo pao, nambi kwaliji uli kasilika Kwangu (ngati najonasile wosope)?');
INSERT INTO "silika" VALUES(67,19,'67:19','Ana ngaakuiona ijuni pachanya pao mwaikusapangulila mapapiko ni kugapinda? Pangali jwaakuijangata ikaŵeje (Allah) Jwaukoto wejinji, chisimu Jwalakwe pa chindu chilichose aŵele Jwakulolechesya.');
INSERT INTO "silika" VALUES(67,20,'67:20','Kapena niŵani aŵala ŵali asilikali ŵenu ŵanti ni kunkulupusya pangaŵaga (Allah) Jwaukoto wejinji? Nganaŵatu achimakafili ikaŵeje ali mu kunyenjeka basi.');
INSERT INTO "silika" VALUES(67,21,'67:21','Kapena niŵani aŵala ŵampaka ampe lisiki naga Jwalakwe (Allah) ali aumbete lisiki Lyakwe? Nambo ŵanganyao akwendelechela kulikwesya ni kuitila (yakuona).');
INSERT INTO "silika" VALUES(67,22,'67:22','Ana mundu jwaakwenda chiinamile (akakulola m''bujo jwanti komboleka kugwa) chamakuku ni jwali jwakongoka, kapena mundu jwaakwenda chongochele petala lyagoloka?');
INSERT INTO "silika" VALUES(67,23,'67:23','Jilani: “Jwalakwe ni Ajula jwaŵan''gumbile (nkakupatikana kalakose), ni ŵam''biichiile kupikana, kulola ni mitima (jaganichisya). Nambo kutogolela kwenu kuŵele kwannonope.”');
INSERT INTO "silika" VALUES(67,24,'67:24','Jilani: “Jwalakwe ni Ajula jwan''jenesye petaka, soni Kukwakwe ni kutinchisonganganyisidwa.”');
INSERT INTO "silika" VALUES(67,25,'67:25','(Achimakafili) niakutiji: “Ana nichakachi (chatichikwanilana) chele chilanga (cha Kiyamachi) nam''baga jenumanja nkuŵecheta yakuona?”');
INSERT INTO "silika" VALUES(67,26,'67:26','Jilani: “Umanyilisi (wa yele) uli kwa Allah pe, sano une ngambile kuŵa nkutetela jwakusalichisya.”');
INSERT INTO "silika" VALUES(67,27,'67:27','Nambo ndema jatakachione (chilanga cha Kiyamacho) chili dusi (ni ŵanganyao), ngope sya aŵala ŵaŵakanile sichipiilila ni kalaluka (ligongo lya madandausi). Ni kuchiŵechetedwa (mwa kwachindimbula kuti): “Ayi niayilayila yamwaliji nkuiŵilasya (kuti iyiche, sano ni iyiiche).”');
INSERT INTO "silika" VALUES(67,28,'67:28','Jilani: “Ana nkuiona uli, naga Allah ali anjonasile une pamo ni aŵala ŵandinao (mwankusachila ŵanganyammwe), kapena kututendela chanasa, ana ni ŵani ŵampaka agajokole makafili ku ilagasyo yakupoteka nnope?”');
INSERT INTO "silika" VALUES(67,29,'67:29','Jilani: “Jwalakwe ni (Allah) Jwaukoto wejinji, tunkulupilile Jwele, soni kwa Jwalakwe pe ni kutujeegamiile. Basi tinchimanyilila (panyuma pakwika ilagasyo kuti) nduni jwali m’kupotela kwakuonechela.”');
INSERT INTO "silika" VALUES(67,30,'67:30','Jilani: “Ana nkuiona uli, naga mesi genu (gosope) gali gajumilile, ana ni ŵani ŵampaka ayiche nago kukwenu mesi gatutumule?”');
INSERT INTO "silika" VALUES(68,1,'68:1','Nûn. Nguchilumbila chakulembela (chaakuleembelaga Achimalaika ni ŵane wosope) kwisa soni yaakulembaga.');
INSERT INTO "silika" VALUES(68,2,'68:2','Mmwe (Muhammadi صلى الله عليه وسلم) nganim''ba ŵamasoka pachindimba cha Ambuje ŵenu (champele, wauli utume).');
INSERT INTO "silika" VALUES(68,3,'68:3','Soni chisimu nkwete kupata mmwejo malipilo gangamala (ligongo lya kulagasika kwenu pakwenesya utenga).');
INSERT INTO "silika" VALUES(68,4,'68:4','Soni chisimu mmwe n''di pachanya pandamo jambone nnope.');
INSERT INTO "silika" VALUES(68,5,'68:5','Basi, sampano tinyyione, nombe nao soni taione.');
INSERT INTO "silika" VALUES(68,6,'68:6','Yanti ana ŵani mwa jenumanja ŵagaapatile masoka? (Mmwejo kapena ŵanganyao)?');
INSERT INTO "silika" VALUES(68,7,'68:7','Chisimu Ambuje ŵenu ni ŵaakummanyilila chenene mundu jwaapoteele kuleka litala Lyakwe, soni Ŵalakweo ni ŵaakwamanyilila chenene ŵali ŵakongoka.');
INSERT INTO "silika" VALUES(68,8,'68:8','Basi ngasimwapikanila ŵakanila.');
INSERT INTO "silika" VALUES(68,9,'68:9','Akasasachililaga n''di mwaakundilile (pa yakusakala), basi nombe nao ni ankundilile (pa yambone).');
INSERT INTO "silika" VALUES(68,10,'68:10','Soni ngasimumpikanila jwalijose jwakulumbila-lumbila soni jwakwaluka.');
INSERT INTO "silika" VALUES(68,11,'68:11','Nkusogoda, jwakwendajenda kupita nchitenda unakunaku.');
INSERT INTO "silika" VALUES(68,12,'68:12','Jwakusiŵilila yambone, jwakusumba mpika, jwasambi syejinji.');
INSERT INTO "silika" VALUES(68,13,'68:13','Jwakumula ntima soni panyuma pa yele jwakulipakasya utundu wa ŵane.');
INSERT INTO "silika" VALUES(68,14,'68:14','(Akulikwesya) ligongo lyakuŵa jwana chipanje ni ŵanache.');
INSERT INTO "silika" VALUES(68,15,'68:15','Pati gachisoomedwaga kukwakwe ma Ȃya Getu jukasatiji: “Asi ni adisiadisi sya ŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(68,16,'68:16','Chiitum''biika chimanyilo palupula.');
INSERT INTO "silika" VALUES(68,17,'68:17','Chisimu Uwwe twalinjile (achimakafili ŵachikuraishio pakwapa chindimba ni kuchikana) yatwatite pakwalinga achinsyene n''gunda, ndema jaŵalumbile kuti takaugungule (isogosi yakwe) kumasikusiku (kuti ŵakulaga akaaona atakwika muŵaŵendile).');
INSERT INTO "silika" VALUES(68,18,'68:18','Ni nganaŵecheta maloŵe ganti “Nasakaga Allah.”');
INSERT INTO "silika" VALUES(68,19,'68:19','Nilyaawusyungwile (n''gundao) likungu lyakuumila kwa Ambuje ŵenu kutendaga ŵanganyao ali nnugono.');
INSERT INTO "silika" VALUES(68,20,'68:20','Basi niwachelelwe uli nti chisawu wegungule.');
INSERT INTO "silika" VALUES(68,21,'68:21','Basi ni ŵaŵilangene kumasikusiku.');
INSERT INTO "silika" VALUES(68,22,'68:22','“Laŵililani kun''gunda wenu naga nkusaka gungula.”');
INSERT INTO "silika" VALUES(68,23,'68:23','Basi ni ŵaŵigwile aku achisongonelanaga.');
INSERT INTO "silika" VALUES(68,24,'68:24','“Akasan''jinjilila ata panandi kwalakweko lelo masikini jwalijose.”');
INSERT INTO "silika" VALUES(68,25,'68:25','Ni ŵalaŵilile mwachisindo achiganichisyaga kuti apakombwele (kwasiŵilila masikinio).');
INSERT INTO "silika" VALUES(68,26,'68:26','Basi ndema jaŵauweni (n''gundao) ŵatite: “Chisimu uwe tusokonechele.”');
INSERT INTO "silika" VALUES(68,27,'68:27','(Niŵatite soni: “Ngwamba, niwwuwu) nambo kuti uwwe tujiimiidwe.”');
INSERT INTO "silika" VALUES(68,28,'68:28','Jwakulungamika chilikati chao jwatite: “Ana nganinansalila yanti: ‘Ana uli ngankunswejesya (Allah pakuŵecheta maloŵe ganti: Naga Allah akusaka chitukagungule)?”');
INSERT INTO "silika" VALUES(68,29,'68:29','Ŵanganyao ŵatite: “Aswejeele Ambuje ŵetu! Chisimu uwwe twaliji ŵakulitenda lupuso (pangankumbuchila Allah).”');
INSERT INTO "silika" VALUES(68,30,'68:30','Basi ni ŵalolegene aku achidandaulanaga (pakupulisyana yaagajima masikini).');
INSERT INTO "silika" VALUES(68,31,'68:31','Ŵanganyao ŵatite: “Kutapana kwetuwe! Chisimu uwwe twaliji ŵakusumba mpika (paganisya yagajima masikini).”');
INSERT INTO "silika" VALUES(68,32,'68:32','“Mwine Ambuje ŵetu kuŵa nkututindanyichisya ni (n''gunda) wambone nnope kupunda walakweu, chisimu uwwe kwa Ambuje ŵetu tukusachilila (chikululuko Chakwe).”');
INSERT INTO "silika" VALUES(68,33,'68:33','Mwele ni mwaikasaŵelaga ilagasyo (ya pa duniya), nambo ilagasyo ya ku Akhera ni yaikulungwa nnope ingaŵe ŵamanyililaga.');
INSERT INTO "silika" VALUES(68,34,'68:34','Chisimu ŵakun''jogopa Allah akwete kupata kwa Ambuje ŵao Matimbe Gachindimba (Mbepo).');
INSERT INTO "silika" VALUES(68,35,'68:35','Ana twatende Asilamu nti chisawu akuleeŵa (pakwalipila mwakulandana)?');
INSERT INTO "silika" VALUES(68,36,'68:36','Chipali chichi kukwenu! Ana nkwiilanya chinauli?');
INSERT INTO "silika" VALUES(68,37,'68:37','Kapena nkwete chitabu chankusoomaga nkati mwakwe.');
INSERT INTO "silika" VALUES(68,38,'68:38','Yanti chisimu nkwete kupata mu lyele (lisiku lya Kiyamalyo) yankuisaka?');
INSERT INTO "silika" VALUES(68,39,'68:39','Kapena nkwete ŵanganyammwe ilanga Kukwetu yakuja kwika mpaka lisiku lya Kiyama, yakuti chisimu ŵanganyammwe chinchipata yankulijiilanyichisya?');
INSERT INTO "silika" VALUES(68,40,'68:40','Mwawusyani kuti ŵani mwa ŵanganyao ŵali nnongola jwatachajimila pa yalakweyi?');
INSERT INTO "silika" VALUES(68,41,'68:41','Kapena akwete ŵanganyao ŵakamusyangana nao? Basi ayiche naotu ŵakamusyangana naowo naga akuŵecheta yakuona.');
INSERT INTO "silika" VALUES(68,42,'68:42','(Kumbuchilani) lisiku lya luchiunukulidwa Lusonjo (lwa Allah) ni tachiŵilanjidwa (achinakunaku) kuti atende sijida (kunsujudila Allah) nambo ngasakombola.');
INSERT INTO "silika" VALUES(68,43,'68:43','Meso gao gachiteteŵala, ni kuchaunichila kunyosyeka. Nambo posepo ŵaliji nkuŵilanjidwa kuti asujudu (kuswali) ali ŵambonembonepe (ku duniya).');
INSERT INTO "silika" VALUES(68,44,'68:44','Basi munekani ni akukanila aji abaliji, chitwajigale mbolembole (wa ku ipotesi) kupitila m’matala gangaakugamanyilila.');
INSERT INTO "silika" VALUES(68,45,'68:45','Soni ngamba kwalechelesya ŵanganyao, chisimu mpango Wangu uŵele wakulimbangana.');
INSERT INTO "silika" VALUES(68,46,'68:46','Kapena nkwajuga malipilo (pakulungusya utenga), mwanti ŵanganyao ni akusitopelwa nasyo ngongole?');
INSERT INTO "silika" VALUES(68,47,'68:47','Kapena akwete (chitabu) chakusisika, mwanti ŵanganyao niakulemba?');
INSERT INTO "silika" VALUES(68,48,'68:48','Basi pilililani ku ulamusi wa Ambuje ŵenu (wakwalechelesya achimakafili), mwanti ngasim''ba mpela nsyene somba (Yunusu pakwanguya kwakasilichila ŵandu ŵakwe), ndema jajwaŵilasile (Ambujegwe) juli jwakutaŵika ni madandausi.');
INSERT INTO "silika" VALUES(68,49,'68:49','Ukaŵa kunsimana ukoto wakuumila kwa M’mbujegwe jukaponyedwe pantetete juli jwanchesela.');
INSERT INTO "silika" VALUES(68,50,'68:50','Basi Ambujegwe ŵansagwile ni kuntenda kuŵa jumpepe mwa ŵandu ŵambone.');
INSERT INTO "silika" VALUES(68,51,'68:51','Soni chisimu aŵala ŵakufuulu akusigalaga panandi kuti antelesyasye ni meso gao (ligongo lya kunchima) ndema jaakuchipikana chikumbusyo (Qur’an), ni akutiji: “Chisimu jwalakwe (Muhammadi صلى الله عليه وسلم) jwamasoka.”');
INSERT INTO "silika" VALUES(68,52,'68:52','Ni nganijiŵa jalakwe (Qur’aniji) ikaŵeje chikumbusyo ku iumbe yosope.');
INSERT INTO "silika" VALUES(69,1,'69:1','Chakusimichika (kwika kwakwe)!');
INSERT INTO "silika" VALUES(69,2,'69:2','Ana chakusimichika (kwika kwakwe) nichichi?');
INSERT INTO "silika" VALUES(69,3,'69:3','Ana ni chichi champaka chimmanyisye ya chakusimichika (kwika kwakwe)? (Jeletu ni Kiyama).');
INSERT INTO "silika" VALUES(69,4,'69:4','Ŵanya Asamuda ni Adi ŵakaaniile ya chagunda ntima (Kiyama).');
INSERT INTO "silika" VALUES(69,5,'69:5','Mwanti ŵanya Asamuda ŵajonanjidwe ni n''gumilo wakupelenganya nnope.');
INSERT INTO "silika" VALUES(69,6,'69:6','Sano ŵanya Adi ni ŵajonanjidwe ni mbungo jakusisima nnope soni jamachili gakupelenganya.');
INSERT INTO "silika" VALUES(69,7,'69:7','(Allah) ŵajitumisye kukwao yilo nsano naiŵili ni muusi nsano nautatu mwakulundanya, mwanti nkaweni ŵandu mwalakwemo ali jajaja! Nti chisawu mapata ga itela yatende gangali chachipali nkati mwakwe.');
INSERT INTO "silika" VALUES(69,8,'69:8','Nambi kwali nkum''bona mwa ŵanganyao jwalijose jwasigele?');
INSERT INTO "silika" VALUES(69,9,'69:9','Nombe Afiriauna kwisa soni ŵandu ŵa paujo pakwe ni achinsyene misi jajapitikusyidwe (ŵandu ŵa Lutwi) ŵayiche ni ilemwa.');
INSERT INTO "silika" VALUES(69,10,'69:10','Sano (ŵaliose mwa ŵanganyao) ŵannyosyisye Ntenga jwa M’mbuje gwao, basi (Allah) ni jwaakwembekenye ni ilagasyo yakupelenganya nnope.');
INSERT INTO "silika" VALUES(69,11,'69:11','Chisimu Uwwe ndema jagaagumbalile mesi (pachigumula cha Nuhu), twankwesisye ŵanganyammwe m’chombo chakwenda (mwakujajaŵala).');
INSERT INTO "silika" VALUES(69,12,'69:12','Kuti tuchitende kuŵa chikumbusyo kukwenu, ni kuti lijisunje (jele abaliji) liwiwi lyakupikanichisya.');
INSERT INTO "silika" VALUES(69,13,'69:13','Basi pandema jalichigombedwa lipenga n''gombo umpepe.');
INSERT INTO "silika" VALUES(69,14,'69:14','Nombe litaka ni matumbi ni kutyosyedwa m’mangwakwe kaneka ni kusyajidwa nsyago umo.');
INSERT INTO "silika" VALUES(69,15,'69:15','Basi lyele lisikulyo chichipatikana chakupatikana (Kiyama).');
INSERT INTO "silika" VALUES(69,16,'69:16','Ni lichipapuka liwunde, mwanti lyalakwe palyele lisikulyo lichiŵa nyangwinyangwi (ngalimba).');
INSERT INTO "silika" VALUES(69,17,'69:17','Ni Achimalaika tachiŵa ali mungulugulu mwakwemo, soni pachanya pao lyele lisikulyo (Achimalaika ŵakwana) nsano naŵatatu, tachitwichila Arishi ja Ambuje ŵenu (Chindanda Chaulungu).');
INSERT INTO "silika" VALUES(69,18,'69:18','Lyele lisikulyo tachiika namwe (ku magambo), pangali chilichose chankasasisaga jenumanja chachichisisika.');
INSERT INTO "silika" VALUES(69,19,'69:19','Sano mundu jwatachipedwa chitabu chakwe chakunkono wakwe wan''dyo, basi tachiti: “Eni! Soomani chitabu changu!”');
INSERT INTO "silika" VALUES(69,20,'69:20','“Chisimu une (ku duniya) nasimichisyaga yanti tinjisimangana nacho chiŵalanjilo changu.”');
INSERT INTO "silika" VALUES(69,21,'69:21','Basi jwalakwe tachiŵa mu ndamo jakusengwasya.');
INSERT INTO "silika" VALUES(69,22,'69:22','Mu Mbepo Japenani.');
INSERT INTO "silika" VALUES(69,23,'69:23','Isogosi yakwe yakuŵandichila (yangasausya kaŵa).');
INSERT INTO "silika" VALUES(69,24,'69:24','(Tachasalila kuti): “Lyagani soni mwagani mwakusengwa ligongo lya ayila imwalongwesye m’moŵa gagaapite (kala ku duniya).”');
INSERT INTO "silika" VALUES(69,25,'69:25','Nambo kwa mundu jwatachipedwa chitabu chakwe cha kunkono wakwe wanchiji, basi tachiti (pakudandaula): “Nkongwene! Nabola nga nganimbedwa chitabu changu!”');
INSERT INTO "silika" VALUES(69,26,'69:26','“Ningaatende ngachimanya chiŵalanjilo changu!”');
INSERT INTO "silika" VALUES(69,27,'69:27','“Ha! Nabola chiwa chila chikaŵe chipelela (changu ni ngaujila soni ku umi)!”');
INSERT INTO "silika" VALUES(69,28,'69:28','“Nganichingamuchisya chipanje changu.”');
INSERT INTO "silika" VALUES(69,29,'69:29','“Gaanjonasichile machili gangu (gakulichenjelela).”');
INSERT INTO "silika" VALUES(69,30,'69:30','(Kuchiŵechetedwa kuti): “Munkamulani soni muntaŵe makongwa.”');
INSERT INTO "silika" VALUES(69,31,'69:31','“Kaneka ni mumponye ku Moto Waukali.”');
INSERT INTO "silika" VALUES(69,32,'69:32','“Kaneko muntaŵikanye m’malundelunde (ga unyolo), ulewu wakwe makono makumi nsano nagaŵili (70).”');
INSERT INTO "silika" VALUES(69,33,'69:33','Chisimu jwalakwe jwaliji jukaakunkulupilila Allah Jwakuchimbichika nnope.');
INSERT INTO "silika" VALUES(69,34,'69:34','Soni nganaŵa jwakuchisya yagaliisya masikini.');
INSERT INTO "silika" VALUES(69,35,'69:35','Basi nganakola jwalakwejo lelo kwekuno nsyoŵe (jwakunkamuchisya).');
INSERT INTO "silika" VALUES(69,36,'69:36','Namuno yakulya ikaŵeje uwowu (wakusulula m’maŵanga ga ŵandu ŵa ku Moto).');
INSERT INTO "silika" VALUES(69,37,'69:37','Pangali ŵatachiulya ikaŵeje ŵakulemwa.');
INSERT INTO "silika" VALUES(69,38,'69:38','Basi nguilumbila yankuiwona.');
INSERT INTO "silika" VALUES(69,39,'69:39','Ni yangankuiwona.');
INSERT INTO "silika" VALUES(69,40,'69:40','Chisimu jalakwe (Qur''aniji) liŵele liloŵe lya Ntenga Jwakuchimbichika (Jibulilu).');
INSERT INTO "silika" VALUES(69,41,'69:41','Ni nganijiŵa jalakwejo liloŵe lya nkwimba chilaanga, yannonope ni yankuikulupilila.');
INSERT INTO "silika" VALUES(69,42,'69:42','Soni ngaŵa liloŵe lya abimbi, yannonope ni yankukumbuchila.');
INSERT INTO "silika" VALUES(69,43,'69:43','(Jiŵele) jakutulusyidwa kuumila kwa M''mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(69,44,'69:44','Soni ingaŵe kuti (Muhammadi صلى الله عليه وسلم) jwalukenye Kukwetu unami winewakwe.');
INSERT INTO "silika" VALUES(69,45,'69:45','Chisimu tukankwembekenye mwamachili.');
INSERT INTO "silika" VALUES(69,46,'69:46','Kaneka nitukakatile kwene mwa jwalakwejo ntasi (wakwe waumi nikuwa).');
INSERT INTO "silika" VALUES(69,47,'69:47','Mwanti nga nganapatikana jwalijose mwa jenumanja jwakun''gosa (ku ipotesi Yetu).');
INSERT INTO "silika" VALUES(69,48,'69:48','Soni chisimu jalakwe (Qur’ani) jiŵele chikumbusyo kwa ŵakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(69,49,'69:49','Sonitu Uwwe tukumanyilila kusyene yanti mwa ŵanganyammwe mwana ŵakanila.');
INSERT INTO "silika" VALUES(69,50,'69:50','Soni chisimu jalakwe (Qur’ani) jichiŵa jakwadandaulikasya kusyene achimakafili (ku Kiyama ndema jatachiuwona ntendele wa ŵajikuya ni ipotesi ya ŵajikana).');
INSERT INTO "silika" VALUES(69,51,'69:51','Soni chisimu jalakweji jiŵele jakuona kusyesyene.');
INSERT INTO "silika" VALUES(69,52,'69:52','Basi liswejesyani lina lya Ambuje ŵenu Ŵakuchimbichika nnope.');
INSERT INTO "silika" VALUES(70,1,'70:1','Jwawusisye nkuwusya (mwachipongwe) yailagasyo yakupatikana (kuti: Tiyipatikane chakachi?).');
INSERT INTO "silika" VALUES(70,2,'70:2','Kwa achimakafili pangali jwampaka ayisiŵilile.');
INSERT INTO "silika" VALUES(70,3,'70:3','Yakuumila kwa Allah Nsyene Makwelelo.');
INSERT INTO "silika" VALUES(70,4,'70:4','Akasakwelaga Kukwakwe Achimalaika ni Jibulilu jwakwe. (Tiyichipatikana ilagasyoyo) mu lisiku (lya Kiyama) lyalili chipiimo chaulewu wakwe yaka yakwana masausande makumi nsano (50,000).');
INSERT INTO "silika" VALUES(70,5,'70:5','Basi pililani kupilila kwambone.');
INSERT INTO "silika" VALUES(70,6,'70:6','Chisimu ŵanganyao akuiwona (ilagasyoyo) kuti ili kwanaula (yangaŵa nkupatikana).');
INSERT INTO "silika" VALUES(70,7,'70:7','Nambo Uwwe tukuiwona kuti ili dusi (kupatikana).');
INSERT INTO "silika" VALUES(70,8,'70:8','Lisiku lya lichiŵa liwunde chisawu ntofu wenyelenyenduche.');
INSERT INTO "silika" VALUES(70,9,'70:9','Nombe matumbi gachiŵa chisawu litonji (pa kaululuche kakwe).');
INSERT INTO "silika" VALUES(70,10,'70:10','Soni nsyoŵe ngasaja kuwusya ya nsyoŵe jwakwe.');
INSERT INTO "silika" VALUES(70,11,'70:11','Namuno kuti tachipedwa lipesa lyakuonegana (mwanti mundu nikwaona babagwe, ŵanagwe ni ŵaulongo, nambo ngasakombola kwaŵechetekasya namuno kwaŵenda chikamuchisyo), jwakuleŵa tachilajila ali aliombwele ku ilagasyo ya lyele lisikulyo pakutola ŵanagwe.');
INSERT INTO "silika" VALUES(70,12,'70:12','Kwisa soni ŵankwakwe ni nchimwenegwe.');
INSERT INTO "silika" VALUES(70,13,'70:13','Ni ŵalukosyo lwakwe ŵaŵaliji nkunsunga.');
INSERT INTO "silika" VALUES(70,14,'70:14','Ni yosope yayili pachilambo (kupeleka kwa Allah), kut in''jokole.');
INSERT INTO "silika" VALUES(70,15,'70:15','Nambo kwapi! (Nganikuŵa kun''jokwele kuliwombola kwakweko), chisimu walakweo ni Moto Wakolela (mwamachili).');
INSERT INTO "silika" VALUES(70,16,'70:16','Wakusupula mwanakamo lipende lyamuntwe.');
INSERT INTO "silika" VALUES(70,17,'70:17','Uchiŵa uli nkum''bilanga mundu jwa ŵachitundumalile chikulupi ni kunyosya.');
INSERT INTO "silika" VALUES(70,18,'70:18','Ni ŵasonganganyisye (chipanje) ni kusalasya (mwakusalasya ata Zaka ngatola).');
INSERT INTO "silika" VALUES(70,19,'70:19','Chisimu mundu agumbidwe kuŵa jwakupapamba kusyene (jwangapilila).');
INSERT INTO "silika" VALUES(70,20,'70:20','Pati impatile yakusakala akasaŵa jasajasa.');
INSERT INTO "silika" VALUES(70,21,'70:21','Nambo pati impatile yambone akasaŵa jwaugaga.');
INSERT INTO "silika" VALUES(70,22,'70:22','Ikaŵeje ŵakuswali.');
INSERT INTO "silika" VALUES(70,23,'70:23','Aŵala ŵanya ŵaakuŵa pa swala syao ali nkwendelechela (kusiswaliswali).');
INSERT INTO "silika" VALUES(70,24,'70:24','Ni aŵala ŵamuli nchipanje chao mwana liunjili lyachimanyichile.');
INSERT INTO "silika" VALUES(70,25,'70:25','Lyakumpa nkuŵenda ni nkwimidwa jwakwe (ligongo lyakulisiŵilila kuŵenda).');
INSERT INTO "silika" VALUES(70,26,'70:26','Ni aŵala ŵaakwitichisya ya Lisiku Lyamalipilo.');
INSERT INTO "silika" VALUES(70,27,'70:27','Ni aŵala ŵanya ŵaakuŵa nkuijogopa ilagasyo ya M’mbuje gwao.');
INSERT INTO "silika" VALUES(70,28,'70:28','Chisimu ilagasyo ya M’mbuje gwao nganiyiŵa yati kulupuka nayo (mundu jwakusakala).');
INSERT INTO "silika" VALUES(70,29,'70:29','Ni aŵala ŵanya ŵaakuŵa ali nkuugosa utukani wao (pangatenda yansese). ');
INSERT INTO "silika" VALUES(70,30,'70:30','Ikaŵeje kwa achiŵammakwao kapena (mateeka) gagapatile magasa gao gakun''dyo; basi chisimu ŵanganyao (pa yalakweyo) nganaŵa agambidwe.');
INSERT INTO "silika" VALUES(70,31,'70:31','Nambo ŵatasache yangaŵaga yele, basi ŵanganyao niŵali ŵakusumba mpika.');
INSERT INTO "silika" VALUES(70,32,'70:32','Ni aŵala ŵanya ŵaakuŵa ali nkusunga chenene amana syao kwisa soni ilanga yao.');
INSERT INTO "silika" VALUES(70,33,'70:33','Ni aŵala ŵanya ŵaakuŵa ali nkwimilichika pakupeleka umboni wao.');
INSERT INTO "silika" VALUES(70,34,'70:34','Ni aŵala ŵanya ŵaakuŵa ali nkwendelechela kusigosa swala syao (pakusiswaliswali).');
INSERT INTO "silika" VALUES(70,35,'70:35','Ŵanganyao tachiŵa m’Matimbe ga ku Mbepo ali nkupedwa uchimbichimbi.');
INSERT INTO "silika" VALUES(70,36,'70:36','Ana chipali chichi kwa aŵala ŵaakufuulu chaakukangamalila kukwenu (mmwe Muhammadi صلى الله عليه وسلم ali atunjile meso)?');
INSERT INTO "silika" VALUES(70,37,'70:37','(Ni kutama) nganyanganya chakun''dyo ni kunchiji (kwenu nambo ningakuuya yankwasalila)!');
INSERT INTO "silika" VALUES(70,38,'70:38','Ana jwalijose mwa jamanjao akukolaga tama janti takajinjisyidwe ku Litimbe Lyachindimba (ku Mbepo)?');
INSERT INTO "silika" VALUES(70,39,'70:39','Ngwamba! Chisimu Uwwe twagumbile ŵanganyao kuumila mu yaakuimanyilila (gagali mesi).');
INSERT INTO "silika" VALUES(70,40,'70:40','Basi ngunnumbila M''mbuje jwa kungopoko ni kungapililo kosope (kwa lyuŵa, mwesi ni ndondwa), kuti chisimu Uwwe tuŵele Ŵakombola.');
INSERT INTO "silika" VALUES(70,41,'70:41','Kuti twagalausye (nikwikanao ŵane) ŵambone kwapunda ŵanganyao, sonitu Uwwe nganituŵa ŵanti nikupundidwa (ni chilichose).');
INSERT INTO "silika" VALUES(70,42,'70:42','Basi mwalekani agambeje kuweweta yangali mate ni kupita nching''anda, mpaka chasimangane nalyo Lisiku lyao alila lyaakuŵa ali nkupedwa chilanga (cha kwisa kwakwe).');
INSERT INTO "silika" VALUES(70,43,'70:43','Lisiku lyatachikopokangana m’malembe mwachitema chisawu ŵanganyao akuutuchila ku (isanamu yao) yaisimichidwe.');
INSERT INTO "silika" VALUES(70,44,'70:44','Meso gao gali gateteŵele, kwaluka kuli kwaunichile, lyalakwelyo ni Lisiku alila lyaŵaliji nkupedwa chilanga (cha kwisa kwakwe).');
INSERT INTO "silika" VALUES(71,1,'71:1','Chisimu Uwwe twantumisye Nuhu ku ŵandu ŵakwe (ni kuntenda kuti): “Mwatetelani ŵandu ŵenu nkaniyayichilile ilagasyo yakupoteka nnope.”');
INSERT INTO "silika" VALUES(71,2,'71:2','Jwalakwe jwatite: “E ŵandu ŵangu! Chisimu une ninkuntetela jwenu jwakuonechela.”');
INSERT INTO "silika" VALUES(71,3,'71:3','“Mun''galagatilani Allah ni kun''jogopa soni mumbikanilani une.”');
INSERT INTO "silika" VALUES(71,4,'71:4','“Tankululuchile sambi syenu ni kunchelewasya kwikanila ndema jajikolanjidwe (kuŵa mbesi ja umi wenu), chisimu ndema ja Allah (jakuwila mundu) pati jiyiche, jangaŵa nkujichelewasya ingaŵe mwamanyililaga.”');
INSERT INTO "silika" VALUES(71,5,'71:5','Jwalakwe jwatite: “Ambuje ŵangu! Chisimu une mbele ndili nkwaŵilanjila ŵandu ŵangu chilo ni muusi.”');
INSERT INTO "silika" VALUES(71,6,'71:6','“Nambo kuŵilanjila kwanguko nganikwajonjechesya (chilichose) ikaŵeje kutilape.”');
INSERT INTO "silika" VALUES(71,7,'71:7','“Soni chisimu une ndema jilijose jimbele ndili nkwaŵilanjila (Kukwenu) kuti mwakululuchile, aŵele ali nkuŵika yala yao m’mawiwi mwao ni kuliunichilaga nguwo syao, soni aŵele ali nkuchalila (kutenda makani), ni alikwesisye kulikwesya kusyene.”');
INSERT INTO "silika" VALUES(71,8,'71:8','“Kaneka une chisimu naŵilanjiile ŵanganyao mwakunyanyika.”');
INSERT INTO "silika" VALUES(71,9,'71:9','“Kaneko chisimu une nagombelesye kukwao, soni ni nasongonele mwaasili.”');
INSERT INTO "silika" VALUES(71,10,'71:10','“Basi ni natite: ‘Mum''bendani chikululuko M''mbuje gwenu, chisimu Jwalakwejo juŵele Jwakululuka kusyene.”');
INSERT INTO "silika" VALUES(71,11,'71:11','“Juntuluchisye wula mwawinji.”');
INSERT INTO "silika" VALUES(71,12,'71:12','“Ni jun''jonjechesye chipanje ni ŵanache, ni kum''bichila migunda, ni kum''bichila soni sulo.”');
INSERT INTO "silika" VALUES(71,13,'71:13','“Chipali chichi kukwenu changampela Allah uchimbichimbi (pakun''galagatila Jwele Jikape)!”');
INSERT INTO "silika" VALUES(71,14,'71:14','“Kutendaga pamasile pan''gumbile mu kaŵe kakulekangana-lekangana kane panyuma pa kane!”');
INSERT INTO "silika" VALUES(71,15,'71:15','“Ana ngankuyiona yatite kagumbe Allah mawunde nsano nagaŵili mwakusajikanya?”');
INSERT INTO "silika" VALUES(71,16,'71:16','“Ni auŵisile mwesi mwalakwemo kuŵa lilanguka, ni aliŵisile lyuŵa kuŵa nyali?”');
INSERT INTO "silika" VALUES(71,17,'71:17','“Soni Allah ammesisye (pakun''gumba) kuumila mwitaka mwammela.”');
INSERT INTO "silika" VALUES(71,18,'71:18','“Kaneka tachim''buchisya m’momo (panyuma pakuwa), ni kwisa kunkoposya mwanakamo (pa Kiyama).”');
INSERT INTO "silika" VALUES(71,19,'71:19','“Soni Allah am''biichiile litaka kuŵa chakutandika.”');
INSERT INTO "silika" VALUES(71,20,'71:20','“Kuti n''jendejejendeje palakwepo m’matala gakusapanguka.”');
INSERT INTO "silika" VALUES(71,21,'71:21','Nuhu jwatite: “Ambuje ŵangu! Chisimu ŵanganyao anyosisye une, ni ankuyiye ajula jwangaŵa nkun''jonjechesya chipanje chakwe ni ŵanache ŵakwe (chilichose) ikaŵeje kupelelwape.”');
INSERT INTO "silika" VALUES(71,22,'71:22','“Ni atesile malindi gamakulungwa nnope.”');
INSERT INTO "silika" VALUES(71,23,'71:23','“Soni aŵele ali nkuti: ‘Ngasinneka (galagatila) milungu jenu ata panandi, ngasinnogwa kwaleka Awadda, Asuwa''a, Ayagh''utha, Aya’uka ni Anasra.”');
INSERT INTO "silika" VALUES(71,24,'71:24','“Soni ŵanganyao pamasile paŵasokonesye (ŵandu) ŵajinji, ni ngasimwajonjechesya ŵakulitenda lupuso (chine chakwe) ikaŵeje kupotelape.”');
INSERT INTO "silika" VALUES(71,25,'71:25','Ligongo lya ilemwa yao ŵamisyidwe (m’mesi) ni kwinjisyidwa ku Moto, soni nganansimana jwalijose jwanti ni kwakulupusya jwangaŵaga Allah.');
INSERT INTO "silika" VALUES(71,26,'71:26','Soni Nuhu jwatite: “Ambuje ŵangu! Ngasimunneka pa chilambo pano ata jumo mwa makafiliŵa.”');
INSERT INTO "silika" VALUES(71,27,'71:27','“Chisimu Mmwe naga nkwaleka ŵanganyao taŵe ali nkwasokonesya achikapolo Ŵenu, soni ŵanyao ngaŵa nkuŵeleka (mwanache jwambone) ikaŵeje jwakusakasya nnope, jwakanila kusyene.”');
INSERT INTO "silika" VALUES(71,28,'71:28','“Ambuje ŵangu! Mungululuchilani une ni achinangolo ŵangu, ni jwalijose jwaajinjile m’nyumba mwangu ali jwakulupilila, kwisa soni ŵakulupilila ŵachilume ni ŵachikongwe (wosope), ni ngasimwajonjechesya ŵakulitenda lupuso (chine chakwe) ikaŵeje chonasikope.”');
INSERT INTO "silika" VALUES(72,1,'72:1','Jilani (mmwe Muhammadi صلى الله عليه وسلم): “Ijuwulidwe kukwangu yanti likuga line lya majini lyapikanile (Qur’an), basi ni gatite (pakugasalila majini gajao): ‘Chisimu uwwe tupikene Qur’an jakusimosya.');
INSERT INTO "silika" VALUES(72,2,'72:2','Jikongolela kuchongoko mwanti tujikulupilile, soni nganituŵa tum’bwanganyisye M’mbuje gwetu ni chilichose.');
INSERT INTO "silika" VALUES(72,3,'72:3','Soni chisimu Uchimbichimbi wa M’mbuje gwetu unyakwiche kusyene, nganijulitendela ŵankwakwe namuno mwanache.');
INSERT INTO "silika" VALUES(72,4,'72:4','Soni chisimu jwam’bwawate mwetuwe jwaliji nkum''bechetela Allah yaukoleko wakunaula.');
INSERT INTO "silika" VALUES(72,5,'72:5','Soni chisimu uwwe twaliji nkuganichisya yanti mundu ni lijini ngaŵa nkum''bechetela Allah yaunami.');
INSERT INTO "silika" VALUES(72,6,'72:6','Soni chisimu achalume ŵachiŵandu ŵaliji nkulijuya mwa achalume ŵachijini, basi niŵaliji nkwajonjechesya kusumba mpika pasambi.');
INSERT INTO "silika" VALUES(72,7,'72:7','Soni chisimu ŵanganyao ŵaiche mwaganisya mwamwaganichisyaga ŵanganyammwe kuti Allah ngaŵa nkutumisya (ntume) jwalijose (kuŵandu ni majini).');
INSERT INTO "silika" VALUES(72,8,'72:8','Soni chisimu uwwe twasachilile kuja kwika kwinani, nambo twakusimene kuli kugumbele achilonda ŵamachili (ŵachilaika) ni yenje (yandondwa).');
INSERT INTO "silika" VALUES(72,9,'72:9','Soni chisimu uwwe (kala) twaliji nkutama mwalakwemo m’malo mwakupikanila (abali syakwinani), nambo jwatasache kuja kupikanila sanojino, takachisimane chenje (chandondwa) chili chin''juŵilile.');
INSERT INTO "silika" VALUES(72,10,'72:10','Soni chisimu uwwe ngatukumanyilila kuti, ana akusachiilidwa yangalumbana ŵali pa chilambo kapena M’mbuje gwao jukwasachilila chongoko!');
INSERT INTO "silika" VALUES(72,11,'72:11','Soni chisimu mwetuwe mwana ŵane ŵambone, nambo ŵane mwetuwe nganaŵa iyyo. Tuŵele (mikutula ja) matala gakulekangana-lekangana (ga dini).');
INSERT INTO "silika" VALUES(72,12,'72:12','Soni chisimu uwwe twaimanyi kusyene yanti ngaŵa nkunnepelaasya Allah (kutupa ilagasyo) pa chilambo pano, soni ngaŵa nkunnepelaasya pakutilila (kwinani).');
INSERT INTO "silika" VALUES(72,13,'72:13','Soni chisimu uwwe ndema jatuchipikene chongolo (Qur’an) tuchikulupilile, basi jwatankulupilile M’mbujeegwe nikuti ngajogopa kupungusyidwa (yambone yakwe) kapena kutwichidwa (nsigo wa sambi sya jwine).');
INSERT INTO "silika" VALUES(72,14,'72:14','Soni chisimu mwetuwe mwana Asilamu ni mwana ŵane mwetuwe ŵapombotale (kuleka litala lyagoloka lya Usilamu). Sano ŵajinjile mu Usilamu, basi ŵanganyao ni ŵalungeme ku chongoko.”');
INSERT INTO "silika" VALUES(72,15,'72:15','Nambo aŵala ŵapombotele (kuleka Usilamu), nikuti chachiŵa sasu sya ku Jahannama.');
INSERT INTO "silika" VALUES(72,16,'72:16','Soni mwanti ingaŵe ŵajimilichiche (akupombotalao) petala (lya Usilamu), chisimu tungaamwesisye mesi gamajinji (nga nganalajila chilichose).');
INSERT INTO "silika" VALUES(72,17,'72:17','Kuti twalinje muwele (ukotowo yanti: Ana chapitilisye Usilamu kapena chakufulu)? Sano jwatakutundumalile kunkumbuchila M’mbujeegwe (Allah), tan''jinjisye ku ilagasyo yakusausya nnope.');
INSERT INTO "silika" VALUES(72,18,'72:18','Soni chisimu misikiti nsyene jwakwe ni Allah, basi ngasimun''galagatila jwalijose pampepe ni Allah.');
INSERT INTO "silika" VALUES(72,19,'72:19','Soni chisimu ndema ja jwajimi kapolo jwa Allah (Muhammadi صلى الله عليه وسلم pa swala) achim''bombaga (Allah), ŵasigele panandi (achimajini) kuti ammate (ligongo lya winji nnope ni kunyambatikana pakusaka kupikanila Qur’an).');
INSERT INTO "silika" VALUES(72,20,'72:20','Jilani: “Chisimu ngun''galagatila M''mbuje gwangu pe, ni ngangum''bwanganya Najo julijose.”');
INSERT INTO "silika" VALUES(72,21,'72:21','Jilani: “Chisimu une nganingola kukwenu (machili gakumpela) isausyo kapena chongoko.”');
INSERT INTO "silika" VALUES(72,22,'72:22','Jilani: “Chisimu une ngaŵa nkuunjokola jwalijose (ku ipotesi ya) kuumila kwa Allah (naga ndili nannyosisye), soni ngaŵa nkupata kwakutilila kwangaŵaga Kukwakwe.');
INSERT INTO "silika" VALUES(72,23,'72:23','Ikaŵeje kulungusya (yakuona) ya kuumila kwa Allah ni utenga Wakwe (gele ni machili gangwete une), sano jwatannyosye Allah ni Ntenga Jwakwe صلى الله عليه وسلم; basi chisimu uŵele wakwe jwalakwejo moto wa Jahannama, chakaŵe ŵandamo mwalakwemo yaka-yaka.”');
INSERT INTO "silika" VALUES(72,24,'72:24','Kwikanila ndema jataachiiwona yaakuŵa ali nkupedwa chilanga (cha kwika kwakwe), pele nipatachimamyilila (yanti) ana ŵani ŵaakwete nkwaakamuchisya jwangali machili ni ŵaali chiŵalanjilo channono.');
INSERT INTO "silika" VALUES(72,25,'72:25','Jilani: “Ngangupata kumanyilila kuti ana ili pakuŵandika (ipotesi) yankupedwa chilangayo kapena Ambuje ŵangu tayiŵiichile ndaŵi jelewu!”');
INSERT INTO "silika" VALUES(72,26,'72:26','(Jwalakwe ni) Nkumanyilila yakusisika, basi ni jwangailosya yakusisika Yakweyo kwa jwalijose.');
INSERT INTO "silika" VALUES(72,27,'72:27','Ikaŵeje Ntenga jwansagwile, basi chisimu akasam''biichilaga m''bujo mwakwe ni munyuma mwakwe (Achimalaika) ŵakunnonda-londa.');
INSERT INTO "silika" VALUES(72,28,'72:28','Kuti amanyilile (Ntengajo) yanti pamasile paŵaalungwisye (Achimitenga ŵane wosope) mautenga ga M’mbuje gwao, soni nikuti (Allah) ayisyuungwiile yali nayo (paaimanya chenene), soni ni akusenye chiŵalanjilo cha chilichose.');
INSERT INTO "silika" VALUES(73,1,'73:1','E, mmwe ŵan''diŵijiiliile mu nguwo!');
INSERT INTO "silika" VALUES(73,2,'73:2','Jimani chilo (kuswali) ikaŵeje kandaŵi kannono.');
INSERT INTO "silika" VALUES(73,3,'73:3','Litika lyakwe (1⁄2), kapena mpungusye mwitikamo panandi.');
INSERT INTO "silika" VALUES(73,4,'73:4','Kapena n''jonjechesye petikalyo, ni nsoomeje Qur’an mwaakosoŵalila.');
INSERT INTO "silika" VALUES(73,5,'73:5','Chisimu Uwwe chituntuluchisye mmwejo liloŵe lyausito.');
INSERT INTO "silika" VALUES(73,6,'73:6','Chisimu kwimuchila chilo (kuswali) nikwakuli kwakwinjilisya nnope (mu ntima), soni yakuŵecheta yakwe ikasaŵaga yagoloka kusyene.');
INSERT INTO "silika" VALUES(73,7,'73:7','Chisimu mmwejo muusi nkwete ipaka yejinji.');
INSERT INTO "silika" VALUES(73,8,'73:8','Ni likumbuchilani lina lya Ambuje ŵenu (Allah pakulikolanga), soni lipelekani Kukwakwe nalipelekape.');
INSERT INTO "silika" VALUES(73,9,'73:9','M''mbuje jwa kungopoko ni kungapililo, pangali nnungu jwine (jwakuŵajilwa kun’galagatila) ikaŵeje Jwalakwe, basi muntendani kuŵa Nkun''jegama.');
INSERT INTO "silika" VALUES(73,10,'73:10','Soni pililani pa yaakuŵecheta (achimakafili), ni mwaŵambalani kuŵambala kwambone.');
INSERT INTO "silika" VALUES(73,11,'73:11','Soni munekaani Une ni ŵakanila achinsyene ukoto (wa duniya), gambani kwalechelela panandipe (timalane nao).');
INSERT INTO "silika" VALUES(73,12,'73:12','Chisimu Kukwetu kwana matondolo gakulimbangana ni Moto Waukali.');
INSERT INTO "silika" VALUES(73,13,'73:13','Ni yakulya yakwama (palukosi) kwisa soni ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(73,14,'73:14','Lisiku lya lichitenganyika litaka kwisa soni matumbi, ni gachiŵa matumbigo chisau liunjili lya nsanga wakupwelembwesuka.');
INSERT INTO "silika" VALUES(73,15,'73:15','Chisimu Uwwe tutumisye kukwenu Ntenga (Muhammadi صلى الله عليه وسلم) kuŵa mboni pa jenumanja, mpela yatwatite pakutumisya kwa Afiriauna Ntenga (Musa).');
INSERT INTO "silika" VALUES(73,16,'73:16','Nambo Afiriauna ŵannyosyisye Ntengajo, basi ni twaakwembekenye ni ilagasyo yakandapala kusyene.');
INSERT INTO "silika" VALUES(73,17,'73:17','Ana chin''digose chinauli (ku ipotesi) naga nkulikanila Lisiku lyalichitendekasya ŵanache kola wuuli?');
INSERT INTO "silika" VALUES(73,18,'73:18','Liwunde lichipasuka mu lyele Lisikuli (ligongo lya kogoya kwakwe), chilanga Chakwe (Allah) chichiŵa chakutendekwa (mwangalepeleka).');
INSERT INTO "silika" VALUES(73,19,'73:19','Chisimu galakwe (ma Ȃyaga) gaŵele chikumbusyo, basi jwaakusaka aliikolochesye chilepe litala lyakuujila kwa M''bujegwe.');
INSERT INTO "silika" VALUES(73,20,'73:20','Chisimu Ambuje ŵenu akumanyilila yanti mmwejo (Muhammadi صلى الله عليه وسلم) nkwima (kuswali) mwakunandipila pa ipande iŵili mu ipande itatu yaachilo (2⁄3), kapena litika lyakwe (1⁄2), kapena chimo mu ipande itatu (1⁄3), pamo ni likuga lya aŵala ŵan''di nao, sano Allah ni Jwaakupimila chenene (ulewu wa) chilo ni muusi, jwaimanyi yanti nganim''ba nkukwanisye kupimila kusyesyene (nikuswali mwannamulile, niligongo lyakwe n''gamba kuswali chilo chosope), basi ankululuchile (pakuusyaausya lilamusili). Kwaligongo lyele; soomagani chachijepepele chakuumila m Qur’an. Jwaimanyi yanti ŵane mwa jenumanja tachiŵa ŵakulwala, soni ŵane tachiŵa ali nkwendajenda pa chilambo kupita nchisosasosa umbone wa Allah, soni ŵane tachiŵa ali nkuputa ngondo petala lya Allah, basi soomagani chachijepepele chakuumila mwele (mu Qur’animo), ni n''jimicheje Swala mwakolosya ni kutolaga Zaka, soni munkongosyeje Allah ngongole jambone. Soni chambone chilichose chankulilongochesya mwachimisyene tinkachisimane kwa Allah chili chambone nnope ni chamalipilo gekulungwa kusyene. Ni mum''bendagani chikuluko Allah, chisimu Allah ni Jwakululuka kusyene, Jwachanasa channope.');
INSERT INTO "silika" VALUES(74,1,'74:1','E, mmwe ŵan''diunichile nguwo!');
INSERT INTO "silika" VALUES(74,2,'74:2','Jimani ni mwaatetele (ŵandu).');
INSERT INTO "silika" VALUES(74,3,'74:3','Soni Ambuje ŵenu (Allah) mwakusyaani.');
INSERT INTO "silika" VALUES(74,4,'74:4','Soni nguwo syenu siswejesyani.');
INSERT INTO "silika" VALUES(74,5,'74:5','Soni yakusakala (mpela isanamu) iŵambalani.');
INSERT INTO "silika" VALUES(74,6,'74:6','Soni ngasimumpa (jwinejwakwe chindu) ni ligongo lyakuti mpochele yejinji.');
INSERT INTO "silika" VALUES(74,7,'74:7','Soni ligongo lya Ambuje ŵenu (Allah) pililani.');
INSERT INTO "silika" VALUES(74,8,'74:8','Basi ndema jalichigombedwa lipenga.');
INSERT INTO "silika" VALUES(74,9,'74:9','Lyele lisikulyotu lichiŵa lisiku lyakunonopa kusyene.');
INSERT INTO "silika" VALUES(74,10,'74:10','Kwa achimakafili ngasiliŵa lyakwepepala.');
INSERT INTO "silika" VALUES(74,11,'74:11','Munekaani ni mundu jwanan''gumbile jikajikape.');
INSERT INTO "silika" VALUES(74,12,'74:12','Ninampele ipanje yejinji.');
INSERT INTO "silika" VALUES(74,13,'74:13','Ni ŵanache ŵakuŵanajo.');
INSERT INTO "silika" VALUES(74,14,'74:14','Ni nankolochesye (ndamo jakwe) kunkolochesya kwambone nnope.');
INSERT INTO "silika" VALUES(74,15,'74:15','Panyuma pakwe jukolaga tama janti ndili nan''jonjechesye.');
INSERT INTO "silika" VALUES(74,16,'74:16','Kwapi! Chisimu jwalakwejo jwaliji nkugakanila maaya Getu.');
INSERT INTO "silika" VALUES(74,17,'74:17','Chingankanganichisye ku ilagasyo yakusausya kuikwela.');
INSERT INTO "silika" VALUES(74,18,'74:18','Chisimu jwalakwe jwachetelele (yatajile paajigamba Qur’an) ni jwapimiile.');
INSERT INTO "silika" VALUES(74,19,'74:19','Basi julwesedwe, ana jwapimiile chantiuli!');
INSERT INTO "silika" VALUES(74,20,'74:20','Soni julwesedwe, ana jwapimiile chantiuli!');
INSERT INTO "silika" VALUES(74,21,'74:21','Kaneka ni jwalolite (ŵandu).');
INSERT INTO "silika" VALUES(74,22,'74:22','Kaneko ni jwautile sinya ni kusakasya kungope (kulosya ukali).');
INSERT INTO "silika" VALUES(74,23,'74:23','Kaneko soni jwachitundumalile (chikulupi) ni kulikwesya.');
INSERT INTO "silika" VALUES(74,24,'74:24','Ni jwatite: “Nganijiŵatu jalakwe (Qur’aniji) ikaŵeje usaŵi waukupocheledwa (kuumila kwa asaŵi ŵakala).”');
INSERT INTO "silika" VALUES(74,25,'74:25','“Nganijiŵatu jalakwe (Qur’aniji) ikaŵeje maloŵe ga mundu.”');
INSERT INTO "silika" VALUES(74,26,'74:26','Sampano tinan''jinjisye ku Saqara.');
INSERT INTO "silika" VALUES(74,27,'74:27','Ana ni chichi champaka chimmanyisye ya Saqara?');
INSERT INTO "silika" VALUES(74,28,'74:28','(Weletu ni Moto) wangasigasya (chilichose chachiponyedwemo) soni wangachileka (kuti chikopoche).');
INSERT INTO "silika" VALUES(74,29,'74:29','Wakutinisya ni kuŵiilisya lipende.');
INSERT INTO "silika" VALUES(74,30,'74:30','Pa walakweo pana (Achimalika ŵakuugosa) ŵakwana likumi kwisa nsano nancheche (19).');
INSERT INTO "silika" VALUES(74,31,'74:31','Ni nganitwaaŵiika akuuloleela Moto ikaŵeje Achimalaika, soni nganituchiŵiika chiŵalanjilo chaocho ikaŵeje kuŵa mayeso gakwalinjila achimakafili, kuti asimichisye ŵandu ŵaŵapedwile chitabu (ya chiŵalanjilochi), ni kuti soni ajonjechesye chikulupi chao ŵandu ŵaakulupilile, soni kuti akakaichila ŵandu ŵaŵapedwile chitabu ni ŵakulupilila, ni kuti soni aŵala ŵamuli m’mitima mwao mwana ulwele (wa unakunaku) ni achimakafili ajileje: “Ana Allah akusaka chichi pachilandanyo chalakwechi?” Iyyoyopeyo niyaakutiji Allah pa kunnechelela kusokonechela jwansachile ni kun''jongola jwansachile. Ni pangali ŵaakumanyilila mikutula ja asilikali ŵa Ambuje ŵenu ikaŵeje Jwelepejo. Ni nganiuŵa Walakwe (Motowo) ikaŵeje chikumbusyo kwa ŵandu (cha kwatetela nacho).');
INSERT INTO "silika" VALUES(74,32,'74:32','Ngwamba! Nguulumbila mwesi.');
INSERT INTO "silika" VALUES(74,33,'74:33','Ni chilo ndema jachikutyoka (pakucha).');
INSERT INTO "silika" VALUES(74,34,'74:34','Ni kumasikusiku ndema jakukulanguka.');
INSERT INTO "silika" VALUES(74,35,'74:35','Chisimu Walakwe (Motowo) uŵele limpepe (m’makungu) gekulungwa nnope.');
INSERT INTO "silika" VALUES(74,36,'74:36','Chitetelo kwa ŵandu.');
INSERT INTO "silika" VALUES(74,37,'74:37','Kwa jwalijose mwa jenumanja jwaasachile kuti alongolele (pakutenda yambone) kapena kusigalila (pakupanganya yakusakala).');
INSERT INTO "silika" VALUES(74,38,'74:38','Mundu jwalijose tachiŵa uwombosi wakuliombolela ku (yakusakala) yaŵapanganyisye (pakutaŵidwa ku Moto).');
INSERT INTO "silika" VALUES(74,39,'74:39','Ikaŵeje aŵala ŵakunkono wan''dyo.');
INSERT INTO "silika" VALUES(74,40,'74:40','(Chachiŵa) m’Matimbe ga ku Mbepo achiwuusyanaga.');
INSERT INTO "silika" VALUES(74,41,'74:41','Ya (ŵandu) ŵakuleŵa.');
INSERT INTO "silika" VALUES(74,42,'74:42','“Ana ni chichi chachin''jinjisye ŵanganyammwe ku Saqara?”');
INSERT INTO "silika" VALUES(74,43,'74:43','(Pakwanga) chachitiji: “Nganituŵa mwa akuswali.”');
INSERT INTO "silika" VALUES(74,44,'74:44','“Soni nganituŵa nkwaaliisya masikini.”');
INSERT INTO "silika" VALUES(74,45,'74:45','“Ni twaliji nkutiŵila (mu sambi) pamo ni akutiŵila.”');
INSERT INTO "silika" VALUES(74,46,'74:46','“Ni twaliji soni nkukanila ya Lisiku Lyamalipilo.”');
INSERT INTO "silika" VALUES(74,47,'74:47','“Mpaka chatuichilile chiwa.”');
INSERT INTO "silika" VALUES(74,48,'74:48','Basi ngasichija kwakamuchisya ŵanganyao chondechonde cha akuchondelela.');
INSERT INTO "silika" VALUES(74,49,'74:49','Ana chipali chichi kukwao chikwatendekasya kuchitundumalila chikumbusyo (Qur’an)?');
INSERT INTO "silika" VALUES(74,50,'74:50','(Kakwipuche kakwe kuti akajipikana Qur’an) gamba kuŵa ŵanganyao mpela mbunda (syamwitinji) syasikwipusyidwe.');
INSERT INTO "silika" VALUES(74,51,'74:51','Syasikutila lisimba.');
INSERT INTO "silika" VALUES(74,52,'74:52','Yeti mundu jwalijose mwa ŵanganyao akusakaga ali apedwile ikalakala yeunukule-unukule (kuumila kwa Allah kutendela umboni yakuŵecheta Muhammadi صلى الله عليه وسلم).');
INSERT INTO "silika" VALUES(74,53,'74:53','Ngwamba! Nambo kuti ŵanganyao ngaakujijogopa Akhera.');
INSERT INTO "silika" VALUES(74,54,'74:54','Ngwamba! Chisimu jalakwe (Qur’aniji) jiŵele chikumbusyo.');
INSERT INTO "silika" VALUES(74,55,'74:55','Basi jwaakusaka taalikumbusye najo.');
INSERT INTO "silika" VALUES(74,56,'74:56','Ni nganaŵa alikumbwisye ŵanganyao ikaŵeje naga Allah ali asachile. Jwalakwe ni jwaali Jwakuŵajilwa kogopedwa soni ni Nysene chikululuko.');
INSERT INTO "silika" VALUES(75,1,'75:1','Ngulilumbila lisiku lya Kiyama.');
INSERT INTO "silika" VALUES(75,2,'75:2','Ni nguulumbila soni ntima wakuliigamba.');
INSERT INTO "silika" VALUES(75,3,'75:3','Ana akuganichisya mundu kuti ngaŵa nkusonganganya Uwwe maupa gakwe?');
INSERT INTO "silika" VALUES(75,4,'75:4','Elo (tuchigasonganganya), ngati Uwwe tuli ŵakombola kwalanya chenene (tumisela twa) songa sya yala yakwe, (kuli uli maupa)!');
INSERT INTO "silika" VALUES(75,5,'75:5','Nambo mundu (akukanila ya Kiyama) pakusachilila kwendelechela kusakasya m''bujo mwakwe.');
INSERT INTO "silika" VALUES(75,6,'75:6','Akuusya (mwa chipongwe kuti): “Ana lisiku lya Kiyama lichiŵa chakachi?”');
INSERT INTO "silika" VALUES(75,7,'75:7','Sano ndema ja lichikolondoka liso (ligongo lya woga).');
INSERT INTO "silika" VALUES(75,8,'75:8','Ni kutyoka kuŵala kwa mwesi.');
INSERT INTO "silika" VALUES(75,9,'75:9','Ni kusonganganyisyidwa lyuŵa ni mwesi.');
INSERT INTO "silika" VALUES(75,10,'75:10','Lyele lisikulyo mundu tachiti: “Kwakutilila ni kwapi?”');
INSERT INTO "silika" VALUES(75,11,'75:11','Iyayi, pangali kwakutilila.');
INSERT INTO "silika" VALUES(75,12,'75:12','Kwa Ambuje ŵenupe (Allah) lyele lisikulyo nikuchipelela kwakuujila.');
INSERT INTO "silika" VALUES(75,13,'75:13','Tachisalilidwa mundu lyele lisikulyo ichindu yajwalongwesye (kupanganya) ni yajwalesile (munyuma).');
INSERT INTO "silika" VALUES(75,14,'75:14','Nambo mundu tachilichonjela nsyene (ligongo kutandila lipende, makono ni ngongolo syakwe ichijuwula masengo gakusakala ga jwapanganyisye).');
INSERT INTO "silika" VALUES(75,15,'75:15','Namuno tatole madandawusi gakwe (pakuliŵalanga ngasigankamuchisya).');
INSERT INTO "silika" VALUES(75,16,'75:16','Ngasin''jitenganyila (Qur’aniji mmwe Muhammadi صلى الله عليه وسلم) lulimi lwenu (ndema jajikutuluka) kuti n''jikulumisye (kujisunga ni kujisooma).');
INSERT INTO "silika" VALUES(75,17,'75:17','Chisimu ili Kukwetu Uwwe kujisonganganya (mu ntima mwenu) ni kumpa ukombosi wakujisooma.');
INSERT INTO "silika" VALUES(75,18,'75:18','Sano pati tuchinsoomelaga, basi kuyichisyani kusoomwa kwakwe.');
INSERT INTO "silika" VALUES(75,19,'75:19','Soni chisimu ili Kukwetu Uwwe kujisalichisya.');
INSERT INTO "silika" VALUES(75,20,'75:20','Ngaŵa (chine chikuntendekasya kaanila Kiyama), nambo kuti ŵanganyammwe nkunonyelwa umi wachitema (wa duniya).');
INSERT INTO "silika" VALUES(75,21,'75:21','Ni nkujileka Akhera.');
INSERT INTO "silika" VALUES(75,22,'75:22','Ngope sine lyele lisikulyo sichiŵa syakuŵala.');
INSERT INTO "silika" VALUES(75,23,'75:23','Sili nkwalola Ambuje ŵao (Allah).');
INSERT INTO "silika" VALUES(75,24,'75:24','Sano nambo ngope sine lyele lisikulyo sichiyinda.');
INSERT INTO "silika" VALUES(75,25,'75:25','Sichisimichisya yanti chiitendekwe kukwakwe yakuniula n''gongo.');
INSERT INTO "silika" VALUES(75,26,'75:26','Ngaŵa (yanti nikanila), ndema jauchiika ntima paligongwa (papasimanile maupa gammakoyo).');
INSERT INTO "silika" VALUES(75,27,'75:27','Nikuŵechetedwa (yanti): “Ŵani ŵampaka aposye (chilwelechi)?”');
INSERT INTO "silika" VALUES(75,28,'75:28','Nikumala mwakusimichisya (chilwelecho) kuti kwalakweku ni kulekangana (najo duniya).');
INSERT INTO "silika" VALUES(75,29,'75:29','Mwanti lusonjo nikusajikana ni lusonjo (lujakwe ndema jakutyoka ntima).');
INSERT INTO "silika" VALUES(75,30,'75:30','Kwa Ambuje ŵenupe lyele lisikulyo ni kwakuwujila mwakuchinjidwa.');
INSERT INTO "silika" VALUES(75,31,'75:31','Mwanti (kafili) nganijukunda (chikulupi) soni (namuno swala) nganijuswali.');
INSERT INTO "silika" VALUES(75,32,'75:32','Nambo jwakaaniile ni kunyosya.');
INSERT INTO "silika" VALUES(75,33,'75:33','Kaneka jwapite kwiŵasa lyakwe (juli nkwenda) mwakutwanga.');
INSERT INTO "silika" VALUES(75,34,'75:34','Chonasiko nichenu! Chonasiko chisyene!');
INSERT INTO "silika" VALUES(75,35,'75:35','Soni chonasiko nichenu! Chonasiko chisyene!');
INSERT INTO "silika" VALUES(75,36,'75:36','Ana jukuganichisya mundu yanti chijugambe kulechedwa wambape (ngapedwa malamusi soni ngaja kulipidwa)?');
INSERT INTO "silika" VALUES(75,37,'75:37','Ana nganijuŵa lindondwa lya ulume wakutajidwa (m’chiŵelechelo)?');
INSERT INTO "silika" VALUES(75,38,'75:38','Kaneka jwaliji chipande cha myasi, niŵan''gumbile ni kunkolosya.');
INSERT INTO "silika" VALUES(75,39,'75:39','Ni ŵagumbile kuumila mwalakwemo mitundu jiŵili (jaŵandu) jwannume ni jwankongwe.');
INSERT INTO "silika" VALUES(75,40,'75:40','Ana Jwalakwe (Allah jwaŵapakombwele kutenda yele) nganaŵa jwanti ni kombola kwasyusya (ŵandu) ŵawe?');
INSERT INTO "silika" VALUES(76,1,'76:1','Ana nganijipita kwamundu mbinga jelewu ja ndaŵi nkanaŵe kuŵa chindu chanti ni kusalidwa?');
INSERT INTO "silika" VALUES(76,2,'76:2','Chisimu Uwwe tun''gumbile mundu kuumila mwindondwa lyakuwanganyichilana (lya mesi ga ŵalume ni ŵakongwe), kuti tun''dinje (ni malamusi Getu), ni tuntesile kuŵa jwakupikana, jwakulola.');
INSERT INTO "silika" VALUES(76,3,'76:3','Chisimu Uwwe tunsaalichiisye jwalakwejo litala (lyambone ni lyakusakala, sano ili ni nsyene) kuŵa jwakutogolela (pakusagula lyambonelyo) kapena kuŵa jwakanila (pakusagula lyakusakala).');
INSERT INTO "silika" VALUES(76,4,'76:4','Chisimu Uwwe twalinganyichisye achimakafili malundelunde ga unyolo, ni makongwa kwisa soni Moto Wakulapuka.');
INSERT INTO "silika" VALUES(76,5,'76:5','Chisimu atenda ngoto chakamweje chipanda (chankologo) chakuwanganyichisya chakwe (nkologowo) chigakaŵeje (mesi ga) Kaafuura.');
INSERT INTO "silika" VALUES(76,6,'76:6','(Kaafuurajo ni) tutumule jwachakamwejemo achikapolo ŵa Allah, chakam''bumyeje (palipose pachakasacheje) kaumye kangasausya.');
INSERT INTO "silika" VALUES(76,7,'76:7','(Ŵanganyao ni aŵala) ŵaakukwanilisya nasili (syao), soni ni akulijogopa Lisiku lya ichiŵa yakusakala yakwe yakwenela.');
INSERT INTO "silika" VALUES(76,8,'76:8','Ni akutolaga yakulya - achiisakaga - kwapa masikini, jwawina ni mateeka.');
INSERT INTO "silika" VALUES(76,9,'76:9','(Chamuntima akutiji): “Chisimu tukun''diisya pakusachilila chinonyelo cha Allah, ngatukusaka kukwenu malipilo galigose namuno kutogolela.”');
INSERT INTO "silika" VALUES(76,10,'76:10','“Chisimu uwwe tukogopa kwa M’mbuje gwetu Lisiku lyakuuta sinya (kafili) soni lyakupoteka nnope ni kupwelembwesula (ligongo lya kuleupa kwakwe mu ilagasyo).”');
INSERT INTO "silika" VALUES(76,11,'76:11','Basi Allah tachagosa kuyakusakala ya lyele lisikulyo ni kwapa kumelemenda (kungope kwao) ni kusengwa.');
INSERT INTO "silika" VALUES(76,12,'76:12','Soni tachalipila ligongo lya kupilila kwao Litimbe lya ku Mbepo kwisa soni nguwo sya silika.');
INSERT INTO "silika" VALUES(76,13,'76:13','Ali ajegeme kwalakweko nyyindanda yetandiche chenene, ngaaja kuchiona chitukuta cha lyuŵa namuno mbepo ja chikweenya.');
INSERT INTO "silika" VALUES(76,14,'76:14','Soni miulili jakwe chijikaaŵandichile ŵanganyao, ni mikonga (ja isogosi) chijikagoyeche kwakwalala (yanti konyola chitamile).');
INSERT INTO "silika" VALUES(76,15,'76:15','Soni chiikaŵeje ili nkwasyungula ikolelo ya silifa kwisa soni migawo jegalasi.');
INSERT INTO "silika" VALUES(76,16,'76:16','Jegalasi (jagumbidwa) kuumila mu silifa, chakajipimileje (pakujigumbasya) kapimile kambone (kakulandana ni njota jao, ngapelembechesya soni ngajonjechesya).');
INSERT INTO "silika" VALUES(76,17,'76:17','Ni chakamwesyedweje kwalakweko chipanda (chankologo) chakuwanganyichisya chakwe (nkologowo) chichikaŵe (mesi ga) chikasu.');
INSERT INTO "silika" VALUES(76,18,'76:18','(Jwalakwejo ni) tutumule jwajuli kwalakweko jwakuŵilanjikwa Salsabila.');
INSERT INTO "silika" VALUES(76,19,'76:19','Soni chaakaasyunguleje ŵanganyao (pakwaatumichila) achachanda ŵakutamilichika (pa uchanda, ŵangachekulupa). N''di mwaweni ŵanganyao mpaka mwaganichisye kuti ngalekale syakumelemenda nnope syemwasyidwe.');
INSERT INTO "silika" VALUES(76,20,'76:20','Soni patinchilola kwalakwe (ku Mbepoko) tinchiuona ntendele wakusengwasya ni uchimwene wekulungwa.');
INSERT INTO "silika" VALUES(76,21,'76:21','Pachanya pao chipakaŵe pana nguwo sya silika jwakuŵeŵela syachisamba kwisa soni nguwo sya silika jwakandapalila. Soni chakawechedwe makosa ga silifa, nombe soni M’mbuje gwao (Allah) chijukaamwesye ŵanganyao yakumwa yakulenjela kusyene.');
INSERT INTO "silika" VALUES(76,22,'76:22','(Chakasalilidwe kuti): “Chisimu galakwega gaŵele malipilo genu, soni masengo genu gaŵele gakupocheledwa.”');
INSERT INTO "silika" VALUES(76,23,'76:23','Chisimu Uwwe tujitulwisye kukwenu (mmwe Muhammadi صلى الله عليه وسلم) Qur’an kujitulusya kwapanandi panandi (kuti n''jisunje chenene).');
INSERT INTO "silika" VALUES(76,24,'76:24','Basi pililani ni lilamusi lya Ambuje ŵenu (Allah) ni ngasimumpikanila mwa ŵanganyao jwasambi kapena jwakanila (jwalijose).');
INSERT INTO "silika" VALUES(76,25,'76:25','Soni likumbuchilani lina lya Ambuje ŵenu kundaŵi ni kwigulo (pakuswali swala sya Fajr, Dhuhr ni Asr).');
INSERT INTO "silika" VALUES(76,26,'76:26','Soni chilo munsujudilani (pakuswali swala sya Maghrib ni Isha), ni munswejesyeje chilo ndema jelewu.');
INSERT INTO "silika" VALUES(76,27,'76:27','Chisimu ŵanganyaŵa akunonyelwa umi wachitema (wa duniya), ni akulileka munyuma mwao Lisiku lya usito.');
INSERT INTO "silika" VALUES(76,28,'76:28','Uwwe ni ŵatwaagumbile ŵanganyao soni twaliyiye kagumbidwe kao, soni tuli tusachile mpaka tuiche nao ŵane ŵanti mpela ŵanganyao kwinjila m’malo mwao (nambo ali ŵakun''jogopa Allah).');
INSERT INTO "silika" VALUES(76,29,'76:29','Chisimu galakwe (ma Ȃyaga) gaŵele chikumbusyo, basi jwaakusaka aliikolochesye chilepe litala lyakuujila kwa M''bujegwe (Allah).');
INSERT INTO "silika" VALUES(76,30,'76:30','Ni nganim''ba nsachile ŵanganyammwe ikaŵeje akasache kaje Allah, chisimu Allah aŵele Jwakumanyilila nnope, Jwalunda lwakusokoka.');
INSERT INTO "silika" VALUES(76,31,'76:31','Taakan''jinjisye jwansachile ku ukoto Wakwe (ku Mbepo), nambo ŵakulitenda lupuso ŵalinganyichisye ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(77,1,'77:1','Ngujilumbila mbungo jajikupuga mwakuyana.');
INSERT INTO "silika" VALUES(77,2,'77:2','Ni mbungo jajikupuga mwa chimbunga.');
INSERT INTO "silika" VALUES(77,3,'77:3','Ni mbungo jajikwenesya (mawunde kwisa soni wula) mu kajenesye (kaasachile Allah).');
INSERT INTO "silika" VALUES(77,4,'77:4','Ni ma Ȃya (ga Qur’an) gakulekanganya (chilikati cha yakuona ni yaunami).');
INSERT INTO "silika" VALUES(77,5,'77:5','Ni (Achimalaika) ŵaakupelekaga utenga (kwa Achimitenga).');
INSERT INTO "silika" VALUES(77,6,'77:6','Kuti utyose kuliŵalanga (kwakupeleka madandausi) kapena kuti utetele.');
INSERT INTO "silika" VALUES(77,7,'77:7','Chisimu ichindu yankupedwa chilanga (cha kwisa kwakwe, mpela Kiyama) iŵele yakupatikana (mwangalepeleka).');
INSERT INTO "silika" VALUES(77,8,'77:8','Pa ndema ja sichisiimaasidwa ndondwa (kuŵala kwakwe).');
INSERT INTO "silika" VALUES(77,9,'77:9','Ni ndema ja lichipesedwa liwunde (nikuŵa mapesa-mapesa).');
INSERT INTO "silika" VALUES(77,10,'77:10','Ni ndema ja gachichuŵidwa matumbi ni kusyajidwa mwachitema ni kuululusyidwa.');
INSERT INTO "silika" VALUES(77,11,'77:11','Ni ndema ja chachisonganganyisyidwa Achimitenga pa ndema jetomechedwe.');
INSERT INTO "silika" VALUES(77,12,'77:12','Ana lisikuchi lyaiŵichilidwe (yayisalidweyi kuti tiyitendekwe)?');
INSERT INTO "silika" VALUES(77,13,'77:13','Pa Lisiku Lyachiilanyo.');
INSERT INTO "silika" VALUES(77,14,'77:14','Ana ni chichi champaka chimmanyisye ya Lisiku Lyachiilanyo?');
INSERT INTO "silika" VALUES(77,15,'77:15','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,16,'77:16','Ana Uwwe nganitwajonanga (ŵandu) ŵandanda (ligongo lya ilemwa yao)?');
INSERT INTO "silika" VALUES(77,17,'77:17','Kaneka chitukuichisye pajamanjao (kwajonanga ŵandu) ŵambesi.');
INSERT INTO "silika" VALUES(77,18,'77:18','Mwele ni mwatukutendelaga ni ŵandu ŵakuleŵa.');
INSERT INTO "silika" VALUES(77,19,'77:19','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,20,'77:20','Ana nganitun''gumba jenumanja kuumila mmesi gakunyosyeka?');
INSERT INTO "silika" VALUES(77,21,'77:21','Basi ni twagaŵisile pa malo gakutamilichika ni kusunjika (m’chiŵelechelo).');
INSERT INTO "silika" VALUES(77,22,'77:22','Kwikanila ndema jakumanyika.');
INSERT INTO "silika" VALUES(77,23,'77:23','Basi ni twapimiile (ndema jakupagwila mwanache), Uwwetu niŵambone nnope mwa akupimila (ichindu).');
INSERT INTO "silika" VALUES(77,24,'77:24','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,25,'77:25','Ana nganituliŵika litaka kuŵa chikuti?');
INSERT INTO "silika" VALUES(77,26,'77:26','Cha ŵachijumi ni ŵawe?');
INSERT INTO "silika" VALUES(77,27,'77:27','Ni tuŵisile mwalakwemo yakunyichisya yaileu-leu (gagali matumbi), ni tumpele kumwa ŵanganyammwe mesi gakunong’a.');
INSERT INTO "silika" VALUES(77,28,'77:28','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,29,'77:29','(Chachisalilidwa achimakafili kuti): “Ŵigulani mwanja kwa ayila yamwaliji nkuikanila.');
INSERT INTO "silika" VALUES(77,30,'77:30','Ŵigulani mwanja ku m''bulili (walyosi lya ku Moto) waukwete nyambi sitatu.');
INSERT INTO "silika" VALUES(77,31,'77:31','Nganiuŵa mwa m''bulili wachisisimila soni wangakamuchisya kwilamba (lya Moto).”');
INSERT INTO "silika" VALUES(77,32,'77:32','Chisimu Walakwe (Motowo) ukasaponyaga (pakutatasika) ndamba (syekulungwa) mpela nyumba jandalaja.');
INSERT INTO "silika" VALUES(77,33,'77:33','Syanti mpela ngamiya syam''bilichila.');
INSERT INTO "silika" VALUES(77,34,'77:34','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,35,'77:35','Lyalakweli nilisiku lyatachitenda ngapaŵecheta (ŵanganyao).');
INSERT INTO "silika" VALUES(77,36,'77:36','Soni ngasapedwa luusa lwanti aliŵalanje (pakupeleka madandausi).');
INSERT INTO "silika" VALUES(77,37,'77:37','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,38,'77:38','Lyalakweli ni Lisiku Lyachiilanyo, tunsonganganyisye ŵanganyammwe ni (ŵandu) ŵandanda.');
INSERT INTO "silika" VALUES(77,39,'77:39','Sano naga nkwete malindi, basi mundendelani.');
INSERT INTO "silika" VALUES(77,40,'77:40','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,41,'77:41','Chisimu ŵakun''jogopa (Allah) chachiŵa m’miulili (jakusisimila chenene) ni achitutumule.');
INSERT INTO "silika" VALUES(77,42,'77:42','Ni isogosi yatachiilaluchilaga.');
INSERT INTO "silika" VALUES(77,43,'77:43','(Tachasalila kuti): “Lyagani soni mwagani mwakusengwa ligongo lya ayila (yambone) yamwaliji nkutenda.”');
INSERT INTO "silika" VALUES(77,44,'77:44','Chisimu Uwwe iyyoyopeyo ni yatukutiji pakwalipila ŵakolosya.');
INSERT INTO "silika" VALUES(77,45,'77:45','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,46,'77:46','(E, jenumanja achimakafili!) Lyagani ni kusengwa panandi (mukandaŵi kannono kaumi wa duniyau), chisimu ŵanganyammwe ŵaleŵeleŵe.');
INSERT INTO "silika" VALUES(77,47,'77:47','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,48,'77:48','Soni pati iŵechetedwe kukwao (yanti): “Jinamani (kuswali).” Ngakwinamaga.');
INSERT INTO "silika" VALUES(77,49,'77:49','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(77,50,'77:50','Ana ni abalichi jatachijikulupilila panyuma pa aji (Qur’an)?');
INSERT INTO "silika" VALUES(78,1,'78:1','Ana akuwusyana ya chichi?');
INSERT INTO "silika" VALUES(78,2,'78:2','Ya abali jekulungwa (ja Kiyama)!');
INSERT INTO "silika" VALUES(78,3,'78:3','Jaakuŵa ŵanganyao pa jalakwejo ali nkutindana!');
INSERT INTO "silika" VALUES(78,4,'78:4','Ngwamba! Sampano chaimanye (isyesyene yakwe).');
INSERT INTO "silika" VALUES(78,5,'78:5','Soni ngwamba! Sampano chaimanye (isyesyene yakwe).');
INSERT INTO "silika" VALUES(78,6,'78:6','Ana nganituliŵika litaka kuŵa chakutandika?');
INSERT INTO "silika" VALUES(78,7,'78:7','Matumbi ni kuŵa ichili?');
INSERT INTO "silika" VALUES(78,8,'78:8','Ni tun''gumbile ŵanganyammwe mitundu jiŵili (achalume ni achakongwe).');
INSERT INTO "silika" VALUES(78,9,'78:9','Soni tuluŵisile lugono lwenu kuŵa kupumula (kwa ilu yenu).');
INSERT INTO "silika" VALUES(78,10,'78:10','Ni tuchiŵisile chilo kuŵa chiwaalo (pakusiŵilila ilu yenu ni chipi chakwe).');
INSERT INTO "silika" VALUES(78,11,'78:11','Ni tuuŵisile muusi kuŵa ndema jakupalila.');
INSERT INTO "silika" VALUES(78,12,'78:12','Ni tutaŵile pachanya penu (mawunde) nsano nagaŵili gakulimbangana.');
INSERT INTO "silika" VALUES(78,13,'78:13','Soni tuŵisile (kwalakweko) nyali jaakolela kusyene (lyuŵa).');
INSERT INTO "silika" VALUES(78,14,'78:14','Ni tutulwisye kuumila m’mawunde gawula mesi gakugwa mwamachili.');
INSERT INTO "silika" VALUES(78,15,'78:15','Kuti tukopochesye mbeju ni mmela.');
INSERT INTO "silika" VALUES(78,16,'78:16','Kwisa soni migunda jetotekane (nyambi sya itela).');
INSERT INTO "silika" VALUES(78,17,'78:17','Chisimu Lisiku Lyachiilanyo liŵele lyana ndema jakwe jisyesyene.');
INSERT INTO "silika" VALUES(78,18,'78:18','Lisiku lya chiiligombedwa lipenga, mwanti ŵanganyammwe chinchiikanganaga mikutula-mikutula.');
INSERT INTO "silika" VALUES(78,19,'78:19','Ni lichiugulidwa liwunde, mwanti lichiŵa milango-milango.');
INSERT INTO "silika" VALUES(78,20,'78:20','Nombe matumbi gachityosyedwa m’mangwakwe, mwanti gachiŵa lunyisinyisi.');
INSERT INTO "silika" VALUES(78,21,'78:21','Chisimu Jahannama jikuŵa nkwajembecheya (ŵakulemwa).');
INSERT INTO "silika" VALUES(78,22,'78:22','Kwa ŵakusumba mpika gaŵele malo gakuujila.');
INSERT INTO "silika" VALUES(78,23,'78:23','Tachiŵa nkutama mwalakwemo yaka-yaka.');
INSERT INTO "silika" VALUES(78,24,'78:24','Ngasapasya mwalakwemo kusisimila namuno yakumwa (yakumasya njota).');
INSERT INTO "silika" VALUES(78,25,'78:25','Ikaŵeje mesi gamasemule nnope ni uwowu.');
INSERT INTO "silika" VALUES(78,26,'78:26','(Kuŵa) malipilo gakulandanila (ni masengo gao).');
INSERT INTO "silika" VALUES(78,27,'78:27','Chisimu ŵanganyao ŵaliji ali ngaakwembecheya ya chiŵalanjilo.');
INSERT INTO "silika" VALUES(78,28,'78:28','Ni ŵaikaaniile imanyilo Yetu naikanilape.');
INSERT INTO "silika" VALUES(78,29,'78:29','Soni chindu chilichose tuchisunjile paachilemba m’Chitabu.');
INSERT INTO "silika" VALUES(78,30,'78:30','Basi pasyani; mwanti ngatun''jonjechesya (inepe) ikaŵeje ilagasyo (pachanya pa ilagasyo).');
INSERT INTO "silika" VALUES(78,31,'78:31','Chisimu ŵaoga (wakun’jogopa Allah) akwete kupata malo ga kupunda (ku Mbepo).');
INSERT INTO "silika" VALUES(78,32,'78:32','Matimbe gana uŵigo ni itela ya zabibu.');
INSERT INTO "silika" VALUES(78,33,'78:33','Ni ŵaali ŵangagwa maŵele ŵakulandana misingu.');
INSERT INTO "silika" VALUES(78,34,'78:34','Kwisa soni ipanda yachigumbalile.');
INSERT INTO "silika" VALUES(78,35,'78:35','Ngasaja kupikana mwalakwemo yangali mate atamuno unami.');
INSERT INTO "silika" VALUES(78,36,'78:36','(Aga ni) malipilo gakuumila kwa Ambuje ŵenu (Allah), ntuuka wakwana.');
INSERT INTO "silika" VALUES(78,37,'78:37','M''mbuje jwa kumawunde ni petaka ni yaili chilikati chakwe, Jwaukoto wejinji. Pangali ŵachachikola machili gakuŵecheta Najo.');
INSERT INTO "silika" VALUES(78,38,'78:38','Lisiku lyatiuchijima Nsimu (Jibulilu) pamo ni Achimalaika m’misela; ngasaŵecheta (jwalijose) ikaŵeje jwatachipedwa lusa ni (Allah) Jwaukoto wejinji, ni tachiŵecheta yakulungamika.');
INSERT INTO "silika" VALUES(78,39,'78:39','Lyele ni Lisiku lyakuonaonape, basi jwaakusaka aliikolochesye chilepe malo gakuujila kwa M''mbujegwe (Allah).');
INSERT INTO "silika" VALUES(78,40,'78:40','Chisimu Uwwe tukuntetela chilepe ya ilagasyo yakuŵandika. Lisiku lyataachiiwona mundu yagaalongwesye makono gakwe, nitachiŵecheta kafili kuti: “Nkongwene! Bola ningaliji litaka.”');
INSERT INTO "silika" VALUES(79,1,'79:1','Ngwaalumbila (Achimalaika) ŵaakuchuŵaga (mitima ja makafili) mwamachili.');
INSERT INTO "silika" VALUES(79,2,'79:2','Ngwaalumbila soni (Achimalaika) ŵaakutyosyaga (mitima ja ŵandu ŵakulupilila) mwambolembole.');
INSERT INTO "silika" VALUES(79,3,'79:3','Nguilumbila soni yaying''ambila mwangalechesya (munnengaleenga, mpela Achimalaika ni ndondwa).');
INSERT INTO "silika" VALUES(79,4,'79:4','Ni ŵaakutendesyana mpikisano (pakwanilisya malamusi ga Allah).');
INSERT INTO "silika" VALUES(79,5,'79:5','Ni (Achimalaika) ŵaakuŵalaganya malamusi (ga Allah pakugaŵika m’mangwakwe).');
INSERT INTO "silika" VALUES(79,6,'79:6','Lisiku lyalichigombedwa lipenga lyandanda (lyaliichitenganya chilichose ni kuwa).');
INSERT INTO "silika" VALUES(79,7,'79:7','Lichilikuichisya lipenga lyaaŵili (yosope yene nikwimuka ni kola umi).');
INSERT INTO "silika" VALUES(79,8,'79:8','Lyele lisikulyo mitima (jine) jichitetemela ni woga.');
INSERT INTO "silika" VALUES(79,9,'79:9','Meso ga (achinsyene) mitimajo gachiteteŵala.');
INSERT INTO "silika" VALUES(79,10,'79:10','(Makafili) gakasatiji: “Ana uwwe yakuona chachituuchisya mukaŵe ketu kakala (nikuŵa niumi soni)?');
INSERT INTO "silika" VALUES(79,11,'79:11','Kutendaga uwwe tuli maupa gepukuswe-pukuswe?”');
INSERT INTO "silika" VALUES(79,12,'79:12','Ŵatite (mwachisyawumi ni mwakuchindimbula): “Nakuŵaga kwele kuujilaako kusyene, basi kuchiŵa kuujila kwachasiko (kwangali ukoto kukwetu).”');
INSERT INTO "silika" VALUES(79,13,'79:13','Chisimu walakweo ni n''gumiilo umpepe.');
INSERT INTO "silika" VALUES(79,14,'79:14','Basi ajile sayi ŵanganyao tachigamba kuona ali pachanya petaka.');
INSERT INTO "silika" VALUES(79,15,'79:15','Ana jinyyiche abali ja Musa?');
INSERT INTO "silika" VALUES(79,16,'79:16','Pandema jaŵam''bilaasile Ambujegwe (Allah) pachiŵata chakutakata (chakuŵilanjikwa) Tuwaa.');
INSERT INTO "silika" VALUES(79,17,'79:17','(Nikuntenda kuti): Jaawulani kwa Firiauna, chisimu jwalakwejo jusumbile mpika.');
INSERT INTO "silika" VALUES(79,18,'79:18','Ni nkajile: “Ana nkusaka kuti n''diiswejesye?”');
INSERT INTO "silika" VALUES(79,19,'79:19','“Ni nan''jongolele kwa Ambuje ŵenu kuti mwaajogopeje?”');
INSERT INTO "silika" VALUES(79,20,'79:20','Basi niŵannosisye chisimosimo chekulungwa.');
INSERT INTO "silika" VALUES(79,21,'79:21','Nambo jwaakaaniile nikunyosya.');
INSERT INTO "silika" VALUES(79,22,'79:22','Kaneka jwachitundumalile (chikulupi) aku junankulimbichila (kwasiŵilila ŵandu kwa Allah).');
INSERT INTO "silika" VALUES(79,23,'79:23','Basi jwaasonganganyisye (asaŵi) nikwaŵilanga (ŵandu).');
INSERT INTO "silika" VALUES(79,24,'79:24','Ni jwatite: “Une ni nam''mbuje gwenu jwapenani kusyene!”');
INSERT INTO "silika" VALUES(79,25,'79:25','Basi Allah ni ŵankwembekenye ni ipotesi ligongo lya maloŵe gakwe gambesi ni gandanda .');
INSERT INTO "silika" VALUES(79,26,'79:26','Chisimu mu yalakweyi mwana chaamuko kwa mundu jwaakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(79,27,'79:27','Ana ŵanganyammwe niŵakusausya nnope kagumbidwe (kenu) kapena liwunde lyaŵalitaŵile?');
INSERT INTO "silika" VALUES(79,28,'79:28','Ŵalinyakwile nsingu wakwe nikulikolosya.');
INSERT INTO "silika" VALUES(79,29,'79:29','Ni ŵachiunichile chilo chakwe (nichipi) nikulikoposya lilanguka lyakwe (muusi).');
INSERT INTO "silika" VALUES(79,30,'79:30','Nombe litaka panyuma payalakweyo ŵalitandiche.');
INSERT INTO "silika" VALUES(79,31,'79:31','Ŵakopwesye mwalakwemo mesi gakwe ni mopo wakwe.');
INSERT INTO "silika" VALUES(79,32,'79:32','Nombe matumbi ŵagasimiiche.');
INSERT INTO "silika" VALUES(79,33,'79:33','(Kuŵa) yakunsengwasya ŵanganyammwe ni ilango yenu.');
INSERT INTO "silika" VALUES(79,34,'79:34','Basi ndema jaliichiika likungu lyekulungwa nnope (Kiyama).');
INSERT INTO "silika" VALUES(79,35,'79:35','Lyele lisikulyo mundu tachikumbuchila yaŵapanganyisye.');
INSERT INTO "silika" VALUES(79,36,'79:36','Nombe Moto Waukali wuchiŵichidwa palangulangu kwa nkulola (jwalijose).');
INSERT INTO "silika" VALUES(79,37,'79:37','Sano mundu jwaŵasumbile mpika.');
INSERT INTO "silika" VALUES(79,38,'79:38','Ni kulongosya umi wa duniya.');
INSERT INTO "silika" VALUES(79,39,'79:39','Chisimu Moto Waukali basi ni liuto lyakwe.');
INSERT INTO "silika" VALUES(79,40,'79:40','Sano jwaakogopa kuti tachija kwima pameso pa Ambujegwe (Allah), nikuwukanyaga ntima ku isako yakusakala.');
INSERT INTO "silika" VALUES(79,41,'79:41','Chisimu Litimbe lya ku Mbepo basi ni liuto lyakwe.');
INSERT INTO "silika" VALUES(79,42,'79:42','Akum''buusya (mmwe Muhammadi صلى الله عليه وسلم) ya ndema (jakwichila Kiyama kuti): “Jichiŵa chakachi?”');
INSERT INTO "silika" VALUES(79,43,'79:43','Mmwe nganinkola umanyilisi wanti nikujitomeka.');
INSERT INTO "silika" VALUES(79,44,'79:44','Kwa Ambuje ŵenupe (Allah) ni kuchipelela kwakwe (kwa umanyilisi wa Kiyama).');
INSERT INTO "silika" VALUES(79,45,'79:45','Mmwe masengo genu ni kwaatetela ŵaakujijogopa (Kiyamajo).');
INSERT INTO "silika" VALUES(79,46,'79:46','Chisimu ŵanganyao lisiku lyatachijiona (Kiyamajo) tachiŵa nti chisawu nganatama (pa duniya) ikaŵeje ligulo limo kapena kundaŵi kwakwe.');
INSERT INTO "silika" VALUES(80,1,'80:1','Jwautile sinya (Muhammadi صلى الله عليه وسلم) ni kulola kumbali.');
INSERT INTO "silika" VALUES(80,2,'80:2','Ligongo lyakum’bichiilila jwangalola.');
INSERT INTO "silika" VALUES(80,3,'80:3','Ana chichi chachimmanyisye kuti mwine jwalakwejo kuŵa nkuliswejesya (ku ilemwa)?');
INSERT INTO "silika" VALUES(80,4,'80:4','Mwine chakumbuchile, basi nikunkamuchisya kumbuchilako?');
INSERT INTO "silika" VALUES(80,5,'80:5','Nambo mundu jwaliweni kusichila,');
INSERT INTO "silika" VALUES(80,6,'80:6','Basi jwalakwejo mmwe ni nkunkosoŵalila;');
INSERT INTO "silika" VALUES(80,7,'80:7','Pangalitu ulemwa kukwenu naga ngaakuliswejesya (ku ukafili).');
INSERT INTO "silika" VALUES(80,8,'80:8','Nambo mundu jwam’bichilile aku juchikangamalaga,');
INSERT INTO "silika" VALUES(80,9,'80:9','Soni jwalakwejo ali nkun''jogopa (Allah),');
INSERT INTO "silika" VALUES(80,10,'80:10','Basi ya jwalakwejo mmwe ni nkuikwanjila;');
INSERT INTO "silika" VALUES(80,11,'80:11','Ngwamba (ngasintenda iyyo)! Chisimu galakwe (ma Ȃyaga) gaŵele chikumbusyo.');
INSERT INTO "silika" VALUES(80,12,'80:12','Basi jwaakusaka talikumbusye najo (Qur’anijo).');
INSERT INTO "silika" VALUES(80,13,'80:13','(Jili) mu ikalakala yakuchimbichikwa.');
INSERT INTO "silika" VALUES(80,14,'80:14','Yapenani, yakuswejesyedwa.');
INSERT INTO "silika" VALUES(80,15,'80:15','Ili m’makono mwa akulemba (Achimalaika).');
INSERT INTO "silika" VALUES(80,16,'80:16','Ŵakuchimbichika, ŵaakunda (malamusi).');
INSERT INTO "silika" VALUES(80,17,'80:17','Pamasile palaniwe mundu (kafili), kanila kwakwe kwalakweku ni kwantiuli!');
INSERT INTO "silika" VALUES(80,18,'80:18','Ana an''gumbile kuumila mu chinduchi?');
INSERT INTO "silika" VALUES(80,19,'80:19','Kuumila m’mesi (gaŵalume ni ŵakongwe) an''gumbile, ni kumpanjila mpango (wakagumbidwe kakwe).');
INSERT INTO "silika" VALUES(80,20,'80:20','Kaneka naakun''jepepachisyaga litala (lyakopochela ku duniya).');
INSERT INTO "silika" VALUES(80,21,'80:21','Kaneko nakasampaga chiwa ni kunsiika m’malembe.');
INSERT INTO "silika" VALUES(80,22,'80:22','Soni patachisakapo tachinsyusya.');
INSERT INTO "silika" VALUES(80,23,'80:23','Ngwamba! Chisimu (kafili) nganakwanilisyepe yannamwile (Allah).');
INSERT INTO "silika" VALUES(80,24,'80:24','Basi achetelele mundu ya yakulya yakwe.');
INSERT INTO "silika" VALUES(80,25,'80:25','Chisimu Uwwe tukasakusula mesi (gawula) mwamachili.');
INSERT INTO "silika" VALUES(80,26,'80:26','Kaneka ni tukasaligangaga litaka kuliganga kusyene.');
INSERT INTO "silika" VALUES(80,27,'80:27','Ni tukasakoposya mwalakwemo songolo.');
INSERT INTO "silika" VALUES(80,28,'80:28','Kwisa soni isogosi ya zabibu ni maupulu (gakulya ilango).');
INSERT INTO "silika" VALUES(80,29,'80:29','Ni kwisa soni isogosi ya zaituni (yakwenga mauta) ni itela yatende.');
INSERT INTO "silika" VALUES(80,30,'80:30','Ni migunda jaitela yejinji.');
INSERT INTO "silika" VALUES(80,31,'80:31','Kwisa soni isogosi (yamitundu mitundu) ni mopo.');
INSERT INTO "silika" VALUES(80,32,'80:32','(Kuŵa) yakunsengwasya ŵanganyammwe ni ilango yenu.');
INSERT INTO "silika" VALUES(80,33,'80:33','Basi ndema jaliichiika lipenga lyakusiŵa m’mawiwi (ligongo lya machili gakwe).');
INSERT INTO "silika" VALUES(80,34,'80:34','Lyele lisikulyo mundu tachatila achimwenegwe.');
INSERT INTO "silika" VALUES(80,35,'80:35','Kwisa soni mamagwe ni babagwe.');
INSERT INTO "silika" VALUES(80,36,'80:36','Ni kwisa soni ŵankwakwe ni ŵanache ŵakwe.');
INSERT INTO "silika" VALUES(80,37,'80:37','Mundu jwalijose mwa ŵanganyao lyele lisikulyo tachikola yakunkwanila (kuntendekasya kuti akakosoŵalila ya jwine.)');
INSERT INTO "silika" VALUES(80,38,'80:38','Ngope sine lyele lisikulyo sichiŵa syakumelemenda.');
INSERT INTO "silika" VALUES(80,39,'80:39','Syakusechelela ni kusengwa (pakupikana yakuja ku Mbepo).');
INSERT INTO "silika" VALUES(80,40,'80:40','Sano ngope sine lyele lisikulyo sichipakala luwundu.');
INSERT INTO "silika" VALUES(80,41,'80:41','Sichiŵa siunichilidwe ni chipi.');
INSERT INTO "silika" VALUES(80,42,'80:42','Ŵanganyao ni ŵali achimakafili ŵakusakasya nnope.');
INSERT INTO "silika" VALUES(81,1,'81:1','Pandema jalichikulunjidwa lyuŵa (nikutyoka lilanguka lyakwe).');
INSERT INTO "silika" VALUES(81,2,'81:2','Ni ndema jasichipatuka ndondwa.');
INSERT INTO "silika" VALUES(81,3,'81:3','Ni ndema jagachijendesyedwa matumbi (paagachuŵa m’mangwakwe).');
INSERT INTO "silika" VALUES(81,4,'81:4','Ni ndema jasichilechedwa ngamiya syana maŵele gepele.');
INSERT INTO "silika" VALUES(81,5,'81:5','Ni ndema jaichisonganganyisyidwa inyama yamwitinji.');
INSERT INTO "silika" VALUES(81,6,'81:6','Ni ndema jasichikolesyedwa mbwani moto.');
INSERT INTO "silika" VALUES(81,7,'81:7','Ni ndema jajichilumbikanyisyidwa mitima (ku ilu yakwe).');
INSERT INTO "silika" VALUES(81,8,'81:8','Ni ndema jachachiwusyidwa mwanache jwankwongwe jwaŵasichidwe ali jwaumi.');
INSERT INTO "silika" VALUES(81,9,'81:9','Kuti ŵawulajidwe ni ulemwachi?');
INSERT INTO "silika" VALUES(81,10,'81:10','Ni ndema jaichipindikulidwa ikalakala (mugalembedwe masengo gamundu).');
INSERT INTO "silika" VALUES(81,11,'81:11','Ni ndema jalichitandukulidwa liwunde.');
INSERT INTO "silika" VALUES(81,12,'81:12','Ni ndema jauchikwisilidwa Moto Waukali.');
INSERT INTO "silika" VALUES(81,13,'81:13','Ni ndema jajichiŵandichisyidwa Mbepo.');
INSERT INTO "silika" VALUES(81,14,'81:14','Mundu jwalijose tachimanyilila yayiche nayo.');
INSERT INTO "silika" VALUES(81,15,'81:15','Basi ngusilumbila ndondwa syasikusisika muusi.');
INSERT INTO "silika" VALUES(81,16,'81:16','(Ni) syasikwendaga (chilo kaneka) ni kusisika pakutiŵila.');
INSERT INTO "silika" VALUES(81,17,'81:17','Ni nguchilumbila soni chilo ndema jachikwika ni kutyoka.');
INSERT INTO "silika" VALUES(81,18,'81:18','Ni ngukulumbila soni kumasikusiku ndema jakukulanguka.');
INSERT INTO "silika" VALUES(81,19,'81:19','Chisimu aji (Qur’aniji) gaŵele maloŵe ga (kulungusyidwa ni) Ntenga Jwakuchimbichika (Jibulilu).');
INSERT INTO "silika" VALUES(81,20,'81:20','Nsyene machili, soni jwana uchimbichimbi wapenani kwa (Allah) Nsyene Arishi (Chindanda Chaulungu).');
INSERT INTO "silika" VALUES(81,21,'81:21','Jwakundidwa malamusi kwinaniko (ni Achimalaika) soni jwakulupichika.');
INSERT INTO "silika" VALUES(81,22,'81:22','Soni Nnongo n''jenuju (Muhammadi صلى الله عليه وسلم) nganaŵa jwamasoka.');
INSERT INTO "silika" VALUES(81,23,'81:23','Soni chisimu chene (Muhammadi صلى الله عليه وسلم) ŵam''bweni jwalakwe (Jibulilu mu kagumbidwe kakwe kasyesyene) kuchipelela chameso chakuonechela (cha ku ngopoko lyuŵa).');
INSERT INTO "silika" VALUES(81,24,'81:24','Soni jwalakwe nganijuŵa jwausuchi pa (umanyilisi wa) yakusisika.');
INSERT INTO "silika" VALUES(81,25,'81:25','Soni aji (Qur’an) nganigaŵa maloŵe ga shetani jwakuŵinjidwa.');
INSERT INTO "silika" VALUES(81,26,'81:26','Nambi kwapi nkuja?');
INSERT INTO "silika" VALUES(81,27,'81:27','Nganijiŵa aji (Qur’an) ikaŵeje chikumbusyo cha iwumbe yosope.');
INSERT INTO "silika" VALUES(81,28,'81:28','Kwa mundu jwaakusaka mwa jenumanja kuti ajongoche.');
INSERT INTO "silika" VALUES(81,29,'81:29','Ni nganim''ba nsachile, ikaŵeje akasache kaje Allah M’mbuje jwa iwumbe yosope.');
INSERT INTO "silika" VALUES(82,1,'82:1','Pandema jalichipasuka liwunde.');
INSERT INTO "silika" VALUES(82,2,'82:2','Ni ndema jasichilakatika ndondwa.');
INSERT INTO "silika" VALUES(82,3,'82:3','Ni ndema jasichigumuchila mbwani.');
INSERT INTO "silika" VALUES(82,4,'82:4','Ni ndema jagachiukulidwa malembe.');
INSERT INTO "silika" VALUES(82,5,'82:5','Mundu jwalijose juchimanyilila yajwalongwesye (kupanganya) ni yajwalesile munyuma.');
INSERT INTO "silika" VALUES(82,6,'82:6','E mmwe amundu! Ana ni chichi chinnyenjile kwa (kwikanila kunnyosya) M’mbuje gwenu (Allah) Jwantuka wejinji?');
INSERT INTO "silika" VALUES(82,7,'82:7','Ajula jwaŵan''gumbile, ni kunkolosya pa kunnandanya (iŵalo yenu) soni ni kun''golosya (nsingu wenu).');
INSERT INTO "silika" VALUES(82,8,'82:8','Mukawoneche kalikose kaŵasachile (kuti m''bonecheje) ŵampanganyisye.');
INSERT INTO "silika" VALUES(82,9,'82:9','Ngwamba! Nambo kuti ŵanganyammwe nkukanila ya malipilo.');
INSERT INTO "silika" VALUES(82,10,'82:10','Soni chisimu kwa jenumanja kwana (Achimalaika) ŵakunnolela (ni kusunga itendo yenu). ');
INSERT INTO "silika" VALUES(82,11,'82:11','Ŵakuchimbichika, ŵakulemba.');
INSERT INTO "silika" VALUES(82,12,'82:12','Akumanyilila yankuntenda.');
INSERT INTO "silika" VALUES(82,13,'82:13','Chisimu atenda ngoto chachiŵa nchindimba (mu Mbepo Jakusengwasya).');
INSERT INTO "silika" VALUES(82,14,'82:14','Sano ŵandu ŵakusakasya nnope, chisimu chene tachiŵa m’Moto Waukali.');
INSERT INTO "silika" VALUES(82,15,'82:15','Chachiujinjila Lisiku Lyamalipilo.');
INSERT INTO "silika" VALUES(82,16,'82:16','Soni ŵanganyao ngasaŵa asoŵile mwalakwemo (pakopoka ata panandi).');
INSERT INTO "silika" VALUES(82,17,'82:17','Ana ni chichi champaka chimmanyisye ya Lisiku Lyamalipilo?');
INSERT INTO "silika" VALUES(82,18,'82:18','Soni ana ni chichi champaka chimmanyisye ya Lisiku Lyamalipilo?');
INSERT INTO "silika" VALUES(82,19,'82:19','Lyele ni lisiku lyangakola mundu chilichose chanti ni kunkamuchisya mundu jwine. Nombe ulamusi (wosope) lyele lisikulyo uchiŵa wa Allah pe.');
INSERT INTO "silika" VALUES(83,1,'83:1','Ipotesi ni yao ŵakupuungusya (miliingo).');
INSERT INTO "silika" VALUES(83,2,'83:2','Aŵala ŵaakutiji naga ndema jaakulilinjila ku ŵandu (pakwasuma indu yao) akasaligumbachisyaga.');
INSERT INTO "silika" VALUES(83,3,'83:3','Nambo pa ndema jaakwaaliinjila (ŵanduo) kapena kwapimila (pakwasumisya indu) akupungusyaga.');
INSERT INTO "silika" VALUES(83,4,'83:4','Ana ngakuganisya ŵanganyao yanti chachiukulidwa (m’malembe);');
INSERT INTO "silika" VALUES(83,5,'83:5','Pa Lisiku Lyekulungwa nnope;');
INSERT INTO "silika" VALUES(83,6,'83:6','Lisiku lyatachijima ŵandu pameso pa M’mbuje jwa iwumbe yosope?');
INSERT INTO "silika" VALUES(83,7,'83:7','Yakuona! Chisimu yakulembedwa ya (masengo ga) ŵandu ŵakusakasya nnope ili mu Sijjin.');
INSERT INTO "silika" VALUES(83,8,'83:8','Ana ni chichi champaka chimmanyisye ya Sijjin?');
INSERT INTO "silika" VALUES(83,9,'83:9','Chele ni chitabu (chekulungwa) chachilembedwe (nkati mwakwe yakusakalape).');
INSERT INTO "silika" VALUES(83,10,'83:10','Ipotesi lyele lisikulyo taachiipata ŵakanila.');
INSERT INTO "silika" VALUES(83,11,'83:11','Aŵala ŵakukanila ya Lisiku Lyamalipilo.');
INSERT INTO "silika" VALUES(83,12,'83:12','Ni pangali jwampaka alikanile lyalakwelyo ikaŵeje jwalijose jwakusumba mpika jwasambi syejinji.');
INSERT INTO "silika" VALUES(83,13,'83:13','Pandema jakusoomedwa kukwakwe ma Ȃya Getu akasatiji: “Asi ni adisiadisi sya ŵandu ŵandanda.”');
INSERT INTO "silika" VALUES(83,14,'83:14','Ngwamba! Nambo kuti m’mitima mwao mwana nkuwo wa (ilemwa) yaaŵele ali nkupanganya. ');
INSERT INTO "silika" VALUES(83,15,'83:15','Yakuona! Chisimu ŵanganyao lyele lisikulyo kwa M’mbuje gwao (Allah) tachisiŵilidwa (mwanti ngaaja kum’bona).');
INSERT INTO "silika" VALUES(83,16,'83:16','Kaneka chisimu ŵanganyao tachiujinjila Moto Waukali.');
INSERT INTO "silika" VALUES(83,17,'83:17','Kaneko ni kuchiŵechetedwa kuti: “Ayi ni ayilayila yamwaliji nkuikanila.”');
INSERT INTO "silika" VALUES(83,18,'83:18','Yakuona! Chisimu yakulembedwa ya (masengo ga) atenda ngoto ili mu Illiyyun.');
INSERT INTO "silika" VALUES(83,19,'83:19','Ana ni chichi champaka chimmanyisye ya Illiyyun?');
INSERT INTO "silika" VALUES(83,20,'83:20','Chalakwecho ni chitabu (chekulungwa) chachilembedwe (nkati mwakwe yambonepe).');
INSERT INTO "silika" VALUES(83,21,'83:21','Akuchiŵichilaga umboni (Achimalaika) ŵaŵandichisyidwe (kwa Allah).');
INSERT INTO "silika" VALUES(83,22,'83:22','Chisimu atenda ngoto tachiŵa m’chindimba (mu Mbepo Jakusengwasya).');
INSERT INTO "silika" VALUES(83,23,'83:23','Ali pachanya pa indanda yetandiche chenene aku achilolaga (ku chindimba chaŵapele ni kum''bona soni Allah).');
INSERT INTO "silika" VALUES(83,24,'83:24','Tinchikumanyilila pangope syao kumelemenda kwa chindimba.');
INSERT INTO "silika" VALUES(83,25,'83:25','Takamwesyedweje nkologo wambone nnope wesiŵe (ni isiŵilo yakulimbangana).');
INSERT INTO "silika" VALUES(83,26,'83:26','Kumbesi kwa kumwako (chikukanunjileje luwumba lwa) miski. Sano kuti akaipate yele basi apikisane akupikisana (pakutenda yambone).');
INSERT INTO "silika" VALUES(83,27,'83:27','Soni chakuwanganyichisya chakwe (nkologowo chigakaŵe) mesi gakuumila mu Tasnim.');
INSERT INTO "silika" VALUES(83,28,'83:28','Jwalakwejo ni tutumule jwachakamweje (ŵandu) ŵakuŵandichisyidwa (kwa Allah).');
INSERT INTO "silika" VALUES(83,29,'83:29','Chisimu (ku duniya) ŵandu ŵaŵanneŵele (Allah) ŵaliji nkwaseka ŵandu ŵakulupilila.');
INSERT INTO "silika" VALUES(83,30,'83:30','Mwanti ndema jaŵaliji nkupita (ŵakulupililao) paali ŵanganyao ŵaliji nkukupililana meso (mwakuchembulusya).');
INSERT INTO "silika" VALUES(83,31,'83:31','Sano naga atite agalauche kuja kwaachin''jao ŵagalaukaga ali nkusengwa (ligongo lya kuchembulusya kwao).');
INSERT INTO "silika" VALUES(83,32,'83:32','Soni naga ali ŵawenimo ŵatiji: “Chisimu ŵanganyaŵa ni ŵali ŵakupotela nnope.”');
INSERT INTO "silika" VALUES(83,33,'83:33','Nambotu (achimakafilio) nganatumisyidwa kuŵa ŵakwalolela (Asilamu nikusunga itendo yao).');
INSERT INTO "silika" VALUES(83,34,'83:34','Nambo lelojino (lisiku lya Kiyama) ŵandu ŵaŵakulupilile chaŵe ali nkugaseka makafili.');
INSERT INTO "silika" VALUES(83,35,'83:35','Ali pachanya pa indanda yetandiche chenene aku achigalolaga (makafiligo galinkulagasika).');
INSERT INTO "silika" VALUES(83,36,'83:36','Ana alipiidwe achimakafili pa yaŵaliji nkutenda!?');
INSERT INTO "silika" VALUES(84,1,'84:1','Pandema jalichipapuka liwunde.');
INSERT INTO "silika" VALUES(84,2,'84:2','Ni kwisa kunda lilamusi lya M’mbujeegwe, soni liichiŵajilwa (kuti likunde).');
INSERT INTO "silika" VALUES(84,3,'84:3','Ni ndema jalichijasyisyidwa litaka (pakusyaga matumbi ni ikwesya).');
INSERT INTO "silika" VALUES(84,4,'84:4','Ni kwisa kuponya kusa (litakalyo) yayaliji nkati mwakwe nikuŵa mwangali chachipali.');
INSERT INTO "silika" VALUES(84,5,'84:5','Ni kwisa kunda lilamusi lya M’mbujeegwe, soni liichiŵajilwa (kuti likunde).');
INSERT INTO "silika" VALUES(84,6,'84:6','E mmwe amundu! Chisimutu mmwe nkukangamala wa kwa Ambuje ŵenu (Allah) nchilimbichilaga (kutenda itendo yambone kapena yakusakala), basi ni tinchisimangana nayo (pa lisiku lya Kiyamalyo).');
INSERT INTO "silika" VALUES(84,7,'84:7','Sano mundu jwatachipedwa chitabu chakwe ku nkono wakwe wan''dyo.');
INSERT INTO "silika" VALUES(84,8,'84:8','Basi jwalakwe tachiŵalanjidwa kuŵalanjidwa kwakwepepala.');
INSERT INTO "silika" VALUES(84,9,'84:9','Soni ni tachiujila kwa achalongo achin''jakwe ali jwakusengwa.');
INSERT INTO "silika" VALUES(84,10,'84:10','Nambo mundu jwatachipedwa chitabu chakwe cha kunyuma kwa n''gongo wakwe.');
INSERT INTO "silika" VALUES(84,11,'84:11','Basi jwalakwe, tachichiŵilanga chonasiko (kuti bola agambe kuwa, nambo ngasiyikomboleka).');
INSERT INTO "silika" VALUES(84,12,'84:12','Ni tachijinjila ku Moto Wakulapuka.');
INSERT INTO "silika" VALUES(84,13,'84:13','Chisimu jwalakwe jwaliji jwakusengwa (pajwaliji) ni achalongo achin''jakwe (ku duniya).');
INSERT INTO "silika" VALUES(84,14,'84:14','Chisimu jwalakwe jwasimichisyaga kuti ngasaujila (kwa Allah).');
INSERT INTO "silika" VALUES(84,15,'84:15','Elo! Chisimu Ambujegwe ŵaliji ali nkunnolechesya.');
INSERT INTO "silika" VALUES(84,16,'84:16','Basi ngukulumbila kuchejela kwegulo.');
INSERT INTO "silika" VALUES(84,17,'84:17','Kwisa soni chilo ni yachikusonganganyaga.');
INSERT INTO "silika" VALUES(84,18,'84:18','Ni mwesi ndema jaukwanila lilanguka (lyakwe).');
INSERT INTO "silika" VALUES(84,19,'84:19','Chisimu ŵanganyammwe chinsimanganeje nako kaŵe kane kutyochela pa kaŵe kane (paumi wa duniya uno mpaka kwa Akhera).');
INSERT INTO "silika" VALUES(84,20,'84:20','Ana akwete chichi ŵanganyao chikwatendekasya kuti akakulupilila?');
INSERT INTO "silika" VALUES(84,21,'84:21','Soni pati jichisoomedwaga kukwao Qur’an ŵangasujudu!');
INSERT INTO "silika" VALUES(84,22,'84:22','Nambo kuti achimakafili agamba kanila basi.');
INSERT INTO "silika" VALUES(84,23,'84:23','Sano Allah ni jwaakuyimanya chenene yaakusonganganya ŵanganyao.');
INSERT INTO "silika" VALUES(84,24,'84:24','Basi mwatagulilani ya ilagasyo yakupoteka nnope.');
INSERT INTO "silika" VALUES(84,25,'84:25','Ikaŵeje aŵala ŵaakulupilile ni kutendaga yambone, akwete kupata ŵanganyao malipilo gangamala.');
INSERT INTO "silika" VALUES(85,1,'85:1','Ngulilumbila liwunde lyalikwete ndondwa syekulungwa-kulungwa ni mwakwenda mwakwe.');
INSERT INTO "silika" VALUES(85,2,'85:2','Ni Lisiku Lyeŵichidwe chilanga.');
INSERT INTO "silika" VALUES(85,3,'85:3','Kwisa soni nkuŵichila ni nkuŵichilidwa umboni.');
INSERT INTO "silika" VALUES(85,4,'85:4','Pamasile paŵalaniwe achinsyene masimbo,');
INSERT INTO "silika" VALUES(85,5,'85:5','(Ga) moto wawakwete sasu (syejinji syakuukolechesya ni kwachomela ŵandu ŵakulupilila).');
INSERT INTO "silika" VALUES(85,6,'85:6','Pandema janti ŵanganyao ŵaliji ali chitamile mungulugulu mwakwe.');
INSERT INTO "silika" VALUES(85,7,'85:7','Soni ŵanganyao ŵaliji aku ali nkulolela yaŵaliji nkwatenda ŵakulupilila (yakwachoma motoyo).');
INSERT INTO "silika" VALUES(85,8,'85:8','Ni nganauwona ulemwa uliose kwa jamanjao ikaŵeje ligongo lyanti ŵaliji nkunkulupilila Allah Jwamachili gakupunda, Jwakulapilildwa kusyesyene. ');
INSERT INTO "silika" VALUES(85,9,'85:9','Ajula Jwauli wakwe uchimwene wa kumawunde ni petaka, sanotu Allah pa chindu chilichose aŵele Mboni.');
INSERT INTO "silika" VALUES(85,10,'85:10','Chisimu aŵala ŵaŵalagasisye ŵakulupilila ŵachilume ni ŵakulupilila ŵachikongwe (pakwachoma moto) kaneka ni ngatenda toba, basi akwete kupata ilagasyo ya Jahannama, soni akwete kupata ilagasyo yakutinisya.');
INSERT INTO "silika" VALUES(85,11,'85:11','Chisimu aŵala ŵaakulupilile ni kutendaga yambone, akwete kupata Matimbe ga ku Mbepo gasikujilima cha pasi pakwe sulo. Kwalakweko nikupunda kwekulungwa.');
INSERT INTO "silika" VALUES(85,12,'85:12','Chisimu kapeleche ka ilagasyo ka Ambuje ŵenu (Allah) kaŵele kaukali nnope.');
INSERT INTO "silika" VALUES(85,13,'85:13','Chisimu Jwelepejo ni juŵatandisye (gumba iwumbe) soni ni jutachiyiusya-usya (kuigumba panyuma pakonasika).');
INSERT INTO "silika" VALUES(85,14,'85:14','Soni Jwalakwe ni Jwakululuka kusyene Jwachinonyelo chejinji.');
INSERT INTO "silika" VALUES(85,15,'85:15','Nsyene Arish (Chindanda Chaulungu), Jwakuchimbichika kusyene.');
INSERT INTO "silika" VALUES(85,16,'85:16','Jwakutenda yajukusaka mwangalepeleka.');
INSERT INTO "silika" VALUES(85,17,'85:17','Ana jinyyiche abali ja mikutula ja asilikali?');
INSERT INTO "silika" VALUES(85,18,'85:18','Achina Firiauna ni achina Samuda?');
INSERT INTO "silika" VALUES(85,19,'85:19','Nambo kuti achimakafili ali mu kukanila basi.');
INSERT INTO "silika" VALUES(85,20,'85:20','Sano Allah ni ŵasyungulile chakunyuma kwao.');
INSERT INTO "silika" VALUES(85,21,'85:21','Nambo jalakweji ni Qur’an Jakuchimbichika.');
INSERT INTO "silika" VALUES(85,22,'85:22','(Jilembedwe) mu Ubawo Wagosedwa.');
INSERT INTO "silika" VALUES(86,1,'86:1','Ngulilumbila liwunde ni chakwisa chilo.');
INSERT INTO "silika" VALUES(86,2,'86:2','Ana ni chichi champaka chimmanyisye ya chakwisa chilo?');
INSERT INTO "silika" VALUES(86,3,'86:3','(Jalakwejo ni) ndondwa jakupowola (chipi ligongo lya kuŵala nnope).');
INSERT INTO "silika" VALUES(86,4,'86:4','Pangali mundu jwalijose ikaŵeje kwa jwalakwejo kwana nkunnolela (jwaakusunga itendo yakwe).');
INSERT INTO "silika" VALUES(86,5,'86:5','Basi achetelele mundu, ana agumbidwe kuumila ku chichi?');
INSERT INTO "silika" VALUES(86,6,'86:6','Agumbidwe kuumila ku mesi gakusumbika.');
INSERT INTO "silika" VALUES(86,7,'86:7','Gakopokaga kutyochela chilikati cha maupa ga n''gongo (wa ŵalume) ni maupa ga chidali (cha ŵakongwe).');
INSERT INTO "silika" VALUES(86,8,'86:8','Chisimu Jwalakwe (Allah) pakum''buchisya (kuwumi mundujo) aŵele Jwakombola kusyene.');
INSERT INTO "silika" VALUES(86,9,'86:9','Lisiku lyasichionechela papaswela asili (syosope).');
INSERT INTO "silika" VALUES(86,10,'86:10','Ni ngasakola (mundujo) machili galigose namuno nkulupusyo.');
INSERT INTO "silika" VALUES(86,11,'86:11','Ngukulumbila kwinani kwagwisyagwisya wula.');
INSERT INTO "silika" VALUES(86,12,'86:12','Ni ngulilumbila soni litaka lyaliganjikaga (pakoposya mmela).');
INSERT INTO "silika" VALUES(86,13,'86:13','Chisimu jalakwe (Qur’aniji) gaŵele maloŵe gakulekanganya (chilikati cha yakuona ni yaunami).');
INSERT INTO "silika" VALUES(86,14,'86:14','Soni jalakweji nganijiŵa chindu chambenje.');
INSERT INTO "silika" VALUES(86,15,'86:15','Chisimu ŵanganyao akuŵa ali nkutenda malindi (kwa Asilamu).');
INSERT INTO "silika" VALUES(86,16,'86:16','None ni ngwauchisya malindi (gaogo pangaapa ilagasyo chitema kuti alilechelele).');
INSERT INTO "silika" VALUES(86,17,'86:17','Basi, mwalechelesyani achimakafili, mwalechelelani panandipe (chaiwone).');
INSERT INTO "silika" VALUES(87,1,'87:1','Liswejesyani lina lya Ambuje ŵenu (Allah) Ŵapenani kusyene.');
INSERT INTO "silika" VALUES(87,2,'87:2','Aŵala ŵaŵagumbile (chilichose) ni kuchikolosya.');
INSERT INTO "silika" VALUES(87,3,'87:3','Soni Aŵala ŵaŵapimiile (chilichose mwachiichiŵeela) ni kuchijongolela (ku yaŵachipimileyo).');
INSERT INTO "silika" VALUES(87,4,'87:4','Soni Aŵala ŵaakumesya mopo.');
INSERT INTO "silika" VALUES(87,5,'87:5','Ni kuwusyusya kuŵa wejumu ŵakupiililila.');
INSERT INTO "silika" VALUES(87,6,'87:6','Chitunsoomesye ni ngan''diŵalila.');
INSERT INTO "silika" VALUES(87,7,'87:7','Ikaŵeje yasachile Allah, chisimu Jwalakwe jukumanyililaga yakuonechela ni yayikasasisika.');
INSERT INTO "silika" VALUES(87,8,'87:8','Ni chitun''jepepachisye litala lyakwalukangana (lyakutendela yambone).');
INSERT INTO "silika" VALUES(87,9,'87:9','Basi kumbusyani naga kumbusyako tikuŵe kwakamuchisya.');
INSERT INTO "silika" VALUES(87,10,'87:10','Talikumbusye mundu jwaakun''jogopa (Allah).');
INSERT INTO "silika" VALUES(87,11,'87:11','Ni takuŵambale (mundu) jwakusakala nnope.');
INSERT INTO "silika" VALUES(87,12,'87:12','Ajula jwatakajinjile ku Moto Wekulungwa.');
INSERT INTO "silika" VALUES(87,13,'87:13','Soni ngachija kumpata chiwa mwalakwemo, nambo soni ngaaja kuŵa ni umi (wambone).');
INSERT INTO "silika" VALUES(87,14,'87:14','Pamasile paapundile mundu jwaliswejesye (ntima wakwe).');
INSERT INTO "silika" VALUES(87,15,'87:15','Ni akuŵa ali nkulikumbuchila Lina lya Ambujegwe (pakulikolanga) aku achiswaliji.');
INSERT INTO "silika" VALUES(87,16,'87:16','Nambo ŵanganyammwe nkulongosya umi wa duniya.');
INSERT INTO "silika" VALUES(87,17,'87:17','Kutendaga Akhera ni jambone nnope soni jandamo.');
INSERT INTO "silika" VALUES(87,18,'87:18','Chisimu yalakweyi yili mu ikalakala yandanda.');
INSERT INTO "silika" VALUES(87,19,'87:19','Ikalakala ya Ibrahima ni Musa.');
INSERT INTO "silika" VALUES(88,1,'88:1','Ana jinyyiche abali ja chakuunichila (ŵandu ni yogoyo yakwe, jajili Kiyama)?');
INSERT INTO "silika" VALUES(88,2,'88:2','Ngope sine lyele lisikulyo sichiŵa syakunyosyeka.');
INSERT INTO "silika" VALUES(88,3,'88:3','Syasyapanganyisye masengo (gasambi ku duniya), syakupela (nikwaluka ku Akhera).');
INSERT INTO "silika" VALUES(88,4,'88:4','Sichijinjila ku Moto Wakupoteka nnope.');
INSERT INTO "silika" VALUES(88,5,'88:5','Sichimwesyedwa mu tutumule jwansemule kusyene.');
INSERT INTO "silika" VALUES(88,6,'88:6','Ngasakola ŵanganyao yakulya ikaŵeje miŵa janchongwe.');
INSERT INTO "silika" VALUES(88,7,'88:7','Jangajimbasya soni jangakamuchisya ku sala.');
INSERT INTO "silika" VALUES(88,8,'88:8','Ngope sine lyele lisikulyo sichiŵa m’chindimba.');
INSERT INTO "silika" VALUES(88,9,'88:9','Ligongo lya masengo gakwe (gambone) sichiŵa syakusengwa.');
INSERT INTO "silika" VALUES(88,10,'88:10','Ku Mbepo Japenani.');
INSERT INTO "silika" VALUES(88,11,'88:11','Ngasisija kupikana kwalakweko yangali mate.');
INSERT INTO "silika" VALUES(88,12,'88:12','Kweleko kwana tutumule jwakujilimaga.');
INSERT INTO "silika" VALUES(88,13,'88:13','Kweleko kwana itengu yauchimbichimbi yenyakulidwe.');
INSERT INTO "silika" VALUES(88,14,'88:14','Ni migawo jeŵichidwe (chilepe).');
INSERT INTO "silika" VALUES(88,15,'88:15','Ni misago jejalidwe mu ntungo wambone.');
INSERT INTO "silika" VALUES(88,16,'88:16','Ni yakutandika yaofuofu yetandichidwe posepose.');
INSERT INTO "silika" VALUES(88,17,'88:17','Ana ngakujiona ngamiya yajatite kagumbidwe?');
INSERT INTO "silika" VALUES(88,18,'88:18','Ni liwunde yalyatite kanyakulidwe?');
INSERT INTO "silika" VALUES(88,19,'88:19','Ni matumbi yagatite kasimichidwe?');
INSERT INTO "silika" VALUES(88,20,'88:20','Ni litaka yalyatite katandichidwe?');
INSERT INTO "silika" VALUES(88,21,'88:21','Basi kumbusyani, chisimu mmwejo ni nkukumbusya.');
INSERT INTO "silika" VALUES(88,22,'88:22','Nganim''batu mmwe ŵakwakanganichisya ŵanganyao (chikulupi).');
INSERT INTO "silika" VALUES(88,23,'88:23','Ikaŵeje jwatagalauche (kuleka masengo gambone) ni kufulu (kuleka Usilamu),');
INSERT INTO "silika" VALUES(88,24,'88:24','Basi Allah tachinnagasya ni ilagasyo yekulungwa nnope.');
INSERT INTO "silika" VALUES(88,25,'88:25','Chisimu Kukwetu pe ni kwakuujila kwao.');
INSERT INTO "silika" VALUES(88,26,'88:26','Soni chisimu kuŵalanjidwa kwao kuli Nowe.');
INSERT INTO "silika" VALUES(89,1,'89:1','Ngukulumbila kumasikusiku.');
INSERT INTO "silika" VALUES(89,2,'89:2','Ni ilo likumi (yandanda yamwesi wa Mfungotatu). ');
INSERT INTO "silika" VALUES(89,3,'89:3','Ni (indu yayili) yiŵiliyiŵili kwisa soni (yayili) chimochimo.');
INSERT INTO "silika" VALUES(89,4,'89:4','Ni chilo ndema jachikuŵigula.');
INSERT INTO "silika" VALUES(89,5,'89:5','Ana mu yalakweyi ngaŵa mwana kulumbila kwakwanila, kwa jwakwete lunda?');
INSERT INTO "silika" VALUES(89,6,'89:6','Ana nganinyyiona yaŵatite Ambuje ŵenu kaape ipotesi ŵanya Adi?');
INSERT INTO "silika" VALUES(89,7,'89:7','Ŵa ku Irama; achalewulewu mpela misaati?');
INSERT INTO "silika" VALUES(89,8,'89:8','Ŵanganagumbidweje ŵane ŵanti chisawu ŵanganyao mu ilambo (ine)?');
INSERT INTO "silika" VALUES(89,9,'89:9','Ni ŵanya Asamuda ŵaŵaliji nkusokola maganga pachiŵata (chakuŵilanjikwa Wadi Qura kuŵa majumba gao)?');
INSERT INTO "silika" VALUES(89,10,'89:10','Kwisa soni Firiauna nsyene ichili (yakulimbanganyichisya uchimwene wakwe)?');
INSERT INTO "silika" VALUES(89,11,'89:11','Aŵala ŵana kusumbaga mpika mu ilambo (pakusakasya).');
INSERT INTO "silika" VALUES(89,12,'89:12','Ni ŵatupiye mwalakwemo kuwatanganya.');
INSERT INTO "silika" VALUES(89,13,'89:13','Basi Ambuje ŵenu ŵapungulile ŵanganyao mitundu ja ilagasyo (yakupoteka nnope).');
INSERT INTO "silika" VALUES(89,14,'89:14','Chisimu Ambuje ŵenu aŵele Ŵang''unulila (masengo ga ŵandu, mwanti chachalipila mwaakupanganyichisya).');
INSERT INTO "silika" VALUES(89,15,'89:15','Sano mundu ndema jaakun''dinga Ambujegwe pakunchimbichisya ni kumpa chindimba, basi akasatiji: “Ambuje ŵangu anjimbichisye (ligongo une nijwakuŵajilwa kupata yeleyi).”');
INSERT INTO "silika" VALUES(89,16,'89:16','Nambo ndema jaakun''dinga pakunnandiyichisya lisiki lyakwe, akasatiji: “Ambuje ŵangu anjalwisye.”');
INSERT INTO "silika" VALUES(89,17,'89:17','Ngwamba! Nambo kuti ngankwatendelaga yaukoto ŵanache ŵawina.');
INSERT INTO "silika" VALUES(89,18,'89:18','Soni ngankuchisyanaga yaagaliisya masikini.');
INSERT INTO "silika" VALUES(89,19,'89:19','Ni nkulyaga chipanje chamalekwa kalye kakutunduka.');
INSERT INTO "silika" VALUES(89,20,'89:20','Ni nkuchinonyelaga soni chipanje kachinonyele kakupelenganya nnope.');
INSERT INTO "silika" VALUES(89,21,'89:21','Ngaŵa (mŵankutendelamo)! Ndema jalichisyajidwa litaka syajidwe-syajidwe!');
INSERT INTO "silika" VALUES(89,22,'89:22','Ni kwika Ambuje ŵenu (Allah kukwiilanya magambo), Achimalaika ali ndandanda!');
INSERT INTO "silika" VALUES(89,23,'89:23','Ni kwikanajo lyele lisikulyo Jahannama! Lyele lisikulyo mundu tachikumbuchila (yaŵapanganyisye), nambo ana kuchinkamuchisya chichi kwele kumbuchilako?');
INSERT INTO "silika" VALUES(89,24,'89:24','Tachiti (pakuligamba): “Nkongwene! Bola ngalongosye (yambone yakungamuchisya) pa umi wangu (walelo uno).”');
INSERT INTO "silika" VALUES(89,25,'89:25','Mwanti palyele lisikuli, pangali jwampaka apeleche ilagasyo mwatachipelechela (Allah).');
INSERT INTO "silika" VALUES(89,26,'89:26','Soni pangali jwampaka ataŵe mwatachitaŵila (Jwalakwe).');
INSERT INTO "silika" VALUES(89,27,'89:27','E mmwe antima ŵakutulala!');
INSERT INTO "silika" VALUES(89,28,'89:28','Wujilani kwa Ambuje ŵenu n''di ŵakusengwa ni kunonyeledwa.');
INSERT INTO "silika" VALUES(89,29,'89:29','Jinjilani mwikuga lya achikapolo Ŵangu.');
INSERT INTO "silika" VALUES(89,30,'89:30','Soni jinjilani ku Mbepo Jangu.');
INSERT INTO "silika" VALUES(90,1,'90:1','Nguulumbila awu musi (wa Maaka).');
INSERT INTO "silika" VALUES(90,2,'90:2','Soni mmwe (Muhammadi صلى الله عليه وسلم) tinchiŵa ŵakundisyidwa (kuputa ngondo) mu wele musiwu.');
INSERT INTO "silika" VALUES(90,3,'90:3','Ni ngullumbila soni nangolo ni uŵelesi wajuŵeleche.');
INSERT INTO "silika" VALUES(90,4,'90:4','Chisimu pamasile patun''gumbile mundu m’matabu.');
INSERT INTO "silika" VALUES(90,5,'90:5','Ana juganichisya yanti pangali ŵampaka ankombole (kumpa ilagasyo)?');
INSERT INTO "silika" VALUES(90,6,'90:6','Jukuŵecheta (mwakutwanga kuti): “Njonasile yipanje yejinji (kuti Usilamu usilale).”');
INSERT INTO "silika" VALUES(90,7,'90:7','Ana juganichisya kuti pangali ŵaakun''bona?');
INSERT INTO "silika" VALUES(90,8,'90:8','Ana nganitun''gumbila meso gaŵili (gajukulolela)?');
INSERT INTO "silika" VALUES(90,9,'90:9','Ni lulimi kwisa soni ngomo siŵili (syajukuŵechetela)?');
INSERT INTO "silika" VALUES(90,10,'90:10','Ni tun’jongolele matala gaŵili (lyambone ni lyakusakala, kuti jusagule lyajukusaka).');
INSERT INTO "silika" VALUES(90,11,'90:11','Ana pakwete pajujesile litala lyamwitumbi (lyalikam’bikasye ku Mbepo)?');
INSERT INTO "silika" VALUES(90,12,'90:12','Ana ni chichi champaka chimmanyisye ya litala lyamwitumbi?');
INSERT INTO "silika" VALUES(90,13,'90:13','(Litala lyamwitumbi nimasengo gambone nambo gakunonopa, mpela): gopola kapolo. ');
INSERT INTO "silika" VALUES(90,14,'90:14','Kapena kupeleka yakulya palisiku lyasala.');
INSERT INTO "silika" VALUES(90,15,'90:15','(Kumpa) jwawina jwapaulongo.');
INSERT INTO "silika" VALUES(90,16,'90:16','Kapena masikini jwaluwunduwundupe (ligongo lyakulaga nnope).');
INSERT INTO "silika" VALUES(90,17,'90:17','Soni nikuŵa (nkutola yakulyajo) jumpepe mwa aŵala ŵaakulupilile, ni kulimbikasyanaga ya kupilila soni ni kulimbikasyanaga ya kutendelana chanasa.');
INSERT INTO "silika" VALUES(90,18,'90:18','Ŵanganyao niŵandu ŵakun''dyo (ŵali ŵambone).');
INSERT INTO "silika" VALUES(90,19,'90:19','Nambo ŵandu ŵaagakaaniile ma Ȃya Getu ŵanganyao niŵandu ŵakunchiji (ŵali ŵakusakala).');
INSERT INTO "silika" VALUES(90,20,'90:20','Kwaŵanganyao kuchiŵa kwana Moto Wakuunichila ni kutika (wangali pakopochela).');
INSERT INTO "silika" VALUES(91,1,'91:1','Ngulilumbila lyuŵa ni lilanguka lyakwe.');
INSERT INTO "silika" VALUES(91,2,'91:2','Ni mwesi ndema jaukulikuya (lyuŵalyo).');
INSERT INTO "silika" VALUES(91,3,'91:3','Ni muusi ndema jaukulilosya palangulangu.');
INSERT INTO "silika" VALUES(91,4,'91:4','Ni chilo ndema jachikuliunichila (kuti likaoneka).');
INSERT INTO "silika" VALUES(91,5,'91:5','Kwisa soni liwunde ni Juŵalitaŵile (mwakulitalichisya).');
INSERT INTO "silika" VALUES(91,6,'91:6','Ni litaka ni Juŵalitandiche (mwakulijaasasya).');
INSERT INTO "silika" VALUES(91,7,'91:7','Kwisa soni ntima ni Juŵaukolosisye.');
INSERT INTO "silika" VALUES(91,8,'91:8','Basi ni jwaumanyisye yakusakala yakwe ni yambone yakwe.');
INSERT INTO "silika" VALUES(91,9,'91:9','Pamasile paapundile jwauswejesye (ntima wakwe).');
INSERT INTO "silika" VALUES(91,10,'91:10','Soni pamasile papelelwe jwautwinyichisye (ntima wakwe mu sambi).');
INSERT INTO "silika" VALUES(91,11,'91:11','Ŵaakaaniile ŵanya Asamudu ligongo lyakusumba mpika kwao.');
INSERT INTO "silika" VALUES(91,12,'91:12','Ndema jajwalipeleche jwakusakalilwa jwao (kuulaga ngamiya).');
INSERT INTO "silika" VALUES(91,13,'91:13','Basi Ntenga jwa Allah (Swaleh) ŵasalile kuti: “(Aji ni) ngamiya ja Allah, sano ngasin''jikanyaga kumwa mesi (palisiku lyakwe).');
INSERT INTO "silika" VALUES(91,14,'91:14','Nambo ŵankaaniile nikujiulaga; basi Ambuje ŵao ŵajonasile ligongo lya chileŵi chao; niŵalandenye pachonasikocho.');
INSERT INTO "silika" VALUES(91,15,'91:15','Soni jwangajogopa yakuichisya yakwe.');
INSERT INTO "silika" VALUES(92,1,'92:1','Nguchilumbila chilo ndema jachikuunichila (chilichose ni chipi chakwe).');
INSERT INTO "silika" VALUES(92,2,'92:2','Ni muusi ndema jakukulanguka.');
INSERT INTO "silika" VALUES(92,3,'92:3','Ni Ajula (Allah) Jwagumbile chachilume ni chachikongwe.');
INSERT INTO "silika" VALUES(92,4,'92:4','Chisimu itendo yenu iŵele yakulekangana-lekangana.');
INSERT INTO "silika" VALUES(92,5,'92:5','Sano mundu jwaakutola (petala lya Allah) ni kuŵa niwoga (wakun''jogopa Jwalakwe).');
INSERT INTO "silika" VALUES(92,6,'92:6','Ni kulupilila yambone nnope (yatakalipidwe ligongo lyakutola).');
INSERT INTO "silika" VALUES(92,7,'92:7','Basi chitun''jepepachisye litala lyakwalukangana (lyakutendela yambone).');
INSERT INTO "silika" VALUES(92,8,'92:8','Nambo mundu jwachatende ubayili ni kuliona kusichila.');
INSERT INTO "silika" VALUES(92,9,'92:9','Ni kanila yambone nnope (yaakajiile kuipata akatole).');
INSERT INTO "silika" VALUES(92,10,'92:10','Basi chitun''jepepachisye litala lyausito (lyakutendela yakusakala).');
INSERT INTO "silika" VALUES(92,11,'92:11','Soni ngasichija kunkamuchisya chipanje chakwe ndema jagwila (ku Moto).');
INSERT INTO "silika" VALUES(92,12,'92:12','Chisimu Kukwetu nikwachili chongolo.');
INSERT INTO "silika" VALUES(92,13,'92:13','Soni chisimu Uwwe ni twachinsyene Akhera ni Duniya.');
INSERT INTO "silika" VALUES(92,14,'92:14','Basi nguntetela ya Moto waukolela (mwamachili).');
INSERT INTO "silika" VALUES(92,15,'92:15','Ngaaja kuwujinjila ikaŵeje jwakusakalilwa nnope.');
INSERT INTO "silika" VALUES(92,16,'92:16','Ajula jwaakuŵa nkukanila ni kusyulula.');
INSERT INTO "silika" VALUES(92,17,'92:17','Ni takaŵambasyidwe nao mundu jwawoga kusyene (wakun''jogopa Allah).');
INSERT INTO "silika" VALUES(92,18,'92:18','Ajula jwaakutola chipanje chakwe pakuliswejesya.');
INSERT INTO "silika" VALUES(92,19,'92:19','Soni pangali kwa jwalakwe jwalijose jwaŵantendele yaukoto yaikuŵa ili nkuusyidwa.');
INSERT INTO "silika" VALUES(92,20,'92:20','Ikaŵeje (jukutola) pakusachilila Ngope ja M’mbujeegwe (Allah) Jwapenani kusyene (kuti jukajione ku Akhera).');
INSERT INTO "silika" VALUES(92,21,'92:21','Ni juchija kusengwa.');
INSERT INTO "silika" VALUES(93,1,'93:1','Nguulumbila nsana.');
INSERT INTO "silika" VALUES(93,2,'93:2','Ni chilo ndema jachikutulala.');
INSERT INTO "silika" VALUES(93,3,'93:3','Ngananneka Ambuje ŵenu (mmwe Muhammadi صلى الله عليه وسلم) soni nganam''benga.');
INSERT INTO "silika" VALUES(93,4,'93:4','Soni chisimu umi wambesi uŵele wambone nnope kukwenu kupunda wandanda.');
INSERT INTO "silika" VALUES(93,5,'93:5','Soni tachija kumpa Ambuje ŵenu (yambone) ni tinchisengwa.');
INSERT INTO "silika" VALUES(93,6,'93:6','Ana nganansimana n''di ŵawina nikumpa liwuto lyambone?');
INSERT INTO "silika" VALUES(93,7,'93:7','Soni ni ŵansimene n''di ŵangachimanya chongoko (jajili Qur''an ni malamusi ga Dini) nikun''jongola?');
INSERT INTO "silika" VALUES(93,8,'93:8','Ni ŵansimene soni n''di ŵakulaga nikunsichilisya?');
INSERT INTO "silika" VALUES(93,9,'93:9','Sano mwanache jwawina basi ngasimunchima.');
INSERT INTO "silika" VALUES(93,10,'93:10','Nombe soni jwakuŵenda basi ngasimunkalipila.');
INSERT INTO "silika" VALUES(93,11,'93:11','Soni chindimba cha Ambuje ŵenu (Allah) basi chisimbani (pakutogolela ni kutenda yambone).');
INSERT INTO "silika" VALUES(94,1,'94:1','Ana nganitunsapangulila (mmwe Muhammadi صلى الله عليه وسلم) chidali chenu (pakumpa utume)?');
INSERT INTO "silika" VALUES(94,2,'94:2','Ni tuntuusile nsigo wenu (wausito wa kalungusye ka utenga, pakun’jepepachisya kalungusye kakwe),');
INSERT INTO "silika" VALUES(94,3,'94:3','Awula (nsigo wakwe) wawalumbisye n''gongo wenu?');
INSERT INTO "silika" VALUES(94,4,'94:4','Ni tunnyakuliile kusaalidwa kwenu?');
INSERT INTO "silika" VALUES(94,5,'94:5','Basi chisimu pakuli kunonopa pana kwepepala.');
INSERT INTO "silika" VALUES(94,6,'94:6','Chisimu pakuli kunonopa pana kwepepala.');
INSERT INTO "silika" VALUES(94,7,'94:7','Sano pati mmalisyisye (masengo genu), basi limbichilani (kutenda ibada).');
INSERT INTO "silika" VALUES(94,8,'94:8','Soni kwa Ambuje ŵenu pe (Allah) basi sachililani (ukoto Wakwe).');
INSERT INTO "silika" VALUES(95,1,'95:1','Nguulumbila nkuju ni nzaituni.');
INSERT INTO "silika" VALUES(95,2,'95:2','Ni litumbi lya Sinai.');
INSERT INTO "silika" VALUES(95,3,'95:3','Kwisa soni awu nsinda (wa Maaka) wantunjelele.');
INSERT INTO "silika" VALUES(95,4,'95:4','Chisimu pamasile patun''gumbile mundu mu kagumbidwe kambone nnope.');
INSERT INTO "silika" VALUES(95,5,'95:5','Kaneka nitukum''busyaaga kuŵa jwapasi kwapunda ŵapasi wosope (pa uchekulu wakulepela nao kutenda yambone).');
INSERT INTO "silika" VALUES(95,6,'95:6','Ikaŵeje ŵandu ŵaakulupilile ni kutendaga yambone (ali ŵachilimbanganile), basi akwete kupata ŵanganyao malipilo gangamala.');
INSERT INTO "silika" VALUES(95,7,'95:7','Ana panyuma (pa kuimanya yele) nichichi chikuntendekasya kanila ya malipilo (ga pa Kiyama)?');
INSERT INTO "silika" VALUES(95,8,'95:8','Ana Allah nganaŵa Nkwiilanya chenene (magambo) kwapunda akwiilanya wosope?!');
INSERT INTO "silika" VALUES(96,1,'96:1','Soomani mu lina lya Ambuje ŵenu (Allah) ŵaagumbile.');
INSERT INTO "silika" VALUES(96,2,'96:2','Agumbile mundu kuumila mu chipande chamyasi jekamatane.');
INSERT INTO "silika" VALUES(96,3,'96:3','Soomani! Soni Ambuje ŵenu ni Ŵantuka kusyene.');
INSERT INTO "silika" VALUES(96,4,'96:4','Aŵala ŵaajiganyisye (kulemba) ni chakulembela.');
INSERT INTO "silika" VALUES(96,5,'96:5','An''jiganyisye mundu yaŵalekaga ngaimanya.');
INSERT INTO "silika" VALUES(96,6,'96:6','Yakuona! Chisimu mundu akuŵaga jwakusumba mpika (pa kulikwesya).');
INSERT INTO "silika" VALUES(96,7,'96:7','Ligongo lyakuliona kusichila.');
INSERT INTO "silika" VALUES(96,8,'96:8','Chisimu kwa Ambuje ŵenu pe (Allah) ni kwakuujila.');
INSERT INTO "silika" VALUES(96,9,'96:9','Ana mum''bweni ajula jwaakuŵa nkunkaanya,');
INSERT INTO "silika" VALUES(96,10,'96:10','Kapolo (jwa Allah) ndema jajukusaka kuswali?');
INSERT INTO "silika" VALUES(96,11,'96:11','Ana nkuyiona uli najuŵaga (Muhammadi صلى الله عليه وسلم) juli pachongoko?');
INSERT INTO "silika" VALUES(96,12,'96:12','Kapena jukulamula ya woga (wakun''jogopa Allah)?');
INSERT INTO "silika" VALUES(96,13,'96:13','Nambi nkuyiona uli naŵaga (Abujahalio) akukanila ni kusyulula?');
INSERT INTO "silika" VALUES(96,14,'96:14','Ana ngaakumanyilila yanti Allah akwaona (yaakutenda)?');
INSERT INTO "silika" VALUES(96,15,'96:15','Ngwamba! Chisimu naga ngaakuleka, chiitwawuta umbo syapewuwo (pakwaponya ku Moto).');
INSERT INTO "silika" VALUES(96,16,'96:16','Liwuwo lyakwe lyaunami soni lyakulemwa.');
INSERT INTO "silika" VALUES(96,17,'96:17','Basi ŵaaŵilanje ŵandu ŵakwe (kuti ankamuchisye).');
INSERT INTO "silika" VALUES(96,18,'96:18','Nowe chitwaŵilanje Azabaniya (asilikali ŵa ku Moto).');
INSERT INTO "silika" VALUES(96,19,'96:19','Ngwamba! Mmwe ngasimunkunda jwalakwejo, nambo nsujuduji (kuswali) ni n’diŵandichisyeje (kwa Allah).');
INSERT INTO "silika" VALUES(97,1,'97:1','Chisimu Uwwe tujitulwisye jalakwe (Qur''aniji) m’Chilo Chakuchimbichika.');
INSERT INTO "silika" VALUES(97,2,'97:2','Ana ni chichi champaka chimmanyisye ya Chilo Chakuchimbichika?');
INSERT INTO "silika" VALUES(97,3,'97:3','Chilo Chakuchimbichikachi chiŵele chambone nnope kupunda myesi jakwana sausande (1000). ');
INSERT INTO "silika" VALUES(97,4,'97:4','Achimalaika akasatulukaga pamo ni Nsimu (Jibulilu) mu Chele (Chilochi) kwa lisosa lya M’mbuje gwao, kwisa kulondesya chindu chilichose (pakuchiŵika mu mpango wakwe). ');
INSERT INTO "silika" VALUES(97,5,'97:5','Ntunjelele-tunjelelepe! Chele Chilocho, mpaka kumasikusiku. ');
INSERT INTO "silika" VALUES(98,1,'98:1','Ŵandu achimakafili kutandila achinsyene chitabu (Ayuda ni Akilisito) kwisa soni ŵakuwanganya (Allah ni isanamu), nganaŵa alekangene (ni ukafili wao) kwikanila ndema ja chayichilile chilosyo chakuonechela palangulangu.');
INSERT INTO "silika" VALUES(98,2,'98:2','(Jwali) Ntenga (Muhammadi صلى الله عليه وسلم) jwakuumila kwa Allah jwaakusooma (Qur’an jajili mu) ikalakala yakuswejesyedwa.');
INSERT INTO "silika" VALUES(98,3,'98:3','Mwalakwemo mwana malamusi gagoloka.');
INSERT INTO "silika" VALUES(98,4,'98:4','Chisimu Allah akupeleka chanasa kwa Ntume (Muhammadi صلى الله عليه وسلم), nombe nao soni Achimalaika (akum’bombelaga chanasa Ntumejo صلى الله عليه وسلم). E jenumanja ŵankulupilile! Mum’bombelani chanasa jwalakwe ni kum’bombela soni ntendele.');
INSERT INTO "silika" VALUES(98,5,'98:5','Ni nganalamulidwa ikaŵeje yanti an''galagatileje Allah mwakunswejechesya dini Jakwe pakupendechela ku Uujika Wakwe (nikutalikangana nayo isanamu), nikuti ajimicheje Swala mwakolosya ni kutolaga Zaka, soni jalakwejo ni dini jagoloka.');
INSERT INTO "silika" VALUES(98,6,'98:6','Chisimu ŵandu ŵaakanile (dini ja Usilamu); kutandila achinsyene chitabu (Ayuda ni Akilisito) kwisa soni ŵakuwanganya (Allah ni isanamu), chakaŵe m’moto wa Jahannama, chakaŵe ŵandamo mwalakwemo. Ŵanganyao ni iŵumbe yakusakala nnope. ');
INSERT INTO "silika" VALUES(98,7,'98:7','Chisimu ŵandu ŵaakulupilile nikutendaga yambone, ŵanganyao ni iwumbe yambone nnope.');
INSERT INTO "silika" VALUES(98,8,'98:8','Malipilo gao kwa Ambuje ŵao chigakaŵe Matimbe Gandamo gasikujilima cha pasi pakwe sulo. Chakaŵe ŵandamo mwalakwemo kwa yaka ni yaka. Allah tachanonyela (pakwapochela masengo gao gambone), nombe nao tachinonyelwa Najo (pamalipilo gataachaapa). Yalakweyo ni yamundu jwatan''jogope M''mbujeegwe (Allah).');
INSERT INTO "silika" VALUES(99,1,'99:1','Pandema jalichitenganyika litaka nichisikinya chakwe chamachili.');
INSERT INTO "silika" VALUES(99,2,'99:2','Ni kwisa koposya litaka misigo jakwe jausito (ichindu yayaliji nkati mwakwe, mpela malilo ni mitapo jambiya).');
INSERT INTO "silika" VALUES(99,3,'99:3','Ni kwisa kuŵecheta mundu (mwawoga soni mwakusimonga kuti): “Litite uli (litaka, ana Kiyama jiyiche?)”');
INSERT INTO "silika" VALUES(99,4,'99:4','Lyele lisikulyo (litaka) lichiŵecheta abali syakwe syosope (sya indu yayatendekwele pachanya pakwe yambone kapena yakusakala).');
INSERT INTO "silika" VALUES(99,5,'99:5','Ligongo lyanti Ambuje ŵenu tachililamula lyalakwelyo (kuti liŵechete).');
INSERT INTO "silika" VALUES(99,6,'99:6','Lyele lisikulyo ŵandu tachipwilingana ali m''mikutula jakulekangana-lekangana kuti akaalosye masengo gao.');
INSERT INTO "silika" VALUES(99,7,'99:7','Basi jwatapanganye chambone chausito wanti chisawu lunyilili, tachija kuchiona.');
INSERT INTO "silika" VALUES(99,8,'99:8','Nombe soni jwatapanganye chakusakala chausito wanti chisawu lunyilili, tachija kuchiona.');
INSERT INTO "silika" VALUES(100,1,'100:1','Ngusilumbila hachi syasikuwutuka sili nkupumasika.');
INSERT INTO "silika" VALUES(100,2,'100:2','Ni syasikoposya moto mu ikotwa mwakwe (pakuwutuka m’maganga).');
INSERT INTO "silika" VALUES(100,3,'100:3','Ni syasikwagungumuchila amagongo kumasikusiku.');
INSERT INTO "silika" VALUES(100,4,'100:4','Nikututumya palakwepo luwundu lwejinji.');
INSERT INTO "silika" VALUES(100,5,'100:5','Nikulijinjilila palakwepo likuga (lya amagongo).');
INSERT INTO "silika" VALUES(100,6,'100:6','Chisimu mundu kwa Ambujegwe (Allah) juŵele jwakanila (ukoto waakumpa).');
INSERT INTO "silika" VALUES(100,7,'100:7','Soni chisimu jwalakwe jukuŵichila umboni pa yalakweyo (kupitila mu itendo yakwe).');
INSERT INTO "silika" VALUES(100,8,'100:8','Soni chisimu jwalakwe pakuchinonyela chipanje juŵele jwakupelenganya nnope.');
INSERT INTO "silika" VALUES(100,9,'100:9','Ana ngajukumanyilila pandema jaichikoposyedwa yaili m’malembe?');
INSERT INTO "silika" VALUES(100,10,'100:10','Nikwisa kwilochesya papaswela yaili m’mitima?');
INSERT INTO "silika" VALUES(100,11,'100:11','Chisimu lyele lisikulyo M’mbuje gwao (Allah) tachamanya abali syosope (syakwamba yaŵapanganyaga ŵanganyao).');
INSERT INTO "silika" VALUES(101,1,'101:1','Chagunda ntima (Kiyama).');
INSERT INTO "silika" VALUES(101,2,'101:2','Ana chagunda ntima nichichi?');
INSERT INTO "silika" VALUES(101,3,'101:3','Ana ni chichi champaka chimmanyisye ya chagunda ntima?');
INSERT INTO "silika" VALUES(101,4,'101:4','Lyele ni lisiku (lya Kiyama) lyachachiŵa ŵandu (sakalaalii!) mpela mapuluputwa gakusapalala.');
INSERT INTO "silika" VALUES(101,5,'101:5','Nombe matumbi gachiŵa (nkuwululuka) mpela litonji lyetyosye mbele.');
INSERT INTO "silika" VALUES(101,6,'101:6','Sano mundu jwachiukasitope n''diingo wakwe (wa yambone, nikwepepala yakusakala). ');
INSERT INTO "silika" VALUES(101,7,'101:7','Basi jwalakwejo takaŵe mu ndamo jakusengwasya.');
INSERT INTO "silika" VALUES(101,8,'101:8','Nambo mundu jwachiukajepepale n''dingo wakwe (wa yambone, ni kusitopa yakusakala).');
INSERT INTO "silika" VALUES(101,9,'101:9','Basi liuto lyakwe ni ku Hawiya.');
INSERT INTO "silika" VALUES(101,10,'101:10','Ana ni chichi champaka chimmanyisye ya jele (Hawiya)?');
INSERT INTO "silika" VALUES(101,11,'101:11','Wele ni Moto Wakoocha nnope.');
INSERT INTO "silika" VALUES(102,1,'102:1','Kumpele chipaka kusonganganya (ya duniya mwampikisano kuleka ganisya ya Akhera).');
INSERT INTO "silika" VALUES(102,2,'102:2','Mpaka nkwikanila kwaula ku malembe (n''di m''bwile).');
INSERT INTO "silika" VALUES(102,3,'102:3','Ngwamba! Chinchiika kumanyilila.');
INSERT INTO "silika" VALUES(102,4,'102:4','Soni ngwamba! Chinchiika kumanyilila.');
INSERT INTO "silika" VALUES(102,5,'102:5','Ngwamba! Ingaŵe mwamanyililaga umanyilisi usyesyene (nga nganin''jijilikaga ni ya duniya).');
INSERT INTO "silika" VALUES(102,6,'102:6','Chisimu chene chinkauone Moto Waukali.');
INSERT INTO "silika" VALUES(102,7,'102:7','Soni chinkauone kusyene ni liso lyakusimichisya.');
INSERT INTO "silika" VALUES(102,8,'102:8','Soni lyele lisikulyo tinkawusyidwe kusyene ya chindimba (chaŵampele pa duniya). ');
INSERT INTO "silika" VALUES(103,1,'103:1','Ngujilumbila ndaŵi.');
INSERT INTO "silika" VALUES(103,2,'103:2','Chisimu chene mundu ali nchasiko (ni kupelelwa).');
INSERT INTO "silika" VALUES(103,3,'103:3','Ikaŵeje aŵala ŵaakulupilile ni kutendaga yambone, ni kulimbisyanaga ya (kuya ichindu) yakuona, ni kulimbisyanaga soni yakupilila.');
INSERT INTO "silika" VALUES(104,1,'104:1','Ipotesi ni yakwe jwalijose jwakwasogoda ni kwachembulusya ŵandu mwakwamumunya.');
INSERT INTO "silika" VALUES(104,2,'104:2','Ajula jwaachisonganganyisye chipanje nikuchiŵalanjilaga (ndema ni katema).');
INSERT INTO "silika" VALUES(104,3,'104:3','Akuganichisya kuti chipanje chakwecho chichintendekasye kuŵa jwandamo!');
INSERT INTO "silika" VALUES(104,4,'104:4','Ngwamba! Chisimu chene takaponyedwe ku Hutwama.');
INSERT INTO "silika" VALUES(104,5,'104:5','Ana ni chichi champaka chimmanyisye ya Hutwama?');
INSERT INTO "silika" VALUES(104,6,'104:6','Wele ni Moto wa Allah wachikolelele (wakuteketulanya chilichose chakuponyedwamo).');
INSERT INTO "silika" VALUES(104,7,'104:7','Awula wachiuŵa nkukwelela ku mitima.');
INSERT INTO "silika" VALUES(104,8,'104:8','Chisimu walakweo uchaunichila ni kwaakutika ŵanganyao (kwakusoŵa pakopochela).');
INSERT INTO "silika" VALUES(104,9,'104:9','(Chachiŵa) m’makongwa getandaliche chanchilewu (mwanti ngasakombola kupuluputa).');
INSERT INTO "silika" VALUES(105,1,'105:1','Ana nganinyyiona yaŵatite Ambuje ŵenu (Allah) pakwajonanga achinsyene ndembo (ŵaŵayikaga kugumula Likaaba)?');
INSERT INTO "silika" VALUES(105,2,'105:2','Ana nganagatenda malindi gao kuŵa gakusokonechela?');
INSERT INTO "silika" VALUES(105,3,'105:3','Ni ŵatumichisye ŵanganyao ijuni mikutula-mikutula.');
INSERT INTO "silika" VALUES(105,4,'105:4','Yaliji nkwaasoma ni maganga gelongo lyejoche (ni moto wa ku Jahannama).');
INSERT INTO "silika" VALUES(105,5,'105:5','Niŵatesile kuŵa mpela masapi gagalilidwe (ni inyama ni kulukulidwa). ');
INSERT INTO "silika" VALUES(106,1,'106:1','(Ŵajonasile achinsyene ndembo) kuti ajendelechele Makuraishi.');
INSERT INTO "silika" VALUES(106,2,'106:2','Panyendele jao jamaulendo ga chuku (kuja ku Yemeni) ni chawu (kuja ku Shami kumalonda mwantunjelele).');
INSERT INTO "silika" VALUES(106,3,'106:3','Basi an''galagatileje M''mbuje jwa aji Nyumba (ja Likaaba).');
INSERT INTO "silika" VALUES(106,4,'106:4','Ajula jwaŵapele yakulya ligongo lyasala, ni kwaŵambasya ku woga.');
INSERT INTO "silika" VALUES(107,1,'107:1','Ana mum''bweni ajula jwaakukanila ya malipilo (ga pa Kiyama)?');
INSERT INTO "silika" VALUES(107,2,'107:2','Basi jwele ni ajula jwaakuntutanga mwanache jwawina. ');
INSERT INTO "silika" VALUES(107,3,'107:3','Soni jwangachisya yagaliisya masikini. ');
INSERT INTO "silika" VALUES(107,4,'107:4','Basi ipotesi niyao ŵandu ŵakuswaliji.');
INSERT INTO "silika" VALUES(107,5,'107:5','Aŵala ŵanya ŵaakuŵa ali nkusikwanjila swala syao.');
INSERT INTO "silika" VALUES(107,6,'107:6','Aŵala ŵanya ŵaakuŵa ali nkutenda ndola.');
INSERT INTO "silika" VALUES(107,7,'107:7','Ni akuŵa soni ali nkukanya yaakamuchisya (pannango, mpela liŵago, chipula, lituli ni yine).');
INSERT INTO "silika" VALUES(108,1,'108:1','Chisimu Uwwe tumpele (mmwe Muhammadi صلى الله عليه وسلم) Kauthara (lusulo lwa ku Mbepo). ');
INSERT INTO "silika" VALUES(108,2,'108:2','Basi mwaswalilani Ambuje ŵenu (Allah) nikusikita (inyama yakur’bani yakupelechela mbepesi Kukwakwe ngaŵaga kwa akumasoka).');
INSERT INTO "silika" VALUES(108,3,'108:3','Chisimu jwaakum''benga mmwejo ni jwali jwakatika (upile wakwe ni kusalidwa kwakwe).');
INSERT INTO "silika" VALUES(109,1,'109:1','Jilani: “E jenumanja achimakafili!”');
INSERT INTO "silika" VALUES(109,2,'109:2','“Ngangalagatila chinkuchigalagatila (kunneka Allah).”');
INSERT INTO "silika" VALUES(109,3,'109:3','“Nombe ŵanganyammwe nganim''ba mun''galagatile (Allah) jwangun''galagatila (kutendaga n''di mu ukafili pemo).”');
INSERT INTO "silika" VALUES(109,4,'109:4','“Nombe une soni nganimba nachigalagatile cham''bele n''di nkuchigalagatila ŵanganyammwe.”');
INSERT INTO "silika" VALUES(109,5,'109:5','“Nombe ŵanganyammwe nganim''ba mun''galagatile (Allah) jwangun''galagatila (kutendaga n''di mu ukafili pemo).”');
INSERT INTO "silika" VALUES(109,6,'109:6','“Jenumanja nkwete dini jenu (jagalagatila isanamu) une none ngwete dini jangu (jakun’galagatila Allah).”');
INSERT INTO "silika" VALUES(110,1,'110:1','Ndema jachiyiche chikulupusyo cha Allah ni kuwugula (Maaka kuŵa m’makono mwa Asilamu).');
INSERT INTO "silika" VALUES(110,2,'110:2','Ni kwaona mmwejo ŵandu ali nkwinjila mu dini ja Allah mikutula-mikutula.');
INSERT INTO "silika" VALUES(110,3,'110:3','Basi (jele ndemajo) luswejesyani lumbili lwa Ambuje ŵenu, ni kwaŵenda chikululuko. Chisimu Ŵalakweo aŵele Ŵakupochela nnope toba.');
INSERT INTO "silika" VALUES(111,1,'111:1','Gajonasiche makono ganagaŵili ga Abu Lahabi, nombe nao ajonasiche. ');
INSERT INTO "silika" VALUES(111,2,'111:2','Ngaija kwakamuchisya ipanje yao atamuno yaŵapanganyisye (mpela ŵanache ŵaŵaŵeleche).');
INSERT INTO "silika" VALUES(111,3,'111:3','Takajinjile ku Moto waukwete malamba (gakulapuka).');
INSERT INTO "silika" VALUES(111,4,'111:4','Niŵankwao ŵakwe ŵaŵaliji nkutwichila sasu (syamiŵa nikuntejelaga Muhammadi صلى الله عليه وسلم mwakupita).');
INSERT INTO "silika" VALUES(111,5,'111:5','Nnukosi mwao (ŵele ŵakongweo) chimukaŵe mwana litondolo lyamiŵamiŵa (lyakwalagachisya nalyo).');
INSERT INTO "silika" VALUES(112,1,'112:1','Jilani: “Jwalakwe ni Allah Jumpepe.” ');
INSERT INTO "silika" VALUES(112,2,'112:2','“Allah pe ni jwakun''jegama.”');
INSERT INTO "silika" VALUES(112,3,'112:3','“Nganaŵeleka soni nganaŵelechedwa.”');
INSERT INTO "silika" VALUES(112,4,'112:4','“Soni nganakola jwakulanda najo ata jumo.” ');
INSERT INTO "silika" VALUES(113,1,'113:1','Jilani: “Ngulijuya mwa M’mbuje jwa kumasikusiku.”');
INSERT INTO "silika" VALUES(113,2,'113:2','“Ku yakusakala yaŵaigumbile.”');
INSERT INTO "silika" VALUES(113,3,'113:3','“Ni ku yakusakala yachilo ndema jachikwinjila chipi.”');
INSERT INTO "silika" VALUES(113,4,'113:4','“Ni ku yakusakala ya (asaŵi) ŵaakupepela mu iwundo (pakuloga).”');
INSERT INTO "silika" VALUES(113,5,'113:5','“Ni ku yakusakala ya jwalitima ndema jajukutenda litima.”');
INSERT INTO "silika" VALUES(114,1,'114:1','Jilani: “Ngulijuya mwa M''mbuje jwa ŵandu.”');
INSERT INTO "silika" VALUES(114,2,'114:2','“Nchimwene jwa ŵandu.” ');
INSERT INTO "silika" VALUES(114,3,'114:3','“Nnungu jwa ŵandu.”');
INSERT INTO "silika" VALUES(114,4,'114:4','“Ku yakusakala ya nkusongona (pakuunyenjelela ntima yakusakala), jwakuujiilila ni kuliisisa (naga nsyene ntimao juli junkolasile Allah).”');
INSERT INTO "silika" VALUES(114,5,'114:5','“Ajula jwaakusongonaga m’mitima mwa ŵandu.”');
INSERT INTO "silika" VALUES(114,6,'114:6','“Jwakuumila m’majini ni mu ŵandu.” ');
