INSERT INTO "transliteration" VALUES(1,1,'1:1','Bismi Allahi arrahmani arraheem');
INSERT INTO "transliteration" VALUES(1,2,'1:2','Alhamdu lillahi rabbi alAAalameen');
INSERT INTO "transliteration" VALUES(1,3,'1:3','Arrahmani arraheem');
INSERT INTO "transliteration" VALUES(1,4,'1:4','Maliki yawmi addeen');
INSERT INTO "transliteration" VALUES(1,5,'1:5','Iyyaka naAAbudu wa-iyyaka nastaAAeen');
INSERT INTO "transliteration" VALUES(1,6,'1:6','Ihdina assirata almustaqeem');
INSERT INTO "transliteration" VALUES(1,7,'1:7','Sirata allatheena anAAamta AAalayhim ghayri almaghdoobi AAalayhim wala addalleen');
INSERT INTO "transliteration" VALUES(2,1,'2:1','Alif-lam-meem');
INSERT INTO "transliteration" VALUES(2,2,'2:2','Thalika alkitabu larayba feehi hudan lilmuttaqeen');
INSERT INTO "transliteration" VALUES(2,3,'2:3','Allatheena yu/minoona bilghaybiwayuqeemoona assalata wamimma razaqnahumyunfiqoon');
INSERT INTO "transliteration" VALUES(2,4,'2:4','Wallatheena yu/minoona bimaonzila ilayka wama onzila min qablika wabil-akhiratihum yooqinoon');
INSERT INTO "transliteration" VALUES(2,5,'2:5','Ola-ika AAala hudan minrabbihim waola-ika humu almuflihoon');
INSERT INTO "transliteration" VALUES(2,6,'2:6','Inna allatheena kafaroo sawaonAAalayhim aanthartahum am lam tunthirhum layu/minoon');
INSERT INTO "transliteration" VALUES(2,7,'2:7','Khatama Allahu AAala quloobihimwaAAala samAAihim waAAala absarihim ghishawatunwalahum AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(2,8,'2:8','Wamina annasi man yaqoolu amannabiAllahi wabilyawmi al-akhiri wamahum bimu/mineen');
INSERT INTO "transliteration" VALUES(2,9,'2:9','YukhadiAAoona Allaha wallatheenaamanoo wama yakhdaAAoona illa anfusahum wamayashAAuroon');
INSERT INTO "transliteration" VALUES(2,10,'2:10','Fee quloobihim maradun fazadahumuAllahu maradan walahum AAathabun aleemun bimakanoo yakthiboon');
INSERT INTO "transliteration" VALUES(2,11,'2:11','Wa-itha qeela lahum latufsidoo fee al-ardi qaloo innama nahnumuslihoon');
INSERT INTO "transliteration" VALUES(2,12,'2:12','Ala innahum humu almufsidoona walakinla yashAAuroon');
INSERT INTO "transliteration" VALUES(2,13,'2:13','Wa-itha qeela lahum aminoo kamaamana annasu qaloo anu/minu kamaamana assufahao ala innahum humu assufahaowalakin la yaAAlamoon');
INSERT INTO "transliteration" VALUES(2,14,'2:14','Wa-itha laqoo allatheena amanooqaloo amanna wa-itha khalaw ilashayateenihim qaloo inna maAAakum innamanahnu mustahzi-oon');
INSERT INTO "transliteration" VALUES(2,15,'2:15','Allahu yastahzi-o bihim wayamudduhumfee tughyanihim yaAAmahoon');
INSERT INTO "transliteration" VALUES(2,16,'2:16','Ola-ika allatheena ishtarawooaddalalata bilhuda famarabihat tijaratuhum wama kanoomuhtadeen');
INSERT INTO "transliteration" VALUES(2,17,'2:17','Mathaluhum kamathali allatheeistawqada naran falamma adaat ma hawlahuthahaba Allahu binoorihim watarakahum fee thulumatinla yubsiroon');
INSERT INTO "transliteration" VALUES(2,18,'2:18','Summun bukmun AAumyun fahum layarjiAAoon');
INSERT INTO "transliteration" VALUES(2,19,'2:19','Aw kasayyibin mina assama-ifeehi thulumatun waraAAdun wabarqunyajAAaloona asabiAAahum fee athanihim mina assawaAAiqihathara almawti wallahu muheetunbilkafireen');
INSERT INTO "transliteration" VALUES(2,20,'2:20','Yakadu albarqu yakhtafu absarahumkullama adaa lahum mashaw feehi wa-itha athlamaAAalayhim qamoo walaw shaa Allahu lathahababisamAAihim waabsarihim inna Allaha AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,21,'2:21','Ya ayyuha annasuoAAbudoo rabbakumu allathee khalaqakum wallatheenamin qablikum laAAallakum tattaqoon');
INSERT INTO "transliteration" VALUES(2,22,'2:22','Allathee jaAAala lakumu al-ardafirashan wassamaa binaan waanzalamina assama-i maan faakhraja bihi mina aththamaratirizqan lakum fala tajAAaloo lillahi andadanwaantum taAAlamoon');
INSERT INTO "transliteration" VALUES(2,23,'2:23','Wa-in kuntum fee raybin mimma nazzalnaAAala AAabdina fa/too bisooratin min mithlihi wadAAooshuhadaakum min dooni Allahi in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(2,24,'2:24','Fa-in lam tafAAaloo walan tafAAaloo fattaqooannara allatee waqooduha annasuwalhijaratu oAAiddat lilkafireen');
INSERT INTO "transliteration" VALUES(2,25,'2:25','Wabashshiri allatheena amanoowaAAamiloo assalihati anna lahum jannatintajree min tahtiha al-anharu kullamaruziqoo minha min thamaratin rizqan qaloo hathaallathee ruziqna min qablu waotoo bihi mutashabihanwalahum feeha azwajun mutahharatun wahumfeeha khalidoon');
INSERT INTO "transliteration" VALUES(2,26,'2:26','Inna Allaha la yastahyeean yadriba mathalan ma baAAoodatan famafawqaha faamma allatheena amanoofayaAAlamoona annahu alhaqqu min rabbihim waammaallatheena kafaroo fayaqooloona matha aradaAllahu bihatha mathalan yudillu bihikatheeran wayahdee bihi katheeran wama yudillu bihiilla alfasiqeen');
INSERT INTO "transliteration" VALUES(2,27,'2:27','Allatheena yanqudoona AAahdaAllahi min baAAdi meethaqihi wayaqtaAAoona maamara Allahu bihi an yoosala wayufsidoona fee al-ardiola-ika humu alkhasiroon');
INSERT INTO "transliteration" VALUES(2,28,'2:28','Kayfa takfuroona billahiwakuntum amwatan faahyakum thumma yumeetukumthumma yuhyeekum thumma ilayhi turjaAAoon');
INSERT INTO "transliteration" VALUES(2,29,'2:29','Huwa allathee khalaqa lakum mafee al-ardi jameeAAan thumma istawa ila assama-ifasawwahunna sabAAa samawatin wahuwa bikullishay-in AAaleem');
INSERT INTO "transliteration" VALUES(2,30,'2:30','Wa-ith qala rabbuka lilmala-ikatiinnee jaAAilun fee al-ardi khaleefatan qalooatajAAalu feeha man yufsidu feeha wayasfiku addimaawanahnu nusabbihu bihamdika wanuqaddisu lakaqala innee aAAlamu ma la taAAlamoon');
INSERT INTO "transliteration" VALUES(2,31,'2:31','WaAAallama adama al-asmaakullaha thumma AAaradahum AAala almala-ikatifaqala anbi-oonee bi-asma-i haola-iin kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(2,32,'2:32','Qaloo subhanaka laAAilma lana illa ma AAallamtanainnaka anta alAAaleemu alhakeem');
INSERT INTO "transliteration" VALUES(2,33,'2:33','Qala ya adamu anbi/humbi-asma-ihim falamma anbaahum bi-asma-ihim qalaalam aqul lakum innee aAAlamu ghayba assamawatiwal-ardi waaAAlamu ma tubdoona wamakuntum taktumoon');
INSERT INTO "transliteration" VALUES(2,34,'2:34','Wa-ith qulna lilmala-ikatiosjudoo li-adama fasajadoo illa ibleesa abawastakbara wakana mina alkafireen');
INSERT INTO "transliteration" VALUES(2,35,'2:35','Waqulna ya adamu oskunanta wazawjuka aljannata wakula minha raghadan haythushi/tuma wala taqraba hathihi ashshajaratafatakoona mina aththalimeen');
INSERT INTO "transliteration" VALUES(2,36,'2:36','Faazallahuma ashshaytanuAAanha faakhrajahuma mimma kanafeehi waqulna ihbitoo baAAdukum libaAAdinAAaduwwun walakum fee al-ardi mustaqarrun wamataAAunila heen');
INSERT INTO "transliteration" VALUES(2,37,'2:37','Fatalaqqa adamu min rabbihikalimatin fataba AAalayhi innahu huwa attawwabuarraheem');
INSERT INTO "transliteration" VALUES(2,38,'2:38','Qulna ihbitoo minhajameeAAan fa-imma ya/tiyannakum minnee hudan faman tabiAAahudaya fala khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(2,39,'2:39','Wallatheena kafaroo wakaththaboobi-ayatina ola-ika as-habuannari hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,40,'2:40','Ya banee isra-eela othkurooniAAmatiya allatee anAAamtu AAalaykum waawfoo biAAahdee oofibiAAahdikum wa-iyyaya farhaboon');
INSERT INTO "transliteration" VALUES(2,41,'2:41','Waaminoo bima anzaltu musaddiqanlima maAAakum wala takoonoo awwala kafirinbihi wala tashtaroo bi-ayatee thamananqaleelan wa-iyyaya fattaqoon');
INSERT INTO "transliteration" VALUES(2,42,'2:42','Wala talbisoo alhaqqa bilbatiliwataktumoo alhaqqa waantum taAAlamoon');
INSERT INTO "transliteration" VALUES(2,43,'2:43','Waaqeemoo assalata waatooazzakata warkaAAoo maAAa arrakiAAeen');
INSERT INTO "transliteration" VALUES(2,44,'2:44','Ata/muroona annasa bilbirriwatansawna anfusakum waantum tatloona alkitaba afalataAAqiloon');
INSERT INTO "transliteration" VALUES(2,45,'2:45','WastaAAeenoo bissabriwassalati wa-innaha lakabeeratunilla AAala alkhashiAAeen');
INSERT INTO "transliteration" VALUES(2,46,'2:46','Allatheena yathunnoonaannahum mulaqoo rabbihim waannahum ilayhi rajiAAoon');
INSERT INTO "transliteration" VALUES(2,47,'2:47','Ya banee isra-eela othkurooniAAmatiya allatee anAAamtu AAalaykum waannee faddaltukumAAala alAAalameen');
INSERT INTO "transliteration" VALUES(2,48,'2:48','Wattaqoo yawman la tajzeenafsun AAan nafsin shay-an wala yuqbalu minha shafaAAatunwala yu/khathu minha AAadlun wala humyunsaroon');
INSERT INTO "transliteration" VALUES(2,49,'2:49','Wa-ith najjaynakum min alifirAAawna yasoomoonakum soo-a alAAathabi yuthabbihoonaabnaakum wayastahyoona nisaakum wafee thalikumbalaon min rabbikum AAatheem');
INSERT INTO "transliteration" VALUES(2,50,'2:50','Wa-ith faraqna bikumu albahrafaanjaynakum waaghraqna ala firAAawnawaantum tanthuroon');
INSERT INTO "transliteration" VALUES(2,51,'2:51','Wa-ith waAAadna moosaarbaAAeena laylatan thumma ittakhathtumu alAAijla minbaAAdihi waantum thalimoon');
INSERT INTO "transliteration" VALUES(2,52,'2:52','Thumma AAafawna AAankum min baAAdi thalikalaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(2,53,'2:53','Wa-ith atayna moosaalkitaba walfurqana laAAallakum tahtadoon');
INSERT INTO "transliteration" VALUES(2,54,'2:54','Wa-ith qala moosaliqawmihi ya qawmi innakum thalamtumanfusakum bittikhathikumu alAAijla fatooboo ilabari-ikum faqtuloo anfusakum thalikumkhayrun lakum AAinda bari-ikum fataba AAalaykuminnahu huwa attawwabu arraheem');
INSERT INTO "transliteration" VALUES(2,55,'2:55','Wa-ith qultum ya moosalan nu/mina laka hatta nara Allahajahratan faakhathatkumu assaAAiqatu waantumtanthuroon');
INSERT INTO "transliteration" VALUES(2,56,'2:56','Thumma baAAathnakum min baAAdimawtikum laAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(2,57,'2:57','Wathallalna AAalaykumualghamama waanzalna AAalaykumu almanna wassalwakuloo min tayyibati ma razaqnakum wamathalamoona walakin kanooanfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(2,58,'2:58','Wa-ith qulna odkhuloo hathihialqaryata fakuloo minha haythu shi/tum raghadan wadkhulooalbaba sujjadan waqooloo hittatun naghfirlakum khatayakum wasanazeedu almuhsineen');
INSERT INTO "transliteration" VALUES(2,59,'2:59','Fabaddala allatheena thalamooqawlan ghayra allathee qeela lahum faanzalna AAalaallatheena thalamoo rijzan mina assama-ibima kanoo yafsuqoon');
INSERT INTO "transliteration" VALUES(2,60,'2:60','Wa-ithi istasqa moosaliqawmihi faqulna idrib biAAasaka alhajarafanfajarat minhu ithnata AAashrata AAaynan qadAAalima kullu onasin mashrabahum kuloo washraboomin rizqi Allahi wala taAAthaw fee al-ardimufsideen');
INSERT INTO "transliteration" VALUES(2,61,'2:61','Wa-ith qultum ya moosalan nasbira AAala taAAamin wahidinfadAAu lana rabbaka yukhrij lana mimmatunbitu al-ardu min baqliha waqiththa-ihawafoomiha waAAadasiha wabasaliha qalaatastabdiloona allathee huwa adna billatheehuwa khayrun ihbitoo misran fa-inna lakum masaaltum waduribat AAalayhimu aththillatu walmaskanatuwabaoo bighadabin mina Allahi thalikabi-annahum kanoo yakfuroona bi-ayati Allahiwayaqtuloona annabiyyeena bighayri alhaqqi thalikabima AAasaw wakanoo yaAAtadoon');
INSERT INTO "transliteration" VALUES(2,62,'2:62','Inna allatheena amanoo wallatheenahadoo wannasara wassabi-eenaman amana biAllahi walyawmi al-akhiriwaAAamila salihan falahum ajruhum AAinda rabbihimwala khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(2,63,'2:63','Wa-ith akhathna meethaqakumwarafaAAna fawqakumu attoora khuthooma ataynakum biquwwatin wathkurooma feehi laAAallakum tattaqoon');
INSERT INTO "transliteration" VALUES(2,64,'2:64','Thumma tawallaytum min baAAdi thalikafalawla fadlu Allahi AAalaykum warahmatuhulakuntum mina alkhasireen');
INSERT INTO "transliteration" VALUES(2,65,'2:65','Walaqad AAalimtumu allatheenaiAAtadaw minkum fee assabti faqulna lahum koonooqiradatan khasi-een');
INSERT INTO "transliteration" VALUES(2,66,'2:66','FajaAAalnaha nakalanlima bayna yadayha wama khalfahawamawAAithatan lilmuttaqeen');
INSERT INTO "transliteration" VALUES(2,67,'2:67','Wa-ith qala moosaliqawmihi inna Allaha ya/murukum an tathbahoobaqaratan qaloo atattakhithuna huzuwan qalaaAAoothu biAllahi an akoona mina aljahileen');
INSERT INTO "transliteration" VALUES(2,68,'2:68','Qaloo odAAu lana rabbakayubayyin lana ma hiya qala innahu yaqooluinnaha baqaratun la faridun walabikrun AAawanun bayna thalika fafAAaloo matu/maroon');
INSERT INTO "transliteration" VALUES(2,69,'2:69','Qaloo odAAu lana rabbakayubayyin lana ma lawnuha qala innahuyaqoolu innaha baqaratun safrao faqiAAunlawnuha tasurru annathireen');
INSERT INTO "transliteration" VALUES(2,70,'2:70','Qaloo odAAu lana rabbakayubayyin lana ma hiya inna albaqara tashabahaAAalayna wa-inna in shaa Allahulamuhtadoon');
INSERT INTO "transliteration" VALUES(2,71,'2:71','Qala innahu yaqoolu innahabaqaratun la thaloolun tutheeru al-arda walatasqee alhartha musallamatun la shiyata feehaqaloo al-ana ji/ta bilhaqqi fathabahoohawama kadoo yafAAaloon');
INSERT INTO "transliteration" VALUES(2,72,'2:72','Wa-ith qataltum nafsan faddara/tumfeeha wallahu mukhrijun ma kuntumtaktumoon');
INSERT INTO "transliteration" VALUES(2,73,'2:73','Faqulna idriboohu bibaAAdihakathalika yuhyee Allahu almawtawayureekum ayatihi laAAallakum taAAqiloon');
INSERT INTO "transliteration" VALUES(2,74,'2:74','Thumma qasat quloobukum min baAAdi thalikafahiya kalhijarati aw ashaddu qaswatanwa-inna mina alhijarati lama yatafajjaruminhu al-anharu wa-inna minha lamayashshaqqaqu fayakhruju minhu almao wa-inna minhalama yahbitu min khashyati Allahi wamaAllahu bighafilin AAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(2,75,'2:75','AfatatmaAAoona an yu/minoo lakumwaqad kana fareequn minhum yasmaAAoona kalama Allahithumma yuharrifoonahu min baAAdi ma AAaqaloohuwahum yaAAlamoon');
INSERT INTO "transliteration" VALUES(2,76,'2:76','Wa-itha laqoo allatheena amanooqaloo amanna wa-itha khalabaAAduhum ila baAAdin qaloo atuhaddithoonahumbima fataha Allahu AAalaykum liyuhajjookumbihi AAinda rabbikum afala taAAqiloon');
INSERT INTO "transliteration" VALUES(2,77,'2:77','Awa la yaAAlamoona anna AllahayaAAlamu ma yusirroona wama yuAAlinoon');
INSERT INTO "transliteration" VALUES(2,78,'2:78','Waminhum ommiyyoona la yaAAlamoonaalkitaba illa amaniyya wa-in hum illayathunnoon');
INSERT INTO "transliteration" VALUES(2,79,'2:79','Fawaylun lillatheena yaktuboona alkitababi-aydeehim thumma yaqooloona hatha min AAindi Allahiliyashtaroo bihi thamanan qaleelan fawaylun lahum mimmakatabat aydeehim wawaylun lahum mimma yaksiboon');
INSERT INTO "transliteration" VALUES(2,80,'2:80','Waqaloo lan tamassana annaruilla ayyaman maAAdoodatan qul attakhathtumAAinda Allahi AAahdan falan yukhlifa AllahuAAahdahu am taqooloona AAala Allahi ma lataAAlamoon');
INSERT INTO "transliteration" VALUES(2,81,'2:81','Bala man kasaba sayyi-atan waahatatbihi khatee-atuhu faola-ika as-habu annarihum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,82,'2:82','Wallatheena amanoowaAAamiloo assalihati ola-ika as-habualjannati hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,83,'2:83','Wa-ith akhathna meethaqabanee isra-eela la taAAbudoona illa Allahawabilwalidayni ihsanan watheealqurba walyatama walmasakeeniwaqooloo linnasi husnan waaqeemoo assalatawaatoo azzakata thumma tawallaytum illaqaleelan minkum waantum muAAridoon');
INSERT INTO "transliteration" VALUES(2,84,'2:84','Wa-ith akhathna meethaqakumla tasfikoona dimaakum wala tukhrijoonaanfusakum min diyarikum thumma aqrartum waantum tashhadoon');
INSERT INTO "transliteration" VALUES(2,85,'2:85','Thumma antum haola-itaqtuloona anfusakum watukhrijoona fareeqan minkum min diyarihimtathaharoona AAalayhim bil-ithmi walAAudwaniwa-in ya/tookum osara tufadoohum wahuwa muharramunAAalaykum ikhrajuhum afatu/minoona bibaAAdi alkitabiwatakfuroona bibaAAdin fama jazao manyafAAalu thalika minkum illa khizyun fee alhayatiaddunya wayawma alqiyamati yuraddoona ilaashaddi alAAathabi wama Allahu bighafilinAAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(2,86,'2:86','Ola-ika allatheena ishtarawooalhayata addunya bil-akhiratifala yukhaffafu AAanhumu alAAathabu wala humyunsaroon');
INSERT INTO "transliteration" VALUES(2,87,'2:87','Walaqad atayna moosaalkitaba waqaffayna min baAAdihi birrusuliwaatayna AAeesa ibna maryama albayyinatiwaayyadnahu biroohi alqudusi afakullama jaakumrasoolun bima la tahwa anfusukumuistakbartum fafareeqan kaththabtum wafareeqan taqtuloon');
INSERT INTO "transliteration" VALUES(2,88,'2:88','Waqaloo quloobuna ghulfun ballaAAanahumu Allahu bikufrihim faqaleelan mayu/minoon');
INSERT INTO "transliteration" VALUES(2,89,'2:89','Walamma jaahum kitabunmin AAindi Allahi musaddiqun lima maAAahumwakanoo min qablu yastaftihoona AAala allatheenakafaroo falamma jaahum ma AAarafoo kafaroobihi falaAAnatu Allahi AAala alkafireen');
INSERT INTO "transliteration" VALUES(2,90,'2:90','Bi/sama ishtaraw bihi anfusahum anyakfuroo bima anzala Allahu baghyan an yunazzilaAllahu min fadlihi AAala man yashaomin AAibadihi fabaoo bighadabin AAalaghadabin walilkafireena AAathabun muheen');
INSERT INTO "transliteration" VALUES(2,91,'2:91','Wa-itha qeela lahum aminoo bimaanzala Allahu qaloo nu/minu bima onzilaAAalayna wayakfuroona bima waraahu wahuwa alhaqqumusaddiqan lima maAAahum qul falima taqtuloonaanbiyaa Allahi min qablu in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(2,92,'2:92','Walaqad jaakum moosa bilbayyinatithumma ittakhathtumu alAAijla min baAAdihi waantum thalimoon');
INSERT INTO "transliteration" VALUES(2,93,'2:93','Wa-ith akhathna meethaqakumwarafaAAna fawqakumu attoora khuthooma ataynakum biquwwatin wasmaAAoo qaloosamiAAna waAAasayna waoshriboo feequloobihimu alAAijla bikufrihim qul bi/sama ya/murukumbihi eemanukum in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(2,94,'2:94','Qul in kanat lakumu addarual-akhiratu AAinda Allahi khalisatanmin dooni annasi fatamannawoo almawta in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(2,95,'2:95','Walan yatamannawhu abadan bimaqaddamat aydeehim wallahu AAaleemun biaththalimeen');
INSERT INTO "transliteration" VALUES(2,96,'2:96','Walatajidannahum ahrasa annasiAAala hayatin wamina allatheenaashrakoo yawaddu ahaduhum law yuAAammaru alfa sanatin wamahuwa bimuzahzihihi mina alAAathabi anyuAAammara wallahu baseerun bimayaAAmaloon');
INSERT INTO "transliteration" VALUES(2,97,'2:97','Qul man kana AAaduwwan lijibreelafa-innahu nazzalahu AAala qalbika bi-ithni Allahimusaddiqan lima bayna yadayhi wahudan wabushralilmu/mineen');
INSERT INTO "transliteration" VALUES(2,98,'2:98','Man kana AAaduwwan lillahiwamala-ikatihi warusulihi wajibreela wameekalafa-inna Allaha AAaduwwun lilkafireen');
INSERT INTO "transliteration" VALUES(2,99,'2:99','Walaqad anzalna ilayka ayatinbayyinatin wama yakfuru biha illa alfasiqoon');
INSERT INTO "transliteration" VALUES(2,100,'2:100','Awa kullama AAahadoo AAahdannabathahu fareequn minhum bal aktharuhum layu/minoon');
INSERT INTO "transliteration" VALUES(2,101,'2:101','Walamma jaahum rasoolun minAAindi Allahi musaddiqun lima maAAahum nabathafareequn mina allatheena ootoo alkitaba kitabaAllahi waraa thuhoorihim kaannahum layaAAlamoon');
INSERT INTO "transliteration" VALUES(2,102,'2:102','WattabaAAoo ma tatloo ashshayateenuAAala mulki sulaymana wama kafara sulaymanuwalakinna ashshayateena kafarooyuAAallimoona annasa assihra wamaonzila AAala almalakayni bibabila harootawamaroota wama yuAAallimani min ahadinhatta yaqoola innama nahnufitnatun fala takfur fayataAAallamoona minhuma mayufarriqoona bihi bayna almar-i wazawjihi wama hum bidarreenabihi min ahadin illa bi-ithni AllahiwayataAAallamoona ma yadurruhum walayanfaAAuhum walaqad AAalimoo lamani ishtarahu malahu fee al-akhirati min khalaqin walabi/sa masharaw bihi anfusahum law kanoo yaAAlamoon');
INSERT INTO "transliteration" VALUES(2,103,'2:103','Walaw annahum amanoo wattaqawlamathoobatun min AAindi Allahi khayrun law kanooyaAAlamoon');
INSERT INTO "transliteration" VALUES(2,104,'2:104','Ya ayyuha allatheena amanoola taqooloo raAAina waqooloo onthurnawasmaAAoo walilkafireena AAathabun aleem');
INSERT INTO "transliteration" VALUES(2,105,'2:105','Ma yawaddu allatheena kafaroomin ahli alkitabi wala almushrikeena an yunazzalaAAalaykum min khayrin min rabbikum wallahu yakhtassubirahmatihi man yashao wallahu thooalfadli alAAatheem');
INSERT INTO "transliteration" VALUES(2,106,'2:106','Ma nansakh min ayatin awnunsiha na/ti bikhayrin minha aw mithlihaalam taAAlam anna Allaha AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,107,'2:107','Alam taAAlam anna Allaha lahu mulkuassamawati wal-ardi wamalakum min dooni Allahi min waliyyin wala naseer');
INSERT INTO "transliteration" VALUES(2,108,'2:108','Am tureedoona an tas-aloo rasoolakum kamasu-ila moosa min qablu waman yatabaddali alkufra bil-eemanifaqad dalla sawaa assabeel');
INSERT INTO "transliteration" VALUES(2,109,'2:109','Wadda katheerun min ahli alkitabilaw yaruddoonakum min baAAdi eemanikum kuffaran hasadanmin AAindi anfusihim min baAAdi ma tabayyana lahumu alhaqqufaAAfoo wasfahoo hattaya/tiya Allahu bi-amrihi inna Allaha AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,110,'2:110','Waaqeemoo assalata waatooazzakata wama tuqaddimoo li-anfusikum minkhayrin tajidoohu AAinda Allahi inna Allaha bimataAAmaloona baseer');
INSERT INTO "transliteration" VALUES(2,111,'2:111','Waqaloo lan yadkhula aljannata illaman kana hoodan aw nasara tilka amaniyyuhumqul hatoo burhanakum in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(2,112,'2:112','Bala man aslama wajhahu lillahiwahuwa muhsinun falahu ajruhu AAinda rabbihi walakhawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(2,113,'2:113','Waqalati alyahoodu laysati annasaraAAala shay-in waqalati annasaralaysati alyahoodu AAala shay-in wahum yatloona alkitabakathalika qala allatheena layaAAlamoona mithla qawlihim fallahu yahkumubaynahum yawma alqiyamati feema kanoo feehiyakhtalifoon');
INSERT INTO "transliteration" VALUES(2,114,'2:114','Waman athlamu mimman manaAAamasajida Allahi an yuthkara feehaismuhu wasaAAa fee kharabiha ola-ikama kana lahum an yadkhulooha illa kha-ifeenalahum fee addunya khizyun walahum fee al-akhiratiAAathabun AAatheem');
INSERT INTO "transliteration" VALUES(2,115,'2:115','Walillahi almashriqu walmaghribufaaynama tuwalloo fathamma wajhu Allahi inna AllahawasiAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,116,'2:116','Waqaloo itakhatha Allahuwaladan subhanahu bal lahu ma fee assamawatiwal-ardi kullun lahu qanitoon');
INSERT INTO "transliteration" VALUES(2,117,'2:117','BadeeAAu assamawatiwal-ardi wa-itha qada amran fa-innamayaqoolu lahu kun fayakoon');
INSERT INTO "transliteration" VALUES(2,118,'2:118','Waqala allatheena layaAAlamoona lawla yukallimuna Allahu awta/teena ayatun kathalika qala allatheenamin qablihim mithla qawlihim tashabahat quloobuhum qadbayyanna al-ayati liqawmin yooqinoon');
INSERT INTO "transliteration" VALUES(2,119,'2:119','Inna arsalnaka bilhaqqibasheeran wanatheeran wala tus-alu Aaan as-habialjaheem');
INSERT INTO "transliteration" VALUES(2,120,'2:120','Walan tarda AAanka alyahoodu walaannasara hatta tattabiAAamillatahum qul inna huda Allahi huwa alhudawala-ini ittabaAAta ahwaahum baAAda allathee jaakamina alAAilmi ma laka mina Allahi min waliyyin walanaseer');
INSERT INTO "transliteration" VALUES(2,121,'2:121','Allatheena ataynahumualkitaba yatloonahu haqqa tilawatihi ola-ikayu/minoona bihi waman yakfur bihi faola-ika humu alkhasiroon');
INSERT INTO "transliteration" VALUES(2,122,'2:122','Ya banee isra-eela othkurooniAAmatiya allatee anAAamtu AAalaykum waannee faddaltukumAAala alAAalameen');
INSERT INTO "transliteration" VALUES(2,123,'2:123','Wattaqoo yawman la tajzeenafsun AAan nafsin shay-an wala yuqbalu minhaAAadlun wala tanfaAAuha shafaAAatun walahum yunsaroon');
INSERT INTO "transliteration" VALUES(2,124,'2:124','Wa-ithi ibtala ibraheemarabbuhu bikalimatin faatammahunna qala innee jaAAilukalinnasi imaman qala wamin thurriyyateeqala la yanalu AAahdee aththalimeen');
INSERT INTO "transliteration" VALUES(2,125,'2:125','Wa-ith jaAAalna albayta mathabatanlinnasi waamnan wattakhithoo minmaqami ibraheema musallan waAAahidnaila ibraheema wa-ismaAAeela an tahhirabaytiya litta-ifeena walAAakifeenawarrukkaAAi assujood');
INSERT INTO "transliteration" VALUES(2,126,'2:126','Wa-ith qala ibraheemurabbi ijAAal hatha baladan aminan warzuqahlahu mina aththamarati man amana minhumbillahi walyawmi al-akhiri qalawaman kafara faomattiAAuhu qaleelan thumma adtarruhu ilaAAathabi annari wabi/sa almaseer');
INSERT INTO "transliteration" VALUES(2,127,'2:127','Wa-ith yarfaAAu ibraheemualqawaAAida mina albayti wa-ismaAAeelu rabbanataqabbal minna innaka anta assameeAAu alAAaleem');
INSERT INTO "transliteration" VALUES(2,128,'2:128','Rabbana wajAAalnamuslimayni laka wamin thurriyyatina ommatanmuslimatan laka waarina manasikana watubAAalayna innaka anta attawwabu arraheem');
INSERT INTO "transliteration" VALUES(2,129,'2:129','Rabbana wabAAath feehimrasoolan minhum yatloo AAalayhim ayatikawayuAAallimuhumu alkitaba walhikmatawayuzakkeehim innaka anta alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(2,130,'2:130','Waman yarghabu AAan millati ibraheemailla man safiha nafsahu walaqadi istafaynahufee addunya wa-innahu fee al-akhiratilamina assaliheen');
INSERT INTO "transliteration" VALUES(2,131,'2:131','Ith qala lahu rabbuhu aslim qalaaslamtu lirabbi alAAalameen');
INSERT INTO "transliteration" VALUES(2,132,'2:132','Wawassa biha ibraheemubaneehi wayaAAqoobu ya baniyya inna Allaha istafalakumu addeena fala tamootunna illa waantummuslimoon');
INSERT INTO "transliteration" VALUES(2,133,'2:133','Am kuntum shuhadaa ith hadarayaAAqooba almawtu ith qala libaneehi mataAAbudoona min baAAdee qaloo naAAbudu ilahakawa-ilaha aba-ika ibraheema wa-ismaAAeelawa-ishaqa ilahan wahidan wanahnu lahumuslimoon');
INSERT INTO "transliteration" VALUES(2,134,'2:134','Tilka ommatun qad khalat laha makasabat walakum ma kasabtum wala tus-aloona AAammakano yaAAmaloon');
INSERT INTO "transliteration" VALUES(2,135,'2:135','Waqaloo koonoo hoodan aw nasaratahtadoo qul bal millata ibraheema haneefan wamakana mina almushrikeen');
INSERT INTO "transliteration" VALUES(2,136,'2:136','Qooloo amanna billahiwama onzila ilayna wama onzila ilaibraheema wa-ismaAAeela wa-ishaqawayaAAqooba wal-asbati wama ootiya moosawaAAeesa wama ootiya annabiyyoona minrabbihim la nufarriqu bayna ahadin minhum wanahnulahu muslimoon');
INSERT INTO "transliteration" VALUES(2,137,'2:137','Fa-in amanoo bimithli ma amantumbihi faqadi ihtadaw wa-in tawallaw fa-innama hum fee shiqaqinfasayakfeekahumu Allahu wahuwa assameeAAualAAaleem');
INSERT INTO "transliteration" VALUES(2,138,'2:138','Sibghata Allahi waman ahsanumina Allahi sibghatan wanahnu lahu AAabidoon');
INSERT INTO "transliteration" VALUES(2,139,'2:139','Qul atuhajjoonana fee Allahiwahuwa rabbuna warabbukum walana aAAmalunawalakum aAAmalukum wanahnu lahu mukhlisoon');
INSERT INTO "transliteration" VALUES(2,140,'2:140','Am taqooloona inna ibraheema wa-ismaAAeelawa-ishaqa wayaAAqooba wal-asbata kanoohoodan aw nasara qul aantum aAAlamu ami Allahuwaman athlamu mimman katama shahadatanAAindahu mina Allahi wama Allahu bighafilinAAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(2,141,'2:141','Tilka ommatun qad khalat laha makasabat walakum ma kasabtum wala tus-aloona AAammakanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(2,142,'2:142','Sayaqoolu assufahao mina annasima wallahum AAan qiblatihimu allatee kanooAAalayha qul lillahi almashriqu walmaghribuyahdee man yashao ila siratinmustaqeem');
INSERT INTO "transliteration" VALUES(2,143,'2:143','Wakathalika jaAAalnakumommatan wasatan litakoonoo shuhadaa AAala annasiwayakoona arrasoolu AAalaykum shaheedan wamajaAAalna alqiblata allatee kunta AAalayha illalinaAAlama man yattabiAAu arrasoola mimman yanqalibu AAalaAAaqibayhi wa-in kanat lakabeeratan illa AAalaallatheena hada Allahu wama kanaAllahu liyudeeAAa eemanakum inna Allahabinnasi laraoofun raheem');
INSERT INTO "transliteration" VALUES(2,144,'2:144','Qad nara taqalluba wajhika fee assama-ifalanuwalliyannaka qiblatan tardaha fawalli wajhakashatra almasjidi alharami wahaythu makuntum fawalloo wujoohakum shatrahu wa-inna allatheenaootoo alkitaba layaAAlamoona annahu alhaqqu minrabbihim wama Allahu bighafilin AAammayaAAmaloon');
INSERT INTO "transliteration" VALUES(2,145,'2:145','Wala-in atayta allatheena ootooalkitaba bikulli ayatin ma tabiAAooqiblataka wama anta bitabiAAin qiblatahum wamabaAAduhum bitabiAAin qiblata baAAdinwala-ini ittabaAAta ahwaahum min baAAdi ma jaakamina alAAilmi innaka ithan lamina aththalimeen');
INSERT INTO "transliteration" VALUES(2,146,'2:146','Allatheena ataynahumualkitaba yaAArifoonahu kama yaAArifoona abnaahumwa-inna fareeqan minhum layaktumoona alhaqqa wahumyaAAlamoon');
INSERT INTO "transliteration" VALUES(2,147,'2:147','Alhaqqu min rabbika falatakoonanna mina almumtareen');
INSERT INTO "transliteration" VALUES(2,148,'2:148','Walikullin wijhatun huwa muwalleehafastabiqoo alkhayrati aynama takoonoo ya/tibikumu Allahu jameeAAan inna Allaha AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,149,'2:149','Wamin haythu kharajta fawalliwajhaka shatra almasjidi alharami wa-innahulalhaqqu min rabbika wama Allahu bighafilinAAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(2,150,'2:150','Wamin haythu kharajta fawalliwajhaka shatra almasjidi alharami wahaythuma kuntum fawalloo wujoohakum shatrahu li-allayakoona linnasi AAalaykum hujjatun illaallatheena thalamoo minhum falatakhshawhum wakhshawnee wali-otimma niAAmatee AAalaykumwalaAAallakum tahtadoon');
INSERT INTO "transliteration" VALUES(2,151,'2:151','Kama arsalna feekum rasoolanminkum yatloo AAalaykum ayatinawayuzakkeekum wayuAAallimukumu alkitaba walhikmatawayuAAallimukum ma lam takoonoo taAAlamoon');
INSERT INTO "transliteration" VALUES(2,152,'2:152','Fathkuroonee athkurkumwashkuroo lee wala takfuroon');
INSERT INTO "transliteration" VALUES(2,153,'2:153','Ya ayyuha allatheena amanooistaAAeenoo bissabri wassalatiinna Allaha maAAa assabireen');
INSERT INTO "transliteration" VALUES(2,154,'2:154','Wala taqooloo liman yuqtalu feesabeeli Allahi amwatun bal ahyaon walakinla tashAAuroon');
INSERT INTO "transliteration" VALUES(2,155,'2:155','Walanabluwannakum bishay-in mina alkhawfiwaljooAAi wanaqsin mina al-amwali wal-anfusiwaththamarati wabashshiri assabireen');
INSERT INTO "transliteration" VALUES(2,156,'2:156','Allatheena itha asabat-hummuseebatun qaloo inna lillahi wa-innailayhi rajiAAoon');
INSERT INTO "transliteration" VALUES(2,157,'2:157','Ola-ika AAalayhim salawatunmin rabbihim warahmatun waola-ika humu almuhtadoon');
INSERT INTO "transliteration" VALUES(2,158,'2:158','Inna assafa walmarwatamin shaAAa-iri Allahi faman hajja albaytaawi iAAtamara fala junaha AAalayhi an yattawwafabihima waman tatawwaAAa khayran fa-inna Allahashakirun AAaleem');
INSERT INTO "transliteration" VALUES(2,159,'2:159','Inna allatheena yaktumoona maanzalna mina albayyinati walhuda minbaAAdi ma bayyannahu linnasi feealkitabi ola-ika yalAAanuhumu AllahuwayalAAanuhumu allaAAinoon');
INSERT INTO "transliteration" VALUES(2,160,'2:160','Illa allatheena taboowaaslahoo wabayyanoo faola-ika atoobuAAalayhim waana attawwabu arraheem');
INSERT INTO "transliteration" VALUES(2,161,'2:161','Inna allatheena kafaroo wamatoowahum kuffarun ola-ika AAalayhim laAAnatu Allahiwalmala-ikati wannasi ajmaAAeen');
INSERT INTO "transliteration" VALUES(2,162,'2:162','Khalideena feeha layukhaffafu AAanhumu alAAathabu wala hum yuntharoon');
INSERT INTO "transliteration" VALUES(2,163,'2:163','Wa-ilahukum ilahun wahidunla ilaha illa huwa arrahmanuarraheem');
INSERT INTO "transliteration" VALUES(2,164,'2:164','Inna fee khalqi assamawatiwal-ardi wakhtilafi allayli wannahariwalfulki allatee tajree fee albahri bimayanfaAAu annasa wama anzala Allahumina assama-i min ma-in faahyabihi al-arda baAAda mawtiha wabaththa feehamin kulli dabbatin watasreefi arriyahiwassahabi almusakhkhari bayna assama-iwal-ardi laayatin liqawminyaAAqiloon');
INSERT INTO "transliteration" VALUES(2,165,'2:165','Wamina annasi man yattakhithumin dooni Allahi andadan yuhibboonahum kahubbiAllahi wallatheena amanoo ashaddu hubbanlillahi walaw yara allatheena thalamooith yarawna alAAathaba anna alquwwata lillahijameeAAan waanna Allaha shadeedu alAAathab');
INSERT INTO "transliteration" VALUES(2,166,'2:166','Ith tabarraa allatheenaittubiAAoo mina allatheena ittabaAAoo waraawoo alAAathabawataqattaAAat bihimu al-asbab');
INSERT INTO "transliteration" VALUES(2,167,'2:167','Waqala allatheena ittabaAAoolaw anna lana karratan fanatabarraa minhum kamatabarraoo minna kathalika yureehimu AllahuaAAmalahum hasaratin AAalayhim wamahum bikharijeena mina annar');
INSERT INTO "transliteration" VALUES(2,168,'2:168','Ya ayyuha annasukuloo mimma fee al-ardi halalan tayyibanwala tattabiAAoo khutuwati ashshaytaniinnahu lakum AAaduwwun mubeen');
INSERT INTO "transliteration" VALUES(2,169,'2:169','Innama ya/murukum bissoo-iwalfahsha-i waan taqooloo AAala Allahima la taAAlamoon');
INSERT INTO "transliteration" VALUES(2,170,'2:170','Wa-itha qeela lahumu ittabiAAoo maanzala Allahu qaloo bal nattabiAAu ma alfaynaAAalayhi abaana awa law kana abaohumla yaAAqiloona shay-an wala yahtadoon');
INSERT INTO "transliteration" VALUES(2,171,'2:171','Wamathalu allatheena kafarookamathali allathee yanAAiqu bima la yasmaAAuilla duAAaan wanidaan summun bukmunAAumyun fahum la yaAAqiloon');
INSERT INTO "transliteration" VALUES(2,172,'2:172','Ya ayyuha allatheena amanookuloo min tayyibati ma razaqnakum washkuroolillahi in kuntum iyyahu taAAbudoon');
INSERT INTO "transliteration" VALUES(2,173,'2:173','Innama harrama AAalaykumualmaytata waddama walahma alkhinzeeri wamaohilla bihi lighayri Allahi famani idturra ghayra baghinwala AAadin fala ithma AAalayhi inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(2,174,'2:174','Inna allatheena yaktumoona maanzala Allahu mina alkitabi wayashtaroona bihithamanan qaleelan ola-ika ma ya/kuloona fee butoonihimilla annara wala yukallimuhumu Allahuyawma alqiyamati wala yuzakkeehim walahum AAathabunaleem');
INSERT INTO "transliteration" VALUES(2,175,'2:175','Ola-ika allatheena ishtarawooaddalalata bilhuda walAAathababilmaghfirati fama asbarahum AAala annar');
INSERT INTO "transliteration" VALUES(2,176,'2:176','Thalika bi-anna Allahanazzala alkitaba bilhaqqi wa-inna allatheenaikhtalafoo fee alkitabi lafee shiqaqin baAAeed');
INSERT INTO "transliteration" VALUES(2,177,'2:177','Laysa albirra an tuwalloo wujoohakum qibalaalmashriqi walmaghribi walakinna albirra man amanabillahi walyawmi al-akhiri walmala-ikatiwalkitabi wannabiyyeena waataalmala AAala hubbihi thawee alqurbawalyatama walmasakeena wabnaalssabeeli wassa-ileena wafee arriqabiwaaqama assalata waataazzakata walmoofoona biAAahdihim ithaAAahadoo wassabireena fee alba/sa-iwaddarra-i waheena alba/si ola-ikaallatheena sadaqoo waola-ika humualmuttaqoon');
INSERT INTO "transliteration" VALUES(2,178,'2:178','Ya ayyuha allatheena amanookutiba AAalaykumu alqisasu fee alqatla alhurrubilhurri walAAabdu bilAAabdi walonthabilontha faman AAufiya lahu min akheehi shay-on fattibaAAunbilmaAAroofi waadaon ilayhi bi-ihsaninthalika takhfeefun min rabbikum warahmatun famaniiAAtada baAAda thalika falahu AAathabunaleem');
INSERT INTO "transliteration" VALUES(2,179,'2:179','Walakum fee alqisasi hayatunya olee al-albabi laAAallakum tattaqoon');
INSERT INTO "transliteration" VALUES(2,180,'2:180','Kutiba AAalaykum itha hadaraahadakumu almawtu in taraka khayran alwasiyyatulilwalidayni wal-aqrabeena bilmaAAroofi haqqanAAala almuttaqeen');
INSERT INTO "transliteration" VALUES(2,181,'2:181','Faman baddalahu baAAda ma samiAAahufa-innama ithmuhu AAala allatheenayubaddiloonahu inna Allaha sameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,182,'2:182','Faman khafa min moosinjanafan aw ithman faaslaha baynahum falaithma AAalayhi inna Allaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(2,183,'2:183','Ya ayyuha allatheena amanookutiba AAalaykumu assiyamu kamakutiba AAala allatheena min qablikum laAAallakumtattaqoon');
INSERT INTO "transliteration" VALUES(2,184,'2:184','Ayyaman maAAdoodatin faman kanaminkum mareedan aw AAala safarin faAAiddatun minayyamin okhara waAAala allatheena yuteeqoonahufidyatun taAAamu miskeenin faman tatawwaAAakhayran fahuwa khayrun lahu waan tasoomoo khayrun lakum inkuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(2,185,'2:185','Shahru ramadana allatheeonzila feehi alqur-anu hudan linnasiwabayyinatin mina alhuda walfurqanifaman shahida minkumu ashshahra falyasumhu waman kanamareedan aw AAala safarin faAAiddatun min ayyaminokhara yureedu Allahu bikumu alyusra wala yureedubikumu alAAusra walitukmiloo alAAiddata walitukabbiroo AllahaAAala ma hadakum walaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(2,186,'2:186','Wa-itha saalaka AAibadeeAAannee fa-innee qareebun ojeebu daAAwata addaAAiitha daAAani falyastajeeboo lee walyu/minoo beelaAAallahum yarshudoon');
INSERT INTO "transliteration" VALUES(2,187,'2:187','Ohilla lakum laylata assiyamiarrafathu ila nisa-ikum hunna libasunlakum waantum libasun lahunna AAalima Allahuannakum kuntum takhtanoona anfusakum fatabaAAalaykum waAAafa AAankum fal-ana bashiroohunnawabtaghoo ma kataba Allahu lakum wakuloo washraboohatta yatabayyana lakumu alkhaytu al-abyadumina alkhayti al-aswadi mina alfajri thumma atimmoo assiyamaila allayli wala tubashiroohunna waantum AAakifoonafee almasajidi tilka hudoodu Allahi falataqrabooha kathalika yubayyinu Allahu ayatihilinnasi laAAallahum yattaqoon');
INSERT INTO "transliteration" VALUES(2,188,'2:188','Wala ta/kuloo amwalakumbaynakum bilbatili watudloo biha ilaalhukkami lita/kuloo fareeqan min amwali annasibil-ithmi waantum taAAlamoon');
INSERT INTO "transliteration" VALUES(2,189,'2:189','Yas-aloonaka AAani al-ahillati qul hiya mawaqeetulinnasi walhajji walaysa albirrubi-an ta/too albuyoota min thuhooriha walakinnaalbirra mani ittaqa wa/too albuyoota min abwabihawattaqoo Allaha laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(2,190,'2:190','Waqatiloo fee sabeeli Allahiallatheena yuqatiloonakum wala taAAtadooinna Allaha la yuhibbu almuAAtadeen');
INSERT INTO "transliteration" VALUES(2,191,'2:191','Waqtuloohum haythuthaqiftumoohum waakhrijoohum min haythu akhrajookum walfitnatuashaddu mina alqatli wala tuqatiloohum AAindaalmasjidi alharami hatta yuqatilookumfeehi fa-in qatalookum faqtuloohum kathalikajazao alkafireen');
INSERT INTO "transliteration" VALUES(2,192,'2:192','Fa-ini intahaw fa-inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(2,193,'2:193','Waqatiloohum hatta latakoona fitnatun wayakoona addeenu lillahi fa-iniintahaw fala AAudwana illa AAala aththalimeen');
INSERT INTO "transliteration" VALUES(2,194,'2:194','Ashshahru alharamu bishshahrialharami walhurumatu qisasunfamani iAAtada AAalaykum faAAtadoo AAalayhibimithli ma iAAtada AAalaykum wattaqoo AllahawaAAlamoo anna Allaha maAAa almuttaqeen');
INSERT INTO "transliteration" VALUES(2,195,'2:195','Waanfiqoo fee sabeeli Allahi walatulqoo bi-aydeekum ila attahlukati waahsinooinna Allaha yuhibbu almuhsineen');
INSERT INTO "transliteration" VALUES(2,196,'2:196','Waatimmoo alhajja walAAumratalillahi fa-in ohsirtum fama istaysara minaalhadyi wala tahliqoo ruoosakum hattayablugha alhadyu mahillahu faman kana minkum mareedanaw bihi athan min ra/sihi fafidyatun min siyaminaw sadaqatin aw nusukin fa-itha amintum famantamattaAAa bilAAumrati ila alhajji famaistaysara mina alhadyi faman lam yajid fasiyamuthalathati ayyamin fee alhajji wasabAAatin itharajaAAtum tilka AAasharatun kamilatun thalika limanlam yakun ahluhu hadiree almasjidi alharamiwattaqoo Allaha waAAlamoo anna Allahashadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(2,197,'2:197','Alhajju ashhurun maAAloomatunfaman farada feehinna alhajja fala rafathawala fusooqa wala jidala fee alhajjiwama tafAAaloo min khayrin yaAAlamhu Allahuwatazawwadoo fa-inna khayra azzadi attaqwawattaqooni ya olee al-albab');
INSERT INTO "transliteration" VALUES(2,198,'2:198','Laysa AAalaykum junahun an tabtaghoofadlan min rabbikum fa-itha afadtum minAAarafatin fathkuroo Allaha AAindaalmashAAari alharami wathkuroohu kamahadakum wa-in kuntum min qablihi lamina addalleen');
INSERT INTO "transliteration" VALUES(2,199,'2:199','Thumma afeedoo min haythu afadaannasu wastaghfiroo Allaha inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(2,200,'2:200','Fa-itha qadaytum manasikakumfathkuroo Allaha kathikrikum abaakumaw ashadda thikran famina annasi manyaqoolu rabbana atina fee addunyawama lahu fee al-akhirati min khalaq');
INSERT INTO "transliteration" VALUES(2,201,'2:201','Waminhum man yaqoolu rabbana atinafee addunya hasanatan wafee al-akhiratihasanatan waqina AAathaba annar');
INSERT INTO "transliteration" VALUES(2,202,'2:202','Ola-ika lahum naseebun mimmakasaboo wallahu sareeAAu alhisab');
INSERT INTO "transliteration" VALUES(2,203,'2:203','Wathkuroo Allaha feeayyamin maAAdoodatin faman taAAajjala fee yawmaynifala ithma AAalayhi waman taakhkhara fala ithmaAAalayhi limani ittaqa wattaqoo Allaha waAAlamooannakum ilayhi tuhsharoon');
INSERT INTO "transliteration" VALUES(2,204,'2:204','Wamina annasi man yuAAjibukaqawluhu fee alhayati addunyawayushhidu Allaha AAala ma fee qalbihiwahuwa aladdu alkhisam');
INSERT INTO "transliteration" VALUES(2,205,'2:205','Wa-itha tawalla saAAafee al-ardi liyufsida feeha wayuhlika alharthawannasla wallahu la yuhibbualfasad');
INSERT INTO "transliteration" VALUES(2,206,'2:206','Wa-itha qeela lahu ittaqi Allahaakhathat-hu alAAizzatu bil-ithmi fahasbuhujahannamu walabi/sa almihad');
INSERT INTO "transliteration" VALUES(2,207,'2:207','Wamina annasi man yashreenafsahu ibtighaa mardati Allahi wallahuraoofun bilAAibad');
INSERT INTO "transliteration" VALUES(2,208,'2:208','Ya ayyuha allatheena amanooodkhuloo fee assilmi kaffatan walatattabiAAoo khutuwati ashshaytaniinnahu lakum AAaduwwun mubeen');
INSERT INTO "transliteration" VALUES(2,209,'2:209','Fa-in zalaltum min baAAdi ma jaatkumualbayyinatu faAAlamoo anna Allaha AAazeezunhakeem');
INSERT INTO "transliteration" VALUES(2,210,'2:210','Hal yanthuroona illaan ya/tiyahumu Allahu fee thulalin minaalghamami walmala-ikatu waqudiyaal-amru wa-ila Allahi turjaAAu al-omoor');
INSERT INTO "transliteration" VALUES(2,211,'2:211','Sal banee isra-eela kam ataynahummin ayatin bayyinatin waman yubaddil niAAmata Allahimin baAAdi ma jaat-hu fa-inna Allahashadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(2,212,'2:212','Zuyyina lillatheena kafaroo alhayatuaddunya wayaskharoona mina allatheena amanoowallatheena ittaqaw fawqahum yawma alqiyamatiwallahu yarzuqu man yashao bighayri hisab');
INSERT INTO "transliteration" VALUES(2,213,'2:213','Kana annasu ommatan wahidatanfabaAAatha Allahu annabiyyeena mubashshireenawamunthireena waanzala maAAahumu alkitaba bilhaqqiliyahkuma bayna annasi feemaikhtalafoo feehi wama ikhtalafa feehi illa allatheenaootoohu min baAAdi ma jaat-humu albayyinatubaghyan baynahum fahada Allahu allatheena amanoolima ikhtalafoo feehi mina alhaqqi bi-ithnihiwallahu yahdee man yashao ila siratinmustaqeem');
INSERT INTO "transliteration" VALUES(2,214,'2:214','Am hasibtum an tadkhuloo aljannatawalamma ya/tikum mathalu allatheena khalaw minqablikum massat-humu alba/sao waddarraowazulziloo hatta yaqoola arrasoolu wallatheenaamanoo maAAahu mata nasru Allahi alainna nasra Allahi qareeb');
INSERT INTO "transliteration" VALUES(2,215,'2:215','Yas-aloonaka matha yunfiqoona qul maanfaqtum min khayrin falilwalidayni wal-aqrabeenawalyatama walmasakeeni wabniassabeeli wama tafAAaloo min khayrin fa-inna Allahabihi AAaleem');
INSERT INTO "transliteration" VALUES(2,216,'2:216','Kutiba AAalaykumu alqitalu wahuwakurhun lakum waAAasa an takrahoo shay-an wahuwa khayrunlakum waAAasa an tuhibboo shay-an wahuwa sharrunlakum wallahu yaAAlamu waantum lataAAlamoon');
INSERT INTO "transliteration" VALUES(2,217,'2:217','Yas-aloonaka AAani ashshahri alharamiqitalin feehi qul qitalun feehi kabeerun wasaddunAAan sabeeli Allahi wakufrun bihi walmasjidi alharamiwa-ikhraju ahlihi minhu akbaru AAinda Allahi walfitnatuakbaru mina alqatli wala yazaloona yuqatiloonakumhatta yaruddookum AAan deenikum ini istataAAoowaman yartadid minkum AAan deenihi fayamut wahuwa kafirunfaola-ika habitat aAAmaluhum fee addunyawal-akhirati waola-ika as-habuannari hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,218,'2:218','Inna allatheena amanoo wallatheenahajaroo wajahadoo fee sabeeli Allahi ola-ikayarjoona rahmata Allahi wallahughafoorun raheem');
INSERT INTO "transliteration" VALUES(2,219,'2:219','Yas-aloonaka AAani alkhamri walmaysiriqul feehima ithmun kabeerun wamanafiAAu linnasiwa-ithmuhuma akbaru min nafAAihima wayas-aloonaka mathayunfiqoona quli alAAafwa kathalika yubayyinu Allahulakumu al-ayati laAAallakum tatafakkaroon');
INSERT INTO "transliteration" VALUES(2,220,'2:220','Fee addunya wal-akhiratiwayas-aloonaka AAani alyatama qul islahunlahum khayrun wa-in tukhalitoohum fa-ikhwanukumwallahu yaAAlamu almufsida mina almuslihiwalaw shaa Allahu laaAAnatakum inna AllahaAAazeezun hakeem');
INSERT INTO "transliteration" VALUES(2,221,'2:221','Wala tankihoo almushrikatihatta yu/minna walaamatun mu/minatun khayrun minmushrikatin walaw aAAjabatkum wala tunkihooalmushrikeena hatta yu/minoo walaAAabdun mu/minunkhayrun min mushrikin walaw aAAjabakum ola-ika yadAAoonaila annari wallahu yadAAooila aljannati walmaghfirati bi-ithnihiwayubayyinu ayatihi linnasilaAAallahum yatathakkaroon');
INSERT INTO "transliteration" VALUES(2,222,'2:222','Wayas-aloonaka AAani almaheediqul huwa athan faAAtaziloo annisaafee almaheedi wala taqraboohunna hattayathurna fa-itha tatahharna fa/toohunna min haythuamarakumu Allahu inna Allaha yuhibbu attawwabeenawayuhibbu almutatahhireen');
INSERT INTO "transliteration" VALUES(2,223,'2:223','Nisaokum harthun lakum fa/tooharthakum anna shi/tum waqaddimoo li-anfusikum wattaqooAllaha waAAlamoo annakum mulaqoohuwabashshiri almu/mineen');
INSERT INTO "transliteration" VALUES(2,224,'2:224','Wala tajAAaloo Allaha AAurdatanli-aymanikum an tabarroo watattaqoo watuslihoobayna annasi wallahu sameeAAunAAaleem');
INSERT INTO "transliteration" VALUES(2,225,'2:225','La yu-akhithukumu Allahubillaghwi fee aymanikum walakin yu-akhithukumbima kasabat quloobukum wallahu ghafoorun haleem');
INSERT INTO "transliteration" VALUES(2,226,'2:226','Lillatheena yu/loona min nisa-ihimtarabbusu arbaAAati ashhurin fa-in faoo fa-inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(2,227,'2:227','Wa-in AAazamoo attalaqafa-inna Allaha sameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,228,'2:228','Walmutallaqatuyatarabbasna bi-anfusihinna thalathata quroo-in walayahillu lahunna an yaktumna ma khalaqa Allahufee arhamihinna in kunna yu/minna billahiwalyawmi al-akhiri wabuAAoolatuhunna ahaqqubiraddihinna fee thalika in aradoo islahanwalahunna mithlu allathee AAalayhinna bilmaAAroofiwalirrijali AAalayhinna darajatun wallahuAAazeezun hakeem');
INSERT INTO "transliteration" VALUES(2,229,'2:229','Attalaqu marratanifa-imsakun bimaAAroofin aw tasreehun bi-ihsaninwala yahillu lakum an ta/khuthoo mimmaataytumoohunna shay-an illa an yakhafaalla yuqeema hudooda Allahi fa-inkhiftum alla yuqeema hudooda Allahifala junaha AAalayhima feema iftadatbihi tilka hudoodu Allahi fala taAAtadoohawaman yataAAadda hudooda Allahi faola-ikahumu aththalimoon');
INSERT INTO "transliteration" VALUES(2,230,'2:230','Fa-in tallaqaha falatahillu lahu min baAAdu hatta tankihazawjan ghayrahu fa-in tallaqaha fala junahaAAalayhima an yatarajaAAa in thannaan yuqeema hudooda Allahi watilka hudooduAllahi yubayyinuha liqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(2,231,'2:231','Wa-itha tallaqtumu annisaafabalaghna ajalahunna faamsikoohunna bimaAAroofin aw sarrihoohunnabimaAAroofin wala tumsikoohunna diraranlitaAAtadoo waman yafAAal thalika faqad thalamanafsahu wala tattakhithoo ayati Allahihuzuwan wathkuroo niAAmata Allahi AAalaykumwama anzala AAalaykum mina alkitabi walhikmatiyaAAithukum bihi wattaqoo Allaha waAAlamooanna Allaha bikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(2,232,'2:232','Wa-itha tallaqtumu annisaafabalaghna ajalahunna fala taAAduloohunna an yankihnaazwajahunna itha taradaw baynahum bilmaAAroofithalika yooAAathu bihi man kanaminkum yu/minu billahi walyawmi al-akhirithalikum azka lakum waatharu wallahuyaAAlamu waantum la taAAlamoon');
INSERT INTO "transliteration" VALUES(2,233,'2:233','Walwalidatu yurdiAAnaawladahunna hawlayni kamilayni liman aradaan yutimma arradaAAata waAAala almawloodilahu rizquhunna wakiswatuhunna bilmaAAroofi latukallafu nafsun illa wusAAaha la tudarrawalidatun biwaladiha wala mawloodun lahubiwaladihi waAAala alwarithi mithlu thalikafa-in arada fisalan AAan taradinminhuma watashawurin fala junahaAAalayhima wa-in aradtum an tastardiAAoo awladakumfala junaha AAalaykum itha sallamtum maataytum bilmaAAroofi wattaqoo AllahawaAAlamoo anna Allaha bima taAAmaloona baseer');
INSERT INTO "transliteration" VALUES(2,234,'2:234','Wallatheena yutawaffawnaminkum wayatharoona azwajan yatarabbasnabi-anfusihinna arbaAAata ashhurin waAAashran fa-ithabalaghna ajalahunna fala junaha AAalaykum feemafaAAalna fee anfusihinna bilmaAAroofi wallahubima taAAmaloona khabeer');
INSERT INTO "transliteration" VALUES(2,235,'2:235','Wala junaha AAalaykum feemaAAarradtum bihi min khitbati annisa-iaw aknantum fee anfusikum AAalima Allahu annakum satathkuroonahunnawalakin la tuwaAAidoohunna sirran illaan taqooloo qawlan maAAroofan wala taAAzimoo AAuqdata annikahihatta yablugha alkitabu ajalahu waAAlamooanna Allaha yaAAlamu ma fee anfusikum fahtharoohuwaAAlamoo anna Allaha ghafoorun haleem');
INSERT INTO "transliteration" VALUES(2,236,'2:236','La junaha AAalaykum in tallaqtumuannisaa ma lam tamassoohunna aw tafridoolahunna fareedatan wamattiAAoohunna AAalaalmoosiAAi qadaruhu waAAala almuqtiri qadaruhu mataAAanbilmaAAroofi haqqan AAala almuhsineen');
INSERT INTO "transliteration" VALUES(2,237,'2:237','Wa-in tallaqtumoohunna min qabli antamassoohunna waqad faradtum lahunna fareedatanfanisfu ma faradtum illa an yaAAfoonaaw yaAAfuwa allathee biyadihi AAuqdatu annikahiwaan taAAfoo aqrabu littaqwa wala tansawooalfadla baynakum inna Allaha bimataAAmaloona baseer');
INSERT INTO "transliteration" VALUES(2,238,'2:238','Hafithoo AAalaassalawati wassalatialwusta waqoomoo lillahi qaniteen');
INSERT INTO "transliteration" VALUES(2,239,'2:239','Fa-in khiftum farijalan aw rukbananfa-itha amintum fathkuroo Allaha kamaAAallamakum ma lam takoonoo taAAlamoon');
INSERT INTO "transliteration" VALUES(2,240,'2:240','Wallatheena yutawaffawnaminkum wayatharoona azwajan wasiyyatanli-azwajihim mataAAan ila alhawlighayra ikhrajin fa-in kharajna fala junahaAAalaykum fee ma faAAalna fee anfusihinna min maAAroofinwallahu AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(2,241,'2:241','Walilmutallaqati mataAAunbilmaAAroofi haqqan AAala almuttaqeen');
INSERT INTO "transliteration" VALUES(2,242,'2:242','Kathalika yubayyinu Allahulakum ayatihi laAAallakum taAAqiloon');
INSERT INTO "transliteration" VALUES(2,243,'2:243','Alam tara ila allatheenakharajoo min diyarihim wahum oloofun hatharaalmawti faqala lahumu Allahu mootoo thumma ahyahuminna Allaha lathoo fadlin AAala annasiwalakinna akthara annasi layashkuroon');
INSERT INTO "transliteration" VALUES(2,244,'2:244','Waqatiloo fee sabeeli AllahiwaAAlamoo anna Allaha sameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,245,'2:245','Man tha allathee yuqriduAllaha qardan hasanan fayudaAAifahulahu adAAafan katheeratan wallahuyaqbidu wayabsutu wa-ilayhi turjaAAoon');
INSERT INTO "transliteration" VALUES(2,246,'2:246','Alam tara ila almala-i min banee isra-eelamin baAAdi moosa ith qaloo linabiyyin lahumuibAAath lana malikan nuqatil fee sabeeli Allahiqala hal AAasaytum in kutiba AAalaykumu alqitalualla tuqatiloo qaloo wama lanaalla nuqatila fee sabeeli Allahi waqadokhrijna min diyarina waabna-inafalamma kutiba AAalayhimu alqitalu tawallaw illaqaleelan minhum wallahu AAaleemun biththalimeen');
INSERT INTO "transliteration" VALUES(2,247,'2:247','Waqala lahum nabiyyuhum inna Allahaqad baAAatha lakum taloota malikan qaloo annayakoonu lahu almulku AAalayna wanahnu ahaqqubilmulki minhu walam yu/ta saAAatan mina almali qalainna Allaha istafahu AAalaykum wazadahubastatan fee alAAilmi waljismi wallahuyu/tee mulkahu man yashao wallahu wasiAAunAAaleem');
INSERT INTO "transliteration" VALUES(2,248,'2:248','Waqala lahum nabiyyuhum inna ayatamulkihi an ya/tiyakumu attabootu feehi sakeenatunmin rabbikum wabaqiyyatun mimma taraka alu moosawaalu haroona tahmiluhu almala-ikatuinna fee thalika laayatan lakum in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(2,249,'2:249','Falamma fasala talootubiljunoodi qala inna Allaha mubtaleekumbinaharin faman shariba minhu falaysa minnee waman lam yatAAamhufa-innahu minnee illa mani ightarafa ghurfatan biyadihifashariboo minhu illa qaleelan minhum falamma jawazahuhuwa wallatheena amanoo maAAahu qaloola taqata lana alyawma bijalootawajunoodihi qala allatheena yathunnoonaannahum mulaqoo Allahi kam min fi-atin qaleelatinghalabat fi-atan katheeratan bi-ithni Allahi wallahumaAAa assabireen');
INSERT INTO "transliteration" VALUES(2,250,'2:250','Walamma barazoo lijalootawajunoodihi qaloo rabbana afrigh AAalayna sabranwathabbit aqdamana wansurnaAAala alqawmi alkafireen');
INSERT INTO "transliteration" VALUES(2,251,'2:251','Fahazamoohum bi-ithni Allahiwaqatala dawoodu jaloota waatahu Allahualmulka walhikmata waAAallamahu mimma yashaowalawla dafAAu Allahi annasa baAAdahumbibaAAdin lafasadati al-ardu walakinna Allahathoo fadlin AAala alAAalameen');
INSERT INTO "transliteration" VALUES(2,252,'2:252','Tilka ayatu Allahinatlooha AAalayka bilhaqqi wa-innaka laminaalmursaleen');
INSERT INTO "transliteration" VALUES(2,253,'2:253','Tilka arrusulu faddalnabaAAdahum AAala baAAdin minhum man kallamaAllahu warafaAAa baAAdahum darajatin waataynaAAeesa ibna maryama albayyinati waayyadnahubiroohi alqudusi walaw shaa Allahu maiqtatala allatheena min baAAdihim min baAAdi ma jaat-humualbayyinatu walakini ikhtalafoo faminhum man amanawaminhum man kafara walaw shaa Allahu maiqtataloo walakinna Allaha yafAAalu mayureed');
INSERT INTO "transliteration" VALUES(2,254,'2:254','Ya ayyuha allatheena amanooanfiqoo mimma razaqnakum min qabli an ya/tiyayawmun la bayAAun feehi wala khullatun walashafaAAatun walkafiroona humu aththalimoon');
INSERT INTO "transliteration" VALUES(2,255,'2:255','Allahu la ilaha illahuwa alhayyu alqayyoomu la ta/khuthuhusinatun wala nawmun lahu ma fee assamawatiwama fee al-ardi man tha allatheeyashfaAAu AAindahu illa bi-ithnihi yaAAlamu mabayna aydeehim wama khalfahum wala yuheetoonabishay-in min AAilmihi illa bima shaawasiAAa kursiyyuhu assamawati wal-ardawala yaooduhu hifthuhumawahuwa alAAaliyyu alAAatheem');
INSERT INTO "transliteration" VALUES(2,256,'2:256','La ikraha fee addeeniqad tabayyana arrushdu mina alghayyi faman yakfur bittaghootiwayu/min billahi faqadi istamsaka bilAAurwatialwuthqa la infisama laha wallahusameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,257,'2:257','Allahu waliyyu allatheena amanooyukhrijuhum mina aththulumati ilaannoori wallatheena kafaroo awliyaohumuattaghootu yukhrijoonahum mina annoori ilaaththulumati ola-ika as-habuannari hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,258,'2:258','Alam tara ila allathee hajjaibraheema fee rabbihi an atahu Allahualmulka ith qala ibraheemu rabbiya allatheeyuhyee wayumeetu qala ana ohyeewaomeetu qala ibraheemu fa-inna Allahaya/tee bishshamsi mina almashriqi fa/ti biha minaalmaghribi fabuhita allathee kafara wallahula yahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(2,259,'2:259','Aw kallathee marra AAalaqaryatin wahiya khawiyatun AAala AAurooshihaqala anna yuhyee hathihi AllahubaAAda mawtiha faamatahu Allahu mi-ata AAaminthumma baAAathahu qala kam labithta qala labithtuyawman aw baAAda yawmin qala bal labithta mi-ata AAaminfanthur ila taAAamikawasharabika lam yatasannah wanthurila himarika walinajAAalaka ayatan linnasiwanthur ila alAAithamikayfa nunshizuha thumma naksooha lahmanfalamma tabayyana lahu qala aAAlamu anna AllahaAAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,260,'2:260','Wa-ith qala ibraheemurabbi arinee kayfa tuhyee almawta qala awalam tu/min qala bala walakin liyatma-innaqalbee qala fakhuth arbaAAatan mina attayrifasurhunna ilayka thumma ijAAal AAala kulli jabalinminhunna juz-an thumma odAAuhunna ya/teenaka saAAyan waAAlamanna Allaha AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(2,261,'2:261','Mathalu allatheena yunfiqoona amwalahumfee sabeeli Allahi kamathali habbatin anbatatsabAAa sanabila fee kulli sunbulatin mi-atu habbatinwallahu yudaAAifu liman yashao wallahuwasiAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,262,'2:262','Allatheena yunfiqoona amwalahumfee sabeeli Allahi thumma la yutbiAAoona maanfaqoo mannan wala athan lahum ajruhum AAindarabbihim wala khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(2,263,'2:263','Qawlun maAAroofun wamaghfiratun khayrun minsadaqatin yatbaAAuha athan wallahughaniyyun haleem');
INSERT INTO "transliteration" VALUES(2,264,'2:264','Ya ayyuha allatheena amanoola tubtiloo sadaqatikum bilmanniwal-atha kallathee yunfiqu malahuri-aa annasi wala yu/minu billahiwalyawmi al-akhiri famathaluhu kamathali safwaninAAalayhi turabun faasabahu wabilunfatarakahu saldan la yaqdiroona AAalashay-in mimma kasaboo wallahu layahdee alqawma alkafireen');
INSERT INTO "transliteration" VALUES(2,265,'2:265','Wamathalu allatheena yunfiqoona amwalahumuibtighaa mardati Allahi watathbeetan minanfusihim kamathali jannatin birabwatin asabaha wabilunfaatat okulaha diAAfayni fa-in lam yusibhawabilun fatallun wallahu bimataAAmaloona baseer');
INSERT INTO "transliteration" VALUES(2,266,'2:266','Ayawaddu ahadukum an takoona lahujannatun min nakheelin waaAAnabin tajree min tahtihaal-anharu lahu feeha min kulli aththamaratiwaasabahu alkibaru walahu thurriyyatun duAAafaofaasabaha iAAsarun feehi narun fahtaraqatkathalika yubayyinu Allahu lakumu al-ayatilaAAallakum tatafakkaroon');
INSERT INTO "transliteration" VALUES(2,267,'2:267','Ya ayyuha allatheena amanooanfiqoo min tayyibati ma kasabtum wamimmaakhrajna lakum mina al-ardi wala tayammamooalkhabeetha minhu tunfiqoona walastum bi-akhitheehiilla an tughmidoo feehi waAAlamoo anna Allahaghaniyyun hameed');
INSERT INTO "transliteration" VALUES(2,268,'2:268','Ashshaytanu yaAAidukumualfaqra waya/murukum bilfahsha-i wallahuyaAAidukum maghfiratan minhu wafadlan wallahuwasiAAun AAaleem');
INSERT INTO "transliteration" VALUES(2,269,'2:269','Yu/tee alhikmata man yashaowaman yu/ta alhikmata faqad ootiya khayran katheeran wamayaththakkaru illa oloo al-albab');
INSERT INTO "transliteration" VALUES(2,270,'2:270','Wama anfaqtum min nafaqatin aw nathartummin nathrin fa-inna Allaha yaAAlamuhu wamaliththalimeena min ansar');
INSERT INTO "transliteration" VALUES(2,271,'2:271','In tubdoo assadaqatifaniAAimma hiya wa-in tukhfooha watu/toohaalfuqaraa fahuwa khayrun lakum wayukaffiru AAankum minsayyi-atikum wallahu bimataAAmaloona khabeer');
INSERT INTO "transliteration" VALUES(2,272,'2:272','Laysa AAalayka hudahum walakinnaAllaha yahdee man yashao wama tunfiqoo minkhayrin fali-anfusikum wama tunfiqoona illa ibtighaawajhi Allahi wama tunfiqoo min khayrin yuwaffailaykum waantum la tuthlamoon');
INSERT INTO "transliteration" VALUES(2,273,'2:273','Lilfuqara-i allatheena ohsiroofee sabeeli Allahi la yastateeAAoona darbanfee al-ardi yahsabuhumu aljahilu aghniyaamina attaAAaffufi taAArifuhum biseemahum layas-aloona annasa ilhafan wamatunfiqoo min khayrin fa-inna Allaha bihi AAaleem');
INSERT INTO "transliteration" VALUES(2,274,'2:274','Allatheena yunfiqoona amwalahumbillayli wannahari sirran waAAalaniyatanfalahum ajruhum AAinda rabbihim wala khawfun AAalayhim walahum yahzanoon');
INSERT INTO "transliteration" VALUES(2,275,'2:275','Allatheena ya/kuloona arribala yaqoomoona illa kama yaqoomu allatheeyatakhabbatuhu ashshaytanu mina almassi thalikabi-annahum qaloo innama albayAAu mithlu arribawaahalla Allahu albayAAa waharrama arribafaman jaahu mawAAithatun min rabbihi fantahafalahu ma salafa waamruhu ila Allahi wamanAAada faola-ika as-habu annarihum feeha khalidoon');
INSERT INTO "transliteration" VALUES(2,276,'2:276','Yamhaqu Allahu arribawayurbee assadaqati wallahula yuhibbu kulla kaffarin atheem');
INSERT INTO "transliteration" VALUES(2,277,'2:277','Inna allatheena amanoowaAAamiloo assalihati waaqamoo assalatawaatawoo azzakata lahum ajruhum AAindarabbihim wala khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(2,278,'2:278','Ya ayyuha allatheena amanooittaqoo Allaha watharoo ma baqiya mina arribain kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(2,279,'2:279','Fa-in lam tafAAaloo fa/thanoo biharbinmina Allahi warasoolihi wa-in tubtum falakum ruoosu amwalikumla tathlimoona wala tuthlamoon');
INSERT INTO "transliteration" VALUES(2,280,'2:280','Wa-in kana thoo AAusratinfanathiratun ila maysaratin waan tasaddaqookhayrun lakum in kuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(2,281,'2:281','Wattaqoo yawman turjaAAoona feehiila Allahi thumma tuwaffa kullu nafsin makasabat wahum la yuthlamoon');
INSERT INTO "transliteration" VALUES(2,282,'2:282','Ya ayyuha allatheena amanooitha tadayantum bidaynin ila ajalin musammanfaktuboohu walyaktub baynakum katibun bilAAadliwala ya/ba katibun an yaktuba kamaAAallamahu Allahu falyaktub walyumlili allatheeAAalayhi alhaqqu walyattaqi Allaha rabbahu walayabkhas minhu shay-an fa-in kana allathee AAalayhialhaqqu safeehan aw daAAeefan aw la yastateeAAuan yumilla huwa falyumlil waliyyuhu bilAAadli wastashhidooshaheedayni min rijalikum fa-in lam yakoonarajulayni farajulun wamraatani mimman tardawnamina ashshuhada-i an tadilla ihdahumafatuthakkira ihdahuma al-okhrawala ya/ba ashshuhadao itha maduAAoo wala tas-amoo an taktuboohu sagheeran awkabeeran ila ajalihi thalikum aqsatu AAindaAllahi waaqwamu lishshahadati waadnaalla tartaboo illa an takoona tijaratanhadiratan tudeeroonaha baynakum falaysa AAalaykumjunahun alla taktubooha waashhidoo ithatabayaAAtum wala yudarra katibun walashaheedun wa-in tafAAaloo fa-innahu fusooqun bikum wattaqooAllaha wayuAAallimukumu Allahu wallahubikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(2,283,'2:283','Wa-in kuntum AAala safarin walamtajidoo katiban farihanun maqboodatun fa-inamina baAAdukum baAAdan falyu-addiallatheei/tumina amanatahu walyattaqi Allaha rabbahuwala taktumoo ashshahadata waman yaktumhafa-innahu athimun qalbuhu wallahu bimataAAmaloona AAaleem');
INSERT INTO "transliteration" VALUES(2,284,'2:284','Lillahi ma fee assamawatiwama fee al-ardi wa-in tubdoo ma feeanfusikum aw tukhfoohu yuhasibkum bihi Allahufayaghfiru liman yashao wayuAAaththibu man yashaowallahu AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(2,285,'2:285','Amana arrasoolu bimaonzila ilayhi min rabbihi walmu/minoona kullun amanabillahi wamala-ikatihi wakutubihiwarusulihi la nufarriqu bayna ahadin min rusulihiwaqaloo samiAAna waataAAna ghufranakarabbana wa-ilayka almaseer');
INSERT INTO "transliteration" VALUES(2,286,'2:286','La yukallifu Allahu nafsanilla wusAAaha laha ma kasabatwaAAalayha ma iktasabat rabbana latu-akhithna in naseena aw akhta/narabbana wala tahmil AAalayna isrankama hamaltahu AAala allatheena minqablina rabbana wala tuhammilnama la taqata lana bihi waAAfuAAanna waghfir lana warhamnaanta mawlana fansurna AAalaalqawmi alkafireen');
INSERT INTO "transliteration" VALUES(3,1,'3:1','Alif-lam-meem');
INSERT INTO "transliteration" VALUES(3,2,'3:2','Allahu la ilaha illahuwa alhayyu alqayyoom');
INSERT INTO "transliteration" VALUES(3,3,'3:3','Nazzala AAalayka alkitaba bilhaqqimusaddiqan lima bayna yadayhi waanzala attawratawal-injeel');
INSERT INTO "transliteration" VALUES(3,4,'3:4','Min qablu hudan linnasiwaanzala alfurqana inna allatheena kafaroo bi-ayatiAllahi lahum AAathabun shadeedun wallahuAAazeezun thoo intiqam');
INSERT INTO "transliteration" VALUES(3,5,'3:5','Inna Allaha la yakhfaAAalayhi shay-on fee al-ardi wala fee assama/-');
INSERT INTO "transliteration" VALUES(3,6,'3:6','Huwa allathee yusawwirukum feeal-arhami kayfa yashao la ilaha illahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(3,7,'3:7','Huwa allathee anzala AAalayka alkitabaminhu ayatun muhkamatun hunna ommualkitabi waokharu mutashabihatun faammaallatheena fee quloobihim zayghun fayattabiAAoona matashabaha minhu ibtighaa alfitnati wabtighaata/weelihi wama yaAAlamu ta/weelahu illa Allahuwarrasikhoona fee alAAilmi yaqooloona amannabihi kullun min AAindi rabbina wama yaththakkaruilla oloo al-albab');
INSERT INTO "transliteration" VALUES(3,8,'3:8','Rabbana la tuzigh quloobanabaAAda ith hadaytana wahab lana min ladunkarahmatan innaka anta alwahhab');
INSERT INTO "transliteration" VALUES(3,9,'3:9','Rabbana innaka jamiAAu annasiliyawmin la rayba feehi inna Allaha layukhlifu almeeAAad');
INSERT INTO "transliteration" VALUES(3,10,'3:10','Inna allatheena kafaroo lan tughniyaAAanhum amwaluhum wala awladuhum mina Allahishay-an waola-ika hum waqoodu annar');
INSERT INTO "transliteration" VALUES(3,11,'3:11','Kada/bi ali firAAawna wallatheenamin qablihim kaththaboo bi-ayatinafaakhathahumu Allahu bithunoobihim wallahushadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(3,12,'3:12','Qul lillatheena kafaroo satughlaboonawatuhsharoona ila jahannama wabi/sa almihad');
INSERT INTO "transliteration" VALUES(3,13,'3:13','Qad kana lakum ayatun feefi-atayni iltaqata fi-atun tuqatilu fee sabeeli Allahiwaokhra kafiratun yarawnahum mithlayhim ra/yaalAAayni wallahu yu-ayyidu binasrihi manyashao inna fee thalika laAAibratan li-olee al-absar');
INSERT INTO "transliteration" VALUES(3,14,'3:14','Zuyyina linnasi hubbuashshahawati mina annisa-i walbaneenawalqanateeri almuqantarati mina aththahabiwalfiddati walkhayli almusawwamati wal-anAAamiwalharthi thalika mataAAu alhayatiaddunya wallahu AAindahu husnualmaab');
INSERT INTO "transliteration" VALUES(3,15,'3:15','Qul aonabbi-okum bikhayrin min thalikumlillatheena ittaqaw AAinda rabbihim jannatun tajreemin tahtiha al-anharu khalideena feehawaazwajun mutahharatun waridwanunmina Allahi wallahu baseerun bilAAibad');
INSERT INTO "transliteration" VALUES(3,16,'3:16','Allatheena yaqooloona rabbanainnana amanna faghfir lana thunoobanawaqina AAathaba annar');
INSERT INTO "transliteration" VALUES(3,17,'3:17','Assabireena wassadiqeenawalqaniteena walmunfiqeena walmustaghfireenabil-ashar');
INSERT INTO "transliteration" VALUES(3,18,'3:18','Shahida Allahu annahu la ilahailla huwa walmala-ikatu waoloo alAAilmi qa-imanbilqisti la ilaha illa huwaalAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(3,19,'3:19','Inna addeena AAinda Allahial-islamu wama ikhtalafa allatheena ootooalkitaba illa min baAAdi ma jaahumualAAilmu baghyan baynahum waman yakfur bi-ayati Allahifa-inna Allaha sareeAAu alhisab');
INSERT INTO "transliteration" VALUES(3,20,'3:20','Fa-in hajjooka faqul aslamtu wajhiyalillahi wamani ittabaAAani waqul lillatheena ootooalkitaba walommiyyeena aaslamtum fa-inaslamoo faqadi ihtadaw wa-in tawallaw fa-innama AAalaykaalbalaghu wallahu baseerun bilAAibad');
INSERT INTO "transliteration" VALUES(3,21,'3:21','Inna allatheena yakfuroona bi-ayatiAllahi wayaqtuloona annabiyyeena bighayri haqqinwayaqtuloona allatheena ya/muroona bilqistimina annasi fabashshirhum biAAathabin aleem');
INSERT INTO "transliteration" VALUES(3,22,'3:22','Ola-ika allatheena habitataAAmaluhum fee addunya wal-akhiratiwama lahum min nasireen');
INSERT INTO "transliteration" VALUES(3,23,'3:23','Alam tara ila allatheena ootoonaseeban mina alkitabi yudAAawna ila kitabiAllahi liyahkuma baynahum thumma yatawallafareequn minhum wahum muAAridoon');
INSERT INTO "transliteration" VALUES(3,24,'3:24','Thalika bi-annahum qaloo lantamassana annaru illa ayyamanmaAAdoodatin wagharrahum fee deenihim ma kanooyaftaroon');
INSERT INTO "transliteration" VALUES(3,25,'3:25','Fakayfa itha jamaAAnahumliyawmin la rayba feehi wawuffiyat kullu nafsin makasabat wahum la yuthlamoon');
INSERT INTO "transliteration" VALUES(3,26,'3:26','Quli allahumma malika almulkitu/tee almulka man tashao watanziAAu almulka mimman tashaowatuAAizzu man tashao watuthillu man tashaobiyadika alkhayru innaka AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(3,27,'3:27','Tooliju allayla fee annahariwatooliju annahara fee allayli watukhriju alhayyamina almayyiti watukhriju almayyita mina alhayyi watarzuquman tashao bighayri hisab');
INSERT INTO "transliteration" VALUES(3,28,'3:28','La yattakhithi almu/minoonaalkafireena awliyaa min dooni almu/mineena wamanyafAAal thalika falaysa mina Allahi fee shay-in illaan tattaqoo minhum tuqatan wayuhaththirukumuAllahu nafsahu wa-ila Allahi almaseer');
INSERT INTO "transliteration" VALUES(3,29,'3:29','Qul in tukhfoo ma fee sudoorikumaw tubdoohu yaAAlamhu Allahu wayaAAlamu ma fee assamawatiwama fee al-ardi wallahu AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(3,30,'3:30','Yawma tajidu kullu nafsin ma AAamilatmin khayrin muhdaran wama AAamilat min soo-intawaddu law anna baynaha wabaynahu amadan baAAeedan wayuhaththirukumuAllahu nafsahu wallahu raoofun bilAAibad');
INSERT INTO "transliteration" VALUES(3,31,'3:31','Qul in kuntum tuhibboona AllahafattabiAAoonee yuhbibkumu Allahu wayaghfirlakum thunoobakum wallahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(3,32,'3:32','Qul ateeAAoo Allaha warrasoolafa-in tawallaw fa-inna Allaha la yuhibbu alkafireen');
INSERT INTO "transliteration" VALUES(3,33,'3:33','Inna Allaha istafa adamawanoohan waala ibraheema waala AAimranaAAala alAAalameen');
INSERT INTO "transliteration" VALUES(3,34,'3:34','Thurriyyatan baAAduhamin baAAdin wallahu sameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(3,35,'3:35','Ith qalati imraatu AAimranarabbi innee nathartu laka ma fee batnee muharraranfataqabbal minnee innaka anta assameeAAu alAAaleem');
INSERT INTO "transliteration" VALUES(3,36,'3:36','Falamma wadaAAat-ha qalatrabbi innee wadaAAtuha ontha wallahuaAAlamu bima wadaAAat walaysa aththakarukalontha wa-innee sammaytuha maryamawa-innee oAAeethuha bika wathurriyyatahamina ashshaytani arrajeem');
INSERT INTO "transliteration" VALUES(3,37,'3:37','Fataqabbalaha rabbuhabiqaboolin hasanin waanbataha nabatan hasananwakaffalaha zakariyya kullama dakhalaAAalayha zakariyya almihraba wajadaAAindaha rizqan qala ya maryamu annalaki hatha qalat huwa min AAindi Allahi innaAllaha yarzuqu man yashao bighayri hisab');
INSERT INTO "transliteration" VALUES(3,38,'3:38','Hunalika daAAa zakariyyarabbahu qala rabbi hab lee min ladunka thurriyyatantayyibatan innaka sameeAAu adduAAa/-');
INSERT INTO "transliteration" VALUES(3,39,'3:39','Fanadat-hu almala-ikatu wahuwaqa-imun yusallee fee almihrabi annaAllaha yubashshiruka biyahya musaddiqanbikalimatin mina Allahi wasayyidan wahasooranwanabiyyan mina assaliheen');
INSERT INTO "transliteration" VALUES(3,40,'3:40','Qala rabbi anna yakoonu leeghulamun waqad balaghaniya alkibaru wamraatee AAaqirunqala kathalika Allahu yafAAalu mayasha/');
INSERT INTO "transliteration" VALUES(3,41,'3:41','Qala rabbi ijAAal lee ayatan qalaayatuka alla tukallima annasa thalathataayyamin illa ramzan wathkur rabbakakatheeran wasabbih bilAAashiyyi wal-ibkar');
INSERT INTO "transliteration" VALUES(3,42,'3:42','Wa-ith qalati almala-ikatuya maryamu inna Allaha istafaki watahharakiwastafaki AAala nisa-i alAAalameen');
INSERT INTO "transliteration" VALUES(3,43,'3:43','Ya maryamu oqnutee lirabbiki wasjudeewarkaAAee maAAa arrakiAAeen');
INSERT INTO "transliteration" VALUES(3,44,'3:44','Thalika min anba-i alghaybinooheehi ilayka wama kunta ladayhim ithyulqoona aqlamahum ayyuhum yakfulu maryama wamakunta ladayhim ith yakhtasimoon');
INSERT INTO "transliteration" VALUES(3,45,'3:45','Ith qalati almala-ikatuya maryamu inna Allaha yubashshiruki bikalimatinminhu ismuhu almaseehu AAeesa ibnu maryama wajeehanfee addunya wal-akhirati waminaalmuqarrabeen');
INSERT INTO "transliteration" VALUES(3,46,'3:46','Wayukallimu annasa feealmahdi wakahlan wamina assaliheen');
INSERT INTO "transliteration" VALUES(3,47,'3:47','Qalat rabbi anna yakoonu leewaladun walam yamsasnee basharun qala kathaliki Allahuyakhluqu ma yashao itha qada amranfa-innama yaqoolu lahu kun fayakoon');
INSERT INTO "transliteration" VALUES(3,48,'3:48','WayuAAallimuhu alkitaba walhikmatawattawrata wal-injeel');
INSERT INTO "transliteration" VALUES(3,49,'3:49','Warasoolan ila banee isra-eelaannee qad ji/tukum bi-ayatin min rabbikum annee akhluqulakum mina atteeni kahay-ati attayrifaanfukhu feehi fayakoonu tayran bi-ithni Allahiwaobri-o al-akmaha wal-abrasa waohyeealmawta bi-ithni Allahi waonabbi-okum bimata/kuloona wama taddakhiroona fee buyootikum inna fee thalikalaayatan lakum in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(3,50,'3:50','Wamusaddiqan lima baynayadayya mina attawrati wali-ohilla lakumbaAAda allathee hurrima AAalaykum waji/tukumbi-ayatin min rabbikum fattaqoo Allaha waateeAAoon');
INSERT INTO "transliteration" VALUES(3,51,'3:51','Inna Allaha rabbee warabbukum faAAbudoohuhatha siratun mustaqeem');
INSERT INTO "transliteration" VALUES(3,52,'3:52','Falamma ahassa AAeesaminhumu alkufra qala man ansaree ila Allahiqala alhawariyyoona nahnu ansaruAllahi amanna billahi washhadbi-anna muslimoon');
INSERT INTO "transliteration" VALUES(3,53,'3:53','Rabbana amanna bimaanzalta wattabaAAna arrasoola faktubnamaAAa ashshahideen');
INSERT INTO "transliteration" VALUES(3,54,'3:54','Wamakaroo wamakara Allahu wallahukhayru almakireen');
INSERT INTO "transliteration" VALUES(3,55,'3:55','Ith qala Allahu yaAAeesa innee mutawaffeeka warafiAAuka ilayya wamutahhirukamina allatheena kafaroo wajaAAilu allatheenaittabaAAooka fawqa allatheena kafaroo ila yawmialqiyamati thumma ilayya marjiAAukum faahkumubaynakum feema kuntum feehi takhtalifoon');
INSERT INTO "transliteration" VALUES(3,56,'3:56','Faamma allatheena kafaroofaoAAaththibuhum AAathaban shadeedan fee addunyawal-akhirati wama lahum min nasireen');
INSERT INTO "transliteration" VALUES(3,57,'3:57','Waamma allatheena amanoowaAAamiloo assalihati fayuwaffeehimojoorahum wallahu la yuhibbu aththalimeen');
INSERT INTO "transliteration" VALUES(3,58,'3:58','Thalika natloohu AAalayka mina al-ayatiwaththikri alhakeem');
INSERT INTO "transliteration" VALUES(3,59,'3:59','Inna mathala AAeesa AAinda Allahikamathali adama khalaqahu min turabin thumma qalalahu kun fayakoon');
INSERT INTO "transliteration" VALUES(3,60,'3:60','Alhaqqu min rabbika fala takunmina almumtareen');
INSERT INTO "transliteration" VALUES(3,61,'3:61','Faman hajjaka feehi min baAAdi majaaka mina alAAilmi faqul taAAalaw nadAAu abnaanawaabnaakum wanisaana wanisaakumwaanfusana waanfusakum thumma nabtahil fanajAAal laAAnataAllahi AAala alkathibeen');
INSERT INTO "transliteration" VALUES(3,62,'3:62','Inna hatha lahuwa alqasasualhaqqu wama min ilahin illa Allahuwa-inna Allaha lahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(3,63,'3:63','Fa-in tawallaw fa-inna AllahaAAaleemun bilmufsideen');
INSERT INTO "transliteration" VALUES(3,64,'3:64','Qul ya ahla alkitabi taAAalawila kalimatin sawa-in baynana wabaynakum allanaAAbuda illa Allaha wala nushrika bihishay-an wala yattakhitha baAAdunabaAAdan arbaban min dooni Allahi fa-intawallaw faqooloo ishhadoo bi-anna muslimoon');
INSERT INTO "transliteration" VALUES(3,65,'3:65','Ya ahla alkitabi lima tuhajjoonafee ibraheema wama onzilati attawratuwal-injeelu illa min baAAdihi afalataAAqiloon');
INSERT INTO "transliteration" VALUES(3,66,'3:66','Ha antum haola-i hajajtumfeema lakum bihi AAilmun falima tuhajjoona feemalaysa lakum bihi AAilmun wallahu yaAAlamu waantumla taAAlamoon');
INSERT INTO "transliteration" VALUES(3,67,'3:67','Ma kana ibraheemuyahoodiyyan wala nasraniyyan walakinkana haneefan musliman wama kana minaalmushrikeen');
INSERT INTO "transliteration" VALUES(3,68,'3:68','Inna awla annasibi-ibraheema lallatheena ittabaAAoohu wahathaannabiyyu wallatheena amanoo wallahuwaliyyu almu/mineen');
INSERT INTO "transliteration" VALUES(3,69,'3:69','Waddat ta-ifatun min ahli alkitabilaw yudilloonakum wama yudilloona illaanfusahum wama yashAAuroon');
INSERT INTO "transliteration" VALUES(3,70,'3:70','Ya ahla alkitabi limatakfuroona bi-ayati Allahi waantumtashhadoon');
INSERT INTO "transliteration" VALUES(3,71,'3:71','Ya ahla alkitabi limatalbisoona alhaqqa bilbatili wataktumoonaalhaqqa waantum taAAlamoon');
INSERT INTO "transliteration" VALUES(3,72,'3:72','Waqalat ta-ifatun min ahlialkitabi aminoo billathee onzilaAAala allatheena amanoo wajha annahariwakfuroo akhirahu laAAallahum yarjiAAoon');
INSERT INTO "transliteration" VALUES(3,73,'3:73','Wala tu/minoo illa limantabiAAa deenakum qul inna alhuda huda Allahian yu/ta ahadun mithla ma ooteetum aw yuhajjookumAAinda rabbikum qul inna alfadla biyadi Allahiyu/teehi man yashao wallahu wasiAAunAAaleem');
INSERT INTO "transliteration" VALUES(3,74,'3:74','Yakhtassu birahmatihi man yashaowallahu thoo alfadli alAAatheem');
INSERT INTO "transliteration" VALUES(3,75,'3:75','Wamin ahli alkitabi man in ta/manhubiqintarin yu-addihi ilayka waminhum man in ta/manhubideenarin la yu-addihi ilayka illa madumta AAalayhi qa-iman thalika bi-annahum qaloolaysa AAalayna fee al-ommiyyeena sabeelun wayaqooloonaAAala Allahi alkathiba wahum yaAAlamoon');
INSERT INTO "transliteration" VALUES(3,76,'3:76','Bala man awfa biAAahdihi wattaqafa-inna Allaha yuhibbu almuttaqeen');
INSERT INTO "transliteration" VALUES(3,77,'3:77','Inna allatheena yashtaroona biAAahdiAllahi waaymanihim thamanan qaleelan ola-ikala khalaqa lahum fee al-akhirati walayukallimuhumu Allahu wala yanthuruilayhim yawma alqiyamati wala yuzakkeehim walahumAAathabun aleem');
INSERT INTO "transliteration" VALUES(3,78,'3:78','Wa-inna minhum lafareeqan yalwoonaalsinatahum bilkitabi litahsaboohu minaalkitabi wama huwa mina alkitabiwayaqooloona huwa min AAindi Allahi wama huwa minAAindi Allahi wayaqooloona AAala Allahi alkathibawahum yaAAlamoon');
INSERT INTO "transliteration" VALUES(3,79,'3:79','Ma kana libasharin anyu/tiyahu Allahu alkitaba walhukmawannubuwwata thumma yaqoola linnasikoonoo AAibadan lee min dooni Allahi walakinkoonoo rabbaniyyeena bima kuntum tuAAallimoonaalkitaba wabima kuntum tadrusoon');
INSERT INTO "transliteration" VALUES(3,80,'3:80','Wala ya/murakum an tattakhithooalmala-ikata wannabiyyeena arbabanaya/murukum bilkufri baAAda ith antum muslimoon');
INSERT INTO "transliteration" VALUES(3,81,'3:81','Wa-ith akhatha Allahumeethaqa annabiyyeena lama ataytukummin kitabin wahikmatin thumma jaakumrasoolun musaddiqun lima maAAakum latu/minunna bihiwalatansurunnahu qala aaqrartum waakhathtumAAala thalikum isree qaloo aqrarnaqala fashhadoo waana maAAakum mina ashshahideen');
INSERT INTO "transliteration" VALUES(3,82,'3:82','Faman tawalla baAAda thalikafaola-ika humu alfasiqoon');
INSERT INTO "transliteration" VALUES(3,83,'3:83','Afaghayra deeni Allahi yabghoonawalahu aslama man fee assamawati wal-arditawAAan wakarhan wa-ilayhi yurjaAAoon');
INSERT INTO "transliteration" VALUES(3,84,'3:84','Qul amanna billahiwama onzila AAalayna wama onzila AAalaibraheema wa-ismaAAeela wa-ishaqawayaAAqooba wal-asbati wama ootiya moosawaAAeesa wannabiyyoona min rabbihim lanufarriqu bayna ahadin minhum wanahnu lahumuslimoon');
INSERT INTO "transliteration" VALUES(3,85,'3:85','Waman yabtaghi ghayra al-islamideenan falan yuqbala minhu wahuwa fee al-akhirati minaalkhasireen');
INSERT INTO "transliteration" VALUES(3,86,'3:86','Kayfa yahdee Allahu qawman kafaroobaAAda eemanihim washahidoo anna arrasoola haqqunwajaahumu albayyinatu wallahu layahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(3,87,'3:87','Ola-ika jazaohum annaAAalayhim laAAnata Allahi walmala-ikati wannasiajmaAAeen');
INSERT INTO "transliteration" VALUES(3,88,'3:88','Khalideena feeha layukhaffafu AAanhumu alAAathabu wala hum yuntharoon');
INSERT INTO "transliteration" VALUES(3,89,'3:89','Illa allatheena taboomin baAAdi thalika waaslahoo fa-inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(3,90,'3:90','Inna allatheena kafaroo baAAda eemanihimthumma izdadoo kufran lan tuqbala tawbatuhum waola-ikahumu addalloon');
INSERT INTO "transliteration" VALUES(3,91,'3:91','Inna allatheena kafaroo wamatoowahum kuffarun falan yuqbala min ahadihim miloal-ardi thahaban walawi iftada bihi ola-ikalahum AAathabun aleemun wama lahum min nasireen');
INSERT INTO "transliteration" VALUES(3,92,'3:92','Lan tanaloo albirra hattatunfiqoo mimma tuhibboona wama tunfiqoo minshay-in fa-inna Allaha bihi AAaleem');
INSERT INTO "transliteration" VALUES(3,93,'3:93','Kullu attaAAami kanahillan libanee isra-eela illa ma harramaisra-eelu AAala nafsihi min qabli an tunazzala attawratuqul fa/too bittawrati fatlooha inkuntum sadiqeen');
INSERT INTO "transliteration" VALUES(3,94,'3:94','Famani iftara AAala Allahialkathiba min baAAdi thalika faola-ika humuathalimoon');
INSERT INTO "transliteration" VALUES(3,95,'3:95','Qul sadaqa Allahu fattabiAAoomillata ibraheema haneefan wama kanamina almushrikeen');
INSERT INTO "transliteration" VALUES(3,96,'3:96','Inna awwala baytin wudiAAa linnasilallathee bibakkata mubarakan wahudan lilAAalameen');
INSERT INTO "transliteration" VALUES(3,97,'3:97','Feehi ayatun bayyinatunmaqamu ibraheema waman dakhalahu kana aminanwalillahi AAala annasi hijjualbayti mani istataAAa ilayhi sabeelan waman kafarafa-inna Allaha ghaniyyun AAani alAAalameen');
INSERT INTO "transliteration" VALUES(3,98,'3:98','Qul ya ahla alkitabi limatakfuroona bi-ayati Allahi wallahushaheedun AAala ma taAAmaloon');
INSERT INTO "transliteration" VALUES(3,99,'3:99','Qul ya ahla alkitabi lima tasuddoonaAAan sabeeli Allahi man amana tabghoonahaAAiwajan waantum shuhadao wama Allahu bighafilinAAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(3,100,'3:100','Ya ayyuha allatheena amanooin tuteeAAoo fareeqan mina allatheena ootoo alkitabayaruddookum baAAda eemanikum kafireen');
INSERT INTO "transliteration" VALUES(3,101,'3:101','Wakayfa takfuroona waantum tutlaAAalaykum ayatu Allahi wafeekum rasooluhuwaman yaAAtasim billahi faqad hudiya ilasiratin mustaqeem');
INSERT INTO "transliteration" VALUES(3,102,'3:102','Ya ayyuha allatheena amanooittaqoo Allaha haqqa tuqatihi walatamootunna illa waantum muslimoon');
INSERT INTO "transliteration" VALUES(3,103,'3:103','WaAAtasimoo bihabliAllahi jameeAAan wala tafarraqoo wathkurooniAAmata Allahi AAalaykum ith kuntum aAAdaanfaallafa bayna quloobikum faasbahtum biniAAmatihiikhwanan wakuntum AAala shafa hufratinmina annari faanqathakum minha kathalikayubayyinu Allahu lakum ayatihi laAAallakumtahtadoon');
INSERT INTO "transliteration" VALUES(3,104,'3:104','Waltakun minkum ommatun yadAAoona ilaalkhayri waya/muroona bilmaAAroofi wayanhawna AAanialmunkari waola-ika humu almuflihoon');
INSERT INTO "transliteration" VALUES(3,105,'3:105','Wala takoonoo kallatheenatafarraqoo wakhtalafoo min baAAdi ma jaahumualbayyinatu waola-ika lahum AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(3,106,'3:106','Yawma tabyaddu wujoohun wataswadduwujoohun faamma allatheena iswaddat wujoohuhumakafartum baAAda eemanikum fathooqoo alAAathababima kuntum takfuroon');
INSERT INTO "transliteration" VALUES(3,107,'3:107','Waamma allatheena ibyaddatwujoohuhum fafee rahmati Allahi hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(3,108,'3:108','Tilka ayatu Allahinatlooha AAalayka bilhaqqi wama Allahuyureedu thulman lilAAalameen');
INSERT INTO "transliteration" VALUES(3,109,'3:109','Walillahi ma fee assamawatiwama fee al-ardi wa-ila AllahiturjaAAu al-omoor');
INSERT INTO "transliteration" VALUES(3,110,'3:110','Kuntum khayra ommatin okhrijat linnasita/muroona bilmaAAroofi watanhawna AAani almunkariwatu/minoona billahi walaw amana ahlu alkitabilakana khayran lahum minhumu almu/minoona waaktharuhumualfasiqoon');
INSERT INTO "transliteration" VALUES(3,111,'3:111','Lan yadurrookum illa athanwa-in yuqatilookum yuwallookumu al-adbara thumma layunsaroon');
INSERT INTO "transliteration" VALUES(3,112,'3:112','Duribat AAalayhimu aththillatuayna ma thuqifoo illa bihablin mina Allahiwahablin mina annasi wabaoo bighadabinmina Allahi waduribat AAalayhimu almaskanatu thalikabi-annahum kanoo yakfuroona bi-ayati Allahiwayaqtuloona al-anbiyaa bighayri haqqin thalikabima AAasaw wakanoo yaAAtadoon');
INSERT INTO "transliteration" VALUES(3,113,'3:113','Laysoo sawaan min ahli alkitabiommatun qa-imatun yatloona ayati Allahianaa allayli wahum yasjudoon');
INSERT INTO "transliteration" VALUES(3,114,'3:114','Yu/minoona billahi walyawmial-akhiri waya/muroona bilmaAAroofi wayanhawnaAAani almunkari wayusariAAoona fee alkhayrati waola-ikamina assaliheen');
INSERT INTO "transliteration" VALUES(3,115,'3:115','Wama yafAAaloo min khayrin falanyukfaroohu wallahu AAaleemun bilmuttaqeen');
INSERT INTO "transliteration" VALUES(3,116,'3:116','Inna allatheena kafaroo lan tughniyaAAanhum amwaluhum wala awladuhum mina Allahishay-an waola-ika as-habu annarihum feeha khalidoon');
INSERT INTO "transliteration" VALUES(3,117,'3:117','Mathalu ma yunfiqoona fee hathihialhayati addunya kamathali reehinfeeha sirrun asabat hartha qawmin thalamooanfusahum faahlakat-hu wama thalamahumu Allahuwalakin anfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(3,118,'3:118','Ya ayyuha allatheena amanoola tattakhithoo bitanatan min doonikum laya/loonakum khabalan waddoo ma AAanittum qad badatialbaghdao min afwahihim wama tukhfee sudooruhumakbaru qad bayyanna lakumu al-ayati inkuntum taAAqiloon');
INSERT INTO "transliteration" VALUES(3,119,'3:119','Ha antum ola-i tuhibboonahumwala yuhibboonakum watu/minoona bilkitabikullihi wa-itha laqookum qaloo amannawa-itha khalaw AAaddoo AAalaykumu al-anamilamina alghaythi qul mootoo bighaythikuminna Allaha AAaleemun bithati assudoor');
INSERT INTO "transliteration" VALUES(3,120,'3:120','In tamsaskum hasanatun tasu/humwa-in tusibkum sayyi-atun yafrahoo bihawa-in tasbiroo watattaqoo la yadurrukumkayduhum shay-an inna Allaha bima yaAAmaloona muheet');
INSERT INTO "transliteration" VALUES(3,121,'3:121','Wa-ith ghadawta min ahlika tubawwi-oalmu/mineena maqaAAida lilqitali wallahusameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(3,122,'3:122','Ith hammat ta-ifataniminkum an tafshala wallahu waliyyuhumawaAAala Allahi falyatawakkali almu/minoon');
INSERT INTO "transliteration" VALUES(3,123,'3:123','Walaqad nasarakumu Allahubibadrin waantum athillatun fattaqoo AllahalaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(3,124,'3:124','Ith taqoolu lilmu/mineena alanyakfiyakum an yumiddakum rabbukum bithalathati alafinmina almala-ikati munzaleen');
INSERT INTO "transliteration" VALUES(3,125,'3:125','Bala in tasbiroo watattaqoowaya/tookum min fawrihim hatha yumdidkum rabbukumbikhamsati alafin mina almala-ikatimusawwimeen');
INSERT INTO "transliteration" VALUES(3,126,'3:126','Wama jaAAalahu Allahu illabushra lakum walitatma-inna quloobukum bihi wamaannasru illa min AAindi AllahialAAazeezi alhakeem');
INSERT INTO "transliteration" VALUES(3,127,'3:127','LiyaqtaAAa tarafan mina allatheenakafaroo aw yakbitahum fayanqaliboo kha-ibeen');
INSERT INTO "transliteration" VALUES(3,128,'3:128','Laysa laka mina al-amri shay-on aw yatoobaAAalayhim aw yuAAaththibahum fa-innahum thalimoon');
INSERT INTO "transliteration" VALUES(3,129,'3:129','Walillahi ma fee assamawatiwama fee al-ardi yaghfiru liman yashaowayuAAaththibu man yashao wallahughafoorun raheem');
INSERT INTO "transliteration" VALUES(3,130,'3:130','Ya ayyuha allatheena amanoola ta/kuloo arriba adAAafanmudaAAafatan wattaqoo Allaha laAAallakumtuflihoon');
INSERT INTO "transliteration" VALUES(3,131,'3:131','Wattaqoo annaraallatee oAAiddat lilkafireen');
INSERT INTO "transliteration" VALUES(3,132,'3:132','WaateeAAoo Allaha warrasoolalaAAallakum turhamoon');
INSERT INTO "transliteration" VALUES(3,133,'3:133','WasariAAoo ila maghfiratinmin rabbikum wajannatin AAarduha assamawatuwal-ardu oAAiddat lilmuttaqeen');
INSERT INTO "transliteration" VALUES(3,134,'3:134','Allatheena yunfiqoona fee assarra-iwaddarra-i walkathimeenaalghaytha walAAafeena AAani annasiwallahu yuhibbu almuhsineen');
INSERT INTO "transliteration" VALUES(3,135,'3:135','Wallatheena ithafaAAaloo fahishatan aw thalamoo anfusahum thakarooAllaha fastaghfaroo lithunoobihim wamanyaghfiru aththunooba illa Allahuwalam yusirroo AAala ma faAAaloo wahumyaAAlamoon');
INSERT INTO "transliteration" VALUES(3,136,'3:136','Ola-ika jazaohum maghfiratunmin rabbihim wajannatun tajree min tahtihaal-anharu khalideena feeha waniAAma ajrualAAamileen');
INSERT INTO "transliteration" VALUES(3,137,'3:137','Qad khalat min qablikum sunanun faseeroofee al-ardi fanthuroo kayfa kanaAAaqibatu almukaththibeen');
INSERT INTO "transliteration" VALUES(3,138,'3:138','Hatha bayanun linnasiwahudan wamawAAithatun lilmuttaqeen');
INSERT INTO "transliteration" VALUES(3,139,'3:139','Wala tahinoo wala tahzanoowaantumu al-aAAlawna in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(3,140,'3:140','In yamsaskum qarhun faqad massaalqawma qarhun mithluhu watilka al-ayyamu nudawiluhabayna annasi waliyaAAlama Allahu allatheenaamanoo wayattakhitha minkum shuhadaa wallahula yuhibbu aththalimeen');
INSERT INTO "transliteration" VALUES(3,141,'3:141','Waliyumahhisa Allahuallatheena amanoo wayamhaqa alkafireen');
INSERT INTO "transliteration" VALUES(3,142,'3:142','Am hasibtum an tadkhuloo aljannatawalamma yaAAlami Allahu allatheena jahadoominkum wayaAAlama assabireen');
INSERT INTO "transliteration" VALUES(3,143,'3:143','Walaqad kuntum tamannawna almawta min qablian talqawhu faqad raaytumoohu waantum tanthuroon');
INSERT INTO "transliteration" VALUES(3,144,'3:144','Wama muhammadun illarasoolun qad khalat min qablihi arrusulu afa-in mataaw qutila inqalabtum AAala aAAqabikum wamanyanqalib AAala AAaqibayhi falan yadurra Allahashay-an wasayajzee Allahu ashshakireen');
INSERT INTO "transliteration" VALUES(3,145,'3:145','Wama kana linafsin an tamootailla bi-ithni Allahi kitabanmu-ajjalan waman yurid thawaba addunyanu/tihi minha waman yurid thawaba al-akhiratinu/tihi minha wasanajzee ashshakireen');
INSERT INTO "transliteration" VALUES(3,146,'3:146','Wakaayyin min nabiyyin qatalamaAAahu ribbiyyoona katheerun fama wahanoo lima asabahumfee sabeeli Allahi wama daAAufoo wamaistakanoo wallahu yuhibbu assabireen');
INSERT INTO "transliteration" VALUES(3,147,'3:147','Wama kana qawlahum illaan qaloo rabbana ighfir lana thunoobanawa-israfana fee amrina wathabbit aqdamanawansurna AAala alqawmi alkafireen');
INSERT INTO "transliteration" VALUES(3,148,'3:148','Faatahumu Allahu thawabaaddunya wahusna thawabi al-akhiratiwallahu yuhibbu almuhsineen');
INSERT INTO "transliteration" VALUES(3,149,'3:149','Ya ayyuha allatheena amanooin tuteeAAoo allatheena kafaroo yaruddookum AAalaaAAqabikum fatanqaliboo khasireen');
INSERT INTO "transliteration" VALUES(3,150,'3:150','Bali Allahu mawlakum wahuwakhayru annasireen');
INSERT INTO "transliteration" VALUES(3,151,'3:151','Sanulqee fee quloobi allatheenakafaroo arruAAba bima ashrakoo billahima lam yunazzil bihi sultanan wama/wahumu annaruwabi/sa mathwa aththalimeen');
INSERT INTO "transliteration" VALUES(3,152,'3:152','Walaqad sadaqakumu AllahuwaAAdahu ith tahussoonahum bi-ithnihi hattaitha fashiltum watanazaAAtum fee al-amri waAAasaytummin baAAdi ma arakum ma tuhibboonaminkum man yureedu addunya waminkum man yureedual-akhirata thumma sarafakum AAanhum liyabtaliyakumwalaqad AAafa AAankum wallahu thoofadlin AAala almu/mineen');
INSERT INTO "transliteration" VALUES(3,153,'3:153','Ith tusAAidoona walatalwoona AAala ahadin warrasooluyadAAookum fee okhrakum faathabakum ghammanbighammin likay la tahzanoo AAala mafatakum wala ma asabakum wallahukhabeerun bima taAAmaloon');
INSERT INTO "transliteration" VALUES(3,154,'3:154','Thumma anzala AAalaykum min baAAdi alghammiamanatan nuAAasan yaghsha ta-ifatan minkumwata-ifatun qad ahammat-hum anfusuhum yathunnoonabillahi ghayra alhaqqi thannaaljahiliyyati yaqooloona hal lana mina al-amri minshay-in qul inna al-amra kullahu lillahi yukhfoona feeanfusihim ma la yubdoona laka yaqooloona law kanalana mina al-amri shay-on ma qutilna hahunaqul law kuntum fee buyootikum labaraza allatheena kutibaAAalayhimu alqatlu ila madajiAAihim waliyabtaliyaAllahu ma fee sudoorikum waliyumahhisama fee quloobikum wallahu AAaleemun bithatiassudoor');
INSERT INTO "transliteration" VALUES(3,155,'3:155','Inna allatheena tawallaw minkumyawma iltaqa aljamAAani innama istazallahumuashshaytanu bibaAAdi ma kasaboowalaqad AAafa Allahu AAanhum inna Allahaghafoorun haleem');
INSERT INTO "transliteration" VALUES(3,156,'3:156','Ya ayyuha allatheena amanoola takoonoo kallatheena kafaroo waqalooli-ikhwanihim itha daraboo fee al-ardiaw kanoo ghuzzan law kanoo AAindana mamatoo wama qutiloo liyajAAala Allahu thalikahasratan fee quloobihim wallahu yuhyeewayumeetu wallahu bima taAAmaloona baseer');
INSERT INTO "transliteration" VALUES(3,157,'3:157','Wala-in qutiltum fee sabeeli Allahiaw muttum lamaghfiratun mina Allahi warahmatunkhayrun mimma yajmaAAoon');
INSERT INTO "transliteration" VALUES(3,158,'3:158','Wala-in muttum aw qutiltum la-ilaAllahi tuhsharoon');
INSERT INTO "transliteration" VALUES(3,159,'3:159','Fabima rahmatin mina Allahilinta lahum walaw kunta faththan ghaleethaalqalbi lanfaddoo min hawlika faAAfuAAanhum wastaghfir lahum washawirhum fee al-amrifa-itha AAazamta fatawakkal AAala Allahiinna Allaha yuhibbu almutawakkileen');
INSERT INTO "transliteration" VALUES(3,160,'3:160','In yansurkumu Allahu falaghaliba lakum wa-in yakhthulkum faman thaallathee yansurukum min baAAdihi waAAala Allahifalyatawakkali almu/minoon');
INSERT INTO "transliteration" VALUES(3,161,'3:161','Wama kana linabiyyin anyaghulla waman yaghlul ya/ti bima ghalla yawma alqiyamatithumma tuwaffa kullu nafsin ma kasabat wahum layuthlamoon');
INSERT INTO "transliteration" VALUES(3,162,'3:162','Afamani ittabaAAa ridwana Allahikaman baa bisakhatin mina Allahi wama/wahujahannamu wabi/sa almaseer');
INSERT INTO "transliteration" VALUES(3,163,'3:163','Hum darajatun AAinda Allahiwallahu baseerun bima yaAAmaloon');
INSERT INTO "transliteration" VALUES(3,164,'3:164','Laqad manna Allahu AAalaalmu/mineena ith baAAatha feehim rasoolan min anfusihimyatloo AAalayhim ayatihi wayuzakkeehimwayuAAallimuhumu alkitaba walhikmata wa-inkanoo min qablu lafee dalalin mubeen');
INSERT INTO "transliteration" VALUES(3,165,'3:165','Awa lamma asabatkum museebatunqad asabtum mithlayha qultum anna hathaqul huwa min AAindi anfusikum inna Allaha AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(3,166,'3:166','Wama asabakum yawma iltaqaaljamAAani fabi-ithni Allahi waliyaAAlamaalmu/mineen');
INSERT INTO "transliteration" VALUES(3,167,'3:167','WaliyaAAlama allatheena nafaqoowaqeela lahum taAAalaw qatiloo fee sabeeli Allahiawi idfaAAoo qaloo law naAAlamu qitalan lattabaAAnakumhum lilkufri yawma-ithin aqrabu minhum lil-eemaniyaqooloona bi-afwahihim ma laysa fee quloobihim wallahuaAAlamu bima yaktumoon');
INSERT INTO "transliteration" VALUES(3,168,'3:168','Allatheena qaloo li-ikhwanihimwaqaAAadoo law ataAAoona ma qutiloo qul fadraooAAan anfusikumu almawta in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(3,169,'3:169','Wala tahsabanna allatheenaqutiloo fee sabeeli Allahi amwatan bal ahyaonAAinda rabbihim yurzaqoon');
INSERT INTO "transliteration" VALUES(3,170,'3:170','Fariheena bima atahumuAllahu min fadlihi wayastabshiroona billatheenalam yalhaqoo bihim min khalfihim alla khawfunAAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(3,171,'3:171','Yastabshiroona biniAAmatin mina Allahiwafadlin waanna Allaha la yudeeAAuajra almu/mineen');
INSERT INTO "transliteration" VALUES(3,172,'3:172','Allatheena istajaboo lillahiwarrasooli min baAAdi ma asabahumu alqarhulillatheena ahsanoo minhum wattaqaw ajrunAAatheem');
INSERT INTO "transliteration" VALUES(3,173,'3:173','Allatheena qala lahumu annasuinna annasa qad jamaAAoo lakum fakhshawhumfazadahum eemanan waqaloo hasbunaAllahu waniAAma alwakeel');
INSERT INTO "transliteration" VALUES(3,174,'3:174','Fanqalaboo biniAAmatin mina Allahiwafadlin lam yamsas-hum soo-on wattabaAAoo ridwanaAllahi wallahu thoo fadlinAAatheem');
INSERT INTO "transliteration" VALUES(3,175,'3:175','Innama thalikumu ashshaytanuyukhawwifu awliyaahu fala takhafoohum wakhafooniin kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(3,176,'3:176','Wala yahzunka allatheenayusariAAoona fee alkufri innahum lan yadurroo Allahashay-an yureedu Allahu alla yajAAala lahum haththanfee al-akhirati walahum AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(3,177,'3:177','Inna allatheena ishtarawoo alkufrabil-eemani lan yadurroo Allahashay-an walahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(3,178,'3:178','Wala yahsabanna allatheenakafaroo annama numlee lahum khayrun li-anfusihim innamanumlee lahum liyazdadoo ithman walahum AAathabunmuheen');
INSERT INTO "transliteration" VALUES(3,179,'3:179','Ma kana Allahu liyatharaalmu/mineena AAala ma antum AAalayhi hattayameeza alkhabeetha mina attayyibi wama kanaAllahu liyutliAAakum AAala alghaybi walakinnaAllaha yajtabee min rusulihi man yashao faaminoobillahi warusulihi wa-in tu/minoo watattaqoofalakum ajrun AAatheem');
INSERT INTO "transliteration" VALUES(3,180,'3:180','Wala yahsabanna allatheenayabkhaloona bima atahummu Allahu minfadlihi huwa khayran lahum bal huwa sharrun lahum sayutawwaqoonama bakhiloo bihi yawma alqiyamati walillahimeerathu assamawati wal-ardiwallahu bima taAAmaloona khabeer');
INSERT INTO "transliteration" VALUES(3,181,'3:181','Laqad samiAAa Allahu qawla allatheenaqaloo inna Allaha faqeerun wanahnu aghniyaonsanaktubu ma qaloo waqatlahumu al-anbiyaabighayri haqqin wanaqoolu thooqoo AAathabaalhareeq');
INSERT INTO "transliteration" VALUES(3,182,'3:182','Thalika bima qaddamataydeekum waanna Allaha laysa bithallaminlilAAabeed');
INSERT INTO "transliteration" VALUES(3,183,'3:183','Allatheena qaloo inna AllahaAAahida ilayna alla nu/mina lirasoolin hattaya/tiyana biqurbanin ta/kuluhu annaruqul qad jaakum rusulun min qablee bilbayyinatiwabillathee qultum falima qataltumoohum in kutum sadiqeen');
INSERT INTO "transliteration" VALUES(3,184,'3:184','Fa-in kaththabooka faqad kuththibarusulun min qablika jaoo bilbayyinati wazzuburiwalkitabi almuneer');
INSERT INTO "transliteration" VALUES(3,185,'3:185','Kullu nafsin tha-iqatu almawtiwa-innama tuwaffawna ojoorakum yawma alqiyamatifaman zuhziha AAani annari waodkhilaaljannata faqad faza wama alhayatu addunyailla mataAAu alghuroor');
INSERT INTO "transliteration" VALUES(3,186,'3:186','Latublawunna fee amwalikumwaanfusikum walatasmaAAunna mina allatheena ootoo alkitabamin qablikum wamina allatheena ashrakoo athankatheeran wa-in tasbiroo watattaqoo fa-inna thalikamin AAazmi al-omoor');
INSERT INTO "transliteration" VALUES(3,187,'3:187','Wa-ith akhatha Allahumeethaqa allatheena ootoo alkitabalatubayyinunnahu linnasi wala taktumoonahufanabathoohu waraa thuhoorihim washtarawbihi thamanan qaleelan fabi/sa ma yashtaroon');
INSERT INTO "transliteration" VALUES(3,188,'3:188','La tahsabanna allatheenayafrahoona bima ataw wayuhibboona an yuhmadoobima lam yafAAaloo fala tahsabannahum bimafazatinmina alAAathabi walahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(3,189,'3:189','Walillahi mulku assamawatiwal-ardi wallahu AAala kullishay-in qadeer');
INSERT INTO "transliteration" VALUES(3,190,'3:190','Inna fee khalqi assamawatiwal-ardi wakhtilafi allayli wannaharilaayatin li-olee al-albab');
INSERT INTO "transliteration" VALUES(3,191,'3:191','Allatheena yathkuroona Allahaqiyaman waquAAoodan waAAala junoobihimwayatafakkaroona fee khalqi assamawati wal-ardirabbana ma khalaqta hatha batilan subhanakafaqina AAathaba annar');
INSERT INTO "transliteration" VALUES(3,192,'3:192','Rabbana innaka man tudkhili annarafaqad akhzaytahu wama liththalimeenamin ansar');
INSERT INTO "transliteration" VALUES(3,193,'3:193','Rabbana innana samiAAnamunadiyan yunadee lil-eemani an aminoobirabbikum faamanna rabbana faghfirlana thunoobana wakaffir AAannasayyi-atina watawaffana maAAa al-abrar');
INSERT INTO "transliteration" VALUES(3,194,'3:194','Rabbana waatina mawaAAadtana AAala rusulika wala tukhzinayawma alqiyamati innaka la tukhlifu almeeAAad');
INSERT INTO "transliteration" VALUES(3,195,'3:195','Fastajaba lahum rabbuhumannee la odeeAAu AAamala AAamilin minkum minthakarin aw ontha baAAdukum min baAAdinfallatheena hajaroo waokhrijoo min diyarihimwaoothoo fee sabeelee waqataloo waqutiloolaokaffiranna AAanhum sayyi-atihim walaodkhilannahum jannatintajree min tahtiha al-anharu thawabanmin AAindi Allahi wallahu AAindahu husnuaththawab');
INSERT INTO "transliteration" VALUES(3,196,'3:196','La yaghurrannaka taqallubu allatheenakafaroo fee albilad');
INSERT INTO "transliteration" VALUES(3,197,'3:197','MataAAun qaleelun thumma ma/wahumjahannamu wabi/sa almihad');
INSERT INTO "transliteration" VALUES(3,198,'3:198','Lakini allatheena ittaqawrabbahum lahum jannatun tajree min tahtihaal-anharu khalideena feeha nuzulan minAAindi Allahi wama AAinda Allahi khayrunlil-abrar');
INSERT INTO "transliteration" VALUES(3,199,'3:199','Wa-inna min ahli alkitabi lamanyu/minu billahi wama onzila ilaykum wamaonzila ilayhim khashiAAeena lillahi layashtaroona bi-ayati Allahi thamananqaleelan ola-ika lahum ajruhum AAinda rabbihim inna AllahasareeAAu alhisab');
INSERT INTO "transliteration" VALUES(3,200,'3:200','Ya ayyuha allatheena amanooisbiroo wasabiroo warabitoo wattaqooAllaha laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(4,1,'4:1','Ya ayyuha annasuittaqoo rabbakumu allathee khalaqakum min nafsin wahidatinwakhalaqa minha zawjaha wabaththa minhumarijalan katheeran wanisaan wattaqoo Allahaallathee tasaaloona bihi wal-arhamainna Allaha kana AAalaykum raqeeb');
INSERT INTO "transliteration" VALUES(4,2,'4:2','Waatoo alyatama amwalahumwala tatabaddaloo alkhabeetha bittayyibiwala ta/kuloo amwalahum ila amwalikuminnahu kana hooban kabeera');
INSERT INTO "transliteration" VALUES(4,3,'4:3','Wa-in khiftum alla tuqsitoo feealyatama fankihoo ma tabalakum mina annisa-i mathna wathulathawarubaAAa fa-in khiftum alla taAAdiloo fawahidatanaw ma malakat aymanukum thalika adnaalla taAAooloo');
INSERT INTO "transliteration" VALUES(4,4,'4:4','Waatoo annisaa saduqatihinnanihlatan fa-in tibna lakum AAan shay-in minhunafsan fakuloohu hanee-an maree-a');
INSERT INTO "transliteration" VALUES(4,5,'4:5','Wala tu/too assufahaaamwalakumu allatee jaAAala Allahu lakum qiyamanwarzuqoohum feeha waksoohum waqooloo lahumqawlan maAAroofa');
INSERT INTO "transliteration" VALUES(4,6,'4:6','Wabtaloo alyatama hattaitha balaghoo annikaha fa-in anastumminhum rushdan fadfaAAoo ilayhim amwalahum walata/kulooha israfan wabidaran an yakbaroowaman kana ghaniyyan falyastaAAfif waman kanafaqeeran falya/kul bilmaAAroofi fa-itha dafaAAtumilayhim amwalahum faashhidoo AAalayhim wakafa billahihaseeba');
INSERT INTO "transliteration" VALUES(4,7,'4:7','Lirrijali naseebun mimmataraka alwalidani wal-aqraboona walinnisa-inaseebun mimma taraka alwalidani wal-aqraboonamimma qalla minhu aw kathura naseeban mafrooda');
INSERT INTO "transliteration" VALUES(4,8,'4:8','Wa-itha hadara alqismataoloo alqurba walyatama walmasakeenufarzuqoohum minhu waqooloo lahum qawlan maAAroofa');
INSERT INTO "transliteration" VALUES(4,9,'4:9','Walyakhsha allatheena law tarakoo minkhalfihim thurriyyatan diAAafan khafooAAalayhim falyattaqoo Allaha walyaqooloo qawlan sadeeda');
INSERT INTO "transliteration" VALUES(4,10,'4:10','Inna allatheena ya/kuloona amwalaalyatama thulman innamaya/kuloona fee butoonihim naran wasayaslawnasaAAeera');
INSERT INTO "transliteration" VALUES(4,11,'4:11','Yooseekumu Allahu fee awladikumliththakari mithlu haththialonthayayni fa-in kunna nisaan fawqa ithnatayni falahunnathulutha ma taraka wa-in kanat wahidatanfalaha annisfu wali-abawayhi likulli wahidinminhuma assudusu mimma taraka in kanalahu waladun fa-in lam yakun lahu waladun wawarithahu abawahufali-ommihi aththuluthu fa-in kana lahu ikhwatunfali-ommihi assudusu min baAAdi wasiyyatin yooseebiha aw daynin abaokum waabnaokum latadroona ayyuhum aqrabu lakum nafAAan fareedatan mina Allahiinna Allaha kana AAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,12,'4:12','Walakum nisfu ma taraka azwajukumin lam yakun lahunna waladun fa-in kana lahunna waladunfalakumu arrubuAAu mimma tarakna min baAAdi wasiyyatinyooseena biha aw daynin walahunna arrubuAAumimma taraktum in lam yakun lakum waladun fa-in kanalakum waladun falahunna aththumunu mimma taraktummin baAAdi wasiyyatin toosoona biha awdaynin wa-in kana rajulun yoorathu kalalatan awiimraatun walahu akhun aw okhtun falikulli wahidin minhumaassudusu fa-in kanoo akthara min thalikafahum shurakao fee aththuluthi min baAAdi wasiyyatinyoosa biha aw daynin ghayra mudarrin wasiyyatanmina Allahi wallahu AAaleemun haleem');
INSERT INTO "transliteration" VALUES(4,13,'4:13','Tilka hudoodu Allahi waman yutiAAiAllaha warasoolahu yudkhilhu jannatin tajree min tahtihaal-anharu khalideena feeha wathalikaalfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(4,14,'4:14','Waman yaAAsi Allahawarasoolahu wayataAAadda hudoodahu yudkhilhu narankhalidan feeha walahu AAathabun muheen');
INSERT INTO "transliteration" VALUES(4,15,'4:15','Wallatee ya/teena alfahishatamin nisa-ikum fastashhidoo AAalayhinna arbaAAatanminkum fa-in shahidoo faamsikoohunna fee albuyooti hattayatawaffahunna almawtu aw yajAAala Allahu lahunnasabeela');
INSERT INTO "transliteration" VALUES(4,16,'4:16','Wallathani ya/tiyanihaminkum faathoohuma fa-in taba waaslahafaaAAridoo AAanhuma inna Allaha kanatawwaban raheema');
INSERT INTO "transliteration" VALUES(4,17,'4:17','Innama attawbatu AAalaAllahi lillatheena yaAAmaloona assoo-abijahalatin thumma yatooboona min qareebin faola-ikayatoobu Allahu AAalayhim wakana AllahuAAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,18,'4:18','Walaysati attawbatu lillatheenayaAAmaloona assayyi-ati hatta ithahadara ahadahumu almawtu qala inneetubtu al-ana wala allatheena yamootoonawahum kuffarun ola-ika aAAtadna lahum AAathabanaleema');
INSERT INTO "transliteration" VALUES(4,19,'4:19','Ya ayyuha allatheena amanoola yahillu lakum an tarithoo annisaakarhan wala taAAduloohunna litathhaboobibaAAdi ma ataytumoohunna illa anya/teena bifahishatin mubayyinatin waAAashiroohunnabilmaAAroofi fa-in karihtumoohunna faAAasa antakrahoo shay-an wayajAAala Allahu feehi khayran katheera');
INSERT INTO "transliteration" VALUES(4,20,'4:20','Wa-in aradtumu istibdala zawjin makanazawjin waataytum ihdahunna qintaranfala ta/khuthoo minhu shay-an ata/khuthoonahubuhtanan wa-ithman mubeena');
INSERT INTO "transliteration" VALUES(4,21,'4:21','Wakayfa ta/khuthoonahu waqad afdabaAAdukum ila baAAdin waakhathnaminkum meethaqan ghaleetha');
INSERT INTO "transliteration" VALUES(4,22,'4:22','Wala tankihoo ma nakahaabaokum mina annisa-i illa maqad salafa innahu kana fahishatan wamaqtan wasaasabeela');
INSERT INTO "transliteration" VALUES(4,23,'4:23','Hurrimat AAalaykum ommahatukumwabanatukum waakhawatukum waAAammatukum wakhalatukumwabanatu al-akhi wabanatu al-okhti waommahatukumuallatee ardaAAnakum waakhawatukum mina arradaAAatiwaommahatu nisa-ikum waraba-ibukumu allateefee hujoorikum min nisa-ikumu allateedakhaltum bihinna fa-in lam takoonoo dakhaltum bihinna falajunaha AAalaykum wahala-ilu abna-ikumuallatheena min aslabikum waan tajmaAAoobayna al-okhtayni illa ma qad salafa inna Allahakana ghafooran raheema');
INSERT INTO "transliteration" VALUES(4,24,'4:24','Walmuhsanatu mina annisa-iilla ma malakat aymanukum kitaba AllahiAAalaykum waohilla lakum ma waraa thalikuman tabtaghoo bi-amwalikum muhsineena ghayra musafiheenafama istamtaAAtum bihi minhunna faatoohunnaojoorahunna fareedatan wala junaha AAalaykumfeema taradaytum bihi min baAAdi alfareedatiinna Allaha kana AAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,25,'4:25','Waman lam yastatiAA minkum tawlanan yankiha almuhsanati almu/minatifamin ma malakat aymanukum min fatayatikumualmu/minati wallahu aAAlamu bi-eemanikumbaAAdukum min baAAdin fankihoohunnabi-ithni ahlihinna waatoohunna ojoorahunna bilmaAAroofimuhsanatin ghayra masafihatin walamuttakhithati akhdanin fa-itha ohsinnafa-in atayna bifahishatin faAAalayhinna nisfu maAAala almuhsanati mina alAAathabi thalikaliman khashiya alAAanata minkum waan tasbiroo khayrunlakum wallahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(4,26,'4:26','Yureedu Allahu liyubayyina lakumwayahdiyakum sunana allatheena min qablikum wayatoobaAAalaykum wallahu AAaleemun hakeem');
INSERT INTO "transliteration" VALUES(4,27,'4:27','Wallahu yureedu an yatoobaAAalaykum wayureedu allatheena yattabiAAoona ashshahawatian tameeloo maylan AAatheem');
INSERT INTO "transliteration" VALUES(4,28,'4:28','Yureedu Allahu an yukhaffifa AAankumwakhuliqa al-insanu daAAeefa');
INSERT INTO "transliteration" VALUES(4,29,'4:29','Ya ayyuha allatheena amanoola ta/kuloo amwalakum baynakum bilbatiliilla an takoona tijaratan AAan taradinminkum wala taqtuloo anfusakum inna Allaha kanabikum raheema');
INSERT INTO "transliteration" VALUES(4,30,'4:30','Waman yafAAal thalika AAudwananwathulman fasawfa nusleehi naran wakanathalika AAala Allahi yaseera');
INSERT INTO "transliteration" VALUES(4,31,'4:31','In tajtaniboo kaba-ira matunhawna AAanhu nukaffir AAankum sayyi-atikum wanudkhilkummudkhalan kareema');
INSERT INTO "transliteration" VALUES(4,32,'4:32','Wala tatamannaw ma faddalaAllahu bihi baAAdakum AAala baAAdinlirrijali naseebun mimma iktasaboowalinnisa-i naseebun mimma iktasabnawas-aloo Allaha min fadlihi inna Allahakana bikulli shay-in AAaleema');
INSERT INTO "transliteration" VALUES(4,33,'4:33','Walikullin jaAAalna mawaliyamimma taraka alwalidani wal-aqraboonawallatheena AAaqadat aymanukum faatoohumnaseebahum inna Allaha kana AAalakulli shay-in shaheeda');
INSERT INTO "transliteration" VALUES(4,34,'4:34','Arrijalu qawwamoonaAAala annisa-i bima faddalaAllahu baAAdahum AAala baAAdin wabimaanfaqoo min amwalihim fassalihatu qanitatunhafithatun lilghaybi bima hafithaAllahu wallatee takhafoonanushoozahunna faAAithoohunna wahjuroohunnafee almadajiAAi wadriboohunna fa-in ataAAnakumfala tabghoo AAalayhinna sabeelan inna Allaha kanaAAaliyyan kabeera');
INSERT INTO "transliteration" VALUES(4,35,'4:35','Wa-in khiftum shiqaqa baynihimafabAAathoo hakaman min ahlihi wahakaman minahliha in yureeda islahan yuwaffiqiAllahu baynahuma inna Allaha kanaAAaleeman khabeera');
INSERT INTO "transliteration" VALUES(4,36,'4:36','WaAAbudoo Allaha walatushrikoo bihi shay-an wabilwalidayni ihsananwabithee alqurba walyatama walmasakeeniwaljari thee alqurba waljarialjunubi wassahibi biljanbi wabniassabeeli wama malakat aymanukum inna Allahala yuhibbu man kana mukhtalan fakhoora');
INSERT INTO "transliteration" VALUES(4,37,'4:37','Allatheena yabkhaloona waya/muroona annasabilbukhli wayaktumoona ma atahumuAllahu min fadlihi waaAAtadna lilkafireenaAAathaban muheena');
INSERT INTO "transliteration" VALUES(4,38,'4:38','Wallatheena yunfiqoona amwalahumri-aa annasi wala yu/minoona billahiwala bilyawmi al-akhiri waman yakuni ashshaytanulahu qareenan fasaa qareena');
INSERT INTO "transliteration" VALUES(4,39,'4:39','Wamatha AAalayhim law amanoobillahi walyawmi al-akhiriwaanfaqoo mimma razaqahumu Allahu wakana Allahubihim AAaleema');
INSERT INTO "transliteration" VALUES(4,40,'4:40','Inna Allaha la yathlimumithqala tharratin wa-in taku hasanatan yudaAAifhawayu/ti min ladunhu ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,41,'4:41','Fakayfa itha ji/na min kulliommatin bishaheedin waji/na bika AAala haola-ishaheeda');
INSERT INTO "transliteration" VALUES(4,42,'4:42','Yawma-ithin yawaddu allatheenakafaroo waAAasawoo arrasoola law tusawwabihimu al-ardu wala yaktumoona Allaha hadeetha');
INSERT INTO "transliteration" VALUES(4,43,'4:43','Ya ayyuha allatheena amanoola taqraboo assalata waantum sukarahatta taAAlamoo ma taqooloona walajunuban illa AAabiree sabeelin hattataghtasiloo wa-in kuntum marda aw AAala safarin awjaa ahadun minkum mina algha-iti aw lamastumuannisaa falam tajidoo maan fatayammamoo saAAeedantayyiban famsahoo biwujoohikum waaydeekuminna Allaha kana AAafuwwan ghafoora');
INSERT INTO "transliteration" VALUES(4,44,'4:44','Alam tara ila allatheena ootoonaseeban mina alkitabi yashtaroona addalalatawayureedoona an tadilloo assabeel');
INSERT INTO "transliteration" VALUES(4,45,'4:45','Wallahu aAAlamu bi-aAAda-ikumwakafa billahi waliyyan wakafa billahinaseera');
INSERT INTO "transliteration" VALUES(4,46,'4:46','Mina allatheena hadoo yuharrifoonaalkalima AAan mawadiAAihi wayaqooloona samiAAnawaAAasayna wasmaAA ghayra musmaAAin waraAAinalayyan bi-alsinatihim wataAAnan fee addeeni walawannahum qaloo samiAAna waataAAna wasmaAAwanthurna lakana khayranlahum waaqwama walakin laAAanahumu Allahubikufrihim fala yu/minoona illa qaleela');
INSERT INTO "transliteration" VALUES(4,47,'4:47','Ya ayyuha allatheenaootoo alkitaba aminoo bima nazzalnamusaddiqan lima maAAakum min qabli an natmisawujoohan fanaruddaha AAala adbarihaaw nalAAanahum kama laAAanna as-habaassabti wakana amru Allahi mafAAoola');
INSERT INTO "transliteration" VALUES(4,48,'4:48','Inna Allaha la yaghfiru anyushraka bihi wayaghfiru ma doona thalika limanyashao waman yushrik billahi faqadi iftaraithman AAatheema');
INSERT INTO "transliteration" VALUES(4,49,'4:49','Alam tara ila allatheenayuzakkoona anfusahum bali Allahu yuzakkee man yashaowala yuthlamoona fateela');
INSERT INTO "transliteration" VALUES(4,50,'4:50','Onthur kayfa yaftaroona AAalaAllahi alkathiba wakafa bihi ithman mubeena');
INSERT INTO "transliteration" VALUES(4,51,'4:51','Alam tara ila allatheena ootoonaseeban mina alkitabi yu/minoona biljibtiwattaghooti wayaqooloona lillatheenakafaroo haola-i ahda mina allatheena amanoosabeela');
INSERT INTO "transliteration" VALUES(4,52,'4:52','Ola-ika allatheena laAAanahumuAllahu waman yalAAani Allahu falan tajida lahu naseera');
INSERT INTO "transliteration" VALUES(4,53,'4:53','Am lahum naseebun mina almulki fa-ithanla yu/toona annasa naqeera');
INSERT INTO "transliteration" VALUES(4,54,'4:54','Am yahsudoona annasaAAala ma atahumu Allahu min fadlihifaqad atayna ala ibraheema alkitabawalhikmata waataynahum mulkan AAatheema');
INSERT INTO "transliteration" VALUES(4,55,'4:55','Faminhum man amana bihi waminhum man saddaAAanhu wakafa bijahannama saAAeera');
INSERT INTO "transliteration" VALUES(4,56,'4:56','Inna allatheena kafaroo bi-ayatinasawfa nusleehim naran kullama nadijatjulooduhum baddalnahum juloodan ghayraha liyathooqooalAAathaba inna Allaha kana AAazeezan hakeema');
INSERT INTO "transliteration" VALUES(4,57,'4:57','Wallatheena amanoowaAAamiloo assalihati sanudkhiluhum jannatintajree min tahtiha al-anharu khalideenafeeha abadan lahum feeha azwajun mutahharatunwanudkhiluhum thillan thaleela');
INSERT INTO "transliteration" VALUES(4,58,'4:58','Inna Allaha ya/murukum an tu-addooal-amanati ila ahliha wa-itha hakamtumbayna annasi an tahkumoo bilAAadliinna Allaha niAAimma yaAAithukum bihiinna Allaha kana sameeAAan baseera');
INSERT INTO "transliteration" VALUES(4,59,'4:59','Ya ayyuha allatheena amanooateeAAoo Allaha waateeAAoo arrasoolawaolee al-amri minkum fa-in tanazaAAtum fee shay-infaruddoohu ila Allahi warrasooli in kuntumtu/minoona billahi walyawmi al-akhirithalika khayrun waahsanu ta/weela');
INSERT INTO "transliteration" VALUES(4,60,'4:60','Alam tara ila allatheenayazAAumoona annahum amanoo bima onzila ilayka wamaonzila min qablika yureedoona an yatahakamoo ila attaghootiwaqad omiroo an yakfuroo bihi wayureedu ashshaytanuan yudillahum dalalan baAAeeda');
INSERT INTO "transliteration" VALUES(4,61,'4:61','Wa-itha qeela lahum taAAalawila ma anzala Allahu wa-ila arrasooliraayta almunafiqeena yasuddoona AAanka sudooda');
INSERT INTO "transliteration" VALUES(4,62,'4:62','Fakayfa itha asabat-hum museebatunbima qaddamat aydeehim thumma jaooka yahlifoonabillahi in aradna illa ihsananwatawfeeqa');
INSERT INTO "transliteration" VALUES(4,63,'4:63','Ola-ika allatheena yaAAlamuAllahu ma fee quloobihim faaAArid AAanhumwaAAithhum waqul lahum fee anfusihim qawlan baleegha');
INSERT INTO "transliteration" VALUES(4,64,'4:64','Wama arsalna min rasoolin illaliyutaAAa bi-ithni Allahi walaw annahum iththalamoo anfusahum jaooka fastaghfarooAllaha wastaghfara lahumu arrasoolulawajadoo Allaha tawwaban raheema');
INSERT INTO "transliteration" VALUES(4,65,'4:65','Fala warabbika la yu/minoona hattayuhakkimooka feema shajara baynahum thumma layajidoo fee anfusihim harajan mimma qadaytawayusallimoo tasleema');
INSERT INTO "transliteration" VALUES(4,66,'4:66','Walaw anna katabna AAalayhimani oqtuloo anfusakum awi okhrujoo min diyarikum mafaAAaloohu illa qaleelun minhum walaw annahum faAAaloo mayooAAathoona bihi lakana khayran lahumwaashadda tathbeeta');
INSERT INTO "transliteration" VALUES(4,67,'4:67','Wa-ithan laataynahummin ladunna ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,68,'4:68','Walahadaynahum siratanmustaqeema');
INSERT INTO "transliteration" VALUES(4,69,'4:69','Waman yutiAAi Allaha warrasoolafaola-ika maAAa allatheena anAAama AllahuAAalayhim mina annabiyyeena wassiddeeqeenawashshuhada-i wassaliheenawahasuna ola-ika rafeeqa');
INSERT INTO "transliteration" VALUES(4,70,'4:70','Thalika alfadlu mina Allahiwakafa billahi AAaleema');
INSERT INTO "transliteration" VALUES(4,71,'4:71','Ya ayyuha allatheena amanookhuthoo hithrakum fanfiroo thubatinawi infiroo jameeAAa');
INSERT INTO "transliteration" VALUES(4,72,'4:72','Wa-inna minkum laman layubatti-annafa-in asabatkum museebatun qala qad anAAamaAllahu AAalayya ith lam akun maAAahum shaheeda');
INSERT INTO "transliteration" VALUES(4,73,'4:73','Wala-in asabakum fadlun minaAllahi layaqoolanna kaan lam takun baynakum wabaynahumawaddatun ya laytanee kuntu maAAahum faafooza fawzan AAatheema');
INSERT INTO "transliteration" VALUES(4,74,'4:74','Falyuqatil fee sabeeli Allahiallatheena yashroona alhayata addunyabil-akhirati waman yuqatil fee sabeeli Allahifayuqtal aw yaghlib fasawfa nu/teehi ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,75,'4:75','Wama lakum la tuqatiloonafee sabeeli Allahi walmustadAAafeena mina arrijaliwannisa-i walwildani allatheenayaqooloona rabbana akhrijna min hathihialqaryati aththalimi ahluhawajAAal lana min ladunka waliyyan wajAAallana min ladunka naseera');
INSERT INTO "transliteration" VALUES(4,76,'4:76','Allatheena amanoo yuqatiloonafee sabeeli Allahi wallatheena kafaroo yuqatiloonafee sabeeli attaghooti faqatiloo awliyaaashshaytani inna kayda ashshaytanikana daAAeefa');
INSERT INTO "transliteration" VALUES(4,77,'4:77','Alam tara ila allatheena qeelalahum kuffoo aydiyakum waaqeemoo assalatawaatoo azzakata falamma kutibaAAalayhimu alqitalu itha fareequn minhum yakhshawnaannasa kakhashyati Allahi aw ashaddakhashyatan waqaloo rabbana lima katabta AAalaynaalqitala lawla akhkhartana ila ajalinqareebin qul mataAAu addunya qaleelun wal-akhiratukhayrun limani ittaqa wala tuthlamoonafateela');
INSERT INTO "transliteration" VALUES(4,78,'4:78','Aynama takoonoo yudrikkumu almawtuwalaw kuntum fee buroojin mushayyadatin wa-in tusibhum hasanatunyaqooloo hathihi min AAindi Allahi wa-in tusibhumsayyi-atun yaqooloo hathihi min AAindika qul kullun minAAindi Allahi famali haola-i alqawmila yakadoona yafqahoona hadeetha');
INSERT INTO "transliteration" VALUES(4,79,'4:79','Ma asabaka min hasanatinfamina Allahi wama asabaka min sayyi-atinfamin nafsika waarsalnaka linnasi rasoolanwakafa billahi shaheedan');
INSERT INTO "transliteration" VALUES(4,80,'4:80','Man yutiAAi arrasoola faqad ataAAaAllaha waman tawalla fama arsalnakaAAalayhim hafeetha');
INSERT INTO "transliteration" VALUES(4,81,'4:81','Wayaqooloona taAAatun fa-ithabarazoo min AAindika bayyata ta-ifatun minhum ghayra allatheetaqoolu wallahu yaktubu ma yubayyitoonafaaAArid AAanhum watawakkal AAala Allahiwakafa billahi wakeela');
INSERT INTO "transliteration" VALUES(4,82,'4:82','Afala yatadabbaroona alqur-anawalaw kana min AAindi ghayri Allahi lawajadoo feehiikhtilafan katheera');
INSERT INTO "transliteration" VALUES(4,83,'4:83','Wa-itha jaahum amrun minaal-amni awi alkhawfi athaAAoo bihi walaw raddoohu ilaarrasooli wa-ila olee al-amri minhum laAAalimahuallatheena yastanbitoonahu minhum walawla fadluAllahi AAalaykum warahmatuhu lattabaAAtumuashshaytana illa qaleela');
INSERT INTO "transliteration" VALUES(4,84,'4:84','Faqatil fee sabeeli Allahi latukallafu illa nafsaka waharridialmu/mineena AAasa Allahu an yakuffa ba/sa allatheenakafaroo wallahu ashaddu ba/san waashaddu tankeela');
INSERT INTO "transliteration" VALUES(4,85,'4:85','Man yashfaAA shafaAAatan hasanatanyakun lahu naseebun minha waman yashfaAA shafaAAatansayyi-atan yakun lahu kiflun minha wakana AllahuAAala kulli shay-in muqeeta');
INSERT INTO "transliteration" VALUES(4,86,'4:86','Wa-itha huyyeetum bitahiyyatinfahayyoo bi-ahsana minha aw ruddoohainna Allaha kana AAala kulli shay-in haseeba');
INSERT INTO "transliteration" VALUES(4,87,'4:87','Allahu la ilaha illahuwa layajmaAAannakum ila yawmi alqiyamati larayba feehi waman asdaqu mina Allahi hadeetha');
INSERT INTO "transliteration" VALUES(4,88,'4:88','Fama lakum fee almunafiqeenafi-atayni wallahu arkasahum bima kasabooatureedoona an tahdoo man adalla Allahu waman yudliliAllahu falan tajida lahu sabeela');
INSERT INTO "transliteration" VALUES(4,89,'4:89','Waddoo law takfuroona kama kafaroofatakoonoona sawaan fala tattakhithoo minhumawliyaa hatta yuhajiroo fee sabeeliAllahi fa-in tawallaw fakhuthoohum waqtuloohumhaythu wajadtumoohum wala tattakhithoominhum waliyyan wala naseera');
INSERT INTO "transliteration" VALUES(4,90,'4:90','Illa allatheena yasiloonaila qawmin baynakum wabaynahum meethaqun aw jaookumhasirat sudooruhum an yuqatilookum awyuqatiloo qawmahum walaw shaa Allahu lasallatahumAAalaykum falaqatalookum fa-ini iAAtazalookum falam yuqatilookumwaalqaw ilaykumu assalama fama jaAAala Allahulakum AAalayhim sabeela');
INSERT INTO "transliteration" VALUES(4,91,'4:91','Satajidoona akhareena yureedoona anya/manookum waya/manoo qawmahum kulla ma ruddoo ilaalfitnati orkisoo feeha fa-in lam yaAAtazilookum wayulqooilaykumu assalama wayakuffoo aydiyahum fakhuthoohumwaqtuloohum haythu thaqiftumoohum waola-ikumjaAAalna lakum AAalayhim sultanan mubeena');
INSERT INTO "transliteration" VALUES(4,92,'4:92','Wama kana limu/minin anyaqtula mu/minan illa khataan waman qatala mu/minankhataan fatahreeru raqabatin mu/minatin wadiyatunmusallamatun ila ahlihi illa an yassaddaqoofa-in kana min qawmin AAaduwwin lakum wahuwa mu/minun fatahreeruraqabatin mu/minatin wa-in kana min qawmin baynakumwabaynahum meethaqun fadiyatun musallamatun ilaahlihi watahreeru raqabatin mu/minatin faman lam yajid fasiyamushahrayni mutatabiAAayni tawbatan mina Allahi wakanaAllahu AAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,93,'4:93','Waman yaqtul mu/minan mutaAAammidan fajazaohujahannamu khalidan feeha waghadiba AllahuAAalayhi walaAAanahu waaAAadda lahu AAathaban AAatheema');
INSERT INTO "transliteration" VALUES(4,94,'4:94','Ya ayyuha allatheena amanooitha darabtum fee sabeeli Allahifatabayyanoo wala taqooloo liman alqa ilaykumu assalamalasta mu/minan tabtaghoona AAarada alhayatiaddunya faAAinda Allahi maghanimukatheeratun kathalika kuntum min qablu famanna AllahuAAalaykum fatabayyanoo inna Allaha kana bimataAAmaloona khabeera');
INSERT INTO "transliteration" VALUES(4,95,'4:95','La yastawee alqaAAidoona minaalmu/mineena ghayru olee addarari walmujahidoonafee sabeeli Allahi bi-amwalihim waanfusihim faddalaAllahu almujahideena bi-amwalihimwaanfusihim AAala alqaAAideena darajatan wakullanwaAAada Allahu alhusna wafaddala Allahualmujahideena AAala alqaAAideena ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,96,'4:96','Darajatin minhu wamaghfiratan warahmatanwakana Allahu ghafooran raheema');
INSERT INTO "transliteration" VALUES(4,97,'4:97','Inna allatheena tawaffahumualmala-ikatu thalimee anfusihim qaloofeema kuntum qaloo kunna mustadAAafeena feeal-ardi qaloo alam takun ardu AllahiwasiAAatan fatuhajiroo feeha faola-ikama/wahum jahannamu wasaat maseera');
INSERT INTO "transliteration" VALUES(4,98,'4:98','Illa almustadAAafeena mina arrijaliwannisa-i walwildani layastateeAAoona heelatan wala yahtadoonasabeela');
INSERT INTO "transliteration" VALUES(4,99,'4:99','Faola-ika AAasa Allahuan yaAAfuwa AAanhum wakana Allahu AAafuwwan ghafoora');
INSERT INTO "transliteration" VALUES(4,100,'4:100','Waman yuhajir fee sabeeli Allahiyajid fee al-ardi muraghaman katheeran wasaAAatanwaman yakhruj min baytihi muhajiran ila Allahiwarasoolihi thumma yudrik-hu almawtu faqad waqaAAa ajruhu AAalaAllahi wakana Allahu ghafooran raheema');
INSERT INTO "transliteration" VALUES(4,101,'4:101','Wa-itha darabtum fee al-ardifalaysa AAalaykum junahun an taqsuroo mina assalatiin khiftum an yaftinakumu allatheena kafaroo inna alkafireenakanoo lakum AAaduwwan mubeena');
INSERT INTO "transliteration" VALUES(4,102,'4:102','Wa-itha kunta feehim faaqamta lahumuassalata faltaqum ta-ifatun minhummaAAaka walya/khuthoo aslihatahum fa-ithasajadoo falyakoonoo min wara-ikum walta/ti ta-ifatunokhra lam yusalloo falyusalloo maAAakawalya/khuthoo hithrahum waaslihatahumwadda allatheena kafaroo law taghfuloona AAan aslihatikumwaamtiAAatikum fayameeloona AAalaykum maylatan wahidatanwala junaha AAalaykum in kana bikum athanmin matarin aw kuntum marda an tadaAAoo aslihatakumwakhuthoo hithrakum inna AllahaaAAadda lilkafireena AAathaban muheena');
INSERT INTO "transliteration" VALUES(4,103,'4:103','Fa-itha qadaytumu assalatafathkuroo Allaha qiyaman waquAAoodanwaAAala junoobikum fa-itha itma/nantumfaaqeemoo assalata inna assalatakanat AAala almu/mineena kitaban mawqoota');
INSERT INTO "transliteration" VALUES(4,104,'4:104','Wala tahinoo fee ibtigha-ialqawmi in takoonoo ta/lamoona fa-innahum ya/lamoona kamata/lamoona watarjoona mina Allahi ma layarjoona wakana Allahu AAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,105,'4:105','Inna anzalna ilayka alkitababilhaqqi litahkuma bayna annasibima araka Allahu wala takun lilkha-ineenakhaseema');
INSERT INTO "transliteration" VALUES(4,106,'4:106','Wastaghfiri Allaha inna Allahakana ghafooran raheema');
INSERT INTO "transliteration" VALUES(4,107,'4:107','Wala tujadil AAani allatheenayakhtanoona anfusahum inna Allaha la yuhibbuman kana khawwanan atheema');
INSERT INTO "transliteration" VALUES(4,108,'4:108','Yastakhfoona mina annasi walayastakhfoona mina Allahi wahuwa maAAahum ithyubayyitoona ma la yarda mina alqawli wakanaAllahu bima yaAAmaloona muheeta');
INSERT INTO "transliteration" VALUES(4,109,'4:109','Haantum haola-i jadaltumAAanhum fee alhayati addunya famanyujadilu Allaha AAanhum yawma alqiyamati amman yakoonu AAalayhim wakeela');
INSERT INTO "transliteration" VALUES(4,110,'4:110','Waman yaAAmal soo-an aw yathlimnafsahu thumma yastaghfiri Allaha yajidi Allahaghafooran raheema');
INSERT INTO "transliteration" VALUES(4,111,'4:111','Waman yaksib ithman fa-innamayaksibuhu AAala nafsihi wakana AllahuAAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,112,'4:112','Waman yaksib khatee-atan aw ithmanthumma yarmi bihi baree-an faqadi ihtamala buhtananwa-ithman mubeena');
INSERT INTO "transliteration" VALUES(4,113,'4:113','Walawla fadlu AllahiAAalayka warahmatuhu lahammat ta-ifatun minhum anyudillooka wama yudilloona illaanfusahum wama yadurroonaka min shay-in waanzalaAllahu AAalayka alkitaba walhikmatawaAAallamaka ma lam takun taAAlamu wakana fadluAllahi AAalayka AAatheema');
INSERT INTO "transliteration" VALUES(4,114,'4:114','La khayra fee katheerin min najwahumilla man amara bisadaqatin aw maAAroofin aw islahinbayna annasi waman yafAAal thalika ibtighaamardati Allahi fasawfa nu/teehi ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,115,'4:115','Waman yushaqiqi arrasoolamin baAAdi ma tabayyana lahu alhuda wayattabiAAghayra sabeeli almu/mineena nuwallihi ma tawallawanuslihi jahannama wasaat maseera');
INSERT INTO "transliteration" VALUES(4,116,'4:116','Inna Allaha la yaghfiru anyushraka bihi wayaghfiru ma doona thalika limanyashao waman yushrik billahi faqad dalladalalan baAAeeda');
INSERT INTO "transliteration" VALUES(4,117,'4:117','In yadAAoona min doonihi illa inathanwa-in yadAAoona illa shaytanan mareeda');
INSERT INTO "transliteration" VALUES(4,118,'4:118','LaAAanahu Allahu waqalalaattakhithanna min AAibadika naseebanmafrooda');
INSERT INTO "transliteration" VALUES(4,119,'4:119','Walaodillannahum walaomanniyannahumwalaamurannahum falayubattikunna athana al-anAAamiwalaamurannahum falayughayyirunna khalqa Allahiwaman yattakhithi ashshaytana waliyyan mindooni Allahi faqad khasira khusranan mubeena');
INSERT INTO "transliteration" VALUES(4,120,'4:120','YaAAiduhum wayumanneehim wamayaAAiduhumu ashshaytanu illa ghuroora');
INSERT INTO "transliteration" VALUES(4,121,'4:121','Ola-ika ma/wahum jahannamuwala yajidoona AAanha maheesa');
INSERT INTO "transliteration" VALUES(4,122,'4:122','Wallatheena amanoowaAAamiloo assalihati sanudkhiluhum jannatintajree min tahtiha al-anharu khalideenafeeha abadan waAAda Allahi haqqan waman asdaqumina Allahi qeela');
INSERT INTO "transliteration" VALUES(4,123,'4:123','Laysa bi-amaniyyikum wala amaniyyiahli alkitabi man yaAAmal soo-an yujza bihi walayajid lahu min dooni Allahi waliyyan wala naseera');
INSERT INTO "transliteration" VALUES(4,124,'4:124','Waman yaAAmal mina assalihatimin thakarin aw ontha wahuwa mu/minun faola-ikayadkhuloona aljannata wala yuthlamoonanaqeera');
INSERT INTO "transliteration" VALUES(4,125,'4:125','Waman ahsanu deenan mimman aslamawajhahu lillahi wahuwa muhsinun wattabaAAamillata ibraheema haneefan wattakhathaAllahu ibraheema khaleela');
INSERT INTO "transliteration" VALUES(4,126,'4:126','Walillahi ma fee assamawatiwama fee al-ardi wakana Allahubikulli shay-in muheeta');
INSERT INTO "transliteration" VALUES(4,127,'4:127','Wayastaftoonaka fee annisa-iquli Allahu yufteekum feehinna wama yutlaAAalaykum fee alkitabi fee yatama annisa-iallatee la tu/toonahunna ma kutiba lahunnawatarghaboona an tankihoohunna walmustadAAafeenamina alwildani waan taqoomoo lilyatama bilqistiwama tafAAaloo min khayrin fa-inna Allaha kanabihi AAaleema');
INSERT INTO "transliteration" VALUES(4,128,'4:128','Wa-ini imraatun khafat min baAAlihanushoozan aw iAAradan fala junaha AAalayhimaan yusliha baynahuma sulhan wassulhukhayrun waohdirati al-anfusu ashshuhhawa-in tuhsinoo watattaqoo fa-inna Allaha kanabima taAAmaloona khabeera');
INSERT INTO "transliteration" VALUES(4,129,'4:129','Walan tastateeAAoo an taAAdiloobayna annisa-i walaw harastum falatameeloo kulla almayli fatatharooha kalmuAAallaqatiwa-in tuslihoo watattaqoo fa-inna Allaha kanaghafooran raheema');
INSERT INTO "transliteration" VALUES(4,130,'4:130','Wa-in yatafarraqa yughni Allahukullan min saAAatihi wakana Allahu wasiAAan hakeema');
INSERT INTO "transliteration" VALUES(4,131,'4:131','Walillahi ma fee assamawatiwama fee al-ardi walaqad wassaynaallatheena ootoo alkitaba min qablikum wa-iyyakumani ittaqoo Allaha wa-in takfuroo fa-inna lillahi mafee assamawati wama fee al-ardiwakana Allahu ghaniyyan hameeda');
INSERT INTO "transliteration" VALUES(4,132,'4:132','Walillahi ma fee assamawatiwama fee al-ardi wakafa billahiwakeela');
INSERT INTO "transliteration" VALUES(4,133,'4:133','In yasha/ yuthhibkum ayyuha annasuwaya/ti bi-akhareena wakana Allahu AAalathalika qadeera');
INSERT INTO "transliteration" VALUES(4,134,'4:134','Man kana yureedu thawaba addunyafaAAinda Allahi thawabu addunya wal-akhiratiwakana Allahu sameeAAan baseera');
INSERT INTO "transliteration" VALUES(4,135,'4:135','Ya ayyuha allatheena amanookoonoo qawwameena bilqisti shuhadaalillahi walaw AAala anfusikum awi alwalidayniwal-aqrabeena in yakun ghaniyyan aw faqeeran fallahuawla bihima fala tattabiAAoo alhawaan taAAdiloo wa-in talwoo aw tuAAridoo fa-inna Allahakana bima taAAmaloona khabeera');
INSERT INTO "transliteration" VALUES(4,136,'4:136','Ya ayyuha allatheena amanooaminoo billahi warasoolihi walkitabiallathee nazzala AAala rasoolihi walkitabiallathee anzala min qablu waman yakfur billahiwamala-ikatihi wakutubihi warusulihi walyawmi al-akhirifaqad dalla dalalan baAAeeda');
INSERT INTO "transliteration" VALUES(4,137,'4:137','Inna allatheena amanoo thummakafaroo thumma amanoo thumma kafaroo thumma izdadookufran lam yakuni Allahu liyaghfira lahum walaliyahdiyahum sabeela');
INSERT INTO "transliteration" VALUES(4,138,'4:138','Bashshiri almunafiqeena bi-annalahum AAathaban aleema');
INSERT INTO "transliteration" VALUES(4,139,'4:139','Allatheena yattakhithoona alkafireenaawliyaa min dooni almu/mineena ayabtaghoona AAindahumualAAizzata fa-inna alAAizzata lillahi jameeAAa');
INSERT INTO "transliteration" VALUES(4,140,'4:140','Waqad nazzala AAalaykum fee alkitabian itha samiAAtum ayati Allahiyukfaru biha wayustahzao biha fala taqAAudoomaAAahum hatta yakhoodoo fee hadeethinghayrihi innakum ithan mithluhum inna Allaha jamiAAualmunafiqeena walkafireena fee jahannamajameeAAa');
INSERT INTO "transliteration" VALUES(4,141,'4:141','Allatheena yatarabbasoonabikum fa-in kana lakum fathun mina Allahi qalooalam nakun maAAakum wa-in kana lilkafireena naseebunqaloo alam nastahwith AAalaykum wanamnaAAkummina almu/mineena fallahu yahkumu baynakumyawma alqiyamati walan yajAAala Allahu lilkafireenaAAala almu/mineena sabeela');
INSERT INTO "transliteration" VALUES(4,142,'4:142','Inna almunafiqeena yukhadiAAoonaAllaha wahuwa khadiAAuhum wa-itha qamooila assalati qamoo kusalayuraoona annasa wala yathkuroonaAllaha illa qaleela');
INSERT INTO "transliteration" VALUES(4,143,'4:143','Muthabthabeena bayna thalikala ila haola-i wala ilahaola-i waman yudlili Allahu falantajida lahu sabeela');
INSERT INTO "transliteration" VALUES(4,144,'4:144','Ya ayyuha allatheena amanoola tattakhithoo alkafireena awliyaamin dooni almu/mineena atureedoona an tajAAaloo lillahiAAalaykum sultanan mubeena');
INSERT INTO "transliteration" VALUES(4,145,'4:145','Inna almunafiqeena fee addarkial-asfali mina annari walan tajida lahum naseera');
INSERT INTO "transliteration" VALUES(4,146,'4:146','Illa allatheena taboowaaslahoo waAAtasamoo billahiwaakhlasoo deenahum lillahi faola-ika maAAaalmu/mineena wasawfa yu/ti Allahu almu/mineena ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,147,'4:147','Ma yafAAalu Allahu biAAathabikumin shakartum waamantum wakana Allahu shakiranAAaleema');
INSERT INTO "transliteration" VALUES(4,148,'4:148','La yuhibbu Allahualjahra bissoo-i mina alqawli illa man thulimawakana Allahu sameeAAan AAaleema');
INSERT INTO "transliteration" VALUES(4,149,'4:149','In tubdoo khayran aw tukhfoohu aw taAAfooAAan soo-in fa-inna Allaha kana AAafuwwan qadeera');
INSERT INTO "transliteration" VALUES(4,150,'4:150','Inna allatheena yakfuroona billahiwarusulihi wayureedoona an yufarriqoo bayna Allahiwarusulihi wayaqooloona nu/minu bibaAAdin wanakfuru bibaAAdinwayureedoona an yattakhithoo bayna thalika sabeela');
INSERT INTO "transliteration" VALUES(4,151,'4:151','Ola-ika humu alkafiroona haqqanwaaAAtadna lilkafireena AAathaban muheena');
INSERT INTO "transliteration" VALUES(4,152,'4:152','Wallatheena amanoo billahiwarusulihi walam yufarriqoo bayna ahadin minhum ola-ikasawfa yu/teehim ojoorahum wakana Allahu ghafooranraheema');
INSERT INTO "transliteration" VALUES(4,153,'4:153','Yas-aluka ahlu alkitabi an tunazzilaAAalayhim kitaban mina assama-i faqadsaaloo moosa akbara min thalika faqaloo arinaAllaha jahratan faakhathat-humu assaAAiqatubithulmihim thumma ittakhathoo alAAijla minbaAAdi ma jaat-humu albayyinatu faAAafawnaAAan thalika waatayna moosa sultananmubeena');
INSERT INTO "transliteration" VALUES(4,154,'4:154','WarafaAAna fawqahumu attoorabimeethaqihim waqulna lahumu odkhuloo albabasujjadan waqulna lahum la taAAdoo fee assabtiwaakhathna minhum meethaqan ghaleetha');
INSERT INTO "transliteration" VALUES(4,155,'4:155','Fabima naqdihim meethaqahumwakufrihim bi-ayati Allahi waqatlihimual-anbiyaa bighayri haqqin waqawlihim quloobunaghulfun bal tabaAAa Allahu AAalayhabikufrihim fala yu/minoona illa qaleela');
INSERT INTO "transliteration" VALUES(4,156,'4:156','Wabikufrihim waqawlihim AAalamaryama buhtanan AAatheema');
INSERT INTO "transliteration" VALUES(4,157,'4:157','Waqawlihim inna qatalnaalmaseeha AAeesa ibna maryama rasoola Allahiwama qataloohu wama salaboohu walakinshubbiha lahum wa-inna allatheena ikhtalafoo feehi lafeeshakkin minhu ma lahum bihi min AAilmin illa ittibaAAaaththanni wama qataloohu yaqeena');
INSERT INTO "transliteration" VALUES(4,158,'4:158','Bal rafaAAahu Allahu ilayhi wakanaAllahu AAazeezan hakeema');
INSERT INTO "transliteration" VALUES(4,159,'4:159','Wa-in min ahli alkitabi illalayu/minanna bihi qabla mawtihi wayawma alqiyamati yakoonuAAalayhim shaheeda');
INSERT INTO "transliteration" VALUES(4,160,'4:160','Fabithulmin mina allatheenahadoo harramna AAalayhim tayyibatinohillat lahum wabisaddihim AAan sabeeli Allahikatheera');
INSERT INTO "transliteration" VALUES(4,161,'4:161','Waakhthihimu arribawaqad nuhoo AAanhu waaklihim amwala annasibilbatili waaAAtadna lilkafireenaminhum AAathaban aleema');
INSERT INTO "transliteration" VALUES(4,162,'4:162','Lakini arrasikhoonafee alAAilmi minhum walmu/minoona yu/minoona bimaonzila ilayka wama onzila min qablika walmuqeemeenaassalata walmu/toona azzakatawalmu/minoona billahi walyawmi al-akhiriola-ika sanu/teehim ajran AAatheema');
INSERT INTO "transliteration" VALUES(4,163,'4:163','Inna awhayna ilaykakama awhayna ila noohin wannabiyyeenamin baAAdihi waawhayna ila ibraheemawa-ismaAAeela wa-ishaqa wayaAAqooba wal-asbatiwaAAeesa waayyooba wayoonusa waharoona wasulaymanawaatayna dawooda zaboora');
INSERT INTO "transliteration" VALUES(4,164,'4:164','Warusulan qad qasasnahumAAalayka min qablu warusulan lam naqsushum AAalaykawakallama Allahu moosa takleema');
INSERT INTO "transliteration" VALUES(4,165,'4:165','Rusulan mubashshireena wamunthireenali-alla yakoona linnasi AAala Allahihujjatun baAAda arrusuli wakana AllahuAAazeezan hakeema');
INSERT INTO "transliteration" VALUES(4,166,'4:166','Lakini Allahu yashhadu bimaanzala ilayka anzalahu biAAilmihi walmala-ikatuyashhadoona wakafa billahi shaheeda');
INSERT INTO "transliteration" VALUES(4,167,'4:167','Inna allatheena kafaroo wasaddooAAan sabeeli Allahi qad dalloo dalalanbaAAeeda');
INSERT INTO "transliteration" VALUES(4,168,'4:168','Inna allatheena kafaroo wathalamoolam yakuni Allahu liyaghfira lahum walaliyahdiyahum tareeqa');
INSERT INTO "transliteration" VALUES(4,169,'4:169','Illa tareeqa jahannama khalideenafeeha abadan wakana thalika AAala Allahiyaseera');
INSERT INTO "transliteration" VALUES(4,170,'4:170','Ya ayyuha annasuqad jaakumu arrasoolu bilhaqqi minrabbikum faaminoo khayran lakum wa-in takfuroo fa-innalillahi ma fee assamawati wal-ardiwakana Allahu AAaleeman hakeema');
INSERT INTO "transliteration" VALUES(4,171,'4:171','Ya ahla alkitabi lataghloo fee deenikum wala taqooloo AAala Allahiilla alhaqqa innama almaseehu AAeesaibnu maryama rasoolu Allahi wakalimatuhu alqahaila maryama waroohun minhu faaminoo billahiwarusulihi wala taqooloo thalathatun intahookhayran lakum innama Allahu ilahun wahidunsubhanahu an yakoona lahu waladun lahu ma fee assamawatiwama fee al-ardi wakafa billahiwakeela');
INSERT INTO "transliteration" VALUES(4,172,'4:172','Lan yastankifa almaseehu an yakoonaAAabdan lillahi wala almala-ikatualmuqarraboona waman yastankif AAan AAibadatihiwayastakbir fasayahshuruhum ilayhi jameeAAa');
INSERT INTO "transliteration" VALUES(4,173,'4:173','Faamma allatheena amanoowaAAamiloo assalihati fayuwaffeehimojoorahum wayazeeduhum min fadlihi waamma allatheenaistankafoo wastakbaroo fayuAAaththibuhum AAathabanaleeman wala yajidoona lahum min dooni Allahiwaliyyan wala naseera');
INSERT INTO "transliteration" VALUES(4,174,'4:174','Ya ayyuha annasuqad jaakum burhanun min rabbikum waanzalnailaykum nooran mubeena');
INSERT INTO "transliteration" VALUES(4,175,'4:175','Faamma allatheena amanoobillahi waAAtasamoo bihifasayudkhiluhum fee rahmatin minhu wafadlinwayahdeehim ilayhi siratan mustaqeema');
INSERT INTO "transliteration" VALUES(4,176,'4:176','Yastaftoonaka quli Allahu yufteekumfee alkalalati ini imruon halaka laysa lahu waladun walahuokhtun falaha nisfu ma taraka wahuwayarithuha in lam yakun laha waladun fa-in kanataithnatayni falahuma aththuluthani mimmataraka wa-in kanoo ikhwatan rijalan wanisaanfaliththakari mithlu haththialonthayayni yubayyinu Allahu lakum an tadilloo wallahubikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(5,1,'5:1','Ya ayyuha allatheena amanooawfoo bilAAuqoodi ohillat lakum baheematu al-anAAamiilla ma yutla AAalaykum ghayra muhilleeassaydi waantum hurumun inna Allahayahkumu ma yureed');
INSERT INTO "transliteration" VALUES(5,2,'5:2','Ya ayyuha allatheena amanoola tuhilloo shaAAa-ira Allahi walaashshahra alharama wala alhadya walaalqala-ida wala ammeena albayta alharamayabtaghoona fadlan min rabbihim waridwananwa-itha halaltum fastadoo walayajrimannakum shanaanu qawmin an saddookum AAanialmasjidi alharami an taAAtadoo wataAAawanooAAala albirri wattaqwa wala taAAawanooAAala al-ithmi walAAudwani wattaqooAllaha inna Allaha shadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(5,3,'5:3','Hurrimat AAalaykumu almaytatu waddamuwalahmu alkhinzeeri wama ohilla lighayri Allahibihi walmunkhaniqatu walmawqoothatu walmutaraddiyatuwannateehatu wama akala assabuAAuilla ma thakkaytum wama thubihaAAala annusubi waan tastaqsimoo bil-azlamithalikum fisqun alyawma ya-isa allatheena kafaroomin deenikum fala takhshawhum wakhshawni alyawmaakmaltu lakum deenakum waatmamtu AAalaykum niAAmatee waradeetulakumu al-islama deenan famani idturra fee makhmasatinghayra mutajanifin li-ithmin fa-inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(5,4,'5:4','Yas-aloonaka matha ohilla lahumqul ohilla lakumu attayyibatu wamaAAallamtum mina aljawarihi mukallibeenatuAAallimoonahunna mimma AAallamakumu Allahufakuloo mimma amsakna AAalaykum wathkurooisma Allahi AAalayhi wattaqoo Allaha innaAllaha sareeAAu alhisab');
INSERT INTO "transliteration" VALUES(5,5,'5:5','Alyawma ohilla lakumu attayyibatuwataAAamu allatheena ootoo alkitaba hillunlakum wataAAamukum hillun lahum walmuhsanatumina almu/minati walmuhsanatu minaallatheena ootoo alkitaba min qablikum itha ataytumoohunnaojoorahunna muhsineena ghayra musafiheenawala muttakhithee akhdanin waman yakfur bil-eemanifaqad habita AAamaluhu wahuwa fee al-akhiratimina alkhasireen');
INSERT INTO "transliteration" VALUES(5,6,'5:6','Ya ayyuha allatheena amanooitha qumtum ila assalati faghsiloowujoohakum waaydiyakum ila almarafiqi wamsahoobiruoosikum waarjulakum ila alkaAAbayni wa-in kuntumjunuban fattahharoo wa-in kuntum marda awAAala safarin aw jaa ahadun minkum mina algha-itiaw lamastumu annisaa falam tajidoo maanfatayammamoo saAAeedan tayyiban famsahoobiwujoohikum waaydeekum minhu ma yureedu AllahuliyajAAala AAalaykum min harajin walakin yureeduliyutahhirakum waliyutimma niAAmatahu AAalaykumlaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(5,7,'5:7','Wathkuroo niAAmata AllahiAAalaykum wameethaqahu allathee wathaqakumbihi ith qultum samiAAna waataAAna wattaqooAllaha inna Allaha AAaleemun bithati assudoor');
INSERT INTO "transliteration" VALUES(5,8,'5:8','Ya ayyuha allatheena amanookoonoo qawwameena lillahi shuhadaa bilqistiwala yajrimannakum shanaanu qawmin AAala allataAAdiloo iAAdiloo huwa aqrabu littaqwa wattaqooAllaha inna Allaha khabeerun bima taAAmaloon');
INSERT INTO "transliteration" VALUES(5,9,'5:9','WaAAada Allahu allatheena amanoowaAAamiloo assalihati lahum maghfiratunwaajrun AAatheem');
INSERT INTO "transliteration" VALUES(5,10,'5:10','Wallatheena kafaroo wakaththaboobi-ayatina ola-ika as-habualjaheem');
INSERT INTO "transliteration" VALUES(5,11,'5:11','Ya ayyuha allatheena amanooothkuroo niAAmata Allahi AAalaykum ith hammaqawmun an yabsutoo ilaykum aydiyahum fakaffa aydiyahumAAankum wattaqoo Allaha waAAala Allahifalyatawakkali almu/minoon');
INSERT INTO "transliteration" VALUES(5,12,'5:12','Walaqad akhatha Allahu meethaqabanee isra-eela wabaAAathna minhumu ithnay AAasharanaqeeban waqala Allahu innee maAAakum la-inaqamtumu assalata waataytumu azzakatawaamantum birusulee waAAazzartumoohum waaqradtumuAllaha qardan hasanan laokaffiranna AAankumsayyi-atikum walaodkhilannakum jannatin tajree mintahtiha al-anharu faman kafara baAAda thalikaminkum faqad dalla sawaa assabeel');
INSERT INTO "transliteration" VALUES(5,13,'5:13','Fabima naqdihim meethaqahumlaAAannahum wajaAAalna quloobahum qasiyatanyuharrifoona alkalima AAan mawadiAAihi wanasoo haththanmimma thukkiroo bihi wala tazalu tattaliAAuAAala kha-inatin minhum illa qaleelan minhumfaAAfu AAanhum wasfah inna Allahayuhibbu almuhsineen');
INSERT INTO "transliteration" VALUES(5,14,'5:14','Wamina allatheena qaloo innanasara akhathna meethaqahumfanasoo haththan mimma thukkiroobihi faaghrayna baynahumu alAAadawata walbaghdaaila yawmi alqiyamati wasawfa yunabbi-ohumu Allahubima kanoo yasnaAAoon');
INSERT INTO "transliteration" VALUES(5,15,'5:15','Ya ahla alkitabi qad jaakumrasooluna yubayyinu lakum katheeran mimma kuntumtukhfoona mina alkitabi wayaAAfoo AAan katheerin qad jaakummina Allahi noorun wakitabun mubeen');
INSERT INTO "transliteration" VALUES(5,16,'5:16','Yahdee bihi Allahu mani ittabaAAa ridwanahusubula assalami wayukhrijuhum mina aththulumatiila annoori bi-ithnihi wayahdeehim ilasiratin mustaqeem');
INSERT INTO "transliteration" VALUES(5,17,'5:17','Laqad kafara allatheena qalooinna Allaha huwa almaseehu ibnu maryama qul famanyamliku mina Allahi shay-an in arada an yuhlikaalmaseeha ibna maryama waommahu waman fee al-ardijameeAAan walillahi mulku assamawatiwal-ardi wama baynahuma yakhluqu mayashao wallahu AAala kulli shay-inqadeer');
INSERT INTO "transliteration" VALUES(5,18,'5:18','Waqalati alyahoodu wannasaranahnu abnao Allahi waahibbaohuqul falima yuAAaththibukum bithunoobikum bal antumbasharun mimman khalaqa yaghfiru liman yashao wayuAAaththibuman yashao walillahi mulku assamawatiwal-ardi wama baynahuma wa-ilayhialmaseer');
INSERT INTO "transliteration" VALUES(5,19,'5:19','Ya ahla alkitabi qad jaakumrasooluna yubayyinu lakum AAala fatratin mina arrusulian taqooloo ma jaana min basheerin walanatheerin faqad jaakum basheerun wanatheerunwallahu AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(5,20,'5:20','Wa-ith qala moosaliqawmihi ya qawmi othkuroo niAAmata AllahiAAalaykum ith jaAAala feekum anbiyaa wajaAAalakummulookan waatakum ma lam yu/ti ahadanmina alAAalameen');
INSERT INTO "transliteration" VALUES(5,21,'5:21','Ya qawmi odkhuloo al-ardaalmuqaddasata allatee kataba Allahu lakum walatartaddoo AAala adbarikum fatanqaliboo khasireen');
INSERT INTO "transliteration" VALUES(5,22,'5:22','Qaloo ya moosa innafeeha qawman jabbareena wa-inna lannadkhulaha hatta yakhrujoo minhafa-in yakhrujoo minha fa-inna dakhiloon');
INSERT INTO "transliteration" VALUES(5,23,'5:23','Qala rajulani mina allatheenayakhafoona anAAama Allahu AAalayhimaodkhuloo AAalayhimu albaba fa-itha dakhaltumoohufa-innakum ghaliboona waAAala Allahifatawakkaloo in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(5,24,'5:24','Qaloo ya moosa innalan nadkhulaha abadan ma damoo feehafa-ithhab anta warabbuka faqatila innahahuna qaAAidoon');
INSERT INTO "transliteration" VALUES(5,25,'5:25','Qala rabbi innee la amliku illanafsee waakhee fafruq baynana wabayna alqawmi alfasiqeen');
INSERT INTO "transliteration" VALUES(5,26,'5:26','Qala fa-innaha muharramatunAAalayhim arbaAAeena sanatan yateehoona fee al-ardi falata/sa AAala alqawmi alfasiqeen');
INSERT INTO "transliteration" VALUES(5,27,'5:27','Watlu AAalayhim nabaa ibnay adamabilhaqqi ith qarraba qurbananfatuqubbila min ahadihima walam yutaqabbal mina al-akhariqala laaqtulannaka qala innama yataqabbaluAllahu mina almuttaqeen');
INSERT INTO "transliteration" VALUES(5,28,'5:28','La-in basatta ilayya yadakalitaqtulanee ma ana bibasitin yadiyailayka li-aqtulaka innee akhafu Allaha rabba alAAalameen');
INSERT INTO "transliteration" VALUES(5,29,'5:29','Innee oreedu an taboo-a bi-ithmee wa-ithmikafatakoona min as-habi annari wathalikajazao aththalimeen');
INSERT INTO "transliteration" VALUES(5,30,'5:30','FatawwaAAat lahu nafsuhu qatlaakheehi faqatalahu faasbaha mina alkhasireen');
INSERT INTO "transliteration" VALUES(5,31,'5:31','FabaAAatha Allahu ghuraban yabhathufee al-ardi liyuriyahu kayfa yuwaree saw-ataakheehi qala ya waylata aAAajaztu an akoonamithla hatha alghurabi faowariya saw-ataakhee faasbaha mina annadimeen');
INSERT INTO "transliteration" VALUES(5,32,'5:32','Min ajli thalika katabna AAalabanee isra-eela annahu man qatala nafsan bighayri nafsinaw fasadin fee al-ardi fakaannama qatala annasajameeAAan waman ahyaha fakaannama ahyaannasa jameeAAan walaqad jaat-hum rusulunabilbayyinati thumma inna katheeran minhum baAAda thalikafee al-ardi lamusrifoon');
INSERT INTO "transliteration" VALUES(5,33,'5:33','Innama jazao allatheenayuhariboona Allaha warasoolahu wayasAAawna feeal-ardi fasadan an yuqattaloo aw yusallabooaw tuqattaAAa aydeehim waarjuluhum min khilafin awyunfaw mina al-ardi thalika lahum khizyun fee addunyawalahum fee al-akhirati AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(5,34,'5:34','Illa allatheena taboomin qabli an taqdiroo AAalayhim faAAlamoo anna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(5,35,'5:35','Ya ayyuha allatheena amanooittaqoo Allaha wabtaghoo ilayhi alwaseelata wajahidoofee sabeelihi laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(5,36,'5:36','Inna allatheena kafaroo law annalahum ma fee al-ardi jameeAAan wamithlahu maAAahuliyaftadoo bihi min AAathabi yawmi alqiyamati matuqubbila minhum walahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(5,37,'5:37','Yureedoona an yakhrujoo mina annariwama hum bikharijeena minha walahum AAathabunmuqeem');
INSERT INTO "transliteration" VALUES(5,38,'5:38','Wassariqu wassariqatufaqtaAAoo aydiyahuma jazaan bimakasaba nakalan mina Allahi wallahuAAazeezun hakeem');
INSERT INTO "transliteration" VALUES(5,39,'5:39','Faman taba min baAAdi thulmihiwaaslaha fa-inna Allaha yatoobu AAalayhiinna Allaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(5,40,'5:40','Alam taAAlam anna Allaha lahu mulku assamawatiwal-ardi yuAAaththibu man yashaowayaghfiru liman yashao wallahu AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(5,41,'5:41','Ya ayyuha arrasoolu layahzunka allatheena yusariAAoona fee alkufrimina allatheena qaloo amanna bi-afwahihimwalam tu/min quloobuhum wamina allatheena hadoosammaAAoona lilkathibi sammaAAoona liqawmin akhareenalam ya/tooka yuharrifoona alkalima min baAAdi mawadiAAihiyaqooloona in ooteetum hatha fakhuthoohu wa-in lamtu/tawhu fahtharoo waman yuridi Allahufitnatahu falan tamlika lahu mina Allahi shay-an ola-ikaallatheena lam yuridi Allahu an yutahhiraquloobahum lahum fee addunya khizyun walahum feeal-akhirati AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(5,42,'5:42','SammaAAoona lilkathibi akkaloonalissuhti fa-in jaooka fahkumbaynahum aw aAArid AAanhum wa-in tuAArid AAanhumfalan yadurrooka shay-an wa-in hakamta fahkumbaynahum bilqisti inna Allaha yuhibbualmuqsiteen');
INSERT INTO "transliteration" VALUES(5,43,'5:43','Wakayfa yuhakkimoonaka waAAindahumu attawratufeeha hukmu Allahi thumma yatawallawna minbaAAdi thalika wama ola-ika bilmu/mineen');
INSERT INTO "transliteration" VALUES(5,44,'5:44','Inna anzalna attawratafeeha hudan wanoorun yahkumu biha annabiyyoonaallatheena aslamoo lillatheena hadoo warrabbaniyyoonawal-ahbaru bima istuhfithoomin kitabi Allahi wakanoo AAalayhi shuhadaafala takhshawoo annasa wakhshawniwala tashtaroo bi-ayatee thamanan qaleelanwaman lam yahkum bima anzala Allahu faola-ikahumu alkafiroon');
INSERT INTO "transliteration" VALUES(5,45,'5:45','Wakatabna AAalayhim feeha annaannafsa binnafsi walAAayna bilAAayniwal-anfa bil-anfi walothuna bilothuniwassinna bissinni waljurooha qisasunfaman tasaddaqa bihi fahuwa kaffaratun lahu wamanlam yahkum bima anzala Allahu faola-ikahumu aththalimoon');
INSERT INTO "transliteration" VALUES(5,46,'5:46','Waqaffayna AAala atharihimbiAAeesa ibni maryama musaddiqan lima baynayadayhi mina attawrati waataynahual-injeela feehi hudan wanoorun wamusaddiqan limabayna yadayhi mina attawrati wahudan wamawAAithatanlilmuttaqeen');
INSERT INTO "transliteration" VALUES(5,47,'5:47','Walyahkum ahlu al-injeeli bimaanzala Allahu feehi waman lam yahkum bimaanzala Allahu faola-ika humu alfasiqoon');
INSERT INTO "transliteration" VALUES(5,48,'5:48','Waanzalna ilayka alkitaba bilhaqqimusaddiqan lima bayna yadayhi mina alkitabiwamuhayminan AAalayhi fahkum baynahum bimaanzala Allahu wala tattabiAA ahwaahum AAammajaaka mina alhaqqi likullin jaAAalna minkumshirAAatan waminhajan walaw shaa AllahulajaAAalakum ommatan wahidatan walakin liyabluwakumfeema atakum fastabiqoo alkhayratiila Allahi marjiAAukum jameeAAan fayunabbi-okum bimakuntum feehi takhtalifoon');
INSERT INTO "transliteration" VALUES(5,49,'5:49','Waani ohkum baynahum bimaanzala Allahu wala tattabiAA ahwaahum wahtharhuman yaftinooka AAan baAAdi ma anzala Allahuilayka fa-in tawallaw faAAlam annama yureedu Allahuan yuseebahum bibaAAdi thunoobihim wa-innakatheeran mina annasi lafasiqoon');
INSERT INTO "transliteration" VALUES(5,50,'5:50','Afahukma aljahiliyyatiyabghoona waman ahsanu mina Allahu hukmanliqawmin yooqinoon');
INSERT INTO "transliteration" VALUES(5,51,'5:51','Ya ayyuha allatheena amanoola tattakhithoo alyahooda wannasaraawliyaa baAAduhum awliyao baAAdinwaman yatawallahum minkum fa-innahu minhum inna Allaha layahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(5,52,'5:52','Fatara allatheena feequloobihim maradun yusariAAoona feehim yaqooloonanakhsha an tuseebana da-iratun faAAasaAllahu an ya/tiya bilfathi aw amrin minAAindihi fayusbihoo AAala ma asarroofee anfusihim nadimeen');
INSERT INTO "transliteration" VALUES(5,53,'5:53','Wayaqoolu allatheena amanoo ahaola-iallatheena aqsamoo billahi jahda aymanihiminnahum lamaAAakum habitat aAAmaluhum faasbahookhasireen');
INSERT INTO "transliteration" VALUES(5,54,'5:54','Ya ayyuha allatheena amanooman yartadda minkum AAan deenihi fasawfa ya/tee Allahubiqawmin yuhibbuhum wayuhibboonahu athillatinAAala almu/mineena aAAizzatin AAala alkafireenayujahidoona fee sabeeli Allahi wala yakhafoonalawmata la-imin thalika fadlu Allahiyu/teehi man yashao wallahu wasiAAunAAaleem');
INSERT INTO "transliteration" VALUES(5,55,'5:55','Innama waliyyukumu Allahuwarasooluhu wallatheena amanoo allatheenayuqeemoona assalata wayu/toona azzakatawahum rakiAAoon');
INSERT INTO "transliteration" VALUES(5,56,'5:56','Waman yatawalla Allaha warasoolahu wallatheenaamanoo fa-inna hizba Allahi humu alghaliboon');
INSERT INTO "transliteration" VALUES(5,57,'5:57','Ya ayyuha allatheena amanoola tattakhithoo allatheena ittakhathoodeenakum huzuwan walaAAiban mina allatheena ootoo alkitabamin qablikum walkuffara awliyaa wattaqooAllaha in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(5,58,'5:58','Wa-itha nadaytum ila assalatiittakhathooha huzuwan walaAAiban thalikabi-annahum qawmun la yaAAqiloon');
INSERT INTO "transliteration" VALUES(5,59,'5:59','Qul ya ahla alkitabi haltanqimoona minna illa an amanna billahiwama onzila ilayna wama onzila min qabluwaanna aktharakum fasiqoon');
INSERT INTO "transliteration" VALUES(5,60,'5:60','Qul hal onabbi-okum bisharrin min thalikamathoobatan AAinda Allahi man laAAanahu Allahuwaghadiba AAalayhi wajaAAala minhumu alqiradata walkhanazeerawaAAabada attaghooti ola-ika sharrun makananwaadallu AAan sawa-i assabeel');
INSERT INTO "transliteration" VALUES(5,61,'5:61','Wa-itha jaookum qaloo amannawaqad dakhaloo bilkufri wahum qad kharajoo bihi wallahuaAAlamu bima kanoo yaktumoon');
INSERT INTO "transliteration" VALUES(5,62,'5:62','Watara katheeran minhum yusariAAoonafee al-ithmi walAAudwani waaklihimu assuhtalabi/sa ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(5,63,'5:63','Lawla yanhahumu arrabbaniyyoonawal-ahbaru AAan qawlihimu al-ithmawaaklihimu assuhta labi/sa ma kanooyasnaAAoon');
INSERT INTO "transliteration" VALUES(5,64,'5:64','Waqalati alyahoodu yadu Allahimaghloolatun ghullat aydeehim waluAAinoo bima qaloobal yadahu mabsootatani yunfiqu kayfa yashaowalayazeedanna katheeran minhum ma onzila ilayka minrabbika tughyanan wakufran waalqaynabaynahumu alAAadawata walbaghdaa ilayawmi alqiyamati kullama awqadoo naran lilharbiatfaaha Allahu wayasAAawna fee al-ardifasadan wallahu la yuhibbualmufsideen');
INSERT INTO "transliteration" VALUES(5,65,'5:65','Walaw anna ahla alkitabi amanoowattaqaw lakaffarna AAanhum sayyi-atihimwalaadkhalnahum jannati annaAAeem');
INSERT INTO "transliteration" VALUES(5,66,'5:66','Walaw annahum aqamoo attawratawal-injeela wama onzila ilayhim min rabbihimlaakaloo min fawqihim wamin tahti arjulihim minhum ommatunmuqtasidatun wakatheerun minhum saa mayaAAmaloon');
INSERT INTO "transliteration" VALUES(5,67,'5:67','Ya ayyuha arrasooluballigh ma onzila ilayka min rabbika wa-in lam tafAAal famaballaghta risalatahu wallahu yaAAsimukamina annasi inna Allaha la yahdeealqawma alkafireen');
INSERT INTO "transliteration" VALUES(5,68,'5:68','Qul ya ahla alkitabi lastumAAala shay-in hatta tuqeemoo attawratawal-injeela wama onzila ilaykum min rabbikumwalayazeedanna katheeran minhum ma onzila ilayka minrabbika tughyanan wakufran fala ta/sa AAalaalqawmi alkafireen');
INSERT INTO "transliteration" VALUES(5,69,'5:69','Inna allatheena amanoo wallatheenahadoo wassabi-oona wannasaraman amana billahi walyawmi al-akhiriwaAAamila salihan fala khawfun AAalayhim walahum yahzanoon');
INSERT INTO "transliteration" VALUES(5,70,'5:70','Laqad akhathna meethaqabanee isra-eela waarsalna ilayhim rusulan kullamajaahum rasoolun bima la tahwaanfusuhum fareeqan kaththaboo wafareeqan yaqtuloon');
INSERT INTO "transliteration" VALUES(5,71,'5:71','Wahasiboo alla takoonafitnatun faAAamoo wasammoo thumma taba AllahuAAalayhim thumma AAamoo wasammoo katheerun minhum wallahubaseerun bima yaAAmaloon');
INSERT INTO "transliteration" VALUES(5,72,'5:72','Laqad kafara allatheena qalooinna Allaha huwa almaseehu ibnu maryama waqalaalmaseehu ya banee isra-eela oAAbudoo Allaharabbee warabbakum innahu man yushrik billahi faqadharrama Allahu AAalayhi aljannata wama/wahuannaru wama liththalimeenamin ansar');
INSERT INTO "transliteration" VALUES(5,73,'5:73','Laqad kafara allatheena qalooinna Allaha thalithu thalathatin wamamin ilahin illa ilahun wahidun wa-inlam yantahoo AAamma yaqooloona layamassanna allatheenakafaroo minhum AAathabun aleem');
INSERT INTO "transliteration" VALUES(5,74,'5:74','Afala yatooboona ila Allahiwayastaghfiroonahu wallahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(5,75,'5:75','Ma almaseehu ibnu maryama illarasoolun qad khalat min qablihi arrusulu waommuhu siddeeqatunkana ya/kulani attaAAamaonthur kayfa nubayyinu lahumu al-ayatithumma onthur anna yu/fakoon');
INSERT INTO "transliteration" VALUES(5,76,'5:76','Qul ataAAbudoona min dooni Allahi mala yamliku lakum darran wala nafAAan wallahuhuwa assameeAAu alAAaleem');
INSERT INTO "transliteration" VALUES(5,77,'5:77','Qul ya ahla alkitabi lataghloo fee deenikum ghayra alhaqqi wala tattabiAAooahwaa qawmin qad dalloo min qablu waadallookatheeran wadalloo AAan sawa-i assabeel');
INSERT INTO "transliteration" VALUES(5,78,'5:78','LuAAina allatheena kafaroo min baneeisra-eela AAala lisani dawoodawaAAeesa ibni maryama thalika bima AAasawwakanoo yaAAtadoon');
INSERT INTO "transliteration" VALUES(5,79,'5:79','Kanoo la yatanahawnaAAan munkarin faAAaloohu labi/sa ma kanooyafAAaloon');
INSERT INTO "transliteration" VALUES(5,80,'5:80','Tara katheeran minhum yatawallawnaallatheena kafaroo labi/sa ma qaddamat lahumanfusuhum an sakhita Allahu AAalayhim wafee alAAathabihum khalidoon');
INSERT INTO "transliteration" VALUES(5,81,'5:81','Walaw kanoo yu/minoona billahiwannabiyyi wama onzila ilayhi ma ittakhathoohumawliyaa walakinna katheeran minhum fasiqoon');
INSERT INTO "transliteration" VALUES(5,82,'5:82','Latajidanna ashadda annasiAAadawatan lillatheena amanoo alyahooda wallatheenaashrakoo walatajidanna aqrabahum mawaddatan lillatheena amanooallatheena qaloo inna nasara thalikabi-anna minhum qisseeseena waruhbanan waannahum layastakbiroon');
INSERT INTO "transliteration" VALUES(5,83,'5:83','Wa-itha samiAAoo ma onzila ilaarrasooli tara aAAyunahum tafeedu mina addamAAimimma AAarafoo mina alhaqqi yaqooloona rabbanaamanna faktubna maAAa ashshahideen');
INSERT INTO "transliteration" VALUES(5,84,'5:84','Wama lana la nu/minu billahiwama jaana mina alhaqqi wanatmaAAuan yudkhilana rabbuna maAAa alqawmi assaliheen');
INSERT INTO "transliteration" VALUES(5,85,'5:85','Faathabahumu Allahu bimaqaloo jannatin tajree min tahtihaal-anharu khalideena feeha wathalikajazao almuhsineen');
INSERT INTO "transliteration" VALUES(5,86,'5:86','Wallatheena kafaroo wakaththaboobi-ayatina ola-ika as-habualjaheem');
INSERT INTO "transliteration" VALUES(5,87,'5:87','Ya ayyuha allatheena amanoola tuharrimoo tayyibati ma ahallaAllahu lakum wala taAAtadoo inna Allaha layuhibbu almuAAtadeen');
INSERT INTO "transliteration" VALUES(5,88,'5:88','Wakuloo mimma razaqakumu Allahuhalalan tayyiban wattaqoo Allahaallathee antum bihi mu/minoon');
INSERT INTO "transliteration" VALUES(5,89,'5:89','La yu-akhithukumu Allahubillaghwi fee aymanikum walakin yu-akhithukumbima AAaqqadtumu al-aymana fakaffaratuhu itAAamuAAasharati masakeena min awsati ma tutAAimoonaahleekum aw kiswatuhum aw tahreeru raqabatin faman lamyajid fasiyamu thalathati ayyamin thalikakaffaratu aymanikum itha halaftum wahfathooaymanakum kathalika yubayyinu Allahu lakum ayatihilaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(5,90,'5:90','Ya ayyuha allatheena amanooinnama alkhamru walmaysiru wal-ansabuwal-azlamu rijsun min AAamali ashshaytanifajtaniboohu laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(5,91,'5:91','Innama yureedu ashshaytanuan yooqiAAa baynakumu alAAadawata walbaghdaafee alkhamri walmaysiri wayasuddakum AAan thikriAllahi waAAani assalati fahal antummuntahoon');
INSERT INTO "transliteration" VALUES(5,92,'5:92','WaateeAAoo Allaha waateeAAooarrasoola wahtharoo fa-in tawallaytum faAAlamooannama AAala rasoolina albalaghualmubeen');
INSERT INTO "transliteration" VALUES(5,93,'5:93','Laysa AAala allatheena amanoowaAAamiloo assalihati junahun feemataAAimoo itha ma ittaqaw waamanoowaAAamiloo assalihati thumma ittaqaw waamanoothumma ittaqaw waahsanoo wallahu yuhibbualmuhsineen');
INSERT INTO "transliteration" VALUES(5,94,'5:94','Ya ayyuha allatheena amanoolayabluwannakumu Allahu bishay-in mina assayditanaluhu aydeekum warimahukum liyaAAlama Allahuman yakhafuhu bilghaybi famani iAAtadabaAAda thalika falahu AAathabun aleem');
INSERT INTO "transliteration" VALUES(5,95,'5:95','Ya ayyuha allatheena amanoola taqtuloo assayda waantum hurumunwaman qatalahu minkum mutaAAammidan fajazaon mithlu maqatala mina annaAAami yahkumu bihi thawaAAadlin minkum hadyan baligha alkaAAbati aw kaffaratuntaAAamu masakeena aw AAadlu thalika siyamanliyathooqa wabala amrihi AAafa AllahuAAamma salafa waman AAada fayantaqimu Allahuminhu wallahu AAazeezun thoo intiqam');
INSERT INTO "transliteration" VALUES(5,96,'5:96','Ohilla lakum saydu albahriwataAAamuhu mataAAan lakum walissayyaratiwahurrima AAalaykum saydu albarri ma dumtum hurumanwattaqoo Allaha allathee ilayhi tuhsharoon');
INSERT INTO "transliteration" VALUES(5,97,'5:97','JaAAala Allahu alkaAAbata albayta alharamaqiyaman linnasi washshahra alharamawalhadya walqala-ida thalikalitaAAlamoo anna Allaha yaAAlamu ma fee assamawatiwama fee al-ardi waanna Allaha bikullishay-in AAaleem');
INSERT INTO "transliteration" VALUES(5,98,'5:98','IAAlamoo anna Allaha shadeedu alAAiqabiwaanna Allaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(5,99,'5:99','Ma AAala arrasooli illaalbalaghu wallahu yaAAlamu matubdoona wama taktumoon');
INSERT INTO "transliteration" VALUES(5,100,'5:100','Qul la yastawee alkhabeethu wattayyibuwalaw aAAjabaka kathratu alkhabeethi fattaqoo Allahaya olee al-albabi laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(5,101,'5:101','Ya ayyuha allatheena amanoola tas-aloo AAan ashyaa in tubda lakum tasu/kumwa-in tas-aloo AAanha heena yunazzalu alqur-anutubda lakum AAafa Allahu AAanha wallahughafoorun haleem');
INSERT INTO "transliteration" VALUES(5,102,'5:102','Qad saalaha qawmun min qablikumthumma asbahoo biha kafireen');
INSERT INTO "transliteration" VALUES(5,103,'5:103','Ma jaAAala Allahu min baheeratinwala sa-ibatin wala waseelatin walahamin walakinna allatheena kafarooyaftaroona AAala Allahi alkathibawaaktharuhum la yaAAqiloon');
INSERT INTO "transliteration" VALUES(5,104,'5:104','Wa-itha qeela lahum taAAalawila ma anzala Allahu wa-ila arrasooliqaloo hasbuna ma wajadnaAAalayhi abaana awa law kana abaohumla yaAAlamoona shay-an wala yahtadoon');
INSERT INTO "transliteration" VALUES(5,105,'5:105','Ya ayyuha allatheena amanooAAalaykum anfusakum la yadurrukum man dallaitha ihtadaytum ila Allahi marjiAAukumjameeAAan fayunabbi-okum bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(5,106,'5:106','Ya ayyuha allatheena amanooshahadatu baynikum itha hadara ahadakumualmawtu heena alwasiyyati ithnani thawaAAadlin minkum aw akharani min ghayrikum in antum darabtumfee al-ardi faasabatkum museebatu almawti tahbisoonahumamin baAAdi assalati fayuqsimani billahiini irtabtum la nashtaree bihi thamanan walaw kana thaqurba wala naktumu shahadata Allahiinna ithan lamina al-athimeen');
INSERT INTO "transliteration" VALUES(5,107,'5:107','Fa-in AAuthira AAala annahumaistahaqqa ithman faakharani yaqoomanimaqamahuma mina allatheena istahaqqaAAalayhimu al-awlayani fayuqsimani billahilashahadatuna ahaqqu min shahadatihimawama iAAtadayna inna ithan lamina aththalimeen');
INSERT INTO "transliteration" VALUES(5,108,'5:108','Thalika adna an ya/too bishshahadatiAAala wajhiha aw yakhafoo an turadda aymanunbaAAda aymanihim wattaqoo Allaha wasmaAAoowallahu la yahdee alqawma alfasiqeen');
INSERT INTO "transliteration" VALUES(5,109,'5:109','Yawma yajmaAAu Allahu arrusulafayaqoolu matha ojibtum qaloo la AAilma lanainnaka anta AAallamu alghuyoob');
INSERT INTO "transliteration" VALUES(5,110,'5:110','Ith qala Allahu yaAAeesa ibna maryama othkur niAAmatee AAalaykawaAAala walidatika ith ayyadtuka biroohialqudusi tukallimu annasa fee almahdi wakahlanwa-ith AAallamtuka alkitaba walhikmatawattawrata wal-injeela wa-ithtakhluqu mina atteeni kahay-ati attayribi-ithnee fatanfukhu feeha fatakoonu tayranbi-ithnee watubri-o al-akmaha wal-abrasabi-ithnee wa-ith tukhriju almawta bi-ithneewa-ith kafaftu banee isra-eela AAanka ithji/tahum bilbayyinati faqala allatheenakafaroo minhum in hatha illa sihrun mubeen');
INSERT INTO "transliteration" VALUES(5,111,'5:111','Wa-ith awhaytu ila alhawariyyeenaan aminoo bee wabirasoolee qaloo amannawashhad bi-annana muslimoon');
INSERT INTO "transliteration" VALUES(5,112,'5:112','Ith qala alhawariyyoonaya AAeesa ibna maryama hal yastateeAAurabbuka an yunazzila AAalayna ma-idatan mina assama-iqala ittaqoo Allaha in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(5,113,'5:113','Qaloo nureedu an na/kula minhawatatma-inna quloobuna wanaAAlama an qad sadaqtanawanakoona AAalayha mina ashshahideen');
INSERT INTO "transliteration" VALUES(5,114,'5:114','Qala AAeesa ibnu maryama allahummarabbana anzil AAalayna ma-idatan mina assama-itakoonu lana AAeedan li-awwalina waakhirinawaayatan minka warzuqna waanta khayru arraziqeen');
INSERT INTO "transliteration" VALUES(5,115,'5:115','Qala Allahu innee munazziluhaAAalaykum faman yakfur baAAdu minkum fa-innee oAAaththibuhuAAathaban la oAAaththibuhu ahadanmina alAAalameen');
INSERT INTO "transliteration" VALUES(5,116,'5:116','Wa-ith qala Allahu yaAAeesa ibna maryama aanta qulta linnasiittakhithoonee waommiya ilahayni min dooni Allahiqala subhanaka ma yakoonu lee an aqoola malaysa lee bihaqqin in kuntu qultuhu faqad AAalimtahutaAAlamu ma fee nafsee wala aAAlamu ma feenafsika innaka anta AAallamu alghuyoob');
INSERT INTO "transliteration" VALUES(5,117,'5:117','Ma qultu lahum illa maamartanee bihi ani oAAbudoo Allaha rabbee warabbakumwakuntu AAalayhim shaheedan ma dumtu feehim falammatawaffaytanee kunta anta arraqeeba AAalayhim waanta AAalakulli shay-in shaheed');
INSERT INTO "transliteration" VALUES(5,118,'5:118','In tuAAaththibhum fa-innahum AAibadukawa-in taghfir lahum fa-innaka anta alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(5,119,'5:119','Qala Allahu hathayawmu yanfaAAu assadiqeena sidquhum lahumjannatun tajree min tahtiha al-anharukhalideena feeha abadan radiya AllahuAAanhum waradoo AAanhu thalika alfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(5,120,'5:120','Lillahi mulku assamawatiwal-ardi wama feehinna wahuwa AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(6,1,'6:1','Alhamdu lillahi allatheekhalaqa assamawati wal-ardawajaAAala aththulumati wannoorathumma allatheena kafaroo birabbihim yaAAdiloon');
INSERT INTO "transliteration" VALUES(6,2,'6:2','Huwa allathee khalaqakum min teeninthumma qada ajalan waajalun musamman AAindahu thumma antumtamtaroon');
INSERT INTO "transliteration" VALUES(6,3,'6:3','Wahuwa Allahu fee assamawatiwafee al-ardi yaAAlamu sirrakum wajahrakum wayaAAlamu mataksiboon');
INSERT INTO "transliteration" VALUES(6,4,'6:4','Wama ta/teehim min ayatin min ayatirabbihim illa kanoo AAanha muAArideen');
INSERT INTO "transliteration" VALUES(6,5,'6:5','Faqad kaththaboo bilhaqqilamma jaahum fasawfa ya/teehim anbao makanoo bihi yastahzi-oon');
INSERT INTO "transliteration" VALUES(6,6,'6:6','Alam yaraw kam ahlakna min qablihimmin qarnin makkannahum fee al-ardi ma lamnumakkin lakum waarsalna assamaa AAalayhimmidraran wajaAAalna al-anhara tajree min tahtihimfaahlaknahum bithunoobihim waansha/na minbaAAdihim qarnan akhareen');
INSERT INTO "transliteration" VALUES(6,7,'6:7','Walaw nazzalna AAalayka kitabanfee qirtasin falamasoohu bi-aydeehim laqala allatheenakafaroo in hatha illa sihrun mubeen');
INSERT INTO "transliteration" VALUES(6,8,'6:8','Waqaloo lawla onzila AAalayhimalakun walaw anzalna malakan laqudiya al-amruthumma la yuntharoon');
INSERT INTO "transliteration" VALUES(6,9,'6:9','Walaw jaAAalnahu malakan lajaAAalnahurajulan walalabasna AAalayhim ma yalbisoon');
INSERT INTO "transliteration" VALUES(6,10,'6:10','Walaqadi istuhzi-a birusulin min qablika fahaqabillatheena sakhiroo minhum ma kanoobihi yastahzi-oon');
INSERT INTO "transliteration" VALUES(6,11,'6:11','Qul seeroo fee al-ardi thumma onthurookayfa kana AAaqibatu almukaththibeen');
INSERT INTO "transliteration" VALUES(6,12,'6:12','Qul liman ma fee assamawatiwal-ardi qul lillahi kataba AAalanafsihi arrahmata layajmaAAannakum ilayawmi alqiyamati la rayba feehi allatheenakhasiroo anfusahum fahum la yu/minoon');
INSERT INTO "transliteration" VALUES(6,13,'6:13','Walahu ma sakana fee allayli wannahariwahuwa assameeAAu alAAaleem');
INSERT INTO "transliteration" VALUES(6,14,'6:14','Qul aghayra Allahi attakhithuwaliyyan fatiri assamawati wal-ardiwahuwa yutAAimu wala yutAAamu qul inneeomirtu an akoona awwala man aslama wala takoonanna minaalmushrikeen');
INSERT INTO "transliteration" VALUES(6,15,'6:15','Qul innee akhafu in AAasayturabbee AAathaba yawmin AAatheem');
INSERT INTO "transliteration" VALUES(6,16,'6:16','Man yusraf AAanhu yawma-ithinfaqad rahimahu wathalika alfawzu almubeen');
INSERT INTO "transliteration" VALUES(6,17,'6:17','Wa-in yamsaska Allahu bidurrinfala kashifa lahu illa huwa wa-in yamsaskabikhayrin fahuwa AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(6,18,'6:18','Wahuwa alqahiru fawqa AAibadihiwahuwa alhakeemu alkhabeer');
INSERT INTO "transliteration" VALUES(6,19,'6:19','Qul ayyu shay-in akbaru shahadatanquli Allahu shaheedun baynee wabaynakum waoohiyailayya hatha alqur-anu li-onthirakum bihiwaman balagha a-innakum latashhadoona anna maAAa Allahi alihatanokhra qul la ashhadu qul innama huwa ilahunwahidun wa-innanee baree-on mimma tushrikoon');
INSERT INTO "transliteration" VALUES(6,20,'6:20','Allatheena ataynahumualkitaba yaAArifoonahu kama yaAArifoona abnaahumallatheena khasiroo anfusahum fahum la yu/minoon');
INSERT INTO "transliteration" VALUES(6,21,'6:21','Waman athlamu mimmani iftaraAAala Allahi kathiban aw kaththababi-ayatihi innahu la yuflihu aththalimoon');
INSERT INTO "transliteration" VALUES(6,22,'6:22','Wayawma nahshuruhum jameeAAan thummanaqoolu lillatheena ashrakoo ayna shurakaokumu allatheenakuntum tazAAumoon');
INSERT INTO "transliteration" VALUES(6,23,'6:23','Thumma lam takun fitnatuhum illa an qaloowallahi rabbina ma kunnamushrikeen');
INSERT INTO "transliteration" VALUES(6,24,'6:24','Onthur kayfa kathabooAAala anfusihim wadalla AAanhum ma kanooyaftaroon');
INSERT INTO "transliteration" VALUES(6,25,'6:25','Waminhum man yastamiAAu ilayka wajaAAalnaAAala quloobihim akinnatan an yafqahoohu wafee athanihimwaqran wa-in yaraw kulla ayatin la yu/minoo bihahatta itha jaooka yujadiloonakayaqoolu allatheena kafaroo in hatha illa asateerual-awwaleen');
INSERT INTO "transliteration" VALUES(6,26,'6:26','Wahum yanhawna AAanhu wayan-awna AAanhuwa-in yuhlikoona illa anfusahum wama yashAAuroon');
INSERT INTO "transliteration" VALUES(6,27,'6:27','Walaw tara ith wuqifoo AAalaannari faqaloo ya laytananuraddu wala nukaththiba bi-ayatirabbina wanakoona mina almu/mineen');
INSERT INTO "transliteration" VALUES(6,28,'6:28','Bal bada lahum ma kanooyukhfoona min qablu walaw ruddoo laAAadoo limanuhoo AAanhu wa-innahum lakathiboon');
INSERT INTO "transliteration" VALUES(6,29,'6:29','Waqaloo in hiya illa hayatunaaddunya wama nahnu bimabAAootheen');
INSERT INTO "transliteration" VALUES(6,30,'6:30','Walaw tara ith wuqifoo AAalarabbihim qala alaysa hatha bilhaqqiqaloo bala warabbina qala fathooqooalAAathaba bima kuntum takfuroon');
INSERT INTO "transliteration" VALUES(6,31,'6:31','Qad khasira allatheena kaththaboobiliqa-i Allahi hatta itha jaat-humuassaAAatu baghtatan qaloo ya hasratanaAAala ma farratna feeha wahumyahmiloona awzarahum AAala thuhoorihimala saa ma yaziroon');
INSERT INTO "transliteration" VALUES(6,32,'6:32','Wama alhayatu addunyailla laAAibun walahwun waladdaru al-akhiratikhayrun lillatheena yattaqoona afala taAAqiloon');
INSERT INTO "transliteration" VALUES(6,33,'6:33','Qad naAAlamu innahu layahzunuka allatheeyaqooloona fa-innahum la yukaththiboonaka walakinnaaththalimeena bi-ayatiAllahi yajhadoon');
INSERT INTO "transliteration" VALUES(6,34,'6:34','Walaqad kuththibat rusulun minqablika fasabaroo AAala ma kuththiboowaoothoo hatta atahum nasrunawala mubaddila likalimati Allahi walaqad jaakamin naba-i almursaleen');
INSERT INTO "transliteration" VALUES(6,35,'6:35','Wa-in kana kabura AAalayka iAAraduhumfa-ini istataAAta an tabtaghiya nafaqan fee al-ardiaw sullaman fee assama-i fata/tiyahum bi-ayatinwalaw shaa Allahu lajamaAAahum AAala alhudafala takoonanna mina aljahileen');
INSERT INTO "transliteration" VALUES(6,36,'6:36','Innama yastajeebu allatheenayasmaAAoona walmawta yabAAathuhumu Allahuthumma ilayhi yurjaAAoon');
INSERT INTO "transliteration" VALUES(6,37,'6:37','Waqaloo lawla nuzzila AAalayhiayatun min rabbihi qul inna Allaha qadirunAAala an yunazzila ayatan walakinnaaktharahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(6,38,'6:38','Wama min dabbatin fee al-ardiwala ta-irin yateeru bijanahayhi illaomamun amthalukum ma farratna feealkitabi min shay-in thumma ila rabbihim yuhsharoon');
INSERT INTO "transliteration" VALUES(6,39,'6:39','Wallatheena kaththaboobi-ayatina summun wabukmun fee aththulumatiman yasha-i Allahu yudlilhu waman yasha/ yajAAalhuAAala siratin mustaqeem');
INSERT INTO "transliteration" VALUES(6,40,'6:40','Qul araaytakum in atakum AAathabuAllahi aw atatkumu assaAAatu aghayra AllahitadAAoona in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(6,41,'6:41','Bal iyyahu tadAAoona fayakshifu matadAAoona ilayhi in shaa watansawna ma tushrikoon');
INSERT INTO "transliteration" VALUES(6,42,'6:42','Walaqad arsalna ila omamin minqablika faakhathnahum bilba/sa-i waddarra-ilaAAallahum yatadarraAAoon');
INSERT INTO "transliteration" VALUES(6,43,'6:43','Falawla ith jaahumba/suna tadarraAAoo walakin qasat quloobuhumwazayyana lahumu ashshaytanu ma kanooyaAAmaloon');
INSERT INTO "transliteration" VALUES(6,44,'6:44','Falamma nasoo ma thukkiroobihi fatahna AAalayhim abwaba kulli shay-in hattaitha farihoo bima ootoo akhathnahumbaghtatan fa-itha hum mublisoon');
INSERT INTO "transliteration" VALUES(6,45,'6:45','FaqutiAAa dabiru alqawmi allatheenathalamoo walhamdu lillahirabbi alAAalameen');
INSERT INTO "transliteration" VALUES(6,46,'6:46','Qul araaytum in akhatha AllahusamAAakum waabsarakum wakhatama AAala quloobikumman ilahun ghayru Allahi ya/teekum bihi onthurkayfa nusarrifu al-ayati thumma hum yasdifoon');
INSERT INTO "transliteration" VALUES(6,47,'6:47','Qul araaytakum in atakum AAathabuAllahi baghtatan aw jahratan hal yuhlaku illaalqawmu aththalimoon');
INSERT INTO "transliteration" VALUES(6,48,'6:48','Wama nursilu almursaleena illamubashshireena wamunthireena faman amana waaslahafala khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(6,49,'6:49','Wallatheena kaththaboobi-ayatina yamassuhumu alAAathabu bimakanoo yafsuqoon');
INSERT INTO "transliteration" VALUES(6,50,'6:50','Qul la aqoolu lakum AAindee khaza-inuAllahi wala aAAlamu alghayba wala aqoolulakum innee malakun in attabiAAu illa ma yoohailayya qul hal yastawee al-aAAma walbaseeruafala tatafakkaroon');
INSERT INTO "transliteration" VALUES(6,51,'6:51','Waanthir bihi allatheena yakhafoonaan yuhsharoo ila rabbihim laysa lahum min doonihiwaliyyun wala shafeeAAun laAAallahum yattaqoon');
INSERT INTO "transliteration" VALUES(6,52,'6:52','Wala tatrudi allatheenayadAAoona rabbahum bilghadati walAAashiyyiyureedoona wajhahu ma AAalayka min hisabihimmin shay-in wama min hisabika AAalayhim minshay-in fatatrudahum fatakoona mina aththalimeen');
INSERT INTO "transliteration" VALUES(6,53,'6:53','Wakathalika fatanna baAAdahumbibaAAdin liyaqooloo ahaola-i manna AllahuAAalayhim min baynina alaysa Allahu bi-aAAlama bishshakireen');
INSERT INTO "transliteration" VALUES(6,54,'6:54','Wa-itha jaaka allatheenayu/minoona bi-ayatina faqul salamunAAalaykum kataba rabbukum AAala nafsihi arrahmataannahu man AAamila minkum soo-an bijahalatin thumma tabamin baAAdihi waaslaha faannahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(6,55,'6:55','Wakathalika nufassilu al-ayatiwalitastabeena sabeelu almujrimeen');
INSERT INTO "transliteration" VALUES(6,56,'6:56','Qul innee nuheetu an aAAbuda allatheenatadAAoona min dooni Allahi qul la attabiAAu ahwaakumqad dalaltu ithan wama ana minaalmuhtadeen');
INSERT INTO "transliteration" VALUES(6,57,'6:57','Qul innee AAala bayyinatin min rabbeewakaththabtum bihi ma AAindee matastaAAjiloona bihi ini alhukmu illa lillahiyaqussu alhaqqa wahuwa khayru alfasileen');
INSERT INTO "transliteration" VALUES(6,58,'6:58','Qul law anna AAindee matastaAAjiloona bihi laqudiya al-amru baynee wabaynakum wallahuaAAlamu biththalimeen');
INSERT INTO "transliteration" VALUES(6,59,'6:59','WaAAindahu mafatihu alghaybi layaAAlamuha illa huwa wayaAAlamu ma feealbarri walbahri wama tasqutu minwaraqatin illa yaAAlamuha wala habbatinfee thulumati al-ardi wala ratbinwala yabisin illa fee kitabin mubeen');
INSERT INTO "transliteration" VALUES(6,60,'6:60','Wahuwa allathee yatawaffakumbillayli wayaAAlamu ma jarahtum binnaharithumma yabAAathukum feehi liyuqda ajalun musamman thummailayhi marjiAAukum thumma yunabbi-okum bima kuntumtaAAmaloon');
INSERT INTO "transliteration" VALUES(6,61,'6:61','Wahuwa alqahiru fawqa AAibadihiwayursilu AAalaykum hafathatan hattaitha jaa ahadakumu almawtu tawaffat-hurusuluna wahum la yufarritoon');
INSERT INTO "transliteration" VALUES(6,62,'6:62','Thumma ruddoo ila Allahi mawlahumualhaqqi ala lahu alhukmu wahuwa asraAAu alhasibeen');
INSERT INTO "transliteration" VALUES(6,63,'6:63','Qul man yunajjeekum min thulumatialbarri walbahri tadAAoonahu tadarruAAanwakhufyatan la-in anjana min hathihilanakoonanna mina ashshakireen');
INSERT INTO "transliteration" VALUES(6,64,'6:64','Quli Allahu yunajjeekum minhawamin kulli karbin thumma antum tushrikoon');
INSERT INTO "transliteration" VALUES(6,65,'6:65','Qul huwa alqadiru AAala anyabAAatha AAalaykum AAathaban min fawqikum aw min tahtiarjulikum aw yalbisakum shiyaAAan wayutheeqa baAAdakumba/sa baAAdin onthur kayfa nusarrifual-ayati laAAallahum yafqahoon');
INSERT INTO "transliteration" VALUES(6,66,'6:66','Wakaththaba bihi qawmuka wahuwa alhaqququl lastu AAalaykum biwakeel');
INSERT INTO "transliteration" VALUES(6,67,'6:67','Likulli naba-in mustaqarrun wasawfataAAlamoon');
INSERT INTO "transliteration" VALUES(6,68,'6:68','Wa-itha raayta allatheenayakhoodoona fee ayatina faaAAridAAanhum hatta yakhoodoo fee hadeethinghayrihi wa-imma yunsiyannaka ashshaytanufala taqAAud baAAda aththikra maAAaalqawmi aththalimeen');
INSERT INTO "transliteration" VALUES(6,69,'6:69','Wama AAala allatheenayattaqoona min hisabihim min shay-in walakinthikra laAAallahum yattaqoon');
INSERT INTO "transliteration" VALUES(6,70,'6:70','Wathari allatheena ittakhathoodeenahum laAAiban walahwan wagharrat-humu alhayatuaddunya wathakkir bihi an tubsala nafsunbima kasabat laysa laha min dooni Allahiwaliyyun wala shafeeAAun wa-in taAAdil kulla AAadlin layu/khath minha ola-ika allatheenaobsiloo bima kasaboo lahum sharabun min hameeminwaAAathabun aleemun bima kanoo yakfuroon');
INSERT INTO "transliteration" VALUES(6,71,'6:71','Qul anadAAoo min dooni Allahi mala yanfaAAuna wala yadurrunawanuraddu AAala aAAqabina baAAda ithhadana Allahu kallathee istahwat-huashshayateenu fee al-ardi hayranalahu as-habun yadAAoonahu ila alhudai/tina qul inna huda Allahi huwa alhudawaomirna linuslima lirabbi alAAalameen');
INSERT INTO "transliteration" VALUES(6,72,'6:72','Waan aqeemoo assalatawattaqoohu wahuwa allathee ilayhi tuhsharoon');
INSERT INTO "transliteration" VALUES(6,73,'6:73','Wahuwa allathee khalaqa assamawatiwal-arda bilhaqqi wayawma yaqoolukun fayakoonu qawluhu alhaqqu walahu almulku yawmayunfakhu fee assoori AAalimu alghaybi washshahadatiwahuwa alhakeemu alkhabeer');
INSERT INTO "transliteration" VALUES(6,74,'6:74','Wa-ith qala ibraheemuli-abeehi azara atattakhithu asnaman alihataninnee araka waqawmaka fee dalalin mubeen');
INSERT INTO "transliteration" VALUES(6,75,'6:75','Wakathalika nuree ibraheemamalakoota assamawati wal-ardiwaliyakoona mina almooqineen');
INSERT INTO "transliteration" VALUES(6,76,'6:76','Falamma janna AAalayhi allaylu raakawkaban qala hatha rabbee falamma afala qalala ohibbu al-afileen');
INSERT INTO "transliteration" VALUES(6,77,'6:77','Falamma raa alqamara bazighanqala hatha rabbee falamma afala qalala-in lam yahdinee rabbee laakoonanna mina alqawmi addalleen');
INSERT INTO "transliteration" VALUES(6,78,'6:78','Falamma raa ashshamsabazighatan qala hatha rabbee hathaakbaru falamma afalat qala ya qawmi inneebaree-on mimma tushrikoon');
INSERT INTO "transliteration" VALUES(6,79,'6:79','Innee wajjahtu wajhiya lillathee fataraassamawati wal-arda haneefanwama ana mina almushrikeen');
INSERT INTO "transliteration" VALUES(6,80,'6:80','Wahajjahu qawmuhu qala atuhajjoonneefee Allahi waqad hadani wala akhafu matushrikoona bihi illa an yashaa rabbee shay-anwasiAAa rabbee kulla shay-in AAilman afala tatathakkaroon');
INSERT INTO "transliteration" VALUES(6,81,'6:81','Wakayfa akhafu ma ashraktumwala takhafoona annakum ashraktum billahima lam yunazzil bihi AAalaykum sultanan faayyualfareeqayni ahaqqu bil-amni in kuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(6,82,'6:82','Allatheena amanoo walamyalbisoo eemanahum bithulmin ola-ikalahumu al-amnu wahum muhtadoon');
INSERT INTO "transliteration" VALUES(6,83,'6:83','Watilka hujjatuna ataynahaibraheema AAala qawmihi narfaAAu darajatinman nashao inna rabbaka hakeemun AAaleem');
INSERT INTO "transliteration" VALUES(6,84,'6:84','Wawahabna lahu ishaqawayaAAqooba kullan hadayna wanoohan hadaynamin qablu wamin thurriyyatihi dawooda wasulaymanawaayyooba wayoosufa wamoosa waharoona wakathalikanajzee almuhsineen');
INSERT INTO "transliteration" VALUES(6,85,'6:85','Wazakariyya wayahyawaAAeesa walyasa kullun mina assaliheen');
INSERT INTO "transliteration" VALUES(6,86,'6:86','Wa-ismaAAeela walyasaAAawayoonusa walootan wakullan faddalna AAalaalAAalameen');
INSERT INTO "transliteration" VALUES(6,87,'6:87','Wamin aba-ihim wathurriyyatihimwa-ikhwanihim wajtabaynahum wahadaynahumila siratin mustaqeem');
INSERT INTO "transliteration" VALUES(6,88,'6:88','Thalika huda Allahiyahdee bihi man yashao min AAibadihi walaw ashrakoolahabita AAanhum ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(6,89,'6:89','Ola-ika allatheena ataynahumualkitaba walhukma wannubuwwatafa-in yakfur biha haola-i faqad wakkalnabiha qawman laysoo biha bikafireen');
INSERT INTO "transliteration" VALUES(6,90,'6:90','Ola-ika allatheena hadaAllahu fabihudahumu iqtadih qul la as-alukumAAalayhi ajran in huwa illa thikra lilAAalameen');
INSERT INTO "transliteration" VALUES(6,91,'6:91','Wama qadaroo Allaha haqqaqadrihi ith qaloo ma anzala AllahuAAala basharin min shay-in qul man anzala alkitabaallathee jaa bihi moosa nooran wahudan linnasitajAAaloonahu qarateesa tubdoonaha watukhfoonakatheeran waAAullimtum ma lam taAAlamoo antum wala abaokumquli Allahu thumma tharhum fee khawdihimyalAAaboon');
INSERT INTO "transliteration" VALUES(6,92,'6:92','Wahatha kitabun anzalnahumubarakun musaddiqu allathee bayna yadayhiwalitunthira omma alqura waman hawlahawallatheena yu/minoona bil-akhiratiyu/minoona bihi wahum AAala salatihim yuhafithoon');
INSERT INTO "transliteration" VALUES(6,93,'6:93','Waman athlamu mimmani iftaraAAala Allahi kathiban aw qala oohiyailayya walam yooha ilayhi shay-on waman qalasaonzilu mithla ma anzala Allahu walaw taraithi aththalimoona fee ghamaratialmawti walmala-ikatu basitooaydeehim akhrijoo anfusakumu alyawma tujzawna AAathabaalhooni bima kuntum taqooloona AAala Allahighayra alhaqqi wakuntum AAan ayatihitastakbiroon');
INSERT INTO "transliteration" VALUES(6,94,'6:94','Walaqad ji/tumoona furadakama khalaqnakum awwala marratin wataraktum makhawwalnakum waraa thuhoorikum wamanara maAAakum shufaAAaakumu allatheenazaAAamtum annahum feekum shurakao laqad taqattaAAabaynakum wadalla AAankum ma kuntum tazAAumoon');
INSERT INTO "transliteration" VALUES(6,95,'6:95','Inna Allaha faliqu alhabbiwannawa yukhriju alhayya mina almayyitiwamukhriju almayyiti mina alhayyi thalikumu Allahufaanna tu/fakoon');
INSERT INTO "transliteration" VALUES(6,96,'6:96','Faliqu al-isbahiwajaAAala allayla sakanan washshamsa walqamara husbananthalika taqdeeru alAAazeezi alAAaleem');
INSERT INTO "transliteration" VALUES(6,97,'6:97','Wahuwa allathee jaAAala lakumu annujoomalitahtadoo biha fee thulumati albarriwalbahri qad fassalna al-ayatiliqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(6,98,'6:98','Wahuwa allathee anshaakum min nafsinwahidatin famustaqarrun wamustawdaAAun qad fassalnaal-ayati liqawmin yafqahoon');
INSERT INTO "transliteration" VALUES(6,99,'6:99','Wahuwa allathee anzala mina assama-imaan faakhrajna bihi nabata kulli shay-infaakhrajna minhu khadiran nukhriju minhu habbanmutarakiban wamina annakhli min talAAihaqinwanun daniyatun wajannatin min aAAnabinwazzaytoona warrummana mushtabihanwaghayra mutashabihin onthuroo ilathamarihi itha athmara wayanAAihi inna fee thalikumlaayatin liqawmin yu/minoon');
INSERT INTO "transliteration" VALUES(6,100,'6:100','WajaAAaloo lillahi shurakaaaljinna wakhalaqahum wakharaqoo lahu baneena wabanatinbighayri AAilmin subhanahu wataAAala AAammayasifoon');
INSERT INTO "transliteration" VALUES(6,101,'6:101','BadeeAAu assamawatiwal-ardi anna yakoonu lahu waladun walamtakun lahu sahibatun wakhalaqa kulla shay-in wahuwabikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(6,102,'6:102','Thalikumu Allahu rabbukum lailaha illa huwa khaliqu kulli shay-in faAAbudoohuwahuwa AAala kulli shay-in wakeel');
INSERT INTO "transliteration" VALUES(6,103,'6:103','La tudrikuhu al-absaru wahuwayudriku al-absara wahuwa allateefu alkhabeer');
INSERT INTO "transliteration" VALUES(6,104,'6:104','Qad jaakum basa-iru minrabbikum faman absara falinafsihi waman AAamiya faAAalayhawama ana AAalaykum bihafeeth');
INSERT INTO "transliteration" VALUES(6,105,'6:105','Wakathalika nusarrifu al-ayatiwaliyaqooloo darasta walinubayyinahu liqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(6,106,'6:106','IttabiAA ma oohiya ilayka minrabbika la ilaha illa huwa waaAAridAAani almushrikeen');
INSERT INTO "transliteration" VALUES(6,107,'6:107','Walaw shaa Allahu maashrakoo wama jaAAalnaka AAalayhim hafeethanwama anta AAalayhim biwakeel');
INSERT INTO "transliteration" VALUES(6,108,'6:108','Wala tasubboo allatheenayadAAoona min dooni Allahi fayasubboo AllahaAAadwan bighayri AAilmin kathalika zayyanna likulliommatin AAamalahum thumma ila rabbihim marjiAAuhumfayunabbi-ohum bima kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(6,109,'6:109','Waaqsamoo billahi jahda aymanihimla-in jaat-hum ayatun layu/minunna biha qulinnama al-ayatu AAinda Allahi wamayushAAirukum annaha itha jaat layu/minoon');
INSERT INTO "transliteration" VALUES(6,110,'6:110','Wanuqallibu af-idatahum waabsarahumkama lam yu/minoo bihi awwala marratin wanatharuhumfee tughyanihim yaAAmahoon');
INSERT INTO "transliteration" VALUES(6,111,'6:111','Walaw annana nazzalnailayhimu almala-ikata wakallamahumu almawta wahasharnaAAalayhim kulla shay-in qubulan ma kanoo liyu/minooilla an yashaa Allahu walakinnaaktharahum yajhaloon');
INSERT INTO "transliteration" VALUES(6,112,'6:112','Wakathalika jaAAalna likullinabiyyin AAaduwwan shayateena al-insi waljinni yooheebaAAduhum ila baAAdin zukhrufa alqawlighurooran walaw shaa rabbuka ma faAAaloohu fatharhumwama yaftaroon');
INSERT INTO "transliteration" VALUES(6,113,'6:113','Walitasgha ilayhi af-idatuallatheena la yu/minoona bil-akhiratiwaliyardawhu waliyaqtarifoo ma hum muqtarifoon');
INSERT INTO "transliteration" VALUES(6,114,'6:114','Afaghayra Allahi abtaghee hakamanwahuwa allathee anzala ilaykumu alkitaba mufassalanwallatheena ataynahumu alkitabayaAAlamoona annahu munazzalun min rabbika bilhaqqifala takoonanna mina almumtareen');
INSERT INTO "transliteration" VALUES(6,115,'6:115','Watammat kalimatu rabbika sidqanwaAAadlan la mubaddila likalimatihi wahuwa assameeAAualAAaleem');
INSERT INTO "transliteration" VALUES(6,116,'6:116','Wa-in tutiAA akthara man fee al-ardiyudillooka AAan sabeeli Allahi in yattabiAAoona illaaththanna wa-in hum illa yakhrusoon');
INSERT INTO "transliteration" VALUES(6,117,'6:117','Inna rabbaka huwa aAAlamu man yadilluAAan sabeelihi wahuwa aAAlamu bilmuhtadeen');
INSERT INTO "transliteration" VALUES(6,118,'6:118','Fakuloo mimma thukira ismuAllahi AAalayhi in kuntum bi-ayatihimu/mineen');
INSERT INTO "transliteration" VALUES(6,119,'6:119','Wama lakum alla ta/kuloo mimmathukira ismu Allahi AAalayhi waqad fassalalakum ma harrama AAalaykum illa ma idturirtumilayhi wa-inna katheeran layudilloona bi-ahwa-ihimbighayri AAilmin inna rabbaka huwa aAAlamu bilmuAAtadeen');
INSERT INTO "transliteration" VALUES(6,120,'6:120','Watharoo thahiraal-ithmi wabatinahu inna allatheena yaksiboonaal-ithma sayujzawna bima kanoo yaqtarifoon');
INSERT INTO "transliteration" VALUES(6,121,'6:121','Wala ta/kuloo mimma lam yuthkariismu Allahi AAalayhi wa-innahu lafisqun wa-inna ashshayateenalayoohoona ila awliya-ihim liyujadilookumwa-in ataAAtumoohum innakum lamushrikoon');
INSERT INTO "transliteration" VALUES(6,122,'6:122','Awa man kana maytan faahyaynahuwajaAAalna lahu nooran yamshee bihi fee annasikaman mathaluhu fee aththulumatilaysa bikharijin minha kathalika zuyyinalilkafireena ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(6,123,'6:123','Wakathalika jaAAalna feekulli qaryatin akabira mujrimeeha liyamkuroo feehawama yamkuroona illa bi-anfusihim wamayashAAuroon');
INSERT INTO "transliteration" VALUES(6,124,'6:124','Wa-itha jaat-hum ayatunqaloo lan nu/mina hatta nu/ta mithlama ootiya rusulu Allahi Allahu aAAlamu haythuyajAAalu risalatahu sayuseebu allatheenaajramoo sagharun AAinda Allahi waAAathabunshadeedun bima kanoo yamkuroon');
INSERT INTO "transliteration" VALUES(6,125,'6:125','Faman yuridi Allahu an yahdiyahuyashrah sadrahu lil-islami waman yurid an yudillahuyajAAal sadrahu dayyiqan harajan kaannamayassaAAAAadu fee assama-i kathalikayajAAalu Allahu arrijsa AAala allatheenala yu/minoon');
INSERT INTO "transliteration" VALUES(6,126,'6:126','Wahatha siratu rabbikamustaqeeman qad fassalna al-ayatiliqawmin yaththakkaroon');
INSERT INTO "transliteration" VALUES(6,127,'6:127','Lahum daru assalamiAAinda rabbihim wahuwa waliyyuhum bima kanooyaAAmaloon');
INSERT INTO "transliteration" VALUES(6,128,'6:128','Wayawma yahshuruhum jameeAAan yamaAAshara aljinni qadi istakthartum mina al-insi waqalaawliyaohum mina al-insi rabbana istamtaAAa baAAdunabibaAAdin wabalaghna ajalana allatheeajjalta lana qala annaru mathwakumkhalideena feeha illa ma shaaAllahu inna rabbaka hakeemun AAaleem');
INSERT INTO "transliteration" VALUES(6,129,'6:129','Wakathalika nuwallee baAAda aththalimeenabaAAdan bima kanoo yaksiboon');
INSERT INTO "transliteration" VALUES(6,130,'6:130','Ya maAAshara aljinni wal-insialam ya/tikum rusulun minkum yaqussoona AAalaykum ayateewayunthiroonakum liqaa yawmikum hatha qalooshahidna AAala anfusina wagharrat-humu alhayatuaddunya washahidoo AAala anfusihim annahumkanoo kafireen');
INSERT INTO "transliteration" VALUES(6,131,'6:131','Thalika an lam yakun rabbuka muhlikaalqura bithulmin waahluha ghafiloon');
INSERT INTO "transliteration" VALUES(6,132,'6:132','Walikullin darajatun mimmaAAamiloo wama rabbuka bighafilin AAammayaAAmaloon');
INSERT INTO "transliteration" VALUES(6,133,'6:133','Warabbuka alghaniyyu thoo arrahmatiin yasha/ yuthhibkum wayastakhlif min baAAdikum mayashao kama anshaakum min thurriyyati qawminakhareen');
INSERT INTO "transliteration" VALUES(6,134,'6:134','Inna ma tooAAadoona laatinwama antum bimuAAjizeen');
INSERT INTO "transliteration" VALUES(6,135,'6:135','Qul ya qawmi iAAmaloo AAalamakanatikum innee AAamilun fasawfa taAAlamoona mantakoonu lahu AAaqibatu addari innahu layuflihu aththalimoon');
INSERT INTO "transliteration" VALUES(6,136,'6:136','WajaAAaloo lillahi mimma tharaamina alharthi wal-anAAami naseebanfaqaloo hatha lillahi bizaAAmihim wahathalishuraka-ina fama kana lishuraka-ihimfala yasilu ila Allahi wama kanalillahi fahuwa yasilu ila shuraka-ihimsaa ma yahkumoon');
INSERT INTO "transliteration" VALUES(6,137,'6:137','Wakathalika zayyana likatheerin minaalmushrikeena qatla awladihim shurakaohumliyurdoohum waliyalbisoo AAalayhim deenahum walaw shaa Allahuma faAAaloohu fatharhum wama yaftaroon');
INSERT INTO "transliteration" VALUES(6,138,'6:138','Waqaloo hathihi anAAamunwaharthun hijrun la yatAAamuhailla man nashao bizaAAmihim waanAAamun hurrimatthuhooruha waanAAamun la yathkuroonaisma Allahi AAalayha iftiraan AAalayhisayajzeehim bima kanoo yaftaroon');
INSERT INTO "transliteration" VALUES(6,139,'6:139','Waqaloo ma fee butoonihathihi al-anAAami khalisatun lithukoorinawamuharramun AAala azwajina wa-inyakun maytatan fahum feehi shurakao sayajzeehim wasfahuminnahu hakeemun AAaleem');
INSERT INTO "transliteration" VALUES(6,140,'6:140','Qad khasira allatheena qataloo awladahumsafahan bighayri AAilmin waharramoo ma razaqahumuAllahu iftiraan AAala Allahi qad dalloowama kanoo muhtadeen');
INSERT INTO "transliteration" VALUES(6,141,'6:141','Wahuwa allathee anshaa jannatinmaAArooshatin waghayra maAArooshatin wannakhlawazzarAAa mukhtalifan okuluhu wazzaytoona warrummanamutashabihan waghayra mutashabihin kuloo minthamarihi itha athmara waatoo haqqahu yawma hasadihiwala tusrifoo innahu la yuhibbu almusrifeen');
INSERT INTO "transliteration" VALUES(6,142,'6:142','Wamina al-anAAami hamoolatanwafarshan kuloo mimma razaqakumu Allahu walatattabiAAoo khutuwati ashshaytaniinnahu lakum AAaduwwun mubeen');
INSERT INTO "transliteration" VALUES(6,143,'6:143','Thamaniyata azwajin mina adda/niithnayni wamina almaAAzi ithnayni qul aththakarayniharrama ami alonthayayni amma ishtamalat AAalayhiarhamu alonthayayni nabbi-oonee biAAilmin in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(6,144,'6:144','Wamina al-ibili ithnayni wamina albaqariithnayni qul aththakarayni harramaami alonthayayni amma ishtamalat AAalayhi arhamualonthayayni am kuntum shuhadaa ith wassakumuAllahu bihatha faman athlamu mimmaniiftara AAala Allahi kathiban liyudillaannasa bighayri AAilmin inna Allaha layahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(6,145,'6:145','Qul la ajidu feema oohiyailayya muharraman AAala taAAimin yatAAamuhuilla an yakoona maytatan aw daman masfoohan aw lahmakhinzeerin fa-innahu rijsun aw fisqan ohilla lighayri Allahibihi famani idturra ghayra baghin wala AAadinfa-inna rabbaka ghafoorun raheem');
INSERT INTO "transliteration" VALUES(6,146,'6:146','WaAAala allatheena hadooharramna kulla thee thufurinwamina albaqari walghanami harramnaAAalayhim shuhoomahuma illa ma hamalatthuhooruhuma awi alhawayaaw ma ikhtalata biAAathmin thalikajazaynahum bibaghyihim wa-inna lasadiqoon');
INSERT INTO "transliteration" VALUES(6,147,'6:147','Fa-in kaththabooka faqul rabbukum thoorahmatin wasiAAatin wala yuraddu ba/suhuAAani alqawmi almujrimeen');
INSERT INTO "transliteration" VALUES(6,148,'6:148','Sayaqoolu allatheena ashrakoo law shaaAllahu ma ashrakna wala abaonawala harramna min shay-in kathalikakaththaba allatheena min qablihim hattathaqoo ba/sana qul hal AAindakum min AAilminfatukhrijoohu lana in tattabiAAoona illa aththannawa-in antum illa takhrusoon');
INSERT INTO "transliteration" VALUES(6,149,'6:149','Qul falillahi alhujjatu albalighatufalaw shaa lahadakum ajmaAAeen');
INSERT INTO "transliteration" VALUES(6,150,'6:150','Qul halumma shuhadaakumu allatheenayashhadoona anna Allaha harrama hatha fa-inshahidoo fala tashhad maAAahum wala tattabiAA ahwaaallatheena kaththaboo bi-ayatinawallatheena la yu/minoona bil-akhiratiwahum birabbihim yaAAdiloon');
INSERT INTO "transliteration" VALUES(6,151,'6:151','Qul taAAalaw atlu ma harramarabbukum AAalaykum alla tushrikoo bihi shay-an wabilwalidayniihsanan wala taqtuloo awladakum minimlaqin nahnu narzuqukum wa-iyyahum walataqraboo alfawahisha ma thahara minhawama batana wala taqtuloo annafsaallatee harrama Allahu illa bilhaqqithalikum wassakum bihi laAAallakum taAAqiloon');
INSERT INTO "transliteration" VALUES(6,152,'6:152','Wala taqraboo mala alyateemiilla billatee hiya ahsanu hattayablugha ashuddahu waawfoo alkayla walmeezana bilqistila nukallifu nafsan illa wusAAaha wa-ithaqultum faAAdiloo walaw kana tha qurbawabiAAahdi Allahi awfoo thalikum wassakumbihi laAAallakum tathakkaroon');
INSERT INTO "transliteration" VALUES(6,153,'6:153','Waanna hatha sirateemustaqeeman fattabiAAoohu wala tattabiAAoo assubulafatafarraqa bikum AAan sabeelihi thalikum wassakumbihi laAAallakum tattaqoon');
INSERT INTO "transliteration" VALUES(6,154,'6:154','Thumma atayna moosaalkitaba tamaman AAala allathee ahsanawatafseelan likulli shay-in wahudan warahmatanlaAAallahum biliqa-i rabbihim yu/minoon');
INSERT INTO "transliteration" VALUES(6,155,'6:155','Wahatha kitabun anzalnahumubarakun fattabiAAoohu wattaqoolaAAallakum turhamoon');
INSERT INTO "transliteration" VALUES(6,156,'6:156','An taqooloo innama onzila alkitabuAAala ta-ifatayni min qablina wa-in kunnaAAan dirasatihim laghafileen');
INSERT INTO "transliteration" VALUES(6,157,'6:157','Aw taqooloo law anna onzila AAalaynaalkitabu lakunna ahda minhum faqad jaakumbayyinatun min rabbikum wahudan warahmatun faman athlamumimman kaththaba bi-ayati Allahi wasadafaAAanha sanajzee allatheena yasdifoona AAan ayatinasoo-a alAAathabi bima kanoo yasdifoon');
INSERT INTO "transliteration" VALUES(6,158,'6:158','Hal yanthuroona illaan ta/tiyahumu almala-ikatu aw ya/tiya rabbuka aw ya/tiyabaAAdu ayati rabbika yawma ya/tee baAAduayati rabbika la yanfaAAu nafsan eemanuhalam takun amanat min qablu aw kasabat fee eemanihakhayran quli intathiroo inna muntathiroon');
INSERT INTO "transliteration" VALUES(6,159,'6:159','Inna allatheena farraqoo deenahumwakanoo shiyaAAan lasta minhum fee shay-in innamaamruhum ila Allahi thumma yunabbi-ohum bimakanoo yafAAaloon');
INSERT INTO "transliteration" VALUES(6,160,'6:160','Man jaa bilhasanatifalahu AAashru amthaliha waman jaa bissayyi-atifala yujza illa mithlaha wahum layuthlamoon');
INSERT INTO "transliteration" VALUES(6,161,'6:161','Qul innanee hadanee rabbee ilasiratin mustaqeemin deenan qiyaman millata ibraheemahaneefan wama kana mina almushrikeen');
INSERT INTO "transliteration" VALUES(6,162,'6:162','Qul inna salatee wanusukeewamahyaya wamamatee lillahi rabbialAAalameen');
INSERT INTO "transliteration" VALUES(6,163,'6:163','La shareeka lahu wabithalikaomirtu waana awwalu almuslimeen');
INSERT INTO "transliteration" VALUES(6,164,'6:164','Qul aghayra Allahi abghee rabbanwahuwa rabbu kulli shay-in wala taksibu kullu nafsin illaAAalayha wala taziru waziratun wizra okhrathumma ila rabbikum marjiAAukum fayunabbi-okum bimakuntum feehi takhtalifoon');
INSERT INTO "transliteration" VALUES(6,165,'6:165','Wahuwa allathee jaAAalakum khala-ifaal-ardi warafaAAa baAAdakum fawqa baAAdindarajatin liyabluwakum fee ma atakuminna rabbaka sareeAAu alAAiqabi wa-innahu laghafoorun raheem');
INSERT INTO "transliteration" VALUES(7,1,'7:1','Alif-lam-meem-sad');
INSERT INTO "transliteration" VALUES(7,2,'7:2','Kitabun onzila ilayka falayakun fee sadrika harajun minhu litunthirabihi wathikra lilmu/mineen');
INSERT INTO "transliteration" VALUES(7,3,'7:3','IttabiAAoo ma onzila ilaykum minrabbikum wala tattabiAAoo min doonihi awliyaaqaleelan ma tathakkaroon');
INSERT INTO "transliteration" VALUES(7,4,'7:4','Wakam min qaryatin ahlaknahafajaaha ba/suna bayatan aw hum qa-iloon');
INSERT INTO "transliteration" VALUES(7,5,'7:5','Fama kana daAAwahum ithjaahum ba/suna illa an qaloo innakunna thalimeen');
INSERT INTO "transliteration" VALUES(7,6,'7:6','Falanas-alanna allatheena orsilailayhim walanas-alanna almursaleen');
INSERT INTO "transliteration" VALUES(7,7,'7:7','Falanaqussanna AAalayhim biAAilmin wamakunna gha-ibeen');
INSERT INTO "transliteration" VALUES(7,8,'7:8','Walwaznu yawma-ithini alhaqqufaman thaqulat mawazeenuhu faola-ika humu almuflihoon');
INSERT INTO "transliteration" VALUES(7,9,'7:9','Waman khaffat mawazeenuhu faola-ikaallatheena khasiroo anfusahum bima kanoo bi-ayatinayathlimoon');
INSERT INTO "transliteration" VALUES(7,10,'7:10','Walaqad makkannakum fee al-ardiwajaAAalna lakum feeha maAAayisha qaleelan matashkuroon');
INSERT INTO "transliteration" VALUES(7,11,'7:11','Walaqad khalaqnakum thumma sawwarnakumthumma qulna lilmala-ikati osjudoo li-adamafasajadoo illa ibleesa lam yakun mina assajideen');
INSERT INTO "transliteration" VALUES(7,12,'7:12','Qala ma manaAAaka allatasjuda ith amartuka qala ana khayrun minhukhalaqtanee min narin wakhalaqtahu min teen');
INSERT INTO "transliteration" VALUES(7,13,'7:13','Qala fahbit minhafama yakoonu laka an tatakabbara feeha fakhrujinnaka mina assaghireen');
INSERT INTO "transliteration" VALUES(7,14,'7:14','Qala anthirnee ilayawmi yubAAathoon');
INSERT INTO "transliteration" VALUES(7,15,'7:15','Qala innaka mina almunthareen');
INSERT INTO "transliteration" VALUES(7,16,'7:16','Qala fabima aghwaytaneelaaqAAudanna lahum sirataka almustaqeem');
INSERT INTO "transliteration" VALUES(7,17,'7:17','Thumma laatiyannahum min bayniaydeehim wamin khalfihim waAAan aymanihim waAAan shama-ilihimwala tajidu aktharahum shakireen');
INSERT INTO "transliteration" VALUES(7,18,'7:18','Qala okhruj minha mathoomanmadhooran laman tabiAAaka minhum laamlaanna jahannamaminkum ajmaAAeen');
INSERT INTO "transliteration" VALUES(7,19,'7:19','Waya adamu oskun antawazawjuka aljannata fakula min haythu shi/tumawala taqraba hathihi ashshajaratafatakoona mina aththalimeen');
INSERT INTO "transliteration" VALUES(7,20,'7:20','Fawaswasa lahuma ashshaytanuliyubdiya lahuma ma wooriya AAanhuma minsaw-atihima waqala ma nahakumarabbukuma AAan hathihi ashshajarati illaan takoona malakayni aw takoona mina alkhalideen');
INSERT INTO "transliteration" VALUES(7,21,'7:21','Waqasamahuma innee lakumalamina annasiheen');
INSERT INTO "transliteration" VALUES(7,22,'7:22','Fadallahuma bighuroorin falammathaqa ashshajarata badat lahuma saw-atuhumawatafiqa yakhsifani AAalayhimamin waraqi aljannati wanadahuma rabbuhumaalam anhakuma AAan tilkuma ashshajaratiwaaqul lakuma inna ashshaytana lakumaAAaduwwun mubeen');
INSERT INTO "transliteration" VALUES(7,23,'7:23','Qala rabbana thalamnaanfusana wa-in lam taghfir lana watarhamnalanakoonanna mina alkhasireen');
INSERT INTO "transliteration" VALUES(7,24,'7:24','Qala ihbitoo baAAdukumlibaAAdin AAaduwwun walakum fee al-ardi mustaqarrunwamataAAun ila heen');
INSERT INTO "transliteration" VALUES(7,25,'7:25','Qala feeha tahyawnawafeeha tamootoona waminha tukhrajoon');
INSERT INTO "transliteration" VALUES(7,26,'7:26','Ya banee adama qad anzalnaAAalaykum libasan yuwaree saw-atikumwareeshan walibasu attaqwa thalikakhayrun thalika min ayati AllahilaAAallahum yaththakkaroon');
INSERT INTO "transliteration" VALUES(7,27,'7:27','Ya banee adama layaftinannakumu ashshaytanu kama akhrajaabawaykum mina aljannati yanziAAu AAanhuma libasahumaliyuriyahuma saw-atihima innahu yarakumhuwa waqabeeluhu min haythu la tarawnahum innajaAAalna ashshayateena awliyaa lillatheenala yu/minoon');
INSERT INTO "transliteration" VALUES(7,28,'7:28','Wa-itha faAAaloo fahishatan qaloowajadna AAalayha abaana wallahuamarana biha qul inna Allaha laya/muru bilfahsha-i ataqooloona AAalaAllahi ma la taAAlamoon');
INSERT INTO "transliteration" VALUES(7,29,'7:29','Qul amara rabbee bilqistiwaaqeemoo wujoohakum AAinda kulli masjidin wadAAoohumukhliseena lahu addeena kama badaakumtaAAoodoon');
INSERT INTO "transliteration" VALUES(7,30,'7:30','Fareeqan hada wafareeqan haqqaAAalayhimu addalalatu innahumu ittakhathooashshayateena awliyaa min dooni Allahiwayahsaboona annahum muhtadoon');
INSERT INTO "transliteration" VALUES(7,31,'7:31','Ya banee adama khuthoozeenatakum AAinda kulli masjidin wakuloo washraboo walatusrifoo innahu la yuhibbu almusrifeen');
INSERT INTO "transliteration" VALUES(7,32,'7:32','Qul man harrama zeenata Allahiallatee akhraja liAAibadihi wattayyibatimina arrizqi qul hiya lillatheena amanoofee alhayati addunya khalisatanyawma alqiyamati kathalika nufassilu al-ayatiliqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(7,33,'7:33','Qul innama harrama rabbiyaalfawahisha ma thahara minhawama batana wal-ithma walbaghyabighayri alhaqqi waan tushrikoo billahi malam yunazzil bihi sultanan waan taqooloo AAala Allahima la taAAlamoon');
INSERT INTO "transliteration" VALUES(7,34,'7:34','Walikulli ommatin ajalun fa-itha jaaajaluhum la yasta/khiroona saAAatan walayastaqdimoon');
INSERT INTO "transliteration" VALUES(7,35,'7:35','Ya banee adama immaya/tiyannakum rusulun minkum yaqussoona AAalaykum ayateefamani ittaqa waaslaha fala khawfunAAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(7,36,'7:36','Wallatheena kaththaboobi-ayatina wastakbaroo AAanhaola-ika as-habu annari humfeeha khalidoon');
INSERT INTO "transliteration" VALUES(7,37,'7:37','Faman athlamu mimmani iftaraAAala Allahi kathiban aw kaththababi-ayatihi ola-ika yanaluhum naseebuhummina alkitabi hatta itha jaat-humrusuluna yatawaffawnahum qaloo ayna makuntum tadAAoona min dooni Allahi qaloo dallooAAanna washahidoo AAala anfusihim annahum kanookafireen');
INSERT INTO "transliteration" VALUES(7,38,'7:38','Qala odkhuloo fee omamin qad khalatmin qablikum mina aljinni wal-insi fee annarikullama dakhalat ommatun laAAanat okhtaha hattaitha iddarakoo feeha jameeAAan qalatokhrahum li-oolahum rabbana haola-iadalloona faatihim AAathaban diAAfanmina annari qala likullin diAAfunwalakin la taAAlamoon');
INSERT INTO "transliteration" VALUES(7,39,'7:39','Waqalat oolahum li-okhrahumfama kana lakum AAalayna min fadlinfathooqoo alAAathaba bima kuntum taksiboon');
INSERT INTO "transliteration" VALUES(7,40,'7:40','Inna allatheena kaththaboo bi-ayatinawastakbaroo AAanha la tufattahulahum abwabu assama-i walayadkhuloona aljannata hatta yalija aljamalu feesammi alkhiyati wakathalika najzee almujrimeen');
INSERT INTO "transliteration" VALUES(7,41,'7:41','Lahum min jahannama mihadun waminfawqihim ghawashin wakathalika najzee aththalimeen');
INSERT INTO "transliteration" VALUES(7,42,'7:42','Wallatheena amanoowaAAamiloo assalihati la nukallifunafsan illa wusAAaha ola-ika as-habualjannati hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(7,43,'7:43','WanazaAAna ma fee sudoorihimmin ghillin tajree min tahtihimu al-anharu waqalooalhamdu lillahi allathee hadanalihatha wama kunna linahtadiya lawlaan hadana Allahu laqad jaat rusulurabbina bilhaqqi wanoodoo an tilkumualjannatu oorithtumooha bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(7,44,'7:44','Wanada as-habualjannati as-haba annari an qadwajadna ma waAAadana rabbuna haqqanfahal wajadtum ma waAAada rabbukum haqqan qaloonaAAam faaththana mu-aththinun baynahum an laAAnatuAllahi AAala aththalimeen');
INSERT INTO "transliteration" VALUES(7,45,'7:45','Allatheena yasuddoona AAansabeeli Allahi wayabghoonaha AAiwajan wahum bil-akhiratikafiroon');
INSERT INTO "transliteration" VALUES(7,46,'7:46','Wabaynahuma hijabunwaAAala al-aAArafi rijalun yaAArifoonakullan biseemahum wanadaw as-habaaljannati an salamun AAalaykum lam yadkhuloohawahum yatmaAAoon');
INSERT INTO "transliteration" VALUES(7,47,'7:47','Wa-itha surifat absaruhumtilqaa as-habi annari qaloorabbana la tajAAalna maAAa alqawmi aththalimeen');
INSERT INTO "transliteration" VALUES(7,48,'7:48','Wanada as-habual-aAArafi rijalan yaAArifoonahum biseemahumqaloo ma aghna AAankum jamAAukum wamakuntum tastakbiroon');
INSERT INTO "transliteration" VALUES(7,49,'7:49','Ahaola-i allatheenaaqsamtum la yanaluhumu Allahu birahmatinodkhuloo aljannata la khawfun AAalaykum wala antumtahzanoon');
INSERT INTO "transliteration" VALUES(7,50,'7:50','Wanada as-habu anarias-haba aljannati an afeedoo AAalaynamina alma-i aw mimma razaqakumu Allahu qalooinna Allaha harramahuma AAala alkafireen');
INSERT INTO "transliteration" VALUES(7,51,'7:51','Allatheena ittakhathoodeenahum lahwan walaAAiban wagharrat-humu alhayatuaddunya falyawma nansahum kamanasoo liqaa yawmihim hatha wama kanoobi-ayatina yajhadoon');
INSERT INTO "transliteration" VALUES(7,52,'7:52','Walaqad ji/nahum bikitabin fassalnahuAAala AAilmin hudan warahmatan liqawmin yu/minoon');
INSERT INTO "transliteration" VALUES(7,53,'7:53','Hal yanthuroona illata/weelahu yawma ya/tee ta/weeluhu yaqoolu allatheenanasoohu min qablu qad jaat rusulu rabbina bilhaqqifahal lana min shufaAAaa fayashfaAAoo lanaaw nuraddu fanaAAmala ghayra allathee kunnanaAAmalu qad khasiroo anfusahum wadalla AAanhum makanoo yaftaroon');
INSERT INTO "transliteration" VALUES(7,54,'7:54','Inna rabbakumu Allahu allatheekhalaqa assamawati wal-ardafee sittati ayyamin thumma istawa AAalaalAAarshi yughshee allayla annahara yatlubuhuhatheethan washshamsa walqamara wannujoomamusakhkharatin bi-amrihi ala lahu alkhalqu wal-amrutabaraka Allahu rabbu alAAalameen');
INSERT INTO "transliteration" VALUES(7,55,'7:55','OdAAoo rabbakum tadarruAAanwakhufyatan innahu la yuhibbu almuAAtadeen');
INSERT INTO "transliteration" VALUES(7,56,'7:56','Wala tufsidoo fee al-ardibaAAda islahiha wadAAoohu khawfan watamaAAaninna rahmata Allahi qareebun mina almuhsineen');
INSERT INTO "transliteration" VALUES(7,57,'7:57','Wahuwa allathee yursilu arriyahabushran bayna yaday rahmatihi hatta ithaaqallat sahaban thiqalan suqnahu libaladinmayyitin faanzalna bihi almaa faakhrajnabihi min kulli aththamarati kathalikanukhriju almawta laAAallakum tathakkaroon');
INSERT INTO "transliteration" VALUES(7,58,'7:58','Walbaladu attayyibuyakhruju nabatuhu bi-ithni rabbihi wallatheekhabutha la yakhruju illa nakidan kathalikanusarrifu al-ayati liqawmin yashkuroon');
INSERT INTO "transliteration" VALUES(7,59,'7:59','Laqad arsalna noohan ilaqawmihi faqala ya qawmi oAAbudoo Allaha malakum min ilahin ghayruhu innee akhafu AAalaykumAAathaba yawmin AAatheem');
INSERT INTO "transliteration" VALUES(7,60,'7:60','Qala almalao min qawmihi innalanaraka fee dalalin mubeen');
INSERT INTO "transliteration" VALUES(7,61,'7:61','Qala ya qawmi laysa bee dalalatunwalakinnee rasoolun min rabbi alAAalameen');
INSERT INTO "transliteration" VALUES(7,62,'7:62','Oballighukum risalati rabbeewaansahu lakum waaAAlamu mina Allahi mala taAAlamoon');
INSERT INTO "transliteration" VALUES(7,63,'7:63','Awa AAajibtum an jaakum thikrunmin rabbikum AAala rajulin minkum liyunthirakumwalitattaqoo walaAAallakum turhamoon');
INSERT INTO "transliteration" VALUES(7,64,'7:64','Fakaththaboohu faanjaynahu wallatheenamaAAahu fee alfulki waaghraqna allatheena kaththaboobi-ayatina innahum kanoo qawmanAAameen');
INSERT INTO "transliteration" VALUES(7,65,'7:65','Wa-ila AAadin akhahumhoodan qala ya qawmi oAAbudoo Allaha malakum min ilahin ghayruhu afala tattaqoon');
INSERT INTO "transliteration" VALUES(7,66,'7:66','Qala almalao allatheenakafaroo min qawmihi inna lanaraka fee safahatinwa-inna lanathunnuka mina alkathibeen');
INSERT INTO "transliteration" VALUES(7,67,'7:67','Qala ya qawmi laysa bee safahatunwalakinnee rasoolun min rabbi alAAalameen');
INSERT INTO "transliteration" VALUES(7,68,'7:68','Oballighukum risalati rabbeewaana lakum nasihun ameen');
INSERT INTO "transliteration" VALUES(7,69,'7:69','Awa AAajibtum an jaakum thikrunmin rabbikum AAala rajulin minkum liyunthirakum wathkurooith jaAAalakum khulafaa min baAAdi qawmi noohinwazadakum fee alkhalqi bastatan fathkurooalaa Allahi laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(7,70,'7:70','Qaloo aji/tana linaAAbuda Allahawahdahu wanathara ma kana yaAAbudu abaonafa/tina bima taAAiduna in kunta mina assadiqeen');
INSERT INTO "transliteration" VALUES(7,71,'7:71','Qala qad waqaAAa AAalaykum minrabbikum rijsun waghadabun atujadiloonanee fee asma-insammaytumooha antum waabaokum manazzala Allahu biha min sultanin fantathirooinnee maAAakum mina almuntathireen');
INSERT INTO "transliteration" VALUES(7,72,'7:72','Faanjaynahu wallatheenamaAAahu birahmatin minna waqataAAna dabiraallatheena kaththaboo bi-ayatinawama kanoo mu/mineen');
INSERT INTO "transliteration" VALUES(7,73,'7:73','Wa-ila thamooda akhahum salihanqala ya qawmi oAAbudoo Allaha malakum min ilahin ghayruhu qad jaatkum bayyinatunmin rabbikum hathihi naqatu Allahi lakum ayatanfatharooha ta/kul fee ardi Allahi walatamassooha bisoo-in faya/khuthakum AAathabunaleem');
INSERT INTO "transliteration" VALUES(7,74,'7:74','Wathkuroo ithjaAAalakum khulafaa min baAAdi AAadin wabawwaakumfee al-ardi tattakhithoona min suhooliha qusooranwatanhitoona aljibala buyootan fathkurooalaa Allahi wala taAAthaw fee al-ardimufsideen');
INSERT INTO "transliteration" VALUES(7,75,'7:75','Qala almalao allatheenaistakbaroo min qawmihi lillatheena istudAAifooliman amana minhum ataAAlamoona anna salihanmursalun min rabbihi qaloo inna bima orsilabihi mu/minoon');
INSERT INTO "transliteration" VALUES(7,76,'7:76','Qala allatheena istakbaroo innabillathee amantum bihi kafiroon');
INSERT INTO "transliteration" VALUES(7,77,'7:77','FaAAaqaroo annaqata waAAatawAAan amri rabbihim waqaloo ya salihui/tina bima taAAiduna in kunta minaalmursaleen');
INSERT INTO "transliteration" VALUES(7,78,'7:78','Faakhathat-humu arrajfatu faasbahoofee darihim jathimeen');
INSERT INTO "transliteration" VALUES(7,79,'7:79','Fatawalla AAanhum waqala yaqawmi laqad ablaghtukum risalata rabbee wanasahtulakum walakin la tuhibboona annasiheen');
INSERT INTO "transliteration" VALUES(7,80,'7:80','Walootan ith qalaliqawmihi ata/toona alfahishata ma sabaqakum bihamin ahadin mina alAAalameen');
INSERT INTO "transliteration" VALUES(7,81,'7:81','Innakum lata/toona arrijalashahwatan min dooni annisa-i bal antum qawmunmusrifoon');
INSERT INTO "transliteration" VALUES(7,82,'7:82','Wama kana jawabaqawmihi illa an qaloo akhrijoohum min qaryatikuminnahum onasun yatatahharoon');
INSERT INTO "transliteration" VALUES(7,83,'7:83','Faanjaynahu waahlahu illaimraatahu kanat mina alghabireen');
INSERT INTO "transliteration" VALUES(7,84,'7:84','Waamtarna AAalayhim mataranfanthur kayfa kana AAaqibatualmujrimeen');
INSERT INTO "transliteration" VALUES(7,85,'7:85','Wa-ila madyana akhahumshuAAayban qala ya qawmi oAAbudoo Allaha malakum min ilahin ghayruhu qad jaatkum bayyinatunmin rabbikum faawfoo alkayla walmeezana walatabkhasoo annasa ashyaahum walatufsidoo fee al-ardi baAAda islahiha thalikumkhayrun lakum in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(7,86,'7:86','Wala taqAAudoo bikulli siratintooAAidoona watasuddoona AAan sabeeli Allahi man amanabihi watabghoonaha AAiwajan wathkuroo ithkuntum qaleelan fakaththarakum wanthurookayfa kana AAaqibatu almufsideen');
INSERT INTO "transliteration" VALUES(7,87,'7:87','Wa-in kana ta-ifatun minkum amanoobillathee orsiltu bihi wata-ifatun lamyu/minoo fasbiroo hatta yahkumaAllahu baynana wahuwa khayru alhakimeen');
INSERT INTO "transliteration" VALUES(7,88,'7:88','Qala almalao allatheenaistakbaroo min qawmihi lanukhrijannaka ya shuAAaybu wallatheenaamanoo maAAaka min qaryatina aw lataAAoodunna feemillatina qala awa law kunna kariheen');
INSERT INTO "transliteration" VALUES(7,89,'7:89','Qadi iftarayna AAala Allahikathiban in AAudna fee millatikum baAAda ithnajjana Allahu minha wamayakoonu lana an naAAooda feeha illa an yashaaAllahu rabbuna wasiAAa rabbuna kulla shay-inAAilman AAala Allahi tawakkalna rabbanaiftah baynana wabayna qawmina bilhaqqiwaanta khayru alfatiheen');
INSERT INTO "transliteration" VALUES(7,90,'7:90','Waqala almalao allatheenakafaroo min qawmihi la-ini ittabaAAtum shuAAayban innakum ithanlakhasiroon');
INSERT INTO "transliteration" VALUES(7,91,'7:91','Faakhathat-humu arrajfatu faasbahoofee darihim jathimeen');
INSERT INTO "transliteration" VALUES(7,92,'7:92','Allatheena kaththabooshuAAayban kaan lam yaghnaw feeha allatheena kaththabooshuAAayban kanoo humu alkhasireen');
INSERT INTO "transliteration" VALUES(7,93,'7:93','Fatawalla AAanhum waqala yaqawmi laqad ablaghtukum risalati rabbee wanasahtulakum fakayfa asa AAala qawmin kafireen');
INSERT INTO "transliteration" VALUES(7,94,'7:94','Wama arsalna fee qaryatin minnabiyyin illa akhathna ahlaha bilba/sa-iwaddarra-i laAAallahum yaddarraAAoon');
INSERT INTO "transliteration" VALUES(7,95,'7:95','Thumma baddalna makana assayyi-atialhasanata hatta AAafaw waqaloo qadmassa abaana addarraowassarrao faakhathnahum baghtatanwahum la yashAAuroon');
INSERT INTO "transliteration" VALUES(7,96,'7:96','Walaw anna ahla alqura amanoowattaqaw lafatahna AAalayhim barakatinmina assama-i wal-ardi walakinkaththaboo faakhathnahum bima kanooyaksiboon');
INSERT INTO "transliteration" VALUES(7,97,'7:97','Afaamina ahlu alqura an ya/tiyahumba/suna bayatan wahum na-imoon');
INSERT INTO "transliteration" VALUES(7,98,'7:98','Awa amina ahlu alqura an ya/tiyahumba/suna duhan wahum yalAAaboon');
INSERT INTO "transliteration" VALUES(7,99,'7:99','Afaaminoo makra Allahi falaya/manu makra Allahi illa alqawmu alkhasiroon');
INSERT INTO "transliteration" VALUES(7,100,'7:100','Awa lam yahdi lillatheena yarithoonaal-arda min baAAdi ahliha an law nashao asabnahumbithunoobihim wanatbaAAu AAala quloobihimfahum la yasmaAAoon');
INSERT INTO "transliteration" VALUES(7,101,'7:101','Tilka alqura naqussu AAalaykamin anba-iha walaqad jaat-hum rusuluhum bilbayyinatifama kanoo liyu/minoo bima kaththaboomin qablu kathalika yatbaAAu Allahu AAalaquloobi alkafireen');
INSERT INTO "transliteration" VALUES(7,102,'7:102','Wama wajadna li-aktharihimmin AAahdin wa-in wajadna aktharahum lafasiqeen');
INSERT INTO "transliteration" VALUES(7,103,'7:103','Thumma baAAathna min baAAdihim moosabi-ayatina ila firAAawna wamala-ihifathalamoo biha fanuthurkayfa kana AAaqibatu almufsideen');
INSERT INTO "transliteration" VALUES(7,104,'7:104','Waqala moosa yafirAAawnu innee rasoolun min rabbi alAAalameen');
INSERT INTO "transliteration" VALUES(7,105,'7:105','Haqeequn AAala an laaqoola AAala Allahi illa alhaqqa qadji/tukum bibayyinatin min rabbikum faarsil maAAiya banee isra-eel');
INSERT INTO "transliteration" VALUES(7,106,'7:106','Qala in kunta ji/ta bi-ayatinfa/ti biha in kunta mina assadiqeen');
INSERT INTO "transliteration" VALUES(7,107,'7:107','Faalqa AAasahu fa-ithahiya thuAAbanun mubeen');
INSERT INTO "transliteration" VALUES(7,108,'7:108','WanazaAAa yadahu fa-itha hiya baydaolinnathireen');
INSERT INTO "transliteration" VALUES(7,109,'7:109','Qala almalao min qawmi firAAawnainna hatha lasahirun AAaleem');
INSERT INTO "transliteration" VALUES(7,110,'7:110','Yureedu an yukhrijakum min ardikumfamatha ta/muroon');
INSERT INTO "transliteration" VALUES(7,111,'7:111','Qaloo arjih waakhahu waarsilfee almada-ini hashireen');
INSERT INTO "transliteration" VALUES(7,112,'7:112','Ya/tooka bikulli sahirin AAaleem');
INSERT INTO "transliteration" VALUES(7,113,'7:113','Wajaa assaharatufirAAawna qaloo inna lana laajran in kunnanahnu alghalibeen');
INSERT INTO "transliteration" VALUES(7,114,'7:114','Qala naAAam wa-innakum laminaalmuqarrabeen');
INSERT INTO "transliteration" VALUES(7,115,'7:115','Qaloo ya moosa immaan tulqiya wa-imma an nakoona nahnu almulqeen');
INSERT INTO "transliteration" VALUES(7,116,'7:116','Qala alqoo falamma alqaw saharooaAAyuna annasi wastarhaboohum wajaoobisihrin AAatheem');
INSERT INTO "transliteration" VALUES(7,117,'7:117','Waawhayna ila moosaan alqi AAasaka fa-itha hiya talqafu maya/fikoon');
INSERT INTO "transliteration" VALUES(7,118,'7:118','FawaqaAAa alhaqqu wabatala makanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(7,119,'7:119','Faghuliboo hunalika wanqalaboosaghireen');
INSERT INTO "transliteration" VALUES(7,120,'7:120','Waolqiya assaharatu sajideen');
INSERT INTO "transliteration" VALUES(7,121,'7:121','Qaloo amanna birabbialAAalameen');
INSERT INTO "transliteration" VALUES(7,122,'7:122','Rabbi moosa waharoon');
INSERT INTO "transliteration" VALUES(7,123,'7:123','Qala firAAawnu amantum bihiqabla an athana lakum inna hatha lamakrunmakartumoohu fee almadeenati litukhrijoo minha ahlahafasawfa taAAlamoon');
INSERT INTO "transliteration" VALUES(7,124,'7:124','LaoqatiAAanna aydiyakum waarjulakummin khilafin thumma laosallibannakum ajmaAAeen');
INSERT INTO "transliteration" VALUES(7,125,'7:125','Qaloo inna ila rabbinamunqaliboon');
INSERT INTO "transliteration" VALUES(7,126,'7:126','Wama tanqimu minna illaan amanna bi-ayati rabbinalamma jaatna rabbana afrigh AAalaynasabran watawaffana muslimeen');
INSERT INTO "transliteration" VALUES(7,127,'7:127','Waqala almalao min qawmi firAAawnaatatharu moosa waqawmahu liyufsidoo fee al-ardiwayatharaka waalihataka qala sanuqattilu abnaahumwanastahyee nisaahum wa-inna fawqahum qahiroon');
INSERT INTO "transliteration" VALUES(7,128,'7:128','Qala moosa liqawmihiistaAAeenoo billahi wasbiroo innaal-arda lillahi yoorithuha man yashaomin AAibadihi walAAaqibatu lilmuttaqeen');
INSERT INTO "transliteration" VALUES(7,129,'7:129','Qaloo ootheena minqabli an ta/tiyana wamin baAAdi ma ji/tana qalaAAasa rabbukum an yuhlika AAaduwwakum wayastakhlifakum feeal-ardi fayanthura kayfa taAAmaloon');
INSERT INTO "transliteration" VALUES(7,130,'7:130','Walaqad akhathna alafirAAawna bissineena wanaqsin mina aththamaratilaAAallahum yaththakkaroon');
INSERT INTO "transliteration" VALUES(7,131,'7:131','Fa-itha jaat-humu alhasanatuqaloo lana hathihi wa-in tusibhumsayyi-atun yattayyaroo bimoosa waman maAAahu alainnama ta-iruhum AAinda Allahi walakinnaaktharahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(7,132,'7:132','Waqaloo mahma ta/tinabihi min ayatin litasharana biha famanahnu laka bimu/mineen');
INSERT INTO "transliteration" VALUES(7,133,'7:133','Faarsalna AAalayhimu attoofanawaljarada walqummala waddafadiAAawaddama ayatin mufassalatinfastakbaroo wakanoo qawman mujrimeen');
INSERT INTO "transliteration" VALUES(7,134,'7:134','Walamma waqaAAa AAalayhimu arrijzuqaloo ya moosa odAAu lana rabbaka bimaAAahida AAindaka la-in kashafta AAanna arrijzalanu/minanna laka walanursilanna maAAaka banee isra-eel');
INSERT INTO "transliteration" VALUES(7,135,'7:135','Falamma kashafna AAanhumu arrijzaila ajalin hum balighoohu itha humyankuthoon');
INSERT INTO "transliteration" VALUES(7,136,'7:136','Fantaqamna minhum faaghraqnahumfee alyammi bi-annahum kaththaboo bi-ayatinawakanoo AAanha ghafileen');
INSERT INTO "transliteration" VALUES(7,137,'7:137','Waawrathna alqawma allatheenakanoo yustadAAafoona mashariqa al-ardiwamagharibaha allatee barakna feehawatammat kalimatu rabbika alhusna AAalabanee isra-eela bima sabaroo wadammarnama kana yasnaAAu firAAawnu waqawmuhu wamakanoo yaAArishoon');
INSERT INTO "transliteration" VALUES(7,138,'7:138','Wajawazna bibanee isra-eelaalbahra faataw AAala qawmin yaAAkufoona AAalaasnamin lahum qaloo ya moosaijAAal lana ilahan kama lahum alihatunqala innakum qawmun tajhaloon');
INSERT INTO "transliteration" VALUES(7,139,'7:139','Inna haola-i mutabbarun mahum feehi wabatilun ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(7,140,'7:140','Qala aghayra Allahi abgheekumilahan wahuwa faddalakum AAala alAAalameen');
INSERT INTO "transliteration" VALUES(7,141,'7:141','Wa-ith anjaynakum min alifirAAawna yasoomoonakum soo-a alAAathabi yuqattiloona abnaakumwayastahyoona nisaakum wafee thalikum balaonmin rabbikum AAatheem');
INSERT INTO "transliteration" VALUES(7,142,'7:142','WawaAAadna moosa thalatheenalaylatan waatmamnaha biAAashrin fatamma meeqaturabbihi arbaAAeena laylatan waqala moosa li-akheehiharoona okhlufnee fee qawmee waaslih walatattabiAA sabeela almufsideen');
INSERT INTO "transliteration" VALUES(7,143,'7:143','Walamma jaa moosalimeeqatina wakallamahu rabbuhu qala rabbiarinee anthur ilayka qala lan taraneewalakini onthur ila aljabali fa-iniistaqarra makanahu fasawfa taranee falammatajalla rabbuhu liljabali jaAAalahu dakkan wakharra moosasaAAiqan falamma afaqa qala subhanakatubtu ilayka waana awwalu almu/mineen');
INSERT INTO "transliteration" VALUES(7,144,'7:144','Qala ya moosa innee istafaytukaAAala annasi birisalateewabikalamee fakhuth ma ataytuka wakunmina ashshakireen');
INSERT INTO "transliteration" VALUES(7,145,'7:145','Wakatabna lahu fee al-alwahimin kulli shay-in mawAAithatan watafseelanlikulli shay-in fakhuthha biquwwatin wa/mur qawmakaya/khuthoo bi-ahsaniha saoreekum daraalfasiqeen');
INSERT INTO "transliteration" VALUES(7,146,'7:146','Saasrifu AAan ayatiyaallatheena yatakabbaroona fee al-ardi bighayri alhaqqiwa-in yaraw kulla ayatin la yu/minoo bihawa-in yaraw sabeela arrushdi la yattakhithoohusabeelan wa-in yaraw sabeela alghayyi yattakhithoohusabeelan thalika bi-annahum kaththaboo bi-ayatinawakanoo AAanha ghafileen');
INSERT INTO "transliteration" VALUES(7,147,'7:147','Wallatheena kaththaboobi-ayatina waliqa-i al-akhiratihabitat aAAmaluhum hal yujzawna illama kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(7,148,'7:148','Wattakhatha qawmu moosamin baAAdihi min huliyyihim AAijlan jasadan lahu khuwarunalam yaraw annahu la yukallimuhum wala yahdeehimsabeelan ittakhathoohu wakanoo thalimeen');
INSERT INTO "transliteration" VALUES(7,149,'7:149','Walamma suqita fee aydeehimwaraaw annahum qad dalloo qaloo la-in lam yarhamnarabbuna wayaghfir lana lanakoonanna mina alkhasireen');
INSERT INTO "transliteration" VALUES(7,150,'7:150','Walamma rajaAAa moosa ilaqawmihi ghadbana asifan qala bi/samakhalaftumoonee min baAAdee aAAajiltum amra rabbikum waalqa al-alwahawaakhatha bira/si akheehi yajurruhu ilayhi qalaibna omma inna alqawma istadAAafoonee wakadooyaqtuloonanee fala tushmit biya al-aAAdaa walatajAAalnee maAAa alqawmi aththalimeen');
INSERT INTO "transliteration" VALUES(7,151,'7:151','Qala rabbi ighfir lee wali-akheewaadkhilna fee rahmatika waanta arhamu arrahimeen');
INSERT INTO "transliteration" VALUES(7,152,'7:152','Inna allatheena ittakhathooalAAijla sayanaluhum ghadabun min rabbihim wathillatunfee alhayati addunya wakathalikanajzee almuftareen');
INSERT INTO "transliteration" VALUES(7,153,'7:153','Wallatheena AAamiloo assayyi-atithumma taboo min baAAdiha waamanoo innarabbaka min baAAdiha laghafoorun raheem');
INSERT INTO "transliteration" VALUES(7,154,'7:154','Walamma sakata AAan moosaalghadabu akhatha al-alwaha wafee nuskhatihahudan warahmatun lillatheena hum lirabbihimyarhaboon');
INSERT INTO "transliteration" VALUES(7,155,'7:155','Wakhtara moosaqawmahu sabAAeena rajulan limeeqatina falammaakhathat-humu arrajfatu qala rabbi lawshi/ta ahlaktahum min qablu wa-iyyaya atuhlikunabima faAAala assufahao minna in hiyailla fitnatuka tudillu biha man tashaowatahdee man tashao anta waliyyuna faghfirlana warhamna waanta khayru alghafireen');
INSERT INTO "transliteration" VALUES(7,156,'7:156','Waktub lana fee hathihiaddunya hasanatan wafee al-akhiratiinna hudna ilayka qala AAathabee oseebubihi man ashao warahmatee wasiAAat kulla shay-infasaaktubuha lillatheena yattaqoona wayu/toona azzakatawallatheena hum bi-ayatinayu/minoon');
INSERT INTO "transliteration" VALUES(7,157,'7:157','Allatheena yattabiAAoona arrasoolaannabiyya al-ommiyya allathee yajidoonahumaktooban AAindahum fee attawrati wal-injeeliya/muruhum bilmaAAroofi wayanhahum AAani almunkariwayuhillu lahumu attayyibati wayuharrimuAAalayhimu alkhaba-itha wayadaAAu AAanhum israhumwal-aghlala allatee kanat AAalayhim fallatheenaamanoo bihi waAAazzaroohu wanasaroohu wattabaAAooannoora allathee onzila maAAahu ola-ikahumu almuflihoon');
INSERT INTO "transliteration" VALUES(7,158,'7:158','Qul ya ayyuha annasuinnee rasoolu Allahi ilaykum jameeAAan allatheelahu mulku assamawati wal-ardila ilaha illa huwa yuhyee wayumeetufaaminoo billahi warasoolihi annabiyyial-ommiyyi allathee yu/minu billahi wakalimatihiwattabiAAoohu laAAallakum tahtadoon');
INSERT INTO "transliteration" VALUES(7,159,'7:159','Wamin qawmi moosa ommatun yahdoonabilhaqqi wabihi yaAAdiloon');
INSERT INTO "transliteration" VALUES(7,160,'7:160','WaqattaAAnahumu ithnatayAAashrata asbatan omaman waawhayna ilamoosa ithi istasqahu qawmuhu ani idribbiAAasaka alhajara fanbajasat minhu ithnataAAashrata AAaynan qad AAalima kullu onasin mashrabahum wathallalnaAAalayhimu alghamama waanzalna AAalayhimu almannawassalwa kuloo min tayyibati marazaqnakum wama thalamoona walakinkanoo anfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(7,161,'7:161','Wa-ith qeela lahumu oskunoo hathihialqaryata wakuloo minha haythu shi/tum waqooloo hittatunwadkhuloo albaba sujjadan naghfir lakum khatee-atikumsanazeedu almuhsineen');
INSERT INTO "transliteration" VALUES(7,162,'7:162','Fabaddala allatheena thalamoominhum qawlan ghayra allathee qeela lahum faarsalnaAAalayhim rijzan mina assama-i bima kanooyathlimoon');
INSERT INTO "transliteration" VALUES(7,163,'7:163','Was-alhum AAani alqaryati allatee kanathadirata albahri ith yaAAdoona fee assabtiith ta/teehim heetanuhum yawma sabtihimshurraAAan wayawma la yasbitoona la ta/teehim kathalikanabloohum bima kanoo yafsuqoon');
INSERT INTO "transliteration" VALUES(7,164,'7:164','Wa-ith qalat ommatun minhumlima taAAithoona qawman Allahu muhlikuhum awmuAAaththibuhum AAathaban shadeedan qaloomaAAthiratan ila rabbikum walaAAallahum yattaqoon');
INSERT INTO "transliteration" VALUES(7,165,'7:165','Falamma nasoo ma thukkiroobihi anjayna allatheena yanhawna AAani assoo-iwaakhathna allatheena thalamoobiAAathabin ba-eesin bima kanoo yafsuqoon');
INSERT INTO "transliteration" VALUES(7,166,'7:166','Falamma AAataw AAan ma nuhooAAanhu qulna lahum koonoo qiradatan khasi-een');
INSERT INTO "transliteration" VALUES(7,167,'7:167','Wa-ith taaththana rabbukalayabAAathanna AAalayhim ila yawmi alqiyamati manyasoomuhum soo-a alAAathabi inna rabbaka lasareeAAu alAAiqabiwa-innahu laghafoorun raheem');
INSERT INTO "transliteration" VALUES(7,168,'7:168','WaqattaAAnahum fee al-ardiomaman minhumu assalihoona waminhum doona thalikawabalawnahum bilhasanati wassayyi-atilaAAallahum yarjiAAoon');
INSERT INTO "transliteration" VALUES(7,169,'7:169','Fakhalafa min baAAdihim khalfun warithooalkitaba ya/khuthoona AAarada hathaal-adna wayaqooloona sayughfaru lana wa-in ya/tihimAAaradun mithluhu ya/khuthoohu alam yu/khathAAalayhim meethaqu alkitabi an la yaqoolooAAala Allahi illa alhaqqa wadarasoo mafeehi waddaru al-akhiratu khayrun lillatheenayattaqoona afala taAAqiloon');
INSERT INTO "transliteration" VALUES(7,170,'7:170','Wallatheena yumassikoona bilkitabiwaaqamoo assalata inna lanudeeAAu ajra almusliheen');
INSERT INTO "transliteration" VALUES(7,171,'7:171','Wa-ith nataqna aljabalafawqahum kaannahu thullatun wathannooannahu waqiAAun bihim khuthoo ma ataynakumbiquwwatin wathkuroo ma feehi laAAallakumtattaqoon');
INSERT INTO "transliteration" VALUES(7,172,'7:172','Wa-ith akhatha rabbuka minbanee adama min thuhoorihim thurriyyatahumwaashhadahum AAala anfusihim alastu birabbikum qaloobala shahidna an taqooloo yawma alqiyamatiinna kunna AAan hatha ghafileen');
INSERT INTO "transliteration" VALUES(7,173,'7:173','Aw taqooloo innama ashraka abaonamin qablu wakunna thurriyyatan min baAAdihimafatuhlikuna bima faAAala almubtiloon');
INSERT INTO "transliteration" VALUES(7,174,'7:174','Wakathalika nufassilu al-ayatiwalaAAallahum yarjiAAoon');
INSERT INTO "transliteration" VALUES(7,175,'7:175','Watlu AAalayhim nabaa allatheeataynahu ayatina fansalakhaminha faatbaAAahu ashshaytanu fakanamina alghaween');
INSERT INTO "transliteration" VALUES(7,176,'7:176','Walaw shi/na larafaAAnahu bihawalakinnahu akhlada ila al-ardi wattabaAAahawahu famathaluhu kamathali alkalbi in tahmilAAalayhi yalhath aw tatruk-hu yalhath thalika mathalualqawmi allatheena kaththaboo bi-ayatinafaqsusi alqasasa laAAallahumyatafakkaroon');
INSERT INTO "transliteration" VALUES(7,177,'7:177','Saa mathalan alqawmu allatheenakaththaboo bi-ayatina waanfusahum kanooyathlimoon');
INSERT INTO "transliteration" VALUES(7,178,'7:178','Man yahdi Allahu fahuwa almuhtadeewaman yudlil faola-ika humu alkhasiroon');
INSERT INTO "transliteration" VALUES(7,179,'7:179','Walaqad thara/na lijahannamakatheeran mina aljinni wal-insi lahum quloobun layafqahoona biha walahum aAAyunun la yubsiroonabiha walahum athanun la yasmaAAoona bihaola-ika kal-anAAami bal hum adalluola-ika humu alghafiloon');
INSERT INTO "transliteration" VALUES(7,180,'7:180','Walillahi al-asmao alhusnafadAAoohu biha watharoo allatheenayulhidoona fee asma-ihi sayujzawna ma kanooyaAAmaloon');
INSERT INTO "transliteration" VALUES(7,181,'7:181','Wamimman khalaqna ommatun yahdoonabilhaqqi wabihi yaAAdiloon');
INSERT INTO "transliteration" VALUES(7,182,'7:182','Wallatheena kaththaboobi-ayatina sanastadrijuhum min haythula yaAAlamoon');
INSERT INTO "transliteration" VALUES(7,183,'7:183','Waomlee lahum inna kaydee mateen');
INSERT INTO "transliteration" VALUES(7,184,'7:184','Awa lam yatafakkaroo ma bisahibihimmin jinnatin in huwa illa natheerun mubeen');
INSERT INTO "transliteration" VALUES(7,185,'7:185','Awalam yanthuroo feemalakooti assamawati wal-ardiwama khalaqa Allahu min shay-in waan AAasaan yakoona qadi iqtaraba ajaluhum fabi-ayyi hadeethinbaAAdahu yu/minoon');
INSERT INTO "transliteration" VALUES(7,186,'7:186','Man yudlili Allahu falahadiya lahu wayatharuhum fee tughyanihimyaAAmahoon');
INSERT INTO "transliteration" VALUES(7,187,'7:187','Yas-aloonaka AAani assaAAatiayyana mursaha qul innama AAilmuhaAAinda rabbee la yujalleeha liwaqtiha illahuwa thaqulat fee assamawati wal-ardila ta/teekum illa baghtatan yas-aloonaka kaannaka hafiyyunAAanha qul innama AAilmuha AAinda Allahiwalakinna akthara annasi layaAAlamoon');
INSERT INTO "transliteration" VALUES(7,188,'7:188','Qul la amliku linafsee nafAAan waladarran illa ma shaa Allahuwalaw kuntu aAAlamu alghayba lastakthartu mina alkhayriwama massaniya assoo-o in ana illanatheerun wabasheerun liqawmin yu/minoon');
INSERT INTO "transliteration" VALUES(7,189,'7:189','Huwa allathee khalaqakum min nafsinwahidatin wajaAAala minha zawjaha liyaskunailayha falamma taghashshaha hamalathamlan khafeefan famarrat bihi falamma athqalatdaAAawa Allaha rabbahuma la-in ataytanasalihan lanakoonanna mina ashshakireen');
INSERT INTO "transliteration" VALUES(7,190,'7:190','Falamma atahumasalihan jaAAala lahu shurakaa feemaatahuma fataAAala AllahuAAamma yushrikoon');
INSERT INTO "transliteration" VALUES(7,191,'7:191','Ayushrikoona ma la yakhluqushay-an wahum yukhlaqoon');
INSERT INTO "transliteration" VALUES(7,192,'7:192','Wala yastateeAAoona lahum nasranwala anfusahum yansuroon');
INSERT INTO "transliteration" VALUES(7,193,'7:193','Wa-in tadAAoohum ila alhuda layattabiAAookum sawaon AAalaykum adaAAawtumoohum am antum samitoon');
INSERT INTO "transliteration" VALUES(7,194,'7:194','Inna allatheena tadAAoona min dooniAllahi AAibadun amthalukum fadAAoohumfalyastajeeboo lakum in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(7,195,'7:195','Alahum arjulun yamshoona biha amlahum aydin yabtishoona biha am lahum aAAyunun yubsiroonabiha am lahum athanun yasmaAAoona biha quliodAAoo shurakaakum thumma keedooni fala tunthiroon');
INSERT INTO "transliteration" VALUES(7,196,'7:196','Inna waliyyiya Allahu allatheenazzala alkitaba wahuwa yatawalla assaliheen');
INSERT INTO "transliteration" VALUES(7,197,'7:197','Wallatheena tadAAoona mindoonihi la yastateeAAoona nasrakum walaanfusahum yansuroon');
INSERT INTO "transliteration" VALUES(7,198,'7:198','Wa-in tadAAoohum ila alhuda layasmaAAoo watarahum yanthuroona ilayka wahumla yubsiroon');
INSERT INTO "transliteration" VALUES(7,199,'7:199','Khuthi alAAafwa wa/mur bilAAurfiwaaAArid AAani aljahileen');
INSERT INTO "transliteration" VALUES(7,200,'7:200','Wa-imma yanzaghannaka mina ashshaytaninazghun fastaAAith billahi innahusameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(7,201,'7:201','Inna allatheena ittaqaw ithamassahum ta-ifun mina ashshaytani tathakkaroofa-itha hum mubsiroon');
INSERT INTO "transliteration" VALUES(7,202,'7:202','Wa-ikhwanuhum yamuddoonahum feealghayyi thumma la yuqsiroon');
INSERT INTO "transliteration" VALUES(7,203,'7:203','Wa-itha lam ta/tihim bi-ayatinqaloo lawla ijtabaytaha qul innamaattabiAAu ma yooha ilayya min rabbee hathabasa-iru min rabbikum wahudan warahmatun liqawminyu/minoon');
INSERT INTO "transliteration" VALUES(7,204,'7:204','Wa-itha quri-a alqur-anu fastamiAAoolahu waansitoo laAAallakum turhamoon');
INSERT INTO "transliteration" VALUES(7,205,'7:205','Wathkur rabbaka fee nafsikatadarruAAan wakheefatan wadoona aljahri mina alqawli bilghuduwwiwal-asali wala takun mina alghafileen');
INSERT INTO "transliteration" VALUES(7,206,'7:206','Inna allatheena AAinda rabbika layastakbiroona AAan AAibadatihi wayusabbihoonahuwalahu yasjudoon');
INSERT INTO "transliteration" VALUES(8,1,'8:1','Yas-aloonaka AAani al-anfali qulial-anfalu lillahi warrasooli fattaqooAllaha waaslihoo thata baynikum waateeAAooAllaha warasoolahu in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(8,2,'8:2','Innama almu/minoona allatheenaitha thukira Allahu wajilat quloobuhum wa-ithatuliyat AAalayhim ayatuhu zadat-hum eemananwaAAala rabbihim yatawakkaloon');
INSERT INTO "transliteration" VALUES(8,3,'8:3','Allatheena yuqeemoona assalatawamimma razaqnahum yunfiqoon');
INSERT INTO "transliteration" VALUES(8,4,'8:4','Ola-ika humu almu/minoona haqqanlahum darajatun AAinda rabbihim wamaghfiratun warizqunkareem');
INSERT INTO "transliteration" VALUES(8,5,'8:5','Kama akhrajaka rabbuka min baytika bilhaqqiwa-inna fareeqan mina almu/mineena lakarihoon');
INSERT INTO "transliteration" VALUES(8,6,'8:6','Yujadiloonaka fee alhaqqibaAAda ma tabayyana kaannama yusaqoona ilaalmawti wahum yanthuroon');
INSERT INTO "transliteration" VALUES(8,7,'8:7','Wa-ith yaAAidukumu Allahu ihdaatta-ifatayni annaha lakum watawaddoonaanna ghayra thati ashshawkati takoonu lakumwayureedu Allahu an yuhiqqa alhaqqa bikalimatihiwayaqtaAAa dabira alkafireen');
INSERT INTO "transliteration" VALUES(8,8,'8:8','Liyuhiqqa alhaqqa wayubtilaalbatila walaw kariha almujrimoon');
INSERT INTO "transliteration" VALUES(8,9,'8:9','Ith tastagheethoona rabbakum fastajabalakum annee mumiddukum bi-alfin mina almala-ikatimurdifeen');
INSERT INTO "transliteration" VALUES(8,10,'8:10','Wama jaAAalahu Allahu illabushra walitatma-inna bihi quloobukum wama annasruilla min AAindi Allahi inna Allaha AAazeezunhakeem');
INSERT INTO "transliteration" VALUES(8,11,'8:11','Ith yughashsheekumu annuAAasaamanatan minhu wayunazzilu AAalaykum mina assama-imaan liyutahhirakum bihi wayuthhiba AAankumrijza ashshaytani waliyarbita AAalaquloobikum wayuthabbita bihi al-aqdam');
INSERT INTO "transliteration" VALUES(8,12,'8:12','Ith yoohee rabbuka ilaalmala-ikati annee maAAakum fathabbitoo allatheena amanoosaolqee fee quloobi allatheena kafaroo arruAAba fadriboofawqa al-aAAnaqi wadriboo minhum kulla banan');
INSERT INTO "transliteration" VALUES(8,13,'8:13','Thalika bi-annahum shaqqoo Allahawarasoolahu waman yushaqiqi Allaha warasoolahufa-inna Allaha shadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(8,14,'8:14','Thalikum fathooqoohu waannalilkafireena AAathaba annar');
INSERT INTO "transliteration" VALUES(8,15,'8:15','Ya ayyuha allatheena amanooitha laqeetumu allatheena kafaroo zahfan falatuwalloohumu al-adbar');
INSERT INTO "transliteration" VALUES(8,16,'8:16','Waman yuwallihim yawma-ithin duburahuilla mutaharrifan liqitalin aw mutahayyizanila fi-atin faqad baa bighadabin mina Allahiwama/wahu jahannamu wabi/sa almaseer');
INSERT INTO "transliteration" VALUES(8,17,'8:17','Falam taqtuloohum walakinna Allahaqatalahum wama ramayta ith ramayta walakinnaAllaha rama waliyubliya almu/mineena minhu balaanhasanan inna Allaha sameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(8,18,'8:18','Thalikum waanna Allaha moohinukaydi alkafireen');
INSERT INTO "transliteration" VALUES(8,19,'8:19','In tastaftihoo faqad jaakumualfathu wa-in tantahoo fahuwa khayrun lakum wa-intaAAoodoo naAAud walan tughniya AAankum fi-atukum shay-an walawkathurat waanna Allaha maAAa almu/mineen');
INSERT INTO "transliteration" VALUES(8,20,'8:20','Ya ayyuha allatheena amanooateeAAoo Allaha warasoolahu wala tawallawAAanhu waantum tasmaAAoon');
INSERT INTO "transliteration" VALUES(8,21,'8:21','Wala takoonoo kallatheenaqaloo samiAAna wahum la yasmaAAoon');
INSERT INTO "transliteration" VALUES(8,22,'8:22','Inna sharra addawabbi AAindaAllahi assummu albukmu allatheena layaAAqiloon');
INSERT INTO "transliteration" VALUES(8,23,'8:23','Walaw AAalima Allahu feehim khayranlaasmaAAahum walaw asmaAAahum latawallaw wahum muAAridoon');
INSERT INTO "transliteration" VALUES(8,24,'8:24','Yaayyuha allatheena amanooistajeeboo lillahi walirrasooli itha daAAakumlima yuhyeekum waAAlamoo anna Allahayahoolu bayna almar-i waqalbihi waannahu ilayhi tuhsharoon');
INSERT INTO "transliteration" VALUES(8,25,'8:25','Wattaqoo fitnatan la tuseebannaallatheena thalamoo minkum khassatanwaAAlamoo anna Allaha shadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(8,26,'8:26','Wathkuroo ith antumqaleelun mustadAAafoona fee al-ardi takhafoonaan yatakhattafakumu annasu faawakumwaayyadakum binasrihi warazaqakum mina attayyibatilaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(8,27,'8:27','Ya ayyuha allatheena amanoola takhoonoo Allaha warrasoola watakhoonooamanatikum waantum taAAlamoon');
INSERT INTO "transliteration" VALUES(8,28,'8:28','WaAAlamoo annama amwalukumwaawladukum fitnatun waanna Allaha AAindahu ajrunAAatheem');
INSERT INTO "transliteration" VALUES(8,29,'8:29','Ya ayyuha allatheena amanooin tattaqoo Allaha yajAAal lakum furqananwayukaffir AAankum sayyi-atikum wayaghfir lakum wallahuthoo alfadli alAAatheem');
INSERT INTO "transliteration" VALUES(8,30,'8:30','Wa-ith yamkuru bika allatheenakafaroo liyuthbitooka aw yaqtulooka aw yukhrijooka wayamkuroonawayamkuru Allahu wallahu khayru almakireen');
INSERT INTO "transliteration" VALUES(8,31,'8:31','Wa-itha tutla AAalayhim ayatunaqaloo qad samiAAna law nashao laqulnamithla hatha in hatha illa asateerual-awwaleen');
INSERT INTO "transliteration" VALUES(8,32,'8:32','Wa-ith qaloo allahummain kana hatha huwa alhaqqa min AAindika faamtirAAalayna hijaratan mina assama-iawi i/tina biAAathabin aleem');
INSERT INTO "transliteration" VALUES(8,33,'8:33','Wama kana AllahuliyuAAaththibahum waanta feehim wama kanaAllahu muAAaththibahum wahum yastaghfiroon');
INSERT INTO "transliteration" VALUES(8,34,'8:34','Wama lahum alla yuAAaththibahumuAllahu wahum yasuddoona AAani almasjidi alharamiwama kanoo awliyaahu in awliyaohu illaalmuttaqoona walakinna aktharahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(8,35,'8:35','Wama kana salatuhumAAinda albayti illa mukaan watasdiyatan fathooqooalAAathaba bima kuntum takfuroon');
INSERT INTO "transliteration" VALUES(8,36,'8:36','Inna allatheena kafaroo yunfiqoonaamwalahum liyasuddoo AAan sabeeli Allahifasayunfiqoonaha thumma takoonu AAalayhim hasratanthumma yughlaboona wallatheena kafaroo ilajahannama yuhsharoon');
INSERT INTO "transliteration" VALUES(8,37,'8:37','Liyameeza Allahu alkhabeetha mina attayyibiwayajAAala alkhabeetha baAAdahu AAala baAAdinfayarkumahu jameeAAan fayajAAalahu fee jahannama ola-ikahumu alkhasiroon');
INSERT INTO "transliteration" VALUES(8,38,'8:38','Qul lillatheena kafaroo in yantahooyughfar lahum ma qad salafa wa-in yaAAoodoo faqad madatsunnatu al-awwaleen');
INSERT INTO "transliteration" VALUES(8,39,'8:39','Waqatiloohum hatta latakoona fitnatun wayakoona addeenu kulluhu lillahifa-ini intahaw fa-inna Allaha bima yaAAmaloona baseer');
INSERT INTO "transliteration" VALUES(8,40,'8:40','Wa-in tawallaw faAAlamoo anna Allahamawlakum niAAma almawla waniAAma annaseer');
INSERT INTO "transliteration" VALUES(8,41,'8:41','WaAAlamoo annama ghanimtummin shay-in faanna lillahi khumusahu walirrasooliwalithee alqurba walyatama walmasakeeniwabni assabeeli in kuntum amantum billahiwama anzalna AAala AAabdina yawmaalfurqani yawma iltaqa aljamAAani wallahuAAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(8,42,'8:42','Ith antum bilAAudwati addunyawahum bilAAudwati alquswa warrakbuasfala minkum walaw tawaAAadtum lakhtalaftum feealmeeAAadi walakin liyaqdiya Allahuamran kana mafAAoolan liyahlika man halaka AAan bayyinatinwayahya man hayya AAan bayyinatin wa-innaAllaha lasameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(8,43,'8:43','Ith yureekahumu Allahu fee manamikaqaleelan walaw arakahum katheeran lafashiltum walatanazaAAtumfee al-amri walakinna Allaha sallama innahuAAaleemun bithati assudoor');
INSERT INTO "transliteration" VALUES(8,44,'8:44','Wa-ith yureekumoohum ithiiltaqaytum fee aAAyunikum qaleelan wayuqallilukum fee aAAyunihimliyaqdiya Allahu amran kana mafAAoolan wa-ilaAllahi turjaAAu al-omoor');
INSERT INTO "transliteration" VALUES(8,45,'8:45','Ya ayyuha allatheena amanooitha laqeetum fi-atan fathbutoo wathkurooAllaha katheeran laAAallakum tuflihoon');
INSERT INTO "transliteration" VALUES(8,46,'8:46','WaateeAAoo Allaha warasoolahuwala tanazaAAoo fatafshaloo watathhaba reehukumwasbiroo inna Allaha maAAa assabireen');
INSERT INTO "transliteration" VALUES(8,47,'8:47','Wala takoonoo kallatheenakharajoo min diyarihim bataran wari-aa annasiwayasuddoona AAan sabeeli Allahi wallahubima yaAAmaloona muheet');
INSERT INTO "transliteration" VALUES(8,48,'8:48','Wa-ith zayyana lahumu ashshaytanuaAAmalahum waqala la ghaliba lakumualyawma mina annasi wa-innee jarun lakumfalamma taraati alfi-atani nakasaAAala AAaqibayhi waqala innee baree-on minkum inneeara ma la tarawna innee akhafu Allahawallahu shadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(8,49,'8:49','Ith yaqoolu almunafiqoona wallatheenafee quloobihim maradun gharra haola-ideenuhum waman yatawakkal AAala Allahi fa-inna AllahaAAazeezun hakeem');
INSERT INTO "transliteration" VALUES(8,50,'8:50','Walaw tara ith yatawaffaallatheena kafaroo almala-ikatu yadriboonawujoohahum waadbarahum wathooqoo AAathaba alhareeq');
INSERT INTO "transliteration" VALUES(8,51,'8:51','Thalika bima qaddamat aydeekumwaanna Allaha laysa bithallaminlilAAabeed');
INSERT INTO "transliteration" VALUES(8,52,'8:52','Kada/bi ali firAAawna wallatheenamin qablihim kafaroo bi-ayati Allahi faakhathahumuAllahu bithunoobihim inna Allaha qawiyyunshadeedu alAAiqab');
INSERT INTO "transliteration" VALUES(8,53,'8:53','Thalika bi-anna Allaha lamyaku mughayyiran niAAmatan anAAamaha AAala qawmin hattayughayyiroo ma bi-anfusihim waanna Allaha sameeAAunAAaleem');
INSERT INTO "transliteration" VALUES(8,54,'8:54','Kada/bi ali firAAawna wallatheenamin qablihim kaththaboo bi-ayati rabbihimfaahlaknahum bithunoobihim waaghraqna alafirAAawna wakullun kanoo thalimeen');
INSERT INTO "transliteration" VALUES(8,55,'8:55','Inna sharra addawabbi AAindaAllahi allatheena kafaroo fahum la yu/minoon');
INSERT INTO "transliteration" VALUES(8,56,'8:56','Allatheena AAahadta minhumthumma yanqudoona AAahdahum fee kulli marratin wahum layattaqoon');
INSERT INTO "transliteration" VALUES(8,57,'8:57','Fa-imma tathqafannahum fee alharbifasharrid bihim man khalfahum laAAallahum yaththakkaroon');
INSERT INTO "transliteration" VALUES(8,58,'8:58','Wa-imma takhafanna min qawminkhiyanatan fanbith ilayhim AAalasawa-in inna Allaha la yuhibbu alkha-ineen');
INSERT INTO "transliteration" VALUES(8,59,'8:59','Wala yahsabanna allatheenakafaroo sabaqoo innahum la yuAAjizoon');
INSERT INTO "transliteration" VALUES(8,60,'8:60','WaaAAiddoo lahum ma istataAAtummin quwwatin wamin ribati alkhayli turhiboona bihiAAaduwwa Allahi waAAaduwwakum waakhareena mindoonihim la taAAlamoonahumu Allahu yaAAlamuhum wamatunfiqoo min shay-in fee sabeeli Allahi yuwaffa ilaykumwaantum la tuthlamoon');
INSERT INTO "transliteration" VALUES(8,61,'8:61','Wa-in janahoo lissalmi fajnahlaha watawakkal AAala Allahi innahu huwa assameeAAualAAaleem');
INSERT INTO "transliteration" VALUES(8,62,'8:62','Wa-in yureedoo an yakhdaAAooka fa-inna hasbakaAllahu huwa allathee ayyadaka binasrihi wabilmu/mineen');
INSERT INTO "transliteration" VALUES(8,63,'8:63','Waallafa bayna quloobihim law anfaqta mafee al-ardi jameeAAan ma allafta bayna quloobihimwalakinna Allaha allafa baynahum innahu AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(8,64,'8:64','Ya ayyuha annabiyyu hasbukaAllahu wamani ittabaAAaka mina almu/mineen');
INSERT INTO "transliteration" VALUES(8,65,'8:65','Ya ayyuha annabiyyu harridialmu/mineena AAala alqitali in yakun minkumAAishroona sabiroona yaghliboo mi-atayni wa-in yakunminkum mi-atun yaghliboo alfan mina allatheena kafaroobi-annahum qawmun la yafqahoon');
INSERT INTO "transliteration" VALUES(8,66,'8:66','Al-ana khaffafa Allahu AAankumwaAAalima anna feekum daAAfan fa-in yakun minkum mi-atun sabiratunyaghliboo mi-atayni wa-in yakun minkum alfun yaghliboo alfaynibi-ithni Allahi wallahu maAAa assabireen');
INSERT INTO "transliteration" VALUES(8,67,'8:67','Ma kana linabiyyin an yakoonalahu asra hatta yuthkhina fee al-arditureedoona AAarada addunya wallahuyureedu al-akhirata wallahu AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(8,68,'8:68','Lawla kitabun mina Allahisabaqa lamassakum feema akhathtum AAathabunAAatheem');
INSERT INTO "transliteration" VALUES(8,69,'8:69','Fakuloo mimma ghanimtum halalantayyiban wattaqoo Allaha inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(8,70,'8:70','Ya ayyuha annabiyyuqul liman fee aydeekum mina al-asra in yaAAlami Allahufee quloobikum khayran yu/tikum khayran mimma okhithaminkum wayaghfir lakum wallahu ghafoorun raheem');
INSERT INTO "transliteration" VALUES(8,71,'8:71','Wa-in yureedoo khiyanataka faqad khanooAllaha min qablu faamkana minhum wallahuAAaleemun hakeem');
INSERT INTO "transliteration" VALUES(8,72,'8:72','Inna allatheena amanoo wahajaroowajahadoo bi-amwalihim waanfusihim fee sabeeli Allahiwallatheena awaw wanasaroo ola-ikabaAAduhum awliyao baAAdin wallatheenaamanoo walam yuhajiroo ma lakum min walayatihimmin shay-in hatta yuhajiroo wa-ini istansarookumfee addeeni faAAalaykumu annasru illaAAala qawmin baynakum wabaynahum meethaqun wallahubima taAAmaloona baseer');
INSERT INTO "transliteration" VALUES(8,73,'8:73','Wallatheena kafaroo baAAduhumawliyao baAAdin illa tafAAaloohu takunfitnatun fee al-ardi wafasadun kabeer');
INSERT INTO "transliteration" VALUES(8,74,'8:74','Wallatheena amanoo wahajaroowajahadoo fee sabeeli Allahi wallatheenaawaw wanasaroo ola-ika humu almu/minoona haqqanlahum maghfiratun warizqun kareem');
INSERT INTO "transliteration" VALUES(8,75,'8:75','Wallatheena amanoo minbaAAdu wahajaroo wajahadoo maAAakum faola-ikaminkum waoloo al-arhami baAAduhum awlabibaAAdin fee kitabi Allahi inna Allahabikulli shay-in AAaleem');
INSERT INTO "transliteration" VALUES(9,1,'9:1','Baraatun mina Allahiwarasoolihi ila allatheena AAahadtum minaalmushrikeen');
INSERT INTO "transliteration" VALUES(9,2,'9:2','Faseehoo fee al-ardi arbaAAataashhurin waAAlamoo annakum ghayru muAAjizee Allahiwaanna Allaha mukhzee alkafireen');
INSERT INTO "transliteration" VALUES(9,3,'9:3','Waathanun mina Allahiwarasoolihi ila annasi yawma alhajjial-akbari anna Allaha baree-on mina almushrikeenawarasooluhu fa-in tubtum fahuwa khayrun lakum wa-in tawallaytumfaAAlamoo annakum ghayru muAAjizee Allahiwabashshiri allatheena kafaroo biAAathabin aleem');
INSERT INTO "transliteration" VALUES(9,4,'9:4','Illa allatheena AAahadtummina almushrikeena thumma lam yanqusookum shay-an walam yuthahirooAAalaykum ahadan faatimmoo ilayhim AAahdahum ilamuddatihim inna Allaha yuhibbu almuttaqeen');
INSERT INTO "transliteration" VALUES(9,5,'9:5','Fa-itha insalakha al-ashhuru alhurumufaqtuloo almushrikeena haythu wajadtumoohum wakhuthoohumwahsuroohum waqAAudoo lahum kulla marsadinfa-in taboo waaqamoo assalatawaatawoo azzakata fakhalloo sabeelahum innaAllaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(9,6,'9:6','Wa-in ahadun mina almushrikeena istajarakafaajirhu hatta yasmaAAa kalama Allahithumma ablighhu ma/manahu thalika bi-annahum qawmun layaAAlamoon');
INSERT INTO "transliteration" VALUES(9,7,'9:7','Kayfa yakoonu lilmushrikeena AAahdun AAindaAllahi waAAinda rasoolihi illa allatheena AAahadtumAAinda almasjidi alharami fama istaqamoolakum fastaqeemoo lahum inna Allaha yuhibbualmuttaqeen');
INSERT INTO "transliteration" VALUES(9,8,'9:8','Kayfa wa-in yathharoo AAalaykumla yarquboo feekum illan wala thimmatan yurdoonakumbi-afwahihim wata/ba quloobuhum waaktharuhum fasiqoon');
INSERT INTO "transliteration" VALUES(9,9,'9:9','Ishtaraw bi-ayati Allahithamanan qaleelan fasaddoo AAan sabeelihi innahum saama kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(9,10,'9:10','La yarquboona fee mu/minin illan walathimmatan waola-ika humu almuAAtadoon');
INSERT INTO "transliteration" VALUES(9,11,'9:11','Fa-in taboo waaqamoo assalatawaatawoo azzakata fa-ikhwanukum feeaddeeni wanufassilu al-ayatiliqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(9,12,'9:12','Wa-in nakathoo aymanahum min baAAdiAAahdihim wataAAanoo fee deenikum faqatilooa-immata alkufri innahum la aymana lahumlaAAallahum yantahoon');
INSERT INTO "transliteration" VALUES(9,13,'9:13','Ala tuqatiloona qawmannakathoo aymanahum wahammoo bi-ikhraji arrasooliwahum badaookum awwala marratin atakhshawnahum fallahuahaqqu an takhshawhu in kuntum mu/mineen');
INSERT INTO "transliteration" VALUES(9,14,'9:14','Qatiloohum yuAAaththibhumu Allahubi-aydeekum wayukhzihim wayansurkum AAalayhim wayashfi sudooraqawmin mu/mineen');
INSERT INTO "transliteration" VALUES(9,15,'9:15','Wayuthhib ghaythaquloobihim wayatoobu Allahu AAala man yashaowallahu AAaleemun hakeem');
INSERT INTO "transliteration" VALUES(9,16,'9:16','Am hasibtum an tutrakoo walammayaAAlami Allahu allatheena jahadoo minkumwalam yattakhithoo min dooni Allahi walarasoolihi wala almu/mineena waleejatan wallahukhabeerun bima taAAmaloon');
INSERT INTO "transliteration" VALUES(9,17,'9:17','Ma kana lilmushrikeena anyaAAmuroo masajida Allahi shahideena AAalaanfusihim bilkufri ola-ika habitataAAmaluhum wafee annari hum khalidoon');
INSERT INTO "transliteration" VALUES(9,18,'9:18','Innama yaAAmuru masajida Allahiman amana billahi walyawmi al-akhiriwaaqama assalata waataazzakata walam yakhsha illa AllahafaAAasa ola-ika an yakoonoo mina almuhtadeen');
INSERT INTO "transliteration" VALUES(9,19,'9:19','AjaAAaltum siqayata alhajjiwaAAimarata almasjidi alharami kaman amanabillahi walyawmi al-akhiri wajahadafee sabeeli Allahi la yastawoona AAinda Allahiwallahu la yahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(9,20,'9:20','Allatheena amanoo wahajaroowajahadoo fee sabeeli Allahi bi-amwalihimwaanfusihim aAAthamu darajatan AAinda Allahiwaola-ika humu alfa-izoon');
INSERT INTO "transliteration" VALUES(9,21,'9:21','Yubashshiruhum rabbuhum birahmatinminhu waridwanin wajannatin lahum feehanaAAeemun muqeem');
INSERT INTO "transliteration" VALUES(9,22,'9:22','Khalideena feeha abadan innaAllaha AAindahu ajrun AAatheem');
INSERT INTO "transliteration" VALUES(9,23,'9:23','Ya ayyuha allatheena amanoola tattakhithoo abaakum wa-ikhwanakumawliyaa ini istahabboo alkufra AAala al-eemaniwaman yatawallahum minkum faola-ika humu aththalimoon');
INSERT INTO "transliteration" VALUES(9,24,'9:24','Qul in kana abaokumwaabnaokum wa-ikhwanukum waazwajukumwaAAasheeratukum waamwalun iqtaraftumooha watijaratuntakhshawna kasadaha wamasakinu tardawnahaahabba ilaykum mina Allahi warasoolihi wajihadinfee sabeelihi fatarabbasoo hatta ya/tiya Allahubi-amrihi wallahu la yahdee alqawma alfasiqeen');
INSERT INTO "transliteration" VALUES(9,25,'9:25','Laqad nasarakumu Allahu feemawatina katheeratin wayawma hunaynin ithaAAjabatkum kathratukum falam tughni AAankum shay-an wadaqatAAalaykumu al-ardu bima rahubat thummawallaytum mudbireen');
INSERT INTO "transliteration" VALUES(9,26,'9:26','Thumma anzala Allahu sakeenatahu AAalarasoolihi waAAala almu/mineena waanzala junoodan lamtarawha waAAaththaba allatheena kafaroo wathalikajazao alkafireen');
INSERT INTO "transliteration" VALUES(9,27,'9:27','Thumma yatoobu Allahu min baAAdi thalikaAAala man yashao wallahu ghafoorunraheem');
INSERT INTO "transliteration" VALUES(9,28,'9:28','Ya ayyuha allatheena amanooinnama almushrikoona najasun fala yaqrabooalmasjida alharama baAAda AAamihim hathawa-in khiftum AAaylatan fasawfa yughneekumu Allahu min fadlihiin shaa inna Allaha AAaleemun hakeem');
INSERT INTO "transliteration" VALUES(9,29,'9:29','Qatiloo allatheena layu/minoona billahi wala bilyawmial-akhiri wala yuharrimoona ma harramaAllahu warasooluhu wala yadeenoona deena alhaqqimina allatheena ootoo alkitaba hattayuAAtoo aljizyata AAan yadin wahum saghiroon');
INSERT INTO "transliteration" VALUES(9,30,'9:30','Waqalati alyahoodu AAuzayrun ibnu Allahiwaqalati annasara almaseehuibnu Allahi thalika qawluhum bi-afwahihim yudahi-oonaqawla allatheena kafaroo min qablu qatalahumu Allahuanna yu/fakoon');
INSERT INTO "transliteration" VALUES(9,31,'9:31','Ittakhathoo ahbarahumwaruhbanahum arbaban min dooni Allahi walmaseehaibna maryama wama omiroo illa liyaAAbudoo ilahanwahidan la ilaha illa huwa subhanahuAAamma yushrikoon');
INSERT INTO "transliteration" VALUES(9,32,'9:32','Yureedoona an yutfi-oo noora Allahibi-afwahihim waya/ba Allahu illa anyutimma noorahu walaw kariha alkafiroon');
INSERT INTO "transliteration" VALUES(9,33,'9:33','Huwa allathee arsala rasoolahu bilhudawadeeni alhaqqi liyuthhirahu AAala addeenikullihi walaw kariha almushrikoon');
INSERT INTO "transliteration" VALUES(9,34,'9:34','Ya ayyuha allatheena amanooinna katheeran mina al-ahbari warruhbanilaya/kuloona amwala annasi bilbatiliwayasuddoona AAan sabeeli Allahi wallatheenayaknizoona aththahaba walfiddatawala yunfiqoonaha fee sabeeli Allahifabashshirhum biAAathabin aleem');
INSERT INTO "transliteration" VALUES(9,35,'9:35','Yawma yuhma AAalayhafee nari jahannama fatukwa biha jibahuhumwajunoobuhum wathuhooruhum hatha makanaztum li-anfusikum fathooqoo ma kuntum taknizoon');
INSERT INTO "transliteration" VALUES(9,36,'9:36','Inna AAiddata ashshuhoori AAinda Allahiithna AAashara shahran fee kitabi Allahiyawma khalaqa assamawati wal-ardaminha arbaAAatun hurumun thalika addeenualqayyimu fala tathlimoo feehinna anfusakumwaqatiloo almushrikeena kaffatan kama yuqatiloonakumkaffatan waAAlamoo anna Allaha maAAaalmuttaqeen');
INSERT INTO "transliteration" VALUES(9,37,'9:37','Innama annasee-o ziyadatunfee alkufri yudallu bihi allatheena kafaroo yuhilloonahuAAaman wayuharrimoonahu AAaman liyuwati-ooAAiddata ma harrama Allahu fayuhillooma harrama Allahu zuyyina lahum soo-o aAAmalihimwallahu la yahdee alqawma alkafireen');
INSERT INTO "transliteration" VALUES(9,38,'9:38','Ya ayyuha allatheena amanooma lakum itha qeela lakumu infiroo fee sabeeli Allahiiththaqaltum ila al-ardi aradeetum bilhayatiaddunya mina al-akhirati fama mataAAualhayati addunya fee al-akhiratiilla qaleel');
INSERT INTO "transliteration" VALUES(9,39,'9:39','Illa tanfiroo yuAAaththibkumAAathaban aleeman wayastabdil qawman ghayrakum walatadurroohu shay-an wallahu AAalakulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(9,40,'9:40','Illa tansuroohu faqad nasarahuAllahu ith akhrajahu allatheena kafaroo thaniyaithnayni ith huma fee alghari ithyaqoolu lisahibihi la tahzan inna AllahamaAAana faanzala Allahu sakeenatahu AAalayhiwaayyadahu bijunoodin lam tarawha wajaAAala kalimata allatheenakafaroo assufla wakalimatu Allahi hiyaalAAulya wallahu AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(9,41,'9:41','Infiroo khifafan wathiqalanwajahidoo bi-amwalikum waanfusikum fee sabeeli Allahithalikum khayrun lakum in kuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(9,42,'9:42','Law kana AAaradan qareebanwasafaran qasidan lattabaAAooka walakinbaAAudat AAalayhimu ashshuqqatu wasayahlifoona billahilawi istataAAna lakharajna maAAakumyuhlikoona anfusahum wallahu yaAAlamu innahum lakathiboon');
INSERT INTO "transliteration" VALUES(9,43,'9:43','AAafa Allahu AAanka lima athintalahum hatta yatabayyana laka allatheena sadaqoowataAAlama alkathibeen');
INSERT INTO "transliteration" VALUES(9,44,'9:44','La yasta/thinuka allatheenayu/minoona billahi walyawmi al-akhirian yujahidoo bi-amwalihim waanfusihim wallahuAAaleemun bilmuttaqeen');
INSERT INTO "transliteration" VALUES(9,45,'9:45','Innama yasta/thinuka allatheenala yu/minoona billahi walyawmi al-akhiriwartabat quloobuhum fahum fee raybihimyataraddadoon');
INSERT INTO "transliteration" VALUES(9,46,'9:46','Walaw aradoo alkhurooja laaAAaddoolahu AAuddatan walakin kariha Allahu inbiAAathahumfathabbatahum waqeela oqAAudoo maAAa alqaAAideen');
INSERT INTO "transliteration" VALUES(9,47,'9:47','Law kharajoo feekum ma zadookumilla khabalan walaawdaAAoo khilalakumyabghoonakumu alfitnata wafeekum sammaAAoona lahum wallahuAAaleemun biththalimeen');
INSERT INTO "transliteration" VALUES(9,48,'9:48','Laqadi ibtaghawoo alfitnata min qabluwaqallaboo laka al-omoora hatta jaa alhaqquwathahara amru Allahi wahum karihoon');
INSERT INTO "transliteration" VALUES(9,49,'9:49','Waminhum man yaqoolu i/than lee walataftinnee ala fee alfitnati saqatoo wa-innajahannama lamuheetatun bilkafireen');
INSERT INTO "transliteration" VALUES(9,50,'9:50','In tusibka hasanatun tasu/humwa-in tusibka museebatun yaqooloo qad akhathnaamrana min qablu wayatawallaw wahum farihoon');
INSERT INTO "transliteration" VALUES(9,51,'9:51','Qul lan yuseebana illama kataba Allahu lana huwa mawlanawaAAala Allahi falyatawakkali almu/minoon');
INSERT INTO "transliteration" VALUES(9,52,'9:52','Qul hal tarabbasoona bina illaihda alhusnayayni wanahnu natarabbasubikum an yuseebakumu Allahu biAAathabin minAAindihi aw bi-aydeena fatarabbasoo innamaAAakum mutarabbisoon');
INSERT INTO "transliteration" VALUES(9,53,'9:53','Qul anfiqoo tawAAan aw karhan lanyutaqabbala minkum innakum kuntum qawman fasiqeen');
INSERT INTO "transliteration" VALUES(9,54,'9:54','Wama manaAAahum an tuqbala minhumnafaqatuhum illa annahum kafaroo billahiwabirasoolihi wala ya/toona assalatailla wahum kusala wala yunfiqoona illawahum karihoon');
INSERT INTO "transliteration" VALUES(9,55,'9:55','Fala tuAAjibka amwaluhum walaawladuhum innama yureedu Allahu liyuAAaththibahumbiha fee alhayati addunyawatazhaqa anfusuhum wahum kafiroon');
INSERT INTO "transliteration" VALUES(9,56,'9:56','Wayahlifoona billahiinnahum laminkum wama hum minkum walakinnahumqawmun yafraqoon');
INSERT INTO "transliteration" VALUES(9,57,'9:57','Law yajidoona maljaan aw magharatinaw muddakhalan lawallaw ilayhi wahum yajmahoon');
INSERT INTO "transliteration" VALUES(9,58,'9:58','Waminhum man yalmizuka fee assadaqatifa-in oAAtoo minha radoo wa-in lam yuAAtawminha itha hum yaskhatoon');
INSERT INTO "transliteration" VALUES(9,59,'9:59','Walaw annahum radoo ma atahumuAllahu warasooluhu waqaloo hasbunaAllahu sayu/teena Allahu min fadlihiwarasooluhu inna ila Allahi raghiboon');
INSERT INTO "transliteration" VALUES(9,60,'9:60','Innama assadaqatulilfuqara-i walmasakeeni walAAamileenaAAalayha walmu-allafati quloobuhum wafee arriqabiwalgharimeena wafee sabeeli Allahi wabniassabeeli fareedatan mina Allahi wallahuAAaleemun hakeemun');
INSERT INTO "transliteration" VALUES(9,61,'9:61','Waminhumu allatheena yu/thoonaannabiyya wayaqooloona huwa othunun qul othunukhayrin lakum yu/minu billahi wayu/minulilmu/mineena warahmatun lillatheena amanoominkum wallatheena yu/thoona rasoola Allahilahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(9,62,'9:62','Yahlifoona billahilakum liyurdookum wallahu warasooluhu ahaqquan yurdoohu in kanoo mu/mineen');
INSERT INTO "transliteration" VALUES(9,63,'9:63','Alam yaAAlamoo annahu man yuhadidiAllaha warasoolahu faanna lahu nara jahannama khalidanfeeha thalika alkhizyu alAAatheem');
INSERT INTO "transliteration" VALUES(9,64,'9:64','Yahtharu almunafiqoona antunazzala AAalayhim sooratun tunabbi-ohum bima feequloobihim quli istahzi-oo inna Allaha mukhrijun matahtharoon');
INSERT INTO "transliteration" VALUES(9,65,'9:65','Wala-in saaltahum layaqoolunna innamakunna nakhoodu wanalAAabu qul abillahiwaayatihi warasoolihi kuntum tastahzi-oon');
INSERT INTO "transliteration" VALUES(9,66,'9:66','La taAAtathiroo qad kafartumbaAAda eemanikum in naAAfu AAan ta-ifatin minkumnuAAaththib ta-ifatan bi-annahum kanoomujrimeen');
INSERT INTO "transliteration" VALUES(9,67,'9:67','Almunafiqoona walmunafiqatubaAAduhum min baAAdin ya/muroona bilmunkariwayanhawna AAani almaAAroofi wayaqbidoona aydiyahum nasooAllaha fanasiyahum inna almunafiqeena humu alfasiqoon');
INSERT INTO "transliteration" VALUES(9,68,'9:68','WaAAada Allahu almunafiqeenawalmunafiqati walkuffara narajahannama khalideena feeha hiya hasbuhumwalaAAanahumu Allahu walahum AAathabun muqeem');
INSERT INTO "transliteration" VALUES(9,69,'9:69','Kallatheena min qablikum kanooashadda minkum quwwatan waakthara amwalan waawladanfastamtaAAoo bikhalaqihim fastamtaAAtumbikhalaqikum kama istamtaAAa allatheena minqablikum bikhalaqihim wakhudtum kallatheekhadoo ola-ika habitat aAAmaluhumfee addunya wal-akhirati waola-ikahumu alkhasiroon');
INSERT INTO "transliteration" VALUES(9,70,'9:70','Alam ya/tihim nabao allatheena minqablihim qawmi noohin waAAadin wathamooda waqawmiibraheema waas-habi madyana walmu/tafikatiatat-hum rusuluhum bilbayyinati fama kanaAllahu liyathlimahum walakin kanooanfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(9,71,'9:71','Walmu/minoona walmu/minatubaAAduhum awliyao baAAdin ya/muroona bilmaAAroofiwayanhawna AAani almunkari wayuqeemoona assalatawayu/toona azzakata wayuteeAAoona Allahawarasoolahu ola-ika sayarhamuhumu Allahuinna Allaha AAazeezun hakeem');
INSERT INTO "transliteration" VALUES(9,72,'9:72','WaAAada Allahu almu/mineena walmu/minatijannatin tajree min tahtiha al-anharukhalideena feeha wamasakina tayyibatanfee jannati AAadnin waridwanun mina Allahiakbaru thalika huwa alfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(9,73,'9:73','Ya ayyuha annabiyyu jahidialkuffara walmunafiqeena waghluthAAalayhim wama/wahum jahannamu wabi/sa almaseer');
INSERT INTO "transliteration" VALUES(9,74,'9:74','Yahlifoona billahi maqaloo walaqad qaloo kalimata alkufri wakafaroobaAAda islamihim wahammoo bima lam yanaloowama naqamoo illa an aghnahumu Allahuwarasooluhu min fadlihi fa-in yatooboo yaku khayran lahumwa-in yatawallaw yuAAaththibhumu Allahu AAathabanaleeman fee addunya wal-akhiratiwama lahum fee al-ardi min waliyyin wala naseer');
INSERT INTO "transliteration" VALUES(9,75,'9:75','Waminhum man AAahada Allahala-in atana min fadlihi lanassaddaqannawalanakoonanna mina assaliheen');
INSERT INTO "transliteration" VALUES(9,76,'9:76','Falamma atahum min fadlihibakhiloo bihi watawallaw wahum muAAridoon');
INSERT INTO "transliteration" VALUES(9,77,'9:77','FaaAAqabahum nifaqan fee quloobihimila yawmi yalqawnahu bima akhlafoo Allaha mawaAAadoohu wabima kanoo yakthiboon');
INSERT INTO "transliteration" VALUES(9,78,'9:78','Alam yaAAlamoo anna Allaha yaAAlamusirrahum wanajwahum waanna Allaha AAallamualghuyoob');
INSERT INTO "transliteration" VALUES(9,79,'9:79','Allatheena yalmizoona almuttawwiAAeenamina almu/mineena fee assadaqati wallatheenala yajidoona illa juhdahum fayaskharoona minhumsakhira Allahu minhum walahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(9,80,'9:80','Istaghfir lahum aw la tastaghfirlahum in tastaghfir lahum sabAAeena marratan falan yaghfira Allahulahum thalika bi-annahum kafaroo billahiwarasoolihi wallahu la yahdee alqawma alfasiqeen');
INSERT INTO "transliteration" VALUES(9,81,'9:81','Fariha almukhallafoona bimaqAAadihimkhilafa rasooli Allahi wakarihoo an yujahidoobi-amwalihim waanfusihim fee sabeeli Allahi waqaloola tanfiroo fee alharri qul naru jahannamaashaddu harran law kanoo yafqahoon');
INSERT INTO "transliteration" VALUES(9,82,'9:82','Falyadhakoo qaleelan walyabkookatheeran jazaan bima kanoo yaksiboon');
INSERT INTO "transliteration" VALUES(9,83,'9:83','Fa-in rajaAAaka Allahu ila ta-ifatinminhum fasta/thanooka lilkhurooji faqul lantakhrujoo maAAiya abadan walan tuqatiloo maAAiya AAaduwwaninnakum radeetum bilquAAoodi awwala marratin faqAAudoomaAAa alkhalifeen');
INSERT INTO "transliteration" VALUES(9,84,'9:84','Wala tusalli AAala ahadinminhum mata abadan wala taqum AAala qabrihiinnahum kafaroo billahi warasoolihi wamatoowahum fasiqoon');
INSERT INTO "transliteration" VALUES(9,85,'9:85','Wala tuAAjibka amwaluhum waawladuhuminnama yureedu Allahu an yuAAaththibahum bihafee addunya watazhaqa anfusuhum wahum kafiroon');
INSERT INTO "transliteration" VALUES(9,86,'9:86','Wa-itha onzilat sooratun an aminoobillahi wajahidoo maAAa rasoolihi ista/thanakaoloo attawli minhum waqaloo tharnanakun maAAa alqaAAideen');
INSERT INTO "transliteration" VALUES(9,87,'9:87','Radoo bi-an yakoonoo maAAa alkhawalifiwatubiAAa AAala quloobihim fahum layafqahoon');
INSERT INTO "transliteration" VALUES(9,88,'9:88','Lakini arrasoolu wallatheenaamanoo maAAahu jahadoo bi-amwalihimwaanfusihim waola-ika lahumu alkhayratu waola-ikahumu almuflihoon');
INSERT INTO "transliteration" VALUES(9,89,'9:89','aAAadda Allahu lahum jannatintajree min tahtiha al-anharu khalideenafeeha thalika alfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(9,90,'9:90','Wajaa almuAAaththiroona minaal-aAArabi liyu/thana lahum waqaAAada allatheenakathaboo Allaha warasoolahu sayuseebu allatheenakafaroo minhum AAathabun aleem');
INSERT INTO "transliteration" VALUES(9,91,'9:91','Laysa AAala adduAAafa-iwala AAala almarda wala AAalaallatheena la yajidoona ma yunfiqoona harajunitha nasahoo lillahi warasoolihi maAAala almuhsineena min sabeelin wallahughafoorun raheem');
INSERT INTO "transliteration" VALUES(9,92,'9:92','Wala AAala allatheena ithama atawka litahmilahum qulta la ajidu maahmilukum AAalayhi tawallaw waaAAyunuhum tafeedumina addamAAi hazanan alla yajidoo mayunfiqoon');
INSERT INTO "transliteration" VALUES(9,93,'9:93','Innama assabeelu AAalaallatheena yasta/thinoonaka wahum aghniyaoradoo bi-an yakoonoo maAAa alkhawalifi watabaAAaAllahu AAala quloobihim fahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(9,94,'9:94','YaAAtathiroona ilaykum itharajaAAtum ilayhim qul la taAAtathiroo lan nu/minalakum qad nabbaana Allahu min akhbarikumwasayara Allahu AAamalakum warasooluhu thummaturaddoona ila AAalimi alghaybi washshahadatifayunabbi-okum bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(9,95,'9:95','Sayahlifoona billahilakum itha inqalabtum ilayhim lituAAridoo AAanhumfaaAAridoo AAanhum innahum rijsun wama/wahumjahannamu jazaan bima kanoo yaksiboon');
INSERT INTO "transliteration" VALUES(9,96,'9:96','Yahlifoona lakum litardawAAanhum fa-in tardaw AAanhum fa-inna Allaha layarda AAani alqawmi alfasiqeen');
INSERT INTO "transliteration" VALUES(9,97,'9:97','Al-aAArabu ashaddu kufran wanifaqanwaajdaru alla yaAAlamoo hudooda ma anzalaAllahu AAala rasoolihi wallahuAAaleemun hakeem');
INSERT INTO "transliteration" VALUES(9,98,'9:98','Wamina al-aAArabi man yattakhithuma yunfiqu maghraman wayatarabbasu bikumu addawa-iraAAalayhim da-iratu assaw-i wallahusameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(9,99,'9:99','Wamina al-aAArabi man yu/minu billahiwalyawmi al-akhiri wayattakhithu mayunfiqu qurubatin AAinda Allahi wasalawatiarrasooli ala innaha qurbatun lahumsayudkhiluhumu Allahu fee rahmatihi inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(9,100,'9:100','Wassabiqoona al-awwaloonamina almuhajireena wal-ansari wallatheenaittabaAAoohum bi-ihsanin radiya AllahuAAanhum waradoo AAanhu waaAAadda lahum jannatintajree tahtaha al-anharu khalideenafeeha abadan thalika alfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(9,101,'9:101','Wamimman hawlakum mina al-aAArabimunafiqoona wamin ahli almadeenati maradoo AAala annifaqila taAAlamuhum nahnu naAAlamuhum sanuAAaththibuhummarratayni thumma yuraddoona ila AAathabin AAatheem');
INSERT INTO "transliteration" VALUES(9,102,'9:102','Waakharoona iAAtarafoo bithunoobihimkhalatoo AAamalan salihan waakharasayyi-an AAasa Allahu an yatooba AAalayhim inna Allahaghafoorun raheem');
INSERT INTO "transliteration" VALUES(9,103,'9:103','Khuth min amwalihim sadaqatantutahhiruhum watuzakkeehim biha wasalliAAalayhim inna salataka sakanun lahum wallahusameeAAun AAaleem');
INSERT INTO "transliteration" VALUES(9,104,'9:104','Alam yaAAlamoo anna Allaha huwayaqbalu attawbata AAan AAibadihi waya/khuthuassadaqati waanna Allaha huwa attawwabuarraheem');
INSERT INTO "transliteration" VALUES(9,105,'9:105','Waquli iAAmaloo fasayara AllahuAAamalakum warasooluhu walmu/minoona wasaturaddoona ilaAAalimi alghaybi washshahadatifayunabbi-okum bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(9,106,'9:106','Waakharoona murjawna li-amri Allahiimma yuAAaththibuhum wa-imma yatoobuAAalayhim wallahu AAaleemun hakeem');
INSERT INTO "transliteration" VALUES(9,107,'9:107','Wallatheena ittakhathoomasjidan diraran wakufran watafreeqan baynaalmu/mineena wa-irsadan liman haraba Allahawarasoolahu min qablu walayahlifunna in aradna illaalhusna wallahu yashhadu innahum lakathiboon');
INSERT INTO "transliteration" VALUES(9,108,'9:108','La taqum feehi abadan lamasjidunossisa AAala attaqwa min awwali yawmin ahaqquan taqooma feehi feehi rijalun yuhibboona an yatatahharoowallahu yuhibbu almuttahhireen');
INSERT INTO "transliteration" VALUES(9,109,'9:109','Afaman assasa bunyanahu AAalataqwa mina Allahi waridwanin khayrunam man assasa bunyanahu AAala shafa jurufinharin fanhara bihi fee narijahannama wallahu la yahdee alqawma aththalimeen');
INSERT INTO "transliteration" VALUES(9,110,'9:110','La yazalu bunyanuhumuallathee banaw reebatan fee quloobihim illa an taqattaAAaquloobuhum wallahu AAaleemun hakeem');
INSERT INTO "transliteration" VALUES(9,111,'9:111','Inna Allaha ishtara minaalmu/mineena anfusahum waamwalahum bi-anna lahumualjannata yuqatiloona fee sabeeli Allahifayaqtuloona wayuqtaloona waAAdan AAalayhi haqqan fee attawratiwal-injeeli walqur-ani waman awfabiAAahdihi mina Allahi fastabshiroo bibayAAikumuallathee bayaAAtum bihi wathalika huwaalfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(9,112,'9:112','Atta-iboona alAAabidoonaalhamidoona assa-ihoona arrakiAAoonaassajidoona al-amiroona bilmaAAroofiwannahoona AAani almunkari walhafithoonalihudoodi Allahi wabashshiri almu/mineen');
INSERT INTO "transliteration" VALUES(9,113,'9:113','Ma kana linnabiyyi wallatheenaamanoo an yastaghfiroo lilmushrikeena walaw kanooolee qurba min baAAdi ma tabayyana lahum annahum as-habualjaheem');
INSERT INTO "transliteration" VALUES(9,114,'9:114','Wama kana istighfaruibraheema li-abeehi illa AAan mawAAidatin waAAadahaiyyahu falamma tabayyana lahu annahu AAaduwwun lillahitabarraa minhu inna ibraheema laawwahun haleem');
INSERT INTO "transliteration" VALUES(9,115,'9:115','Wama kana Allahu liyudillaqawman baAAda ith hadahum hattayubayyina lahum ma yattaqoona inna Allaha bikullishay-in AAaleem');
INSERT INTO "transliteration" VALUES(9,116,'9:116','Inna Allaha lahu mulku assamawatiwal-ardi yuhyee wayumeetu wama lakummin dooni Allahi min waliyyin wala naseer');
INSERT INTO "transliteration" VALUES(9,117,'9:117','Laqad taba Allahu AAalaannabiyyi walmuhajireena wal-ansariallatheena ittabaAAoohu fee saAAati alAAusrati minbaAAdi ma kada yazeeghu quloobu fareeqin minhumthumma taba AAalayhim innahu bihim raoofun raheem');
INSERT INTO "transliteration" VALUES(9,118,'9:118','WaAAala aththalathatiallatheena khullifoo hatta itha daqatAAalayhimu al-ardu bima rahubat wadaqatAAalayhim anfusuhum wathannoo an la maljaamina Allahi illa ilayhi thumma tabaAAalayhim liyatooboo inna Allaha huwa attawwabuarraheem');
INSERT INTO "transliteration" VALUES(9,119,'9:119','Ya ayyuha allatheena amanooittaqoo Allaha wakoonoo maAAa assadiqeen');
INSERT INTO "transliteration" VALUES(9,120,'9:120','Ma kana li-ahli almadeenatiwaman hawlahum mina al-aAArabi an yatakhallafooAAan rasooli Allahi wala yarghaboo bi-anfusihimAAan nafsihi thalika bi-annahum la yuseebuhumthamaon wala nasabun walamakhmasatun fee sabeeli Allahi wala yataoonamawti-an yagheethu alkuffara walayanaloona min AAaduwwin naylan illa kutiba lahumbihi AAamalun salihun inna Allaha layudeeAAu ajra almuhsineen');
INSERT INTO "transliteration" VALUES(9,121,'9:121','Wala yunfiqoona nafaqatan sagheeratanwala kabeeratan wala yaqtaAAoona wadiyanilla kutiba lahum liyajziyahumu Allahu ahsanama kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(9,122,'9:122','Wama kana almu/minoonaliyanfiroo kaffatan falawla nafara min kullifirqatin minhum ta-ifatun liyatafaqqahoo fee addeeniwaliyunthiroo qawmahum itha rajaAAoo ilayhimlaAAallahum yahtharoon');
INSERT INTO "transliteration" VALUES(9,123,'9:123','Ya ayyuha allatheena amanooqatiloo allatheena yaloonakum mina alkuffariwalyajidoo feekum ghilthatan waAAlamoo annaAllaha maAAa almuttaqeen');
INSERT INTO "transliteration" VALUES(9,124,'9:124','Wa-itha ma onzilat sooratunfaminhum man yaqoolu ayyukum zadat-hu hathihi eemananfaamma allatheena amanoo fazadat-humeemanan wahum yastabshiroon');
INSERT INTO "transliteration" VALUES(9,125,'9:125','Waamma allatheena feequloobihim maradun fazadat-hum rijsan ilarijsihim wamatoo wahum kafiroon');
INSERT INTO "transliteration" VALUES(9,126,'9:126','Awa la yarawna annahum yuftanoonafee kulli AAamin marratan aw marratayni thumma layatooboona wala hum yaththakkaroon');
INSERT INTO "transliteration" VALUES(9,127,'9:127','Wa-itha ma onzilat sooratunnathara baAAduhum ila baAAdinhal yarakum min ahadin thumma insarafoo sarafaAllahu quloobahum bi-annahum qawmun la yafqahoon');
INSERT INTO "transliteration" VALUES(9,128,'9:128','Laqad jaakum rasoolun min anfusikumAAazeezun AAalayhi ma AAanittum hareesunAAalaykum bilmu/mineena raoofun raheem');
INSERT INTO "transliteration" VALUES(9,129,'9:129','Fa-in tawallaw faqul hasbiya Allahula ilaha illa huwa AAalayhi tawakkaltuwahuwa rabbu alAAarshi alAAatheem');
INSERT INTO "transliteration" VALUES(10,1,'10:1','Alif-lam-ra tilka ayatualkitabi alhakeem');
INSERT INTO "transliteration" VALUES(10,2,'10:2','Akana linnasi AAajabanan awhayna ila rajulin minhum an anthiriannasa wabashshiri allatheena amanooanna lahum qadama sidqin AAinda rabbihim qala alkafiroonainna hatha lasahirun mubeen');
INSERT INTO "transliteration" VALUES(10,3,'10:3','Inna rabbakumu Allahu allatheekhalaqa assamawati wal-ardafee sittati ayyamin thumma istawa AAalaalAAarshi yudabbiru al-amra ma min shafeeAAin illamin baAAdi ithnihi thalikumu Allahu rabbukumfaAAbudoohu afala tathakkaroon');
INSERT INTO "transliteration" VALUES(10,4,'10:4','Ilayhi marjiAAukum jameeAAan waAAda Allahihaqqan innahu yabdao alkhalqa thumma yuAAeeduhu liyajziyaallatheena amanoo waAAamiloo assalihatibilqisti wallatheena kafaroo lahumsharabun min hameemin waAAathabun aleemunbima kanoo yakfuroon');
INSERT INTO "transliteration" VALUES(10,5,'10:5','Huwa allathee jaAAala ashshamsadiyaan walqamara nooran waqaddarahu manazilalitaAAlamoo AAadada assineena walhisabama khalaqa Allahu thalika illa bilhaqqiyufassilu al-ayati liqawmin yaAAlamoon');
INSERT INTO "transliteration" VALUES(10,6,'10:6','Inna fee ikhtilafi allayli wannahariwama khalaqa Allahu fee assamawatiwal-ardi laayatin liqawmin yattaqoon');
INSERT INTO "transliteration" VALUES(10,7,'10:7','Inna allatheena la yarjoona liqaanawaradoo bilhayati addunyawatmaannoo biha wallatheenahum AAan ayatina ghafiloon');
INSERT INTO "transliteration" VALUES(10,8,'10:8','Ola-ika ma/wahumu annarubima kanoo yaksiboon');
INSERT INTO "transliteration" VALUES(10,9,'10:9','Inna allatheena amanoowaAAamiloo assalihati yahdeehim rabbuhumbi-eemanihim tajree min tahtihimu al-anharufee jannati annaAAeem');
INSERT INTO "transliteration" VALUES(10,10,'10:10','DaAAwahum feeha subhanakaallahumma watahiyyatuhum feeha salamunwaakhiru daAAwahum ani alhamdu lillahirabbi alAAalameen');
INSERT INTO "transliteration" VALUES(10,11,'10:11','Walaw yuAAajjilu Allahu linnasiashsharra istiAAjalahum bilkhayri laqudiyailayhim ajaluhum fanatharu allatheena layarjoona liqaana fee tughyanihimyaAAmahoon');
INSERT INTO "transliteration" VALUES(10,12,'10:12','Wa-itha massa al-insana addurrudaAAana lijanbihi aw qaAAidan aw qa-imanfalamma kashafna AAanhu durrahu marra kaanlam yadAAuna ila durrin massahu kathalikazuyyina lilmusrifeena ma kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(10,13,'10:13','Walaqad ahlakna alquroona minqablikum lamma thalamoo wajaat-humrusuluhum bilbayyinati wama kanooliyu/minoo kathalika najzee alqawma almujrimeen');
INSERT INTO "transliteration" VALUES(10,14,'10:14','Thumma jaAAalnakum khala-ifafee al-ardi min baAAdihim linanthura kayfataAAmaloon');
INSERT INTO "transliteration" VALUES(10,15,'10:15','Wa-itha tutla AAalayhim ayatunabayyinatin qala allatheena layarjoona liqaana i/ti biqur-anin ghayri hathaaw baddilhu qul ma yakoonu lee an obaddilahu min tilqa-inafsee in attabiAAu illa ma yooha ilayyainnee akhafu in AAasaytu rabbee AAathabayawmin AAatheem');
INSERT INTO "transliteration" VALUES(10,16,'10:16','Qul law shaa Allahu matalawtuhu AAalaykum wala adrakum bihi faqadlabithtu feekum AAumuran min qablihi afala taAAqiloon');
INSERT INTO "transliteration" VALUES(10,17,'10:17','Faman athlamu mimmani iftaraAAala Allahi kathiban aw kaththababi-ayatihi innahu la yuflihualmujrimoon');
INSERT INTO "transliteration" VALUES(10,18,'10:18','WayaAAbudoona min dooni Allahi mala yadurruhum wala yanfaAAuhum wayaqooloonahaola-i shufaAAaona AAinda Allahiqul atunabbi-oona Allaha bima la yaAAlamufee assamawati wala fee al-ardisubhanahu wataAAala AAamma yushrikoon');
INSERT INTO "transliteration" VALUES(10,19,'10:19','Wama kana annasuilla ommatan wahidatan fakhtalafoo walawlakalimatun sabaqat min rabbika laqudiya baynahum feemafeehi yakhtalifoon');
INSERT INTO "transliteration" VALUES(10,20,'10:20','Wayaqooloona lawla onzila AAalayhi ayatunmin rabbihi faqul innama alghaybu lillahi fantathirooinnee maAAakum mina almuntathireen');
INSERT INTO "transliteration" VALUES(10,21,'10:21','Wa-itha athaqna annasarahmatan min baAAdi darraa massat-hum ithalahum makrun fee ayatina quli AllahuasraAAu makran inna rusulana yaktuboona matamkuroon');
INSERT INTO "transliteration" VALUES(10,22,'10:22','Huwa allathee yusayyirukum feealbarri walbahri hatta ithakuntum fee alfulki wajarayna bihim bireehin tayyibatinwafarihoo biha jaat-ha reehunAAasifun wajaahumu almawju min kulli makaninwathannoo annahum oheeta bihimdaAAawoo Allaha mukhliseena lahu addeenala-in anjaytana min hathihi lanakoonanna mina ashshakireen');
INSERT INTO "transliteration" VALUES(10,23,'10:23','Falamma anjahum ithahum yabghoona fee al-ardi bighayri alhaqqi yaayyuha annasu innama baghyukum AAalaanfusikum mataAAa alhayati addunyathumma ilayna marjiAAukum fanunabbi-okum bimakuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(10,24,'10:24','Innama mathalu alhayatiaddunya kama-in anzalnahu mina assama-ifakhtalata bihi nabatu al-ardi mimmaya/kulu annasu wal-anAAamu hattaitha akhathati al-ardu zukhrufaha wazzayyanatwathanna ahluha annahum qadiroonaAAalayha ataha amruna laylan aw naharanfajaAAalnaha haseedan kaan lam taghnabil-amsi kathalika nufassilu al-ayatiliqawmin yatafakkaroon');
INSERT INTO "transliteration" VALUES(10,25,'10:25','Wallahu yadAAoo ila dariassalami wayahdee man yashao ila siratinmustaqeem');
INSERT INTO "transliteration" VALUES(10,26,'10:26','Lillatheena ahsanoo alhusnawaziyadatun wala yarhaqu wujoohahum qatarun walathillatun ola-ika as-habu aljannatihum feeha khalidoon');
INSERT INTO "transliteration" VALUES(10,27,'10:27','Wallatheena kasaboo assayyi-atijazao sayyi-atin bimithliha watarhaquhum thillatunma lahum mina Allahi min AAasimin kaannamaoghshiyat wujoohuhum qitaAAan mina allayli muthlimanola-ika as-habu annari humfeeha khalidoon');
INSERT INTO "transliteration" VALUES(10,28,'10:28','Wayawma nahshuruhum jameeAAan thummanaqoolu lillatheena ashrakoo makanakum antumwashurakaokum fazayyalna baynahum waqalashurakaohum ma kuntum iyyanataAAbudoon');
INSERT INTO "transliteration" VALUES(10,29,'10:29','Fakafa billahishaheedan baynana wabaynakum in kunna AAan AAibadatikumlaghafileen');
INSERT INTO "transliteration" VALUES(10,30,'10:30','Hunalika tabloo kullu nafsin maaslafat waruddoo ila Allahi mawlahumu alhaqqiwadalla AAanhum ma kanoo yaftaroon');
INSERT INTO "transliteration" VALUES(10,31,'10:31','Qul man yarzuqukum mina assama-iwal-ardi amman yamliku assamAAa wal-absarawaman yukhriju alhayya mina almayyiti wayukhriju almayyitamina alhayyi waman yudabbiru al-amra fasayaqooloona Allahufaqul afala tattaqoon');
INSERT INTO "transliteration" VALUES(10,32,'10:32','Fathalikumu Allahu rabbukumualhaqqu famatha baAAda alhaqqi illa addalalufaanna tusrafoon');
INSERT INTO "transliteration" VALUES(10,33,'10:33','Kathalika haqqat kalimaturabbika AAala allatheena fasaqoo annahum layu/minoon');
INSERT INTO "transliteration" VALUES(10,34,'10:34','Qul hal min shuraka-ikum man yabdaoalkhalqa thumma yuAAeeduhu quli Allahu yabdao alkhalqathumma yuAAeeduhu faanna tu/fakoon');
INSERT INTO "transliteration" VALUES(10,35,'10:35','Qul hal min shuraka-ikum man yahdeeila alhaqqi quli Allahu yahdee lilhaqqiafaman yahdee ila alhaqqi ahaqqu anyuttabaAAa amman la yahiddee illa an yuhdafama lakum kayfa tahkumoon');
INSERT INTO "transliteration" VALUES(10,36,'10:36','Wama yattabiAAu aktharuhum illathannan inna aththanna layughnee mina alhaqqi shay-an inna Allaha AAaleemunbima yafAAaloon');
INSERT INTO "transliteration" VALUES(10,37,'10:37','Wama kana hatha alqur-anuan yuftara min dooni Allahi walakin tasdeeqaallathee bayna yadayhi watafseela alkitabi larayba feehi min rabbi alAAalameen');
INSERT INTO "transliteration" VALUES(10,38,'10:38','Am yaqooloona iftarahu qul fa/toobisooratin mithlihi wadAAoo mani istataAAtum mindooni Allahi in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(10,39,'10:39','Bal kaththaboo bima lam yuheetoobiAAilmihi walamma ya/tihim ta/weeluhu kathalika kaththabaallatheena min qablihim fanthurkayfa kana AAaqibatu aththalimeen');
INSERT INTO "transliteration" VALUES(10,40,'10:40','Waminhum man yu/minu bihi waminhum man layu/minu bihi warabbuka aAAlamu bilmufsideen');
INSERT INTO "transliteration" VALUES(10,41,'10:41','Wa-in kaththabooka faqul lee AAamaleewalakum AAamalukum antum baree-oona mimma aAAmalu waanabaree-on mimma taAAmaloon');
INSERT INTO "transliteration" VALUES(10,42,'10:42','Waminhum man yastamiAAoona ilayka afaantatusmiAAu assumma walaw kanoo layaAAqiloon');
INSERT INTO "transliteration" VALUES(10,43,'10:43','Waminhum man yanthuru ilaykaafaanta tahdee alAAumya walaw kanoo la yubsiroon');
INSERT INTO "transliteration" VALUES(10,44,'10:44','Inna Allaha la yathlimuannasa shay-an walakinna annasaanfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(10,45,'10:45','Wayawma yahshuruhum kaan lamyalbathoo illa saAAatan mina annahariyataAAarafoona baynahum qad khasira allatheena kaththaboobiliqa-i Allahi wama kanoo muhtadeen');
INSERT INTO "transliteration" VALUES(10,46,'10:46','Wa-imma nuriyannaka baAAdaallathee naAAiduhum aw natawaffayannaka fa-ilaynamarjiAAuhum thumma Allahu shaheedun AAala mayafAAaloon');
INSERT INTO "transliteration" VALUES(10,47,'10:47','Walikulli ommatin rasoolun fa-itha jaarasooluhum qudiya baynahum bilqisti wahum layuthlamoon');
INSERT INTO "transliteration" VALUES(10,48,'10:48','Wayaqooloona mata hathaalwaAAdu in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(10,49,'10:49','Qul la amliku linafsee darranwala nafAAan illa ma shaa Allahulikulli ommatin ajalun itha jaa ajaluhum falayasta/khiroona saAAatan wala yastaqdimoon');
INSERT INTO "transliteration" VALUES(10,50,'10:50','Qul araaytum in atakum AAathabuhubayatan aw naharan matha yastaAAjilu minhualmujrimoon');
INSERT INTO "transliteration" VALUES(10,51,'10:51','Athumma itha ma waqaAAa amantumbihi al-ana waqad kuntum bihi tastaAAjiloon');
INSERT INTO "transliteration" VALUES(10,52,'10:52','Thumma qeela lillatheena thalamoothooqoo AAathaba alkhuldi hal tujzawna illabima kuntum taksiboon');
INSERT INTO "transliteration" VALUES(10,53,'10:53','Wayastanbi-oonaka ahaqqun huwa qul eewarabbee innahu lahaqqun wama antum bimuAAjizeen');
INSERT INTO "transliteration" VALUES(10,54,'10:54','Walaw anna likulli nafsin thalamatma fee al-ardi laftadat bihi waasarroo annadamatalamma raawoo alAAathaba waqudiya baynahum bilqistiwahum la yuthlamoon');
INSERT INTO "transliteration" VALUES(10,55,'10:55','Ala inna lillahi ma feeassamawati wal-ardi alainna waAAda Allahi haqqun walakinnaaktharahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(10,56,'10:56','Huwa yuhyee wayumeetu wa-ilayhiturjaAAoon');
INSERT INTO "transliteration" VALUES(10,57,'10:57','Ya ayyuha annasuqad jaatkum mawAAithatun min rabbikum washifaonlima fee assudoori wahudan warahmatunlilmu/mineen');
INSERT INTO "transliteration" VALUES(10,58,'10:58','Qul bifadli Allahi wabirahmatihifabithalika falyafrahoo huwa khayrun mimmayajmaAAoon');
INSERT INTO "transliteration" VALUES(10,59,'10:59','Qul araaytum ma anzala Allahulakum min rizqin fajaAAaltum minhu haraman wahalalanqul allahu athina lakum am AAala Allahitaftaroon');
INSERT INTO "transliteration" VALUES(10,60,'10:60','Wama thannu allatheenayaftaroona AAala Allahi alkathiba yawmaalqiyamati inna Allaha lathoo fadlinAAala annasi walakinna aktharahum layashkuroon');
INSERT INTO "transliteration" VALUES(10,61,'10:61','Wama takoonu fee sha/nin wamatatloo minhu min qur-anin wala taAAmaloona minAAamalin illa kunna AAalaykum shuhoodan ithtufeedoona feehi wama yaAAzubu AAan rabbika minmithqali tharratin fee al-ardi walafee assama-i wala asghara min thalikawala akbara illa fee kitabin mubeen');
INSERT INTO "transliteration" VALUES(10,62,'10:62','Ala inna awliyaa Allahila khawfun AAalayhim wala hum yahzanoon');
INSERT INTO "transliteration" VALUES(10,63,'10:63','Allatheena amanoo wakanooyattaqoon');
INSERT INTO "transliteration" VALUES(10,64,'10:64','Lahumu albushra fee alhayatiaddunya wafee al-akhirati latabdeela likalimati Allahi thalika huwaalfawzu alAAatheem');
INSERT INTO "transliteration" VALUES(10,65,'10:65','Wala yahzunka qawluhum innaalAAizzata lillahi jameeAAan huwa assameeAAualAAaleem');
INSERT INTO "transliteration" VALUES(10,66,'10:66','Ala inna lillahi man fee assamawatiwaman fee al-ardi wama yattabiAAu allatheenayadAAoona min dooni Allahi shurakaa inyattabiAAoona illa aththanna wa-inhum illa yakhrusoon');
INSERT INTO "transliteration" VALUES(10,67,'10:67','Huwa allathee jaAAala lakumu allaylalitaskunoo feehi wannahara mubsiran innafee thalika laayatin liqawmin yasmaAAoon');
INSERT INTO "transliteration" VALUES(10,68,'10:68','Qaloo ittakhatha Allahuwaladan subhanahu huwa alghaniyyu lahu ma fee assamawatiwama fee al-ardi in AAindakum min sultaninbihatha ataqooloona AAala Allahi ma lataAAlamoon');
INSERT INTO "transliteration" VALUES(10,69,'10:69','Qul inna allatheena yaftaroona AAalaAllahi alkathiba la yuflihoon');
INSERT INTO "transliteration" VALUES(10,70,'10:70','MataAAun fee addunyathumma ilayna marjiAAuhum thumma nutheequhumu alAAathabaashshadeeda bima kanoo yakfuroon');
INSERT INTO "transliteration" VALUES(10,71,'10:71','Watlu AAalayhim nabaa noohinith qala liqawmihi ya qawmi in kanakabura AAalaykum maqamee watathkeeree bi-ayatiAllahi faAAala Allahi tawakkaltu faajmiAAooamrakum washurakaakum thumma la yakun amrukumAAalaykum ghummatan thumma iqdoo ilayya wala tunthiroon');
INSERT INTO "transliteration" VALUES(10,72,'10:72','Fa-in tawallaytum fama saaltukum minajrin in ajriya illa AAala Allahi waomirtuan akoona mina almuslimeen');
INSERT INTO "transliteration" VALUES(10,73,'10:73','Fakaththaboohu fanajjaynahuwaman maAAahu fee alfulki wajaAAalnahum khala-ifawaaghraqna allatheena kaththaboo bi-ayatinafanthur kayfa kana AAaqibatualmunthareen');
INSERT INTO "transliteration" VALUES(10,74,'10:74','Thumma baAAathna min baAAdihi rusulanila qawmihim fajaoohum bilbayyinatifama kanoo liyu/minoo bima kaththaboobihi min qablu kathalika natbaAAu AAalaquloobi almuAAtadeen');
INSERT INTO "transliteration" VALUES(10,75,'10:75','Thumma baAAathna min baAAdihim moosawaharoona ila firAAawna wamala-ihi bi-ayatinafastakbaroo wakanoo qawman mujrimeen');
INSERT INTO "transliteration" VALUES(10,76,'10:76','Falamma jaahumu alhaqqumin AAindina qaloo inna hatha lasihrunmubeen');
INSERT INTO "transliteration" VALUES(10,77,'10:77','Qala moosa ataqooloona lilhaqqilamma jaakum asihrun hatha walayuflihu assahiroon');
INSERT INTO "transliteration" VALUES(10,78,'10:78','Qaloo aji/tana litalfitanaAAamma wajadna AAalayhi abaanawatakoona lakuma alkibriyao fee al-ardi wamanahnu lakuma bimu/mineen');
INSERT INTO "transliteration" VALUES(10,79,'10:79','Waqala firAAawnu i/toonee bikulli sahirinAAaleem');
INSERT INTO "transliteration" VALUES(10,80,'10:80','Falamma jaa assaharatuqala lahum moosa alqoo ma antum mulqoon');
INSERT INTO "transliteration" VALUES(10,81,'10:81','Falamma alqaw qala moosama ji/tum bihi assihru inna Allahasayubtiluhu inna Allaha la yuslihuAAamala almufsideen');
INSERT INTO "transliteration" VALUES(10,82,'10:82','Wayuhiqqu Allahu alhaqqabikalimatihi walaw kariha almujrimoon');
INSERT INTO "transliteration" VALUES(10,83,'10:83','Fama amana limoosa illathurriyyatun min qawmihi AAala khawfin minfirAAawna wamala-ihim an yaftinahum wa-inna firAAawna laAAalinfee al-ardi wa-innahu lamina almusrifeen');
INSERT INTO "transliteration" VALUES(10,84,'10:84','Waqala moosa ya qawmiin kuntum amantum billahi faAAalayhitawakkaloo in kuntum muslimeen');
INSERT INTO "transliteration" VALUES(10,85,'10:85','Faqaloo AAala Allahitawakkalna rabbana la tajAAalnafitnatan lilqawmi aththalimeen');
INSERT INTO "transliteration" VALUES(10,86,'10:86','Wanajjina birahmatika minaalqawmi alkafireen');
INSERT INTO "transliteration" VALUES(10,87,'10:87','Waawhayna ila moosawaakheehi an tabawwaa liqawmikuma bimisrabuyootan wajAAaloo buyootakum qiblatan waaqeemoo assalatawabashshiri almu/mineen');
INSERT INTO "transliteration" VALUES(10,88,'10:88','Waqala moosa rabbanainnaka atayta firAAawna wamalaahu zeenatan waamwalanfee alhayati addunya rabbanaliyudilloo AAan sabeelika rabbana itmis AAalaamwalihim washdud AAala quloobihim falayu/minoo hatta yarawoo alAAathaba al-aleem');
INSERT INTO "transliteration" VALUES(10,89,'10:89','Qala qad ojeebat daAAwatukumafastaqeema wala tattabiAAannisabeela allatheena la yaAAlamoon');
INSERT INTO "transliteration" VALUES(10,90,'10:90','Wajawazna bibanee isra-eelaalbahra faatbaAAahum firAAawnu wajunooduhu baghyanwaAAadwan hatta itha adrakahu algharaqu qalaamantu annahu la ilaha illa allatheeamanat bihi banoo isra-eela waana minaalmuslimeen');
INSERT INTO "transliteration" VALUES(10,91,'10:91','Al-ana waqad AAasaytaqablu wakunta mina almufsideen');
INSERT INTO "transliteration" VALUES(10,92,'10:92','Falyawma nunajjeeka bibadanikalitakoona liman khalfaka ayatan wa-inna katheeran mina annasiAAan ayatina laghafiloon');
INSERT INTO "transliteration" VALUES(10,93,'10:93','Walaqad bawwa/na banee isra-eelamubawwaa sidqin warazaqnahum mina attayyibatifama ikhtalafoo hatta jaahumualAAilmu inna rabbaka yaqdee baynahum yawma alqiyamatifeema kanoo feehi yakhtalifoon');
INSERT INTO "transliteration" VALUES(10,94,'10:94','Fa-in kunta fee shakkin mimma anzalnnailayka fas-ali allatheena yaqraoona alkitabamin qablika laqad jaaka alhaqqu min rabbika falatakoonanna mina almumtareen');
INSERT INTO "transliteration" VALUES(10,95,'10:95','Wala takoonanna mina allatheenakaththaboo bi-ayati Allahi fatakoonamina alkhasireen');
INSERT INTO "transliteration" VALUES(10,96,'10:96','Inna allatheena haqqatAAalayhim kalimatu rabbika la yu/minoon');
INSERT INTO "transliteration" VALUES(10,97,'10:97','Walaw jaat-hum kullu ayatin hattayarawoo alAAathaba al-aleem');
INSERT INTO "transliteration" VALUES(10,98,'10:98','Falawla kanat qaryatun amanatfanafaAAaha eemanuha illa qawmayoonusa lamma amanoo kashafna AAanhum AAathabaalkhizyi fee alhayati addunyawamattaAAnahum ila heen');
INSERT INTO "transliteration" VALUES(10,99,'10:99','Walaw shaa rabbuka laamana manfee al-ardi kulluhum jameeAAan afaanta tukrihu annasahatta yakoonoo mu/mineen');
INSERT INTO "transliteration" VALUES(10,100,'10:100','Wama kana linafsin an tu/minailla bi-ithni Allahi wayajAAalu arrijsaAAala allatheena la yaAAqiloon');
INSERT INTO "transliteration" VALUES(10,101,'10:101','Quli onthuroo mathafee assamawati wal-ardi wamatughnee al-ayatu wannuthuru AAanqawmin la yu/minoon');
INSERT INTO "transliteration" VALUES(10,102,'10:102','Fahal yantathiroona illamithla ayyami allatheena khalaw min qablihim qul fantathirooinnee maAAakum mina almuntathireen');
INSERT INTO "transliteration" VALUES(10,103,'10:103','Thumma nunajjee rusulana wallatheenaamanoo kathalika haqqan AAalaynanunjee almu/mineen');
INSERT INTO "transliteration" VALUES(10,104,'10:104','Qul ya ayyuha annasuin kuntum fee shakkin min deenee fala aAAbudu allatheenataAAbudoona min dooni Allahi walakin aAAbudu Allahaallathee yatawaffakum waomirtu an akoona minaalmu/mineen');
INSERT INTO "transliteration" VALUES(10,105,'10:105','Waan aqim wajhaka liddeeni haneefanwala takoonanna mina almushrikeen');
INSERT INTO "transliteration" VALUES(10,106,'10:106','Wala tadAAu min dooni Allahima la yanfaAAuka wala yadurruka fa-infaAAalta fa-innaka ithan mina aththalimeen');
INSERT INTO "transliteration" VALUES(10,107,'10:107','Wa-in yamsaska Allahu bidurrinfala kashifa lahu illa huwa wa-in yuridkabikhayrin fala radda lifadlihi yuseebubihi man yashao min AAibadihi wahuwa alghafooru arraheem');
INSERT INTO "transliteration" VALUES(10,108,'10:108','Qul ya ayyuha annasuqad jaakumu alhaqqu min rabbikum famani ihtadafa-innama yahtadee linafsihi waman dalla fa-innamayadillu AAalayha wama ana AAalaykumbiwakeel');
INSERT INTO "transliteration" VALUES(10,109,'10:109','WattabiAA ma yoohailayka wasbir hatta yahkumaAllahu wahuwa khayru alhakimeen');
INSERT INTO "transliteration" VALUES(11,1,'11:1','Alif-lam-ra kitabun ohkimatayatuhu thumma fussilat min ladun hakeeminkhabeer');
INSERT INTO "transliteration" VALUES(11,2,'11:2','Alla taAAbudoo illa Allahainnanee lakum minhu natheerun wabasheer');
INSERT INTO "transliteration" VALUES(11,3,'11:3','Waani istaghfiroo rabbakum thumma toobooilayhi yumattiAAkum mataAAan hasanan ilaajalin musamman wayu/ti kulla thee fadlin fadlahuwa-in tawallaw fa-inee akhafu AAalaykum AAathabayawmin kabeer');
INSERT INTO "transliteration" VALUES(11,4,'11:4','Ila Allahi marjiAAukum wahuwaAAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(11,5,'11:5','Ala innahum yathnoona sudoorahumliyastakhfoo minhu ala heena yastaghshoona thiyabahumyaAAlamu ma yusirroona wama yuAAlinoona innahuAAaleemun bithati assudoor');
INSERT INTO "transliteration" VALUES(11,6,'11:6','Wama min dabbatin fee al-ardiilla AAala Allahi rizquha wayaAAlamumustaqarraha wamustawdaAAaha kullun fee kitabinmubeen');
INSERT INTO "transliteration" VALUES(11,7,'11:7','Wahuwa allathee khalaqa assamawatiwal-arda fee sittati ayyamin wakanaAAarshuhu AAala alma-i liyabluwakum ayyukum ahsanuAAamalan wala-in qulta innakum mabAAoothoona min baAAdi almawtilayaqoolanna allatheena kafaroo in hatha illasihrun mubeen');
INSERT INTO "transliteration" VALUES(11,8,'11:8','Wala-in akhkharna AAanhumu alAAathabaila ommatin maAAdoodatin layaqoolunna ma yahbisuhuala yawma ya/teehim laysa masroofan AAanhum wahaqabihim ma kanoo bihi yastahzi-oon');
INSERT INTO "transliteration" VALUES(11,9,'11:9','Wala-in athaqna al-insanaminna rahmatan thumma nazaAAnahaminhu innahu layaoosun kafoor');
INSERT INTO "transliteration" VALUES(11,10,'11:10','Wala-in athaqnahu naAAmaabaAAda darraa massat-hu layaqoolanna thahabaassayyi-atu AAannee innahu lafarihunfakhoor');
INSERT INTO "transliteration" VALUES(11,11,'11:11','Illa allatheena sabaroowaAAamiloo assalihati ola-ika lahummaghfiratun waajrun kabeer');
INSERT INTO "transliteration" VALUES(11,12,'11:12','FalaAAallaka tarikun baAAda mayooha ilayka wada-iqun bihi sadruka anyaqooloo lawla onzila AAalayhi kanzun aw jaamaAAahu malakun innama anta natheerun wallahuAAala kulli shay-in wakeel');
INSERT INTO "transliteration" VALUES(11,13,'11:13','Am yaqooloona iftarahu qul fa/toobiAAashri suwarin mithlihi muftarayatin wadAAoomani istataAAtum min dooni Allahi in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(11,14,'11:14','Fa-illam yastajeeboo lakum faAAlamooannama onzila biAAilmi Allahi waan la ilahailla huwa fahal antum muslimoon');
INSERT INTO "transliteration" VALUES(11,15,'11:15','Man kana yureedu alhayataaddunya wazeenataha nuwaffi ilayhim aAAmalahumfeeha wahum feeha la yubkhasoon');
INSERT INTO "transliteration" VALUES(11,16,'11:16','Ola-ika allatheena laysa lahumfee al-akhirati illa annaru wahabitama sanaAAoo feeha wabatilun makanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(11,17,'11:17','Afaman kana AAala bayyinatinmin rabbihi wayatloohu shahidun minhu wamin qablihi kitabumoosa imaman warahmatan ola-ikayu/minoona bihi waman yakfur bihi mina al-ahzabi fannarumawAAiduhu fala taku fee miryatin minhu innahu alhaqqumin rabbika walakinna akthara annasi layu/minoon');
INSERT INTO "transliteration" VALUES(11,18,'11:18','Waman athlamu mimmani iftaraAAala Allahi kathiban ola-ika yuAAradoonaAAala rabbihim wayaqoolu al-ashhadu haola-iallatheena kathaboo AAala rabbihim alalaAAnatu Allahi AAala aththalimeen');
INSERT INTO "transliteration" VALUES(11,19,'11:19','Allatheena yasuddoona AAansabeeli Allahi wayabghoonaha AAiwajan wahum bil-akhiratihum kafiroon');
INSERT INTO "transliteration" VALUES(11,20,'11:20','Ola-ika lam yakoonoo muAAjizeena feeal-ardi wama kana lahum min dooni Allahimin awliyaa yudaAAafu lahumu alAAathabu makanoo yastateeAAoona assamAAa wama kanooyubsiroon');
INSERT INTO "transliteration" VALUES(11,21,'11:21','Ola-ika allatheena khasirooanfusahum wadalla AAanhum ma kanoo yaftaroon');
INSERT INTO "transliteration" VALUES(11,22,'11:22','La jarama annahum fee al-akhiratihumu al-akhsaroon');
