INSERT INTO "transliteration" VALUES(12,22,'12:22','Walamma balagha ashuddahu ataynahuhukman waAAilman wakathalika najzee almuhsineen');
INSERT INTO "transliteration" VALUES(12,23,'12:23','Warawadat-hu allatee huwa fee baytihaAAan nafsihi waghallaqati al-abwaba waqalat haytalaka qala maAAatha Allahi innahu rabbee ahsanamathwaya innahu la yuflihu aththalimoon');
INSERT INTO "transliteration" VALUES(12,24,'12:24','Walaqad hammat bihi wahamma biha lawlaan raa burhana rabbihi kathalika linasrifaAAanhu assoo-a walfahshaa innahumin AAibadina almukhlaseen');
INSERT INTO "transliteration" VALUES(12,25,'12:25','Wastabaqa albabawaqaddat qameesahu min duburin waalfaya sayyidahalada albabi qalat ma jazao manarada bi-ahlika soo-an illa an yusjana aw AAathabunaleem');
INSERT INTO "transliteration" VALUES(12,26,'12:26','Qala hiya rawadatnee AAannafsee washahida shahidun min ahliha in kanaqameesuhu qudda min qubulin fasadaqat wahuwa minaalkathibeen');
INSERT INTO "transliteration" VALUES(12,27,'12:27','Wa-in kana qameesuhu qudda minduburin fakathabat wahuwa mina assadiqeen');
INSERT INTO "transliteration" VALUES(12,28,'12:28','Falamma raa qameesahuqudda min duburin qala innahu min kaydikunna innakaydakunna AAatheem');
INSERT INTO "transliteration" VALUES(12,29,'12:29','Yoosufu aAArid AAan hatha wastaghfireelithanbiki innaki kunti mina alkhati-een');
INSERT INTO "transliteration" VALUES(12,30,'12:30','Waqala niswatun fee almadeenatiimraatu alAAazeezi turawidu fataha AAannafsihi qad shaghafaha hubban inna lanarahafee dalalin mubeen');
INSERT INTO "transliteration" VALUES(12,31,'12:31','Falamma samiAAat bimakrihinna arsalatilayhinna waaAAtadat lahunna muttakaan waatat kulla wahidatinminhunna sikkeenan waqalati okhruj AAalayhinna falammaraaynahu akbarnahu waqattaAAna aydiyahunna waqulna hashalillahi ma hatha basharan in hathailla malakun kareem');
INSERT INTO "transliteration" VALUES(12,32,'12:32','Qalat fathalikunna allatheelumtunnanee feehi walaqad rawadtuhu AAan nafsihi fastAAsamawala-in lam yafAAal ma amuruhu layusjanannawalayakoonan mina assaghireen');
INSERT INTO "transliteration" VALUES(12,33,'12:33','Qala rabbi assijnu ahabbuilayya mimma yadAAoonanee ilayhi wa-illa tasrifAAannee kaydahunna asbu ilayhinna waakun mina aljahileen');
INSERT INTO "transliteration" VALUES(12,34,'12:34','Fastajaba lahu rabbuhu fasarafaAAanhu kaydahunna innahu huwa assameeAAu alAAaleem');
INSERT INTO "transliteration" VALUES(12,35,'12:35','Thumma bada lahum min baAAdi maraawoo al-ayati layasjununnahu hatta heen');
INSERT INTO "transliteration" VALUES(12,36,'12:36','Wadakhala maAAahu assijna fatayaniqala ahaduhuma innee aranee aAAsirukhamran waqala al-akharu innee aranee ahmilufawqa ra/see khubzan ta/kulu attayru minhu nabbi/nabita/weelihi inna naraka mina almuhsineen');
INSERT INTO "transliteration" VALUES(12,37,'12:37','Qala la ya/teekuma taAAamunturzaqanihi illa nabba/tukuma bita/weelihiqabla an ya/tiyakuma thalikuma mimmaAAallamanee rabbee innee taraktu millata qawmin layu/minoona billahi wahum bil-akhiratihum kafiroon');
INSERT INTO "transliteration" VALUES(12,38,'12:38','WattabaAAtu millata aba-eeibraheema wa-ishaqa wayaAAqooba ma kanalana an nushrika billahi min shay-in thalikamin fadli Allahi AAalayna waAAala annasiwalakinna akthara annasi layashkuroon');
INSERT INTO "transliteration" VALUES(12,39,'12:39','Ya sahibayi assijniaarbabun mutafarriqoona khayrun ami Allahu alwahidualqahhar');
INSERT INTO "transliteration" VALUES(12,40,'12:40','Ma taAAbudoona min doonihi illaasmaan sammaytumooha antum waabaokumma anzala Allahu biha min sultaninini alhukmu illa lillahi amara allataAAbudoo illa iyyahu thalika addeenualqayyimu walakinna akthara annasi layaAAlamoon');
INSERT INTO "transliteration" VALUES(12,41,'12:41','Ya sahibayi assijniamma ahadukuma fayasqee rabbahu khamranwaamma al-akharu fayuslabu fata/kulu attayrumin ra/sihi qudiya al-amru allathee feehi tastaftiyan');
INSERT INTO "transliteration" VALUES(12,42,'12:42','Waqala lillathee thannaannahu najin minhuma othkurnee AAindarabbika faansahu ashshaytanu thikrarabbihi falabitha fee assijni bidAAa sineen');
INSERT INTO "transliteration" VALUES(12,43,'12:43','Waqala almaliku innee arasabAAa baqaratin simanin ya/kuluhunna sabAAun AAijafunwasabAAa sunbulatin khudrin waokhara yabisatinya ayyuha almalao aftoonee fee ru/yaya inkuntum lirru/ya taAAburoon');
INSERT INTO "transliteration" VALUES(12,44,'12:44','Qaloo adghathu ahlaminwama nahnu bita/weeli al-ahlami biAAalimeen');
INSERT INTO "transliteration" VALUES(12,45,'12:45','Waqala allathee najaminhuma waddakara baAAda ommatin anaonabbi-okum bita/weelihi faarsiloon');
INSERT INTO "transliteration" VALUES(12,46,'12:46','Yoosufu ayyuha assiddeequaftina fee sabAAi baqaratin simaninya/kuluhunna sabAAun AAijafun wasabAAi sunbulatinkhudrin waokhara yabisatin laAAallee arjiAAuila annasi laAAallahum yaAAlamoon');
INSERT INTO "transliteration" VALUES(12,47,'12:47','Qala tazraAAoona sabAAa sineenadaaban fama hasadtum fatharoohu feesunbulihi illa qaleelan mimma ta/kuloon');
INSERT INTO "transliteration" VALUES(12,48,'12:48','Thumma ya/tee min baAAdi thalikasabAAun shidadun ya/kulna ma qaddamtum lahunna illaqaleelan mimma tuhsinoon');
INSERT INTO "transliteration" VALUES(12,49,'12:49','Thumma ya/tee min baAAdi thalika AAamunfeehi yughathu annasu wafeehi yaAAsiroon');
INSERT INTO "transliteration" VALUES(12,50,'12:50','Waqala almaliku i/toonee bihi falammajaahu arrasoolu qala irjiAA ilarabbika fas-alhu ma balu anniswatiallatee qattaAAna aydiyahunna inna rabbeebikaydihinna AAaleem');
INSERT INTO "transliteration" VALUES(12,51,'12:51','Qala ma khatbukunna ithrawadtunna yoosufa AAan nafsihi qulna hasha lillahima AAalimna AAalayhi min soo-in qalatiimraatu alAAazeezi al-ana hashasa alhaqquana rawadtuhu AAan nafsihi wa-innahu lamina assadiqeen');
INSERT INTO "transliteration" VALUES(12,52,'12:52','Thalika liyaAAlama annee lam akhunhubilghaybi waanna Allaha la yahdee kaydaalkha-ineen');
INSERT INTO "transliteration" VALUES(12,53,'12:53','Wama obarri-o nafsee inna annafsalaammaratun bissoo-i illa ma rahimarabbee inna rabbee ghafoorun raheem');
INSERT INTO "transliteration" VALUES(12,54,'12:54','Waqala almaliku i/toonee bihiastakhlishu linafsee falamma kallamahu qalainnaka alyawma ladayna makeenun ameen');
INSERT INTO "transliteration" VALUES(12,55,'12:55','Qala ijAAalnee AAala khaza-inial-ardi innee hafeethun AAaleem');
INSERT INTO "transliteration" VALUES(12,56,'12:56','Wakathalika makanna liyoosufafee al-ardi yatabawwao minha haythu yashaonuseebu birahmatina man nashao walanudeeAAu ajra almuhsineen');
INSERT INTO "transliteration" VALUES(12,57,'12:57','Walaajru al-akhirati khayrun lillatheenaamanoo wakanoo yattaqoon');
INSERT INTO "transliteration" VALUES(12,58,'12:58','Wajaa ikhwatu yoosufa fadakhalooAAalayhi faAAarafahum wahum lahu munkiroon');
INSERT INTO "transliteration" VALUES(12,59,'12:59','Walamma jahhazahum bijahazihimqala i/toonee bi-akhin lakum min abeekum alatarawna annee oofee alkayla waana khayru almunzileen');
INSERT INTO "transliteration" VALUES(12,60,'12:60','Fa-in lam ta/toonee bihi fala kaylalakum AAindee wala taqraboon');
INSERT INTO "transliteration" VALUES(12,61,'12:61','Qaloo sanurawidu AAanhu abahuwa-inna lafaAAiloon');
INSERT INTO "transliteration" VALUES(12,62,'12:62','Waqala lifityanihi ijAAaloo bidaAAatahumfee rihalihim laAAallahum yaAArifoonaha ithainqalaboo ila ahlihim laAAallahum yarjiAAoon');
INSERT INTO "transliteration" VALUES(12,63,'12:63','Falamma rajaAAoo ila abeehim qalooya abana muniAAa minna alkaylufaarsil maAAana akhana naktal wa-innalahu lahafithoon');
INSERT INTO "transliteration" VALUES(12,64,'12:64','Qala hal amanukum AAalayhi illakama amintukum AAala akheehi min qablu fallahukhayrun hafithan wahuwa arhamu arrahimeen');
INSERT INTO "transliteration" VALUES(12,65,'12:65','Walamma fatahoo mataAAahumwajadoo bidaAAatahum ruddat ilayhim qaloo yaabana ma nabghee hathihi bidaAAatunaruddat ilayna wanameeru ahlana wanahfathuakhana wanazdadu kayla baAAeerin thalikakaylun yaseer');
INSERT INTO "transliteration" VALUES(12,66,'12:66','Qala lan orsilahu maAAakum hattatu/tooni mawthiqan mina Allahi lata/tunnanee bihi illaan yuhata bikum falamma atawhu mawthiqahum qalaAllahu AAala ma naqoolu wakeel');
INSERT INTO "transliteration" VALUES(12,67,'12:67','Waqala ya baniyya latadkhuloo min babin wahidin wadkhuloo minabwabin mutafarriqatin wama oghnee AAankum mina Allahimin shay-in ini alhukmu illa lillahiAAalayhi tawakkaltu waAAalayhi falyatawakkali almutawakkiloon');
INSERT INTO "transliteration" VALUES(12,68,'12:68','Walamma dakhaloo min haythuamarahum aboohum ma kana yughnee AAanhum mina Allahimin shay-in illa hajatan fee nafsi yaAAqooba qadahawa-innahu lathoo AAilmin lima AAallamnahuwalakinna akthara annasi layaAAlamoon');
INSERT INTO "transliteration" VALUES(12,69,'12:69','Walamma dakhaloo AAala yoosufaawa ilayhi akhahu qala innee anaakhooka fala tabta-is bima kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(12,70,'12:70','Falamma jahhazahum bijahazihimjaAAala assiqayata fee rahli akheehi thummaaththana mu-aththinun ayyatuha alAAeeruinnakum lasariqoon');
INSERT INTO "transliteration" VALUES(12,71,'12:71','Qaloo waaqbaloo AAalayhim mathatafqidoon');
INSERT INTO "transliteration" VALUES(12,72,'12:72','Qaloo nafqidu suwaAAaalmaliki waliman jaa bihi himlu baAAeerin waanabihi zaAAeem');
INSERT INTO "transliteration" VALUES(12,73,'12:73','Qaloo tallahi laqadAAalimtum ma ji/na linufsida fee al-ardi wamakunna sariqeen');
INSERT INTO "transliteration" VALUES(12,74,'12:74','Qaloo fama jazaohu inkuntum kathibeen');
INSERT INTO "transliteration" VALUES(12,75,'12:75','Qaloo jazaohu man wujida feerahlihi fahuwa jazaohu kathalika najzee aththalimeen');
INSERT INTO "transliteration" VALUES(12,76,'12:76','Fabadaa bi-awAAiyatihim qabla wiAAa-iakheehi thumma istakhrajaha min wiAAa-i akheehi kathalikakidna liyoosufa ma kana liya/khuthaakhahu fee deeni almaliki illa an yashaa AllahunarfaAAu darajatin man nashao wafawqa kulli theeAAilmin AAaleem');
INSERT INTO "transliteration" VALUES(12,77,'12:77','Qaloo in yasriq faqad saraqa akhunlahu min qablu faasarraha yoosufu fee nafsihi walam yubdihalahum qala antum sharrun makanan wallahuaAAlamu bima tasifoon');
INSERT INTO "transliteration" VALUES(12,78,'12:78','Qaloo ya ayyuhaalAAazeezu inna lahu aban shaykhan kabeeran fakhuth ahadanamakanahu inna naraka mina almuhsineen');
INSERT INTO "transliteration" VALUES(12,79,'12:79','Qala maAAatha Allahi anna/khutha illa man wajadna mataAAanaAAindahu inna ithan lathalimoon');
INSERT INTO "transliteration" VALUES(12,80,'12:80','Falamma istay-asoo minhu khalasoonajiyyan qala kabeeruhum alam taAAlamoo anna abakumqad akhatha AAalaykum mawthiqan mina Allahi waminqablu ma farrattum fee yoosufa falan abrahaal-arda hatta ya/thana lee abee aw yahkumaAllahu lee wahuwa khayru alhakimeen');
INSERT INTO "transliteration" VALUES(12,81,'12:81','IrjiAAoo ila abeekum faqooloo yaabana inna ibnaka saraqa wama shahidnailla bima AAalimna wama kunnalilghaybi hafitheen');
INSERT INTO "transliteration" VALUES(12,82,'12:82','Was-ali alqaryata allatee kunnafeeha walAAeera allatee aqbalna feehawa-inna lasadiqoon');
INSERT INTO "transliteration" VALUES(12,83,'12:83','Qala bal sawwalat lakum anfusukumamran fasabrun jameelun AAasa Allahu anya/tiyanee bihim jameeAAan innahu huwa alAAaleemu alhakeem');
INSERT INTO "transliteration" VALUES(12,84,'12:84','Watawalla AAanhum waqala yaasafa AAala yoosufa wabyaddat AAaynahumina alhuzni fahuwa katheem');
INSERT INTO "transliteration" VALUES(12,85,'12:85','Qaloo tallahi taftaotathkuru yoosufa hatta takoona haradanaw takoona mina alhalikeen');
INSERT INTO "transliteration" VALUES(12,86,'12:86','Qala innama ashkoo baththee wahuzneeila Allahi waaAAlamu mina Allahi ma lataAAlamoon');
INSERT INTO "transliteration" VALUES(12,87,'12:87','Ya baniyya ithhaboo fatahassasoomin yoosufa waakheehi wala tay-asoo min rawhi Allahiinnahu la yay-asu min rawhi Allahi illaalqawmu alkafiroon');
INSERT INTO "transliteration" VALUES(12,88,'12:88','Falamma dakhaloo AAalayhi qalooya ayyuha alAAazeezu massana waahlanaaddurru waji/na bibidaAAatin muzjatinfaawfi lana alkayla watasaddaq AAalayna innaAllaha yajzee almutasaddiqeen');
INSERT INTO "transliteration" VALUES(12,89,'12:89','Qala hal AAalimtum mafaAAaltum biyoosufa waakheehi ith antum jahiloon');
INSERT INTO "transliteration" VALUES(12,90,'12:90','Qaloo a-innaka laanta yoosufa qalaana yoosufu wahatha akhee qad manna AllahuAAalayna innahu man yattaqi wayasbir fa-inna Allahala yudeeAAu ajra almuhsineen');
INSERT INTO "transliteration" VALUES(12,91,'12:91','Qaloo tallahi laqad atharakaAllahu AAalayna wa-in kunna lakhati-een');
INSERT INTO "transliteration" VALUES(12,92,'12:92','Qala la tathreeba AAalaykumualyawma yaghfiru Allahu lakum wahuwa arhamu arrahimeen');
INSERT INTO "transliteration" VALUES(12,93,'12:93','Ithhaboo biqameesee hathafaalqoohu AAala wajhi abee ya/ti baseeran wa/tooneebi-ahlikum ajmaAAeen');
INSERT INTO "transliteration" VALUES(12,94,'12:94','Walamma fasalati alAAeeru qalaaboohum innee laajidu reeha yoosufa lawla antufannidoon');
INSERT INTO "transliteration" VALUES(12,95,'12:95','Qaloo tallahi innakalafee dalalika alqadeem');
INSERT INTO "transliteration" VALUES(12,96,'12:96','Falamma an jaa albasheeru alqahuAAala wajhihi fartadda baseeran qalaalam aqul lakum innee aAAlamu mina Allahi ma lataAAlamoon');
INSERT INTO "transliteration" VALUES(12,97,'12:97','Qaloo ya abanaistaghfir lana thunoobana inna kunnakhati-een');
INSERT INTO "transliteration" VALUES(12,98,'12:98','Qala sawfa astaghfiru lakum rabbeeinnahu huwa alghafooru arraheem');
INSERT INTO "transliteration" VALUES(12,99,'12:99','Falamma dakhaloo AAala yoosufaawa ilayhi abawayhi waqala odkhuloo misrain shaa Allahu amineen');
INSERT INTO "transliteration" VALUES(12,100,'12:100','WarafaAAa abawayhi AAala alAAarshiwakharroo lahu sujjadan waqala ya abati hathata/weelu ru/yaya min qablu qad jaAAalaha rabbee haqqanwaqad ahsana bee ith akhrajanee mina assijniwajaa bikum mina albadwi min baAAdi an nazagha ashshaytanubaynee wabayna ikhwatee inna rabbee lateefun limayashao innahu huwa alAAaleemu alhakeem');
INSERT INTO "transliteration" VALUES(12,101,'12:101','Rabbi qad ataytanee mina almulkiwaAAallamtanee min ta/weeli al-ahadeethi fatira assamawatiwal-ardi anta waliyyee fee addunyawal-akhirati tawaffanee musliman waalhiqneebissaliheen');
INSERT INTO "transliteration" VALUES(12,102,'12:102','Thalika min anba-i alghaybinooheehi ilayka wama kunta ladayhim ithajmaAAoo amrahum wahum yamkuroon');
INSERT INTO "transliteration" VALUES(12,103,'12:103','Wama aktharu annasiwalaw harasta bimu/mineen');
INSERT INTO "transliteration" VALUES(12,104,'12:104','Wama tas-aluhum AAalayhi min ajrinin huwa illa thikrun lilAAalameen');
INSERT INTO "transliteration" VALUES(12,105,'12:105','Wakaayyin min ayatin fee assamawatiwal-ardi yamurroona AAalayha wahum AAanhamuAAridoon');
INSERT INTO "transliteration" VALUES(12,106,'12:106','Wama yu/minu aktharuhum billahiilla wahum mushrikoon');
INSERT INTO "transliteration" VALUES(12,107,'12:107','Afaaminoo an ta/tiyahum ghashiyatunmin AAathabi Allahi aw ta/tiyahumu assaAAatubaghtatan wahum la yashAAuroon');
INSERT INTO "transliteration" VALUES(12,108,'12:108','Qul hathihi sabeelee adAAoo ilaAllahi AAala baseeratin ana wamaniittabaAAanee wasubhana Allahi wama anamina almushrikeen');
INSERT INTO "transliteration" VALUES(12,109,'12:109','Wama arsalna min qablika illarijalan noohee ilayhim min ahli alquraafalam yaseeroo fee al-ardi fayanthurookayfa kana AAaqibatu allatheena min qablihimwaladaru al-akhirati khayrun lillatheenaittaqaw afala taAAqiloon');
INSERT INTO "transliteration" VALUES(12,110,'12:110','Hatta itha istay-asa arrusuluwathannoo annahum qad kuthiboo jaahumnasruna fanujjiya man nashao walayuraddu ba/suna AAani alqawmi almujrimeen');
INSERT INTO "transliteration" VALUES(12,111,'12:111','Laqad kana fee qasasihimAAibratun li-olee al-albabi ma kana hadeethanyuftara walakin tasdeeqa allatheebayna yadayhi watafseela kulli shay-in wahudan warahmatanliqawmin yu/minoon');
INSERT INTO "transliteration" VALUES(13,1,'13:1','Alif-lam-meem-ra tilka ayatualkitabi wallathee onzila ilayka minrabbika alhaqqu walakinna akthara annasila yu/minoon');
INSERT INTO "transliteration" VALUES(13,2,'13:2','Allahu allathee rafaAAa assamawatibighayri AAamadin tarawnaha thumma istawa AAalaalAAarshi wasakhkhara ashshamsa walqamara kullunyajree li-ajalin musamman yudabbiru al-amra yufassilu al-ayatilaAAallakum biliqa-i rabbikum tooqinoon');
INSERT INTO "transliteration" VALUES(13,3,'13:3','Wahuwa allathee madda al-ardawajaAAala feeha rawasiya waanharan waminkulli aththamarati jaAAala feeha zawjayniithnayni yughshee allayla annahara inna fee thalikalaayatin liqawmin yatafakkaroon');
INSERT INTO "transliteration" VALUES(13,4,'13:4','Wafee al-ardi qitaAAun mutajawiratunwajannatun min aAAnabin wazarAAun wanakheelun sinwanunwaghayru sinwanin yusqa bima-in wahidinwanufaddilu baAAdaha AAala baAAdinfee alokuli inna fee thalika laayatinliqawmin yaAAqiloon');
INSERT INTO "transliteration" VALUES(13,5,'13:5','Wa-in taAAjab faAAajabun qawluhum a-ithakunna turaban a-inna lafee khalqin jadeedinola-ika allatheena kafaroo birabbihim waola-ikaal-aghlalu fee aAAnaqihim waola-ika as-habuannari hum feeha khalidoon');
INSERT INTO "transliteration" VALUES(13,6,'13:6','WayastaAAjiloonaka bissayyi-atiqabla alhasanati waqad khalat min qablihimu almathulatuwa-inna rabbaka lathoo maghfiratin linnasiAAala thulmihim wa-inna rabbaka lashadeedualAAiqab');
INSERT INTO "transliteration" VALUES(13,7,'13:7','Wayaqoolu allatheena kafaroo lawlaonzila AAalayhi ayatun min rabbihi innama anta munthirunwalikulli qawmin had');
INSERT INTO "transliteration" VALUES(13,8,'13:8','Allahu yaAAlamu ma tahmilukullu ontha wama tagheedu al-arhamuwama tazdadu wakullu shay-in AAindahu bimiqdar');
INSERT INTO "transliteration" VALUES(13,9,'13:9','AAalimu alghaybi washshahadatialkabeeru almutaAAal');
INSERT INTO "transliteration" VALUES(13,10,'13:10','Sawaon minkum man asarra alqawlawaman jahara bihi waman huwa mustakhfin billayli wasaribunbinnahar');
INSERT INTO "transliteration" VALUES(13,11,'13:11','Lahu muAAaqqibatun min bayni yadayhiwamin khalfihi yahfathoonahu min amri Allahiinna Allaha la yughayyiru ma biqawmin hattayughayyiroo ma bi-anfusihim wa-itha aradaAllahu biqawmin soo-an fala maradda lahu wamalahum min doonihi min wal');
INSERT INTO "transliteration" VALUES(13,12,'13:12','Huwa allathee yureekumu albarqakhawfan watamaAAan wayunshi-o assahaba aththiqal');
INSERT INTO "transliteration" VALUES(13,13,'13:13','Wayusabbihu arraAAdu bihamdihiwalmala-ikatu min kheefatihi wayursilu assawaAAiqafayuseebu biha man yashao wahum yujadiloonafee Allahi wahuwa shadeedu almihal');
INSERT INTO "transliteration" VALUES(13,14,'13:14','Lahu daAAwatu alhaqqi wallatheenayadAAoona min doonihi la yastajeeboona lahum bishay-in illakabasiti kaffayhi ila alma-iliyablugha fahu wama huwa bibalighihi wamaduAAao alkafireena illa fee dalal');
INSERT INTO "transliteration" VALUES(13,15,'13:15','Walillahi yasjudu man fee assamawatiwal-ardi tawAAan wakarhan wathilaluhumbilghuduwwi wal-asal');
INSERT INTO "transliteration" VALUES(13,16,'13:16','Qul man rabbu assamawatiwal-ardi quli Allahu qul afattakhathtummin doonihi awliyaa la yamlikoona li-anfusihimnafAAan wala darran qul hal yastawee al-aAAmawalbaseeru am hal tastawee aththulumatuwannooru am jaAAaloo lillahi shurakaakhalaqoo kakhalqihi fatashabaha alkhalqu AAalayhim quliAllahu khaliqu kulli shay-in wahuwa alwahidualqahhar');
INSERT INTO "transliteration" VALUES(13,17,'13:17','Anzala mina assama-i maanfasalat awdiyatun biqadariha fahtamalaassaylu zabadan rabiyan wamimma yooqidoonaAAalayhi fee annari ibtighaa hilyatinaw mataAAin zabadun mithluhu kathalika yadribuAllahu alhaqqa walbatila faammaazzabadu fayathhabu jufaan waamma mayanfaAAu annasa fayamkuthu fee al-ardi kathalikayadribu Allahu al-amthal');
INSERT INTO "transliteration" VALUES(13,18,'13:18','Lillatheena istajaboolirabbihimu alhusna wallatheena lamyastajeeboo lahu law anna lahum ma fee al-ardijameeAAan wamithlahu maAAahu laftadaw bihi ola-ikalahum soo-o alhisabi wama/wahum jahannamuwabi/sa almihad');
INSERT INTO "transliteration" VALUES(13,19,'13:19','Afaman yaAAlamu annama onzila ilaykamin rabbika alhaqqu kaman huwa aAAma innamayatathakkaru oloo al-albab');
INSERT INTO "transliteration" VALUES(13,20,'13:20','Allatheena yoofoona biAAahdi Allahiwala yanqudoona almeethaq');
INSERT INTO "transliteration" VALUES(13,21,'13:21','Wallatheena yasiloonama amara Allahu bihi an yoosala wayakhshawnarabbahum wayakhafoona soo-a alhisab');
