INSERT INTO "transliteration" VALUES(15,90,'15:90','Kama anzalna AAalaalmuqtasimeen');
INSERT INTO "transliteration" VALUES(15,91,'15:91','Allatheena jaAAaloo alqur-anaAAideen');
