INSERT INTO "transliteration" VALUES(15,97,'15:97','Walaqad naAAlamu annaka yadeequ sadrukabima yaqooloon');
INSERT INTO "transliteration" VALUES(15,98,'15:98','Fasabbih bihamdi rabbika wakunmina assajideen');
INSERT INTO "transliteration" VALUES(15,99,'15:99','WaAAbud rabbaka hattaya/tiyaka alyaqeen');
INSERT INTO "transliteration" VALUES(16,1,'16:1','Ata amru Allahi falatastaAAjiloohu subhanahu wataAAala AAammayushrikoon');
