INSERT INTO "transliteration" VALUES(16,3,'16:3','Khalaqa assamawati wal-ardabilhaqqi taAAala AAammayushrikoon');
INSERT INTO "transliteration" VALUES(16,4,'16:4','Khalaqa al-insana min nutfatinfa-itha huwa khaseemun mubeen');
INSERT INTO "transliteration" VALUES(16,5,'16:5','Wal-anAAama khalaqahalakum feeha dif-on wamanafiAAu waminhata-kuloon');
INSERT INTO "transliteration" VALUES(16,6,'16:6','Walakum feeha jamalun heenatureehoona waheena tasrahoon');
