INSERT INTO "transliteration" VALUES(16,8,'16:8','Walkhayla walbighalawalhameera litarkabooha wazeenatanwayakhluqu ma la taAAlamoon');
INSERT INTO "transliteration" VALUES(16,9,'16:9','WaAAala Allahi qasdu assabeeliwaminha ja-irun walaw shaa lahadakumajmaAAeen');
INSERT INTO "transliteration" VALUES(16,10,'16:10','Huwa allathee anzala mina assama-imaan lakum minhu sharabun waminhu shajarun feehituseemoon');
INSERT INTO "transliteration" VALUES(16,11,'16:11','Yunbitu lakum bihi azzarAAa wazzaytoonawannakheela wal-aAAnaba wamin kulli aththamaratiinna fee thalika laayatan liqawmin yatafakkaroon');
INSERT INTO "transliteration" VALUES(16,12,'16:12','Wasakhkhara lakumu allayla wannaharawashshamsa walqamara wannujoomumusakhkharatun bi-amrihi inna fee thalika laayatinliqawmin yaAAqiloon');
INSERT INTO "transliteration" VALUES(16,13,'16:13','Wama tharaa lakum fee al-ardimukhtalifan alwanuhu inna fee thalika laayatanliqawmin yaththakkaroon');
INSERT INTO "transliteration" VALUES(16,14,'16:14','Wahuwa allathee sakhkhara albahralita/kuloo minhu lahman tariyyan watastakhrijoominhu hilyatan talbasoonaha watara alfulkamawakhira feehi walitabtaghoo min fadlihiwalaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(16,15,'16:15','Waalqa fee al-ardi rawasiyaan tameeda bikum waanharan wasubulan laAAallakum tahtadoon');
INSERT INTO "transliteration" VALUES(16,16,'16:16','WaAAalamatin wabinnajmihum yahtadoon');
INSERT INTO "transliteration" VALUES(16,17,'16:17','Afaman yakhluqu kaman la yakhluquafala tathakkaroon');
INSERT INTO "transliteration" VALUES(16,18,'16:18','Wa-in taAAuddoo niAAmata Allahi latuhsooha inna Allaha laghafoorun raheem');
INSERT INTO "transliteration" VALUES(16,19,'16:19','Wallahu yaAAlamu matusirroona wama tuAAlinoon');
INSERT INTO "transliteration" VALUES(16,20,'16:20','Wallatheena yadAAoona mindooni Allahi la yakhluqoona shay-an wahumyukhlaqoon');
INSERT INTO "transliteration" VALUES(16,21,'16:21','Amwatun ghayru ahya-inwama yashAAuroona ayyana yubAAathoon');
INSERT INTO "transliteration" VALUES(16,22,'16:22','Ilahukum ilahun wahidunfallatheena la yu/minoona bil-akhiratiquloobuhum munkiratun wahum mustakbiroon');
INSERT INTO "transliteration" VALUES(16,23,'16:23','La jarama anna Allaha yaAAlamuma yusirroona wama yuAAlinoona innahu la yuhibbualmustakbireen');
INSERT INTO "transliteration" VALUES(16,24,'16:24','Wa-itha qeela lahum mathaanzala rabbukum qaloo asateeru al-awwaleen');
INSERT INTO "transliteration" VALUES(16,25,'16:25','Liyahmiloo awzarahum kamilatanyawma alqiyamati wamin awzari allatheena yudilloonahumbighayri AAilmin ala saa ma yaziroon');
INSERT INTO "transliteration" VALUES(16,26,'16:26','Qad makara allatheena min qablihimfaata Allahu bunyanahum mina alqawaAAidifakharra AAalayhimu assaqfu min fawqihim waatahumualAAathabu min haythu la yashAAuroon');
INSERT INTO "transliteration" VALUES(16,27,'16:27','Thumma yawma alqiyamati yukhzeehimwayaqoolu ayna shuraka-iya allatheena kuntum tushaqqoonafeehim qala allatheena ootoo alAAilma inna alkhizyaalyawma wassoo-a AAala alkafireen');
INSERT INTO "transliteration" VALUES(16,28,'16:28','Allatheena tatawaffahumu almala-ikatuthalimee anfusihim faalqawoo assalamama kunna naAAmalu min soo-in bala inna AllahaAAaleemun bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(16,29,'16:29','Fadkhuloo abwaba jahannama khalideenafeeha falabi/sa mathwa almutakabbireen');
INSERT INTO "transliteration" VALUES(16,30,'16:30','Waqeela lillatheena ittaqaw mathaanzala rabbukum qaloo khayran lillatheena ahsanoofee hathihi addunya hasanatun waladarual-akhirati khayrun walaniAAma daru almuttaqeen');
INSERT INTO "transliteration" VALUES(16,31,'16:31','Jannatu AAadnin yadkhuloonahatajree min tahtiha al-anharu lahum feehama yashaoona kathalika yajzee Allahualmuttaqeen');
INSERT INTO "transliteration" VALUES(16,32,'16:32','Allatheena tatawaffahumu almala-ikatutayyibeena yaqooloona salamun AAalaykumu odkhulooaljannata bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(16,33,'16:33','Hal yanthuroona illa anta/tiyahumu almala-ikatu aw ya/tiya amru rabbika kathalikafaAAala allatheena min qablihim wama thalamahumuAllahu walakin kanoo anfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(16,34,'16:34','Faasabahum sayyi-atu maAAamiloo wahaqa bihim ma kanoo bihiyastahzi-oon');
INSERT INTO "transliteration" VALUES(16,35,'16:35','Waqala allatheena ashrakoo lawshaa Allahu ma AAabadna min doonihimin shay-in nahnu wala abaonawala harramna min doonihi min shay-in kathalikafaAAala allatheena min qablihim fahal AAala arrusuliilla albalaghu almubeen');
INSERT INTO "transliteration" VALUES(16,36,'16:36','Walaqad baAAathna fee kulli ommatinrasoolan ani oAAbudoo Allaha wajtaniboo attaghootafaminhum man hada Allahu waminhum man haqqatAAalayhi addalalatu faseeroo fee al-ardifanthuroo kayfa kana AAaqibatualmukaththibeen');
INSERT INTO "transliteration" VALUES(16,37,'16:37','In tahris AAala hudahumfa-inna Allaha la yahdee man yudillu wamalahum min nasireen');
INSERT INTO "transliteration" VALUES(16,38,'16:38','Waaqsamoo billahi jahda aymanihimla yabAAathu Allahu man yamootu bala waAAdanAAalayhi haqqan walakinna akthara annasila yaAAlamoon');
