INSERT INTO "transliteration" VALUES(16,42,'16:42','Allatheena sabaroo waAAalarabbihim yatawakkaloon');
INSERT INTO "transliteration" VALUES(16,43,'16:43','Wama arsalna min qablika illarijalan noohee ilayhim fas-aloo ahla aththikriin kuntum la taAAlamoon');
INSERT INTO "transliteration" VALUES(16,44,'16:44','Bilbayyinati wazzuburiwaanzalna ilayka aththikra litubayyina linnasima nuzzila ilayhim walaAAallahum yatafakkaroon');
INSERT INTO "transliteration" VALUES(16,45,'16:45','Afaamina allatheena makaroo assayyi-atian yakhsifa Allahu bihimu al-arda aw ya/tiyahumualAAathabu min haythu la yashAAuroon');
INSERT INTO "transliteration" VALUES(16,46,'16:46','Aw ya/khuthahum fee taqallubihim famahum bimuAAjizeen');
INSERT INTO "transliteration" VALUES(16,47,'16:47','Aw ya/khuthahum AAalatakhawwufin fa-inna rabbakum laraoofun raheem');
