INSERT INTO "transliteration" VALUES(16,55,'16:55','Liyakfuroo bima ataynahumfatamattaAAoo fasawfa taAAlamoon');
INSERT INTO "transliteration" VALUES(16,56,'16:56','WayajAAaloona lima layaAAlamoona naseeban mimma razaqnahum tallahilatus-alunna AAamma kuntum taftaroon');
INSERT INTO "transliteration" VALUES(16,57,'16:57','WayajAAaloona lillahi albanatisubhanahu walahum ma yashtahoon');
