INSERT INTO "transliteration" VALUES(16,60,'16:60','Lillatheena la yu/minoona bil-akhiratimathalu assaw-i walillahi almathalu al-aAAlawahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(16,61,'16:61','Walaw yu-akhithu Allahuannasa bithulmihim ma tarakaAAalayha min dabbatin walakin yu-akhkhiruhumila ajalin musamman fa-itha jaa ajaluhum layasta/khiroona saAAatan wala yastaqdimoon');
INSERT INTO "transliteration" VALUES(16,62,'16:62','WayajAAaloona lillahi mayakrahoona watasifu alsinatuhumu alkathiba annalahumu alhusna la jarama anna lahumu annarawaannahum mufratoon');
INSERT INTO "transliteration" VALUES(16,63,'16:63','Tallahi laqad arsalnaila omamin min qablika fazayyana lahumu ashshaytanuaAAmalahum fahuwa waliyyuhumu alyawma walahum AAathabunaleem');
