INSERT INTO "transliteration" VALUES(16,66,'16:66','Wa-inna lakum fee al-anAAamilaAAibratan nusqeekum mimma fee butoonihi min baynifarthin wadamin labanan khalisan sa-ighan lishsharibeen');
