INSERT INTO "transliteration" VALUES(16,68,'16:68','Waawha rabbuka ila annahliani ittakhithee mina aljibali buyootan wamina ashshajariwamimma yaAArishoon');
INSERT INTO "transliteration" VALUES(16,69,'16:69','Thumma kulee min kulli aththamaratifaslukee subula rabbiki thululan yakhruju min butoonihasharabun mukhtalifun alwanuhu feehi shifaonlinnasi inna fee thalika laayatanliqawmin yatafakkaroon');
INSERT INTO "transliteration" VALUES(16,70,'16:70','Wallahu khalaqakum thummayatawaffakum waminkum man yuraddu ila arthalialAAumuri likay la yaAAlama baAAda AAilmin shay-an innaAllaha AAaleemun qadeer');
INSERT INTO "transliteration" VALUES(16,71,'16:71','Wallahu faddala baAAdakumAAala baAAdin fee arrizqi fama allatheenafuddiloo biraddee rizqihim AAala mamalakat aymanuhum fahum feehi sawaon afabiniAAmatiAllahi yajhadoon');
INSERT INTO "transliteration" VALUES(16,72,'16:72','Wallahu jaAAala lakum minanfusikum azwajan wajaAAala lakum min azwajikumbaneena wahafadatan warazaqakum mina attayyibatiafabilbatili yu/minoona wabiniAAmati Allahihum yakfuroon');
INSERT INTO "transliteration" VALUES(16,73,'16:73','WayaAAbudoona min dooni Allahi mala yamliku lahum rizqan mina assamawatiwal-ardi shay-an wala yastateeAAoon');
INSERT INTO "transliteration" VALUES(16,74,'16:74','Fala tadriboo lillahial-amthala inna Allaha yaAAlamu waantum lataAAlamoon');
INSERT INTO "transliteration" VALUES(16,75,'16:75','Daraba Allahu mathalan AAabdanmamlookan la yaqdiru AAala shay-in waman razaqnahuminna rizqan hasanan fahuwa yunfiqu minhu sirranwajahran hal yastawoona alhamdu lillahi balaktharuhum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(16,76,'16:76','Wadaraba Allahu mathalanrajulayni ahaduhuma abkamu la yaqdiru AAalashay-in wahuwa kallun AAala mawlahu aynamayuwajjihhu la ya/ti bikhayrin hal yastawee huwa wamanya/muru bilAAadli wahuwa AAala siratinmustaqeem');
INSERT INTO "transliteration" VALUES(16,77,'16:77','Walillahi ghaybu assamawatiwal-ardi wama amru assaAAatiilla kalamhi albasari aw huwa aqrabu innaAllaha AAala kulli shay-in qadeer');
INSERT INTO "transliteration" VALUES(16,78,'16:78','Wallahu akhrajakum min butooniommahatikum la taAAlamoona shay-an wajaAAala lakumuassamAAa wal-absara wal-af-idatalaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(16,79,'16:79','Alam yaraw ila attayrimusakhkharatin fee jawwi assama-i mayumsikuhunna illa Allahu inna fee thalika laayatinliqawmin yu/minoon');
INSERT INTO "transliteration" VALUES(16,80,'16:80','Wallahu jaAAala lakum minbuyootikum sakanan wajaAAala lakum min juloodi al-anAAamibuyootan tastakhiffoonaha yawma thaAAnikumwayawma iqamatikum wamin aswafihawaawbariha waashAAariha athathanwamataAAan ila heen');
INSERT INTO "transliteration" VALUES(16,81,'16:81','Wallahu jaAAala lakum mimmakhalaqa thilalan wajaAAala lakum mina aljibaliaknanan wajaAAala lakum sarabeela taqeekumu alharrawasarabeela taqeekum ba/sakum kathalika yutimmuniAAmatahu AAalaykum laAAallakum tuslimoon');
INSERT INTO "transliteration" VALUES(16,82,'16:82','Fa-in tawallaw fa-innama AAalaykaalbalaghu almubeen');
INSERT INTO "transliteration" VALUES(16,83,'16:83','YaAArifoona niAAmata Allahi thummayunkiroonaha waaktharuhumu alkafiroon');
INSERT INTO "transliteration" VALUES(16,84,'16:84','Wayawma nabAAathu min kulli ommatinshaheedan thumma la yu/thanu lillatheenakafaroo wala hum yustaAAtaboon');
INSERT INTO "transliteration" VALUES(16,85,'16:85','Wa-itha raa allatheena thalamooalAAathaba fala yukhaffafu AAanhum wala humyuntharoon');
INSERT INTO "transliteration" VALUES(16,86,'16:86','Wa-itha raa allatheenaashrakoo shurakaahum qaloo rabbana haola-ishurakaona allatheena kunna nadAAoomin doonika faalqaw ilayhimu alqawla innakum lakathiboon');
INSERT INTO "transliteration" VALUES(16,87,'16:87','Waalqaw ila Allahi yawma-ithinassalama wadalla AAanhum ma kanooyaftaroon');
INSERT INTO "transliteration" VALUES(16,88,'16:88','Allatheena kafaroo wasaddooAAan sabeeli Allahi zidnahum AAathaban fawqaalAAathabi bima kanoo yufsidoon');
INSERT INTO "transliteration" VALUES(16,89,'16:89','Wayawma nabAAathu fee kulli ommatinshaheedan AAalayhim min anfusihim waji/na bika shaheedanAAala haola-i wanazzalna AAalaykaalkitaba tibyanan likulli shay-in wahudan warahmatanwabushra lilmuslimeen');
INSERT INTO "transliteration" VALUES(16,90,'16:90','Inna Allaha ya/muru bilAAadliwal-ihsani wa-eeta-i theealqurba wayanha AAani alfahsha-i walmunkariwalbaghyi yaAAithukum laAAallakum tathakkaroon');
INSERT INTO "transliteration" VALUES(16,91,'16:91','Waawfoo biAAahdi Allahi ithaAAahadtum wala tanqudoo al-aymanabaAAda tawkeediha waqad jaAAaltumu Allaha AAalaykumkafeelan inna Allaha yaAAlamu ma tafAAaloon');
INSERT INTO "transliteration" VALUES(16,92,'16:92','Wala takoonoo kallatee naqadatghazlaha min baAAdi quwwatin ankathan tattakhithoonaaymanakum dakhalan baynakum an takoona ommatun hiya arbamin ommatin innama yablookumu Allahu bihiwalayubayyinanna lakum yawma alqiyamati ma kuntumfeehi takhtalifoon');
INSERT INTO "transliteration" VALUES(16,93,'16:93','Walaw shaa Allahu lajaAAalakumommatan wahidatan walakin yudillu man yashaowayahdee man yashao walatus-alunna AAamma kuntumtaAAmaloon');
INSERT INTO "transliteration" VALUES(16,94,'16:94','Wala tattakhithoo aymanakumdakhalan baynakum fatazilla qadamun baAAda thubootiha watathooqooassoo-a bima sadadtum AAan sabeeli Allahiwalakum AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(16,95,'16:95','Wala tashtaroo biAAahdi Allahithamanan qaleelan innama AAinda Allahi huwa khayrunlakum in kuntum taAAlamoon');
INSERT INTO "transliteration" VALUES(16,96,'16:96','Ma AAindakum yanfadu wamaAAinda Allahi baqin walanajziyanna allatheenasabaroo ajrahum bi-ahsani ma kanooyaAAmaloon');
INSERT INTO "transliteration" VALUES(16,97,'16:97','Man AAamila salihan min thakarinaw ontha wahuwa mu/minun falanuhyiyannahu hayatantayyibatan walanajziyannahum ajrahum bi-ahsani makanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(16,98,'16:98','Fa-itha qara/ta alqur-ana fastaAAithbillahi mina ashshaytani arrajeem');
INSERT INTO "transliteration" VALUES(16,99,'16:99','Innahu laysa lahu sultanun AAalaallatheena amanoo waAAala rabbihimyatawakkaloon');
INSERT INTO "transliteration" VALUES(16,100,'16:100','Innama sultanuhu AAalaallatheena yatawallawnahu wallatheena humbihi mushrikoon');
INSERT INTO "transliteration" VALUES(16,101,'16:101','Wa-itha baddalna ayatanmakana ayatin wallahu aAAlamu bimayunazzilu qaloo innama anta muftarin bal aktharuhumla yaAAlamoon');
INSERT INTO "transliteration" VALUES(16,102,'16:102','Qul nazzalahu roohu alqudusi minrabbika bilhaqqi liyuthabbita allatheena amanoowahudan wabushra lilmuslimeen');
INSERT INTO "transliteration" VALUES(16,103,'16:103','Walaqad naAAlamu annahum yaqooloona innamayuAAallimuhu basharun lisanu allathee yulhidoonailayhi aAAjamiyyun wahatha lisanun AAarabiyyunmubeen');
INSERT INTO "transliteration" VALUES(16,104,'16:104','Inna allatheena la yu/minoonabi-ayati Allahi la yahdeehimu Allahuwalahum AAathabun aleem');
INSERT INTO "transliteration" VALUES(16,105,'16:105','Innama yaftaree alkathibaallatheena la yu/minoona bi-ayati Allahiwaola-ika humu alkathiboon');
INSERT INTO "transliteration" VALUES(16,106,'16:106','Man kafara billahi minbaAAdi eemanihi illa man okriha waqalbuhu mutma-innunbil-eemani walakin man sharaha bilkufrisadran faAAalayhim ghadabun mina Allahiwalahum AAathabun AAatheem');
INSERT INTO "transliteration" VALUES(16,107,'16:107','Thalika bi-annahumu istahabbooalhayata addunya AAala al-akhiratiwaanna Allaha la yahdee alqawma alkafireen');
INSERT INTO "transliteration" VALUES(16,108,'16:108','Ola-ika allatheena tabaAAaAllahu AAala quloobihim wasamAAihim waabsarihimwaola-ika humu alghafiloon');
INSERT INTO "transliteration" VALUES(16,109,'16:109','La jarama annahum fee al-akhiratihumu alkhasiroon');
INSERT INTO "transliteration" VALUES(16,110,'16:110','Thumma inna rabbaka lillatheena hajaroomin baAAdi ma futinoo thumma jahadoo wasabarooinna rabbaka min baAAdiha laghafoorun raheem');
INSERT INTO "transliteration" VALUES(16,111,'16:111','Yawma ta/tee kullu nafsin tujadiluAAan nafsiha watuwaffa kullu nafsin maAAamilat wahum la yuthlamoon');
INSERT INTO "transliteration" VALUES(16,112,'16:112','Wadaraba Allahu mathalanqaryatan kanat aminatan mutma-innatanya/teeha rizquha raghadan min kulli makaninfakafarat bi-anAAumi Allahi faathaqaha Allahulibasa aljooAAi walkhawfi bima kanooyasnaAAoon');
INSERT INTO "transliteration" VALUES(16,113,'16:113','Walaqad jaahum rasoolun minhum fakaththaboohufaakhathahumu alAAathabu wahum thalimoon');
INSERT INTO "transliteration" VALUES(16,114,'16:114','Fakuloo mimma razaqakumu Allahuhalalan tayyiban washkuroo niAAmataAllahi in kuntum iyyahu taAAbudoon');
INSERT INTO "transliteration" VALUES(16,115,'16:115','Innama harrama AAalaykumualmaytata waddama walahma alkhinzeeri wamaohilla lighayri Allahi bihi famani idturra ghayra baghinwala AAadin fa-inna Allaha ghafoorun raheem');
INSERT INTO "transliteration" VALUES(16,116,'16:116','Wala taqooloo lima tasifualsinatukumu alkathiba hatha halalunwahatha haramun litaftaroo AAala Allahialkathiba inna allatheena yaftaroona AAalaAllahi alkathiba la yuflihoon');
INSERT INTO "transliteration" VALUES(16,117,'16:117','MataAAun qaleelun walahum AAathabunaleem');
INSERT INTO "transliteration" VALUES(16,118,'16:118','WaAAala allatheena hadooharramna ma qasasnaAAalayka min qablu wama thalamnahumwalakin kanoo anfusahum yathlimoon');
INSERT INTO "transliteration" VALUES(16,119,'16:119','Thumma inna rabbaka lillatheenaAAamiloo assoo-a bijahalatin thumma taboomin baAAdi thalika waaslahoo inna rabbakamin baAAdiha laghafoorun raheem');
INSERT INTO "transliteration" VALUES(16,120,'16:120','Inna ibraheema kana ommatan qanitanlillahi haneefan walam yaku mina almushrikeen');
INSERT INTO "transliteration" VALUES(16,121,'16:121','Shakiran li-anAAumihi ijtabahuwahadahu ila siratin mustaqeem');
INSERT INTO "transliteration" VALUES(16,122,'16:122','Waataynahu fee addunyahasanatan wa-innahu fee al-akhirati lamina assaliheen');
INSERT INTO "transliteration" VALUES(16,123,'16:123','Thumma awhayna ilayka aniittabiAA millata ibraheema haneefan wama kanamina almushrikeen');
INSERT INTO "transliteration" VALUES(16,124,'16:124','Innama juAAila assabtu AAalaallatheena ikhtalafoo feehi wa-inna rabbaka layahkumubaynahum yawma alqiyamati feema kanoo feehiyakhtalifoon');
INSERT INTO "transliteration" VALUES(16,125,'16:125','OdAAu ila sabeeli rabbika bilhikmatiwalmawAAithati alhasanati wajadilhumbillatee hiya ahsanu inna rabbaka huwa aAAlamubiman dalla AAan sabeelihi wahuwa aAAlamu bilmuhtadeen');
INSERT INTO "transliteration" VALUES(16,126,'16:126','Wa-in AAaqabtum faAAaqiboobimithli ma AAooqibtum bihi wala-in sabartum lahuwakhayrun lissabireen');
INSERT INTO "transliteration" VALUES(16,127,'16:127','Wasbir wama sabrukailla billahi wala tahzanAAalayhim wala taku fee dayqin mimmayamkuroon');
INSERT INTO "transliteration" VALUES(16,128,'16:128','Inna Allaha maAAa allatheenaittaqaw wallatheena hum muhsinoon');
INSERT INTO "transliteration" VALUES(17,1,'17:1','Subhana allathee asrabiAAabdihi laylan mina almasjidi alharami ilaalmasjidi al-aqsa allathee barakna hawlahulinuriyahu min ayatina innahu huwa assameeAAualbaseer');
INSERT INTO "transliteration" VALUES(17,2,'17:2','Waatayna moosa alkitabawajaAAalnahu hudan libanee isra-eela allatattakhithoo min doonee wakeela');
INSERT INTO "transliteration" VALUES(17,3,'17:3','Thurriyyata man hamalnamaAAa noohin innahu kana AAabdan shakoora');
INSERT INTO "transliteration" VALUES(17,4,'17:4','Waqadayna ila banee isra-eelafee alkitabi latufsidunna fee al-ardi marratayniwalataAAlunna AAuluwwan kabeera');
INSERT INTO "transliteration" VALUES(17,5,'17:5','Fa-itha jaa waAAdu oolahumabaAAathna AAalaykum AAibadan lana oleeba/sin shadeedin fajasoo khilala addiyariwakana waAAdan mafAAoola');
INSERT INTO "transliteration" VALUES(17,6,'17:6','Thumma radadna lakumu alkarrataAAalayhim waamdadnakum bi-amwalin wabaneenawajaAAalnakum akthara nafeera');
INSERT INTO "transliteration" VALUES(17,7,'17:7','In ahsantum ahsantumli-anfusikum wa-in asa/tum falaha fa-itha jaawaAAdu al-akhirati liyasoo-oo wujoohakum waliyadkhulooalmasjida kama dakhaloohu awwala marratin waliyutabbiroo maAAalaw tatbeera');
INSERT INTO "transliteration" VALUES(17,8,'17:8','AAasa rabbukum an yarhamakumwa-in AAudtum AAudna wajaAAalna jahannama lilkafireenahaseera');
INSERT INTO "transliteration" VALUES(17,9,'17:9','Inna hatha alqur-ana yahdeelillatee hiya aqwamu wayubashshiru almu/mineena allatheenayaAAmaloona assalihati anna lahum ajrankabeera');
INSERT INTO "transliteration" VALUES(17,10,'17:10','Waanna allatheena layu/minoona bil-akhirati aAAtadna lahum AAathabanaleema');
INSERT INTO "transliteration" VALUES(17,11,'17:11','WayadAAu al-insanu bishsharriduAAaahu bilkhayri wakana al-insanuAAajoola');
INSERT INTO "transliteration" VALUES(17,12,'17:12','WajaAAalna allayla wannaharaayatayni famahawna ayata allayliwajaAAalna ayata annahari mubsiratanlitabtaghoo fadlan min rabbikum walitaAAlamoo AAadada assineenawalhisaba wakulla shay-in fassalnahutafseela');
INSERT INTO "transliteration" VALUES(17,13,'17:13','Wakulla insanin alzamnahu ta-irahufee AAunuqihi wanukhriju lahu yawma alqiyamati kitabanyalqahu manshoora');
INSERT INTO "transliteration" VALUES(17,14,'17:14','Iqra/ kitabaka kafa binafsikaalyawma AAalayka haseeba');
INSERT INTO "transliteration" VALUES(17,15,'17:15','Mani ihtada fa-innama yahtadeelinafsihi waman dalla fa-innama yadilluAAalayha wala taziru waziratun wizra okhrawama kunna muAAaththibeena hattanabAAatha rasoola');
INSERT INTO "transliteration" VALUES(17,16,'17:16','Wa-itha aradna an nuhlikaqaryatan amarna mutrafeeha fafasaqoo feehafahaqqa AAalayha alqawlu fadammarnahatadmeera');
INSERT INTO "transliteration" VALUES(17,17,'17:17','Wakam ahlakna mina alqurooni minbaAAdi noohin wakafa birabbika bithunoobiAAibadihi khabeeran baseera');
INSERT INTO "transliteration" VALUES(17,18,'17:18','Man kana yureedu alAAajilataAAajjalna lahu feeha ma nashao limannureedu thumma jaAAalna lahu jahannama yaslahamathmooman madhoora');
INSERT INTO "transliteration" VALUES(17,19,'17:19','Waman arada al-akhirata wasaAAalaha saAAyaha wahuwa mu/minun faola-ika kanasaAAyuhum mashkoora');
INSERT INTO "transliteration" VALUES(17,20,'17:20','Kullan numiddu haola-i wahaola-imin AAata-i rabbika wama kana AAataorabbika mahthoora');
INSERT INTO "transliteration" VALUES(17,21,'17:21','Onthur kayfa faddalnabaAAdahum AAala baAAdin walal-akhiratuakbaru darajatin waakbaru tafdeela');
INSERT INTO "transliteration" VALUES(17,22,'17:22','La tajAAal maAAa Allahi ilahanakhara fataqAAuda mathmooman makhthoola');
INSERT INTO "transliteration" VALUES(17,23,'17:23','Waqada rabbuka alla taAAbudooilla iyyahu wabilwalidayni ihsananimma yablughanna AAindaka alkibara ahaduhumaaw kilahuma fala taqul lahuma offinwala tanharhuma waqul lahuma qawlan kareema');
INSERT INTO "transliteration" VALUES(17,24,'17:24','Wakhfid lahuma janahaaththulli mina arrahmati waqulrabbi irhamhuma kama rabbayanee sagheera');
INSERT INTO "transliteration" VALUES(17,25,'17:25','Rabbukum aAAlamu bima fee nufoosikumin takoonoo saliheena fa-innahu kana lil-awwabeenaghafoora');
INSERT INTO "transliteration" VALUES(17,26,'17:26','Waati tha alqurba haqqahuwalmiskeena wabna assabeeli walatubaththir tabtheera');
INSERT INTO "transliteration" VALUES(17,27,'17:27','Inna almubaththireena kanooikhwana ashshayateeni wakana ashshaytanulirabbihi kafoora');
INSERT INTO "transliteration" VALUES(17,28,'17:28','Wa-imma tuAAridanna AAanhumuibtighaa rahmatin min rabbika tarjooha faqullahum qawlan maysoora');
INSERT INTO "transliteration" VALUES(17,29,'17:29','Wala tajAAal yadaka maghloolatan ilaAAunuqika wala tabsutha kulla albastifataqAAuda malooman mahsoora');
INSERT INTO "transliteration" VALUES(17,30,'17:30','Inna rabbaka yabsutu arrizqaliman yashao wayaqdiru innahu kana biAAibadihikhabeeran baseera');
INSERT INTO "transliteration" VALUES(17,31,'17:31','Wala taqtuloo awladakumkhashyata imlaqin nahnu narzuquhum wa-iyyakuminna qatlahum kana khit-an kabeera');
INSERT INTO "transliteration" VALUES(17,32,'17:32','Wala taqraboo azzinainnahu kana fahishatan wasaa sabeela');
INSERT INTO "transliteration" VALUES(17,33,'17:33','Wala taqtuloo annafsa allateeharrama Allahu illa bilhaqqiwaman qutila mathlooman faqad jaAAalnaliwaliyyihi sultanan fala yusrif fee alqatli innahukana mansoora');
INSERT INTO "transliteration" VALUES(17,34,'17:34','Wala taqraboo mala alyateemiilla billatee hiya ahsanu hattayablugha ashuddahu waawfoo bilAAahdi inna alAAahda kanamas-oola');
INSERT INTO "transliteration" VALUES(17,35,'17:35','Waawfoo alkayla itha kiltum wazinoobilqistasi almustaqeemi thalika khayrun waahsanuta/weela');
INSERT INTO "transliteration" VALUES(17,36,'17:36','Wala taqfu ma laysa laka bihiAAilmun inna assamAAa walbasara walfu-adakullu ola-ika kana AAanhu mas-oola');
INSERT INTO "transliteration" VALUES(17,37,'17:37','Wala tamshi fee al-ardi marahaninnaka lan takhriqa al-arda walan tablugha aljibalatoola');
INSERT INTO "transliteration" VALUES(17,38,'17:38','Kullu thalika kana sayyi-ohuAAinda rabbika makrooha');
INSERT INTO "transliteration" VALUES(17,39,'17:39','Thalika mimma awhailayka rabbuka mina alhikmati wala tajAAal maAAaAllahi ilahan akhara fatulqa feejahannama malooman madhoora');
INSERT INTO "transliteration" VALUES(17,40,'17:40','Afaasfakum rabbukum bilbaneenawattakhatha mina almala-ikati inathaninnakum lataqooloona qawlan AAatheema');
INSERT INTO "transliteration" VALUES(17,41,'17:41','Walaqad sarrafna fee hathaalqur-ani liyaththakkaroo wama yazeeduhumilla nufoora');
INSERT INTO "transliteration" VALUES(17,42,'17:42','Qul law kana maAAahu alihatunkama yaqooloona ithan labtaghaw ila theealAAarshi sabeela');
INSERT INTO "transliteration" VALUES(17,43,'17:43','Subhanahu wataAAalaAAamma yaqooloona AAuluwwan kabeera');
INSERT INTO "transliteration" VALUES(17,44,'17:44','Tusabbihu lahu assamawatuassabAAu wal-ardu waman feehinna wa-in minshay-in illa yusabbihu bihamdihi walakinla tafqahoona tasbeehahum innahu kana haleemanghafoora');
INSERT INTO "transliteration" VALUES(17,45,'17:45','Wa-itha qara/ta alqur-anajaAAalna baynaka wabayna allatheena layu/minoona bil-akhirati hijabanmastoora');
INSERT INTO "transliteration" VALUES(17,46,'17:46','WajaAAalna AAala quloobihimakinnatan an yafqahoohu wafee athanihim waqran wa-ithathakarta rabbaka fee alqur-ani wahdahuwallaw AAala adbarihim nufoora');
INSERT INTO "transliteration" VALUES(17,47,'17:47','Nahnu aAAlamu bimayastamiAAoona bihi ith yastamiAAoona ilayka wa-ithhum najwa ith yaqoolu aththalimoonain tattabiAAoona illa rajulan mashoora');
INSERT INTO "transliteration" VALUES(17,48,'17:48','Onthur kayfa daraboolaka al-amthala fadalloo fala yastateeAAoonasabeela');
INSERT INTO "transliteration" VALUES(17,49,'17:49','Waqaloo a-itha kunnaAAithaman warufatan a-innalamabAAoothoona khalqan jadeeda');
INSERT INTO "transliteration" VALUES(17,50,'17:50','Qul koonoo hijaratan aw hadeeda');
INSERT INTO "transliteration" VALUES(17,51,'17:51','Aw khalqan mimma yakburu fee sudoorikumfasayaqooloona man yuAAeeduna quli allathee fatarakumawwala marratin fasayunghidoona ilayka ruoosahumwayaqooloona mata huwa qul AAasa an yakoona qareeba');
INSERT INTO "transliteration" VALUES(17,52,'17:52','Yawma yadAAookum fatastajeeboona bihamdihiwatathunnoona in labithtum illa qaleela');
INSERT INTO "transliteration" VALUES(17,53,'17:53','Waqul liAAibadee yaqooloo allateehiya ahsanu inna ashshaytana yanzaghubaynahum inna ashshaytana kana lil-insaniAAaduwwan mubeena');
INSERT INTO "transliteration" VALUES(17,54,'17:54','Rabbukum aAAlamu bikum in yasha/ yarhamkumaw in yasha/ yuAAaththibkum wama arsalnakaAAalayhim wakeela');
INSERT INTO "transliteration" VALUES(17,55,'17:55','Warabbuka aAAlamu biman fee assamawatiwal-ardi walaqad faddalna baAAdaannabiyyeena AAala baAAdin waataynadawooda zaboora');
INSERT INTO "transliteration" VALUES(17,56,'17:56','Quli odAAu allatheena zaAAamtum mindoonihi fala yamlikoona kashfa addurriAAankum wala tahweela');
INSERT INTO "transliteration" VALUES(17,57,'17:57','Ola-ika allatheena yadAAoonayabtaghoona ila rabbihimu alwaseelata ayyuhum aqrabuwayarjoona rahmatahu wayakhafoona AAathabahuinna AAathaba rabbika kana mahthoora');
INSERT INTO "transliteration" VALUES(17,58,'17:58','Wa-in min qaryatin illa nahnumuhlikooha qabla yawmi alqiyamati aw muAAaththiboohaAAathaban shadeedan kana thalika fee alkitabimastoora');
INSERT INTO "transliteration" VALUES(17,59,'17:59','Wama manaAAana an nursila bil-ayatiilla an kaththaba biha al-awwaloona waataynathamooda annaqata mubsiratan fathalamoobiha wama nursilu bil-ayatiilla takhweefa');
INSERT INTO "transliteration" VALUES(17,60,'17:60','Wa-ith qulna laka inna rabbakaahata binnasi wama jaAAalnaarru/ya allatee araynaka illafitnatan linnasi washshajarataalmalAAoonata fee alqur-ani wanukhawwifuhum famayazeeduhum illa tughyanan kabeera');
INSERT INTO "transliteration" VALUES(17,61,'17:61','Wa-ith qulna lilmala-ikatiosjudoo li-adama fasajadoo illa ibleesa qalaaasjudu liman khalaqta teena');
INSERT INTO "transliteration" VALUES(17,62,'17:62','Qala araaytaka hatha allatheekarramta AAalayya la-in akhkhartani ila yawmi alqiyamatilaahtanikanna thurriyyatahu illa qaleela');
INSERT INTO "transliteration" VALUES(17,63,'17:63','Qala ithhab faman tabiAAakaminhum fa-inna jahannama jazaokum jazaan mawfoora');
INSERT INTO "transliteration" VALUES(17,64,'17:64','Wastafziz mani istataAAtaminhum bisawtika waajlib AAalayhim bikhaylika warajlikawasharik-hum fee al-amwali wal-awladiwaAAidhum wama yaAAiduhumu ashshaytanu illaghuroora');
INSERT INTO "transliteration" VALUES(17,65,'17:65','Inna AAibadee laysa laka AAalayhimsultanun wakafa birabbika wakeela');
INSERT INTO "transliteration" VALUES(17,66,'17:66','Rabbukumu allathee yuzjee lakumualfulka fee albahri litabtaghoo min fadlihi innahukana bikum raheema');
INSERT INTO "transliteration" VALUES(17,67,'17:67','Wa-itha massakumu addurrufee albahri dalla man tadAAoona illa iyyahufalamma najjakum ila albarri aAAradtumwakana al-insanu kafoora');
INSERT INTO "transliteration" VALUES(17,68,'17:68','Afaamintum an yakhsifa bikum janibaalbarri aw yursila AAalaykum hasiban thumma latajidoo lakum wakeela');
INSERT INTO "transliteration" VALUES(17,69,'17:69','Am amintum an yuAAeedakum feehi taratanokhra fayursila AAalaykum qasifan mina arreehifayughriqakum bima kafartum thumma la tajidoo lakumAAalayna bihi tabeeAAa');
INSERT INTO "transliteration" VALUES(17,70,'17:70','Walaqad karramna banee adamawahamalnahum fee albarri walbahriwarazaqnahum mina attayyibati wafaddalnahumAAala katheerin mimman khalaqna tafdeela');
INSERT INTO "transliteration" VALUES(17,71,'17:71','Yawma nadAAoo kulla onasin bi-imamihimfaman ootiya kitabahu biyameenihi faola-ikayaqraoona kitabahum wala yuthlamoonafateela');
INSERT INTO "transliteration" VALUES(17,72,'17:72','Waman kana fee hathihi aAAmafahuwa fee al-akhirati aAAma waadallu sabeela');
INSERT INTO "transliteration" VALUES(17,73,'17:73','Wa-in kadoo layaftinoonaka AAani allatheeawhayna ilayka litaftariya AAalayna ghayrahuwa-ithan lattakhathooka khaleela');
INSERT INTO "transliteration" VALUES(17,74,'17:74','Walawla an thabbatnaka laqadkidta tarkanu ilayhim shay-an qaleela');
INSERT INTO "transliteration" VALUES(17,75,'17:75','Ithan laathaqnaka diAAfaalhayati wadiAAfa almamati thumma latajidu laka AAalayna naseera');
INSERT INTO "transliteration" VALUES(17,76,'17:76','Wa-in kadoo layastafizzoonaka minaal-ardi liyukhrijooka minha wa-ithan layalbathoona khilafaka illa qaleela');
INSERT INTO "transliteration" VALUES(17,77,'17:77','Sunnata man qad arsalna qablaka minrusulina wala tajidu lisunnatina tahweela');
INSERT INTO "transliteration" VALUES(17,78,'17:78','Aqimi assalatalidulooki ashshamsi ila ghasaqi allayli waqur-anaalfajri inna qur-ana alfajri kana mashhooda');
INSERT INTO "transliteration" VALUES(17,79,'17:79','Wamina allayli fatahajjad bihi nafilatanlaka AAasa an yabAAathaka rabbuka maqaman mahmooda');
INSERT INTO "transliteration" VALUES(17,80,'17:80','Waqul rabbi adkhilnee mudkhala sidqinwaakhrijnee mukhraja sidqin wajAAal lee minladunka sultanan naseera');
INSERT INTO "transliteration" VALUES(17,81,'17:81','Waqul jaa alhaqqu wazahaqa albatiluinna albatila kana zahooqa');
INSERT INTO "transliteration" VALUES(17,82,'17:82','Wanunazzilu mina alqur-ani mahuwa shifaon warahmatun lilmu/mineena walayazeedu aththalimeena illakhasara');
INSERT INTO "transliteration" VALUES(17,83,'17:83','Wa-itha anAAamna AAalaal-insani aAArada wanaa bijanibihiwa-itha massahu ashsharru kana yaoosa');
INSERT INTO "transliteration" VALUES(17,84,'17:84','Qul kullun yaAAmalu AAala shakilatihifarabbukum aAAlamu biman huwa ahda sabeela');
INSERT INTO "transliteration" VALUES(17,85,'17:85','Wayas-aloonaka AAani arroohiquli arroohu min amri rabbee wama ooteetummina alAAilmi illa qaleela');
INSERT INTO "transliteration" VALUES(17,86,'17:86','Wala-in shi/na lanathhabannabillathee awhayna ilayka thumma latajidu laka bihi AAalayna wakeela');
INSERT INTO "transliteration" VALUES(17,87,'17:87','Illa rahmatan min rabbika innafadlahu kana AAalayka kabeera');
INSERT INTO "transliteration" VALUES(17,88,'17:88','Qul la-ini ijtamaAAati al-insu waljinnuAAala an ya/too bimithli hatha alqur-ani laya/toona bimithlihi walaw kana baAAduhum libaAAdinthaheera');
INSERT INTO "transliteration" VALUES(17,89,'17:89','Walaqad sarrafna linnasifee hatha alqur-ani min kulli mathalin faabaaktharu annasi illa kufoora');
INSERT INTO "transliteration" VALUES(17,90,'17:90','Waqaloo lan nu/mina laka hattatafjura lana mina al-ardi yanbooAAa');
INSERT INTO "transliteration" VALUES(17,91,'17:91','Aw takoona laka jannatun min nakheelinwaAAinabin fatufajjira al-anhara khilalahatafjeera');
INSERT INTO "transliteration" VALUES(17,92,'17:92','Aw tusqita assamaa kamazaAAamta AAalayna kisafan aw ta/tiya billahiwalmala-ikati qabeela');
INSERT INTO "transliteration" VALUES(17,93,'17:93','Aw yakoona laka baytun min zukhrufin aw tarqafee assama-i walan nu/mina liruqiyyika hattatunazzila AAalayna kitaban naqraohu qul subhanarabbee hal kuntu illa basharan rasoola');
