INSERT INTO "transliteration" VALUES(27,58,'27:58','Waamtarna AAalayhim mataranfasaa mataru almunthareen');
INSERT INTO "transliteration" VALUES(27,59,'27:59','Quli alhamdu lillahi wasalamunAAala AAibadihi allatheena istafaallahu khayrun amma yushrikoon');
