INSERT INTO "transliteration" VALUES(28,19,'28:19','Falamma an arada an yabtishabillathee huwa AAaduwwun lahuma qalaya moosa atureedu an taqtulanee kama qataltanafsan bil-amsi in tureedu illa an takoona jabbaranfee al-ardi wama tureedu an takoona mina almusliheen');
