INSERT INTO "transliteration" VALUES(28,51,'28:51','Walaqad wassalna lahumualqawla laAAallahum yatathakkaroon');
INSERT INTO "transliteration" VALUES(28,52,'28:52','Allatheena ataynahumualkitaba min qablihi hum bihi yu/minoon');
INSERT INTO "transliteration" VALUES(28,53,'28:53','Wa-itha yutla AAalayhim qalooamanna bihi innahu alhaqqu min rabbinainna kunna min qablihi muslimeen');
INSERT INTO "transliteration" VALUES(28,54,'28:54','Ola-ika yu/tawna ajrahum marrataynibima sabaroo wayadraoona bilhasanatiassayyi-ata wamimma razaqnahum yunfiqoon');
INSERT INTO "transliteration" VALUES(28,55,'28:55','Wa-itha samiAAoo allaghwa aAAradooAAanhu waqaloo lana aAAmaluna walakumaAAmalukum salamun AAalaykum la nabtagheealjahileen');
INSERT INTO "transliteration" VALUES(28,56,'28:56','Innaka la tahdee man ahbabtawalakinna Allaha yahdee man yashao wahuwaaAAlamu bilmuhtadeen');
INSERT INTO "transliteration" VALUES(28,57,'28:57','Waqaloo in nattabiAAi alhudamaAAaka nutakhattaf min ardina awa lamnumakkin lahum haraman aminan yujba ilayhithamaratu kulli shay-in rizqan min ladunna walakinnaaktharahum la yaAAlamoon');
INSERT INTO "transliteration" VALUES(28,58,'28:58','Wakam ahlakna min qaryatin batiratmaAAeeshataha fatilka masakinuhum lam tuskan minbaAAdihim illa qaleelan wakunna nahnu alwaritheen');
