INSERT INTO "transliteration" VALUES(28,60,'28:60','Wama ooteetum min shay-in famataAAualhayati addunya wazeenatuhawama AAinda Allahi khayrun waabqa afalataAAqiloon');
INSERT INTO "transliteration" VALUES(28,61,'28:61','Afaman waAAadnahu waAAdan hasananfahuwa laqeehi kaman mattaAAnahu mataAAa alhayatiaddunya thumma huwa yawma alqiyamati minaalmuhdareen');
INSERT INTO "transliteration" VALUES(28,62,'28:62','Wayawma yunadeehim fayaqoolu aynashuraka-iya allatheena kuntum tazAAumoon');
INSERT INTO "transliteration" VALUES(28,63,'28:63','Qala allatheena haqqaAAalayhimu alqawlu rabbana haola-i allatheenaaghwayna aghwaynahum kama ghawaynatabarra/na ilayka ma kanoo iyyanayaAAbudoon');
INSERT INTO "transliteration" VALUES(28,64,'28:64','Waqeela odAAoo shurakaakumfadaAAawhum falam yastajeeboo lahum waraawoo alAAathabalaw annahum kanoo yahtadoon');
INSERT INTO "transliteration" VALUES(28,65,'28:65','Wayawma yunadeehim fayaqoolu mathaajabtumu almursaleen');
INSERT INTO "transliteration" VALUES(28,66,'28:66','FaAAamiyat AAalayhimu al-anbaoyawma-ithin fahum la yatasaaloon');
INSERT INTO "transliteration" VALUES(28,67,'28:67','Faamma man taba waamanawaAAamila salihan faAAasa an yakoona minaalmufliheen');
INSERT INTO "transliteration" VALUES(28,68,'28:68','Warabbuka yakhluqu ma yashaowayakhtaru ma kana lahumu alkhiyaratu subhanaAllahi wataAAala AAamma yushrikoon');
INSERT INTO "transliteration" VALUES(28,69,'28:69','Warabbuka yaAAlamu ma tukinnu sudooruhumwama yuAAlinoon');
INSERT INTO "transliteration" VALUES(28,70,'28:70','Wahuwa Allahu la ilahailla huwa lahu alhamdu fee al-oola wal-akhiratiwalahu alhukmu wa-ilayhi turjaAAoon');
INSERT INTO "transliteration" VALUES(28,71,'28:71','Qul araaytum in jaAAala AllahuAAalaykumu allayla sarmadan ila yawmi alqiyamatiman ilahun ghayru Allahi ya/teekum bidiya-inafala tasmaAAoon');
INSERT INTO "transliteration" VALUES(28,72,'28:72','Qul araaytum in jaAAala AllahuAAalaykumu annahara sarmadan ila yawmialqiyamati man ilahun ghayru Allahiya/teekum bilaylin taskunoona feehi afala tubsiroon');
INSERT INTO "transliteration" VALUES(28,73,'28:73','Wamin rahmatihi jaAAala lakumuallayla wannahara litaskunoo feehi walitabtaghoomin fadlihi walaAAallakum tashkuroon');
INSERT INTO "transliteration" VALUES(28,74,'28:74','Wayawma yunadeehim fayaqoolu aynashuraka-iya allatheena kuntum tazAAumoon');
INSERT INTO "transliteration" VALUES(28,75,'28:75','WanazaAAna min kulli ommatinshaheedan faqulna hatoo burhanakumfaAAalimoo anna alhaqqa lillahi wadallaAAanhum ma kanoo yaftaroon');
INSERT INTO "transliteration" VALUES(28,76,'28:76','Inna qaroona kana min qawmimoosa fabagha AAalayhim waataynahumina alkunoozi ma inna mafatihahu latanoo-obilAAusbati olee alquwwati ith qalalahu qawmuhu la tafrah inna Allaha layuhibbu alfariheen');
INSERT INTO "transliteration" VALUES(28,77,'28:77','Wabtaghi feema atakaAllahu addara al-akhirata walatansa naseebaka mina addunya waahsinkama ahsana Allahu ilayka wala tabghialfasada fee al-ardi inna Allaha layuhibbu almufsideen');
INSERT INTO "transliteration" VALUES(28,78,'28:78','Qala innama ooteetuhu AAalaAAilmin AAindee awa lam yaAAlam anna Allaha qad ahlaka minqablihi mina alqurooni man huwa ashaddu minhu quwwatan waaktharujamAAan wala yus-alu AAan thunoobihimu almujrimoon');
INSERT INTO "transliteration" VALUES(28,79,'28:79','Fakharaja AAala qawmihi fee zeenatihiqala allatheena yureedoona alhayata addunyaya layta lana mithla ma ootiya qaroonuinnahu lathoo haththin AAatheem');
INSERT INTO "transliteration" VALUES(28,80,'28:80','Waqala allatheena ootooalAAilma waylakum thawabu Allahi khayrun liman amanawaAAamila salihan wala yulaqqahailla assabiroon');
INSERT INTO "transliteration" VALUES(28,81,'28:81','Fakhasafna bihi wabidarihial-arda fama kana lahu min fi-atin yansuroonahumin dooni Allahi wama kana mina almuntasireen');
INSERT INTO "transliteration" VALUES(28,82,'28:82','Waasbaha allatheenatamannaw makanahu bil-amsi yaqooloona waykaannaAllaha yabsutu arrizqa liman yashaomin AAibadihi wayaqdiru lawla an manna AllahuAAalayna lakhasafa bina waykaannahu la yuflihualkafiroon');
