INSERT INTO "transliteration" VALUES(28,84,'28:84','Man jaa bilhasanatifalahu khayrun minha waman jaa bissayyi-atifala yujza allatheena AAamiloo assayyi-atiilla ma kanoo yaAAmaloon');
