INSERT INTO "transliteration" VALUES(28,87,'28:87','Wala yasuddunnaka AAan ayatiAllahi baAAda ith onzilat ilayka wadAAu ilarabbika wala takoonanna mina almushrikeen');
INSERT INTO "transliteration" VALUES(28,88,'28:88','Wala tadAAu maAAa Allahi ilahanakhara la ilaha illa huwa kullushay-in halikun illa wajhahu lahu alhukmuwa-ilayhi turjaAAoon');
INSERT INTO "transliteration" VALUES(29,1,'29:1','Alif-lam-meem');
