INSERT INTO "transliteration" VALUES(29,8,'29:8','Wawassayna al-insana biwalidayhihusnan wa-in jahadaka litushrika bee malaysa laka bihi AAilmun fala tutiAAhumailayya marjiAAukum faonabbi-okum bima kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(29,9,'29:9','Wallatheena amanoowaAAamiloo assalihati lanudkhilannahum feeassaliheen');
