INSERT INTO "transliteration" VALUES(29,17,'29:17','Innama taAAbudoona min dooni Allahiawthanan watakhluqoona ifkan inna allatheenataAAbudoona min dooni Allahi la yamlikoona lakumrizqan fabtaghoo AAinda Allahi arrizqa waAAbudoohuwashkuroo lahu ilayhi turjaAAoon');
INSERT INTO "transliteration" VALUES(29,18,'29:18','Wa-in tukaththiboo faqad kaththabaomamun min qablikum wama AAala arrasooliilla albalaghu almubeen');
