INSERT INTO "transliteration" VALUES(29,20,'29:20','Qul seeroo fee al-ardi fanthurookayfa badaa alkhalqa thumma Allahu yunshi-o annash-ataal-akhirata inna Allaha AAala kulli shay-inqadeer');
INSERT INTO "transliteration" VALUES(29,21,'29:21','YuAAaththibu man yashao wayarhamuman yashao wa-ilayhi tuqlaboon');
