INSERT INTO "transliteration" VALUES(29,29,'29:29','A-innakum lata/toona arrijalawataqtaAAoona assabeela wata/toona fee nadeekumualmunkara fama kana jawaba qawmihi illaan qaloo i/tina biAAathabi Allahi inkunta mina assadiqeen');
INSERT INTO "transliteration" VALUES(29,30,'29:30','Qala rabbi onsurnee AAalaalqawmi almufsideen');
