INSERT INTO "transliteration" VALUES(29,32,'29:32','Qala inna feeha lootanqaloo nahnu aAAlamu biman feehalanunajjiyannahu waahlahu illa imraatahu kanat minaalghabireen');
INSERT INTO "transliteration" VALUES(29,33,'29:33','Walamma an jaat rusulunalootan see-a bihim wadaqa bihim tharAAan waqaloola takhaf wala tahzan inna munajjookawaahlaka illa imraataka kanat mina alghabireen');
