INSERT INTO "transliteration" VALUES(29,40,'29:40','Fakullan akhathna bithanbihifaminhum man arsalna AAalayhi hasiban waminhum manakhathat-hu assayhatu waminhum mankhasafna bihi al-arda waminhum man aghraqnawama kana Allahu liyathlimahumwalakin kanoo anfusahum yathlimoon');
