INSERT INTO "transliteration" VALUES(29,45,'29:45','Otlu ma oohiya ilayka minaalkitabi waaqimi assalata inna assalatatanha AAani alfahsha-i walmunkariwalathikru Allahi akbaru wallahuyaAAlamu ma tasnaAAoon');
