INSERT INTO "transliteration" VALUES(29,54,'29:54','YastaAAjiloonaka bilAAathabiwa-inna jahannama lamuheetatun bilkafireen');
INSERT INTO "transliteration" VALUES(29,55,'29:55','Yawma yaghshahumu alAAathabumin fawqihim wamin tahti arjulihim wayaqoolu thooqooma kuntum taAAmaloon');
INSERT INTO "transliteration" VALUES(29,56,'29:56','Ya AAibadiya allatheenaamanoo inna ardee wasiAAatun fa-iyyayafaAAbudoon');
INSERT INTO "transliteration" VALUES(29,57,'29:57','Kullu nafsin tha-iqatu almawti thummailayna turjaAAoon');
INSERT INTO "transliteration" VALUES(29,58,'29:58','Wallatheena amanoowaAAamiloo assalihati lanubawwi-annahummina aljannati ghurafan tajree min tahtiha al-anharukhalideena feeha niAAma ajru alAAamileen');
INSERT INTO "transliteration" VALUES(29,59,'29:59','Allatheena sabaroo waAAalarabbihim yatawakkaloon');
