INSERT INTO "transliteration" VALUES(30,20,'30:20','Wamin ayatihi an khalaqakummin turabin thumma itha antum basharun tantashiroon');
INSERT INTO "transliteration" VALUES(30,21,'30:21','Wamin ayatihi an khalaqa lakummin anfusikum azwajan litaskunoo ilayha wajaAAalabaynakum mawaddatan warahmatan inna fee thalika laayatinliqawmin yatafakkaroon');
