INSERT INTO "transliteration" VALUES(30,28,'30:28','Daraba lakum mathalan min anfusikumhal lakum mimma malakat aymanukum min shurakaafee ma razaqnakum faantum feehi sawaon takhafoonahumkakheefatikum anfusakum kathalika nufassilu al-ayatiliqawmin yaAAqiloon');
