INSERT INTO "transliteration" VALUES(30,30,'30:30','Faaqim wajhaka liddeeni haneefanfitrata Allahi allatee fatara annasaAAalayha la tabdeela likhalqi Allahi thalikaaddeenu alqayyimu walakinna akthara annasila yaAAlamoon');
