INSERT INTO "transliteration" VALUES(30,39,'30:39','Wama ataytum min ribanliyarbuwa fee amwali annasi falayarboo AAinda Allahi wama ataytum min zakatintureedoona wajha Allahi faola-ika humu almudAAifoon');
INSERT INTO "transliteration" VALUES(30,40,'30:40','Allahu allathee khalaqakumthumma razaqakum thumma yumeetukum thumma yuhyeekum halmin shuraka-ikum man yafAAalu min thalikum minshay-in subhanahu wataAAala AAammayushrikoon');
INSERT INTO "transliteration" VALUES(30,41,'30:41','Thahara alfasadu feealbarri walbahri bima kasabat aydee annasiliyutheeqahum baAAda allathee AAamiloolaAAallahum yarjiAAoon');
