INSERT INTO "transliteration" VALUES(31,9,'31:9','Khalideena feeha waAAda Allahihaqqan wahuwa alAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(31,10,'31:10','Khalaqa assamawatibighayri AAamadin tarawnaha waalqa fee al-ardirawasiya an tameeda bikum wabaththa feeha min kullidabbatin waanzalna mina assama-i maanfaanbatna feeha min kulli zawjin kareem');
