INSERT INTO "transliteration" VALUES(33,15,'33:15','Walaqad kanoo AAahadoo Allahamin qablu la yuwalloona al-adbara wakanaAAahdu Allahi mas-oola');
INSERT INTO "transliteration" VALUES(33,16,'33:16','Qul lan yanfaAAakumu alfiraru infarartum mina almawti awi alqatli wa-ithan latumattaAAoona illa qaleela');
INSERT INTO "transliteration" VALUES(33,17,'33:17','Qul man tha allathee yaAAsimukummina Allahi in arada bikum soo-an aw aradabikum rahmatan wala yajidoona lahum min dooni Allahiwaliyyan wala naseera');
INSERT INTO "transliteration" VALUES(33,18,'33:18','Qad yaAAlamu Allahu almuAAawwiqeenaminkum walqa-ileena li-ikhwanihim halummailayna wala ya/toona alba/sa illa qaleela');
INSERT INTO "transliteration" VALUES(33,19,'33:19','Ashihhatan AAalaykum fa-itha jaaalkhawfu raaytahum yanthuroona ilayka tadooruaAAyunuhum kallathee yughsha AAalayhi minaalmawti fa-itha thahaba alkhawfu salaqookumbi-alsinatin hidadin ashihhatan AAalaalkhayri ola-ika lam yu/minoo faahbata AllahuaAAmalahum wakana thalika AAala Allahiyaseera');
INSERT INTO "transliteration" VALUES(33,20,'33:20','Yahsaboona al-ahzabalam yathhaboo wa-in ya/ti al-ahzabu yawaddoolaw annahum badoona fee al-aAArabi yas-aloona AAananba-ikum walaw kanoo feekum ma qatalooilla qaleela');
INSERT INTO "transliteration" VALUES(33,21,'33:21','Laqad kana lakum fee rasooli Allahioswatun hasanatun liman kana yarjoo Allahawalyawma al-akhira wathakara Allahakatheera');
INSERT INTO "transliteration" VALUES(33,22,'33:22','Walamma raa almu/minoona al-ahzabaqaloo hatha ma waAAadana Allahuwarasooluhu wasadaqa Allahu warasooluhu wamazadahum illa eemanan watasleema');
INSERT INTO "transliteration" VALUES(33,23,'33:23','Mina almu/mineena rijalun sadaqooma AAahadoo Allaha AAalayhi faminhum man qadanahbahu waminhum man yantathiru wamabaddaloo tabdeela');
INSERT INTO "transliteration" VALUES(33,24,'33:24','Liyajziya Allahu assadiqeenabisidqihim wayuAAaththiba almunafiqeena inshaa aw yatooba AAalayhim inna Allaha kanaghafooran raheema');
INSERT INTO "transliteration" VALUES(33,25,'33:25','Waradda Allahu allatheenakafaroo bighaythihim lam yanaloo khayranwakafa Allahu almu/mineena alqitala wakanaAllahu qawiyyan AAazeeza');
INSERT INTO "transliteration" VALUES(33,26,'33:26','Waanzala allatheena thaharoohummin ahli alkitabi min sayaseehim waqathafafee quloobihimu arruAAba fareeqan taqtuloona wata/siroonafareeqa');
INSERT INTO "transliteration" VALUES(33,27,'33:27','Waawrathakum ardahum wadiyarahumwaamwalahum waardan lam tataooha wakanaAllahu AAala kulli shay-in qadeera');
INSERT INTO "transliteration" VALUES(33,28,'33:28','Ya ayyuha annabiyyuqul li-azwajika in kuntunna turidna alhayataaddunya wazeenataha fataAAalaynaomattiAAkunna waosarrihkunna sarahan jameela');
INSERT INTO "transliteration" VALUES(33,29,'33:29','Wa-in kuntunna turidna Allahawarasoolahu waddara al-akhirata fa-innaAllaha aAAadda lilmuhsinati minkunna ajranAAatheema');
INSERT INTO "transliteration" VALUES(33,30,'33:30','Ya nisaa annabiyyi manya/ti minkunna bifahishatin mubayyinatin yudaAAaflaha alAAathabu diAAfayni wakana thalikaAAala Allahi yaseera');
INSERT INTO "transliteration" VALUES(33,31,'33:31','Waman yaqnut minkunna lillahiwarasoolihi wataAAmal salihan nu/tiha ajrahamarratayni waaAAtadna laha rizqan kareema');
INSERT INTO "transliteration" VALUES(33,32,'33:32','Ya nisaa annabiyyilastunna kaahadin mina annisa-i iniittaqaytunna fala takhdaAAna bilqawli fayatmaAAaallathee fee qalbihi maradun waqulna qawlanmaAAroofa');
INSERT INTO "transliteration" VALUES(33,33,'33:33','Waqarna fee buyootikunna walatabarrajna tabarruja aljahiliyyati al-oola waaqimnaassalata waateena azzakatawaatiAAna Allaha warasoolahu innama yureeduAllahu liyuthhiba AAankumu arrijsa ahlaalbayti wayutahhirakum tatheera');
INSERT INTO "transliteration" VALUES(33,34,'33:34','Wathkurna ma yutlafee buyootikunna min ayati Allahi walhikmatiinna Allaha kana lateefan khabeera');
INSERT INTO "transliteration" VALUES(33,35,'33:35','Inna almuslimeena walmuslimatiwalmu/mineena walmu/minati walqaniteenawalqanitati wassadiqeena wassadiqatiwassabireena wassabiratiwalkhashiAAeena walkhashiAAatiwalmutasaddiqeena walmutasaddiqatiwassa-imeena wassa-imatiwalhafitheena furoojahum walhafithatiwaththakireena Allaha katheeran waththakiratiaAAadda Allahu lahum maghfiratan waajran AAatheema');
INSERT INTO "transliteration" VALUES(33,36,'33:36','Wama kana limu/minin walamu/minatin itha qada Allahu warasooluhuamran an yakoona lahumu alkhiyaratu min amrihim waman yaAAsiAllaha warasoolahu faqad dalla dalalanmubeena');
INSERT INTO "transliteration" VALUES(33,37,'33:37','Wa-ith taqoolu lillatheeanAAama Allahu AAalayhi waanAAamta AAalayhi amsik AAalaykazawjaka wattaqi Allaha watukhfee fee nafsika maAllahu mubdeehi watakhsha annasa wallahuahaqqu an takhshahu falamma qadazaydun minha wataran zawwajnakahalikay la yakoona AAala almu/mineena harajunfee azwaji adAAiya-ihim itha qadawminhunna wataran wakana amru Allahi mafAAoola');
INSERT INTO "transliteration" VALUES(33,38,'33:38','Ma kana AAala annabiyyimin harajin feema farada Allahu lahusunnata Allahi fee allatheena khalaw min qablu wakanaamru Allahi qadaran maqdoora');
INSERT INTO "transliteration" VALUES(33,39,'33:39','Allatheena yuballighoona risalatiAllahi wayakhshawnahu wala yakhshawna ahadanilla Allaha wakafa billahi haseeba');
INSERT INTO "transliteration" VALUES(33,40,'33:40','Ma kana muhammadun abaahadin min rijalikum walakin rasoola Allahiwakhatama annabiyyeena wakana Allahubikulli shay-in AAaleema');
INSERT INTO "transliteration" VALUES(33,41,'33:41','Ya ayyuha allatheena amanooothkuroo Allaha thikran katheera');
INSERT INTO "transliteration" VALUES(33,42,'33:42','Wasabbihoohu bukratan waaseela');
INSERT INTO "transliteration" VALUES(33,43,'33:43','Huwa allathee yusalleeAAalaykum wamala-ikatuhu liyukhrijakum mina aththulumatiila annoori wakana bilmu/mineena raheema');
INSERT INTO "transliteration" VALUES(33,44,'33:44','Tahiyyatuhum yawma yalqawnahu salamunwaaAAadda lahum ajran kareema');
INSERT INTO "transliteration" VALUES(33,45,'33:45','Ya ayyuha annabiyyuinna arsalnaka shahidan wamubashshiran wanatheera');
INSERT INTO "transliteration" VALUES(33,46,'33:46','WadaAAiyan ila Allahibi-ithnihi wasirajan muneera');
INSERT INTO "transliteration" VALUES(33,47,'33:47','Wabashshiri almu/mineena bi-anna lahum minaAllahi fadlan kabeera');
INSERT INTO "transliteration" VALUES(33,48,'33:48','Wala tutiAAi alkafireenawalmunafiqeena wadaAA athahum watawakkalAAala Allahi wakafa billahiwakeela');
INSERT INTO "transliteration" VALUES(33,49,'33:49','Ya ayyuha allatheena amanooitha nakahtumu almu/minati thumma tallaqtumoohunnamin qabli an tamassoohunna fama lakum AAalayhinna minAAiddatin taAAtaddoonaha famattiAAoohunna wasarrihoohunnasarahan jameela');
INSERT INTO "transliteration" VALUES(33,50,'33:50','Ya ayyuha annabiyyuinna ahlalna laka azwajaka allateeatayta ojoorahunna wama malakat yameenuka mimmaafaa Allahu AAalayka wabanati AAammika wabanatiAAammatika wabanati khalika wabanatikhalatika allatee hajarna maAAaka wamraatanmu/minatan in wahabat nafsaha linnabiyyi in aradaannabiyyu an yastankihaha khalisatanlaka min dooni almu/mineena qad AAalimna ma faradnaAAalayhim fee azwajihim wama malakat aymanuhumlikayla yakoona AAalayka harajun wakana Allahughafooran raheema');
INSERT INTO "transliteration" VALUES(33,51,'33:51','Turjee man tashao minhunna watu/weeilayka man tashao wamani ibtaghayta mimman AAazalta falajunaha AAalayka thalika adna an taqarraaAAyunuhunna wala yahzanna wayardayna bimaataytahunna kulluhunna wallahu yaAAlamu mafee quloobikum wakana Allahu AAaleeman haleema');
INSERT INTO "transliteration" VALUES(33,52,'33:52','La yahillu laka annisaomin baAAdu wala an tabaddala bihinna min azwajinwalaw aAAjabaka husnuhunna illa ma malakatyameenuka wakana Allahu AAala kulli shay-inraqeeba');
INSERT INTO "transliteration" VALUES(33,53,'33:53','Ya ayyuha allatheena amanoola tadkhuloo buyoota annabiyyi illa an yu/thanalakum ila taAAamin ghayra nathireenainahu walakin itha duAAeetum fadkhuloofa-itha taAAimtum fantashiroo walamusta/niseena lihadeethin inna thalikum kanayu/thee annabiyya fayastahyee minkum wallahula yastahyee mina alhaqqi wa-ithasaaltumoohunna mataAAan fas-aloohunna min wara-ihijabin thalikum atharu liquloobikumwaquloobihinna wama kana lakum an tu/thoorasoola Allahi wala an tankihoo azwajahumin baAAdihi abadan inna thalikum kana AAinda AllahiAAatheema');
INSERT INTO "transliteration" VALUES(33,54,'33:54','In tubdoo shay-an aw tukhfoohu fa-inna Allahakana bikulli shay-in AAaleema');
INSERT INTO "transliteration" VALUES(33,55,'33:55','La junaha AAalayhinna fee aba-ihinnawala abna-ihinna wala ikhwanihinnawala abna-i ikhwanihinna wala abna-iakhawatihinna wala nisa-ihinna wala mamalakat aymanuhunna wattaqeena Allaha innaAllaha kana AAala kulli shay-in shaheeda');
INSERT INTO "transliteration" VALUES(33,56,'33:56','Inna Allaha wamala-ikatahu yusalloonaAAala annabiyyi ya ayyuha allatheenaamanoo salloo AAalayhi wasallimoo tasleema');
INSERT INTO "transliteration" VALUES(33,57,'33:57','Inna allatheena yu/thoona Allahawarasoolahu laAAanahumu Allahu fee addunyawal-akhirati waaAAadda lahum AAathabanmuheena');
INSERT INTO "transliteration" VALUES(33,58,'33:58','Wallatheena yu/thoonaalmu/mineena walmu/minati bighayri maiktasaboo faqadi ihtamaloo buhtanan wa-ithmanmubeena');
INSERT INTO "transliteration" VALUES(33,59,'33:59','Ya ayyuha annabiyyuqul li-azwajika wabanatika wanisa-ialmu/mineena yudneena AAalayhinna min jalabeebihinna thalikaadna an yuAArafna fala yu/thayna wakanaAllahu ghafooran raheema');
INSERT INTO "transliteration" VALUES(33,60,'33:60','La-in lam yantahi almunafiqoona wallatheenafee quloobihim maradun walmurjifoona feealmadeenati lanughriyannaka bihim thumma la yujawiroonakafeeha illa qaleela');
INSERT INTO "transliteration" VALUES(33,61,'33:61','MalAAooneena ayna ma thuqifoo okhithoowaquttiloo taqteela');
INSERT INTO "transliteration" VALUES(33,62,'33:62','Sunnata Allahi fee allatheenakhalaw min qablu walan tajida lisunnati Allahi tabdeela');
INSERT INTO "transliteration" VALUES(33,63,'33:63','Yas-aluka annasu AAani assaAAatiqul innama AAilmuha AAinda Allahi wamayudreeka laAAalla assaAAata takoonu qareeba');
INSERT INTO "transliteration" VALUES(33,64,'33:64','Inna Allaha laAAana alkafireenawaaAAadda lahum saAAeera');
INSERT INTO "transliteration" VALUES(33,65,'33:65','Khalideena feeha abadan layajidoona waliyyan wala naseera');
INSERT INTO "transliteration" VALUES(33,66,'33:66','Yawma tuqallabu wujoohuhum fee annariyaqooloona ya laytana ataAAna AllahawaataAAna arrasoola');
INSERT INTO "transliteration" VALUES(33,67,'33:67','Waqaloo rabbana inna ataAAnasadatana wakubaraana faadalloonaassabeela');
INSERT INTO "transliteration" VALUES(33,68,'33:68','Rabbana atihim diAAfaynimina alAAathabi walAAanhum laAAnan kabeera');
INSERT INTO "transliteration" VALUES(33,69,'33:69','Ya ayyuha allatheena amanoola takoonoo kallatheena athaw moosafabarraahu Allahu mimma qaloo wakanaAAinda Allahi wajeeha');
INSERT INTO "transliteration" VALUES(33,70,'33:70','Ya ayyuha allatheena amanooittaqoo Allaha waqooloo qawlan sadeeda');
INSERT INTO "transliteration" VALUES(33,71,'33:71','Yuslih lakum aAAmalakumwayaghfir lakum thunoobakum waman yutiAAi Allahawarasoolahu faqad faza fawzan AAatheema');
INSERT INTO "transliteration" VALUES(33,72,'33:72','Inna AAaradna al-amanataAAala assamawati wal-ardiwaljibali faabayna an yahmilnahawaashfaqna minha wahamalaha al-insanuinnahu kana thalooman jahoola');
INSERT INTO "transliteration" VALUES(33,73,'33:73','LiyuAAaththiba Allahu almunafiqeenawalmunafiqati walmushrikeena walmushrikatiwayatooba Allahu AAala almu/mineena walmu/minatiwakana Allahu ghafooran raheema');
INSERT INTO "transliteration" VALUES(34,1,'34:1','Alhamdu lillahi allatheelahu ma fee assamawati wamafee al-ardi walahu alhamdu fee al-akhiratiwahuwa alhakeemu alkhabeer');
INSERT INTO "transliteration" VALUES(34,2,'34:2','YaAAlamu ma yaliju fee al-ardiwama yakhruju minha wama yanzilu mina assama-iwama yaAAruju feeha wahuwa arraheemualghafoor');
INSERT INTO "transliteration" VALUES(34,3,'34:3','Waqala allatheena kafaroo lata/teena assaAAatu qul bala warabbeelata/tiyannakum AAalimi alghaybi la yaAAzubu AAanhumithqalu tharratin fee assamawatiwala fee al-ardi wala asgharu min thalikawala akbaru illa fee kitabin mubeen');
INSERT INTO "transliteration" VALUES(34,4,'34:4','Liyajziya allatheena amanoowaAAamiloo assalihati ola-ika lahummaghfiratun warizqun kareem');
INSERT INTO "transliteration" VALUES(34,5,'34:5','Wallatheena saAAaw fee ayatinamuAAajizeena ola-ika lahum AAathabun minrijzin aleem');
INSERT INTO "transliteration" VALUES(34,6,'34:6','Wayara allatheena ootooalAAilma allathee onzila ilayka min rabbika huwa alhaqqawayahdee ila sirati alAAazeezi alhameed');
INSERT INTO "transliteration" VALUES(34,7,'34:7','Waqala allatheena kafaroo halnadullukum AAala rajulin yunabbi-okum ithamuzziqtum kulla mumazzaqin innakum lafee khalqin jadeed');
INSERT INTO "transliteration" VALUES(34,8,'34:8','Aftara AAala Allahi kathibanam bihi jinnatun bali allatheena la yu/minoona bil-akhiratifee alAAathabi waddalali albaAAeed');
INSERT INTO "transliteration" VALUES(34,9,'34:9','Afalam yaraw ila ma baynaaydeehim wama khalfahum mina assama-i wal-ardiin nasha/ nakhsif bihimu al-arda aw nusqitAAalayhim kisafan mina assama-i inna fee thalikalaayatan likulli AAabdin muneeb');
INSERT INTO "transliteration" VALUES(34,10,'34:10','Walaqad atayna dawoodaminna fadlan ya jibalu awwibeemaAAahu wattayra waalanna lahu alhadeed');
INSERT INTO "transliteration" VALUES(34,11,'34:11','Ani iAAmal sabighatin waqaddirfee assardi waAAmaloo salihan inneebima taAAmaloona baseer');
INSERT INTO "transliteration" VALUES(34,12,'34:12','Walisulaymana arreehaghuduwwuha shahrun warawahuha shahrunwaasalna lahu AAayna alqitri wamina aljinni manyaAAmalu bayna yadayhi bi-ithni rabbihi waman yazighminhum AAan amrina nuthiqhu min AAathabi assaAAeer');
INSERT INTO "transliteration" VALUES(34,13,'34:13','YaAAmaloona lahu ma yashao minmahareeba watamatheela wajifanin kaljawabiwaqudoorin rasiyatin iAAmaloo ala dawoodashukran waqaleelun min AAibadiya ashshakoor');
INSERT INTO "transliteration" VALUES(34,14,'34:14','Falamma qadaynaAAalayhi almawta ma dallahum AAala mawtihi illadabbatu al-ardi ta/kulu minsaatahu falammakharra tabayyanati aljinnu an law kanoo yaAAlamoonaalghayba ma labithoo fee alAAathabi almuheen');
INSERT INTO "transliteration" VALUES(34,15,'34:15','Laqad kana lisaba-in fee maskanihim ayatunjannatani AAan yameenin washimalin kuloo min rizqirabbikum washkuroo lahu baldatun tayyibatunwarabbun ghafoor');
INSERT INTO "transliteration" VALUES(34,16,'34:16','FaaAAradoo faarsalna AAalayhimsayla alAAarimi wabaddalnahum bijannatayhim jannatayni thawatayokulin khamtin waathlin washay-in min sidrin qaleel');
INSERT INTO "transliteration" VALUES(34,17,'34:17','Thalika jazaynahum bimakafaroo wahal nujazee illa alkafoor');
INSERT INTO "transliteration" VALUES(34,18,'34:18','WajaAAalna baynahum wabayna alquraallatee barakna feeha quran thahiratanwaqaddarna feeha assayra seeroo feehalayaliya waayyaman amineen');
INSERT INTO "transliteration" VALUES(34,19,'34:19','Faqaloo rabbana baAAidbayna asfarina wathalamoo anfusahumfajaAAalnahum ahadeetha wamazzaqnahum kullamumazzaqin inna fee thalika laayatin likullisabbarin shakoor');
INSERT INTO "transliteration" VALUES(34,20,'34:20','Walaqad saddaqa AAalayhim ibleesu thannahufattabaAAoohu illa fareeqan mina almu/mineen');
INSERT INTO "transliteration" VALUES(34,21,'34:21','Wama kana lahu AAalayhim minsultanin illa linaAAlama man yu/minu bil-akhiratimimman huwa minha fee shakkin warabbuka AAala kullishay-in hafeeth');
INSERT INTO "transliteration" VALUES(34,22,'34:22','Quli odAAoo allatheena zaAAamtum mindooni Allahi la yamlikoona mithqala tharratinfee assamawati wala fee al-ardiwama lahum feehima min shirkin wama lahuminhum min thaheer');
INSERT INTO "transliteration" VALUES(34,23,'34:23','Wala tanfaAAu ashshafaAAatuAAindahu illa liman athina lahu hattaitha fuzziAAa AAan quloobihim qaloo matha qalarabbukum qaloo alhaqqa wahuwa alAAaliyyu alkabeer');
INSERT INTO "transliteration" VALUES(34,24,'34:24','Qul man yarzuqukum mina assamawatiwal-ardi quli Allahu wa-inna aw iyyakumlaAAala hudan aw fee dalalin mubeen');
INSERT INTO "transliteration" VALUES(34,25,'34:25','Qul la tus-aloona AAammaajramna wala nus-alu AAamma taAAmaloon');
INSERT INTO "transliteration" VALUES(34,26,'34:26','Qul yajmaAAu baynana rabbunathumma yaftahu baynana bilhaqqiwahuwa alfattahu alAAaleem');
INSERT INTO "transliteration" VALUES(34,27,'34:27','Qul arooniya allatheena alhaqtumbihi shurakaa kalla bal huwa AllahualAAazeezu alhakeem');
INSERT INTO "transliteration" VALUES(34,28,'34:28','Wama arsalnaka illa kaffatanlinnasi basheeran wanatheeran walakinnaakthara annasi la yaAAlamoon');
INSERT INTO "transliteration" VALUES(34,29,'34:29','Wayaqooloona mata hathaalwaAAdu in kuntum sadiqeen');
INSERT INTO "transliteration" VALUES(34,30,'34:30','Qul lakum meeAAadu yawmin latasta/khiroona AAanhu saAAatan wala tastaqdimoon');
INSERT INTO "transliteration" VALUES(34,31,'34:31','Waqala allatheena kafaroo lannu/mina bihatha alqur-ani wala billatheebayna yadayhi walaw tara ithi aththalimoonamawqoofoona AAinda rabbihim yarjiAAu baAAduhum ilabaAAdin alqawla yaqoolu allatheena istudAAifoolillatheena istakbaroo lawla antum lakunnamu/mineen');
INSERT INTO "transliteration" VALUES(34,32,'34:32','Qala allatheena istakbaroolillatheena istudAAifoo anahnu sadadnakumAAani alhuda baAAda ith jaakum bal kuntummujrimeen');
INSERT INTO "transliteration" VALUES(34,33,'34:33','Waqala allatheena istudAAifoolillatheena istakbaroo bal makru allayli wannahariith ta/muroonana an nakfura billahiwanajAAala lahu andadan waasarroo annadamatalamma raawoo alAAathaba wajaAAalna al-aghlalafee aAAnaqi allatheena kafaroo hal yujzawna illama kanoo yaAAmaloon');
INSERT INTO "transliteration" VALUES(34,34,'34:34','Wama arsalna fee qaryatin minnatheerin illa qala mutrafooha innabima orsiltum bihi kafiroon');
INSERT INTO "transliteration" VALUES(34,35,'34:35','Waqaloo nahnu aktharu amwalanwaawladan wama nahnu bimuAAaththabeen');
INSERT INTO "transliteration" VALUES(34,36,'34:36','Qul inna rabbee yabsutu arrizqaliman yashao wayaqdiru walakinna akthara annasila yaAAlamoon');
INSERT INTO "transliteration" VALUES(34,37,'34:37','Wama amwalukum wala awladukumbillatee tuqarribukum AAindana zulfa illaman amana waAAamila salihan faola-ikalahum jazao addiAAfi bima AAamiloowahum fee alghurufati aminoon');
INSERT INTO "transliteration" VALUES(34,38,'34:38','Wallatheena yasAAawna fee ayatinamuAAajizeena ola-ika fee alAAathabi muhdaroon');
INSERT INTO "transliteration" VALUES(34,39,'34:39','Qul inna rabbee yabsutu arrizqaliman yashao min AAibadihi wayaqdiru lahu wamaanfaqtum min shay-in fahuwa yukhlifuhu wahuwa khayru arraziqeen');
INSERT INTO "transliteration" VALUES(34,40,'34:40','Wayawma yahshuruhum jameeAAan thummayaqoolu lilmala-ikati ahaola-i iyyakumkanoo yaAAbudoon');
INSERT INTO "transliteration" VALUES(34,41,'34:41','Qaloo subhanaka anta waliyyunamin doonihim bal kanoo yaAAbudoona aljinna aktharuhumbihim mu/minoon');
INSERT INTO "transliteration" VALUES(34,42,'34:42','Falyawma la yamliku baAAdukumlibaAAdin nafAAan wala darran wanaqoolulillatheena thalamoo thooqoo AAathabaannari allatee kuntum biha tukaththiboon');
INSERT INTO "transliteration" VALUES(34,43,'34:43','Wa-itha tutla AAalayhim ayatunabayyinatin qaloo ma hatha illarajulun yureedu an yasuddakum AAamma kanayaAAbudu abaokum waqaloo ma hathailla ifkun muftaran waqala allatheenakafaroo lilhaqqi lamma jaahum in hathailla sihrun mubeen');
INSERT INTO "transliteration" VALUES(34,44,'34:44','Wama ataynahum minkutubin yadrusoonaha wama arsalna ilayhimqablaka min natheer');
INSERT INTO "transliteration" VALUES(34,45,'34:45','Wakaththaba allatheena minqablihim wama balaghoo miAAshara ma ataynahumfakaththaboo rusulee fakayfa kana nakeer');
INSERT INTO "transliteration" VALUES(34,46,'34:46','Qul innama aAAithukumbiwahidatin an taqoomoo lillahi mathna wafuradathumma tatafakkaroo ma bisahibikum min jinnatin inhuwa illa natheerun lakum bayna yaday AAathabinshadeed');
INSERT INTO "transliteration" VALUES(34,47,'34:47','Qul ma saaltukum min ajrin fahuwalakum in ajriya illa AAala Allahi wahuwaAAala kulli shay-in shaheed');
INSERT INTO "transliteration" VALUES(34,48,'34:48','Qul inna rabbee yaqthifu bilhaqqiAAallamu alghuyoob');
INSERT INTO "transliteration" VALUES(34,49,'34:49','Qul jaa alhaqqu wamayubdi-o albatilu wama yuAAeed');
INSERT INTO "transliteration" VALUES(34,50,'34:50','Qul in dalaltu fa-innama adilluAAala nafsee wa-ini ihtadaytu fabima yooheeilayya rabbee innahu sameeAAun qareeb');
INSERT INTO "transliteration" VALUES(34,51,'34:51','Walaw tara ith faziAAoo falafawta waokhithoo min makanin qareeb');
