INSERT INTO "transliteration" VALUES(39,9,'39:9','Amman huwa qanitun anaaallayli sajidan waqa-iman yahtharu al-akhiratawayarjoo rahmata rabbihi qul hal yastawee allatheenayaAAlamoona wallatheena la yaAAlamoonainnama yatathakkaru oloo al-albab');
